INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'به نام خداوند بخشنده مهربان‌'),
(2, 1, 2, 'سپاس خداوند را که پروردگار جهانیان است‌'),
(3, 1, 3, 'خدای رحمان مهربان‌'),
(4, 1, 4, 'دادار روز جزا'),
(5, 1, 5, '[خداوندا] تنها تو را می‌پرستیم و تنها از تو یاری می‌خواهیم‌'),
(6, 1, 6, 'ما را بر راه راست استوار بدار'),
(7, 1, 7, 'راه کسانی که آنان را نواخته‌ای، آنان نه که از نظر انداخته‌ای، و نه گمراهان‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الم [الف لام میم‌]'),
(9, 2, 2, 'این کتابی است که شک در آن روا نیست [که‌] راهنمای پرهیزگاران است‌'),
(10, 2, 3, 'همان کسانی که به غیب ایمان دارند و نماز را برپا می‌دارند و از آنچه روزیشان داده‌ایم، انفاق می‌کنند'),
(11, 2, 4, 'و کسانی که به آنچه بر تو نازل شده است و آنچه پیش از تو فرود آمده است، ایمان دارند و هم آنان به آخرت ایقان دارند'),
(12, 2, 5, 'اینان از جانب پروردگارشان از هدایتی برخوردارند و هم اینان رستگارانند'),
(13, 2, 6, 'برای کافران یکسان است چه هشدارشان بدهی چه هشدارشان ندهی، ایمان نمی‌آورند'),
(14, 2, 7, 'خداوند بر دلها و بر گوشهایشان مهر نهاده است، و بر دیدگانشان پرده‌ای است و عذابی بزرگ [در پیش‌] دارند'),
(15, 2, 8, 'و کسانی از مردم هستند که می‌گویند به خداوند و روز بازپسین ایمان آورده‌ایم، ولی آنان مؤمن نیستند'),
(16, 2, 9, 'می‌خواهند به خداوند و مؤمنان نیرنگ بزنند، در حالی که جز به خودشان نیرنگ نمی‌زنند، و نمی‌دانند'),
(17, 2, 10, 'در دلهایشان بیماری‌ای هست و خداوند بر بیماریشان بیفزاید، و به کیفر دروغی که می‌گفتند عذابی دردناک [در پیش‌] دارند'),
(18, 2, 11, 'و چون به آنان گفته شود در [این‌] سرزمین فساد نکنید، گویند ما که اهل اصلاحیم‌'),
(19, 2, 12, 'بدانید که ایشان اهل فسادند، ولی خود نمی‌دانند'),
(20, 2, 13, 'و چون به ایشان گفته شود چنانکه [سایر] مردم ایمان آورده‌اند، شما هم ایمان آورید، گویند آیا ما هم مانند کم‌خردان ایمان بیاوریم؟ بدانید که خودشان کم‌خردند، ولی نمی‌دانند'),
(21, 2, 14, 'و چون با مؤمنان روبه‌رو شوند، گویند ایمان آورده‌ایم، و چون با پیشوایان خویش تنها شوند، گویند ما با شما هستیم، ما فقط ریشخند می‌کنیم‌'),
(22, 2, 15, 'خداوند ریشخندشان می‌کند و در طغیانشان سرگشته می‌دارد'),
(23, 2, 16, 'اینان کسانی هستند که گمراهی را به بهای راهیابی خریدند، و سودای آنان سودی نکرد، و راهیاب نشدند'),
(24, 2, 17, 'داستان ایشان همچون داستان کسانی است که آتشی افروختند، و چون پیرامونشان را روشن کرد [بناگاه‌] خداوند نورشان را خاموش کرد، و در تاریکی‌ای که [چیزی را] نمی‌بینند واگذاشت‌'),
(25, 2, 18, 'ناشنوا و گنگ و نابینا هستند و به راه نمی‌آیند'),
(26, 2, 19, 'یا چون [گرفتاران در] بارانی سخت که از آسمان می‌بارد و همراه با تاریکی و رعد و برق است از بیم مرگ ناشی از صاعقه‌ها انگشتانشان را در گوشهایشان می‌کنند، و خداوند بر کافران چیره است‌'),
(27, 2, 20, 'نزدیک است که برق [نور] چشمانشان را برباید هرگاه که [برق راهشان را] روشن کند، پیش روند، و چون [راهشان را] تاریک کند، بایستند و خداوند اگر می‌خواست شنوایی و بیناییشان را از بین می‌برد چرا که خداوند بر هر کاری تواناست‌'),
(28, 2, 21, 'ای مردم پروردگارتان را که شما و پیشینیانتان را آفریده است، بپرستید، تا در امان مانید'),
(29, 2, 22, 'آنکه زمین را [همچون‌] زیرانداز و آسمان را [همچون‌] سرپناه شما کرد، و از آسمان آبی فرو فرستاد، آنگاه بدان میوه‌ها برای روزی شما برآورد، پس آگاهانه برای او همتا نیاورید'),
(30, 2, 23, 'و اگر از آنچه بر بنده خویش فرو فرستاده‌ایم، شک دارید، اگر راست می‌گویید سوره‌ای همانند آن بیاورید و از یاورانتان در برابر خداوند، یاری بخواهید'),
(31, 2, 24, 'ولی اگر چنین نکردید که هرگز نخواهید کرد از آتشی که هیزم آن انسان و سنگهاست و برای کافران آماده شده است، بترسید'),
(32, 2, 25, 'و به کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، نوید بده که ایشان را بوستانهایی است که در فرودست آن جویباران جاری است، هر گاه از میوه‌های آن روزی یابند، گویند این همان است که پیشترها از آن بهره‌مند بودیم، و به ایشان همانند آن داده شود، و در آنجا جفتهای پاکیزه دارند، و هم در آنجا جاویدانند'),
(33, 2, 26, 'خداوند پروا ندارد که به پشه و فراتر [یا فروتر] از آن مثل زند، آنگاه مؤمنان می‌دانند که آن [مثل‌] راست و درست است [و] از سوی پروردگارشان است، ولی کافران می‌گویند خداوند از این مثل چه می‌خواهد؟ [بدین‌سان‌] بسیاری را بدان گمراه و بسیاری را راهنمایی می‌کند، ولی جز نافرمانان کسی را بدان بیراه نمی‌گرداند'),
(34, 2, 27, 'کسانی که پیمان خداوند را پس از بستنش می‌شکنند، و آنچه خداوند به پیوستن آن فرمان داده است، می‌گسلند، و در زمین فساد می‌کنند، اینان زیانکارند'),
(35, 2, 28, 'چگونه به خداوند کفرمی‌ورزید، حال آنکه بی‌جان بودید و او به شما جان بخشید، سپس شما را می‌میراند و دوباره زنده می‌کند، آنگاه به سویش بازگردانده می‌شوید'),
(36, 2, 29, 'او کسی است که آنچه در زمین است همه را برای شما آفرید، سپس به [آفرینش‌] آسمان پرداخت، و هفت آسمان استوار کرد و او به هر چیزی داناست‌'),
(37, 2, 30, 'و چون پروردگارت به فرشتگان گفت من گمارنده جانشینی در زمینم، گفتند آیا کسی را در آن می‌گماری که در آن فساد می‌کند و خونها می‌ریزد، حال آنکه ما شاکرانه تو را نیایش می‌کنیم و تو را به پاکی یاد می‌کنیم، فرمود من چیزی می‌دانم که شما نمی‌دانید'),
(38, 2, 31, 'و همه نامها را به آدم آموخت، سپس آنها را بر فرشتگان عرضه داشت و گفت اگر راست می‌گویید به من از نامهای ایشان خبر دهید'),
(39, 2, 32, 'گفتند پاکا که تویی، ما دانشی نداریم جز آنچه به ما آموخته‌ای، تو دانای فرزانه‌ای‌'),
(40, 2, 33, 'فرمود ای آدم آنان را از نامهایشان خبر ده، و چون از نامهایشان خبرشان داد، فرمود آیا به شما نگفتم که من ناپیدای آسمانها و زمین را می‌دانم‌'),
(41, 2, 34, 'و چون به فرشتگان گفتیم بر آدم سجده برید، همه سجده بردند جز ابلیس که سرکشید و کبرورزید و از کافران شد'),
(42, 2, 35, 'و گفتیم ای آدم تو و همسرت در بهشت بیارامید و از [نعمتهای‌] آن از هر جا که خواستید به خوشی و فراوانی بخورید، ولی به این درخت نزدیک نشوید که از ستمکاران خواهید بود'),
(43, 2, 36, 'سپس شیطان آنان را به لغزش کشانید و از جایی که بودند آواره کرد، و گفتیم پایین روید برخی دشمن یکدیگر و در روی زمین تا وقت معین آرامشگاه و بهره‌مندی دارید'),
(44, 2, 37, 'آنگاه آدم کلماتی از پروردگارش فراگرفت و [خداوند] از او درگذشت، چه او توبه‌پذیر مهربان است‌'),
(45, 2, 38, 'گفتیم همه از آن [بهشت‌] پایین روید، آنگاه اگر رهنمودی از من برای شما آمد، کسانی که از رهنمودم پیروی کنند، بیمی برایشان نیست و اندوهگین نگردند'),
(46, 2, 39, 'و کسانی که کفرورزیدند و آیات ما را دروغ انگاشتند، دوزخی‌اند و جاودانه در آنند'),
(47, 2, 40, 'ای بنی‌اسرائیل نعمتم را که بر شما ارزانی داشتم یاد کنید و به پیمان من وفا کنید تا به پیمان خود با شما وفا کنم و تنها از من بترسید'),
(48, 2, 41, 'و به آنچه همخوان کتاب شما نازل کرده‌ام ایمان بیاورید و نخستین منکر آن نباشید، و آیات مرا به بهای ناچیز نفروشید و تنها از من پروا کنید'),
(49, 2, 42, 'و حق را به باطل میامیزید و دیده و دانسته حق را پنهان نکنید'),
(50, 2, 43, 'و نماز را برپا دارید و زکات را بپردازید و با نمازگزاران نماز کنید'),
(51, 2, 44, 'آیا مردم را به نیکی فرمان می‌دهید و خودتان را فراموش می‌کنید؟ حال آنکه کتاب [تورات‌] می‌خوانید، مگر نمی‌اندیشید؟'),
(52, 2, 45, 'از صبر [روزه‌] و نماز یاری بجویید و آن [نماز] جز بر فروتنان گران می‌آید'),
(53, 2, 46, 'کسانی که یقین دارند به لقای پروردگارشان می‌رسند و بازگردنده به سوی او هستند'),
(54, 2, 47, 'ای بنی‌اسرائیل نعمتم را که بر شما ارزانی داشتم و اینکه شما را بر جهانیان [هم زمانتان‌] برتری بخشیدم، یاد کنید'),
(55, 2, 48, 'و از روزی بترسید که کسی به داد کسی نرسد و از کسی شفاعتی پذیرفته نشود و بدل و بلاگردانی گرفته نشود و یاری نیابند'),
(56, 2, 49, 'و یاد کنید که شما را از آل فرعون رهانیدیم که عذابی سخت را به شما می‌چشانیدند، پسرانتان را می‌کشتند و دخترانتان را [برای کنیزی‌] زنده نگاه می‌داشتند، و در آن آزمونی بزرگ از سوی پروردگارتان بود'),
(57, 2, 50, 'و یاد کنید که دریا را برای شما شکافتیم و شما را رهانیدیم و آل فرعون را غرق کردیم و شما نظاره می‌کردید'),
(58, 2, 51, 'و یاد کنید که با موسی چهل شب وعده گذاشته بودیم، آنگاه شما در غیاب او گوساله را به پرستش گرفتید و شما ستمکار بودید'),
(59, 2, 52, 'آنگاه پس از آن از شما درگذشتیم تا سپاس بگزارید'),
(60, 2, 53, 'و یاد کنید که به موسی کتاب آسمانی و فرقان دادیم تا به راه آیید'),
(61, 2, 54, 'و یاد کنید که موسی به قومش گفت ای قوم من، شما با گوساله‌پرستی بر خودتان ستم کرده‌اید، اینک به درگاه آفریدگارتان توبه کنید و خودتان را بکشید، که این در نزد آفریدگارتان برای شما بهتر است، آنگاه [که چنین کردید، خداوند] از شما درگذشت، چرا که او توبه‌پذیر مهربان است‌'),
(62, 2, 55, 'و یاد کنید که گفتید ای موسی ما [سخن‌] تو را باور نمی‌کنیم مگر آنکه خداوند را آشکارا ببینیم، آنگاه [به چشم خود] دیدید که صاعقه بر شما فرود آمد'),
(63, 2, 56, 'آنگاه پس از مردنتان، شما را [دیگر بار] زنده کردیم، تا سپاس بگزارید'),
(64, 2, 57, 'و ابر را سایبان شما گرداندیم و برایتان من و سلوی فرو فرستادیم [و گفتیم‌] از روزی پاکیزه‌ای که به شما بخشیده‌ایم بخورید، و بر ما ستم نکردند، بلکه بر خویشتن ستم می‌کردند'),
(65, 2, 58, 'و یاد کنید که گفتیم به این شهر درآیید و هر گونه که خواستید به خوشی و فراوانی از [نعمتهای‌] آن بخورید و از آن دروازه فروتنانه وارد شوید [و برای عذرخواهی‌] حطه بگویید، تا گناهان شما را ببخشیم، و پاداش نیکوکاران را خواهیم افزود'),
(66, 2, 59, 'آنگاه ستمکاران سخن را به چیزی جز آنچه به آنان گفته شده بود، تبدیل کردند، پس بر سر ستمکاران به کیفر نافرمانیشان عذابی از آسمان فرو فرستادیم‌'),
(67, 2, 60, 'و چون موسی برای قومش در طلب آب برآمد، به او گفتیم با عصایت به آن سنگ بزن، آنگاه از آن دوازده چشمه شکافت و هر گروهی [از اسباط] آبشخور خود را شناختند، [گفتیم‌] از روزی خداوند بخورید و بیاشامید، و در این سرزمین فتنه و فساد برپا نکنید'),
(68, 2, 61, 'و چنین بود که گفتید ای موسی هرگز تاب تحمل یک خوراک تنها را نداریم، پس از پروردگارت بخواه که برای ما از آنچه زمین می‌رویاند از [جمله‌] سبزی، و خیار، و سیر و عدس و پیاز برآورد [موسی‌] گفت آیا چیزی را که پست‌تر است جانشین چیزی می‌کنید که بهتر است؟ به شهری درآیید که [در آنجا] آنچه خواستید فراهم است، و دچار خواری و ناداری شدند و سزاوار خشم خداوندی گردیدند، چرا که آیات الهی را انکار می‌کردند و پیامبران را به ناحق می‌کشتند، این از آن روی بود که سرکشی می‌کردند و از حد در می‌گذشتند'),
(69, 2, 62, 'از مؤمنان و یهودیان و مسیحیان و صابئین، هر کس که به خداوند و روز بازپسین ایمان آورده و نیکوکاری کرده باشد، پاداششان نزد خداوند [محفوظ] است و نه بیمی بر آنهاست و نه اندوهگین می‌شوند'),
(70, 2, 63, 'و یاد کنید که از شما پیمان گرفتیم و [کوه‌] طور را بر فراز شما برافراشتیم [و گفتیم‌] آنچه به شما داده‌ایم به جد و جهد بگیرید و آنچه در آن هست به خاطر بسپارید باشد که پروا کنید'),
(71, 2, 64, 'آنگاه، پس از آن روی گرداندید، و اگر بخشش و رحمت الهی در حق شما نبود، از زیانکاران می‌شدید'),
(72, 2, 65, 'کسانی از خودتان را که در سبت شنبه بی‌روشی کردند خوب می‌شناسید، که به ایشان گفتیم بوزینگان مطرود باشید'),
(73, 2, 66, 'و این [پدیده‌] را مایه عبرت مردم حال و آینده و پندآموز پرهیزگاران ساختیم‌'),
(74, 2, 67, 'و چون موسی به قومش گفت خداوند به شما دستور می‌دهد که ماده‌گاوی بکشید، گفتند آیا ما را ریشخند می‌کنی؟ گفت پناه بر خدا می‌برم که [مبادا] از نادانان باشم‌'),
(75, 2, 68, 'گفتند از پروردگارت برای ما بخواه که چون و چند آن گاو را بر ما روشن کند، گفت می‌فرماید آن گاوی است نه پیر و نه جوان، میانسال بینابین، پس آنچه دستور یافته‌اید انجام دهید'),
(76, 2, 69, 'گفتند از پروردگارت بخواه برای ما روشن کند که رنگ آن چیست، گفت می‌فرماید آن گاوی است رنگش زرد روشن که بینندگان را شاد می‌کند'),
(77, 2, 70, 'گفتند از پروردگارت بخواه که برای ما روشن کند که آن چگونه است [چون و چند] گاو بر ما مشتبه شده است و ما اگر خدا بخواهد راهیاب خواهیم شد'),
(78, 2, 71, 'گفت می‌فرماید آن گاوی است که نه رام [و کاری‌] است که زمین را شیار کند و نه کشتزار را آبیاری کند، تندرست و یکرنگ گفتند اینک سخن درست آوردی سپس آن را کشتند و نزدیک بود که این کار را نکنند'),
(79, 2, 72, 'و یاد کنید که چون کسی را کشتید و درباره او به ستیزه پرداختید [و به گردن همدیگر انداختید] و خداوند آشکارکننده چیزی است که پنهان می‌ساختید'),
(80, 2, 73, 'آنگاه گفتیم بخشی از بدن گاو را به او [بدن آن کشته‌] بزنید [تا زنده شود] و خداوند این چنین مردگان را زنده می‌کند و معجزات خویش را به شما می‌نمایاند تا بیندیشید'),
(81, 2, 74, 'سپس دلهایتان پس از آن سخت شد، همانند سنگ، یا از آن سخت‌تر، چرا که بعضی از سنگهاست که از آنها جویباران می‌شکافد و بعضی از آنهاست که می‌شکند و آب از آنها بیرون می‌آید و بعضی از آنهاست که از خشیت الهی [از کوه‌] فرو می‌افتد و خدا از آنچه می‌کنید غافل نیست‌'),
(82, 2, 75, '[ای مؤمنان‌] آیا امید دارید که [یهودیان‌] به [حقانیت‌] شما ایمان آورند، و حال آنکه گروهی از آنان کلام الهی را می‌شنیدند و پس از آنکه آن را در می‌یافتند آگاهانه دگرگونش می‌ساختند'),
(83, 2, 76, 'و چون با مؤمنان روبه‌رو شوند گویند ایمان آورده‌ایم و چون با همدیگر تنها شوند گویند آیا آنچه خدا بر شما آشکار کرده است، با آنان درمیان می‌گذارید، تا در پیشگاه خداوند با آن بر شما حجت آورند، مگر اندیشه نمی‌کنید؟'),
(84, 2, 77, 'آیا نمی‌دانند که خداوند آنچه پنهان و آنچه آشکار می‌دارند می‌داند؟'),
(85, 2, 78, 'و از آنان بی‌سوادانی هستند که کتاب آسمانی [تورات‌] را جز طوطی‌وار [و لقلقه زبان‌] نمی‌دانند و جز پندار نمی‌بافند'),
(86, 2, 79, 'پس وای بر کسانی که [اینگونه‌] کتاب را به دست خویش می‌نویسند و سپس برای آنکه آن را به بهای ناچیزی بفروشند می‌گویند این از نزد خداوند است، وای بر آنان از آنچه به دست خویش نوشته‌اند و وای بر آنان از آنچه به دست می‌آورند'),
(87, 2, 80, 'و گویند آتش دوزخ جز چند روز اندکشمار به ما نمی‌رسد، بگو آیا از خدا پیمان گرفته‌اید -که البته خداوند خلف وعده نخواهد کردیا چیزی را که نمی‌دانید به خدا نسبت می‌دهید؟'),
(88, 2, 81, 'آری کسانی که بدی کنند و گناهشان بر آنان چیره شود، دوزخی‌اند و در آن جاودانه می‌مانند'),
(89, 2, 82, 'و کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، بهشتی‌اند و جاودانه در آن ماندگاراند'),
(90, 2, 83, 'و یاد کنید که از بنی‌اسرائیل پیمان گرفتیم که جز خداوند را نپرستید و به پدر و مادر و خویشاوندان و یتیمان و بینوایان نیکی کنید و با مردم به زبان خوش سخن بگویید و نماز را برپا دارید و زکات را بپردازید، سپس جز اندکی از شما [بقیه‌] سر برتافته و رویگردان شدید'),
(91, 2, 84, 'و یاد کنید که از شما پیمان گرفتیم که خون یکدیگر را نریزید و یکدیگر را از خانه و کاشانه نرانید، آنگاه دیده و دانسته گردن نهادید'),
(92, 2, 85, '[ولی‌] باز همین شما هستید که همدیگر را می‌کشید و گروهی از خودتان را از خانه و کاشانه‌شان می‌رانید و به گناه و ستمگری در برابر آنان همدست می‌شوید و چون کسانی از شما اسیر می‌شوند آنان را [بر وفق حکم تورات‌] باز می‌خرید، حال آنکه راندن [و کشتن‌] آنان بر شما حرام است آیا به بخشی از کتاب [در باب بازخرید اسیران‌] ایمان می‌آورید، و به بخشی دیگر [در باب تحریم کشتار] ایمان نمی‌آورید؟ کیفر کسی که چنین کند چیست جز خفت و خواری در زندگانی دنیا و در روز قیامت ایشان را به سخت‌ترین عذابها بازبرند و خدا از آنچه می‌کنید غافل نیست‌'),
(93, 2, 86, 'اینان کسانی هستند که زندگانی دنیا را به بهای آخرت خریدند، از این روی عذاب آنها کاسته نشود و یاری نبینند'),
(94, 2, 87, 'به موسی کتاب آسمانی دادیم و از پی او پیامبران فرستادیم، و معجزات آشکاری به عیسی بن مریم بخشیدیم و او را به روح القدس یاری دادیم، پس چرا هرگاه که پیامبری احکامی بر خلاف دلخواه شما برایتان آورد، سرکشی کردید و گروهی را دروغگو انگاشتید و گروهی را کشتید؟'),
(95, 2, 88, 'و گفتند دلهای ما در پوشش است، [چنین نیست‌] بلکه خداوند به کیفر کفرشان ایشان را لعنت کرده است، از این روی اندکی ایمان می‌آورند'),
(96, 2, 89, 'و آنگاه که کتابی از سوی خداوند برای آنان آمد که همخوان با کتابشان بود، با آنکه پیش از آن در برابر کافران [از فرارسیدن پیامبر اسلام و قرآن‌] یاری می‌جستند، چون آنچه [از پیش‌] می‌شناختند به نزدشان آمد، آن را انکار کردند، لعنت الهی بر کافران است‌'),
(97, 2, 90, 'بدا بدانچه به جان خریدند که آنچه خدا فرستاده بود از این رشک که [چرا] خداوند فضل خویش را بر هر کس از بندگانش که بخواهد ارزانی می‌دارد، انکار کردند، پس سزاوار خشم اندر خشم شدند و کافران عذابی خفت‌بار [در پیش‌] دارند'),
(98, 2, 91, 'و چون به ایشان گفته شود به آنچه خداوند فروفرستاده است [قرآن‌] ایمان آورید، گویند به آنچه بر [پیامبر] خودمان نازل شده است ایمان می‌آوریم، و آنچه جز آن است، انکار می‌کنند، حال آنکه آن [کتاب‌] حق و همخوان کتاب آنهاست بگو اگر مؤمنید پس چرا پیامبران الهی را در گذشته می‌کشتید؟'),
(99, 2, 92, 'و موسی آن معجزات روشن را برای شما آورد، سپس در غیاب او شما بیدادگرانه گوساله‌پرستی کردید'),
(100, 2, 93, 'و یاد کنید که از شما پیمان گرفتیم و [کوه‌] طور را برفراز شما برافراشتیم [و گفتیم‌] آنچه به شما داده‌ایم، به جد و جهد بگیرید و گوش شنوا داشته باشید، گفتند شنیدیم و [در دل گفتند] سرپیچیدیم [و نشنیده گرفتیم‌] و بر اثر کفرشان مهر گوساله در دلشان سرشته شد، بگو اگر مؤمن باشید چه بد است آنچه "ایمان" شما به آن فرمان می‌دهد'),
(101, 2, 94, 'بگو اگر راست می‌گویید که به حکم الهی سرای آخرت از میان همه مردم ویژه شماست، آرزوی مرگ کنید'),
(102, 2, 95, 'و به خاطر کارهایی که کرده‌اند هرگز آن را آرزو نخواهند کرد، و خداوند به [احوال‌] ستمگران داناست‌'),
(103, 2, 96, 'و آنان را آزمندترین مردم -و نیز مشرکانبه زندگانی دنیا می‌یابی، هر یک از آنان خوش دارد که کاش هزار سال عمر دهندش، ولی این عمر یافتن، دور دارنده او از عذاب نیست، و خداوند به آنچه می‌کنند بیناست‌'),
(104, 2, 97, 'بگو هر کس دشمن جبرئیل باشد [بداند] که جبرئیل آن را به دستور الهی بر دل تو نازل کرده است و آن همخوان کتابهایی است که پیشاپیش آن است و راهنما و مژده‌بخش مؤمنان است‌'),
(105, 2, 98, 'کسی که دشمن خداوند و فرشتگان او و پیامبرانش و جبرئیل و میکائیل باشد [کافر است‌] و خداوند دشمن کافران است‌'),
(106, 2, 99, 'و بر تو آیات روشنگری فرو فرستاده‌ایم، و جز نافرمانان کسی به آنها انکار نمی‌ورزد'),
(107, 2, 100, 'چرا هر گاه پیمانی بستند گروهی از آنان آن را شکستند، آری بیشتر آنان ایمان ندارند'),
(108, 2, 101, 'و چون پیامبری از سوی خداوند به نزد ایشان آمد که گواهی‌دهنده بر [حقانیت‌] کتابشان بود، گروهی از اهل کتاب، کتاب الهی را به پشت سر افکندند، گویی [حقیقت را] نمی‌دانند'),
(109, 2, 102, 'و از آنچه شیاطین در روزگار فرمانروایی سلیمان می‌خواندند، پیروی کردند و سلیمان [به سحر نپرداخت و] کفر نورزید، ولی شیاطین کفرورزیدند آنان به مردم سحر می‌آموختند و نیز آنچه بر دو فرشته هاروت و ماروت در بابل نازل شده بود این دو به هیچ کس چیزی نمی‌آموختند مگر آنکه می‌گفتند ما مایه آزمونیم [با به کار بستن سحر] کافر مشو اما [مردمان‌] از آنها چیزی می‌آموختند که با آن بین مرد و زنش جدایی افکنند، و البته به کسی زیان رسان نبودند مگر به اذن الهی، و چیزی می‌آموختند که به ایشان زیان می‌رساند و سودی برایشان نداشت، و به خوبی می‌دانستند که هر کس خریدار آن باشد در آخرت بهره‌ای ندارد و اگر می‌دانستند بد چیزی را به جان خریده بودند'),
(110, 2, 103, 'هر گاه ایمان آورده و پارسایی ورزیده بودند، -اگر می‌دانستندپاداش الهی بهتر بود'),
(111, 2, 104, 'ای کسانی که ایمان آورده‌اید نگویید "راعنا" و بگویید "انظرنا" و گوش شنوا داشته باشید و کافران عذابی دردناک [در پیش‌] دارند'),
(112, 2, 105, 'کسانی از اهل کتاب که کفرورزیده‌اند و همچنین مشرکان خوش ندارند که از سوی پروردگارتان خیری بر شما نازل گردد، حال آنکه خداوند هر کس را بخواهد مشمول رحمت خویش می‌گرداند، و خداوند دارای بخشش بیکران است‌'),
(113, 2, 106, 'هر آیه‌ای را که نسخ کنیم یا فروگذاریم، بهتر از آن یا همانندش را در میان آوریم، آیا نمی‌دانی که خداوند بر هر کاری تواناست‌'),
(114, 2, 107, 'آیا نمی‌دانی که فرمانروایی آسمانها و زمین از آن خداوند است و شما را جز خداوند، سرور و یاوری نیست؟'),
(115, 2, 108, 'گویی می‌خواهید از پیامبرتان درخواستهایی بکنید، همچنانکه پیشترها از موسی درخواست شد؟ و حال آنکه هر کس کفر را جانشین ایمان سازد، به راستی که از راه راست بیراه شده است‌'),
(116, 2, 109, 'بسیاری از اهل کتاب، با آنکه حق برایشان آشکار شده است، به انگیزه رشکی که در دل دارند، خوش دارند که شما را پس از ایمانتان کافر گردانند بگذارید و بگذرید تا خداوند فرمان خویش را به میان آورد، آری خداوند بر هر کاری تواناست‌'),
(117, 2, 110, 'و نماز را برپا دارید و زکات را بپردازید و هر خیری پیشاپیش برای [ذخیره آخرت‌] خویش بفرستید [پاداش‌] آن را نزد خداوند خواهید یافت، خداوند به آنچه می‌کنید بیناست‌'),
(118, 2, 111, 'و گفتند هیچ کس به بهشت نمی‌رود مگر آنکه یهودی یا مسیحی باشد، این [از] آرزوهای آنان است، بگو اگر راست می‌گویید برهانتان را بیاورید'),
(119, 2, 112, 'حق این است که هر کس روی دل به سوی خدا نهد و نیکوکار باشد پاداشش نزد پروردگارش [محفوظ] است و نه بیمی بر آنهاست و نه اندوهگین می‌شوند'),
(120, 2, 113, 'یهودیان گفتند که مسیحیان بر حق نیستند، و مسیحیان گفتند یهودیان بر حق نیستند، حال آنکه کتاب آسمانی را می‌خوانند، کسانی هم که هیچ چیز نمی‌دانند سخنی همانند سخن ایشان گفتند، سرانجام خداوند در روز قیامت، در آنچه اختلاف داشتند بی‌نشان داوری خواهد کرد'),
(121, 2, 114, 'و کیست ستمکارتر از کسی که نگذارد نام خداوند در مساجد یاد شود، و در ویرانی آن بکوشد، آنان را نسزد جز اینکه ترسان -لرزان پا به درون آنها بگذارند، در دنیا خفت و خواری و در آخرت عذابی عظیم دارند'),
(122, 2, 115, 'مشرق و مغرب خدای راست، پس به هرجا روی آورید، رو به سوی خداوند است، بی‌گمان خدا گشایشگر داناست‌'),
(123, 2, 116, 'و گفتند خداوند فرزندی برگزیده است، او منزه است، بلکه هر آنچه در آسمانها و زمین است از آن اوست و همه سر به راه او هستند'),
(124, 2, 117, 'نو پدیدآورنده آسمانها و زمین است، و چون به کاری اراده کند، فقط می‌گوید موجود شو و بی‌درنگ موجود می‌شود'),
(125, 2, 118, 'نادانان گفتند چرا خداوند با ما [بی‌واسطه‌] سخن نمی‌گوید، یا چرا معجزه‌ای برای ما نازل نمی‌شود کسانی که پیش از اینان بودند نیز مانند سخن ایشان را گفتند، دل و درونشان همانند است، و ما آیات خود را برای مردمی که اهل یقین هستند، روشن ساخته‌ایم‌'),
(126, 2, 119, 'ما تو را به حق مژده‌بخش و هشداردهنده فرستاده‌ایم و با تو درباره دوزخیان چون و چرا نکنند'),
(127, 2, 120, 'و یهودیان و مسیحیان هرگز از تو خشنود نخواهند شد مگر آنکه از آیین آنها پیروی کنی، بگو هدایت هدایت الهی است، و اگر پس از دانشی که بر تو [فرود] آمده است، از خواسته‌های آنان پیروی کنی، در برابر خداوند یار و یاوری نداری‌'),
(128, 2, 121, 'کسانی که به آنان کتاب داده‌ایم [و] آن را چنانکه باید و شاید می‌خوانند، آنانند که به آن ایمان می‌آورند و کسانی که آن را انکار می‌کنند، زیانکارند'),
(129, 2, 122, 'ای بنی‌اسرائیل نعمتم را که بر شما ارزانی داشتم و اینکه شما را بر جهانیان [هم زمانتان‌] برتری بخشیدم، یاد کنید'),
(130, 2, 123, 'و از روزی بترسید که کسی به داد کسی نرسد و از کسی بدل و بلاگردانی پذیرفته نشود و شفاعتی به حال او سود ندهد و یاری نیابند'),
(131, 2, 124, 'و چون ابراهیم را پروردگارش به شعائری چند آزمود [و آموزش داد] و او آنها را به انجام رساند، فرمود من تو را پیشوای مردم می‌گمارم گفت و از زاد و رود من [چه کسی را می‌گماری؟] فرمود عهد من به ستمکاران [مشرکان‌] نمی‌رسد'),
(132, 2, 125, 'و یاد کنید که خانه [کعبه‌] را بازگشتگاه و حرم امن مردم قرار دادیم و [گفتیم‌] از مقام ابراهیم نمازگاهی بسازید و به ابراهیم و اسماعیل سفارش کردیم که خانه‌ام را برای غریبان [مسافران‌] و مقیمان و نمازگزاران پاکیزه گردانید'),
(133, 2, 126, 'و چون ابراهیم گفت پروردگارا اینجا را شهری امن بگردان و از اهلش هرکس را که به خداوند و روز بازپسین ایمان دارد از فرآورده‌ها روزی ببخش، فرمود و هرکس که کفرورزد، اندکی بهره‌مندش گردانم سپس دچار عذاب دوزخش سازم و چه بد سرانجامی است‌'),
(134, 2, 127, 'و چون ابراهیم و اسماعیل پایه‌های خانه [کعبه‌] را برآوردند [گفتند] پروردگارا از ما بپذیر که تویی شنوای دانا'),
(135, 2, 128, 'پروردگارا ما را فرمانبردار خویش بگردان و از زاد و رود ما امتی فرمانبردار خویش پدید آور و مناسک ما را به ما بنما، و از ما درگذر که تویی توبه‌پذیر مهربان‌'),
(136, 2, 129, 'پروردگارا و از ایشان در میانشان پیامبری برانگیز که آیات تو را بر آنان بخواند و به آنان کتاب آسمانی و حکمت بیاموزد و پاکدلشان سازد که تویی پیروزمند فرزان‌'),
(137, 2, 130, 'و کیست که از آیین ابراهیم روی برتابد، مگر کسی که سبک‌سر باشد، و ما او را در دنیا برگزیده‌ایم و همو در آخرت از شایستگان است‌'),
(138, 2, 131, 'چون پروردگارش به او گفت فرمانبر باش، گفت فرمانبر پروردگار جهانیانم‌'),
(139, 2, 132, 'و ابراهیم و یعقوب پسرانشان را به آن سفارش کردند که ای فرزندان من خداوند این دین را برای شما برگزیده است، مبادا که جز به فرمانبرداری، [از جهان‌] درگذرید'),
(140, 2, 133, 'مگر شما شاهد بودید که چون مرگ یعقوب فرارسید به پسرانش گفت پس از [درگذشت‌] من چه می‌پرستید؟ گفتند خدای تو را و خدای نیاکانت ابراهیم و اسماعیل و اسحاق را که خدای یگانه است می‌پرستیم و ما فرمانبردار او هستیم‌'),
(141, 2, 134, 'این امتی است که درگذشته است، از اوست آنچه کرده است و از شماست آنچه کرده‌اید، و با شما چون و چرا نکنند که آنان چه کرده‌اند'),
(142, 2, 135, 'و گفتند یهودی یا مسیحی باشید تا رستگار شوید، بگو چنین نیست، بلکه [رستگاری در] آیین ابراهیم پاکدین است که از مشرکان نبود'),
(143, 2, 136, 'بگو به خداوند و آنچه بر ما و آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط نازل شده و آنچه به موسی و عیسی و آنچه به پیامبران از سوی پروردگارشان داده شده، ایمان آورده‌ایم، و بین هیچ‌یک از آنان فرق نمی‌گذاریم و ما فرمانبردار او هستیم‌'),
(144, 2, 137, 'پس اگر به آنچه شما ایمان آورده‌اید، ایمان آوردند، راهیاب شده‌اند و اگر روی برتافتند، بی‌شک در ستیزاند، خداوند، یاور تو در برابر آنان بس است، و او شنوای داناست‌'),
(145, 2, 138, 'این نگارگری الهی است، و چه کسی خوش نگارتر از خداوند است و ما پرستندگان او هستیم‌'),
(146, 2, 139, 'بگو آیا درباره خداوند با ما محاجه می‌کنید حال آنکه او پروردگار ما و پروردگار شماست و ماییم و کردار خویش و شمایید و کردار خویش، و ما برای او اخلاص می‌ورزیم‌'),
(147, 2, 140, 'یا بر آنید که ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط یهودی یا مسیحی بوده‌اند؟ بگو شما داناترید یا خداوند؟ و کیست ستمکارتر از کسی که شهادتی را که از جانب خداوند بر او مقرر گردیده است، پنهان دارد؟ و خداوند از آنچه می‌کنید غافل نیست‌'),
(148, 2, 141, 'این امتی است که در گذشته است، از اوست آنچه کرده است و از شماست آنچه کرده‌اید، و با شما چون و چرا نکنند که آنان چه کرده‌اند'),
(149, 2, 142, 'گروهی از کم‌خردان از این مردم هستند که به زودی خواهند گفت چه چیز آنان را از قبله‌ای که بر آن بودند، بازگرداند؟ بگو مشرق و مغرب خدای راست، و هرکس را که بخواهد به راه راست رهنمون می‌گردد'),
(150, 2, 143, 'و بدین‌سان شما را گروهی بهینه گرداندیم تا گواهان مردم باشید و پیامبر بر شما گواه باشد، و قبله‌ای را که بر آن بودی برنگرداندیم مگر از آن روی که کسی را که پیروی از پیامبر می‌کند از کسی که از عقیده‌اش بازمی‌گردد، بازشناسانیم، و آن [پیروی‌] جز بر کسانی که خداوند هدایتشان کرده است، گران می‌آید، و خداوند هرگز ایمان شما را ضایع نمی‌گرداند، به راستی که خداوند با مردم رئوف و مهربان است‌'),
(151, 2, 144, 'آری رویکرد تو را به آسمان می‌بینیم، اینک روی تو را به قبله‌ای که از آن خشنود هستی می‌گردانیم، پس روی به سوی مسجدالحرام آور، و هرجا که هستید رویتان را بدان سو کنید، و اهل کتاب می‌دانند که آن حقی است از جانب پروردگارشان، و خدا از آنچه می‌کنند غافل نیست‌'),
(152, 2, 145, 'و اگر هر معجزه‌ای برای اهل کتاب بیاوری، از قبله تو پیروی نخواهند کرد، و تو [نیز] پیرو قبله‌شان نیستی، و آنان پیرو قبله همدیگر هم نیستند، و اگر پس از دانشی که بر تو فرود آمده است، از خواسته‌های آنان پیروی کنی، آنگاه از ستمکاران خواهی بود'),
(153, 2, 146, 'اهل کتاب او را می‌شناسند به همان گونه که فرزندانشان را می‌شناسند البته گروهی از آنان دیده و دانسته حق را پنهان می‌دارند'),
(154, 2, 147, 'حق از سوی پروردگارت [نازل شده‌] است، هرگز از دودلان مباش‌'),
(155, 2, 148, 'و هر یک را جهتی است [برای قبله‌] که خداوند روی‌آورشان گردانده است، به انجام خیرات بشتابید، و هر جا که باشید خداوند همگی شما را [در آخرت‌] باز می‌آورد، چرا که خدا بر هر کاری تواناست‌'),
(156, 2, 149, 'و از هر جا که بیرون شدی روی به سوی مسجدالحرام آور و آن حق است و از جانب پروردگار توست، و خداوند از آنچه می‌کنید غافل نیست‌'),
(157, 2, 150, 'و از هر جا که بیرون شدی روی به سوی مسجدالحرام آور و هر جا که بودید روی به سوی آن آورید، تا مردم از شما بهانه‌ای نداشته باشند، مگر ستمگران، که از آنان نترسید و از من بترسید و [چنین مقرر داشتم‌] تا نعمتم را بر شما تمام گردانم و باشد که راهیاب شوید'),
(158, 2, 151, 'همچنانکه پیامبری از خودتان به میان شما فرستادم تا آیات ما را بر شما بخواند و پاکدلتان بگرداند و به شما کتاب و حکمت بیاموزد و آنچه نمی‌دانستید به شما آموزش دهد'),
(159, 2, 152, 'پس مرا یاد کنید تا شما را یاد کنم و مرا سپاس بگزارید و با من ناسپاسی نکنید'),
(160, 2, 153, 'ای مؤمنان از صبر و نمازی یاری بجویید که خدا با صابران است‌'),
(161, 2, 154, 'و کسانی را که در راه خدا کشته می‌شوند، مرده نخوانید، بلکه زنده‌اند ولی شما نمی‌دانید'),
(162, 2, 155, 'و همواره شما را به نوعی از ترس و گرسنگی و زیان مالی و جانی و کمبود محصول می‌آزماییم و صابران را نوید بده‌'),
(163, 2, 156, 'کسانی که چون مصیبتی به آنان رسد گویند انا لله و انا الیه راجعون [ما از خداییم و به خدا باز می‌گردیم‌]'),
(164, 2, 157, 'بر اینان درود پروردگارشان و رحمت او باد و اینانند که رهیافته‌اند'),
(165, 2, 158, 'صفا و مروه از شعائر الهی است، چون کسی حج یا عمره بگزارد بر او گناهی نیست که بین آن دو را بپیماید و هرکس به دلخواه کار خیری بجا آورد همانا خداوند قدردان و داناست‌'),
(166, 2, 159, 'کسانی که آیات روشن و رهنمودهایی را که نازل کرده‌ایم، بعد از آنکه در کتاب برای مردم بیان کرده‌ایم، پنهان می‌دارند، خداوند و لعنت‌گران بر آنان لعنت می‌فرستند'),
(167, 2, 160, 'مگر کسانی که توبه کنند و به صلاح بازآیند و [پنهان داشته‌ها را] آشکار کنند که از آنان در می‌گذرم و من توبه‌پذیر مهربانم‌'),
(168, 2, 161, 'کسانی که کفرورزیده‌اند و در کفر مرده‌اند، لعنت خداوند و فرشتگان و مردم جملگی بر آنان است‌'),
(169, 2, 162, 'جاودانه در دوزخ‌اند و از عذابشان کاسته نمی‌شود، و به آنان مهلت ندهند'),
(170, 2, 163, 'و خدای شما خدای یگانه است که خدایی جز او نیست و رحمان و رحیم است‌'),
(171, 2, 164, 'آری در آفرینش آسمانها و زمین و در پی یکدیگر آمدن شب و روز و در کشتی‌ای که برای بهره‌وری مردم در دریا روان است، و آبی که خداوند از آسمان فرو فرستاده است و بدان زمین را پس از پژمردنش زنده داشته و جانورانی که در آن پراکنده است، و در گردش بادها و در ابری که بین آسمان و زمین آرمیده است، برای مردمی که می‌اندیشند، شگفتیهاست‌'),
(172, 2, 165, 'کسانی از مردم هستند که همتایانی برای خداوند قائل می‌شوند و آنها را همچون خدا دوست می‌دارند، حال آنکه مؤمنان خداوند را دوست‌تر دارند، و اگر ستمکاران [ مشرکان‌] به هنگامی که عذاب [روز قیامت‌] را مشاهده کنند، بدانند که هر قدرتی از آن خداوند است و خداوند سخت کیفر است [پشیمان می‌شوند و به حقیقت پی می‌برند]'),
(173, 2, 166, 'آنگاه که پیشوایان [گمراه‌] از پیروان تبری جویند و عذاب را مشاهده کنند و پیوندشان گسسته شود'),
(174, 2, 167, 'و پیروان گویند کاش برای ما بازگشتی بود، تا همان گونه که آنان اکنون از ما تبری جستند، ما نیز از آنان تبری می‌جستیم، بدین‌سان خداوند کردارشان را همچون مایه حسرت بدیشان می‌نمایاند، و از آتش دوزخ بیرون آمدنی نیستند'),
(175, 2, 168, 'ای مردم از آنچه در زمین حلال و پاکیزه است بخورید و از گامهای شیطان پیروی نکنید چرا که او دشمن آشکار شماست‌'),
(176, 2, 169, 'او شما را به بدی و ناشایستی وامی دارد و بر آن می‌دارد که آنچه نمی‌دانید به خداوند نسبت دهید'),
(177, 2, 170, 'و چون به ایشان گفته شود از آنچه خداوند نازل کرده است پیروی کنید، گویند خیر، ما از آنچه نیاکانمان را بر آن یافته‌ایم پیروی می‌کنیم آیا اگر نیاکانشان چیزی نیندیشیده و راهی نیافته باشند [باز هم از آنان پیروی می‌کنند؟]'),
(178, 2, 171, 'داستان کافران همانند داستان کسی است که جانوری را که جز بانگ و ندایی نمی‌شنود، آواز می‌دهد، کر و گنگ و نابینا هستند و از این روی نمی‌اندیشند'),
(179, 2, 172, 'ای مؤمنان از پاکیزه‌های آنچه روزیتان کرده‌ایم بخورید، و اگر تنها خدای را می‌پرستید او را سپاس بگزارید'),
(180, 2, 173, '[بدانید که خداوند] بر شما مردار و خون و گوشت خوک و آنچه نامی جز نام خدا به هنگام ذبحش برده باشند، حرام کرده است، اما اگر کسی درمانده شود، و تجاوز کار و زیاده‌خواه نباشد [و از آنها بخورد] گناهی بر او نیست، چرا که خداوند آمرزگار مهربان است‌'),
(181, 2, 174, 'کسانی که بخشی از کتاب فرو فرستاده خداوند را پنهان می‌دارند و آن را به بهای ناچیز می‌فروشند، جز آتش در درون خویش نمی‌انبارند و خداوند روز قیامت با آنان سخن نخواهد گفت، و پاکیزه‌شان نمی‌دارد و عذابی دردناک [در پیش‌] دارند'),
(182, 2, 175, 'اینان کسانی هستند که گمراهی را به بهای راهیابی و عذاب را به بهای آمرزش خریدند و چه دلیرند بر آتش دوزخ‌'),
(183, 2, 176, 'این از آن است که خداوند کتاب را به حق نازل کرده است، و کسانی که در آن اختلاف ورزیده‌اند، در ستیزه‌ای دور و درازاند'),
(184, 2, 177, 'نیکی آن نیست که رویتان را به سوی مشرق و مغرب آورید، بلکه نیکی آن است که کسی به خداوند و روز بازپسین و فرشتگان و کتابهای آسمانی و پیامبران ایمان داشته باشد و مال را با وجود دوست داشتنش به خویشاوندان و یتیمان و بینوایان و در راه ماندگان و خواهندگان و بردگان ببخشد و نماز را برپا دارد و زکات را بپردازد، و وفاکنندگان به پیمانشان چون پیمان بندند و خوشا شکیبایان به هنگام تنگدستی و ناخوشی و هنگامه کارزار، اینان صادقان و هم اینان پرهیزگارانند'),
(185, 2, 178, 'ای مؤمنان بر شما در مورد کشتگان قصاص مقرر گردیده است، که آزاد در برابر آزاد، برده در برابر برده و زن در برابر زن [قصاص شود] و اگر کسی در حق برادر [دینی‌]اش بخشش کند، بر اوست که خوشرفتاری کند و [قاتل باید دیه را] به نیکی به او بپردازد، این آسانگیری و رحمتی از سوی پروردگارتان است، از آن پس هرکس بی‌روشی کند، عذابی دردناک [در پیش‌] خواهد داشت‌'),
(186, 2, 179, 'ای خردمندان شما را در قصاص زندگانی نهفته است، باشد که تقوا پیشه کنید'),
(187, 2, 180, 'بر شما مقرر گردیده است که چون مرگ یکی از شما فرارسد و مالی باقی گذارد، برای پدر و مادر و خویشاوندان به نیکی وصیت کند و این بر پرهیزگاران مقرر است‌'),
(188, 2, 181, 'پس از آن هرکس که آن [وصیت‌] را پس از شنیدنش دگرگون کند، گناهش بر گردن همان کسانی است که دگرگونش می‌کنند، خداوند شنوای داناست‌'),
(189, 2, 182, 'و هرکس که از وصیت‌کننده‌ای بیم اجحاف یا گناه داشته باشد و بین آنان [ورثه‌] آشتی دهد، گناهی بر او نیست که خداوند آمرزگار مهربان است‌'),
(190, 2, 183, 'ای مؤمنان بر شما روزه مقرر گردیده است همچنانکه بر کسانی که پیش از شما بودند نیز مقرر شده بود، باشد که تقوا پیشه کنید'),
(191, 2, 184, '[روزه‌] روزهایی اندکشمار است و هرکس از شما که بیمار یا در سفر باشد، تعدادی از روزهای دیگر [روزه بگیرد] و برای کسانی که به دشواری آن را تاب می‌آورند کفاره‌ای است که غذای بینوایی است [در ازاء هر یک روزه‌] و هرکس به دلخواه خود خیری [افزون‌] انجام دهد چه بهتر و اگر بدانید روزه گرفتن برای شما بهتر است‌'),
(192, 2, 185, '[ایام روزه‌] ماه رمضان است که قرآن در آن نازل شده است که راهنمای مردم است و آیات روشنگری شامل رهنمودها و جداکننده حق از باطل در بردارد، پس هرکس از شما که در آغاز ماه حاضر [مقیم‌] بود بر اوست که روزه بگیرد و هرکس بیمار یا در سفر بود، باید تعدادی از روزهای دیگر [روزه بدارد]، خداوند برای شما آسانی می‌خواهد و دشواری نمی‌خواهد، تا سرانجام شمار [روزهای روزه‌] را کامل کنید و خداوند را برای آنکه رهنمونیتان کرده است تکبیر بگویید و باشد که سپاسگزار باشید'),
(193, 2, 186, 'و چون بندگانم درباره من از تو پرسش کنند [بگو] من نزدیکم و چون بخواندم دعای دعاکننده را اجابت می‌کنم، پس به فرمان من گردن نهند و به من ایمان آورند، باشد که راه یابند'),
(194, 2, 187, 'آمیزش شما با زنانتان در شبهای روزه بر شما حلال شد، آنان "پیراهن تن" شما و شما "پیراهن تن" آنانید، خداوند می‌دانست که با خود ناراستی می‌کنید، آنگاه از شما درگذشت و شما را بخشید، اینک با آنان در آمیزید و در طلب آنچه خداوند برایتان مقرر داشته برآیید و بخورید و بیاشامید تا آنکه رشته سپید سپیده از رشته سیاه [شب‌] برایتان آشکار شود، سپس روزه را تا شب به پایان برید و هنگامی که در مساجد معتکف هستید با آنان [زنان خود] آمیزش نکنید، این حدود احکام الهی است به آنها نزدیک نشوید [که مبادا تجاوز کنید]، بدین‌سان خداوند آیاتش را برای مردم روشن می‌سازد باشد که تقوا پیشه کنند'),
(195, 2, 188, 'و اموال یکدیگر را به ناحق نخورید، و به داوران با [رشوت از] آن نزدیکی نجویید تا پاره‌ای از اموال مردم را آگاهانه به ناحق بخورید'),
(196, 2, 189, 'از تو در باب هلالهای ماه می‌پرسند، بگو آن وقت نمای مردم و [موسم‌] حج است، و نیکی آن نیست که از پشت خانه‌ها به درون روید، بلکه نیکی آن است که کسی تقوا پیشه کند، و از درها به خانه‌ها درآیید، و از خداوند پروا کنید باشد که رستگار شوید'),
(197, 2, 190, 'و با کسانی که با شما آغاز کارزار می‌کنند در راه خدا کارزار کنید، ولی ستمکار [و آغازگر] نباشید، چرا که خداوند ستمکاران را دوست ندارد'),
(198, 2, 191, 'و هر جا که بر آنان دست یافتید بکشیدشان و آنان را از همانجا که شما را رانده‌اند [مکه‌] برانید، و فتنه شرک بدتر از قتل است، و در مسجدالحرام با آنان [آغاز] کارزار نکنید، مگر آنکه آنان در آنجا با شما کارزار کنند، و چون کارزار کردند بکشیدشان که سزای کافران چنین است‌'),
(199, 2, 192, 'و اگر دست برداشتند، خداوند آمرزگار مهربان است‌'),
(200, 2, 193, 'و با آنان چندان نبرد کنید که فتنه شرک باقی نماند، و دین فقط دین الهی باشد، و چون دست بداشتند، ستم نباید کرد مگر بر ستمگران‌'),
(201, 2, 194, 'ماه حرام در برابر ماه حرام است و حرمت [شکنی‌] ها را باید مقابله به مثل کرد، پس هرکس که ستم بر شما کرد، به همان گونه که بر شما ستم می‌کند، بر او "ستم" روا دارید و از خداوند پروا کنید و بدانید که او با پرهیزگاران است‌'),
(202, 2, 195, 'و در راه خدا هزینه کنید و خودتان را به دستان خویش به کام هلاکت نیندازید، و نیکی کنید که خداوند نیکوکاران را دوست دارد'),
(203, 2, 196, 'و حج و عمره را خاص خداوند به پایان برید، و اگر بازداشته شدید، هر آنچه میسر باشد قربانی کنید، و سرتان را نتراشید، تا قربانی به قربانگاه برسد، آنگاه هرکس از شما که بیمار باشد یا سرش را آزاری رسیده باشد [و سر بتراشد] بر اوست که کفاره‌ای بدهد [که عبارت است از] یک روز روزه یا صدقه یا قربانی، و چون امن و آسایش یافتید، هرکس که از عمره آهنگ حج تمتع کند بر اوست که هر آنچه میسر باشد قربانی کند، و کسی که نیابد بر اوست سه روز روزه در ایام حج و هفت روز پس از بازگشتتان، و این یک ده کامل است، و اینها درباره کسی است که خانواده او اهل مسجدالحرام نباشد و از خداوند پروا کنید و بدانید که خدا سخت کیفر است‌'),
(204, 2, 197, '[موسم‌] حج ماههای معین و معلومی است، و هرکس که در آن ماهها عهده‌دار حج شود، [باید بداند که‌] آمیزش جنسی، و نافرمانی و مجادله در حج روا نیست، و هر کار خیری که انجام می‌دهید خداوند آن را می‌داند، و توشه برگیرید [و بدانید] که بهترین توشه تقوا است، و ای خردمندان از من پروا کنید'),
(205, 2, 198, 'بر شما گناهی نیست که در طلب روزی پروردگارتان برآیید، و چون از عرفات روانه شدید، در مشعرالحرام خداوند را یاد کنید و یادش کنید که شما را که پیشترها از بیراهان بودید فراراه آورد'),
(206, 2, 199, 'سپس از همانجا که مردم روانه می‌شوند شما هم روانه شوید و از خداوند آمرزش بخواهید، که خداوند آمرزگار مهربان است‌'),
(207, 2, 200, 'و چون مناسکتان را به جای آوردید، همان گونه که نیاکانتان را یاد می‌کنید، یا بلکه بهتر و بیشتر از آن خداوند را یاد کنید، کسانی از مردم هستند که می‌گویند پروردگارا به ما در دنیا [چیزی‌] ببخش، و اینان در آخرت بهره‌ای ندارند'),
(208, 2, 201, 'و از ایشان کسانی هستند که می‌گویند پروردگارا به ما در دنیا بهره نیک و در آخرت هم بهره نیک عطا فرما و ما را از عذاب دوزخ درامان بدار'),
(209, 2, 202, 'اینانند که از کار و کردار خویش بهره‌ای می‌برند و خداوند زودشمار است‌'),
(210, 2, 203, 'و خداوند را در روزهای اندکشمار [تشریق‌] یاد کنید و هرکس شتاب کند [و اعمال را در دو روز انجام دهد] بر او گناهی نیست و هرکس تاخیر کند [و در سه روز انجام دهد] بر او هم گناهی نیست چون تقوا ورزد، و از خداوند پروا کنید و بدانید که به نزد او محشور خواهید شد'),
(211, 2, 204, 'و کسی از مردمان هست که سخنش درباره زندگانی دنیا تو را به شگفتی می‌اندازد، و خداوند را بر آنچه در دل دارد گواه می‌گیرد، و حال آنکه بسیار کینه‌توز است‌'),
(212, 2, 205, 'و چون دست یابد می‌کوشد که در این سرزمین فتنه و فساد برپا کند و زراعت و دام را نابود می‌کند، و خداوند فساد را دوست ندارد'),
(213, 2, 206, 'و چون به او گفته شد از خداوند پروا کن، خود بزرگ‌بینی او را به گنهکاری کشاند، جهنم سزای او بس است و چه بد آرامگاهی است‌'),
(214, 2, 207, 'و نیز از مردمان کسی هست که در طلب خشنودی خداوند از سر جان برمی‌خیزد، و خداوند به بندگانش رئوف است‌'),
(215, 2, 208, 'ای مؤمنان همگی از در ایمان و اطاعت [اسلام‌] درآیید و از گامهای شیطان پیروی مکنید که او دشمن آشکار شماست‌'),
(216, 2, 209, 'و اگر بعد از روشنگریهایی که برایتان نازل شده است، کژروی کنید، بدانید که خداوند پیروزمند فرزانه است‌'),
(217, 2, 210, '[گویی‌] جز این انتظار ندارند که خدا و فرشتگان در سایبانهای ابر به دید آنان بیایند، ولی کار [به نحو دیگر] سامان یافته است و سرانجام کارها به خدا باز می‌گردد'),
(218, 2, 211, 'از بنی‌اسرائیل بپرس چه بسیار معجزه آشکار به آنان [نشان‌] دادیم و هرکس نعمت الهی را پس از آنکه نصیبش شد، دگرگون سازد [بداند] که خداوند سخت کیفر است‌'),
(219, 2, 212, 'زندگانی دنیا در چشم کافران آراسته شده است و مؤمنان را ریشخند می‌کنند، و [حال آنکه‌] پرهیزگاران در روز قیامت از آنان برتراند، و خداوند هرکس را بخواهد بی‌حساب روزی می‌بخشد'),
(220, 2, 213, '[در آغاز] مردم امت یگانه‌ای بودند، آنگاه خداوند پیامبران مژده‌آور و هشداردهنده برانگیخت و بر آنان بحق کتابهای آسمانی نازل کرد تا در هر آنچه اختلاف می‌ورزند در میان مردم داوری کند، و در آن اختلاف نکردند مگر کسانی که به آنان [دین و کتاب‌] داده شده بود که پس از آنکه روشنگریها نصیبشان شد از رشک و رقابتی که با هم داشتند [به اختلاف دامن زدند]، آنگاه خداوند به فرمان خویش مؤمنان را در اختلافاتی که داشتند به حق رهنمون شد، و خداوند هرکس را که بخواهد به راه راست هدایت می‌کند'),
(221, 2, 214, 'آیا گمان کرده‌اید به بهشت می‌روید حال آنکه نظیر آنچه بر سر پیشینیان آمد، بر سر شما نیامده است، که تنگدستی و ناخوشی به آنان رسید و تکانها خوردند تا آنجا که پیامبر و کسانی که همراه او ایمان آورده بودند گفتند پس نصرت الهی کی فرا می‌رسد؟ بدانید که نصرت الهی نزدیک است‌'),
(222, 2, 215, 'از تو می‌پرسند چه ببخشند، بگو هر مالی که می‌بخشید [بهتر است‌] به پدر و مادر و خویشاوندان و یتیمان و بینوایان و در راه ماندگان ببخشید و هر خیری که به جای می‌آورید، خداوند از آن آگاه است‌'),
(223, 2, 216, 'بر شما جهاد مقرر شده است، و آن برای شما ناخوشایند است، و چه بسا چیزی را ناخوش داشته باشید و آن به سود شما باشد، و چه بسا چیزی را خوش داشته باشید و آن به زیان شما باشد، و خداوند می‌داند و شما نمی‌دانید'),
(224, 2, 217, 'از تو درباره کارزار در ماه حرام می‌پرسند، بگو کارزار در آن نارواست و بازداشتن از راه خدا و کفرورزیدن به خداوند و بازداشتن از مسجدالحرام [حج‌] و راندن اهل آن، در نزد خداوند نارواتر است، و فتنه شرک بدتر از قتل است، و اینان همچنان با شما کارزار می‌کنند تا اگر بتوانند شما را از دینتان برگردانند، و هرکس از شما که از دینش برگردد و در حال کفر بمیرد، اعمالش در دنیا و آخرت باطل گردیده است، و اینان دوزخی‌اند و جاودانه در آنند'),
(225, 2, 218, 'کسانی که ایمان آورده‌اند و کسانی که در راه خدا هجرت و جهاد کرده‌اند، به رحمت الهی امیدوارند و خداوند آمرزگار مهربان است‌'),
(226, 2, 219, 'از تو درباره شراب و قمار می‌پرسند، بگو در آنها گناهی بزرگ و نیز سودهایی برای مردم هست، ولی گناه آنها بر سودشان می‌چربد، و از تو می‌پرسند که چه چیزی ببخشند، بگو آنچه افزون بر نیاز است، بدین‌سان خداوند آیات خود را برایتان روشن می‌سازد، باشد که اندیشه کنید'),
(227, 2, 220, 'درباره دنیا و آخرت [بیندیشید] و از تو درباره یتیمان می‌پرسند، بگو پرداختن به اصلاح کار آنان بهتر است، و اگر با آنان همزیستی کنید، برادران [دینی‌] شما هستند و خداوند تباهکار را از درستکار باز می‌شناسد، و اگر خدا می‌خواست [کار را] بر شما دشوار می‌گرفت، خداوند پیروزمند فرزانه است‌'),
(228, 2, 221, 'و زنان مشرک را به همسری خود در نیاورید، مگر آنکه ایمان بیاورند، و کنیزی مؤمن بهتر است از زن آزاد مشرک، هرچند که شما را از او خوش آید و [زنان مسلمان را] به همسری مشرکان در نیاورید، مگر آنکه ایمان آورند و برده‌ای مؤمن بهتر از مرد آزاد مشرک است، هر چند که شما را از او خوش آید، اینان دعوتگر به آتش دوزخ‌اند، و خداوند دعوتگر به بهشت و آمرزش به توفیق خویش است، و آیاتش را برای مردم به روشنی بیان می‌دارد تا پند گیرند'),
(229, 2, 222, 'و از تو درباره حیض می‌پرسند، بگو آن مایه رنج است، پس در مدت حیض از زنان کناره بگیرید و با آنان نزدیکی نکنید تا پاک شوند و چون پاکیزه شدند [و غسل کردند] آن گونه که خدا فرموده است با آنان بیامیزید که خداوند آمرزش خواهان و پاکیزگان را دوست دارد'),
(230, 2, 223, 'زنان شما [در حکم‌] کشتزار شما هستند، هرگونه که خواستید به کشتزار خویش درآیید و برای خویش پیش اندیشی کنید، و از خداوند پروا کنید و بدانید که شما به لقای او خواهید رسید، و مؤمنان را بشارت بخش‌'),
(231, 2, 224, 'و خداوند را دستاویز سوگندهای خود نسازید که از کار نیک و پرهیزگاری و آشتی دادن بین مردم، تن بزنید، و خداوند شنوای داناست‌'),
(232, 2, 225, 'خداوند شما را در سوگندهای بیهوده و [بی‌اختیار] بازخواست نمی‌کند، ولی در آنچه بر آن دل می‌نهید [سوگندهای آگاهانه‌] بازخواست می‌کند، و خداوند آمرزگار بردبار است‌'),
(233, 2, 226, 'کسانی که سوگند می‌خورند که با زنانشان نزدیکی نکنند باید چهارماه درنگ کنند، سپس اگر بازگشتند خداوند آمرزگار مهربان است‌'),
(234, 2, 227, 'و اگر آهنگ طلاق کردند [بدانند] که خداوند شنوای داناست‌'),
(235, 2, 228, 'و زنان طلاق داده باید به مدت سه پاکی درنگ کنند و برای آنان روا نیست که اگر به خداوند و روز بازپسین ایمان دارند، آنچه خداوند در رحمهایشان آفریده است، پنهان دارند، و شوهرانشان اگر قصد اصلاح دارند در این [مدت‌] سزاوارتر به بازگرداندن آنها هستند، و زنان را بر مردان حقی است همچنانکه مردان را بر زنان، و مردان را بر آنان به میزانی برتری است، و خداوند پیروزمند فرزانه است‌'),
(236, 2, 229, 'طلاق [رجعی‌] دوبار است، پس از آن یا باید او را به نیکی نگاه داشت، یا به نیکی رها کرد، و بر شما روا نیست که از آنچه به آنان بخشیده‌اید، چیزی بازستانید، مگر آنکه نگران باشند که مبادا احکام الهی را مراعات نکنند، و اگر بیمناک شدید که مبادا احکام الهی را رعایت نکنند، گناهی بر آنان نیست، که زن خود را بازخرد، اینها حدود الهی است، از آنها تجاوز نکنید و هرکس از احکام الهی تجاوز کند، ستمکار است‌'),
(237, 2, 230, 'آنگاه چون زن را طلاق گفت، دیگر بر او حلال نیست، مگر آنکه به همسری جز او شوهر کند، سپس اگر آن مرد او را طلاق داد، بر آنان گناهی نیست که اگر می‌دانند احکام الهی را مراعات خواهند کرد دوباره به همسری یکدیگر درآیند، و این احکام الهی است که برای مردم دانا بیان می‌دارد'),
(238, 2, 231, 'و چون زنان را طلاق گفتید و نزدیک شد که عده‌شان را به سر رسانند، آنگاه یا ایشان را به نیکی نزد خود نگاه دارید، یا به نیکی رها کنید، و هرگز به زور و زیان نگاهشان ندارید که ستم کنید، و هرکس چنین کند بی‌شک بر خود ستم رواداشته است، و آیات الهی را به ریشخند مگیرید و نعمت الهی را بر خود و آنچه از کتاب و حکمت که بر شما نازل کرد و بدان پندتان داد، یاد کنید، و از خداوند پروا کنید و بدانید که خداوند به همه چیز داناست‌'),
(239, 2, 232, 'و چون زنان را طلاق گفتید و عده‌شان را به سر رساندند، آنگاه آنان را باز ندارید که اگر به خیر و خوشی به توافق رسیده بودند، به نکاح همسرانشان درآیند، هرکس از شما که به خداوند و روز بازپسین ایمان داشته باشد، به این پند می‌گیرد، اینها برای شما پاک‌تر و پاکیزه‌تر است و خداوند می‌داند و شما نمی‌دانید'),
(240, 2, 233, 'و مادران فرزندانشان را دو سال کامل شیر دهند، این برای کسی است که بخواهد دوران شیرخوارگی را به حد کمال برساند و خوراک و پوشاک آنان در حد عرف بر عهده پدر فرزند است، هیچ‌کس جز به اندازه توانش مکلف نمی‌شود، هیچ مادری نباید به خاطر فرزندش رنجه شود و نیز نباید پدر به خاطر فرزندش رنجه شود، وارث [و قیم‌] نیز همین حکم را دارد، و اگر به توافق و مشورت، قصد از شیر گرفتن فرزند را کردند، گناهی بر آنان نیست، و اگر خواستید برای فرزندانتان دایه بگیرید، چون به درستی دستمزد آنان را بپردازید، گناهی بر شما نیست، و از خداوند پروا کنید و بدانید که خدا از کار و کردار شما آگاه است‌'),
(241, 2, 234, 'و کسانی از شما که جان می‌سپارند و همسرانی به جا می‌گذارند، [آن زنان‌] باید چهارماه و ده روز درنگ کنند [عده نگهدارند] و چون مدت عده‌شان را به سر رساندند، در آنچه به وجه پسندیده در حق خویش می‌کنند بر آنان گناهی نیست و خداوند از آنچه می‌کنید آگاه است‌'),
(242, 2, 235, 'و نیز در خواستگاری از زنان [مطلقه، یا همسر مرده‌] آنچه به کنایه بر زبان آورید یا در دل نگه دارید، گناهی بر شما نیست، خداوند می‌داند که به زودی از آنان یاد خواهید کرد، ولی در نهان به آنان وعده ندهید، مگر آنکه سخنی سنجیده بگویید، و آهنگ عقد ازدواج نکنید تا عده به سر رسد، و بدانید که خداوند از آنچه در دل دارید آگاه است، از او بر حذر باشید و بدانید که خداوند آمرزگار بردبار است‌'),
(243, 2, 236, 'بر شما گناهی نیست که زنانی را که با آنان نزدیکی نکرده و مهر نیز تعیین نکرده‌اید، طلاق دهید، ولی آنان را [به عطیه‌ای‌] بهره‌مند سازید [عطیه‌ای‌] که به وجه پسندیده بر عهده توانگر به اندازه توانش و بر تنگدست نیز به اندازه توانش نهاده شده است که بر نیکوکاران مقرر است‌'),
(244, 2, 237, 'و اگر پیش از نزدیکی با آنان، خواستید طلاقشان بدهید، و مهری برایشان تعیین کرده بودید، پرداختن نیمه آن بر عهده شماست، مگر آنکه زنان [آن نیمه را] ببخشند، یا کسی که سررشته نکاح در دست اوست [نیمه دیگر را هم‌] ببخشد [و مهر را به تمامی بدهد] و بخشش و بخشایش نزدیکتر به پرهیزگاری است، و بزرگواری را در بین خودتان فراموش نکنید، که خداوند از کار و کردار شما آگاه است‌'),
(245, 2, 238, 'بر نمازهای پنج‌گانه مواظبت کنید، به ویژه نماز میانه، و فروتنانه در برابر خدا [به نماز] بایستید'),
(246, 2, 239, 'ولی اگر بیمناک بودید [نماز خوف را] پیاده یا سواره [به جای آورید] و چون امن و آسایش یافتید، خدا را یاد کنید، که آنچه نمی‌دانستید به شما آموخت‌'),
(247, 2, 240, 'و کسانی از شما که جان می‌سپارند و همسرانی برجای می‌گذارند باید برای همسرانشان وصیت کنند که آنان را تا یک سال بهره‌مند سازند و [از خانه شوهر] بیرون نکنند، ولی اگر [به دلخواه خویش‌] بیرون رفتند، در آنچه در حق خویش، به وجه پسندیده انجام دهند، بر شما گناهی نیست و خدا پیروزمند فرزانه است‌'),
(248, 2, 241, 'و برای زنان طلاق داده باید عطیه‌ای در حد عرف تعیین کرد که بر پرهیزگاران مقرر است‌'),
(249, 2, 242, 'بدین‌سان خداوند آیاتش را برای شما روشن می‌گرداند، تا اندیشه کنید'),
(250, 2, 243, 'آیا [داستان‌] کسانی را که هزاران هزار بودند و از بیم مرگ از خانه و کاشانه خویش به در رفتند، ندانسته‌ای؟ که خداوند به آنان گفت بمیرند [و مردند] سپس زنده‌شان کرد، چرا که خداوند بر مردم بخشش و بخشایش دارد، ولی اکثر مردم سپاس نمی‌گزارند'),
(251, 2, 244, 'در راه خدا کارزار کنید و بدانید که خداوند شنوای داناست‌'),
(252, 2, 245, 'کیست که در راه خدا وامی نیکو دهد تا برای او چندین و چند برابرش کند، و خداوند تنگنا و گشایش [در معیشت مردم‌] پدید می‌آورد، و به سوی او بازگردانده می‌شوید'),
(253, 2, 246, 'آیا [داستان‌] بزرگان بنی‌اسرائیل را پس از موسی ندانسته‌ای؟ آنگاه که به پیامبرشان گفتند برای ما فرمانروائی بگمار تا [به فرمان او] در راه خدا جهاد کنیم، گفت اگر جهاد بر شما مقرر گردد، چه‌بسا کارزار نکنید، گفتند دلیلی ندارد که در راه خدا نجنگیم، و حال آنکه از خانه و کاشانه‌مان رانده [و از زن‌] و فرزندانمان جدا شده‌ایم، آنگاه چون کارزار بر ایشان مقرر گشت، جز اندکی از ایشان، همه رویگردان شدند، و خدا از ستمکاران آگاه است‌'),
(254, 2, 247, 'و پیامبرشان به ایشان گفت خداوند طالوت را به فرمانروایی شما برگماشته است، گفتند چگونه بر ما فرمانروایی کند، حال آنکه ما از او به فرمانروایی سزاوارتریم، و مال و منال چندانی نیز ندارد، گفت خداوند او را بر شما برگزیده است و به او دانایی و توانایی بسیار بخشیده است و خدا فرمانرواییش را به هرکس که بخواهد ارزانی می‌دارد و خدا گشایشگری داناست‌'),
(255, 2, 248, 'و پیامبرشان به ایشان گفت نشانه [صدق و صحت‌] فرمانروایی او این است که تابوت عهد که در آن مایه آرامشی از سوی پروردگارتان و یادگاری از میراث آل موسی و آل هارون هست، و فرشتگانش حمل می‌کنند، به سوی شما می‌آید، اگر مؤمن باشید در این امر عبرتی برای شماست‌'),
(256, 2, 249, 'و هنگامی که طالوت با سپاهیان رهسپار شد، گفت خداوند آزماینده شما به جویباری است، که هرکس از آن بنوشد از من نیست، و هرکس از آن نخورد از من است، مگر آنکه مشتی [آب‌] به دست خویش برگیرد، و جز اندکی از ایشان، همه از آن نوشیدند، و چون او و کسانی که همراه او ایمان آورده بودند، از آن جوی برگذشتند، [بددلان‌] گفتند امروز تاب جالوت و سپاهیانش را نداریم، [اما] کسانی که یقین داشتند که به لقای الهی خواهند رسید گفتند چه‌بسا گروهی اندکشمار که به توفیق الهی بر گروهی انبوه پیروز گردیده است، و خداوند با شکیبایان است‌'),
(257, 2, 250, 'و چون با جالوت و سپاهیانش رو در رو شدند گفتند پروردگارا بر ما [باران‌] صبر فرو ریز و گامهای ما را استوار بدار و ما را بر خدانشناسان پیروز گردان‌'),
(258, 2, 251, 'آنگاه به توفیق الهی آنان را شکست دادند و داوود جالوت را کشت، و خداوند به او فرمانروایی و پیامبری ارزانی داشت و از هر آنچه خواست به او آموخت، و اگر خداوند بعضی از مردم را به دست بعضی دیگر دفع نکند، زمین تباه شود، ولی خداوند بر جهانیان بخشش و بخشایش دارد'),
(259, 2, 252, 'این آیات الهی است که به حق بر تو می‌خوانیمش و تو از فرستادگانی‌'),
(260, 2, 253, 'اینان پیامبرانی هستند که بعضی را بر بعضی برتری بخشیده‌ایم، از آنان کسی هست که خداوند با او سخن گفته است، و بعضی را مرتبتی بلند ارزانی داشته است، و عیسی بن مریم را معجزات آشکار دادیم و او را به روح‌القدس یاری کردیم، و اگر خدا می‌خواست کسانی که پس از ایشان آمدند، پس از روشنگریهایی که برایشان آمد، کشمکش و کارزار نمی‌کردند، ولی اختلاف پیشه کردند، و بعضی از ایشان ایمان آوردند و بعضی کفرورزیدند، و اگر خدا می‌خواست کشمکش و کارزار نمی‌کردند، ولی خداوند هر آنچه اراده کند انجام می‌دهد'),
(261, 2, 254, 'ای مؤمنان، پیش از آنکه روزی فرارسد که در آن نه خرید و فروشی هست و نه دوستی و شفاعتی، از آنچه روزیتان کرده‌ایم، انفاق کنید و [بدانید که‌] کافران همان ستمگرانند'),
(262, 2, 255, 'خداوند کسی است که جز او خدایی نیست، زنده پاینده است، نه غنودن او را فراگیرد نه خواب، آنچه در آسمانها و در زمین است از اوست، کیست که در نزد او، جز به اذن او، به شفاعت برخیزد، گذشته و آینده ایشان را می‌داند، و به چیزی از علم او احاطه و آگاهی نیابند مگر آنچه خود بخواهد، کرسی [علم و قدرت‌] او آسمانها و زمین را فراگرفته است، و نگاهداشت آنها بر او دشوار نیست، و او بزرگوار و بزرگ است‌'),
(263, 2, 256, 'در کار دین اکراه روا نیست، چرا که راه از بیراهه به روشنی آشکار شده است، پس هرکس که به طاغوت کفر ورزد، و به خداوند ایمان آورد، به راستی که به دستاویز استواری دست زده است که گسستی ندارد و خداوند شنوای داناست‌'),
(264, 2, 257, 'خداوند سرور مؤمنان است و آنان را از تاریکیها به سوی روشنایی به در می‌برد، و کافران سرورشان طاغوت است که ایشان را از روشنایی به سوی تاریکیها به در می‌برد، اینان دوزخی‌اند و در آن جاودانه می‌مانند'),
(265, 2, 258, 'آیا [داستان‌] کسی را که از [سرمستی‌] آنکه خداوند به او ملک و مکنت بخشیده بود، با ابراهیم درباره پروردگارش محاجه می‌کرد ندانسته‌ای؟ چون ابراهیم گفت پروردگار من کسی است که زندگی می‌بخشد و می‌میراند، او [نمرود] گفت من [نیز] زندگی می‌بخشم و می‌میرانم، ابراهیم گفت خداوند خورشید را از مشرق برمی‌آورد، تو از مغربش برآور آن کفر پیشه سرگشته [و خاموش‌] ماند، و خداوند مردم ستمکار را هدایت نمی‌کند'),
(266, 2, 259, 'یا [داستان‌] کسی را که بر شهری گذشت که سقفها و دیوارهایش فرو ریخته بود، [در دل‌] گفت چگونه خداوند [اهل‌] این [شهر] را پس از مرگشان زنده می‌کند؟ آنگاه خداوند او را [به مدت‌] صد سال میراند، سپس زنده کرد، [و به او] گفت چه مدت [در این حال‌] مانده‌ای؟ گفت یک روز یا بخشی از یک روز [در این حال‌] مانده‌ام فرمود چنین نیست، صد سال [در چنین حالی‌] مانده‌ای، به خوردنی و نوشیدنی‌ات بنگر که با گذشت زمان دیگرگون نشده است، و به درازگوشت بنگر، و [بدین‌سان‌] تو را مایه عبرت مردم خواهیم ساخت، و به استخوانها بنگر که چگونه فراهمشان می‌نهیم، سپس بر آنها [پرده‌] گوشت می‌پوشانیم، و هنگامی که [حقیقت امر] بر او آشکار شد، گفت می‌دانم که خداوند بر هر کاری تواناست‌'),
(267, 2, 260, 'و آنگاه که ابراهیم گفت پروردگارا به من بنمای که چگونه مردگان را زنده می‌کنی، فرمود مگر ایمان نداری؟ گفت چرا، ولی برای آنکه دلم آرام گیرد، فرمود چهار پرنده بگیر [و بکش‌] و پاره پاره کن [و همه را در هم بیامیز]، سپس بر سر هر کوهی پاره‌ای از آنها را بگذار، آنگاه آنان را [به خود] بخوان [خواهی دید] که شتابان به سوی تو می‌آیند و بدان که خداوند پیروزمند فرزانه است‌'),
(268, 2, 261, 'داستان کسانی که اموالشان را در راه خدا می‌بخشند همچون دانه‌ای است که هفت خوشه می‌رویاند و در هر خوشه‌ای یکصد دانه، و خداوند برای هرکس که بخواهد [پاداش را] چند برابر می‌کند و خداوند گشایشگر داناست‌'),
(269, 2, 262, 'کسانی که اموالشان را در راه خدا می‌بخشند و به دنبال بخشش خود منت و آزاری در میان نمی‌آورند، پاداششان نزد پروردگارشان [محفوظ] است، نه بیمی بر آنهاست و نه اندوهگین می‌شوند'),
(270, 2, 263, 'زبان خوش و پرده‌پوشی بهتر است از صدقه‌ای که آزاری در پی داشته باشد، و خداوند بی‌نیاز بردبار است‌'),
(271, 2, 264, 'ای مؤمنان صدقات خود را با منت نهادن و آزار باطل نکنید، همانند کسی که مالش را برای نمایش دادن به مردم، انفاق می‌کند، و به خداوند و روز بازپسین ایمان ندارد، آری داستان او همچون تخته‌سنگی است که بر آن خاکی باشد و باران سنگینی بر آن ببارد و آن را [همچنان‌] سترون واگذارد، آنان از کار و کردار خویش حاصلی نبرند، و خداوند خدانشناسان را هدایت نمی‌کند'),
(272, 2, 265, 'و داستان کسانی که اموالشان را در طلب خشنودی خداوند و برای تحکیم اعتقاد خویش می‌بخشند، همچون بوستانی است بر پشته‌ای که باران سنگینی بر آن ببارد و میوه‌هایش را دوچندان به بار آورد، و اگر باران سنگین بر آن نبارد [دست کم‌] باران سبک [بر آن ببارد] و خداوند از آنچه می‌کنید آگاه است‌'),
(273, 2, 266, 'آیا کسی از شما دوست دارد که باغی از درختان خرما و انگور داشته باشد و جویباران از فرودست آن جاری باشد، و هرگونه میوه برایش به بار آورد و [او] پیریش فرارسیده و فرزندانی خرد و ناتوان داشته باشد، [و به ناگاه‌] گردبادی که آتشی دربر دارد، بر آن بزند و [سراپا] بسوزد خداوند بدین گونه آیاتش را برای شما روشن می‌گرداند تا اندیشه کنید'),
(274, 2, 267, 'ای مؤمنان از دستاورد خویش و از آنچه از زمین برایتان برآورده‌ایم، پاکیزه‌هایش را انفاق کنید و [حاشا که‌] قصد بخشیدن [چیزهای‌] بد و بیهوده را نکنید، چرا که خود شما نیز ستاننده آن نیستید، مگر آنکه چشم‌پوشی کنید، و بدانید که خداوند بی‌نیاز ستوده است‌'),
(275, 2, 268, 'شیطان شما را از تهیدستی بیم می‌دهد و به ناشایستی [بخل‌] فرمان می‌دهد، و خداوند به شما بخشایش و بخشش خویش را نوید می‌دهد و خدا گشایشگر داناست‌'),
(276, 2, 269, 'او به هرکس که بخواهد حکمت می‌بخشد و به هرکس که حکمت بخشیده باشند، بی‌شک خیر بسیارش داده‌اند، و جز خردمندان کسی پند نمی‌گیرد'),
(277, 2, 270, 'هر بخششی که انجام دهید یا هر نذری که ببندید خداوند آن را می‌داند و ستمکاران یاورانی ندارند'),
(278, 2, 271, 'اگر صدقات خویش را آشکار کنید، نیکوست و اگر پنهانش بدارید و به تهیدستان بدهید برایتان بهتر است، و بخشی از سیئات شما را می‌زداید و خداوند از آنچه می‌کنید آگاه است‌'),
(279, 2, 272, 'هدایت آنان بر عهده تو نیست، بلکه خداوند هرکس را که بخواهد هدایت می‌کند هر مالی که ببخشید به سود خودتان است، و جز در راه رضای الهی انفاق نکنید، و هر مالی که ببخشید، پاداشش به تمامی به شما داده خواهد شد و بر شما ستم نمی‌رود'),
(280, 2, 273, '[صدقات‌] برای تهیدستانی است که خود را وقف [جهاد] در راه خدا کرده‌اند، و نمی‌توانند [جز آن‌] سیر و سفری کنند، و از مناعتی که دارند، هرکس ناآگاه باشد آنان را توانگر می‌انگارد، آنان را به سیمایشان می‌شناسی، از مردم به اصرار چیزی نمی‌خواهند، و هر مالی که ببخشید خداوند از آن آگاه است‌'),
(281, 2, 274, 'کسانی که اموالشان را در شب و روز، پنهانی و آشکارا می‌بخشند، پاداششان نزد پروردگارشان [محفوظ] است، و نه بیمی بر آنهاست و نه اندوهگین می‌شوند'),
(282, 2, 275, 'آنان که ربا می‌خورند [به کار، یا از گور] برنمی‌خیزند مگر مانند برخاستن کسی که شیطانش زده و دچار جنون کرده باشد، این از آن است که می‌گویند دادوستد مانند رباست، حال آنکه خداوند دادوستد را حلال و ربا را حرام کرده است، اینک کسی که پند پروردگارش به او رسیده و دست بردارد، گذشته‌اش را ایرادی نیست و کار او با خداوند است، و کسانی که به این کار بازگردند دوزخی‌اند و جاودانه در آنند'),
(283, 2, 276, 'خداوند ربا را کم و کاست [و بی‌برکت‌] می‌گرداند و صدقات را افزایش [و برکت‌] می‌دهد، و خداوند هیچ کفر[ان‌] پیشه گنهکاری را دوست ندارد'),
(284, 2, 277, 'کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند و نماز خوانده‌اند و زکات پرداخته‌اند، پاداششان نزد پروردگارشان [محفوظ] است و نه بیمی بر آنهاست و نه اندوهگین می‌شوند'),
(285, 2, 278, 'ای کسانی که ایمان آورده‌اید، اگر به واقع مؤمنید، از خداوند پروا کنید و بازمانده ربا را رها کنید'),
(286, 2, 279, 'و اگر نکردید بدانید که به جنگ با خدا و رسولش برخاسته‌اید، و اگر توبه کنید، سرمایه‌های شما از آن شماست، نه ستم می‌کنید و نه بر شما ستم می‌رود'),
(287, 2, 280, 'و اگر [وامدار] تنگدست بود، مهلتی باید، تا گشایشی یابد، و اگر بدانید، بخشیدن آن برایتان بهتر است‌'),
(288, 2, 281, 'و از روزی بترسید که در آن به سوی خداوند بازگردانده می‌شوید، سپس به هرکس جزای هر کاری که کرده است به تمامی داده شود، و بر آنان ستم نرود'),
(289, 2, 282, 'ای مؤمنان اگر وامی [یا معامله نسیه‌ای انجام‌] دادید که سررسید معینی داشت آن را بنویسید، و باید نویسنده‌ای در میان شما دادگرانه آن را بنویسد، و هیچ نویسنده‌ای نباید از نوشتن آن سر باززند، چنانکه [و به شکرانه آنکه‌] خداوند او را آموزش داده است، باید کسی که وام بر عهده اوست املاء کند و او [نویسنده‌] بنویسد و از خداوند [جهان‌]، پروردگارش پروا کند و چیزی از آن فرونگذارد، و اگر کسی که وام بر عهده اوست کم‌خرد یا ناتوان باشد، یا املاء کردن نتواند، باید "ولی" او عادلانه املاء کند، و دو شاهد از مردان خودتان [مسلمانان‌] را بر آن گواه بگیرید، و اگر دو مرد نبود، یک مرد و دو زن از گواهانی که می‌پسندید [انتخاب کنید]، که اگر یکی از آنها فراموش کرد، آن دیگری به یادش آورد، و گواهان چون [برای ادای گواهی‌] خوانده شوند، سر بازنزنند، و ملول نشوید از اینکه آن را چه خرد باشد چه بزرگ، طبق مدتش بنویسید، این کار نزد خداوند درست‌تر و از لحاظ گواهی دادن استوارتر و بی‌شک و شبهه‌تر است، مگر آنکه دادوستد نقدی باشد که بین خود [دستادست‌] برگذار کنید، که در این صورت گناهی بر شما نیست که آن را ننویسید، و چون دادوستد کنید بر آن گواه بگیرید، و نباید نویسنده و گواه به رنج و زیان افتند، و اگر چنین کاری کنید، نافرمانی کرده‌اید، و از خداوند پروا کنید، و خداوند [بدین گونه‌] به شما آموزش می‌دهد، و خدا به هر چیز داناست‌'),
(290, 2, 283, 'و اگر در سفر بودید و نویسنده نیافتید باید گرویی بستانید، و اگر کسی از شما دیگری را امین دانست [و گرویی نگرفت‌] باید کسی که امین دانسته شده، امانت [دین‌] او را بازپس دهد و از خداوند [جهان‌] پروردگارش، پروا کند، و شهادت را کتمان نکنید، و هرکس که آن را کتمان کند، دلش گنهکار است، و خداوند به آنچه می‌کنید داناست‌'),
(291, 2, 284, 'آنچه در آسمانها و زمین است از آن خداوند است، و اگر ما فی‌الضمیر خود را آشکار کنید یا پنهان بدارید، خداوند آن را با شما محاسبه خواهد کرد، آنگاه هرکس را که بخواهد می‌آمرزد و هرکس را که بخواهد به عذاب دچار می‌کند، و خداوند بر هر کاری تواناست‌'),
(292, 2, 285, 'پیامبر به آنچه از سوی پروردگارش بر او نازل شده است، ایمان آورده است و مؤمنان هم، همگی به خداوند و فرشتگانش و کتابهایش و پیامبرانش ایمان آورده‌اند [و می‌گویند] بین هیچ‌یک از پیامبران او فرق نمی‌گذاریم، و می‌گویند شنیدیم و اطاعت کردیم، پروردگارا آمرزش تو را خواهانیم و بازگشت به سوی توست‌'),
(293, 2, 286, 'خداوند هیچ‌کس را جز به اندازه توانش تکلیف نمی‌کند، هرکس هر چه نیکی کند به سود او و هرچه بدی کند به زیان اوست، خدایا اگر [فرمانی را] فراموش یا خطایی کردیم بر ما مگیر، پروردگارا بار گرانی بر عهده ما مگذار، چنانکه بر عهده پیشینیان ما گذارده‌ای، پروردگارا آنچه تاب و توان آن را نداریم بر دوش ما مگذار، ما را ببخش و بیامرز و بر ما رحمت‌آور، تو مولای مایی، ما را بر خدا نشناسان پیروز گردان‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الم الفلاممیم'),
(295, 3, 2, 'خداوند است آنکه جز او خدایی نیست و زنده پاینده است‌'),
(296, 3, 3, 'این کتاب را که همخوان با کتب آسمانی پیشین است به درستی بر تو نازل کرد و تورات و انجیل را پیشتر فروفرستاد'),
(297, 3, 4, 'که راهنمای مردم است، و نیز فرقان را نازل کرد، منکران آیات الهی، عذابی شدید [در پیش‌] دارند و خداوند پیروزمند دادستان است‌'),
(298, 3, 5, 'هیچ چیز در زمین و در آسمان بر خداوند پوشیده نیست‌'),
(299, 3, 6, 'او کسی است که بدانگونه که خواهد شما را در رحمها نقشبندی کند، جز او که پیروزمند فرزانه است خدایی نیست‌'),
(300, 3, 7, 'او کسی است که کتاب را بر تو نازل کرد که بخشی از آن محکمات است که اساس کتاب است، و بخش دیگر متشابهات است، اما کج‌دلان، برای فتنه جویی و در طلب تاویل، پیگیر متشابهات می‌شوند، حال آنکه تاویل آن را جز خداوند و راسخان در علم که می‌گویند به آن ایمان آورده‌ایم، همه از پیشگاه خداوند است نمی‌دانند، و جز خردمندان کسی پند نمی‌گیرد'),
(301, 3, 8, '[می‌گویند] پروردگارا، پس از آنکه هدایتمان کردی، دلهای ما را مگردان، و رحمتی از سوی خویش به ما ارزانی دار که تو بخشنده‌ای‌'),
(302, 3, 9, 'پروردگارا تو گردآورنده مردمان در روزی هستی که شکی در [آمدن‌] آن نیست، چرا که خداوند خلف وعده نمی‌کند'),
(303, 3, 10, 'کافران را اموال و فرزندانشان به هیچ روی از عذاب الهی بازندارد، و اینان همگی هیزم جهنم‌اند'),
(304, 3, 11, 'همانند شیوه آل فرعون و پیشینیان ایشان که آیات ما را دروغ انگاشتند، آنگاه خداوند آنان را به گناهانشان فروگرفت، و خدا سخت کیفر است‌'),
(305, 3, 12, 'بگو به کافران زودا که به زانو درآیید و [سرانجام‌] در جهنم گرد آورده شوید و چه بد جایگاهی است‌'),
(306, 3, 13, 'حقا که در کار و بار دو گروهی که با هم رو در رو شدند مایه عبرتی برای شما بود، گروهی در راه خدا می‌جنگید و دیگری کفر پیشه بود، [کافران مسلمانان را] به چشم خویش دوچندان خویش می‌دیدند، و خداوند هرکس را بخواهد به نصرت خویش یاری می‌دهد، و در این امر دیده‌وران را عبرت است‌'),
(307, 3, 14, 'عشق به خواستنیها از جمله زنان و فرزندان [پسران‌] و مال هنگفت اعم از زر و سیم و اسبان نشاندار و چارپایان و کشتزاران، در چشم مردم آراسته شده است، اینها بهره [گذاری‌] زندگانی دنیاست، و نیک سرانجامی نزد خداوند است‌'),
(308, 3, 15, 'بگو آیا به بهتر از اینها آگاهتان کنم؟ برای پرهیزگاران نزد پروردگارشان بوستانهایی است که جویباران از فرودست آنها جاری است، جاودانه در آنند و جفتهای پاکیزه دارند و از خشنودی خداوند برخوردارند، و خدا بر [احوال‌] بندگانش بیناست‌'),
(309, 3, 16, 'آنان که می‌گویند پروردگارا ما ایمان آورده‌ایم گناهان ما را بیامرز و ما را از عذاب دوزخ در امان بدار'),
(310, 3, 17, 'آنان شکیبایان و راستگویان و فرمانبرداران و بخشندگان و استغفارگران سحرگاهانند'),
(311, 3, 18, 'خداوند که در دادگری استوار است و فرشتگان و عالمان [راستین‌] گواهی می‌دهند که خدایی جز او نیست، آری جز او که پیروزمند فرزانه است، خدایی نیست‌'),
(312, 3, 19, 'دین خداپسند همانا اسلام است و اهل کتاب پس از آنکه علم یافتند، از رشک و رقابتی که با هم داشتند، اختلاف پیشه کردند و هر کس که آیات الهی را انکار کند [بداند که‌] خداوند زودشمار است‌'),
(313, 3, 20, 'و اگر با تو محاجه کردند بگو من و هر آن کس که پیرو من است روی دل به سوی خداوند می‌نهیم، و به اهل کتاب و قوم بی‌کتاب [مشرکان عرب‌] بگو آیا اسلام می‌آورید؟ اگر اسلام آوردند، به راستی راهیاب شده‌اند و اگر رویگردان شدند، همین رساندن پیام بر تو است و خداوند بر [احوال‌] بندگانش بیناست‌'),
(314, 3, 21, 'به کسانی که آیات الهی را انکار می‌کنند و پیامبران را به ناحق می‌کشند و دادگستران را به قتل می‌رسانند، از عذابی دردناک خبر ده‌'),
(315, 3, 22, 'اینان کسانی هستند که اعمالشان در دنیا و آخرت تباه شده است و یاورانی ندارند'),
(316, 3, 23, 'آیا داستان کسانی را که بهره‌ای از کتاب الهی دارند، ندانسته‌ای که چون برای داوری در میانشان، ایشان را به کتاب الهی [تورات‌] بازخوانند، آنگاه گروهی از آنان سر برتافته، رویگردان می‌شوند'),
(317, 3, 24, 'این از آن است که می‌گویند آتش دوزخ جز چند روز اندکشمار به ما نمی‌رسد، و برساخته‌هایشان آنان را در دینشان فریفته کرده است‌'),
(318, 3, 25, 'تا اینان در روزی که شکی در فرارسیدن آن نیست و گردشان آوریم و به هرکس جزای کردارش به تمامی داده شود و بر هیچ کس ستم نرود، چه خواهند کرد؟'),
(319, 3, 26, 'بگو خداوندا، ای فرمانفرمای هستی، به هرکس که خواهی فرمانروایی بخشی و از هرکس که خواهی فرمانروایی بازستانی، و تویی که هرکس را که خواهی گرامی داری و هرکسی را که خواهی خوارکنی، [سررشته‌] خیر به دست توست، تو بر هر کار توانایی‌'),
(320, 3, 27, 'از شب بکاهی و بر روز بیفزایی و از روز بکاهی و بر شب بیفزایی، و زنده را از مرده برآوری و مرده را از زنده، و هرکس را که خواهی بی‌حساب روزی دهی‌'),
(321, 3, 28, 'مؤمنان نباید که کافران را -به جای مؤمناندوست بگیرند، و هرکس چنین کند از [لطف و ولایت‌] خداوند بی‌بهره است، مگر آنکه از آنان به نوعی تقیه کنید، و خداوند شما را از خویش بر حذر می‌دارد، و بازگشت به سوی خداوند است‌'),
(322, 3, 29, 'بگو اگر مافی‌الضمیرتان را پنهان بدارید یا آشکار کنید، خداوند آن را می‌داند و آنچه در آسمانها و زمین است [هم‌] می‌داند، و خداوند بر هر کاری تواناست‌'),
(323, 3, 30, '[یاد کنید از] روزی که هرکس هر آنچه از نیکی و بدی کرده است، حاضر یابد، و آرزو کند که مگر بین او و کار بدش فرسنگها فاصله باشد، و خداوند شما را از خودش بر حذر می‌دارد، و خدا به بندگانش رئوف است‌'),
(324, 3, 31, 'بگو اگر خدا را دوست دارید از من پیروی کنید تا خدا نیز شما را دوست داشته باشد و گناهانتان را بیامرزد، و خداوند آمرزگار مهربان است‌'),
(325, 3, 32, 'بگو از خدا و پیامبر اطاعت کنید، و اگر رویگردان شدند [بدانند که‌] خداوند کافران را دوست ندارد'),
(326, 3, 33, 'خداوند آدم و نوح و آل ابراهیم و آل عمران را بر جهانیان برگزید'),
(327, 3, 34, 'اینان بعضی زاد و رود بعضی دیگر هستند، و خداوند شنوای داناست‌'),
(328, 3, 35, 'و چنین بود که همسر عمران گفت پروردگارا من آنچه در شکم دارم نذر کردم که آزاد از هر قید، دربند خدمت تو باشد، از من بپذیر که تو شنوای دانایی‌'),
(329, 3, 36, 'و چون فرزندش را بزاد گفت پروردگارا من دختر زاده‌ام -و خدا بهتر می‌داند که او چه زاده بود-و پسر مانند دختر نیست، و من او را مریم نام نهادم و او و فرزندانش را از شر شیطان مطرود به پناه تو می‌آورم‌'),
(330, 3, 37, 'آنگاه پروردگارش او را هرچه نیک‌تر پذیرفت، و به نیکی پرورش داد، و زکریا را به سرپرستی او برگماشت، هرگاه که زکریا در آن غرفه بر او وارد می‌شد می‌دید که نزد او رزق و روزی‌ای هست [و] می‌گفت ای مریم اینها از کجا برایت آمده است؟ مریم [در پاسخ‌] می‌گفت آن از نزد خداوند است، خدا هرکس را که بخواهد بی‌حساب روزی می‌بخشد'),
(331, 3, 38, 'آنجا بود که زکریا به [درگاه‌] پروردگارش دعا کرد و گفت پروردگارا به من از نزد خود فرزندی پاک عطا فرما، که تو شنوای دعایی‌'),
(332, 3, 39, 'آنگاه در حالی که در محراب به نماز ایستاده بود، فرشتگان ندایش دادند که خداوند تو را به [فرزندی به نام‌] یحیی بشارت می‌دهد که گواهی‌دهنده بر [حقانیت‌] کلمه الله [عیسی بن مریم‌] و مهتر و خویشتندار [پرهیزنده از زنان‌] و پیامبری از شایستگان است‌'),
(333, 3, 40, 'گفت پروردگارا چگونه مرا پسری باشد، حال آنکه پیری‌ام بالا گرفته است و همسرم نازاست، گفت بدین‌سان خداوند هرچه را خواهد به انجام رساند'),
(334, 3, 41, 'گفت پروردگارا برای من نشانه‌ای قرار ده، گفت نشانه تو این است که سه روز با مردم سخن نگویی مگر به اشاره، و پروردگارت را بسیار یاد کن و شامگاهان و بامدادان او را تسبیح گوی‌'),
(335, 3, 42, 'و یاد کن از آنکه فرشتگان گفتند ای مریم خداوند تو را پذیرفته و پاکیزه داشته و بر زنان جهانیان [هم زمان خویش‌] برتری داده است‌'),
(336, 3, 43, 'ای مریم پروردگارت را اطاعت کن و سجده‌بر و همراه نمازگزاران نماز بگزار'),
(337, 3, 44, 'این از اخبار غیب است که بر تو وحی می‌کنیم و تو آنگاه که قلمها[ی قرعه‌]شان را انداختند که کدامیک مریم را سرپرستی کنند، و هنگامی که ستیزه می‌کردند در نزد ایشان نبودی‌'),
(338, 3, 45, 'چنین بود که فرشتگان گفتند ای مریم خداوند تو را به کلمه خویش که نامش عیسی مسیح فرزند مریم است و در دنیا و آخرت آبرومند و از مقربان [الهی‌] است، بشارت می‌دهد'),
(339, 3, 46, 'او در گهواره [به اعجاز] و در میانسالی [به وحی‌] با مردم سخن می‌گوید و از شایستگان است‌'),
(340, 3, 47, '[مریم‌] گفت پروردگارا چگونه مرا فرزندی باشد، حال آنکه دست هیچ بشری به من نرسیده است، گفت خداوند بدین‌سان هرچه را بخواهد می‌آفریند، [و] چون اراده او به کاری تعلق گرفت، به آن می‌گوید موجود شو، و بی‌درنگ موجود می‌شود'),
(341, 3, 48, 'و به او کتاب و حکمت و تورات و انجیل می‌آموزد'),
(342, 3, 49, 'و پیامبری است به سوی بنی‌اسرائیل که [با آنان می‌گوید] من از سوی پروردگارتان برای شما معجزه‌ای آورده‌ام، که از گل برای شما چیزی به هیئت پرنده می‌سازم و در آن می‌دمم و آن به اذن الهی پرنده‌[ی جاندار] می‌شود، و به اذن الهی نابینای مادرزاد و پیس را بهبود می‌بخشم و مردگان را زنده می‌کنم، و از آنچه می‌خورید و در خانه‌هایتان ذخیره می‌کنید به شما خبر می‌دهم، اگر اهل ایمان باشید در این برای شما مایه آگاهی و عبرت است‌'),
(343, 3, 50, 'همچنین گواهی‌دهنده بر [صدق‌] تورات هستم که پیش روی من است [و برانگیخته شده‌ام‌] که بعضی از آنچه بر شما حرام بوده است، حلال کنم و معجزه‌ای از سوی پروردگارتان برای شما آورده‌ام، پس از خداوند پروا و از من پیروی کنید'),
(344, 3, 51, 'خداوند پروردگار من و پروردگار شماست، او را بپرستید که راه راست همین است‌'),
(345, 3, 52, 'سپس، هنگامی که عیسی به کفر آنان پی برد، گفت یاوران من در راه خداوند کیستند؟ حواریون گفتند ما یاوران [دین‌] الهی هستیم، به خداوند ایمان آورده‌ایم و گواه باش که ما فرمانبرداریم‌'),
(346, 3, 53, 'پروردگارا به آنچه نازل کرده‌ای ایمان آوردیم و از پیامبرت پیروی کردیم پس ما را در زمره گواهان بنویس‌'),
(347, 3, 54, 'و [منکران‌] مکرورزیدند و خداوند هم [در پاسخشان‌] مکر در میان آورد، و خداوند بهترین مکرانگیزان است‌'),
(348, 3, 55, 'و چنین بود که خداوند فرمود ای عیسی من فراگیرنده [روح‌] تو و برکشنده‌ات به سوی خویش و رهایی‌دهنده‌ات از [شر] کافرانم و پیروان تو را تا روز قیامت از کافران برتر می‌دارم، سپس بازگشت شما به سوی من است، و در آنچه اختلاف دارید داوری خواهم کرد'),
(349, 3, 56, 'آنگاه کافران را در دنیا و آخرت به عذابی شدید دچار می‌کنم، و یاورانی ندارند'),
(350, 3, 57, 'ولی کسانی را که ایمان آورده و کارهای شایسته کرده‌اند، پاداشهایشان را به تمامی می‌دهد و خداوند ستمکاران را دوست ندارد'),
(351, 3, 58, 'اینها [همه را] از آیات خویش و قرآن حکمت‌آمیز بر تو می‌خوانیم‌'),
(352, 3, 59, 'شان [آفرینش‌] عیسی برای خداوند همچون شان [آفرینش‌] آدم است که او را از خاک آفرید، سپس به او گفت موجود شو و بی‌درنگ موجود شد'),
(353, 3, 60, 'این حقایق از سوی پروردگار تو است، پس هرگز از دودلان مباش‌'),
(354, 3, 61, 'و هرکس که پس از فرا رسیدن علم [وحی‌] به تو درباره او [عیسی‌] با تو محاجه کند بگو بیایید ما پسرانمان و شما پسرانتان، ما زنانمان و شما زنانتان، ما خویشان نزدیک و شما خویشان نزدیک خود را بخوانیم، سپس [به درگاه خداوند] تضرع کنیم و بخواهیم که لعنت الهی بر دروغگویان فرود آید'),
(355, 3, 62, 'اینها حدیث حق و حقیقت است و خدایی جز خداوند نیست، و خداوند پیروزمند فرزانه است‌'),
(356, 3, 63, 'و اگر رویگردان شدند [بدان که‌] خداوند از تبهکاران آگاه است‌'),
(357, 3, 64, 'بگو ای اهل کتاب بیایید بر سر سخنی که بین ما و شما یکسان است بایستیم که جز خداوند را نپرستیم و برای او هیچ‌گونه شریکی نیاوریم و هیچ‌کس از ما دیگری را به جای خداوند، به خدایی برنگیرد و اگر رویگردان شدند، بگویید شاهد باشید که ما فرمانبرداریم‌'),
(358, 3, 65, 'ای اهل کتاب چرا در حق ابراهیم محاجه می‌کنید، حال آنکه تورات و انجیل پس از او، نازل شده است، آیا اندیشه نمی‌کنید؟'),
(359, 3, 66, 'بهوش باشید گیریم که در آنچه به آن علم دارید محاجه کردید، اما چرا در آنچه به آن علمی ندارید مجادله می‌کنید؟ و خداوند می‌داند و شما نمی‌دانید'),
(360, 3, 67, 'ابراهیم نه یهودی بود و نه مسیحی، بلکه پاکدین فرمانبرداری بود و از مشرکان نبود'),
(361, 3, 68, 'نزدیک‌ترین مردم به ابراهیم همان کسانی هستند که از او پیروی کرده‌اند و این پیامبر و مؤمنان، و خداوند سرور مؤمنان است‌'),
(362, 3, 69, 'گروهی از اهل کتاب خوش دارند که شما را گمراه کنند، ولی جز خودشان را به گمراهی نمی‌کشند و نمی‌دانند'),
(363, 3, 70, 'ای اهل کتاب چرا آیات الهی را آگاهانه انکار می‌کنید؟'),
(364, 3, 71, 'ای اهل کتاب چرا دیده و دانسته حق را به باطل می‌آمیزید و آن را کتمان می‌کنید؟'),
(365, 3, 72, 'و گروهی از اهل کتاب گفتند به آنچه بر مؤمنان [مسلمانان‌] نازل شده است، در آغاز روز ایمان بیاورید، و در پایان روز انکارش کنید، شاید [از دین خود] برگردند'),
(366, 3, 73, 'و جز به کسی که از دین شما پیروی می‌کند، ایمان نیاورید -بگو هدایت، هدایت الهی است[و باور مکنید] که به کسی نظیر آنچه به شما داده شده است داده شود یا [باور مکنید که بتوانند] در پیشگاه خداوند با شما احتجاج کنند بگو چنین فضلی در دست خداوند است، به هرکس که بخواهد می‌بخشد، و خداوند گشایشگر داناست‌'),
(367, 3, 74, 'هرکس را که بخواهد مشمول رحمت خویش می‌گرداند و خدا دارنده بخشش و بخشایش عظیم است‌'),
(368, 3, 75, 'و از اهل کتاب کسی هست که اگر مالی هنگفت به او امانت دهی، به تو بازخواهد داد، و هم از ایشان کسی هست که اگر دیناری به او امانت دهی به تو باز نخواهد داد، مگر آنکه پیوسته بر سرش ایستاده باشی [و باز پس بخواهی‌]، این از آن است که می‌گویند قوم بی‌کتاب [عرب‌] بر ما حقی و حکمی ندارند و آگاهانه به خداوند دروغ می‌بندند'),
(369, 3, 76, 'چنین نیست، بلکه هر کس که به پیمانش وفا کند و پارسایی‌ورزد [بداند که‌] خداوند پرهیزگاران را دوست دارد'),
(370, 3, 77, 'کسانی که عهد الهی و سوگندهای خود را به بهای ناچیزی می‌فروشند، در آخرت بهره‌ای ندارند و خداوند در روز قیامت با آنان سخن نمی‌گوید و [به نظر رحمت‌] به سویشان نمی‌نگرد و پاکیزه‌شان نمی‌دارد و عذاب دردناکی [در پیش‌] دارند'),
(371, 3, 78, 'و از ایشان گروهی هستند که به تقلید و تحریف کتاب آسمانی می‌کوشند تا [آنچه از خود می‌بافند] جزو کتاب آسمانی بشمارید و حال آنکه جزو کتاب آسمانی نیست، و می‌گویند آنها از سوی خداوند [نازل شده‌] است و حال آنکه از سوی خداوند نیست، و آگاهانه به خداوند دروغ می‌بندند'),
(372, 3, 79, 'هیچ بشری را نرسد که خداوند به او کتاب و حکمت و نبوت داده باشد، سپس به مردمان بگوید به جای آنکه بندگان خداوند باشید، بندگان من باشید، بلکه [باید بگوید] شما که کتاب آسمانی را آموزش داده و آموزش یافته‌اید، عالمان ربانی باشید'),
(373, 3, 80, 'و به شما دستور ندهد که فرشتگان و پیامبران را به خدایی برگیرید، آیا پس از آنکه مسلمان گشته‌اید شما را به کفر فرمان می‌دهد؟'),
(374, 3, 81, 'و چنین بود که خداوند از پیامبران پیمان گرفت که چون به شما کتاب و حکمت بخشیدم، آنگاه پیامبری به سویتان آمد که گواهی‌دهنده بر [حقانیت‌] کتب آسمانی شما گردید، بر شماست که به او ایمان آورید و یاریش دهید، آنگاه فرمود آیا گردن نهادید و بر این [امر] با من پیمان بستید؟ گفتند آری گردن نهادیم، فرمود پس هم شما گواهی دهید و هم من همراه با شما، شاهدم‌'),
(375, 3, 82, 'سپس هر کس که بعد از آن رویگردان شد، [بدانید که‌] ایشان از نافرمانانند'),
(376, 3, 83, 'آیا دینی جز دین الهی را می‌جویند، حال آنکه هر آن کس که در آسمانها و زمین است خواه و ناخواه فرمانبردار او هستند، و به سوی او بازگردانده می‌شوند'),
(377, 3, 84, 'بگو به خداوند و آنچه بر ما و آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط نازل شده و آنچه به موسی و عیسی و پیامبران از سوی پروردگارشان داده شده، ایمان آورده‌ایم، و بین هیچ‌یک از آنان فرق نمی‌گذاریم و ما فرمانبردار او هستیم‌'),
(378, 3, 85, 'و هر کس که دینی غیر از اسلام برگزیند، هرگز از او پذیرفته نمی‌شود و او در آخرت از زیانکاران است‌'),
(379, 3, 86, 'چگونه خداوند قومی را هدایت کند که بعد از ایمانشان و پس از اینکه شهادت داده‌اند که پیامبر [اسلام‌] بر حق است و روشنگریهایی برای آنان آمده است، کافر شدند، و خداوند قوم ستمکار را هدایت نمی‌کند'),
(380, 3, 87, 'جزای اینان آن است که لعنت الهی و لعنت فرشتگان و مردم همگی بر آنان است‌'),
(381, 3, 88, 'جاودانه در دوزخ‌اند، و از عذابشان کاسته نمی‌شود و به آنان مهلت داده نمی‌شود'),
(382, 3, 89, 'مگر کسانی که پس از آن، توبه و درستکاری کردند که خداوند آمرزگار مهربان است‌'),
(383, 3, 90, 'کسانی که پس از ایمانشان کفرورزیدند، سپس بر کفر خود افزودند، هرگز توبه‌شان پذیرفته نمی‌شود و اینان گمراهند'),
(384, 3, 91, 'کسانی که کفرورزیده‌اند و در حال کفر مرده‌اند، اگر به اندازه روی زمین، طلا فدیه دهند از ایشان پذیرفته نمی‌شود، اینان عذابی دردناک [در پیش‌] دارند و یاورانی ندارند'),
(385, 3, 92, 'هرگز به نیکی دست نیابید مگر آنکه از آنچه دوست دارید [و عزیز می‌شمارید] ببخشید، و هر آنچه ببخشید خداوند از آن آگاه است‌'),
(386, 3, 93, 'همه خوردنیها برای بنی‌اسرائیل حلال بود، مگر آنچه یعقوب پیش از نزول تورات بر خود حرام کرده بود، بگو اگر راست می‌گویید [و سخن مرا باور ندارید] تورات را بیاورید و بخوانید'),
(387, 3, 94, 'سپس هر آن کس که بعد از آن، بر خداوند دروغ بندد، از ستمکاران است‌'),
(388, 3, 95, 'بگو خداوند راست گفته است، از این روی از آیین ابراهیم که پاکدین بود و از مشرکان نبود، پیروی کنید'),
(389, 3, 96, 'نخستین خانه‌ای که برای [عبادت‌] مردمان نهاده شد، آن است که در مکه [بنا شده‌] است و مبارک و رهنمای جهانیان است‌'),
(390, 3, 97, 'در آن شگفتیهای آشکار از جمله مقام ابراهیم است، و هر کس که وارد آن شود در امان است، و خدای را بر مردم، حج خانه‌[ی کعبه‌] مقرر است، برای کسی که بدان راه تواند برد، و هر کس که کفر [و انکار] ورزد [بداند] که خداوند از جهانیان بی‌نیاز است‌'),
(391, 3, 98, 'بگو ای اهل کتاب چرا آیات الهی را انکار می‌کنید، در حالی که خداوند شاهد [و ناظر] کردار شماست‌'),
(392, 3, 99, 'بگو ای اهل کتاب چرا مؤمنان را از راه خداوند باز می‌دارید و آن را آگاهانه [و به ناحق‌] ناهموار و ناهنجار می‌شمارید، و خداوند از کار و کردار شما غافل نیست‌'),
(393, 3, 100, 'ای مؤمنان اگر از گروهی از اهل کتاب اطاعت کنید، شما را بعد از ایمانتان به کفر برمی‌گردانند'),
(394, 3, 101, 'و چگونه کفرورزید حال آنکه آیات الهی بر شما خوانده می‌شود و رسول خدا در میان شماست، و هر کس به خداوند پناه برد، به راه راست رهنمون شده است‌'),
(395, 3, 102, 'ای مؤمنان از خداوند چنانکه سزاوار پروای اوست، پروا کنید و جز در مسلمانی نمیرید'),
(396, 3, 103, 'و همگی به رشته الهی درآویزید و پراکنده نشوید، و نعمت خداوند را بر خود یاد کنید که دشمنان همدیگر بودید و او میان دلهای شما الفت داد و به نعمت او با هم دوست شدید، و بر لبه پرتگاه آتش بودید و او بازتان رهاند، خداوند بدین‌سان آیات خویش را برای شما به روشنی بیان می‌دارد تا هدایت یابید'),
(397, 3, 104, 'و باید از میان شما گروهی باشند دعوتگر به خیر که به نیکی فرمان دهند و از ناشایستی بازدارند و اینان رستگارانند'),
(398, 3, 105, 'و همانند کسانی نباشید که پس از آنکه روشنگریها فراراهشان آمد، پراکنده شدند و اختلاف یافتند، و اینان عذابی سهمگین [در پیش‌] دارند'),
(399, 3, 106, 'روزی که چهره‌هایی سفید و چهره‌هایی سیاه شود، آنگاه [به‌] سیاه‌رویان [گویند] آیا بعد از ایمانتان روی به کفر آورده بودید؟ پس عذاب را به خاطر کفری که ورزیده بودید بچشید'),
(400, 3, 107, 'و اما سپیدرویان در بهشت رحمت الهی جاویدانند'),
(401, 3, 108, 'این آیات الهی است که به راستی و درستی بر تو می‌خوانیم و خداوند ستمی بر جهانیان نمی‌پسندد'),
(402, 3, 109, 'آنچه در آسمانها و در زمین است از آن خداست و کارها به خداوند بازمی‌گردد'),
(403, 3, 110, 'شما بهترین امتی هستید که برای مردمان پدید آمده‌اند، که به نیکی فرمان می‌دهید و از ناشایستی باز می‌دارید و به خداوند ایمان دارید، و اگر اهل کتاب ایمان آورده بودند برایشان بهتر بود، از آنان بعضی مؤمن و بسیاری نافرمانند'),
(404, 3, 111, 'به شما جز آزاری اندک، زیانی نمی‌رسانند، و اگر آهنگ کارزار با شما کنند به شما پشت می‌کنند، و یاری نمی‌یابند'),
(405, 3, 112, 'هر جا که یافته شوند، دچار خواری‌اند، مگر آنکه به پناه امان خدا و زینهار مردم [مسلمان‌] روند، و سزاوار خشم الهی شدند و دچار ناداری گردیدند، این از آن بود که آیات الهی را انکار می‌کردند و پیامبران را به ناحق می‌کشتند، و از آن بود که سرکشی کردند و از حد درگذشتند'),
(406, 3, 113, 'آنان یکسان و همسان نیستند، از اهل کتاب گروهی درست کردارند که آیات الهی را در دل شب می‌خوانند و سر به سجده می‌نهند'),
(407, 3, 114, 'به خداوند و روز بازپسین ایمان می‌آورند و به نیکی فرمان می‌دهند و از ناشایستی بازمی‌دارند و به نیکوکاری می‌شتابند و اینان از شایستگانند'),
(408, 3, 115, 'و هر کار خیری که انجام دهند هرگز بدون پاداش و سپاس نخواهند ماند، و خداوند از پرهیزگاران آگاه است‌'),
(409, 3, 116, 'کافران را اموال و فرزندانشان به هیچ روی از عذاب الهی باز ندارد، و اینان دوزخی‌اند و جاودانه در آنند'),
(410, 3, 117, 'داستان آنچه در زندگانی دنیا می‌بخشند، همچون داستان بادی است که سرمای سوزانی در بردارد و به کشتزار کسانی که در حق خویش ستم کرده‌اند، بزند و آن را نابود سازد، و خداوند به آنان ستم نکرد بلکه خود بر خویشتن ستم کردند'),
(411, 3, 118, 'ای مؤمنان از غیر خودتان کسانی را به همدلی نگیرید که از هیچ نابکاری در حق شما فروگذار نکنند و به رنج و محنت افتادن شما را خوش دارند و دشمنی از لحن و سخنشان آشکار شده است و آنچه دلهاشان پنهان می‌دارد، بدتر است، آری اگر اندیشه کنید آیات خویش را به روشنی برایتان بیان کرده‌ایم‌'),
(412, 3, 119, 'شمایید که آنها را دوست دارید ولی آنان شما را دوست ندارند، و شما به همه کتابهای آسمانی ایمان دارید، و چون با شما رو در رو شوند، گویند ایمان آورده‌ایم و چون تنها شوند، سرانگشتان خویش را از غیظ شما به دندان می‌گزند، بگو به غیظ خویش بمیرید، خداوند از راز دلها آگاه است‌'),
(413, 3, 120, 'اگر به شما خیری برسد ایشان را اندوهگین کند و اگر به شما بدی و ناخوشی رسد، از آن شاد می‌شوند، و اگر شکیبایی و پارسایی، بدسگالی آنان شما را زیانی نرساند، که خداوند به کار و کردار آنان چیره است‌'),
(414, 3, 121, 'و یاد آر آنگاه که [در جنگ احد] بامدادان از خانه‌ات بیرون شدی و [یکایک‌] مؤمنان را در مواضعی برای کارزار می‌گماردی و خداوند شنوای داناست‌'),
(415, 3, 122, 'یاد آر از هنگامی که دو گروه از شما خواستند هراسی به دل خود راه دهند، و حال آنکه خداوند یاور آنان بود، و مؤمنان باید که بر خداوند توکل کنند'),
(416, 3, 123, 'و خداوند شما را در حالی که ناتوان بودید در [جنگ‌] بدر یاری داد، پس از خداوند پروا کنید مگر سپاس او را به جای آورید'),
(417, 3, 124, 'آنگاه که به مؤمنان گفتی آیا شما را کفایت نمی‌کند که پروردگارتان به سه هزار فرشته که [از آسمان‌] فرو فرستاده یاری‌تان دهد؟'),
(418, 3, 125, 'آری اگر شکیبایی و پارسایی کنید و آنان [دشمنان‌] همین‌گونه شتابان بر سر شما فرود آیند، پروردگارتان شما را با [فرستادن‌] پنج‌هزار فرشته نشانگذار یاری می‌دهد'),
(419, 3, 126, 'و خداوند آن را جز مژده‌ای برای شما نکرد و تا دلهایتان به آن آرام گیرد، و پیروزی جز از سوی خداوند پیروزمند فرزانه نیست‌'),
(420, 3, 127, 'تا بخشی از کافران را براندازد یا تارومار کند، تا نومیدانه بازگردند'),
(421, 3, 128, 'اختیار این کار با تو نیست، چه [خداوند] از آنان درگذرد، چه عذابشان کند، چرا که ستمکارند'),
(422, 3, 129, 'و آنچه در آسمانها و در زمین است از آن خداوند است، هر که را که خواهد می‌آمرزد و هر که را که خواهد عذاب می‌کند و خداوند آمرزگار مهربان است‌'),
(423, 3, 130, 'ای مؤمنان ربا را چندین و چند برابر مخورید و از خداوند پروا کنید تا رستگار شوید'),
(424, 3, 131, 'و از آتشی بترسید که برای کافران آماده شده است‌'),
(425, 3, 132, 'و از خداوند و پیامبر اطاعت کنید باشد که مشمول رحمت شوید'),
(426, 3, 133, 'و برای نیل به آمرزش پروردگارتان و بهشتی که پهنای آن همچند پهنای آسمانها و زمین است و برای پرهیزگاران آماده شده است، بشتابید'),
(427, 3, 134, 'کسانی که در راحت و رنج انفاق می‌کنند و خشم خود را فرو می‌خورند و از مردمان در می‌گذرند و خداوند نیکوکاران را دوست دارد'),
(428, 3, 135, 'و کسانی که چون کار ناشایستی کردند یا بر خویشتن ستم روا داشتند خدای را یاد کنند و برای گناهانشان آمرزش خواهند -و چه کسی جز خداوند گناهان را می‌آمرزدو آنان که آگاهانه در کارهای [ناروایی‌] که کرده‌اند پافشاری و پیگیری نکرده‌اند'),
(429, 3, 136, 'پاداش اینان آمرزش پروردگارشان و بوستانهایی است که از فرودست آن جویباران جاری است و جاودانه در آنند و پاداش نیکوکاران چه خوب است‌'),
(430, 3, 137, 'پیش از شما سنتهایی بوده و گذشته است، در زمین سیر و سفر کنید و سرانجام دروغ انگاران را بنگرید'),
(431, 3, 138, 'این بیانگر [عبرتها] برای مردم و راهنما و پندآموز پرهیزگاران است‌'),
(432, 3, 139, 'و سستی نورزید و اندوهگین نباشید چرا که شما اگر مؤمن باشید، برترید'),
(433, 3, 140, 'اگر آسیبی به شما رسیده باشد، به آن قوم [حریفان شما] نیز آسیبی همانند آن رسیده است، و این روزگار را [برای عبرت‌] در میان مردم می‌گردانیم، و تا خداوند مؤمنان را معلوم بدارد و از شما گواهانی بگیرد و خدا ستمکاران را دوست ندارد'),
(434, 3, 141, 'و تا خداوند مؤمنان را [کامل عیار سازد و] بپالاید و کافران را نیست و ناچیز گرداند'),
(435, 3, 142, 'آیا گمان برده‌اید که به بهشت می‌روید حال آنکه خداوند هنوز جهادگران و شکیبایان شما را معلوم نداشته است؟'),
(436, 3, 143, 'و پیش از رویاروی شدن با مرگ، آرزوی شهادت داشتید، سرانجام آن [معرکه جنگ‌] را دیدید و به آن می‌نگرید'),
(437, 3, 144, 'و محمد جز پیامبری نیست که پیش از او هم پیامبرانی بوده‌اند، آیا اگر او بمیرد یا کشته شود [از پیروی او و عقیده خود] باز می‌گردید؟ و هرکس که [از عقیده خود] بازگردد [بداند که‌] هرگز به خداوند زیانی نمی‌رساند، و زودا که خداوند سپاسگزاران را پاداش دهد'),
(438, 3, 145, 'و هیچ کس جز به اذن الهی نمی‌میرد که این سرنوشتی زماندار است، و هرکس پاداش دنیوی بخواهد، چنین پاداشی به او می‌بخشیم، و هرکس جزای اخروی بخواهد، چنان پاداشی به او خواهیم داد و زودا که خداوند سپاسگزاران را پاداش دهد'),
(439, 3, 146, 'و چه بسیار پیامبرانی که همراه ایشان توده‌های انبوه نبرد کردند و از هر رنجی که در راه خدا دیدند نه سستی‌ورزیدند و نه ضعف و زبونی نشان دادند، و خداوند شکیبایان را دوست دارد'),
(440, 3, 147, 'و سخن ایشان جز این نبود که گفتند پروردگارا گناهان ما و گزافکاریمان را در کارمان بیامرز و گامهای ما را استوار بدار و ما را بر خدانشناسان پیروز گردان‌'),
(441, 3, 148, 'آنگاه خداوند به آنان پاداش دنیوی و پاداش نیک اخروی عطا کرد، و خداوند نیکوکاران را دوست دارد'),
(442, 3, 149, 'ای مؤمنان اگر از کافران پیروی کنید شما را [از عقیده‌تان‌] باز می‌گردانند و آنگاه زیانکار می‌گردید'),
(443, 3, 150, 'آری خداوند مولای شماست و همو بهترین یاور است‌'),
(444, 3, 151, 'زودا که در دل کافران به خاطر شرک‌ورزیدنشان به خدا که هیچ‌گونه حجت و برهانی بر آن نفرستاده است، هراس افکنیم و سرا و سرانجام آنان دوزخ است و جایگاه ستمکاران چه بد است‌'),
(445, 3, 152, 'و آنگاه که به اذن الهی آنان را تارومار می‌کردید، خداوند وعده خویش را به شما تحقق بخشید، تا آنجا که بد دل شدید و پس از آنکه خواسته‌ها و خواستنیها را به شما نمایاند، بگو-مگو در کار آوردید و سرکشی پیشه کردید، چه گروهی از شما دنیا را می‌طلبد و گروهی از شما آخرت را، سپس شما را از آنان منصرف کرد تا شما را [در بوته امتحان‌] بیازماید، و سرانجام از شما درگذشت، و خداوند بر مؤمنان بخشش و بخشایش دارد'),
(446, 3, 153, 'یاد آورید آنگاه که راه خویش در پیش گرفته، و پروای هیچ‌کس را نداشتید و پیامبر در دنبال شما، شما را فرا می‌خواند، و بدین‌سان غم و محنتی نصیب شما کرد، تا سرانجام بر آنچه از دستتان رفت یا بر سرتان آمد، اندوهی نخورید، و خداوند به کار و کردار شما آگاه است‌'),
(447, 3, 154, 'سپس، پس از آن غم و محنت، آرامشی [به صورت‌] خوابی سبک بر شما نازل کرد که گروهی از شما را فراگرفت، و گروهی دیگر هم بودند که فقط در غم جان خویش بودند و اندیشه‌های ناسزاواری درباره خداوند داشتند که همچون پندارهای جاهلیت بود، می‌گفتند آیا ما را در این کار اختیاری هست؟ بگو سررشته همه کارها به دست خداوند است، و در دل خویش چیزهایی می‌نهفتند که بر تو آشکار نمی‌کردند، می‌گفتند اگر ما را در این کار اختیاری بود، در اینجا کشته نمی‌شدیم، بگو اگر در خانه‌های خویش هم بودید، کسانی که کشته شدن در سرنوشتشان نوشته شده بود، [با پای خویش‌] به قتلگاه خویش رهسپار می‌شدند، تا بدین‌سان خداوند نیات شما را بیازماید، و آنچه در دل دارید بپالاید، و خداوند به راز دلها آگاه است‌'),
(448, 3, 155, 'کسانی از شما که روز برخورد دو گروه [غزوه احد] پشت کردند، شیطان به خاطر بعضی از کار و کردارشان آنان را به لغزش کشاند، و خداوند از آنان درگذشت، چرا که خداوند آمرزگار بردبار است‌'),
(449, 3, 156, 'ای مؤمنان مانند کسانی نباشید که کفرورزیدند و در حق دوستانشان هنگامی که به راه افتادند یا جهادگر شدند، گفتند اگر نزد ما می‌ماندند نمی‌مردند و کشته نمی‌شدند، تا خداوند اینچنین این [پندار] را مایه حسرت در دلشان قرار دهد، و خداست که زنده نگه می‌دارد و می‌میراند، و خدا به آنچه می‌کنید بیناست‌'),
(450, 3, 157, 'و اگر در راه خدا کشته شوید، یا درگذرید، آمرزش و رحمتی از خدا بهتر است از آنچه آنان می‌اندوزند'),
(451, 3, 158, 'و اگر درگذرید یا کشته شوید، سرانجام به نزد خداوند محشور می‌گردید'),
(452, 3, 159, 'به لطف رحمت الهی با آنان نرمخویی کردی، و اگر درشت‌خوی سختدل بودی بی‌شک از پیرامون تو پراکنده می‌شدند، پس از ایشان درگذر و برایشان آمرزش بخواه و در [این‌] کار با آنان مشورت کن، و چون عزمت را جزم کردی بر خداوند توکل کن، که خدا اهل توکل را دوست دارد'),
(453, 3, 160, 'اگر خداوند شما را یاری دهد، کسی بر شما پیروز نخواهد شد، و اگر شما را فروگذارد، کیست که شما را پس از او یاری دهد، و مؤمنان باید که تنها بر خداوند توکل کنند'),
(454, 3, 161, 'و هیچ پیامبری را نمی‌سزد که خیانت روا دارد و هرکس خیانت‌ورزد، روز قیامت، وبال خیانتش را همراه آورد، آنگاه به هرکس جزای کار و کردارش به تمامی داده شود و بر آنان ستم نرود'),
(455, 3, 162, 'آیا کسی که جویای خشنودی الهی است همانند کسی است که سزاوار خشم الهی شده است؟ که سرا و سرانجامش جهنم است و چه بد سرانجامی است‌'),
(456, 3, 163, 'آنان را در نزد خداوند درجات [متفاوت‌] است و خداوند به آنچه می‌کنند بیناست‌'),
(457, 3, 164, 'خداوند بر مؤمنان منت نهاد [و نعمت داد] که پیامبری از میان خودشان برانگیخت که آیات او را بر آنان می‌خواند و پاکیزه‌شان می‌دارد و به آنان کتاب و حکمت می‌آموزد، در حالی که در گذشته در گمراهی آشکاری بودند'),
(458, 3, 165, 'آیا هنگامی که به شما مصیبتی رسید که دو چندان آن را [بر دشمنان خود] وارد آورده بودید، می‌گویید این از کجاست؟ بگو این از سوی خودتان است [و] خداوند بر هر کاری تواناست‌'),
(459, 3, 166, 'و آنچه در روز برخورد دو گروه [غزوه احد] به شما رسید، به اذن الهی بود، تا مؤمنان را معلوم بدارد'),
(460, 3, 167, 'همچنین منافقان را، که چون به آنان گفته شد بیایید در راه خدا کارزار کنید یا به دفاع برخیزید، گفتند اگر می‌دانستیم جنگی خواهد بود، حتما از شما پیروی می‌کردیم، اینان در چنین روزی به کفر نزدیک‌تر بودند تا به ایمان، به زبان چیزی می‌گویند که در دلهایشان نیست و خداوند به آنچه نهان می‌دارند، داناتر است‌'),
(461, 3, 168, 'اینان [کسانی هستند] که خود از جهاد تن زدند و در حق دوستانشان گفتند اگر از ما پیروی می‌کردند کشته نمی‌شدند، بگو اگر راست می‌گویید مرگ را از خودتان باز بدارید'),
(462, 3, 169, 'و کسانی را که در راه خدا کشته شده‌اند، مرده مپندار، بلکه اینان زنده‌اند و در نزد پروردگارشان روزی دارند'),
(463, 3, 170, 'از آنچه خداوند از لطف خویش به آنان بخشیده شادمانند، و از احوال کسانی که به دنبالند و هنوز به آنان نپیوسته‌اند خوشوقتند، چرا که نه بیمی بر آنهاست و نه اندوهگین می‌شوند'),
(464, 3, 171, 'و به نعمت و بخشش الهی شادمانند و به اینکه خداوند پاداش مؤمنان را تباه نمی‌گرداند'),
(465, 3, 172, 'کسانی که پس از آنکه به آنان رنج و آسیب رسید به حکم خداوند و پیامبر گردن نهادند برای نیکوکاران و پرهیزگارانشان پاداشی بزرگ [مقرر گردیده‌] است‌'),
(466, 3, 173, 'همان کسانی که چون بعضی به ایشان گفتند که مردمان [مشرکان مکه‌] در برابر شما گرد آمده‌اند از آنان بترسید [به جای ترس و بددلی، این کار] بر ایمانشان افزود و گفتند خداوند ما را بس و چه نیکوکار ساز است‌'),
(467, 3, 174, 'آنگاه به فضل و نعمت الهی باز گشتند و آسیبی به آنان نرسید و همچنان جویای خشنودی الهی بودند و خداوند دارای بخشش و بخشایش بیکران است‌'),
(468, 3, 175, 'آن شیطان است که دوستدارانش را می‌ترساند، اگر مؤمنید از آنان نترسید و از من بترسید'),
(469, 3, 176, 'و کسانی که در کفر می‌کوشند، تو را اندوهگین نکنند، اینان هرگز زیانی به خداوند نمی‌رسانند، خدا می‌خواهد که نصیبی برای آنان در آخرت نگذارد، و عذابی سهمگین [در پیش‌] دارند'),
(470, 3, 177, 'کسانی که کفر را به بهای ایمان خریدند هرگز به خداوند زیانی نمی‌رسانند و عذابی دردناک خواهند داشت‌'),
(471, 3, 178, 'و کافران هرگز مپندارند که چون مهلتشان می‌دهیم به سود آنان است، تنها [از آن روی‌] به آنان مهلت [و میدان‌] می‌دهیم که تا سرانجام بر گناهان خویش بیفزایند و عذاب خفت‌باری [در کمین‌] دارند'),
(472, 3, 179, 'خداوند نمی‌خواهد مؤمنان را به حالی که شما دارید رها کند، مگر آنکه پاک را از پلید جدا کند، و خداوند نمی‌خواهد شما را از غیب آگاه سازد، ولی هرکس را که بخواهد از پیامبرانش بر می‌گزیند، پس به خداوند و پیامبرانش ایمان بیاورید، و اگر ایمان بیاورید و پرهیزگاری کنید، پاداش بزرگی خواهید داشت‌'),
(473, 3, 180, 'و کسانی که در بخشیدن از آنچه خدا از فضل خویش به آنان بخشیده، بخل می‌ورزند مپندارند که این کار به سودشان است، بلکه آن به زیان آنهاست، زودا که مایه بخلشان در روز قیامت [همچون طوق لعنت‌] گردنگیرشان شود، و میراث آسمانها و زمین از آن خداوند است، و خدا به آنچه می‌کنید آگاه است‌'),
(474, 3, 181, 'آری خداوند سخن کسانی را که گفتند خدا فقیر است و ماییم که توانگریم، شنید، سخنشان را و نیز [گناهشان را] که پیامبران را به ناحق کشته‌اند، باز می‌نویسیم و می‌گوییم عذاب آتش دوزخ را بچشید'),
(475, 3, 182, 'این به خاطر کار و کردار پیشین شماست و[گرنه‌] خداوند هرگز در حق بندگان ستمگر نیست‌'),
(476, 3, 183, 'اینان کسانی هستند که می‌گفتند خداوند به ما سفارش کرده است که به هیچ پیامبری ایمان نیاوریم مگر آنکه قربانی‌ای عرضه بدارد که آتش آن را پاک بسوزد، بگو پیش از من پیامبرانی بودند که برای شما معجزاتی آوردند و همین را هم که گفتید عرضه داشتند، اگر راست می‌گویید پس چرا آنان را کشتید؟'),
(477, 3, 184, 'پس اگر تو را دروغ‌زن انگاشتند، بدان که پیامبرانی هم که پیش از تو بودند و معجزات و نوشته‌ها و کتابهای روشنگر آورده بودند، با انکار و تکذیب مواجه شدند'),
(478, 3, 185, 'هر جانداری چشنده [طعم‌] مرگ است و بی‌شک در روز قیامت پاداشتهایتان را به تمامی خواهند داد، آنگاه هرکس که از آتش دوزخ دور داشته و به بهشت درآورده شود، رستگار شده است، و زندگانی دنیا جز مایه فریب نیست‌'),
(479, 3, 186, 'و شما همواره به مال و جان آزموده شوید و از پیشینیان اهل کتاب و مشرکان، سخنان دلازار بسیار خواهید شنید، و اگر شکیبایی و پارسایی ورزید، بدانید که اینها از کارهای سترگ است‌'),
(480, 3, 187, 'و خداوند از اهل کتاب پیمان گرفت که آن را برای مردم بیان کنید و پنهان مدارید، ولی آن را پشت‌سر افکندند و به بهای ناچیزی فروختند، چه بد است آنچه خریدند'),
(481, 3, 188, 'هرگز کسانی را که به آنچه کرده‌اند شادمانی می‌کنند، و دوست دارند برای آنچه نکرده‌اند، ستوده شوند، رسته از عذاب مپندار و عذاب دردناکی [در پیش‌] دارند'),
(482, 3, 189, 'و فرمانروایی آسمانها و زمین از آن خداوند است و خدا بر هر کاری تواناست‌'),
(483, 3, 190, 'در آفرینش آسمانها و زمین و در پی یکدیگر آمدن شب و روز، شگفتیهایی برای خردمندان است‌'),
(484, 3, 191, 'کسانی که خداوند را [در همه احوال‌] ایستاده و نشسته و بر پهلو آرمیده، یاد می‌کنند و در آفرینش آسمانها و زمین می‌اندیشند که پروردگارا این را بیهوده نیافریده‌ای، پاکا که تویی، ما را از عذاب آتش دوزخ در امان بدار'),
(485, 3, 192, 'پروردگارا هرکس را که به دوزخ درآوری، خوار و رسوایش ساخته‌ای و ستمکاران یاورانی ندارند'),
(486, 3, 193, 'پروردگارا شنیدیم که دعوتگری به ایمان فرامی‌خواند که به پروردگارتان ایمان بیاورید، و ما ایمان آوردیم، پروردگارا گناهان ما را بیامرز و سیئات ما را بزدای و ما را در زمره نیکان بمیران‌'),
(487, 3, 194, 'پروردگارا آنچه به پیامبرانت وعده داده‌ای به ما ببخش و ما را روز قیامت خوار و رسوا مگردان، که تو خلف وعده نخواهی کرد'),
(488, 3, 195, 'آنگاه پروردگارشان دعای آنان را اجابت کرد که من عمل هیچ صاحب عملی را از شما چه مرد باشد، چه زن -که همه همانند یکدیگریدضایع [و بی‌پاداش‌] نمی‌گذارم، آری کسانی که هجرت کرده‌اند و از خانه و کاشانه‌هایشان رانده شده‌اند و در راه من آزار دیده‌اند و به جهاد پرداخته‌اند و کشته شده‌اند، سیئاتشان را می‌زدایم و به بوستانهایی که جویباران از فرو دست آن جاری است درشان می‌آورم، این پاداش الهی است و پاداش نیکو نزد خداوند است‌'),
(489, 3, 196, 'گشت و گذار کافران در شهرها تو را نفریبد'),
(490, 3, 197, 'بهره‌مندی ناچیزی است، سپس سرا و سرانجام آنان جهنم است و چه بد آرامگاهی است‌'),
(491, 3, 198, 'اما کسانی که از پروردگارشان پروا داشته‌اند، بوستانهایی دارند که جویباران از فرودست آن جاری است و جاودانه در آنند، این پیشکش الهی است و آنچه در نزد خداوند است برای نیکوکاران بهتر است‌'),
(492, 3, 199, 'و از اهل کتاب کسانی هستند که به خداوند و آنچه برای شما نازل شده و آنچه برای خودشان نازل شده، ایمان دارند، و در برابر خداوند خاشعند و آیات الهی را به بهای ناچیز نمی‌فروشند، پاداش اینان نزد پروردگارشان [محفوظ] است که خداوند زودشمار است‌'),
(493, 3, 200, 'ای مؤمنان شکیبایی و پایداری ورزید و آماده جهاد [و سایر عبادات‌] باشید، و از خداوند پروا کنید، باشد که رستگار شوید');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'ای مردم از پروردگارتان پروا کنید، همو که شما را از یک تن یگانه بیافرید و همسر او را هم از او پدید آورد و از آن دو مردان و زنان بسیاری پراکند، و از خدایی که به نام او پیمان می‌بندید یا سوگند می‌دهید، همچنین از گسستن پیوند خویشاوندان پروا کنید، چرا که خداوند [ناظر و] نگاهبان شماست‌'),
(495, 4, 2, 'و اموال یتیمان را به ایشان برگردانید و پلید را جانشین پاک مسازید و اموال ایشان را با اموال خویش مخورید که این گناه بزرگی است‌'),
(496, 4, 3, 'و اگر بیمناکید که در حق دختران یتیم به عدل و انصاف رفتار نکنید، در این صورت از زنانی که می‌پسندید [یک یا] دو و [یا] سه و [یا] چهار تن را به همسری درآورید، و اگر می‌ترسید که مبادا عادلانه رفتار نکنید، فقط به یک تن، یا ملک یمین [کنیز] اکتفا کنید، این نزدیکتر است به اینکه ستم نکنید'),
(497, 4, 4, 'و مهر زنان را به ایشان با خوشدلی بدهید، ولی اگر به طیب خاطر خویش، چیزی از آن را به شما بخشیدند، آن را نوشین و گوارا بخورید'),
(498, 4, 5, 'و به کم‌خردان اموالشان را که خداوند شما را به سرپرستی آن گماشته است ندهید و از [سود و بهره‌] آن خوراک و پوشاکشان دهید و با آنان به زبان خوش سخن بگویید'),
(499, 4, 6, 'و یتیمان را بیازمایید تا هنگامی که قابلیت زناشویی پیدا کنند، آنگاه اگر در آنان کاردانی سراغ گرفتید اموالشان را به آنان برگردانید و آن را به اسراف و پیشاپیش از بیم آنکه مبادا بالغ و بزرگ شوند نخورید و هرکس توانگر بود خویشتنداری کند و هرکس تنگدست باشد، در حد عرف از آن بخورد، و هرگاه که اموالشان را به آنان برگرداندید، بر آنان گواه بگیرید که خداوند حسابرسی را کافی است‌'),
(500, 4, 7, 'مردان را از آنچه پدر و مادر و خویشاوندان باقی می‌گذارند، بهره‌ای است، همچنین زنان را از آنچه پدر و مادر و خویشاوندان باقی می‌گذارند، بهره‌ای است، چه کم باشد چه بسیار، چنین بهره‌ای معین [و از جانب خداوند] مقرر است‌'),
(501, 4, 8, 'و چون خویشاوندان [غیر از ورثه‌] و یتیمان و بینوایان بر سر تقسیم [ترکه‌] حضور یابند، از آن به چیزی ایشان را بهره‌مند سازید و با ایشان به زبان خوش سخن بگویید'),
(502, 4, 9, 'و کسانی که اگر [درگذرند] فرزندانی خرد و ناتوان از خود باقی گذارند، برای ایشان نگرانند، باید که [در کار دیگران هم‌] از خداوند پروا کنند و سنجیده سخن بگویند'),
(503, 4, 10, 'کسانی که اموال یتیمان را به ستم می‌خورند، جز این نیست که شکمشان را از آتش می‌انبارند و به آتش سوزان [جهنم‌] در خواهند آمد'),
(504, 4, 11, 'خداوند به شما چنین سفارش می‌کند که در مورد فرزندانتان، پسر را برابر بهره دو دختر باشد، و اگر [وارثان، دو دختر یا] بیشتر از دو دختر باشند، سهم آنان دو سوم ترکه است، و اگر فقط یک دختر باشد، سهم او نصف ترکه است، و اگر [متوفی‌] فرزند داشته باشد، برای پدر و مادرش هریک، یک ششم ترکه است، و اگر فرزند نداشته باشد، و میراث بر او فقط پدر و مادرش باشند، در این صورت برای مادرش یک سوم [و برای پدر دو سوم‌] است، و اگر برادرانی داشته باشد، سهم مادرش یک ششم است، [این تقسیم میراث‌] پس از عمل به وصیتی که کرده است یا پرداخت وامی که دارد [انجام می‌گیرد] شما نمی‌دانید که پدرانتان یا پسرانتان کدامیک بیشتر به شما سود می‌رسانند، [این احکام‌] فریضه الهی است، و خداوند دانای فرزانه است‌'),
(505, 4, 12, 'و اگر زنان شما فرزندی نداشته باشند، نیمی از ترکه، و اگر فرزند داشته باشند، یک چهارم ترکه، پس از [عمل به‌] وصیتی که کرده‌اند یا [پرداخت‌] وامی که دارند، از آن شماست، و اگر فرزندی نداشته باشید یک چهارم، و اگر فرزندی داشته باشید یک هشتم از ترکه شما، پس از [عمل به‌] وصیتی که کرده‌اید یا [پرداخت‌] وامی که دارید، از آن ایشان [همسران‌] است، و اگر مردی یا زنی که از او ارث می‌برند کلاله [بی‌فرزند و بی‌پدر و مادر] باشد و برادر یا خواهری داشته باشد، برای هر یک از آنان یک ششم [ترکه‌] است، و اگر [خویشاوندان کلاله‌] بیش از این [دو] باشند، در آن صورت همه آنان پس از قبول وصیت و عمل به آن یا پرداخت وام [متوفی‌] در یک سوم شریکند، و باید که وصیت [به حال ورثه‌] زیان رسان [مازاد بر ثلث‌] نباشد، این سفارش الهی است و خداوند دانای بردبار است‌'),
(506, 4, 13, 'این احکام الهی است و هرکس از خداوند و پیامبر او اطاعت کند، او را به بوستانهایی که جویباران از فرودست آن جاری است، در می‌آورد که جاودانه در آنند، و آن رستگاری بزرگی است‌'),
(507, 4, 14, 'و هرکس از فرمان خداوند و پیامبر او سرپیچد و از حدود مقرر او تجاوز کند، او را به آتشی درآورد که جاودانه در آن است و عذابی خفت‌بار [در پیش‌] دارد'),
(508, 4, 15, 'و از زنان شما کسانی که مرتکب ناشایستی [زنا] می‌شوند، باید بر آنان چهار شاهد از خود [مردان مسلمان‌] بگیرید، آنگاه اگر شهادت دادند، آنان را در خانه‌ها محبوس نگه دارید، تا مرگ فراگیردشان، یا خداوند راهی برایشان مقرر کند'),
(509, 4, 16, 'و کسانی را که مرتکب آن [ناشایستی، زنا] شوند برنجانید، آنگاه اگر توبه کردند و به صلاح آمدند، از آنان دست بردارید که خداوند توبه‌پذیر مهربان است‌'),
(510, 4, 17, 'جز این نیست که خداوند توبه کسانی را می‌پذیرد که از روی نادانی کار ناشایستی می‌کنند، سپس به زودی توبه می‌کنند، خداوند از اینان در می‌گذرد و خدا دانای فرزانه است‌'),
(511, 4, 18, 'و توبه کسانی که مرتکب کارهای ناشایست می‌شوند، و سرانجام چون مرگ هریکشان فرارسد گویند اینک توبه کردم، پذیرفته نیست، و نیز توبه کسانی که در حال کفر می‌میرند، برای اینان عذابی دردناک آماده ساخته‌ایم‌'),
(512, 4, 19, 'ای مؤمنان بر شما روا نیست که زنان را بر خلاف میلشان میراث برید [و آنان را به اجبار در حباله نکاح خود نگاه دارید] و بر آنان سختگیری نکنید تا بخشی از چیزی را که به آنان بخشیده‌اید پس بگیرید، مگر آنکه مرتکب ناشایستی آشکاری شوند، و با آنان خوشرفتاری کنید و اگر آنان را خوش ندارید [بدانید که‌] چه بسا چیزی را ناخوش بدارید و خداوند در آن خیر بسیار نهاده باشد'),
(513, 4, 20, 'و اگر می‌خواهید همسری را جانشین همسری [زن پیشین خود] کنید و به یکی از آنها مال هنگفتی [به عنوان مهر] داده بودید، چیزی از آن را باز پس نگیرید، آیا با بهتان و گناهی آشکار، آن را باز پس می‌گیرید؟'),
(514, 4, 21, 'و چگونه آن را باز پس می‌گیرید، و حال آنکه با همدیگر خلوت [و همخوابگی‌] کرده‌اید و آنان از شما پیمان استواری گرفته‌اند'),
(515, 4, 22, 'و با زنانی که پدرانتان ازدواج کرده‌اند، ازدواج نکنید، مگر آنچه در گذشته رخ داده باشد، چرا که این کار ناشایست و منفور و بی‌روشی است‌'),
(516, 4, 23, '[نکاح‌] مادرانتان و دخترانتان و خواهرانتان و عمه‌هایتان و خاله‌هایتان و دختران برادرتان و دختران خواهرتان و مادران [دایگانی‌] که شما را شیر داده‌اند و خواهران رضاعی‌تان و مادران زنانتان و دختران زنانتان [دختر اندرهایی‌] که در دامان [تربیت و] نگهداری شما هستند -در صورتی که از زنانی باشند که با آنان آمیزش جنسی کرده‌اید، و اگر آمیزش جنسی نکرده باشید، گناهی بر شما نیستو [عروسان یا] همسران پسرانتان -پسرانی که از صلب خودتان هستندبر شما حرام گردیده است، و [همچنین‌] جمع بین دو خواهر [در یک زمان‌]، مگر آنچه در گذشته رخ داده باشد، که خداوند آمرزگار مهربان است‌'),
(517, 4, 24, 'و [همچنین‌] زنان شوهردار، مگر ملک یمین [کنیز]تان، این فریضه الهی است که بر شما مقرر گردیده است، و فراتر از اینان بر شما حلال گردیده است که با صرف اموال خویش، پاکدامنانه و نه پلیدکارانه، آن را به دست آورید، و آنانکه [به صورت متعه‌] از آنان برخوردار شوید، باید مهرهایشان را که بر عهده شما مقرر است، بپردازید، و در آنچه پس از تعیین [در مدت یا مهر تغییر دهید یا] به توافق رسید، گناهی بر شما نیست، که خداوند دانای فرزانه است‌'),
(518, 4, 25, 'و هرکس از شما که توانائی مالی نداشته باشد که با زنان آزاد مؤمن ازدواج کند [بهتر است‌] با ملک یمینهایتان، از کنیزان مؤمن، ازدواج کند، و خداوند به ایمان شما داناتر است، همه از یکدیگرید، پس با اجازه سرپرستشان با آنان ازدواج کنید و مهرهایشان را به وجه پسندیده به ایشان بدهید [در حالی که‌] پاکدامنان غیر پلیدکار باشند، و دوست‌گیران نهانی نباشند، آنگاه چون ازدواج کردند، اگر مرتکب ناشایستی [زنا] شدند، مجازات آنان به اندازه نصف مجازاتی است که بر زنان آزاد مقرر است، این حکم [ازدواج با کنیزان‌] برای کسی از شماست که از آلایش گناه بترسد، و شکیبائی [و پاکدامنی‌] پیشه‌کردن برایتان بهتر است، و خداوند آمرزگار مهربان است‌'),
(519, 4, 26, 'خداوند می‌خواهد [احکام خویش را] برای شما روشن سازد و شما را به سیره و سنت [حسنه‌] پیشینیان راهبر شود و از شما درگذرد و خداوند دانای فرزانه است‌'),
(520, 4, 27, 'و خداوند می‌خواهد از شما درگذرد و کسانی که پیرو شهواتند می‌خواهند که کجروی بزرگی پیشه کنند'),
(521, 4, 28, 'خداوند می‌خواهد بر شما آسان گیرد و انسان ناتوان آفریده شده است‌'),
(522, 4, 29, 'ای مؤمنان اموال همدیگر را به ناروا مخورید، مگر آنکه دادوستدی به توافق خودتان باشد، و خودتان را نکشید چرا که خداوند با شما مهربان است‌'),
(523, 4, 30, 'و هرکس از روی سرکشی و ستم چنین کند، او را به آتشی در خواهیم آورد و این امر بر خداوند آسان است‌'),
(524, 4, 31, 'اگر از گناهان بزرگی که از آن نهی شده‌اید، پرهیز کنید، سیئات شما را می‌زداییم و شما را به جایگاهی شایسته درمی‌آوریم‌'),
(525, 4, 32, 'و چیزی را که خداوند بدان بعضی از شما را بر دیگری برتری بخشیده است، آرزو مکنید، مردان را از کار و کردار خویش بهره‌ای و زنان را [نیز] از کار و کردار خویش بهره‌ای معین است و [هر چه خواهید] از فضل الهی بخواهید، و خداوند از همه چیز آگاه است‌'),
(526, 4, 33, 'برای همه در آنچه پدر و مادر و خویشاوندان نزدیک به جا گذاشته‌اند، میراث برانی قرار داده‌ایم، و نیز کسانی که با آنان پیمان بسته‌اید، بهره‌شان را [از ارث‌] بدهید، که خداوند بر همه چیزی گواه است‌'),
(527, 4, 34, 'مردان باید بر زنان مسلط باشند چرا که خداوند بعضی از انسانها را بر بعضی دیگر برتری بخشیده است، و نیز از آن روی که مردان از اموال خویش [برای زنان‌] خرج می‌کنند، زنان شایسته آنانند که مطیع و به حفظ الهی در نهان خویشتندار هستند، و زنانی که از نافرمانیشان نگرانید، باید نصیحتشان کنید و [سپس‌] در خوابگاهها از آنان دوری کنید [و سپس اگر لازم افتاد] آنان را ترک کنید آنگاه اگر از شما اطاعت کردند، دیگر بهانه جویی [و زیاده‌روی‌] نکنید، خداوند بلندمرتبه بزرگوار است‌'),
(528, 4, 35, 'و اگر از بالا گرفتن اختلاف بین آنان بیمناک بودید، داوری از خانواده مرد و داوری از خانواده زن تعیین کنید، که چون به اصلاح [ذات‌البین‌] کوشند، خداوند بین آنان الفت و آشتی برقرار خواهد ساخت، چرا که خداوند دانا و آگاه است‌'),
(529, 4, 36, 'و خداوند را بپرستید و چیزی را برای او شریک نیاورید و به پدر و مادر و به خویشاوندان و یتیمان و بینوایان و همسایه خویش و همسایه بیگانه و همنشین و در راه مانده و ملک یمین خود نیکی کنید، [و بدانید] که خداوند کسی را که متکبر فخرفروش باشد، دوست ندارد'),
(530, 4, 37, '[همان‌] کسانی که بخل می‌ورزند و مردمان را به بخل وامی‌دارند و آنچه خداوند از فضل خویش به آنان بخشیده پنهان می‌دارند، و برای کافران عذابی خفت‌بار آماده ساخته‌ایم‌'),
(531, 4, 38, 'و کسانی که اموالشان را برای نمایش در چشم مردمان، می‌بخشند و به خداوند و روز بازپسین ایمان ندارند، و کسی که شیطان همنشین او باشد، چه بد همنشینی دارد'),
(532, 4, 39, 'و آنان را چه زیان داشت اگر به خداوند و روز بازپسین ایمان می‌آوردند و از آنچه روزیشان داده بود، می‌بخشیدند؟ و خداوند به [احوال‌] آنان داناست‌'),
(533, 4, 40, 'خداوند به اندازه ذره‌ای نیز ستم نمی‌کند، و اگر حسنه‌ای باشد [و از کسی سر بزند] به آن دو چندان پاداش می‌دهد و از پیشگاه خود [به بندگان‌] پاداش بیکران می‌بخشد'),
(534, 4, 41, 'و حال [آنان‌] چگونه باشد آنگاه که از هر امتی شاهدی به میان آوریم و تو را گواه بر اینان آوریم‌'),
(535, 4, 42, 'در چنین روز کسانی که کفرورزیده و از پیامبر سر پیچیده‌اند آرزو کنند کاش زمین [آنان را فرو برد و] بر آنان هموار گردد و سخنی را از خداوند پنهان نمی‌توانند داشت‌'),
(536, 4, 43, 'ای مؤمنان در حال مستی به نماز نزدیک نشوید، تا زمانی که بدانید که چه می‌گویید، همچنین هنگامی که جنب هستید نیز به نماز [و مسجد] نزدیک نشوید، مگر آنکه راهگذر باشید تا زمانی که غسل کنید، و اگر بیمار یا مسافر باشید، یا یکی از شما از موضع قضای حاجت بازگشت، یا با زنان تماس [جنسی‌] گرفتید، آنگاه آب نیافتید [باید] بر خاکی پاک تیمم کنید و چهره‌ها و دستهایتان را به آن مسح کنید، که خداوند بخشاینده مهربان است‌'),
(537, 4, 44, 'آیا احوال کسانی را که بهره‌ای از کتاب آسمانی دارند ندانسته‌ای که خریدار گمراهی‌اند و می‌خواهند که شما نیز از راه بیراه باشید'),
(538, 4, 45, 'و خداوند به دشمنان شما [از شما] آگاه‌تر است و خدا یار و یاور شما بس‌'),
(539, 4, 46, 'از یهودیان کسانی هستند که کلمات را جا به جا می‌کنند و از راه زبان بازی و برای طعنه‌زدن به دین [اسلام‌] [به زبان‌] می‌گویند شنیدیم و [در دل می‌گویند] نپذیرفتیم و بشنو سخن ما و نشنیده بگیر و "راعنا" [را به شیوه نادرستی‌] می‌گویند، حال آنکه اگر می‌گفتند شنیدیم و اطاعت کردیم و سخن ما را بشنو و در کار ما بنگر، برای آنان بهتر و استوارتر بود، ولی خداوند آنان را به سبب کفرشان دچار لعنت خویش ساخته و جز اندکی ایمان نمی‌آورند'),
(540, 4, 47, 'ای اهل کتاب، پیش از آنکه چهره‌هایی را مسخ و محو کنیم و آنها را واپس‌گردانیم، یا ایشان را همانند اصحاب سبت [حرمت شکنان شنبه‌] دچار لعنت کنیم، به آنچه فرو فرستاده‌ایم و همخوان کتاب شماست، ایمان آورید [و بدانید] که امر الهی انجام پذیر است‌'),
(541, 4, 48, '[بدانید که‌] خداوند این را که برایش شریک قائل شوند، نمی‌بخشد، و هر گناهی را که فروتر از آن است، برای کسی که بخواهد می‌بخشد، و هرکس که به خدا شرک‌ورزد، در حقیقت گناه عظیمی را به گردن گرفته است‌'),
(542, 4, 49, 'آیا احوال کسانی را که خود را نیک و پاک می‌شمارند ندانسته‌ای؟ چنین نیست، بلکه خداوند هرکس را که بخواهد نیک و پاک می‌دارد، و به اندازه ذره ناچیزی نیز بر آنان ستم نمی‌رود'),
(543, 4, 50, 'بنگر که چگونه بر خداوند دروغ می‌بندد و همین گناه به این آشکاری بس‌'),
(544, 4, 51, 'آیا احوال کسانی را که بهره‌ای از کتاب آسمانی دارند، ندانسته‌ای که به "جبت" و "طاغوت" ایمان می‌ورزند و درباره کافران می‌گویند که اینان از مؤمنان راه یافته‌ترند'),
(545, 4, 52, 'اینان کسانی هستند که خداوند لعنتشان کرده است و کسی که خدا لعنتش کرده باشد، یاوری برایش نخواهی یافت‌'),
(546, 4, 53, 'یا مگر آنان را بهره‌ای از فرمانروایی است که باز هم به اندازه ذره ناچیزی به مردم نمی‌بخشند'),
(547, 4, 54, 'یا مگر به خاطر آنچه خداوند از لطف خویش [به بعضی از] مردمان بخشیده به آنان رشک می‌برند؟ ما به خاندان ابراهیم کتاب آسمانی و نبوت داده‌ایم و به آنان فرمانروایی بزرگی بخشیده‌ایم‌'),
(548, 4, 55, 'پس برخی از آنان به آن ایمان آوردند، و دسته‌ای از ایشان از آن اعراض کردند و [اینان را] آتش افروخته جهنم بس است‌'),
(549, 4, 56, 'کسانی که آیات ما را انکار کرده‌اند، به آتشی در خواهیمشان آورد که هر چه پوستهایشان پخته [و فرسوده‌] شود به جای آنها پوستهای دیگر آوریم، تا عذاب را بچشند، خداوند پیروزمند فرزانه است‌'),
(550, 4, 57, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، به بوستانهایی در خواهیمشان آورد که جویباران از فرودست آن جاری است و جاودانه در آنند، و در آنجا جفتهایی پاکیزه دارند و آنان را به سایه ساری سایه‌گستر در خواهیم آورد'),
(551, 4, 58, 'خداوند به شما فرمان می‌دهد که امانتها را به صاحبان آنها بازگردانید، و چون بین مردم داوری می‌کنید دادگرانه داوری کنید، خداوند به امری نیک پندتان می‌دهد، که خداوند شنوای بیناست‌'),
(552, 4, 59, 'ای مؤمنان از خداوند و پیامبر و اولوالامرتان اطاعت کنید، و اگر به خداوند و روز بازپسین ایمان دارید، هرگاه در امری [دینی‌] اختلاف‌نظر یافتید، آن را به [کتاب‌] خدا و [سنت‌] پیامبر عرضه بدارید که این بهتر و نیک انجام‌تر است‌'),
(553, 4, 60, 'آیا داستان کسانی را که گمان می‌برند به آنچه بر تو نازل شده و به آنچه پیش از تو نازل شده، ایمان دارند، ندانسته‌ای که می‌خواهند به طاغوت داوری برند و حال آنکه به آنان دستور داده شده است که به آن کفر بورزند و شیطان می‌خواهد آنان را به گمراهی دور و درازی بکشاند'),
(554, 4, 61, 'و چون به آنان گفته شود به سوی [قرآن‌] فرو فرستاده الهی و پیامبر آیید، منافقان را می‌بینی که به کلی از تو اعراض می‌کنند'),
(555, 4, 62, 'چگونه باشد آنگاه که به خاطر کار و کردار پیشینشان مصیبتی به آنان رسد، آنگاه به نزد تو آمده به خدا سوگند می‌خورند که جز نیکی و همراهی قصدی نداشتیم‌'),
(556, 4, 63, 'اینان کسانی هستند که خداوند ما فی‌الضمیرشان را می‌داند، پس از ایشان روی بگردان و پندشان ده و به آنان سخنی رسا که در دلشان مؤثر افتد بگو'),
(557, 4, 64, 'و هیچ پیامبری نفرستادیم مگر آنکه به توفیق الهی از او فرمانبرداری شود، و اگر هنگامی که به خویشتن ستم کردند به نزد تو می‌آمدند و از خداوند آمرزش می‌خواستند و پیامبر هم برایشان آمرزش می‌خواست، خداوند را توبه‌پذیر مهربان می‌یافتند'),
(558, 4, 65, 'چنین نیست و سوگند به پروردگارت که ایمان نیاورده‌اند مگر آنکه در اختلافی که دارند تو را داور کنند، آنگاه در آنچه داوری کردی هیچ دلتنگی در خود نیابند و به خوبی [به حکم تو] گردن بگذارند'),
(559, 4, 66, 'و اگر بر آنان مقرر داشته بودیم که خودتان را بکشید یا از خانه و کاشانه‌تان بیرون روید، جز اندکی از آنان آن کار را نمی‌کردند، و اگر آنچه به آنان اندرز داده شد انجام می‌دادند بی‌شک برایشان بهتر و در ثبات قدم ایشان مؤثرتر بود'),
(560, 4, 67, 'و در آن صورت از پیشگاه خود به ایشان پاداش عظیمی می‌بخشیدیم‌'),
(561, 4, 68, 'و به راهی راست هدایتشان می‌کردیم‌'),
(562, 4, 69, 'و کسانی که از خداوند و پیامبر اطاعت کنند، در زمره کسانی‌اند که خداوند بر آنان انعام فرموده است، اعم از پیامبران و صدیقان و شهیدان و صالحان، و اینان نیک رفیقانی هستند'),
(563, 4, 70, 'این بخشش الهی است و خدا دانایی بسنده است‌'),
(564, 4, 71, 'ای مؤمنان سلاح خود را برگیرید و گروه گروه یا دسته‌جمعی رهسپار شوید'),
(565, 4, 72, 'و از شما کسی هست که پا سست می‌کند و چون مصیبتی به شما رسد می‌گوید خدا به من رحم کرد که همراه آنان حاضر نبودم‌'),
(566, 4, 73, 'و چون به شما خیر و خوبیی از خداوند برسد، در حالی که گویی بین شما و او هیچ‌گونه دوستی نیست می‌گوید ای کاش من همراه ایشان بودم تا به سعادت بزرگی نایل می‌شدم‌'),
(567, 4, 74, 'و کسانی که زندگانی دنیا را به آخرت فروخته‌اند، باید در راه خدا کارزار کنند، و هرکس که در راه خدا کارزار کند و کشته یا پیروز شود، به زودی به او پاداشی عظیم می‌بخشیم‌'),
(568, 4, 75, 'و چرا در راه خدا و در راه [آزادسازی‌] مردان و زنان و کودکان مستضعف -که می‌گویند پروردگارا ما را از این شهری که اهلش ستمگرند خارج فرما و از سوی خویش برای ما یاری و یاوری بگمارکارزار نکنید؟'),
(569, 4, 76, 'مؤمنان در راه خدا کارزار می‌کنند و کافران در راه طاغوت، آری با دوستداران شیطان بجنگید که نیرنگ شیطان [در نهایت‌] ضعیف است‌'),
(570, 4, 77, 'آیا داستان کسانی را که به آنان گفته شد [عجالتا] دست از جهاد نگه دارید و نماز را برپا دارید و زکات بپردازید ندانسته‌ای؟ که چون بر آنان جهاد مقرر گشت، آنگاه گروهی از آنان از مردم [مشرکان مکه‌] چنان می‌ترسیدند که باید از خداوند ترسید، یا حتی از آن شدیدتر، و [در دل‌] می‌گفتند پروردگارا چرا بر ما جهاد را مقرر داشتی، کاش ما را تا زمانی نزدیک واپس می‌داشتی، بگو بهره دنیا ناچیز است و برای کسی که تقوا پیشه کند آخرت بهتر است، و بر شما به اندازه ذره ناچیزی هم ستم نخواهد رفت‌'),
(571, 4, 78, 'هر جا که باشید -ولو در دژهای استوار سر به فلک کشیدهمرگ شما را فراخواهد گرفت، و چون به ایشان خیری برسد می‌گویند این از سوی خداوند است، و اگر شری به ایشان برسد می‌گویند این از سوی توست، بگو همه از سوی خداوند است، این گروه را چه می‌شود که به نظر نمی‌رسد سخنی دریابند'),
(572, 4, 79, 'هر خیری که به تو برسد از سوی خداوند است و هر شری که به تو برسد از خود توست، و تو را به پیامبری برای مردمان فرستاده‌ایم و خداوند گواهی را کافی است‌'),
(573, 4, 80, 'هرکس از پیامبر اطاعت کند در حقیقت از خداوند اطاعت کرده است، و هرکس سر پیچد [بدان‌] که تو را نگهبان ایشان نفرستاده‌ایم‌'),
(574, 4, 81, 'می‌گویند سمعا و طاعة، ولی چون از نزد تو بیرون می‌روند، گروهی از آنان شبانه چیزی جز آنچه تو گفته‌ای می‌سگالند و خداوند سگالش شبانه‌شان را می‌نویسد، پس از آنان روی بگردان و بر خداوند توکل کن و خداوند کارسازی را کافی است‌'),
(575, 4, 82, 'آیا در [معانی‌] قرآن نمی‌اندیشند؟ و اگر از نزد کسی غیر از خدا بود، در آن اختلاف بسیار می‌یافتند'),
(576, 4, 83, 'و چون به ایشان خبری که مایه آرامش یا نگرانی است برسد، آن را فاش می‌سازند، حال آنکه اگر آن را به پیامبر و اولوالامر عرضه می‌داشتند، آنگاه اهل استنباط ایشان، به [مصلحت‌] آن پی می‌بردند، و اگر بخشش و رحمت الهی بر شما نبود، جز عده‌ای اندکشمار همه از شیطان پیروی می‌کردید'),
(577, 4, 84, 'پس در راه خدا کارزار کن، تو عهده‌دار کسی جز خود نیستی و مؤمنان را نیز [به کارزار] تشویق کن، چه بسا خداوند بلای کافران را [از شما] بگرداند و عذاب الهی سخت‌تر و خداوند سختگیرتر است‌'),
(578, 4, 85, 'هرکس که شفاعت پسندیده‌ای کند از [خیر] آن نصیب برد و هرکس که شفاعت ناپسندیده‌ای کند از [شر] آن نصیب برد، و خداوند بر هر چیز مقتدر است‌'),
(579, 4, 86, 'و چون شما را به تحیتی بنوازند، با تحیتی بهتر از آن، یا همانند آن، پاسخ دهید، که خداوند حسابگر همه چیز است‌'),
(580, 4, 87, 'خداوند کسی است که خدایی جز او نیست، همو شما را در روز قیامت که در [فرارسیدن‌] آن شکی نیست، گرد می‌آورد، و کیست از خدا راستگوتر'),
(581, 4, 88, 'از چیست که شما درباره منافقان دو گروه شده‌اید و حال آنکه خداوند آنان را به خاطر کار و کردارشان سرنگون ساخته است، آیا می‌خواهید کسی را که خداوند در گمراهی‌اش وانهاده است، رهنمایی کنید؟ و حال آنکه هرگز برای کسی که خداوند در گمراهی‌اش وانهاده، بیرون شدی نخواهی یافت‌'),
(582, 4, 89, '[آنان‌] خوش دارند که شما نیز مانند ایشان کفر بورزید و در نتیجه همانند باشید، پس هیچ‌کس از آنان را دوست نگیرید، مگر آنکه [همانند شما] در راه خدا هجرت کنند، و اگر رویگردان شدند، ایشان را هرجا که یافتید به اسارت بگیرید و بکشید و از آنان [برای خود] یار و یاور نگیرید'),
(583, 4, 90, 'مگر کسانی که با قومی که بین شما و ایشان پیمانی هست، پیوند دارند، یا به نزد شما می‌آیند و دلهایشان تنگ است از اینکه با شما یا با قوم خویش کارزار کنند، و اگر خدا می‌خواست آنان را بر شما چیره می‌ساخت و با شما به کارزار برمی‌خاستند، ولی اگر از شما کناره گرفتند و با شما کارزار نکردند و از در تسلیم و اسلام وارد شدند، خداوند به شما اجازه مبارزه با آنان نمی‌دهد'),
(584, 4, 91, 'و دیگرانی را خواهید یافت که می‌خواهند هم از شما در امان باشند، و هم از قوم خودشان، هر بار که به فتنه شرک بازگردانده شوند، در دام آن گرفتار آیند، ولی اگر از شما کناره نگرفتند و از در تسلیم و اسلام وارد نشدند، و دست از شما باز نداشتند، آنگاه هرجا که یافتیدشان به اسارت بگیرید و بکشید، اینانند که دست شما را در مبارزه با آنان باز گذارده‌ایم‌'),
(585, 4, 92, 'و هیچ مؤمنی نباید هیچ مؤمنی را بکشد، مگر از روی خطا، و هرکس که مؤمنی را به خطا بکشد، بر اوست که [به کفاره‌] برده مؤمنی را آزاد کند و خونبهایی به خانواده او بدهد، مگر آنکه آنان درگذرند و ببخشند، و اگر [مقتول‌] مؤمن و از قومی باشد که دشمن شما هستند، [قاتل‌] باید برده مؤمنی را آزاد کند، و اگر [مقتول‌] از قومی باشد که بین شما و ایشان پیمانی هست، آنگاه [قاتل‌] باید خونبهایی به خانواده او [مقتول‌] بپردازد و برده مؤمنی آزاد کند، و کسی که [برده‌] نیابد بر اوست که دو ماه پیاپی روزه بگیرد، این توبه‌پذیری خداوند است و خداوند دانای فرزانه است‌'),
(586, 4, 93, 'و هرکس مؤمنی را عمدا بکشد، جزای او جهنم است که جاودانه در آن بماند و خداوند بر او خشم گیرد و لعنتش کند و برای او عذابی عظیم آماده سازد'),
(587, 4, 94, 'ای مؤمنان چون در راه خدا سفر کنید، به درستی پرس و جو کنید، و به کسی که با شما از در تسلیم و اسلام وارد شود، مگویید که مؤمن نیستی تا بدین وسیله بهره دنیوی ببرید، چرا که غنایم بسیار در نزد خداوند است، در گذشته این گونه [بی‌پروا] بودید، ولی خداوند بر شما منت نهاد، پس به درستی پرس و جو کنید، که خداوند به آنچه می‌کنید آگاه است‌'),
(588, 4, 95, 'جهادگریزان مؤمن، بجز آسیب‌مندان، با جهادگران به مال و جان در راه خدا برابر نیستند، خداوند جهادگران به مال و جان را بر جهادگریزان به مرتبتی برتری داده است، و همگان را خداوند وعده پاداش نیک [بهشت‌] داده است، و خداوند جهادگران را بر جهادگریزان به پاداشی عظیم برتری داده است‌'),
(589, 4, 96, 'این درجات و آمرزش و رحمت از سوی اوست و خداوند آمرزگار مهربان است‌'),
(590, 4, 97, 'کسانی که فرشتگان جانشان را می‌گیرند و آنان بر خود ستم روا داشته‌اند، به ایشان گویند در چه حال بودید؟ گویند ما در سرزمین خود مستضعف بودیم، گویند آیا مگر زمین خداوند وسیع نبود که بتوانید در آن [به هر کجا که خواهید] هجرت کنید؟ سرا و سرانجام اینان جهنم است و چه بد سرانجامی است‌'),
(591, 4, 98, 'مگر مردان و زنان و کودکان مستضعف که نمی‌توانند چاره‌ای بیندیشند و راه به جایی نمی‌برند'),
(592, 4, 99, 'اینان را باشد که خداوند ببخشاید و خداوند بخشاینده آمرزگار است‌'),
(593, 4, 100, 'و هر کس که در راه خدا هجرت کند، در روی زمین سرپناه بسیار و گشایش [در کار] می‌یابد و هرکس که از خانه و کاشانه خویش به عزم هجرت در راه خدا و پیامبرش بیرون رود، سپس مرگش فرارسد، حقا که پاداش او بر خداوند است و خداوند آمرزگار مهربان است‌'),
(594, 4, 101, 'و چون رهسپار سفر باشید، بر شما گناهی نیست که اگر می‌ترسید که مبادا کافران شما را در بلا افکنند، نماز خود را کوتاه کنید و به راستی که کافران آشکارا دشمن شما هستند'),
(595, 4, 102, 'و چون در میان آنان بودی و خواستی برای آنان اقامه نماز کنی، باید گروهی از آنان با تو [به نماز] برخیزند و سلاحهای خود را نیز برگیرند، و چون به سجده روند، گروه دیگر در پشت شما [ایستاده‌] باشند و سپس گروه دیگر که هنوز نماز نخوانده‌اند، پیش آیند و همراه تو نماز بگزارند و احتیاط خود را حفظ کنند و سلاحهای خود را همراه داشته باشند، [چرا که‌] کافران خوش دارند که کاش شما از سلاحها و ساز و برگتان غافل باشید تا بناگاه به یک حمله بر شما بتازند، و بر شما گناهی نیست که اگر از یاران [یا موانع دیگر] در رنج یا بیمار بودید، سلاحهایتان را بر زمین بگذارید، ولی احتیاطتان را داشته باشید، خداوند برای کافران عذابی خوارکننده آماده ساخته است‌'),
(596, 4, 103, 'و چون نمازتان را برگزار کردید، خداوند را [در همه حال‌] ایستاده و نشسته و بر پهلو خفته یاد کنید و چون امن و آسایش یافتید نماز را [به درستی‌] بر پا دارید، که نماز بر مؤمنان واجب [و عبادتی‌] زماندار است‌'),
(597, 4, 104, 'در پیگرد قوم مشرک، سستی نورزید [زیرا] اگر شما درد و رنجی کشیده باشید، آنان نیز همانند رنجی که شما می‌برید، رنج برده‌اند، وانگهی شما امیدی به خداوند دارید که آنان ندارند، و خداوند دانای فرزانه است‌'),
(598, 4, 105, 'ما کتاب آسمانی را به راستی و درستی بر تو نازل کردیم تا [بر مبنای آن و] به مدد آنچه خداوند به تو باز نموده است، بین مردم داوری کنی، و مدافع خیانت‌پیشگان مباش‌'),
(599, 4, 106, 'و از خداوند آمرزش بخواه که خداوند آمرزگار مهربان است‌'),
(600, 4, 107, 'و از کسانی که بر خود خیانت روا داشته‌اند، دفاع مکن که خداوند خیانتگران گنهکار را دوست ندارد'),
(601, 4, 108, 'اینان با مردم پنهانکاری می‌کنند، ولی نمی‌توانند چیزی را از خداوند پنهان بدارند، چرا که او در زمانی که شبانه سخنان ناپسند می‌گویند، همراه آنان است و خداوند به آنچه می‌کنند چیره است‌'),
(602, 4, 109, 'بهوش باشید که گیریم شما از ایشان در زندگانی دنیا دفاع کردید، آنگاه در روز قیامت چه کسی از آنان در برابر خداوند دفاع می‌کند یا چه کسی نگهبان ایشان است؟'),
(603, 4, 110, 'و هرکس بدی یا بر خویش ستم کند، سپس از خداوند آمرزش بخواهد، خدا را آمرزگاری مهربان می‌یابد'),
(604, 4, 111, 'و هرکس مرتکب گناهی شود، به زیان خویش چنین کاری کرده است، و خداوند دانای فرزانه است‌'),
(605, 4, 112, 'و کسی که مرتکب خطا یا گناهی شود، سپس آن را به گردن بیگناهی بیندازد، زیر بار بهتان و گناهی آشکار رفته است‌'),
(606, 4, 113, 'و اگر لطف و رحمت الهی در حق تو نبود، گروهی از ایشان کوشیده بودند که تو را گمراه کنند و در واقع کسی را جز خود را گمراه نکنند و کوچکترین زیانی به تو نرسانند و خداوند بر تو کتاب و حکمت نازل کرد و [به وحی خویش‌] به تو چیزی را که نمی‌دانستی آموخت و لطف الهی بر تو بسیار است‌'),
(607, 4, 114, 'در اغلب رازگویی‌های ایشان خیری نیست، مگر آنکه کسی به صدقه یا به نیکوکاری یا اصلاح بین مردم بپردازد و هر کس که در طلب خشنودی خداوند چنین کاری کند، به زودی به او پاداش بزرگی خواهیم داد'),
(608, 4, 115, 'و هرکس پس از آنکه حق و هدایت برایش آشکار شد با پیامبر مخالفت کند و راهی جز راه مسلمانان در پیش گیرد، او را با مراد خویش وامی‌گذاریم و به دوزخش در می‌آوریم و چه بد سرانجامی است‌'),
(609, 4, 116, 'خداوند [این گناه را] نمی‌آمرزد که برایش شریک قائل شوند، و جز این [هر گناهی‌] را برای هرکس که بخواهد می‌آمرزد و هرکس به خداوند شرک‌ورزد، دچار گمراهی دور و درازی شده است‌'),
(610, 4, 117, 'اینان به جای خداوند جز مادگانی را نمی‌پرستند، و جز شیطان سرکش را پرستش نمی‌کنند'),
(611, 4, 118, 'که خداوند او را گرفتار لعنت کرد و او گفت از بندگان تو سهم معینی برمی‌گیرم‌'),
(612, 4, 119, 'و آنگاه آنان را به گمراهی می‌کشانم و به [دام‌] آرزوهای دور و دراز می‌اندازم و می‌فرمایمشان تا گوشهای چارپایان را ببرند و می‌فرمایمشان تا آفرینش الهی را دگرگون کنند، و هرکس که شیطان را به جای خداوند دوست گیرد، آشکارا زیانکار شده است‌'),
(613, 4, 120, 'به آنان وعده می‌دهد و ایشان را به [دام‌] آرزو می‌اندازد و شیطان وعده‌ای جز فریب به آنان نمی‌دهد'),
(614, 4, 121, 'سرا و سرانجام ایشان جهنم است و گریزگاهی از آن ندارند'),
(615, 4, 122, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، زودا که به بوستانهایی درآوریمشان که جویباران از فرودست آن جاری است [و] جاودانه در آنند، وعده الهی حق است و کیست راستگوتر از خدا؟'),
(616, 4, 123, '[وعده الهی‌] بر وفق آرزوهای شما و آرزوهای اهل کتاب نیست، هرکس مرتکب ناشایستی شود، جزای آن را می‌یابد و برای خود در برابر خداوند یار و یاوری نمی‌یابد'),
(617, 4, 124, 'و هرکس از زن و مرد که کارهای شایسته کند و مؤمن باشد، وارد بهشت می‌شود و به اندازه ذره ناچیزی نیز به آنان ستم نمی‌رود'),
(618, 4, 125, 'چه کسی بهدین‌تر از کسی است که روی دل به درگاه خدا آورد و نیکوکار باشد و از آیین پاک ابراهیم پیروی کند و خداوند ابراهیم را [همچون‌] دوست خود برگرفته است‌'),
(619, 4, 126, 'آنچه در آسمانها و آنچه در زمین است از آن خداوند است و خداوند بر همه چیز چیره است‌'),
(620, 4, 127, 'و از تو درباره زنان نظرخواهی می‌کنند، بگو خداوند درباره آنان فتوا می‌دهد -و نیز در آنچه از کتاب بر شما خوانده می‌شود، آمده استکه [حکم او] درباره دختران یتیمی که حق مقرر آنان را نمی‌پردازید، و میل به ازدواج با آنان ندارید و نیز کودکان مستضعف [یتیم و محروم‌] این است که در حق یتیمان به عدل و انصاف بکوشید و هر خیری که انجام دهید خداوند از آن آگاه است‌'),
(621, 4, 128, 'و اگر زنی از ناسازگاری یا رویگردانی شوهرش بیمناک باشد، بر آن دو گناهی نیست که بین خود صلحی کنند، و صلح بهتر است، و آزمندی در دلها خانه دارد، و اگر نیکوکاری و پارسایی کنید، خداوند به آنچه می‌کنید آگاه است‌'),
(622, 4, 129, 'و هر قدر هم که بکوشید و بخواهید نمی‌توانید بین زنان به عدل و انصاف رفتار کنید، پس [در میل به دیگری‌] پا را از حد فراتر مگذارید تا همسر [پیشین‌] خود را بلاتکلیف بگذارید، و اگر به اصلاح بپردازید و تقوا پیشه کنید، [بدانید که‌] خداوند آمرزگاری مهربان است‌'),
(623, 4, 130, 'و اگر از هم جدا شوند خداوند هر یک را با توانگری خویش بی‌نیاز گرداند، و خداوند گشایشگر فرزانه است‌'),
(624, 4, 131, 'و آنچه در آسمانها و در زمین است از آن خداوند است و پیش از شما به اهل کتاب و [سپس‌] به خود شما سفارش کردیم که از خداوند پروا کنید، و اگر کفربورزید، [بدانید] آنچه در آسمانها و در زمین است از آن خداوند است و خداوند بی‌نیاز ستوده است‌'),
(625, 4, 132, 'و آنچه در آسمانها و زمین است از آن خداوند است و خدا کارسازی را کافی است‌'),
(626, 4, 133, 'ای مردم اگر [خدا] بخواهد شما را [از میان‌] می‌برد و دیگرانی را [پدید] می‌آورد و خداوند بر این کار تواناست‌'),
(627, 4, 134, 'هرکس ثواب دنیوی می‌خواهد [بداند که‌] ثواب دنیوی و اخروی نزد خداوند است و خدا شنوای داناست‌'),
(628, 4, 135, 'ای مؤمنان به عدل و داد برخیزید و در راه رضای خدا شاهد باشید [و شهادت دهید] ولو آنکه به زیان خودتان یا پدر و مادر و خویشاوندانتان باشد، [خواهان گواهی‌] چه توانگر چه تهیدست باشد، خداوند بر آنان مهربان‌تر است، پس از هوای نفس پیروی مکنید که از حق عدول کنید، و اگر زبان بازی کنید یا تن زنید [بدانید که‌] خداوند به آنچه می‌کنید آگاه است‌'),
(629, 4, 136, 'ای کسانی که ایمان آورده‌اید، به خداوند و پیامبر او و کتابی که بر پیامبرش فرو فرستاده است و کتابی که از پیش منازل کرده است، ایمان بیاورید و هرکس به خداوند و فرشتگانش و کتابهایش و پیامبرانش و روز بازپسین، کفر بورزد، به گمراهی دور و درازی افتاده است‌'),
(630, 4, 137, 'کسانی که ایمان آوردند، سپس کفرورزیدند، سپس ایمان آوردند، و دوباره کفرورزیدند و سپس بر کفر خود افزودند، خداوند در مقام آمرزش آنان نیست و راهی به ایشان نمی‌نمایاند'),
(631, 4, 138, 'منافقان را خبر ده که عذاب دردناکی [در پیش‌] دارند'),
(632, 4, 139, 'کسانی که کافران را به جای مؤمنان دوست خود می‌گیرند، آیا در نزد آنان عزت می‌جویند، در حالی که هر چه عزت است، نزد خداوند است‌'),
(633, 4, 140, 'و در کتاب بر شما نازل کرده است که چون بشنوید که به آیات الهی کفر می‌ورزند و آنها را به ریشخند می‌گیرند، با آنان منشینید تا به سخن دیگری جز آن بپردازند، چرا که در این صورت شما نیز مانند آنان خواهید بود، [بدانید که‌] خداوند گردآورنده منافقان و کافران یکجا در جهنم است‌'),
(634, 4, 141, '[آری‌] همانها که مترصد شما هستند، پس اگر پیروزی‌ای از جانب خداوند نصیب شما شود می‌گویند مگر ما همراه [و پشتیبان‌] شما نبودیم؟ و اگر کافران نصیبی برند [به آنان‌] گویند مگر ما بر شما دست نداشتیم و دست مسلمانان را از شما دور نداشتیم؟ و خداوند روز قیامت در میان شما [و ایشان‌] داوری خواهد کرد، و خداوند هرگز کافران را به مؤمنان سلطه نمی‌دهد'),
(635, 4, 142, 'منافقان می‌خواهند به خدا نیرنگ بزنند، و حال آنکه خداوند به آنان [به جزای عملشان‌] نیرنگ می‌زند، و [اینان‌] چون به نماز برخیزند کسل‌وار برخیزند، [و] با مردم ریاکاری کنند و خدا را جز اندکی یاد نمی‌کنند'),
(636, 4, 143, 'در این میان [بین کفر و ایمان‌] سرگشته‌اند، نه جزو آنان [مؤمنان‌] و نه جزو اینان [نامؤمنان‌]، و هرکس که خداوند در گمراهی واگذاردش، هرگز برای او بیرون شدی نخواهی یافت‌'),
(637, 4, 144, 'ای مؤمنان کافران را به جای مؤمنان، دوست نگیرید، آیا می‌خواهید در پیشگاه خداوند حجت را بر خود تمام کنید؟'),
(638, 4, 145, 'منافقان در طبقه زیرین جهنم جای دارند و هرگز برای آنان یاوری نخواهی یافت‌'),
(639, 4, 146, 'مگر کسانی که توبه و درستکاری کنند و به خداوند پناه برند و دین خویش را برای خدا خالص سازند، اینان در زمره مؤمنانند و خداوند به زودی به مؤمنان پاداش بزرگی می‌بخشد'),
(640, 4, 147, 'اگر شکر کنید و ایمان ورزید خداوند را با عذاب شما چه کار؟ و خداوند قدردان داناست‌'),
(641, 4, 148, 'خداوند بانگ برداشتن به بد زبانی را دوست ندارد، مگر از [سوی‌] کسی که به او ستم شده باشد، و خداوند شنوای داناست‌'),
(642, 4, 149, 'اگر خیری را آشکار یا پنهان کنید، یا از عمل ناخوشایندی درگذرید، [بدانید که‌] خداوند بخشاینده تواناست‌'),
(643, 4, 150, 'کسانی که به خداوند و پیامبرانش کفر می‌ورزند و می‌خواهند بین خداوند و پیامبرانش جدائی قائل شوند و می‌گویند به بعضی ایمان داریم و به بعضی نداریم و می‌خواهند راهی بینابین پیش گیرند'),
(644, 4, 151, 'اینان به واقع کافرند و ما برای کافران عذابی خفت‌بار آماده ساخته‌ایم‌'),
(645, 4, 152, 'و کسانی که به خداوند و پیامبرانش ایمان دارند و بین هیچ‌یک از آنان فرق نمی‌گذارند، خداوند به زودی پاداش آنان را خواهد داد، و خدا آمرزگار مهربان است‌'),
(646, 4, 153, 'اهل کتاب از تو می‌خواهند که برایشان [به یکباره‌] از آسمان کتابی نازل کنی، بدان که از موسی درخواستی عجیب‌تر کردند، و گفتند خدا را آشکارا به ما نشان بده، آنگاه به خاطر درخواست ناحقشان صاعقه‌[ی مرگ‌] آنان را فروگرفت، آنگاه [چون به درخواست موسی از نو زنده شدند] پس از آن همه معجزات که برایشان پدید آمده بود، گوساله‌پرستی کردند، و از این گناهشان هم درگذشتیم و به موسی حجتی آشکار بخشیدیم‌'),
(647, 4, 154, 'و [کوه‌] طور را بر فرازشان افراشته داشتیم تا از ایشان پیمان گرفتیم، و به ایشان گفتیم فروتنانه از این دروازه وارد شوید، و به ایشان گفتیم حرمت سبت [ شنبه‌] را نشکنید، و از ایشان پیمانی استوار گرفتیم‌'),
(648, 4, 155, 'آنگاه به سبب پیمان شکنی‌شان و کفرورزیدنشان به آیات الهی و کشتنشان پیامبران را به ناحق و ادعایشان که دلهای ما در پوشش است [لعنتشان کردیم‌]، بلکه خداوند به سبب کفرشان بر دلهایشان مهر نهاد، که جز اندکی ایمان نمی‌آورند'),
(649, 4, 156, 'و به سبب کفرشان و سخنشان که به مریم بهتان بزرگی زدند'),
(650, 4, 157, 'و ادعایشان که ما مسیح عیسی بن مریم پیامبر خدا را کشته‌ایم و حال آنکه نه او را کشتند و نه بر دار کردند، بلکه [حقیقت امر] بر آنان مشتبه شد، و کسانی که در این امر اختلاف کردند از آن در شک‌اند و به آن علم ندارند، فقط پیروی از حدس و گمان می‌کنند و او را به یقین نکشته‌اند'),
(651, 4, 158, 'بلکه خداوند او را به سوی خویش برکشید و خدا پیروزمند فرزانه است‌'),
(652, 4, 159, 'و هیچ‌کس از اهل کتاب نیست مگر آنکه پیش از مرگش به او [عیسی‌] ایمان آورد، و روز قیامت او [عیسی‌] برایشان گواه است‌'),
(653, 4, 160, 'آری به سبب ظلم [بعضی از] یهودیان، و به سبب باز داشتنشان بسیاری [از مردم‌] را از راه خدا، چیزهای پاکیزه‌ای را که بر آنان حلال بود، بر آنان حرام کردیم‌'),
(654, 4, 161, 'و نیز به سبب ربا گرفتنشان که از آن نهی شده بودند، و اینکه اموال مردم را به باطل می‌خوردند، و ما برای کافرانشان عذابی دردناک آماده ساخته‌ایم‌'),
(655, 4, 162, 'ولی از میان آنان راسخان در علم و مؤمنان به آنچه بر تو و پیش از تو نازل شده است، ایمان دارند، به ویژه نمازگزاران، و زکات‌دهندگان و مؤمنان به خداوند و روز بازپسین، به اینان به زودی پاداش بزرگی خواهیم داد'),
(656, 4, 163, 'ما به تو وحی فرستادیم، همچنانکه به نوح و پیامبران بعد از او، و نیز به ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط و عیسی و ایوب و یونس و هارون و سلیمان نیز وحی فرستادیم، و به داوود زبور بخشیدیم‌'),
(657, 4, 164, 'و پیامبرانی که پیشتر داستان آنان را بر تو خوانده‌ایم و پیامبرانی که داستان آنان را بر تو نخوانده‌ایم، و خداوند با موسی [بی‌میانجی‌] سخن گفت‌'),
(658, 4, 165, '[اینان‌] پیامبرانی مژده‌آور و هشداردهنده هستند، تا بعد از [فرستادن‌] پیامبران، مردم را بر خداوند حجتی نباشد، و خداوند پیروزمند فرزانه است‌'),
(659, 4, 166, 'آری خداوند به [صحت‌] آنچه بر تو نازل کرده است، شهادت می‌دهد، آن را به علم خویش نازل کرده است، و فرشتگان نیز شهادت می‌دهند و خداوند گواهی را کافی است‌'),
(660, 4, 167, 'کسانی که کفرورزیده‌اند و از راه خدا باز داشته‌اند، به راستی به گمراهی دور و درازی دچارند'),
(661, 4, 168, 'کسانی که کفر و ظلم‌ورزیده‌اند، خداوند بر آن نیست که آنان را بیامرزد و یا راهی بنماید'),
(662, 4, 169, 'مگر راه جهنم، که جاودانه در آن می‌مانند، و این امر بر خداوند آسان است‌'),
(663, 4, 170, 'ای مردم این پیامبر حق و حقیقت را از جانب پروردگارتان برای شما آورده است، پس ایمان بیاورید که به خیر [و صلاح‌] شماست، و اگر [انکار کنید و] کفرورزید بدانید که آنچه در آسمانها و زمین است از آن خداوند است، و خداوند دانای فرزانه است‌'),
(664, 4, 171, 'ای اهل کتاب در دینتان از حد و حق نگذرید و جز حق بر خداوند نگویید، جز این نیست که مسیح عیسی بن مریم پیامبر خدا و کلمه او و روح اوست که آن را به مریم فراداد، پس به خداوند و پیامبرانش ایمان بیاورید، و نگویید که [خدا] سه‌گانه است، از این بازآیید که به خیر [و صلاح‌] شماست، همانا خداوند خدای یگانه است، که منزه و مبرا از داشتن فرزند است، او راست آنچه در آسمانها و در زمین است، و خداوند کارسازی را کافی است‌'),
(665, 4, 172, 'مسیح و فرشتگان مقرب هرگز ابا ندارند از اینکه بنده خدا باشند، و هرکس از پرستش او ابا داشته باشد و کبر بورزد به زودی همگی آنان را [برای حسابرسی‌] نزد خویش گرد می‌آورد'),
(666, 4, 173, 'اما کسانی را که ایمان آورده و کارهای شایسته کرده‌اند، پاداشهایشان را به تمامی می‌دهد و از فضل خویش نیز به ایشان افزونتر می‌بخشد، و اما کسانی که استنکاف و استکبار ورزیده‌اند، به عذابی دردناک دچارشان می‌سازد و در برابر خداوند برای خود یار و یاوری نمی‌یابند'),
(667, 4, 174, 'ای مردم به راستی برهانی از سوی پروردگارتان برای شما نازل شده است و برای شما نوری آشکار فرو فرستاده‌ایم‌'),
(668, 4, 175, 'آنگاه کسانی که به خداوند ایمان آورده‌اند و به او پناه برده‌اند، ایشان را به زودی به جوار فضل و رحمت خویش درمی‌آورد و به راهی راست به سوی خویش رهنمون می‌گردد'),
(669, 4, 176, 'از تو نظر خواهی می‌کنند بگو خداوند درباره کلاله به شما چنین فتوا می‌دهد که اگر مردی درگذرد و فرزندی نداشته باشد و خواهری داشته باشد، نصف ترکه از آن او [خواهر] است، [و اگر زنی درگذرد و برادری داشته باشد] در صورتی که فرزند نداشته باشد، آن مرد [همه ترکه را] از او ارث می‌برد، و اگر خواهران دو تن [یا بیشتر] باشند، دو سوم ترکه از آنان است، و اگر گروهی برادر و خواهر باشند، در این صورت برای هر مرد بهره‌ای برابر با بهره دو زن است، خداوند [احکامش را] برایتان روشن می‌سازد تا مبادا گمراه شوید، و خدا به هر چیزی داناست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'ای مؤمنان به پیمانها[ی خود] وفا کنید، [گوشت‌] چارپایان بر شما حلال است مگر آنچه حکمش بر شما خوانده شود، و [به شرط آنکه‌] در حال احرام، حلال دارنده صید نباشید، خداوند هر حکمی که بخواهد مقرر می‌دارد'),
(671, 5, 2, 'ای مؤمنان حرمت شعائر الهی و ماههای حرام و قربانیهای نشاندار و قربانیهای قلاده‌دار و زائران بیت‌الحرام را که از پروردگارشان جویای گشایش معاش و خشنودی او هستند، نگه دارید، و چون از حال احرام بیرون آمدید می‌توانید به شکار بپردازید، و دشمنی با مردمی که شما را از مسجدالحرام باز می‌دارند، بر آنتان ندارد که تجاوز کاری کنید، و در نیکی و پارسایی همدستی کنید نه در گناه و ستمکاری، و از خدا پروا کنید که خداوند سخت کیفر است‌'),
(672, 5, 3, 'مردار و خون و گوشت خوک و آنچه نامی جز خدا به هنگام ذبحش برده باشند، و [حیوان حلال گوشت‌] خفه شده، و به ضرب [چوب و سنگ‌] مرده، و از بلندی افتاده، و شاخ خورده و آنچه درندگان از آن خورده باشند، مگر آنچه [به موقع به ذبح شرعی‌] حلال کنید و همچنین آنچه برای بتان ذبح شده است و نیز تعیین قسمت به کمک ازلام همه بر شما حرام شده است و [پرداختن به هریک از] اینها نافرمانی است، امروز کافران از دین شما [و بازگشت شما از آن‌] نومید شده‌اند، لذا از آنان نترسید و از من بترسید، امروز دین شما را به کمال رساندم و نعمتم را بر شما تمام کردم و دین اسلام را بر شما پسندیدم، و هرکس در مخمصه‌ای ناچار شود و مایل به گناه نباشد [و از آنچه نهی شده بخورد] بداند که خداوند آمرزگار مهربان است‌'),
(673, 5, 4, 'از تو می‌پرسند چه چیز برای آنان حلال است، بگو همه پاکیزه‌ها و آنچه به کمک حیوانات شکارگر [به دست می‌آورید] که با آنچه خداوند به شما آموخته است، آنها را دست‌آموز می‌کنید، پس از آنچه برای شما گرفته و نگه داشته‌اند بخورید و [به هنگام ذبح یا فرستادن حیوانات شکارگر] نام خدا را بر آن ببرید، و از خداوند پروا کنید که خداوند زودشمار است‌'),
(674, 5, 5, 'امروز پاکیزه‌ها و طعام [و ذبایح‌] اهل کتاب بر شما و طعام [و ذبایح‌] شما بر آنان حلال است، و نیز ازدواج با زنان عفیف مؤمن و زنان عفیف اهل کتاب، به شرط آنکه مهر آنان را بپردازید و زن نگهدار و غیر پلیدکار باشید و دوست‌گیران نهانی نباشید، و هرکس به [ارکان‌] ایمان کفرورزد، عملش تباه است و همو در آخرت از زیانکاران است‌'),
(675, 5, 6, 'ای مؤمنان چون برای نماز برخاستید چهره و دستهایتان را تا آرنج‌ها بشویید و سپس سر و پاهایتان را تا قوزکها مسح کنید، و اگر جنب بودید غسل کنید و اگر بیمار یا مسافر بودید یا یکی از شما از موضع قضای حاجت بازگشت، یا با زنان تماس [جنسی‌] گرفتید، آنگاه آب نیافتید، بر خاکی پاک تیمم کنید و چهره و دستهایتان را به آن مسح کنید، که خداوند نمی‌خواهد برای شما دشواری قرار دهد، بلکه می‌خواهد تا شما را پاکیزه بدارد و نعمتش را بر شما تمام کند، باشد که سپاس بگزارید'),
(676, 5, 7, 'و نعمت الهی و پیمانی را که با شما بسته است به یاد آورید که گفتید شنیدیم و اطاعت کردیم، و از خداوند پروا کنید که خداوند به راز دلها آگاه است‌'),
(677, 5, 8, 'ای مؤمنان در راه [رضای‌] خدا به داد برخیزید و به عدل شهادت دهید و دشمنی‌تان با بعضی از مردم شما را بر آن ندارد که بیداد کنید، دادگری کنید که به تقوا نزدیکتر است و از خداوند پروا کنید که خداوند به آنچه می‌کنید آگاه است‌'),
(678, 5, 9, 'خداوند به کسانی که ایمان آورده و کارهای شایسته کرده‌اند وعده داده است که آمرزش و پاداشی بزرگ خاص آنان است‌'),
(679, 5, 10, 'و کسانی که کفرورزیده و آیات ما را دروغ انگاشته‌اند، دوزخی‌اند'),
(680, 5, 11, 'ای مؤمنان نعمت الهی را بر خود به یاد آورید که چون قومی کوشید که بر شما دست دراز کند، دستشان را از شما بازداشت، و از خداوند پروا کنید و مؤمنان باید که بر خدا توکل کنند'),
(681, 5, 12, 'و خداوند از بنی‌اسرائیل پیمان گرفت و از میان آنان دوازده سالار برگماشتیم، و خداوند گفت من با شما هستم، اگر نماز برپا داشتید و زکات پرداختید و به پیامبران من ایمان آوردید و آنان را گرامی داشتید و در راه خدا وامی نیکو دادید، سیئات شما را می‌زدایم و شما را به بوستانهایی که جویباران از فرودست آن جاری است، درمی‌آورم، اما اگر پس از این، کسی از شما کفر ورزد، بی‌شک از راه راست گمراه شده است‌'),
(682, 5, 13, 'آنگاه به سبب پیمان شکنی‌شان لعنتشان کردیم، و دلهایشان را سخت گردانیدیم [به طوری‌] که کلمات [کتاب‌] را از مواضع خود تحریف می‌کردند و بخشی از آنچه به آنان پند داده بودند، فراموش کردند، و همچنان به خیانت آنان پی می‌بری، مگر عده اندکی از آنان، پس آنان را ببخش و بگذار و بگذر که خداوند نیکوکاران را دوست دارد'),
(683, 5, 14, 'و از کسانی که گفتند ما مسیحی هستیم پیمان گرفتیم، آنگاه بخشی از آنچه به آنان پند داده بودند، فراموش کردند، سپس بین ایشان، تا روز قیامت دشمنی و کینه انداختیم، و خداوند به زودی آنان را [از نتیجه‌] آنچه کرده‌اند، آگاه می‌سازد'),
(684, 5, 15, 'ای اهل کتاب پیامبر ما به سوی شما آمده است، تا بسیاری از آنچه از کتاب آسمانی پنهان داشته‌اید، برای شما روشن سازد و از بسیاری [خطاهای شما] درگذرد، آری نور و کتابی روشنگر از خداوند به سوی شما آمده است‌'),
(685, 5, 16, 'که خداوند در پرتو آن کسانی را که از رضای او پیروی می‌کنند به راههای امن و عافیت رهنمون می‌گردد، و آنان را به توفیق خویش از تاریکیها به سوی روشنایی بازمی‌برد و به راه راست هدایتشان می‌کند'),
(686, 5, 17, 'به راستی کسانی که می‌گویند خدا همان مسیح بن مریم است، کافر شده‌اند، بگو اگر خداوند بخواهد مسیح بن مریم و مادرش و اهل زمین، همه را نابود کند، چه کسی می‌تواند او را بازدارد؟ و فرمانروایی آسمانها و زمین و آنچه مابین آنهاست از آن خداوند است، که هر چه بخواهد می‌آفریند، و خدا بر هر کاری تواناست‌'),
(687, 5, 18, 'و یهودیان و مسیحیان گفتند ما پسران خدا و دوستان او هستیم، بگو پس چرا شما را به خاطر گناهانتان عذاب می‌کند، آری شما هم انسانهایی هستید از همان دست که آفریده است، هرکس را که بخواهد می‌آمرزد و هرکس را که بخواهد عذاب می‌کند، و فرمانروایی آسمانها و زمین و آنچه در میان آنهاست از آن خداوند است، و سیر بازگشت به سوی اوست‌'),
(688, 5, 19, 'ای اهل کتاب پیامبر ما به سوی شما آمده است و پس از عصر [انقطاع وحی و] فترت پیامبران [احکام او را] برای شما روشن می‌سازد، تا مگویید که نه پیامبر مژده‌آور و نه هشداردهنده‌ای به سوی ما آمد، آری پیامبر مژده‌آور و هشداردهنده‌ای به سوی شما آمد، و خداوند بر هر کاری تواناست‌'),
(689, 5, 20, 'و چنین بود که موسی به قومش گفت ای قوم من، نعمت خدا را بر خود به یاد آورید که در میان شما پیامبرانی برانگیخت و [بعضی از] شما را پادشاه ساخت، و به شما چیزهایی بخشید که به هیچ‌کس از جهانیان نداده است‌'),
(690, 5, 21, 'ای قوم من وارد سرزمین مقدسی شوید که خداوند برایتان مقرر داشته است، و پشت مکنید که زیانکار خواهید شد'),
(691, 5, 22, 'گفتند ای موسی در آنجا قوم زورمند و ستمگری هستند، و مادام که آنان بیرون نرفته‌اند، ما وارد آنجا نخواهیم شد، و چون از آن بیرون شدند، ما به درون خواهیم رفت‌'),
(692, 5, 23, 'و دو مرد از پرواپیشگان که خداوند بر آنها لطف و انعام کرده بود، گفتند [بی‌باکانه‌] بر آنان از دروازه وارد شوید و چون وارد شدید پیروز خواهید شد و اگر مؤمن هستید بر خداوند توکل کنید'),
(693, 5, 24, 'گفتند ای موسی مادام که آنان در آنجا هستند ما هرگز وارد نخواهیم شد، تو با پروردگارت بروید و بجنگید ما در اینجا برکنار می‌مانیم‌'),
(694, 5, 25, 'موسی گفت پروردگارا من جز بر خود و برادرم دست ندارم، بین ما و قوم نافرمان جدایی بیفکن‌'),
(695, 5, 26, 'فرمود در این صورت [ورود به‌] آن سرزمین چهل سال بر آنان حرام است که در بیابانها سرگشته باشند، پس بر نافرمانان دریغ مخور'),
(696, 5, 27, 'و بر آنان داستان دو پسر آدم را به راستی و درستی بخوان، که چون قربانی‌ای عرضه داشتند، از یکی از آنها پذیرفته شد و از دیگری پذیرفته نشد، [قابیل به هابیل‌] گفت تو را خواهم کشت، گفت خداوند تنها از پرهیزگاران می‌پذیرد'),
(697, 5, 28, 'اگر دستت را به سوی من دراز کنی که مرا بکشی [بدان که‌] من دست درازکننده به سوی تو نخواهم بود که بکشمت، چرا که از خداوند، پروردگار جهانیان می‌ترسم‌'),
(698, 5, 29, 'من می‌خواهم که بار گناه من و گناه خودت را بر دوش کشی و از دوزخیان شوی، و این جزای ستمگران است‌'),
(699, 5, 30, 'و نفس اماره او را وادار به کشتن برادرش کرد، و او را کشت، و از زیانکاران گردید'),
(700, 5, 31, 'آنگاه خداوند کلاغی برانگیخت که زمین را [با چنگ و منقار] بکاود، تا بدین وسیله به او بنمایاند که چگونه جسد برادرش را [در خاک‌] پنهان کند، [قابیل که چنین دید] گفت وای بر من آیا عاجزم که مثل این کلاغ باشم تا جسد برادرم را پنهان کنم و بدین‌سان پشیمان شد'),
(701, 5, 32, 'به این جهت بر بنی‌اسرائیل مقرر داشتیم که هرکس کسی را جز به قصاص قتل، یا به جزای فساد در روی زمین، بکشد مانند این است که همه مردم را کشته باشد، و هرکس کسی را زنده بدارد مانند این است که همه مردم را زنده داشته باشد، و پیامبران ما معجزاتی برای آنان آوردند آنگاه بسیاری از آنان در سرزمین خویش زیاده‌روی پیشه کردند'),
(702, 5, 33, 'همانا جزای کسانی که با [دوستداران‌] خداوند و پیامبر او به محاربه برمی‌خیزند و در زمین به فتنه و فساد می‌کوشند، این است که کشته شوند یا بر دار شوند یا دستها و پاهایشان در خلاف جهت یکدیگر بریده شود، یا از سرزمین خویش تبعید شوند، این خواری و زاری دنیویشان است و در آخرت هم عذاب بزرگی [در پیش‌] خواهند داشت‌'),
(703, 5, 34, 'مگر کسانی که پیش از آنکه بر آنان دست یابید، توبه کنند، که بدانید در این صورت خداوند آمرزگار مهربان است‌'),
(704, 5, 35, 'ای مؤمنان از خداوند پروا کنید و به او [توسل و] تقرب جویید و در راه او جهاد کنید، باشد که رستگار شوید'),
(705, 5, 36, 'اگر کافران دارای هر آنچه باشند که در روی زمین است و آن و همچند آن را فدیه دهند و بلاگردان عذاب روز قیامت سازند، از آنان پذیرفته نخواهد شد و عذابی دردناک [در پیش‌] دارند'),
(706, 5, 37, 'اینان می‌خواهند از جهنم بیرون روند ولی بیرون آمدنی از آن نیستند، و عذابی پاینده دارند'),
(707, 5, 38, 'دستان مرد و زن دزد را به کیفر کاری که کرده‌اند ببرید که عقوبتی الهی است و خداوند پیروزمند فرزانه است‌'),
(708, 5, 39, 'و هر کس که بعد از ستمش، توبه و درستکاری پیشه کند، خداوند از [گناه‌] او درمی‌گذرد، چرا که خداوند آمرزگار مهربان است‌'),
(709, 5, 40, 'آیا نمی‌دانی که فرمانروایی آسمانها و زمین از آن خداوند است؟ هرکس را بخواهد عذاب می‌کند و هرکس را که بخواهد می‌آمرزد، و خداوند بر هر کاری تواناست‌'),
(710, 5, 41, 'ای پیامبر کسانی که در کفر می‌کوشند تو را اندوهگین نکنند اینان از کسانی هستند که به زبان می‌گویند ایمان آورده‌ایم ولی دلشان ایمان نیاورده است همچنین از یهودیان، که عده‌ای پذیرا و شنوای دروغ و جاسوسان قومی دیگرند که به نزد شما [از در اسلام و تسلیم‌] نیامده‌اند اینان کلمات [کتاب آسمانی‌] را از مواضعش تحریف می‌کنند و [به همدیگر] می‌گویند اگر چنین [حکمی از سوی پیامبر] به شما داده شد، آن را بپذیرید و اگر داده نشد، از او کناره کنید، و کسی که خداوند سرگشتگی‌اش را خواسته باشد، هرگز برای او در برابر خداوند کاری نمی‌توانی کرد، اینان کسانی هستند که خداوند نخواسته است دلهایشان را پاکیزه بدارد، در دنیا خواری و در آخرت عذابی بزرگ [در پیش‌] دارند'),
(711, 5, 42, 'پذیرا و شنوای دروغ و رشوه‌خوار هستند و اگر به نزد تو آمدند، در میان آنان داوری کن، یا از آنان روی بگردان، و اگر از آنان رویگردان شدی بدان که هرگز زیانی به تو نخواهند رسانید، و چون داوری کردی، به داد داوری کن که خداوند دادگران را دوست دارد'),
(712, 5, 43, 'و چگونه تو را به داوری می‌گیرند و حال آنکه تورات در نزد آنان است و حکم الهی در آن هست، آنگاه با وجود این رویگردان می‌شوند و اینان مؤمن نیستند'),
(713, 5, 44, 'ما تورات را نازل کرده‌ایم در آن رهنمود و نوری هست که پیامبران اهل تسلیم و عالمان ربانی و احبار بر وفق آنچه از کتاب الهی به آنان سفارش کرده‌اند و بر آن گواهند، برای یهودیان داوری می‌کنند، پس از مردم نترسید و از من پروا کنید و آیات مرا به بهای ناچیز نفروشید، و کسانی که بر وفق آنچه خداوند نازل کرده است، حکم نکنند، کافرند'),
(714, 5, 45, 'و در آن بر آنان مقرر داشتیم که جان در برابر جان، و چشم در برابر چشم و بینی در برابر بینی و گوش در برابر گوش و دندان در برابر دندان و نیز همه ضرب و جرحها قصاص دارد، و هرکس که [از قصاص‌] درگذرد، این [عفو] در حکم کفاره [گناهان‌] اوست، و کسانی که بر وفق آنچه خداوند نازل کرده است، حکم نکنند، ستمگرند'),
(715, 5, 46, 'و به دنبال ایشان عیسی بن مریم را فرستادیم که گواهی‌دهنده بر [حقانیت‌] تورات بود که پیشاپیش او بود، و به او انجیل دادیم که در آن نوری هست و همخوان با تورات است که پیشاپیش آن [نازل شده‌] است، و راهنما و پندآموز پارسایان است‌'),
(716, 5, 47, 'و باید که اهل انجیل بر وفق آنچه در آن هست حکم کنند، و کسانی که بر وفق آنچه خداوند نازل کرده است، حکم نکنند نافرمانند'),
(717, 5, 48, 'و کتاب آسمانی [قرآن‌] را به راستی و درستی بر تو نازل کردیم که همخوان با کتابهای آسمانی پیشین و حاکم بر آنهاست، پس در میان آنان بر وفق آنچه خداوند نازل کرده است، داوری کن و به جای حق و حقیقتی که بر تو نازل شده از هوی و هوس آنان پیروی مکن برای هریک از شما راه و روشی معین داشته‌ایم و اگر خداوند می‌خواست شما را امت یگانه‌ای قرار می‌داد ولی [چنین کرد] تا شما را در آنچه به شما بخشیده است بیازماید پس به انجام خیرات بشتابید، بازگشت همگی شما به سوی خداست، آنگاه شما را به [حقیقت‌] آنچه اختلاف داشتید آگاه خواهد ساخت‌'),
(718, 5, 49, 'و در میان آنان بر وفق آنچه خداوند نازل کرده است، داوری کن و از هوی و هوس آنان پیروی مکن و از آنان بر حذر باش، مبادا که تو را از بعضی از آنچه خداوند بر تو نازل کرده است غافل کنند، و اگر رویگردان شدند بدان که خداوند می‌خواهد که آنان را به کیفر بعضی از گناهانشان دچار کند و بسیاری از مردم نافرمانند'),
(719, 5, 50, 'آیا حکم عهد جاهلیت را می‌پسندند؟ و برای اهل یقین داوری چه کسی بهتر از خداوند است؟'),
(720, 5, 51, 'ای مؤمنان یهودیان و مسیحیان را که [در مقابله با شما] دوست و هوادار یکدیگرند، به دوستی نگیرید، و هرکس از شما آنان را دوست نگیرد، و هر کس از شما آنان را دوست گیرد، جزو آنان است، خداوند ستمکاران را راهنمایی نمی‌کند'),
(721, 5, 52, 'آنگاه بیماردلان را می‌بینی که در کار آنان می‌کوشند و می‌گویند می‌ترسیم بلایی بر سرمان بیاید، چه بسا خداوند گشایش و رهایشی از سوی خویش پیش آورد تا سرانجام از آنچه در دل خویش نهفتند، پشیمان گردند'),
(722, 5, 53, 'و مؤمنان گویند آیا اینان بودند که سخت‌ترین سوگندهایشان را به نام خدا می‌خوردند که همراه [و همدست‌] شما هستند؟ اعمالشان تباه شده و زیانکار گردیده‌اند'),
(723, 5, 54, 'ای مؤمنان هر کس از شما از دینش [اسلام‌] برگردد، [بداند که‌] خداوند به زودی قومی به عرصه می‌آورد که دوستشان می‌دارد و آنان نیز او را دوست می‌دارند، اینان با مؤمنان مهربان و فروتن و با کافران سختگیرند، در راه خدا [به جهاد] می‌کوشند و از ملامت هیچ ملامت‌گری نمی‌هراسند، این فضل الهی است که به هر کس که بخواهد ارزانی می‌دارد و خداوند گشایشگر داناست‌'),
(724, 5, 55, 'همانا سرور شما خداوند است و پیامبر او و مؤمنانی که نماز را برپا می‌دارند و در حال رکوع زکات می‌دهند'),
(725, 5, 56, 'و کسانی که خداوند و پیامبر او و مؤمنان را دوست بدارند [بدانند که‌] خدا خواهان پیروزند'),
(726, 5, 57, 'ای مؤمنان کسانی را که دین شما را به ریشخند و بازیچه می‌گیرند، چه از کسانی که پیش از شما بدیشان کتاب داده شد، و چه کافران دوست نگیرید، و اگر [به راستی‌] مؤمنید از خداوند پروا کنید'),
(727, 5, 58, 'و چون بانگ نماز در دهید، آن را به ریشخند و بازیچه می‌گیرند، این از آن است که قومی نابخردند'),
(728, 5, 59, 'بگو ای اهل کتاب آیا جز به خاطر اینکه به خدا و آنچه بر ما و پیش از ما نازل شده است، ایمان داریم با ما سر ستیز دارید؟ و آیا جز به خاطر آن است که اغلب شما نافرمانید؟'),
(729, 5, 60, 'بگو آیا از کسانی که در نزد خداوند از این هم بد سرانجام‌تراند آگاهتان کنم؟ [اینان‌] کسانی هستند که خداوند لعنتشان کرده و بر آنان خشم گرفته و طاغوت را پرستیده‌اند و [خداوند] آنان را [همچون‌] بوزینه و خوک گردانده است، اینان بدمقام‌تر و از راه راست [از همه‌] گم‌گشته‌تراند'),
(730, 5, 61, 'و چون به نزد شما آیند گویند ایمان آورده‌ایم و حال آنکه هم با کفر پیش شما آمده و هم با کفر از نزد شما بیرون رفته‌اند، و خداوند به آنچه پنهان می‌داشتند آگاه‌تر است‌'),
(731, 5, 62, 'و بسیاری از آنان را می‌بینی که در گناهکاری و ستمگری و رشوه‌خواری می‌کوشند، چه بد است کار و کردارشان‌'),
(732, 5, 63, 'چرا علمای ربانی و احبار آنان را از سخنان ناشایسته‌شان و رشوه‌خواری‌شان نهی نمی‌کنند؟ چه بد است کار و کردارشان‌'),
(733, 5, 64, 'و یهودیان مدعی شدند که دست خداوند [از آفرینش یا بخشش‌] بسته است، دستان خودشان بسته باد، و به خاطر این سخن که گفتند لعنت بر آنان باد، آری دستان او گشاده است، هرگونه که خواهد ببخشد، و آنچه از سوی پروردگارت نازل می‌شود [نزول قرآن‌]، بر طغیان و کفر بسیاری از آنان می‌افزاید، و تا روز قیامت دشمنی و کینه در میانشان انداخته‌ایم، هربار که آتش جنگ افروختند خداوند آن را خاموش گرداند، و در این سرزمین به فتنه و فساد می‌کوشند و خداوند مفسدان را دوست ندارد'),
(734, 5, 65, 'و اگر اهل کتاب ایمان آورده و تقوا ورزند، سیئاتشان را می‌زداییم و ایشان را به بوستانهای [بهشتی‌] پرنعمت درمی‌آوریم‌'),
(735, 5, 66, 'و اگر آنان به تورات و انجیل و آنچه از سوی پروردگارشان بر آنها نازل شده است، عمل می‌کردند از برکات آسمانی و زمینی برخوردار می‌شدند، از آنان امت میانه‌رویی هستند، و بسیاری از آنان بدکردارند'),
(736, 5, 67, 'ای پیامبر آنچه از سوی پروردگارت بر تو نازل شده است [به مردم‌] برسان، و اگر چنین نکنی، رسالت او را نگزارده‌ای، و خداوند تو را از [آسیب‌] مردمان حفظ می‌کند، خداوند خدانشناسان را هدایت نمی‌کند'),
(737, 5, 68, 'بگو ای اهل کتاب مادام که به تورات و انجیل و آنچه از سوی پروردگارتان بر شما نازل شده است، عمل نکرده‌اید، حقی به دست ندارید، و آنچه از سوی پروردگارت بر تو نازل می‌شود [قرآن‌] بر طغیان و کفر بسیاری از آنان می‌افزاید، پس بر قوم کافر دریغ مخور'),
(738, 5, 69, 'از مؤمنان و یهودیان و صابئین و مسیحیان، هر کس که به خداوند و روز بازپسین ایمان آورده و نیکوکاری کرده باشد، نه بیمی بر آنهاست و نه اندوهگین می‌شوند'),
(739, 5, 70, 'به راستی از بنی‌اسرائیل پیمان گرفتیم و به سوی آنان پیامبرانی فرستادیم، [اما] هرگاه که پیامبری احکامی بر خلاف دلخواهشان آورد، گروهی را دروغگو انگاشتند و گروهی را کشتند'),
(740, 5, 71, 'و گمان بردند که آزمونی در کار نخواهد بود، لذا هوش و گوششان را از دست دادند، سپس خداوند از آنان درگذشت، دیگربار بسیاری از آنان هوش و گوششان را از دست دادند، و خداوند به آنچه می‌کنند بیناست‌'),
(741, 5, 72, 'به راستی کسانی که گفتند خداوند همان مسیح بن مریم است، کافر شدند، حال آنکه مسیح می‌گفت ای بنی‌اسرائیل خداوند را که پروردگار من و پروردگار شماست بپرستید، که هرکس به خداوند شرک آورد، خداوند بهشت را بر او حرام می‌گرداند و سرا و سرانجام او دوزخ است و ستمکاران [مشرک‌] یاوری ندارند'),
(742, 5, 73, 'به راستی کسانی که گفتند خداوند یکی از [اقانیم‌] سه‌گانه است کافر شدند، و خدایی جز خداوند یگانه نیست، و اگر از آنچه می‌گویند بازنایستند، کافرانشان به عذابی دردناک دچار خواهند شد'),
(743, 5, 74, 'چرا به [درگاه‌] خداوند توبه نمی‌کنند و از او آمرزش نمی‌خواهند، و خداوند آمرزگار مهربان است‌'),
(744, 5, 75, 'مسیح بن مریم جز پیامبری نیست که پیش از او هم پیامبرانی آمده بودند، و مادرش زن بسیار درستکاری بود و هر دو [مانند انسانهای دیگر] غذا می‌خوردند، بنگر که آیات خود را چگونه برای آنان روشن می‌سازیم و باز بنگر چگونه رویگردان می‌شوند'),
(745, 5, 76, 'بگو آیا به جای خداوند چیزی را می‌پرستید که نه زیانی برای شما دارد و نه سودی، و خداوند شنوای داناست‌'),
(746, 5, 77, 'بگو ای اهل کتاب به ناحق در دینتان از حد و حق نگذرید، و از هوی و هوس کسانی که هم خود گمراه شدند و هم بسیاری را گمراه کردند، و از راه راست به دور افتادند، پیروی نکنید'),
(747, 5, 78, 'کسانی از بنی‌اسرائیل که کفرورزیده بودند بر زبان داوود و عیسی بن مریم لعنت شدند، چرا که سرکشی کردند و از حد درگذشتند'),
(748, 5, 79, '[آنان‌] یکدیگر را از کار ناپسندی که می‌کردند، بازنمی‌داشتند، چه بد است کار و کردارشان‌'),
(749, 5, 80, 'بسیاری از آنان را می‌بینی که کافران را دوست می‌دارند، چه بد است آنچه برای خود پیش‌اندیشی کردند، که سرانجام خداوند بر آنان خشم گرفته و جاودانه در عذاب خواهند بود'),
(750, 5, 81, 'و اگر به خداوند و پیامبر [اسلام‌] و آنچه بر او نازل شده است ایمان داشتند، آنان را دوست نمی‌گرفتند، ولی بسیاری از آنان نافرمانند'),
(751, 5, 82, 'یهودیان و مشرکان را دشمن‌ترین مردم نسبت به مؤمنان [مسلمانان‌] می‌یابی، همچنین مهربان‌ترین مردم را نسبت به مؤمنان [مسلمانان‌] کسانی می‌یابی که می‌گویند ما مسیحی هستیم، این از آن است که در میان ایشان کشیشان و راهبانی [حق‌پرست‌] هستند و نیز از آن روی است که کبرنمی‌ورزند'),
(752, 5, 83, 'و [اینان‌] چون آنچه به پیامبر [اسلام‌] نازل شده است، بشنوند، می‌بینی که چشمانشان از اشک لبریز می‌شود از آنکه حق را می‌شناسند، [و] می‌گویند پروردگارا ما ایمان آورده‌ایم، ما را در زمره گواهان بنویس‌'),
(753, 5, 84, 'و چرا به خداوند و حق و حقیقتی که برای همه ما نازل شده است، ایمان نیاوریم و حال آنکه امید داریم که پروردگارمان ما را در زمره شایستگان درآورد'),
(754, 5, 85, 'و خداوند نیز به پاداش این سخن که گفتند بوستانهایی که جویباران از فرودست آن جاری است، نصیبشان می‌سازد که جاودانه در آن خواهند بود و این پاداش نیکوکاران است‌'),
(755, 5, 86, 'و کسانی که کفرورزیدند و آیات ما را دروغ انگاشتند، دوزخی‌اند'),
(756, 5, 87, 'ای مؤمنان چیزهای پاک و پسندیده‌ای را که خداوند بر شما روا داشته است، ناروا مشمارید، و از حد مگذرید که خداوند تجاوزکاران را دوست ندارد'),
(757, 5, 88, 'و از آنچه خداوند به شما روزی داده است و پاک و پاکیزه است بخورید و از خداوندی که به او ایمان دارید پروا داشته باشید'),
(758, 5, 89, 'خداوند شما را در سوگندهای بیهوده [و بی‌اختیار] بازخواست نمی‌کند، ولی در سوگندهایی که [آگاهانه‌] می‌خورید [و می‌شکنید] مؤاخذه می‌کند، کفاره [شکستن‌] آن اطعام ده بینواست از میانگین آنچه به خانواده خود می‌خورانید، یا لباس آنان، یا آزاد کردن یک برده، و هرکس که [اینها را] نیابد، بر اوست که سه روز روزه بگیرد، این کفاره سوگندهای شماست که می‌خورید [و می‌شکنید]، سوگندهای خود را حفظ کنید، خداوند بدین گونه آیات خویش را بر شما روشن می‌سازد، باشد که سپاس بگزارید'),
(759, 5, 90, 'ای مؤمنان شراب و قمار و انصاب و ازلام پلید و عمل شیطانی است، از آن پرهیز کنید باشد که رستگار شوید'),
(760, 5, 91, 'همانا شیطان می‌خواهد در پرداختن به شراب و قمار، بین شما دشمنی و کینه بیندازد، و شما را از یاد خداوند و از نماز بازدارد، آیا دست‌بردار هستید؟'),
(761, 5, 92, 'و از خداوند و پیامبر اطاعت کنید و پروا داشته باشید، و اگر رویگردان شدید بدانید که وظیفه پیامبر ما پیام‌رسانی آشکار است‌'),
(762, 5, 93, 'بر کسانی که ایمان آورده و کارهای شایسته کرده‌اند، چون تقوا ورزند و ایمان داشته باشند و کارهای شایسته کنند، سپس همچنان تقوا ورزند و ایمان داشته باشند، و همواره تقوا ورزیده و نیکوکاری کنند، در آنچه در گذشته [خمر] خورده‌اند، گناهی بر آنان نیست، و خداوند نیکوکاران را دوست دارد'),
(763, 5, 94, 'ای مؤمنان خداوند شما را به صیدی که در دسترس و نیزه‌رس شما هست، می‌آزماید، تا معلوم بدارد که چه کسی در نهان از او می‌ترسد، و هرکس که پس از این از حد درگذرد، عذابی دردناک [درپیش‌] دارد'),
(764, 5, 95, 'ای مؤمنان در حالی که محرم هستید شکار را نکشید و هرکس از شما که عمدا آن را بکشد، بر اوست که به کیفر این کار نظیر آنچه کشته است از چارپایان، که دو [داور] عادل از شما به همانندی آن حکم دهند، در حول و حوش کعبه قربانی کند، یا باید کفاره‌ای بدهد [که عبارت است از] طعام [شصت‌] بینوا یا برابر با آن [شصت روز] روزه بگیرد، تا کیفر کار [نادرست‌] خود را بچشد، و خداوند از گذشته‌ها گذشته است، ولی هرکس که بازگردد، خداوند از او انتقام می‌گیرد، و خداوند پیروزمند دادستان است‌'),
(765, 5, 96, 'صید دریایی و خوردن آن برای شما حلال شده است، تا هم شما و هم کاروانیان [غیر محرم‌] از آن برخوردار شوید، و صید بیابانی مادام که محرم هستید، بر شما حرام است، و از خدایی که به نزد او محشور می‌شوید پروا داشته باشید'),
(766, 5, 97, 'خداوند [زیارت‌] کعبه را که بیت‌الحرام است، و نیز ماه‌[های‌] حرام را و [رسم‌] قربانیهای نشاندار و قربانیهای قلاده‌دار، همه را مایه قوام [دین و دنیای‌] مردم قرار داده است، آری تا بدانید خداوند هر چه را که در آسمانها و زمین است می‌داند و به هر چیزی آگاه است‌'),
(767, 5, 98, 'بدانید که خداوند سخت کیفر و همچنین خداوند آمرزگار مهربان است‌'),
(768, 5, 99, 'جز پیام‌رسانی چیزی بر عهده پیامبر نیست، و خداوند آنچه آشکار می‌دارید و آنچه پنهان می‌دارید، می‌داند'),
(769, 5, 100, 'بگو پلید و پاک برابر نیستند ولو اینکه فراوانی پلید تو را به شگفتی اندازد، ای خردمندان از خداوند پروا کنید باشد که رستگار شوید'),
(770, 5, 101, 'ای مؤمنان از چیزهایی پرس و جو نکنید که چون بر شما آشکار شود، شما را اندوهگین کند، و اگر در زمانی که قرآن نازل می‌گردد، پرس و جو کنید، [حکم و تکلیف شاق آن‌] بر شما آشکار می‌گردد، خداوند از گذشته‌ها گذشته است و خدا آمرزگار مهربان است‌'),
(771, 5, 102, 'قومی که پیش از شما بودند، نظیر آن [مساله‌] ها را پرسیدند، سپس به همان سبب کافر شدند'),
(772, 5, 103, 'خداوند نه در مورد بحیره و نه سائبه و نه وصیله، و نه حامی حکمی مقرر نداشته است، ولی کافران بر خداوند دروغ می‌بندند و بیشترشان تعقل نمی‌کنند'),
(773, 5, 104, 'و چون به ایشان گفته شود از آنچه خداوند نازل کرده است و از پیامبر پیروی کنید، گویند آنچه نیاکانمان را بر آن یافته‌ایم، برای ما کافی است، حتی اگر نیاکانشان چیزی ندانسته و راهی نیافته باشند؟'),
(774, 5, 105, 'ای مؤمنان شما مسؤولیت [حفظ و هدایت‌] خودتان را دارید، چون شما راهیافته باشید، کسی که به بیراهه رفته باشد، زیانی به شما نمی‌رساند، بازگشت همگی شما به سوی خداوند است، آنگاه شما را از [حقیقت و نتیجه‌] آنچه انجام داده‌اید، آگاه می‌سازد'),
(775, 5, 106, 'ای مؤمنان چون یکی از شما را مرگ فرا رسد، به هنگام وصیت باید دو تن از عادلان خودتان [مسلمانان‌]، یا اگر در سفر دچار مصیبت مرگ شدید، دو تن از غیر خودتان [اهل کتاب‌] شاهد و حاضر باشند، و اگر در حق آنان شک دارید، آنان را نگاه دارید تا بعد از نماز به خداوند سوگند یاد کنند که ما آن [شهادت یا سوگند خود] را به هیچ قیمتی نمی‌فروشیم، اگر چه به سود خویشاوندمان باشد، و شهادت الهی را پنهان نمی‌کنیم، که در غیر این صورت از گناهکاران خواهیم بود'),
(776, 5, 107, 'و اگر معلوم شود که آنان مرتکب گناه [خیانت‌] شده‌اند، آنگاه دو تن از کسانی که بر آنان جفا رفته است و از نزدیکان [متوفی‌] هستند به جای آنان قیام کنند، و به خداوند سوگند بخورند که شهادت ما از شهادت آن دو درست‌تر است و ما از حق تجاوز نمی‌کنیم و در غیر این صورت از ستمکاران خواهیم بود'),
(777, 5, 108, 'این نزدیکتر است به آنکه شهادت چنانکه باید و شاید ادا شود، یا شاهدان بترسند که مبادا سوگندی بعد از سوگند آنان [و بر خلاف آنان‌] به میان آید، و از خداوند پروا کنید و گوش شنوا داشته باشید و [بدانید که‌] خداوند نافرمانان را هدایت نمی‌کند'),
(778, 5, 109, 'روزی [فرارسد] که خداوند پیامبران را گرد آورد و بپرسد که [امتتان‌] چه پاسخی به شما دادند؟ گویند ما علمی [به حقیقت امر] نداریم، تویی که دانای رازهای نهانی‌'),
(779, 5, 110, 'چنین بود که خداوند گفت ای عیسی بن مریم نعمتم را بر خود و بر مادرت به یادآور که تو را به روح‌القدس یاری دادم که در گهواره [به اعجاز] و در میانسالی [به وحی‌] با مردم سخن گفتی، و یاد کن که به تو کتاب و حکمت و تورات و انجیل آموختم، و یاد کن که به اذن من از گل چیزی به هیئت پرنده می‌ساختی و در آن می‌دمیدی و آن به اذن من پرنده [ای جاندار] می‌شد و به اذن من نابینای مادرزاد و پیس را بهبود می‌بخشیدی و یاد کن که مردگان را به اذن من زنده [از گور] بیرون می‌آوردی، و یاد کن که چون برای بنی‌اسرائیل معجزاتی آوردی [آسیب و آزار] آنان را از تو بازداشتم، آنگاه کفرپیشگان ایشان می‌گفتند این جز جادوی آشکار نیست‌'),
(780, 5, 111, 'و چنین بود که به حواریون الهام کردم که به من و پیامبرم ایمان آورید، گفتند ایمان آوردیم و گواه باش که ما فرمانبرداریم‌'),
(781, 5, 112, 'و چنین بود که حواریون گفتند ای عیسی بن مریم آیا پروردگارت می‌تواند برای ما مائده‌ای از آسمان نازل کند؟ گفت اگر مؤمنید از خداوند پروا کنید'),
(782, 5, 113, 'گفتند می‌خواهیم از آن بخوریم و دلهایمان آرام گیرد و بدانیم که به ما راست گفته‌ای و خود از گواهان آن باشیم‌'),
(783, 5, 114, 'عیسی بن مریم گفت بار پروردگارا برای ما مائده‌ای از آسمان نازل فرما که عیدی برای امروزیان و آیندگانمان و معجزه‌ای از تو باشد، و به ما روزی ببخش که تو بهترین روزی‌دهندگانی‌'),
(784, 5, 115, 'خداوند گفت من فرو فرستنده آن بر شما هستم، ولی پس از آن اگر کسی از شما انکارورزد او را به چنان عذابی دچار می‌سازم که هیچ‌یک از جهانیان را نکرده باشم‌'),
(785, 5, 116, 'و چنین بود که خداوند گفت ای عیسی بن مریم آیا تو به مردم گفتی که من و مادرم را همچون دو خدا به جای خداوند بپرستید؟ گفت پاکا که تویی مرا نرسد که چیزی را که حد من و حق من نیست گفته باشم، اگر گفته بودم بی‌شک می‌دانستی که آنچه در ذات من است می‌دانی و من آنچه در ذات توست نمی‌دانم، تویی که دانای رازهای نهانی‌'),
(786, 5, 117, 'به آنان چیزی جز آنچه به من فرمان داده‌ای نگفته‌ام گفته‌ام که خداوند را که پروردگار من و پروردگار شماست بپرستید، و مادام که در میان آنان بودم بر آنان شاهد و ناظر بودم و چون روح مرا گرفتی تو خود [ناظر و] نگاهبان آنان بودی و تو بر همه چیز شاهدی‌'),
(787, 5, 118, 'اگر آنان را عذاب کنی، بندگان تو هستند، و اگر از آنان درگذری تو پیروزمند فرزانه‌ای‌'),
(788, 5, 119, 'خداوند فرمود امروز روزی است که راستگویان را راستی و درستی‌شان سود رساند، برای آنان بوستانهایی [بهشتی‌] است که جویباران از فرودست آن جاری است، و جاودانه در آنند و هم خداوند از آنان خشنود است و هم آنان از خداوند خشنودند، و این رستگاری بزرگ است‌'),
(789, 5, 120, 'فرمانروایی آسمانها و زمین و آنچه در میان آنهاست، از آن خداوند است، و او بر هر کاری تواناست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'سپاس خداوندی را که آسمانها و زمین را آفرید و تاریکی و روشنایی را پدید آورد، آنگاه کافران برای پروردگارشان شریک قائل می‌شوند'),
(791, 6, 2, 'او کسی است که شما را از گل آفرید، سپس [شما را در دنیا] عمری مقرر داشت، و [برای آخرت‌] سر رسیدی در علم اوست، آنگاه [بعضی از شما] تردید می‌ورزید'),
(792, 6, 3, 'و او در آسمانها و زمین خداست، نهان و آشکارای شما و کار و کردارتان را می‌داند'),
(793, 6, 4, 'هیچ آیه‌ای از آیات پروردگارشان برای آنان [مشرکان‌] نیامد مگر آنکه از آن رویگردان بودند'),
(794, 6, 5, 'و حق را چون بر ایشان نازل شد، دروغ انگاشتند، به زودی [حقیقت‌] اخبار آنچه بدان استهزاء کردند، بر ایشان آشکار خواهد شد'),
(795, 6, 6, 'آیا نیندیشیده‌اند که پیش از آنان چه بسیار اقوامی را که به آنان در روی زمین امکان و اقتداری داده بودیم که به شما نداده بودیم، نابود کردیم و برای آنان بسی باران [و برکت‌] از آسمان نازل کردیم و چه رودها که از برای آنان جاری ساختیم، آنگاه آنان را به کیفر گناهانشان نابود کردیم و بعد از آنان قومی دیگر پدید آوردیم‌'),
(796, 6, 7, 'و اگر کتابی نوشته بر کاغذ بر تو نازل می‌کردیم و آنان با دستان خویش آن را لمس می‌کردند، باز هم کافران می‌گفتند این جز جادوی آشکاری نیست‌'),
(797, 6, 8, 'و گفتند چرا فرشته‌ای بر او نازل نمی‌شود؟ و اگر فرشته‌ای نازل می‌کردیم، کار به پایان رسیده بود، و دیگر مجال و مهلتی نمی‌یافتند'),
(798, 6, 9, 'و اگر او [پیامبر] را فرشته‌ای می‌گرداندیم، آن [فرشته‌] را به صورت مردی در می‌آوردیم، و امر را همچنان بر آنان مشتبه می‌ساختیم‌'),
(799, 6, 10, 'و پیش از تو پیامبرانی را که بودند استهزاء کردند، و کیفر استهزایشان بر سر ریشخندکنندگانشان فرود آمد'),
(800, 6, 11, 'بگو در زمین سیر و سفر کنید، سپس بنگرید که سرانجام دروغ انگاران چگونه بوده است‌'),
(801, 6, 12, 'بگو آنچه در آسمانها و زمین است از آن کیست؟ [و در پاسخ‌] بگو از آن خداوند است، که بر خود رحمت را مقرر داشته است، [و] در روز قیامت، که در فرارسیدن آن شکی نیست، شما را گرد می‌آورد، کسانی که بر خود زیان زده‌اند، ایمان نمی‌آورند'),
(802, 6, 13, 'و آنچه در شب و روز آرام [و تکاپو] دارد از آن اوست، و او شنوای داناست‌'),
(803, 6, 14, 'بگو آیا غیر از خداوند را سرور خود گیرم که آفرینشگر آسمانها و زمین است؟ و کسی است که به همگان روزی می‌خوراند و خود روزی نمی‌خورد بگو به من فرمان داده شده است که نخستین کسی باشم که سر [به طاعت و توحید] می‌نهد و [به من گفته شده است که‌] هرگز از مشرکان مباش‌'),
(804, 6, 15, 'بگو اگر از حکم پروردگارم سرکشی کنم از عذاب روز بزرگ می‌ترسم‌'),
(805, 6, 16, 'هرکس که در چنین روزی [بلا] از او بگردد، به راستی [خداوند] بر او رحمت آورده است، و این رستگاری آشکاری است‌'),
(806, 6, 17, 'و اگر خداوند بلایی به تو برساند، بازدارنده‌ای جز خود او برای آن وجود ندارد، و اگر بخواهد خیری به تو برساند بدان که او بر هر کاری تواناست‌'),
(807, 6, 18, 'و او بر بندگان خود چیره است و او فرزانه آگاه است‌'),
(808, 6, 19, 'بگو [و بپرس‌] شهادت چه کسی برتر است، بگو خدا که بین ما و شما گواه است، و این قرآن بر من وحی می‌شود که بدان شما را و هر کس را که [این پیام‌] به او برسد، هشدار دهم، آیا شما شهادت می‌دهید که خدایان دیگری در جنب خداوند وجود دارد؟ بگو من چنین شهادتی نمی‌دهم، بگو همانا او خدای یگانه است، و من از شریکی که می‌آورید، بری و برکنارم‌'),
(809, 6, 20, 'اهل کتاب او [پیامبر] را به همان گونه می‌شناسند که فرزندانشان را می‌شناسند، کسانی که بر خود زیان زده‌اند ایمان نمی‌آورند'),
(810, 6, 21, 'و کیست ستمکارتر از کسی که بر خداوند دروغ بندد یا آیات او را دروغ انگارد، آری ستمکاران [مشرکان‌] رستگار نمی‌شوند'),
(811, 6, 22, 'و روزی که همه آنان را گرد آوریم، آنگاه به مشرکان گوییم که شرکایتان که ادعا می‌کردید کجا هستند؟'),
(812, 6, 23, 'آنگاه عاقبت شرک آنان جز این نیست که می‌گویند سوگند به خدا، پروردگارمان که ما مشرک نبودیم‌'),
(813, 6, 24, 'بنگر چگونه بر خود دروغ می‌بندند و بر ساخته‌هایشان بر باد رفته است‌'),
(814, 6, 25, 'و از ایشان کسانی هستند که [به ظاهر] به تو گوش می‌دهند، و ما بر دلهایشان پرده‌هایی کشیده‌ایم که آن را درنیابند و در گوش آنان سنگینی‌ای نهاده‌ایم، و اگر هر معجزه‌ای ببینند به آن ایمان نمی‌آورند، و چون به نزد تو آیند با تو مجادله می‌کنند و کافران می‌گویند این جز افسانه‌های پیشینیان نیست‌'),
(815, 6, 26, 'و ایشان [دشمنان را] از او [پیامبر] دور می‌دارند ولی خود نیز از او دور می‌مانند، جز خود را به نابودی نمی‌اندازند ولی نمی‌دانند'),
(816, 6, 27, 'و اگر آنان را بینی که بر آتش دوزخ بداشته‌اند [شگرفیها خواهی دید] و گویند کاش ما را باز می‌گرداندند و دیگر آیات پروردگارمان را انکار نمی‌کردیم و از مؤمنان می‌شدیم‌'),
(817, 6, 28, 'آری آنچه در گذشته پنهان می‌داشتند بر آنان آشکار شد و اگر باز گردانده می‌شدند، بی‌شک به همانچه از آن نهی شده بودند، بر می‌گشتند و آنان دروغگو هستند'),
(818, 6, 29, 'و می‌گفتند حیاتی جز همین زندگی دنیویمان در کار نیست و ما برانگیختنی نیستیم‌'),
(819, 6, 30, 'و اگر بنگری آنگاه که ایشان را در پیشگاه پروردگارشان نگاه داشته‌اند [به آنان‌] گوید آیا این حق نیست؟ گویند چرا سوگند به پروردگارمان گوید پس به خاطر کفری که می‌ورزیدید عذاب را بچشید'),
(820, 6, 31, 'کسانی که لقای الهی را دروغ انگاشتند، زیانکار شدند، و آنگاه که قیامت بناگهان فرارسد، گویند دریغ بر ما که چه بسیار غفلت و قصور کردیم، و اینان بار گناهانشان را به دوش کشند، آری چه بد است آنچه بر دوش کشند'),
(821, 6, 32, 'و زندگانی دنیا جز بازیچه و سرگرمی نیست، و سرای آخرت برای پرهیزگاران بهتر است، آیا نمی‌اندیشید؟'),
(822, 6, 33, 'نیک می‌دانیم که آنچه ایشان می‌گویند اندوهگینت می‌کند، بدان که ایشان تو را دروغگو نمی‌انگارند بلکه کفرپیشگان آیات الهی را انکار می‌کنند'),
(823, 6, 34, 'و پیامبران پیش از تو نیز دروغگو انگاشته شدند، ولی بر آن انکار و آزاری که دیدند شکیبایی‌ورزیدند تا یاری ما به دادشان رسید، و کلمات الهی دگرگون‌کننده‌ای ندارد و به راستی اخبار پیامبران به تو رسیده است‌'),
(824, 6, 35, 'و اگر رویگردانی آنان بر تو گران می‌آید، در صورتی که می‌توانی نقبی در زمین یا نردبانی در آسمان بجویی و برای آنان معجزه‌ای بیاوری [چنین کن‌] و اگر خداوند می‌خواست آنان را بر طریق هدایت فرا می‌آورد، پس از نادانان مباش‌'),
(825, 6, 36, 'فقط کسانی که گوش شنوا[ی حق‌] دارند، لبیک اجابت می‌گویند، و مردگان را خداوند بر می‌انگیزد، سپس [همگان‌] به سوی او بازگردانده می‌شوند'),
(826, 6, 37, 'و گویند چرا بر او معجزه‌ای از سوی پروردگارش نازل نمی‌شود؟ بگو بی‌گمان خداوند توانای آن هست که معجزه‌ای نازل کند ولی بیشتر آنان نمی‌دانند'),
(827, 6, 38, 'و هیچ جنبنده‌ای در زمین و هیچ پرنده‌ای نیست که با دو بال خویش می‌پرد، مگر آنکه امتهایی همانند شما هستند، ما هیچ چیز را در کتاب [لوح محفوظ] فروگذار نکرده‌ایم، آنگاه همه به نزد پروردگارشان محشور می‌گردند'),
(828, 6, 39, 'و دروغ‌انگاران آیات ما ناشنوا و گنگ و در تاریکی [سرگشته‌]اند، خداوند هرکس را بخواهد بیراه می‌گذارد، و هرکس را بخواهد بر راه راست می‌دارد'),
(829, 6, 40, 'بگو بیندیشید اگر عذاب الهی یا قیامت فرارسد، آیا اگر راست می‌گویید کسی جز خدا را می‌خوانید؟'),
(830, 6, 41, 'آری تنها او را [به دعا] می‌خوانید و اگر او بخواهد رنج و بلا را از شما دور می‌گرداند و شما شریکی را که برای او قائل شده‌اید، از یاد می‌برید'),
(831, 6, 42, 'پیش از تو [پیامبرانی‌] به سوی امتها[ی پیشین‌] فرستادیم و آنان را به تنگدستی و ناخوشی دچار ساختیم تا زاری و خاکساری کنند'),
(832, 6, 43, 'پس چرا هنگامی که عذاب ما به سراغشان آمد زاری و خاکساری نکردند؟ زیرا دلهایشان سخت شده و شیطان کار و کردارشان را در نظرشان آراسته بود'),
(833, 6, 44, 'آنگاه چون پند و هشدارها را فراموش کردند، درهای نعمت را بر آنان گشودیم و چون بدانچه دریافته بودند سرمست شدند، ناگهان فرو گرفتیمشان و آنگاه بود که نومید شدند'),
(834, 6, 45, 'و ریشه ستمکاران [مشرک‌] برکنده شد، و سپاس خداوند را که پروردگار جهانیان است‌'),
(835, 6, 46, 'بگو بیندیشید اگر خداوند شنوایی و دیدگان شما را بگیرد و بر دلهایتان مهر نهد، چه خدایی غیر از خداوند آنها را به شما برمی‌گرداند؟ بنگر که چگونه آیات را گونه‌گونه بیان می‌داریم، آنگاه ایشان رویگردانند'),
(836, 6, 47, 'بگو بیندیشید اگر عذاب الهی ناگهان [در شب‌] یا آشکارا [در روز] به سراغتان بیاید آیا [قومی‌] جز قوم ستمکار [مشرک‌] نابود می‌شوند؟'),
(837, 6, 48, 'و ما پیامبران را جز مژده‌آور و هشداردهنده نمی‌فرستیم، و کسانی که ایمان آورند و نیکوکاری کنند نه بیمی بر آنهاست و نه اندوهگین می‌شوند'),
(838, 6, 49, 'و دروغ‌انگاران آیات ما به خاطر نافرمانیشان گرفتار عذاب گردند'),
(839, 6, 50, 'بگو من به شما نمی‌گویم که خزاین الهی نزد من است، و غیب نیز نمی‌دانم، و به شما نمی‌گویم که من فرشته‌ای هستم، من از هیچ چیز پیروی نمی‌کنم جز از آنچه به من وحی می‌شود، بگو آیا نابینا و بینا برابرند، آیا نمی‌اندیشید؟'),
(840, 6, 51, 'و کسانی را که از محشور شدن به نزد پروردگارشان اندیشناکند، به آن [قرآن‌] هشدار بده، در برابر او [خداوند] یاور و شفیعی ندارند باشد که پروا پیشه کنند'),
(841, 6, 52, 'و کسانی را که پروردگارشان را بامدادان و شامگاهان [به نیایش‌] می‌خوانند و خشنودی او را می‌جویند[از خود] مران، چیزی از حساب آنان بر تو و چیزی از حساب تو بر آنان نیست که برانی‌شان و از ستمکاران شوی‌'),
(842, 6, 53, 'و این چنین بعضی از آنان را به بعضی دیگر آزمودیم، تا سرانجام بگویند آیا اینانند که خداوند از میان همه ما فقط بر آنان منت نهاده [و نعمت داده‌] است، آیا خداوند خود به احوال سپاسگزاران آگاه‌تر نیست؟'),
(843, 6, 54, 'و چون کسانی که به آیات ما ایمان دارند، به نزد تو آیند، بگو سلام بر شما، پروردگارتان رحمت را بر خود مقرر داشته است، هرکس از شما که از روی نادانی مرتکب کار ناشایستی شود، سپس توبه و درستکاری کند و به صلاح آید [بداند که‌] خداوند آمرزگار مهربان است‌'),
(844, 6, 55, 'و بدین‌سان آیات خود را به روشنی بیان می‌داریم، تا راه و رسم گناهکاران آشکار گردد'),
(845, 6, 56, 'بگو مرا نهی کرده‌اند که مبادا آنچه را به جای خدا می‌پرستید، بپرستم بگو از هوی و هوسهای شما پیروی نمی‌کنم، و اگر پیروی کنم به گمراهی افتاده‌ام و از رهیافتگان نیستم‌'),
(846, 6, 57, 'بگو من حجت آشکاری از پروردگارم دارم و شما آن را دروغ می‌انگارید، آنچه با [شدت و] شتاب از من می‌خواهید در اختیار من نیست، حکم جز به دست خداوند نیست، که گویای حق و حقیقت و بهترین داوران است‌'),
(847, 6, 58, 'بگو اگر آنچه با [شدت و] شتاب از من می‌خواهید، در اختیار من بود، بین من و شما کار یکسره می‌شد و خداوند به ستمکاران آگاه‌تر است‌'),
(848, 6, 59, 'و کلیدهای [گنجینه‌های‌] غیب نزد اوست و هیچ‌کس جز او آن را نمی‌داند، و آنچه در خشکی و دریاست می‌داند و هیچ برگی [از درخت‌] نمی‌افتد مگر آنکه آن را می‌داند، و هیچ دانه‌ای در تاریکیهای [توبرتوی‌] زمین و هیچ تر و خشکی نیست مگر آنکه در کتاب مبینی [مسطور] است‌'),
(849, 6, 60, 'و او کسی است که روح شما را در شب [به هنگام خواب‌] می‌گیرد و می‌داند که در روز چه کرده‌اید، سپس در آن شما را [از خواب‌] بر می‌انگیزد تا مهلت معینی سرآید، آنگاه بازگشت شما به سوی اوست و آنگاه شما را از [حقیقت‌] کار و کردارتان آگاه می‌گرداند'),
(850, 6, 61, 'و او بر بندگان خود چیره است و بر شما نگهبانانی می‌فرستد، و چون مرگ یکی از شما فرارسد، فرشتگان ما روح او را می‌گیرند و فروگذار نمی‌کنند'),
(851, 6, 62, 'آنگاه به سوی خداوند، که سرور راستینشان است، بازگردانده می‌شوند، بدانید که حکم او راست و او سریع‌ترین حسابرسان است‌'),
(852, 6, 63, 'بگو چه کسی شما را از تاریکیها [و شداید] خشکی و دریا می‌رهاند، [زمانی‌] که او را به زاری و پنهانی می‌خوانید [و می‌گویید] اگر ما را از این [مهلکه‌] نجات داد، بی‌گمان از سپاسگزاران خواهیم شد'),
(853, 6, 64, 'بگو خداوند شما را از آن نجات می‌دهد و نیز از هر گرفتاری، و باز شما شرک می‌ورزید'),
(854, 6, 65, 'بگو او تواناست به اینکه به شما عذابی از فراز یا فرودتان برانگیزد یا شما را گرفتار اختلاف کلمه کند و به بعضی از شما شر و بلای بعضی دیگر را بچشاند، بنگر چگونه آیات را گونه‌گون بیان می‌داریم تاایشان دریابند'),
(855, 6, 66, 'و قوم تو آن را دروغ انگاشتند و حال آنکه آن حق است، بگو من نگهبان شما نیستم‌'),
(856, 6, 67, 'هر خبری سرانجامی دارد، و به زودی خواهید دانست‌'),
(857, 6, 68, 'و چون کسانی را که در آیات ما کندوکاو می‌کنند دیدی از آنان روی بگردان تا به سخنی غیر از آن بپردازند و اگر شیطان تو را به فراموشی انداخت، بعد از آنکه به یاد آوردی با قوم ستمکار [مشرک‌] منشین‌'),
(858, 6, 69, 'و از حساب آنان چیزی بر عهده پرهیزگاران نیست، ولی این یادآوری است، تا پروا پیشه کنند'),
(859, 6, 70, 'و کسانی را که دینشان را [چون‌] بازی و سرگرمی گرفته‌اند و زندگانی دنیا فریبشان داده است، رها کن و به آن پند بده، تا مبادا کسی به سبب کار و کردارش به مهلکه افتد، که او را در برابر خداوند نه یاوری هست و نه شفیعی، و اگر هر فدیه‌ای بدهد، از او پذیرفته نمی‌شود، اینان کسانی هستند که به سبب کار و کردارشان به مهلکه افتاده‌اند، و به خاطر کفری که ورزیده‌اند، نوشابه‌ای از آب گرم و عذابی دردناک [در پیش‌] دارند'),
(860, 6, 71, 'بگو آیا به جای خداوند چیزی را بپرستیم که نه سودی به ما می‌رساند و نه زیانی، و آیا بعد از آنکه خداوند هدایتمان کرد از عقیده خود بازگردیم، همانند کسی که شیطان راهش را زده باشد، که در بیابان [سرگشته و] حیران ماند و یارانی داشته باشد که او را به هدایت بخوانند [و به او گویند] به سوی ما بیا، بگو بی‌گمان هدایت، بخوانند [و به او گویند] به سوی ما بیا، بگو بی‌گمان هدایت، همان هدایت الهی است، و به ما فرمان داده شده است که در برابر پروردگار جهانیان تسلیم باشیم‌'),
(861, 6, 72, 'و اینکه نماز را برپا دارید و از او پروا کنید و اوست که به نزد او محشور می‌شوید'),
(862, 6, 73, 'و اوست که آسمانها و زمین را به حق آفرید و روزی که بگوید موجود شو، بی‌درنگ موجود شود، سخن او حق است، و روزی که در صور دمیده شود، فرمانروایی از آن اوست، اوست که دانای پنهان و پیداست، و اوست که فرزانه آگاه است‌'),
(863, 6, 74, 'و چنین بود که ابراهیم به پدرش آزر گفت آیا بتان را به خدایی می‌گیری؟ من تو و قومت را در گمراهی آشکار می‌بینم‌'),
(864, 6, 75, 'و بدین‌سان ملکوت آسمانها و زمین را به ابراهیم می‌نمایانیم تا از اصحاب یقین گردد'),
(865, 6, 76, 'زمانی که شب بر او پرده انداخت، ستاره‌ای دید و گفت این پروردگار من است، آنگاه چون افول کرد، گفت افول‌کنندگان را دوست ندارم‌'),
(866, 6, 77, 'آنگاه چون ماه را تابان دید، گفت این پروردگار من است، و چون افول کرد گفت اگر پروردگارم مرا راهنمایی نکند بی‌گمان از گمراهان خواهم شد'),
(867, 6, 78, 'آنگاه چون خورشید را تابان دید گفت این پروردگار من است، این بزرگتر است، و چون افول کرد گفت ای قوم من، من از شرکی که می‌ورزید، بری و برکنارم‌'),
(868, 6, 79, 'من پاکدینانه روی دل می‌نهم به سوی کسی که آسمانها و زمین را آفریده است، و من از مشرکان نیستم‌'),
(869, 6, 80, 'و قوم او با او محاجه می‌کردند، او به ایشان گفت آیا درباره خداوند با من محاجه می‌کنید، و حال آنکه مرا به راه راست هدایت کرده است و از [شر] شرک شما نمی‌ترسم مگر آنکه پروردگارم چیزی [و خوف و خطری برایم‌] خواسته باشد، پروردگار من بر همه چیز احاطه علمی دارد، آیا پند نمی‌گیرید؟'),
(870, 6, 81, 'و چگونه از آنچه [برای خدا] شریک می‌آورید، بترسم در حالی که شما از اینکه برای خداوند شریک آورده‌اید، و هیچ حجتی در این باره به دست شما نداده است، نمی‌ترسید، پس اگر می‌دانید کدامیک از دو گروه سزاوارتر به امن [و امان‌] است؟'),
(871, 6, 82, 'کسانی که ایمان آورده‌اند و ایمانشان را به شرک نیالوده‌اند، آنانند که امن [و امان‌] دارند و آنانند که رهیافته‌اند'),
(872, 6, 83, 'و این حجت ماست که به ابراهیم در برابر قومش بخشیدیم، درجات هرکس را که بخواهیم فرا می‌بریم، بی‌گمان پروردگار تو فرزانه داناست‌'),
(873, 6, 84, 'و به او اسحاق و یعقوب را بخشیدیم و همه را هدایت کردیم، و نیز پیش از آن نوح را راه نمودیم و داوود و سلیمان و ایوب و یوسف و موسی و هارون را که از زاد و رود او بودند، و بدین‌سان نیکوکاران را جزا می‌دهیم‌'),
(874, 6, 85, 'و نیز زکریا و یحیی و عیسی و الیاس را، که همگی از شایستگان بودند'),
(875, 6, 86, 'و نیز اسماعیل و الیسع و یونس و لوط را و همگیشان را بر جهانیان برتری دادیم‌'),
(876, 6, 87, 'و برخی از پدرانشان و فرزندانشان و برادرانشان را، و آنان را برگزیدیم و به راهی راست هدایت کردیم‌'),
(877, 6, 88, 'این هدایت الهی است که هرکس از بندگانش را که بخواهد به آن راه می‌نماید، و اگر شرک ورزیده بودند، اعمالشان تباه شده بود'),
(878, 6, 89, 'اینان کسانی هستند که بدیشان کتاب و حکمت و نبوت بخشیدیم، اگر اینان [معاصران پیامبر] به آن [شریعت الهی‌] کفرورزند، قومی را بر آن گماریم که به آن کافر نباشند'),
(879, 6, 90, 'اینان کسانی هستند که خداوند هدایتشان کرده است، پس به هدایت آنان اقتداء کن، بگو برای آن از شما مزدی نمی‌طلبم، این جز پندآموزی برای جهانیان نیست‌'),
(880, 6, 91, 'و خداوند را چنانکه سزاوار ارج اوست، ارج نگذاشتند چرا که گفتند خداوند هیچ چیز بر هیچ بشری نازل نکرده است، بگو کتابی را که موسی آورد و روشنگر و رهنمودی برای مردم بود و آن را بر کاغذها نوشته بودید و [بعضی را] آشکار و بسیاری از آن را پنهان می‌دارید چه کسی نازل کرده است؟ و آنچه شما و پدرانتان نمی‌دانستید به شما در آموخته‌اند، بگو خدا [آن را نازل کرده است‌]، سپس آنان را در کند و کاوشان رها کن که بازی کنند'),
(881, 6, 92, 'و این کتابی است مبارک که فرو فرستاده‌ایم، و همخوان با کتابی است که پیشاپیش آن است، تا [مردم‌] مکه و پیرامونیان آن را هشدار دهی و مؤمنان به آخرت، به آن ایمان می‌آورند و آنان مواظبت بر نمازهای خویش دارند'),
(882, 6, 93, 'و کیست ستمکارتر از کسی که بر خداوند دروغ بندد، یا بگوید بر من وحی شده است و چیزی بر او وحی نشده باشد، و کسی که گوید به زودی نظیر آنچه خداوند نازل کرده است، نازل می‌کنم، و اگر بنگری که ستمکاران [مشرکان‌] در سکرات مرگ‌اند و فرشتگان [بر آنان‌] دست گشوده‌اند که جانهایتان را تسلیم کنید، امروز به سبب آنچه به ناحق به خداوند نسبت می‌دادید و در برابر آیات او کبر می‌ورزیدید، به کیفر عذاب خوارکننده می‌رسید'),
(883, 6, 94, 'و به نزد ما یکه و تنها آمده‌اید، چنانکه نخستین بار نیز شما را همین‌گونه آفریده بودیم، و آنچه از ناز و نعمت که به شما بخشیده بودیم در پس پشت خود رها کرده‌اید و شفیعانتان را که گمان می‌کردید با [خدا در عبادت‌] شما شریک هستند، همراه شما نمی‌بینیم، پیوند بین شما گسسته شد و آنچه [شریک و شفیع‌] می‌انگاشتید، بر باد رفت‌'),
(884, 6, 95, 'خداوند شکافنده دانه و هسته است، زنده را از مرده بیرون می‌آورد و بیرون آورنده مرده از زنده است، چنین است خدای شما، چگونه به بیراهه می‌روید؟'),
(885, 6, 96, '[هموست که‌] شکافنده صبح است و شب را آرام‌بخش گردانده است، و خورشید و ماه را شماری مقرر داشته است، تقدیر [خداوند] پیروزمند دانا چنین است‌'),
(886, 6, 97, 'و هموست که ستارگان را برای شما آفرید تا در تاریکیهای خشکی و دریا راه خود را پیدا کنید، به راستی که آیات [خود] را برای اهل معرفت به روشنی بیان کرده‌ایم‌'),
(887, 6, 98, 'و اوست که شما را از تن یگانه‌ای پدید آورد و آنگاه [شما را] قرارگاه و ودیعت گاهی است، به راستی که آیات [خود] را برای قومی که درمی‌یابند به روشنی بیان کرده‌ایم‌'),
(888, 6, 99, 'و اوست که از آسمان، آبی فروفرستاد و بدان هرگونه گیاه برآوردیم و از آن جوانه سبز رویاندیم و از آن دانه‌های درهم رسته بیرون آوریم، و از شکوفه خرما خوشه‌های دسترس و باغهای انگور و [درختان‌] زیتون و انار [پدید آوردیم‌] که همانند و ناهمانند هستند، به میوه‌های آن چون میوه دهد و برسد بنگرید، در این برای اهل ایمان، مایه‌های عبرت است‌'),
(889, 6, 100, 'و [مشرکان‌] برای خداوند از جن شریکانی قائل شده‌اند، حال آنکه [خداوند] آنان را آفریده است، و برای او از روی نادانی [فرزندان‌] پسر و دختر بر می‌سازند، او بسی پاک و فراتر است از آنچه می‌گویند'),
(890, 6, 101, 'نوپدیدآورنده آسمانها و زمین است، چگونه فرزندی داشته باشد در حالی که همسری ندارد، و همه چیز را آفریده است، و به هر چیزی داناست‌'),
(891, 6, 102, 'چنین است خداوند که پروردگار شماست، خدایی جز او نیست، آفریدگار همه چیز است، پس او را بپرستید، و او بر همه چیز نگهبان است‌'),
(892, 6, 103, 'دیدگان او را درنیابد و او دیدگان را دریابد، و اوست باریک بین آگاه‌'),
(893, 6, 104, 'به راستی روشنگریهایی از سوی پروردگارتان برای شما آمده است، پس هرکس به دیده بصیرت بنگرد به سود خود اوست، و هرکس از سر بصیرت ننگرد به زیان خود اوست، و من نگهبان شما نیستم‌'),
(894, 6, 105, 'و بدین‌سان آیات [خود] را گونه‌گون بیان می‌داریم برای آنکه مبادا بگویند درس گرفته‌ای، و برای آنکه آن را به روشنی برای اهل معرفت باز می‌گوییم‌'),
(895, 6, 106, 'از آنچه از پروردگارت به تو وحی می‌شود پیروی کن، خدایی جز او نیست، و از مشرکان روی بگردان‌'),
(896, 6, 107, 'و اگر خداوند می‌خواست، شرک نمی‌ورزیدند، ولی تو را نگهبان آنان نگماشته‌ایم و تو کارساز آنان نیستی‌'),
(897, 6, 108, 'و کسانی را که به جای خداوند می‌پرستند، دشنام ندهید، چرا که از سر دشمنی و نادانی خداوند را دشنام می‌دهند، بدین‌سان عمل هر امتی را در چشمشان آراستیم، سپس بازگشتشان به سوی پروردگارشان است و او ایشان را از [حقیقت‌] کار و کردارشان آگاه می‌سازد'),
(898, 6, 109, 'و [اینان‌] سخت‌ترین سوگندهایشان را به نام خدا خوردند که اگر معجزه‌ای برایشان بیاید به آن ایمان می‌آورند، بگو معجزات در اختیار خداوند است، و چه دانید که چون [معجزه‌ای هم‌] بیاید به آن ایمان نمی‌آورند'),
(899, 6, 110, 'و دلها و دیدگانشان را [از قبول آن‌] بر می‌گردانیم همچنانکه نخستین بار هم به آن ایمان نیاوردند، و ایشان را در طغیانشان سرگشته رها می‌سازیم‌'),
(900, 6, 111, 'و اگر فرشتگان را بر آنان نازل می‌کردیم و اگر مردگان با آنان سخن می‌گفتند و همه چیز [از گذشته‌ها] را نزد آنان رویاروی باز می‌آوردیم، ایمان نمی‌آوردند مگر آنکه خدا بخواهد، ولی بیشترشان [حقیقت را] نمی‌دانند'),
(901, 6, 112, 'و بدین‌سان دشمنانی برای هر پیامبر از شیاطین انس و جن پدید آوردیم، و بعضی به بعضی دیگر سخنان آراسته ظاهر فریب، پیام و الهام می‌دهند، و اگر پروردگارت [به قضای حتمی‌] می‌خواست چنین نمی‌کردند، پس ایشان را با هر افترایی که می‌بندند واگذار'),
(902, 6, 113, 'چنین [مقرر شده‌] است تا دلهای بی‌ایمانان به آخرت به آن بگراید و آن را بپسندند و هرچه [ناشایسته که‌] کننده آنند، بکنند'),
(903, 6, 114, 'آیا کسی جز خداوند را داور گیرم و او کسی است که کتاب [قرآن‌] را شیوا و روشن بر شما نازل کرده است، و اهل کتاب می‌دانند که آن به راستی و درستی فرو فرستاده از سوی پروردگار توست، پس از دودلان مباش‌'),
(904, 6, 115, 'و سخن [وعده‌] پروردگارت به درستی و داد سرانجام پذیرفته است، کلمات او را دگرگون‌کننده‌ای نیست، و او شنوای داناست‌'),
(905, 6, 116, 'و اگر از بیشتر مردم [این سر] زمین پیروی کنی تو را از راه خدا گمراه می‌کنند، چرا که جز از وهم و گمان پیروی نمی‌کنند و جز این نیست که دروغ می‌بافند'),
(906, 6, 117, 'بی‌گمان پروردگارت داناتر است که چه کسانی از راه او به در افتاده‌اند و هم او به رهیافتگان داناتر است‌'),
(907, 6, 118, 'باری اگر به آیات خداوند ایمان دارید [فقط] از آنچه نام خدا را [به هنگام ذبحش‌] برده‌اند، بخورید'),
(908, 6, 119, 'و چرا از قربانی‌ای که نام خدا را [به هنگام ذبحش‌] بر او برده‌اند نخورید، و حال آنکه [خداوند] آنچه را بر شما حرام کرده است، برایتان روشن کرده است، مگر آنچه از [خوردن‌] آن ناچار شوید، و بسیاری به هوی و هوس خویش و از روی نادانی [مردم را] گمراه می‌کنند، بی‌گمان پروردگار تو به تجاوزکاران آگاه‌تر است‌'),
(909, 6, 120, 'و گناه را چه آشکار باشد چه پنهان رها کنید، بی‌شک کسانی که مرتکب گناه می‌شوند، جزای کاری را که کرده‌اند، خواهند دید'),
(910, 6, 121, 'و از قربانی‌ای که نام خدا را [به هنگام ذبحش‌] بر او نبرده‌اند، نخورید چرا که نافرمانی است و شیاطین به دوستداران خویش الهام و پیام می‌دهند تا با شما مجادله کنند، و اگر از آنان پیروی کنید، شما هم مشرکید'),
(911, 6, 122, 'آیا کسی که مرده‌دل بود و زنده‌اش کردیم و نوری به او بخشیدیم که در پرتو آن در میان مردم راه می‌برد، همانند کسی است که گویی گرفتار ظلمات است و از آن بیرون آمدنی نیست، بدین‌سان در چشم کافران کار و کردارشان آراسته شده است‌'),
(912, 6, 123, 'و بدین‌سان در هر شهری گناهکاران بزرگش را می‌گماریم، تا سرانجام در آن مکرورزند [و فسق و فساد کنند]، و جز در حق خویش مکرنمی‌ورزند و نمی‌دانند'),
(913, 6, 124, 'و چون نشانه‌ای [از جانب خداوند] بر ایشان بیاید گویند هرگز به آن ایمان نمی‌آوریم، مگر آنکه مانند آنچه به پیامبران الهی داده شده، به ما نیز داده شود، خداوند بهتر می‌داند که رسالت خویش را در کجا [و بر چه کسی‌] قرار دهد، به زودی گناهکاران را به سزای مکری که ورزیده بودند، از جانب خداوند خواری و عذابی شدید خواهد رسید'),
(914, 6, 125, 'بدین‌سان هرکس که خداوند هدایتش را بخواهد دلش را به پذیرش اسلام می‌گشاید، و هرکس را که بخواهد در گمراهی واگذارد، دلش را تنگ و تاریک می‌گرداند، چنانکه گویی می‌خواهد به آسمان بپرد [و نمی‌تواند]، خداوند این چنین عذاب را بر کسانی که ایمان نمی‌آورند، می‌گمارد'),
(915, 6, 126, 'و این راه پروردگار توست که مستقیم است، به راستی که آیات [خود] را برای پندگیران به روشنی بیان داشته‌ایم‌'),
(916, 6, 127, 'برای آنان نزد پروردگارشان دارالسلام [سرای سلامت، بهشت‌] محفوظ است و او به خاطر کار و کردارشان دوستدار آنان است‌'),
(917, 6, 128, 'و روزی که همه آنان را [در محشر] گرد آورد [و فرماید] ای گروه جن به راستی که بسیاری از انسانها را از راه به در بردید، و دوستان آنان از میان انسانها پاسخ دهند که پروردگارا ما از همدیگر بهره‌مند شدیم، و به اجلی که برای ما مقرر داشته بودی، رسیدیم، فرماید آتش دوزخ جایگاه شماست که جاودانه در آنید، مگر آنچه خدا بخواهد که پروردگارت فرزانه داناست‌'),
(918, 6, 129, 'و بدین‌سان بعضی از ستمکاران [مشرکان‌] را بر بعضی دیگر، به خاطر کار و کردارشان سلطه دهیم‌'),
(919, 6, 130, 'ای گروه جن و انس آیا پیامبرانی از خودتان به سوی شما نیامدند که آیات مرا بر شما فرو خوانند و شما را به دیدار چنین روزی هشدار دهند؟ گویند بر خود گواهی دهیم [که آری آمدند] و زندگانی دنیا ایشان را فریفته است و بر خود گواهی دهند که کافر بوده‌اند'),
(920, 6, 131, 'این از آن روی است که پروردگارت نابودکننده شهرها از روی ستم -در حالتی که مردمانش بی‌خبر باشندنیست‌'),
(921, 6, 132, 'و یکایک ایشان طبق کار و کردارشان درجاتی دارند، و پروردگارت از آنچه می‌کنند غافل نیست‌'),
(922, 6, 133, 'و پروردگارت بی‌نیاز و صاحب رحمت است، اگر بخواهد شما را از میان می‌برد و بعد از شما و به جای شما آنچه بخواهد جانشین می‌سازد، همچنانکه شما را از زاد و رود قومی دیگر پدید آورد'),
(923, 6, 134, 'آنچه به شما وعده داده شده است فرارسنده است و شما گزیر و گریزی نخواهید داشت‌'),
(924, 6, 135, 'بگو ای قوم من هرچه می‌توانید بکنید، من نیز می‌کنم، سپس به زودی خواهید دانست که نیک سرانجامی سرای آخرت از آن کیست و بی‌گمان ستمکاران رستگار نمی‌گردند'),
(925, 6, 136, 'و برای خداوند از زراعت و چارپایانی که خود او آفریده است، بهره‌ای قائل شدند و به پندار خویش چنین مدعی شدند که این برای خداست و این برای بتان ما، آنگاه آنچه خاص بتانشان بود، به خداوند نمی‌رسد، ولی آنچه خاص خداوند بود به بتانشان می‌رسد، چه بد حکم می‌کنند'),
(926, 6, 137, 'و بدین‌سان بتانشان در نظر بسیاری از آنان قتل فرزندانشان را آراسته جلوه دادند تا سرانجام آنان را نابود کنند و دینشان را بر آنان مشتبه سازند، و اگر خداوند [به قضای حتمی‌] می‌خواست، چنین نمی‌کردند، پس ایشان را با افترایی که می‌بندند، واگذار'),
(927, 6, 138, 'و به پندار خویش گفتند این چارپایان و زراعت حرام است، و جز کسی که ما بخواهیم هیچ کس نباید از آن بخورد، و چارپایانی که سواری آنها حرام شده است، و چارپایانی است که نام خدا را [هنگام ذبح‌] بر آنها نمی‌برند، [این احکام را] از روی افتراء به خداوند قائل شدند، [خداوند] به زودی به خاطر افترایی که بستند جزای‌شان را خواهد داد'),
(928, 6, 139, 'و گفتند آنچه در شکم این چارپایان است، خاص مردان ماست و بر زنان ما حرام است، و اگر آن مرده باشد، همه در آن شریکند [خداوند] به زودی جزای این [احکام ادعایی‌شان‌] را خواهد داد که او فرزانه داناست‌'),
(929, 6, 140, 'به راستی کسانی که فرزندانشان را از روی کم‌خردی و نادانی کشته‌اند، و آنچه خداوند به آنان روزی داده است، از روی افتراء به خداوند، حرام کرده‌اند، زیانکار شدند، اینان به راستی گمراه شدند، و رهیافته نبودند'),
(930, 6, 141, 'و او کسی است که باغهای [دارای درختان‌] داربست‌دار و بدون داربست پدید آورده است و نیز درخت خرما و زراعتی که میوه‌های آن گوناگون است، و درخت زیتون و انار که همانند و ناهمانندند، از میوه آن چون میوه برآورد بخورید و روز چیدنش حق [بینوایان از] آن را بپردازید، ولی اسراف نکنید که او اسرافکاران را دوست ندارد'),
(931, 6, 142, 'و از چارپایان بعضی را باربردار و بعضی را دست‌آموز آفریده است، از آنچه خداوند روزیتان داده است، بخورید و از گامهای شیطان پیروی نکنید که او دشمن آشکار شماست‌'),
(932, 6, 143, 'هشت فرد [بیافرید و بر شما حلال کرد] از میش دو تا و از بز دو تا، بگو آیا نرهای آنها را حرام کرده است یا ماده‌های آنها را، یا آنچه در رحمهای ماده‌های آنهاست؟ بگو اگر راست می‌گویید از روی علم مرا آگاه سازید'),
(933, 6, 144, 'و همچنین از شتر دو تا و از گاو دو تا، بگو آیا نرهای آنها را حرام کرده است یا ماده‌های آنها را، یا آنچه در رحمهای ماده‌های آنهاست؟ یا مگر به هنگامی که خداوند به شما چنین سفارش کرد شاهد بوده‌اید؟ [چنین نیست‌] آنگاه چه کسی ستمکارتر است از کسی که به خداوند دروغ بسته است تا مردم را بی‌هیچ علم و اطلاعی به گمراهی بکشاند، بی‌گمان خداوند ستم‌پیشگان را هدایت نمی‌کند'),
(934, 6, 145, 'بگو در آنچه به من وحی شده است چیزی را که خوردنش بر خورنده‌ای حرام باشد، نمی‌یابم، مگر آنکه مرداری باشد، یا خونی روان، یا گوشت خوک، که پلید است، یا قربانی‌ای که از روی نافرمانی هنگام ذبحش نامی غیر از نام خدا را برده باشند، اما اگر کسی درمانده شود، و تجاوزکار و زیاده خواه نباشد [و از آن بخورد، گناهی بر او نیست‌] و بی‌گمان پروردگارت آمرزگار مهربان است‌'),
(935, 6, 146, 'و بر یهودیان هر جانور ناخن‌دار [و چنگال‌داری‌] را حرام کردیم و از گاو و گوسفند پیهشان را بر آنان حرام کردیم، مگر آنچه بر پشت آنها باشد، یا بر روده‌ها، یا آنچه به استخوان چسبیده باشد این چنین به خاطر سرکشی‌شان به آنان کیفر دادیم، و ما راستگوییم‌'),
(936, 6, 147, 'و اگر تو را دروغگو انگاشتند، بگو پروردگارتان صاحب رحمتی فراگیر است، ولی عذاب او از گناهکاران بر نمی‌گردد'),
(937, 6, 148, 'به زودی مشرکان خواهند گفت اگر خدا می‌خواست ما شرک نمی‌آوردیم، همچنین پدران ما، و چیزی را حرام نمی‌کردیم، پیشینیان آنان هم به همین‌گونه تکذیب پیشه کردند، تا آنکه عذاب ما را چشیدند، بگو آیا علم و اطلاعی دارید که به ما نشان دهید؟ [ولی حق این است که‌] جز از پندار پیروی نمی‌کنید، و جز دروغ نمی‌بافید'),
(938, 6, 149, 'بگو حجت رسا و روشن از آن خداوند است، و اگر می‌خواست همگی شما را هدایت می‌کرد'),
(939, 6, 150, 'بگو گواهانتان را که گواهی دهند خداوند این چیزها را حرام کرده است، بیاورید، و اگر آنان هم گواهی دادند، تو همراه با آنان گواهی مده، و از هوی و هوس دروغ انگاران آیات ما، و بی‌ایمانان به آخرت که به پروردگارشان شرک می‌آورند، پیروی مکن‌'),
(940, 6, 151, 'بگو بیایید تا آنچه پروردگارتان بر شما حرام کرده است بر شما بخوانم [نخست این است که‌] چیزی را شریک خداوند نیاورید، و در حق پدر و مادر نیکی کنید و فرزندانتان را از [ترس‌] فقر نکشید، ما هم شما و هم ایشان را روزی می‌دهیم، و به کارهای ناشایسته، چه آشکار باشد، چه پنهان نزدیک نشوید، و هیچ انسانی را که خداوند خون او را حرام داشته است نکشید مگر به حق [به قصاص یا حد]، این چنین به شما سفارش کرده است، باشد که اندیشه کنید'),
(941, 6, 152, 'و به مال یتیم نزدیک نشوید، مگر به شیوه‌ای که بهتر باشد، تا زمانی که به کمال بلوغش برسد، و در کار پیمانه و ترازو به داد و درستی رفتار کنید، ما بر هیچ کس هیچ تکلیفی نمی‌کنیم مگر به اندازه توانش، و چون [به داوری یا شهادت‌] سخن گویید به عدالت عمل کنید ولو او [اصحاب دعوی‌] خویشاوند [شما] باشد، و به عهد الهی وفا کنید، این چنین به شما سفارش کرده است، باشد که پند گیرید'),
(942, 6, 153, 'و بدانید که این راه راست و درست من است، پس آن را در پیش گیرید، و به راههای دیگر نروید که شما را از راه او دور اندازد، این چنین به شما سفارش کرده است باشد که تقوا پیشه کنید [و در امان مانید]'),
(943, 6, 154, 'باری به موسی کتاب آسمانی دادیم، برای اینکه نعمت را بر کسی که نیکی پیش گرفته بود، تمام کنیم و برای آنکه همه چیز را به روشنی بیان کنیم و رهنمود و رحمتی باشد، تا به لقای پروردگارشان ایمان بیاورند'),
(944, 6, 155, 'و این کتابی مبارک است که آن را نازل کرده‌ایم، پس از آن پیروی کنید و تقوا بورزید باشد که مشمول رحمت او شوید'),
(945, 6, 156, 'تا نگویید که کتاب آسمانی فقط بر دو طایفه پیش از ما نازل شده است، و ما از قرائت و تلاوت آنان بی‌خبر بوده‌ایم‌'),
(946, 6, 157, 'یا نگویید که اگر کتاب آسمانی بر ما نازل می‌شد، از آنان راهیافته‌تر بودیم، اینک برهان و رهنمود و رحمتی از سوی پروردگارتان به سوی شما آمده است، کیست ستمکارتر از کسی که آیات الهی را دروغ انگارد و از آن روی بگرداند، به زودی به خاطر اینکه رویگردان شدند، سخت‌ترین عذاب را کیفر کسانی می‌گردانیم که از آیات ما روی گردانیدند'),
(947, 6, 158, '[اینان‌] انتظاری ندارند جز اینکه فرشتگان به سویشان بیایند یا پروردگارت بیاید، یا بعضی از آیات پروردگارت ظاهر گردد، حال آنکه روزی که بعضی از آیات پروردگارت ظاهر گردد، برای کسی که از پیش ایمان نیاورده باشد، یا با ایمانش خیری حاصل نکرده باشد، ایمان آوردنش سودی ندارد، بگو منتظر باشید، ما نیز منتظریم‌'),
(948, 6, 159, 'کسانی که دینشان را پاره پاره کردند و فرقه فرقه شدند، تو را کاری با آنان نیست، کارشان فقط با خداوند است، آنگاه ایشان را [از حقیقت‌] کار و کردارشان آگاه می‌سازد'),
(949, 6, 160, 'کسی که کار نیکی پیش آورد، ده چندان آن پاداش دارد، و هر که کار بدی پیش آورد، جز همانند آن کیفر نیابد، و بر ایشان ستم نرود'),
(950, 6, 161, 'بگو مرا پروردگارم به راهی راست که دین استوار و آیین پاک ابراهیم است -که هرگز از مشرکان نبودرهنمایی کرده است‌'),
(951, 6, 162, 'بگو نماز و نیایش من و زندگانی و مرگ من، در راه خدا، پروردگار جهانیان است‌'),
(952, 6, 163, 'که شریکی ندارد، و به همین [راه و روش‌] فرمان یافته‌ام و من پیشوای مسلمانانم‌'),
(953, 6, 164, 'بگو آیا جز خداوند پروردگاری جویم، و او پروردگار همه چیز است، و هیچ کس هیچ کاری نکند مگر آنکه سود و زیانش با خود اوست، و هیچ بردارنده‌ای بار گناه دیگری را بر ندارد، سپس بازگشتتان به سوی پروردگارتان است، آنگاه شما را [از حقیقت‌] آنچه در آن اختلاف داشتید آگاه می‌سازد'),
(954, 6, 165, 'و او کسی است که شما را جانشینان [الهی‌] بر روی زمین برگماشت، و بعضی را بر بعضی دیگر به درجاتی برتری داد تا شما را در آنچه به شما بخشیده است بیازماید، بی‌گمان پروردگارت زودکیفر و همو آمرزگار مهربان است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'الف لام میم صاد'),
(956, 7, 2, 'کتابی است که به سوی تو فروفرستاده شده است، پس نباید از آن تنگدلی یابی، تا بدان هشدار دهی، و پندآموزی برای مؤمنان باشد'),
(957, 7, 3, 'از آنچه از جانب پروردگارتان به سوی شما نازل شده است، پیروی کنید و جز او هیچ معبودی نگیرید، چه اندک پند می‌گیرید'),
(958, 7, 4, 'و چه بسیار شهرها که نابودشان کردیم و عذاب ما شبانه یا به هنگامی که در نیمروز خفته بودند بر ایشان فرود آمد'),
(959, 7, 5, 'و هنگامی که عذاب ما بر آنان فرود آمد، سخنشان جز این نبود که می‌گفتند ما خود ستمکار بودیم‌'),
(960, 7, 6, 'بی‌گمان از کسانی که پیامبران به سویشان رفته‌اند و همچنین از پیامبران پرس و جو خواهیم کرد'),
(961, 7, 7, 'و بر ایشان از روی علم [نامه اعمالشان را] فرو خوانیم و ما غائب [و غافل از احوال آنان‌] نبوده‌ایم‌'),
(962, 7, 8, 'و در آن روز وزن [سنجیدن اعمال‌] حقیقت دارد، آنگاه کسانی که کفه اعمالشان سنگین باشد، آنان رستگارند'),
(963, 7, 9, 'و کسانی که کفه اعمالشان سبک باشد، آنان کسانی هستند که بر خود زیان زده‌اند چرا که به آیات ما ستم کرده‌اند'),
(964, 7, 10, 'و به راستی که به شما در زمین تمکن داده بودیم و برایتان در آن انواع معاش پدید آورده بودیم، چه اندک سپاس می‌گزارید'),
(965, 7, 11, 'و به راستی که شما را آفریدیم و صورت و سامان بخشیدیم، آنگاه به فرشتگان گفتیم بر آدم سجده برید، همه سجده بردند مگر ابلیس که از سجده‌کنندگان نبود'),
(966, 7, 12, 'فرمود چون تو را امر به سجده کردم چه چیز تو را بازداشت که سجده نکردی؟ گفت برای اینکه من بهتر از او هستم، مرا از آتش آفریده‌ای و او را از گل‌'),
(967, 7, 13, 'فرمود پس از آنجا [بهشت یا آسمان‌] پایین رو که تو را نرسد که در آن بزرگی بفروشی، بیرون شو که تو خرد و خواری‌'),
(968, 7, 14, 'گفت [پس‌] تا روزی که [مردمان‌] برانگیخته شوند مرا مهلت ده‌'),
(969, 7, 15, 'فرمود تو از مهلت یافتگانی‌'),
(970, 7, 16, 'گفت پروردگارا از آنجا که مرا فریفتی، بر سر راه راست تو در کمین آنان [بندگان‌] می‌نشینم‌'),
(971, 7, 17, 'آنگاه از پیش و پس و راست و چپشان به سروقتشان می‌روم، و بیشترشان را سپاسگزار نخواهی یافت‌'),
(972, 7, 18, 'فرمود از آنجا نکوهیده و سرشکسته بیرون شو [و بدان که‌] جهنم را از پیروان تو و همگی شما آکنده خواهم ساخت‌'),
(973, 7, 19, 'و ای آدم تو و همسرت در بهشت بیارامید و از هر جا [هر چه‌] که خواستید بخورید، ولی به این درخت نزدیک نشوید وگرنه از ستمکاران خواهید بود'),
(974, 7, 20, 'آنگاه شیطان آنان را وسوسه کرد تا سرانجام عورتشان را که از دید آنان پوشیده بود، آشکار کند، و گفت پروردگارتان شما را از این درخت نهی نکرد مگر از آن روی که [با خوردن از میوه آن‌] دو فرشته شوید یا از جاودانگان گردید'),
(975, 7, 21, 'و برای آنان سوگند یاد کرد که من از خیرخواهان شما هستم‌'),
(976, 7, 22, 'آنگاه آنان را فریبکارانه به طمع انداخت و چون از [میوه‌] آن درخت خوردند، عورتهایشان بر آنان آشکار شد و بر آنها از برگ [درختان‌] بهشتی می‌چسباندند [تا پوشیده شود] و پروردگارشان به آنان ندا در داد که مگر شما را از این درخت نهی نکرده بودم و به شما نگفته بودم که شیطان دشمن آشکار شماست‌'),
(977, 7, 23, 'گفتند پروردگارا بر خود ستم کرده‌ایم و اگر بر ما نبخشایی و رحمت نیاوری بی‌شک از زیانکاران خواهیم بود'),
(978, 7, 24, 'فرمود پایین روید -برخی دشمن برخی دیگرو در روی زمین تا وقت معین آرامشگاه و بهره‌مندی دارید'),
(979, 7, 25, 'فرمود در همانجا زندگی می‌کنید و همانجا می‌میرید و از همانجا شما را برمی‌انگیزند'),
(980, 7, 26, 'ای فرزندان آدم، به راستی که برای شما لباسی پدید آوردیم تا هم عورت شما را بپوشاند و هم مایه تجمل باشد، ولی لباس تقوا بهتر است، این از آیات الهی است باشد که پند گیرند'),
(981, 7, 27, 'ای فرزندان آدم، شیطان شما را نفریبد، چنانکه پدر و مادر [نخستین‌] شما را از بهشت آواره کرد و لباس آنان را از آنان برکند تا عورتشان را بر ایشان آشکار کند، او و همانندانش شما را از جایی که شما آنان را نمی‌بینید، می‌بینند، ما شیاطین را سرور نامؤمنان گردانده‌ایم‌'),
(982, 7, 28, 'و [اینان‌] چون کار ناشایستی انجام دهند گویند پدران خود را بر همین شیوه یافته‌ایم، و خداوند ما را به آن فرمان داده است، بگو بی‌گمان خداوند به ناشایستی فرمان نمی‌دهد، آیا چیزی را که نمی‌دانید [به دروغ‌] به خداوند نسبت می‌دهید؟'),
(983, 7, 29, 'بگو پروردگارم به دادگری فرمان داده است، و در هر مسجدی روی دلتان را به سوی او بدارید، و او را -در حالی که دینتان را برای او پاک و پیراسته می‌داریدبخوانید، [و بدانید] همچنانکه شما را آفرید، [به سوی او] باز خواهید گشت‌'),
(984, 7, 30, 'گروهی را رهنمود و گروهی را گمراهی سزاوار است، چرا که اینان شیاطین را به جای خداوند سرور و معبود خود گرفتند و گمان می‌کنند که رهیافته‌اند'),
(985, 7, 31, 'ای فرزندان آدم زینت [ پوشاک‌] خود را در هر مسجد برگیرید و بخورید و بیاشامید ولی اسراف مکنید چرا که او اسرافکاران را دوست ندارد'),
(986, 7, 32, 'بگو چه کسی زینت الهی را که برای بندگانش پدید آورده، و رزق پاکیزه او را حرام کرده است؟ بگو اینها در زندگانی دنیا برای مؤمنان [و نامؤمنان‌] است و در روز قیامت نیز خاص مؤمنان است، بدین‌سان آیات خود را برای اهل معرفت به روشنی بیان می‌کنیم‌'),
(987, 7, 33, 'بگو همانا پروردگارم ناشایستیها را، چه آشکار باشد چه پنهان، حرام کرده است، و نیز گناه و سرکشی ناحق را و اینکه برای خداوند شریک بیاورید -که در این باره هیچ حجت و برهانی نفرستاده استو نیز اینکه آنچه نمی‌دانید به خداوند دروغ ببندید'),
(988, 7, 34, 'و هر امتی را اجلی است، چون اجلشان فرارسد نه ساعتی پس افتند و نه ساعتی پیش افتند'),
(989, 7, 35, 'ای فرزندان آدم، چون پیامبرانی از خودتان به سوی شما آمدند و آیات مرا بر شما خواندند [بدانید که‌] کسانی که تقوا و صلاح پیشه کنند، نه بیمی بر آنهاست و نه اندوهگین شوند'),
(990, 7, 36, 'و کسانی که آیات ما را دروغ انگارند و در برابر آنها تکبر ورزند، اینان دوزخی‌اند و جاودانه درآنند'),
(991, 7, 37, 'پس کیست ستمکارتر از کسی که بر خداوند دروغ بندد و آیات او را دروغ انگارد، اینان کسانی هستند که بهره‌ای که در کتاب [لوح محفوظ] برایشان مقرر است به ایشان می‌رسد، تا آنکه فرشتگان ما به سراغشان می‌آیند و جانشان را می‌گیرند و [به آنان‌] گویند کجاست آنچه به جای خداوند می‌پرستیدید؟ پاسخ دهند ایشان از نظر ما ناپدید شدند، و بر خود گواهی دهند که کافر بوده‌اند'),
(992, 7, 38, 'گوید به میان امتهایی از جن و انس که پیش از شما بوده‌اند، در آتش جهنم وارد شوید، هرگاه امتی وارد شود همانندش را لعنت کند، تا همه در آن به همدیگر رسند، [آنگاه‌] پیروان درباره پیشوایانشان گویند پروردگارا اینان ما را گمراه کردند، به ایشان دو چندان از عذاب جهنم بده، فرماید برای همه دو چندان هست ولی نمی‌دانید'),
(993, 7, 39, 'و پیشوایان آنان به پیروانشان گویند شما هیچ برتری بر ما ندارید، پس عذاب را به خاطر کار و کردارتان بچشید'),
(994, 7, 40, 'کسانی که آیات ما را دروغ انگاشتند و در برابر آن تکبر ورزیدند، درهای آسمان بر آنان گشوده نگردد و وارد بهشت نشوند مگر آنکه شتر وارد سوراخ سوزن شود، و بدین‌سان گناهکاران را کیفر می‌دهیم‌'),
(995, 7, 41, 'بسترها و بالاپوشهایشان از آتش جهنم است، و بدین‌سان ستمکاران [و مشرکان‌] را کیفر می‌دهیم‌'),
(996, 7, 42, 'و کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، اینان بهشتی‌اند و در آن جاودانه خواهند ماند، و ما بر هیچ کس جز به اندازه توانش تکلیف نمی‌کنیم‌'),
(997, 7, 43, 'و از سینه‌های آنان هر کینه‌ای که باشد می‌زداییم، جویباران از فرودست آنان جاری است و گویند سپاس خدای را که ما را به اینجا رهنمون شد و اگر خداوند راهبر نمی‌شد، هرگز راه نمی‌یافتیم، بی‌شک فرستادگان پروردگارمان به راستی و درستی آمدند، و ندا داده شود که اینک این بهشتی است که به خاطر کار و کردارتان به شما رسیده است‌'),
(998, 7, 44, 'و بهشتیان دوزخیان را ندا دهند که ما وعده پروردگارمان را راست و درست یافتیم، آیا شما هم آنچه پروردگارتان وعده داده بود راست و درست یافتید؟ گویند آری، آنگاه آوازدهنده‌ای در میان آنان آواز در دهد که لعنت الهی بر ستمگران باد'),
(999, 7, 45, 'کسانی که از راه خدا باز می‌دارند و آن را ناهموار و ناهنجار می‌شمارند و منکر آخرت هستند'),
(1000, 7, 46, 'و بین آنان حایلی است و بر روی اعراف [ بلندیها] مردانی هستند که همگان را به سیمایشان می‌شناسند، و بهشتیان را ندا دهند که سلام بر شما باد، اینان هنوز وارد آن [بهشت‌] نشده‌اند، اما امید می‌برند'),
(1001, 7, 47, 'و چون چشمان آنان به جانب دوزخیان افتد گویند پروردگارا ما را در زمره [این‌] قوم ستمکار میاور'),
(1002, 7, 48, 'و اهل اعراف مردانی را که به نشانه‌هایشان می‌شناسند ندا دهند و گویند مال‌اندوزی شما و تکبر ورزیدنتان به کارتان نیامد'),
(1003, 7, 49, 'آیا هم اینان بودند که شما سوگند یاد می‌کردید که خداوند مشمول رحمت خود قرارشان نمی‌دهد؟ [و خطاب به آنان گویند] وارد بهشت شوید، نه بیمی بر شماست و نه اندوهگین شوید'),
(1004, 7, 50, 'و دوزخیان بهشتیان را ندا دهند که از آب یا آنچه خداوند بهره‌مندتان ساخته است به ما ببخشید، گویند خداوند اینها را بر کافران حرام گردانده است‌'),
(1005, 7, 51, 'کسانی که دینشان را به بازیچه گرفتند و زندگانی دنیا فریبشان داد، امروز آنان را فراموش می‌کنیم چرا که دیدار چنین روزشان را فراموش کردند و آیات ما را انکار می‌کردند'),
(1006, 7, 52, 'و به راستی که برای آنان کتابی آورده‌ایم که از روی علم، روشن و شیوایش ساخته‌ایم، و رهنمود و رحمتی است برای اهل ایمان‌'),
(1007, 7, 53, 'فقط منتظر سرانجام آن هستند، روزی که سرانجام آن درآید، کسانی که پیشتر آن را فراموش کرده بودند، گویند بی‌شک فرستادگان پروردگارمان حق و حقیقت را آوردند آیا شفیعانی داریم که برای ما شفاعت کنند یا [به دنیا] بازگردانده شویم و [این بار] کاری جز آنچه می‌کردیم در پیش بگیریم؟ به راستی که به خود زیان زدند و بر ساخته‌هایشان نابود گردید'),
(1008, 7, 54, 'همانا پروردگار شما کسی است که آسمانها و زمین را در شش روز آفرید، سپس [ملکوتش کامل شد و] بر عرش استیلاء یافت، شب را به روز [و روز را به شب‌] -که آن را شتابان می‌جویدمی‌پوشاند، و خورشید و ماه و ستارگان را [آفرید] که رام شده فرمان اویند، بدانید که خلق و امر او راست، بزرگا خداوندا که پروردگار جهانیان است‌'),
(1009, 7, 55, 'پروردگارتان را به زاری و پنهانی بخوانید، که او از حد درگذرندگان را دوست ندارد'),
(1010, 7, 56, 'و در این سرزمین پس از آنکه به صلاح آمده است، فتنه و فساد مکنید، و او را با بیم و امید بخوانید که رحمت الهی به نیکوکاران نزدیک است'),
(1011, 7, 57, 'و او کسی است که بادها را پیشاپیش رحمتش [باران‌] مژده بخش می‌فرستد، تا آنکه ابرهای گرانبار را [همراه‌] بردارد و آن را به سوی زمین بایری برانیم و از آن آب [باران‌] فرو فرستیم و به آن هر میوه‌ای برآوریم، مردگان را نیز بدین‌سان برانگیزانیم، باشد که پند گیرید'),
(1012, 7, 58, 'و زمین پاک گیاهش به اذن پروردگارش می‌روید و آنچه پلید است جز [حاصلی‌] ناچیز برنیاورد، بدین‌سان آیات [خود] را برای سپاسگزاران گونه‌گون بیان می‌داریم‌'),
(1013, 7, 59, 'به راستی که نوح را به سوی قومش فرستادیم پس آنگاه گفت ای قوم من خداوند را که خدایی جز او ندارید بپرستید، که من بر شما از عذاب روزی سترگ می‌ترسم‌'),
(1014, 7, 60, 'بزرگان قومش گفتند ما تو را در گمراهی آشکار می‌بینیم‌'),
(1015, 7, 61, 'گفت ای قوم من، در من هیچ‌گونه گمراهی نیست، بلکه من پیامبری از سوی پروردگار جهانیان هستم‌'),
(1016, 7, 62, 'که پیامهای پروردگارم را به شما برسانم و خیر و صلاح شما را بجویم، و من از جانب خداوند چیزهایی می‌دانم که شما نمی‌دانید'),
(1017, 7, 63, 'آیا تعجب کرده‌اید که از سوی پروردگارتان پندی بر مردی از قوم خودتان برای شما نازل شده باشد تا شما را هشدار دهد و پروا و پرهیز پیشه کنید، و باشد که مشمول رحمت او شوید'),
(1018, 7, 64, 'آنگاه او را دروغگو انگاشتند و ما او و کسانی را که در کشتی همراهش بودند نجات دادیم و دروغ‌انگاران آیات خود را غرق کردیم که قومی کوردل بودند'),
(1019, 7, 65, 'و به سوی قوم عاد برادرشان هود را [فرستادیم‌]، گفت ای قوم من خداوند را که خدایی جز او ندارید بپرستید، آیا پروا نمی‌کنید؟'),
(1020, 7, 66, 'بزرگان قومش که کفر ورزیده بودند گفتند ما تو را دچار بی‌خردی می‌بینیم و ما تو را از دروغگویان می‌دانیم‌'),
(1021, 7, 67, 'گفت ای قوم من در من هیچ‌گونه بی‌خردی نیست بلکه من پیامبری از سوی پروردگار جهانیان هستم‌'),
(1022, 7, 68, 'که پیامهای پروردگارم را به شما می‌رسانم و من برای شما خیرخواهی امینم‌'),
(1023, 7, 69, 'آیا تعجب کرده‌اید که از سوی پروردگارتان پندی بر مردم از قوم خودتان برای شما نازل شده باشد تا شما را هشدار دهد، و یاد کنید که شما را بعد از قوم نوح جانشین کرد و به شما توانایی بسیار بخشید، پس نعمتهای الهی را یاد کنید، باشد که رستگار شوید'),
(1024, 7, 70, 'گفتند آیا به سراغ ما آمده‌ای تا فقط خدا را بپرستیم و آنچه پدرانمان می‌پرستیدند رها کنیم؟ اگر راست می‌گویی آنچه [از عذاب‌] به ما وعده می‌دهی هم‌اکنون [بر سر ما] بیاور'),
(1025, 7, 71, 'گفت به راستی که عذاب و غضبی از جانب پروردگارتان بر شما مقرر گردیده است، آیا با من درباره نامهایی که شما خودتان و پدرانتان نهاده‌اید، و خداوند هیچ حجت و برهانی بر آن نازل نکرده است، مجادله می‌کنید؟ منتظر باشید که من هم همراه شما از منتظران خواهم بود'),
(1026, 7, 72, 'آنگاه او و همراهان او را به رحمت خویش نجات دادیم و ریشه دروغ‌انگاران آیات خود را برکندیم، و آنان مؤمن نبودند'),
(1027, 7, 73, 'و به سوی قوم ثمود برادرشان صالح را [فرستادیم‌] [که به آنان‌] گفت ای قوم من، خداوند را که خدایی جز او ندارید بپرستید، به راستی که پدیده‌ای از سوی پروردگارتان برای شما آمده است، این شتر خداست که معجزه‌ای برای شماست پس رهایش کنید که در زمین خدا [بچرد و هر چه خواست‌] بخورد و به او آسیبی نرسانید که عذابی دردناک گریبانگیرتان می‌شود'),
(1028, 7, 74, 'و یاد کنید که شما را پس از قوم عاد جانشین قرار داد و در این سرزمین جای داد که در بخشهای هموار و هامونش قصرهایی می‌ساختید و از کوهها برای خود خانه‌هایی می‌تراشیدید، پس نعمتهای الهی را یاد کنید و در این سرزمین فتنه و فساد برپا مکنید'),
(1029, 7, 75, 'بزرگان قومش که استکبار می‌ورزیدند، به مؤمنان مستضعف می‌گفتند آیا مطمئنید که صالح از سوی پروردگارش به پیامبری فرستاده شده است؟ [ایشان در پاسخ‌] می‌گفتند [آری‌] ما به رسالت او مؤمن هستیم‌'),
(1030, 7, 76, 'استکبارپیشگان می‌گفتند ما به آنچه شما ایمان دارید، اعتقادی نداریم‌'),
(1031, 7, 77, 'آنگاه شتر را پی کردند و از فرمان پروردگارشان سر پیچیدند و گفتند ای صالح اگر از پیامبرانی آنچه از عذاب به ما وعده می‌دهی [بر سر ما] بیاور'),
(1032, 7, 78, 'آنگاه زلزله ایشان را فرو گرفت و در خانه‌شان از پا درآمدند'),
(1033, 7, 79, '[صالح‌] از آنان رویگردان شد و گفت ای قوم من، به راستی که پیام پروردگارم را به شما رساندم و خیر شما را خواستم، ولی شما خیرخواهان [و اندرزگویان‌] را دوست ندارید'),
(1034, 7, 80, 'و لوط را [به پیامبری فرستادیم‌] که به قومش می‌گفت آیا عمل ناشایستی را که پیش از شما هیچ‌کس از جهانیان مرتکب نشده است، مرتکب می‌شوید؟'),
(1035, 7, 81, 'شما از روی شهوت، با مردان، به جای زنان می‌آمیزید، آری شما قومی تجاوزکار هستید'),
(1036, 7, 82, 'و پاسخ قوم او جز این نبود که می‌گفتند آنان را از شهرتان برانید که مردمانی منزه‌طلب هستند'),
(1037, 7, 83, 'آنگاه او و خانواده‌اش را نجات دادیم، مگر زنش را که از واپس ماندگان بود'),
(1038, 7, 84, 'و بر آنان بارانی [از سنگ‌] باراندیم، بنگر که سرانجام گناهکاران چگونه بود'),
(1039, 7, 85, 'و به سوی [قوم‌] مدین برادرشان شعیب را [فرستادیم‌] که گفت ای قوم من خداوند را که خدایی جز او ندارید بپرستید، به راستی که حکم روشنی از سوی پروردگارتان برای شما آمده است، پس پیمانه و ترازو را درست بدارید [و تمام بدهید] و به مردم اجناسشان را کم ندهید، و در این سرزمین پس از آنکه به صلاح آمده است، فتنه و فساد نکنید که اگر مؤمن باشید این برایتان بهتر است‌'),
(1040, 7, 86, 'بر سر هر راهی [به کمین مردم‌] منشینید که [مردم را] بترسانید، و هرکسی را که ایمان دارد از راه خدا بازدارید و آن را ناهموار و ناهنجار مشمارید، و یاد کنید که زمانی اندکشمار بودید و او افزونتان کرد، و بنگرید که سرانجام اهل فساد چگونه بود'),
(1041, 7, 87, 'و اگر گروهی از شما به پیام من ایمان آوردند و گروهی ایمان نیاوردند صبر کنید که خداوند در میان ما داوری کند و او بهترین داوران است‌'),
(1042, 7, 88, 'بزرگان قوم او که استکبار ورزیده بودند گفتند ای شعیب تو و کسانی را که همراه تو ایمان آورده‌اند از شهرمان بیرون می‌کنیم مگر آنکه به آیین ما بازمی‌گردید، گفت حتی اگر دلمان نخواهد؟'),
(1043, 7, 89, 'اگر به آیین شما -پس از آنکه خداوند از آن نجاتمان داده استبازگردیم، به راستی که بر خداوند دروغ بسته‌ایم، و ما را نسزد که به آن بازگردیم، مگر آنکه خدا، پروردگارمان بخواهد، که علم او همه چیز را فراگیر است، بر خدا توکل کردیم، پروردگارا بین ما و قوممان به حق داوری کن که تو بهترین داورانی‌'),
(1044, 7, 90, 'و بزرگان قومش که کفرورزیده بودند، گفتند اگر از شعیب پیروی کنید، در آن صورت زیانکار خواهید بود'),
(1045, 7, 91, 'آنگاه زلزله آنان را فروگرفت و در خانه‌شان از پا درآمدند'),
(1046, 7, 92, 'گویی دروغگوانگاران شعیب، هرگز در آن [سرزمین‌] نبوده‌اند، دروغگوانگاران شعیب زیانکار بوده‌اند'),
(1047, 7, 93, 'آنگاه [شعیب‌] از آنان رویگردان شد و گفت ای قوم من، به راستی که پیامهای پروردگارم را به شما رساندم و خیر شما را خواستم، پس چگونه بر قوم کافر دریغ بخورم‌'),
(1048, 7, 94, 'و هیچ پیامبری به هیچ شهری نفرستادیم مگر آنکه اهل آن را به تنگدستی و ناخوشی دچار کردیم، تا زاری و خاکساری کنند'),
(1049, 7, 95, 'سپس به جای ناخوشی، خوشی آوردیم تا آنکه انبوه شدند و گفتند به راستی به پدران ما هم [به همین‌گونه، از گردش روزگار] رنج و راحت می‌رسید، آنگاه آنان را ناگهان -در حالی که بی‌خبر بودندفروگرفتیم‌'),
(1050, 7, 96, 'و اگر اهل آن شهرها ایمان آورده و تقوا پیشه کرده بودند، بر آنان [درهای‌] برکات آسمان و زمین می‌گشودیم، ولی [آیات و پیامهای ما را] دروغ انگاشتند، آنگاه به خاطر کار و کردارشان ایشان را فروگرفتیم‌'),
(1051, 7, 97, 'آیا اهل شهرها ایمنند از اینکه عذاب ما شبانه به سراغشان بیاید و ایشان خفته باشند؟'),
(1052, 7, 98, 'یا ایمنند اهل شهرها که عذاب ما در روز به هنگامی که سرگرم بازی‌اند به سراغشان بیاید؟'),
(1053, 7, 99, 'آیا از مکر الهی ایمنند؟ در صورتی که جز زیانکاران کسی از مکر الهی [به خیال خویش‌] ایمن نیست‌'),
(1054, 7, 100, 'آیا برای کسانی که این سرزمین را پس از اهل آن به ارث برده‌اند، روشن نشده است که اگر بخواهیم به کیفر گناهانشان گرفتارشان سازیم؟ و بر دلهایشان مهر می‌نهیم، آنگاه [پندی‌] نمی‌شنوند'),
(1055, 7, 101, 'این شهرهایی است که از اخبارش بر تو می‌خوانیم، و به راستی که پیامبرانشان برای آنان معجزات آوردند و آنان بر آن نبودند که به چیزی که از پیش انکار کرده بودند، ایمان بیاورند، بدین‌سان خداوند بر دلهای کافران مهر می‌نهد'),
(1056, 7, 102, 'و در اکثرشان وفای به عهد نیافتیم، و اکثرشان را نافرمان یافتیم‌'),
(1057, 7, 103, 'سپس بعد از آنان موسی را همراه با معجزات خویش به سوی فرعون و بزرگان قومش فرستادیم و در حق آیات ما ستم کردند، بنگر که سرانجام اهل فساد چگونه بوده است‌'),
(1058, 7, 104, 'و موسی گفت ای فرعون من پیامبری از سوی پروردگار جهانیانم‌'),
(1059, 7, 105, 'سزاوار است که به خداوند جز سخن حق نسبت ندهم، به راستی که معجزه‌ای از سوی پروردگارتان برایتان آورده‌ام، پس بنی‌اسرائیل را همراه من بفرست‌'),
(1060, 7, 106, 'گفت اگر راست می‌گویی که معجزه‌ای آورده‌ای آن را [به میان‌] بیاور'),
(1061, 7, 107, 'آنگاه عصایش را در انداخت و ناگهان به صورت اژدهایی آشکار درآمد'),
(1062, 7, 108, 'و دستش را [از گریبانش‌] بیرون آورد، و ناگهان سپید و درخشان در دید تماشاگران پدیدار شد'),
(1063, 7, 109, 'بزرگان قوم فرعون گفتند این [مرد] جادوگری داناست‌'),
(1064, 7, 110, 'می‌خواهد شما را از سرزمینتان آواره کند، تا رأی شما چه باشد'),
(1065, 7, 111, 'گفتند او و برادرش را [به نحوی‌] بازدار و گردآورندگان [جادوگران‌] را به شهرها بفرست‌'),
(1066, 7, 112, 'تا هر جادوگر دانایی را به حضورت بیاورند'),
(1067, 7, 113, 'و [سرانجام‌] جادوگران به نزد فرعون آمدند [و] گفتند آیا اگر ما پیروز شویم، پاداشی داریم؟'),
(1068, 7, 114, '[فرعون‌] گفت آری و از نزدیکان [من‌] خواهید بود'),
(1069, 7, 115, 'گفتند ای موسی اول تو می‌اندازی یا ما باید اندازنده باشیم؟'),
(1070, 7, 116, 'گفت شما بیندازید، و چون درانداختند، مردمان را چشم‌بندی کردند و به هول و هراس انداختند و جادویی سترگ به میان آوردند'),
(1071, 7, 117, 'و [ما] به موسی وحی کردیم که عصایت را بینداز، [انداخت و اژدها شد] و ناگهان برساخته‌هایشان را فرو بلعید'),
(1072, 7, 118, 'آنگاه حق برقرار شد و کار و کوشش آنان بر باد رفت‌'),
(1073, 7, 119, 'و آنجا بود که شکست خوردند و خرد و خوار شدند'),
(1074, 7, 120, 'و جادوگران به سجده درافتادند'),
(1075, 7, 121, 'گفتند به پروردگار جهانیان ایمان آوردیم‌'),
(1076, 7, 122, 'پروردگار موسی و هارون‌'),
(1077, 7, 123, 'فرعون گفت آیا پیش از آنکه من به شما اجازه دهم به او ایمان آوردید؟ این مکری است که در شهر با همدیگر سگالیده‌اید تا اهل شهر را از آن آواره کنید، به زودی خواهید دانست‌'),
(1078, 7, 124, 'که دستان و پاهایتان را در خلاف جهت همدیگر خواهم برید، سپس همگی‌تان را به دار خواهم زد'),
(1079, 7, 125, 'گفتند [هر چه می‌خواهی بکن‌] ما به پروردگارمان روی آورده‌ایم‌'),
(1080, 7, 126, 'و فقط از آن روی با ما کینه می‌ورزی که به آیات پروردگارمان، به هنگامی که بر ما ظاهر شد، ایمان آورده‌ایم، پروردگارا بر ما [باران‌] صبر فروریز و ما را مسلمان بمیران‌'),
(1081, 7, 127, 'بزرگان قوم فرعون گفتند آیا موسی و قومش را رها می‌کنی که در این سرزمین فساد برپا کنند، و تو و خدایانت را رها کنند؟ گفت به زودی پسرانشان را خواهیم کشت و زنان [و دختران‌]شان را [برای کنیزی‌] زنده خواهیم گذاشت و ما بر آنان چیره‌ایم‌'),
(1082, 7, 128, 'موسی به قومش گفت از خداوند مدد بجویید و شکیبایی کنید، چرا که زمین از آن خداوند است، به هر کس از بندگانش که بخواهد به میراث می‌دهد و نیک سرانجامی از آن پرهیزگاران است‌'),
(1083, 7, 129, 'گفتند پیش از آنکه نزد ما بیایی و پس از آنکه نزد ما آمدی همچنان آزار و اذیت دیده‌ایم [پس چه باید کرد؟] [موسی‌] گفت چه بسا پروردگارتان دشمنتان را نابود کند و شما را در این سرزمین جانشین گرداند، و بنگرد تا چه می‌کنید'),
(1084, 7, 130, 'و به راستی که فرعونیان را گرفتار قحطسال و کاهش محصولات کردیم، تا پند پذیرند'),
(1085, 7, 131, 'آنگاه چون به آنان خوشی می‌رسید می‌گفتند سزاوار این هستیم، و چون ناخوشی فرا می‌رسید، به موسی و همراهان او فال بد می‌زدند، بدانید که فال [نیک و بد]شان با خداست ولی بیشترشان نمی‌دانند'),
(1086, 7, 132, 'و گفتند هر پدیده شگرفی که برای ما بیاوری که ما را با آن جادو کنی بدان که ما به تو ایمان نمی‌آوریم‌'),
(1087, 7, 133, 'آنگاه بر سر آنان [بلای‌] طوفان و ملخ و شپش و وزغ و [باران‌] خون فرستادیم که پدیده‌های روشن و گوناگونی بود، آنگاه [باز هم‌] سرکشی کردند و قومی گناهکار بودند'),
(1088, 7, 134, 'و چون بلا بر سرشان آمد گفتند ای موسی برای ما از پروردگارت بخواه که با پیمانی که با تو دارد [بر ما رحمت آورد] که اگر بلا را از ما بگردانی، به تو ایمان می‌آوریم و بنی‌اسرائیل را همراهت می‌فرستیم‌'),
(1089, 7, 135, 'آنگاه چون بلا را -تا زمانی که به سر رساندنداز آنان گرداندیم، باز پیمان‌شکنی کردند'),
(1090, 7, 136, 'سپس از آنان انتقام گرفتیم و آنان را در دریا غرقه ساختیم چرا که آیات ما را دروغ انگاشته و از آنها غافل بودند'),
(1091, 7, 137, 'و آن قوم را که مستضعف بودند، وارث مشرق‌ها و مغرب‌های سرزمینی ساختیم که به آن برکت بخشیده بودیم، و [بدین گونه‌] به خاطر صبری که کرده بودند وعده نیکوی پروردگارت در حق بنی‌اسرائیل تحقق یافت و آنچه فرعون و قومش ساخته و برآورده بودند، نابود کردیم‌'),
(1092, 7, 138, 'و بنی‌اسرائیل را از دریا گذراندیم، آنگاه به سروقت قومی رسیدند که به پرستش بتانشان می‌پرداختند، [بنی‌اسرائیل‌] گفتند ای موسی برای ما خدایی قرار بده چنانکه آنان خدایانی دارند گفت به راستی قومی نادان هستید'),
(1093, 7, 139, 'اینان بنیاد کارشان بر باد است و آنچه می‌کنند باطل است‌'),
(1094, 7, 140, 'گفت آیا جز خداوند را خدای شما بپسندم، و حال آنکه او شما را بر جهانیان [هم زمانتان‌] برتری بخشیده است‌'),
(1095, 7, 141, 'و چنین بود که شما را [از دست‌] فرعونیان رهانیدیم که عذابی سخت به شما می‌چشانیدند، پسرانتان را می‌کشتند و زنان [و دختران‌]تان را [برای کنیزی‌] زنده نگاه می‌داشتند و در آن آزمونی بزرگ از سوی پروردگارتان بود'),
(1096, 7, 142, 'و با موسی سی شب وعده گذاشتیم، و آن را به ده شب دیگر پایان بخشیدیم، چندانکه میقات پروردگارش چهل شب کامل شد، و موسی به برادرش هارون گفت در میان قوم من جانشین من باش و در اصلاح بکوش و از راه و روش اهل فساد پیروی مکن‌'),
(1097, 7, 143, 'و چون موسی به میعاد ما آمد و پروردگارش با او سخن گفت، گفت پروردگارا خود را به من بنمایان تا بر تو بنگرم، فرمود هرگز مرا نخواهی دید، ولی [اگر اصرار می‌ورزی‌] به آن کوه بنگر، اگر در جایش استوار ماند، مرا خواهی دید، و چون پروردگارش بر کوه تجلی کرد، آن را پخش و پریشان کرد و موسی بیهوش در افتاد، سپس چون به خود آمد گفت پاکا که تویی، به درگاهت توبه کردم و من نخستین مؤمن [به این حقیقت‌] هستم‌'),
(1098, 7, 144, 'فرمود ای موسی [بدان که‌] من تو را به پیامها و کلام خویش بر مردمان برگزیدم، آنچه به تو می‌بخشم بگیر و از سپاسگزاران باش‌'),
(1099, 7, 145, 'و در الواح [تورات‌] هر گونه پند و روشنگری در هر باب برای او نوشته‌ایم، پس آن را به جد و جهد بگیر و به قومت دستور بده که احکام نیکوی آن را دریافت [و به آن عمل‌] کنند، زودا که سرای نافرمانان را به شما نشان دهم‌'),
(1100, 7, 146, 'کسانی را که به ناحق در این سرزمین تکبر می‌ورزند از آیات خود باز می‌دارم و هر پدیده‌ای و نشانه‌ای که ببینند به آن ایمان نمی‌آورند و اگر راه رستگاری را ببینند آن را در پیش نمی‌گیرند و اگر گمراهه را ببینند آن را در پیش می‌گیرند، این از آن است که آیات ما را دروغ انگاشتند و از آن غافل بودند'),
(1101, 7, 147, 'و دروغ دارندگان آیات ما و لقای آن جهانی، اعمالشان باطل شده است، آیا جز در برابر کار و کردارشان جزا می‌یابند؟'),
(1102, 7, 148, 'و قوم موسی در غیاب او از آرایه‌هایشان پیکر گوساله‌ای ساختند و پرداختند که بانگی داشت، آیا نمی‌دیدند که با آنان سخن نمی‌گوید و راهی به ایشان نمی‌نمایاند؟ آن را به پرستش گرفتند و مشرک شدند'),
(1103, 7, 149, 'و چون پشیمان شدند و دیدند که گمراه شده‌اند گفتند اگر پروردگارمان بر ما رحمت نیاورد و ما را نبخشاید، بی‌شک از زیانکاران خواهیم بود'),
(1104, 7, 150, 'و چون موسی [آگاه شد و] خشمناک و اندوهگین به سوی قومش بازگشت گفت در پشت‌سر من چه بد رفتار کردید، چرا مهلت ندادید فرمان پروردگارتان برسد، و الواح [تورات‌] را بینداخت و سر برادرش را [در دست‌] گرفت و به سوی خود کشید [هارون‌] گفت ای پسر مادرم، این قوم مرا زار و زبون کردند و نزدیک بود که مرا بکشند، مرا دشمن‌شاد مکن، و مرا در زمره ستمکاران قرار مده‌'),
(1105, 7, 151, 'موسی [آرام گرفت و] گفت پروردگارا مرا و برادرم را بیامرز و ما را در [پناه‌] رحمت خود درآور، و تو مهربان‌ترین مهربانانی‌'),
(1106, 7, 152, 'کسانی که گوساله را به پرستش گرفتند زودا که گرفتار خشم پروردگارشان و در زندگانی دنیا دچار خواری شوند، و بدین‌سان افترازنندگان را جزا می‌دهیم‌'),
(1107, 7, 153, 'و کسانی که دست به ناشایستیها زده‌اند، سپس توبه کرده‌اند و ایمان آورده‌اند، بی‌گمان پروردگارت پس از آن آمرزگار مهربان است‌'),
(1108, 7, 154, 'و چون خشم موسی خاموش شد، الواح را برگرفت، و در نسخه آن برای خداترسان رهنمود و رحمت بود'),
(1109, 7, 155, 'و موسی از میان قومش هفتاد مرد را برای میعاد ما برگزید [که درخواست رؤیت الهی را داشتند] آنگاه چون به زلزله گرفتار شدند، موسی گفت پروردگارا اگر می‌خواستی ایشان و مرا پیشاپیش هلاک می‌کردی آیا ما را به خاطر آنچه کم‌خردان ما کردند نابود می‌کنی؟ این جز آزمون تو نیست که هر کس را که بخواهی به آن گمراه و هر کس را که بخواهی به آن هدایت می‌کنی، تو سرور مایی، پس ما را بیامرز و بر ما رحمت آور و تو بهترین آمرزشگرانی‌'),
(1110, 7, 156, 'و در این جهان و در آخرت برای ما نیکی بنویس که ما به سوی تو بازگشته‌ایم، فرمود عذاب خود را به هر آن کس که بخواهم می‌رسانم و رحمت من همه‌چیز را فراگیر است، پس برای کسانی که تقوا می‌ورزند و زکات می‌پردازند، و کسانی که به آیات ما ایمان دارند، آن [رحمت‌] را مقرر می‌دارم‌'),
(1111, 7, 157, 'کسانی که از فرستاده و پیامبر امی پیروی می‌کنند، که نام [و نشان‌] او را در تورات و انجیل که در نزدشان است، نوشته می‌یابند، [همو] که آنان را به نیکی فرمان می‌دهد و از ناشایستی باز می‌دارد، و پاکیزه‌ها را بر آنان حلال و پلیدها را حرام می‌دارد، و از آنان قید و بندهای [پیمانهای‌] سنگین را که بر آنان مقرر شده بود بر می‌دارد، و کسانی که به او ایمان آورده و او را گرامی داشته و یاری کرده‌اند و از نوری که همراه او نازل شده پیروی می‌کنند، اینان رستگارند'),
(1112, 7, 158, 'بگو ای مردم من پیامبر الهی به سوی همه شما هستم، همان [خدایی‌] که فرمانروایی آسمانها و زمین از آن اوست، خدایی جز او نیست، که زنده می‌دارد و می‌میراند، پس به خداوند و فرستاده‌اش، پیامبر امی، که به خدا و کلمات او ایمان دارد، ایمان بیاورید، و از او پیروی کنید باشد که هدایت یابید'),
(1113, 7, 159, 'و از قوم موسی گروهی هستند که به حق راه می‌نمایند و به حق داد می‌دهند'),
(1114, 7, 160, 'و ایشان را به دوازده سبط و امت تقسیم کردیم، و چون قوم موسی از او آب خواستند به او وحی کردیم که با عصایت به سنگ بزن، آنگاه از آن دوازده چشمه شکافت، و هر گروهی [از اسباط] آبشخور خود را شناختند و ابر را سایبانشان گرداندیم و برایشان من و سلوی فرو فرستادیم [و گفتیم‌] از روزی پاکیزه‌ای که به شما بخشیده‌ایم بخورید و بر ما ستم نکردند، بلکه بر خویشتن ستم کردند'),
(1115, 7, 161, 'چنین بود که به ایشان گفته شد در این شهر سکنا کنید و هرگونه که خواستید [از نعمتهای آن‌] بخورید و[برای عذرخواهی‌] حطه بگویید و از آن دروازه فروتنانه وارد شوید تا گناهان شما را ببخشیم و پاداش نیکوکاران را خواهیم افزود'),
(1116, 7, 162, 'آنگاه ستمکارانشان سخن را به چیزی جز آنچه به آنان گفته شده بود، تبدیل کردند، سپس بر سر آنان به کیفر ستمکاریشان عذابی از آسمان فرو فرستادیم‌'),
(1117, 7, 163, 'و از ایشان درباره [اهالی‌] شهری که در کنار دریا بود بپرس که در روز شنبه که باید حرمت [سبت‌] را نگه می‌داشتند، بر ایشان ماهیهایشان [بر روی آب‌] پدیدار می‌آمد، تجاوز می‌کردند [و حرمت سبت را می‌شکستند] و [بر عکس‌] روزی که سبت نمی‌داشتند برایشان [ماهی بر روی آب‌] نمی‌آمد بدین‌سان ایشان را به نافرمانیشان می‌آزماییم‌'),
(1118, 7, 164, 'و چون گروهی از ایشان گفتند چرا قومی را پند می‌دهید که خداوند هلاک‌کننده یا عذاب‌کننده آنان به عذابی شدید است؟ گفتند این [در حکم‌] معذرتی به درگاه پروردگار شماست و باشد که پروا پیشه کنند'),
(1119, 7, 165, 'و چون پندی را که به ایشان داده بودند، از یاد بردند، کسانی را که ناهیان از منکر بودند نجات دادیم و ستمکاران را به عذابی شدید گرفتار کردیم چرا که نافرمانی کرده بودند'),
(1120, 7, 166, 'و چون در برابر آنچه از آن نهی شده بودند، گردنکشی کردند، فرمودیمشان که بوزینگان مطرود باشید'),
(1121, 7, 167, 'و یاد کن که پروردگارت فرمود که بر آنان کسی را تا روز قیامت خواهد گماشت که به آنان سخت‌ترین عذاب را بچشاند، که پروردگارت زودکیفر است و همو آمرزگار مهربان است‌'),
(1122, 7, 168, 'و ایشان را به صورت امتهایی در آن سرزمین تقسیم کردیم، بعضی از ایشان صالح‌اند و بعضی جز آن، و آنان را به خوشیها و ناخوشیها آزمودیم باشد که [به راه حق‌] بازگردند'),
(1123, 7, 169, 'و پس از آنان جانشینان [ناصالحی‌] آمدند که کتاب آسمانی را از پیشینیان فراستاندند [ولی‌] متاع زندگی دنیوی را طلب کردند و ادعا کردند که به زودی آمرزیده خواهیم شد، و حال آنکه اگر متاعی همانند آن به دستشان می‌آمد، باز همچنان آن را می‌ربودند، آیا از آنان در کتاب آسمانی پیمان گرفته نشده است که بر خداوند جز راستی و درستی نسبت ندهند و در آن آموزش هم یافته‌اند و سرای آخرت برای پرهیزگاران بهتر است، آیا نمی‌اندیشید؟'),
(1124, 7, 170, 'و کسانی که به کتاب آسمانی تمسک می‌جویند و نماز برپا می‌دارند [بدانند که‌] ما پاداش نیکوکاران را ضایع نمی‌گذاریم‌'),
(1125, 7, 171, 'و چنین بود که کوه [طور] را بر سر آنان برافراشتیم، گویی سایبانی بود و گمان بردند بر آنان فروخواهد افتاد [و گفتیم‌] آنچه به شما داده‌ایم به جد و جهد بگیرید و آنچه در آن هست به خاطر بسپارید باشد که پروا پیشه کنید'),
(1126, 7, 172, 'و چون پروردگارت زاد و رود بنی‌آدم را از پشتهای ایشان برگرفت، و آنان را بر خودشان گواه گرفت [و پرسید] آیا پروردگار شما نیستم؟ گفتند چرا، شهادت می‌دهیم تا مبادا روز قیامت بگویید ما از این [حقیقت‌] بی‌خبر بودیم‌'),
(1127, 7, 173, 'یا مبادا بگویید همانا پدران ما در گذشته شرک آورده بودند و ما زاد و رودی از پی ایشان بودیم، آیا ما را به خاطر کاری که باطل‌اندیشان کرده‌اند هلاک می‌کنی؟'),
(1128, 7, 174, 'و بدین‌سان آیات [خود] را به روشنی بیان می‌داریم، باشد که [به راه حق‌] بازگردند'),
(1129, 7, 175, 'و بر آنان خبر کسی را بخوان که به او [علم‌] آیات خود را بخشیده بودیم و از آن عاری شد و شیطان در پی او افتاد و آنگاه از گمراهان شد'),
(1130, 7, 176, 'و اگر می‌خواستیم قدر او را به خاطر آن [علمش به آیات‌] بلند می‌داشتیم، ولی او به دنیا [و پستی‌] گرایید و از هوای نفس خویش پیروی کرد، آری داستان او همچون داستان سگ است که اگر بر او حمله آوری، زبان از دهان بیرون می‌آورد و اگر هم او را [به حال خود] واگذاری باز زبان از دهان بیرون می‌آورد، این داستان دروغ‌انگاران آیات ماست، پس برایشان این پند و داستان را بخوان باشد که اندیشه کنند'),
(1131, 7, 177, 'دروغ‌انگاران آیات ما بد سرانجامی داشتند و بر خود ستم می‌کردند'),
(1132, 7, 178, 'کسی که خداوند هدایتش کند رهیافته است، و کسانی که او بیراه گذاردشان، آنانند که زیانکارند'),
(1133, 7, 179, 'و به راستی بسیاری از جن و انس را برای دوزخ آفریده‌ایم [چرا که‌] دلهایی دارند که با آن در نمی‌یابند، و دیدگانی دارند که با آن نمی‌بینند، و گوشهایی دارند که با آن نمی‌شنوند، اینان همچون چارپایانند، بلکه گمراه‌تر، اینان غافلانند'),
(1134, 7, 180, 'و خدای را نامهای نیکوست، پس او را با آنها بخوانید، و کسانی را که در نامهای او کجروی می‌کنند، واگذارید، به زودی به جزای کار و کردار خویش می‌رسند'),
(1135, 7, 181, 'و از آفریدگان ما گروهی هستند که به حق راه می‌نمایند و به حق داد می‌دهند'),
(1136, 7, 182, 'و دروغ انگاران آیات خود را آهسته آهسته، به نحوی که درنیابند، فرو می‌گیریم‌'),
(1137, 7, 183, 'و آنان را مهلت می‌دهم چرا که مکر من متین است‌'),
(1138, 7, 184, 'آیا نیندیشیده‌اند که همسخن آنان جنونی ندارد، و جز هشداردهنده‌ای آشکار نیست‌'),
(1139, 7, 185, 'آیا در ملکوت آسمانها و زمین و آنچه خداوند آفریده است، ننگریسته‌اند، و اینکه چه بسا اجلشان نزدیک شده باشد، آنگاه بعد از آن [قرآن‌] به چه سخنی ایمان می‌آورند'),
(1140, 7, 186, 'کسی که خداوند بیراه گذاردش، رهنمایی ندارد و آنان را در طغیانشان سرگشته وامی‌گذارد'),
(1141, 7, 187, 'از تو درباره قیامت می‌پرسند که استقرار آن چه وقت است؟ بگو علم آن نزد پروردگار من است، هیچ‌کس جز او هنگام آن را آشکار نمی‌سازد، این امر بر اهل آسمانها و زمین دشوار است، [قیامت‌] جز ناگهان به سراغ شما نمی‌آید، از تو می‌پرسند چنانکه گویا تو از چون و چند آن آگاهی، بگو همانا علم آن با خداوند است ولی بیشتر مردم نمی‌دانند'),
(1142, 7, 188, 'بگو که برای خود اختیار سود و زیانی ندارم مگر آنچه خدا بخواهد، و اگر غیب می‌دانستم خیر فراوان برای خود کسب می‌کردم و هیچ ناگواری به من نمی‌رسید، من کسی جز هشداردهنده و مژده‌آور اهل ایمان نیستم‌'),
(1143, 7, 189, 'او کسی است که شما را از تن یگانه‌ای آفرید و همسرش را از او پدید آورد تا در کنار او آرام گیرد، و چون [آدم‌] با او [حوا] آمیزش کرد، باری سبک گرفت و با آن آسان به سر می‌برد و چون گرانبار شد، هردو خداوند، پروردگارشان را به دعا خواندند که اگر به ما فرزند سالم و شایسته‌ای عطا کنی از سپاسگزاران خواهیم بود'),
(1144, 7, 190, 'و چون به آن دو، فرزندی سالم و شایسته عطا کرد، آن دو در عطای او، برای او شریکانی قائل شدند، ولی خداوند از آنچه شرک می‌آورند منزه است‌'),
(1145, 7, 191, 'آیا چیزهایی را شریک او می‌آورند که چیزی نمی‌آفرینند و خود آفریده شده‌اند'),
(1146, 7, 192, 'و نمی‌توانند به ایشان و نه به خودشان یاری‌ای برسانند'),
(1147, 7, 193, 'و اگر ایشان را به [راه‌] هدایت بخوانید از شما پیروی نمی‌کنند، برای شما یکسان است، چه ایشان را بخوانید چه آنکه خاموش باشید'),
(1148, 7, 194, 'کسانی را که در برابر و به جای خداوند می‌خوانید بندگانی مانند شما هستند، بخوانیدشان و اگر راست می‌گویید باید که پاسخ شما را بدهند'),
(1149, 7, 195, 'آیا پاهایی دارند که با آن راه روند؟ یا دستانی دارند که با آن کاری انجام دهند، یا چشمانی دارند که با آن ببینند؟ یا گوشهایی دارند که با آن بشنوند؟ بگو شریکانی را که قائلید بخوانید، سپس در حق من هرچه خواهید بسگالید و مرا مهلت ندهید'),
(1150, 7, 196, 'سرور من خداوند است که [این‌] کتاب آسمانی را فرو فرستاده است و او دوستدار شایستگان است‌'),
(1151, 7, 197, 'و کسانی که در برابر و به جای او [خداوند] می‌خوانید، نمی‌توانند به شما و نه به خودشان یاری‌ای برسانند'),
(1152, 7, 198, 'و اگر ایشان را به [راه‌] هدایت بخوانید نمی‌شنوند و می‌نگریشان که به سوی تو می‌نگرند ولی نمی‌بینند'),
(1153, 7, 199, 'عفو پیشه کن و به نیکی فرمان ده و از نادانان روی بگردان‌'),
(1154, 7, 200, 'و اگر وسوسه‌ای از سوی شیطان تو را به وسواس انداخت، به خداوند پناه ببر که او شنوای داناست‌'),
(1155, 7, 201, 'پرهیزگاران هرگاه خیالی شیطانی عارضشان شود، یاد [خدا] کنند، و آنگاه دیده‌ور شوند'),
(1156, 7, 202, 'و یاران [شیطان‌صفت آن دیگران‌] آنان را در گمراهی بدارند و هیچ فرونگذارند'),
(1157, 7, 203, 'و چون برای آنان آیه‌ای نیاوری گویند چرا از خود برنمی‌سازی؟ بگو فقط از آنچه از سوی پروردگارم به من وحی می‌شود پیروی می‌کنم، این روشنگریهایی از سوی پروردگارتان و رهنمود و رحمتی برای اهل ایمان است‌'),
(1158, 7, 204, 'و چون قرآن خوانند به آن گوش بسپارید و [در برابر آن‌] خاموش باشید، باشد که مشمول رحمت شوید'),
(1159, 7, 205, 'و پروردگارت را در بامدادان و شامگاهان در دلت به زاری و ترس و بدون بانگ برداشتن، یاد کن و از غافلان مباش‌'),
(1160, 7, 206, 'کسانی که نزد پروردگارت هستند [تقرب دارند] از عبادت او گردنکشی نمی‌کنند و او را تسبیح می‌گویند و بر او سجده می‌برند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'از تو درباره [تقسیم‌] انفال می‌پرسند، بگو [حکم تقسیم‌] انفال مختص به خدا و رسول [او] است، پس از خداوند پروا کنید و بین خودتان آشتی کنید، و اگر به راستی مؤمن هستید از خداوند و پیامبر او اطاعت کنید'),
(1162, 8, 2, 'مؤمنان همان کسانی هستند که چون یاد خدا به میان آید دلهایشان ترسان شود و چون آیات او را برایشان بخوانند، بر ایمانشان بیفزاید و بر پروردگارشان توکل کنند'),
(1163, 8, 3, '[همان‌] کسانی که نماز را برپا می‌دارند و از آنچه به آنان روزی داده‌ایم [به دیگران‌] می‌بخشند'),
(1164, 8, 4, 'اینان به راستی مؤمن‌اند، [و] ایشان را نزد پروردگارشان درجات [عالی‌] است و آمرزش و روزی شایسته است‌'),
(1165, 8, 5, 'همچنانکه پروردگارت تو را از خانه‌ات [مدینه‌] به حق بیرون آورد، و گروهی از مؤمنان آن را ناخوش می‌داشتند'),
(1166, 8, 6, '[و] درباره حق پس از آشکار شدنش با تو مجادله می‌کردند، گویی که ایشان را به سوی مرگ می‌رانند و ایشان همچنان نظاره می‌کنند'),
(1167, 8, 7, 'و یاد آورید که خداوند به شما وعده داده بود که یکی از دو طایفه نصیب شما [و مغلوب شما]ست، و دوست می‌داشتید آنکه بدون سلاح است [کاروان تجاری‌] نصیب شما باشد، و حال آنکه خداوند می‌خواست که به کلمات خویش حق را احقاق کند و ریشه کافران را برکند'),
(1168, 8, 8, 'تا حق را -ولو گناهکاران ناخوش داشته باشنداحقاق و باطل را ابطال کند'),
(1169, 8, 9, 'یاد آورید هنگامی که به پروردگارتان استغاثه کردید، و او دعای شما را اجابت کرد [و فرمود] که من یاری‌دهنده شما با [فرو فرستادن‌] هزار فرشته پیاپی هستم‌'),
(1170, 8, 10, 'و خداوند آن را جز بشارتی برای شما نکرد و تا دلهایتان با آن آرام بگیرد، و پیروزی جز از سوی خداوند نیست، که خداوند پیروزمند فرزانه است‌'),
(1171, 8, 11, 'یاد آورید که شما را در پرده خوابی سبک که [در حکم‌] آرامش‌بخشی از سوی او بود، پوشاند، و از آسمان آبی بر شما بارید، تا شما را با آن پاکیزه بدارد و از شما پلیدی شیطان را بزداید، و دلهایتان را گرم و گامهایتان را بدان استوار کند'),
(1172, 8, 12, 'چنین بود که پروردگارت به فرشتگان وحی فرستاد که من با شما هستم، مؤمنان را ثابت قدم بدارید، که به زودی در دل کافران هراس می‌افکنم، گردنها و سرانگشتانشان را قطع کنید'),
(1173, 8, 13, 'این از آن است که آنان با خداوند و پیامبر او مخالفت ورزیدند، و هرکس با خداوند و پیامبر او مخالفت ورزد، خداوند سخت کیفر است‌'),
(1174, 8, 14, 'اینها را بچشید و بدانید که برای کافران عذاب آتش جهنم مهیاست‌'),
(1175, 8, 15, 'ای مؤمنان چون با انبوه کافران رو در رو شدید هرگز به آنان پشت مکنید'),
(1176, 8, 16, 'و هرکس در چنین روزی به آنان پشت کند -مگر آنکه کناره‌جو برای [ادامه‌] کارزار، یا پناه‌جو به گروهی [خودی‌] باشدسزاوار خشم الهی شده است، و سرا و سرانجام او دوزخ است، و بد سرانجامی است‌'),
(1177, 8, 17, 'پس شما آنان را نکشته‌اید بلکه خداوند کشته است، و چون تیر انداختی، به حقیقت تو نبودی که تیر می‌انداختی بلکه خداوند بود که می‌انداخت، تا مؤمنان را بدین وسیله به آزمونی نیک بیازماید، که خداوند شنوای داناست‌'),
(1178, 8, 18, 'این چنین است که خداوند نقش بر آب کننده نقشه کافران است‌'),
(1179, 8, 19, 'اگر از خداوند داوری می‌خواستید، [نتیجه‌] داوری او برایتان پیش آمد، و اگر [از کفر] باز ایستید برایتان بهتر است، و اگر باز گردید ما نیز باز می‌گردیم و گروه شما، ولو انبوه باشد، به کارتان نیاید، و بدانید که خداوند با مؤمنان است‌'),
(1180, 8, 20, 'ای مؤمنان از خداوند و پیامبر او اطاعت کنید و از او روی نگردانید، در حالی که [پند و پیام او را] می‌شنوید'),
(1181, 8, 21, 'و مانند کسانی نباشید که گفتند [پند و پیام حق را] شنیدیم ولی نمی‌شنیدند'),
(1182, 8, 22, 'بدترین جنبندگان از نظر خداوند، ناشنوایان گنگ [از شنیدن حق‌] هستند که در نمی‌یابند'),
(1183, 8, 23, 'و اگر خداوند خیری در آنان سراغ داشت به آنان گوش شنوا می‌داد، و اگر به آنان گوش شنوا هم داده بود، باز پشت می‌کردند و رویگردان می‌شدند'),
(1184, 8, 24, 'ای مؤمنان به ندای خداوند و پیامبر که شما را به پیامی حیات‌بخش می‌خوانند، لبیک اجابت بگویید و بدانید که خداوند بین انسان و دل او حایل می‌گردد، و بدانید که در نزد او محشور می‌گردید'),
(1185, 8, 25, 'و از فتنه‌ای که [چون درگیرد] فقط به ستمکارانتان نمی‌رسد و [دامن‌گیر همگان می‌شود] پروا کنید و بدانید که خداوند سخت کیفر است‌'),
(1186, 8, 26, 'و یاد کنید آنگاه که در این سرزمین اندکشمار و مستضعف بودید و می‌ترسیدید که مردمان [کافر] شما را از خانه و کاشانه‌تان آواره کنند، ولی او شما را [نزد یارانتان‌] جای داد و به نصرت خویش یاری کرد و از پاکیزه‌ها روزی داد، باشد که سپاس بگزارید'),
(1187, 8, 27, 'ای مؤمنان آگاهانه به خداوند و پیامبر و در امانات خود خیانت مکنید'),
(1188, 8, 28, 'و بدانید که اموال و اولادتان مایه آزمون شما هستند و پاداش سترگ نزد خداوند است‌'),
(1189, 8, 29, 'ای مؤمنان اگر از خداوند پروا کنید برای شما [پدیده‌ای‌] جداکننده حق از باطل پدید آورد و سیئات شما را بزداید و شما را بیامرزد، و خداوند بخشنده و بخشاینده بزرگ است‌'),
(1190, 8, 30, 'و یاد کن که کافران در حق تو بدسگالی می‌کردند تا تو را در بند کشند یا بکشند یا آواره کنند، و آنان مکر می‌ورزیدند و خداوند هم مکر می‌ورزید و خداوند بهترین مکرانگیزان است‌'),
(1191, 8, 31, 'و چون آیات ما بر آنان خوانده می‌شد می‌گفتند شنیدیم و اگر بخواهیم مانند این خواهیم گفت، این جز افسانه‌های پیشینیان نیست‌'),
(1192, 8, 32, 'و چنین بود که می‌گفتند بار خدایا اگر این حق است و از سوی توست، بر ما از آسمان سنگ ببار، یا عذاب دردناکی بر سر ما بیاور'),
(1193, 8, 33, 'و خداوند مادام که تو در میان ایشان هستی، آنان را عذاب نمی‌کند، و نیز مادام که استغفار می‌کنند، عذاب‌کننده آنان نیست‌'),
(1194, 8, 34, 'و سزاوار نیستند که خداوند [در آخرت‌] عذابشان نکند، و حال آنکه مردمان را از [زیارت‌] مسجدالحرام باز می‌دارند و ایشان متولیان آن نباشند، چرا که جز پارسایان کسی متولی آن [مسجدالحرام‌] نمی‌تواند باشد، ولی بیشترشان [مردم‌] نمی‌دانند'),
(1195, 8, 35, 'و [اینان کسانی هستند که‌] نمازشان در پیرامون خانه کعبه جز سوت زدن و دست‌افشانی نیست، پس عذاب [الهی‌] را به خاطر کفرورزیدنتان بچشید'),
(1196, 8, 36, '[آن‌] کافران اموالشان را برای بازداشتن از راه خدا خرج می‌کنند، آری خرجش خواهند کرد آنگاه مایه حسرت آنان می‌گردد و سرانجام مغلوب می‌گردند، و کافران را در جهنم گرد می‌آورند'),
(1197, 8, 37, '[چنین است‌] تا خداوند پاک را از پلید جدا کند و پلید را برهم نهد و همه را برهم بیفشرد و سرانجام در جهنم اندازد، اینان همان زیانکارانند'),
(1198, 8, 38, 'به کافران بگو اگر دست [از کفر خود] بردارند، گذشته‌هایشان آمرزیده خواهد شد و اگر به آن بازگردند، [بدانند که بر آنان نیز] سنت پیشینیان خواهد گذشت‌'),
(1199, 8, 39, 'و با آنان کارزار کنید تا آنکه فتنه شرک باقی نماند و دین، سراسر دین خدایی باشد، و اگر دست بردارند خداوند به کار و کردارشان بیناست‌'),
(1200, 8, 40, 'و اگر روی گرداندند [شما مسلمانان‌] بدانید که خداوند سرور شماست، چه نیکو سروری و چه نیکو یاوری‌'),
(1201, 8, 41, 'اگر به خداوند و آنچه بر بنده خود در یوم‌الفرقان [روز جنگ بدر، روز جدایی حق از باطل‌]، روز برخورد دو گروه [حق و باطل‌] نازل کرده‌ایم، ایمان دارید، بدانید که از هر غنیمتی که به دست آورید یک پنجم آن خاص خداوند و پیامبر و خویشاوندان [او] و یتیمان و بینوایان و در راه ماندگان است، و خداوند بر هر کاری تواناست‌'),
(1202, 8, 42, 'یاد کنید زمانی که شما در کناره نزدیک [به مدینه‌] و آنان در کناره دورتر بودند، و سواران [کاروان تجاری قریش‌] در فرودست شما [در کناره دریا] بودند، و اگر شما با همدیگر وعده [جنگ‌] گذارده بودید [چه‌بسا] در آن وعده خلاف می‌کردید، ولی خداوند [چنین مقرر داشت‌] تا کاری انجام یافتنی را به سرانجام برساند، تا سرانجام هرکس که نابود [و گمراه‌] می‌گردد، دیده و دانسته باشد و هرکس زنده [و راهیاب‌] می‌گردد دیده و دانسته باشد، و [بدانید که‌] خداوند شنوای داناست‌'),
(1203, 8, 43, 'یاد کنید که خداوند آنان را در رؤیای تو اندکشمار نمایاند، و اگر بسیار نمایانده بود، هراس به دل راه می‌دادید و در کارتان اختلاف کلمه می‌یافتید، ولی خداوند درامان داشت، که او از راز دلها آگاه است‌'),
(1204, 8, 44, 'و یاد کنید که چون رو در رو شدید آنان را در دیدگان شما اندکشمار نمایاند و شما را نیز در دیدگان ایشان اندک نمایاند، تا خداوند کاری انجام یافتنی را به سرانجام برساند، و همه کارها به او باز می‌گردد'),
(1205, 8, 45, 'ای مؤمنان چون با گروهی [از دشمن‌] رو در رو شدید، ثابت‌قدم باشید و خداوند را بسیار یاد کنید، باشد که رستگار شوید'),
(1206, 8, 46, 'و از خداوند و پیامبر او اطاعت کنید و اختلاف کلمه نداشته باشید که بددل خواهید شد و شان و شوکتتان بر باد می‌رود، و شکیبایی کنید که خداوند با شکیبایان است‌'),
(1207, 8, 47, 'و همانند کسانی مباشید که از سرزمینشان با سرمستی و نمایشگری در چشم مردم، بیرون شدند، و [مردمان را] از راه خدا باز می‌داشتند و خداوند به کار و کردار ایشان چیره است‌'),
(1208, 8, 48, 'چنین بود که شیطان کارهای ایشان را در نظرشان آراسته جلوه داد، و گفت امروز کسی از مردم بر شما پیروز نخواهد شد و من امان‌دهنده شما هستم، و چون دو گروه رو در رو شدند، به عقب برگشت و گفت من از شما بری و برکنار هستم، چرا که من چیزی را می‌بینم که شما نمی‌بینید، من از خداوند می‌ترسم و خداوند سخت کیفر است‌'),
(1209, 8, 49, 'باری منافقان و بیماردلان گفتند اینان را دینشان فریفته است، و حال آنکه هرکس بر خدا توکل کند [بداند که‌] خداوند پیروزمند فرزانه است‌'),
(1210, 8, 50, 'اگر بدانی آنگاه که فرشتگان جان کافران را بگیرند و بر چهره‌ها و پشتهایشان بکوبند و [گویند] عذاب آتش سوزان را بچشید'),
(1211, 8, 51, 'این به خاطر کار و کردار پیشین شماست [وگرنه‌] خداوند هرگز در حق بندگان ستمگر نیست‌'),
(1212, 8, 52, 'همانند شیوه فرعونیان و پیشینیان ایشان که به آیات الهی کفر ورزیدند، آنگاه خداوند ایشان را به کیفر گناهانشان فرو گرفت، که خداوند نیرومند سخت کیفر است‌'),
(1213, 8, 53, 'این از آن است که خداوند دگرگون‌کننده نعمتی نیست که بر قومی ارزانی داشته باشد، مگر آنکه آنچه در دلهایشان دارند بگردانند، و خداوند شنوای داناست‌'),
(1214, 8, 54, 'همانند شیوه فرعونیان و پیشینیان ایشان که آیات پروردگارشان را دروغ انگاشتند، آنگاه ایشان را به خاطر گناهانشان نابود کردیم و فرعونیان را غرقه ساختیم و همگی ستمکار بودند'),
(1215, 8, 55, 'بدترین جنبندگان از نظر الهی کسانی هستند که کفرورزیده‌اند و ایمان نمی‌آورند'),
(1216, 8, 56, 'همانان که با ایشان پیمان بسته‌ای، سپس هربار پیمانشان را می‌شکنند و پروایی ندارند'),
(1217, 8, 57, 'و اگر در جنگ بر ایشان دست یافتی چنان تارومارشان کن که عبرت آیندگانشان شود باشد که پند گیرند'),
(1218, 8, 58, 'و اگر از خیانت قومی اندیشناک شدی، تو نیز همسان عهدشان را به سویشان بینداز، که خداوند خائنان را دوست ندارد'),
(1219, 8, 59, 'و کافران هرگز مپندارند که جان به در برده‌اند، آنان گزیر و گریزی ندارند'),
(1220, 8, 60, 'و در برابر آنان هر نیرویی که می‌توانید، از جمله نگاهداری اسبان، فراهم آورید، تا به آن وسیله دشمن خدا و دشمن خود را بترسانید، همچنین دیگرانی را هم غیر از آنان، که شما ایشان را نمی‌شناسید، و خداوند می‌شناسدشان، و آنچه در راه خدا انفاق کنید، پاداشش به شما بر می‌گردد، و بر شما ستم نمی‌رود'),
(1221, 8, 61, 'و اگر جانب آشتی را گرفتند تو نیز جانب آن را بگیر و بر خداوند توکل کن، که او شنوای داناست‌'),
(1222, 8, 62, 'و اگر خواستند به تو نیرنگ بزنند، بدان که خداوند تو را کافی است، و او کسی است که تو را به نصرت خویش و به مؤمنان یاری کرد'),
(1223, 8, 63, 'و بین دلهای ایشان الفت داد که اگر هر آنچه در روی زمین است خرج می‌کردی، بین دلهای ایشان الفت نمی‌افکندی، ولی خداوند میان آنان الفت برقرار کرد، که او پیروزمند فرزانه است‌'),
(1224, 8, 64, 'ای پیامبر، خدا و مؤمنانی که پیرو تو هستند تو را کافی است‌'),
(1225, 8, 65, 'ای پیامبر مؤمنان را به جهاد برانگیز، [و بدان که‌] اگر از شما بیست تن شکیبا باشند، بر دویست تن غلبه خواهند کرد و اگر از شما صدتن باشند بر هزار تن از کافران غلبه می‌کنند، چرا که اینان قومی هستند که چیزی در نمی‌یابند'),
(1226, 8, 66, 'اینک خداوند بر شما آسان گرفت و معلوم داشت که در شما ضعفی هست، در این صورت اگر از شما صدتن شکیبا باشند بر دویست تن غلبه می‌کنند و اگر از شما هزار تن باشند، به توفیق الهی بر دو هزار تن غلبه می‌کنند، و خداوند با شکیبایان است‌'),
(1227, 8, 67, 'هیچ پیامبری را نسزد که اسیران [دشمن‌] را نگاه دارد، مگر زمانی که در این سرزمین استیلاء و استقرار یابد، شما متاع دنیوی می‌خواهید و خداوند آخرت را [برای شما] می‌خواهد، و خداوند پیروزمند فرزانه است‌'),
(1228, 8, 68, 'اگر حکم پیشین الهی نبود، به خاطر آنچه گرفته بودید، عذابی سهمگین به شما می‌رسید'),
(1229, 8, 69, 'پس از آنچه به دست آورده‌اید، پاک و پاکیزه بخورید، و از خداوند پروا کنید، بی‌گمان خداوند آمرزگار مهربان است‌'),
(1230, 8, 70, 'ای پیامبر به اسیرانی که در دست شما هستند بگو اگر خداوند خیری در دلهای شما سراغ کند، به شما چیزی می‌بخشد که از آنچه از شما گرفته‌اند، بهتر است، و شما را می‌آمرزد، و خداوند آمرزگار مهربان است‌'),
(1231, 8, 71, 'و اگر آهنگ خیانت به تو کردند، بدان که پیشترها هم نسبت به خداوند خیانت روا داشته‌اند، آنگاه شما را بر آنان پیروز کرد، و خداوند دانای فرزانه است‌'),
(1232, 8, 72, 'کسانی که ایمان آورده‌اند و هجرت کرده‌اند، و در راه خدا به مال و جان جهاد کرده‌اند، همچنین کسانی که ایشان را جا و پناه و یاری داده‌اند، اینان دوستان همدیگرند، و کسانی که ایمان آورده‌اند، ولی هجرت نکرده‌اند، شما از دوستی [و توارث‌] آنان برخوردار نمی‌شوید، مگر آنکه هجرت کنند، و اگر در کار دین از شما یاری خواستند بر شماست که یاری کنید مگر در برابر قومی که بین شما و ایشان پیمانی باشد، و خداوند به کار و کردار شما بیناست‌'),
(1233, 8, 73, 'و کافران دوستان همدیگرند، اگر [به این سفارش‌] عمل نکنید، در این سرزمین فتنه و فساد بزرگی برپا می‌گردد'),
(1234, 8, 74, 'و کسانی که ایمان آورده و هجرت کرده و در راه خداوند جهاد کرده‌اند و کسانی که ایشان را جا و پناه و یاری داده‌اند، اینانند که به راستی مؤمن‌اند، برای ایشان آمرزش [الهی‌] و روزی پاک مقرر است‌'),
(1235, 8, 75, 'و کسانی که پس از آن ایمان آورده‌اند و همراه شما هجرت و جهاد کرده‌اند، اینان از شما هستند، و در حکم الهی خویشاوندان به یکدیگر [برای ارث بردن‌] سزاوارترند، که خداوند بر هر چیزی داناست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'این برائت خداوند و پیامبر اوست از مشرکانی که با آنان پیمان بسته‌اید'),
(1237, 9, 2, '[ای مشرکان تا] چهارماه [آزادانه‌] در این سرزمین بگردید و بدانید که از خداوند گزیر و گریزی ندارید و خداوند رسواگر کافران است‌'),
(1238, 9, 3, 'و این اعلامی از سوی خداوند و پیامبر اوست به مردم در روز حج‌اکبر که [بدانند] خداوند و پیامبر او از مشرکان بری و بیزار است، پس اگر [از کفر] توبه کنید، برایتان بهتر است و اگر رویگردان شوید بدانید که از خداوند گزیر و گریزی ندارید، و کافران را از عذابی دردناک خبر ده‌'),
(1239, 9, 4, 'مگر کسانی از مشرکان که با آنان پیمان بسته‌اید و با شما هیچ‌گونه نابکاری نکرده‌اند و هیچ کس را در برابر شما یاری نداده‌اند، پس پیمان ایشان را تا پایان مدتش به سر برید، که خداوند پرهیزگاران را دوست دارد'),
(1240, 9, 5, 'و چون ماههای حرام به سرآمد، آنگاه مشرکان را هرجا که یافتید بکشید و به اسارت بگیریدشان و محاصره‌شان کنید و همه جا در کمینشان بنشینید، آنگاه اگر [از کفر] توبه کردند و نماز برپا داشتند و زکات پرداختند، آزادشان بگذارید که خداوند آمرزگار مهربان است‌'),
(1241, 9, 6, 'و اگر کسی از مشرکان از تو پناه خواست به او پناه بده، تا آنکه کلام الهی را بشنود، سپس او را به جای امنش برسان، این از آن است که ایشان قومی ناآگاه‌اند'),
(1242, 9, 7, 'چگونه پیمان با مشرکان نزد خداوند و نزد پیامبرش محترم داشته شود؟ مگر [در مورد] کسانی که با آنان نزدیک مسجدالحرام [در حدیبیه‌] پیمان بسته‌اید، پس مادام که بر عهد خود با شما استوار باشند، شما نیز با آنان استوار باشید، که خداوند پرهیزگاران را دوست دارد'),
(1243, 9, 8, 'چگونه چنین نباشد، و حال آنکه اگر اینان بر شما دست یابند نه پیوند و نه پیمانی را در حق شما رعایت می‌کنند، اینان شما را با زبان خود خشنود می‌کنند، حال آنکه دلهایشان از آن ابا دارد و بیشترشان نافرمانند'),
(1244, 9, 9, 'اینان آیات الهی را به بهای ناچیز فروخته‌اند و [مردم را] از راه او بازداشته‌اند، و کار و کردارشان چه بد است‌'),
(1245, 9, 10, 'اینان در حق هیچ مؤمنی هیچ پیوند و پیمانی را رعایت نمی‌کنند، و اینان تجاوزکار هستند'),
(1246, 9, 11, 'اما اگر [از کفر] توبه کردند و نماز بر پا داشتند و زکات پرداختند، در آن صورت برادران دینی شما هستند، و ما آیات [خود] را برای اهل معرفت به روشنی بیان می‌داریم‌'),
(1247, 9, 12, 'ولی اگر سوگندهایشان را پس از پیمان بستنشان شکستند و در دین شما طعنه زدند، آنگاه با پیشوایان کفر کارزار کنید، چرا که حرمت سوگند [و پیمان‌] را رعایت نمی‌کنند، باشد که دست بردارند'),
(1248, 9, 13, 'آیا نباید با قومی که سوگندها [و پیمانها]یشان را شکسته‌اند و در آواره ساختن پیامبر کوشیده‌اند و نخست‌بار بنای ستیزه با شما را گذارده‌اند، کارزار کنید، آیا از آنها می‌ترسید؟ حال آنکه اگر مؤمنید، سزاوارتر است که از خداوند بترسید'),
(1249, 9, 14, 'با آنان کارزار کنید تا خداوند آنان را به دست شما عذاب کند و خوار و رسوا سازد و شما را بر ایشان پیروز گرداند و درد دلهای قومی از مؤمنان را تشفی دهد'),
(1250, 9, 15, 'و خشم و خروش دلهایشان را برطرف کند، و خداوند از هرکس که بخواهد درمی‌گذرد، و خداوند دانای فرزانه است‌'),
(1251, 9, 16, 'آیا گمان برده‌اید که به امان خود رها می‌شوید و خداوند کسانی را از شما که جهاد می‌کنند و به جای خداوند و پیامبر او و مؤمنان، دوست همرازی نگرفته‌اند، معلوم نمی‌دارد؟ و خداوند به کار و کردار شما آگاه است‌'),
(1252, 9, 17, 'مشرکان را نرسد که مساجد الهی را آبادان کنند حال آنکه [عملا] بر کفر خود گواه هستند، اعمال اینان تباه شده و جاودانه در آتش دوزخ خواهند بود'),
(1253, 9, 18, 'مساجد الهی را فقط کسانی آباد می‌کنند که به خداوند و روز بازپسین ایمان آورده، و نماز برپا داشته و زکات می‌پردازند، و از هیچ کس جز خداوند نترسیده‌اند، و چه بسا اینان رهیافته باشند'),
(1254, 9, 19, 'آیا آب دادن به حاجیان و آباد سازی مسجدالحرام را همانند [عمل‌] کسی می‌شمارید که به خداوند و روز بازپسین ایمان آورده و در راه خدا جهاد کرده است؟ اینان نزد خداوند برابر نیستند، و خداوند ستمکاران [مشرکان‌] را هدایت نمی‌کند'),
(1255, 9, 20, 'کسانی که ایمان آورده و هجرت کرده‌اند و در راه خداوند به مال و جان جهاد کرده‌اند، نزد خداوند بلندمرتبه‌ترند و اینانند که رستگارند'),
(1256, 9, 21, 'و پروردگارشان ایشان را به رحمت و رضوان خویش و بوستانهایی که در آنها ناز و نعمتی پاینده دارند، بشارت می‌دهد'),
(1257, 9, 22, 'جاودانه در آنند، چرا که نزد خداوند پاداشی بزرگ است‌'),
(1258, 9, 23, 'ای مؤمنان پدران و برادرانتان را اگر کفر را از ایمان خوشتر دارند، دوست مگیرید، و هرکس از شما ایشان را دوست گیرد، بدانید که ایشان ستمکارند'),
(1259, 9, 24, 'بگو اگر پدرانتان و پسرانتان و برادرانتان و همسرانتان و خاندانتان و اموالی که به دست آورده‌اید و تجارتی که از کسادش می‌ترسید، و خانه‌هایی که خوش دارید، در نزد شما از خداوند و پیامبرش و جهاد در راهش عزیزتر است، منتظر باشید تا خداوند فرمانش را به میان آورد، و خداوند نافرمانان را هدایت نمی‌کند'),
(1260, 9, 25, 'به راستی خداوند شما را در موارد بسیار و نیز در جنگ حنین یاری کرده است، آنگاه که کثرتتان شما را شاد و شگفت‌زده کرد، ولی هیچ سودی به حال شما نداشت، و زمین با همه گستردگی‌اش، بر شما تنگ آمد، آنگاه [به دشمن‌] پشت کردید [و گریختید]'),
(1261, 9, 26, 'سپس خداوند آرامش خویش را بر پیامبرش و بر مؤمنان نازل کرد و سپاهیانی که شما نمی‌دیدید [به کمک شما] فرو فرستاد و کافران را به رنج و عذاب انداخت، و کیفر کافران این چنین است‌'),
(1262, 9, 27, 'سپس خداوند از هرکس که بخواهد درمی‌گذرد و خداوند آمرزگار مهربان است‌'),
(1263, 9, 28, 'ای مؤمنان همانا مشرکان پلیدند، لذا نباید پس از امسالشان به مسجدالحرام نزدیک شوند، و اگر از [رکود تجارت و] تنگدستی می‌ترسید، زودا که خداوند، اگر بخواهد شما را از فضل خویش توانگر گرداند، که خداوند دانای فرزانه است‌'),
(1264, 9, 29, 'با اهل کتابی که به خداوند و روز بازپسین ایمان ندارند و حرام داشته خدا و پیامبرش را حرام نمی‌گیرند و دین حق نمی‌ورزند، کارزار کنید تا به دست خویش و خاکسارانه جزیه بپردازند'),
(1265, 9, 30, 'و یهودیان می‌گویند عزیر پسر خداست و مسیحیان می‌گویند مسیح پسر خداست، این سخنی است [باطل‌] که به زبان می‌آورند و به سخن کافران پیشین تشبه می‌جویند، خداوند بکشدشان چگونه بیراهه می‌روند'),
(1266, 9, 31, 'اینان احبار و راهبانشان و مسیح بن مریم را به جای خداوند به خدایی گرفته‌اند، و حال آنکه فرمانی جز این به آنان داده نشده است که خدای یگانه را بپرستند [همان خداوندی‌] که خدایی جز او نیست، [و] منزه است از شریکی که برای او قائل می‌شوند'),
(1267, 9, 32, 'می‌خواهند نور الهی را با سخنان خویش خاموش کنند و خداوند جز این نمی‌خواهد که نور خویش را کمال بخشد، ولو کافران ناخوش داشته باشند'),
(1268, 9, 33, 'او کسی است که پیامبرش را با هدایت و دین حق فرستاده است، تا آن را بر همه ادیان پیروز گرداند ولو آنکه مشرکان ناخوش داشته باشند'),
(1269, 9, 34, 'ای مؤمنان بدانید که بسیاری از احبار و راهبان، اموال مردم را به ناحق می‌خورند و [مردمان را] از راه خدا باز می‌دارند، و کسانی که زر و سیم می‌اندوزند و آن را در راه خدا خرج نمی‌کنند، ایشان را از عذابی دردناک خبر ده‌'),
(1270, 9, 35, 'آن روز که آن را در آتش جهنم تافته کنند و پیشانی و پهلو و پشت آنان را با آن داغ بگذارند [به آنان گویند] این همان است که برای خودتان اندوخته بودید، پس [طعم‌] اندوخته‌هایتان را بچشید'),
(1271, 9, 36, 'بدانید که شمار ماهها نزد خداوند در کتاب الهی [لوح محفوظ از] روزی که آسمانها و زمین را آفریده است، دوازده ماه است، که از آن چهار ماه حرام است، این دین استوار است، پس در مورد آنها بر خود ستم نکنید، و با مشرکان همگی کارزار کنید، همچنانکه آنان هم با شما همگی کارزار می‌کنند و بدانید که خداوند با پرهیزگاران است‌'),
(1272, 9, 37, 'همانا نسئی و [کبیسه کردن سال و تغییر دادن ماه حرام‌] افزایش در کفر است که کافران به آن وسیله به گمراهی کشیده می‌شوند، زیرا در سالی آن [ماه‌] را حلال می‌شمارند و در سالی حرام تا [ظاهرا]با شماره ماههایی که خداوند حرام گردانده است برابر کنند، بالنتیجه حرام الهی را حلال می‌شمارند، بد کرداریهایشان در نظرشان آراسته جلوه یافته است، و خداوند خداشناسان را هدایت نمی‌کند'),
(1273, 9, 38, 'ای مؤمنان چرا هنگامی که به شما گفته می‌شود در راه خدا رهسپار [جهاد] شوید، گرانجانی می‌کنید؟ آیا زندگانی دنیا را به جای آخرت پسندیده‌اید؟ در حالی که متاع زندگانی دنیا در جنب آخرت، بس اندک است‌'),
(1274, 9, 39, 'اگر رهسپار [جهاد] نشوید خداوند دچار عذابی دردناک می‌گرداندتان و به جای شما قوم دیگری پدید می‌آورد و به او زیانی نمی‌رسانید، و خداوند بر هر کاری تواناست‌'),
(1275, 9, 40, 'اگر او [پیامبر] را یاری ندهید، [بدانید که‌] خداوند او را هنگامی که کافران آواره‌اش ساختند، یاری داد، آنگاه که یکی از آن دو به هنگامی که در غار بودند به رفیقش [ابوبکر] گفت نگران مباش که خدا با ماست، آنگاه خداوند آرامش خویش را بر او نازل کرد و به سپاهیانی که نمی‌دیدیدشان یاری داد و [سرانجام‌] آرمان کافران را پست گردانید، و آرمان الهی است که والاست، و خداوند پیروزمند فرزانه است‌'),
(1276, 9, 41, 'چه سبکبار، چه سنگین، روانه شوید و به مال و جانتان در راه خدا جهاد کنید، که اگر بدانید این برایتان بهتر است‌'),
(1277, 9, 42, 'اگر سود و ثمری زودیاب و سفری سهل و ساده در پیش بود، از تو پیروی می‌کردند، ولی راه پرمشقت بر ایشان گران آمده است و به خداوند سوگند یاد خواهند کرد که اگر تاب و توان داشتیم، همراه شما رهسپار می‌شدیم، خود را هلاک می‌کنند و خداوند می‌داند که دروغ می‌گویند'),
(1278, 9, 43, 'خداوند از تو درگذرد، چرا پیش از آنکه حال راستگویان بر تو معلوم گردد و دروغگویان را بشناسی، به آنان اجازه دادی؟'),
(1279, 9, 44, 'کسانی که به خداوند و روز بازپسین ایمان دارند از تو عذر و اجازه نمی‌خواهند [که تن بزنند] از اینکه به مال و جانشان جهاد کنند، و خداوند از پرهیزگاران آگاه است‌'),
(1280, 9, 45, 'همانا کسانی از تو عذر و اجازه می‌خواهند که به خداوند و روز بازپسین ایمان ندارند و دلهایشان مردد است و در شک و شبهه‌شان سرگشته‌اند'),
(1281, 9, 46, 'و اگر اینان به راستی عزم رهسپاری داشتند، برای آن تهیه و تدارکی می‌دیدند، ولی خداوند انگیزش آنان را خوش نداشت، لذا بی‌تصمیمشان گذاشت و [به ایشان‌] گفته شد همنشین خانه‌نشینان باشید'),
(1282, 9, 47, 'اگر همراه شما رهسپار می‌شدند جز فتنه و فساد برای شما به بار نمی‌آوردند و در بین شما رخنه می‌کردند و در حق شما فتنه جویی می‌کردند، و در میان شما جاسوسانی دارند، و خداوند به [احوال‌] ستمگران آگاه است‌'),
(1283, 9, 48, 'به راستی که پیشترها هم فتنه‌جویی می‌کردند و در حق تو بد می‌سگالیدند، تا آنکه حق پدید آمد و امر الهی -با آنکه آنان ناخوش داشتندآشکار شد'),
(1284, 9, 49, 'و از ایشان کسی هست که می‌گوید به من اجازه [نیامدن به جهاد] بده و مرا در فتنه مینداز، بدانید که هم اکنون در فتنه افتاده‌اند و جهنم بر کافران چیره است‌'),
(1285, 9, 50, 'اگر خیر و خوشی به تو برسد ایشان را بد آید و اگر مصیبتی به تو رسد گویند ما از پیش حساب کار خود را داشتیم و شادمانه روی برتافته بر می‌گردند'),
(1286, 9, 51, 'بگو هرگز چیزی جز آنچه خداوند برای ما مقرر داشته است، به ما نمی‌رسد، [و] او مولای ماست و مؤمنان باید بر خداوند توکل کنند'),
(1287, 9, 52, 'بگو آیا غیر از یکی از دو خوبی را برای ما انتظار می‌کشید، و ما برای شما انتظار می‌کشیم که خداوند به عذابی از سوی خویش یا به دست ما دچارتان کند، پس هم شما چشم به راه باشید و هم ما چشم به راهیم‌'),
(1288, 9, 53, 'بگو چه با رغبت انفاق کنید، چه با اکراه هرگز از شما پذیرفته نمی‌شود چرا که شما قومی نافرمان بوده‌اید'),
(1289, 9, 54, 'و هیچ چیز آنان را از پذیرفته شدن نفقاتشان باز نداشت، مگر آنکه به خداوند و پیامبر او کفرورزیده و جز با حالت کسالت به نماز نپرداخته‌اند و جز با اکراه انفاق نکرده‌اند'),
(1290, 9, 55, 'پس نه اموال و نه اولادشان تو را به شگفتی نیندازد، جز این نیست که خداوند می‌خواهد آنان را در زندگانی دنیا به این وسیله عذاب کند و در حال کفر جانشان به در رود'),
(1291, 9, 56, 'و به خداوند سوگند یاد می‌کنند که آنان از شما هستند ولی آنان از شما نیستند، بلکه گروهی‌اند که می‌ترسند'),
(1292, 9, 57, 'اگر پناهگاه یا نهانگاهها یا گریزگاهی بیابند، شتابان به آن روی می‌آورند'),
(1293, 9, 58, 'و از ایشان کسی هست که از تو در تقسیم صدقات عیب می‌گیرد، یعنی اگر از آنها به آنان ببخشند، خشنود شوند، و اگر از آن نبخشند، آنگاه در خشم می‌شوند'),
(1294, 9, 59, 'ولی اگر آنان به آنچه خداوند و پیامبرش به ایشان بخشیده‌اند، خشنود می‌شدند و می‌گفتند خداوند ما را بس، زودا که خداوند و پیامبر او از فضل خویش به ما ببخشند و ما به خداوند می‌گراییم [برایشان بهتر بود]'),
(1295, 9, 60, 'زکات فقط برای تهیدستان و بینوایان و کارگزاران آن و دلجویی شدگان و بردگان‌[ی که می‌خواهند بازخرید شوند] و وامداران و هزینه در راه خدا و در راه ماندگان است که فریضه الهی است و خداوند دانای فرزانه است‌'),
(1296, 9, 61, 'و از ایشان کسانی هستند که پیامبر را می‌آزارند و می‌گویند او زودباور است، بگو به سود شماست که زودباور است، که به خداوند و مؤمنان ایمان دارد و رحمت الهی برای کسانی از شماست که ایمان آورده‌اند، و کسانی که پیامبر خدا را می‌آزارند، عذاب دردناکی [در پیش‌] دارند'),
(1297, 9, 62, 'برای شما به خداوند سوگند می‌خورند که خشنودتان کنند، و حال آنکه اینان اگر مؤمنند [بدانند که‌] خداوند و پیامبر او سزاوارترند به آنکه خشنودشان کنند'),
(1298, 9, 63, 'آیا ندانسته‌اند که هرکس با خداوند و پیامبر او مخالفت ورزد، آتش جهنم نصیب اوست که جاودانه در آن می‌ماند، و این خفت و خواری بزرگ است‌'),
(1299, 9, 64, 'منافقان بیمناکند از اینکه سوره‌ای بر ضد آنان نازل گردد که [مؤمنان را] از ما فی‌الضمیرشان آگاه گرداند، بگو ریشخند کنید، بی‌گمان خداوند آشکارکننده رازی است که از آن بیمناکید'),
(1300, 9, 65, 'و اگر از ایشان بپرسی [چرا ریشخند می‌کردید] بی‌گمان خواهند گفت، ما فقط حرف می‌زدیم و خود را سرگرم می‌کردیم، بگو آیا به خداوند و آیات او و پیامبر او ریشخند می‌کردید؟'),
(1301, 9, 66, 'عذر و بهانه نیاورید به راستی که پس از ایمانتان کافر شده‌اید، اگر گروهی از شما را ببخشیم گروهی را عذاب می‌کنیم چرا که گناهکار بوده‌اند'),
(1302, 9, 67, 'مردان و زنان منافق همه از یکدیگرند که امر به منکر و نهی از معروف می‌کنند و دستانشان را [از انفاق‌] بسته می‌دارند، خداوند را فراموش کرده‌اند و خداوند هم [به کیفر آن‌] فراموششان کرده است، بی‌گمان منافقان فاسقند'),
(1303, 9, 68, 'خداوند به مردان منافق و زنان منافق و کافران از آتش جهنم بیم داده است که جاودانه در آنند و همان ایشان را بس، و خداوند لعنتشان کرده است و عذابی پاینده دارند'),
(1304, 9, 69, 'همانند پیشینیانتان که از شما نیرومندتر و ثروتمندتر و پرزاد و رودتر بودند، آری آنان از نصیب خویش بهره‌مند شدند و شما نیز همانند پیشینیانتان که از نصیب خویش بهره‌مند شدند، از نصیب خویش بهره‌مند شدید، و همانند آنان که ژاژخایی کردند، ژاژخایی کردید، اینان اعمالشان در دنیا و آخرت تباه شده و اینان زیانکارند'),
(1305, 9, 70, 'آیا خبر پیشینیانشان از قوم نوح و عاد و ثمود و قوم ابراهیم و اهل مدین و شهرهای زیر و زبر شده‌[ی قوم لوط] به آنان نرسیده است که پیامبرانشان معجزات برایشان آوردند و [در نهایت‌] خداوند نخواست بر آنان ستم کند اما [آنان‌] بر خود ستم می‌کردند'),
(1306, 9, 71, 'و مردان و زنان مؤمن دوستدار همدیگرند، که امر به معروف و نهی از منکر می‌کنند و نماز برپا می‌دارند و زکات می‌پردازند و از خداوند و پیامبر او اطاعت می‌کنند، زودا که خداوند بر آنان رحمت آورد، که خداوند پیروزمند فرزانه است‌'),
(1307, 9, 72, 'خداوند به مردان مؤمن و زنان مؤمن وعده بوستانهایی را داده است که جویباران از فرودست آن جاری است، که جاودانه در آنند و [نیز] خانه‌های پاک و پسندیده‌ای در بهشت عدن، و خشنودی الهی برتر [از همه چیز] است، این همان رستگاری بزرگ است‌'),
(1308, 9, 73, 'ای پیامبر با کافران و منافقان جهاد کن و با آنان درشتی کن که سر او سرانجامشان جهنم است و بد سرانجامی است‌'),
(1309, 9, 74, 'اینان به خداوند سوگند می‌خورند که نگفته‌اند ولی به راستی سخن کفرآمیز را گفته‌اند، و بعد از اسلام آوردنشان کافر شده‌اند، و آهنگ کاری را کرده‌اند که به آن دست نیافته‌اند و به انکار برنخواستند مگر از آن روی که خداوند و پیامبرش از فضل خویش ایشان را توانگر ساخته‌اند، اگر توبه کنند برایشان بهتر است، و اگر رویگردان شوند، خداوند ایشان را در دنیا و آخرت به عذابی دردناک گرفتار خواهد ساخت و در این سرزمین یار و یاوری ندارند'),
(1310, 9, 75, 'و از ایشان کسانی هستند که با خداوند عهد کرده بودند که اگر از فضل خویش به ما ببخشد، زکات خواهیم داد و از صالحان خواهیم شد'),
(1311, 9, 76, 'و چون خداوند از فضل خویش به ایشان بخشید، در آن بخل ورزیدند و برگشتند و رویگردان شدند'),
(1312, 9, 77, 'او نیز به خاطر آنکه وعده خویش را با خداوند خلاف کرده بودند و به خاطر آنکه دروغ می‌گفتند، تا روزی که به لقای او [و پای حساب‌] برسند، به دنبال آن، [داغ‌] نفاق بر دلهایشان نهاد'),
(1313, 9, 78, 'آیا نمی‌دانند که خداوند راز و نجوایشان را می‌داند و اینکه خداوند دانای رازهای نهانی است‌'),
(1314, 9, 79, 'کسانی که به داوطلبان مؤمن درباره صدقات [آنان‌] و نیز درباره کسانی که چیزی جز نهایت وسع [ناچیز] خویش نمی‌یابند، طعنه می‌زنند و آنان را ریشخند می‌کنند، [بدانند که‌] خداوند [به کیفر آن‌] ریشخندشان می‌کند و عذابی دردناک [درپیش‌] دارند'),
(1315, 9, 80, 'چه برای آنان آمرزش بخواهی، چه آمرزش نخواهی [یکسان است‌]، اگر هفتاد بار برای آنان آمرزش بخواهی خداوند هرگز آنان را نخواهد آمرزید، این از آن است که به خداوند و پیامبرش کفرورزیدند و خداوند نافرمانان را هدایت نمی‌کند'),
(1316, 9, 81, 'واپس‌گذشتگان، از خانه‌نشینی خود که مخالفت با پیامبر خدا بود، شادمان شدند، و [در دل‌] ناخوش داشتند که در راه خدا به مال و جانشان جهاد کنند، و می‌گفتند در گرما رهسپار نشوید، بگو اگر دریابند آتش جهنم گرمتر است‌'),
(1317, 9, 82, 'از این پس، اندکی بخندند و فراوان بگریند، که کیفر کار و کردارشان است‌'),
(1318, 9, 83, 'و اگر خداوند تو را به سوی گروهی از آنان باز گرداند، و از تو برای همراهی اجازه خواستند، بگو هرگز همراه من نخواهید آمد، و هرگز همراه من، با هیچ دشمنی نخواهید جنگید، چرا که نخست بار به خانه‌نشینی راضی شدید، اکنون هم با خانه‌نشینان بنشینید'),
(1319, 9, 84, 'و هرگز بر هیچ‌یک از آنان هنگامی که درگذشت نماز مخوان و بر سر گور او مایست، چرا که اینان به خداوند و پیامبر او کفرورزیده‌اند و در نافرمانی مرده‌اند'),
(1320, 9, 85, 'و اموال و اولادشان تو را به شگفتی نیندازد، جز این نیست که خداوند می‌خواهد به این وسیله در دنیا عذابشان کند و در حال کفر جانشان به در رود'),
(1321, 9, 86, 'و چون سوره‌ای نازل شود که به خداوند ایمان آورید و همراه پیامبر او جهاد کنید توانمندانشان از تو عذر و اجازه می‌خواهند و گویند ما را بگذار با خانه‌نشینان باشیم‌'),
(1322, 9, 87, 'راضی شدند که با خانه‌نشینان باشند، و بر دلهایشان مهر [نفاق‌] نهاد شده است، از این روی در نمی‌یابند'),
(1323, 9, 88, 'ولی پیامبر و کسانی که همراه او ایمان آورده‌اند به مال و جان جهاد می‌کنند و خیرات نصیب آنان است و هم اینان رستگارند'),
(1324, 9, 89, 'خداوند برایشان بوستانهایی که جویباران از فرودست آن جاری است، آماده ساخته است که جاودانه در آنند، و این رستگاری بزرگ است‌'),
(1325, 9, 90, 'و عذرتراشان اعرابی به نزد تو آمدند که به آنان اجازه [خانه‌نشینی‌] داده شود، و کسانی که به خداوند و پیامبرش دروغ گفتند نیز خانه‌نشین شدند، زودا که به آن دسته از آنان که کافر شده‌اند، عذابی دردناک برساند'),
(1326, 9, 91, 'بر ناتوانان و بیماران و کسانی که چیزی ندارند که انفاق کنند، ایرادی نیست، به شرط آنکه نسبت به خداوند و پیامبرش خیراندیش باشند، و بر نیکوکاران ایرادی نیست، و خداوند آمرزگار مهربان است‌'),
(1327, 9, 92, 'و نیز بر کسانی که چون به نزد تو آمدند که سوار و رهسپارشان کنی، گفتی چیزی ندارم که شما را بر آن سوار کنم، برگشتند و چشمانشان سرشار از اشک بود، از اندوه اینکه چرا چیزی ندارند که خرج [راه‌] کنند'),
(1328, 9, 93, 'ایراد فقط بر کسانی است که با آنکه توانگرند از تو عذر و اجازه می‌خواهند و به این راضی شده‌اند که با خانه‌نشینان باشند، و خداوند بر دلهایشان مهر [نفاق‌] نهاده است، از این روی در نمی‌یابند'),
(1329, 9, 94, 'چون به سوی ایشان بازگشتید از شما عذر می‌خواهند، بگو عذر نخواهید که هرگز [سخن‌] شما را باور نخواهیم کرد، به راستی که خداوند ما را از احوال شما آگاه ساخته است و خداوند و پیامبر او شاهد عمل شما هستند، آنگاه به سوی [خداوند] دانای پنهان و پیدا بازگردانده شوید و شما را از [نتیجه‌] کار و کردارتان آگاه خواهد ساخت‌'),
(1330, 9, 95, 'چون به سوی آنان بازگشتید به خداوند سوگند می‌خورند که از آنان درگذرید، آری از آنان روی بگردانید که پلیدند و به کیفر کار و کردارشان سرا و سرانجامشان جهنم است‌'),
(1331, 9, 96, 'برای شما سوگند یاد می‌کنند که شما از ایشان راضی شوید، و اگر هم شما از ایشان راضی شوید، خداوند از نافرمانان راضی نیست‌'),
(1332, 9, 97, 'اعرابیان کفرپیشه‌تر و نفاق‌پیشه‌تر [از دیگران‌]اند و در ندانستن چون و چند احکامی که خداوند بر پیامبرش نازل کرده است، پیشتر [از دیگران‌]اند، و خداوند دانای فرزانه است‌'),
(1333, 9, 98, 'و از اعرابیان کسانی هستند که آنچه انفاق کرده‌اند [در حکم‌] تاوان می‌گیرند و در حق شما انتظار حادثه‌های ناگوار دارند، حادثه ناگوار نصیب خودشان باد، و خداوند شنوای داناست‌'),
(1334, 9, 99, 'و بعضی از اعرابیان هستند که به خداوند و روز بازپسین ایمان دارند و نفقات خود را مایه تقرب در نزد خداوند و درود و دعای پیامبر می‌شمارند، آری آنها مایه تقرب ایشان است، زودا که خداوند ایشان را به جوار رحمت خویش در آورد، که خداوند آمرزگار مهربان است‌'),
(1335, 9, 100, 'و پیشروان نخستین از مهاجران و انصار و کسانی که به نیکوکاری از ایشان پیروی کرده‌اند [تابعان‌]، خداوند از آنان خشنود است و آنان نیز از او خشنودند و برای آنان بوستانهایی آماده کرده است که جویباران از فرودست آن جاری است و جاودانه در آنند، این رستگاری بزرگ است‌'),
(1336, 9, 101, 'و از اعرابیان پیرامون شما و نیز از اهل مدینه منافقانی هستند که به نفاق خوگر شده‌اند، تو آنان را نمی‌شناسی، ما ایشان را می‌شناسیم، دو بار عذابشان خواهیم کرد، سپس دچار عذابی سهمگین شوند'),
(1337, 9, 102, 'و دیگرانی هستند که به گناهان خویش اعتراف کرده‌اند و کارشایسته را با ناشایسته آمیخته‌اند، باشد که خداوند از آنان درگذرد، چرا که خداوند آمرزگار مهربان است‌'),
(1338, 9, 103, 'از اموال ایشان کفاره‌ای بگیر که بدان پاکیزه‌شان کنی و در حق آنان دعا کن که دعای تو مایه آرامش آنان است و خداوند شنوای داناست‌'),
(1339, 9, 104, 'آیا ندانسته‌اند که خداوند است که توبه بندگانش و نیز صدقات را می‌پذیرد، و اینکه خداوند توبه‌پذیر مهربان است‌'),
(1340, 9, 105, 'و بگو کارتان را بکنید به زودی خداوند و پیامبر او و مؤمنان در کار شما خواهند نگریست، و زودا که به محضر دانای پنهان و پیدا بازگردانده شوید، آنگاه شما را [از حقیقت و نتیجه‌] کار و کردارتان آگاه می‌سازد'),
(1341, 9, 106, 'و دیگرانی هستند که واگذاشته به امر الهی‌اند، یا عذابشان می‌کند یا از آنان در می‌گذرد، و خداوند دانای فرزانه است‌'),
(1342, 9, 107, 'و کسانی هستند که مسجد را دستاویز زیان رساندن و کفر و تفرقه‌اندازی بین مؤمنان، و نیز کمینگاهی برای کسانی که پیش از آن با خداوند و پیامبر او به محاربه برخاسته بودند، ساختند، و سوگند می‌خورند که هدفی جز خیر و خدمت نداریم، و خداوند گواهی می‌دهد که ایشان دروغگو هستند'),
(1343, 9, 108, 'هرگز در آن درنگ مکن، چرا که مسجدی که از روز نخست بنیادش بر تقوا بوده است، سزاوارتر است که در آن درنگ کنی [و] در آن مردانی هستند که دوست دارند که پاک و پیراسته شوند، و خداوند پاکیزگان را دوست دارد'),
(1344, 9, 109, 'آیا کسی که بنیاد آن [مسجد] را بر خدا ترسی و خشنودی الهی گذاشته باشد بهتر است یا کسی که بنیاد آن را بر لبه پرتگاه فرو ریختنی نهاده باشد که به آتش دوزخ سرازیرش می‌کند، و خداوند ستمکاران را هدایت نمی‌کند'),
(1345, 9, 110, 'پیوسته بنیادی که بنا گذاشته‌اند در دلشان شک و شبهه برمی‌انگیزد تا آن زمان که دلهایشان پاره و پراکنده شود، و خداوند دانای فرزانه است‌'),
(1346, 9, 111, 'خداوند جان و مال مؤمنان را در ازاء بهشت از آنان خریده است، همان کسانی که در راه خدا کارزار می‌کنند و می‌کشند و کشته می‌شوند، این وعده به راستی و درستی در تورات و انجیل و قرآن بر عهده اوست، و کیست از خداوند وفادارتر به پیمانش؟ پس به داد و ستدی که به آن دست زده‌اید، شادمان باشید، و این همان رستگاری بزرگ است‌'),
(1347, 9, 112, 'آنان توبه‌کارانند و پرستشگران و سپاسگزاران و روزه‌داران و نمازگزاران و اهل سجود که آمران به معروف و ناهیان از منکر و پاسداران احکام الهی هستند، و مؤمنان را بشارت ده‌'),
(1348, 9, 113, 'سزاوار نیست که پیامبر و مؤمنان برای مشرکان، ولو خویشاوند باشند، پس از آنکه بر ایشان آشکار شده است که آنان دوزخی‌اند، آمرزش بخواهند'),
(1349, 9, 114, 'و آمرزش خواهی ابراهیم برای پدرش جز به خاطر وعده‌ای نبود که [ابراهیم‌] به او داده بود، و آنگاه که بر او آشکار شد که او دشمن خداست، از او بری و برکنار شد، چرا که ابراهیم دردمندی بردبار بود'),
(1350, 9, 115, 'و خداوند بر آن نیست که گروهی را پس از آنکه هدایتشان کرده است، بیراه بگذارد، مگر آنکه چیزی را که باید از آن پروا و پرهیز داشته باشند برای آنان روشن کرده باشد، که بی‌گمان خداوند به هر چیزی داناست‌'),
(1351, 9, 116, 'همانا فرمانروایی آسمانها و زمین از آن خداوند است که زنده می‌دارد و می‌میراند، و غیر از او یار و یاوری ندارید'),
(1352, 9, 117, 'به راستی که خداوند بر پیامبر و مهاجران و انصاری که از او در هنگام سختی پیروی کردند، رحمت آورد، و آن پس از زمانی بود که نزدیک بود دلهای گروهی از آنان [از طاعت و اطاعت‌] بگردد، آنگاه از آنان درگذشت چرا که او به ایشان رئوف و مهربان است‌'),
(1353, 9, 118, 'و نیز بر سه تنی که [از پیوستن به مجاهدان تبوک‌] باز پس مانده شدند، تا آنگاه که عرصه زمین با آنکه گسترده است، بر آنان تنگ آمد، و تنگدل شدند و دانستند که پناهی از خداوند جز به او نیست، سپس به آنان توفیق توبه داد تا توبه کنند، که بی‌گمان خداوند توبه‌پذیر مهربان است‌'),
(1354, 9, 119, 'ای مؤمنان از خداوند پروا کنید و در زمره راستان باشید'),
(1355, 9, 120, 'اهل مدینه و اعرابیان پیرامونشان را نرسد که از حکم پیامبر خدا سر پیچند و جان خود را از جان او عزیزتر بدارند، چرا که هیچ تشنگی و هیچ خستگی و هیچ گرسنگی در راه خدا به آنان نرسد و هیچ گامی به پایگاهی [از سرزمین دشمن‌] نگذارند که کافران را به خشم آورد، و هیچ دستبردی به هیچ دشمنی نزنند، مگر آنکه در برابر آن عمل صالحی برای آنان [در کارنامه‌شان‌] نوشته شود، زیرا که خداوند پاداش نیکوکاران را فرو نمی‌گذارد'),
(1356, 9, 121, 'و هیچ هزینه خرد و کلانی نکنند و هیچ سرزمینی را نپیمایند، مگر آنکه به حساب آنان نوشته شود، تا سرانجام خداوند به بهتر از آنچه کرده‌اند، پاداششان دهد'),
(1357, 9, 122, 'و سزاوار نیست که مؤمنان همگی رهسپار [جهاد] شوند، اما چرا از هر فرقه‌ای از آنان گروهی رهسپار نشوند که دین پژوهی کنند و چون به نزد قومشان باز گشتند ایشان را هشدار دهند تا پروا پیشه کنند؟'),
(1358, 9, 123, 'ای مؤمنان با کسانی از کفار که نزدیک شما هستند کارزار کنید و باید که در شما درشتی بینند و بدانید که خداوند با پارسایان است‌'),
(1359, 9, 124, 'و چون سوره‌ای نازل گردد، بعضی از ایشان می‌گویند این سوره به ایمان کدام یک از شما افزود؟ اما کسانی که ایمان آورده‌اند، بر ایمانشان می‌افزاید و به آن شادمانند'),
(1360, 9, 125, 'و اما بیماردلان را پلیدی بر پلیدیشان می‌افزاید و در حال کفر در می‌گذرند'),
(1361, 9, 126, 'آیا نمی‌بینند که در هر سال یک بار یا دو بار در معرض آزمون و ابتلا در می‌آیند، و باز توبه نمی‌کنند و پند نمی‌گیرند'),
(1362, 9, 127, 'و چون سوره‌ای نازل گردد، بعضی از آنان به بعضی دیگر بنگرند [و بپرسند که‌] آیا کسی شما را می‌بیند؟ [و چون کسی ایشان را نبیند] بر می‌گردند، خداوند دلهایشان را بگرداند، چرا که قومی بی‌تمیزند'),
(1363, 9, 128, 'به راستی که پیامبری از میان خودتان به سوی شما آمده است که هر رنج که شما می‌برید، برای او گران می‌آید، و سخت هواخواه شماست و به مؤمنان رئوف و مهربان است‌'),
(1364, 9, 129, 'و اگر رویگردان شدند، بگو خداوند مرا کافی است، خدایی جز او نیست، بر او توکل کردم و او صاحب عرش عظیم است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الر الف لام راء این آیات کتاب حکمت‌آموز است‌'),
(1366, 10, 2, 'آیا برای مردمان شگفت‌آور است که به مردی از ایشان وحی فرستادیم که مردم را هشدار ده و مؤمنان را بشارت ده که نزد پروردگارشان مقام و منزلتی راستین دارند، و کافران گفتند این جادوگری آشکار است‌'),
(1367, 10, 3, 'پروردگار شما همان کسی است که آسمانها و زمین را در شش روز آفرید، سپس بر عرش استیلاء یافت، کار [جهان‌] را تدبیر می‌کند، شفیعی نیست مگر پس از اذن او، چنین است خداوند، پروردگارتان، او را بپرستید، آیا پند نمی‌گیرید؟'),
(1368, 10, 4, 'بازگشت همگی شما به سوی اوست، این وعده راست و درست خداوند است، هموست که آفرینش را آغاز کرده، سپس بازش می‌گرداند، تا کسانی را که ایمان آورده‌اند و کارهای شایسته کرده‌اند، دادگرانه جزا دهد، و کافران به خاطر کفری که ورزیده‌اند، نوشابه‌ای از آب گرم و عذابی دردناک [در پیش‌] دارند'),
(1369, 10, 5, 'اوست کسی که خورشید را روشن و ماه را تابان کرد و برای آن [ماه‌] منزلهایی معین کرد، تا شمار سالها و حساب [زندگی خود] را بدانید، خداوند این را جز به حق نیافریده است، و برای اهل معرفت آیات خود را به روشنی بیان می‌دارد'),
(1370, 10, 6, 'در پی یکدیگر آمدن شب و روز و در آنچه خداوند در آسمانها و زمین آفریده است، مایه‌های عبرتی برای پرواپیشگان است‌'),
(1371, 10, 7, 'کسانی که به لقای ما امید [و باور] ندارند و به زندگانی دنیا خشنودند و بر آن دل نهاده‌اند و هم آنان که از آیات ما غافلند'),
(1372, 10, 8, 'اینان به خاطر کار و کردارشان سرا و سرانجامشان دوزخ است‌'),
(1373, 10, 9, 'کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، پروردگارشان ایشان را به جزای ایمانشان به بوستانهای بهشتی پر از ناز و نعمت -که جویباران از فرودست آنان جاری استرهنمون می‌گردد'),
(1374, 10, 10, 'ندایشان در آنجا سبحانک اللهم و تحیتشان سلام و پایان دعایشان الحمد لله رب العالمین است‌'),
(1375, 10, 11, 'و اگر خداوند برای مردم به همان گونه که خیر را به شتاب می‌خواهند، شر را به پیش می‌انداخت، اجلشان به سر می‌آمد، آری ناامیدواران به لقائمان را وامی‌گذاریم که در طغیانشان سرگشته باشند'),
(1376, 10, 12, 'و چون انسان را رنجی رسد، ما را [در همه حال‌] چه بر پهلو خفته، چه نشسته، چه ایستاده بخواند، آنگاه چون رنجش را از او باز داریم، به راه خود رود، گویی ما را به خاطر رنجی که به او رسیده بود، به دعا نخوانده است، بدین‌سان در نظر اسرافکاران کار و کردارشان آراسته شده است‌'),
(1377, 10, 13, 'و به راستی اقوام پیش از شما را آنگاه که ستم پیشه کردند، نابود کردیم و پیامبرانشان برایشان معجزات آوردند ولی بر آن نبودند که ایمان بیاورند، بدین‌سان گناهکاران را جزا می‌دهیم‌'),
(1378, 10, 14, 'سپس شما را پس از ایشان در این سرزمین جانشین گرداندیم، تا بنگریم که چگونه رفتار می‌کنید'),
(1379, 10, 15, 'و چون آیات ما که روشن و شیواست، بر آنان خوانده شود، ناامیدواران به لقائمان [به پیامبر] می‌گویند قرآنی غیر از این بیاور، یا این را تغییر ده، بگو مرا نرسد که آن را از پیش خود تغییر دهم، جز از وحی که به من می‌شود، از چیزی پیروی نمی‌کنم، من اگر از پروردگارم نافرمانی کنم، از عذاب روز سهمگین می‌ترسم‌'),
(1380, 10, 16, 'بگو اگر خداوند می‌خواست آن را بر شما نمی‌خواندم و خود خداوند هم شما را از آن آگاه نمی‌ساخت، [بنگرید] که پیش از آن عمری در میان شما به سر برده‌ام، چرا تعقل نمی‌کنید؟'),
(1381, 10, 17, 'بدین‌سان کیست ستمکارتر از کسی که بر خداوند دروغ بندد، یا آیات او را دروغ انگارد، به راستی که گنهکاران رستگار نمی‌شوند'),
(1382, 10, 18, 'و به جای خداوند چیزی را می‌پرستند که نه زیانی به آنان می‌رساند و نه سودی، و می‌گویند اینان شفیعان ما در نزد خداوند هستند، بگو آیا خداوند را به چیزی که در آسمانها و زمین به آن علم ندارد، آگاهی می‌دهید؟ او پاک و منزه است از آنچه شریک او قرار می‌دهند'),
(1383, 10, 19, 'و مردم جز امتی واحده نبودند که سپس اختلاف پیدا کردند، و اگر وعده خداوند از پیش مقرر نگشته بود [به تاخیر حکم و عذاب‌] بین آنان در آنچه اختلاف دارند، داوری می‌شد'),
(1384, 10, 20, 'و می‌گویند چرا از سوی پروردگارش معجزه‌ای بر او نازل نمی‌گردد؟ بگو [آگاهی از] غیب خاص خداوند است، پس انتظار بکشید من نیز از منتظران خواهم بود'),
(1385, 10, 21, 'و چون به مردم، بعد از رنجی که به ایشان رسیده است، راحتی بچشانیم، آنگاه است که در آیات ما بدسگالی می‌کنند، بگو خداوند مکراندیش چابک‌تری است، بی‌گمان فرشتگان ما هر مکری را که می‌ورزید می‌نویسند'),
(1386, 10, 22, 'او کسی است که شما را در خشکی و دریا سیر می‌دهد و چون در کشتی نشینید، کشتیها ایشان را با بادی خوش پیش برد، و به آن شادمان شوند، [آنگاه‌] تندبادی بر آن بوزد و موج از هر سو به سوی آنان آید، و دانند که از هر سو گرفتار شده‌اند، [آن وقت است که‌] خداوند را -در حالی که دین خود را برای او پاک و پیراسته دارندخوانند [و گویند] اگر از این [بلیه‌] نجاتمان دهی، بی‌شک از شاکران خواهیم بود'),
(1387, 10, 23, 'و چون نجاتشان دهد، آنگاه است که به ناحق در زمین سرکشی کنند، ای مردم بدانید که سرکشی‌تان به زیان شماست، بهره‌وری [کوتاهی از] زندگانی دنیاست، سپس بازگشتتان به سوی ماست و شما را از [حقیقت و نتیجه‌] کار و کردارتان آگاه می‌سازیم‌'),
(1388, 10, 24, 'همانا داستان زندگانی دنیا، همانند داستان آبی است که آن را از آسمان فرو فرستادیم و رستنیهای خاک از آنچه مردمان یا چارپایان می‌خورند، با آن درهم آمیخت [و انبوه شد] تا آنجا که زمین گل و شکوفه‌هایش را برآورد و آراسته گشت، و سپس اهلش گمان کردند که مهار کار در دست آنان است، [آنگاه‌] فرمان ما در شب یا روز در رسید و آن را چون محصول درو شده گرداندیم، گویی که دیروز هیچ چیز نبوده است، بدین‌سان آیات [خود] را برای اندیشه‌وران روشن و شیوا بیان می‌داریم‌'),
(1389, 10, 25, 'و خداوند به سوی دارالسلام [ سرای سلامت، بهشت‌] می‌خواند و هرکس را که بخواهد به راه راست هدایت می‌کند'),
(1390, 10, 26, 'برای نیکوکاران بهشت و [نعمتی‌] افزونتر هست، و بر چهره آنان غبار [رنج‌] و خواری ننشیند، اینان بهشتیانند و در آن جاودانه‌اند'),
(1391, 10, 27, 'و بدکرداران [بدانند که‌] جزای بدی همانند آن است، و بر چهره‌های آنان غبار خواری نشیند، در برابر خداوند پشت و پناهی ندارند، گویی چهره‌های آنان با تکه‌هایی از شب تاریک پوشیده شده است، اینان دوزخیانند و در آن جاودانه‌اند'),
(1392, 10, 28, 'و روزی که همگی آنان را محشور گردانیم، آنگاه به مشرکان گوییم شما و شریکانی که قائل بودید، باشید و بنگرید، سپس بین ایشان جدایی اندازیم و شریکانی که ایشان قائل بوده‌اند گویند شما [به حقیقت‌] ما را نپرستیده‌اید'),
(1393, 10, 29, 'آری خداوند بین ما و شما گواه بس که ما از عبادت شما به کلی بی‌خبر بودیم‌'),
(1394, 10, 30, 'آنجاست که هر کسی جزای آنچه در گذشته انجام داده است، می‌چشد، و به پیشگاه خداوند که سرور راستین آنهاست، بازبرده شوند، و بر ساخته‌هایشان بر باد رود و از دید آنان ناپدید شود'),
(1395, 10, 31, 'بگو چه کسی شما را از آسمان و زمین روزی می‌دهد، یا کیست که بر گوشها و چشمها حاکم است؟ و کیست که زنده را از مرده و مرده را از زنده بر می‌آورد، و کیست که کار [جهان‌] را تدبیر می‌کند، زودا که خواهند گفت خدا، بگو پس چرا پروا نمی‌کنید؟'),
(1396, 10, 32, 'این چنین است خداوند که پروردگار راستین شماست، و پس از حق غیر از گمراهی چیست، پس چگونه به بیراهه می‌روید'),
(1397, 10, 33, 'بدین‌سان سخن [حکم‌] پروردگارت بر نافرمانان تحقق یافت که ایمان نمی‌آورند'),
(1398, 10, 34, 'بگو آیا از شریکانی که قائل شده‌اید کسی هست که آفرینش را آغاز کند و سپس باز گرداند؟ بگو [فقط] خداوند است که آفرینش را آغاز می‌کند و سپس باز می‌گرداند، پس چگونه به بیراهه می‌روید؟'),
(1399, 10, 35, 'بگو آیا از شریکانی که قائل شده‌اید کیست که به راه حق هدایت کند؟ بگو خداوند است که به راه حق هدایت می‌کند، آیا کسی که به راه حق هدایت می‌کند، سزاوارتر است که از او پیروی شود یا کسی که راه نمی‌برد مگر آنکه راه برده شود، شما را چه می‌شود، چگونه داوری می‌کنید؟'),
(1400, 10, 36, 'و بیشترینه‌شان جز از گمان پیروی نمی‌کنند و گمان حق را به بار نمی‌آورد، بی‌گمان خداوند به آنچه می‌کنند داناست‌'),
(1401, 10, 37, 'و نشاید که این قرآن بر ساخته و از سوی کسی غیر از خداوند باشد، بلکه همخوان با چیزی است که پیشاپیش آن است و روشنگر آن کتاب است، که در آن تردیدی نیست، [و] از پروردگار جهانیان است‌'),
(1402, 10, 38, 'یا می‌گویند که [پیامبر] آن را برساخته است، بگو اگر راست می‌گویید سوره‌ای همانند آن بیاورید و هرکس را که می‌توانید، در برابر خداوند [به کمک‌] بخوانید'),
(1403, 10, 39, 'حق این است که چیزی را که به شناخت آن احاطه نیافته‌اند و سرانجام آن هنوز بر آنان آشکار نشده است، دروغ می‌انگارند، بدین‌سان کسانی که پیش از اینان بودند نیز دروغ انگار بودند بنگر که سرانجام ستمکاران چگونه است‌'),
(1404, 10, 40, 'و از ایشان کسی هست که به آن ایمان دارد و کسی هست که به آن ایمان ندارد و پروردگارت به اهل فساد [از همه‌] آگاه‌تر است‌'),
(1405, 10, 41, 'و اگر تو را دروغگو انگاشتند، بگو عمل من از آن من و عمل شما از آن شما، شما از آنچه من می‌کنم بری و برکنارید و من از آنچه شما می‌کنید بری و برکنارم‌'),
(1406, 10, 42, 'و از ایشان کسانی هستند که به [ظاهر به سخن‌] تو گوش می‌دهند اما آیا تو می‌توانی [سخنت را] به ناشنوایان بشنوانی، ولو اینکه تعقل نکنند؟'),
(1407, 10, 43, 'و از ایشان کسانی هستند که به [ظاهر] به تو می‌نگرند، اما آیا تو می‌توانی نابینایان را هدایت کنی ولو اینکه اهل بصیرت نباشند؟'),
(1408, 10, 44, 'خداوند هیچ ستمی بر مردم روا نمی‌دارد، ولی مردم خودشان بر خود ستم می‌کنند'),
(1409, 10, 45, 'و روزی که آنان را محشور گرداند، گویی جز به اندازه ساعتی از روز [در دنیا یا در گور] به سر نبرده‌اند، و همدیگر را می‌شناسند، به راستی کسانی که لقای الهی را انکار کردند و رهیافته نبودند، زیانکار شدند'),
(1410, 10, 46, 'و اگر بخشی از آنچه به ایشان وعده داده‌ایم، به تو بنمایانیم، یا جان تو را [پیش از آن‌] بگیریم، بدان که بازگشت آنان به سوی ماست، آنگاه خداوند خود گواه بر کار و کردار آنان است‌'),
(1411, 10, 47, 'و هر امتی پیامبری دارد، و چون پیامبرشان بیاید در میان آنان دادگرانه داوری شود و بر آنان ستم نرود'),
(1412, 10, 48, 'و گویند اگر راست می‌گویید [سررسید] این وعده کی خواهد بود؟'),
(1413, 10, 49, 'بگو برای خود اختیار زیان و سودی ندارم، مگر آنچه خدا بخواهد، هر امتی را اجلی است، و چون اجلشان فرا رسد، نه ساعتی پس افتند و نه ساعتی پیش افتند'),
(1414, 10, 50, 'بگو بیندیشید اگر عذاب او شبانه یا در روز بر شما نازل گردد [چه می‌کنید؟] گناهکاران چه چیز را از او به شتاب می‌خواهند'),
(1415, 10, 51, 'آیا آنگاه که رخ داد باورش می‌کنید، آیا اکنون؟ و حال آنکه آن را به شتاب می‌خواستید'),
(1416, 10, 52, 'سپس به ستمکاران گفته شود عذاب جاودانه را بچشید، آیا غیر از جزای کار و کردارتان را می‌بینید؟'),
(1417, 10, 53, 'و از تو می‌پرسند که آیا آن حق است؟ بگو آری سوگند به پروردگارم که آن حق است، و شما گزیر و گریزی ندارید'),
(1418, 10, 54, 'و اگر آنچه در زمین هست از آن هر کسی که ستم [شرک‌]ورزیده است، باشد، بی‌شک آن را بلا گردان کند، و چون عذاب را بنگرند پشیمانی [شان‌] را پنهان کنند، و در میان آنان دادگرانه داوری شود و بر آنان ستم نرود'),
(1419, 10, 55, 'بدانید که آنچه در آسمانها و زمین است از آن خداوند است، بدانید که وعده الهی راست و درست است ولی بیشترینه آنان نمی‌دانند'),
(1420, 10, 56, 'اوست که زنده می‌دارد و می‌میراند و به سوی او باز گردانده شوید'),
(1421, 10, 57, 'ای مردم از سوی پروردگارتان پندی برای شما و شفابخش دلها و رهنمود و رحمتی برای مؤمنان آمده است‌'),
(1422, 10, 58, 'بگو به فضل و رحمت خدا باید شادمانی کنند که این بهتر است از آنچه می‌اندوزند'),
(1423, 10, 59, 'بگو بیندیشید که در رزقی که خداوند برایتان فرستاده است [چرا] چیزی را حرام و چیزی را حلال می‌گردانید بگو آیا خداوند به شما اجازه داده است، یا آنکه بر خداوند دروغ می‌بندید؟'),
(1424, 10, 60, 'و دروغ زنان بر خداوند در روز قیامت چه گمان دارند؟ خداوند بر مردمان بخشش و بخشایش دارد ولی بیشترشان سپاس نمی‌گزارند'),
(1425, 10, 61, 'و تو در هیچ کاری نیستی و هیچ بخشی از قرآن را از سوی او نمی‌خوانی و شما نیز هیچ عملی نمی‌کنید مگر آنکه آنگاه که به آن می‌پردازید ما بر شما گواهیم، و هم سنگ ذره‌ای نه در زمین و نه در آسمان از پروردگارت پنهان نیست و کوچکتر و بزرگتر از این چیزی نیست مگر آنکه در کتابی مبین [لوح محفوظ] ثبت است‌'),
(1426, 10, 62, 'بدانید که دوستداران خدا، نه بیمی بر آنهاست و نه اندوهگین می‌شوند'),
(1427, 10, 63, 'همان کسانی که ایمان آورده‌اند و تقوا ورزیده‌اند'),
(1428, 10, 64, 'آنان در زندگانی دنیا [رؤیاهای‌] بشارت‌بخش دارند، و در آخرت هم [بهشت دارند]، وعده‌های الهی خلف و خلاف ندارد، این همان رستگاری بزرگ است‌'),
(1429, 10, 65, 'و سخن ایشان تو را اندوهگین نکند، که عزت به تمامی به دست خداست، او شنوای داناست‌'),
(1430, 10, 66, 'بدانید که هرکس که در آسمانها و هرکس که در زمین است، ملک و مطیع خداوند است، و کسانی که غیر از خداوند را به دعا می‌خوانند، در حقیقت از شریکانی که قائلند پیروی نمی‌کنند، و پیروی نمی‌کنند مگر از پندار، و کاری جز برساختن ندارند'),
(1431, 10, 67, 'او کسی است که شب را برای شما آفرید تا در آن آرام گیرید و روز را چشم‌اندازی روشن ساخت، در این عبرتها برای گروهی است که گوش شنوا دارند'),
(1432, 10, 68, '[مشرکان‌] گفتند خداوند فرزندی برگزیده است، او منزه است، او بی‌نیاز است، او راست آنچه در آسمانها و زمین است، شما دلیلی بر این [ادعا] ندارید، آیا چیزی را که نمی‌دانید به خداوند نسبت می‌دهید؟'),
(1433, 10, 69, 'بگو کسانی که بر خداوند دروغ می‌بندند، رستگار نمی‌شوند'),
(1434, 10, 70, 'بهره‌وری [گذرایی‌] در دنیاست، سپس بازگشتشان به سوی ماست، آنگاه عذاب شدید را به خاطر کفرورزیدنشان به ایشان می‌چشانیم‌'),
(1435, 10, 71, 'و بر آنان خبر نوح را بخوان، آنگاه که به قومش گفت ای قوم من، اگر قیام من و پند دادنم به آیات الهی بر شما گران آمده است، [بدانید که من‌] بر خداوند توکل کرده‌ام، شما با شریکانی که قائلید کارتان را هماهنگ و عزمتان را جزم کنید، سپس در کارتان پرده‌پوشی نکنید، آنگاه کار مرا یکسره کنید و مهلتم ندهید'),
(1436, 10, 72, 'و اگر رویگردان شوید بدانید که من پاداشی از شما نخواسته‌ام، چرا که پاداشم تنها بر خداوند است، و دستور یافته‌ام که از اهل تسلیم باشم‌'),
(1437, 10, 73, 'اما او را دروغگو انگاشتند، آنگاه او و همراهانش را در کشتی نجات دادیم و آنان را جانشین [پیشینیان‌] گرداندیم، و دروغ انگاران آیات خود را غرقه ساختیم، پس بنگر که سرانجام هشدار یافتگان چگونه بود'),
(1438, 10, 74, 'سپس، بعد از او پیامبرانی برای قومهایشان برانگیختیم، و برای آنان معجزاتی آوردند، ولی آنان بر آن نبودند که به چیزی که از پیش انکار کرده بودند، ایمان بیاورند، بدین‌سان بر دلهای تجاوزکاران مهر می‌نهیم‌'),
(1439, 10, 75, 'سپس بعد از ایشان موسی و هارون را با معجزات خویش به سوی فرعون و بزرگان قومش فرستادیم، ولی استکبارورزیدند و قومی گناهکار بودند'),
(1440, 10, 76, 'و چون حق از جانب ما به سوی آنان آمد، گفتند بی‌گمان این جادویی آشکار است‌'),
(1441, 10, 77, 'موسی گفت آیا این سخن را درباره حقی که برای شما آمده است می‌گویید؟ آیا این جادوست، حال آنکه جادوگران رستگار نمی‌شوند'),
(1442, 10, 78, 'گفتند آیا به سراغ ما آمده‌ای که ما را از شیوه‌ای که پدرانمان را بر آن یافته بودیم، بازداری، و در این سرزمین ریاست از شما دو تن باشد؟ و ما [سخن‌] شما را باور نداریم‌'),
(1443, 10, 79, 'و فرعون گفت همه جادوگران دانا را به نزد من آورید'),
(1444, 10, 80, 'و چون جادوگران فرا آمدند، موسی به آنان گفت چیزی را که اندازنده‌اش هستید، بیندازید'),
(1445, 10, 81, 'و چون درانداختند، موسی گفت آنچه به میان آورده‌اید، جادوست، که خداوند به زودی باطلش می‌کند، چرا که خداوند عمل مفسدان را تایید نمی‌کند'),
(1446, 10, 82, 'و خداوند به کلمات خویش حق را به کرسی می‌نشاند، ولو آنکه گناهکاران ناخوش داشته باشند'),
(1447, 10, 83, 'لذا به موسی جز زاد و رودی [اندکشمار] از قومش ایمان نیاوردند، آن هم با ترس از فرعون و بزرگان قومش که مبادا ایشان را در بلا افکند، و فرعون در آن سرزمین گردنکش بود و همو از تجاوزگران بود'),
(1448, 10, 84, 'و موسی گفت ای قوم من، اگر به خداوند ایمان آورده‌اید و اگر اهل تسلیم هستید، پس بر او توکل کنید'),
(1449, 10, 85, 'در پاسخ گفتند بر خداوند توکل کردیم، پروردگارا ما را دستخوش ستمگران مگردان‌'),
(1450, 10, 86, 'و ما را به رحمت خویش از خدانشناسان رهایی بخش‌'),
(1451, 10, 87, 'و به موسی و برادرش وحی کردیم که قومتان را در مصر در خانه‌هایی سکنا دهید و خانه‌هایتان را [رو به‌] قبله کنید و نماز را برپا دارید و مؤمنان را بشارت ده‌'),
(1452, 10, 88, 'و موسی گفت پروردگارا تو به فرعون و بزرگان قومش در زندگانی دنیا، زینت و مال و منال بخشیدی، پروردگارا، که سرانجام [مردمان را] از راه تو گمراه دارند، پروردگارا اموالشان را بی‌برکت گردان و دلهایشان را سخت کن که تا زمانی که عذاب الیم را ببینند، ایمان نیاورند'),
(1453, 10, 89, 'فرمود دعای شما دو تن اجابت شده است، پس پایداری کنید و از شیوه نادانان پیروی مکنید'),
(1454, 10, 90, 'و بنی‌اسرائیل را از دریا گذراندیم، آنگاه فرعون و سپاهیانش از روی ظلم و تجاوز سر در پی آنان نهادند، تا آنجا که او [فرعون‌] غرق شدن را نزدیک دید، گفت خدایی جز آنکه بنی‌اسرائیل به او ایمان آورده‌اند، نیست و من از اهل تسلیمم‌'),
(1455, 10, 91, 'اینک [ایمان می‌آوری‌]؟ و حال آنکه پیشتر سرپیچی کرده و از تبهکاران بودی‌'),
(1456, 10, 92, 'بدان که امروز بدنت را [از دریا] برهانیم [و بر بلندی اندازیم‌] تا برای آیندگانت مایه عبرت باشد، و چه بسیار از مردم از آیات ما غافلند'),
(1457, 10, 93, 'و بنی‌اسرائیل را در جایی درست ماوا دادیم، و از پاکیزه‌ها روزی بخشیدیم، و بنای اختلاف ننهادند مگر زمانی که خبر وحی به آنان رسید، بی‌گمان پروردگارت در روز قیامت در آنچه اختلاف ورزیده‌اند، بین آنان داوری خواهد کرد'),
(1458, 10, 94, 'و اگر از آنچه بر تو نازل کرده‌ایم، در تردید هستی، از کسانی که پیش از تو کتاب آسمانی خوانده‌اند پرس و جو کن، بی‌شبهه حق از سوی پروردگارت بر تو نازل شده است، پس هرگز از دو دلان مباش‌'),
(1459, 10, 95, 'و از زمره دروغ انگاران آیات الهی مباش که از زیانکاران خواهی شد'),
(1460, 10, 96, 'کسانی که سخن [حکم‌] پروردگارت بر آنان تحقق یافته است،'),
(1461, 10, 97, 'ولو آنکه هر معجزه‌ای برایشان بیاید، ایمان نمی‌آورند، مگر زمانی که [حلول‌] عذاب دردناک را بنگرند'),
(1462, 10, 98, 'چرا اهل هیچ شهری [بهنگام‌] ایمان نیاورد که ایمانش سود دهد، مگر قوم یونس که چون [بهنگام‌] ایمان آوردند، عذاب رسواگر را در زندگانی دنیویشان از آنان برداشتیم و آنان را تا زمانی [معین‌] بهره‌ور ساختیم‌'),
(1463, 10, 99, 'و اگر پروردگارت [به اراده حتمی‌] می‌خواست، تمامی اهل زمین ایمان می‌آوردند، پس آیا تو مردم را به اکراه وامی‌داری تا اینکه مؤمن شوند؟'),
(1464, 10, 100, 'و حال آنکه هیچ کسی را نرسد که جز به اذن [و توفیق‌] الهی ایمان آورد، و پلیدی را بر کسانی که تعقل نمی‌کنند، مقرر می‌دارد'),
(1465, 10, 101, 'بگو [به دیده عبرت‌] بنگرید که در آسمانها و زمین چیست؟ و نشانه‌های عبرت‌انگیز و نیز هشدارها سودی به حال قومی که ایمان نمی‌آورند، ندارد'),
(1466, 10, 102, 'پس آیا انتظار چیزی را جز نظیر روزگار پیشینیانشان می‌کشند؟ بگو پس انتظار بکشید که من نیز همراه شما از منتظرانم‌'),
(1467, 10, 103, 'سپس فرستادگانمان و مؤمنان را نجات می‌دهیم، بدین‌سان بر ماست که مؤمنان را نجات دهیم‌'),
(1468, 10, 104, 'بگو ای مردم، اگر از دین من در شک هستید، بدانید که من کسانی را که به جای خداوند می‌پرستید، نمی‌پرستم، بلکه خدایی را می‌پرستم که [به هنگام‌] جان شما را می‌گیرد، و به من امر شده است که از مؤمنان باشم‌'),
(1469, 10, 105, 'و اینکه به سوی دین پاک روی آور و هرگز از مشرکان مباش‌'),
(1470, 10, 106, 'و به جای خداوند چیزی را که نه سودی به تو می‌رساند و نه زیانی، مپرست، و اگر چنین کردی در آن صورت از ستمکاران [مشرک‌] خواهی بود'),
(1471, 10, 107, 'و اگر خداوند بخواهد بلایی به تو برساند، بازدارنده‌ای جز خود او برای آن وجود ندارد، و اگر خیری برایت بخواهد، فضل او بازدارنده‌ای ندارد، که آن را به هرکس از بندگانش که بخواهد می‌رساند، و او آمرزگار مهربان است‌'),
(1472, 10, 108, 'بگو ای مردم حق از سوی پروردگارتان بر شما نازل شده است، پس هر کس که رهیاب شود، همانا به سود خویش رهیاب شده است، و هرکس بیراه رود، همانا به زیان خویش بیراه رفته است، و من نگهبان شما نیستم‌'),
(1473, 10, 109, 'و از آنچه بر تو وحی می‌شود پیروی کن و شکیبا باش، تا خداوند حکم خویش به میان آورد، و او بهترین داوران است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الر الف لام راء کتابی است از سوی فرزانه‌ای آگاه که آیات آن استوار و روشن و شیواست‌'),
(1475, 11, 2, 'حاکی از اینکه جز خداوند را مپرستید، که من برای شما از سوی او هشداردهنده و بشارت آورنده‌ام‌'),
(1476, 11, 3, 'و اینکه از پروردگارتان آمرزش بخواهید سپس به درگاه او توبه کنید، تا شما را [در زندگانی‌] تا سرآمدی معین به خیر و خوبی بهره‌ور گرداند، و به هرکسی که سزاوار بخشش است، حقش را ببخشد، و اگر روی بگردانید، بدانید که من بر شما از عذاب روزی سهمگین می‌ترسم‌'),
(1477, 11, 4, 'بازگشتتان به سوی خداست و او به هر کاری تواناست‌'),
(1478, 11, 5, 'آری آنان دل می‌گردانند تا از او رازشان را پنهان دارند، بدانید آنگاه که جامه‌هایشان را بر سر می‌کشند، او هر آنچه پنهان می‌دارند و هر آنچه آشکار می‌دارند، می‌داند، چرا که دانای راز دلهاست‌'),
(1479, 11, 6, 'و هیچ جنبنده‌ای در زمین نیست مگر آنکه روزی او بر خداوند است و او آرامشگاه و بازگشتگاهشان را می‌داند، همه در کتابی مبین [ثبت‌] است‌'),
(1480, 11, 7, 'و او کسی است که آسمانها و زمین را در شش روز بیافرید و عرش او بر آب بود، تا سرانجام شما را بیازماید [و معلوم بدارد] که کدامتان نیکو کردارترید، و اگر بگویی شما بعد از مرگ برانگیخته می‌شوید، کافران گویند این جز جادوی آشکار نیست‌'),
(1481, 11, 8, 'و اگر عذاب را به مدت اندکشماری به تاخیر اندازیم، خواهند گفت چه چیزی آن را بازداشت؟ بدانید روزی که بر آنان فرود آید، از آنان بازداشته نمی‌گردد، و آنچه به ریشخند می‌گیرند فرو می‌گیردشان‌'),
(1482, 11, 9, 'و هرگاه انسان را از سوی خود رحمتی بچشانیم، سپس آن را از او باز گیریم، بس نومید و ناسپاس گردد'),
(1483, 11, 10, 'و اگر پس از رنجی که به او رسیده آسایشی بچشانیم، خواهد گفت رنج و بلا از من دور شد و شادمان و نازان گردد'),
(1484, 11, 11, 'مگر کسانی که شکیبایی‌ورزیده و کارهای شایسته کرده‌اند که اینان از آمرزش و پاداشی بزرگ برخوردارند'),
(1485, 11, 12, 'مبادا از بیم آنکه بگویند چرا بر او گنجی نازل نمی‌شود، یا فرشته‌ای همراه او نیامده است، برخی از آنچه بر تو وحی شده است، فروگذاری و دل تنگ داری، تو فقط هشداردهنده‌ای و خداست که کارساز هر چیزی است‌'),
(1486, 11, 13, 'یا می‌گویند آن [قرآن‌] را برساخته است، بگو اگر راست می‌گویید ده سوره برساخته همانند آن بیاورید، و هر کسی را که می‌توانید در برابر خداوند به یاری بخوانید'),
(1487, 11, 14, 'آنگاه اگر از عهده پاسخ شما بر نیامدند بدانید که آن به علم الهی نازل شده است، و اینکه خدایی جز او نیست، پس آیا پذیرنده‌اید؟'),
(1488, 11, 15, 'هر کس زندگانی دنیا و تجمل آن را بخواهد، حاصل اعمالشان را در همین دنیا به تمامی به آنان می‌دهیم، و در آن کم و کاستی نخواهند دید'),
(1489, 11, 16, 'اینان کسانی هستند که در آخرت جز آتش دوزخ ندارند و هر چه در دنیا کرده‌اند بر باد رفته و کار و کردارشان باطل شده است‌'),
(1490, 11, 17, 'آیا کسی که حجت آشکاری از پروردگارش دارد و شاهدی از [خویشان‌] او پیرو آن است [همانند کسی است که بینه و شاهدی ندارد؟] و حال آنکه پیش از آن هم کتاب موسی رهنما و مایه رحمت بود، اینان به آن ایمان دارند و هر کس از گروه مشرکان که به آن کفر و انکار ورزد، آتش دوزخ وعده‌گاه اوست، پس از [حقیقت‌] آن در تردید مباش، که آن راست و درست از سوی پروردگار توست ولی بیشتر مردم باور نمی‌کنند'),
(1491, 11, 18, 'و کیست ستمکارتر از کسی که بر خداوند دروغ بندد، اینان را [در قیامت‌] به پیشگاه پروردگارشان عرضه دارند، و گواهان گویند اینان کسانی هستند که بر پروردگارشان دروغ بستند، بدانید که لعنت الهی بر ستمکاران [مشرک‌] است‌'),
(1492, 11, 19, 'همان کسانی که [مردم را] از راه خدا باز می‌دارند و آن را ناهموار و ناهنجار می‌شمارند و هم آنان آخرت را منکرند'),
(1493, 11, 20, 'اینان در این سرزمین گزیر و گریزی ندارند، و ایشان در برابر خداوند [و به جای او] سروری ندارند، عذابشان دو چندان خواهد شد، [چرا] که گوش شنیدن [حق‌] و چشم دیدن [حق‌] را نداشتند'),
(1494, 11, 21, 'اینان کسانی هستند که به خود زیان زده‌اند و برساخته‌هایشان بر باد رفت و از دید آنان ناپدید شد'),
(1495, 11, 22, 'حقا که در آخرت هم اینان زیانکارترند'),
(1496, 11, 23, 'کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند و در برابر پروردگارشان خشیت و خشوع داشته‌اند، اینان بهشتیان‌اند [و] هم اینان در آن جاودانه‌اند'),
(1497, 11, 24, 'مثل این دو گروه همانند نابینا و ناشنوا [از یک سو] و بینا و شنوا [از سوی دیگر] است، آیا با همدیگر برابر و همانندند؟ پس چرا پند نمی‌گیرید؟'),
(1498, 11, 25, 'به راستی که نوح را به سوی قومش فرستادیم [که با ایشان گفت‌] من هشداردهنده آشکاری شما هستم‌'),
(1499, 11, 26, 'که جز خداوند را مپرستید، که من بر شما از عذاب روزی سهمگین می‌ترسم‌'),
(1500, 11, 27, 'بزرگانی از قومش که کفرورزیده بودند، در پاسخ او گفتند تو را جز بشری همانند خویش نمی‌بینیم و نمی‌بینیم که جز فرومایگان ما [آن هم‌] نسنجیده، از تو پیروی کنند، و برای شما بر خویش [هیچ گونه‌] برتری نمی‌بینیم، بلکه شما را دروغگو می‌دانیم‌'),
(1501, 11, 28, 'گفت ای قوم من بیندیشید که اگر من از سوی پروردگارم حجت آشکاری داشته باشم، و او از سوی خویش رحمتی بر من بخشیده باشد، و [این حقیقت‌] از دید شما پنهان مانده باشد، پس آیا ما می‌توانیم در حالی که شما ناخوش دارید، شما را به آن ملزم کنیم؟'),
(1502, 11, 29, 'و ای قوم من، من از شما مالی [و منالی‌] نمی‌خواهم، اجر من فقط با خداوند است، من طردکننده کسانی که ایمان آورده‌اند، نیستم، چرا که اینان به لقای پروردگارشان خواهند رسید، و من به واقع شما را قومی نادان [و ندانم‌کار] می‌بینم‌'),
(1503, 11, 30, 'و ای قوم من، اگر من آنان را طرد کنم، چه کسی مرا در برابر خداوند یاری خواهد کرد، چرا پند نمی‌گیرید؟'),
(1504, 11, 31, 'و به شما نمی‌گویم که خزاین الهی نزد من است، و غیب نیز نمی‌دانم، و نمی‌گویم که فرشته‌ام، و به کسانی که در چشمان شما خوار می‌آیند، نمی‌گویم که خداوند هرگز خیری به آنان نخواهد داد، خداوند به ما فی‌الضمیر آنان داناتر است، اگر چنین کنم از ستمکاران خواهم بود'),
(1505, 11, 32, 'گفتند ای نوح به راستی که با ما مجادله کردی و چه مجادله دور و درازی هم با ما کردی، و اگر راست می‌گویی هر چه به ما وعده داده‌ای [هم‌اکنون بر سر ما] بیاور'),
(1506, 11, 33, 'گفت بی‌شک خداوند اگر بخواهد آن را [بر سر شما] خواهد آورد و شما گزیر و گریزی ندارید'),
(1507, 11, 34, 'و اگر من بخواهم در حق شما خیر خواهی کنم، در صورتی که خداوند بخواهد شما را بیراه گذارد، خیرخواهی من سودی به حال شما نخواهد کرد، او پروردگار شماست و به سوی او باز گردانده می‌شوید'),
(1508, 11, 35, 'یا می‌گویند آن را برساخته است، بگو اگر آن را برساخته باشم، در آن صورت [مسؤولیت‌] گناه من با من است، ولی من از گناهی که شما می‌کنید بری و برکنارم‌'),
(1509, 11, 36, 'و به نوح وحی شد که از قوم تو جز کسانی که تاکنون ایمان آورده‌اند، ایمان نخواهند آورد، و از آنچه کرده‌اند اندوهگین مباش‌'),
(1510, 11, 37, 'و کشتی را زیر نظر ما و وحی ما بساز و با من درباره کسانی که شرک‌ورزیده‌اند، سخن مگو، که آنان غرق شدنی هستند'),
(1511, 11, 38, 'و [نوح‌] کشتی را می‌ساخت و هر بار که بزرگانی از قومش بر او می‌گذشتند او را ریشخند می‌کردند، می‌گفت اگر ما را ریشخند کنید، ما نیز [به هنگامش‌] به همان گونه که ما را ریشخند می‌کنید، ریشخندتان خواهیم کرد'),
(1512, 11, 39, 'و به زودی خواهید دانست که بر سر چه کسی عذابی می‌آید که خوارش سازد، و عذابی پاینده بر او فرود می‌آید'),
(1513, 11, 40, 'تا به آنجا که فرمان ما در رسید و [آب از] تنور فوران کرد آنگاه گفتیم که در آن [کشتی‌] از هر جنس [جانوری‌] دو تا سوار کن و نیز خانواده‌ات را، مگر کسی که حکم [ما] از پیش درباره او مقرر شده است، و نیز کسانی را که ایمان آورده‌اند، و همراه او جز اندکی ایمان نیاورده بودند'),
(1514, 11, 41, 'و گفت بر آن سوار شوید، که روانه شدن و لنگر انداختنش با نام خداست، که پروردگار من آمرزگار مهربان است‌'),
(1515, 11, 42, 'و آن [کشتی‌] ایشان را در دل موج‌[های‌] کوه پیکر پیش می‌برد، و نوح پسرش را ندا داد -و او در کناری بودکه ای فرزندم همراه ما سوار شو، و با کافران مباش‌'),
(1516, 11, 43, 'گفت به زودی در کوهی پناه می‌گیرم که مرا از سیلاب در امان می‌دارد، [نوح‌] گفت امروز در برابر امر الهی، پشت و پناهی وجود ندارد، مگر کسی که [خداوند] بر او رحمت آورده باشد، و در میان ایشان موج حایل شد، و او از غرق شدگان گردید'),
(1517, 11, 44, 'و گفته شد که ای زمین آبت را فرو بر، و ای آسمان [بارانت را] فروبند، و آب فروکش کرد و کار به سرانجام رسید و [کشتی‌] بر [کوه‌] جودی قرار گرفت، و گفته شد که لعنت بر ستم‌پیشگان [مشرک‌]'),
(1518, 11, 45, 'و نوح پروردگارش را ندا داد و گفت پروردگارا پسرم از خانواده من است و البته وعده تو نیز راست و درست است و تو داورترین داورانی‌'),
(1519, 11, 46, 'فرمود ای نوح او [در حقیقت‌] از خانواده تو نیست، او [را] عملی ناشایسته است، پس از من چیزی مخواه که به [حقیقت‌] آن آگاهی نداری، من پندت می‌دهم که مبادا از نادانان باشی‌'),
(1520, 11, 47, 'گفت پروردگارا من به تو پناه می‌برم که مبادا چیزی را که به آن آگاهی ندارم، از تو بخواهم، و اگر مرا نیامرزی و بر من رحمت نیاوری، از زیانکاران خواهم بود'),
(1521, 11, 48, 'گفته شد ای نوح، فرود آی با سلامت و برکتهایی از ما بر تو و بر امتهایی از [نسل‌] همراهان تو، و امتهایی‌اند که یک چند بهره‌مندشان سازیم، سپس عذابی دردناک از ما به ایشان خواهد رسید'),
(1522, 11, 49, 'این از اخبار غیبی است که بر تو وحی می‌کنیم، نه تو و نه قومت پیش از این آنها را نمی‌دانستید، پس شکیبایی پیشه کن که نیک سرانجامی از آن پرهیزکاران است‌'),
(1523, 11, 50, 'و به سوی قوم عاد برادرشان هود را [فرستادیم‌] گفت ای قوم من خداوند را که خدایی جز او ندارید، بپرستید، شما جز [قومی‌] افتراء زن نیستید'),
(1524, 11, 51, 'ای قوم من از شما برای این کار مزدی نمی‌طلبم، مزد من [بر عهده کسی‌] نیست مگر بر کسی که مرا آفریده است، آیا تعقل نمی‌کنید؟'),
(1525, 11, 52, 'و ای قوم من از پروردگارتان آمرزش بخواهید و به درگاه او توبه کنید، تا از آسمان بر شما بارانی پیوسته و پیگیر بباراند، و قوتی بر قوت شما بیفزاید، و گنهکارانه روی برمتابید'),
(1526, 11, 53, 'گفتند ای هود، برای ما حجتی روشن نیاورده‌ای و ما رهاکننده خدایان خود به خاطر سخن تو نیستیم و ما به تو ایمان نمی‌آوریم‌'),
(1527, 11, 54, 'ما جز این نمی‌گوییم که بعضی از خدایان ما به تو گزندی رسانده‌اند، [هود] گفت من خداوند را گواه می‌گیرم و شما هم گواه باشید که من از شرکی که در برابر او می‌ورزید، بری و برکنارم‌'),
(1528, 11, 55, 'پس همگی در حق من هر کید و مکری که دارید به کار برید، و مهلتم مدهید'),
(1529, 11, 56, 'چرا که من بر خداوند، پروردگارم، که پروردگار شما نیز هست، توکل کرده‌ام، هیچ جنبنده‌ای نیست مگر آنکه او [خداوند] حاکم بر هستی اوست، پروردگار من بر راه راست است‌'),
(1530, 11, 57, 'پس اگر رو بر تابید [بدانید که‌] من به راستی رسالتی را که برای شما داشته‌ام، به شما رسانده‌ام و پروردگار من قومی غیر از شما را جانشین شما خواهد کرد و به او هیچ زیانی نمی‌توانید برسانید، که پروردگار من نگهبان هر چیز است‌'),
(1531, 11, 58, 'و چون فرمان ما در رسید، هود و کسانی را که همراه او ایمان آورده بودند، به رحمت خویش نجات دادیم و از عذاب سخت رهانیدیم‌'),
(1532, 11, 59, 'و این قوم عاد بود که آیات پروردگارشان را انکار کردند و از پیامبران او سرپیچی کردند و از کار و بار هر زورگوی ستیزه‌جویی پیروی کردند'),
(1533, 11, 60, 'و هم در این دنیا و هم در روز قیامت لعنتی گریبانگیرشان شد، بدانید که قوم عاد به پروردگارشان کفرورزیدند، هان نفرین بر عاد قوم هود باد'),
(1534, 11, 61, 'و به سوی قوم ثمود برادرشان صالح را [فرستادیم‌] گفت ای قوم من خداوند را که خدایی جز او ندارید بپرستید، او شما را از زمین پدید آورد و شما را به آبادگری در آن واداشت، پس از او آمرزش بخواهید و به درگاه او توبه کنید، که بی‌گمان پروردگار من نزدیک و اجابتگر است‌'),
(1535, 11, 62, 'گفتند ای صالح پیش از این در میان ما مایه امیدواری بودی، آیا ما را باز می‌داری که چیزی را که پدرانمان پرستیده‌اند بپرستیم؟ ما به چیزی که دعوتمان می‌کنی سخت شک داریم‌'),
(1536, 11, 63, 'گفت ای قوم من، بیندیشید اگر من از سوی پروردگارم حجت آشکاری داشته باشم و بر من از سوی خویش رحمتی بخشیده باشد، اگر از خداوند نافرمانی کنم، چه کسی می‌تواند در برابر خداوند به من یاری برساند؟ پس جز بر خسران من نمی‌افزایید'),
(1537, 11, 64, 'و ای قوم من، این شتر خداست که معجزه برای شماست، پس بگذارید در زمین خدا [آزادانه بچرد و] بخورد و به آن آسیبی نرسانید، که عذابی زودرس گریبانگیرتان خواهد شد'),
(1538, 11, 65, 'آنگاه آن را پی کردند [صالح‌] گفت سه روز در خانه‌هایتان [از زندگی‌] برخوردار باشید، که این وعده‌ای بی‌دروغ است‌'),
(1539, 11, 66, 'و چون فرمان ما در رسید صالح و کسانی را که همراه او ایمان آورده بودند، به رحمت خویش از خواری و زاری آن روز نجات دادیم، چرا که پروردگارت نیرومند پیروزمند است‌'),
(1540, 11, 67, 'و ستم‌پیشگان [مشرک‌] را بانگ مرگبار فرو گرفت، و در خانه و کاشانه‌شان از پا در آمدند'),
(1541, 11, 68, 'گویی که در آن نبوده‌اند، بدانید که قوم ثمود به پروردگارشان کفر ورزیدند، هان نفرین بر ثمود باد'),
(1542, 11, 69, 'و فرشتگان ما برای ابراهیم بشارت آوردند، گفتند سلام [بر تو] گفت سلام [بر شما] و چیزی نگذشت که [ابراهیم برای آنان‌] گوساله‌ای بریان شده آورد'),
(1543, 11, 70, 'و چون دید که دستانشان به سوی آن دراز نمی‌شود، به آنان احساس بیگانگی کرد، و از آنان ترسی در دل یافت، گفتند مترس ما به سوی قوم لوط فرستاده شده‌ایم‌'),
(1544, 11, 71, 'و همسر او [ابراهیم‌] ایستاده بود و [از بشارت الهی‌] خندید، آنگاه او را به [بار گرفتن و زادن‌] اسحاق و پس از اسحاق به یعقوب، بشارت دادیم‌'),
(1545, 11, 72, 'گفت وای بر من آیا در حالی که خودم پیرزنم و این شوهرم پیرمرد [فرتوت‌] است، فرزند می‌زایم؟ این چیزی شگفت‌آور است‌'),
(1546, 11, 73, '[فرشتگان‌] گفتند آیا از امر الهی تعجب می‌کنی؟ ای خاندان [رسالت‌] رحمت الهی و برکات او بر شما باد، او ستوده بزرگوار است‌'),
(1547, 11, 74, 'و چون ترس از ابراهیم دور شد، و بشارت به او رسید، درباره قوم لوط [به قصد شفاعت‌] با ما به چون و چرا پرداخت‌'),
(1548, 11, 75, 'بی‌گمان ابراهیم بردبار دردمند توبه کار بود'),
(1549, 11, 76, '[گفتیم‌] ای ابراهیم از این [درخواست‌] درگذر، چرا که فرمان پروردگارت در رسیده است و عذابی بی‌بازگشت بر آنان فرود خواهد آمد'),
(1550, 11, 77, 'و چون فرشتگان ما به نزد لوط آمدند از ایشان نگران شد و [از کمک به آنان‌] دستش کوتاه شد، و گفت امروز روزی سخت و سنگین است‌'),
(1551, 11, 78, 'و قومش شتابان به سوی او هجوم آوردند و هم آنان بودند که مرتکب کارهای ناشایست می‌شدند [لوط] گفت ای قوم من اینان دختران من هستند، آنان برای شما پاکیزه‌تر و رواترند، پس از خدا پروا کنید و مرا در کار و بار مهمانانم خوار و رسوا مسازید، آیا در میان شما مردی فهیم نیست؟'),
(1552, 11, 79, 'گفتند خوب می‌دانی که ما با دخترانت کاری نداریم و تو خوب می‌دانی که ما چه می‌خواهیم‌'),
(1553, 11, 80, '[لوط] گفت کاش نیروی مقابله با شما را داشتم، یا از دست شما به پناهگاهی امن پناه می‌بردم‌'),
(1554, 11, 81, '[فرشتگان‌] گفتند ای لوط ما فرستادگان پروردگارت هستیم [بدان که‌] آنان هرگز به تو نخواهند رسید، پس پاسی از شب گذشته خانواده‌ات را همراه ببر و هیچ‌یک از شما باز پس ننگرد، مگر زنت که به او همان بلایی که به آنان می‌رسد، خواهد رسید، بدان که وعده‌گاه آنان صبح است، آیا صبح نزدیک نیست؟'),
(1555, 11, 82, 'چون فرمان ما در رسید آن را زیر و زبر کردیم و بر آن سنگپاره‌هایی از سنگ-گل پیاپی فرو باریدیم‌'),
(1556, 11, 83, 'که [هر یک‌] نزد پروردگارت نشان کرده بود و آن از ستمکاران [مشرک‌] دور نیست‌'),
(1557, 11, 84, 'و به سوی قوم مدین، برادرشان شعیب را [فرستادیم‌]، گفت ای قوم من، خداوند را که جز او خدایی ندارید، بپرستید، و در پیمانه و ترازو کم مگذارید، چرا که من شما را در رفاه و ثروت می‌بینم، و بر شما از عذاب روزی فراگیر می‌ترسم‌'),
(1558, 11, 85, 'ای قوم من پیمانه و ترازو را دادگرانه تمام و کمال دهید و به مردم اجناسشان را کم مدهید و در این سرزمین فتنه و فساد برپا نکنید'),
(1559, 11, 86, 'اگر مؤمن باشید، باز نهاده الهی برای شما بهتر است، و من نگهبان شما نیستم‌'),
(1560, 11, 87, 'گفتند ای شعیب آیا نمازت تو را بر آن می‌دارد که [ما را تکلیف کنی که‌] دست از آنچه پدرانمان می‌پرستند برداریم یا در اموالمان هر کار که خواهیم نکنیم، تو بی‌هیچ شک و شبهه بردبار و فهیمی‌'),
(1561, 11, 88, 'گفت ای قوم من بیندیشید اگر من از سوی پروردگارم حجت آشکاری داشته باشم و از سوی خویش به من روزی نیک بخشیده باشد [چه خواهید کرد] و من نمی‌خواهم با شما، در آنچه شما را از آن باز داشته‌ام مخالفت کنم [و خود مرتکب آن شوم‌] تا آنجا که بتوانم جز اصلاح نمی‌خواهم، و توفیق من جز به [اراده‌] خداوند نیست، که بر او توکل کرده‌ام و به او روی آورده‌ام‌'),
(1562, 11, 89, 'و ای قوم من شما را ستیزه‌جویی با من به آنجا نکشاند که همانند آنچه بر سر قوم نوح یا قوم هود یا قوم صالح آمد، بر سر شما هم بیاید، و قوم لوط از شما دور نیست‌'),
(1563, 11, 90, 'و از پروردگارتان آمرزش بخواهید و به درگاه او توبه کنید، که پروردگار من مهربان و دوستدار [بندگان صالح خویش‌] است‌'),
(1564, 11, 91, 'گفتند ای شعیب بسیاری از آنچه می‌گویی در نمی‌یابیم و تو را در میان خود ناتوان می‌بینیم، و اگر خاندانت نبود، بی‌شک سنگسارت می‌کردیم و تو نزد ما عزیز نیستی‌'),
(1565, 11, 92, 'گفت ای قوم من آیا خاندان من نزد شما عزیزتر از خداوند است، که او را پس پشت انداخته‌اید، بی‌گمان پروردگار من بر آنچه می‌کنید چیره است‌'),
(1566, 11, 93, 'و ای قوم من هر چه می‌توانید بکنید، من نیز می‌کنم، به زودی خواهید دانست که بر سر چه کسی عذابی می‌آید که خوارش سازد، و چه کسی دروغگوست، شما چشم به راه باشید که من نیز همراه با شما چشم به راهم‌'),
(1567, 11, 94, 'و چون فرمان ما در رسید، شعیب و کسانی را که همراه او ایمان آورده بودند به رحمت خویش نجات دادیم و بانگ مرگبار ستم‌پیشگان [مشرک‌] را فرو گرفت و در خانه و کاشانه‌شان از پا درآمدند'),
(1568, 11, 95, 'گویی در آنجا نبوده‌اند، هان نفرین بر قوم مدین، همچنانکه قوم ثمود هم نفرین زده بود'),
(1569, 11, 96, 'و به راستی موسی را همراه با معجزات خویش و برهانی آشکار به سوی فرعون و بزرگان قومش فرستادیم‌'),
(1570, 11, 97, 'آنگاه [آنان‌] از فرمان فرعون پیروی کردند و فرمان فرعون صواب نبود'),
(1571, 11, 98, 'او در روز قیامت پیشاپیش قومش است، آنگاه آنان را به آتش دوزخ درمی‌آورد، چه بد است درآیندی که به آن درآمده‌اند'),
(1572, 11, 99, 'و در این [جهان‌] و در روز قیامت لعنتی گریبانگیرشان شود، چه بد است عطیه‌ای که به ایشان داده‌اند'),
(1573, 11, 100, 'این از اخبار آن شهرهاست که بر تو می‌خوانیم بعضی از آنها [هنوز] برپا مانده‌اند و بعضی بر باد رفته‌اند'),
(1574, 11, 101, 'و ما بر ایشان ستم نکردیم، بلکه خود بر خویشتن ستم کردند، و چون فرمان پروردگارت در رسید خدایانشان که به جای خداوند می‌پرستیدندشان هیچ به کارشان نیامدند، و جز بر خسران ایشان نیفزودند'),
(1575, 11, 102, 'و چنین است بازخواست پروردگارت که اهالی شهرهایی را که ستمگر [و مشرک‌]اند فرو می‌گیرد، بی‌گمان بازخواست او سهمگین و سنگین است‌'),
(1576, 11, 103, 'در این برای کسی که از عذاب آخرت می‌ترسد، عبرتی هست، آن روزی است که در آن مردم گرد آیند و آن روز گرد هم آمدن است‌'),
(1577, 11, 104, 'و آن را جز تا مهلتی محدود به تاخیر نمی‌اندازیم‌'),
(1578, 11, 105, 'روزی بیاید که هیچ کس جز به اذن او سخن نگوید و از مردمان [بعضی‌] بدبخت و بعضی نیکبخت باشند'),
(1579, 11, 106, 'و اما کسانی که بدبخت شده‌اند در آتش دوزخ‌اند و در آن فریاد و عربده دارند'),
(1580, 11, 107, 'پیوسته تا زمانی که آسمانها و زمین وجود دارند، در آن هستند مگر آنچه پروردگارت بخواهد، که پروردگارت هر چه خواهد همان تواند کرد'),
(1581, 11, 108, 'و اما کسانی که نیکبخت شده‌اند پیوسته تا زمانی که آسمانها و زمین وجود دارند، در بهشت‌اند، مگر آنچه پروردگارت بخواهد، و این بخششی است پیوسته‌'),
(1582, 11, 109, 'پس از آنچه اینان می‌پرستند، سردرگم مباش، اینان جز به شیوه‌ای که پدرانشان [خدایان و بتان را] می‌پرستیده‌اند، نمی‌پرستند، و ما بازدهنده سهمشان به تمام و کمال و بدون کم و کاست به ایشان هستیم‌'),
(1583, 11, 110, 'و به راستی که به موسی کتاب آسمانی دادیم، آنگاه در آن اختلاف پیدا کردند و اگر وعده پروردگارت [به تاخیر حکم و عذاب‌] از پیش مقرر نگشته بود، بین آنان داوری می‌شد، و آنان از آن سخت در شک هستند'),
(1584, 11, 111, 'و بی‌شک پروردگارت حاصل اعمالشان را به تمامی به یکایک آنان می‌دهد، چرا که او به کار و کردارشان آگاه است‌'),
(1585, 11, 112, 'پس همچنانکه دستور یافته‌ای پایداری کن و نیز هر کس که با تو روی به سوی خداوند آورده است [چنین کند] و سرکشی مکنید، چرا که او به کار و کردارتان بیناست‌'),
(1586, 11, 113, 'و به ستم‌پیشگان [مشرک‌] گرایش نیابید که آتش دوزخ به شما خواهد رسید و در برابر خداوند سروری ندارید، و یاری نیز نخواهید یافت‌'),
(1587, 11, 114, 'و نماز را در آن سو و این سوی روز و در پاسهایی از شب برپا دار، همانا طاعات گناهان را می‌زدایند، این پندی برای پندگیران است‌'),
(1588, 11, 115, 'و شکیبایی پیشه کن که خداوند پاداش نیکوکاران را فرو نمی‌گذارد'),
(1589, 11, 116, 'از چه رو در میان اقوامی که پیش از شما بودند خردمندانی نبودند که از فساد در روی زمین نهی کنند، مگر گروهی اندک از کسانی که نجاتشان داده بودیم، و ستم‌پیشگان [مشرک‌] دل در ناز و نعمتی که یافته بودند، بستند، و گنهکار بودند'),
(1590, 11, 117, 'و پروردگار تو شهرها را در حالی که مردمانش صالح باشند، ستمگرانه نابود نمی‌کند'),
(1591, 11, 118, 'و اگر پروردگارت می‌خواست مردم را امت یگانه‌ای قرار می‌داد، ولی همچنان اختلاف می‌ورزند'),
(1592, 11, 119, 'مگر کسانی که خداوند [بر آنان‌] رحمت آورد، و برای همین آنان را آفریده است، وعده پروردگارت [چنین‌] سرانجام گرفته است که جهنم را همه از جن و انس آکنده خواهم ساخت‌'),
(1593, 11, 120, 'و یکایک اخبار پیامبران را بر تو می‌خوانیم، همانچه به آن دل تو را استوار می‌داریم، و در این [سوره‌] حق و موعظه و تذکری برای مؤمنان بر تو نازل شده است‌'),
(1594, 11, 121, 'و به نامؤمنان بگو هر چه می‌توانید بکنید که ما نیز می‌کنیم‌'),
(1595, 11, 122, 'و منتظر باشید که ما نیز منتظریم‌'),
(1596, 11, 123, 'و علم غیب آسمانها و زمین از آن خداوند است و همه کارها به سوی او باز گردانده می‌شود، پس او را بپرست و بر او توکل کن و پروردگارت از کار و کردارتان غافل نیست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الر الف لام راء اینها آیات کتاب روشنگر است‌'),
(1598, 12, 2, 'ما آن را به صورت قرآنی عربی [و روشن‌] نازل کردیم، باشد که اندیشه کنید'),
(1599, 12, 3, 'ما بهترین داستانسرایی را با وحی فرستادن همین قرآن بر تو می‌خوانیم و بی‌گمان پیش از آن از بی‌خبران بودی‌'),
(1600, 12, 4, 'چنین بود که یوسف به پدرش گفت پدر جان من در خواب یازده ستاره دیدم، و خورشید و ماه را، دیدم که به من سجده می‌کنند'),
(1601, 12, 5, '[یعقوب‌] گفت فرزندم خوابت را برای برادرانت بازگو مکن که در حقت بدسگالی می‌کنند، چرا که شیطان دشمن آشکار انسان است‌'),
(1602, 12, 6, 'و بدین‌سان پروردگارت تو را برمی‌گزیند و به تو تعبیر خواب می‌آموزد و نعمتش را بر تو و بر آل یعقوب به کمال می‌رساند، همچنانکه در گذشته بر پدرانت ابراهیم و اسحاق به کمال رسانده بود، بی‌گمان پروردگارت دانای فرزانه است‌'),
(1603, 12, 7, 'به راستی در داستان یوسف و برادرانش برای پرسندگان عبرتهاست‌'),
(1604, 12, 8, 'چنین بود که گفتند یوسف و برادرش [برادر ابوینی او] از ما نزد پدرمان محبوب‌ترند و ما برای خود جوانان برومندی هستیم، بی‌گمان پدرمان در گمراهی آشکار است‌'),
(1605, 12, 9, '[یکی گفت] یا یوسف را بکشید، یا در سرزمینی [گم و گور] بیندازید، تا توجه پدرتان فقط به شما پرداخته شود، و پس از آن مردمی درستکار شوید'),
(1606, 12, 10, 'گوینده‌ای از میان آنان گفت یوسف را نکشید، بلکه اگر می‌خواهید کاری بکنید او را در نهانگاه چاهی بیندازید که برخی از کاروانیان او را برگیرد'),
(1607, 12, 11, 'گفتند پدرجان، چرا در کار یوسف ما را امین نمی‌داری؟ حال آنکه ما خیرخواه او هستیم‌'),
(1608, 12, 12, 'او را فردا همراه ما بفرست تا بگردد و بازی کند و ما مراقب او هستیم‌'),
(1609, 12, 13, '[یعقوب‌] گفت اینکه شما ببریدش مرا اندوهگین می‌کند و می‌ترسم که شما از او غافل شوید و گرگ او را بخورد'),
(1610, 12, 14, 'گفتند در حالی که ما جوانانی برومند هستیم، اگر گرگ او را بخورد، [ضایع و] زیانکاریم‌'),
(1611, 12, 15, 'چون او را [همراه‌] بردند و همداستان شدند که او را در نهانگاه چاه بگذارند، به او وحی [/الهام‌] کردیم که [سرانجام‌] ایشان را در حالی که هیچ آگاه نیستند، از [چون و چند] این کارشان آگاه خواهی ساخت‌'),
(1612, 12, 16, 'و شبانگاه گریه کنان نزد پدرشان آمدند'),
(1613, 12, 17, '[و] گفتند پدرجان، ما به مسابقه رفته بودیم و یوسف را نزدیک بار و بنه خودمان گذاشته بودیم، که گرگ او را خورد، و ما اگر هم راستگو باشیم، تو سخن ما را باور نخواهی کرد'),
(1614, 12, 18, 'و بر پیراهنش خونی دروغین آوردند [یعقوب‌] گفت ولی نفس اماره‌تان به دست شما کار داد پس [چاره من‌] صبری نیکوست، و خداوند در آنچه می‌گویید مددکار [من‌] است‌'),
(1615, 12, 19, 'و کاروانی [پیش‌] آمد، و آب‌آورشان را فرستادند، و او دلوش را [در چاه‌] انداخت، [چون یوسف را بالا کشید] گفت مژده باد، چه جوانی! و او را پنهانی برای خود برداشتند که دستمایه کنند، و خداوند به آنچه می‌کردند، آگاه بود'),
(1616, 12, 20, 'او را به ثمن بخس فروختند، به چند درهم اندکشمار، و به [کار و بار] او بی‌علاقه بودند'),
(1617, 12, 21, 'و کسی که اهل مصر بود و او را خریده بود، به همسرش گفت قدر او را بدان، چه بسا به ما سود رساند، یا به فرزندی بگیریمش، و بدین‌سان یوسف را در آن سرزمین تمکن بخشیدیم، تا سرانجام به او تعبیر خواب بیاموزیم، و خداوند سررشته کار خویش را در دست دارد، ولی بیشتر مردم نمی‌دانند'),
(1618, 12, 22, 'و چون به عنفوان جوانی رسید، به او حکمت [نبوت‌] و علم بخشیدیم، و بدین‌سان نیکوکاران را جزا می‌دهیم‌'),
(1619, 12, 23, 'و زنی که او [یوسف‌] در خانه‌اش بود، از او کام خواست، و [یک روز، همه‌] درها را بست و [به یوسف‌] گفت بیا پیش من [یوسف‌] گفت پناه بر خدا، او [شوهرت‌] سرور من است و به من منزلتی نیکو داده است، آری ستمکاران رستگار نمی‌شوند'),
(1620, 12, 24, 'و آن زن آهنگ او [یوسف‌] کرد و او نیز اگر برهان پروردگارش را ندیده بود، آهنگ آن زن می‌کرد، اینگونه [کردیم‌] تا نابکاری و ناشایستی را از او بگردانیم، چرا که از بندگان اخلاص یافته ماست‌'),
(1621, 12, 25, 'و [سپس‌] هر دو به سوی در شتافتند و آن زن پیراهن او را از پشت درید، و [ناگهان‌] شوهر او را نزدیک در یافتند [زلیخا، مکارانه‌] گفت جزای کسی که به زن تو نظر بد داشته باشد چیست غیر از اینکه زندانی شود، یا عذابی دردناک [بچشد]؟'),
(1622, 12, 26, '[یوسف‌] گفت او بود که از من کام خواست، و شاهدی از کسان زن شهادت داد که اگر پیراهن او از جلو دریده شده، زن راست می‌گوید و مرد دروغگوست‌'),
(1623, 12, 27, 'و اگر پیراهنش از پشت دریده شده، زن دروغ می‌گوید و مرد راستگوست‌'),
(1624, 12, 28, 'و چون دید که پیراهنش از پشت دریده شده [حقیقت را دریافت و] گفت این از مکر شما [زنان‌] است که مکرتان شگرف است‌'),
(1625, 12, 29, 'اما یوسف تو از این ماجرا درگذر، و [اما] تو ای زن برای گناهت استغفار کن، چرا که از خطاکاران بوده‌ای‌'),
(1626, 12, 30, 'و زنانی در شهر [بودند که‌] گفتند همسر عزیز [مصر] از غلامش کام خواسته است و پاک، دل در گرو محبت او داده است، ما او را در گمراهی آشکار می‌بینیم‌'),
(1627, 12, 31, 'و چون بدگویی ایشان را شنید [کسی را برای دعوت‌] به سوی ایشان فرستاد و برای آنان مجلسی آماده ساخت و به هر یک از آنان کاردی داد و [به یوسف‌] گفت بر آنان ظاهر شو، آنگاه که دیدندش بس بزرگش یافتند و [از بی‌حواسی‌] دستانشان را [به جای ترنج‌] بریدند و گفتند پناه بر خدا، این آدمیزاده نیست، این جز فرشته‌ای گرامی نیست‌'),
(1628, 12, 32, '[زلیخا] گفت این همان کسی است که شما به خاطر او مرا سرزنش می‌کردید، و من از او کام خواسته بودم، ولی او خویشتنداری‌ورزید، و اگر آنچه به او دستور می‌دهم نکند، به زندان خواهد افتاد و خوار و زبون خواهد شد'),
(1629, 12, 33, '[یوسف‌] گفت پروردگارا زندان برای من خوشتر است از آنچه مرا به آن می‌خوانند، و اگر مکر آنان را از من باز نداری، به آنان می‌گرایم و از نادانان می‌گردم‌'),
(1630, 12, 34, 'آنگاه پروردگارش [درخواست‌] او را اجابت کرد و مکر آنان را از او باز داشت، چرا که او شنوای داناست‌'),
(1631, 12, 35, 'سپس، بعد از آنکه نشانه‌ها را دیدند، بهتر آن دیدند که او را تا مدت زمانی زندانی کنند'),
(1632, 12, 36, 'و همراه او دو جوان وارد زندان شدند، یکی از آن دو [به یوسف‌] گفت من به خواب دیده‌ام که [انگور برای‌] شراب می‌فشارم و دیگری گفت من به خواب دیده‌ام که بالای سرم نان می‌برم که پرندگان از آن می‌خورند، ما را از تعبیر آن آگاه کن، که تو را از نیکوکاران می‌یابیم‌'),
(1633, 12, 37, '[یوسف‌] گفت خوراکی برایتان نمی‌آید که بخوریدش مگر آنکه پیش از آمدن آن، شما را از تعبیر آن [خواب‌] آگاه می‌کنم، این از چیزهایی است که پروردگارم به من آموخته است، که من آیین مردمی را که به خداوند ایمان ندارند، و هم ایشان آخرت را منکرند، رها کرده‌ام‌'),
(1634, 12, 38, 'و از آیین پدرانم ابراهیم و اسحاق و یعقوب پیروی کرده‌ام سزاوار نیست که ما هیچ‌گونه شریکی برای خدا قائل شویم، این از فضل الهی در حق ما و در حق مردم است، ولی بیشترینه مردم سپاس نمی‌گزارند'),
(1635, 12, 39, '[سپس گفت‌] ای دو هم زندان من آیا خدایان گوناگون بهتر است یا خداوند یگانه قهار؟'),
(1636, 12, 40, 'شما به جای او [خدا]، جز نامهایی که خودتان و پدرانتان گذارده‌اید، نمی‌پرستید که خداوند بر آن هیچ‌گونه برهانی نفرستاده است، حکم نیست مگر خداوند را، [که‌] فرمان داده است که جز او را مپرستید، این دین استوار است، ولی بیشترینه مردم نمی‌دانند'),
(1637, 12, 41, 'ای دو هم زندان من، اما یکی از شما دو تن به سرور خویش [دوباره‌] شراب می‌نوشاند، و اما دیگری بر دار می‌شود و پرندگان از سر او می‌خورند، چیزی که در باب آن از من نظر خواستید، سرانجام یافته است‌'),
(1638, 12, 42, 'و به یکی از آن دو که گمان می‌کرد رهایی یافتنی است، گفت مرا نزد سرورت یاد کن، آنگاه شیطان یاد سرورش را از خاطر او برد، لذا [یوسف‌] چند سال در زندان ماند'),
(1639, 12, 43, 'و پادشاه [مصر] گفت من در خواب هفت گاو ماده فربه دیده‌ام که هفت [گاو] لاغر آنها را می‌خورند، و نیز هفت خوشه سرسبز و هفت دیگر که خشک بوده‌اند [به خواب دیده‌ام‌] ای بزرگان اگر خواب تعبیر می‌کنید، درباره خواب من نظر دهید'),
(1640, 12, 44, 'گفتند [اینها] خوابهای پریشان است و ما دانای تعبیر خوابها[ی پریشان‌] نیستیم‌'),
(1641, 12, 45, 'و آن کسی از آن دو تن که رهایی یافته بود، و پس از مدتی [درخواست یوسف را] به یاد آورده بود، گفت من شما را از تعبیر آن آگاه می‌سازم، پس مرا [به زندان یوسف‌] بفرستید'),
(1642, 12, 46, 'ای یوسف، ای صدیق، درباره [خواب‌] هفت گاو ماده فربه که هفت [گاو] لاغر آنها را می‌خورند و هفت خوشه سرسبز و هفت دیگر که خشک بوده‌اند، به ما نظر بده، تا به سوی مردم‌[ی که منتظرند] برگردم، باشد که باخبر شوند'),
(1643, 12, 47, '[یوسف‌] گفت هفت سال پیاپی مثل همیشه کشت و زرع کنید، و آنچه درو می‌کنید، با خوشه‌اش کنار بگذارید، مگر اندکی که از آن می‌خورید'),
(1644, 12, 48, 'سپس بعد از هفت سال سختی [و قحطی‌] پیش می‌آید که [مردم‌] آنچه برایشان از پیش نهاده‌اید، می‌خورند، مگر اندکی که آن را ذخیره می‌کنید'),
(1645, 12, 49, 'سپس بعد از این سالی پیش می‌آید که [با باران‌] داد مردم داده خواهد شد و مردم [از قحط و غلا] نجات می‌یابند'),
(1646, 12, 50, 'پادشاه گفت او [یوسف‌] را نزد من آورید، آنگاه که فرستاده نزد او آمد [یوسف‌] گفت به نزد سرورت بازگرد و از او بپرس که کار و بار آن زنان که دستانشان را بریدند، چه بود؟ که پروردگار من از مکر آنان آگاه است‌'),
(1647, 12, 51, '[پادشاه به زنان‌] گفت کار و بار شما چه بود که از یوسف کام خواستید؟ گفتند پناه بر خدا ما هیچ بد و بیراهی از او سراغ نداریم، [آنگاه زلیخا] همسر عزیز گفت اینک حق آشکار شد، من [بودم که‌] از او کام خواستم و او از راستگویان است‌'),
(1648, 12, 52, '[یوسف‌] گفت چنین بود تا او [عزیز] بداند که من در نهان به او خیانت نکرده‌ام، و اینکه خداوند نیرنگ خیانتکاران را به جایی نمی‌رساند'),
(1649, 12, 53, 'و من خود را مبرا نمی‌شمارم، چرا که نفس [آدمی‌] بدفرماست، مگر آنکه پروردگارم رحمت آورد، که پروردگار من آمرزگار مهربان است‌'),
(1650, 12, 54, 'و پادشاه گفت او [یوسف‌] را به نزد من آورید که ندیم ویژه خود گردانمش، و چون با او گفت و گو کرد [به او] گفت تو امروز نزد ما صاحب جاه و امین هستی‌'),
(1651, 12, 55, '[یوسف‌] گفت مرا بر خزاین این سرزمین بگمار، که من نگهبانی کاردانم‌'),
(1652, 12, 56, 'و بدین‌سان یوسف را در آن سرزمین تمکن بخشیدیم که در آن هر جا که خواهد قرار گیرد، هر که را خواهیم رحمت خویش بر او ارزانی داریم، و پاداش نیکوکاران را فرونگذاریم‌'),
(1653, 12, 57, 'و پاداش اخروی برای کسانی که ایمان آورده و تقوا ورزیده باشند، بهتر است‌'),
(1654, 12, 58, 'و برادران یوسف آمدند و بر او وارد شدند آنگاه او ایشان را شناخت ولی ایشان او را نشناختند'),
(1655, 12, 59, 'و چون زاد و برگ ایشان را آماده ساختند [یوسف‌] گفت [آن‌] برادر پدری‌تان را هم نزد من بیاورید، مگر نمی‌بینید که من پیمانه را تمام می‌دهم و بهترین میزبانان هستم‌'),
(1656, 12, 60, 'و اگر او را به نزد من نیاورید، نزد من پیمانه‌ای ندارید و نزدیک من نیایید'),
(1657, 12, 61, 'گفتند او را [به هر تدبیر و ترفند] از پدرش خواهیم گرفت و ما چنین کاری خواهیم کرد'),
(1658, 12, 62, '[یوسف‌] به غلامانش گفت سرمایه‌شان را در خرجینهایشان بگذارید تا چون به نزد خانواده‌شان بازگشتند آن را باز شناسند، باشد که بازگردند'),
(1659, 12, 63, 'و چون به نزد پدرشان بازگشتند گفتند پدرجان از ما پیمانه را دریغ داشته‌اند، پس برادرمان [بنیامین‌] را همراه ما بفرست تا بار و پیمانه گیریم و ما مراقب او هستیم‌'),
(1660, 12, 64, '[یعقوب‌] گفت شما را در حق او امین ندارم، مگر همانطور که پیشترها در حق برادرش امین داشته بودم، اما خداوند بهترین نگهبان است، و همو مهربان‌ترین مهربانان است‌'),
(1661, 12, 65, 'و چون بار و بنه‌شان را باز کردند چنین یافتند که سرمایه‌شان به ایشان بازگردانده شده است، گفتند پدرجان دیگر چه می‌خواهیم؟ این سرمایه ماست که به ما بازگردانده شده است [بگذارید بار دیگر برویم‌] و برای خانواده خود آذوقه بیاوریم و مراقب برادرمان هم هستیم و یک بار شتر هم اضافه خواهیم گرفت، چه [تاکنون‌] به ما بار و پیمانه اندکی داده‌اند'),
(1662, 12, 66, 'گفت هرگز او را همراه شما نمی‌فرستم مگر آنکه عهدی به نام خدا به من بدهید که او را به نزد من باز آورید، مگر آنکه از هر سو گرفتار آیید، و چون عهدشان را دادند، [یعقوب‌] گفت خداوند بر آنچه می‌گوییم [و قول و قرار ما] [ناظر و] کارساز است‌'),
(1663, 12, 67, 'و گفت فرزندان من، از یک دروازه وارد نشوید و از دروازه‌های گوناگون وارد شوید، و البته شما را از قضای [ناگوار] الهی باز نتوانم داشت، حکم جز از آن خداوند نیست، بر او توکل کردم و اهل توکل باید بر او توکل کنند'),
(1664, 12, 68, 'و چون به همان گونه که پدرشان به ایشان دستور داده بود، وارد شدند، [این شیوه ورود] ایشان را از قضای الهی باز نداشت، ولی [هر چه بود] نیاز روحی یعقوب بود که بدین‌سان برآورده کرد، و او با آنچه به او آموخته بودیم، دانشور بود، ولی بیشترینه مردم نمی‌دانند'),
(1665, 12, 69, 'و چون بر یوسف وارد شدند، برادر [ابوینی‌]اش را در کنار گرفت [و در خلوت به او] گفت من [همان‌] برادرت هستم، از آنچه کرده‌اند غمگین مباش‌'),
(1666, 12, 70, 'و چون ساز و برگ آنان را آماده ساختند [به فرمان یوسف‌] جام [پادشاه‌] را در خرجین برادرش [بنیامین‌] گذاشت سپس منادی ندا در داد که ای کاروانیان شما دزدید'),
(1667, 12, 71, 'گفتند -و رو به ایشان آوردندکه مگر چه گم کرده‌اید؟'),
(1668, 12, 72, 'گفتند جام پادشاه را گم کرده‌ایم، و هرکس آن را بیاورد، بار شتری [آذوقه جایزه‌] دارد و من این [وعده‌] را ضامنم‌'),
(1669, 12, 73, 'گفتند به خدا خودتان می‌دانید که ما نیامده‌ایم که در این سرزمین فتنه و فساد کنیم و ما دزد نیستیم‌'),
(1670, 12, 74, 'گفتند جزای آن [سرقت‌] اگر شما دروغگو باشید، چه باشد؟'),
(1671, 12, 75, '[اینان در پاسخ‌] گفتند هرکس که [جام‌] در خرجین او پیدا شود، خودش [برده شود و اسارتش‌] جزای آن باشد، که ما به این شیوه ستمکاران [سارق‌] را کیفر می‌دهیم‌'),
(1672, 12, 76, '[پذیرفتند] و آغاز به جستجوی باردانهای ایشان پیش از باردان برادر [ابوینی‌]اش کرد، [سرانجام‌] آن را از باردان برادرش بیرون آورد، بدین‌سان به یوسف تدبیر و ترفند آموختیم [زیرا] نمی‌توانست برادرش را طبق رسم و آیین پادشاه، بازداشت کند، مگر آنکه خداوند بخواهد، که درجات هر کس را که بخواهیم بلند می‌گردانیم و برتر از هر داننده‌ای داناتری هست‌'),
(1673, 12, 77, 'گفتند اگر [بنیامین‌] دزدی کرده است [عجب نیست، چرا که‌] پیشترها برادر [ابوینی‌]اش هم دزدی کرده بود، اما یوسف این [شماتت را فرو خورد و] در دل نگه داشت و به روی آنان نیاورد، [اما در دل‌] گفت خودتان بدمنصب‌ترید، و خداوند به آنچه می‌گویید داناتر است‌'),
(1674, 12, 78, 'گفتند ای عزیز او پدری پیر فرتوت دارد، یکی از ما را به جای او نگهدار، که ما تو را از نیکوکاران می‌بینیم‌'),
(1675, 12, 79, '[یوسف‌] گفت پناه بر خدا که جز کسی را که کالایمان [جام‌] را در نزد او یافته‌ایم، بازداشت کنیم، که در این صورت ستمکار خواهیم بود'),
(1676, 12, 80, 'آنگاه چون از او نومید شدند، نجوا کنان بین خود خلوت [و مشورت‌] کردند، بزرگترشان گفت مگر نمی‌دانید که پدرتان از شما با نام خدا عهدی گرفته است، و پیشترها هم چه تقصیرها در حق یوسف کرده‌اید، من از این سرزمین قدم بیرون نمی‌گذارم، مگر آنکه پدرم اجازه دهد، یا خداوند در حق من حکمی فرماید و او بهترین داوران است‌'),
(1677, 12, 81, 'به سوی پدرتان باز گردید و بگویید پدرجان، پسرت [بنیامین‌] دزدی کرد و ما جز به چیزی که می‌دانستیم شهادت ندادیم، و ما در برابر [رویدادهای‌] نهانی نگهبان نبودیم‌'),
(1678, 12, 82, 'از مردم شهری که ما در آن بودیم [مصر] و از اهل کاروانی که ما با آن روی به اینجا آوردیم، بپرس، و ما راستگوییم‌'),
(1679, 12, 83, '[یعقوب‌] گفت باز نفس اماره‌تان به دست شما کار داد، پس [چاره من‌] صبری نیکوست، چه بسا خداوند همه آنان را برای من باز آورد، که او دانای فرزانه است‌'),
(1680, 12, 84, 'و از آنان روی برگرداند و [با اشک و اندوه‌] گفت بر [فراق‌] یوسف اسف می‌خورم، و چشمانش از [اشک و] اندوه سپید [و نابینا] شد و اندوه خود را فرو خورد'),
(1681, 12, 85, 'گفتند به خدا پیوسته یوسف را یاد می‌کنی تا زار و نزار یا نابود شوی‌'),
(1682, 12, 86, 'گفت درد و اندوهم را فقط با خداوند در میان می‌گذارم، و از [عنایت‌] خداوند چیزی می‌دانم که شما نمی‌دانید'),
(1683, 12, 87, 'ای فرزندان بروید و در پی یوسف و برادرش بگردید، و از رحمت الهی نومید مباشید، چرا که جز خدانشناسان کسی از رحمت الهی نومید نمی‌گردد'),
(1684, 12, 88, 'و چون بر او [یوسف‌] وارد شدند، گفتند ای عزیز، به ما و خانواده ما رنج [بسیار] رسیده است و سرمایه‌ای اندک آورده‌ایم، پس به ما پیمانه تمام و کمال بده و بر ما [افزونتر هم‌] ببخش که خداوند بخشندگان را دوست دارد'),
(1685, 12, 89, '[یوسف‌] گفت آیا دانستید که در حق یوسف و برادرش، وقتی که جاهل [و جوان‌] بودید، چه کردید؟'),
(1686, 12, 90, 'گفتند آیا تو خود یوسفی؟ گفت آری من یوسفم و این برادر [ابوینی‌] من است، خداوند بر من منت نهاد [و نعمت داد]، چرا که هرکس پروا و شکیبایی پیشه کند، خداوند پاداش نیکوکاران را فرو نمی‌گذارد'),
(1687, 12, 91, 'گفتند به خدا، خداوند تو را بر ما برتری داد، و ما خطاکار بودیم‌'),
(1688, 12, 92, '[یوسف‌] گفت امروز سرزنشی بر شما روا نیست، خداوند شما را می‌آمرزد و او مهربان‌ترین مهربانان است‌'),
(1689, 12, 93, '[حال‌] این پیراهن مرا ببرید و آن را بر روی پدرم بیفکنید تا [به خواست خدا] بینا شود، و همه خانواده‌هایتان را به نزد من بیاورید'),
(1690, 12, 94, 'و چون کاروان رهسپار شد، پدرشان گفت اگر مرا به خرفتی متهم ندارید، من بوی یوسف را می‌شنوم‌'),
(1691, 12, 95, 'گفتند به خدا تو در همان خبط و خطای دیرینت هستی‌'),
(1692, 12, 96, 'و چون [پیک‌] مژده‌آور آمد، آن [پیراهن‌] را بر روی او [یعقوب‌] انداخت و بینا گشت گفت آیا به شما نگفته بودم که من از [عنایت‌] خداوند چیزی می‌دانم که شما نمی‌دانید'),
(1693, 12, 97, 'گفتند پدرجان برای گناهان ما آمرزش بخواه که ما گناهکار بوده‌ایم‌'),
(1694, 12, 98, 'گفت به زودی برای شما از پروردگارم آمرزش خواهم خواست، که او آمرزگار مهربان است‌'),
(1695, 12, 99, 'و چون [همگان‌] بر یوسف وارد شدند، پدر و مادرش را در کنار گرفت و گفت به خواست خداوند با امن و امان وارد مصر شوید [و همین جا بمانید]'),
(1696, 12, 100, 'و پدر و مادرش را بر تخت برنشاند، و در پیشگاه او به سجده درافتادند، و گفت پدر جان این تعبیر خواب پیشین من است که پروردگارم آن را راست و درست گرداند، و با بیرون در آوردن من از زندان در حق من نیکی کرد و شما را پس از آنکه شیطان میانه من و برادرانم را بر هم زد، از بیابان [کنعان به اینجا] باز آورد، حقا که پروردگار من در آنچه بخواهد باریک بین است و همو دانای فرزانه است‌'),
(1697, 12, 101, 'پروردگارا به من بهره‌ای از فرمانروایی بخشیدی و به من بهره‌ای از تعبیر خواب آموختی، ای پدید آورنده آسمانها و زمین، تو در دنیا و آخرت سرور منی، مرا مسلمان بمیران و به نیکان باز رسان‌'),
(1698, 12, 102, 'این از اخبار غیبی است که بر تو وحی می‌کنیم، و تو [ای پیامبر] آنگاه که کارشان را هماهنگ و عزمشان را جزم کردند و نیرنگ پیشه کردند، نزد آنان نبودی‌'),
(1699, 12, 103, 'و بیشترینه مردم، ولو تو سخت بکوشی و بخواهی، مؤمن نمی‌شوند'),
(1700, 12, 104, 'و تو از ایشان برای آن [رسالت‌] مزدی نمی‌طلبی، آن نیست مگر پندآموزی برای جهانیان‌'),
(1701, 12, 105, 'و چه بسیار نشانه [و مایه عبرت‌] در آسمانها و زمین هست که بر آن می‌گذرند و هم آنان از آنها رویگردانند'),
(1702, 12, 106, 'و بیشترینه ایشان [ظاهرا] به خداوند ایمان نمی‌آورند مگر آنکه [باطنا، به نوعی‌] مشرکند'),
(1703, 12, 107, 'آیا ایمن‌اند از اینکه عذاب فراگیری از سوی خداوند بر آنان فرود آید، یا قیامت ناگهان برایشان فرارسد و آنان ناآگاه باشند'),
(1704, 12, 108, 'بگو این راه و رسم من است که به سوی خداوند دعوت می‌کنم، که من و هرکس که پیرو من باشد، برخوردار از بصیرتیم، و منزه است خداوند، و من از مشرکان [نبوده و]نیستم‌'),
(1705, 12, 109, 'و ما پیش از تو جز مردانی از اهل [همین‌] شهرها [به رسالت‌] نفرستاده‌ایم که به آنان وحی می‌کردیم، آیا در زمین سیر و سفر نمی‌کنند که بنگرند سرانجام پیشینیان آنان چه بوده است، و سرای آخرت برای پرواپیشگان بهتر است، آیا نمی‌اندیشید؟'),
(1706, 12, 110, 'تا آنجا که چون پیامبران نومید شدند و [پیروان‌] پنداشتند که به دروغ وعده داده شده‌اند، آنگاه بود که نصرت ما به آنان در رسید و هرکس که خواسته بودیم نجات یافت، و عذاب ما از قوم گناهکار برنمی‌گردد'),
(1707, 12, 111, 'به راستی که در بیان داستان ایشان مایه عبرتی برای خردمندان هست [و این قرآن‌] سخنی برساخته نیست، بلکه همخوان با کتابی است که پیشاپیش آن است و روشنگر همه چیز است و رهنمود و رحمتی برای اهل ایمان است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'المر الف لام میم راء این آیات کتاب آسمانی است، و آنچه از پروردگارت بر تو نازل شده است، راست و درست است، ولی بیشترینه مردم نمی‌گروند'),
(1709, 13, 2, 'خداوند کسی است که آسمانها را بدون ستونهایی که آنها را ببینید برافراشت، سپس بر عرش استیلاء یافت، و خورشید و ماه را رام کرد که هر یک تا زمانی معین، سیر می‌کند [همو] کار [و بار جهان‌] را تدبیر می‌کند و آیات [خویش‌] را روشن بیان می‌دارد، باشد که شما به لقای پروردگارتان یقین پیدا کنید'),
(1710, 13, 3, 'و او کسی است که زمین را گسترد و در آن کوهها و رودهایی پدید آورد، و در آن از هربار و بری زوجی دوگانه آفرید، شب را به روز [و روز را به شب‌] می‌پوشاند، بی‌گمان در این برای اندیشه‌وران مایه‌های عبرت است‌'),
(1711, 13, 4, 'و در زمین کرتهایی است نزدیک به هم و باغهایی از [درختان‌] انگور و کشتها [ی گوناگون‌] و خرما، همانند و ناهمانند، که همه به یک آب آبیاری می‌شوند و میوه‌های بعضی را از بعضی دیگر برتر ساخته‌ایم، بی‌گمان در این برای خردورزان مایه‌های عبرت است‌'),
(1712, 13, 5, 'و اگر عجب کنی، سخن ایشان عجیب‌[تر] است [که می‌گویند] آیا آنگاه که خاک شدیم، آیا به آفرینش تازه‌ای در می‌آییم؟ اینان کسانی‌اند که به پروردگارشان کفرورزیده‌اند و هم اینانند که [در قیامت و در دوزخ‌] در گردنهایشان غل و بندهاست، و اینان دوزخی‌اند و جاودانه در آنند'),
(1713, 13, 6, 'و از تو به [شدت و] شتاب بدی [عذاب‌] را پیش از نیکی [آمرزش‌] می‌طلبند، و حال آنکه پیش از آنها [رسم‌] عقوبتها [ی الهی‌] برقرار بوده است، و بی‌گمان پروردگارت بر مردمان با وجود ستمشان آمرزنده است، و بی‌گمان پروردگارت سخت کیفر است‌'),
(1714, 13, 7, 'و کافران گویند چرا بر او معجزه‌ای از سوی پروردگارش نازل نمی‌گردد؟ تو فقط هشداردهنده‌ای و هر قومی رهنمایی دارد'),
(1715, 13, 8, 'خداوند می‌داند که هر مادینه‌ای چه باری دارد و اینکه رحمها [از مدت حمل یا از جنین‌] چه می‌کاهند و چه می‌افزایند، هر چیزی نزد او اندازه‌ای معین دارد'),
(1716, 13, 9, '[اوست که‌] دانای پنهان و پیدا، و بزرگ بلندمرتبه است‌'),
(1717, 13, 10, 'یکسان است که از شما کسی سخنش را پنهان بدارد یا آشکارش کند، و کسی که در [پرده‌] شب پنهانکار یا در [روشنای‌] روز پیدارو باشد'),
(1718, 13, 11, 'او [آدمی‌] را فرشتگانی پیایند، در پیش و پشت او هستند که او را به امر الهی نگهبانی می‌کنند، بی‌گمان خداوند آنچه قومی دارند دگرگون نکند مگر آنکه آنچه در دلهایشان دارند، دگرگون کنند و چون خداوند برای قومی [کیفر] بدی بخواهد، برگردانی ندارد، و در برابر او سروری ندارند'),
(1719, 13, 12, 'او کسی است که برق را که مایه بیم [مسافران‌] و امید [کشاورزان‌] است به شما می‌نمایاند، و ابرهای گرانبار پدید می‌آورد'),
(1720, 13, 13, 'و رعد به سپاس او، و فرشتگان از خوف و خشیت او تسبیح می‌گویند، و [اوست که‌] صاعقه‌ها را می‌فرستد، که به هرکس که او بخواهد برمی‌خورد، و آنان درباره خداوند مجادله می‌کنند، و او سختگیر است‌'),
(1721, 13, 14, 'فراخواندن راستین او راست، و کسانی که [مشرکان‌] به جای او می‌خوانند [و می‌پرستند] به آنان هیچ‌گونه پاسخی نمی‌دهند، مگر همانند کسی که گشاینده دستان خویش به سوی آب است، تا آن را به دهانش برساند و رساننده آن نیست، و دعای کافران جز بیراه نیست‌'),
(1722, 13, 15, 'و هر که در آسمانها و زمین است و سایه‌هایشان خواه و ناخواه، در بامدادان و شامگاهان، برای خدا سجده می‌کنند'),
(1723, 13, 16, 'بگو [و بپرس‌] پروردگار آسمانها و زمین کیست؟ بگو خداوند است بگو پس آیا در برابر او سرورانی برای خود می‌گیرید و می‌پرستید که برای خویشتن هم اختیار سود و زیانی ندارند؟ بگو آیا نابینا و بینا برابر است، یا آیا تاریکیها و روشنایی برابر است، یا شریکانی برای خداوند قائلند که همانند آفرینش او آفریده‌اند، و آفرینش [ها] بر آنان مشتبه شده است؟ بگو خداوند آفریدگار هر چیز است و او یگانه قهار است‌'),
(1724, 13, 17, '[همو] از آسمان آبی فرو بارید، آنگاه رودها هر یک به اندازه خویش جاری شد، و سیلاب کف فراوانی با خود آورد، و از آنچه بر آتش می‌افروزند [و می‌گدازند] که زیور یا اثاثیه به دست آورند نیز کفی همانند آن [آب‌] هست، بدین‌سان خداوند حق و باطل را [این چنین‌] مثل می‌زند، و اما کف بر باد می‌رود، و اما آنچه به مردمان سود می‌رساند، بر روی زمین باقی می‌ماند، خداوند بدین گونه مثل می‌زند'),
(1725, 13, 18, 'برای کسانی که [ندای‌] پروردگارشان را استجابت نکرده‌اند، نیکی [بهشت‌] است، و کسانی که [ندای‌] او را استجابت نکرده‌اند، اگر همه آنچه در زمین است و همانند آن، از آن ایشان باشد، آن را بدهند و بلا گردان کنند، اینانند که حسابشان سخت و سنگین است و سرا و سرانجامشان جهنم است و بد آرامگاهی است‌'),
(1726, 13, 19, 'آیا کسی که می‌داند آنچه از سوی پروردگارت بر تو نازل شده است، حق است، همانند کسی است که کوردل است؟ فقط خردمندان پند می‌گیرند'),
(1727, 13, 20, '[همان‌] کسانی که به عهد الهی وفا می‌کنند و پیمان [او] را نمی‌شکنند'),
(1728, 13, 21, 'و کسانی که هر چه خداوند به پیوند کردن آن فرمان داده است، می‌پیوندند و از پروردگارشان می‌ترسند، و از حساب سخت و سنگین می‌هراسند'),
(1729, 13, 22, 'و کسانی که برای نیل به خشنودی پروردگارشان شکیبایی پیشه کرده‌اند و نماز را برپا داشته‌اند و از هر آنچه روزیشان کرده‌ایم، پنهان و آشکارا می‌بخشند و بدی را با نیکی دفع می‌کنند، اینانند که نیک سرانجامی دارند'),
(1730, 13, 23, '[از] بهشتهای عدن [برخوردارند] که هم خودشان و هم کسانی از پدران [و مادران‌] و همسران و زاد و رودشان که نیکوکارند، وارد آن می‌شوند، و فرشتگان از هر دری بر آنان وارد می‌شوند'),
(1731, 13, 24, '[و می‌گویند] سلام بر شما به خاطر صبری که ورزیدید، چه نیکوست این نیک سرانجامی‌'),
(1732, 13, 25, 'و کسانی که عهد الهی را پس از بستنش می‌شکنند، و هر چه خداوند به پیوند کردن آن فرمان داده است، می‌گسلند، و در زمین فتنه و فساد می‌کنند، اینانند که لعنت و بدفرجامی دارند'),
(1733, 13, 26, 'خداوند روزی [خویش‌] را بر هرکس که بخواهد گشایش می‌دهد یا تنگ و فروبسته می‌دارد، و [بعضی‌] به زندگانی دنیا سرمست شده‌اند، و زندگانی دنیا در جنب آخرت، جز بهره‌ای ناچیز نیست‌'),
(1734, 13, 27, 'و کافران گویند چرا از سوی پروردگارش معجزه‌ای بر او نازل نمی‌شود؟ بگو خداوند هر کس را که بخواهد بیراه وامی‌گذارد، و هر کس را که رو به او آورده باشد، به سوی خویش به راه می‌برد'),
(1735, 13, 28, '[همان‌] کسانی که ایمان آورده‌اند و دلهایشان به یاد خدا آرام می‌گیرد، بدانید که با یاد خداست که دلها آرام می‌گیرد'),
(1736, 13, 29, 'کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، خوشا بر ایشان و نیک سرانجامی دارند'),
(1737, 13, 30, 'بدین‌سان تو را به میان امتی فرستادیم که پیش از آنها امتهایی آمده [و رفته‌] بودند، تا آنچه بر تو وحی کرده‌ایم، بر آنان بخوانی، و حال آنکه آنان به خدای رحمان کفر و انکار می‌ورزند، بگو او پروردگار من است [و] خدایی جز او نیست، بر او توکل کرده‌ام و بازگشت من به سوی اوست‌'),
(1738, 13, 31, 'و اگر قرآنی بود که کوهها از [هیبت‌] آن به حرکت در می‌آمد و زمین می‌شکافت، یا مردگان با آن به سخن درمی‌آمدند [همین قرآن بود، و باز ایمان نمی‌آوردند]، آری سررشته کارها همه به دست خداست، آیا کسانی که ایمان آورده‌اند نمی‌دانند که اگر خداوند می‌خواست همه مردم را هدایت می‌کرد، و به کافران همواره به خاطر کار و کردارشان عقوبتی هایل می‌رسد، یا به زودی به خانه [ شهر] آنان [فاتحانه‌] وارد شوی، تا آنکه وعده الهی فرارسد، که خداوند خلف وعده نمی‌کند'),
(1739, 13, 32, 'و به راستی پیامبرانی که پیش از تو بودند ریشخند شدند، آنگاه به کافران مهلت دادم، سپس آنان را فرو گرفتم، [بنگر که‌] عقوبت من چگونه بوده است‌'),
(1740, 13, 33, 'آیا کسی که حاکم بر هر کسی و کار و کردار اوست [همانند کسی است که چنین نیست؟] و برای خداوند شریکانی قائل شدند، بگو نام ایشان را ببرید، آیا او را به چیزی که در روی زمین [چنین چیزی‌] نمی‌شناسد خبر می‌دهید؟ یا صرفا سخنی سطحی می‌گویید؟ بلکه در نظر کافران مکرشان آراسته جلوه کرده است و از راه [خدا] باز داشته شده‌اند، و هرکس که خدا بیراه گذاردش، برای او رهنمایی نیست‌'),
(1741, 13, 34, 'در زندگانی دنیا عذابی دارند و عذاب اخروی سخت و سنگین‌تر است و در برابر خداوند نگهدارنده‌ای ندارند'),
(1742, 13, 35, 'وصف بهشتی که به پارسایان وعده داده شده است [چنین است که‌] جویباران از فرو دست آن جاری است و میوه‌ها و سایه آن همیشگی است، این سرانجام پرواپیشگان است، و سرانجام کافران آتش دوزخ است‌'),
(1743, 13, 36, 'و اهل کتاب به آنچه بر تو نازل شده است، شادمانی می‌کنند، و از گروه مشرکان کسانی هستند که بخشی از آن را انکار می‌کنند، بگو همین به من فرمان رسیده است که خداوند را بپرستم و به او شرک نیاورم، به سوی او دعوت می‌کنم و بازگشت من به سوی اوست‌'),
(1744, 13, 37, 'و بدین‌سان آن را به صورت کتابی حکمت‌آموز عربی نازل کرده‌ایم، و اگر پس از علمی که بر تو نازل شده است، از هوا و هوس آنان پیروی کنی، در برابر خداوند یاور و نگهدارنده‌ای نداری‌'),
(1745, 13, 38, 'و پیش از تو پیامبرانی فرستاده‌ایم و همسران و فرزندانی به آنان بخشیده‌ایم، و هیچ پیامبری را نرسد که معجزه‌ای جز به اذن الهی بیاورد، هر اجلی [را سرآمدی‌] معین است‌'),
(1746, 13, 39, 'خداوند آنچه را بخواهد می‌زداید یا می‌نگارد، و ام‌الکتاب نزد اوست‌'),
(1747, 13, 40, 'اگر بخشی از آنچه به آنان وعده داده‌ایم، به تو بنمایانیم، یا روح تو را [پیش از آن‌] بگیریم، جز این نیست که پیامرسانی بر تو و حسابرسی بر ماست‌'),
(1748, 13, 41, 'آیا ندیده‌اند که ما دامنه‌های سرزمین [کفر] را می‌کاهیم [و بر اسلام می‌افزاییم‌] و خداوند حکم می‌کند و حکم او رد کننده‌ای ندارد، و او زود شمار است‌'),
(1749, 13, 42, 'و به راستی که پیشینیان آنان هم مکرورزیدند، ولی مکر و تدبیر هر چه هست در اختیار خداوند است، می‌داند که هر جانداری چه به دست می‌آورد، و کافران به زودی خواهند دانست که نیک سرانجامی از آن کیست‌'),
(1750, 13, 43, 'و کافران گویند تو پیامبر نیستی، بگو خداوند و کسی که صاحب علم کتاب است، بین من و شما گواه بس‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'مهربان الر الف لام راء [این‌] کتابی است که بر تو نازل کرده‌ایم که مردم را به توفیق پروردگارشان از تاریکیها به سوی روشنایی، به سوی راه خداوند پیروزمند ستوده، باز بری‌'),
(1752, 14, 2, 'خداوندی که آنچه در آسمانها و آنچه در زمین است از آن اوست، و وای بر کافران از عذاب سخت و سنگین‌[ی که در پیش دارند]'),
(1753, 14, 3, '[همان‌] کسانی که زندگانی دنیا را از [زندگانی‌] آخرت خوشتر دارند، و از راه خدا باز می‌دارند و آن را کج می‌شمارند، اینان در گمراهی دور و درازی هستند'),
(1754, 14, 4, 'و هیچ پیامبری نفرستاده‌ایم مگر [با پیامی‌] به زبان قومش، تا [احکام و حقایق را] برای آنان روشن بدارد، آنگاه خداوند هرکس را بخواهد بیراه می‌گذارد و هرکس را که بخواهد هدایت می‌کند و او پیروزمند فرزانه است‌'),
(1755, 14, 5, 'و به راستی که موسی را با معجزات خویش فرستادیم که قومت را از تاریکیها به سوی روشنایی باز بر، و آنان را به یاد ایام الله بینداز، که در این برای هر شکیبای شاکری عبرتهاست‌'),
(1756, 14, 6, 'و چنین بود که موسی به قومش گفت نعمت الهی را بر خودتان یاد آورید که شما را از آل فرعون رهانید که عذابی سخت را به شما می‌چشانیدند و پسرانتان را می‌کشتند و دخترانتان را [برای کنیزی‌] زنده نگاه می‌داشتند، و در آن آزمونی بزرگ از سوی پروردگارتان بود'),
(1757, 14, 7, 'و چنین بود که پروردگارتان اعلام داشت که اگر شکر کنید بر نعمت شما می‌افزایم، و اگر کفران‌بورزید [بدانید که‌] عذاب من سخت و سنگین است‌'),
(1758, 14, 8, 'و موسی گفت اگر شما و هرکس که در روی زمین است، همگی کفرورزید، خداوند بی‌نیاز ستوده است‌'),
(1759, 14, 9, 'آیا خبر کسانی چون قوم نوح و عاد و ثمود که پیش از شما بودند، و کسانی که پس از آنان بودند، به شما نرسیده است، که هیچ کس جز خداوند آنان را نمی‌شناسد، که پیامبرانشان برای آنان معجزاتی [به میان‌] آوردند، آنگاه آنان دستهایشان را بر دهانهایشان بردند [که خاموش!]، و گفتند ما رسالت شما را نمی‌پذیریم و ما از آنچه ما را بدان می‌خوانید، سخت در شکیم‌'),
(1760, 14, 10, 'پیامبرانشان گفتند آیا در خداوندی که پدید آورنده آسمانها و زمین است، شکی هست؟ که شما را می‌خواند که گناهانتان را بیامرزد، و شما را تا مهلتی معین واپس‌دارد، گفتند شما جز انسانهایی همانند ما نیستید که می‌خواهید ما را از آنچه پدرانمان می‌پرستیده‌اند، باز دارید، [اگر چنین نیست‌] پس برای ما برهانی آشکار بیاورید'),
(1761, 14, 11, 'پیامبرانشان به ایشان گفتند [آری‌] ما جز انسانهایی مانند شما نیستیم، ولی خداوند بر هرکس از بندگانش که بخواهد منت می‌نهد [و نعمت می‌دهد] و ما را نرسد که جز به اذن الهی برای شما برهانی بیاوریم و مؤمنان باید که بر خداوند توکل کنند'),
(1762, 14, 12, 'و ما را نرسد که بر خداوند توکل نکنیم، و حال آنکه ما را به راه درستمان هدایت کرده است، و بر آزاری که به ما می‌دهید، شکیبایی خواهیم کرد، و اهل توکل باید که بر خداوند توکل کنند'),
(1763, 14, 13, 'و کافران به پیامبرانشان گفتند یقینا شما را از سرزمینمان آواره می‌کنیم، مگر آنکه به آیین ما باز گردید، و پروردگارشان به آنان وحی کرد که ستمکاران را نابود خواهیم کرد'),
(1764, 14, 14, 'و پس از آنان شما را در این سرزمین سکنا می‌دهیم، این برای کسی است که از ایستادن [در محشر به هنگام حساب‌] در پیشگاه من بترسد، و از هشدار من بهراسد'),
(1765, 14, 15, 'و [یاری و] داوری خواستند و هر زورگوی ستیزه‌جویی ناکام شد'),
(1766, 14, 16, 'که در پیشاپیش او جهنم است و از آبی زردابه‌آمیز به او می‌نوشانند'),
(1767, 14, 17, 'که آن را جرعه جرعه بنوشد و نتواند فرو برد، و مرگ از هر سو به سراغ او آید و او مردنی نیست، و عذابی سخت و سنگین در پیشاپیش خود دارد'),
(1768, 14, 18, 'داستان اعمال کسانی که به پروردگارشان کفرورزیده‌اند، همچون خاکستری است که در روز طوفانی، باد بر آن بوزد، که از آنچه به دست آورده‌اند، چیزی در دست ندارند، این همان گمراهی دور و دراز است‌'),
(1769, 14, 19, 'آیا ندانسته‌ای که خداوند آسمانها و زمین را به حق آفریده است، اگر بخواهد شما را [از میان‌] می‌برد و آفریدگان جدیدی به میان می‌آورد'),
(1770, 14, 20, 'و این بر خداوند دشوار نیست‌'),
(1771, 14, 21, 'و همه در پیشگاه خداوند حاضر آیند، آنگاه درماندگان به گردنکشان گویند ما پیرو شما بودیم، آیا شما باز دارنده چیزی از عذاب الهی از ما هستید؟ گویند اگر خداوند ما را هدایت کرده بود، ما نیز شما را هدایت می‌کردیم، برای ما یکسان است چه زاری و بی‌تابی کنیم و چه شکیبایی ورزیم، گریزگاهی نداریم،'),
(1772, 14, 22, 'و چون کار یکسویه شود، شیطان گوید خداوند به شما وعده راست و درستی داده بود، ولی من به شما وعده‌ای دادم و [در معامله‌] با شما خلاف کردم، و مرا بر شما دستی نبود، جز اینکه شما را [به بیراهه‌] دعوت کردم و شما دعوت مرا پذیرفتید، پس مرا ملامت مکنید و خودتان را ملامت کنید، من فریادرس شما نیستم و شما هم فریادرس من نیستید، پیشتر هم که مرا [در کار خداوند] شریک می‌انگاشتید، منکر بودم، عذاب دردناک نصیب ستمگران است‌'),
(1773, 14, 23, 'و کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، به باغهای [بهشتی‌ای‌] درآیند که جویباران از فرودست آن جاری است، و به اذن پروردگارشان جاودانه در آنند، درودشان در آنجا سلام است‌'),
(1774, 14, 24, 'آیا ندانسته‌ای که خداوند چگونه مثلی می‌زند که کلمه پاک [ایمان‌] همانند درختی پاک [و پرورده‌] است که ریشه‌اش [در زمین‌] استوار است و شاخه‌اش سر به آسمان دارد'),
(1775, 14, 25, 'میوه‌اش را به توفیق پروردگارش، زمان به زمان می‌دهد، و خداوند برای مردم این مثلها را می‌زند باشد که پندگیرند'),
(1776, 14, 26, 'و مثل کلمه پلید [شرک‌] همانند درختی است پلید که [بیخ آن‌] از روی زمین برکنده باشد و استوار نباشد'),
(1777, 14, 27, 'خداوند مؤمنان را در زندگانی دنیا و نیز در آخرت به سخن استوار، پایدار می‌دارد، و خداوند ستمکاران را بیراه می‌گذارد، و خداوند هر چه خواهد همان تواند کرد'),
(1778, 14, 28, 'آیا [داستان‌] کسانی را که [سپاس‌] نعمت الهی را به ناسپاسی بدل کردند و قومشان را به هلاک آباد [دوزخ‌] در آوردند، ندانسته‌ای‌'),
(1779, 14, 29, 'که به جهنم درآیند و بد جایگاهی است‌'),
(1780, 14, 30, 'و برای خداوند همانندانی قائل شدند تا [مردمان را] از راه او گمراه کنند، بگو [اندک زمانی‌] بهره‌مند شوید و بدانید که سیر و سرانجام شما به سوی آتش [دوزخ‌] است‌'),
(1781, 14, 31, 'بگو به آن بندگانم که ایمان آورده‌اند تا نماز را برپا دارند و پیش از آمدن روزی که در آن نه خرید و فروشی هست و نه دوستی، از آنچه روزیشان کرده‌ایم، پنهان و آشکارا ببخشند'),
(1782, 14, 32, 'خداوند کسی است که آسمانها و زمین را آفریده است و از آسمان آبی فرو باریده و بدان فرآورده‌ها برآورده که روزی شماست، و برای شما کشتیها را رام ساخته که به فرمان او در دریا روان شوند و رودباران را نیز برای شما رام کرده است‌'),
(1783, 14, 33, 'و خورشید و ماه را رام شما کرد که پیوسته روانند و شب و روز را [نیز] برای شما رام کرد'),
(1784, 14, 34, 'و از هر آنچه از او خواسته‌اید به شما بخشیده است و اگر نعمت الهی را بشمارید، نمی‌توانید آن را [چنانکه هست‌] شمارش کنید، که انسان ستمکار [در حق خویش‌] و ناسپاس است‌'),
(1785, 14, 35, 'و چنین بود که ابراهیم گفت پروردگارا این شهر را [حرم‌] امن بگردان و مرا و فرزندانم را از پرستش بتان دور بدار'),
(1786, 14, 36, 'پروردگارا آنها بسیاری از مردم را به گمراهی کشانده‌اند، پس هرکس از من پیروی کند از من است، و هر کس از من سرپیچی کند [امر او با توست که‌] تو آمرزگار مهربانی‌'),
(1787, 14, 37, 'پروردگارا من برخی فرزندانم را در سرزمینی بی‌آب و علف، در جوار بیت‌الحرام تو، سکنا داده‌ام، تا -پروردگارانماز را برپا دارند، پس دلهای مردمی را به آنان مهربان کن، و از فرآورده‌ها روزیشان ببخش باشد که سپاس بگزارند'),
(1788, 14, 38, 'پروردگارا تو آنچه پنهان و آنچه آشکار می‌داریم می‌دانی، و هیچ چیز در زمین و در آسمان بر خداوند پنهان نیست‌'),
(1789, 14, 39, 'سپاس خداوندی را که با وجود پیری به من اسماعیل و اسحاق را ارزانی داشت، چرا که پروردگارم شنوای دعاست‌'),
(1790, 14, 40, 'پروردگارا من و نیز زاد و رودم را نمازگزار بگردان، و پروردگارا دعای مرا بپذیر'),
(1791, 14, 41, 'پروردگارا در روزی که حساب برپا شود، مرا و پدر و مادرم و مؤمنان را بیامرز'),
(1792, 14, 42, 'و خداوند را از آنچه ستمکاران [مشرک‌] می‌کنند، غافل مپندار، جز این نیست که [حساب‌] آنان را تا روزی که دیدگان در آن خیره شود، واپس می‌اندازد'),
(1793, 14, 43, '[آنان‌] شتابزده، سرها به بالا گرفته [چشم به زیر انداخته‌]اند، بی‌آنکه چشم برهم زنند، و دل‌کنده‌'),
(1794, 14, 44, 'و مردمان را از روزی بترسان که عذاب آنان را فرو گیرد و ستم‌پیشگان گویند پروردگارا ما را تا زمانی نزدیک واپس بدار تا دعوت تو را اجابت و از پیامبران پیروی کنیم، آیا شما نبودید که پیشترها سوگند می‌خوردید که زوالی ندارید؟'),
(1795, 14, 45, 'و در خانه و کاشانه کسانی که در حق خویش ستم کرده بودند، سکنا یافتید و بر شما آشکار شد که با آنان چه کرده‌ایم، و برای شما مثلها زدیم‌'),
(1796, 14, 46, 'و به راستی که نیرنگشان را ورزیدند و [کیفر] نیرنگشان با خداست، و گرچه کوهها در اثر نیرنگشان از جای بروند'),
(1797, 14, 47, 'و هرگز خداوند را خلاف‌کننده وعده‌ای که به پیامبرانش داده است مینگار، چرا که خداوند پیروزمند دادستان است‌'),
(1798, 14, 48, 'روزی که زمین جز این زمین و آسمانها [جز این آسمانها] شوند و همگان در حضور خداوند یگانه قهار آشکار شوند'),
(1799, 14, 49, 'و گناهکاران را بینی که به هم بسته گرفتار بندها هستند'),
(1800, 14, 50, 'تن‌پوشهایشان از قطران است و آتش چهره‌هایشان را می‌پوشاند'),
(1801, 14, 51, 'تا بدین‌سان خداوند هر کسی را، بر وفق آنچه کرده است، پاداش دهد، چرا که خداوند زودشمار است‌'),
(1802, 14, 52, 'این پند و پیامی برای مردم است، تا به آن هشدار یابند و بدانند که او خدای یگانه است و تا خردمندان پند گیرند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الر الف لام راء این آیات کتاب آسمانی و قرآن مبین است‌'),
(1804, 15, 2, 'چه بسا کافران آرزو کنند که کاش مسلمان بودند'),
(1805, 15, 3, 'رهایشان کن تا بخورند و بهره‌مند شوند، و آرزوها سرگرمشان بدارد، زودا که بدانند'),
(1806, 15, 4, 'و ما [اهل‌] هیچ شهری را نابود نکردیم مگر آنکه اجل مکتوب و معینی داشت‌'),
(1807, 15, 5, 'هیچ امتی از اجلش پیش و پس نیفتد'),
(1808, 15, 6, 'و گفتند ای کسی که قرآن بر او نازل شده است، به یقین تو دیوانه‌ای‌'),
(1809, 15, 7, 'و اگر راست می‌گویی چرا فرشتگان را برای ما نمی‌آوری؟'),
(1810, 15, 8, '[باید بدانند که‌] ما فرشتگان را جز به حق نازل نمی‌کنیم، و در آن صورت هم [که نازل کنیم‌] مهلت نخواهند یافت‌'),
(1811, 15, 9, 'همانا ما قرآن را نازل کرده‌ایم و ما خود نگهبان آنیم‌'),
(1812, 15, 10, 'و به راستی پیش از تو [پیامبرانی‌] به میان امتهای نخستین فرستاده‌ایم‌'),
(1813, 15, 11, 'و هیچ پیامبری برای آنان نیامد مگر آنکه او را ریشخند می‌کردند'),
(1814, 15, 12, 'بدین‌سان آن را در دل گناهکاران راه می‌دهیم‌'),
(1815, 15, 13, '[که‌] به آن ایمان نمی‌آورند، و سنت پیشینیان [نیز چنین‌] گذشته است‌'),
(1816, 15, 14, 'و اگر بر آنان دری از [درهای‌] آسمان می‌گشودیم و آنان به آن عروج می‌کردند'),
(1817, 15, 15, 'باز بی‌شبهه می‌گفتند ما فقط چشم‌بندی شده‌ایم، بلکه قومی جادوزده هستیم‌'),
(1818, 15, 16, 'و به راستی در آسمان برجهایی آفریدیم و آن را در چشم تماشاگران آراستیم‌'),
(1819, 15, 17, 'و از [دستبرد] هر شیطان مطرودی محفوظ داشتیم‌'),
(1820, 15, 18, 'مگر کسی که دزدانه [رازی‌] بشنود، که شهابی روشن در پی او خواهد افتاد'),
(1821, 15, 19, 'و زمین را گسترانیدیم و در آن کوههای استوار در انداختیم و در آن از هر چیز سنجیده‌ای رویانیدیم‌'),
(1822, 15, 20, 'و در آن برای شما و برای آنان که شما روزی‌دهشان نیستید، زیستمایه‌ها پدید آوردیم‌'),
(1823, 15, 21, 'و هیچ چیز نیست مگر آنکه گنجینه‌هایش نزد ماست، و جز به اندازه معین از آن پدید نمی‌آوریم‌'),
(1824, 15, 22, 'و بادهای بارآور را فرستادیم و از آسمان آبی فرو فرستادیم، آنگاه شما را از آن سیراب کردیم و شما خزانه‌دار آن نیستید'),
(1825, 15, 23, 'و ماییم که زنده می‌داریم و می‌میرانیم و ما بازمانده [جاوید]ایم‌'),
(1826, 15, 24, 'و پیشینیان و پسینیان‌شما را به خوبی می‌شناسیم‌'),
(1827, 15, 25, 'و بی‌گمان پروردگارت آنان را محشور می‌گرداند، که او فرزانه داناست‌'),
(1828, 15, 26, 'و انسان [/ آدم‌] را از گل خشک بازمانده از لجنی بویناک آفریده‌ایم‌'),
(1829, 15, 27, 'و پیشتر جن [/ابلیس‌] را از آتشباد آفریده بودیم‌'),
(1830, 15, 28, 'و چنین بود که پروردگارت به فرشتگان فرمود من آفریننده انسانی از گل خشک بازمانده از لجنی بویناک هستم‌'),
(1831, 15, 29, 'پس چون او را سامان دادم و در آن از روح خود دمیدم در برابر او به سجده در افتید'),
(1832, 15, 30, 'آنگاه فرشتگان همگی سجده بردند'),
(1833, 15, 31, 'مگر ابلیس که از اینکه از سجده‌کنندگان باشد، سر باززد'),
(1834, 15, 32, 'فرمود ای ابلیس تو را چه می‌شود که از سجده‌کنندگان نیستی؟'),
(1835, 15, 33, 'گفت من کسی نیستم که به انسانی که از گل خشک بازمانده از لجنی بویناکش آفریده‌ای، سجده برم‌'),
(1836, 15, 34, 'فرمود پس از آن [بهشت‌] بیرون شو که تو مطرودی‌'),
(1837, 15, 35, 'و تا روز جزا بر تو لعنت باد'),
(1838, 15, 36, 'گفت پروردگارا پس مرا تا روزی که [همگان‌] برانگیخته شوند، مهلت ده‌'),
(1839, 15, 37, 'فرمود تو از مهلت یافتگانی‌'),
(1840, 15, 38, 'تا روز و هنگام معین‌'),
(1841, 15, 39, 'گفت پروردگارا از آنجا که مرا فریفتی، در روی زمین در نظر آنان [بدی را نیک‌] خواهم آراست، و همگی آنان را به گمراهی خواهم کشاند'),
(1842, 15, 40, 'مگر آن بندگان اخلاص یافته‌ات را'),
(1843, 15, 41, 'فرمود این راهی است که تا به پیشگاه من مستقیم است‌'),
(1844, 15, 42, 'بدان که بر بندگان [خالص‌] من دست نخواهی یافت، مگر کسانی از گمراهان که از تو پیروی کنند'),
(1845, 15, 43, 'و بی‌گمان جهنم میعادگاه همگی آنان است‌'),
(1846, 15, 44, 'که هفت طبقه دارد و بخشی جدا کرده از آنان خاص هر طبقه است‌'),
(1847, 15, 45, '[اما] پرهیزگاران در باغها[ی بهشتی‌] و چشمه‌سارانند'),
(1848, 15, 46, '[به آنان گویند] به سلامت و در امن و امان به آنجا درآیید'),
(1849, 15, 47, 'و از سینه‌های آنان هر گونه کینه‌ای را می‌زداییم، و دوستانه بر تختها رویاروی بنشینند'),
(1850, 15, 48, 'نه به آنان در آنجا خستگی و ماندگی رسد، و نه از آنجا بیرونشان کنند'),
(1851, 15, 49, 'به بندگان من خبر ده که من آمرزگار مهربانم‌'),
(1852, 15, 50, 'و [در جای خود] عذاب من هم عذابی دردناک است‌'),
(1853, 15, 51, 'و به آنان از مهما[نا]ن ابراهیم خبر ده‌'),
(1854, 15, 52, 'هنگامی که [فرشتگان‌] بر او وارد شدند و سلام گفتند، گفت ما از شما هراسانیم‌'),
(1855, 15, 53, 'گفتند مترس ما تو را به فرزندی دانا بشارت می‌دهیم‌'),
(1856, 15, 54, 'گفت با وجود اینکه پیری من بالا گرفته است به من بشارت می‌دهید، چگونه بشارتی به من می‌دهید؟'),
(1857, 15, 55, 'گفتند ما به راستی و درستی به تو بشارت می‌دهیم، از نومیدان مباش‌'),
(1858, 15, 56, 'گفت و جز گمراهان کسی از رحمت پروردگارش نومید نمی‌شود'),
(1859, 15, 57, 'گفت ای فرشتگان حال، کار و بار شما چیست؟'),
(1860, 15, 58, 'گفتند ما به سروقت قومی گنهکار فرستاده شده‌ایم‌'),
(1861, 15, 59, 'غیر از آل لوط که ما رهاننده همگی آنان هستیم‌'),
(1862, 15, 60, 'مگر همسرش که مقدر کرده‌ایم که از واپس‌ماندگان [در عذاب‌] باشد'),
(1863, 15, 61, 'آنگاه چون فرشتگان به نزد آل لوط آمدند'),
(1864, 15, 62, '[لوط] گفت شما گروهی ناشناس هستید'),
(1865, 15, 63, 'گفتند نه، بلکه ما چیزی را که آنان در آن تردید می‌کردند، برای تو آورده‌ایم‌'),
(1866, 15, 64, 'و [همان‌] حق را برایت آورده‌ایم و ما راستگوییم‌'),
(1867, 15, 65, 'پس پاسی از شب گذشته، خانواده‌ات را همراه ببر و از پی آنان روان شو، و هیچ یک از شما باز پس ننگرد، و به جایی که به شما دستور داده می‌شود روانه شوید'),
(1868, 15, 66, 'و این امر را با او در میان گذاشتیم که بامدادان ریشه اینان برکنده خواهد شد'),
(1869, 15, 67, 'و [از سوی دیگر] مردم شهر شادی کنان باز آمدند'),
(1870, 15, 68, '[لوط] گفت اینان مهمانان من هستند، مرا رسوا مسازید'),
(1871, 15, 69, 'و از خداوند پروا کنید و مرا خوار و خفیف مسازید'),
(1872, 15, 70, 'گفتند مگر تو را از [مهمان کردن‌] مردمان نهی نکرده بودیم؟'),
(1873, 15, 71, 'گفت اگر [از طریق مشروع‌] کاری دارید، اینها دختران من هستند'),
(1874, 15, 72, 'سوگند به جان تو که آنان [قوم لوط] در سر مستی‌شان سرگشته بودند'),
(1875, 15, 73, 'آنگاه هنگام طلوع آفتاب، بانگ مرگبار آنان را فرو گرفت‌'),
(1876, 15, 74, 'و آن را زیر و زبر کردیم و بر آن سنگپاره‌هایی از سنگ گل فرو باریدیم‌'),
(1877, 15, 75, 'در این امر برای اشارت‌دانان مایه‌های عبرت است‌'),
(1878, 15, 76, 'و [آثار] آن [شهر] بر سرراهی پایدار است‌'),
(1879, 15, 77, 'بی‌گمان در این امر برای مؤمنان مایه عبرت است‌'),
(1880, 15, 78, 'و اهل ایکه ستمکار بودند'),
(1881, 15, 79, 'از این روی از آنان انتقام گرفتیم و آنها بر سر راهی آشکار هستند'),
(1882, 15, 80, 'و به راستی که "اهل حجر" پیامبران را دروغ‌زن شمردند'),
(1883, 15, 81, 'و معجزات خود را به آنان بخشیدیم ولی از آنها رویگردان بودند'),
(1884, 15, 82, 'و از کوهها خانه‌هایی [برای خود] می‌تراشیدند که در امان بمانند'),
(1885, 15, 83, 'آنگاه بامدادان بانگ مرگبار آنان را فرو گرفت‌'),
(1886, 15, 84, 'و دستاوردشان [عذاب الهی را] از آنان باز نداشت‌'),
(1887, 15, 85, 'و ما آسمانها و زمین و آنچه در میان آنهاست جز به حق نیافریده‌ایم، و قیامت آمدنی است، پس [از آنان‌] در گذر و گذشتی نیکو پیشه کن‌'),
(1888, 15, 86, 'همانا پروردگارت آفرینشگر داناست‌'),
(1889, 15, 87, 'و به راستی که به تو سبع‌المثانی و [همگی‌] قرآن عظیم را بخشیدیم‌'),
(1890, 15, 88, 'به چیزی که اصنافی از آنان را به آن بهره‌مند گردانده‌ایم، چشم مدوز، و غم‌خوار آنان مباش و با مؤمنان فروتنی کن‌'),
(1891, 15, 89, 'و بگو منم که هشداردهنده آشکارم‌'),
(1892, 15, 90, 'همچنانکه [عذاب را] بر تفرقه‌گرایان نیز نازل کرده‌ایم‌'),
(1893, 15, 91, 'کسانی که قرآن را بخش بخش کردند'),
(1894, 15, 92, 'سوگند به پروردگارت که از همگی آنان خواهیم پرسید'),
(1895, 15, 93, 'از آنچه می‌کرده‌اند'),
(1896, 15, 94, 'پس آنچه دستور یافته‌ای آشکار کن و از مشرکان روی بگردان‌'),
(1897, 15, 95, 'ما تو را از [شر] ریشخندکنندگان کفایت [و حمایت‌] می‌کنیم‌'),
(1898, 15, 96, 'کسانی که در جنب خداوند به خدایی دیگر قائلند، زودا که [حقیقت را] بدانند'),
(1899, 15, 97, 'و به خوبی می‌دانیم که از آنچه می‌گویند دلتنگ می‌شوی‌'),
(1900, 15, 98, 'پس سپاسگزارانه پروردگارت را تسبیح گوی و از ساجدان باش‌'),
(1901, 15, 99, 'و پروردگارت را بپرست تا تو را مرگ فرارسد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'عذاب الهی فرا می‌رسد، در طلب آن شتاب مکنید، او پاک و فراتر است از آنچه با او شریک می‌سازند'),
(1903, 16, 2, 'فرشتگان را با وحی به امر خویش بر هرکس از بندگانش که بخواهد فرو می‌فرستد، که هشدار دهید که خدایی جز من نیست، پس از من پروا کنید'),
(1904, 16, 3, 'آسمانها و زمین را به حق آفریده است، فراتر است از آنچه با او شریک می‌سازند'),
(1905, 16, 4, 'انسان را از نطفه‌ای [بی‌مقدار] آفریده است، آنگاه او ستیزه‌جویی آشکار است‌'),
(1906, 16, 5, 'و چارپایان را برای شما آفرید که برای شما در آنها گرما و سودهای دیگر هست، و هم از آنها می‌خورید'),
(1907, 16, 6, 'و هنگامی که آنها را [شامگاهان‌] از چرا باز می‌گردانید، یا [بامدادان‌] به چرا می‌برید، شما را در آن تجملی هست‌'),
(1908, 16, 7, 'و بارهایتان را تا شهری که جز با به رنج انداختن تن خویش به آن نمی‌توانید رسید، می‌برند، بی‌گمان پروردگارتان رئوف و مهربان است‌'),
(1909, 16, 8, 'و نیز اسبان و استران و درازگوشان را [آفرید] تا بر آنها سوار شوید و مایه تجمل نیز هست، و نیز چیزهای دیگر که شما نمی‌دانید می‌آفریند'),
(1910, 16, 9, 'و [بیان‌] راه میانه با خداوند است، و بعضی از آن [راهها] بیراهه است، و اگر می‌خواست همگی شما را هدایت می‌کرد'),
(1911, 16, 10, 'او کسی است که از آسمان آبی فرو فرستاد که آشامیدنی شما از آن است و از همان گیاهان می‌روید که در آن [چارپایانتان را] می‌چرانید'),
(1912, 16, 11, 'با آن برای شما زراعت و زیتون و خرما و انگور و از هر گونه محصولات [دیگر] می‌رویاند، بی‌گمان در این برای اندیشه‌وران مایه عبرتی هست‌'),
(1913, 16, 12, 'و برای شما شب و روز و خورشید و ماه را رام کرد، و ستارگان به فرمان او رام شده‌اند، بی‌گمان در این برای خردورزان مایه عبرتی هست‌'),
(1914, 16, 13, 'و آنچه در زمین برای شما رنگارنگ آفرید، بی‌گمان در این برای پندگیران مایه عبرتی هست‌'),
(1915, 16, 14, 'او کسی است که دریا را رام کرد تا از [صید] آن گوشتی تر و تازه بخورید و از آن زیورهایی بیرون آورید که آنها را "می‌پوشید" و کشتی‌ها را می‌بینی که در آن دریا شکافند، و چنین کرد تا از فضل و کرم او معاش خود فراهم آورید و باشد که سپاس بگزارید'),
(1916, 16, 15, 'و کوهها را در زمین بیفکند تا شما را نجنباند و نیز رودها و راههایی [پدید آورد] باشد که راه یابید'),
(1917, 16, 16, 'و نیز نشانه‌هایی [دیگر]، و آنان با نظر به ستارگان [در دریا] راه می‌یابند'),
(1918, 16, 17, 'پس آیا کسی که می‌آفریند همانند کسی است که نمی‌آفریند؟ آیا پند نمی‌گیرید؟'),
(1919, 16, 18, 'و اگر نعمت الهی را بشمارید، نمی‌توانید آن را [چنانکه هست‌] شمارش کنید، بی‌گمان خداوند آمرزگار مهربان است‌'),
(1920, 16, 19, 'و خداوند آنچه پنهان و آنچه آشکار می‌دارید، می‌داند'),
(1921, 16, 20, 'و کسانی که [مشرکان به ناحق‌] به جای خداوند می‌پرستند، چیزی نیافریده‌اند و خودشان آفریده شده‌اند'),
(1922, 16, 21, '[اینان‌] مردگانی بی‌جانند، و خود نمی‌دانند که چه هنگام برانگیخته خواهند شد'),
(1923, 16, 22, 'خدای شما خدایی یگانه است، و کسانی که به آخرت ایمان ندارند، دلهایشان انکارگر است و خود مستکبرند'),
(1924, 16, 23, 'حقا که خداوند آنچه پوشیده و آنچه آشکار می‌دارند، می‌داند، [و] او مستکبران را دوست ندارد'),
(1925, 16, 24, 'و چون به ایشان گویند پروردگارتان چه نازل کرده است؟ گویند این افسانه‌های پیشینیان است‌'),
(1926, 16, 25, 'تا سرانجام هم بار گناهان خودشان را به تمامی در روز قیامت بر دوش کشند و هم بخشی از بارگناهان کسانی را که از روی نادانی گمراهشان کرده‌اند، هان چه بد است آنچه بر دوش می‌کشند'),
(1927, 16, 26, 'کسانی هم که [بر همین شیوه و] پیش از آنان بودند، مکرورزیدند، آنگاه فرمان خداوند در رسید که بنیادشان را از پایه برانداخت و سقف از فرازشان بر آنان فرود آمد، و عذاب از جایی که اندیشه‌اش را نمی‌کردند، بر سرشان نازل شد'),
(1928, 16, 27, 'سپس در روز قیامت آنان را خوار سازد و فرماید کجا هستند شریکان من که شما درباره آنان مناقشه می‌کردید؟ دانشوران گویند امروز خواری و عذاب نصیب کافران است‌'),
(1929, 16, 28, 'کسانی که فرشتگان جانشان را می‌گیرند و آنان بر خود ستم روا داشته‌اند، از در تسلیم درآیند [و گویند] ما هیچ بد و بیراهی نکرده‌ایم، ولی بی‌گمان خداوند به آنچه کرده‌اید، داناست‌'),
(1930, 16, 29, 'پس، از دروازه‌های جهنم وارد شوید، و جاودانه در آن خواهید بود، و جایگاه متکبران چه بد است‌'),
(1931, 16, 30, 'و به پرهیزگاران گویند پروردگارتان چه نازل کرده است؟ گویند [آنچه نازل کرده است‌] خیر است، برای نیکوکاران در این دنیا نیکی است و سرای آخرت بهتر است، و سرای پرهیزگاران چه نیکوست‌'),
(1932, 16, 31, 'همان بهشتهای عدن که به آن در آیند، که جویباران از فرودست آن جاری است، در آنجا هر چه خواهند برایشان هست، خداوند بدین گونه به پرهیزگاران پاداش می‌دهد'),
(1933, 16, 32, 'کسانی [از مؤمنان‌] که پاکند چون فرشتگان جانشان را بگیرند گویند سلام بر شما، به خاطر کار و کردارتان وارد بهشت شوید'),
(1934, 16, 33, 'انتظاری جز این ندارند که فرشتگان به نزد آنان بیایند یا امر پروردگارت نازل گردد، کسانی هم که پیش از آنان بودند، به همین شیوه رفتار کردند، و خداوند به آنان ستم نکرد، بلکه خود بر خویشتن ستم کردند'),
(1935, 16, 34, 'کیفر آنچه کرده بودند، گریبانگیرشان شد، و [کیفر] آنچه به ریشخند گرفته بودند، فراگیرشان شد'),
(1936, 16, 35, 'و مشرکان گفتند اگر خدا می‌خواست نه ما و نه پدرانمان چیزی را جز او نمی‌پرستیدیم، و چیزی را جز به امر او حرام نمی‌کردیم، پیشینیان آنان هم به همین شیوه رفتار کردند، و آیا جز پیام‌رسانی آشکار چیزی بر عهده پیامبران است؟'),
(1937, 16, 36, 'و به راستی که در میان هر امتی پیامبری برانگیختیم [تا بگویند] که خداوند را بپرستید و از طاغوت پرهیز کنید و از ایشان کسانی هستند که خداوند هدایتشان کرده و از ایشان کسانی هستند که سزاوار گمراهی‌اند، پس در روی زمین سیر و سفر کنید و بنگرید که سرانجام دروغ دارندگان چگونه بوده است‌'),
(1938, 16, 37, 'اگر بر هدایت آنان حریص باشی [بدان‌] که خداوند کسی را که بیراه گذارد، به راه نمی‌آورد، و ایشان یاوری ندارند'),
(1939, 16, 38, 'و سخت‌ترین سوگندهایشان را به نام خدا می‌خوردند که خداوند کسانی را که مرده‌اند، از نو زنده نخواهد کرد، آری [خواهد کرد]، این است وعده‌ای که به حق بر عهده اوست، ولی بیشترینه مردم نمی‌دانند'),
(1940, 16, 39, 'تا سرانجام برای آنان [حقیقت‌] آنچه را اختلاف دارند روشن سازد و تا کافران بدانند که دروغگو بوده‌اند'),
(1941, 16, 40, 'سخن ما درباره چیزی که اراده‌اش را کرده‌ایم فقط این است که به آن می‌گوییم موجود شو، و بی‌درنگ موجود می‌شود'),
(1942, 16, 41, 'و کسانی که پس از ستم دیدنشان، در راه خدا هجرت کرده‌اند، در دنیا، [در سرا و سرزمین‌] نیکویی جایشان می‌دهیم و اگر بدانند بی‌گمان پاداش اخروی بزرگتر است‌'),
(1943, 16, 42, 'همانان که شکیبایی‌ورزیده و بر پروردگارشان توکل کرده‌اند'),
(1944, 16, 43, 'و ما پیش از تو جز مردانی که به آنان وحی می‌کردیم، نفرستاده‌ایم، پس اگر نمی‌دانید از پژوهندگان کتابهای آسمانی بپرسید'),
(1945, 16, 44, '[و آنان را] همراه با پدیده‌های شگرف و کتابهای آسمانی [فرستادیم‌]، و بر تو قرآن را نازل کردیم، تا برای مردم آنچه بر ایشان نازل شده است، روشن کنی و باشد که اندیشه کنند'),
(1946, 16, 45, 'آیا کسانی که بدسگالی کرده‌اند ایمن‌اند از اینکه خداوند ایشان را [ناگهان‌] به زمین فرو برد، یا از جایی که اندیشه‌اش را نمی‌کنند، عذاب بر سرشان فرود آید؟'),
(1947, 16, 46, 'یا [خداوند] در [گرماگرم‌] گشت و گذارشان فروگیردشان و گزیر و گریزی ندارند'),
(1948, 16, 47, 'یا ایشان را در عین ترس و لرز فرو گیرد، [پس‌] پروردگارتان رئوف و مهربان است‌'),
(1949, 16, 48, 'آیا در آنچه خداوند آفریده است نمی‌نگرند که سایه‌هایشان از راست و چپ می‌گسترد و فروتنانه به درگاه خداوند سجده می‌کنند؟'),
(1950, 16, 49, 'هر آن جنبنده‌ای که در آسمانها و آنچه در زمین است، و فرشتگان به درگاه خدا سجده می‌برند و گردنکشی نمی‌کنند'),
(1951, 16, 50, 'از پروردگارشان، که بر آنان محیط است، می‌ترسند و آنچه دستور یافته‌اند، انجام می‌دهند'),
(1952, 16, 51, 'و خداوند فرمود قائل به دو خدا مباشید، جز این نیست که او خدای یگانه است، پس از من پروا کنید'),
(1953, 16, 52, 'و آنچه در آسمانها و زمین است، از آن اوست، و دین پایدار از آن اوست، آیا از غیر خداوند پروا دارید؟'),
(1954, 16, 53, 'و هر نعمتی که دارید، از جانب خداوند است، سپس چون به شما رنجی رسد به درگاه او می‌نالید'),
(1955, 16, 54, 'و چون رنج را از شما باز دارد، آنگاه گروهی از شما برای پروردگارشان شریک می‌آورند'),
(1956, 16, 55, 'تا سرانجام [ناسپاسان‌] در حق آنچه به ایشان بخشیده‌ایم، ناسپاسی کنند، اینک بهره‌مند شوید، زودا که بدانید'),
(1957, 16, 56, 'و برای بی‌شعوران [بتان‌]، بهره‌ای از آنچه روزیشان داده‌ایم، [نذر و] تعیین می‌کنند، به خدا که درباره آنچه افتراء می‌زنید، از شما پرسش خواهد شد'),
(1958, 16, 57, 'و برای خداوند دخترانی قائل می‌شوند، منزه است او، و برای خودشان هر چه بخواهند'),
(1959, 16, 58, 'و چون یکی از ایشان را از دختر [زادن همسرش‌] خبر دهند، چهره‌اش [از خشم و تاسف‌] سیاه شود و تاسف خود را فرو خورد'),
(1960, 16, 59, 'از ناگواری خبری که به او داده‌اند، از قوم خود پنهان شود [و با خود بیندیشد] آیا او را به خواری و زاری نگه دارد، یا [زنده‌] به خاک بسپارد، هان چه بد است برداشت و برخورد آنان‌'),
(1961, 16, 60, 'وصف بد، سزاوار بی‌ایمانان به آخرت، و وصف والا سزاوار خداوند است، و او پیروزمند فرزانه است‌'),
(1962, 16, 61, 'و اگر خداوند مردمان را به خاطر گناه [و شرک‌]شان فرو می‌گرفت، جنبنده‌ای بر روی زمین باقی نمی‌ماند، بلکه آنان را تا زمانی معین واپس‌می‌دارد، و چون اجلشان فرارسد، نه ساعتی پس و نه ساعتی پیش افتند'),
(1963, 16, 62, 'و برای خداوند چیزی را که خودشان هم خوش ندارند قائل می‌شوند و به زبانشان به دروغ مدعی می‌شوند که بهشت از آن آنان است، حقا که آتش [دوزخ‌] نصیب آنان است و ایشان [در این راه‌] پیشتازند'),
(1964, 16, 63, 'به خدا که برای امتهای پیش از تو هم [پیامبرانی‌] فرستادیم، آنگاه شیطان اعمالشان را در نظرشان بیاراست و آن روز او سرور ایشان است و عذابی دردناک [در پیش‌] دارند'),
(1965, 16, 64, 'و قرآن را جز برای این بر تو فرو نفرستادیم که [حقیقت‌] آنچه را اختلاف دارند، برایشان روشن کنی، و برای آنکه برای اهل ایمان رهنمود و رحمت باشد'),
(1966, 16, 65, 'و خداوند از آسمان آبی فرو فرستاد و با آن زمین را پس از پژمردنش زنده کرد، بی‌گمان در این برای سخن شنوایان مایه عبرت هست‌'),
(1967, 16, 66, 'و در چارپایان برای شما عبرتی است که از شکمهایشان، از میان سرگین و خون، شیری پاک به شما می‌نوشانیم، که گوارای آشامندگان است‌'),
(1968, 16, 67, 'و از میوه‌های خرما و انگور هم [شراب‌] مستی‌آور و هم خوراک نیکو به دست می‌آورید، بی‌گمان در این امر برای خردورزان مایه عبرتی است‌'),
(1969, 16, 68, 'و پروردگارت به زنبور عسل الهام کرد که از کوهها و درختان و آنچه برمی‌افرازند [برای خود] خانه بساز'),
(1970, 16, 69, 'سپس از همه میوه‌ها بخور و راه پروردگارت را فرمانبردارانه بپوی، [آنگاه‌] از شکمهای آنها شهدی رنگارنگ می‌تراود که در آن شفای مردمان است، در این برای اندیشه‌وران مایه عبرتی است‌'),
(1971, 16, 70, 'و خداوند [است که‌] شما را آفریده است و سپس جانتان را می‌گیرد، و بعضی از شما هستند که به حد اعلای فرتوتی می‌رسند، چندانکه پس از دانستن [بسیاری چیزها] چیزی ندانند، بی‌گمان خداوند دانای تواناست‌'),
(1972, 16, 71, 'و خداوند بعضی از شما را بر بعضی دیگر در روزی برتری داده است، و برتری یافتگان بازگرداننده روزیشان به ملک یمینهایشان نیستند، تا در آن برابر شوند پس آیا به نعمت خداوند انکار می‌ورزند؟'),
(1973, 16, 72, 'و خداوند برای شما از خودتان همسرانی قرار داد و از همسرانتان برای شما فرزندان و نوادگانی آفرید و به شما از خوراکهای پاکیزه روزی داد، آیا به باطل ایمان می‌آورند، و به نعمت الهی کفران می‌ورزند'),
(1974, 16, 73, 'و به جای خداوند چیزی را می‌پرستند که در آسمانها و زمین هیچ‌گونه اختیار روزی آنان را ندارند و نمی‌توانند داشته باشند'),
(1975, 16, 74, 'پس برای خداوند مثل مزنید، چرا که خداوند می‌داند و شما نمی‌دانید'),
(1976, 16, 75, 'خداوند مثلی می‌زند بین برده‌ای مملوک که قدرت بر هیچ کاری ندارد و بین کسی که از سوی خویش به او روزی نیکویی بخشیده‌ایم و او از همان پنهان و آشکار می‌بخشد، آیا این دو برابرند؟ سپاس خداوند را، آری بیشترینه‌شان در نمی‌یابند'),
(1977, 16, 76, 'و [همچنین‌] خداوند از دو مرد مثلی می‌زند که یکی از آن دو گنگ است و قدرت بر هیچ کاری ندارد و سربار سرور خویش است، هر جا که می‌فرستدش، خیری به بار نمی‌آورد، آیا او و کسی که به داد فرمان می‌دهد و در راه راست می‌رود، برابرند؟'),
(1978, 16, 77, 'و [علم‌] غیب آسمانها و زمین خداوند راست، و کار [قیام‌] قیامت، جز [به سرعت‌] چشم بر هم زدنی نیست، یا بلکه کمتر، که خداوند بر هر کاری تواناست‌'),
(1979, 16, 78, 'و خداوند [است‌] که شما را از شکمهای مادرانتان، در حالی که هیچ چیز نمی‌دانستید، بیرون آورد، و [از پیش‌] برای شما گوش و چشم و قلب آفرید، باشد که سپاس گزارید'),
(1980, 16, 79, 'آیا به پرندگان ننگریسته‌اند که در فضای آسمان رام شده‌اند، و کسی جز خداوند آنها را [در آن حال‌] نگاه نمی‌دارد، بی‌گمان در این برای اهل ایمان مایه‌های عبرت است‌'),
(1981, 16, 80, 'و خداوند از خانه‌هایتان برای شما مایه آرامش پدید آورد، و برای شما از پوستهای چارپایان خیمه‌هایی پدید آورد که در روز کوچیدنتان و روز اقامتتان آن را سبکبار می‌یابید، و از پشمها و کرکها و موهای آنها اثاثیه و مایه بهره‌وری تا هنگامی معین پدید آورد'),
(1982, 16, 81, 'و خداوند برای شما از آنچه آفریده است، سایه‌سارها و از کوهها برای شما غارها پدید آورد، و برای شما تن‌پوشهایی که شما را از گرما [و سرما] نگه دارد، و تن‌پوشهایی که شما را از آسیب همدیگر نگه می‌دارد، بدین‌سان نعمتش را بر شما تمام می‌کند باشد که تسلیم پیشه کنید'),
(1983, 16, 82, 'و اگر روی برتافتند [بدان‌] که بر تو پیام رسانی آشکار است‌'),
(1984, 16, 83, '[آنان‌] نعمت الهی را می‌شناسند، سپس انکارش می‌کنند، و بیشترینه‌شان ناسپاسند'),
(1985, 16, 84, 'و روزی که از هر امتی شاهدی برانگیزیم، سپس به کافران اجازه [عذرخواهی‌] داده نشود، و عذرشان را نپذیرند'),
(1986, 16, 85, 'و چون ستم‌پیشگان عذاب را بنگرند، آنگاه نه [عذابشان‌] سبک گردد و نه مهلت داده شوند'),
(1987, 16, 86, 'و چون مشرکان شریکانی را که [به جای خداوند] قائلند بنگرند، گویند پروردگارا اینان شریکانی هستند که ما به جای تو می‌پرستیدیم، آنگاه به آنان پاسخ دهند که شما دروغگویید'),
(1988, 16, 87, 'و در چنین روزی با خداوند از در تسلیم درآیند و بر ساخته‌هایشان [بر باد رود و] از دید آنان ناپدید شود'),
(1989, 16, 88, 'کسانی که کفرورزیدند و [مردمان را] از راه خدا باز داشتند، به خاطر فتنه و فسادشان عذابی بر عذابشان بیفزاییم‌'),
(1990, 16, 89, 'و روزی که از هر امتی شاهدی از خودشان بر آنان برانگیزیم و تو را بر اینان شاهد آوریم [تا چه خواهند کرد] و کتاب آسمانی [قرآن‌] را بر تو نازل کردیم که روشنگر همه چیز است، و برای مسلمانان رهنمود و رحمت و بشارت است‌'),
(1991, 16, 90, 'به راستی خداوند به عدل و احسان و ادای حق خویشاوندان فرمان می‌دهد، و از ناشایستی و کار ناپسند و سرکشی باز می‌دارد، او شما را اندرز می‌دهد باشد که پند گیرید'),
(1992, 16, 91, 'و به عهد الهی چون پیمان بستید وفا کنید و سوگندهایتان را پس از مؤکد داشتن آنها، مشکنید، حال آنکه خداوند را بر خود [در آن سوگندها] ضامن گرفته‌اید، بی‌گمان خداوند از آنچه می‌کنید آگاه است‌'),
(1993, 16, 92, 'و همانند زنی مباشید که پشمهایی را که می‌بافت، پس از محکم داشتنش، رشته رشته وامی‌تافت، تا سوگندهایتان را دستاویز دغلی کنید، به خیال اینکه امتی از امت دیگر بیشتر و پیشتر است، جز این نیست که خداوند به آن می‌آزمایدتان، و سرانجام در روز قیامت، [حقیقت‌] آنچه را در آن اختلاف داشتید برایتان روشن خواهد ساخت‌'),
(1994, 16, 93, 'و اگر خداوند می‌خواست شما را امت یگانه‌ای قرار می‌داد، ولی هر که را بخواهد بیراه می‌گذارد، و هر که را بخواهد به راه می‌آورد، و از چون و چند آنچه می‌کردید، از شما خواهند پرسید'),
(1995, 16, 94, 'و سوگندهایتان را دستاویز دغلی در بین خودتان قرار مدهید که لغزش قدمی، پس از استواری‌اش پیش آید، و به خاطر آنکه از راه خدا باز داشتید، عذاب بچشید و برایتان عذابی سهمگین [در پیش‌] است‌'),
(1996, 16, 95, 'و پیمان الهی را به بهای ناچیزی مفروشید، زیرا اگر بدانید آنچه در نزد خداوند است، برایتان بهتر است‌'),
(1997, 16, 96, 'آنچه نزد شماست، به پایان می‌رسد و آنچه نزد خداوند است، پاینده است، و به کسانی که شکیبایی‌ورزیده‌اند، به بهتر از آنچه کرده‌اند پاداششان را می‌پردازیم‌'),
(1998, 16, 97, 'هر کس از مرد یا زن که کار نیک کند و مؤمن باشد، به زندگانی پاک و پسندیده‌ای زنده‌اش می‌داریم و به بهتر از آنچه کرده‌اند پاداششان را می‌پردازیم‌'),
(1999, 16, 98, 'پس چون خواهی قرآن بخوانی، از [شر] شیطان مطرود به خداوند پناه ببر'),
(2000, 16, 99, 'آری او بر کسانی که ایمان آورده‌اند و بر پروردگارشان توکل کرده‌اند، دست ندارد'),
(2001, 16, 100, 'سلطه او فقط بر کسانی است که دوستدارش هستند و کسانی که به خداوند شرک می‌ورزند'),
(2002, 16, 101, 'و چون آیه‌ای را به جای آیه دیگر آوریم -و خداوند به آنچه نازل کرده است، آگاه‌تر استگویند تو فقط افترازنی، چنین نیست بلکه بیشترینه‌شان در نمی‌یابند'),
(2003, 16, 102, 'بگو آن را روح‌القدس [جبرئیل‌] به راستی و درستی از سوی پروردگارت نازل کرده است، تا مؤمنان را ثابت‌قدم بدارد و برای مسلمانان رهنمود و بشارتی باشد'),
(2004, 16, 103, 'و به خوبی می‌دانیم که ایشان می‌گویند همانا بشری او را آموزش می‌دهد [حاشا، چرا که‌] زبان کسی که اینان کژاندیشانه ادعا می‌کنند، گنگ و بیگانه است و این زبان [زبان قرآن‌] عربی [شیوا و] روشن است‌'),
(2005, 16, 104, 'کسانی که به آیات الهی ایمان ندارند، خداوند هدایتشان نمی‌کند و عذابی دردناک [در پیش‌] دارند'),
(2006, 16, 105, 'جز این نیست که دروغ‌پردازی را کسانی می‌کنند که به آیات الهی ایمان ندارند و اینان دروغگو هستند'),
(2007, 16, 106, 'هر کس که بعد از ایمانش به خداوند کفرورزد [بازخواست شود] مگر کسی که وادار شود، و دلش به ایمان آرام و استوار باشد، ولی کسانی که دل بر کفر نهاده باشند، خشم خداوند بر آنان است و عذابی سهمگین [در پیش‌] دارند'),
(2008, 16, 107, 'این از آن است که ایشان زندگی دنیا را از آخرت خوشتر دارند، و خداوند خدانشناسان را هدایت نمی‌کند'),
(2009, 16, 108, 'اینان کسانی هستند که خداوند بر دلها و گوشها و دیدگانشان مهر نهاده است و اینانند که غافلند'),
(2010, 16, 109, 'حقا که ایشان در آخرت زیانکارند'),
(2011, 16, 110, 'از سوی دیگر، پروردگارت در حق کسانی که پس از شکنجه دیدن، هجرت کردند، سپس به جهاد پرداختند و شکیبایی پیشه کردند، بدان که پروردگارت پس از آن آمرزگار مهربان است‌'),
(2012, 16, 111, 'روزی [فرارسد] که هر کس [فقط] به دفاع از خویش بپردازد، و جزای هرکسی بر وفق کاری که کرده است، تمام و کمال داده شود، و بر ایشان ستم نرود'),
(2013, 16, 112, 'و خداوند [اهل‌] شهری را مثل زند که امن و آسوده به سر می‌برد و روزی آن از هر سو به خوشی و فراوانی می‌رسید، آنگاه به نعمتهای الهی کفران‌ورزید، و سپس خداوند به کیفر کار و کردارشان بلای فراگیر گرسنگی و ناامنی را به آنان چشانید'),
(2014, 16, 113, 'و به راستی که پیامبری از خودشان به سوی آنان آمد، آنگاه او را دروغ‌زن انگاشتند، و سرانجام در حالی که ستمکار [مشرک‌] بودند، عذاب آنان را فرو گرفت‌'),
(2015, 16, 114, 'از آنچه خداوند روزیتان کرده است، حلال و پاکیزه بخورید، و اگر فقط او را می‌پرستید، بر نعمت الهی شکر کنید'),
(2016, 16, 115, 'بدانید [که خداوند] بر شما مردار و خون و گوشت خوک و آنچه نامی جز نام خدا [به هنگام ذبحش‌] برده باشند، حرام کرده است، اما اگر کسی درمانده شود، و تجاوزکار و زیاده خواه نباشد [و از آنها بخورد، بداند که‌] خداوند آمرزگار مهربان است‌'),
(2017, 16, 116, 'و با هر دروغی که به زبانتان می‌آید، مگویید که این حلال است و این حرام، تا بر خداوند دروغ بسته باشید، زیرا کسانی که بر خداوند دروغ ببندند رستگار نمی‌شوند'),
(2018, 16, 117, 'برخورداری اندکی است و [سپس‌] بر ایشان عذابی دردناک [در پیش‌] است‌'),
(2019, 16, 118, 'و بر یهودیان، آنچه را پیشتر بر تو خوانده‌ایم، حرام کرده‌ایم، و ما در حق ایشان ستم نکردیم، بلکه خود بر خویشتن ستم کردند'),
(2020, 16, 119, 'آنگاه پروردگارت در حق کسانی که از روی نادانی مرتکب کار ناشایستی شده‌اند، و سپس توبه و درستکاری کرده‌اند، پس از آن، آمرزگار مهربان است‌'),
(2021, 16, 120, 'بی‌گمان ابراهیم پیشوایی فروتن در برابر خداوند و پاکدین بود و از مشرکان نبود'),
(2022, 16, 121, 'که در برابر نعمتهای او سپاسگزار بود [و خداوند] او را برگزید و به راه راست هدایت کرد'),
(2023, 16, 122, 'و به او در دنیا نیکی بخشیدیم و او در آخرت هم از شایستگان است‌'),
(2024, 16, 123, 'سپس به تو وحی کردیم که از آیین ابراهیم پاکدین پیروی کن که از مشرکان نبود'),
(2025, 16, 124, 'حرمت نهادن به شنبه [ سبت‌] فقط بر کسانی مقرر شد که در آن [و در صید ماهی و نظایر آن‌] اختلاف‌ورزیدند، و بی‌گمان پروردگارت در روز قیامت در آنچه اختلاف می‌داشتند، در میان آنان داوری خواهد کرد'),
(2026, 16, 125, 'به راه پروردگارت با حکمت و پند[های‌] پسندیده فراخوان، و با آنان به شیوه‌ای که بهتر است مجادله [و مقابله‌] کن، چرا که پروردگارت داناتر است که چه کسانی از راه او به در افتاده‌اند و هم او به رهیافتگان داناتر است‌'),
(2027, 16, 126, 'و اگر خواستید کسی را مکافات کنید، نظیر آنچه مکافات دیده‌اید، مکافات دهید، و اگر شکیبایی [و خویشتنداری‌]ورزید، بدانید که این شیوه برای شکیبایان بهتر است‌'),
(2028, 16, 127, 'شکیبایی پیشه کن و شکیبایی تو جز به توفیق الهی نیست و بر آنان اندوه مخور و از مکری که می‌ورزند دلتنگ مباش‌'),
(2029, 16, 128, 'بی‌گمان خداوند با پرهیزگاران و نیکوکاران است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'پاکا کسی که بنده‌اش را شبی از مسجدالحرام تا مسجدالاقصی که پیرامونش را برکت بخشیده‌ایم، سیر داد، تا به او نمونه‌هایی از آیات خویش نشان دهیم، اوست که شنوا و بیناست‌'),
(2031, 17, 2, 'و به موسی کتاب آسمانی داده و آن را رهنمود بنی‌اسرائیل گرداندیم که جز من کسی را کارساز [مدانید و] مگیرید'),
(2032, 17, 3, '[ای‌] زاد و رود کسانی که همراه نوح سوار [بر کشتی‌] کردیم، همانا او بنده‌ای سپاسگزار بود'),
(2033, 17, 4, 'و بر بنی‌اسرائیل در کتاب آسمانی مقرر داشتیم که در این سرزمین دوبار فتنه و فساد و سرکشی بی‌اندازه خواهید کرد'),
(2034, 17, 5, 'و چون وعده نخستینشان فرارسید، بر شما کسانی از بندگان خود را که ستیزه‌جویانی سهمگین بودند، بر گماشتیم که اندرون خانه‌ها را جستجو می‌کردند، و آن وعده‌ای انجام یافتنی بود'),
(2035, 17, 6, 'سپس شما را بر ایشان پیروزی دادیم و شما را به اموال و پسران مدد رساندیم و نفرات شما را افزونتر ساختیم‌'),
(2036, 17, 7, 'اگر نیکی کنید در حق خویش نیکی کرده‌اید و اگر بدی کنید، به زیان خویش کرده‌اید، و چون وعده آخرشان فرارسید [کسانی بر گماشتیم تا] چهره شما را اندوهگین کنند، و به همان مسجد [بیت‌المقدس‌] وارد شوند همچنانکه بار اول هم وارد شده بودند، و به هر چه دست یابند به کلی نابود کنند'),
(2037, 17, 8, 'باشد که پروردگار شما بر شما رحمت آورد، و اگر باز گردید ما نیز باز می‌گردیم و جهنم را زندان کافران گردانده‌ایم‌'),
(2038, 17, 9, 'بی‌گمان این قرآن به آیینی که استوارتر است، راه می‌نمایاند و به مؤمنانی که نیکوکاری می‌کنند بشارت می‌دهد که برای آنان پاداش بزرگی هست‌'),
(2039, 17, 10, 'و اینکه برای بی‌ایمانان به آخرت، عذابی دردناک آماده ساخته‌ایم‌'),
(2040, 17, 11, 'و انسان همان گونه که در مورد خیر دعا می‌کند، در مورد شر نفرین می‌کند، و انسان شتاب‌کار است‌'),
(2041, 17, 12, 'و شب و روز را دو پدیده قرار دادیم، و آنگاه پدیده شب را محو، و پدیده روز را روشن گرداندیم، تا از فضل پروردگارتان [روزی خویش را] بجویید، و شمار سالها و حساب [هر چیز] را بدانید، و هر چیز را به خوبی روشن ساخته‌ایم‌'),
(2042, 17, 13, 'و سرنوشت هر انسانی را به گردن خود او پیوسته‌ایم، و روز قیامت کارنامه‌ای برای او بیرون می‌آوریم که آن را باز گشوده می‌یابد'),
(2043, 17, 14, '[و گوییم‌] کارنامه‌ات را بخوان، که امروز حسابگری خود تو برای خودت کافی است‌'),
(2044, 17, 15, 'هر کس که رهیاب شود، همانا به سود خویش رهیاب شده است، و هر کس بیراه رود، همانا به زیان خویش بیراه رفته است، و هیچ بردارنده‌ای بار گناه دیگری را برندارد، و ما عذابی در کار نمی‌آوریم مگر آنکه پیش از آن پیامبری برانگیخته باشیم‌'),
(2045, 17, 16, 'و چون خواهیم شهری را نابود کنیم، نازپروردگان آن را فرمان [و میدان‌] دهیم، و سرانجام در آن نافرمانی کنند و سزاوار حکم [عذاب‌] شوند، آنگاه به کلی نابودشان کنیم‌'),
(2046, 17, 17, 'و چه بسیار اقوام را که پس از نوح هلاک کردیم و بس است که پروردگارت به گناهان بندگانش آگاه و بیناست‌'),
(2047, 17, 18, 'هر کس بهره زودیاب [دنیوی‌] بخواهد، برای هر کس که بخواهیم و هر چه بخواهیم، در آن پیش اندازیم، سپس جهنم را برای او مقرر می‌داریم که نکوهیده و مطرود به آن در آید'),
(2048, 17, 19, 'و هر کس آخرت را بخواهد و چنانکه باید در راه آن بکوشد و مؤمن باشد، اینانند که سعیشان مشکور است‌'),
(2049, 17, 20, 'هر یک از آنان و اینان را از عطای پروردگارت بهره می‌رسانیم و عطای پروردگارت ممنوع نیست‌'),
(2050, 17, 21, 'بنگر که چگونه بعضی از آنان را بر بعضی دیگر برتری می‌دهیم، و درجات آخرت بزرگتر و برتر است‌'),
(2051, 17, 22, 'در جنب خداوند، خدایی دیگر قائل مشو، که نکوهیده و بی‌یار و یاور بنشینی‌'),
(2052, 17, 23, 'و پروردگارت امر کرده است که جز او را مپرستید و به پدر و مادر نیکی کنید، و اگر یکی از آنها، یا هر دو آنها در نزد تو به سن پیری برسند، به آنان حتی اف مگو، و آنان را مران و با ایشان به نرمی [و احترام‌] سخن بگو'),
(2053, 17, 24, 'و بر آنان بال فروتنی مهرآمیز بگستر و بگو پروردگارا بر آنان رحمت‌آور، همچنانکه [بر من رحمت آوردند و] مرا در کودکی پرورش دادند'),
(2054, 17, 25, 'پروردگارتان به آنچه در دلهایتان هست آگاه‌تر است، و اگر نیکوکار باشید، بدانید که او در حق توبه‌کاران آمرزگار است‌'),
(2055, 17, 26, 'و به خویشاوند حقش را ببخش، و نیز به بینوا و در راه مانده، و هیچ‌گونه تبذیری پیشه مکن‌'),
(2056, 17, 27, 'چرا که اهل تبذیر قرین شیاطین هستند، و شیطان نسبت به پروردگارش ناسپاس است‌'),
(2057, 17, 28, 'و اگر از ایشان، در طلب رحمتی از پروردگارت که در آن امید بسته‌ای، رویگردان شدی، با آنان به خوشی سخن بگو'),
(2058, 17, 29, 'و دستت را [از شدت بخل‌] بر گردنت مبند، و نیز بسیار گشاده‌اش مدار که ملامت زده و حسرت زده بنشینی‌'),
(2059, 17, 30, 'بی‌گمان پروردگارت [درهای‌] روزی را بر هر کس که بخواهد می‌گشاید و فرو می‌بندد، چرا که او به بندگانش آگاه و داناست‌'),
(2060, 17, 31, 'و فرزندانتان را از ترس فقر مکشید، ماییم که هم ایشان و هم شما را روزی می‌دهیم، به راستی که کشتن آنان گناهی بزرگ است‌'),
(2061, 17, 32, 'و به زنا نزدیک مشوید که آن ناشایستی و بی‌روشی است‌'),
(2062, 17, 33, 'و هیچ کس را که خداوند کشتنش را حرام داشته، جز به حق مکشید، و هر کس مظلومانه کشته شود، به راستی که برای ولی و وارث او حق و حجتی مقرر داشته‌ایم، ولی او هم نباید در [قصاص‌] قتل زیاده‌روی کند، چرا که [مقتول و کسانش از سوی شرع‌] یاری شده است‌'),
(2063, 17, 34, 'و به مال یتیم جز به شیوه‌ای که نیکوتر است، نزدیک مشوید، تا آنگاه که به کمال بلوغش برسد، و به پیمان وفا کنید، چرا که از پیمان پرسیده خواهد شد'),
(2064, 17, 35, 'و پیمانه را چون پیمان کنید، تمام و کمال دهید، و با ترازوی درست، وزن کنید که این بهتر و نیک انجام‌تر است‌'),
(2065, 17, 36, 'و آنچه به آن علم نداری پیروی مکن، چرا که گوش و چشم و دل هر یک در آن کار مسؤول است‌'),
(2066, 17, 37, 'و در زمین خرامان راه مرو، چرا که تو نه هرگز زمین را توانی شکافت و نه از بلندی هرگز سر به کوهها توانی رساند'),
(2067, 17, 38, 'همه اینها نابسامانیهایش نزد پروردگارت ناپسند است‌'),
(2068, 17, 39, 'این از آن حکمتهاست که از سوی پروردگارت بر تو وحی شده است، و در جنب خداوند، به خدایی دیگر قائل مشو، که سرانجام ملامت‌زده و مطرود به جهنم درخواهی افتاد'),
(2069, 17, 40, 'آیا پروردگارت شما را به داشتن پسران برگزیده، و خود از میان فرشتگان، دخترانی برای خود پذیرفته است؟ شما سخنی بس بزرگ [و ناروا] می‌گویید'),
(2070, 17, 41, 'و به راستی که ما در این قرآن، سخنهای گونه‌گون بیان داشته‌ایم تا پند گیرند، و حال آنکه جز بر رمیدن آنان نمی‌افزاید'),
(2071, 17, 42, 'بگو اگر چنانکه می‌گویند در جنب او خدایانی هم بودند، در آن صورت به سوی خداوند صاحب عرش [برای معارضه یا تقرب‌] راه می‌بردند'),
(2072, 17, 43, 'او از آنچه می‌گویند بسی منزه و فراتر است‌'),
(2073, 17, 44, 'آسمانهای هفت‌گانه و زمین و هر که در آنهاست برای او تسبیح می‌گویند، و هیچ چیز نیست مگر آنکه شاکرانه او را تسبیح می‌گوید ولی شما تسبیح آنان را در نمی‌یابید، او بردبار آمرزگار است‌'),
(2074, 17, 45, 'و چون قرآن خوانی، بین تو و بی‌ایمانان به آخرت، حجابی پوشنده قرار دهیم‌'),
(2075, 17, 46, 'و بر دلهای ایشان پرده‌هایی کشیده‌ایم که آن [پیام‌] را درنیابند، و در گوشهایشان سنگینی‌ای [نهاده‌ایم‌]، و چون پروردگارت را به تنهایی در قرآن یاد کنی، از سر نفرت پشت کنند'),
(2076, 17, 47, 'آنگاه که به تو گوش می‌سپارند، ما به آنچه گوش می‌دهند آگاه‌تریم، و نیز هنگامی که راز می‌گویند، زمانی که ستمکاران [مشرک‌] گویند که شما جز از مردی جادوزده پیروی نمی‌کنید'),
(2077, 17, 48, 'بنگر که چگونه برای تو مثل زده‌اند و گمراه شده‌اند، و راه به جایی نمی‌توانند برد'),
(2078, 17, 49, 'و گفتند آیا چون استخوانهای [پوسیده و] خرد و خاک شدیم، در هیئت آفرینشی تازه برانگیخته خواهیم شد؟'),
(2079, 17, 50, 'بگو سنگ یا آهن باشید'),
(2080, 17, 51, 'یا آفریده‌ای که در دلهای شما بزرگ می‌نماید، زودا که خواهند گفت چه کسی ما را [پس از مرگ‌] باز می‌گرداند؟ بگو همان کسی که شما را نخست‌بار آفرید، آنگاه در برابر تو سرهایشان را [به علامت انکار] تکان می‌دهند و می‌گویند آن امر کی خواهد بود؟ بگو چه بسا نزدیک باشد'),
(2081, 17, 52, 'روزی که شما را بخواند و شما سپاسگزارانه پاسخ دهید و چنین پندارید که جز اندکی [در دنیا یا در گور] درنگ نکرده‌اید'),
(2082, 17, 53, 'بگو به بندگانم سخنی را که بهتر است بگویند، چرا که شیطان میان آنان را به هم می‌زند، بی‌گمان شیطان دشمن آشکار انسان است‌'),
(2083, 17, 54, 'پروردگارتان به [احوال‌] شما داناتر است، اگر بخواهد به شما رحمت می‌آورد یا اگر بخواهد شما را عذاب می‌کند، و ما تو را نگهبان آنان نفرستاده‌ایم‌'),
(2084, 17, 55, 'و پروردگار تو به هر آنکه در آسمانها و زمین هست، داناتر است، و به راستی بعضی از پیامبران را بر بعضی دیگر برتری بخشیده‌ایم، و به داوود زبور عطا کرده‌ایم‌'),
(2085, 17, 56, 'بگو کسانی را که در برابر او قائل هستید به دعا بخوانید، [خواهید دید که‌] قادر به بلاگردانی از شما و هیچ تغییر و تحولی نیستند'),
(2086, 17, 57, 'این کسانی که [به ناحق به خدایی‌] می‌خوانند، کسانی که از همه مقرب‌ترند، خود به پروردگارشان تقرب می‌جویند، و به رحمت او امید دارند، و از عذاب او بیمناکند، که از عذاب پروردگارت باید بر حذر بود'),
(2087, 17, 58, 'و هیچ شهری نیست مگر آنکه ما پیش از [فرارسیدن‌] روز قیامت نابودکننده یا عذاب‌کننده [اهل‌] آن به عذابی شدید هستیم، و این امر در لوح محفوظ نگاشته شده است‌'),
(2088, 17, 59, 'و چیزی ما را از فرستادن معجزات باز نداشت مگر [همین امر] که پیشینیان آن را تکذیب کردند، و به ثمود ماده شتری که نشانه‌ای روشنگر بود، بخشیدیم، ولی در حق آن ستم کردند و ما [این گونه‌] معجزات را جز برای بیم دادن نمی‌فرستیم‌'),
(2089, 17, 60, 'و چنین بود که با تو گفتیم که پروردگارت بر همه مردمان احاطه دارد، و رؤیایی را که به تو نمایاندیم و شجره نفرین شده در قرآن را جز برای آزمون مردم قرار ندادیم، و ایشان را بیم می‌دهیم ولی جز بر طغیان شدید آنان نمی‌افزاید'),
(2090, 17, 61, 'و چنین بود که به فرشتگان گفتیم بر آدم سجده برید، آنگاه همه سجده بردند، جز ابلیس که گفت آیا بر کسی سجده برم که او را از گل آفریده‌ای؟'),
(2091, 17, 62, '[همچنین‌] گفت خواهی دید که همین کسی که بر من برتری‌اش دادی اگر مرا تا روز قیامت بازپس داری [مهلت دهی‌] همه زاد و رود او جز اندکی را از راه به در برم‌'),
(2092, 17, 63, '[خداوند] فرمود برو که هر کس از ایشان از تو پیروی کند، جهنم جزای شما خواهد بود که جزایی بسنده است‌'),
(2093, 17, 64, 'و هر کس را که توانی از ایشان به بانگ خویش از جای ببر و سوارگان و پیادگانت را بر سر ایشان بتاز و با آنان در اموال و اولاد شریک شو، و به آنان وعده‌[های رنگین‌] بده، و شیطان وعده‌ای جز فریب به آنان نمی‌دهد'),
(2094, 17, 65, 'بدان که بر بندگان من سلطه نداری و پروردگارت کارسازی را بسنده است‌'),
(2095, 17, 66, 'پروردگار شماست که کشتی را برای شما در دریا روان می‌کند تا از فضل او [روزی خویش را] بجویید، چرا که او با شما مهربان است‌'),
(2096, 17, 67, 'و چون در دریا بلایی دامن‌گیرتان شود، هر چه به دعایش می‌خوانید [و به ناحق می‌پرستید] جز او ناپدید می‌گردد آنگاه چون شما را برهاند و به خشکی برساند [از او] روی بر می‌گردانید و انسان ناسپاس است‌'),
(2097, 17, 68, 'پس آیا ایمنید از اینکه شما را در گوشه‌ای از خشکی به زمین فرو برد، یا بر سر شما شن‌بادی بفرستد آنگاه برای خویش نگهبانی نیابید؟'),
(2098, 17, 69, 'یا آیا ایمنید از اینکه بار دیگر شما را به آن [دریا] بازگرداند و بر سر شما تندبادی کشتی شکن بفرستد و شما را به خاطر کفر و کفرانتان غرقه سازد، آنگاه در این امر برای خویش در برابر ما خونخواهی نیابید'),
(2099, 17, 70, 'و به راستی که فرزندان آدم را گرامی داشتیم و آنان را در خشکی و دریا [بر مرکب مراد] روانه داشتیم و به ایشان از پاکیزه‌ها روزی دادیم و آنان را بر بسیاری از آنچه آفریده‌ایم، چنانکه باید و شاید برتری بخشیدیم‌'),
(2100, 17, 71, 'روزی که هر گروه از مردم را با امامشان فراخوانیم، کسانی که کارنامه‌شان به دست راستشان داده شود، کارنامه خود را [شادمانه‌] می‌خوانند و به آنان به اندازه ذره ناچیزی هم ستم نرود'),
(2101, 17, 72, 'و هر کس که در اینجا کوردل باشد، همو در آخرت هم کوردل و گمراه است‌'),
(2102, 17, 73, 'و بسا نزدیک بود که تو را از آنچه بر تو وحی می‌کنیم غافل کنند تا چیزی غیر از آن را بر ما بربندی، و آنگاه تو را دوست گیرند'),
(2103, 17, 74, 'و اگر گامت را استوار نداشته بودیم، چه بسا نزدیک بود که اندک گرایشی به آنان بیابی‌'),
(2104, 17, 75, 'در آن صورت دو چندان [عذاب‌] در زندگی دنیا و دو چندان پس از مرگ به تو می‌چشاندیم آنگاه برای خود در برابر ما یاوری نمی‌یافتی‌'),
(2105, 17, 76, 'و نیز بسا نزدیک بود تو را از این سرزمین به فریب و فتنه به جای دیگر بکشانند، تا تو را از آنجا آواره کنند، و آنگاه پس از تو جز اندکی نمی‌پاییدند'),
(2106, 17, 77, 'این سنت همان است که در حق پیامبران خویش که پیش از تو فرستاده بودیم، برقرار بود، و در سنت ما تغییر و تحولی نمی‌یابی‌'),
(2107, 17, 78, 'نمازت را هنگام زوال خورشید [در نیمروز] تا تاریکنای شب و نیز قرآن خوانی را در هنگام سپیده [برای نماز صبح‌] برپا دار، که در قرآن خوانی سپیده [ نماز صبح‌] [فرشتگان‌] حضور دارند'),
(2108, 17, 79, 'و از شب هم بخشی را بیدار باش که [این نماز شب‌] نافله‌ای [واجب‌] خاص توست، باشد که پروردگارت تو را به مقامی پسندیده [شفاعت‌] بگمارد'),
(2109, 17, 80, 'و بگو پروردگارا مرا به درستی [به مدینه‌] درون آور، و به درستی [از مکه‌] بیرون بر و برای من از سوی خویش سلطه‌ای نیرومند [بر دشمنان‌] قرار بده‌'),
(2110, 17, 81, 'و بگو حق فرارسید و باطل از میان رفت، بی‌گمان باطل از میان رفتنی است‌'),
(2111, 17, 82, 'و از قرآن آنچه برای مؤمنان شفابخش و رحمت است نازل می‌کنیم، و [این کتاب‌] بر ستمکاران [مشرک‌] جز زیان نیفزاید'),
(2112, 17, 83, 'و چون بر انسان نعمتی مقرر داریم [از سرمستی‌] روی برتابد و دامن کشان بگذرد، و چون به او شری رسد، بس نومید گردد'),
(2113, 17, 84, 'بگو هر کس فراخور خویش عمل می‌کند، و پروردگارتان داناتر است که چه کسی رهیافته‌تر است‌'),
(2114, 17, 85, 'و از تو درباره روح می‌پرسند، بگو روح از [عالم‌] امر پروردگارم است، و شما را از علم جز اندکی نداده‌اند'),
(2115, 17, 86, 'و اگر بخواهیم آنچه به تو وحی کرده‌ایم، از میان می‌بریم، آنگاه در آن برای خویش در برابر ما نگهبانی نمی‌یابی‌'),
(2116, 17, 87, 'مگر به رحمت پروردگارت که بخشش و بخشایش او در حق تو بسیار است‌'),
(2117, 17, 88, 'بگو اگر انس و جن گرد آیند که نظیر این قرآن را بیاورند، ولو بعضی از آنان پشتیبان بعضی دیگر باشند، نمی‌توانند نظیر آن بیاورند'),
(2118, 17, 89, 'و در این قرآن برای مردم هرگونه مثلی را گونه‌گون بیان داشته‌ایم و بیشترینه مردم جز ناسپاسی را نخواستند'),
(2119, 17, 90, 'و گفتند به تو ایمان نمی‌آوریم مگر آنکه برای ما از زمین چشمه‌ای بجوشانی‌'),
(2120, 17, 91, 'یا برای تو باغی از خرما و انگور باشد و در لابلای [درختان‌] آن جویباران را جاری گردانی‌'),
(2121, 17, 92, 'یا پاره‌هایی از آسمان را چنانکه گمان داری بر [سر] ما بیندازی یا خدا و فرشتگان را رویاروی ما بیاوری‌'),
(2122, 17, 93, 'یا تو را خانه‌ای از زر و زیور باشد، یا به آسمان بر شوی و بالا رفتنت را باور نکنیم مگر آنکه کتابی برای ما فرود آوری که بخوانیمش، بگو پاک و منزه است پروردگارم، آیا من جز بشری هستم که پیامبرم‌'),
(2123, 17, 94, 'و مردمان را به هنگامی که هدایت به سویشان آمد، چیزی از ایمان آوردن باز نداشت مگر اینکه گفتند آیا خداوند بشری را به پیامبری فرستاده است‌'),
(2124, 17, 95, 'بگو اگر در روی زمین فرشتگانی بودند که آرام و آهسته [به این سو و آن سو] می‌رفتند ما نیز برای آنان از آسمانی فرشته‌ای به پیامبری فرو می‌فرستادیم‌'),
(2125, 17, 96, 'بگو خداوند بین من و شما گواه بس، که او بر بندگان خویش آگاه و بیناست‌'),
(2126, 17, 97, 'و کسی که خداوند هدایتش کند رهیافته است و کسانی را که بیراه واگذارد، هرگز برای آنان در برابر او دوستانی نخواهی یافت، و آنان را در روز قیامت، نابینا و گنگ و ناشنوا، به رو در افتاده محشور گردانیم و سرایشان جهنم است که هر چه شعله‌اش فرو نشیند، بر ایشان افروخته می‌داریم‌'),
(2127, 17, 98, 'این جزای آنان است چرا که آیات ما را انکار کردند و گفتند آیا چون استخوانهای [پوسیده و] خرد و خاک شدیم در هیئت آفرینشی تازه برانگیخته خواهیم شد؟'),
(2128, 17, 99, 'آیا ننگریسته‌اند که خداوندی که آسمانها و زمین را آفریده است، تواناست که همانند ایشان را بیافریند، و برای آنان اجلی مقرر دارد که شکی در فرارسیدنش نباشد، و ستمکاران [مشرک‌] از ناحق‌شناسی جز ناسپاسی را نخواستند'),
(2129, 17, 100, 'بگو اگر شما خزاین رحمت پروردگارم را در اختیار داشتید، در آن هنگام هم از بیم درویشی، امساک پیشه می‌کردید، و انسان بخیل است‌'),
(2130, 17, 101, 'و به راستی که به موسی معجزات نه‌گانه روشنگری بخشیدیم، از بنی‌اسرائیل بپرس آنگاه که [موسی‌] به نزدشان آمد و فرعون به او گفت ای موسی تو را جادو زده می‌دانم‌'),
(2131, 17, 102, '[موسی‌] گفت خوب می‌دانی که اینها را جز پروردگار آسمانها و زمین روشنگرانه فرو نفرستاده است، و من تو را ای فرعون نابود شدنی می‌دانم‌'),
(2132, 17, 103, '[فرعون‌] می‌خواست که راه آنان را از سرزمین خودشان بزند، ولی او و همه کسانی را که همراه او بودند، غرقه ساختیم‌'),
(2133, 17, 104, 'و پس از او به بنی‌اسرائیل گفتیم در سرزمین خویش ساکن شوید، و چون وعده آخرت فرارسد همگی‌تان را با هم باز آوریم‌'),
(2134, 17, 105, 'و آن را به حق فرو فرستادیم و به حق هم فرود آمد و تو را جز مژده‌رسان و هشداردهنده نفرستاده‌ایم‌'),
(2135, 17, 106, 'و [این‌] قرآنی است که آن را بخش بخش فرو فرستاده و چنانکه باید و شاید نازل کرده‌ایم تا آن را به آهستگی و درنگ بر مردم بخوانی‌'),
(2136, 17, 107, 'بگو شما چه به آن ایمان بیاورید چه نیاورید، کسانی که پیش از آن دانش یافته‌اند، چون بر آنان خوانده شود، سجده کنان به رو در می‌افتند'),
(2137, 17, 108, 'و می‌گویند پاک و منزه است پروردگار ما، وعده پروردگار ما انجام یافتنی است‌'),
(2138, 17, 109, 'و به رو در می‌افتند و می‌گریند و بر خشوع و خشیت آنان می‌افزاید'),
(2139, 17, 110, 'بگو او را چه الله بخوانید چه رحمان، هر چه بخوانید او را نامهای نیک است، و نمازت را نه بلند بخوان و نه آهسته، و راهی میانه برگزین‌'),
(2140, 17, 111, 'و بگو سپاس خداوندی را که نه فرزندی برگزیده و نه در جهانداری شریکی دارد و نه از سر ناچاری دوستی دارد، و او را چنانکه باید و شاید بزرگ بشمار');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'سپاس خداوندی را که بر بنده خویش کتاب آسمانی را که در آن هیچ‌گونه کژی و کاستی نگذارده، نازل کرده است‌'),
(2142, 18, 2, 'کتابی استوار که از سوی او عقوبتی سخت را هشدار دهد، و به مؤمنانی که نیکوکاری می‌کنند بشارت دهد که پاداشی نیک [در پیش‌] دارند'),
(2143, 18, 3, 'که جاودانه [در جوار رحمت او] ماندگارند'),
(2144, 18, 4, 'و تا کسانی را که می‌گویند خداوند فرزندی برگزیده است، بیم دهد'),
(2145, 18, 5, 'به این امر نه خودشان علم [و یقین‌] دارند نه پدرانشان، ناپسند است سخنی که از دهان ایشان بر می‌آید، و جز دروغ نمی‌گویند'),
(2146, 18, 6, 'و چه بسا تو جان خود را در کار و بار ایشان، از شدت تاسف می‌فرسایی، که چرا به این سخن ایمان نمی‌آورند'),
(2147, 18, 7, 'ما آنچه را در زمین هست زیوری برای آن ساخته‌ایم تا بیازماییمشان که کدامیک نیکو کردارترند'),
(2148, 18, 8, 'و ما [سرانجام‌] آنچه بر روی آن [زمین‌] هست به صورت خاک و خاشاکی سترون در می‌آوریم‌'),
(2149, 18, 9, 'گویا دانسته‌ای که داستان اصحاب کهف و رقیم از آیات شگفت‌آور ماست‌'),
(2150, 18, 10, 'چنین بود که جوانمردان به غار پناه بردند و گفتند پروردگارا بر ما رحمت خویش بگستر و کار ما را به سامان آور'),
(2151, 18, 11, 'و در همان غار بر [چشمها و] گوشهایشان تا چندین سال پرده کشیدیم‌'),
(2152, 18, 12, 'آنگاه از خواب بیدارشان ساختیم تا معلوم بداریم که کدام یک از دو گروه، حساب مدت درنگ [و خوابشان‌] را بهتر می‌شمارد'),
(2153, 18, 13, 'ما داستانشان را به راستی و درستی بر تو می‌خوانیم ایشان جوانمردانی بودند که [در نهان‌] به پروردگارشان ایمان آورده بودند و ما بر هدایتشان افزودیم‌'),
(2154, 18, 14, 'و دلهایشان را استوار داشتیم که برخاستند و گفتند پروردگار ما پروردگار آسمانها و زمین است، ما جز او خدایی نمی‌پرستیم که در غیر این صورت هر چه بگوییم باطل است‌'),
(2155, 18, 15, 'اینان قوم ما هستند که به جای او خدایانی را به پرستش گرفته‌اند، چرا برهانی آشکار [بر حقانیت‌] آنان نمی‌آورند؟ پس کیست ستمکارتر از کسی که بر خداوند دروغ بندد'),
(2156, 18, 16, 'و چون از ایشان و آنچه جز خداوند می‌پرستند، کناره گرفتید، در آن غار جای گیرید تا پروردگارتان رحمت خویش را بر شما بگستراند و کار شما را به سامان آورد'),
(2157, 18, 17, 'و خورشید را چون طلوع می‌کرد می‌دیدی که از غارشان به سوی دست راست می‌گراید و چون غروب می‌کرد از دست چپ آنان بر می‌گذشت و آنان در گستره غار بودند، این از آیات الهی است، هرکس که خداوند هدایتش کند رهیافته است و هرکس که او بیراه واگذارد، هرگز برایش سرور و راهنمایی نمی‌یابی‌'),
(2158, 18, 18, 'و آنان را بیدار می‌انگاشتی و حال آنکه خفته بودند، و ایشان را [از چپ‌] به راست و [از راست‌] به چپ می‌گردانیم و سگشان بازوانش را بر درگاه غار گشوده بود، چون به ایشان می‌نگریستی، پشت می‌کردی و می‌گریختی و از ایشان هراسان می‌شدی‌'),
(2159, 18, 19, 'و بدین‌سان بود که آنان را از خواب بیدار کردیم تا در میان خویش همپرسی کنند، [چنانکه‌] یکی از آنان گفت چه مدت در اینجا مانده‌اید؟ [در پاسخش‌] گفتند به اندازه یک روز یا بخشی از روز [در اینجا] مانده‌ایم، [دیگران‌] گفتند پروردگارتان آگاه‌تر است که چه مدت مانده‌اید، حال یکی از خودتان را با این درهمتان به سوی شهر بفرستید تا ببیند که خوراک بهتر کجاست، و خوراکی از آن برایتان [بخرد و] بیاورد و پنهانکاری کند و هیچ کس را از حال شما آگاه نگرداند'),
(2160, 18, 20, 'چرا که آنان اگر بر شما دست یابند سنگسارتان می‌کنند یا شما را [با زور] به آیین خویش در می‌آورند و در آن صورت هرگز رستگار نخواهید شد'),
(2161, 18, 21, 'و بدین‌سان دیگران را از حال ایشان با خبر گرداندیم تا بدانند که وعده الهی راست و درست است و در قیامت تردیدی نیست، و هنگامی که در میان خویش بگومگو کردند و [عده‌ای‌] گفتند بر جایگاه آنان یادمانی بسازید، پروردگارشان به احوال آنان آگاه‌تر است، و کسانی که مهار کار ایشان را در دست داشتند گفتند بر جایگاه آنان [زیارتگاه و] معبدی خواهیم ساخت‌'),
(2162, 18, 22, 'زودا که بگویند آنان سه تن بودند، چهارمینشان سگشان بود، و بگویند پنج تن بودند، ششمینشان سگشان بود که همه از روی حدس و گمان است، و گویند هفت تن بودند و هشتمینشان سگشان بود، بگو پروردگار من به عده آنان داناتر است، هیچ کس عده آنان را نمی‌داند جز معدودی، پس در کار و بار آنان جز در حدی سطحی بگومگو مکن و از احد از آنان درباره آنان نظر مخواه'),
(2163, 18, 23, 'و هرگز در هیچ کاری مگو که من فردا کننده آن هستم‌'),
(2164, 18, 24, '[و بگو] مگر آنکه خدا بخواهد، و چون [ان شاء الله گفتن را] فراموش کردی [هنگامی که به یاد آوردی‌] پروردگارت را یاد کن و بگو باشد که پروردگارم مرا به راهی نزدیکتر از این به صواب هدایت کند'),
(2165, 18, 25, 'و در غارشان سیصد سال ماندند و نه سال هم بر آن افزودند'),
(2166, 18, 26, 'بگو خداوند به مدت ماندنشان داناتر است، علم غیب آسمانها و زمین خاص اوست، چقدر بینا و چقدر شنواست، ایشان را جز او سروری نیست، و در فرمانروایی خود کسی را شریک نمی‌سازد'),
(2167, 18, 27, 'و آنچه از کتاب پروردگارت بر تو وحی شده است بخوان، [و بدان که‌] کلمات [/ وعده‌های‌] او دگرگون‌کننده‌ای ندارد، و هرگز جز او پشت و پناهی نخواهی یافت‌'),
(2168, 18, 28, 'و با کسانی که بامدادان و شامگاهان، پروردگارشان را [به دعا و نیایش‌] می‌خوانند و در طلب خشنودی او هستند، مدارا کن، و در هوای تجمل زندگی دنیوی، چشم از ایشان بر مگیر، و از کسی که دلش را از یاد خویش غافل داشته‌ایم، و در پی هوی و هوس خویش است و کارش تباه است، پیروی مکن‌'),
(2169, 18, 29, 'و بگو این حق و از سوی پروردگارتان است، هرکس که خواهد ایمان بیاورد، و هرکس که خواهد کفرورزد، [و بدانند که‌] ما برای ستمکاران [مشرک‌] آتشی فراهم آورده‌ایم که سراپرده‌های آن آنان را فراخواهد گرفت، و چون به استغاثه آبی طلب کنند، به ایشان آبی چون فلز گداخته دهند که [حرارتش‌] چهره‌هایشان را بریان کند، هم نوشابه‌ای بد است و هم مجلسی بد'),
(2170, 18, 30, 'کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند [بدانند که‌] ما پاداش کسی را که نیکوکاری کرده است، فرو نمی‌گذاریم‌'),
(2171, 18, 31, 'اینانند که بهشت‌[های‌] عدن دارند که جویباران از فرودستشان جاری است، و در آنجا به دستبندهای زرین آراسته شوند و جامه‌های سبزرنگ از پرنیان [نازک‌] و ابریشم ستبر بپوشند، و در آنجا بر تختها تکیه زنند، هم پاداشی نیکوست و هم مجلسی نیکو'),
(2172, 18, 32, 'و برای آنان مثلی بزن از دو مرد که به یکی از آنان دو تاکستان داده بودیم، و پیرامون آن را درخت خرما و در میانه آنها کشتزاری قرار داده بودیم‌'),
(2173, 18, 33, 'و هر دو باغ میوه‌اش را می‌داد و از صاحبش چیزی دریغ نمی‌ورزید و در میانه آنها جویباری روان کرده بودیم‌'),
(2174, 18, 34, 'و بدین‌سان دارایی‌ای داشت، و [یک روز] به دوستش که با او گفت و گو می‌کرد گفت من از تو مالدارتر و با خدم و حشم‌ترم‌'),
(2175, 18, 35, 'و در حالی که در حق خویش ستمکار بود، داخل باغش شد و گفت گمان ندارم که این [نعمت‌] هرگز نابود شود'),
(2176, 18, 36, 'و گمان ندارم که قیامت بر پا شود، و اگر هم به سوی پروردگارم باز گردانده شوم، در آنجا جایگاهی بهتر از این خواهم یافت‌'),
(2177, 18, 37, 'دوستش که با او گفت و گو می‌کرد گفت آیا به کسی که تو را از خاک و سپس از نطفه آفریده است و سپس در هیئت انسانی [معتدل‌] سامان داده است، کفرمی‌ورزی؟'),
(2178, 18, 38, 'ولی من می‌گویم او که خداوند است، پروردگار من است و با پروردگارم احدی را شریک نمی‌آورم‌'),
(2179, 18, 39, 'و چرا چون وارد باغت شدی، نگفتی "ما شاء الله لا قوة الا بالله"، اگر مرا کم‌مال‌تر و کم‌زاد و رودتر می‌بینی،'),
(2180, 18, 40, 'چه بسا پروردگارم بهتر از باغ تو به من ببخشد و بر باغ تو صاعقه‌ای از آسمان فرود فرستد و به صورت خاک و خاشاکی سترون در آید'),
(2181, 18, 41, 'یا آب آن [به اعماق زمین‌] فرو رود، و هرگز نتوانی بازش یافت‌'),
(2182, 18, 42, 'و [سرانجام‌] دارایی‌اش بر باد رفت، و او به خاطر هزینه‌ای که در آن کرده بود، دست [حسرت‌] بر دست می‌زد، و آن باغ سقفها و دیوارهایش فرو ریخته بود، و می‌گفت کاش من هیچ کس را با پروردگارم شریک نمی‌انگاشتم‌'),
(2183, 18, 43, 'و برای او گروهی نبودند که او را در برابر خداوند یاری دهند و انتقام گیرنده نبود'),
(2184, 18, 44, 'آنجاست که ولایت از آن خداوند بر حق است، هموست که خوش پاداش‌تر و بهترین بخشنده نیک سرانجامی است‌'),
(2185, 18, 45, 'و برای آنان زندگی دنیوی را به آبی مثل بزن که آن را از آسمان نازل کنیم و به آن گل و گیاه زمین آمیزد، و سرانجام خرد و خوار شود که بادها پراکنده‌اش کنند، و خداوند بر هر کاری تواناست‌'),
(2186, 18, 46, 'اموال و پسران، تجمل زندگی دنیوی است، و کارهای ماندگار شایسته، نزد پروردگارت خوش پاداش‌تر و امیدبخش‌تر است‌'),
(2187, 18, 47, 'و روزی [آید] که کوهها را به حرکت در آوریم و زمین را آشکار و هموار بینی و آنان را گرد آوریم و هیچ کس از آنان را فرو نگذاریم‌'),
(2188, 18, 48, 'و [آنان را] بر پروردگارت به صف عرضه دارند [فرماید] همچنانکه نخست بار شما را آفریده بودیم [تنها و برهنه‌] نزد ما آمدید، ولی گمان می‌کردید که برای شما موعد [دیداری‌] نمی‌گذاریم‌'),
(2189, 18, 49, 'و کارنامه‌ها را در میان آورند، آنگاه گناهکاران را از آنچه در آن است هراسان بینی، و گویند وای بر ما این چه کتابی است که هیچ خرد و بزرگی را فرو نگذاشته مگر آنکه بر شمرده است، و آنچه را انجام داده‌اند حاضر یابند و پروردگارت بر هیچ کس ستم روا نمی‌دارد'),
(2190, 18, 50, 'و چنین بود که به فرشتگان گفتیم به آدم سجده برید، همه سجده بردند مگر ابلیس که از جنیان بود و از فرمان پروردگارش سرپیچید، آیا شما او و زاد و رودش را به جای من به دوستی می‌گیرید، و حال آنکه ایشان دشمنان شما هستند، ستمکاران [مشرک‌] بد جانشینی [به جای خدا] دارند'),
(2191, 18, 51, 'آنان را در هنگام آفرینش آسمانها و زمین و آفرینش خودشان گواه نگرفتم و من آن نیستم که گمراه‌کنندگان را به یاری بگیرم‌'),
(2192, 18, 52, 'و روزی که فرماید شرکایی را که برای من می‌انگاشتید فراخوانید، آنگاه ایشان را بخوانند و پاسخی به ایشان ندهند، و در میان آنان مهلکه‌ای فاصله اندازیم‌'),
(2193, 18, 53, 'و گناهکاران آتش دوزخ را ببینند و بدانند که در آن خواهند افتاد و از آن گریزی نیابند'),
(2194, 18, 54, 'و در این قرآن برای مردم هر گونه مثلی را گونه‌گون بیان داشته‌ایم و انسان از هر موجودی جدل‌پیشه‌تر است‌'),
(2195, 18, 55, 'و مردمان را به هنگامی که هدایت به سویشان آمد، چیزی از ایمان آوردن و آمرزش خواهی از پروردگارشان باز نداشت، مگر آنکه سنت پیشینیان برای آنان نیز پیش آمد، یا عذاب رویا رویشان آمد'),
(2196, 18, 56, 'و ما پیامبران را جز مژده آور و هشداردهنده نمی‌فرستیم و کافران از باطل دفاع می‌کنند که حق را با آن ابطال کنند، و آیات من و هشدار خویش را به ریشخند گرفتند'),
(2197, 18, 57, 'و کیست ستمکارتر از کسی که به آیات پروردگارش پند داده شود و از آن روی بگرداند و کار و کردار پیشین خود را فراموش کند، ما بر دلهایشان پرده‌هایی کشیده‌ایم که آن [پیام‌] را در نیابند و در گوشهای آنان سنگینی‌ای [نهاده‌ایم‌] و اگر ایشان را به سوی هدایت فراخوانی، هرگز و به هیچ وجه راه نیابند'),
(2198, 18, 58, 'و پروردگار تو آمرزگار صاحب رحمت است، اگر آنان را به خاطر آنچه کرده‌اند، فرو می‌گرفت، عذابشان را پیش می‌انداخت، ولی برای آنان میعادی مقرر است که در برابر آن پناهگاهی نمی‌یابند'),
(2199, 18, 59, 'و این شهرهایی است که چون [اهلش‌] ستم ورزیدند، نابودشان کردیم، و برای نابودیشان میعادی مقرر داشتیم‌'),
(2200, 18, 60, 'و چنین بود که موسی به شاگردش گفت دست از سیر و طلب برندارم تا به مجمع‌البحرین برسم، یا آنکه روزگارانی دراز راه بپیمایم‌'),
(2201, 18, 61, 'و چون به مجمع بین آن دو [دریا] رسیدند ماهیشان را فراموش کردند که راهش را به میان دریا در پیش گرفته بود و روانه شده بود'),
(2202, 18, 62, 'و چون چندی از آنجا گذشتند [موسی‌] به شاگردش گفت غذایمان را بیاور که از این سفرمان خستگی و ماندگی دیده‌ایم‌'),
(2203, 18, 63, 'گفت ملاحظه کن، وقتی که در کنار آن تخته‌سنگ آرام گرفتیم، من [داستان‌] ماهی را فراموش کردم و جز شیطان آن را از یاد من نبرد که [به شما] بگویم و [آن ماهی‌] با کمال شگفتی راهش را به میان دریا در پیش گرفت‌'),
(2204, 18, 64, 'گفت این همانجاست که ما جستجویش می‌کردیم، لذا پی‌جویانه بازگشتند'),
(2205, 18, 65, 'آنگاه بنده‌ای از بندگان ما [خضر] را یافتند که به او رحمتی از سوی خویش ارزانی داشته و از پیشگاه خود به او علم [لدنی‌] آموخته بودیم‌'),
(2206, 18, 66, 'موسی به او گفت آیا می‌توانم از شما پیروی کنم که از بینشی که آموخته‌ای به من نیز بیاموزی؟'),
(2207, 18, 67, '[خضر] گفت تو هرگز همپای من صبر نتوانی کرد'),
(2208, 18, 68, 'و چگونه درباره چیزی که به آن علم و احاطه نداری، صبر ورزی؟'),
(2209, 18, 69, '[موسی‌] گفت ان شاء الله مرا شکیبا خواهی یافت، و از امر تو سرپیچی نمی‌کنم‌'),
(2210, 18, 70, 'گفت اگر از من پیروی می‌کنی از هیچ چیز از من سؤال مکن تا آنکه درباره آن با تو سخن بگویم‌'),
(2211, 18, 71, 'پس رهسپار شدند تا آنکه سوار کشتی‌ای شدند [و خضر] آن را سوراخ کرد [موسی‌] گفت سوراخش کردی که سرنشینانش را غرق کنی؟ عجب کار هول‌انگیزی کردی‌'),
(2212, 18, 72, '[خضر] گفت مگر نگفتمت که تو همپای من صبر نتوانی کرد'),
(2213, 18, 73, 'گفت مرا به خاطر آنچه فراموش کردم مؤاخذه مکن و کار را بر من سخت مگیر'),
(2214, 18, 74, 'باز رهسپار شدند تا آنکه به جوانی برخوردند و [خضر] او را کشت [موسی‌] گفت آیا انسان بیگناهی را بدون آنکه قصاصی در بین باشد، کشتی، به راستی کار ناپسندیده‌ای کردی‌'),
(2215, 18, 75, '[خضر] گفت مگر نگفتمت که تو همپای من صبر نتوانی کرد؟'),
(2216, 18, 76, 'گفت اگر بعد از این از تو درباره چیزی پرس و جو کردم با من همراهی مکن، که دیگر [در ترکم‌] معذور خواهی بود'),
(2217, 18, 77, 'باز رهسپار شدند تا آنکه به اهل شهری رسیدند و از مردمش خوراکی خواستند، آنان از مهمان کردنشان، ابا کردند، سپس دیواری را دیدند که می‌خواست فرو ریزد [خضر] آن را برپا داشت [موسی‌] گفت اگر می‌خواستی برای این کار از آنان مزدی می‌گرفتی‌'),
(2218, 18, 78, '[خضر] گفت اینجا دیگر [هنگام‌] جدایی من و توست، هم‌اکنون تو را از معنای آنچه بر آن صبر نتوانستی کرد، آگاه می‌سازم‌'),
(2219, 18, 79, 'اما کشتی متعلق به بینوایانی بود که خود [یا برای آنان‌] در دریا کار می‌کردند، پس خواستم آن را عیب‌ناک کنم، و پادشاهی در پیشاروی آنان بود که هر کشتی [سالمی‌] را به زور می‌گرفت‌'),
(2220, 18, 80, 'و اما آن جوان، پدر و مادرش مؤمن بودند و ما بیمناک شدیم که مبادا کفر و طغیانی بر آنان تحمیل کند'),
(2221, 18, 81, 'و خواستیم که پروردگارشان به جای او برای ایشان فرزندی پاکنهادتر و مهربان‌تر جانشین گرداند'),
(2222, 18, 82, 'و اما دیوار متعلق به دو جوان یتیم در آن شهر بود و زیر آن گنجی از آن ایشان بود و پدرشان مردی صالح بود، و پروردگارت خواست که آنان به کمال بلوغشان برسند و آنگاه گنجشان را [از آنجا] بیرون آوردند، که رحمتی از پروردگارت [در حق آنان‌] بود، و من آن کار را از پیش خود نکردم، این معنای چیزی است که نتوانستی بر آن صبر کنی‌'),
(2223, 18, 83, 'و از تو درباره ذوالقرنین می‌پرسند، بگو هم‌اکنون یادی از او برای شما می‌خوانم‌'),
(2224, 18, 84, 'ما به او در روی زمین تمکن داده بودیم و سررشته هر کاری را به او بخشیده بودیم‌'),
(2225, 18, 85, 'و او سررشته [کار خود] را دنبال گرفت‌'),
(2226, 18, 86, 'تا آنکه به سرزمین مغرب [خورشید] رسید و چنین یافت که در چشمه‌ای گل‌آلود [و گرم‌] غروب می‌کند و در نزدیکی آن قومی را یافت گفتیم ای ذوالقرنین [اختیار با توست‌] یا آنان را عذاب می‌کنی، یا با آنان نیکی می‌کنی‌'),
(2227, 18, 87, 'گفت هرکس شرک ورزد، زودا که عذابش کنیم، سپس به سوی پروردگارش باز برده می‌شود، و او به عذابی سخت معذبش می‌دارد'),
(2228, 18, 88, 'و اما هرکس ایمان آورد و نیکوکاری کند، او را پاداش نیکو باشد و کار را بر او آسان می‌گیریم‌'),
(2229, 18, 89, 'آنگاه سررشته [کار خود] را دنبال گرفت‌'),
(2230, 18, 90, 'تا آنکه به سرزمین مشرق [خورشید] رسید و آن را چنین یافت که بر مردمانی که در برابر [تابش‌] آن پوششی برایشان نگذاشته بودیم، می‌تافت‌'),
(2231, 18, 91, 'بدین‌سان از کار و بار او آگاهی داشتیم‌'),
(2232, 18, 92, 'آنگاه سررشته [کار خود] را دنبال گرفت‌'),
(2233, 18, 93, 'تا به فاصله میان دو کوه سدآسا رسید و در پیش آن مردمانی را یافت که زبانی نمی‌فهمیدند'),
(2234, 18, 94, 'گفتند ای ذوالقرنین قوم یاجوج و ماجوج در این سرزمین فتنه و فساد برپا می‌کنند، آیا [می‌خواهی‌] خراجی به تو بپردازیم که بین ما و آنان سدی بسازی؟'),
(2235, 18, 95, 'گفت تمکنی که پروردگارم به من داده است بهتر [از خراج شما] است، ولی مرا به نیرو[ی انسانی‌] یاری دهید که بین شما و ایشان حایلی بسازم‌'),
(2236, 18, 96, '[آنگاه که شالوده را ریختند گفت] برایم پاره‌های آهن بیاورید [و برهم بینبارید] تا آنکه بین دو کوه را انباشت و همسطح ساخت گفت [در کوره‌های آتش‌] بدمید [و دمیدند] تا آنکه آن [آهن‌] را [گداخته و] آتش گونه ساخت گفت اینک برایم روی گداخته بیاورید تا بر آن بریزم‌'),
(2237, 18, 97, '[سد سکندری ساخته شد و یاجوج و ماجوج‌] نتوانستند بر آن دست یابند و نتوانستند در آن رخنه کنند'),
(2238, 18, 98, 'گفت این رحمتی از سوی پروردگار من است چون وعده پروردگارم [قیامت‌] فرارسد، آن را پخش و پریشان کند و وعده پروردگار من حق است‌'),
(2239, 18, 99, 'و آن روز آنان را رها کنیم که در هم و برهم شوند، [و آنگاه‌] در صور دمیده شود و آنان را چنانکه باید گرد آوریم‌'),
(2240, 18, 100, 'و در آن روز جهنم را بر کافران چنانکه باید و شاید بنمایانیم‌'),
(2241, 18, 101, 'همان کسانی که دیدگانشان در پرده [غفلت‌] از آیات من بود، و نمی‌توانستند [حق را] بشنوند'),
(2242, 18, 102, 'آیا کافران پنداشته‌اند که بندگان مرا [به ناحق‌] به جای من به دوستی بگیرند، [باید بدانند که‌] ما جهنم را چون منزلگاهی برای کافران آماده کرده‌ایم‌'),
(2243, 18, 103, 'بگو آیا از زیانکارترین انسانها آگاهتان کنیم؟'),
(2244, 18, 104, 'کسانی‌[اند] که کوشش آنان در راه زندگانی دنیا، نقش بر آب شده است و ایشان چنین می‌انگارند که نیکو کردارند'),
(2245, 18, 105, 'اینان کسانی هستند که آیات پروردگارشان و لقای او را انکار کرده‌اند، و اعمالشان تباه شده، لذا روز قیامت وزنی برای آنان قائل نیستیم‌'),
(2246, 18, 106, 'این چنین است که به خاطر کفری که ورزیده‌اند و آیات و پیامبران مرا به ریشخند گرفته‌اند، جزای آنان جهنم است‌'),
(2247, 18, 107, 'بی‌گمان منزلگاه کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، باغهای فردوس است‌'),
(2248, 18, 108, 'که جاودانه در آنند [و] از آنجا گرایش به هیچ جا ندارند'),
(2249, 18, 109, 'بگو اگر دریا برای [نوشتن‌] کلمات پروردگارم مرکب باشد، بی‌شک آن دریا، پیش از به پایان رسیدن کلمات پروردگارم، به پایان می‌رسد، ولو آنکه مددی همانند آن به میان آوریم‌'),
(2250, 18, 110, 'بگو من بشری همانند شما هستم، با این تفاوت که به من وحی می‌شود که خدای شما خدای یگانه است، حال هر آن کس که امید در لقای پروردگارش بسته است، کار نیکو پیشه کند و در پرستش پروردگارش کسی را شریک نیاورد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کهیعص‌'),
(2252, 19, 2, '[کاف ها یا عین صاد] این بیان رحمت پروردگارت در حق بنده‌اش زکریاست‌'),
(2253, 19, 3, 'چنین بود که به ندایی خاموش پروردگارش را به دعا خواند'),
(2254, 19, 4, 'گفت پروردگارا استخوانم سستی گرفته و برف پیری بر سرم نشسته است، و پروردگارا هرگز در دعای تو سخت دل نبوده‌ام‌'),
(2255, 19, 5, 'و من پس از خویش از وارثان بیمناکم، و همسرم نازا است، پس از پیشگاه خود به من وارثی عطا فرما'),
(2256, 19, 6, 'تا هم وارث من باشد و هم وارث آل یعقوب، و پروردگارا او را مقبول بگردان‌'),
(2257, 19, 7, '[فرمود] ای زکریا ما تو را به پسری که نامش یحیی است و تاکنون همنامی برایش قرار نداده‌ایم، مژده می‌دهیم‌'),
(2258, 19, 8, 'گفت پروردگارا چگونه مرا پسری باشد، حال آنکه همسرم نازا است و خود نیز از پیری به فرتوتی و فرسودگی رسیده‌ام‌'),
(2259, 19, 9, '[فرشته‌] گفت این چنین است، پروردگارت فرموده است آن کار بر من آسان است، و خودت را پیشتر آفریده‌ام و چیزی نبودی‌'),
(2260, 19, 10, 'گفت پروردگارا برای من نشانه‌ای قرار بده فرمود نشانه تو این است که سه شب [و روز] در عین سلامت، با مردم نتوانی سخن گفت‌'),
(2261, 19, 11, '[زکریا] از محراب به نزد قومش بیرون آمد، و به آنان اشاره کرد که بامدادان و شامگاهان [او را] نیایش کنید'),
(2262, 19, 12, '[گفتیم‌] ای یحیی کتاب آسمانی را به جد و جهد بگیر، و به او در عهد صباوت نبوت بخشیدیم‌'),
(2263, 19, 13, 'و از سوی خویش بر او رحمت آوردیم و پاکیزه‌اش داشتیم و او پرهیزگار بود'),
(2264, 19, 14, 'و در حق پدر و مادرش نیکوکار بود و زورگوی سرکش نبود'),
(2265, 19, 15, 'و بر او در روزی که زاد، و روزی که در می‌گذرد و روزی که زنده برانگیخته می‌شود، درود باد'),
(2266, 19, 16, 'و در کتاب از مریم یاد کن آنگاه که از خاندان خویش، در گوشه‌ای شرقی، کناره گرفت‌'),
(2267, 19, 17, 'و از آنان پنهان شد، آنگاه روح خویش [جبرئیل‌] را به سوی او فرستادیم که به صورت انسانی معتدل به دیده او درآمد'),
(2268, 19, 18, '[مریم‌] گفت من از تو اگر پرهیزگار باشی به خدای رحمان پناه می‌برم‌'),
(2269, 19, 19, 'گفت من فقط فرستاده پروردگارت هستم، تا به تو پسری پاکیزه ببخشم‌'),
(2270, 19, 20, '[مریم‌] گفت چگونه مرا پسری باشد، و حال آنکه هیچ بشری به من دست نزده است و من پلیدکار نبوده‌ام‌'),
(2271, 19, 21, 'گفت همین است، پروردگارت فرموده است آن کار بر من آسان است، تا او را پدیده شگرفی برای مردم قرار دهیم و رحمتی از ماست و کاری انجام یافتنی است‌'),
(2272, 19, 22, 'سپس [مریم‌] به او باردار شد و با او در جایی دوردست کناره گرفت‌'),
(2273, 19, 23, 'آنگاه درد زایمان او را به پناه تنه درخت خرمایی کشانید گفت ای کاش پیش از این مرده بودم و از یاد رفته بودم و فراموش شده بودم‌'),
(2274, 19, 24, 'از فرودست او ندا در داد که اندوهگین مباش، پروردگارت از فرودست تو جویباری روان کرده است‌'),
(2275, 19, 25, 'و تنه درخت خرما را به سوی خود تکان بده تا بر تو رطب تازه چیده فرو ریزد'),
(2276, 19, 26, 'پس بخور و بیاشام و دیده روشن دار، آنگاه اگر انسانی را دیدی بگو برای خداوند رحمان روزه‌[ی سکوت‌] گرفته‌ام و هرگز امروز با هیچ انسانی سخن نمی‌گویم‌'),
(2277, 19, 27, 'سپس او [عیسی‌] را برداشت و به نزد قومش آورد گفتند ای مریم کار شگرفی پیش آوردی‌'),
(2278, 19, 28, 'ای خواهر هارون نه پدرت مردی نابکار، و نه مادرت پلیدکار بود'),
(2279, 19, 29, 'آنگاه [مریم‌] به او [نوزاد] اشاره کرد گفتند چگونه با کودکی که در گهواره است، سخن بگوییم‌'),
(2280, 19, 30, '[نوزاد به سخن درآمده و] گفت من بنده خداوندم که به من کتاب آسمانی داده است و مرا پیامبر گردانیده است‌'),
(2281, 19, 31, 'و مرا، هر جا که باشم، مبارک گردانیده و مرا مادام که زنده باشم به نماز و زکات سفارش فرموده است‌'),
(2282, 19, 32, 'و نیز مرا در حق مادرم نیکوکار گردانده و مرا زورگوی سخت دل نگردانده است‌'),
(2283, 19, 33, 'و بر من در روزی که زادم و در روزی که درگذرم و روزی که زنده برانگیخته شوم درود باد'),
(2284, 19, 34, '[آری‌] این است عیسی بن مریم، این سخن راست و درستی است که آنان در آن شک و شبهه دارند'),
(2285, 19, 35, 'سزاوار نیست که خداوند فرزندی برگیرد، او منزه است، آنگاه که کاری را مقرر فرماید فقط به آن می‌گوید، موجود شو، و بی‌درنگ موجود می‌شود'),
(2286, 19, 36, 'و خداوند پروردگار من و پروردگار شماست پس او را بپرستید که این راه راست است‌'),
(2287, 19, 37, 'ولی گروه منکران در میان خود اختلاف‌ورزیدند، پس وای بر کافران از حضور در روزی سترگ‌'),
(2288, 19, 38, 'روزی که نزد ما آیند چقدر شنوا و چقدر بینا هستند، ولی امروز ستمگران در گمراهی آشکارند'),
(2289, 19, 39, 'و آنان را از روز حسرت بترسان، که امر [الهی‌] تحقق یابد و آنان غافلند و ایمان نیاورند'),
(2290, 19, 40, 'ما زمین را با هر که روی آن است، میراث می‌بریم و به سوی ما بازگردانده شوند'),
(2291, 19, 41, 'و در کتاب آسمانی از ابراهیم یاد کن که صدیقی پیامبر بود'),
(2292, 19, 42, 'آنگاه که به پدرش گفت پدرجان چرا چیزی را می‌پرستی که نمی‌بیند و نمی‌شنود و به حال تو سودی ندارد'),
(2293, 19, 43, 'پدرجان از علم [وحی‌] چیزی به من رسیده است که به تو نرسیده است، پس از من پیروی کن که تو را به راهی راست رهنمایی کنم‌'),
(2294, 19, 44, 'پدرجان شیطان را مپرست، که شیطان در برابر خداوند رحمان سرکش است‌'),
(2295, 19, 45, 'پدرجان می‌ترسم که عذابی از سوی خداوند رحمان به تو برسد و دوستدار شیطان شوی‌'),
(2296, 19, 46, 'گفت ای ابراهیم آیا از خدایان من روی بر می‌تابی؟ اگر دست برنداری سنگسارت می‌کنم و روزگاری دراز از من دور شو'),
(2297, 19, 47, '[ابراهیم‌] گفت سلام بر تو، زودا که از پروردگارم برایت آمرزش خواهم که او به من مهربان است‌'),
(2298, 19, 48, 'و از شما و آنچه به جای خداوند می‌پرستید کناره می‌کنم و پروردگارم را می‌خوانم، باشد که در دعای پروردگارم سخت دل نباشم‌'),
(2299, 19, 49, 'و چون از ایشان و آنچه به جای خداوند می‌پرستیدند، کناره گرفت، به او اسحاق و یعقوب را بخشیدیم و همه را پیامبر گرداندیم‌'),
(2300, 19, 50, 'و به آنان از رحمت خویش بخشیدیم و برای آنان آوازه راستین و بلند پدید آوردیم‌'),
(2301, 19, 51, 'و در کتاب آسمانی از موسی یاد کن که او اخلاص یافته و فرستاده‌ای پیامبر بود'),
(2302, 19, 52, 'و او و او را از جانب طور ایمن ندا دادیم و او را به همرازی خود نزدیک گرداندیم‌'),
(2303, 19, 53, 'و از رحمت خویش برادری چون هارون که پیامبر [و شریک و یاور او] بود به او ارزانی داشتیم‌'),
(2304, 19, 54, 'و در کتاب آسمانی از اسماعیل یاد کن که درست وعده و فرستاده‌ای پیامبر بود'),
(2305, 19, 55, 'و خاندانش را به نماز و زکات امر می‌کرد و نزد پروردگارش مقبول بود'),
(2306, 19, 56, 'و در کتاب آسمانی از ادریس یاد کن که صدیقی پیامبر بود'),
(2307, 19, 57, 'و او را بلندمرتبه گرداندیم‌'),
(2308, 19, 58, 'اینان کسانی هستند از پیامبران، از زاد و رود آدم و از کسانی که همراه نوح در کشتی سوار کردیم و از زاد و رود ابراهیم و اسرائیل [ یعقوب‌] و از کسانی که هدایت کرده‌ایم و بر گزیده‌ایم، که خداوند به آنان انعام و اکرام فرموده است، که چون آیات خداوند رحمان بر آنان خوانده، می‌شد، گریان به سجده می‌افتادند'),
(2309, 19, 59, 'ولی پس از آنان جانشینان ناخلفی بازماندند که نماز را فرو گذاشتند و از شهوات پیروی کردند، و زودا که با زیان و ذلت رو در رو شوند'),
(2310, 19, 60, 'مگر کسی که توبه کند و ایمان آورد و کاری شایسته پیشه کند، اینانند که وارد بهشت می‌شوند و هیچ‌گونه ستم [و کم و کاستی‌] نمی‌بینند'),
(2311, 19, 61, 'همان جنات عدن که خداوند رحمان به نادیده به بندگانش وعده داده است، همانا که وعده او وفا خواهد شد'),
(2312, 19, 62, 'در آنجا هیچ‌گونه لغوی نشنوند و جز سلام نشنوند، و بامدادان و شامگاهان در آنجا رزقشان برقرار است‌'),
(2313, 19, 63, 'این بهشتی است که به بندگان پرهیزگار خود میراث می‌دهیم‌'),
(2314, 19, 64, '[جبرئیل گفت‌] و ما جز به فرمان پروردگار فرود نمی‌آییم، او حاکم آینده و گذشته و حال ماست و پروردگارت فراموشکار نیست‌'),
(2315, 19, 65, 'پروردگار آسمانها و زمین و آنچه در میان آنهاست، پس او را بپرست، و در عبادت او شکیبایی پیشه کن، آیا همنام و همانندی بر او می‌شناسی؟'),
(2316, 19, 66, 'و انسان [منکر] گوید آیا چون مردم به زودی زنده برانگیخته خواهم شد؟'),
(2317, 19, 67, 'آیا [این‌] انسان نمی‌اندیشد که ما در گذشته او را آفریده‌ایم و حال آنکه چیزی نبود'),
(2318, 19, 68, 'سوگند به پروردگارت که آنان و شیاطین را گرد می‌آوریم سپس همه‌شان را پیرامون جهنم به زانو در افتاده حاضر می‌گردانیم‌'),
(2319, 19, 69, 'آنگاه از میان آنان از هر فرقه‌ای هر کدام را که در برابر خدای رحمان سرکش‌ترند جدا می‌سازیم‌'),
(2320, 19, 70, 'آنگاه ما بهتر می‌دانیم که کدامشان سزاوارتر به درافتادن به میان آن هستند'),
(2321, 19, 71, 'و هیچ‌کس از شما نیست مگر آنکه وارد آن خواهد شد، این امر بر پروردگارت قضای حتمی است‌'),
(2322, 19, 72, 'سپس کسانی را که پروا پیشه کرده‌اند، می‌رهانیم و ستم‌پیشگان را در آنجا به زانو درافتاده فرو می‌گذاریم‌'),
(2323, 19, 73, 'و چون آیات روشنگر ما بر آنان خوانده شود، کفرپیشگان به مؤمنان گویند، باید دید کدام یک از دو گروه [از ما یا شما] نیکمرتبه‌تر و مجلس‌آراترست‌'),
(2324, 19, 74, 'و چه بسیار پیش از آنان نسلهایی را که خوش‌ساز و برگ‌تر و خوش‌نماتر بودند، نابود کردیم‌'),
(2325, 19, 75, 'بگو هرکس که ضلالت را برگزیند، خداوند رحمان [از روی استدراج‌] به او افزونی بخشد تا آنکه آنچه به ایشان وعده داده‌اند، چه عذاب [الهی‌]، چه قیام قیامت ببینند، و زودا خواهند دانست که چه کسی بدمرتبه‌تر و سست نیروتر است‌'),
(2326, 19, 76, 'و خداوند بر هدایت رهیافتگان بیفزاید و کارهای ماندگار شایسته در نزد پروردگارت خوش پاداش‌تر و خوش عاقبت‌تر است‌'),
(2327, 19, 77, 'آیا ننگریسته‌ای کسی را که به آیات ما کفر [و انکار]ورزید و ادعا کرد که به من مال و فرزند داده خواهد شد'),
(2328, 19, 78, 'آیا از غیب اطلاع داشت، یا از خدای رحمان پیمانی [خاص‌] گرفته بود؟'),
(2329, 19, 79, 'چنین نیست، گفته‌اش را باز می‌نویسیم و به عذاب او می‌افزاییم‌'),
(2330, 19, 80, 'و مدعیاتش را میراث می‌بریم و به نزد ما یکه و تنها خواهد آمد'),
(2331, 19, 81, 'و به جای خداوند خدایانی را به پرستش گرفتند که مایه عزت و اعتبار آنان باشد'),
(2332, 19, 82, 'چنین نیست، زودا که [آن خدایان‌] پرستش ایشان را انکار کنند و مخالف آنان باشند'),
(2333, 19, 83, 'آیا ندانسته‌ای که ما شیاطین را به سراغ کافران می‌فرستیم که از راه به درشان برند'),
(2334, 19, 84, 'در کارشان شتاب مکن، ما حساب کارشان را داریم‌'),
(2335, 19, 85, 'روزی [آید] که پرهیزگاران را چون مهمانان گرامی به نزد خدای رحمان محشور سازیم‌'),
(2336, 19, 86, 'و گناهکاران را به هیئت [رمه‌ای‌] پیاده و تشنه به سوی جهنم برانیم‌'),
(2337, 19, 87, 'از شفاعت برخوردار نیستند، مگر کسی که [با ایمان و توحید] از خدای رحمان پیمانی گرفته باشد'),
(2338, 19, 88, 'و ادعا کردند که خدای رحمان فرزندی برگزیده است‌'),
(2339, 19, 89, 'به راستی که ادعای شگرفی پیش آوردید'),
(2340, 19, 90, 'نزدیک است که آسمانها از [ناروایی‌] آن پاره پاره شود و زمین بشکافد و کوهها فرو ریزند'),
(2341, 19, 91, 'از اینکه برای خدای رحمان فرزندی قائل شدند'),
(2342, 19, 92, 'و سزاوار نیست که خداوند رحمان فرزندی برگزیند'),
(2343, 19, 93, 'جز این نیست که هر موجودی که در آسمانها و زمین است، بنده‌وار سر به درگاه خدای رحمان فرود می‌آورد'),
(2344, 19, 94, 'به راستی همه را شماره کرده و حساب همگیشان را داریم‌'),
(2345, 19, 95, 'و همگیشان روز قیامت یکه و تنها به نزد او آیند'),
(2346, 19, 96, 'کسانی که ایمان آورده و کارهای شایسته کرده‌اند، زودا که خداوند رحمان در حق آنان مهربانی کند'),
(2347, 19, 97, 'همانا آن [قرآن‌] را به زبان تو آسان بیان کردیم تا پرهیزگاران را به آن بشارت دهی و مردم ستیزه‌جو را به آن بیم دهی‌'),
(2348, 19, 98, 'و چه بسیار پیش از آنان نسلهایی را بر انداختیم، آیا هیچ یک از آنان را می‌یابی یا کمترین صدایی از آنان می‌شنوی؟');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طه [طا، ها]'),
(2350, 20, 2, 'قرآن را بر تو نازل نکرده‌ایم که در رنج افتی‌'),
(2351, 20, 3, 'جز این نیست که یادآور کسی است که خشوع و خشیت داشته باشد'),
(2352, 20, 4, 'فرو فرستاده‌ای است از سوی کسی که زمین و آسمانهای برافراشته را آفریده است‌'),
(2353, 20, 5, 'خداوند رحمان بر عرش استیلاء یافت‌'),
(2354, 20, 6, 'او راست هر آنچه در آسمانها و در زمین و بین آنها و در زیر زمین است‌'),
(2355, 20, 7, 'و اگر سخنت را آشکار کنی [یا پوشیده بداری‌] بدان که او هر راز و هر نهفته‌ای را می‌داند'),
(2356, 20, 8, 'خداوند است که خدایی جز او نیست، او را نامهای نیکوست‌'),
(2357, 20, 9, 'و آیا داستان موسی به تو رسیده است؟'),
(2358, 20, 10, 'چنین بود که [از دور] آتشی دید و به خانواده‌اش گفت بایستید که من آتشی می‌بینم، باشد که اخگری از آن برای شما بیاورم، یا در پرتو آن راه را باز یابم‌'),
(2359, 20, 11, 'و چون به نزدیک آن رسید، ندا در داده شد که ای موسی‌'),
(2360, 20, 12, 'همانا من پروردگار تو هستم کفشهایت را [به احترام‌] از پا بیرون کن و بدان که تو در وادی مقدس طوی هستی‌'),
(2361, 20, 13, 'و من تو را برگزیده‌ام، پس به آنچه وحی می‌شود، گوش دل بسپار'),
(2362, 20, 14, 'همانا من خداوندم که جز من خدایی نیست، پس مرا بپرست و نماز را به یاد من برپا دار'),
(2363, 20, 15, 'قیامت فرارسنده است، می‌خواهم [چندی هم‌] پنهانش بدارم تا هر کسی بر وفق کوششش جزا یابد'),
(2364, 20, 16, 'مبادا کسی که به آن ایمان ندارد و از هوی و هوس خویش پیروی می‌کند، تو را از آن باز دارد و به هلاکت افتی‌'),
(2365, 20, 17, 'و ای موسی در دستت چیست؟'),
(2366, 20, 18, 'گفت این عصای من است که بر آن تکیه می‌کنم و با آن برای گوسفندانم برگ فرو می‌تکانم، و حاجتهای دیگر نیز به آن دارم‌'),
(2367, 20, 19, 'فرمود ای موسی آن را به زمین بینداز'),
(2368, 20, 20, 'آن را انداخت و ناگهان به هیئت ماری که جنب و جوش داشت در آمد'),
(2369, 20, 21, 'فرمود آن را بگیر و مترس، آن را به هیئت نخستینش در می‌آوریم‌'),
(2370, 20, 22, 'و دستت را در بغلت کن، تا سپید و درخشان بدون هیچ بیماری [پیسی‌] بیرون آید که این نیز معجزه دیگری است‌'),
(2371, 20, 23, 'تا بعضی از آیات سترگ خود را به تو بنمایانیم‌'),
(2372, 20, 24, 'به سوی فرعون برو که او سر به طغیان برداشته است‌'),
(2373, 20, 25, 'گفت پروردگارا دل مرا برایم گشاده‌دار'),
(2374, 20, 26, 'و کارم را بر من آسان کن‌'),
(2375, 20, 27, 'و گره از زبانم بگشا'),
(2376, 20, 28, 'تا سخنم را دریابند'),
(2377, 20, 29, 'و از خانواده‌ام برایم دستیاری بگمار'),
(2378, 20, 30, 'برادرم هارون را'),
(2379, 20, 31, 'و با او پشتوانه‌ام را نیرومند گردان‌'),
(2380, 20, 32, 'و او را در کارم شریک گردان‌'),
(2381, 20, 33, 'تا تو را بسیار نیایش کنیم‌'),
(2382, 20, 34, 'و بسیار یادت کنیم‌'),
(2383, 20, 35, 'که تو خود بر احوال ما بینا بوده‌ای‌'),
(2384, 20, 36, 'فرمود ای موسی خواسته‌ات بر آورده شد'),
(2385, 20, 37, 'و بار دیگر هم در حق تو نیکی کرده بودیم‌'),
(2386, 20, 38, 'آنگاه که به مادرت آنچه باید وحی کردیم‌'),
(2387, 20, 39, 'که او را در صندوقی بگذار و آن را در دریا بیفکن، تا دریا او را به ساحل افکند، تا سرانجام دشمن من و دشمن او، او را بیابد و برگیرد، و در حقت مهربانی کردم تا زیر نظر من بار آیی‌'),
(2388, 20, 40, 'چنین بود که خواهرت [سرگشته‌] می‌رفت و می‌گفت آیا کسی را به شما نشان دهم که سرپرستی او را عهده‌دار شود؟ [گفتند آری‌] و [سرانجام‌] تو را به آغوش مادرت باز گرداندیم که دیده‌اش روشن شود و اندوهگین نگردد، [سپس که بزرگ شدی‌] کسی را [به غیر عمد] کشتی، و تو را از غم و غصه رهانیدیم و چنانکه باید و شاید آزمودیم، سپس چندی در میان اهل مدین به سر بردی، سپس بهنگام [برای رسالت‌] آمدی‌'),
(2389, 20, 41, 'و تو را برای خود پروردم‌'),
(2390, 20, 42, 'تو و برادرت نشانه‌های معجزه‌وار مرا ببرید و در یاد کرد من سستی‌مورزید'),
(2391, 20, 43, 'به سوی فرعون بروید که سر به طغیان برداشته است‌'),
(2392, 20, 44, 'و با او سخنی نرم بگویید، باشد که پند گیرد یا خشوع و خشیت یابد'),
(2393, 20, 45, 'گفتند پروردگارا ما می‌ترسیم که بر ما پیشدستی یا گردنکشی کند'),
(2394, 20, 46, 'فرمود مهراسید من خود با شما هستم و می‌شنوم و می‌بینم‌'),
(2395, 20, 47, 'پس به نزد او بروید و بگویید ما فرستادگان پروردگارت هستیم، بنی‌اسرائیل را همراه ما بفرست، و آزارشان مکن، ما برای تو از سوی پروردگارت پدیده‌ای معجزه‌آسا آورده‌ایم، و سلام بر کسی که از هدایت پیروی کند'),
(2396, 20, 48, 'به راستی به ما وحی شده است که عذاب بر کسی نازل می‌شود که تکذیب پیشه کند و روی بگرداند'),
(2397, 20, 49, 'گفت ای موسی پروردگار شما کیست؟'),
(2398, 20, 50, 'گفت پروردگار ما همان کسی است که به هر چیز آفرینش سزاوار او را بخشیده سپس هدایتش کرده است‌'),
(2399, 20, 51, 'گفت پس سرنوشت اقوام پیشین چه می‌شود؟'),
(2400, 20, 52, 'گفت علم آن با پروردگار من و در کتابی مکتوب است، و پروردگار من نه فرو می‌گذارد و نه فراموش می‌کند'),
(2401, 20, 53, 'همان کسی که زمین را زیرانداز شما کرد و در آن برای شما راهها کشید و از آسمان آبی فرو فرستاد و با آن از هر گونه گیاه گوناگون برآوردیم‌'),
(2402, 20, 54, 'بخورید و چارپایانتان را بچرانید، که در این مایه‌های عبرت برای خردمندان است‌'),
(2403, 20, 55, 'از آن [خاک‌] شما را آفریده‌ایم، و به آن بازتان می‌گردانیم، و بار دیگر از آن بیرونتان می‌آوریم‌'),
(2404, 20, 56, 'و همه پدیده‌های شگرف خویش را به او نمایاندیم ولی دروغ انگاشت و سر باززد'),
(2405, 20, 57, 'گفت ای موسی آیا آمده‌ای که ما را با جادوی خود از سرزمینمان آواره کنی؟'),
(2406, 20, 58, 'بدان که ما نیز جادویی همانند آن برایت به میان می‌آوریم، پس بین ما و خودت موعدی بگذار در مکانی مقبول هر دو طرف که ما و تو در آن خلاف نکنیم‌'),
(2407, 20, 59, 'گفت موعدتان روز جشن باشد که مردمان نیز در روز گرد آیند'),
(2408, 20, 60, 'فرعون برگشت و همه مکر و تدبیر خود را به کار برد و به میان آمد'),
(2409, 20, 61, 'موسی به ایشان گفت وای بر شما، بر خداوند افتراء مزنید که شما را با عذابی ریشه کن می‌سازد و هرکس افتراء پیشه کند نومید گردد'),
(2410, 20, 62, 'آنان در میان خود درباره کار و بارشان اختلاف پیدا کردند و رازگویی‌شان را پنهان داشتند'),
(2411, 20, 63, '[و] ادعا کردند این دو جادوگرانی هستند که می‌خواهند با جادوشان شما را از سرزمینتان آواره کنند و آیین پسندیده شما را از بین ببرند'),
(2412, 20, 64, 'پس همفکری کنید و هماهنگ عمل کنید و امروزه هر که چیره شود، رستگار است‌'),
(2413, 20, 65, 'گفتند ای موسی تو [اول‌] می‌اندازی یا ما اول بیندازیم؟'),
(2414, 20, 66, 'گفت شما بیندازید، و [انداختند و] ناگهان از جادوی آنان چنین به نظرش آمد که ریسمانهایشان و چوبدستی‌هایشان [مار شده و] جنب و جوش دارد'),
(2415, 20, 67, 'پس موسی در دل خود بیمی احساس کرد'),
(2416, 20, 68, 'گفتیم مترس که تو برتری‌'),
(2417, 20, 69, 'و آنچه در دست داری بینداز تا برساخته‌هایشان را ببلعد، که آنچه برساخته‌اند، نیرنگ جادوگر است و جادوگر هر چه کند رستگار نمی‌شود'),
(2418, 20, 70, 'آنگاه جادوگران به سجده در افتادند [و] گفتند به پروردگار هارون و موسی ایمان آوردیم‌'),
(2419, 20, 71, '[فرعون‌] گفت آیا پیش از آنکه به شما اجازه دهم به او ایمان آوردید، بی‌شک او بزرگتر [و آموزگار] شماست که به شما جادوگری آموخته است، بدانید که دستها و پاهایتان را بر خلاف جهت یکدیگر می‌برم و شما را بر تنه‌های درخت خرما به دار می‌کشم، و خواهید دانست که کداممان عذابی شدیدتر و پاینده‌تر دارد'),
(2420, 20, 72, '[جادوگران‌] گفتند هرگز تو را بر روشنگریهایی که برای ما آمده است، و بر کسی که ما را آفریده است، بر نمی‌گزینیم، هر چه خواهی بکن، جز این نیست که تو فقط در زندگی دنیوی کاری توانی کرد'),
(2421, 20, 73, 'ما به پروردگارمان ایمان آورده‌ایم تا گناهانمان را و همین جادویی را که تو به آن وادارمان کرده‌ای ببخشد و خداوند است که بهتر و پاینده‌تر است‌'),
(2422, 20, 74, 'به راستی هرکس گناهکار به نزد پروردگارش بیاید، جهنم نصیب اوست که در آن نه می‌میرد و نه زنده می‌ماند'),
(2423, 20, 75, 'و هرکس مؤمن به نزد او بیاید، و کارهای شایسته کرده باشد، اینانند که درجات عالی دارند'),
(2424, 20, 76, '[در] بهشتهای عدن که جویباران از فرودست آن جاری است، و جاودانه در آنند، و این پاداش کسی است که پاکی پیشه کند'),
(2425, 20, 77, 'و به موسی وحی کردیم که بندگان مرا شبانه روانه کن، و برای آنان راهی خشک در دریا بشکاف، به طوری که نه از فرارسیدن [دشمن‌] بیمناک باشی و نه [از غرق‌] بترسی‌'),
(2426, 20, 78, 'آنگاه فرعون با سپاهیانش آنان را دنبال کرد، و آب دریا آنان را فراگرفت و فروپوشاند'),
(2427, 20, 79, 'و [بدین‌سان‌] فرعون قومش را به گمراهی کشاند و به جایی نرساند'),
(2428, 20, 80, 'ای بنی‌اسرائیل به راستی شما را از دشمنتان رهانیدیم و با شما در جانب طور ایمن وعده گذاردیم و بر شما من و سلوی فرو فرستادیم‌'),
(2429, 20, 81, '[و گفتیم‌] از هر آنچه از پاکیزه‌ها روزیتان کرده‌ایم، بخورید و در آن از حد تجاوز مکنید، که خشم من بر شما فرود می‌آید، و هرکس که خشم من بر او فرود آید، به راستی نابود شود'),
(2430, 20, 82, 'و من در حق کسی که توبه کند و ایمان آورد و کاری شایسته پیش گیرد و به راه آید آمرزگارم‌'),
(2431, 20, 83, 'و [گفتیم‌] ای موسی چه چیز باعث شد که بر قومت پیشی بگیری؟'),
(2432, 20, 84, 'گفت آنان به دنبال من روانند و من پروردگارا به سوی تو شتافتم تا از من خشنود باشی‌'),
(2433, 20, 85, 'فرمود ما قومت را در غیاب تو امتحان کردیم و سامری آنان را گمراه ساخت‌'),
(2434, 20, 86, 'آنگاه موسی خشمگین و اندوهگین [و شتابان‌] به سوی قومش بازگشت و گفت ای قوم من آیا پروردگارتان با شما وعده‌ای نیکو نگذارد، آیا به نظرتان این عهد طولانی شد، یا خواستید که خشم پروردگارتان بر شما فرود آید، که در وعده‌تان با من خلاف کردید'),
(2435, 20, 87, 'گفتند، در وعده تو به اختیار خود خلاف نکردیم، بلکه از زر و زیور مردم انبوهی برگرفتیم و آنها را [در آتش‌] انداختیم، و سامری چنین راهنمایی کرد'),
(2436, 20, 88, 'و [چنین بود که سامری‌] برای آنان پیکر گوساله‌ای ساخت و پرداخت که بانگ گاو داشت، آنگاه گفتند این خدای شما و موسی است که فراموشش کرده بود'),
(2437, 20, 89, 'آیا اینان نیندیشیدند که [این گوساله‌] پاسخ سخنی به آنان نمی‌دهد و زیان و سودی برای آنان ندارد؟'),
(2438, 20, 90, 'و هارون پیش از آن به آنان گفته بود، ای قوم من شما با آن امتحان پس می‌دهید، و پروردگار [حقیقی‌] شما خداوند رحمان است، از من پیروی و از دستور من اطاعت کنید'),
(2439, 20, 91, 'گفتند همچنان در خدمت او [گوساله‌] می‌ایستیم تا موسی به نزد ما بازگردد'),
(2440, 20, 92, '[موسی آمد و برآشفت و گفت‌] ای هارون چون دیدی که گمراه شده‌اند'),
(2441, 20, 93, 'چه چیزی تو را از متابعت من بازداشت؟ آیا از دستور من سرپیچی کردی؟'),
(2442, 20, 94, '[هارون‌] گفت ای پسر مادرم ریش مرا و سرم را مگیر [و با من درشتی مکن‌] من ترسیدم که بگویی بین بنی‌اسرائیل تفرقه انداختی و سخن مرا پاس نداشتی‌'),
(2443, 20, 95, '[سپس رو به سامری کرد و] گفت ای سامری کار و بار تو چیست؟'),
(2444, 20, 96, '[سامری‌] گفت من چیزی را دیدم که دیگران ندیده بودند، و مشتی از خاک پای جبرئیل برگرفتم، و آن را [در خمیر مایه گوساله‌] انداختم، و بدین‌سان بود که نفسم بدی را به من آراسته جلوه داد'),
(2445, 20, 97, 'گفت پس برو و بدان که جزای تو این است که در زندگی بگویی به من نزدیک مشوید، و برای تو موعدی است که در آن با تو خلاف نشود، و به "خدایت" که در خدمتش معتکف بودی بنگر که می‌سوزانیمش، سپس آن را بر دریا می‌افشانیم‌'),
(2446, 20, 98, 'همانا خدای شما خداوند است که خدایی جز او نیست، که علمش بر همه چیز احاطه دارد'),
(2447, 20, 99, 'بدین‌سان بر تو از اخبار گذشته می‌خوانیم و به راستی از سوی خویش به تو پندآموزی بخشیده‌ایم‌'),
(2448, 20, 100, 'هرکس از آن روی برتابد، روز قیامت بار گناهی بر دوش کشد'),
(2449, 20, 101, 'جاودانه در آن [عذاب‌] می‌مانند و در روز قیامت سربار بدی برای آنهاست‌'),
(2450, 20, 102, 'همان روزی که در صور دمیده شود، و ما گناهکاران را در آن روز سبز چشم محشور گردانیم‌'),
(2451, 20, 103, 'آهسته در میان خود سخن گویند که جز ده روز [در دنیا] به سر نبرده‌اید'),
(2452, 20, 104, 'ما به آنچه می‌گویند آگاهتریم آنگاه که رهیافته‌ترین آنها به ایشان گوید جز [به اندازه‌] یک روز به سر نبرده‌اید'),
(2453, 20, 105, 'و از تو درباره کوهها می‌پرسند بگو پروردگارم آنها را پخش و پریشان می‌کند'),
(2454, 20, 106, 'و زمین را همچون دشتی هموار رها می‌کند'),
(2455, 20, 107, 'که در آن نه نشیبی می‌بینی و نه فرازی‌'),
(2456, 20, 108, 'در آن روز همه از منادی [اسرافیل‌] پیروی می‌کنند، که در کارش هیچ‌گونه کژی نیست، و صداها همه در برابر خدای رحمان به خاموشی گراید، آنگاه جز نوایی نرم نشنوی‌'),
(2457, 20, 109, 'در آن روز شفاعت سودی ندارد مگر برای کسی که خداوند رحمان به او اجازه دهد و از سخن او خشنود باشد'),
(2458, 20, 110, 'آنچه پیش‌رو و آنچه پشت‌سرشان است، می‌داند و آنان به او احاطه علمی ندارند'),
(2459, 20, 111, 'و سرها در برابر [پروردگار] زنده پاینده فرود آید، و هر کسی بار ستمی برداشته باشد، نومید گردد'),
(2460, 20, 112, 'و هرکس که کارهای شایسته کرده باشد و مؤمن باشد از ستم و کاستی [در پاداشش‌] نترسد'),
(2461, 20, 113, 'و بدین‌سان آن را به صورت قرآنی عربی نازل کردیم و در آن گونه‌گونه هشدار آوردیم، باشد که پروا پیشه کنند، یا پندی برای آنان پدید آورد'),
(2462, 20, 114, 'بزرگا خداوندی که پادشاه بر حق است، و در [باز خوانی‌] قرآن پیش از به پایان رسیدن وحی آن شتاب مکن، و بگو پروردگارا مرا دانش‌افزای‌'),
(2463, 20, 115, 'و از پیش به آدم سفارش کردیم، ولی فراموش کرد، و در او عزمی استوار نیافتیم‌'),
(2464, 20, 116, 'و چنین بود که به فرشتگان گفتیم به آدم سجده برید، همه سجده بردند مگر ابلیس که سر باززد'),
(2465, 20, 117, 'آنگاه گفتیم ای آدم این [ابلیس‌] دشمن تو و [دشمن‌] همسرت است، مبادا شما را از بهشت آواره کند که در رنج افتی‌'),
(2466, 20, 118, 'برای تو مقرر است که در آنجا [بهشت‌] گرسنه و برهنه نمایی‌'),
(2467, 20, 119, 'و همچنین آنجا تشنه و آفتاب‌زده نشوی‌'),
(2468, 20, 120, 'سپس شیطان او را وسوسه کرد، گفت ای آدم آیا می‌خواهی درخت جاودانگی و سلطنت بی‌انقراض را نشانت دهم؟'),
(2469, 20, 121, 'آنگاه از آن [میوه ممنوعه‌] خوردند و عورتهایشان بر آنان آشکار شد و بر آنها از برگ [درختان‌] بهشتی می‌چسباندند [تا پوشیده شود] و بدین‌سان آدم از امر پروردگارش سرپیچی کرد و گمراه شد'),
(2470, 20, 122, 'سپس پروردگارش باز او را برگزید و از او درگذشت و هدایتش کرد'),
(2471, 20, 123, '[و] فرمود همگی از آن [بهشت‌] پایین روید -برخی دشمن برخی دیگرو چون از سوی من رهنمودی برایتان آمد، هرکس که رهنمود مرا پیروی کند، نه گمراه شود و نه به رنج افتد'),
(2472, 20, 124, 'و هرکس از یاد من دل بگرداند، زندگانی او تنگ خواهد بود، و او را روز قیامت نابینا برانگیزیم‌'),
(2473, 20, 125, 'گوید پروردگارا چرا مرا نابینا برانگیختی و حال آنکه من بینا بودم‌'),
(2474, 20, 126, 'فرماید بدین‌سان بود که آیات ما برای تو آمد و آنها را فراموش کردی و به همان گونه امروز فراموش شده باشی‌'),
(2475, 20, 127, 'و بدین‌سان هرکس را که از حد درگذرد و به آیات پروردگارش ایمان نیاورده باشد، جزا می‌دهیم، و عذاب آخرت سنگین‌تر و پاینده‌تر است‌'),
(2476, 20, 128, 'آیا برای آنان روشن نشده است که پیش از آنان چه بسیار نسلهایی را نابود کردیم که [آنان اکنون‌] در خانه و کاشانه‌هایشان آمد و رفت می‌کنند، بی‌گمان در این برای خردمندان مایه‌های عبرت است‌'),
(2477, 20, 129, 'و اگر کلمه [/وعده‌] پیشین پروردگارت و اجل معینی در کار نبود، آن عذاب [هم‌اکنون‌] لازم می‌شد'),
(2478, 20, 130, 'پس بر آنچه می‌گویند شکیبایی کن و شاکرانه پروردگارت را پیش از طلوع خورشید و پیش از غروب آن، و در پاسهایی از شب و در دو سوی روز [به نماز برخیز و] تسبیح بگوی، باشد که خشنود شوی‌'),
(2479, 20, 131, 'به چیزی که اصنافی از آنان را به آن بهره‌مند گردانده‌ایم چشم مدوز که تجمل زندگی دنیوی است تا سرانجام آنان را بدان بیازماییم، و روزی پروردگارت بهتر و پاینده‌تر است‌'),
(2480, 20, 132, 'و خانواده‌ات را به نماز فرمان ده و در آن صبورانه بکوش، ما از تو روزی نخواسته‌ایم بلکه ما خود تو را روزی می‌دهیم، و سرانجام نیک، با پروا و پرهیز است‌'),
(2481, 20, 133, 'و گویند چرا پدیده روشنگری از سوی پروردگارش برای ما نمی‌آورد، [بگو] آیا پدیده روشنگری که در کتابهای آسمانی پیشین است برای آنان نیامده است؟'),
(2482, 20, 134, 'و اگر آنان را پیش از آن [آیات بینات‌] به عذابی نابود می‌ساختیم بی‌شک می‌گفتند که پروردگارا چرا پیامبری به سوی ما نفرستادی، تا پیش از آنکه خوار و زار شویم، از آیات تو پیروی کنیم؟'),
(2483, 20, 135, 'بگو همه منتظرند، پس شما هم منتظر باشید، زودا که خواهید دانست رهروان راه راست و رهیافتگان چه کسانی هستند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'مردمان را [هنگام‌] حسابشان نزدیک شده است و ایشان همچنان در غفلت رویگردانند'),
(2485, 21, 2, 'هیچ پند تازه‌ای از سوی پروردگارشان برای آنان نیامد مگر آنکه آن را به بازیچه شنیدند'),
(2486, 21, 3, 'دلبسته لهوند، و ستم‌پیشگان [مشرک‌] رازگویی‌شان را پوشیده داشتند [و گفتند] آیا این [پیامبر] جز بشری همانند شماست، آیا دیده و دانسته به سوی جادو می‌روید؟'),
(2487, 21, 4, 'گفت پروردگار من هر سخن را در آسمان و زمین می‌داند، و اوست شنوای دانا'),
(2488, 21, 5, 'یا اینکه گویند [قرآن‌] خوابهای پریشان است یا [گویند] آن را برساخته است یا [گویند] او شاعری است پس باید مانند آنچه به پیشینیان داده شد معجزه‌ای برای ما بیاورد'),
(2489, 21, 6, 'پیش از آنان هم اهل هیچ شهری که [بعدها] نابودش کردیم، ایمان نیاورده بودند، آیا آن وقت اینان ایمان می‌آورند؟'),
(2490, 21, 7, 'و ما پیش از تو جز مردانی که به آنان وحی می‌فرستادیم، نفرستادیم، اگر نمی‌دانید از پژوهندگان کتابهای آسمانی بپرسید'),
(2491, 21, 8, 'و ما آنان را به صورت پیکری نساخته بودیم که خوراک نخورند و آنان هم جاودانه نبودند'),
(2492, 21, 9, 'سپس وعده خویش را درباره آنان تحقق بخشیدیم و آنان و کسانی را که می‌خواستیم نجات دادیم و اسرافکاران را نابود ساختیم‌'),
(2493, 21, 10, 'به راستی که به سوی شما کتابی فرو فرستاده‌ایم که در آن یاد [و سخن‌] شما هست، آیا تعقل نمی‌کنید؟'),
(2494, 21, 11, 'و چه بسیار شهرهایی را که [اهالی آن‌] ستمگر بودند، درهم شکستیم، و بعد از آنان قومی دیگر پدید آوردیم‌'),
(2495, 21, 12, 'و چون عذاب ما را احساس کردند، آنگاه بود که از آن گریختند'),
(2496, 21, 13, 'نگریزید و به مهد ناز و نعمت و خانه و کاشانه‌تان باز گردید، باشد که از شما بازخواست شود'),
(2497, 21, 14, 'گویند وای بر ما، ما ستمگر [و مشرک‌] بودیم‌'),
(2498, 21, 15, 'و همچنان سخنشان این است تا آنکه آنان را همچون [گیاه‌] درو شده پژمرده گردانیم‌'),
(2499, 21, 16, 'و ما آسمانها و زمین و ما بین آنها را به بازیچه نیافریده‌ایم‌'),
(2500, 21, 17, 'اگر می‌خواستیم که بازیچه بگیریم، به اختیار خویش می‌گرفتیم که ما کاردانیم‌'),
(2501, 21, 18, 'بلکه حق را بر باطل می‌کوبیم و آن را فرو می‌شکافد، آنگاه است که آن نابود می‌گردد، و وای بر شما از توصیفی که می‌کنید'),
(2502, 21, 19, 'و هرکس که در آسمانها و زمین است از آن اوست و کسانی که نزد او هستند از پرستش او سر بازنمی‌زنند و خسته نمی‌شوند'),
(2503, 21, 20, 'شب و روز نیایش می‌کنند و سستی نمی‌ورزند'),
(2504, 21, 21, 'یا خدایانی زمینی به پرستش گرفته‌اند که برانگیزاننده آنان [در قیامت‌]اند؟'),
(2505, 21, 22, 'اگر در آن دو خدایان متعددی جز خداوند [یگانه‌] بود، تباه می‌شدند، خداوند صاحب عرش از توصیف آنان منزه است‌'),
(2506, 21, 23, '[او] در آنچه می‌کند بازخواست نمی‌شود، و ایشان [/ انسانها] بازخواست می‌شوند'),
(2507, 21, 24, 'یا به جای او خدایانی را به پرستش گرفته‌اند؟ بگو برهانتان را بیاورید، این یاد کرد همراهان من و یاد کرد پیشینیان من است، ولی بیشترینه‌شان حق را نمی‌شناسند و رویگردانند'),
(2508, 21, 25, 'و ما پیش از تو هیچ پیامبری نفرستادیم، مگر آنکه به او وحی می‌فرستادیم که خدایی جز من نیست، پس مرا بپرستید'),
(2509, 21, 26, 'و گفتند که خداوند رحمان فرزندی برگزیده است، منزه است او، بلکه آنان [فرشتگان‌] بندگانی گرامی‌اند'),
(2510, 21, 27, 'در سخن بر او پیشدستی نکنند و به فرمان او کار کنند'),
(2511, 21, 28, 'او پیش روی آنان و پشت سرشان را می‌داند، و [فرشتگان‌] شفاعت نمی‌کنند مگر برای کسی که [خداوند] از او خشنود باشد، و ایشان از خوف و خشیت او بیمناکند'),
(2512, 21, 29, 'و هرکس از آنان که گوید من خدایی به جای او هستم، جهنم را جزای او می‌گردانیم، [و] بدین‌سان ستمگران [مشرک‌] را جزا می‌دهیم‌'),
(2513, 21, 30, 'آیا کافران نیندیشیده‌اند که آسمانها و زمین فروبسته بودند، آنگاه آنها را برگشادیم، و هر موجود زنده‌ای را از آب آفریده‌ایم، آیا ایمان نمی‌آورند؟'),
(2514, 21, 31, 'و در زمین کوههای استوار افکنده‌ایم تا ایشان را نجنباند و در آن راههایی گشاده پدید آوردیم باشد که راه یابند'),
(2515, 21, 32, 'و آسمانها را همچون سقفی محفوظ آفریده‌ایم، و حال آنکه ایشان از پدیده‌های شگرف آن رویگردانند'),
(2516, 21, 33, 'و اوست کسی که شب و روز و خورشید و ماه را آفریده، که هر یک در سپهری شناورند'),
(2517, 21, 34, 'و ما پیش از تو هم برای هیچ انسانی جاودانگی مقرر نداشته‌ایم، آیا اگر تو بمیری آنان جاویدانند؟'),
(2518, 21, 35, 'هر جانداری چشنده [طعم‌] مرگ است و شما را به بد و نیک، چنانکه باید و شاید، می‌آزماییم، و به سوی ما بازگردانده می‌شوید'),
(2519, 21, 36, 'و چون کافران تو را ببینند، جز به ریشخندت نمی‌گیرند [و گویند] آیا این همان کسی است که از خدایان شما [به بدی‌] یاد می‌کند؟ و هم آنان یاد خدای رحمان را منکرند'),
(2520, 21, 37, 'انسان [گویی‌] از شتاب آفریده شده است، به زودی آیات خود را به شما می‌نمایانیم، از من به شتاب مخواهید'),
(2521, 21, 38, 'و گویند اگر راست می‌گویید پس کی این وعده فرا می‌رسد؟'),
(2522, 21, 39, 'اگر کافران بدانند که هنگامی که نتوانند آتش را از چهره‌هایشان و از پشتهایشان باز دارند، و یاری نیابند [به صدق آن وعده پی برند]'),
(2523, 21, 40, 'آری [آن وعده و قیامت‌] ناگهانشان فرا می‌رسد، و حیرانشان می‌سازد، و نمی‌توانند بازش گردانند، و به آنان مهلتی هم ندهند'),
(2524, 21, 41, 'و پیامبرانی هم که پیش از تو بودند، ریشخند دیدند، و بر سر ریشخندکنندگانشان کیفر استهزایشان فرود آمد'),
(2525, 21, 42, 'بگو چه کسی شما را در شب و روز از [عذاب‌] خدای رحمان باز می‌دارد، آری آنان از یاد پروردگارشان دل می‌گردانند'),
(2526, 21, 43, 'یا برای آنان خدایانی هست که آنان را در برابر ما حفظ می‌کند، [حال آنکه‌] نمی‌توانند به خودشان یاری برسانند، و از ما هم یاری نبینند'),
(2527, 21, 44, 'حق این است که اینان و پدرانشان را [از ناز و نعمت‌] بهره‌مند ساخته‌ایم، تا آنکه روزگاری دراز یافتند، پس آیا نیندیشیده‌اند که ما دامنه‌های سرزمین [کفر] را می‌کاهیم [و بر گستره اسلام می‌افزاییم‌]، آیا ایشان پیروز خواهند شد؟'),
(2528, 21, 45, 'بگو همانا شما را از طریق وحی هشدار می‌دهم، و البته کران-چون هشدار یابندندایی نمی‌شنوند'),
(2529, 21, 46, 'و چون شمه‌ای از عذاب پروردگارت به آنان برسد خواهند گفت وای بر ما که ستمکار [/مشرک‌] بودیم‌'),
(2530, 21, 47, 'و ترازوهای راست و درست را در روز قیامت در میان نهیم، و بر هیچ کس هیچ ستمی نرود، و اگر [عملی‌] هم سنگ دانه خردلی باشد، آن را به حساب آوریم، و ما خود حسابرسی را کفایت کنیم‌'),
(2531, 21, 48, 'و به راستی که به موسی و هارون فرقان [تورات‌] و روشنی بخش و پندآموزی برای پرهیزگاران بخشیدیم‌'),
(2532, 21, 49, 'همان کسانی که از پروردگارشان در نهان می‌ترسند، و هم آنان که از قیامت بیمناکند'),
(2533, 21, 50, 'این پندآموزی مبارک است که فرو فرستادیمش، آیا شما منکرش هستید؟'),
(2534, 21, 51, 'و به راستی پیشاپیش به ابراهیم رهیافتی که سزاوارش بود بخشیدیم، و به آن آگاه بودیم‌'),
(2535, 21, 52, 'چنین بود که به پدرش و قومش گفت این تندیسها چیست که شما در خدمتشان معتکف‌اید'),
(2536, 21, 53, 'گفتند پدرانمان را پرستنده آنها یافتیم‌'),
(2537, 21, 54, 'گفت هم شما و هم پدرانتان در گمراهی آشکار بوده‌اید'),
(2538, 21, 55, 'گفتند آیا برای ما حق را آورده‌ای یا بازیگری؟'),
(2539, 21, 56, 'گفت حق این است که پروردگارتان، پروردگار آسمانها و زمین است همو که آنها را آفریده است، و من بر این [سخن‌] گواهم‌'),
(2540, 21, 57, '[و در دل گفت‌] به خدا پس از آنکه روی بر تافتید فکری به حال بتهایتان خواهم کرد'),
(2541, 21, 58, 'آنگاه آنها را خرد و ریز کرد، مگر بزرگترشان را، باشد که به او روی آورند'),
(2542, 21, 59, 'گفتند چه کسی این کار را در حق خدایان ما انجام داده است، بی‌شک که از ستمکاران است‌'),
(2543, 21, 60, 'گفتند، شنیدیم جوانی که به او ابراهیم گفته می‌شد، از آنان سخن می‌گفت‌'),
(2544, 21, 61, 'گفتند او را در پیش چشمان مردم حاضر کنید تا آنان حاضر و ناظر باشند'),
(2545, 21, 62, '[آوردندش و] گفتند ای ابراهیم آیا تو این کار را با خدایان ما کردی؟'),
(2546, 21, 63, '[به ریشخند] گفت نه بلکه همین بزرگترشان چنین کاری کرده است، اگر سخن می‌گویند از آنها بپرسید'),
(2547, 21, 64, 'به خود آمدند و گفتند شما خود ستمگرید'),
(2548, 21, 65, 'سپس سرهایشان را تکان دادند [و گفتند] خوب می‌دانی که اینها سخن نمی‌گویند'),
(2549, 21, 66, 'گفت آیا پس به جای خداوند چیزی را که نه سودی به شما می‌رساند و نه زیانی می‌پرستید؟'),
(2550, 21, 67, 'اف بر شما و بر آنچه به جای خداوند می‌پرستید، آیا اندیشه نمی‌کنید؟'),
(2551, 21, 68, 'گفتند او را بسوزانید و اگر می‌توانید خدایانتان را یاری دهید'),
(2552, 21, 69, 'گفتیم ای آتش بر ابراهیم سرد و سلامت شو'),
(2553, 21, 70, 'و در حق او بدسگالی کردند، آنگاه آنان را زیانکارترین [مردم‌] گرداندیم‌'),
(2554, 21, 71, 'و او و لوط را رهاندیم و به سرزمینی رساندیم که برای همگان برکتش بخشیده بودیم‌'),
(2555, 21, 72, 'و به او اسحاق و یعقوب را برتری بخشیدیم، و همه‌شان را از شایستگان قرار دادیم‌'),
(2556, 21, 73, 'و آنان را پیشوایانی که به فرمان ما ره می‌نمودند، گرداندیم، و به آنان نیکوکاری و برپاداشتن نماز و پرداختن زکات را وحی کردیم، و پرستندگان ما بودند'),
(2557, 21, 74, 'و به لوط حکمت و علم بخشیدیم و از شهری که اهلش پلیدکاری می‌کردند، نجاتش دادیم، که آنان مردمی پلید و نافرمان بودند'),
(2558, 21, 75, 'و او را در جوار رحمت خود در آوردیم، که او از شایستگان بود'),
(2559, 21, 76, 'و نوح را [نیز رهاندیم‌] که پیش از آن ندا به دعا برداشته بود، و دعایش را اجابت کردیم و او و خانواده‌اش را از گرفتاری بزرگ رهاندیم‌'),
(2560, 21, 77, 'و او را در برابر قومی که آیات ما را دروغ می‌انگاشتند یاری دادیم، که آنان قومی پلید بودند و همگی‌شان را غرق کردیم‌'),
(2561, 21, 78, 'و داوود و سلیمان را یاد کن که درباره کشتزاری که گوسفند کسانی شبانه در آن چریده بود، داوری کردند و ما شاهد داوریشان بودیم‌'),
(2562, 21, 79, 'و آن را به سلیمان فهماندیم، و به هر دو حکمت و علم بخشیدیم، و کوهها و پرندگان را تسخیر کردیم که همراه با داوود تسبیح می‌گفتند، و توانای آن کار بودیم‌'),
(2563, 21, 80, 'و به او [داوود] فن زره‌بافی برای شما آموخته بودیم تا شما را از آسیب همدیگر محفوظ بدارد، آیا شما شاکرید؟'),
(2564, 21, 81, 'و برای سلیمان باد تندرو را [تسخیر کردیم‌] که به فرمان او به سرزمینی که آن را برکت داده بودیم روان می‌شد، و به هر چیزی دانا [و توانا]ییم'),
(2565, 21, 82, 'و نیز بعضی از شیاطین را که برای او غواصی می‌کردند و کارهایی جز این هم انجام می‌دادند، و نگاهبان آنان بودیم‌'),
(2566, 21, 83, 'و ایوب را [یاد کن‌] که پروردگارش را به دعا ندا داد که به من رنج رسیده است و حال آنکه تو مهربان‌ترین مهربانانی‌'),
(2567, 21, 84, 'سپس دعای او را اجابت کردیم و رنجی را که به او رسیده بود، برطرف کردیم، و خانواده‌اش را [دیگر بار] به او بخشیدیم و همانند آنان را، که رحمتی از جانب ما بود، و پندی برای عبادت‌پیشگان‌'),
(2568, 21, 85, 'و اسماعیل و ادریس و ذوالکفل را [یاد کن‌] که همگی از صابران بودند'),
(2569, 21, 86, 'و آنان را در جوار رحمت خویش درآوردیم، که آنان از شایستگانند'),
(2570, 21, 87, 'و ذوالنون [/ صاحب ماهی /یونس‌] را [یاد کن‌] که خشمگنانه به راه خود رفت، و گمان کرد هرگز بر او تنگ نمی‌گیریم، آنگاه در دل تاریکی ندا در داد که خدایی جز تو نیست، پاکا که تویی، من از ستمکاران بودم‌'),
(2571, 21, 88, 'آنگاه دعای او را اجابت کردیم و او را از اندوه رهاندیم، و بدین‌سان مؤمنان را می‌رهانیم‌'),
(2572, 21, 89, 'و زکریا را [یاد کن‌] که پروردگارش را به دعا ندا داد که پروردگارا مرا تنها مگذار، و حال آنکه تو بهترین بازماندگانی‌'),
(2573, 21, 90, 'آنگاه دعای او را اجابت کردیم، و به او یحیی را بخشیدیم و همسرش را برای او شایسته گرداندیم، اینان به نیکوکاری می‌شتافتند، و ما را از روی امید و بیم می‌خواندند، و در برابر ما فروتن بودند'),
(2574, 21, 91, 'و همچنین آن زن که پاکدامنی ورزید و ما از روح خویش در او دمیدیم و او و پسرش را پدیده شگرفی برای جهانیان گرداندیم‌'),
(2575, 21, 92, 'این امت شماست که امتی یگانه است و من پروردگار شما هستم، پس مرا بپرستید'),
(2576, 21, 93, 'ولی آنان در کار خود اختلاف یافتند، همگی‌شان به سوی ما باز می‌گردند'),
(2577, 21, 94, 'پس هرکس که از کارهای شایسته انجام دهد و مؤمن باشد، در برابر کوشش او ناسپاسی نخواهد شد، و ما نویسنده [کار و کردار] او هستیم‌'),
(2578, 21, 95, 'و بر اهل هر شهری که ما نابودش کردیم حرام است که بازگردند'),
(2579, 21, 96, 'تا آنگاه که یاجوج و ماجوج رها شوند و از هر پشته‌ای بشتابند'),
(2580, 21, 97, 'و وعده راست و درست نزدیک شود، آنگاه دیدگان کافران خیره گردد [و گویند] وای بر ما که از این حقیقت غافل بودیم، بلکه ستمگر بودیم‌'),
(2581, 21, 98, '[گویند] شما و آنچه به جای خداوند می‌پرستید هیزم جهنمید و شما وارد آن خواهید شد'),
(2582, 21, 99, 'و اگر اینان خدایان حقیقی بودند، وارد آن [جهنم‌] نمی‌شدند که همه در آن جاویدانند'),
(2583, 21, 100, 'آنان در آن فریاد دارند، و در آن [هیچ چیز] نمی‌شنوند'),
(2584, 21, 101, 'کسانی که از ما در حق آنان نیکی مقرر شده است، آنان از آن دور داشته شوند'),
(2585, 21, 102, 'آواز آن را نشنوند و ایشان در [بهشت‌] دلخواهشان جاویدانند'),
(2586, 21, 103, 'بیم بزرگ آنان را اندوهگین نکند و فرشتگان به استقبال آنان آیند [و گویند] این همان روزتان است که به شما وعده داده شده بود'),
(2587, 21, 104, 'روزی که آسمان را همچون طومار کتابها درنوردیم، همچنانکه آفرینش نخستین را آغاز کرده‌ایم، آن را باز می‌گردانیم، این وعده بر عهده ماست، و ما انجام‌دهنده آنیم‌'),
(2588, 21, 105, 'و به راستی در زبور، پس از تورات نوشته‌ایم که زمین را بندگان شایسته من به ارث می‌برند'),
(2589, 21, 106, 'در این برای اهل عبادت، کفایتی هست‌'),
(2590, 21, 107, 'و ما تو را جز مایه رحمت برای جهانیان نفرستاده‌ایم‌'),
(2591, 21, 108, 'بگو همین به من وحی می‌شود که خدای شما خدای یگانه است، پس آیا شما پذیرنده‌اید؟'),
(2592, 21, 109, 'و اگر رویگردان شدند بگو همه‌تان را یکسان آگاه کردم، و خود نمی‌دانم که آیا آنچه به شما وعده داده شده است نزدیک است یا دور؟'),
(2593, 21, 110, 'به راستی که او هم سخن آشکار را می‌داند و هم آنچه پنهان می‌دارید می‌داند'),
(2594, 21, 111, 'و نمی‌دانم شاید آن آزمونی برای شما باشد، و برخورداری تا زمانی معین‌'),
(2595, 21, 112, 'گفت پروردگارا به حق حکم فرما، و پروردگار ما که خدای رحمان است در آنچه می‌گویید مددکار [ما]ست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'هان ای مردم از پروردگارتان پروا کنید، که زلزله قیامت چیزی سهمگین است‌'),
(2597, 22, 2, 'روزی که در آن بینید هر زن شیردهنده‌ای از نوزاد شیری‌اش غافل شود، و هر زن آبستنی [بی‌اختیار] وضع حمل کند و مردمان را مست بینی، و حال آنکه مست نباشند، ولی عذاب الهی سخت و سنگین است‌'),
(2598, 22, 3, 'و از مردمان کسی هست که درباره خداوند بدون دانش مجادله می‌کند و از هر شیطان سرکشی پیروی می‌کند'),
(2599, 22, 4, 'بر او مقرر شده است که هرکس او را دوست گیرد، او گمراهش می‌کند و به عذاب آتش جهنم می‌کشاند'),
(2600, 22, 5, 'هان ای مردم اگر درباره رستاخیز شک و شبهه دارید، بدانید که ما شما را از خاک، سپس از نطفه، سپس از خون بسته، سپس گوشت پاره شکل یافته و شکل نیافته آفریده‌ایم تا [حقیقت را] برای شما هویدا کنیم، و هر چه را بخواهیم تا زمانی معین در رحمها قرار می‌دهیم، سپس شما را که کودکی شده‌اید [از شکم مادر] بیرون می‌آوریم که به کمال بلوغتان برسید، و بعضی از شما جانشان گرفته می‌شود، و بعضی به حد اعلای فرتوتی بازبرده شود، چندانکه پس از دانستن [بسیاری چیزها] چیزی نداند، و زمین را پژمرده بینی، آنگاه چون بر آن آب [باران‌] فرو فرستیم، جنبش یابد و رشد کند، و چه بسیار از گونه‌های خرم برویاند'),
(2601, 22, 6, 'این از آن است که خداوند بر حق است و او مردگان را زنده می‌کند و او بر هر کاری تواناست‌'),
(2602, 22, 7, 'و اینکه قیامت آمدنی است [و] در آن شکی نیست و خداوند کسانی را که در گورها خفته‌اند، بر می‌انگیزد'),
(2603, 22, 8, 'و از مردمان کسی هست که درباره خداوند بدون دانش و بدون رهنمود و بدون کتابی روشنگر مجادله می‌کند'),
(2604, 22, 9, 'پهلو به تکبر بگرداند تا [مردمان را] از راه خدا گمراه کند در دنیا خفت و خواری دارد و در روز قیامت عذاب آتش را به او می‌چشانیم‌'),
(2605, 22, 10, 'این به خاطر کار و کردار پیشین توست، و گرنه‌] خداوند هرگز در حق بندگان ستمگر نیست‌'),
(2606, 22, 11, 'و از مردم کسی هست که خداوند را با دودلی می‌پرستد، پس اگر خیری به او برسد، دلش به آن آرام گیرد، و اگر رنجی به او رسد رویگردان شود، در دنیا و آخرت زیانکار شده است، این همان زیانکاری آشکار است‌'),
(2607, 22, 12, 'به جای خداوند چیزی به دعا خواند که نه زیانی به او می‌رساند و نه سودی، این همان گمراهی دور و دراز است‌'),
(2608, 22, 13, 'کسی را به دعا می‌خواند که زیانش محتمل‌تر است از سودش، بد یار و بد دمسازی است‌'),
(2609, 22, 14, 'بی‌گمان خداوند کسانی را که ایمان آورده‌اند و کارهای شایسته کرده‌اند، به باغهایی درمی‌آورد که جویباران از فرو دست آن جاری است، بی‌گمان خداوند هرچه خواهد همان تواند کرد'),
(2610, 22, 15, 'هرکس گمان می‌برد که خداوند هرگز او [پیامبر] را در دنیا و آخرت یاری نمی‌کند، ریسمانی به سقف خانه‌[اش‌] ببندد [و به گردن اندازد] سپس [آن یا نفس خود را] ببرد و بنگرد آیا این تدبیر او مایه خشمش را از بین می‌برد؟'),
(2611, 22, 16, 'و بدین‌سان آن را به صورت آیاتی روشنگر فرو فرستادیم و خداوند هر که را خواهد هدایت کند'),
(2612, 22, 17, 'همانا خداوند در میان مؤمنان و یهودیان و صابئین و مسیحیان و مجوس و مشرکان در روز قیامت داوری خواهد کرد، بی‌گمان خداوند بر همه چیز گواه است‌'),
(2613, 22, 18, 'آیا نیندیشیده‌ای که هر که در آسمانها و زمین است و خورشید و ماه و ستارگان و کوهها و درختان و جانوران و بسیاری از مردم، بر خداوند سجده می‌برند، و بسیاری هم هستند که عذاب بر آنان محقق شده است، و هرکس که خداوند خوارش بدارد، گرامی دارنده‌ای ندارد، که خداوند هرچه خواهد همان تواند کرد'),
(2614, 22, 19, 'اینان حریفانی هستند که در حق پروردگارشان مجادله کرده‌اند، اما کسانی که کفرورزیده‌اند، بر بالای آنان جامه‌هایی از آتش بریده‌اند [و] از بالای سرهایشان آب جوش ریخته شود'),
(2615, 22, 20, 'که آنچه در درونشان هست و پوستهایشان به آن گداخته می‌شود'),
(2616, 22, 21, 'و گرزهای آهنینی برای آنان [مهیا] هست‌'),
(2617, 22, 22, 'هر بار که بخواهند از شدت اندوه از آن [جهنم‌] بیرون روند، به آن بازگردانده شوند [و گویند] عذاب آتش را بچشید'),
(2618, 22, 23, 'بی‌گمان خداوند کسانی را که ایمان آورده‌اند و کارهای شایسته کرده‌اند، به باغهایی درمی‌آورد که جویباران از فرودست آن جاری است، در آنجا به دستبندهایی زرین و نیز مروارید آراسته شوند، و لباسشان در آنجا ابریشم است‌'),
(2619, 22, 24, 'و به سخن پاکیزه و راه خداوند ستوده رهنمون شوند'),
(2620, 22, 25, 'کسانی که کفر ورزیده و [مردم را] از راه خدا و مسجدالحرام -که آن را برای مردم اعم از مقیم و مسافر بیابانی نهاده‌ایمباز داشته‌اند [آنان را به عذابی دردناک دچار می‌کنیم‌]، و هرکس در آن از سر ستمگری آهنگ کژروی کند، به او عذابی دردناک می‌چشانیم‌'),
(2621, 22, 26, 'و چنین بود که برای ابراهیم جایگاه خانه کعبه را معین کردیم [و گفتیم‌] که برای من هیچ‌گونه شریک میاور و خانه‌ام را برای غریبان و مقیمان و نمازگزاران پاکیزه بدار'),
(2622, 22, 27, 'و در میان مردم برای حج ندا در ده که پیاده و سوار بر هر شتر لاغری -که از هر راه دوری می‌آیندرو به سوی تو آورند'),
(2623, 22, 28, 'تا در منافعی که برای آنان هست حضور داشته باشند، و نام خداوند را در روزهای معین بر چارپایان زبان بسته‌ای که روزیشان داده‌ایم، ببرند [و قربانی کنند]، آنگاه از آن بخورید و به درمانده بینوا هم بخورانید'),
(2624, 22, 29, 'آنگاه باید آلایشهایشان را بزدایند و نذرهایشان را وفا کنند و پیرامون بیت‌العتیق طواف کنند'),
(2625, 22, 30, 'چنین است و هرکس شعائر الهی را بزرگ بشمارد، برایش در نزد پروردگارش بهتر است، و بر شما همه چارپایان جز آنچه [حرمت آن‌] بر شما خوانده شده است، حلال است، پس از پلیدی بتها پرهیز کنید، و نیز از شهادت دروغ پرهیز کنید'),
(2626, 22, 31, 'برای خداوند پاکدین باشید و به او شرک نورزید، و هرکس به خداوند شرک ورزد، گویی از آسمان درافتاده و پرنده‌ای او را در ربوده، یا باد او را به جایی دوردست درانداخته است‌'),
(2627, 22, 32, 'چنین است و هرکس شعائر الهی را بزرگ شمارد، آن از پروا و پرهیز دلهاست‌'),
(2628, 22, 33, 'در آنها تا زمانی معین برای شما سودهایی هست، آنگاه بازگشتگاه آن بیت‌العتیق است‌'),
(2629, 22, 34, 'و برای هر امتی قربانی‌ای معین داشته‌ایم تا نام خدا را، [به هنگام ذبح‌] بر چارپایان زبان بسته‌ای که روزیشان کرده‌ایم، ببرند، آری خدای شما خدای یگانه است، در برابر او تسلیم باشید، و به فروتنان بشارت ده‌'),
(2630, 22, 35, 'همان کسانی که چون یاد خدا به میان آید، دلهایشان خشیت گیرد، و نیز کسانی که بر مصائبشان شکیبایی می‌ورزند، و برپا دارندگان نماز و کسانی که از آنچه روزیشان داده‌ایم می‌بخشند'),
(2631, 22, 36, 'و [قربانی‌] شتران درشت اندام را برای شما از شعائر الهی گردانده‌ایم، برای شما در آن خیری هست، پس در حالی که به صف و برپا ایستاده‌اند، نام خداوند را بر آنها ببرید [و قربانی کنید] و چون پهلوهایشان به خاک رسید [و بدنشان سرد شد] از آن بخورید، و به فقیر غیر سائل و سائل نیز بخورانید، بدین‌سان آنها را برای شما رام کرده‌ایم، باشد که سپاس بگزارید'),
(2632, 22, 37, 'گوشتهای آنها و خونهایشان هرگز به خداوند نمی‌رسد، بلکه پرهیزگاری شما به رضای او نایل می‌گردد، بدین‌سان آنها را برای شما رام کرده‌ایم تا خداوند را به خاطر آنکه راهنمایی‌تان کرده است، تکبیر گویید، و به نیکوکاران بشارت ده‌'),
(2633, 22, 38, 'بی‌گمان خداوند از مؤمنان دفاع می‌کند، بی‌گمان خداوند هیچ خیانتگر ناسپاسی را دوست ندارد'),
(2634, 22, 39, 'به کسانی [از مؤمنان‌] که [مشرکان‌] با آنان کارزار کرده‌اند، رخصت جهاد داده شده است، چرا که ستم دیده‌اند، و خداوند بر یاری دادن آنان تواناست‌'),
(2635, 22, 40, 'همان کسانی که از خانه و کاشانه‌شان به ناحق رانده شده‌اند، و جز این نبوده که گفته‌اند خداوند پروردگار ماست، و اگر خداوند بعضی از مردم را به دست بعضی دیگر دفع نمی‌کرد، هم صومعه‌های راهبان و هم معابد [مسیحیان‌] و هم عبادتگاهها[ی یهودیان‌] و هم مساجد [مسلمانان‌] که نام خداوند در آنها بسیار یاد می‌شود، ویران می‌گردید، و خداوند هرکس را که دینش را یاری کند، یاری می‌دهد، که خداوند توانای پیروزمند است‌'),
(2636, 22, 41, 'کسانی که چون در این سرزمین توانایشان دهیم، نماز را برپا می‌دارند و زکات را می‌پردازند، و امر به معروف و نهی از منکر می‌کنند، و سرانجام کارها با خداوند است‌'),
(2637, 22, 42, 'و اگر تو را دروغگو انگاشتند بدان که قوم نوح و عاد و ثمود هم [پیامبرانشان را] دروغگو انگاشتند'),
(2638, 22, 43, 'همچنین قوم ابراهیم و قوم لوط'),
(2639, 22, 44, 'و اهل مدین، و نیز موسی با تکذیب مواجه شد، آنگاه به کافران مهلت و میدان دادم، سپس فروگرفتمشان، بنگر که عقاب من چگونه بوده است‌'),
(2640, 22, 45, 'و چه بسیار شهرها را که چون [مردمش‌] ستمکار بود، نابود کردیم، و سقفها و دیوارهایش فروریخته است، و چه بسیار چاها که بی‌رونق مانده، و نیز چه بسیار قصر استوار و سر به فلک کشیده‌'),
(2641, 22, 46, 'آیا در زمین گردش نکرده‌اند تا دلهایی داشته باشند که با آن بیندیشند یا گوشهایی که با آن [حق را] بشنوند، آری [فقط] دیدگان نیست که نابینا می‌شود، بلکه دلهایی که در سینه‌ها هست هم نابینا می‌گردد'),
(2642, 22, 47, 'و از تو عذاب را به شتاب می‌خواهند و حال آنکه خداوند هرگز در وعده‌اش خلاف نمی‌کند، و یک روز به حساب پروردگارت برابر با هزار سال است از آن دست که شما می‌شمارید'),
(2643, 22, 48, 'و چه بسیار [اهل‌] شهرها که به آنان مهلت و میدان دادم در حالی که ستمگر بودند، سپس فروگرفتمشان، و سیر و سرانجام به سوی من است‌'),
(2644, 22, 49, 'بگو ای مردم من برای شما هشداردهنده‌ای آشکارم‌'),
(2645, 22, 50, 'بدانید کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند از آمرزش و روزی نیک برخوردارند'),
(2646, 22, 51, 'و کسانی که در [رد و انکار] آیات ما مقابله کنان [و بی‌حاصل‌] می‌کوشند، اینان دوزخی‌اند'),
(2647, 22, 52, 'و پیش از تو هیچ رسول یا نبی نفرستادیم مگر آنکه چون قرائت [وحی را] آغاز کرد، شیطان در خواندن او اخلال می‌کرد، آنگاه خداوند اثر القای شیطان را می‌زداید، و سپس آیات خویش را استوار می‌دارد و خداوند دانای فرزانه است‌'),
(2648, 22, 53, 'تا [بدین وسیله‌] خداوند القای شیطان را مایه آزمون بیماردلان و سختدلان بگرداند، و بی‌گمان ستمکاران در ستیزه‌ای دور و درازند'),
(2649, 22, 54, 'و تا دانش‌یافتگان [راستین‌] بدانند که آن حق و از سوی پروردگار توست، و به آن ایمان آورند و دلهایشان در برابر آن خاشع شود، و بی‌گمان خداوند رهنمای مؤمنان به راه راست است‌'),
(2650, 22, 55, 'و کافران همچنان از آن در شک هستند، تا آنکه قیامت ناگاه فرارسدشان، یا عذاب روزی [سهمگین و] سترون گریبانگیرشان شود'),
(2651, 22, 56, 'در چنین روزی فرمانروایی خاص خداوند است که میان آنان داوری می‌کند، آنگاه کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، در باغهای بهشتی پرناز و نعمت‌اند'),
(2652, 22, 57, 'و کسانی که کفر ورزیده‌اند و آیات ما را دروغ انگاشته‌اند، اینان عذابی خوارکننده [در پیش‌] دارند'),
(2653, 22, 58, 'و کسانی که در راه خدا هجرت کرده‌اند، سپس کشته شده، یا در گذشته‌اند، بی‌گمان خداوند آنان را از روزی نیکو برخوردار می‌سازد، و بی‌گمان خداوند بهترین روزی‌دهندگان است‌'),
(2654, 22, 59, 'بی‌شک آنان را به جایگاهی که آن را می‌پسندند درآورد، و به راستی خداوند دانای بردبار است‌'),
(2655, 22, 60, 'آری و کسی که همانند عقابی که دیده است، [بر دیگران‌] عقاب روا دارد، سپس بر او ستم رود، خداوند او را یاری می‌دهد که بی‌گمان خداوند بخشاینده آمرزگار است‌'),
(2656, 22, 61, 'این از آن است که خداوند از شب می‌کاهد و بر روز می‌افزاید، و از روز می‌کاهد و بر شب می‌افزاید، و بی‌گمان خداوند شنوای بیناست‌'),
(2657, 22, 62, 'این از آن است که خداوند بر حق است، و آنچه به جای او می‌پرستند باطل است، و همانا خداوند بلندمرتبه و بزرگ است‌'),
(2658, 22, 63, 'آیا نیندیشیده‌ای که خداوند از آسمان آبی فرو فرستاد، آنگاه زمین سبز و خرم گردید، بی‌گمان خداوند باریک‌بین و آگاه است‌'),
(2659, 22, 64, 'آنچه در آسمانها و آنچه در زمین است از آن اوست، و بی‌گمان خداوند بی‌نیاز ستوده است‌'),
(2660, 22, 65, 'آیا نیندیشیده‌ای که خداوند آنچه در زمین است و کشتیها را که در دریا به فرمان او روانند، برای شما رام کرد، و او [اجرام‌] آسمان را نگاه می‌دارد که بر زمین نیفتد، مگر به اذن او، بی‌گمان خداوند به مردم رئوف مهربان است‌'),
(2661, 22, 66, 'و اوست کسی که شما را زنده کرد، سپس شما را می‌میراند، آنگاه دوباره زنده‌تان می‌دارد، بی‌گمان انسان ناسپاس است‌'),
(2662, 22, 67, 'هر امتی را شریعتی مقرر داشته‌ایم که ایشان متمسک به آنند، پس نباید که با تو در این امر ستیزه کنند، و به راه پروردگارت بخوان، که تو بر طریق هدایتی مستقیم هستی‌'),
(2663, 22, 68, 'و اگر با تو مجادله کردند بگو خداوند به آنچه می‌کنید داناتر است‌'),
(2664, 22, 69, 'خداوند در میان شما، در آنچه اختلاف داشتید، در روز قیامت داوری می‌کند'),
(2665, 22, 70, 'آیا نمی‌دانی که خداوند آنچه در آسمان و زمین است می‌داند، که اینها در کتابی [مسطور] است، بی‌گمان این امر بر خداوند آسان است‌'),
(2666, 22, 71, 'و به جای خداوند چیزی را می‌پرستند که درباره آن برهانی نازل نکرده است، و چیزی را [می‌پرستند] که به آن علم ندارند، و ستمکاران [مشرک‌] یاوری ندارند'),
(2667, 22, 72, 'و چون آیات روشنگر ما را بر آنان بخوانند، در چهره‌های کفرپیشگان [نشان‌] ناخوشایندی می‌بینی، نزدیک است که به کسانی که آیات ما را بر آنان می‌خوانند حمله برند، بگو آیا به از این بدتری [که در کمین شماست‌] آگاهتان کنم؟ آن آتش دوزخ است که خداوند به کافران وعده‌اش را داده است، و بد سرانجامی است‌'),
(2668, 22, 73, 'ای مردم مثلی زده می‌شود که به آن گوش فرادهید کسانی که به جای خداوند می‌پرستید، اگر هم دست یکی کنند، هرگز مگسی را هم نتوانند آفرید، و اگر مگس چیزی از آنان برباید، نمی‌توانند آن را از او بازپس گیرند، هم پرستنده و هم پرستیده ناتوانند'),
(2669, 22, 74, 'خداوند را چنانکه سزاوار ارج اوست، ارج نگذاشتند، بی‌گمان خداوند توانای پیروزمند است‌'),
(2670, 22, 75, 'خداوند از میان فرشتگان و مردمان، پیامبرانی برمی‌گزیند، بی‌گمان خداوند شنوای بیناست‌'),
(2671, 22, 76, 'هرچه پیش روی آنان و پشت‌سرشان است، می‌داند، و همه کارها به خداوند بازگردانده می‌شود'),
(2672, 22, 77, 'ای مؤمنان به رکوع و سجود [/نماز] بپردازید و پروردگارتان را بپرستید، و نیکی کنید، باشد که رستگار شوید'),
(2673, 22, 78, 'و در راه خدا چنانکه سزاوار جهاد اوست جهاد کنید، او شما را برگزیده است و برای شما در دینتان محظوری قرار نداده است، که آیین پدرتان ابراهیم است، همو که شما را از پیش مسلمان نامید، و در این [کتاب‌] هم [مسلمان نامیده شده‌اید]، تا سرانجام پیامبر گواه بر شما و شما گواه بر مردمان باشید، پس نماز برپا دارید و زکات بدهید و به خداوند پناه برید، او سرور شماست، چه نیکو سروری و چه نیکو یاوری‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'به راستی که مؤمنان رستگار شوند'),
(2675, 23, 2, 'همان کسانی که در نمازشان فروتنند'),
(2676, 23, 3, 'و کسانی که از [کار و سخن‌] بیهوده رویگردانند'),
(2677, 23, 4, 'و کسانی که زکات می‌پردازند'),
(2678, 23, 5, 'و کسانی که پاکدامنی می‌ورزند'),
(2679, 23, 6, 'مگر در مورد زنانشان یا ملک یمینشان، که در این صورت نکوهیده نیستند'),
(2680, 23, 7, 'پس هر کس که از این فراتر رود، اینانند که تجاوزکارند'),
(2681, 23, 8, 'و نیز [رستگار شوند] کسانی که رعایتگر امانتهایشان و پیمانهایشان هستند'),
(2682, 23, 9, 'و کسانی که بر نمازهای خویش مواظبت دارند'),
(2683, 23, 10, 'اینانند که میراث برند'),
(2684, 23, 11, 'که فردوس را به ارث می‌برند و در آن جاودانه‌اند'),
(2685, 23, 12, 'و به راستی که انسان را از چکیده گل آفریدیم‌'),
(2686, 23, 13, 'آنگاه او را به صورت نطفه‌ای در جایگاهی استوار قرار دادیم‌'),
(2687, 23, 14, 'آنگاه نطفه را به صورت خون بسته و سپس خون بسته را به صورت گوشت پاره درآوردیم، و سپس گوشت پاره را استخواندار کردیم و آنگاه بر استخوانها پرده‌ای گوشت پوشاندیم، آنگاه آن را به صورت آفرینشی دیگر پدید آوردیم، بزرگا خداوندا که بهترین آفرینندگان است‌'),
(2688, 23, 15, 'سپس شما پس از اینها میرا هستید'),
(2689, 23, 16, 'سپس شما در روز قیامت برانگیخته شوید'),
(2690, 23, 17, 'و به راستی که برفراز شما هفت طبقه [آسمان‌] آفریده‌ایم، و ما هرگز از آفرینش غافل نبوده‌ایم‌'),
(2691, 23, 18, 'و از آسمان آبی به اندازه فرو فرستادیم و آن را در زمین جای دادیم، و ما به از بین بردن آن تواناییم‌'),
(2692, 23, 19, 'آنگاه با آن برای شما باغهای خرما و انگور پدید آوردیم که در آن برای شما میوه‌های بسیار است و از آن می‌خورید'),
(2693, 23, 20, 'و [همچنین‌] درختی که از طور سینا می‌روید و روغن [زیتون‌] برمی‌آورد و نیز نان خورشی برای خورندگان‌'),
(2694, 23, 21, 'و برای شما در چارپایان مایه عبرتی هست که شما را از آنچه در شکمهای آنهاست می‌نوشانیم و در آنها برای شما سودهای فراوان است و از [گوشت‌] آنها می‌خورید'),
(2695, 23, 22, 'و بر آنها و بر کشتی سوار می‌شوید'),
(2696, 23, 23, 'و به راستی نوح را به سوی قومش فرستادیم، که [به آنان‌] گفت ای قوم من خداوند را بپرستید که خدایی جز او ندارید، آیا پروا نمی‌کنید؟'),
(2697, 23, 24, 'و بزرگانی از قومش که کفرورزیده بودند گفتند این [مرد] جز بشری همانند شما نیست که می‌خواهد بر شما برتری جوید، و اگر خدا می‌خواست [بر شما] فرشتگانی می‌فرستاد، ما چنین چیزی در [سرنوشت‌] نیاکانمان نشنیده‌ایم‌'),
(2698, 23, 25, 'او جز مردی نیست که جنونی دارد، در کار او چندی درنگ کنید'),
(2699, 23, 26, '[نوح‌] گفت پروردگارا در قبال اینکه تکذیبم می‌کنند یاریم فرما'),
(2700, 23, 27, 'آنگاه به او وحی کردیم که کشتی را زیر نظر ما و با وحی ما بساز، و چون فرمان ما در رسید و [آب از] تنور فوران کرد، در آن از هر [جانوری‌] جفتی دوگانه راه بده، و نیز خانواده‌ات را مگر کسی از ایشان که حکم [ما] از پیش درباره او تحقق یافته است، و درباره کسانی که ستم [و شرک‌]ورزیده‌اند، با من سخن مگو که ایشان غرق شدنی‌اند'),
(2701, 23, 28, 'چون تو و همراهانت بر کشتی قرار گرفتید، آنگاه بگو سپاس خداوندی را که ما را از قوم ستم پیشه [و مشرک‌] رهانید'),
(2702, 23, 29, 'و بگو پروردگارا مرا به منزلی مبارک فرود آور و تو بهترین میزبانانی‌'),
(2703, 23, 30, 'بی‌گمان در این مایه‌های عبرتی هست و ما آزماینده بوده‌ایم‌'),
(2704, 23, 31, 'سپس، بعد از ایشان نسلی دیگر را پدید آوردیم‌'),
(2705, 23, 32, 'آنگاه پیامبری از ایشان به میان ایشان فرستادیم [و گفتیم‌] که خداوند را بپرستید که خدایی جز او ندارید، آیا پروا نمی‌کنید؟'),
(2706, 23, 33, 'و بزرگانی از قومش که کفرورزیده و لقای آن جهانی را انکار کرده بودند، و در زندگانی دنیا، از ناز و نعمت برخوردارشان ساخته بودیم، گفتند این [مرد] جز بشری همانند شما نیست، که از همانچه شما از آن می‌خورید، می‌خورد و از همانچه شما می‌آشامید، می‌آشامد'),
(2707, 23, 34, 'و اگر از بشری همانند خودتان پیروی کنید، آنگاه است که شما زیانکارید'),
(2708, 23, 35, 'آیا به شما وعده می‌دهد که چون شما مردید و خاک و استخوان [پوسیده‌] شدید، از نو برانگیخته می‌شوید'),
(2709, 23, 36, 'بعید اندر بعید است آنچه به شما وعده داده‌اند'),
(2710, 23, 37, 'این جز زندگانی دنیای ما نیست که [بعضی‌] می‌میریم و [بعضی‌] زندگی می‌کنیم، و ما [هرگز] برانگیختنی نیستیم‌'),
(2711, 23, 38, 'او جز مردی که بر خداوند دروغی بسته است نیست، و ما به [سخن‌] او باور نداریم‌'),
(2712, 23, 39, '[هود] گفت پروردگارا در قبال اینکه تکذیبم می‌کنند یاریم فرما'),
(2713, 23, 40, 'فرمود زودا که به سختی پشیمان گردند'),
(2714, 23, 41, 'آنگاه بانگ مرگبار آنان را به حق فرو گرفت، و آنگاه آنان را همچون خاشاک گرداندیم، نفرین بر ستم‌پیشگان [مشرک‌]'),
(2715, 23, 42, 'سپس، بعد از آنان نسلهای دیگر پدید آوردیم‌'),
(2716, 23, 43, 'هیچ امتی از اجل خویش پیش نمی‌افتند و پس نمی‌مانند'),
(2717, 23, 44, 'سپس پیامبرانمان را پیاپی فرستادیم، هربار که پیامبری به سراغ امتش می‌آمد او را دروغگو می‌انگاشتند، آنگاه آنان را به دنبال همدیگر آوردیم [و از میان بردیم‌] و افسانه‌شان گرداندیم، آری وای بر قوم بی‌ایمان‌'),
(2718, 23, 45, 'سپس موسی و برادرش هارون را همراه با معجزات خویش و برهان آشکار فرستادیم‌'),
(2719, 23, 46, 'به سوی فرعون و بزرگان قومش، آنگاه گردنکشی کردند و قومی بزرگی طلب بودند'),
(2720, 23, 47, 'پس گفتند آیا به دو بشر همانند خودمان ایمان بیاوریم؟ و حال آنکه قوم آنان پرستندگان ما هستند'),
(2721, 23, 48, 'سپس آن دو را دروغگو انگاشتند و جزو نابود شدگان گردیدند'),
(2722, 23, 49, 'و به راستی به موسی کتاب آسمانی دادیم، باشد که هدایت یابند'),
(2723, 23, 50, 'و پسر مریم و مادرش را پدیده [معجزه‌آسای‌] شگرفی قرار دادیم و آن دو را بر زمینی بلند که آرام جای بود، و آبی روان داشت، جای دادیم‌'),
(2724, 23, 51, 'ای پیامبران از پاکیزه‌ها بخورید و کارهای شایسته کنید که من از آنچه می‌کنید آگاهم‌'),
(2725, 23, 52, 'و همانا این امت شماست، امتی یگانه، و من پروردگار شما هستم، از من پروا کنید'),
(2726, 23, 53, 'ولی آنان در کارشان، در میان خود اختلاف و تفرقه یافتند، هر گروهی به آنچه در دست دارد، شادمان است‌'),
(2727, 23, 54, 'پس ایشان را یک چند در غفلتشان واگذار'),
(2728, 23, 55, 'آیا چنین می‌انگارند که آنچه از مال و فرزندان که بدان مددشان می‌کنیم؟'),
(2729, 23, 56, 'در خیر و خوبی به نفع ایشان می‌کوشیم؟ [چنین نیست‌] بلکه [حقیقت را] در نمی‌یابند'),
(2730, 23, 57, 'بی‌گمان کسانی که از خشیت پروردگارشان بیمناکند'),
(2731, 23, 58, 'و کسانی که به آیات پروردگارشان ایمان می‌آورند'),
(2732, 23, 59, 'و کسانی که به پروردگارشان شرک نمی‌ورزند'),
(2733, 23, 60, 'و کسانی که آنچه باید [انجام‌] بدهند، [انجام‌] می‌دهند و دلهایشان هراسان است که به سوی پروردگارشان باز می‌گردند'),
(2734, 23, 61, 'اینانند که در خیرات می‌کوشند و هم ایشان در آن پیشتازند'),
(2735, 23, 62, 'و ما هیچ کس را جز به اندازه توانش تکلیف نمی‌کنیم، و نزد ما کتابی است که به حق سخن می‌گوید، و به ایشان ستم نرود'),
(2736, 23, 63, 'بلکه دلهای ایشان از این [حقیقت‌] در غفلت است، و کارهایی [ناروا] جز این دارند که انجام‌دهنده آنند'),
(2737, 23, 64, 'تا آنگاه که نازپروردگانشان را به عذاب فروگیریم، آنگاه است که زاری می‌کنند'),
(2738, 23, 65, 'امروز زاری مکنید، چرا که از ما یاری نمی‌یابید'),
(2739, 23, 66, 'چنین بود که آیات من بر شما خوانده می‌شد، و شما [به آن‌] پشت می‌کردید'),
(2740, 23, 67, 'در برابر آن تکبر می‌ورزیدید و درباره آن شبها افسانه سرایی و پریشان‌گویی می‌کردید'),
(2741, 23, 68, 'آیا در این سخن اندیشه نمی‌کنند، یا چیزی که به سراغ نیاکانشان نیامده بود، به سراغ آنان آمده است؟'),
(2742, 23, 69, 'یا آنکه پیامبرشان را نمی‌شناسند، و لذا با او بیگانه‌اند'),
(2743, 23, 70, 'یا می‌گویند او جنونی دارد، [چنین نیست‌] بلکه [پیامبر] حق را برای آنان آورده است، و بیشترینه آنان ناخواهان حق‌اند'),
(2744, 23, 71, 'و اگر حق از هوی و هوسهای آنان پیروی می‌کرد، بی‌شک آسمان و زمین و هر که در آنهاست، تباه می‌شد، حق این است که حدیث خودشان را برایشان آورده‌ایم، آنگاه آنان از یاد خویش رویگردانند'),
(2745, 23, 72, 'یا شاید از آنان مزدی می‌طلبی؟ ولی پاداش پروردگارت [بسی‌] بهتر است، و او بهترین روزی‌دهندگان است‌'),
(2746, 23, 73, 'و تو ایشان را به راهی راست می‌خوانی‌'),
(2747, 23, 74, 'و کسانی که به آخرت ایمان نمی‌آورند، از راه [راست‌] به در افتاده‌اند'),
(2748, 23, 75, 'و اگر بر آنان رحمت می‌آوردیم و بلایی را که دچارش بودند، می‌گرداندیم، در طغیانشان با سرگشتگی پای می‌فشردند'),
(2749, 23, 76, 'و به راستی آنان را با عذاب فرو گرفتیم، و در برابر پروردگارشان خاکساری و زاری نکردند'),
(2750, 23, 77, 'تا آنکه بر آنان دری از عذاب سخت گشودیم، آنگاه بود که نومید شدند'),
(2751, 23, 78, 'و او کسی است که برای شما گوش [ها] و چشم [ها] و قلب [ها] آفرید، چه اندکمایه سپاس می‌گزارید'),
(2752, 23, 79, 'و او کسی است که شما را در زمین پدید آورد و نزد او محشور می‌شوید'),
(2753, 23, 80, 'و او کسی است که زنده می‌دارد و می‌میراند و در پی یکدیگر آمدن شب و روز از اوست، آیا اندیشه نمی‌کنید؟'),
(2754, 23, 81, 'بلکه همانند آنچه پیشینیان گفتند، می‌گویند'),
(2755, 23, 82, 'گویند آیا چون مردیم، و خاک و استخوان [پوسیده‌] شدیم، از نو برانگیخته می‌شویم؟'),
(2756, 23, 83, 'به راستی که به ما و پدرانمان پیشترها چنین وعده‌ای داده‌اند، این جز افسانه‌های پیشینیان نیست‌'),
(2757, 23, 84, 'بگو اگر می‌دانید، زمین و هر کس که در آن است، از آن کیست؟'),
(2758, 23, 85, 'زودا که می‌گویند از آن خداست، بگو آیا پند نمی‌گیرید؟'),
(2759, 23, 86, 'بگو پروردگار آسمانهای هفت‌گانه و پروردگار عرش عظیم کیست؟'),
(2760, 23, 87, 'زودا که می‌گویند اینها از آن خداست، بگو پروا نمی‌کنید؟'),
(2761, 23, 88, 'بگو اگر می‌دانید ملکوت همه چیز به دست کیست، و کیست که امان می‌دهد و در برابر او نتوان به کسی امان داد؟'),
(2762, 23, 89, 'زودا که می‌گویند اینها از آن خداست، بگو پس چگونه فریب داده می‌شوید؟'),
(2763, 23, 90, 'بلکه حق را برایشان آورده‌ایم، و آنان دروغگو هستند'),
(2764, 23, 91, 'خداوند فرزندی برنگزیده است، و خدایی در جنب او نیست، چرا که [در آن صورت‌] هر خدایی آفریده خود را پیش می‌آورد، و بعضی از آنان بر بعضی دیگر غلبه می‌جست، منزه است خداوند از آنچه می‌گویند'),
(2765, 23, 92, 'دانای پنهان و پیدا، فراتر است از آنچه شرک می‌ورزند'),
(2766, 23, 93, 'بگو پروردگارا اگر آنچه به آنان وعده داده شده است، به من بنمایانی [یا نه‌]'),
(2767, 23, 94, 'پروردگارا هرگز مرا در زمره ستم‌پیشگان [مشرک‌] میاور'),
(2768, 23, 95, 'و ما توانای آن هستیم که آنچه به آنان وعده می‌دهیم به تو بنمایانیم‌'),
(2769, 23, 96, 'ناشایستی را به شیوه‌ای که نیکوتر است، دفع کن، ما به آنچه می‌گویند داناتریم‌'),
(2770, 23, 97, 'و بگو پروردگارا از وسوسه‌های شیاطین به تو پناه می‌آورم‌'),
(2771, 23, 98, 'و پناه بر تو پروردگارا از اینکه آنان نزد من حاضر شوند'),
(2772, 23, 99, 'آنگاه که یکی از ایشان را مرگ فرارسد، گوید پروردگارا، مرا باز گردانید'),
(2773, 23, 100, 'باشد که در آنچه فروگذار کرده‌ام، کاری شایسته پیش گیرم، حاشا، این سخنی است که او [ظاهرا] گوینده آن است، و پیشاپیش آنان [زندگی‌] برزخی است تا روزی که برانگیخته شوند'),
(2774, 23, 101, 'آنگاه که در صور دمیده شود، در آن روز پیوند و خویشی در میانشان برقرار نماند، و از هم پرس و جو نکنند'),
(2775, 23, 102, 'آنگاه کسانی که کفه اعمالشان سنگین باشد، آنانند که رستگارند'),
(2776, 23, 103, 'و کسانی که کفه اعمالشان سبک باشد، آنان کسانی هستند که بر خود زیان زده‌اند و جاودانه در جهنم‌اند'),
(2777, 23, 104, 'چهره‌هایشان را آتش می‌گدازد، و ایشان در آن ترشرو هستند'),
(2778, 23, 105, '[به آنان گویند] آیا آیات من بر شما خوانده نمی‌شد، و شما آنها را دروغ می‌انگاشتید؟'),
(2779, 23, 106, 'گویند پروردگارا شقاوت ما بر ما چیره شد و قومی گمراه بودیم‌'),
(2780, 23, 107, 'پروردگارا ما را از آن [جهنم‌] بیرون‌آور، و اگر [به کارهای گذشته‌] بازگشتیم، آنگاه ستم‌پیشه‌ایم‌'),
(2781, 23, 108, 'گوید در آن گم شوید، و با من سخن مگویید'),
(2782, 23, 109, 'چرا که گروهی از بندگان من بودند که می‌گفتند پروردگارا ایمان آورده‌ایم، ما را بیامرز و بر ما رحمت‌آور، و تو بهترین مهربانانی‌'),
(2783, 23, 110, 'آنگاه شما ایشان را به ریشخند می‌گرفتید، تا آنجا که یاد مرا [از بس به آنها پرداختید] از خاطر شما بردند، و به آنان می‌خندیدید'),
(2784, 23, 111, 'امروز به خاطر صبری که پیشه کرده بودند، پاداششان می‌دهم، ایشانند که کامروا هستند'),
(2785, 23, 112, 'گوید چه مدت در روی زمین، به شمار سالیان، به سر بردید؟'),
(2786, 23, 113, 'گویند [به اندازه‌] روزی یا بخشی از روز به سر بردیم، [باید] از شمارگران بپرسی‌'),
(2787, 23, 114, 'گفت اگر می‌دانستید جز اندکی به سر نبرده‌اید'),
(2788, 23, 115, 'آیا پنداشته‌اید که شما را بیهوده آفریده‌ایم، و شما به نزد ما بازگردانده نمی‌شوید؟'),
(2789, 23, 116, 'بس پاک و فراتر است خداوند فرمانروای بر حق [از کار بیهوده‌]، خدایی جز او نیست که پروردگار عرش گرانقدر است‌'),
(2790, 23, 117, 'و هر کس در جنب خداوند خدایی دیگر بپرستد که در این کار حجتی ندارد، جز این نیست که حسابش با پروردگارش است، آری کافران رستگار نمی‌شوند'),
(2791, 23, 118, 'و بگو پروردگارا بیامرز و رحمت آور و تو بهترین مهربانانی‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'این سوره‌ای است که فرو فرستاده‌ایمش و [احکام‌] آن را واجب گردانده‌ایم، و در آن آیاتی روشنگر نازل کرده‌ایم باشد که پند گیرید'),
(2793, 24, 2, 'زن و مرد زناکار [بکر] را به هر یک از آنان یکصد تازیانه بزنید، و اگر به خداوند و روز بازپسین ایمان دارید، در دین الهی، در حق آنان دچار ترحم نشوید، و گروهی از مؤمنان، در صحنه عذاب کشیدن آنان حاضر باشند'),
(2794, 24, 3, 'مرد زانی نباید جز با زن زانی یا زن مشرک ازدواج کند، همچنین با زن زانی نباید جز مرد زانی یا مشرک ازدواج کند، و این کار بر مؤمنان حرام گردیده است‌'),
(2795, 24, 4, 'و کسانی که به زنان پاکدامن تهمت [زنا] می‌زنند، سپس چهار شاهد نمی‌آورند، ایشان را هشتاد تازیانه بزنید، و دیگر هرگز شهادت آنان را قبول نکنید، و اینانند که فاسقند'),
(2796, 24, 5, 'مگر کسانی که پس از آن توبه کنند و کارشایسته پیش بگیرند، که خداوند آمرزگار مهربان است‌'),
(2797, 24, 6, 'و کسانی که به زنانشان تهمت [زنا] می‌زنند، و شاهدی جز خویشتن ندارند، [باید] چهار بار به نام خداوند سوگند بخورند که ایشان راستگو هستند'),
(2798, 24, 7, 'و بار پنجم بگویند که لعنت الهی بر او باد اگر از دروغگویان باشد'),
(2799, 24, 8, 'و عذاب [حد] این گونه از زن، بازداشته می‌شود که چهار بار سوگند به نام خداوند بخورد که او [شوهرش‌] از دروغگویان است‌'),
(2800, 24, 9, 'و بار پنجم بگوید که خشم الهی بر آن زن باد اگر آن مرد از راستگویان باشد'),
(2801, 24, 10, 'و اگر بخشش و بخشایش الهی و رحمت او بر شما نباشد و اینکه خداوند توبه‌پذیر فرزانه است [کار بر شما دشوار می‌شد]'),
(2802, 24, 11, 'کسانی که تهمت ناپاکی را در میان آوردند، جماعتی از شما هستند، آن را شری به زیان خویش مپندارید، بلکه [در نهایت‌] خیری برای شماست، بر عهده هر یک از آنان سهمی از گناه است که مرتکب شده است، و کسی از آنان که عمده آن را دامن زد، عذابی سهمگین دارد'),
(2803, 24, 12, 'چرا چون آن را شنیدید، مردان و زنان مؤمن در حق خویش گمان نیک نبردند و نگفتند که این تهمت آشکاری است‌'),
(2804, 24, 13, 'چرا بر آن چهار گواه نیاوردند، و چون گواهان را نیاوردند، اینان نزد خداوند دروغگو هستند'),
(2805, 24, 14, 'و اگر در دنیا و آخرت بخشایش و رحمت الهی بر شما نبود در آنچه گفت و گو می‌کردید به شما عذابی سهمگین می‌رسید'),
(2806, 24, 15, 'آنگاه که از زبان همدیگر فرا می‌گرفتیدش و دهان به دهان چیزی را که به آن علم نداشتید، می‌گفتید و آن را آسان [و ساده‌] می‌پنداشتید، حال آنکه آن نزد خداوند سترگ است‌'),
(2807, 24, 16, 'و چرا چون شنیدیدش نگفتید که ما را نرسد که در این باره سخن گوییم، پاکا که تویی، این بهتانی بزرگ است‌'),
(2808, 24, 17, 'خداوند اندرزتان می‌دهد که اگر مؤمن هستید، هرگز مانند آن را تکرار مکنید'),
(2809, 24, 18, 'و خداوند آیاتش را برای شما روشن می‌گرداند و خداوند دانای فرزانه است‌'),
(2810, 24, 19, 'کسانی که خوش دارند که بدنامی در حق مؤمنان شایع گردد، در دنیا و آخرت عذابی دردناک دارند و خداوند [حقایق را] می‌داند و شما نمی‌دانید'),
(2811, 24, 20, 'و اگر بخشایش و رحمت الهی در حق شما نبود و اینکه خداوند رئوف و مهربان است [شما را سخت کیفر می‌داد]'),
(2812, 24, 21, 'ای مؤمنان از گامهای شیطان پیروی مکنید و هر کس از گامهای شیطان پیروی کند [بداند که‌] او به ناشایستی و نابکاری فرمان می‌دهد، و اگر بخشایش و رحمت الهی در حق شما نبود، هیچ‌یک از شما هرگز پاکدل نمی‌شد، ولی خداوند است که هر کس را بخواهد پاکدل می‌سازد و خداوند شنوای داناست‌'),
(2813, 24, 22, 'و متمکنان و توانگران شما نباید سوگند بخورند که به خویشاوندان و بینوایان و مهاجران [بی‌چیز] در راه خدا بخشش نکنند، و باید که عفو کنند و درگذرند، آیا دوست ندارید که خداوند از شما درگذرد؟ و خدا آمرزگار مهربان است‌'),
(2814, 24, 23, 'بی‌گمان کسانی که به زنان پاکدامن بی‌خبر مؤمن، تهمت زنا می‌زنند، در دنیا و آخرت ملعونند و عذابی سهمگین [در پیش‌] دارند'),
(2815, 24, 24, 'روزی که زبان و دست و پاهایشان بر آنها به کارهایی که کرده‌اند شهادت دهند'),
(2816, 24, 25, 'در چنین روزی خداوند جزای حقانی‌شان را به تمام و کمال بدهد و بدانند که خداوند بر حق آشکار است‌'),
(2817, 24, 26, 'زنان پلید برای مردان پلیدند، و مردان پلید برای زنان پلید، و زنان پاک برای مردان پاکند و مردان پاک برای زنان پاک، اینانند که از آنچه [شایعه افکنان‌] در حقشان می‌گویند بری و برکنار هستند، و از مغفرت الهی و روزی ارزشمند برخوردارند'),
(2818, 24, 27, 'ای مؤمنان به خانه‌هایی جز خانه‌های خودتان وارد نشوید، مگر آنکه آشنایی دهید و اجازه گیرید و بر اهل آنها سلام کنید این به خیر شماست، باشد که پند پذیرید'),
(2819, 24, 28, 'و اگر کسی را در آنها نیافتید، وارد نشوید، تا آنکه به شما اجازه داده شود، و اگر به شما گفته شد برگردید، برگردید که برای شما پاکیزه‌تر است، و خداوند به آنچه می‌کنید داناست‌'),
(2820, 24, 29, 'بر شما گناهی نیست که وارد خانه‌های غیر مسکونی شوید که در آنجا کالایی از آن شما هست، و خداوند آنچه آشکار می‌دارید و آنچه پنهان می‌دارید، می‌داند'),
(2821, 24, 30, 'به مردان مؤمن بگو دیدگان [از نظر بازی‌] فرو گذارند، و ناموسشان را محفوظ بدارند، این برای آنان پاکیزه‌تر است، بی‌گمان خداوند به آنچه می‌کنند آگاه است‌'),
(2822, 24, 31, 'و به زنان مؤمن [هم‌] بگو دیدگانشان را فروگذارند و ناموسشان را محفوظ بدارند و زینتشان را جز آنچه از آن آشکار است، آشکار نکنند، روسریهایشان را بر گریبانهایشان بیندازند، و زینتشان را آشکار نکنند مگر بر شوهرشان یا پدرشان، یا پدران شوهرشان یا پسرانشان، یا پسران شوهرشان، یا برادرشان یا پسران برادرشان، یا پسران خواهرشان یا زنان [همکیش‌]شان، یا ملک یمینهایشان، یا غلامانی که نیازمند [به زن‌] نیستند، یا کودکانی که بر نهانیهای زنان آگاه نیستند، و بگو که چنان پای نکوبند تا زینتی که پنهان داشته‌اند، معلوم شود، و ای مؤمنان همگی به درگاه خداوند توبه کنید، باشد که رستگار شوید'),
(2823, 24, 32, 'و بی‌همسران خویش و بردگان و کنیزکان صالح خویش را به همسری [دیگران‌] دهید، و اگر تهیدست باشند خداوند از بخشش خویش آنان را توانگر می‌گرداند، و خداوند گشایشگر داناست‌'),
(2824, 24, 33, 'و کسانی که اسباب زناشویی نمی‌یابند، پاکدامنی ورزند، تا آنکه خداوند از بخشش خویش توانگرشان گرداند، و کسانی از ملک یمینهایتان که قصد بازخرید خویش را دارند، اگر در آنان خیری سراغ دارید، آنان را بازخرید کنید، و به آنان [برای کمک به بازخریدشان‌] از مال الهی [سهمی از زکات‌] که به شما بخشیده است، ببخشید، و کنیزکانتان را اگر عزم پاکدامنی دارند، به فحشا وادار مکنید که مال دنیا به دست آورید، و هر کس ایشان را اجبار کند، بداند که خداوند با توجه به اکراهشان، آمرزگار مهربان است‌'),
(2825, 24, 34, 'و به راستی به سوی شما آیاتی روشنگر نازل کرده‌ایم، و مثلی از پیشینیانتان و پندی برای پرهیزگاران‌'),
(2826, 24, 35, 'خداوند نور آسمانها و زمین است داستان نورش همچون چراغدانی است که در آن چراغی هست، و چراغ در آبگینه‌ای هست، که آبگینه گویی ستاره‌ای درخشان است، که از درخت مبارک زیتون -که نه شرقی است و نه غربیافروخته شود نزدیک است که روغنش، با آنکه آتشی به آن نرسیده است، فروزان گردد، نور در نور است، خداوند به نور خویش هر کس را که خواهد هدایت کند، و خداوند برای مردم مثلها می‌زند و خداوند به هر چیزی داناست‌'),
(2827, 24, 36, 'در خانه‌هایی که خداوند فرمان داده است که گرامی داشته و نامش در آنها یاد شود، در آن بامدادان و شامگاهان نیایش او گویند'),
(2828, 24, 37, 'مردانی هستند که هیچ داد و ستد و خرید و فروشی ایشان را از یاد خداوند و برپاداشتن نماز و پرداختن زکات باز نمی‌دارد، و از روزی که در آن دلها و دیدگان دیگرگون شود، بیمناکند'),
(2829, 24, 38, 'تا خداوند به بهتر از آنچه کرده‌اند پاداششان دهد و از بخشش خویش بر پاداش آنان بیفزاید، و خداوند هر که را بخواهد بی‌حساب روزی می‌دهد'),
(2830, 24, 39, 'و کافران اعمالشان همچون سرابی در بیابانی است که تشنه آبش می‌پندارد، تا آنکه نزدیک آن رسد و آن را چیزی نیابد، و آنگاه خداوند را حاضر یابد که حسابش را به تمام و کمال بپردازد و خداوند زود شمار است‌'),
(2831, 24, 40, 'یا [اعمالشان‌] همچون تاریکیهایی است در دریایی ژرف که آن را موجی فرو پوشانده و برفراز آن موجی دیگر است که برفراز آن ابری است تاریکیهایی تو بر تو، چون دستش را برآورد، چه بسا نبیندش، و هر کس که خداوند برایش نوری مقرر نداشته باشد، نوری ندارد'),
(2832, 24, 41, 'آیا ندانسته‌ای که هر کس که در آسمانها و زمین است، و پرندگان بال گشاده خداوند را تسبیح می‌گویند هر یک نماز و نیایش را می‌داند، و خداوند به آنچه می‌کنند داناست‌'),
(2833, 24, 42, 'و فرمانروایی آسمانها و زمین از آن خداوند است، و سیر و سرانجام [جهان‌] به سوی خداوند است‌'),
(2834, 24, 43, 'آیا ندانسته‌ای که خداوند ابرها را می‌راند، سپس بین آنها را پیوند می‌دهد، سپس درهم فشرده‌اش می‌کند، آنگاه باران درشت را می‌بینی که از لابلای آن بیرون می‌آید، و سپس از آسمان، از ابری که به کوه می‌ماند تگرگ فرو می‌فرستد و به هر کس که بخواهد آن را می‌زند و از هر کس که بخواهد بر می‌گرداند، نزدیک است که درخشش برق آن، دیدگان را از بین ببرد'),
(2835, 24, 44, 'خداوند شب و روز را می‌گرداند [و پیاپی می‌آورد] بی‌گمان در این امر مایه عبرتی برای دیده‌وران است‌'),
(2836, 24, 45, 'و خداوند هر جانوری را از آب آفریده است، که بعضی از آنها بر شکمش راه می‌رود و بعضی از آنها بر دو پا راه می‌رود، و بعضی از آنها بر چهار [پا] راه می‌رود، خداوند هر چه بخواهد می‌آفریند، بی‌گمان خداوند بر هر کاری تواناست‌'),
(2837, 24, 46, 'به راستی آیاتی روشنگر نازل کردیم و خداوند هر کس را که بخواهد به راه راست هدایت می‌کند'),
(2838, 24, 47, 'و می‌گویند به خداوند و پیامبر ایمان آوردیم و فرمان می‌بریم، سپس گروهی از آنان بعد از این روی می‌گردانند، و اینان مؤمن نیستند'),
(2839, 24, 48, 'و چون به سوی خداوند و پیامبرش خوانده شوند که [پیامبر] در میان آنان داوری کند، آنگاه است که گروهی از آنان رویگردان می‌شوند'),
(2840, 24, 49, 'و اگر حق با آنان باشد [شتابان‌] با اطاعت و تسلیم به نزد او می‌آیند'),
(2841, 24, 50, 'آیا در دلهایشان بیماری است، یا شک و شبهه دارند، یا می‌ترسند که خداوند و پیامبر او بر آنان ستم روا دارند، نه بلکه اینان ستم‌پیشه‌اند'),
(2842, 24, 51, 'سخن مؤمنان چون به سوی خداوند و پیامبرش خوانده شوند که در میان آنان داوری کند، این است که می‌گویند شنیدیم و فرمان می‌بریم، و اینانند که رستگارند'),
(2843, 24, 52, 'و هر کس که از خداوند و پیامبر او اطاعت کند و از خداوند بترسد، و از او پروا داشته باشد، اینانند که کامیابند'),
(2844, 24, 53, 'و سخت‌ترین سوگندهایشان را به [نام‌] خداوند می‌خورند که اگر به ایشان دستور دهی [برای جهاد] بیرون می‌آیند بگو سوگند نخورید اطاعت متعارف بهتر [از گزافگویی‌] است، بی‌گمان خداوند به آنچه می‌کنید آگاه است‌'),
(2845, 24, 54, 'بگو از خداوند اطاعت کنید و از پیامبر [هم‌] اطاعت کنید، و اگر روی بگردانید، بر عهده او [پیامبر] تکلیف خود اوست، و بر شما تکلیف خود شماست، و اگر از او اطاعت کنید هدایت می‌یابید، و بر پیامبر جز پیام‌رسانی آشکار نیست‌'),
(2846, 24, 55, 'خداوند به کسانی از شما که ایمان آورده‌اند و کارهای شایسته کرده‌اند، وعده داده است که آنان را در این سرزمین جانشین گرداند، همچنانکه کسانی را که پیش از آنان بودند نیز جانشین [پیشینیان‌] گرداند، و دینشان را که بر آنان می‌پسندد، برای آنان پایگاه دهد، و بعد از بیمناکیشان، به آنان امن و امان ببخشد، [تا] مرا بپرستند و چیزی را شریک من قرار ندهند، و هر کس که بعد از این کفرورزد، اینانند که نافرمانند'),
(2847, 24, 56, 'و نماز را برپا دارید و زکات را بپردازید و از پیامبر اطاعت کنید باشد که مشمول رحمت شوید'),
(2848, 24, 57, 'کافران را هرگز در این سرزمین به ستوه‌آورنده‌[ی مؤمنان‌] مپندار، و سرا و سرانجام آنان آتش دوزخ است، و بد سیر و سرانجامی است‌'),
(2849, 24, 58, 'ای مؤمنان، باید که ملک یمینهایتان و کسانی از شما که هنوز به حد بلوغ نرسیده‌اند، سه بار، پیش از نماز صبح، و هنگامی که در ظهر لباسهایتان را درمی‌آورید، و پس از نماز عشاء، که سه هنگام برهنگی شماست، از شما [برای ورود] اجازه بگیرند، و پس از آن [سه هنگام‌] نه بر شما و نه برایشان گناهی نیست، چرا که پیرامون شما در گردشند، و با هم حشر و نشر دارید، بدین‌سان خداوند آیات خویش را برای شما روشن می‌گرداند، و خداوند دانای فرزانه است‌'),
(2850, 24, 59, 'و چون فرزندانتان به حد بلوغ می‌رسند، باید همانند کسانی که پیش از آنان [در همین شرایط] اجازه می‌گرفتند، [برای ورود] اجازه بگیرند، بدین‌سان خداوند آیاتش را برای شما روشن می‌گرداند، و خداوند دانای فرزانه است‌'),
(2851, 24, 60, 'و زنان یائسه‌ای که امید زناشویی ندارند، بر آنان گناهی نیست که جامه‌ها [/چادرها]یشان را فرو گذارند، به شرط آنکه زینت‌نمایی نکنند، و اینکه پاکدامنی بورزند [و چادرهایشان را فرو نگذارند] بر ایشان بهتر است، و خداوند شنوای داناست‌'),
(2852, 24, 61, 'بر نابینا ایرادی نیست، و بر لنگ ایرادی نیست و بر بیمار هم ایرادی نیست، و نیز بر خود شما در اینکه از [آذوقه‌] خانه‌های خودتان، یا خانه‌های پدرانتان، یا خانه‌های مادرانتان، یا خانه‌های برادرانتان، یا خانه‌های خواهرانتان، یا خانه‌های عموهایتان، یا خانه‌های عمه‌هایتان، یا خانه‌های دایی‌هایتان، یا خانه‌های خاله‌هایتان، یا خانه‌هایی که کلیدشان را در اختیار دارید، یا خانه‌های دوستان، بخورید، بر شما گناهی نیست در اینکه با همدیگر یا پراکنده‌وار [و تنها] غذا بخورید، و چون وارد هر خانه‌ای شدید بر خودتان سلام دهید که تحیت الهی است و مبارک و پسندیده است، بدین‌سان خداوند آیاتش را برای شما روشن می‌گرداند، باشد که اندیشه کنید'),
(2853, 24, 62, 'همانا مؤمنان کسانی هستند که به خداوند و پیامبر او ایمان دارند و چون با او در کاری همداستان شدند [به جایی‌] نمی‌روند مگر آنکه از او اجازه بگیرند بی‌گمان کسانی که از تو اجازه می‌گیرند همان کسانی هستند که به خداوند و پیامبر او ایمان دارند، و چون برای بعضی از کارهایشان از تو اجازه خواستند، به هر کس از آنان که خواستی اجازه بده و برای آنان از خداوند آمرزش بخواه، چرا که خداوند آمرزگار مهربان است‌'),
(2854, 24, 63, 'خواندن پیامبر را در میان خودتان همانند خواندن بعضی از شما بعضی دیگر را مشمارید، به راستی که خداوند کسانی را از شما که پنهانی و پناه‌جویانه خود را بیرون می‌کشند می‌شناسد، باید کسانی که از فرمان او سرپیچی می‌کنند بر حذر باشند از اینکه بلایی یا عذابی دردناک به آنان برسد'),
(2855, 24, 64, 'هان بی‌گمان آنچه در آسمانها و زمین است از آن خداوند است، به راستی می‌داند که شما اکنون در چه کاری هستید و روزی را که به سوی او باز گردانده می‌شوند، آنگاه آنان را از [نتیجه و حقیقت‌] آنچه کرده‌اند آگاه می‌گرداند و خداوند به هر چیزی داناست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'بزرگا کسی که فرقان را بر بنده‌اش نازل کرد تا هشداردهنده جهانیان باشد'),
(2857, 25, 2, 'کسی که فرمانروایی آسمانها و زمین از آن اوست، و فرزندی برنگزیده است، و در فرمانروایی شریکی ندارد، و همه‌چیز را آفریده است و به اندازه‌اش مقرر داشته است‌'),
(2858, 25, 3, 'و [مشرکان‌] به جای او خدایانی را به پرستش گرفته‌اند که چیزی نیافریده‌اند و خود آفریده شده‌اند، و برای خود اختیار زیان و سودی ندارند و اختیار میراندن و زنده‌داشتن و برانگیختن ندارند'),
(2859, 25, 4, 'و کافران گفتند این [قرآن‌] جز افترایی نیست که آن را بر ساخته است و گروهی دیگر بر آن یاریش داده‌اند، به راستی که ستم و بهتانی در میان آوردند'),
(2860, 25, 5, 'و گفتند افسانه‌های پیشینیان است که برای خود نسخه بر داشته است، و آن بامداد و شامگاه بر او خوانده می‌شود'),
(2861, 25, 6, 'بگو آن را کسی نازل کرده است که نهانیهای آسمان و زمین را می‌داند، او آمرزگار مهربان است‌'),
(2862, 25, 7, 'و گفتند این چه پیامبری است که غذا می‌خورد و در بازارها راه می‌رود، چرا فرشته‌ای با او فرستاده نشده است که همراه او هشداردهنده باشد؟'),
(2863, 25, 8, 'یا چرا گنجی بر او نازل نمی‌شود، یا چرا باغی ندارد که از [بار و بر] آن بخورد، و مشرکان گفتند که جز از مردی جادوزده پیروی نمی‌کنید'),
(2864, 25, 9, 'بنگر که چگونه برای تو مثل می‌زنند و گمراه شده‌اند و راهی [به جایی‌] نمی‌توانند برد'),
(2865, 25, 10, 'بزرگا کسی که اگر خواهد برای تو بهتر از این پدید آورد بوستانهایی که جویباران از فرودست آن جاری است، و برایت کوشکها قرار دهد'),
(2866, 25, 11, 'حق این است که قیامت را انکار کرده‌اند، و برای منکر قیامت آتشی فروزان آماده ساخته‌ایم‌'),
(2867, 25, 12, 'چون [دوزخ‌] از فاصله‌ای دور آنان را ببیند، خشم و خروشی از آن می‌شنوند'),
(2868, 25, 13, 'و چون دست و پا بسته در تنگنایی از آن انداخته شوند، آنجاست که زاری کنند'),
(2869, 25, 14, 'امروز یک بار زاری مکنید، بلکه بسیار زاری کنید'),
(2870, 25, 15, 'بگو آیا این بهتر است یا بهشت جاویدانی که به پرهیزگاران وعده داده شده است که پاداش و سرانجام آنان است‌'),
(2871, 25, 16, 'در آنجا هر چه خواهند هست و جاویدانند، این بر پروردگارت وعده‌ای واجب است‌'),
(2872, 25, 17, 'و روزی که آنان را با آنچه به جای خدا پرستیده‌اند، گرد آورد، [به آنان‌] گوید آیا شما این بندگان مرا گمراه کردید؟ یا آنکه خود ایشان ره گم کردند؟'),
(2873, 25, 18, 'گویند پاکا که تویی، ما را نرسد که به جای تو سروری بگیریم، ولی ایشان و پدرانشان را چندان برخوردار گرداندی که پند [قرآن‌] را فراموش کردند، و قومی سردرگم شدند'),
(2874, 25, 19, 'و به راستی گفته‌های [باطل‌] شما را تخطئه کردند، پس نه چاره‌ای توانید و نه نصرتی یابید، و هر کس از شما که ستم [شرک‌]ورزیده باشد، به او عذابی سهمگین می‌چشانیم‌'),
(2875, 25, 20, 'و پیش از تو کسی از پیامبران را نفرستادیم مگر آنکه غذا می‌خوردند و در بازارها راه می‌رفتند، و بعضی از شما را مایه آزمون بعضی دیگر ساخته‌ایم آیا شکیبایی می‌ورزید؟ و پروردگار تو بیناست‌'),
(2876, 25, 21, 'و کسانی که به لقای ما امید ندارند گویند چرا فرشتگان بر ما نازل نمی‌شوند، یا چرا پروردگارمان را نمی‌بینیم؟ در دلشان استکباری‌ورزیدند و سرکشی بزرگی کردند'),
(2877, 25, 22, 'روزی که فرشتگان را ببینند، آن روز بشارتی برای گناهکاران نیست، و گویند حرمان نصیب شماست‌'),
(2878, 25, 23, 'و به هرگونه کاری که کرده‌اند می‌پردازیم و آن را هیچ و پوچ می‌گردانیم‌'),
(2879, 25, 24, 'در آن روز بهشتیان خوش جایگاه‌تر و مرفه‌ترند'),
(2880, 25, 25, 'و روزی که آسمان با ابرها بشکافد و فرشتگان فرو فرستاده شوند'),
(2881, 25, 26, 'در چنین روزی فرمانروایی بر حق از آن خداوند رحمان است، و روزی است که بر کافران سخت و سنگین است‌'),
(2882, 25, 27, 'و روزی است که ستمکار [مشرک‌] دست [حسرت‌] می‌گزد و می‌گوید کاش من راه [پیروی از] پیامبر را پیش می‌گرفتم‌'),
(2883, 25, 28, 'وای بر من کاش من فلانی را دوست نمی‌گرفتم‌'),
(2884, 25, 29, 'او مرا از پند [قرآن‌] پس از آنکه برایم آمده بود، دور و گمراه کرد، و شیطان تنها گذار انسان است‌'),
(2885, 25, 30, 'و پیامبر گوید پروردگارا قوم من این قرآن را وانهادند'),
(2886, 25, 31, 'و بدین‌سان برای هر پیامبری دشمنی از گناهکاران قرار دادیم، و پروردگارت بس رهنما و یاور است‌'),
(2887, 25, 32, 'و کافران گویند چرا قرآن یکباره بر او نازل نمی‌شود؟ بدین‌سان [نازل می‌شود] تا دل تو را به آن استوار داریم و آن را چنانکه باید و شاید بخوانیم‌'),
(2888, 25, 33, 'و [کافران‌] به نزد تو هیچ مثلی نیاورند، مگر آنکه [جوابی‌] بر حق و خوش‌بیان‌تر برایت بیاوریم‌'),
(2889, 25, 34, '[آری‌] کسانی که به سوی جهنم بر روی چهره‌هایشان محشور شوند، اینان بدمرتبه‌تر و گمراه‌ترند'),
(2890, 25, 35, 'و به راستی به موسی کتاب آسمانی دادیم و برادرش هارون را همراه او دستیار گرداندیم‌'),
(2891, 25, 36, 'آنگاه گفتیم که به سوی قومی که آیات ما را دروغ انگاشتند بروید، آنگاه به کلی نابودشان کردیم‌'),
(2892, 25, 37, 'و قوم نوح چون پیامبران را دروغگو انگاشتند، غرقه‌شان کردیم و آنان را برای مردم مایه عبرت ساختیم، و برای ستمکاران [مشرک‌] عذابی دردناک آماده ساختیم‌'),
(2893, 25, 38, 'و عاد و ثمود و اصحاب رس و نسلهایی فراوان را در میان اینان [هلاک کردیم‌]'),
(2894, 25, 39, 'و برای هر یک مثلها زدیم و همه را یکایک هلاک ساختیم‌'),
(2895, 25, 40, 'و به سراغ شهری که بر آن باران بلا باریده شده بود، رفتند، آیا آن را نمی‌دیدند، یا بلکه امیدی به حشر و نشر نداشتند'),
(2896, 25, 41, 'و چون تو را می‌دیدند جز به ریشخندت نمی‌گرفتند [و می‌گفتند] آیا این همان کسی است که خداوند به پیامبری برانگیخته است؟'),
(2897, 25, 42, 'چه بسا نزدیک بود که ما را از پرستش خدایانمان -اگر در راه آنان مقاومت نمی‌کردیمبیراه کند، و به زودی، چون عذاب را بینند، بدانند که چه کسی گمراه‌تر است‌'),
(2898, 25, 43, 'آیا آن کس را که هوای نفسش را خدای خود گرفته بود، ندیده‌ای، آیا تو نگهبان او هستی؟'),
(2899, 25, 44, 'یا گمان می‌کنی که بیشترینه آنان گوش شنوا دارند یا تعقل می‌کنند، آنان جز همانند چارپایان نیستند، بلکه ایشان گمراه‌ترند'),
(2900, 25, 45, 'آیا نیندیشیده‌ای که پروردگارت چگونه سایه را می‌گسترد، و اگر می‌خواست آن را ساکن می‌گرداند، سپس خورشید را نمایانگر آن می‌گردانیم‌'),
(2901, 25, 46, 'سپس آن را اندک اندک به سوی خود باز می‌گیریم‌'),
(2902, 25, 47, 'و او کسی است که شب را برای شما پرده‌پوش و خواب را آرام‌بخش گرداند و روز را مایه جنب و جوش ساخت‌'),
(2903, 25, 48, 'و او کسی است که بادها را پیشاپیش رحمتش [باران‌] مژده‌بخش می‌فرستد، و از آسمان آبی بس پاکیزه فرو فرستادیم‌'),
(2904, 25, 49, 'تا بدان سرزمینی پژمرده را زنده گردانیم و آن را به چارپایان و مردمان بسیاری از آنان که آفریده‌ایمشان می‌نوشانیم‌'),
(2905, 25, 50, 'و به راستی که آن را گونه‌گونه برایشان بیان داشتیم تا پند گیرند، و بیشترینه مردم، چیزی غیر از ناسپاسی نخواستند'),
(2906, 25, 51, 'و اگر می‌خواستیم در هر شهری [پیامبر] هشداردهنده‌ای برمی‌انگیختیم‌'),
(2907, 25, 52, 'پس، از کافران اطاعت مکن و با آنان به سختی جهاد کن‌'),
(2908, 25, 53, 'و او کسی است که دو دریا را به هم برآمیخت این یک شیرین و خوشگوار، و این یک شور و تلخ و در میان آن دو برزخ و حایلی جداگر قرار داد'),
(2909, 25, 54, 'و او کسی است که از آب انسانی آفرید، و او را دارای پیوند نسبی و سببی گردانید و پروردگار تو تواناست‌'),
(2910, 25, 55, 'و به جای خداوند چیزی را می‌پرستند که نه سودی برایشان دارد و نه زیانی، و کافر بر مخالفت پروردگارش پشتیبان [دیگران‌] است‌'),
(2911, 25, 56, 'و ما تو را جز مژده رسان و هشداردهنده نفرستاده‌ایم‌'),
(2912, 25, 57, 'بگو برای آن از شما مزدی نمی‌طلبم، مگر اینکه کسی بخواهد که به سوی پروردگارش راه برد'),
(2913, 25, 58, 'و بر [خداوند] زنده‌ای که نمی‌میرد توکل کن و شاکرانه او را تسبیح گوی و او به گناهان بندگانش بس آگاه است‌'),
(2914, 25, 59, 'همان کسی که آسمانها و زمین و مابین آنها را در شش روز آفرید و سپس بر عرش استیلاء یافت، اوست خداوند رحمان، و درباره‌اش از [فردی‌] آگاه بپرس‌'),
(2915, 25, 60, 'و چون به آنان گفته شود به خداوند رحمان سجده برید، گویند رحمان دیگر کیست، آیا به چیزی که تو می‌فرمایی سجده بریم؟ و بر رمیدگیشان می‌افزاید'),
(2916, 25, 61, 'بزرگا کسی که در آسمان برجهایی آفریده است و در آنها چراغی و ماهی تابان قرار داده است‌'),
(2917, 25, 62, 'و او کسی است که شب و روز را پیایند همدیگر آفرید، تا هر که خواهد پند گیرد یا خواهد سپاس گزارد'),
(2918, 25, 63, 'و بندگان خدای رحمان کسانی‌اند که روی زمین فروتنانه راه می‌روند، و چون نادانان ایشان را مخاطب سازند، سلیمانه پاسخ دهند'),
(2919, 25, 64, 'و کسانی که برای پروردگارشان به سجده و قیام شب زنده‌داری کنند'),
(2920, 25, 65, 'و کسانی که گویند پروردگارا از ما عذاب جهنم را بگردان، چرا که عذاب آن سخت و سنگین است‌'),
(2921, 25, 66, 'آن بد جایگاه و منزلگاهی است‌'),
(2922, 25, 67, 'و کسانی که چون انفاق کنند، اسراف نمی‌کنند و بخل نمی‌ورزند و در میان این دو اعتدالی هست‌'),
(2923, 25, 68, 'و کسانی که در جنب خداوند خدای دیگری را نمی‌پرستند و هیچ نفسی را که خداوند [کشتنش را] حرام داشته، جز به حق نمی‌کشند، و زنا نمی‌کنند، و هر کس چنین کند [عقوبت‌] گناه را خواهد دید'),
(2924, 25, 69, 'در روز قیامت عذابش دو چندان شود، و به خواری و زاری جاودانه در آن [عذاب‌] بماند'),
(2925, 25, 70, 'مگر کسی که توبه کند و ایمان ورزد و عملی صالح پیشه کند، و اینان کسانی هستند که خداوند سیئاتشان را به حسنات بدل می‌کند، و خداوند آمرزگار مهربان است‌'),
(2926, 25, 71, 'و هر کس توبه کند و عمل صالح پیشه کند، حقا که به درگاه خداوند چنانکه باید و شاید توبه کرده است‌'),
(2927, 25, 72, 'و نیز کسانی که در مجلس باطل حضور نیابند و چون بر امری لغو بگذرند کریمانه بگذرند'),
(2928, 25, 73, 'و کسانی که چون آیات پروردگارشان را فرایادشان دهند، هنگام شنیدن آن ناشنواوار و نابیناوار به سجده درنیایند'),
(2929, 25, 74, 'و کسانی که گویند پروردگارا به ما از همسرانمان و زاد و رودمان مایه روشنی چشم ببخش، و ما را پیشوای پرهیزگاران قرار ده‌'),
(2930, 25, 75, 'اینانند که غرفه‌های بهشتی را به خاطر صبری که [در شداید] ورزیده‌اند، نصیب می‌برند، و در آنجا با تحیت و سلام روبه‌رو می‌شوند'),
(2931, 25, 76, 'جاودانه در آنند، چه نیکو جایگاه و منزلگاهی است‌'),
(2932, 25, 77, 'بگو اگر دعایتان نباشد، پروردگار من به شما اعتنایی ندارد، و به راستی که [حقایق‌] را دروغ انگاشته‌اید، و زودا که [عذابتان‌] گریبانگیر شود');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طسم طا سین میم'),
(2934, 26, 2, 'این آیات کتاب روشنگرست‌'),
(2935, 26, 3, 'چه بسا تو جانت را می‌فرسایی که چرا [مشرکان‌] مؤمن نمی‌شوند'),
(2936, 26, 4, 'اگر معجزه‌ای از آسمان بر آنان نازل می‌کنیم، آنگاه گردنهایشان را [به تسلیم‌] در برابر آن فرود خواهند آورد'),
(2937, 26, 5, 'و هیچ یادی نوپدید از سوی خدای رحمان برای آنان نیامد مگر آنکه از آن رویگردان بودند'),
(2938, 26, 6, 'به راستی که آن را دروغ انگاشتند، زودا که خبر [و نتیجه‌] آنچه ریشخندش می‌کردند برایشان بیاید'),
(2939, 26, 7, 'آیا به زمین ننگریسته‌اند که چه بسیار در آن از هر جفت ارزشمندی رویانده‌ایم‌'),
(2940, 26, 8, 'بی‌گمان در این مایه عبرتی هست، ولی بیشترینه آنان مؤمن نیستند'),
(2941, 26, 9, 'و بی‌گمان پروردگارت پیروزمند مهربان است‌'),
(2942, 26, 10, 'و چنین بود که پروردگارت به موسی ندا در داد که به سوی قوم ستم‌پیشه برو'),
(2943, 26, 11, 'قوم فرعون، که آیا پروا نمی‌کنید؟'),
(2944, 26, 12, 'گفت پروردگارا من می‌ترسم که مرا دروغگو بینگارند'),
(2945, 26, 13, 'و دلم تنگ می‌شود و زبانم گشاده نیست، پس به هارون نیز [برای همکاری‌] پیام بفرست‌'),
(2946, 26, 14, 'و آنان را بر من [ادعای‌] گناهی هست، لذا می‌ترسم که مرا بکشند'),
(2947, 26, 15, 'فرمود چنین نیست معجزات ما را [همراه‌] ببرید، ما خود همراه شما شنوا هستیم‌'),
(2948, 26, 16, 'به سوی فرعون بروید و بگویید ما پیامبر پروردگار جهانیان هستیم‌'),
(2949, 26, 17, 'برای اینکه را همراه ما بفرستی‌'),
(2950, 26, 18, '[فرعون‌] گفت آیا تو را در کودکی در میان خود پرورش ندادیم؟ و سالهایی از عمرت را در نزد ما به سر نبردی؟'),
(2951, 26, 19, 'و کاری را که کرده بودی کردی و تو از کافر [نعمت‌]انی‌'),
(2952, 26, 20, 'گفت آن کار را در هنگامی می‌کردم که از سرگشتگان بودم‌'),
(2953, 26, 21, 'آنگاه چون از شما ترسیدم از شما گریختم، سپس پروردگارم به من حکمت بخشید و مرا از پیامبران گردانید'),
(2954, 26, 22, 'و آیا این نعمتی است که بر من منت می‌نهی که بنی‌اسرائیل را به بردگی کشانده‌ای؟'),
(2955, 26, 23, 'فرعون گفت و "پروردگار جهانیان" دیگر چیست؟'),
(2956, 26, 24, 'گفت اگر ایقان داشته باشید، پروردگار آسمانها و زمین است و آنچه در میان آنهاست‌'),
(2957, 26, 25, '[فرعون‌] به اطرافیانش گفت آیا نمی‌شنوید؟'),
(2958, 26, 26, '[موسی‌] گفت پروردگار شما و پروردگار نیاکان نخستین شما'),
(2959, 26, 27, '[فرعون‌] گفت بی‌شک پیامبرتان که به سوی شما فرستاده شده است، دیوانه است‌'),
(2960, 26, 28, '[موسی‌] گفت اگر تعقل کنید [او] پروردگار مشرق و مغرب است و آنچه در میان آنهاست‌'),
(2961, 26, 29, '[فرعون‌] گفت اگر خدایی جز من برگزینی، تو را از زندانیان خواهم ساخت‌'),
(2962, 26, 30, '[موسی‌] گفت ولو معجزه‌ای آشکار برایت بیاورم؟'),
(2963, 26, 31, '[فرعون‌] گفت اگر راست می‌گویی بیاورش‌'),
(2964, 26, 32, 'آنگاه [موسی‌] عصایش را درانداخت و ناگهان به صورت اژدهایی آشکار درآمد'),
(2965, 26, 33, 'و دستش را [از گریبانش‌] بیرون آورد، و ناگهان سپید و درخشان در دید تماشاگران پدیدار شد'),
(2966, 26, 34, '[فرعون‌] به بزرگان پیرامونش گفت این [مرد] جادوگری داناست‌'),
(2967, 26, 35, 'می‌خواهد شما را با جادویش از سرزمینتان آواره کند، تا رأی شما چه باشد'),
(2968, 26, 36, 'گفتند او و برادرش را [به نحوی‌] بازدار و گردآورندگان [جادوگران‌] را به شهرها بفرست‌'),
(2969, 26, 37, 'تا هر جادوگر دانایی را به حضورت بیاورند'),
(2970, 26, 38, 'آنگاه جادوگران در موعد روزی معین گرد آورده شدند'),
(2971, 26, 39, 'و به مردم گفته شد آیا شما هم جمع می‌شوید؟'),
(2972, 26, 40, 'باشد که اگر جادوگران پیروز شدند از آنان پیروی کنیم‌'),
(2973, 26, 41, 'چون جادوگران [گرد] آمدند به فرعون گفتند آیا اگر ما پیروز شویم، پاداشی داریم؟'),
(2974, 26, 42, '[فرعون‌] گفت آری و شما در آن صورت از نزدیکان [من‌] خواهید بود'),
(2975, 26, 43, 'موسی به آنان گفت چیزی را که اندازنده‌اش هستید، بیندازید'),
(2976, 26, 44, 'آنگاه ریسمانها و چوبدستی‌هایشان را در انداختند و گفتند به جاه و جلال فرعون که ما پیروز خواهیم شد'),
(2977, 26, 45, 'سپس موسی عصایش را درانداخت [که اژدها شد] و ناگهان برساخته‌هایشان را فرو بلعید'),
(2978, 26, 46, 'و جادوگران به سجده افتادند'),
(2979, 26, 47, 'گفتند به پروردگار جهانیان ایمان آوردیم‌'),
(2980, 26, 48, 'پروردگار موسی و هارون‌'),
(2981, 26, 49, '[فرعون‌] گفت آیا پیش از آنکه به شما اجازه دهم به او ایمان آوردید، بی‌شک او بزرگتر [و آموزگار] شماست که به شما جادوگری آموخته است به زودی خواهید دانست، دستان و پاهایتان را در خلاف جهت همدیگر خواهم برید، سپس همگی‌تان را به دار خواهم زد'),
(2982, 26, 50, '[جادوگران‌] گفتند باکی نیست، ما به پروردگارمان روی آورده‌ایم‌'),
(2983, 26, 51, 'ما امید داریم که پروردگارمان خطاهای ما را ببخشد چرا که نخستین ایمان آورندگان بوده‌ایم‌'),
(2984, 26, 52, 'و به موسی وحی کردیم که بندگان مرا شبانه روانه کن، که شما تعقیب می‌شوید'),
(2985, 26, 53, 'آنگاه فرعون گردآورندگان را به شهرها فرستاد'),
(2986, 26, 54, '[آنان درباره یاران موسی‌] گفتند اینان گروهکی اندکشمارند'),
(2987, 26, 55, 'و ایشان ما را به خشم آورده‌اند'),
(2988, 26, 56, 'و ما انبوهی مسلح هستیم‌'),
(2989, 26, 57, 'آنگاه ایشان را از باغها و چشمه‌ها آواره کردیم‌'),
(2990, 26, 58, 'و از گنجها و جایگاه نیکو'),
(2991, 26, 59, 'و بدین‌سان آنها را به بنی‌اسرائیل به میراث دادیم‌'),
(2992, 26, 60, 'سپس ایشان را به هنگام طلوع آفتاب دنبال کردند'),
(2993, 26, 61, 'و چون دو گروه همدیگر را دیدند، یاران موسی گفتند، ما گیر افتاده‌ایم‌'),
(2994, 26, 62, '[موسی‌] گفت چنین نیست، پروردگارم با من است و به زودی مرا راهنمایی خواهد کرد'),
(2995, 26, 63, 'سپس به موسی وحی کردیم که با عصایت به دریا بزن، آنگاه [دریا] بشکافت و هر پاره‌ای از آن همچون کوهی بزرگ بود'),
(2996, 26, 64, 'و دیگران را به آنجا نزدیک کردیم‌'),
(2997, 26, 65, 'و موسی و همراهانش، همگیشان را رهانیدیم‌'),
(2998, 26, 66, 'سپس دیگران را غرقه ساختیم‌'),
(2999, 26, 67, 'بی‌گمان در این مایه عبرتی هست، و بیشترینه آنان مؤمن نبودند'),
(3000, 26, 68, 'همانا پروردگارت پیروزمند مهربان است‌'),
(3001, 26, 69, 'و بر آنان داستان ابراهیم را بخوان‌'),
(3002, 26, 70, 'چنین بود که به پدرش و قومش گفت که شما چه می‌پرستید؟'),
(3003, 26, 71, 'گفتند بتانی می‌پرستیم و در خدمت آنها معتکفیم‌'),
(3004, 26, 72, '[ابراهیم‌] گفت آیا چون آنان را به پرستش می‌خوانید، ندای شما را می‌شنوند؟'),
(3005, 26, 73, 'یا برای شما سودی یا زیانی دارند'),
(3006, 26, 74, 'گفتند نه ولی پدرانمان را یافتیم که چنین می‌کردند'),
(3007, 26, 75, '[ابراهیم‌] گفت آیا در آنچه می‌پرستید تامل نکرده‌اید؟'),
(3008, 26, 76, 'هم شما و هم نیاکان کهنتان‌'),
(3009, 26, 77, '[بدانید که‌] آنها دشمن منند، بر خلاف پروردگار جهانیان‌'),
(3010, 26, 78, 'همان کسی که مرا آفریده است و همو که هدایتم می‌کند'),
(3011, 26, 79, 'و همان کسی که مرا سیر و سیراب می‌سازد'),
(3012, 26, 80, 'و چون بیمار شدم مرا شفا می‌بخشد'),
(3013, 26, 81, 'و همان کسی که مرا می‌میراند و سپس [از نو] زنده‌ام می‌سازد'),
(3014, 26, 82, 'و همان کسی که امید دارم که در روز جزا خطای مرا ببخشد'),
(3015, 26, 83, 'پروردگارا به من حکمت ببخش و مرا به شایستگان بپیوند'),
(3016, 26, 84, 'و برای من در میان امتهای آینده سخن [گوی‌] نیک قرار ده‌'),
(3017, 26, 85, 'و مرا از میراث بران بهشت پرناز و نعمت بدار'),
(3018, 26, 86, 'و پدرم را بیامرز که او از سرگشتگان بود'),
(3019, 26, 87, 'و مرا در روزی که [مردم‌] برانگیخته شوند رسوا مساز'),
(3020, 26, 88, '[همان‌] روزی که مال و پسران سود ندهد'),
(3021, 26, 89, 'مگر آنکه کسی دلی پاک و پیراسته [از شک و شرک‌] به نزد خداوند آورد'),
(3022, 26, 90, 'و بهشت برای پرهیزگاران نزدیک شود'),
(3023, 26, 91, 'و جهنم برای گمراهان آشکار گردد'),
(3024, 26, 92, 'و به آنان گفته شود آنچه به جای خداوند می‌پرستیدید کجاست‌'),
(3025, 26, 93, 'آیا شما را یاری می‌دهند یا خود یاری می‌یابند؟'),
(3026, 26, 94, 'آنگاه ایشان و گمراهان [دیگر] در آن سرنگون شوند'),
(3027, 26, 95, 'و سپاهیان ابلیس همگی‌'),
(3028, 26, 96, 'در حالی که در آنجا با همدیگر ستیزه می‌کنند گویند'),
(3029, 26, 97, 'به خدا ما در گمراهی آشکار بودیم‌'),
(3030, 26, 98, 'چرا که شما را با پروردگار جهانیان برابر می‌شمردیم‌'),
(3031, 26, 99, 'و جز گناهکاران کسی ما را گمراه نکرد'),
(3032, 26, 100, 'حال هیچ شفیعی نداریم‌'),
(3033, 26, 101, 'و نه دوستی مهربان‌'),
(3034, 26, 102, 'کاش برای ما بازگشتی بود، تا آنگاه از مؤمنان می‌شدیم‌'),
(3035, 26, 103, 'بی‌گمان در این مایه عبرتی هست، و بیشترینه آنان مؤمن نبودند'),
(3036, 26, 104, 'و همانا پروردگارت پیروزمند مهربان است‌'),
(3037, 26, 105, 'قوم نوح پیامبران را دروغگو می‌انگاشتند'),
(3038, 26, 106, 'چنین بود که برادرشان نوح به ایشان گفت آیا [از شرک‌] پروا نمی‌کنید؟'),
(3039, 26, 107, 'من برای شما پیامبر امینی هستم‌'),
(3040, 26, 108, 'پس از خدا پروا و از من پیروی کنید'),
(3041, 26, 109, 'و برای آن از شما پاداشی نمی‌طلبم، پاداش من جز بر پروردگار جهانیان نیست‌'),
(3042, 26, 110, 'پس از خداوند پروا و از من پیروی کنید'),
(3043, 26, 111, 'گفتند آیا به تو ایمان بیاوریم، حال آنکه فرومایگان از تو پیروی می‌کنند'),
(3044, 26, 112, '[نوح‌] گفت من از [حقیقت‌] آنچه ایشان کرده‌اند آگاهی ندارم‌'),
(3045, 26, 113, 'اگر دریابید، حساب ایشان جز بر پروردگار من نیست‌'),
(3046, 26, 114, 'و من طردکننده مؤمنان نیستم‌'),
(3047, 26, 115, 'من جز هشداردهنده‌ای روشنگر نیستم‌'),
(3048, 26, 116, 'گفتند ای نوح اگر دست برنداری از سنگسار شدگان خواهی شد'),
(3049, 26, 117, '[نوح‌] گفت پروردگارا قوم من مرا دروغگو انگاشتند'),
(3050, 26, 118, 'پس در میان من و ایشان، چنانکه باید و شاید، داوری کن و مرا و همراهان مؤمنم را رهایی ده‌'),
(3051, 26, 119, 'آنگاه او و همراهانش را در کشتی پر و گرانبار رهایی دادیم‌'),
(3052, 26, 120, 'سپس بازماندگان را غرقه ساختیم‌'),
(3053, 26, 121, 'بی‌گمان در این مایه عبرتی هست، و بیشترینه آنان مؤمن نبودند'),
(3054, 26, 122, 'و همانا پروردگارت پیروزمند مهربان است‌'),
(3055, 26, 123, 'قوم عاد پیامبران را دروغگو انگاشتند'),
(3056, 26, 124, 'چنین بود که برادرشان هود به آنان گفت آیا [از شرک‌] پروا نمی‌کنید؟'),
(3057, 26, 125, 'من برای شما پیامبری امینم‌'),
(3058, 26, 126, 'پس از خداوند پروا و از من پیروی کنید'),
(3059, 26, 127, 'و برای آن از شما پاداشی نمی‌طلبم، پاداش من جز بر پروردگار جهانیان نیست‌'),
(3060, 26, 128, 'آیا در هر بلندی بنایی از سر بازیچه می‌سازید'),
(3061, 26, 129, 'و کوشکها[ی استوار] می‌سازید به امید آنکه جاویدان بمانید؟'),
(3062, 26, 130, 'و چون دست گشایید همچون زورگویان دست گشایید'),
(3063, 26, 131, 'پس از خداوند پروا و از من پیروی کنید'),
(3064, 26, 132, 'و از کسی که شما را به آنچه می‌دانید یاری‌تان داد، پروا کنید'),
(3065, 26, 133, 'شما را با [بخشیدن‌] چارپایان و پسران یاری داد'),
(3066, 26, 134, 'و نیز باغها و چشمه‌سارها'),
(3067, 26, 135, 'من از عذاب روزی سهمگین بر شما بیمناکم‌'),
(3068, 26, 136, 'گفتند برای ما یکسان است چه پند دهی، چه از پنددهندگان نباشی‌'),
(3069, 26, 137, 'این جز شیوه پیشینیان نیست‌'),
(3070, 26, 138, 'و ما از عذاب شوندگان نیستیم‌'),
(3071, 26, 139, 'بدین‌سان او را دروغگو انگاشتند، آنگاه آنان را نابود کردیم، بی‌گمان در این مایه عبرتی هست، و بیشترینه آنان مؤمن نبودند'),
(3072, 26, 140, 'و همانا پروردگارت پیروزمند مهربان است‌'),
(3073, 26, 141, 'قوم ثمود [هم‌] پیامبران را دروغگو انگاشتند'),
(3074, 26, 142, 'چنین بود که برادرشان صالح به آنان گفت آیا [از شرک‌] پروا نمی‌کنید؟'),
(3075, 26, 143, 'من برای شما پیامبری امینم‌'),
(3076, 26, 144, 'پس از خداوند پروا و از من پیروی کنید'),
(3077, 26, 145, 'و برای آن از شما پاداشی نمی‌طلبم، پاداش من جز بر پروردگار جهانیان نیست‌'),
(3078, 26, 146, 'آیا شما را به حالتی که در اینجا هست در امن و امان رها می‌کنند؟'),
(3079, 26, 147, 'در باغها و چشمه‌سارها'),
(3080, 26, 148, 'و کشتزارها و خرمابنانی که بار و بر آنها لطیف است‌'),
(3081, 26, 149, 'و از کوهها، ماهرانه [برای خود] خانه‌هایی می‌تراشید'),
(3082, 26, 150, 'پس از خداوند پروا و از من پیروی کنید'),
(3083, 26, 151, 'و از فرمان گزافکاران اطاعت مکنید'),
(3084, 26, 152, '[همان‌] کسانی که در این سرزمین فتنه و فساد می‌کنند و در صلاح نمی‌کوشند'),
(3085, 26, 153, 'گفتند جز این نیست که تو از جادوزدگانی‌'),
(3086, 26, 154, 'تو جز بشری همانند ما نیستی، اگر راست می‌گویی معجزه‌ای بیاور'),
(3087, 26, 155, 'گفت این ماده شتری است که آبشخوری [معین‌] برای او، و آبشخور روزی معین برای شماست‌'),
(3088, 26, 156, 'و به آن آسیبی نرسانید که عذاب روزی سهمگین گریبانگیرتان خواهد شد'),
(3089, 26, 157, 'آنگاه آن را پی کردند، و سپس پشیمان شدند'),
(3090, 26, 158, 'و عذاب ایشان را فرو گرفت، بی‌گمان در این مایه عبرتی هست و بیشترینه آنان مؤمن نبودند'),
(3091, 26, 159, 'و همانا پروردگارت پیروزمند مهربان است‌'),
(3092, 26, 160, 'قوم لوط [هم‌] پیامبران را دروغگو انگاشتند'),
(3093, 26, 161, 'چنین بود که برادرشان لوط به آنان گفت آیا [از شرک‌] پروا نمی‌کنید؟'),
(3094, 26, 162, 'من برای شما پیامبری امینم‌'),
(3095, 26, 163, 'پس از خداوند پروا و از من پیروی کنید'),
(3096, 26, 164, 'و برای آن از شما پاداشی نمی‌طلبم، پاداش من جز بر پروردگار جهانیان نیست‌'),
(3097, 26, 165, 'آیا از میان مردم جهان با مردان می‌آمیزید؟'),
(3098, 26, 166, 'و همسرانی که پروردگارتان برای شما آفریده است رها می‌کنید، آری شما قومی تجاوزکار هستید'),
(3099, 26, 167, 'گفتند ای لوط اگر دست برنداری از رانده شدگان خواهی شد'),
(3100, 26, 168, '[لوط] گفت من از دشمنان کار و کردار شما هستم‌'),
(3101, 26, 169, 'پروردگارا مرا و خانواده‌ام را از شر کاری که می‌کنند رهایی ده‌'),
(3102, 26, 170, 'آنگاه او و خانواده‌اش همگی را رهانیدیم‌'),
(3103, 26, 171, 'مگر پیرزنی را که از بازپس ماندگان بود'),
(3104, 26, 172, 'سپس دیگران را نابود کردیم‌'),
(3105, 26, 173, 'و بر آنان بارانی [از بلا] باریدیم، چه بد است باران هشداریافتگان‌'),
(3106, 26, 174, 'بی‌گمان در این مایه عبرتی هست، و بیشترینه آنان مؤمن نبودند'),
(3107, 26, 175, 'و همانا پروردگارت پیروزمند مهربان است‌'),
(3108, 26, 176, 'اصحاب ایکه [هم‌] پیامبران را دروغگو انگاشتند'),
(3109, 26, 177, 'چنین بود که شعیب به ایشان گفت آیا [از شرک‌] پروا نمی‌کنید؟'),
(3110, 26, 178, 'من برای شما پیامبری امینم‌'),
(3111, 26, 179, 'پس از خداوند پروا و از من پیروی کنید'),
(3112, 26, 180, 'و برای آن از شما پاداشی نمی‌طلبم، پاداش من جز بر پروردگار جهانیان نیست‌'),
(3113, 26, 181, 'پیمانه را تمام دهید و از کم‌فروشان مباشید'),
(3114, 26, 182, 'و با ترازوی درست بسنجید'),
(3115, 26, 183, 'و به مردم اجناسشان را کم مدهید و در این سرزمین فتنه و فساد برپا مکنید'),
(3116, 26, 184, 'و از کسی که شما و امت پیشین را آفریده است پروا کنید'),
(3117, 26, 185, 'گفتند جز این نیست که تو از جادوزدگانی‌'),
(3118, 26, 186, 'و تو جز بشری همانند ما نیستی و ما تو را بی‌شبهه از دروغگویان می‌دانیم‌'),
(3119, 26, 187, 'پس اگر از راستگویانی پاره‌ای از آسمان را بر [سر] ما بینداز'),
(3120, 26, 188, '[شعیب‌] گفت پروردگار من به آنچه می‌کنید داناتر است‌'),
(3121, 26, 189, 'آنگاه او را دروغگو انگاشتند و عذاب روز ابری سایه‌افکن آنان را فروگرفت، که عذاب روزی سهمگین بود'),
(3122, 26, 190, 'بی‌گمان در این مایه عبرتی هست و بیشترینه آنان مؤمن نبودند'),
(3123, 26, 191, 'و همانا پروردگارت پیروزمند مهربان است‌'),
(3124, 26, 192, 'و آن [قرآن‌] فرو فرستاده پروردگار جهانیان است‌'),
(3125, 26, 193, 'که روح‌الامین [جبرئیل‌] آن را بر دل تو فرود آورده است‌'),
(3126, 26, 194, 'تا از هشداردهندگان باشی‌'),
(3127, 26, 195, 'به زبان عربی شیوا'),
(3128, 26, 196, 'و [خبر] آن در صحف [آسمانی‌] پیشینیان هست‌'),
(3129, 26, 197, 'آیا این برای آنان نشانه‌[ی صدق‌] نیست که علمای بنی‌اسرائیل آن را [از پیش‌] می‌شناسند'),
(3130, 26, 198, 'و اگر آن را بر بعضی از گنگان بیگانه زبان نازل کرده بودیم‌'),
(3131, 26, 199, 'سپس آن را برایشان می‌خواند، به آن ایمان نمی‌آوردند'),
(3132, 26, 200, 'بدین‌سان آن را در دلهای گناهکاران راه داده‌ایم‌'),
(3133, 26, 201, 'که به آن ایمان نمی‌آورند مگر آنکه عذاب دردناک را بنگرند'),
(3134, 26, 202, 'که ناگهانی بر سر آنان آید و آنان آگاه نباشند'),
(3135, 26, 203, 'آنگاه گویند آیا ما مهلت یافته خواهیم بود؟'),
(3136, 26, 204, 'پس آیا عذاب ما را به شتاب می‌خواهند'),
(3137, 26, 205, 'ملاحظه کن که اگر سالها برخوردارشان سازیم‌'),
(3138, 26, 206, 'سپس آنچه از آن بیمشان داده‌ایم، به سراغ آنان آید'),
(3139, 26, 207, 'آن برخورداریشان به کار آنان نیاید'),
(3140, 26, 208, 'و [اهل‌] هیچ شهری را نابود نکردیم مگر آنکه هشداردهندگانی داشتند'),
(3141, 26, 209, '[برای‌] پندآموزی، و ما ستمگر نبوده‌ایم‌'),
(3142, 26, 210, 'و آن را شیاطین نازل نکرده‌اند'),
(3143, 26, 211, 'و آنان را نرسد، و چنین کاری نتوانند کرد'),
(3144, 26, 212, 'چرا که ایشان از شنیدن [وحی‌] برکنار هستند'),
(3145, 26, 213, 'پس در جنب خداوند خدایی دیگر [به نیایش‌] مخوان که از عذاب دیدگان خواهی شد'),
(3146, 26, 214, 'و خاندان خویشاوندت را هشدار ده‌'),
(3147, 26, 215, 'و در برابر مؤمنانی که از تو پیروی می‌کنند مهربان و فروتن باش‌'),
(3148, 26, 216, 'پس اگر از تو سرپیچی کردند بگو که من از آنچه می‌کنید بری و برکنارم‌'),
(3149, 26, 217, 'و بر [خداوند] پیروزمند مهربان توکل کن‌'),
(3150, 26, 218, 'همان که تو را به هنگامی که [برای عبادت‌] برمی‌خیزی می‌بیند'),
(3151, 26, 219, 'و نیز گشتن تو را در میان نمازگزاران‌'),
(3152, 26, 220, 'چرا که او شنوای داناست‌'),
(3153, 26, 221, 'آیا شما را آگاه کنم که شیاطین بر چه کسی فرود می‌آیند؟'),
(3154, 26, 222, 'بر هر تهمت‌زن گناهکاری فرود می‌آیند'),
(3155, 26, 223, 'گوش فرا می‌دهند و بیشترینه آنان دروغگو هستند'),
(3156, 26, 224, 'و شاعران [کافر] را گمراهان پیروی می‌کنند'),
(3157, 26, 225, 'آیا نمی‌نگری که ایشان در هر وادی سرگشته‌اند'),
(3158, 26, 226, 'و ایشان چیزهایی می‌گویند که خود انجام نمی‌دهند'),
(3159, 26, 227, 'مگر کسانی که ایمان آورده و کارهای شایسته کرده‌اند و خداوند را بسیار یاد می‌کنند، و پس از آنکه ستم دیده‌اند انتقامشان را گرفته‌اند، و کسانی که ستم کرده‌اند زودا که بدانند که به چه بازگشتگاهی راه خواهند برد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طس [طا سین‌] این آیات قرآن و کتابی روشنگر است‌'),
(3161, 27, 2, 'رهنمود و مژده‌بخش مؤمنان‌'),
(3162, 27, 3, 'کسانی که نماز را برپا می‌دارند و زکات را می‌پردازند و به آخرت ایقان دارند'),
(3163, 27, 4, 'بی‌گمان کسانی که به آخرت ایمان ندارند کار و کردارشان را در چشمشان آراسته جلوه می‌دهیم، و ایشان سرگشته‌اند'),
(3164, 27, 5, 'اینان کسانی هستند که عذاب سخت را [در پیش‌] دارند و در آخرت زیانکارترین [انسانها] هستند'),
(3165, 27, 6, 'و تو قرآن را از پیشگاه فرزانه‌ای دانا فرا می‌گیری‌'),
(3166, 27, 7, 'چنین بود که موسی به خانواده‌اش گفت من آتشی می‌بینم، به زودی از آن خبری برای شما می‌آورم یا شعله اخگری برایتان می‌آورم باشد که گرم شوید'),
(3167, 27, 8, 'و چون به نزدیک آن آمد، ندا داده شد که هر کس در نزدیک آتش و پیرامون آن است، برکت یافته است، و پاک است خداوندی که پروردگار جهانیان است‌'),
(3168, 27, 9, 'ای موسی آن منم که خداوند پیروزمند فرزانه هستم‌'),
(3169, 27, 10, 'و عصایت را بینداز، آنگاه که آن را نگریست که می‌جنبید گویی که ماری است، پشت کرد [و پا به فرار گذاشت‌] و برنگشت [گفته شد] ای موسی مترس که پیامبران در پیشگاه من نمی‌ترسند'),
(3170, 27, 11, 'مگر کسی که ستمی کرده باشد، سپس نیکوکاری را جانشین گناه کرده باشد، که آنگاه من آمرزگار مهربانم‌'),
(3171, 27, 12, 'و دستت را در گریبانت کن، تا سپید و درخشان بدون هیچ بیماری [پیسی‌] بیرون آید [و با] معجزات نه‌گانه به سوی فرعون و قومش [برو] که ایشان قومی نافرمانند'),
(3172, 27, 13, 'و چون آیات ما روشنگرانه به سر وقت آنان آمد، گفتند این جادویی آشکار است‌'),
(3173, 27, 14, 'و آنها را از در ستم و سرکشی انکار کردند، و حال آنکه دلهایشان آنها را باور داشت، بنگر که سرانجام اهل فساد چگونه بود'),
(3174, 27, 15, 'و به راستی به داوود و سلیمان دانشی بخشیدیم و هر دو گفتند سپاس خداوند را که ما را بر بسیاری از بندگان مؤمنش برتری داد'),
(3175, 27, 16, 'و سلیمان از داوود میراث برد و گفت ای مردم به ما [فهم‌] زبان مرغان آموخته شده است، و بسی چیزها به ما بخشیده شده است، این بخششی آشکار است‌'),
(3176, 27, 17, 'و سپاهیان سلیمان از جن و انس و مرغان به نزد او گرد آمدند، آنگاه به همدیگر پیوستند'),
(3177, 27, 18, 'تا آنجا که به وادی موران رسیدند، موری [به زبان حال‌] گفت ای موران به درون خانه‌هایتان بروید که سلیمان و سپاهیانش شما را ناآگاهانه درهم نکوبند'),
(3178, 27, 19, '[سلیمان‌] از سخن او دهان به خنده گشود و گفت پروردگارا به من الهام کن [و توفیق ده‌] که بر نعمتت که بر من و پدر و مادرم ارزانی داشتی سپاسگزارم و کاری نیک کنم که آن را بپسندی، و مرا به رحمتت در زمره بندگان شایسته‌ات درآور'),
(3179, 27, 20, 'و سپس از [حال‌] مرغان بازجست و گفت مرا چه می‌شود که هدهد را نمی‌بینم، یا شاید از غائبان است؟'),
(3180, 27, 21, 'او را عذابی سخت می‌کنم یا سرش را می‌برم، مگر آنکه حجتی روشن برای من [برای غیبت خود] بیاورد'),
(3181, 27, 22, 'آنگاه [هدهد] مدتی نه چندان دراز [در غیبت‌] درنگ کرد، سپس [آمد و] گفت من به چیزی که تو آگاهی نیافتی، آگاه شدم و برایت از سبا خبری یقینی آورده‌ام‌'),
(3182, 27, 23, 'من زنی را یافتم که بر آنان فرمانروایی می‌کرد و از همه‌چیز برخوردار بود، و تختی شگرف داشت‌'),
(3183, 27, 24, 'او و قومش را چنین یافتم که به جای خداوند، به خورشید سجده می‌کردند، و شیطان کار و کردارشان را در نظرشان آراسته جلوه داده بود، و ایشان را از راه [خدا] باز داشته بود و آنان رهیافته نبودند'),
(3184, 27, 25, 'تا سجده نکنند در برابر خداوندی که نهان آسمانها و زمین را آشکار می‌کند و آنچه پنهان می‌دارید و آنچه آشکار می‌دارید می‌داند'),
(3185, 27, 26, 'خداوند است که خدایی جز او نیست و او پروردگار عرش عظیم است‌'),
(3186, 27, 27, '[سلیمان‌] گفت خواهیم دید که آیا راست گفته‌ای یا از دروغگویان بوده‌ای‌'),
(3187, 27, 28, 'این نامه مرا ببر و نزد آنان بیفکن، سپس از آنان روی برتاب، و ببین که چه پاسخی می‌دهند'),
(3188, 27, 29, '[بلقیس‌] گفت ای بزرگان نزد من نامه‌ای گرامی انداخته شده است‌'),
(3189, 27, 30, 'آن از سوی سلیمان است و با نام خداوند بخشنده مهربان [آغاز می‌گردد]'),
(3190, 27, 31, 'حاکی از اینکه بر من سرکشی مکنید و با من از در تسلیم در آیید'),
(3191, 27, 32, '[بلقیس‌] گفت ای بزرگان در کارم به من نظر دهید [چرا که‌] هیچ کاری را از پیش نبرده‌ام، مگر آنکه شما در آن حاضر و ناظر بوده‌اید'),
(3192, 27, 33, 'گفتند ما نیرومندان و رزم‌آوران سهمگینی هستیم و حکم حکم توست، بنگر که چه می‌فرمایی‌'),
(3193, 27, 34, '[بلقیس‌] گفت همانا پادشاهان چون به شهری درآیند، آن را به تباهی کشند و گرانقدران اهلش را بی‌مقدار گردانند، و اینگونه عمل کنند'),
(3194, 27, 35, 'و من فرستنده هدیه‌ای به سوی آنان هستم و چشم به راه اینکه فرستادگان چه پاسخی می‌آورند'),
(3195, 27, 36, 'و چون [پیک‌] نزد سلیمان آمد، [سلیمان هدیه را دید و] گفت آیا به من مدد مالی می‌رسانید؟ بدانید که آنچه خداوند به من بخشیده است، از آنچه به شما بخشیده است بهتر است، آری [می‌بینم که‌] شما به هدیه‌تان دلخوشید'),
(3196, 27, 37, 'به سوی آنان باز گرد [و بگو] به سر آنان لشکری آوریم که تاب پایداری در برابر آن را نداشته باشند و آنان را خوار و زبون از آنجا آواره کنیم‌'),
(3197, 27, 38, 'گفت ای بزرگان کدامیک از شما پیش از آنکه آنان از در تسلیم نزد من آیند، تخت او را برای من می‌آورد؟'),
(3198, 27, 39, 'عفریتی از جنیان گفت من پیش از آنکه از جایت برخیزی آن را به نزد تو می‌آورم، و من بر این کار توانا و درستکارم‌'),
(3199, 27, 40, 'کسی که از "علم کتاب" بهره‌ای داشت گفت من پیش از آنکه چشمت را برهم زنی آن را به نزدت می‌آورم [پذیرفت و آورد] و چون [سلیمان‌] آن را در نزد خویش مستقر دید، گفت این از فضل پروردگار من است که می‌آزمایدم که آیا شکر می‌ورزم یا کفران، و هر کس شکرورزد جز این نیست که به سود خویش شکرورزیده است و هر کس کفران کند، پروردگارم بی‌نیاز و بخشنده است‌'),
(3200, 27, 41, '[سلیمان‌] گفت تختش را در دید او ناشناخته کنید، تا بنگریم که به آن پی می‌برد یا از کسانی است که پی نمی‌برند'),
(3201, 27, 42, 'و چون [بلقیس‌] آمد به او گفته شد آیا تخت تو این چنین است؟ گفت‌گویی همان است، و پیش از این، به ما علم [به حقانیت سلیمان‌] داده شده است و ما فرمانبردار بوده‌ایم‌'),
(3202, 27, 43, 'و آنچه به جای خدا می‌پرستید، او را [از ایمان به خدا] باز داشته بود، که او [در آغاز] از قوم کافران بود'),
(3203, 27, 44, 'به او گفته شد وارد قصر شو، و چون آن را دید پنداشت که برکه آبی است، و از دو ساق پایش جامه برداشت [سلیمان‌] گفت آن قصری صاف و صیقلی از آبگینه‌هاست [و آب نیست‌] [بلقیس‌] گفت پروردگارا بر خود ستم کرده‌ام و اینک همراه سلیمان در برابر خداوند که پروردگار جهانیان است فرمانبردارم‌'),
(3204, 27, 45, 'و به راستی به سوی قوم ثمود، برادرشان صالح را فرستادیم، که خداوند را بپرستید، آنگاه به دو گروه ستیزه‌جو تقسیم شدند'),
(3205, 27, 46, '[صالح‌] گفت ای قوم من چرا عذاب را پیش از رحمت، به شتاب می‌طلبید، چرا از خداوند آمرزش نمی‌خواهید، باشد که مشمول رحمت شوید'),
(3206, 27, 47, 'گفتند ما به تو و همراهانت فال بد زده‌ایم گفت فال شما با خداست، بلکه شما قومی هستید که در معرض آزمونید'),
(3207, 27, 48, 'و در شهر نه تن بودند که در آن سرزمین به فساد می‌پرداختند و هیچ‌کار شایسته‌ای نمی‌کردند'),
(3208, 27, 49, 'گفتند به خدا هم قسم شوید که بر او و خانواده‌اش شبیخون بزنیم، سپس به خونخواهش بگوییم که ما در کشتار خانواده‌اش حاضر و ناظر نبوده‌ایم، و ما راستگوییم‌'),
(3209, 27, 50, 'و مکرورزیدند، و ما نیز مکر در میان آوردیم و آنان در نمی‌یافتند'),
(3210, 27, 51, 'پس بنگر که سرانجام مکرشان چگونه بود که ایشان و قومشان را همگی، نابود کردیم‌'),
(3211, 27, 52, 'این است خانه‌هایشان که به خاطر ستمی که ورزیدند [خاموش و] خالی است، بی‌گمان در این برای اهل معرفت مایه عبرتی هست‌'),
(3212, 27, 53, 'و کسانی را که ایمان آوردند و پروا و پرهیز ورزیدند نجات دادیم‌'),
(3213, 27, 54, 'و نیز لوط را [فرستادیم‌] که به قومش گفت آیا دیده و دانسته مرتکب کار ناشایسته می‌شوید؟'),
(3214, 27, 55, 'آیا شما از روی شهوت، با مردان به جای زنان می‌آمیزید؟ آری شما قومی ندانم‌کار هستید'),
(3215, 27, 56, 'آنگاه پاسخ قوم او جز این نبود که می‌گفتند خاندان لوط را از شهرتان برانید که ایشان مردمی منزه‌طلب هستند'),
(3216, 27, 57, 'آنگاه او و خانواده‌اش را نجات دادیم، مگر زنش را که جزو واپس‌ماندگان تعیینش کرده بودیم‌'),
(3217, 27, 58, 'و بر آنان بارانی [از سنگ‌] باراندیم، و باران [بلای‌] هشداریافتگان چه بد است‌'),
(3218, 27, 59, 'بگو سپاس خداوند را و سلام بر بندگانی که ایشان را برگزیده است، آیا خداوند بهتر است یا آنچه برایش شریک می‌آورند؟'),
(3219, 27, 60, 'یا کیست که آسمانها و زمین را آفریده است و از آسمان برای شما آبی فرو فرستاده است و بدان باغهای خرم رویانده‌ایم که شما نمی‌توانید درختانش را برویانید، آیا در جنب خداوند خدایی هست؟ آری آنان قومی کژرو هستند'),
(3220, 27, 61, 'یا کیست که زمین را قرارگاه ساخت و در میان آن جویبارها پدید آورد و برای آن کوههای استوار آفرید و بین دو دریا برزخی قرار داد، آیا در جنب خداوند خدایی هست، بلکه بیشترینه آنان نمی‌دانند'),
(3221, 27, 62, 'یا کیست که دعای درمانده را چون بخواندش، اجابت می‌کند، و بلا را می‌گرداند، و شما را جانشینان [پیشینیان بر] روی زمین می‌کند آیا در جنب خداوند خدایی هست؟ چه اندک پند می‌گیرید'),
(3222, 27, 63, 'یا کیست که شما را در تاریکیهای خشکی و دریا راه می‌نماید و کیست که بادها را پیشاپیش [باران‌] رحمتش مژده‌بخش می‌فرستد؟ آیا در جنب خداوند خدایی هست، خداوند فراتر است از آنچه شریکش می‌گیرند'),
(3223, 27, 64, 'یا کیست که آفرینش را آغاز می‌کند، سپس آن را باز می‌گرداند، و کیست که شما را از آسمان و زمین روزی می‌دهد؟ آیا در جنب خداوند خدایی هست؟ بگو اگر راست می‌گویید برهانتان را بیاورید'),
(3224, 27, 65, 'بگو هیچ‌کس جز خداوند در آسمان و زمین غیب نمی‌داند، و ایشان نمی‌دانند که چه هنگامی برانگیخته می‌شوند'),
(3225, 27, 66, 'یا مگر علمشان در[باره‌] آخرت به کمال است [؟!]، بلکه ایشان از آن در شک هستند، و بلکه در [درک‌] آن کوردل هستند'),
(3226, 27, 67, 'و کافران گویند آیا چون ما و پدرانمان [مردیم‌] و خاک شدیم آیا [از نو زنده و] برانگیخته خواهیم شد؟'),
(3227, 27, 68, 'به ما و پدرانمان از این پیش همچنین وعده‌ای داده‌اند، این جز افسانه‌های پیشینیان نیست‌'),
(3228, 27, 69, 'بگو در زمین سیر و سفر کنید و بنگرید که سرانجام گناهکاران چگونه بوده است‌'),
(3229, 27, 70, 'و بر آنان اندوه مخور، و از نیرنگی که می‌ورزند دلتنگ مباش‌'),
(3230, 27, 71, 'و گویند اگر راست می‌گویید این وعده کی فرا می‌رسد؟'),
(3231, 27, 72, 'بگو چه‌بسا بخشی از آنچه درباره‌اش بی‌تابی می‌کنید به شما برسد'),
(3232, 27, 73, 'و بی‌گمان پروردگارت به مردم بخشش و بخشایش دارد ولی بیشترینه آنان سپاس نمی‌گزارند'),
(3233, 27, 74, 'و بی‌گمان پروردگارت آنچه در دلهایشان دارند و آنچه را آشکار می‌کنند می‌داند'),
(3234, 27, 75, 'و هیچ نهفته‌ای در آسمان و زمین نیست مگر آنکه در کتابی روشنگر است‌'),
(3235, 27, 76, 'همانا این قرآن بیشترینه آنچه بنی‌اسرائیل در آن اختلاف نظر دارند بر آنان بیان می‌دارد'),
(3236, 27, 77, 'و آن رهنمود و رحمتی برای مؤمنان است‌'),
(3237, 27, 78, 'بی‌گمان پروردگارت با حکم خویش در میان آنان داوری خواهد کرد و او پیروزمند داناست‌'),
(3238, 27, 79, 'پس بر خداوند توکل کن که تو برخوردار از حق [و حقیقتی‌] آشکار هستی‌'),
(3239, 27, 80, 'تو به مردگان و کران، آوازی نشنوانی به ویژه آنگاه که پشت کنند'),
(3240, 27, 81, 'و تو رهنمای نابینایان [و باز گرداننده‌] از بیراهیشان نیستی، تو جز کسانی را که به آیات ما ایمان دارند و اهل تسلیمند، [سخن خود را] نمی‌شنوانی‌'),
(3241, 27, 82, 'و چون حکم [عذاب‌] ما بر آنان تعلق گرفت بر ایشان دابةالارض را از زمین برآوریم که با آنان سخن بگوید که مردم به آیات ما ایقان نداشته‌اند'),
(3242, 27, 83, 'و روزی که از هر امتی گروهی از دروغ‌انگاران آیات خود را محشور گردانیم و سپس به هم بپیوندند'),
(3243, 27, 84, 'چون گرد آمدند فرماید آیا آیات مرا دروغ انگاشتید، و حال آنکه به آنها احاطه علمی نداشتید، یا خود چه کارها کردید'),
(3244, 27, 85, 'و به خاطر ستمی که ورزیده بودند، حکم [عذاب‌] بر آنان تعلق گیرد، و سخن نگویند'),
(3245, 27, 86, 'آیا نگریسته‌اند که ما شب را آفریده‌ایم که در آن آرام گیرند، و روز را چشم‌اندازی روشن [گردانده‌ایم‌]، بی‌گمان در این برای اهل ایمان مایه‌های عبرت است‌'),
(3246, 27, 87, 'و روزی که در صور دمیده شود، و هر کس که در آسمانها و زمین هست -جز کسی که خدا خواهدهراسان شوند، و همگان خاکسارانه به نزد او آیند'),
(3247, 27, 88, 'و کوهها را بینی و آنها را ساکن انگاری و حال آنکه همانند حرکت ابر، حرکت دارد، [این‌] آفرینش الهی است که هر چه را در کمال استواری پدید آورده است، او به آنچه می‌کنید آگاه است‌'),
(3248, 27, 89, 'هر کس که کار نیکی پیش آورد، او را پاداشی است بهتر از آن، و آنان از هراس آن روز ایمن هستند'),
(3249, 27, 90, 'و هر کس کار بدی پیش آورد، چهره‌هایشان در آتش جهنم سرنگون شود، آیا جز در برابر آنچه کرده‌اید جزا می‌یابید؟'),
(3250, 27, 91, 'همانا فرمان یافته‌ام که پروردگار این شهر را که آن را حرم [امن‌] قرار داده است، بپرستم، و همه چیز او راست، و فرمان یافته‌ام که از مسلمانان باشم‌'),
(3251, 27, 92, 'و اینکه قرآن را بخوانم، پس هر کس رهیاب شود، همانا به سود خویش راه یافته است، و هر کس بیراه رود بگو که من فقط از هشداردهندگانم‌'),
(3252, 27, 93, 'و بگو سپاس خداوند را، زودا که آیات خویش را به شما بنمایاند و آنگاه آنها را بشناسید، و پروردگارت از آنچه می‌کنید غافل نیست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طسم طا سین میم'),
(3254, 28, 2, 'این آیات کتاب روشنگر است‌'),
(3255, 28, 3, 'بخشی از داستان موسی و فرعون را برای اهل ایمان به راستی و درستی بر تو می‌خوانیم‌'),
(3256, 28, 4, 'همانا فرعون در آن سرزمین سرکشی کرد و اهل آن را فرقه فرقه کرد، طایفه‌ای از آنان را به زبونی کشید [چنانکه‌] پسرانشان را می‌کشت و زنان [و دخترانشان را برای کنیزی‌] زنده باقی می‌گذاشت، او از تبهکاران بود'),
(3257, 28, 5, 'و ما می‌خواهیم که بر کسانی که در روی زمین به زبونی کشیده شده‌اند، منت نهیم [و نعمت دهیم‌] و ایشان را پیشوایان و وارثان گردانیم‌'),
(3258, 28, 6, 'و به آنان در روی زمین تمکن بخشیم، و از آنان به فرعون و هامان و سپاهیانشان چیزی که از آن پروا داشتند، نشان دادیم‌'),
(3259, 28, 7, 'و به مادر موسی الهام کردیم که او را شیر بده، و چون بر او بیمناک شدی، او را [در جعبه‌ای‌] به دریا بیفکن، و مترس و غم مخور، [چرا که‌] ما برگرداننده او به سوی تو و گرداننده او از پیامبران هستیم‌'),
(3260, 28, 8, 'آنگاه فرعونیان او را [یافتند و] برگرفتند تا سرانجام دشمن و مایه اندوهشان شود، چرا که فرعون و هامان و سپاهیانشان خطاکار بودند'),
(3261, 28, 9, 'و همسر فرعون گفت هم برای من و هم برای تو روشنی چشم است، او را نکشید، چه بسا به ما سود برساند، یا آنکه به فرزندی بگیریمش، و آنان [حقیقت را] در نیافته بودند'),
(3262, 28, 10, 'و دل مادر موسی به کلی [از امید و شکیب‌] خالی شد، چنانکه نزدیک بود، اگر دلش را گرم نمی‌کردیم که از باور دارندگان باشد، راز او را آشکار کند'),
(3263, 28, 11, 'و به خواهر او گفت پی او را بگیر، آنگاه دورا دور او را می‌پایید، ولی ایشان در نمی‌یافتند'),
(3264, 28, 12, 'و از پیش او [نوزاد] را از پذیرفتن پستانها[ی دایگان‌] باز داشتیم سپس [خواهر موسی‌] گفت آیا شما را به خانواده‌ای راهنمایی کنم که نگهداری او را برای شما بپذیرند، و خیرخواه او باشند؟'),
(3265, 28, 13, 'سرانجام او را به مادرش بازگرداندیم تا دل و دیده‌اش [به او] روشنی یابد و غم نخورد و بداند که وعده الهی حق است، ولی بیشترینه آنان نمی‌دانند'),
(3266, 28, 14, 'و چون [موسی‌] به کمال بلوغ رسید و برومند شد، به او حکمت [/نبوت‌] و علم بخشیدیم و بدین‌سان نیکوکاران را پاداش دهیم‌'),
(3267, 28, 15, 'و او در هنگامی که مردم شهر [سرگرم و] بی‌خبر بودند وارد شهر شد، آنگاه در آنجا دو مرد را یافت که با هم سخت ستیزه می‌کردند این یک از پیروانش، و آن یک از دشمنانش [بود]، آنگاه کسی که از پیروانش بود، در برابر کسی که از دشمنانش بود، از او یاری خواست، پس موسی مشتی به او زد که کارش ساخته شد [موسی تکان خورد و] گفت این کار شیطان بود، که او دشمن و گمراه‌کننده‌ای آشکار است‌'),
(3268, 28, 16, 'گفت پروردگارا من بر خود ستم کردم، مرا بیامرز، آنگاه [خداوند] او را آمرزید، چرا که او آمرزگار مهربان است‌'),
(3269, 28, 17, 'گفت پروردگارا به خاطر لطفی که در حق من کردی هرگز پشتیبان گناهکاران نخواهم شد'),
(3270, 28, 18, 'سپس ترسان و نگران در شهر می‌گشت، ناگهان همان کسی که دیروز از او یاری خواسته بود، باز از او فریادرسی خواست موسی [برآشفت و] به او گفت تو واقعا ندانم‌کاری‌'),
(3271, 28, 19, 'و چون خواست به کسی که دشمن هردوشان بود حمله برد، گفت ای موسی می‌خواهی مرا بکشی همانطور که دیروز کسی را کشتی، نمی‌خواهی مگر اینکه زورگوی ستمگری در این سرزمین باشی، و نمی‌خواهی از نیکوکاران باشی‌'),
(3272, 28, 20, 'و مردی از دورترین نقطه شهر شتابان آمد [و] گفت ای موسی بدان که بزرگان درباره‌ات همرأی شده‌اند که تو را بکشند [از این شهر] بیرون برو که من از خیرخواهان توام‌'),
(3273, 28, 21, 'آنگاه [موسی‌] از آنجا ترسان و نگران بیرون شد و گفت پروردگارا مرا از قوم ستمکار نجات بده‌'),
(3274, 28, 22, 'و چون رو به سوی مدین نهاد، گفت باشد که پروردگارم مرا به راه راست راهنمایی کند'),
(3275, 28, 23, 'و چون به آبشخور مدین رسید، پیرامون آن گروهی از مردم را یافت که [اغنام خویش را] آب می‌دادند، و از پس ایشان دو زن که [چارپایان خود را] جمع و جور می‌کردند [موسی‌] گفت کار و بار شما چیست؟ گفتند ما [به چارپایان خود] آب نمی‌دهیم تا آنکه شبانان [چارپایان خود را از آبشخور] بازگردانند، و پدر ما پیری فرتوت است‌'),
(3276, 28, 24, 'سپس برای آنها [چارپایانشان را] آب داد، آنگاه رو به سایه آورد، و گفت پروردگارا من به هر خیری که برایم بفرستی نیازمندم‌'),
(3277, 28, 25, 'سپس یکی از آن دو، در حالی که با شرم و آزرم گام برمی‌داشت، به نزد او آمد و گفت پدرم شما را دعوت کرده است که پاداش آبدهی‌ات را برای [چارپایان‌] ما به شما بدهد، و چون [موسی‌] به نزد او آمد و برای او داستانش را بیان کرد، [شعیب‌] گفت مترس که از قوم ستمکار نجات یافتی‌'),
(3278, 28, 26, 'یکی از آن دو [دختر] گفت پدرجان او را [با دستمزد] به کار گیر که او بهترین کسی است که می‌توانی به کار بگیری، هم تواناست و هم درستکار'),
(3279, 28, 27, '[شعیب به موسی‌] گفت من می‌خواهم یکی از این دو دخترم را به همسری تو درآورم، در قبال اینکه [به جای کابین‌] هشت سال برای من کار کنی، و اگر آن را به ده سال پایان دادی، میل خودت است، ولی من نمی‌خواهم بر تو سخت بگیرم، که مرا، اگر خدا بخواهد، از درستکاران خواهی یافت‌'),
(3280, 28, 28, '[موسی‌] گفت این بین من و بین شما باشد که هر کدام از دو مدت را به سر بردم، از من زیاده‌خواهی نشود، و خداوند بر آنچه می‌گوییم ضامن [و شاهد] ماست‌'),
(3281, 28, 29, 'و چون موسی مدت [مقرر] را به سر برد و خانواده‌اش را [همراه خود] برد، از جانب طور آتشی دید، به خانواده‌اش گفت صبر کنید، من [از دور] آتشی دیده‌ام، شاید از آنجا برای شما خبری یا پاره آتشی بیاورم، باشد که گرم شوید'),
(3282, 28, 30, 'و چون به نزدیک آن [آتش‌] آمد، از کرانه وادی ایمن، در جایگاه متبرک، از درخت ندا داده شد که ای موسی من خداوندم، پروردگار جهانیان‌'),
(3283, 28, 31, 'و عصایت را بینداز چون [انداخت و] آن را نگریست که می‌جنبید گویی که ماری بود، پشت کرد [و پا به فرار گذاشت‌] و برنگشت [گفته شد] ای موسی روی به این سو کن و مترس، تو از ایمنانی‌'),
(3284, 28, 32, 'دستت را در گریبانت کن، تا سپید و درخشان بدون هیچ بیماری [پیسی‌] بیرون آید، و بازوی خود را از ترس جمع کن، بدان که این دو، دو برهان از سوی پروردگارت هستند برای فرعون و بزرگان قومش، که ایشان قومی نافرمان هستند'),
(3285, 28, 33, '[موسی‌] گفت پروردگارا من یکی از ایشان را کشته‌ام و می‌ترسم که مرا بکشند'),
(3286, 28, 34, 'و برادرم هارون از من گشاده زبان‌تر است، او را یاور من بفرست که به صدق من گواهی دهد که من می‌ترسم مرا دروغگو بدانند'),
(3287, 28, 35, 'فرمود زودا که تو را با [پیوستن‌] برادرت نیرومند سازیم و به شما دو تن سلطه دهیم که با معجزات ما [که همراه شماست‌] دستشان به شما نرسد شما و هرکس از شما پیروی کند پیروزید'),
(3288, 28, 36, 'و چون موسی برای آنان معجزات روشنگر ما را آورد، گفتند این جز جادوی برساخته نیست و ما در حق نیاکانمان چنین چیزی نشنیده‌ایم‌'),
(3289, 28, 37, 'و موسی گفت پروردگارم داناتر است که چه کسی از سوی او رهنمود آورده است، و چه کسی نیک سرانجامی دارد، همانا ستمکاران رستگار نمی‌شوند'),
(3290, 28, 38, 'و فرعون گفت ای بزرگان برای شما خدایی جز خود نمی‌شناسم، و ای هامان برای من آتش بر گل برافروز [/ آجر بپز] و برای من برجی [بلند] برآور، باشد که به خدای موسی پی ببرم، و من او را از دروغگویان می‌دانم‌'),
(3291, 28, 39, 'و او و سپاهیانش به ناحق در روی زمین استکبار ورزیدند، و پنداشتند که ایشان به سوی ما بازگردانده نمی‌شوند'),
(3292, 28, 40, 'آنگاه او و سپاهیانش را فرو گرفتیم و آنان را به دریا رها کردیم، پس بنگر که سرانجام ستمکاران چگونه بوده است'),
(3293, 28, 41, 'و آنان را پیشوایانی خواندیم که به سوی آتش دوزخ دعوت می‌کنند، و روز قیامت یاری نمی‌یابند'),
(3294, 28, 42, 'و در این جهان، لعنتی گریبانگیرشان کردیم و در روز قیامت هم ایشان از نفرین زدگان هستند'),
(3295, 28, 43, 'و به راستی پس از آنکه نسلهای نخستین را نابود کردیم به موسی کتاب [آسمانی‌] دادیم که روشنگریهایی برای مردم و رهنمود و رحمت بود باشد که پندگیرند'),
(3296, 28, 44, 'و تو در جانب غربی [کوه‌] طور نبودی آنگاه که با موسی کار [رسالت‌] سپری کردیم و تو از گواهان نبودی‌'),
(3297, 28, 45, 'ولی [در این میان‌] نسلهایی پدید آوردیم و روزگار بر آنان دراز شد و تو در میان اهل مدین مقیم نبودی که آیات ما را برایشان بخوانی ولی ما فرستندگان [پیامبران‌] بودیم‌'),
(3298, 28, 46, 'و تو در جانب [غربی کوه‌] طور نبودی آنگاه که ندا در دادیم، ولی این [وحی‌] رحمتی از سوی پروردگار توست که قومی را که پیش از تو هشداردهنده‌ای به سویشان نیامده است، هشدار دهی باشد که پند بگیرند'),
(3299, 28, 47, 'و اگر به خاطر کار و کردار پیشینشان مصیبتی به آنان نمی‌رسید [و عذابی بر آنان نمی‌فرستادیم‌] می‌گفتند پروردگارا چرا پیامبری به سوی ما نفرستادی که از آیات تو پیروی کنیم و از مؤمنان باشیم‌'),
(3300, 28, 48, 'و چون از جانب ما حق به سوی آنان آمد گفتند چرا او [محمد -ص-] را نظیر آنچه به موسی داده بودند، نداده‌اند؟ آیا آنچه پیشتر به موسی داده شده بود، انکار نکردند؟ گفتند این دو [تورات و قرآن‌] جادوهایی هستند که از همدیگر پشتیبانی می‌کنند، و گفتند ما همه آنها را منکریم‌'),
(3301, 28, 49, 'بگو اگر راست می‌گویید کتابی از سوی خداوند بیاورید که از این دو راهنماتر باشد، تا از آن پیروی کنم‌'),
(3302, 28, 50, 'و اگر پاسخت را ندادند پس بدان که ایشان از هوی و هوسهایشان پیروی می‌کنند، و کیست گمراه‌تر از کسی که بدون رهنمود الهی از هوای نفس خویش پیروی کند، بی‌گمان خداوند قوم ستمکار را هدایت نمی‌کند'),
(3303, 28, 51, 'و برای آنان سخن در سخن پیوستیم باشد که پندگیرند'),
(3304, 28, 52, 'کسانی که پیش از آن به ایشان کتاب آسمانی داده بودیم، به آن ایمان می‌آورند'),
(3305, 28, 53, 'و چون [قرآن‌] بر آنان خوانده شود گویند به آن ایمان داریم آن حق و از جانب پروردگار ماست، ما پیش از آن اهل تسلیم [و باور] بوده‌ایم‌'),
(3306, 28, 54, 'اینانند که به خاطر صبری که ورزیده‌اند پاداششان دوباره داده شود، و بدی را با نیکی دفع کنند و از آنچه روزیشان کرده‌ایم انفاق کنند'),
(3307, 28, 55, 'و چون لغوی بشنوند از آن روی بر می‌گردانند و گویند اعمال ما از آن ما و اعمال شما از آن شما، سلام بر شما، ما با نادانان کاری نداریم‌'),
(3308, 28, 56, 'تو هرکس را که دوست داری هدایت نمی‌کنی، بلکه خداوند است که هرکس را که بخواهد هدایت می‌کند و او به رهیافتگان داناتر است‌'),
(3309, 28, 57, 'و گویند اگر همراه تو از هدایت پیروی کنیم، از سرزمینمان ربوده و رانده شویم، [بگو] آیا آنان را در حرمی امن جای نداده‌ایم که فرآورده‌های هر چیز -که روزی‌ای از جانب ماست[جمع و] به سوی آنان ارسال می‌گردد؟ ولی بیشترینه آنان نمی‌دانند'),
(3310, 28, 58, 'و چه بسیار شهرها که از [شدت رفاه‌] زندگانیشان سرمستی می‌کردند نابود کردیم و این خانه‌هایشان است که پس از آنان جز اندکی مسکون نبوده است، و ما خود وارثانیم‌'),
(3311, 28, 59, 'و پروردگار تو نابودگر شهرها نیست مگر آنکه در مرکز آنها پیامبری برانگیزد که بر آنان آیات ما را بخواند، و ما نابودکنندگان شهرها نیستیم مگر آنکه اهالی آنها ستمگر باشند'),
(3312, 28, 60, 'و آنچه به شما داده شود بهره زندگانی دنیا و پیرایه آن است، و آنچه نزد خداوند است بهتر و پایدارتر است، آیا اندیشه نمی‌کنید'),
(3313, 28, 61, 'آیا کسی که به او وعده‌ای نیکو داده‌ایم و او دریابنده آن است، مانند کسی است که به بهره زندگانی دنیا بهره‌مندش ساخته‌ایم، سپس در روز قیامت از حاضر شدگان [در صحنه عذاب‌] است‌'),
(3314, 28, 62, 'و روزی که به ایشان ندا در دهد و فرماید پس همتایان من که شما گمان می‌بردید، کجا هستند؟'),
(3315, 28, 63, 'کسانی که حکم [عذاب‌] بر آنان تعلق گرفته گویند پروردگارا اینان کسانی هستند که ما گمراهشان کردیم، ما همچنان که خود گمراه بودیم آنان را گمراه کردیم، [اینک‌] در نزد تو تبری می‌جوییم، ایشان [در واقع‌] ما را نمی‌پرستیدند'),
(3316, 28, 64, 'گفته شود همتایانی را که قائل بودید بخوانید آنگاه بخوانندشان، ولی آنان به ایشان پاسخ ندهند و عذاب را بنگرند، و تمنا کنند که کاش ایشان رهیافته بودند'),
(3317, 28, 65, 'و روزی که ایشان را ندا در دهد و فرماید به فرستادگان چه پاسخی دادید؟'),
(3318, 28, 66, 'آنگاه در آن روز همه اخبار و احوال بر آنان مشتبه شود، و ایشان همپرسی نکنند'),
(3319, 28, 67, 'و اما کسی که توبه کند و ایمان آورد و کاری شایسته در پیش گیرد، چه بسا از رستگاران باشد'),
(3320, 28, 68, 'و پروردگارت آنچه بخواهد می‌آفریند و بر می‌گزیند، اختیاری برای آنان نیست، خداوند پاک و فراتر از شرکی است که می‌ورزند'),
(3321, 28, 69, 'و پروردگارت آنچه دلهایشان پنهان می‌دارد و آنچه آشکار می‌دارد می‌داند'),
(3322, 28, 70, 'و او خداوند است که خدایی جز او نیست، سپاس او را در آغاز و انجام، و حکم او راست و به سوی او باز گردانده می‌شوید'),
(3323, 28, 71, 'بگو بیندیشید اگر خداوند شب را بر شما تا روز قیامت پاینده گرداند، چه خدایی جز خداوند برای شما روشنایی به میان می‌آورد، آیا نمی‌شنوید؟'),
(3324, 28, 72, 'بگو بیندیشید اگر خداوند روز را بر شما تا روز قیامت پاینده گرداند، چه خدایی جز خداوند برای شما شب را به میان می‌آورد که در آن بیارامید، آیا نمی‌نگرید؟'),
(3325, 28, 73, 'و از رحمت او [این است که‌] برای شما شب و روز آفریده است تا در آن بیارامید، و از فضل او روزی خویش بجویید باشد که سپاس بگزارید'),
(3326, 28, 74, 'و روزی که به ایشان ندا در دهد و فرماید پس همتایان من که شما گمان می‌بردید، کجا هستند؟'),
(3327, 28, 75, 'و از هر امتی گواهی جدا کنیم و گوییم برهانتان را بیاورید، آنگاه دانند که حق از آن خداوند است و برساخته‌شان از دید آنان گم شود'),
(3328, 28, 76, 'قارون از قوم موسی بود و بر آنان شورید و ما به او گنجینه‌هایی بخشیده بودیم که کلیدهای آن بر جوانانی نیرومند هم سنگین می‌آمد، چنین بود که قومش به او گفتند شادی مکن بی‌گمان خداوند شادی زدگان را دوست ندارد'),
(3329, 28, 77, 'و در آنچه خداوند بر تو بخشیده است، سرای آخرت را بجوی [و در عین حال‌] بهره‌ات را از دنیا هم فراموش مکن، و همچنانکه خداوند به تو نیکی کرده است، نیکی کن، و در این سرزمین فتنه و فساد مجوی که خداوند تبهکاران را دوست ندارد'),
(3330, 28, 78, 'گفت همانا به خاطر علمی که دارم اینها را به من داده‌اند، آیا نمی‌دانست که خداوند پیش از او از میان نسلها[یی که بودند] کسانی را که از او نیرومندتر و مال‌اندوزتر بودند، نابود کرده است، و گناهکاران را از گناهانشان نپرسند'),
(3331, 28, 79, 'آنگاه [قارون‌] با تجملش در میان قومش آشکار شد، و کسانی که خواهان زندگانی دنیا بودند گفتند ای کاش ما نیز مانند آنچه به قارون داده شده است داشتیم، چرا که او دارای بهره [و مال و منال‌] کلانی است‌'),
(3332, 28, 80, 'و دانش یافتگان گفتند وای بر شما، ثواب الهی برای کسی که ایمان آورد و نیکوکاری کند بهتر است، و جز شکیبایان آن را فرانگیرد'),
(3333, 28, 81, 'آنگاه او و خانه‌اش را به زمین فرو بردیم، و کس و کاری نداشت که در برابر خداوند یاریش دهند و به فریادش هم نرسیدند'),
(3334, 28, 82, 'و کسانی که دیروز مقام او را آرزو می‌کردند می‌گفتند وه که خداوند روزی را بر هرکس از بندگانش که بخواهد گشاده می‌دارد و بر هرکس که بخواهد تنگ می‌گیرد، اگر خداوند بر ما منت نهاده [و نعمت نداده‌] بود، ما را نیز فرو می‌برد، وه که کافران رستگار نمی‌شوند'),
(3335, 28, 83, 'این سرای آخرت است، آن را برای کسانی که در روی زمین نمی‌خواهند سرکشی و تبهکاری کنند، مقرر داشته‌ایم، و سرانجام نیک از آن پرهیزگاران است‌'),
(3336, 28, 84, 'هرکس که نیکی پیش آرد، او را [پاداشی‌] بهتر از آن است، و هرکس بدی پیش آورد، بدانند که کسانی که کارهای ناروا انجام می‌دهند جز همانند آنچه کرده‌اند کیفر نیابند'),
(3337, 28, 85, 'بی‌گمان کسی که [احکام‌] قرآن را بر تو واجب کرد، بازگرداننده تو به بازگشتگاه توست، بگو پروردگارم بهتر می‌داند که چه کسی هدایت پیش آورده است، و چه کسی در گمراهی آشکار است‌'),
(3338, 28, 86, 'و امید نداشتی که کتاب آسمانی بر تو فرود آید، [این نبود] مگر رحمتی از جانب پروردگارت، پس پشتیبان کافران مباش‌'),
(3339, 28, 87, 'و تو را از آیات الهی پس از آنکه بر تو نازل گردیده است باز ندارند، و به سوی پروردگارت بخوان و از مشرکان مباش‌'),
(3340, 28, 88, 'و در جنب خداوند خدایی دیگر مخوان، که خدایی جز او نیست، همه چیز فناپذیر است، مگر ذات او، حکم او راست، و به سوی او بازگردانده می‌شوید');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الم الف لام میم'),
(3342, 29, 2, 'آیا مردم گمان می‌برند که رهایشان کنند که [به زبان‌] بگویند ایمان آورده‌ایم و ایشان را نمی‌آزمایند؟'),
(3343, 29, 3, 'و به راستی پیشینیان آنان را آزموده‌ایم، و بی‌شک خداوند راستگویان و دروغگویان را معلوم می‌دارد'),
(3344, 29, 4, 'یا کسانی که کارهای ناروا انجام می‌دهند گمان می‌برند که بر ما پیشی می‌گیرند، چه بد است داوریشان‌'),
(3345, 29, 5, 'هرکس که به لقای الهی امید داشته باشد [بداند که‌] اجل مقرر الهی فرارسنده است و او شنوای داناست‌'),
(3346, 29, 6, 'و هرکس [در راه حق‌] بکوشد، به سود خویش کوشیده است، بی‌گمان خداوند از جهانیان بی‌نیاز است‌'),
(3347, 29, 7, 'و کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند سیئاتشان را از ایشان می‌زداییم و به بهتر از آنچه کرده‌اند پاداششان دهیم‌'),
(3348, 29, 8, 'و انسان را سفارش کرده‌ایم که به پدر و مادرش نیکی کند و [می‌گوییم‌] اگر تو را واداشتند که چیزی را که بدان علم نداری شریک من گردانی، پس از آن دو اطاعت مکن، چرا که بازگشت شما به سوی من است و آنگاه به [حقیقت‌] آنچه می‌کردید آگاهتان می‌سازم‌'),
(3349, 29, 9, 'و کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، در زمره شایستگان در می‌آوریمشان‌'),
(3350, 29, 10, 'و از مردم کسی هست که می‌گوید به خدا ایمان آورده‌ایم و چون در راه خدا رنج و آزار بیند، آزارگری مردم را همچون عذاب الهی پندارد، و اگر نصرتی از سوی پروردگارت فراز آید، می‌گویند ما همراه شما بودیم، آیا خداوند به آنچه در دلهای جهانیان هست آگاه‌تر نیست؟'),
(3351, 29, 11, 'و خداوند مؤمنان را [نیک‌] می‌شناسد و منافقان را [نیک‌] می‌شناسد'),
(3352, 29, 12, 'و کافران به مؤمنان گویند از راه ما پیروی کنید ما گناهانتان را به گردن می‌گیریم، ولی در حقیقت هیچ باری از گناهان ایشان را به گردن نگیرند، که ایشان دروغگو هستند'),
(3353, 29, 13, 'و باشد که بار گناهان خودشان و بار گناهانی علاوه بر بار گناهان خودشان را بر دوش گیرند، و روز قیامت از آنچه افتراء می‌بستند خواهندشان پرسید'),
(3354, 29, 14, 'و به راستی نوح را به سوی قومش [به رسالت‌] فرستادیم و در میان آنان هزار سال منهای پنجاه سال به سر برد، آنگاه طوفان [سیل و بلا] آنان را فرو گرفت، در حالی که ایشان ستمکار [مشرک‌] بودند'),
(3355, 29, 15, 'آنگاه او و کشتی‌نشینان را نجات دادیم و آن را مایه عبرت جهانیان گرداندیم‌'),
(3356, 29, 16, 'و ابراهیم را [به رسالت فرستادیم‌] آنگاه که به قومش گفت خداوند را بپرستید و از او پروا کنید، این اگر بدانید برایتان بهتر است‌'),
(3357, 29, 17, 'شما فقط بتهایی را به جای خداوند می‌پرستید و بهتانی برساخته‌اید، بی‌گمان کسانی که به جای خداوند می‌پرستید اختیار روزی شما را ندارند، پس روزی را نزد خداوند بجویید و او را بپرستید و او را سپاس بگزارید، که به سوی او بازگردانده می‌شوید'),
(3358, 29, 18, 'و اگر [پیامبر را] دروغگو انگارید بدانید که امتهای پیش از شما هم تکذیب پیشه کردند، و بر عهده پیامبر جز پیام‌رسانی آشکار نیست‌'),
(3359, 29, 19, 'آیا نیندیشیده‌اند که خداوند چگونه آفرینش را آغاز می‌کند و سپس بازش می‌گرداند، بی‌گمان این بر خداوند آسان است‌'),
(3360, 29, 20, 'بگو در زمین سیر و سفر کنید و بنگرید خداوند چگونه آفرینش را آغاز کرده است و سپس نشاه آخرت را پدید می‌آورد، بی‌گمان خداوند بر هر کاری تواناست‌'),
(3361, 29, 21, 'هرکس را که بخواهد عذاب می‌کند و بر هرکس که بخواهد رحمت می‌آورد، و به سوی او بازگردانده می‌شوید'),
(3362, 29, 22, 'و شما چه در زمین و چه در آسمان از خداوند گریز و گزیری ندارید و در برابر خداوند یار و یاوری ندارید'),
(3363, 29, 23, 'و کسانی که آیات الهی و لقای او را منکر شدند اینانند که از رحمت من نومید شدند، و اینانند که عذابی دردناک [در پیش‌] دارند'),
(3364, 29, 24, 'آنگاه پاسخ قومش جز این نبود که گفتند او را بکشید یا بسوزانید، سپس خداوند او را از آتش نجات داد، بی‌گمان در این امر مایه‌های عبرتی برای اهل ایمان است‌'),
(3365, 29, 25, 'و گفت همانا به جای خداوند بتانی را می‌پرستید که در زندگانی دنیا مایه دوستی‌ورزیدن بین شماست، سپس روز قیامت بعضی از شما بعض دیگر را رد و تخطئه می‌کند و بعضی از شما بعض دیگر را لعنت می‌کند، و سرا و سرانجام شما آتش دوزخ است و یاوری ندارید'),
(3366, 29, 26, 'آنگاه لوط به او [ابراهیم‌] ایمان آورد، و [ابراهیم‌] گفت من به سوی پروردگارم مهاجرم، بی‌گمان او پیروزمند فرزانه است‌'),
(3367, 29, 27, 'و به او اسحاق و یعقوب را بخشیدیم و در میان زاد و رود او پیامبری و کتاب آسمانی قرار دادیم و پاداش او را در دنیا به او دادیم و او در آخرت از شایستگان است‌'),
(3368, 29, 28, 'و لوط را نیز [به رسالت فرستادیم‌]، آنگاه که به قومش گفت شما ناشایستی را مرتکب می‌شوید که هیچ‌کس از مردم جهان در آن بر شما پیشدستی نکرده است‌'),
(3369, 29, 29, 'آیا شما با مردان می‌آمیزید و راه و پیوند [طبیعی‌] را می‌برید و در انجمنتان مرتکب زشتکاری می‌شوید؟ آنگاه پاسخ قومش جز این نبود که گفتند اگر از راستگویان هستی عذاب الهی را [هم‌اکنون‌] بر سرما بیاور'),
(3370, 29, 30, '[لوط] گفت پروردگارا مرا در برابر تباهکاران یاری ده‌'),
(3371, 29, 31, 'و چون فرشتگان ما برای ابراهیم مژده آوردند، گفتند ما نابودگران اهالی این شهر هستیم، چرا که اهل آن ستمکارند'),
(3372, 29, 32, '[ابراهیم‌] گفت در آنجا لوط هم هست گفتند ما به کسانی که در آنجا هستند آگاه‌تریم، او و خانواده‌اش جز همسرش را که از واپس‌ماندگان [در عذاب‌] است، نجات می‌دهیم‌'),
(3373, 29, 33, 'و چون فرشتگان ما به نزد لوط آمدند، از ایشان نگران شد و [از کمک به آنان‌] دستش کوتاه شد و [آنان‌] به او گفتند مترس و اندوهگین مباش، ما رهاننده تو و خاندانت هستیم، جز همسرت که از واپس ماندگان [در عذاب‌] است‌'),
(3374, 29, 34, 'ما بر اهل این شهر به خاطر نافرمانی ورزیدنشان عذابی از آسمان فرود می‌آوریم‌'),
(3375, 29, 35, 'و به راستی از آن پدیده روشنگری برای خردورزان باقی گذاردیم‌'),
(3376, 29, 36, 'و به سوی قوم مدین برادرشان شعیب را [به رسالت فرستادیم‌] که گفت ای قوم من خداوند را بپرستید و به روز بازپسین امید داشته باشید، و در این سرزمین فتنه و فساد برپا مکنید'),
(3377, 29, 37, 'سپس او را دروغگو شمردند، آنگاه زلزله ایشان را فرو گرفت و در خانه‌شان از پای در آمدند'),
(3378, 29, 38, 'و عاد و ثمود نیز، و [این امر] به راستی از خانه‌ها و کاشانه‌هایشان بر شما روشن شده است، و شیطان کار و کردارشان را در چشم آنان آراست و آنان را از راه [راست‌] بازداشت، و حال آنکه باریک‌بین می‌نمودند'),
(3379, 29, 39, 'و قارون و فرعون و هامان نیز، که موسی برای آنان پدیده‌های روشنگر آورد، آنگاه در آن سرزمین استکبار ورزیدند و پیشتاز نبودند'),
(3380, 29, 40, 'آنگاه هر یک از آنان را به گناهش فرو گرفتیم، پس بعضی از آنان بودند که بر آنان شن‌بادی فرستادیم، و بعضی از آنان بودند که بانگ مرگبار فروگرفتشان، و بعضی از آنان بودند که به زمین فرو بردیمشان، و بعضی از آنان بودند که غرقه‌شان کردیم و خداوند نبود که به آنان ستم کرد، بلکه خود بر خویشتن ستم کردند'),
(3381, 29, 41, 'داستان کسانی که به جای خداوند سرورانی را به پرستش گرفتند همانند داستان عنکبوت است که خانه‌ای ساخت، و اگر در می‌یافتند سست‌ترین خانه‌ها، خانه عنکبوت است‌'),
(3382, 29, 42, 'بی‌گمان خداوند هر چیز را که به جای او به پرستش می‌گیرند، می‌شناسد و او پیروزمند فرزانه است‌'),
(3383, 29, 43, 'و این مثلها را برای مردم می‌زنیم و جز دانشمندان کسی درباره آنها تعقل نمی‌کند'),
(3384, 29, 44, 'خداوند آسمانها و زمین را به حق آفریده است، بی‌گمان در این امر مایه عبرتی برای مؤمنان است‌'),
(3385, 29, 45, 'آنچه از کتاب آسمانی که بر تو وحی شده است، بخوان و نماز را برپا دار، که نماز از ناشایستی و زشتکاری باز می‌دارد، و یاد کرد خداوند [از هر کاری‌] مهمتر است و خداوند می‌داند که چه می‌کنید'),
(3386, 29, 46, 'و با اهل کتاب جز به شیوه‌ای که نیکوتر است، مجادله مکنید، مگر با ستمگران آنان، و بگویید به آنچه بر ما و به آنچه بر شما نازل شده ایمان آورده‌ایم، و خدای ما و خدای ما و خدای شما یکی است و ما همه فرمانبردار اوییم‌'),
(3387, 29, 47, 'و بدین‌سان بر تو کتاب آسمانی را نازل کردیم، و اهل کتاب به آن ایمان آورند، و از ایشان [اهل مکه‌] نیز کسانی هستند که به آن ایمان آورند، و جز کافران کسی به آیات ما انکار نمی‌ورزد'),
(3388, 29, 48, 'و پیش از آن [وحی و نبوت‌] نه کتابی می‌خواندی و نه به دست خود [مکتوبی‌] می‌نوشتی، چه در آن صورت باطل‌اندیشان شک و شبهه به میان می‌آوردند'),
(3389, 29, 49, 'آری آن آیاتی روشنگر [و محفوظ] در سینه‌های دانش یافتگان است، و جز ستمگران [مشرک‌] کسی به آیات ما انکار نمی‌ورزد'),
(3390, 29, 50, 'و گویند چرا بر او معجزاتی از سوی پروردگارش نازل نمی‌شود، بگو معجزات فقط در اختیار خداوند است، و من فقط هشداردهنده‌ای آشکارم‌'),
(3391, 29, 51, 'آیا برای ایشان کافی نیست که ما بر تو کتاب آسمانی را فرو فرستادیم که بر آنان خوانده می‌شود، بی‌گمان در این امر رحمت و پندآموزی برای اهل ایمان است‌'),
(3392, 29, 52, 'بگو خداوند بین من و شما گواه بس، که آنچه در آسمانها و زمین است می‌داند، و کسانی که به باطل ایمان آورده و به خداوند کفر می‌ورزند، اینانند که زیانکارند'),
(3393, 29, 53, 'و از تو عذاب را به شتاب می‌خواهند و اگر اجل معینی در کار نبود، عذاب بر آنان نازل می‌شد و در حالی که ناآگاهند ناگهان به سراغشان می‌آید'),
(3394, 29, 54, 'از تو عذاب را به شتاب می‌خواهند، و [غافل از آنکه‌] بی‌گمان جهنم فراگیر کافران است‌'),
(3395, 29, 55, 'روزی که عذاب از فراز و فرودشان فراگیردشان و گوید [حاصل‌] عملکرد خود را بچشید'),
(3396, 29, 56, 'ای بندگان من که ایمان آورده‌اید، بدانید که زمین من گسترده است، پس فقط مرا بپرستید'),
(3397, 29, 57, 'هر موجود زنده‌ای چشنده [طعم‌] مرگ است، سپس به سوی ما بازگردانده می‌شوید'),
(3398, 29, 58, 'و کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، در غرفه‌هایی از بهشت که جویباران از فرودست آنها جاری است، جایشان می‌دهیم و در آنجا جاویدانند، چه نیکوست پاداش عملداران‌'),
(3399, 29, 59, '[همان‌] کسانی که شکیبایی ورزیده و بر پروردگارشان توکل می‌کنند'),
(3400, 29, 60, 'و چه بسیار جنبنده که عهده‌دار روزی خود نیست، بلکه خداوند روزی‌بخش او و شماست و او شنوای داناست‌'),
(3401, 29, 61, 'و اگر از ایشان بپرسی چه کسی آسمانها و زمین را آفریده است و خورشید و ماه را رام کرده است، گویند خداوند، پس چگونه بیراهه می‌روند؟'),
(3402, 29, 62, 'خداوند است که روزی را برای هرکس از بندگانش که بخواهد گشاده یا تنگ می‌دارد، بی‌گمان خداوند به هر چیزی داناست‌'),
(3403, 29, 63, 'و اگر از ایشان بپرسی چه کسی از آسمان آبی فرو فرستاد و بدان زمین را پس از پژمردنش زنده [و بارور] کرد، خواهند گفت خداوند، بگو سپاس خداوند را، ولی بیشترینه‌شان تعقل نمی‌کنند'),
(3404, 29, 64, 'و این زندگانی دنیا جز سرگرمی و بازیچه نیست، و اگر می‌دانستند بی‌گمان سرای آخرت کانون زندگی است‌'),
(3405, 29, 65, 'و چون سوار بر کشتی شوند خداوند را در حالی که دین خود را برای او پاک و پیراسته می‌دارندبخوانند، ولی آنگاه که آنان را رهانید و به خشکی رسانید، آن وقت است که ایشان شرک می‌ورزند'),
(3406, 29, 66, 'تا سرانجام در آنچه به ایشان بخشیده‌ایم کفران پیشه کنند و [از ظواهر زندگی‌] بهره برند، زودا که [حقیقت را] بدانند'),
(3407, 29, 67, 'آیا ندانسته‌اند که ما حرمی امن [از کعبه‌] قرار داده‌ایم، و حال آنکه مردمان را در پیرامونشان تاراج می‌کردند و می‌ربودند، آیا به باطل ایمان می‌آورند و به نعمت خداوند کفران می‌ورزند؟'),
(3408, 29, 68, 'و کیست ستمکارتر از کسی که بر خداوند دروغ بندد یا حق را چون به سراغش آید تکذیب کند، آیا جایگاه کافران در دوزخ نیست؟'),
(3409, 29, 69, 'و کسانی را که در حق ما کوشیده‌اند به راههای خاص خویش رهنمون می‌شویم، و بی‌گمان خداوند با نیکوکاران است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الم [الف لام میم‌]'),
(3411, 30, 2, 'روم شکست خورد'),
(3412, 30, 3, 'در نزدیکترین سرزمین [به شما]، و ایشان بعد از مغلوب شدنشان به زودی غالب خواهند شد'),
(3413, 30, 4, 'در عرض چند سال، چرا که امر در گذشته و آینده با خداوند است، و در چنین روزی مؤمنان شادمان شوند'),
(3414, 30, 5, 'به نصرت الهی، که هر کس را بخواهد یاری می‌کند، و او پیروزمند مهربان است‌'),
(3415, 30, 6, '[این‌] وعده الهی است و خداوند در وعده خویش خلاف نمی‌کند ولی بیشترینه مردم نمی‌دانند'),
(3416, 30, 7, 'فقط ظاهری از زندگانی دنیا را می‌دانند و ایشان از آخرت غافلند'),
(3417, 30, 8, 'آیا در دلهای خویش نیندیشیده‌اند که خداوند آسمانها و زمین را و آنچه ما بین آنهاست، جز به حق و سرآمد معین نیافریده است، و بی‌گمان بسیاری از مردم لقای پروردگارشان را منکرند'),
(3418, 30, 9, 'آیا در زمین سیر و سفر نکرده‌اند که بنگرند چگونه بوده است سرانجام کسانی که پیش از آنان بودند آنان از ایشان نیرومندتر بودند، و زمین را کندوکاو کردند و بیش از آنچه آنان آباد ساخته بودند، آباد کردند، و پیامبرانشان برایشان پدیده‌های شگرف آوردند، و [سرانجام‌] خداوند بر آنان ستم نکرد، بلکه خود در حق خویش ستم کردند'),
(3419, 30, 10, 'سپس سرانجام کسانی که بد عملی کرده بودند، بدتر شد چرا که آیات الهی را انکار و به آنها استهزاء می‌کردند'),
(3420, 30, 11, 'خداوند است که آفرینش را آغاز کرده است، سپس آن را باز می‌گرداند، سپس به سوی او باز گردانده می‌شوید'),
(3421, 30, 12, 'روزی که قیامت برپا شود، گناهکاران نومید شوند'),
(3422, 30, 13, 'و برای آنان از شریکانی که قائل بودند، کسی شفیعشان نباشد، و خود به شریکانی که قائل بودند، منکر [و بی‌اعتقاد] شوند'),
(3423, 30, 14, 'و روزی که قیامت برپا شود، آنگاه است که از هم جدا شوند'),
(3424, 30, 15, 'اما کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، ایشان در بوستانی سرخوشند'),
(3425, 30, 16, 'و اما کسانی که کفرورزیده و آیات ما و لقای آخرت را تکذیب کرده‌اند، اینانند که در عذاب حاضر کرده شوند'),
(3426, 30, 17, 'پس هنگامی که در شامگاه یا در بامداد وارد شوید خداوند را تسبیح گویید'),
(3427, 30, 18, 'و در آسمانها و زمین سپاس او راست، و هنگامی که در عصر وارد شوید و هنگامی که به نیمروز رسید [هم تسبیح گویید]'),
(3428, 30, 19, 'زنده را از مرده بر می‌آورد و مرده را از زنده، و زمین را پس از پژمردنش زنده [و بارور] می‌دارد، و بدین‌سان شما نیز برانگیخته شوید'),
(3429, 30, 20, 'و از جمله آیات او این است که شما را از خاک آفرید، سپس که انسان شدید [در زمین‌] پراکنده شدید'),
(3430, 30, 21, 'و از جمله آیات او این است که برای شما از نوع خودتان، همسرانی آفرید که با آنان آرام گیرید، و در میان شما دوستی و مهربانی افکند، و در این امر برای اندیشه‌وران مایه‌های عبرت است‌'),
(3431, 30, 22, 'و از جمله آیات او آفرینش آسمانها و زمین است و دگرگونی زبانهای شما و رنگهای شما، بی‌گمان در این برای دانشمندان مایه‌های عبرت است‌'),
(3432, 30, 23, 'و از جمله آیات او خفتن شما در شب و روز و طلب معاش شما از فضل اوست، بی‌گمان در این [امر] برای کسانی که گوش شنوا دارند، مایه‌های عبرت است‌'),
(3433, 30, 24, 'و از جمله آیات او این است که برق را [به شیوه‌ای‌] بیم‌انگیز و امیدانگیز به شما می‌نمایاند و از آسمان آبی فرو می‌فرستد و با آن زمین را پس از پژمردنش زنده می‌دارد، بی‌گمان در این [امر] برای خردورزان مایه‌های عبرت است‌'),
(3434, 30, 25, 'و از آیات او این است که آسمانها و زمین به فرمان او برپاست، سپس آنگاه که شما را به صلایی از زمین بخواند، آنگاه است که برانگیخته می‌شوید'),
(3435, 30, 26, 'و او راست هر که در آسمانها و زمین است، و همگان فرمانبردار اویند'),
(3436, 30, 27, 'و او کسی است که آفرینش را آغاز می‌کند و سپس بازش می‌گرداند، و این امر بر او آسان‌تر است، و او را در آسمانها و زمین برترین وصف است و او پیروزمند فرزانه است‌'),
(3437, 30, 28, 'برای شما مثلی از خودتان می‌زند، آیا از ملک یمینتان شریکی در آنچه روزیتان داده‌ایم، دارید که در آن برابر باشید و از آنان همان گونه که از [امثال‌] خودتان بیمناکید، بیمناک باشید؟ [چنین نیست‌]، بدین‌سان آیات [خود] را برای خردورزان روشن بیان می‌کنیم‌'),
(3438, 30, 29, 'چنین است که ستم‌پیشگان [کافر] بی‌هیچ علمی، از هوی و هوسشان پیروی می‌کنند، و کیست که کسی را که خداوند در بیراهی وانهاده است، به راه آورد، و اینان یاوری ندارند'),
(3439, 30, 30, 'پاکدلانه روی به دین بیاور، این فطرت الهی است که مردمان را بر وفق آن آفریده است، در آفرینش الهی تغییری راه ندارد، این دین استوار است، ولی بیشترینه مردم نمی‌دانند'),
(3440, 30, 31, 'رو به سوی او آورده، و از او پروا کنید و نماز را برپا دارید و از مشرکان مباشید'),
(3441, 30, 32, 'از همان کسانی که دینشان را پاره و پراکنده کردند و فرقه فرقه شدند، هر گروهی به آنچه دارند دلخوشند'),
(3442, 30, 33, 'و چون بلایی به مردم رسد انابت کنان پروردگارشان را می‌خوانند و رو به سوی او می‌آورند، سپس چون رحمتی از سوی خویش به ایشان بچشاند، آنگاه است که گروهی از آنان به پروردگارشان شرک می‌آورند'),
(3443, 30, 34, 'تا سرانجام درباره آنچه به ایشان بخشیده‌ایم کفران پیشه کنند، و [از ظواهر زندگی‌] بهره برید، زودا که [حقیقت را] بدانید'),
(3444, 30, 35, 'یا مگر برایشان حجتی نازل کرده‌ایم که درباره آنچه به آن شرک‌ورزیده‌اند، سخن می‌گوید'),
(3445, 30, 36, 'و چون به مردم رحمتی بچشانیم به آن شادمان شوند، و اگر به خاطر کار و کردار پیشینشان مکروهی به ایشان رسد، آنگاه است که نومید می‌شوند'),
(3446, 30, 37, 'آیا ندانسته‌اند که خداوند بی‌گمان روزی را برای هر کس که بخواهد گشاده و [برای هر کس که بخواهد] تنگ می‌دارد، بی‌شک در این امر برای اهل ایمان مایه‌های عبرت است‌'),
(3447, 30, 38, 'پس به خویشاوند حقش را بده و نیز به بینوا و در راه مانده، این برای کسانی که در طلب خشنودی الهی هستند بهتر است و اینانند که رستگارانند'),
(3448, 30, 39, 'و هر ربایی که بدهید که بهره شما را از اموال مردم افزونتر کند، [در حقیقت‌] نزد خداوند افزایش ندارد، و هر زکاتی -که در طلب خشنودی الهیبپردازید، اینان افزایش‌یاب هستند'),
(3449, 30, 40, 'خداوند کسی است که شما را آفرید و سپس روزیتان بخشید، سپس شما را می‌میراند، سپس زنده می‌دارد، آیا از شریکانی که قائلید هیچ کدام هست که چیزی از این کارها را انجام دهد؟ او منزه و از آنچه شریکش می‌آورند فراتر است‌'),
(3450, 30, 41, 'به خاطر کار و کردار مردم، تباهی در بر و بحر فراگیر شده است، تا [خداوند] جزای بخشی از کار و کردارشان را به آنان بچشاند، باشد که بازگردند [و به خود آیند]'),
(3451, 30, 42, 'بگو در زمین سیر و سفر کنید و بنگرید سرانجام کسانی که پیشتر بودند چگونه بوده است؟ که بیشترشان مشرک بودند'),
(3452, 30, 43, 'پس روی دلت را -پیش از آنکه روزی بی‌بازگشت از جانب خداوند فرارسد و مردم از هم جدا شوندبه سوی دین استوار بدار'),
(3453, 30, 44, 'هر کس کفرورزد، کیفر کفرش بر اوست، و کسانی که کرداری شایسته پیشه کنند، برای خودشان پیش‌اندیشی کرده‌اند'),
(3454, 30, 45, 'تا کسانی را که ایمان آورده‌اند و کارهای شایسته کرده‌اند، از فضل خویش جزا دهد، چرا که او کافران را دوست ندارد'),
(3455, 30, 46, 'و از آیات او این است بادها را بشارت‌دهنده [باران رحمت‌] می‌فرستد تا به شما از رحمت خویش بچشاند، و تا کشتیها به امر او جریان یابند، و شما از فضل او در طلب روزی خود بر آیید، باشد که سپاس بگزارید'),
(3456, 30, 47, 'و به راستی پیش از تو پیامبرانی به سوی قومشان فرستادیم، آنگاه ایشان معجزات را به نزد آنان آوردند [و با انکار مواجه شدند] و ما از کسانی که گناه [و بد عملی‌] کرده بودند، انتقام گرفتیم، و یاری رساندن به مؤمنان حقی است که بر عهده ماست‌'),
(3457, 30, 48, 'خداوند کسی است که بادها را می‌فرستد که ابرها را بر می‌انگیزد، و آن را در آسمان هرگونه که بخواهد می‌گستراند، و آن را پاره‌پاره می‌کند، و باران درشت قطره را بینی که از لابلای آن می‌تراود، و چون آن را به هر یک از بندگانش که بخواهد برساند، آنگاه است که شادی می‌کنند'),
(3458, 30, 49, 'و بی‌شک پیش از آنکه برایشان فرو فرستاده شود [آری‌] پیش از آن نومید بودند'),
(3459, 30, 50, 'پس به آثار رحمت الهی بنگر که چگونه زمین را پس از پژمردنش زنده می‌دارد، هموست که زندگی بخش مردگان است و او بر هر کاری تواناست‌'),
(3460, 30, 51, 'و اگر بادی بفرستیم و آن [کشتزار] را زرد شده بینند، بعد از آن کفر [/انکار] پیشه کنند'),
(3461, 30, 52, 'بدان که تو مردگان را [سخن‌] نشنوانی و به ناشنوایان [به ویژه‌] چون پشت کنند آوایی نشنوانی‌'),
(3462, 30, 53, 'و تو به راه آورنده نابینایان از گمراهیشان نیستی، تو جز کسانی را که به آیات ما ایمان آورند و اهل تسلیمند [سخنی‌] نشنوانی‌'),
(3463, 30, 54, 'خداوند کسی است که شما را از حالت ناتوانی آفرید، سپس بعد از آن ناتوانی توانایی پدید آورد، سپس [دوباره‌] پس از توانایی، ناتوانی و پیری قرار دهد، هر آنچه خواهد می‌آفریند و او دانای تواناست‌'),
(3464, 30, 55, 'و روزی که قیامت بر پا شود، گناهکاران سوگند خورند که [در دنیا، یا گور] جز ساعتی درنگ نکرده‌اند، بدین‌سان بیراهه رفته‌اند'),
(3465, 30, 56, 'و کسانی که از دانش و ایمان برخوردار شده‌اند گویند بر وفق کتاب الهی تا روز رستاخیز درنگ کرده‌اید، و این روز رستاخیز است، ولی شما به هیچ وجه نمی‌دانستید'),
(3466, 30, 57, 'امروز [روزی است که‌] ستم‌پیشگان [مشرک‌] را عذرخواهیشان سود ندهد، و عذرشان را نپذیرند'),
(3467, 30, 58, 'و به راستی در این قرآن برای مردم هرگونه مثلی زده‌ایم و اگر معجزه‌ای برای آنان بیاوری، کافران گویند شما جز باطل‌اندیش نیستید'),
(3468, 30, 59, 'بدین‌سان خداوند بر دلهای کسانی که [حق را] در نمی‌یابند مهر می‌زند'),
(3469, 30, 60, 'شکیبایی پیشه کن و بدان که وعده الهی حق است و نامؤمنان تو را از راه به در نبرند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الم [الف لام میم‌]'),
(3471, 31, 2, 'این آیات کتاب حکمت‌آموز است‌'),
(3472, 31, 3, 'رهنمود و رحمتی برای نیکوکاران‌'),
(3473, 31, 4, '[همان‌] کسانی که نماز را برپا می‌دارند و زکات را می‌پردازند و به آخرت یقین دارند'),
(3474, 31, 5, 'اینانند که از سوی پروردگارشان از هدایتی برخوردارند و هم اینانند که رستگارند'),
(3475, 31, 6, 'و از مردمان کسی هست که خریدار سخنان سرگرم‌کننده است تا بی‌هیچ علمی [مردمان را] از راه خدا گمراه گرداند، و آن را به ریشخند گیرد، اینانند که عذابی خفت‌بار [در پیش‌] دارند'),
(3476, 31, 7, 'و چون آیات ما بر او خوانده شود، متکبرانه روی برگرداند که گویی آن را نشنیده است، چنانکه گویی در گوشهایش سنگینی‌ای هست، پس او را از عذاب دردناک خبر ده‌'),
(3477, 31, 8, 'بی‌گمان کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، بوستانهای پرناز و نعمت برای آنان است‌'),
(3478, 31, 9, 'که جاودانه در آنند، وعده الهی حق است، و او پیروزمند فرزانه است‌'),
(3479, 31, 10, 'آسمانها را بدون ستونهایی که ببینید پدید آورد، و در زمین کوهها بیفکند تا شما را نجنباند، و در آن از هرگونه جانوری پراکند، و از آسمان آبی فرو فرستادیم، آنگاه در آن از هر گونه‌ای ارزشمند رویاندیم'),
(3480, 31, 11, 'این آفرینش خداست، پس به من نشان دهید کسانی که [مدعی و] در برابر او هستند چه چیزی آفریده‌اند؟ آری ستمکاران [مشرک‌] در گمراهی آشکارند'),
(3481, 31, 12, 'و به راستی به لقمان حکمت بخشیدیم، که خدای را سپاس گو، [و بدان که‌] هر کس سپاسگزاری کند، همانا به سود خویش سپاس گزارده است، و هر کس کفران ورزد، [بداند که‌] خداوند بی‌نیاز ستوده است‌'),
(3482, 31, 13, 'و چنین بود که لقمان به پسرش -که پندش می‌دادگفت فرزندم به خداوند شرک میاور، چرا که شرک ستم بزرگی است‌'),
(3483, 31, 14, 'و انسان را در حق پدر و مادرش سفارش کردیم که مادرش او را با ضعف روزافزون، آبستن بوده است، [و شیر دادن‌] و از شیر گرفتن او دو سال به طول انجامیده، برای من و پدر و مادرت سپاس بگزار که سیر و سرانجام به سوی من است‌'),
(3484, 31, 15, 'و اگر تو را وادارند چیزی را که بدان علم نداری شریک من گردانی، از آنان فرمان مبر، و با آنان در دنیا به نیکی همنشینی کن، و به راه کسی برو که به سوی من بازگشته است، سپس بازگشت شما به سوی من است، آنگاه از [حقیقت و نتیجه‌] کار و کردارتان آگاهتان می‌سازم‌'),
(3485, 31, 16, 'فرزندم بدان که اگر [عملی‌] هم سنگ دانه خردلی باشد، و آنگاه در دل تخته‌سنگی یا در آسمانها یا در زمین نهفته باشد، خداوند آن را به میان می‌آورد، چرا که خداوند باریک‌بین آگاه است‌'),
(3486, 31, 17, 'فرزندم نماز را برپا دار و امر به معروف و نهی از منکر کن، و بر مصیبتی که تو را فرارسد شکیبایی کن که این از کارهای سترگ است‌'),
(3487, 31, 18, 'و رویت را از مردم [به تکبر] برمگردان و در زمین خرامان راه مرو، چرا که خداوند، هیچ متکبر فخرفروشی را دوست ندارد'),
(3488, 31, 19, 'و میانه‌روی کن و صدایت را آهسته بدار، چرا که ناخوشترین آوازها بانگ درازگوشان است‌'),
(3489, 31, 20, 'آیا نیندیشیده‌اید که خداوند آنچه در آسمانها و آنچه در زمین است، برای شما رام کرد، و نعمتهای آشکار و پنهانش را بر شما تمام کرد، و از مردم کسی هست که بدون هیچ علمی و هیچ رهنمودی و هیچ کتاب روشنگری در حق خداوند مجادله می‌کند'),
(3490, 31, 21, 'و چون به ایشان گفته شود از آنچه خداوند نازل کرده است پیروی کنید گویند بلکه از آنچه پدرانمان را طرف‌دار آن یافته‌ایم پیروی می‌کنیم، حتی اگر شیطان ایشان را به سوی عذاب آتش دوزخ بخواند؟'),
(3491, 31, 22, 'و هر کس روی دلش را به سوی خداوند نهد و نیکوکار باشد، به راستی که دست در دستاویز استواری زده است، و سرانجام کارها با خداوند است‌'),
(3492, 31, 23, 'و هر کس کفر ورزد، کفر او تو را اندوهگین نکند، بازگشتشان به سوی ماست، آنگاه ایشان را از [حقیقت و نتیجه‌] آنچه کرده‌اند آگاه سازیم، که بی‌گمان خداوند دانای راز دلهاست‌'),
(3493, 31, 24, 'اندکی برخوردارشان سازیم، سپس به عذابی سخت و سنگین دچارشان سازیم‌'),
(3494, 31, 25, 'و اگر از ایشان پرسی چه کسی آسمانها و زمین را آفریده است گویند خداوند، بگو سپاس خدای را، ولی بیشترینه آنان نمی‌دانند'),
(3495, 31, 26, 'آنچه در آسمانها و زمین است از آن خداوند است، بی‌گمان خداوند بی‌نیاز ستوده است‌'),
(3496, 31, 27, 'و اگر آنچه درخت در زمین هست، قلم شود، و دریا [چون مرکب باشد] سپس هفت دریا به آن مدد برساند، کلمات الهی به پایان نرسد، که خداوند پیروزمند فرزانه است‌'),
(3497, 31, 28, 'آفرینش و برانگیختن شما [در قیامت‌] جز مانند تنی یگانه نیست، بی‌گمان خداوند شنوای بیناست‌'),
(3498, 31, 29, 'آیا ننگریسته‌ای که خداوند از شب می‌کاهد و بر روز می‌افزاید و از روز می‌کاهد و بر شب می‌افزاید و خورشید و ماه را رام کرده است که هر یک تا سرآمدی معین روان است، و خداوند به آنچه می‌کنید آگاه است‌'),
(3499, 31, 30, 'این از آن است که خداوند بر حق است، و آنچه به جای او می‌پرستند، باطل است، و بی‌گمان خداوند است که بلندمرتبه بزرگ است‌'),
(3500, 31, 31, 'آیا ننگریسته‌ای که کشتیها به نعمت الهی در دریا روانند، تا به شما از آیات خویش بنمایاند، بی‌گمان در این برای هر شکیبای شاکری مایه‌های عبرت هست‌'),
(3501, 31, 32, 'و چون موجی سایبان‌وار آنان را فراگیرد، خداوند را در حالی که دین خود را برای او پیراسته می‌دارند، به دعا می‌خوانند آنگاه چون آنان را برهاند و به خشکی برساند، بعضی از ایشان میانه‌رو [و درستکارند] [و بعضی کجرو] و جز غدار ناسپاس کسی منکر آیات ما نمی‌شود'),
(3502, 31, 33, 'ای مردم از پروردگارتان پروا کنید و از روزی بترسید که هیچ پدری به داد فرزندش نرسد، و هیچ فرزندی فریادرس پدرش نباشد، بی‌گمان وعده الهی حق است، پس زندگانی دنیا شما را نفریبد، و [شیطان‌] فریبکار شما را نسبت به خداوند فریفته نگرداند'),
(3503, 31, 34, 'بی‌گمان آگاهی از قیامت خاص خداوند است، و باران را نیز او نازل می‌کند، و می‌داند که در رحمها چیست، و هیچ کس نمی‌داند که فردا چه به دست می‌آورد، و هیچ کس نمی‌داند که در کدامین سرزمین می‌میرد، بی‌گمان خداوند دانای آگاه است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الم [الف لام میم‌]'),
(3505, 32, 2, 'کتابی است فرو فرستاده و شک در آن نیست [که‌] از سوی پروردگار جهان است‌'),
(3506, 32, 3, 'یا گویند که آن را برساخته است، نه بلکه حق است و از سوی پروردگار توست تا قومی را که [پیامبری‌] هشداردهنده پیش از تو به سراغشان نیامده بود، بیم‌دهی، باشد که به راه آیند'),
(3507, 32, 4, 'خداوند کسی است که آسمان و زمین و مابین آنها را در شش روز آفرید، سپس بر عرش استیلاء یافت، شما را جز او سرور و شفیعی نیست، آیا پند نمی‌گیرید'),
(3508, 32, 5, 'از آسمان کار و بار زمین را تدبیر می‌کند، سپس [اعمال مردمان‌] در روزی که اندازه‌اش هزار سال است از آنچه شما می‌شمارید، به سوی او بالا می‌رود'),
(3509, 32, 6, 'اوست که دانای نهان و آشکارا [و] پیروزمند مهربان است‌'),
(3510, 32, 7, 'و [همان‌] کسی که هرچیز را که آفرید نیکو آفرید، و آفرینش انسان [/آدم‌] را از گل آغاز کرد'),
(3511, 32, 8, 'سپس نسل او را از چکیده آب بی‌مقدار پدید آورد'),
(3512, 32, 9, 'سپس او را استوار کرد و در او از روح خویش دمید و برای شما گوش [ها] و چشمان و دلها آفرید، چه اندک سپاس می‌گزارید'),
(3513, 32, 10, 'و گویند آیا چون در [خاک‌] زمین ناپدید شدیم، آیا آفرینش تازه‌ای خواهیم یافت؟ بلکه ایشان لقای پروردگارشان را منکرند'),
(3514, 32, 11, 'بگو فرشته مرگ که بر شما گماشته شده است، روح شما را می‌گیرد، سپس به سوی پروردگارتان بازگردانده می‌شوید'),
(3515, 32, 12, 'و چون گناهکاران را نزد پروردگارشان سرافکنده بینی [گویند] پروردگارا چشم بینا و گوش شنوا یافتیم پس ما را بازگردان که کاری شایسته پیشه کنیم، ما [دیگر] اهل یقینیم‌'),
(3516, 32, 13, 'و اگر خواهیم به هر انسانی هدایتش را ارزانی می‌داریم، ولی وعده من تحقق یافته است که جهنم را از جن و انس، همگان، آکنده سازم‌'),
(3517, 32, 14, 'پس به خاطر آنکه دیدار این روزتان را فراموش کردید [عذاب را] بچشید ما نیز "فراموشتان" کرده‌ایم و عذاب جاویدان را به خاطر کار و کردار پیشینتان بچشید'),
(3518, 32, 15, 'به آیات ما فقط کسانی ایمان آورند که چون آنها را فرایادشان آرند، به سجده درافتند و پروردگارشان را شاکرانه تسبیح گویند و تکبرنورزند'),
(3519, 32, 16, 'پهلوهایشان از بسترها جدا شود [و به نیایش شبانه برخیزند و] پروردگارشان را با بیم و امید بخوانند و از آنچه روزیشان کرده‌ایم ببخشند'),
(3520, 32, 17, 'آری هیچ کسی نداند که چه بسیار مایه روشنی چشمها برای آنان نهفته است، که جزای کار و کردار پیشینشان است‌'),
(3521, 32, 18, 'آیا کسی که مؤمن است همانند کسی است که فاسق است؟ [هرگز] برابر نیستند'),
(3522, 32, 19, 'اما کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، جنةالماوی دارند که به خاطر کار و کردار پیشینشان پیشکش [آنان‌] است‌'),
(3523, 32, 20, 'و اما کسانی که سرپیچی کرده‌اند سرا و سرانجامشان آتش دوزخ است، هرگاه که خواهند از آن بیرون روند، به آن بازگردانده شوند، و به ایشان گفته شود، عذاب آتشی را که منکرش بودید، بچشید'),
(3524, 32, 21, 'و به ایشان عذاب کوچکتر را غیر از عذاب بزرگتر، می‌چشانیم باشد که بازگردند'),
(3525, 32, 22, 'و کیست ستمکارتر از کسی که به آیات پروردگارش پند داده شود، سپس از آن رو بگرداند، ما از گناهکاران انتقام می‌گیریم‌'),
(3526, 32, 23, 'و به راستی به موسی کتاب آسمانی بخشیدیم، پس در لقای او شک نداشته باش، و آن را رهنمود بنی‌اسرائیل گرداندیم‌'),
(3527, 32, 24, 'و از ایشان چون شکیبایی ورزیدند و به آیات ما ایقان یافتند، پیشوایانی برگماشتیم که به فرمان ما هدایت می‌کردند'),
(3528, 32, 25, 'بی‌گمان پروردگارت در میان آنان در روز قیامت در آنچه در آن اختلاف داشتند، داوری خواهد کرد'),
(3529, 32, 26, 'آیا برای ایشان روشن نشده است که پیش از آنان چه بسیار نسلهایی را نابود کردیم که [اینان‌] بر خانه و کاشانه‌هایشان می‌گذرند، بی‌گمان در این مایه‌های عبرت است، آیا [به گوش دل‌] نمی‌شنوند؟'),
(3530, 32, 27, 'آیا نیندیشیده‌اند که ما آب [ابرهای بارانی‌] را به سوی سرزمین سترون می‌رانیم و بدان کشتزاری برمی‌آوریم که چارپایانشان و خودشان از آن می‌خورند، آیا [به چشم بصیرت‌] نمی‌نگرند؟'),
(3531, 32, 28, 'و گویند اگر راست می‌گویید این داوری کی خواهد بود؟'),
(3532, 32, 29, 'بگو روز داوری کافران را ایمانشان سود ندهد و به آنان مهلت ندهند'),
(3533, 32, 30, 'پس از آنان روی بگردان و منتظر باش که آنان نیز منتظرند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'ای پیامبر از خداوند پروا کن و از کافران و منافقان اطاعت مکن، که خداوند دانای فرزانه است‌'),
(3535, 33, 2, 'و از آنچه از پروردگارت به تو وحی می‌شود پیروی کن، که خداوند از آنچه می‌کنید آگاه است‌'),
(3536, 33, 3, 'و بر خداوند توکل کن و خداوند کارسازی را بسنده است‌'),
(3537, 33, 4, 'خداوند برای هیچ مردی دو دل در درونش ننهاده است، و همسرانتان را که با آنان ظهار می‌کنید مادر [حقیقی‌] شما نگردانده است، و پسر خواندگانتان را نیز فرزند [حقیقی‌] شما نگردانده است این سخن شماست که [ندانسته‌] به زبان می‌آورید، و خداوند حق را می‌گوید و به راه [راست‌] هدایت می‌کند'),
(3538, 33, 5, 'آنان را به [نام‌] پدرانشان بخوانید که نزد خداوند دادگرانه‌تر است، و اگر پدرانشان را نمی‌شناسید در آن صورت برادران دینی و آزاد کردگان شما هستند، و بر شما در آنچه اشتباه کرده‌اید گناهی نیست مگر در آنچه دلهایتان قصد آن را دارد، و خداوند آمرزگار مهربان است‌'),
(3539, 33, 6, 'پیامبر از خود مؤمنان به آنها نزدیکتر و سزاوارتر است و همسران او در حکم مادران ایشان هستند، و خویشاوندان در [حکم و] کتاب الهی نسبت به همدیگر از سایر مؤمنان و مهاجران سزاوارتر [به ارث بردن‌]، هستند، مگر آنکه در حق دوستانتان وصیتی نیک انجام دهید [که بی‌اشکال است‌]، و این در کتاب آسمانی نوشته شده است‌'),
(3540, 33, 7, 'چنین بود که از پیامبران پیمانشان را گرفتیم و نیز از تو و از نوح و ابراهیم و موسی و عیسی بن مریم، و از ایشان پیمان اکید گرفتیم‌'),
(3541, 33, 8, 'تا [سرانجام‌] درستکاران را از درستکاریشان بپرسد و برای کافران عذابی دردناک آماده ساخته است‌'),
(3542, 33, 9, 'ای مؤمنان نعمت الهی را بر خودتان یاد کنید آنگاه که سپاهیانی بر شما تاختند، سپس، بر آنان تندبادی فرستادیم و سپاهیانی که آنها را نمی‌دیدید و خداوند به آنچه می‌کنید بیناست‌'),
(3543, 33, 10, 'آنگاه از فراز و فرودتان به سراغ شما [به هجوم‌] آمدند، و آنگاه که چشمها برگشت و جانها به گلوگاهها رسید، و در حق خداوند گمانهایی [ناروا] کردید'),
(3544, 33, 11, 'آنجا بود که مؤمنان [به محنت‌] آزموده شدند، و تکانی سخت خوردند'),
(3545, 33, 12, 'و آنگاه که منافقان و بیماردلان گفتند که خداوند و پیامبر او جز وعده فریب‌آمیز به ما نداده‌اند'),
(3546, 33, 13, 'و آنگاه که گروهی از آنان گفتند ای اهل مدینه شما را جای ماندن نیست، بازگردید، و گروهی از ایشان از پیامبر اجازه [قعود] خواستند [و به بهانه‌] می‌گفتند خانه‌های ما بی‌حفاظ است، و آن بی‌حفاظ نبود، هیچ قصدی جز فرار نداشتند'),
(3547, 33, 14, 'و چون از حوالی آن [شهر] بر ایشان وارد شوند، سپس از ایشان اقرار به شرک طلب کنند، آن را انجام دهند و سپس در آنجا جز اندکی درنگ نخواهند کرد'),
(3548, 33, 15, 'و همینان بودند که پیشترها با خداوند پیمان بسته بودند که [پایداری ورزند و] پشت نکنند، و پیمان الهی بازخواست دارد'),
(3549, 33, 16, 'بگو فرار از مرگ یا کشته شدن، اگر بگریزید برایتان [نهایتا] سودی ندارد، و در آن صورت هم جز اندکی [از زندگی‌] برخوردار نخواهید شد'),
(3550, 33, 17, 'اگر خداوند در حق شما بلا یا رحمتی خواسته باشد، چه کسی شما را در برابر خداوند نگاه می‌دارد؟ و در برابر خداوند یار و یاوری برای خود نخواهند یافت‌'),
(3551, 33, 18, 'به راستی که خداوند از میان شما، بازدارندگان [/کارشکنان‌] را می‌شناسد، و نیز کسانی را که به دوستان خود می‌گویند به راه ما بیایید، و جز اندکی در کارزار شرکت نمی‌کنند'),
(3552, 33, 19, 'و در حق شما بسیار بخیل‌اند و چون بیمی فرارسد، می‌بینیشان در حالتی که دیدگانشان می‌گردد مانند کسی که از نزدیکی مرگ بیهوش شده باشد، به سوی تو می‌نگرند، و چون آن بیم برطرف شود، به شما با زبانهای تند و تیز خویش آزار می‌رسانند، و آزمند مال [غنایم‌]اند، اینانند که ایمان نیاورده‌اند و خداوند اعمالشان را تباه [و باطل‌] می‌گرداند و این بر خداوند آسان است‌'),
(3553, 33, 20, 'اینان گمان می‌برند که هنوز "گروه مشرکان همدست" [از صحنه به در] نرفته‌اند و اگر گروه مشرکان همدست برگردند، خوش دارند که کاش بیابانی و در میان بادیه‌نشینان باشند و از اخبار شما پرس و جو کنند، و اگر در میان شما بودند، جز اندکی کارزار نمی‌کردند'),
(3554, 33, 21, 'به راستی که برای شما و برای کسی که به خداوند و روز بازپسین امید [و ایمان‌] دارد و خداوند را بسیار یاد می‌کند، در پیامبر خدا سرمشق نیکویی هست‌'),
(3555, 33, 22, 'و چون مؤمنان [هجوم‌] گروه مشرکان همدست را دیدند، گفتند این همان است که خداوند و پیامبرش به ما وعده داده بودند و خداوند و پیامبر او [در امید بخشیدن به ما] راست گفته‌اند، و [در نهایت‌] جز بر ایمان و تسلیم آنان نیفزاید'),
(3556, 33, 23, 'از میان مؤمنان مردمی هستند که در پیمانی که با خداوند بسته‌اند، راست و درست رفتار کرده‌اند، و از ایشان کسی هست که عهد خویش [تا پایان حیات‌] به سر برده است، و کسی هست که [شهادت را] انتظار می‌کشد، و هیچ گونه تغییر و تبدیلی در کار نیاورده‌اند'),
(3557, 33, 24, 'تا خداوند درستکاران را بر وفق درستی‌شان پاداش دهد، و منافقان را اگر خواهد عذاب کند یا از آنان درگذرد، بی‌گمان خداوند آمرزگار مهربان است‌'),
(3558, 33, 25, 'و خداوند کافران را در عین غیظ و غضبشان بازگرداند، که هیچ کامی نیافتند، و خداوند در کارزار مؤمنان را حمایت و کفایت کرد، و خداوند توانای پیروزمند است‌'),
(3559, 33, 26, 'و آن عده از اهل کتاب را که از آنان [احزاب/گروه مشرکان همدست‌] پشتیبانی کردند، از برج و باروهاشان فرود آورد و در دلشان هراس افکند، چندانکه گروهی از ایشان را کشتید و گروهی را به اسارت گرفتید'),
(3560, 33, 27, 'و سرزمینشان و خانه و کاشانه‌هاشان و مال و منالشان را به شما میراث داد، و نیز سرزمینی را که هنوز پا به آنجا نگذارده بودید، و خداوند بر هر کاری تواناست‌'),
(3561, 33, 28, 'ای پیامبر به همسرانت بگو اگر زندگانی دنیا و تجمل آن را می‌خواهید پس بیایید تا بهره‌مندتان سازم و به رهایشی نیکو رهایتان کنم‌'),
(3562, 33, 29, 'و اگر خداوند و پیامبر او و سرای آخرت را می‌خواهید بدانید که خداوند از برای نیکوکاران شما پاداشی بزرگ آماده ساخته است‌'),
(3563, 33, 30, 'بگو ای زنان پیامبر هر کس از شما مرتکب کار ناشایست آشکار شود، عذاب او دوچندان می‌شود و این امر بر خداوند آسان است‌'),
(3564, 33, 31, 'و هر کس از شما در برابر خداوند و پیامبرش فروتنی و تسلیم پیشه کند، و کار شایسته پیش گیرد، پاداش او را دوبار [/دو چندان‌] می‌دهیم، و برای او روزی ارزشمند فراهم سازیم‌'),
(3565, 33, 32, 'ای زنان پیامبر شما همانند هیچ یک از زنان نیستید [و برتر و مسؤول‌ترید]، اگر تقوا پیشه کنید پس در سخن نرمی نکنید که فرد بیماردل به طمع [خام‌] افتد، و به نیکی [و سنجیدگی‌] سخن گویید'),
(3566, 33, 33, 'و در خانه‌هایتان قرار و آرام گیرید و همانند زینت‌نمایی روزگار جاهلیت پیشین، زینت‌نمایی نکنید، و نماز را برپا دارید و زکات را بپردازید و از خداوند و پیامبر او اطاعت کنید، همانا خداوند می‌خواهد که از شما اهل بیت هر پلیدی [احتمالی / شک و شبهه‌] را بزداید، و شما را چنانکه باید و شاید پاکیزه بدارد'),
(3567, 33, 34, 'و هر آنچه از آیات الهی و حکمت که در خانه‌های شما خوانده می‌شود، در یاد گیرید، خداوند باریک‌بین آگاه است‌'),
(3568, 33, 35, 'بی‌گمان مردان و زنان مسلمان، و مردان و زنان مؤمن، و مردان و زنان فرمانبر، و مردان و زنان درستکار، و مردان و زنان شکیبا، و مردان و زنان فروتن، و مردان و زنان صدقه‌بخش، و مردان و زنان روزه‌دار، و مردان و زنان پاکدامن، و مردان و زنانی که خداوند را بسیار یاد می‌کنند، خداوند برای همگیشان آمرزش و پاداشی بزرگ آماده ساخته است‌'),
(3569, 33, 36, 'و هیچ مرد و زن مؤمنی را نرسد که چون خداوند و پیامبرش امری را مقرر دارند، آنان را در کارشان اختیار [و چون و چرایی‌] باشد، و هر کس از [امر] خداوند و پیامبر او سرپیچی کند در گمراهی آشکاری افتاده است‌'),
(3570, 33, 37, 'چنین بود که به کسی که هم خداوند و هم خود تو در حق او نیکی کرده بودید، گفتی که همسرت را نزد خویش نگهدار [و طلاق مده‌] و از خداوند پروا کن و چیزی را در دل خود پنهان داشتی که خداوند آشکارکننده آن بود، و از مردم بیم داشتی، و حال آنکه خداوند سزاوارتر است به اینکه از او بیم داشته باشی، آنگاه چون زید از او حاجت خویش برآورد، او را به همسری تو درآوردیم، تا برای مؤمنان در مورد همسران پسر خواندگانشان -به ویژه آنگاه که از اینان حاجت خویش را برآورده باشندمحظوری نباشد، و امر الهی انجام یافتنی است‌'),
(3571, 33, 38, 'بر پیامبر در آنچه خداوند برایش مقرر داشته است، محظوری نیست، این سنت الهی است که در حق پیشینیان هم معمول بوده است، و امر الهی سنجیده و بسامان است‌'),
(3572, 33, 39, 'همان کسانی که پیامهای الهی را می‌رسانند و از او پروا دارند و از هیچ کس جز خداوند پروا ندارند و خداوند حسابرسی را بسنده است‌'),
(3573, 33, 40, 'محمد هرگز پدر هیچ یک از مردان شما نیست، بلکه پیامبر خدا و خاتم پیامبران است، و خداوند به هر چیزی داناست‌'),
(3574, 33, 41, 'ای مؤمنان خداوند را بسیار یاد کنید'),
(3575, 33, 42, 'و او را در بامداد و شامگاه نیایش کنید'),
(3576, 33, 43, 'او کسی است که خود و فرشتگانش به شما درود می‌فرستند، تا سرانجام شما را از تاریکیها به سوی روشنایی برآورد، و او در حق مؤمنان مهربان است‌'),
(3577, 33, 44, 'درود [خاص‌] آنان در روزی که به لقای او نایل شوند، سلام است، و او برای ایشان پاداشی ارجمند آماده ساخته است‌'),
(3578, 33, 45, 'ای پیامبر ما تو را گواه و مژده‌رسان و هشداردهنده فرستاده‌ایم‌'),
(3579, 33, 46, 'و نیز دعوتگر به سوی خداوند به اذن او، و همچون چراغی تابان‌'),
(3580, 33, 47, 'و مؤمنان را بشارت ده که برای آنان از جانب خداوند بخشش و بخشایش بزرگی [در پیش‌] است‌'),
(3581, 33, 48, 'و از کافران و منافقان اطاعت مکن و آزارشان را [بدون مقابله و تلافی‌] بگذار و بر خداوند توکل کن و خداوند کارسازی را بسنده است‌'),
(3582, 33, 49, 'ای مؤمنان چون با زنان مؤمن ازدواج کردید، سپس پیش از آنکه با آنان هم‌آغوشی کنید، طلاقشان دادید، برای شما به عهده آنان عده‌ای نیست که حسابش را نگه دارید، پس آنان را [به نیمه مهر، یا هدیه‌ای‌] برخوردار سازید و به خیر و خوشی رهایشان کنید'),
(3583, 33, 50, 'ای پیامبر، ما همسرانت را بر تو حلال داشته‌ایم، [یعنی‌] آنانی را که مهرشان را داده‌ای، و آنانی را که خداوند از طریق فی‌ء و غنیمت به تو بخشیده است، و ملک یمین تو هستند، و همچنین دختران عمویت و دختران عمه‌ات و دختران دایی‌ات، و دختران خاله‌ات که همراه با تو هجرت کرده‌اند، و نیز زن مؤمنی را که خویشتن را به پیامبر ببخشد -به شرط آنکه پیامبر بخواهد او را به همسری خود درآوردکه این خاص تو و نه سایر مؤمنان است، خود به خوبی می‌دانیم که برای ایشان در مورد همسرانشان و ملک یمینهایشان چه چیزهایی مقرر داشته‌ایم، تا [در نهایت‌] برای تو محظوری نباشد، و خداوند آمرزگار مهربان است‌'),
(3584, 33, 51, 'هر کدام از آنان [همسرانت‌] را که می‌خواهی از خود دور بدار و هر کدام را که می‌خواهی نزدیک بدار، و نیز اگر هر یک از آنانی که از ایشان کناره گرفته‌ای جویا شوی، در همه حال، هیچ گناهی بر تو نیست، این نزدیکتر است به آنکه دل و دیدگانشان روشن شود، و اندوهگین نشوند و همگیشان به آنچه به آنان بخشیده‌ای خشنود شوند، و خداوند می‌داند که در دلهای شما چیست، و خداوند دانای بردبار است‌'),
(3585, 33, 52, 'پس از آن دیگر هیچ زنی بر تو حلال نیست، و نشاید که همسرانی را جانشین آنان سازی، و گرچه زیبایی آنان تو را خوش آید، مگر آنچه ملک یمینت باشد، و خداوند نگاهبان و ناظر همه چیز است‌'),
(3586, 33, 53, 'ای مؤمنان وارد حجره‌های پیامبر نشوید مگر آنکه به شما برای صرف طعامی اجازه داده شود، بی‌آنکه [بی‌تابانه‌] منتظر آماده‌شدنش باشید، ولی چون دعوت شدید، وارد شوید، و چون غذا خوردید، پراکنده شوید، و سرگرم سخنگویی نشوید، چرا که این کارتان پیامبر را رنج می‌دهد و او از شما شرم می‌کند [که حقیقت را بگوید] ولی خداوند از گفتن حق، شرم نمی‌کند، و نیز هنگامی که از همسران او [کالایی /] چیزی خواستید، از پشت حجاب و حایلی آن را از ایشان بخواهید، که این کارتان برای دلهای شما و دلهای ایشان پاکیزه‌تر است، و شما را نرسد که پیامبر خدا را آزار برسانید، و نیز نرسد که هرگز پس از او با همسرانش ازدواج کنید بی‌گمان این کارتان از نظر خداوند سهمگین است‌'),
(3587, 33, 54, 'اگر چیزی را آشکار کنید یا پنهانش بدارید، بدانید که خداوند به هر چیزی داناست‌'),
(3588, 33, 55, '[ولی‌] بر آنان [در مورد فروگذاشتن حجاب‌] در مورد پدرانشان، و پسرانشان، و برادرانشان، و پسران برادرشان، و پسران خواهرشان، و نیز زنان همنشینشان و ملک یمینهایشان گناهی نیست، و از خداوند پروا کنید که خدا بر هر چیزی گواه است‌'),
(3589, 33, 56, 'همانا خداوند و فرشتگانش به پیامبر درود می‌فرستند، ای مؤمنان [شما نیز] بر او درود بفرستید و سلام [و تسلیم‌] عرضه دارید'),
(3590, 33, 57, 'کسانی که [می‌خواهند] خداوند و پیامبر او را "برنجانند"، خداوند در دنیا و آخرت ایشان را لعنت می‌کند و برایشان عذابی خفت‌بار آماده ساخته است‌'),
(3591, 33, 58, 'و کسانی که مردان و زنان مؤمن را، بدون آنکه مرتکب عملی [ناروا] شده باشند، آزار می‌رسانند، زیر بار بهتان و گناهی آشکار رفته‌اند'),
(3592, 33, 59, 'ای پیامبر به همسرانت و دخترانت و زنان مسلمان بگو که روسریهای خود را بر خود بپوشند، که به این وسیله محتمل‌ترست که شناخته شوند و رنجانده نشوند، و خداوند آمرزگار مهربان است‌'),
(3593, 33, 60, 'اگر منافقان و بیماردلان و شایعه‌سازان در مدینه [از کارهای خود] دست برندارند، تو را به ایشان تسلط دهیم و سپس جز اندک مدتی در آن [شهر مدینه‌] در جوار تو نباشند'),
(3594, 33, 61, 'اینان ملعونند و هر جا که یافته شوند، باید بی‌محابا به اسارت و قتل درآیند'),
(3595, 33, 62, 'این سنت الهی است که در باب پیشینیان جاری بوده است، و هرگز در سنت الهی تغییر و تبدیلی نخواهی یافت‌'),
(3596, 33, 63, 'مردم از تو درباره قیامت می‌پرسند، بگو علم آن [و اطلاع از زمانش‌] با خداوند است، و تو چه دانی چه بسا قیامت نزدیک باشد'),
(3597, 33, 64, 'بی‌گمان خداوند کافران را لعنت کرده است، و برای آنان آتشی [سهمگین‌] آماده ساخته است‌'),
(3598, 33, 65, 'که جاودانه در آنند، و یار و یاوری نمی‌یابند'),
(3599, 33, 66, 'روزی که چهره‌هایشان در آتش [دوزخ‌] گردانده شود، گویند کاش از خداوند اطاعت می‌کردیم و از پیامبر اطاعت می‌کردیم‌'),
(3600, 33, 67, 'و گویند پروردگارا ما از پیشوایان و بزرگترانمان اطاعت کردیم، آنگاه ما را به گمراهی کشاندند'),
(3601, 33, 68, 'پروردگار را به آنان دوچندان [سهم‌] از عذاب بده و عظیم لعنتشان کن‌'),
(3602, 33, 69, 'ای مؤمنان مانند کسانی که موسی را آزار دادند مباشید که خداوند از آنچه می‌گفتند [و بهتان می‌زدند] بری و برکنارش داشت، و نزد خداوند آبرومند بود'),
(3603, 33, 70, 'ای مؤمنان از خداوند پروا کنید و سخنی درست و استوار بگویید'),
(3604, 33, 71, 'تا اعمالتان و پیامبر او اطاعت کند، به راستی به رستگاری بزرگی نایل شده است‌'),
(3605, 33, 72, 'ما امانت [خویش‌] را بر آسمانها و زمین و کوهها عرضه داشتیم، ولی از پذیرفتن آن سر باززدند، و از آن هراسیدند، و انسان آن را پذیرفت، که او [در حق خویش‌] ستمکار نادانی بود'),
(3606, 33, 73, 'تا سرانجام خداوند مردان و زنان منافق و مردان و زنان مشرک را عذاب کند، و از [غفلت و قصور] مردان و زنان مؤمن درگذرد، و خداوند آمرزگار مهربان است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'سپاس خداوندی را که آنچه در آسمانها و زمین است، از آن اوست، و در [آغاز و] آخرت نیز سپاس او راست و او فرزانه آگاه است‌'),
(3608, 34, 2, 'می‌داند که چه چیزی در زمین فرو می‌رود، و چه چیزی از آن برون می‌آید، و چه چیزی از آسمان فرود می‌آید، و چه چیزی به آن فرا می‌رود، و او مهربان آمرزگار است‌'),
(3609, 34, 3, 'و کافران گویند قیامت برای ما فرا نمی‌رسد، بگو چرا، سوگند به پروردگارم که بی‌شبهه به شما فرا می‌رسد، همو که دانای نهان است، و هم سنگ ذره‌ای در آسمانها و زمین از او پنهان نیست، نیز چیزی کوچکتر از این و نه بزرگتر نیست مگر آنکه در کتابی مبین [ثبت‌] است‌'),
(3610, 34, 4, 'تا بدین‌سان کسانی را که ایمان آورده‌اند و کارهای شایسته کرده‌اند، پاداش دهد، اینانند که بر ایشان آمرزش و روزی ارجمند مقرر است‌'),
(3611, 34, 5, 'و کسانی که در [رد و انکار] آیات ما مقابله کنان [و بی‌حاصل‌] می‌کوشند، اینانند که عذابی از عقوبتی دردناک در انتظار آنان است‌'),
(3612, 34, 6, 'و دانش یافتگان آنچه از جانب پروردگارت بر تو نازل شده است، حق می‌دانند، که به راه خداوند پیروزمند ستوده، هدایت می‌کند'),
(3613, 34, 7, 'و کافران گویند آیا بشناسانیم به شما مردی را که خبر می‌دهد به شما که چون پاره و پراکنده شدید، آنگاه آفرینش [و هیئت‌] جدیدی خواهید یافت‌'),
(3614, 34, 8, 'آیا بر خداوند دروغ بسته است، یا [به خیال آنان‌] جنونی دارد؟ [هیچ کدام‌] بلکه نامؤمنان به آخرت در عذاب و گمراهی دور و دراز هستند'),
(3615, 34, 9, 'آیا آسمان و زمینی را که پیش روی و پشت ایشان است ننگریسته‌اند؟، اگر خواهیم به زمین فرو بریمشان، یا بر آنان پاره‌ای از آسمان را فرو اندازیم، در این امر برای هر بنده توبه کار، مایه عبرتی است‌'),
(3616, 34, 10, 'و به راستی از خود به داوود بخششی [و موهبتی‌] ارزانی داشتیم [و گفتیم‌] ای کوهها و ای مرغان با او [در تسبیح‌] همنوایی کنید، و آهن را برای او نرم گردانیدیم‌'),
(3617, 34, 11, '[و گفتیم‌] که زره‌های بلند و رسا بساز و در زره‌بافی سنجیده و بسامان کار کن، و همگان نیکوکاری کنید، که من به آنچه می‌کنید آگاهم‌'),
(3618, 34, 12, 'و برای سلیمان باد را [رام گردانیدیم‌]، که سیر بامدادیش یکماهه راه و سیر شامگاهیش یکماهه راه بود، و برای او چشمه مس [گداخته و جوشان‌] را روان ساختیم، و از جنیان گروهی در نزد او و به اذن پروردگارش کار می‌کردند، و هر کدام از آنان که از فرمان ما سرپیچید، به او از عذاب آتش [دوزخ‌] می‌چشانیم‌'),
(3619, 34, 13, '[آنان‌] برای او هر چه می‌خواست از محرابها و نقش و نگارها و کاسه‌های بزرگ حوض مانند، و دیگدانهای استوار [و غیر قابل نقل‌] می‌ساختند، [و گفتیم‌] ای خاندان داوود سپاس ورزید، و از بندگان من اندکی سپاسگزار هستند'),
(3620, 34, 14, 'و چون مرگ او را مقرر داشتیم، چیزی جز کرم چوب‌خواره، مرگ او را به آنان نشان نداد، که عصایش را خورد و چون [جسدش‌] در افتاد، جنیان پی بردند که اگر غیب می‌دانستند، در آن رنج و عذاب خفت‌بار نمی‌ماندند'),
(3621, 34, 15, 'برای قوم سبا در مسکنهایشان پدیده شگرفی بود [از جمله‌] دو بوستان در جانب راست و چپ، [که به ایشان گفتیم‌] از روزی پروردگارتان بخورید، و او را سپاس بگزارید، [شما را] شهری پاکیزه و پروردگاری آمرزگار است‌'),
(3622, 34, 16, 'ولی رویگردان شدند، آنگاه بر آنان سیل بنیان‌کن را روانه کردیم، و به جای آن دو بوستانشان دو بوستان دارای میوه‌های ناگوار و درخت گز و اندکمایه‌ای از درخت سدر جانشین کردیم‌'),
(3623, 34, 17, 'به خاطر کفرانی که ورزیده بودند این گونه جز ایشان دادیم، و آیا جز ناسپاس را کیفر می‌دهیم؟'),
(3624, 34, 18, 'و در بین آنان و آبادیهایی که به آنها برکت بخشیده بودیم، آبادیهای به هم پیوسته قرار داده بودیم، و در میان آنها سیر و سفر مقرر داشته بودیم، و [به آنان می‌گفتیم‌] شبها و روزها با کمال امن و امان در آنها سیر و سفر کنید'),
(3625, 34, 19, 'پس گفتند پروردگارا بین سفرهای ما فاصله انداز، و بدین‌سان بر خویشتن ستم کردند، آنگاه همچون افسانه‌شان گرداندیم و پاره و پراکنده‌شان ساختیم، بی‌گمان در این برای هر شکیبای شاکری مایه‌های عبرت است‌'),
(3626, 34, 20, 'و به راستی شیطان ظن خود را درباره ایشان راست یافت، آنگاه [همگی‌] جز گروهی از مؤمنان از او پیروی کردند'),
(3627, 34, 21, 'و او [شیطان‌] را بر آنان سلطه‌ای نبود، مگر آنکه سرانجام کسی را که به آخرت ایمان دارد، از کسی که از آن شک دارد، باز شناسانیم، و پروردگارت بر همه چیز نگهبان است‌'),
(3628, 34, 22, 'بگو کسانی را که در برابر خدا قائلید بخوانید، [خواهید دید] که هم سنگ ذره‌ای در آسمان و زمین اختیار و دست ندارند، و در [اداره و آفرینش آنها] ایشان را شرکتی نیست، و او [خداوند] را از میان آنان پشتیبانی نیست‌'),
(3629, 34, 23, 'و شفاعت نزد او سودی ندهد، مگر درباره کسی که برای او اجازه دهد، تا چون هراس از دلهای ایشان برطرف شود، گویند پروردگارتان چه گفت؟ گویند حق، و او بلندمرتبه بزرگ است‌'),
(3630, 34, 24, 'بگو چه کسی از آسمانها و زمین شما را روزی می‌دهد؟ بگو خداوند و ما یا شما بر طریق هدایت، یا در گمراهی آشکاریم‌'),
(3631, 34, 25, 'بگو نه از شما درباره گناهی که ما کرده‌ایم می‌پرسند و نه از ما درباره آنچه شما می‌کنید خواهند پرسید'),
(3632, 34, 26, 'بگو پروردگار ما، ما و شما را گرد می‌آورد، سپس در میان ما به حق داوری می‌کند و اوست داور دانا'),
(3633, 34, 27, 'بگو به من بنمایانید کسانی را که در مقام شریک به او نسبت می‌دهید چنین نیست، بلکه او خداوند پیروزمند فرزانه است‌'),
(3634, 34, 28, 'و تو را جز مژده‌آور و هشداردهنده برای همگی مردم نفرستاده‌ایم، ولی بیشترینه مردم نمی‌دانند'),
(3635, 34, 29, 'و گویند اگر راست می‌گویید این وعده کی فرا می‌رسد'),
(3636, 34, 30, 'بگو برای شما موعد روزی مقرر است که نه از آن ساعتی پس افتید و نه پیش افتید'),
(3637, 34, 31, 'و کافران گویند هرگز به این قرآن، و به آنچه پیش از آن بود، ایمان نمی‌آوریم، و اگر ستمکاران [مشرک‌] را بنگری که نزد پروردگارشان بازداشته شوند، بعضی با بعضی دیگر بگو مگو کنند مستضعفان به مستکبران گویند اگر شما نبودید بی‌شک، ما مؤمن بودیم‌'),
(3638, 34, 32, 'مستکبران به مستضعفان گویند آیا ما شما را از هدایتی که به سراغ شما آمد بازداشتیم؟ چنین نیست، بلکه خودتان گناهکار بودید'),
(3639, 34, 33, 'و مستضعفان به مستکبران گویند چنین نیست، بلکه مکر [شما در] شب و روز بود، آنگاه که به ما فرمان می‌دادید که به خداوند کفر بورزیم و برای او شریک قائل شویم، و چون عذاب را ببینند پشیمانی خود را پنهان دارند، و غلها را در گردنهای کافران بگذاریم، آیا جز در برابر آنچه کرده‌اند، جزا می‌یابند؟'),
(3640, 34, 34, 'و هیچ هشداردهنده‌ای به هیچ شهری نفرستادیم مگر آنکه نازپروردگان آن گفتند ما رسالت شما را منکریم‌'),
(3641, 34, 35, 'گفتند ما پرمال و منال‌تر و پرزاد و رودتر هستیم، و ما عذاب‌دیده نخواهیم بود'),
(3642, 34, 36, 'بگو بی‌گمان پروردگار من روزی را برای هر کس که بخواهد گشاده یا تنگ می‌دارد، ولی بیشترینه مردم نمی‌دانند'),
(3643, 34, 37, 'و اموال و اولاد شما چیزی نیست که شما را چنانکه باید و شاید به ما نزدیک گرداند، مگر [در مورد] کسی که ایمان آورده و کار شایسته کند، اینانند که به خاطر کار و کردارشان پاداش دوچندانی دارند، و هم ایشان در غرفه‌ها[ی بهشتی‌] در امن و امانند'),
(3644, 34, 38, 'و آنان که در [رد و انکار] آیات ما مقابله کنان [و بی‌حاصل‌] می‌کوشند، اینان برای عذاب احضار شوند'),
(3645, 34, 39, 'بگو بی‌گمان پروردگار من روزی را برای هر کس از بندگانش که بخواهد گشاده یا برای او تنگ می‌دارد، و هر آنچه انفاق کنید او [خداوند] عوض آن را می‌دهد، و او بهترین روزی‌دهندگان است‌'),
(3646, 34, 40, 'و روزی که همگی آنان را محشور گرداند، سپس به فرشتگان گوید آیا اینان شما را می‌پرستیدند؟'),
(3647, 34, 41, 'گویند پاکا که تویی، تو سرور ما هستی نه آنان، خیر، ایشان جنیان را می‌پرستیدند [و] بیشترینه‌شان به آنان مؤمن بودند'),
(3648, 34, 42, 'و امروز هیچ‌یک از آنان در حق دیگری اختیار سود و زیانی ندارد، و به کسانی که ستم [/شرک‌]ورزیده‌اند گوییم عذاب آتش [دوزخ‌] را که آن را تکذیب می‌کردید، بچشید'),
(3649, 34, 43, 'و چون آیات روشنگر ما بر آنان خوانده شود، گویند این جز مردی نیست که می‌خواهد شما را از آنچه پدرانتان می‌پرستیدند بازدارد، و گویند این جز بهتانی برساخته نیست، و کافران درباره حق -چون فراز آیدشانگویند این جز جادویی آشکار نیست‌'),
(3650, 34, 44, 'و به آنان کتابهایی [آسمانی‌] نداده بودیم، که آنها را بخوانند و بیاموزند، و پیش از تو به سوی آنان [پیامبر] هشداردهنده‌ای نفرستاده‌ایم‌'),
(3651, 34, 45, 'و کسانی که پیش از آنان بودند، تکذیب پیشه کردند، و به یک دهم آنچه [از مکنت و نعمت به پیشینیان‌] آنان داده بودیم، نایل نشدند، آنگاه پیامبران مرا دروغ‌زن شمردند، [بنگر] تا عقوبت من چگونه بود'),
(3652, 34, 46, 'بگو شما را فقط به کلمه‌ای یگانه پند می‌دهیم، و آن این است که دوگان دوگان، و یکان یکان به کار خداوند برخیزید و سپس اندیشه کنید، همسخن شما جنونی ندارد، او جز هشداردهنده‌ای برای شما در پیشاپیش عذابی شدید نیست‌'),
(3653, 34, 47, 'بگو هر مزدی که از شما طلبیده باشم متعلق به خودتان، مزد من جز با خداوند نیست، و او بر هر چیزی گواه است‌'),
(3654, 34, 48, 'بگو بی‌گمان پروردگار من حق را به میان می‌آورد و او دانای رازهای نهانی است‌'),
(3655, 34, 49, 'بگو حق به میان آمد [معبود] باطل نه [آفرینش چیزی را] آغاز کند و نه بازگرداند'),
(3656, 34, 50, 'بگو اگر گمراه باشم، فقط به زیان خویش گمراه بوده‌ام، و اگر رهیافته باشم، آن به برکت وحی است که پروردگار من به من فرستاده است، چرا که او شنوای نزدیک است‌'),
(3657, 34, 51, 'و چون بنگری آنگاه که هراسان شوند گریزی در کار نیست، و از جایی نزدیک فرو گرفته شوند'),
(3658, 34, 52, 'و گویند [اکنون‌] به آن [قرآن/ قیامت/ پیامبر] ایمان آوردیم، و چگونه از جایی [چنین‌] دور، دسترس [به آن‌] برای آنان میسر باشد؟'),
(3659, 34, 53, 'و پیشتر هم به آن انکار ورزیده بودند، و از دور دستها، تیری در تاریکی می‌اندازند'),
(3660, 34, 54, 'و بین آنان و آنچه خوش دارند، فاصله افتد، چنانکه پیشترها در حق همانندانشان هم چنین شده بود که آنان سخت در شک بودند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'سپاس خداوند را، پدید آورنده آسمانها و زمین، که فرشتگان را پیام رسان گردانده است، [فرشتگانی‌] که دارای بالهای دوگانه و سه‌گانه و چهارگانه‌اند، در آفرینش هر چه خواهد می‌افزاید، بی‌گمان خداوند بر همه چیز تواناست‌'),
(3662, 35, 2, 'هر رحمتی که خداوند در حق مردم گشاده سازد، بازدارنده‌ای ندارد، و هر آنچه فروبندد، گشاینده‌ای جز او ندارد، و او پیروزمند فرزانه است‌'),
(3663, 35, 3, 'ای مردم نعمت الهی را بر خودتان یاد آورید، آیا آفریدگاری غیر از خداوند هست که شما را از آسمان و زمین روزی دهد؟ خدایی جز او نیست، پس چگونه بیراهه می‌روید؟'),
(3664, 35, 4, 'و اگر تو را دروغ‌زن شمردند بدان که پیامبران پیش از تو هم با تکذیب روبه‌رو شدند، و کارها به سوی خداوند بازگردانده می‌شود'),
(3665, 35, 5, 'ای مردم بی‌گمان وعده الهی حق است، پس زندگانی دنیا شما را مفریبد، و [شیطان‌] فریبکار شما را نسبت به خداوند فریفته نگرداند'),
(3666, 35, 6, 'بی‌گمان شیطان دشمن شماست، شما هم او را دشمن گیرید، جز این نیست که او حزبش را دعوت می‌کند که سرانجام از دوزخیان باشند'),
(3667, 35, 7, 'کسانی که کفرورزیده‌اند عذابی شدید [در پیش‌] دارند، و کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، از آمرزش و پاداشی عظیم برخوردارند'),
(3668, 35, 8, 'آیا کسی که بدی عملش در نظرش آراسته جلوه داده شده است، و لذا آن را نیک می‌بیند [مانند کسی است که خداوند هدایتش کرده باشد] بی‌گمان خداوند هر کس را که خواهد بیراه دارد، و هر کس را که خواهد هدایت کند مبادا جان تو از حسرت خوردن بر ایشان بفرساید، بی‌گمان خداوند به آنچه می‌کنند آگاه است‌'),
(3669, 35, 9, 'و خداوند کسی است که بادها را می‌فرستد که ابری را بر می‌انگیزد، آنگاه آن را به سوی سرزمینی پژمرده می‌رانیم، و بدان زمین را بعد از پژمردنش زنده می‌داریم، رستاخیز هم همین‌گونه است‌'),
(3670, 35, 10, 'هر کس عزت می‌خواهد [بداند که‌] هر چه عزت است، نزد خداوند است، سخنان پاکیزه به سوی او بالا می‌رود، و کار نیک آن را بالا می‌برد، و کسانی که بدسگالی می‌کنند، عذابی شدید [در پیش‌] دارند، و مکر اینان بر باد است‌'),
(3671, 35, 11, 'و خداوند شما را از خاک، سپس از نطفه آفریده است، سپس شما را زوج [نر و ماده‌] گردانده است، و هیچ زنی باردار نمی‌شود و وضع حمل نمی‌کند مگر با علم او، و هیچ کهنسالی عمر داده یا از عمر او کاسته نمی‌شود، مگر آنکه در کتابی [مسطور] است، این امر بر خداوند آسان است‌'),
(3672, 35, 12, 'و آن دو دریا برابر نیستند، این یک شیرین خوشگوار که نوشیدنش گواراست، و این یک شور و تلخ، و از هر کدام گوشت تر و تازه می‌خورید، و [از آنها] زیوری بیرون می‌آورید و آن را "می‌پوشید" کشتیی [ها] را در آن، دریا شکاف می‌بینی تا در طلب روزی مقرر از جانب او برآیید، و باشد که سپاس بگزارید'),
(3673, 35, 13, 'از شب می‌کاهد و بر روز می‌افزاید و از روز می‌کاهد و بر شب می‌افزاید، و خورشید و ماه را رام کرده است که هر یک تا سرآمدی معین جریان دارند، چنین است خداوند پروردگارتان، که فرمانروایی او راست، و کسانی که [ای ناباوران‌] به جای او می‌خوانید، مالک [چیزی حتی به اندازه‌] پوست هسته خرما نیستند'),
(3674, 35, 14, 'اگر بخوانیدشان، ندای شما را نمی‌شنوند، و اگر هم می‌شنیدند، پاسختان را نمی‌دادند، و روز قیامت، شرک شما را انکار می‌کنند، و هیچ کس چون [خداوند] آگاه تو را آگاه نمی‌سازد'),
(3675, 35, 15, 'هان ای مردم، شما [نادار و] نیازمند به خداوند هستید، و خداوند است که بی‌نیاز ستوده است‌'),
(3676, 35, 16, 'اگر بخواهد شما را [از میان‌] می‌برد، و آفریدگان جدیدی به میان می‌آورد'),
(3677, 35, 17, 'و این امر بر خداوند دشوار نیست‌'),
(3678, 35, 18, 'و هیچ بردارنده‌ای بار گناه دیگری را بر ندارد، و اگر گرانباری [دیگران را] بخواند که بارش را بردارند، چیزی از آن بار برداشته نشود، و اگر چه [مخاطب‌] خویشاوند باشد، تو فقط کسانی را که به نادیده از پروردگارشان خوف و خشیت دارند، و نماز را برپا می‌دارند، هشدار می‌دهی، و هر کس پاکی ورزد، همانا به سود خویش پاکی ورزیده است، و سیر و سرانجام به سوی خداوند است‌'),
(3679, 35, 19, 'و نابینا و بینا برابر نیست‌'),
(3680, 35, 20, 'و نه تاریکی و روشنایی‌'),
(3681, 35, 21, 'و نیز سایه و آتشباد'),
(3682, 35, 22, 'و [دل‌] زندگان و [دل‌] مردگان نیز برابر نیستند، بی‌گمان خداوند هر کس را که بخواهد [پیام و پند خویش‌] می‌شنواند، و تو شنواننده [پند و پیامی به‌] در گور خفتگان نیستی‌'),
(3683, 35, 23, 'تو نیستی مگر هشداردهنده‌ای‌'),
(3684, 35, 24, 'ما تو را به حق، مژده‌رسان و هشداردهنده فرستاده‌ایم، و امتی نیست مگر آنکه در میان آنان هشداردهنده‌ای بوده است‌'),
(3685, 35, 25, 'و اگر تو را تکذیب کردند، بدان که پیشینیان آنان هم که پیامبرانشان برای آنان پدیده‌های روشنگر و نوشته‌ها و کتاب‌[های‌] روشنگر آوردند، نیز تکذیب پیشه کردند'),
(3686, 35, 26, 'سپس کافران را فرو گرفتم، [بنگر] تا عقاب من چگونه بوده است‌'),
(3687, 35, 27, 'آیا ننگریسته‌ای که خداوند از آسمان آبی فرو فرستاده است، سپس بدان میوه‌های رنگارنگ برآوردیم، و نیز از کوهها راههایی سفید و سرخ رنگارنگ، و همچنین سیاه سیاه [پدید آوردیم‌]'),
(3688, 35, 28, 'بدین گونه از مردم و جانوران و چارپایان رنگارنگ [آفریده‌ایم‌]، از میان بندگان خداوند فقط دانشوران از او خوف و خشیت دارند، بی‌گمان خداوند پیروزمند آمرزگار است‌'),
(3689, 35, 29, 'بی‌گمان کسانی که کتاب الهی را می‌خوانند، و نماز را برپا می‌دارند، و از آنچه به ایشان روزی داده‌ایم، پنهان و آشکارا می‌بخشند، به سودایی که هرگز زیان ندارد، امید دارند'),
(3690, 35, 30, 'تا سرانجام پاداشهایشان را به تمام و کمال بپردازد، و از فضل خویش به سهمشان بیفزاید، چرا که آمرزگار و قدردان است‌'),
(3691, 35, 31, 'و آنچه از کتاب [آسمانی‌] به تو وحی کرده‌ایم، حق است و همخوان با آنچه [از کتب آسمانی، که‌] پیشاپیش اوست، بی‌گمان خداوند به [احوال‌] بندگانش آگاه بیناست‌'),
(3692, 35, 32, 'سپس کتاب آسمانی را به بندگان خود که برگزیده بودیمشان به میراث دادیم، و از ایشان [بعضی‌] ستمکار در حق خویش، و بعضی از ایشان میانه‌رو، و بعضی از ایشان پیشتاز در نیکوکاریها به اذن الهی است، این همانا فضل بزرگ است‌'),
(3693, 35, 33, 'بهشتهای عدن که واردش شوند، و در آنجا به دستبندهایی زرین و مروارید آراسته شوند، و لباس آنان در آنجا ابریشم است‌'),
(3694, 35, 34, 'و گویند سپاس خداوندی را که از ما اندوه را زدود، بی‌گمان پروردگار ما آمرزگار قدردان است‌'),
(3695, 35, 35, 'همان که ما را از فضل خویش به اقامتگاه جاویدان درآورد، که در آنجا به ما خستگی نرسد، و در آنجا به ما ماندگی نرسد'),
(3696, 35, 36, 'و کسانی که کفر ورزیده‌اند، آتش جهنم را در پیش دارند، که نه کارشان سپری شود که بمیرند، و نه چیزی از عذاب آن از ایشان کاسته شود، بدین‌سان هر [انسان‌] ناسپاسی را جزا دهیم‌'),
(3697, 35, 37, 'و ایشان در آنجا فریاد بردارند که پروردگارا ما را بیرون آور، که کاری شایسته، غیر از آنچه می‌کردیم، پیشه کنیم [در پاسخشان گوییم‌] آیا شما را چندان عمر ندادیم که در آن هر کس که اهل پند گرفتن است، پند گیرد و آیا [پیامبر] هشداردهنده‌ای به سوی شما نیامد؟ پس [عذاب را] بچشید که ستمکاران [مشرک‌] یاوری ندارند'),
(3698, 35, 38, 'بی‌گمان خداوند دانای نهانیهای آسمانها و زمین است، او دانای راز دلهاست‌'),
(3699, 35, 39, 'او کسی است که شما را در این سرزمین جانشینان [پیشینیان‌] ساخت، پس هر کس کفر ورزد، کفرش به زیان اوست، و کافران را کفرشان در نزد خداوند جز نفرت نیفزاید، و کافران را کفرشان جز زیان نیفزاید'),
(3700, 35, 40, 'بگو آیا شریکانی را که به جای خداوند قائلید نگریسته‌اید، به من بنمایانید چه چیزی را در زمین آفریده‌اند؟، یا آیا در [آفرینش‌] آسمانها شرکتی داشته‌اند؟ یا آیا به آنان کتابی داده‌ایم که ایشان بر مبنای آن حجتی [در دست‌] دارند؟ حق این است که ستمکاران [مشرک‌] به یکدیگر وعده‌ای جز فریب نمی‌دهند'),
(3701, 35, 41, 'بی‌گمان خداوند آسمانها و زمین را از زوال نگه می‌دارد، و اگر بخواهند زوال یابند هیچ کس جز او نگاهشان نمی‌دارد، او بردبار آمرزگار است‌'),
(3702, 35, 42, 'و سخت‌ترین سوگندهایشان را به [نام‌] خدا یاد کنند که اگر [پیامبر] هشداردهنده‌ای به نزد آنان می‌آمد، از هر یک از امتها [یهود و نصارا] رهیافته‌تر می‌شدند، و چون هشداردهنده‌ای به نزدشان آمد، جز بر گریزشان نیفزود'),
(3703, 35, 43, 'و این از سر استکبار در این سرزمین و بدسگالی بود، و بدسگالی جز به صاحب آن باز نمی‌گردد، آیا جز سنت پیشینیان را انتظار می‌کشند؟ پس هرگز در سنت الهی تبدیلی نمی‌یابی، و هرگز در سنت الهی تغییری نمی‌یابی‌'),
(3704, 35, 44, 'آیا در زمین سیر و سفر نکرده‌اند که بنگرند سرانجام کسانی که پیش از ایشان بودند و از ایشان نیرومندتر بودند، چگونه بوده است؟ و هیچ چیز در آسمانها و زمین از [حیطه قدرت‌] خداوند نتواند گریخت، چرا که او دانای تواناست‌'),
(3705, 35, 45, 'و اگر خداوند مردمان را به خاطر کار و کردار [ناروای‌] آنان فرو می‌گرفت، هیچ جانوری را بر پشت آن [زمین‌] باقی نمی‌گذارد، ولی ایشان را تا سرآمدی معین باز پس می‌دارد، و چون اجلشان فرارسد، آنگاه خداوند در حق بندگانش بیناست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یس [یاسین‌]'),
(3707, 36, 2, 'سوگند به قرآن حکمت‌آموز'),
(3708, 36, 3, 'که تو از پیامبرانی‌'),
(3709, 36, 4, 'بر راهی راست‌'),
(3710, 36, 5, '[این کتاب‌] فرو فرستاده [خداوند] پیروزمند مهربان است‌'),
(3711, 36, 6, 'تا قومی را که پدرانشان هشدار نیافته بودند، و خود غافلند، هشدار دهی‌'),
(3712, 36, 7, 'به راستی که حکم [عذاب‌] بر بیشترینه آنان تحقق یافته است و ایشان ایمان نمی‌آورند'),
(3713, 36, 8, 'ما بر گردنهایشان غفلهایی نهاده‌ایم تا [دستانشان را بسته است به گردنها و] چانه‌هایشان و ایشان سرهایشان به بالا و نگاهشان به پایین است‌'),
(3714, 36, 9, 'و در پیشاپیش آنان سدی و در پشتشان هم سدی نهاده‌ایم و بر [دیدگان‌] آنان پرده‌ای افکنده‌ایم، لذا نمی‌توانند دید'),
(3715, 36, 10, 'برای ایشان یکسان است چه هشدارشان دهی، چه هشدارشان ندهی، ایشان ایمان نمی‌آورند'),
(3716, 36, 11, 'تنها کسی را توانی هشدار داد که از پند [/ کتاب آسمانی‌] پیروی کند و به نادیده از خدای رحمان بهراسد، پس او را به آمرزش و پاداشی ارجمند بشارت ده‌'),
(3717, 36, 12, 'ما خود مردگان را [از نو] زنده می‌کنیم و آنچه در گذشته انجام داده‌اند، و حتی نقش گامهایشان را می‌نویسیم، و همه چیز را در کتابی روشنگر بر شمارده‌ایم‌'),
(3718, 36, 13, 'و برای آنان مثلی بزن از شهروندانی که پیامبران به آنجا [نزدشان‌] آمدند'),
(3719, 36, 14, 'آنگاه که به نزد آنان دو تن را فرستادیم، و آنان را دروغگو شمردند، سپس جانب آنان را با [فرستادن‌] سومین فرد استوار داشتیم، آنگاه [همگی‌] گفتند ما به سوی شما [به رسالت‌] فرستاده شده‌ایم‌'),
(3720, 36, 15, 'گفتند شما جز بشری همانند ما نیستید، و خدای رحمان چیزی فرو نفرستاده است، شما چیزی جز دروغ نمی‌گویید'),
(3721, 36, 16, 'گفتند پروردگار ما می‌داند که ما به سوی شما [به رسالت‌] فرستاده شده‌ایم‌'),
(3722, 36, 17, 'و بر عهده ما جز پیام‌رسانی آشکار چیزی نیست‌'),
(3723, 36, 18, 'گفتند ما به شما فال بد می‌زنیم، اگر دست برندارید، شما را سنگسار می‌کنیم، و از ما عذابی دردناک به شما می‌رسد'),
(3724, 36, 19, 'گفتند فال بدتان با شماست، آیا چون اندرز داده شوید [باید فال بد بزنید؟]، حق این است که شما قومی تجاوزپیشه‌اید'),
(3725, 36, 20, 'و مردی از دوردست شهر شتابان آمد، گفت ای قوم من از فرستادگان [/پیامبران‌] پیروی کنید'),
(3726, 36, 21, 'از کسانی که از شما پاداشی نمی‌خواهند و خود رهیافته‌اند، پیروی کنید'),
(3727, 36, 22, 'و مرا نرسد که کسی را که مرا آفریده است، و شما هم به سوی او بازگردانده می‌شوید، نپرستم‌'),
(3728, 36, 23, 'آیا به جای او خدایانی را به پرستش گیرم که اگر خدای رحمان بلایی در حق من اراده کند، شفاعت ایشان مرا سود ندهد و نتوانند مرا نجات دهند'),
(3729, 36, 24, 'من در آن صورت در گمراهی آشکارم‌'),
(3730, 36, 25, 'پس سخن مرا بشنوید [و شهادت دهید] که من به پروردگارتان ایمان آورده‌ام‌'),
(3731, 36, 26, 'گفته شود وارد بهشت شو، گوید ای کاش قوم من می‌دانستند'),
(3732, 36, 27, 'این را که پروردگارم مرا آمرزیده است، و مرا از گرامیان قرار داده است‌'),
(3733, 36, 28, 'و ما بر سر قوم او، پس از او، سپاهی از آسمان فرو نفرستادیم، و ما فرو فرستنده [آن‌] نبودیم‌'),
(3734, 36, 29, 'آن جز بانگ مرگباری یگانه نبود، آنگاه ایشان خاموش شدند'),
(3735, 36, 30, 'ای دریغ بر بندگان، هیچ پیامبری برای آنان نیامد، مگر آنکه او را ریشخند کردند'),
(3736, 36, 31, 'آیا نیندیشیده‌اند که چه بسیار پیش از ایشان، نسلهایی را نابود کردیم که آنان به سوی اینان باز نگشتند'),
(3737, 36, 32, 'و همگیشان جز جمعی نیستند که نزد ما احضار کرده شوند'),
(3738, 36, 33, 'و زمین پژمرده [/بایر]مایه عبرتی است برای آنان که زنده‌اش گرداندیم و از آن دانه‌ها برآوردیم که از آن می‌خورند'),
(3739, 36, 34, 'و در آن باغهایی از خرما و انگور پدید آوردیم و در آنجا چشمه‌ساران روان ساختیم‌'),
(3740, 36, 35, 'تا سرانجام از بار و بر آن و آنچه دستهای خودشان عمل آورده بود، بخورند، آیا سپاس نمی‌گزارند؟'),
(3741, 36, 36, 'پاک و منزه است کسی که همه گونه‌ها را آفریده است، از جمله آنچه زمین می‌رویاند و نیز از [وجود] خودشان و نیز آنچه نمی‌شناسند'),
(3742, 36, 37, 'و برای آنان شب پدیده شگرفی است که روز را از آن جدا می‌سازیم که آنگاه در تاریکی فرو می‌روند'),
(3743, 36, 38, 'و خورشید با قرار و قاعده‌[ی معینش‌] جریان دارد، این اندازه آفرینی [خداوند] پیروزمند داناست‌'),
(3744, 36, 39, 'و ماه را نیز منزلگاههایی مقرر داشته‌ایم تا [در سیر خویش‌] همچون شاخه خشکیده دیرینه باز می‌گردد'),
(3745, 36, 40, 'نه خورشید را سزاوار است که [در سیر خود] به ماه برسد، و نه شب بر روز سبقت جوید، و همه در سپهری شناورند'),
(3746, 36, 41, 'و مایه عبرتی است برای آنان که ما زاد و رودشان را در کشتی گرانبار سوار کردیم‌'),
(3747, 36, 42, 'و برای آنان چیزی همانند آن آفریده‌ایم که سوارش می‌شوند'),
(3748, 36, 43, 'و اگر بخواهیم آنان را غرقه می‌سازیم، و فریادرسی ندارند، و نجات داده نشوند'),
(3749, 36, 44, 'مگر رحمتی از سوی ما [بینند] و برخورداری تا زمانی معین‌'),
(3750, 36, 45, 'و چون به ایشان گفته شود از آنچه پیش روی شما و از آنچه پشت سرتان است، پروا کنید، باشد که مشمول رحمت شوید [اعراض کنند]'),
(3751, 36, 46, 'و هیچ آیتی از آیات پروردگارشان برای آنان نیامده است مگر آنکه از آن رویگردان بوده‌اند'),
(3752, 36, 47, 'و چون به ایشان گفته شود از آنچه خداوند به شما روزی داده است، بخشش کنید، کافران به مؤمنان گویند، آیا کسی را خوراک دهیم که اگر خداوند بخواهد خوراکش می‌دهد، شما جز در گمراهی آشکار نیستید'),
(3753, 36, 48, 'و گویند اگر راست می‌گویید این وعده کی فرا می‌رسد'),
(3754, 36, 49, 'جز بانگ مرگبار یگانه‌ای را انتظار نمی‌کشند که در حالی که ستیزه و جدل می‌کنند، فروگیردشان‌'),
(3755, 36, 50, 'و در آن هنگام نه وصیتی توانند کرد و نه به سوی خانواده‌شان باز می‌گردند'),
(3756, 36, 51, 'و در صور دمیده شود، آنگاه ایشان از گورها [برخیزند و] به سوی پروردگارشان بشتابند'),
(3757, 36, 52, 'گویند وای بر ما، کی ما را از خواب [گاه‌] مان برانگیخت؟ این همان است که خدای رحمان وعده داده بود و پیامبران راست گفته‌اند'),
(3758, 36, 53, '[سپس‌] جز بانگ مرگبار یگانه‌ای در کار نیست، آنگاه است که همگی [آنان‌] در نزد ما حاضر شدگانند'),
(3759, 36, 54, '[بدانید که‌] امروز بر هیچ کس ستمی نرود، و جز در برابر کاری که کرده‌اید، جزا نیابید'),
(3760, 36, 55, 'بی‌گمان بهشتیان امروز در کاری خوش و خرمند'),
(3761, 36, 56, 'ایشان و جفتهایشان در سایه‌ساران بر روی اورنگها تکیه زده‌اند'),
(3762, 36, 57, 'در آنجا برای آنان میوه‌هاست، و برای آنان هرچه طلب کنند، آماده است‌'),
(3763, 36, 58, 'سلام [بر شما]، این سخنی است از پروردگار مهربان‌'),
(3764, 36, 59, '[و ندا آید] امروز ای گنهکاران [از نیکوکاران‌] جدا شوید'),
(3765, 36, 60, 'آیا ای آدمیان با شما پیمان نبسته بودم که شیطان را نپرستید که او دشمن آشکار شماست؟'),
(3766, 36, 61, 'و اینکه مرا بپرستید، که این راهی راست است‌'),
(3767, 36, 62, 'و به راستی گروهی بسیار از شما را گمراه کرد، آیا تعقل نمی‌کردید؟'),
(3768, 36, 63, 'این همان جهنمی است که به شما وعده داده شده بود'),
(3769, 36, 64, 'امروز به خاطر کفری که می‌ورزیدید، به آن درآیید'),
(3770, 36, 65, 'امروز بر دهانهایشان مهر گذاریم و درباره آنچه می‌کردند، دستهایشان با ما سخن بگویند، و پاهایشان گواهی دهند'),
(3771, 36, 66, 'و اگر خواهیم دیدگانشان را نابینا سازیم، آنگاه به سوی راه [/صراط] بشتابند، اما چگونه بنگرند'),
(3772, 36, 67, 'و اگر خواهیم آنان را در جایشان مسخ گردانیم، آنگاه نتوانند رفتاری کنند و نه بازگردند'),
(3773, 36, 68, 'و هر کس را که عمر [دراز] دهیم، خلقت [و رفتارش‌] را باژگونه کنیم، آیا تعقل نمی‌کنند؟'),
(3774, 36, 69, 'و ما به او [پیامبر] شعر نیاموخته‌ایم، و سزاوار او [هم‌] نیست، این جز اندرز و قرآن مبین نیست‌'),
(3775, 36, 70, 'تا هر کس را که زنده [دل‌] است هشدار دهد و حجت را بر کافران تمام گرداند'),
(3776, 36, 71, 'آیا نیندیشیده‌اند که ما برای آنان از آنچه دستان [قدرت‌] مان بر سازد، چارپایانی آفریده‌ایم که ایشان دارای آن هستند'),
(3777, 36, 72, 'و آنها را رام ایشان گردانده‌ایم، لذا هم مرکوبشان از آنهاست و هم از آن می‌خورند'),
(3778, 36, 73, 'و برای آنان در آنها سودها و آشامیدنی‌هاست، آیا سپاس نمی‌گزارند؟'),
(3779, 36, 74, 'و به جای خداوند خدایانی را [به پرستش‌] گرفته‌اند به امید آنکه ایشان یاری یابند'),
(3780, 36, 75, '[اما] آنها به یاری [دادن‌] ایشان توانایی ندارند، و ایشان برای آنها چون سپاهی هستند که در [عرصه قیامت‌] حاضر کرده شوند'),
(3781, 36, 76, 'پس سخنشان تو را اندوهگین نکند، ما آنچه پنهان می‌دارند و آنچه آشکار می‌دارند می‌دانیم‌'),
(3782, 36, 77, 'آیا انسان نیندیشیده است که ما او را از نطفه‌ای آفریده‌ایم، آنگاه او جدل‌پیشه‌ای آشکار است‌'),
(3783, 36, 78, 'و برای ما مثل می‌زند و آفرینش خود را فراموش می‌کند، گوید چه کسی استخوانها را -در حالی که پوسیده‌انداز نو زنده می‌گرداند؟'),
(3784, 36, 79, 'بگو همان کسی که نخستین‌بار آن را پدید آورده است، زنده‌اش می‌گرداند، او به هر آفرینشی دانا [و توانا]ست‌'),
(3785, 36, 80, 'همان کسی که برای شما از درخت سبز[تر و تازه‌] آتشی پدید آورد، که آنگاه از آن آتش می‌افروزید'),
(3786, 36, 81, 'آیا کسی که آسمانها و زمین را آفریده است، توانای آن نیست که مانند ایشان را بیافریند، چرا، و او آفرینشگر داناست‌'),
(3787, 36, 82, 'امر او چون [آفرینش‌] چیزی را اراده کند، تنها همین است که به آن می‌گوید موجود شو، [و بی‌درنگ‌] موجود می‌شود'),
(3788, 36, 83, 'پس منزه است کسی که ملکوت هر چیز به دست اوست و به سوی او باز گردانده می‌شوید');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'سوگند به فرشتگان صف در صف‌'),
(3790, 37, 2, 'و به بازدارندگان [از معاصی‌]'),
(3791, 37, 3, 'و به خوانندگان ذکر [قرآن و کتابهای آسمانی‌]'),
(3792, 37, 4, 'که خدای شما یگانه است‌'),
(3793, 37, 5, 'پروردگار آسمانها و زمین و مابین آنها و پروردگار مشرق‌ها [و مغرب‌ها]'),
(3794, 37, 6, 'ما آسمان دنیا را به زیور ستارگان آراسته‌ایم‌'),
(3795, 37, 7, 'و از هر شیطان سرکشی محفوظ داشته‌ایم‌'),
(3796, 37, 8, 'به [اسرار] ملأاعلی گوش نتوانند داد و از هر سو رانده شوند'),
(3797, 37, 9, 'به راندنی سخت، و عذابی پاینده [در پیش‌] دارند'),
(3798, 37, 10, 'مگر کسی که ربایشی برباید، که شهابی درخشان در پی‌اش افتد'),
(3799, 37, 11, 'پس از ایشان بپرس آیا آفرینش آنان استوارتر است یا آنانی که آفریده‌ایم، ما ایشان را از گلی چسبنده آفریده‌ایم‌'),
(3800, 37, 12, 'بلکه تو [از حال آنان‌] تعجب کرده‌ای، و آنان [کار و بار تو را] ریشخند می‌کنند'),
(3801, 37, 13, 'و چون اندرزشان دهند، پند نپذیرند'),
(3802, 37, 14, 'و چون هر پدیده شگرفی بینند، تمسخر پیشه کنند'),
(3803, 37, 15, 'و گویند این جز جادویی آشکار نیست‌'),
(3804, 37, 16, '[گویند] چون مردیم و خاک و استخوانها[ی پوسیده‌] شدیم، آیا ما از نو برانگیخته [و زنده‌] خواهیم شد؟'),
(3805, 37, 17, 'و همچنین پدران نخستین ما؟'),
(3806, 37, 18, 'بگو آری، و شما خوار و زبون باشید'),
(3807, 37, 19, 'پس آن یک نعره تند باشد، پس آنگاه ایشان بنگرند'),
(3808, 37, 20, 'و گویند ای وای بر ما این روز جزاست‌'),
(3809, 37, 21, 'این [همان‌] روز داوری است که آن را انکار می‌کردید'),
(3810, 37, 22, 'ستم‌پیشگان [مشرک‌] و همتایانشان را همراه با آنچه به جای خداوند می‌پرستیدند [یکجا] گرد آورید،'),
(3811, 37, 23, 'آنگاه به سوی راه جهنم راهنمایشان کنید'),
(3812, 37, 24, 'و آنان را بازدارید که ایشان بازخواست کردنی هستند'),
(3813, 37, 25, 'شما را چه می‌شود که همدیگر را یاری نمی‌کنید؟'),
(3814, 37, 26, 'آری ایشان امروز تسلیم‌پیشه‌اند'),
(3815, 37, 27, 'و بعضی از آنان به بعضی دیگر به همپرسی روی آورند'),
(3816, 37, 28, 'گویند شما بودید که "حق به جانب" به سراغ ما می‌آمدید'),
(3817, 37, 29, 'گویند بلکه خود شما مؤمن نبودید'),
(3818, 37, 30, 'و ما را بر شما سلطه‌ای نبود، بلکه شما قومی سرکش بودید'),
(3819, 37, 31, 'و حکم پروردگارمان در حق ما تحقق یافت، ما چشندگان [عذاب‌]ایم‌'),
(3820, 37, 32, 'آنگاه شما را گمراه کردیم، ما خود هم گمراه بودیم‌'),
(3821, 37, 33, 'آنگاه ایشان در چنین روزی در عذاب مشترکند'),
(3822, 37, 34, 'ما با گناهکاران چنین می‌کنیم‌'),
(3823, 37, 35, 'اینان چنان بودند که چون به آنان [کلمه‌] لا اله الا الله گفته می‌شد، استکبار می‌ورزیدند'),
(3824, 37, 36, 'و می‌گفتند آیا ما رهاکننده خدایانمان به خاطر شاعری دیوانه باشیم؟'),
(3825, 37, 37, 'حق این است که او [پیامبر] حق را به میان آورد و پیامبران [پیشین‌] را تصدیق کرد'),
(3826, 37, 38, 'شما چشندگان عذاب دردناکید'),
(3827, 37, 39, 'و جز بر حسب آنچه کرده‌اید، جزا نمی‌یابید'),
(3828, 37, 40, 'مگر بندگان اخلاص یافته خداوند'),
(3829, 37, 41, 'اینان روزی معین دارند'),
(3830, 37, 42, 'انواع میوه‌ها، و خود گرامیانند'),
(3831, 37, 43, 'در بهشتهای پرناز و نعمت‌'),
(3832, 37, 44, 'بر روی تختها، رو به روی همدیگر'),
(3833, 37, 45, 'برای آنان جامی از شرابی جاری بگردانند [و پیش آرند]'),
(3834, 37, 46, 'سپید درخشان و لذت‌بخش آشامندگان‌'),
(3835, 37, 47, 'هیچ دردسری در آن نیست، و از آن مست نشوند'),
(3836, 37, 48, 'و نزد ایشان دوشیزگان چشم فروهشته [/قانع به همسر] درشت چشم هستند'),
(3837, 37, 49, 'گویی ایشان بیضه‌های شتر مرغ نهفته‌اند'),
(3838, 37, 50, 'و بعضی از آنان به بعضی دیگر به همپرسی روی آورند'),
(3839, 37, 51, 'گوینده‌ای از میان آنان گوید مرا [در دنیا] همنشینی بود'),
(3840, 37, 52, 'که می‌گفت آیا تو [هم‌] از باور دارندگانی؟'),
(3841, 37, 53, '[به اینکه‌] آیا چون مردیم و خاک و استخوانها[ی پوسیده‌] شدیم، آیا ما جزا خواهیم یافت؟'),
(3842, 37, 54, '[سپس‌] گوید آیا شما نگرندگانید؟'),
(3843, 37, 55, 'سپس فرانگرد و او را در میانه دوزخ ببیند'),
(3844, 37, 56, '[و از دور به او] گوید به خدا نزدیک بود که تو مرا به نابودی بکشانی‌'),
(3845, 37, 57, 'و اگر نعمت پروردگارم نبود من نیز از حاضر شدگان [در عذاب‌] بودم‌'),
(3846, 37, 58, 'پس آیا ما دیگر نمی‌میریم؟'),
(3847, 37, 59, 'مگر به مردن نخستینمان، و ما عذاب شونده نیستیم؟'),
(3848, 37, 60, 'بی‌گمان این [که من در بهشت هستم‌] همان رستگاری بزرگ است‌'),
(3849, 37, 61, 'باید که اهل عمل برای چنین هدفی بکوشند'),
(3850, 37, 62, 'آیا این پیشکش بهتر است یا درخت زقوم؟'),
(3851, 37, 63, 'ما آن را مایه آزمون ستمکاران [مشرک‌] قرار داده‌ایم‌'),
(3852, 37, 64, 'آن درختی است که از بن جهنم می‌روید'),
(3853, 37, 65, 'میوه‌اش گویی کله‌های شیاطین است‌'),
(3854, 37, 66, 'آنگاه ایشان خورنده از آنند، و پرکننده شکمهای خویش از آن‌'),
(3855, 37, 67, 'سپس برای آنان آمیزه‌ای از آب جوش هست که به دنبال آن نوشند'),
(3856, 37, 68, 'سپس بازگشتگاهشان به سوی دوزخ است‌'),
(3857, 37, 69, 'چرا که ایشان پدرانشان را گمراه یافتند'),
(3858, 37, 70, 'و آنان به دنبال ایشان شتافتند'),
(3859, 37, 71, 'و به راستی پیش از آنان، بیشترینه پیشینیان گمراه شدند'),
(3860, 37, 72, 'و به راستی به میان آنان هشداردهندگانی فرستادیم‌'),
(3861, 37, 73, 'پس بنگر سرانجام هشدار یافتگان چگونه بوده است‌'),
(3862, 37, 74, 'مگر بندگان اخلاص یافته خداوند'),
(3863, 37, 75, 'و به راستی نوح ما را ندا داد، و چه نیکو پاسخ‌دهندگانیم‌'),
(3864, 37, 76, 'و او و خانواده‌اش را از گرفتاری بزرگ رهانیدیم‌'),
(3865, 37, 77, 'و زاد و رود او را ماندگار گرداندیم‌'),
(3866, 37, 78, 'و برای او در میان واپسینیان، نام نیک نهادیم‌'),
(3867, 37, 79, 'در میان جهانیان، سلام بر نوح‌'),
(3868, 37, 80, 'ما بدین‌سان نیکوکاران را جزا می‌دهیم‌'),
(3869, 37, 81, 'که او از بندگان مؤمن ما بود'),
(3870, 37, 82, 'سپس دیگران را غرقه ساختیم‌'),
(3871, 37, 83, 'و ابراهیم جزو پیروان او بود'),
(3872, 37, 84, 'چنین بود که دل پاک و پیراسته [از شک و شرک‌] به درگاه پروردگارش آورد'),
(3873, 37, 85, 'آنگاه که به پدرش و قومش گفت چه چیزی را می‌پرستید؟'),
(3874, 37, 86, 'آیا به دروغ و دغل در هوای خدایانی به جای خداوند هستید؟'),
(3875, 37, 87, 'پس گمانتان درباره پروردگار جهانیان چیست؟'),
(3876, 37, 88, 'سپس نگاهی به ستارگان انداخت‌'),
(3877, 37, 89, 'و گفت من بیمارم‌'),
(3878, 37, 90, 'آنگاه به او پشت کردند [و تنهایش گذاشتند]'),
(3879, 37, 91, 'رو به خدایانشان آورد و گفت چرا [چیزی‌] نمی‌خورید'),
(3880, 37, 92, 'شما را چه می‌شود چرا سخن نمی‌گویید؟'),
(3881, 37, 93, 'پس [نهانی‌] بر آنان ضربه‌ای به شدت فرود آورد'),
(3882, 37, 94, 'آنگاه شتابان رو به سوی او آوردند'),
(3883, 37, 95, '[ابراهیم‌] گفت آیا چیزی را که [با دست خود] تراشیده‌اید می‌پرستید؟'),
(3884, 37, 96, 'و حال آنکه خداوند شما را و چیزهایی را که بر سر آنها کار می‌کنید، آفریده است‌'),
(3885, 37, 97, 'گفتند برای او بنایی بر آورید، و او را در آتش بیندازید'),
(3886, 37, 98, 'آنگاه در حق او بد سگالیدند، ولی آنان را فرودست گرداندیم‌'),
(3887, 37, 99, 'و [ابراهیم‌] گفت من رونده به سوی پروردگارم هستم، که به زودی مرا رهنمایی خواهد کرد'),
(3888, 37, 100, 'پروردگارا به من از شایستگان فرزندی ببخش‌'),
(3889, 37, 101, 'آنگاه او را به فرزندی بردبار مژده دادیم‌'),
(3890, 37, 102, 'و چون در کار و کوشش به پای او رسید، [ابراهیم‌] گفت ای فرزندم من در خواب دیده‌ام که سر تو را می‌برم، بنگر [در این کار] چه می‌بینی؟ [اسماعیل‌] گفت پدر جان آنچه فرمانت داده‌اند، انجام بده، که به زودی مرا به خواست خداوند، از شکیبایان خواهی یافت‌'),
(3891, 37, 103, 'آنگاه چون هر دو [بر این کار] گردن نهادند و او را بر گونه‌اش به خاک افکند'),
(3892, 37, 104, 'ندایش دادیم که ای ابراهیم‌'),
(3893, 37, 105, 'رؤیایت را به حقیقت باورداشتی، ما بدین‌سان نیکوکاران را جزا می‌دهیم‌'),
(3894, 37, 106, 'بی‌گمان این آزمونی آشکار است‌'),
(3895, 37, 107, 'و به جای او قربانی بزرگی را فدیه پذیرفتیم‌'),
(3896, 37, 108, 'و برای او در میان واپسینان نام نیک نهادیم‌'),
(3897, 37, 109, 'سلام بر ابراهیم‌'),
(3898, 37, 110, 'بدین‌سان نیکوکاران را جزا می‌دهیم‌'),
(3899, 37, 111, 'چرا که او از بندگان مؤمن ما بود'),
(3900, 37, 112, 'و او را به اسحاق که پیامبری از شایستگان بود، بشارت دادیم‌'),
(3901, 37, 113, 'و به او و به اسحاق برکت بخشیدیم، و از زاد و رود آنان، هم نیکوکار بود و هم آشکارا ستمگر در حق خویش‌'),
(3902, 37, 114, 'و به راستی بر موسی و هارون منت نهادیم [و نعمت دادیم‌]'),
(3903, 37, 115, 'آن دو و قومشان را از گرفتاری بزرگ رهانیدیم‌'),
(3904, 37, 116, 'و آنان را یاری دادیم و آنان بودند که پیروز شدند'),
(3905, 37, 117, 'و به آن دو، کتاب روشنگر بخشیدیم‌'),
(3906, 37, 118, 'و آن دو را به راه راست هدایت کردیم‌'),
(3907, 37, 119, 'و برای آنان در میان واپسینان نام نیک نهادیم‌'),
(3908, 37, 120, 'سلام بر موسی و هارون‌'),
(3909, 37, 121, 'ما بدین‌سان نیکوکاران را جزا می‌دهیم‌'),
(3910, 37, 122, 'که آن دو از بندگان مؤمن ما بودند'),
(3911, 37, 123, 'و همانا الیاس از پیامبران بود'),
(3912, 37, 124, 'چنین بود که به قومش گفت آیا پروا نمی‌کنید'),
(3913, 37, 125, 'آیا بعل را می‌پرستید و بهترین آفریدگاران را رها می‌کنید؟'),
(3914, 37, 126, 'خداوند را، که پروردگار شما و پروردگار پدران نخستین شماست؟'),
(3915, 37, 127, 'پس او را دروغگو شمردند و ایشان [برای حساب و عذاب‌] حاضر شدگانند'),
(3916, 37, 128, 'مگر آن بندگان خداوند که اخلاص یافته‌اند'),
(3917, 37, 129, 'و برای او در میان واپسینان نام نیک نهادیم‌'),
(3918, 37, 130, 'و سلام بر آل یاسین‌'),
(3919, 37, 131, 'ما بدین‌سان نیکوکاران را جزا می‌دهیم‌'),
(3920, 37, 132, 'او از بندگان مؤمن ما بود'),
(3921, 37, 133, 'و همانا لوط از پیامبران بود'),
(3922, 37, 134, 'چنین بود که او و خانواده‌اش، همگی را، رهایی دادیم‌'),
(3923, 37, 135, 'مگر پیرزنی که جزو واپس‌ماندگان [در عذاب‌] بود'),
(3924, 37, 136, 'سپس دیگران را نابود کردیم‌'),
(3925, 37, 137, 'و خود شما بر [آثار] آنان بامدادان می‌گذرید'),
(3926, 37, 138, 'و نیز شامگاهان، پس آیا تعقل نمی‌کنید؟'),
(3927, 37, 139, 'و بی‌گمان یونس از پیامبران بود'),
(3928, 37, 140, 'آنگاه که به سوی کشتی گرانبار گریخت‌'),
(3929, 37, 141, 'پس با آنان قرعه انداخت و او بیرون انداختنی شد'),
(3930, 37, 142, 'و ماهی [بزرگ /نهنگ‌] او را فرو بلعید و او در خور ملامت بود'),
(3931, 37, 143, 'و اگر از تسبیح‌گویان نبود'),
(3932, 37, 144, 'در شکم آن تا روزی که مردمان برانگیخته شوند به سر می‌برد'),
(3933, 37, 145, 'آنگاه او را به کرانه بایر افکندیم و او بیمار بود'),
(3934, 37, 146, 'و بر سر او [برای سایه افکنی‌] گیاهی از قسم کدو رویاندیم‌'),
(3935, 37, 147, 'و [سرانجام‌] او را به سوی صدهزار نفر یا بیشتر [به رسالت‌] فرستادیم‌'),
(3936, 37, 148, 'آنگاه ایمان آوردند و ما نیز آنان را تا مدتی بهره‌مند ساختیم‌'),
(3937, 37, 149, 'از آنان بپرس که آیا پروردگار تو را دختران است و خود ایشان را پسران؟'),
(3938, 37, 150, 'یا آنکه ما فرشتگان را مادینه آفریده‌ایم و ایشان شاهد بوده‌اند؟'),
(3939, 37, 151, 'هان ایشان از سر تهمتشان می‌گویند'),
(3940, 37, 152, 'خداوند فرزندی پدید آورده است، و ایشان دروغگو هستند'),
(3941, 37, 153, 'آیا [خداوند] دختران را بر پسران ترجیح داده است؟'),
(3942, 37, 154, 'شما را چه می‌شود، چگونه داوری می‌کنید؟'),
(3943, 37, 155, 'آیا اندیشه نمی‌کنید؟'),
(3944, 37, 156, 'یا حجتی آشکار در دست دارید؟'),
(3945, 37, 157, 'پس اگر راست می‌گویید سندتان را بیاورید'),
(3946, 37, 158, 'و بین او و جن پیوندی قائل شدند، و جنیان خود به خوبی می‌دانند که ایشان [برای حساب‌] حاضر شدگانند'),
(3947, 37, 159, 'پاک و منزه است خداوند از آنچه می‌گویند'),
(3948, 37, 160, 'مگر بندگان خداوند که اخلاص یافتگانند'),
(3949, 37, 161, 'بدانید که شما و آنچه می‌پرستید'),
(3950, 37, 162, 'شما کسی را به گمراهی نمی‌کشید'),
(3951, 37, 163, 'مگر کسی را که درآینده به دوزخ است‌'),
(3952, 37, 164, '[و فرشتگان گویند] هیچ کس از ما نیست مگر آنکه پایگاهی معین دارد'),
(3953, 37, 165, 'و ماییم که صف در صفیم‌'),
(3954, 37, 166, 'و ماییم که تسبیح گویانیم‌'),
(3955, 37, 167, 'و آنان ادعا می‌کردند'),
(3956, 37, 168, 'که اگر در دست ما کتابی از [کتابهای آسمانی‌] پیشینیان بود'),
(3957, 37, 169, 'بی‌شک از بندگان اخلاص یافته خداوند می‌شدیم‌'),
(3958, 37, 170, 'ولی آن [کتاب آسمانی/ قرآن‌] را انکار کردند، زودا که بدانند'),
(3959, 37, 171, 'و حکم ما در حق بندگان به رسالت فرستاده‌مان از پیش معین شده است‌'),
(3960, 37, 172, 'ایشانند که نصرت یافتگانند'),
(3961, 37, 173, 'و سپاه ماست که ایشان پیروزند'),
(3962, 37, 174, 'پس تا مدتی از آنان روی بگردان‌'),
(3963, 37, 175, 'و بنگر ایشان را، که زودا به دیده بصیرت بنگرند'),
(3964, 37, 176, 'آیا عذاب ما را به شتاب می‌خواهند'),
(3965, 37, 177, 'آنگاه که به سرای ایشان فرود آید، چه بد است بامداد [عذاب‌] هشدار یافتگان‌'),
(3966, 37, 178, 'و تا مدتی از آنان روی بگردان‌'),
(3967, 37, 179, 'و بنگر زودا که به دیده بصیرت بنگرند'),
(3968, 37, 180, 'پاک و منزه است پروردگارت، که پروردگار پیروزمند است، از آنچه می‌گویند'),
(3969, 37, 181, 'و درود بر پیامبران باد'),
(3970, 37, 182, 'و سپاس خداوند را که پروردگار جهانیان است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'ص [صاد] سوگند به قرآن پندآموز'),
(3972, 38, 2, 'آری کافران گرفتار حمیت [جاهلیت‌] و ستیزه‌جویی‌اند'),
(3973, 38, 3, 'چه بسیار نسلها که پیش از ایشان نابودشان کردیم و فریاد خواستند اما زمان، زمان گریز [و رهایی‌] نبود'),
(3974, 38, 4, 'و شگفتی می‌کردند که [پیامبر] هشداردهنده‌ای از خودشان به سراغ آنان آمده است، و کافران گفتند این جادوگر دروغ‌زن است‌'),
(3975, 38, 5, 'آیا همه خدایان را خدای یگانه‌ای می‌سازد، این امر عجیبی است‌'),
(3976, 38, 6, 'و بزرگان ایشان به راه خود رفتند و گفتند که بروید و بر [عبادت‌] خدایان خود شکیبایی پیشه کنید، که این امر مطلوب است‌'),
(3977, 38, 7, 'ما چنین چیزی در آیین اخیر نشنیده‌ایم این جز از خود بر ساختن نیست‌'),
(3978, 38, 8, 'آیا از میان همه ما قرآن بر او نازل شده است؟ بلکه ایشان از یاد من در شک و شبهه‌اند، یا هنوز عذاب مرا نچشیده‌اند'),
(3979, 38, 9, 'یا گنجینه‌های رحمت پروردگار پیروزمند بخشنده‌ات در اختیار آنان است؟'),
(3980, 38, 10, 'یا فرمانروایی آسمانها و زمین و مابین آنها از آن ایشان است، پس با نردبانها فراروند'),
(3981, 38, 11, 'در آنجا سپاهی است از همدستان که شکست خوردنی است‌'),
(3982, 38, 12, 'پیش از آنان قوم نوح و عاد و فرعون صاحب سپاه، تکذیب [پیامبران را] پیشه کردند'),
(3983, 38, 13, 'و نیز ثمود و قوم لوط و اصحاب ایکه، این جماعتها'),
(3984, 38, 14, 'یکایک آنان جز تکذیب پیامبران کاری نکردند، و لذا عقاب من [در حق آنان‌] تحقق یافت‌'),
(3985, 38, 15, 'و اینان جز بانگ مرگبار یگانه‌ای را انتظار نمی‌کشند که فروگذار نمی‌کند'),
(3986, 38, 16, 'و ادعا کردند که پروردگارا کارنامه ما را پیش از روز حساب، هر چه زودتر به ما بده‌'),
(3987, 38, 17, 'بر آنچه می‌گویند شکیبایی کن، و از بنده ما داوود یاد کن که هم توانمند بود و هم توبه کار'),
(3988, 38, 18, 'ما کوهها را همراه او رام کرده بودیم که شامگاهان و بامدادان همنوا با او تسبیح می‌گفتند'),
(3989, 38, 19, 'و پرندگان را که گرد آینده بودند و همه باز گردنده‌'),
(3990, 38, 20, 'و فرمانروایی او را استوار داشتیم و به او پیامبری و نفوذ کلامی فیصله‌بخش بخشیده بودیم‌'),
(3991, 38, 21, 'و آیا خبر اصحاب دعوی به تو رسیده است که از دیوار محراب او بالا رفتند'),
(3992, 38, 22, 'آنگاه که [ناگهان‌] بر داوود وارد شدند و او از ایشان هراسید، گفتند مترس، اصحاب دعوایی هستیم که بعضی از ما بر دیگری ستم کرده است، پس در میان ما به حق داوری کن، و بیداد مکن و ما را به راه راست رهنمایی کن‌'),
(3993, 38, 23, '[یکی از آنان گفت] این دوست من است که نود و نه میش دارد، و من یک میش تنها دارم، و می‌گوید آن را هم به من واگذار و با من درشت‌گویی می‌کند'),
(3994, 38, 24, '[داوود] گفت به راستی با خواستن میش تو و افزودنش به میشهای خود، در حق تو ستم کرده است، و بسیاری از شریکان هستند که در حق همدیگر ستم می‌کنند، مگر کسانی که ایمان آورده و کارهای شایسته پیش گرفته باشند، و ایشان [به واقع‌] اندکشمارند، و داوود دانست که ما او را آزموده‌ایم، آنگاه به درگاه پروردگارش استغفار کرد، و به سجده در افتاد و توبه کرد'),
(3995, 38, 25, 'آنگاه این امر را بر او آمرزیدیم، و او در نزد ما تقرب و نیک سرانجامی داشت‌'),
(3996, 38, 26, 'ای داوود ما تو را در روی زمین خلیفه [خود] برگماشته‌ایم، پس در میان مردم به حق داوری کن، و از هوی و هوس پیروی مکن، که تو را از راه خدا گمراه کند، بی‌گمان کسانی که از راه خدا گمراه می‌شوند عذاب سخت و سنگینی در پیش دارند چرا که روز حساب را فراموش کرده‌اند'),
(3997, 38, 27, 'و ما آسمان و زمین و مابین آنها را بیهوده نیافریده‌ایم، این پندار کسانی است که کفر ورزیده‌اند، و وای بر کسانی که کفرورزیده‌اند از [عذاب‌] آتش دوزخ‌'),
(3998, 38, 28, 'آیا کسانی را که ایمان آورده‌اند و کارهای شایسته کرده‌اند، همانند مفسدان در زمین قرار می‌دهیم، یا آیا پارسایان را مانند فاجران می‌شماریم؟'),
(3999, 38, 29, 'کتابی است مبارک که آن را بر تو فرو فرستاده‌ایم، تا در آیات آن اندیشه کنند، و خردمندان از آن پند گیرند'),
(4000, 38, 30, 'و به داوود سلیمان را بخشیدیم، چه نیکو بنده‌ای که تواب بود'),
(4001, 38, 31, 'چنین بود که شامگاهان اسبهای نیکویی بر او عرضه داشتند'),
(4002, 38, 32, '[سلیمان سرگرم تماشای آنان شد] آنگاه گفت [دریغا] من چنان شیفته مهر اسبان شدم که از یاد پروردگارم غافل شدم، تا آنکه [خورشید] در حجاب [مغرب‌] پنهان شد'),
(4003, 38, 33, '[سپس گفت‌] آنها را به نزد من باز گردانید آنگاه به دست کشیدن بر ساقها و گردنهای آنها پرداخت‌'),
(4004, 38, 34, 'و به راستی سلیمان را آزمودیم و بر تخت او جسدی را افکندیم، سپس توبه کرد'),
(4005, 38, 35, 'گفت پروردگارا مرا بیامرز و به من فرمانروایی‌ای ببخش که سزاوار هیچ کس پس از من نباشد که تو بخششگری‌'),
(4006, 38, 36, 'آنگاه باد را رام او ساختیم که به فرمان او هر جا که خواسته باشد به آهستگی روان باشد'),
(4007, 38, 37, 'و دیوانی را که همه بنا یا غواص بودند [مسخر او ساختیم‌]'),
(4008, 38, 38, 'و دیگرانی که به هم بستگان با پای بندها بودند'),
(4009, 38, 39, 'این بخشش ماست، بی‌حساب [آن را] ببخش یا برای خود نگهدار'),
(4010, 38, 40, 'و او را نزد ما تقرب و نیک سرانجامی بود'),
(4011, 38, 41, 'و بنده ما ایوب را یاد کن آنگاه که پروردگارش را ندا داد که شیطان به من رنج و عذاب رسانده است‌'),
(4012, 38, 42, '[گفتیم‌] پایت را به زمین بزن، اینک این شستن‌گاهی است سرد و نوشیدنی‌'),
(4013, 38, 43, 'و [سپس /دیگربار] خانواده‌اش و همانند آن را همراه ایشان، از سر رحمت خویش و برای پندآموزی به خردمندان، به او بخشیدیم‌'),
(4014, 38, 44, 'و [به او به رعایت عهدی که کرده بود، گفتیم] دسته‌ای چوب ترکه به دستت بگیر، و با آن [همسرت را فقط یک بار] بزن، و سوگند مشکن، ما او را شکیبا یافتیم، چه نیکو بنده‌ای که تواب بود'),
(4015, 38, 45, 'و یاد کن بندگان ما ابراهیم و اسحاق و یعقوب را که همه توانمند [در عبادت‌] و دیده‌ور بودند'),
(4016, 38, 46, 'ما آنان را به خصلتی ویژه ساختیم که آخرت اندیشی بود'),
(4017, 38, 47, 'و ایشان از نظر ما از برگزیدگان نیک بودند'),
(4018, 38, 48, 'و یاد کن اسماعیل و الیسع و ذوالکفل را که همه از نیکان بودند'),
(4019, 38, 49, 'این یاد کردی است و پرهیزگاران را نیک سرانجامی است‌'),
(4020, 38, 50, 'بهشتهای عدن که درهایش برای ایشان گشوده است‌'),
(4021, 38, 51, 'در آنجا تکیه [بر اورنگها] زدگانند، در آنجا میوه‌های بسیار، و نوشیدنی [ها] می‌طلبند'),
(4022, 38, 52, 'و نزد آنان دوشیزگان همسال چشم فروهشته [/قانع به شوهر] هستند'),
(4023, 38, 53, 'این است آنچه به شما برای روز حساب وعده داده شده بود'),
(4024, 38, 54, 'این همان رزق معین از جانب ماست که تمامی ندارد'),
(4025, 38, 55, 'چنین است، و سرکشان را بدترین سرانجام است‌'),
(4026, 38, 56, 'جهنم است که واردش می‌شوند و بد آرامگاهی است‌'),
(4027, 38, 57, 'چنین است، آب جوش و چرکابه که باید بچشندش‌'),
(4028, 38, 58, 'و عذابی دیگر است همانند آن به انواع و اقسام‌'),
(4029, 38, 59, 'این گروهی است که همراه با شما به دوزخ فرو رفته‌اند، خوشامدی نیست ایشان را، چرا که به آتش دوزخ در آینده‌اند'),
(4030, 38, 60, 'گویند شما را خوشامدی نباشد، شما بودید که آن راه را پیش پای ما گذاشتید، پس چه بد قرارگاهی است‌'),
(4031, 38, 61, 'گویند پروردگارا هر کس که این را پیش پای ما گذاشته است، عذابش را در آتش دوزخ دو چندان بیفزای‌'),
(4032, 38, 62, 'و گویند ما را چه شده است که مردانی را که از بد کرداران می‌شمردیمشان، [در اینجا] نمی‌بینیم؟'),
(4033, 38, 63, 'آیا آنان را به ریشخند گرفته‌ایم، یا چشمها[مان‌] بر ایشان نمی‌افتد؟'),
(4034, 38, 64, 'بی‌گمان ستیزه دوزخیان با همدیگر حق است‌'),
(4035, 38, 65, 'بگو من فقط هشداردهنده‌ام و خدایی جز خداوند یگانه قهار نیست‌'),
(4036, 38, 66, 'پروردگار آسمانها و زمین و مابین آنها که پیروزمند آمرزگار است‌'),
(4037, 38, 67, 'بگو آن خبری است بس بزرگ‌'),
(4038, 38, 68, 'که شما از آن رویگردانید'),
(4039, 38, 69, 'مرا هیچ آگاهی از ملأاعلی نیست، آنگاه که [فرشتگان‌] مجادله کردند'),
(4040, 38, 70, 'به من وحی نمی‌شود جز در این باب که من هشداردهنده‌ای آشکارم‌'),
(4041, 38, 71, 'چنین بود که پروردگارت به فرشتگان فرمود همانا من آفریننده انسانی از گل هستم‌'),
(4042, 38, 72, 'پس چون او را استوار بپرداختم، و در آن از روح خود دمیدم برای او به سجده در افتید'),
(4043, 38, 73, 'آنگاه فرشتگان همگی سجده کردند'),
(4044, 38, 74, 'مگر ابلیس که استکبار ورزید و از کافران شد'),
(4045, 38, 75, 'فرمود ای ابلیس چه چیزی تو را از سجده به آنچه با دستان خویش آفریده‌ام، بازداشت؟ آیا استکبار ورزیدی یا از بلندمرتبگان بودی؟'),
(4046, 38, 76, 'گفت من بهتر از او هستم، [چرا] که مرا از آتش آفریده‌ای و او را از گل آفریده‌ای‌'),
(4047, 38, 77, 'فرمود از آنجا بیرون شو که تو مطرودی‌'),
(4048, 38, 78, 'و لعنت من تا روز جزا بر تو خواهد بود'),
(4049, 38, 79, 'گفت پروردگارا پس مرا تا روزی که [مردمان‌] برانگیخته شوند مهلت ده‌'),
(4050, 38, 80, 'فرمود [پذیرفتم‌] تو از مهلت یافتگانی‌'),
(4051, 38, 81, 'تا روز آن هنگام معین‌'),
(4052, 38, 82, 'گفت [سوگند] به عزت تو که همگی آنان را گمراه خواهم ساخت‌'),
(4053, 38, 83, 'مگر از میان آنان، آن بندگانت را که اخلاص یافته‌اند'),
(4054, 38, 84, 'فرمود حق است و حق را می‌گویم‌'),
(4055, 38, 85, 'که جهنم را از تو و همه کسانی از آنان که از تو پیروی می‌کنند، آکنده خواهم ساخت‌'),
(4056, 38, 86, 'بگو برای آن [رسالت‌] از شما مزدی نمی‌طلبم، و من از بر خود بستگان نیستم‌'),
(4057, 38, 87, 'این جز پندی برای جهانیان نیست‌'),
(4058, 38, 88, 'و خبرش را پس از چندی خواهید دانست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'کتابی است که از سوی خداوند پیروزمند فرزانه فرو فرستاده شده است‌'),
(4060, 39, 2, 'کتاب آسمانی را به راستی و درستی بر تو نازل کرده‌ایم، پس خداوند را در حالی که دین خود را برای او پاک و پیراسته می‌داری بپرست‌'),
(4061, 39, 3, 'هان دین خالص خدای راست، و کسانی که به جای او سرورانی به پرستش می‌گیرند [و مدعی می‌شوند] آنان را جز برای این نمی‌پرستیم که ما را با تقربی به خداوند نزدیک گردانند، بی‌گمان خداوند در آنچه ایشان در آن اختلاف دارند، در میانشان داوری خواهد کرد، که خداوند کسی را که دروغ‌زن کفران پیشه است، هدایت نمی‌کند'),
(4062, 39, 4, 'اگر خداوند می‌خواست که فرزندی [برای خود] برگزیند، آنچه می‌خواست از میان آنچه آفریده است، بر می‌گزید، اما او [از این نسبتها] منزه است، او خداوند یگانه قهار است‌'),
(4063, 39, 5, 'که آسمانها و زمین را به حق آفریده است، و شب را بر روز و روز را بر شب می‌گستراند، و خورشید و ماه را رام کرده است، همه تا سرآمدی معین سیر می‌کنند، هان او پیروزمند آمرزگار است‌'),
(4064, 39, 6, 'شما را از تنی یگانه آفرید، سپس همسرش را از او پدید آورد، و برای شما هشت قسم از چارپایان آفرید، و خود شما را در شکم مادرانتان، آفرینشی پس از آفرینشی در میان تاریکیهای سه‌گانه می‌آفریند، این چنین است خداوند پروردگارتان، که فرمانروایی او راست، خدایی جز او نیست، پس چگونه به بیراهه می‌روید؟'),
(4065, 39, 7, 'اگر کفران‌بورزید، بدانید که خداوند از شما بی‌نیاز است، ولی در عین حال کفران را هم بر بندگان خویش نمی‌پسندد، و اگر سپاس بگزارید آن را بر شما می‌پسندد، و هیچ بردارنده‌ای بار گناه دیگری را برندارد، سپس بازگشتتان به سوی پروردگارتان است، آنگاه شما را از [حقیقت و نتیجه‌] آنچه کرده‌اید، آگاه می‌سازد، که او دانای راز دلهاست‌'),
(4066, 39, 8, 'و چون به انسان رنجی رسد پروردگارش را -انابت کنانمی‌خواند، سپس چون نعمتی از سوی خویش به او ارزانی دارد، فراموش می‌کند که پیشتر چه دعایی به درگاه او داشت، و برای خداوند همتایانی قائل می‌شود که [دیگران را هم‌] از راه او گمراه سازد، بگو با کفر خویش اندکی [از زندگانی‌] بهره‌مند شو که [سرانجام‌] تو از دوزخیانی‌'),
(4067, 39, 9, 'آیا کسی که در پاسهایی از شب، گاه به سجده و گاه بر پا، نیایشگر است و از آخرت بیم و به رحمت پروردگارش امید دارد [بهتر است یا مشرک غافل ناسپاس‌]، بگو آیا کسانی که می‌دانند، با کسانی که نمی‌دانند برابرند؟ [هرگز] فقط خردمندانند که پند می‌گیرند'),
(4068, 39, 10, 'بگو ای بندگان مؤمن من از پروردگارتان پروا کنید، برای کسانی که در این دنیا نیکی کنند، پاداش نیکوست، و زمین خدا گسترده است [از هجرت مهراسید] همانا پاداش شکیبایان بی‌حساب و به تمامی داده خواهد شد'),
(4069, 39, 11, 'بگو فرمان یافته‌ام که خداوند را -در حالی که دینم را برای او پاک و پیراسته می‌دارمبپرستم‌'),
(4070, 39, 12, 'و فرمان یافته‌ام که نخستین مسلمان [این امت‌] باشم‌'),
(4071, 39, 13, 'بگو من اگر از امر پروردگارم سرپیچی کنم، از عذاب روزی سهمگین می‌ترسم‌'),
(4072, 39, 14, 'بگو خداوند را -در حالی که دینم را برایش پیراسته می‌دارممی‌پرستم‌'),
(4073, 39, 15, 'شما هم هر چه می‌خواهید به جای او بپرستید، بگو زیانکاران [واقعی‌] کسانی هستند که به خویشتن و خانواده‌شان در روز قیامت زیان رسانده‌اند، هان این است زیانمندی آشکار'),
(4074, 39, 16, 'آنان را از فرازشان سایبانهایی از آتش است، و از فرودشان هم سایبانهایی، این همان است که خداوند بدان بندگانش را می‌ترساند، ای بندگان من از من پروا کنید'),
(4075, 39, 17, 'و کسانی که از طاغوت پرهیز کرده‌اند از اینکه بپرستندش، و به سوی خداوند باز گشته‌اند، بشارت [/بهشت‌] ایشان راست، پس بندگانم را بشارت ده‌'),
(4076, 39, 18, 'همان کسانی را که قول [نیک و حق‌] را می‌شنوند و آنگاه از بهترین آن پیروی می‌کنند، اینانند که خداوند هدایتشان کرده است و اینانند که خردمندانند'),
(4077, 39, 19, 'آیا کسی که حکم عذاب بر او تحقق یافته [و دوزخی‌] است، آیا تو کسی را که در دوزخ است می‌رهانی؟'),
(4078, 39, 20, 'ولی کسانی که از پروردگارشان پروا داشته‌اند، غرفه‌هایی دارند که بر فراز آنها غرفه‌هایی ساخته شده است، [و] از فرو دست آن جویباران جاری است، این وعده الهی است [و] خداوند در وعده‌[اش‌] خلاف نمی‌کند'),
(4079, 39, 21, 'آیا ننگریسته‌ای که خداوند از آسمان آبی فرو فرستاد و آن را در [مخزن‌] چشمه‌ساران در دل زمین راه داد، سپس بدان کشت گوناگون بر می‌آورد، سپس [آن کشت‌] می‌خشکد و آن را زرد می‌بینی، سپس آن را خرد و خوار می‌کند، در این امر برای خردمندان یادآوری است‌'),
(4080, 39, 22, 'آیا کسی که خداوند دلش را به اسلام گشاده داشته است، و از سوی پروردگارش برخوردار از نوری [هدایت‌] است [همانند سختدلان است؟]، پس وای بر آنان که از ترک یاد الهی، سخت دل هستند، اینانند که در گمراهی آشکارند'),
(4081, 39, 23, 'خداوند بهترین سخن را در هیئت کتابی همگون و مکرر فرو فرستاد، که پوستهای کسانی که از پروردگارشان خشیت دارند از آن به لرزه در آید، سپس [آرامش یابند و] پوستهایشان و دلهایشان با یاد خدا نرم شود، این هدایت الهی است که به آن هر کس را که بخواهد به راه می‌آورد، و هر کس که خداوند بیراهش گذارده باشد، رهنمایی ندارد'),
(4082, 39, 24, 'آیا کسی که روز قیامت از بدترین عذاب جان به در برد [مانند کسی است که از آن در امان نیست؟]، و به ستمکاران [مشرک‌] گفته شود [نتیجه‌] کاری را که کرده بودید بچشید'),
(4083, 39, 25, 'پیشینیان آنان هم تکذیب [پیامبران و کتب آسمانی‌] پیشه کردند، آنگاه عذاب به نحوی که گمانش را نمی‌بردند بر سرشان آمد'),
(4084, 39, 26, 'سپس خداوند به آنان در زندگانی دنیا خفت و خواری چشانید، و اگر می‌دانستند عذاب اخروی سهمگین‌تر است‌'),
(4085, 39, 27, 'و برای مردم در این قرآن از هر گونه مثل زده‌ایم، باشد که پندگیرند'),
(4086, 39, 28, 'که قرآنی است عربی [/شیوا] بدون کژی و کاستی، باشد که پروا پیشه کنند'),
(4087, 39, 29, 'خداوند مثلی می‌زند از مردی [/برده‌ای‌] که چند شریک درباره او ستیزجو و ناسازگارند و مردی [/برده‌ای‌] که [بی‌مدعی‌] ویژه یک مرد است، آیا این دو برابر و همانندند، سپاس خدای راست، ولی بیشترینه آنان نمی‌دانند'),
(4088, 39, 30, 'تو میرا هستی و آنان هم میرا هستند'),
(4089, 39, 31, 'سپس شما در روز قیامت در برابر پروردگارتان مجادله [و محاکمه‌] می‌کنید'),
(4090, 39, 32, 'پس کیست ستمکارتر از کسی که بر خداوند دروغ بندد، و دین راست و درست را چون بر او عرضه شد، تکذیب کند، آیا منزلگاه کافران در جهنم نیست؟'),
(4091, 39, 33, 'و آن کس که دین راست و درست را آورد و آن را باور داشت، اینانند که پرهیزگارند'),
(4092, 39, 34, 'برای آنان هر چه بخواهند در نزد پروردگارشان هست، این پاداش نیکوکاران است‌'),
(4093, 39, 35, 'تا خداوند بدترین کاری را که کرده‌اند از آنان بزداید، و پاداششان را بر وفق بهترین کاری که کرده‌اند، بپردازد'),
(4094, 39, 36, 'آیا خداوند بنده‌اش را بسنده نیست؟ و تو را از کسانی [/معبودان ناحقی‌] که به جای او می‌پرستیدند، می‌ترسانند، و کسی که خداوند بیراهش گذارده باشد، او را رهنمایی نیست‌'),
(4095, 39, 37, 'و هر کس که خداوند هدایتش کرده باشد، گمراه‌کننده‌ای ندارد، آیا خداوند پیروزمند دادستان نیست؟'),
(4096, 39, 38, 'و اگر از ایشان بپرسی که چه کسی آسمانها و زمین را آفریده است، بی‌شک گویند خداوند، بگو ملاحظه کنید که آنچه به جای خداوند می‌پرستید، اگر خداوند در حق من بلایی خواسته باشد، آیا آنان بلاگردانش هستند؟ یا اگر در حق من خیری خواسته باشد، آیا آنان بازدارنده رحمت او هستند؟ بگو خداوند مرا کافی است، که اهل توکل بر او توکل می‌کنند'),
(4097, 39, 39, 'بگو ای قوم من، هر چه توانید بکنید، من نیز کننده‌ام، پس زودا بدانید'),
(4098, 39, 40, 'که بر چه کسی عذابی فرود می‌آید که خوارش بدارد، و عذاب پاینده‌ای بر او نازل می‌گردد'),
(4099, 39, 41, 'ما کتاب [قرآن‌] را به حق برای مردم بر تو فرستادیم، پس هر کس که رهیاب شود، همانا به سود خویش رهیاب شده است، و هر کس بیراه می‌رود، همانا به زیان خویش بیراه رفته است، و تو نگهبان آنان نیستی‌'),
(4100, 39, 42, 'خداوند جانها را به هنگام مرگ آنها، و نیز آن را که نمرده است در خوابش، می‌گیرد، سپس آن را که مرگش را رقم زده است، نگاه می‌دارد، و دیگری را تا زمانی معین گسیل می‌دارد، بی‌گمان در این امر برای اندیشه‌وران مایه‌های عبرت است‌'),
(4101, 39, 43, 'یا آنکه [کافران‌] به جای خداوند شفیعانی برگرفته‌اند؟ بگو آیا اگر هم بر چیزی دست نداشته باشند و تعقل نکنند [باز هم آنان را شفیع می‌گیرند؟]'),
(4102, 39, 44, 'بگو شفاعت به تمامی خدای راست، فرمانروایی آسمانها و زمین او راست، آنگاه به سوی او باز گردانده می‌شوید'),
(4103, 39, 45, 'و چون خداوند به تنهایی یاد شود، دلهای نامؤمنان به آخرت تنگ شود، و چون کسانی که در برابر او به پرستش گرفته شده‌اند، یاد شوند، آنگاه است که شادمانی می‌کنند'),
(4104, 39, 46, 'بگو بار خدایا، ای پدید آورنده آسمانها و زمین، ای دانای پنهان و پیدا، تو در میان بندگانت، در آنچه اختلاف داشته‌اند، داوری می‌کنی‌'),
(4105, 39, 47, 'اگر هر آنچه در زمین است و همانند آن، از آن ستمکاران [مشرک‌] باشد، آن را در برابر سهمگینی عذاب در روز قیامت، بلا گردان کنند، و از سوی خداوند، چیزی که حسابش را نمی‌کردند، بر آنان آشکار شود'),
(4106, 39, 48, 'و کیفر آنچه کرده بودند، بر آنان آشکار شد و [و کیفر] آنچه به ریشخند گرفته بودند، آنان را فرو گرفت‌'),
(4107, 39, 49, 'و چون به انسان بلایی رسد، ما را به دعا بخواند، سپس چون از جانب خود به او نعمتی ارزانی داریم، گوید همانا به خاطر علم‌[ی که داشته‌ام‌] آن را به من داده‌اند، حق این است که آن آزمونی است، ولی بیشترینه آنان نمی‌دانند'),
(4108, 39, 50, 'به راستی که پیشینیانشان هم همین سخن را گفتند، و آنچه به دست آورده بودند، به دادشان نرسید'),
(4109, 39, 51, 'سپس کیفر آنچه انجام داده بودند، به آنان رسید، و کسانی از اینان که ستم کرده [/شرک‌ورزیده‌] بودند زودا که کیفر آنچه انجام داده بودند، به آنان برسد و آنان گزیر و گریزی ندارند'),
(4110, 39, 52, 'آیا ندانسته‌اند که خداوند روزی را برای هر کس که بخواهد گشاده یا فروبسته می‌دارد، بی‌گمان در این امر برای اهل ایمان مایه‌های عبرت است‌'),
(4111, 39, 53, 'بگو ای بندگانم که زیاده بر خویشتن ستم روا داشته‌اید، از رحمت الهی نومید مباشید، چرا که خداوند همه گناهان را می‌بخشد، که او آمرزگار مهربان است‌'),
(4112, 39, 54, 'و پیش از آنکه عذاب بر شما نازل گردد و سپس یاری نیابید، به سوی پروردگارتان بازآیید و در برابر او تسلیم پیشه کنید'),
(4113, 39, 55, 'همچنین پیش از آنکه عذاب به ناگهان بر سر شما فرود آید و شما ناآگاه باشید، از بهترین آنچه از سوی پروردگارتان به سوی شما نازل شده است، پیروی کنید'),
(4114, 39, 56, 'تا مبادا کسی بگوید واحسرتا در آنچه در کار خداوند فروگذار کردم، و به راستی که از ریشخندکنندگان [اسلام و قرآن‌] بودم‌'),
(4115, 39, 57, 'یا بگوید اگر خداوند مرا هدایت کرده بود، بی‌شک از پرهیزگاران بودم‌'),
(4116, 39, 58, 'یا چون عذاب را بنگرد، بگوید کاش مرا بازگشتی [به دنیا] بود، آنگاه از نیکوکاران می‌شدم‌'),
(4117, 39, 59, 'حق این است که آیات من به سوی تو آمد و تو آنها را دروغ شمردی و سرکشی کردی و از کافران بودی‌'),
(4118, 39, 60, 'و روز قیامت کسانی را که بر خداوند دروغ بسته‌اند، بینی که چهره‌هایشان سیاه [شده‌] است [به آنان گویند] آیا منزلگاه متکبران در جهنم نیست؟'),
(4119, 39, 61, 'و خداوند کسانی را که پرهیزگاری ورزیده‌اند به رستگاری‌شان برهاند، چنانکه عذابی به آنان نرسد و اندوهگین هم نشوند'),
(4120, 39, 62, 'خداوند آفریدگار همه چیز است، و او بر هر چیزی نگهبان است‌'),
(4121, 39, 63, 'او راست کلیدهای آسمانها و زمین، کسانی که آیات ما را انکار کرده‌اند، آنانند که زیانکارند'),
(4122, 39, 64, 'بگو ای نادانان آیا فرمانم می‌دهید که غیر از خدا را بپرستم؟'),
(4123, 39, 65, 'و به راستی بر تو و بر کسانی که پیش از تو بوده‌اند، وحی شده است که اگر شرک ورزی، عملت تباه گردد، و بی‌شک از زیانکاران باشی‌'),
(4124, 39, 66, 'بلکه خداوند را بپرست و از سپاسگزاران باش‌'),
(4125, 39, 67, 'و خداوند را چنانکه سزاوار قدر اوست ارج ننهادند، حال آنکه سراسر زمین در روز قیامت در قبضه قدرت اوست، و آسمانها به دست او درهم نوردیده می‌گردد، منزه است او و فراتر است از آنچه برای او شریک می‌دانند'),
(4126, 39, 68, 'و در صور دمیده شود، سپس هر کس که در آسمانها و هر کس که در زمین است، بیهوش شود، مگر آنکه خدا خواهد، سپس بار دیگر در آن دمیده شود، آنگاه ایشان [انسانها] ایستادگانی چشم به راهند'),
(4127, 39, 69, 'و سراسر عرصه محشر به نور پروردگارش درخشان گردد و نامه اعمال در میان نهند و پیامبران و گواهان را به میان آورند و بین مردم به حق داوری شود و بر آنان ستم نرود'),
(4128, 39, 70, 'و به هر کس [جزای‌] آنچه کرده است به تمامی داده شود، و او [خداوند] به آنچه کرده‌اند داناتر است‌'),
(4129, 39, 71, 'و کافران را گروه گروه به سوی جهنم برانند، چون به نزدیک آن رسند، درهایش بازگردد، و نگهبانان آن به ایشان گویند آیا پیامبران پروردگارتان به نزد شما نیامدند که بر شما آیات پروردگارتان را بخوانند، و شما را از دیدار این روزتان هشدار دهند، گویند چرا، ولی حکم عذاب بر کافران تحقق یافته است‌'),
(4130, 39, 72, 'گفته شود از درهای جهنم وارد شوید که جاودانه در آنید، چه بد است منزلگاه متکبران‌'),
(4131, 39, 73, 'و کسانی را که از پروردگارشان پروا کرده‌اند، گروه گروه به سوی بهشت برانند، تا به نزدیک آن رسند، در حالی که درهایش گشوده است، و نگهبانان آن به ایشان گویند سلام بر شما خوش آمدید به آن وارد شوید و جاودانه بمانید'),
(4132, 39, 74, 'و گویند سپاس خداوندی را که وعده خود را در حق ما راست گردانید و به ما سرزمین [بهشت‌] را به میراث داد که از بهشت هر جا که خواهیم سکنا کنیم، پس چه نیکوست پاداش عملداران‌'),
(4133, 39, 75, 'و فرشتگان را بینی که عرش را در میان گرفته‌اند، سپاسگزارانه پروردگارشان را تسبیح می‌گویند، و در میان آنان به حق داوری شود، و گفته شود سپاس خداوند را که پروردگار جهانیان است است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حم [حامیم‌]'),
(4135, 40, 2, 'کتابی است فرو فرستاده از سوی خداوند پیروزمند دانا'),
(4136, 40, 3, 'آمرزنده گناه و توبه‌پذیر سخت کیفر نعمت بخش، که خدایی جز او نیست، و سیر و سرانجام به سوی اوست‌'),
(4137, 40, 4, 'و جز کافران کسی در آیات الهی مجادله نکند، پس گشت و گذار آنان در شهرها تو را مفریبد'),
(4138, 40, 5, 'بدین‌سان پیش از آنان قوم نوح و پس از آنان گروههای مشرک نیز تکذیب [پیامبران الهی را] پیشه کردند، و هر امتی قصد پیامبرشان را کردند که او را فرو گیرند، و به دستاویز باطل مجادله کردند که حق را با آن ابطال کنند، آنگاه ایشان را فرو گرفتم، پس بنگر که عقوبت من چگونه بوده است‌'),
(4139, 40, 6, 'و بدین‌سان حکم پروردگارت بر کافران تحقق یافت که ایشان دوزخی‌اند'),
(4140, 40, 7, 'کسانی که عرش [الهی‌] را حمل می‌کنند، و اطرافیان آن سپاسگزارانه پروردگارشان را تسبیح می‌گویند و به او ایمان دارند و برای مؤمنان آمرزش می‌خواهند [و می‌گویند] پروردگارا رحمت و علم تو همه چیز را فراگرفته است، پس کسانی را که توبه کرده‌اند و راه تو را در پیش گرفته‌اند، بیامرز، و از عذاب دوزخ در امانشان بدار'),
(4141, 40, 8, 'پروردگارا و ایشان را به بهشتهای عدن وارد کن، که آن را به آنان و هر کس از پدرانشان و همسرانشان و زاد و رودشان که شایسته باشد، وعده داده‌ای، که تو پیروزمند فرزانه‌ای‌'),
(4142, 40, 9, 'و ایشان را از عقوبتها[ی اخروی‌] در امان بدار و هر کس را که در چنین روزی از عقوبتها در امان بداری، به راستی که بر او رحمت آورده‌ای، و این همان رستگاری بزرگ است‌'),
(4143, 40, 10, 'به کافران ندا در دهند که نفرت خداوند از شما، بزرگتر از نفرت شما از خویشتن است، بدانگاه که به سوی ایمان دعوت می‌شدید و انکار و کفر می‌ورزیدید'),
(4144, 40, 11, 'گویند پروردگارا ما را دوبار میراندی و دوبار زنده کردی، ما به گناهانمان اعتراف کرده‌ایم، پس آیا برای بیرون رفتن [از اینجا] راهی هست؟'),
(4145, 40, 12, 'این از آن است که چون خداوند به تنهایی خوانده می‌شد، کفر می‌ورزیدید و چون به او شرک‌ورزیده می‌شد، ایمان می‌آوردید، حال داوری با خداوند بلندمرتبه بزرگ است‌'),
(4146, 40, 13, 'اوست که آیاتش را به شما می‌نمایاند و برای شما از آسمان روزی‌ای فرو می‌فرستد، و جز کسانی که رو به توبه آورده باشند، کسی پند نمی‌گیرد'),
(4147, 40, 14, 'پس خداوند را -در حالی که دین خود را برای او پاک و پیراسته می‌داریدبخوانید هر چند که کافران ناخوش داشته باشند'),
(4148, 40, 15, 'او برافرازنده درجات [و] صاحب عرش است، و وحی را به فرمان خویش بر هر کس از بندگانش که بخواهد فرو می‌فرستد، تا از روز همدیداری هشدار دهد'),
(4149, 40, 16, 'روزی که ایشان [سراپا] آشکار باشند و از آنان چیزی بر خداوند پوشیده نباشد [ندا آید] امروز فرمانروایی از آن کیست؟ [پاسخ آید] از آن خداوند یگانه قهار است‌'),
(4150, 40, 17, 'امروز هر کسی بر وفق کار و کردارش جزا یابد، امروز ستمی [بر کسی‌] نرود، بی‌گمان خداوند زود شمار است‌'),
(4151, 40, 18, 'و ایشان را از روز قیامت [/بس نزدیک‌] بیم ده، آنگاه که جانها به گلوگاهها رسد، و غصه خویش فرو برند، برای ستمکاران [مشرک‌] دوستی و شفیعی که اجازه و اجابت یابد، نیست‌'),
(4152, 40, 19, '[خداوند] خیانت چشمها و آنچه دلها پنهان می‌دارد، می‌داند'),
(4153, 40, 20, 'و خداوند به حق حکم می‌راند و معبودانی که به جای او به پرستش گرفته می‌شوند، حکمی نمی‌رانند، بی‌گمان خداوند شنوای بیناست‌'),
(4154, 40, 21, 'آیا در زمین سیر و سفر نکرده‌اند که بنگرند سرانجام کسانی که پیش از ایشان بودند چگونه بوده است آنان از ایشان در روی زمین پرتوان‌تر و پراثرتر بوده‌اند، که خداوند آنان را به گناهانشان فروگرفت، و در برابر خداوند نگهدارنده‌ای نداشتند'),
(4155, 40, 22, 'این از آن بود که پیامبرانشان برای ایشان معجزات می‌آوردند، ولی ایشان انکار کردند، آنگاه خداوند فروگرفتشان که او توانای سخت کیفر است‌'),
(4156, 40, 23, 'و به راستی موسی را همراه با آیات خویش و حجتی آشکار فرستادیم‌'),
(4157, 40, 24, 'به سوی فرعون و هامان و قارون، آنگاه گفتند او جادوگری دروغ‌زن است‌'),
(4158, 40, 25, 'و چون برای آنان حق را از سوی ما آورد گفتند پسران کسانی را که همراه با او ایمان آورده‌اند، بکشید و [دختران و] زنانشان را زنده بگذارید، و نیرنگ کافران جز در تباهی نیست‌'),
(4159, 40, 26, 'و فرعون گفت مرا بگذارید تا موسی را بکشم و او پروردگارش را بخواند، چه می‌ترسم دین شما را تغییر دهد، یا در این سرزمین فتنه و فساد آشکار کند'),
(4160, 40, 27, 'و موسی گفت من به پروردگار خود و پروردگار شما از [شر] هر متکبری که به روز حساب ایمان ندارد، پناه می‌برم‌'),
(4161, 40, 28, 'و مردی مؤمن از آل فرعون -که ایمانش را پنهان می‌داشتگفت آیا می‌خواهید مردی را بکشید به خاطر اینکه می‌گوید پروردگار من خداوند است؟ و برای شما معجزاتی از سوی پروردگارتان آورده است، و اگر دروغگو باشد زیان دروغش بر اوست، و اگر راستگو باشد بخشی از آنچه به شما وعده می‌دهد به شما خواهد رسید، بی‌گمان خداوند کسی را که گزافکار و دروغ‌زن است، هدایت نمی‌کند'),
(4162, 40, 29, 'ای قوم من، امروز فرمانروایی از آن شماست، و در این سرزمین چیره‌اید، ولی چه کسی ما را در برابر عذاب الهی -اگر بر سرمان بیایدیاری خواهد داد؟ فرعون گفت به صلاح شما نمی‌دانم جز چیزی را که خود صلاح بدانم، و شما را جز به راه رشد و راستی هدایت نمی‌کنم‌'),
(4163, 40, 30, 'و همان کسی که [پنهانی‌] ایمان آورده بود، گفت ای قوم من، من بر شما از چیزی همانند روزگار [سخت‌] گروههای مشرک بیمناکم‌'),
(4164, 40, 31, 'مانند حال و روز قوم نوح و عاد و ثمود و کسانی که پس از ایشان بودند، و خداوند در حق بندگان ستمی نمی‌خواهد'),
(4165, 40, 32, 'و ای قوم من، من بر شما از روز فریاد خوانی بیمناکم‌'),
(4166, 40, 33, 'روزی که پشت کنید، برای شما در برابر خداوند پناهی نیست، و هر کس که خداوند بیراهش گذارد، رهنمایی ندارد'),
(4167, 40, 34, 'و پیشتر یوسف [پیامبر] برای شما معجزاتی آورد و همچنان از آنچه برایتان آورده بود در شک بودید، تا آنکه چون درگذشت، گفتید خداوند هرگز پس از او پیامبری برنخواهد انگیخت، بدین‌سان خداوند کسی را که گزافکار شک‌گراست بیراه می‌دارد'),
(4168, 40, 35, 'کسانی که در آیات الهی بدون حجتی که برایشان آمده باشد، مجادله می‌کنند، نزد خداوند و نزد مؤمنان بس ناپسند است، بدین‌سان خداوند بر هر قلب متکبر زورگویی مهر می‌نهد'),
(4169, 40, 36, 'و فرعون گفت ای هامان برای من برجی [بلند] برآور باشد که به این راهها برسم‌'),
(4170, 40, 37, 'راههای آسمان، تا به خدای موسی پی ببرم، و من او را دروغگو می‌دانم، و بدین‌سان در نظر فرعون بد کرداری‌اش آراسته شد، و از راه [صواب‌] باز داشته شد، و نیرنگ فرعون جز در تباهی نبود'),
(4171, 40, 38, 'و کسی که ایمان آورده بود، گفت ای قوم من، از من پیروی کنید تا شما را به راه رشد و راستی هدایت کنم‌'),
(4172, 40, 39, 'ای قوم من، همانا این زندگانی دنیا، بهره‌ای [اندک‌] است، و آخرت سرای اقامت است‌'),
(4173, 40, 40, 'هر کس کار ناپسندی مرتکب شود، جز به مانند آن جزا نیابد، و هر کس کاری شایسته انجام دهد، اعم از مرد یا زن، و مؤمن باشد، اینانند که وارد بهشت می‌شوند، و در آنجا بی‌حساب روزی می‌یابند'),
(4174, 40, 41, 'و ای قوم من، مرا چه می‌شود که شما را به رهایی می‌خوانم، و حال آنکه شما مرا به آتش دوزخ دعوت می‌کنید'),
(4175, 40, 42, 'مرا به این دعوت می‌کنید که به خداوند کفر بورزم، و چیزی را که به آن علم ندارم، شریک او بگیرم، و حال آنکه من شما را به سوی [خداوند] پیروزمند آمرزگار می‌خوانم‌'),
(4176, 40, 43, 'حقا که آنچه مرا به آن می‌خوانید، در دنیا و آخرت صاحب دعوتی نیست، و سرانجام بازگشتمان به سوی خداوند است و گزافکاران دوزخی‌اند'),
(4177, 40, 44, 'و زود باشد که آنچه [اینک‌] به شما می‌گویم به یاد آورید، و کارم را به خداوند واگذار می‌کنم، چرا که خداوند به احوال بندگانش بیناست‌'),
(4178, 40, 45, 'و خداوند او را از عواقب سوء نیرنگی که می‌ورزیدند، در امان داشت، و عذاب سهمگین آل فرعون را فرو گرفت‌'),
(4179, 40, 46, '[یعنی‌] آتش دوزخ که بامدادان و شامگاهان ایشان را بر آن عرضه دارند و روزی که قیامت بر پا شود [گویند] آل فرعون را به [جایگاه‌] سهمگین‌ترین عذاب وارد کنید'),
(4180, 40, 47, 'و آنگاه که در آتش [دوزخ‌] با یکدیگر بگو مگو می‌کنند، ناتوانان به مستکبران می‌گویند ما پیرو شما بودیم، پس آیا شما باز دارنده بخشی از آتش [دوزخ‌] از ما هستید؟'),
(4181, 40, 48, 'مستکبران گویند همه ما در آن هستیم، به راستی که خداوند در میان بندگان داوری کرده است‌'),
(4182, 40, 49, 'و دوزخیان به نگهبانان گویند از پروردگارتان بخواهید که روزی از [روزهای‌] عذاب ما را کاهش دهد'),
(4183, 40, 50, 'گویند آیا چنین نبود که پیامبرانتان معجزاتی برایتان آوردند؟ گفتند چرا [نگهبانان‌] گویند پس دعا کنید، و دعای کافران جز در تباهی نیست‌'),
(4184, 40, 51, 'ما پیامبرانمان و مؤمنان را در زندگانی دنیا و روزی که شاهدان به شهادت برخیزند، یاری می‌کنیم‌'),
(4185, 40, 52, 'روزی که برای ستمکاران [مشرک‌] عذرخواهیشان سود ندهد، و لعنت و بد فرجامی نصیب آنان باشد'),
(4186, 40, 53, 'و به راستی به موسی رهنمود بخشیدیم و به بنی‌اسرائیل کتاب آسمانی به میراث دادیم‌'),
(4187, 40, 54, 'که رهنمود و پندآموز خردمندان است‌'),
(4188, 40, 55, 'پس شکیبایی پیشه کن، که وعده الهی حق است و برای گناهت آمرزش بخواه و شامگاهان و بامدادان سپاسگزارانه پروردگارت را تسبیح گوی‌'),
(4189, 40, 56, 'کسانی که بدون حجتی که بر ایشان آمده باشد، در آیات الهی مجادله می‌کنند، در دلهایشان جز خود بزرگ‌بینی نیست، که به آن نایل نشوند، پس بر خداوند پناه ببر، که او شنوای بیناست‌'),
(4190, 40, 57, 'بی‌گمان آفرینش آسمانها و زمین از آفرینش انسانها بزرگتر است، ولی بیشترینه مردم نمی‌دانند'),
(4191, 40, 58, 'و نابینا و بینا برابر نیستند، همچنین کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند و بدکرداران هم [برابر نیستند]، چه اندک پند می‌گیرید'),
(4192, 40, 59, 'بی‌گمان قیامت آمدنی است و شکی در آن نیست، ولی بیشترینه مردم باور نمی‌دارند'),
(4193, 40, 60, 'و پروردگارت گوید مرا به دعا بخوانید تا برایتان اجابت کنم، بی‌گمان کسانی که از عبادت من استکبار می‌ورزند، زودا که به خواری و زاری وارد دوزخ شوند'),
(4194, 40, 61, 'خداوند کسی است که شب را برای شما آفرید تا در آن آرام گیرید و روز را چشم‌اندازی روشن ساخت، بی‌گمان خداوند بر مردمان بخشش و بخشایش دارد، ولی بیشترینه مردم سپاس نمی‌گزارند'),
(4195, 40, 62, 'چنین است خداوند، پروردگارتان، که آفریننده همه چیز است، خدایی جز او نیست، پس چگونه به بیراهه می‌روید؟'),
(4196, 40, 63, 'بدین‌سان کسانی که به آیات الهی انکار ورزیدند، به بیراهه افتادند'),
(4197, 40, 64, 'خداوند کسی است که زمین را آرامشگاه و آسمان را سرپناه شما قرار داد، و شما را نقشبندی کرد و شکلهای شما را نیکو پرداخت و شما را از پاکیزه‌ها روزی داد، چنین است خداوند، پروردگارتان، بزرگا خداوندی که پروردگار جهانیان است‌'),
(4198, 40, 65, 'اوست زنده که خدایی جز او نیست، پس او را -در حالی که دین خود را برای او پیراسته می‌دارید بخوانید، سپاس خداوند را که پروردگار جهانیان است‌'),
(4199, 40, 66, 'بگو من بازداشته شده‌ام از اینکه کسانی را که شما به جای خداوند می‌پرستید، بپرستم، آن هم هنگامی که روشنگریهایی برای من از جانب پروردگارم آمده است، و دستور یافته‌ام که در برابر پروردگار جهانیان، تسلیم پیشه کنم‌'),
(4200, 40, 67, 'او کسی است که شما را [ابتدا] از خاک، سپس از نطفه، سپس از خون بسته آفرید، سپس شما را به هیئت نوزادی [از رحمها] بیرون آورد، تا به کمال رشدتان برسید، سپس تا پیر شوید، و بعضی از شما پیشاپیش جانش گرفته می‌شود، و تا به سرآمدی معین برسید، و باشد که تعقل کنید'),
(4201, 40, 68, 'او کسی است که زنده می‌دارد و می‌میراند و چون امری را اراده کند، فقط به آن می‌گوید موجود شو، و بی‌درنگ موجود می‌شود'),
(4202, 40, 69, 'آیا کسانی را که در آیات الهی مجادله می‌کنند، ندیده‌ای که چگونه بیراهه می‌روند'),
(4203, 40, 70, 'کسانی که کتاب آسمانی و آنچه پیامبرانمان را برای آن فرستاده‌ایم، تکذیب می‌کنند، زودا که [نتیجه و حقیقت را] بدانند'),
(4204, 40, 71, 'آنگاه که غلها در گردنهایشان است، و به زنجیرها در آب گرم کشیده می‌شوند'),
(4205, 40, 72, 'سپس در آتش [دوزخ‌] سوخته می‌شوند'),
(4206, 40, 73, 'سپس به آنان گویند آنچه در برابر خداوند شریک می‌آوردید، کجاست؟'),
(4207, 40, 74, 'گویند از دید ما گم شدند، بلکه پیشتر هم چیزی را به پرستش نمی‌خواندیم، بدین‌سان خداوند کافران را بیراه گذارد'),
(4208, 40, 75, 'این از آن است که در روی زمین به ناحق شادی می‌کردید و از آن است که فخر می‌فروختید'),
(4209, 40, 76, 'از دروازه‌های جهنم وارد شوید، که جاودانه در آنید، و جایگاه متکبران چه بد است‌'),
(4210, 40, 77, 'پس شکیبایی پیشه کن، که وعده الهی حق است، اگر بخشی از آنچه به ایشان وعده داده‌ایم به تو بنمایانیم، یا جان تو را [پیش از آن‌] بگیریم، در هر صورت به سوی ما بازگردانده می‌شوند'),
(4211, 40, 78, 'و به راستی پیش از تو پیامبرانی فرستادیم که از بعضی از آنان با تو سخن گفته‌ایم، و از بعضی از آنان با تو سخن نگفته‌ایم، و هیچ پیامبری را نرسد که جز به اذن الهی معجزه‌ای بیاورد، پس چون امر الهی فرارسد، به حق انجام گیرد و باطل‌اندیشان در اینجا زیانکار شوند'),
(4212, 40, 79, 'خداوند است که برای شما چارپایانی آفریده است که بر برخی از آنها سوار شوید و از برخی از آنها بخورید'),
(4213, 40, 80, 'و برای شما در آنها سودهایی هست و نیز برای آنکه سوار بر آنها به مقصدی که در دل دارید برسید، و بر آنها و بر کشتیها [به هر جا که خواهید] منتقل می‌شوید'),
(4214, 40, 81, 'و به شما آیات خویش را می‌نمایاند، پس کدام یک از آیات الهی را انکار می‌کنید؟'),
(4215, 40, 82, 'آیا در زمین سیر و سفر نکرده‌اند که بنگرند سرانجام کسانی که پیش از آنان بوده‌اند چگونه بوده است آنان از ایشان پرشمارتر و پرتوان‌تر و پراثرتر بوده‌اند، اما آنچه به دست آوردند به کارشان نیامد'),
(4216, 40, 83, 'آنگاه که پیامبرانشان برای آنان پدیده‌های روشنگر آوردند، از آن مقدار علمی که داشتند، شادمانی کردند و [تبعات‌] آنچه ریشخندش می‌کردند آنان را فرو گرفت‌'),
(4217, 40, 84, 'و چون عذاب ما را دیدند گفتند تنها به خداوند ایمان آوردیم و به آنچه شرک آورده بودیم، اینک کافریم‌'),
(4218, 40, 85, 'ولی هنگامی که عذاب ما را دیدند، دیگر ایمانشان برایشان سودی نداشت، این سنت الهی است که در میان بندگانش جاری بوده است، و در اینجا کافران زیانکار شدند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حم [حا میم‌]'),
(4220, 41, 2, '[کتابی است‌] فرو فرستاده از جانب [خداوند] رحمان رحیم‌'),
(4221, 41, 3, 'کتابی است که آیاتش به شیوایی بیان شده است، قرآنی عربی برای اهل معرفت‌'),
(4222, 41, 4, 'که مژده‌آور و هشداردهنده است، ولی بیشترینه آنان روی گردان شدند که به گوش [دل‌] نمی‌شنوند'),
(4223, 41, 5, 'و گویند دلهای ما از آنچه ما را به آن می‌خوانید در پوشش است و در گوشهایمان سنگینی‌ای، و میان ما و تو حجابی است، هر چه خواهی کن که ما نیز کننده‌ایم‌'),
(4224, 41, 6, 'بگو جز این نیست که من بشری مانند شما هستم [با این تفاوت‌] که به من وحی می‌شود که خدای شما خدای یگانه است، در کار او راست و درست باشید و از او آمرزش بخواهید، و وای بر مشرکان‌'),
(4225, 41, 7, 'کسانی که زکات نمی‌پردازند و هم ایشان آخرت را منکرند'),
(4226, 41, 8, 'بی‌گمان کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند پاداشی ناکاسته [/بی‌منت‌] دارند'),
(4227, 41, 9, 'بگو آیا شما به کسی که زمین را در دو روز آفریده است، کفر می‌ورزید و برای او همتایانی قائل می‌شوید، اوست که پروردگار جهانیان است‌'),
(4228, 41, 10, 'و بر روی آن [زمین‌]، کوههای استوار آفرید و به آن برکت بخشید و در چهار روز زاد و برگ آن را در آن آماده ساخت، که برای خواهندگان یکسان است‌'),
(4229, 41, 11, 'سپس به آسمان پرداخت که به صورت دودی [/بخاری‌] بود، به آن و به زمین فرمود خواه یا ناخواه رام شوید، [به زبان حال‌] گفتند البته رام و تسلیم هستیم‌'),
(4230, 41, 12, 'آنگاه آنها را به صورت آسمانهای هفت‌گانه در دو روز استوار کرد و در هر آسمانی امرش را وحی کرد، و آسمان دنیا را به چراغها[ی ستارگان‌] آراستیم و آن را محفوظ داشتیم، این اندازه آفرینی [خداوند] پیروزمند داناست‌'),
(4231, 41, 13, 'و اگر رویگردان شدند بگو شما را از صاعقه‌ای مانند صاعقه عاد و ثمود هشدار می‌دهم‌'),
(4232, 41, 14, 'چنین بود که پیامبران آنان پیش از آنان و پس از آنان آمدند که جز خداوند را مپرستید، گفتند اگر پروردگارمان می‌خواست فرشتگانی می‌فرستاد، پس ما رسالت شما را منکریم‌'),
(4233, 41, 15, 'اما قوم عاد در آن سرزمین به ناحق سرکشی کردند و گفتند چه کسی از ما پرتوان‌تر است؟ آیا ننگریسته‌اند که خداوندی که آنان را آفریده است، از آنها پرتوان‌تر است؟ [این چنین بود که‌] آیات ما را انکار می‌کردند'),
(4234, 41, 16, 'آنگاه بر ایشان بادی سخت سرد، در روزهای شوم، فرستادیم که به آنان عذاب رسواگر را در زندگانی دنیا بچشانیم و عذاب اخروی رسواگرتر است، و آنان یاری نیابند'),
(4235, 41, 17, 'و اما قوم ثمود هدایتشان کردیم، ولی سرگشتگی را از هدایت خوشتر داشتند، آنگاه به خاطر کار و کردارشان، صاعقه عذاب رسواگر آنان را فرو گرفت‌'),
(4236, 41, 18, 'و کسانی را که ایمان آورده و پروا پیشه کرده بودند رهاندیم‌'),
(4237, 41, 19, 'و روزی [باشد] که دشمنان خدا به سوی دوزخ گرد آیند، و به همدیگر فرارسند'),
(4238, 41, 20, 'تا چون به آنجا رسند، گوشها و چشمها و پوستهایشان درباره آنچه کرده‌اند، بر آنان گواهی دهند'),
(4239, 41, 21, 'به پوستهایشان گویند چرا بر ما گواهی دادید؟ گویند ما را خداوندی که هر چیز را به سخن درآورد، به سخن درآورده است، و او شما را نخست بار [که چیزی نبودید] آفرید، و به سوی او باز گردانده می‌شوید'),
(4240, 41, 22, 'و شما پرده‌پوشی نمی‌کردید از اینکه مبادا گوشهایتان و چشمهایتان و پوستهایتان بر شما گواهی دهند، [بلکه از این روی بود که‌] گمان می‌کردید که خداوند بسیاری از کار و کردارتان را نمی‌داند'),
(4241, 41, 23, 'و این گمان شما بود که در حق پروردگارتان می‌پنداشتید که شما را هلاک کرد و از زیانکاران شدید'),
(4242, 41, 24, 'پس اگر شکیبایی‌ورزند، آتش [دوزخ‌] جایگاه آنان است، و اگر بخشایش طلبند، از بخشودگان نیستند'),
(4243, 41, 25, 'و برای آنان همنشینانی گماشتیم که حال و آینده‌شان را در نظر ایشان آراسته جلوه دادند، و در میان امتهایی از جن و انس که پیش از ایشان بوده‌اند، حکم [عذاب‌] در حق ایشان تحقق یافت که ایشان زیانکار بودند'),
(4244, 41, 26, 'و کافران گویند به این قرآن گوش مدهید و در اثنای خواندن آن سخنان بیهوده بگویید، باشد که پیروز شوید'),
(4245, 41, 27, 'پس به کافران عذابی سخت بچشانیم، و بر وفق بدترین کاری که کرده‌اند ایشان را جزا دهیم‌'),
(4246, 41, 28, 'چنین است که جزای دشمنان خدا آتش [دوزخ‌] است، که در آنجا سرایی جاودانه داشته باشند، که کیفر آن است که به آیات ما انکار می‌ورزیدند'),
(4247, 41, 29, 'و کافران گویند پروردگارا کسانی را از جن و انس که ما را گمراه کردند، به ما نشان بده که به زیر گامهایمان در اندازیمشان که از فروماندگان باشند'),
(4248, 41, 30, 'بی‌گمان کسانی که گویند پروردگار ما خداوند است، سپس پایداری ورزند، فرشتگان بر آنان نازل شوند [و گویند] که مترسید و اندوهگین مباشید و مژده باد شما را به بهشتی که به شما وعده داده بودند'),
(4249, 41, 31, 'ما دوستداران شما در زندگانی دنیا و در آخرت هستیم، و در آنجا برای شما هر چه دلهایتان بخواهد و هر آنچه بطلبید هست‌'),
(4250, 41, 32, 'که پیشکشی از [خداوند] آمرزگار مهربان است‌'),
(4251, 41, 33, 'و کیست نیکوسخن‌تر از کسی که به سوی خداوند دعوت کند، و کاری شایسته در پیش گیرد و بگوید که من از مسلمانانم‌'),
(4252, 41, 34, 'و نیکی و بدی برابر نیست همواره به شیوه‌ای که نیکوتر است مجادله کن، آنگاه [خواهی دید] کسی که بین تو و او دشمنی‌ای بود، گویی دوستی مهربان است‌'),
(4253, 41, 35, 'و آن را جز شکیبایان نپذیرند، و آن را جز بختیار فرانگیرد'),
(4254, 41, 36, 'و اگر وسوسه‌ای از سوی شیطان تو را به وسواس افکند، به خداوند پناه ببر، چرا که او شنوای داناست‌'),
(4255, 41, 37, 'و از آیات او شب و روز و خورشید و ماه است، در برابر خورشید و ماه سجده نکنید، بلکه در برابر خداوندی که آنها را آفریده است -اگر تنها او را می‌پرستیدسجده کنید'),
(4256, 41, 38, 'و اگر تکبر ورزیدند، بدان که کسانی که نزد پروردگارت هستند، در شب و روز او را تسبیح می‌گویند و ایشان ملول نمی‌شوند'),
(4257, 41, 39, 'و از آیات او این است که تو زمین را پژمرده بینی، آنگاه چون بر آن، آب [باران‌] فرو فرستیم، جنبش یابد و رشد کند، بی‌گمان کسی که آن را زنده گردانده است، زندگی‌بخش مردگان است، او بر هر کاری تواناست‌'),
(4258, 41, 40, 'بی‌گمان کسانی که در آیات ما کژاندیشی می‌کنند، از ما پوشیده و پنهان نیستند، آیا [سرنوشت‌] کسی که در آتش [دوزخ‌] افکنده شود، بهتر است، یا کسی که در روز قیامت ایمن آید، هر چه می‌خواهید بکنید، او به آنچه می‌کنید بیناست‌'),
(4259, 41, 41, 'بی‌گمان کسانی که قرآن را -چون بر آنان نازل شدانکار می‌کنند [از ما پوشیده و پنهان نیستند]، و آن کتابی است گرامی‌'),
(4260, 41, 42, 'که در اکنون یا آینده‌اش، باطل در آن راه نمی‌یابد، فرو فرستاده‌ای از سوی [خداوند] فرزانه ستوده است‌'),
(4261, 41, 43, 'به تو چیزی گفته نمی‌شود، جز آنچه به پیامبران پیش از تو گفته شده است بی‌گمان پروردگارت هم صاحب آمرزش و هم صاحب عقوبتی دردناک است‌'),
(4262, 41, 44, 'و اگر آن را به صورت قرآنی بیگانه و ناشیوا پدید می‌آوردیم، بی‌شک می‌گفتند چرا آیات آن شیوا بیان نشده است، چرا آن بیگانه و ناشیواست، حال آنکه پیامبر عربی [و شیوا] است؟ بگو آن برای مؤمنان رهنمود و شفابخش است و کسانی که ایمان ندارند در گوشهایشان سنگینی‌ای هست و آن [قرآن‌] برایشان مایه سردرگمی است، اینانند که از جایی دور دست ندایشان می‌دهند'),
(4263, 41, 45, 'و به راستی به موسی کتاب آسمانی بخشیدیم، آنگاه درباره آن اختلاف کلمه پیدا شد، و اگر حکم پیشین پروردگارت تعلق نگرفته بود، هر آینه در میان آنان داوری می‌شد، و [اینک‌] آنان از آن سخت در شک هستند'),
(4264, 41, 46, 'و هرکس که کاری شایسته پیشه کند، به سود خود اوست، و هرکس کاری بد پیش گیرد، به زیان خود اوست، و پروردگارت در حق بندگان ستمگر نیست‌'),
(4265, 41, 47, 'آگاهی از قیامت به او باز می‌گردد و هیچ بار و بری از پوستش بر نمی‌آید، و هیچ مادینه‌ای بار بر نمی‌گیرد، و بار خود را به زمین نمی‌گذارد، مگر با آگاهی او، و روزی که ایشان را ندا دهد که پس شریکان من [که شما ادعا می‌کردید] کجا هستند؟ گویند تو را خبر دادیم که ما را هیچ شاهدی نیست‌'),
(4266, 41, 48, 'و آنچه در گذشته به پرستش می‌خواندند، از دید آنان ناپدید شود، و دریابند که گریزگاهی ندارند'),
(4267, 41, 49, 'انسان هرگز از طلب‌خیز به دعا خسته نمی‌شود، ولی چون شری به او رسد، بس نومید و دلسرد است‌'),
(4268, 41, 50, 'و اگر به او پس از رنجی که رسیده است، از سوی خود رحمتی بچشانیم، گوید این حق من است و گمان ندارم که قیامت برپا شود، و اگر هم به سوی پروردگارم بازگردانده شوم، همانا برای من در نزد او خوشی [/بهشت‌] خواهد بود، آنگاه کافران را از [نتیجه و حقیقت‌] کار و کردارشان آگاه سازیم و به آنان از عذاب سهمگین بچشانیم‌'),
(4269, 41, 51, 'و چون به انسان ناز و نعمتی ارزانی داریم [از سر نعمت زدگی و ناسپاسی‌] روی بگرداند و دامن کشان بگذرد، و چون بلایی به او رسد دعاخوانی پیگیر است‌'),
(4270, 41, 52, 'بگو اندیشه کنید اگر [قرآن‌] از سوی خدا باشد، و سپس شما منکرش شوید، دیگر چه کسی از کسی که چنین ناسازگاری عظیمی دارد، گمراه‌تر خواهد بود؟'),
(4271, 41, 53, 'زودا که آیات خود را در بیرون و درونشان به ایشان بنمایانیم، تا آنکه بر آنان آشکار شود که آن حق است، آیا کافی نیست که پروردگارت بر همه چیز گواه است‌'),
(4272, 41, 54, 'بدانید که ایشان از لقای پروردگارشان در شک‌اند، بدانید که او بر هر چیز چیره است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حم [حا میم‌]'),
(4274, 42, 2, 'عسق [عین سین قاف‌]'),
(4275, 42, 3, 'بدین‌سان خداوند پیروزمند فرزانه به سوی تو و کسانی که پیش از تو [پیامبر] بودند، وحی می‌فرستد'),
(4276, 42, 4, 'آنچه در آسمانها و آنچه در زمین است او راست، و او بلندمرتبه بزرگ است‌'),
(4277, 42, 5, 'نزدیک است که آسمانها [از ناروایی بهتان آنان‌] بر فرازشان پاره پاره شود، و فرشتگان سپاسگزارانه پروردگارشان را تسبیح می‌گویند و برای زمینیان آمرزش می‌خواهند، هان همانا خداوند است که آمرزگار مهربان است‌'),
(4278, 42, 6, 'و کسانی که به جای او سرورانی را به پرستش می‌گیرند، خداوند حاکم بر آنهاست، و تو نگهبان آنان نیستی‌'),
(4279, 42, 7, 'و بدین‌سان قرآنی عربی [/شیوا] بر تو وحی کردیم، تا [اهل‌] مکه و پیرامونیان آن را هشدار دهی، و از روز محشر که شکی در آن نیست، هشدار دهی، که گروهی در بهشتند و گروهی در دوزخ‌'),
(4280, 42, 8, 'و اگر خداوند می‌خواست آنان را امت یگانه‌ای قرار می‌داد، ولی هرکس را که بخواهد در جوار رحمتش در می‌آورد، و ستمکاران [مشرک‌] یار و یاوری ندارند'),
(4281, 42, 9, 'یا به جای او سرورانی را به پرستش گرفته‌اند، حال آنکه سرور [راستین‌] خداوند است و اوست که مردگان را زندگی می‌بخشد و او بر هر کاری تواناست‌'),
(4282, 42, 10, 'و در هر آنچه اختلاف نظر پیدا کنید، حکمش با خداوند است، چنین است خداوند، پروردگار من که بر او توکل کرده‌ام و به درگاهش روی آورده‌ام‌'),
(4283, 42, 11, 'پدید آورنده آسمانها و زمین که برای شما از نوع خودتان همتایانی آفریده است، و از چارپایان نیز انواعی [قرار داده است‌]، و شما را در آن آفریده است، همانند او چیزی نیست و اوست که شنوای بیناست‌'),
(4284, 42, 12, 'او راست کلیدهای آسمانها و زمین، روزی را برای هرکس که بخواهد گشاده یا فروبسته می‌دارد، که او به هر کاری داناست‌'),
(4285, 42, 13, 'در دین شما، هر آنچه به نوح سفارش کرده بود، مقرر داشت، و نیز آنچه به تو وحی کرده‌ایم، و آنچه به ابراهیم و موسی و عیسی سفارش کرده‌ایم، که دین را برپا بدارید، و در آن اختلاف نورزید، آنچه مشرکان را به آن می‌خوانی، بر ایشان دشوار آید، خداوند است که هرکس را بخواهد به راه خویش برمی‌گزیند و هرکس را که روی به درگاه او آورد، به سوی خویش هدایت می‌کند'),
(4286, 42, 14, 'و تفرقه پیدا نکردند مگر پس از آنکه علم [وحی‌] برایشان آمد، آن هم از روی رشک و رقابتی که در میانشان بود، و اگر حکم پیشین پروردگارت تا سرآمدی معین تعلق نگرفته بود، در میانشان داوری می‌شد، و کسانی که پس از ایشان کتاب را فراگرفتند از آن سخت در شک‌اند'),
(4287, 42, 15, 'پس برای این [امر] دعوت کن، و چنانکه دستور یافته‌ای پایداری کن، و از هوی و هوس آنان پیروی مکن و بگو به هر کتابی که خداوند فرو فرستاده است، ایمان آورده‌ام، و دستور یافته‌ام که در میان شما دادگری کنم، خداوند پروردگار ما و پروردگار شماست، کار و کردار ما از آن ماست، و کار و کردار شما از آن شما، در میان ما و شما گفت و گویی نیست، خداوند ما و شما را [برای داوری‌] گرد می‌آورد و سیر و سرانجام به سوی اوست‌'),
(4288, 42, 16, 'و کسانی که پس از پذیرفته شدن دعوت او درباره خداوند محاجه می‌کنند، احتجاجشان در نزد پروردگارشان باطل است، و بر آنان خشمی [از جانب خداوند] است، و عذابی سهمگین [در پیش‌] دارند'),
(4289, 42, 17, 'خداوند کسی است که کتاب آسمانی و سنجه را به حق نازل کرده است و چه دانی چه بسا قیامت نزدیک باشد'),
(4290, 42, 18, 'کسانی که آن را باور ندارند درباره آن شتاب می‌ورزند، و کسانی که ایمان آورده‌اند، از آن ترسانند، و می‌دانند که آن حق است، بدانید کسانی که درباره قیامت شک و شبهه دارند، در گمراهی دور و درازند'),
(4291, 42, 19, 'خداوند در کار بندگانش باریک‌بین است، هرکس را که بخواهد روزی می‌دهد و اوست توانای پیروزمند'),
(4292, 42, 20, 'هرکس که بهره کشت اخروی را خواسته باشد، برای او در کشت او می‌افزاییم، و هرکس بهره کشت دنیوی را خواسته باشد، از آن به او می‌بخشیم، و برای او در آخرت بهره‌ای نیست‌'),
(4293, 42, 21, 'یا مگر برای آنان شریکان [/معبودان ناحق‌] ی است که برای آنان احکامی دینی مقرر داشته است که خداوند آن را اجازه نداده است؟ و اگر حکم فیصله‌بخش [پیشین‌] نبود، در میان آنان داوری می‌شد، و برای ستمکاران [مشرک‌] عذابی دردناک [در پیش‌] است‌'),
(4294, 42, 22, '[آنگاه‌] ستمکاران [مشرک‌] را از کار و کردارشان هراسان بینی و [کیفر] آن به ایشان فرا می‌رسد، و کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، در سرابستانها باشند، آنچه بخواهند برای ایشان نزد پروردگارشان فراهم است، آن همان نعمت بزرگ است‌'),
(4295, 42, 23, 'این همان است که خداوند به بندگانش که ایمان آورده‌اند و کارهای شایسته کرده‌اند، مژده داده است، بگو برای آن کار از شما مزدی نمی‌طلبم، مگر دوستداری در حق نزدیکان [/اهل بیتم‌]، و هر کس کار نیکی کند، در آن برایش جزای نیک بیفزاییم، چرا که خداوند آمرزگار قدردان است‌'),
(4296, 42, 24, 'یا می‌گویند [پیامبر] بر خداوند دروغ بسته است، بدانید که اگر خداوند بخواهد بر دل تو مهر می‌گذارد، و خداوند باطل را می‌زداید، و با کلمات خویش [دین‌] حق را استوار می‌دارد، که او دانای راز دلهاست‌'),
(4297, 42, 25, 'و اوست که توبه بندگانش را می‌پذیرد و گناهان را می‌بخشد و می‌داند که چه می‌کنید'),
(4298, 42, 26, 'و [دعای‌] کسانی را که ایمان آورده‌اند و کارهای شایسته کرده‌اند، اجابت می‌کند، و بر جزای آنان از فضل خویش می‌افزاید، و کافران عذابی سهمگین [در پیش‌] دارند'),
(4299, 42, 27, 'و اگر خداوند روزی را بر همه بندگانش گسترده می‌داشت، در روی زمین فتنه و فساد می‌کردند، ولی به اندازه‌ای که بخواهد مقرر می‌دارد، که او به کار بندگانش آگاه و بیناست‌'),
(4300, 42, 28, 'و هموست که باران را پس از آنکه نومید شده‌اند، فرو می‌فرستد، و رحمتش را می‌گسترد، و او سرور ستوده است‌'),
(4301, 42, 29, 'و از آیات او آفرینش آسمانها و زمین است و جانورانی که در آنها پراکنده است، و او هرگاه بخواهد بر گردآوردن آنها تواناست‌'),
(4302, 42, 30, 'و هر مصیبتی که به شما برسد از کار و کردار خودتان است، و او از بسیاری [گناهان‌] در می‌گذرد'),
(4303, 42, 31, 'و شما در روی زمین گزیر و گریزی ندارید، و شما را در برابر خداوند یار و یاوری نیست‌'),
(4304, 42, 32, 'و از پدیده‌های شگرف او کشتیهاست مانند کوهها در دریا'),
(4305, 42, 33, 'اگر خواهد باد را نگه دارد، آنگاه آنها بر روی آن [دریا] راکد مانند، در این برای هر شکیبای شاکری مایه‌های عبرت است‌'),
(4306, 42, 34, 'یا آنان [/اهل کشتی‌] را به خاطر کار و کردارشان نابود کند، و او از بسیاری [گناهان‌] در می‌گذرد'),
(4307, 42, 35, 'و کسانی که در آیات ما مجادله می‌کنند، بدانند که گریزگاهی ندارند'),
(4308, 42, 36, 'پس آنچه به شما داده شده است، بهره زندگانی دنیاست، و برای کسانی که ایمان آورده‌اند و به پروردگارشان توکل کرده‌اند، آنچه نزد خداوند است، بهتر و پایدارتر است‌'),
(4309, 42, 37, 'و همچنین [برای‌] کسانی که از گناهان کبیره و ناشایستیها پرهیز می‌کنند، و چون خشمگین شوند، گذشت می‌کنند'),
(4310, 42, 38, 'و کسانی که ندای پروردگارشان را استجابت کرده‌اند، و نماز را بر پا داشته‌اند و کارشان رایزنی با همدیگر است، و از آنچه روزیشان داده‌ایم، می‌بخشند'),
(4311, 42, 39, 'و کسانی که چون به آنان تعدی رسد، انتقام می‌گیرند'),
(4312, 42, 40, 'و جزای هر بدی، بدیی همانند آن است، پس هر که عفو و نیکوکاری پیشه کند، پاداش او بر خداوند است، که ستمکاران را دوست ندارد'),
(4313, 42, 41, 'و هرکس بعد از ستمی که دیده است، انتقام گیرد، اینانند که بر آنان ایرادی نیست‌'),
(4314, 42, 42, 'ایراد تنها بر کسانی است که به مردم ستم می‌کنند و در روی زمین به ناحق سرکشی می‌نمایند، اینانند که عذابی دردناک در پیش دارند'),
(4315, 42, 43, 'و هرکس که شکیبایی و گذشت پیشه کند، بی‌گمان این از کارهای سترگ است‌'),
(4316, 42, 44, 'و هرکس خداوند بیراهش گذارد، دیگر پس از او سروری ندارد، و ستمکاران [مشرک‌] را بینی که چون عذاب را بنگرند، گویند آیا راهی برای بازگشت [به دنیا] هست‌'),
(4317, 42, 45, 'و آنان را بینی که بر آن [آتش دوزخ‌] عرضه دارندشان، از خواری زبونند، و به گوشه چشم نیم گشاده می‌نگرند، و مؤمنان گویند بی‌گمان زیانکاران کسانی هستند که در روز [حساب و] قیامت به خویشتن و خانواده خویش زیان زده‌اند، بدانید که ستمکاران [مشرک‌] در عذابی پاینده‌اند'),
(4318, 42, 46, 'و آنان را دوستانی نیست که در برابر خداوند یاریشان کنند، و هرکس که خداوند بیراهش گذارد، او را راهی نیست‌'),
(4319, 42, 47, 'ندای پروردگارتان را، پیش از آنکه روزی بیاید که در برابر امر الهی بازگشتی نداشته باشد، بپذیرید، در چنین روزی شما را پناهگاهی نیست، و شما را مجال انکاری نیست‌'),
(4320, 42, 48, 'پس اگر رویگردان شدند بدان که تو را نگهبان ایشان نفرستاده‌ایم، بر تو جز پیام‌رسانی نیست، و ما چون از جانب خویش به انسان رحمتی بچشانیم، به آن شاد شود، و اگر به خاطر کار و کردار پیشینشان به ایشان بلایی رسد، آنگاه است که انسان ناسپاس است‌'),
(4321, 42, 49, 'فرمانروایی آسمانها و زمین از آن خداوند است، هر چه می‌خواهد می‌آفریند، به هرکس که خواهد [فرزند] دختر بخشد، و به هرکس که خواهد [فرزند] پسر'),
(4322, 42, 50, 'یا به آنان هم پسر و هم دختر دهد، و هرکس را که خواهد سترون گرداند، که او دانای تواناست‌'),
(4323, 42, 51, 'و هیچ بشری را نرسد که خداوند با او سخن گوید مگر از راه وحی، یا از پشت پرده‌ای، یا فرشته‌ای بفرستد و آنچه می‌خواهد به اذن خویش وحی کند که او بلندمرتبه فرزانه است‌'),
(4324, 42, 52, 'و بدین‌سان [پیام و کتاب‌] روحبخشی از امر خویش به تو وحی کردیم، و تو پیشتر نمی‌دانستی کتاب چیست و ایمان چیست؟ ولی آن را همچون نوری گرداندیم که هرکس را از بندگان خویش که بخواهیم با آن هدایت می‌کنیم و بی‌گمان تو به راهی راست هدایت می‌کنی‌'),
(4325, 42, 53, 'راه خداوندی که آنچه در آسمانها و آنچه در زمین است، او راست، بدانید که کارها به سوی خداوند باز می‌گردد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حم [حا میم‌]'),
(4327, 43, 2, 'سوگند به کتاب روشنگر'),
(4328, 43, 3, 'ما آن را به هیئت قرآنی عربی پدید آورده‌ایم، باشد که تعقل کنید'),
(4329, 43, 4, 'و آن بلندمرتبه و حکمت‌آمیز است و در ام‌الکتاب در نزد ماست‌'),
(4330, 43, 5, 'آیا به خاطر آنکه شما قومی گزافکار هستید، پند [قرآن‌] را از شما باز داریم؟'),
(4331, 43, 6, 'و چه بسیار پیامبر در میان پیشینیان فرستاده‌ایم‌'),
(4332, 43, 7, 'و هیچ پیامبری به نزد آنان نیامد مگر آنکه او را ریشخند کردند'),
(4333, 43, 8, 'آنگاه دست درازتر از آنان را نابود کردیم، و سرنوشت پیشینیان تکرار شد'),
(4334, 43, 9, 'و اگر از آنان پرسی که آسمانها و زمین را چه کسی آفریده است، بی‌شک گویند خداوند پیروزمند دانا آنها را آفریده است‌'),
(4335, 43, 10, 'همان کسی که زمین را آسایشگاه شما ساخت و در آن راههایی پدید آورد باشد که راه یابید'),
(4336, 43, 11, 'و همان کسی که از آسمان آبی به اندازه فرو فرستاد، آنگاه بدان سرزمینی پژمرده را زنده ساختیم، [شما هم‌] بدین‌سان [از گورها] بیرون آورده شوید'),
(4337, 43, 12, 'و همان کسی که همه گونه‌ها را آفرید و برای شما از کشتیها و چارپایان مرکوب ساخت‌'),
(4338, 43, 13, 'که بر پشت آن برآیید و سپس نعمت پروردگارتان را آنگاه که بر آن بر آمدید، یاد کنید و بگویید پاکا کسی که این را رام ما ساخت و ما بر آن توانا نبودیم‌'),
(4339, 43, 14, 'و ما به [درگاه‌] پروردگارمان روی می‌آوریم‌'),
(4340, 43, 15, 'و برای او از بندگانش فرزندی قائل شدند، بی‌گمان انسان، ناسپاسی آشکار است‌'),
(4341, 43, 16, 'آیا از آنچه آفریده است برای خود دختران را برگزیده است و شما را به داشتن پسران، برگزیده است؟'),
(4342, 43, 17, 'و چون هر یک از آنان را به آنچه برای خداوند مثل می‌زند [دختر]، خبر دهند، چهره‌اش سیاه شود و اندوه خود را فرو خورد'),
(4343, 43, 18, 'آیا کسی که در زر و زیور پرورش یافته است [دختر] که در جدل هم ناتوان است [شایسته نسبت دادن به خداوند است؟]'),
(4344, 43, 19, 'و مدعی شدند که فرشتگان که خود بندگان خداوند رحمان‌اند، مادینه‌اند، آیا آفرینش آنان را شاهد بوده‌اند؟ که [در این صورت‌] زودا که شهادت ایشان نوشته شود، و بازخواست شوند'),
(4345, 43, 20, 'و گویند اگر خداوند رحمان می‌خواست ما آنان [فرشتگان‌] را نمی‌پرستیدیم، آنان را به این امر، علمی نیست، ایشان جز دروغ نمی‌بافند'),
(4346, 43, 21, 'یا شاید به آنان کتابی پیش از آن داده‌ایم که ایشان به آن متمسک‌اند؟'),
(4347, 43, 22, 'بلکه گویند ما پدرانمان را بر شیوه‌ای یافته‌ایم، و با پیروی از آنان رهیافته‌ایم‌'),
(4348, 43, 23, 'و بدین‌سان پیش از تو در هیچ آبادی، هشداردهنده‌ای نفرستادیم مگر آنکه ناز پروردگانش گفتند ما پدرانمان را بر شیوه‌ای یافته‌ایم، و ما در پی آنان دنباله روانیم‌'),
(4349, 43, 24, 'بگو حتی اگر برای شما راهنماتر از آنچه پدرانتان را پیرو آن یافتید، بیاورم؟ گفتند ما رسالت شما را منکریم‌'),
(4350, 43, 25, 'آنگاه از ایشان داد ستاندیم، پس بنگر که سرانجام دروغ انگاران چه بوده است‌'),
(4351, 43, 26, 'و چنین بود که ابراهیم به پدرش و قومش گفت همانا من از آنچه می‌پرستید بری و برکنارم‌'),
(4352, 43, 27, 'مگر از کسی که مرا آفریده است و همو مرا هدایت فرماید'),
(4353, 43, 28, 'و آن [اندیشه توحید] را سخنی ماندگار در میان احفاد او قرار داد، باشد که به راه آیند'),
(4354, 43, 29, 'آری اینان و پدرانشان را بهره‌مند ساخته‌ام تا آنکه [دین‌] حق و پیامبری آشکار به نزد آنان آمد'),
(4355, 43, 30, 'و چون [دین‌] حق به نزد آنان آمد، گفتند این جادوست و ما منکر آنیم‌'),
(4356, 43, 31, 'و گفتند چرا این قرآن بر مردی بزرگ از آن دو شهر [مکه و طائف‌] فرود نیامده است‌'),
(4357, 43, 32, 'آیا ایشان رحمت پروردگارت را تقسیم می‌کنند [نه بلکه‌] ما زیستمایه‌شان را در زندگانی دنیا در میان آنان تقسیم می‌کنیم، و بعضی از ایشان را بر بعضی دیگر به درجاتی برتر داشتیم تا بعضی از آنان بعضی دیگر را به بیگاری بگیرند، و رحمت پروردگارت بهتر است از آنچه گرد می‌آورند'),
(4358, 43, 33, 'و اگر این نبود که [نمی‌خواستیم‌] مردم امت یگانه‌ای [در کفر] شوند، برای خانه‌های کسانی که بر خداوند رحمان کفر می‌ورزیدند، سقفهایی سیمین پدید می‌آوردیم، و نیز نردبانهایی [از سیم‌] که بر آنها بالا روند'),
(4359, 43, 34, 'و نیز برای خانه‌هایشان درهایی [از سیم‌] و تختهایی [قرار می‌دادیم‌] که بر آنها تکیه زنند'),
(4360, 43, 35, 'و زر و زیورهایی، و همه اینها کالای زندگانی دنیاست، و آخرت در نزد پروردگارت از آن پرهیزگاران است‌'),
(4361, 43, 36, 'و هر کس از یاد خدای رحمان دل بگرداند، برای او شیطانی بگماریم که او همنشین اوست‌'),
(4362, 43, 37, 'و آنان ایشان را از راه [راست‌] باز دارند، و گمان برند که خود رهیافته‌اند'),
(4363, 43, 38, 'تا آنکه به نزد ما آید [و به آن شیطان‌] گوید کاش بین من و تو فاصله مشرق و مغرب بود، و چه بد همنشینی است‌'),
(4364, 43, 39, 'و هرگز چون ستم [/شرک‌]ورزیده‌اید، این روز [این امر] سودتان ندهد که در عذاب مشترکید'),
(4365, 43, 40, 'آیا تو به ناشنوایان [پیام خود را] می‌شنوانی، یا نابینایان و کسی را که در گمراهی آشکار است، هدایت می‌کنی؟'),
(4366, 43, 41, 'پس اگر تو را [از دنیا] ببریم، در آن صورت از ایشان داد می‌ستانیم‌'),
(4367, 43, 42, 'یا اگر به تو آنچه [از عذاب‌] به ایشان وعده داده‌ایم نشان دهیم، در هر صورت، بر ایشان تواناییم‌'),
(4368, 43, 43, 'به آنچه بر تو وحی شده است، تمسک کن [و بدان که‌] تو بر راه راست هستی‌'),
(4369, 43, 44, 'و آن یادآوری برای تو و قوم توست، و زودا که باز خواست شوید'),
(4370, 43, 45, 'و از [پیروان‌] پیامبران ما، کسانی که پیش از تو فرستاده‌ایم بپرس، که آیا به جای خداوند رحمان، خدایانی را حکم کرده‌ایم که پرستیده شوند؟'),
(4371, 43, 46, 'و به راستی موسی را همراه با پدیده‌های شگرفمان به سوی فرعون و بزرگان قومش فرستادیم، آنگاه گفت من پیامبر پروردگار جهانیانیم‌'),
(4372, 43, 47, 'چون پدیده‌های شگرف ما را برای آنان آورد، آنگاه بود که به آنها می‌خندیدند'),
(4373, 43, 48, 'و هیچ پدیده شگرفی به آنان ننمایاندیم مگر آنکه از همانندش بزرگتر بود، و ایشان را با عذاب فرو گرفتیم باشد که به راه آیند'),
(4374, 43, 49, 'و گفتند ای ساحر [ارجمند] برای ما به درگاه پروردگارت -با عهدی که با تو دارددعا کن که ما هم رهیافته‌ایم‌'),
(4375, 43, 50, 'و چون عذاب را از آنان بر طرف ساختیم، آنگاه بود که پیمان شکنی کردند'),
(4376, 43, 51, 'و فرعون در میان قومش [چنین‌] آواز در داد که ای قوم من آیا فرمانروایی مصر از آن من نیست و آیا این رودها از زیر [کوشک‌] من روان نیست، آیا نمی‌نگرید؟'),
(4377, 43, 52, 'بلکه من بهترم از این کسی که بی‌مقدار است، و نزدیک نیست که سخن واضح بگوید'),
(4378, 43, 53, 'پس چرا دستبندهایی زرین برای او نازل نشده، یا چرا با او فرشتگانی نیامده‌اند که همراهی کنند؟'),
(4379, 43, 54, 'پس قومش را از راه به در برد، آنگاه از او پیروی کردند که ایشان [در برابر خداوند] قومی نافرمان بودند'),
(4380, 43, 55, 'و چون ما را به خشم آوردند، از ایشان انتقام گرفتیم، و همگیشان را غرقه ساختیم‌'),
(4381, 43, 56, 'و آنان را پیشینه و مایه عبرت واپسینان گرداندیم‌'),
(4382, 43, 57, 'و چون [آفرینش‌] پسر مریم مثل زده شد، آنگاه قوم تو از آن بانگ [به ریشخند] برداشتند'),
(4383, 43, 58, 'و گفتند آیا خدایان ما بهترند یا او؟ و این مثل را برای تو نزدند مگر از راه جدل، آری که قومی ستیزه‌جو هستند'),
(4384, 43, 59, 'او جز بنده‌ای نیست که بر او نعمتها ارزانی داشته‌ایم، و او را برای بنی‌اسرائیل مایه عبرت ساخته‌ایم‌'),
(4385, 43, 60, 'و اگر می‌خواستیم به جای شما فرشتگانی در روی زمین پدید می‌آوردیم که جانشین شوند'),
(4386, 43, 61, 'و همانا او [عیسی یا قرآن‌] نشانه قیامت است، پس هرگز در آن شک نداشته باشید، و از من پیروی کنید، این راهی راست است‌'),
(4387, 43, 62, 'و شیطان راه شما را نزند، که او دشمن آشکار شماست‌'),
(4388, 43, 63, 'و چون عیسی پدیده‌های شگرف را آورد، گفت به راستی برای شما حکمت آورده‌ام، و برای شما بعضی از اموری را که در آن اختلاف نظر دارید روشن می‌سازم، پس از خداوند پروا و از من پیروی کنید'),
(4389, 43, 64, 'بی‌گمان خداوند پروردگار من است و پروردگار شماست، او را بپرستید که راه راست همین است‌'),
(4390, 43, 65, 'ولی گروه مشرکان در میان خود به اختلاف سخن گفتند، پس وای بر ستمگران [مشرک‌] از عذاب روزی سهمگین‌'),
(4391, 43, 66, 'انتظار ندارند مگر قیامت را که به ناگهان به سراغشان آید و ایشان ناآگاه باشند'),
(4392, 43, 67, 'دوستان در چنین روزی، بعضی دشمن بعض دیگر باشند، مگر پرهیزگاران‌'),
(4393, 43, 68, 'ای بندگانم امروز نه بیمی بر شماست و نه اندوهگین شوید'),
(4394, 43, 69, 'کسانی که به آیات ما ایمان آورده‌اند و مسلمان هستند'),
(4395, 43, 70, 'شما و همسرانتان شادمانه به بهشت درآیید'),
(4396, 43, 71, 'برگرد آنان سینی‌های زرین و کوزه‌ها را به گردش درآورند و در آنجا هر چه دلها خواهد و دیدگان بپسندد، هست و شما در آنجا جاودانه هستید'),
(4397, 43, 72, 'و این بهشتی است که به خاطر کار و کردارتان به شما رسیده است‌'),
(4398, 43, 73, 'برای شما در آنجا میوه‌های بسیار هست که از آنها می‌خورید'),
(4399, 43, 74, 'بی‌گمان گناهکاران در عذاب دوزخ جاویدانند'),
(4400, 43, 75, '[عذاب‌] از ایشان کاهش نیابد و آنان در آن حال دلسردند'),
(4401, 43, 76, 'و ما در حق ایشان ستم نکردیم، بلکه خود [در حق خویش‌] ستمکار بودند'),
(4402, 43, 77, 'و ندا در دهندای "مالک" کاش پروردگارت کار ما را یکسره کند، گوید شما ماندگارید'),
(4403, 43, 78, 'به راستی که برای شما [دین و سخن‌] حق را آورده‌ایم، ولی بیشترینه شما ناخواهان حق هستید'),
(4404, 43, 79, 'یا در کار خود عزم و جزم داشتند، ما نیز جازم هستیم‌'),
(4405, 43, 80, 'یا شاید گمان کنند که ما راز و رازگویی‌شان را نمی‌شنویم؟ آری می‌شنویم، و فرشتگان ما نزد ایشان [کار و کردارشان را] می‌نویسند'),
(4406, 43, 81, 'بگو اگر برای خدای رحمان فرزندی بود، من خود نخستین پرستنده بودم'),
(4407, 43, 82, 'منزه و فراتر است پروردگار آسمانها و زمین صاحب عرش، از آنچه می‌گویند'),
(4408, 43, 83, 'پس بگذارشان تا ژاژخایی و بازیگوشی کنند تا به دیدار آن روزشان که وعده‌اش را به ایشان داده‌اند، برسند'),
(4409, 43, 84, 'و او کسی است که هم در آسمان خداست و هم در زمین خداست، و او فرزانه داناست‌'),
(4410, 43, 85, 'و بزرگا کسی که فرمانروایی آسمانها و زمین و آنچه در میان آنهاست از آن اوست و آگاهی از قیامت با اوست و به نزد او باز گردانده می‌شوید'),
(4411, 43, 86, 'و کسانی که [کافران‌] به جای او [خداوند] به پرستش می‌خوانند، دارای شفاعت نیستند، مگر [برای‌] آن کسی که شاهد و ناظر حق باشد و ایشان می‌دانند'),
(4412, 43, 87, 'و اگر از ایشان بپرسی چه کسی ایشان را آفریده است، بی‌شک خواهند گفت خداوند، پس چگونه بیراهه می‌روند؟'),
(4413, 43, 88, 'و سوگند به سخن او [محمد] که یارب گوید، اینان قومی هستند که ایمان نمی‌آورند'),
(4414, 43, 89, 'از ایشان درگذر و بگو سلام، زودا که بدانند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حم [حا میم‌]'),
(4416, 44, 2, 'سوگند به کتاب روشنگر'),
(4417, 44, 3, 'که ما آن را در شبی فرخنده فرو فرستاده‌ایم که ما هشداردهنده‌ایم‌'),
(4418, 44, 4, 'در آن [شب‌] هر کار استواری فیصله یابد'),
(4419, 44, 5, 'که فرمانی از سوی ماست، که ما فرستنده‌ایم‌'),
(4420, 44, 6, 'رحمتی است از سوی پروردگارت، که او شنوای داناست‌'),
(4421, 44, 7, 'پروردگار آسمانها و زمین و آنچه در میان آنهاست، اگر اهل ایقان باشید'),
(4422, 44, 8, 'خدایی جز او نیست که زندگی می‌بخشد و می‌میراند، پروردگار شما و پروردگار پدران پیشین شما'),
(4423, 44, 9, 'آری آنان به شک و شبهه خویش سرگرمند'),
(4424, 44, 10, 'پس چشم بدار روزی را که آسمان دودی آشکار برآورد'),
(4425, 44, 11, 'که مردم را فراگیرد، این عذابی دردناک است‌'),
(4426, 44, 12, '[گویند] پروردگارا این بلا را از ما بگردان، که ما [اینک‌] مؤمن هستیم‌'),
(4427, 44, 13, '[اما] چگونه پند گیرند، و به راستی که پیامبری آشکار به نزد آنان آمده بود'),
(4428, 44, 14, 'سپس از او رویگردان شدند، و گفتند [او] تعلیم گرفته‌ای دیوانه است‌'),
(4429, 44, 15, '[گفتیم‌] ما برگرداننده بلا به اندک مایه‌ای هستیم، [و می‌دانیم‌] که شما باز گردنده‌اید'),
(4430, 44, 16, 'روزی که گیر و داری سخت و سترگ آغاز کنیم، [آنگاه‌] ما دادستانیم‌'),
(4431, 44, 17, 'و به راستی که پیش از آنان قوم فرعون را آزمودیم، و پیامبری گرامی به نزد آنان آمد'),
(4432, 44, 18, '[و با ایشان گفت‌] که بندگان خدا را به من بسپارید، که من پیامبری امین برای شما هستم‌'),
(4433, 44, 19, 'و اینکه در برابر خداوند بزرگی نجویید، که من آورنده برهانی آشکار برای شما هستم‌'),
(4434, 44, 20, 'و من از شر اینکه سنگسارم کنید به پروردگار خود و پروردگار شما پناه می‌برم‌'),
(4435, 44, 21, 'و اگر سخن مرا باور ندارید، پس از من کناره بگیرید'),
(4436, 44, 22, 'آنگاه به درگاه پروردگارش ندا در داد که اینان قومی گنهکارند'),
(4437, 44, 23, '[پاسخ آمد که‌] پس بندگانم را شبانه روانه کن، [و بدانید که‌] شما تعقیب می‌شوید'),
(4438, 44, 24, 'و دریا را آرمیده [به حال خود] بگذار و بگذر، آنان [فرعونیان‌] سپاهی غرق شدنی‌اند'),
(4439, 44, 25, 'چه بسیار باغها و چشمه‌ساران از خود باقی گذارند'),
(4440, 44, 26, 'و [نیز] کشتزارها و خانه‌های نیکو'),
(4441, 44, 27, 'و نعمتی که در آن خوش و خرم بودند'),
(4442, 44, 28, 'این چنین بود که آنها را به قومی دیگر به میراث دادیم‌'),
(4443, 44, 29, 'و آسمان و زمین بر آنان نگریست، و به آنان مهلتی داده نشد'),
(4444, 44, 30, 'و به راستی که بنی‌اسرائیل را از عذاب خوارکننده رهانیدیم‌'),
(4445, 44, 31, 'از [شر] فرعون که بزرگی طلبی از تجاوزکاران بود'),
(4446, 44, 32, 'و به راستی که ایشان [بنی‌اسرائیل‌] را آگاهانه بر جهانیان [هم زمانشان‌] برگزیدیم‌'),
(4447, 44, 33, 'و به ایشان از پدیده‌های شگرف خویش، آنچه در آن آزمون آشکاری بود بخشیدیم‌'),
(4448, 44, 34, '[و می‌دانستیم که‌] اینان خواهند گفت'),
(4449, 44, 35, 'این جز مرگ نخستین [و آخرین‌] ما نیست، و ما برانگیخته نخواهیم بود'),
(4450, 44, 36, 'اگر راست می‌گویید [و حقیقت غیر از این است‌] پدرانمان را باز آورید'),
(4451, 44, 37, 'آیا ایشان بهتر بودند یا قوم تبع، و پیشینیان آنان که نابودشان کردیم، چرا که گناهکار بودند'),
(4452, 44, 38, 'و آسمانها و زمین را و آنچه در میان آنهاست، به بازیچه نیافریده‌ایم‌'),
(4453, 44, 39, 'آنها را جز به حق نیافریده‌ایم ولی بیشترینه آنان نمی‌دانند'),
(4454, 44, 40, 'بی‌گمان روز داوری، موعد همگی آنان است‌'),
(4455, 44, 41, 'روزی که هیچ دوستی از هیچ دوستی دفاع نکند و نیز ایشان یاری نیابند'),
(4456, 44, 42, 'مگر کسی که خداوند بر او رحمت آورده باشد، که او پیروزمند مهربان است‌'),
(4457, 44, 43, 'همانا درخت زقوم'),
(4458, 44, 44, 'خوراک گناهکار[ان‌] است‌'),
(4459, 44, 45, 'که مانند فلز گداخته در شکمها می‌جوشد'),
(4460, 44, 46, 'مانند جوشیدن آب گرم‌'),
(4461, 44, 47, '[درباره هر گناهکار گوییم‌] او را بگیرید و به میانه دوزخش بکشید'),
(4462, 44, 48, 'سپس بر فراز سرش از عذاب آب گرم فرو ریزید'),
(4463, 44, 49, '[و بگوییدش‌] بچش که تو [به خیال خودت‌] گرانقدر گرامی هستی‌'),
(4464, 44, 50, 'این همان است که به آن شک داشتید'),
(4465, 44, 51, 'بی‌گمان پرهیزگاران در مقامی امن هستند'),
(4466, 44, 52, 'در میان باغها و چشمه‌ساران‌'),
(4467, 44, 53, 'جامه‌هایی از ابریشم نازک و ابریشم ستبر در بر کرده، رو به روی یکدیگرند'),
(4468, 44, 54, 'آری چنین است، و آنان را با حوریان درشت چشم همسر می‌گردانیم‌'),
(4469, 44, 55, 'در آنجا هر میوه‌ای که خواهند در کمال آرامش طلب کنند'),
(4470, 44, 56, 'در آنجا [طعم‌] هیچ مرگی جز مرگ نخستین نچشند، و [خداوند] ایشان را از عذاب دوزخ در امان دارد'),
(4471, 44, 57, 'این بخششی از سوی پروردگار توست، این همان رستگاری بزرگ است‌'),
(4472, 44, 58, 'جز این نیست که آن را به زبان تو به آسانی روان ساخته‌ایم، باشد که پند گیرند'),
(4473, 44, 59, 'منتظر باش که آنان نیز منتظرند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حم [حا میم‌]'),
(4475, 45, 2, 'کتابی است فرو فرستاده از سوی خداوند پیروزمند فرزانه‌'),
(4476, 45, 3, 'بی‌گمان در آسمانها و زمین مایه‌های عبرتی برای مؤمنان هست‌'),
(4477, 45, 4, 'نیز در آفرینشتان و جانورانی که [در زمین‌] می‌پراکند، برای اهل یقین مایه‌های عبرت است‌'),
(4478, 45, 5, 'و در پی یکدیگر آمدن شب و روز، و هر روزی‌ای [باران‌] که خداوند از آسمان فرو فرستاده است، و زمین را پس از پژمردنش بدان زنده داشته است، و در گردش بادها مایه‌های عبرتی برای خردمندان هست‌'),
(4479, 45, 6, 'این آیات الهی است که به حق بر تو می‌خوانیم پس به کدامین حدیث بعد از خداوند و آیات او ایمان می‌آورند؟'),
(4480, 45, 7, 'وای بر هر دروغ‌زن گناهکاری‌'),
(4481, 45, 8, 'که آیات الهی را که بر او خوانده می‌شود، می‌شنود، سپس متکبرانه در شیوه خود سماجت می‌ورزد، گویی آنها را نشنیده است، پس او را از عذاب دردناک خبر ده‌'),
(4482, 45, 9, 'و چون از چیزی از آیات ما آگاه شود، آن را به ریشخند می‌گیرد، اینانند که برایشان عذاب خوارکننده است‌'),
(4483, 45, 10, 'دوزخ پیشاپیش ایشان است، و آنچه به دست آورده‌اند هیچ به کار ایشان نیاید، همچنین دوستانی که به جای خداوند گرفته‌اند، و عذابی سهمگین در پیش دارند'),
(4484, 45, 11, 'این رهنمودی است و کسانی که به آیات پروردگارشان انکار ورزیده‌اند، عذابی از عقوبتی دردناک دارند'),
(4485, 45, 12, 'خداوند کسی است که دریا را برای شما رام کرد تا کشتیها در آن به فرمان او [برای شما] روان شود، تا از نعمت او روزی خود بجویید و باشد که سپاس بگزارید'),
(4486, 45, 13, 'و آنچه در آسمانها و آنچه در زمین است همه از اوست و آنها را برای شما رام کرد، بی‌گمان در این مایه‌های عبرتی برای اندیشه‌وران هست‌'),
(4487, 45, 14, 'به کسانی که ایمان آورده‌اند بگو از کسانی که از ایام الله بیمی ندارند درگذرند، تا خداوند هر قومی را به حسب کار و کردارشان جزا دهد'),
(4488, 45, 15, 'هر کس که کاری شایسته پیشه کند به سود خود اوست، و هر کس کاری بد در پیش گیرد، به زیان خود اوست، سپس به سوی پروردگارتان بازگردانده شوید'),
(4489, 45, 16, 'و به راستی که به بنی‌اسرائیل کتاب آسمانی و فرمانروایی و پیامبری بخشیدیم، و از پاکیزه‌ها روزیشان دادیم، و ایشان را بر جهانیان [هم زمانشان‌] برتری بخشیدیم‌'),
(4490, 45, 17, 'و از کار [دین‌] روشنگریها به ایشان دادیم، و اختلاف پیشه نکردند، مگر پس از آنکه علم [وحی‌] بر ایشان نازل شد، آن هم از روی رشک و رقابتی که در میان خود داشتند، بی‌گمان پروردگارت در روز قیامت در میان آنان در آنچه اختلاف داشتند، داوری خواهد کرد'),
(4491, 45, 18, 'سپس تو را برخوردار از آبشخوری از امر [دین‌] ساختیم، پس از آن پیروی کن و از هوی و هوس نادانان پیروی مکن‌'),
(4492, 45, 19, 'که ایشان در برابر [عذاب‌] خداوند، چیزی را از تو باز ندارند، و بی‌گمان بعضی از ستمکاران [مشرک‌] سرور بعضی دیگرند، و خداوند سرور پرهیزگاران است‌'),
(4493, 45, 20, 'این دیده‌وریهایی برای مردم است، و برای اهل ایقان رهنمود و رحمت است‌'),
(4494, 45, 21, 'یا کسانی که مرتکب گناهان شده‌اند گمان می‌دارند که ایشان را همانند کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، قرار می‌دهیم، که زندگانی و مرگشان یکسان باشد؟ چه بد است داوریشان‌'),
(4495, 45, 22, 'خداوند آسمانها و زمین را به حق آفریده است، تا هر کسی را بر حسب آنچه کرده است، جزا دهد و بر ایشان ستم نرود'),
(4496, 45, 23, 'آیا دیده‌ای آن کس را که هوای نفسش را [همچون‌] خدای خود گرفت و خداوند او را با وجود آگاهی‌اش، بیراه گذارد، و بر گوش و دل او مهر نهاد و بر چشمش پرده کشید، پس چه کسی پس از خداوند او را هدایت می‌کند، آیا پند نمی‌گیرید؟'),
(4497, 45, 24, 'و گویند چیزی جز زندگی دنیوی ما در کار نیست، که زنده می‌شویم و می‌میریم و چیزی جز روزگار ما را نمی‌کشد، و آنان را به این امر علم نیست، چیزی جز پنداربافی نمی‌کنند'),
(4498, 45, 25, 'و چون آیات روشنگر ما بر آنان خوانده شود، بهانه‌شان جز این نیست که گویند اگر راست می‌گویید پدران ما را بازآورید'),
(4499, 45, 26, 'بگو خداوند است که شما را زندگی می‌بخشد و سپس می‌میراند، سپس در روز قیامت که شکی در [فرارسیدن‌] آن نیست، گرد می‌آورد، ولی بیشترینه مردم نمی‌دانند'),
(4500, 45, 27, 'و فرمانروایی آسمانها و زمین از آن خداوند است، و روزی که قیامت برپا شود، در چنین روزی باطل‌اندیشان زیان کنند'),
(4501, 45, 28, 'و بینی هر امتی را که [برای دادخواهی‌] به زانو درآمده، هر امتی با کتابش فراخوانده شود، [گویند] امروز در برابر آنچه کرده‌اید، جزا می‌یابید'),
(4502, 45, 29, 'این کتاب ماست که به راستی بر شما گواهی می‌دهد، ما آنچه شما انجام می‌دادید ثبت می‌کردیم‌'),
(4503, 45, 30, 'اما کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، پروردگارشان ایشان را به جوار رحمت خویش [بهشت‌] در می‌آورد، این همان رستگاری آشکار است‌'),
(4504, 45, 31, 'و اما کسانی که کفرورزیده‌اند [به آنان فرماید] آیا آیات من بر شما خوانده نمی‌شد که استکبار ورزیدید و قومی گنهکار شدید'),
(4505, 45, 32, 'و چون گفته می‌شد که وعده الهی حق است و در قیامت شکی نیست، می‌گفتید نمی‌دانیم قیامت دیگر چیست؟ آن را جز گمانی نمی‌پنداریم، و ما [در حق آن‌] باورمند نیستیم‌'),
(4506, 45, 33, 'و کیفر آنچه کرده بودند، بر آنان آشکار شد [و کیفر] آنچه به ریشخند گرفته بودند، فراگیرشان شد'),
(4507, 45, 34, 'و گفته شود، امروز فراموشتان کرده‌ایم، همچنانکه شما هم دیدار امروزتان را فراموش کرده بودید، و جایگاه شما آتش [دوزخ‌] است و برای شما یاوری نیست‌'),
(4508, 45, 35, 'این از آن است که آیات الهی را به ریشخند گرفتید و زندگانی دنیا شما را فریفت، پس امروز، نه از آن بیرون برده شوند، نه عذرشان را بپذیرند'),
(4509, 45, 36, 'سپاس خداوند راست، که پروردگار آسمانها و زمین [و] پروردگار جهانیان است‌'),
(4510, 45, 37, 'و تنها او را در آسمانها و زمین بزرگی سزد، و او پیروزمند فرزانه است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حم [حا میم‌]'),
(4512, 46, 2, 'کتابی است فروفرستاده از سوی خداوند پیروزمند فرزانه‌'),
(4513, 46, 3, 'آسمانها و زمین و آنچه در میان آنهاست، جز به حق و سرآمدی معین نیافریده‌ایم و کافران از آنچه هشدار می‌یابند رویگردانند'),
(4514, 46, 4, 'بگو آیا شریکانی را که به جای خداوند قائل هستید، نگریسته‌اید، به من بنمایانید که چه چیزی را در زمین آفریده‌اند، یا آیا در [آفرینش‌] آسمانها شرکتی داشته‌اند، اگر راست می‌گویید کتابی پیش از این [کتاب‌]، یا نشانه‌ای از علم بیاورید'),
(4515, 46, 5, 'و کیست گمراه‌تر از کسی که موجودی را به دعا خواند که تا روز قیامت پاسخش را ندهد، و ایشان از دعای آنان بی‌خبرند'),
(4516, 46, 6, 'و چون مردم گرد آورده شوند، [آن معبودان باطل‌] دشمنان ایشان باشند، و عبادتشان را منکر شوند'),
(4517, 46, 7, 'و چون آیات روشنگر ما بر آنان خوانده شود، کافران درباره حق چون فراز آیدشان گویند، این جادوی آشکار است‌'),
(4518, 46, 8, 'یا گویند آن را بر ساخته است بگو اگر آن را برساخته باشم، در برابر خداوند چیزی به سود من ندارید، او به آنچه به آن می‌پردازید آگاه‌تر است، خداوند در میان من و شما گواه بس، و او آمرزگار مهربان است‌'),
(4519, 46, 9, 'بگو [من‌] پدیده‌ای نو ظهور در میان پیامبران نیستم، و نمی‌دانم بر من و بر شما چه خواهد رفت، جز از وحیی که به من می‌شود، از چیزی پیروی نمی‌کنم، و من جز هشداردهنده‌ای آشکار نیستم‌'),
(4520, 46, 10, 'بگو بیندیشید اگر [قرآن‌] از سوی خداوند باشد و سپس به آن انکار ورزید، [آیا ستمگر نیستید؟] و گواهی از بنی‌اسرائیل بر همانند آن گواهی داده است، آنگاه او ایمان آورده و شما استکبار ورزیدید، بی‌گمان خداوند قوم ستمکاران را هدایت نمی‌کند'),
(4521, 46, 11, 'و کافران در حق مؤمنان گویند اگر خیر بود آنان بر ما در زمینه آن پیشی نمی‌گرفتند، و چون به آن راهیاب نشدند، گویند این برساخته‌ای کهن است‌'),
(4522, 46, 12, 'و پیش از آن کتاب موسی رهنما و رحمت بود، و این کتابی است همخوان [با آن‌]، به زبان عربی [/شیوا]، تا ستم‌پیشگان [مشرک‌] را هشدار دهد، و بشارتی برای نیکوکاران باشد'),
(4523, 46, 13, 'بی‌گمان کسانی که گفتند پروردگار ما خداوند است، سپس [در این راه‌] پایداری ورزیدند، نه بیمی بر آنان است و نه اندوهگین می‌شوند'),
(4524, 46, 14, 'اینان بهشتیانند که جاودانه در آنند، که پاداش کاری است که کرده‌اند'),
(4525, 46, 15, 'و به انسان سفارش کرده‌ایم که در حق پدر و مادرش نیکی کند [چرا که‌] مادرش به دشواری او را آبستن بوده و به دشواری او را زاده است، و [دوران‌] بارگرفتن و از شیر گرفتن او سی‌ماه است، تا آنکه به کمال رشدش برسد و به چهل‌سالگی برسد گوید پروردگارا مرا توفیق ده که نعمتی را که بر من و بر پدر و مادرم ارزانی داشتی سپاس بگزارم، و کارهای شایسته‌ای کنم که آن را بپسندی، و زاد و رود مرا نیز به صلاح‌آور، من به درگاه تو توبه کرده‌ام و من از مسلمانانم‌'),
(4526, 46, 16, 'اینانند که نیکوترین کاری را که کرده‌اند، از ایشان می‌پذیریم، و در میان بهشتیان از گناهانشان در می‌گذریم، [این‌] وعده راستینی است که به آنان نوید داده شده است‌'),
(4527, 46, 17, 'و کسی که به پدر و مادرش می‌گفت اف بر شما آیا به من وعده می‌دهید که [از گور زنده‌] بیرون آورده شوم، حال آنکه نسلهای [بسیاری‌] پیش از من بوده‌اند [و زنده نشده‌اند]، و آن دو به درگاه خداوند استغاثه می‌کنند [و به او می‌گویند] وای بر تو، ایمان بیاور بی‌گمان وعده الهی راست است، و [او] گوید این جز افسانه‌های پیشینیان نیست‌'),
(4528, 46, 18, 'اینانند که در میان امتهای جن و انس که پیش از ایشان بوده‌اند، حکم [عذاب‌] بر آنان تعلق گرفته است، اینانند که زیانکارند'),
(4529, 46, 19, 'و هر یک را بر حسب آنچه کرده‌اند درجاتی است، و تا سرانجام جزای اعمالشان را به تمام و کمال بدهد، و بر ایشان ستم نرود'),
(4530, 46, 20, 'و روزی که کافران را به نزدیک آتش [دوزخ‌] بدارند [و گویند] خیر و خوشیهایتان را در زندگانی دنیویتان به پایان بردید، و از آن بهره‌مند شدید، پس امروز به خاطر آنکه در روی زمین به ناحق استکبار می‌ورزیدید و به خاطر آنکه نافرمانی می‌کردید، عذاب خوارکننده را کیفر برید'),
(4531, 46, 21, 'و از برادر عادیان [ هود] یاد کن که قومش را در احقاف هشدار داد، و پیشاپیش وی و پس از وی هشداردهندگانی به میان آمده بودند [و گفت‌] جز خداوند را مپرستید، که من بر شما از عذاب روزی سهمگین می‌ترسم‌'),
(4532, 46, 22, 'گفتند آیا به سراغ ما آمده‌ای که ما را از خدایانمان باز داری، پس اگر از راستگویان هستی، آنچه به ما وعده داده‌ای به میان بیاور'),
(4533, 46, 23, 'گفت همانا علم نزد خداوند است، و من رسالتم را به شما می‌رسانم، ولی شما را قومی می‌بینم که نادانی می‌کنید'),
(4534, 46, 24, 'آنگاه چون آن [عذاب‌] را به هیئت ابری دیدند که رو به [دشت و] دره‌هایشان نهاده بود، گفتند این ابری است بارنده بر ما، نه بلکه آن چیزی است که به شتابش می‌خواستید، بادی است که در آن عذابی دردناک است‌'),
(4535, 46, 25, 'که به فرمان پروردگارش همه چیز را نابود می‌کند، و چنان شدند که چیزی جز خانه‌هایشان دیده نمی‌شد، بدین‌سان قوم گناهکاران را کیفر می‌دهیم‌'),
(4536, 46, 26, 'و به راستی به آنان در چیزهایی تمکن داده بودیم که شما را آن تمکن نداده بودیم، و برای آنان گوشها و چشمها و دلها آفریده بودیم، اما گوشها و چشمها و دلهایشان سودی به حالشان نداد، چرا که آیات الهی را انکار می‌کردند و آنچه ریشخندش می‌کردند، ایشان را فروگرفت‌'),
(4537, 46, 27, 'و به راستی چه بسیار از شهرهایی را که پیرامونتان بود، نابود کردیم و آیات [خود] را گونه‌گون بیان کردیم، باشد که بازآیند'),
(4538, 46, 28, 'پس چرا کسانی که به جای خداوند به پرستششان گرفته بودند، که مایه تقربشان شود، یعنی آن خدایان یاریشان ندادند؟ بلکه از دید آنان گم و گور شدند، و این است افترای ایشان و آنچه بر می‌ساختند'),
(4539, 46, 29, 'و چنین بود که گروهی از جن را متوجه تو ساختیم که به قرآن گوش سپارند، و چون نزد او حضور یافتند گفتند خاموش باشید [و گوش دهید]، و چون سپری شد، هشدارگر به سوی قومشان بازگشتند'),
(4540, 46, 30, 'گفتند ای هم قومان ما، [آیات‌] کتابی را شنیدیم که پس از موسی فروفرستاده شده، [و] همخوان [کتابهای‌] پیشاپیش خویش است، و به حق و راهی راست رهنمون است‌'),
(4541, 46, 31, 'ای هم قومان ما دعوتگر الهی را بپذیرید و به او ایمان آورید، تا از گناهانتان درگذرد و شما را از عذابی دردناک در امان دارد'),
(4542, 46, 32, 'و هر کس دعوتگر الهی را نپذیرد، بداند که در [این سر] زمین گزیر و گریزی ندارد، و در برابر او سرورانی ندارد، اینانند که در گمراهی آشکارند'),
(4543, 46, 33, 'آیا نیندیشیده‌اند که خداوندی که آسمانها و زمین را آفریده است، و در آفرینش آنها در نمانده است، توانای آن است که مردگان را زنده بدارد، آری او بر هر کاری تواناست‌'),
(4544, 46, 34, 'و روزی که کافران را به نزدیک آتش [دوزخ‌] بدارند [و از ایشان بپرسند] آیا این [وعده‌] حق نیست؟ گویند چرا سوگند به پروردگارمان گوید پس عذاب را به خاطر آنکه [انکار و] کفرمی‌ورزیدید بچشید'),
(4545, 46, 35, 'پس همان گونه که پیامبران نستوه، صبر کردند، صبر کن، و برای آنان [عذاب را] به شتاب مخواه زیرا روزی که آنچه وعده‌شان داده شده است، بنگرند، گویی جز ساعتی از یک روز به سر نبرده‌اند، [این‌] پیامی است، پس به نابودی کشانده نخواهند شد جز قوم نافرمانان‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'کسانی که کفرورزیدند و [مردمان را] از راه خدا بازداشتند، [خداوند] اعمالشان را تباه [و بی‌ارزش‌] کرد'),
(4547, 47, 2, 'و کسانی که ایمان آوردند و کارهای شایسته کردند، و به آنچه بر محمد فروفرستاده شده -که حق است و از جانب پروردگارشانباور داشتند، گناهانشان را زدود و کار و بار ایشان به صلاح آورد'),
(4548, 47, 3, 'این از آن است که کافران از باطل پیروی کرده‌اند، و[لی‌] مؤمنان از حق پیروی کرده‌اند که از جانب پروردگارشان است، بدین‌سان خداوند برای مردم مثلهایشان را می‌زند'),
(4549, 47, 4, 'پس چون با کافران [حربی‌] رو به رو شوید، گردنهایشان را بزنید تا آنکه ایشان را به زانو در آورید [و در دست شما اسیر شوند] آنگاه بندهایشان را سخت استوار کنید، و پس از آن یا منت نهید [و رها کنید بدون فدیه‌] یا فدیه بستانید [و رها کنید]، تا اهل کارزار بارهای [سلاح‌] خود را فرو گذارد، چنین است [حکم الهی‌]، و اگر خداوند می‌خواست از ایشان انتقام می‌کشید، ولی [چنین کرد] تا بعضی از شما را به بعضی دیگر بیازماید، و کسانی که در راه خدا کشته شده‌اند، هرگز [خداوند] اعمالشان را تباه [و بی‌ارزش‌] نخواهد کرد'),
(4550, 47, 5, 'به زودی ایشان را هدایت می‌کند و کار و بارشان را به صلاح می‌آورد'),
(4551, 47, 6, 'و به بهشتی که به آنان شناسانده است، در می‌آوردشان‌'),
(4552, 47, 7, 'ای کسانی که ایمان آورده‌اید اگر [دین‌] خدا را یاری دهید، شما را یاری می‌دهد و گامهایتان را استوار می‌دارد'),
(4553, 47, 8, 'و کسانی که کفرورزیده‌اند بدا به حالشان، [خداوند] اعمالشان را تباه کرده است‌'),
(4554, 47, 9, 'چرا که [وحی‌] فرو فرستاده الهی را ناخوش دارند، پس اعمالشان را تباه [و بی‌ارزش‌] گرداند'),
(4555, 47, 10, 'آیا در زمین سیر و سفر نکرده‌اند که بنگرند سرانجام کسانی که پیش از آنان بوده‌اند چگونه بود، که خداوند نابودشان کرد، و سرنوشت کافران همانند آن است‌'),
(4556, 47, 11, 'این از آن است که خداوند سرور مؤمنان است و کافران سروری ندارند'),
(4557, 47, 12, 'بی‌گمان خداوند کسانی را که ایمان آورده‌اند و کارهای شایسته کرده‌اند، به بوستانهایی در می‌آورد که جویباران از فرودست آن جاری است، و کافران [از ظواهر زندگی‌] بهره بر می‌گیرند و همان گونه که چارپایان می‌خورند، می‌خورند و [می‌خوابند] و آتش [دوزخ‌] جایگاه ایشان است‌'),
(4558, 47, 13, 'و چه بسیار [مردم‌] شهرها که از شهر تو که آواره‌ات کرد نیرومندتر بودند، نابودشان کردیم، و یاوری نداشتند'),
(4559, 47, 14, 'آیا کسی که به طریقه روشنی از جانب پروردگار خویش است، همانند کسی است که بد عملی‌اش در نظرش آراسته جلوه داده شده و از هوی و هوسهایشان پیروی می‌کنند'),
(4560, 47, 15, 'وصف بهشتی که به پرهیزگاران وعده داده شده است [چنین است که‌] در آن جویهایی از آب ناگندا، و جویهایی از شیری که مزه‌اش تغییرناپذیر است، و جویهایی از شراب لذت‌بخش آشامندگان، و جویهایی از عسل پالوده [روان‌] است، و برایشان در آنجا همه گونه میوه هست، و نیز آمرزشی از جانب پروردگارشان، [آیا چنین کسی‌] همانند کسی است که جاودانه در آتش [دوزخ‌] است؟ و به آنان آبی جوشان نوشانده می‌شود که دل و روده‌هایشان را پاره پاره می‌کند'),
(4561, 47, 16, 'و از ایشان کسانی هستند که [ظاهرا] به تو گوش می‌سپارند، سپس که از نزدت بیرون می‌روند، به دانش یافتگان گویند هم اکنون چه گفت؟ اینان کسانی هستند که خداوند به دلهایشان مهر نهاده است و از هوی و هوسهایشان پیروی کرده‌اند'),
(4562, 47, 17, 'و رهیافتگان را هدایت افزود و [راه و رسم‌] پارسایی‌شان آموخت‌'),
(4563, 47, 18, 'پس انتظار ندارند مگر قیامت را که به ناگهان به سراغشان آید، که به راستی نشانه‌های آن ظاهر شده است، پس آنگاه که فراز آیدشان، چه پندی می‌خواهند بگیرند؟'),
(4564, 47, 19, 'بدان که فی‌الحقیقه خدایی جز خداوند نیست، و برای گناهت، و نیز برای مردان و زنان مؤمن آمرزش بخواه، و خداوند [جای‌] جنب و جوش و آرام و قرارتان را می‌داند'),
(4565, 47, 20, 'و [بعضی از] مؤمنان گویند چرا سوره‌ای [شامل حکم جهاد] فرو فرستاده نمی‌شود، آنگاه چون سوره‌ای از محکمات فرو فرستاده می‌شود و در آن کارزار [با مشرکان‌] یاد می‌شود، بیماردلان را بینی که به تو با نگاه کسی که از [سکرات‌] مرگ بیهوش شده باشد، می‌نگرند، بدا به حال ایشان‌'),
(4566, 47, 21, 'اطاعت باید و سخنی نیک، آنگاه چون کار قطعی شود، اگر با خداوند صادق باشند، بی‌شک برایشان بهتر است‌'),
(4567, 47, 22, 'چه بسا چون دست یابید در این سرزمین فتنه و فساد کنید و پیوند خویشاوندانتان را بگسلید'),
(4568, 47, 23, 'اینان کسانی هستند که خداوند لعنتشان کرده و [از شنیدن و دیدن حق‌] ناشنوا و نابیناشان ساخته است‌'),
(4569, 47, 24, 'آیا در قرآن تامل نمی‌کنند، یا بر دلها قفلها[ی غفلت‌]شان افتاده است؟'),
(4570, 47, 25, 'بی‌گمان کسانی که پس از آنکه راه هدایت بر آنان روشن شده است، به آن پشت کردند [و به گذشته برگشتند]، شیطان آن را در چشمشان آراسته است و به آنان مهلت و میدان داده است‌'),
(4571, 47, 26, 'این از آن است که به کسانی که [وحی‌] فرو فرستاده الهی را ناخوش داشته‌اند، گویند زودا که در بعضی کارها از شما اطاعت کنیم، و خداوند پنهانکاریشان را می‌داند'),
(4572, 47, 27, 'پس چگونه باشد حالشان آنگاه که فرشتگان جانشان را بگیرند و چهره‌ها و پشتهایشان را فرو کوبند؟'),
(4573, 47, 28, 'این از آن است که از آنچه خداوند را به خشم آورد پیروی می‌کنند و خشنودی او را ناخوش دارند، و [خداوند] اعمالشان را تباه [و بی‌ارزش‌] کند'),
(4574, 47, 29, 'آیا بیماردلان پنداشته‌اند که خداوند هرگز حقد و حسدهایشان را آشکار نمی‌سازد؟'),
(4575, 47, 30, 'و اگر بخواهیم آنان را به تو می‌نمایانیم، آنگاه ایشان را به سیمایشان می‌شناسی، و آنان را از آهنگ سخنشان می‌شناسی، و خداوند [ظاهر و باطن‌] اعمالتان را می‌داند'),
(4576, 47, 31, 'و همه گونه می‌آزماییمتان تا از میان شما مجاهدان و صابران را معلوم بداریم، و احوالتان را بشناسانیم‌'),
(4577, 47, 32, 'بی‌گمان کسانی که کفرورزیده‌اند و [مردم را] از راه خدا باز داشته‌اند، و پس از آنکه راه هدایت بر آنان روشن شده است، با پیامبر مخالفت ورزیده‌اند، هرگز به خداوند زیانی نمی‌رسانند و زودا که [خداوند] اعمالشان را تباه و [بی‌ارزش‌] گرداند'),
(4578, 47, 33, 'ای کسانی که ایمان آورده‌اید از خداوند و از پیامبر اطاعت کنید و اعمالتان را باطل مگردانید'),
(4579, 47, 34, 'بی‌گمان کسانی که کفرورزیده‌اند و [مردم را] از راه خدا باز داشته‌اند، سپس در حالی که کافر بوده‌اند در گذشته‌اند، هرگز خداوند آنان را نمی‌آمرزد'),
(4580, 47, 35, 'پس سستی‌مورزید و از در صلح و سازش در نیایید که شما برتر و [چیره‌]اید و خداوند با شماست و [پاداش‌] اعمال شما را نمی‌کاهد'),
(4581, 47, 36, 'همانا زندگانی دنیا [مانند] بازیچه و سرگرمی است و اگر ایمان داشته باشید و پروا و پرهیز بورزید، پاداشهایتان را به شما می‌پردازد، و [همه‌] اموال شما را از شما نمی‌گیرد'),
(4582, 47, 37, 'اگر آن را از شما بطلبد و بر شما سخت بگیرد [چه بسا] بخل ورزید و ناهمدلی شما را آشکار سازد'),
(4583, 47, 38, 'همین شما هستید که از شما خواسته می‌شود که در راه خدا انفاق کنید، آنگاه بعضی از شما هستند که بخل می‌ورزند، و هر کس بخل ورزد همانا از خود دریغ می‌ورزد، و خداوند بی‌نیاز است و شما نیازمندانید، و اگر روی بگردانید قومی غیر شما را جانشین شما می‌سازد، آنگاه آنان مانند شما نخواهند بود');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'همانا گشایشی آشکار در کار تو پدید آوردیم‌'),
(4585, 48, 2, 'تا سرانجام خداوند گناه نخستین و اخیر [امت‌] تو را برای تو بیامرزد و نعمتش را بر تو به کمال برساند و تو را به راهی راست هدایت فرماید'),
(4586, 48, 3, 'و خداوند تو را به نصرتی پیروزمندانه یاری دهد'),
(4587, 48, 4, 'هموست که آرامش را در دلهای مؤمنان جای داد، تا ایمانی بر ایمانشان بیفزایند، و سپاههای آسمان و زمین خداوند راست، و خداوند دانای فرزانه است‌'),
(4588, 48, 5, 'تا سرانجام مردان مؤمن و زنان مؤمن را به بوستانهایی که جویباران از فرو دست آن جاری است درآورد، که جاودانه در آنجا هستند، و سیئاتشان را از ایشان بزداید، و این نزد خداوند رستگاری بزرگی است‌'),
(4589, 48, 6, 'و تا مردان منافق و زنان منافق و مردان مشرک و زنان مشرک را که درباره خداوند بداندیشند، عذاب کند، گردش بد روزگار بر آنان باد و خداوند بر آنان خشم گرفته و لعنت آورده و برایشان دوزخ را آماده ساخته است و بد سرانجامی است‌'),
(4590, 48, 7, 'و سپاههای آسمان و زمین خداوند راست، و خداوند پیروزمند فرزانه است‌'),
(4591, 48, 8, 'همانا ما تو را گواه و مژده‌بخش و هشداردهنده فرستاده‌ایم‌'),
(4592, 48, 9, 'تا به خداوند و پیامبر او ایمان آورید و او [پیامبر] را گرامی دارید و بزرگ شمارید و [خداوند را] بامدادان و شامگاهان تسبیح گویید'),
(4593, 48, 10, 'بی‌گمان کسانی که با تو بیعت می‌کنند، در واقع با خداوند بیعت می‌کنند، دست خداوند برفراز دست ایشان است، پس هر کس که پیمان شکند، همانا به زیان خویش پیمان شکسته است، و هر کس به آنچه با خداوند پیمان بسته است، وفا کند، زودا که به او پاداش بزرگ ببخشد'),
(4594, 48, 11, 'به زودی واپس‌ماندگان اعرابی [/جهادگریزان بادیه‌نشین‌] به تو گویند اموال ما و خانواده‌هایمان ما را [از همراهی با شما] مشغول داشت، پس برای ما آمرزش بخواه، به زبانهایشان چیزی را می‌گویند که در دلهایشان نیست، بگو پس چه کسی در برابر خداوند -اگر در حق شما زیانی یا در حق شما سودی بخواهداز شما حمایت خواهد کرد؟ آری خداوند به آنچه می‌کنید آگاه است‌'),
(4595, 48, 12, 'حق این است که گمان می‌کردید که پیامبر و مؤمنان هرگز به سوی خانواده‌هایشان باز نمی‌گردند، و این امر در دلهایتان آراسته جلوه داده شد، و بداندیشی کردید و قومی ورشکسته شدید'),
(4596, 48, 13, 'و هر کس به خداوند و پیامبر او ایمان نیاورد [بداند که‌] ما برای کافران آتشی افروخته آماده ساخته‌ایم‌'),
(4597, 48, 14, 'و فرمانروایی آسمانها و زمین خداوند راست، که هر کس را بخواهد می‌آمرزد و هر کس را بخواهد عذاب می‌کند، و خداوند آمرزگار مهربان است‌'),
(4598, 48, 15, 'چون به سوی غنایم راه برید که آن را به دست آورید، زودا که واپس ماندگان گویند ما را بگذارید که از شما پیروی کنیم می‌خواهند حکم الهی را دگرگون کنند، بگو هرگز از ما پیروی نخواهید کرد، خداوند پیشاپیش درباره شما چنین گفته است، زودا که [در پاسخ شما] گویند خیر، شما به ما رشک می‌برید، حق این است جز اندکی در نمی‌یابند'),
(4599, 48, 16, 'به واپس ماندگان اعرابی [/جهادگریزان بادیه‌نشین‌] بگو زودا که به سوی قومی ستیزه‌جوی سهمگین خوانده شوید که با ایشان کارزار کنید، یا آنکه آنان مسلمان شوند، پس اگر اطاعت کنید، خداوند پاداشی نیکو به شما بدهد و اگر روی برتابید، چنانکه پیشتر هم روی برتافته بودید، به عذابی دردناک عذابتان کند'),
(4600, 48, 17, '[البته‌] بر نابینا ایرادی نیست، و بر لنگ ایرادی نیست، و بر بیمار هم ایرادی نیست، و هر کس از خداوند و پیامبرش اطاعت کند، به بوستانهایی که جویباران از فرو دست آن جاری است، درآوردشان، و هر کس روی برتابد، به عذابی دردناک عذابش کند'),
(4601, 48, 18, 'به راستی که خداوند از مؤمنان، چون با تو در زیر آن درخت بیعت کردند، خشنود شد، و می‌دانست که در دلهایشان چیست، و آنگاه آرامش [خود] را بر آنان نازل کرد و به پیروزی‌ای نزدیک پاداششان داد'),
(4602, 48, 19, 'و نیز غنایمی بسیار که به آن دست‌یابند، و خداوند پیروزمند فرزانه است‌'),
(4603, 48, 20, 'خداوند به شما غنایم بسیاری وعده کرده است که بر آن دست یابید، و این را برای شما پیش انداخت، و دستان مردم [مخالف‌] را از شما بازداشت، تا این امر نشانه عبرتی برای مؤمنان باشد، و شما را به راهی راست هدایت کند'),
(4604, 48, 21, 'و موردی دیگر که هنوز به آن دست نیافته‌اید، ولی خداوند به آن احاطه دارد، و خداوند بر همه چیز تواناست‌'),
(4605, 48, 22, 'و اگر کافران با شما به کارزار برخیزند، بی‌شک به شما پشت خواهند کرد، سپس نه یاری و نه یاوری یابند'),
(4606, 48, 23, 'این سنت الهی است که پیشتر نیز جاری بوده است، و هرگز در سنت الهی تغییر و تبدیلی نخواهی یافت‌'),
(4607, 48, 24, 'و هموست که پس از آنکه شما را بر ایشان پیروزی داد، در میان مکه دستان ایشان را از شما، و دستان شما را از ایشان بازداشت، و خداوند به آنچه می‌کنید بیناست‌'),
(4608, 48, 25, 'آنان بودند که کفرورزیدند و شما را از مسجدالحرام باز داشتند و نگذاشتند قربانی [شما] که باز داشته شده بود، به محل [قربانگاه‌]اش برسد، و اگر [در مکه‌] مردان و زنان [مستضعف‌] با ایمانی نبودند که [ممکن بود] بی‌آنکه آنان را بشناسید، ندانسته پایمالشان کنید و تاوانشان بر شما بماند [فرمان حمله به مکه می‌دادیم‌] تا خدا هر که را بخواهد در جوار رحمت خویش درآورد اگر [کافران و مؤمنان‌] از هم متمایز می‌شدند، قطعا از میان آنان کافران را به عذاب دردناکی معذب می‌داشتیم‌'),
(4609, 48, 26, 'چنین بود که کافران در دلهایشان حمیت -حمیت جاهلیتآوردند، ولی خداوند آرامشش را بر پیامبرش و بر مؤمنان نازل کرد و آنان را پایبند حکم تقوا ساخت، و به آن سزاوارتر و اهل آن بودند، و خداوند به همه‌چیز داناست‌'),
(4610, 48, 27, 'به راستی که خداوند رؤیای پیامبرش را به حقیقت پیوست، که شما ان شاء الله در امن و امان و حلق و تقصیر کرده وارد مسجدالحرام خواهید شد بی‌آنکه بیمی داشته باشید، و او چیزی را که شما نمی‌دانید می‌داند و جز این نیز پیروزی‌ای نزدیک نصیب شما گردانده است‌'),
(4611, 48, 28, 'او کسی است که پیامبرش را با هدایت و دین حق فرستاده است، تا آن را بر همه ادیان پیروز گرداند، و خداوند گواهی را بس‌'),
(4612, 48, 29, 'محمد پیامبر الهی است و کسانی که با او هستند بر کافران سختگیر و با خودشان مهربانند، آنان را راکع و ساجد بینی که در طلب بخشش و خشنودی خداوند، نشانه آنان در چهره‌هایشان از اثر سجود [آشکار] است، و این است توصیف آنان در تورات، و توصیف آنان در انجیل هست، همانند نهالی که جوانه‌اش را برآورد، و آن را نیرومند سازد و ستبر گردد و بر ساقه‌هایش بایستد، و کشاورزان را شاد و شگفت‌زده سازد، تا از [دیدن‌] ایشان کافران را به خشم آورد، خداوند به کسانی از ایشان که ایمان آورده‌اند و کارهای شایسته کرده‌اند، آمرزش و پاداشی بزرگ وعده داده است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'ای مؤمنان بر حکم خداوند و پیامبر او پیشدستی نکنید، و از خداوند پروا داشته باشید، بی‌گمان خداوند شنوای داناست‌'),
(4614, 49, 2, 'ای کسانی که ایمان آورده‌اید صداهایتان را از صدای پیامبر بلندتر مسازید، و در سخن با او، مانند بلند حرف زدنتان با همدیگر، بلند حرف مزنید مبادا که اعمالتان تباه شود و آگاه نباشید'),
(4615, 49, 3, 'بی‌گمان کسانی که صداهایشان را نزد پیامبر خدا [آهسته و] پوشیده می‌دارند، اینانند که خداوند دلهایشان را برای تقوا پالوده است، ایشان را آمرزش و پاداشی بزرگ است‌'),
(4616, 49, 4, 'بی‌گمان کسانی که تو را از پشت در حجره‌ها صدا می‌زنند، بیشترشان نابخردانند'),
(4617, 49, 5, 'و اگر آنان صبر می‌کردند که تو بر ایشان بیرون آیی، بی‌شک بر ایشان بهتر بود، و خداوند آمرزگار مهربان است‌'),
(4618, 49, 6, 'ای مؤمنان اگر فرد فاسقی خبری برایتان آورد، [در آن‌] بررسی کنید مبادا نادانسته به قومی زیان رسانید، آنگاه به خاطر کاری که کرده‌اید پشیمان شوید'),
(4619, 49, 7, 'و بدانید که پیامبر خدا در میان شماست که اگر در بسیاری از امور از شما پیروی کند، به مشقت افتید، ولی خداوند ایمان را خوشایند شما قرار داد و آن را در دلهایتان آراست، و کفر و فسق و عصیان را برای شما ناخوشایند ساخت، اینانند که رهیافتگانند'),
(4620, 49, 8, 'بخشش و نعمتی از جانب خداوند است، و خداوند دانای فرزانه است‌'),
(4621, 49, 9, 'و اگر دو طایفه از مؤمنان با هم در افتند، میان آنان سازش دهید، و اگر یکی از آنها بر دیگری تعدی کرد، پس با آنکه تعدی پیشه کرده است کارزار کنید، تا آنکه به سوی امر الهی باز آید، و اگر باز آمد، آنگاه میان آنان دادگرانه سازش دهید، و به داد بکوشید که خداوند دادگران را دوست دارد'),
(4622, 49, 10, 'همانا مؤمنان [مسلمانان‌] بردارند، پس بین برادرانتان آشتی برقرار سازید، و از خداوند پروا کنید، باشد که مشمول رحمت شوید'),
(4623, 49, 11, 'ای مؤمنان نباید که قومی، قوم دیگر را به ریشخند بگیرد، چه بسا اینان از آنان بهتر باشند، و نیز نباید زنانی زنان دیگر را [ریشخند کنند] چه بسا اینان از آنان بهتر باشند، و در میان خویش عیب‌جویی مکنید، و یکدیگر را به لقبهای بد مخوانید پس از ایمان، پرداختن به فسق، بد رسمی است، و هر کس [از این کار] بازنگردد، اینانند که ستمگرانند'),
(4624, 49, 12, 'ای مؤمنان از بسیاری از گمانها پرهیز کنید، چرا که بعضی از گمانها گناه است، و [در کار دیگران‌] تجسس مکنید، و بعضی از شما از بعضی دیگر غیبت نکند، آیا هیچ کدام از شما خوش دارد که گوشت برادر مرده‌اش را بخورد، که از آن تنفر دارید، و از خداوند پروا کنید که بی‌گمان خداوند توبه‌پذیر مهربان است‌'),
(4625, 49, 13, 'هان ای مردم همانا ما شما را از یک مرد و یک زن آفریده‌ایم و شما را به هیئت اقوام و قبایلی در آورده‌ایم تا با یکدیگر انس و آشنایی یابید، بی‌گمان گرامی‌ترین شما در نزد خداوند پرهیزگارترین شماست، که خداوند دانای آگاه است‌'),
(4626, 49, 14, 'اعرابیان گفتند ایمان آورده‌ایم بگو هنوز ایمان [حقیقی‌] نیاورده‌اید، بهتر است بگویید اسلام آورده‌ایم چرا که هنوز ایمان به [ژرفنای‌] دلهایتان راه نیافته است، و اگر از خداوند و پیامبر او اطاعت کنید، چیزی از [پاداش‌] اعمال شما نمی‌کاهد، بی‌گمان خداوند آمرزگار مهربان است‌'),
(4627, 49, 15, 'همانا مؤمنان کسانی هستند که به خداوند و پیامبر او ایمان آورده‌اند، سپس شک و شبهه نیاورده‌اند، و در راه خدا به مال و جانشان جهاد کرده‌اند، اینانند که راستگویانند'),
(4628, 49, 16, 'بگو آیا دینتان را به خداوند می‌شناسانید؟ حال آنکه خداوند آنچه در آسمانهاست و آنچه در زمین است، می‌داند، و خداوند به همه چیز داناست‌'),
(4629, 49, 17, 'بر تو منت می‌نهند که اسلام آورده‌اند، بگو اسلام آوردنتان را بر من منت منهید، بلکه خداوند است که اگر راست می‌گویید، بر شما منت می‌نهد که شما را به [راه‌] ایمان هدایت کرده است‌'),
(4630, 49, 18, 'بی‌گمان خداوند نهانیهای آسمان و زمین را می‌داند و خداوند به آنچه می‌کنید بیناست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'ق [ قاف‌]، سوگند به قرآن مجید'),
(4632, 50, 2, 'حق این است که [کافران‌] از اینکه از میان خودشان هشداردهنده‌ای به سراغ آنان آمده است، شگفت‌زده شده‌اند، و کافران گویند این چیزی گفت‌آور است‌'),
(4633, 50, 3, 'آیا چون مردیم و خاک شدیم [از نو زنده شویم؟]، این بازگشتی بعید است‌'),
(4634, 50, 4, 'به راستی می‌دانیم که زمین از ایشان چه می‌کاهد، و نزد ما کتابی است محفوظ'),
(4635, 50, 5, 'حق این است که [دین و پیامبر] حق را چون فراز آمدشان دروغ شمردند، و ایشان در کاری سردرگمند'),
(4636, 50, 6, 'آیا به آسمان فرازشان ننگریسته‌اند، که چگونه آن را برافراشته‌ایم و آن را آراسته‌ایم، و هیچ خللی ندارد'),
(4637, 50, 7, 'و زمین را گسترانده‌ایم، و در آن کوهها را درانداخته‌ایم، و در آن از هرگونه خرمی رویانده‌ایم‌'),
(4638, 50, 8, 'برای روشنگری و یادآوری هر بنده اهل انابت‌'),
(4639, 50, 9, 'و از آسمان آبی پربرکت فرو فرستادیم، آنگاه بدان بوستانها و دانه درودنی رویانده‌ایم‌'),
(4640, 50, 10, 'و درختان خرمای بلند بالا که میوه توبرتو دارد'),
(4641, 50, 11, 'تا روزی بندگان باشد، و بدان سرزمینی پژمرده را زنده کردیم، رستاخیز هم همین‌گونه است‌'),
(4642, 50, 12, 'پیش از آنان قوم نوح و اصحاب رس و ثمود تکذیب پیشه کردند'),
(4643, 50, 13, 'و نیز عاد و فرعون و قوم لوط'),
(4644, 50, 14, 'و اصحاب ایکه، و قوم تبع همگان پیامبران را دروغ‌زن شمردند، آنگاه عقاب من بر آنان تعلق گرفت‌'),
(4645, 50, 15, 'آیا در آفرینش نخستین درمانده بودیم؟ [هرگز]، بلکه ایشان از آفرینش جدید شک و شبهه دارند'),
(4646, 50, 16, 'و به راستی که انسان را آفریده‌ایم و می‌دانیم که نفسش چه وسوسه‌ای به او می‌کند، و ما به او از رگ جان نزدیکتریم‌'),
(4647, 50, 17, 'چون فراگیران که از راست و از چپ او کمین‌دار هستند، [کارها و سخنان او را] فراگیرند'),
(4648, 50, 18, 'سخنی به زبان نمی‌آورد، مگر آنکه نگهبان حاضر و ناظری نزد اوست‌'),
(4649, 50, 19, 'و مستی مرگ، حقیقت را پدید آورد، این همان است که از آن کناره می‌گرفتی‌'),
(4650, 50, 20, 'و در صور دمیده شود، این روز وعده عذاب است‌'),
(4651, 50, 21, 'و همراه هر کسی راهبری و شاهدی فراز آید'),
(4652, 50, 22, '[و به او گویند] به راستی که از این امر در غفلت بودی و حال پرده‌ات را از تو برطرف ساخته‌ایم، و امروز دیده‌ات تیزبین است‌'),
(4653, 50, 23, 'و همنشین او گوید این همان است که نزد من آماده است‌'),
(4654, 50, 24, 'هر کفر پیشه ستیزه‌جویی را به جهنم اندازید'),
(4655, 50, 25, '[همان که‌] بازدارنده خیر و تجاوزکار شکاک است‌'),
(4656, 50, 26, 'کسی که در جنب خداوند به خدایی دیگر قائل است، پس او را در [جایگاه‌] عذاب سهمگین بیندازید'),
(4657, 50, 27, 'همنشین او گوید پروردگارا من او را طغیانگر نساخته‌ام، ولی خودش در گمراهی دور و دراز بود'),
(4658, 50, 28, 'فرماید نزد من ستیزه‌جویی مکنید، و به راستی که پیشاپیش وعده عذاب را [برای آگاهی شما] فرستاده بودم‌'),
(4659, 50, 29, 'در نزد من آن حکم دیگرگون نشود، و [در عین حال‌] من در حق بندگان ستمگر نیستم‌'),
(4660, 50, 30, 'روزی که به جهنم گوییم آیا پرشدی؟ و گوید آیا باز هم بیشتر هست؟'),
(4661, 50, 31, 'و بهشت برای پرهیزگاران نزدیک آورده شود و دور نباشد'),
(4662, 50, 32, 'این همان است که به شما وعده داده شده است، [و] خاص هر توبه کار [ادب‌] نگاه دار'),
(4663, 50, 33, 'همان کسی که به نادیده از خداوند رحمان بیمناک باشد، و دلی پرانابت پیش آورد'),
(4664, 50, 34, '[گوییم‌] به سلامت [و امن و امان‌] وارد آن [بهشت‌] شوید، این روز [آغاز] جاودانگی است‌'),
(4665, 50, 35, 'در آنجا ایشان راست هر چه خواهند، و نزد ما افزونتر هم هست‌'),
(4666, 50, 36, 'و چه بسیار پیش از آنان نسلهایی را نابود کردیم که از ایشان دراز دست‌تر بودند، که در گوشه و کنار شهرها جستجو کردند که آیا گریزگاهی هست‌'),
(4667, 50, 37, 'بی‌گمان در این برای کسی که صاحبدل باشد یا سمع قبول داشته و شاهد باشد، پندآموزی است‌'),
(4668, 50, 38, 'و به راستی که آسمانها و زمین و مابین آنها را در شش روز آفریدیم و به ما ماندگی نرسید'),
(4669, 50, 39, 'پس بر آنچه می‌گویند شکیبایی‌ورز و سپاسگزارانه پروردگارت را پیش از طلوع خورشید و پیش از غروب [آن‌] تسبیح گوی‌'),
(4670, 50, 40, 'و در شب و به دنباله سجده [و نماز واجب‌] نیز او را نیایش کن‌'),
(4671, 50, 41, 'و گوش بدار روزی را که منادی از جایی نزدیک ندا در دهد'),
(4672, 50, 42, 'روزی که صیحه را به حق بشنوند، آنگاه روز رستاخیز است‌'),
(4673, 50, 43, 'بی‌گمان ماییم که زنده می‌داریم و می‌میرانیم و سرانجام [همه چیز] به سوی ماست‌'),
(4674, 50, 44, 'روزی که زمین شتابان بر آنان بشکافد [و از گورها بیرون آیند] این [هنگام‌] حشر است که [اقامه آن‌] بر ما آسان است‌'),
(4675, 50, 45, 'ما به آنچه می‌گویند آگاه‌تریم و تو زورگوی بر آنان نیستی، پس هر کس را که از وعده عذاب من می‌ترسد، به قرآن پند بده‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'سوگند به بادهای پراکنده‌گر'),
(4677, 51, 2, 'و به ابرهای سنگین‌بار'),
(4678, 51, 3, 'و به کشتیهای آسان گذار'),
(4679, 51, 4, 'و به فرشتگان تقسیم‌گر کار'),
(4680, 51, 5, 'که همانا آنچه به شما وعده داده‌اند راست و درست است‌'),
(4681, 51, 6, 'و جزا[ی اخروی‌] واقعی است‌'),
(4682, 51, 7, 'و سوگند به آسمان تو بر تو'),
(4683, 51, 8, 'که شما سخنهای گوناگون پیش آرید'),
(4684, 51, 9, 'هر کس که از پیش، باز گردانده شده است از [ایمان به‌] آن باز گردانده شود'),
(4685, 51, 10, 'مرگ بر دروغ زنان‌'),
(4686, 51, 11, 'کسانی که در بی‌خبری فراموشکارند'),
(4687, 51, 12, 'می‌پرسند که روز جزا چه وقت است‌'),
(4688, 51, 13, 'روزی که ایشان بر آتش [دوزخ‌] عذاب کرده شوند'),
(4689, 51, 14, 'همین عذابتان را، که به شتابش می‌خواستید، بچشید'),
(4690, 51, 15, 'پرهیزگاران در بوستانها و چشمه‌سارانند'),
(4691, 51, 16, 'آنچه پروردگارشان به ایشان بخشیده است پذیرا هستند، که ایشان پیش از این نیکوکار بودند'),
(4692, 51, 17, '[فقط] اندکی از شب را می‌خفتند'),
(4693, 51, 18, 'و در سحرگاهان استغفار می‌کردند'),
(4694, 51, 19, 'و در اموالشان سهمی برای سائل و محروم بود'),
(4695, 51, 20, 'و در زمین مایه‌های عبرتی برای اهل یقین هست‌'),
(4696, 51, 21, 'و نیز در وجود خودتان آیا [به چشم بصیرت‌] نمی‌نگرید؟'),
(4697, 51, 22, 'و در آسمان مایه روزی شماست و نیز آنچه به شما وعده داده‌اند'),
(4698, 51, 23, 'آری، سوگند به پروردگار آسمان و زمین که آن مانند همین که شما سخن می‌گویید، حق است‌'),
(4699, 51, 24, 'آیا داستان مهمانان گرامی ابراهیم به تو رسیده است؟'),
(4700, 51, 25, 'که چون بر او وارد شدند گفتند سلام، گفت سلام [بر شما] [و در دل گفت ایشان‌] گروهی ناشناخته‌[اند]'),
(4701, 51, 26, 'سپس به خانواده‌اش روی کرد، آنگاه گوساله‌ای فربه [و بریان‌] به میان آورد'),
(4702, 51, 27, 'پس آن را نزدیک ایشان نهادند گفت چرا نمی‌خورید؟'),
(4703, 51, 28, 'سپس از آنان ترسی در دل یافت، گفتند مترس و او را به فرزندی دانا بشارت دادند'),
(4704, 51, 29, 'آنگاه زنش با آوایی [بلند] رو به او آورد و بر چهره‌اش چک زد و گفت [چگونه فرزند بزایم که من‌] پیرزنی سترون هستم‌'),
(4705, 51, 30, '[فرشتگان‌] گفتند پروردگارت چنین فرموده است، که او فرزانه داناست‌'),
(4706, 51, 31, 'گفت ای فرشتگان کار و بار شما چیست؟'),
(4707, 51, 32, 'گفتند ما به سر وقت قومی گنهکار فرستاده شده‌ایم‌'),
(4708, 51, 33, 'تا بر آنان سنگواره‌ای از گل فروباریم‌'),
(4709, 51, 34, 'که [هر یک‌] از جانب پروردگارت نشان کرده است و خاص تجاوزکاران است‌'),
(4710, 51, 35, 'آنگاه هر کس را که از مؤمنان بود [از آنجا] بیرون بردیم‌'),
(4711, 51, 36, 'چندانکه در آنجا جز خانه‌ای از آن مسلمانان، نیافتیم‌'),
(4712, 51, 37, 'و در آنجا نشانه‌ای برای کسانی که از عذاب دردناک می‌ترسند، باقی نهادیم‌'),
(4713, 51, 38, 'و در مورد موسی، آنگاه که او را با حجتی آشکار به سوی فرعون فرستادیم‌'),
(4714, 51, 39, 'سپس رو به سپاه خویش برگشت و گفت [او] جادوگر یا دیوانه است‌'),
(4715, 51, 40, 'سرانجام او و سپاهیانش را فروگرفتیم، سپس او را که نکوهیده بود به دریا انداختیم و [غرقه ساختیم‌]'),
(4716, 51, 41, 'و در مورد عاد، آنگاه که بر آنان بادی سترون فرستادیم‌'),
(4717, 51, 42, 'که هیچ چیزی که بر آن می‌گذشت باقی نمی‌گذاشت، مگر آنکه آن را مانند خاک و خاشاک می‌ساخت‌'),
(4718, 51, 43, 'و در مورد ثمود آنگاه که به آنان گفته شد که تا زمانی [معین‌] از زندگی برخوردار شوید'),
(4719, 51, 44, 'سپس از فرمان پروردگارشان سرپیچیدند، آنگاه صاعقه‌[ی مرگ‌] آنان را که [درمانده‌وار] می‌نگریستند، فروگرفت‌'),
(4720, 51, 45, 'آنگاه توان ایستادن هم نداشتند و کین‌ستان نبودند'),
(4721, 51, 46, 'و قوم نوح از آن پیش، که آنان قومی نافرمان بودند'),
(4722, 51, 47, 'و آسمان را توانمندانه برافراشتیم و ما توانمندیم‌'),
(4723, 51, 48, 'و زمین را گستراندیم چه نیکو گستراننده‌ایم‌'),
(4724, 51, 49, 'و از هر چیز گونه‌هایی آفریدیم باشد که پند گیرید'),
(4725, 51, 50, 'پس در خداوند بگریزید، که من برای شما از جانب او هشداردهنده‌ای آشکارم‌'),
(4726, 51, 51, 'و در جنب خداوند خدایی دیگر قائل مشوید که من برای شما از جانب او هشداردهنده‌ای آشکارم‌'),
(4727, 51, 52, 'بدین‌سان برای پیشینیان آنان هیچ پیامبری نیامد مگر آنکه گفتند [او] جادوگر یا دیوانه است‌'),
(4728, 51, 53, 'آیا همدیگر را بدان سفارش کرده‌اند؟ حق این است که قومی طغیانگرند'),
(4729, 51, 54, 'پس، از آنان روی بگردان که تو سزاوار سرزنش نیستی‌'),
(4730, 51, 55, 'و پند بده که اندرز مؤمنان را سود دهد'),
(4731, 51, 56, 'و جن و انس را جز برای آنکه مرا بپرستند، نیافریده‌ام‌'),
(4732, 51, 57, 'از آنان رزقی نخواسته‌ام و نخواسته‌ام که به من خوراک دهند'),
(4733, 51, 58, 'بی‌گمان خداوند است که روزی‌بخش نیرومند استوار است‌'),
(4734, 51, 59, 'آری برای [این‌] ستمکاران هم نصیبی [از عذاب‌] همانند نصیب یارانشان است، نباید که به شتابش از من بطلبند'),
(4735, 51, 60, 'پس وای بر کافران از آن روزشان که وعده‌اش را به ایشان داده‌اند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'سوگند به [کوه‌] طور'),
(4737, 52, 2, 'و به کتابی نوشته‌'),
(4738, 52, 3, 'در ورقی سرگشاده‌'),
(4739, 52, 4, 'و به بیت‌المعمور'),
(4740, 52, 5, 'و به سقف برافراشته‌'),
(4741, 52, 6, 'و به دریای سرشار [از آتش‌]'),
(4742, 52, 7, 'که عذاب پروردگارت واقع شدنی است‌'),
(4743, 52, 8, 'و برگردانی ندارد'),
(4744, 52, 9, 'روزی که آسمان به شدت درهم گردد'),
(4745, 52, 10, 'و کوهها به شدت روان شود'),
(4746, 52, 11, 'آری در چنین روزی وای بر دروغ‌انگاران‌'),
(4747, 52, 12, 'کسانی که به ژاژخایی سرگرمند'),
(4748, 52, 13, 'روزی که به سوی آتش جهنم به شدت رانده شوند'),
(4749, 52, 14, '[و گویند] این همان آتشی است که آن را دروغ می‌انگاشتید'),
(4750, 52, 15, 'آیا پس این جادوست، یا آنکه شما [به چشم بصیرت‌] نمی‌نگرید؟'),
(4751, 52, 16, 'وارد آن شوید، و چه صبر کنید، چه صبر نکنید، برایتان یکسان است، فقط در برابر آنچه کرده‌اید جزا می‌یابید'),
(4752, 52, 17, 'پرهیزگاران در بوستانها و ناز و نعمت‌اند'),
(4753, 52, 18, 'از آنچه پروردگارشان به آنان ارزانی داشته است، خرمند، و [از اینکه‌] پروردگارشان از عذاب جهنم در امانشان داشته است‌'),
(4754, 52, 19, 'به خاطر آنچه کرده‌اید، به گوارایی بخورید و بیاشامید'),
(4755, 52, 20, '[آنان‌] بر تختهای رج زده تکیه زده‌اند و آنان را جفت حوریان درشت چشم گردانیم‌'),
(4756, 52, 21, 'و کسانی که ایمان آورده‌اند و زاد و رودشان در ایمان از ایشان پیروی کرده‌اند، زاد و رودشان را به ایشان ملحق سازیم، و چیزی از [جزای‌] عملشان نکاهیم، هر انسانی در گرو کار و کردار خویش است‌'),
(4757, 52, 22, 'و ایشان را پی در پی از میوه‌ها و گوشت [پرندگانی‌] که خوش دارند می‌دهیم‌'),
(4758, 52, 23, 'در آنجا جامی را که نه مایه بیهوده‌گویی و نه گناه است، از دست هم می‌گیرند'),
(4759, 52, 24, 'و بر گرداگرد آنان جوانان [خدمتکار]شان می‌گردند که گویی ایشان مروارید نهفته‌اند'),
(4760, 52, 25, 'و بعضی از آنان به بعضی دیگر به همپرسی روی آورند'),
(4761, 52, 26, 'گویند ما پیش از این، در میان خانواده‌مان بیمناک بودیم‌'),
(4762, 52, 27, 'سپس خداوند بر ما منت نهاد [و نعمت داد] و ما را از عذاب آتشباد [جهنم‌] در امان داشت‌'),
(4763, 52, 28, 'ما پیش از این او را [به دعا و نیایش‌] می‌خواندیم، اوست که نیکوکار مهربان است‌'),
(4764, 52, 29, 'پس پند ده که تو به نعمت پروردگارت نه کاهنی و نه دیوانه‌'),
(4765, 52, 30, 'مگر گویند شاعری است که ما در حق او حوادث روزگار [/مرگ‌] را انتظار می‌کشیم‌'),
(4766, 52, 31, 'بگو منتظر باشید که من هم همراه شما از منتظرانم‌'),
(4767, 52, 32, 'یا مگر خردهایشان آنان را به این امر فرمان می‌دهد، یا مگر ایشان قومی طغیانگرند'),
(4768, 52, 33, 'یا گویند آن را از خود بربافته است، حق این است که ایمان نمی‌ورزند'),
(4769, 52, 34, 'پس اگر راستگو هستند سخنی مانند آن بیاورند'),
(4770, 52, 35, 'یا از هیچ خلق شده‌اند، یا آنکه خودشان خالق [خود]اند؟'),
(4771, 52, 36, 'یا آسمانها و زمین را آفریده‌اند، حق این است که یقین نمی‌ورزند'),
(4772, 52, 37, 'یا گنجینه‌های پروردگارت نزد آنهاست، یا آنان چیرگانند؟'),
(4773, 52, 38, 'یا نردبانی دارند که [بر آن بالا رفته‌] [اسرار را] با آن می‌شنوند، در این صورت شنونده آنان دلیلی آشکار بیاورد'),
(4774, 52, 39, 'یا مگر او [خداوند] را دختران و شما را پسران است؟'),
(4775, 52, 40, 'یا مگر از ایشان مزدی می‌طلبی، و ایشان از تاوانی گرانبارند؟'),
(4776, 52, 41, 'یا مگر غیب در اختیار آنان است و ایشان [هر چه خواهند] می‌نویسند'),
(4777, 52, 42, 'یا مگر نیرنگی در سر دارند، ولی کافران خود نیرنگ خورده‌اند'),
(4778, 52, 43, 'یا مگر ایشان را خدایی غیر از خداوند است، منزه است خداوند از شریکی که می‌انگارند'),
(4779, 52, 44, 'و چون پاره‌ای از آسمان را افتاده بینند، گویند ابری متراکم [و نعمت‌] است‌'),
(4780, 52, 45, 'پس ایشان را رها کن تا روز خاصشان را که در آن بیهوش کرده شوند، دیدار کنند'),
(4781, 52, 46, 'روزی که نیرنگشان به کار آنان نیاید و یاری نیابند'),
(4782, 52, 47, 'و برای ستمکاران عذابی جز این است، ولی بیشترشان نمی‌دانند'),
(4783, 52, 48, 'و تا رسیدن حکم پروردگارت شکیبایی کن، که تو زیر نظر مایی، و چون [از خواب‌] برخاستی سپاسگزارانه پروردگارت را تسبیح گوی‌'),
(4784, 52, 49, 'و در شب و به هنگام غائب شدن ستارگان نیز او را نیایش کن‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'سوگند به ثریا چون فرو گراید'),
(4786, 53, 2, 'که همسخن شما نه سرگشته است و نه گمراه شده است‌'),
(4787, 53, 3, 'و از سر هوای نفس سخن نمی‌گوید'),
(4788, 53, 4, 'آن جز وحیی نیست که به او فرستاده می‌شود'),
(4789, 53, 5, '[فرشته‌] نیرومند او را آموخته است‌'),
(4790, 53, 6, 'برومند است و سپس [در برابر او] در ایستاد'),
(4791, 53, 7, 'و او در افق بالا بود'),
(4792, 53, 8, 'سپس نزدیک شد و فرود آمد'),
(4793, 53, 9, 'تا که [فاصله آنها به قدر] دو کمان شد یا کمتر'),
(4794, 53, 10, 'آنگاه به بنده او آنچه باید وحی کند، وحی کرد'),
(4795, 53, 11, 'دل او در آنچه دید ناراستی نکرد'),
(4796, 53, 12, 'آیا شما با او درباره آنچه دیده است، مجادله می‌کنید؟'),
(4797, 53, 13, 'و به راستی که بار دیگر هم او [جبرئیل‌] را دید'),
(4798, 53, 14, 'در نزدیکی سدرةالمنتهی‌'),
(4799, 53, 15, 'که جنةالماوی هم نزدیک آن است‌'),
(4800, 53, 16, 'آنگاه که [درخت‌] سدره را چیزی که فرو پوشاند، فرو پوشاند'),
(4801, 53, 17, 'دیده‌[اش‌] کژتابی و سرپیچی نکرد'),
(4802, 53, 18, 'به راستی که نشانه‌های بزرگ پروردگارش را دید'),
(4803, 53, 19, 'آیا شما لات و عزی را نگریسته‌اید'),
(4804, 53, 20, 'و آن دیگری منات را که سومین است‌'),
(4805, 53, 21, 'آیا برای شما پسر و برای او [خداوند] دختر است؟'),
(4806, 53, 22, 'در این صورت این تقسیم‌بندی ناعادلانه‌ای است‌'),
(4807, 53, 23, 'آنها چیزی جز نامهایی که شما و پدرانتان نامیده‌اید، نیست، [و] خداوند بدان حجتی فرو نفرستاده است، جز از پندار و خواسته دلها پیروی نمی‌کنند، و حال آنکه به راستی برای آنان از سوی پروردگارشان هدایت آمده است‌'),
(4808, 53, 24, 'یا مگر انسان راست هر چه آرزو کند؟'),
(4809, 53, 25, 'آری انجام و آغاز، خداوند راست‌'),
(4810, 53, 26, 'و چه بسیار فرشته در آسمانهاست که شفاعت آنان سودی ندارد، مگر پس از آنکه خداوند برای کسی که بخواهد و بپسندد، اجازه دهد'),
(4811, 53, 27, 'بی‌ایمانان به آخرتند که فرشتگان را مادینه می‌نامند'),
(4812, 53, 28, 'و ایشان را به آن علمی نیست، جز از پندار پیروی نمی‌کنند و بی‌گمان پندار چیزی از حقیقت را به بار نمی‌آورد'),
(4813, 53, 29, 'پس از کسی که از یاد ما دل می‌گرداند و جز زندگانی دنیا را نمی‌خواهد، روی بگردان‌'),
(4814, 53, 30, 'این منتهای علمشان است، بی‌گمان پروردگارت به کسی که از راه او گمراه شده است آگاه‌تر است، و همو به کسی که راه یافته است، آگاه‌تر است‌'),
(4815, 53, 31, 'و آنچه در آسمانها و آنچه در زمین است خداوند راست، تا سرانجام بد کرداران را بر وفق کار و کردارشان جزا دهد و نیکوکاران را به پاداش نیکو [/بهشت‌] جزا دهد'),
(4816, 53, 32, 'کسانی که از گناهان کبیره و ناشایستیها پرهیز می‌کنند، مگر صغیره، بی‌گمان پروردگارت گسترده آمرزش است، او آنگاه که شما را از زمین پدید آورد، و آنگاه که جنین‌هایی در شکمهای مادرانتان بودید، به شما آگاه‌تر است پس خودتان را پاکدامن مشمرید، او به آنکه [از ناپسند] می‌پرهیزد، آگاه‌تر است‌'),
(4817, 53, 33, 'آیا کسی را که رویگردان شد نگریسته‌ای‌'),
(4818, 53, 34, 'و اندکی بخشید و باز ایستاد'),
(4819, 53, 35, 'آیا نزد او علم غیب است و او [حقایق را] می‌بیند؟'),
(4820, 53, 36, 'یا از آنچه در صحیفه‌های موسی است، آگاهش نکرده‌اند'),
(4821, 53, 37, 'و [صحیفه‌های‌] ابراهیم که عهد به جای آورد'),
(4822, 53, 38, 'که هیچ بر دارنده‌ای بار گناه دیگری را بر ندارد'),
(4823, 53, 39, 'و اینکه برای انسان هیچ چیز نیست مگر آنچه کوشیده است‌'),
(4824, 53, 40, 'و حاصل کوشش او زودا که دیده شود'),
(4825, 53, 41, 'سپس پاداش دهند او را به پاداشی هر چه وافی‌تر'),
(4826, 53, 42, 'و اینکه سرانجام [همه کار و همه چیز] با پروردگار توست‌'),
(4827, 53, 43, 'و اوست که می‌خنداند و می‌گریاند'),
(4828, 53, 44, 'و اوست که می‌میراند و زنده می‌دارد'),
(4829, 53, 45, 'و اوست که زوج نرینه و مادینه را آفرید'),
(4830, 53, 46, 'از نطفه‌ای که [در رحم‌] فرو می‌ریزد'),
(4831, 53, 47, 'و اینکه پدید آوردن نشاه آخرت با اوست‌'),
(4832, 53, 48, 'و اوست که بی‌نیاز کند و سرمایه دهد'),
(4833, 53, 49, 'و اوست که پروردگار شعری است‌'),
(4834, 53, 50, 'و اوست که عاد نخستین را نابود کرد'),
(4835, 53, 51, 'و ثمود را نیز، باقی نگذاشت‌'),
(4836, 53, 52, 'و نیز پیش از آن قوم نوح را، که ایشان ستمکارتر و سرکش‌تر بودند'),
(4837, 53, 53, 'و سرزمین نگونسار را واژگون کرد'),
(4838, 53, 54, 'و آن را چنانکه باید، پوشیده داشت‌'),
(4839, 53, 55, 'پس به کدامیک از نعمتهای پروردگارت شک و شبهه داری؟'),
(4840, 53, 56, 'این هشداردهنده‌ای از هشداردهندگان نخستین است‌'),
(4841, 53, 57, '[قیامت‌] فرارسنده، فرارسید'),
(4842, 53, 58, 'جز خداوند، برای آن آشکارکننده‌ای نیست‌'),
(4843, 53, 59, 'پس آیا از این سخن عجب می‌کنید؟'),
(4844, 53, 60, 'و می‌خندید و نمی‌گریید؟'),
(4845, 53, 61, 'و شمایید که بازی کنانید'),
(4846, 53, 62, 'پس برای خداوند سجده برید و پرستش کنید');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'قیامت نزدیک شد و ماه دو پاره شد'),
(4848, 54, 2, 'و اگر معجزه‌ای بینند روی برتابند و گویند جادویی دنباله‌دار است‌'),
(4849, 54, 3, 'و انکار پیشه کردند و از هوی و هوسهایشان پیروی کردند و هر کاری سرانجامی دارد'),
(4850, 54, 4, 'و به راستی برای آنان از اخبار [پیشینیان‌] آنچه در آن درس عبرت هست، به میان آمده است‌'),
(4851, 54, 5, 'حکمتی است رسا، و هشدارها سودی نبخشیده است‌'),
(4852, 54, 6, 'پس از آنان روی بر تاب [تا] روزی که آن دعوتگر به چیزی غریب و ناشناخته دعوت کند'),
(4853, 54, 7, 'در حالی که دیدگانشان را فروداشته‌اند، از گورها بیرون آیند، گویی که ملخهای پراکنده‌اند'),
(4854, 54, 8, 'به سوی دعوتگر شتابند، و کافران گویند امروز روزی دشوار است‌'),
(4855, 54, 9, 'پیش از آنان قوم نوح انکار پیشه کردند و بنده ما را دروغ‌زن انگاشتند و گفتند دیوانه است و رانده شد'),
(4856, 54, 10, 'سپس پروردگارش را به دعا خواند که من درمانده‌ام [یاری کن و] انتقام [مرا] بگیر'),
(4857, 54, 11, 'آنگاه درهای آسمان را به آبی سیل‌آسا گشودیم‌'),
(4858, 54, 12, 'و از زمین چشمه‌هایی بر شکافتیم، سپس آب [بسیار] برای کاری که مقدر بود، به هم بر آمد'),
(4859, 54, 13, 'و او [نوح‌] را بر [کشتی‌] ساخته و پرداخته از تخته‌ها و میخها سوار کردیم‌'),
(4860, 54, 14, 'که زیر نظر ما روان بود، این پاداش کسی بود که به او ناسپاسی کرده بودند'),
(4861, 54, 15, 'و به راستی آن را به عنوان مایه عبرت باقی گذاردیم، پس آیا پندپذیری هست؟'),
(4862, 54, 16, 'بنگر تا عذاب و هشدار من چگونه بود'),
(4863, 54, 17, 'و به راستی قرآن را قابل پند گیری گرداندیم، پس آیا پندپذیری هست؟'),
(4864, 54, 18, '[قوم‌] عاد تکذیب کردند، بنگر تا عذاب و هشدار من چگونه بود'),
(4865, 54, 19, 'ما بر آنان بادی سخت سرد در روزی شوم دنباله‌دار فرو فرستادیم‌'),
(4866, 54, 20, 'که مردمان را از جا می‌کند، گویی که ایشان خرمابنان ریشه‌کن شده‌اند'),
(4867, 54, 21, 'بنگر تا عذاب و هشدار من چگونه بود'),
(4868, 54, 22, 'و به راستی قرآن را قابل پندگیری گرداندیم، پس آیا پندپذیری هست؟'),
(4869, 54, 23, '[قوم‌] ثمود هشداردهندگان را دروغ‌زن شمرد'),
(4870, 54, 24, 'و گفتند آیا از میان خود، از انسانی تک و تنها پیروی کنیم، در آن صورت دچار گمراهی و سردرگمی خواهیم بود'),
(4871, 54, 25, 'آیا از میان همه ما کتاب آسمانی بر او فرود آمده است، نه بلکه او دروغ‌زن خودپسند است‌'),
(4872, 54, 26, 'زودا که فردا بدانند که دروغ‌زن خودپسند کیست‌'),
(4873, 54, 27, 'ما فرستنده ماده شتر [معجزه‌آسا] برای آزمایش ایشانیم، پس منتظر ایشان باش و شکیبایی کن‌'),
(4874, 54, 28, 'و به آنان خبر بده که آب در میان آنان تقسیم شده است، هر یک به حصه‌ای از آب حاضر باشند'),
(4875, 54, 29, 'آنگاه رفیقشان را ندا دادند، سپس او دست درازی کرد و [شتر را] پی کرد'),
(4876, 54, 30, 'بنگر عذاب و هشدار من چگونه بود'),
(4877, 54, 31, 'ما بر آنان بانگ مرگباری یگانه فرو فرستادیم، آنگاه مانند خار و خاشاکی شدند که از سایه‌بان‌ساز بازماند'),
(4878, 54, 32, 'و به راستی قرآن را قابل پندگیری گرداندیم، پس آیا پندپذیری هست؟'),
(4879, 54, 33, 'قوم لوط هشداردهندگان را دروغ‌زن شمردند'),
(4880, 54, 34, 'ما بر آنان شن‌بادی فرو فرستادیم، مگر بر خاندان لوط که سحرگاهی نجاتشان دادیم‌'),
(4881, 54, 35, 'که نعمتی از جانب ما بود، بدین‌سان کسی را که سپاس گزارده است، جزا دهیم‌'),
(4882, 54, 36, 'و به راستی [لوط] آنان را از سختگیری ما هشدار داده بود، ولی در برابر هشدار گردنکشی می‌کردند'),
(4883, 54, 37, 'و به راستی از مهمانان او کام می‌خواستند، پس ایشان را نابینا ساختیم [و گفتیم‌] پس عذاب و هشدار مرا بچشید'),
(4884, 54, 38, 'و به راستی بامدادی پگاه عذابی پایدار آنان را فرو گرفت‌'),
(4885, 54, 39, '[و گفتیم‌] پس عذاب و هشدار مرا بچشید'),
(4886, 54, 40, 'و به راستی قرآن را قابل پندگیری گرداندیم، پس آیا پندپذیری هست؟'),
(4887, 54, 41, 'و به راستی هشداردهندگان به سراغ خاندان فرعون آمدند'),
(4888, 54, 42, 'آنان همه معجزات ما را دروغ شمردند، آنگاه آنان را چنان فرو گرفتیم که پیروزمند توانمند گیرد'),
(4889, 54, 43, 'آیا کافران شما از همه اینان بهترند، یا برای شما امان نامه‌ای است در کتابهای آسمانی؟'),
(4890, 54, 44, 'یا می‌گویند ما هم‌پشتیم [و] کین‌ستان‌'),
(4891, 54, 45, 'زودا که آن جمع ورشکسته شود و همه پشت کنند'),
(4892, 54, 46, 'آری قیامت موعد [اصلی‌] ایشان است و قیامت سهمگین‌تر و تلخ‌تر است‌'),
(4893, 54, 47, 'آری که گناهکاران در گمراهی و سردرگمی‌اند'),
(4894, 54, 48, 'روزی که بر چهره‌هایشان در آتش کشیده شوند [و گویندشان‌] آسیب دوزخ را بچشید'),
(4895, 54, 49, 'ما هر چیز را به اندازه آفریده‌ایم‌'),
(4896, 54, 50, 'و فرمان ما جز [فرمانی‌] یگانه نیست، مانند چشم برهم‌زدنی‌'),
(4897, 54, 51, 'و به راستی همانندانتان را نابود کردیم، پس آیا پندپذیری هست؟'),
(4898, 54, 52, 'و هر چیزی که انجام داده‌اند در نامه‌های اعمال هست‌'),
(4899, 54, 53, 'و هر خرد و بزرگی نوشته شده است‌'),
(4900, 54, 54, 'بی‌گمان پرهیزگاران در باغها و [جوار] جویبارها هستند'),
(4901, 54, 55, 'در مقام و منزلتی راستین، نزد فرمانروای توانا');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'خداوند رحمان‌'),
(4903, 55, 2, 'قرآن را آموزش داد'),
(4904, 55, 3, 'انسان را آفرید'),
(4905, 55, 4, 'به او زبان آموخت‌'),
(4906, 55, 5, 'خورشید و ماه، حسابی [معین‌] دارند'),
(4907, 55, 6, 'و گیاه و درخت سجده می‌کنند'),
(4908, 55, 7, 'و آسمان را برافراشت و معیار و میزان مقرر داشت‌'),
(4909, 55, 8, 'از بهر آنکه در ترازو از حد تجاوز مکنید'),
(4910, 55, 9, 'و وزن را دادگرانه به کار آورید و در ترازو کمی و کاستی میاورید'),
(4911, 55, 10, 'و زمین را برای جهانیان پدید آورد'),
(4912, 55, 11, 'در آن [همه گونه‌] میوه و خرمای پوشش‌دار هست‌'),
(4913, 55, 12, 'و دانه برگ‌دار و گیاه خوشبو'),
(4914, 55, 13, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4915, 55, 14, 'انسان را از گل خشک همچون سفال، آفرید'),
(4916, 55, 15, 'و جن را از زبانه آتش پدید آورد'),
(4917, 55, 16, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4918, 55, 17, 'پروردگار مشرق‌ها و مغرب‌ها'),
(4919, 55, 18, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4920, 55, 19, 'دو دریا را که به هم می‌رسند درآمیخت‌'),
(4921, 55, 20, 'در میان آنها برزخی است که به همدیگر تجاوز نکنند'),
(4922, 55, 21, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4923, 55, 22, 'از آن دو در و مرجان بیرون می‌آید'),
(4924, 55, 23, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4925, 55, 24, 'و او راست کشتیهای بادبان برافراشته که چون کوهها در دریا روانند'),
(4926, 55, 25, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4927, 55, 26, 'هر کس که بر روی آن [زمین‌] است فناپذیر است‌'),
(4928, 55, 27, 'و [سرانجام‌] ذات پروردگارت که شکوهمند و گرامی است، باقی می‌ماند'),
(4929, 55, 28, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4930, 55, 29, 'هر آن کس که در آسمانها و زمین است از او درخواست [امداد] دارد، او هر روزی در کار است‌'),
(4931, 55, 30, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4932, 55, 31, 'به زودی ای ثقلان [جن و انس‌] به [حساب‌] شما می‌پردازیم‌'),
(4933, 55, 32, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4934, 55, 33, 'ای گروه جن و انس اگر توانید که از گوشه و کنار آسمانها و زمین نفوذ کنید، نفوذ کنید، اما جز با نیرویی عظیم، نتوانید به درون راه برید'),
(4935, 55, 34, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4936, 55, 35, 'بر شما شعله‌ای بی‌دود و دودی بی‌شعله فرو فرستاده شود، ولی شما [هماورد نخواهید شد و] کین نخواهید ستاند'),
(4937, 55, 36, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4938, 55, 37, 'آنگاه که آسمان از هم بشکافد و چون گل سرخ و روغن گداخته باشد'),
(4939, 55, 38, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4940, 55, 39, 'در آن روز از هیچ انس و جن درباره گناهش پرسشی نشود'),
(4941, 55, 40, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4942, 55, 41, 'گناهکاران با نشانه‌هایشان شناخته شوند، آنگاه از موهای پیشانی و پاهایشان گرفته [و به دوزخ افکنده‌] شوند'),
(4943, 55, 42, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4944, 55, 43, 'این همان جهنمی است که گناهکاران انکارش می‌کردند'),
(4945, 55, 44, 'در میان آن و میان آب گرمی جوشان می‌گردند'),
(4946, 55, 45, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4947, 55, 46, 'و برای کسی که از ایستادن در پیشگاه پروردگارش [برای حساب در حشر] هراسیده باشد، دو بوستان است‌'),
(4948, 55, 47, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4949, 55, 48, '[درختان آن دو بوستان‌] شاخه در شاخه‌اند'),
(4950, 55, 49, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4951, 55, 50, 'در آن دو [بوستان‌] دو چشمه هست که روانند'),
(4952, 55, 51, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4953, 55, 52, 'در آن دو [بوستان‌]از هر میوه‌ای دو گونه هست‌'),
(4954, 55, 53, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4955, 55, 54, '[بهشتیان‌] بر فرشهایی آرمیده‌اند که آسترهای آنها از ابریشم ستبر است، و میوه هر دو بوستان نزدیک [و دسترس‌] است‌'),
(4956, 55, 55, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4957, 55, 56, 'در آنها [حوریان‌] چشم فروهشته [/قانع به همسر] هستند که پیش از آنان هیچ انسان و هیچ جنی با آنان آمیزش نکرده است‌'),
(4958, 55, 57, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4959, 55, 58, 'گویی آنان یاقوت و مرجانند'),
(4960, 55, 59, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4961, 55, 60, 'آیا جزای نیکوکاری، جز نیکوکاری است؟'),
(4962, 55, 61, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4963, 55, 62, 'و جز آن دو نیز دو بوستان است‌'),
(4964, 55, 63, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4965, 55, 64, 'که سبز سیر سیه‌تاب‌اند'),
(4966, 55, 65, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4967, 55, 66, 'در آن دو، دو چشمه فواره‌زن هست‌'),
(4968, 55, 67, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4969, 55, 68, 'در آن دو [درختان‌] میوه و خرما و انار هست‌'),
(4970, 55, 69, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4971, 55, 70, 'در آنها دوشیزگان نیکخو و زیبا روست‌'),
(4972, 55, 71, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4973, 55, 72, 'حوریان پرده‌نشین در خیمه‌ها'),
(4974, 55, 73, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4975, 55, 74, 'پیش از آنان، هیچ انسان و هیچ جنی با آنان آمیزش نکرده است‌'),
(4976, 55, 75, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4977, 55, 76, 'تکیه زده بر بالش‌[های‌] سبزرنگ و بر فرش‌[های‌] گرانمایه نیکو'),
(4978, 55, 77, 'پس کدامین نعمتهای پروردگارتان را انکار می‌کنید؟'),
(4979, 55, 78, 'متبرک باد نام پروردگارت که شکوهمند و گرامی است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'چون واقعه بزرگ [قیامت‌] واقع شود'),
(4981, 56, 2, 'در وقوع آن دروغی نیست‌'),
(4982, 56, 3, 'هم فرو دارنده‌[ی کافران‌] است و هم فرادارنده‌[ی مؤمنان‌]'),
(4983, 56, 4, 'آنگاه که زمین به جنبشی سخت جنبانده شود'),
(4984, 56, 5, 'و کوهها سخت خرد و ریز شود'),
(4985, 56, 6, 'و همچون غباری پراکنده گردد'),
(4986, 56, 7, 'و شما گروههای سه‌گانه‌ای باشید'),
(4987, 56, 8, '[یکی‌] اصحاب یمین، و چه حال دارند اصحاب یمین‌'),
(4988, 56, 9, 'و [دیگری‌] اصحاب شمال، و چه حال دارند اصحاب شمال‌'),
(4989, 56, 10, 'و [سومین‌] سابقان که پیشتازانند'),
(4990, 56, 11, 'اینانند که مقرب‌اند'),
(4991, 56, 12, 'در بهشتهای پرناز و نعمت‌'),
(4992, 56, 13, 'گروهی بسیار از پیشینیان‌'),
(4993, 56, 14, 'و اندکی از واپسینان‌'),
(4994, 56, 15, 'بر تختهای گوهرنشان‌'),
(4995, 56, 16, 'رودرروی هم بر آنها تکیه زده‌اند'),
(4996, 56, 17, 'جاودانه جوانان بر گرد آنان می‌گردند'),
(4997, 56, 18, 'همراه با کوزه‌ها و ابریقها و جامهایی از شراب جاری‌'),
(4998, 56, 19, 'که [بهشتیان‌] از آن سردرد نگیرند و بد مست نشوند'),
(4999, 56, 20, 'و میوه‌هایی از آنچه بر می‌گزینند'),
(5000, 56, 21, 'و گوشت مرغ از آنچه خوش دارند'),
(5001, 56, 22, 'و حوریان چشم درشت‌'),
(5002, 56, 23, 'که همانندان مروارید نهفته‌اند'),
(5003, 56, 24, 'این پاداش کاری است که کرده‌اند'),
(5004, 56, 25, 'در آنجا [سخنان‌] بیهوده و [گزاف‌] گناه‌آلود نشنوند'),
(5005, 56, 26, 'نشنوند مگر سخنی که سلام است و سلام‌'),
(5006, 56, 27, 'و اصحاب یمین چه حال دارند اصحاب یمین‌'),
(5007, 56, 28, 'در جوار درختان سدر بی‌خار'),
(5008, 56, 29, 'و موزهای توبرتو'),
(5009, 56, 30, 'و سایه گسترده‌'),
(5010, 56, 31, 'و آبی ریزان‌'),
(5011, 56, 32, 'و میوه بسیار'),
(5012, 56, 33, 'که نه پایان‌پذیر است و نه بازداشته‌'),
(5013, 56, 34, 'و دوشیزگان گرامی‌'),
(5014, 56, 35, 'ما ایشان را به ابداع آفریده‌ایم‌'),
(5015, 56, 36, 'و دوشیزه‌شان داشته‌ایم‌'),
(5016, 56, 37, 'همسر دوست و هم سن و سال‌'),
(5017, 56, 38, 'خاص اصحاب یمین‌'),
(5018, 56, 39, 'جماعتی بسیار از پیشینیان‌'),
(5019, 56, 40, 'و جماعتی بسیار از واپسینان‌'),
(5020, 56, 41, 'و اصحاب شمال، چه حال دارند اصحاب شمال‌'),
(5021, 56, 42, 'در میان آتشباد و آب جوشند'),
(5022, 56, 43, 'و سایه‌ای از دوده‌'),
(5023, 56, 44, 'که نه خنک است و نه خوش‌'),
(5024, 56, 45, 'ایشان پیش از این نازپرورده بودند'),
(5025, 56, 46, 'بر گناه بزرگ مداومت می‌کردند'),
(5026, 56, 47, 'و می‌گفتند آیا چون مردیم و خاک و استخوانها[ی پوسیده‌] شدیم، آیا برانگیخته خواهیم شد؟'),
(5027, 56, 48, 'و همچنین نیاکان ما؟'),
(5028, 56, 49, 'بگو که پیشینیان و واپسینان‌'),
(5029, 56, 50, 'برای موعد روزی معین گرد آیند'),
(5030, 56, 51, 'سپس شمایان ای گمراهان دروغ انگار [و اهل انکار]'),
(5031, 56, 52, 'خورندگان از درخت زقومید'),
(5032, 56, 53, 'و شکم انباران از آن‌'),
(5033, 56, 54, 'و بر آن آب جوش آشامندگانید'),
(5034, 56, 55, 'و مانند نوشیدن شتران عطش زده می‌آشامید'),
(5035, 56, 56, 'این پیشکش ایشان در روز جزاست‌'),
(5036, 56, 57, 'ما شما را آفریده‌ایم، پس چرا تصدیق نمی‌کنید؟'),
(5037, 56, 58, 'آیا اندیشیده‌اید آنچه [از منی‌] را که [در رحمها] می‌ریزید'),
(5038, 56, 59, 'آیا شما آن را آفریده‌اید یا ما آفریننده‌ایم‌'),
(5039, 56, 60, 'ما در میان شما مرگ را مقدر داشته‌ایم، و ما درمانده نیستیم‌'),
(5040, 56, 61, 'که همانندان شما را جانشین شما گردانیم، و شما را در هیئتی که نمی‌دانید باز آفرینیم‌'),
(5041, 56, 62, 'و به راستی نشاه نخستین را شناخته‌اید، پس چرا پند نمی‌گیرید؟'),
(5042, 56, 63, 'آیا اندیشیده‌اید در آنچه می‌کارید؟'),
(5043, 56, 64, 'آیا شما آن را می‌رویانید یا ما رویاننده‌ایم؟'),
(5044, 56, 65, 'اگر خواهیم آن را خرد و ریز گردانیم و شما حسرت‌زده شوید'),
(5045, 56, 66, '[و گویید] ما غرامت دیدگانیم‌'),
(5046, 56, 67, 'بلکه ما بی‌بهرگانیم‌'),
(5047, 56, 68, 'آیا اندیشیده‌اید به آبی که می‌آشامید؟'),
(5048, 56, 69, 'آیا شما آن را از ابر فرو فرستاده‌اید یا ما فرو فرستنده‌ایم؟'),
(5049, 56, 70, 'اگر خواهیم آن را شور و تلخ گردانیم پس چرا سپاس نمی‌گزارید؟'),
(5050, 56, 71, 'آیا اندیشیده‌اید به آتشی که می‌افروزید؟'),
(5051, 56, 72, 'آیا شما درختش را آفریده‌اید یا ما آفریننده‌ایم؟'),
(5052, 56, 73, 'ما آن را پندآموزی ساخته‌ایم و توشه‌ای برای رهروان‌'),
(5053, 56, 74, 'پس به نام پروردگارت که بزرگ است تسبیح گوی‌'),
(5054, 56, 75, 'سوگند می‌خورم به منزلگاههای ستارگان‌'),
(5055, 56, 76, 'و آن اگر بدانید سوگندی عظیم است‌'),
(5056, 56, 77, 'آن قرآنی کریم است‌'),
(5057, 56, 78, 'در کتابی نهفته‌'),
(5058, 56, 79, 'جز پاکیزگان به آن دسترس ندارند'),
(5059, 56, 80, 'فرو فرستاده‌ای از سوی پروردگار جهانیان است‌'),
(5060, 56, 81, 'آیا شما در کار این سخن سستی می‌ورزید؟'),
(5061, 56, 82, 'و سپاس روزیتان را چنان کرده‌اید که [آن را] انکار می‌کنید'),
(5062, 56, 83, 'پس چرا چون جان به گلوگاه رسد'),
(5063, 56, 84, 'و شما در آن هنگام نظاره‌گرید'),
(5064, 56, 85, 'و ما به آن [جان شما] از شما نزدیکتریم ولی شما به چشم بصیرت نمی‌نگرید'),
(5065, 56, 86, 'پس چرا اگر شما جزا دادنی نیستید'),
(5066, 56, 87, 'اگر راست می‌گویید چرا آن را باز نمی‌گردانید؟'),
(5067, 56, 88, 'سپس آنگاه اگر از مقربان باشد'),
(5068, 56, 89, 'رهایش و گشایش است و بهشت پرناز و نعمت‌'),
(5069, 56, 90, 'و اما اگر از اصحاب یمین باشد'),
(5070, 56, 91, 'پس سلام بر تو باد از اصحاب یمین‌'),
(5071, 56, 92, 'و اما اگر از منکران گمراه باشد'),
(5072, 56, 93, 'پیشکش [او] از آب‌جوشان است‌'),
(5073, 56, 94, 'و ورود به جهنم‌'),
(5074, 56, 95, 'این همانا حق‌الیقین است‌'),
(5075, 56, 96, 'پس به نام پروردگارت که بزرگ است، تسبیح بگوی‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'آنچه در آسمانها و زمین است، خداوند را تسبیح می‌گوید، و اوست پیروزمند فرزانه‌'),
(5077, 57, 2, 'او راست فرمانروایی آسمانها و زمین، که زنده می‌دارد و می‌میراند، و او بر هر کاری تواناست‌'),
(5078, 57, 3, 'اوست اول و آخر و ظاهر و باطن و او به همه چیز داناست‌'),
(5079, 57, 4, 'اوست که آسمانها و زمین را در شش روز آفرید و سپس بر عرش استیلاء یافت، می‌داند که چه چیزی وارد زمین می‌شود و چه چیزی از آن بیرون می‌رود، و آنچه از آسمان فرو می‌آید و آنچه به آن فرا می‌رود، و او هرجا که باشید با شماست، و خداوند به آنچه می‌کنید بیناست‌'),
(5080, 57, 5, 'فرمانروایی آسمانها و زمین او راست، و کارها به خداوند باز گردانده شود'),
(5081, 57, 6, 'از شب می‌کاهد و بر روز می‌افزاید و از روز می‌کاهد و بر شب می‌افزاید و او به راز دلها داناست‌'),
(5082, 57, 7, 'به خداوند و پیامبر او ایمان بیاورید و از آنچه شما را در آن جانشین ساخته است انفاق کنید، آری کسانی از شما که ایمان آورده و انفاق کنند پاداش بزرگی دارند'),
(5083, 57, 8, 'و شما را چه می‌شود که به خداوند ایمان نمی‌آورید، حال آنکه پیامبر شما را دعوت می‌کند که به پروردگارتان ایمان آورید، و به راستی اگر ایمان داشته باشید از شما پیمانتان را گرفته است‌'),
(5084, 57, 9, 'اوست که بر بنده‌اش آیاتی روشنگر فرو فرستاده است تا شما را از تاریکیها[ی جهل و کفر] به سوی نور [علم و ایمان‌] بازبرد، و بی‌گمان خداوند در حق شما رئوف مهربان است‌'),
(5085, 57, 10, 'و شما را چه می‌شود که در راه خدا انفاق نمی‌کنید، و حال آنکه میراث آسمانها و زمین از خداوند است، و از میان شما کسی که پیش از پیروزی [فتح مکه‌] انفاق و کارزار کرده باشد [با دیگران‌] برابر نیست، اینان بلندمرتبه‌ترند از کسانی که بعد از آن انفاق و کارزار کرده‌اند، و همه را خداوند وعده نیکی [/بهشت‌] داده است، و خداوند به آنچه می‌کنید آگاه است‌'),
(5086, 57, 11, 'کیست که در راه خداوند قرض‌الحسنه دهد، تا برایش دوچندان سازد و برای او پاداشی ارجمند است‌'),
(5087, 57, 12, 'روزی که مردان مؤمن و زنان مؤمن را بینی که نورشان پیشاپیش آنان و در سمت راستشان می‌شتابد، امروز بشارت شما باغهایی است که از فرودست آن جویباران جاری است و جاودانه در آن هستید، این همانا رستگاری بزرگ است‌'),
(5088, 57, 13, 'روزی که مردان منافق و زنان منافق به مؤمنان گویند به خاطر ما درنگ کنید تا از نورتان روشنی فراگیریم، گفته شود پس پشت خویش باز گردید و نوری بجویید، سپس میان آنان دیواری زده شود که دری دارد، از درون‌سو در آن رحمت است و از برون سوی آن عذاب است‌'),
(5089, 57, 14, 'آنان را ندا دهند که مگر ما با شما نبودیم؟ گویند چرا ولی شما خودتان را در بلا افکندید و چشم به راه [حوادث سوء برای مسلمانان‌] بودید و شک و شبهه ورزیدید و آرزوها شما را فریفت، تا آنکه فرمان الهی [/مرگ‌] در رسید، و [شیطان‌] فریبکار شما را در حق خداوند فریب داد'),
(5090, 57, 15, 'پس امروز نه از شما و نه از کافران، بدل و بلاگردانی پذیرفته نشود، سرا و سرانجام شما آتش [دوزخ‌] است، او پناه شماست و چه بد سرانجامی است‌'),
(5091, 57, 16, 'آیا مؤمنان را هنگام آن نرسیده است که دلهایشان به یاد خداوند، و آنچه از حق که نازل شده است، خشوع یابد، و مانند کسانی نباشند که پیشترها به آنان کتاب آسمانی داده شده است، و سپس روزگارشان دراز نمود، آنگاه دلهایشان سخت شد و بسیاری از آنان نافرمان بودند'),
(5092, 57, 17, 'بدانید که خداوند زمین را پس از پژمردنش زنده می‌دارد، به راستی که آیات [خود] را برای شما به روشنی بیان کردیم، باشد که تعقل کنید'),
(5093, 57, 18, 'بی‌گمان مردان صدقه‌بخش و زنان صدقه‌بخش و کسانی که در راه خدا قرض‌الحسنه داده‌اند، برای آنان دوچندان شود، و برای آنان پاداشی ارجمند است‌'),
(5094, 57, 19, 'و کسانی که به خداوند و پیامبرانش ایمان آورده‌اند، اینانند که صدیقند، و شهیدان نزد پروردگارشان هستند، و اجرشان و نورشان را دارند، و کسانی که کفرورزیده‌اند و آیات ما را دروغ انگاشته‌اند اینانند که دوزخی‌اند'),
(5095, 57, 20, 'بدانید که همانا زندگانی دنیا بازیچه و سرگرمی، و زیور و فخرفروشی در میان شما و افزون طلبی در اموال و اولاد است، همانند بارانی که کشاورزان را گیاه آن خوش آید، سپس پژمرده شود و آن را زرد شده بینی، سپس خرد و ریز شود، و در آخرت هم عذابی شدید هست و هم آمرزش و خشنودی از جانب خداوند، و زندگانی دنیا جز مایه فریب نیست‌'),
(5096, 57, 21, 'به سوی آمرزشی از پروردگارتان، و بهشتی که پهنای آن همچند پهنای آسمان و زمین است و برای مؤمنان به خداوند و پیامبران او آماده شده است، بشتابید، این بخشش الهی است که به هر کس خواهد ارزانی‌اش دارد، و خداوند دارای بخشش و بخشایش بیکران است‌'),
(5097, 57, 22, 'هیچ مصیبتی در زمین [به جسم و مال‌] و به جانهای شما نرسد مگر آنکه پیش از آنکه آن را آفریده باشیم در کتابی [ثبت‌] است، بی‌گمان این امر بر خداوند آسان است‌'),
(5098, 57, 23, 'تا آنکه بر آنچه از دست شما رود اندوه مخورید و بر آنچه به شما بخشد شادمانی مکنید، و خداوند هیچ متکبر فخر فروشی را دوست ندارد'),
(5099, 57, 24, 'همان کسانی که بخل می‌ورزند و مردمان را نیز به بخل می‌فرمایند، و هر کس روی بر تابد، [بداند که‌] خداوند بی نیاز ستوده است‌'),
(5100, 57, 25, 'به راستی که پیامبرانمان را همراه با پدیده‌های روشنگر فرستادیم و همراه آنان کتاب آسمانی و سنجه فرو فرستادیم، تا مردم به دادگری برخیزند، و آهن را پدید آوردیم که در آن [مایه‌] ستیز و صلابت است و نیز سودهایی برای مردم، تا سرانجام خداوند معلوم بدارد که چه کسی در نهان [جانب‌] او و پیامبرانش را یاری می‌دهد، بی‌گمان خداوند نیرومند پیروزمند است‌'),
(5101, 57, 26, 'و به راستی نوح و ابراهیم را به رسالت فرستادیم، و در زاد و رود آن دو، پیامبری و کتاب آسمانی قرار دادیم، آنگاه بعضی از آنان رهیافته و بسیاری از آنان نافرمانند'),
(5102, 57, 27, 'سپس از پی ایشان پیامبرانمان را آوردیم، و عیسی بن مریم را از پی آوردیم، و به او انجیل دادیم و در دل کسانی که از او پیروی کردند رأفت و رحمت قرار دادیم، و رهبانیتی را به صورت بدعتی در پیش گرفتند که ما آن را بر ایشان واجب نکرده بودیم، مگر آنکه در طلب خشنودی الهی آن را در پیش گرفته بودند، ولی چنانکه شایسته رعایت بود، رعایتش نکردند، آنگاه به مؤمنان آنان پاداششان را بخشیدیم و بسیاری از آنان نافرمانند'),
(5103, 57, 28, 'ای مؤمنان از خداوند پروا کنید و به پیامبر او ایمان آورید، تا بهره دوچندان از رحمت خویش به شما ارزانی دارد، و برای شما نوری قرار دهد که با آن [به درستی‌] راه روید و شما را بیامرزد، و خداوند آمرزگار مهربان است‌'),
(5104, 57, 29, 'تا اهل کتاب بدانند که صاحب اختیار چیزی از بخشش و بخشایش الهی نیستند، و بخشش و بخشایش به دست خداوند است، به هر کس که خواهد ارزانی‌اش دارد، و خداوند دارای بخشش بیکران است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'به راستی که خداوند سخن زنی را که با تو درباره همسرش مجادله می‌کرد و به خداوند شکایت حال خود می‌گفت، شنید، و خداوند گفت و گوی شما دو تن را می‌شنود، چرا که خداوند شنوای بیناست‌'),
(5106, 58, 2, 'کسانی که از میان شما با زنانشان ظهار می‌کنند، [بدانند که‌] آن زنان [با این ماجرا در حکم‌] مادران آنان نیستند، مادران آنان جز کسانی که آنان را زاده‌اند، نیستند، و ایشان [با این عمل‌] سخنی ناپسند و ناحق می‌گویند، و بی‌گمان خداوند بخشاینده آمرزگار است‌'),
(5107, 58, 3, 'و کسانی که با زنانشان ظهار می‌کنند، سپس از آنچه گفته‌اند برمی‌گردند [و می‌خواهند به همسر خود بازگردند] باید پیش از آنکه با همدیگر تماس گیرند، برده‌ای آزاد کنند، این اندرزی است که به شما داده می‌شود، و خداوند به آنچه می‌کنید آگاه است‌'),
(5108, 58, 4, 'و هر کس که [امکان آن را] نیافت، باید که پیش از آنکه با همدیگر تماس گیرند، دو ماه پی در پی روزه بگیرد، سپس [بازهم‌] اگر نتوانست [روزه بگیرد]، در آن صورت باید به اطعام شصت فقیر بپردازد، این [کفاره‌] برای آن است که به [امر] خداوند و پیامبر او ایمان آورید، و این احکام الهی است، و برای کافران عذابی دردناک [مقرر] است‌'),
(5109, 58, 5, 'بی‌گمان کسانی که با [امر] خداوند و پیامبر او مخالفت می‌کنند، سرنگون شوند، چنانکه پیشینیانشان هم سرنگون شدند، و به راستی آیاتی روشنگر فرو فرستاده‌ایم، و برای کافران عذابی خفت‌بار [درپیش‌] است‌'),
(5110, 58, 6, 'روزی که خداوند همگی آنان را [از گور] برانگیزد، سپس آنان را به [حقیقت و نتیجه‌] آنچه کرده‌اند آگاه سازد، خداوند آن را در شمار آورده است اما [خود] آنان فراموشش کرده‌اند، و خداوند بر هر چیز گواه است‌'),
(5111, 58, 7, 'آیا نیندیشیده‌ای که خداوند آنچه در آسمانها و آنچه در زمین است می‌داند، هیچ رازگویی سه تن نباشد مگر آنکه او چهارمین آنان است، و نه [رازگویی‌] پنج تن مگر آنکه او ششمین آنان است، و نه کمتر از این و نه بیشتر مگر آنکه او هر جا که باشند با ایشان است، سپس در روز قیامت آنان را به [نتیجه و حقیقت‌] کار و کردارشان آگاه می‌سازد، بی‌گمان خداوند به هر چیزی داناست‌'),
(5112, 58, 8, 'آیا ننگریسته‌ای در کار کسانی که از نجوا نهی شده‌اند، سپس به آنچه از آن نهی شده‌اند، باز می‌گردند و به گناه و ستمکاری و نافرمانی از پیامبر باهم نجوا می‌کنند، و چون به نزدت آیند به نحوی به تو درود می‌فرستند که خداوند آن گونه به تو درود نفرستاده است، و در دلهایشان گویند چرا به خاطر آنچه می‌گوییم خداوند عذابمان نمی‌کند؟ جهنم برایشان کافی است که به آن اندر آیند، و چه بد سرانجامی است‌'),
(5113, 58, 9, 'ای مؤمنان چون رازگویی کنید، به قصد گناه و ستمکاری و نافرمانی از پیامبر نجوا مکنید، و به قصد نیکی و تقوا نجوا کنید، و از خداوندی که نزد او محشور می‌گردید پروا کنید'),
(5114, 58, 10, 'جز این نیست که نجوا از سوی شیطان است تا مؤمنان را غمگین کند، و هیچ زیان رسان به آنان نیست، مگر به اذن الهی، و مؤمنان باید فقط بر خداوند توکل کنند'),
(5115, 58, 11, 'ای مؤمنان چون به شما گفته شود در مجالس جا باز کنید، باز کنید، که خداوند در کار شما گشایش آورد، و چون گفته شود برخیزید، برخیزید، خداوند از میان شما مؤمنان و دانش یافتگان را به درجاتی رفعت دهد و خداوند به آنچه می‌کنید آگاه است‌'),
(5116, 58, 12, 'ای مؤمنان چون خواهید که با پیامبر راز گویید، پیش از نجوایتان صدقه‌ای تقدیم دارید این برای شما بهتر و پاکیزه‌تر است، اما اگر چیزی نیافتید، بی‌گمان خداوند آمرزگار مهربان است‌'),
(5117, 58, 13, '[سپس که کسی جز علی ع توفیق نیافت فرمود] آیا ترسیدید که پیش از نجوایتان صدقاتی تقدیم دارید؟ حال که چنین نکردید و خداوند هم از شما درگذشت، پس نماز را برپا دارید و زکات را بپردازید و از خداوند و پیامبرش اطاعت کنید، و خداوند به آنچه می‌کنید آگاه است‌'),
(5118, 58, 14, 'آیا ننگریسته‌ای کسانی را که با قومی که خداوند بر آنان خشم آورده است، و نه از شما هستند و نه از آنان [یهودیان‌]، دوستی ورزیده‌اند؟ و آگاهانه به دروغ سوگند می‌خورند'),
(5119, 58, 15, 'خداوند برای آنان عذابی سهمگین آماده ساخته است، آنان عملکردشان بد است‌'),
(5120, 58, 16, 'سوگندهایشان را سپر بلا کرده‌اند، و [مردمان را] از راه خدا باز می‌دارند، پس عذابی خفت‌بار [در پیش‌] دارند'),
(5121, 58, 17, 'اموال و اولادشان به هیچ روی از [عذاب‌] خدا بازشان ندارد، اینان دوزخی‌اند و جاودانه در آنند'),
(5122, 58, 18, 'روزی که خداوند همگی آنان را برانگیزد، آنگاه [مشرکان‌] برای او به همان گونه سوگند یاد می‌کنند که برای شما یاد می‌کنند و گمان می‌کنند که حقی در دست دارند، بدانید که آنان دروغگویانند'),
(5123, 58, 19, 'شیطان بر آنان دست یافت، سپس یاد خدا را از خاطر آنان برد، اینان حزب شیطانند، هان بی‌گمان حزب شیطان زیانکارانند'),
(5124, 58, 20, 'بی‌گمان کسانی که با [امر] خداوند و پیامبر او مخالفت می‌کنند، اینان از زمره فرومایگانند'),
(5125, 58, 21, 'خداوند مقرر داشته است که من و پیامبرانم پیروز گردیم، بی‌گمان خداوند نیرومند پیروزمند است‌'),
(5126, 58, 22, 'قومی نیابی که به خداوند و روز بازپسین ایمان داشته باشند و با کسانی که با خداوند و پیامبر او مخالفت می‌ورزند، دوستی کنند، ولو آنکه پدرانشان یا فرزندانشان یا برادرانشان یا خاندانشان باشند اینانند که [خداوند] در دلهایشان [نقش‌] ایمان نگاشته است، و به فیضی از جانب خویش استوارشان داشته است، و [ایشان را] به بوستانهایی درآورد که جویباران از فرودست آن جاری است، جاودانه در آنند، خداوند از آنان خشنود است، و آنان هم از او خشنودند، اینان خدا خواهانند، بدانید که خدا خواهان رستگارانند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'آنچه در آسمانها و آنچه در زمین است خداوند را تسبیح می‌گوید، و او پیروزمند فرزانه است‌'),
(5128, 59, 2, 'اوست که کافران اهل کتاب [یهودیان بنی‌نضیر] را از خانه و کاشانه‌شان در آغاز هنگامه رهسپاری انبوه آواره کرد، گمان نمی‌بردید که بیرون روند، و [خودشان هم‌] گمان می‌کردند که دژهایشان باز دارنده آنان در برابر امر و عقوبت الهی است، و امر [/عذاب‌] الهی از جایی که گمانش را نمی‌بردند بر آنان فرود آمد و در دلهایشان هراس انداخت، [چندانکه‌] خانه‌هایشان را به دستهای خود و دستان مؤمنان ویران می‌کردند، پس ای دیده‌وران عبرت گیرید'),
(5129, 59, 3, 'و اگر خداوند جلای وطن را بر آنان مقرر نداشته بود، در دنیا [به عذابی سهمگین‌] معذبشان می‌داشت، و برای آنان در آخرت عذاب آتش دوزخ است‌'),
(5130, 59, 4, 'این از آن است که با [امر] خداوند و پیامبرش مخالفت کردند، و هر کس با امر خداوند مخالفت کند، بی‌گمان خداوند سخت کیفر است‌'),
(5131, 59, 5, 'هر چه از درختان خرما بریدید یا آن را ایستاده با ریشه‌هایش باقی گذاردید، همه به اذن الهی بود تا نافرمانان را خوار بدارد'),
(5132, 59, 6, 'و هر چه خداوند از ایشان به [صورت فی‌ء] عاید پیامبرش گرداند، شما در آن نه اسبان را به تاخت در آوردید و نه شتران را، بلکه خداوند پیامبرانش را بر هر کس که خواهد سلطه دهد، و خداوند بر هر کاری تواناست‌'),
(5133, 59, 7, 'آنچه خداوند [به صورت فی‌ء] از اموال اهل آبادیها عاید پیامبرش گرداند، خاص خداوند و پیامبر و خویشاوندان و یتیمان و بینوایان و در راه ماندگان است، تا در میان توانگران شما دستگردان نشود، و آنچه پیامبر شما را دهد آن را بپذیرید و آنچه شما را از آن بازدارد، از آن دست بدارید، و از خداوند پروا کنید، که بی‌گمان خداوند سخت کیفر است‌'),
(5134, 59, 8, 'برای بینوایان از مهاجران است که از خانه و کاشانه‌هایشان و ملک و مالهایشان رانده شده‌اند، و در طلب بخشش و خشنودی خداوندند و [دین‌] خداوند و پیامبر او را یاری می‌دهند، اینانند که راستگویانند'),
(5135, 59, 9, 'و کسانی که پیش از آنان در سرای اسلام [مدینه‌] جای گرفتند و ایمان را پذیرفتند، کسانی را که به سوی آنان هجرت کرده‌اند، دوست می‌دارند و نسبت به آنچه به ایشان داده شده است، در دل خود احساس نیاز نمی‌کنند، و [آنان را] ولو خود نیازمندی داشته باشند، بر خود بر می‌گزینند، و کسانی که از آزمندی نفس خویش در امان مانند، آنانند که رستگارانند'),
(5136, 59, 10, 'و کسانی که پس از آنان آمده‌اند گویند پروردگارا ما و برادرانمان را که در ایمان بر ما سبقت دارند، بیامرز، و در دلهای ما نسبت به مؤمنان کینه‌ای مگذار، پروردگارا تویی که رئوف مهربانی‌'),
(5137, 59, 11, 'آیا ننگریسته‌ای کسانی را که نفاق ورزیدند به دوستانشان از اهل کتاب که کفرورزیدند، گفتند اگر شما رانده شوید، ما نیز همراه شما بیرون می‌آییم، و به زیان شما هرگز از امر هیچ کس اطاعت نخواهیم کرد، و اگر با شما کارزار شود، حتما شما را یاری خواهیم کرد، خداوند شاهد است که آنان دروغگو هستند'),
(5138, 59, 12, 'اگر آنان [یهودیان بنی‌نضیر] رانده شوند، ایشان همراه آنان بیرون نروند، و اگر با آنان کارزار شود، ایشان را یاری ندهند، و اگر بخواهند یاریشان دهند، پشت کنند، سپس از هیچ کس یاری نیابند'),
(5139, 59, 13, 'بی‌شک شما در دلهای آنان هراس‌انگیزترید از خداوند، این از آن است که ایشان قومی بی‌تمیزند'),
(5140, 59, 14, 'هرگز همگی با شما کارزار نکنند مگر در میان برزنهای محصور، یا از پشت دیوارها، ستیز و صلابت آنان در میان خودشان سخت است، آنان را همدست می‌انگاری، حال آنکه دلهایشان پراکنده است، این از آن است که قومی نابخردند'),
(5141, 59, 15, 'همانند پیشینیان نزدیکشان که عقوبت کار [نادرست‌]شان را چشیدند و عذابی دردناک [در پیش‌] دارند'),
(5142, 59, 16, 'همانند شیطان که به انسان گوید کافر شو، و چون کافر شود، گوید من از تو بری و بر کنارم، که من از خداوند -پروردگار جهانیانمی‌ترسم‌'),
(5143, 59, 17, 'و سرانجام آن دو چنین است که هر دو در آتش جهنم‌اند، و جاودانه در آنند، و این جزای ستمکاران [مشرک‌] است‌'),
(5144, 59, 18, 'ای مؤمنان از خداوند پروا کنید و هر کس بنگرد که برای فردا چه پیش اندیشی کرده است، و از خداوند پروا کنید، چرا که خداوند به آنچه می‌کنید آگاه است‌'),
(5145, 59, 19, 'و همانند کسانی که خداوند را فراموش کردند، مباشید که خداوند هم [تیمار] خودشان را از یادشان برد، اینانند که نافرمانند'),
(5146, 59, 20, 'دوزخیان و بهشتیان برابر نیستند، بهشتیانند که رستگارند'),
(5147, 59, 21, 'اگر این قرآن را بر کوهی فرو فرستاده بودیم، بی‌شک آن را از ترس خداوند خاکسار و فرو پاشیده می‌دیدی، و اینها مثلهایی است که برای مردم می‌زنیم باشد که اندیشه کنند'),
(5148, 59, 22, 'او خداوندی است که خدایی جز او نیست، دانای پنهان و پیدا، اوست خدای رحمان مهربان‌'),
(5149, 59, 23, 'اوست خداوندی که خدایی جز او نیست، فرمانروای قدوس سلام مؤمن مهیمن پیروزمند جبار صاحب کبریاست، پاک و برتر است خداوند از آنچه برای او شریک می‌انگارند'),
(5150, 59, 24, 'اوست خداوند آفریدگار پدیدآور صورتگر، او راست نامهای نیک، آنچه در آسمانها و زمین است او را تسبیح می‌گوید، و اوست پیروزمند فرزانه‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'ای مؤمنان اگر برای جهاد در راه من و طلب خشنودی من از [وطن خود] بیرون آمده‌اید، دشمن من و دشمن خودتان را دوست مگیرید، که به آنها مهربانی کنید، و حال آنکه آنان به دین حقی که برای شما آمده است، کفر و انکار ورزیده‌اند و پیامبر و شما را آواره کرده‌اند، چرا که به خداوند -پروردگارتانایمان دارید، [شمایید که‌] با آنان سر و سر دوستانه دارید و من به آنچه پنهان دارید و آنچه آشکار دارید، آگاه‌ترم، و هر کس از شما که چنین کند به راستی که از راه راست گم گشته است‌'),
(5152, 60, 2, 'اگر بر شما دست یابند دشمنان شما خواهند بود و دست و زبانشان را به بدی در حق شما می‌گشایند، و خوش دارند که شما کفر بورزید'),
(5153, 60, 3, 'خویشاوندانتان و همچنین فرزندانتان به شما سود نمی‌رسانند [و] در روز قیامت در میان شما داوری کند و خداوند به آنچه می‌کنید بیناست‌'),
(5154, 60, 4, 'برای شما در ابراهیم و همراهان او سرمشقی نیکوست، آنگاه که به قومشان گفتند و ما از شما و آنچه به جای خداوند می‌پرستید، بری و برکناریم، ما منکر شماییم و همواره در میان ما و شما دشمنی و کینه پدید آمده است تا آنکه فقط به خداوند ایمان آورید، مگر در این سخن ابراهیم به پدرش که حتما برای تو آمرزش خواهم طلبید، و برای تو در برابر خداوند صاحب اختیار چیزی نیستم، پروردگارا بر تو توکل کرده‌ایم و به تو روی آورده‌ایم و سیر و سرانجام [همه‌] به سوی توست‌'),
(5155, 60, 5, 'پروردگارا ما را زیردست کافران مگردان و ما را بیامرز، پروردگارا تویی که پیروزمند فرزانه‌ای‌'),
(5156, 60, 6, 'به راستی برای شما در آنان سرمشقی نیکوست، برای کسی که به خداوند و روز بازپسین امید داشته باشد، و هر کس روی برتابد [بداند] که خداوند بی‌نیاز ستوده است‌'),
(5157, 60, 7, 'چه بسا خداوند در میان شما و کسانی از آنان که باهم دشمنی دارید، مهربانی پدید آورد، و خداوند تواناست، و خداوند آمرزگار مهربان است‌'),
(5158, 60, 8, 'خداوند شما را از کسانی که با شما در کار دین کارزار نکرده‌اند، و شما را از خانه و کاشانه‌تان آواره نکرده‌اند، نهی نمی‌کند از اینکه در حقشان نیکی کنید و با آنان دادگرانه رفتار کنید، بی‌گمان خداوند دادگران را دوست دارد'),
(5159, 60, 9, 'خداوند فقط شما را از کسانی که با شما در کار دین کارزار کرده‌اند و شما را از خانه و کاشانه‌تان آواره کرده‌اند و برای راندنتان [با دیگران‌] همدستی کرده‌اند، نهی می‌کند از اینکه دوستشان بدارید، و هر کس دوستشان بدارد، آنانند که ستمکار [مشرک‌] هستند'),
(5160, 60, 10, 'ای مؤمنان هر گاه زنان مؤمن مهاجر به نزد شما آیند، آنان را بیازمایید، خداوند به [حقیقت‌] ایمانشان آگاه‌تر است، پس اگر آنان را مؤمن شناختید دیگر آنان را به سوی کفار باز مگردانید، نه ایشان بر آنان حلال هستند و نه آنان برایشان حلالند، و به آنان آنچه خرج کرده‌اند بدهید، و بر شما گناهی نیست که پس از آنکه مهرهایشان را پرداختید، با آنان ازدواج کنید، و دست در دامان زنان کافر مزنید، و [چون زنان شما نزد آنان روند] آنچه خرج کرده‌اید از آنان [کافران‌] طلب کنید و آنان هم آنچه خرج کرده‌اند از شما طلب کنند، این حکم الهی است که در میان شما داوری می‌کند و خداوند دانای فرزانه است‌'),
(5161, 60, 11, 'و اگر از دست شما زنی به سوی کافران رود، و سپس با آنان تلافی کردید و غنیمت گرفتید، آنگاه به کسانی که همسرانشان [به سوی کفار] رفته‌اند، معادل خرجی را که کرده‌اند [از غنایم‌] بپردازید، و از خداوندی که شما به او ایمان دارید پروا کنید'),
(5162, 60, 12, 'ای پیامبر چون زنان مؤمن به نزد تو آمدند که با تو بیعت کنند که برای خداوند شریکی نیاورند و دزدی نکنند و زنا نکنند و فرزندانشان را نکشند و فرزندی را از راه بهتان به دست نیاورند که نسبت آن را به دست و پای خویش دهند، و در کار خیری از تو نافرمانی نکنند، پس با آنان بیعت کن و برایشان از خداوند آمرزش بخواه، چرا که خداوند آمرزگار مهربان است‌'),
(5163, 60, 13, 'ای مؤمنان قومی را که خداوند بر آنان خشم آورده است، به دوستی مگیرید، که از آخرت به همان گونه نومیدند که کافران از بازگشت در گور خفتگان‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'آنچه در آسمانها و آنچه در زمین است خداوند را نیایش کند، و اوست پیروزمند فرزانه‌'),
(5165, 61, 2, 'ای مؤمنان چرا چیزی می‌گویید که انجام نمی‌دهید'),
(5166, 61, 3, 'نزد خداوند بس منفور است که چیزی را بگویید که انجام نمی‌دهید'),
(5167, 61, 4, 'در حقیقت خداوند دوست دارد کسانی را که در راه او صف در صف جهاد می‌کنند، چنانکه گویی ایشان بنیادی ریخته از سرب‌اند'),
(5168, 61, 5, 'و چنین بود که موسی به قومش گفت ای قوم من چرا مرا می‌آزارید و حال آنکه می‌دانید که پیامبر خداوند به سوی شما هستم، پس چون [از حق‌] برگشتند خدا دلهایشان را برگردانید، و خداوند نافرمانان را هدایت نمی‌کند'),
(5169, 61, 6, 'و چنین بود که عیسی بن مریم گفت ای بنی‌اسرائیل من پیامبر خداوند به سوی شما هستم، [و] استوار دارنده توراتی که پیشاروی من است و بشارتگر به پیامبری که پس از من می‌آید و نامش احمد است، و چون بر ایشان پدیده‌های شگرف آورد، گفتند این جادوی آشکار است‌'),
(5170, 61, 7, 'و کیست ستمکارتر از کسی که بر خداوند دروغ بندد، و حال آنکه به سوی اسلام دعوت می‌گردد، و خداوند قوم ستمکار را هدایت نمی‌کند'),
(5171, 61, 8, 'می‌خواهند نور الهی را با سخنان خویش خاموش کنند و حال آنکه خداوند کمال بخش نور خویش است، ولو آنکه کافران ناخوش داشته باشند'),
(5172, 61, 9, 'او کسی است که پیامبرش را با هدایت و دین حق فرستاده است تا آنان را بر همه ادیان پیروز گرداند، ولو مشرکان ناخوش داشته باشند'),
(5173, 61, 10, 'ای مؤمنان آیا شما را به داد و ستدی رهنمون شوم که شما را از عذاب دردناک می‌رهاند؟'),
(5174, 61, 11, '[و آن اینکه‌] به خداوند و پیامبر او ایمان آورید و به مال و جان در راه خدا جهاد کنید، این، اگر بدانید، برایتان بهتر است‌'),
(5175, 61, 12, 'تا بر شما گناهانتان را بیامرزد و شما را به بوستانهایی که جویباران از فرودست آن جاری است، و خانه‌های پاک و پسندیده در بهشتهای عدن درآورد، این رستگاری بزرگ است‌'),
(5176, 61, 13, 'و [نعمت‌] دیگری که آن را خوش دارید یاری از جانب خداوند و پیروزی نزدیک، و مؤمنان را بشارت ده‌'),
(5177, 61, 14, 'ای مؤمنان یاوران [دین‌] خدا باشید، چنانکه عیسی بن مریم به حواریون گفت چه کسانی در راه دین خدا یاوران منند؟ حواریون گفتند ما یاوران [دین‌] خداوندیم، سپس گروهی از بنی‌اسرائیل ایمان آوردند و گروهی [انکار و] کفرورزیدند، آنگاه مؤمنان را در برابر دشمنشان یاری دادیم، و پیروز گردیدند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'آنچه در آسمانها و آنچه در زمین است خداوند را نیایش کند، [خداوندی که‌] فرمانروای قدوس پیروزمند فرزانه است‌'),
(5179, 62, 2, 'اوست که در میان قوم بی‌کتاب [عرب‌] پیامبری از میان خودشان برانگیخت که آیات او را بر آنان می‌خواند و پاکیزه‌شان می‌دارد، و به آنان کتاب و حکمت می‌آموزد، و حقا که در گذشته در گمراهی آشکاری بودند'),
(5180, 62, 3, 'و دیگرانی از آنان که هنوز به آنان نپیوسته‌اند، و اوست پیروزمند فرزانه‌'),
(5181, 62, 4, 'این بخشش الهی است که به هر کس خواهد ارزانی‌اش دارد، و خداوند دارای بخشش و بخشایش بیکران است‌'),
(5182, 62, 5, 'داستان کسانی که [عمل به‌] تورات بر آنان تکلیف شد، سپس آن را [چنانکه باید و شاید] رعایت نکردند، همانند چارپایی بر او کتابی چند است، چه بد است وصف گروهی که آیات الهی را تکذیب کردند، و خداوند قوم ستمکار [مشرک‌] را هدایت نمی‌کند'),
(5183, 62, 6, 'بگو ای یهودیان اگر گمان می‌کنید که شما از میان همه مردم، دوستان خدا هستید، اگر راست می‌گویید آرزوی مرگ کنید'),
(5184, 62, 7, 'و به خاطر کار و کردار پیشینشان هرگز آن را آرزو نمی‌کنند، و خداوند به ستمکاران آگاه است‌'),
(5185, 62, 8, 'بگو همان مرگی که از آن می‌گریزید، روی آور به شماست، سپس به سوی دانای پنهان و پیدا بازگردانده شوید، آنگاه شما را به [نتیجه و حقیقت‌] آنچه کرده‌اید، آگاه می‌سازد'),
(5186, 62, 9, 'ای مؤمنان چون برای نماز روز جمعه بانگ [اذن‌] در داده شود، به سوی یاد کرد خداوند بشتابید و خرید و فروش را رها کنید، اگر بدانید این برایتان بهتر است‌'),
(5187, 62, 10, 'سپس چون نماز گزارده شود، در زمین پراکنده شوید [و به راه خود بروید] و [روزی خود] از بخشش الهی بجویید، و خداوند را بسیار یاد کنید، باشد که رستگار شوید'),
(5188, 62, 11, 'و [غافلان‌] چون داد و ستدی یا سرگرمی‌ای ببینند، به سوی آن بشتابند و تو را ایستاده رها کنند، بگو آنچه نزد خداوند است از سرگرمی و از داد و ستد بهتر است، و خداوند بهترین روزی‌دهندگان است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'منافقان چون به نزد تو آیند گویند گواهی می‌دهیم که تو پیامبر خدا هستی، و خداوند می‌داند که تو پیامبرش هستی، و خداوند گواهی می‌دهد که منافقان دروغگو هستند'),
(5190, 63, 2, 'سوگندهایشان را سپر بلا گرفته‌اند، سپس [مردمان را] از راه خدا بازداشته‌اند، چه بد است کاری که آنان می‌کنند'),
(5191, 63, 3, 'این از آن است که ایشان ایمان آورده‌اند سپس کفر ورزیده‌اند، و بر دلهایشان مهر نهاده شده است، و ایشان در نمی‌یابند'),
(5192, 63, 4, 'و چون ایشان را بنگری، بدنهای ایشان تو را به شگفت‌آورد، و چون سخن گویند به سخنانشان گوش دهی، گویی همانند الوارهای تکیه داده بر دیوارند، و هر بانگی را بر ضد خویش می‌انگارند، آنان دشمن‌اند، از آنان بر حذر باش، خداوند لعنتشان کند، چگونه بیراهه می‌روند؟'),
(5193, 63, 5, 'و چون به ایشان گفته شود بیایید که پیامبر خدا برای شما آمرزش بطلبد، سرهای خود را [به انکار] برگردانند، و بنگریشان که [از اعتذار و استغفار] رویگردانند و گردنکش‌اند'),
(5194, 63, 6, 'در حق آنان یکسان است چه برایشان آمرزش بخواهی، چه بر ایشان آمرزش نخواهی، هرگز خداوند آنان را نخواهد آمرزید، بی‌گمان خداوند نافرمانان را هدایت نمی‌کند'),
(5195, 63, 7, 'ایشان کسانی هستند که می‌گویند برای کسانی که نزد پیامبر خدا هستند چیزی هزینه نکنید تا آنکه [از پیرامون او] پراکنده شوند، و حال آنکه خزانه‌های آسمان و زمین از آن خداوند است، ولی منافقان در نمی‌یابند'),
(5196, 63, 8, 'می‌گویند چون به مدینه بازگردیم، بلندپایگان، فرومایگان را از آنجا بیرون خواهند کرد، حال آنکه عزت، خاص خداوند و پیامبر او و مؤمنان است، ولی منافقان نمی‌دانند'),
(5197, 63, 9, 'ای مؤمنان اموال و اولادتان، شما را از یاد خداوند بازندارد، و هر کس چنین کند، آنانند که زیانکارانند'),
(5198, 63, 10, 'و پیش از آنکه مرگ به سراغ هر یک از شما بیاید و او بگوید پروردگارا چرا مرا تا مهلتی نزدیک باز پس نداشتی که بتوانم صدقه بدهم و از شایستگان باشم، از آنچه روزیتان کرده‌ایم، بخشش کنید'),
(5199, 63, 11, 'و خداوند هرگز کسی را چون اجلش فرا رسد، بازپس ندارد، و خداوند به آنچه می‌کنید آگاه است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'آنچه در آسمانها و آنچه در زمین است، خداوند را نیایش کند، فرمانروایی او راست، و سپاس او راست، و او بر هر کاری تواناست‌'),
(5201, 64, 2, 'و اوست که شما را آفریده است، و بعضی از شما کافرند، و بعضی از شما مؤمن‌اند، و خداوند به آنچه می‌کنید بیناست‌'),
(5202, 64, 3, 'آسمانها و زمین را به حق آفرید و شما را نقش و نگار کرد و پیکرهای شما را بر آراست، و سیر و سرانجام به سوی اوست‌'),
(5203, 64, 4, 'آنچه در آسمانها و زمین است می‌داند و آنچه پنهان می‌دارید و آنچه آشکار می‌دارید، می‌داند، و خداوند به راز دلها داناست‌'),
(5204, 64, 5, 'آیا خبر کسانی که پیش از این کفرورزیدند به شما نرسیده است که عقوبت کارشان را چشیدند و برای آنان عذابی دردناک [در پیش‌] است‌'),
(5205, 64, 6, 'این از آن است که پیامبرانشان برای آنان پدیده‌های شگرف آوردند، و گفتند آیا بشری می‌خواهد هدایتمان کند، پس کفر [و انکار]ورزیدند و روی بر تافتند و خداوند بی‌نیازی نمود و خداوند بی‌نیاز ستوده است‌'),
(5206, 64, 7, 'کافران پنداشته‌اند که هرگز برانگیخته نمی‌شوند، بگو آری سوگند به پروردگارم که برانگیخته خواهید شد، سپس از [حقیقت‌] آنچه کرده‌اید، آگاهتان کنند، و این بر خداوند آسان است‌'),
(5207, 64, 8, 'پس به خداوند و پیامبر او و نوری که فرو فرستاده‌ایم ایمان آورید، و خداوند به آنچه می‌کنید آگاه است‌'),
(5208, 64, 9, 'روزی که برای روز محشر گردتان آورد، آن روز تغابن است و هر کس به خداوند ایمان آورد و نیکوکاری کند، گناهانش را از او می‌زداید، و او را به بوستانهایی که جویباران از فرودست آن جاری است، درآورد، که جاودانه در آن باشند، این رستگاری بزرگ است‌'),
(5209, 64, 10, 'و کسانی که به آیات ما کفر و انکار ورزیده‌اند، اینان دوزخیانند، جاودانه در آنند و بد سرانجامی است‌'),
(5210, 64, 11, 'هیچ مصیبتی [به کسی‌] نرسد مگر به اذن الهی، و هر کس به خداوند ایمان آورد [او] دلش را هدایت کند، و خداوند به هر چیزی داناست‌'),
(5211, 64, 12, 'و از خداوند اطاعت کنید، و از پیامبر [هم‌] اطاعت کنید، و اگر روی برتابید [بدانید که‌] بر پیامبر ما فقط پیام رسانی آشکار است‌'),
(5212, 64, 13, 'خداوند است که خدایی جز او نیست، و مؤمنان فقط بر خداوند باید توکل کنند'),
(5213, 64, 14, 'ای مؤمنان بی‌گمان از میان همسران و فرزندانتان بعضی دشمن شما هستند، از آنان بر حذر باشید، و اگر گذشت و بخشایش پیشه کنید و درگذرید، خداوند هم آمرزگار مهربان است‌'),
(5214, 64, 15, 'همانا اموال و اولاد شما مایه آزمون [و دردسر]اند، و پاداش بیکران فقط نزد خداوند است‌'),
(5215, 64, 16, 'تا آنجا که بتوانید از خداوند پروا کنید و [حق را] بشنوید و اطاعت پیشه کنید و به انفاق بپردازید که به خیر خودتان است، و کسانی که از آزمندی نفس خویش در امان مانند رستگارند'),
(5216, 64, 17, 'اگر در راه خداوند قرض‌الحسنه دهید، برای شما [اجرش را] دو چندان کند و شما را بیامرزد، و خداوند قدردان بردبار است‌'),
(5217, 64, 18, 'دانای پنهان و پیدا [و] پیروزمند فرزانه است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'ای پیامبر، چون خواهید زنان را طلاق گویید، از هنگامی که عده‌شان آغاز تواند شد [طهر غیر مواقعه‌]، طلاق دهید، و حساب عده را نگه دارید، و از خداوند، پروردگارتان، پروا کنید، آنان را از خانه‌هایشان بیرون مکنید، و خودشان هم بیرون نروند، مگر آنکه مرتکب کار ناشایست آشکاری شوند، و این احکام الهی است و هر کس از [حد و مرز] احکام الهی تجاوز کند، به راستی در حق خود ستم روا داشته است، ندانی باشد که خداوند بعد از این کاری تازه پدید آورد'),
(5219, 65, 2, 'پس چون به پایان عده‌شان نزدیک شوند، یا به نیکویی نگاهشان دارید [و رجوع کنید]، یا با خیر و خوشی از آنان جدا شوید، و [بر این کار] دو [شاهد] عادل از میان خودتان را شاهد بگیرید، و شهادت را برای خداوند برپا دارید، این است که هر کس به خداوند و روز بازپسین ایمان دارد، بدان اندرز داده شود، و هر کس که از خداوند پروا [و پارسایی پیشه‌] کند، [خداوند] برای او بیرون‌شدی بگذارد'),
(5220, 65, 3, 'و او را از جایی که گمان نمی‌برد، روزی رساند، و هر کس بر خداوند توکل کند، همو وی را کافی است، بی‌گمان خداوند سررشته‌دار کار خویش است، به راستی که خداوند برای هر چیز اندازه‌ای نهاده است‌'),
(5221, 65, 4, 'و کسانی از زنان شما که از حیض مایوسند، اگر در کارشان شک دارید، بدانید که عده آنان سه ماه [/سه پاکی‌] است، همچنین [است امر] زنانی که حیض ندیده‌اند، و بارداران، سرآمد عده‌شان این است که وضع حمل کنند، و هر کس از خداوند پرواکند، در کارش آسانی پدید آورد'),
(5222, 65, 5, 'این امر الهی است که به سوی شما نازلش کرده است، و هر کس از خداوند پرواکند، گناهانش را از او می‌زداید و پاداش او را بزرگ گرداند'),
(5223, 65, 6, 'آنان را در همانجا که خود سکنا دارید، در حد وسعتان سکنا دهید، و به آنان زیان مرسانید که عرصه را بر آنان تنگ بگیرید، و اگر [مطلقات‌] باردار باشند، خرجشان را بدهید تا آنکه وضع حمل کنند، و اگر برای شما [نوزاد را] شیر دادند، پس اجرتشان را بپردازید، و در میان خود به نیکی رایزنی کنید، و اگر به توافق نرسیدید [زن‌] دیگری او را شیر دهد'),
(5224, 65, 7, 'تا توانگر بر وفق توان خود خرج کند و کسی که روزی‌اش تنگ شده است، در همان حد که خداوند به او بخشیده است خرج کند، خداوند بر هیچ کس جز بر وفق آنچه به او بخشیده است، تکلیف نمی‌کند، زودا که خداوند پس از تنگنا، آسایش مقرر دارد'),
(5225, 65, 8, 'و چه بسا شهرها که [اهالی آن‌] از فرمان پروردگارش و پیامبران او سرپیچید، آنگاه به سختی از آنان حساب کشیدیم، و به عذابی بی‌مانند معذبشان داشتیم‌'),
(5226, 65, 9, 'بدین‌سان عقوبت کار خود را چشید و عاقبت کارش زیان بود'),
(5227, 65, 10, 'خداوند برای آنان عذابی سهمگین آماده کرده است، پس ای خردمندان مؤمن از خداوند پروا کنید، به راستی که خداوند به سوی شما پندآموزی فرستاده است‌'),
(5228, 65, 11, 'پیامبری که آیات روشنگر الهی را بر شما می‌خواند، تا کسانی را که ایمان آورده‌اند و کارهای شایسته کرده‌اند، از تاریکیها به سوی روشنایی برآورد، و هر کس به خداوند ایمان آورد و نیکوکاری کند، او را به بوستانهایی که جویباران از فرودست آن جاری است، درآورد، و جاودانه در آنند، به راستی که خداوند روزی او را نیکو گردانده است‌'),
(5229, 65, 12, 'خداوند کسی است که هفت آسمان را آفریده است، و از زمین نیز همانند آن را، فرمان او از میان آنها نازل می‌گردد، تا بدانید که خداوند بر هر کاری توانا است، و اینکه خداوند بر هر چیز احاطه علمی دارد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'ای پیامبر چرا در طلب خشنودی [بعضی از] همسرانت چیزی را که خداوند بر تو حلال گردانده است، تحریم می‌کنی؟ و خداوند [در همه حال‌] آمرزگار مهربان است‌'),
(5231, 66, 2, 'به راستی که خداوند شکستن سوگندهایتان را [با دادن کفاره‌] بر شما روا داشته است، و خداوند سرور شماست و او دانای فرزانه است‌'),
(5232, 66, 3, 'و چنین بود که پیامبر سخنی را پوشیده‌وار به بعضی از همسرانش گفت، و چون آن را [آن زن به زن دیگر] خبر داد، و خداوند او را از آن آگاه کرد، بخشی از آن را [در عتاب خود به آنان‌] بیان داشت و از بخش دیگر صرفنظر کرد، و چون آن زن [صاحب راز و افشاءگر آن‌] را از آن آگاه کرد، گفت چه کسی تو را از این خبر داد؟ فرمود مرا خداوند دانای آگاه با خبر کرد'),
(5233, 66, 4, 'اگر شما دو زن به درگاه خداوند توبه کنید، و به راستی هم دلهای شما [از طاعت خداوند و پیامبر او در این امر] برگشته است [از گناه شما در می‌گذرد]، و اگر در برابر او همدستی کنید، بدانید که خداوند دوست اوست و جبرئیل و صالح‌[ترین‌] مؤمنان، و علاوه بر آن فرشتگان هم پشتیبانند'),
(5234, 66, 5, 'چه بسا اگر شما را طلاق دهد، پروردگارش برای او همسرانی بهتر از شما جانشین گرداند که زنانی مسلمان، مؤمن، فرمانبردار، توبه‌کار، پرستشگر خداوند، روزه‌دار، اعم از بیوه و دوشیزه باشند'),
(5235, 66, 6, 'ای مؤمنان خود و خانواده‌تان را از آتشی حفظ کنید که هیزم آن مردم و سنگها هستند و فرشتگانی درشت‌خو و سخت‌رو بر آن گمارده‌اند که از خداوند در آنچه فرمانشان دهد سرپیچی نمی‌کنند، و هر چه به ایشان فرمان داده می‌شود، انجام دهند'),
(5236, 66, 7, 'ای کافران امروز عذرخواهی مکنید، فقط در برابر کاری که کرده‌اید جزا یابید'),
(5237, 66, 8, 'ای مؤمنان به درگاه خداوند توبه‌ای خالصانه کنید، چه بسا پروردگارتان گناهانتان را از شما بزداید و شما را به بوستانهایی که جویباران از فرودست آن جاری است، در آورد [در] روزی که خداوند پیامبر و ایمان آوردگان همراه او را فرو نگذارد، نورشان در پیشاپیش و سمت راستشان می‌شتابد، گویند پروردگارا نور ما را برای ما به کمال برسان، و ما را بیامرز، که تو بر هر کاری توانایی‌'),
(5238, 66, 9, 'ای پیامبر با کافران و منافقان جهاد کن، و با آنان درشتی کن که سرا و سرانجامشان جهنم است و بد سرانجامی است‌'),
(5239, 66, 10, 'خداوند درباره کافران مثلی می‌زند، و آن همسر نوح است و همسر لوط که در حباله دو بنده از بندگان شایسته ما بودند، سپس به ایشان خیانت کردند، و آن دو [پیامبر] در برابر امر [و عذاب‌] الهی چیزی را از آنان باز نداشتند، و [به آنان‌] گفته شود، همراه سایر وارد شوندگان، وارد آتش [جهنم‌] شوید'),
(5240, 66, 11, 'و نیز خداوند درباره مؤمنان مثلی می‌زند و آن همسر فرعون است که [در اوج سختی کشیدن‌] گفت پروردگارا برای من در نزد خودت، خانه‌ای در بهشت بنا کن، و مرا از شر فرعون و عمل او رهایی ده، و از قوم ستمکار نجاتم بخش‌'),
(5241, 66, 12, 'و نیز مریم دختر عمران را که ناموس خود را محفوظ داشت، آنگاه از روح خویش در آن دمیدیم، و سخنان و کتابهای پروردگارش را استوار گرفت، و از فرمانبرداران بود');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'بزرگا کسی که فرمانروایی به دست اوست و او بر هر کاری تواناست‌'),
(5243, 67, 2, 'کسی که مرگ و زندگی را آفرید تا شما را بیازماید که کدامیک نیکو کردارترید، و او پیروزمند آمرزگار است‌'),
(5244, 67, 3, 'کسی که هفت آسمان را توبرتو آفرید، در آفرینش خداوند رحمان، هیچ گونه نابسامانی نمی‌بینی، باز چشم بگردان آیا هیچ رخنه‌ای می‌بینی؟'),
(5245, 67, 4, 'سپس دوباره چشم بگردان، چشمت خسته و مانده باز می‌گردد'),
(5246, 67, 5, 'و به راستی آسمان دنیا را به چراغهای [ستارگان‌] آراستیم و آنها را رماننده شیاطین گرداندیم و برای آنان عذاب آتش افروخته، آماده ساختیم‌'),
(5247, 67, 6, 'و برای کسانی که به پروردگارشان کفرورزیدند عذاب جهنم [آماده‌] است، و چه بد سرانجامی است‌'),
(5248, 67, 7, 'چون در آنجا افکنده شوند، نعره آن را می‌شنوند، و آن بر می‌جوشد'),
(5249, 67, 8, 'نزدیک است که از خشم پاره پاره گردد، هرگاه که گروهی در آن افکنده شوند، نگهبانانش از ایشان پرسند آیا هشداردهنده‌ای نزد شما نیامد؟'),
(5250, 67, 9, 'گویند چرا، هشداردهنده‌ای نزد ما آمد، آنگاه [او را] تکذیب کردیم و گفتیم خداوند چیزی فرو نفرستاده است، شما جز در گمراهی بزرگ نیستید'),
(5251, 67, 10, 'و گویند اگر [سخنان آنان را] به سمع قبول می‌شنیدیم یا تعقل می‌کردیم، از زمره دوزخیان نبودیم‌'),
(5252, 67, 11, 'و بدین‌سان به گناهشان اعتراف می‌کنند، لعنت باد دوزخیان را'),
(5253, 67, 12, 'بی‌گمان کسانی که به نهان از پروردگارشان می‌هراسند، برای آنان آمرزش و پاداشی بزرگ است‌'),
(5254, 67, 13, 'و سخنتان را چه پوشیده دارید چه آشکار، او به راز دلها داناست‌'),
(5255, 67, 14, 'آیا کسی که آفریده است، علم ندارد؟ و اوست باریک‌بین آگاه‌'),
(5256, 67, 15, 'اوست کسی که زمین را برای شما رام گردانید، پس در گوشه و کنار آن رهسپار شوید و از روزی او بخورید، و رستاخیز به امر [و در پیشگاه‌] اوست‌'),
(5257, 67, 16, 'آیا از آن که در آسمان است ایمنید که شما را در زمین فرو برد، در حالتی که آن ناگاه می‌جنبد؟'),
(5258, 67, 17, 'یا از آن که در آسمان است ایمنید که بر شما شن‌بادی بفرستد، زودا که بدانید هشدار من چگونه است‌'),
(5259, 67, 18, 'و به راستی که پیشینیان آنان تکذیب پیشه کردند پس [بنگر که‌] عقوبت من چگونه بود'),
(5260, 67, 19, 'آیا به پرندگان بر فراز سرشان ننگریسته‌اند، که بال گشاده‌اند و فرو بندند [آن را]، هیچ کس جز خداوند رحمان آنها را نگه نمی‌دارد، که او به هر چیزی بیناست‌'),
(5261, 67, 20, 'یا کیست آنکه سپاه شماست که شما را در برابر خدای رحمان یاری دهد؟ کافران جز در [توهم و] فریب خوردگی نیستند'),
(5262, 67, 21, 'یا کیست آنکه اگر [خداوند] روزی‌اش را باز گیرد، شما را روزی دهد؟، حق این است که در گردنکشی و رمیدگی پای می‌فشرند'),
(5263, 67, 22, 'آیا کسی که به رو در افتاده نگونسار می‌رود، رهیافته‌تر است، یا کسی که استوار بر راه راست ره می‌سپارد؟'),
(5264, 67, 23, 'بگو او کسی است که شما را پدید آورده است و برای شما گوش [ها] و چشم [ها] و قلب [ها] آفریده است، چه اندکمایه سپاس می‌گزارید'),
(5265, 67, 24, 'بگو او کسی است که شما را در زمین پدید آورد، و به نزد او محشور می‌شوید'),
(5266, 67, 25, 'و می‌گویند اگر راست می‌گویید، پس کی این وعده فرا می‌رسد؟'),
(5267, 67, 26, 'بگو آگاهی از آن فقط نزد خداوند است، و من فقط هشداردهنده‌ای آشکارم‌'),
(5268, 67, 27, 'و چون آن را نزدیک بینند، چهره‌های کافران اندوهگین شود، و [به آنان‌] گفته شود، این همان است که [در دنیا از ما به شتاب‌] می‌طلبیدید'),
(5269, 67, 28, 'بگو ملاحظه کنید، اگر خداوند مرا و همراهانم را نابود کند، یا بر ما رحمت آورد، پس کیست که کافران را از عذابی دردناک پناه دهد؟'),
(5270, 67, 29, 'بگو او خداوند رحمان است، که به او ایمان آورده‌ایم و بر او توکل کرده‌ایم، زودا بدانید که چه کسی در گمراهی آشکار است‌'),
(5271, 67, 30, 'بگو ملاحظه کنید، اگر آب شما در زمین فرو رود [و ناپدید گردد] چه کسی برای شما آب روان می‌آورد؟');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'ن [نون‌]، سوگند به قلم و آنچه نویسند'),
(5273, 68, 2, 'تو -به لطف پروردگارتدیوانه نیستی‌'),
(5274, 68, 3, 'و تو را پاداشی ناکاسته [/بی‌منت‌] است‌'),
(5275, 68, 4, 'و تو دارای اخلاق بسیار بزرگوارانه‌ای هستی‌'),
(5276, 68, 5, 'زودا که بنگری و بنگرند'),
(5277, 68, 6, 'که کدامیک از شما دیوانه‌اید'),
(5278, 68, 7, 'بی‌گمان پروردگارت به کسی که از راه او گم گشته و به رهیافتگان آگاه‌تر است‌'),
(5279, 68, 8, 'پس از منکران اطاعت مکن‌'),
(5280, 68, 9, 'خوش دارند که با آنان سازگاری کنی تا با تو سازگاری کنند'),
(5281, 68, 10, 'و از هیچ بیهوده سوگند خوار پستی اطاعت مکن‌'),
(5282, 68, 11, 'عیبجوی روانه در پی سخن‌چینی‌'),
(5283, 68, 12, 'باز دارنده، از نیکی، تجاوزگر گناهکار'),
(5284, 68, 13, 'درشت‌خوی، و بعد از این همه، ناپاکزاد'),
(5285, 68, 14, 'به خاطر آنکه مالدار است و صاحب پسران‌'),
(5286, 68, 15, 'چون آیات ما بر او خوانده شود، گوید افسانه‌های پیشینیان است‌'),
(5287, 68, 16, 'زودا که بر بینی او نشانه‌ای بگذاریم‌'),
(5288, 68, 17, 'ما آنان را آزموده‌ایم، همچنانکه باغداران را هم آزموده‌ایم، آنگاه که سوگند خوردند که صبحگاهان میوه‌های آن را بازچینند'),
(5289, 68, 18, 'و ان شاء الله نگفتند'),
(5290, 68, 19, 'سپس بلایی شبانه از جانب پروردگارت، در حالی که ایشان خفته بودند، بر آن زد'),
(5291, 68, 20, 'و مانند خاکستر سیاه شد'),
(5292, 68, 21, '[آنان بی‌خبر] صبحگاهان همدیگر را فراخواندند'),
(5293, 68, 22, 'که اگر میوه‌چین هستید، پگاه به سراغ کشتزارتان بروید'),
(5294, 68, 23, 'سپس رهسپار شدند، در حالی که آهسته با همدیگر سخن می‌گفتند'),
(5295, 68, 24, 'که مبادا بی‌نوایی به آنجا بر شما وارد شود'),
(5296, 68, 25, 'و بر منع و مضایقه توانا شدند'),
(5297, 68, 26, 'و چون آن [باغ سیاه‌سوخته‌] را دیدند [جا خوردند و] گفتند ما گم گشته‌ایم‌'),
(5298, 68, 27, 'نه، مثل اینکه، بی‌بهره‌ایم‌'),
(5299, 68, 28, 'بهترینشان گفت مگر به شما نگفته بودم که چرا تسبیح [و ان شاء الله‌] نمی‌گویید؟'),
(5300, 68, 29, 'گفتند پاک است پروردگارمان، ما خود ستمکار بودیم‌'),
(5301, 68, 30, 'سپس به سرزنش به همدیگر روی آوردند'),
(5302, 68, 31, 'گفتند وای بر ما، ما سرکش بوده‌ایم‌'),
(5303, 68, 32, 'چه بسا پروردگارمان بهتر از آن را برایمان جانشین گرداند، ما به سوی پروردگارمان می‌گراییم‌'),
(5304, 68, 33, 'این چنین است عذاب، و اگر می‌دانستند عذاب اخروی سهمگین‌تر است‌'),
(5305, 68, 34, 'برای پرهیزگاران در نزد پروردگارشان بهشتهای پرناز و نعمت است‌'),
(5306, 68, 35, 'آیا مسلمانان را همانند گناهکاران می‌شماریم؟'),
(5307, 68, 36, 'شما را چه می‌شود چگونه داوری می‌کنید؟'),
(5308, 68, 37, 'یا مگر کتابی دارید که در آنها مطالعه می‌کنید'),
(5309, 68, 38, 'که در آن برای شما هرچه آرزو کنید هست؟'),
(5310, 68, 39, 'یا شما را پیمانهایی است با ما که حکم آن تا قیامت باقی باشد که هر چه حکم کنید برای شما باشد؟'),
(5311, 68, 40, 'از ایشان بپرس که کدامیک از آنان ضامن این امر است؟'),
(5312, 68, 41, 'یا شرکایی دارند، پس اگر راست می‌گویند شریکانشان را به میان آورند'),
(5313, 68, 42, 'روزی که هنگامه بالا گیرد، و آنان را به سجده دعوت کنند ولی نتوانند'),
(5314, 68, 43, 'دیدگانشان [از ترس‌] فرو افتاده باشد، غبار خفت و خواری بر رخسارشان نشسته، و چنین بود که به سجده دعوت می‌شدند و تندرست بودند'),
(5315, 68, 44, 'پس مرا با کسی که این سخن را تکذیب می‌کند، وابگذار، زودا که آهسته آهسته به نحوی که در نیابند، فرو خواهیمشان گرفت‌'),
(5316, 68, 45, 'و مهلتشان می‌دهم، زیرا تدبیر من سخت استوار است‌'),
(5317, 68, 46, 'یا مگر از آنان مزدی درخواست می‌کنی، و آنان خود را زیر بار تاوان، گرانبار می‌یابند؟'),
(5318, 68, 47, 'یا مگر علم و اسرار غیب با ایشان است و از روی آن می‌نویسند؟'),
(5319, 68, 48, 'پس در انتظار حکم پروردگارت شکیبایی کن و همانند صاحب ماهی [ یونس‌] مباش که [در تاریکی‌] ندا در داد و خشم فرو خورده بود'),
(5320, 68, 49, 'اگر نعمتی از جانب پروردگارش او را دستگیری نمی‌کرد، به کرانه‌[ی بی‌آب و علف‌] افکنده شده بود و قابل ملامت بود'),
(5321, 68, 50, 'سپس پروردگارش او را برگزید و از شایستگانش قرار داد'),
(5322, 68, 51, 'و بسیار نزدیک بود که کافران چون قرآن را شنیدند، تو را با دیدگانشان آسیب برسانند و گفتند او دیوانه است‌'),
(5323, 68, 52, 'و حال آنکه آن جز پندی برای جهانیان نیست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'حاقه‌'),
(5325, 69, 2, 'حاقه چیست؟'),
(5326, 69, 3, 'و چه دانی حاقه چیست؟'),
(5327, 69, 4, 'قوم ثمود و عاد درهم کوب [/قیامت‌] را دروغ انگاشتند'),
(5328, 69, 5, 'و اما ثمود، بر اثر طغیان خود به نابودی کشیده شدند'),
(5329, 69, 6, 'و اما عاد، با تندبادی سخت سرد و بنیان‌کن به نابودی کشیده شدند'),
(5330, 69, 7, 'که خداوند آن را هفت شب و هشت روز پیوسته بر آنان گماشت و آن قوم را در آن حال از پا در افتاده بینی، گویی ایشان خرمابنانی هستند ریشه کن شده‌'),
(5331, 69, 8, 'پس آیا اثری از ایشان بازمانده می‌بینی؟'),
(5332, 69, 9, 'و فرعون و پیشینیان او و [اهل‌] شهرهای نگونسار، طغیان پیش آوردند'),
(5333, 69, 10, 'سپس از پیامبر پروردگارشان نافرمانی کردند، آنگاه [خداوند] آنان را به مؤاخذه‌ای سخت فرو گرفت‌'),
(5334, 69, 11, 'ما آنگاه که سیلاب طغیان کرد، شما را در کشتی سوار کردیم‌'),
(5335, 69, 12, 'تا سرانجام آن را پندآموزی برای شما گردانیم، و گوشهای نیوشا آن را فرا می‌گیرد'),
(5336, 69, 13, 'و چون دمی یگانه در صور دمیده شود'),
(5337, 69, 14, 'و زمین و کوهها برداشته شده و یکباره درهم کوبیده شود'),
(5338, 69, 15, 'پس در چنین روزی واقعه‌[ی قیامت‌] روی دهد'),
(5339, 69, 16, 'و آسمان از هم بشکافد و آن در چنین روزی سست پیوند است‌'),
(5340, 69, 17, 'و فرشتگان بر کناره‌ها[ی آسمان‌] ایستاده باشند، و عرش پروردگارت را در آن روز هشت تن برفرازشان حمل می‌کنند'),
(5341, 69, 18, 'در چنین روزی [بر خداوند] عرضه شوید، و هیچ رازتان پوشیده نماند'),
(5342, 69, 19, 'سپس هر کس که کارنامه‌اش به دست راستش داده شود، [شادمانه‌] گوید بیایید کارنامه مرا بخوانید'),
(5343, 69, 20, 'به یقین می‌دانستم که با حساب و کتاب خود مواجه خواهم شد'),
(5344, 69, 21, 'آنگاه او در زندگی پسندیده‌ای است‌'),
(5345, 69, 22, 'در بهشتی برین‌'),
(5346, 69, 23, 'که میوه‌هایش دسترس است‌'),
(5347, 69, 24, '[به آنان گویند] به خاطر آنچه در روزگاران گذشته انجام داده‌اید، بخورید و بیاشامید گوارا'),
(5348, 69, 25, 'و اما کسی که کارنامه‌اش به دست چپش داده شود، [سوکمندانه‌] گوید ای کاش کارنامه‌ام به من داده نمی‌شد'),
(5349, 69, 26, 'و نمی‌دانستم حساب و کتابم چیست‌'),
(5350, 69, 27, 'کاش آن [مرگ‌] یکسره بود'),
(5351, 69, 28, 'مال و منال من به کار من نیامد'),
(5352, 69, 29, 'دستگاه من از دستم برفت‌'),
(5353, 69, 30, '[گویند] او را فرو گیرید و دربندش کنید'),
(5354, 69, 31, 'سپس به دوزخش درآورید'),
(5355, 69, 32, 'سپس در زنجیری که طولش هفتاد ذرع است، بندش کنید'),
(5356, 69, 33, 'که او به خداوند بزرگ ایمانی نداشت‌'),
(5357, 69, 34, 'و بر اطعام بینوایان ترغیب نمی‌کرد'),
(5358, 69, 35, 'حال امروز و اینجا دوستی ندارد'),
(5359, 69, 36, 'و نه خوراکی، بجز زردابه‌'),
(5360, 69, 37, 'که آن را جز خطاکاران نمی‌خورند'),
(5361, 69, 38, 'پس به آنچه می‌بینید سوگند می‌خورم‌'),
(5362, 69, 39, 'و به آنچه نمی‌بینید'),
(5363, 69, 40, 'که آن [قرآن‌] برخوانده فرستاده گرامی است‌'),
(5364, 69, 41, 'و سخن هیچ شاعری نیست، چه اندک ایمان می‌آورید'),
(5365, 69, 42, 'و سخن هیچ کاهنی نیست، چه اندک پند می‌گیرید'),
(5366, 69, 43, 'و فرو فرستاده‌ای از سوی پروردگار جهانیان است‌'),
(5367, 69, 44, 'و اگر بر ما سخنانی می‌بست‌'),
(5368, 69, 45, 'دست راستش را می‌گرفتیم‌'),
(5369, 69, 46, 'سپس شاهرگش را قطع می‌کردیم‌'),
(5370, 69, 47, 'و هیچ‌یک از شما مدافع او نبود'),
(5371, 69, 48, 'و آن پندآموزی برای پرهیزگاران است‌'),
(5372, 69, 49, 'و ما به یقین می‌دانیم که از میان شما تکذیب‌کنندگانی هستند'),
(5373, 69, 50, 'و آن [پیام‌]، مایه حسرتی بر کافران است‌'),
(5374, 69, 51, 'و آن [پیام‌]، حق‌الیقین است‌'),
(5375, 69, 52, 'پس به نام پروردگار بزرگ خود تسبیح گوی‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'خواهنده‌ای عذابی رخ دادنی را طلب کرد'),
(5377, 70, 2, 'که برای کافران است [و] بازدارنده‌ای ندارد'),
(5378, 70, 3, 'از جانب خداوند صاحب درجات‌'),
(5379, 70, 4, 'که فرشتگان و روح در روزی که به اندازه پنجاه هزار سال [دنیوی‌] است به سوی او فرا می‌روند'),
(5380, 70, 5, 'پس صبری نیکو پیشه کن‌'),
(5381, 70, 6, 'آنان آن را دوردست می‌بینند'),
(5382, 70, 7, 'و ما نزدیک می‌بینیمش‌'),
(5383, 70, 8, 'روزی که آسمان چون فلز گداخته شود'),
(5384, 70, 9, 'و کوهها همچون پشم رنگین گردد'),
(5385, 70, 10, 'و هیچ خویشاوندی از حال خویشاوندی نپرسد'),
(5386, 70, 11, 'در حالی که به همدیگر نشان داده شوند، گناهکار آرزو کند کاش عذاب چنین روزی را،'),
(5387, 70, 12, 'با فدا کردن پسرانش و همسرش و برادرش باز خرد'),
(5388, 70, 13, 'همچنین خاندانش که به او سر و سامان داده است‌'),
(5389, 70, 14, 'همچنین هر کس که در روی زمین است، آنگاه باز رهاندش‌'),
(5390, 70, 15, 'حاشا، آن آتشی زبانه‌زن است‌'),
(5391, 70, 16, 'برکننده پوست سر'),
(5392, 70, 17, 'که هر کس را که [به حق‌] پشت کرد و [از ایمان‌] روی گرداند، به خود می‌خواند'),
(5393, 70, 18, 'همان کسی که مال اندوخت و جا به جا کرد'),
(5394, 70, 19, 'بی‌گمان انسان ناشکیب آفریده شده است‌'),
(5395, 70, 20, 'چون شر به او رسد، بی‌تاب است‌'),
(5396, 70, 21, 'و چون خیر به او رسد، بازدارنده است‌'),
(5397, 70, 22, 'مگر نمازگزاران‌'),
(5398, 70, 23, 'آنان که در نمازهایشان پیگیرند'),
(5399, 70, 24, 'و کسانی که در اموالشان حقی معین است‌'),
(5400, 70, 25, 'برای خواهنده و بی‌بهره‌'),
(5401, 70, 26, 'و کسانی که روز جزا را باور می‌دارند'),
(5402, 70, 27, 'و کسانی که از عذاب پروردگارشان بیمناکند'),
(5403, 70, 28, 'بی‌گمان عذاب پروردگارشان، بدون ایمنی است‌'),
(5404, 70, 29, 'و کسانی که ناموسشان را حفظ می‌کنند'),
(5405, 70, 30, 'مگر در برابر همسرانشان یا ملک یمینهایشان، که در این صورت سرزنش ناپذیرند'),
(5406, 70, 31, 'پس هر کس فراتر از این بجوید، آنانند که تجاوزکارند'),
(5407, 70, 32, 'و کسانی که در برابر امانتها و پیمانهایشان رعایتگرند'),
(5408, 70, 33, 'و کسانی که در مقام گواهی دادنهایشان ایستاده‌اند'),
(5409, 70, 34, 'و کسانی که بر نمازهایشان مواظبند'),
(5410, 70, 35, 'اینان در بوستانهایی، گرامی داشته شده‌اند'),
(5411, 70, 36, 'پس کافران را چه می‌شود که به سوی تو شتابانند'),
(5412, 70, 37, 'از جانب راست و از جانب چپ، گروه در گروه‌'),
(5413, 70, 38, 'آیا هر کدام از آنان طمع می‌برد که به بهشت پرناز و نعمت درآورده شود'),
(5414, 70, 39, 'حاشا ما ایشان را از آنچه می‌دانند آفریده‌ایم‌'),
(5415, 70, 40, 'به پروردگار مشرق‌ها و مغرب‌ها سوگند می‌خورم که ما تواناییم‌'),
(5416, 70, 41, 'که بهتر از آنان را جانشین آنان سازیم و ما درمانده نیستیم‌'),
(5417, 70, 42, 'پس بگذارشان تا ژاژخایی و بازیگوشی کنند تا به دیدار آن روزشان که وعده‌اش را به ایشان داده‌اند، برسند'),
(5418, 70, 43, 'روزی که از گورها بیرون شتابند، گویی به سوی [قربانگاه‌] بتان می‌شتابند'),
(5419, 70, 44, 'دیدگانشان از [ترس‌] فرو افتاده باشد، غبار خفت و خواری بر رخسارشان نشسته، این همان روزی است که به آنان وعده داده بودند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'ما نوح را به سوی قومش فرستاده‌ایم که قومت را پیش از آنکه عذابی دردناک بر سرشان بیاید، هشدار ده‌'),
(5421, 71, 2, 'گفت ای قوم من، من هشداردهنده آشکار شما هستم‌'),
(5422, 71, 3, 'که خداوند را بپرستید و از او پروا و از من پیروی کنید'),
(5423, 71, 4, 'تا گناهانتان را برای شما بیامرزد و شما را تا سرآمدی معین واپس‌بدارد، چرا که اگر بدانید اجل الهی، چون فرا رسد، بازپس داشته نمی‌شود'),
(5424, 71, 5, 'گفت پروردگارا من قومم را شب و روز [به خداپرستی‌] دعوت کردم‌'),
(5425, 71, 6, 'ولی دعوت من جز بر گریزشان نیفزود'),
(5426, 71, 7, 'و من هرچه دعوتشان می‌کنم تا سرانجام آنان را بیامرزی، انگشتانشان را در گوشهایشان می‌گذارند، و خود را در جامه‌هایشان می‌پوشانند و [در کفر] پای می‌فشرند و سخت گردنکشی می‌کنند'),
(5427, 71, 8, 'سپس باز ایشان را آشکارا دعوت کردم‌'),
(5428, 71, 9, 'آنگاه آشکارا به ایشان گفتم و پنهانی به ایشان گفتم‌'),
(5429, 71, 10, 'سپس گفتم از پروردگارتان آمرزش بخواهید چرا که او آمرزگار است‌'),
(5430, 71, 11, 'تا بر شما از آسمان بارانی پیوسته و پیگیر بفرستد'),
(5431, 71, 12, 'و شما را با بخشیدن اموال و پسران مدد رساند و برای شما باغها و برای شما جویبارها پدید آورد'),
(5432, 71, 13, 'شما را چه می‌شود که از عظمت خداوند نمی‌ترسید؟'),
(5433, 71, 14, 'و حال آنکه شما را گونه‌گون آفریده است‌'),
(5434, 71, 15, 'آیا نیندیشیده‌اید که خداوند چگونه هفت آسمان را توبرتو آفریده است؟'),
(5435, 71, 16, 'و ماه را در آن پرتوافشان و خورشید را [چون‌] چراغی قرار داده است‌'),
(5436, 71, 17, 'و خداوند شما را مانند گیاهی از زمین رویاند'),
(5437, 71, 18, 'سپس شما را به آن باز می‌گرداند، و [پس از آن‌] شما را [دوباره‌] بیرون می‌آورد'),
(5438, 71, 19, 'و خداوند زمین را زیرانداز شما کرد'),
(5439, 71, 20, 'تا به شاهراههای گشاده آن راه یابید'),
(5440, 71, 21, 'نوح گفت پروردگارا آنان از من نافرمانی کردند و از کسی که مال و فرزندش جز به زیانکاریش نیفزوده است، پیروی کردند'),
(5441, 71, 22, 'و نیرنگی سترگ در کار آوردند'),
(5442, 71, 23, 'و گفتند نه هرگز دست از خدایانتان و نه هرگز دست از ود و سواع و یغوث و یعوق و نسر بردارید'),
(5443, 71, 24, 'و بسیاری را بیراه کردند و جز بر بیراهی ستمکاران [مشرک‌] میفزای‌'),
(5444, 71, 25, 'بر اثر گناهانشان بود که به غرق کشیده شدند، سپس به آتشی درآورده شدند و برای خود در برابر خداوند یاوری نیافتند'),
(5445, 71, 26, 'و نوح گفت پروردگارا بر روی زمین از کافران دیاری باقی مگذار'),
(5446, 71, 27, 'چرا که اگر باقیشان گذاری، بندگانت را گمراه کنند و جز فاجر کفران‌پیشه‌ای به بار نیاورند'),
(5447, 71, 28, 'پروردگارا مرا و پدر و مادرم را و هر کس را که مؤمن به خانه من درآید و نیز مردان مؤمن و زنان مؤمن را بیامرز، و جز بر تباهی ستمکاران [مشرک‌] میفزای‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'بگو به من وحی فرستاده شده است که گروهی از جن گوش [به قرآن‌] سپردند، سپس گفتند ما قرآنی شگرف را شنیده‌ایم‌'),
(5449, 72, 2, 'که به راستی راه می‌نماید، آنگاه به آن ایمان آوردیم، و هرگز برای پروردگارمان کسی را شریک نمی‌آوریم‌'),
(5450, 72, 3, 'و حقا که بلند است شان پروردگار ما، نه همسری برگزیده است، و نه فرزندی‌'),
(5451, 72, 4, 'و نادان ما بود که در حق خداوند، پریشان می‌گفت‌'),
(5452, 72, 5, 'و ما چنین می‌پنداشتیم که انس و جن هرگز بر خداوند دروغ نمی‌بندند'),
(5453, 72, 6, 'و چنین بود که مردانی از انسانها به مردانی از جنیان پناه می‌بردند، و بر سرکشی آنان می‌افزودند'),
(5454, 72, 7, 'و آنان نیز می‌پنداشتند همچنانکه شما می‌پندارید که خداوند هرگز کسی را [به حشر یا پیامبری‌] بر نمی‌انگیزد'),
(5455, 72, 8, 'و ما در آسمان جستیم و آن را سرشار از نگهبانان نیرومند و شهابها یافتیم‌'),
(5456, 72, 9, 'و ما خود در نشستنگاههایی برای [دزدیده‌] شنیدن از آن می‌نشستیم، اما اکنون هر کس گوش بسپارد، شهابی در کمین خود یابد'),
(5457, 72, 10, 'و ما خود نمی‌دانیم که در حق زمینیان شر خواسته شده است، یا پروردگارشان در حق آنان رهیافت خواسته است‌'),
(5458, 72, 11, 'و از میان ما هم صالحان هست و هم از میان ما جز این، ما [روندگان‌] راههای گوناگونیم‌'),
(5459, 72, 12, 'و ما یقین داشتیم که هرگز در زمین از خداوند گزیر و گریزی نداریم‌'),
(5460, 72, 13, 'و ما چون [پیام‌] هدایت را شنیدیم، به آن ایمان آوردیم، پس هر کس به پروردگارش ایمان آورد، نه از نقصانی بیم داشته باشد، و نه از ستمی‌'),
(5461, 72, 14, 'و جمعی از میان ما مسلمان است، و جمعی کژراه، پس کسانی که اسلام آورند، آنانند که هدایت جسته‌اند'),
(5462, 72, 15, 'و اما کژراهان همانا هیزم جهنمند'),
(5463, 72, 16, 'و اگر در شیوه‌[ی ناصواب‌] پایداری ورزند [از روی استدراج‌] آنان را آب [و امکانات‌] فراوان ارزانی می‌داریم‌'),
(5464, 72, 17, 'تا سرانجام در آن بیازماییمشان، و هر کس از یاد پروردگارش دل بگرداند، او را به عذابی سهمگین بکشاند'),
(5465, 72, 18, 'و همه مسجدها خاص خداوند است، پس در جنب خداوند هیچ کس را [به پرستش‌] مخوان‌'),
(5466, 72, 19, 'و چنین بود که چون بنده خدا [محمد -ص-] او را به نیایش می‌خواند، نزدیکشان بود که بر سر او بریزند'),
(5467, 72, 20, 'بگو فقط پروردگارم را به نیایش می‌خوانم و هیچ کس را شریک او نمی‌شمارم‌'),
(5468, 72, 21, 'بگو من نه صاحب اختیار زیانی برای شما هستم، نه سامانی‌'),
(5469, 72, 22, 'بگو هیچ کس نیست که هرگز مرا در برابر خداوند پناه دهد، و هرگز بجز او پناهی نمی‌یابم‌'),
(5470, 72, 23, '[در اختیار ندارم‌] مگر پیامی از جانب خداوند و رسالت او، و هر کس از خداوند و پیامبر او سرپیچی کند، او راست آتش دوزخ که جاودانه در آن ماند'),
(5471, 72, 24, 'تا هنگامی رسد که آنچه به آنان وعده داده شده است، ببینند، و زودا بدانند که چه کسی سست یاورتر و اندک سپاه‌تر است‌'),
(5472, 72, 25, 'بگو نمی‌دانم آنچه به شما وعده داده‌اند نزدیک است، یا پروردگار من برای آن فاصله‌ای [دراز] قرار داده است‌'),
(5473, 72, 26, 'اوست دانای [راز] پنهان، که هیچ کس را از غیب خویش آگاه نمی‌سازد'),
(5474, 72, 27, 'مگر پیامبری که او بپسندد، که پیشاپیش و پشت سر او نگهبانانی راه دهد'),
(5475, 72, 28, 'تا معلوم بدارد که رسالت پروردگارشان را گزارده‌اند و به آنچه نزد آنان است احاطه دارد، و هر چیز را به شماره می‌شمارد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'ای مرد جامه بر خود پیچیده‌'),
(5477, 73, 2, 'شب را به پای‌دار، مگر اندکی از آن را'),
(5478, 73, 3, 'نیمه‌اش، یا اندکی از نیمه کم نما'),
(5479, 73, 4, 'یا اندکی بر آن بیفزا، و قرآن را شمرده و شیوا بخوان‌'),
(5480, 73, 5, 'ما سخنی سنگین بر تو نازل می‌کنیم‌'),
(5481, 73, 6, 'بی‌گمان شب‌خیزی بیشتر موافقت [دل و زبان‌] در بردارد و از لحاظ سخن استوارتر است‌'),
(5482, 73, 7, 'تو را در روز کار و کوشش بسیار است‌'),
(5483, 73, 8, 'و نام پروردگارت را یاد کن و از همه گسسته و با او پیوسته شو'),
(5484, 73, 9, 'پروردگار مشرق و مغرب، که خدایی جز او نیست، او را کارساز خود بشمار'),
(5485, 73, 10, 'و بر آنچه می‌گویند شکیبایی کن و از آنان به نیکویی دوری کن‌'),
(5486, 73, 11, 'و مرا با منکران متنعم واگذار و اندکی به آنان مهلت ده‌'),
(5487, 73, 12, 'بی‌گمان نزد ما بندها و آتش دوزخ است‌'),
(5488, 73, 13, 'و خوراکی گلوگیر و عذابی دردناک‌'),
(5489, 73, 14, 'روزی که زمین و کوهها بلرزد و کوهها [چون‌] ریگهای روان شود'),
(5490, 73, 15, 'ما به سوی شما پیامبری فرستاده‌ایم که بر شما گواه است، همچنانکه به سوی فرعون هم پیامبری فرستادیم‌'),
(5491, 73, 16, 'آنگاه فرعون از پیامبر نافرمانی کرد، و او را به گیر و داری سخت فرو گرفتیم‌'),
(5492, 73, 17, 'پس اگر کفرورزید، چگونه از روزی که کودکان را پیر می‌گرداند، می‌پرهیزید [و در امان می‌مانید]؟'),
(5493, 73, 18, 'و آسمان بدان شکافته گردد، که وعده او انجام گرفتنی است‌'),
(5494, 73, 19, 'این پندآموزی است، پس هر کس که خواهد راهی به سوی پروردگارش پیش گیرد'),
(5495, 73, 20, 'بی‌گمان پروردگارت می‌داند که تو و گروهی از همراهانت نزدیک به دو سوم شب و [گاه‌] نصف آن و [گاه‌] یک سوم آن را [به نماز] بر می‌خیزی، و خداوند است که شب و روز را سامان می‌دهد، و معلوم داشت که شما هرگز آن را تاب نمی‌آورید، لذا از شما درگذشت، حال هر چه مقدور باشد از قرآن بخوانید، معلوم داشت که بعضی از شما [ممکن است‌] بیمار باشند و دیگران در این سرزمین [تلاش می‌کنند و] گام می‌زنند و روزی خود از فضل الهی می‌جویند، و دیگرانی هم هستند که در راه خدا کارزار می‌کنند، لذا هر چه مقدور باشد از آن بخوانید، و نماز [پنج‌گانه واجب‌] را برپا دارید و زکات بپردازید، و در راه خدا قرض‌الحسنه دهید، و هر آنچه از کارهای خیر که برای خود پیش اندیشی کنید، پاداش آن را بهتر و بزرگتر نزد خداوند خواهید یافت، و از خداوند آمرزش خواهید، که خداوند آمرزگار مهربان است');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'ای مرد جامه بر خود پیچیده‌'),
(5497, 74, 2, 'برخیز و هشدار ده‌'),
(5498, 74, 3, 'و پروردگارت را تکبیر گوی‌'),
(5499, 74, 4, 'و دامنت را پاکیزه بدار'),
(5500, 74, 5, 'و از آلایش [شرک‌] بپرهیز'),
(5501, 74, 6, 'و بخششی مکن که پاداش [و پاسخ‌] بزرگتریابی‌'),
(5502, 74, 7, 'و در راه پروردگارت شکیبایی کن‌'),
(5503, 74, 8, 'و چون در صور دمیده شود'),
(5504, 74, 9, 'بدین‌سان چنین روز، روزی دشوار است‌'),
(5505, 74, 10, '[و] بر کافران آسان نیست‌'),
(5506, 74, 11, 'مرا با کسی که تنها آفریده‌ام واگذار'),
(5507, 74, 12, 'و برای او مالی روزافزون قرار دادم‌'),
(5508, 74, 13, 'و پسرانی حاضر و ناظر'),
(5509, 74, 14, 'و به او [چه بسیار] میدان [و امکان‌] دادم‌'),
(5510, 74, 15, 'باز طمع دارد که افزونتر کنم‌'),
(5511, 74, 16, 'حاشا، که او به آیات ما ستیزه‌جوست‌'),
(5512, 74, 17, 'زودا که مشقتی سنگین بر او تحمیل کنم‌'),
(5513, 74, 18, 'چرا که او اندیشید و سگالید'),
(5514, 74, 19, 'پس مرگ بر او باد چگونه سگالید'),
(5515, 74, 20, 'باز مرگ بر او باد چگونه سگالید'),
(5516, 74, 21, 'سپس بازنگریست‌'),
(5517, 74, 22, 'و آنگاه روی درهم کشید و ترشرویی کرد'),
(5518, 74, 23, 'سپس روی برتافت و گردن کشید'),
(5519, 74, 24, 'و گفت این جز جادویی فرا گرفته [از دیگران‌] نیست‌'),
(5520, 74, 25, 'این جز گفتار آدمی نیست‌'),
(5521, 74, 26, 'زودا که به دوزخش در آوریم‌'),
(5522, 74, 27, 'و تو چه دانی که دوزخ چیست‌'),
(5523, 74, 28, 'نه باقی می‌گذارد، نه رها می‌کند'),
(5524, 74, 29, 'گرداننده رنگ پوستهاست [با سوختن‌]'),
(5525, 74, 30, 'بر آن نوزده [نگهبان‌] است‌'),
(5526, 74, 31, 'و ما نگهبانان آتش دوزخ را جز از فرشتگان نگماشته‌ایم، و شماره آنان را جز آزمونی برای کافران نگردانده‌ایم، تا سرانجام اهل کتاب یقینی افزونتر یابند، و اهل ایمان، بر ایمان خود بیفزایند، و اهل کتاب و مؤمنان [/مسلمانان‌] شک و شبهه نورزند، و تا سرانجام بیماردلان و کافران بگویند، خداوند از این توصیف چه می‌خواهد؟ بدین‌سان خداوند هر کس را بخواهد بیراه می‌گذارد، و هر کس را بخواهد به راه می‌آورد، و سپاهیان پروردگارت را کسی جز خود او نمی‌شناسد، و این جز پندآموزی برای بشر نیست‌'),
(5527, 74, 32, 'چنین نیست [که گویند]، سوگند به ماه‌'),
(5528, 74, 33, 'و شب چون بگذرد'),
(5529, 74, 34, '[و] سوگند به صبح چون روشن شود'),
(5530, 74, 35, 'آن یکی از سترگهاست‌'),
(5531, 74, 36, 'هشداردهنده بشر است‌'),
(5532, 74, 37, 'برای هر کس از شما که خواهد [در راه حق و ایمان‌] پیشتر آید، یا بازپس ماند'),
(5533, 74, 38, 'هر کس در گرو کردار خویش است‌'),
(5534, 74, 39, 'مگر اصحاب یمین‌'),
(5535, 74, 40, 'که در بوستانهایی [هستند و] همپرسی می‌کنند'),
(5536, 74, 41, 'از گناهکاران‌'),
(5537, 74, 42, 'که چه چیزی شما را به راه دوزخ کشاند؟'),
(5538, 74, 43, 'گویند از نمازگزاران نبودیم‌'),
(5539, 74, 44, 'و بینوایان را هم اطعام نمی‌کردیم‌'),
(5540, 74, 45, 'و با ژاژخایان ژاژخایی می‌کردیم‌'),
(5541, 74, 46, 'و روز جزا را دروغ می‌انگاشتیم‌'),
(5542, 74, 47, 'تا آنکه مرگ به سراغمان آمد'),
(5543, 74, 48, 'حال شفاعت شفیعان سودشان ندهد'),
(5544, 74, 49, 'ایشان را چه می‌شود که از اندرز رویگردانند'),
(5545, 74, 50, 'گویی درازگوشانی رمانند'),
(5546, 74, 51, 'که از شیر می‌گریزند'),
(5547, 74, 52, 'بلکه هر یک از ایشان می‌خواهد که به او نامه‌هایی سرگشاده دهند'),
(5548, 74, 53, 'حاشا، حق این است که از آخرت نمی‌ترسند'),
(5549, 74, 54, 'حاشا، آن پندآموزی است‌'),
(5550, 74, 55, 'و هر کس خواهد از آن پندگیرد'),
(5551, 74, 56, 'و پند نگیرند مگر آنکه خدا خواهد، اوست که سزاوار پرهیز ورزیدن است، و سزاوار آمرزگاری‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'سوگند به روز قیامت می‌خورم‌'),
(5553, 75, 2, 'و سوگند به نفس ملامت‌گر'),
(5554, 75, 3, 'آیا انسان چنین می‌پندارد که هرگز استخوانهای [پوسیده و پراکنده‌] او را گرد نمی‌آوریم؟'),
(5555, 75, 4, 'حق این است که تواناییم بر این که سرانگشتهای او را فراهم آوریم‌'),
(5556, 75, 5, 'بلکه انسان می‌خواهد در مهلتی که در پیش دارد، فسق و فجور کند'),
(5557, 75, 6, '[از سر استکبار] می‌پرسد روز قیامت چه وقت است؟'),
(5558, 75, 7, 'آنگاه که دیدگان خیره شود'),
(5559, 75, 8, 'و ماه تیره شود'),
(5560, 75, 9, 'و خورشید و ماه فرا یکدیگر آورده شوند'),
(5561, 75, 10, 'انسان گوید در چنین روزی گریزگاه کجاست؟'),
(5562, 75, 11, 'حاشا، پناهی نیست‌'),
(5563, 75, 12, 'سرانجام [همه چیز و همه کار] با پروردگار توست‌'),
(5564, 75, 13, 'در این روز انسان را از آنچه پیش فرستاده یا باز پس داشته است آگاه سازند'),
(5565, 75, 14, 'حق این است که انسان بر نفس خود بصیر است‌'),
(5566, 75, 15, 'ولو بهانه‌هایش را در میان آورد'),
(5567, 75, 16, 'زبانت را به [بازخوانی وحی‌] مجنبان که در کار آن شتاب کنی‌'),
(5568, 75, 17, 'گردآوری و بازخوانی آن بر عهده ماست‌'),
(5569, 75, 18, 'و چون آن را باز خوانیم، از بازخوانی‌اش پیروی کن‌'),
(5570, 75, 19, 'سپس شرح و بیان آن بر عهده ماست‌'),
(5571, 75, 20, 'حاشا، حق این است که بهره زودیاب [دنیوی‌] را دوست دارید'),
(5572, 75, 21, 'و [بهره دیریاب‌] آخرت را فرو می‌گذارید'),
(5573, 75, 22, 'در چنین روز، چهره‌هایی تازه و خرم باشد'),
(5574, 75, 23, 'به سوی پروردگارشان نگران‌'),
(5575, 75, 24, 'و در چنین روز چهره‌هایی دژم باشد'),
(5576, 75, 25, 'که می‌داند بلایی کمرشکن بر سر او آید'),
(5577, 75, 26, 'حاشا، چون جان به گلوگاهها رسد'),
(5578, 75, 27, 'و گفته شود کیست افسونگر [درمانگر]'),
(5579, 75, 28, 'و به یقین داند که هنگام جدایی است‌'),
(5580, 75, 29, 'و هنگامه بالا گیرد'),
(5581, 75, 30, 'در چنین روز سیر و سرانجام به سوی پروردگار توست‌'),
(5582, 75, 31, '[مدعی‌] نه [حق را] تصدیق کرد، و نه نمازگزارد'),
(5583, 75, 32, 'بلکه دروغ انگاشت و رویگردان شد'),
(5584, 75, 33, 'سپس تبخترکنان به سوی خانواده‌اش رفت‌'),
(5585, 75, 34, 'وای بر تو، باز هم وای بر تو'),
(5586, 75, 35, 'سپس وای بر تو، و باز هم وای بر تو'),
(5587, 75, 36, 'آیا انسان می‌پندارد که به امان خود رها خواهد شد؟'),
(5588, 75, 37, 'آیا نطفه‌ای از منی که [در رحم‌] ریخته شده بود، نبود؟'),
(5589, 75, 38, 'سپس خون بسته‌ای بود که [خداوندش‌] آفرید و به سامان کرد'),
(5590, 75, 39, 'و از آن جفتی نرینه و مادینه پدید آورد'),
(5591, 75, 40, 'آیا آن [آفریدگار] توانا بر آن نیست که مردگان را زنده کند؟');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'آری مدتی از روزگار بر انسان گذشت که هنوز چیزی قابل ذکر نبود'),
(5593, 76, 2, 'ما انسان را از نطفه‌ای آمیخته آفریده‌ایم و به آزمونش کشانده‌ایم، و او را شنوا و بینا ساخته‌ایم‌'),
(5594, 76, 3, 'ما او را به راه آورده‌ایم، چه سپاسگزار باشد، چه ناسپاس‌'),
(5595, 76, 4, 'ما برای کافران زنجیرها و بندها و آتش فروزان آماده ساخته‌ایم‌'),
(5596, 76, 5, 'بی‌گمان نیکان از جامی که آمیزه آن کافور است، می‌نوشند'),
(5597, 76, 6, 'چشمه‌ای که بندگان [درستکار] خداوند از آن می‌آشامند و روان می‌کنندش هرگونه بخواهند'),
(5598, 76, 7, 'آنان به نذر خود وفا می‌کنند و از روزی که شرش دامن‌گستر است، بیم دارند'),
(5599, 76, 8, 'و خوراک را با وجود دوست داشتنش به بینوا و یتیم و اسیر اطعام کنند'),
(5600, 76, 9, '[و در دل گویند] ما فقط برای خشنودی خداوند شما را اطعام می‌کنیم، از شما نه پاداشی می‌خواهیم، و نه سپاسی‌'),
(5601, 76, 10, 'ما از پروردگارمان در روزی که دژم و دشوار است، می‌ترسیم‌'),
(5602, 76, 11, 'سپس خداوند آنان را از شر آن روز در امان دارد و به ایشان تازگی و خرمی بنمایاند'),
(5603, 76, 12, 'و به آنان به خاطر شکیبی که ورزیده‌اند بوستانی [بهشتی‌] و [جامه‌] ابریشم پاداش دهد'),
(5604, 76, 13, 'در آنجا بر اورنگها تکیه زده، در آنجا نه [گرمای‌] خورشیدی بینند و نه سرمای سختی‌'),
(5605, 76, 14, 'و سایه‌های آن به ایشان نزدیک باشد، و میوه‌های آن در کمال دسترس پذیری باشد'),
(5606, 76, 15, 'و بر ایشان ظرفهایی سیمین و کوزه‌هایی که آبگینه است می‌گردانند'),
(5607, 76, 16, 'آبگینه‌هایی از سیم که آنها را به اندازه پیموده باشند'),
(5608, 76, 17, 'و در آنجا به آنان جامی بنوشانند که آمیزه آن زنجبیل است‌'),
(5609, 76, 18, 'از چشمه‌ای در آنجا که سلسبیل نامیده می‌شود'),
(5610, 76, 19, 'و پسرانی جاویدان برگرد آنان گردند، که چون بنگریشان، پنداریشان که مروارید پراکنده‌اند'),
(5611, 76, 20, 'و چون آنجا را بنگری، ناز و نعمت [فراوان‌] و ملک بیکران بینی‌'),
(5612, 76, 21, 'بر بالای آنان جامه‌هایی از ابریشم نازک سبز و ابریشم ستبر است، و به دستبندهای سیمین آراسته‌اند، و پروردگارشان به آنان شرابی پاکیزه نوشاند'),
(5613, 76, 22, 'این پاداشی برای شماست و سعی شما مشکور است‌'),
(5614, 76, 23, 'ما خود قرآن را بر تو چنانکه باید و شاید فرو فرستاده‌ایم‌'),
(5615, 76, 24, 'به انتظار حکم پروردگارت شکیبا باش و از آنان از هیچ گناهکار یا ناسپاسی اطاعت مکن‌'),
(5616, 76, 25, 'و هر بامداد و شامگاهی نام پروردگارت را یاد کن‌'),
(5617, 76, 26, 'و در بخشی از شب برای او [به نماز] سجده کن و در بخشی بلند از شب او را نیایش کن‌'),
(5618, 76, 27, 'بی‌گمان اینان بهره [زودیاب‌] دنیوی را دوست دارند و روزی سنگین را که در پیش دارند، رها می‌کنند'),
(5619, 76, 28, 'ما آنان را آفریده‌ایم و پیوندشان را استوار داشته‌ایم، و چون خواهیم آنان را به همانندانشان جانشین می‌سازیم‌'),
(5620, 76, 29, 'این پندآموزی است، پس هر کس که خواهد راهی به سوی پروردگارش در پیش گیرد'),
(5621, 76, 30, 'و [چنین چیزی‌] نخواهید مگر آنکه خدا خواهد، بی‌گمان خداوند دانای فرزانه است‌'),
(5622, 76, 31, 'هر کس را که خواهد به جوار رحمت خویش در آورد، و برای ستمکاران [مشرک‌] عذابی دردناک آماده ساخته است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'سوگند به فرستادگان پیاپی‌'),
(5624, 77, 2, 'و تندبادهای سخت‌'),
(5625, 77, 3, 'و سوگند به بادهای پراکنده ساز [ابرها]'),
(5626, 77, 4, 'و جداسازان [حق و باطل‌]'),
(5627, 77, 5, 'و سوگند به فرشتگان وحی‌آور'),
(5628, 77, 6, 'برای اتمام حجت و هشدار'),
(5629, 77, 7, 'که آنچه به شما وعده داده شده است، رخ دادنی است‌'),
(5630, 77, 8, 'پس چون ستارگان تیره شوند'),
(5631, 77, 9, 'و چون آسمان شکافته شود'),
(5632, 77, 10, 'و چون کوهها بر باد رود'),
(5633, 77, 11, 'و چون پیامبران را میقات معین کنند'),
(5634, 77, 12, 'برای کدامین روز زمان داده شود؟'),
(5635, 77, 13, 'برای روز داوری‌'),
(5636, 77, 14, 'و تو چه دانی روز داوری چیست؟'),
(5637, 77, 15, 'در چنین روز وای بر منکران‌'),
(5638, 77, 16, 'آیا پیشینیان را نابود نکردیم؟'),
(5639, 77, 17, 'سپس به دنبال آنان واپسینان را روان نساختیم؟'),
(5640, 77, 18, 'با گناهکاران چنین رفتار کنیم‌'),
(5641, 77, 19, 'در چنین روز وای بر منکران‌'),
(5642, 77, 20, 'آیا شما را از آبی بی‌مقدار نیافریده‌ایم؟'),
(5643, 77, 21, 'که سپس آن را در جایگاهی استوار قرار دادیم‌'),
(5644, 77, 22, 'تا هنگامی معین‌'),
(5645, 77, 23, 'پس سامان دادیم و چه نیک سامانگریم‌'),
(5646, 77, 24, 'در چنین روز وای بر منکران‌'),
(5647, 77, 25, 'آیا زمین را فراگیر نساختیم؟'),
(5648, 77, 26, 'چه برای زندگان، چه برای مردگان‌'),
(5649, 77, 27, 'و در آن کوههای بلند پدید آوردیم و به شما آبی شیرین نوشاندیم‌'),
(5650, 77, 28, 'در چنین روز وای بر منکران‌'),
(5651, 77, 29, 'به سوی چیزی که انکارش می‌کردید بروید'),
(5652, 77, 30, 'به سوی دودی دارای سه شاخه بروید'),
(5653, 77, 31, 'که نه سایه‌گستر است و نه از لهیب آتش باز می‌دارد'),
(5654, 77, 32, 'آن شراره‌هایی همچون کوشک می‌پراکند'),
(5655, 77, 33, 'گویی شترانی زرد سیه‌تاب هستند'),
(5656, 77, 34, 'در چنین روز وای بر منکران‌'),
(5657, 77, 35, 'این روزی است که سخن نگویند'),
(5658, 77, 36, 'و به ایشان اجازه ندهند که عذرخواهی کنند'),
(5659, 77, 37, 'در چنین روز وای بر منکران‌'),
(5660, 77, 38, 'این روز داوری است، شما و پیشینیان را گرد آورده‌ایم‌'),
(5661, 77, 39, 'اگر نیرنگی دارید برای من بسگالید'),
(5662, 77, 40, 'در چنین روز وای بر منکران‌'),
(5663, 77, 41, 'پرهیزگاران در سایه‌ساران و [کنار] چشمه‌سارانند'),
(5664, 77, 42, 'و میوه‌هایی که دلخواهشان است‌'),
(5665, 77, 43, 'به خاطر کارهایی که کرده‌اید، بخورید و بنوشید، [خوش و] گوارا'),
(5666, 77, 44, 'ما بدین گونه نیکوکاران را جزا می‌دهیم‌'),
(5667, 77, 45, 'در چنین روز وای بر منکران‌'),
(5668, 77, 46, 'اندکی بخورید و بهره‌مند باشید، چرا که شما گناهکارید'),
(5669, 77, 47, 'در چنین روز وای بر منکران‌'),
(5670, 77, 48, 'و چون به ایشان گفته می‌شد نماز بگزارید، نماز نمی‌گزاردند'),
(5671, 77, 49, 'در چنین روز وای بر منکران‌'),
(5672, 77, 50, 'پس به کدامین سخن پس از آن ایمان می‌آورند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'از چه از همدیگر می‌پرسند؟'),
(5674, 78, 2, 'از خبر بزرگ‌'),
(5675, 78, 3, 'همانکه ایشان در آن اختلاف دارند'),
(5676, 78, 4, 'چنین نیست، زودا که بدانند'),
(5677, 78, 5, 'باز چنین نیست زودا که بدانند'),
(5678, 78, 6, 'آیا زمین را زیرانداز نساخته‌ایم؟'),
(5679, 78, 7, 'و کوهها را مانند میخها'),
(5680, 78, 8, 'و شما را به صورت جفتها[ی گوناگون‌] آفریده‌ایم‌'),
(5681, 78, 9, 'و خوابتان را مایه آرامش گردانده‌ایم‌'),
(5682, 78, 10, 'و شب را همچون پوششی ساخته‌ایم‌'),
(5683, 78, 11, 'و روز را وقت تلاش معاش قرار داده‌ایم‌'),
(5684, 78, 12, 'و برفرازتان هفت آسمان استوار برافراشته‌ایم‌'),
(5685, 78, 13, 'و چراغی درخشان پدید آورده‌ایم‌'),
(5686, 78, 14, 'و از ابرها آبی ریزان فرو فرستاده‌ایم‌'),
(5687, 78, 15, 'تا بدان دانه و گیاه برآوریم‌'),
(5688, 78, 16, 'و بوستانهایی انبوه‌'),
(5689, 78, 17, 'بی‌گمان روز داوری، هنگامی معین است‌'),
(5690, 78, 18, 'روزی که در صور دمیده شود و فوج فوج بیایید'),
(5691, 78, 19, 'و آسمان گشوده شود و دروازه دروازه باشد'),
(5692, 78, 20, 'و کوهها روان کرده شود و چون سرابی باشد'),
(5693, 78, 21, 'بی‌گمان جهنم کمینگاهی است‌'),
(5694, 78, 22, 'و بازگشتگاه سرکشان است‌'),
(5695, 78, 23, 'که روزگارانی در آن به سر برند'),
(5696, 78, 24, 'در آن نه خنکی بینند نه نوشابه‌ای چشند'),
(5697, 78, 25, 'مگر آب جوش و چرکابه‌'),
(5698, 78, 26, 'که جزایی موافق [اعمال ایشان‌] است‌'),
(5699, 78, 27, 'آنان حساب و کتابی را امید نمی‌داشتند'),
(5700, 78, 28, 'و آیات ما را سخت تکذیب می‌کردند'),
(5701, 78, 29, 'و هر چیزی را در نامه‌ای به شمار آورده‌ایم‌'),
(5702, 78, 30, 'پس بچشید که هرگز جز به عذابتان نمی‌افزاییم‌'),
(5703, 78, 31, 'بی‌گمان پرهیزگاران را رستگاری است'),
(5704, 78, 32, 'بوستانها و درختان انگور'),
(5705, 78, 33, 'و [حوریان‌] نارپستان همسال‌'),
(5706, 78, 34, 'و جامهای سرشار'),
(5707, 78, 35, 'در آنجا نه لغوی بشنوند و نه دروغی‌'),
(5708, 78, 36, 'پاداشی است از سوی پروردگارت و بخششی بسنده است‌'),
(5709, 78, 37, 'پروردگار آسمانها و زمین و مابین آنها که خدای رحمان است، و از جانب او اجازه سخن گفتن ندارند'),
(5710, 78, 38, 'روزی که روح [جبرئیل‌] و فرشتگان به صف ایستند و هیچ‌یک جز کسی که خداوند رحمان به او اجازه دهد، و صواب گوید، سخن نگویند'),
(5711, 78, 39, 'این روز واقعی است پس هر که خواهد بازگشتگاهی به سوی پروردگار خویش بجوید'),
(5712, 78, 40, 'ما شما را از عذابی نزدیک هشدار دادیم، از روزی که هر انسانی به [نتیجه‌] آنچه به دستان خویش پیش فرستاده است، بنگرد و کافر [از سر حسرت‌] گوید کاش من خاک بودم‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'سوگند به فرشتگانی که در گرفتن جان [کافران‌] به سختی کوشند'),
(5714, 79, 2, 'و سوگند به فرشتگانی که جان [مؤمنان‌] به نرمی گیرند'),
(5715, 79, 3, 'و سوگند به فرشتگانی که شنا کنان هبوط کنند'),
(5716, 79, 4, 'و سوگند به فرشتگان پیشتاز'),
(5717, 79, 5, 'و به فرشتگانی که کار سازند'),
(5718, 79, 6, 'روزی که [زمین‌] لرزنده بلرزد'),
(5719, 79, 7, 'و پیایند در پی آن آید'),
(5720, 79, 8, 'در چنین روز دلهایی ترسان و لرزان باشند'),
(5721, 79, 9, 'دیدگان ایشان فروشده‌'),
(5722, 79, 10, 'گویند آیا ما به حالت نخستین [زندگی‌] باز گردانده شویم‌'),
(5723, 79, 11, 'آنگاه که استخوانهایی پوسیده شدیم؟'),
(5724, 79, 12, 'گویند این در آن صورت بازگشتی زیانبار خواهد بود'),
(5725, 79, 13, 'آن همین یک بانگ یگانه است‌'),
(5726, 79, 14, 'آنگاه ایشان در هامون [رستاخیز] باشند'),
(5727, 79, 15, 'آیا داستان موسی به تو رسیده است؟'),
(5728, 79, 16, 'چون پروردگارش او را در وادی مقدس طوی ندا داد'),
(5729, 79, 17, '[فرمود] به سوی فرعون برو که او سر به طغیان بر داشته است‌'),
(5730, 79, 18, 'و بگو آیا بر آن هستی که پاکدلی پیشه کنی؟'),
(5731, 79, 19, 'و تو را به سوی پروردگارت راه نمایم و خشیت یابی؟'),
(5732, 79, 20, 'و به او معجزه سترگ نشان داد'),
(5733, 79, 21, 'پس او انکار کرد و سرکشید'),
(5734, 79, 22, 'سپس شتابان روی برتافت‌'),
(5735, 79, 23, 'و [سپاه جادوگران را] گردآورد و ندا [به ادعا] در داد'),
(5736, 79, 24, 'و گفت من پروردگار برتر شما هستم‌'),
(5737, 79, 25, 'آنگاه خداوند به عقوبت [گناه‌] واپسین و نخستین او را فرو گرفت‌'),
(5738, 79, 26, 'بی‌گمان در این برای هر کس که خشیت داشته باشد، عبرتی است‌'),
(5739, 79, 27, 'آیا شما استوار آفریده‌ترید یا آسمان که آن را برافراشت‌'),
(5740, 79, 28, 'سقف آن را بلند برکشید و آن را استوار کرد'),
(5741, 79, 29, 'و شب آن را تاریک کرد و روز آن را برآورد'),
(5742, 79, 30, 'و بعد از آن زمین را گستراند'),
(5743, 79, 31, 'از آن آب و چراگاه بیرون آورد'),
(5744, 79, 32, 'و کوهها را استوار بداشت‌'),
(5745, 79, 33, 'برای برخورداری شما و چارپایانتان‌'),
(5746, 79, 34, 'آنگاه که هنگامه بزرگ فرارسد'),
(5747, 79, 35, 'روزی که انسان از آنچه کوشیده است، یاد آورد'),
(5748, 79, 36, 'و جهنم برای هر که درنگرد آشکار شود'),
(5749, 79, 37, 'آنگاه هر کس طغیان‌ورزیده باشد'),
(5750, 79, 38, 'و زندگانی دنیا را برگزیده باشد'),
(5751, 79, 39, 'بی‌شک جهنم جایگاه اوست‌'),
(5752, 79, 40, 'و اما هر کس که از ایستادن [در موضع حساب و حشر] در پیشگاه پروردگارش ترسیده و نفس را از هوی و هوس بازداشته باشد'),
(5753, 79, 41, 'بی‌گمان بهشت جایگاه اوست‌'),
(5754, 79, 42, 'از تو درباره قیامت پرسند که استقرار آن چه وقت است؟'),
(5755, 79, 43, 'تو در مقامی نیستی که از آن یاد کنی‌'),
(5756, 79, 44, 'منتهای علم آن با پروردگارت توست‌'),
(5757, 79, 45, 'تو فقط هشداردهنده کسی هستی که از آن بهراسد'),
(5758, 79, 46, 'روزی که آن را بنگرند گویی جز شبانگاهی یا بامدادش [در دنیا] به سر نبرده‌اند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'ترشرویی کرد و روی بر تافت‌'),
(5760, 80, 2, 'از اینکه آن نابینا به نزد او آمد'),
(5761, 80, 3, 'و تو چه دانی چه بسا او پاکدلی ورزد'),
(5762, 80, 4, 'یا پندگیرد و پندش سود بخشد'),
(5763, 80, 5, 'اما کسی که بی‌نیازی نشان می‌دهد'),
(5764, 80, 6, '[چرا] تو به او می‌پردازی‌'),
(5765, 80, 7, 'و اگر هم پاکدلی پیشه نکند، ایرادی بر تو نیست‌'),
(5766, 80, 8, 'و اما کسی که شتابان به سویت آمد'),
(5767, 80, 9, 'و او خشیت می‌ورزد'),
(5768, 80, 10, 'تو از او به دیگری می‌پردازی‌'),
(5769, 80, 11, 'چنین نیست، آن پندآموزی است‌'),
(5770, 80, 12, 'هر که خواهد آن را یاد کند'),
(5771, 80, 13, 'در میان صحیفه‌های ارجمند است‌'),
(5772, 80, 14, 'که بلندمرتبه است و پاک داشته‌'),
(5773, 80, 15, 'به دست نویسندگانی‌'),
(5774, 80, 16, 'که گرامیانند و نیکان‌'),
(5775, 80, 17, 'مرگ بر انسان [کافر] چقدر کافرکیش است‌'),
(5776, 80, 18, 'مگر از چه چیزی او را آفریده است‌'),
(5777, 80, 19, 'او را از نطفه‌ای آفریده است و سر و سامان بخشیده است‌'),
(5778, 80, 20, 'سپس راه را بر او آسان داشته است‌'),
(5779, 80, 21, 'سپس او را میرانید و او را در گور کرد'),
(5780, 80, 22, 'سپس هر زمان که خواست او را برانگیزد'),
(5781, 80, 23, 'حاشا، او آنچه به او فرمان داده بود، انجام نداد'),
(5782, 80, 24, 'پس باید انسان به خوراک خویش بنگرد'),
(5783, 80, 25, 'ما آب را به فراوانی فرو ریخته‌ایم‌'),
(5784, 80, 26, 'سپس زمین را به نیکی برشکافته‌ایم‌'),
(5785, 80, 27, 'و در آن دانه [ها] رویانده‌ایم‌'),
(5786, 80, 28, 'و نیز انگور و سبزیجات‌'),
(5787, 80, 29, 'و درخت زیتون و خرما'),
(5788, 80, 30, 'و بوستانهای انبوه‌'),
(5789, 80, 31, 'و میوه و علف‌'),
(5790, 80, 32, 'کالایی برای شما و برای چارپایانتان‌'),
(5791, 80, 33, 'چون بانگ گوش‌فرسا در آید'),
(5792, 80, 34, 'روزی که انسان از برادرش بگریزد'),
(5793, 80, 35, 'و از مادرش و پدرش‌'),
(5794, 80, 36, 'و همسرش و پسرانش‌'),
(5795, 80, 37, 'برای هر کس از آنان در چنین روز کاری باشد که او را از همه باز دارد'),
(5796, 80, 38, 'در چنین روز چهره‌هایی تابناک باشد'),
(5797, 80, 39, 'خندان و شادمان‌'),
(5798, 80, 40, 'و در چنین روز بر چهره‌هایی غبار باشد'),
(5799, 80, 41, 'تیرگی آنها را فرو گیرد'),
(5800, 80, 42, 'اینان همان کافرکیشان نافرمانند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'آنگاه که خورشید تاریک شود'),
(5802, 81, 2, 'و آنگاه که ستارگان رو به خاموشی نهند'),
(5803, 81, 3, 'و آنگاه که کوهها روان کرده شوند'),
(5804, 81, 4, 'و آنگاه که شتران آبستن ده‌ماهه‌[ی بس عزیز] وانهاده گردند'),
(5805, 81, 5, 'و آنگاه که جانوران گرد آورده شوند'),
(5806, 81, 6, 'و آنگاه که دریاها افروخته گردد'),
(5807, 81, 7, 'و آنگاه که جانها را قرین همدگر کنند'),
(5808, 81, 8, 'و آنگاه که از دختر زنده به گور شده پرسیده شود'),
(5809, 81, 9, 'که به کدامین گناه [به ناحق‌] کشته شده است‌'),
(5810, 81, 10, 'و چون کارنامه‌ها گشوده شود'),
(5811, 81, 11, 'و چون آسمان پاره پاره گردد'),
(5812, 81, 12, 'و چون دوزخ فروزانده گردد'),
(5813, 81, 13, 'و چون بهشت نزدیک آورده شود'),
(5814, 81, 14, 'هر کس بداند که چه آماده ساخته است‌'),
(5815, 81, 15, 'پس سوگند می‌خورم به اختران باز گردنده‌'),
(5816, 81, 16, 'سیارگان پنهان رونده‌'),
(5817, 81, 17, 'و سوگند به شب چون برود'),
(5818, 81, 18, 'و سوگند به صبح چون بدمد'),
(5819, 81, 19, 'که آن بر خوانده پیام‌آوری گرامی است‌'),
(5820, 81, 20, 'نیرومندی که نزد [خداوند] صاحب عرش، صاحب مقام است‌'),
(5821, 81, 21, 'در آنجا فرمانش برند و امینش شمرند'),
(5822, 81, 22, 'و همسخن شما دیوانه نیست‌'),
(5823, 81, 23, 'و به راستی او [جبرئیل‌] را در افق آشکار دیده است‌'),
(5824, 81, 24, 'و او در [گزاردن‌] وحی غیبی، بخیل نیست‌'),
(5825, 81, 25, 'و آن سخن شیطان مطرود نیست‌'),
(5826, 81, 26, 'پس به کجا می‌روید؟'),
(5827, 81, 27, 'آن جز پندآموزی برای جهانیان نیست‌'),
(5828, 81, 28, 'برای هر کس از شما که بخواهد راستی و درستی پیشه کند'),
(5829, 81, 29, 'و نخواهید مگر آنچه خداوند، که پروردگار جهانیان است، بخواهد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'چون آسمان گسسته شود'),
(5831, 82, 2, 'و چون ستارگان فروریزند'),
(5832, 82, 3, 'و چون دریاها به هم راه یابند'),
(5833, 82, 4, 'و چون گورها زیر و زبر شود'),
(5834, 82, 5, 'هر کس بداند که چه چیزی پیش داشته و چه چیزی را فرو گذاشته است‌'),
(5835, 82, 6, 'هان ای انسان چه چیزی تو را در حق پروردگار بخشنده‌ات غره کرد؟'),
(5836, 82, 7, 'همان که تو را آفرید و استوار کرد و متعادل ساخت‌'),
(5837, 82, 8, 'به هر صورتی که خواست [وجود] تو را ترکیب کرد'),
(5838, 82, 9, 'چنین نیست، بلکه [روز] جزا را انکار دارید'),
(5839, 82, 10, 'و بر شما نگهبانانی معین شده است‌'),
(5840, 82, 11, 'که نویسندگانی گرامی‌اند'),
(5841, 82, 12, 'آنچه می‌کنید می‌دانند'),
(5842, 82, 13, 'بی‌گمان نیکان در ناز و نعمت [بهشتی‌]اند'),
(5843, 82, 14, 'و بی‌گمان نافرمانان در آتش دوزخند'),
(5844, 82, 15, 'در روز جزا وارد آن شوند'),
(5845, 82, 16, 'و از آن بدور [و در امان‌] نیستند'),
(5846, 82, 17, 'و چه دانی روز جزا چیست‌'),
(5847, 82, 18, 'باز چه دانی که روز جزا چیست‌'),
(5848, 82, 19, 'روزی که هیچ کس، اختیاری در حق هیچ کس ندارد، و در چنین روز کار با خداوند است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'وای بر کم‌فروشان‌'),
(5850, 83, 2, 'کسانی که چون از مردمان پیمانه می‌ستانند، کامل می‌گیرند'),
(5851, 83, 3, 'و چون به ایشان پیمانه دهند یا برایشان [کالایی‌] برکشند، کم می‌گذارند'),
(5852, 83, 4, 'آیا اینان نمی‌دانند که برانگیخته خواهند شد'),
(5853, 83, 5, 'در روزی بزرگ‌'),
(5854, 83, 6, 'روزی که مردم در پیشگاه پروردگار جهانیان بایستند'),
(5855, 83, 7, 'چنین نیست، بی‌گمان کارنامه نافرمانان در سجین است‌'),
(5856, 83, 8, 'و تو چه دانی سجین چیست؟'),
(5857, 83, 9, 'کتابی است نگاشته‌'),
(5858, 83, 10, 'در چنین روز وای بر منکران‌'),
(5859, 83, 11, 'کسانی که روز جزا را منکرند'),
(5860, 83, 12, 'و جز هر تجاوزگر گناهکار هیچ کس آن را انکار نکند'),
(5861, 83, 13, 'که چون آیات ما بر او خوانده شود، گوید افسانه‌های پیشینیان است‌'),
(5862, 83, 14, 'چنین نیست، بلکه آنچه کرده‌اند بر دلهایشان زنگار نهاده است‌'),
(5863, 83, 15, 'چنین نیست، آنان در چنین روز از دیدار پروردگارشان در پرده باشند'),
(5864, 83, 16, 'سپس آنان در آینده به دوزخند'),
(5865, 83, 17, 'سپس گفته شود این همان است که به آن انکار می‌ورزیدید'),
(5866, 83, 18, 'چنین نیست، بی‌گمان کارنامه نیکان در علیین است‌'),
(5867, 83, 19, 'و تو چه دانی علیین چیست‌'),
(5868, 83, 20, 'کتابی است نگاشته‌'),
(5869, 83, 21, 'که مقربان شاهد آنند'),
(5870, 83, 22, 'بی‌گمان نیکان در ناز و نعمت [بهشتی‌]اند'),
(5871, 83, 23, 'بر او رنگها [نشسته‌اند و] می‌نگرند'),
(5872, 83, 24, 'در چهره‌هایشان خرمی ناز و نعمت را باز می‌شناسی‌'),
(5873, 83, 25, 'از شرابی مهر شده به آنان نوشانند'),
(5874, 83, 26, 'که مهر آن از مشک است و در این راه است که رقابت‌کوشان باید به رقابت کوشند'),
(5875, 83, 27, 'و آمیزه آن از تسنیم است‌'),
(5876, 83, 28, 'چشمه‌ای که مقربان از آن می‌نوشند'),
(5877, 83, 29, 'بی‌گمان کسانی که گناه کرده‌اند به مؤمنان می‌خندیدند'),
(5878, 83, 30, 'و چون از کنار ایشان می‌گذشتند، به چشم و ابرو اشارت می‌کردند'),
(5879, 83, 31, 'و چون به سوی خانواده‌شان باز می‌گشتند، ریشخند کنان باز می‌گشتند'),
(5880, 83, 32, 'و چون ایشان را می‌دیدند می‌گفتند اینان گمراهانند'),
(5881, 83, 33, 'و آنان را بر ایشان نگهبان نفرستاده بودند'),
(5882, 83, 34, 'حال امروز مؤمنان به کافران می‌خندند'),
(5883, 83, 35, 'که بر او رنگها می‌نگرند'),
(5884, 83, 36, 'که آیا به کافران در برابر آنچه کرده بودند جزا داده می‌شود');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'آنگاه که آسمان بشکافد'),
(5886, 84, 2, 'و به [امر] پروردگارش گوش بسپارد و خود چنین سزد'),
(5887, 84, 3, 'و آنگاه که زمین بازکشیده گردد'),
(5888, 84, 4, 'و هر چه در دل آن است بیرون اندازد و تهی گردد'),
(5889, 84, 5, 'و به [امر] پروردگارش گوش بسپارد و خود چنین سزد'),
(5890, 84, 6, 'هان ای انسان تو در راه پروردگارت سخت کوشیده‌ای [و رنج برده‌ای‌] و به لقای او نایل خواهی شد'),
(5891, 84, 7, 'اما هر کس که کارنامه‌اش به دست راستش داده شود'),
(5892, 84, 8, 'زودا که حسابی آسان، با او محاسبه گردد'),
(5893, 84, 9, 'و شادمان به سوی خانواده‌اش بازگردد'),
(5894, 84, 10, 'و اما هر کس که کارنامه‌اش از پس پشتش به او داده شود'),
(5895, 84, 11, 'زودا که زاری سر دهد'),
(5896, 84, 12, 'و به دوزخ در آید'),
(5897, 84, 13, 'چرا که او در میان خانواده‌اش [به ناحق‌] شادمان بود'),
(5898, 84, 14, 'و می‌پنداشت که هرگز [به قیامت‌] باز نگردد'),
(5899, 84, 15, 'آری بی‌گمان پروردگارش به [احوال‌] او بینا بود'),
(5900, 84, 16, 'پس سوگند می‌خورم به شفق‌'),
(5901, 84, 17, 'و به شب و آنچه گرد آورد'),
(5902, 84, 18, 'و سوگند به ماه چون کامل گردد'),
(5903, 84, 19, 'که شما از حالی به حالی دیگر در آیید'),
(5904, 84, 20, 'پس ایشان را چه می‌شود که ایمان نمی‌آورند'),
(5905, 84, 21, 'و چون بر آنان قرآن خوانند، به سجده نیفتند'),
(5906, 84, 22, 'بلکه کافران انکار می‌ورزند'),
(5907, 84, 23, 'و خداوند به آنچه در دل می‌دارند داناتر است‌'),
(5908, 84, 24, 'پس ایشان را از عذابی دردناک خبر ده‌'),
(5909, 84, 25, 'مگر کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، که ایشان را پاداشی است ناکاسته [/بی‌منت‌]');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'سوگند به آسمانی که دارای برجهاست‌'),
(5911, 85, 2, 'و سوگند به روز موعود'),
(5912, 85, 3, 'و سوگند به گواه و مورد گواهی‌'),
(5913, 85, 4, 'مرگ بر آتش افروزان گودال پرآتش‌'),
(5914, 85, 5, 'آتشی دارای هیزم‌'),
(5915, 85, 6, 'آنگاه که آنان بر کناره آن نشسته بودند'),
(5916, 85, 7, 'و آنان بر آنچه در حق مؤمنان می‌کردند، گواه بودند'),
(5917, 85, 8, 'و به آنان کینه نورزیدند، مگر از آن روی که به خداوند پیروزمند ستوده ایمان می‌ورزیدند'),
(5918, 85, 9, 'کسی که فرمانروایی آسمانها و زمین او راست، و خداوند بر همه چیز گواه است‌'),
(5919, 85, 10, 'کسانی که مردان مؤمن و زنان مؤمن را آزار دادند و سپس توبه نکردند، برای آنان عذاب جهنم [مهیا] است و عذاب آتش [در پیش‌] دارند'),
(5920, 85, 11, 'بی‌گمان کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، ایشان راست بوستانهایی [بهشتی‌] که جویباران از فرودست آن جاری است، این رستگاری بزرگ است‌'),
(5921, 85, 12, 'بی‌گمان سختگیری پروردگارت، سهمگین است‌'),
(5922, 85, 13, 'اوست که [آفرینش را] آغاز کرده است و باز می‌گرداند'),
(5923, 85, 14, 'و اوست آمرزگار دوستدار'),
(5924, 85, 15, 'صاحب ارجمند عرش‌'),
(5925, 85, 16, 'کردگار خواسته خویش‌'),
(5926, 85, 17, 'آیا داستان سپاهیان به تو رسیده است؟'),
(5927, 85, 18, 'فرعون و قوم ثمود'),
(5928, 85, 19, 'آری کافران در انکارند'),
(5929, 85, 20, 'و خداوند بر فراز ایشان چیره است‌'),
(5930, 85, 21, 'حق این است که آن قرآن مجید است‌'),
(5931, 85, 22, 'در لوحی محفوظ');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'سوگند به آسمان و شباینده‌'),
(5933, 86, 2, 'و تو چه دانی شباینده چیست‌'),
(5934, 86, 3, 'ستاره درخشان است‌'),
(5935, 86, 4, 'هیچ جانی نیست مگر آنکه بر او نگهبانی هست‌'),
(5936, 86, 5, 'پس انسان بنگرد که از چه آفریده شده است‌'),
(5937, 86, 6, 'از آبی جهیده آفریده شده است‌'),
(5938, 86, 7, 'که از میانه پشت و سینه‌ها بیرون آید'),
(5939, 86, 8, 'بی‌گمان او بر باز گرداندن وی تواناست‌'),
(5940, 86, 9, 'روزی که رازها از پرده بیرون افتد'),
(5941, 86, 10, 'آنگاه او را یارا و یاوری نیست‌'),
(5942, 86, 11, 'و سوگند به آسمان باران‌دار'),
(5943, 86, 12, 'و سوگند به زمین برشکافته به گیاه‌'),
(5944, 86, 13, 'که آن [قرآن‌] سخنی قاطع است‌'),
(5945, 86, 14, 'و هزل نیست‌'),
(5946, 86, 15, 'آنان نیرنگی می‌سگالند'),
(5947, 86, 16, 'من نیز تدبیری می‌اندیشم‌'),
(5948, 86, 17, 'پس کافران را مهلت ده، و اندک زمانی فرو گذارشان‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'به نام پروردگارت که بلندمرتبه است، تسبیح گوی‌'),
(5950, 87, 2, 'همانکه آفرید و سامان بخشید'),
(5951, 87, 3, 'همان کسی که اندازه آفرینی و رهنمایی کرد'),
(5952, 87, 4, 'و همان کسی که چراگاهها[ی سبز و خرم‌] رویانید'),
(5953, 87, 5, 'و سرانجام آن را خاشاکی [پژمرده و] سیاه گردانید'),
(5954, 87, 6, 'زودا که تو را به خواندن آوریم و از یاد مگذاری‌'),
(5955, 87, 7, 'مگر آنچه خدا خواهد، که او آشکار و نهان را می‌داند'),
(5956, 87, 8, 'و در کار تو آسانی پدید آوریم‌'),
(5957, 87, 9, 'پس اندرز ده، اگر [دهی‌] اندرز سود دهد'),
(5958, 87, 10, 'زودا کسی که خشیت پیشه کرده است، پند گیرد'),
(5959, 87, 11, 'و شقاوت‌پیشه از آن دوری کند'),
(5960, 87, 12, 'همان کسی که به آتش سترگ درآید'),
(5961, 87, 13, 'آنگاه در آنجا نه بمیرد و نه زنده بماند'),
(5962, 87, 14, 'به راستی هر کس پاکدلی پیشه کرد، رستگار شد'),
(5963, 87, 15, 'و [هر کس‌] که نام پروردگارش را یاد کرد و نیایش کرد'),
(5964, 87, 16, 'حق این است که شما زندگانی دنیا را بر می‌گزینید'),
(5965, 87, 17, 'و حال آنکه آخرت بهتر و پاینده‌تر است‌'),
(5966, 87, 18, 'این [نکته‌] در کتب آسمانی پیشین هم هست‌'),
(5967, 87, 19, 'در صحف ابراهیم و موسی‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'آیا داستان هنگامه فراگیر به تو رسیده است؟'),
(5969, 88, 2, 'در چنین روز چهره‌هایی خاکسار باشند'),
(5970, 88, 3, 'سختی دیده و محنت کشیده‌'),
(5971, 88, 4, 'که به آتشی بس سوزان درآیند'),
(5972, 88, 5, 'از چشمه‌ای گرم به آنان نوشانده شود'),
(5973, 88, 6, 'خوراکی غیر از خار خشک زهردار ندارند'),
(5974, 88, 7, 'که نه فربه گرداند، و نه گرسنگی را چاره کند'),
(5975, 88, 8, 'در چنین روز چهره‌هایی خرم باشند'),
(5976, 88, 9, 'از کوشش [پیشین‌] خود خشنود'),
(5977, 88, 10, 'در بهشت برین‌'),
(5978, 88, 11, 'در آنجا لغوی نشنوند'),
(5979, 88, 12, 'در آنجا چشمه‌ای روان باشد'),
(5980, 88, 13, 'در آنجا تختهایی است بالا بلند'),
(5981, 88, 14, 'و کوزه‌هایی پیش نهاده‌'),
(5982, 88, 15, 'و بالشهایی ردیف شده‌'),
(5983, 88, 16, 'و فرشهای زرباف گسترده‌'),
(5984, 88, 17, 'آیا در شتر نمی‌نگرند که چگونه [بدیع‌] آفریده شده است؟'),
(5985, 88, 18, 'و نیز به آسمان که چگونه برافراشته شده است‌'),
(5986, 88, 19, 'و نیز به کوهها که چگونه بر قرار گردیده است‌'),
(5987, 88, 20, 'و نیز به زمین که چگونه گسترده شده است‌'),
(5988, 88, 21, 'پس اندرز ده که همانا تو اندرزگویی‌'),
(5989, 88, 22, '[و] بر آنان مسلط [و حکمفرما] نیستی‌'),
(5990, 88, 23, 'مگر کسی که روی برتابد و کفر پیشه کند'),
(5991, 88, 24, 'که خداوند او را به عذابی سهمگین، عذاب کند'),
(5992, 88, 25, 'بی‌گمان بازگشت آنان به نزد ماست‌'),
(5993, 88, 26, 'سپس حساب آنان با ماست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'سوگند به سپیده دم‌'),
(5995, 89, 2, 'و سوگند به شبهای ده‌گانه‌'),
(5996, 89, 3, 'و سوگند به زوج و فرد'),
(5997, 89, 4, 'و سوگند به شب چون سرآید'),
(5998, 89, 5, 'آیا در اینها خردمندان را سوگندی سزاوار است؟'),
(5999, 89, 6, 'آیا نیندیشیده‌ای که پروردگارت در حق [قوم و سرزمین‌] عاد چگونه عمل کرد؟'),
(6000, 89, 7, 'که ارم ستون‌دار داشت‌'),
(6001, 89, 8, 'همان که مانندش در [هیچ‌یک از] شهرها ساخته نشده بود'),
(6002, 89, 9, 'و نیز قوم ثمود که در وادی‌القری، [خانه از] سنگ می‌بریدند [و می‌تراشیدند]'),
(6003, 89, 10, 'و فرعون صاحب سپاه‌'),
(6004, 89, 11, 'کسانی که در شهرها سر به طغیان برداشته بودند'),
(6005, 89, 12, 'و در آنجا چه بسیار فتنه و فساد می‌کردند'),
(6006, 89, 13, 'و سرانجام پروردگارت تازیانه عذاب را بر سر آنان کشید'),
(6007, 89, 14, 'بی‌گمان پروردگارت در کمینگاه است‌'),
(6008, 89, 15, 'اما انسان چون پروردگارش او را بیازماید و او را گرامی دارد و به او نعمت بخشد، [شاد و مغرور شود و] گوید پروردگارم مرا گرامی داشت‌'),
(6009, 89, 16, 'و اما چون او را [به بلا و محنت‌] بیازماید و روزی او را بر او تنگ گیرد، [بی‌صبری و ناسپاسی کند و] گوید پروردگارم مرا خوار داشت‌'),
(6010, 89, 17, 'چنین نیست، بلکه یتیم را نمی‌نوازید'),
(6011, 89, 18, 'و بر اطعام بینوا ترغیب نمی‌کنید'),
(6012, 89, 19, 'و میراث را [به حق و ناحق‌] یکسره می‌خورید'),
(6013, 89, 20, 'و مال را چه بسیار دوست می‌دارید'),
(6014, 89, 21, 'چنین نیست، آنگاه که زمین پخش و پراکنده شود'),
(6015, 89, 22, 'و امر پروردگارت فرارسد و فرشتگان صف در صف آیند'),
(6016, 89, 23, 'و در چنین روز جهنم را به میان آورند، در چنین روز انسان پندگیرد و او را چه جای پند گرفتن است‌'),
(6017, 89, 24, '[از سر حسرت‌] گوید کاش من برای این زندگانی [اخروی‌]ام پیش اندیشی کرده بودم‌'),
(6018, 89, 25, 'پس در چنین روز هیچ کس چون عذاب او [خداوند]، عذاب نکند'),
(6019, 89, 26, 'و هیچ کس چون بند نهادن او، بند ننهد'),
(6020, 89, 27, 'هان ای نفس مطمئنه‌'),
(6021, 89, 28, 'به سوی پروردگارت که تو از او خشنودی و او از تو خشنود است، باز گرد'),
(6022, 89, 29, 'و در زمره بندگان من درآی‌'),
(6023, 89, 30, 'و به بهشت من وارد شو');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'سوگند به این شهر می‌خورم‌'),
(6025, 90, 2, 'و تو [یکچند] در این شهر [حکمروای مطلق و] دست گشاده‌ای‌'),
(6026, 90, 3, 'و سوگند به پدر و فرزندی که پدید آورده است‌'),
(6027, 90, 4, 'به راستی انسان را در رنج و محنت کشیدن آفریده‌ایم‌'),
(6028, 90, 5, 'آیا می‌پندارد که هرگز کسی بر او دست ندارد؟'),
(6029, 90, 6, 'گوید مالی فراوان [در راه مبارزه با پیامبر] بر باد دادم‌'),
(6030, 90, 7, 'آیا می‌پندارد که هیچ کس [کار و بار] او را ندیده است؟'),
(6031, 90, 8, 'آیا برای او دو چشم نیافریده‌ایم؟'),
(6032, 90, 9, 'و زبانی و دو لب‌'),
(6033, 90, 10, 'و او را بر سر [هر یک از] دو راه [خیر و شر] نیاورده‌ایم؟'),
(6034, 90, 11, 'ولی خود در پی عقبه نبود'),
(6035, 90, 12, 'و تو چه دانی عقبه چیست‌'),
(6036, 90, 13, 'آزادسازی برده‌'),
(6037, 90, 14, 'یا اطعامی [از بینوایان‌] در روزی گرسنگی زده‌'),
(6038, 90, 15, 'در حق یتیمی خویشاوند'),
(6039, 90, 16, 'یا بینوایی خاک‌نشین‌'),
(6040, 90, 17, 'تا سپس از کسانی باشد که ایمان آورده‌اند و همدیگر را به شکیبایی و همدیگر را به مهربانی سفارش کرده‌اند'),
(6041, 90, 18, 'اینانند که اصحاب یمین‌اند'),
(6042, 90, 19, 'و کسانی که به آیات ما انکار ورزیده‌اند، آنانند که اصحاب شمال‌اند'),
(6043, 90, 20, 'بر آنان آتشی چند لایه، [چیره‌] باشد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سوگند به خورشید و پرتوافشانی‌اش‌'),
(6045, 91, 2, 'و سوگند به ماه چون از آن پیروی کند'),
(6046, 91, 3, 'و سوگند به روز چون روشنش دارد'),
(6047, 91, 4, 'و سوگند به شب چون فرو پوشد'),
(6048, 91, 5, 'و سوگند به آسمان و آنکه آن را برافراشت‌'),
(6049, 91, 6, 'و سوگند به زمین و آنکه آن را بگسترد'),
(6050, 91, 7, 'و سوگند به نفس انسان و آنکه آن را سامان داد'),
(6051, 91, 8, 'آنگاه نافرمانی و پرهیزگاری‌اش را در آن الهام کرد'),
(6052, 91, 9, 'به راستی هر کس که آن را پاکیزه داشت، رستگار شد'),
(6053, 91, 10, 'و به راستی نومید شد هر کس که آن را فرومایه داشت‌'),
(6054, 91, 11, 'قوم ثمود با طغیانش انکار پیشه کرد'),
(6055, 91, 12, 'آنگاه که شقاوت‌پیشه‌ترینشان برپا خاست‌'),
(6056, 91, 13, 'حال آنکه پیامبر خدا به آنان گفته بود این شتر خداوند است، او و بهره آبش را رعایت کنید'),
(6057, 91, 14, 'سپس او را دروغ‌زن شمردند و آن [شتر] را پی کردند، آنگاه پروردگارشان آنان را به گناهشان، به یکسان نابود ساخت‌'),
(6058, 91, 15, 'و از عاقبت کارش نترسد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'سوگند به شب چون فرو پوشاند'),
(6060, 92, 2, 'و سوگند به روز چون روشن شود'),
(6061, 92, 3, 'و سوگند به آنکه نرینه و مادینه آفرید'),
(6062, 92, 4, 'که کوشش شما پراکنده [و گونه‌گون‌] است‌'),
(6063, 92, 5, 'حال اگر کسی [مالی‌] بخشید و پروا و پرهیز ورزید'),
(6064, 92, 6, 'و [وعده‌] بهشت را استوار داشت‌'),
(6065, 92, 7, 'زودا که راهش را به سوی خیر و آسانی هموار کنیم‌'),
(6066, 92, 8, 'و اما کسی که دریغ‌ورزید و بی‌نیازی نمود'),
(6067, 92, 9, 'و وعده بهشت را دروغ انگاشت‌'),
(6068, 92, 10, 'زودا که راهش را به سوی شر و دشواری هموار کنیم‌'),
(6069, 92, 11, 'و چون به هلاکت افتد مالش به کار او نیاید'),
(6070, 92, 12, 'بی‌گمان راهنمایی [بندگان‌] بر ماست‌'),
(6071, 92, 13, 'و بی‌گمان [جهان‌] واپسین و نخستین ما راست‌'),
(6072, 92, 14, 'پس شما را از آتشی که زبانه زند هشدار دهم‌'),
(6073, 92, 15, 'که جز شقاوت پیشه وارد آن نشود'),
(6074, 92, 16, 'همان کسی که [حق را] انکار کرد و روی بر تافت‌'),
(6075, 92, 17, 'و تقواپیشه از آن دور داشته خواهد شد'),
(6076, 92, 18, 'همان کسی که مالش را می‌بخشد که پاکدلی یابد'),
(6077, 92, 19, 'و بر او [منت از] نعمت کسی نیست که باید جزایش را بدهد'),
(6078, 92, 20, '[و نمی‌کند آنچه می‌کند] مگر در طلب خشنودی پروردگار بلندمرتبه‌اش‌'),
(6079, 92, 21, 'و زودا که خشنود شود');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'سوگند به روز روشن‌'),
(6081, 93, 2, 'و سوگند به شب چون بیارمد'),
(6082, 93, 3, 'که پروردگارت با تو بدرود نکرده و بی‌مهر نشده است‌'),
(6083, 93, 4, 'و بی‌شک جهان واپسین برای تو بهتر از نخستین است‌'),
(6084, 93, 5, 'و پروردگارت به زودی به تو [مقام شفاعت‌] می‌بخشد و خشنود می‌شوی‌'),
(6085, 93, 6, 'آیا یتیمت نیافت که سر و سامانت داد'),
(6086, 93, 7, 'و تو را سرگشته یافت و رهنمایی کرد'),
(6087, 93, 8, 'و تو را تهیدست یافت و بی‌نیازت کرد'),
(6088, 93, 9, 'پس با یتیم تندی مکن‌'),
(6089, 93, 10, 'و بر خواهنده بانگ مزن [و او را مران‌]'),
(6090, 93, 11, 'و اما درباره نعمت پروردگارت [با سپاس‌] سخن بگوی‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'آیا دلت را برایت گشاده نداشتیم؟'),
(6092, 94, 2, 'و بارت را [از دوشت‌] برنگرفتیم؟'),
(6093, 94, 3, 'همان که می‌خواست پشت را بشکند'),
(6094, 94, 4, 'و آوازه‌ات را بلند گرداندیم‌'),
(6095, 94, 5, 'بی‌گمان در جنب دشواری، آسانی است‌'),
(6096, 94, 6, 'آری در جنب دشواری آسانی است‌'),
(6097, 94, 7, 'پس چون فراغت یافتی، [در دعا] بکوش‌'),
(6098, 94, 8, 'و به سوی پروردگارت بگرای‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'سوگند به [سرزمین قدسی‌] انجیر و زیتون‌'),
(6100, 95, 2, 'و سوگند به طور سینا'),
(6101, 95, 3, 'و سوگند به این شهر [و حرم‌] امن‌'),
(6102, 95, 4, 'به راستی که انسان را در بهترین قوام آفریده‌ایم‌'),
(6103, 95, 5, 'سپس او را به فرودین فرود باز گرداندیم‌'),
(6104, 95, 6, 'مگر کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، که ایشان را پاداشی ناکاسته [/بی‌منت‌] است‌'),
(6105, 95, 7, '[ای انسان‌] با این حال چه چیزی تو را به انکار روز جزا کشانید؟'),
(6106, 95, 8, 'آیا خداوند داورترین داوران نیست؟');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'بخوان به نام پروردگارت که آفریده است‌'),
(6108, 96, 2, 'که انسان را از [نطفه و سپس‌] خون بسته آفریده است‌'),
(6109, 96, 3, 'بخوان و پروردگار تو بس گرامی است‌'),
(6110, 96, 4, 'همان که با قلم [و کتابت انسان را] آموزش داد'),
(6111, 96, 5, 'به انسان چیزی را که نمی‌دانست، آموخت‌'),
(6112, 96, 6, 'چنین نیست، بی‌گمان انسان سر به طغیان برآورد'),
(6113, 96, 7, 'از این که خود را بی‌نیاز [و توانگر] بیند'),
(6114, 96, 8, 'همانا بازگشت به سوی پروردگار توست‌'),
(6115, 96, 9, 'آیا نگریسته‌ای کسی را که باز می‌دارد'),
(6116, 96, 10, 'بنده‌ای را که به نماز برخیزد'),
(6117, 96, 11, 'آیا اندیشیده‌ای که اگر [پیامبر و پیرو او] بر طریق هدایت باشد'),
(6118, 96, 12, 'یا امر به پرهیزگاری کند [بر حق است‌]'),
(6119, 96, 13, 'آیا اندیشیده‌ای که اگر انکار پیشه کند و روی برتابد [فقط خود را نابود سازد]'),
(6120, 96, 14, 'آیا نمی‌داند که همانا خداوند [همه چیز را] می‌بیند؟'),
(6121, 96, 15, 'حاشا، اگر از آن کار دست بر ندارد، موی پیشانی او را به سختی بگیریم‌'),
(6122, 96, 16, 'موی پیشانی دروغ‌زن خطاپیشه را'),
(6123, 96, 17, 'پس [مذبوحانه‌] هم‌مجلسانش را [به کمک‌] بخواند'),
(6124, 96, 18, 'ما نیز آتشبانان دوزخ را فراخوانیم‌'),
(6125, 96, 19, 'حاشا، از او پیروی مکن، و سجده بر و تقرب بجوی‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'ما آن [قرآن‌] را در شب قدر نازل کرده‌ایم‌'),
(6127, 97, 2, 'و تو چه دانی شب قدر چیست؟'),
(6128, 97, 3, 'شب قدر بهتر از هزار ماه [عادی‌] است‌'),
(6129, 97, 4, 'در آن [شب‌] فرشتگان و جبرئیل به اذن پروردگارشان برای انجام هر کار فرود آیند'),
(6130, 97, 5, '[این شب‌] تا دمیدن سپیده‌دم آکنده از سلامت [و امن و امان‌] است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'کافران از میان اهل کتاب و مشرکان، دست بردار نبودند، تا آنکه برایشان حجت هویدا آمد'),
(6132, 98, 2, 'پیامبری از سوی خداوند که بر آنان صحیفه‌های پاک [آسمانی‌] می‌خواند'),
(6133, 98, 3, 'که در آنها نوشته‌های استواری هست‌'),
(6134, 98, 4, 'و اهل کتاب تفرقه پیشه نکردند، مگر بعد از آنکه برایشان حجت هویدا آمد'),
(6135, 98, 5, 'و جز این فرمان نیافته بودند که خداوند را بپرستند و پاکدینانه دین خود را برای او خالص دارند، و نماز را بر پا دارند و زکات را بپردازند، و این دین استوار است‌'),
(6136, 98, 6, 'کافران از میان اهل کتاب و مشرکان در آتش جهنمند، و جاودانه در آنند، اینانند که خود بدترین آفریدگانند'),
(6137, 98, 7, 'کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند، اینانند که خود بهترین آفریدگانند'),
(6138, 98, 8, 'پاداش آنان در نزد پروردگارشان بهشتهای عدن است که جویباران از فرودست آن جاری است، و جاودانه در آنند، خداوند از ایشان خشنود است و آنان هم از او خشنودند، این از آن کسی است که از پروردگارش بترسد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'آنگاه که زمین به زلزله‌[ی واپسین‌]اش بلرزد'),
(6140, 99, 2, 'و زمین بارهایش را بیرون ریزد'),
(6141, 99, 3, 'و انسان گوید آن را چه می‌شود؟'),
(6142, 99, 4, 'در چنین روز رازهایش را باز گوید'),
(6143, 99, 5, 'از آنکه پروردگارت به او الهام فرستاده است‌'),
(6144, 99, 6, 'در چنین روز انسانها گروه گروه باز گردند تا [نتیجه و حاصل‌] کارهایشان را به آنان بنمایانند'),
(6145, 99, 7, 'پس هر کس هم سنگ ذره‌ای عمل خیر انجام داده باشد، [پاداش‌] آن را می‌بیند'),
(6146, 99, 8, 'و هرکس هم سنگ ذره‌ای عمل ناشایست انجام داده باشد، [کیفر] آن را می‌بیند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'سوگند به اسبان تیز تک [جهادگر] که نفس نفس می‌زنند'),
(6148, 100, 2, 'و سوگند به اخگرانگیزان [از برخورد سمها]'),
(6149, 100, 3, 'و سوگند به تکاوران بامدادی‌'),
(6150, 100, 4, 'که در آنجا گرد برانگیزند'),
(6151, 100, 5, 'و با همدیگر به میانه آن [معرکه‌] در آیند'),
(6152, 100, 6, 'که انسان در برابر پروردگارش ناسپاس است‌'),
(6153, 100, 7, 'و او بر این امر گواه است‌'),
(6154, 100, 8, 'و او [انسان‌] از مالدوستی بخیل است‌'),
(6155, 100, 9, 'آیا نمی‌داند که چون آنچه در گورهاست زیر و زبر شود'),
(6156, 100, 10, 'و راز دلها آشکار گردانیده شود'),
(6157, 100, 11, 'بی‌گمان پروردگارت در چنین روز از حال آنان آگاه است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'درهم کوب‌'),
(6159, 101, 2, 'درهم کوب چیست؟'),
(6160, 101, 3, 'و تو چه دانی که درهم کوب چیست؟'),
(6161, 101, 4, 'روزی که مردم مانند پروانه‌های پراکنده باشند'),
(6162, 101, 5, 'و کوهها مانند پشمهای زده باشند'),
(6163, 101, 6, 'در آن حال هر کس کفه‌ها[ی اعمال خیر]ش سنگینی کند'),
(6164, 101, 7, 'او در زندگانی پسندیده است‌'),
(6165, 101, 8, 'و اما کسی که کفه‌هایش سبکی یابد'),
(6166, 101, 9, 'بازگشتگاه او دوزخ است‌'),
(6167, 101, 10, 'و تو چه دانی آن چیست؟'),
(6168, 101, 11, 'آتشی است [بس‌] سوزان‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'فزون‌طلبی شما را بازی داد'),
(6170, 102, 2, 'تا آنکه با گورها رو در رو شدید'),
(6171, 102, 3, 'حاشا، زودا که بدانید'),
(6172, 102, 4, 'باز حاشا، زودا که بدانید'),
(6173, 102, 5, 'حاشا اگر به علم‌الیقین بدانید'),
(6174, 102, 6, 'بی‌شبهه دوزخ را ببینید'),
(6175, 102, 7, 'آری آن را به عین‌الیقین بنگرید'),
(6176, 102, 8, 'سپس در چنین روز از شما درباره نعمتها[ی کفران شده دنیا] بازخواست شود');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'سوگند به روزگار'),
(6178, 103, 2, 'که بی‌گمان انسان در زیانکاری است‌'),
(6179, 103, 3, 'مگر کسانی که ایمان آورده‌اند و کارهای شایسته کرده‌اند و همدیگر را به حق سفارش کرده‌اند، و همدیگر را به شکیبایی سفارش کرده‌اند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'وای بر هر عیب‌جوی طعنه‌زن‌'),
(6181, 104, 2, 'همان که مالی گردآورد و شماره‌اش کرد'),
(6182, 104, 3, 'پندارد که مالش او را جاودانه می‌سازد'),
(6183, 104, 4, 'حاشا، بی‌شبهه به کام درهم شکن انداخته شود'),
(6184, 104, 5, 'و تو چه دانی که درهم شکن چیست‌'),
(6185, 104, 6, 'آتش الهی فروزان‌'),
(6186, 104, 7, 'همان که بر دلها راه یابد'),
(6187, 104, 8, 'بر ایشان لایه لایه فرو افتد'),
(6188, 104, 9, 'در ستونهایی بالا بلند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'آیا ندانسته‌ای که پروردگارت با پیل‌سواران چگونه رفتار کرد؟'),
(6190, 105, 2, 'آیا نیرنگشان را بی‌اثر نساخت؟'),
(6191, 105, 3, 'و بر سر آنان پرندگانی فوج‌فوج فرستاد'),
(6192, 105, 4, 'که بر آنان سنگریزه‌هایی از سنگ گل فرو انداختند'),
(6193, 105, 5, 'و سرانجام آنان را مانند برگ کاه نیمه جویده ساخت‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'برای همبستگی قریش‌'),
(6195, 106, 2, 'همبستگیشان در کوچ زمستانی و تابستانی‌'),
(6196, 106, 3, 'پس باید صاحب این خانه را بپرستند'),
(6197, 106, 4, 'همان که ایشان را پس از گرسنگی [آذوقه و] خوراک داد و پس از ترس [و نگرانی‌]، ایمن داشت‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'آیا ملاحظه کرده‌ای کسی را که روز جزا را انکار می‌کند؟'),
(6199, 107, 2, 'این همان کسی است که یتیم را می‌راند'),
(6200, 107, 3, 'و بر اطعام بینوا، ترغیب نمی‌کند'),
(6201, 107, 4, 'پس وای بر آن نمازگزاران‌'),
(6202, 107, 5, 'همان کسانی که از نماز خویش غافلند'),
(6203, 107, 6, 'کسانی که ایشان [در نماز هم‌] ریاکاری می‌کنند'),
(6204, 107, 7, 'و زکات را باز می‌دارند');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'ما به تو کوثر بخشیده‌ایم‌'),
(6206, 108, 2, 'پس برای پروردگارت نماز بگزار و قربانی کن‌'),
(6207, 108, 3, 'بی‌گمان دشمن تو بی‌سرانجام [و بلاعقب‌] است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'بگو هان ای کافران‌'),
(6209, 109, 2, 'من معبود شما را نمی‌پرستم‌'),
(6210, 109, 3, 'و شما هم پرستندگان معبود من نیستید'),
(6211, 109, 4, 'و من پرستنده آنچه شما می‌پرستید نیستم‌'),
(6212, 109, 5, 'و شما هم پرستندگان معبود من نیستید'),
(6213, 109, 6, 'شما را دین شما، و مرا دین من‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'آنگاه که نصرت الهی و پیروزی فراز آید'),
(6215, 110, 2, 'و مردم را بینی که گروه گروه به دین الهی در آیند'),
(6216, 110, 3, 'پس سپاسگزارانه پروردگارت را نیایش کن و از او آمرزش بخواه که او بس توبه‌پذیر است‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'زیانکار باد دستان ابولهب، و خود او هم زیانکار شد'),
(6218, 111, 2, 'مالش و دستاوردش به کارش نیامد'),
(6219, 111, 3, 'زودا که به آتشی شعله‌ور در آید'),
(6220, 111, 4, 'و زنش هیزم‌کش [و آتش‌افروز معرکه‌] است‌'),
(6221, 111, 5, 'و ریسمانی از لیف خرمای تافته بر گردن دارد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'بگو او خداوند یگانه است‌'),
(6223, 112, 2, 'خداوند صمد'),
(6224, 112, 3, 'نه فرزند آرد و نه از کسی زاده است‌'),
(6225, 112, 4, 'و او را هیچ کس همتا نیست‌');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'بگو به پروردگار فلق پناه می‌برم‌'),
(6227, 113, 2, 'از شر هر آنچه آفریده است‌'),
(6228, 113, 3, 'و از شر شب چون در آید'),
(6229, 113, 4, 'و از شر زنان افسونگر دمنده در گره‌ها'),
(6230, 113, 5, 'و از شر رشک‌بری که رشک برد');
INSERT INTO `fa_khorramshahi` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'بگو به پروردگار مردم پناه می‌برم‌'),
(6232, 114, 2, 'فرمانروای مردم‌'),
(6233, 114, 3, 'خدای مردم‌'),
(6234, 114, 4, 'از شر وسوسه‌گر پنهانکار'),
(6235, 114, 5, 'کسی که در دلهای مردم وسوسه می‌کند'),
(6236, 114, 6, 'اعم از جن و انسان‌');
