INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'بنام خداوند بخشاینده مهربان‌'),
(2, 1, 2, 'سپاس خدایرا پروردگار جهانیان‌'),
(3, 1, 3, 'بخشاینده مهربان‌'),
(4, 1, 4, 'که فرمانروای روز رستاخیز است‌'),
(5, 1, 5, 'خدایا تو را پرستش کنیم و از تو یاری خواهیم‌'),
(6, 1, 6, 'ما را به راه راست هدایت فرما'),
(7, 1, 7, 'راه آنانکه برایشان روزی فرستادی نه راه غضب‌شدگان و نه راه گمگشتگان‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الم‌'),
(9, 2, 2, 'این نامه که تردیدی در آن نیست راهنمای پرهیزکاران است‌'),
(10, 2, 3, 'که به نادیده ایمان آوردند و برپای دارند نماز را و از آنچه بدانان داده‌ایم ببخشند'),
(11, 2, 4, 'و آن کسانی که ایمان آوردند بدانچه بر تو و پیش از تو نازل شده است و به آخرت یقین دارند'),
(12, 2, 5, 'آنانند بر هدایتی از پروردگار خویش و آنانند رستگاران‌'),
(13, 2, 6, 'همانا گروه کفّار یکسان است بر ایشان بترسانیشان یا نترسانیشان ایمان نیاورند'),
(14, 2, 7, 'مهر نهاد خدا بر دلهاشان و بر گوش ایشان و بر دیدگان ایشان است پرده‌ای و برای ایشان است شکنجه بزرگ‌'),
(15, 2, 8, 'و از مردمند گروهی که گویند ایمان آوردیم به خدا و به روز بازپسین حالی که نیستند مؤمنان‌'),
(16, 2, 9, 'نیرنگ با خدا و مؤمنان می‌بازند حالی که فریب نمی‌دهند جز خویشتن را و درنمی‌یابند'),
(17, 2, 10, 'در دلهای آنان است مرضی و خدا بیفزودشان مرضی و ایشان را است عذابی دردناک بدانچه بودند دروغ می‌گفتند'),
(18, 2, 11, 'و اگر گفته شود بدیشان فساد نکنید در زمین گویند نیستیم ما جز اصلاح‌کنندگان‌'),
(19, 2, 12, 'همانا آنانند مفسدان و لیکن درنمی‌یابند'),
(20, 2, 13, 'و هرگاه گفته شود بدیشان ایمان آرید چنان که ایمان آوردند مردم گویند آیا ایمان آریم چنان که ایمان آوردند بی‌خردان همانا ایشانند بی‌خردان و لیکن نمی‌دانند'),
(21, 2, 14, 'و هرگاه ملاقات کنند مؤمنان را گویند ایمان آوردیم و چون خلوت کنند با شیاطین خود گویند همانا ما با شمائیم نیستیم ما جز مسخره‌کنندگان‌'),
(22, 2, 15, 'خدا مسخره کند بدیشان و مهلتشان دهد در سرکشی خود فرو روند'),
(23, 2, 16, 'آنانند که خریدند گمراهی را به هدایت پس نه سودمند شد سوداگریشان و نبودند راه‌یافتگان‌'),
(24, 2, 17, 'مثَل ایشان مثَل آن است که افروخت آتشی تا گاهی که روشن کرد پیرامونش را ببرد خدا نور ایشان را و بگذاردشان در تاریکیهائی که نمی‌بینند'),
(25, 2, 18, 'کرانند گنگانند کورانند پس باز نمی‌گردند'),
(26, 2, 19, 'یا مانند بارانی از آسمان که در آن است تاریکیهائی و رعد و برقی می‌نهند انگشتان خویش را در گوشهاشان از صاعقه‌ها از ترس مرگ و خداوند فراگیرنده است به کافران‌'),
(27, 2, 20, 'نزدیک است برق برباید دیدگان ایشان را هرگاه روشنائیشان دهد روان شوند در آن و گاهی که تاریکی افکند بر ایشان بایستند و اگر می‌خواست خدا می‌برد گوش و دیدگان ایشان را همانا خداوند است بر همه چیز توانا'),
(28, 2, 21, 'ای مردم پرستش کنید پروردگار خویش را آنکه بیافرید شما و پیشینیان را شاید که شما پرهیزکاری کنید'),
(29, 2, 22, 'آنکه گردانید برای شما زمین را فرش و بستری و آسمان را سازمانی و پوششی و فرو ریخت از آسمان آبی پس برون آورد بدان از میوه‌ها روزیی برای شما پس قرار ندهید برای خدا همتایانی و خود می‌دانید'),
(30, 2, 23, 'و اگر در شکّی هستید بدانچه فرستادیم بر بنده خود پس بیارید سوره‌ای همانند آن و بخوانید گواهان خویش را جز خدا اگر هستید راستگویان‌'),
(31, 2, 24, 'پس اگر نکردید و هرگز نمی‌کنید پس بپرهیزید آتشی را که سوختش مردم است و سنگ آماده شده است برای کافران‌'),
(32, 2, 25, 'و مژده ده آنان را که ایمان آوردند و کردار شایسته کردند که ایشان را است باغهائی که روان است از زیر آنها نهرها هرگاه روزیمند شوند از آن میوه‌ای گویند این است همانکه روزیمند بودیم از پیش و آورده‌شدندش همانند و ایشان را است در آن زنانی پاک‌شده و ایشانند در آن جاودانان‌'),
(33, 2, 26, 'همانا پروا ندارد خدا که مثَلی زند پشه‌ای تا برتر از آن پس آنان که ایمان آوردند دانند آن را حقّ‌ّ از نزد پروردگار خود و آنان که کفر ورزیدند گویند آیا خدا را غرض چه باشد از این مثَل که گروهی را بدان گمراه و گروهی را بدان هدایت کند حالی که گمراه نمی‌کند بدان مگر فاسقان را'),
(34, 2, 27, 'آنان که بشکنند پیمان خدا را پس از بستنش و ببرند آنچه را که خدا دستور پیوندش داده و فساد کنند در زمین همانا آنانند زیانکاران‌'),
(35, 2, 28, 'چگونه کافر شوید به خدا با آنکه مردگانی بودید که زنده کرد شما را پس می‌میراند و سپس زنده کند شما را پس بسوی او بازگردانیده شوید'),
(36, 2, 29, 'اوست آن که آفرید برای شما هرچه در زمین است و سپس پرداخت به آسمان و آن را هفت آسمان ساخت و اوست به هر چیز دانا'),
(37, 2, 30, 'و هنگامی که گفت پروردگار تو به فرشتگان که خواهم قرار داد در زمین جانشینی گفتند آیا قرار دهی در زمین آن را که فساد جوید در آن و خون ریزد حالی که ما ستایش و سپاس تو گوئیم و تو را تقدیس کنیم گفت من می‌دانم آنچه را که نمی‌دانید'),
(38, 2, 31, 'و آموخت به آدم نامها را همگی سپس عرضشان کرد بر ملائکه و گفت خبر دهید مرا از نامهای اینان اگر هستید راستگویان‌'),
(39, 2, 32, 'گفتند منزّهی تو نیست ما را دانشی جز آنچه تو آموختی همانا توئی دانشمند حکیم‌'),
(40, 2, 33, 'گفت ای آدم آگهیشان ده به نامهای آنان و گاهی که آگهیشان داد به نامهای آنان گفت آیا نگفتم شما را که من می‌دانم غیب آسمانها و زمین را و می‌دانم آنچه را که فاش و پنهان سازید'),
(41, 2, 34, 'و هنگامی که گفتیم به فرشتگان سجده کنید به آدم پس سجده کردند همه جز ابلیس که سر پیچید و بزرگی طلبید و بود از گروه کافران‌'),
(42, 2, 35, 'و به آدم گفتیم که با همسر خویش در بهشت بیارام و بخورید از آن گوارا آنچه خواهید و نزدیک نشوید بدین درخت که می‌شوید از ستمگران‌'),
(43, 2, 36, 'پس بلغزانیدشان شیطان و برون راندشان از آنچه بودند در آن و گفتیم فرود شوید گروهی از شما دشمن گروهی و شما را است در زمین آرامشگاه و بهره‌ای تا زمانی‌'),
(44, 2, 37, 'پس دریافت آدم از پروردگار خود سخنانی که توبه‌اش پذیرفت و همانا اوست بسی توبه‌پذیرنده مهربان‌'),
(45, 2, 38, 'گفتیم فرود شوید از آن همگی هرگاه شما را راهنمائی از جانب من آید پس آنانکه پیروی کنند راهنمائی مرا نه برایشان است ترسی و نه اندوهگین شوند'),
(46, 2, 39, 'و آنان که کفر ورزیدند و تکذیب آیتهای ما کردند ایشانند یاران آتش در آنند جاودانان‌'),
(47, 2, 40, 'ای بنی‌اسرائیل یاد آورید نعمتی را که به شما ارزانی فرمودم و وفا کنید به عهد من وفا کنم به عهد شما و مرا پس بترسید'),
(48, 2, 41, 'و ایمان آرید بدانچه فرستادم که مصدّق آنچه با شما است و نباشید نخستین کس که بدان کفر ورزد و نفروشید آیتهای مرا به بهائی کم و از من پس بترسید'),
(49, 2, 42, 'و نپوشانید حقّ‌ّ را به باطل و کتمان نکنید حقّ‌ّّ را و شما می‌دانید'),
(50, 2, 43, 'و بپای دارید نماز را و بدهید زکوة را و رکوع گزارید (نماز گزارید) با رکوع‌گزاران‌'),
(51, 2, 44, 'آیا مردم را به نیکی خوانید و خود را فراموش کنید حالی که می‌خوانید کتاب را آیا بخرد نمی‌یابید'),
(52, 2, 45, 'و کمک جوئید از صبر و نماز و همانا گران است آن جز بر فروتنان‌'),
(53, 2, 46, 'آنان که ملاقات پروردگار خویش را گمان دارند و آن که ایشانند بسوی او بازگشت‌کنندگان‌'),
(54, 2, 47, 'ای بنی اسرائیل یاد آرید نعمتم را که به شما ارزانی داشتم و آنکه برتری دادم شما را بر جهانیان‌'),
(55, 2, 48, 'و بترسید از روزی که انجام ندهد کسی برای کسی چیزی را و نه از کسی شفاعتی پذیرفته شود و نه بدلی از او قبول گردد و نه ایشان یاری شوند'),
(56, 2, 49, 'و زمانی که نجات دادیم شما را از آل فرعون که روا می‌داشتند بر شما شکنجه زشت را می‌کشتند به سختی پسران شما را و زنده می‌گذاشتند زنان شما را و در آنتان بود آزمایشی از پروردگار شما بزرگ‌'),
(57, 2, 50, 'و هنگامی که بشکافتیم برای شما دریا را پس نجات دادیم شما را و غرق نمودیم آل فرعون را حالی که بودید نگران‌'),
(58, 2, 51, 'و هنگامی که میعاد نهادیم با موسی چهل شب سپس برگرفتید گوساله را از آن پس و بودید ستمگران‌'),
(59, 2, 52, 'پس بخشودیم بر شما پس از آن باشد که شما شکر گزارید'),
(60, 2, 53, 'و هنگامی که دادیم به موسی کتاب و فرقان را شاید رهبری شوید'),
(61, 2, 54, 'و هنگامی که گفت موسی به قوم خود ای قوم من همانا ستم بر خود کردید به گرفتن شما گوساله را پس بازگردید به آفریدگار خود و بکشید نفوس خویش را آن بهتر است از برای شما نزد پروردگارتان پس پذیرفت توبه شما را که اوست توبه‌پذیرنده مهربان‌'),
(62, 2, 55, 'و هنگامی که گفتید ای موسی ایمان نیاریم به تو تا ببینیم خدا را آشکارا پس بگرفت شما را صاعقه حالی که بودید نگران‌'),
(63, 2, 56, 'سپس زنده کردیم شما را پس از مرگتان شاید که شما شکر گزارید'),
(64, 2, 57, 'و سایبان گردانیدیم بر شما ابر را و فرود آوردیم بر شما «مَنّ» و «سَلوی» را بخورید از پاکهای آنچه روزیتان دادیم و ستم نکردند ما را لیکن بودند نفوس خویش را ستم‌کنندگان‌'),
(65, 2, 58, 'و هنگامی که گفتیم درآئید این قریه را و بخورید از آن هر جا خواهید گوارا و درآئید در را سجده‌کنان و بگوئید کاهشی تا بیامرزیم از برای شما لغزشهای شما را و بزودی بیش دهیم به نیکوکاران‌'),
(66, 2, 59, 'پس تبدیل کردند ستمگران سخن را به غیر از آنچه به ایشان گفته شد پس فرستادیم بر ستم‌پیشه‌گان شکنجه‌ای را از آسمان بدانچه بودند نافرمانی‌کنندگان‌'),
(67, 2, 60, 'و هنگامی که آب خواست موسی برای قوم خود پس گفتیم بزن عصایت را به سنگ پس منفجر شد از آن دوازده چشمه که بشناخت هر گروهی آبشخور خود را بخورید و بنوشید از روزی خدا و نکوشید در زمین فسادکنندگان‌'),
(68, 2, 61, 'و هنگامی که گفتید ای موسی هرگز شکیبا نشویم بر یک خوراک پس بخوان برای ما پروردگار خود را تا بیرون آرد برای ما از رستنیهای زمین از سبزیش و خیارش و سیرش و عدسش و پیازش گفت آیا تبدیل کنید آن را که پست‌تر است به بهتر به شهر درآئید (فرود آئید) که شما را است آنچه را خواستید و زده شد بر ایشان خواری و بینوائی و بازگشتند به خشمی از خدا این بدان شد که بودند کفر می‌ورزیدند به آیتهای خدا و می‌کشتند پیمبران را به ناحقّ‌ّ این بدان شد که نافرمانی کردند و بودند تجاوزکنندگان‌'),
(69, 2, 62, 'همانا آنان که ایمان آوردند و آنان که جهود شدند و ترسایان و صائبان هر کس ایمان آرد به خدا و روز بازپسین و کردار نیک کند آنان را است پاداششان نزد پروردگارشان نه ترسی است بر ایشان و نه اندوهگین شوند'),
(70, 2, 63, 'و هنگامی که گرفتیم پیمان شما را و برافراشتیم بر فراز شما طور را بگیرید آنچه را به شما دادیم به نیرومندی و یاد آرید آنچه را در آن است شاید پرهیزکاری کنید'),
(71, 2, 64, 'سپس پشت کردید پس از آن که اگر نبود فضل خدا بر شما و رحمتش هرآینه می‌شدید از زیانکاران‌'),
(72, 2, 65, 'و همانا دانستید آنان را که تجاوز کردند از شما در روز شنبه که گفتیم ایشان را باشید بوزینه‌هایی سرافکندگان‌'),
(73, 2, 66, 'پس قرارش دادیم عبرتی برای پیش روی آن و پشت سر آن و اندرزی برای پرهیزکاران‌'),
(74, 2, 67, 'و هنگامی که گفت موسی به قوم خود که خدا فرمانتان دهد که بکشید گاوی گفتند ما را به مسخره گیری گفت پناه می‌برم به خدا که باشم از نادانان‌'),
(75, 2, 68, 'گفتند بخوان پروردگارت را بیان کند برای ما چیست آن گفت او گوید همانا آن گاوی است نه پیر و نه جوان میانه بین آن پس بکنید آنچه را مأمور می‌شوید'),
(76, 2, 69, 'گفتند بخوان پروردگار خود را بیان کند برای ما چیست رنگش گفت او گوید همانا آن گاوی است به سختی زردفام که رنگش سرور آورد برای بینندگان‌'),
(77, 2, 70, 'گفتند بخوان پروردگار خود را بیان کند برای ما چیست آن همانا گاو بر ما مشتبه شده است و همانا مائیم اگر خدا خواهد هدایت‌یافتگان‌'),
(78, 2, 71, 'گفت او گوید همانا آن گاوی است نه رام تا زمین را شیار کند و نه کشت را آب دهد تندرست است نیست لکّه‌ای در آن گفتند اکنون حقّ‌ّ را آوردی پس سربریدندش و نزدیک نبودند آن را کنندگان‌'),
(79, 2, 72, 'و هنگامی که کشتید تنی را پس بیزاری جستید در آن و خداست بیرون‌آرنده آنچه بودید نهان‌کنندگان‌'),
(80, 2, 73, 'پس گفتیم بزنیدش با پاره‌ای از آن چنین زنده کند خدا مُردگان را و بنمایاند به شما آیتهای خویش را شاید بخرد آئید'),
(81, 2, 74, 'سپس سخت شد دلهای شما پس از آن که مانند سنگ است یا سنگین‌تر و همانا از سنگها است آنچه روان گردد از آن جویها و از آنها است آنچه بشکافد پس برون آید از آن آب و از آنها است آنچه فرودآید از ترس خدا و نیست خدا غافل از آنچه می‌کنید'),
(82, 2, 75, 'آیا طمع دارید که ایمان آرند به شما حالی که بودند گروهی از ایشان می‌شنیدند سخن خدا را پس دگرگونش می‌کردند از پس آنکه می‌یافتندش بخرد و ایشان می‌دانستند'),
(83, 2, 76, 'و هرگاه ملاقات کنند مؤمنان را گویند ایمان آوردیم و چون خلوت کند بعضی از ایشان با بعضی گویند آیا حدیث کنید ایشان را بدانچه بگشود است خدا بر شما تا بدان بستیزند با شما نزد پروردگارتان آیا بخرد نمی‌یابید'),
(84, 2, 77, 'آیا نمی‌دانند که خدا می‌داند آنچه را نهان می‌کنند و آنچه را آشکار می‌کنند'),
(85, 2, 78, 'و از ایشانند گروهی اُمّی و مادرزاد که نمی‌دانند کتاب را جز آرزو و افسانه‌هایی و نیستند جز پندارندگان‌'),
(86, 2, 79, 'پس وای بر آنان که می‌نویسند کتاب را با دستهای خود و سپس گویند که آن از نزد خدا است تا بخرند بدان بهائی اندک را پس وای بر آنان از آنچه بنوشت دستهای ایشان و وای بر آنان از آنچه فراهم می‌کنند'),
(87, 2, 80, 'گفتند هرگز نرسد ما را آتش جز روزکی چند بگو آیا برگرفتید نزد خدا پیمانی که هرگز نشکند خدا پیمان خود را یا گوئید بر خدا آنچه را نمی‌دانید'),
(88, 2, 81, 'بلکه آن کس که بدی را فراهم کرد و فراگرفتش گناه او آنانند یاران آتش ایشانند در آن جاودانان‌'),
(89, 2, 82, 'و آنانکه ایمان آوردند و کردارهای نیک کردند آنانند یاران بهشت ایشانند در آن جاودانان‌'),
(90, 2, 83, 'و هنگامی که گرفتیم پیمان بنی اسرائیل را که پرستش نکنید جز خدا را و به والدین نیکی را و به نزدیکان و یتیمان و بینوایان و بگوئید با مردم گفتاری نیک و برپا دارید نماز را و بدهید زکات را سپس پشت کردید جز کمی از شما حالی که بودید روی‌گردانان‌'),
(91, 2, 84, 'و هنگامی که گرفتیم پیمان شما را که نریزید خونهای خود را و بیرون نرانید همدیگر را از دیار خویش پس اقرار کردید حالی که بودید گواهان‌'),
(92, 2, 85, 'سپس اینک شمائید که می‌کشید همدیگر را و بیرون می‌رانید گروهی را از شما از خانه‌های ایشان پشتیبانی جوئید بر ایشان به گناه و ستم و اگر بیایند شما را اسیران فدیه از ایشان دهید حالی که حرام است بر شما برون راندن ایشان آیا ایمان آرید به بعض کتاب و کفر ورزید به بعضی پس چیست کیفر آنکه این کار را کند از شما جز خواریی در زندگانی دنیا و روز قیامت برگردانیده شوند بسوی اشدّ عذاب و نیست خدا غافل از آنچه می‌کنید'),
(93, 2, 86, 'آنانند که خریدند زندگانی دنیا را به آخرت پس سبک نشود از ایشان عذاب و نه یاری شوند'),
(94, 2, 87, 'همانا دادیم به موسی کتاب را و در پی آوردیم پس از وی پیمبران را و دادیم عیسی بن مریم را بیّنات و مؤیدش داشتیم به روح‌القدس آیا هرگاه بیاید شما را پیمبری بدانچه نپسندد نفوستان سرکشی کردید پس گروهی را تکذیب کردید و گروهی را می‌کشتید'),
(95, 2, 88, 'و گفتند دلهای ما نهان در پرده است بلکه لعنت کرد خدا ایشان را به کفرشان پس به کمی ایمان آرند'),
(96, 2, 89, 'هنگامی که بیامدشان کتابی از نزد خدا که مصدق بود آنچه را با ایشان است و بودند از پیش پیروزی می‌جستند بر آنان که کفر ورزیدند تا گاهی که بیامدشان بشناختند آنچه کافر شدند بدان پس لعنت خدا باد بر کافران‌'),
(97, 2, 90, 'چه زشت است آنچه بدان فروختند نفوس خود را که کفر ورزند بدانچه فرستاده است خدا به ستمگری که چرا می‌فرستد از فضلش بر هر که از بندگانش که خواهد پس جلب کردند بر خود غضبی را بر غضبی و کافران را است عذابی خوارکننده‌'),
(98, 2, 91, 'و هرگاه گفته شود بدانان ایمان آرید بدانچه خدا فرستاده است گویند ایمان آوریم بدانچه فرو شده است بر ما و کفر ورزند به ماورای آن حالی که آن است حقّ تصدیق‌کننده آنچه با ایشان است بگو پس چرا می‌کشتید پیمبران خدا را از پیش اگر هستید مؤمنان‌'),
(99, 2, 92, 'و همانا بیامد شما را موسی با بینات پس برگرفتید گوساله را پس از او حالی که بودید ستمگران‌'),
(100, 2, 93, 'و هنگامی که گرفتیم پیمان شما را و برافراشتیم بر فراز شما طور را بگیرید آنچه را به شما دادیم به نیرومندی و بشنوید گفتند شنیدیم و نافرمانی کردیم و نوشانیده شدند در دلهای خود گوساله را (دوستی گوساله با دلشان آمیخته شد) به کفرشان بگو چه زشت است آنچه فرمانتان دهد ایمانتان اگر هستید مؤمنان‌'),
(101, 2, 94, 'بگو اگر برای شما است خانه آخرت نزد خدا مختص شما جز مردم پس آرزوی مرگ کنید اگر هستید راستگویان‌'),
(102, 2, 95, 'و آرزو نکندش هیچگاه بدانچه پیش آورده است دستهای ایشان و خدا عالم است به ظالمان‌'),
(103, 2, 96, 'و همانا یابیشان حریص‌ترین مردم بر زندگی و از مشرکانند آنان که دوست دارد هر کدام که عمر داده شود هزار سال و نیستش دورکننده از عذاب که سالمند باشد و خدا بینا است بدانچه می‌کنند'),
(104, 2, 97, 'بگو هر کس دشمن جبرئیل باشد همانا او فرود آوردش بر دلت با دستوری از خدا مصدق آنچه پیش روی آن است و هدایت و بشارتی از برای مؤمنان‌'),
(105, 2, 98, 'کسی که دشمن خدا و فرشتگان او و پیمبرانش و جبرئیل و میکائیل باشد همانا خداوند است دشمن کافران‌'),
(106, 2, 99, 'و همانا فرستادیم بسوی تو نشانیهای تابناک و کفر نورزند بدانها جز فاسقان‌'),
(107, 2, 100, 'آیا هرگاه پیمانی بستند دور افکندش گروهی از ایشان بلکه بیشتر ایشان ایمان نیاورند'),
(108, 2, 101, 'و هنگامی که بیامدشان پیکی از نزد خدا تصدیق‌کننده آنچه با ایشان افکندند گروهی از آنان که داده شدند کتاب را کتاب خدا را پشت سر خود چنان که گوئی نمی‌دانند'),
(109, 2, 102, 'و پیروی کردند آنچه می‌خواندند شیاطین بر پادشاهی (کشور) سلیمان و کافر نشد سلیمان و لکن شیاطین کافر شدند می‌آموختند به مردم جادو را و آنچه فرستاده شد بر دو فرشته در بابل هاروت و ماروت و نمی‌آموختند کسی را تا می‌گفتند جز این نیست که ما آزمایشیم زنهار کافر مشو پس می‌آموختند از آنان آنچه را جدائی بدان می‌افکندند میان مرد و زنش و زیان نمی‌رساندند بدان کس را جز با اذن خدا و یاد می‌گرفتند آنچه را زیانشان داشت و سودشان نمی‌رسانید و همانا دانسته بودند که هر کس آن را بخرد نیست او را بهره‌ای در آخرت چه زشت است آنچه فروختند بدان خویشتن را اگر بودند می‌دانستند'),
(110, 2, 103, 'و اگر ایمان می‌آوردند و پرهیزکاری می‌کردند همانا ثوابی از نزد خدا بهتر است اگر بودند می‌دانستند'),
(111, 2, 104, 'ای مؤمنان نگوئید راعنا (رعایت کن ما را) و بگوئید اُنظرنا (منظور دار ما را) و بشنوید و کافران راست عذابی دردناک‌'),
(112, 2, 105, 'دوست ندارند آنانکه کافر شدند از اهل کتاب و از مشرکان که فرود شود بر شما خیری از پروردگار شما و خدا مخصوص کند به رحمت خود هر که را خواهد و خدا است دارنده فضلی بزرگ‌'),
(113, 2, 106, 'آنچه براندازیم از آیتها یا از یاد بریمش بیاریم بهتر از آن یا مانند آن را آیا ندانی که خدا بر همه چیز تواناست‌'),
(114, 2, 107, 'آیا ندانی که خدا راست پادشاهی آسمانها و زمین و نیست شما را جز خدا دوست و نه یاوری‌'),
(115, 2, 108, 'یا خواهید پرسید پیمبر خود را بدانسان که پرسیده شد موسی از پیش و آنکه تبدیل کند کفر را به ایمان همانا گم کرده است راه راست را'),
(116, 2, 109, 'دوست داشتند بسیاری از اهل کتاب که برگردانند شما را پس از ایمان شما کافران به رشکی از نزد خود از پس آنکه آشکار شد برای ایشان حقّ پس درگذرید و گذشت کنید تا بیارد خدا امر خویش را همانا خدا بر همه چیز است توانا'),
(117, 2, 110, 'و بپای دارید نماز را و بدهید زکات را و آنچه پیش فرستید برای خود از خوبی یابیدش نزد خدا همانا خدا است بدانچه می‌کنید بینا'),
(118, 2, 111, 'و گفتند به بهشت درنیاید جز آنکو جهود است یا ترسا این است آرزوهای ایشان بگو بیارید دستاویز خویش را اگر هستید راستگویان‌'),
(119, 2, 112, 'بلکه هر که رویش را به خدا آورد و نیکوکار باشد او را است پاداش او نزد خدا و نه بیمی بر ایشان است و نه اندوهگین شوند'),
(120, 2, 113, 'و گفتند جهودان نیستند ترسایان بر چیزی و گفتند ترسایان نیستند جهودان بر چیزی و ایشان همی‌خوانند کتاب را بدینسان گفتند آنان که نمی‌دانند همانا گفتار ایشان پس خدا حکم کند میان ایشان روز قیامت در آنچه بودند در آن اختلاف‌کنندگان‌'),
(121, 2, 114, 'و کیست ستمگرتر از آنکه بازداشت است مساجد خدا را از آنکه برده شود در آنها نامش و بکوشید است در ویرانی آنها آنان را نرسد که در آنها درآیند مگر هراسان ایشان را است در دنیا خواریی و ایشان را است در آخرت عذابی بزرگ‌'),
(122, 2, 115, 'و از آنِ خدا است مشرق و مغرب به هر سو که روی آرید همانجا است روی خدا همانا خدا است گشایشمند دانا'),
(123, 2, 116, 'و گفتند برگرفت خدا فرزندی منزه است او بلکه او را است آنچه در آسمانها است و زمین همگی برای اویند فروتنان‌'),
(124, 2, 117, 'پدیدآرنده آسمانها و زمین و هرگاه بگذارند امری را جز این نیست که گویدش بشو پس بشود'),
(125, 2, 118, 'و گفتند آنان که نمی دانند چرا سخن نگوید با ما خدا یا نیاید ما را آیتی بدینسان گفتند آنان که پیش از ایشان بودند همانند گفتار ایشان ماننده شده است دلهای آنان همانا بیان کردیم آیتها را برای گروهی که یقین دارند'),
(126, 2, 119, 'همانا فرستادیمت به حقّ بشارت‌دهنده و ترساننده و پرسیده نمی‌شوی تو از دوزخیان‌'),
(127, 2, 120, 'و هرگز راضی نشوند از تو جهودان و نه ترسایان تا پیروی کنی آیین آنان را بگو همانا هدایت خدا است هدایت و اگر پیروی کنی هوسهای آنان را پس از آنچه بیامدت از دانش نباشدت از خدا دوست و نه یاوری‌'),
(128, 2, 121, 'آنان که بدیشان دادیم کتاب را تلاوتش کنند حقّ تلاوت آن، آنان ایمان آرند بدان و کسی که کفر ورزد بدان همانا ایشانند زیانکاران‌'),
(129, 2, 122, 'ای بنی‌اسرائیل یاد آرید نعمت مرا که به شما دادم و آنکه برتری دادم شما را بر جهانیان‌'),
(130, 2, 123, 'و بترسید روزی را که کفایت نکند کسی از کسی چیزی را و نه پذیرفته شود از او بدلی و نه سود دهدش شفاعتی و نه یاری شوند'),
(131, 2, 124, 'و هنگامی که بیازمود ابراهیم را پروردگار او با کلماتی پس به انجام رسانید آنها را گفت همانا گرداننده تواَم از برای مردم پیشوائی گفت و از نژاد من گفت نرسد عهد من به ستمگران‌'),
(132, 2, 125, 'و هنگامی که گردانیدیم خانه را بازگشتگاهی برای مردم و امنی برگیرید از مقام ابراهیم نمازگاهی و سپردیم به ابراهیم و اسماعیل که پاک دارید خانه مرا برای طواف‌کنندگان و معتکفان و رکوع‌گزاران سجده‌کنان‌'),
(133, 2, 126, 'و هنگامی که گفت ابراهیم پروردگارا بگردان این را شهرستانی امن و روزی ده اهلش را از ثمرات آن که ایمان آورده است از ایشان به خدا و روز بازپسین گفت و آن کس که کفر ورزیده است کامیابیش دهم اندکی و سپس کشانمش بسوی عذاب آتش و چه زشت است آن بازگشتگاه‌'),
(134, 2, 127, 'و هنگامی که برمیافراشت ابراهیم ستونهای خانه را و اسماعیل پروردگارا بپذیر از ما همانا توئی شنونده دانا'),
(135, 2, 128, 'بار پروردگارا بگردان ما را اسلام آرندگانی برای تو و از نژاد ما مردمی اسلام‌آورنده برای تو و بنمای به ما عبادتهای ما را و توبه کن بر ما که توئی توبه‌پذیرنده مهربان‌'),
(136, 2, 129, 'بار پروردگارا و برانگیز در ایشان پیمبری از ایشان که تلاوت کند بر ایشان آیتهای تو را و بیاموزدشان کتاب و حکمت را و پاکشان سازد که توئی عزتمند حکیم‌'),
(137, 2, 130, 'و کیست که روی بگرداند از آئین ابراهیم جز آن کس که تباه کرده است خویشتن را و همانا برگزیدیمش در دنیا و او است در آخرت از شایستگان‌'),
(138, 2, 131, 'هنگامی که بدو گفت پروردگارش اسلام آور گفت اسلام آوردم برای پروردگار جهانیان‌'),
(139, 2, 132, 'و وصیت کرد بدان ابراهیم فرزندان خویش را و یعقوب ای فرزندان من همانا برگزید خدا برای شما دین را پس نمیرید جز آنکه باشید مسلمانان‌'),
(140, 2, 133, 'یا بودید گواهان هنگامی که رسید یعقوب را مرگ گاهی که گفت به فرزندان خود چه را می‌پرستید پس از من گفتند می‌پرستیم خدای تو و خدای پدرانت ابراهیم و اسماعیل و اسحق را خداوندی یگانه و مائیم برای او اسلام‌آورندگان‌'),
(141, 2, 134, 'این است امّتی که گذشت وی را است دست‌رنج او و شما را است دست‌رنج شما و پرسش نشوید از آنچه بودند می‌کردند'),
(142, 2, 135, 'و گفتند جهود شوید یا ترسا تا هدایت یابید بگو بلکه ملّت ابراهیم یکتاپرست و نبود از شرک‌ورزندگان‌'),
(143, 2, 136, 'بگوئید ایمان آوردیم به خدا و بدانچه فرستاده شد بسوی ما و بدانچه فرستاده شد بسوی ابراهیم و اسماعیل و اسحق و یعقوب و اسباط و بدانچه داده شدند موسی و عیسی و آنچه آورده شدند پیمبران از پروردگارشان جدائی نیفکنیم میان هیچکدام از آنان و مائیم از برای او اسلام‌آورندگان‌'),
(144, 2, 137, 'پس اگر ایمان آوردند به مثل آنچه بدان ایمان آوردید همانا هدایت یافتند و اگر روی گردانیدند جز این نیست که ایشانند در خلافی بزودی کفایت کند شما را از ایشان خدا و او است شنوای دانا'),
(145, 2, 138, 'رنگ خدا و کیست نکوتر از خدا در رنگ و مائیم برای او پرستش‌گران‌'),
(146, 2, 139, 'بگو آیا در خدا با ما می‌ستیزید و او است پروردگار ما و شما و ما را است کردار ما و شما را است کردار شما و مائیم برای او اخلاص‌آورندگان‌'),
(147, 2, 140, 'یا گوئید ابراهیم و اسماعیل و اسحق و یعقوب و سبطها بودند جهودان یا ترسایان بگو آیا شما داناترید یا خدا و کیست ستمگرتر از آنکه نهان کرده است گواهیی را که نزد او است از خدا و نیست خدا غافل از آنچه می‌کنید'),
(148, 2, 141, 'این است ملّتی که گذشت ایشان را است دسترنجشان و شما را است دسترنج شما و پرسش نشوید از آنچه بودند می‌کردند'),
(149, 2, 142, 'بزودی گویند نابخردان از مردم که چه چیز برگردانیدشان از قبله‌ای که بر آن بودند بگو از آن خدا است مشرق و مغرب هدایت کند هر که را خواهد به راه راست‌'),
(150, 2, 143, 'و بدینسان گردانیدیم شما را امّتی میانه تا باشید گواهانی بر مردم و باشد پیمبر گواهی بر شما و ننهادیم قبله‌ای را که بر آن بودی مگر تا بشناسیم آن را که پیروی کند پیمبر را از آنکه بر پاشنه پای خود برگردد و همانا گران است آن مگر بر آنان که هدایت کرده است خدا و نیست خدا تباه‌کننده ایمان شما همانا خداوند است به مردم رؤوف مهربان‌'),
(151, 2, 144, 'هر آینه دیدیم گردش (بی‌تابی) روی تو را بسوی آسمان همانا خواهیم گردانید تو را بسوی (یا ارزانی داشت به تو) قبله‌ای که راضی از آن باشی پس بگردان رویت را بسوی مسجد حرام و هر جا باشید بگردانید رویهای خود را بسوی آن و آنان که داده شدند کتاب را هر آینه دانند که آن است حقّ از پروردگارشان و نیست خدا غافل از آنچه می‌کنند'),
(152, 2, 145, 'و اگر بیاری آنان را که کتاب را داده شدند هر آیتی هرآینه پیروی نکنند قبله تو را و نه توئی پیروی‌کننده قبله آنان و نه بعضی از ایشان است پیرو قبله بعضی و اگر پیروی کنی هوسهای آنان را پس از آنچه بیامده است تو را از دانش همانا باشی از ستمگران‌'),
(153, 2, 146, 'آنان که بدیشان دادیم کتاب را بشناسندش چنان که بشناسند فرزندان خویش را و همانا گروهی از ایشان فروپوشند حقّ را حالی که می‌دانند'),
(154, 2, 147, 'حقّ از پروردگار تو است پس نباش از شکّ‌کنندگان‌'),
(155, 2, 148, 'و هر کدام را وجهه‌ای (سوئی) است که بدان روی‌آورنده پس مسابقه کنید بسوی خیرات هرجا باشید بیارد شما را همگی خدا زیرا خدا است بر همه چیز توانا'),
(156, 2, 149, 'و هرگاه (یا هرجا) برون رفتی پس بگردان روی خود را بسوی مسجد حرام و همانا آن است حقّ از پروردگار تو و نیست خدا غافل از آنچه می‌کنید'),
(157, 2, 150, 'و هرگاه (یا هرجا) برون رفتی پس بگردان روی خود بسوی مسجد حرام و هر جا باشید بگردانید رویهای خود را بسوی آن تا نباشد مردم را بر شما دستاویزی مگر آنان که ستم کردند از ایشان پس نترسید از ایشان و مرا بترسید و تا تمام کنم نعمتم را بر شما و شاید هدایت یابید'),
(158, 2, 151, 'چنان که فرستادیم بر شما فرستاده‌ای از شما که می‌خواند بر شما آیتهای ما را و پاکیزه می‌سازد شما را و می‌آموزد به شما کتاب و حکمت را و می‌آموزد شما را آنچه نبودید بدانید'),
(159, 2, 152, 'پس یاد کنید مرا یاد کنم شما را و سپاس گزارید برای من و به من کفر نورزید'),
(160, 2, 153, 'ای آنان که ایمان آوردید کمک جوئید از صبر و نماز همانا خداوند است با صبرکنندگان‌'),
(161, 2, 154, 'و نگوئید بدانان که کشته شدند در راه خدا مردگان بلکه زندگانند و لیکن درنمی‌یابید'),
(162, 2, 155, 'و هرآینه بیازمائیم شما را به چیزی از ترس و گرسنگی و کاهشی از مالها و جانها و میوه‌ها و مژده ده به صبرکنندگان‌'),
(163, 2, 156, 'آنان که هرگاه پیشامدی بدیشان رسد گویند ما از خدائیم و مائیم بسوی او بازگردندگان‌'),
(164, 2, 157, 'آنانند که بر ایشان است درودهائی از پروردگارشان و رحمتی و آنانند هدایت‌شدگان‌'),
(165, 2, 158, 'همانا صفا و مروه از شعارها (پرستشگاه‌ها)ی خدا است پس کسی که حجّ بیت کند یا عمره به جا آورد نیست بر او باکی که طواف کند بر آنها و کسی که خواستار نکوئی شود همانا خداست سپاسگزار دانا'),
(166, 2, 159, 'همانا آنان که کتمان می‌کنند آنچه را فرستادیم از نشانیها و راهنمائی پس از آنکه بیانش کردیم برای مردم در کتاب آنان را لعن کند خدا و لعن کنند لعن‌کنندگان‌'),
(167, 2, 160, 'مگر آنان که بازگشتند و درستی کردند و بیان کردند که بر ایشان بازگشت کنم و منم بازگشت‌کننده مهربان‌'),
(168, 2, 161, 'همانا آنان که کفر ورزیدند و مُردند حالی که کافرند بر ایشان است لعنت خدا و فرشتگان و مردم همگی‌'),
(169, 2, 162, 'جاودانان در آن نه کاسته شود از ایشان عذاب و نه مهلت داده شوند'),
(170, 2, 163, 'و خدای شما خداوند یکتا است نیست خدائی جز او بخشنده مهربان‌'),
(171, 2, 164, 'همانا در آفرینش آسمانها و زمین و گردش شب و روز و کشتیی که روان شود در دریا بدانچه سود دهد به مردم و آنچه فرو فرستاده است خدا از آسمان از آبی که زنده ساخته است بدان زمین را پس از مُردنش و پراکنده است در آن از هر جنبنده‌ای و گردش بادها و ابری که مسخّر است میان آسمان و زمین همانا آیتهائی است برای قومی که بخرد یابند'),
(172, 2, 165, 'و از مردمند گروهی که برگیرند جز خدا همتایانی دوستشان دارند مانند دوستی خدا و آنان که ایمان آوردند سخت‌ترند در دوستی خدا و اگر می‌دیدند آنانکه ستم ورزیدند گاهی که بینند عذاب را آنکه نیرو از آن خدا است همگی و آنکه خدا است سخت شکنجه‌'),
(173, 2, 166, 'هنگامی که بیزاری جستند آنان که پیروی شدند از آنان که پیروی کردند و نگریستند عذاب را و گسیخته شد از ایشان رشته‌ها'),
(174, 2, 167, 'و گویند آنان که پیروی کردند کاش ما را بازگشتی می‌بود تا بیزاری می‌جستیم از ایشان بدانسان که بیزاری جستند از ما بدینگونه بنمایدشان خدا کردارهای ایشان را حسرتهائی بر ایشان و نیستند برون‌روندگان از آتش‌'),
(175, 2, 168, 'ای مردم بخورید از آنچه در زمین است حلال پاک و پیروی نکنید گامهای شیطان را که او شما را دشمنی است آشکار'),
(176, 2, 169, 'جز این نیست که شما را امر کند به بدی و فحشاء و اینکه گوئید بر خدا آنچه را نمی‌دانید'),
(177, 2, 170, 'و اگر گفته شود بدیشان پیروی کنید آنچه را خدا فرستاده گویند بلکه پیروی کنیم آنچه را یافتیم بر آن پدران خویش را و اگر چه باشند پدرانشان نه چیزی را تعقّل کنند و نه هدایت یابند'),
(178, 2, 171, 'و مثَل آنان که کفر ورزیدند مثَل آن کس است که بانگ زند بر حیوانی که نمی‌شنود جز فراخواندن و بانگ‌زدن را کرانند گنگانند کورانند که تعقل نمی‌کنند'),
(179, 2, 172, 'ای آنان که ایمان آوردید بخورید از پاکهای آنچه روزی دادیم به شما و سپاس گزارید برای خدا اگر هستید او را پرستش‌کنندگان‌'),
(180, 2, 173, 'جز این نیست حرام کرد بر شما مردار و خون و گوشت خوک و آنچه را برده شده است بر آن نام جز خدا پس اگر کسی ناچار شود نه ستم‌کننده و نه تجاوزکننده نیست گناهی بر او همانا خداوند است آمرزنده مهربان‌'),
(181, 2, 174, 'همانا آنان که کتمان کنند آنچه را خدا فرستاده است از کتاب و بفروشندش به بهائی کم آنان نمی‌خورند در شکمهای خویش جز آتش و نه سخن گوید با ایشان خدا روز قیامت و نه پاک سازدشان و ایشان را است عذابی دردناک‌'),
(182, 2, 175, 'آنانند که خریدند گمراهی را به هدایت و عذاب را به مغفرت شگفتا چه بردبارند بر آتش‌'),
(183, 2, 176, 'این بدان است که فرستاد خدا کتاب را به حقّ و آنان که اختلاف کردند در کتاب همانا ایشانند در ستیزشی دور'),
(184, 2, 177, 'نیکی آن نیست که گردانید رویهای خود را بسوی مشرق و مغرب لیکن نکوکار آن است که ایمان آورده است به خدا و به روز بازپسین و فرشتگان و کتاب و پیمبران و داده است مال را با همه دوستی آن به نزدیکان و یتیمان و بینوایان و درماندگان و دریوزگان و در راه آزادکردن بندگان و بپای داشته است نماز را و داده است آکو را و آنان که وفا به پیمان کنند هرگاه پیمان بندند و بردباران در پریشانی و رنجوری و هنگام ترس جنگ آنانند که راست گفته‌اند و آنانند پرهیزکاران‌'),
(185, 2, 178, 'ای گروه مؤمنان نوشته شده است بر شما قصاص (خونخواهی) در کشتگان آزاد به آزاد و بنده به بنده و زن به زن و آن کس که بخشیده شده است بدو از برادرش چیزی پس پیروی‌کردن خوبی و پرداختنی بدو به نکوئی این کاهشی است از پروردگار شما و رحمتی پس آنکه تعدّی کند پس از این او را است عذابی دردناک‌'),
(186, 2, 179, 'و شما را است در قصاص (خون خواستن) زندگانیی ای خردمندان شاید پرهیزکاری کنید'),
(187, 2, 180, 'نوشته شده است بر شما گاهی که برسد یکی از شما را مرگ اگر مالی باز گذاشته است وصیّت برای والدین و نزدیکان بخوبی حقّی است بر پرهیزکاران‌'),
(188, 2, 181, 'پس آنکو بگرداندش پس از شنیدنش همانا گناه آن بر آنان است که بگردانندش همانا خدا شنوای دانا است‌'),
(189, 2, 182, 'و کسی که بترسیده است از وصیّت‌کننده ستم یا گناهی را پس اصلاح افکنده است میان ایشان پس نیست گناهی بر او همانا خدا است آمرزنده مهربان‌'),
(190, 2, 183, 'ای گروه مؤمنان نوشته شده است بر شما روزه بدانسان که نوشته شده است بر آنان که پیش از شما بودند شاید پرهیزکاری کنید'),
(191, 2, 184, 'روزکی چند و هر کس از شما بیمار یا در سفری باشد پس چند روز از روزهای دیگر و بر آنان که نتوانندش فدیه است خوراک بینوائی و آن کس که خواستار نکوئی شود بهتر است او را و روزه بگیرید بهتر است از برای شما اگر بدانید'),
(192, 2, 185, 'ماه رمضان که فرو شد در آن قرآن راهنمائی برای مردم و تابشهائی یا نشانیها از هدایت و فرقان پس هر کس شهر باشد از شما در این ماه روزه بگیردش و آن کس که بیمار یا در سفری باشد چند روز از روزهای دیگر خدا بر شما آسانی خواهد و نخواهد برای شما سختی را و تا به انجام رسانید شما را و تا بزرگ شمرید خدا را بدانچه رهبریتان کرد و شاید شکر گزارید'),
(193, 2, 186, 'و هرگاه پرسندت بندگان من از من همانا منم نزدیک اجابت کنم دعای آنکه مرا خواند پس بپذیرند از من و ایمان آورند به من شاید رهبری شوند'),
(194, 2, 187, 'حلال شد برای شما شب روزه درآمیختن با زنان خود آنانند پوشاک شما و شمائید پوشاک آنان دانست خدا که بودید شما خیانت می‌ورزیدید با خویشتن پس توبه کرد بر شما و ببخشود بر شما اکنون درآمیزید با آنان و بخواهید آنچه را خدا برای شما نوشت و بخورید و بنوشید تا پدیدار شود برای شما رشته سفید از رشته سیاه در بامدادگاه سپس به انجام رسانید روزه را تا شب و درنیامیزید با آنان حالی که معتکفید در مساجد این است حدود خدا زنهار بدانها نزدیک نشوید بدینسان بیان کند خدا آیتهای خویش را برای مردم شاید پرهیزکاری کنند'),
(195, 2, 188, 'و نخورید مالهای خویش را میان خویش به بیهوده و نیفکنید آنها را بسوی فرمانداران تا بخورید پاره‌ای از اموال مردم را به گناه حالی که خود می‌دانید'),
(196, 2, 189, 'پرسندت از ماه‌های تو بگو آنها گاهنماهائی است برای مردم و حجّ و نیست نکوئی که درآئید به خانه‌ها از پشت آنها بلکه نکوکار آن است که پرهیزکاری کند و به خانه‌ها درآئید از درگاه‌های آنها و بترسید خدا را باشد رستگار شوید'),
(197, 2, 190, 'و پیکار کنید در راه خدا آنان را که پیکارتان کنند و تجاوز نکنید که خدا دوست ندارد تجاوزگران را'),
(198, 2, 191, 'و بکشیدشان هر جا که یابیدشان و برون کنیدشان بدانسان که برون کردندتان و آشوب و نیرنگ سخت‌تر است از کشتن و پیکارشان نکنید نزد مسجد حرام تا پیکارتان کنند در آن پس اگر کشتند شما را بکشید ایشان را چنین است کیفر کافران‌'),
(199, 2, 192, 'و اگر دست کشیدند همانا خداوند است آمرزنده مهربان‌'),
(200, 2, 193, 'و نبرد کنید با ایشان تا نماند آشوبی و بشود دین از آن خدا پس اگر دست برداشتند همانا نیست ستمی مگر بر ستمگران‌'),
(201, 2, 194, 'ماه ارجمند (یا حرام) به ماه ارجمند و ارجمندیها (حرمتها) برابر است پس آن کس که تجاوز کند بر شما تجاوز کنید بر او برابر آنچه بر شما تجاوز کرده است و بترسید خدا را و بدانید که خدا است با پرهیزکاران‌'),
(202, 2, 195, 'و ببخشید در راه خدا و خود را با دست خود به پرتگاه نیفکنید و نکوئی کنید که خدا دوست دارد نکوکاران را'),
(203, 2, 196, 'به انجام رسانید حج و عمره را برای خدا و اگر بازداشته شدید پس آنچه فراهم شود از قربانی و نتراشید سرهای خویش را تا برسد قربانی به جایگاه خود و آن کس از شما که بیمار یا به او آزاری از سرش باشد پس فدیه‌ای از روزه یا صدقه یا قربانی تا گاهی که ایمن شدید پس هر کس متمتع شده است عمره را بسوی حجّ پس آنچه فراهم شود از قربانی و آنکس که نیابد پس روزه سه روز در حجّ و هفت روز گاهی که بازگردید اینک ده روز کامل این برای آن کس است که نیستند خاندان او نزدیک مسجد حرام و بترسید خدا را و بدانید که خداوند است سخت‌شکنجه‌'),
(204, 2, 197, 'حجّ ماه‌هائی است دانسته پس آنکه حجّ در آنها کند نه آمیزش نه نافرمانی و نه ستیزه‌کردنی است در حج و آنچه خوبی کنید میداندش خدا و توشه برگیرید که بهترین توشه‌ها تقوی است و بترسید مرا ای خردمندان‌'),
(205, 2, 198, 'نیست بر شما باکی که بخواهید فزونیی از پروردگار خود سپس گاهی که کوچ کردید از عرفات پس یاد خدا کنید نزد مشعر حرام و یاد او کنید بدانسان که رهبری کرد شما را و اگر چه بودید پیش از آن همانا از گمراهان‌'),
(206, 2, 199, 'سپس کوچ کنید بدانسان که کوچ کردند مردم و استغفار کنید خدا را که خدا است آمرزنده مهربان‌'),
(207, 2, 200, 'و هنگامی که جای آوردید عبادات خویش را پس یاد کنید خدا را مانند یادکردن پدران خویش یا سخت‌تر و از مردم است کسی که گوید پروردگارا بده ما را در دنیا و نیستش در آخرت بهره‌ای‌'),
(208, 2, 201, 'و از آنان است آنکه گوید پروردگارا بده ما را در دنیا نکوئی و در آخرت نکوئی و نگهدار ما را از عذاب آتش‌'),
(209, 2, 202, 'آنان را است بهره‌ای از آنچه فراهم کردند و خدا است شتابنده در حساب‌'),
(210, 2, 203, 'و یاد کنید خدا را روزهائی چند پس آن کس که بشتابد در دو روز نیست بر او گناهی و آن کس که دیر کند نیست بر او گناهی برای آن کس که پرهیز کرده است و بترسید خدا را و بدانید که بسوی او گرد آورده شوید'),
(211, 2, 204, 'و از مردم است آنکه شگفت آورد تو را سخنش در زندگانی دنیا و گواه گیرد خدا را بر آنچه در دل او است و او است سخت‌ترین دشمنان‌'),
(212, 2, 205, 'و هرگاه پشت کند بکوشد در زمین تا فساد کند در آن و نابود کند کِشت و نژاد را و خدا را خوش نیاید فساد'),
(213, 2, 206, 'و هرگاه بدو گفته شود بترس از خدا بگیردش تکبر به گناه بس است وی را دوزخ و چه زشت است آرامشگاه‌'),
(214, 2, 207, 'و از مردم است آنکه بفروشد خود را در پی خوشنودی خدا و خداوند است مهربان به بندگان‌'),
(215, 2, 208, 'ای گروه مؤمنان اندرآئید به صلح همگی و پیروی نکنید گامهای شیطان را که او است برای شما دشمنی آشکار'),
(216, 2, 209, 'و اگر لغزیدید از پس آنکه بیامده است شما را نشانیها پس بدانید که خدا است عزّتمند حکیم‌'),
(217, 2, 210, 'آیا انتظار دارند جز آنکه بیایدشان خدا در سایبانهائی از ابر و فرشتگان و بگذرد کار و بسوی خدا بازگردانیده شوند کارها'),
(218, 2, 211, 'بپرس بنی‌اسرائیل را چند آوردیمشان از آیتهای پدیدار و کسی که تبدیل کند نعمت خدا را پس از آنکه بیامدستش همانا خدا است سخت‌شکنجه‌'),
(219, 2, 212, 'آراسته شد برای کافران زندگانی دنیا و مسخره کنند به مؤمنان و آنان که پرهیزکاری کرده‌اند برتر از ایشانند روز قیامت و خدا روزی دهد هر که را که خواهد بی‌حساب‌'),
(220, 2, 213, 'بودند مردم یک امت پس برانگیخت خدا پیمبران را بشارت‌دهندگان و ترسانندگان و فرستاد با ایشان کتاب را به حقّ تا حکم کند میان مردم در آنچه اختلاف کردند در آن و اختلاف نکردند در آن مگر آنان که داده شدندش پس از رسیدن نشانیها بدیشان به ستمی میان خویش پس هدایت کرد خدا مؤمنان را بدانچه اختلاف کردند در آن از حقّ به اذن خود و خدا هدایت کند هر که را خواهد به راه راست‌'),
(221, 2, 214, 'یا گمان کردید که به بهشت درآئید و هنوز نیامده است شما را نمونه آنان که پیش از شما بودند که بدیشان رسید پریشانی و رنجوری و بر خود لرزیدند تا گفت پیمبر و آنان که ایمان آورده و با او بودند چه وقت است یاری خدا همانا یاری خدا است نزدیک‌'),
(222, 2, 215, 'پرسندت چه را انفاق کنند بگو آنچه انفاق کنید از مال پس برای والدین و نزدیکان و یتیمان و بینوایان و درماندگان راه است و آنچه نیکی کنید همانا خداوند است بدان دانا'),
(223, 2, 216, 'نوشته شد بر شما جنگ و آن ناپسند شما است و چه بسا ناخوش دارید چیزی را و آن است خوب برای شما و چه بسا دوست دارید چیزی را و آن است بد برای شما و خدا می‌داند و شما نمی‌دانید'),
(224, 2, 217, 'پرسندت از ماه حرام جنگ کردن در آن بگو جنگ کردن در آن گران است و بازداشتن است از راه خدا و کفر است بدو و به مسجد حرام و برون راندن مردمش از آن بزرگتر است نزد خدا و فتنه بزرگتر است از کشتن و پیوسته همی جنگ کنند با شما تا بازگردانند شما را از کیشتان اگر بتوانند و کسی که بازگردد از کیش خود پس بمیرد حالی که او است کافر آنان تباه شده است کردارشان در دنیا و آخرت و آنانند یاران آتش در آنند جاودانان‌'),
(225, 2, 218, 'همانا آنان که ایمان آوردند و آنان که هجرت کردند و جهاد کردند در راه خدا امید دارند رحمت خدا را و خداست آمرزنده مهربان‌'),
(226, 2, 219, 'پرسندت از باده و قمار بگو در آنها است گناهی بزرگ و سودهائی برای مردم و گناه آنها بزرگتر است از سود آنها و پرسندت چه چیز انفاق کنند بگو گذشت یا فزونی را چنین بیان کند خدا برای شما آیات را شاید بیندیشید'),
(227, 2, 220, 'در دنیا و آخرت و پرسندت از یتیمان بگو کارسازی ایشان بهتر است و اگر با آنان اختلاط کنید پس برادران شمایند و خدا می‌شناسد تبهکار را از کارساز و اگر می‌خواست خدا به ستوه می‌آورد شما را و خدا است عزّتمند حکیم‌'),
(228, 2, 221, 'و همسر نگیرید زنان مشرکه را تا ایمان آرند و همانا یک کنیزک مؤمنه بهتر است از زنی مشرکه اگر چه شیفته سازد شما را و همسر نگیرید مردان مشرک را تا ایمان آرند و همانا بنده مؤمن بهتر است از مشرک و اگر چه شگفت آورد شما را آنان دعوت کنند بسوی آتش و خدا دعوت کند بسوی بهشت و آمرزش به فرمان او و بیان کند آیتهای خویش را برای مردم شاید یادآور شوند'),
(229, 2, 222, 'پرسندت از حیض (خون زنان) بگو آن آزاری است پس کناره گیرید زنان را در حیض و نزدیکی نکنید با ایشان تا پاک شوند سپس گاهی که پاک شدند نزدیکی کنید با ایشان بدانسان که خدا دستورتان داده است همانا خدا دوست دارد توبه‌کنندگان و دوست دارد پاکیزگی‌جویان را'),
(230, 2, 223, 'زنان شما کشتزار شمایند پس به کشتزار خویش درآئید هر جا خواهید و پیش فرستید برای خود و بترسید خدا را و بدانید شمائید ملاقات‌کننده او و بشارت ده به مؤمنان‌'),
(231, 2, 224, 'نگردانید خدا را آماج سوگندهای خویش که نکوکاری کنید و پرهیزکاری کنید و کارسازی کنید میان مردم و خدا است شنوای دانا'),
(232, 2, 225, 'نگیرد شما را خدا به یاوه‌گویی در سوگندهای شما و لیکن بگیردتان بدانچه فراهم کرده است دلهای شما و خدا است آمرزنده بردبار'),
(233, 2, 226, 'برای آنان که به سوگند از زنان خود کناره‌گیری کنند انتظار چهار ماه است پس اگر بازگشتند همانا خدا است آمرزنده مهربان‌'),
(234, 2, 227, 'و اگر خواستار طلاق شدند همانا خدا است شنوای دانا'),
(235, 2, 228, 'و زنان طلاق گرفته در انتظار نگه دارند خویشتن را مدت سه پاک شدن و روا نیست آنان را که نهان دارند آنچه بیافریده است خدا در رحمهای ایشان اگر ایمان دارند به خدا و روز آخر شوهران ایشان سزاوارترند به بازگردانیدشان در آن اگر خواستار آشتی شدند و برای آن زنان است مثل آنچه بر آنان است به متعارف و مردان را است بر فراز ایشان پایه‌ای و خدا است عزّتمند حکیم‌'),
(236, 2, 229, 'طلاق دو بار است پس نگه داشتنی به خوبی یا رها ساختنی با نیکی و روا نیست شما را که بازستانید از آنچه بدانان داده‌اید چیزی را مگر آنکه بترسند که بپای ندارند حدود خدا را و اگر ترسیدید که بپای ندارند حدود خدا را نیست باکی بر آنان در آنچه زن به فدیه دهد (کابین بخشد و جان آزاد سازد) این است حدود خدا پس تجاوز نکنید آنها را و هر که تجاوز کند حدود خدا را آنانند ستمکاران‌'),
(237, 2, 230, 'و اگر طلاقش داد پس وی را روا نیست از آن پس تا درآمیزد با شوهری جز او سپس اگر طلاقش داد نیست باکی بر آنان که بازگردند به همدیگر اگر پنداشتند که بپای می‌کنند حدود خدا را این است حدود خدا بیان سازدش برای قومی که می‌دانند'),
(238, 2, 231, 'و اگر طلاق دادید زنان را و رسیدند به سرآمد خویش پس نگه داریدشان بخوبی یا رهاشان کنید بخوبی و نگهداری نکنید آنان را به آزار تا ستم کنید و آنکه این کار را کند ستم کرده است خویشتن را و نگیرید آیتهای خدا را به مسخره و یاد آرید نعمت خدا را بر شما و آنچه فرستاده است بر شما از کتاب و حکمت که اندرز دهد شما را بدان و بترسید خدا را و بدانید که خدا است به هر چیزی دانا'),
(239, 2, 232, 'و هنگامی که طلاق دادید زنان را و رسیدند به سرآمدشان پس بازندارید آنان را از آنکه همسر شوند با شوهران خویش اگر تراضی کنند فی ما بَین خویش به خوبی بدین اندرز داده شود آن کس از شما که ایمان دارد به خدا و روز بازپسین این پاکتر است برای شما و پاکیزه‌تر و خدا می داند و شما نمی‌دانید'),
(240, 2, 233, 'و مادران شیر دهند کودکان خویش را دو سال کامل برای کسی که بخواهد تمام کند شیر دادن را و بر مولودله (پدر کودک) است خوراک و پوشاک آنان به متعارف تکلیف نشود کسی جز به اندازه توانائیش آزار نشود مادر به کودکش و نه مولودله (پدر) به فرزندش و بر وارث است مانند این پس اگر خواستند از شیر بازگرفتن را با تراضی و مشاورت هر دو باکی بر آنان نیست و اگر برای کودکان خود شیرده خواستید باکی بر شما نیست اگر بپردازید آنچه را دهید بخوبی و بترسید خدا را و بدانید که خدا بدانچه می‌کنید بینا است‌'),
(241, 2, 234, 'و آنان که بمیرند از شما و بازمی گذارند زنانی در انتظار بگذارند آن زنان خویشتن را چهار ماه و ده روز تا گاهی که به سرآمد خود رسیدند باکی نیست بر شما در آنچه در باره خود کنند به نیکی و خدا بدانچه می‌کنید دانا است‌'),
(242, 2, 235, 'و نیست باکی بر شما در آنچه بدان اشارت کرده‌اید به خواستگاری زنان یا نهان داشته‌اید نزد خویشتن (در دل خویش) دانست خدا که خواهید سخن گفت با آنان و لیکن وعده ندهید ایشان را در خلوت (پنهانی) مگر آنکه بگوئید گفتاری متعارف و نبندید گره نکاح را (زناشوئی را) تا برسد نامه به سررسید خود و بدانید که خدا می‌داند آنچه را در دلهای شما است پس بترسیدش و بدانید که خدا است آمرزنده بردبار'),
(243, 2, 236, 'نیست باکی بر شما اگر طلاق دهید زنان را مادامی که دست بدیشان نرسانیده یا چیزی برای ایشان معین نکرده باشید و بهره‌مندشان (برخوردارشان) کنید بر توانگر است به مقدار توانائیش و بر تنگدست است به اندازه قدرتش بهره به خوبی حقی است بر نکوکاران‌'),
(244, 2, 237, 'و اگر طلاقشان دادید پیش از آنکه دست بدانان رسانید حالی که فریضه‌ای برای آنان فرض کرده باشید (کابینی بسته باشید) پس نیمی از آنچه فرض کرده‌اید مگر آنکه ببخشند یا ببخشد آنکه به دست اوست گره زناشوئی و ببخشید نزدیکتر است به تقوی و فراموش نکنید نکوکاری را میان خود همانا خدا بدانچه می‌کنید بینا است‌'),
(245, 2, 238, 'مواظبت کنید بر نمازها و نماز میانه و بپای ایستید برای خدا فروتنان‌'),
(246, 2, 239, 'و اگر ترسیدید پس پیادگان یا سواران تا گاهی که ایمن شدید یاد کنید خدا را بدانچه بیاموخت شما را آنچه نمی‌دانستید'),
(247, 2, 240, 'و آنان که می‌میرند از شما و باز می‌گذارند زنانی وصیّتی است برای زنانشان بهره‌ای تا یک سال بی‌برون‌کردن پس اگر برون شدند نیست باکی بر شما در آنچه درباره خویش کرده‌اند به متعارف و خدا است عزّتمند حکیم‌'),
(248, 2, 241, 'و برای زنان طلاق گرفته است بهره به متعارف حقّی است بر پرهیزکاران‌'),
(249, 2, 242, 'چنین بیان کند خدا آیتهای خود را برای شما شاید تعقّل کنید'),
(250, 2, 243, 'آیا ندیدی آنان را که برون شدند از کشور خود حالی که هزاران بودند از ترس مرگ پس بدیشان گفت خدا بمیرید سپس زنده کردشان همانا خدا است خداوند افزایش و بخشایش بر مردم و لیکن بیشتر مردم سپاس نمی‌گزارند'),
(251, 2, 244, 'و جنگ کنید در راه خدا و بدانید که خدا است شنوای دانا'),
(252, 2, 245, 'کیست آنکه وام دهد خدا را وامی نیکو تا بیافزایدش برای او چندین برابر و خدا تنگی و گشایش دهد و بسوی او بازگردانیده می‌شوید'),
(253, 2, 246, 'آیا ننگری بدان گروه از بنی‌اسرائیل پس از موسی هنگامی که گفتند به پیمبری که ایشان را بود برانگیز برای ما پادشاهی (فرماندهی) تا جنگ کنیم در راه خدا گفت آیا چنین نیستید که اگر جنگ بر شما نوشته شود نکنید گفتند چه شود ما را که پیکار نکنیم در راه خدا و برون رانده شدیم از خانمان و فرزندان خویش امّا گاهی که نوشته شد بر ایشان جنگ پشت کردند جز کمی از ایشان و خدا دانا است به ستمگران‌'),
(254, 2, 247, 'و گفت بدیشان پیغمبرشان همانا خدا برانگیخت برای شما طالوت را پادشاهی گفتند چگونه وی را بر ما فرمانروائی باشد و ما سزاوارتریم از او به پادشاهی و داده نشده است گشایشی در مال گفت همانا خدا برگزیدش بر شما و بیفزودش عظمتی در دانش و پیکر و خدا دهد پادشاهیش را به هر که خواهد و خدا است گشایشمند دانا'),
(255, 2, 248, 'و گفت بدیشان پیمبرشان همانا نشانی پادشاهی او آن است که بیاید شما را تابوت در آن آرامشی از پروردگار شما و بازمانده‌ای از آنچه بازگذاردند خاندان موسی و هارون که حمل کنندش فرشتگان همانا در این است نشانیی برای شما اگر هستید مؤمنان‌'),
(256, 2, 249, 'و هنگامی که سان داد و براند طالوت سپاه را گفت هرآینه خداوند است آزماینده شما به وسیله جویی تا هر کس بنوشد از آن نباشد از من و آن کس که نچشدش از من باشد مگر آنکه با دست خویش کفی از آن برگیرد پس نوشیدند از آن جز کمی از ایشان سپس گاهی که بگذشت از آن او و آنان که ایمان آورده بودند با او گفتند نیست ما را نیرویی امروز برابر جالوت و لشکرش گفتند آنان که می پنداشتند که ایشانند ملاقات‌کننده خدا چه بسا گروهی کم پیروز شدند بر گروهی بسیار به اذن خدا و خدا است با شکیبایان‌'),
(257, 2, 250, 'و هنگامی که برابر شدند با جالوت و سپاهش گفتند پروردگارا بریز بر ما شکیبایی را و استوار ساز پاهای ما را و یاری کن ما را بر گروه کافران‌'),
(258, 2, 251, 'پس شکستشان دادند به اذن خدا و کشت داود جالوت را و داد خدا بدو پادشاهی و حکمت را و بیاموختش از آنچه می‌خواست و اگر نبود برکنارکردن خدا مردم را بعضی را با بعضی هرآینه تباه می‌شد زمین و لیکن خدا دارای فضل است بر جهانیان‌'),
(259, 2, 252, 'این است آیتهای خدا که می‌خوانیمش بر تو به حقّ و همانا توئی از فرستادگان‌'),
(260, 2, 253, 'اینک پیمبران برتری دادیم بعضیشان را بر بعضی از آنان است آنکه سخن گفت با او خدا و بالا برد بعضی را از ایشان پایه‌هائی و دادیم به عیسی ابن مریم نشانیها را و مؤیّدش داشتیم به روح‌القدس و اگر می‌خواست خدا هرآینه کارزار نمی‌کردند آنان که پس از ایشانند از پس آنچه بیامدشان نشانیها و لکن اختلاف کردند پس بعضی از ایشان ایمان آوردند و بعضی کفر ورزیدند و اگر می‌خواست خدا کارزار نمی‌کردند و لیکن خدا می‌کند آنچه را بخواهد'),
(261, 2, 254, 'ای آنان که ایمان آوردید انفاق کنید از آنچه شما را روزی دادیم پیش از آنکه بیاید روزی که نیست در آن سوداگری و نه دوستی و نه شفاعتی و کافرانند ستمگران‌'),
(262, 2, 255, 'خدا که نیست خداوندی جز او زنده پاینده نگیردش خمار و نه خوابی (یا بیداری و خوابی) او را است آنچه در آسمانها است و آنچه در زمین کیست که شفاعت کند به نزدش جز با اذن او می‌داند آنچه را پیش روی ایشان و آنچه را پشت سر ایشان است و فرانگیرند به چیزی از دانش او جز بدانچه خواهد فراگرفته است کرسیش آسمانها و زمین را و بر او گران نیاید نگهداری آنها و او است برتر بزرگوار'),
(263, 2, 256, 'نیست به ناخواه واداشتنی در دین همانا آشکار شد رهبری از گمراهی پس آنکه کفر ورزد به ستمگر و ایمان آورد به خدا همانا چنگ زده است به دست‌آویزی استوار که نیستش گسیختنی و خدا است شنوای دانا'),
(264, 2, 257, 'خدا دوست آنان است که ایمان آوردند برون آردشان از تاریکیها بسوی روشنائی و آنان که کفر ورزیدند دوستانشان ستمگرانند که برون رانندشان از روشنائی بسوی تاریکیها آنانند یاران آتش در آنند جاودانان‌'),
(265, 2, 258, 'آیا ندیدی آن را که ستیزه کرد با ابراهیم درباره پروردگارش زیرا که خدا داده بودش پادشاهی را هنگامی که گفت ابراهیم پروردگار من آن است که زنده کند و بمیراند گفت من زنده کنم و بمیرانم ابراهیم گفت همانا خدا بیاورد خورشید را از خاور پس بیارش از باختر پس سراسیمه شد آنکه کفر ورزید و خدا هدایت نکند گروه ستمگران را'),
(266, 2, 259, 'یا مانند آنکه بگذشت بر شهری که فرو خوابیده بود بر ستونها (یا بر پوشهای) خود گفت کی (چگونه) خدا زنده کند این را پس از مردنش پس بمیرانیدش خدا صد سال سپس برانگیختش گفت چند مانده‌ای گفت مانده‌ام روزی یا پاره‌ای از روز گفت بلکه مانده‌ای صد سال پس بنگر به خوراک و نوشابه خویش که بویش نگشته و بنگر به درازگوش خود و تا بگردانیمت آیتی برای مردم و بنگر به استخوانها چگونه پیوند کنیم سپس آنها را به گوشت پوشانیم پس هنگامی که آشکار شد برای او گفت می‌دانم که خدا به هر چیز است توانا'),
(267, 2, 260, 'و هنگامی که گفت ابراهیم پروردگارا بنمایانم چگونه زنده کنی مردگان را گفت مگر ایمان نیاوردی گفت بلی و لیکن تا آرام گیرد دلم گفت پس برگیر چهار مرغ را و پاره گردان آنها را به نزد خود پس بگذار فراز هر کوهی از آنها پاره‌ای را پس فراخوانشان بیایندت به دویدن و بدان که خدا است عزتمند حکیم‌'),
(268, 2, 261, 'مثَل آنان که بخشند مال خود را در راه خدا مانند دانه است که برویاند هفت خوشه در هر خوشه صد دانه و خدا چند برابر کند برای هر که خواهد و خدا است گشایشمند دانا'),
(269, 2, 262, 'آنان که بخشند مال خود را در راه خدا و در پی نیارند آنچه را بخشیدند منتی و نه آزاری ایشان را است پاداش ایشان نزد پروردگار خویش و نه ترسی بر ایشان است و نه اندوهگین شوند'),
(270, 2, 263, 'گفتاری نیک و آمرزش گنهی بهتر است از تصدقّی که در پیش آزاری باشد و خدا است بی‌نیاز بردبار'),
(271, 2, 264, 'ای آنان که ایمان آوردید تباه نکنید تصدقهای خود را به منّت و آزار مانند آنکه انفاق کند مال خویش را برای ریای (خودنمائی) مردم و ایمان نیارد به خدا و روز جزا پس مثَل او مانند سنگی است لغزان که بر آن باشد خاکی و رسیده باشدش بارانی سخت که بازگذاردش لغزنده قدرت ندارند بر چیزی از آنچه فراهم آوردند و خدا هدایت نکند گروه کافران را'),
(272, 2, 265, 'و مثَل آنان که می‌بخشند مال خویش را در پیجوئی رضای خدا و برای استوارساختن نفوس خود مانند باغی است بر پشته‌ای که رسیده باشدش بارانی سخت پس داده باشد میوه خود را چهار برابر و اگر نرسیده باشدش بارانی درشت پس بارانی خرد و خدا بدانچه می‌کنید بینا است‌'),
(273, 2, 266, 'آیا دوست دارد یکی از شما که وی را باغی باشد از نخلستان و تاکستان روان باشد زیر آن جویها و برای او در آن از تمام میوه‌ها باشد و رسیده باشدش پیری و او را فرزندانی بینوا باشند پس برسدش بادی که در آن باشد آتشی پس بسوخته باشد چنین بیان کند خدا برای شما آیتهای خویش را شاید اندیشه کنید'),
(274, 2, 267, 'ای گروه مؤمنان ببخشید از پاکیزه‌های آنچه فراهم کردید و از آنچه بیرون آوردیم از زمین و آهنگ پلید نکنید که از آن انفاق کنید و نیستید ستاننده آن جز با فروخواباندن چشم در آن و بدانید که خدا است بی‌نیاز ستوده‌'),
(275, 2, 268, 'شیطان به شما وعده فقر دهد و بفرمایدتان به فحشاء و خدا نویدتان دهد به آمرزشی از خویش و فزونیی و خداست گشایشمند دانا'),
(276, 2, 269, 'دهد حکمت را به هر که خواهد و آن کس که حکمت را داده شده است همانا داده شده است خیری بسیار و یادآور نشوند جز خردمندان‌'),
(277, 2, 270, 'و آنچه انفاق کرده‌اید از نفقه یا نذر کرده‌اید از نذر همانا می‌داندش خدا و نیست ستمگران را یاران‌'),
(278, 2, 271, 'اگر آشکارا دهید تصدقها را چه خوب و اگر پنهان دارید و به فقراء دهید بهتر باشد برای شما و جبران کند از گناهان شما و خدا بدانچه می‌کنید دانا است‌'),
(279, 2, 272, 'نیست بر تو هدایت ایشان و لیکن خدا هدایت کند آنکه را خواهد و آنچه انفاق کنید از مال برای خود شما است و انفاق نمی‌کنید جز در پیجوئی روی خدا و آنچه انفاق کنید از مال داده می‌شود به شما و ستم کرده نمی‌شوید'),
(280, 2, 273, 'برای بینوایانی که در راه خدا دچار تنگی شدند نتوانند گشتن را در زمین از پی روزی پنداردشان نادان توانگرانی از عفت‌نمائی بشناسیشان به چهره‌های ایشان دریوزگی نکنند از مردم به سماجت و آنچه انفاق کنید از مال همانا خداوند است بدان دانا'),
(281, 2, 274, 'آنان که انفاق کنند مال خود را در شب و روز نهان و آشکارا ایشان را است پاداش ایشان نزد پروردگار خود و نه ترسی بر ایشان است و نه اندوهگین شوند'),
(282, 2, 275, 'آنان که ربا (سود) می‌خورند برنخیزند جز مانند برخاستن آن که آشفته سازدش شیطان به دیوانگی این بدان است که گفتند همانا سوداگری مانند مانند ربا است حالی که حلال کرد خدا سوداگری را و حرام کرده است سودخوری را و آن کس که بیایدش اندرزی از پروردگار او پس باز ایستد او را است آنچه بگذشت و کار او با خدا است و آن کس که بازگردد پس آنانند یاران آتش در آنند جاودانان‌'),
(283, 2, 276, 'نابود کند خدا ربا را و برکت دهد به صدقات و خدا دوست ندارد هر ناسپاس تبهکاری را'),
(284, 2, 277, 'همانا آنان که ایمان آوردند و کردار شایسته کردند و بپا کردند نماز را و دادند زکات را ایشان را است پاداششان نزد پروردگارشان نه ترسی بر ایشان است و نه اندوهگین شوند'),
(285, 2, 278, 'ای آنان که ایمان آوردید بترسید خدا را و رها کنید آنچه را به جای مانده است از ربا اگر هستید مؤمنان‌'),
(286, 2, 279, 'و اگر نکردید پس اعلام کنید جنگی را با خدا و پیمبرش و اگر توبه کردید شما را است سرمایه‌های شما نه ستم کنید و نه ستم کرده شوید'),
(287, 2, 280, 'و اگر تنگدست باشد پس مهلتی تا گشایش و تصدق کنید بهتر است شما را اگر بدانید'),
(288, 2, 281, 'و بترسید روزی را که بازگردانیده شوید در آن بسوی خدا پس داده شود به هر کس آنچه را فراهم کرده است و ستم نشوند'),
(289, 2, 282, 'ای آنان که ایمان آوردید هرگاه وام دهید یا گیرید تا سرآمدی نامبرده پس بنویسیدش و باید نویسد میان شما نویسنده‌ای به داد و نباید خودداری کند نویسنده‌ای از آنکه بنویسد بدانسان که خدا بیاموخته است او را پس بنویسد و املاء کند بر او آن کس که بر او است حق و بترسد خدا را پروردگار خویش و نکاهد از آن چیزی و اگر آن کس که بر او است حق کم خرد بود یا ناتوان یا نتوانست املاء کند پس املاء کند ولی او به داد و گواه گیرید دو گواه از مردان خویش را و اگر نبود دو مرد پس یک مرد و دو زن از آنان که پسند کنید از گواهان تا چون فراموش کند یکی به یاد آردش دیگری و خودداری نکنند گواهان هرگاه خوانده شدند و خسته نشوید از نوشتن آن خرد یا کلان تا سرآمدش این است دادگرانه‌تر نزد خدا و استوارتر برای گواهی و نزدیکتر بدانکه شک نیارید مگر آنکه باشد داد ستدی جاری که می‌گردانیدش میان خویش که نیست باکی بر شما ننوشتن آن و گواه گیرید گاهی که داد ستد کنید و آزار نشود نویسنده‌ای و نه گواهی و اگر بکنید همانا آن نافرمانیی است از شما و بترسید خدا را و می‌آموزدتان خدا و خدا است به هر چیز دانا'),
(290, 2, 283, 'و اگر بر سفری باشید و نیابید نویسنده‌ای پس گروگانی به دست و اگر بسپرد یکی از شما به دیگری پس باید بپردازد آن کس که سپرده شده است سپرده خویش را و باید بترسد خدا را پروردگار خود و کتمان نکنید گواهی را و هر کس کتمان کند آن را همانا گنهکار است دلش و خدا بدانچه می‌کنید دانا است‌'),
(291, 2, 284, 'خدا را است آنچه در آسمانها و آنچه در زمین است و اگر آشکار کنید آنچه را در دلهای شما است یا نهان داریدش حساب کند شما را بدان خدا پس بیامرزد هر که را خواهد و عذاب کند هر که را خواهد و خدا بر هر چیزی توانا است‌'),
(292, 2, 285, 'ایمان آورده است پیمبر بدانچه فرستاده شده به سوی او از پروردگار خویش و مؤمنان هر کدام ایمان آوردند به خدا و فرشتگان او و کتابهای او و پیمبران او فرق نگذاریم بین احدی از پیمبرانش و گفتند شنیدیم و فرمان بردیم آمرزشی از تو پروردگارا و به سوی تو است بازگشت‌'),
(293, 2, 286, 'تکلیف نکند خدا کسی را جز به قدر طاقت او برای او است آنچه دست آورد اوست و بر او است آنچه دست آورده است پروردگارا مگیر بر ما اگر فراموش کردیم یا خطا کردیم پروردگارا بار مکن ما را گرانی چنانکه بار کردی آن را بر آنان که پیش از ما بودند پروردگارا و نه تحمیل کن بر ما آنچه را بدان طاقتی نیست و ببخش بر ما و بیامرز ما را و رحم کن ما را توئی سرپرست ما پس یاری کن ما را بر گروه کافران‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الم‌'),
(295, 3, 2, 'خدا که نیست خدائی جز او زنده پاینده‌'),
(296, 3, 3, 'فرستاده است بر تو نامه را به حقّ حالی که تصدیق‌کننده آنچه پیش روی آن است و فرستاده است تورات و انجیل را'),
(297, 3, 4, 'از پیش حالی که هدایتی است برای مردم و فرستاده است فرقان را همانا آنانکه کفر ورزیدند به آیتهای خدا ایشان را است شکنجه‌ای سخت و خدا است عزتمند انتقام جوی‌'),
(298, 3, 5, 'همانا خدا مخفی نیست بر او چیزی در زمین و نه در آسمان‌'),
(299, 3, 6, 'اوست که صورتگری کند شما را در رحمها هرگونه که خواهد نیست خدائی جز او عزتمند حکیم‌'),
(300, 3, 7, 'او است آنکه فرستاده است بر تو نامه را از آن است آیتهای استواری که آنها است مادر کتاب و بخش دیگر متشابهات (ناهویدا) پس آنان که در دلهاشان کجی است پیروی کنند آنچه را متشابه است از آن برای فتنه‌جوئی و از پی تاویل آن حالی که نمی‌داند تاویل آن را جز خدا و فرورفتگان در علم گویند ایمان آوردیم بدان هر یک (همگی) از نزد پروردگار ما است و یادآور نشوند جز خردمندان‌'),
(301, 3, 8, 'پروردگارا ملغزان دلهای ما را پس از آنکه هدایت کردی ما را و ببخش ما را از نزد خود رحمتی که توئی بسیار بخشنده‌'),
(302, 3, 9, 'پروردگارا همانا توئی گردآورنده مردم برای روزی که نیست در آن شکی همانا خدا خلف نکند وعده را'),
(303, 3, 10, 'آنان که کفر ورزیدند بی‌نیاز نکندشان اموالشان و نه فرزندانشان از خدا به چیزی و ایشانند سوخت آتش‌'),
(304, 3, 11, 'همچون شیوه آل فرعون و آنان که پیش از ایشان بودند تکذیب کردند آیتهای ما را پس گرفت خدا ایشان را به گناهانشان و خدا است سخت‌شکنجه‌'),
(305, 3, 12, 'بگو بدانان که کفر ورزیدند به زودی سرکوب شوید و گرد آورده شوید به سوی دوزخ و چه زشت است آرامشگاه‌'),
(306, 3, 13, 'همانا شما را است آیتی در دو سپاه که تلاقی کردند سپاهی که نبرد می‌کرد در راه خدا و دیگری که کافر بود می‌دیدندشان دو برابر خویش به دیدن چشم و خدا است آنکه کمک کند به یاری خود هر که را خواهد همانا در این است پندی از برای خردمندان یا بینایان‌'),
(307, 3, 14, 'آراسته شد برای مردم دوستی شهوتها از زنان و فرزندان و پوستهای گاو آکنده از زر و سیم و اسبان داغ‌دار و دامها و کشت این است بهره زندگانی دنیا و خدا است نزد او نکو بازگشتنگاه‌'),
(308, 3, 15, 'بگو آیا آگهیتان دهم به بهتر از این آنان را که پرهیزکارند نزد پروردگارشان باغهائی است روان زیر آنها جویها جاودانند در آنها و زنانی پاک شده و خوشنودیی از خدا و خدا به بندگان است بینا'),
(309, 3, 16, 'آنان که گویند پروردگارا ایمان آوردیم پس بیامرز برای ما گناهان ما را و نگه دار ما را از عذاب آتش‌'),
(310, 3, 17, 'آن شکیبایان و راستگویان و فروتنان و بخشایندگان و استغفارکنندگان در سحرها'),
(311, 3, 18, 'گواهی داد خدا که نیست خدائی جز او و فرشتگان و دانشمندان قیام‌کننده به داد نیست خداوندی جز او عزتمند حکیم‌'),
(312, 3, 19, 'همانا دین نزد خدا اسلام است و اختلاف نکردند آنان که داده شدند کتاب را مگر پس از آنکه بیامدشان علم به ستمگری میان خویش و کسی که کفر ورزد به آیتهای خدا همانا خدا است شتابنده در حساب‌'),
(313, 3, 20, 'پس اگر با تو در ستیزند بگو روی آوردم به سوی خدا و آنکه مرا پیروی کرد و بگو بدانان که داده شدند کتاب را و امیون (مادرزادگان یا ناخوانایان) آیا اسلام آوردید پس اگر اسلام آوردند همانا هدایت یافتند و اگر پشت کردند نیست بر تو جز رساندن و خدا به بندگان است بینا'),
(314, 3, 21, 'همانا آنان که کفر ورزند به آیتهای خدا و بکشند پیمبران را به ناحقّ و بکشند آنان را که امر کنند به دادگری از مردم پس آگهیشان ده به عذابی دردناک‌'),
(315, 3, 22, 'آنانند که تباه شد کارهای ایشان در دنیا و آخرت و نیستشان یارانی‌'),
(316, 3, 23, 'آیا ندیدی آنان را که داده شدند بهره‌ای را از کتاب خوانده می‌شوند به کتاب خدا تا حکم کند میانشان پس پشت کنند گروهی از ایشان حالی که روگردانند'),
(317, 3, 24, 'این بدان است که گفتند نرسد ما را آتش جز روزکی چند و بفریفتشان در دینشان آنچه دروغ می‌بستند'),
(318, 3, 25, 'پس چگونه است هنگامی که گرد آوریمشان برای روزی که نیست شکی در آن و داده شود به هر کس هر آنچه دست آورده است و ستم نشوند'),
(319, 3, 26, 'بگو بار خدایا دارنده پادشاهی (فرمانروائی) دهی پادشاهی را به هر که خواهی و بستانیش از هر که خواهی و عزیز گردانی هر که را خواهی و خوار کنی هر که را خواهی به دست تو است خیر همانا توئی بر همه چیز توانا'),
(320, 3, 27, 'فرو بری شب را در روز و فرو بری روز را در شب و برون آری زنده را از مرده و بیرون آوری مرده را از زنده و روزی دهی هر که را خواهی بی‌شمار'),
(321, 3, 28, 'نگیرند مؤمنان کافران را دوستانی جز مؤمنان و آنکس که بکند این کار را نیست از خدا در چیزی مگر آنکه پرهیز کنید از ایشان پرهیزی و بیم دهد شما را خدا از خویشتن و به سوی خدا است بازگشت‌'),
(322, 3, 29, 'بگو اگر پنهان سازید آنچه را در سینه‌های شما است یا آشکار کنید بداندش خدا و می‌داند آنچه را در آسمانها است و آنچه در زمین و خدا بر هر چیزی توانا است‌'),
(323, 3, 30, 'روزی که بیابد هر کس آنچه را کرده است از خوبی آماده و هر چه کرده است از بدی آرزو کند کاش میان او و میان آن مسافت دوری می‌بود و بیم دهد شما را خدا از خویش و خداوند است به بندگان مهربان‌'),
(324, 3, 31, 'بگو اگر دوستدار خدائید مرا پیروی کنید تا دوست دارد شما را خدا و بیامرزد از گناهان شما و خدا است آمرزنده مهربان‌'),
(325, 3, 32, 'بگو فرمان برید خدا و رسول را پس اگر پشت کردند همانا خدا دوست ندارد کافران را'),
(326, 3, 33, 'همانا برگزید خدا آدم و نوح و خاندان ابراهیم و خاندان عمران را بر جهانیان‌'),
(327, 3, 34, 'نژادی که بعضیشان از بعضی است و خدا شنوای دانا است‌'),
(328, 3, 35, 'هنگامی که گفت زن عمران پروردگارا نذر کردم برای تو آنچه را در شکم دارم آزاد پس بپذیر از من همانا توئی شنوای دانا'),
(329, 3, 36, 'و هنگامی که بنهادش گفت پروردگارا همانا بنهادمش مادینه و خدا داناتر است بدانچه بنهاد و نیست نرینه مانند مادینه و همانا نامیدش مریم و پناه دادمش به تو خود و فرزندانش را از شیطان رانده شده‌'),
(330, 3, 37, 'پس پذیرفتش خدا پذیرفتنی نکو و رویانیدش روئیدنی نکو و کفیلش ساخت زکریا را هرگاه درآمدی بر او زکریا در محراب یافتی نزدش روزیی گفتی ای مریم از کجا است تو را این گفتی آن از نزد خدا است همانا خداوند روزی دهد هر که را خواهد بی‌شمار'),
(331, 3, 38, 'اینجا خواند زکریا پروردگار خویش را گفت پروردگارا ببخش مرا از نزد خود نسلی پاکیزه که توئی همانا شنونده دعا'),
(332, 3, 39, 'پس بانگ زدندش فرشتگان و او ایستاده بود به نماز در پرستشگاه که خدایت مژده دهد به یحیی تصدیق‌کننده به کلمه‌ای از خدا و بزرگواری (مهتری) و خویشتن نگهدار و پیمبری از شایستگان‌'),
(333, 3, 40, 'گفت پروردگارا چگونه مرا پسری شود و رسیده است مرا پیری و زنم نازا است گفت بدینگونه خدا می‌کند هرچه را خواهد'),
(334, 3, 41, 'گفت پروردگارا قرار ده برای من آیتی گفت آیت تو آن است که سخن نگوئی با مردم سه روز جز به رمز و یاد کن پروردگار خویش را بسیار و سپاس گزار به شامگاهان و بامداد'),
(335, 3, 42, 'و هنگامی که گفتند فرشتگان ای مریم همانا خداوندت برگزید و پاک ساخت و اختیار کرد تو را بر زنان جهانیان‌'),
(336, 3, 43, 'ای مریم فروتن شو برای پروردگار خویش و سجود کن و رکوع کن با رکوع گزاران‌'),
(337, 3, 44, 'این از اخبار نهان است که وحی فرستیمش به سوی تو و نبودی نزد آنان گاهی که می‌افکندند قلمهای خویش را که کدامین کفیل شوند مریم را و نبودی نزد ایشان گاهی که ستیزه می‌کردند'),
(338, 3, 45, 'هنگامی که گفتند فرشتگان ای مریم همانا خدایت بشارت دهد به کلمه‌ای از خود نامش مسیح عیسی فرزند مریم آبرومندی در دنیا و آخرت و از مقربان‌'),
(339, 3, 46, 'و سخن گوید مردم را در گهواره و در پیری و از شایستگان‌'),
(340, 3, 47, 'گفت پروردگارا چگونه باشدم فرزندی و به من نزدیک نشده است بشری گفت بدینسان خدا بیافرد هرچه را خواهد چون بگذراند کاری را جز این نیست که گویدش بشو پس بشود'),
(341, 3, 48, 'و بیاموزدش کتاب و حکمت و تورات و انجیل را'),
(342, 3, 49, 'و فرستاده‌ای به سوی بنی‌اسرائیل که آوردم شما را آیتی از پروردگار شما که می‌آفرم برای شما از گل مانند پیکر پرنده و می‌دمم در آن پس می‌شود پرنده‌ای به اذن خدا و بهبودی دهم کور مادرزاد و پیس را و زنده کنم مردگان را به اذن خدا و آگهی دهم شما را از آنچه می‌خورید و آنچه در خانه‌های خود نگه می‌دارید همانا در این است آیتی برای شما اگر هستید مؤمنان‌'),
(343, 3, 50, 'و تصدیق‌کننده آنچه پیش روی من است از تورات و تا حلال کنم برای شما بعض آنچه حرام بوده است بر شما و آوردم شما را آیتی از پروردگار شما پس بترسید خدا را و مرا فرمان برید'),
(344, 3, 51, 'همانا خدا است پروردگار من و شما پس بپرستیدش این است راهی راست‌'),
(345, 3, 52, 'و هنگامی که احساس کرد عیسی از ایشان کفر را گفت کیستند یاران من به سوی خدا گفتند حواریون مائیم یاران خدا ایمان آوردیم به خدا و گواه باش که مائیم مسلمانان‌'),
(346, 3, 53, 'پروردگارا ایمان آوردیم بدانچه فرستادی و پیروی کردیم پیمبر را پس بنویس ما را با گواهان‌'),
(347, 3, 54, 'و مکر کردند و مکر کرد خدا و خدا است بهترین مکرکنندگان‌'),
(348, 3, 55, 'هنگامی که گفت خدا ای عیسی همانا بازگیرنده‌ام تو را و بالابرنده‌ام تو را به سوی خود و پاک‌کننده‌ام تو را از آنان که کفر ورزیدند و گرداننده‌ام آنان را که پیرویت کردند برتر از آنان که کفر ورزیدند تا روز قیامت سپس به سوی من است بازگشت شما پس حکم کنم میان شما در آنچه بودید در آن اختلاف می‌کردید'),
(349, 3, 56, 'پس آنان که کفر ورزیدند عذابشان کنم عذابی سخت در دنیا و آخرت و نیستشان یارانی‌'),
(350, 3, 57, 'و اما آنان که ایمان آوردند و کردار شایسته کردند پس سراسر دهد بدیشان پاداششان را و خدا نیست دوستدار ستمگران‌'),
(351, 3, 58, 'این را همی‌خوانیمش بر تو از آیتها و یادداشت حکمت‌آمیز (استوار)'),
(352, 3, 59, 'همانا مثل عیسی نزد خدا مانند آدم است بیافریدش از خاک پس بدو گفت بشو پس شد'),
(353, 3, 60, 'حق از پروردگار تو است پس نباش از شک‌کنندگان‌'),
(354, 3, 61, 'پس هر که بستیزد با تو در آن از پس آنچه بیامده است تو را از دانش بگو بیائید بخوانیم فرزندان ما و فرزندان شما را و زنان ما و زنان شما را و ما خود و شما خویش را سپس نفرین کنیم پس بگردانیم لعنت خدا را بر دروغگویان‌'),
(355, 3, 62, 'همانا این است داستانهای حقّ و نیست خدائی جز خدا و همانا خدا است عزتمند حکیم‌'),
(356, 3, 63, 'پس اگر روی برگردانیدند همانا خدا دانا است به تبهکاران‌'),
(357, 3, 64, 'بگو ای اهل کتاب بیائید به سوی سخنی یکسان میان ما و شما که نپرستیم جز خدا را و انباز وی نگردانیم چیزی را و نگیرد بعضی از ما بعضی را خدایانی جز خدا پس اگر پشت کردند بگوئید گواه باشید که مائیم مسلمانان‌'),
(358, 3, 65, 'ای اهل کتاب چرا ستیزه کنید در ابراهیم حالی که فرستاده نشده است تورات و انجیل مگر پس از او آیا تعقّل نمی‌کنید'),
(359, 3, 66, 'اینک شما ستیزه کردید در آنچه شما را است علمی بدان پس چرا ستیزه کنید در آنچه نیستتان علمی بدان و خدا می‌داند و شما نمی‌دانید'),
(360, 3, 67, 'نبود ابراهیم یهود و نه ترسائی و لیکن بود یکتاپرستی مسلمان و نبود از شرک ورزندگان‌'),
(361, 3, 68, 'همانا سزاوارترین مردم به ابراهیم آنانند که پیرویش کردند و این پیمبر و آنان که ایمان آوردند و خدا است سرپرست مؤمنان‌'),
(362, 3, 69, 'دوست داشتند گروهی از اهل کتاب کاش گمراه می‌کردند شما را و گمراه نکنند جز خویشتن را و درنمی یابند'),
(363, 3, 70, 'ای اهل کتاب چرا کفر می‌ورزید به آیتهای خدا و شما خود گواهید'),
(364, 3, 71, 'ای اهل کتاب چگونه می‌پوشانید حقّ را به باطل و نهان می‌دارید حقّ را و خود می‌دانید'),
(365, 3, 72, 'و گفتند گروهی از اهل کتاب ایمان آرید بدانچه فرستاده شده است بر آنان که ایمان آوردند آغاز روز و کفر ورزید آخر آن شاید بازگردند'),
(366, 3, 73, 'و ایمان نیارید جز برای آنکو پیروی کرده است کیش شما را بگو همانا هدایت خدا است هدایت تا نبادا داده شود کسی مانند آنچه داده شدید یا بستیزند با شما نزد پروردگارتان بگو همانا فزونی به دست خدا است دهدش به هر که خواهد و خدا است گشایشمند دانا'),
(367, 3, 74, 'مخصوص گرداند به رحمت خود هر که را خواهد و خدا است دارنده فضلی بزرگ‌'),
(368, 3, 75, 'و از اهل کتاب است آنکه اگر بسپری بدو پوست گاوی آکنده از زر بازگرداندش به تو و از ایشان است آنکه اگر دیناری بدو سپری بازش نگرداند به تو مگر آنچه پیوسته بر آن ایستاده باشی این بدان است که گفتند نیست بر ما در اُمیّین (مادرزادگان یا ناخوانایان) راهی و گویند بر خدا دروغ را و خود می‌دانند'),
(369, 3, 76, 'بلکه آن کس که وفا کند به عهد خود و بپرهیزد همانا خدا است دوستدار پرهیزکاران‌'),
(370, 3, 77, 'همانا آنان که بفروشند پیمان خدا و سوگندهای خویش را به بهائی اندک آنان را نیست بهره‌ای در آخرت و سخن نگوید با ایشان خدا و ننگرد بدیشان روز قیامت و نه پاک سازدشان و برای ایشان است عذابی دردناک‌'),
(371, 3, 78, 'و همانا از ایشان است گروهی که گردانند زبانهای خویش را به کتاب تا پنداریدش از کتاب و نیست آن از کتاب و گویند آن از نزد خدا است و نیست آن از نزد خدا و گویند بر خدا دروغ را و خود می‌دانند'),
(372, 3, 79, 'نبوده است برای بشری که بدهدش خدا کتاب و حکم و پیمبری را سپس به مردم گوید باشید بندگان من جز خدا و لیکن باشید خدایگان (یا دانشمندان دین یا پرورش‌دهندگان) بدانچه آموزید کتاب را و بدانچه درس فراگیرید'),
(373, 3, 80, 'و نفرماید شما را که برگیرید فرشتگان و پیمبران را خدایانی آیا فرمان دهد شما را به کفر پس از آنکه هستید مسلمانان‌'),
(374, 3, 81, 'و هنگامی که برگرفت خدا پیمان پیمبران را که آنچه آوردم شما را از کتاب و حکمت پس آمد شما را فرستاده‌ای تصدیق‌کننده آنچه با شما است البته بدو ایمان آرید و البته یاریش کنید گفت آیا اقرار کردید و گرفتید بر این پیمان مرا گفتند اقرار کردیم گفت پس گواه باشید و منم با شما از گواهان‌'),
(375, 3, 82, 'و آن کس که روی گرداند پس از این آنانند نافرمانان‌'),
(376, 3, 83, 'پس آیا جز دین خدا را خواهند و برای او اسلام آورده است هر که در آسمانها و زمین است خواه و ناخواه و به سوی او بازگردانیده می‌شوند'),
(377, 3, 84, 'بگو ایمان آوردیم به خدا و آنچه بر ما فرود آمد و آنچه فرود آمد بر ابراهیم و اسماعیل و اسحق و یعقوب و اسباط و آنچه داده شدند موسی و عیسی و پیمبران از پروردگارشان فرق نگذاریم میان هیچکدام از ایشان و مائیم برای او اسلام‌آرندگان‌'),
(378, 3, 85, 'و هر کس بخواهد جز اسلام دینی را هرگز پذیرفته نشود از او و او است در آخرت از زیانکاران‌'),
(379, 3, 86, 'چگونه هدایت کند خدا قومی را که کافر شدند پس از ایمانشان و گواهی دادند که پیغمبر است حقّ و بیامدشان نشانیها و خدا نیست هدایت‌کننده گروه ستمکاران‌'),
(380, 3, 87, 'آنان را پاداش آن است که بر ایشان است لعنت خدا و فرشتگان و مردم همگی‌'),
(381, 3, 88, 'جاودانند در آن کاسته نشود از ایشان عذاب و نه مهلت داده شوند'),
(382, 3, 89, 'مگر آنان که توبه کردند پس از این و اصلاح کردند که خداوند است آمرزنده مهربان‌'),
(383, 3, 90, 'همانا آنان که کفر ورزیدند پس از ایمانشان سپس فزونی گرفتند در کفر هرگز پذیرفته نشود توبه ایشان و آنانند گمراهان‌'),
(384, 3, 91, 'همانا آنان که کفر ورزیدند و مُردند حالی که کافرند هرگز قبول نشود از یکی از ایشان سراسر زمین آکنده از زر و گرچه فدیه دهد آن را به جای خویش آنان را است عذابی دردناک و نیست برای ایشان یارانی‌'),
(385, 3, 92, 'هرگز به نیکی نرسید تا بدهید از آنچه دوست دارید و آنچه می‌دهید همانا خداوند است بدان دانا'),
(386, 3, 93, 'همه خوراکها حلال بود برای بنی‌اسرائیل جز آنچه حرام کرد اسرائیل بر خویشتن پیش از آنکه نازل شود تورات بگو بیارید تورات را پس بخوانیدش اگر هستید راستگویان‌'),
(387, 3, 94, 'پس آن کس که دروغ بندد بر خدا پس از این آنانند ستمکاران‌'),
(388, 3, 95, 'بگو راست گفته است خدا پس پیروی کنید ملت ابراهیم یکتاپرست را و نبوده است از شرک‌ورزان‌'),
(389, 3, 96, 'همانا نخست خانه که بپا شد برای مردم آن است که در مکّه است فرخنده‌ای و هدایتی برای جهانیان‌'),
(390, 3, 97, 'در آن است آیتهای تابناک مقام ابراهیم و کسی که درآیدش ایمن باشد و خدا را است بر مردم حجّ خانه آن کس که بتواند به سویش راهی و آن کس که کفر ورزد همانا خدا بی‌نیاز است از جهانیان‌'),
(391, 3, 98, 'بگو ای اهل کتاب چرا کفر می‌ورزید به آیتهای خدا و خدا گواه است بر آنچه می‌کنید'),
(392, 3, 99, 'بگو ای اهل کتاب چرا باز می‌دارید از راه خدا آن را که ایمان آورده است خواهیدش کج و شما خود گواهید و نیست خدا غافل از آنچه می‌کنید'),
(393, 3, 100, 'ای گروه مؤمنان اگر فرمان برید گروهی را از آنان که داده شدستند کتاب را برمی‌گردانند شما را پس از ایمانتان کافران‌'),
(394, 3, 101, 'و چگونه کفر ورزید و بر شما خوانده می‌شود آیتهای خدا و در میان شما است فرستاده او و آن کس که نگهداری از خدا خواهد همانا هدایت شده است به سوی راهی راست‌'),
(395, 3, 102, 'ای گروه مؤمنان بترسید خدا را حقّ ترسیدنش و نمیرید جز آنکه باشید مسلمانان‌'),
(396, 3, 103, 'و چنگ زنید به رشته خدا همگی و پراکنده نشوید و یاد آرید نعمت خدا را بر شما هنگامی که بودید دشمنان پس الفت افکند میان دلهای شما تا شدید به نعمت او برادران و بودید بر کنار گودالی از آتش پس رهانید شما را از آن چنین بیان کند خدا برای شما آیتهای خویش را شاید هدایت یابید'),
(397, 3, 104, 'و باید باشد از شما گروهی که دعوت‌کنند به نیکی و امرکنند به خوبی و نهی‌کنند از بدی و آنانند رستگاران‌'),
(398, 3, 105, 'و نباشید مانند آنان که پراکنده شدند و اختلاف کردند پس از آنکه بیامدشان نشانیها و از برای آنان است عذابی سخت‌'),
(399, 3, 106, 'روزی که سفید شوند رویهائی و سیاه شوند رویهائی پس آنان که سیاه شده است رویهای ایشان آیا کفر ورزیدید پس از ایمان خود پس بچشید عذاب را بدانچه بودید کفر می‌ورزیدید'),
(400, 3, 107, 'و اما آنان که سفید شده است رویهای ایشان پس در رحمت خداوندند ایشانند در آن جاودانان‌'),
(401, 3, 108, 'این است آیتهای خدا که خوانیمش بر تو به حقّ و نیست خدا خواهان ستمی بر جهانیان‌'),
(402, 3, 109, 'و برای خدا است آنچه در آسمانها و آنچه در زمین است و به سوی خدا بازگردانیده شوند امور'),
(403, 3, 110, 'بودید بهترین امتی که برون آورده شدید برای مردم امر می‌کنید به نیکی و نهی می‌کنید از زشتی و ایمان می‌آورید به خدا و اگر ایمان می‌آوردند اهل کتاب همانا بهتر بود ایشان را از ایشانند ایمان آرندگانی و بیشتر ایشانند نافرمانان‌'),
(404, 3, 111, 'هرگز به شما آسیبی نرسانند جز آزاری و اگر جنگ‌کنند با شما پشت به سوی شماکنند و سپس یاری نشوند'),
(405, 3, 112, 'زده شد بر ایشان خواری هر کجا یافت شوند مگر با رشته‌ای از خدا و رشته‌ای از مردم و گرفتار شدند به خشمی از خدا یا آوردند خشمی از خدا و افکنده شد بر ایشان پریشانی این بدان بود که کفر می‌ورزیدند به آیتهای خدا و می‌کشتند پیمبران را به ناحق این بدان شد که عصیان ورزیدند و بودند تجاوز کنندگان‌'),
(406, 3, 113, 'نیستند یکسان از اهل کتابند گروهی ایستاده نمازگزار می‌خوانند آیتهای خدا را اثنار شب و ایشانند سجده‌کنندگان‌'),
(407, 3, 114, 'ایمان آورند به خدا و روز آخر و امر می‌کنند به معروف و نهی می‌کنند از منکر و بر همدیگر پیشی می‌گیرند در شتافتن به سوی خوبیها و آنانند از شایستگان‌'),
(408, 3, 115, 'و آنچه‌کنند از نیکی ناسپاس نمانند بدان و خدا دانا است به پرهیزکاران‌'),
(409, 3, 116, 'همانا آنان که کفر ورزیدند بی‌نیازشان نکند اموالشان و نه اولادشان از خدا به چیزی و آنانند یاران آتش در آنند جاودانان‌'),
(410, 3, 117, 'مثل آنچه انفاق‌کنند در این زندگانی دنیا مانند بادی است که در آن است سرمائی سخت وزیده باشد به کشتزار گروهی که خویشتن را ستم کردند پس نابودش ساخته باشد و خدا ستم بر آنان نکرد و لیکن بودند خویشتن را ستم‌کنندگان‌'),
(411, 3, 118, 'ای گروه مؤمنان نگیرید همدستانی جز از خویش اطرافیان از غیر از خویش انتخاب نکنید که در آشفتگی شما فروگزار نکنند آنان دوست داشتند رنج شما را همانا کینه از دهانهای آنان هویدا است و آنچه نهان کند سینه‌های ایشان بزرگتر است همانا بیان کردیم آیتها را برای شما اگر تعقل کنید'),
(412, 3, 119, 'اینک شما دوست دارید ایشان را و دوست ندارند شما را و ایمان می‌آورید به کتاب همه آن و هرگاه ملاقات‌کنند شما را گویند ایمان آوردیم و هرگاه به خلوت روند سرانگشت خشم بر شما به دندان گزند بگو بمیرید به خشم خود همانا خدا دانا است بدانچه در سینه‌ها است‌'),
(413, 3, 120, 'اگر به شما خوبیی رسد ناخوش دارندش و اگر بدی به شما رسد شاد شوند بدان و اگر شکیبا شوید و پرهیزکاری کنید آیان نرساند نیرنگ ایشان شما را به چیزی همانا خدا است بدانچه‌کنند فراگیرنده‌'),
(414, 3, 121, 'و هنگامی که بامداد کردی از نزد خاندان خویش برون شدی و مؤمنان را در مواضع خود جای می‌دادی برای جنگ و خدا است شنوای دانا'),
(415, 3, 122, 'هنگامی که آهنگ کردند دو گروه از شما که سست شوند و خدا است سرپرست آنان و بر خدا باید توکل‌کننده مؤمنان‌'),
(416, 3, 123, 'و همانا یاری کرد شما را خدا در بدر حالی که خوار بودید پس بترسید خدا را شاید شکر گزارید'),
(417, 3, 124, 'هنگامی که می‌گفتی به مؤمنان آیا بس نیست شما را که کمک فرستد شما را پروردگار شما به سه هزار فرشته فرودآوردگان‌'),
(418, 3, 125, 'بلکه اگر بردباری گزینید و پرهیزکاری کنید و بر شما درآیند به زودی هم اکنون کمک فرستد شما را پروردگار شما به پنج هزار تن از فرشتگان نشان دارندگان‌'),
(419, 3, 126, 'و نگردانید آن را خدا جز بشارتی برای شما و تا آرام گیرد دلهای شما بدان حالی که نیست یاری پیروزی جز از نزد پروردگار عزتمند حکیم‌'),
(420, 3, 127, 'تا ببرد دنباله آنان را که کفر ورزیدند یا سرکوبشان سازد تا بازگردند زیانکاران‌'),
(421, 3, 128, 'نیست تو را از امر چیزی یا بپذیرد توبه آنان را یا عذابشان کند آنانند ستمگران‌'),
(422, 3, 129, 'خدای را است آنچه در آسمانها است و آنچه در زمین بیامرزد هر که را خواهد و عذاب کند هر که را خواهد و خدا است آمرزنده مهربان‌'),
(423, 3, 130, 'ای آنان که ایمان آوردید نخورید ربا را سودهائی چند برابر و بترسید خدا را شاید رستگار شوید'),
(424, 3, 131, 'و بترسید آتشی را که آماده شده است برای کافران‌'),
(425, 3, 132, 'و فرمان برید خدا و رسول را شاید رحم شوید'),
(426, 3, 133, 'و بشتابید به سوی آمرزشی از پروردگار خود و بهشتی که فراخیش پهنای آسمانها و زمین است آماده است برای پرهیزکاران‌'),
(427, 3, 134, 'آنان که انفاق می‌کنند مال خود را هنگام گشایش و تنگدستی و خشم فروخورندگان و گذشت کنندگان از مردم و خدا دوست دارد نکوکاران را'),
(428, 3, 135, 'و آنان که هرگاه فحشائی کنند یا بر خود ستم کنند یاد خدا کنند و آمرزش گناهان خویش خواهند و کیست که بیامرزد گناهان را جز خدا و اصرار نکنند بر آنچه کرده‌اند و خود می‌دانند'),
(429, 3, 136, 'آنان را پاداش آمرزشی است از پروردگارشان و باغهائی که روان است زیر آنها جویها جاودانند در آنها و چه نیکو است پاداش عمل کنندگان‌'),
(430, 3, 137, 'همانا بگذشت پیش از شما ملتهائی پس بگردید در زمین و بنگرید چگونه بود فرجام ناباوران تکذیب کنندگان‌'),
(431, 3, 138, 'این بیانی است برای مردم و راهنمائی و اندرزی برای پرهیزکاران‌'),
(432, 3, 139, 'سست نشوید و اندوهگین نباشید و شما برترید اگر هستید مؤمنان‌'),
(433, 3, 140, 'اگر شما را گزندی رسد همانا دشمن را رسیده است گزندی همانند آن و این روزگار می‌گردانیمش میان مردم و تا خدا بداند آنان را که ایمان آوردند و تا بگیرد از شما گواهانی و خدا دوست ندارد ستمگران را'),
(434, 3, 141, 'و تا پاک سازد خدا مؤمنان را و تباه گرداند کافران را'),
(435, 3, 142, 'یا پنداشته‌اید که به بهشت درمی آیید و هنوز ندانسته است خدا آنان را که کوشیدند جهاد کردند از شما و نشناخته است بردباران را'),
(436, 3, 143, 'و همانا شما بودید که آرزوی مرگ می‌کردید پیش از آنکه بدان رسید اکنون دیدید آن را و شمائید نگران‌'),
(437, 3, 144, 'و نیست محمد «ص» مگر پیمبری که بگذشته است پیش از او پیمبرانی پس آیا اگر بمیرد یا کشته شود بازگردید بر پاشنه‌های خویش و آن کس که برگردد بر پاشنه‌های خود همانا آیان نرساند خدا را به چیزی و به زودی پاداش دهد خدا به شکرگزاران‌'),
(438, 3, 145, 'و نرسیده است کسی را که بمیرد مگر به اذن خدا نامه سرآمددار و هر کس خواهد پاداش دنیا دهیمش از آن و هر که پاداش آخرت خواهد دهیمش از آن و زود است پاداش دهیم به شکرگزاران‌'),
(439, 3, 146, 'و چه بسا پیمبری که همراهش کارزار کردند گروندگانی بسیار پس نه سست شدند از آنچه بدیشان رسید در راه خدا و نه ناتوان شدند و نه سرافکندگی به خود گرفتند و خدا است دوست دار پایداری کنان‌'),
(440, 3, 147, 'و نبود سخن ایشان جز آنکه گفتند پروردگارا بیامرز از برای ما گناهان ما و فزون رفتن ما را در کار خویش و استوار دار پایهای ما را و یاری کن ما را بر گروه کافران‌'),
(441, 3, 148, 'پس بدیشان داد خدا اجر دنیا و پاداش نکوی آخرت را و خدا است دوستدار نکوکاران‌'),
(442, 3, 149, 'ای گروه مؤمنان اگر اطاعت کنید آنان را که کفر ورزیدند برگردانند شما را بر پاشنه‌های شما پس بازمی گردید زیانکاران‌'),
(443, 3, 150, 'بلکه خدا است سرپرست شما و او است بهترین یاری کنندگان‌'),
(444, 3, 151, 'بزودی افکنیم در دلهای آنان که کفر ورزیدند هراس را بدانچه شریک گردانیدند با خدا آن را که فرو نفرستاده است بدان فرمانروائی و جایگاه ایشان آتش است و چه زشت است جایگاه ستمکاران‌'),
(445, 3, 152, 'و همانا راست آورد بر شما خدا وعده خویش را هنگامی که به سختی می‌کشتید آنان را به اذن او تا گاهی که از هم گسیختید و با هم ستیزه کردید در کار و نافرمانی کردید پس از آنکه بنمود به شما آنچه دوست می‌داشتید از شما گروهی خواهان دنیا بودند و گروهی خواهان آخرت سپس منصرف ساخت شما را از ایشان تا بیازمایدتان و هرآینه بخشود بر شما و خدا است دارنده فضلی بر مؤمنان‌'),
(446, 3, 153, 'هنگامی که بالا می‌رفتید و برنمی گشتید به سوی کسی و پیمبر می‌خواند شما را از پشت سرتان پس رسانید به شما اندوهی بر اندوهی تا اندوهگین نشوید بر آنچه از شما فوت شده است و نه بدانچه به شما رسیده است و خدا دانا است بدانچه می‌کنید'),
(447, 3, 154, 'سپس فرود آورد بر شما پس از اندوه برای آرامش خماری را گیجی را که فرامی گرفت گروهی از شما را و گروهی دیگر را سرگرم داشته بود جانهای ایشان گمان می‌بردند به خدا ناروا را گمان جاهلیت می‌گفتند آیا ما را است از امر چیزی بگو همانا امر همه آن از آن خدا است نهان می‌داشتند در دلهای خود آنچه را آشکار نمی‌کردند برای تو می گفتند اگر ما را از امر چیزی می‌بود کشته نمی‌شدیم در اینجا بگو اگر می‌بودید در خانه‌های خویش همانا برون می‌آمدند آنان که نوشته شده بود بر ایشان کشته شدن به سوی آرامگاه‌های خود و تا بیازماید خدا آنچه را در سینه‌های شما است و تا پاک کند آنچه را در دلهای شما است و خدا دانا است بدانچه در سینه‌ها است‌'),
(448, 3, 155, 'همانا آنان که پشت کردند از شما روز تلاقی دو سپاه نلغزایندشان شیطان جز به پاره آنچه خود فراهم کردند و هرآینه بخشود خدا بر ایشان همانا خدا است آمرزنده بردبار'),
(449, 3, 156, 'ای گروه مؤمنان نباشید مانند آنان که کفر ورزیدند و گفتند به برادران خویش هنگامی که به سفر می‌رفتند یا به جنگ می‌شدند که اگر می‌ماندند نزد ما نمی‌مردند و نه کشته می‌شدند تا قرار دهد خدا آن را حسرتی در دلهای ایشان و خدا زنده می‌کند و می‌میراند و خدا بدانچه می‌کنید بینا است‌'),
(450, 3, 157, 'و اگر کشته شوید در راه خدا یا بمیرید همانا آمرزشی از خدا و رحمتی بهتر است از آنچه گرد می‌آورند'),
(451, 3, 158, 'و اگر بمیرید یا کشته شوید همانا به سوی خدا گرد آورده می‌شوید'),
(452, 3, 159, 'و هرآینه با مهری از خدا نرم گشته‌ای برای ایشان و اگر تندخوئی سنگین دل می‌بودی همانا پراکنده می‌شدند از پیرامونت پس ببخش بر ایشان و آمرزش خواه برای ایشان و مشورت کن با ایشان در کار سپس گاهی که آهنگ کردی پس توکل کن بر خدا که خدا است دوستدار توکل کنندگان‌'),
(453, 3, 160, 'اگر یاری کند شما را خدا نیست چیره شونده‌ای بر شما و اگر خوارتان دارد پس کیست که یاری کند شما را پس از او و بر خدا باید توکل کنند مؤمنان‌'),
(454, 3, 161, 'و نرسد یا نبوده است پیمبری را که خیانت ورزد در بهره‌های جنگ و هر کس خیانت کند بیاید با آنچه خیانت کرده است روز قیامت سپس سر به سر پرداخته شود به هر کس آنچه را فراهم کرده است و آنان ستم نشوند'),
(455, 3, 162, 'آیا کسی که پیرو خوشنودی خدا است مانند کسی است که بیانگیزد خشم خدا را و جایش دوزخ است و چه بد بازگشتگاهی است‌'),
(456, 3, 163, 'آنانند بر پایه هائی نزد خدا و خدا بینا است بدانچه می‌کنند'),
(457, 3, 164, 'همانا منت نهاد خدا بر مؤمنان هنگامی که برانگیخت در ایشان فرستاده‌ای پیمبری از خود ایشان تا بخواند بر ایشان آیتهای او را و پاک سازد ایشان را و بیاموزدشان کتاب و حکمت را و اگر چه بودند پیش از آن در گمراهیی آشکار'),
(458, 3, 165, 'آیا چنین است که هرگاه برسد شما را پیش آمدی که شما رسانده باشید دوبرابر آن را گوئید از کجاست این بگو آن از نزد خود شما است همانا خدا بر همه چیز است توانا'),
(459, 3, 166, 'و آنچه به شما رسید روز تلاقی دو سپاه همانا به اذن خدا بود و تا بشناسد مؤمنان را'),
(460, 3, 167, 'و بشناسد آنان را که نفاق ورزیدند و چون بدیشان گفته شد که بیائید پیکار کنید در راه خدا یا دفاع کنید گفتند اگر پیکاری می‌دانستیم پیروی می‌کردیم شما را آنان به کفر نزدیکترند در آن روز از ایمان گویند با دهانهای خویش آنچه را نیست در دلهاشان و خدا داناتر است بدانچه نهان می‌دارند'),
(461, 3, 168, 'آنان که گفتند به برادرانشان و بازنشستند که اگر فرمان ما را می‌بردند کشته نمی‌شدند بگو پس دور کنید از خویشتن مرگ را اگر هستید راستگویان‌'),
(462, 3, 169, 'و نپندارید آنان را که کشته شدند در راه خدا مردگان بلکه زندگانند نزد پروردگار خویش روزیمندان‌'),
(463, 3, 170, 'شادمانند بدانچه خدا از فضل خود بدیشان ارزانی داشته و به خود مژده دهند بدانان که هنوز بدیشان نپیوسته‌اند از پشت سر ایشان که نه بیمی بر ایشان است و نه اندوهگین شوند'),
(464, 3, 171, 'شادمانی کنند به نعمتی از خدا و فضلی و آنکه خدا تباه نمی‌کند پاداش مؤمنان را'),
(465, 3, 172, 'آنان که دعوت خدا و رسول را اجابت کردند از پس آنچه زخم بدیشان رسید برای آنان که نکوکاری و پرهیزکاری کردند از ایشان است پاداشی بزرگ‌'),
(466, 3, 173, 'آنان که گفتند بدیشان مردم که مردم بسیج کردند برای شما پس بترسیدشان پس بیفزود آنان را ایمان و گفتند بس است ما را خدا و چه خوب کارگزاری است‌'),
(467, 3, 174, 'پس بازگشتند به نعمتی از خدا و فضلی نرسیدشان گزندی و پیروی کردند خوشنودی خدا را و خدا است دارای فضلی بزرگ‌'),
(468, 3, 175, 'نیست آن جز شیطان که می‌ترساند دوستان خویش را پس نترسید او را و مرا بترسید اگر هستید مؤمنان‌'),
(469, 3, 176, 'اندوهگینت نکنند آنان که می‌شتابند در کفر هرگز زیان نرسانند خدا را به چیزی خدا خواهد که نگذارد برای ایشان بهره‌ای در آخرت و برای ایشان است عذابی بزرگ‌'),
(470, 3, 177, 'همانا آنان که خریدند کفر را به ایمان زیان نرسانند خدا را به چیزی و ایشان را است عذابی دردناک‌'),
(471, 3, 178, 'و نپندارند آنان که کفر ورزیدند مهلت‌دادن ما را بدانان نکوییی برای ایشان جز این نیست که مهلت دهیمشان تا بیفزایند در گناه و ایشان را است عذابی خوارکننده‌'),
(472, 3, 179, 'نیست خدا که بازگذارد مؤمنان را بر آنچه شما برآنید تا جدا گرداند پلید را از پاکیزه (زشت از زیبا) و نبوده است خدا که آگاه سازد شما را بر ناپیدا و لکن خدا انتخاب می‌کند از فرستادگان خویش هر که را خواهد پس ایمان آرید به خدا و فرستادگانش و اگر ایمان آرید و پرهیزکاری کنید شما را هست پاداشی بزرگ‌'),
(473, 3, 180, 'و نپندارند آنان که بخل ورزیدند بدانچه خدا از فضل خویش بدیشان ارزانی داشته آن را بهتر برای ایشان بلکه آن بدتر است برای ایشان بزودی گردن‌بندشان شود آنچه خودداری از دادن آن کردند روز قیامت و خدا را است میراث آسمانها و زمین و خدا بدانچه می‌کنید دانا است‌'),
(474, 3, 181, 'همانا شنید خدا گفتار آنان را که گفتند خدا نیازمند است و ما بی‌نیاز بزودی بنویسیم آنچه را که گفتند و کشتار ایشان پیمبران را به ناحقّ و گوئیم بچشید عذاب سوزان را'),
(475, 3, 182, 'این بدان است که پیش فرستاد دستهای شما و نیست خدا ستم‌کننده بر بندگان‌'),
(476, 3, 183, 'آنان که گفتند همانا خدا با ما پیمان بسته است که ایمان نیاریم برای پیمبری تا بیاورد ما را به قربانی که بخوردش آتش بگو همانا آمد شما را پیمبرانی پیش از من به نشانیها و بدانچه گفتید پس چرا کشتید ایشان را اگر هستید راستگویان‌'),
(477, 3, 184, 'پس اگر باورت نداشتند همانا باور نداشتند فرستادگانی را پیش از تو که آمدند به نشانیها و نوشته‌ها و نامه‌ای درخشان‌'),
(478, 3, 185, 'همه کس چشنده مرگ است و جز این نیست که به شما پرداخته شود مزدهای شما روز قیامت پس کسی که برکنار شد از آتش و برده شد به بهشت همانا رستگار شد و نیست زندگانی دنیا جز کالای فریب (سرگرمی)'),
(479, 3, 186, 'همانا خواهید آزموده شد در مالها و جانهای خویش و خواهید شنید از آنان که داده شدند کتاب را پیش از شما و از شرک‌ورزان آزار فراوانی و اگر شکیبا شوید و پرهیزکاری کنید همانا آن است از عزیمت کارها'),
(480, 3, 187, 'و هنگامی که گرفت خدا پیمان آنان که داده شدند کتاب را که بیان کنندش برای مردم و کتمانش نکنند پس افکندند آن را پشت سر خویش و فروختندش به بهائی کم و چه بد است آنچه بها ستانند (یا فروشند)'),
(481, 3, 188, 'مپندار آنان را که شاد شوند بدانچه آوردند و دوست دارند که ستوده شوند بدانچه نکردند مپندار ایشان را بر رستنگاهی از عذاب و آنان را است عذابی دردناک‌'),
(482, 3, 189, 'و خدا را است فرمانروائی آسمانها و زمین و خدا بر هر چیز توانا است‌'),
(483, 3, 190, 'همانا در آفرینش آسمانها و زمین و گردش شب و روز نشانیهائی است برای خردمندان‌'),
(484, 3, 191, 'آنان که یاد خدا کنند ایستادگان و نشستگان و بر پهلوهای خویش و می‌اندیشند در آفرینش آسمانها و زمین پروردگارا نیافریدی این را به باطل (بیهوده) دور از تو (منزهی تو) نگهدار ما را از عذاب آتش‌'),
(485, 3, 192, 'پروردگارا هر که را در آتش بری همانا خوارش کرده‌ای و نیست ستمگران را یارانی‌'),
(486, 3, 193, 'پروردگارا همانا ما شنیدیم بانگ‌دهنده‌ای را که بانگ می‌داد برای ایمان که ایمان آرید به پروردگار خویش پس ایمان آوردیم پروردگارا بیامرز برای ما گناهان ما را و بزدای از ما زشتیهای ما را و دریاب ما را با نیکان‌'),
(487, 3, 194, 'پروردگارا و ارزانی دار به ما آنچه بر زبان فرستادگانت به ما نوید دادی و خوار مکن ما را روز قیامت که نیستی تو شکننده پیمان‌'),
(488, 3, 195, 'پس پذیرفت از ایشان پروردگارشان که تباه ننمایم کار هر عمل‌کننده‌ای را از شما مرد یا آن بعضیتان از بعضی پس آنان که هجرت کردند و برون (رانده شدند) از خانه‌های خویش و آزار شدند در راه من و جنگ کردند و کشته شدند همانا بزُدایم از ایشان زشتیهای ایشان را و درآرمشان باغهائی که روان است از زیر آنها جویها پاداشی از نزد خدا و خدا نزد او است پاداش نیکو'),
(489, 3, 196, 'فریفته‌ات نکند گردش آنان که کفر ورزیدند در شهرها'),
(490, 3, 197, 'بهره‌ای است اندک و سپس جایگاه ایشان دوزخ است و چه زشت آرامشگاهی است‌'),
(491, 3, 198, 'لیکن آنان که پروردگار خود را ترسیدند برای ایشان است باغهائی که جاری است از زیر آنها جویها جاودانان در آنها پیشکشی از نزد خدا و آنچه نزد خدا است بهتر است برای نیکان‌'),
(492, 3, 199, 'همانا از اهل کتاب است آنکه ایمان آورد به خدا و آنچه فرود آورده شد بر شما و آنچه فرود آورده شد بر ایشان فروتنی‌کنان برای خدا نفروشند آیتهای خدا را به بهائی کم آنان را است پاداش ایشان نزد پروردگارشان همانا خدا است شتابنده در حساب‌'),
(493, 3, 200, 'ای آنان که ایمان آوردید بردبار باشید و شکیبائی ورزید و خویش را آماده جنگ نگه دارید و بترسید خدا را شاید رستگار شوید');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'ای مردم بترسید پروردگار خود را که آفریدتان از یک تن و آفرید از او جفتش را و گسترانید از آنان مردانی بسیار و زنانی و بترسید خداوندی را که از او پرسش شوید و رحمها همانا خداوند است بر شما نگهبان‌'),
(495, 4, 2, 'بپردازید به یتیمان اموال ایشان را و زشت را به جای زیبا ندهید و نخورید اموال آنان را با اموال خویش که آن است همانا گناهی بزرگ‌'),
(496, 4, 3, 'و اگر بترسید که دادگری نکنید در یتیمان پس کابین بندید بر آنچه خوش آید شما را (یا پاک باشد برای شما) از زنان دو تا و سه تا و چهار تا و اگر ترسیدید که دادگری نکنید پس یکی را یا آنچه ملک یمین شما باشد این است نزدیکتر بدانکه ستم نکنید (یا کجروی نکنید)'),
(497, 4, 4, 'و دهید به زنان تصدّقهای (کابینهای) آنان را بخششی و اگر به دلخواه خویش گذشتند از چیزی از آن برای شما پس بخوریدش گوارا نوش باد'),
(498, 4, 5, 'و ندهید به کم‌خردان اموالتان را که آن را خدا پایداری شما گردانیده است و لیکن خوراک و پوشاک دهیدشان از آن و بگوئید بدیشان گفتاری نیک‌'),
(499, 4, 6, 'و بیازمائید یتیمان را تا گاهی که به زناشوئی رسیدند پس اگر یافتید از ایشان خردمندیی پس بپردازید بدیشان اموال ایشان را و نخورید آنها را به اسراف (به بیهوده و گزاف در هزینه) و پیشی جستن که نبادا بزرگ شوند و آن که بی‌نیاز است خودداری کند و آن که نیازمند است باید بخورد به متعارف و هرگاه دادید بدیشان اموالشان را پس گواه گیرید بر ایشان و بس است خدا حسابداری‌'),
(500, 4, 7, 'مردان را است بهره‌ای از آنچه بازگذاردند پدر و مادر و نزدیکان و زنان را است بهره‌ای از آنچه بازگذاردند پدر و مادر و نزدیکان از کم یا بیش آن بهره‌ای بایسته و مشخص‌'),
(501, 4, 8, 'و اگر حاضر شدند قسمت را نزدیکان و یتیمان و بینوایان پس بخورانیدشان از آن و بگوئید بدیشان گفتاری نکو'),
(502, 4, 9, 'و باید بترسند آنان که اگر بازگذارند پس از خود فرزندان ناتوانی می‌ترسند بر ایشان پس بترسند خدا را و بگویند سخنی استوار'),
(503, 4, 10, 'همانا آنان که می‌خورند اموال یتیمان را به ستم جز این نیست که می‌خورند در شکمهای خویش آتش را و بزودی درآیند در آتش افروخته‌'),
(504, 4, 11, 'دستور دهد شما را خدا در باره فرزندان شما که برای نرینه است مانند بهره دو مادینه پس اگر زنانی باشند بیش از دو تن برای ایشان است دو ثُلث آنچه بازگذارده و اگر یک آن باشد پس برای او است نیم و برای پدر و مادرش هر کدام از آنان است شش یک از آنچه بازگذارده اگر وی را فرزندی باشد و اگر وی را فرزندی نباشد و ارث برد از او پدر و مادرش پس برای مادرش سه یک است و اگر وی را برادرانی باشند پس مادرش را شش یک است پس از وصیّتی که کرده باشد یا وامی پدران شما و فرزندان شما ندانید کدامین نزدیکترند به شما در سودمندی بایسته است از خدا همانا خداوند است دانای حکیم‌'),
(505, 4, 12, 'و شما را است نیمی از آنچه بازگذاردند همسران شما اگر نباشد ایشان را فرزندی و اگر بود ایشان را فرزندی پس برای شما است چهار یک از آنچه بازگذاردند پس از وصیّتی که کرده باشند یا وامی و ایشان را است چهار یک از آنچه بازگذاردید اگر نباشد شما را فرزند و اگر شما را فرزند باشد پس ایشان را است هشت یک از آنچه بازگذارید پس از وصیّتی که کنید یا وامی و اگر مردی باشد که ارث برده شود به کلاله (برادران و خواهران) یا زنی باشد که وی را برادر یا خواهری باشد پس برای هر کدام است شش یک و اگر باشند بیشتر از این پس آنانند شریکانی در ثلث پس از وصیّتی که کرده شود و یا وامی نازیان آورنده دستوری است از خدا و خدا است دانای بردبار'),
(506, 4, 13, 'این است مرزهای خدا و آن کس که اطاعت کند خدا و پیمبرش را درآردش به باغهائی که جاری است از زیر آنها جویها جاودانان در آن و این است کامیابی بزرگ‌'),
(507, 4, 14, 'و آن کس نافرمانی خدا و پیمبرش کند و بگذرد از مرزهای او فروبردش در آتشی جاودان ماند در آن و برای او است عذابی خوارکننده‌'),
(508, 4, 15, 'و آنان که فحشائی آرند از زنان شما پس گواه گیرید بر آنان چهار تن از خود شما پس اگر گواهی دادند نگهدارید ایشان را (بازداشت کنیدشان) در خانه‌ها تا برسدشان مرگ یا بگذارد خدا برای ایشان راهی‌'),
(509, 4, 16, 'و آن دو مرد از شما که فحشائی آرند آزارشان کنید پس اگر توبه کردند و به شایستگی آمدند بگذرید از ایشان همانا خدا است بسی توبه‌پذیرنده مهربان‌'),
(510, 4, 17, 'جز این نیست که بر خدا پذیرفتن توبه آنانی است که به نادانی کرداری زشت کنند و سپس بزودی توبه کنند آنانند که بپذیرد خدا توبه ایشان را و خدا است دانای حکیم‌'),
(511, 4, 18, 'و نیست توبه برای آنان که کردار زشت کنند تا گاهی که یکی از ایشان را مرگ فرارسد گوید توبه کردم اکنون و نه آنان که می‌میرند و ایشانند کافران آنان را آماده کردیم برای ایشان عذابی دردناک‌'),
(512, 4, 19, 'ای آنان که ایمان آوردید روا نیست شما را که ارث برید زنان را به ناخواه و بازشان ندارید از زناشوئی که ببرید پاره آنچه را بدیشان دادید مگر آنکه بیارند فحشائی آشکار و زندگانی کنید با ایشان به خوبی و اگر خوش نداشتید آنان را چو بسا چیزی را ناخوش دارید و قرار دهد خداوند در آن خیری بسیار'),
(513, 4, 20, 'و اگر خواستید بگزینید همسری به جای همسری گزینید و دادید یکی از ایشان را پوست گاوی آکنده از زر یا سیم نگیرید از او چیزی آیا می‌گیریدش به دروغ بستن و گناهی آشکار'),
(514, 4, 21, 'و چگونه می‌گیریدش و حالی که رسانده‌اند بعضی از شما به بعضی و گرفته‌اند از شما پیمانی سخت‌'),
(515, 4, 22, 'و همسر نگیرید آنان را که پدران شما همسر گرفتند از زنان مگر آنچه گذشته است که آن است فحشائی و خشمی و چه زشت راهی است‌'),
(516, 4, 23, 'حرام شده است بر شما مادران شما و دختران شما و خواهران شما و عمّه‌های شما و خاله‌های شما و دختران برادر و دختران خواهر و مادران شما آنان که شیرتان دادند و خواهران شما در شیر و مادران زنان شما و دخترانی که در دامان شما هستند از زنانی که همبستر شده‌اید با آنان پس اگر همبستر نشده باشید با آنان نیست باکی بر شما و همسران فرزندان شما آنان که از صلبهای (کمرهای) شما هستند و آنکه جمع کنید میان دو خواهر جز آنچه گذشته است همانا خداوند است آمرزنده مهربان‌'),
(517, 4, 24, 'و زنان شوهردار مگر آنچه از آن شما است به ملک یمین کتاب خدا است بر شما و حلال شد برای شما جز اینان که به مالهای خویش خواستگاری کنید حالی که شوهران باشید نه زناکاران پس هرچند کامیاب شدید از ایشان بدهید بدیشان مزدهای ایشان را به واجب و نیست باکی بر شما در آنچه تراضی کنید بدان پس از واجب همانا خدا است دانای حکیم‌'),
(518, 4, 25, 'و آن کس که توانائی مالی ندارد از شما که کابین کند زنان آزاد با ایمان را پس از آنچه در ملک یمین شما است از کنیزکان با ایمان و خدا داناتر است به ایمان شما یک به یک (یا بعضی از شما از بعضی است) پس ازدواج کنید با آنان به اذن خانواده‌های آنان و بدیشان دهید مزدهای ایشان را به خوبی حالی که خویشتن نگهدارندگانند نه زناکاران و نه یارگیرندگان پس گاهی که شوی کردند اگر فحشائی آوردند بر ایشان است نصف آنچه بر زنان آزاد است از شکنجه این برای کسی است از شما که رنج عزوبت را ترسد و صبرکردن بهتر است برای شما و خدا است آمرزنده مهربان‌'),
(519, 4, 26, 'خدا خواهد بیان کند برای شما و هدایت کند شما را به راه‌های آنان که پیش از شما بودند و بگذرد از شما و خدا است دانای حکیم‌'),
(520, 4, 27, 'خدا خواهد که بپذیرد توبه شما را و خواهند آنان که پیروی شهوات کنند که منحرف شوید انحرافی بزرگ‌'),
(521, 4, 28, 'خدا خواهد سبک کند از شما و آفریده شده است انسان ناتوان‌'),
(522, 4, 29, 'ای آنان که ایمان آوردید نخورید اموال خویش را میان خویش به ناحقّ مگر آنکه داد و ستدی باشد بر تراضی شما و نکُشید همدیگر را همانا خداوند است به شما مهربان‌'),
(523, 4, 30, 'و آن کس که این کار را کند به بیداد و ستم بزودی چشانیمش آتش را و آن است بر خدا آسان‌'),
(524, 4, 31, 'اگر دوری کنید از گرانهای آنچه نهی شوید از آن بستریم از شما گناهان شما را و درآریمتان جایگاهی ارجمند'),
(525, 4, 32, 'و آرزو نکنید آنچه را خداوند فزونی داد (برتری داده است) بدان بعضی از شما را بر بعضی مردان را است بهره‌ای از آنچه کسب کردند و زنان را است بهره‌ای از آنچه کسب کردند و بخواهید خدا را از فضلش همانا خدا است به همه چیز دانا'),
(526, 4, 33, 'و برای هر کدام قرار دادیم نزدیکانی که ارث برند از آنچه به جای گذارند پدر مادر و خویشاوندان و آنان را که پیمان بسته است سوگندهای شما پس بدهید ایشان را بهره ایشان همانا خدا است بر همه چیز گواه‌'),
(527, 4, 34, 'مردان ایستادگانند (سرپرستند) بر زنان بدانچه برتری داد خدا بعضی از ایشان را بر بعضی و بدانچه داده‌اند از اموال خویش پس زنان شایسته فرمانبردارانند (فروتنانند) خویشتن نگهدارندگانند در نهان بدانچه خدا نگهداشته است و آنان را که بیم سرکشی‌شان دارید اندرز دهید و دوری گزینید از ایشان در بسترها و بزنیدشان پس اگر فرمانبرداری کردند شما را نجوئید بر ایشان راهی همانا خداوند است برتر بزرگوار'),
(528, 4, 35, 'و اگر بیم داشتید خلافی میان آنان (زن و شوهر) پس برانگیزید داوری از خاندان مرد و داوری از خاندان زن تا اگر ساز آوردنی خواهند بساز آورد خدا میان ایشان همانا خدا است دانای کارآگاه‌'),
(529, 4, 36, 'و پرستش کنید خدا را و همتا قرار ندهید برای او چیزی و با پدر و مادر نکوئی کنید و با نزدیکان و یتیمان و بینوایان و همسایه خویشاوند و همسایه بیگانه و یار هم پهلو و راهگذر نیازمند و آنکو شما را است به ملک یمین همانا خدا دوست ندارد آن را که او است گردنفراز خودخواه (خودستای)'),
(530, 4, 37, 'آنان که بخل ورزند و مردم را به بخل‌ورزی فرمان دهند و پنهان کنند آنچه را خدا بدیشان داده است از فضل خویش و آماده کردیم برای کافران عذابی خوارکننده‌'),
(531, 4, 38, 'و آنان که دهند اموال خود را برای خودنمائی به مردم و ایمان نیارند به خدا و روز بازپسین و آن کس که شیطانش همنشین باشد چه زشت است همنشینی‌'),
(532, 4, 39, 'و چه چیز بر ایشان بود اگر ایمان می‌آوردند به خدا و روز بازپسین و می‌بخشیدند از آنچه روزی داده است ایشان را خدا و خدا بدیشان است دانا'),
(533, 4, 40, 'همانا خدا ستم نکند به سنگینی ذرّه‌ای و اگر کردار خوبی باشد دو برابرش کند و بیارد از نزد خویش پاداشی بزرگ‌'),
(534, 4, 41, 'پس چگونه است هنگامی که بیاریم از هر گروهی گواهی و بیاریم تو را بر اینان گواه‌'),
(535, 4, 42, 'در آن روز دوست دارند آنان که کفر ورزیدند و نافرمانی کردند پیمبر را کاش یکسان می‌شدند با زمین و نهان نمی‌دارند از خدا داستانی را'),
(536, 4, 43, 'ای آنان که ایمان آوردید نزدیک نشوید به نماز حالی که مست هستید تا آنگاه که بدانید چه می‌گوئید و نه جُنب مگر راهگذری تا آنگاه که غسل کنید و اگر بیمار باشید یا در سفری یا یکی از شما از آب ریز برون آید یا با زنان نزدیکی کنید و نیابید آبی پس آهنگ زمینی پاکیزه کنید و بمالید رویها و دستهای خود را همانا خدا است بخشنده آمرزگار'),
(537, 4, 44, 'آیا ننگریستی بدانان که داده شدند بهره‌ای از کتاب را می‌خرند گمراهی را و خواهند که گم کنید راه را'),
(538, 4, 45, 'و خدا داناتر است به دشمنان شما و بس است خدا سرپرستی و بس است خدا یاوری‌'),
(539, 4, 46, 'از آنان که جهودند گروهی برمی گردانند سخنان را از جایهایش و گویند شنیدیم و نافرمانی کردیم و بشنوان ناشنوائی را و رعایت کن ما را به گردانیدن زبانهای خویش و ستیزگی در دین و اگر آنان می‌گفتند شنیدیم و فرمان بردیم و بشنوان و مهلت ده ما را هرآینه بهتر بود ایشان را و درست‌تر لیکن دورشان کرد خدا به کفرشان پس ایمان نیارند جز اندکی‌'),
(540, 4, 47, 'ای آنان که داده شدید کتاب را ایمان آرید بدانچه فرستادیم تصدیق‌کننده آنچه با شما پیش از آنکه واژگون کنیم رویهائی را پس بازگردانیمشان به سوی پشت یا دور سازیم آنان را چنان که دور ساختیم یاران شنبه را و بوده است کار خدا شدنی‌'),
(541, 4, 48, 'همانا خدا نمی‌آمرزد که شرک بدو ورزیده شود و می‌آمرزد هر چه کمتر از آن باشد برای هر کس که خواهد و هر که شرک ورزد به خدا همانا دروغ بسته است گناهی بزرگ را'),
(542, 4, 49, 'آیا ندیدی آنان را که خویشتن را ستایند (پاک شمرند) بلکه خدا ستوده دارد (پاک کند) هر که را خواهد و ستم نشوند به اندازه رشته‌ای که میان هسته خرما است‌'),
(543, 4, 50, 'بنگر چگونه بر خدا دروغ می‌بندند و بس است آن گناهی آشکار'),
(544, 4, 51, 'آیا نبینی آنان را که داده شدند بهره‌ای را از کتاب ایمان آرند به جبت و طاغوت و گویند بدانان که کفر ورزیدند که اینان رهبرنده‌ترند از آنان که ایمان آوردند راه را'),
(545, 4, 52, 'آنانند که دور کردشان خدا و کسی که دور سازدش خدا نیابی برای او یاوری را'),
(546, 4, 53, 'یا ایشان را است بهره‌ای از فرمانروائی که در آن هنگام ندهند به مردم پشیزی را'),
(547, 4, 54, 'یا رشک برند مردم را بدانچه خدا از فضل خویش بدانان داده است همانا دادیم به خاندان ابراهیم کتاب و حکمت را و دادیم بدیشان پادشاهی بزرگ را'),
(548, 4, 55, 'پس از ایشان است آنکه ایمان بدان آورده است از ایشان است آنکه بازداشته است از آن و بس است دوزخ آتشی سوزان‌'),
(549, 4, 56, 'همانا آنان که کفر ورزیدند به آیتهای ما بزودی چشانیمشان آتشی که هرگاه پخته شود پوستهای ایشان جایگزین گردانیمشان پوستهای دیگری تا بچشند عذاب را همانا خداوند است عزّتمند حکیم‌'),
(550, 4, 57, 'و آنان که ایمان آوردند و کردار شایسته کردند بزودی درآریمشان باغهائی که جاری است از زیر آنها جویها جاودانان در آنها همیشه برای ایشان است در آنها همسرانی پاکیزه و درآریمشان در سایه‌ای سایه افکن‌'),
(551, 4, 58, 'همانا خدا فرمانتان دهد که بازگردانید سپرده‌ها را به اهل آنها و اگر داوری کنید میان مردم آنکه داوری کنید به داد چه خوب است آنچه خدا اندرز دهد شما را بدان همانا خداوند است شنونده‌ای بینا'),
(552, 4, 59, 'ای آنان که ایمان آوردید فرمان برید خدا و رسول را و اولیاء امر (کارداران) را از شما و اگر در چیزی با هم ستیزه کردید به خدا و رسولش بازگردانید اگر هستید ایمان آورنده به خدا و روز آخر این بهتر است و نکوتر در فرجام‌'),
(553, 4, 60, 'آیا ندیدی آنان را که پندارند آنکه ایشان ایمان آورده‌اند بدانچه به سوی تو فرستاده شده است و آنچه پیش از تو فرستاده شده است خواهند داوری برند به سوی ستمگر حالی که امر شدند که کفر ورزند بدو و خواهد شیطان که گمراهشان سازد گمراهیی دور'),
(554, 4, 61, 'و اگر گفته شود بدیشان که بیائید به سوی آنچه خدا فرستاده است و به سوی رسول بنگری منافقان را که بازمی دارند از تو بازداشتنی‌'),
(555, 4, 62, 'پس چگونه است که اگر پیش آمدی بدیشان رسد بدانچه دستهاشان پیش فرستاده است آیند نزد تو سوگند خورند که نخواستیم ما جز نکوکاری و کارساز آوردن را'),
(556, 4, 63, 'آنانند که خدا می‌داند آنچه در دلهای ایشان است پس روی گردان از ایشان و اندرز ده ایشان را و بگو ایشان را به دلهای ایشان گفتاری رسا'),
(557, 4, 64, 'و نفرستادیم پیمبری را مگر تا اطاعت شود به اذن خدا و اگر اینان گاهی که به خود ستم روا می‌داشتند می‌آمدند نزد تو و آمرزش می‌خواستند از خدا و آمرزش می‌خواست برای ایشان پیمبر همانا می‌یافتند خدا را بسی توبه‌پذیرنده مهربان‌'),
(558, 4, 65, 'نه چنین است به پروردگار تو سوگند ایمان نیارند به تو تا به داوری بگزیندت در آنچه سرزده است بینشان و سپس نیابند در دلهای خویش چاره‌ای از آنچه تو قضاوت کرده‌ای و تسلیم شوند تسلیم شدنی‌'),
(559, 4, 66, 'و اگر ما می‌نوشتیم بر ایشان که بکشید همدیگر را یا برون روید از شهرهای خود نمی‌کردندش جز کمی از ایشان و اگر می‌کردند آنچه را پند داده می‌شدند هرآینه برای آنان بهتر بود و سخت استوارتر'),
(560, 4, 67, 'و در آن هنگام می‌دادیمشان از نزد خویش مزدی بزرگ‌'),
(561, 4, 68, 'و رهبریشان می‌کردیم به راهی راست‌'),
(562, 4, 69, 'و آن کس که اطاعت کند خدا و پیمبر را همانا آنان با کسانیند که خدا نعمت بدیشان داده است از پیمبران و راستگویان و کشتگان راه خدا و شایستگان و چه نیکو رفیقانند'),
(563, 4, 70, 'این است فزونی از خدا و بس است خدا دانا'),
(564, 4, 71, 'ای آنان که ایمان آوردید برگیرید اسلحه خویش را پس کوچ کنید دسته دسته یا کوچ کنید با هم‌'),
(565, 4, 72, 'و همانا از شما است آنکه به سختی درنگ‌کاری کند پس اگر پیشامدی به شما رسد گوید خدا به من نعمت داد که نشدم با ایشان کشته‌'),
(566, 4, 73, 'و اگر فضلی از خدا به شما رسد بگوید چنان که تو گوئی هرگز بین تو و او دوستی نبوده کاش با شما بودم تا بهره‌مند می‌شدم به بهره‌ای بزرگ‌'),
(567, 4, 74, 'پس باید نبرد کند در راه خدا آنان را که می‌فروشند زندگانی دنیا را به آخرت و کسی که پیکار کند در راه خدا پس کشته شود یا پیروز گردد بزودی دهیمش پاداشی گران‌'),
(568, 4, 75, 'و چیست شما را که پیکار نمی‌کنید در راه خدا و ناتوانان از مردان و زنان و کودکانی که گویند پروردگارا برون کن ما را از این شهری که ستمگرند مردمش و قرار ده برای ما از نزد خود سرپرستی و قرار ده برای ما از نزد خود یاوری‌'),
(569, 4, 76, 'آنان که ایمان آورده پیکار کنند در راه خدا و آنان که کفر ورزیده‌اند پیکار کنند در راه ستمگر پس پیکار کنید با یاران شیطان همانا نیرنگ شیطان است ناتوان‌'),
(570, 4, 77, 'آیا ننگریستی بدانان که بدیشان گفته شد بازدارید دستهای خود را و بپای دارید نماز را و بدهید زکات را تا گاهی که بر ایشان جنگ نوشته شد ناگهان گروهی از ایشان ترسند مردم را مانند ترسیدن خدا یا سخت‌تر و گفتند پروردگارا چرا جنگ بر ما نوشتی کاش ما را مهلت می‌دادی تا سرآمدی نزدیک بگو کامیابی دنیااست اندک و آخرت بهتر است برای آنکه پرهیزکاری کند و ستم بر شما نشود به مقدار تاری که در هسته خرما است‌'),
(571, 4, 78, 'هرجا باشید دریابد شما را مرگ و اگرچه در دژهای استوار باشید و اگر بدیشان رسد خوشی گویند این از نزد خدا است و اگر بدی بدیشان رسد گویند این از نزد تو است بگو همه از نزد خدا است پس چه شود این گروه را نزدیک نیستند دریابند سخنی را'),
(572, 4, 79, 'آنچه تو را از نکوئی رسد همانا از نزد خدا است و آنچه رسدت از بدی از خویشتن است و فرستادیم تو را برای مردم پیمبری و بس است خداوند گواه‌'),
(573, 4, 80, 'آن کس که اطاعت پیمبر کند خدا را اطاعت کرده است و آن کس که پشت کند همانا نفرستادیمت بر ایشان نگهبان‌'),
(574, 4, 81, 'گویند اطاعت ولی گاهی که برون روند از نزد تو درون پرورانند گروهی از ایشان غیر آنچه را که گویند و خدا می‌نویسد آنچه را نهان می‌دارند پس روی گردان از ایشان و توکل کن بر خدا و بس است خداوند وکیل‌'),
(575, 4, 82, 'آیا تدبّر (بررسی) نمی‌کنند قرآن را و اگر می‌بود از نزد غیر از خدا همانا می‌یافتند در آن اختلافی بسیار'),
(576, 4, 83, 'و هرگاه برسدشان امری درباره امنیّت یا ترس فاش کنندش و اگر بازمی گردانیدند آن را به سوی پیمبر و به سوی اولیاء امر (کارداران) خویش همانا می‌دانستندش آنان که استخراجش می‌کردند از ایشان و اگر نبود فضل خدا و رحمت او بر شما همانا پیروی می‌کردید شیطان را مگر کمی‌'),
(577, 4, 84, 'پس نبرد کن در راه خدا تکلیف نشود جز خودت و برانگیز مؤمنان را امید است خدا بازدارد نیروی آنان را که کفر ورزیدند و خدا است سخت نیروتر و سخت تر به شکنجه‌'),
(578, 4, 85, 'کسی که شفاعت نیکی کند او را است بهره‌ای از آن و کسی که شفاعت بدی کند او را است بهره‌ای از آن و خدا است بر همه چیز نگهبان‌'),
(579, 4, 86, 'و هرگاه به شما درود گفته شود درود گوئید به بهتر از آن یا همان را برگردانید همانا خداوند است بر همه چیز حسابگر'),
(580, 4, 87, 'خدا نیست خداوندی جز او گردآورنده شما است به روز قیامت نیست شکّی در آن و کیست راستگوتر از خدا در سخن‌'),
(581, 4, 88, 'چیست شما را که درباره منافقین بر دو گروهید حالی که برگردانیدستشان بدانچه دست آوردند آیا خواهید هدایت کنید آن را که گمراه کرده است خدا و آن کس که گمراهش کند خدا نیابی برای او راهی‌'),
(582, 4, 89, 'دوست داشتند شما نیز مانند ایشان کافر شوید تا با هم همانند گردید پس نگیرید از ایشان دوستانی تا هجرت کنند در راه خدا و اگر روی گردانیدند بگیریدشان و بکشیدشان هر کجا بیابیدشان و نگیرید از ایشان دوست و نه یاوری‌'),
(583, 4, 90, 'مگر آنان که بپیوندند با گروهی که میان شما و ایشان است پیمانی یا بیایند شما را حالی که تنگ آمده باشد سینه‌های ایشان از آنکه نبرد کنند با شما یا نبرد کنند با قوم خود و اگر می‌خواست خدا همانا مسلّط می‌ساختشان بر شما تا جنگ کنند با شما ولی اگر کناره‌گیری کردند از شما و نبرد نکردند با شما و دست صلح بسوی شما دراز کردند همانا قرار نداده است خدا برای شما بر ایشان راهی‌'),
(584, 4, 91, 'بزودی یابید دیگرانی را که خواهند ایمن باشند از شما و ایمن باشند از قوم خویش هرگاه خوانده شوند بسوی آشوبی بیفتند در آن پس اگر دور نشدند از شما و آشتی نکردند با شما و بازنداشتند دستهای خود را پس بگیرید ایشان را و بکشیدشان هر کجا بیابیدشان و آنان را قرار دادیم برای شما بر ایشان فرمانروائی آشکار'),
(585, 4, 92, 'و نیست برای مؤمنی که بکشد مؤمنی را مگر به خطا و کسی که مؤمنی را به خطا بکشد بر او است آزاد کردن بنده مؤمنی و خونبهائی که پرداخت شود به خاندانش مگر آنکه تصدق کنند و اگر از گروهی باشند که دشمن شما هستند و او مؤمن باشد پس آزاد کردن بنده مؤمنی و اگر از گروهی باشد که میان شما و ایشان است پیمانی پس دیه‌ای پرداخت شده به خویشان او و آزاد ساختن بنده مؤمن و آن کس که ندارد پس روزه دو ماه پی در پی توبه است از نزد خدا و خدا است دانای حکیم‌'),
(586, 4, 93, 'و کسی که بکشد مؤمنی را دانسته و خواسته پس کیفرش دوزخ است جاودان در آن و خشم آورد خدا بر او و دورش کند و آماده کند برای او عذابی بزرگ‌'),
(587, 4, 94, 'ای آنان که ایمان آوردید هرگاه در راه خدا سفر کردید رسیدگی کنید و نگوئید بدان کس که از در زشتی با شما درآمده است نیستی مؤمن خواهید بهره زندگانی دنیا را و نزد خدا است خواسته‌های فراوان چنین بودید پیش از این پس منت نهاد خدا بر شما پس رسیدگی کنید که خداوند است بدانچه می‌کنید آگاه‌'),
(588, 4, 95, 'یکسان نیستند بازنشستگان از مؤمنان که از نه مرد رنج و آسیبند با کوشش گران در راه خدا به مالها و جانهای خویش برتری داده است خدا کوشش‌کنندگان را به مالها و جانهای خود بر بازنشستگان از جنگ در پایه و هر کدام را نوید نکوئی داده است و فزونی داده است خدا رزم دهندگان را بر خانه‌نشینان به پاداشی گران‌'),
(589, 4, 96, 'پایه‌هائی از نزد خود و آمرزش و رحمتی و خداوند است آمرزنده مهربان‌'),
(590, 4, 97, 'همانا آنان که دریابندشان فرشتگان ستم‌کنندگان بر خویشتن گویند در چه بودید گویند بودیم ناتوانانی در زمین گویند آیا نبود زمین خدا پهناور تا هجرت کنید در آن آنان جایگاهشان دوزخ است و چه بد بازگشتگاهی است‌'),
(591, 4, 98, 'مگر ناتوانان از مردان و زنان و کودکانی که نه چاره توانند و نه راه به جائی برند'),
(592, 4, 99, 'که اینان را امید است خدا درگذرد از ایشان و خدا است بخشنده آمرزگار'),
(593, 4, 100, 'و کسی که هجرت کند در راه خدا بیابد در زمین هجرتگاه‌هائی بسیار و گشایشی و کسی که برون آید از خانه خویش حالی که هجرت‌کننده است به سوی خدا و پیمبرش پس دریابدش مرگ همانا مزدش بر خدا افتاد و خدا است آمرزنده مهربان‌'),
(594, 4, 101, 'و هرگاه سفر کنید باکی نیست بر شما که کوتاه کنید نماز را اگر بترسید که بتازند بر شما آنان که کفر ورزیدند زیرا کافرانند برای شما دشمنی آشکار'),
(595, 4, 102, 'و اگر در میان ایشان بودی و بپای داشتی برای ایشان نماز را پس بایستد گروهی از ایشان با تو و برگیرند اسلحه خود را تا هرگاه به سجده روند باشند پشت سر شما و بیایند گروهی دیگر که نماز نگزارده‌اند پس نماز بگزارند با تو و بردارند با خود اسلحه و آمادگی خویش را دوست داشتند آنان که کفر ورزیدند کاش غفلت می‌کردید از خواسته‌ها و اسلحه خود تا بتازند بر شما به یک تاختن و باکی نیست بر شما اگر شما را آزاری از باران باشد یا بیمار باشید که بگذارید سلاحهای خود را و برگیرید بیمناکی و آمادگی خویش را همانا خدا آماده کرده است برای کافران عذابی خوارکننده را'),
(596, 4, 103, 'پس گاهی که به جای آوردید نماز را یاد کنید خدا را ایستادگان و نشستگان و بر پهلوهای خویش سپس گاهی که دل آسوده شدید به پا بدارید نماز را همانا نماز است بر مؤمنان نوشته بایسته‌'),
(597, 4, 104, 'سستی مورزید در جستجوی دشمن اگر رنج می‌کشید همانا رنج می‌برند ایشان چنانکه رنج می‌برید شما و امید دارید از خدا آنچه را امید ندارند و خدا است دانای حکیم‌'),
(598, 4, 105, 'همانا فرستادیم کتاب را به سوی تو به حقّ تا حکومت کنی میان مردم بدانچه نمایاند تو را خدا و نباش برای خیانتکاران ستیزه‌گری‌'),
(599, 4, 106, 'و آمرزش خواه از خدا که خدا است آمرزنده مهربان‌'),
(600, 4, 107, 'و ستیزه مکن از آنان که با خود خیانت می‌ورزند زیرا خدا دوست ندارد آن را که خیانت‌کننده گنهکار است‌'),
(601, 4, 108, 'نهفتگی جویند از مردم و نهفتگی نمی‌جویند از خدا حال آنکه او با ایشان است هنگامی که درون می‌پرورانند آنچه را خوش ندارد از گفتار و خدا است بدانچه می‌کنند فراگیرنده‌'),
(602, 4, 109, 'اینک شما از ایشان دفاع کردید در زندگانی دنیا پس کیست تا خدا را دفاع کند از ایشان روز قیامت یا کیست که باشد بر ایشان وکیل‌'),
(603, 4, 110, 'و کسی که به کاری زشت بپردازد یا ستم کند بر خویشتن و سپس از خدا آمرزش خواهد بیابد خداوند را آمرزنده مهربان‌'),
(604, 4, 111, 'و کسی که گنهی فراهم آورد همانا بر خویشتن آورده است و خدا است دانای حکیم‌'),
(605, 4, 112, 'و کسی که فراهم کند لغزش یا گنهی را سپس بسوی کسی که بیزار باشد افکندش همانا بار کرده است بر خود تهمتی و گناهی آشکار را'),
(606, 4, 113, 'و اگر نبود فضل خدا و رحمتش بر تو همانا آهنگ کردند گروهی از ایشان که گمراهت کنند و گمراه نکنند جز خویشتن را و زیانت نرسانند به چیزی و فرستاد خدا بر تو کتاب و حکمت را و بیاموخت آنچه را ندانستی و همانا فضل خدا است بر تو بزرگ‌'),
(607, 4, 114, 'نیست نیکیی در بسیاری از رازگوئیشان جز آنکه امر کند به صدقه یا نیکی یا کارسازی میان مردم و آن کس که این کار را کند از پی خوشنودی خدا بزودی دهیمش پاداشی بزرگ‌'),
(608, 4, 115, 'و آن کس که مخالفت کند با پیمبر پس از آنکه پدیدار شد برایش هدایت و پیروی کند غیر از راه مؤمنان را رسانیمش بدانچه دوست دارد و افکنیمش در دوزخ و چه بد بازگشتگاهی است‌'),
(609, 4, 116, 'همانا خدا نمی‌آمرزد آنکه شرک ورزیده شود بدو و می‌آمرزد جز آن را برای هر کس خواهد و کسی که شرک ورزد به خدا همانا گمراه شده است گمراهی دور'),
(610, 4, 117, 'نمی‌خوانند جز او مگر مادگانی را و نمی‌خوانند جز شیطانی گردنکش را'),
(611, 4, 118, 'که دورش کرده است خدا و گفت هر آینه بگیرم از بندگان تو بهره‌ای معین را'),
(612, 4, 119, 'و همانا گمراهشان کنم و به آرزوشان افکنم و بفرمایمشان که بشکافند گوشهای چهارپایان را و بفرمایمشان که برگردانند آفرینش خدا را و کسی که شیطان را به دوستی گزیند جز خدا همانا زیان کرده است زیانی آشکار'),
(613, 4, 120, 'نویدشان دهد و به آرزوشان افکند و نویدشان ندهد شیطان مگر فریب (سرگرمی)'),
(614, 4, 121, 'آنان جایگاهشان دوزخ است و نیابند از آن چاره‌ای‌'),
(615, 4, 122, 'و آنان که ایمان آوردند و کردار شایسته کردند بزودی درآریمشان باغهائی که جاری است در آنها جویها جاودانان در آن همیشه وعده خدا به درست و کیست راستگوتر از خدا در سخن‌'),
(616, 4, 123, 'نیست به دلخواه شما و نه به دلخواه اهل کتاب هر کس کردار زشت کند کیفر شود بدان و نیابد برای خویشتن از خدا یار و نه یاوری‌'),
(617, 4, 124, 'و آن کس که کرداریهای شایسته کند مرد یا زن و او مؤمن باشد آنان به بهشت درآیند و ستم بر ایشان نشود به اندازه منقارگاهی‌'),
(618, 4, 125, 'و کیست به دین‌تر (نکوتر دین) از آن کس که روی به خدا آورد و او نکوکار باشد و پیروی کند آئین ابراهیم یکتاپرست را و برگرفت خدا ابراهیم را دوستی‌'),
(619, 4, 126, 'و خدا را است آنچه در آسمانها و آنچه در زمین است و خداوند است به همه چیز فراگیرنده‌'),
(620, 4, 127, 'پرسندت درباره زنان بگو خدا دستور دهد شما را درباره ایشان و آنچه خوانده شود بر شما در کتاب درباره یتیمان زنانی که نمی‌دهیدشان آنچه نوشته شده است برای ایشان و خوش ندارید که زناشوئی کنید با ایشان و ناتوانان از کودکان و آنکه قیام کنید درباره یتیمان به داد و هر چه کنید از نیکی همانا خداوند است بدان دانا'),
(621, 4, 128, 'و اگر زنی ترسید از شوی خویش ناسازگاری یا روی گردانی را پس باکی بر آنان نیست که آشتی افکند میان خویش آشتیی و آشتی بهتر است و جانها را فرآورده شد بخل و اگر نکوکاری و پرهیزکاری کنید همانا خدا بدانچه کنید دانا است‌'),
(622, 4, 129, 'و هرگز نتوانید دادگری کنید میان زنان و اگر چه آرزومند آن باشید پس کج نشوید با همه خواست خود به یک سوی تا او را بازگذارید مانند آویخته و اگر اصلاح کنید و پرهیزکاری کنید همانا خداوند است آمرزنده مهربان‌'),
(623, 4, 130, 'و اگر جدا شوند (زن و شوهر از همدیگر) خدا بی‌نیاز کند هر کدام را از گشایش خویش و خدا است گشایشمند حکیم‌'),
(624, 4, 131, 'و برای خدا است آنچه در آسمانها است و آنچه در زمین و همانا اندرز دادیم آنان را که داده شدند کتاب را پیش از شما و شما که بترسید خدا را و اگر کفر ورزید همانا برای خدا است آنچه در آسمانها است و آنچه در زمین و خدا است بی‌نیاز ستوده‌'),
(625, 4, 132, 'و خدا را است آنچه در آسمانها است و آنچه در زمین و بس است خداوند وکیل (کارگذار)'),
(626, 4, 133, 'اگر بخواهد می‌برد شما را ای مردم و می‌آورد دیگران را و خداوند است بر آن توانا'),
(627, 4, 134, 'کسی که بخواهد پاداش دنیا را همانا نزد خدا است پاداش دنیا و آخرت و خدا است شنونده بینا'),
(628, 4, 135, 'ای آنان که ایمان آوردید باشید قیام‌کنندگان به داد (یا نگهبانان بر داد) گواهانی برای خدا هر چند بر خویشتن یا پدر و مادر و نزدیکان اگر بی‌نیاز یا نیازمند باشد همانا خدا سزاوارتر است بدانان پس پیروی هوس نکنید تا کج شوید و اگر سرپیچید یا روی گردانید همانا خدا است بدانچه می‌کنید آگاه‌'),
(629, 4, 136, 'ای آنان که ایمان آوردید ایمان آرید به خدا و پیمبرش و کتابی که فرستاد بر پیمبرش و کتابی که فرستاده است از پیش و کسی که کفر ورزد به خدا و فرشتگان و کتابهایش و پیمبرانش و به روز بازپسین همانا گمراه شده است گمراهی دور'),
(630, 4, 137, 'همانا آنان که ایمان آوردند پس کافر شدند و سپس ایمان آوردند و پس کافر شدند پس فزونی یافتند در کفر نیست خدا آمرزنده ایشان و نه راهنمائیشان کند به راهی‌'),
(631, 4, 138, 'مژده ده منافقان (مردم دوروی) را که برای آنان است شکنجه دردناک‌'),
(632, 4, 139, 'آنان که می‌گیرند کافران را دوستانی جز مؤمنان آیا نزد آنان عزت را جویند همانا عزت از آن خدا است همگی‌'),
(633, 4, 140, 'و همانا فرود آورده است بر شما در کتاب که اگر شنیدید به آیتهای خدا کفرورزی می‌شود یا استهزاء می‌شود بدانها نه نشینید با ایشان تا فروروند در سخنی غیر از آن که شمائید در آن هنگام مانند ایشان همانا خدا است گردآورنده منافقان و کافران در دوزخ همگی‌'),
(634, 4, 141, 'آنان که انتظار به شما می‌کشند تا اگر شما را پیروزی (گشایشی) از خدا باشد گویند آیا نبودیم با شما و اگر کافران را بهره‌ای بود گویند آیا چیرگی نورزیدیم بر شما و نگاهداری نکردیم شما را از مؤمنان پس خدا حکم کند بین شما روز رستاخیز و هرگز قرار ندهد خدا برای کافران بر مؤمنان راهی‌'),
(635, 4, 142, 'همانا منافقان نیرنگ ورزند با خدا و او است فریب‌دهنده ایشان و هرگاه برخیزند به سوی نماز برخیزند سرگران خودنمائی کنند به مردم و یاد نکنند خدا را جز اندکی‌'),
(636, 4, 143, 'سرگردانند میان این نه به سوی آنانند و نه به سوی اینان و کسی را که خدا گمراه کند نیابی برای او راهی‌'),
(637, 4, 144, 'ای آنان که ایمان آوردید نگیرید کافران را دوستانی جز مؤمنان آیا خواهید قرار دهید برای خدا بر خویشتن دستاویزی آشکار'),
(638, 4, 145, 'همانا منافقان در پایه فرودینند از آتش و نیابی برای ایشان یاوری‌'),
(639, 4, 146, 'مگر آنان که توبه کردند و اصلاح نمودند و به خدا چنگ زدند (نگهداری از خدا خواستند) و پاک کردند دین خود را برای خدا پس آنانند با مؤمنان و بزودی می‌دهد خدا مؤمنان را پاداشی بزرگ‌'),
(640, 4, 147, 'چه می‌کند خدا به عذاب شما مگر سپاسگزارید و ایمان آرید و خدا است شکرپذیرنده دانا'),
(641, 4, 148, 'دوست ندارد خدا بلند سخن گفتن را به گفتار زشت جز آن کس که ستم بر او شده است و خدا است شنونده دانا'),
(642, 4, 149, 'اگر آشکار کنید نیکی را یا نهان داریدش یا گذشت کنید از زشتی همانا خدا است گذشت‌کننده توانا'),
(643, 4, 150, 'همانا آنان که کفر ورزیدند به خدا و پیمبرانش و خواهند جدائی افکنند میان خدا و پیمبرانش و گویند ایمان آریم به بعضی و کفر ورزیم به بعضی و خواهند که برگیرند میان این راهی‌'),
(644, 4, 151, 'آنانند کافران به راستی و آماده کردیم برای کافران عذابی خوارکننده را'),
(645, 4, 152, 'و آنان که ایمان آوردند به خدا و پیمبرانش و جدائی نیفکندند میان هیچیک از ایشان آنان را به زودی دهد بدیشان پاداشهای ایشان را و خدا است آمرزنده مهربان‌'),
(646, 4, 153, 'می‌خواهند از تو اهل کتاب که فرو فرستی بر ایشان نامه‌ای از آسمان همانا خواستند از موسی بزرگتر از این را گفتند به ما بنمایان خدا را آشکار پس فراگرفتشان صاعقه (آتش آسمانی) به ستمکاریشان پس برگرفتند گوساله را پس از آنکه بیامدشان نشانیها پس درگذشتیم از آن و دادیم به موسی فرمانروائیی آشکار'),
(647, 4, 154, 'و برافراشتیم بر فراز ایشان طور را به پیمانشان و بدیشان گفتیم اندرآیید در را سجده‌کنان و گفتیم بدیشان که تجاوز نکنید در شنبه و گرفتیم از ایشان پیمانی سخت‌'),
(648, 4, 155, 'پس بشکستن ایشان پیمان خویش را و کفر ورزیدن ایشان به آیتهای خدا و کشتنِ ایشان پیمبران را به ناحقّ و گفتنِ ایشان که دلهای ما است نهان در پرده بلکه مُهر نهاده است خدا بر آنها به کفرشان پس ایمان نیارند مگر اندکی‌'),
(649, 4, 156, 'و به کفرشان و گفتارشان در باره مریم تهمتی بزرگ‌'),
(650, 4, 157, 'و گفتارشان که ما کشتیم مسیح عیسی ابن مریم پیمبر خدا را حالی که نکشتندش و نه به دارش آویختند بلکه مشتبه شد بر ایشان و همانا آنان که در او اختلاف کردند در شکّی از او هستند نیستشان بدان دانائیی جز پیروی پندار حالی که نکشتند او را به یقین‌'),
(651, 4, 158, 'بلکه بالا بردش خدا به سوی خویش و خدا است عزّتمند حکیم‌'),
(652, 4, 159, 'و اگر نیست از اهل کتاب جز آنکه ایمان آورد بدو پیش از مرگش و روز رستاخیز می‌باشد بر ایشان گواه‌'),
(653, 4, 160, 'پس به ستمی از آنان که جهود شدند حرام کردیم بر ایشان پاکهائی را که حلال بود برای ایشان و به بازداشتن ایشان از راه خدا بسیاری را'),
(654, 4, 161, 'و گرفتن ایشان ربا را حالی که نهی شدند از آن و خوردن ایشان اموال مردم را به ناحقّ و آماده کردیم برای کافران از ایشان عذابی دردناک‌'),
(655, 4, 162, 'لیکن به دانش فرورفتگان از ایشان و مؤمنان ایمان آورند بدانچه فرستاده شد به سوی تو و بدانچه فرستاده شده است پیش از تو و بپای‌دارندگان نماز و دهندگان زکوة و ایمان‌آرندگان به خدا و روز بازپسین آنان را بزودی دهیمشان پاداشی بزرگ‌'),
(656, 4, 163, 'همانا وحی فرستادیم به سوی تو چنانکه وحی فرستادیم بسوی نوح و پیمبران پس از او و وحی فرستادیم بسوی ابراهیم و اسماعیل و اسحق و یعقوب و اسباط و عیسی و ایّوب و یونس و هارون و سلیمان و دادیم به داود زبور را'),
(657, 4, 164, 'و پیمبرانی که خواندیم داستانشان را بر تو پیش از این و پیمبرانی که نخواندیم داستانشان را بر تو و سخن گفت خدا با موسی سخن‌گفتنی‌'),
(658, 4, 165, 'پیمبرانی نویددهندگان و ترسانندگان تا نباشد برای مردم بر خدا بهانه‌ای پس از پیمبران و خدا است عزّتمند حکیم‌'),
(659, 4, 166, 'لیکن خدا گواهی دهد بدانچه فرستاده است به سوی تو فرستاد آن را به علم خود و فرشتگان گواهی دهند و بس است خداوند گواه‌'),
(660, 4, 167, 'همانا آنان که کفر ورزیدند و بازداشتند از راه خدا هر آینه گمراه شدند گمراهیی دور'),
(661, 4, 168, 'همانا آنان که کفر ورزیدند و ستم کردند نیست خدا که بیامرزدشان و نه رهبریشان کند به راهی‌'),
(662, 4, 169, 'مگر به راه دوزخ جاودانند در آن همیشه و همانا آن است بر خدا آسان‌'),
(663, 4, 170, 'ای مردم همانا بیامد شما را فرستاده‌ای به حقّ از پروردگار شما پس ایمان آرید بهتر است برای شما و اگر کفر ورزید همانا برای خدا است آنچه در آسمانها است و زمین و خدا است دانای حکیم‌'),
(664, 4, 171, 'ای اهل کتاب فزون نروید در کیش خود و نگوئید بر خدا مگر حقّ جز این نیست که مسیح عیسی ابن مریم فرستاده خدا است و سخن او که افکندش به سوی مریم و روحی است از او پس ایمان آرید به خدا و پیمبرانش و نگوئید سه تا بس کنید بهتر است شما را جز این نیست که خداوند خدای یکتا است منزّه است او از آنکه وی را فرزندی باشد او را است آنچه در آسمانها است و زمین و بس است خداوند وکیل‌'),
(665, 4, 172, 'هرگز سرنپیچد مسیح از آنکه بنده‌ای از برای خدا باشد و نه فرشتگان مقرّب و آن کس که سرپیچد از پرستش او و گردن فرازد بزودی گردآورد آنان را بسوی خود همگی‌'),
(666, 4, 173, 'پس آنان که ایمان آوردند و کردار شایسته کردند بپردازد بدیشان همه مزد ایشان را و بیفزایدشان از فضل خویش و آنان که استنکاف ورزیدند و سرکشی کردند عذاب کند ایشان را عذابی دردناک و نیابند برای خویش جز خدا دوست و نه یاوری‌'),
(667, 4, 174, 'ای مردم همانا بیامد شما را برهانی از پروردگار شما و فرستادیم به سوی شما پرتوی آشکار'),
(668, 4, 175, 'پس آنان که ایمان آوردند به خدا و چنگ زدند بدو بزودی درآوردشان به رحمتی و فضلی از خویش و رهبریشان کند به سوی خود به راهی راست‌'),
(669, 4, 176, 'فتوی از تو خواهند بگو خدا فتوی دهد شما را در کلاله (خواهران و برادران) اگر مردی بمیرد و نباشدش فرزندی و او را خواهری باشد پس برای آن خواهر است نصف آنچه بجای گذارده است و او ارث می‌برد اگر نباشدش فرزندی و اگر دو خواهر باشند پس ایشان را است دو ثلث آنچه به جای گذارده است اگر باشند برادرانی مردان و زنان پس برای مرد است مانند بهره دو زن بیان می‌کند خدا برای شما نبادا گمراه شوید و خدا است به همه چیز دانا');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'ای آنان که ایمان آوردید وفا کنید به پیمانها حلال شده است برای شما دامهای چهارپا مگر آنچه سروده شود بر شما حلال ناشمرندگان شکار حالی که هستید در احرام همانا خدا حکم می‌کند بدانچه می‌خواهد'),
(671, 5, 2, 'ای آنان که ایمان آوردید روا ندارید شعارهای خدا و نه ماه حرام را و نه قربانی و نه اشتران قلاّده‌دار و نه پویندگان خانه حرام را که می‌جویند فضلی از پروردگار خود و خوشنودیی و هنگامی که برون شدید از احرام پس شکار کنید و واندارد شما را کینه گروهی که بازداشتند شما را از مسجد حرام بر آنکه ستم کنید و کمک کنید همدیگر را بر نکوکاری و پرهیزکاری و کمک نکنید بر گناه و ستم و بترسید خدا را همانا خدا است سخت‌شکنجه‌'),
(672, 5, 3, 'حرام شد بر شما مردار و خون و گوشت خوک و آنچه نام غیر از خدا بر آن برده شود و گلوگیر شده و کوبیده و پرتاب‌شده و شاخ‌زده و آنچه خورند ددان مگر آنچه پاک کنید و آنچه نزد بتان کشته شود و آنکه بخش کنید با تیرها این است برون‌رفتن از فرمان خدا امروز نومید شدند آنان که کفر ورزیدند از دین شما پس نترسید ایشان را و مرا بترسید امروز به انجام رسانیدم برای شما دین شما را و تمام کردم بر شما نعمتم را و پسندیدم برای شما اسلام را کیشی پس آن کس که ناچار شود در گرسنگی ناگراینده به سوی گناهی همانا خدا است آمرزنده مهربان‌'),
(673, 5, 4, 'پرسندت چه چیز حلال شده است برای ایشان بگو حلال شده است برای شما پاکیزه‌ها و شکار و آنچه آموختید از ددان شکاری حالیکه پرورش‌دهندگان آنها باشید که بیاموزید آنها را از آنچه آموختتان خدا پس بخورید از آنچه نگهداشتند بر شما و یاد کنید نام خدا را بر آن و بترسید خدا را که خدا است شتاب‌کننده در حساب‌'),
(674, 5, 5, 'امروز حلال شد برای شما پاکیزه‌ها و خوراک آنان که داده شدند کتاب را حلال است برای شما و خوراک شما حلال است برای آنان و زنان پارسا از ایمان‌آورندگان و زنان پارسا از آنان که داده شدند کتاب را پیش از شما هرگاه دهید بدیشان مزدهای ایشان را حالی که پارسا باشید نه زناکاران و نه گیرندگان یار و آن که کفر ورزد به ایمان همانا تباه شده است کردار او و اوست در آخرت از زیانکاران‌'),
(675, 5, 6, 'ای آنان که ایمان آوردید گاهی که برخاستید به سوی نماز بشوئید رویها و دستهای خویش را تا مِرفقها (آرنجها) و بمالید سرها و پایهای خود را تا دو قاب یا اگر جنب شدید پس پاک کنید خود را و اگر بیمار بودید یا در سفری یا برون آمد یکی از شما از آب‌ریز یا با زنان درآمیختید و آب نیافتید پس آهنگ زمینی پاک کنید سپس بمالید بر رویها و دستهای خود از آن نخواهد خدا که سخت بر شما گیرد لیکن خواهد که شما را پاک سازد و تمام کند بر شما نعمتش را و شاید شکر گزارید'),
(676, 5, 7, 'و یاد آرید نعمت خدا را بر شما و پیمانش را که با شما بست هنگامی که گفتید شنیدیم و فرمان بردیم و بترسید خدا را که خداوند است دانا بدانچه در سینه‌ها است‌'),
(677, 5, 8, 'ای آنان که ایمان آوردید باشید نگهبانان یا قیام‌کنندگان برای خدا گواهانی به داد و واندارد شما را کین قومی بر آنکه داد نکنید دادگری کنید که آن نزدیکتر است به پرهیزکاری و بترسید خدا را که خدا آگاه است بدانچه می‌کنید'),
(678, 5, 9, 'نوید داده است خدا آنان را که ایمان آوردند و کردار شایسته کردند ایشان را است آمرزشی و پاداشی بزرگ‌'),
(679, 5, 10, 'و آنان که کفر ورزیدند و دروغ پنداشتند آیتهای ما را آنانند یاران دوزخ‌'),
(680, 5, 11, 'ای آنان که ایمان آوردید یاد آرید نعمت خدا را بر شما هنگامی که آهنگ نمودند گروهی که دست به سوی شما دراز کنند پس بازداشت دستهای ایشان را از شما و بترسید خدا را و بر خدا باید توکل کنند مؤمنان‌'),
(681, 5, 12, 'همانا برگرفت خدا پیمان بنی‌اسرائیل را و برانگیختیم از ایشان دوازده سردار و گفت خدا منم با شما اگر بپا داشتید نماز را و دادید زکوة را و ایمان آوردید به فرستادگان من و احترام گذاردید آنان را و وام دادید خدا را وامی نکو همانا بسترم از شما زشتیهای شما را و درآرم شما را باغهائی که جاری است زیر آنها جویها پس آنکس که کفر ورزد پس از این از شما هر آینه گم کرده است راه راست را'),
(682, 5, 13, 'پس بشکستن ایشان پیمان خویش را لعنتشان کردیم و گردانیدیم دلهای ایشان را سنگین که برگردانند (جابجا کنند) سخنان را از جایهای خود و فراموش کردند بخشی را از آنچه یادآوری شدند بدان و گاهی نیست که آگاه نشوی بر خیانتی از آنان مگر کمی از آنان پس درگذر از ایشان و چشم‌پوشی کن که خدا دوست دارد نکوکاران را'),
(683, 5, 14, 'و از آنان که گفتند مائیم ترسایان گرفتیم پیمان ایشان را پس فراموش کردند بخشی را از آنچه یادآوری شدند بدان و برانگیختیم میان آنان دشمنی و کینه را تا روز قیامت و بزودی آگهیشان دهد خدا بدانچه بودند می‌ساختند'),
(684, 5, 15, 'ای اهل کتاب همانا بیامد شما را فرستاده‌ای که بیان می‌کند برای شما بیشی را از آنچه بودید نهان می‌داشتید از کتاب و درمی‌گذرد از بیشی همانا بیامد شما را از خدا پرتوی و نامه‌ای آشکارا'),
(685, 5, 16, 'هدایت می‌کند خدا بدان هر که را پیرو خوشنودی او باشد به راه‌های آشتی (و آرامش) و برون می‌برد ایشان را از تاریکیها به سوی روشنائی به اذن خود و رهبریشان می‌کند به راهی راست‌'),
(686, 5, 17, 'همانا کفر ورزیدند آنان که گفتند خدا او مسیح فرزند مریم است بگو که را دستی بر خدا است اگر خواهد نابود کند مسیح فرزند مریم و مادرش را و هر که در زمین است همگی و خدا را است فرمانروائی آسمانها و زمین و آنچه میان آنها است می‌آفرد هرچه خواهد و خدا است بر هر چیز توانا'),
(687, 5, 18, 'گفتند جهودان و ترسایان مائیم فرزندان خدا و دوستان او بگو پس چرا عذاب کند شما را به گناهانتان بلکه شمائید بشری از آنکه بیافرید بیامرزد برای هر که خواهد و عذاب کند هر که را خواهد و از آن خدا است پادشاهی آسمانها و زمین و آنچه میان آنها است و به سوی او است بازگشت‌'),
(688, 5, 19, 'ای اهل کتاب همانا بیامد شما را فرستاده ما بیان می‌کند برای شما پس از فترتی از پیمبران تا نگوئید نیامد ما را امیددهنده و بیم‌دهنده همانا آمد شما را نویددهنده‌ای و ترساننده‌ای و خدا است بر همه چیز توانا'),
(689, 5, 20, 'و هنگامی که گفت موسی به قوم خود ای قوم من یاد آرید نعمت خدا را بر شما هنگامی که قرار داد در شما پیمبرانی و گردانید شما را پادشاهانی و ارزانی داشت به شما آنچه را نداد به کسی از جهانیان‌'),
(690, 5, 21, 'ای قوم من اندر آئید سرزمین مقدسی را که خدا برای شما نوشت و برنگردید بر پشتهای خود تا بازگردید زیانکاران‌'),
(691, 5, 22, 'گفتند ای موسی همانا در آنند گروهی سرکش و هرگز ما در آن نرویم تا برون آیند از آن سپس گاهی که برون آمدند از آن مائیم درآیندگان‌'),
(692, 5, 23, 'گفتند دو تن از آنان که می‌ترسیدند و خدا بر ایشان انعام کرده بود درآئید بر ایشان درگاه را که هرگاه درون شدید همانا شمائید پیروزمندان و بر خدا تکیه کنید اگر هستید مؤمنان‌'),
(693, 5, 24, 'گفتند ای موسی هرگز در آن نشویم هیچگاه تا آنان در آنند پس برو تو و پروردگار خود و نبرد کنید که مائیم در اینجا نشستگان‌'),
(694, 5, 25, 'گفت پروردگارا مالک نیستم من جز خود و برادرم را پس جدائی افکن میان ما و گروه نافرمانان‌'),
(695, 5, 26, 'گفت همانا آن حرام است بر ایشان چهل سال سرگردان مانند در زمین پس دریغ نخور بر گروه نافرمانان‌'),
(696, 5, 27, 'و بخوان بر ایشان داستان فرزندان آدم را به حقّ هنگامی که قربان کردند پس پذیرفته شد از یکیشان و پذیرفته نشد از دیگری گفت همانا می‌کشمت گفت جز این نیست که می‌پذیرد خدا از پرهیزکاران‌'),
(697, 5, 28, 'اگر دست فراسوی من آری که بکشی مرا دراز نکنم دستم را به تو تا تو را بکشم همانا می‌ترسم من خداوند را پروردگار جهانیان‌'),
(698, 5, 29, 'همانا می‌خواهم که برگیری گناه من و گناه خویش را تا شوی از یاران آتش و این است پاداش ستمگران‌'),
(699, 5, 30, 'پس بیاراست برای او دلش کشتن برادر خویش را پس کشت او را و گردید از زیانکاران‌'),
(700, 5, 31, 'پس برانگیخت خدا کلاغی که می‌کاوید در زمین تا بنمایاندش چگونه نهان سازد پیکر برادر خویش را گفت ای وای بر من آیا عاجز شدم از آنکه مانند این غراب باشم که نهان کنم پیکر برادر خویش را پس گشت از پشیمانان‌'),
(701, 5, 32, 'برای این نوشتیم بر بنی‌اسرائیل که هر کس بکشد تنی را نه در برابر تنی یا تبهکاریی در زمین مانند آن است که بکُشد مردم را همگی و آن کس که زنده سازدش گوئیا زنده ساخته است مردم را همگی و همانا بیامدشان فرستادگان ما به نشانیها سپس بسیاری از ایشانند پس از این در زمین اسراف‌کنندگان‌'),
(702, 5, 33, 'همانا پاداش آنان که با خدا و رسولش بستیزند و راه تبهکاری در زمین پویند (به تبهکاری در زمین کوشند) آن است که به سختی کشته شوند یا به دار آویخته شوند یا به سختی بریده شود دستها و پایهای ایشان از برابر یکدیگر یا رانده شوند از زمین این است برای ایشان خواریی در دنیا و ایشان را است در آخرت عذابی سنگین‌'),
(703, 5, 34, 'مگر آنان که توبه کردند پیش از آنکه بر ایشان دست یابید پس بدانید که خداوند است آمرزنده مهربان‌'),
(704, 5, 35, 'ای آنان که ایمان آوردید بترسید خدا را و بجوئید به سوی او دستاویزی و کوشش کنید (جهاد کنید) در راه او شاید رستگار شوید'),
(705, 5, 36, 'همانا آنان که کفر ورزیدند اگر برای ایشان باشد آنچه در زمین است همگی و مانند آن با آن تا عوض خود دهند از عذاب روز قیامت پذیرفته نشود از ایشان و ایشان را است عذابی دردناک‌'),
(706, 5, 37, 'خواهند برون روند از آتش و نیستند برون روندگان از آن و ایشان را است عذابی پاینده‌'),
(707, 5, 38, 'مرد دزد و زن دزد را ببرید دستهای آنان را کیفری بدانچه خود فراهم کردند شکنجه‌ای از نزد خدا و خدا است عزّتمند حکیم‌'),
(708, 5, 39, 'ولی آن کس که توبه کند پس از ستمگریش و شایستگی گزیند بپذیرد خدا توبه او را و خدا است آمرزنده مهربان‌'),
(709, 5, 40, 'آیا ندانستی که خدای را است پادشاهی آسمانها و زمین عذاب کند هر که را خواهد و بیامرزد هر که را خواهد و خداوند است بر هر چیز توانا'),
(710, 5, 41, 'ای پیمبر اندوهگینت نکند آنان که می‌شتابند در کفر از آنان که گویند ایمان آوردیم با دهانهای خود ولی ایمان نیاورده است دلهای ایشان و از آنان که جهودند شنوندگانند دروغ را شنوانندگانند برای گروهی دیگر که نیامدند تو را برمی‌گردانند کلمات را پس از جایگاه‌های آن گویند اگر داده شدید این را بگیریدش و اگر داده نشدیدش پس بترسید و کسی که خدا خواهد آزمایش وی را هرگز مالک نیستی برای او از خدا چیزی را آنانند که نخواسته است خدا پاک ساختن دلهای ایشان را آنان را است در دنیا خواریی و آنان را است در آخرت عذابی بزرگ‌'),
(711, 5, 42, 'شنوندگانند دروغ را خورندگانند رشوت را اگر آمدند تو را پس حکم کن میان ایشان یا روی گردان از ایشان و اگر روی گردانی از ایشان همانا آسیبت نرسانند به چیزی و هرگاه حکم کنی پس حکم کن میان ایشان به داد همانا خدا دوست دارد دادگران را'),
(712, 5, 43, 'چگونه تو را به داوری گزینند حالی که نزد ایشان است تورات که در آن است حکم خدا سپس پشت می‌کنند پس از آن و نیستند آنان مؤمنان‌'),
(713, 5, 44, 'همانا فرستادیم تورات را در آن رهبریی و روشنائیی حکم می‌کنند بدان پیمبرانی که اسلام آوردند برای آنان که جهود شدند و علماء عاملین و دانشمندان بدانچه نگهبان آن شدند از کتاب خدا و بودند گواهانی بر آن پس نترسید مردم را و بترسید مرا و نفروشید آیتهای مرا به بهائی اندک و کسی که حکم نکند بدانچه خدا فرستاده است آنانند کافران‌'),
(714, 5, 45, 'و نوشتیم بر ایشان در آنکه جان برابر جان و چشم برابر چشم و بینی برابر بینی و گوش برابر گوش و دندان برابر دندان و زخمها برابر است پس کسی که تصدّق کند بدان همانا آن زداینده گناهان او است و آن کس که حکم نکند بدانچه خدا فرستاده است آنانند ستمگران‌'),
(715, 5, 46, 'و از پی ایشان فرستادیم عیسی بن مریم را تصدیق‌کننده آنچه پیش روی اوست از تورات و بدو دادیم انجیل را که در آن بود راهنمائی و روشنائیی و تصدیق‌کننده آنچه پیش روی اوست از تورات و رهبری و اندرزی برای مؤمنان‌'),
(716, 5, 47, 'و باید حکم کنند انجیلیان بدانچه فرستاده است خدا در آن و آن کس که حکم نکند بدانچه فرستاده است خدا آنانند تبهکاران‌'),
(717, 5, 48, 'فرستادیم بر تو کتاب را به حقّ تصدیق‌کننده آنچه پیش روی اوست از کتاب و نگهبانی بر آن پس حکم کن میان ایشان بدانچه خدا فرستاد و پیروی مکن آرزوهای ایشان را که متجاوز است از آنچه بیامده است تو را از حقّ برای هر کدام قرار دادیم راه و روشی و اگر می‌خواست خدا می‌گردانید شما را یک ملّت لیکن تا بیازماید شما را در آنچه به شما داده است پس بشتابید به سوی نیکیها به سوی خدا است بازگشت شما همگی سپس آگهی دهد شما را بدانچه بودید در آن اختلاف‌کنندگان‌'),
(718, 5, 49, 'و آنکه حکم کن میان ایشان بدانچه فرستاد خدا و پیروی منما هوسهای ایشان را و بترس از ایشان که نبادا بفریبندت از پاره آنچه خدا فرستاده است به سوی تو پس اگر پشت کردند بدانکه می‌خواهد خدا آنکه بگیرد ایشان را به پاره‌ای از گناهانشان و همانا بسیاری از مردمند نافرمانان‌'),
(719, 5, 50, 'آیا دستور جاهلیّت را خواهند و کیست نکوتر از خدا در دستور برای گروهی که یقین دارند'),
(720, 5, 51, 'ای آنان که ایمان آوردید نگیرید جهودان و ترسایان را دوستانی برخی از ایشانند دوستان برخی و کسی که دوستی کند با آنان از شما هر آینه او از آنان است همانا خدا هدایت نکند گروه ستمگران را'),
(721, 5, 52, 'بینی آنان را که در دلهاشان بیماریی است می‌شتابند در ایشان گویند ترسیم به ما رسد پیشامدی امید است خدا بیارد پیروزی یا امری را از نزد خویش چنان که بگردند از آنچه در دلهای خود نهان می‌داشتند پشیمان‌'),
(722, 5, 53, 'گویند کسانی که ایمان آوردند آیا اینانند آنان که سوگند یاد نمودند به سوگندهای سخت خویش که ایشانند با شما تباه شد کارهای ایشان و شدند زیانکاران‌'),
(723, 5, 54, 'ای آنان که ایمان آوردید هر کس بازگردد از شما از دین خود بزودی بیارد خدا قومی را که دوستش دارند و دوستشان دارد فروتنان برابر مؤمنان گردن فرازان بر کافران بکوشند در راه خدا و نترسند سرزنش سرزنشگری را این است فضل خدا که دهدش به هر که خواهد و خدا است گشایشمند دانا'),
(724, 5, 55, 'جز این نیست که دوست شما خدا است و رسول او و آنان که ایمان آوردند آنان که بپای دارند نماز را و بدهند زکوة را حالی که ایشانند رکوع‌گزاران‌'),
(725, 5, 56, 'و آنکس که دوستی کند با خدا و پیمبرش و آنان که ایمان آوردند همانا حزب خدا است پیروزمندان‌'),
(726, 5, 57, 'ای آنان که ایمان آوردید نگیرید آنان را که دین شما را ریشخند و بازیچه گرفتند از آنان که داده شدند کتاب را پیش از شما و کافران و دوستانی و بترسید خدا را اگر هستید مؤمنان‌'),
(727, 5, 58, 'و هنگامی که بانگ برآرید برای نماز گیرندش مسخره و بازیچه این بدان است که آنانند گروهی نابخردان‌'),
(728, 5, 59, 'بگو ای اهل کتاب آیا کینه جوئید از ما جز آنکه ایمان آوردیم به خدا و آنچه بر ما فرستاده شده و آنچه فرستاده شده است از پیش و همانا بیشتر شمائید نافرمانان‌'),
(729, 5, 60, 'بگو آیا خبر دهم شما را به بدتر از این پاداشی از نزد خدا آنکس که دور کردش خدا و خشم بر او گرفت و قرار داد از ایشان میمونان و خوکان و پرستندگان سرکش آنانند بدترین در جایگاه و گمراهتر از راه راست‌'),
(730, 5, 61, 'و هرگاه شما را آیند گویند ایمان آوردیم حالی که با کفر درون رفتند و با آن برون آمدند و خدا است داناتر بدانچه نهان می‌سازند'),
(731, 5, 62, 'بینی بسیاری را از ایشان پیشی می‌گیرند بر همدیگر در شتافتن به سوی گناه و ستمگری و خوردنشان رشوت را چه زشت است آنچه بودند می‌کردند'),
(732, 5, 63, 'چرا نهیشان نمی‌کنند خداپرستان و دانشمندان از گفتنشان گناه را و خوردنشان رشوت را چه زشت است آنچه بودند می‌ساختند'),
(733, 5, 64, 'گفتند جهودان دست خدا بسته به زنجیر است به زنجیر بسته باد دستهای آنان و لعنت بر آنان باد بدانچه گفتند بلکه دستهای او است باز می‌بخشد هرگونه خواهد و همانا می‌افزاید بسیاری از آنان را آنچه فرستاده شد بسوی تو از پروردگارت سرکشی و کفر و افکندیم میان ایشان دشمنی و کینه را تا روز قیامت هرگاه بیفروزند آتشی را برای جنگ فرونشاندش خدا و بکوشند به تبهکاری در زمین و خدا دوست ندارد تبهکاران را'),
(734, 5, 65, 'اگر آنکه اهل کتاب ایمان می‌آوردند و پرهیزکاری می‌کردند هرآینه می‌ستردیم از ایشان گناهان ایشان را و درمی‌آوردیمشان به باغستان نعمت‌'),
(735, 5, 66, 'و اگر بپای می‌داشتند تورات و انجیل و آنچه را فرستاده شده است به سوی ایشان از پروردگارشان همانا می‌خوردند از بالای سر و از زیر پایهای خود ایشانند گروهی میانه‌رو و بسیاری از ایشان زشت است آنچه می‌کنند'),
(736, 5, 67, 'ای پیمبر ابلاغ کن آنچه را فرود آمد بر تو از پروردگارت و اگر نکنی نرسانده باشی پیام او را و خدا نگه می‌داردت از مردم همانا خدا هدایت نکند گروه کافران را'),
(737, 5, 68, 'بگو ای اهل کتاب نیستید بر چیزی تا بپای دارید تورات و انجیل را و آنچه فرود آمده است بر شما از پروردگار شما و هرآینه بیفزاید بسیاری از ایشان را آنچه فرود آمده است بر تو از پروردگارت سرکشی و کفر پس اندوهگین نباش بر گروه کافران‌'),
(738, 5, 69, 'همانا آنکه ایمان آوردند و آنان که جهود شدند و صابیان و ترسایان هر کس ایمان آرد به خدا و روز بازپسین و کردار شایسته کند نیست بر ایشان بیمی و نه اندوهگین شوند'),
(739, 5, 70, 'همانا گرفتیم پیمان بنی‌اسرائیل را و فرستادیم بسوی آنان پیمبرانی هرگاه می‌آمدشان پیمبری بدانچه هوس نمی‌کرد دلهای ایشان گروهی را دروغگو شمردند و کشتند گروهی را'),
(740, 5, 71, 'پنداشتند نخواهد بود آزمایشی پس کور شدند و کر شدند سپس پذیرفت توبه ایشان را خدا پس کور شدند و کر گردیدند بسیاری از ایشان و خدا بینا است بدانچه می‌کنند'),
(741, 5, 72, 'همانا کفر ورزیدند آنان که گفتند خدا مسیح فرزند مریم است حالی که گفت مسیح ای بنی‌اسرائیل پرستش کنید خدا پروردگار من و پروردگار شما را همانا کسی که شرک ورزد به خدا حرام کرده است بر او بهشت را و جایگاه او است آتش و نیست ستمکاران را یارانی‌'),
(742, 5, 73, 'همانا کفر ورزیدند آنان که گفتند خدا سیّمین سه تا است حالی که نیست خداوندی جز خدای یکتا و اگر دست برندارند از آنچه گویند هر آینه برسد آنان را که کفر ورزیدند از ایشان عذابی دردناک‌'),
(743, 5, 74, 'چرا بازنگردند بسوی خدا و از او آمرزش نخواهند و خدا است آمرزنده مهربان‌'),
(744, 5, 75, 'نیست مسیح پسر مریم جز پیمبری که بگذشت پیش از او پیمبرانی و مادرش زنی است راستگو بودند می‌خوردند خوراک را بنگر چگونه برای ایشان بیان کنیم آیتها را سپس بنگر کجا به دروغ رانده می‌شوند'),
(745, 5, 76, 'بگو آیا پرستش کنید جز خدای آنچه را ندارد برای شما زیان و نه سودی و خدا است شنونده دانا'),
(746, 5, 77, 'بگو ای اهل کتاب فزون نروید در دین خود به ناحقّ و پیروی نکنید خواسته‌های گروهی را که گمراه شدند از پیش و گمراه کردند بسیاری را و گم شدند از راه راست‌'),
(747, 5, 78, 'لعنت شدند آنان که کفر ورزیدند از بنی‌اسرائیل بر زبان داود و عیسی ابن مریم این بدان است که نافرمانی کردند و بودند تجاوزکنندگان‌'),
(748, 5, 79, 'بودند دست برنمی‌داشتند از زشتی که می‌کردند چه زشت است آنچه بودند می‌کردند'),
(749, 5, 80, 'بینی بسیاری از ایشان را دوستی می‌کنند با آنان که کفر ورزیدند چه زشت است آنچه پیش آورده است برای ایشان نفوسشان که خشم گرفت خدا بر ایشان و در عذابند جاودانان‌'),
(750, 5, 81, 'و اگر اینان ایمان می‌آوردند به خدا و پیمبر و آنچه فرستاده شده است بسوی او نمی‌گرفتندشان دوستانی لیکن بسیاری از ایشانند نافرمانان‌'),
(751, 5, 82, 'همانا بیابی سخت‌ترین مردم را در دشمنی با آنان که ایمان آوردند جهودان و آنان که مشرک شدند و همی یابی هر آینه نزدیکترین مردم را در دوستی با آنان که ایمان آوردند کسانی که گویند مائیم ترسایان این بدان است که از ایشانند کشیشان و صومعه‌نشینانی و آنکه ایشان کبر نمی‌ورزند'),
(752, 5, 83, 'و گاهی که بشنوند آنچه را فرود آمده است بر پیمبر بینی دیدگانشان را اشک ریزد بدانچه شناخته‌اند از حقّ گویند پروردگارا همانا ایمان آوردیم پس بنویس ما را با گواهان‌'),
(753, 5, 84, 'و چه شود ما را ایمان نیاریم به خدا و آنچه بیامد ما را از حقّ حالی که طمع داریم که درآرد ما را پروردگار ما با گروه شایستگان‌'),
(754, 5, 85, 'پس پاداششان داد خدا بدانچه گفتند باغهائی که روان است زیر آنها جویها جاودانان در آن و این است پاداش نکوکاران‌'),
(755, 5, 86, 'و آنان که کفر ورزیدند و دروغ پنداشتند آیتهای ما را آنانند یاران دوزخ‌'),
(756, 5, 87, 'ای آنان که ایمان آوردید حرام نکنید پاکهای آنچه را حلال کرده خدا برای شما و تجاوز نکنید که خدا دوست ندارد تجاوزکنندگان را'),
(757, 5, 88, 'و بخورید از آنچه روزی داد شما را خدا حلال پاکیزه و بترسید خداوندی را که شمائید بدو ایمان‌آورندگان‌'),
(758, 5, 89, 'نگیرد شما را خدا به یاوه در سوگندهای شما و لیکن بگیرد شما را به بستن سوگندها پس کفاره آن خوراک ده تن بینوا است از میانه آنچه خوراک دهید خانواده خود را یا پوشاک آنان یا آزادکردن یک بنده و آنکس که ندارد پس روزه سه روز این است کفاره سوگندهای شما هرگاه سوگند خوردید و نگهدارید سوگندهای خویش را چنین بیان کند خدا برای شما آیتهای خود را شاید شکر گزارید'),
(759, 5, 90, 'ای آنان که ایمان آوردید جز این نیست که باده و قمار و بتان و تیرها پلیدی است از کردار شیطان پس دوری گزینید از آن شاید رستگار شوید'),
(760, 5, 91, 'جز این نیست که خواهد شیطان آنکه بیفکند میان شما دشمنی و کینه را در باده و قمار و بازدارد شما را از یاد خدا و از نماز پس آیا هستید دست‌بردارندگان‌'),
(761, 5, 92, 'فرمان برید خدا را و فرمان برید پیمبر را و بترسید پس اگر پشت کردید بدانید که بر فرستاده ما است همانا رساندن آشکار'),
(762, 5, 93, 'نیست بر آنان که ایمان آوردند و کردار شایسته کردند باکی در آنچه بخورند اگر پرهیزکاری کنند و ایمان آرند و کردار شایسته کنند پس پرهیز کنند و ایمان آورند سپس پرهیز کنند و نیکی کنند و خدا دوست دارد نکوکاران را'),
(763, 5, 94, 'ای آنان که ایمان آوردید همانا بیازماید شما را خدا به چیزی از شکار که دریابدش دستهای شما و نیزه‌های شما تا بداند خدا آن را که می‌ترسدش در نهان پس هر کس تجاوز کند پس از این او را است عذابی دردناک‌'),
(764, 5, 95, 'ای آنان که ایمان آوردید نکشید شکار را حالی که در جامه احرامید و آنکس که بکشد آنرا از شما دانسته و خواسته پس کیفری مانند آنچه کشته است از دامهای چهارپا که داوری بدان کند دو تن دادگر از شما قربانیی رسیده به کعبه یا کفاره خوراک بینوایان یا برابر آن روزه تا بچشد کیفر کار خود را درگذشت خدا از آنچه گذشت و آنکس که بازگردد انتقام کشد از او خدا و خداست عزتمند انتقام‌جوی‌'),
(765, 5, 96, 'حلال است برای شما شکار دریا و خوراک آن بهره‌ای برای شما و کاروان و حرام است بر شما شکار دشت مادامی که در لباس احرامید و بترسید خداوندی را که بسوی او گردآورده شوید'),
(766, 5, 97, 'گردانید خدا کعبه آن خانه محترم را ایستادگیی برای مردم و ماه محترم را و قربانی و دامهای قلاّده بگردن را این تا بدانید خدا می‌داند آنچه در آسمانها و آنچه در زمین است و آنکه خدا است به هر چیز دانا'),
(767, 5, 98, 'بدانید که خدا است سخت‌شکنجه و آنکه خدا است به هر آمرزنده مهربان‌'),
(768, 5, 99, 'نیست بر پیغمبر جز رساندن و خدا می‌داند آنچه آشکار و آنچه نهان کنید'),
(769, 5, 100, 'بگو یکسان نیستند پلید و پاکیزه اگر چه تو را شگفت آید فراوانی پلید پس بترسید خدا را ای خردمندان شاید رستگار شوید'),
(770, 5, 101, 'ای آنان که ایمان آوردید نپرسید از چیزهائی که اگر فاش شود برای شما بد آید شما را و اگر بپرسید از آنها هنگامی که فرود آید قرآن فاش شود برای شما درگذشت خدا از آنها و خدا است آمرزنده بردبار'),
(771, 5, 102, 'همانا پرسیدند آنها را گروهی پیش از شما پس گردیدند بدانها کافران‌'),
(772, 5, 103, 'قرار نداده است خدا «بحیره» و نه «سائبه» و نه «وصیله» و نه «حامی» لیکن آنان که کفر ورزیدند می‌بندند بر خدا دروغ را و بیشتر ایشان بخرد درنمی‌یابند'),
(773, 5, 104, 'و اگر گفته شود بدیشان بیائید بسوی آنچه خدا فرستاده است و بسوی پیمبر گویند بس است ما را آنچه یافتیم بر آن پدران خویش را اگرچه پدران ایشان ندانند چیزی را و نه راه برند'),
(774, 5, 105, 'ای آنان که ایمان آوردید بر شما باد خود شما زیان نرساند شما را آنکو گمراه شده است اگر هدایت یافته‌اید به سوی خدا است بازگشت شما همگی پس آگهیتان دهد بدانچه بودید می‌کردید'),
(775, 5, 106, 'ای آنان که ایمان آوردید گواهی میان شما گاهی که برسد یکی از شما را مرگ به هنگام وصیت دو عدالتمند از شما یا دو تن دیگر از غیر شما اگر سفر کردید در زمین پس رسید شما را پیش آمد مرگ بازدارید آنان را پس از نماز تا سوگند یاد کنند به خدا اگر گمان بد بردید که نفروشیمش به هیچ بهائی اگرچه باشد خویشاوند و پنهان ننمائیم شهادت خدا را همانا مائیم در آن هنگام از گناهکاران‌'),
(776, 5, 107, 'و اگر اطّلاع حاصل شد که آنان مستوجب گناهی شدند پس دو دیگر جانشین آنان شوند از آنان که مستوجب شدند بر ایشان آن دو نخستین (یا آن دو سزاوارتران) پس سوگند یاد کنند به خدا که گواهی ما درست‌تر است از گواهی آنان و تجاوز ننمودیم که مائیم در آن هنگام از ستمکاران‌'),
(777, 5, 108, 'این است نزدیکتر که بیارند گواهی را بر گونه آن یا بترسند که ردّ شود سوگندهائی پس از سوگندهای ایشان و بترسید خدا را و بشنوید و خدا هدایت نکند گروه نافرمانان را'),
(778, 5, 109, 'روزی که گردآرد خدا پیامبران را پس گوید چه چیز پاسخ داده شدید گویند نیست علمی ما را همانا توئی تو داننده نهانها'),
(779, 5, 110, 'هنگامی که گفت خدا ای عیسی ابن مریم به یاد آر نعمت مرا بر تو و بر مادر تو گاهی که تأییدت نمودم به روح‌القدس سخن گوئی با مردم در گهواره و هنگام سال خوردگی و گاهی که آموختمت کتاب و حکمت و تورات و انجیل را و هنگامی که می‌آفریدی از گل مانند پیکر پرنده به اذن من پس می‌دمیدی در آن پس می‌شد مرغی به اذن من و بهبودی می‌دادی کور و پیس را به اذن من و هنگامی که برون می‌آوردی مردگان را به اذن من و آن هنگامی که بازداشتم بنی اسرائیل را از تو گاهی که بیامدی ایشان را با نشانیها پس گفتند آنان که کفر ورزیدند از ایشان نیست این جز جادوئی آشکار'),
(780, 5, 111, 'و هنگامی که وحی فرستادم بسوی حواریون که ایمان آرید به من و به فرستاده من گفتند ایمان آوردیم و گواه باش که مائیم مسلمانان‌'),
(781, 5, 112, 'هنگامی که گفتند حواریّون ای عیسی ابن مریم آیا تواند پروردگار تو بفرستد بر ما سفره خوراکی از آسمان گفت بترسید خدا را اگر هستید مؤمنان‌'),
(782, 5, 113, 'گفتند خواهیم از آن خوریم و مطمئن گردد دلهای ما و بدانیم که راست گفته‌ای با ما و باشیم بر آن از گواهان‌'),
(783, 5, 114, 'گفت عیسی بن مریم بار خدایا پروردگارا بفرست بر ما خوان خوراکی از آسمان که عیدی باشد برای آغاز ما و انجام ما و آیتی از تو و روزی ده به ما و توئی بهترین روزی‌دهندگان‌'),
(784, 5, 115, 'گفت خدا هرآینه فرستنده‌ام آن را بر شما تا هر کس کفر ورزد پس از آن از شما عذابش کنم بدانسان که عذاب نکنم بدان هیچکس را از جهانیان‌'),
(785, 5, 116, 'و هنگامی که گفت خدا ای عیسی بن مریم آیا تو گفتی به مردم که بگیرید من و مادرم را خدایانی جز خداوند گفت منزّهی تو نرسد مرا که گویم آنچه را سزاوار من نیست اگر می‌گفتمش همانا می‌دانستیش می‌دانی آنچه را در نهاد من است و ندانم آنچه را در نهان تو است همانا توئی تو دانای نهانها'),
(786, 5, 117, 'نگفتم بدیشان جز آنچه مرا فرمودی که پرستش کنید خدا را پروردگار من و پروردگار شما و بودم گواه بر ایشان مادامی که بودم در ایشان تا هنگامی که مرا برگرفتی تو مراقب ایشان بودی و توئی بر همه چیز گواه‌'),
(787, 5, 118, 'اگر عذابشان کنی ایشانند بندگان تو و اگر بیامرزیشان همانا توئی عزّتمند حکیم‌'),
(788, 5, 119, 'گفت خدا این است روزی که سود دهد راستگویان را راستی ایشان آنان را است باغهائی که جاری است زیر آنها جویها جاودانند در آن همیشه خوشنود است خدا از ایشان و خوشنودند از او این است رستگاری بزرگ‌'),
(789, 5, 120, 'خدای را است پادشاهی آسمانها و زمین و آنچه در آنها است و او است بر همه چیز توانا');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'سپاس خداوندی را که آفرید آسمانها و زمین را و قرار داد تاریکیها و روشنائی را پس آنان که کفر ورزیدند به پروردگار خویش شرک همی‌ورزند'),
(791, 6, 2, 'اوست آنکه آفرید شما را از گل پس گذارد سرآمدی را و سرآمدی نامبرده نزد او است سپس شما تردید می‌کنید'),
(792, 6, 3, 'و او است خدا در آسمانها و در زمین می‌داند نهان و آشکار شما را و می‌داند آنچه را فراهم آرید'),
(793, 6, 4, 'و نمی‌آیدشان آیتی از آیتهای پروردگارشان جز آنکه باشند از آن روی گردانان‌'),
(794, 6, 5, 'همانا دروغ پنداشتند حقّ را گاهی که بیامدشان پس بزودی بیایدشان خبرهای آنچه بودند بدان استهزاءکنندگان‌'),
(795, 6, 6, 'آیا نبینند بسا هلاک کردیم پیش از آنان قرنهائی که توانائیشان دادیم در زمین آنچه توانائیتان ندادیم و فرستادیم آسمان را بر ایشان ریزاریز و گردانیدیم جویها را روان از زیر ایشان پس نابودشان نمودیم به گناهانشان و آوردیم از نو پس از ایشان قرنهائی دگران‌'),
(796, 6, 7, 'و اگر می‌فرستادیم بر تو نامه‌ای در کاغذی که می‌سودندش با دستهای خویش هرآینه می‌گفتند آنان که کفر ورزیدند نیست این جز جادوئی آشکار'),
(797, 6, 8, 'و گفتند چرا فرو نیامده است بر او فرشته‌ای و اگر می‌فرستادیم فرشته‌ای هرآینه می‌گذشت کار پس مهلت داده نمی‌شدند'),
(798, 6, 9, 'و اگر می‌گردانیدیمش فرشته‌ای همانا می‌گردانیدیمش مردی و همی پوشانیدیم بر او آنچه را می‌پوشند'),
(799, 6, 10, 'و همانا استهزاء شد به پیمبرانی که پیش از تو بودند پس فرود آمد بدانان که مسخره کردند از ایشان آنچه بودند بدان استهزاءکنندگان‌'),
(800, 6, 11, 'بگو بگردید در زمین پس بنگرید چگونه بوده است فرجام تکذیب‌کنندگان‌'),
(801, 6, 12, 'بگو از آن کیست آنچه در آسمانها است و زمین بگو از آن خدا نوشت بر خویشتن رحمت را که گردآورد شما را بسوی روز رستاخیز نیست تردیدی در آن آنان که زیانمند کردند خویشتن را ایمان نمی‌آورند'),
(802, 6, 13, 'و از آنِ وی است آنچه آرمیده است در شب و روز و او است شنوای دانا'),
(803, 6, 14, 'بگو آیا جز خدا را برگیرم دوستی آفریننده آسمانها و زمین و او می‌خوراند و خورانده نشود بگو همانا مأمور شدم که نخستین اسلام‌آورنده باشم و نباش البته از شرک‌آورندگان‌'),
(804, 6, 15, 'بگو همانا می‌ترسم اگر نافرمانی کنم پروردگار خود را از عذاب روزی بزرگ‌'),
(805, 6, 16, 'آن کس که گردانیده شود از او در آن روز همانا رحم بدو کرده و آن است رستگاریی آشکار'),
(806, 6, 17, 'و اگر برساندت خدا رنجی پس نیست بازگیرنده آن جز او و اگر رساندت به خیری پس او است بر همه چیز توانا'),
(807, 6, 18, 'و او است نیرومند بر فراز بندگان خویش و او است حکیم دانا'),
(808, 6, 19, 'بگو چه چیز بزرگتر است در گواهی بگو خدا گواه است میان من و شما و وحی شده است بسوی من این قرآن تا بترسانم بدان شما را و هر که را برسد آیا گواهی دهید که با خدا است خدایان دیگری بگو گواهی ندهم بگو جز این نیست که او است خداوند یکتا و همانا بیزارم از آنچه شرک می‌ورزید'),
(809, 6, 20, 'آنان که دادیمشان کتاب را بشناسندش چنانکه بشناسند فرزندان خود را آنان که زیانمند کردند خویشتن را ایمان نیارند'),
(810, 6, 21, 'و کیست ستمگرتر از آنکه ببندد بر خدا دروغی یا دروغ پندارد آیتهای او را همانا رستگار نشوند ستمگران‌'),
(811, 6, 22, 'و روزی که گردشان آریم همگی پس گوئیم بدانان که شرک ورزیدند کجا است شریکان شما آنان که بودید می‌پنداشتید'),
(812, 6, 23, 'پس نبود نیرنگ آنان جز آنکه گفتند به خدا سوگند پروردگار ما که نبودیم شرک‌ورزندگان‌'),
(813, 6, 24, 'بنگر چگونه دروغ گفتند بر خویشتن و گم شد از ایشان آنچه بودند دروغ می‌بستند'),
(814, 6, 25, 'و از ایشان است آنکه گوش فرا می‌دهد بسوی تو و قرار دادیم بر دلهای ایشان پرده‌هائی از آنکه دریابندش و در گوشهای ایشان سنگینی و اگر بینند هر آیتی را ایمان نیارند بدان تا گاهی که آیندت ستیزه‌کنان با تو گویند آنان که کفر ورزیدند نیست این جز افسانه‌های پیشینیان‌'),
(815, 6, 26, 'و ایشان نهی کنند از آن و دوری گزینند از آن و نابود نکنند جز خویشتن را و نمی‌دانند'),
(816, 6, 27, 'و ای کاش می‌دیدی گاهی را که بازداشت شدند بر آتش گفتند کاش برگردانیده می‌شدیم و دروغ نمی‌شمردیم آیتهای پروردگار خود را و می‌شدیم از مؤمنان‌'),
(817, 6, 28, 'بلکه آشکار شد برای ایشان آنچه پنهان کرده بودند از پیش و اگر بازگردانیده شوند همانا برگردند بدانچه نهی شدند از آن و هر آینه آنانند دروغگویان‌'),
(818, 6, 29, 'گفتند نیست آن جز زندگانی دنیای ما و نیستیم ما برانگیختگان‌'),
(819, 6, 30, 'و کاش می‌دیدی گاهی را که بازداشت شدند پیشگاه پروردگارشان گوید آیا نیست این به حقّ گویند بلی سوگند به پروردگار ما گوید پس بچشید عذاب را بدانچه بودید کفر می‌ورزیدید'),
(820, 6, 31, 'همانا آیان کردند آنان که دروغ شمردند ملاقات خدا را تا گاهی که بیامدشان قیامت ناگهان گفتند دریغ ما را بدانچه کوتاهی کردیم در آن و حمل کنند بارهای خود را بر پشتهای خویش چه زشت است آنچه بار برمی‌دارند'),
(821, 6, 32, 'و نیست زندگانی دنیا جز بازیچه و هوسرانی و همانا خانه آخرت است بهتر برای آنان که پرهیزکاری کنند آیا به خرد نمی‌آیید'),
(822, 6, 33, 'همانا دانیم که اندوهگینت کند آنچه گویند و هر آینه تکذیب نمی‌کنند تو را و لیکن ستمگران به آیتهای خدا انکار همی ورزند'),
(823, 6, 34, 'و همانا تکذیب شدند پیمبرانی پیش از تو پس شکیبا شدند بر آنچه تکذیب شدند و آزار کشیدند تا رسیدشان یاری ما و نیست برگرداننده برای سخنان خدا و همانا رسیده است تو را از داستان پیمبران‌'),
(824, 6, 35, 'و اگر گران آید بر تو پشت کردن ایشان پس اگر توانی بیابی سوراخی در زمین یا نردبانی به آسمان پس بیاریشان آیتی و اگر می‌خواست خدا هر آینه گردشان می‌آورد بر هدایت پس نباش از نادانان‌'),
(825, 6, 36, 'جز این نیست که می‌پذیرند از تو آنان که می‌شنوند و مردگان را خدا برانگیزد سپس بسوی او بازگردانیده شوند'),
(826, 6, 37, 'گفتند چرا فرود نیامده است بر او آیتی از پروردگارش بگو همانا خدا توانا است که فرستد آیتی را لیکن بیشتر ایشان نمی‌دانند'),
(827, 6, 38, 'نیست جنبنده‌ای در زمین و نه پرنده‌ای پروازکننده با بالهای خود جز ملتهائی همانند شما فروگذار نکردیم در کتاب از چیزی سپس بسوی پروردگارشان گردآورده شوند'),
(828, 6, 39, 'و آنان که تکذیب کردند آیتهای ما را کران و گنگانند در تاریکیها هر که را خدا خواهد گمراه کند و هر که را خواهد بگرداندش بر راهی راست‌'),
(829, 6, 40, 'بگو آیا دیدستید که اگر بیاید شما را عذاب خدا یا بیاید شما را قیامت آیا جز خدا را خوانید اگر هستید راستگویان‌'),
(830, 6, 41, 'بلکه او را می‌خوانید پس برمی دارد از شما آنچه را خوانیدش بسوی آن اگر خواهد و فراموش می‌کنید آنچه را شرک می‌ورزید'),
(831, 6, 42, 'و همانا فرستادیم بسوی مللی پیش از تو پس گرفتیمشان به پریشانی و رنجوری شاید زاری کنند'),
(832, 6, 43, 'چرا هنگامی که بیامدشان خشم ما زاری نکردند لیکن سنگین شد دلهای آنان و آراست برای ایشان شیطان آنچه را بودند عمل می‌کردند'),
(833, 6, 44, 'پس هنگامی که فراموش کردند آنچه را تذکّر بدان داده شدند بگشودیم بر ایشان درهای همه چیز را تا گاهی که شاد شدند بدانچه داده شدند گرفتیمشان ناگهان ناگاه ایشانند سرافکندگان‌'),
(834, 6, 45, 'پس بریده شد دنباله گروهی که ستم کردند و سپاس خدای را پروردگار چهانیان‌'),
(835, 6, 46, 'بگو آیا نگریسته‌اید که اگر خدای بگیرد گوش و دیدگان شما را و مُهر زند بر دلهای شما کیست خدائی جز خداوند که برگرداند آنها را به شما بنگر چگونه می‌گردانیم آیتها را سپس ایشان روی برمی‌گردانند'),
(836, 6, 47, 'بگو آیا دیدستید اگر بیاید شما را عذاب خدا ناگهان یا آشکارا آیا نابود می‌شوند جز گروه ستمکاران‌'),
(837, 6, 48, 'و نمی‌فرستیم فرستادگان را جز نویددهندگان و ترسانندگان پس آنان که ایمان آورند و اصلاح کنند نیست بر ایشان بیمی و نه اندوهگین شوند'),
(838, 6, 49, 'و آنان که تکذیب کنند آیتهای ما را برسد بدیشان عذاب بدانچه بودند نافرمانی می‌کردند'),
(839, 6, 50, 'بگو نمی‌گویم برای شما که نزد من است گنجهای خدا و نه ناپیدا را دانم و نمی‌گویم برای شما منم فرشته پیروی نمی‌کنم جز آنچه را به من وحی شود بگو آیا یکسان است کور و بینا آیا نمی‌اندیشید'),
(840, 6, 51, 'و بیم ده بدان آنان را که می‌ترسند گردآورده شوند بسوی پروردگار خود نیستشان دوست و نه شفاعتگری جز او شاید ایشان پرهیز کنند'),
(841, 6, 52, 'و از خود نران آنان را که می‌خوانند پروردگار خود را بامدادان و شامگاه خواهان روی اویند نیست بر تو از حساب ایشان چیزی و نه از حساب تو است بر ایشان چیزی که برانی آنان را پس بشوی از ستمگران‌'),
(842, 6, 53, 'و بدینگونه آزمودیم برخی از ایشان را به برخی تا بگویند آیا اینانند که خدا منّت نهاده است بر ایشان از میان ما آیا نیست خدا داناتر به سپاسگزاران‌'),
(843, 6, 54, 'و هرگاه درآیند بر تو آنان که ایمان آورند به آیتهای ما بگو سلام بر شما نوشت پروردگار شما بر خویشتن رحمت را که هر کس انجام دهد از شما بدی را به نادانی سپس بازگردد پس از آن و اصلاح کند همانا او است آمرزنده مهربان‌'),
(844, 6, 55, 'چنین تفصیل دهیم آیتها را و تا پدیدار شود راه گنهکاران‌'),
(845, 6, 56, 'بگو همانا نهی شدم از آنکه پرستش کنم آنان را که می‌خوانید جز خدا بگو پیروی نکنم خواسته‌های شما را که گمراه شوم در آن هنگام و نباشم از هدایت‌یافتگان‌'),
(846, 6, 57, 'بگو همانا من بر نشانه‌ای از پروردگار خویشم و شما تکذیب آن کردید نیست نزد من آنچه شتاب دارید بدان نیست حکم جز از آن خدا میسراید حقّ را و او است بهتر جداسازندگان‌'),
(847, 6, 58, 'بگو اگر نزد من بود آنچه بدان شتاب می‌کنید همانا می‌گذشت کار میان من و شما و خدا داناتر است به ستمگران‌'),
(848, 6, 59, 'و نزد او است کلیدهای ناپیدا که نداند آنها را جز او و می‌داند آنچه را در دشت و دریا است و نیفتد برگی جز آنکه بداندش و نه دانه‌ای در تاریکیهای زمین و نه تری و نه خشکی مگر در کتابی است آشکار'),
(849, 6, 60, 'و او است آنکه دریابد (بمیراند) شما را در شب و می‌داند آنچه را فراآورید در روز سپس برانگیزاند شما را در آن تا بگذرد سرآمدی معین سپس بسوی او است بازگشت شما پس آگهی دهد شما را بدانچه بودید عمل می‌کردید'),
(850, 6, 61, 'و او است نیرومند بر فراز بندگان خود و می‌فرستد بر شما نگهبانانی تا گاهی که برسد یکی از شما را مرگ دریابندش فرستادگان ما و آنان فروگذار نمی‌کنند'),
(851, 6, 62, 'سپس بازگردانیده شوند بسوی خدا سرپرست ایشان حقّ همانا از آن وی است حکم و اوست شتابنده‌ترین حسابگران‌'),
(852, 6, 63, 'بگو که شما را رهائی دهد از تاریکیهای دشت و دریا خوانیدش به زاری و نهان که اگر رها ساختی ما را از این باشیم البته از سپاسگزاران‌'),
(853, 6, 64, 'بگو خدا رهائی دهد شما را از آن و از هر اندوهی سپس شما بدو شرک همی ورزید'),
(854, 6, 65, 'بگو او است توانا بر آنکه بیانگیزاند بر شما عذابی از فراز شما یا از زیر پایهای شما یا به هم اندازد شما را گروه‌هائی و بچشاند بعضی را از شما آیان بعضی بنگر چگونه می‌گردانیم آیتها را شاید دریابند ایشان‌'),
(855, 6, 66, 'و دروغش شمردند قوم تو و آن است حقّ بگو نیستیم بر شما وکیل‌'),
(856, 6, 67, 'برای هر خبری است قرارگاهی و بزودی می‌دانید'),
(857, 6, 68, 'و هرگاه بینی آنان را که فروروند در آیتهای ما پس روگردان از ایشان تا فرو روند در داستانی دیگر و اگر فراموشی آردت شیطان پس منشین پس از یادآوردن با گروه ستمگران‌'),
(858, 6, 69, 'و نیست بر آنان که پرهیز می‌کنند از حساب ایشان چیزی و لیکن یادآوردنی است شاید بترسند ایشان‌'),
(859, 6, 70, 'و بگذارید آنان را که گرفتند دین خود را بازی و هوسرانی و بفریفت ایشان را زندگانی دنیا و تذکّر ده بدان تا سرافکنده نشود کسی بدانچه دست آورده است نیستش جز خدا دوست و نه شفاعتگری و اگر به جای خود دهد هر بدلی پذیرفته نشود از او آنانند که سرافکنده شدند بدانچه دست آوردند ایشان را است نوشابه‌ای از آب جوشان و شکنجه‌ای سخت بدانچه بودند کفر می‌ورزیدند'),
(860, 6, 71, 'بگو آیا خوانیم جز خدا آنچه را نه سود دهد ما را و نه آیان رساند و برگردیم بر پاشنه‌های خود پس از آنکه راهنمائی کرد ما را خدا مانند آنکه فریفتندش شیاطین در زمین سراسیمه وی را یارانی است که خوانندش بسوی هدایت به نزد ما بیا بگو همانا هدایت خدا است هدایت و امر شدیم که گردن نهیم برای پروردگار جهانیان‌'),
(861, 6, 72, 'و آنکه بپا دارید نماز را و بترسیدش و او است آنکه بسوی او گردآورده شوید'),
(862, 6, 73, 'و او است آنکه آفرید آسمانها و زمین را به حقّ و روزی که گوید بشو پس بشود گفتار او است حقّ و از آنِ وی است پادشاهی روزی که دمیده شود در صور دانای نهان و پدیدار و اوست حکیم دانا'),
(863, 6, 74, 'و هنگامی که گفت ابراهیم به پدر خود آزر آیا می‌گیری بتان را خدایانی همانا می‌بینم تو و قومت را در گمراهی آشکار'),
(864, 6, 75, 'و بدینسان بنمایانیم به ابراهیم پادشاهیهای آسمانها و زمین را و تا بگردد از یقین‌دارندگان‌'),
(865, 6, 76, 'آنگاه که فراگرفتش تاریکی شب دید ستاره‌ای را گفت این است پروردگار من و هنگامی که فرو نشست گفت دوست ندارم فروروندگان را'),
(866, 6, 77, 'و هنگامی که دید ماه را تابنده گفت این است پروردگار من و گاهی که درون شد گفت اگر رهبریم نکند پروردگارم همانا می‌شوم از گروه گمراهان‌'),
(867, 6, 78, 'تا گاهی که دید خورشید را درخشان گفت این است پروردگار من این بزرگتر است سپس هنگامی که ناپدید شد گفت ای قوم همانا بیزارم از آنچه شما شرک می‌ورزید'),
(868, 6, 79, 'همانا برگرداندم روی خود را بسوی آنکه آفرید آسمانها و زمین را یکتاپرست و نیستم از شرک‌ورزندگان‌'),
(869, 6, 80, 'و ستیزه کردند با وی قومش گفت آیا با من ستیزه کنید در خدا حالی که هدایتم کرده است و نترسم آنچه را شرک بدان ورزید جز آنکه بخواهد پروردگار من چیزی را فراگرفته است پروردگارم همه چیز را به دانش آیا یاد نمی‌آورید'),
(870, 6, 81, 'و چگونه ترسم آنچه را شرک ورزیدید و نترسید از آنکه شرک ورزیدید به خدا آن را که نفرستاده است بدان بر شما فرمانروائی‌ای پس کدام یک از دو گروه سزاوارترند به ایمن‌شدن اگر می‌دانید'),
(871, 6, 82, 'آنان که ایمان آوردند و نیالودند ایمان خویش را به ستم برای آنان است ایمنی و ایشانند هدایت‌شدگان‌'),
(872, 6, 83, 'و این است حجّت ما که دادیمش به ابراهیم بر قوم او بالا بریم پایه‌های هر که را خواهیم همانا پروردگار تو است حکیم دانا'),
(873, 6, 84, 'و ارزانی داشتیم بدو اسحق و یعقوب را هر کدام هدایت کردیم و نوح را هدایت کردیم از پیش و از نژاد او داود و سلیمان و ایوب و یوسف و موسی و هارون را و بدینسان پاداش دهیم نیکوکاران را'),
(874, 6, 85, 'و زکریا و یحیی و عیسی و اِلیاس را هرکدام از شایستگان‌'),
(875, 6, 86, 'و اسمعیل و اَلیَسع و یونس و لوط و هر کدام را برتری دادیم بر جهانیان‌'),
(876, 6, 87, 'و از پدران ایشان و فرزندان ایشان و برادران ایشان و برگزیدیمشان و رهبریشان نمودیم بسوی راهی راست‌'),
(877, 6, 88, 'این است هدایت خدا که رهبری کند بدان هر که را خواهد از بندگان خویش و اگر شرک می‌ورزیدند هرآینه سترده می‌شد از ایشان آنچه بودند می‌کردند'),
(878, 6, 89, 'آنانند که بدیشان دادیم کتاب و حکم و پیمبری را پس اگر بدان کفر ورزند آنان همانا نگهبان کردیم بر آن گروهی را که نیستند بدان کفرورزندگان‌'),
(879, 6, 90, 'آنانند که رهبریشان نمود خدا پس به رهبریشان پیروی جوی بگو نخواهم از شما بر آن پاداشی نیست آن جز یادآورییی برای جهانیان‌'),
(880, 6, 91, 'و ارج ننهادند خدا را شایان ارجمندیش هنگامی که گف‌تند نفرستاده است خدا بر بشری چیزی را بگو که فرستاده است کتابی را که بیاورد موسی روشنائی و هدایتی برای مردم که می‌گردانیدش کاغذهائی که آنها را آشکار کنید و نهان کنید بسیاری را و آموخته شدید آنچه را نمی‌دانستید نه شما و نه پدران شما بگو خدا سپس بگذارشان در فرورفتگی (سرگرمی) خویش بازی کنند'),
(881, 6, 92, 'و این است کتابی فرستادیمش فرخنده تصدیق‌کننده آنچه پیش روی او است و تا بترسانی مادرشهرها و آنان را که پیرامون آنند و آنان که ایمان آرند به آخرت ایمان آرند بدان حالی که بر نماز خویشند مواظبت‌کنندگان‌'),
(882, 6, 93, 'و کیست ستمگرتر از آنکه ببندد بر خدا دروغی یا گوید وحی رسیده است به من حالی که وحی نشده است بسوی او چیزی و کسی که گوید بزودی می‌فرستم همانند آنچه خدا فرستاده است و اگر (کاش) می‌دیدی هنگامی را که ستمگران در گردابهای مرگند و فرشتگان گشاینده‌اند دستهای خویش را برون دهید جانهای خود را امروز کیفر شوید به عذاب خواری بدانچه می‌گفتید بر خدا نادرست را و بودید از آیتهای او سرکشی می‌کردید'),
(883, 6, 94, 'و همانا آمدید ما را تنها چنانکه آفریدیمتان نخستین‌بار و بازگذاشتید آنچه را به شما دادیم پشت سر خویش و نبینیم با شما شفیعان شما را آنان که پنداشتیدشان در شما شریکان همانا بگسیخت میان شما و گم شد از شما آنچه بودید می‌پنداشتید'),
(884, 6, 95, 'همانا خدا است شکافنده دانه و هسته برون آرد زنده را از مرده و برون آرنده است مرده را از زنده این است خدای شما پس کجا به دروغ رانده شوید'),
(885, 6, 96, 'شکافنده صبح و گردانید شب را آرامشی و مهر و ماه را شمارگرانی این است تقدیر خداوند عزیز دانا'),
(886, 6, 97, 'و او است آنکه نهاد برای شما ستارگان را تا هدایت شوید در تاریکیهای دشت و دریا همانا تفصیل دادیم آیتها را برای گروهی که می‌دانند'),
(887, 6, 98, 'و او است آنکه آفرید شما را از یک تن پس پایدار و ناپایداری همانا تفصیل دادیم آیتها را برای گروهی که درمی‌یابند'),
(888, 6, 99, 'و او است آنکه فرستاد از آسمان آبی پس برون آوردیم با آن روئیدنی همه چیز را پس برون آوردیم از آن سبزه‌ای که برون آریم از آن دانه‌ای انبوه و از خرمابن از شکوفه آن خوشه‌ای نزدیک (فرود) و باغستانی از انگورها و زیتون و انار همانند و گوناگون بنگرید به میوه آن گاهی که باردهد و رسیدن آن همانا در این است آیتهائی برای گروهی که ایمان آورند'),
(889, 6, 100, 'و قرار دادند برای خدا شریکانی جنی که آفریدستشان و به هم بافتند برایش پسران و دخترانی به نادانی منزه و برتر است خدا از آنچه می‌ستایند'),
(890, 6, 101, 'پدیدآرنده آسمانها و زمین چگونه وی را فرزندی باشد و نبوده است برای او همسری و آفریده است همه چیز را و او است به هر چیزی دانا'),
(891, 6, 102, 'این است خدا پروردگار شما نیست خدائی جز او آفریننده همه چیز پس بپرستیدش و او است بر همه چیز نگهبان‌'),
(892, 6, 103, 'درنیابندش دیدگان و او دریابد دیدگان را و او است بخشنده آگاه‌'),
(893, 6, 104, 'همانا بیامد شما را بینشهائی از پروردگار شما پس آنکه بینا شود برای خویشتن است و آنکه نابینا شود بر خویشتن است و نیستم من بر شما نگهبان‌'),
(894, 6, 105, 'و بدینسان بگردانیم آیتها را و تا گویند درس‌خوانده‌ای و تا بیان کنیمش برای گروهی که می‌دانند'),
(895, 6, 106, 'پیروی کن آنچه را وحی شده است بسوی تو از پروردگارت نیست خدائی جز او و روی گردان از شرک‌ورزان‌'),
(896, 6, 107, 'و اگر می‌خواست خدا شرک نمی‌ورزیدند و نگردانیدیمت بر آنان نگهبان و نیستی تو بر ایشان وکیل‌'),
(897, 6, 108, 'و دشنام ندهید آنان را که جز خدا می‌خوانند تا دشنام دهند خدا را ستمگرانه به نادانی بدینگونه آراستیم برای هر گروهی کردار ایشان را سپس بسوی پروردگار ایشان است بازگشتشان تا آگهیشان دهد بدانچه بودند می‌کردند'),
(898, 6, 109, 'و سوگند یاد نمودند به خدا سخت‌ترین سوگندهای خود را که اگر بیایدشان آیتی هرآینه ایمان آرند بدان بگو جز این نیست که آیتها نزد خدا است و چه دانید (یا چه آگهیتان دهد) گاهی که آنها آیند ایمان نیارند ایشان‌'),
(899, 6, 110, 'و باژگون سازیم دلها و دیدگان ایشان را چنانکه ایمان نیاوردند بدان نخستین بار و بگذاریمشان در سرکشی خود فروروند'),
(900, 6, 111, 'و اگر فرستیم بسوی ایشان فرشتگان را و سخن گویند با ایشان مردگان و گرد آریم بر ایشان همه چیز را روبروی نیستند که ایمان آرند جز آنکه بخواهد خدا و لیکن بیشتر ایشان نمی‌دانند'),
(901, 6, 112, 'و بدینسان قرار دادیم برای هر پیمبری دشمنی شیاطین آدمی و پری که وحی فرستند بعضی از ایشان بسوی بعضی سخنان بیهوده را بفریب و اگر می‌خواست پروردگار تو نمی‌کردندش پس بگذارشان به آنچه دروغ می‌بندند'),
(902, 6, 113, 'و تا بشنوندش دلهای آنان که ایمان نمی‌آورند به آخرت و تا پسندش دارند و تا فراهم آرند از گناه آنچه ایشانند فراهم‌کنندگان‌'),
(903, 6, 114, 'آیا جز خدا داوری جویم و اوست آنکه فرستاد بسوی شما کتاب را به تفصیل و آنان که دادیمشان کتاب را دانند که آن است فرود آمده از پروردگار تو به حق پس نباش از شک‌کنندگان‌'),
(904, 6, 115, 'و انجام یافت سخن پروردگار تو به راستی و داد نیست برگرداننده‌ای برای سخنان او و او است شنونده دانا'),
(905, 6, 116, 'و اگر فرمانبری بیشتر آنان را که در زمینند گمراهت کنند از راه خدا پیروی نمی‌کنند جز گمان را و نیستند جز پندارندگان‌'),
(906, 6, 117, 'همانا پروردگار تو داناتر است بدانکه گمراه شود از راه او و او است داناتر به هدایت‌شدگان‌'),
(907, 6, 118, 'پس بخورید از آنچه برده شده است نام خدا بر آن اگر هستید به آیتهای او ایمان‌آورندگان‌'),
(908, 6, 119, 'و چه شود شما را نخورید از آنچه نام خدا بر آن برده شده است حالی که تفصیل داده است برای شما آنچه را حرام کرده است بر شما جز آنچه ناگزیر شوید بدان و همانا بسیاری گمراه می‌کنند به هوسهای خود به نادانی و هرآینه پروردگار تو داناتر است به تجاوزکنندگان‌'),
(909, 6, 120, 'و رها کنید آشکار گناه و نهانش (یا برون گناه و درونش را) همانا آنان که فراهم آورند گناه را بزودی کیفر شوند بدانچه بودند فراهم می‌آوردند'),
(910, 6, 121, 'و نخورید از آنچه برده نشده است نام خدا بر آن و هرآینه آن است نافرمانی و همانا شیاطین وحی کنند بسوی دوستان خود تا درستیزند با شما و اگر فرمان برید ایشان را هرآینه باشید شرک‌ورزندگان‌'),
(911, 6, 122, 'آیا آنکه مرداری بود پس زنده‌اش کردیم و قرار دادیم برای او روشنائی که راه رود بدان در مردم همانند آن است که در تاریکیها نیست برون آینده از آنها بدینسان آراسته شد برای کافران آنچه بودند می‌کردند'),
(912, 6, 123, 'و بدینسان نهادیم در هر شهر سران گنهکارانش را تا نیرنگ آرند در آن و نیرنگ نیاورند جز بر خویشتن و درنمی‌یابند'),
(913, 6, 124, 'و هرگاه بیایدشان آیتی گویند ایمان نیاریم تا آورده شویم همانند آنچه آورده شدند فرستادگان خدا خدا داناتر است کجا نهد پیمبری خویش را بزودی رسد آنان را که گناه کردند خواریی نزد خدا و عذابی سخت بدانچه بودند نیرنگ می‌باختند'),
(914, 6, 125, 'آن را که خواهد خدا هدایت کند بگشاید سینه او را برای اسلام و آن را که خواهد گمراه سازد بگرداند سینه‌اش را تنگ فشرده چنانکه گوئی سخت می‌رود به آسمان بدینسان نهد خدا پلید را بر آنان که ایمان نمی‌آورند'),
(915, 6, 126, 'و این است راه پروردگار تو راست همانا تفصیل دادیم آیتها را برای گروهی که یادآور شوند'),
(916, 6, 127, 'آنان را است خانه آرامش نزد پروردگار خویش و او است دوست ایشان بدانچه بودند می‌کردند'),
(917, 6, 128, 'و روزی که گردشان آرد همگی ای گروه پری همانا فراوان گرفتید از آدمی و گفتند دوستان ایشان از آدمیان پروردگارا بهره‌مند شدند بعضی از ما به بعضی و رسیدیم سرآمدی را که برای ما نهادی گفت آتش است جایگاه شما جاودانان در آن جز آنچه خدا خواهد همانا پروردگار تو است حکیم دانا'),
(918, 6, 129, 'و بدینسان دوست گردانیم بعضی ستمگران را با بعضی بدانچه بودند فراهم میکردند'),
(919, 6, 130, 'ای گروه پری و آدمی آیا نیامد شما را فرستادگانی از شما که بسرایند بر شما آیتهای مرا و بترسانند شما را از رسیدنتان بدین روز گفتند گواهی دادیم بر خویشتن و بفریفتشان زندگانی دنیا و گواهی دادند بر خویش که بودند کافران‌'),
(920, 6, 131, 'این بدان است که نیست پروردگار تو نابودکننده شهرها به ستم حالی که باشند مردم آنها بی‌خبران‌'),
(921, 6, 132, 'و هرکدام را پایه‌هائی است از آنچه کردند و نیست پروردگار تو غافل از آنچه می‌کنند'),
(922, 6, 133, 'و پروردگار تو است بی‌نیاز مهربان اگر خواهد ببرد شما را و جانشین سازد پس از شما هرچه را خواهد چنانکه پدید آورد شما را از نژاد گروه دگران‌'),
(923, 6, 134, 'همانا وعده‌های شما است آینده و نیستید شما به عجزآرندگان‌'),
(924, 6, 135, 'بگو ای قوم بکنید هر آنچه توانید که منم کننده زود است بدانید که را است پایان خانه همانا رستگار نشوند ستمگران‌'),
(925, 6, 136, 'و قرار دادند برای خدا از آنچه بیافریده است از کشت و دامها بهره‌ای پس گفتند این برای خدا بر پندار ایشان و این برای شریکان پس آنچه برای شریکان ایشان است نرسد به خدا و آنچه برای خدا است برسد به شریکان ایشان چه زشت است آنچه حکم می‌کند'),
(926, 6, 137, 'و بدینسان آراستند برای بسیاری از مشرکان کشتن فرزندانشان را شریکان ایشان تا بلغزانندشان و تا آشفته سازند بر ایشان دینشان را و اگر می‌خواست خدا نمی‌کردندش پس بگذار ایشان را با آنچه دروغ می‌بندند'),
(927, 6, 138, 'و گفتند این است دامها و کشتی بازداشت شده نچشدش جز آنکه خواهیم پندار ایشان و دامهائی که حرام است پشتهای آنها و دامهائی که نبرند نام خدا را بر آنها به دروغ‌بستن بر او بزودی کیفرشان دهد بدانچه بودند دروغ می‌بستند'),
(928, 6, 139, 'و گفتند آنچه در شکمهای این دامها است مختص مردان ما است و حرام است بر همسران ما و اگر مرداری باشد پس ایشانند در آن شریکان بزودی کیفرشان دهد ستودن ایشان را همانا او است حکیم دانا'),
(929, 6, 140, 'همانا زیان کردند آنان که کشتند فرزندان خود را بی خردانه به نادانی و حرام کردند آنچه را خدا روزیشان داده است به دروغ‌بستن بر خدا همانا گمراه شدند و نبودند از هدایت‌شدگان‌'),
(930, 6, 141, 'و او است آنکه پدید آورده است باغهای افراشته و ناافراشته و خرمابن و کشت‌زار حالی که گوناگون است میوه (یا مزه) آن و زیتون و انار را همانند و ناهمانند بخورید از میوه آن هنگامی که میوه دهد و بپردازید بهره آن را روز درویدنش و اسراف نکنید که او دوست ندارد اسراف‌کنندگان را'),
(931, 6, 142, 'و از دامها باربرنده و بستری را بخورید از آنچه روزی داده است شما را خداوند و پیروی نکنید گامهای شیطان را که او است برای شما دشمنی آشکار'),
(932, 6, 143, 'هشت جفت از میش دو و از بزغاله دو بگو آیا دو نر را حرام کرده است یا دو ماده را یا آنچه فراگرفته است زهدانهای دو ماده آگهی دهید مرا به دانائی اگر هستید راستگویان‌'),
(933, 6, 144, 'و از شتر دو و از گاو دو بگو آیا دو نر را حرام کرد یا دو ماده را یا آنچه را فراگرفت زهدانهای دو ماده یا بودید شما گواهان هنگامی که اندرز داد شما را خدا بدین پس کیست ستمگرتر از آنکه ببندد بر خدا دروغی را تا گمراه کند مردم را به نادانی همانا خدا هدایت نکند گروه ستمکاران را'),
(934, 6, 145, 'بگو نمی‌یابم در آنچه به من وحی شده است حرامی را برخورنده‌ای که بخوردش جز آنکه مردار باشد یا خونی ریخته یا گوشت خوک که آن است پلید یا نافرمانی که نام جز خدا بر آن برده شده است پس آنکه ناچار شود نه ستمکار و نه تجاوزکننده همانا پروردگار تو است آمرزنده مهربان‌'),
(935, 6, 146, 'و بر آنان که جهود شدند حرام کردیم هر ناخن‌داری را و از گاو و گوسفند حرام کردیم بر ایشان پیه‌های آنها را جز آنچه برگرفته است پشتهای آنها یا پشکلدانها یا آنچه آمیخته است با استخوان این را کیفر بدیشان دادیم به ستمگریشان و هرآینه مائیم راستگویان‌'),
(936, 6, 147, 'پس اگر تکذیب کردند بگو همانا پروردگار شما است خداوند رحمتی پهناور و برنگردد خشمش از گروه گنهکاران‌'),
(937, 6, 148, 'زود است گویند آنان که شرک ورزیدند اگر می‌خواست خدا مشرک نمی‌شدیم ما و نه پدران ما و نه حرام می‌کردیم چیزی را بدینسان تکذیب کردند آنان که پیش از ایشان بودند تا چشیدند خشم ما را بگو آیا نزد شما دانشی است که برون آریدش برای ما پیروی نمی‌کنید جز پندار را و نیستید شما جز گمان‌آوران‌'),
(938, 6, 149, 'بگو خدای را است حجّت رسا و اگر می‌خواست هرآینه رهبری می‌کرد شما را همگی‌'),
(939, 6, 150, 'بگو بیارید گواهان خویش را که گواهی می‌دهند آنکه خدا حرام کرده است این را پس اگر گواهی دادند تو گواهی نده با ایشان و پیروی نکن هوسهای آنان را که تکذیب نمودند آیتهای ما را و آنان که ایمان نمی‌آورند به آخرت و ایشانند به پروردگار خویش شرک‌ورزان‌'),
(940, 6, 151, 'بگو بیائید بخوانم بر شما آنچه را که حرام کرده است پروردگار شما بر شما که شرک نورزید بدو چیزی را و به پدر و مادر نکوئی کنید و نکشید فرزندان خویش را از تنگدستی ما روزی دهیم شما و ایشان را و گرد ناشایسته‌ها (فواحش) نگردید آنچه آشکار است از آنها و آنچه نهان و نکشید تنی را که خدا حرام کرده است جز به حق این است آنچه خدا به شما اندرز داده است شاید به خرد یابید'),
(941, 6, 152, 'و نزدیک نشوید به دارائی یتیم جز بدانچه نکوتر است تا برسد به نیروهای (به جوانی) خویش خویش و بپردازید پیمانه و سنجش را به داد تکلیف نکنیم کسی را جز به مقدار توانائیش و هرگاه سخنی گفتید دادگری کنید اگرچه باشد خویشاوند و به پیمان خدا وفا کنید این است آنچه توصیه کرده است شما را بدان شاید یادآور شوید'),
(942, 6, 153, 'و آنکه این است راه من راست پس پیرویش کنید و پیروی نکنید راه‌ها را که پراکنده‌تان کند از راه او بدین توصیه کرده است شما را شاید پرهیزکاری کنید'),
(943, 6, 154, 'سپس دادیم به موسی کتاب را تمامی بر آنکه نکوکاری کرد (یا بر آنچه نکوئی کرد) و تفصیلی برای همه چیز و هدایت و رحمتی شاید به ملاقات پروردگار خود ایمان آرند'),
(944, 6, 155, 'و این است کتابی فرستادیمش فرخنده پس پیرویش کنید و پرهیزکاری کنید شاید رحم آورده شوید'),
(945, 6, 156, 'تا نگوئید جز این نیست که فرود آورده شد کتاب بر دو گروه پیش از ما و بودیم از درس گرفتنشان بی‌خبران‌'),
(946, 6, 157, 'یا گوئید اگر بر ما فرستاده می‌شد کتاب هر آینه بودیم هدایت‌یافته‌تر از ایشان اینک بیامد شما را نشانی از پروردگار شما و هدایت و رحمتی پس کیست ستمگرتر از آنکه دروغ پندارد آیتهای خدا را و بازدارد از آنها بزودی پاداش دهیم آنان را که بازمی دارند از آیتهای ما عذاب زشت را بدانچه بودند بازمی‌داشتند'),
(947, 6, 158, 'آیا انتظار دارند جز آنکه بیایندشان فرشتگان یا بیاید پروردگار تو یا بیاید پاره‌ای از آیات پروردگار تو روزی که می‌آید پاره‌ای از آیات پروردگار تو سود ندهد کسی را ایمان آوردنش اگر ایمان نیاورده است از پیش یا نیندوخته است در ایمان خود خبری را بگو پس منتظر باشید که مائیم منتظران‌'),
(948, 6, 159, 'همانا آنان که پراکنده ساختند دین خود را و شدند گروهائی نیستی تو از ایشان در چیزی جز این نیست که کار آنان با خدا است پس آگهیشان دهد بدانچه بودند می‌کردند'),
(949, 6, 160, 'آن کس که نکوئی آورد وی را است ده برابر آن و آن کس که زشتی آورد کیفر نشود جز همانند آن و ایشان ستم نمی‌شوند'),
(950, 6, 161, 'بگو همانا مرا هدایت کرده است پروردگارم بسوی راهی راست دینی استوار آئین ابراهیم یکتاپرست و نبوده است از شرک‌ورزان‌'),
(951, 6, 162, 'بگو همانا نماز من و پرستش من و زندگیم و مرگم از آن خداوند است پروردگار جهانیان‌'),
(952, 6, 163, 'نیست انبازی برای او و بدان مأمور شدم و منم نخستین اسلام‌آرندگان‌'),
(953, 6, 164, 'بگو آیا جز خدا پروردگاری جویم و او است پروردگار همه چیز و نیندوزد هر کسی جز بر خویشتن و برندارد باربری بار دگری را پس بسوی پروردگار شما است بازگشت شما تا آگهیتان دهد بدانچه بودید در آن اختلاف می‌کردید'),
(954, 6, 165, 'و او است آنکه گردانید شما را جانشینان زمین و برتری داد گروهی از شما را بر گروهی پایه‌هائی تا بیازمایدتان در آنچه به شما داده است همانا پروردگار تو است زودشکنجه (شتابنده در عقوبت) و همانا او است آمرزنده مهربان‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'المص‌'),
(956, 7, 2, 'کتابی است که فرستاده شده است بسوی تو پس نباشد در سینه‌ات تنگیی از آن تا بترسانی بدان و یادآوردنی است برای مؤمنان‌'),
(957, 7, 3, 'پیروی کنید آنچه را فرستاده شده است بسوی شما از پروردگارتان و پیروی نکنید جز آن دوستانی را چه کم یادآور می‌شوید'),
(958, 7, 4, 'و بسا شهری که نابودش ساختیم پس بیامدش خشم ما شبانگاه یا به خواب نیمروز بودند'),
(959, 7, 5, 'پس نبود سخن ایشان هنگامی که بیامدشان خشم ما جز آنکه گفتند همانا بودیم ما ستمگران‌'),
(960, 7, 6, 'پس خواهیم پرسید آنان را که به سویشان فرستاده شد و خواهیم پرسید از فرستادگان‌'),
(961, 7, 7, 'سپس هرآینه فرامی‌خوانیم بر ایشان به دانش و نبودیم ما غائبان‌'),
(962, 7, 8, 'و سنجش در آن روز حق است پس آنکه سنگین شود ترازوهایش آنانند رستگاران‌'),
(963, 7, 9, 'و آنکه سبک گردد ترازوهایش آنانند که زیان کردند خویشتن را بدانچه بودند به آیتهای ما ستم می‌کردند'),
(964, 7, 10, 'همانا جایگزین ساختیم شما را در زمین و نهادیم در آن روزیهائی برای شما چه کم شکرگزارید'),
(965, 7, 11, 'و همانا آفریدیمتان پس صورتگریتان کردیم و سپس گفتیم به فرشتگان سجده کنید برای آدم پس سجده کردند جز ابلیس که نشد از سجده‌کنندگان‌'),
(966, 7, 12, 'گفت چه چیز بازداشتت که سجده نیاوردی گاهی که فرمودیمت گفت که من بهترم از او مرا از آتش آفریدی و او را از گل‌'),
(967, 7, 13, 'گفت فرود شو از آن نرسد تو را که کبر ورزی در آن پس برون شو که توئی از سرافکندگان‌'),
(968, 7, 14, 'گفت مهلتم ده تا روزی که برانگیخته شوند'),
(969, 7, 15, 'گفت همانا توئی از مهلت‌دادگان‌'),
(970, 7, 16, 'گفت پس بدانکه مرا گمراه کردی همانا در کمینشان نشینم بر راه راست تو'),
(971, 7, 17, 'سپس بر ایشان درآیم از پیش رویشان و از پشت سرشان و از راستها و چپهای ایشان و نیابی بیشتر ایشان را شکرگزاران‌'),
(972, 7, 18, 'گفت برون شو از آن نکوهیده سرافکنده هر آنکه تو را پیروی کند از ایشان همانا پر سازم دوزخ را از شما همگی‌'),
(973, 7, 19, 'و ای آدم بیارام تو و همسرت در بهشت پس بخورید از هرجا خواهید و نزدیک نشوید بدین درخت که می‌شوید از ستمگران‌'),
(974, 7, 20, 'پس بفریفتشان شیطان تا بنمایاند بدیشان آنچه را نهان بود از ایشان از عورتهای ایشان و گفت همانا نهی نکرده است شما را پروردگارتان از این درخت مگر تا نباشید دو فرشته یا باشید از جاودانان‌'),
(975, 7, 21, 'و سوگند آوردشان که منم برای شما از اندرزدهندگان‌'),
(976, 7, 22, 'پس سرنگونشان ساخت به فریب تا گاهی که چشیدند از آن درخت پدیدار شد برای ایشان عورتهاشان و آغاز کردند گرد می‌آوردند بر خود از برگهای بهشت و بانگ زد بدیشان پروردگارشان که آیا نهی نکردم شما را از این درخت و نگفتم به شما که شیطان برای شما دشمنی است آشکار'),
(977, 7, 23, 'گفتند بار پروردگارا ستم کردیم خویش را و اگر نیامرزی ما را و رحم نکنی بر ما هرآینه باشیم از زیانکاران‌'),
(978, 7, 24, 'گفت فرودشوید گروهی از شما دشمن گروهی و شما را است در زمین قرارگاه و بهره‌ای تا زمانی‌'),
(979, 7, 25, 'گفت در آن زندگی می‌کنید و در آن می‌میرید و از آن بیرون آورده شوید'),
(980, 7, 26, 'ای فرزندان آدم همانا فرستادیم بر شما جامه که بپوشد عورتهای شما را و ساز و برگی و لباس پرهیزکاری بهتر است این است از آیتهای خدا شاید یادآور شوند'),
(981, 7, 27, 'ای فرزندان آدم نفریبد شما را شیطان چنانکه برون کرد پدر و مادر شما را از بهشت برمی کند از ایشان رخت ایشان را تا بنماید بدیشان عورتهای ایشان را همانا بینند شما را او و کسانش چنانکه نبیندشان همانا گردانیدیم شیاطین را دوستانی برای آنان که ایمان نمی‌آورند'),
(982, 7, 28, 'و هرگاه فحشائی (ناشایسته‌ای) مرتکب شوند گویند یافتیم بر آن پدران خویش و خدایمان بدان فرمود بگو همانا خدا امر نکند به فحشاء آیا بر خدا گوئید آنچه را نمی‌دانید'),
(983, 7, 29, 'بگو پروردگارم امر کرده است به دادگری و راست کنید رویهای خود را نزد هر پرستشگاهی و بخوانیدش پاک‌دارندگان برای او دین را بدانسان که آغازتان کرده است برمی‌گردید'),
(984, 7, 30, 'گروهی را هدایت کرد و بر گروهی بیفتاد گمراهی همانا ایشان برگرفتند شیاطین را دوستانی جز خدا و پنداشتند که ایشانند هدایت‌یابندگان‌'),
(985, 7, 31, 'ای فرزندان آدم برگیرید زینت خود را نزد هر پرستشگاهی و بخورید و بیاشامید و اسراف نکنید که خدا دوست ندارد اسراف‌کنندگان را'),
(986, 7, 32, 'بگو که حرام کرده است زینت خدا آن را که برون آورده است برای بندگان خویش و پاکیزه‌ها را از روزی بگو آنها از آن کسانی است که ایمان آوردند در زندگانی دنیا مخصوص بدیشان در روز رستاخیز چنین تفصیل دهیم آیتها را برای گروهی که می‌دانند'),
(987, 7, 33, 'بگو جز این نیست که حرام کرده است پروردگار من فواحش را آنچه آشکار است از آنها و آنچه نهان و گناه و ستمگری را به ناحق و آنکه شریک برای خدا گردانید آنرا که نفرستاده است بدان فرمانروایی و آنکه گوئید بر خدا آنچه را نمی‌دانید'),
(988, 7, 34, 'و برای هر ملتی است سرآمدی که هرگاه رسدشان سرآمدشان نه دیر کنند ساعتی و نه پیشی گیرند'),
(989, 7, 35, 'ای فرزندان آدم اگر بیاید شما را فرستادگانی از شما که فروخوانند بر شما آیتهای مرا پس آنکه پرهیزکاری کند و اصلاح کند نیست بیمی بر ایشان و نه اندوهگین شوند'),
(990, 7, 36, 'و آنان که تکذیب کنند آیتهای ما را و سرپیچند از آنها آنانند یاران آتش ایشانند در آن جاودانان‌'),
(991, 7, 37, 'پس کیست ستمگرتر از آنکه دروغ بندد بر خدا یا دروغ پندارد آیتهای او را اینان را رسد بهره ایشان از کتاب تا گاهی که درآیند بر ایشان فرستادگان ما یابندشان گویند کجا است آنکه بودید می‌خواندید جز خدا گویند گم شدند از ما و گواهی دادند بر خویشتن که ایشان کافران‌'),
(992, 7, 38, 'گفت درآئید در ملتهائی که گذشتند پیش از شما از پری و آدمی در آتش هرگاه درآید ملتی لعن کند دیگری را تا گاهی که گردآیند همگی در آن گوید آخرینشان برای اولین پروردگارا اینان گمراهمان کردند پس بده ایشان را عذابی دوبرابر در آتش گوید برای هرکدام است دوبرابر لیکن نمی‌دانید'),
(993, 7, 39, 'و گوید اولینشان برای آخرینشان نبود شما را برتری بر ما پس بچشید عذاب را بدانچه بودید فراهم می‌کردید'),
(994, 7, 40, 'همانا آنان که تکذیب کردند آیتهای ما را و سرپیچیدند از آنها گشوده نشود برای ایشان درهای آسمان و درنیایند به بهشت تا برود اشتر در سوراخ سوزن بدینسان کیفر دهیم به گنهکاران‌'),
(995, 7, 41, 'ایشان را است در دوزخ آرامگاه‌هائی و از فرازشان پوشهایی و چنین کیفر دهیم ستمکاران را'),
(996, 7, 42, 'و آنان که ایمان آوردند و کردار شایسته کردند تکلیف نکنیم کس را جز به اندازه توانائیش آنانند یاران بهشت ایشانند در آن جاودانان‌'),
(997, 7, 43, 'و برانداختیم آنچه در سینه‌های ایشان است از کینه روان شود از زیر ایشان جویها و گویند سپاس خداوندی را که ما را بدین هدایت نمود و نبودیم هدایت یابیم اگر نه هدایت می‌کرد ما را خدا همانا آمدند فرستادگان پروردگار ما به حق و خوانده شدند که اینک بهشت ارث داده شدیدش بدانچه بودید می‌کردید'),
(998, 7, 44, 'و بانگ برآوردند یاران بهشت به یاران آتش که همانا یافتیم آنچه وعده داد به ما پروردگار ما حق آیا یافتید آنچه وعده داده به شما پروردگارتان حق گفتند بلی پس بانگ داد بانگ دهنده‌ای میان ایشان که لعنت خدا باد بر ستمگران‌'),
(999, 7, 45, 'آنان که بازدارند از راه خدا و خواهندش کج و ایشانند به آخرت کافران‌'),
(1000, 7, 46, 'و میان ایشان است حجابی و بر اعرافند مردانی که بشناسند هرکدام را به سیمای ایشان و ندا کردند یاران بهشت را که سلام بر شما درون نشدندش حالی که طمع داشتند'),
(1001, 7, 47, 'و هرگاه گردانیده شود رویهای ایشان بسوی یاران آتش گویند پروردگارا نگردان ما را با گروه ستمکاران‌'),
(1002, 7, 48, 'و بانگ دهند یاران اعراف مردانی را که بشناسندشان به سیماشان گویند بی‌نیازتان نکرد گردآوردنتان و آنچه بودید کبر می‌ورزیدید'),
(1003, 7, 49, 'آیا اینانند آنان که سوگند آوردید که نرساندشان خدا به رحمتی درآئید بهشت را نیست بر شما بیمی و نه اندوهگین شوید'),
(1004, 7, 50, 'و خواندند یاران آتش یاران بهشت را که بریزید بر ما از آب یا از آنچه روزیتان داده است خدا گویند همانا حرام کرده است خدا آنها را بر کافران‌'),
(1005, 7, 51, 'آنان که گرفتند دین خود را هوسرانی و بازیچه و بفریفتشان زندگی دنیا پس امروز فراموششان کنیم بدانسان که فراموش کردند رسیدنشان را بدین روز و آنکه بودند آیتهای ما را انکار می‌کردند'),
(1006, 7, 52, 'و همانا آوردیمشان به کتابی که تفصیلش دادیم بر دانش هدایت و رحمتی برای قومی که ایمان آرند'),
(1007, 7, 53, 'آیا انتظاری جز تأویل آن دارند روزی که رسد تأویلش گویند آنان که فراموشش نمودند از پیش همانا بیامد فرستادگان پروردگار ما به حقّ آیا ما را است شفیعانی که شفاعت کنند برای ما یا بازگردانیده شویم تا بکنیم جز آنچه را بودیم می‌کردیم همانا زیان کردند خویشتن را و گم شد از ایشان آنچه بودند دروغ می‌بستند'),
(1008, 7, 54, 'همانا پروردگار شما خداوندی است که آفرید آسمانها و زمین را در شش روز سپس پرداخت به عرش می‌پوشاند (فرامی گیرد) شب را به روز که در پی آن است به شتاب و مهر و ماه و ستارگان مسخّرند به فرمانش همانا او راست آفرینش و امر بزرگ است خداوند پروردگار جهانیان‌'),
(1009, 7, 55, 'بخوانید پروردگار خویش را به زاری و نهان که او دوست ندارد تجاوزگران را'),
(1010, 7, 56, 'و فساد نکنید در زمین پس از اصلاحش و بخوانیدش از راه بیم و امید همانا رحمت خدا نزدیک است به نیکوکاران‌'),
(1011, 7, 57, 'و او است آنکه فرستد بادها را مژده‌ای پیش روی رحمتش تا گاهی که بردارند ابرهائی سنگین برانیمش بسوی شهری مُرده تا فرود آریم بدان آب را پس برون آوریم بدان از هر میوه بدینسان برون آوریم مردگان را شاید یادآور شوید'),
(1012, 7, 58, 'و شهر پاک برون آید رستنیش به اذن پروردگارش و آنی که پلید است برون نیاید جز ناچیزی پست بدینسان بگردانیم آیتها را برای قومی که شکرگزارند'),
(1013, 7, 59, 'همانا فرستادیم نوح را بسوی قوم او پس گفت ای قوم پرستش کنید خدا را نیست شما را خدائی جز او همانا بیمناکم بر شما از عذاب روزی بزرگ‌'),
(1014, 7, 60, 'گفتند توده قومش هر آینه می‌بینیمت در گمراهی آشکار'),
(1015, 7, 61, 'گفت ای قوم نیست به من گمراهی‌ای لیکن منم فرستاده از پروردگار جهانیان‌'),
(1016, 7, 62, 'که می‌رسانم به شما پیامهای پروردگار خویش را و اندرز گویم شما را و می‌دانم از خدا آنچه نمی‌دانید'),
(1017, 7, 63, 'و آیا شگفت ماندید که بیامدتان یادآوریی از پروردگارتان بر مردی از شما که بیم دهد شما را و تا پرهیزکاری کنید و شاید رحم آورده شوید'),
(1018, 7, 64, 'پس دروغگویش شمردند پس نجاتش دادیم و آنان را که با وی بودند در کشتی و غرق کردیم آنان را که تکذیب کردند آیتهای ما را چه همانا بودند گروهی کوران‌'),
(1019, 7, 65, 'و بسوی عاد برادرشان هود را گفت ای قوم پرستش کنید خدا را نیستتان خدائی جز او آیا نمی‌ترسید'),
(1020, 7, 66, 'گفتند گروهی که کفر ورزیدند از قومش می‌نگریمت همانا در بی‌خردی و پنداریمت هر آینه از دروغگویان‌'),
(1021, 7, 67, 'گفت ای قوم نیست به من بی‌خردی و لیکن منم فرستاده از پروردگار جهانیان‌'),
(1022, 7, 68, 'که می‌رسانم به شما پیامهای پروردگار خود را و منم برای شما اندرزگوئی امین‌'),
(1023, 7, 69, 'و آیا شگفت ماندید که آمد شما را یادآوریی از پروردگارتان بر مردی از شما تا بیمتان دهد و یاد بیاورید گاهی را که گردانیدتان جانشینانی پس از قوم نوح و بیفزودتان سطبری در آفرینش پس یاد آرید نعمتهای خدا را شاید رستگار شوید'),
(1024, 7, 70, 'گفتند آیا ما را آمدی که پرستش کنیم خدای را تنها و رها کنیم آنچه را می‌پرستیدند پدران ما پس بیاور ما را آنچه به ما وعده دهی اگر هستی از راستگویان‌'),
(1025, 7, 71, 'گفت همانا افتاد بر شما از پروردگارتان چرک و خشم آیا با من می‌ستیزید در نامهائی که نامیدید آنها را شما و پدران شما نفرستاده است خدا بدانها فرمانروائیی پس منتظر باشید که منم با شما از منتظران‌'),
(1026, 7, 72, 'پس نجات دادیم او و آنان که با وی بودند به رحمتی از ما و بریدیم دنباله آنان را که تکذیب کردند آیتهای ما را و نبودند مؤمنان‌'),
(1027, 7, 73, 'و بسوی ثمود برادر ایشان صالح را گفت ای قوم من پرستش کنید خدا را نیست شما را خدائی جز او همانا بیامدتان نشانی‌ای از پروردگار شما اینک اشتر خدا آیتی برای شما پس بگذاریدش بچرد در زمین خدا و گردش به بدی نگردید که می‌گیرد شما را عذابی دردناک‌'),
(1028, 7, 74, 'و یاد آرید هنگامی را که گردانیدتان جانشینانی پس از عاد و جایگزینتان کرد در زمین که می‌گرفتید از هموارهای آن کاخهائی و می‌بریدید از کوه‌ها خانه‌هائی پس یاد آرید نعمتهای خدا را و نکوشید در زمین تبهکاران‌'),
(1029, 7, 75, 'گفتند آن گروه که کبر ورزیدند از قومش بدانان که ناتوان شمرده شدند بدانان که ایمان آوردند از ایشان آیا دانید که صالح است فرستاده‌ای از پروردگار خود گفتند مائیم بدانچه او فرستاده شده است بدان ایمان‌آرندگان‌'),
(1030, 7, 76, 'گفتند آنان که کبر ورزیدند مائیم بدانچه شما ایمان آوردید بدان کافران‌'),
(1031, 7, 77, 'پس پی کردند شتر را و سرکشی نمودند از فرمان پروردگار خود و گفتند ای صالح بیار ما را آنچه وعده دهی به ما اگر هستی از فرستادگان‌'),
(1032, 7, 78, 'پس بگرفتشان زمین لرزشی که گردیدند در خانه خود مردگان‌'),
(1033, 7, 79, 'پس پشت کرد بدیشان و گفت ای قوم همانا ابلاغ کردم به شما پیام پروردگار خود را و اندرزتان گفتم لیکن نیستید دوستدار اندرزگویان‌'),
(1034, 7, 80, 'و لوط را هنگامی که گفت به قوم خود آیا فحشاء (ننگ) آورید که پیشی نگرفته است شما را بدان کسی از جهانیان‌'),
(1035, 7, 81, 'همانا شما بر مردان به شهوت درآئید نه زنان بلکه شمائید گروهی اسراف کنندگان‌'),
(1036, 7, 82, 'و نبود پاسخ قومش جز آنکه گفتند برون رانیدشان از شهر خود که اینانند مردمی پاکیزگی‌جویان‌'),
(1037, 7, 83, 'پس نجات دادیم او و خاندانش را مگر همسرش که شد از گذشتگان‌'),
(1038, 7, 84, 'و باریدیم بر آنان بارانی پس بنگر چگونه شد فرجام گنهکاران‌'),
(1039, 7, 85, 'و بسوی مَدیَن برادرشان شعیب را گفت ای قوم پرستش کنید خدا را نیستتان خداوندی جز او همانا بیامد شما را نشانه‌ای از پروردگار شما پس تمام دهید پیمانه و سنجش را و کم ندهید به مردم چیزهای ایشان را و تبهکاری نکنید در زمین پس از اصلاحش این بهتر است برای شما اگر هستید مؤمنان‌'),
(1040, 7, 86, 'و ننشینید بر هر راهی تا بترسانید و بازدارید از راه خدا هر که را ایمان بدان آورده است و بخواهیدش کج و یاد آرید هنگامی را که کم بودید پس بیفزودتان و بنگرید چگونه بود فرجام تبهکاران‌'),
(1041, 7, 87, 'و اگر باشند گروهی از شما ایمان آورده بدانچه فرستاده شدم بدان و گروهی ایمان نیاورده باشند پس صبر کنید تا حکم کند خدا میان ما و او است بهترین حکم‌کنندگان‌'),
(1042, 7, 88, 'گفتند گروهی که کبر ورزیدند از قومش هر آینه برون رانیمت ای شعیب و آنان را که ایمان آوردند با تو از شهر خود مگر برگردید در ملت ما گفت آیا هرچند باشیم ناخوش‌دارندگان‌'),
(1043, 7, 89, 'همانا بسته‌ایم بر خدا دروغی را اگر بازگردیم در ملت شما پس از آنکه نجاتمان داد خدا از آن و نرسد ما را که بازگردیم در آن مگر بخواهد خدا پروردگار ما فراگرفته است پروردگار ما همه چیز را به دانش بر خدا توکل کردیم پروردگار ما بگشای میان ما و قوم ما به حقّ و توئی بهترین گشایندگان‌'),
(1044, 7, 90, 'و گفتند آن گروه که کافر شدند از قومش اگر پیروی کنید شعیب را همانا شمائید در آن هنگام زیانکاران‌'),
(1045, 7, 91, 'پس بگرفتشان زمین‌لرزش که گردیدند در خانه خود مردگان‌'),
(1046, 7, 92, 'آنان که تکذیب کردند شعیب را گوئیا نشیمن نگزیدند در آن آنان که تکذیب کردند شعیب را بودند زیانکاران‌'),
(1047, 7, 93, 'پس روی گردانید از ایشان و گفت ای قوم همانا ابلاغ نمودم به شما پیامهای پروردگار خود را و اندرز گفتم شما را پس چگونه افسوس خورم بر گروه کافران‌'),
(1048, 7, 94, 'و نفرستادیم در شهری پیمبری را جز آنکه گرفتیم مردمش را به سختی و رنج شاید به زاری آیند'),
(1049, 7, 95, 'سپس تبدیل کردیم به جای بدی نیکی را تا برآسودند و گفتند همانا رسیده است به پدران ما رنج و سختی پس گرفتیمشان ناگهان حالی که نمی‌دانستند'),
(1050, 7, 96, 'و اگر مردم شهرها ایمان می‌آوردند و پرهیزکاری می‌نمودند هر آینه می‌گشودیم بر ایشان برکتهائی را از آسمان و زمین لیکن تکذیب نمودند پس گرفتیمشان بدانچه بودند دست می‌آوردند'),
(1051, 7, 97, 'آیا ایمن شدند مردم شهرها که بیایدشان خشم ما شبانه حالی که ایشانند به خواب اندران‌'),
(1052, 7, 98, 'یا ایمن شدند مردم شهرها که بیایدشان خشم ما روزانه حالی که آنانند بازی‌کنان‌'),
(1053, 7, 99, 'پس آیا ایمن شدند مکر خدا را که ایمن نشود مکر خدا را جز گروه زیانکاران‌'),
(1054, 7, 100, 'آیا رهبری نشد برای آنان که ارث می‌برند زمین را پس از اهلش که اگر می‌خواستیم هر آینه می‌گرفتیمشان به گناهانشان و مهر می‌زدیم بر دلهای ایشان چنانکه نشنوند'),
(1055, 7, 101, 'این شهرها می‌سرائیم بر تو از داستانهای آنها و همانا بیامدشان فرستادگانشان به نشانیها پس نبودند که ایمان آرند بدانچه تکذیب کردند از پیش بدینگونه مهر نهد خدا بر دلهای کافران‌'),
(1056, 7, 102, 'و نیافتیم برای بیشترشان عهدی و بیافتیم بیشترشان را نافرمانان‌'),
(1057, 7, 103, 'سپس برانگیختیم پس از ایشان موسی را به آیتهای ما بسوی فرعون و قومش پس ستم نمودند بدانها بنگر تا چگونه شد فرجام تبهکاران‌'),
(1058, 7, 104, 'و گفت موسی ای فرعون همانا منم فرستاده از پروردگار جهانیان‌'),
(1059, 7, 105, 'سزاوارم بر آنکه نگویم بر خدا جز حقّ را همانا آوردم شما را نشانی از پروردگار شما پس بفرست با من بنی اسرائیل را'),
(1060, 7, 106, 'گفت اگر آیتی آورده‌ای پس بیارش اگر هستی از راستگویان‌'),
(1061, 7, 107, 'پس بیفکند عصایش را ناگاه آن اژدهائی است آشکار'),
(1062, 7, 108, 'و برآورد دستش را ناگهان آن پرتوی است برای بینندگان‌'),
(1063, 7, 109, 'گفتند توده‌ای از قوم فرعون این است جادوگری دانشمند'),
(1064, 7, 110, 'که خواهد برون راند شما را از سرزمینتان تا چه امر کنید'),
(1065, 7, 111, 'گفتند بگذار او و برادرش را و بفرست در شهرها گردآورندگان‌'),
(1066, 7, 112, 'تا آرندت به هر جادوگری دانا'),
(1067, 7, 113, 'و بر فرعون درآمدند جادوگران گفتند همانا ما را است پاداشی اگر شدیم پیروزمندان‌'),
(1068, 7, 114, 'گفت آری و همانا شمائید از مقربان‌'),
(1069, 7, 115, 'گفتند ای موسی یا تو افکن و یا ما باشیم افکنندگان‌'),
(1070, 7, 116, 'گفت بیفکنید پس گاهی که افکندند افسون نمودند (بستند) دیدگان مردم را و بترساندندشان و آوردند جادوئی‌گران‌'),
(1071, 7, 117, 'و وحی نمودیم به موسی که بیفکن عصای خویش را ناگهان فروبرد آنچه را دروغ آورند'),
(1072, 7, 118, 'پس بیامد حقّ و باطل شد آنچه بودند می‌کردند'),
(1073, 7, 119, 'پس شکست خوردند در آن هنگام و بازگشتند سرافکندگان‌'),
(1074, 7, 120, 'و بیفتادند جادوگران سجده‌کنندگان‌'),
(1075, 7, 121, 'گفتند ایمان آوردیم به پروردگار جهانیان‌'),
(1076, 7, 122, 'پروردگار موسی و هارون‌'),
(1077, 7, 123, 'گفت فرعون آیا ایمان آوردید بدو پیش از آنکه اذنتان دهم همانا آن نیرنگی است که شما در شهر باختید تا برون رانید از آن مردمش را زود است بدانید'),
(1078, 7, 124, 'همانا سخت ببرّم دستها و پاهای شما را از برابر (دست راست پای چپ) سپس به دارتان کشم همگی‌'),
(1079, 7, 125, 'گفتند همانا مائیم بسوی پروردگار خود بازگشت‌کنان‌'),
(1080, 7, 126, 'و کین نورزی با ما جز بدانکه ایمان آوردیم به آیتهای پروردگار ما گاهی که بیامدند ما را پروردگارا بریز بر ما شکیبائی را و دریاب ما را مسلمانان‌'),
(1081, 7, 127, 'و گفتند توده‌ای از قوم فرعون آیا می‌گذاری موسی و قومش را تا فساد کنند در زمین و بگذاردت تو و خدایانت را گفت بزودی زار بکشیم فرزندان ایشان را و بگذاریم زنانشان را و همانا مائیم بر فراز ایشان نیرومندان‌'),
(1082, 7, 128, 'گفت موسی به قوم خود کمک جوئید از خدا و شکیبا شوید همانا زمین از آن خدا است ارث دهدش به هر که از بندگان خود که خواهد و پایان است برای پرهیزکاران‌'),
(1083, 7, 129, 'گفتند آزرده شدیم پیش از آنکه بیائی ما را و پس از آنکه بیامدی ما را گفت امید است پروردگار شما نابود کند دشمن شما را و جانشینان سازد در زمین تا بنگرد چگونه می‌کنید'),
(1084, 7, 130, 'و هر آینه بگرفتیم خاندان فرعون را به قحطی و کاهشی از میوه‌ها شاید یادآور شوند'),
(1085, 7, 131, 'پس گاهی که بیامدشان خوشی گفتند از آنِ ما است این و اگر می‌رسیدشان بدی شوم می‌گرفتند موسی را و آنان را که با وی بودند همانا شومی ایشان نزد خدا است لیکن بیشترشان نمی‌دانند'),
(1086, 7, 132, 'و گفتند هرچند بیاری ما را آیتی که ما را بدان افسون کنی نباشیم به تو ایمان‌آرندگان‌'),
(1087, 7, 133, 'پس فرستادیم بر ایشان طوفان و ملخ و شپشه (آفتی که در گندم به هم رسد) و غوک و خون را آیتهائی جدا جدا پس کبر ورزیدند و شدند قومی گنهکاران‌'),
(1088, 7, 134, 'و هنگامی که افتاد بر ایشان چرک گفتند ای موسی بخوان برای ما پروردگار خویش را بدانچه به نزد تو سپرده است که اگر برداری از ما چرک را هر آینه ایمان آریم به تو و با تو فرستیم بنی‌اسرائیل را'),
(1089, 7, 135, 'و هنگامی که برداشتیم از ایشان چرک را تا سرآمدی که ایشانند بدان رسنده ناگهان ایشانند پیمان‌شکنان‌'),
(1090, 7, 136, 'پس کین کشیدیم از ایشان و غرقشان نمودیم در دریا بدانچه تکذیب کردند آیتهای ما را و بودند از آنها غفلت‌کنان‌'),
(1091, 7, 137, 'و ارث دادیم به گروهی که ناتوان شمرده می‌شدند خاورها و باخترهای زمین را که در آنها برکت نهادیم و انجام گرفت سخن پروردگارت نکوی بر بنی‌اسرائیل بدانچه شکیبا شدند و واژگون ساختیم آنچه را می‌ساختند فرعون و قومش و آنچه می‌افراشتند'),
(1092, 7, 138, 'و گذراندیم بنی‌اسرائیل را از دریا تا رسیدند به قومی که گرد می‌آمدند بر بتانی از آن خود گفتند ای موسی قرار ده برای ما خدائی چنانکه برای اینان است خدایانی گفت هر آینه شمائید گروهی نادانان‌'),
(1093, 7, 139, 'همانا اینان تباه است آنچه در آن اندرند و باطل است آنچه می‌کنند'),
(1094, 7, 140, 'گفت آیا جز خدا برای شما خدائی جویم و او برتری داده است شما را بر جهانیان‌'),
(1095, 7, 141, 'و هنگامی که نجات دادیم شما را از خاندان فرعون که روا می‌داشتند بر شما عذاب زشت را به سختی می‌کشتند فرزندان شما را و می‌گذاشتند زنان شما را و در این بود بلائی از پروردگار شما گران‌'),
(1096, 7, 142, 'و وعده نهادیم با موسی سی شب و تکمیل آن کردیم به ده‌تا تا تمام شد میعاد پروردگار او چهل شب و گفت موسی به برادرش هارون جانشینم باش در قومم و اصلاح کن و پیروی مکن از راه فسادکاران‌'),
(1097, 7, 143, 'و هنگامی که آمد موسی به وعده‌گاه ما و سخن گفت با او پروردگارش گفت پروردگارا مرا بنمایان که بنگرم بسوی تو گفت هرگز نخواهی مرا دید و لیکن بنگر بسوی کوه تا اگر آرام گرفت بر جای خود بزودی مرا بینی پس گاهی که تابید پروردگار او به کوه گردانیدش هموار (پست) و بیفتاد موسی بیهوش و هنگامی که به هوش آمد گفت منزّهی تو بازگشتم بسوی تو و منم نخستین ایمان‌آرندگان‌'),
(1098, 7, 144, 'گفت ای موسی همانا برگزیدمت بر مردم به پیامهای خود و به سخن خود پس برگیر آنچه را به تو دادم و باش از شکرگزاران‌'),
(1099, 7, 145, 'و نوشتیم برایش در الواح از هر چیزی اندرز و تفصیلی برای همه چیز پس برگیر آن را به نیرو و بفرمای قومت را تا گیرند بهترین آن را بزودی بنمایانم شما را خانه فاسقان‌'),
(1100, 7, 146, 'بزودی بازگردانم از آیتهای خود آنان را که تکبّر کنند در زمین به ناحقّ و اگر بینند هر آیتی ایمان نیارند بدان و اگر بینند راه رهبری را نگیرندش راه و اگر بینند راه گمراهی را گیرندش راه این بدان است که تکذیب کردند آیتهای ما را و بودند از آنها غافلان‌'),
(1101, 7, 147, 'و آنان که تکذیب کردند به آیتهای ما و رسیدن آخرت تباه شده است اعمال ایشان آیا پاداش داده می‌شوند جز آنچه را بودند می‌کردند'),
(1102, 7, 148, 'و برگرفتند قوم موسی پس از او از زیور خود گوساله‌ای را کالبدی که او راست بانگی آیا ندیدند که سخن نگوید با ایشان و نه رهبریشان کند به راهی برگرفتندش و بودند ستمگران‌'),
(1103, 7, 149, 'و هنگامی که افتاد در دست ایشان و دیدند که گمراه شدند گفتند اگر رحم نکند بر ما پروردگار ما و نیامرزد ما را همانا باشیم از زیانکاران‌'),
(1104, 7, 150, 'و هنگامیکه برگشت موسی بسوی قوم خود خشمگین اسفناک گفت چه زشت است آنچه جانشینم ساختید (بجای آوردید) پس از من آیا شتاب گرفتید امر پروردگار خود را و افکند الواح را و بگرفت سر برادر خویش را می‌کشیدش بسوی خود گفت ای فرزند مادر همانا قومم ناتوانم گرفتند و نزدیک شد بکشندم پس شادکام نگردان به من دشمنان را و نگردانم با گروه ستمکاران‌'),
(1105, 7, 151, 'گفت پروردگارا بیامرز برای من و برادرم و درآور ما را به رحمت خود و توئی مهربانترین مهربانان‌'),
(1106, 7, 152, 'همانا آنان که برگرفتند گوساله را بزودی رسدشان خشمی از پروردگارشان و خواریی در زندگانی دنیا و چنین کیفر دهیم به دروغ آوران‌'),
(1107, 7, 153, 'و آنان که بدیها کرده‌اند پس توبه کردند از آن پس و ایمان آوردند همانا پروردگار تو است پس از آن آمرزنده مهربان‌'),
(1108, 7, 154, 'و هنگامی که فرونشست از موسی خشم بگرفت الواح را و در نسخه آنها هدایت و رحمتی است برای آنان که از پروردگار خویشند هراسان‌'),
(1109, 7, 155, 'و برگزید موسی قومش را هفتاد مرد برای وعده‌گاه ما و هنگامی که بگرفتشان لرزش گفت پروردگارا اگر می‌خواستی نابود می‌کردی ایشان را و مرا از پیش آیا ما را نابود کنی بدانچه بی‌خردان از ما کردند نیست این جز فتنه تو (آزمایش تو) گمراه کنی بدان هر که را خواهی و هدایت کنی هر که خواهی توئی سرپرست ما پس بیامرز ما را و رحم کن بر ما و توئی بهترین آمرزندگان‌'),
(1110, 7, 156, 'و بنویس برای ما در این دنیا نیکی و در آخرت نیکی همانا هدایت شدیم بسوی تو گفت عذاب من رسانم بدان هر که را خواهم و رحمتم فراگرفته است همه چیز را زود است بنویسمش برای آنان که پرهیزکاری کنند و بدهند آکا را و آنان که به آیتهای ما ایمان آرند'),
(1111, 7, 157, 'آنان که پیروی کنند پیمبر فرستاده درس ناخوانده‌ای را که می‌یابندش نوشته نزد ایشان در توراة و انجیل امرشان کند به نیکی و نهیشان کند از بدی و حلال کند برای ایشان پاکها را و حرام کند بر ایشان پلیدها را و بنهد از ایشان بارشان و آن زنجیرهائی را که بر ایشان بوده است پس آنان که ایمان بدو آوردند و ارجمندش (گرامیش) داشتند و یاریش کردند و پیروی کردند روشنائی را که با او فرستاده شده است آنانند رستگاران‌'),
(1112, 7, 158, 'بگو ای مردم منم فرستاده خدا بسوی شما همگی آنچنان خدائی که وی را است پادشاهی آسمانها و زمین نیست خدائی جز او زنده کند و بمیراند پس ایمان آرید به خدا و فرستاده او آن پیمبر درس ناخوانده که ایمان می‌آورد به خدا و سخنان او و پیرویش کنید شاید هدایت یابید'),
(1113, 7, 159, 'و از قوم موسی امّتی است که هدایت کنند به حقّ و بدان دادگری کنند'),
(1114, 7, 160, 'و بخش کردیم آنان را دوازده سبط (نبیره‌گان) امّتهائی و وحی نمودیم به موسی هنگامی که آب از او خواستند قومش که بنواز عصای خویش را به سنگ پس برجهید از آن دوازده چشمه دانستند هر مردمی آبشخور خود را و سایه افکندیم بر ایشان با ابر و فرود آوردیم بر ایشان مَنّ و سَلوی (نان و مرغ بریان) بخورید از پاکهای آنچه به شما روزی دادیم و ستم نکردند بر ما و لیکن بودند خویشتن را ستم‌کنندگان‌'),
(1115, 7, 161, 'و هنگامی که گفته شد بدیشان سکونت کنید در این شهر و بخورید از آن هرجا خواهید و بگوئید حطّه (کاهشی) و درآئید در را سجده‌کنان می‌آمرزیم برای شما گناهان شما را و بزودی فزونی دهیم به نکوکاران‌'),
(1116, 7, 162, 'پس تبدیل کردند آنان که ستم کردند از ایشان سخن را بجز آنچه بدیشان گفته شد پس فرستادیم بر ایشان چرکی را از آسمان بدانچه بودند ستم می‌کردند'),
(1117, 7, 163, 'و بپرسشان از شهری که کنار دریا بود گاهی که تجاوز می‌کردند در روز شنبه چرا که می‌آمدشان ماهیهاشان روز شنبه ایشان بر روی آب و روزی که جز شنبه بود نمی‌آمدندشان بدینگونه آزمودیمشان بدانچه بودند نافرمانی می‌کردند'),
(1118, 7, 164, 'و هنگامی که گروهی از ایشان گفتند چرا اندرز گوئید قومی را که خداست نابودکننده آنان یا عذاب‌کننده ایشان عذابی سخت گفتند پوزشی بسوی پروردگار شما و شاید پرهیزکاری کنند'),
(1119, 7, 165, 'تا گاهی که فراموش کردند آنچه را یادآوری شدند نجات دادیم آنان را که نهی می‌کردند از بدی و گرفتیم آنان را که ستم کردند به عذابی زشت بدانچه بودند نافرمانی می‌کردند'),
(1120, 7, 166, 'پس گاهی که سرپیچیدند از آنچه نهی شدند از آن گفتیم بدیشان بشوید بوزینه‌گانی راندگان‌'),
(1121, 7, 167, 'و هنگامی که اعلام کرد پروردگار تو که برانگیزاند بر ایشان تا روز قیامت آن را که بچشاندشان شکنجه‌ای بد همانا پروردگار تو شتابنده است در عقوبت و هر آینه او است آمرزنده مهربان‌'),
(1122, 7, 168, 'و بخش کردیمشان در زمین ملّتهائی از ایشانند شایستگان و از ایشانند جز آن و آزمودیمشان به خوبیها و بدیها شاید بازگردند'),
(1123, 7, 169, 'پس جانشین شد پس از ایشان جانشینانی که ارث بردند کتاب را می‌گیرند بهره این کوتاه (پست یا نزدیک) را و گویند بزودی آمرزش شود برای ما و اگر بیایدشان بهره‌ای همانند آن برگیرندش آیا گرفته نشد بر ایشان پیمان کتاب که نگوئید بر خدا جز حقّ را و خواندند آنچه را در آن بود و خانه آخرت بهتر است برای آنان که پرهیزکاری کنند آیا بخرد نمی‌یابید'),
(1124, 7, 170, 'و آنان که می‌گیرند کتاب را (چنگ می‌زنند) به کتاب و بپا می‌دارند نماز را همانا تباه ننمائیم پاداش اصلاح‌کنندگان را'),
(1125, 7, 171, 'و هنگامی که بالا بردیم کوه را فراز ایشان مانند سایبانی و پنداشتندش فرودآینده بر خویش بگیرید آنچه را به شما دادیم به قوّت و یاد آرید آنچه را در آن است شاید پرهیزکاری کنید'),
(1126, 7, 172, 'و هنگامی که بگرفت پروردگار تو از فرزندان آدم از پشتهای ایشان نژاد ایشان را و گواهشان گرفت بر خویشتن آیا نیستم پروردگار شما گفتند بلی گواهی دادیم تا نگوئید روز قیامت بودیم ما از این ناآگاهان‌'),
(1127, 7, 173, 'یا گوئید جز این نیست که شرک ورزیدند پدران ما از پیش و بودیم نژادی پس از ایشان آیا تباه کنی ما را بدانچه کردند نادرستان‌'),
(1128, 7, 174, 'و بدینسان تفصیل دهیم آیتها را و شاید ایشان بازگردند'),
(1129, 7, 175, 'و بخوان بر ایشان داستان آن را که دادیمش آیتهای خود را پس برهنه گشت (برون شد) از آنها سپس شیطان پیروش گرفت و شد از گمراهان‌'),
(1130, 7, 176, 'و اگر می‌خواستیم هر آینه بالا می‌بردیمش بدانها لیکن او بسوی زمین گرائید و پیروی کرد هوس خود را پس مثَل او مانند سگ است که اگر آهنگش کنی زبان از کام درآرد و اگر بگذاریش زبان از کام درآرد (فروهلد) این است مثَل گروهی که تکذیب نمودند آیتهای ما را پس بخوان داستانها را شاید اندیشه کنند'),
(1131, 7, 177, 'چه زشت است مثَل قومی که تکذیب کردند آیتهای ما و بودند خویشتن را ستم می‌کردند'),
(1132, 7, 178, 'آن را که خدا هدایت کند او است هدایت شده و آنان را که گمراه کند آنانند زیانکاران‌'),
(1133, 7, 179, 'و همانا بیافریدیم برای دوزخ بسیاری از پری و آدمی را آنان را است دلهائی که درنیابند بدانها و آنان را است دیدگانی که نبینند بدانها و آنان را است گوشهائی که نشنوند بدانها آنانند مانند دامها بلکه گمراه‌تر آنانند ناآگاهان‌'),
(1134, 7, 180, 'و خدا را است نامهای نکو پس بخوانیدش بدانها و بگذارید آنان را که کجروی کنند در نامهای او بزودی پاداش داده شوند آنچه را بودند می‌کردند'),
(1135, 7, 181, 'و از آنان که آفریدیم ملّتی است که هدایت کنند به حقّ و بدان دادگری کنند'),
(1136, 7, 182, 'و آنان که دروغ پنداشتند آیتهای ما را زود است به استدراج (به آهستگی ناگهان) گیریمشان از آنجا که ندانند'),
(1137, 7, 183, 'و مهلتشان دهیم که همانا نیرنگ من است استوار'),
(1138, 7, 184, 'آیا نیندیشیدند که نیست بر رفیقشان دیوانگیی نیست او جز ترساننده آشکار'),
(1139, 7, 185, 'آیا ننگریستند در پادشاهیهای آسمانها و زمین و آنچه می‌آفریده است خدا از چیزها و آنکه شاید نزدیک شده باشد سرآمد ایشان پس به کدام سخن پس از این ایمان می‌آورند'),
(1140, 7, 186, 'آن را که گمراه کند خدا نیستش راهنمائی و بگذاردشان در سرکشی خود فرو روند'),
(1141, 7, 187, 'پرسندت از ساعت چه وقت است لنگرگاه آن بگو جز این نیست که علم آن نزد پروردگار من است پدیدارش نسازد به هنگامش جز او سنگین است در آسمانها و زمین نیایدتان مگر ناگهان پرسندت چنانکه گوئی تو آگاهی از آن بگو جز این نیست که علمش نزد خدا است لیکن بیشتر مردم نمی‌دانند'),
(1142, 7, 188, 'بگو دارا نیستم برای خویشتن سود و نه زیانی جز آنچه خدا خواهد و اگر دانای ناپیدا می‌بودم هر آینه فراوان می‌گرفتم از خوبی و به من نمی‌رسید بدی نیستم من مگر بیم‌دهنده و نویددهنده برای گروهی که ایمان آرند'),
(1143, 7, 189, 'او است آنکه آفرید شما را از یک تن و قرار داد از او جفتش را تا آرامش گیرد بدو سپس هنگامی که بر او درآمد برداشت باری سبک پس بگذشت بر آن تا گاهی که سنگین شد خواندند پروردگار خویش را که اگر ما را صالحی (شایسته) دهی باشیم از شکرگزاران‌'),
(1144, 7, 190, 'ولی هنگامی که داد بدیشان صالحی (شایسته) و قرار دادند شریکانی برای او در آنچه بدیشان داد همانا برتر است خدا از آنچه شرک می‌ورزند'),
(1145, 7, 191, 'آیا شریک کنند آنان را که نیافرند چیزی را و خود آفریده شوند'),
(1146, 7, 192, 'و نتوانندشان یاریی و نه خویشتن را یاری‌ای کنند'),
(1147, 7, 193, 'و اگر بسوی هدایتشان خوانید پیرویتان نکنند یکسان است بر شما بخوانید ایشان را یا باشید خموشان‌'),
(1148, 7, 194, 'همانا آنان را که جز خدا می‌خوانید بندگانیند مانند شما پس بخوانیدشان تا اجابت کنند شما را اگر هستید راستگویان‌'),
(1149, 7, 195, 'آیا آنان را است پاهائی که روان گردند بدانها یا آنان را است دستهائی که آهنگ کنند بدانها یا آنان را است دیدگانی که بینند بدانها یا آنان را است گوشهائی که بشنوند بدانها بگو بخوانید شریکان خویش را پس نیرنگ با من کنید و مهلتم ندهید'),
(1150, 7, 196, 'همانا یار (دوست و سرپرست و پشتیبان) من خداوندی است که فرستاد کتاب را و او است سرپرست شایستگان‌'),
(1151, 7, 197, 'و آنان را که جز او خوانید نتوانند شما را یاری‌کردن و نه خویشتن را یاری کنند'),
(1152, 7, 198, 'و اگر خوانیشان بسوی هدایت نشنوند و بینیشان می‌نگرند بسوی تو و لیکن نمی‌بینند'),
(1153, 7, 199, 'بگیر گذشت را و امر کن به خوبی و روی بگردان از نادانان‌'),
(1154, 7, 200, 'و اگر برانگیزدت از شیطان انگیزشی پس پناهنده شو به خدا همانا او است شنوای دانا'),
(1155, 7, 201, 'همانا آنان که پرهیزکاری کرده‌اند هرگاه آهنگشان کند رهگذاری از شیطان یادآور شوند ناگاه ایشانند بینایان‌'),
(1156, 7, 202, 'و برادران شیاطین را فروبرند شیاطین در گمراهی سپس کوتاه نمی‌آیند'),
(1157, 7, 203, 'و هرگاه نیاوریشان آیتی گویند چرا آیتی نساختی بگو پیروی نکنم جز آنچه را وحی شود به من از پروردگار من اینک بینشهائی از پروردگارتان و هدایت و رحمتی برای گروهی که ایمان آرند'),
(1158, 7, 204, 'و گاهی که خوانده شود قرآن گوش دهید بدان و خموشی گزینید شاید رحم کرده شوید'),
(1159, 7, 205, 'و یاد کن پروردگار خویش را درون خود به زاری و ترس و پست‌تر از گفتار بلند بامدادان و شبانگاه و نباش از غافلان‌'),
(1160, 7, 206, 'همانا آنان که نزد پروردگار تواند کبر نورزند از پرستش او و می‌ستایندش و برای او سجده می‌کنند');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'پرسندت از بهره‌های جنگ بگو آنها از آن خدا و پیمبر است پس بترسید خدا را و اصلاح کنید میانه خویش را و فرمان برید خدا و پیمبرش را اگر هستید مؤمنان‌'),
(1162, 8, 2, 'جز این نیست که مؤمنان آنانند که هرگاه یاد خدا شود بهراسد دلهاشان و هرگاه خوانده شود بر ایشان آیتهای او بفزایدشان ایمان و بر پروردگار خویش توکّل کنند'),
(1163, 8, 3, 'آنان که بپای می‌دارند نماز را و از آنچه روزیشان داده‌ایم ببخشند'),
(1164, 8, 4, 'آنانند مؤمنان بدرست ایشان را است پایه‌هائی نزد پروردگارشان و آمرزشی و روزیی گرامی‌'),
(1165, 8, 5, 'بدانسان که برون آورد تو را پروردگار تو از خانه‌ات به حقّ حالی که گروهی از مؤمنانند ناخوش‌دارندگان‌'),
(1166, 8, 6, 'می‌ستیزند با تو در حقّ پس از آنکه پدیدار شد گوئیا رانده می‌شوند بسوی مرگ و آنانند نگران‌'),
(1167, 8, 7, 'و هنگامی که وعده می‌دادتان خدا یکی از دو گروه را که آن از آن شما است و دوست می‌داشتید که جز شوکتمند شما را باشد و خدا می‌خواست جایگزین کند حقّ را به کلمات خود و ببُرد دنباله کافران را'),
(1168, 8, 8, 'تا بپای دارد حقّ را و تباه کند باطل را و اگر چه ناخوش دارند گنهکاران‌'),
(1169, 8, 9, 'هنگامی که می‌نالیدید (کمک می‌خواستید) به پروردگار خود پس اجابت کرد برای شما که کمک‌کننده‌ام شما را به هزار تن از فرشتگان دوش به دوش‌'),
(1170, 8, 10, 'و نگردانید آن را خدا جز بشارتی و تا آرام گیرد بدان دلهای شما حالی که نیست یاری جز از نزد خدا همانا خداوند است عزّتمند حکیم‌'),
(1171, 8, 11, 'هنگامی که افکند بر شما خمار خواب را تا آرامشی باشد از او و فرستاد بر شما از آسمان آبی تا پاکتان سازد بدان و تا دور کند از شما چرک شیطان را و تا پیوند نهند بر دلهای شما و استوار سازد بدان قدمها را'),
(1172, 8, 12, 'هنگامی که وحی می‌فرستاد پروردگار تو بسوی فرشتگان که من با شما هستم پس استوار دارید آنان را که ایمان آوردند زود است افکنم در دلهای آنان که کفر ورزیدند هراس را پس بزنید فراز گردنها را و بزنید از ایشان هر سرانگشتی را'),
(1173, 8, 13, 'این بدان است که درافتادند با خدا و رسولش و هر که با خدا و رسولش درافتد همانا خداوند است سخت‌عقوبت‌'),
(1174, 8, 14, 'اینک بچشیدش و همانا کافران را است عذاب آتش‌'),
(1175, 8, 15, 'ای آنان که ایمان آوردید هرگاه تلاقی کردید با آنان که کفر ورزیدند در جنگ پس برنگردانید بسوی آنان پشتها را'),
(1176, 8, 16, 'و آنکس که بگرداند پشتش را بسوی آنان جز گرائیده برای جنگی یا ملحق شونده به گروهی همانا بازگشت است به خشمی از خدا و جایگاه او است دوزخ و چه زشت است آن جایگاه‌'),
(1177, 8, 17, 'پس نکشتید ایشان را شما و لیکن خداوند کشتشان و نه تو انداختی تیر را گاهی که انداختی و لیکن خدا بینداخت و تا بیازماید مؤمنان را از خود آزمایشی نکو همانا خداوند شنوای دانا'),
(1178, 8, 18, 'این است و آنکه خدا است سست‌کننده نیرنگ کافران‌'),
(1179, 8, 19, 'اگر پیروزی جوئید همانا بیامدتان پیروزی و اگر کوتاه آئید (دست بردارید) پس آن بهتر است برای شما و اگر بازگردید بازگردیم و بی‌نیازتان نکند جمعیت شما به چیزی و هر چند فزون باشد و آنکه خدا است با مؤمنان‌'),
(1180, 8, 20, 'ای آنان که ایمان آوردید فرمانبرید خدا و پیمبرش را و برنگردید از او و شما می‌شنوید'),
(1181, 8, 21, 'و نباشید مانند آنان که گفتند شنیدیم و ایشان نمی‌شنوند'),
(1182, 8, 22, 'همانا بدترین جنبندگان نزد خدا کَران و گنگانی‌اند که ناخردمندند'),
(1183, 8, 23, 'و اگر می‌دانست خدا در ایشان خیری هر آینه می‌شنواندشان و اگر می‌شنواند ایشان را هر آینه پشت می‌کردند و آنانند روی‌گردانان‌'),
(1184, 8, 24, 'ای آنان که ایمان آوردید اجابت کنید خدا و رسولش را گاهی که خوانندتان بدانچه زنده‌تان سازد و بدانید که خدا حایل می‌شود میان مرد و دلش و آنکه بسوی او گردآورده می‌شوید'),
(1185, 8, 25, 'و بپرهیزید فتنه (آزمایشی) را که نرسد بدانان که ستم کردند از شما تنها و بدانید که خداوند است سخت‌شکنجه‌'),
(1186, 8, 26, 'و یاد آرید هنگامی را که بودید گروه اندکی ناتوان شمردگان در زمین بیم داشتید که بربایند شما را مردم پس پناهتان داد و کمک کرد شما را به یاری خود و روزی داد شما را از پاکیزه‌ها شاید شکر گزارید'),
(1187, 8, 27, 'ای آنان که ایمان آوردید خیانت نکنید خدا و رسول را و خیانت نکنید سپرده‌های خویش را و شما می‌دانید'),
(1188, 8, 28, 'و بدانید که مالهای شما و فرزندان شما فتنه است و همانا خدا نزد او است پاداشی بزرگ‌'),
(1189, 8, 29, 'ای آنان که ایمان آوردید اگر بترسید خدا را بنهد برای شما جداکننده‌ای و بزداید از شما گناهان شما را و بیامرزد شما را و خدا است دارای فضلی بزرگ‌'),
(1190, 8, 30, 'و هنگامی که می‌اندیشیدند برای تو آنان که کفر ورزیدند که بازدارندت یا بکشندت یا برون‌کنندت و مکر کردند و مکر کرد خدا و خدا است بهترین مکرآوران‌'),
(1191, 8, 31, 'و هنگامی که خوانده شود بر ایشان آیتهای ما گویند همانا شنیدیم اگر می‌خواستیم می‌گفتیم همانند این نیست این جز افسانه‌های باستان‌'),
(1192, 8, 32, 'و هنگامی که گفتند بار خدایا اگر این است حقّ از نزد تو پس ببار بر ما سنگی از آسمان یا بیاور ما را عذابی دردناک‌'),
(1193, 8, 33, 'و نبوده است خدا عذاب‌کننده آنان حالی که توئی در آنان و نبوده است خدا عذاب‌کننده ایشان و ایشانند استغفارکنندگان‌'),
(1194, 8, 34, 'و چیست ایشان را (چرا) عذابشان نکند خدا حالی که بازمی‌دارند از مسجد حرام و نیستند یاران آن همانا نیستند یاران آن مگر پرهیزکاران و لیکن بیشتر ایشان نمی‌دانند'),
(1195, 8, 35, 'و نبود نمازشان نزد خانه جز صفیر و دستک‌زدنی پس بچشید عذاب را بدانچه بودید کفر می‌ورزیدید'),
(1196, 8, 36, 'همانا آنان که کافر شدند می‌دهند مالهای خود را تا بازدارند از راه خدا زود است بدهند آنها را سپس حسرتی بر ایشان شود پس شکست خورند و آنان که کفر ورزیدند بسوی دوزخ گردآورده شوند'),
(1197, 8, 37, 'تا جداسازد خدا پلید را از پاک و بنهد پلید را پاره‌ای از آن بر پاره‌ای پس انباشته سازدش با هم پس بگرداندش در دوزخ آنانند زیانکاران‌'),
(1198, 8, 38, 'بگو بدانان که کفر ورزیدند اگر دست بردارند آمرزیده شود برای ایشان آنچه گذشته است و اگر بازگردند همانا گذشت شیوه پیشینیان‌'),
(1199, 8, 39, 'و نبرد کنید با ایشان تا نشود فتنه‌ای و بشود دین همه آن برای خدا پس اگر دست برداشتند همانا خدا بدانچه می‌کنند بینا است‌'),
(1200, 8, 40, 'و اگر روی گردانیدند پس بدانید که خدا است سرپرست شما چه خوب سرپرست و چه خوب یاوری است‌'),
(1201, 8, 41, 'و بدانید که آنچه در جنگ به دست آوردید همانا برای خدا است پنج یک آن و برای پیمبر و برای نزدیکان و یتیمان و بینوایان و درماندگان راه اگر ایمان آورده‌اید به خدا و آنچه فرستادیم بر بنده خود روز جداشدن روزی که دو سپاه روبرو شدند و خدا است بر هر چیزی توانا'),
(1202, 8, 42, 'هنگامی که بودید بر کنار فرودین درّه و ایشان بر کنار فرازین آن و کاروان فرودتر از شما و اگر وعده می‌گزاردید با هم هر آینه اختلاف می‌کردید در وعده‌گاه لیکن تا بگذراند خدا کاری را که بوده است شدنی تا هلاک شود آنکه هلاک شده است از روی بینش و زنده شود آنکه زنده شده است از روی بینش و همانا خدا است شنونده دانا'),
(1203, 8, 43, 'هنگامی که نمایاندشان خداوند به تو در خواب تو اندکی و اگر می‌نمایاندشان به تو فزون هر آینه سست می‌شدید و اختلاف می‌کردید در کار لیکن خدا نگه داشت همانا اوست دانا بدانچه در سینه‌ها است‌'),
(1204, 8, 44, 'گاهی که می‌نمایاند ایشان را به شما هنگام تلاقی شما با ایشان در دیدگانتان اندک و اندک نمود شما را در دیدگان ایشان تا بگذراند خدا کاری را که بوده است شدنی و بسوی خدا بازگردانیده شوند کارها'),
(1205, 8, 45, 'ای آنان که ایمان آوردید هرگاه ملاقات کردید گروهی را در جنگ پس پایداری کنید و یاد کنید خدا را فراوان شاید رستگار شوید'),
(1206, 8, 46, 'و اطاعت کنید خدا و پیمبرش را و درنستیزید با هم که سست شوید و برود نیروی شما و شکیبا شوید همانا خدا است با صبرکنندگان‌'),
(1207, 8, 47, 'و نباشید مانند آنان که برون رفتند از خانه‌های خود به سستی و خودنمائی به مردم و بازمی‌داشتند از راه خدا و خدا است بدانچه کنند فراگیرنده‌'),
(1208, 8, 48, 'و هنگامی که آراست برای ایشان شیطان کردار ایشان را و گفت نیست چیره‌شونده شما را امروز از مردم و منم پشتیبان شما تا گاهی که دیدار کردند دو سپاه همدیگر را برگشت بر پاشنه‌های خود و گفت همانا بیزارم از شما چه می‌بینم آنچه را نمی‌بینید و همانا می‌ترسم خدا را و خدا است سخت‌شکنجه‌'),
(1209, 8, 49, 'هنگامی که می‌گفتند دورویان و آنان که در دلهاشان بیماری است فریب داد اینان را دینشان و هر که بر خدا توکل کند همانا خداوند است عزّتمند حکیم‌'),
(1210, 8, 50, 'و اگر (کاش) می‌دیدی هنگامی را که دریابند گروه کافران را فرشتگان زنند رویهایشان و پشتهایشان را و بچشید عذاب سوزان را'),
(1211, 8, 51, 'این بدان است که پیش آورد دستهای شما و آنکه نیست خدا ستم‌کننده بر بندگان‌'),
(1212, 8, 52, 'مانند شیوه خاندان فرعون و آنان که پیش از ایشان بودند کفر ورزیدند به آیتهای خدا پس گرفتشان خدا به گناهانشان همانا خدا نیرومندی است سخت‌شکنجه‌'),
(1213, 8, 53, 'این بدان است که نیست خدا تغییردهنده نعمتی که به قومی ارزانی داشته است تا تغییر دهند آنچه را که در خود ایشان است و آنکه خدا است شنونده دانا'),
(1214, 8, 54, 'مانند شیوه خاندان فرعون و آنان که پیش از ایشان بودند تکذیب کردند آیتهای پروردگار خود را پس نابودشان کردیم به گناهانشان و غرق ساختیم خاندان فرعون را و هرکدام بودند ستمگران‌'),
(1215, 8, 55, 'همانا بدترین دامها نزد خدا آنانند که کفر ورزیدند پس ایمان نمی‌آورند'),
(1216, 8, 56, 'آنان که پیمان بستی با ایشان و سپس می‌شکنند عهد خویش را در هربار و ایشان نمی‌پرهیزند'),
(1217, 8, 57, 'پس هرگاه دست بر ایشان یافتی در جنگ پراکنده سازیدیشان آنان را که پشت سر ایشانند (چنان بتاز بر ایشان که تارومار شود هر که پشت ایشان است) شاید یادآور شوند'),
(1218, 8, 58, 'و اگر بیم داشتی از قومی خیانتی را پس بیفکن بسوی ایشان برابر همانا خدا دوست ندارد خیانتکاران را'),
(1219, 8, 59, 'و نپندارند آنان که کفر ورزیدند که سبقت جستند همانا به عجز نمی‌آورند ایشان‌'),
(1220, 8, 60, 'و آماده کنید برای ایشان هرچه توانید از نیرو و از اسبان بسته که بترسانید بدان دشمن خدا و دشمن خویش را و دیگران را از غیر ایشان که ندانیدشان خدا داندشان و آنچه دهید در راه خدا پرداخت شود به شما سراسر و ستم نمی‌شوید'),
(1221, 8, 61, 'و اگر گرائیدند به صلح پس بدان گرای و توکّل کن بر خدای که او است شنونده دانا'),
(1222, 8, 62, 'و اگر خواهند با تو نیرنگ کنند همانا بس است تو را خدا او است آنکه کمک کرد تو را به یاری خویش و به مؤمنان‌'),
(1223, 8, 63, 'و الفت افکند میان دلهای آنان که اگر می‌دادی هرچه در زمین است نمی‌توانستی الفت افکنی میان دلهای آنان و لیکن خدا الفت افکند میان ایشان همانا او است عزّتمند حکیم‌'),
(1224, 8, 64, 'ای پیمبر بس است تو را خدا و آنان که پیرویت کردند از مؤمنان‌'),
(1225, 8, 65, 'ای پیمبر برانگیز مؤمنان را بر جنگ اگر باشد از شما بیست تن بردبار چیره شوند دویست تن را و اگر باشد از شما صد تن چیره شوند یکهزار را از آنان که کفر ورزیدند این بدان است که ایشانند گروهی که درنمی‌یابند'),
(1226, 8, 66, 'اکنون کاست خدا (سبک کرد) از شما و دانست که در شما است ناتوانی‌ای پس اگر باشد از شما صد تن بردبار چیره شوند دویست تن را و اگر باشد از شما هزار تن چیره آیند بر دو هزار به اذن خدا و خدا است با صبرکنندگان‌'),
(1227, 8, 67, 'نبوده است پیمبری را که باشدش بردگانی تا کشتار کند در زمین خواهید بهره دنیا را و خدا آخرت را خواهد و خدا است عزّتمند حکیم‌'),
(1228, 8, 68, 'اگر نبود سرنوشتی که از خدا پیشی گرفته است همان می‌رسید شما را در آنچه گرفتید عذابی بزرگ‌'),
(1229, 8, 69, 'پس بخورید از آنچه به غنیمت گرفتید (در جنگ) حلال پاکیزه و بترسید خدا را که خدا است آمرزنده مهربان‌'),
(1230, 8, 70, 'ای پیمبر بگو بدانان که در دست شمایند از بردگان اگر بداند خدا در دلهای شما خیری بدهد شما را بهتر از آنچه گرفته شده است از شما و بیامرزد شما را و خدا است آمرزنده مهربان‌'),
(1231, 8, 71, 'و اگر خواهند خیانت ورزیدن با تو همانا خیانت ورزیدند خدا را پیش از این پس دستیابی داد بر ایشان و خدا است دانشمند حکیم‌'),
(1232, 8, 72, 'همانا آنان که ایمان آوردند و مهاجرت کردند (از وطن آواره شدند) و جهاد کردند (کوشیدند) با مالها و جانهای خود در راه خدا و آنان که پناه دادند و یاری کردند همانا گروهی از ایشانند دوستان گروهی و آنان که ایمان آوردند و مهاجرت ننمودند (از خانمان خود آواره نشدند) نیست شما را از دوستی ایشان چیزی تا ترک خانمان کنند و اگر یاری خواستند از شما در دین پس بر شما است یاری‌کردن مگر بر قومی که میان شما و ایشان است پیمانی و خدا بدانچه می‌کنید بینا است‌'),
(1233, 8, 73, 'و آنان که کفر ورزیدند بعضی از ایشانند دوستان بعضی اگر نکنید این را می‌شود فتنه‌ای در زمین و فسادی بزرگ‌'),
(1234, 8, 74, 'و آنان که ایمان آوردند و هجرت کردند و جهاد نمودند در راه خدا و آنان که پناه دادند و یاری کردند آنانند مؤمنان به درست ایشان را است آمرزش و روزیی گرامی‌'),
(1235, 8, 75, 'و آنان که ایمان آوردند از این پس و هجرت نمودند و جهاد کردند همراه شما پس ایشانند از شما و خویشاوندان بعضی از ایشانند سزاوارتر به بعضی در کتاب خدا همانا خداوند است به همه چیز دانا');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'بیزاری از خدا و پیمبرش بسوی آنان که پیمان بستید از مشرکان‌'),
(1237, 9, 2, 'پس بگردید در زمین چهار ماه و بدانید که نیستید ناتوان کننده خدا و آنکه خدا است خوارکننده کافران‌'),
(1238, 9, 3, 'و اعلانی است از خدا و پیمبرش به مردم در روز حجّ اکبر که خدا بیزار است از مشرکان و پیمبرش پس اگر توبه کنید بهتر است برای شما و اگر پشت کردید بدانید که نیستید عاجز کننده خدا و بشارت ده کافران را به عذابی دردناک‌'),
(1239, 9, 4, 'مگر آنان را که پیمان بستید از مشرکان پس نکاستند شما را چیزی و پشتیبانی نکردند بر شما کسی را پس به پایان رسانید بدیشان پیمانشان را تا سرآمدشان و خدا دوست دارد پرهیزکاران را'),
(1240, 9, 5, 'تا گاهی که به پایان رسید ماه‌های حرام پس بکشید مشرکان را هرکجا بیابیدشان و دستگیرشان کنید و تنگ بر ایشان گیرید و بنشینید برای ایشان به هر کمینگاهی پس اگر توبه کردند و بپای داشتند نماز را و دادند زکات را رها کنید راه ایشان را همانا خدا است آمرزنده مهربان‌'),
(1241, 9, 6, 'و اگر یکی از مشرکان به تو پناهنده شد پس پناهش ده تا بشنود گفتار خدا را سپس برسانش به مأمن خویش این بدان است که آنانند گروهی که نمی‌دانند'),
(1242, 9, 7, 'چگونه باشد برای مشرکان پیمانی نزد خدا و نزد پیمبرش جز آنان که پیمان بستید نزد مسجد حرام پس مادام که پایدارند برای شما پایدار باشید برای آنان همانا خدا دوست دارد پرهیزکاران را'),
(1243, 9, 8, 'چگونه و اگر چیره آیند بر شما رعایت نکنند در شما خویشاوندی و نه پیمانی را خوشنودتان سازند با دهانهای خود و نمی‌خواهد دلهای ایشان و بیشتر ایشانند نافرمانان‌'),
(1244, 9, 9, 'فروختند آیتهای خدا را به بهائی کم پس بازداشتند از راه او چه زشت است آنچه بودند می‌کردند'),
(1245, 9, 10, 'رعایت نمی‌کنند در مؤمنی خویشاوندی و نه پیمانی را و ایشانند تجاوزگران‌'),
(1246, 9, 11, 'پس اگر توبه کردند و بپای داشتند نماز را و دادند زکات را پس برادرانند شما را در دین و تفصیل دهیم آیتها را برای قومی که می‌دانند'),
(1247, 9, 12, 'و اگر شکستند سوگندهای خویش را پس از پیمانشان و بد سگالیدند در دین شما پس بکشید پیشوایان کفر را که نیست سوگندهائی برای ایشان شاید دست بردارند'),
(1248, 9, 13, 'چرا پیکار نکنید با قومی که شکستند سوگندهای خود را و آهنگ آن کردند که پیمبر را برون کنند و ایشان آغاز (جنگ) کردند با شما نخستین‌بار آیا همی‌ترسیدشان پس خدا سزاوارتر است که بترسیدش اگر هستید مؤمنان‌'),
(1249, 9, 14, 'بکشیدشان عذابشان کند خدا به دست شما و خوارشان گرداند و یاریتان کند بر ایشان و شفا بخشد سینه‌های قومی مؤمنان را'),
(1250, 9, 15, 'و بردارد خشم را از دلهای ایشان و توبه پذیرد خدا از هر که خواهد و خدا است دانای حکیم‌'),
(1251, 9, 16, 'یا پنداشتید که رها شوید حالی که هنوز نمی‌داند خدا آنان را که کوشیدند (جهاد کردند) از شما و نگرفتند جز خدا و پیمبرش و مؤمنان همرازی و خدا آگاه است بدانچه کنید'),
(1252, 9, 17, 'نرسد مشرکان را که آباد کنند مساجد خدا را حالی که گواهی‌دهندگانند بر خویشتن به کفر آنان باطل شده است کردارشان و در آتشند جاودانان‌'),
(1253, 9, 18, 'جز این نیست که آباد می‌کند مساجد خدا را آنکه ایمان آورده است به خدا و روز بازپسین و بپا داشته است نماز را و بداده است زکات را و نترسیده است جز خدا را پس امید است آنان که باشند از هدایت‌شدگان‌'),
(1254, 9, 19, 'آیا قرار دادید آب دادن حاجیان و آبادی مسجد حرام را مانند آنکه ایمان آورده است به خدا و در روز آخر و بکوشیده است در راه خدا یکسان نیستند نزد خدا و خدا هدایت نکند گروه ستمگران را'),
(1255, 9, 20, 'آنان که ایمان آوردند و هجرت کردند و بکوشیدند در راه خدا به مالها و جانهای خود بزرگتر است پایه آنان نزد خدا و آنانند رستگاران‌'),
(1256, 9, 21, 'نوید دهدشان پروردگارشان به رحمتی از خود و خوشنودی و باغستانی که ایشان را است در آن نعمتی پایدار'),
(1257, 9, 22, 'جاودانند در آن همیشه همانا خداوند است نزد او پاداشی گران‌'),
(1258, 9, 23, 'ای آنان که ایمان آوردید نگیرید پدران خود و برادران خود را دوستانی اگر برگزیدند کفر را بر ایمان و آنکه دوستی کند با ایشان از شما پس آنانند ستمگران‌'),
(1259, 9, 24, 'بگو اگر هستند پدران شما و فرزندان شما و برادران شما و همسران شما و بستگان شما و مالهائی که اندوخته‌اید و سوداگریی که از کسادش ترسید و خانه‌هائی که خوش دارید دوست‌تر بسوی شما از خدا و پیمبرش و جهادی در راهش پس منتظر باشید تا بیارد خدا امر خویش را و هدایت نکند خدا قوم نافرمانان را'),
(1260, 9, 25, 'همانا یاریتان کرد خدا در جایهائی فراوان و روز حنین هنگامی که به شگفت آورد شما را فزونیتان پس بی نیاز نکرد شما را به چیزی و تنگ شد بر شما زمین با فراخیش سپس برگشتید پشت‌کنندگان‌'),
(1261, 9, 26, 'پس فرستاد خدا آرامش خود را بر پیمبرش و بر مؤمنان و فرستاد لشکرهائی که ندیدید آنها را و عذاب کرد آنان را که کفر ورزیدند و این است پاداش کافران‌'),
(1262, 9, 27, 'سپس توبه کند خدا پس از آن بر هر که خواهد و خدا است آمرزنده مهربان‌'),
(1263, 9, 28, 'ای آنان که ایمان آوردید جز این نیست که مشرکان پلیدند پس نزدیک نشوند مسجد حرام را پس از سالشان این (امسال) و اگر ترسیدید از سنگینی هزینه بزودی بی‌نیازتان گرداند خدا از فضل خود اگر خواهد همانا خداوند است دانای حکیم‌'),
(1264, 9, 29, 'بکشید آنان را که ایمان نیارند به خدا و روز آخر و حرام ندارند آنچه را که حرام کرده است خدا و رسولش و نمی‌گروند به کیش حقّ از آنان که داده شدند کتاب را تا جزیه پردازند از دسترنج خود (با دست خود یا به جای خود) و ایشانند سرافکندگان‌'),
(1265, 9, 30, 'و گفتند جهودان عزیر فرزند خدا است و گفتند ترسایان مسیح است فرزند خدا این است گفتارشان با دهانهاشان هم آهنگ شوند سخن آنان را که کفر ورزیدند از پیش بکشدشان خدا کجا به دروغ رانده می‌شوند'),
(1266, 9, 31, 'گرفتند کشیشان و دیرنشینان خود را خدایانی جز خدا و مسیح را فرزند مریم حالی که فرمان نشدند جز آنکه پرستش کنند خدائی یگانه را نیست خدائی جز او منزّه است از آنچه شرک ورزند'),
(1267, 9, 32, 'خواهند فرو نشانند پرتو خدا را با دهانهای خود و نخواهد خدا مگر به انجام رساند پرتو خویش را و اگرچه ناخوش دارند کافران‌'),
(1268, 9, 33, 'اوست آنکه فرستاد رسولش را به هدایت و دین حقّ تا چیره گرداندش بر کیشها همگی و هرچند نپسندند مشرکان‌'),
(1269, 9, 34, 'ای آنان که ایمان آوردید همانا بسی از کشیشان و دیرنشینان می‌خورند اموال مردم را به ناروا و بازمی‌دارند از راه خدا و آنان که می‌اندوزند زر و سیم را و نمی‌دهندش در راه خدا نویدشان ده به عذابی دردناک‌'),
(1270, 9, 35, 'روزی که تفتیده شوند در آتش دوزخ پس داغ شود بدانها پیشانیهاشان و پهلوهاشان و پشتهاشان این است آنچه اندوختید برای خویشتن پس بچشید آنچه را بودید می‌اندوختید'),
(1271, 9, 36, 'همانا شماره ماه‌ها نزد خدا دوازده ماه است در کتاب خدا روزی که آفرید آسمانها و زمین را از آنها است چهار ماه حرام این است آئین استوار پس ستم نکنید در آنها خویش را و پیکار کنید مشرکان را همگی چنانکه پیکارتان کنند همگی و بدانید که خدا است با پرهیزکاران‌'),
(1272, 9, 37, 'جز این نیست که نسی‌ء (فراموشی) افزایش است در کفر گمراه شوند بدان آنان که کفر ورزیدند حلالش شمرند سالی و حرام شمرندش سالی تا پایمال کنند شمار آنچه را حرام کرد خدا پس حلال کنند آنچه را حرام کرد خدا آراسته برای ایشان زشتی کردارشان و خدا هدایت نمی‌کند گروه کافران را'),
(1273, 9, 38, 'ای آنان که ایمان آوردید چیست شما را که هرگاه گفته شود به شما بسیج کنید در راه خدا سرگرانی کنید بسوی زمین آیا خوشنود شدید به زندگانی دنیا به جای آخرت همانا نیست بهره زندگانی دنیا در آخرت مگر اندک‌'),
(1274, 9, 39, 'اگر نکوچید عذاب کند شما را عذابی دردناک و برگیرد به جای شما گروهی را جز شما و هرگز زیانش نرسانید به چیزی و خدا است بر هر چیز توانا'),
(1275, 9, 40, 'اگر یاریش نکنید همانا خدا یاریش کرد هنگامی که برونش کردند آنان که کفر ورزیدند حالی که دوّمین دو تن بودند هر دو در غار هنگامی که می‌گفت به یار خود اندوهگین نباش که خدا با ما است پس فرستاد خدا آرامش خویش را بر او و یاریش کرد به لشگرهائی که ندیدیدشان و گردانید سخن آنان را که کفر ورزیدند فرودین و سخن خدا است فرازین و خدا است عزتمند حکیم‌'),
(1276, 9, 41, 'بکوچید سبک و سنگین و پیکار کنید با مالها و جانهای خود در راه خدا این بهتر است برای شما اگر بدانید'),
(1277, 9, 42, 'اگر می‌بود بهره‌ای نزدیک یا سفری هموار همانا پیرویت می‌کردند (از پیت می‌آمدند) لیکن گران آمد بر ایشان رنج و بزودی سوگند یاد کنند به خدا که اگر می‌توانستیم می‌آمدیم با شما خویشتن را تباه کنند و خدا می‌داند که ایشانند دروغگویان‌'),
(1278, 9, 43, 'بگذرد خدا از تو چرا بدانان اذن دادی تا پدیدار شود برایت آنان که راست گفتند و بدانی دروغگویان را'),
(1279, 9, 44, 'اذن نخواهند از تو آنان که ایمان آرند به خدا و روز آخر که جهاد کنند با مالها و جانهای خود و خدا است دانا به پرهیزکاران‌'),
(1280, 9, 45, 'جز این نیست که اذن از تو خواهند آنان که ایمان نیارند به خدا و روز آخر و شک دارد دلهای ایشان پس ایشانند در شک خویش متردّدان‌'),
(1281, 9, 46, 'و اگر می‌خواستند برون آمدن را هر آینه مهیا می‌کردند برایش تهیه‌ای و لیکن خوش نداشت خدا جنبش آنان را پس بازنشاندشان و گفته شد بازنشینید با بازنشستگان‌'),
(1282, 9, 47, 'اگر برون می‌آمدند در شما نمی‌افزودند شما را جز گسیختگی و همانا می‌گشتند میان شما تا فتنه برای شما جویند و در شما است شنوندگانی برای آنان و خدا دانا است به ستمگران‌'),
(1283, 9, 48, 'همانا فتنه‌جوئی نمودند از پیش و دگرگون کردند برای تو کارها را تا بیامد حقّ و نمودار شد (پیروز شد) امر خدا و ایشانند ناخوش‌دارندگان‌'),
(1284, 9, 49, 'و از ایشان است آنکه گوید رخصتم ده دچار فتنه‌ام نکن همانا در فتنه افتادند و هر آینه دوزخ است فراگیرنده به کافران‌'),
(1285, 9, 50, 'اگر خوشی رسدت ناخوش آیدشان و اگر رسدت بدی گویند همانا گرفتیم کار خود را از پیش و برمی‌گردند حالی که ایشانند شادی‌کنان‌'),
(1286, 9, 51, 'بگو هرگز نرسد به ما جز آنچه برای ما نوشته است خدا او است سرپرست ما و بر خدا باید توکّل کنند مؤمنان‌'),
(1287, 9, 52, 'بگو آیا انتظار دارید به ما جز یکی از دو خوشی را و ما انتظار داریم به شما که رساندتان خدا به عذابی از نزد خویش یا به دست ما پس منتظر باشید که مائیم با شما منتظران‌'),
(1288, 9, 53, 'بگو انفاق کنید خواه یا ناخواه هرگز پذیرفته نشود از شما که هستید گروهی نافرمانان‌'),
(1289, 9, 54, 'و بازشان نداشت از آنکه پذیرفته شود بخششهاشان جز آنکه آنان کفر ورزیدند به خدا و به پیمبرش و نیایند نماز را جز آنکه آنانند فسردگان و نه بخشش کنند جز آنکه ایشانند ناخواستاران‌'),
(1290, 9, 55, 'پس شگفتت نیارد مالهای ایشان و نه فرزندان ایشان جز این نیست که خدا خواهد تا عذابشان کند بدانها در زندگی دنیا و برون رود جانهای ایشان حالی که هستند کافران‌'),
(1291, 9, 56, 'و سوگند یاد کنند به خدا که ایشانند از شما و نیستند از شما و لیکن آنانند گروهی هراسان‌'),
(1292, 9, 57, 'اگر بیابند پناهگاهی یا غارهائی یا نهانگاهی هر آینه بسوی آن گرایند و ایشانند شتابان‌'),
(1293, 9, 58, 'و از ایشان است آنکه خرده بر تو گیرد در صدقات تا اگر داده شوند از آنها خوشنود شوند و اگر داده نشوند از آنها ناگهان ایشانند خشم‌آوران‌'),
(1294, 9, 59, 'و اگر که ایشان خوشنود می‌شدند بدانچه خدا و پیمبرش بدیشان داده و می‌گفتند بس است ما را خدا زود است بدهد به ما خدا از فضل خویش و پیمبرش همانا مائیم بسوی خدا گروندگان‌'),
(1295, 9, 60, 'جز این نیست که صدقات برای بینوایان و دریوزگان و کارگران بر آنها و دل به دست آوردگان و در آزاد کردن بندگان و وام داران و در راه خدا و درماندگان راه راست بایسته است از خدا و خدا است دانای حکیم‌'),
(1296, 9, 61, 'و از ایشانند آنان که بیازارند پیمبر را و گویند او است گوشی (سخن شنو) بگو گوش خیری است برای شما ایمان آورد به خدا و ایمان آورد برای مؤمنان و رحمتی است برای آنان که ایمان آوردند از شما و آنان که می‌آزارند پیمبر خدای را ایشان را است عذابی دردناک‌'),
(1297, 9, 62, 'سوگند خورند به خدا برای شما تا خوشنودتان کنند و خدا و پیمبرش سزاوارتر است که خوشنود کنندش اگر هستید مؤمنان‌'),
(1298, 9, 63, 'آیا ندانستید که هر که با خدا و پیمبرش درافتد همانا وی را است آتش دوزخ جاودان در آن این است خواری بزرگ‌'),
(1299, 9, 64, 'ترسند منافقان که فرستاده شود بر ایشان سوره‌ای که آگهیشان دهد آنچه در دلهای ایشان است بگو استهزاء کنید همانا خدا است برون‌آورنده آنچه می‌ترسید'),
(1300, 9, 65, 'و اگر پرسیشان همانا گویند جز این نیست که فرو می‌رفتیم (سرگرمی داشتیم) و بازی می‌کردیم بگو آیا به خدا و آیتهای او و پیمبرش استهزاء می‌کردید'),
(1301, 9, 66, 'پوزش نخواهید همانا کافر شدید پس از ایمانتان اگر درگذریم از گروهی از شما عذاب کنیم گروهی را بدانکه بودند گنهکاران‌'),
(1302, 9, 67, 'مردان دوروی و زنان دوروی برخیشان از آن برخی است فرمان دهند به زشتی و بازدارند از نیکی و بسته دارند دستهای خود را فراموش کردند خدای را پس فراموششان کرد همانا دورویانند نافرمانان‌'),
(1303, 9, 68, 'وعده داد خدا مردان دوروی و زنان دوروی و کافران را آتش دوزخ جاودانان در آن بس است ایشان را و براندشان خدا و ایشان را است عذابی پاینده‌'),
(1304, 9, 69, 'مانند آنان که پیش از شما بودند سخت‌تر از شما در نیرو و بیشتر در خواسته‌ها و فرزندان پس کامیاب شدند از بهره خویش سپس شما کامیاب شدید از بهره خود چنانکه کامیاب شدند آنان که پیش از شما بودند از بهره خود و فرورفتید چنانکه فرورفتند (سرگرم شدید) آنان تباه شد کردارشان در دنیا و آخرت و ایشانند زیانکاران‌'),
(1305, 9, 70, 'آیا نرسیدشان داستان آنان که پیش از ایشان بودند قوم نوح و عاد و ثمود و قوم ابراهیم و یاران مَدیَن و واژگون شدگان آمدندشان پیمبرانشان به نشانیها پس ستم نکرد بر ایشان خدا لیکن بودند خویشتن را ستم می‌کردند'),
(1306, 9, 71, 'مردان مؤمن و زنان مؤمنه برخی از ایشانند دوستان برخی فرمان کنند به نیکی و بازدارند از بدی و بپای دارند نماز را و بدهند زکوة را و فرمان برند خدا و پیمبرش را آنان را بزودی رحم کند خدا و هر آینه خدا است عزّتمند حکیم‌'),
(1307, 9, 72, 'وعده داد خدا مردان مؤمن و زنان مؤمنه را باغهائی که روان است زیر آنها جویها جاودانان در آن و نشیمنهای پاکیزه در باغستان جاودان و خوشنودی از خدا است بزرگتر این است آن رستگاری بزرگ‌'),
(1308, 9, 73, 'ای پیمبر نبرد کن با کافران و منافقان و درشتی نمای بر ایشان و جایگاه ایشان است دوزخ و چه بد جایگاهی است‌'),
(1309, 9, 74, 'سوگند خورند به خدا نگفتند و هر آینه گفتند سخن کفر را و کافر شدند پس از اسلامشان و خواستند آنچه را بدان نرسیدند و کین ندارند مگر از آن رو که بی‌نیازشان کرد خدا و پیمبرش از فضل خود پس اگر توبه کنند بهتر باشدشان و اگر پشت کنند عذابشان کند خدا عذابی دردناک در دنیا و آخرت و نیستشان در زمین دوست و نه یاوری‌'),
(1310, 9, 75, 'و از ایشان است آنکه پیمان بندد با خدا که اگر داد به ما از فضل خویش همانا صدقه دهیم و بشویم از شایستگان‌'),
(1311, 9, 76, 'تا گاهی که داد بدیشان از فضلش بخل ورزیدند بدان و پشت کردند حالی که ایشانند روی‌گردانان‌'),
(1312, 9, 77, 'پس پیرو ایشان گردانید نفاقی را در دلهای ایشان تا روزی که بدو رسند بدانچه شکستند آنچه را با خدا پیمان بستند و بدانچه بودند دروغ می‌گفتند'),
(1313, 9, 78, 'آیا ندانستند که خدا می‌داند نهانشان و رازشان را و آنکه خدا است دانای نهانها'),
(1314, 9, 79, 'آنان که نکوهش کنند داوطلبان را از مؤمنان در صدقات و آنان را که ندارند جز تلاش خویش پس مسخره کنندشان خدا آنان را مسخره کند و برای آنان است عذابی دردناک‌'),
(1315, 9, 80, 'آمرزش بخواه برای آنان یا نخواه اگر هفتاد بار برای ایشان آمرزش خواهی نیامرزدشان خدا این بدان است که کفر ورزیدند به خدا و پیمبرش و خدا هدایت نکند گروه نافرمانان را'),
(1316, 9, 81, 'شاد شدند کنارگیرندگان به بازنشستن خود بر زیان پیمبر خدا و ناخوش داشتند جهادکردن را به مالها و جانهای خود در راه خدا و گفتند نکوچید در گرما بگو آتش دوزخ سخت‌تر است به سوزش اگر درمی‌یافتند'),
(1317, 9, 82, 'باید بخندند کم و بگریند بسیار پاداشی بدانچه بودند دست می‌آوردند'),
(1318, 9, 83, 'پس اگر بازگردانیدت خدا بسوی گروهی از ایشان پس دستور خواستند از تو برای کوچ بگو هرگز با من برون نیائید هیچگاه و هرگز نبرد نکنید با من دشمنی را همانا خوشنود شدید به بازنشستن نخستین بار پس بنشینید با بازماندگان‌'),
(1319, 9, 84, 'و نماز نیار بر یکیشان که بمیرد هیچگاه و نایست بر قبرش همانا ایشان کفر ورزیدند به خدا و رسولش و مردند حالی که آنانند نافرمانان‌'),
(1320, 9, 85, 'شگفت نیاردت خواسته‌ها و فرزندان ایشان جز این نیست که خدا خواهد عذابشان کند بدانها در دنیا و برون رود جانهاشان حالی که ایشانند کافران‌'),
(1321, 9, 86, 'و هرگاه فرود آید سوره‌ای که ایمان آرید به خدا و جهاد کنید همراه پیمبرش رخصت طلبند از تو توانگران ایشان و گویند بازگذار ما را باشیم با بازنشستگان‌'),
(1322, 9, 87, 'خرسند شدند که با بازماندگان مانند و مُهر نهاده شد بر دلهای ایشان چنانکه درنمی‌یابند'),
(1323, 9, 88, 'لیکن پیمبر و آنان که ایمان آوردند با وی جهاد کردند با مالها و جانهای خود و برای آنان است خوبیها و ایشانند رستگاران‌'),
(1324, 9, 89, 'آماده کرد خدا برای ایشان باغهائی که روان است زیر آنها جویها جاودانان در آن این است رستگاری بزرگ‌'),
(1325, 9, 90, 'آمدند پوزش‌خواهان از اعراب (دشت نشینان) که اذن داده شود بدیشان و بازنشستند آنان که دروغ گفتند با خدا و پیمبرش زود است رسد آنان را که کفر ورزیدند از ایشان عذابی دردناک‌'),
(1326, 9, 91, 'نیست بر ناتوانان و نه بر بیماران و نه بر آنان که ندارند چیزی که هزینه کنند پروائی اگر نکو اندیشند برای خدا و پیمبرش نیست بر نکوکاران راهی و خدا است آمرزنده مهربان‌'),
(1327, 9, 92, 'و نه بر آنان که گاهی که بیایندت تا سوارشان کنی گوئی ندارم آنچه بر آن سوارتان کنم برگردند حالی که دیدگانشان اشکبار است اندوهگینند چرا ندارند چیزی که هزینه کنند'),
(1328, 9, 93, 'جز این نیست که راه آنانی است که از تو اذن می‌خواهند حالی که ایشانند توانگران خوشنودند که با بازماندگان مانند و مُهر نهاد خدا بر دلهای آنان پس نمی‌دانند'),
(1329, 9, 94, 'بهانه آرند برای شما گاهی که بازگردید بسوی ایشان بگو بهانه میارید که باور نداریمتان همانا آگاه ساخت ما را خدا از اخبار شما و زود است ببیند خدا کردار شما را و پیمبرش سپس بازگردانیده شوید بسوی دانای نهان و آشکار پس آگهیتان دهد بدانچه بودید می‌کردید'),
(1330, 9, 95, 'زود است سوگند خورند به خدا برای شما گاهی که بازگردید بسوی ایشان که روی گردانید از ایشان پس روی بگردانید از ایشان که ایشانند چرک و جایگاه ایشان است دوزخ پاداشی بدانچه بودند دست می‌آوردند'),
(1331, 9, 96, 'سوگند یاد کنند برای شما تا خوشنود شوید از ایشان اگر شما خوشنود شوید از ایشان همانا خدا خوشنود نشود از گروه نافرمانان‌'),
(1332, 9, 97, 'اعراب (دشت‌نشینان) سخت‌ترند در کفرورزی و دوروئی و سزاوارترند که ندانند مرزهای آنچه خدا فرستاده است بر پیمبرش و خدا است دانای حکیم‌'),
(1333, 9, 98, 'و از اعراب (دشت‌نشینان) است آنکه می‌شمرد آنچه را انفاق کند زیانی و انتظار کشد به شما پیش آمدها را بر ایشان است پیش آمد بد و خدا است شنوای دانا'),
(1334, 9, 99, 'و از اعراب (دشت‌نشینان) است آنکه ایمان آرد به خدا و روز بازپسین و برگیرد آنچه را انفاق کند نزدیکیهائی نزد خدا و آمرزشهائی از پیمبر همانا آنها است نزدیک شدنی برای ایشان بزودی درآردشان خدا به رحمتی از خدا همانا خداست آمرزنده مهربان‌'),
(1335, 9, 100, 'و پیشینیان نخستین از مهاجرین (آوارگان) و انصار (پناه‌دهندگان) و آنان که پیرویشان کردند به نکوکاری خوشنود است خدا از ایشان و خوشنودند از او و آماده کرده است برای ایشان باغهائی که روان است زیر آنها جویها جاودانند در آن همیشه این است رستگاری بزرگ‌'),
(1336, 9, 101, 'و از آنان که پیرامون شمایند از اعراب (دشت نشینان) دورویانند و از مردم مدینه ورزیدگان در دوروئی نشناسیشان ما شناسیمشان زود است عذابشان کنیم دوبار سپس برگردانیده شوند بسوی عذابی بزرگ‌'),
(1337, 9, 102, 'و دیگران که اقرار کردند به گناهان خویش بیامیختند کرداری شایسته با دیگری ناشایسته امید است خدا بپذیرد توبه ایشان را همانا خداوند است آمرزنده مهربان‌'),
(1338, 9, 103, 'بگیر از اموالشان صدقه تا پاک سازیشان و پاکیزه گردانیشان بدان و نماز گزار بر ایشان که نماز تو آرامشی است برای ایشان و خدا است شنوای دانا'),
(1339, 9, 104, 'آیا ندانستند که خدا است آنکه می‌پذیرد توبه را از بندگان خویش و می‌ستاند صدقات را و آنکه خدا است بسیار توبه‌پذیرنده مهربان‌'),
(1340, 9, 105, 'بگو عمل کنید زود هست ببیند خدا کردار شما را و پیمبرش و مؤمنان و بزودی بازگردانیده شوید بسوی دانای نهان و هویدا تا آگهیتان دهد بدانچه بودید می‌کردید'),
(1341, 9, 106, 'که به امید خدایند یا عذابشان کند و یا توبه‌شان پذیرد و خدا است دانای حکیم‌'),
(1342, 9, 107, 'و آنان که برگرفتند مسجدی تا بیازارند و کفر ورزند و جدائی نهند میان مؤمنان و کمینگاهی برای آنان که با خدا و پیمبرش نبرد نمودند از پیش و همانا سوگند آورند که نخواستیم ما جز نکوکاری و خدا گواهی دهد که آنانند دروغگویان‌'),
(1343, 9, 108, 'بپای نایست در آن هیچگاه همانا مسجدی که بنیاد شده است بر پرهیزکاری از نخستین روز سزاوارتر است که بپای ایستی در آن در آنند مردانی که دوست دارند آنکه پاک شوند و خدا است دوستدار پاکیزگی‌جویان‌'),
(1344, 9, 109, 'آیا آنکه بنیاد کرده است شالوده آن را بر ترسی از خدا و خوشنودیی بهتر است یا آنکه بنیاد نهاده است سازمانش را بر کنار پرتگاهی سراشیب که واژگونش ساخت در آتش دوزخ و خدا هدایت نکند گروه ستمگران را'),
(1345, 9, 110, 'بماند سازمانی که ساختند همیشه ریبی در دلهای ایشان مگر آنکه بگسلد از هم (پاره پاره شود) دلهای ایشان و خدا است دانای حکیم‌'),
(1346, 9, 111, 'همانا خدا خرید از مؤمنان جانها و مالهای ایشان را که برای ایشان است بهشت جنگ کنند در راه خدا پس بکشند و کشته شوند وعده‌ای است بر او حقّ در تورات و انجیل و قرآن و کیست وفادارتر به عهد خود از خدا پس شاد باشید به سوداگریی که سودا نمودید و این است آن رستگاری بزرگ‌'),
(1347, 9, 112, 'توبه‌کنندگان پرستشگران سپاسگزاران رهروان رکوع‌گزاران سجده‌کنندگان امرکنندگان به نیکی و بازدارندگان از بدی و نگهبانان مرزهای خدا و بشارت ده به مؤمنان‌'),
(1348, 9, 113, 'نرسد پیمبر را و نه آنان را که ایمان آوردند اینکه آمرزش خواهند برای مشرکان و اگر چه باشند خویشاوندان پس از آنکه آشکار شد برای ایشان که آنانند یاران دوزخ‌'),
(1349, 9, 114, 'و نبود آمرزش خواستن ابراهیم برای پدر خود جز از روی وعده‌ای که بدو داد سپس گاهی که آشکار شد برای او آنکه وی دشمنی است برای خدا بیزاری جست از او و همانا ابراهیم است بسیار زاری‌کننده‌ای شکیبا'),
(1350, 9, 115, 'و نیست خدا که گمراه سازد قومی را پس از آنکه رهبریشان کرد تا بنمایدشان که از چه چیز پرهیز کنند همانا خداوند است به همه چیز دانا'),
(1351, 9, 116, 'همانا خدا از آن وی است پادشاهی آسمانها و زمین زنده کند و بمیراند و نیست شما را جز خدا دوست و نه یاوری‌'),
(1352, 9, 117, 'همانا پذیرفت خدا توبه پیمبر و مهاجران و انصار را که پیرویش نمودند به هنگام سختی پس از آنکه نزدیک بود بلغزد دلهای گروهی از ایشان پس توبه کرد بر ایشان همانا او است بدیشان نوازنده مهربان‌'),
(1353, 9, 118, 'و بر آن سه تن که بازماندند تا آنکه تنگ شد بر ایشان زمین با فراخیش و تنگ آمد بر ایشان جانهای ایشان و دانستند که نیست پناهگاهی از خدا جز بسوی او پس توبه کرد بر ایشان تا توبه کنند همانا خدا است توبه‌پذیرنده مهربان‌'),
(1354, 9, 119, 'ای آنان که ایمان آوردید بترسید خدا را و باشید با راستگویان‌'),
(1355, 9, 120, 'نرسد مردم مدینه و آنان را که پیرامون آنند از اعراب (دشت نشینان) که بازمانند از رسول خدا و نه برگزینند جان خویش را بر جانش این بدان است که نمیرسدشان تشنگی و نه رنجی و نه گرسنگی در راه خدا و نه پای نهند در جائی که به خشم آرد کافران را و نه نبردی با دشمنی کنند جز آنکه نوشته شود برای ایشان بدان عملی صالح همانا خدا تباه نکند پاداش نکوکاران را'),
(1356, 9, 121, 'و نه هزینه کنند خرد یا کلان و نه برند دره جز آنکه نوشته شود برای ایشان تا پاداش دهدشان خدا بهترین آنچه را بودند می‌کردند'),
(1357, 9, 122, 'نرسد مؤمنان را که بکوچند همگی پس چرا نکوچد از هر گروهی از ایشان دسته‌ای تا دانش جویند در دین و تا بترسانند قوم خویش را گاهی که بازگردند بسوی آنان شاید بترسند ایشان‌'),
(1358, 9, 123, 'ای آنان که ایمان آوردید پیکار کنید آنان را که نزدیکند به شما از کافران و باید بیابند در شما درشتخوئی و بدانید که خدا است با پرهیزکاران‌'),
(1359, 9, 124, 'و هرگاه فرود شود سوره‌ای گویند گروهی از ایشان کدامیک را از شما بیفزودش ایمان اما آنان که ایمان آوردند بیفزودشان ایمان و ایشانند شادی کنان‌'),
(1360, 9, 125, 'و اما آنان که در دلهاشان بیماری است پس بیفزودشان پلیدی بر پلیدیشان و مُردند حالی که آنانند کافران‌'),
(1361, 9, 126, 'آیا نبینند که آزموده می‌شوند همه ساله یک بار یا دو بار پس نه توبه می‌کنند و نه یادآور می‌شوند'),
(1362, 9, 127, 'و هرگاه فرود شود سوره‌ای بنگرد برخیشان به برخی آیا می‌بیند شما را کسی سپس برگردند برگرداند خدا دلهای ایشان را بدانکه آنانند گروهی نادریابندگان‌'),
(1363, 9, 128, 'همانا بیامد شما را پیمبری از خود شما گران است بر او رنج بردن شما حریص است بر شما به مؤمنان است نوازنده‌ای مهربان‌'),
(1364, 9, 129, 'پس اگر پشت کردند بگو بس است مرا خدا نیست خداوندی جز او بر او توکل کردم و او است پروردگار عرش بزرگ‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'این است آیتهای کتاب حکیم (نامه استوار)'),
(1366, 10, 2, 'آیا شگفت آمیز شد برای مردم که وحی نمودیم بسوی مردی از ایشان که بترسان مردم را و بشارت ده بدانان که ایمان آوردند که ایشان را است قدم راستی نزد پروردگارشان گفتند کافران همانا این است جادوگری آشکار'),
(1367, 10, 3, 'همانا پروردگار شما خداوندی است که آفرید آسمانها و زمین را در شش روز پس استوار شد بر عرش تدبیر کند کار را نیست شفاعت‌کننده مگر پس از رخصتش این است خدا پروردگار شما پس پرستش کنیدش آیا یادآور نمی‌شوید'),
(1368, 10, 4, 'بسوی او است بازگشت شما همگی وعده خدا است به درست هر آینه آغاز کند آفرینش را و سپس برگرداندش تا پاداش دهد آنان را که ایمان آوردند و کردار شایسته کردند به داد و آنان را که کفر ورزیدند ایشان را است نوشابه‌ای از آب جوشان و عذابی دردناک بدانچه بودند کفر می‌ورزیدند'),
(1369, 10, 5, 'او است آن که گردانید خورشید را درخشندگی و ماه را پرتوی و گردانیدش منزلهائی تا بدانید شمار سالها و حساب را نیافریده است خدا این را جز به حقّ تفصیل دهد آیتها را برای گروهی که می‌دانند'),
(1370, 10, 6, 'همانا در گردش شبانه‌روز و آنچه آفرید خدا در آسمانها و زمین آیتهائی است برای گروهی که پرهیز کنند'),
(1371, 10, 7, 'همانا آنان که امید ندارند ملاقات ما را و خوشنود شدند به زندگانی دنیا و دل بدان بستند و آنان که از آیتهای ما غفلت دارند'),
(1372, 10, 8, 'آنان را جایگاه آتش است بدانچه بودند دست می‌آوردند'),
(1373, 10, 9, 'همانا آنان که ایمان آوردند و کارهای شایسته کردند رهبریشان کند پروردگارشان به ایمانشان روان شود زیرا آنان جویهائی در باغستان نعمتها'),
(1374, 10, 10, 'گفتارشان در آن منزهی تو بار خدایا و درود ایشان است در آن سلام و آخرین گفتارشان آنکه سپاس خدای را پروردگار جهانیان‌'),
(1375, 10, 11, 'و اگر بشتابد خدا بسوی بدی برای مردم مانند شتافتن ایشان بسوی خوبی هر آینه گذرانده شود سرآمد ایشان پس می‌گذاریم آنان را که امید ندارند ملاقات ما را در سرکشی خود فرو روند'),
(1376, 10, 12, 'و هرگاه برسد انسان را سختی بخواند ما را بر پهلو یا نشسته یا ایستاده تا گاهی که بازکردیم (برگشودیم) از او سختیش را درگذرد چنانکه گوئی نخوانده است ما را بسوی سختی که بدو رسیده بدینسان بیاراست برای اسراف‌کنندگان آنچه بودند می‌کردند'),
(1377, 10, 13, 'و هر آینه نابود کردیم قرنهائی که پیش از شما بودند گاهی که ستم کردند و بیامدشان پیمبرانشان به نشانیها و نبودند که ایمان آرند بدینسان کیفر دهیم به گروه گنهکاران‌'),
(1378, 10, 14, 'سپس گردانیدیم شما را جانشینانی در زمین از پس ایشان تا بنگریم چگونه عمل می‌کنید'),
(1379, 10, 15, 'و هرگاه خوانده شود بر ایشان آیتهای ما آشکار گویند آنان که امید ندارند ملاقات ما را بیاور قرآنی غیر از این یا تبدیلش کن بگو نرسد مرا که تبدیلش کنم از نزد خود پیروی نکنم جز آنچه را وحی شود به سویم همانا می‌ترسم اگر نافرمانی کنم پروردگارم را از عذاب روزی بزرگ‌'),
(1380, 10, 16, 'بگو اگر می‌خواست خدا نمی‌سرودمش بر شما و نه می‌آموختمش به شما هر آینه درنگ کردم میان شما عمری را پیش از آن آیا درنمی‌یابید'),
(1381, 10, 17, 'پس کیست ستمگرتر از آنکه ببندد بر خدا دروغی را یا تکذیب کند آیتهای او را همانا رستگار نشوند گنهکاران‌'),
(1382, 10, 18, 'و پرستند جز خدا آنچه را نه زیانشان رساند و نه سودی و گویند اینانند شفیعان ما نزد خدا بگو آیا آگهی دهید خدا را بدانچه خود نمی‌داند در آسمانها و نه در زمین منزّه و برتر است او از آنچه شرک می‌ورزند'),
(1383, 10, 19, 'و نبودند مردم جز یک ملّت پس اختلاف کردند و اگر نبودند سرنوشتی که پیشی گرفته است از پروردگار تو همانا داوری می‌شد میان ایشان در آنچه بودند در آن اختلاف‌کنان‌'),
(1384, 10, 20, 'گویند چرا فرود نیاید بر او آیتی از پروردگارش بگو جز این نیست که ناپیدا از آن خدا است پس منتظر باشید که مائیم با شما از منتظران‌'),
(1385, 10, 21, 'و هرگاه چشانیم مردم را رحمتی پس از سختی که بدانان رسیده ناگهان ایشان را است نیرنگی در آیتهای ما بگو خدا شتابنده‌تر است در نیرنگ همانا فرستادگان ما می‌نویسند آنچه را نیرنگ کنید'),
(1386, 10, 22, 'او است آنکه می‌برد شما را در دشت و دریا تا گاهی که باشید در کشتی و روان شود بدیشان با بادی پاک (خوش) و شاد شوند بدان بیایدشان بادی تند و بیایدشان موج از هر سوی و پندارند که فراگرفته شدند (فروماندند) بخوانند خدای را پاک‌کنندگان دین برای او که اگر رهائیمان دادی از این هر آینه باشیم از شکرگزاران‌'),
(1387, 10, 23, 'تا گاهی که رهائیشان داد ناگهان سرکشی کنند در زمین به ناحقّ ای مردم جز این نیست که ستم شما بر خویش کامیابی زندگانی دنیا است پس بسوی ما است بازگشت شما تا آگهیتان دهیم بدانچه بودید می‌کردید'),
(1388, 10, 24, 'جز این نیست که مثل زندگانی دنیا مانند آبی است که فرستادیمش از آسمان پس بیامیخت بدان روئیدنی زمین از آنچه می‌خورند مردم و چهارپایان تا گاهی که برگرفت زمین زیور خویش را و بیاراست خود را و پنداشتند اهلش که ایشانند توانایان بر آن بیامدش امر ما شبی یا روزی پس تباهش ساختیم چنانکه گوئی مسکون نبوده است دیروز بدینسان تفصیل دهیم آیتها را برای گروهی که تفکر کنند'),
(1389, 10, 25, 'و خدا می‌خواند بسوی خانه آرامش و هدایت کند هر که را خواهد بسوی راهی راست‌'),
(1390, 10, 26, 'برای آنان که نکوئی کردند نکوئی است و فزونی و ننشیند بر چهره‌های ایشان گرد پریشانی و نه خواری آنان یاران بهشتند و در آنند جاودانان‌'),
(1391, 10, 27, 'و آنان که بدیها کردند کیفر هر بدی همانند آن است و دستخوششان سازد خواری نیستشان از خدا نگهدارنده‌ای گوئیا پوشیده است رویهای آنان با پاره‌هائی از شب تار آنانند یاران آتش ایشانند در آن جاودانان‌'),
(1392, 10, 28, 'و روزی که گردشان آریم همگی پس گوئیم بدانان که شرک ورزیدند بر جای خود ایستید شما و شریکان شما سپس جدائی افکنیم میانشان و گویند شریکان ایشان نبودید شما ما را پرستش‌کنندگان‌'),
(1393, 10, 29, 'و بس است خداوند گواهی میان ما و میان شما که بودیم از پرستش شما بی‌خبران‌'),
(1394, 10, 30, 'اینجاست که می‌آزماید همه کس آنچه را پیش فرستاده است و بازگردانیده شوند بسوی خدا سرپرست ایشان به حقّ و گم شد از ایشان آنچه بودند دروغ می‌بستند'),
(1395, 10, 31, 'بگو که روزی دهد شما را از آسمان و زمین یا کیست مالک گوش و دیدگان و که برون آرد زنده را از مُرده و مُرده را از زنده و که تدبیر کند کار را بزودی گویند خدا بگو پس چرا پرهیز نمی‌کنید'),
(1396, 10, 32, 'این است خدا پروردگار شما به حقّ و چیست پس از حقّ جز گمراهی پس به کدام سوی گردانیده می‌شوید'),
(1397, 10, 33, 'بدینسان استوار شد سرنوشت پروردگار تو بر آنان که نافرمانی کردند که ایشان ایمان نمی‌آورند'),
(1398, 10, 34, 'بگو آیا هست از شریکان شما آنکه آغاز کند آفرینش را و سپس بازش گرداند بگو خدا است که آغاز کند آفرینش را و سپس باز همی‌گرداند پس کجا به دروغ رانده می‌شوید'),
(1399, 10, 35, 'بگو آیا هست از شریکان شما آنکه هدایت کند بسوی حقّ بگو خدا هدایت کند بسوی حقّ آیا آنکه هدایت کند بسوی حقّ سزاوارتر است که پیروی شود یا آنکه خود راه نبرد تا رهبریش کنند چه شود شما را چگونه حکم کنید'),
(1400, 10, 36, 'و پیروی نکنند بیشتر ایشان جز گمان را و هر آینه گمان بی‌نیاز نکند از حقّ به چیزی همانا خدا دانا است بدانچه می‌کنند'),
(1401, 10, 37, 'و نیست این قرآن که دروغ آورده شود از غیر از خدا و لیکن تصدیق آنچه پیش روی او است و تفصیل کتاب نیست شکّی در آن از پروردگار جهانیان‌'),
(1402, 10, 38, 'یا گویند دروغ بسته است آن را بگو پس بیارید یک سوره همانند آن و بخوانید هر که را توانید جز خدا اگر هستید راستگویان‌'),
(1403, 10, 39, 'بلکه دروغ پنداشتند آنچه را فرانگرفتند به دانش آن و هنوز نرسیده است بدیشان تأویلش بدینگونه تکذیب کردند آنان که پیش از ایشان بودند پس بنگر چگونه شد فرجام ستمکاران‌'),
(1404, 10, 40, 'و از ایشان است آنکه ایمان آورد بدان و از ایشان است آنکه ایمان نیاورد بدان و پروردگار تو داناتر است به تبهکاران‌'),
(1405, 10, 41, 'و اگر تکذیبت نمودند بگو مرا است کردارم و شما را است کردار شما بیزارید از آنچه من می‌کنم و بیزارم از آنچه شما کنید'),
(1406, 10, 42, 'و از ایشان است آنکه گوش فرادارد به سویت آیا تو می‌شنوانی گنگان را و اگرچه درنیابند'),
(1407, 10, 43, 'و از ایشان است آنکه بنگرد به سویت آیا تو رهبری کنی کوران را و اگر چه نبینند'),
(1408, 10, 44, 'همانا خدا ستم نکند بر مردم به چیزی و لیکن مردم خویشتن را ستم می‌کنند'),
(1409, 10, 45, 'و روزی که گردشان آرد چنانکه گوئی درنگ نکردند جز ساعتی از روز شناسا شوند با هم همانا زیان کردند آنان که تکذیب نمودند ملاقات خدا را و نبودند هدایت‌شدگان‌'),
(1410, 10, 46, 'و اگر بنمایانیم تو را پاره‌ای از آنچه بدیشان وعده دهیم یا دریابیمت (بمیرانیمت) پس بسوی ما است بازگشت ایشان سپس خدا گواه است بر آنچه می‌کنند'),
(1411, 10, 47, 'و هر ملّتی را پیغامبری است که هرگاه بیاید پیغامبر آنان داوری شود میان ایشان به داد و ستم نمی‌شوند'),
(1412, 10, 48, 'و گویند کی است (چه وقت) این وعده اگر هستید راستگویان‌'),
(1413, 10, 49, 'بگو دارا نیستم برای خویشتن زیان و نه سودی را جز آنچه خدا خواهد برای هر ملتی است سرآمدی که هرگاه بیاید سرآمد ایشان نه دیر کنند ساعتی و نه پیشی گیرند'),
(1414, 10, 50, 'بگو آیا دیده‌اید که اگر بیاید شما را عذاب او شبانه یا در روز چه چیز را می‌شتابند از آن گنهکاران‌'),
(1415, 10, 51, 'آیا پس از آنکه فرود آمد ایمان آوردید بدان اکنون حالی که بودید بدان شتابندگان‌'),
(1416, 10, 52, 'پس گفته شد بدانان که ستم کردند بچشید عذاب جاودانی را آیا پاداش داده می‌شوید جز بدانچه بودید فراهم می‌کردید'),
(1417, 10, 53, 'پرسندت آیا حقّ است آن بگو آری سوگند به پروردگارم و همانا آن است حقّ و نیستید شما به عجزآرندگان‌'),
(1418, 10, 54, 'و اگر باشد آن را که ستم کرده است هر آنچه در زمین است هر آینه بدهدش به جای خویش و نهان کردند پشیمانی را گاهی که دیدند عذاب را و داوری شد میان ایشان به داد و ستم نشوند ایشان‌'),
(1419, 10, 55, 'همانا خدا را است آنچه در آسمانها و آنچه در زمین است همانا وعده خدا است حقّ و لیکن بیشترشان نمی‌دانند'),
(1420, 10, 56, 'او زنده کند و بمیراند و به سویش بازگردانیده شوید'),
(1421, 10, 57, 'ای مردم همانا بیامد شما را اندرزی از پروردگار شما و درمانی برای آنچه در سینه‌هاست و هدایت و رحمتی برای مؤمنان‌'),
(1422, 10, 58, 'بگو به فضل خدا و به رحمتش پس بدین باید شاد شوند آن بهتر است از آنچه گرد می‌آرند'),
(1423, 10, 59, 'بگو آیا دیدید آنچه را فرستاد خدا برای شما از روزی پس قرار دادید از آن حرامی و حلالی بگو آیا خدا به شما اجازت داده است یا بر خدا دروغ بندید'),
(1424, 10, 60, 'و چیست پندار آنان که بندند بر خدا دروغ را روز قیامت همانا خدا دارای فضل است بر مردم و لیکن بیشتر ایشان سپاس نگزارند'),
(1425, 10, 61, 'و نباشی تو در کاری و نه برخوانی از آن قرآنی (خواندنی) را و نکنید کاری را به جز آنکه باشیم بر شما گواه هنگامی که فرو می‌روید در آن و پوشیده نماند از پروردگارت سنگینی ذرّه‌ای نه در زمین و نه در آسمان و نه کوچکتر از آن و نه بزرگتر مگر در کتابی است آشکار'),
(1426, 10, 62, 'همانا دوستان خدا نیست بر ایشان بیمی و نه اندوهگین شوند'),
(1427, 10, 63, 'آنان که ایمان آوردند و بودند پرهیز می‌کردند'),
(1428, 10, 64, 'آنان را است بشارت در زندگانی دنیا و در آخرت نیست تبدیلی برای سخنان خدا این است آن رستگاری بزرگ‌'),
(1429, 10, 65, 'و اندوهگینت نکند گفتارشان همانا عزّت از آن خداست همگی او است شنوای دانا'),
(1430, 10, 66, 'همانا خدای را است آنکه در آسمانها و آنکه در زمین است و پیروی نکنند آنان که خوانند جز خدا شریکانی پیروی نکنند جز پندار را و نیستند جز گمان‌آوران‌'),
(1431, 10, 67, 'او است آنکه نهاد برای شما شب را تا بیارامید در آن و روز را بینا همانا در این است آیتهائی برای گروهی که بشنوند'),
(1432, 10, 68, 'گفتند برگرفت خدا فرزندی منزّه است او او است بی‌نیاز وی را است آنچه در آسمانها و آنچه در زمین است نیست نزد شما فرمانروائی بدان آیا گوئید بر خدا آنچه را نمی‌دانید'),
(1433, 10, 69, 'بگو همانا آنان که بندند بر خدا دروغ را رستگار نشوند'),
(1434, 10, 70, 'کامیابی است در دنیا و سپس بسوی ما است بازگشت ایشان پس بچشانیمشان عذاب سخت را بدانچه بودند کفر می‌ورزیدند'),
(1435, 10, 71, 'و بخوان بر ایشان داستان نوح را گاهی که گفت به قوم خویش ای قوم من اگر گران است بر شما ماندن من و یادآوردن من آیتهای خدا را پس بر خدا توکّل نمودم پس گردآورید کار خود و شریکان خود را تا نباشد کار شما بر شما اندوهی سپس بگذرانید بسوی من و مهلتم ندهید'),
(1436, 10, 72, 'پس اگر پشت کردید همانا نخواهم از شما پاداشی نیست پاداشم مگر بر خدا و مأمور شدم که باشم از مسلمانان‌'),
(1437, 10, 73, 'پس تکذیبش کردند پس رهانیدیمش و هر که را با او بود در کشتی و گردانیدیم ایشان را جانشیانی و غرق ساختیم آنان را که تکذیب کردند آیتهای ما را پس بنگر چگونه شد فرجام بیم‌دادگان‌'),
(1438, 10, 74, 'سپس برانگیختیم پس از او پیمبرانی بسوی قومشان پس بیامدندشان به نشانیها پس نبودند که ایمان آرند بدانچه تکذیب کردند از پیش بدینسان مُهر نهیم بر دلهای تجاوزکنندگان‌'),
(1439, 10, 75, 'سپس برانگیختیم پس از ایشان موسی و هارون را بسوی فرعون و قوم او به آیتهای ما پس کبر ورزیدند و شدند قومی گنهکاران‌'),
(1440, 10, 76, 'و هنگامی که بیامدشان حقّ از نزد ما گفتند همانا این است جادوئی آشکار'),
(1441, 10, 77, 'گفت موسی آیا گوئید به حقّ گاهی که بیامده است شما را آیا جادو است این و رستگار نشوند جادوگران‌'),
(1442, 10, 78, 'گفتند آیا آمدی ما را تا برگردانی ما را از آنچه یافتیم بر آن پدران خویش را و باشد برای شما بزرگواری در زمین و نیستیم ما برای شما باورکنندگان‌'),
(1443, 10, 79, 'و گفت فرعون بیارید مرا به هر جادوگری دانشمند'),
(1444, 10, 80, 'پس گاهی که آمدند جادوگران گفت بدیشان موسی بیفکنید آنچه را هستید افکنندگان‌'),
(1445, 10, 81, 'سپس هنگامی که افکندند گفت موسی آنچه آوردید جادو است و خدا زود است تباه سازدش همانا خدا راست نیارد کردار تبهکاران را'),
(1446, 10, 82, 'و بپای می‌دارد خدا حقّ را به سخنان خویش و اگر چه خوش ندارند گنهکاران‌'),
(1447, 10, 83, 'پس ایمان نیاورد برای موسی جز تیره‌ای از قومش با ترسی از فرعون و کسان او نبادا برآشوبند بر ایشان و همانا فرعون برتری‌جوئی است در زمین و همانا او است از اسراف‌کنندگان‌'),
(1448, 10, 84, 'و گفت موسی ای قوم اگر ایمان آوردید به خدا پس بر او توکل کنید اگر هستید مسلمانان (تسلیم‌شدگان)'),
(1449, 10, 85, 'گفتند بر خدا توکّل کردیم پروردگارا نگردان ما را دستخوش گروه ستمکاران‌'),
(1450, 10, 86, 'و بِرهان ما را به رحمت خود از گروه کافران‌'),
(1451, 10, 87, 'و وحی نمودیم به موسی و برادرش که برگیرید برای قوم خویش در مصر (شهر) خانه‌هائی و بگردانید خانه‌های خویش را قبله و بپای دارید نماز را و بشارت ده به مؤمنان‌'),
(1452, 10, 88, 'و گفت موسی پروردگارا همانا دادی فرعون و کسانش را زیوری و مالهائی در زندگانی دنیا تا گمراه کنند از راهت پروردگارا تباه ساز اموال آنان را و ببند دلهای آنان را که ایمان نیارند تا بنگرند عذاب دردناک را'),
(1453, 10, 89, 'گفت همانا مستجاب شد دعای شما پس پایداری کنید و پیروی نکنید راه آنان را که نمی‌دانند'),
(1454, 10, 90, 'و گذراندیم بنی‌اسرائیل را از دریا پس پیروی کرد ایشان را فرعون و سپاه او به ستم و سرکشی تا گاهی که دریافت او را غرق گفت باور کردم که نیست خدائی جز آنکه ایمان بدو آوردند بنی‌اسرائیل و منم از مسلمانان (تسلیم‌شدگان)'),
(1455, 10, 91, 'آیا کنون حالی که عصیان کردی پیش و بودی از تباهکاران‌'),
(1456, 10, 92, 'پس امروز برهانیمت به کالبدت تا باشی برای آنان که پس از تو آیند نشانه (عبرتی) و همانا بسیاری از مردمند از آیتهای ما غافلان‌'),
(1457, 10, 93, 'و همانا جای دادیم بنی‌اسرائیل را جایگاه راستین و روزیشان دادیم از پاکیزه‌ها پس اختلاف نکردند تا آنکه بیامدشان دانش همانا پروردگار تو داوری کند میان آنان روز قیامت در آنچه بودند در آن اختلاف‌کنان‌'),
(1458, 10, 94, 'و اگر هستی در شکّی بدانچه فرستادیم به سویت پس بپرس آنان را که می‌خوانند کتاب را پیش از تو همانا بیامد تو را حقّ از پروردگارت پس نباش از شکّ‌کنندگان‌'),
(1459, 10, 95, 'و البته نباش از آنان که دروغ پنداشتند آیتهای خدا را که می‌شوی از زیانکاران‌'),
(1460, 10, 96, 'همانا آنان که فرود آمد بر ایشان سخن پروردگار تو ایمان نیارند'),
(1461, 10, 97, 'و اگرچه بیامدشان هر آیتی تا ببینند عذاب دردناک را'),
(1462, 10, 98, 'پس چرا نبود شهری که ایمان آورد و سود دهدش ایمانش مگر قوم یونس هنگامی که ایمان آوردند برگشودیم از ایشان عذاب خواری را در زندگانی دنیا و کامیابیشان دادیم تا سرآمدی‌'),
(1463, 10, 99, 'و اگر می‌خواست پروردگار تو هر آینه ایمان می‌آوردند آنان که در زمینند همگی با هم پس آیا تو وامیداری به ناخواه مردم را که باشند از مؤمنان‌'),
(1464, 10, 100, 'و نرسد تنی را که ایمان آرد مگر به دستور خدا و می‌گرداند پلید را بر آنان که درنمی‌یابند'),
(1465, 10, 101, 'بگو بنگرید چیست در آسمانها و زمین و بی‌نیاز نگردانند آیتها و نه بیم‌دادنها از قومی که ایمان نمی‌آورند'),
(1466, 10, 102, 'پس آیا انتظار می‌کشند جز همانند روزگار آنان را که گذشتند پیش از ایشان بگو پس منتظر باشید که منم با شما از منتظران‌'),
(1467, 10, 103, 'سپس بِرهانیم پیمبران خود و آنان را که ایمان آوردند بدینسان حقّی است بر ما که برهانیم مؤمنان را'),
(1468, 10, 104, 'بگو ای مردم اگر هستید در شکی از آئینم پس نمی‌پرستم آنان را که می‌پرستید جز خدا و لیکن پرستش کنم خداوندی را که دریابد (بمیراند) شما را و مأمور شدم که باشم از ایمان‌آورندگان‌'),
(1469, 10, 105, 'و آنکه راست کن روی خود را بسوی دین یکتاپرست و نباش البته از شرک‌ورزندگان‌'),
(1470, 10, 106, 'و نخوان جز خدا آنچه را نه سودی دهدت و نه گزندت رساند که اگر کنی هر آینه باشی در آن هنگام از ستمکاران‌'),
(1471, 10, 107, 'و اگر برساندت خدا به رنجی نیست گشاینده برای آن جز او و اگر اراده کند تو را به خیری نباشد برگرداننده برای فضلش رساندش به هر که خواهد از بندگان خود و او است آمرزنده مهربان‌'),
(1472, 10, 108, 'بگو ای مردم همانا بیامد شما را حقّ از پروردگار شما پس هر که هدایت شود جز این نیست که هدایت شود برای خویشتن و هر که گمراه شود گمراه نشود مگر بر خویش و نیستم من بر شما وکیل‌'),
(1473, 10, 109, 'و پیروی کن آنچه را وحی می‌شود بسوی تو و صبر کن تا حکم کند خدا و او است بهترین حکم‌کنندگان‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'کتابی است که استوار شد آیتهای آن و سپس تفصیل داده شده است از نزد حکیمی دانا'),
(1475, 11, 2, 'که پرستش نکنید جز خدا را همانا منم برای شما از نزد او ترساننده و نویددهنده‌'),
(1476, 11, 3, 'و آنکه آمرزش خواهید از پروردگار خویش و سپس بازگشت کنید بسوی او تا بهره دهد شما را بهره‌ای نکو تا سرآمدی نامبرده و بدهد به هر صاحب فضلی فضلش را و اگر پشت کردند همانا می‌ترسم بر شما از عذاب روزی بزرگ‌'),
(1477, 11, 4, 'بسوی خدا است بازگشت شما و او است بر هر چیز توانا'),
(1478, 11, 5, 'همانا ایشان برمی تابند سینه‌های خود را تا پوشیده دارند از او همانا گاهی که می‌پوشند جامه‌های خود را می‌داند آنچه را نهان کنند و آنچه را آشکار سازند که او دانا است بدانچه در سینه‌ها است‌'),
(1479, 11, 6, 'و نیست جنبنده‌ای در زمین مگر بر خدا است روزی او و می‌داند آرامشگاه و کوچگاه او را هرکدام است در کتابی آشکار'),
(1480, 11, 7, 'و او است آنکه آفرید آسمانها و زمین را در شش روز و بود عرش او بر آب تا بیازماید شما را کدامیک نکوترید در کردار و اگر گوئی شمائید برانگیختگان پس از مرگ هر آینه گویند آنان که کفر ورزیدند نیست این جز جادوئی آشکار'),
(1481, 11, 8, 'و اگر دور کنیم از ایشان عذاب را تا امّتی شمرده گویند چه بازدارد آن را همانا روزی که می‌آید ایشان را نیست برگرداننده از ایشان و فرود آمد بر ایشان آنچه بودند بدان استهزاءکنان‌'),
(1482, 11, 9, 'و اگر چشانیم انسان را از خود رحمتی و سپس بگیریمش از او همانا او است نومید ناسپاس‌'),
(1483, 11, 10, 'و اگر چشانیمش نعمتی بعد از رنجی که بدو رسیده است هر آینه گوید رفت بدیها از من همانا او است شادمان خودستای‌'),
(1484, 11, 11, 'مگر آنان که صبر کردند و عمل صالح نمودند که آنان را است آمرزش و پاداشی بزرگ‌'),
(1485, 11, 12, 'شاید توئی رهاکننده پاره‌ای از آنچه وحی می‌شود بسوی تو و تنگ شود بدان سینه تو که گویند چرا بر او فرود نیامد گنجی یا نیامد با او فرشته‌ای جز این نیست که توئی بیم‌دهنده و خدا است بر هر چیزی وکیل‌'),
(1486, 11, 13, 'یا گویند دروغ بسته است آن را بگو بیاورید ده سوره همانند آن دروغ پرداخته و بخوانید هر آن را که بتوانید جز خدا اگر هستید راستگویان‌'),
(1487, 11, 14, 'پس اگر نپذیرفتند از شما بدانید که آنچه فرستاده شده است به علم خدا است و آنکه نیست خدائی جز او پس آیا هستید شما اسلام‌آورندگان‌'),
(1488, 11, 15, 'آنان که خواهان زندگانی دنیا و زیور آنند تمام دهیم بدیشان کردار ایشان را در آن و ایشان در آن کم داده نشوند'),
(1489, 11, 16, 'آنانند که نیست ایشان را در آخرت جز آتش و تباه است آنچه ساختند در آن و ناچیز است آنچه بودند می‌کردند'),
(1490, 11, 17, 'آیا آنکه بر روشنائی است از پروردگار خود و از پیش آید گواهی از آن و پیش از آن است کتاب موسی پیشوا و رحمتی ایشان ایمان آرند بدان و آنکه کفر ورزد بدان از احزاب آتش است وعده‌گاه او پس نباش در شکی از آن همانا آن است حقّ از پروردگار تو لیکن بیشتر مردم ایمان نمی‌آورند'),
(1491, 11, 18, 'و کیست ستمگرتر از آنکه ببندد بر خدا دروغی را آنان عرض شوند بر پروردگار خویش و گویند گواهان اینانند آنان که دروغ گفتند بر پروردگار خویش همانا لعنت خدا باد بر ستمگران‌'),
(1492, 11, 19, 'آنان که بازدارند از راه خدا و خواهندش کج و ایشانند به آخرت کافران‌'),
(1493, 11, 20, 'آنان نیستند به عجزآرندگان در زمین و نیستشان جز خدا دوستانی افزوده شود برای ایشان عذاب نبودند بتوانند شنیدن را و نبودند که ببینند'),
(1494, 11, 21, 'آنانند که زیان کردند خویشتن را و گم شد از ایشان آنچه بودند دروغ می‌بستند'),
(1495, 11, 22, 'ناگزیر آنانند در آخرت زیانمندتران‌'),
(1496, 11, 23, 'همانا آنان که ایمان آوردند و کردار شایسته کردند و زاری کردند بسوی پروردگار خویش آنانند یاران بهشت ایشانند در آن جاودانان‌'),
(1497, 11, 24, 'مثَل دو گروه مانند کور و کر و بینا و شنوا است آیا یکسانند در مثَل آیا یادآور نمی‌شوید'),
(1498, 11, 25, 'و همانا فرستادیم نوح را بسوی قومش که منم برای شما ترساننده آشکار'),
(1499, 11, 26, 'که پرستش نکنید جز خدا را که می‌ترسم بر شما از عذاب روزی دردناک‌'),
(1500, 11, 27, 'پس گفتند گروهی که کفر ورزیدند از قومش نمی‌بینیمت جز بشری همانند خویش و نمی‌بینیم تو را که پیرویت کرده‌اند جز آنان که فرومایگان ما هستند نخستین بار و نمی‌بینیم شما را بر ما برتریی بلکه می‌پنداریم شما را دروغگویان‌'),
(1501, 11, 28, 'گفت ای قوم من آیا دیده‌اید که اگر باشم من بر روشنائی از پروردگار خود و بداد مرا رحمتی از نزدش پس پوشیده شد بر شما آیا وامیدارم شما بر آن حالی که هستید آنرا ناخوش‌دارندگان‌'),
(1502, 11, 29, 'و ای قوم من نخواهم از شما بر آن مالی را نیست پاداشم جز بر خدا و نیستم دورکننده آنان که ایمان آوردند همانا ایشانند ملاقات‌کننده پروردگار خویش لیکن بینم شما را گروهی که نمی‌دانید'),
(1503, 11, 30, 'و ای قوم من که مرا یاری می‌کند از خدا اگر برانمشان آیا یادآور نمی‌شوید'),
(1504, 11, 31, 'و نمی‌گویم به شما که نزد من است گنجهای خدا و ندانم ناپیدا را و نگویم که منم فرشته و نمی‌گویم بدانان که به خواری نگرد دیدگان شما هرگز ندهد خدا بدیشان خیری خدا داناتر است بدانچه در دلهای آنان است که می‌باشم در آن هنگام از ستمکاران‌'),
(1505, 11, 32, 'گفتند ای نوح همانا با ما ستیزه کردی پس فزون کردی ستیزه ما را پس بیاور ما را آنچه به ما وعده دهی اگر هستی از راستگویان‌'),
(1506, 11, 33, 'گفت جز این نیست که آورد شما را بدان خدا اگر خواهد و نیستید شما به عجزآرندگان‌'),
(1507, 11, 34, 'و سود ندهد شما را اندرز من اگر خواهم شما را اندرز دهم اگر خواهد گمراهتان کند او است پروردگار شما و بسوی او بازگردانیده شوید'),
(1508, 11, 35, 'یا گویند دروغ بسته است آن را بگو اگر دروغش بسته باشم بر من است گناهم و بیزارم از آنچه شما گناه کنید'),
(1509, 11, 36, 'و وحی شد بسوی نوح که هرگز ایمان نیارند از قومت جز آنان که ایمان آوردند پس آزرده نشو از آنچه می‌کنند'),
(1510, 11, 37, 'و بساز کشتی را به دیدگان ما و وحی ما و سخن نگوی با من در باره آنان که ستم کردند همانا ایشانند غرق‌شدگان‌'),
(1511, 11, 38, 'و می‌ساخت کشتی را و هرگاه بر او گذشتی گروهی از قومش مسخره‌اش گرفتندی گفت اگر ما را مسخره کنید همانا ما شما را مسخره کنیم چنانکه بودید شما مسخره می‌کردید'),
(1512, 11, 39, 'زود است بدانید که را آید عذابی که خوار سازدش و فرود آید بر او عذابی جایگزین‌'),
(1513, 11, 40, 'تا گاهی که بیامد امر ما و بجوشید تنور گفتیم بار کن در آن از هرکدام دو جفت را و خاندان خویش را مگر آنکه بر او پیشی گرفته است سخن و آنان را که ایمان آوردند و ایمان نیاورده بود با او جز اندکی‌'),
(1514, 11, 41, 'گفت سوار شوید در آن با نام خدا روان شدنش و لنگر انداختنش همانا پروردگار من است آمرزنده مهربان‌'),
(1515, 11, 42, 'و آن روان بود بدیشان در موجی همانند کوه‌ها و برخواند نوح فرزند خود را و بود بر کناری که‌ای پسرک من سوار شو با ما و نباش با کافران‌'),
(1516, 11, 43, 'گفت زود است پناه برم به کوهی که نگه داردم از آب گفت نیست نگهدارنده‌ای امروز از امر خدا مگر آن را که رحم کرده است و حایل شد میان ایشان موج پس شد از غرق‌شدگان‌'),
(1517, 11, 44, 'و گفته شد ای زمین فروخور آب خویش را و ای آسمان بس کن و فرونشست آب و گذشت کار و استوار شد بر جودی و گفته شد دور باد برای گروه ستمگران‌'),
(1518, 11, 45, 'و خواند نوح پروردگار خود را پس گفت پروردگارا همانا پسرم از خاندان من است و هر آینه وعده تو حقّ است و توئی بهترین حکم‌کنندگان‌'),
(1519, 11, 46, 'گفت ای نوح نیست او از خاندان تو و همانا او کرداری است ناشایست پس نپرس مرا آنچه نیستت بدان دانشی همانا اندرزت گویم تا نباشی از نادانان‌'),
(1520, 11, 47, 'گفت پروردگارا پناه برم به تو که بپرسمت آنچه را نباشد مرا دانشی بدان و اگر نیامرزی مرا و رحم نکنی بر من باشم از زیانکاران‌'),
(1521, 11, 48, 'گفته شد ای نوح فرودآی با آرامشی از ما و برکتهائی بر تو و بر ملتهائی از آنان که با تو هستند و ملتهائی که زود است بهره‌مندشان گردانیم و سپس برسد ایشان را از ما عذابی دردناک‌'),
(1522, 11, 49, 'این از خبرهای ناپیدا است که وحی می‌فرستیمش به سویت و نبوده‌ای که بدانی آنها را تو و نه قومت پیش از این پس بردبار شو همانا فرجام است از آن پرهیزکاران‌'),
(1523, 11, 50, 'و بسوی عاد برادر ایشان هود را گفت ای قوم من پرستش کنید خدا را نیست شما را خداوندی جز او نیستید شما جز دروغ‌آوران‌'),
(1524, 11, 51, 'ای قوم من نخواهم از شما بر آن مزدی را نیست مزد من جز بر آنکه مرا آفریده است پس آیا بخرد نمی‌یابید'),
(1525, 11, 52, 'و ای قوم من آمرزش جوئید از پروردگار خود سپس بازگشت کنید بسوی او تا فرستد آسمان را بر شما ریزاریز و بیفزاید شما را نیروئی بر نیرویتان و پشت نکنید گنهکاران‌'),
(1526, 11, 53, 'گفتند ای هود نیاوردی ما را نشانیی و نیستیم ما ترک‌کننده خدایان خود به گفتار تو و نیستیم ما به تو ایمان‌آورندگان‌'),
(1527, 11, 54, 'نگوئیم جز آنکه گرفته است تو را برخی از خدایان ما به بدی گفت همانا گواه گیرم خدا را و گواه باشید شما که بیزارم از آنچه شرک می‌ورزید'),
(1528, 11, 55, 'جز خدا پس آهنگ من کنید همگی و سپس مهلتم ندهید'),
(1529, 11, 56, 'همانا توکل کردم بر خدا پروردگار من و پروردگار شما نیست جنبنده‌ای جز آنکه او است گیرنده پیشانیش پروردگار من است بر راهی راست‌'),
(1530, 11, 57, 'پس اگر پشت کردند همانا ابلاغ کردم به شما آنچه را بدان فرستاده شدم بسوی شما و برگیرد به جای شما پروردگار من قومی را غیر از شما و گزندش نرسانید به چیزی همانا پروردگار من است بر همه چیز نگهبان‌'),
(1531, 11, 58, 'و هنگامی که بیامد امر ما رهانیدیم هود را و آنان که ایمان آوردند با او به رحمتی از ما و رهائیشان دادیم از عذابی انبوه‌'),
(1532, 11, 59, 'و اینک عاد انکار کردند آیتهای پروردگار خود را و نافرمانی کردند پیمبران او را و پیروی کردند فرمان هر فرمانده خیره‌سری را'),
(1533, 11, 60, 'و پیرو شدند در این دنیا لعنتی را و در روز قیامت همانا عاد کفر ورزیدند به پروردگار خویش همانا دور باد برای عاد قوم هود'),
(1534, 11, 61, 'و بسوی ثمود برادر ایشان صالح را گفت ای قوم من پرستش کنید خدا را نیست شما را خدائی جز او پدید آورده است شما را از زمین و استعمار کرده است شما را در آن پس استغفار کنید او را سپس بازگشت کنید بسوی او که پروردگار من نزدیک است و پذیرنده‌'),
(1535, 11, 62, 'گفتند ای صالح همانا امیدواری ما بودی پیش از این آیا نهی کنی ما را از آنکه پرستیم آنچه را می‌پرستند پدران ما و مائیم در شکی از آنچه خوانیمان بدان به ریب‌افکننده‌'),
(1536, 11, 63, 'گفت ای قوم من آیا دیده‌اید که اگر بر رهبرئی از پروردگار خود باشم و آمده باشد مرا از او رحمتی پس که مرا یاری دهد از خدا اگر نافرمانیش کنم پس نیفزائید مرا جز زیانکاری‌'),
(1537, 11, 64, 'و ای قوم من این است اشتر خدا برای شما آیتی پس بگذاریدش بچرد در زمین خدا و گردش نگردید به بدی که برگیرد شما را عذابی نزدیک‌'),
(1538, 11, 65, 'پس پی کردند آن را پس گفت کامیاب مانید در خانه خود سه روز این است وعده نادروغ‌'),
(1539, 11, 66, 'تا هنگامی که بیامد امر ما رهانیدیم صالح را و آنان که ایمان آوردند با او به رحمتی از ما و از خواریِ آن روز همانا پروردگار تو است نیرومند دادستان‌'),
(1540, 11, 67, 'و بگرفت آنان را که ستم کردند خروشی پس شدند در خانه‌های خود مُردگان‌'),
(1541, 11, 68, 'چنانکه گوئی سکونت نگزیده‌اند در آنها همانا ثمود کفر ورزیدند به پروردگار خویش همانا دور باد برای ثمود'),
(1542, 11, 69, 'و همانا آمدند فرستادگان ما ابراهیم را به مژده گفتند سلام گفت سلام پس درنگ ناکرده بیاورد گوساله بریان‌'),
(1543, 11, 70, 'و هنگامی که دید دستهاشان نمی‌رسد بسوی آن ناشناسشان گرفت و برداشت از ایشان بیمی گفتند نترس که ما فرستاده شدیم بسوی قوم لوط'),
(1544, 11, 71, 'و زنش ایستاده بود پس خندید پس مژده دادیمش به اسحق و از پس اسحق یعقوب‌'),
(1545, 11, 72, 'گفت ای وای آیا بزایم و منم پیری ناتوان و این است شوهر من پیری فرتوت همانا این است چیزی شگفت‌'),
(1546, 11, 73, 'گفتند آیا شگفت مانی از کار خدا رحمت خدا و برکتهای او بر شما خاندان است همانا او است ستوده ارجمند'),
(1547, 11, 74, 'تا گاهی که برفت از ابراهیم ترس و بیامدش مژده می‌ستیزد با ما در باره قوم لوط'),
(1548, 11, 75, 'همانا ابراهیم بردباری است نالانی است زاری‌کنان‌'),
(1549, 11, 76, 'ای ابراهیم درگذر از این همانا بیامد امر پروردگار تو و همانا آینده است ایشان را عذابی نابازگردانیده‌'),
(1550, 11, 77, 'و هنگامی که آمد فرستادگان ما لوط را به رنج شد از ایشان و تنگ آمدش بدیشان تاب و توان و گفت این است روزی سخت (خشم‌آگین)'),
(1551, 11, 78, 'و بیامدندش قومش می‌شتافتند بسوی او و از پیش بودند می‌کردند بدیها را گفت ای قوم اینک دخترانم پاکیزه‌ترند برای شما پس بترسید خدا را و خوارم نسازید در میهمانانم آیا نیست در شما مردی خردمند'),
(1552, 11, 79, 'گفتند تو دانی نیست ما را در دختران تو کامی و هر آینه می‌دانی ما چه خواهیم‌'),
(1553, 11, 80, 'گفت کاش مرا بر شما نیروئی می‌بود یا پناه می‌بردم به پایگاهی سخت‌'),
(1554, 11, 81, 'گفتند ای لوط مائیم فرستادگان پروردگار تو هرگز نرسند به تو پس کوچ کن با خاندان خود در پاره‌ای از شب و روی برنگرداند از شما هیچکس جز زن تو که وی را رسنده است آنچه ایشان را رسد همانا وعده‌گاه آنان بامداد است آیا نیست بامداد نزدیک‌'),
(1555, 11, 82, 'تا گاهی که بیامد امر ما گردانیدیم روی آن را زیر آن و باریدیم بر آن سنگهائی از سِجّیل (سنگ گِل) به رشته‌کشیده‌'),
(1556, 11, 83, 'نشاندارانی نزد پروردگار تو و نیست آن از ستمگران دور'),
(1557, 11, 84, 'و بسوی مَدیَن برادر ایشان شُعیب را گفت ای قوم من پرستش کنید خدا را نیست شما را خداوندی جز او و کم ندهید پیمانه و ترازو را همانا بینم شما را در خوشی و ترسم بر شما از عذاب روزی فراگیرنده‌'),
(1558, 11, 85, 'و ای قوم تمام دهید پیمانه و ترازو را به داد و کم ندهید به مردم چیزهای ایشان را و نکوشید (نتازید) در زمین تباهکاران‌'),
(1559, 11, 86, 'بازمانده خدا بهتر است برای شما اگر هستید مؤمنان و نیستم من بر شما نگهبان‌'),
(1560, 11, 87, 'گفتند ای شعیب آیا نمازت فرمانت دهد که رها کنیم آنچه را می‌پرستیدند پدران ما یا آنکه کنیم در مالهای خود هر آنچه خواهیم همانا توئی بردبار خردمند'),
(1561, 11, 88, 'گفت ای قوم من آیا دیده‌اید که اگر باشم بر نشانه‌ای از پروردگارم و روزیم دهد از خویش روزئی نیکو و نخواهم مخالفت کنم شما را بسوی آنچه شما را از آن نهی می‌کنم نخواهم جز کارساختن (درست کردن) را هر چه توانم نیست موفق داشتم جز با خدا بر او تکیه نمودم و بسوی او زاری کنم‌'),
(1562, 11, 89, 'و ای قوم به گناه نیندازد شما را مخالفت من تا برسد شما را مانند آنچه رسیده است قوم نوح را یا قوم هود را یا قوم صالح را و نیست قوم لوط از شما دور'),
(1563, 11, 90, 'و استغفار کنید پروردگار خویش را پس توبه کنید بسوی او همانا پروردگار من است مهربانی دوست‌دار'),
(1564, 11, 91, 'گفتند ای شعیب درنمی‌یابیم بسیاری از آنچه گوئی و همانا بینیمت میان ما ناتوان و اگر نبودند کسان تو هر آینه سنگسارت می‌کردیم و نیستی تو بر ما برتر'),
(1565, 11, 92, 'گفت ای قوم آیا کسان من عزیزترند نزد شما از خدا و برگرفتید او را از پس خویش به پشت افکنده همانا پروردگار من است بدانچه می‌کنید فراگیرنده‌'),
(1566, 11, 93, 'و ای قوم عمل کنید با توانائی خویش که منم عمل‌کننده زود است بدانید که را آید عذابی که خوار سازدش و کیست آنکه او است دروغگوی و چشم به راه باشید که منم با شما چشم به راه‌'),
(1567, 11, 94, 'و هنگامی که بیامد امر ما رهانیدیم شعیب را و آنان که ایمان آوردند با او به رحمتی از ما و بگرفت آنان را که ستم کردند خروشی پس شدند در خانه‌های خویش مُردگان‌'),
(1568, 11, 95, 'چنانکه گوئی نبوده‌اند در آن هرگز همانا دور باد برای مَدین چنانکه دور شدند ثمود'),
(1569, 11, 96, 'و همانا فرستادیم موسی را به آیتهای ما و فرمانروائی آشکار'),
(1570, 11, 97, 'بسوی فرعون و کسانش پس پیروی کردند امر فرعون را و نیست امر فرعون رهنمون‌'),
(1571, 11, 98, 'پیشوا شود قوم خویش را روز قیامت پس درآوردشان در آتش و چه زشت آبشخوری است فرود آمده بر آن‌'),
(1572, 11, 99, 'و پیرو شدند در این لعنتی را و روز قیامت چه زشت است میهمانان پذیرائی‌شده‌'),
(1573, 11, 100, 'این از داستان شهرها است که می‌سرائیم بر تو از آنها است ایستاده و درویده‌'),
(1574, 11, 101, 'و ستم نکردیم بر ایشان و لیکن ستم کردند خویش را پس بی‌نیاز نکرد از ایشان خدایانشان که می‌خواندند جز خدا به چیزی گاهی که بیامد امر پروردگار تو و نیفزودشان جز تباهی‌'),
(1575, 11, 102, 'و بدینسان گرفتن پروردگار تو گاهی که گرفت شهرها را حالی که ستمکار بودند همانا گرفتن او است دردناک سخت‌'),
(1576, 11, 103, 'همانا در این است آیتی برای آن که بترسد عذاب آخرت را این است روزی که گردآورده شود برای آن مردم و این است روزی گواهی شده‌'),
(1577, 11, 104, 'و پس نیندازیمش مگر تا سرآمدی شمرده‌'),
(1578, 11, 105, 'روزی که آید سخن نگوید کسی جز به رخصت او پس از ایشان است بدبخت و نیکبخت‌'),
(1579, 11, 106, 'پس آنان که بدبخت شدند در آتش ایشان را است در آن آه و ناله (آهی که برآید و فرو رود)'),
(1580, 11, 107, 'جاودانند در آن مادامی که آسمانها است و زمین جز آنچه خواهد پروردگار تو که پروردگار تو کننده است آنچه را خواهد'),
(1581, 11, 108, 'و اما آنان که نیکبخت شدند پس در بهشت جاودانند در آن مادامی که آسمانها و زمین است جز آنچه خواهد پروردگار تو بخششی نابریده یا بیکران‌'),
(1582, 11, 109, 'پس نباش در تردیدی از آنچه می‌پرستند اینان نمی‌پرستند مگر چنانکه پرستیدند پدران ایشان از پیش و همانا پردازنده‌ایم بدیشان بهره ایشان را ناکاسته‌'),
(1583, 11, 110, 'و همانا دادیم به موسی کتاب را پس اختلاف شد در آن و اگر نبود سخنی که پیش گرفته است از پروردگار تو هر آینه قضاوت می‌شد میان ایشان و همانا ایشانند در شکی از آن به ریب افکننده‌'),
(1584, 11, 111, 'و هر کدام را هر آینه بپردازد بدیشان پروردگار تو کردارهای ایشان را همانا او است بدانچه می‌کنند آگاه‌'),
(1585, 11, 112, 'پس پایدار باش چنانکه مأمور شدی و آنان که توبه کردند با تو و سرکشی نکنید که او بدانچه می‌کنید بینا است‌'),
(1586, 11, 113, 'و نگروید بسوی آنان که ستم کردند که می‌رسد شما را آتش و نمی‌باشد شما را جز خدا دوستانی و سپس یاری نمی‌شوید'),
(1587, 11, 114, 'و بپای دار نماز را هر دو سر روز و پاره‌هائی از شب همانا خوبیها می‌برند بدیها را این است یادآوردنی برای یادآرندگان‌'),
(1588, 11, 115, 'و صبر کن که خدا تباه نکند پاداش نکوکاران را'),
(1589, 11, 116, 'پس چرا نبود از قرنهای پیش از شما بازماندگانی که نهی کنند از تباه‌کاری در زمین مگر کمی از آنان که نجات دادیم از ایشان و پیروی کردند ستمگران آنچه را کامرانی نمودند در آن و شدند بزهکاران‌'),
(1590, 11, 117, 'و نیست پروردگار تو که بکشد شهرها را به ستم و مردمان آنهایند اصلاح‌کنندگان (درست‌کاران)'),
(1591, 11, 118, 'و اگر می‌خواست پروردگار تو هر آینه می‌گردانید مردم را یک ملت و پیوسته اختلاف‌کنندگان باشند'),
(1592, 11, 119, 'مگر آنکه رحم کند پروردگار تو و برای این بیافریدستشان و تمام شد سخن پروردگار تو که همانا پُر سازم دوزخ را از جنیان و مردم همگی‌'),
(1593, 11, 120, 'و هر کدام را خوانیم بر تو از داستانهای پیمبران آنچه استوار داریم بدان دلت را و بیامده است تو را در این حقّ و اندرزی و یادآوردنی برای مؤمنان‌'),
(1594, 11, 121, 'بگو بدانان که ایمان نمی‌آورند عمل کنید بر توانائی خویش که مائیم عمل‌کنندگان‌'),
(1595, 11, 122, 'و منتظر باشید که مائیم منتظران‌'),
(1596, 11, 123, 'و خدای را است ناپیدای آسمانها و زمین و بسوی او بازگردانیده شود امر همه آن پس او را پرستش کن و بر او توکّل کن و نیست پروردگار تو غافل از آنچه می‌کنید');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'این است آیتهای کتاب آشکارا'),
(1598, 12, 2, 'همانا فرستادیمش خواندنیی به زبان تازی شاید بخرد یابید'),
(1599, 12, 3, 'ما می‌خوانیم بر تو بهترین داستانها را بدانچه وحی فرستادیم بسوی تو این قرآن را و هر چند بوده‌ای پیش از آن از غافلان‌'),
(1600, 12, 4, 'هنگامی که گفت یوسف به پدر خود ای پدر من همانا در خواب دیدم یازده ستاره و مهر و ماه را دیدمشان برایم سجده‌کنندگان‌'),
(1601, 12, 5, 'گفت ای فرزند من فرونخوان خواب خویش را بر برادرانت تا برایت نیرنگی کنند همانا شیطان است برای انسان دشمنی آشکار'),
(1602, 12, 6, 'و بدینسان برگزیند تو را پروردگار تو و بیاموزدت از تعبیر خوابها و تمام کند نعمتش را بر تو و بر خاندان یعقوب چنان که تمام کرد آن را بر پدران تو از پیش ابراهیم و اسحق همانا پروردگار تو است دانای حکیم‌'),
(1603, 12, 7, 'همانا در یوسف و برادرانش آیتهائی است برای پرسش‌کنندگان‌'),
(1604, 12, 8, 'هنگامی که گفتند هر آینه یوسف و برادرش محبوبترند بسوی پدر ما از ما حالی که مائیم گروهی نیرومند همانا پدر ما است در گمراهی آشکار'),
(1605, 12, 9, 'بکشید یوسف را یا بیفکنیدش بر زمینی که تنها ماند برای شما روی پدر شما و باشید پس از وی قومی شایستگان‌'),
(1606, 12, 10, 'گفت گوینده‌ای از ایشان نکشید یوسف را و بیفکنیدش در تاریکی چاه تا برگیردش برخی از کاروانیان اگر هستید کنندگان‌'),
(1607, 12, 11, 'گفتند ای پدر ما چه شود تو را که امین نشماری ما را بر یوسف حالی که مائیم برای او اندرزگویان (نیک‌اندیشان)'),
(1608, 12, 12, 'بفرستش با ما بامداد بچرد و بازی کند و همانا مائیم او را نگهدارندگان‌'),
(1609, 12, 13, 'گفت هر آینه اندوهگینم سازد که بریدش و ترسم بخوردش گرگ حالی که باشید شما از او غافلان‌'),
(1610, 12, 14, 'گفتند اگر بخوردش گرگ حالی که مائیم گروهی نیرومند همانا باشیم در آن هنگام از زیانکاران‌'),
(1611, 12, 15, 'سپس هنگامی که بردند او را و گرد آمدند بر آنکه نهندش در تک چاه و وحی فرستادیم بدو که همانا آگهیشان دهی به کارشان این و آنان درنیابند'),
(1612, 12, 16, 'و آمدند شبانگاه پدر خویش را گریان‌'),
(1613, 12, 17, 'گفتند ای پدر ما رفتیم مسابقه کنیم و گذاشتیم یوسف را نزد کالای خویش پس خورد او را گرگ و نیستی تو باورکننده ما را هر چند باشیم راستگویان‌'),
(1614, 12, 18, 'و آوردند بر جامه وی خونی دروغین گفت بلکه بیاراست برای شما دلهای شما کاری را پس شکیبائی نکو است و خدا است یاری خواسته بر آنچه می‌ستائید'),
(1615, 12, 19, 'و بیامد کاروانی پس فروکردند آبگیرنده خویش را پس سرازیر کرد دلو خود را و گفت مژده باد اینک تازه‌پسری و نهانش داشتند کالائی برای سوداگری و خدا داناست بدانچه می‌کنند'),
(1616, 12, 20, 'و فروختندش به بهائی کم درهمهائی چند و بودند در او از قناعت‌کنندگان‌'),
(1617, 12, 21, 'و گفت آنکه خریدش در مصر به زن خود گرامی دار جایگاه او را شاید سودی به ما دهد یا برگیریمش فرزندی و بدینسان فرمانروائی دادیم به یوسف در زمین و تا بیاموزیمش از تعبیر خوابها و خدا چیره است بر کار او و لیکن بیشتر مردم نمی‌دانند'),
(1618, 12, 22, 'و هنگامی که رسید نیروهای خود را (جوانی خود را) دادیمش فرمانروائی و دانش را و بدینسان پاداش دهیم نیکوکاران‌'),
(1619, 12, 23, 'و کام خواست زنی که در خانه‌اش بود از او و ببست درها را و گفت هان بشتاب گفت پناه برم به خدا همانا او پروردگار من نکو داشت جایگاه مرا همانا رستگار نشوند ستمکاران‌'),
(1620, 12, 24, 'و هر آینه آن زن آهنگ او کرد و او آهنگ آن زن کرد اگر نمی‌دید دستاویز پروردگار خویش را چنین تا برگردانیم از او زشتی و ناشایست را که او است از بندگان ما ناآلودگان‌'),
(1621, 12, 25, 'و سبقت گرفتند بر همدیگر بسوی در و بشکافت آن زن جامه او را از پشت و یافتند شوهرش را نزد در گفت آن زن چیست پاداش آنکه به خاندان تو بدی خواهد مگر آنکه زندانی شود یا شکنجه دردناک‌'),
(1622, 12, 26, 'گفت این آن کام جست از من و گواهی داد گواهی از خاندان آن زن که اگر پیراهنش از پیش دریده باشد راست گوید آن زن و او است از دروغگویان‌'),
(1623, 12, 27, 'و اگر جامه او از پشت بشکافته زن دروغ گوید و او است از راستگویان‌'),
(1624, 12, 28, 'پس گاهی که دید پیراهنش از پشت بشکافته گفت همانا این از نیرنگ شما زنان است همانا نیرنگ شما است بزرگ‌'),
(1625, 12, 29, 'ای یوسف درگذر از این و تو ای زن آمرزش خواه برای گناه خود که هستی تو از لغزشکاران‌'),
(1626, 12, 30, 'و گفتند زنانی در شهر زن عزیز در پی جستن کام از بنده خویش است همانا مهر او دلش را ربوده هر آینه بینیمش در گمراهی آشکار'),
(1627, 12, 31, 'پس هنگامی که بشنید آن زن آنان را پی ایشان فرستاد و فراهم کرد برای هر یک بالشی و داد به هرکدام از ایشان کاردی و گفت برون آی بر ایشان پس گاهی که بدیدندش بزرگش شمردند و بریدند دستهای خویش را و گفتند هرگز به خدا نباشد این بشری نیست او مگر فرشته گرامی‌'),
(1628, 12, 32, 'گفت این است آنکه مرا بدو نکوهش می‌کردید و همانا کام از او خواستم ولی خودداری کرد و اگر نکند آنچه بفرمایمش همانا زندانی شود و هر آینه بگردد از زبونان‌'),
(1629, 12, 33, 'گفت پروردگارا زندان مرا خوشتر است از آنچه خوانندم بسوی آن و اگر بازنگردانی از من نیرنگ ایشان را آهنگشان کنم و بشوم از نادانان‌'),
(1630, 12, 34, 'پس پذیرفت از او پروردگارش و برگردانید از او نیرنگ آنان را همانا او است شنوای دانا'),
(1631, 12, 35, 'سپس نمایان شد برای ایشان پس از آنکه دیدند نشانیها را که هر آینه به زندانش افکنند تا زمانی‌'),
(1632, 12, 36, 'و درون شدند با وی زندان را دو جوان گفت یکیشان همانا به خواب دیدم خویش را که انگوری می‌فشارم و دیگری گفت دیدم به خواب خویش را که برگرفتم بر سر خود نانی که می‌خوردند پرندگان از آن آگهی ده ما را از تعبیر آن که بینیمت هر آینه از نکوکاران‌'),
(1633, 12, 37, 'گفت نیاید شما را خوراکی که روزیمند شویدش جز آنکه آگهیتان دهم به سرانجامش پیش از آنکه بیاید شما را این از آن است که بیاموخت مرا پروردگارم همانا من رها کردم آئین گروهی را که ایمان نیاورند به خدا و ایشانند به آخرت کافران‌'),
(1634, 12, 38, 'و پیروی کردم آئین پدرانم ابراهیم و اسحق و یعقوب را نرسد ما را که شرک ورزیم به خدا چیزی این از فضل خداوند است بر ما و بر مردم لیکن بیشتر مردم سپاس نگزارند'),
(1635, 12, 39, 'ای دو یار زندانم آیا پروردگارانی پراکنده بهترند یا خداوند یگانه سخت‌گیر'),
(1636, 12, 40, 'نمی‌پرستید جز او مگر نامهائی که نامیدید شما و پدران شما نفرستاده است خدا بدانها فرمانی نیست فرمان جز برای خدا فرمود که نپرستید جز او را این است دین استوار و لیکن بیشتر مردم نمی‌دانند'),
(1637, 12, 41, 'ای یاران زندان اما یکی از شما بنوشاند خداوند خویش را باده و اما دیگری به دار آویزان شود پس بخورند پرندگان از سرش بگذشت کاری که در آن فتوی می‌خواستید'),
(1638, 12, 42, 'و گفت بدان که می‌پنداشتش نجات یابنده از آن دو یاد کن مرا نزد خداوند خویش پس فراموشش ساخت شیطان یادآوری خداوندش را پس ماند در زندان چند سال‌'),
(1639, 12, 43, 'و گفت پادشاه دیدم در خواب هفت گاو فربه‌ای که می‌خوردندشان هفت گاو لاغر و هفت خوشه سبز و دیگری خشک ای گروه فتوی دهیدم در خوابم اگر خواب را تعبیر توانید'),
(1640, 12, 44, 'گفتند پاره‌ای خوابهای بیهوده است و نیستیم ما تعبیر خوابهای بیهوده را دانایان‌'),
(1641, 12, 45, 'و گفت آنکه نجات یافته بود از آن دو و یاد آمدش پس از نسلی من آگهیتان دهم به تعبیرش پس مرا بفرستید'),
(1642, 12, 46, 'یوسف ای راستگو فتوی ده ما را در هفت گاو فربه که خورندشان هفت لاغر و هفت خوشه سبز و دیگرانی خشک شاید بازگردم بسوی مردم شاید ایشان بدانند'),
(1643, 12, 47, 'گفت کشت کنید هفت سال پیاپی پس آنچه را درویدید بگذاریدش در خوشه خود جز اندکی از آنچه می‌خورید'),
(1644, 12, 48, 'پس بیاید پس از این هفت سال سخت که می‌خورند آنچه آماده کردید برای آنها بجز اندکی از آنچه نگه می‌دارید'),
(1645, 12, 49, 'پس بیاید پس از آن سالی که در آن باران بارد بر مردم و در آن رهائی یابند'),
(1646, 12, 50, 'گفت شاه بیاوریدم بدو و هنگامی که آمدش فرستاده گفت برگرد بسوی خداوند خویش پس بپرس از او چه بود آن زنان را که سخت بریدند دستهای خود را همانا پروردگار من به نیرنگ آنان است دانا'),
(1647, 12, 51, 'گفت چه شد شما را که کام جستید از یوسف گفتند هرگز خدا را ندانستیم بر او زشتیی گفت زن عزیز اکنون پدیدار گشت حقّ من کام خواستم از او و او است راستگویان‌'),
(1648, 12, 52, 'این تا بداند که من خیانتش نکردم پنهان و آنکه خدا نیست رهبری‌کننده نیرنگ خیانتکاران‌'),
(1649, 12, 53, 'و تبرئه نکنم (بیزار نشمرم) خویشتن را همانا دل بسیار امرکننده است به زشتی مگر آنکه رحم کرده است پروردگار من همانا پروردگار من است آمرزنده مهربان‌'),
(1650, 12, 54, 'گفت شاه بر من درآریدش تا مخصوصش به خود سازم سپس گاهی که سخن گفت با او گفت همانا توئی امروز نزد ما فرمانروای امین‌'),
(1651, 12, 55, 'گفت بگمار مرا بر گنجهای زمین که منم نگهبانی دانشمند'),
(1652, 12, 56, 'بدینسان فرمانروائی دادیم یوسف را در زمین تا نشیمن گزیند از آن در هرجا که خواهد رسانیم به رحمت خویش هرکه را خواهیم و تباه نکنیم پاداش نکوکاران را'),
(1653, 12, 57, 'و همانا پاداش آخرت بهتر است برای آنان که ایمان آوردند و بودند پرهیز می‌کردند'),
(1654, 12, 58, 'و آمدند برادران یوسف پس بر او درآمدند پس بشناخت آنان را و بودند ایشان او را ناشناسندگان‌'),
(1655, 12, 59, 'و هنگامی که بست بار ایشان را گفت بیارید مرا برادری که شما را است از پدر شما آیا نبینید که تمام می‌دهم پیمانه را و منم بهترین میزبانان (فرودآرندگان)'),
(1656, 12, 60, 'پس اگر نیاوریدم بدو نه پیمایشی باشد شما را نزد من و نه به من نزدیک شوید'),
(1657, 12, 61, 'گفتند زود است بفریبیم از او پدرش را همانا مائیم کنندگان‌'),
(1658, 12, 62, 'و گفت به جوانان خود بگذارید کالای ایشان را در بارهای ایشان باشد بشناسندش گاهی که بازگردند بسوی خاندان خود شاید بازگردند ایشان‌'),
(1659, 12, 63, 'و هنگامی که بازگشتند بسوی پدر خویش گفتند ای پدر بازداشته شد از ما پیمایش پس بفرست با ما برادر ما را تا بپیمائیم و همانا مائیم برای او نگهدارندگان‌'),
(1660, 12, 64, 'گفت آیا امین شمرم شما را بر او جز بدانگونه که امین شمردم شما را بر برادرش از پیش پس خدا است بهترین نگاهدارنده و او است مهربانترین مهربانان‌'),
(1661, 12, 65, 'و هنگامی که برگشودند بار خویش را دیدند کالای خود را که بازگردانیده شده است بسوی ایشان گفتند ای پدر چه می‌خواهیم اینک کالای ما برگردانیده شد بسوی ما و خوراک دهیم خاندان خویش را و نگهداریم برادر خویش را و بیفزائیم پیمایش اشتری را این است پیمایشی اندک‌'),
(1662, 12, 66, 'گفت نفرستمش با شما تا دهیدم گروگانی از خدا که بیاریدش بسوی من مگر آنکه فراگرفته شوید و هنگامی که دادندش گروگان خود را گفت خدا است بر آنچه گوئیم وکیل‌'),
(1663, 12, 67, 'و گفت ای فرزندان من درون نشوید از یک در و درآئید از درهای پراکنده و بی‌نیاز نگردانم شما را از خدا به چیزی نیست حکم جز آن خدا بر او توکل کردم و بر او توکّل‌کنند توکّل‌کنندگان‌'),
(1664, 12, 68, 'و هنگامی که درآمدند بدانسان که فرمود ایشان را پدرشان نبود که بی‌نیاز کند از ایشان از خدا چیزی مگر خواسته‌ای را در دل یعقوب که برآوردش و همانا او است دانشمندی بدانچه بیاموختیمش و لیکن بیشتر مردم نمی‌دانند'),
(1665, 12, 69, 'و هنگامی که درآمدند بر یوسف جای داد نزد خود برادر خود را گفت همانا منم برادر تو پس اندوهگین مباش بدانچه بودند می‌کردند'),
(1666, 12, 70, 'و هنگامی که بست بار ایشان را گذارد جام را میان بار برادر خویش پس بانگ برآورد بانگ‌دهنده‌ای که‌ای کاروان همانا شمائید دزدی‌کنندگان‌'),
(1667, 12, 71, 'گفتند و بازگشتند بسوی آنان چه چیز گم کردید'),
(1668, 12, 72, 'گفتند گم کردیم پیمانه (جام) شاه را و هرکه آن را آورد برایش بار اشتری است و منم بدان ضامن‌'),
(1669, 12, 73, 'گفتند به خدا سوگند شما می‌دانید ما نیامدیم تا تبهکاری کنیم در زمین و نبوده‌ایم دزدان‌'),
(1670, 12, 74, 'گفتند پس چیست کیفر او اگر باشید دروغگویان‌'),
(1671, 12, 75, 'گفتند کیفر او هرکه یافت شود در بار اوست کیفرش بدینگونه کیفر دهیم ستمکاران‌'),
(1672, 12, 76, 'پس آغاز کرد به بارهای آنان پیش از بار برادر خویش سپس درآوردش از بار برادر خویش بدینگونه تدبیر کردیم برای یوسف نمی‌رسید او را که برگیرد برادرش را در کیش شاه جز آنکه خدا خواهد بالا بریم پایه‌های هرکه را خواهیم و بر بالای هر دانشوری است دانشمند'),
(1673, 12, 77, 'گفتند اگر بدزدد همانا دزدیده است برادری او را پیش پس نهانش داشت یوسف در دل خود و فاش ننمودش برای آنان گفت شما بدترید در جایگاه و خدا داناتر است بدانچه می‌ستایید'),
(1674, 12, 78, 'گفتند ای عزیز همانا او را پدری است پیری فرتوت پس برگیر یکی از ما را به جای او که می‌بینیم تو را از نکوکاران‌'),
(1675, 12, 79, 'گفت پناه برم به خدا که بگیریم جز آن را که یافتیم کالای خویش را به نزدش که مائیم در آن هنگام از ستمگران‌'),
(1676, 12, 80, 'پس هنگامی که نومید شدند از او جدا شدند رازگویان گفت بزرگ ایشان آیا ندانستید که پدر شما بگرفت بر شما گروگانی از خدا و از پیش چه کوتاهی کردید در باره یوسف پس ترک نکنم زمین را هرگز تا رخصتم دهد پدرم یا حکم کند خدا برای من و اوست بهترین حکمرانان‌'),
(1677, 12, 81, 'بازگردید بسوی پدر خویش پس بگوئید ای پدر همانا فرزندت دزدید و گواهی ندهیم جز بدانچه دانستیم و نیستیم ناپیدا را نگهبان‌'),
(1678, 12, 82, 'و بپرس شهری را که در آن بودیم و کاروانی را که در آن آمدیم و همانا مائیم راستگویان‌'),
(1679, 12, 83, 'گفت بلکه آراست برای شما دلهای شما امری را پس شکیبائی نکو است امید است خدا بیاوردم به هر دو با هم که او است دانای حکیم‌'),
(1680, 12, 84, 'و روی برتافت از ایشان و گفت دریغ بر یوسف و سفید شد دیدگان او از اندوه و بود به اندوه گلوگیر'),
(1681, 12, 85, 'گفتند به خدا ترک نکنی یاد کردن یوسف را تا بیماری فسرده گردی یا بشوی از نابودشدگان‌'),
(1682, 12, 86, 'گفت جز این نیست که شکوه اندوه و فسردگی خویش را به خدا برم و می‌دانم از خدا آنچه نمی‌دانید'),
(1683, 12, 87, 'ای فرزندان من بروید پس جستجو کنید از یوسف و برادرش و نومید نشوید از روح خدا همانا نومید نشوند از روح خدا جز گروه کافران‌'),
(1684, 12, 88, 'پس هنگامی که درآمدند بر او گفتند ای عزیز رسید به ما و خاندان ما رنج و آوردیم کالائی ناچیز پس پرکن برای ما پیمانه را و تصدق کن بر ما که خدا پاداش دهد به تصدق‌کنندگان‌'),
(1685, 12, 89, 'گفت آیا دانستید چه کردید به یوسف و برادرش هنگامی که بودید نادانان‌'),
(1686, 12, 90, 'گفتند مگر توئی یوسف گفت من یوسفم و این است برادرم که خدا منّت نهاد بر ما بدرستی هرکه پرهیزکاری کند و شکیبا شود همانا خدا تباه نگرداند پاداش نکوکاران را'),
(1687, 12, 91, 'گفتند به خدا برگزیده است خدا تو را بر ما و همانا بودیم ما تباهکاران‌'),
(1688, 12, 92, 'گفت نیست نکوهشی بر شما امروز بیامرزد شما را خدا و او است مهربانترین مهربانان‌'),
(1689, 12, 93, 'ببرید جامه مرا این پس بیفکنیدش بر روی پدرم تا بینا گردد و بیارید مرا به خانواده خود همگی‌'),
(1690, 12, 94, 'و هنگامی که بار نهاد کاروان گفت پدر ایشان همانا می‌یابم بوی یوسف را اگر نه کم خردم خوانید'),
(1691, 12, 95, 'گفتند به خدا توئی در گمراهی پیشین‌'),
(1692, 12, 96, 'و هنگامی که آمد مژده‌رسان بیفکندش بر روی او پس برگشت بینا گفت آیا نگفتم به شما که می‌دانم از خدا آنچه را نمی‌دانید'),
(1693, 12, 97, 'گفتند ای پدر ما آمرزش خواه برای ما گناهان ما را که بودیم لغزشکاران‌'),
(1694, 12, 98, 'گفت بزودی آمرزش خواهم برای شما از پروردگار خود همانا او است آمرزنده مهربان‌'),
(1695, 12, 99, 'و هنگامی که درآمدند بر یوسف کشید در بر خود پدر و مادرش را و گفت به مصر درآئید اگر خدا خواهد آسودگان‌'),
(1696, 12, 100, 'و برآورد پدر و مادر خویش را بر تخت و بیفتادند برایش سجده کنان و گفت ای پدر این است تعبیر خوابم از پیش که گردانیدش پروردگارم راست و همانا به من نکوئی کرد هنگامی که برونم آورد از زندان و بیاورد شما را از بیابان پس از آنکه آشوب کرد شیطان میان من و برادرانم همانا پروردگار من لطیف (بخشنده) است آنچه را خواهد همانا او است دانای حکیم‌'),
(1697, 12, 101, 'پروردگارا همانا دادِیَم از پادشاهی و آموختِیَم از تعبیر خوابها ای پدیدآرنده آسمانها و زمین توئی دوست من در دنیا و آخرت دریاب مرا مسلمان و پیوسته دار مرا با شایستگان‌'),
(1698, 12, 102, 'این از داستانهای ناپیدا است که وحی فرستیم بسوی تو و نبودی نزد ایشان گاهی که گردآوردند کار خویش را حالی که نیرنگ می‌ساختند'),
(1699, 12, 103, 'و نیستند بیشتر مردم و هر چند آرزومند باشی باورکنندگان‌'),
(1700, 12, 104, 'و نپرسشان بر آن مزدی نیست آن مگر یادآوریی برای جهانیان‌'),
(1701, 12, 105, 'بسا نشانهائی در آسمان و زمین که می‌گذرند بر آنها و ایشانند از آن روی‌گردانان‌'),
(1702, 12, 106, 'و ایمان نیارند بیشتر ایشان به خدا مگر آنکه ایشانند شرک‌ورزندگان‌'),
(1703, 12, 107, 'پس آیا ایمن شدند که بیایدشان فراگیرنده‌ای از شکنجه خدا یا بیایدشان ساعت ناگهان حالی که درنیابند ایشان‌'),
(1704, 12, 108, 'بگو اینک راه من می‌خوانم بسوی خدا بر بینائی من و هر که پیرویم کند و منزه است خدا و نیستم من از شرک‌ورزان‌'),
(1705, 12, 109, 'و نفرستادیم پیش از تو جز مردانی که وحی فرستادیم بسوی آنان از اهل شهرها پس آیا نمی‌گردند در زمین تا بنگرند چگونه بود فرجام آنان که پیش از ایشان بودند و همانا خانه آخرت بهتر است برای آنان که پرهیز کنند آیا بخرد نمی‌یابید'),
(1706, 12, 110, 'تا هنگامی که نومید شدند فرستادگان و پنداشتند که دروغ گفته شدند بیامدشان یاری ما پس نجات دهیم هرکه را خواهیم و برنگردد خشم ما از گروه گنهکاران‌'),
(1707, 12, 111, 'همانا بوده است در داستانهای ایشان عبرتی برای خردمندان نیست داستانی دروغ پرداخته و لیکن گواهی آنچه پیش روی او است و تفصیل همه چیز و هدایت و رحمتی برای گروه ایمان‌آورندگان‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'این است آیتهای کتاب و آنچه فرستاده شد بسوی تو از پروردگار تو حق است و لیکن بیشتر مردم باور ندارند'),
(1709, 13, 2, 'خدا است آنکه بیافراشت آسمانها را بی‌ستونی که بینیدش سپس استوار شد بر عرش و رام کرد مهر و ماه را هر کدام روانند تا سرآمدی نامبرده می‌پردازد کار را تفصیل دهد آیتها را شاید شما به ملاقات پروردگار خویش یقین آرید'),
(1710, 13, 3, 'و او است آنکه گسترانید زمین را و نهاد در آن لنگرها و جویهائی و از همه میوه‌ها نهاد در آن دو جفت می‌پوشاند شب را به روز همانا در این است آیتهائی برای گروهی که اندیشه کنند'),
(1711, 13, 4, 'و در زمین است بخشهائی نزدیک به هم و باغهائی از انگورها و کشتزار و درختان خرما رسته از یک بن و نارسته از یک بن که آبیاری می‌شود به یک آب و برتری نهیم پاره‌ای از آنها را بر پاره‌ای در خوراک همانا در این است آیتهائی برای گروهی که بخرد دریابند'),
(1712, 13, 5, 'و اگر شگفت مانی پس شگفت است گفتار ایشان که آیا گاهی که خاک شویم آیا به راستی مائیم در آفرینشی نوین آنانند که کفر ورزیدند به پروردگار خود و آنانند که زنجیرها به گردنهای ایشان است و آنانند یاران آتش ایشانند در آن جاودانان‌'),
(1713, 13, 6, 'و شتاب خواهند از تو به بدی پیش از خوبی حالی که گذشت است پیش از ایشان شکنجه‌ها و همانا پروردگار تو آمرزشگر است برای مردم بر ستمگریشان و همانا پروردگار تو است سخت شکنجه‌'),
(1714, 13, 7, 'و گویند آنان که کفر ورزیدند چرا فرود نیاید بر او آیتی از پروردگارش جز این نیست که توئی بیم‌دهنده و هر گروهی را است راهنمائی‌'),
(1715, 13, 8, 'خدا داند آنچه بارور شود هر ماده‌ای و آنچه فروبرند زهدانها و آنچه بیفزایند و هر چیزی است نزد او به اندازه‌'),
(1716, 13, 9, 'دانای نهان و آشکار آن بزرگ عالی‌جایگاه‌'),
(1717, 13, 10, 'یکسان است از شما آنکه نهان کند گفتار را و آنکه آشکار سازدش و آنکه نهان است در شب و به کار خویش اندر است در روز'),
(1718, 13, 11, 'او را است پیاپی آیندگانی از پیش رویش و از پشت سرش که نگه میدارندش از امر خدا همانا خدا دگرگون نکند آنچه را به گروهی است تا دگرگون سازند آنچه را که در خود آنان است و هرگاه بخواهد خدا بر گروهی بدی پس نیست بازگشتی برای آن و نیست ایشان را جز او سرپرستی‌'),
(1719, 13, 12, 'و او است آنکه بنماید به شما برق را بیمی و امیدی و پدید آرد ابرهای سنگین‌'),
(1720, 13, 13, 'و درود گوید رعد به سپاس او و فرشتگان از بیمش و بفرستد صاعقه‌ها را تا برساند آنها را به هر که خواهد و می‌ستیزند در خدا و او است سخت‌نیرو'),
(1721, 13, 14, 'وی را است دعوت حق و آنان را که خوانند جز او اجابت نکنندشان به چیزی مگر مانند گشاینده دستهای خویش بسوی آب تا برسد به دهانش و نیست رسنده بدان و نیست دعوت کافران مگر در گمراهی‌'),
(1722, 13, 15, 'و برای خدا سجده کنند آنان که در آسمانها و زمینند خواه و ناخواه و سایه‌های آنان در بامداد و شامها'),
(1723, 13, 16, 'بگو کیست پروردگار آسمانها و زمین بگو خدا بگو پس آیا برگرفتید جز او دوستانی که دارا نیستند برای خویشتن سود و نه زیانی را بگو آیا یکسانند کور و بینا یا آیا یکسانند تاریکیها و روشنائی یا قرار دادند برای خدا شریکانی که بیافریدند همانند آفرینش او پس مشتبه شده است آفرینش بر ایشان بگو خدا است آفریننده همه چیز و او است یکتای چیرگی‌جوی‌'),
(1724, 13, 17, 'فرستاد از آسمان آبی پس روان گشت دره‌هائی از سیل به اندازه خود پس برداشت سیل کفی برآمده و از آنچه می‌افروزند بر آن در آتش به طلب زیوری یا کالائی کفی مانند آن بدینگونه می‌زند خدا حق و باطل را اما کف می‌رود کناری و اما آنچه سود دهد مردم را پس می‌ماند در زمین بدینگونه می‌زند خدا مثلها را'),
(1725, 13, 18, 'آنان را که پذیرفتند از پروردگار خویش نکوئی است و آنان که نپذیرفتند اگر باشد ایشان را آنچه در زمین است همگی و مانند آن با آن هرآینه به جای خویش دهند آنان برای ایشان است بدی حساب و جایگاه ایشان است دوزخ و چه زشت است آن آرامیدنگاه‌'),
(1726, 13, 19, 'آیا آنکه می‌داند که آنچه فرستاده شده است بسوی تو از پروردگارت حق است مانند آن است که او کور است جز این نیست که یادآور شوند خداوندان خردها'),
(1727, 13, 20, 'آنان که وفا کنند به عهد خدا و نشکنند پیمان را'),
(1728, 13, 21, 'و آنان که پیوند کنند آنچه را خدا فرموده است که پیوند شود و بترسند پروردگار خویش را و بترسند از زشتی حساب‌'),
(1729, 13, 22, 'و آنان که شکیبائی گزیدند برای روی پروردگار خود و بپا داشتند نماز را و دادند از آنچه روزیشان دادیم نهان و آشکارا و دور کنند به خوبی بدی را آنان را است فرجام آن سرای‌'),
(1730, 13, 23, 'بهشتهای جاودان درآیند در آنها خود و آنان که شایسته شدند از پدرانشان و همسرانشان و فرزندانشان و فرشتگان درآیند بر ایشان از هر دری‌'),
(1731, 13, 24, 'سلام بر شما بدانچه صبر نمودید چه خوب است فرجام آن سرای‌'),
(1732, 13, 25, 'و آنان که می‌شکنند پیمان خدا را پس از بستنش و می‌برند آنچه را خدا فرمود که پیوند شود و تبهکاری کنند در زمین آنان را است لعنت و برای ایشان است بدی آن سرای‌'),
(1733, 13, 26, 'همانا خدا فراخ گرداند روزی را برای هر که خواهد و تنگ گرداند و خرسند شدند به زندگی دنیا حالی که نیست زندگانی دنیا در آخرت مگر بهره‌ای‌'),
(1734, 13, 27, 'و گویند آنان که کفر ورزیدند چرا فرود نیاید بر او آیتی از پروردگارش بگو همانا خدا گمراه کند هر که را خواهد و هدایت کند بسوی خود هر که را بازگشت (یا زاری کند)'),
(1735, 13, 28, 'آنان که ایمان آوردند آرام گیرد دلهای ایشان به یاد خدا همانا به یاد خدا آرام گیرد دلها'),
(1736, 13, 29, 'آنان که ایمان آوردند و کردار شایسته کردند خوشی باد برای ایشان و نیکی بازگشتگاه‌'),
(1737, 13, 30, 'بدینسان فرستادیمت در امتی که بگذشته است پیش از آن امتهائی تا بخوانی بر ایشان آنچه را وحی فرستادیم بسوی تو حالی که کفر می‌ورزند به خدای مهربان بگو او است پروردگار من نیست خدائی جز او بر او توکل کردم و بسوی او است بازگشتم‌'),
(1738, 13, 31, 'و اگر می‌بود قرآنی که روان می‌شدند بدان کوه‌ها یا پاشیده می‌شد بدان زمین یا سخن گفته می‌شدند بدان مردگان بلکه خدای را است کار همگی پس آیا نومید نشدند آنان که ایمان آوردند که اگر می‌خواست خدا هرآینه هدایت می‌کرد مردم را همگی و پیوسته آنان را که کفر ورزیدند می‌رسد بدانچه ساختند سرکوبی (کوبنده‌ای) یا فرود می‌آید نزدیکی خانه ایشان تا برسد وعده خدا همانا خدا خلف نمی‌کند میعاد را'),
(1739, 13, 32, 'و همانا استهزاء شد به فرستادگانی پیش از تو پس مهلت دادم آنان را که کفر ورزیدند سپس گرفتم ایشان را پس چگونه بود مرا عقاب‌'),
(1740, 13, 33, 'پس آیا آنکه او است ایستاده بر هر کس بدانچه دست آورده است و قرار دادند برای خدا شریکانی بگو نام بریدشان یا آگهی می‌دهید او را بدانچه نمی‌داند در زمین یا به ظاهری از گفتار بلکه بیاراست برای آنان که کفر ورزیدند نیرنگشان و بازداشته شدند از راه و آن را که گمراه کند خدا نباشدش راهنمای‌'),
(1741, 13, 34, 'آنان را است شکنجه‌ای در زندگی دنیا و همانا شکنجه آخرت است گرانتر و نیست ایشان را از خدا نگهدارنده‌ای‌'),
(1742, 13, 35, 'مثل بهشتی که نوید داده شدند پرهیزکاران روان است زیر آن جویها خوراکیش همیشگی است و سایه‌اش این است فرجام آنان که پرهیزکاری کردند و فرجام کافران است آتش‌'),
(1743, 13, 36, 'و آنان را که کتاب دادیم شاد شوند بدانچه فرود آید بسوی تو و از احزاب است آنکه انکار کند پاره آن را بگو جز این نیست مامور شدم که پرستش کنم خدا را و شرک نورزم بدو بسوی او می‌خوانم و بسوی او است بازگشتم‌'),
(1744, 13, 37, 'و بدینسان فرستادیمش حکمی عربی (فرمانی به تازی) و اگر پیروی کنی هوسهای ایشان را پس از آنچه بیامدت از دانش نباشدت از خدا دوست و نه نگهدارنده‌ای‌'),
(1745, 13, 38, 'و همانا فرستادیم پیمبرانی را پیش از تو و قرار دادیم برای ایشان زنان و فرزندانی نبوده است فرستاده‌ای را که بیارد آیتی جز به اذن خدا برای هر سرآمدی است نامه‌ای‌'),
(1746, 13, 39, 'بزداید خدا آنچه را خواهد و بازنهد (ثبت کند) و نزد او است مادر کتاب‌'),
(1747, 13, 40, 'و اگر بنمایانیمت پاره آنچه بدیشان وعده دهیم یا دریابیمت (بمیرانیمت) جز این نیست که بر تو است رساندن و بر ما است حساب‌'),
(1748, 13, 41, 'آیا ندیدند که ما می‌پردازیم به زمین می‌کاهیمش از همه سوی آن و خدا حکم کند نیست تبدیل‌کننده برای حکمش و او است زود شمار'),
(1749, 13, 42, 'و همانا نیرنگ ساختند آنان که پیش از ایشان بودند پس خدای را است نیرنگ همگی می‌داند آنچه را فراهم آرد هر کس و زود است بدانند کافران که برای کیست پایان آن سرای‌'),
(1750, 13, 43, 'و گویند آنان که کفر ورزیدند نیستی فرستاده بگو بس است خدا گواهی میان من و شما و آنکه نزد او است علم کتاب‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'کتابی است فرستادیمش به سویت تا برون آری مردم را از تاریکیها بسوی روشنائی به اذن پروردگار ایشان بسوی راه خداوند ارجمند ستوده‌'),
(1752, 14, 2, 'خدا است آنکه وی را است آنچه در آسمانها و آنچه در زمین است و وای برای کافران از شکنجه سخت‌'),
(1753, 14, 3, 'آنان که برگزینند زندگانی دنیا را بر آخرت و بازدارند از راه خداوند و خواهندش کج آنانند در گمراهی دور'),
(1754, 14, 4, 'و نفرستادیم فرستاده‌ای جز با زبان قوم خود تا آشکار کند برای ایشان پس گمراه کند خدا هر که را خواهد و هدایت کند هر که را خواهد و او است عزتمند حکیم‌'),
(1755, 14, 5, 'و همانا فرستادیم موسی را به آیتهای خود که برون آور قوم خویش را از تاریکیها بسوی روشنائی و یادآوریشان کن به روزهای خدا همانا در این است آیتهایی برای هر شکیبائی سپاسگزار'),
(1756, 14, 6, 'و هنگامی که گفت موسی به قوم خود یاد آرید نعمت خدا را بر خویشتن گاهی که رهانید شما را از خاندان فرعون روامی‌داشتند بر شما زشتی عذاب را و به سختی می‌کشتند پسران شما را و به جای می‌گذاردند زنان شما را و در این بود آزمایشی از پروردگار شما بزرگ‌'),
(1757, 14, 7, 'و هنگامی که اعلام کرد پروردگار شما اگر سپاسگزارید هر آینه بیفزایم شما را و اگر کفر ورزیدید همانا عذاب من است سخت‌'),
(1758, 14, 8, 'و گفت موسی اگر کفر ورزید شما و هر که در زمین است همگی همانا خدا بی‌نیازی است ستوده‌'),
(1759, 14, 9, 'آیا نرسید به شما داستان آنان که پیش از شما بودند قوم نوح و عاد و ثمود و آنان که پس از ایشان بودند نداندشان جز خدا بیامدشان فرستادگان ایشان به نشانیها پس بازگردانیدند دستهای خویش را در دهانهای خود و گفتند همانا کفر ورزیدیم بدانچه فرستاده شده‌اید بدان و همانا مائیم در شکی از آنچه ما را بدان خوانید شک‌آورنده‌'),
(1760, 14, 10, 'گفتند فرستادگان ایشان آیا در خدا هست شکی پدیدآرنده آسمانها و زمین می‌خواند شما را تا بیامرزد برای شما از گناهان شما و پس اندازد شما را تا سرآمدی نامبرده گفتند نیستید شما مگر بشری همانند ما خواهید که بازدارید ما را از آنچه می‌پرستیدند پدران ما پس بیاورید ما را به فرمانروائی آشکار'),
(1761, 14, 11, 'گفت بدیشان فرستادگان ایشان نیستیم ما جز بشری همانند شما و لیکن خدا منّت نهد بر هرکه خواهد از بندگان خویش و نرسد ما را که بیاریمتان فرمانروائیی مگر به اذن خدا و بر خدا پس باید توکّل کنند مؤمنان‌'),
(1762, 14, 12, 'و چه شود ما را توکّل نکنیم بر خدا حالی که به راستی هدایت کرده است ما را به راه‌های ما و همانا شکیبا شویم بر آنچه ما را بیازرده‌اید و بر خدا پس باید توکّل کنند توکل‌کنان‌'),
(1763, 14, 13, 'و گفتند آنان که کفر ورزیدند به پیمبران خود همانا برون رانیم شما را از سرزمین خود یا که بازگردید در کیش ما پس وحی کرد بسوی ایشان پروردگارشان که البته هلاک کنیم ستمگران را'),
(1764, 14, 14, 'و البته جایگزین سازیم شما را در زمین پس از ایشان این برای آن کس است که بترسد جایگاه مرا و بترسد از تهدید من‌'),
(1765, 14, 15, 'و پیروزی (یا گشایش) جستند و نومید شد هر گردنکش کینه‌ورزی‌'),
(1766, 14, 16, 'پشت سر او است دوزخ و نوشانیده شود از آب چرک خون آلود'),
(1767, 14, 17, 'جرعه جرعه نوشدش و نیارد فروبردش (گوارا شودش) و بیایدش مرگ از همه سوی و نیست او مرده و از پس او است عذابی انبوه‌'),
(1768, 14, 18, 'مثَل آنان که کفر ورزیدند به پروردگار خویش کردار ایشان مانند خاکستری است که بوزد بدان باد در روزی تند قادر نباشند از آنچه فراهم کردند بر چیزی این است آن گمراهی دور'),
(1769, 14, 19, 'آیا نبینی که خدا بیافریده است آسمانها و زمین را به حقّ اگر خواهد می‌برد شما را و می‌آورد آفرینشی نوین‌'),
(1770, 14, 20, 'و نیست آن بر خدا گران‌'),
(1771, 14, 21, 'و برون آمدند پیشگاه خدا همگی پس گفتند ناتوانان بدانان که سرکشی ورزیدند که بودیم ما شما را پیروانی آیا هستید بی‌نیازکننده ما از عذاب خدا به چیزی گفتند اگر رهبری می‌کرد ما را خدا هر آینه راهبریتان می‌کردیم یکسان است بر ما چه بی‌تابی کنیم و چه شکیبا شویم نیست ما را چاره‌ای‌'),
(1772, 14, 22, 'و گفت شیطان هنگامی که گذشت کار همانا خدا وعده داد شما را وعده حقّ و وعده دادم شما را پس خلف کردم با شما و نبود مرا بر شما فرمانروائیی جز آنکه خواندم شما را پس اجابتم کردید پس سرزنش نکنید مرا و سرزنش کنید خویشتن را نه من فریاد شما رسم و نه شمائید دادرس من همانا کفر ورزیدم بدانچه مرا شریک گردانیدید از پیش همانا ستمگران را است عذابی دردناک‌'),
(1773, 14, 23, 'و درآورده شدند آنان که ایمان آوردند و کردار شایسته کردند باغهائی که روان است زیر آنها جویها جاودانند در آن به اذن پروردگار خویش درود ایشان است در آن سلام‌'),
(1774, 14, 24, 'آیا ندیدی چگونه برزد است خدا مثَلی سخنی پاک مانند درختی است پاک بیخ و بنش برجا و شاخ و برگش در آسمان‌'),
(1775, 14, 25, 'می‌دهد خوراک خود را هرگاه (همیشه) به اذن پروردگار خویش و می‌زند خدا مثَلها را برای مردم شاید یادآور شوند'),
(1776, 14, 26, 'و مثَل سخنی پلید همانند درختی است پلید که برجهیده است از روی زمین نیستش آرامشی‌'),
(1777, 14, 27, 'استوار دارد خدا آنان را که ایمان آوردند به گفتار استوار در زندگانی دنیا و در آخرت و گمراه کند خدا ستمگران را و می‌کند خدا هر چه خواهد'),
(1778, 14, 28, 'آیا ننگریستی بدانان که تبدیل کردند نعمت خدا را به ناسپاسی و درآوردند قوم خویش را به خانه مرگ (نابودی)'),
(1779, 14, 29, 'دوزخ می‌چشندش و چه زشت است آن آرامشگاه‌'),
(1780, 14, 30, 'و قرار دادند برای خدا همتایانی تا گم کنند از راهش بگو کامیاب شوید که بازگشت شما است بسوی آتش‌'),
(1781, 14, 31, 'بگو به بندگان من آنان که ایمان آوردند بپای دارند نماز را و بدهند از آنچه روزیشان دادیم نهان و آشکارا پیش از آنکه بیاید روزی که نیست سوداگریی در آن و نه دوستیی‌'),
(1782, 14, 32, 'خدا است آنکه آفرید آسمانها و زمین را و فرستاد از آسمان آبی پس برون آورد بدان از میوه‌ها روزیی برای شما و رام کرد برای شما کشتی را تا روان شود در دریا به فرمان او و رام کرد برای شما جویها را'),
(1783, 14, 33, 'و رام کرد برای شما مِهر و ماه را از پی هم درآیندگان و رام کرد برای شما شب و روز را'),
(1784, 14, 34, 'و داد به شما از هرچه مسئلتش کردید و اگر بشمرید نعمت خدا را شمار آن را نتوانید همانا انسان است ستمگری ناسپاس‌'),
(1785, 14, 35, 'و هنگامی که گفت ابراهیم پروردگارا بگردان این شهر را آسوده و برکنار دار مرا و فرزندانم از پرستش بتها'),
(1786, 14, 36, 'پروردگارا همانا آنها گمراه کردند بسیاری را از مردم پس هر که پیرویم کند همانا او از من است و هر که نافرمانیم کند همانا توئی آمرزنده مهربان‌'),
(1787, 14, 37, 'پروردگارا همانا جای دادم از خاندانم (نژادم) در درّه بی‌گیاه (کشت) نزد خانه محترم تو پروردگارا تا بپای دارند نماز را پس بگردان دلهائی را از مردم بگروند بسوی آنان و روزیشان ده از میوه‌ها شاید سپاس گزارند'),
(1788, 14, 38, 'پروردگارا همانا تو دانی آنچه را نهان کنیم و آنچه را آشکار سازیم و نهان نیست بر خدا چیزی در زمین و نه در آسمان‌'),
(1789, 14, 39, 'سپاس خدائی را که بخشید به من سر پیری اسماعیل و اسحق را همانا پروردگار من است شنونده دعاء'),
(1790, 14, 40, 'پروردگارا بگردانم برپاکننده نماز و از فرزندانم پروردگارا و بپذیر درخواست مرا'),
(1791, 14, 41, 'پروردگارا بیامرز برای من و برای پدر و مادرم و برای مؤمنان روزی که بپای شود حساب‌'),
(1792, 14, 42, 'و مپندار البتّه خدا را غافل از آنچه می‌کنند ستمگران جز این نیست که پس اندازدشان برای روزی که در آن بازمانند از گردش دیدگان‌'),
(1793, 14, 43, 'شتاب‌زدگان بالا نگهدارندگان سرهای خویش بازنگردد بسوی ایشان مژگان ایشان و دلهای ایشان است تهی‌'),
(1794, 14, 44, 'و بترسان مردم را روزی که بیایدشان عذاب پس گویند آنان که ستم کردند پروردگارا پس انداز ما را تا سرآمدی نزدیک بپذیریم دعوتت را و پیروی کنیم پیمبران را آیا نبودید شما سوگند یاد کردید پیش از این که نیست شما را زوال (برافتادن)'),
(1795, 14, 45, 'و آرمیدید در نشیمنهای آنان که ستم کردند خویش را و آشکار شد برای شما چه کردیم با ایشان و زدیم برای شما مثَلها را'),
(1796, 14, 46, 'و همانا آوردند نیرنگ خویش را و نزد خدا است نیرنگ ایشان و هر چند باشد نیرنگ ایشان که نابود شود از آن کوه‌ها'),
(1797, 14, 47, 'پس گمان مبر خدا را شکننده وعده خویش به فرستادگان خود همانا خدا عزّتمند است دارای انتقام‌'),
(1798, 14, 48, 'روزی که دگرگون شود زمین به غیر از زمین و آسمانها و بیایند پیشگاه خداوند یگانه چیرگی‌جوی‌'),
(1799, 14, 49, 'و بینی گناهکاران را در آن روز بستگان به زنجیرها'),
(1800, 14, 50, 'جامه‌های ایشان است از قَطران و بپوشد رویهای ایشان را آتش‌'),
(1801, 14, 51, 'تا پاداش دهد خدا به هر کس آنچه فراهم آورده است و همانا خداوند است شتابنده در حساب‌'),
(1802, 14, 52, 'این است آگهیی برای مردم و تا بیم داده شوند بدان و تا بدانند همانا او خداوندی است یکتا و تا یادآور شوند خداوندان خردها');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'این است آیتهای کتاب و قرآنی آشکار'),
(1804, 15, 2, 'بسا دوست دارند آنان که کفر ورزیدند کاش می‌بودند مسلمانان‌'),
(1805, 15, 3, 'بگذارشان بخورند و کام روا دارند و سرگرمشان سازد آرزو پس زود است بدانند'),
(1806, 15, 4, 'و نابود نکردیم شهری را جز آنکه بود آن را کتابی دانسته‌'),
(1807, 15, 5, 'نه پیشی گیرد امّتی سرآمد خویش را و نه پس افتد'),
(1808, 15, 6, 'و گفتند ای آنکه فرود آورده شده است بر او قرآن همانا توئی دیوانه‌'),
(1809, 15, 7, 'چرا نیاوری ما را به فرشتگان اگر هستی از راستگویان‌'),
(1810, 15, 8, 'نمی‌فرستیم فرشتگان را جز به حقّ و نیستند در آن هنگام مهلت‌دادگان‌'),
(1811, 15, 9, 'همانا ما فرستادیم قرآن را و همانا مائیم مر آن را نگهدارندگان‌'),
(1812, 15, 10, 'و همانا فرستادیم پیش از تو در توده‌های پیشینیان‌'),
(1813, 15, 11, 'و نیامدشان فرستاده‌ای جز آنکه بودند بدو استهزاءکنندگان‌'),
(1814, 15, 12, 'بدینسان بمیرانیمش در دلهای گنهکاران‌'),
(1815, 15, 13, 'ایمان نیارند بدان حالی که گذشت آئین پیشینیان‌'),
(1816, 15, 14, 'و اگر بگشائیم بر ایشان دری را از آسمان پس آغاز کنند در آن بالا روند'),
(1817, 15, 15, 'هر آینه گویند جز این نیست که چشم‌بند شدیم بلکه مائیم گروهی جادوشدگان‌'),
(1818, 15, 16, 'و همانا نهادیم در آسمان برجهائی و آراستیم آنها را برای بینندگان‌'),
(1819, 15, 17, 'و حفظ کردیم آنها را از هر شیطانی رانده‌شده‌'),
(1820, 15, 18, 'مگر آنکه بدزدد شنیدن را (دزدکی شنود) که در پیش افتد شهابی آشکار'),
(1821, 15, 19, 'و زمین را گسترانیدیم و افکندیم در آن لنگرهائی و رویانیدیم در آن از هر چیزی زیبا'),
(1822, 15, 20, 'و قرار دادیم برای شما در آن روزیهائی (زندگیهائی) و آن کس را که نیستید مرا او را روزی‌دهندگان‌'),
(1823, 15, 21, 'و نیست چیزی جز آنکه نزد ما است گنجهایش و نفرستیمش مگر به اندازه دانسته‌'),
(1824, 15, 22, 'و فرستادیم بادها را آبستن‌کنندگان پس فرود آوردیم از آسمان آبی پس نوشانیدیمش به شما و نبودید آن را اندوزندگان‌'),
(1825, 15, 23, 'و همانا ما زنده کنیم و بمیرانیم و مائیم ارث‌برندگان‌'),
(1826, 15, 24, 'و همانا دانستیم پیشینیان را از شما و دانستیم پس آیندگان را'),
(1827, 15, 25, 'و همانا پروردگار تو برانگیزاندشان همانا او است حکیم دانا'),
(1828, 15, 26, 'و همانا آفریدیم انسان را از گِل خشک از گِلی تیره ریخته‌شده‌'),
(1829, 15, 27, 'و جن را بیافریدیم از پیش از آتشی که بوی او است کشنده‌'),
(1830, 15, 28, 'و هنگامی که گفت پروردگار تو به فرشتگان که همانا منم آفریننده بشری از گِل خشک از گِلی تیره‌رنگ ریخته‌شده‌'),
(1831, 15, 29, 'پس گاهی که آراستمش و دمیدم در او از روح خویش پس بیفتید برایش سجده‌کنان‌'),
(1832, 15, 30, 'پس سجده کردند فرشتگان همگی با هم‌'),
(1833, 15, 31, 'جز ابلیس که سرپیچید از آنکه باشد با سجده‌کنندگان‌'),
(1834, 15, 32, 'گفت ای ابلیس چه شُدت که نبودی با سجده‌کنندگان‌'),
(1835, 15, 33, 'گفت نیستم من که سجده کنم برای بشری که آفریدی او را از گِلی خشک از گل تیره ریخته‌شده‌'),
(1836, 15, 34, 'گفت پس برون شو از آن که توئی رانده شده‌'),
(1837, 15, 35, 'و همانا بر تو است لعنت من تا روز دین‌'),
(1838, 15, 36, 'گفت پروردگارا مهلتم ده تا روزی که برانگیخته شوند'),
(1839, 15, 37, 'گفت همانا توئی از مهلت‌دادگان‌'),
(1840, 15, 38, 'تا روز هنگام دانسته‌'),
(1841, 15, 39, 'گفت پروردگارا بدانکه گمراهم کردی همانا آرایش دهم برای ایشان در زمین و هر آینه گمراهشان کنم همگی‌'),
(1842, 15, 40, 'جز بندگان تو از ایشان آن ناآلودگان (پاک‌شدگان)'),
(1843, 15, 41, 'گفت این است راهی بر من راست‌'),
(1844, 15, 42, 'که بندگانم را نباشدت بر ایشان فرمانروائیی مگر آنکه پیرویت کند از گمراهان‌'),
(1845, 15, 43, 'و همانا دوزخ است وعده‌گاه آنان همگی‌'),
(1846, 15, 44, 'برای آن است هفت در هر دری را است از ایشان بخشی جداگانه‌'),
(1847, 15, 45, 'همانا پرهیزکارانند در باغها و چشمه‌سارهائی‌'),
(1848, 15, 46, 'درآئیدش به سلامت بر آسودگان‌'),
(1849, 15, 47, 'و برافکندیم آنچه در سینه‌های ایشان است از کینه برادرانند بر تختهائی روی بروی‌'),
(1850, 15, 48, 'نزدیک بدیشان نشود رنجی و نباشند از آن برون‌راندگان‌'),
(1851, 15, 49, 'آگهی ده بندگانم را که منم آمرزنده مهربان‌'),
(1852, 15, 50, 'و آنکه عذاب من است آن عذاب دردناک‌'),
(1853, 15, 51, 'و آگهیشان ده از میهمانان ابراهیم‌'),
(1854, 15, 52, 'هنگامی که درآمدند بر او پس گفتند سلامی گفت همانا مائیم از شما هراسان‌'),
(1855, 15, 53, 'گفتند بیم مدار همانا نویدیت دهیم به فرزندی دانا'),
(1856, 15, 54, 'گفت آیا نوید دهیدم با آنکه مرا رسیده است پیری پس به چه نویدم دهید'),
(1857, 15, 55, 'گفتند بشارت آوردیمت به حقّ پس نباش از نومیدان‌'),
(1858, 15, 56, 'گفت کیست که نومید شود از رحمت پروردگار خویش مگر گمراهان‌'),
(1859, 15, 57, 'گفت پس چیست کار شما ای فرستادگان‌'),
(1860, 15, 58, 'گفتند همانا فرستاده شدیم بسوی قومی گنهکار'),
(1861, 15, 59, 'مگر خاندان لوط که نجات دهنده‌ایم ایشان را همگی‌'),
(1862, 15, 60, 'مگر زنش مقدّر کردیم که او است از گذشتگان‌'),
(1863, 15, 61, 'پس گاهی که آمدند خاندان لوط را فرستادگان‌'),
(1864, 15, 62, 'گفت همانا شمائید گروهی ناشناختگان‌'),
(1865, 15, 63, 'گفتند بلکه آوردیمت بدانچه بودند در آن شکّ می‌آوردند'),
(1866, 15, 64, 'و آوردیمت حقّ را و همانا مائیم راستگویان‌'),
(1867, 15, 65, 'پس رهسپار شو با خاندان خویش در پاره‌ای از شب و پیروی کن پشتهای ایشان را و برنگردد از شما کسی و بگذرید آنجا که فرمان داده شوید'),
(1868, 15, 66, 'و بگذراندیم بسویش این امر را که دنباله آنان است بریده به بامداد درآیندگان‌'),
(1869, 15, 67, 'و آمدند مردم شهر شادمانی‌کنان‌'),
(1870, 15, 68, 'گفت همانا اینان میهمانان منند پس رسوایم نکنید'),
(1871, 15, 69, 'و بترسید خدا را و خوارم نسازید'),
(1872, 15, 70, 'گفتند آیا نهی نکردیم تو را از جهانیان‌'),
(1873, 15, 71, 'گفت اینک دختران من اگر هستید کنندگان‌'),
(1874, 15, 72, 'به جان تو اینانند در مستی خویش فروروندگان‌'),
(1875, 15, 73, 'پس بگرفتشان خروش حالی که خورشید بود تابنده بر ایشان‌'),
(1876, 15, 74, 'پس گردانیدیم زیرش را رویش و باریدیم بر ایشان سنگی از سجّیل‌'),
(1877, 15, 75, 'همانا در این است آیتهائی برای هوشمندان‌'),
(1878, 15, 76, 'و همانا آن به راهی است استوار (پایدار)'),
(1879, 15, 77, 'همانا در این است آیتی برای مؤمنان‌'),
(1880, 15, 78, 'و هر چند بودند یاران اَیکه همانا ستمگران‌'),
(1881, 15, 79, 'پس کین کشیدیم از ایشان و همانا آنانند پیشوائی آشکار'),
(1882, 15, 80, 'و همانا تکذیب کردند یاران حَجر فرستادگان را'),
(1883, 15, 81, 'و آوردیمشان آیتهای خویش را پس بودند از آنها روی‌گردانان‌'),
(1884, 15, 82, 'و بودند می‌تراشیدند از کوه‌ها خانه‌هائی بر آسودگان‌'),
(1885, 15, 83, 'پس بگرفتشان خروش گاهی که بودند بامدادکنندگان‌'),
(1886, 15, 84, 'پس بی‌نیاز نکرد از ایشان چیزی آنچه بودند فراهم می‌آوردند'),
(1887, 15, 85, 'و نیافریدیم آسمانها و زمین را و آنچه میان آنها است جز به حقّ و همانا ساعت است آینده پس بگذر گذشتی نکو'),
(1888, 15, 86, 'همانا پروردگار تو است آفریننده دانا'),
(1889, 15, 87, 'و همانا آوردیم تو را هفت تا از بندها و قرآنی بزرگ‌'),
(1890, 15, 88, 'باز مکن (برمکش) دیدگان خویش را بسوی آنچه بهره دادیم مردانی از ایشان را و اندوهگین نباش بر آنان و بخوابان بال خویش را برای مؤمنان‌'),
(1891, 15, 89, 'و بگو منم همانا آن بیم‌دهنده آشکار'),
(1892, 15, 90, 'بدانسان که فرستادیم بر بخش‌کنندگان‌'),
(1893, 15, 91, 'آنان که گردانیدند قرآن را بخشهایی‌'),
(1894, 15, 92, 'پس سوگند به پروردگارت همانا بپرسیمشان همگی‌'),
(1895, 15, 93, 'از آنچه بودند می‌کردند'),
(1896, 15, 94, 'پس بانگ درده بدانچه مأمور شوی و روی برگردان از شرک‌ورزان‌'),
(1897, 15, 95, 'همانا ما نگهداشتیمت از استهزاءکنندگان‌'),
(1898, 15, 96, 'آنان که قرار دهند با خدا خداوند دیگری پس زود است بدانند'),
(1899, 15, 97, 'و همانا می‌دانیم تو را تنگ همی‌آید سینه بدانچه گویند'),
(1900, 15, 98, 'پس تسبیح کن به سپاس پروردگار خویش و باش از سجده‌کنندگان‌'),
(1901, 15, 99, 'و پرستش کن پروردگار خویش را تا بیایدت یقین‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'آمد امر خدا پس شتاب نجوئیدش منزّه و برتر است او از آنچه شرک می‌ورزند'),
(1903, 16, 2, 'فرود آورد فرشتگان را با روح از امر خود بر هر که خواهد از بندگان خویش که بترسانید آنکه نیست خدائی جز من پس پرهیزکاری کنید'),
(1904, 16, 3, 'بیافریده است آسمانها و زمین را به حقّ برتر است از آنچه شرک ورزند'),
(1905, 16, 4, 'بیافرید انسان را از چکّه آبی که ناگهان او است دشمنی آشکار'),
(1906, 16, 5, 'و دامها را بیافرید برای شما در آنها است پوشاکی گرم و سودهائی و از آنها می‌خورید'),
(1907, 16, 6, 'و شما را است در آنها زیبی گاهی که شب کنید و گاهی که سردهید'),
(1908, 16, 7, 'و می‌کشند بارهای شما را بسوی شهری که نرسید بدان جز با رنج کسان همانا پروردگار شما است نوازنده مهربان‌'),
(1909, 16, 8, 'و اسبان و استرها و خرها تا سوار آنها شوید و زیبی را و می‌آفریند آنچه را نمی‌دانید'),
(1910, 16, 9, 'و بر خداست رهنمائی به راه راست و از آن است کج‌رونده و اگر می‌خواست هدایت می‌کرد شما را همگی‌'),
(1911, 16, 10, 'او است آنکه فرستاد از آسمان آبی برای شما از آن است نوشابه و از آن است درختی که در آن می‌چرانید'),
(1912, 16, 11, 'می‌رویاند برای شما بدان کشت و زیتون و خرمابنها و انگورها را و از همه میوه‌ها همانا در این است آیتی برای گروهی که بیاندیشند'),
(1913, 16, 12, 'و رام کرد برای شما شب و روز و مِهر و ماه را و ستارگان فرمانبردارند به فرمانش همانا در این است آیتهائی برای گروهی که بخرد یابند'),
(1914, 16, 13, 'و آنچه آفرید برای شما در زمین رنگارنگ همانا در این است آیتی برای گروهی که یادآور شوند'),
(1915, 16, 14, 'و او است آنکه رام کرد دریا را تا بخورید از آن گوشتی تازه و برون آرید از آن زیوری که بپوشیدش و بینی کشتیها را در آن شکافندگان آب و تا بجوئید از فضلش و شاید شما سپاس گزارید'),
(1916, 16, 15, 'و افکند در زمین لنگرهائی نبادا کج شود به شما و رودهائی و راه‌هائی شاید هدایت یابید'),
(1917, 16, 16, 'و نشانهائی و با ستاره ایشان هدایت شوند'),
(1918, 16, 17, 'پس آیا آنکه می‌آفرد مانند آن است که نمی‌آفرد پس آیا یادآور نمی‌شوید'),
(1919, 16, 18, 'و اگر بشمرید نعمت خدای را شمار آن نتوانید همانا خداوند است آمرزنده مهربان‌'),
(1920, 16, 19, 'و خدا می‌داند آنچه نهان کنید و آنچه آشکار کنید'),
(1921, 16, 20, 'و آنان را که می‌خوانند جز خدا نیافرند چیزی را و خود آفریده شوند'),
(1922, 16, 21, 'مُردگانند نه زندگان و درنیابند که چه وقت برانگیخته می‌شوند'),
(1923, 16, 22, 'خداوند شما خدای یکتا است پس آنان که ایمان ندارند به آخرت دلهای ایشان است انکارکننده و ایشانند کبرورزندگان‌'),
(1924, 16, 23, 'لاجرم خدا می‌داند آنچه را نهان کنند و آنچه را آشکار کنند همانا او دوست ندارد گردنکشان را'),
(1925, 16, 24, 'و هرگاه گفته شود به ایشان چه فرستاده است پروردگار شما گویند داستانهای باستان‌'),
(1926, 16, 25, 'تا بردارند بارهای خویش را تمامی روز قیامت و از گناهان آنان که گمراهشان کنند به نادانی چه زشت است آنچه گنهبار می‌شوند'),
(1927, 16, 26, 'همانا مکر کردند آنان که پیش از ایشان بودند پس واژگون ساخت خدا بنیاد ایشان را از پایه‌ها پس فرود آمد بر ایشان بام از فراز ایشان و بیامدشان عذاب از آنجا که ندانستند'),
(1928, 16, 27, 'سپس روز قیامت خوارشان سازد و گوید کجایند شریکان من آنان که بودید ستیزه می‌کردید در ایشان گویند آنان که داده شدند دانش را همانا خواری در امروز و بدی است بر کافران‌'),
(1929, 16, 28, 'آنان که دریابندشان فرشتگان ستمگران بر خویشتن پس بیفکندند آشتی را که ما نکردیم بدیی نه چنین است بلکه خدا دانا است بدانچه بودید می‌کردید'),
(1930, 16, 29, 'پس درآئید درهای دوزخ را جاودانان در آن پس چه زشت است جایگاه گردنکشان‌'),
(1931, 16, 30, 'و گفته شد بدانان که پرهیزکاری کردند چه فرستاده است پروردگار شما گفتند نکو را آنان را که نکوئی کردند در این دنیا است نکوئی و همانا خانه آخرت است بهتر و چه خوب است سرای پرهیزکاران‌'),
(1932, 16, 31, 'بهشتهای جاودان درآیند در آنها روان است زیر آنها جویها ایشان را است در آنجا آنچه خواهند بدینسان پاداش دهد خدا به پرهیزکاران‌'),
(1933, 16, 32, 'آنان که یابندشان فرشتگان پاک گویند سلام بر شما درآئید بهشت را بدانچه بودید می‌کردید'),
(1934, 16, 33, 'آیا چشم به راهند جز آنکه بیایدشان فرشتگان یا بیاید امر پروردگار تو چنین کردند آنان که پیش از ایشان بودند و ستم نکرد بر ایشان خدا و لیکن بودند خویشتن را ستم می‌کردند'),
(1935, 16, 34, 'پس رسید بدیشان بدیهای آنچه کردند و فرود آمد بدیشان آنچه بودند بدان استهزاء می‌کردند'),
(1936, 16, 35, 'و گفتند آنان که شرک ورزیدند اگر می‌خواست خدا همانا نمی‌پرستیدیم جز از وی چیزی را نه ما و نه پدران ما و نه حرام (محترم) می‌شمردیم جز او چیزی را چنین کردند آنان که پیش از ایشان بودند پس آیا هست بر فرستادگان جز پیام آشکار'),
(1937, 16, 36, 'و همانا برانگیختیم در هر ملتی پیمبری را که پرستش کنید خدا را و دوری گزینید از سرکش پس از ایشان است آنکه رهبریش کرد خدا و از ایشان است آنکه فرود آمد بر او گمراهی پس بگردید در زمین و بنگرید چگونه بود فرجام تکذیب‌کنندگان‌'),
(1938, 16, 37, 'اگر حریص باشی بر هدایت ایشان همانا خدا هدایت نکند آن را که گمراه می‌کند و نیست ایشان را یاورانی‌'),
(1939, 16, 38, 'و سوگند آوردند به خدا سخت‌ترین سوگندهای خویش که نیانگیزد خدا آن را که می‌میرد نه چنین است بلکه وعده‌ای است بر او حق و لیکن بیشتر مردم نمی‌دانند'),
(1940, 16, 39, 'تا بیان کند برای ایشان آنچه را که اختلاف می‌کنند در آن و تا بدانند آنان که کفر ورزیدند که بودند ایشان دروغگویان‌'),
(1941, 16, 40, 'نیست گفتار ما برای چیزی گاهی که بخواهیمش جز آنکه گوئیمش بشو پس می‌شود'),
(1942, 16, 41, 'و آنان که هجرت کردند در راه خدا پس از آنکه ستم شدند البته جایشان دهیم در دنیا جایگاهی نکو و هر آینه پاداش آخرت است بزرگتر اگر باشند که بدانند'),
(1943, 16, 42, 'آنان که شکیبا شدند و بر پروردگار خویش توکّل کنند'),
(1944, 16, 43, 'و نفرستادیم پیش از تو مگر مردانی را که وحی می‌فرستادیم بدیشان پس بپرسید اهل کتاب را اگر شما نمی‌دانید'),
(1945, 16, 44, 'با نشانیها و نامه‌ها و فرستادیم بسوی تو نامه را تا بیان کنی برای مردم آنچه را فرستاده شده است بسوی ایشان و شاید بیاندیشند'),
(1946, 16, 45, 'آیا ایمن شدند آنان که مکر کردند بدیها را که فروبردشان زمین یا بیایدشان عذاب از جائی که نمی‌دانند'),
(1947, 16, 46, 'یا بگیردشان در گردش (رفت و شد) ایشان پس نباشند به عجزآورندگان‌'),
(1948, 16, 47, 'یا بگیردشان بر بیمناکی (یا آهسته و اندک‌اندک) همانا پروردگار تو است نوازنده مهربان‌'),
(1949, 16, 48, 'آیا ننگریستند بدانچه بیافرید خدا از چیزی که سایه‌افکن گردد سایه‌های آن از راست و چپ سجده‌کنان برای خدا حالی که آنانند فروتنان‌'),
(1950, 16, 49, 'و برای خدا سجده کنند آنچه در آسمانها و آنچه در زمین است از جنبنده فرشتگان و ایشان سرنپیچند'),
(1951, 16, 50, 'بترسند پروردگار خویش را از فراز خویش و کنند آنچه فرمان داده شوند'),
(1952, 16, 51, 'و گفت خدا نگیرید خدایانی دوگانه جز این نیست که او است خداوند یگانه پس مرا بترسید'),
(1953, 16, 52, 'و برای او است آنچه در آسمانها و زمین است و برای او است دین پیوسته پس آیا جز خدای را می‌پرهیزید'),
(1954, 16, 53, 'و آنچه بر شما است از نعمت از خدا است سپس گاهی که می‌رسد شما را رنج پس بسوی او زاری کنید'),
(1955, 16, 54, 'تا گاهی که بگشاید از شما رنج را ناگاه گروهی از شما به پروردگار خویش شرک ورزند'),
(1956, 16, 55, 'تا ناسپاسی کنند بدانچه آوردیمشان پس کامیاب شوید که زود است بدانید'),
(1957, 16, 56, 'و قرار دهند برای آنچه نمی‌دانند بهره‌ای از آنچه روزیشان دادیم به خدا پرسش شوید البته از آنچه بودید دروغ می‌بستید'),
(1958, 16, 57, 'و قرار دهند برای خدا دختران منزّه است و برای خویش آنچه هوس کنند'),
(1959, 16, 58, 'و گاهی که مژده داده شود یکیشان به مادینه بگردد روی او سیاه حالی که او است خشم‌خورنده‌'),
(1960, 16, 59, 'نهان کند خویش را از قوم از زشتی آنچه مژده داده شده است بدان که آیا نگهداردش بر زبونی (خواری) یا فروکندش در خاک همانا زشت است آنچه حکم کنند'),
(1961, 16, 60, 'آنان را که ایمان نمی‌آورند به آخرت مثَل زشت است و برای خدا است مثَل برتر و او است عزّتمند حکیم‌'),
(1962, 16, 61, 'و اگر بگیرد خدا مردم را به ستمگریشان نگذارد بر آن جنبنده‌ای و لیکن پس می‌اندازدشان تا سرآمدی نامبرده و گاهی که بیاید سرآمد ایشان نه پس افتند ساعتی و نه پیش افتند'),
(1963, 16, 62, 'و قرار دهند برای خدا آنچه را ناخوش دارند و می‌ستاید زبانهای ایشان دروغ را که ایشان را است نیکی لاجرم ایشان را است آتش و ایشانند پیش راندگان‌'),
(1964, 16, 63, 'به خدا سوگند که فرستادیم بسوی مللی پیش از تو پس بیاراست برای ایشان شیطان کردار ایشان را پس او است دوست ایشان آن روز و برای ایشان است عذابی دردناک‌'),
(1965, 16, 64, 'و نفرستادیم بر تو کتاب را مگر تا بیان کنی برای ایشان آنچه را اختلاف کردند در آن و هدایت و رحمتی برای گروهی که ایمان آرند'),
(1966, 16, 65, 'خدا فرستاد از آسمان آبی پس زنده ساخت بدان زمین را پس از مردنش همانا در این است آیتی برای قومی که می‌شنوند'),
(1967, 16, 66, 'و همانا شما را هست در دامها عبرتی بنوشانیمتان از آنچه در شکمهای آنها است از میان سرگین و خون شیری نیالوده گوارا برای نوشندگان‌'),
(1968, 16, 67, 'و از میوه‌های خرمابنها و تاکها برمی گیرید از آن سکری و روزیی نکو همانا در این است آیتی برای قومی که بخرد یابند'),
(1969, 16, 68, 'و وحی نمود پروردگار تو بسوی زنبور (مگس عسل) که برگیر از کوه‌ها خانه‌هائی و از درخت و از آنچه برافرازند'),
(1970, 16, 69, 'سپس بخور از همه میوه‌ها پس روان شو به راه‌های پروردگار خویش هموار برون شود از شکمهای آنها نوشابه‌ای به رنگهای گوناگون در آن است درمانی برای مردم همانا در این است آیتی برای قومی که بیندیشند'),
(1971, 16, 70, 'و خدا بیافریدتان و سپس بمیراندتان و از شما است آنکه گردانیده شود بسوی پسترین عمر تا نداند پس از دانست چیزی را همانا خدا است دانای توانا'),
(1972, 16, 71, 'و خدا برتری داد گروهی از شما را بر گروهی در روزی پس نیستند آنان که برتر شدند برگرداننده روزی خویش بر آنچه دارا است یمینهای ایشان (بندگانشان) که باشند ایشان در آن یکسان پس آیا نعمت خدا را نادیده گیرند'),
(1973, 16, 72, 'و خدا قرار داد برای شما از خود شما جفتهائی و قرار داد برای شما از جفتهای شما پسرانی و نبیرگانی و روزیتان داد از پاکیزه‌ها پس آیا به باطل ایمان آرند و به نعمت خدا کفر ورزند'),
(1974, 16, 73, 'و می‌پرستند جز خدا آنچه را دارا نیست برای ایشان روزیی از آسمانها و زمین چیزی را و نمی‌توانند'),
(1975, 16, 74, 'پس نزنید برای خدا مثَلها را و همانا خدا می‌داند و شما نمی‌دانید'),
(1976, 16, 75, 'زده است خدا مثَلی بنده‌ای مملوک که توانائی ندارد بر چیزی و آنکه روزیش دادیم از خود روزیی نکو پس او انفاق می‌کند از آن نهان و آشکارا آیا یکسانند سپاس خدای را بلکه بیشترشان نمی‌دانند'),
(1977, 16, 76, 'و زده است خدا مثَلی دو مرد که یکیشان گنگ است توانائی ندارد بر چیزی و او است باری بر مهتر خویش به هر سویش گرداند نیارد به خیری آیا یکسان است او با آنکه امر کند به داد و او است بر راهی راست‌'),
(1978, 16, 77, 'و از آن خدا است ناپیدای آسمانها و زمین و نیست امر ساعت جز مانند بهم زدن چشم یا نزدیکتر همانا خدا است بر همه چیز توانا'),
(1979, 16, 78, 'و خدا برون آورد شما را از شکمهای مادرهای خویش نمی‌دانستید چیزی را و نهاد برای شما گوش و دیدگان و دلها را شاید سپاس گزارید'),
(1980, 16, 79, 'آیا ننگریستند بسوی پرندگان که فرمانبردارانند در فضای (پهنه) آسمان نگه نداردشان جز خدا همانا در این است آیتهائی برای قومی که ایمان آرند'),
(1981, 16, 80, 'و خدا قرار داد برای شما از خانه‌های شما آرامشگاهی و قرار داد برای شما از پوست دامها خانه هائی که آسان برگیرید آنها را روز کوچیدن شما و روز ماندن شما و از پشمهای آنها و از کرکهای آنها و مویهای آنها ابزار و کالائی (بهره‌ای) تا هنگامی‌'),
(1982, 16, 81, 'و خدا قرار داد برای شما از آنچه بیافرید سایه‌هائی و قرار داد برای شما از کوه‌ها پناهگاه‌هائی و قرار داد برای شما جامه‌هائی که نگهدارد شما را از گرما و جامه‌هائی که نگاهتان دارد از خشم خویش بدینسان انجام رساند نعمت خود را بر شما شاید اسلام آورید'),
(1983, 16, 82, 'و اگر پشت کردند پس جز این نیست که بر تو است رسانیدن آشکار'),
(1984, 16, 83, 'می‌شناسند نعمت خدا را پس انکارش می‌کنند و بیشتر ایشانند کافران‌'),
(1985, 16, 84, 'و روزی که برانگیزیم از هر ملتی گواهی پس اذن داده نشود به کافران و نه عتاب از ایشان خواسته شود'),
(1986, 16, 85, 'و هنگامی که بینند ستمگران عذاب را پس نه از ایشان کاسته شود و نه مهلت داده شوند'),
(1987, 16, 86, 'و هنگامی که ببینند مشرکان شریکان خود را گویند پروردگارا اینانند شریکان ما که بودیم می‌خواندیم جز تو پس افکندند بسوی ایشان سخن را که شمائید همانا دروغگویان‌'),
(1988, 16, 87, 'و افکندند بسوی خدا در آن روز آشتی را و گم شد از ایشان آنچه بودند دروغ می‌بستند'),
(1989, 16, 88, 'آنان که کفر ورزیدند و بازداشتند از راه خدا بیفزودیمشان عذابی بر روی عذاب بدانچه بودند فساد می‌کردند'),
(1990, 16, 89, 'و روزی که برانگیزانیم از هر ملّتی گواهی را بر ایشان از خود ایشان و بیاریم تو را بر اینان گواه و فرستادیم بر تو کتاب را بیانی برای همه چیز و هدایت و رحمت و بشارتی برای مسلمین‌'),
(1991, 16, 90, 'همانا خدا امر می‌کند به داد و نیکی و دادن خویشاوندان و نهی می‌کند از فحشاء و ناشایست و ستم اندرز دهد شما را شاید یادآور شوید'),
(1992, 16, 91, 'و وفا کنید به عهد خدا گاهی که عهد بندید و نشکنید سوگندها را بعد از استوار کردن آنها حالی که قرار دادید خدا را بر خویش نگهبان همانا خدا می‌داند آنچه را می‌کنید'),
(1993, 16, 92, 'و نباشید مانند آن زن که واتابید رشته خود را پس از استوارشدن تارهائی گسیخته که برگیرید سوگندهای خویش را نیرنگی میان خویش تا بشود گروهی فزوده‌تر از گروهی جز این نیست که بیازماید شما را خدا بدان و بیان می‌کند برای شما روز قیامت آنچه را بودید در آن اختلاف می‌کردید'),
(1994, 16, 93, 'و اگر می‌خواست خدا هر آینه می‌گردانید شما را یک ملت و لیکن گمراه کند هر که را خواهد و رهبری کند هرکه را خواهد و هر آینه پرسیده شوید البته از آنچه بودید می‌کردید'),
(1995, 16, 94, 'و نگیرید سوگندهای خود را نیرنگی میان خود تا بلغزد قدمی پس از استواریش و بچشید زشتی را بدانچه بازداشتید از راه خدا و برای شما است عذابی بزرگ‌'),
(1996, 16, 95, 'و نفروشید پیمان خدا را به بهائی کم همانا آنچه نزد خدا است بهتر است برای شما اگر باشید که بدانید'),
(1997, 16, 96, 'آنچه نزد شما است پایان یابد و آنچه نزد خدا است پاینده است و هر آینه پاداش دهیم آنان را که صبر ورزیدند مزدشان را به نکوترین چیزی که بودند می‌کردند'),
(1998, 16, 97, 'آنکو کردار شایسته کند از نر یا ماده حالی که مؤمن باشد هر آینه زنده داریمش زندگیی پاکیزه و همانا پاداششان دهیم مزدشان را به بهتر چیزی که بودند می‌کردند'),
(1999, 16, 98, 'و هنگامی که می‌خوانی قرآن را پناه بر به خدا از شیطان رانده‌شده‌'),
(2000, 16, 99, 'همانا نیست او را فرمانروائی بر آنان که ایمان آورند و بر پروردگار خویش توکّل کنند'),
(2001, 16, 100, 'جز این نیست که فرمانروائی او بر آنان است که دوست دارندش و آنان که بدو شرک می‌ورزند'),
(2002, 16, 101, 'و هرگاه تبدیل کنیم آیتی را به جای آیتی و خدا داناتر است بدانچه می‌فرستد گویند جز این نیست که توئی دروغ‌آورنده بلکه بیشتر ایشان نمی‌دانند'),
(2003, 16, 102, 'بگو فرود آورد آن را روح‌القدس (روان پاک) از پروردگار تو به حقّ تا استوار گرداند آنان را که ایمان آوردند و هدایت و بشارتی برای مسلمانان‌'),
(2004, 16, 103, 'و همانا دانیم که ایشان گویند جز این نیست که می‌آموزدش بشری زبان آنکه بدو این سخن را بندند عجمی (نارسا) است و این زبانی است عربی (رسا) آشکار'),
(2005, 16, 104, 'همانا آنان که ایمان نیارند به آیتهای خدا رهبریشان نکند خدا و برای ایشان است عذابی دردناک‌'),
(2006, 16, 105, 'جز این نیست که دروغ را آنان بندند که ایمان نیارند به آیتهای خدا و ایشانند دروغگویان‌'),
(2007, 16, 106, 'آنکه کفر ورزد به خدا پس از ایمانش مگر آنکه وادار شود بناخواه حالی که دلش آرمیده است به ایمان و لیکن آنکو گشاده باشد به کفر سینه او پس بر ایشان است خشمی از خدا و ایشان را است عذابی بزرگ‌'),
(2008, 16, 107, 'این بدان است که ایشان برگزیدند زندگانی دنیا را بر آخرت و آنکه خدا هدایت نکند گروه کافران را'),
(2009, 16, 108, 'آنانند که مُهر نهاد خدا بر دلهاشان و گوششان و دیدگانشان و آنانند ناآگهان‌'),
(2010, 16, 109, 'ناگزیر ایشانند در آخرت زیانکاران‌'),
(2011, 16, 110, 'همانا پروردگار تو برای آنان که هجرت کردند (آواره شدند) پس از آنکه آزرده شدند سپس جهاد کردند و شکیبا شدند همانا پروردگار تو است پس از آن آمرزنده مهربان‌'),
(2012, 16, 111, 'روزی که آید هر کس بستیزد از جانب خویشتن و داده شود به هر که آنچه کرده است و ستم نشوند'),
(2013, 16, 112, 'و برزده است خدا مثَلی شهری که بود آسوده و آرام می‌آمدش روزیش فراوان از هر سوی پس کفر ورزید به نعمتهای خدا پس چشانیدش خداوند جامه گرسنگی و ترس را بدانچه بودند می‌کردند'),
(2014, 16, 113, 'و هر آینه بیامدشان پیمبری از ایشان پس تکذیبش کردند پس گرفت ایشان را عذاب حالی که بودند ستمگران‌'),
(2015, 16, 114, 'پس بخورید از آنچه روزی داد شما را خدا حلال پاکیزه و سپاس گزارید نعمت خدا را اگر هستید وی را پرستندگان‌'),
(2016, 16, 115, 'جز این نیست که حرام کرد بر شما مردار و خون و گوشت خوک و آنچه را نام جز خدا به آن برده شود پس آنکه ناچار گردد نه ستمگر و نه تجاوزکننده همانا خدا است آمرزنده مهربان‌'),
(2017, 16, 116, 'و نگوئید بدانچه می‌ستاید زبانهای شما به دروغ این است حلال و این است حرام تا ببندید بر خدا دروغ را همانا آنان که بندند بر خدا دروغ را رستگار نشوند'),
(2018, 16, 117, 'کامیابی است اندک و ایشان را است عذابی دردناک‌'),
(2019, 16, 118, 'و بر آنان که جهود شدند حرام کردیم آنچه را فروخواندیم بر تو از پیش و ستم نکردیم بر ایشان لیکن بودند خویش را ستم می‌کردند'),
(2020, 16, 119, 'سپس پروردگار تو برای آنان که انجام دادند بدی را به نادانی پس توبه کردند پس از آن و شایستگی گزیدند همانا پروردگار تو است از پس آن آمرزنده مهربان‌'),
(2021, 16, 120, 'همانا ابراهیم بوده است پیشوائی فروتن برای خدا یکتاپرست و نبوده است از شرک‌ورزندگان‌'),
(2022, 16, 121, 'سپاسگزارنده نعمتهای او برگزیدش و رهبریش کرد بسوی راهی راست‌'),
(2023, 16, 122, 'و دادیمش در دنیا نیکی و همانا او است در آخرت از شایستگان‌'),
(2024, 16, 123, 'سپس وحی کردیم بسوی تو که پیروی کن آئین ابراهیم یکتاپرست را و نبود از شرک‌ورزان‌'),
(2025, 16, 124, 'جز این نیست که نهاده شد شنبه بر آنان که اختلاف کردند در آن و همانا پروردگار تو حکم کند میان ایشان روز قیامت در آنچه بودند در آن اختلاف می‌کردند'),
(2026, 16, 125, 'بخوان بسوی راه پروردگار خویش با حکمت و پند نیکو و درستیز با ایشان بدانچه آن است نکوتر همانا پروردگار تو داناتر است بدانکه گم شده است از راه او و او است داناتر به هدایت‌شدگان‌'),
(2027, 16, 126, 'و اگر شکنجه کنید پس شکنجه کنید همانند آنچه شکنجه شدید و اگر بردباری گزینید همانا آن بهتر است برای بردباران‌'),
(2028, 16, 127, 'و صبرکن و نیست صبرت جز به خدا و اندوهگین نباش بر ایشان و نباش در تنگنائی از آنچه نیرنگ کنند'),
(2029, 16, 128, 'همانا خدا با آنانی است که پرهیز می‌کنند و آنان که ایشانند نکوکاران‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'منزه است خداوندی که شبانه برد بنده خویش را از مسجد حرام بسوی مسجد اقصی آنی که برکت نهادیم پیرامونش تا بنمایانیمش از آیتهای ما همانا او است شنوای بینا'),
(2031, 17, 2, 'و دادیم به موسی کتاب را و گردانیدیمش هدایتی برای بنی اسرائیل که نگیرید جز من سرپرستی‌'),
(2032, 17, 3, 'نژاد آنان که سوار کردیم با نوح همانا بود او بنده سپاسگزار'),
(2033, 17, 4, 'و بگذراندیم بسوی بنی اسرائیل در کتاب که البتّه فساد می‌کنید در زمین دوبار و برتری می‌جوئید برترینی بزرگ‌'),
(2034, 17, 5, 'تا گاهی که آید وعده نخستین آنها را برانگیزیم بر شما بندگانی ما را دارندگان نیروئی سخت پس بگردند میان شهرها و بوده است وعده شدنی‌'),
(2035, 17, 6, 'پس باز آوردیم برای شما تاختن را بر ایشان و کمک دادیم شما را به مالهائی و پسرانی و گردانیدیم شما را فزونتر در گروه‌'),
(2036, 17, 7, 'اگر نیکی کنید برای خویش نیکی کرده‌اید و اگر بدی کنید پس برای آن است و هنگامی که بیاید وعده بار دوم تا ناخوش دارند وجوه شما را و درآیند مسجد را چنانکه درآمدندش نخستین بار و نابود کنند آنچه را برتر آمده‌اند نابودکردنی‌'),
(2037, 17, 8, 'شاید پروردگار شما رحم کند بر شما و اگر بازگردید بازگردیم و گردانیدیم دوزخ را برای کافران تنگنائی (زندانی)'),
(2038, 17, 9, 'همانا این قرآن رهبری می‌کند بدانچه آن استوارتر است و مژده می‌دهد مؤمنانی را که کردار شایسته کنند که ایشان را است پاداشی بزرگ‌'),
(2039, 17, 10, 'و آنکه آنان که ایمان نمی‌آورند به آخرت آماده کردیم برای ایشان عذابی دردناک‌'),
(2040, 17, 11, 'و می‌خواند انسان به بدی همانند خواندنش به خوبی و بوده است انسان شتاب کننده‌'),
(2041, 17, 12, 'و گردانیدیم شب و روز را دو آیت پس برانداختیم آیت شب را و گردانیدیم آیت روز را روشن تا بجوئید فضلی را از پروردگار خویش و تا بدانید شمار سالها و حساب را و هرچیز را تفصیل دادیم تفصیلی‌'),
(2042, 17, 13, 'و هر انسانی را افکندیم نامه او را به گردنش و برون آریم برایش روز قیامت نامه‌ای که بیابدش گشوده‌'),
(2043, 17, 14, 'بخوان نامه خویش را تو خود بس باشی امروز بر خویشتن حسابگر'),
(2044, 17, 15, 'هر کس هدایت شود جز این نیست که هدایت شود برای خویشتن و هر کس گمراه شود جز این نیست که گمراه شود بر خویشتن و برندارد هیچ گنهباری بار گناه دیگری را و نیستیم ما عذاب‌کننده تا برانگیزیم پیمبری را'),
(2045, 17, 16, 'و هرگاه خواهیم نابود کنیم شهری را بفرمائیم کامرانان (هوسرانان) آن را تا نافرمانی کنند در آن پس فرود آید بر آن سخن پس نابود کنیمش نابودکردنی‌'),
(2046, 17, 17, 'و بسا هلاک کردیم از قرنها پس از نوح و بس است پروردگار تو به گناهان بندگان خود آگاهی بینا'),
(2047, 17, 18, 'آنکو زندگانی شتابان را خواهد بشتابیم برایش در آن آنچه را خواهیم برای هر که خواهیم سپس گذاریم برایش دوزخ را بچشدش نکوهیده رانده‌'),
(2048, 17, 19, 'و آنکو خواهد آخرت را و بکوشد برای آن کوشش آن را حالی که او است مؤمن پس آنان بوده است کوشش ایشان سپاس‌گزارده‌'),
(2049, 17, 20, 'هر یک را کمک دهیم اینان و آنان را از بخشش پروردگار تو و نیست بخشش پروردگار تو بازداشته‌'),
(2050, 17, 21, 'بنگر چگونه برتری دادیم برخی از ایشان را بر برخی و همانا آخرت بزرگتر است در پایه‌ها و بزرگتر است در برتری‌دادن‌'),
(2051, 17, 22, 'و قرار مده با خدا خدائی دیگر را که بنشینی نکوهیده خوارشده‌'),
(2052, 17, 23, 'و حکم کرد پروردگار تو که پرستش نکنید جز او را و به پدر و مادر نیکی کنید اگر برسند نزدت پیری را یکی از آنان یا هر دو پس نگو به ایشان اُف و نهیب نزن بر ایشان و بگو بدیشان گفتاری گرامی‌'),
(2053, 17, 24, 'و فروخوابان برای آنان بال فروتنی را از مِهر و بگو پروردگارا رحم کن بر آنان چنانکه مرا پروریدند در کودکی‌'),
(2054, 17, 25, 'پروردگار شما داناتر است بدانچه در دلهای شما است اگر باشید شایستگان همانا او است برای بازگشت‌کنندگان آمرزنده‌'),
(2055, 17, 26, 'و بده به خویشاوند حقّش را و بینوا و درمانده راه و تبذیر مکن تبذیری (بیهوده و فزون از اندازه دادن)'),
(2056, 17, 27, 'که فزون‌دهندگانند برادران شیاطین و شیطان است پروردگار خویش را ناسپاس‌'),
(2057, 17, 28, 'و اگر روی گردانی از ایشان در پی رحمتی از پروردگار خویش که امیدش داری پس بگو بدیشان گفتاری نرم و آرام‌'),
(2058, 17, 29, 'و نگردان دستت را بسته به گردنت و بازش نکن تمام باز تا بنشینی نکوهیده اندوهگین‌'),
(2059, 17, 30, 'همانا پروردگار تو فراخ گرداند روزی را برای هر که خواهد و تنگ گرداند همانا او است به بندگان خویش آگاهی بینا'),
(2060, 17, 31, 'و نکشید فرزندان خویش را از تنگدستی ما روزی دهیم ایشان را و شما را همانا کشتار آنان است لغزشی بزرگ‌'),
(2061, 17, 32, 'و نزدیک نشوید زنا را که بوده است آن فحشائی (ناشایستی) و چه زشت است آن راه‌'),
(2062, 17, 33, 'و نکشید تنی را که حرام کرد (محترم داشت) خدا جز به حقّ و آنکه کشته شود به ستم همانا قرار دادیم برای خونخواه او فرمانروائی پس فزون نرود در کشتار همانا او است یاری‌شده‌'),
(2063, 17, 34, 'و نزدیک نشوید مال یتیم را جز بدانچه آن است نکوتر تا برسد نیروی خویش را و وفا کنید به پیمان که پیمان است همانا پرسیده‌شده‌'),
(2064, 17, 35, 'و تمام دهید پیمانه را گاهی که بپیمائید و بسنجید با ترازوی درست و راست این بهتر است و نکوتر در سرانجام‌'),
(2065, 17, 36, 'و پیروی نکن آنچه را نیستت بدان دانشی همانا گوش و دیده و دل هر کدام آنها است از آن پرسیده‌'),
(2066, 17, 37, 'و راه مرو در زمین خرامان همانا تو نشکافی زمین را هرگز و نرسی کوه‌ها را به بلندی‌'),
(2067, 17, 38, 'همه اینها است زشت آن نزد پروردگار تو ناپسند'),
(2068, 17, 39, 'این است از آنچه که وحی فرستاد بسوی تو پروردگار تو از حکمت و قرار نده با خدا خدائی دیگر را تا افکنده شوی در دوزخ نکوهیده رانده‌'),
(2069, 17, 40, 'آیا برگزیده است شما را پروردگار شما به پسران و بگرفته است از فرشتگان مادگانی همانا شما می‌گوئید سخنی گران را'),
(2070, 17, 41, 'و همانا گردانیدیم در این قرآن تا یادآور شوند و نمیفزایدشان جز رمیدن‌'),
(2071, 17, 42, 'بگو اگر می‌بود با او خدایانی چنانکه گوئید در آن هنگام می‌جستند بسوی خداوند عرش راهی را'),
(2072, 17, 43, 'منزّه است او و برتر از آنچه گویند برتریی بزرگ‌'),
(2073, 17, 44, 'تسبیح کنند برای او آسمانهای هفتگانه و زمین و آنان که در آنها است و نیست چیزی جز آنکه تسبیح گوید به سپاس او و لیکن درنمی‌یابید ستایش ایشان را همانا او است بردبار آمرزنده‌'),
(2074, 17, 45, 'و هرگاه بخوانی قرآن را بر نهیم میان تو و میان آنان که ایمان نمی‌آورند به آخرت پرده‌ای پوشیده‌'),
(2075, 17, 46, 'و نهیم بر دلهای آنان پرده‌هائی از آنکه دریابندش و در گوشهای ایشان سنگینیی و هرگاه یاد کنی پروردگار خویش را در قرآن تنها بازگردند بر پشتهای خویش رمندگان‌'),
(2076, 17, 47, 'ما داناتریم بدانچه گوش می‌دهند بدان هنگامی که گوش می‌دهند به تو و هنگامی که سخن به گوش همدیگر می‌گویند هنگامی که گویند ستمگران پیروی نمی‌کنید جز مردی جادو شده را'),
(2077, 17, 48, 'بنگر چگونه زدند برایت مثَلها را پس گم شدند پس نمی‌توانند راهی را'),
(2078, 17, 49, 'و گفتند آیا گاهی که شدیم ما استخوانهائی و خاکی آیا ما برانگیختگانیم در آفرینشی نوین‌'),
(2079, 17, 50, 'بگو باشید سنگ یا آهن‌'),
(2080, 17, 51, 'یا آفرینشی از آنچه بزرگ آید در سینه‌های شما زود است گویند که ما را برمی‌گرداند بگو آنکه آفریدتان نخستین بار زود است نجنبانند سرهای خویش را به سویت و گویند چه هنگام است آن بگو امید است که باشد نزدیک‌'),
(2081, 17, 52, 'روزی که خوانندتان پس اجابت کنید با سپاس او و پندارید که نماندید جز اندکی‌'),
(2082, 17, 53, 'و بگو به بندگان من گویند آنچه آن است نکوتر همانا شیطان آشوب افکند میان آنان همانا شیطان است برای انسان دشمنی آشکار'),
(2083, 17, 54, 'پروردگار شما داناتر است به شما اگر خواهد رحمت آورد بر شما و اگر خواهد عذاب کند شما را و نفرستادیم تو را بر ایشان نگهبان‌'),
(2084, 17, 55, 'و پروردگارت داناتر است بدانکه در آسمانها است و زمین و همانا برتری دادیم بعض پیمبران را بر بعضی و دادیم به داود زبور را'),
(2085, 17, 56, 'بگو بخوانید آنان را که جز او پندارید دارا نیستند گشودن رنجی را از شما و نه برگرداندنی‌'),
(2086, 17, 57, 'آنانند که می‌خوانند جویند بسوی پروردگار خویش دستاویز را هر کدام از ایشان است نزدیکتر و امیدوارند رحمتش را و می‌ترسند عذابش را همانا عذاب پروردگار تو است ترسناک‌'),
(2087, 17, 58, 'و نیست شهری جز آنکه مائیم نابودکننده آن پیش از روز قیامت یا عذاب‌کننده آن عذابی سخت بوده است این در کتاب نوشته‌'),
(2088, 17, 59, 'و بازنداشت ما را از آنکه فرستیم آیتها را جز آنکه تکذیب کردند بدانها پیشینیان و دادیم به ثمود اشتر را بینا پس ستم کردند بدان و نمی‌فرستیم آیتها را جز ترساندن را'),
(2089, 17, 60, 'و هنگامی که گفتیم تو را که پروردگار تو فراگرفته است بر مردم و نگردانیدیم خوابی را که نمودیمت جز آزمایشی برای مردم و آن درخت لعنت شده را در قرآن و می‌ترسانیمشان پس نیفزایدشان مگر سرکشی بزرگ‌'),
(2090, 17, 61, 'و هنگامی که گفتیم به فرشتگان سجده کنید برای آدم پس سجده کردند جز ابلیس گفت آیا سجده کنم برای آنکه آفریدی گِل‌'),
(2091, 17, 62, 'گفت آیا دیده باشی این را که تو بر من برتری دادی اگر مرا بگذاری تا روز قیامت هر آینه مهار کنم (یا براندازم) فرزندان او را مگر کمی را'),
(2092, 17, 63, 'گفت برو که هر که پیرویت کند از ایشان همانا دوزخ است پاداش شما پاداشی تمام داده‌'),
(2093, 17, 64, 'و بلغزان هر که را توانستی از ایشان به آواز خود و تاخت آور بر ایشان با سواره و پیاده خویش و شرکت کن با ایشان در مالها و فرزندان و وعده بده ایشان را و وعده نمی‌دهد ایشان را شیطان مگر فریب‌'),
(2094, 17, 65, 'همانا بندگان من نیستت بر ایشان فرمانروائیی و بس است پروردگار تو کارگزاری‌'),
(2095, 17, 66, 'پروردگار تو است آنکه می‌راند برای شما کشتی را در دریا تا بجوئید از فضلش همانا او است به شما مهربان‌'),
(2096, 17, 67, 'و هرگاه برسد شما را رنجی در دریا گم است آنکه بخوانیدش جز او تا گاهی که رهائیتان داد بسوی دشت روی گردانیدید و بوده است انسان بسیار ناسپاس‌'),
(2097, 17, 68, 'آیا ایمن شدید که فروبرد شما را در یک سوی دشت یا بفرستد بر شما طوفان سنگریزه را پس نیابید برای خویش نگهبانی‌'),
(2098, 17, 69, 'یا ایمن شدید که بازگرداند شما را در آن بار دیگری پس بفرستد بر شما خردکننده‌ای (و شکننده‌ای) از باد تا غرق کند شما را بدانچه ناسپاسی کردید سپس نیابید برای خویش بر ما بدان خونخواه و نه یاوری را'),
(2099, 17, 70, 'و همانا گرامی داشتیم فرزندان آدم را و سوارشان کردیم در دشت و دریا و روزیشان دادیم از پاکیزه‌ها و برتریشان دادیم بر بسیاری از آنان که آفریدیم برتری‌دادنی‌'),
(2100, 17, 71, 'روزی که بخوانیم هر مردمی را به پیشوای ایشان پس آنکو داده شود کتابش را به دست راستش آنان بخوانند کتاب خود را و ستم نشوند به اندازه تار میان هسته خرما'),
(2101, 17, 72, 'و آنکه بوده است در این (دنیا) کور پس او است در آخرت کورتر و گمراه‌تر در راه‌'),
(2102, 17, 73, 'و هر آینه نزدیک بود فریبت دهند از آنچه وحی فرستادیم بسوی تو تا دروغ بندی بر ما جز آن را و در آن هنگام می‌گرفتندت دوست‌'),
(2103, 17, 74, 'و اگر نه استوار می‌داشتیمت همانا نزدیک بود که کج شوی بسوی ایشان چیزی اندک‌'),
(2104, 17, 75, 'در آن هنگام می‌چشانیدیمت دو برابر زندگی و دو برابر مردن را سپس نمی‌یافتی برای خویش بر ما یاوری را'),
(2105, 17, 76, 'و همانا نزدیک بود که بلغزانندت از زمین تا برونت کنند از آن و در آن هنگام درنگ نمی‌کردند پس از تو مگر کمی‌'),
(2106, 17, 77, 'شیوه آنان که فرستادیم پیش از تو از پیمبران خویش و نیابی برای شیوه ما تبدیلی‌'),
(2107, 17, 78, 'بپای دار نماز را از بازگشتگاه خورشید تا تاریکی شب و خواندنی (نماز) بامداد همانا خواندنی بامداد است گواهی شده‌'),
(2108, 17, 79, 'و از شب پس بیدار باشد در آن این فزونیی است برای تو باشد برانگیزدت پروردگارت به جایگاهی ستوده‌'),
(2109, 17, 80, 'و بگو پروردگارا درآور مرا درآوردنی راست و برونم آور برون آوردنی راست و قرار ده برای من از نزد خویش فرمانروائیی یاری‌شده‌'),
(2110, 17, 81, 'و بگو بیامد حقّ و نابود شد باطل همانا باطل است نابودشونده‌'),
(2111, 17, 82, 'و می‌فرستیم از قرآن آنچه درمان و رحمتی است برای مؤمنان و نیفزاید ستمگران را مگر زیان‌'),
(2112, 17, 83, 'و هرگاه نعمت فرستیم بر انسان روی گرداند و دور کند پهلوی خویش را و هرگاه برسدش بدیی بوده است بسیار نومیدشونده‌'),
(2113, 17, 84, 'بگو هر کدام عمل می‌کند بر راه و روش خویش پس پروردگار شما داناتر است بدانکه او رهبرنده‌تر است در راه‌'),
(2114, 17, 85, 'و می‌پرسندت از روح (روان) بگو روح از امر پروردگار من است و داده نشده‌اید از دانش جز اندکی را'),
(2115, 17, 86, 'و اگر خواهیم همانا می‌بریم آنچه را وحی فرستادیم به سویت پس نیابی برای خویش بدان بر ما نگهبانی را'),
(2116, 17, 87, 'بجز رحمتی از پروردگارت که فضل او است همانا بر تو بزرگ‌'),
(2117, 17, 88, 'بگو اگر گردآیند آدمیان و پریان بر آنکه بیارند مانند این قرآن را نیارند مانندش و اگر چه باشد برخیشان برای برخی پشتیبان‌'),
(2118, 17, 89, 'و همانا گردانیدیم برای مردم در این قرآن از هر مثَلی پس نخواستند بیشتر مردم جز ناسپاسی را'),
(2119, 17, 90, 'و گفتند هرگز ایمان نیاریم برایت تا بشکافی برای ما از زمین چشمه‌ای‌'),
(2120, 17, 91, 'یا باشدت باغی از خرمابنها و انگور پس بشکافی جویها را میان آن شکافتنی‌'),
(2121, 17, 92, 'یا افکنی آسمان را چنانچه می‌پنداری بر ما پاره‌هائی یا بیاری خدا و فرشتگان را روی به روی‌'),
(2122, 17, 93, 'یا باشدت خانه‌ای از زر یا بالا روی در آسمان و هرگز باور نکنیم بالا رفتنت را تا بفرستی بر ما نامه‌ای که بخوانیمش بگو منزّه است پروردگار من آیا هستم من جز بشری فرستاده‌شده‌'),
(2123, 17, 94, 'و بازنداشت مردم را از آنکه ایمان آرند گاهی که بیامدشان رهبری جز آنکه گفتند آیا برانگیخته است خدا بشری را پیمبر'),
(2124, 17, 95, 'بگو اگر میبود در زمین فرشتگانی که راه می‌رفتند آرمیدگان هر آینه می‌فرستادیم بر ایشان از آسمان فرشته‌ای را پیامبر'),
(2125, 17, 96, 'بگو بس است خدا گواهی میان من و شما همانا او است به بندگان خود آگاهی بینا'),
(2126, 17, 97, 'و آن را که خدا هدایت کند او است هدایت‌شده و آن را که گم کند هرگز نیابی برای ایشان دوستانی جز او و گردآریمشان روز قیامت بر چهره‌هاشان کوران و گنگان و کران جایگاه ایشان است دوزخ هرگاه خاموش شود (فرونشیند) بیفزائیمشان آتش را'),
(2127, 17, 98, 'این است پاداش ایشان بدانچه کفر ورزیدند به آیتهای ما و گفتند آیا هرگاه شدیم استخوانهائی و خاکی آیا مائیم برانگیختگان در آفرینشی نوین‌'),
(2128, 17, 99, 'آیا ندیدند که خداوندی که آفرید آسمانها و زمین را توانا است بر آنکه بیافرد مانند ایشان را و نهاده است برای ایشان سرآمدی را که نیست شکّی در آن پس نخواستند ستمگران جز ناسپاسی را'),
(2129, 17, 100, 'بگو اگر شما می‌داشتید گنجهای رحمت پروردگار مرا در آن هنگام خودداری می‌کردید از ترس بخشش و انسان است بسی خویشتن نگهدار'),
(2130, 17, 101, 'و همانا دادیم به موسی نُه آیت روشن پس بپرس بنی‌اسرائیل را گاهی که بیامدشان پس گفت بدو فرعون که می‌پندارمت ای موسی جادوشده‌'),
(2131, 17, 102, 'گفت همانا دانستی که نفرستاده است آنها را مگر پروردگار آسمانها و زمین بینشهائی و همانا می‌پنداریمت ای فرعون نابودشده‌'),
(2132, 17, 103, 'پس خواست بلغزاندشان از زمین پس غرقش نمودیم و آنان را که با او بودند همگی‌'),
(2133, 17, 104, 'و گفتیم پس از او به بنی‌اسرائیل جایگزین شوید در زمین تا گاهی که بیاید وعده آخرت بیاریم شما را پیچیده‌'),
(2134, 17, 105, 'و به حقّ فرستادیمش و به حقّ فرود آمد و نفرستادیم تو را مگر نویددهنده و ترساننده‌'),
(2135, 17, 106, 'و قرآنی که جدا ساختیمش تا بخوانیمش بر مردم بر درنگی و فرستادیم آن را فرستادنی‌'),
(2136, 17, 107, 'بگو ایمان آرید بدان یا نیارید همانا آنان که داده شدند دانش را پیش از آن گاهی که خوانده شود بر ایشان بیفتند بر چانه‌ها سجده‌کنان‌'),
(2137, 17, 108, 'و گویند منزّه است پروردگار ما همانا وعده پروردگار ما است شدنی‌'),
(2138, 17, 109, 'و بیفتند بر چانه‌ها گریه کنند و بیفزایدشان زاری (فروتنی)'),
(2139, 17, 110, 'بگو بخوانید اللّه را یا بخوانید رحمن را هر کدام را بخوانید او را است نامهای نکو و نه بلند گردان آواز را به نماز خویش و نه آهسته بیارش و بجوی میانه این راهی را'),
(2140, 17, 111, 'و بگو سپاس خداوندی را که برنگرفت فرزندی و نبودش شریکی در پادشاهی و نیستش سرپرستی از زبونی و بزرگ دار او را بزرگ داشتنی‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'سپاس خدائی را که فرستاد بر بنده خویش کتاب را و قرار نداد برای آن کجی را'),
(2142, 18, 2, 'راست است تا بترساند نیروئی سخت را از نزدش و مژده دهد مؤمنان را که کردار شایسته کنند آنکه ایشان را پاداشی نکو است‌'),
(2143, 18, 3, 'درنگ‌کنندگان در آن همیشه‌'),
(2144, 18, 4, 'و بترساند آنان را که گفتند برگرفت خدا فرزندی را'),
(2145, 18, 5, 'نیستشان بدان علمی و نه پدران ایشان را بزرگ سخنی است که برون آید از دهانهای ایشان نمی‌گویند جز دروغی را'),
(2146, 18, 6, 'شاید ببازی تو جان خود را از پی ایشان اگر ایمان نیاوردند بدین داستان بدریغ‌'),
(2147, 18, 7, 'همانا گردانیدیم آنچه را بر زمین است آرایشی برای آن تا بیازمائیم ایشان را کدامین نکوترند در کردار'),
(2148, 18, 8, 'و همانا گرداننده‌ایم آنچه را بر آن است بیابانی خشک‌زار'),
(2149, 18, 9, 'یا پنداشتی که یاران کهف و رقیم بودند از آیتهای ما شگفت‌'),
(2150, 18, 10, 'هنگامی که پناه بردند جوانان به غار پس گفتند پروردگارا بیاور ما را از نزد خویش رحمتی و آماده کن برای ما از کار ما رهبری را'),
(2151, 18, 11, 'پس زدیم بر گوشهای آنان در غار سالیانی بی‌شمار (یعنی خوابانیدنشان)'),
(2152, 18, 12, 'سپس برانگیختیمشان تا بدانیم کدام از دو گروه شمرنده‌ترند آنچه را درنگ کردند از مدّت‌'),
(2153, 18, 13, 'ما می‌خوانیم بر تو داستان ایشان را به حقّ همانا آنانند جوانانی که ایمان آوردند به پروردگار خویش و بیفزودیمشان هدایت را'),
(2154, 18, 14, 'و پیوند زدیم بر دلهای ایشان هنگامی که برخاستند پس گفتند پروردگار ما پروردگار آسمانها و زمین است نخوانیم جز او خدائی هر آینه گوئیم در آن هنگام بیهوده را'),
(2155, 18, 15, 'اینان قوم ما برگرفتند جز او خدایانی چرا نمی‌آورند بر ایشان فرمانروائیی آشکار پس کیست ستمگرتر از آنکه بست بر خدا دروغی را'),
(2156, 18, 16, 'و هنگامی که کنار گرفتید از ایشان و آنچه می‌پرستند جز خدا پس پناه آرید به غار می‌گستراند برای شما پروردگار شما از رحمت خود و آماده کند برای شما از کار شما آسایش را'),
(2157, 18, 17, 'و بینی خورشید را گاهی که براید کج شود از غار ایشان بسوی راست و گاهی که فرورود می‌برّدشان بسوی چپ و ایشانند در پهناوری از آن این از آیتهای خدا است آن را که رهبری کند خدا او است هدایت‌شده و آن را که گمراه کند نیابی برایش دوستی راهنما'),
(2158, 18, 18, 'و پنداریشان بیداران حالی که خفتگانند و می‌گردانیمشان بسوی راست و چپ و سگ ایشان پهن‌کننده است بازوهای خود را در آستانه اگر سرمی‌کشیدی بر آنان همانا پشت می‌کردی از ایشان گریزان و هر آینه پرمی‌شدی از ایشان ترس را'),
(2159, 18, 19, 'و بدینسان برانگیختیمشان تا بپرسند میان خویش (از همدیگر) گفت گوینده‌ای از ایشان چند ماندید گفتند ماندیم روزی یا پاره‌ای از روز گفتند پروردگار شما داناتر است بدانچه ماندید بفرستید یکیتان را با برگ بهای خود این بسوی شهر تا ببیند کدامین پاکیزه‌تر است در خوراک پس بیارد شما را روزیی از آن و باید به نرمی رفتار کند و آگاه نسازد بر شما کسی را'),
(2160, 18, 20, 'همانا ایشان اگر دست یابند بر شما سنگسارتان کنند یا برگردانند شما را در کیش خود و هرگز رستگار نشوید در آن هنگام هیچگاه‌'),
(2161, 18, 21, 'و بدینسان آگاه ساختیم بر ایشان تا بدانند که وعده خدا است حقّ و آنکه ساعت نیست شکّی در آن هنگامی که ستیزه می‌کردند با همدیگر در کار خویش پس گفتند بسازید بر ایشان سازمانی را پروردگار ایشان داناتر است بدیشان گفتند آنان که دست یافتند بر کار ایشان همانا برگیریم بر ایشان پرستشگاهی را'),
(2162, 18, 22, 'بزودی گویند سه تنند چهارمیشان سگشان و گویند پنج تنند ششمی ایشان سگشان انداختنی به ناپیدا و گویند هفت تنند و هشتمینشان سگشان بگو پروردگار من داناتر است به شمار آنان نداندشان مگر کمی پس نستیز در آنان مگر ستیزه‌ای به ظاهر و نپرس در آنان از ایشان احدی را'),
(2163, 18, 23, 'و نگوی به چیزی که منم کننده آن فردا'),
(2164, 18, 24, 'مگر آنکه بخواهد خدا و یاد کن پروردگار خویش را گاهی که فراموش کردی و بگو امید است آنکه هدایتم کند پروردگار من به نزدیکتر از این راه را'),
(2165, 18, 25, 'و ماندند در غار خود سیصد سال و بیفزودند نُه را'),
(2166, 18, 26, 'بگو خدا داناتر است بدانچه ماندند وی را است ناپیدای آسمانها و زمین چه بینا و چه شنوا است نیستشان جز او دوستی و شریک نگرداند در حکم خویش کسی را'),
(2167, 18, 27, 'و بخوان آنچه وحی شده است بسوی تو از کتاب پروردگارت نیست برگرداننده‌ای برای سخنانش و هرگز نیابی جز او پناهگاهی را'),
(2168, 18, 28, 'و شکیبا ساز خویش را با آنان که می‌خوانند پروردگار خویش را بامداد و شب خواهند روی او را و تجاوز نکنند (یا برمگردان) دیدگان تو از ایشان که بخواهی زیور زندگانی دنیا را و فرمانبردار مشو آن را که غافل کردیم دلش را از یاد ما و پیروی کرد هوس خویش را و شد کار او از دست‌رفته‌'),
(2169, 18, 29, 'و بگو حقّ از پروردگار شما است پس هر که خواهد ایمان آورد و هر که خواهد کافر شود همانا آماده کردیم برای ستمکاران آتشی که فراگرفته است بدیشان سراپرده آن و اگر فریاد کنند فریادرسی شوند به آبی چون آهن گداخته بریان کند چهره‌ها را چه زشت نوشابه است و چه زشت آسایشگاهی‌'),
(2170, 18, 30, 'همانا آنان که ایمان آوردند و کردار شایسته کردند ما تباه نمی‌سازیم پاداش آنکه نکو کرد کار را'),
(2171, 18, 31, 'ایشان را است بهشتهای جاودان جاری است زیر آنان جویها زیب داده شوند در آن با دستبندها از زر و پوشند جامه‌های سبزی از سُندس و اِستبرق تکیه‌کننده‌اند در آن بر تختها چه خوب پاداشی است و چه نکو است آن آسایشگاه‌'),
(2172, 18, 32, 'و بزن برای ایشان مثَلی دو مرد را که قرار دادیم برای یکیشان دو باغ از انگورها و پیچیده داشتیم آنها را با درخت خرما و نهادیم میان آنها کشتزاری‌'),
(2173, 18, 33, 'هردو باغ دادند میوه خود را و ستم نکرد از آن چیزی را و شکافتیم میان آنها جوئی‌'),
(2174, 18, 34, 'و بود او را میوه‌ای پس گفت به یار خویش حالی که با او گفتگو می‌کرد من بیشترم از تو در مال و نیرومندتر در گروه‌'),
(2175, 18, 35, 'و درآمد به باغ خویش حالی که ستم‌کننده بود خویشتن را گفت نپندارم نابود شود این هیچگاه‌'),
(2176, 18, 36, 'و نپندارم ساعت را برپا شونده و اگر بازگردم بسوی پروردگار خویش هر آینه بیابم بهتر از این جایگاه را'),
(2177, 18, 37, 'گفت بدو یار او حالی که با وی سخن می‌گفت آیا کافر شدی بدانکه بیافریدت از خاکی سپس از چکّه آبی پس بیاراستت مردی‌'),
(2178, 18, 38, 'لیکن همانا خدا است پروردگارم و شرک نورزم به پروردگارم کسی را'),
(2179, 18, 39, 'و چرا گاهی که در باغ خویش درآمدی نگفتی چه خواسته است خدا نیست نیروئی جز با خدا اگر مرا بینی کمترم از تو درخواسته و فرزند'),
(2180, 18, 40, 'امید است پروردگار من بدهدم بهتر از باغ تو و بفرستد بر آن آفتی از آسمان تا بامداد کند خشکزاری لغزنده‌'),
(2181, 18, 41, 'یا بامداد کند آب آن فرورفته که هرگز نتوانی برای آن جستنی را'),
(2182, 18, 42, 'و نابود شد میوه آن پس بامداد کرد می‌گردانید دستهای خویش را بر آنچه هزینه کرده بود در آن و آن فرود آمده بود بر پایه‌ها (یا پوشها)ی خود و می‌گفت کاش شرک نمی‌ورزیدم به پروردگار خود کسی را'),
(2183, 18, 43, 'و نبودش دسته که یاریش کنند جز خدا و نبود یاری شده (یا یاری جوینده)'),
(2184, 18, 44, 'در آنجا فرمانروائی از آن خداست حقّ او است بهتر به پاداش و بهتر در فرجام‌'),
(2185, 18, 45, 'و بزن برای ایشان مثَل زندگانی دنیا را مانند آبی که فرستادیمش از آسمان پس بیامیخت با آن رستنی زمین پس گردید خُرد که پراکنده می‌سازندش بادها و خدا است بر همه چیز توانا'),
(2186, 18, 46, 'خواسته و فرزندان زیب زندگانی دنیا است و بازمانده‌های شایسته بهتر است نزد پروردگار تو به پاداش و بهتر است در آرزو'),
(2187, 18, 47, 'و روزی که برانیم کوه‌ها را و بینی زمین را نمودار (یا برآمده) و گردشان آوردیم پس بجای نگذاشتیم از ایشان کسی را'),
(2188, 18, 48, 'و عرض شدند بر پروردگار تو صفی همانا آمدید ما را چنانکه آفریدیمتان نخستین بار بلکه پنداشتید که هرگز نگذاریم برای شما وعده‌گاهی را'),
(2189, 18, 49, 'و نهاده شد کتاب پس بینی گنهکاران را شوریده از آنچه در آن است و گویند ای وای بر ما چه شود این کتاب را که نگذارد کوچک و نه بزرگی را مگر آنکه برشمردش و یافتند آنچه را کردند حاضر و ستم نکند پروردگار تو کسی را'),
(2190, 18, 50, 'و هنگامی که گفتیم به فرشتگان سجده کنید برای آدم پس سجده کردند جز ابلیس بود از جنّ پس سرپیچید از فرمان پروردگار خویش آیا گیریدش او و نژادش را دوستانی جز از من حالی که ایشانند برای شما دشمن چه زشت است ستمگران را بدلی‌'),
(2191, 18, 51, 'گواه نگرفتم ایشان را بر آفرینش آسمانها و زمین و نه آفرینش خودشان و نبودم هرگز گیرنده گمراهان بازوئی‌'),
(2192, 18, 52, 'و روزی که گوید بخوانید شریکانم را آنان که می‌پنداشتید پس خواندندشان پس پاسخشان نگفتند و نهادیم میانشان پرتگاهی را'),
(2193, 18, 53, 'و دیدند گنهکاران آتش را پس پنداشتند که افتادگانند در آن و نیافتند از آن کنارگاهی را'),
(2194, 18, 54, 'و همانا گردانیدیم در این قرآن برای مردم از هر مثَلی و انسان بیشتر از هر چیز است ستیزه‌گری را'),
(2195, 18, 55, 'و بازنداشت مردم را از آنکه ایمان آرند گاهی که بیامدشان هدایت و از آنکه آمرزش خواهند از پروردگار خویش جز آنکه بیایدشان شیوه پیشینیان یا بیایدشان عذاب روی بروی‌'),
(2196, 18, 56, 'و نفرستیم پیمبران را مگر بشارت‌دهندگان و ترسانندگان و می‌ستیزند کافران با باطل تا تباه کنند بدان حقّ را و برگرفتند آیتهای مرا و آنچه بیم داده شدند مسخره‌'),
(2197, 18, 57, 'و کیست ستمگرتر از آنکه یادآوری شد به آیتهای خدا پس روی گردانید از آنها و فراموش کرد آنچه را پیش فرستاد دستهایش همانا نهادیم بر دلهای ایشان پوششهائی از آنکه دریابندش و در گوشهای ایشان سنگینی را و اگر خوانیشان بسوی هدایت هرگز هدایت نشوند هیچگاه‌'),
(2198, 18, 58, 'و پروردگار تو است آمرزنده صاحب رحمت اگر گرفتارشان کند بدانچه فراهم کردند هر آینه بشتابد برای ایشان در عذاب بلکه ایشان را است وعده‌گاهی که هرگز نیابند جز آن پناهگاهی را'),
(2199, 18, 59, 'و اینک شهرها نابود کردیم آنها را گاهی که ستم کردند و نهادیم برای نابودیشان وعده‌گاهی را'),
(2200, 18, 60, 'و هنگامی که گفت موسی به جوان خویش نروم (دست برندارم) تا نرسم ملتقای (رسیدنگاه) دو دریا را یا راه سپرم هفتاد سال‌'),
(2201, 18, 61, 'پس هنگامی که رسیدند مجمع (رسیدنگاه) آن دو را فراموش کردند ماهی خویش را تا برگرفت راه خویش را در دریا شکافنده‌'),
(2202, 18, 62, 'پس هنگامی که گذشتند گفت به جوان خود بیاور برای ما چاشت ما را همانا رسیدیم از سفر خویش این رنجی را'),
(2203, 18, 63, 'گفت آیا دیدی گاهی که جای گرفتیم نزدیک آن سنگ همانا فراموش کردم ماهی را و فراموشم نکرد جز شیطان از آنکه به یاد آرمش و برگرفت راه خود را در دریا شگفت‌'),
(2204, 18, 64, 'گفت این است آنچه می‌خواستیم پس بازگشتند بر جای پایهای خویش پی‌جویان‌'),
(2205, 18, 65, 'تا یافتند بنده‌ای از بندگان ما را که داده بودیمش رحمتی را از نزد خود و آموخته بودیمش از نزد خود دانش را'),
(2206, 18, 66, 'گفت بدو موسی آیا پیرویت کنم بر آنکه بیاموزیم از آنچه آموخته شدی رهبری را'),
(2207, 18, 67, 'گفت همانا تو نتوانی با من صبرکردن‌'),
(2208, 18, 68, 'و چگونه صبر کنی بر آنچه فرانگرفتی بر آن آگاهی را'),
(2209, 18, 69, 'گفت زود است بیابیم شکیبا اگر خدا خواهد و نافرمانی نکنمت امری را'),
(2210, 18, 70, 'گفت پس اگر پیرویم کردی نپرس مرا از چیزی تا پدید آرم برای تو از آن سخنی را'),
(2211, 18, 71, 'پس برفتند تا گاهی که سوار شدند در کشتی بشکافتش گفت آیا سوراخ کردیش که غرق کنی مردمش را همانا بیاوردی چیزی زشت را'),
(2212, 18, 72, 'گفت آیا نگفتم که تو نتوانی با من صبرکردن‌'),
(2213, 18, 73, 'گفت مرا نگیر بدانچه فراموش کردم و نرسان به من از کارم سختی را'),
(2214, 18, 74, 'پس برفتند تا گاهی که به کودکی رسیدند بکشتش گفت آیا کشتی تنی پاک را نه در برابر تنی همانا آوردی چیزی ناپسند را'),
(2215, 18, 75, 'گفت آیا نگفتم به تو که نتوانی هرگز با من صبرکردن‌'),
(2216, 18, 76, 'گفت اگر پرسیدمت از چیزی پس از این همراهم نگیر به درست رسیدی از نزدم بهانه‌ای را'),
(2217, 18, 77, 'پس برفتند تا گاهی که رسیدند مردم شهری را خوراک خواستند از مردمش پس خودداری کردند از آنکه مهمانشان کنند پس یافتند در آن دیواری را که می‌خواست فرو ریزد پس بپای داشتش گفت اگر می‌خواستی می‌گرفتی بر این مزدی را'),
(2218, 18, 78, 'گفت این است جدائی میان من و تو بزودی آگهیت دهم به سرانجام آنچه نتوانستی بر آن صبرکردن را'),
(2219, 18, 79, 'اما کشتی پس از آنِ بینوایانی بود که کار می‌کردند در دریا خواستم آسیبی بدان رسانم و بود پشت سر ایشان پادشاهی که می‌گرفت هر کشتی را به ستم (بیگاری)'),
(2220, 18, 80, 'و امّا کودک بودند پدر و مادرش مؤمنان پس ترسیدیم که واداردشان (یا تنگ آوردشان) به سرکشی و ناسپاسی‌'),
(2221, 18, 81, 'پس خواستیم تا بازدهد ایشان را پروردگارشان به جای او بهتر از او را به پاکی و نزدیکتر به رحم‌آوردن‌'),
(2222, 18, 82, 'و اما دیوار پس از آن دو کودک یتیم بود در شهر و بود زیر آن گنجی از برای ایشان و بود پدر ایشان شایسته پس خواست پروردگار تو که برسند نیروی (جوانی) خود را و برون آرند گنج خویش را مهربانیی از پروردگار تو و من نکردمش به فرمان خود این است سرانجام آنچه نتوانستی بر آن صبرکردن را'),
(2223, 18, 83, 'و پرسندت از ذوالقرنین بگو زود است بخوانم بر شما از او سخنی را'),
(2224, 18, 84, 'همانا فرمانروایش ساختیم در زمین و دادیمش از هرچیز دستاویزی‌'),
(2225, 18, 85, 'پس پیروی کرد دستاویزی را'),
(2226, 18, 86, 'تا گاهی که رسید فرودگاه خورشید را یافتش فرو می‌رود در چشمه گل آلودی و یافت نزد آن گروهی را گفتیم ای ذوالقرنین آیا یا شکنجه می‌کنی و یا برمی‌گیری در ایشان نکوئی را'),
(2227, 18, 87, 'گفت اما آنکه ستم کند زود است عذابش کنیم و سپس بازگردانیده شود بسوی پروردگار خویش پس عذابش کند عذابی زشت‌'),
(2228, 18, 88, 'و اما آنکو ایمان آرد و کردار شایسته کند پس او را است پاداش نیک و زود است گوئیم برایش از امر خویش گشایش را'),
(2229, 18, 89, 'پس پیروی کرد وسیلتی را'),
(2230, 18, 90, 'تا گاهی که رسید برآمدنگاه خورشید را یافتش برمی‌آید بر گروهی نگذارده‌ایم برای ایشان جز آن پوششی را'),
(2231, 18, 91, 'چنین و همانا فراگرفتیم بدانچه نزد او است به دانش‌'),
(2232, 18, 92, 'پس پیروی کرد وسیلتی را'),
(2233, 18, 93, 'تا گاهی که رسید میان دو بند را یافت نارسیده بدانها گروهی را که نیارند دریابند گفتاری را'),
(2234, 18, 94, 'گفتند ای ذوالقرنین همانا یأجوج و مأجوج تبهکاری کنند در زمین آیا بگذاریم برای تو هزینه‌ای بر آنکه بگذاری میان ما و آنان بندی را'),
(2235, 18, 95, 'گفت آنچه فرمانروا کرده است مرا در آن پروردگارم بهتر است پس کمک دهیدم به نیروئی تا بنهم میان شما و ایشان بندی انبوه را'),
(2236, 18, 96, 'بیارید مرا خرده‌های آهن تا گاهی که یکسان شد میان دو کوه گفت بدهید تا گاهی که گردانیدش آتش گفت بیارید مرا بریزم بر آن آهن یا مس گداخته را'),
(2237, 18, 97, 'پس نتوانستند چیره شوند بر آن و نتوانستندش سوراخ‌کردن را'),
(2238, 18, 98, 'گفت این است رحمتی از پروردگارم تا گاهی بیاید وعده پروردگارم بگرداندش خرد و بوده است وعده پروردگارم درست‌'),
(2239, 18, 99, 'و گذاردیم گروهی از ایشان را در آن روز موج زنند در گروهی و دمیده شد در صور پس گردشان آوردیم گردآوردنی‌'),
(2240, 18, 100, 'و عرض کردیم دوزخ را آن روز بر کافران عرض‌کردنی‌'),
(2241, 18, 101, 'آنان که بود دیدگانشان در پرده از یاد من و بودند نمی‌توانستند شنیدن را'),
(2242, 18, 102, 'آیا پنداشتند آنان که کفر ورزیدند که برگیرند بندگانم را جز من دوستانی همانا آماده کردیم دوزخ را برای کافران پیشکشی‌'),
(2243, 18, 103, 'بگو آیا آگهیتان دهم به زیانکارتران در کردار'),
(2244, 18, 104, 'آنان که تباه شده است کوشش ایشان در زندگانی دنیا و می‌پندارند که ایشان نکو می‌کنند عمل را'),
(2245, 18, 105, 'آنانند که کفر ورزیدند به آیتهای پروردگار خویش و ملاقات او پس تباه شد اعمال ایشان پس بپای نداریم برای آنان روز قیامت وزنی (ترازوئی) را'),
(2246, 18, 106, 'این است پاداش ایشان دوزخ بدانچه کفر ورزیدند و برگرفتند آیتهای مرا و پیمبران مرا ریشخند'),
(2247, 18, 107, 'همانا آنان که ایمان آوردند و کردار شایسته کردند بوده است ایشان را باغهای بهشت پیشکشی‌'),
(2248, 18, 108, 'جاودانند در آن نجویند از آن جابجاشدن را'),
(2249, 18, 109, 'بگو اگر می‌شد دریا مدادی برای سخنان پروردگارم همانا پایان می‌یافت دریا پیش از آنکه پایان یابند سخنان پروردگارم و هر چند بیاریم همانند آن کمک را'),
(2250, 18, 110, 'بگو جز این نیست که من بشری هستم مانند شما وحی می‌شود به سویم که خدای شما است خداوند یکتا پس آنکه امید دارد ملاقات پروردگار خویش را باید بکند کرداری شایسته و شریک نگرداند به پرستش پروردگار خویش کسی را');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کهیعص‌'),
(2252, 19, 2, 'یادی از رحمت پروردگار تو به بنده خویش زکریّا'),
(2253, 19, 3, 'هنگامی که خواند پروردگار خود را خواندی آهسته (نهانی)'),
(2254, 19, 4, 'گفت پروردگارا همانا سست شد استخوان از من و درخشید مرا سر به پیری و نبودم به خواندن تو پروردگار را تیره‌بخت‌'),
(2255, 19, 5, 'و همانا ترسیدم خویشاوندانم را پس از من و بوده است زنم نازا پس ببخش مرا از نزد خود سرپرستی (فرزندی)'),
(2256, 19, 6, 'که ارث برد از من و ارث برد از خاندان یعقوب و بگردانش پروردگارا پسندیده‌'),
(2257, 19, 7, 'ای زکریّا همانا مژده دهیمت به پسری که نامش یحیی است قرار ندادیم برایش پیش از این همنامی‌'),
(2258, 19, 8, 'گفت پروردگارا چگونه باشدم پسری و بوده است زنم نازا و بدرست رسیده‌ام از پیری خشکیدگی را'),
(2259, 19, 9, 'گفت بدینسان گفت پروردگار تو آن است بر من آسان و همانا آفریدمت پیش از این و نبودی چیزی‌'),
(2260, 19, 10, 'گفت پروردگارا بگذار برای من آیتی گفت آیت تو آنکه سخن نگوئی با مردم سه شب تمامی‌'),
(2261, 19, 11, 'پس برون آمد بر قوم خود از محراب پس رساند بدانان که تسبیح کنید بامداد و شب‌هنگام‌'),
(2262, 19, 12, 'ای یحیی بگیر کتاب را به نیرومندی و دادیمش حکم را به کودکی‌'),
(2263, 19, 13, 'و مهربانی از نزد ما و پاکی و بوده است پرهیزکاری‌'),
(2264, 19, 14, 'و نکوکاری به پدر و مادر خویش و نبود گردنکشی نابفرمان‌'),
(2265, 19, 15, 'و درود بر او روزی که بزاد و روزی که بمیرد و روزی که برانگیخته شود زنده‌'),
(2266, 19, 16, 'و یاد کن در کتاب مریم را گاهی که برگرفت دور از خاندان خویش جایگاهی خاوری را'),
(2267, 19, 17, 'پس برگرفت دور از آنان پوششی پس فرستادیم بسوی او روح خود را تا نمایان شد برای او مردی درست‌'),
(2268, 19, 18, 'گفت همانا پناه برم به خدای مهربان از تو اگر هستی پرهیزکار'),
(2269, 19, 19, 'گفت همانا منم فرستاده پروردگار تو تا ببخشم به تو پسری پاک‌'),
(2270, 19, 20, 'گفت چگونه باشدم پسری و به من نزدیک نشده است بشری و نبوده‌ام بدکاره‌'),
(2271, 19, 21, 'گفت بدینسان گفت پروردگار تو آن است بر من آسان تا بگردانیمش آیتی برای مردم و رحمتی از ما و بوده است کاری گذشته‌'),
(2272, 19, 22, 'پس بارور شد بدو پس کناره گرفت بدو جایگاهی دور را'),
(2273, 19, 23, 'پس بیاوردش درد زائیدن بسوی شاخه خرما گفت کاش مرده بودم پیش از این و می‌شدم فراموشی فراموش‌شده‌'),
(2274, 19, 24, 'پس بانگ بدو زد از زیرش که اندوهگین نباش همانا نهاد پروردگار تو زیر تو جوئی را'),
(2275, 19, 25, 'و بجنبان بسوی خود شاخه خرما را می‌ریزد بر تو رطبی تازه‌چیده را'),
(2276, 19, 26, 'پس بخور و بیاشام و چشم‌روشن باش و اگر دیدی از بشر کسی را بگو همانا نذر کردم برای خداوند مهربان روزه را و هرگز سخن نگویم امروز را انسی را'),
(2277, 19, 27, 'پس بیاورد او را به نزد قومش حالی که به دوش همی‌کشیدش گفتند ای مریم هر آینه آوردی چیزی دروغ‌آمیز را'),
(2278, 19, 28, 'ای خواهر هارون نبود پدر تو مردی زشت و نبود مادرت بدکاره‌'),
(2279, 19, 29, 'پس او را نشان داد گفتند چگونه سخن گوئیم آن را که بوده است در گهواره کودکی‌'),
(2280, 19, 30, 'گفت همانا منم بنده خدا داد به من کتاب را و گردانید مرا پمیبری‌'),
(2281, 19, 31, 'و گردانید مرا فرخنده هر کجا باشم و توصیه کرد مرا به نماز و زکات مادامی که هستم زنده‌'),
(2282, 19, 32, 'و نکوکاری به مادرم و نگردانیدم فرمانفرمائی تیره‌بخت‌'),
(2283, 19, 33, 'و درود بر من روزی که زادم و روزی که می‌میرم و روزی که برانگیخته شوم زنده‌'),
(2284, 19, 34, 'این است عیسی پسر مریم سخن حقّی که در آن شک می‌کنند'),
(2285, 19, 35, 'نبوده است خدا که برگیرد فرزندی منزه است او هر گاه بگذراند کاری را جز این نیست که گوید بدو بشو پس می‌شود'),
(2286, 19, 36, 'و همانا خدا است پروردگار من و پروردگار شما پس بپرستیدش این است راه راست‌'),
(2287, 19, 37, 'پس اختلاف کردند گروه‌ها از میان ایشان پس وای بر آنان که کفر ورزیدند از دیدار روزی بزرگ‌'),
(2288, 19, 38, 'چه شنوایند به ایشان و چه بینا روزی که آیند ما را لیکن ستمگرانند در آن روز در گمراهی آشکار'),
(2289, 19, 39, 'و بترسانشان روز دریغ (حسرت) را هنگامی که گذشت کار و ایشانند در غفلتی و ایمان نمی‌آورند'),
(2290, 19, 40, 'همانا ما خود میراث بریم زمین را و آنکه بر آن است و بسوی ما بازگردانیده شوند'),
(2291, 19, 41, 'و یاد کن در کتاب ابراهیم را که بوده است او راستگوی پیمبری‌'),
(2292, 19, 42, 'هنگامی که گفت به پدر خود ای پدر من چرا می‌پرستی آنچه را نمی‌شنود و نمی‌بیند و نه بی‌نیاز می‌کند از تو به چیزی‌'),
(2293, 19, 43, 'ای پدر من همانا بیامده است مرا از دانش آنچه نیامده است تو را پس پیرویم کن تا رهبریت کنم راهی راست را'),
(2294, 19, 44, 'ای پدر من پرستش نکن شیطان را که شیطان است برای خدای مهربان نابفرمان‌'),
(2295, 19, 45, 'ای پدر من همانا می‌ترسم برسدت عذابی از خدای مهربان که بشوی برای شیطان دوستی‌'),
(2296, 19, 46, 'گفت آیا روی گرداننده‌ای از خدایانم ای ابراهیم اگر کوتاه نیائی هر آینه سنگسارت می‌کنم و دوری گزین از من روزگاری دراز (یا اندک اندک)'),
(2297, 19, 47, 'گفت سلام بر تو زود است آمرزش خواهم برای تو از پروردگار خویش همانا او بوده است به من مهربان‌'),
(2298, 19, 48, 'و دوری گزینم از شما و آنچه خوانید جز خدا و می‌خوانم پروردگار خود را شاید نباشم به خواندن پروردگارم تیره‌روز'),
(2299, 19, 49, 'پس هنگامی که دوری گزید از ایشان و آنچه می‌پرستیدند جز خدا بخشیدیم بدو اسحق و یعقوب را و هر کدام را گردانیدیم پیمبری‌'),
(2300, 19, 50, 'و بخشیدیم بدیشان از رحمت خود و نهادیم برای ایشان زبان راستیی فرازنده‌'),
(2301, 19, 51, 'و یاد کن در کتاب موسی را که او است ناآلوده و بوده است پیمبری فرستاده شده‌'),
(2302, 19, 52, 'و خواندیمش از کنار راست طور و نزدیک گردانیدیمش رازگویان‌'),
(2303, 19, 53, 'و بخشیدیم بدو از رحمت خود برادرش هارون را پیمبری‌'),
(2304, 19, 54, 'و یاد کن در کتاب اسماعیل را که او بوده است راست‌وعده و بوده است فرستاده پیمبری‌'),
(2305, 19, 55, 'و بود می‌فرمود خاندان خود را به نماز و زکات و بود نزد پروردگار خویش پسندیده‌'),
(2306, 19, 56, 'و یاد کن در کتاب ادریس را که بوده است راستگوی پیمبری‌'),
(2307, 19, 57, 'و بالا بردیمش جایگاهی بلند'),
(2308, 19, 58, 'اینانند که بخشید خدا بدیشان از پیمبران از فرزندان آدم و از آنان که سوار کردیم با نوح و از فرزندان ابراهیم و اسرائیل و از آنان که هدایت کردیم و برگزیدیم که هرگاه خوانده شود بر ایشان آیتهای خدای مهربان بیفتند سجده‌کنان و گریان‌'),
(2309, 19, 59, 'پس جانشین شد از پس ایشان پشتی که تباه کردند نماز را و پیروی کردند شهوتها را پس زود است برسند گمراهی را'),
(2310, 19, 60, 'مگر آنکه توبه کرد و ایمان آورد و عمل صالح کرد که آنان درآیند بهشت را و ستم نشوند چیزی را'),
(2311, 19, 61, 'بهشتهای جاودانی که وعده داد خدای مهربان بندگان خویش را پنهانی همانا بوده است وعده او آورده‌'),
(2312, 19, 62, 'نشنوند در آن جز درودی و ایشان را است روزیشان در آن بامداد و شب‌هنگام‌'),
(2313, 19, 63, 'این است آن بهشتی که ارث دهیم بندگان خود را آنکو بوده است پرهیزکار'),
(2314, 19, 64, 'و فرود نیائیم مگر به دستور پروردگار تو وی را است آنچه پیش روی ما است و آنچه پشت سر ما و آنچه میان آن است و نیست پروردگار تو فراموش‌کننده‌'),
(2315, 19, 65, 'پروردگار آسمانها و زمین و آنچه میان آنها است پس پرستش کن او را و شکیبائی گزین برای پرستش او آیا می‌دانی برای او همنامی را'),
(2316, 19, 66, 'و گوید انسان آیا هر گاه بمُردم هر آینه بزودی برون آورده می‌شوم زنده‌'),
(2317, 19, 67, 'آیا یاد نمی‌آورد انسان که ما آفریدیمش از پیش حالی که نبود به چیزی‌'),
(2318, 19, 68, 'سوگند به پروردگار تو هر آینه گردآوریمشان البته با شیاطین پس احضارشان کنیم البته پیرامون دوزخ بر زانو نشستگان‌'),
(2319, 19, 69, 'سپس البته برگیریم از هر گروهی هر کدام را از ایشان که سخت‌تر است بر خدای مهربان در سرکشی‌'),
(2320, 19, 70, 'سپس ما هر آینه داناتریم بدانان که سزاوارترند بدان چشیدن را'),
(2321, 19, 71, 'و نیست از شما جز دراینده آن است بوده است بر پروردگار تو بایسته‌ای گذرانیده‌'),
(2322, 19, 72, 'پس می‌رهانیم آنان را که پرهیزکاری کردند و بگذرایم ستمگران را در آن به زانو درآیندگان‌'),
(2323, 19, 73, 'و هر گاه خوانده شود بر ایشان آیتهای ما تابناک گویند آنان که کفر ورزیدند بدانان که ایمان آوردند کدام از دو گروهند بهتر در جایگاه و نکوتر در انجمن‌'),
(2324, 19, 74, 'و بسا نابود کردیم پیش از ایشان از قرنهائی که نکوتر بودند در ابزار (کالاها) و در منظر'),
(2325, 19, 75, 'بگو آنکه در گمراهی است پس باید مهلت دهد بدو خداوند مهربان مهلتی تا گاهی که بینند آنچه را وعده داده شوند یا شکنجه و یا ساعت را زود است بدانند کیست آنکه بدتر است در جایگاه و سست‌تر است در سپاه‌'),
(2326, 19, 76, 'و بیفزاید خدا آنان را که پی‌جوی هدایتند هدایت را و بازمانده‌های شایسته بهتر است نزد پروردگار تو در پاداش و بهتر است در بازگشت‌'),
(2327, 19, 77, 'آیا دیده‌ای آن را که کفر ورزید به آیتهای ما و گفت هر آینه داده شوم مال و فرزندی را'),
(2328, 19, 78, 'آیا آگهی یافت بر ناپیدا یا برگرفته است نزد خدای مهربان عهدی را'),
(2329, 19, 79, 'نه چنین است زود است بنویسیم آنچه را گوید و کِشش دهیم در عذاب برای او کششی‌'),
(2330, 19, 80, 'و ارث بریم از او آنچه را گوید (یا ارث دهیمش آنچه را گوید) و بیاید ما را تنها'),
(2331, 19, 81, 'و برگرفتند جز خدا خدایانی تا باشند برای آنان عزّتی‌'),
(2332, 19, 82, 'نه چنین است زود است کفر ورزند به پرستش ایشان و باشند برای ایشان برابری (ضدّی)'),
(2333, 19, 83, 'آیا ندیدی که ما فرستادیم شیاطین را بر کافران تا بیازارندشان آزردنی (یا برانگیزندشان انگیختنی)'),
(2334, 19, 84, 'پس مشتاب بر ایشان جز این نیست که می‌شماریم برای ایشان (شمردنی)'),
(2335, 19, 85, 'روزی که گردآوریم پرهیزکاران را بسوی خدای مهربان میهمانانی‌'),
(2336, 19, 86, 'و برانیم گنهکاران را بسوی دوزخ تشنگانی‌'),
(2337, 19, 87, 'دارای شفاعت نیستند جز آنکو بگرفته است نزد خداوند مهربان پیمانی‌'),
(2338, 19, 88, 'و گفتند برگرفت خدا فرزندی‌'),
(2339, 19, 89, 'همانا آوردید چیزی گران را'),
(2340, 19, 90, 'که نزدیک است آسمانها پاشیده شوند از آن و از هم بشکافد زمین و بیفتد کوه‌ها فروریخته‌'),
(2341, 19, 91, 'چرا که خواندند برای خدای مهربان فرزندی‌'),
(2342, 19, 92, 'و نیست شایسته برای خداوند مهربان که گیرد فرزندی را'),
(2343, 19, 93, 'نیست هر کدام از آنان که در آسمانها و زمینند جز آینده است خداوند مهربان را بنده‌ای‌'),
(2344, 19, 94, 'همانا فراگرفتشان و بشمردشان شمردنی‌'),
(2345, 19, 95, 'و همگیشان آینده‌اند او را روز قیامت تنها'),
(2346, 19, 96, 'همانا آنان که ایمان آوردند و عمل صالح کردند زود است بنهد برای ایشان خداوند مهربان دوستی را'),
(2347, 19, 97, 'جز این نیست که روانش گردانیدیم بر زبان تو تا بشارت دهی بدان پرهیزکاران را و بترسانی بدان قومی سرسخت را'),
(2348, 19, 98, 'و بسا نابود کردیم پیش از ایشان قرنهائی آیا احساس می‌کنی از ایشان کسی را یا می‌شنوی برای ایشان آوازی را');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طه‌'),
(2350, 20, 2, 'نفرستادیم بر تو قرآن را تا رنج بری‌'),
(2351, 20, 3, 'جز یادآوریی برای آنکه می‌ترسد'),
(2352, 20, 4, 'فرستادنی از آنکه آفرید زمین و آسمانهای افراشته را'),
(2353, 20, 5, 'خداوند مهربان به عرش پرداخت (یا بر عرش استیلا یافت)'),
(2354, 20, 6, 'وی را است آنچه در آسمانها و آنچه در زمین و آنچه میان آنها و آنچه زیر خاک است‌'),
(2355, 20, 7, 'و اگر بلند گوئی سخن را همانا او می‌داند نهان و نهان‌تر را'),
(2356, 20, 8, 'خدا نیست خدائی جز او وی را است نامهای نکو'),
(2357, 20, 9, 'و آیا رسیده است به تو داستان موسی‌'),
(2358, 20, 10, 'هنگامی که دید آتشی پس گفت به خاندان خویش درنگ کنید که من یافتم آتشی را شاید بیارم شما را از آن اخگری (گیرانه‌ای) یا بیابم بر آتش راهنمائی را'),
(2359, 20, 11, 'پس هنگامی که بیامدش خوانده شد ای موسی‌'),
(2360, 20, 12, 'همانا منم پروردگار تو پس درآور کفشهای خویش را که توئی بر درّه مقدّس طُوی‌'),
(2361, 20, 13, 'و منت برگزیدم پس گوش فراده بدانچه وحی شود'),
(2362, 20, 14, 'همانا منم خدا نیست خداوندی جز من پس پرستش کن مرا و بپای دار نماز را برای یادکردنم‌'),
(2363, 20, 15, 'همانا ساعت است آینده خواهم نهان دارمش تا پاداش داده شود هر کسی بدانچه می‌کوشد'),
(2364, 20, 16, 'پس بازنداردت از آن آنکه ایمان نیاورد بدان و پیروی کند هوس خویش را تا نابود شوی‌'),
(2365, 20, 17, 'و چیست این به دست راست تو ای موسی‌'),
(2366, 20, 18, 'گفت این است چوبدست من که بدان تکیه کنم و بریزم برگها را بدان بر گوسفندانم و مرا است در آن حوائجی دیگر'),
(2367, 20, 19, 'گفت بیفکنش ای موسی‌'),
(2368, 20, 20, 'پس افکندش ناگهان آن است ماری شتابان‌'),
(2369, 20, 21, 'گفت بگیرش و نترس زود است بازگردانیمش به شیوه نخستینش‌'),
(2370, 20, 22, 'و بچسبان دست خود را به پهلوی خود برون آید تابنده‌ای بی‌آزار آیتی دیگر'),
(2371, 20, 23, 'تا بنمایانیمت از آیتهای ما بزرگها را'),
(2372, 20, 24, 'برو بسوی فرعون که او سرکشی کرد'),
(2373, 20, 25, 'گفت پروردگارا فراخ گردان برای من سینه مرا'),
(2374, 20, 26, 'و آسان کن برای من کار مرا'),
(2375, 20, 27, 'و بازکن گرهی از زبانم‌'),
(2376, 20, 28, 'تا دریابند گفتار مرا'),
(2377, 20, 29, 'و قرار ده برای من وزیری از خاندانم‌'),
(2378, 20, 30, 'هارون برادرم را'),
(2379, 20, 31, 'استوار سازید بدو پشت مرا'),
(2380, 20, 32, 'و شریکش گردان در کارم‌'),
(2381, 20, 33, 'تا بستایمت بسیار'),
(2382, 20, 34, 'و یادت کنیم بسیار'),
(2383, 20, 35, 'که همانا بوده‌ای تو به ما بینا'),
(2384, 20, 36, 'گفت همانا داده شدی خواست خود را ای موسی‌'),
(2385, 20, 37, 'و همانا منّت نهادیم بر تو باری دیگر'),
(2386, 20, 38, 'هنگامی که وحی کردیم بسوی مادرت آنچه وحی می‌شود'),
(2387, 20, 39, 'که بیفکنش در تابوت پس بیفکنش در دریا پس بیفکندش دریا به ساحل تا گیردش دشمن من و دشمن او و افکندم بر تو مِهری (دوستیی) را از خویش و تا ساخته شوی برابر چشمم‌'),
(2388, 20, 40, 'هنگامی که می‌رفت خواهرت و می‌گفت آیا راهنمائی کنم شما را بر کسی که پرستاریش کند پس بازگردانیدیمت بسوی مادرت تا روشن شود دیده او و اندوهگین نشود و بکشتی تنی را پس رها ساختیمت از اندوه و آزمودیمت آزمایشی پس ماندی سالیانی در مردم مَدین سپس آمدی بر تقدیری ای موسی‌'),
(2389, 20, 41, 'و برگزیدمت برای خویش‌'),
(2390, 20, 42, 'برو تو و برادرت با آیتهایم و سستی نکنید در یادم‌'),
(2391, 20, 43, 'بروید بسوی فرعون که سرکشی کرد'),
(2392, 20, 44, 'پس بگوئید بدو گفتاری نرم شاید یادآور شود یا بترسد'),
(2393, 20, 45, 'گفتند پروردگارا ترسیم که پیش‌دستی گیرد بر ما یا سرکشی کند'),
(2394, 20, 46, 'گفت نترسید که منم با شما می‌شنوم و می‌بینم‌'),
(2395, 20, 47, 'پس بیائید او را و بگوئید ما فرستادگان پروردگار توئیم پس بفرست با ما بنی‌اسرائیل را و شکنجه نکن ایشان را همانا آوردیمت آیتی از پروردگار تو و درود بر آنکه پیروی کند هدایت را'),
(2396, 20, 48, 'همانا به ما وحی شد که شکنجه بر آن است که تکذیب کند و روی برگرداند'),
(2397, 20, 49, 'گفت کیست پروردگار شما ای موسی‌'),
(2398, 20, 50, 'گفت پروردگار ما است آنکه داد به هر چیزی آفرینش آن را پس هدایت کرد'),
(2399, 20, 51, 'گفت پس چیست حال قرنهای پیشین‌'),
(2400, 20, 52, 'گفت علم آن نزد پروردگار من است در کتابی که نه گم کند پروردگار من و نه فراموش کند'),
(2401, 20, 53, 'آنکه گردانید برای شما زمین را گسترده و بنهاد برای شما در آن راه‌هائی و فرستاد از آسمان آبی را پس برون آوردیم بدان جفتهائی از گیاهان گوناگون‌'),
(2402, 20, 54, 'بخورید و بچرانید دامهای خود را که در آن است آیتهائی برای دارندگان خردها'),
(2403, 20, 55, 'از آن آفریدیمتان و در آن برگردانیمتان و از آن برونتان آوریم بار دیگر'),
(2404, 20, 56, 'و همانا نمایاندیمش آیتهای خویش را همگی پس تکذیب کرد و سرپیچید'),
(2405, 20, 57, 'گفت آیا ما را آمدی تا برون کنی ما را از زمین ما به جادوی خود ای موسی‌'),
(2406, 20, 58, 'همانا بیاریمت به جادوئی مانند آن پس بگذار میان ما و تو وعده‌گاهی که تخلف نکنیم از آن ما و نه تو جایگاهی درست را'),
(2407, 20, 59, 'گفت وعده‌گاه شما روز آرایش است و آنکه گردآورده شوند مردم چاشتگاه‌'),
(2408, 20, 60, 'پس برگشت فرعون پس گردآورد نیرنگ خویش را پس بیامد'),
(2409, 20, 61, 'گفت بدیشان موسی وای بر شما نبندید بر خدا دروغی را که نابودتان سازد به عذابی و همانا زیانمند شد آنکه دروغ بست‌'),
(2410, 20, 62, 'پس ستیزه کردند کارشان را میان خود و نهان داشتند راز را'),
(2411, 20, 63, 'گفتند همانا اینان دو جادوگرند خواهند برون کنند شما را از زمین خود به جادویشان و براندازند روش بهتر شما را'),
(2412, 20, 64, 'پس گردآرید نیرنگ خویش را و بیائید صفی به درست رستگار گشت امروز آنکه برتری جست‌'),
(2413, 20, 65, 'گفتند ای موسی یا بیفکنی و یا باشیم ما نخستین کسی که بیفکند'),
(2414, 20, 66, 'گفت بلکه بیفکنید که ناگهان رسنها و عصاهای ایشان به گمان می‌آمدش از جادوی ایشان که می‌دوند'),
(2415, 20, 67, 'پس احساس کرد در خود ترسی را موسی‌'),
(2416, 20, 68, 'گفتیم نترس همانا توئی برتر'),
(2417, 20, 69, 'و بیفکن آنچه به دست راست تو است فروبرد آنچه را ساختند جز این نیست که آنچه ساختند افسون جادوگری است و رستگار نشود جادوگر هر جا آید'),
(2418, 20, 70, 'پس بیفتادند جادوگران سجده‌کنان گفتند ایمان آوردیم به پروردگار هارون و موسی‌'),
(2419, 20, 71, 'گفت آیا ایمان بدو آوردید پیش از آنکه به شما رخصت دهم همانا او است مهتر شما که بیاموختتان جادو را همانا سخت ببرم دستها و پایهای شما را از برابر و همانا به دارتان کشم بر شاخه‌های درخت خرما و البته خواهید دید کدامین از ما سخت‌ترند در عذاب و پاینده‌تر'),
(2420, 20, 72, 'گفتند هرگز نگزینیم تو را بر آنچه بیامد ما را از روشنائیها سوگند بدانکه ما را آفرید پس بفرمای آنچه را می‌فرمائی جز این نیست که می‌فرمائی این زندگانی دنیا را'),
(2421, 20, 73, 'همانا ما ایمان آوردیم به پروردگار خویش تا بیامرزد برای ما لغزشهای ما را و آنچه تو وادار کردی ما را بر آن از جادو و خدا بهتر است و پایدارتر'),
(2422, 20, 74, 'همانا آنکه بیاید پروردگار خویش را گنهکار همانا او را است دوزخ نه بمیرد در آن و نه زنده ماند'),
(2423, 20, 75, 'و آنکو بیایدش مؤمن که کرده باشد کارهای شایسته را پس آنان را است پایه‌های برتر'),
(2424, 20, 76, 'بهشتهای جاودان که روان است زیر آنها جویها جاودانند در آن و این است پاداش آنکه پاکی جست‌'),
(2425, 20, 77, 'و همانا وحی فرستادیم به موسی که شبانه ببر بندگان مرا پس برگیر برای ایشان راهی را در دریا خشک نترسی بازیافتنی را و نه هراسان باشی‌'),
(2426, 20, 78, 'پس از پی آمدشان فرعون با سپاهیان خود پس فراگرفت ایشان را از دریا آنچه فراگرفت‌'),
(2427, 20, 79, 'و گمراه کرد فرعون قوم خویش را و رهبری نکرد'),
(2428, 20, 80, 'ای بنی اسرائیل همانا رهانیدیم شما را از دشمنتان و وعده نهادیم با شما کنار راست طور را و فرستادیم بر شما مَنّ و سَلوی را'),
(2429, 20, 81, 'بخورید از پاکیزه‌های آنچه روزیتان دادیم و سرکشی نکنید در آن تا فرود آید بر شما خشم من و آنکه فرود آید بر او خشم من همانا تباه گشت‌'),
(2430, 20, 82, 'و همانا منم بسیار آمرزنده آنکه بازگشت نمود و ایمان آورد و کردار شایسته کرد پس هدایت یافت‌'),
(2431, 20, 83, 'و چه شتابانی (چه چیزت به شتاب آورد) از قوم خویش ای موسی‌'),
(2432, 20, 84, 'گفت اینک ایشانند پشت سرم و شتافتم بسوی تو پروردگارا تا راضی شوی‌'),
(2433, 20, 85, 'گفت همانا آزمودیم قومت را پس از تو و گمراهشان ساخت سامری‌'),
(2434, 20, 86, 'پس بازگشت موسی بسوی قوم خود خشمگین افسوس خوران گفت ای قوم من آیا نوید نداد شما را پروردگار شما نویدی خوش آیا دراز کشید بر شما روزگار (مدّت) یا خواستید فرود آید بر شما خشمی از پروردگار شما که خلف کردید وعده‌گاه مرا'),
(2435, 20, 87, 'گفتند خلف نکردیم وعده تو را به نیروی خود و لیکن بارشدیم بارهائی را از زیور قوم پس افکندیم آنها را و بدینگونه افکند سامری‌'),
(2436, 20, 88, 'پس برون آورد برای ایشان گوساله‌ای پیکری که آن را است بانگی گفتند این است خدای شما و خدای موسی پس فراموش کرد'),
(2437, 20, 89, 'آیا نبینند که برنمی گرداند بدیشان سخنی را و نه دارا است برای ایشان زیان و نه سودی را'),
(2438, 20, 90, 'و همانا گفت بدیشان هارون پیش از این که‌ای قوم من جز این نیست که آزموده شدید بدان و همانا پروردگار شما خدای مهربان است پس پیرویم کنید و فرمان برید امر مرا'),
(2439, 20, 91, 'گفتند همیشه پیرامون آن بمانیم و پراکنده نمی‌شویم تا بازگردد بسوی ما موسی‌'),
(2440, 20, 92, 'گفت ای هارون چه بازداشت تو را هنگامی که دیدی اینان را گمراه شدند'),
(2441, 20, 93, 'که پیرویم نکردی آیا عصیان ورزیدی امر مرا'),
(2442, 20, 94, 'گفت ای فرزند مادرم نگیر ریش و نه سر مرا همانا ترسیدم که گوئی جدائی افکندی میان بنی اسرائیل و چشم به راه نماندی سخن مرا'),
(2443, 20, 95, 'گفت پس چیست کار تو ای سامری‌'),
(2444, 20, 96, 'گفت دیدم آنچه را ندیدندش پس برگرفتم مشتی از جای پای فرستاده را پس افکندمش و بدینسان آراست برای من دلم‌'),
(2445, 20, 97, 'گفت برو که تو را است در زندگی آنکه گوئی نیست سودنی (دست به من سوده نگرد) و همانا تو را وعده‌گاهی است که خلف کرده نشوی و بنگر بسوی خدایت آنکه پیوسته بر آن معتکفی که بسوزانیمش البتّه و سپس بپراکنیمش در دریا پراکندنی،'),
(2446, 20, 98, 'جز این نیست که خدای شما خداوندی است که نیست خدائی جز او فراگرفته است همه چیز را به دانش‌'),
(2447, 20, 99, 'بدینسان خوانیم بر تو از داستانهای آنچه بگذشت و همانا آوردیم تو را از نزد خویش یادآوری را'),
(2448, 20, 100, 'آنکه روی برگرداند از آن همانا بردارد روز قیامت گناهی را'),
(2449, 20, 101, 'جاودانند در آن و بد است ایشان را روز قیامت باری‌'),
(2450, 20, 102, 'روزی که دمیده شود در صور و گردآریم گنهکاران را در آن روز سبزچشمان (کور)'),
(2451, 20, 103, 'آهسته گویند میان خود را نماندید مگر ده روز'),
(2452, 20, 104, 'ما داناتریم بدانچه گویند گاهی که گوید بهترین ایشان در روش نماندید مگر یک روز'),
(2453, 20, 105, 'بپرسندت از کوه‌ها بگو بپراکندشان (برافشاندشان) پروردگار من پراکندنی‌'),
(2454, 20, 106, 'پس می‌گذارد آنها را زمینی هموار'),
(2455, 20, 107, 'که نبینی در آن کجی و نه بلندی را'),
(2456, 20, 108, 'در آن روز پیروی کنند دعوت‌کننده را نیست کجی برایش و پست شد آوازها برای خداوند مهربان پس نشنوی جز آهسته را'),
(2457, 20, 109, 'در آن روز سود ندهد شفاعت مگر آن را که اذن دادش خدای مهربان و خوشنود شد برایش گفتار را'),
(2458, 20, 110, 'می‌داند آنچه را پیش روی ایشان و آنچه پشت سر ایشان است فرانگیرندش به دانش‌'),
(2459, 20, 111, 'و خوار شدند چهره‌ها برای خدای زنده پاینده و همانا زیانکار شد آنکه برداشت ستمی را'),
(2460, 20, 112, 'و آنکه بکند از شایسته‌ها حالی که مؤمن باشد پس نترسد ستمی را و نه پایمال‌شدن را'),
(2461, 20, 113, 'و بدینسان فرستادیمش قرآنی عربی و گردانیدیم در آن از تهدید شاید پرهیزکاری کنند یا پدید آرد برای ایشان یادآوری را'),
(2462, 20, 114, 'پس برتر است خدا پادشاه حقّ و نشتاب به قرآن پیش از آنکه گذرانده شود بسوی تو وحیش و بگو پروردگارا بیفزای مرا دانش‌'),
(2463, 20, 115, 'و همانا پیمان بستیم با آدم (سپردیم به آدم) پیش از این پس فراموش کرد و نیافتیم برای او عزمی را'),
(2464, 20, 116, 'و هنگامی که گفتیم به فرشتگان سجده کنید برای آدم پس سجده کردند جز ابلیس که سرپیچید'),
(2465, 20, 117, 'پس گفتیم ای آدم همانا این است دشمنی برای تو و همسرت هان تا برون نکند شما را از بهشت که تیره‌بخت شوی‌'),
(2466, 20, 118, 'همانا تو را است که نه گرسنه شوی در آن و نه برهنه گردی‌'),
(2467, 20, 119, 'و آنکه نه تشنه شوی در آن و نه در آفتاب مانی‌'),
(2468, 20, 120, 'پس به ریو افکندش شیطان گفت ای آدم آیا راهبریت کنم بر درخت جاودانی و پادشاهیی که کهنه نگردد'),
(2469, 20, 121, 'پس خوردند از آن پس نمودار شد برای ایشان عورتهای ایشان و آغاز کردند گرد آوردند بر خود از برگهای بهشت و نافرمانی کرد آدم پروردگار خویش را پس زیانکار شد'),
(2470, 20, 122, 'سپس برگزید او را پروردگارش پس بازگشت بر او و هدایت کرد'),
(2471, 20, 123, 'گفت فرود شوید از آن همگی گروهی از شما گروهی را دشمن تا گاهی که بیاید شما را از من رهبری پس آنکه پیروی کند راهنمائی مرا نه گمراه شود و نه رنج برد (یا بدبخت شود)'),
(2472, 20, 124, 'و آنکه روی گرداند از یاد من همانا او را است زندگانی سخت و برون آریمش روز قیامت کور'),
(2473, 20, 125, 'گوید پروردگارا چرا مرا برون آوردی کور حالی که بودم بینا'),
(2474, 20, 126, 'گفت بدینسان آمدت آیتهای ما پس فراموش کردی آنها را و بدینگونه امروز فراموش شوی‌'),
(2475, 20, 127, 'و چنین پاداش دهیم آن را که اسراف کند و ایمان نیارد به آیتهای پروردگار خود و همانا عذاب آخرت سخت‌تر است و پایدارتر'),
(2476, 20, 128, 'آیا هدایت نشد برای ایشان (یا آیا هدایت نکردشان) آنکه بسا نابود کردیم پیش از ایشان از قرنهائی که می‌روند در نشیمنهای ایشان همانا در این است آیتهائی برای دارندگان خرد'),
(2477, 20, 129, 'و اگر نبود سخنی که پیش گرفته است از پروردگار تو و سرآمدی نامبرده همانا می‌گردید لازم‌'),
(2478, 20, 130, 'پس شکیبا شو بر آنچه گویند و تسبیح کن به ستایش پروردگار خویش پیش از برآمدن خورشید و پیش از فرو رفتن آن و از گاه‌های شب پس تسبیح کن و کنارهای روز شاید خوشنود شوی (یا خوشنود کنی)'),
(2479, 20, 131, 'و نگشای دیدگان خود را بدانچه کامیابی دادیم بدان مردان و زنانی را از ایشان شکوفه زندگانی دنیا تا بیازمائیمشان در آن و روزی پروردگار تو بهتر است و پاینده‌تر'),
(2480, 20, 132, 'و امر کن خاندان خویش را به نماز و شکیبائی گزین بر آن نخواهیم از تو روزیی را ما روزی دهیمت و فرجام است از آن پرهیزکاری‌'),
(2481, 20, 133, 'و گفتند چرا نیارد ما را آیتی از پروردگار خویش آیا نیامدشان نشانی (گواه) آنچه در نامه‌های پیشین است‌'),
(2482, 20, 134, 'و اگر نابودشان می‌ساختیم به شکنجه‌ای پیش از آن همانا می‌گفتند پروردگارا چرا نفرستادی بسوی ما پیمبری تا پیروی کنیم آیتهای تو را پیش از آنکه خوار شویم و رسوا گردیم‌'),
(2483, 20, 135, 'بگو همگی چشم به راهند پس چشم به راه باشید زود است بدانید کیستند صاحبان راه راست و کیست آنکه هدایت یافت‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'نزدیک شد برای مردم حساب ایشان و ایشانند در غفلتی روگردانان‌'),
(2485, 21, 2, 'نیایدشان یادآوریی از پروردگارشان نوین مگر بشنوندش و ایشانند بازی کنان‌'),
(2486, 21, 3, 'سرگرم است دلهای ایشان و نهان کردند رازگوئی را آنان که ستم کردند آیا این است جز بشری مانند شما پس آیا می‌آئید جادو را و شما می‌نگرید'),
(2487, 21, 4, 'گفت پروردگار من می‌داند سخن را در آسمان و زمین و او است شنوای دانا'),
(2488, 21, 5, 'بلکه گفتند خوابهائی است پریشان بلکه دروغ آوردش بلکه او است شاعری پس بیارد ما را آیتی بدانسان که فرستاده شدند پیشینان‌'),
(2489, 21, 6, 'ایمان نیاورد پیش از ایشان شهری که نابودش ساختیم پس آیا اینان ایمان می‌آورند'),
(2490, 21, 7, 'و نفرستادیم پیش از تو جز مردانی را که وحی می‌کردیم بسوی آنان پس بپرسید اهل کتاب را اگر نمی‌دانید'),
(2491, 21, 8, 'و نگردانیدیمشان کالبدی که نخورند خوراک را و نبودند جاودانان‌'),
(2492, 21, 9, 'پس راست آوردیمشان وعده را پس رهاشان ساختیم و هر که را خواهیم و نابود کردیم اسراف کنان را'),
(2493, 21, 10, 'و همانا فرستادیم بسوی شما نامه‌ای که در آن است یادآوری شما پس آیا بخرد نمی‌یابید'),
(2494, 21, 11, 'و چه بسا در هم شکستیم شهری را که بود ستمگر و پدید آوردیم پس از آن قومی دیگر را'),
(2495, 21, 12, 'تا گاهی که احساس کردند خشم ما را ناگاه از آن می‌دوند'),
(2496, 21, 13, 'ندوید و بازگردید بسوی آنچه هوسران بودید در آن و نشیمنهای خویش شاید پرسش شوید'),
(2497, 21, 14, 'گفتند ای وای بر ما همانا بودیم ما ستمگران‌'),
(2498, 21, 15, 'پس بود پیوسته این سخن ایشان تا گردانیدیمشان درویدگانی خموش‌'),
(2499, 21, 16, 'و نیافریدیم آسمان و زمین و آنچه را میان آنها است بازی‌کنان‌'),
(2500, 21, 17, 'اگر می‌خواستیم برگیریم سرگرمیی همانا برمی گرفتیمش از نزد خود اگر بودیم کنندگان‌'),
(2501, 21, 18, 'بلکه می‌افکنیم حقّ را بر باطل تا تباه سازدش که ناگهان آن است برافتاده و وای شما را از آنچه می‌ستائید'),
(2502, 21, 19, 'و از آنِ وی است آنکه در آسمانها و زمین است و آنان که نزد اویند سرنپیچند از پرستش او و نه خسته شوند'),
(2503, 21, 20, 'تسبیح می‌کنند شب و روز و سستی نمی‌کنند'),
(2504, 21, 21, 'آیا برگرفتند خدایانی از زمین که ایشان زنده کنند'),
(2505, 21, 22, 'اگر می‌بود در آنها خدایانی جز خدا هر آینه فاسد می‌شدند پس منزه است خدا پروردگار عرش از آنچه می‌ستایند'),
(2506, 21, 23, 'پرسش نشود از آنچه کند و ایشان پرسش شوند'),
(2507, 21, 24, 'یا برگرفتند جز وی خدایانی بگو بیارید برهان خود را این است یادآوری آنان که با منند و یادآوری آنان که پیش از منند بلکه بیشترشان نمی‌دانند حقّ را پس ایشانند روی‌گردانان‌'),
(2508, 21, 25, 'و نفرستادیم پیش از تو پیمبری مگر وحی کردیم بدو که نیست خدائی جز من پس مرا پرستش کنید'),
(2509, 21, 26, 'و گفتند برگرفت خداوند مهربان فرزندی منزه است او بلکه بندگانند گرامی‌'),
(2510, 21, 27, 'پیشی نگیرند بر او به گفتار و ایشانند به فرمان او کارکنندگان‌'),
(2511, 21, 28, 'می‌داند آنچه پیش روی ایشان و آنچه پشت سر ایشان و شفاعت نکنند جز برای هر که خواهد و ایشانند از بیم او هراسان‌'),
(2512, 21, 29, 'و آنکه بگوید از ایشان منم خدائی جز او پس آن را پاداش دهیم دوزخ بدینگونه کیفر دهیم ستمکاران را'),
(2513, 21, 30, 'آیا ندیدند آنان که کفر ورزیدند که آسمانها و زمین بسته بودند پس شکافتیم آنها را و گردانیدیم از آب هر چیز را زنده پس آیا ایمان نمی‌آورند'),
(2514, 21, 31, 'و نهادیم در زمین لنگرهائی نبادا کج شود بدیشان و نهادیم در آن شکافها و راه‌هائی شاید راهبری شوند'),
(2515, 21, 32, 'و گردانیدیم آسمان را پوششی نگاه داشته و ایشانند از آیتهای او روگردانان‌'),
(2516, 21, 33, 'و او است آنکه آفرید شب و روز و مهر و ماه را هر کدامند در گردونه شناوری‌کنان‌'),
(2517, 21, 34, 'و ننهادیم برای بشری پیش از تو جاودانی را پس آیا اگر بمیری تو ایشانند جاودانان‌'),
(2518, 21, 35, 'همه کس چشنده مرگ است و می‌آزمائیم شما را به بدی و خوبی آزمودنی و بسوی ما بازگردانیده شوید'),
(2519, 21, 36, 'و هر گاه بینندت آنان که کفر ورزیدند نگیرندت جز به استهزاء آیا این است آنکه یاد می‌کند خدایان شما را و آنانند به یاد خداوند مهربان کافران‌'),
(2520, 21, 37, 'آفریده شد انسان از شتاب زود است بنمایانمتان آیتهای خویش را پس شتاب نکنید'),
(2521, 21, 38, 'و گویند کی (چه هنگام) است این وعده اگر هستید راستگویان‌'),
(2522, 21, 39, 'اگر می‌دانستند آنان که کفر ورزیدند گاهی را که بازندارند از چهره‌های خویش آتش را و نه از پشتشان و نه ایشان یاری می‌شوند'),
(2523, 21, 40, 'بلکه می‌آیدشان ناگهان پس سراسیمه سازدشان که نتوانند بازگردانیدنش را و نه مهلت داده شوند'),
(2524, 21, 41, 'و همانا استهزاء شد به پیمبرانی پیش از تو پس فرودآمد بدانان که سخریه کردند از ایشان آنچه بودند بدان استهزاءکنان‌'),
(2525, 21, 42, 'بگو که نگه دارد شما را شب و روز از خداوند مهربان بلکه ایشانند از یاد پروردگار خویش روگردانان‌'),
(2526, 21, 43, 'یا ایشان را است خدایانی که بازدارند از ایشان جز ما نتوانند یاری کردن خویش را و نه از ما همراهی شوند'),
(2527, 21, 44, 'بلکه کامیابی دادیم بدیشان و پدرانشان تا دراز شد بر ایشان روزگار (عمر) آیا نبینند که می‌آئیم زمین را می‌کاهیمش از کنارهایش پس آیا ایشانند چیره‌شدگان‌'),
(2528, 21, 45, 'بگو جز این نیست که شما را ترسانم به وحی و نشنوند گنگان بانگ را گاهی که بیم داده شوند'),
(2529, 21, 46, 'و اگر رسدشان دمی از عذاب پروردگار تو هر آینه گویند وای بر ما که ما بودیم ستمگران‌'),
(2530, 21, 47, 'و برنهیم ترازوهای داد را برای روز قیامت پس ستم نشود کسی چیزی را و اگر باشد سنگینی دانه خردلی بیاریمش و بس باشیم ما حسابگران‌'),
(2531, 21, 48, 'و همانا دادیم به موسی و هارون جداکننده را و روشنائی و یادآوریی برای پرهیزکاران‌'),
(2532, 21, 49, 'آنان که می‌ترسند پروردگار خویش را پنهان و ایشانند از قیامت هراسان‌'),
(2533, 21, 50, 'و این است یادداشتی فرخنده فرستادیمش آیا شمائید آن را ناشناسان‌'),
(2534, 21, 51, 'و همانا دادیم به ابراهیم رهبریش را از پیش و بودیم بدان دانایان‌'),
(2535, 21, 52, 'هنگامی که گفت به پدر خود و قوم خود چیست این پیکرهایی که شمائید بر آنها گردآمدگان‌'),
(2536, 21, 53, 'گفتند یافتیم پدران خویش آنها را پرستش کنندگان‌'),
(2537, 21, 54, 'گفت همانا بوده‌اید شما و پدرانتان در گمراهی آشکار'),
(2538, 21, 55, 'گفتند آیا آورده‌ای ما را به حقّ یا توئی از بازی کنندگان‌'),
(2539, 21, 56, 'گفت بلکه پروردگار شما پروردگار آسمانها و زمین است آنکه پدید آورد آنها را و منم بر این از گواهان‌'),
(2540, 21, 57, 'و سوگند به خدا هر آینه بیاندیشم برای بتان شما پس از آنکه بروید پشت‌کنندگان‌'),
(2541, 21, 58, 'پس گردانیدشان پاره‌هائی مگر بزرگی برای ایشان شاید بسوی او بازگردند'),
(2542, 21, 59, 'گفتند آن که کرده است این را به خدایان ما همانا او است از ستمگران‌'),
(2543, 21, 60, 'گفتند شنیدیم جوانی یادشان می‌کرد که گفته می‌شد بدو ابراهیم‌'),
(2544, 21, 61, 'گفتند بیاریدش پیش چشم مردم شاید گواه باشند'),
(2545, 21, 62, 'گفتند آیا تو کردی این را به خدایان ما ای ابراهیم‌'),
(2546, 21, 63, 'گفت بلکه کرد آن را بزرگ ایشان این پس بپرسیدشان اگر هستند سخن‌گویان‌'),
(2547, 21, 64, 'پس بازگشتند به خود و گفتند همانا شمائید ستمگران‌'),
(2548, 21, 65, 'پس سرافکنده آمدند که تو دانستی اینان سخن نگویند'),
(2549, 21, 66, 'گفت آیا می‌پرستید جز خدا آنچه را سود ندهد به شما چیزی را و نه زیان رساند'),
(2550, 21, 67, 'اف بر شما و بر آنچه می‌پرستید جز خدا پس آیا بخرد نمی‌آیید'),
(2551, 21, 68, 'گفتند سخت بسوزانیدش و یاری کنید خدایان خویش را اگر هستید کنندگان‌'),
(2552, 21, 69, 'گفتیم ای آتش باش سرد و سلامت بر ابراهیم‌'),
(2553, 21, 70, 'و خواستند بدو نیرنگی را پس گردانیدیم ایشان را زیانکارتران‌'),
(2554, 21, 71, 'و رهانیدیم او و لوط را بسوی سرزمینی که برکت نهادیم در آن برای جهانیان‌'),
(2555, 21, 72, 'و بخشیدیم بدو اسحق و یعقوب را فزونی و هر یک را گردانیدیم شایستگان‌'),
(2556, 21, 73, 'و گردانیدیمشان پیشوایانی که رهبری کنند به فرمان ما و وحی کردیم بسوی ایشان به جای آوردن خوبیها را و بپا داشتن نماز و دادن زکات را و بودند برای ما پرستندگان‌'),
(2557, 21, 74, 'و لوط را دادیم بدو حکم و دانش را و رهانیدیمش از شهری که بود همی‌می‌کرد پلیدها را همانا بودند قومی زشت و نافرمانان‌'),
(2558, 21, 75, 'و درآوردیمش به رحمت خویش همانا او است از شایستگان‌'),
(2559, 21, 76, 'و نوح را هنگامی که برخواند پیش از این پس پذیرفتیم خواستش را و رهائیش دادیم او و خاندانش را از اندوه بزرگ‌'),
(2560, 21, 77, 'و یاریش کردیم از قومی که دروغ پنداشتند آیتهای ما را همانا بودند قومی زشت پس غرقشان ساختیم همگان‌'),
(2561, 21, 78, 'و داود و سلیمان هنگامی که حکومت می‌کردند در کشتزارگاهی که پراکنده شدند در آن گوسفندان قوم و بودیم حکومت آنان را گواهان‌'),
(2562, 21, 79, 'پس آموختیمش (فهماندیمش) به سلیمان و هر یک را دادیم حکمی و دانشی و فرمانبردار ساختیم با داود کوه‌ها را که تسبیح کنند و مرغان را و بودیم کنندگان‌'),
(2563, 21, 80, 'و آموختیمش ساختن پوشاکی را برای شما تا نگهداردتان از خشم خویش پس آیا هستید سپاسگزاران‌'),
(2564, 21, 81, 'و برای سلیمان باد را سخت وزنده روان می‌شد به فرمان او بسوی سرزمینی که برکت نهادیم در آن و بودیم به هر چیزی دانایان‌'),
(2565, 21, 82, 'و از شیاطین آنان را که به دریا فرو می‌رفتند برایش و می‌کردند کاری را جز آن و بودیم برای آنان نگهبانان‌'),
(2566, 21, 83, 'و ایّوب هنگامی که خواند پروردگار خویش را که مرا رسید رنج و توئی مهربانترین مهربانان‌'),
(2567, 21, 84, 'پس پذیرفتیم از او و بگشودیم آنچه بدو بود از رنج و دادیم بدو خاندانش را و مانند آنان با آنان رحمتی از نزد ما و یادآوریی برای پرستشگران‌'),
(2568, 21, 85, 'و اسمعیل و ادریس و ذوالکفل هر کدام از صبرکنندگان‌'),
(2569, 21, 86, 'و درآوردیمشان در رحمت خود همانا ایشانند از شایستگان‌'),
(2570, 21, 87, 'و صاحب ماهی (یا خداوند ماهی) هنگامی که رفت خشمگین پس پنداشت که هرگز بدو قدرت نیابیم پس بنالید در تاریکیها که نیست خدائی جز تو منزّهی تو همانا بودم من از ستمگران‌'),
(2571, 21, 88, 'پس پذیرفتیم از او و رهائیش دادیم از اندوه و بدینسان رهائی دهیم به مؤمنان‌'),
(2572, 21, 89, 'و زکریا هنگامی که برخواند پروردگار خویش را پروردگارا نگذار مرا تنها و تویی بهترین ارث‌برندگان‌'),
(2573, 21, 90, 'پس پذیرفتیم از او و بخشیدیم بدو یحیی را و شایسته گردانیدیم برایش همسرش را همانا بودند ایشان شتاب‌گیرندگان در خوبیها و می‌خواندند ما را امیدی و بیمی و بودند برای ما فروتنان‌'),
(2574, 21, 91, 'و آن زن که نگاه داشت فرجش را پس دمیدیم در آن از روح خویش و گردانیدیمش و پسرش را آیتی برای جهانیان‌'),
(2575, 21, 92, 'همانا این است ملت شما ملتی یگانه و منم پروردگار شما پس مرا بپرستید'),
(2576, 21, 93, 'و پخش کردند کار خویش را میان خویش هر کدامند بسوی ما بازگردانندگان‌'),
(2577, 21, 94, 'پس آنکو بکند از کردارهای شایسته و او است مؤمن نباشد ناسپاسیی کوشش او را و همانا مائیم برای آن نویسندگان‌'),
(2578, 21, 95, 'و حرام است بر شهری که نابودش کردیم که آنان بازنمی گردند'),
(2579, 21, 96, 'تا گاهی که گشوده شود یأجوج و مأجوج و ایشان از هر پشته‌ای سرازیر گردند (یا برون آیند)'),
(2580, 21, 97, 'و نزدیک شد وعده حقّ که ناگهان باز است دیده آنان که کفر ورزیدند ای وای بر ما همانا بودیم در غفلتی از این بلکه بودیم ما ستمگران‌'),
(2581, 21, 98, 'همانا شما و آنچه می‌پرستید جز خدا سوخت دوزخید شمائید در آن درآیندگان‌'),
(2582, 21, 99, 'اگر می‌بودند اینان خدایانی در آن نمی‌رفتند حالی که هر کدامند در آن جاودان‌'),
(2583, 21, 100, 'ایشان را است در آن آه‌کشیدنی و ایشان در آن نمی‌شنوند'),
(2584, 21, 101, 'همانا آنان که سبقت گرفت برای ایشان از ما نکوئی آنانند از آن دورشدگان‌'),
(2585, 21, 102, 'نشنوند حسش را و ایشانند در آنچه هوس کند دلهای ایشان جاودانان‌'),
(2586, 21, 103, 'اندوهگینشان نسازد آشوب بزرگتر و درآیند بر ایشان فرشتگان (یا پیشواز ایشان روند فرشتگان) که اینک روز شما آنکه بودید وعده داده می‌شدید'),
(2587, 21, 104, 'روزی که پیچیم آسمان را مانند پیچیدن نامه‌دان نامه‌ها را بدانسان که آغاز کردیم نخستین آفرینش را بازگردانیمش وعده است بر ما همانا هستیم ما کنندگان‌'),
(2588, 21, 105, 'و هر آینه نوشتیم در زبور پس از ذکر که زمین را ارث برند بندگان من شایستگان‌'),
(2589, 21, 106, 'همانا در این است ابلاغی برای گروهی که پرستشگرند'),
(2590, 21, 107, 'و نفرستادیمت مگر رحمتی برای جهانیان‌'),
(2591, 21, 108, 'بگو جز این نیست که وحی شود بسوی من همانا خداوند شما است خداوند یکتا پس آیا شمائید اسلام‌آرندگان‌'),
(2592, 21, 109, 'پس اگر پشت کردند بگو آگهیتان دادم یکسان و ندانم آیا نزدیک است یا دور آنچه وعده داده شوید'),
(2593, 21, 110, 'همانا او می‌داند آشکار را از سخن و می‌داند آنچه را نهان کنید'),
(2594, 21, 111, 'و ندانم شاید آزمایشی باشد برای شما و بهره‌مندیی تا زمانی‌'),
(2595, 21, 112, 'گفت پروردگارا حکم کن به حقّ و پروردگار ما خدای مهربان است یاری جسته بر آنچه می‌ستائید');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'ای مردم بترسید پروردگار خود را که لرزش قیامت چیزی است بزرگ‌'),
(2597, 22, 2, 'روزی که بینیدش فراموش کند هر شیردهنده از آنچه شیر دهد و بگذارد هر زن بارداری بار خود را و بینی مردم را مست و نیستند مستان و لیکن عذاب خدا است سخت‌'),
(2598, 22, 3, 'و از مردم است آنکه می‌ستیزد در خدا بی‌دانش و پیروی می‌کند هر شیطان سرکشی را'),
(2599, 22, 4, 'نوشته شد بر او که هر که پیرویش کند همانا گمراهش سازد و رهبریش کند بسوی عذاب سوزان‌'),
(2600, 22, 5, 'ای مردم اگر هستید در شکی از رستاخیز پس ما آفریدیم شما را از خاکی پس از چکه آبی پس از خونی بسته پس از پاره‌گوشتی جویده تمام آفریده و تمام ناآفریده تا بیان کنیم برای شما و نهیم در رحمها هر چه را خواهیم تا سرآمدی معین پس برون آریمتان کودکی پس تا رسید نیروهای خود را و از شما است آنکه دریافت شود (بمیرد) و از شما است آنکه بازگردانیده شود بسوی پس‌ترین عمر (زندگانی) تا نداند پس از دانستن چیزی را و ببینی زمین را فسرده پژمرده تا گاهی که فرستیم بر آن آب را بجنبد و برآید و برویاند از هر جفتی زیبا'),
(2601, 22, 6, 'این بدان است که خدا است حقّ و آنکه او زنده کند مردگان را و آنکه او است بر همه چیز توانا'),
(2602, 22, 7, 'و آنکه ساعت است آینده نیست شکی در آن و آنکه خدا برانگیزد آنان را که در گورند'),
(2603, 22, 8, 'و از مردم است آنکه بستیزد در خدا بی‌دانش و نه راهنمائی و نه نامه‌ای تابناک‌'),
(2604, 22, 9, 'برتابنده پهلوی خویش تا گمراه کند مردم را از راه خدا او را است در دنیا خواری و چشانیمش روز قیامت عذابی سوزان‌'),
(2605, 22, 10, 'این بدانچه پیش فرستاده است دستهایت و نیست خدا ستمگر بر بندگان‌'),
(2606, 22, 11, 'و از مردم است آنکه می‌پرستد خدا را بر نوکی (یا کناری) پس اگر رسدش خوشی برآساید بدان و اگر رسدش آزمایشی بازگردد بر روی خویش زیانمند است در دنیا و آخرت این است آن زیان آشکار'),
(2607, 22, 12, 'می‌خواند جز خدا آنچه را نه زیانش رساند و نه سودش دهد این است آن گمراهی دور'),
(2608, 22, 13, 'می‌خواند آن را که زیانش نزدیکتر است از سودش چه بد سرپرست و چه بد همدمی است‌'),
(2609, 22, 14, 'همانا خدا درآرد آنان را که ایمان آوردند و کردار شایسته کردند باغهائی که روان است زیر آنها جویها همانا خدا می‌کند آنچه را می‌خواهد'),
(2610, 22, 15, 'آنکو می‌پندارد که هرگز یاریش نمی‌کند خدا در دنیا و آخرت پس بکشد ریسمانی بسوی آسمان پس ببرد پس بنگرد آیا می‌برد نیرنگ او آنچه را به خشم می‌آورد'),
(2611, 22, 16, 'و بدینسان فرستادیمش آیتهائی روشن و آنکه خدا هدایت می‌کند هر که را خواهد'),
(2612, 22, 17, 'همانا آنان که ایمان آوردند و آنان که جهود شدند و صابیان و ترسایان و مجوس و آنان که شرک ورزیدند همانا خدا حکم می‌کند میان آنان روز قیامت همانا خدا است بر همه چیز گواه‌'),
(2613, 22, 18, 'آیا ندیدی که خدا سجده می‌کند برایش آنکه در آسمانها و آنکه در زمین است و مهر و ماه و ستارگان و کوه‌ها و درخت و جنبندگان و بسیاری از مردم و بسیاری که فرود آمد (استوار شد) بر او عذاب و آن را که خوار سازد خدا پس نیستش سرافرازنده‌ای همانا خدا می‌کند آنچه را می‌خواهد'),
(2614, 22, 19, 'اینان دو دشمنند که ستیزه کردند در پروردگار خویش پس آنان که کافر شدند بریده شد برای ایشان جامه هائی از آتش ریخته شود از فراز سرهای ایشان آب جوشان‌'),
(2615, 22, 20, 'می‌گدازد بدان آنچه در شکمهای ایشان است و پوستها'),
(2616, 22, 21, 'و ایشان را است گرزهائی از آهن‌'),
(2617, 22, 22, 'هر گاه خواهند برون آیند از آن از اندوهی بازگردانیده شوند در آن و بچشید عذاب سوزان را'),
(2618, 22, 23, 'همانا خدا درآرد آنان را که ایمان آوردند و کردار شایسته کردند باغهائی که روان است زیر آنها جویها زیب داده شوند در آن با دستبندهائی از زر و مروارید و جامه ایشان است در آن حریر'),
(2619, 22, 24, 'و رهبری شدند بسوی پاکیزه از سخن و رهبری شدند بسوی راه خداوند ستوده‌'),
(2620, 22, 25, 'همانا آنان که کفر ورزیدند و باز می‌دارند از راه خدا و مسجد حرام که گردانیدیمش برای مردم یکسان نشیمن گزیده (مانده) در آن و بیابانی (دشت‌نشین) و آنکه خواهد در آن کجروی را به ستم چشانیمش از عذابی دردناک‌'),
(2621, 22, 26, 'و هنگامی که جایگاه گردانیدم برای ابراهیم جای خانه را که شرک نورز به من چیزی را و پاک گردان خانه مرا برای طواف‌کنندگان و بپای‌ایستادگان و رکوع‌کنندگان سجده‌کنندگان‌'),
(2622, 22, 27, 'و بانگ برآور در مردم به حج آیندت پیادگان و بر هر اشتری لاغر که می‌آیند از هر دره‌ای ژرف‌'),
(2623, 22, 28, 'تا بنگرند سودهائی برای خویش و ببرند نام خدا را در روزهائی دانسته بر آنچه روزیشان داده است از دامهای چهارپا پس بخورید از آن و بخورانید به بینوای درویش‌'),
(2624, 22, 29, 'پس تا بگذارند چرک (آلایش) خویش را و وفا کنند به نذرهای خود و طواف کنند گرد خانه کهن‌'),
(2625, 22, 30, 'این و هر که بزرگ شمرد حرمتهای خدا را پس آن بهتر است برای او نزد پروردگارش و حلال شد برای شما چهارپایان مگر آنچه خوانده شود بر شما پس دوری گزینید از پلیدی یعنی بتان و دوری گزینید از سخن ناروا'),
(2626, 22, 31, 'یکتاپرستان برای خدا نه شرک‌ورزندگان بدو و آنکه شرک ورزد به خدا پس گوئیا درافتاده است از آسمان تا بربایدش پرنده یا بپراکندش باد به جائی دور (یا رانده)'),
(2627, 22, 32, 'این و هر که بزرگ شمرد شعارهای خدا را همانا آن است از پرهیزکاری دلها'),
(2628, 22, 33, 'برای شما است در آن سودهائی تا سرآمدی نامبرده سپس فرودگاهش بسوی خانه است کهن‌'),
(2629, 22, 34, 'و برای هر ملتی نهادیم پرستشی تا ببرند نام خدا را بر آنچه روزیشان داد از دامهای چهارپا و خدای شما خداوند یکتا است پس برای او اسلام آرید و مژده ده به فروتنان‌'),
(2630, 22, 35, 'آنان که هر گاه یاد شود خدا بترسد دلهاشان و شکیبایان بر آنچه رسدشان و بپادارندگان نماز و از آنچه روزیشان دادیم ببخشند'),
(2631, 22, 36, 'و اشتران فربه (قربانی) گردانیدیم آنها را برای شما از شعارهای خدا شما را است در آنها خوبیی پس ببرید نام خدا را بر آنها صف‌آرندگان تا گاهی که بیفتد پهلوهای آنها بخورید از آنها و بخورانید بینوا و دریوزه را بدینسان رام گردانیدیمشان برای شما شاید شما سپاسگزارید'),
(2632, 22, 37, 'هرگز نرسد خدا را گوشتها و نه خونهای آنها و لیکن رسدش پرهیزکاری از شما بدینگونه رام کردیم آنها را برای شما تا بزرگ شمرید خدا را بر آنچه راهبریتان کرد و بشارت ده نیکوکاران‌'),
(2633, 22, 38, 'همانا خدا دفاع کند از آنان که ایمان آوردند همانا خدا دوست ندارد هر خیانتکار ناسپاسی را'),
(2634, 22, 39, 'رخصت داده شد (یا اعلام شد) بدانان که پیکار می‌شوند که ستم شدند و همانا خدا است بر یاری آنان توانا'),
(2635, 22, 40, 'آنان که برون رانده شدند از خانه‌های خود به ناحقّ‌ّ جز آنکه می‌گفتند پروردگار ما خدا است و اگر نبود دورساختن خدا مردم را گروهی با گروهی همانا ویران می‌شدند پرستشگاه‌ها و کلیساها و نمازها و مسجدهائی که برده شود در آنها نام خدا بسیار و البته یاری کند خدا هر که را یاریش کند همانا خدا است توانای عزتمند'),
(2636, 22, 41, 'آنان که اگر فرمانروائیشان دهیم در زمین بپای دارند نماز را و بدهند زکات را و فرمان رانند به خوبی و بازدارند از بدی و برای خدا است فرجام کارها'),
(2637, 22, 42, 'و اگر به دروغ گیرندت همانا به دروغ گرفتید پیش از ایشان قوم نوح و عاد و ثمود'),
(2638, 22, 43, 'و قوم ابراهیم و قوم لوط'),
(2639, 22, 44, 'و یاران مدین و دروغگو شمرده شد موسی پس مهلت دادم به کافران سپس گرفتم ایشان را پس چگونه بود برابری کردن و انتقام من‌'),
(2640, 22, 45, 'و بسا شهری که نابودش کردیم و او است ستمگر پس آن است فرود آمده بر پایه‌های خود و چاهی ویران و کاخی افراشته‌'),
(2641, 22, 46, 'آیا نگشتند در زمین تا باشدشان دلهائی که دریابند بدانها یا گوشهائی که بشنوند بدانها زیرا کور نشوند دیدگان و لیکن کور شوند دلهائی که درون سینه‌ها است‌'),
(2642, 22, 47, 'و شتاب خواهند از تو در عذاب و هرگز نشکند خدا وعده خود را و همانا روزی نزد پروردگار تو مانند هزار سال است از آنچه می‌شمرند'),
(2643, 22, 48, 'و بسا شهری که مهلت دادیم بدان حالی که ستمکار بود و سپس گرفتیم آن را و بسوی من است بازگشت‌'),
(2644, 22, 49, 'بگو ای مردم جز این نیست که منم برای شما ترساننده‌ای آشکار'),
(2645, 22, 50, 'پس آنان که ایمان آوردند و کردار شایسته کردند برای ایشان است آمرزشی و روزیی گرامی‌'),
(2646, 22, 51, 'و آنان که بشتافتند در آیتهای ما به عجزآرندگان آنانند یاران دوزخ‌'),
(2647, 22, 52, 'و نفرستادیم پیش از تو فرستاده‌ای و نه پیمبری مگر هر گاه آرزو می‌کرد می‌افکند شیطان در آرزوی او پس برمی‌انداخت خدا آنچه را شیطان می‌افکند سپس استوار می‌ساخت خدا آیتهای خویش را و خدا است دانای حکیم‌'),
(2648, 22, 53, 'تا بگرداند آنچه را می‌افکند شیطان آزمایشی برای آنان که در دلهاشان بیماری است و سنگین‌دلان و همانا ستمگرانند در دشمنیی دور و دراز'),
(2649, 22, 54, 'و تا بدانند آنان که داده شدند دانش را که آن است حقّ از پروردگار تو پس ایمان آرند بدان پس نرم شود برای آن دلهاشان و همانا خدا راهنمای آنانی است که ایمان آوردند بسوی راهی راست‌'),
(2650, 22, 55, 'و پیوسته باشند آنان که کفر ورزیدند در شکی از آن تا بیایدشان ساعت ناگهان یا بیایدشان عذاب روزی نازا (بی‌مانند)'),
(2651, 22, 56, 'پادشاهی در آن روز از آن خدا است حکم کند میان ایشان پس آنان که ایمان آوردند و کردار شایسته کردند در باغستان نعمتند'),
(2652, 22, 57, 'و آنان که کفر ورزیدند و تکذیب کردند آیتهای ما را ایشان را است عذابی خوارکننده‌'),
(2653, 22, 58, 'و آنان که از خانمان آواره شدند در راه خدا سپس کشته شدند یا مردند همانا خدا روزیشان دهد روزیی نکو و همانا خدا است بهترین روزی‌دهندگان‌'),
(2654, 22, 59, 'هر آینه درآردشان خدا جایگاهی که بپسندندش و هر آینه خدا است دانای بردبار'),
(2655, 22, 60, 'این و هر که کیفر دهد مانند آنچه را شکنجه شده است پس ستم شود بر او هر آینه یاریش کند خدا و همانا خدا است درگذرنده آمرزگار'),
(2656, 22, 61, 'این بدان است که خدا فرو می‌برد شب را در روز و فرو می‌برد روز را در شب و آنکه خدا است شنوای بینا'),
(2657, 22, 62, 'این بدان است که خدا است حقّ و آنچه می‌خوانند جز او است باطل و آنکه خدا است برتر بزرگ‌'),
(2658, 22, 63, 'آیا ندیدی که خدا فرستاد از آسمان آبی پس گردید زمین سرسبز همانا خداوند است نوازنده آگاه‌'),
(2659, 22, 64, 'برای او است آنچه در آسمانها و آنچه در زمین است و همانا خدا است بی‌نیاز ستوده‌'),
(2660, 22, 65, 'آیا ندیدی که خدا رام کرد برای شما آنچه در زمین است و کشتی که می‌رود در دریا به فرمانش و نگاه می‌دارد آسمان را که فرود آید بر زمین مگر با اذن او همانا خدا به مردم است نوازنده مهربان‌'),
(2661, 22, 66, 'و او است آنکه زنده کرد شما را پس بمیراندتان و سپس زنده سازدتان و همانا انسان است بسیار ناسپاس‌'),
(2662, 22, 67, 'برای هر ملتی نهادیم روشی که ایشانند رونده آن پس نستیزند با تو در کار و برخوان بسوی پروردگار خود که توئی همانا بر هدایتی راست‌'),
(2663, 22, 68, 'و اگر پرخاش کردند با تو بگو خدا داناتر است بدانچه می‌کنید'),
(2664, 22, 69, 'خدا حکم کند میان شما روز قیامت در آنچه بودید در آن اختلاف می‌کردید'),
(2665, 22, 70, 'آیا ندانستی که خدا می‌داند آنچه در آسمان و زمین است همانا این است در نامه‌ای همانا این است بر خدا آسان‌'),
(2666, 22, 71, 'و پرستش کنند جز خدا آنچه را نفرستاد بدان فرمانروایی و آنچه نیستشان بدان دانشی و نیست ستمکاران را یاوری‌'),
(2667, 22, 72, 'و هر گاه خوانده شود بر ایشان آیتهای ما روشن بشناسی در چهره‌های آنان که کفر ورزیدند بدی را نزدیک است بتازند بر آنان که می‌خوانند بر ایشان آیتهای ما را بگو آیا آگهیتان دهم به بدتر از این آتش وعده دادش خدا بدانان که کفر ورزیدند و بد بازگشتگاهی است‌'),
(2668, 22, 73, 'ای مردم زده شد مثلی پس بشنویدش همانا آنان که می‌خوانید جز خدا هرگز نیافرند مگسی را و هر چند گرد آیند برای آن و اگر بربایدشان مگس چیزی را نستانندش از آن ناتوانند خواهنده و خواسته‌'),
(2669, 22, 74, 'ارج نگذاشتند خدا را حقّ ارجمندیش همانا خدا است توانای عزتمند'),
(2670, 22, 75, 'خدا بگزیند از فرشتگان فرستادگانی و از مردم همانا خدا است شنوای بینا'),
(2671, 22, 76, 'داند آنچه پیش روی ایشان و آنچه پشت سر ایشان است و بسوی خدا بازگردانیده شوند کارها'),
(2672, 22, 77, 'ای آنان که ایمان آوردید رکوع گزارید و سجده کنید و پرستش کنید پروردگار خود را و بکنید خوبی را شاید رستگار شوید'),
(2673, 22, 78, 'و بکوشید در راه خدا سزای کوشش او او برگزید شما را و ننهاد بر شما در دین رنجی را ملت پدر شما ابراهیم او نامیدتان مسلمانان از پیش و در این (قرآن) تا باشد پیمبر گواهی بر شما و باشید گواهان بر مردم پس بپا دارید نماز را و بدهید زکات را و نگهداری از خدا جوئید او است سرپرست شما چه خوب سرپرست و چه خوب یاوری است‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'همانا رستگار شدند مؤمنان‌'),
(2675, 23, 2, 'آنان که در نماز خویشند فروتنان‌'),
(2676, 23, 3, 'و آنان که از یاوه (بیهده)اند روی‌گردانان‌'),
(2677, 23, 4, 'و آنانکه هستند زکات را کنندگان‌'),
(2678, 23, 5, 'و آنان که برای فرجهای خویشتند نگهدارندگانند'),
(2679, 23, 6, 'مگر بر همسران خود یا آنچه دارا است دستهای ایشان که ایشانند نانکوهیدگان‌'),
(2680, 23, 7, 'و آنکو خواهد ماورای این را پس آنانند تجاوزکنندگان‌'),
(2681, 23, 8, 'و آنان که امانتها و عهدهای خویش را هستند رعایت‌کنندگان‌'),
(2682, 23, 9, 'و آنان که بر نمازهای خویشند مواظبت‌کنندگان‌'),
(2683, 23, 10, 'آنانند ارث‌برندگان‌'),
(2684, 23, 11, 'که ارث برند بهشت را ایشانند در آن جاودانان‌'),
(2685, 23, 12, 'و همانا آفریدیم انسان را از کشیده‌ای (یا چکیده) از گل‌'),
(2686, 23, 13, 'پس گردانیدیمش چکه آبی در آرامگاهی جایدار'),
(2687, 23, 14, 'پس آفریدیم نطفه (چکه) را خونی بسته پس آفریدیم خون را گوشتی جویده پس آفریدیم گوشت را استخوانهائی پس پوشانیدیم استخوانها را گوشتی سپس پدید آوردیمش آفرینشی دیگر پس خجسته باد خدا بهترین آفرینندگان‌'),
(2688, 23, 15, 'سپس شمائید پس از آن هر آینه مردگان‌'),
(2689, 23, 16, 'سپس همانا شما روز قیامت برانگیخته می‌شوید'),
(2690, 23, 17, 'و همانا آفریدیم فراز شما هفت راه و نبودیم از آفرینش ناآگاهان‌'),
(2691, 23, 18, 'و فرستادیم از آسمان آبی به اندازه‌ای پس جایگزین ساختیمش در زمین و همانا مائیم بر بردن آن توانایان‌'),
(2692, 23, 19, 'پس پدید آوردیم برای شما بدان باغهائی از خرمابنها و انگورها شما را است در آن میوه‌هائی فراوان و از آن می‌خورید'),
(2693, 23, 20, 'و درختی که برون می‌آید از طور سیناء می‌روید با روغن و رنگی (نان خورشی) برای خورندگان‌'),
(2694, 23, 21, 'و همانا شما را است در چهارپایان عبرتی بنوشانیمتان از آنچه در شکمهای آنها است و برای شما است در آنها سودهای فراوان و از آنها می‌خورید'),
(2695, 23, 22, 'و بر آنها و بر کشتی بار می‌شوید'),
(2696, 23, 23, 'و همانا فرستادیم نوح را بسوی قومش گفت ای قوم من پرستش کنید خدا را نیست شما را خدایی جز او آیا نمی‌ترسید'),
(2697, 23, 24, 'پس گفتند گروهی که کفر ورزیدند از قومش نیست این مگر بشری همانند شما خواهد برتری جوید بر شما و اگر می‌خواست خدا هر آینه می‌فرستاد فرشتگانی نشنیدیم بدین در پدران ما پیشینیان‌'),
(2698, 23, 25, 'نیست او جز مردی که بدو است دیوانگی پس انتظار کشید بدو تا زمانی‌'),
(2699, 23, 26, 'گفت پروردگارا یاریم کن بر آنچه دروغگویم شمردند'),
(2700, 23, 27, 'پس وحی کردیم بدو که بساز کشتی را به دیدگان ما و به وحی ما تا گاهی که آید فرمان ما و بجوشد تنور پس جای ده در آن از هر کدام دو جفت را و خاندان خویش را مگر آنکه پیشی گرفت بر او سخن از ایشان و سخن نگوی با من در آنان که ستم کردند که ایشانند غرق‌شدگان‌'),
(2701, 23, 28, 'پس گاهی که استوار شدی تو و آنکه با تو است در کشتی بگو سپاس خدائی را که رهائید ما را از گروه ستمگران‌'),
(2702, 23, 29, 'و بگو پروردگارا فرود آور مرا فرودگاهی فرخنده و توئی بهترین فرودآرندگان‌'),
(2703, 23, 30, 'همانا در این است آیتهائی و همانا هستیم ما آزمایش‌کنندگان‌'),
(2704, 23, 31, 'سپس پدید آوردیم پس از ایشان قرنی دیگر را'),
(2705, 23, 32, 'پس فرستادیم در ایشان فرستاده‌ای از ایشان که پرستش کنید خدا را نیست شما را خداوندی جز او آیا نمی‌ترسید'),
(2706, 23, 33, 'و گفتند آن گروه از قومش که کفر ورزیدند و دروغ پنداشتند رسیدن را به آخرت و کامرائیشان دادیم در زندگانی دنیا نیست این جز بشری مانند شما می‌خورد از آنچه می‌خورید از آن و می‌نوشد از آنچه می‌نوشید'),
(2707, 23, 34, 'و اگر فرمان برید بشری را همانند خویش شمائید در آن هنگام زیانکاران‌'),
(2708, 23, 35, 'آیا وعده دهد شما را گاهی که مُردید و شدید خاکی و استخوانهائی آنکه شمائید برون‌آوردگان‌'),
(2709, 23, 36, 'دور است دور آنچه وعده داده می‌شوید'),
(2710, 23, 37, 'نیست آن جز زندگانی نزدیک ما می‌میریم و زنده می‌شویم و نیستیم برانگیختگان‌'),
(2711, 23, 38, 'نیست او مگر مردی که بسته است بر خدا دروغی را و نیستیم ما بدو ایمان‌آرندگان‌'),
(2712, 23, 39, 'گفت پروردگارا یاریم کن بدانچه تکذیبم کردند'),
(2713, 23, 40, 'گفت پس از اندکی هر آینه خواهید گردید پشیمان‌'),
(2714, 23, 41, 'پس گرفتشان خروشی به حقّ و گردانیدیمشان خاشاکی به روی سیل پس دور باد برای گروه ستمگران‌'),
(2715, 23, 42, 'سپس پدید آوردیم پس از ایشان قرنهائی دگران‌'),
(2716, 23, 43, 'سبقت نگیرند هیچ ملتی سرآمد خویش را و نه پس افتند'),
(2717, 23, 44, 'سپس فرستادیم فرستادگان خود را پی‌درپی هر گاه می‌آمد ملتی را فرستاده‌اش تکذیبش می‌کردند پس پیرو گردانیدیم گروهی را از برای گروهی و گردانیدیمشان داستانهائی پس دورباد برای گروهی که ایمان نمی‌آورند'),
(2718, 23, 45, 'پس فرستادیم موسی و برادرش هارون را به آیتهای خویش و فرمانروائی آشکار'),
(2719, 23, 46, 'بسوی فرعون و گروهش پس سرکشیدند و شدند قومی برتری‌جویان‌'),
(2720, 23, 47, 'پس گفتند آیا ایمان آریم برای دو بشر همانند ما و قوم آنانند برای ما پرستش‌کنندگان‌'),
(2721, 23, 48, 'پس تکذیبشان کردند پس شدند از هلاک‌شدگان‌'),
(2722, 23, 49, 'و همانا دادیم به موسی کتاب را شاید ایشان رهبری شوند'),
(2723, 23, 50, 'و گردانیدیم پسر مریم و مادرش را آیتی و پناهشان دادیم بسوی فرازی دارای آرامش و آبی گوارا'),
(2724, 23, 51, 'ای پیمبران بخورید از پاکیزه‌ها و بکنید کاری شایسته همانا منم بدانچه می‌کنید دانا'),
(2725, 23, 52, 'و همانا این است ملت شما ملتی یگانه و منم پروردگار شما پس مرا بترسید'),
(2726, 23, 53, 'پس پخش کردند کار خود را میان خود نامه‌هائی هر حزبی است بدانچه نزد ایشان است شادمان‌'),
(2727, 23, 54, 'پس بگذارشان در فرورفتگیشان تا زمانی‌'),
(2728, 23, 55, 'آیا پندارند که آنچه کمک دهیمشان بدان از مال و فرزندان‌'),
(2729, 23, 56, 'شتاب کنیم برای ایشان در خوبیها بلکه درنمی‌یابند'),
(2730, 23, 57, 'همانا آنان که از ترس پروردگار خویشند هراسان‌'),
(2731, 23, 58, 'و آنان که به آیتهای پروردگار خویشند ایمان‌آرندگان‌'),
(2732, 23, 59, 'و آنان که به پروردگار خویش شرک نورزند'),
(2733, 23, 60, 'و آنان که دهند آنچه را دهند و دلهاشان لرزان است که بسوی پروردگار خویشند بازگشت‌کنان‌'),
(2734, 23, 61, 'آنان می‌شتابند در خوبیها و ایشانند آنها را پیشی‌گیرندگان‌'),
(2735, 23, 62, 'و تکلیف نکنیم کس را جز به اندازه گشایش او (تاب و توانش) و نزد ما است نامه‌ای که سخن گوید به حقّ و ایشان ستم نمی‌شوند'),
(2736, 23, 63, 'بلکه دلهاشان در گردابی است از این و ایشان را است کارهائی جز این که ایشانند آنها را کنندگان‌'),
(2737, 23, 64, 'تا گاهی که گیریم هوسرانان (یا فرورفتگان) ایشان را به عذاب ناگهان فغان برآرند'),
(2738, 23, 65, 'ننالید امروز همانا شما از ما یاری نشوید'),
(2739, 23, 66, 'بودند آیتهای ما خوانده می‌شدند بر شما پس بودید بر پاشنه‌های خویش برمی‌گشتید'),
(2740, 23, 67, 'کبرورزندگان بدان شب‌نشینی بیهده‌گویان‌'),
(2741, 23, 68, 'آیا تدبر نکردند سخن را یا بیامدشان آنچه نیامد پدرانشان را آن پیشینیان‌'),
(2742, 23, 69, 'یا نشناختند پیمبر خویش را پس ایشانند او را انکارکنان (ناشناسان)'),
(2743, 23, 70, 'یا گویند بدو آشفتگی است بلکه بیامدشان به حقّ و بیشتر ایشانند حقّ را ناخوش‌دارندگان‌'),
(2744, 23, 71, 'و اگر پیروی کند حقّ هوسهای آنان را هر آینه فاسد شود آسمانها و زمین و هر که در آنها است بلکه آوردیمشان به ذکر خود پس ایشانند از ذکر خود روی‌گردانان‌'),
(2745, 23, 72, 'آیا پرسیشان هزینه پس هزینه پروردگار تو بهتر است و او است بهترین روزی‌دهندگان‌'),
(2746, 23, 73, 'و همانا تو خوانیشان بسوی راهی راست‌'),
(2747, 23, 74, 'و همانا آنان که ایمان نمی‌آرند به آخرت از راهند کناره‌گیران (کجروان)'),
(2748, 23, 75, 'و اگر رحم کنیم ایشان را و بگشائیم آنچه بدیشان است از رنج همانا خیرگی کنند در سرکشی خود فروروندگان‌'),
(2749, 23, 76, 'و همانا گرفتیمشان به عذاب پس فروتن نشدند برای پروردگار خویش و نه زاری کنند'),
(2750, 23, 77, 'تا گاهی که برگشودیم بر ایشان دری را دارای عذابی سخت ناگهان ایشانند در آن سراسیمه یا نومیدان‌'),
(2751, 23, 78, 'و او است آنکه پدید آورد برای شما گوش و دیده‌گان و دلها را به کمی سپاسگزارید'),
(2752, 23, 79, 'و او است آنکه آفرید شما را از زمین و بسوی او گردآورده شوید'),
(2753, 23, 80, 'و او است آنکه زنده کند و بمیراند و برای او است گردش شبانه روز آیا بخرد نمی‌یابید'),
(2754, 23, 81, 'بلکه گفتند همانند آنچه گفتند پیشینیان‌'),
(2755, 23, 82, 'گفتند آیا گاهی که مردیم و شدیم خاکی و استخوانهائی آیا مائیم برانگیختگان‌'),
(2756, 23, 83, 'همانا وعده داده شدیم ما و پدران ما این را از پیش نیست این جز افسانه‌های پیشینیان‌'),
(2757, 23, 84, 'بگو از آنِ کیست زمین و آنکه در آن است اگر هستید دانایان‌'),
(2758, 23, 85, 'زود است گویند برای خدا بگو پس چرا یادآور نمی‌شوید'),
(2759, 23, 86, 'بگو کیست پروردگار آسمانهای هفت‌گانه و پروردگار عرش بزرگ‌'),
(2760, 23, 87, 'بزودی گویند از آن خدا بگو پس چرا پرهیزکاری نکنید'),
(2761, 23, 88, 'بگو کیست که به دست او است پادشاهیهای همه چیز و او پناه دهد و بر او پناه داده نشود اگر می‌دانید'),
(2762, 23, 89, 'زود است گویند برای خدا بگو پس به کجا افسون می‌شوید'),
(2763, 23, 90, 'بلکه آوردیمشان به حقّ و همانا ایشانند دروغگویان‌'),
(2764, 23, 91, 'برنگرفت خدا فرزندی و نبود با وی خدائی چه در آن هنگام می‌برد هر خدائی آنچه را بیافریده است و برتری می‌جست برخی از ایشان بر برخی منزّه است خدا از آنچه می‌ستایند'),
(2765, 23, 92, 'دانای نهان و هویدا پس برتر است از آنچه شرک ورزند'),
(2766, 23, 93, 'بگو پروردگارا اگر بنمایانیم آنچه را وعده داده شوند'),
(2767, 23, 94, 'پروردگارا پس نگردان مرا در گروه ستمگران‌'),
(2768, 23, 95, 'و همانا مائیم بر آنکه بنمایانیمت آنچه را وعده بدیشان دهیم توانایان‌'),
(2769, 23, 96, 'دور کن بدانچه آن بهتر است بدی را ما داناتریم بدانچه می‌ستایند'),
(2770, 23, 97, 'و بگو پروردگارا پناه برم به تو از ریوهای شیاطین‌'),
(2771, 23, 98, 'و پناه برم به تو پروردگارا از آنکه حاضر آیندم‌'),
(2772, 23, 99, 'تا گاهی که بیاید یکیشان را مرگ گوید پروردگارا بازگردانید مرا'),
(2773, 23, 100, 'شاید کرداری شایسته کنم در آنچه بازگذاردم نه چنین است همانا آن است سخنی که او است گوینده آن و از پشت سر آنان است برزخی (دیواری) تا روزی که برانگیخته شوند'),
(2774, 23, 101, 'تا گاهی که دمیده شود در صور نباشد تباری میانشان در آن روز و نه از همدیگر پرسند'),
(2775, 23, 102, 'پس آنکو سنگین شود سنجهایش آنانند رستگاران‌'),
(2776, 23, 103, 'و آنکه سبک شود سنجشهایش پس آنانند که زیان کردند خویش را در دوزخند جاودانان‌'),
(2777, 23, 104, 'بریان سازد رویهای ایشان را آتش و ایشانند در آن چهره‌سوختگان‌'),
(2778, 23, 105, 'آیا نبود آیتهایم خوانده می‌شد بر شما پس بودید بدانها تکذیب‌کنندگان‌'),
(2779, 23, 106, 'گفتند پروردگارا چیره گشت بر ما تیره روزی ما و شدیم گروهی گمراهان‌'),
(2780, 23, 107, 'پروردگارا برون آر ما را از آن سپس اگر بازگشتیم همانا مائیم ستمکاران‌'),
(2781, 23, 108, 'گفت گم شوید (نهیبی که به سگ می‌زنند) در آن و با من سخن نگوئید'),
(2782, 23, 109, 'همانا بودند گروهی از بندگانم می‌گفتند پروردگارا ایمان آوردیم پس بیامرز ما را و رحم کن بر ما و توئی بهترین رحم‌کنندگان‌'),
(2783, 23, 110, 'پس برگرفتیدشان مسخره تا فراموشتان کردند یاد مرا و بودید بدیشان خنده‌زنان‌'),
(2784, 23, 111, 'همانا پاداششان دادم امروز بدانچه شکیبا شدند که ایشانند رستگاران‌'),
(2785, 23, 112, 'گفت چند ماندید در زمین به شمار سالیان‌'),
(2786, 23, 113, 'گفتند ماندیم روزی یا پاره روزی پس بپرس از شمارندگان‌'),
(2787, 23, 114, 'گفت نماندید مگر اندکی اگر بودید می دانستید'),
(2788, 23, 115, 'پس آیا پنداشتید که شما را آفریدیم بیهده و آنکه شما بسوی ما بازنمی‌گردید'),
(2789, 23, 116, 'پس برتر است خدا پادشاه حقّ نیست خدائی جز او پروردگار عرش گرامی‌'),
(2790, 23, 117, 'و آنکو بخواند با خدا خدائی دیگر را که نیستش حجتی بر آن جز این نیست که حسابش نزد پروردگار او است و همانا رستگار نشوند کافران‌'),
(2791, 23, 118, 'و بگو پروردگارا بیامرز و رحم کن و توئی بهترین رحم‌کنندگان‌');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'سورتی است (بخشی است) فرستادیمش و بایسته داشتیمش و فرستادیم در آن آیتهائی روشن شاید یاد آورید'),
(2793, 24, 2, 'زن زناکار و مرد زناکار بزنید هر کدام را از آنان صد ضربه (تازیانه) و نگیردتان بدانان رحمی در دین خدا اگر ایمان دارید به خدا و روز بازپسین و باید گواه باشند شکنجه آنان را گروهی از مؤمنان‌'),
(2794, 24, 3, 'مرد زناکار همسر نگیرد جز زناکاری یا مشرکی را و زن زناکار همسر نشود او را مگر زناکاری یا مشرکی و حرام است آن بر مؤمنان‌'),
(2795, 24, 4, 'و آنان که پرتاب کنند بسوی زنان پارسا و سپس نیارند چهار تن گواهان پس بزنیدشان هشتاد تازیانه و نپذیرید برای ایشان گواهیی هیچگاه و آنانند نافرمانان‌'),
(2796, 24, 5, 'مگر آنان که بازگشتند از این پس و شایستگی گزیدند که خداوند است آمرزنده مهربان‌'),
(2797, 24, 6, 'و آنان که پرتاب کنند بسوی زنان خود (پرتاب‌کردن بمعنی نسبت زنا دادن است) و نباشدشان گواهانی جز خویشتن پس گواهی یکیشان چهار گواهی است سوگند به خدا که آن مرد است از راستگویان‌'),
(2798, 24, 7, 'و پنجمین آنکه لعنت خدا باد بر او اگر باشد از دروغگویان‌'),
(2799, 24, 8, 'و دور می‌کند از زن شکنجه را که گواهی دهد چهار گواهی سوگند به خدا که آن مرد است از دروغگویان‌'),
(2800, 24, 9, 'و پنجمین آنکه خشم خدا بر او باد اگر آن مرد است از راستگویان‌'),
(2801, 24, 10, 'و اگر نبود فضل خدا بر شما و رحمتش و آنکه خدا است توبه‌پذیرنده حکیم‌'),
(2802, 24, 11, 'همانا آنان که آوردند تهمت را گروهی هستند از شما مپنداریدش بد برای شما بلکه آن خوب است برای شما هر مردی از ایشان است آنچه دست آورد است از گناه و آنچه برگرفته است بیشی از آن را از ایشان برای او است عذابی بزرگ‌'),
(2803, 24, 12, 'چرا هنگامی که شنیدیدش گمان نبردند مردان و زنان مؤمن به همدیگر خوبی را و نگفتند این است تهمتی آشکار'),
(2804, 24, 13, 'چرا نیاوردند بر آن چهار تن گواهان پس چون نیاوردند گواهان را آنانند نزد خدا دروغگویان‌'),
(2805, 24, 14, 'و اگر نبود فضل خدا بر شما و رحمتش در دنیا و آخرت هر آینه می‌رسید شما را در آنچه فرو رفتید در آن عذابی بزرگ‌'),
(2806, 24, 15, 'هنگامی که فرامی‌گرفتیدش به زبانهای خود و می‌گفتید با دهانهای خویش آنچه نبودتان بدان علمی و می‌پنداشتیدش آسان حالی که آن است نزد خدا بزرگ‌'),
(2807, 24, 16, 'و چرا هنگامی که شنیدیدش نگفتید نرسد ما را که سخن رانیم بدین منزهی تو خدایا این است تهمتی بزرگ‌'),
(2808, 24, 17, 'اندرز دهد شما را خدا نبادا بازگردید به مانند آن هیچگاه اگر هستید مؤمنان‌'),
(2809, 24, 18, 'و آشکار سازد خدا برای شما آیتها را و خدا است دانای حکیم‌'),
(2810, 24, 19, 'همانا آنان که دوست دارند که فراوان (یا فاش) شود فحشاء در آنان که ایمان آوردند ایشان را است عذابی دردناک در دنیا و آخرت و خدا می‌داند و شما نمی‌دانید'),
(2811, 24, 20, 'و اگر نبود فضل خدا بر شما و رحمتش و آنکه خدا است رحمت‌آرنده مهربان‌'),
(2812, 24, 21, 'ای آنان که ایمان آوردید پیروی نکنید گامهای شیطان را و هرکه پیروی کند گامهای شیطان را همانا او امر کند به فحشاء و ناپسند و اگر نبود فضل خدا بر شما و رحمتش پاک نمی‌شد از شما کسی هیچگاه و لیکن خدا پاک سازد هر که را که خواهد و خدا است شنوای دانا'),
(2813, 24, 22, 'و کوتاهی نکنند توانگران از شما و دارندگان گشایش که بدهند خویشاوندان و بینوایان و هجرت‌کنندگان را در راه خدا و باید عفو کنند و درگذرند آیا نمی‌خواهید (دوست ندارید) که بیامرزد خدا برای شما و خدا است آمرزنده مهربان‌'),
(2814, 24, 23, 'همانا آنان که پرتاب کنند بسوی زنان پارسای بی‌خبر و مؤمن (آنان را به زنا متهم دارند) لعنت شوند در دنیا و آخرت و ایشان را است شکنجه‌ای بزرگ‌'),
(2815, 24, 24, 'روزی که گواهی دهد بر ایشان زبانهاشان و دستهاشان و پاهاشان بدانچه بودند می‌کردند'),
(2816, 24, 25, 'در آن روز بپردازدشان خدا دین ایشان را حقّ و بدانند که خداوند است حقّ آشکار'),
(2817, 24, 26, 'زنان پلید برای مردان پلیدند و مردان پلید برای زنان پلیدند و زنان پارسا (پاک) برای مردان پاکند و مردان پاک برای زنان پاکند آنان بیزارند از آنچه گویند ایشان را است آمرزشی و روزیی گرامی‌'),
(2818, 24, 27, 'ای آنان که ایمان آوردید درون نشوید خانه‌هائی را جز خانه‌های خویش تا آشنا شوید و سلام کنید بر اهلش این بهتر است برای شما شاید یاد آورید'),
(2819, 24, 28, 'و اگر نیافتید در آنها کسی را پس درنیائید در آنها تا اذن داده شوید و اگر گفته شد به شما بازگردید پس بازگردید آن پاکتر است برای شما و خدا بدانچه کنید دانا است‌'),
(2820, 24, 29, 'نیست بر شما باکی که درآئید به خانه‌های غیر مسکونی (تهی از کسان) که در آنها است کالا یا بهره‌ای برای شما و خدا می‌داند آنچه را آشکار کنید و آنچه را نهان دارید'),
(2821, 24, 30, 'بگو به مؤمنان بپوشند دیدگان خود را و نگاه دارند فرجهای خود را آن پاکتر است برای ایشان همانا خدا آگاه است بدانچه می‌سازند'),
(2822, 24, 31, 'و بگو به زنان مؤمنه فروپوشند دیدگان خود را و نگه دارند فرجهای خود را و آشکار نسازند زیور خود را مگر آنچه نمایان است (یا برآمده است از آن) و باید بیفکند مقنعه‌های خود را بر گریبانهای خود و پدیدار نکنند زیور خویش را مگر برای شوهران خود یا پدران خود یا پدران شوهران خود یا فرزندان خود یا فرزندان شوهران خود یا برادران خود یا برادرزادگان خود یا خواهرزادگان خود یا زنان خود یا آنچه مالک است دستهای ایشان یا پیروانی که ندارندگان کامند (یا نیازند) از مردان یا کودکانی که چیره نشده‌اند بر عورتهای زنان و نکوبند زنان پایهای خود را تا دانسته شود آنچه نهان دارند از زیور خویش و بازگردید بسوی خدا همگی ای مؤمنان شاید رستگار شوید'),
(2823, 24, 32, 'و همسر گیرید زنان بی‌شوهر را از شما و شایستگان از بندگان و کنیزگان خویش را اگر باشند بینوایان بی‌نیازشان سازد خدا از فضل خود و خدا است گشایشمند دانا'),
(2824, 24, 33, 'و باید خویشتن‌داری کنند (عفت ورزند) آنان که نمی‌یابند آمیزشی یا همسری تا بی‌نیازشان گرداند خدا از فضل خویش و آنان که خواستار مکاتبه هستند از آنچه دستهای شما مالک است پس مکاتبه کنید با ایشان اگر دانستید در ایشان خوبی را و دهیدشان از مال خدا که به شما داده است و وادار نکنید به ناخواه کنیزان خویش را به روسپی اگر خواهند خویشتن نگه‌داشتن را تا جوئید بهره زندگانی دنیا را و آنکو وادارشان کند همانا خداوند است پس از واداشتن آنان آمرزنده مهربان‌'),
(2825, 24, 34, 'و همانا فرستادیم بسوی شما آیتهائی روشن و نمونه‌ای از آنان که گذاشتند پیش از شما و اندرزی برای پرهیزکاران‌'),
(2826, 24, 35, 'خدا است نور آسمانها و زمین مثل نور او مانند قندیلی است که در آن است چراغی که آن چراغ است در بلوری آن بلور مانند ستاره است درخشان که افروخته شود از درخت خجسته زیتونی نه خاوری و نه باختری نزدیک است روغنش بتابد هر چند نرسدش آتش فروغی است بر فروغی رهبری کند خدا به نور خویش هر که را خواهد و بزند خدا مثلها را برای مردم و خدا است به همه چیز دانا'),
(2827, 24, 36, 'در خانه‌هائی که رخصت داد خدا که افراشته شود و برده شود در آنها نامش تسبیح گویدش در آنها بامدادان و شامگاه‌'),
(2828, 24, 37, 'مردانی که سرگرمشان نسازد بازرگانی و نه سوداگری از یاد خدا و بپای‌داشتن نماز و دادن زکات ترسند روزی را که می‌گردد در آن دلها و دیده‌ها'),
(2829, 24, 38, 'تا پاداششان دهد خدا بهتر آنچه کردند و بیفزایدشان از فضل خویش و خدا روزی دهد هر که را خواهد بی‌شمار'),
(2830, 24, 39, 'و آنان که کفر ورزیدند کردارشان مانند سرابی است در خشکزاری که پنداردش تشنه‌لب آبی تا گاهی که بیایدش نیابدش چیزی و بیابد خدا را نزد آن پس بدو دهد حسابش را و خدا است شتابنده در حساب‌'),
(2831, 24, 40, 'یا مانند تاریکیهائی در دریائی ژرف بپوشاندش موجی (فراگیردش) از فراز آن است موجی که از فراز آن است ابری تاریکیهائی است پاره‌ای از آنها بر فراز پاره‌ای گاهی که برون آرد دستش را نیارد که بیندش و آنکو ننهاده است خدا برایش نوری پس نباشدش نوری‌'),
(2832, 24, 41, 'آیا ندیدی که خدا تسبیح گویدش آنکه در آسمانها و زمین است و مرغان بال‌گشادگان هر کدام دانست نماز و تسبیحش را و خدا دانا است بدانچه کنند'),
(2833, 24, 42, 'و برای خدا است پادشاهی آسمانها و زمین و بسوی خدا است بازگشت‌'),
(2834, 24, 43, 'آیا نبینی که خدا براند ابری پس گردآورد میانش تا بگرداندش توده انباشته که بینی باران را برون آید از شکافهای آن و بفرستد از آسمان از کوه‌هائی که در آنها است از تگرگ پس برساندش به هر که خواهد و بازگرداندش از هر که خواهد نزدیک است تابش برق آن ببرد دیدگان را'),
(2835, 24, 44, 'می‌گرداند خدا شب و روز را همانا در این عبرتی برای خداوندان دیده‌ها است‌'),
(2836, 24, 45, 'و خدا آفرید هر جنبنده را از آب پس از ایشان است آنکه می‌رود بر شکم خود و از ایشان است آنکه می‌رود بر دو پا و از ایشان است آنکه می‌رود بر چهار بیافرد خدا هر چه خواهد همانا خدا است بر همه چیز توانا'),
(2837, 24, 46, 'همانا فرستادیم آیتهائی روشن و خدا هدایت کند هر که را خواهد بسوی راهی راست‌'),
(2838, 24, 47, 'و گویند ایمان آوردیم به خدا و به رسول و فرمان بردیم پس پشت کنند گروهی از ایشان پس از آن و نیستند مؤمنان‌'),
(2839, 24, 48, 'و گاهی که خوانده شوند بسوی خدا و پیمبرش تا حکم راند میانشان ناگاه گروهی از ایشانند روی‌گردانان‌'),
(2840, 24, 49, 'و اگر باشد برای ایشان حقّ آیند بسوی او سرسپردگان‌'),
(2841, 24, 50, 'آیا در دلهای ایشان بیماری است یا شک دارند یا ترسند که ستم کند خدا بر ایشان و پیمبرش بلکه خود آنانند ستمکاران‌'),
(2842, 24, 51, 'جز این نیست که گفتار مؤمنان گاهی که خوانده شوند بسوی خدا و پیمبرش تا حکم راند میان ایشان آن است که گویند شنیدیم و فرمانبردیم و ایشانند رستگاران‌'),
(2843, 24, 52, 'و آنکو فرمان برد خدا و پیمبرش را و بترسد خدا را و بپرهیزدش پس آنانند کامیابان‌'),
(2844, 24, 53, 'و سوگند یاد کردند به خدا سخت‌ترین سوگندان خود که اگر بفرمائیشان هر آینه برون روند بگو سوگند یاد نکنید فرمانبرداری است شناخته همانا خدا آگاه است بدانچه می‌کنید'),
(2845, 24, 54, 'بگو فرمان برید خدا را و فرمان برید پیمبر را پس اگر پشت کردند جز این نیست که بر او است آنچه بار شد و بر شما است آنچه بار شدید و اگر فرمانبریدش رهبری شوید و نیست بر پیمبر جز رساندن آشکار'),
(2846, 24, 55, 'وعده داد خدا آنان را که ایمان آوردند از شما و کردار شایسته کردند که جای‌نشین گرداندشان در زمین چنانکه جای‌نشین گردانید آنان را که پیش از ایشان بودند و هر آینه فرمانروا گرداند برای ایشان دینشان را که پسندید برای ایشان و همانا بدیشان دهد پس از ترسشان آسایشی بپرستندم و شرک نورزند به من چیزی و آنکه کفر ورزد پس از این همانا آنانند نافرمانان‌'),
(2847, 24, 56, 'و بپای دارید نماز را و بدهید زکات را و فرمان برید پیمبر را شاید رحم شوید'),
(2848, 24, 57, 'نپندار آنان را که کفر ورزیدند به عجزآرندگان در زمین و جایگاه ایشان است آتش و چه زشت بازگشتگاهی است‌'),
(2849, 24, 58, 'ای آنان که ایمان آوردید باید دستور خواهند از شما آنان که مالک است دستهای شما و آنان که نرسیدند مردی را از شما سه بار پیش از نماز بامداد و گاهی که بگذارید جامه‌های خود را به نیمروز و پس از نماز شام سه عورت است برای شما نیست بر شما و نه بر ایشان پروائی پس از آنها گردش‌کنندگان باشند بر شما برخی بر برخی بدینسان بیان کند خدا برای شما آیتها را و خدا است دانای حکیم‌'),
(2850, 24, 59, 'و هنگامی که رسند کودکان از شما مردی را پس باید دستوری خواهند چنانکه دستوری خواستند آنان که پیش از ایشان بودند بدینسان بیان کند خدای برای شما آیتهای خویش را و خدا است دانای حکیم‌'),
(2851, 24, 60, 'و بازنشستگان از زنانی که امید آمیزش ندارند پس نیست بر ایشان پروائی که بگذارند جامه‌های خود را ناآشکارکنندگان زیوری و عفّت جویند بهتر است برای ایشان و خدا است شنوای دانا'),
(2852, 24, 61, 'نیست بر کور پروائی و نه بر لنگ پروائی و نه بر بیمار پروائی و نه بر شما که بخورید از خانه‌های خود یا خانه‌های پدران خود یا خانه‌های مادران خود یا خانه‌های برادران خود یا خانه‌های خواهران خود یا خانه‌های عمویان خود یا خانه‌های عمّه‌های خود یا خانه‌های خالوهای خود یا خانه‌های خاله‌های خود یا آنچه دارید کلیدهای آن را یا دوستتان نیست بر شما پروائی که بخورید همگی یا جدا جدا پس هنگامی که درآئید به خانه‌هائی سلام کنید بر خویشتن درودی از نزد خدا فرخنده پاکیزه بدینسان روشن کند خدا برای شما آیتها را شاید بخرد آئید'),
(2853, 24, 62, 'جز این نیست که مؤمنان آنانند که ایمان آوردند به خدا و پیمبرش و هر گاه با او باشند در کاری همگانی نروند تا دستوری از او خواهند همانا آنان که دستوری خواهند از تو آنانند که ایمان آرند به خدا و پیمبرش پس هر گاه دستوری خواستند از تو برای پاره کارشان پس دستوری ده به هر که خواهی از ایشان و آمرزش خواه برای ایشان از خدا که خدا است آمرزنده مهربان‌'),
(2854, 24, 63, 'نگردانید خواندن پیمبر را میان شما مانند خواندن برخی از شما برخی را همانا داند خدا آنان را که می‌خزند از شما به پناهگاهی پس باید بترسند آنان که سرپیچند از فرمانش که برسدشان آزمایشی یا برسدشان شکنجه‌ای دردناک‌'),
(2855, 24, 64, 'همانا خدا را است آنچه در آسمانها و زمین است به درست می‌داند آنچه را شما بر آنید و روزی که بازگردانیده شوند به سویش پس آگهیشان دهد بدانچه کردند و خدا است به همه چیز دانا');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'خجسته باد آنکه فرستاد فرقان (جداکننده) را بر بنده خویش تا باشد برای جهانیان ترساننده‌'),
(2857, 25, 2, 'آنکه وی را است پادشاهی آسمانها و زمین و برنگرفته است فرزندی و نبودش شریکی در پادشاهی و آفرید هر چیزی را و اندازه دادش اندازه دادنی‌'),
(2858, 25, 3, 'و برگرفتند جز وی خدایانی که نیافرند چیزی را و خود آفریده شوند و ندارند برای خویشتن زیان و نه سودی را و ندارند مرگ و نه زندگی و نه برانگیختنی را'),
(2859, 25, 4, 'و گفتند آنان که کفر ورزیدند نیست این مگر دروغی که بست آن را و یاریش کردند بر آن گروهی دگران همانا آوردند ستم و ناروایی را'),
(2860, 25, 5, 'و گفتند افسانه‌های پیشینیان است بنوشته است آنها را پس خوانده می‌شوند بر او بامداد و شب‌هنگام‌'),
(2861, 25, 6, 'بگو فرستاد آن را آنکه می‌داند نهان را در آسمانها و زمین همانا او است آمرزنده مهربان‌'),
(2862, 25, 7, 'و گفتند چه شود این پیمبر را که می‌خورد خوراک را و می‌رود در بازارها چرا فرود آورده نشود بر او فرشته تا باشد با وی ترساننده‌'),
(2863, 25, 8, 'یا افکنده شود به سویش گنجی یا باشد برای او باغی که بخورد از آن و گفتند ستمگران پیروی نمی‌کنید جز مردی جادو شده را'),
(2864, 25, 9, 'بنگر چگونه زدند برای تو مثَلها را پس گمراه شدند پس نتوانند راهی را'),
(2865, 25, 10, 'خجسته باد آنکه اگر خواهد بنهد برایت بهتر از این باغهائی که روان است زیر آنها جویها و بنهد برایت کاخهائی را'),
(2866, 25, 11, 'بلکه تکذیب کردند به ساعت و آماده کردیم برای آنکه تکذیب کند به ساعت آتش سوزان را'),
(2867, 25, 12, 'گاهی که بنگردشان از جایگاهی دور بشنوند برایش خشمی و خروشی را'),
(2868, 25, 13, 'و گاهی که افکنده شوند از آن به جایگاهی تنگ به هم فشردگان بخواهند در آنجا مرگ را'),
(2869, 25, 14, 'نخوانید امروز یک مرگ را و بخوانید مرگی فراوان را'),
(2870, 25, 15, 'بگو آیا این بهتر است یا بهشت جاودانی که نوید داده شدند پرهیزکاران بوده است ایشان را پاداشی و بازگشتگاهی‌'),
(2871, 25, 16, 'برای ایشان است آنچه خواهند جاودانان بوده است آن بر پروردگار تو وعده پرسیده‌'),
(2872, 25, 17, 'و روزی که گردآردشان با آنچه پرستند جز خدا پس گوید آیا شما گمراه کردید بندگانم را اینان یا خود گم کردند راه را'),
(2873, 25, 18, 'گویند منزّهی تو نسزد ما را که گیریم جز تو دوستانی و لیکن کامیابیشان دادی و پدران ایشان را تا فراموش کردند کتاب را و شدند گروهی تباه‌'),
(2874, 25, 19, 'پس به درست شما را تکذیب کردند بدانچه گویند پس نتوانید گردانیدنی و نه یاری‌کردن را و آنکه ستم کند از شما چشانیمش شکنجه‌ای بزرگ‌'),
(2875, 25, 20, 'و نفرستادیم پیش از تو از پیمبران جز آنکه آنان می‌خوردند خوراک را و راه می‌رفتند در بازارها و گردانیدیم برخی از شما را برای برخی آزمایش آیا شکیبا شوید و بوده است پروردگار تو بینا'),
(2876, 25, 21, 'و گفتند آنان که امید ندارند ملاقات ما را چرا فرود نشود بر ما فرشتگان یا بینیم پروردگار خویش را همانا برتری جستند در دلهاشان و سرکشی کردند سرکشی بزرگ‌'),
(2877, 25, 22, 'روزی که بینند فرشتگان را نباد مژده در آن روز برای گنهکاران و گویند دور باشی دور'),
(2878, 25, 23, 'و آمدیم بسوی آنچه کردند از کردار پس گردانیدیمش گردی پراکنده‌'),
(2879, 25, 24, 'یاران بهشت در آن روز بهترند در آرامش و نکوترند در آسودنگاه‌'),
(2880, 25, 25, 'و روزی که بشکافد آسمان به ابر و فرستاده شوند فرشتگان فرستادنی‌'),
(2881, 25, 26, 'پادشاهی در آن روز حقّ از آنِ خدای مهربان است و بوده است روزی بر کافران سخت‌'),
(2882, 25, 27, 'و روزی که می‌گزد ستمگر دستهای خود را گوید کاش گرفته بودم با پیمبر راهی‌'),
(2883, 25, 28, 'ای وای بر من کاش نمی‌گرفتم فلان را دوستی‌'),
(2884, 25, 29, 'همانا گمراهم کرد از یادآوری (یا کتاب) پس از آنکه بیامدم و بوده است شیطان انسان را خوارسازنده‌'),
(2885, 25, 30, 'و گفت پیمبر پروردگارا همانا قومم برگرفتند این قرآن را ترک‌شده‌'),
(2886, 25, 31, 'و بدینسان قرار دادیم برای هر پیمبری دشمنی از گنهکاران و بس است پروردگار تو راهنما و یاوری‌'),
(2887, 25, 32, 'و گفتند آنان که کفر ورزیدند چرا فرود آورده نشد بر او قرآن به یک بار بدینسان تا استوار سازیم بدان دلت را و به تدریج فرستادیمش تدریجی‌'),
(2888, 25, 33, 'و نیارندت مثَلی جز آنکه بیاریمت به حقّ و نکوتر تفسیری‌'),
(2889, 25, 34, 'آنان که گرد آورده شوند (برانگیخته شوند) بر رویهای خود بسوی دوزخ آنان بدترند در جایگاه و گمراه‌ترند در راه‌'),
(2890, 25, 35, 'و همانا دادیم به موسی کتاب را و گردانیدیم با او برادرش هارون را وزیری‌'),
(2891, 25, 36, 'پس گفتیم بروید بسوی قومی که دروغ پنداشتند آیتهای ما را پس سرنگون ساختیمشان سرنگونیی‌'),
(2892, 25, 37, 'و قوم نوح هنگامی که تکذیب کردند فرستادگان را غرقشان کردیم و گردانیدیمشان برای مردم آیتی و آماده کردیم برای ستمگران عذابی دردناک را'),
(2893, 25, 38, 'و عاد و ثمود و یاران رَسّ و قرنهائی را میان آن بسیار'),
(2894, 25, 39, 'و هر کدام زدیم برایش مثَلها را و هر کدام را نابود کردیم نابودیی‌'),
(2895, 25, 40, 'و همانا آمدند بر شهری که باریده شد بارانی بد آیا نبودند که بینندش بلکه بودند امید نداشتند برانگیختنی را'),
(2896, 25, 41, 'و هر گاه بینندت نگیرندت جز مسخره آیا این است آنکه برانگیختش خدا پیمبری‌'),
(2897, 25, 42, 'نزدیک بود که گمراه کند ما را از خدایان ما اگر نه شکیبا می‌شدیم بر آنها و زود است بدانند گاهی که بینند عذاب را کیست گُمتر در راه‌'),
(2898, 25, 43, 'آیا دیدی آن را که بگرفت خدای خویش را هوس خویش پس آیا تو هستی بر او وکیل‌'),
(2899, 25, 44, 'یا پنداری که بیشترشان می‌شنوند یا بخرد می‌یابند نیستند جز مانند چهارپایان بلکه ایشانند گُمتر در راه‌'),
(2900, 25, 45, 'آیا ننگریستی بسوی پروردگار خویش چگونه پهن کرد سایه را و اگر می‌خواست هر آینه می‌گردانیدش مانده در یکجا سپس گردانیدیم خورشید را بر آن راهنما'),
(2901, 25, 46, 'پس بگرفتیمش بسوی ما گرفتنی آسان‌'),
(2902, 25, 47, 'و او است آنکه گردانید برای شما شب را پوششی و خواب را آرامشی و گردانید روز را برانگیختنی‌'),
(2903, 25, 48, 'و او است آنکه فرستاد بادها را مژده پیش روی رحمتش و فرستادیم از آسمان آبی پاک‌کننده‌'),
(2904, 25, 49, 'تا زنده سازیم بدان شهری مرده را و بنوشانیمش از آنچه آفریدیم به دامها و مردمی بسیار'),
(2905, 25, 50, 'و همانا گردانیدیمش میانشان تا یادآور شوند پس نخواستند بیشتر مردم جز ناسپاسی را'),
(2906, 25, 51, 'و اگر می‌خواستیم هر آینه برمی‌انگیختیم در هر شهری ترساننده‌ای‌'),
(2907, 25, 52, 'پس فرمانبرداری مکن کافران را و جهاد کن با ایشان بدان جهادی بزرگ‌'),
(2908, 25, 53, 'و او است آنکه به هم آمیخت یا روان ساخت دو دریا را این گوارای پاکیزه و آن نمکی شور و بنهاد میان آن جداکننده‌ای و دیواری افراشته‌'),
(2909, 25, 54, 'و او است آنکه آفرید از آب بشری پس گردانیدش تباری و پیوندی و بوده است پروردگار تو توانا'),
(2910, 25, 55, 'و می‌پرستند جز خدا آنچه را نه سودشان دهد و نه زیانشان رساند و بوده است کافر بر پروردگار خویش پشتیبان‌'),
(2911, 25, 56, 'و نفرستادیمت مگر نویددهنده و ترساننده‌'),
(2912, 25, 57, 'بگو نپرسم شما را بر آن مزدی مگر آنکه خواهد که گیرد بسوی پروردگار خویش راهی‌'),
(2913, 25, 58, 'و توکّل کن بر زنده‌ای که نمیرد و تسبیح کن به سپاسگزاریش و بس است او به گناهان بندگان خویش آگاه‌'),
(2914, 25, 59, 'آنکه آفرید آسمانها و زمین و آنچه میان آنها است در شش روز سپس استوار شد بر عرش خداوند مهربان پس بپرس بدو آگهی را'),
(2915, 25, 60, 'و هر گاه گفته شود بدینان سجده کنید برای خدای مهربان گویند چیست خدای مهربان آیا سجده کنیم برای آنچه تو ما را فرمائی و بیفزایدشان رمیدن‌'),
(2916, 25, 61, 'خجسته باد آنکه نهاد در آسمان برجهائی و نهاد در آن چراغی و ماهی تابان را'),
(2917, 25, 62, 'و او است آنکه گردانید شب و روز را جایگزین همدیگر برای آنکو بخواهد یادآور شود یا بخواهد سپاسگزاری را'),
(2918, 25, 63, 'و بندگان خدای مهربان آنانند که می‌روند بر زمین هموار و هر گاه سخن گویند با ایشان نادانان گویند سلامی‌'),
(2919, 25, 64, 'و آنان که شب را به روز آرند برای پروردگار خویش سجده‌کنندگان و بپاایستادگان‌'),
(2920, 25, 65, 'و آنان که گویند پروردگارا بگردان از ما عذاب دوزخ را که عذاب آن است گیرنده‌'),
(2921, 25, 66, 'همانا آن زشت است در آرامش و جایگاه‌'),
(2922, 25, 67, 'و آنان که گاهی که انفاق کنند نه فزون روند و نه سخت گیرند و باشند میان این اندازه‌ای‌'),
(2923, 25, 68, 'و آنان که نخوانند با خدا خدائی دیگر و نکشند تنی را که حرام کرد خدا مگر به حقّ و زنا نکنند و آنکه بکندش بیابد کیفری را'),
(2924, 25, 69, 'افزوده شود برایش عذاب روز قیامت و جاودان ماند در آن سرافکنده‌'),
(2925, 25, 70, 'مگر آنکو بازگردد و ایمان آرد و کردار شایسته کند پس آنان تبدیل کند خدا بدیهاشان را به خوبیها و بوده است خدا آمرزنده مهربان‌'),
(2926, 25, 71, 'و آنکه توبه کند و کردار نیک کند همانا بازگردد بسوی خدا بازگشتی‌'),
(2927, 25, 72, 'و آنان که گواه نشوند ناروا را و گاهی که بگذرند به یاوه بگذرند بزرگواران‌'),
(2928, 25, 73, 'و آنان که هر گاه یادآوری شوند به آیتهای پروردگار خویش نیفتند بر آنها کران و کوران‌'),
(2929, 25, 74, 'و آنان که گویند پروردگارا ببخش ما را از همسران ما و فرزندان ما روشنی چشم و بگردان ما را برای پرهیزکاران پیشوائی‌'),
(2930, 25, 75, 'آنان پاداش داده شوند جایگاه بلند را بدانچه شکیبا شدند و پیشکش شوند در آن بدرود و سلامی‌'),
(2931, 25, 76, 'جاودانان در آن نکو آرامگاه و جایگاهی است‌'),
(2932, 25, 77, 'بگو چه ارج نهد (پاک دارد) به شما پروردگار من اگر نبود دعای شما همانا تکذیب گردید پس زود است بشود لازم (گیرنده)');
INSERT INTO `fa_moezzi` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طسم‌'),
(2934, 26, 2, 'این است آیتهای کتاب روشن‌'),
(2935, 26, 3, 'شاید ببازی تو جان خویش را چرا نیستند مؤمنان‌'),
(2936, 26, 4, 'اگر بخواهیم بفرستیم بر ایشان آیتی از آسمان پس بگردد گردنهاشان پیش آن فروتنان‌'),
(2937, 26, 5, 'و نیایدشان یادآوریی از خدای مهربان نوینی مگر آنکه باشند از آن روگردانان‌'),
(2938, 26, 6, 'همانا تکذیب کردند پس زود هست بیایدشان داستانهای آنچه بودند بدان استهزاکنان‌'),
(2939, 26, 7, 'آیا ننگرند بسوی زمین چه فراوان رویانیدیم در آن از هر جفتی گرامی‌'),
(2940, 26, 8, 'در این است هر آینه آیتی و نیستند بیشترشان ایمان‌آرندگان‌'),
(2941, 26, 9, 'و همانا پروردگار تو است عزّتمند مهربان‌'),
(2942, 26, 10, 'هنگامی که خواند پروردگار تو موسی را که برو به نزد گروه ستمگران‌'),
(2943, 26, 11, 'قوم فرعون چرا پرهیزکاری نکنند'),
(2944, 26, 12, 'گفت پروردگارا ترسم تکذیبم کنند'),
(2945, 26, 13, 'و تنگ شود سینه‌ام و روان نگردد زبانم پس بفرست بسوی هارون‌'),
(2946, 26, 14, 'و ایشان را است بر من گناهی و ترسم مرا بکشند'),
(2947, 26, 15, 'گفت نه چنین است بروید شما هر دو به آیتهای ما که مائیم با شما شنوندگان‌'),
(2948, 26, 16, 'پس درآئید بر فرعون و بگوئید مائیم فرستاده پروردگار جهانیان‌'),
(2949, 26, 17, 'که بفرست با ما بنی‌اسرائیل را'),
(2950, 26, 18, 'گفت آیا نپروریدمت نزد ما به نوزادی و ماندی نزد ما از عمر خویش سالیانی‌'),
(2951, 26, 19, 'و کردی کارت را آن را که کردی و بودی از ناسپاسان‌'),
(2952, 26, 20, 'گفت کردمش آن هنگام و بودم من از گمراهان‌'),
(2953, 26, 21, 'پس گریختم از شما گاهی که ترسیدمتان پس بخشید مرا پروردگار من فرمانی و گردانیدم از پیمبران‌'),
(2954, 26, 22, 'و این نعمتی است که منّتش را بر من نهی که بنده گرفتی بنی‌اسرائیل را'),
(2955, 26, 23, 'گفت فرعون و چیست پروردگار جهانیان‌'),
(2956, 26, 24, 'گفت پروردگار آسمانها و زمین و آنچه میان آنها است اگر هستید یقین‌دارندگان‌'),
(2957, 26, 25, 'گفت بدانان که در پیرامونش بودند آیا نمی‌شنوید'),
(2958, 26, 26, 'گفت پروردگار شما و پروردگار پدران شما پیشینیان‌'),
(2959, 26, 27, 'گفت همانا پیمبر شما که فرستاده شده است بسوی شما هر آینه او است دیوانه‌'),
(2960, 26, 28, 'گفت پروردگار خاور و باختر و آنچه میان آنها است اگر هستید خردمندان‌'),
(2961, 26, 29, 'گفت اگر بگرفتی خدائی جز من هر آینه بگردانمت از زندانیان‌'),
(2962, 26, 30, 'گفت و اگر چه بیارمت چیزی آشکار'),
(2963, 26, 31, 'گفت پس بیارش اگر هستی از راستگویان‌'),
(2964, 26, 32, 'پس افکند چوبدست خود را ناگهان آن است اژدری نمایان‌'),
(2965, 26, 33, 'و برآورد دست خود را ناگهان آن است پرتوی برای بینندگان‌'),
(2966, 26, 34, 'گفت بدان گروه که در گردش بودند همانا این است جادوگری دانشمند'),
(2967, 26, 35, 'خواهد برون کند شما را از سرزمین شما به جادوی خویش تا چه فرمان دهید'),
(2968, 26, 36, 'گفتند نگهدار او و برادرش را و بفرست در شهرستانها گردآورندگان‌'),
(2969, 26, 37, 'تا بیارندت بهر جادوگری دانشمند'),
(2970, 26, 38, 'پس گرد آورده شدند جادوگران برای وعده‌گاه روزی شناخته‌'),
(2971, 26, 39, 'و گفته شد به مردم آیا شمائید گردآیندگان‌'),
(2972, 26, 40, 'شاید پیروی کنیم جادوگران را اگر بودند پیروزمندان‌'),
(2973, 26, 41, 'پس هنگامی که آمدند جادوگران گفتند به فرعون آیا ما را است مزدی اگر شدیم پیروزمندان‌'),
(2974, 26, 42, 'گفت آری و همانا شمائید در آن هنگام از نزدیکان‌'),
(2975, 26, 43, 'گفت بدیشان موسی بیفکنید آنچه را شمائید افکنندگان‌'),
(2976, 26, 44, 'پس بیفکندند ریسمانها و چوبهای خود را و گفتند به فَر فرعون مائیم همانا پیروزمندان‌'),
(2977, 26, 45, 'پس افکند موسی چوبدست خویش را ناگهان فروبرد آنچه را به دروغ آوردند'),
(2978, 26, 46, 'پس بیفتادند جادوگران سجده‌کنان‌'),
(2979, 26, 47, 'گفتند ایمان آوردیم به پروردگار جهانیان‌'),
(2980, 26, 48, 'پروردگار موسی و هارون‌'),
(2981, 26, 49, 'گفت ایمان آوردید بدو پیش از آنکه دستورتان دهم همانا او بزرگ شما است که آموخت به شما جادوگری را پس زود است بدانید همانا سخت ببرّم دستها و پایهای شما را از برابر و هر آینه به دارتان کشم همگی‌'),
(2982, 26, 50, 'گفتند نیست باکی مائیم بسوی پروردگار خویش بازگشتگان‌'),
(2983, 26, 51, 'همانا امیدواریم بیامرزد برای ما پروردگار ما گناهان ما را که بودیم نخستین ایمان‌آرندگان‌'),
(2984, 26, 52, 'و وحی کردیم به موسی که شبانه بر بندگانم را همانا شمائید پیروی شدگان‌'),
(2985, 26, 53, 'پس فرستاد فرعون در شهرستانها گردآرندگان‌'),
(2986, 26, 54, 'که اینانند همانا گروهی اندک‌'),
(2987, 26, 55, 'و همانا ایشانند بر ما خشم‌آرندگان‌'),
(2988, 26, 56, 'و هر آینه مائیم همگی بیمناکان‌'),
(2989, 26, 57, 'پس برون راندیمشان از باغها و چشمه‌سارها'),
(2990, 26, 58, 'و گنجها و جایگاهی گرامی‌'),
(2991, 26, 59, 'بدینسان و ارث دادیمش به بنی‌اسرائیل‌'),
(2992, 26, 60, 'پس از پی ایشان بیامدند به هنگام خورشید تابان‌'),
