INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'به نام خداى بخشاينده مهربان'),
(2, 1, 2, 'سپاس و ستايش خداى راست، پروردگار جهانيان'),
(3, 1, 3, 'آن بخشاينده مهربان'),
(4, 1, 4, 'خداوند و فرمانرواى روز پاداش.'),
(5, 1, 5, 'تو را ميپرستيم و بس و از تو يارى ميخواهيم و بس.'),
(6, 1, 6, 'ما را به راه راست راه بنماى'),
(7, 1, 7, 'راه كسانى كه به آنان نعمت دادى- نيكويى كردى- نه راه خشم‌گرفتگان بر آنها و نه راه گمراهان.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الف، لام، ميم.'),
(9, 2, 2, 'اين كتاب، هيچ شكى در آن نيست، راهنماى پرهيزگاران است.'),
(10, 2, 3, 'آنان كه به غيب- نهان و ناپيدا- ايمان دارند و نماز را برپا ميدارند و از آنچه روزيشان كرده‌ايم انفاق ميكنند.'),
(11, 2, 4, 'و آنان كه به آنچه به تو و آنچه پيش از تو فرو فرستاده شده ايمان مى‌آورند و به جهان واپسين بى‌گمان باور دارند.'),
(12, 2, 5, 'ايشان بر رهنمونى [به راه راست‌] از پروردگار خويشند، و آنانند رستگاران.'),
(13, 2, 6, 'كسانى كه كافر شدند بر ايشان يكسان است كه بيم دهيشان يا بيم ندهى، ايمان نمى‌آورند.'),
(14, 2, 7, 'خداوند بر دلهاشان و بر گوشهاشان مُهر نهاده و بر ديدگانشان پرده‌اى است و آنها را عذابى است بزرگ.'),
(15, 2, 8, 'و از مردمان كسانى گويند: به خدا و به روز واپسين ايمان آورديم و حال آنكه ايمان ندارند'),
(16, 2, 9, '[به پندار خود] خدا و كسانى را كه ايمان آورده‌اند فريب مى‌دهند در حالى كه جز خود را فريب نمى‌دهند و نمى‌فهمند.'),
(17, 2, 10, 'در دلهاشان بيماريى- از شك و نفاق- هست، پس خدا بيمارى آنان را بيفزود- يا بيفزاياد- و به سزاى اينكه دروغ مى‌گفتند عذابى دردناك دارند.'),
(18, 2, 11, 'و چون گويندشان: در زمين تباهكارى مكنيد، گويند: همانا ما نيكوكار و سامان آوريم.'),
(19, 2, 12, 'آگاه باشيد كه آنان خود تباهكارانند ولى نمى‌فهمند.'),
(20, 2, 13, 'و چون به آنان گفته شود: ايمان آوريد چنانكه مردم ايمان آورده‌اند، گويند: آيا مانند بيخردان ايمان بياوريم؟ آگاه باشيد كه آنها خود بيخردند ولى نمى‌دانند.'),
(21, 2, 14, 'و چون كسانى را كه ايمان آورده‌اند ديدار كنند گويند: ايمان آورده‌ايم، و چون با ديوسيرتان خود تنها شوند، گويند: ما با شماييم، ما تنها [اينان را] ريشخند مى كنيم.'),
(22, 2, 15, 'خداوند آنها را ريشخند مى‌كند و مهلتشان مى‌دهد تا در سركشى خويش سرگشته و كوردل بمانند.'),
(23, 2, 16, 'اينانند كه گمراهى را به بهاى ره‌يابى خريده‌اند، از اين رو بازرگانيشان سود نكرد و راه‌يافتگان نبودند.'),
(24, 2, 17, 'داستان آنان، داستان كسانى را ماند كه آتشى برافروختند- تا راه خود را بيابند- و چون آتش پيرامونشان را روشن ساخت خداوند روشناييشان را ببرد و آنها را در تاريكيها واگذاشت كه هيج نبينند.'),
(25, 2, 18, 'كرانند- از شنيدن حق-، گنگانند- از گفتن حق-، كورانند- از ديدن حق-، و [از گمراهى‌] بازنمى‌گردند.'),
(26, 2, 19, 'يا [داستان گرفتاران در] بارانى تند از آسمان را ماند كه در آن تاريكيهاست و رعد و برقى كه از بيم مرگ از صاعقه‌ها انگشتان خويش را در گوشهاشان كنند و خدا فراگيرنده كافران است'),
(27, 2, 20, 'نزديك است كه [روشنايى خيره‌كننده‌] برق ديدگانشان را بربايد، هر گاه برق براى آنها بدرخشد در [پرتو] آن مى‌روند و چون بر آنان تاريك شود بايستند. و اگر خدا بخواهد هر آينه شنوايى و بيناييشان را مى‌برد، كه خدا بر همه چيز تواناست.'),
(28, 2, 21, 'اى مردم، پروردگار خويش را كه شما و پيشينيان شما را آفريده بپرستيد، باشد كه پرهيزگار شويد'),
(29, 2, 22, 'آن كه زمين را براى شما بسترى [گسترده‌] و آسمان را بنائى- سقفى برافراشته- ساخت و از آسمان آبى فرو آورد پس بدان [آب‌] از ميوه‌ها روزى براى شما بيرون آورد، پس براى خدا همتايان قرار مدهيد، و خود مى‌دانيد [كه خدا بى‌همتاست‌].'),
(30, 2, 23, 'و اگر در باره آنچه بر بنده خويش فرو فرستاديم در شك و گمانيد سوره‌اى مانند آن- يا از كسى همانند او'),
(31, 2, 24, 'و اگر نكرديد و هرگز نتوانيد كرد، پس از آتشى كه هيمه‌اش آدميان و سنگها- بتها يا سنگ كبريت- است و براى كافران آماده شده، بپرهيزيد.'),
(32, 2, 25, 'و كسانى را كه ايمان آورده و كارهاى نيك و شايسته كرده‌اند مژده ده كه ايشان را بهشتهاست كه از زير آنها جويها روان است، هر گاه ميوه‌اى از آنها روزى آنان شود گويند: اين همان است كه پيش از اين- در دنيا- روزى ما شده بود، و آن ميوه‌هاى همانند بديشان دهند، و در آنجا همسرانى پاك و پاكيزه دارند، و در آنجا جاودانه‌اند.'),
(33, 2, 26, 'خدا شرم ندارد از اينكه مَثَلى بزند، پشه باشد و فراتر از آن، اما آنان كه ايمان دارند مى‌دانند كه آن'),
(34, 2, 27, 'آنان كه پيمان خداى را پس از بستن و استواركردن آن مى‌شكنند و آنچه را كه خدا به پيوند آن فرمان داده مى‌گسلند، و در زمين تباهى مى‌كنند اينانند زيانكاران.'),
(35, 2, 28, 'چسان به خدا كافر مى‌شويد و حال آنكه مردگان- بى‌جان- بوديد پس شما را زنده كرد- جان داد- و ديگر بار شما را بميرانَد و سپس زنده كند و آنگاه به او بازمى‌گرديد؟'),
(36, 2, 29, 'اوست كه هر چه در زمين است همه را براى شما آفريد، آنگاه به آسمان پرداخت و هفت آسمان بساخت، و او به همه چيز داناست.'),
(37, 2, 30, 'و [به ياد آر] آنگاه كه پروردگارت فرشتگان را گفت: من در زمين جانشينى- براى مخلوق پيشين يا نماينده‌اى از خود- خواهم آفريد، گفتند: آيا كسى را در آن مى‌آفرينى كه تباهكارى كند و خونها ريزد، در حالى كه ما تو را به پاكى مى‌ستاييم و تقديس مى كنيم؟ [خداوند] گفت: من مى‌دانم آنچه شما نمى‌دانيد.'),
(38, 2, 31, 'و همه نامها را به آدم آموخت. سپس آن [چيز] ها را بر فرشتگان عرضه كرد و گفت: اگر راست مى‌گوييد مرا از نامهاى اينها خبر دهيد.'),
(39, 2, 32, 'گفتند: پاكى تو راست، ما را دانشى نيست مگر آنچه به ما آموخته‌اى، كه تويى داناى با حكمت- استواركار و درست‌گفتار-.'),
(40, 2, 33, 'گفت: اى آدم، آنان را از نامهاى اينها آگاه كن، و چون از نامهاى آنها آگاهشان كرد، گفت: آيا به شما نگفتم كه من نهان آسمانها و زمين را مى‌دانم و آنچه را آشكار مى‌كنيد و آنچه را پنهان مى‌داشتيد مى‌دانم.'),
(41, 2, 34, 'و [ياد كن‌] آنگاه كه فرشتگان را گفتيم: آدم را سجده كنيد، پس سجده كردند مگر ابليس، سرپيچى و گردنكشى كرد، و از كافران گشت.'),
(42, 2, 35, 'و گفتيم: اى آدم، با همسر خويش در بهشت بيارام، و از آن از هر جا كه خواهيد به فراوانى بخوريد و نزديك اين درخت مشويد كه از ستمكاران مى‌شويد.'),
(43, 2, 36, 'پس شيطان آن دو را از آن'),
(44, 2, 37, 'آنگاه آدم از پروردگار خويش سخنانى فرا گرفت، پس خدا [با رحمت خود] بر او بازگشت و توبه او را پذيرفت، همانا او توبه‌پذير و مهربان است.'),
(45, 2, 38, 'گفتيم: همه از آن'),
(46, 2, 39, 'و كسانى كه كافر شوند و آيات ما را دروغ شمرند دوزخيانند و در آن جاودانه‌اند.'),
(47, 2, 40, 'اى فرزندان اسرائيل- يعقوب- نعمت مرا كه ارزانى شما داشتم به ياد آريد و به پيمان من وفا كنيد تا به پيمان شما وفا كنم و از من بترسيد و بس.'),
(48, 2, 41, 'و به آنچه فرو فرستادم- قرآن- كه آنچه را نزد شماست- تورات- باور دارد بگرويد و نخستين كافر به آن مباشيد، و چيز اندكى را به بهاى آيات من مخريد و از من پروا كنيد و بس.'),
(49, 2, 42, 'و حق را به باطل مياميزيد و حق را پنهان مكنيد در حالى كه مى‌دانيد.'),
(50, 2, 43, 'و نماز را برپا داريد و زكات بدهيد و با ركوع‌كنان ركوع كنيد- با نمازگزاران نماز گزاريد-.'),
(51, 2, 44, 'آيا مردم را به نيكى فرمان مى‌دهيد و خود را از ياد مى‌بريد و حال آنكه كتاب- تورات- را مى‌خوانيد؟ آيا خرد را كار نمى‌بنديد؟'),
(52, 2, 45, 'و به شكيبايى و نماز يارى خواهيد، و اين [كار] هر آينه گران و دشوار است مگر بر فروتنان خداترس'),
(53, 2, 46, 'آنان كه مى‌دانند كه ديداركنندگان پروردگار خويشند و به سوى او بازمى‌گردند.'),
(54, 2, 47, 'اى فرزندان اسرائيل، به ياد آريد نعمت مرا كه بر شما ارزانى داشتم و اين كه شما را بر جهانيان- مردم زمانه- برترى دادم.'),
(55, 2, 48, 'و بترسيد از روزى كه كسى به كار كسى نيايد و از او شفاعتى پذيرفته نشود و از او برابرى- عوضى كه خود را باز خرد- نستانند و نه يارى شوند.'),
(56, 2, 49, 'و [به ياد آريد] هنگامى كه شما را از فرعونيان رهانيديم، كه شما را شكنجه بد و سختى مى‌چشاندند: پسرانتان را سر مى‌بريدند و زنانتان را زنده مى‌گذاشتند و در اين [كار] شما را آزمونى بزرگ از پروردگارتان بود.'),
(57, 2, 50, 'و هنگامى كه دريا را به [ورود] شما شكافتيم، پس شما را رهانيديم و فرعونيان را غرق كرديم و شما مى‌نگريستيد.'),
(58, 2, 51, 'و هنگامى كه با موسى چهل شب وعده كرديم آنگاه از پس [رفتن‌] او گوساله را [به خدايى‌] گرفتيد و شما ستمكار بوديد.'),
(59, 2, 52, 'پس از آن از شما درگذشتيم تا مگر سپاس داريد.'),
(60, 2, 53, 'و هنگامى كه به موسى كتاب و فرقان- جداكننده ميان حق و باطل- داديم تا مگر راه يابيد.'),
(61, 2, 54, 'و هنگامى كه موسى به قوم خويش گفت: اى قوم من، شما با فراگرفتنتان گوساله را [به خدايى‌] به خويشتن ستم كرديد، اكنون به آفريدگار خويش بازگرديد- توبه كنيد- و خود- يكديگر- را بكشيد، كه اين [به كفاره كردارتان‌] براى شما نزد آفريدگارتان بهتر است. پس [چون چنين كرديد] خداوند [به رحمت خود] بر شما بازگشت و توبه شما را پذيرفت كه او توبه‌پذير و مهربان است.'),
(62, 2, 55, 'و هنگامى كه گفتيد: اى موسى، هرگز تو را باور نداريم تا خداى را آشكارا ببينيم، پس صاعقه- آتش هلاك‌كننده آسمانى- شما را در گرفت و شما مى نگريستيد.'),
(63, 2, 56, 'سپس شما را از پس مُردنتان برانگيختيم تا مگر سپاس داريد.'),
(64, 2, 57, 'و ابر را بر شما سايبان كرديم و برايتان مَنّ- ترنجبين- و سَلوى- مرغى مخصوص- فرستاديم، [و گفتيم:] از چيزهاى پاكيزه كه روزيتان كرده‌ايم بخوريد و آنان بر ما ستم نكردند بلكه بر خويشتن ستم مى‌كردند.'),
(65, 2, 58, 'و هنگامى كه گفتيم: به اين شهر- بيت المقدس- در آييد و از [نعمتهاى‌] آن هر جا كه خواهيد به فراوانى بخوريد و به دروازه شهر سر فرو داشته در آييد و بگوييد: گناهان ما فروريز، تا گناهان شما را بيامرزيم، و زودا كه [پاداش‌] نيكوكاران را بيفزاييم.'),
(66, 2, 59, 'اما كسانى كه ستم كردند سخنى را كه به آنان گفته شد دگرگون ساختند، پس بر ستمكاران به سزاى بدكارى و نافرمانيشان عذابى سخت از آسمان فرو فرستاديم.'),
(67, 2, 60, 'و آنگاه كه موسى براى قوم خود آب خواست، گفتيم: عصايت را بر آن سنگ بزن. پس دوازده چشمه از آن بشكافت. هر گروهى آبشخور خويش را دانست. از روزى خدا بخوريد و بياشاميد و در زمين به تباهكارى مپوييد.'),
(68, 2, 61, 'و هنگامى كه گفتيد: اى موسى، ما هرگز بر يك خوراك شكيبا نباشيم، پروردگارت را بخوان تا براى ما از آنچه زمين مى‌روياند از تره- سبزى- و خيار و گندم و عدس و پياز آن بيرون آرد. گفت: آيا آنچه را كه پست‌تر است به جاى آنچه بهتر است مى‌گيريد؟ به شهرى فرود آييد كه آنچه خواستيد بيابيد. و بر آنها خوارى و درماندگى زده شد و به خشمى از خداى بازگشتند- سزاوار خشم خدا گشتند-، زيرا به آيات خدا كفر مى‌ورزيدند و پيامبران را به ناحقّ مى‌كشتند، و اين از آن رو بود كه نافرمان شده و از حدّ درمى‌گذشتند.'),
(69, 2, 62, 'كسانى كه ايمان آورده‌اند و يهودان و ترسايان و صابئان، هر كه [از آنان‌] خداى و روز واپسين را باور داشته باشد و كار نيك و شايسته كند، پاداش آنان نزد پروردگارشان است، نه بيمى بر آنهاست و نه اندوهگين شوند.'),
(70, 2, 63, 'و [به ياد آريد] آنگاه كه از شما پيمان گرفتيم و كوه طور را بالاى سرتان بداشتيم. آن [كتاب‌] را كه به شما داديم با همه نيرو و توان بگيريد و آنچه را كه در آن است به ياد آريد شايد كه پرهيزگار شويد.'),
(71, 2, 64, 'آنگاه پس از آن پشت كرديد، و اگر فزون بخشى خدا و مهربانى او بر شما نبود هر آينه از زيانكاران مى‌شديد.'),
(72, 2, 65, 'و هر آينه [حال‌] كسانى را از شما كه در روز شنبه از حدّ بگذشتند دانستيد، پس به آنها گفتيم: بوزينگان شويد، و خوار و رانده باشيد.'),
(73, 2, 66, 'پس آن'),
(74, 2, 67, 'و [به ياد آريد] آنگاه كه موسى به قوم خويش گفت: خداوند شما را مى‌فرمايد كه گاوى را سر ببريد. گفتند: آيا ما را به ريشخند مى‌گيرى؟!- ما از تو مى‌پرسيم فلان را چه كسى كشته؟ و تو مى‌گويى گاوى بكشيد!- گفت: به خدا پناه مى‌برم كه از نادانان باشم.'),
(75, 2, 68, 'گفتند: خداوندت را بخوان تا براى ما روشن كند كه آن'),
(76, 2, 69, 'گفتند: خداوندت را بخوان تا براى ما بيان كند كه رنگ آن چيست؟ گفت: او مى گويد: گاوى است زرد پررنگ و يك دست كه بينندگان را شاد مى‌كند.'),
(77, 2, 70, 'گفتند: خداوندت را بخوان تا براى ما روشن كند كه آن چه گاوى است كه آن گاو [در ميان گاوان‌] بر ما پوشيده شده است و اگر خداى خواهد راه يافتگان باشيم.'),
(78, 2, 71, 'گفت: او مى‌گويد: آن گاوى است نه چنان رام كه زمين را شخم زند و كشت را آب دهد، بى‌عيب است و هيچ لكه‌اى [از رنگ ديگر] در آن نيست. گفتند: اكنون حقّ را بياوردى پس آن'),
(79, 2, 72, 'و آنگاه كه كسى را كشتيد پس هر يك آن را از خود رد كرديد- به گردن يكديگر انداختيد-، و خدا آنچه را كه پنهان مى‌داشتيد آشكار كننده بود.'),
(80, 2, 73, 'از اين رو گفتيم: او'),
(81, 2, 74, 'پس از آن دلهاتان سخت شد همچون سنگ يا سخت‌تر، و همانا از برخى سنگها جويها روان شود و برخى از آنها بشكافد و آب از آن بيرون آيد، و برخى از آنها از بيم خدا [از كوه‌] فرو ريزد، و خدا از آنچه مى‌كنيد غافل نيست.'),
(82, 2, 75, 'پس [اى مؤمنان،] آيا اميد داريد كه [جهودان‌] به شما بگروند، و حال آنكه گروهى از آنها سخن خداى را مى‌شنوند و آن را از پس آنكه دريافتند مى گردانند و خود مى‌دانند [كه چه مى‌كنند]؟!'),
(83, 2, 76, 'و چون مؤمنان را ديدار كنند، گويند: ما نيز ايمان آورده‌ايم و چون برخى‌شان با برخى تنها نشينند گويند: آيا با آنان- مسلمانان- از آنچه خدا بر شما گشوده- به شما خبر داده'),
(84, 2, 77, 'آيا نمى‌دانند كه خدا آنچه را پنهان كنند و آنچه را آشكار كنند مى‌داند؟'),
(85, 2, 78, 'و برخى از آنان- يعنى عوام جهودان- كه خواندن و نوشتن نتوانند، از كتاب- تورات- ندانند مگر آرزوهايى بيهوده- سازگار با هوسها و خواهشهاى دلشان- و تنها گمانهايى در سر مى‌پرورند.'),
(86, 2, 79, 'پس واى بر آنان كه كتاب را به دست خود مى‌نويسند، آنگاه مى‌گويند اين از جانب خداست تا با آن بهايى اندك بستانند، پس واى بر آنان از آنچه با دست خويش نوشتند و واى بر آنان از آنچه به دست مى‌آورند.'),
(87, 2, 80, 'و گفتند: آتش دوزخ به ما نرسد مگر روزى چند بگو: آيا از نزد خدا پيمانى گرفته‌ايد، كه خدا هرگز خلاف پيمان خود نخواهد كرد، يا چيزى بر خدا مى بنديد كه نمى‌دانيد؟'),
(88, 2, 81, 'آرى، هر كه كار بدى كند و گناهش او را فراگيرد آنان دوزخيانند و در آن جاودانند'),
(89, 2, 82, 'و كسانى كه ايمان آورند و كارهاى نيك و شايسته كنند، آنان بهشتيانند و در آن جاودانند.'),
(90, 2, 83, 'و [به ياد آريد] آنگاه كه از فرزندان اسرائيل پيمان گرفتيم كه جز خداى را نپرستيد و به پدر و مادر و خويشان و يتيمان و تهيدستان نيكى كنيد و به مردم سخن خوش و نيك بگوييد و نماز را برپا داريد و زكات بدهيد. سپس جز اندكى از شما پشت كرديد در حالى كه [از حقّ‌] رويگردان بوديد.'),
(91, 2, 84, 'و هنگامى كه از شما پيمان گرفتيم كه خونهاى يكديگر مريزيد و همديگر را از خانمان و ديارتان بيرون مكنيد، پس اقرار كرديد و خود گواهيد.'),
(92, 2, 85, 'سپس همين شما يكديگر را مى‌كشيد و گروهى از خودتان را از خانمان و ديارشان بيرون مى‌رانيد، بر ضدّ آنها به گناه و ستمگرى با يكديگر همپشت مى‌شويد، و اگر همانان به صورت اسيران نزد شما آيند آنان را [از دشمن‌] بازخريد مى‌كنيد، در حالى كه بيرون‌كردنشان بر شما حرام بود. آيا به برخى از كتاب ايمان مى‌آوريد و به برخى ديگر كافر مى‌شويد؟! پس سزاى كسى از شما كه چنين كند جز رسوايى و خوارى در دنيا نيست، و روز رستاخيز به سخت‌ترين عذاب بازبرده شوند، و خداى از آنچه مى‌كنيد غافل نيست.'),
(93, 2, 86, 'اينان همان كسانند كه زندگى اين جهان را به بهاى آن جهان خريدند پس عذابشان سبك نگردد و يارى نشوند.'),
(94, 2, 87, 'هر آينه موسى را كتاب- تورات- داديم و از پس او پيامبران فرستاديم و به عيسى پسر مريم حجّت‌هاى روشن داديم و او را به روح القدس- جبرئيل- نيرومند كرديم. آيا هر گاه پيامبرى چيزى- احكام و دستورهايى- برايتان آورد كه دلتان نمى‌خواست بزرگ‌منشى و گردنكشى نموديد، پس گروهى را دروغگو شمرديد و گروهى را مى‌كشتيد؟!'),
(95, 2, 88, 'و گفتند: دلهاى ما در پوشش است- سخنانت به دلهاى ما نرسد و آنها را نمى‌فهميم- [چنين نيست‌] بلكه خدا آنان را به سبب كفرشان لعنت كرده- از رحمت و هدايت خود دور ساخته-، پس اندكى ايمان مى‌آورند.'),
(96, 2, 89, 'و چون آنان را كتابى از نزد خدا آمد، كه آنچه را با آنان است- تورات- باور دارد، با آنكه پيش از اين [با آن‌] بر كافران پيروزى مى‌خواستند، همين كه آنچه- يا آن كس كه- مى‌شناختند بديشان آمد به آن- يا به او- كافر شدند پس لعنت خدا بر كافران باد.'),
(97, 2, 90, 'خود را به بد چيزى فروختند كه بدانچه خدا فروفرستاده كافر شوند از روى بدخواهى و ستم بر اينكه خداى از فضل خويش- يعنى كتاب و وحى- بر هر كس از بندگانش- محمد'),
(98, 2, 91, 'و چون به آنان گفته شود كه بدانچه خدا فروفرستاده ايمان آوريد، گويند: بدانچه بر ما فروفرستاده شده ايمان داريم و بدانچه غير از آن است با آنكه راست است و كتابشان را باور دارد، كافر مى‌شوند. بگو: اگر ايمان داشتيد پس چرا پيامبران خدا را پيش از اين مى‌كشتيد؟!'),
(99, 2, 92, 'و هر آينه موسى با حجّت‌هاى روشن نزد شما آمد و از پس او گوساله را [به خدايى‌] گرفتيد در حالى كه ستمكار بوديد.'),
(100, 2, 93, 'و [ياد كنيد] هنگامى كه از شما پيمان گرفتيم و كوه طور را بالاى سرتان بداشتيم، [و گفتيم:] آنچه را به شما داده‌ايم با همه نيرو و توان بگيريد و بشنويد [و فرمان بريد]. گفتند: شنيديم و نافرمانى كرديم و دلهاشان به سبب كفرشان، از [دوستى‌] گوساله سيراب شد. بگو: اگر شما اهل ايمانيد، ايمانتان شما را به بد چيزى فرمان مى‌دهد.'),
(101, 2, 94, 'بگو: اگر سراى واپسين به نزد خداوند ويژه شماست نه ديگر مردمان، پس آرزوى مرگ كنيد اگر راستگوييد.'),
(102, 2, 95, 'و به سبب آنچه كرده‌اند هرگز آرزوى آن نكنند، و خدا به [حال‌] ستمكاران داناست.'),
(103, 2, 96, 'هر آينه آنان- جهودان- را آزمندترين مردم به زندگى [دنيا] خواهى يافت و [حتى آزمندتر] از كسانى كه مشرك شده‌اند هر يك از آنها دوست دارد كه هزار سال عمرش دهند، و حال آنكه اين عمر دراز اگر هم داده شود، دوركننده او از عذاب نخواهد بود و خدا بدانچه مى‌كنند بيناست.'),
(104, 2, 97, 'بگو: هر كه دشمن جبرئيل باشد پس [بداند كه‌] او آن'),
(105, 2, 98, 'هر كه دشمن خدا و فرشتگان و فرستادگان او و جبرئيل و ميكائيل باشد، پس خدا هم دشمن كافران است.'),
(106, 2, 99, 'و هر آينه آيتهاى روشن- آيات قرآن و ديگر نشانه‌ها و معجزه‌ها- به تو فروفرستاديم و جز بدكاران نافرمان به آنها كافر نشوند.'),
(107, 2, 100, 'چرا هر گاه پيمانى بستند گروهيشان آن را شكستند و دور افكندند؟! بلكه بيشترشان ايمان ندارند.'),
(108, 2, 101, 'و چون فرستاده‌اى از سوى خدا- يعنى محمد'),
(109, 2, 102, 'و از آنچه ديوان در [روزگار] پادشاهى سليمان مى‌خواندند پيروى كردند، و سليمان [هرگز دست به سحر نيالود و] كافر نشد بلكه ديوان كافر شدند كه به مردم جادو مى‌آموختند و [نيز] از آنچه بر آن دو فرشته، هاروت و ماروت، در بابل فرود آمده [پيروى كردند] و اين دو هيچ كس را نمى‌آموختند مگر اينكه مى‌گفتند: ما آزمونيم، پس كافر مشو و آنها از آن دو چيزى مى‌آموختند كه با آن ميان مرد و همسرش جدايى افكنند، و اينها با آن بى‌اذن خدا به هيچ كس گزندى نمى‌توانستند رسانيد و آنچه مى‌آموختند آنان را زيان داشت و سود نمى‌رسانيد، و [جهودان‌] نيك مى‌دانستند كه هر كه خريدار آن'),
(110, 2, 103, 'و اگر ايمان آورده و پرهيزگارى كرده بودند، هر آينه پاداشى از نزد خدا [برايشان‌] بهتر بود، اگر مى‌دانستند.'),
(111, 2, 104, 'اى كسانى كه ايمان آورده‌ايد، [به پيامبر] مگوييد: «راعنا» و بگوييد: «انظرنا»- ما را در نظر دار، به ما نگر- و گوش فراداريد، و كافران را عذابى است دردناك.'),
(112, 2, 105, 'كسانى كه كافر شدند از اهل كتاب و مشركان، دوست ندارند كه از پروردگارتان هيچ نيكى- وحى يا رحمتى- بر شما فروآيد، و خداوند هر كه را خواهد به مِهر و بخشايش خود ويژه سازد، و خدا داراى فزونى و بخشش بزرگ است.'),
(113, 2, 106, 'هر آيتى را كه نسخ كنيم- حكم آن را برداريم- يا از يادها ببريم- يا بازپس داريم-، بهتر از آن يا مانند آن را مى‌آوريم آيا نمى‌دانى كه خدا بر همه چيز تواناست؟'),
(114, 2, 107, 'آيا نمى‌دانى كه پادشاهى و فرمانروايى آسمانها و زمين خداى راست، و شما را جز خدا سرپرست و ياورى نيست؟'),
(115, 2, 108, 'يا مگر مى‌خواهيد از پيامبرتان چنان درخواست كنيد كه پيش از اين از موسى درخواستند؟ و هر كه كفر را به جاى ايمان گيرد همانا راه راست را گم كرده است.'),
(116, 2, 109, 'بسيارى از اهل كتاب با اينكه حق براى آنها روشن و هويدا شده از روى حسدى كه در دلشان هست دوست دارند كه شما را پس از ايمان آوردنتان به كفر بازگردانند پس درگذريد و چشم بپوشيد تا خدا فرمان خود بيارد، كه خدا بر همه چيز تواناست.'),
(117, 2, 110, 'و نماز را برپا داريد و زكات بدهيد، و هر نيكى كه براى خود پيش فرستيد آن را نزد خدا باز يابيد، كه خدا بدانچه مى‌كنيد بيناست.'),
(118, 2, 111, 'و گفتند: هرگز به بهشت نرود مگر كسى كه جهود يا ترسا باشد. اينها آرزوهاى آنهاست، بگو: اگر راستگوييد برهان- دليل روشن- خويش بياوريد.'),
(119, 2, 112, 'آرى، هر كس روى خود را به خدا سپارد- با اخلاص به خدا روى آورد- و نيكوكار باشد، مزد او نزد پروردگار اوست، و نه بيمى بر آنهاست و نه اندوهگين شوند.'),
(120, 2, 113, 'جهودان گفتند: ترسايان بر چيزى نيستند، و ترسايان گفتند: جهودان بر چيزى نيستند، و حال آنكه كتاب- تورات و انجيل- را مى‌خوانند همچنين كسانى كه دانا نيستند- مشركان- سخنى مانند سخن آنان گفتند، و خدا روز رستاخيز در باره آنچه اختلاف مى‌ورزيدند داورى خواهد كرد.'),
(121, 2, 114, 'كيست ستمكارتر از آن كه نگذاشت در مسجدهاى خدا نام او را ياد كنند و در ويرانى آنها كوشيد؟ اينان را نرسد كه در آنها در آيند مگر بيمناك و هراسان آنان را در اين جهان، خوارى و رسوايى و در آن جهان عذابى بزرگ است.'),
(122, 2, 115, 'خاور و باختر خداى راست پس به هر سو كه روى آريد همانجا روى خداست- زيرا خدا در همه جا هست-، كه خدا گشايش‌بخش و داناست.'),
(123, 2, 116, 'و گفتند: خدا فرزندى گرفت پاك و منزه است او بلكه او راست آنچه در آسمانها و زمين است، و همه او را با فروتنى فرمانبردارند.'),
(124, 2, 117, 'نو پديدآرنده آسمانها و زمين است و چون خواهد كارى گزارد آن را همين گويد: باش، پس مى‌باشد.'),
(125, 2, 118, 'آنان كه دانا نيستند گفتند: چرا خدا با ما سخن نمى‌گويد يا نشانه‌اى براى ما نمى‌آيد؟ كسانى هم كه پيش از آنها بودند چنين مى‌گفتند، دلهاشان همانند است. ما نشانه‌ها- آيات- را براى گروهى كه يقين دارند روشن و آشكار كرده‌ايم.'),
(126, 2, 119, 'ما تو را براستى نويدرسان و بيم‌دهنده فرستاديم، و تو را از دوزخيان نپرسند.'),
(127, 2, 120, 'جهودان و ترسايان هرگز از تو خشنود نخواهند شد تا آنكه از كيش آنان پيروى كنى. بگو: رهنمونى [راستين‌] همان رهنمونىِ خداست. و اگر پس از دانشى كه سوى تو آمده از خواهشها و هوسهاشان پيروى كنى در برابر خدا هيچ دوست كارساز و ياورى نخواهى داشت.'),
(128, 2, 121, 'آنان كه كتابشان داده‌ايم و آن را چنانكه سزاوار آن است مى‌خوانند، همينانند كه به آن ايمان مى‌آورند، و هر كه بدان كافر شود ايشانند زيانكاران.'),
(129, 2, 122, 'اى فرزندان اسرائيل، نعمت مرا كه بر شما ارزانى داشتم و شما را بر جهانيان- مردم زمانه- برترى دادم، ياد كنيد.'),
(130, 2, 123, 'و بترسيد از روزى كه كسى به كار كسى نيايد و از او برابرى- عوضى كه خود را باز خرد- نپذيرند و او را هيچ شفاعتى سود ندهد و نه يارى شوند.'),
(131, 2, 124, 'و [به ياد آر] آنگاه كه ابراهيم را پروردگار وى به امورى چند بيازمود و او آنها را به انجام رسانيد. [خداى‌] گفت: تو را پيشواى مردم خواهم كرد، [ابراهيم‌] گفت: و از فرزندان من [نيز پيشوايان قرار ده‌]، گفت: پيمان من به ستمكاران نرسد.'),
(132, 2, 125, 'و هنگامى كه آن خانه- كعبه- را براى مردم بازگشتگاه- يا جايگاه رسيدن به ثواب و پاداش- و جاى امن ساختيم، و از جاى ايستادن ابراهيم نمازگاه بگيريد. و به ابراهيم و اسماعيل سفارش كرديم- فرمان داديم- كه خانه مرا براى طواف‌كنان و باشندگان آنجا- ساكنان حَرَم- و ركوع‌كنان و سجودكنان پاك و پاكيزه داريد.'),
(133, 2, 126, 'و آنگاه كه ابراهيم گفت: پروردگارا، اينجا را شهر امنى ساز و از مردم آن كسانى را كه به خدا و روز بازپسين ايمان دارند از ميوه‌ها روزى ده [خداى‌] گفت: و هر كه كافر شود او را اندكى برخوردار كنم، سپس به عذاب دوزخ گرفتارش سازم، و بد بازگشتگاهى است.'),
(134, 2, 127, 'و آنگاه كه ابراهيم و اسماعيل پايه‌هاى خانه را بالا مى‌بردند، [و مى‌گفتند:] پروردگارا، از ما بپذير، كه تويى شنوا و دانا.'),
(135, 2, 128, 'پروردگارا، ما را دو مسلمان- گردن‌نهاده و فرمانبردار- خويش گردان و از فرزندان ما نيز امتى مسلمان خود كن و به ما احكام عبادتمان- حج را بنماى و [به مِهر و بخشايش خود] بر ما بازگرد و توبه ما بپذير، كه تويى توبه‌پذير و مهربان.'),
(136, 2, 129, 'پروردگارا، در ميان ايشان پيامبرى از آنان برانگيز كه آيات تو را بر ايشان بخواند و آنان را كتاب و حكمت- انديشه درست و گفتار و كردار درست- بياموزد و پاكشان سازد، كه تويى تواناى بى‌همتا و داناى استواركار.'),
(137, 2, 130, 'و كيست كه از كيش ابراهيم روى بگرداند مگر آن كه خود را نابخرد شمارد؟ و هر آينه او را در اين جهان برگزيديم و در آن جهان از شايستگان است.'),
(138, 2, 131, 'آنگاه كه پروردگارش به او گفت: گردن نهاده و تسليم باش گفت: پروردگار جهانيان را گردن‌نهاده و تسليمم.'),
(139, 2, 132, 'و ابراهيم فرزندان خود را به همين [كيش‌] سفارش كرد و يعقوب نيز، كه اى فرزندان من، خدا اين دين را براى شما برگزيده است پس مميريد مگر آنكه مسلمان- گردن نهاده و تسليم- باشيد.'),
(140, 2, 133, 'مگر شما حاضر و گواه بوديد آنگاه كه يعقوب را مرگ فرا رسيد، آن دم كه فرزندان خود را گفت: پس از من چه خواهيد پرستيد؟! گفتند: خداى تو و خداى پدرانت ابراهيم و اسماعيل و اسحاق را كه خدايى يكتاست مى‌پرستيم و تسليم و گردن‌نهاده اوييم.'),
(141, 2, 134, 'آنها گروهى بودند كه درگذشتند ايشان راست آنچه كردند و شما راست آنچه كرده باشيد و شما را از آنچه آنان مى‌كردند نپرسند.'),
(142, 2, 135, 'و گفتند: جهود يا ترسا شويد تا [به حق‌] راه يابيد. بگو: بلكه آيين ابراهيم حق‌گراى را [پيروى مى‌كنيم‌]، و او از مشركان نبود.'),
(143, 2, 136, 'بگوييد: به خداى يكتا و آنچه به ما فرود آمده و آنچه به ابراهيم و اسماعيل و اسحاق و يعقوب و اسباط- نوادگان يعقوب- فرود آمده و آنچه به موسى و عيسى داده شده و به آنچه به پيامبران از پروردگارشان داده شده ايمان آورديم ميان هيچ يك از ايشان جدا نكنيم- فرق نگذاريم،- و او را فرمانبردار و گردن‌نهاده‌ايم.'),
(144, 2, 137, 'پس اگر به مانند آنچه شما بدان ايمان آورده‌ايد ايمان آورند همانا [به حق‌] راه يافته‌اند، و اگر روى برتابند جز اين نيست كه در ناسازى و ستيزند و خدا تو را از [شرّ و گزند] آنها نگاه خواهد داشت، و اوست شنوا و دانا.'),
(145, 2, 138, '[بگوييد:] رنگ خدا [يى داريم‌]- يعنى ايمان ما رنگ خدايى دارد و دين خداى را كه همان يكتا پرستى و كيش ابراهيم است پذيرنده‌ايم- و كيست نيكوتر از خداى در رنگ- دين فطرت-؟ و ما او را پرستندگانيم.'),
(146, 2, 139, 'بگو: آيا با ما در باره خدا ستيزه مى‌كنيد و حال آنكه او پروردگار ما و شماست؟! ما راست كردارهاى ما و شما راست كردارهاى شما و ما او را به پاكدلى- اخلاص- پرستنده‌ايم.'),
(147, 2, 140, 'يا مگر مى‌گوييد ابراهيم و اسماعيل و اسحاق و يعقوب و نوادگان وى جهود بودند يا ترسا؟! بگو: شما داناتريد يا خدا؟ و كيست ستمكارتر از آن كه آن گواهى را كه از خدا [در باره اين پيامبران‌] نزد اوست پنهان كند؟ و خداوند از آنچه مى‌كنيد غافل نيست.'),
(148, 2, 141, 'آنها گروهى بودند كه درگذشتند آنان راست آنچه كردند و شما راست آنچه كرده باشيد، و شما را از آنچه آنها مى‌كردند نپرسند.'),
(149, 2, 142, 'زودا كه نابخردان مردم گويند: چه چيزى آنان را از قبله‌اى كه بر آن بودند- يعنى بيت المقدس- بگردانيد؟ بگو: خاور و باختر خداى راست، هر كه را خواهد به راه راست راه مى‌نمايد.'),
(150, 2, 143, 'و اينچنين شما را امتى ميانه ساختيم تا بر مردمان گواه باشيد و پيامبر بر شما گواه باشد. و قبله‌اى را كه بر آن بودى- بيت المقدس- قرار نداديم مگر براى آنكه كسى را كه از پيامبر پيروى مى‌كند از كسى كه بر پاشنه‌هاى خود مى‌گردد- روى مى‌گرداند- معلوم كنيم، و هر آينه اين [قراردادن قبله- مسجد الاقصى-] جز بر آنان كه خدا ايشان را راه نموده است دشوار بود. و خداوند بر آن نيست كه ايمان شما را تباه كند، زيرا كه خداوند به مردم هر آينه رؤوف و مهربان است.'),
(151, 2, 144, 'ما گردش روى تو را در آسمان [به انتظار وحى‌] مى‌بينيم، و هر آينه تو را به قبله‌اى كه آن را مى‌پسندى بگردانيم پس روى خود را سوى مسجد الحرام- نمازگاه شكوهمند- بگردان، و هر جا كه باشيد رويتان را سوى آن بگردانيد. همانا اهل كتاب مى‌دانند كه اين [گردانيدن قبله‌] حق است از سوى پروردگارشان، و خدا از آنچه مى‌كنند غافل نيست.'),
(152, 2, 145, 'اهل كتاب را هر نشانه و حجتى بياورى از قبله تو پيروى نكنند، و تو نيز پيرو قبله آنها نيستى، و آنها هم پيرو قبله يكديگر نيستند. و اگر پس از دانشى كه سوى تو آمده از هوسها و خواهشهاى دل آنها پيروى كنى هر آينه تو آنگاه از ستمكاران خواهى بود.'),
(153, 2, 146, 'كسانى كه كتابشان داده‌ايم او- محمد'),
(154, 2, 147, 'حق- آنچه درست و راست است- از پروردگار توست، پس، از شك‌داران مباش.'),
(155, 2, 148, 'هر گروهى- يا هر كسى- را جهتى است كه رو سوى آن مى‌گرداند. پس به نيكيها پيشى گيريد. هر جا كه باشيد خدا همه شما را فراهم آورد، كه خدا بر هر چيزى تواناست.'),
(156, 2, 149, 'و از هر جا كه بيرون شوى روى خود را سوى مسجد الحرام كن و هر آينه اين راست و درست است از پروردگار تو، و خدا از آنچه مى‌كنيد غافل نيست.'),
(157, 2, 150, 'و از هر جا كه بيرون شوى روى خود را سوى مسجد الحرام كن و هر كه باشيد روى خود را بدان سو كنيد تا مردمان را بر شما بهانه و دستاويزى نباشد- مگر كسانى از آنها كه ستم كردند- كه هيچ گاه دست از بهانه‌جويى برنمى‌دارند- پس، از ايشان مترسيد و از من بترسيد- و تا نعمتم را بر شما تمام كنم، و باشد كه راه يابيد'),
(158, 2, 151, 'همچنان كه در ميان شما پيامبرى از خودتان فرستاديم كه آيات ما را بر شما مى‌خواند و شما را پاك مى‌سازد و شما را كتاب و حكمت- گفتار و كردار درست- مى‌آموزد و آنچه نمى‌دانستيد به شما مى‌آموزد.'),
(159, 2, 152, 'پس مرا ياد كنيد تا شما را ياد كنم، و مرا سپاس گزاريد و ناسپاسى من مكنيد.'),
(160, 2, 153, 'اى كسانى كه ايمان آورده‌ايد، به شكيبايى و نماز يارى خواهيد كه خدا با شكيبايان است.'),
(161, 2, 154, 'و آنان را كه در راه خدا كشته مى‌شوند مردگان مگوييد، بلكه زندگانند ولى شما درنمى‌يابيد.'),
(162, 2, 155, 'و هر آينه شما را به چيزى از بيم و گرسنگى و كاهش مالها و جانها و ميوه‌ها مى‌آزماييم و شكيبايان را مژده ده'),
(163, 2, 156, 'آنان كه چون مصيبتى- پيشامد ناخوشايندى- به ايشان رسد، گويند: ما از آنِ خداييم و به سوى او باز مى‌گرديم.'),
(164, 2, 157, 'آنانند كه درودها و بخشايشى از پروردگارشان بر آنهاست و ايشانند راه‌يافتگان.'),
(165, 2, 158, 'صفا و مروه از نشانه‌هاى [مراسم طاعت و عبادت‌] خداست. پس هر كه حج- آهنگ- خانه كند يا عُمره- زيارت- گزارد بر او ناروا و گناه نيست كه به گرد آن دو بگردد. و هر كه كار نيكى به خواست خويش كند خداوند سپاسدار و داناست.'),
(166, 2, 159, 'كسانى كه آنچه را ما از حجّتهاى روشن و رهنمونى فروفرستاديم، پس از آنكه آن را براى مردم در كتاب بيان كرديم، مى‌پوشانند خدا و لعنت‌كنندگان لعنتشان مى‌كنند'),
(167, 2, 160, 'مگر آنان كه بازآمدند- توبه كردند- و به اصلاح [كژيها و تباهيها] پرداختند و [حق را كه پنهان داشته بودند] آشكار ساختند، پس [به مهر و بخشايش خود] بر آنان بازمى‌گردم و توبه آنها را مى‌پذيرم كه من توبه‌پذير و مهربانم.'),
(168, 2, 161, 'آنان كه كافر شدند و كافر مُردند، لعنت خدا و فرشتگان و همه مردم بر آنهاست،'),
(169, 2, 162, 'جاودانه در آن باشند، عذابشان سبك نشود و مهلت نيابند.'),
(170, 2, 163, 'و خداى شما خدايى است يگانه، خدايى جز او نيست، بخشاينده است و مهربان.'),
(171, 2, 164, 'همانا در آفرينش آسمانها و زمين، و آمد و شد شب و روز، و كشتى‌ها كه در دريا به سود مردم روان مى‌شوند، و آبى كه خدا از آسمان فرو فرستاد و با آن زمين را پس از مُردگى‌اش زنده كرد و از هر جنبنده‌اى در آن پراكند، و گرداندن بادها و ابر رام‌شده ميان آسمان و زمين، نشانه‌هايى است براى مردمى كه خِرد را به كار برند.'),
(172, 2, 165, 'از مردمان كسانى هستند كه به جاى خدا همتايانى [به عبادت‌] مى‌گيرند و آنها را دوست مى‌دارند مانند دوستى خدا ولى كسانى كه ايمان آورده‌اند در دوستى خدا سخت‌ترند- خدا را بسى بيشتر از ايشان دوست مى‌دارند- و اگر كسانى كه ستم كردند آنگاه كه عذاب را ببينند مى‌ديدند كه همه نيرو و توانايى خداى راست و عذاب خدا سخت و سهمگين است [البتّه پشيمان و حسرت‌زده خواهند شد]'),
(173, 2, 166, 'آنگاه كه پيشوايان از پيروان بيزارى نمايند و عذاب را ببينند و رشته‌هاى پيوندشان گسسته گردد'),
(174, 2, 167, 'و آنان كه پيروى كردند گويند: كاش ما را بازگشتى مى‌بود تا از آنها بيزارى مى‌نموديم چنانكه از ما بيزارى نمودند. خداوند اينگونه كردارهاشان را مايه پشيمانى و اندوه به آنان مى‌نماياند و هرگز از آتش دوزخ بيرون نيايند.'),
(175, 2, 168, 'اى مردم، از آنچه در زمين حلال و پاكيزه است بخوريد و از پى گامهاى شيطان مرويد، كه او شما را دشمنى است آشكار'),
(176, 2, 169, 'همانا او شما را به بدى و زشتكارى فرمان مى‌دهد و اينكه در باره خدا آنچه نمى‌دانيد بگوييد.'),
(177, 2, 170, 'و چون به آنها- مشركان- گفته شود كه از آنچه خدا فرو فرستاده پيروى كنيد، گويند: بلكه از آنچه پدران خويش را بر آن يافته‌ايم پيروى مى‌كنيم آيا [از آنها پيروى مى‌كنند] هر چند كه پدرانشان چيزى نمى‌فهميدند و رهيافته نبودند؟'),
(178, 2, 171, 'داستان [خواننده‌] كسانى كه كافر شدند چون داستان كسى است كه به چيزى بانگ مى‌زند كه نمى‌شنود مگر خواندنى و آوايى كرانند، گنگانند، كورانند پس هيچ درنمى‌يابند.'),
(179, 2, 172, 'اى كسانى كه ايمان آورده‌ايد، از پاكيزه‌هايى كه شما را روزى كرده‌ايم بخوريد و خداى را سپاس گزاريد اگر او را مى‌پرستيد و بس.'),
(180, 2, 173, 'جز اين نيست كه مردار و خون و گوشت خوك و آنچه را كه [هنگام كشتن‌] نام غير خدا بر آن خوانده شده بر شما حرام كرده است و هر كه [به خوردن اينها] ناچار شود در حالى كه نه ستمكار و سركش باشد و نه از حدّ گذرنده، گناهى بر او نيست، كه خدا آمرزگار و مهربان است.'),
(181, 2, 174, 'همانا كسانى كه آنچه را خدا از كتاب فروفرستاده پنهان مى‌كنند و چيز اندكى به بهاى آن مى‌ستانند، اينان در شكمهاى خويش جز آتش نمى‌خورند و خداوند روز رستاخيز با آنان سخن نگويد و پاكشان نسازد و آنها را عذابى است دردناك.'),
(182, 2, 175, 'اينان كسانى‌اند كه گمراهى را به [بهاى‌] رهيابى و عذاب را به [بهاى از دست دادن‌] آمرزش خريدند، پس چه شكيبا و دليرند بر آتش!'),
(183, 2, 176, 'آن [عذاب‌] بدين سبب است كه خداوند اين كتاب را براستى و درستى فروفرستاده و كسانى كه در كتاب- كتاب خدا- اختلاف كرده‌اند هر آينه در ستيزگى و جدايى دورى [از حق‌] اند.'),
(184, 2, 177, 'نيكى [همه‌] آن نيست كه روى خويش به سوى خاور و باختر فرا داريد بلكه نيكى [نيكى‌] آن كس است كه به خدا و روز بازپسين و فرشتگان و كتاب و پيامبران ايمان آورده و مال را با اينكه دوست دارد- يا از بهر دوستى خدا- به خويشاوندان و يتيمان و بينوايان و واماندگان در راه و خواهندگان و در [راه آزادىِ‌] بردگان بدهد و نماز برپا دارد و زكات بدهد، و نيز وفاداران به پيمان خويش چون پيمان بندند، و [بويژه‌] شكيبايان در بينوايى و تنگدستى و رنج و سختى و به هنگام كارزار. آنانند كه راست گفتند و آنانند پرهيزگاران.'),
(185, 2, 178, 'اى كسانى كه ايمان آورده‌ايد، بر شما در باره كشتگان قصاص- خونخواهى- نوشته شد: آزاد به آزاد و بنده به بنده و زن به زن پس هر كه از سوى برادر [دينىِ‌] خود چيزى [از قصاص‌] برايش بخشوده شد- يعنى حكم قصاص به پرداخت خونبها بدل گرديد- پس پيش‌گرفتن رفتارى نيك و پسنديده بايد- بخشنده در پيگيرى خونبها و بخشوده‌شده در سپاس‌داشتن- و پرداخت [خونبها از سوى قاتل‌] به او- ولىّ مقتول- به نيكى گزارده شود. اين تخفيف و مهر و بخشايشى است از پروردگارتان، و هر كه پس از آن- عفوكردن و ديه‌ستاندن- از اندازه درگذرد او را عذابى است دردناك.'),
(186, 2, 179, 'اى خردمندان، شما را در قصاص، زندگانى است تا شايد پروا كنيد.'),
(187, 2, 180, 'بر شما نوشته شد كه چون يكى از شما را [نشانه‌هاى‌] مرگ فرارسد هر گاه مالى [بسيار] به جا گذارد، براى پدر و مادر و خويشان- كه ارث نمى‌برند- به نيكويى وصيّت كند، كه اين سزاوار پرهيزگاران است.'),
(188, 2, 181, 'پس هر كه وصيّت را پس از آنكه شنيد دگرگون سازد، هر آينه گناهش بر كسانى است كه آن را دگرگون سازند، همانا خدا شنوا و داناست.'),
(189, 2, 182, 'پس كسى كه از وصيت‌كننده‌اى ستم يا گناهى را بيم دارد، و از اينرو ميانشان سازش دهد او را گناهى نيست، همانا خدا آمرزگار و مهربان است.'),
(190, 2, 183, 'اى كسانى كه ايمان آورده‌ايد، بر شما روزه نوشته شد چنانكه بر آنان كه پيش از شما بودند نوشته شد، باشد كه پرهيزگار شويد.'),
(191, 2, 184, 'روزهايى چند، شمرده پس هر كه از شما بيمار يا در سفر باشد، به شمار آن از روزهاى ديگر روزه بدارد، و بر كسانى كه روزه توانفرسا باشد بازخريدى است كه آن طعام‌دادن درويش درمانده است- هر روز يك خوراك- پس هر كه به خواست خود كار نيكى كند براى او بهتر است و روزه‌داشتن براى شما بهتر است اگر مى‌دانستيد'),
(192, 2, 185, 'ماه رمضان، كه در آن قرآن فروفرستاده شده، كه راهنمايى براى مردم و نشانه‌هاى روشنى از رهنمونى [به راه راست‌] و جداكننده حق از باطل است پس هر كس از شما اين ماه را دريابد بايد آن را روزه بدارد و هر كه بيمار يا در سفر باشد به شمار آن از روزهاى ديگر. خدا براى شما آسانى مى‌خواهد و براى شما دشوارى نمى‌خواهد، و تا شمار آن را تمام كنيد و خداى را به پاس آنكه شما را راه نمود به بزرگى بستاييد و باشد كه سپاس گزاريد.'),
(193, 2, 186, 'و چون بندگانم در باره من از تو پرسند، همانا من نزديكم خواندن خواننده را آنگاه كه مرا بخواند پاسخ مى‌دهم، پس بايد دعوت مرا بپذيرند و به من ايمان آورند، باشد كه ره يابند.'),
(194, 2, 187, 'در شب روزه آميزش با زنانتان براى شما حلال شد آنان پوشش شمايند و شما پوشش آنان. خداى دانست كه شما به خود خيانت مى‌كرديد، پس [به بخشايش خويش‌] بر شما بازگشت و توبه شما را پذيرفت و از شما درگذشت اكنون [توانيد] با ايشان بياميزيد و آنچه خداوند براى شما نوشته بجوييد، و بخوريد و بياشاميد تا شما را رشته سپيدى [صبحدم‌] از رشته سياه [شبانگاه‌] پايدار گردد، و سپس روزه را تا شب به پايان رسانيد. و در حالى كه در مسجدها معتكف هستيد با زنان مياميزيد. اينها حدود- احكام- خداست، پس پيرامون آنها مگرديد. خداوند اينچنين آيات خويش را براى مردم بيان مى‌كند تا مگر پرهيزگار شوند.'),
(195, 2, 188, 'و مالهاى يكديگر را در ميان خود به ناشايست مخوريد و آن را به [رشوت‌] نزد حاكمان- قاضيان- ميفكنيد تا برخى از مالهاى مردم را به گناه بخوريد، در حالى كه مى‌دانيد [كه ستم مى‌كنيد].'),
(196, 2, 189, 'تو را از ماههاى نو پرسند، بگو: آنها گاهنماهاست براى مردم و حجّ. و نيكى آن نيست كه به خانه‌ها از پشت آنها در آييد، و ليكن نيكى [نيكى‌] آن كس است كه پرهيزگارى كند، و به خانه‌ها از درهاى آنها در آييد و از خدا پروا كنيد، باشد كه رستگار شويد.'),
(197, 2, 190, 'و در راه خدا با كسانى كه با شما كارزار كنند كارزار كنيد و از حدّ مگذريد كه خدا از حدّگذرندگان را دوست ندارد.'),
(198, 2, 191, 'آنان- مشركان و كافران حربى- را هر جا كه يابيد بكشيد و از همان جا كه بيرونتان كردند- مكه- بيرونشان كنيد و فتنه [اى كه آنها كردند]- كفر و شرك و شكنجه‌كردن و بيرون‌راندن مؤمنان- از كشتار [ى كه شما كنيد] بدتر است. و نزد مسجد الحرام با آنها كارزار مكنيد مگر آنكه در آنجا با شما كارزار كنند، پس اگر با شما كارزار كردند بكشيدشان، كه سزاى كافران چنين است.'),
(199, 2, 192, 'و اگر باز ايستادند، خداوند آمرزگار و مهربان است.'),
(200, 2, 193, 'با آنان كارزار كنيد تا آشوبى نماند و دين از آنِ خداى باشد پس اگر باز ايستادند، تجاوز روا نيست مگر بر ستمكاران.'),
(201, 2, 194, '[اين‌] ماه حرام به [آن‌] ماه حرام، و [شكستن حرامها و] حرمتها را قصاص است. پس هر كه بر شما تجاوز كند بمانندِ آن بر او تجاوز كنيد- از اندازه مگذريد- و از خدا پروا داشته باشيد و بدانيد كه خدا با پرهيزگاران است.'),
(202, 2, 195, 'در راه خدا هزينه كنيد و [با ترك اين هزينه‌] خود را به دست خويش به هلاكت ميفكنيد، و نيكويى كنيد كه خدا نيكوكاران را دوست دارد.'),
(203, 2, 196, 'حجّ و عمره را براى خدا تمام به جاى آوريد، و اگر بازداشته شويد، پس آنچه از قربانى ميسّر است، و سرهاى خويش متراشيد تا قربانى به جايگاهش برسد، و هر كه از شما بيمار است يا رنجى در سر دارد [كه ناچار است سر را بتراشد] پس فديه‌اى [بايد] از روزه يا صدقه يا قربانى و چون ايمن شديد، هر كه از عمره تمتع به حجّ در آيد پس آنچه از قربانى ميسّر است، و هر كه قربانى نيابد- يا نتواند- سه روز در ايام حج روزه بدارد و هفت روز هنگامى كه [از سفر] بازگرديد. اين دهِ روز كامل است. اين حكم براى كسى است كه خاندانش نزديك مسجد الحرام نباشند- يعنى مُقيم مكه و اطراف آن نباشد- و از خدا پروا كنيد و بدانيد كه خدا سخت كيفر است.'),
(204, 2, 197, 'حج در ماه‌هايى دانسته- شوال، ذو القعده و ذو الحجه- است، پس هر كه در آن ماه‌ها فريضه حج مى‌گزارد [بداند كه‌] در حج آميزش [با همسران‌] و نافرمانى خدا- به گفتن دروغ و دشنام و مانند اينها- و ستيزه نيست. و هر كار نيكى كه كنيد خدا آن را مى‌داند و توشه بر گيريد كه بهترين توشه پرهيزگارى است و اى خردمندان، از من پروا داشته باشيد.'),
(205, 2, 198, 'بر شما گناهى نيست كه فزونيى- روزى از راه تجارت- از پروردگارتان بجوييد. و چون از عرفات بازگشتيد خداى را در مشعر الحرام- مُزدلفه- ياد كنيد و او را ياد كنيد به پاس آنكه شما را كه پيش از آن هر آينه از گمراهان بوديد راه نمود.'),
(206, 2, 199, 'آنگاه از آن راه كه مردم بازمى‌گردند بازگرديد و از خدا آمرزش بخواهيد كه او آمرزگار و مهربان است.'),
(207, 2, 200, 'و چون مناسك حج خود را گزارديد، خداى را ياد كنيد آنگونه كه پدران خود را ياد مى‌كنيد، يا بيشتر از آن. از مردمان كسانى هستند كه گويند: پروردگارا، ما را [آنچه‌] در دنيا [خواهيم‌] بده و آنها را در آخرت بهره‌اى نيست.'),
(208, 2, 201, 'و از آنان كسانى‌اند كه گويند: پرودگارا، ما را در اين جهان نيكويى- نعمت- ده و در آن جهان نيز نيكويى بخش و ما را از عذاب آتش دوزخ نگاه دار.'),
(209, 2, 202, 'اينان از آنچه كرده‌اند بهره‌اى- پاداشى- دارند، و خداوند زود حساب است.'),
(210, 2, 203, 'خداى را در روزهايى چند- سه روز تشريق- ياد كنيد، پس هر كه در دو روز شتاب كند- يعنى اعمال را در دو روز انجام دهد و بازگشت از منى را به روز سوم نكشاند- گناهى بر وى نيست، و هر كه تاخير كند- تا آخر روزهاى تشريق- نيز گناهى بر او نيست، براى كسى كه [از محرمات احرام‌] بپرهيزد. و از خدا پروا كنيد و بدانيد كه به سوى او برانگيخته و فراهم مى‌شويد.'),
(211, 2, 204, 'و از مردم كس هست كه گفتار او در زندگى اين جهان تو را به شگفت آرد و خدا را بر آنچه در دل دارد گواه گيرد و حال آنكه سخت‌ترين دشمنان- يا ستيزندگان- است.'),
(212, 2, 205, 'و چون والى و سرپرست كارى شود- و قدرت و رياست يابد يا چون پشت كند و از نزد تو بازگردد- مى‌كوشد تا در زمين تباهى كند و كشت و نسل- دام يا نژاد- را نابود سازد و خدا تباهكارى را دوست نمى‌دارد.'),
(213, 2, 206, 'و چون به او گويند: از خداى بترس، بزرگ‌منشى او را به گناه وادارد- يا عزت ظاهرى كه با گناه به دست آورده او را مى‌گيرد-. دوزخ او را بس است و هر آينه بد بسترى است.'),
(214, 2, 207, 'و از مردم كس هست كه جان خويش به جستن خشنودى خدا مى‌فروشد و خدا به بندگان مهربان است.'),
(215, 2, 208, 'اى كسانى كه ايمان آورده‌ايد، همگى در آشتى و سازش- يا اسلام و اطاعت- در آييد و در پى گامهاى شيطان مرويد كه او شما را دشمنى هويداست.'),
(216, 2, 209, 'پس اگر از پس آن نشانه‌هاى روشن كه به شما رسيد لغزش كرديد، بدانيد كه خدا تواناى بى‌همتا و داناى استوار كار است.'),
(217, 2, 210, 'آيا اينان جز اين انتظار دارند كه خدا با فرشتگان در سايبانهايى از ابر بديشان آيد- به نشانه عذاب- و كار گزارده شود؟ و همه كارها به خدا بازگردانده مى‌شود.'),
(218, 2, 211, 'از فرزندان اسرائيل- يعقوب- بپرس كه چند از نشانه‌هاى روشن به آنان داديم و هر كه نعمت خدا را پس از آنكه به او رسيد ديگرگون سازد، پس [بداند كه‌] خدا سخت كيفر است.'),
(219, 2, 212, 'براى كسانى كه كافر شدند زندگى دنيا آرايش يافته است و مؤمنان را ريشخند مى‌كنند و كسانى كه پرهيزگارند روز رستاخيز از آنان بالاترند و خداوند هر كه را خواهد بى‌شمار روزى مى‌دهد.'),
(220, 2, 213, 'مردم همه يك گروه- يكسان و بر يك راه و روش و آيين- بودند، [سپس اختلاف كردند] پس خدا پيامبران را برانگيخت نويددهنده و بيم‌كننده، و با ايشان كتاب فرو فرستاد براستى و درستى، تا ميان مردم در آنچه اختلاف كردند حكم كند، و در آن اختلاف نكردند مگر همان كسان كه آن'),
(221, 2, 214, 'آيا پنداشته‌ايد كه به بهشت مى‌رويد و حال آنكه هنوز داستان آنان- خبر دردها و رنجهاى آنها- كه پيش از شما بگذشتند به شما نرسيده است؟ بديشان سختى و رنج و گزند رسيد و چنان پريشان و لرزان شدند كه پيامبر و مؤمنانى كه با او بودند گفتند: يارى خدا كى خواهد رسيد؟ آگاه باشيد كه يارى خدا نزديك است.'),
(222, 2, 215, 'از تو مى‌پرسند چه چيز انفاق كنند؟ بگو: آنچه از مال انفاق مى‌كنيد براى پدر و مادر و خويشان و يتيمان و نيازمندان درمانده و در راه‌ماندگان باشد، و هر كار نيكى كه كنيد خدا به آن داناست.'),
(223, 2, 216, 'بر شما كارزار نوشته شد و آن شما را دشوار و ناخوش است و شايد كه چيزى را خوش نداريد و آن براى شما بهتر باشد و شايد كه چيزى را دوست داريد و آن براى شما بدتر باشد، و خدا [صلاح شما را] مى‌داند و شما نمى‌دانيد.'),
(224, 2, 217, 'تو را كارزار كردن در ماه حرام- محترم و شكوهمند- پرسند، بگو: كارزار كردن در آن [كارى‌] بزرگ است و [ليكن‌] بازداشتن [مردم‌] از راه خدا و كفر ورزيدن به او و مسجد الحرام و بيرون كردن اهل آن نزد خدا [ناروايى‌] بزرگتر است و فتنه [كفر و شرك و بيرون راندن مؤمنان‌] از كشتن بزرگتر- بدتر- است. و [مشركان‌] پيوسته با شما كارزار مى‌كنند تا اگر بتوانند شما را از دينتان برگردانند و هر كه از شما از دين خود برگردد و كافر بميرد، كارهاى اينها در اين جهان و آن جهان تباه و نابود شود و همينان دوزخيانند و هميشه در آن بمانند.'),
(225, 2, 218, 'همانا كسانى كه ايمان آورده‌اند و كسانى كه هجرت كرده و در راه خدا جهاد كرده‌اند، به مِهر و بخشايش خدا اميد دارند، و خدا آمرزگار و مهربان است.'),
(226, 2, 219, 'تو را از مىْ و قمار مى‌پرسند، بگو: در آنها گناهى است بزرگ و سودهايى براى مردم و گناه آنها از سودشان بزرگتر است. و از تو مى‌پرسند كه چه انفاق كنند؟ بگو: افزون [از نياز] را. بدين گونه خدا آيات را براى شما روشن بيان مى‌كند، شايد بينديشيد،'),
(227, 2, 220, 'در باره اين جهان و آن جهان. و تو را از يتيمان پرسند، بگو: اصلاح كارشان بهتر است، و اگر با ايشان [در زندگى‌] بياميزيد، برادران شمايند- پس با آنان برادرانه رفتار كنيد- و خدا تباهكار را از نيكوكار باز مى‌شناسد و اگر خدا بخواهد شما را به تنگى و رنج مى‌افكند، همانا خدا تواناى بى‌همتا و داناى استواركار است.'),
(228, 2, 221, 'و زنان مشرك را به همسرى مگيريد تا ايمان آورند، و كنيز با ايمان بِهْ از زن مشرك است هر چند كه شما را خوش آيد و به شگفت آرد و به مردان مشرك زن با ايمان مدهيد تا ايمان آورند، و بنده مؤمن بِهْ از مرد مشرك است هر چند كه شما را خوش آيد و به شگفت آرد آنها به آتش دوزخ مى‌خوانند و خداوند به خواست و فرمان خويش به بهشت و آمرزش مى‌خواند و آيات خود را براى مردم روشن بيان مى‌كند، باشد كه به ياد آرند و پند گيرند.'),
(229, 2, 222, 'تو را از حيض مى‌پرسند، بگو: آن رنج و آزارى است. پس در حال حيض از [آميزش با] زنان كناره گيريد و به آنان نزديك مشويد تا پاك شوند. پس چون خود را پاك كنند- غسل كنند-، آنسان كه خدا فرمانتان داده است به آنان در آييد، كه خدا توبه‌كاران را دوست دارد و پاكيزگان را دوست دارد.'),
(230, 2, 223, 'زنانتان كشتزار شمايند به كشتزار خويش هر جا و هر گاه كه خواهيد در آييد- مگر در موارد ممنوع كه در فقه آمده است-. و براى خود [كار نيك‌] پيش فرستيد و از خدا بترسيد و بدانيد كه با او ديدار خواهيد كرد و مؤمنان را مژده ده.'),
(231, 2, 224, 'و خداى را دستاويزى براى سوگندهاى خويش مسازيد كه نيكى و پرهيزگارى و اصلاح ميان مردم نكنيد، و خدا شنوا و داناست.'),
(232, 2, 225, 'خداوند شما را به سوگندهاى بيهوده و ناسنجيده‌تان بازخواست نمى‌كند و ليكن به آنچه دلهاى شما [قصد] كرده است- سوگند خوردن از روى قصد و عمد و آهنگ شكستن آن- بازخواست مى‌كند و خدا آمرزگار و بردبار است.'),
(233, 2, 226, 'آنان كه سوگند مى‌خورند كه از زنان خود دور باشند چهار ماه درنگ كنند، پس اگر بازآيند خداوند آمرزگار و مهربان است.'),
(234, 2, 227, 'و اگر آهنگ طلاق كردند هر آينه خدا شنوا و داناست.'),
(235, 2, 228, 'و زنان طلاق‌داده تا سه پاكى درنگ كنند- منتظر بمانند و از شوهر كردن باز ايستند-، و اگر به خدا و روز واپسين ايمان دارند براى آنها روا نيست كه آنچه را خدا در زِهْدان آنان آفريده است پنهان كنند و شوهرانشان در آن [مدت‌] اگر خواهان اصلاح باشند به بازگرداندن آنها سزاوارترند. و براى آن زنان [حقوقى‌] است مانند آنچه [از تكاليف‌] بر آنهاست به شيوه نيكو و پسنديده، و مردان را بر آنان پايه‌اى [برتر] است و خدا تواناى بى‌همتا و داناى با حكمت است.'),
(236, 2, 229, 'طلاق- طلاق رجعى كه در آن شوهر به زن باز تواند گشت- دو بار است. پس از آن به نيكويى نگاه‌داشتن يا به نيكى رهاساختن. و شما را حلال نيست كه از آنچه به ايشان داده‌ايد چيزى باز ستانيد مگر آنكه هر دو بترسند كه نتوانند حدود- مرزها و احكام- خدا را برپا دارند- به سبب ناسازگارى اخلاق زن و شوهر-. پس اگر ترسيديد كه حدود خدا را برپا ندارند- به سبب بى‌ميلى زن به شوهر- گناهى بر آن دو نيست در آنچه زن عوض [طلاق خود] بدهد- كابين بخشد تا خود را برهاند- اينها مرزهاى خداست، از آنها فراتر مرويد و هر كه از مرزهاى خدا فراتر رود چنين كسان ستمكارند.'),
(237, 2, 230, 'پس اگر [بار سوم‌] وى را طلاق داد ديگر بر او حلال نيست تا آنكه شوهرى جز او كند پس اگر [اين شوهر] زن را طلاق دهد، گناهى بر آن دو نيست كه به يكديگر بازگردند، اگر گمان داشته باشند كه حدود خدا را برپا مى‌دارند. اينها مرزهاى خداست كه براى گروهى كه بدانند بيان مى‌كند.'),
(238, 2, 231, 'و چون زنان را طلاق داديد و به [نزديك‌] پايان عده خود رسيدند يا به نيكويى نگاهشان داريد يا به نيكويى رهاشان سازيد، و براى زيان رساندن نگاهشان مداريد تا ستم كرده باشيد. و هر كه چنين كند براستى بر خود ستم كرده است. و آيات خدا را به شوخى و ريشخند مگيريد، و نعمتى را كه خدا به شما ارزانى داشته و آنچه را از كتاب و حكمت بر شما فرو فرستاده است كه شما را بدان پند مى‌دهد به ياد آريد، و از خدا پروا كنيد و بدانيد كه خدا به همه چيز داناست.'),
(239, 2, 232, 'و چون زنان را طلاق داديد و به پايان عده خود رسيدند، آنها را باز مداريد از اينكه با شوهران خويش زناشويى كنند آنگاه كه ميان خود به شيوه‌اى پسنديده خشنود شوند. به اين [دستورها] پند داده مى‌شود هر كسى از شما كه به خدا و روز واپسين ايمان دارد اين براى شما بهتر و پاكيزه‌تر است، و خدا [صلاح كار را] مى داند و شما نمى‌دانيد.'),
(240, 2, 233, 'و مادران، فرزندان خود را دو سال تمام شير دهند [اين حكم‌] براى كسى است كه بخواهد شيردادن را تمام كند. و خوراك و پوشاك آنان به وجه شايسته و پسنديده بر آن كس است كه فرزند از اوست، هيچ كس بيش از توانش مكلف نيست. هيچ مادرى و هيچ پدرى نبايد به سبب فرزندش زيان بيند و [اگر پدر نباشد] بر وارث نيز همچنان [هزينه خوراك و پوشاك‌] است، و اگر پدر و مادر با خشنودى و مشورت يكديگر بخواهند [كودك را] از شير بازگيرند گناهى بر آنها نيست، و اگر بخواهيد براى شيردادن فرزندانتان دايه بگيريد هر گاه آنچه را كه بايد بدهيد به شايستگى داده باشيد گناهى بر شما نيست و از خدا پروا كنيد و بدانيد كه خداوند بدانچه مى‌كنيد بيناست.'),
(241, 2, 234, 'و از شما كسانى كه بميرند و زنانى باز گذارند، اين زنان بايد چهار ماه و ده روز درنگ كنند- عده بدارند- و چون به پايان عده خويش رسيدند در كار شايسته و پسنديده‌اى كه در باره خويشتن كنند- ازدواج با همسر دلخواه- گناهى بر شما نيست و خداوند به آنچه مى‌كنيد آگاه است.'),
(242, 2, 235, 'و در آنچه از خواستارى آن زنان- كه در عدّه هستند- به اشاره و در پرده گوييد يا در دل خويش نهان داريد گناهى بر شما نيست خدا مى‌داند كه از آنها ياد خواهيد كرد، و ليكن با آنان وعده پنهانى [براى نكاح‌] مكنيد مگر آنكه سخنى نيكو- به اشاره و در پرده- گوييد اما آهنگ بستن عقد زناشويى مكنيد تا مدت مقرر به سر آيد و بدانيد كه خدا آنچه را در دل داريد مى‌داند پس، از [نافرمانى و كيفر] او بترسيد و بدانيد كه خدا آمرزگار و بردبار است.'),
(243, 2, 236, 'گناهى بر شما نيست اگر زنانى را كه با آنها نزديكى نكرده‌ايد يا كابينى برايشان مقرر نكرده‌ايد طلاق دهيد و برخوردار و بهره‌مندشان سازيد، فراخْدَست به اندازه توان خود و تنگدست به اندازه توان خود، كه [اين‌] سزاوار نيكوكاران است.'),
(244, 2, 237, 'و اگر زنان را پيش از آنكه با آنها نزديكى كرده باشيد طلاق دهيد و كابينى معين كرده‌ايد نيم آنچه مقرر داشته‌ايد بر شماست، مگر آنكه خود آنها يا كسى كه گره زناشويى به دست اوست گذشت كند و گذشت شما به پرهيزگارى نزديكتر است و بخشش و بزرگوارى را ميان خودتان فراموش مكنيد كه خدا بدانچه مى‌كنيد بيناست.'),
(245, 2, 238, 'بر نمازها و [بويژه‌] نماز ميانه مواظبت كنيد، و خدا را با فروتنى به فرمانبردارى بايستيد.'),
(246, 2, 239, 'پس اگر [از دشمنى يا خطرى‌] بيم داشتيد، پياده يا سواره نماز كنيد، و چون ايمن شديد خدا را ياد كنيد، به پاس آنكه به شما آنچه را نمى‌دانستيد آموخت.'),
(247, 2, 240, 'و از شما كسانى كه بميرند و زنانى باز گذارند براى زنانشان سفارش كنند كه تا يك سال برخوردار شوند بدون بيرون‌كردن [از خانه‌]. پس اگر خود بيرون شوند، در آنچه به وجه شايسته و پسنديده براى خود كنند گناهى بر شما نيست. و خدا تواناى بى‌همتا و داناى با حكمت است.'),
(248, 2, 241, 'و زنان طلاق داده را بهره‌اى است به شيوه پسنديده، كه بر پرهيزگاران سزاست.'),
(249, 2, 242, 'خدا آيات خود را اينچنين براى شما روشن بيان مى‌كند شايد خِرد را كار بنديد.'),
(250, 2, 243, 'آيا ننگريستى به آن هزاران تن كه از بيم مرگ از خانه‌هاى خود بيرون رفتند؟ پس خدا آنان را گفت: بميريد سپس زنده‌شان كرد. هر آينه خدا خداوند نيكويى و افزون‌دهى [نعمت‌] بر مردم است ولى بيشتر مردم سپاس نمى‌گزارند.'),
(251, 2, 244, 'در راه خدا كارزار كنيد و بدانيد كه خدا شنوا و داناست.'),
(252, 2, 245, 'كيست كه خداى را وام دهد وامى نيكو، تا او را افزون دهد افزونيهاى بسيار؟ و خدا [روزى را] تنگ و فراخ مى‌كند، و بازگشت شما به سوى اوست.'),
(253, 2, 246, 'آيا ننگريستى به آن گروه از فرزندان اسرائيل پس از موسى كه به پيامبر خود گفتند: براى ما پادشاهى برانگيز تا در راه خدا كارزار كنيم؟ گفت: آيا احتمال مى‌دهيد كه اگر كارزار بر شما نوشته شود كارزار نكنيد؟ گفتند: ما را چيست كه در راه خدا كارزار نكنيم و حال آنكه از خانمان و فرزندانمان بيرون رانده شده‌ايم؟ و چون كارزار بر آنها نوشته شد جز اندكى پشت كردند، و خداوند به ستمكاران داناست.'),
(254, 2, 247, 'و پيامبرشان به آنها گفت: خداوند طالوت را به پادشاهى شما برانگيخت. گفتند: چگونه او را بر ما پادشاهى باشد و ما به پادشاهى از وى سزاوارتريم و او را گشايشى از مال نداده‌اند. گفت: خدا او را بر شما برگزيده است و در دانش و تن فزونى و فراخى داده است، و خداوند پادشاهى خود را به هر كه خواهد دهد و خدا فراخى‌بخش و داناست.'),
(255, 2, 248, 'و پيامبرشان به آنها گفت كه نشانه پادشاهى او اين است كه آن صندوق [عهد] كه در آن آرامشى از پروردگارتان و بازمانده‌اى از ميراث خاندان موسى و خاندان هارون است و فرشتگان آن را برمى‌دارند نزد شما آيد. همانا در اين براى شما نشانه‌اى است، اگر مؤمن باشيد.'),
(256, 2, 249, 'چون طالوت با سپاهيان بيرون آمد، گفت: خداوند شما را به جوى آبى مى‌آزمايد، پس هر كه از آن بياشامد از من نيست، و هر كه از آن نخورد از من است، مگر آن كه كف دستى آب برگيرد- كه از نافرمانان به شمار نيايد- پس جز اندكى همه از آن بياشاميدند. پس چون او و مؤمنان همراهش از جوى گذشتند، گفتند: امروز ما را توان برابرى با جالوت و لشكريان او نيست [اما] كسانى كه مى‌دانستند كه خداى را ديدار خواهند كرد گفتند: چه بسا گروهى اندك كه به خواست خدا بر گروهى بسيار پيروز شده‌اند و خدا با شكيبايان است.'),
(257, 2, 250, 'و چون با جالوت و سپاه او روبرو شدند، گفتند: پروردگارا، بر ما شكيبايى فروريز و گامهامان استوار بدار و ما را بر گروه كافران يارى و پيروزى ده.'),
(258, 2, 251, 'پس به خواست و فرمان خدا آنها را در هم شكستند و داوود، جالوت را بكشت و خداوند به او پادشاهى و حكمت داد و از آنچه مى‌خواست او را بياموخت. و اگر نبود بازداشتن خدا برخى از مردم را به برخى ديگر، هر آينه زمين- و اهل آن- تباه شده بود، و ليكن خدا خداوند فزون‌بخشى بر جهانيان است.'),
(259, 2, 252, 'اين آيات خداست كه براستى و درستى بر تو مى‌خوانيم و همانا تو از فرستادگانى.'),
(260, 2, 253, 'آن پيامبران، برخى‌شان را بر برخى برترى داديم از ايشان كس بود كه خدا با وى سخن گفت، و برخى‌شان را پايه‌ها بالا برد و عيسى پسر مريم را حجت‌ها و نشانه‌هاى روشن داديم و او را به روح القدس- جبرئيل- نيرومند گردانيديم. و اگر خدا مى‌خواست آنان كه پس از ايشان بودند بعد از آنكه نشانه‌هاى روشن به آنها رسيد با يكديگر پيكار نمى‌كردند، و ليكن اختلاف كردند، از آنها كس بود كه ايمان داشت و كس بود كه كافر شد و اگر خدا مى‌خواست با هم پيكار نمى‌كردند و ليكن خدا آنچه خواهد مى‌كند.'),
(261, 2, 254, 'اى كسانى كه ايمان آورده‌ايد، از آنچه شما را روزى كرده‌ايم انفاق كنيد پيش از آنكه روزى بيايد كه در آن نه داد و ستدى باشد و نه دوستى و نه شفاعتى و كافران همان ستمكارانند.'),
(262, 2, 255, 'خداى يكتا- الله- جز او خدايى نيست، زنده پاينده است، او را نه خواب سبك فرا گيرد و نه خواب گران او راست آنچه در آسمانها و آنچه در زمين است. كيست آن كه جز به خواست و فرمان او نزد وى شفاعت كند؟ آنچه پيش رو و آنچه از پشت سر ايشان است مى‌داند، و به چيزى از علم او دست نيابند مگر بدانچه خود خواهد. كرسى- دامنه علم و قلمرو فرمانروايى- او آسمانها و زمين را فرا گرفته است، و نگاهداشت آنها بر او دشوار نيست و اوست والا و بزرگ.'),
(263, 2, 256, 'در دين اكراه- به ناخواه واداشتن- نيست. رهيابى [به راه راست‌] از بى‌راهى، روشن و نموده شده پس هر كه به طاغوت كافر شود و به خداى ايمان آرد براستى به دستاويز استوار چنگ زده كه آن را گسستن نيست و خدا شنوا و داناست.'),
(264, 2, 257, 'خدا دوست و كارساز مؤمنان است آنان را از تاريكيها به روشنايى مى‌برد. و كسانى كه كافر شدند دوستان و كارسازانشان طاغوتهايند، آنها را از روشنايى به تاريكى مى‌برند. اينان دوزخيانند و هميشه در آن باشند.'),
(265, 2, 258, 'آيا ننگريستى به آن كس كه با ابراهيم در باره [هستى‌] پروردگارش گفت و گو و ستيزه مى‌كرد از آن رو كه خدا به او پادشاهى داده بود آنگاه كه ابراهيم گفت: پروردگار من آن است كه زنده مى‌كند و مى‌ميراند، او گفت: من هم زنده مى‌كنم و مى‌ميرانم. ابراهيم گفت: همانا خدا آفتاب را از سوى خاور برآرد تو آن را از سوى باختر برآر. پس آن كه كافر شده بود فروماند و سرگشته شد و خدا مردم ستمكار را راه ننمايد.'),
(266, 2, 259, 'يا مانند آن كس- عزير- كه بر دهكده‌اى گذر كرد كه ديوارها و سقفهايش فرو ريخته بود گفت: خدا چگونه اين- اهل اين دِه- را پس از مردنش زنده مى‌كند؟ خداوند او را صد سال ميراند و بازش برانگيخت- زنده‌اش ساخت-، گفت: چند درنگ كرده‌اى؟ گفت: روزى يا برخى از روزى درنگ كردم. گفت: بلكه صد سال است كه درنگ كرده‌اى، به خورش و آشاميدنى‌ات بنگر كه ديگرگون نشده است، و به دراز گوش خود بنگر [كه استخوانهايش پوسيده، تا پاسخ خود را بيابى‌] و تا تو را براى مردم دليل و نشانه‌اى [بر رستاخيز] كنيم، و به استخوانها بنگر كه چگونه مى‌جنبانيم و به هم مى‌آوريم و سپس گوشت بر آن مى‌پوشانيم. پس چون [توانايى خداوند در زنده‌كردن مردگان‌] براى او روشن و هويدا شد گفت: مى‌دانم كه خدا بر هر چيزى تواناست.'),
(267, 2, 260, 'و [ياد كن‌] آنگاه كه ابراهيم گفت: پروردگارا، به من بنماى كه چگونه مردگان را زنده مى‌كنى؟ گفت: مگر باور ندارى؟ گفت: چرا، و ليكن تا دلم آرام گيرد. گفت: چهار پرنده برگير و آنها را نزد خود جمع و پاره پاره كن، سپس بر هر كوه پاره‌اى از آنها بِنِه، آنگاه بخوانشان تا شتابان سوى تو آيند و بدان كه خدا تواناى بى‌همتا و داناى استواركار است.'),
(268, 2, 261, 'داستان آنان كه مالهاى خويش را در راه خدا انفاق مى‌كنند داستان دانه‌اى است كه هفت خوشه بروياند و در هر خوشه صد دانه باشد، و خدا هر كه را خواهد- دو يا چند برابر- فزونى دهد و خداوند فراخى‌بخش و داناست.'),
(269, 2, 262, 'كسانى كه مالهاى خود را در راه خدا انفاق كنند و از پى انفاقى كه كرده‌اند منتى ننهند و نرنجانند، مزد آنان نزد پروردگارشان است، نه بيمى بر آنهاست و نه اندوهگين شوند.'),
(270, 2, 263, 'سخنى نيكو و پسنديده و گذشت [از بدى و درشتى نيازمندان‌] بهتر از صدقه‌اى است كه در پى آن رنجاندنى باشد و خدا بى‌نياز و بردبار است.'),
(271, 2, 264, 'اى كسانى كه ايمان آورده‌ايد، صدقه‌هاى خود را به منّت‌نهادن و رنجانيدن تباه مكنيد مانند آن كس كه مال خويش را براى نماياندن به مردم انفاق مى كند و به خدا و روز واپسين ايمان ندارد [و نتيجه‌اى از انفاق خود نمى‌يابد] داستان او چون داستان سنگ سخت صافى است كه بر آن خاكى- غبارى- باشد و تندبارانى به آن برسد [و آن خاك را ببرد] و سنگ را همچنان سخت و صاف- بدون خاك- بگذارد [رياكاران نيز] بر هيچ چيز از آنچه كرده‌اند دست نمى‌يابند و خداوند مردم كافر را راه ننمايد.'),
(272, 2, 265, 'و داستان كسانى كه مالهاى خود را براى جستن خشنودى خدا و استواركردن [باور و اخلاص‌] دلهاى خويش انفاق مى‌كنند چون داستان بوستانى است بر جايى بلند كه تندبارانى بدان رسد پس بار و بر خود را دو چندان دهد و اگر باران درشت و تندى بدان نرسد باران خرد و نرمى رسد و خداوند به آنچه مى‌كنيد بيناست.'),
(273, 2, 266, 'آيا يكى از شما دوست دارد كه او را بوستانى باشد از خرما و انگور، كه جويها از زير آن روان باشد و او را در آن هر گونه ميوه‌اى باشد، و پيرى بدو فرارسد و او را فرزندانى خرد و ناتوان باشند، پس ناگهان گردبادى آتشبار به آن بوستان رسد و بسوزد؟ خدا آيات خود را براى شما اينچنين روشن بيان مى‌كند شايد بينديشيد.'),
(274, 2, 267, 'اى كسانى كه ايمان آورده‌ايد، از چيزهاى پاك و نيكو كه به دست آورده‌ايد و از آنچه از زمين براى شما بيرون آورده‌ايم انفاق كنيد، و آهنگ انفاق از پليد و ناپاك آن- كه خود نمى‌گيريد مگر با چشم‌پوشى- مكنيد، و بدانيد كه خداوند بى نياز و ستوده است.'),
(275, 2, 268, 'شيطان شما را از درويشى و بينوايى مى‌ترساند و به زشتكارى و ناشايست- بخل و منع صدقات- فرمان مى‌دهد، و خدا شما را به آمرزش خويش و فزونى- درخواسته و پاداش- نويد مى‌دهد و خداوند فراخى‌بخش و داناست.'),
(276, 2, 269, 'حكمت- درستى گفتار و كردار- را به هر كه خواهد مى‌دهد، و هر كه را حكمت دهند براستى او را نيكيهاى بسيار داده‌اند و جز خردمندان ياد نكنند و پند نگيرند.'),
(277, 2, 270, 'و هر انفاق يا نذرى كه كرده‌ايد همانا خدا آن را مى‌داند و ستمكاران را هيچ ياورانى نيست.'),
(278, 2, 271, 'اگر صدقه‌ها را آشكارا دهيد نيك است و اگر آن را پنهان داريد و به بينوايان دهيد براى شما بهتر است و برخى از گناهانتان را از شما مى‌زدايد و خدا بدانچه مى‌كنيد آگاه است.'),
(279, 2, 272, 'هدايت- به راه آوردن- آنان بر [عهده‌] تو نيست بلكه خدا هر كه را بخواهد راه نمايد- به راه آورد-. و هر خيرى- مالى- كه انفاق كنيد براى خودتان است، و جز براى جستن خشنودى خدا انفاق نمى‌كنيد [پس بر كسى منّت منهيد]. و هر خيرى- مالى- كه انفاق كنيد شما را بتمامى باز دهند و بر شما ستم نرود.'),
(280, 2, 273, '[صدقه‌ها] براى نيازمندانى است كه در راه خدا باز داشته شده‌اند و نتوانند [براى جستن روزى‌] در زمين سفر كنند، [ولى‌] نادان ايشان را به سبب خويشتن دارى توانگر پندارد آنان را به نشان چهره‌شان مى‌شناسى، از مردم به اصرار چيزى نخواهند. و هر مالى كه بخشش كنيد همانا خداوند بدان داناست.'),
(281, 2, 274, 'آنان كه مالهاى خود را در شب و روز، پنهان و آشكارا، انفاق مى‌كنند مزدشان نزد پروردگارشان است، نه بيمى بر آنهاست و نه اندوهگين شوند.'),
(282, 2, 275, 'آنها كه ربا مى‌خورند برنخيزند مگر مانند برخاستن كسى كه شيطان او را به سبب آسيب رساندن آشفته و ديوانه گرداند- تعادل روحى و عملى او به هم مى‌خورد-. اين [آسيب‌] از آن روست كه گفتند خريد و فروش مانند رباست با اينكه خدا خريد و فروش را حلال و ربا را حرام كرده است. پس هر كه پند خداوند بدو رسيد و [از رباخوارى‌] باز ايستاد، آنچه گذشت از آن اوست، و كار او با خداست، و هر كه [به ربا خوارى‌] باز گردد، چنين كسان دوزخيانند و در آن جاودانه باشند.'),
(283, 2, 276, 'خداوند ربا را مى‌كاهد و نابود مى‌گرداند و صدقه‌ها را فزونى مى‌بخشد خدا هيچ ناسپاس- يا كفرپيشه- گنهكارى را دوست نمى‌دارد.'),
(284, 2, 277, 'آنان كه ايمان آوردند و كارهاى شايسته كردند و نماز را بر پا داشتند و زكات دادند مزدشان نزد پروردگارشان است نه بيمى بر آنهاست و نه اندوهگين شوند.'),
(285, 2, 278, 'اى كسانى كه ايمان آورده‌ايد از خدا بترسيد و اگر ايمان داريد هر چه از ربا [بر مردم‌] مانده است رها كنيد'),
(286, 2, 279, 'و اگر [رها] نكنيد پس بدانيد كه از سوى خدا و پيامبر او در جنگيد و اگر توبه كنيد سرمايه‌هاى شما از آن شماست، در حالى كه نه ستم مى‌كنيد- به خواستن ربا- و نه بر شما ستم مى‌كنند- به بازندادن سرمايه-.'),
(287, 2, 280, 'و اگر [وامدار] تنگدست بود او را تا فراخدستى مهلتى بايد داد و بخشيدن و صدقه‌دادن شما [به آن تنگدست‌] براى شما بهتر است، اگر مى‌دانستيد.'),
(288, 2, 281, 'و بترسيد از روزى كه شما را به سوى خدا باز گردانند، آنگاه به هر كسى [پاداش‌] آنچه كرده است بتمامى دهند و بر آنان ستم نرود.'),
(289, 2, 282, 'اى كسانى كه ايمان آورده‌ايد، هر گاه به يكديگر دَينى پيدا كرديد- به سبب خريد و فروش، وام و جز اينها- تا سرآمدى نامبرده، پس آن را بنويسيد و نويسنده‌اى ميان شما آن را به درستى و داد بنويسد. و نويسنده به پاس اينكه خدا به او آموخته است نبايد از نوشتن سر باز زند پس بايد بنويسد. و كسى كه حق بر گردن اوست- وامدار و بدهكار- بايد وام‌نامه را املا كند و از خداى، پروردگار خويش، پروا داشته باشد و چيزى از آن نكاهد. پس اگر كسى كه حق بر گردن اوست كم خِرد يا ناتوان- كودك و مانند آن- بود يا نتواند املا كند سرپرست او به درستى و داد املا كند و دو گواه از مردانتان به گواهى گيريد، و اگر دو مرد نبود يك مرد و دو زن از گواهانى كه بپسنديد كه اگر يكى از آن دو فراموش [يا اشتباه‌] كند ديگرى به يادش آرد. و چون گواهان را بخوانند نبايد سر باز زنند. و از نوشتن آن- وام يا بدهى-، كوچك باشد يا بزرگ، تا مهلت آن، دلتنگ و آزرده مشويد. اين [نوشتن شما] نزد خداوند عادلانه‌تر و گواهى را پاينده‌تر و به آنكه در شك و بدگمانى نيفتيد نزديكتر است مگر آنكه بازرگانى نقد باشد كه ميان خود [دست به دست‌] مى‌گردانيد، پس گناهى بر شما نيست كه آن را ننويسيد. و چون خريد و فروش كنيد گواه گيريد و به نويسنده و گواه نبايد زيان رسد و اگر چنين كنيد آن نافرمانى شماست. و از خدا پروا كنيد، و خدا شما را مى‌آموزد، و خدا به هر چيزى داناست.'),
(290, 2, 283, 'و اگر در سفر باشيد و نويسنده‌اى نيابيد پس گروگانى گرفته شود، و اگر يكى از شما ديگرى را امين شمرد آن كه امين شمرده شده است- امانت به وى سپرده شده- امانت را بازدهد و از خداى، پروردگار خويش، پروا كند. و گواهى را پنهان مكنيد، و هر كه آن را پنهان كند دل او گناهكار است، و خدا بدانچه مى‌كنيد داناست.'),
(291, 2, 284, 'خداى راست هر چه در آسمانها و زمين است. و اگر آنچه در دل داريد آشكار يا پنهان كنيد خدا شما را به آن حسابرسى مى‌كند. پس هر كه را خواهد- شايسته بيند- بيامرزد و هر كه را خواهد- سزاوار يابد- عذاب كند، و خدا بر هر چيزى تواناست.'),
(292, 2, 285, 'پيامبر به آنچه از پروردگارش به او فرو فرستاده شده ايمان دارد، و همه مؤمنان به خدا و فرشتگان او و كتابها و پيامبران او ايمان دارند، [و گويند:] ميان هيچ يك از فرستادگان او جدا نكنيم- فرق ننهيم- و گفتند: شنيديم و فرمان برديم- و مى‌شنويم و فرمان مى‌بريم-، پروردگارا، آمرزش تو را خواهانيم و بازگشت [ما] به سوى توست.'),
(293, 2, 286, 'خدا هيچ كس را تكليف نكند مگر به اندازه توانش، او راست هر چه كرده [از كارهاى نيك‌] و بر اوست هر چه كرده [از كارهاى بد]. [و گويند:] پروردگارا، اگر فراموش يا خطا كرديم بر ما مگير. پروردگارا، بر ما تكليف گران منه چنانكه بر آنان كه پيش از ما بودند نهادى، و آنچه را توان آن نداريم بر [دوش‌] ما منه، و از ما درگذر و ما را بيامرز و بر ما ببخشاى، تو سرپرست و ياور مايى، پس ما را بر گروه كافران يارى و پيروزى ده.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الف، لام، ميم.'),
(295, 3, 2, 'خداى يكتا جز او خدايى نيست، زنده و پاينده است.'),
(296, 3, 3, 'و اين كتاب را، كه باوردارنده كتابهاى پيشين است، براستى و درستى بر تو فروفرستاد، و تورات و انجيل را فروفرستاده'),
(297, 3, 4, 'پيش از آن، براى راهنمايى مردم، و فرقان- قرآن جداسازنده حق از باطل- را فرو فرستاد. آنان كه به آيات خدا كافر شدند عذابى سخت دارند، و خدا تواناى بى‌همتا و كين‌ستان است.'),
(298, 3, 5, 'هيچ چيز نه در زمين و نه در آسمان بر خدا پوشيده نيست.'),
(299, 3, 6, 'اوست كه شما را در زهدانها چنانكه خواهد مى‌نگارد. خدايى جز او نيست تواناى بى‌همتا و داناى با حكمت است.'),
(300, 3, 7, 'اوست [آن خداى‌] كه [اين‌] كتاب را بر تو فروفرستاد. برخى از آن، آيه‌هاى محكم‌اند كه آنها مادر و اصل كتاب‌اند و برخى ديگر متشابه‌اند. اما آنها كه در دلشان كژى- انحراف از راه راست- است آنچه را متشابه است پى مى‌گيرند براى فتنه‌جويى و در جستن تأويل آن و حال آنكه تأويل آن را جز خدا نمى داند و آنان كه در دانش استوارند گويند: ما بدان گرويده‌ايم، همه از سوى پروردگار ماست. و اين را جز خردمندان ياد نكنند و پند نگيرند.'),
(301, 3, 8, '[و گويند:] پروردگارا، دلهاى ما را، پس از آنكه ما را راه نمودى، از راستى به كژى مگردان، و ما را از نزد خود بخشايشى ارزانى دار، كه تويى بسيار بخشنده.'),
(302, 3, 9, 'پروردگارا، تويى فراهم‌آورنده مردمان براى روزى كه هيچ شكى در آن نيست همانا خدا وعده را خلاف نكند.'),
(303, 3, 10, 'آنان كه كافر شدند مالها و فرزندانشان هرگز آنان را در برابر [عذاب‌] خدا هيچ سودى نخواهد داشت- به كارشان نيايد و عذاب را از آنان دفع نكند-، و آنها هيمه دوزخند.'),
(304, 3, 11, 'همچون شيوه و روش فرعونيان و آنان كه پيش از آنها بودند كه آيات ما را دروغ انگاشتند، پس خدا آنان را به گناهشان بگرفت، و خدا سخت كيفر است.'),
(305, 3, 12, 'به آنها كه كافر شدند بگو: بزودى شكسته و سركوب خواهيد شد و به دوزخ [رانده و] فراهم مى‌شويد، و دوزخ بد آرامگاهى است.'),
(306, 3, 13, 'همانا شما را در آن دو گروه كه با هم روبرو شدند- در جنگ بدر- نشانه‌اى بود، گروهى در راه خدا كارزار مى‌كردند و گروه ديگر كافر بودند كه آنها- مؤمنان- را به ديدار چشم دو چندان- يا دو چندان خود- مى‌ديدند. و خدا آن را كه خواهد به يارى خويش نيرومند كند. همانا خداوندان بينش را در اين عبرتى است.'),
(307, 3, 14, 'دوستى كامها و آرزوها از زنان و پسران و مالهاى گردكرده و بر هم نهاده از زر و سيم و اسبان نشاندار و ديگر چهارپايان- از شتران و گاوان و گوسفندان- و كشتزارها- و باغ و بوستان- در نظر مردم دلفريب آمده اينها كالاى ناچيز زندگانى اين جهان است و سرانجام نيك نزد خداست.'),
(308, 3, 15, 'بگو: آيا شما را به چيزى بهتر از اينها آگاه كنم؟ براى كسانى كه پرهيزگارى كردند در نزد پروردگارشان بهشتهايى است كه جويها از زير [درختان و كوشكهاى‌] آنها روان است، در آنها جاويدانند، و همسران پاكيزه و خشنودى خداى. و خداوند به [حال‌] بندگان بيناست'),
(309, 3, 16, 'آنان كه گويند: پروردگارا، ما ايمان آورديم پس براى ما گناهانمان را بيامرز و ما را از آتش دوزخ نگاه دار'),
(310, 3, 17, 'آن شكيبايان و راستان- راستگويان و راستكاران- و فرمانبرداران و انفاق‌كنندگان و آمرزش‌خواهان در سحرگاهان.'),
(311, 3, 18, 'خداى يكتا- اللّه- كه هميشه به عدل و داد بود و باشد، گواهى داد كه جز او خدايى نيست و فرشتگان و دانشمندان نيز [گواهى دادند] جز او خدايى نيست، كه تواناى بى‌همتا و داناى استوار كار است.'),
(312, 3, 19, 'همانا دين- دين راست و درست- نزد خدا اسلام است. و كسانى كه كتابشان داده‌اند- جهودان و ترسايان- اختلاف نكردند مگر پس از آنكه دانش و آگاهى [به دين حق‌] بديشان آمد، از روى بدخواهى و حسد ميان خويش و هر كه به آيات خدا كافر شود پس [بداند كه‌] خدا زود حساب است.'),
(313, 3, 20, 'اگر با تو ستيزه و گفتگو كنند، بگو: من روى خود را به سوى خدا نهاده‌ام و پيروانم نيز [چنين كرده‌اند]، و به آنان كه كتابشان داده‌اند و به آنان كه كتاب [آسمانى‌] ندارند- مشركان- بگو: آيا شما هم [به خدا] روى نهاده‌ايد- خداى را گردن نهاده‌ايد-؟ پس اگر [خداى را] گردن نهاده‌اند راه راست يافته اند و اگر روى بگردانند بر تو رساندن پيام است و بس، و خدا به [حال‌] بندگان بيناست.'),
(314, 3, 21, 'آنان را كه به آيات خدا كافر مى‌شوند و پيامبران را به ناحق مى‌كشند و كسانى از مردم را كه به عدل و داد فرمان مى‌دهند مى‌كشند به عذابى دردناك مژده ده.'),
(315, 3, 22, 'اينانند كه كارهاشان در اين جهان و آن جهان تباه شده است و هيچ يارى‌كننده‌اى ندارند.'),
(316, 3, 23, 'آيا به كسانى كه از كتاب- تورات- بهره‌اى بدادندشان ننگريستى كه چون به كتاب خدا خوانده شوند تا ميانشان داورى كند، گروهى از آنها پشت مى‌كنند در حالى كه [از حكم خدا] رويگردانند؟'),
(317, 3, 24, 'اين از آن روست كه گفتند: آتش به ما نرسد مگر روزهايى چند. و اين دروغها كه مى‌ساختند آنها را در دينشان فريفته كرده است.'),
(318, 3, 25, 'پس چگونه خواهد بود [حال آنان‌] آنگاه كه فراهم آوريمشان براى روزى كه هيچ شكى در آن نيست و هر كسى را آنچه كرده است بتمامى پاداش دهند و بر آنان ستم نرود؟'),
(319, 3, 26, 'بگو: بار خدايا، اى خداوند پادشاهى- فرمانروايى-، به هر كه خواهى پادشاهى مى‌دهى و از هر كه خواهى پادشاهى باز مى‌ستانى، هر كه را خواهى بزرگى و ارجمندى مى‌دهى و هر كه را خواهى خوار مى‌كنى نيكيها همه به دست توست، كه تو بر همه چيز توانايى.'),
(320, 3, 27, 'شب را در روز درمى‌آورى و روز را در شب درمى‌آورى، زنده را از مرده بيرون مى‌آورى و مرده را از زنده بيرون مى‌آورى، و به هر كه خواهى بى‌شمار روزى مى‌دهى.'),
(321, 3, 28, 'مؤمنان نبايد كافران را به جاى مؤمنان به دوستى گيرند و هر كس چنين كند از خداى در چيزى- پيوندى- نيست- از دين حق و از دوستى و خشنودى خداى بهره‌اى ندارد- مگر اينكه از آنان تقيه كنيد- يعنى مگر آنكه از شرّ آنها بترسيد و بخواهيد از گزندشان حذر كنيد-. خداوند شما را از [نافرمانى‌] خويش بيم مى‌دهد، و بازگشت به سوى خداست.'),
(322, 3, 29, 'بگو: اگر آنچه را كه در سينه‌هاى شماست پنهان داريد يا آشكار كنيد خدا آن را مى‌داند و هر چه را كه در آسمانها و زمين است مى‌داند و خدا بر هر چيزى تواناست.'),
(323, 3, 30, 'روزى كه هر كسى هر كار نيكى كه كرده حاضر بيابد و هر كار بدى كه كرده دوست دارد كه كاش ميان او و كارهاى بدش جدايى دورى مى‌بود. خدا شما را از [نافرمانى‌] خويش بيم مى‌دهد، و خدا به بندگان مهربان است.'),
(324, 3, 31, 'بگو: اگر خدا را دوست مى‌داريد پس مرا پيروى كنيد تا خدا شما را دوست دارد و گناهانتان را بيامرزد، و خداوند آمرزگار و مهربان است.'),
(325, 3, 32, 'بگو: خدا و پيامبر را فرمان بريد، پس اگر پشت كنند و برگردند خدا كافران را دوست ندارد.'),
(326, 3, 33, 'همانا خداوند آدم و نوح و خاندان ابراهيم و خاندان عمران را بر جهانيان برگزيد'),
(327, 3, 34, 'فرزندانى كه برخى‌شان از برخى ديگرند- فرزندانى كه از پدران برگزيده زاده شده‌اند و در پاكى همانند يكديگرند- و خدا شنوا و داناست.'),
(328, 3, 35, '[ياد كن‌] آنگاه كه آن عمران گفت: پروردگارا، نذر كردم كه آنچه در شكم من است آزاد باشد- آزاد از وابستگيهاى دنيا، و در خدمت پرستشگاه تو باشد-. پس از من بپذير، كه تويى شنوا و دانا.'),
(329, 3, 36, 'چون او را بزاد، گفت: پروردگارا، او را دختر زادم!- و خدا بدانچه زاد داناتر است و پسر چون دختر نيست- و او را مريم ناميدم و او و فرزندانش را از [آسيب‌] شيطان رانده‌شده به پناه تو مى‌سپارم.'),
(330, 3, 37, 'پس پروردگارش او'),
(331, 3, 38, 'آنگاه- يا در آنجا- زكريا پروردگار خويش را بخواند، گفت: پروردگارا، مرا از نزد خود فرزندى پاك ببخش، كه تويى شنواى دعا.'),
(332, 3, 39, 'پس فرشتگان او را، در حالى كه در نمازخانه به نماز ايستاده بود، آواز دادند: خدا تو را به يحيى نويد مى‌دهد، او كلمه خدا- يعنى عيسى- را باور دارد و مِهتر و پارسا- خويشتن‌دار از زنان- و پيامبرى از شايستگان است.'),
(333, 3, 40, 'گفت: پروردگارا، چگونه مرا پسرى باشد، و حال آنكه پيرى به من رسيده، و زنم نازاست؟ گفت: چنين است، خدا آنچه خواهد مى‌كند.'),
(334, 3, 41, 'گفت: پروردگارا، براى من نشانه‌اى پديد كن. گفت: نشانه تو اين است كه سه روز با مردم سخن نگويى مگر به اشاره و پروردگار خود را بسيار ياد كن و در شبانگاه و بامداد به پاكى بستاى.'),
(335, 3, 42, 'و آنگاه كه فرشتگان گفتند: اى مريم، خدا تو را برگزيد و پاك گردانيد و بر زنان جهانيان- زنان زمانه- برگزيد.'),
(336, 3, 43, 'اى مريم، خداى خود را با فروتنى فرمانبردار باش و با نمازگزاران سجود و ركوع كن.'),
(337, 3, 44, 'اين از خبرهاى نهان- غيب- است كه به تو وحى مى‌كنيم و تو نزد آنان نبودى آنگاه كه قلمهاى خويش [به قرعه‌] مى‌افكندند كه كدامشان سرپرست مريم شود و تو نزدشان نبودى آنگاه كه ستيزه و كشمكش مى‌كردند.'),
(338, 3, 45, 'آنگاه كه فرشتگان گفتند: اى مريم، خدا تو را به كلمه‌اى از خود كه نامش مسيح عيسى پسر مريم است نويد مى‌دهد كه در اين جهان و آن جهان آبرومند و از مقربان- نزديكان خداوند- است'),
(339, 3, 46, 'و با مردم در گهواره و در بزرگسالى سخن مى‌گويد و از نيكان و شايستگان است.'),
(340, 3, 47, 'مريم گفت: پروردگارا، چگونه مرا فرزندى تواند بود و حال آنكه هيچ آدمى مرا نسوده است؟ گفت: چنين است، خداى آنچه خواهد مى‌آفريند چون چيزى را اراده كند به آن گويد: باش! پس مى‌باشد.'),
(341, 3, 48, 'و او را كتاب و حكمت و تورات و انجيل مى‌آموزد'),
(342, 3, 49, 'و فرستاده‌اى [گرداند] به سوى فرزندان اسرائيل كه همانا شما را نشانه و حجتى از پروردگارتان آورده‌ام كه به خواست و فرمان خدا برايتان از گِل بسان پرنده‌اى مى‌سازم و در آن مى‌دمم پس پرنده‌اى مى‌شود، و به خواست و فرمان خدا نابيناى مادرزاد و پيس را بهبود مى‌بخشم و مرده را زنده مى‌كنم، و شما را از آنچه مى‌خوريد و در خانه‌هايتان اندوخته مى‌كنيد خبر مى‌دهم. هر آينه در اين [كار] براى شما اگر مؤمن باشيد نشانه‌اى است'),
(343, 3, 50, 'و تورات را كه در پيش من است- پيش از من آمده است- باور دارنده‌ام و [آمده‌ام‌] تا برخى از آنچه را كه بر شما حرام شده است برايتان حلال كنم و با نشانه‌اى از پروردگارتان نزد شما آمده‌ام، پس از خدا پروا كنيد و مرا فرمان بريد'),
(344, 3, 51, 'همانا خداى يكتا- الله- پروردگار من و شماست، پس او را بپرستيد. اين است راه راست.'),
(345, 3, 52, 'و چون عيسى كافرى آنان را آشكارا دريافت، گفت: ياوران من به سوى خدا كيانند؟ حواريان- ياران ويژه او- گفتند: ما ياوران [دين‌] خداييم به خدا ايمان آورديم و گواه باش كه ما گردن‌نهاده و فرمانبرداريم'),
(346, 3, 53, 'پرودگارا، به آنچه فرو فرستادى ايمان آورديم و [اين‌] پيامبر را پيروى كرديم، پس [نام‌] ما را با گواهان- كه به پيام رسانى پيامبران گواهى دادند- بنويس.'),
(347, 3, 54, 'آنها'),
(348, 3, 55, 'آنگاه كه خداوند گفت: اى عيسى، من تمام‌گيرنده و بردارنده توام به سوى خود و پاك‌كننده توام از آنان كه كافر شدند و كسانى را كه از تو پيروى كردند تا روز رستاخيز بالادست آنان كه كفر ورزيدند قرار خواهم داد. سپس بازگشتتان به سوى من است، پس در آنچه اختلاف مى‌كرديد ميان شما داورى خواهم كرد.'),
(349, 3, 56, 'اما كسانى را كه كافر شدند در اين جهان و آن جهان عذابى سخت خواهم كرد و يارى‌كنندگانى ندارند.'),
(350, 3, 57, 'و اما آنان كه ايمان آورده و كارهاى شايسته كرده‌اند، پس [خدا] پاداششان را تمام دهد، و خدا ستمكاران را دوست نمى‌دارد.'),
(351, 3, 58, 'اين كه بر تو مى‌خوانيمش- داستان زكريا و يحيى و مريم و عيسى- از آيات- سخنان خداوند و نشانه‌هاى راستى و درستى رسالت تو- و ياد و پند- يعنى قرآن- با حكمت است.'),
(352, 3, 59, 'همانا داستان عيسى نزد خداوند مانند داستان آدم است، او را از خاك آفريد سپس به او گفت: باش! پس ببود.'),
(353, 3, 60, '[اين‌] حق- سخن راست و درست- از پروردگار توست، پس از شك‌داران مباش.'),
(354, 3, 61, 'پس هر كه با تو، پس از آن دانشى كه به تو رسيد، در باره او- عيسى يا آن حق- ستيزه و جدل كند، بگو: بياييد تا ما و شما پسران خويش و زنان خويش و خودمان را- كسى را كه به منزله خودمان است- بخوانيم، آنگاه دعا و زارى كنيم و لعنت خداى را بر دروغگويان بگردانيم.'),
(355, 3, 62, 'همانا، اين داستان راست و درست [مسيح‌] است. و خدايى جز خداى يكتا- الله- نيست و هر آينه خدا تواناى بى‌همتا و داناى با حكمت است.'),
(356, 3, 63, 'پس اگر پشت كنند و برگردند، خداوند به [حال‌] تباهكاران داناست.'),
(357, 3, 64, 'بگو: اى اهل كتاب بياييد به سوى سخنى [گراييم‌] كه ميان ما و شما يكسان است: اين كه جز خداى يكتا را نپرستيم و چيزى را با او انباز نسازيم و برخى از ما برخى را به جاى خداى يكتا به خدايى نگيرد. پس اگر پشت كنند و برگردند بگوييد: گواه باشيد كه ما مسلمانيم- خداى يكتا را گردن نهاده ايم-.'),
(358, 3, 65, 'اى اهل كتاب چرا در باره ابراهيم ستيزه مى‌كنيد- هر كدام او را به كيش خود مى‌پنداريد- و حال آنكه تورات و انجيل فرو نيامده است مگر پس از او؟ آيا درنمى‌يابيد؟'),
(359, 3, 66, 'هان! شما همانهاييد كه در باره آنچه بدان دانشى داريد- در باره عيسى'),
(360, 3, 67, 'ابراهيم نه جهود بود و نه ترسا بلكه حق‌گرايى مسلمان بود، و از مشركان نبود.'),
(361, 3, 68, 'هر آينه نزديكترين مردم به ابراهيم كسانى‌اند كه او را پيروى كردند و اين پيامبر و آنان كه [به اين پيامبر] ايمان آورده‌اند، و خدا دوست و كارساز مؤمنان است.'),
(362, 3, 69, 'گروهى از اهل كتاب دوست مى‌دارند كه كاش شما را گمراه كنند و حال آنكه گمراه نمى‌كنند مگر خود را، و نمى‌فهمند.'),
(363, 3, 70, 'اى اهل كتاب، چرا به آيات خدا- نشانه‌هاى نبوت رسول الله'),
(364, 3, 71, 'اى اهل كتاب، چرا حق را به باطل مى‌آميزيد- مشتبه مى‌سازيد- و حق را پنهان مى‌داريد و حال آنكه خود مى‌دانيد؟'),
(365, 3, 72, 'گروهى از اهل كتاب گفتند: به آنچه بر مؤمنان فرو آمده، در آغاز روز ايمان آريد و در پايان روز انكار كنيد، تا مگر [از دين خود] باز گردند- يعنى در شك افتند-.'),
(366, 3, 73, 'و [با اين ترفند گفتند:] اعتماد مكنيد مگر به كسى كه پيرو دين شما باشد- بگو: رهنمونى [به راه راست‌] رهنمونى خداست- تا مبادا كسى را همانند آنچه به شما داده‌اند- قبله مستقل- بدهند، يا [بتوانند] نزد پروردگارتان بر شما حجت آورند. بگو: همانا فضل- نعمت نبوت و دين و قبله مستقل يا برترى و افزونى- به دست خداوند است، هر كه را خواهد دهد، و خدا فراخى‌بخش و داناست.'),
(367, 3, 74, 'هر كه را خواهد به مِهر و بخشايش خويش ويژه گرداند و خدا خداوند افزونى و بخشش بزرگ است.'),
(368, 3, 75, 'و از اهل كتاب كس هست كه اگر او را بر پوست گاوى پر از زر- كنايه از مال بسيار- امين دارى- بدو سپارى- آن را به تو بازگرداند، و از آنها كس هست كه اگر او را بر دينارى امين دارى به تو بازنگرداند مگر پيوسته بر سر او ايستاده باشى- كه ناچار شود- اين از آن روست كه گفتند: بر ما در باره امى‌ها- غير اهل كتاب- راهى [به اعتراض‌] نيست. و خود مى‌دانند كه بر خدا دروغ مى‌بندند.'),
(369, 3, 76, 'آرى هر كه به پيمان خود وفا نمايد و پرهيزگارى كند، خدا پرهيزگاران را دوست دارد.'),
(370, 3, 77, 'همانا كسانى كه در برابر پيمان خدا و سوگندهاى خويش بهايى اندك مى‌ستانند در آخرت بهره‌اى ندارند و خدا در روز رستاخيز با آنان سخن نگويد و به آنها ننگرد و [از آلودگى گناهان‌] پاكشان نسازد- يا: آنان را نستايد- و ايشان را عذابى است دردناك.'),
(371, 3, 78, 'و از آنان گروهى هستند كه زبان خويش را به خواندن كتاب- دستنوشته خودشان- چنان مى‌گردانند تا پنداريد كه آن از كتاب- تورات- است و حال آنكه از كتاب نيست و گويند كه آن از نزد خداست در حالى كه از نزد خدا نيست، و بر خدا دروغ مى‌بندند و خود مى‌دانند.'),
(372, 3, 79, 'هيچ آدمى را نسزد كه خداوند به او كتاب و حكم- حكمت: دانش گفتار و كردار درست، يا فرمان حكم راندن- و پيامبرى دهد آنگاه وى به مردم گويد: بندگان من باشيد نه بندگان خدا و ليكن [گويد:] خداشناسان و خداپرستان باشيد بدان سبب كه كتاب خدا را به ديگران مى‌آموختيد و خود به خواندن آن مى‌پرداختيد'),
(373, 3, 80, 'و نه شما را فرمان دهد كه فرشتگان و پيامبران را به خدايى گيريد. آيا شما را پس از آنكه مسلمان- تسليم حق- شده باشيد به كفر فرمان مى‌دهد؟!'),
(374, 3, 81, 'و [ياد كن‌] آنگاه كه خدا از پيامبران [و امتهاى ايشان‌] پيمان گرفت كه چون شما را كتاب و حكمت دادم سپس پيامبرى نزد شما آمد كه آنچه را با شماست باور داشت و راست انگاشت بايد به او بگرويد و يارى‌اش كنيد. [آنگاه‌] گفت: آيا اقرار داريد و بر اين [كار] پيمان مرا پذيرفتيد؟ گفتند: اقرار داريم. فرمود: پس گواه باشيد و من هم با شما از گواهانم.'),
(375, 3, 82, 'پس هر كه پس از آن [پيمان و اقرار] پشت كند و برگردد، آنانند بدكاران نافرمان.'),
(376, 3, 83, 'آيا جز دين خدا مى‌جويند، و حال آنكه هر كه [و هر چه‌] در آسمانها و زمين است خواه و ناخواه او را گردن نهاده است، و همه به سوى او بازگردانده مى‌شوند.'),
(377, 3, 84, 'بگو: به خدا و آنچه بر ما و بر ابراهيم و اسماعيل و اسحاق و يعقوب و فرزندان و نوادگان او فرو فرستاده شده، و به آنچه به موسى و عيسى و پيامبران از پروردگارشان داده شده ايمان داريم ميان هيچ يك از ايشان جدا نكنيم- فرق ننهيم- و ما او را گردن‌نهاده و فرمانبرداريم.'),
(378, 3, 85, 'و هر كه جز اسلام دينى بجويد هرگز از او پذيرفته نشود و او در آن جهان از زيانكاران است.'),
(379, 3, 86, 'چگونه خداوند گروهى را راه نمايد كه پس از ايمانشان و پس از آنكه گواهى دادند كه اين پيامبر- محمد'),
(380, 3, 87, 'آنان سزايشان اين است كه لعنت خدا و فرشتگان و همه مردمان بر آنها باشد.'),
(381, 3, 88, 'در آن- لعنت و آتش دوزخ- جاويدانند عذاب از ايشان سبك نشود و مهلتشان ندهند.'),
(382, 3, 89, 'مگر كسانى كه پس از آن [كه كافر شدند] توبه كردند و نيكوكار و راست‌كردار شدند كه خدا آمرزگار و مهربان است.'),
(383, 3, 90, 'همانا كسانى كه پس از ايمانشان كافر شدند و سپس بر كفر بيفزودند، توبه‌شان هرگز پذيرفته نشود و آنانند گمراهان.'),
(384, 3, 91, 'همانا كسانى كه كافر شدند و بر كفر مردند از هيچ يك از آنها اگر چه همه زمين را كه پر از زر باشد براى بازخريد خود دهد [تا از عذاب دوزخ بازرهد] هرگز پذيرفته نشود، آنان را عذابى است دردناك و هيچ يارى‌كننده‌اى ندارند.'),
(385, 3, 92, 'هرگز به نيكى [راستين‌] دست نيابيد مگر آنكه از آنچه دوست داريد انفاق كنيد، و هر چه انفاق كنيد خدا به آن داناست.'),
(386, 3, 93, 'همه خوردنيها براى فرزندان اسرائيل حلال بود- پيش از آنكه تورات فرو آيد-، مگر آنچه اسرائيل- يعقوب- بر خود حرام كرده بود. [ولى جهودان گويند: اين حرامها از پيش بوده است.] بگو: اگر راست مى‌گوييد. پس تورات را بياوريد و آن را بخوانيد.'),
(387, 3, 94, 'پس هر كس از اين پس بر خدا دروغ سازد آنانند ستمكاران.'),
(388, 3, 95, 'بگو: خدا راست گفت، پس آيين ابراهيم را پيروى كنيد كه حق‌گراى بود و از مشركان نبود.'),
(389, 3, 96, 'هر آينه نخستين خانه‌اى كه براى [پرستش‌] مردم نهاده شده همان است كه در بكّه- محل ازدحام جايگاه كعبه در مكّه- است كه با بركت است و جهانيان را راهنماست.'),
(390, 3, 97, 'در آن'),
(391, 3, 98, 'بگو: اى اهل كتاب، چرا به نشانه‌هاى خدا كفر مى‌ورزيد؟ و خدا بر آنچه مى‌كنيد گواه است.'),
(392, 3, 99, 'بگو: اى اهل كتاب، چرا كسانى را كه ايمان آورده‌اند از راه خدا باز مى‌داريد در حالى كه آن [راه راست‌] را كژ مى‌خواهيد؟ و شما خود [بر راستى و درستى آنچه انكار مى‌كنيد] گواهيد، و خداوند از آنچه مى‌كنيد غافل نيست.'),
(393, 3, 100, 'اى كسانى كه ايمان آورده‌ايد، اگر گروهى از كسانى را كه كتاب داده شده‌اند فرمان بريد شما را پس از ايمانتان به كافرى برند.'),
(394, 3, 101, 'و چگونه كافر مى‌شويد و حال آنكه آيات خدا بر شما خوانده مى‌شود و فرستاده او در ميان شماست؟! و هر كس به [دين و كتاب‌] خدا چنگ زند هر آينه به راه راست راه يافته است.'),
(395, 3, 102, 'اى كسانى كه ايمان آورده‌ايد، ترس از خداى را چنانكه شايسته ترس از اوست پيشه كنيد و مميريد مگر در حالى كه مسلمان باشيد.'),
(396, 3, 103, 'و همگى به ريسمان خداى- توحيد، دين و كتاب خدا- چنگ زنيد و پراكنده مشويد، و نعمت خداى را بر خود ياد كنيد آنگاه كه با يكديگر دشمن بوديد پس ميان دلهاى شما همدمى و پيوند داد تا به نعمت وى- اسلام و الفت- با هم برادر گشتيد، و بر لبه پرتگاهى از آتش بوديد پس شما را از آن رهانيد. خدا اينچنين آيات خود را براى شما روشن بيان مى‌كند تا مگر راه يابيد.'),
(397, 3, 104, 'و بايد از شما گروهى باشند كه به نيكى بخوانند و به كارهاى پسنديده فرمان دهند و از كارهاى زشت و ناپسند باز دارند، و آنانند رستگاران.'),
(398, 3, 105, 'و همانند كسانى مباشيد كه پس از آنكه دلايل روشن بديشان رسيد پراكندگى و اختلاف نمودند، و آنها را عذابى است بزرگ،'),
(399, 3, 106, 'در روزى كه رويهايى سپيد گردد و رويهايى سياه اما آنان كه رويهاشان سياه گردد [گويندشان:] آيا پس از ايمان آوردنتان كافر شديد؟! پس بچشيد عذاب را به سزاى آنكه كفر مى‌ورزيديد.'),
(400, 3, 107, 'اما آنان كه رويهاشان سپيد شود، در مهر و بخشايش خدايند [و] در آن جاويدانند.'),
(401, 3, 108, 'اين آيات خداست كه براستى و درستى بر تو مى‌خوانيم، و خداى بر جهانيان هيچ ستمى نمى‌خواهد.'),
(402, 3, 109, 'و خداى راست آنچه در آسمانها و آنچه در زمين است، و همه كارها به خداوند باز مى‌گردد.'),
(403, 3, 110, 'شما بهترين امتى هستيد كه براى مردمان پديدار شده‌ايد، كه به كارهاى پسنديده فرمان مى‌دهيد و از كارهاى زشت و ناپسند بازمى‌داريد و به خداى ايمان داريد و اگر اهل كتاب ايمان آورده بودند هر آينه برايشان بهتر بود، برخى از آنان مؤمنند و بيشترشان بدكاران نافرمانند.'),
(404, 3, 111, 'هرگز به شما زيان و گزندى نرسانند، مگر اندك رنجه و آزارى، و اگر با شما كارزار كنند به شما پشت كنند- به هزيمت شوند- و آنگاه يارى و پيروزى نيابند.'),
(405, 3, 112, 'هر جا يافته شوند [مُهر] خوارى بر آنان زده شده است، مگر به زنهارى- امانى يا پيمانى يا دستاويزى- از خداى و زنهارى از مردم [دست داشته باشند]. و به خشمى از خداى بازگشتند- يعنى سزاوار خشم خداى گشتند- و [مُهر] درماندگى و بيچارگى بر آنان زده شده اين بدان سبب است كه به آيات خدا كفر مى‌ورزيدند و پيامبران را به ناروا مى‌كشتند، اين از آن روست كه نافرمانى كردند و از اندازه درمى‌گذشتند.'),
(406, 3, 113, 'يكسان و برابر نيستند، از اهل كتاب گروهى [به فرمانبردارى خداى‌] ايستاده‌اند، آيات خدا را شبانگاهان مى‌خوانند و سجده مى‌كنند.'),
(407, 3, 114, 'به خدا و روز بازپسين ايمان دارند و به كار نيك و پسنديده فرمان مى‌دهند و از بدى و زشتى بازمى‌دارند و خود در كارهاى نيك مى‌شتابند، و اينان از نيكان و شايستگانند.'),
(408, 3, 115, 'و هر كار نيكى كه كنند هرگز ناسپاسى نبينند و خداوند به [حال‌] پرهيزگاران داناست.'),
(409, 3, 116, 'كسانى كه كافر شدند- گروهى ديگر از اهل كتاب كه ايمان نياورده‌اند- هرگز مالها و فرزندانشان آنان را از [عذاب‌] خدا هيچ سودى نخواهد داشت- به كارشان نيايد و نرهاندشان- و آنان دوزخيانند و در آن جاويدانند.'),
(410, 3, 117, 'داستان آنچه در اين زندگى دنيا هزينه مى‌كنند چون داستان بادى است با سرمايى سخت كه به كشتزار گروهى كه به خود ستم كرده‌اند برسد و آن را نابود كند و خدا به آنان ستم نكرد و ليكن خود بر خويشتن ستم مى‌كنند.'),
(411, 3, 118, 'اى كسانى كه ايمان آورده‌ايد، از غير خودتان دوست همدل و همراز مگيريد، كه در كار شما از هيچ تباهى فروگذار نكنند، دوست دارند كه شما در رنج باشيد همانا دشمنى از دهانشان- گفتارشان- پيداست، و آنچه دلهاشان پنهان مى‌دارد بزرگتر- بدتر و زشت‌تر- است. براستى كه اين آيات را براى شما روشن بيان كرديم، اگر خِرد را كار بنديد.'),
(412, 3, 119, 'اين شماييد كه آنان را دوست مى‌داريد، اما آنها شما را دوست نمى‌دارند و حال آنكه شما به همه كتابها [ى آسمانى‌] ايمان داريد، و چون شما را ديدار كنند، گويند: ما نيز گرويده‌ايم و چون تنها شوند سرانگشتان خويش از خشم به دندان گزند. بگو: به خشم خويش بميريد، كه خدا بدانچه در سينه‌هاست- نيتهاى شما- داناست.'),
(413, 3, 120, 'اگر نيكى به شما رسد آنان را ناخوش آيد و اندوهگين سازد و اگر بدى [و شكستى‌] به شما رسد بدان شاد شوند. و اگر شكيبايى و پرهيزگارى كنيد ترفند و نيرنگشان هيچ زيانى به شما نرساند، كه خداى بدانچه مى‌كنند [دانايى‌] فراگيرنده است.'),
(414, 3, 121, 'و [ياد كن‌] آنگاه كه بامداد از نزد كسان خويش بيرون رفتى [و] مؤمنان را در جايگاه‌هايى براى جنگ- احد- جاى مى‌دادى، و خدا شنوا و داناست.'),
(415, 3, 122, 'آنگاه كه دو گروه از شما آهنگ سستى [و انديشه بددلى‌] كردند و حال آنكه خدا يار و كارساز آنان بود، و مؤمنان بايد بر خدا توكّل كنند و بس.'),
(416, 3, 123, 'و هر آينه خداوند شما را در [جنگ‌] بَدر در حالى كه ناتوان بوديد- به علت كمى سپاه و سازوبرگ جنگ- يارى داد، پس از خدا پروا كنيد، باشد كه سپاس گزاريد.'),
(417, 3, 124, 'آنگاه كه به مؤمنان مى‌گفتى: آيا شما را بسنده نيست كه پروردگارتان شما را به سه هزار فرشته فروفرستاده مدد رساند؟'),
(418, 3, 125, 'آرى، اگر شكيبايى- پايدارى- ورزيد و پرهيزگارى پيشه كنيد و آنان- دشمنان- در اين خشم و شتاب خويش بر شما بتازند، پروردگارتان شما را با پنج هزار فرشته نشاندار مدد مى‌رساند.'),
(419, 3, 126, 'و اين- مدد فرشتگان- را خدا جز مژده‌اى براى شما نكرد و تا دلهايتان بدان آرام گيرد، و يارى و پيروزى نيست مگر از نزد خداى تواناى بى‌همتا و داناى استواركار.'),
(420, 3, 127, '[شما را يارى و پيروز كرد] تا گروهى از [سپاه‌] آنها را كه كافر شدند بريده گرداند [و براندازد] يا سركوبشان كند پس نوميد و شكست‌خورده باز گردند.'),
(421, 3, 128, 'از اين كار- عذاب يا پذيرفتن توبه آنها- چيزى به دست تو نيست، يا توبه آنان را بپذيرد يا عذابشان كند كه آنها ستمكارند.'),
(422, 3, 129, 'و خداى راست آنچه در آسمانها و آنچه در زمين است، هر كه را خواهد- شايسته بيند- بيامرزد و هر كه را خواهد- سزاوار يابد- عذاب كند. و خدا آمرزگار و مهربان است.'),
(423, 3, 130, 'اى كسانى كه ايمان آورده‌ايد، ربا را كه افزودنيها بر افزوده‌هاست- سودهايى است چند برابر- مخوريد و از خدا بترسيد باشد كه رستگار شويد.'),
(424, 3, 131, 'و از آتشى كه براى كافران آماده شده بپرهيزيد.'),
(425, 3, 132, 'و خدا و پيامبر را فرمان بريد باشد كه در خور رحمت- مهر و بخشايش- شويد.'),
(426, 3, 133, 'و بشتابيد به سوى آمرزشى از پروردگارتان و بهشتى كه فراخناى آن آسمانها و زمين است كه از بهر پرهيزگاران آماده شده است،'),
(427, 3, 134, 'آنان كه در آسانى- توانگرى و فراخى و خوشى- و سختى- تنگدستى و زيان و ناخوشى- انفاق مى‌كنند، و فروخورندگان خشم و درگذرندگان از مردم اند، و خدا نيكوكاران را دوست دارد،'),
(428, 3, 135, 'و آنان كه چون كار نكوهيده و زشتى كنند يا [با ارتكاب ديگر گناهان- يا گناهان كوچك-] بر خويشتن ستم كنند خداى را به ياد آرند و براى گناهانشان آمرزش خواهند- و جز خدا كيست كه گناهان را بيامرزد؟- و بر آنچه كرده‌اند، در حالى كه [بدى و زشتى و كيفر آن را] مى‌دانند، نپايند'),
(429, 3, 136, 'اينان پاداششان آمرزشى است از پروردگارشان و بهشت‌هايى كه جويها از زير آنها روان است در آنها جاويدانند، و نيكو مزدى است مزد عمل كنندگان.'),
(430, 3, 137, 'پيش از شما روشها و نهادهايى- سنتهاى الهى در هلاك امتها- بود كه گذشت، پس در زمين بگرديد و بنگريد كه سرانجام آنها كه [پيامها و نشانه‌هاى خدا را] دروغ انگاشتند چگونه بود.'),
(431, 3, 138, 'اين بيانى است براى مردم، و رهنمونى [به راه راست‌] و پندى براى پرهيزگاران.'),
(432, 3, 139, 'و سستى مكنيد و اندوهگين مباشيد كه شما برتريد، اگر مؤمن باشيد.'),
(433, 3, 140, 'اگر شما را زخمى رسد آن گروه- كافران- را نيز زخمى مانند آن رسيد، و اين روزها- پيروزيها و شكستها- را ميان مردمان مى‌گردانيم [تا آنها را بيازماييم‌] و تا خدا كسانى را كه ايمان آورده‌اند باز شناسد- يعنى تا معلوم سازد و بشناساند، و آنان كه نمى‌دانند بدانند- و از شما شهيدانى- يا گواهانى- برگيرد و خدا ستمكاران را دوست ندارد- اگر چه گاهى به ظاهر پيروز شوند-'),
(434, 3, 141, 'و تا كسانى را كه ايمان آورده‌اند پاك و ناب گرداند و كافران را تباه و نابود كند.'),
(435, 3, 142, 'آيا پنداشته‌ايد كه به بهشت درمى‌آييد و حال آنكه هنوز خدا كسانى از شما را كه جهاد كردند و شكيبايان- پايداران- را معلوم نكرده است؟'),
(436, 3, 143, 'و شما پيش از آنكه با مرگ روياروى شويد آرزوى آن مى‌كرديد- پس از جنگ بدر- اينك آن را ديديد- در جنگ احد- و به آن مى‌نگريد [و به آن تن درنمى‌دهيد]'),
(437, 3, 144, 'و محمد'),
(438, 3, 145, 'و هيچ كس جز به فرمان خدا نميرد، نبشته‌اى [است‌] مدّت دار. و هر كه پاداش اين جهان خواهد او را از آن دهيم و هر كه پاداش آن جهان خواهد او را از آن دهيم، و زودا كه سپاسگزاران را پاداش دهيم.'),
(439, 3, 146, 'و بسا پيامبرى كه خدا پرستان بسيار- يا هزاران تن- همراه وى كارزار كردند، پس بدانچه در راه خدا بديشان رسيد سستى و ناتوانى نكردند و [در برابر دشمن‌] زبونى و درماندگى ننمودند، و خدا شكيبايان را دوست دارد.'),
(440, 3, 147, 'و گفتارشان جز اين نبود كه گفتند: پرودگارا، گناهان ما را بيامرز و از گزافكارى ما در كارمان در گذر و گامهاى ما را بر جاى و استوار بدار و ما را بر گروه كافران يارى و پيروزى ده.'),
(441, 3, 148, 'پس خدا پاداش اين جهان- به يارى و پيروزى- و پاداش نيكوى آن جهان بدادشان و خدا نيكوكاران را دوست مى‌دارد.'),
(442, 3, 149, 'اى كسانى كه ايمان آورده‌ايد، اگر آنها را كه كافر شدند فرمان بريد شما را بر پاشنه‌هايتان- به واپس‌تان: به دوران جاهليت- بازمى‌گردانند پس زيانكار خواهيد گشت- يا زيان ديده باز خواهيد گشت-.'),
(443, 3, 150, '[آنها يار و سرپرست شما نيستند] بلكه خداوند يار و سرپرست شماست و او بهترين يارى‌كنان است.'),
(444, 3, 151, 'بزودى در دل كسانى كه كافر شدند بيم و هراس افكنيم بدان سبب كه براى خداوند چيزى را انباز گرفتند كه براى آن دليل و حجتى فرو نفرستاده است- يعنى باورى بى‌پايه است-. و جايگاهشان آتش دوزخ است و ستمكاران را بد جايگاهى است.'),
(445, 3, 152, 'خداوند وعده خويش با شما راست كرد، هنگامى كه [در جنگ بدر و در آغاز جنگ احد] به فرمان او دشمنان را مى‌كشتيد، تا آنگاه كه سستى و بددلى كرديد و در كار [جنگ‌]- خالى كردن سنگر در گردنه احد- با يكديگر اختلاف و كشمكش نموديد، و پس از آنكه آنچه دوست داريد به شما بنمود نافرمانى كرديد، برخى از شما خواهان اين جهان و برخى خواستار آن جهانند سپس شما را [به هزيمت‌]- در جنگ احد- از آنها واگردانيد تا شما را بيازمايد، و هر آينه از شما درگذشت، و خدا خداوند فزون‌بخشى بر مؤمنان است.'),
(446, 3, 153, 'آنگاه كه مى‌گريختيد و دور مى‌شديد- از ميدان احد- و به هيچ كس توجه و التفات نمى‌كرديد و پيامبر در پى شما مى‌خواندتان- كه به جاى خود بازگرديد- پس شما را در برابر اندوهى- اندوه از دست‌دادن غنايم و كشته شدن برخى و برداشتن زخمها- اندوهى [ديگر]- ننگ شكست و شرم از خدا و رسول- سزا داد- اندوهتان را به اندوهى مهمتر بدل كرد-، تا بر آنچه از دست داديد- از غنيمت و پيروزى- و بر آنچه به شما رسيد- از رنج و آسيب- اندوهگين مشويد. و خدا بدانچه مى‌كنيد آگاه است.'),
(447, 3, 154, 'سپس از پى آن غم، ايمنى و آرامشى بر شما فرستاد كه گروهى از شما را خوابى سبك فرا گرفت. و گروهى در انديشه خويشتن بودند، به خدا گمان ناروا داشتند، گمان دوران جاهليت. مى‌گويند: آيا ما را از اين كار- جنگ و نتيجه آن- چيزى- اختيارى- هست؟ بگو: همه كارها خداى راست. در دلهاى خويش چيزى را نهان مى‌دارند كه براى تو آشكار نمى‌كنند. مى‌گويند: اگر ما را از اين كار چيزى- اختيارى- بود در اينجا كشته نمى‌شديم. بگو: اگر در خانه‌هاى خود [هم‌] مى‌بوديد، كسانى كه كشته شدن بر آنان نوشته شده بود هر آينه به سوى بسترهاى مرگشان بيرون مى‌آمدند، [تا آنچه خدا مى‌داند بشود] و تا خداى آنچه را كه در سينه‌هاى شماست بيازمايد و آنچه را كه در دلهاى شماست پاك كند و بپالايد و خداوند بدانچه در سينه‌هاست- نيتها- داناست.'),
(448, 3, 155, 'كسانى از شما كه روز رويارويى دو گروه- مسلمين و كفار- پشت كرده [از ميدان كارزار] برگشتند همانا شيطان آنان را به سبب برخى از آنچه كرده بودند بلغزانيد- يا خواست بلغزاند-. و هر آينه خداى از آنان درگذشت، كه خدا آمرزگار و بردبار است.'),
(449, 3, 156, 'اى كسانى كه ايمان آورده‌ايد، مانند كسانى مباشيد كه كافر شدند و در باره برادرانشان كه به سفر يا به جنگ رفته بودند [و مردند يا به شهادت رسيدند] گفتند: اگر نزد ما بودند نمى‌مردند و كشته نمى‌شدند تا خداى آن را دريغ و اندوهى كند در دلهاشان. و خداست كه زنده مى‌كند و مى‌ميراند و خدا بدانچه مى‌كنيد بيناست.'),
(450, 3, 157, 'و اگر در راه خدا كشته شديد يا مرديد، هر آينه آمرزش و بخشايش خداى از آنچه آنان گرد مى‌آورند بهتر است.'),
(451, 3, 158, 'و اگر بميريد يا كشته شويد هر آينه به سوى خدا برانگيخته و فراهم مى‌شويد.'),
(452, 3, 159, '[اى پيامبر،] به مِهر و بخشايشى از خداست كه براى ايشان نرم شدى، و اگر درشتخوى سخت‌دل بودى از گرد تو پراكنده مى‌شدند. پس، از آنان درگذر و براى ايشان آمرزش بخواه و با آنان در كار [جنگ‌] مشورت كن، و چون بر [كارى‌] دل بنهادى بر خداى توكل كن كه خدا توكل كنندگان را دوست دارد.'),
(453, 3, 160, 'اگر خدا شما را يارى دهد هيچ كس بر شما چيره نخواهد شد، و اگر شما را فرو گذارد كيست كه پس از [فروگذاشتن‌] او شما را يارى كند؟ پس مؤمنان بايد بر خدا توكل كنند و بس.'),
(454, 3, 161, 'و هيچ پيامبرى را نرسد كه خيانت كند و هر كه خيانت كند با آن خيانتى كه كرده روز رستاخيز بيايد و آنگاه به هر كسى [پاداش‌] آنچه كرده تمام داده شود و بر آنان ستم نرود.'),
(455, 3, 162, 'آيا آن كس كه خشنودى خدا را پيروى كرده مانند كسى است كه به خشم خدا بازگشته- سزاوار خشم خدا شده- و جايگاه او دوزخ است؟ و بد بازگشتگاهى است.'),
(456, 3, 163, 'ايشان- پيروان خشنودى خدا- نزد خدا پايه‌ها دارند، و خدا بدانچه مى‌كنند بيناست.'),
(457, 3, 164, 'هر آينه خداى بر مؤمنان منت نهاد كه در ميانشان پيامبرى از خودشان برانگيخت كه آيات او را بر آنان مى‌خواند و پاكشان مى‌سازد و كتاب و حكمتشان مى‌آموزد، در حالى كه پيش از آن- آمدن پيامبر- هر آينه در گمراهى آشكارى بودند.'),
(458, 3, 165, 'و آيا چون آسيبى به شما رسد كه دو چندانش را- در جنگ بدر- [به دشمن‌] رسانده بوديد مى‌گوييد: اين از كجاست؟ بگو: آن از سوى خودتان است- كه بر خلاف فرمان پيامبر سنگر را رها كرديد-، همانا خدا بر همه چيز تواناست.'),
(459, 3, 166, 'و آنچه روز رويارويى دو گروه- مسلمانان و مشركان- به شما رسيد به اذن خدا بود [تا شما را بيازمايد] و تا مؤمنان را باز شناسد'),
(460, 3, 167, 'و تا كسانى را كه دورويى كردند معلوم گرداند و [چون‌] به آنها گفته شد: بياييد در راه خدا كارزار يا دفاع كنيد، گفتند: اگر مى‌دانستيم كه جنگى خواهد بود هر آينه شما را پيروى مى‌كرديم، آنان در آن روز به كفر نزديكتر بودند تا به ايمان با دهانهاشان چيزى مى‌گويند كه در دلهاشان نيست، و خدا بدانچه پنهان مى‌دارند داناتر است.'),
(461, 3, 168, 'آنان كه [از جنگ‌] بنشستند در باره برادرانشان- شهيدان- گفتند: اگر از ما فرمان برده بودند كشته نمى‌شدند بگو: اگر راست مى‌گوييد مرگ را از خود باز داريد.'),
(462, 3, 169, 'و كسانى را كه در راه خدا كشته شدند مرده مپندار، بلكه زندگانند و نزد پروردگارشان روزى داده مى‌شوند'),
(463, 3, 170, 'در حالى كه بدانچه خداوند از فزونى و بخشش خود به آنان داده است شادمانند، و به [نيكويى حالِ‌] آن كسانى كه از پس ايشان هنوز به آنان نپيوسته اند شادى همى كنند كه نه بيمى بر ايشان است و نه اندوهگين شوند.'),
(464, 3, 171, 'ايشان به نعمت و بخششى كه از خداست و به اين كه خداوند مزد مؤمنان را تباه نمى‌كند، شادمانند.'),
(465, 3, 172, 'آنان كه پس از آنكه زخمها بديشان رسيد- در احُد- خدا و پيامبر را- در دعوت به پى گيرى لشكر مشركان- پاسخ [نيكو] دادند، براى كسانى از آنان كه نيكويى و پرهيزگارى كردند مزدى بزرگ است'),
(466, 3, 173, 'آنان كه مردمان ايشان را گفتند كه [سپاهى از] مردم- كافران مكه- براى [جنگ با] شما گرد آمده‌اند پس از آنها بترسيد، اما [اين سخن‌] بر ايمانشان افزود و گفتند: خدا ما را بس است و نيكو كارگزار و پشتيبانى است.'),
(467, 3, 174, 'پس با نعمت و فزونى و بخششى از خدا [از ميدان جنگ‌] بازگشتند در حالى كه هيچ بدى و گزندى به ايشان نرسيد و خشنودى خدا را پيروى كردند و خدا خداوند فزونى و بخشش بزرگ است.'),
(468, 3, 175, 'همانا اين شيطان است كه دوستان خود را مى‌ترساند. پس اگر ايمان داريد از آنها نترسيد و از من بترسيد.'),
(469, 3, 176, 'و آنان كه در كافرى مى‌شتابند تو را اندوهگين نكنند، كه آنها هيچ زيانى به خدا نتوانند رسانيد. خدا مى‌خواهد براى آنان در آن جهان بهره‌اى قرار ندهد، و آنها را عذابى است بزرگ.'),
(470, 3, 177, 'همانا كسانى كه كفر را به بهاى ايمان خريدند هيچ زيانى به خدا نتوانند رسانيد و آنان را عذابى است دردناك.'),
(471, 3, 178, 'و كسانى كه كافر شدند مپندارند كه مهلتى كه به آنان مى‌دهيم خير آنهاست همانا مهلتشان مى‌دهيم تا بر گناه بيفزايند و آنها را عذابى است خواركننده.'),
(472, 3, 179, 'خداوند بر آن نيست كه مؤمنان را بر اين حال كه شماييد- كه با هم آميخته‌ايد- بگذارد [بلكه بر آنست كه‌] تا پليد را از پاك جدا سازد، و خداوند بر آن نيست كه شما را بر نهان و ناپيدا- غيب- آگاه سازد و ليكن خدا از فرستادگان خود هر كه را خواهد بر مى‌گزيند. پس به خدا و فرستادگان او بگرويد، و اگر ايمان آوريد و پرهيزگارى كنيد شما را مزدى بزرگ باشد.'),
(473, 3, 180, 'و كسانى كه به آنچه خدا از فزونى و بخشش خود به آنان داده است بخل مى‌ورزند مپندارند كه برايشان بهتر است بلكه براى آنها بدتر است، زودا كه آنچه بخل ورزيدند در روز رستاخيز طوق گردنشان شود. و ميراث آسمانها و زمين خداى راست و خدا بدانچه مى‌كنيد آگاه است.'),
(474, 3, 181, 'هر آينه خدا سخن كسانى را كه گفتند: خدا نيازمند است و ما توانگريم شنيد. زودا كه آنچه را گفتند و كشتار به ناحقشان مَرْ پيامبران را بنويسيم و گوييم: بچشيد عذاب سوزان را.'),
(475, 3, 182, 'آن [عذاب‌] به سبب كارهايى است كه دستهاى شما از پيش فرستاده، و خدا به بندگان ستمگر نيست.'),
(476, 3, 183, 'همان كسان كه گفتند: خدا با ما عهد كرده- به ما سفارش فرموده- كه به هيچ فرستاده‌اى نگرويم تا ما را قربانيى بياورد كه آتش آن را بخورد- بسوزاند، به نشانه پذيرفته‌شدن-. بگو: بى‌گمان پيش از من فرستادگانى با نشانه‌هاى روشن و آشكار و با آنچه گفتيد نزد شما آمدند، پس چرا آنان را كشتيد اگر راستگوييد؟!'),
(477, 3, 184, 'پس اگر تو را دروغگو شمردند همانا پيامبرانى كه پيش از تو با نشانه‌ها و دلايل هويدا و نبشته‌ها [ى حكمت‌آموز]- يا سخنان استوار- و كتاب روشن و روشن‌كننده آمده بودند نيز دروغگو شمرده شدند.'),
(478, 3, 185, 'هر كسى چشنده مرگ است. و جز اين نيست كه مزدهاتان را روز رستاخيز تمام بدهند. پس هر كه از آتش دوزخ دور داشته و به بهشت درآورده شود براستى رستگار شده است. و زندگى دنيا چيزى جز كالاى فريب نيست.'),
(479, 3, 186, 'هر آينه شما در مالها و جانهاتان آزموده خواهيد شد و از كسانى كه پيش از شما به آنان كتاب داده شده و از كسانى كه شرك ورزيده‌اند آزار- زخم زبان- بسيار خواهيد شنيد. و اگر شكيبايى و پرهيزگارى پيشه كنيد، همانا اين از استوارترين- جدّى‌ترين و راست و درست‌ترين- كارهاست.'),
(480, 3, 187, 'و [ياد كن‌] آنگاه كه خدا از كسانى كه به آنها كتاب داده شده پيمان گرفت كه آن را براى مردم روشن بيان كنيد و پنهانش مكنيد، پس آن را پشت سر خويش افكندند و در برابر آن بهايى اندك ستاندند، بد است آنچه مى‌ستانند.'),
(481, 3, 188, 'مپندار آن كسان را كه بدانچه [از كارهاى بد و زشت‌] كرده‌اند شادى مى‌كنند و دوست مى‌دارند كه بدانچه نكرده‌اند ستايش شوند، مپندارشان كه از عذاب برهند، و آنان را عذابى است دردناك.'),
(482, 3, 189, 'و خداى راست فرمانروايى آسمانها و زمين، و خدا بر همه چيز تواناست.'),
(483, 3, 190, 'همانا در آفرينش آسمانها و زمين و آمد و شد شب و روز خردمندان را نشانه‌هاست'),
(484, 3, 191, 'همان كسان كه ايستاده و نشسته و بر پهلوها خفته خداى را ياد مى‌كنند و در آفرينش آسمانها و زمين مى‌انديشند [و گويند:] پروردگارا، اين را به گزاف و بيهوده نيافريدى، تو پاكى- از اينكه كارى به گزاف و بيهوده كنى- پس ما را از عذاب آتش دوزخ نگاه دار.'),
(485, 3, 192, 'پروردگارا، هر كه را تو به آتش درآورى براستى خوار و رسوايش كرده‌اى، و ستمكاران را هيچ ياورانى نيست.'),
(486, 3, 193, 'پروردگارا، ما ندادهنده‌اى را كه به ايمان فراميخواند- يعنى رسول خدا'),
(487, 3, 194, 'پروردگارا، و آنچه را بر [زبان‌] فرستادگانت به ما وعده دادى به ما ارزانى دار و ما را در روز رستاخيز خوار و رسوا مگردان، كه تو خلاف وعده نمى كنى.'),
(488, 3, 195, 'پس پروردگارشان آنان را پاسخ داد كه من كار هيچ عمل‌كننده‌اى از شما را، از مرد يا زن، تباه نكنم برخى تان از برخى ديگريد. پس كسانى كه هجرت نمودند و [آنان كه‌] از خانه‌هاى خويش بيرون رانده شدند و [آنان كه‌] در راه من آزار ديدند و [آنان كه‌] كارزار كردند و [آنان كه‌] كشته شدند هر آينه گناهانشان را از ايشان بزدايم و آنان را به بهشت‌هايى در آورم كه از زير [درختان‌] آنها جويها روان است تا پاداشى باشد از نزد خدا، و خداست كه پاداش نيكو نزد اوست.'),
(489, 3, 196, 'رفت و آمد- سودجويى و كاميابى- كافران در شهرها تو را نفريبد'),
(490, 3, 197, '[اين‌] برخوردارى اندك است سپس جايگاهشان دوزخ است و بد آرامگاهى است.'),
(491, 3, 198, 'اما كسانى كه ترس از پروردگار خويش را پيشه كردند ايشان را بهشت‌هايى است كه از زير آنها جويها روان است، در آنجا جاودانه باشند، تا پذيرائيى باشد از نزد خدا و آنچه نزد خداست نيكوكاران را بهتر است.'),
(492, 3, 199, 'و هر آينه از اهل كتاب كسانى هستند كه به خدا و آنچه به شما و آنان فروفرستاده شده مى‌گروند، در حالى كه خدا را فروتن و گردن نهاده‌اند در برابر آيات خدا بهايى اندك نمى‌ستانند، اينان مزدشان نزد پروردگارشان است. همانا خدا زود حساب است.'),
(493, 3, 200, 'اى كسانى كه ايمان آورده‌ايد، شكيبايى كنيد و [در برابر دشمن‌] با هم پايدارى ورزيد و آماده كارزار با دشمنان باشيد- يا: مرزبانى كنيد- و از خدا پروا كنيد شايد رستگار شويد.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'اى مردم، از پروردگارتان پروا كنيد، آن كه شما را از يك تن- آدم'),
(495, 4, 2, 'و مالهاى يتيمان را به خودشان بدهيد، و پليد- از اموال خودتان- را با پاكيزه- از اموال آنان- بَدَل مكنيد، و مالهاى آنان را با مالهاى خودتان مخوريد- در هم مكنيد- كه اين گناهى بزرگ است.'),
(496, 4, 3, 'و اگر بيم آن داريد كه در باره يتيمان- در باره ازدواج با دختران يتيم- نتوانيد به داد و انصاف رفتار كنيد پس [با آنان نكاح مكنيد و] آنچه شما را از [ديگر] آنان خوش آيد، دو گانه و سه گانه، و چهار گانه، بگيريد، و اگر بيم آن داريد كه به دادگرى رفتار نكنيد- در چند همسرى- پس به يك زن بسنده كنيد يا به آنچه مالك آنيد- كنيزى كه از آن شماست-، اين نزديكتر است به آنكه ستم نكنيد.'),
(497, 4, 4, 'و كابين‌هاى آنان را تمام و به خوش‌منشى بديشان بدهيد، پس اگر خودشان چيزى از آن به خوشدلى به شما بخشيدند آن را گوارا و نوش بخوريد.'),
(498, 4, 5, 'و مالهاى خود را كه خداوند وسيله برپابودن زندگى شما قرارداده به كم خردان مدهيد- يا: و مالهاى يتيمان را كه در دست شماست به كم خردان آنها مدهيد-، و در آن- يعنى از در آمد آن مالها- بخورانيد و بپوشانيدشان و به آنها سخنى پسنديده گوييد.'),
(499, 4, 6, 'و يتيمان را بيازماييد تا آنگاه كه به حد [بلوغ و توان‌] زناشويى برسند، پس اگر از آنان رشدى- خردمندى و حسن تدبير- بديديد مالهاشان را به خودشان بدهيد، و آنها را به گزاف و شتاب [از ترس آن‌] كه مبادا بزرگ شوند [و مالها را باز ستانند] مخوريد. و هر كه بى‌نياز بود [از آن مال‌] خويشتن دارى كند و هر كه نيازمند و تهيدست بود به اندازه‌اى كه شايسته باشد بخورد. و چون مالشان را بديشان دهيد بر آنان گواه گيريد، و خدا حسابرسى بسنده است.'),
(500, 4, 7, 'مردان را از آنچه پدر و مادر و خويشان باز گذارند بهره‌اى است و زنان را نيز از آنچه پدر و مادر و خويشان باز گذارند بهره‌اى است، اندك باشد يا بسيار، بهره‌اى مقرّر شده.'),
(501, 4, 8, 'و چون به هنگام بخش‌كردن [ميراث‌]، خويشان [فقيرى كه ارث نمى‌برند] و يتيمان و درماندگان حاضر آيند، از آن [مال‌] روزيشان دهيد و به آنان سخنى نيكو و پسنديده گوييد.'),
(502, 4, 9, 'و كسانى كه اگر پس از خود فرزندانى ناتوان باز گذارند بر [بينوايى و تباه‌شدن‌] آنان بيمناكند بايد [در باره يتيمان‌] بترسند و از خدا پروا كنند و سخنى راست و استوار گويند- و روشى درست پيش گيرند-.'),
(503, 4, 10, 'همانا كسانى كه مالهاى يتيمان را به ستم مى‌خورند جز اين نيست كه در شكمهاشان آتشى فرومى‌خورند و زودا كه به آتش افروخته دوزخ در آيند.'),
(504, 4, 11, 'خدا شما را در باره فرزندانتان سفارش مى‌كند: بهره پسر [در ميراث‌] بهره دو دختر باشد. پس اگر [وارثان‌] دختر و بيش از دو باشند، دو سوم ميراث از آنهاست، و اگر يك دختر باشد نيمى از ميراث از آن اوست. و براى هر يك از پدر و مادر وى- آن مرده- شش يك ميراث است اگر فرزندى داشته باشد، و اگر فرزندى ندارد و پدر و مادرش وارث اويند براى مادرش سه يك است، و اگر برادرانى دارد مادرش شش يك مى‌برد. [اين تقسيم ميراث‌] پس از [گزاردن‌] وصيتى است كه كرده يا پرداخت بدهى و وامى است كه داشته. شما نمى‌دانيد كه پدرانتان و فرزندانتان كدامشان براى شما سودمندترند. اين حكمى است مقرّر شده از جانب خدا، همانا خدا دانا و با حكمت است.'),
(505, 4, 12, 'و اگر زنانتان فرزندى نداشته باشند نيمى از آنچه به جا گذاشته‌اند از آن شماست، و اگر فرزندى دارند پس چهار يك از آنچه بر جاى نهاده‌اند براى شماست، پس از [گزاردن‌] وصيّتى كه كرده‌اند يا پرداخت وامى كه داشته‌اند. و اگر شما را فرزندى نباشد آنان را چهار يك ميراث شماست و اگر فرزندى داشته باشيد پس آنان را هشت يك ميراث شماست، پس از [گزاردن‌] وصيّتى كه كرده‌ايد يا پرداخت وامى كه داشته‌ايد. و اگر مردى يا زنى بميرد و پدر و مادر و فرزندى نداشته باشد و او را برادر يا خواهرى [مادرى‌] باشد براى هر يك از آن دو شش يك ميراث باشد، و اگر [برادران و خواهران‌] بيش از اين بودند در سه يك [به تساوى‌] شريكند، پس از [گزاردن‌] وصيّتى كه به آن شده يا پرداخت دينى كه داشته است، بى‌آنكه [اين وصيت و بدهى‌] زيان‌آور باشد- يعنى به قصد زيان‌رساندن به ورثه و بيش از ثلث باشد-، سفارش و دستورى است از خداى، و خدا دانا و بردبار است.'),
(506, 4, 13, 'اينها حدود- مرزهاى- خداست، و هر كه خدا و پيامبرش را فرمان برد او را به بهشت‌هايى در آورد كه از زير [درختان‌] آنها جويها روان است، جاودانه در آن باشند و اين است رستگارى و كاميابى بزرگ.'),
(507, 4, 14, 'و هر كه خدا و پيامبرش را نافرمانى كند و از حدود او درگذرد، وى را به آتشى در آورد كه جاودانه در آن باشد و او را عذابى است خواركننده.'),
(508, 4, 15, 'و از زنانتان آنان كه كارى زشت- زنا- كنند چهار تن [مرد] از خودتان بر آنها گواه خواهيد، پس اگر گواهى دادند آنها را در خانه‌ها بازداريدشان تا مرگشان فرا رسد يا خداوند راهى براى آنها پديد آورد.'),
(509, 4, 16, 'و آن دو تن- مرد و زن- از شما كه زشتكارى كنند بيازاريدشان، پس اگر توبه كردند و به شايستگى آمدند از آنها دست بداريد و در گذريد كه خدا توبه پذير و مهربان است.'),
(510, 4, 17, '[قبول‌] توبه بر خدا تنها براى كسانى است كه بدى به نادانى كنند سپس بزودى توبه كنند. آنانند كه خدا [به بخشايش خويش‌] بر آنان باز گردد و توبه شان را بپذيرد و خداوند دانا و با حكمت است.'),
(511, 4, 18, 'و توبه براى كسانى نيست كه كارهاى بد همى‌كنند تا چون مرگ يكيشان فرا رسد گويد اينك توبه كردم، و نه براى كسانى كه با حال كفر بميرند. آنان را عذابى دردناك آماده ساخته‌ايم.'),
(512, 4, 19, 'اى كسانى كه ايمان آورده‌ايد، شما را حلال نيست كه از زنان به ناخواه ايشان ميراث بريد- به انتظار مرگ زنان ثروتمندتان بنشينيد تا از آنان ارث بريد- و بر آنان سخت و تنگ مگيريد تا برخى از آنچه به آنان داده‌ايد بازستانيد مگر آنكه آشكارا زشتكارى كنند. و با ايشان به شيوه‌اى نيكو و پسنديده زيست كنيد. و اگر آنان را خوش نداريد چه بسا چيزى را خوش نداريد و خداوند در آن نيكى بسيار پديد مى‌آورد.'),
(513, 4, 20, 'و اگر خواستيد به جاى زنى، زنى ديگر گيريد و به يكى از ايشان پوست گاوى پر از زر- يعنى مال بسيار- داده باشيد چيزى از آن باز نستانيد. آيا آن را با دروغ‌بستن و گناهى آشكار- يعنى تهمت بى‌عفتى زدن براى طلاق‌دادن و باز پس گرفتن مهر- باز مى‌ستانيد؟'),
(514, 4, 21, 'و چگونه آن را باز مى‌ستانيد و حال آنكه به يكديگر رسيده‌ايد و آنان از شما پيمانى استوار گرفته‌اند؟!'),
(515, 4, 22, 'و با زنى كه پدرانتان به زنى گرفته‌اند زناشويى مكنيد، مگر آنچه پيش از اين گذشت- كه گناهش آمرزيده شده ولى بايد از هم جدا شويد-، كه اين كارى زشت- زنا- و ناپسند و دشمن داشته شده [خدا] و بد راه و رسمى است.'),
(516, 4, 23, 'بر شما حرام شده است [ازدواج با] مادرانتان و دخترانتان و خواهرانتان و عمه‌هايتان و خاله‌هايتان و دختران برادر و دختران خواهر و آن مادرانتان كه شما را شير داده‌اند- دايه‌هاتان- و خواهرانتان از راه شيرخوارگى و مادران زنانتان و دختران زنانتان- از شوهر پيش- كه در كنار شما پرورش يافته‌اند، از آن زنانى كه بديشان در آمده‌ايد، و اگر به آنان درنيامده‌ايد باكى و گناهى بر شما نيست- در ازدواج با دختران آنها-، و زنان آن پسرانتان كه از پشت شما باشند، و اينكه دو خواهر را با هم به زنى گيريد، مگر آنچه پيش از اين گذشت- كه گناهش آمرزيده شده ولى بايد از يكى جدا شويد-، كه خداوند آمرزگار و مهربان است'),
(517, 4, 24, 'و [همچنين ازدواج با] زنان شوهردار مگر زنى كه مالك آن شده باشيد. نوشته- حكم- خدا بر شماست، و آنچه جز اين باشد براى شما حلال شده است كه به مالهاى خود بجوييد، در حالى كه پاكدامن باشيد نه زشتكاران- زناكنندگان- پس هر گاه از زنان به آميزش بهره گرفتيد- به نكاح موقت- كابين مقرّرشان را بدهيد و در آنچه پس از كابين مقرّر بر آن توافق كرديد گناهى بر شما نيست. همانا خدا دانا و با حكمت است.'),
(518, 4, 25, 'و هر كس از شما كه توانگرى و فراخدستى ندارد تا با زنان [آزاد] پاكدامن و با ايمان ازدواج كند، از كنيزان با ايمان كه مالك آنيد [به زنى گيرد]، و خدا به ايمان شما داناتر است، برخى‌تان از برخى ديگريد- همه يكسان و برابريد-. پس آنان را با اجازه كسانشان به زنى گيريد و كابينشان را به شيوه‌اى نيكو بدهيد، در حالى كه پاكدامنان باشند نه زشتكاران- زناكاران- و نه دوست‌گيران به پنهانى. و چون شوهر كنند اگر زشتكارى- زنا- كنند بر آنهاست نيمى از شكنجه‌اى كه بر آزادان است. اين- ازدواج با كنيزان- براى كسى از شماست كه از رنج و فشار [عزوبت و از گناه زنا بر خويشتن‌] بترسد، و اينكه شكيبايى و خوددارى كنيد براى شما بهتر است، و خدا آمرزگار و مهربان است.'),
(519, 4, 26, 'خداوند مى‌خواهد كه [احكام شرع را] براى شما روشن بيان كند و شما را به نهادهاى- سنّتهاى- كسانى كه پيش از شما بودند راه نمايد، و [به مهر و بخشايش خويش‌] بر شما بازگردد- با وضع و بيان احكام شما را از گناه دور و پاك سازد- و خدا دانا و با حكمت است.'),
(520, 4, 27, 'و خدا مى‌خواهد كه [به مهر و بخشايش خويش‌] بر شما بازگردد- و شما را از كارهاى ناپسند توبه دهد-، و كسانى كه از كامها و خواهشها [ى دل خويش‌] پيروى مى‌كنند مى‌خواهند كه به كجروى و لغزشى بزرگ افتيد.'),
(521, 4, 28, 'خدا مى‌خواهد كه بارتان را سبك كند- تكاليف سخت و دشوار بر شما ننهد-، و آدمى ناتوان آفريده شده است.'),
(522, 4, 29, 'اى كسانى كه ايمان آورده‌ايد، مالهاى خود را ميان خودتان به ناروا و ناشايست مخوريد مگر اينكه بازرگانيى با خشنودى همديگر باشد، و خود [و يكديگر] را مكشيد- و دستخوش هلاكت و تباهى مسازيد- كه خدا به شما مهربان است'),
(523, 4, 30, 'و هر كه از روى تجاوز و ستم چنين كند زودا كه او را به آتش دوزخ درآوريم، و اين بر خدا آسان است.'),
(524, 4, 31, 'اگر از گناهان بزرگى كه از آن بازتان مى‌دارند دورى كنيد بديها و گناهان [كوچك‌] شما را از شما بزداييم و شما را به جايگاهى گرامى درآوريم.'),
(525, 4, 32, 'و آنچه را كه خدا بدان چيز برخى از شما را بر برخى فزونى و برترى داده است آرزو مكنيد. مردان را از آنچه به دست آورده‌اند بهره‌اى است و زنان را نيز از آنچه به دست آورده‌اند بهره‌اى است و از خداوند از فزونى و بخشش او بخواهيد همانا خدا بر هر چيزى داناست.'),
(526, 4, 33, 'براى هر كسى از آنچه پدر و مادر و خويشان نزديك به جا گذاشته‌اند وارثانى قرار داده‌ايم و كسانى كه با آنان پيمان بسته‌ايد پس بهره‌شان را [از ارث‌] بدهيد. همانا خدا بر هر چيزى گواه است.'),
(527, 4, 34, 'مردان بر زنان كارگزاران و سرپرستانند از آن روى كه خدا برخى از ايشان- مردان- را بر برخى- زنان- فزونى و برترى داده و بدان سبب كه از مالهاى خويش هزينه مى‌كنند. پس زنان نيك و شايسته [خداى و شوهران خود را] فرمانبردارند و [عفّت خويش و حق شوهران را] در حال غيبت [آنان‌] به نگاهداشت [حقوقى كه‌] خدا [براى آنان قرار داده است‌] نگهدارنده‌اند. و زنانى كه از سركشى و نافرمانيشان بيم داريد، [نخست‌] پندشان دهيد و [اگر مؤثر نيفتاد] جامه خواب را از آنها جدا كنيد و [اگر باز سودمند نشد] بزنيدشان. پس اگر از شما فرمان بردند، راهى- بهانه‌اى- بر آنان- براى ستم و آزار- مجوييد. همانا خداوند والا و بزرگ است.'),
(528, 4, 35, 'و اگر از ناسازگارى ميان زن و شوهر بيم داريد، داورى از كسان مرد و داورى از كسان زن برانگيزيد، اگر آن دو- زن و شوهر- سازش و آشتى خواهند خداوند ميانشان سازگارى پديد آرد همانا خدا دانا و آگاه است.'),
(529, 4, 36, 'و خداى را بپرستيد و چيزى را با او انباز مسازيد و به پدر و مادر و به خويشان و يتيمان و درماندگان و همسايه نزديك و همسايه دور و يار همنشين و در راه‌مانده و آن كه مالكش شده‌ايد نيكى كنيد. همانا خداوند خودپسند نازنده- متكبر فخرفروش- را دوست ندارد'),
(530, 4, 37, 'همان كسان كه بخل مى‌ورزند و مردم را هم به بخل فرمان مى‌دهند و آنچه را كه خداوند از فزونى و بخشش خويش به آنان داده است پنهان مى‌كنند و كافران را عذابى خواركننده آماده كرده‌ايم.'),
(531, 4, 38, 'و همان كسان كه مالهاى خود را به ريا- براى نماياندن به مردم- انفاق مى‌كنند و به خدا و روز واپسين ايمان ندارند، و كسى كه شيطان يار و همدم وى باشد پس بد يار و همدمى است.'),
(532, 4, 39, 'آنان را چه زيان داشت اگر به خدا و روز واپسين ايمان مى‌آوردند و از آنچه خدا روزيشان داده انفاق مى‌كردند، و خداوند بديشان داناست.'),
(533, 4, 40, 'خدا همسنگ ذره‌اى ستم نكند. و اگر آن، كار نيكى باشد دو چندانش كند و از نزد خود مزدى بزرگ دهد.'),
(534, 4, 41, 'پس چگونه خواهد بود آنگاه كه از هر امتى گواهى بياريم و تو را بر اينان گواه آريم.'),
(535, 4, 42, 'آن روز كسانى كه كافر شدند و پيامبر را نافرمانى كردند آرزو كنند كه كاش با خاك زمين يكسان مى‌شدند و سخنى را از خدا پنهان نتوانند كرد.'),
(536, 4, 43, 'اى كسانى كه ايمان آورده‌ايد، در حالى كه مستيد به نماز نزديك مگرديد تا بدانيد چه مى‌گوييد، و نه [به نمازگاه‌] در حال جنب،- مگر راه‌گذر باشيد-، تا اينكه شست و شو- غسل- كنيد. و اگر بيمار يا در سفر باشيد يا يكى از شما از حاجتگاه آمده باشد يا زنان را بسوده باشيد- با آنها نزديكى كرده باشيد- و آب نيابيد پس آهنگ خاكى- يا زمينى- پاك كنيد و [آن را] به رويها و دستهاى خود بماليد- تيمّم كنيد- زيرا كه خداوند درگذرنده و آمرزگار است.'),
(537, 4, 44, 'آيا كسانى را كه بهره‌اى از كتاب به آنان داده شده ننگريستى كه گمراهى را مى‌خرند و مى‌خواهند كه شما نيز راه را گم كنيد؟'),
(538, 4, 45, 'و خدا به دشمنان شما داناتر است، و خدا دوست و كارسازى بسنده و ياورى بسنده است.'),
(539, 4, 46, 'از جهودان كسانى‌اند كه سخنان [خداى‌] را از جايهايش مى‌گردانند و گويند: شنيديم و نافرمانى كرديم و بشنو كه نشنوى- ناشنوا شوى، يا شنوده نشوى-، و [گويند:] «راعِنا»- كه به زبان جهودان ناسزاست و به عربى يعنى ما را مراعات كن- تا زبانشان را [از حق‌] بگردانند و در دين [اسلام‌] طعنه زنند، و اگر مى‌گفتند: شنيديم و فرمان برديم و بشنو و به ما بنگر، هر آينه براى آنها بهتر و درست‌تر بود ولى خدا آنان را به سبب كفرشان لعنت كرده است، پس ايمان نمى‌آورند مگر اندكى.'),
(540, 4, 47, 'اى كسانى كه كتاب به شما داده شده، به آنچه فرو فرستاديم كه باوردارنده آن [كتابى‌] است كه با شماست، ايمان آوريد پيش از آنكه چهره‌هايى را محو و ناپديد كنيم آنگونه كه آنها را به پشت سرشان بگردانيم يا لعنتشان كنيم چنانكه اهل شنبه را- كه نافرمانى كردند و در روز شنبه ماهى گرفتند- لعنت كرديم- تا به صورت بوزينگان در آمدند- و فرمان خدا انجام‌شدنى است.'),
(541, 4, 48, 'همانا خدا اين را كه با وى انباز گيرند نمى‌آمرزد و آنچه را كه فروتر از آن باشد- غير شرك- براى آن كه بخواهد مى‌آمرزد. و هر كه با خداى انباز گيرد هر آينه با دروغى كه بافته گناهى بزرگ كرده است.'),
(542, 4, 49, 'آيا به كسانى ننگريستى كه خويشتن را به پاكى مى‌ستايند؟ و حال آنكه خداست كه هر كه را بخواهد پاك مى‌گرداند- يا به پاكى مى‌ستايد-، و به اندازه رشته هسته خرمايى- يعنى به اندازه بسيار اندكى- ستم نشوند.'),
(543, 4, 50, 'بنگر كه چگونه بر خدا دروغ مى‌بندند و همين گناهى آشكار را بس است.'),
(544, 4, 51, 'آيا به كسانى كه بهره‌اى از كتاب- تورات- به آنها داده شده ننگريستى كه به بت و طغيانگر مى‌گروند و در باره كسانى كه كافر شدند- مشركان- گويند كه آنها از كسانى كه ايمان آورده‌اند رهيافته‌ترند؟!'),
(545, 4, 52, 'آنانند كه خداى لعنتشان كرده است، و هر كه را خدا لعنت كند هرگز براى او ياريگرى نيابى.'),
(546, 4, 53, 'مگر آنان را از فرمانروايى بهره‌اى است كه در اين حال به مردم به اندازه گودى پشت هسته خرما- يعنى اندكى- ندهند؟!'),
(547, 4, 54, 'بلكه به مردم- پيامبر'),
(548, 4, 55, 'پس، از آنان'),
(549, 4, 56, 'كسانى كه به آيات ما كافر شدند بزودى آنها را به آتشى درآوريم كه هر گاه پوست تنشان پخته شود و بسوزد آنان را پوستهاى ديگرى جايگزين سازيم تا عذاب را بچشند، كه خدا تواناى بى‌همتا و داناى استواركار است.'),
(550, 4, 57, 'و كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند ايشان را به بهشت‌هايى درآوريم كه از زير آنها جويها روان است، هميشه و جاودانه در آن بمانند براى آنان در آن بهشتها همسرانى پاكيزه هست و ايشان را به سايه‌اى پاينده درآوريم.'),
(551, 4, 58, 'همانا خداى به شما فرمان مى‌دهد كه امانتها را به خداوندانش باز دهيد و چون ميان مردم داورى كنيد به داد داورى كنيد. خدا شما را به نيكوچيزى پند مى‌دهد. همانا خداوند شنوا و بيناست.'),
(552, 4, 59, 'اى كسانى كه ايمان آورده‌ايد، خداى را فرمان بريد و پيامبر و صاحبان امر را، كه از شمايند، فرمان بريد، پس اگر در باره چيزى ستيزه و كشمكش كرديد آن را به خدا- كتاب خدا- و پيامبر باز گردانيد اگر به خدا و روز بازپسين ايمان داريد، كه اين بهتر است و سرانجام آن نيكوتر.'),
(553, 4, 60, 'آيا به كسانى كه گمان مى‌كنند كه به آنچه به تو فرو فرستاده شده- قرآن- و به آنچه پيش از تو فرستاده شده- تورات- ايمان دارند ننگريستى كه مى خواهند داورى به طاغوت- سركش- برند و حال آنكه فرمان يافته‌اند كه به آن كافر شوند؟ و شيطان مى‌خواهد كه گمراهشان كند گمراهى دور [از حق‌].'),
(554, 4, 61, 'و چون به آنان گفته شود به سوى آنچه خدا فرو فرستاده و به سوى پيامبر- براى داورى خواستن- بياييد منافقان را بينى كه سخت از تو رويگردان مى‌شوند.'),
(555, 4, 62, 'پس چگونه است- چه عذر آرند- كه چون به سبب آنچه دستهايشان پيش فرستاده پيشامد بدى به آنها رسد آنگاه نزد تو آمده به خدا سوگند مى‌خورند كه ما جز نيكويى و سازوارى نخواستيم؟!'),
(556, 4, 63, 'اينان كسانى‌اند كه خدا آنچه را در دلهاشان هست مى‌داند، پس از آنها روى بگردان و پندشان ده و به آنان گفتارى رسا كه در جانشان بنشيند بگو.'),
(557, 4, 64, 'و هيچ پيامبرى نفرستاديم مگر براى آنكه به فرمان خدا فرمانش برند. و اگر آنان هنگامى كه بر خود ستم كردند نزد تو مى‌آمدند و از خدا آمرزش مى خواستند و پيامبر براى آنان آمرزش مى‌خواست هر آينه خداى را توبه‌پذير و مهربان مى‌يافتند.'),
(558, 4, 65, 'نه، به پروردگارت سوگند كه ايمان ندارند تا تو را در آنچه ميانشان گفت‌وگو و اختلاف است داور كنند و آنگاه در جانهاى خويش از آنچه داورى كرده اى هيچ دلتنگى نيابند و براستى بر آن گردن نهند.'),
(559, 4, 66, 'و اگر ما بر آنان مقرر مى‌داشتيم كه خود- يكديگر- را بكشيد يا از خانه‌هاى خويش بيرون شويد جز اندكيشان چنين نمى‌كردند، و اگر پندى را كه به آنان داده مى‌شد كار مى‌بستند همانا برايشان بهتر و استوارتر بود- يعنى در استوارى ايمانشان مؤثرتر بود'),
(560, 4, 67, 'و آنگاه از نزد خويش مزدى بزرگ به آنها مى‌داديم'),
(561, 4, 68, 'و هر آينه ايشان را راه راست مى‌نموديم.'),
(562, 4, 69, 'و هر كه خدا و پيامبر را فرمان برد، پس اينان با كسانى خواهند بود كه خدا نعمتشان داده: از پيامبران و راستى پيشگان و شهيدان- يا گواهان اعمال- و شايسته كرداران، و ايشان نيكوياران و همنشينانند.'),
(563, 4, 70, 'اين فزونى و برترى از جانب خداست و خدا دانايى بسنده است.'),
(564, 4, 71, 'اى كسانى كه ايمان آورده‌ايد [هنگام كارزار] سلاح و ساز و برگ خويش برگيريد- يعنى آماده شويد- و گروه گروه- در صورت كمى دشمن- يا همه با هم- در صورت افزونى آنان- بيرون شويد.'),
(565, 4, 72, 'و هر آينه از شما كس هست كه [در بيرون‌شدن‌] درنگ مى‌كند، پس اگر به شما گزندى و شكستى رسد گويد: خدا به من نيكويى كرد كه با آنها همراه و حاضر نبودم.'),
(566, 4, 73, 'و اگر از خداى به شما فزونى و بخششى- نعمتى- رسد هر آينه گويد:- چنانكه گويى ميان شما و او هيچ دوستى و آشنايى نبوده- اى كاش با آنان مى‌بودم تا به كاميابى بزرگ دست‌مى‌يافتم.'),
(567, 4, 74, 'پس كسانى بايد در راه خدا كارزار كنند كه زندگى اين جهان را به آن جهان مى‌فروشند، و هر كه در راه خدا كارزار كند پس كشته شود يا پيروز گردد به او مزدى بزرگ خواهيم داد.'),
(568, 4, 75, 'و شما را چه شده كه كارزار نمى‌كنيد در راه خدا و آن ناتوان شمرده‌شدگان از مردان و زنان و كودكانى كه گويند: پروردگارا، ما را از اين شهرى كه مردمش ستمكارند بيرون بَر و براى ما از نزد خويش سرپرستى قرار ده و براى ما از نزد خويش ياورى برگمار.'),
(569, 4, 76, 'كسانى كه ايمان آورده‌اند در راه خدا كارزار مى‌كنند و كسانى كه كافر شده‌اند در راه طاغوت- بت يا طغيانگر- پيكار مى‌كنند، پس با دوستان شيطان بجنگيد، كه نيرنگ شيطان هماره سست است.'),
(570, 4, 77, 'آيا به كسانى ننگريستى كه [پيش از فرمان جهاد] به آنان گفته شد دستهاى خويش [از پيكار] باز داريد و نماز برپا داريد و زكات بدهيد [ولى آنها اعتراض داشتند و خواستار جنگ بودند] پس چون كارزار بر آنان نوشته شد آنگاه گروهى از ايشان از مردم مى‌ترسيدند مانند ترس از خدا يا ترسى بيشتر، و گفتند: پروردگارا، چرا كارزار بر ما نوشتى؟ چرا ما را تا سرآمدى نزديك- مرگ- مهلت ندادى؟ بگو: كالا و برخوردارى اين جهان اندك است و آن جهان براى كسى كه پرهيزگار باشد بهتر است و بر شما به اندازه رشته هسته خرمايى- اندكى- ستم نرود.'),
(571, 4, 78, 'هر كجا باشيد مرگ شما را درخواهد يافت گر چه در كوشكها و دژهاى سخت استوار باشيد. و اگر خوبيى- پيروزى- به آنان رسد گويند: اين از نزد خداست، و اگر بديى- شكست- به آنان رسد گويند: اين از نزد توست، بگو: همه از نزد خداست. پس اين گروه را چيست كه بر آن نيستند- نمى‌خواهند- كه سخنى را دريابند.'),
(572, 4, 79, 'هر نيكيى كه به تو رسد از خداست و هر بديى كه به تو رسد از خود توست. و تو را پيامبرى براى مردم فرستاديم و خدا گواهى بسنده است.'),
(573, 4, 80, 'هر كه پيامبر را فرمان برد هر آينه خداى را فرمان برده و هر كه پشت كند- سرباز زند- ما تو را بر آنان نگاهبان نفرستاديم.'),
(574, 4, 81, 'و [در حضور تو] مى‌گويند: فرمانبرداريم، و چون از نزد تو بيرون شوند شبانه گروهى از آنان جز آنچه مى‌گويند- يا تو مى‌گويى- در سر مى‌پرورند و خدا آنچه را به شب در سر مى‌پرورند مى‌نويسد. از آنها روى بگردان و بر خدا توكّل كن، و خدا كارسازى بسنده است.'),
(575, 4, 82, 'آيا در قرآن نمى‌انديشند؟ و اگر از نزد غير خدا مى‌بود در آن ناسازگارى و ناهمگونى بسيار مى‌يافتند.'),
(576, 4, 83, 'و چون به آنان خبرى- و شايعه‌اى- از ايمنى يا ترس برسد آن را فاش و پخش مى‌كنند، و حال آنكه اگر آن را به پيامبر و صاحبان امر خويش- كارداران يا فرماندارانشان- باز مى‌گرداندند هر آينه كسانى از آنان كه [حقيقت‌] آن'),
(577, 4, 84, 'پس در راه خدا كارزار كن، جز بر خودت مكلّف نيستى، و مؤمنان را [به پيكار] برانگيز، باشد كه خدا زور و گزند آنان را كه كافر شدند باز دارد، و خدا زورآورتر- نيرومندتر- و به كيفر سخت‌تر است.'),
(578, 4, 85, 'هر كه شفاعتى نيكو كند او را از [نيكى‌] آن بهره‌اى است و هر كه شفاعتى بد و ناپسند كند او را از [سرانجام بد] آن بهره‌اى است، و خداوند بر هر چيزى نگاهبانى تواناست.'),
(579, 4, 86, 'و هنگامى كه شما را به درودى درود گويند، پس به نيكوتر از آن درود گوييد يا همان را باز گوييد. همانا خداوند بر هر چيزى حسابگر است.'),
(580, 4, 87, 'خداى يكتا، خدايى جز او نيست هر آينه شما را در روز رستاخيز كه هيچ شكى در آن نيست فراهم خواهد آورد، و چه كسى راستگوتر از خداست؟'),
(581, 4, 88, 'شما را چه شده است كه در باره منافقان دو گروه شده‌ايد- بر كفر و نفاق آنان اتفاق نداريد، گروهى آنها را طرد مى‌كنيد و گروهى ظاهربين و ساده لوح در باره آنها شفاعت مى‌كنيد- و حال آنكه خدا آنها را به سبب آنچه كردند نگونسار كرده است- به كفرشان باز گردانده-؟! آيا مى‌خواهيد آن را كه خدا گمراه كرده است راه نماييد؟ و هر كه را خدا گمراه كند هرگز راهى براى او نيابى.'),
(582, 4, 89, 'دوست مى‌دارند كه كاش شما نيز مانند آنها كافر شويد تا يكسان باشيد. پس، از آنان دوستانى مگيريد تا در راه خدا هجرت كنند. پس اگر [از اسلام و هجرت‌] روى برتافتند، هر جا آنان را يافتيد دستگيرشان كنيد و بكشيدشان، و از آنان كسى را دوست و ياور مگيريد'),
(583, 4, 90, 'مگر آنان كه به گروهى پيوندند كه ميان شما و ايشان پيمانى هست يا در حالى نزد شما آيند كه سينه‌هاشان از اينكه با شما يا با قوم خود پيكار كنند به تنگ آمده. و اگر خداى خواهد آنان را بر شما برگمارد تا با شما بجنگند، پس اگر از شما كناره گرفتند و با شما نجنگيدند و به شما پيشنهاد صلح و آشتى دادند، ديگر خداوند شما را بر آنان راهى [براى پيكار] قرار نداده است.'),
(584, 4, 91, 'گروهى ديگر را خواهيد يافت كه مى‌خواهند از شما و قوم خود ايمن باشند، [ولى‌] هر گاه به فتنه- كفر يا آشوب و پيكار- [خوانده و] باز گردانده شوند در آن نگونسار گردند. پس اگر از شما كناره نگرفتند و به شما پيشنهاد صلح ندادند و دست [از پيكار با شما] بازنداشتند هر جا آنان را يافتيد دستگيرشان كنيد و بكشيدشان. اينانند كه شما را بر آنان حجتى هويدا داديم- تا در چيرگى بر آنها عذرى آشكار داشته باشيد-.'),
(585, 4, 92, 'و هيچ مؤمنى را نسزد كه مؤمنى را بكشد مگر بخطا، و هر كه مؤمنى را بخطا بكشد بر اوست آزادكردن بنده مؤمنى و پرداخت خونبهايى به كسان او مگر آنكه ببخشند و اگر [كشته‌شده‌] مؤمنى از گروه دشمن شما بود- كسان و وارثان او كافر حربى بودند- آزادكردن بنده مؤمنى [بس‌] است- زيرا كافر از مؤمن ارث نمى‌برد تا خونبهاى مؤمن به او پرداخت شود-، و اگر از گروهى بود كه ميان شما و آنان پيمانى هست پرداخت خونبهايى به كسان او و آزادكردن بنده مؤمنى بايد پس هر كه نيابد- بنده‌اى نيابد كه آزاد كند يا توان مالى نداشته باشد- بر اوست كه دو ماه پياپى روزه بدارد، [اين كفاره براى آن قرار داده شده‌] تا بازگشتى باشد از سوى خدا، و خدا دانا و با حكمت است.'),
(586, 4, 93, 'و هر كه مؤمنى را بعمد بكشد سزاى او دوزخ است كه جاودانه در آن باشد و خدا بر او خشم گرفته و او را لعنت كرده- از رحمت خود دور كرده- و براى او عذابى بزرگ آماده ساخته است.'),
(587, 4, 94, 'اى كسانى كه ايمان آورده‌ايد، چون در راه خدا [براى كارزار] بيرون رويد نيك بنگريد و وارسى كنيد و به آن كسى كه به شما سلام- يا پيشنهاد صلح- كند، مگوييد تو مؤمن نيستى در حالى كه [با اين دستاويز] كالاى زندگى دنيا مى‌جوييد، [و اگر اين را مى‌خواهيد] پس نزد خدا غنيمت‌هاى بسيار است. شما پيش از اين چنين بوديد و خدا بر شما [به راه نمودن به اسلام‌] منت نهاد، پس نيك بنگريد و وارسى كنيد، كه خداوند بدانچه مى‌كنيد آگاه است.'),
(588, 4, 95, 'وانشستگان از مؤمنان، بجز كسانى كه گزندى دارند- بيمار يا معلولند-، با جهادكنندگان در راه خدا به مالها و جانهاى خويش، برابر نيستند، كه خدا پايه جهادكنندگان به مالها و جانهاى خويش را بر وانشستگان افزونى و برترى داده است، و همه را خداوند وعده [پاداش‌] نيكو- بهشت- داده است، و خدا جهادكنندگان را بر وانشستگان به مزدى بزرگ برترى و افزونى نهاده است،'),
(589, 4, 96, 'پايه‌هايى از نزد خود و آمرزشى و بخشايشى، و خداوند آمرزگار و مهربان است.'),
(590, 4, 97, 'همانا فرشتگان به كسانى كه جانشان را در حالى كه ستمكار بر خويش بوده‌اند مى‌ستانند گويند: [در كار دين‌] در چه [حال‌] بوديد؟ گويند: ما در زمين ناتوان شمرده‌شدگان بوديم. گويند: مگر زمين خدا فراخ نبود تا در آن هجرت كنيد؟ جايگاه اينان دوزخ است و بد بازگشتگاهى است'),
(591, 4, 98, 'مگر ناتوان شمرده‌شدگان از مردان و زنان و كودكانى كه چاره‌اى ندارند و راهى- براى هجرت- نيابند'),
(592, 4, 99, 'اينانند كه اميد است خدا از آنان درگذرد، و خدا درگذرنده و آمرزگار است.'),
(593, 4, 100, 'و هر كه در راه خدا هجرت كند، در زمين هجرتگاه بسيار و فراخى و گشايشى يابد، و هر كه از خانه خويش هجرت كنان به سوى خدا و پيامبرش بيرون آيد سپس مرگ او را دريابد، همانا مزدش بر خدا باشد، و خدا آمرزگار و مهربان است.'),
(594, 4, 101, 'و چون در زمين سفر كنيد گناهى بر شما نيست كه نماز را كوتاه كنيد- از ركعات نماز بكاهيد و آن را شكسته بخوانيد- اگر بترسيد كه كافران به شما گزندى رسانند، كه كافران شما را دشمنى آشكارند.'),
(595, 4, 102, 'و چون در ميان ايشان باشى و براى آنان نماز برپا كنى پس گروهى از ايشان بايد با تو برخيزند و سلاح خويش برگيرند، و چون سجود كردند- نماز را فرادى تمام كردند- پس بايد در پشت سر شما باشند و گروهى ديگر كه نماز نگزارده‌اند بيايند و با تو نماز گزارند و بايد پاس و پرهيز خويش نگاه دارند و سلاح خود برگيرند. كافران دوست دارند كه شما از سلاحها و كالاهاى خويش غافل شويد تا يكباره بر شما بتازند. و گناهى بر شما نيست كه اگر از باران در رنج بوده يا بيمار باشيد سلاحهاى خويش بنهيد ولى حالت پاس و پرهيز نگاه داريد. همانا خدا براى كافران عذابى خواركننده آماده كرده است.'),
(596, 4, 103, 'پس چون نماز- نماز خوف- گزارديد خداى را ياد كنيد، ايستاده و نشسته و بر پهلو خفته، و همين كه ايمن و دل‌آسوده شديد- در اقامتگاه- نماز را [تمام‌] برپا داريد، كه نماز بر مؤمنان نوشته‌اى است مقرّر و بهنگام.'),
(597, 4, 104, 'و در جستن آن گروه [كافر] سستى مكنيد، اگر شما [از زخم و جراحت‌] دردمنديد آنها نيز چنانكه شما دردمند مى‌شويد دردمند مى‌شوند و شما چيزى- يارى و بهشت- را از خدا اميد داريد كه آنان اميد ندارند. و خدا دانا و با حكمت است.'),
(598, 4, 105, 'همان [اين‌] كتاب- قرآن- را براستى و درستى سوى تو فروفرستاديم تا ميان مردمان بدانچه خدا تو را بنمود حكم كنى، و مدافع- طرفگير- خيانتكاران مباش.'),
(599, 4, 106, 'و از خدا آمرزش بخواه كه خدا آمرزگار و مهربان است.'),
(600, 4, 107, 'و از جانب كسانى كه به خود خيانت مى‌كنند [در مقام دفاع از آنها] ستيزه مكن، كه خداوند كسى را كه خيانتگر و گنه‌پيشه باشد دوست ندارد.'),
(601, 4, 108, '[آنان خيانت خود را] از مردم پنهان مى‌دارند- شرم مى‌كنند- و از خدا پنهان نمى‌دارند- شرم نمى‌كنند- و حال آنكه او با آنهاست هنگامى كه به شب گفتارى را كه او نمى‌پسندد در سر مى‌پرورند، و خدا بدانچه مى‌كنند [دانايى‌] فراگير است.'),
(602, 4, 109, 'هان! اينك شماييد كه در زندگى دنيا [در دفاع‌] از آنان ستيزه كرديد پس كيست كه در روز رستاخيز [در دفاع‌] از آنها با خدا ستيزه كند يا كيست كه كارساز آنان باشد؟'),
(603, 4, 110, 'و هر كه كار بدى كند يا به خود ستم كند آنگاه از خدا آمرزش بخواهد خدا را آمرزگار و مهربان يابد.'),
(604, 4, 111, 'و هر كه گناهى كند همانا به زيان خويشتن مى‌كند، و خدا دانا و با حكمت است.'),
(605, 4, 112, 'و هر كه خطا يا گناهى كند سپس آن را به گردن بيگناهى اندازد هر آينه بار دروغ‌بستنى آشكار و گناهى هويدا را بر دوش گرفته است.'),
(606, 4, 113, 'و اگر فضل خداى و مهر و بخشايش او بر تو نبود هر آينه گروهى از آنان انديشه و آهنگ آن كرده بودند كه تو را گمراه كنند- كه از خيانت‌پيشگان دفاع كنى- و حال آنكه جز خود را گمراه نكنند و هيچ زيانى به تو نرسانند. و خدا كتاب و حكمت بر تو فروفرستاد و آنچه را نمى‌دانستى به تو آموخت و فضل خدا- لطف و نعمت ويژه او- بر تو بزرگ است.'),
(607, 4, 114, 'در بسيارى از رازگويى‌هاى آنان خيرى نيست مگر [در رازگفتن‌] آن كس كه به [دادن‌] صدقه‌اى يا به كارى نيك و پسنديده يا اصلاح ميان مردم فرمان دهد، و هر كه براى جستن خشنودى خدا چنين كند او را مزدى بزرگ خواهيم داد.'),
(608, 4, 115, 'و هر كه پس از آنكه راه راست براى او پديدار شد با پيامبر مخالفت كند و راهى جز راه مؤمنان در پيش گيرد وى را بدان سو كه روى كرده بگردانيم- به همان چيزى كه براى خود برگزيده وا گذاريم- و او را به دوزخ درآريم و بد بازگشتگاهى است.'),
(609, 4, 116, 'خدا اين [گناه‌] را كه با او انباز گيرند نمى‌آمرزد و جز آن را براى هر كه خواهد مى‌آمرزد و هر كه با خدا انباز گيرد هر آينه گمراه شده است گمراهيى دور.'),
(610, 4, 117, 'آنان'),
(611, 4, 118, 'كه خدا لعنتش كرده، و [شيطان‌] گفته است: هر آينه از بندگانت بهره‌اى معيّن- معلوم و مقرّر شده- خواهم گرفت،'),
(612, 4, 119, 'و هر آينه آنان را گمراه مى‌كنم و در دلشان آرزوهاى باطل مى‌افكنم و آنان را فرمايم تا گوشهاى چارپايانشان را بشكافند- تا آنچه را خدا حلال كرده حرام كنند- و بفرمايمشان تا آفريده خداوند را دگرگون سازند. و هر كس شيطان را به جاى خدا دوست و كارساز گيرد براستى زيانى آشكار كرده است.'),
(613, 4, 120, 'آنان را وعده مى‌دهد و آرزو در دلشان مى‌افكند و شيطان به آنان وعده نمى‌دهد مگر فريبى.'),
(614, 4, 121, 'اينانند كه جايشان دوزخ است و از آن هيچ گريزگاهى نيابند.'),
(615, 4, 122, 'و كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند آنان را به بهشت‌هايى درآريم كه از زير [درختان‌] آنها جويها روان است، جاودانه و هميشه در آن باشند. وعده راست خداست و چه كسى از خدا راستگوتر است؟'),
(616, 4, 123, 'نه به آرزوهاى شماست و نه به آرزوهاى اهل كتاب هر كه كار بدى كند بدان كيفر شود، و جز خدا براى خود دوست و ياورى نيابد.'),
(617, 4, 124, 'و هر كس از مرد و زن در حالى كه مؤمن باشد كارهاى نيك و شايسته كند، ايشان به بهشت در آيند و به اندازه شكاف هسته خرمايى- يعنى به مقدار بسيار اندكى- ستم نبينند.'),
(618, 4, 125, 'و كيست از جهت دين نيكوتر از آن كه روى [دل‌] خود را به خداى سپرده در حالى كه نيكوكار است و از آيين ابراهيم حق گراى پيروى نموده، و خدا ابراهيم را دوست گرفت.'),
(619, 4, 126, 'هر چه در آسمانها و در زمين است خداى راست، و خدا به همه چيز [دانايى‌] فراگير است.'),
(620, 4, 127, 'از تو در باره زنان فتوا مى‌خواهند، بگو: خدا شما را در باره آنان فتوا مى‌دهد و [نيز در باره‌] آنچه در كتاب- قرآن- بر شما خوانده مى‌شود در باره دختران پدرمرده‌اى كه آنچه را برايشان [از ارث يا كابين و حقوقشان‌] نوشته و مقرّر شده نمى‌دهيد و مى‌خواهيد آنها را به زنى بگيريد- تا مالهاشان را بخوريد-، و [نيز در باره‌] كودكان ناتوان شمرده‌شده و اينكه در باره يتيمان به داد و انصاف رفتار كنيد و هر كار نيكى كه كنيد خدا به آن داناست.'),
(621, 4, 128, 'و اگر زنى از شوى خويش بيم ناسازگارى يا رويگردانى داشته باشد باكى بر آنها نيست كه ميان خود به گونه‌اى آشتى و سازش كنند و آشتى و سازش بهتر است. و جانها را بخيلى و آزمندى فراآمده، و اگر نيكى و پرهيزگارى كنيد خدا به آنچه مى‌كنيد آگاه است.'),
(622, 4, 129, 'و هرگز نتوانيد ميان زنان به عدالت رفتار كنيد هر چند بسيار خواستار آن باشيد، پس يكسر [به يكى روى نياوريد و] از يكى روى مگردانيد كه او را چون آويخته واگذاريد- يعنى بلا تكليف كه نه بيوه باشد و نه شوهردار- و اگر آشتى و سازش نماييد و پرهيزگارى كنيد همانا خدا آمرزگار و مهربان است.'),
(623, 4, 130, 'و اگر آن دو از يكديگر جدا شوند خدا هر دو را از فراخى و گشايش خويش بى‌نياز كند، و خداوند فراخى‌بخش و با حكمت است.'),
(624, 4, 131, 'و خداى راست آنچه در آسمانها و آنچه در زمين است، و هر آينه به كسانى كه پيش از شما كتاب داده شده‌اند و به شما سفارش كرديم كه از خدا پروا كنيد، و اگر كافر شويد- اين سفارش را كار نبنديد- پس [بدانيد كه‌] آنچه در آسمانها و آنچه در زمين است از آنِ خداست و خدا بى‌نياز و ستوده است.'),
(625, 4, 132, 'و خداى راست آنچه در آسمانها و زمين است، و خدا كارسازى بسنده است.'),
(626, 4, 133, 'اگر خواهد شما را، اى مردم، ببرد و مردمى ديگر آرد، و خدا بر اين كار تواناست.'),
(627, 4, 134, 'هر كه پاداش اين جهان خواهد پس [بداند كه‌] پاداش اين جهان و آن جهان نزد خداست، و خداوند شنوا و بيناست.'),
(628, 4, 135, 'اى كسانى كه ايمان آورده‌ايد، پيوسته به انصاف و داد برپا بوده، براى خدا گواهان باشيد- براى رضاى حق گواهى دهيد- اگر چه به زيان خودتان يا پدر و مادر و خويشان باشد، [آن كس كه برايش گواهى مى‌دهيد] اگر توانگر باشد يا درويش، خدا به آنها سزاوارتر است. پس [در گواهى‌دادن‌] از خواهش و كام دل پيروى مكنيد كه از حق به در رويد- يا: تا گواهى بحق ندهيد- و اگر زبان بپيچانيد- يعنى به ناحق گواهى دهيد- يا روى بگردانيد- از گواهى دادن- خدا بدانچه مى‌كنيد آگاه است.'),
(629, 4, 136, 'اى كسانى كه ايمان آورده‌ايد، به خدا و پيامبر او و كتابى كه بر فرستاده‌اش فروفرستاده و كتابى كه پيش از اين فروفرستاده بگرويد، و هر كه به خداى و فرشتگان و كتابها و فرستادگان او و به روز بازپسين كافر شود هر آينه گمراه گشته گمراهيى دور.'),
(630, 4, 137, 'كسانى كه ايمان آوردند سپس كافر شدند و پس از آن ايمان آوردند و باز كافر شدند و آنگاه بر كفر خويش افزودند خداى بر آن نيست كه آنان را بيامرزد و نه اينكه راهى بديشان بنمايد.'),
(631, 4, 138, 'منافقان را مژده ده كه آنان را عذابى است دردناك'),
(632, 4, 139, 'آنان كه كافران را به جاى مؤمنان به دوستى مى‌گيرند آيا ارجمندى را نزد آنان مى‌جويند؟ پس [بدانند كه‌] همه ارجمندى از آنِ خداست.'),
(633, 4, 140, 'و همانا در [اين‌] كتاب بر شما فروفرستاد كه چون بشنويد كه به آيات خدا كافر شوند و آنها را مسخره كنند با آنان منشينيد تا به گفتارى ديگر پردازند كه آنگاه- اگر بنشينيد- شما هم مانند آنهاييد. همانا خدا فراهم‌آرنده همه منافقان و كافران در دوزخ است.'),
(634, 4, 141, 'همان كسان كه [از سرِ بدخواهى‌] چشم به شما مى‌دارند، پس اگر شما را پيروزى و گشايشى از جانب خدا باشد گويند: مگر ما با شما نبوديم؟ و اگر كافران را بهره‌اى باشد [به آنها] گويند: آيا بر شما دست- چيرگى- نداشتيم و شما را از [گزند] مؤمنان- يا تسليم‌شدن به آنها- باز نداشتيم؟ پس خداوند در روز رستاخيز ميان شما داورى خواهد كرد، و خدا هرگز براى كافران راهى [براى تسلّط] بر مؤمنان ننهاده است.'),
(635, 4, 142, 'منافقان با خدا فريبكارى مى‌كنند، و حال آنكه او [به سزاى فريبكاريشان‌] فريب‌دهنده آنهاست، و چون به نماز برخيزند با گرانى و كاهلى برخيزند، خود را به مردم مى‌نمايند- ريا مى‌كنند- و خدا را ياد نمى‌كنند مگر اندكى.'),
(636, 4, 143, 'ميان اين و آن- كفر و ايمان- سرگردانند، نه با اينان- مؤمنان- اند و نه با آنان- كافران- و هر كه را خدا گمراه كند راهى براى او نيابى.'),
(637, 4, 144, 'اى كسانى كه ايمان آورده‌ايد، كافران را به جاى مؤمنان، دوست مگيريد، آيا مى‌خواهيد براى خدا به زيان خودتان حجّتى آشكار بسازيد؟'),
(638, 4, 145, 'همانا منافقان در فروترين طبقه دوزخند و هرگز براى آنها ياورى نيابى'),
(639, 4, 146, 'مگر كسانى كه توبه كنند و راه اصلاح- نيكوكارى و درستكارى- پيش گيرند و به [كتاب‌] خدا چنگ زنند و دين- اعتقاد و عبادت- خويش را براى خدا پاك و ويژه گردانند پس اينان با مؤمنانند و خداوند مؤمنان را مزدى بزرگ خواهد داد.'),
(640, 4, 147, 'خداى را با عذاب شما چه كار، اگر سپاس گزاريد و با ايمان باشيد؟ و خدا سپاسدار و داناست.'),
(641, 4, 148, 'خداوند بلندگفتن سخن بد را دوست نمى‌دارد مگر [از] كسى كه بر او ستم رفته باشد، و خدا شنوا و داناست.'),
(642, 4, 149, 'اگر كار نيك را آشكار كنيد يا آن را پنهان داريد يا از بدى [ديگران‌] درگذريد پس خدا هم درگذرنده و تواناست.'),
(643, 4, 150, 'كسانى كه به خدا و فرستادگان او كافر مى‌شوند و مى‌خواهند ميان خدا و پيامبران او جدايى افكنند و مى‌گويند: به برخى ايمان مى‌آريم و به برخى كافر مى‌شويم و مى‌خواهند در اين ميان راهى فراگيرند،'),
(644, 4, 151, 'اينانند كه براستى كافرند و ما براى كافران عذابى خواركننده آماده ساخته‌ايم.'),
(645, 4, 152, 'و كسانى كه به خدا و فرستادگان او ايمان آورده‌اند و ميان هيچ يك از ايشان جدايى نيفكنده‌اند اينانند كه مزدهاشان را به آنان خواهد داد و خداوند آمرزگار و مهربان است.'),
(646, 4, 153, 'اهل كتاب از تو مى‌خواهند كه كتابى از آسمان بر آنان فروآرى، همانا از موسى بزرگتر از اين خواستند كه گفتند: خداى را آشكارا به ما بنما، پس صاعقه آنان را به سبب [گستاخى و] ستمشان بگرفت، سپس گوساله را پس از آنكه نشانه‌هاى روشن و آشكار- معجزات- برايشان آمده بود [به خدايى‌] گرفتند و ما از آن [گناه‌] درگذشتيم- پس از آنكه توبه كردند- و موسى را حجّتى هويدا بداديم.'),
(647, 4, 154, 'و كوه طور را به سبب پيمانشان بالاى سرشان بداشتيم و به آنان گفتيم: از دروازه [بيت المقدس‌] سجده‌كنان- سر فرودارندگان، به نشانه خضوع- در آييد و به آنها گفتيم: در روز شنبه از اندازه مگذريد- ماهى مگيريد و از حكم تجاوز مكنيد- و از آنان پيمانى سخت و استوار گرفتيم.'),
(648, 4, 155, 'پس به سبب پيمان شكنى‌شان و كفرشان به نشانه‌هاى خدا و كشتارشان پيامبران را به ناحق و گفتارشان كه دلهاى ما بسته و در پوشش است- سخن پيامبران را نمى‌فهميم- [و حال آنكه چنين نيست كه مى‌گويند] بلكه خداوند به سزاى كفرشان بر دلهاشان مُهر نهاده و از اينرو جز اندكى ايمان نمى آورند،'),
(649, 4, 156, 'و به سبب كفرشان و دروغ‌بستن بزرگشان بر مريم،'),
(650, 4, 157, 'و اين سخنشان كه ما مسيح عيسى پسر مريم فرستاده خدا را كشتيم- و حال آنكه نه او را كشتند و نه بر دار كردند بلكه برايشان مشتبه شد- مردى را به صورت عيسى بر دار كردند- و كسانى كه در باره او اختلاف نمودند هر آينه به گمان اندرند، آنان را به حال او هيچ دانشى نيست مگر پيروى از گمان، و به يقين او را نكشته‌اند،'),
(651, 4, 158, 'بلكه خداوند او را به سوى خود برداشت، و خدا تواناى بى‌همتا و داناى با حكمت است'),
(652, 4, 159, 'و هيچ كس از اهل كتاب نيست مگر آنكه پيش از مرگ خويش- يا مرگ عيسى- به او بگرود، و او در روز رستاخيز بر [ايمان‌] آنان گواه خواهد بود'),
(653, 4, 160, 'پس به سزاى ستمى كه جهودان كردند- آنچه در آيات پيش گذشت- و به سبب بازداشتن بسيارى [از مردم‌] را از راه خدا چيزهاى پاكيزه‌اى را كه براى آنان حلال شده بود حرام كرديم'),
(654, 4, 161, 'و به سبب رباگرفتنشان و حال آنكه از آن بازداشته شده بودند، و نيز خوردنشان مالهاى مردم را به ناروا و براى كافرانشان عذابى دردناك آماده كرده‌ايم.'),
(655, 4, 162, 'ليكن استواران در دانش از آنان و مؤمنان [آنها] ايمان مى‌آورند به آنچه به تو فرو آمده و آنچه پيش از تو فرو آمده، و همچنين برپادارندگان نماز- بخصوص- و دهندگان زكات و كسانى كه به خدا و روز بازپسين ايمان دارند اينان را پاداشى بزرگ خواهيم داد.'),
(656, 4, 163, 'ما به تو وحى فرستاديم، همچنانكه به نوح و پيامبران پس از او وحى كرديم، و به ابراهيم و اسماعيل و اسحاق و يعقوب و اسباط- فرزندان و نبيرگان وى- و عيسى و ايوب و يونس و هارون و سليمان نيز وحى كرديم، و به داوود زبور داديم'),
(657, 4, 164, 'و پيامبرانى [فرستاديم‌] كه سرگذشت ايشان را پيش از اين بر تو گفته‌ايم و پيامبرانى كه داستانشان را بر تو نگفته‌ايم، و خدا با موسى سخن گفت، به همسخنى'),
(658, 4, 165, 'پيامبرانى نويدرسان و بيم‌كننده تا مردم را پس از [فرستادن‌] فرستادگان بر خدا بهانه و دستاويزى نباشد، و خدا تواناى بى‌همتا و داناى با حكمت است.'),
(659, 4, 166, 'ليكن خدا بدانچه سوى تو فروفرستاد گواهى مى‌دهد، آن را به دانش خويش فروفرستاده، و فرشتگان نيز گواهى مى‌دهند، و خداوند گواهى بسنده است.'),
(660, 4, 167, 'كسانى كه كافر شدند و [مردم را] از راه خدا بازداشتند، هر آينه گمراه شدند گمراهيى دور.'),
(661, 4, 168, 'كسانى كه كافر شدند و ستم كردند خدا بر آن نيست كه بيامرزدشان و نه آنكه راهى بنمايدشان،'),
(662, 4, 169, 'جز راه دوزخ كه هميشه در آن جاويدانند و اين بر خدا آسان است.'),
(663, 4, 170, 'اى مردم، اين پيامبر از جانب پروردگارتان براستى و درستى سوى شما آمده، پس ايمان بياوريد كه براى شما بهتر است، و اگر كافر شويد پس [بدانيد كه‌] آنچه در آسمانها و زمين است خداى راست و خدا دانا و با حكمت است.'),
(664, 4, 171, 'اى اهل كتاب، در دين خود از اندازه فراتر مرويد و بر خدا جز راست مگوييد، همانا مسيح عيسى پسر مريم پيامبر خدا و كلمه اوست كه آن را به سوى مريم افكند و روحى است از او، پس به خدا و فرستادگان او ايمان آوريد و مگوييد [خدا] سه است- اب، ابن، روح القدس-، [از اين گفته‌] باز ايستيد كه براى شما بهتر است. جز اين نيست كه «اللّه» خدايى يكتاست، پاك و منزه است از اينكه او را فرزندى باشد، او راست آنچه در آسمانها و آنچه در زمين است، و خدا كارسازى بسنده است.'),
(665, 4, 172, 'مسيح هرگز ننگ ندارد و سرنپيچد از اينكه بنده خدا باشد و فرشتگان مقرّب نيز و هر كه از پرستش او ننگ دارد و سرپيچد و گردنكشى و بزرگ‌منشى نمايد پس همه آنها را به سوى خويش برانگيخته و گرد خواهد آورد.'),
(666, 4, 173, 'اما كسانى را كه ايمان آورده و كارهاى نيك و شايسته كرده‌اند مزدشان را بتمامى دهد و از فزونى و بخشش خويش بيفزايدشان، و اما كسانى كه ننگ داشته و سر باز زدند و گردنكشى و بزرگ‌منشى نمودند آنها را عذابى دردناك كند و براى خويش به جاى خدا دوست و ياورى نخواهند يافت.'),
(667, 4, 174, 'اى مردم، شما را دليل و حجتى- دين حق يا پيامبر- از پروردگارتان آمد و نورى آشكار- قرآن- به سوى شما فرو فرستاديم.'),
(668, 4, 175, 'و اما كسانى كه به خدا ايمان آورده و دست در [پناه‌] او زدند- به فرمانها و طاعت او چنگ زدند- بزودى آنان را در مهر و بخشايشى از خويش و برترى و افزونيى در آورد و به راهى راست به سوى خود راهشان نمايد.'),
(669, 4, 176, 'از تو فتوا مى‌خواهند، بگو: خداوند شما را در باره [ميراث‌] «كلاله»- برادر و خواهر آن مرده‌اى كه پدر و مادر و فرزند ندارد- فتوا مى‌دهد: اگر مردى كه فرزندى [و پدر و مادر] ندارد بميرد و خواهرى دارد، خواهر نيمى از آنچه او به جا گذاشته مى‌برد، و او- آن برادر- از خواهر خود اگر فرزندى ندارد ارث مى‌برد. پس اگر دو خواهر باشند دو سوم ميراث از آنچه [برادر] باز گذاشته مى‌برند. و اگر برادران و خواهران باشند برادر را بهره دو خواهر خواهد بود. خدا براى شما [احكام را] روشن بيان مى‌كند تا مبادا گمراه شويد. و خداوند به همه چيز داناست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'اى كسانى كه ايمان آورده‌ايد، به پيمانها و قراردادها وفا كنيد. [خوردن گوشت‌] چهارپايان زبان‌بسته، جز آنچه بر شما خوانده مى‌شود، براى شما حلال شده است بى آنكه در حال احرام [حج يا عمره‌] شكار را حلال شماريد. همانا خدا هر چه بخواهد حكم مى‌كند.'),
(671, 5, 2, 'اى كسانى كه ايمان آورده‌ايد، [شكستن حرمت‌] نشانه‌هاى خدا- مناسك حج- را روا مداريد و نه [شكستن حرمت‌] ماه حرام و نه قربانى بى‌نشان و نه قربانيان گردنبند بسته- نشاندار- و نه آهنگ‌كنندگان بيت الحرام- خانه شكوهمند يعنى كعبه- را كه از پروردگار خويش بخشش و نيكويى و خشنودى مى‌جويند. و چون از احرام بيرون آمديد شكار توانيد كرد. و دشمنى با قومى كه از مسجد الحرام- نمازگاه شكوهمند- بازتان داشتند شما را بر آن ندارد كه از اندازه درگذريد. و يكديگر را بر نيكوكارى و پرهيزگارى يارى دهيد، و يكديگر را به گناهكارى و ستم بر ديگران يار مباشيد و از خدا پروا كنيد، كه خدا سخت كيفر است.'),
(672, 5, 3, 'حرام شد بر شما مردار و خون و گوشت خوك و آنچه [به هنگام كشتنش‌] نام غير خدا بر آن برده باشند و آنچه خفه شده و با زدن مرده و از بلندى افتاده و مرده و با شاخ‌زدن كشته شده و نيم‌خورده درنده، مگر آنچه [پيش از مردن‌] به كشتن آن برسيد، و آنچه در برابر بتان- بر تخته سنگهايى كه در جاهليت بر آنها براى خدايان خويش قربانى مى‌كردند و كم كم خود اين تخته سنگها را مقدس شمردند- سر بريده و اينكه [گوشت حيوان را] به تيرهاى قرعه بخش كنيد، اينها همه بيرون شدن از فرمان [حق‌] است.- امروز كسانى كه كافر شدند از دين شما- كه بر شما يا دينتان پيروز شوند- نوميد شدند، بنابر اين از آنها مترسيد و از من بترسيد و بس. امروز دين شما را به كمال رساندم و نعمت خود را بر شما تمام كردم و اسلام را دين شما پسنديدم-. پس هر كه در حال گرسنگى [به خوردن گوشتهاى منع شده‌] ناچار شود بى‌آنكه گرايش به گناه داشته باشد همانا خداوند آمرزگار و مهربان است.'),
(673, 5, 4, 'از تو مى‌پرسند چه چيزها برايشان حلال شده است، بگو: چيزهاى پاكيزه براى شما حلال شده است، و آنچه از جانوران شكارى- سگان شكارى- كه تعليم داده‌ايد در حالى كه از آنچه خداوند به شما آموخته به آن سگان آموخته باشيد- كه شكار را بگيرند و خود نخورند-، پس، از شكارى كه براى شما گرفته‌اند بخوريد و نام خدا را [هنگام فرستادن حيوان‌] بر آن ياد كنيد و از خدا پروا نماييد- براى بازى و سرگرمى شكار نكنيد-، كه خدا زود حساب است.'),
(674, 5, 5, 'امروز چيزهاى- خوردنيهاى- پاكيزه براى شما حلال شد و طعام كسانى كه به آنان كتاب داده شده- غير از گوشت كشتارهاى آنها- براى شما حلال است و طعام شما براى آنها حلال است، و زنان پاكدامن با ايمان و زنان پاكدامن از كسانى كه پيش از شما به آنان كتاب داده شده هر گاه كابينهاشان را بدهيد در حالى كه پاكدامن باشيد نه زناكار و نه دوست‌گيرنده [به پنهانى‌]- رفيق‌باز- [براى شما حلال است‌]. و هر كه ايمان را انكار كند- به اسلام كفر ورزد- براستى كردارش تباه گردد و در آخرت از زيانكاران خواهد بود.'),
(675, 5, 6, 'اى كسانى كه ايمان آورده‌ايد، چون به نماز برخيزيد- آهنگ نماز كنيد- رويها و دستهايتان را تا آرنجها بشوييد و سرها و پاهايتان را تا برآمدگيهاى روى پا مسح كنيد. و اگر جنب بوديد خود را پاك سازيد- غسل كنيد- و اگر بيمار يا در سفر بوديد يا يكى از شما از حاجتگاه آمد يا با زنان نزديكى كرديد و آبى نيافتيد پس آهنگ خاكى پاك كنيد و رويها و دستهايتان را مسح كنيد- تيمم-. خدا نمى‌خواهد بر شما هيچ تنگى و دشوارى نهد بلكه مى خواهد شما را پاكيزه سازد و نعمت خويش را بر شما تمام كند شايد سپاس گزاريد.'),
(676, 5, 7, 'و به ياد آريد نعمت خدا را بر خودتان و پيمانى را كه با شما استوار كرد آنگاه كه گفتيد شنيديم و فرمان برديم. و از خدا پروا كنيد كه خدا بدانچه در سينه‌هاست داناست.'),
(677, 5, 8, 'اى كسانى كه ايمان آورده‌ايد، همواره براى خدا [به حق گفتن‌] برپا بوده گواهان به عدل و داد باشيد، و دشمنى گروهى شما را بر آن ندارد كه عدالت نكنيد، عدالت كنيد كه آن به پرهيزگارى نزديكتر است و از خدا پروا كنيد كه خدا بدانچه مى‌كنيد آگاه است.'),
(678, 5, 9, 'خداوند به كسانى كه ايمان آورده و كارهاى نيك و شايسته كرده‌اند نويد داده است كه آنان را آمرزش و مزدى بزرگ است.'),
(679, 5, 10, 'و كسانى كه كافر شدند و آيات- نشانه‌هاى- ما را دروغ شمردند آنان دوزخيانند.'),
(680, 5, 11, 'اى كسانى كه ايمان آورده‌ايد، به ياد آريد نعمت خداى را بر خويش آنگاه كه گروهى آهنگ كردند كه به سوى شما دست بگشايند و او دستهاى آنان را از شما بازداشت، و از خدا پروا كنيد و مؤمنان بايد بر خدا توكل كنند و بس.'),
(681, 5, 12, 'و هر آينه خداوند از فرزندان يعقوب پيمان گرفت، و از ايشان دوازده مهتر و سرپرست برانگيختيم، و خداى گفت: من با شمايم اگر نماز را برپا داريد و زكات بدهيد و به فرستادگانم ايمان آوريد و ايشان را يارى كنيد و بشكوه داريد و خداى را وامى نيكو دهيد- به نيازمندان كمك كنيد-، هر آينه بديهاى شما را از شما بزدايم و شما را به بهشت‌هايى درآورم كه از زير [درختان‌] آنها جويها روان است. پس هر كه از شما از اين پس كافر شود براستى راه راست را گم كرده است.'),
(682, 5, 13, 'پس به سبب شكستن پيمانشان آنان را لعنت كرديم و دلهاشان را سخت گردانيديم. سخن را از جايگاه‌هاى آن مى‌گردانند- تحريف مى‌كنند- و بهرى از آنچه را كه به آن پند داده شدند فراموش كردند و همواره بر خيانتى از آنان آگاه مى‌شوى مگر اندكى از ايشان. پس، از آنان درگذر و چشم بپوش، كه خداوند نيكوكاران را دوست دارد.'),
(683, 5, 14, 'و از كسانى كه گفتند ما ترساييم پيمان گرفتيم و ليكن بهرى از آنچه را كه به آن پند داده شدند از ياد بردند پس تا روز رستاخيز ميان آنان دشمنى و كينه افكنديم و بزودى خدا آنان را از آنچه مى‌كردند آگاه خواهد ساخت.'),
(684, 5, 15, 'اى اهل كتاب، همانا فرستاده ما سوى شما آمده كه براى شما بسيارى از آنچه را كه از كتاب پنهان مى‌كرديد روشن بيان مى‌كند و از بسيارى در مى‌گذرد. براستى شما را از سوى خدا نورى- پيامبر'),
(685, 5, 16, 'كه خداوند به وسيله آن هر كه را پيرو خشنودى او باشد به راه‌هاى سلامت راه مى‌نمايد و آنان را به خواست خود از تاريكيها به روشنايى بيرون مى آرد و به راه راست راه مى‌نمايد.'),
(686, 5, 17, 'هر آينه كافر شدند كسانى كه گفتند: خدا همان مسيح پسر مريم است. بگو: اگر خدا بخواهد مسيح پسر مريم و مادرش و هر كه در زمين است همه را نابود كند چه كسى در برابر خدا توانايى دارد [كه خداى را از آن باز دارد]؟ و خداى راست پادشاهى آسمانها و زمين و آنچه ميان آنهاست، هر چه بخواهد مى‌آفريند و خدا بر هر چيزى تواناست.'),
(687, 5, 18, 'و جهودان و ترسايان گفتند: ما پسران خدا و دوستان اوييم. بگو: پس چرا شما را به گناهانتان عذاب مى‌كند؟ [چنين نيست‌] بلكه شما آدميانيد از آنها كه او آفريده است، هر كه را خواهد بيامرزد و هر كه را خواهد عذاب كند و خداى راست پادشاهى آسمانها و زمين و آنچه ميان آنهاست و بازگشت به سوى اوست.'),
(688, 5, 19, 'اى اهل كتاب، فرستاده ما به سوى شما آمد كه در دوران گسستگى [آمدن‌] پيامبران، [حق را] براى شما روشن بيان مى‌كند تا نگوييد ما را نويدرسان و بيم‌كننده‌اى نيامد. همانا مژده‌دهنده و بيم‌كننده‌اى سوى شما آمد، و خدا بر هر جيزى تواناست.'),
(689, 5, 20, 'و [ياد كن‌] آنگاه كه موسى به قوم خويش گفت: اى قوم من، نعمت خدا را بر خود ياد كنيد هنگامى كه در ميان شما پيامبرانى پديد كرد و شما را پادشاهان گردانيد و به شما چيزها داد كه به هيچ يك از مردم جهان نداد.'),
(690, 5, 21, 'اى قوم من، به اين سرزمين مقدس- فلسطين و شامات- كه خداوند براى ما نوشته است در آييد و [از ترس جباران‌] به پشت خويش بر مگرديد- عقب‌نشينى مكنيد- كه زيانكار مى‌گرديد.'),
(691, 5, 22, 'گفتند: اى موسى، در آنجا گروهى گردنكش و ستمگرند و ما هرگز اندر آن نشويم تا از آنجا بيرون شوند پس اگر از آن [سرزمين‌] بيرون شوند، ما در آييم.'),
(692, 5, 23, 'دو مرد- يوشع و كاليب- از آنان كه [از خدا] مى‌ترسيدند و خدا بر آنان نعمت- ايمان و خردمندى و روشن‌بينى- ارزانى داشته بود گفتند: بر آنان از اين دروازه در آييد، و چون اندر شديد همانا شما پيروز خواهيد شد، و اگر ايمان داريد پس بر خدا توكل كنيد و بس.'),
(693, 5, 24, 'گفتند: اى موسى، تا آنان در آنجا باشند ما هرگز در آن [سرزمين‌] در نياييم، پس تو و پروردگارت برويد و [با آنها] جنگ كنيد كه ما همين جا نشسته‌ايم.'),
(694, 5, 25, '[موسى‌] گفت: پروردگارا، من جز بر خود و برادرم اختيار و توانايى ندارم، پس ميان ما و گروه نافرمان بدكار جدايى افكن.'),
(695, 5, 26, '[خداى‌] فرمود: همانا آن سرزمين چهل سال بر آنان حرام است و در اين سرزمين سرگردان باشند، پس بر گروه نافرمان اندوه مخور.'),
(696, 5, 27, 'و خبر دو فرزند آدم- هابيل و قابيل- را بر آنان بدرستى بخوان كه چون قربانى كردند از يكى پذيرفته شد و از ديگرى پذيرفته نشد. [آن كه قربانيش پذيرفته نشد- قابيل- به برادر] گفت: هر آينه تو را خواهم كشت. [برادرش- هابيل-] گفت: جز اين نيست كه خدا از پرهيزگاران مى‌پذيرد'),
(697, 5, 28, 'سوگند كه اگر دست خويش به من دراز كنى تا مرا بكشى، من دست خويش دراز نمى‌كنم تا تو را بكشم زيرا كه من از پروردگار جهانيان مى‌ترسم،'),
(698, 5, 29, 'من مى‌خواهم گناه من و گناه خود را برگيرى تا از دوزخيان باشى، و اين است پاداش ستمكاران.'),
(699, 5, 30, 'پس نفس او كشتن برادرش را برايش بياراست و آسان نمود تا او را بكشت و از زيانكاران گشت.'),
(700, 5, 31, 'آنگاه خداوند كلاغى را فرستاد كه زمين را مى‌كاويد تا به وى بنماياند كه چگونه جسد برادرش را پنهان كند. [قابيل‌] گفت: اى واى بر من، آيا ناتوانم از اينكه مانند اين كلاغ باشم تا پيكر برادرم را پنهان كنم؟ آنگاه از پشيمانان گشت.'),
(701, 5, 32, 'از اين رو بر فرزندان اسرائيل نوشتيم كه هر كه كسى را بكشد بى‌آنكه كسى را كشته يا تباهى در زمين كرده باشد چنان است كه همه مردم را كشته باشد. و هر كه كسى را زنده كند- از مرگ برهاند يا به حق راه نمايد- چنان است كه همه مردم را زنده كرده است. و هر آينه فرستادگان ما با نشانه‌هاى روشن سوى آنان آمدند، سپس بسيارى از آنان پس از آن در زمين گزافكار و زياده‌رو شدند.'),
(702, 5, 33, 'جز اين نيست كه سزاى كسانى كه با خدا و فرستاده او مى‌جنگند و در زمين به تباهى مى‌كوشند- در جامعه ايجاد ناامنى مى‌كنند يا به راهزنى و قتل و غارت مى‌پردازند- اين است كه آنان را بكشند يا بر دار كنند يا دستها و پاهايشان را بر خلاف يكديگر- قسمتى از دست راست و پاى چپ- ببرند يا از آن سرزمين بيرونشان كنند. اين است خوارى و رسوايى براى آنان در اين جهان، و در آن جهان عذابى بزرگ دارند.'),
(703, 5, 34, 'مگر كسانى كه پيش از آنكه بر آنان دست يابيد توبه كنند، و بدانيد كه خدا آمرزگار و مهربان است.'),
(704, 5, 35, 'اى كسانى كه ايمان آورده‌ايد، از خدا پروا كنيد و به سوى او دستاويز بجوييد و در راه او جهاد كنيد، باشد كه رستگار شويد.'),
(705, 5, 36, 'همانا كسانى كه كافر شدند اگر همه آنچه را كه در زمين است و همچند آن را مى‌داشتند تا خويشتن را بدان از عذاب روز رستاخيز باز خرند از آنها پذيرفته نشود و آنان راست عذابى دردناك،'),
(706, 5, 37, 'مى‌خواهند كه از آتش دوزخ بيرون آيند و حال آنكه از آن بيرون‌آمدنى نيستند و آنان راست عذابى پاينده.'),
(707, 5, 38, 'و مرد دزد و زن دزد [چهار انگشت‌] دستشان را به سزاى آنچه كرده‌اند ببريد، كيفرى است از سوى خدا و خدا تواناى بى‌همتا و داناى با حكمت است.'),
(708, 5, 39, 'پس هر كه پس از ستمش توبه كند و كار خود را به صلاح آورد خدا [به رحمت خويش‌] بر او باز مى‌گردد و توبه‌اش را مى‌پذيرد كه خدا آمرزگار و مهربان است.'),
(709, 5, 40, 'آيا ندانسته‌اى كه خداى راست پادشاهى آسمانها و زمين، آن را كه بخواهد عذاب مى‌كند و آن را كه بخواهد مى‌آمرزد و خدا بر هر چيزى تواناست؟'),
(710, 5, 41, 'اى پيامبر، كسانى كه در كفر مى‌شتابند- اصرار مى‌ورزند- تو را اندوهگين نسازند از آنان كه به دهان خويش مى‌گويند: گرويديم و دلهاشان باور ندارد و از آنها كه جهود شدند كه سخت شنوندگان دروغند، شنوندگانند براى گروهى ديگر كه نزد تو نيامده‌اند- يعنى جاسوسان آنهايند- سخن را پس از جاى‌گرفتن در جايگاه‌هايش مى‌گردانند- تحريف مى‌كنند-، مى‌گويند: اگر اين [حكم تحريف شده‌] به شما داده شد بپذيريد و اگر داده نشد دورى و پرهيز كنيد. و هر كه خدا فتنه- گمراهى يا رسوايى يا هلاكت- او را خواهد، هرگز براى او چيزى را از خدا [در دفع آن فتنه‌] نتوانى به دست آورد- در برابر خدا براى او كارى نتوانى كرد- اينان كسانى‌اند كه خدا نخواسته است كه دلهاشان را پاك كند، آنان را در اين جهان رسوايى و خوارى و در آن جهان عذابى است بزرگ.'),
(711, 5, 42, 'شنوندگان دروغ و خورندگان [مال‌] حرام‌اند پس اگر نزد تو آيند ميانشان داورى كن يا از آنان روى بگردان، و اگر از آنان روى بگردانى هرگز زيانى به تو نتوانند رسانيد و اگر داورى كنى به داد و انصاف ميانشان داورى كن زيرا كه خداوند دادگران را دوست دارد.'),
(712, 5, 43, 'و چگونه تو را به داورى پسندند و حال آنكه تورات كه حكم خدا در آن است نزد آنهاست و آنگاه پس از آن [كه تو موافق آن كتاب حكم كردى‌] روى مى‌گردانند، و اينان ايمان ندارند.'),
(713, 5, 44, 'ما تورات را فرو فرستاديم كه در آن رهنمونى [به حق‌] و روشنايى هست پيامبرانى كه [حكم خدا را] گردن نهاده و فرمانبردارند با آن براى آنها كه يهودى شدند حكم مى‌كنند و [همچنين‌] مربيان خداپرست و دانشمندان ايشان، از آن رو كه نگاهداشت كتاب خدا از آنان خواسته شده بود و بر آن گواه بودند. پس از مردم مترسيد و از من بترسيد و به بهاى آيات من چيز اندكى مخريد. و هر كه بدانچه خدا فروفرستاده حكم نكند پس اينانند كافران.'),
(714, 5, 45, 'و در آن تورات بر آنان اين حكم نوشتيم كه تن را به تن و چشم را به چشم و بينى را به بينى و گوش را به گوش و دندان را به دندان و زخمها را [به برابرش‌] قصاص است، و هر كه آن قصاص را ببخشد اين كفاره‌اى براى [گناهان‌] اوست، و هر كه بدانچه خدا فروفرستاده حكم نكند پس اينانند ستمكاران.'),
(715, 5, 46, 'و بر پى ايشان پيامبران عيسى پسر مريم را درآورديم، باوردارنده آنچه پيش او بود از تورات- يعنى تورات را كه پيش از او فرستاده شده بود راست انگاشت-، و او را انجيل داديم كه در آن رهنمونى و روشنائيى بود و باوردارنده آنچه پيش آن بود از تورات، و رهنمونى و پندى براى پرهيزگاران.'),
(716, 5, 47, 'و اهل انجيل بايد بدانچه خدا در آن فروفرستاده حكم كنند و هر كس بدانچه خدا فروفرستاده حكم نكند پس آنانند بدكاران نافرمان.'),
(717, 5, 48, 'و [اين‌] كتاب- قرآن- را براستى و درستى سوى تو فرستاديم، باوردارنده آنچه پيش آن بوده از كتاب [الهى‌] و نگاهبان بر آن، پس ميان آنان بدانچه خدا فرو فرستاده حكم كن و با روى‌گرداندن از آن حقّى كه به سوى تو آمده است از هوى و هوس آنان پيروى مكن. براى هر امّتى از شما- آدميان- راه و روشى پديد كرديم، و اگر خدا مى‌خواست شما را يك امّت مى‌ساخت و ليكن مى‌خواهد شما را در آنچه به شما داده بيازمايد، پس به نيكيها پيشى گيريد. بازگشت همه شما به خداست و شما را بدانچه در آن اختلاف مى‌كرديد آگاه خواهد كرد.'),
(718, 5, 49, 'و [وحى كرديم به‌] اينكه ميان آنان بدانچه خدا فروفرستاده حكم كن و از هوسها و آرزوهاشان پيروى مكن و از آنان بپرهيز كه مبادا تو را در باره برخى از آنچه خدا به تو فروفرستاده فريب دهند و از آن بگردانند. پس اگر پشت كردند، بدان كه خدا مى‌خواهد به آنان به [سزاى‌] برخى از گناهانشان عقوبت رساند، و براستى بسيارى از مردم بدكاران نافرمانند.'),
(719, 5, 50, 'آيا حكم [روزگار] جاهليت مى‌جويند؟ و چه كسى نيكوحكم‌تر از خداست براى مردمى كه يقين دارند؟'),
(720, 5, 51, 'اى كسانى كه ايمان آورده‌ايد، جهودان و ترسايان را دوست و همدل مگيريد. برخى از آنان دوستان برخى ديگرند و هر كس از شما كه با آنها دوستى و همدلى دارد او از آنهاست. همانا خدا گروه ستمكاران را راه ننمايد.'),
(721, 5, 52, 'از اين رو كسانى را كه در دلشان بيمارى- شك و نفاق- است مى‌بينى كه در [دوستى‌] آنان مى‌شتابند و مى‌گويند: مى‌ترسيم كه ما را رويداد بدى رسد. اميد است كه خدا پيروزى و گشايشى يا امرى- فرمانى يا حادثه‌اى- از نزد خويش پديد آرد، پس بر آنچه در دلهاشان نهان داشتند پشيمان گردند.'),
(722, 5, 53, 'و كسانى كه ايمان آوردند [در اين حال به يكديگر] گويند: آيا اينان- منافقان- همان كسانند كه سوگندهاى سخت به خدا مى‌خوردند كه با شما خواهند بود؟ كردارهاشان تباه شد و زيانكار گشتند.'),
(723, 5, 54, 'اى كسانى كه ايمان آورده‌ايد، هر كس از شما كه از دين خويش برگردد زودا كه خداوند گروهى را بياورد كه ايشان را دوست دارد و ايشان او را دوست دارند، با مؤمنان نرم و خاكسارند و بر كافران سخت و گردنفراز، در راه خدا جهاد مى‌كنند و از سرزنش هيچ سرزنش‌كننده‌اى نمى‌ترسند. اين برترى و بخشش خداست كه آن را به هر كه بخواهد مى‌دهد و خداوند فراخ‌بخشايشى داناست.'),
(724, 5, 55, 'همانا دوست و سرپرست شما خداست و پيامبرش و كسانى كه ايمان آورده‌اند، آنان كه نماز را برپا مى‌دارند و زكات مى‌دهند در حالى كه در ركوعند.'),
(725, 5, 56, 'و هر كه خدا و پيامبرش و [چنين‌] كسانى را كه ايمان آورده‌اند به دوستى و سرپرستى برگزيند [از گروه خداست و] براستى گروه خدا پيروزند.'),
(726, 5, 57, 'اى كسانى كه ايمان آورده‌ايد، كسانى را كه دين شما را به مسخره و بازى مى‌گيرند از آنان كه پيش از شما به آنها كتاب داده شده و نيز كافران را دوست مگيريد و اگر ايمان داريد از خدا پروا كنيد'),
(727, 5, 58, 'و چون به نماز فرامى‌خوانيد- بانگ اذان سر مى‌دهيد- آن را به مسخره و بازى مى‌گيرند، اين از آن روست كه آنها گروهى‌اند كه خِرد را كار نمى‌بندند.'),
(728, 5, 59, 'بگو: اى اهل كتاب، آيا جز اين بر ما خُرده و عيب مى‌گيريد كه به خدا و آنچه به ما فروآمده و آنچه پيش از اين فروآمده ايمان آورده‌ايم و حال آنكه بيشتر شما از فرمان حق بيرون رفته‌ايد؟'),
(729, 5, 60, 'بگو: آيا شما را به بدتر از اين پاداشى نزد خدا خبر بدهم؟ [پاداش‌] آن كس كه خدا او را لعنت كرده و بر او خشم گرفته و برخى از آنان را بوزينگان و خوكان گردانيده و كسى كه طاغوت- بُت و طغيانگر- را پرستيده است اينان در جايگاهى بدترند و از راه راست گم‌تر.'),
(730, 5, 61, 'و چون نزد شما آيند گويند: ايمان داريم و حال آنكه با كفر در آمدند و با همان كفر بيرون شدند، و خدا بدانچه پنهان مى‌كردند داناتر است.'),
(731, 5, 62, 'و بسيارى از آنها را مى‌بينى كه در گناهكارى و ستم به ديگران و حرامخوارى مى‌شتابند. هر آينه بد است آنچه مى‌كنند.'),
(732, 5, 63, 'چرا مربيان خداپرست و دانشمندان، آنان را از گفتار گناه‌آلودشان- دروغگوييشان- و حرام‌خواريشان باز نمى‌دارند؟ هر آينه بد است آنچه مى‌سازند- مى‌كنند.'),
(733, 5, 64, 'و جهودان گفتند: دست خدا بسته است. دستهاشان بسته باد و به سزاى آنچه گفتند لعنت بر آنان باد بلكه دو دست [رحمت و قدرت‌] او گشوده است آنگونه كه بخواهد مى‌بخشد و روزى مى‌دهد. و هر آينه آنچه از پروردگارت سوى تو فروآمده بسيارى از آنان را سركشى و كفر مى‌افزايد و ميانشان تا روز رستاخيز دشمنى و كينه افكنديم هر گاه آتشى براى جنگ افروختند خدا آن را فرونشاند، و پيوسته در زمين به تبهكارى مى‌كوشند و خدا تباهكاران را دوست ندارد.'),
(734, 5, 65, 'و اگر اهل كتاب ايمان آورند و پرهيزگار شوند هر آينه بديهاشان را از آنها بزداييم و آنان را به بهشتهاى پُرنعمت درآوريم.'),
(735, 5, 66, 'و اگر آنان تورات و انجيل و آنچه را كه از پروردگارشان به سوى آنها فروآمده برپا مى‌داشتند هر آينه از بالاى سرشان- آسمان- و از زير پايشان- زمين- برخوردار مى‌شدند. از ايشان گروهى ميانه‌رواند و بسيارى از آنان آنچه مى‌كنند بد است.'),
(736, 5, 67, 'اى پيامبر، آنچه را از سوى پروردگارت به تو فروآمده برسان و اگر اين نكنى پيام او را نرسانده باشى و خدا تو را از [فتنه و گزند] مردم نگاه مى دارد، همانا خدا گروه كافران را راه ننمايد.'),
(737, 5, 68, 'بگو: اى اهل كتاب، شما بر چيزى نيستيد- پايگاه و ارزشى نداريد- تا آنكه تورات و انجيل و آنچه را كه از خداى شما به شما فروفرستاده شده برپا داريد و هر آينه آنچه از پروردگارت سوى تو فروآمده بسيارى از آنان را سركشى و كفر مى‌افزايد. پس بر گروه كافران اندوه مخور.'),
(738, 5, 69, 'همانا كسانى كه ايمان آوردند و كسانى كه يهودى شدند و صابئان و ترسايان هر كه به خدا و روز بازپسين ايمان آورد و كار نيك و شايسته كند نه بيمى بر آنان باشد و نه اندوهگين شوند.'),
(739, 5, 70, 'هر آينه از فرزندان يعقوب پيمان گرفتيم و پيامبرانى سوى آنان گسيل داشتيم، هر گاه آنان را پيامبرى آمد بدانچه خوشايندشان نبود، گروهى را دروغزن شمردند و گروهى را كشتند.'),
(740, 5, 71, 'و پنداشتند كه گرفتارى و آزمايشى نخواهد بود پس كور و كر شدند، سپس خداوند [با رحمت خود] بر آنان بازگشت و توبه‌شان را پذيرفت بار ديگر بسيارى از آنها كور و كر شدند، و خدا بدانچه مى‌كنند بيناست.'),
(741, 5, 72, 'هر آينه كافر شدند كسانى كه گفتند: خدا همان مسيح پسر مريم است و حال آنكه مسيح گفت: اى فرزندان يعقوب، خداى يكتا را بپرستيد كه پروردگار من و پروردگار شماست، كه هر كس براى خدا انباز گيرد خدا بهشت را بر او حرام كرده و جايگاهش آتش دوزخ است، و ستمكاران را هيچ يارى‌كننده‌اى نيست.'),
(742, 5, 73, 'هر آينه كافر شدند كسانى كه گفتند: خداوند يكى از سه [خدا] است- اب و ابن و روح القدس- و حال آنكه هيچ خدايى جز خداى يگانه نيست، و اگر از آنچه مى گويند باز نايستند البته به كسانى از آنها كه كافر شدند عذابى دردناك خواهد رسيد.'),
(743, 5, 74, 'آيا به خدا باز نمى‌گردند- توبه نمى‌كنند- و از او آمرزش نمى‌خواهند؟ و حال آنكه خدا آمرزگار و مهربان است.'),
(744, 5, 75, 'مسيح پسر مريم جز فرستاده‌اى نيست، كه پيش از او نيز فرستادگانى در گذشتند، و مادر او زنى راستى‌پيشه بود، هر دو غذا مى‌خوردند- پس مسيح خدا نيست- بنگر كه چگونه آيات را برايشان روشن بيان مى‌كنيم آنگاه بنگر كه چگونه [از حقّ‌] گردانيده مى‌شوند!'),
(745, 5, 76, 'بگو: آيا غير خداى يكتا چيزى را مى‌پرستيد كه براى شما زيان و سودى در توان ندارد؟ و حال آنكه خداست كه شنوا و داناست.'),
(746, 5, 77, 'بگو: اى اهل كتاب در دينتان به ناروا گزافگويى مكنيد و از هوسها و خواهشهاى گروهى كه پيش از اين گمراه شدند و بسيارى را گمراه كردند و خود از راه راست به گمراهى رفتند پيروى مكنيد.'),
(747, 5, 78, 'كسانى از فرزندان اسرائيل كه كافر شدند، بر زبان داوود و عيسى پسر مريم لعنت شدند، اين از آن رو بود كه نافرمانى كردند و از اندازه درمى گذشتند.'),
(748, 5, 79, 'يكديگر را از كار ناروا و زشتى كه مى‌كردند باز نمى‌داشتند هر آينه بد است آنچه مى‌كردند.'),
(749, 5, 80, 'بسيارى از آنان را مى‌بينى كه با كسانى كه كافر شدند دوستى و همدلى دارند براستى بد است آنچه نفس آنان برايشان پيش آورده كه خداوند بر آنها خشم گرفته است و در عذاب جاويدانند.'),
(750, 5, 81, 'و اگر به خدا و پيامبر و آنچه به او فروآمده ايمان مى‌آوردند آنها را دوست نمى‌گرفتند، و ليكن بسيارى از آنان بدكاران نافرمانند.'),
(751, 5, 82, 'هر آينه سرسخت‌ترين مردم را در دشمنى با مؤمنان، جهودان و مشركان مى‌يابى، و نزديكترينشان را به دوستى با مؤمنان كسانى مى‌يابى كه گويند ما ترساييم، اين از آن‌روست كه ميان آنان كشيشان- دانشمندان مسيحى- و راهبان- صومعه‌داران- اند و آنان گردنكشى و بزرگ‌منشى- در برابر حق- نمى‌كنند'),
(752, 5, 83, 'و چون آنچه را بر پيامبر فروآمده بشنوند مى‌بينى كه ديدگانشان از آنچه از حق شناخته‌اند اشك مى‌بارد مى‌گويند: پروردگارا، ايمان آورديم پس [نام‌] ما را با گواهى‌دهندگان [به حق‌]- يعنى با نام ديگر مؤمنان- بنويس'),
(753, 5, 84, 'و ما را چيست كه به خدا و آنچه از حق سوى ما آمده نگرويم و حال آنكه اميدواريم كه پروردگارمان ما را با گروه نيكان و شايستگان در آورد؟'),
(754, 5, 85, 'پس خداوند آنان را بدانچه گفتند بهشت‌هايى پاداش داد كه از زير [كوشكها و درختان‌] آنها جويها روان است، جاودانه در آنند، و اين است پاداش نيكوكاران.'),
(755, 5, 86, 'و كسانى كه كافر شدند و آيات ما را دروغ انگاشتند آنها دوزخيانند.'),
(756, 5, 87, 'اى كسانى كه ايمان آورده‌ايد، چيزهاى پاكيزه را كه خدا براى شما حلال كرده حرام نكنيد و از حد مگذريد، كه خدا از حدّ گذرندگان را دوست ندارد.'),
(757, 5, 88, 'و از آنچه خدا شما را حلال و پاكيزه روزى داده بخوريد و از خدايى كه به او ايمان داريد پروا داشته باشيد.'),
(758, 5, 89, 'خداوند شما را به سوگندهاى بى‌قصد و بيهوده‌تان بازخواست نمى‌كند و ليكن شما را به سبب [شكستن‌] سوگندهايى كه با قصد و نيت ياد كرده‌ايد بازخواست مى‌كند. پس كفاره آن غذادادن ده مستمند است از ميانه- نه عالى و نه پست- آنچه به خانواده خويش مى‌خورانيد يا پوشاندن آنان يا آزادكردن يك بنده. و هر كه نيابد- يا نتواند- پس سه روز روزه بر اوست، اين كفاره سوگندهاى شماست چون سوگند خوريد [و سپس آن را بشكنيد] و سوگندهاى خويش را نگاه داريد. اينچنين خداوند آيات خود را براى شما روشن بيان مى‌كند شايد سپاس گزاريد.'),
(759, 5, 90, 'اى كسانى كه ايمان آورده‌ايد، همانا مى و قمار و بتها- با سنگهايى كه مقدس مى‌شمردند و بر آن قربانى مى‌كردند- و تيرهاى قرعه پليد و از كار شيطان است. پس، از آن بپرهيزيد باشد كه رستگار شويد.'),
(760, 5, 91, 'همانا شيطان مى‌خواهد در مى و قمار ميان شما دشمنى و كينه بيفكند و شما را از ياد خدا و از نماز بازدارد. پس آيا باز مى‌ايستيد؟!'),
(761, 5, 92, 'و خداى را فرمان بريد و پيامبر را فرمان بريد و پروا داشته باشيد. پس اگر روى برتابيد بدانيد كه بر پيامبر ما رسانيدن روشن و آشكار پيام است و بس.'),
(762, 5, 93, 'بر كسانى كه ايمان آورده‌اند و كارهاى نيك و شايسته كرده‌اند گناهى نيست در آنچه خورده‌اند- پيش از حكم تحريم- هر گاه پرهيزگار بوده و ايمان داشته باشند- بر ايمان پايدار باشند- و كارهاى شايسته كنند، سپس [از محرمات‌] پرهيز نمايند و [به تحريم آن‌] ايمان آورند و آنگاه نيز [از حرامها] بپرهيزند- بر پرهيزگارى پايدارى ورزند- و كار نيك كنند، و خداوند نيكوكاران را دوست دارد.'),
(763, 5, 94, 'اى كسانى كه ايمان آورده‌ايد، هر آينه خداوند شما را به چيزى از شكار كه دستها و نيزه‌هاى شما بدان رسد مى‌آزمايد تا خدا كسى را كه در نهان از او مى‌ترسد معلوم گرداند، و هر كه از اين پس- كه حرام‌بودن صيد حرم دانست- از حد درگذرد پس او را عذابى است دردناك.'),
(764, 5, 95, 'اى كسانى كه ايمان آورده‌ايد، در حالى كه احرام بسته‌ايد شكار مكشيد، و هر كه از شما آن را بعمد بكشد كيفرى- قربانى‌كردن- بايد مانند- برابر- آنچه كشته از جنس چهارپايان- شتر و گاو و گوسفند- به گواهى و حكم دو مرد عادل از شما- كه حكم به برابرى كنند-، قربانيى كه به حرم برسد- در حرم ذبح شود- يا كفاره‌اى كه غذادادن مستمندان است يا برابر آن روزه داشتن، تا كيفر كار خود بچشد. خدا از آنچه گذشته درگذشت، و هر كه باز گردد- باز به شكار در حرم پردازد- خدا از او انتقام گيرد و خدا تواناى بى‌همتا و كين‌ستان است.'),
(765, 5, 96, 'شكار دريا و خوراك آن براى شما حلال شده است براى برخوردارى شما و كاروانيان، و شكار خشكى تا هنگامى كه در احرام هستيد بر شما حرام شده است، و از خدايى كه به سوى او برانگيخته و فراهم مى‌شويد پروا كنيد.'),
(766, 5, 97, 'خداوند، كعبه، خانه شكوهمند، را وسيله برپايى مردم قرار داد و [نيز] ماه حرام- شكوهمند- و قربانى‌هاى بى‌نشان و قربانيهاى گردنبنددار را. اين براى آنست كه بدانيد خدا آنچه را در آسمانها و در زمين است مى‌داند و خداوند به هر چيزى داناست- آنچه حلال و حرام مقرّر مى‌كند از روى علم است-.'),
(767, 5, 98, 'بدانيد كه خدا سخت كيفر است و خدا آمرزگار و مهربان است.'),
(768, 5, 99, 'بر پيامبر جز رساندن پيام نيست، و خدا آنچه را آشكار مى‌كنيد و آنچه را پنهان مى‌داريد مى‌داند.'),
(769, 5, 100, 'بگو: پليد و پاك يكسان نيستند هر چند فراوانى پليد تو را به شگفت آرد پس اى خردمندان، از خدا پروا كنيد، باشد كه رستگار شويد.'),
(770, 5, 101, 'اى كسانى كه ايمان آورده‌ايد، از چيزهايى مپرسيد كه اگر براى شما آشكار شود شما را بد آيد و اندوهگين كند، و اگر [به اصرار] از آنها بپرسيد آنگاه كه قرآن فرومى‌آيد براى شما آشكار مى‌گردد [و از عهده آن بيرون نتوانيد آمد]. خداوند از آنها- پرسشهاى بيجا- درگذشت و خدا آمرزگار و بردبار است.'),
(771, 5, 102, 'همانا گروهى پيش از شما از آنها- آنگونه چيزها- پرسش كردند سپس [همين كه پاسخ شنيدند] بدان كافر گشتند.'),
(772, 5, 103, 'خدا در باره هيچ گوش شكافته‌اى و نه هيچ واگذاشته‌اى و نه هيچ بهم پيوسته‌اى و نه هيچ پشت نگاه داشته‌اى حكمى نكرده است، بلكه كسانى كه كافر شدند بر خدا دروغ مى‌بندند و بيشترشان خرد را به كار نمى‌برند.'),
(773, 5, 104, 'و چون به آنان گفته شود: به سوى آنچه خدا فروفرستاده و به سوى پيامبر بياييد، گويند: آنچه پدرانمان را بر آن يافتيم ما را بس است آيا هر چند كه پدرانشان چيزى نمى‌دانستند و راه نمى‌يافتند [باز هم از آنان پيروى مى‌كنند]؟!'),
(774, 5, 105, 'اى كسانى كه ايمان آورده‌ايد، بر شما باد [نگاهداشت‌] خويشتن چون شما راه‌يافته باشيد هر كه گمراه شد به شما زيانى نرساند. بازگشت همه شما به سوى خداست، پس شما را بدانچه مى‌كرديد آگاه مى‌كند.'),
(775, 5, 106, 'اى كسانى كه ايمان آورده‌ايد، چون مرگ يكيتان فرا رسد، گواهى ميان شما هنگام وصيّت [گواهى‌] دو تن عادل از شماست، يا اگر در سفر بوديد و شما را پيشامد مرگ فرا رسد دو تن ديگر از غير خود- اهل دينى ديگر- و اگر [از آن دو] به شك باشيد پس از نماز آنان را نگاه داريد تا به خدا سوگند خورند كه «ما هيچ بهايى را در برابر آن [گواهى‌] نستانيم هر چند وى خويشاوند باشد و گواهى خدا را پنهان نسازيم، كه آنگاه هر آينه از گناهكاران باشيم».'),
(776, 5, 107, 'و اگر پى برده شد كه آن دو تن [در گواهى خود] مرتكب گناه شده‌اند،- يعنى خيانت ورزيده‌اند كه دروغى را كه ساخته بودند راست جلوه دادند- پس دو گواه ديگر از آنان كه گواهان سزاوارتر [به گواهى‌دادن‌] در حق آنان خيانت ورزيده‌اند- يعنى دو تن از اولياى ميّت- به جاى آن دو بايستند و به خدا سوگند خورند كه گواهى ما درست‌تر است از گواهى آنها و ما [از راستى و حقّ‌] فراتر نرفته‌ايم، كه آنگاه- در صورت تجاوز- هر آينه از ستمكاران باشيم.'),
(777, 5, 108, 'اين [روش‌] نزديكتر است به اينكه آنگونه كه بايد گواهى دهند يا بترسند كه سوگندهايى پس از سوگندهاى ايشان [بر مدعيان‌] ردّ كرده شود- و در نتيجه دروغشان آشكار شده رسوا گردند- و از خدا پروا كنيد و [به فرمان او] گوش فرا داريد و خداوند گروه بدكاران نافرمان را راه ننمايد،'),
(778, 5, 109, 'روزى كه خدا پيامبران را فراهم آورد و گويد: [دعوت‌] شما را چه پاسخ دادند؟ گويند: ما را دانشى نيست، همانا تويى داناى نهانها.'),
(779, 5, 110, '[ياد كن‌] آنگاه كه خداوند به عيسى پسر مريم گفت: به يادآر نعمت مرا بر خود و مادرت، آنگاه كه تو را به روح پاك- روح القدس، جبرئيل- نيرومند گردانيدم كه با مردم در گاهواره و در بزرگسالى- ميانسالى، هنگام دو مويى، ميان پيرى و جوانى- سخن گويى و آنگاه كه تو را كتاب و حكمت- انديشه و گفتار و كردار درست- و تورات و انجيل آموختيم و آنگاه كه به فرمان من از گل بسان پرنده مى‌ساختى و در آن مى‌دميدى پس به فرمان من پرنده‌اى ميشد، و نابيناى مادرزاد و پيس را به فرمان من بهبود مى‌دادى و آنگاه كه مردگان را به فرمان من بيرون مى‌آوردى- زنده مى‌ساختى- و آنگاه كه [آسيب‌] فرزندان اسرائيل را از تو بازداشتم هنگامى كه با نشانه‌هاى روشن- معجزه‌ها- نزدشان آمدى پس كسانى از آنان كه كافر شدند گفتند: اين نيست مگر جادويى آشكار.'),
(780, 5, 111, 'و [ياد كن‌] آنگاه كه به حواريان- ويژگان عيسى- وحى كردم- پيام دادم- كه به من و به فرستاده‌ام ايمان آوريد، گفتند: ايمان آورديم و گواه باش كه ما مسلمانيم- گردن نهاده و تسليم فرمان خداييم-.'),
(781, 5, 112, '[و ياد كن‌] آنگاه كه حواريان گفتند: اى عيسى پسر مريم، آيا پروردگار تو مى‌تواند- دعاى تو را اجابت مى‌كند- كه خوانى از آسمان بر ما فروفرستد؟ گفت: از خدا پروا كنيد، اگر ايمان داريد.'),
(782, 5, 113, 'گفتند: مى‌خواهيم تا از آن بخوريم و دلهامان آرام گيرد و بدانيم كه به ما راست گفته‌اى و بر آن از گواهان باشيم.'),
(783, 5, 114, 'عيسى پسر مريم گفت: بارخدايا، پروردگار ما، خوانى از آسمان بر ما فروفرست تا ما را، پيشينان و پسينان ما را- يعنى ما را و آنان را كه از پس ما آيند-، عيدى باشد و نشانى از تو، و ما را روزى ده و تو بهترين روزى‌دهندگانى.'),
(784, 5, 115, 'خداى گفت: من فروفرستنده آنم بر شما، پس هر كه از شما پس از آن كافر شود او را عذابى كنم كه هيچ يك از مردم جهان را چنان عذاب نكنم.'),
(785, 5, 116, 'و آنگاه كه [در روز رستاخيز] خداى گويد: اى عيسى پسر مريم، آيا تو به مردم گفتى كه مرا و مادرم را سواى خدا به خدايى گيريد؟ گويد: پاك خدايا، مرا نسزد كه آنچه سزاوار من نيست بگويم، اگر اين را گفته بودم تو خود آن را دانسته‌اى، تو آنچه را در نهان من است مى‌دانى و من آنچه را در ذات توست نمى‌دانم، كه تويى داناى نهانها'),
(786, 5, 117, 'من به آنان نگفتم مگر آنچه مرا فرمان دادى كه خداى را كه پروردگار من و پروردگار شماست بپرستيد، و تا در ميان آنها به سر مى‌بردم بر آنان گواه و نگاهبان بودم و چون مرا از ميان ايشان برگرفتى تو خود بر آنان نگاهبان بودى و تو بر هر چيزى گواهى'),
(787, 5, 118, 'اگر عذابشان كنى بندگان تواند و اگر آنان را ببخشايى اين تويى كه تواناى بى‌همتا و داناى با حكمتى.'),
(788, 5, 119, 'خداى گفت: اين روزى است كه راستگويان را راستيشان سود دهد، ايشان را بهشت‌هايى است كه از زير [درختان‌] آنها جويها روان است، هميشه در آن جاويدانند. خدا از آنان خشنود است و آنان از خدا خشنودند. اين است كاميابى و رستگارى بزرگ.'),
(789, 5, 120, 'خداى راست پادشاهى آسمانها و زمين و آنچه در آنهاست و او بر هر چيزى تواناست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'سپاس و ستايش خداى راست كه آسمانها و زمين را بيافريد و تاريكيها و روشنايى را پديد كرد، سپس [با اين همه نشانه‌ها] كسانى كه كافر شدند [بتان را] با پروردگار خويش برابر مى‌كنند- براى خدا همتا مى‌گيرند-.'),
(791, 6, 2, 'اوست كه شما را از گِلى بيافريد، سپس سرآمدى را [براى زندگى شما در دنيا] مقرر داشت، و سرآمدى نامبرده- معين- نزد اوست، آنگاه شما به شكّ اندريد.'),
(792, 6, 3, 'و اوست خداى يگانه- اللّه- در آسمانها و زمين، نهان شما و آشكار شما را مى‌داند و آنچه مى‌كنيد مى‌داند.'),
(793, 6, 4, 'هيچ نشانه‌اى از نشانه‌هاى پروردگارشان بديشان نيامد مگر آنكه از آن رويگردان بودند.'),
(794, 6, 5, 'همانا حق را هنگامى كه بديشان آمد دروغ انگاشتند، پس بزودى خبرهاى آنچه بدان ريشخند مى‌كردند به آنان مى‌رسد.'),
(795, 6, 6, 'آيا نديدند كه چه گروه‌هايى از مردم را پيش از آنان نابود كرديم، آنان را در زمين چنان جايگاه و توان داديم كه شما را نداديم و بر آنان بارانهاى پياپى فرستاديم و جويها را در زير [پاى‌] آنها روان ساختيم، پس آنان را به سزاى گناهانشان هلاك كرديم، و از پس ايشان مردمى ديگر پديد آورديم.'),
(796, 6, 7, 'و اگر كتابى نوشته در كاغذى بر تو فروفرستيم و آن را با دستهاى خويش لمس كنند، كسانى كه كافر شدند هر آينه گويند: اين نيست مگر جادويى آشكار.'),
(797, 6, 8, 'و گفتند چرا فرشته‌اى بر او فرو نيامد؟ و اگر فرشته‌اى مى‌فرستاديم همانا كار گزارده مى‌شد- هلاك مى‌شدند- و ديگر مهلت نمى‌يافتند.'),
(798, 6, 9, 'و اگر او را فرشته‌اى مى‌كرديم باز هم او را [به صورت‌] مردى مى‌ساختيم و آنچه را [اكنون بر خود و ديگران‌] پوشيده مى‌دارند بر آنان پوشيده مى داشتيم.'),
(799, 6, 10, 'و هر آينه فرستادگان پيش از تو نيز استهزا شدند، پس به كسانى كه ايشان را مسخره مى‌كردند همان چيزى كه به ريشخند مى‌گرفتند- عذاب الهى- فرو آمد.'),
(800, 6, 11, 'بگو: در زمين بگرديد و بنگريد كه سرانجام دروغ‌انگاران چگونه بود.'),
(801, 6, 12, 'بگو: آنچه در آسمانها و زمين است كه راست؟ بگو: خداى راست، مِهر و بخشايش را بر خود نوشته است هر آينه شما را براى روز رستاخيز كه شكّى در آن نيست فراهم آرد. كسانى كه خويشتن را در زيان افكندند، ايمان نمى‌آورند.'),
(802, 6, 13, 'و او راست آنچه در شب و روز جاى و آرام دارد و اوست شنوا و دانا.'),
(803, 6, 14, 'بگو: آيا جز خداى را كه آفريننده آسمانها و زمين است و او مى‌خوراند و خورانده نمى‌شود دوست و سرپرست بگيرم؟! بگو: هر آينه من فرمان يافته‌ام كه نخستين كسى باشم كه [او را] گردن نهاده است و البته از شرك‌آرندگان مباش.'),
(804, 6, 15, 'بگو: من مى‌ترسم كه اگر پروردگار خويش را نافرمانى كنم گرفتار عذاب روزى بزرگ شوم.'),
(805, 6, 16, 'هر كه در آن روز، [عذاب‌] از او گردانده شود بى‌گمان [خداوند] او را بخشوده است و كاميابى و رستگارى آشكار همين است.'),
(806, 6, 17, 'و اگر خدا گزندى به تو رساند، كسى جز او بازدارنده آن نيست، و اگر تو را نيكى رساند پس او بر هر چيزى تواناست.'),
(807, 6, 18, 'و اوست چيره بر فراز بندگان خويش، و اوست درست‌كردار و آگاه.'),
(808, 6, 19, 'بگو: كدام چيز گواهى‌اش بزرگتر است؟ بگو: خدا ميان من و شما گواه است. و اين قرآن به من وحى شده است تا با آن شما و هر كه را [كه پيام آن‌] به او برسد بيم دهم. آيا شما براستى گواهى مى‌دهيد كه با خدا خدايان ديگر هست؟! بگو: من گواهى نمى‌دهم. بگو: همانا او خداى يگانه است و من از آنچه شما [با خدا] انباز مى‌گيريد بيزارم.'),
(809, 6, 20, 'كسانى كه به آنان كتاب داده‌ايم او- پيامبر- را مى‌شناسند همچنانكه فرزندان خويش را مى‌شناسند. آنان كه خويشتن را در زيان افكندند، آنها ايمان نمى‌آورند.'),
(810, 6, 21, 'و چه كسى ستمكارتر است از آن كه بر خدا دروغ ساخته- براى او شريك گرفته- يا آيات او را دروغ انگاشته است؟ همانا ستمكاران رستگار نخواهند شد.'),
(811, 6, 22, 'روزى كه همه آنان را برانگيزيم، آنگاه به كسانى كه شرك آوردند گوييم: كجايند آن شريكانتان كه مى‌پنداشتيد [شريك خدايند]؟'),
(812, 6, 23, 'آنگاه عذر [و پاسخ‌] آنها جز اين نيست كه گويند: سوگند به خدا، پروردگارمان، كه ما مشرك نبوديم.'),
(813, 6, 24, 'بنگر كه چگونه بر خويشتن دروغ بستند و آنچه به دروغ مى‌ساختند از آنها گم [و نابود] شد.'),
(814, 6, 25, 'و از آنان كسانى‌اند كه [چون قرآن مى‌خوانى‌] به تو گوش فرامى‌دهند، و بر دلهاشان پوششها افكنده‌ايم تا آن را درنيابند و در گوشهاى [دل‌] آنها گرانى نهاده‌ايم، و اگر هر نشانه‌اى بينند بدان ايمان نمى‌آورند تا چون نزد تو آيند آنها كه كافر شدند ستيزه‌كنان با تو گويند: اين نيست مگر افسانه هاى پيشينيان.'),
(815, 6, 26, 'و آنان- كافران- [مردم را] از وى- پيامبر- بازمى‌دارند و خود از او دور مى‌شوند، و هلاك نمى‌كنند مگر خويشتن را و خود نمى‌فهمند.'),
(816, 6, 27, 'و اگر ببينى آنگاه كه بر آتش دوزخ بدارندشان [منظره‌اى سخت ناخوشايند خواهى ديد]، گويند: اى كاش [به دنيا] باز گردانده شويم و آيات پروردگارمان را دروغ نشمريم و از مؤمنان باشيم.'),
(817, 6, 28, '[چنين نيست‌] بلكه آنچه پيشتر پوشيده و پنهان مى‌داشتند بر آنها پديدار شده، و اگر [به دنيا] بازگردانده شوند بى‌گمان بدانچه از آن نهى شده‌اند بازگردند، و هر آينه آنان دروغگويند.'),
(818, 6, 29, 'و گفتند: جز زندگى ما در دنيا زندگى ديگرى نيست و ما برانگيخته‌شدنى نيستيم.'),
(819, 6, 30, 'و اگر ببينى آنگاه كه در برابر پروردگارشان نگاهشان دارند، [حالشان را سخت ناگوار خواهى ديد]، [خداى‌] گويد: آيا اين [برانگيخته‌شدن‌] حق نيست؟ گويند: چرا، به پروردگارمان سوگند. گويد: پس بچشيد اين عذاب را به سزاى آنكه كفر مى‌ورزيديد.'),
(820, 6, 31, 'هر آينه كسانى كه ديدار خداى را دروغ انگاشتند زيانكار شدند، تا چون رستاخيز ناگهان بديشان فرا رسد گويند: اى دريغا بر آنچه در باره آن كوتاهى كرديم- يا: اى دريغ بر آن كوتاهى كه در دنيا كرديم- و آنها بارهاى [گناهان‌] خود را بر پشتهاى خويش برمى‌دارند، هان كه بد [بارى‌] است آنچه برمى‌دارند.'),
(821, 6, 32, 'و زندگى اين جهان جز بازيچه و سرگرمى نيست و هر آينه سراى واپسين براى آنان كه پرهيزگارى كنند بهتر است آيا خرد را كار نمى‌بنديد؟'),
(822, 6, 33, 'براستى مى‌دانيم كه آنچه مى‌گويند هر آينه تو را اندوهگين مى‌كند، ولى آنها تو را دروغگو نمى‌انگارند بلكه آن ستمكاران آيه‌هاى خدا را انكار مى كنند.'),
(823, 6, 34, 'و هر آينه پيامبرانى كه پيش از تو بودند تكذيب شدند و بر تكذيب و آزارى كه ديدند شكيبايى كردند تا يارى ما بديشان رسيد. و سخنان خداى را دگرگون‌كننده‌اى نيست و هر آينه از خبر [هاى‌] فرستادگان به تو رسيده است.'),
(824, 6, 35, 'و اگر روى‌گرداندن آنان بر تو گران و دشوار مى‌آيد، اگر توانى سوراخى [براى فرو شدن‌] در زمين يا نردبانى [براى فرا شدن‌] در آسمان بجويى تا آيتى برايشان بياورى [چنان كن- كه البته نتوانى كرد-]. و اگر خدا مى‌خواست آنان را بر راه راست گرد مى‌آورد. پس، از نادانان مباش.'),
(825, 6, 36, 'همانا كسانى [دعوت حق را] مى‌پذيرند و پاسخ مى‌دهند كه بشنوند [و دريابند، و كافران كه نمى‌گروند مرده‌دلانند] و مُردگان را خداى بر انگيزد- آنگاه در خواهند يافت ولى آن دانستن سود نكند-، سپس همه به سوى او بازگردانده شوند.'),
(826, 6, 37, 'و گفتند: چرا نشانه‌اى [به دلخواه ما] از خدايش بر او فرو نيامده؟ بگو: همانا خداوند تواناست كه نشانه‌اى فروفرستد، ولى بيشترشان نمى‌دانند.'),
(827, 6, 38, 'و هيچ جُنبده‌اى در زمين نيست و نه هيچ پرنده‌اى كه به دو بال خويش بپرد مگر آنكه گروه‌هايى مانند شمايند- آفريده و روزى خوار-. در كتاب- قرآن يا لوح محفوظ- هيچ چيزى را فروگذار نكرده‌ايم، سپس همه به سوى پروردگارشان برانگيخته و فراهم مى‌شوند.'),
(828, 6, 39, 'و كسانى كه آيات ما را دروغ پنداشتند كرانند- از شنيدن حق- و گنگانند- از اقرار به حق- كه در تاريكيها- نادانى‌ها- باشند. خدا هر كه را خواهد گمراه مى‌كند و هر كه را خواهد بر راه راست فرا مى‌دارد.'),
(829, 6, 40, 'بگو: اگر راستگوييد، مرا گوييد كه اگر عذاب خدا بر شما فروآيد يا روز رستاخيز شما را فرا رسد آيا جز خدا را مى‌خوانيد؟'),
(830, 6, 41, '[نه،] بلكه او را مى‌خوانيد و بس، پس اگر خواهد آنچه را كه برطرف شدنش را دعا مى‌كرديد برطرف مى‌كند، و آنچه را براى او انباز مى‌سازيد از ياد مى‌بريد.'),
(831, 6, 42, 'و هر آينه [پيامبرانى‌] به سوى امّتهاى پيش از تو فرستاديم و آنان- امتها- را به تنگى و سختى و رنج و گزند بگرفتيم تا مگر زارى كنند.'),
(832, 6, 43, 'پس چرا هنگامى كه عذاب ما به آنها رسيد زارى نكردند؟! بلكه دلهايشان سخت شد و شيطان كارهاشان را در نظرشان بياراست.'),
(833, 6, 44, 'پس چون آنچه را بدان پند داده شدند فراموش كردند درهاى همه چيز را بر آنان بگشوديم تا چون بدانچه داده شدند شادمان گشتند ناگهان بگرفتيمشان پس همان دم [از نجات و رحمت‌] نوميد شدند.'),
(834, 6, 45, 'پس بيخ و بن گروهى كه ستم كردند بريده شد و سپاس و ستايش خداى راست، پرودگار جهانيان.'),
(835, 6, 46, 'بگو: مرا گوييد كه اگر خداوند شنوايى و بينايى شما را بازگيرد و بر دلهاى شما مُهر نهد كدام خداست جز خداى يكتا- اللّه- كه آن را به شما بازآرد؟ بنگر كه چسان آيات را مى‌گردانيم- گونه‌گون بيان مى‌كنيم- آنگاه آنان روى مى‌گردانند.'),
(836, 6, 47, 'بگو: مرا گوييد كه اگر عذاب خدا ناگاه يا آشكارا به شما رسد آيا جز گروه ستمكاران هلاك مى‌شوند؟'),
(837, 6, 48, 'ما پيامبران را نمى‌فرستيم مگر مژده‌دهندگان و بيم‌كنندگان، پس كسانى كه ايمان آورند و كار نيك و شايسته كنند نه بيمى بر آنان است و نه اندوهگين شوند'),
(838, 6, 49, 'و كسانى كه آيات ما را دروغ شمردند، به سزاى نافرمانى و بدكاريشان عذاب بديشان رسد.'),
(839, 6, 50, 'بگو: من شما را نمى‌گويم كه گنجينه‌هاى خدا نزد من است و نه غيب مى‌دانم و شما را نمى‌گويم كه من فرشته‌ام من پيروى نمى‌كنم مگر آنچه را كه به من وحى مى‌شود. بگو: آيا نابينا و بينا يكسانند؟ آيا نمى‌انديشيد؟'),
(840, 6, 51, 'و بدين [قرآن‌] هشدار ده كسانى را كه مى‌ترسند كه به سوى پروردگارشان برانگيخته و گرد آورده شوند در حالى كه جز او يارى و شفيعى براى آنان نباشد، شايد پرهيزگارى پيشه كنند.'),
(841, 6, 52, 'و كسانى را كه پروردگار خويش را در بامداد و شبانگاه مى‌خوانند و او را مى‌خواهند از خود مران. چيزى از حساب ايشان بر تو نيست و چيزى از حساب تو بر آنان نيست تا از خود برانيشان پس در شمار ستمكاران شوى.'),
(842, 6, 53, 'و اينچنين برخى‌شان را به برخى آزموديم تا [توانگران و نيرومندان‌] بگويند: آيا اينانند كه خدا از ميان ما بر آنان منّت نهاده است- به آنان توفيق ايمان داده و به ما نداده است-؟! آيا خداوند به سپاسگزاران داناتر نيست؟'),
(843, 6, 54, 'و چون كسانى كه به آيات ما ايمان دارند نزد تو آيند، بگو: درود بر شما، پروردگارتان مِهر و بخشايش را بر خود نوشته- مقرر داشته- است كه هر كس از شما كار بدى به نادانى كند سپس توبه آرد و به كار نيك و شايسته پردازد هر آينه او آمرزگار و مهربان است.'),
(844, 6, 55, 'و بدين سان آيات را شرح و تفصيل مى‌دهيم [تا حق روشن گردد] و تا راه گناهكاران آشكار شود.'),
(845, 6, 56, 'بگو: من نهى شده‌ام از اينكه جز خدا آنهايى را بپرستم كه شما مى‌پرستيد. بگو: من از هوسها و خواهشهاى دل شما پيروى نخواهم كرد كه آنگاه گمراه شوم و از راه‌يافتگان نباشم.'),
(846, 6, 57, 'بگو: من بر دليلى روشن از پروردگار خويشم و شما آن را دروغ پنداشتيد. آنچه به شتاب مى‌خواهيد- يعنى عذاب و يكسره شدن كار- نزد من نيست. فرمانروايى نيست مگر خداى را. حق را [از باطل‌] جدا و بيان مى‌كند و او بهترين جداكننده [حق از باطل‌] است.'),
(847, 6, 58, 'بگو: اگر آنچه به شتاب مى‌خواهيد نزد من بود كار ميان من و شما گزارده شده بود- عذاب مى‌رسيد و كار يكسره مى‌شد و به پايان مى‌رسيد- و خدا به ستمكاران داناتر است.'),
(848, 6, 59, 'و كليدهاى غيب نزد اوست، جز او كسى آنها را نمى‌داند، و آنچه را در خشكى و درياست مى‌داند، و هيچ برگى [از درخت‌] نيفتد مگر آنكه آن را مى داند، و نه هيچ دانه‌اى در تاريكيهاى- درون- زمين و نه هيچ ترى و نه هيچ خشكى مگر آنكه در نوشته‌اى است روشن- لوح محفوظ.'),
(849, 6, 60, 'و اوست كه [روح‌] شما را به شب برمى‌گيرد و آنچه در روز كرده باشيد مى‌داند، سپس شما را در روز بر مى‌انگيزد تا مدتى نامبرده سپرى شود- مرگ فرا رسد-، سپس بازگشت شما به سوى اوست، آنگاه شما را از آنچه مى‌كرديد آگاه مى‌كند.'),
(850, 6, 61, 'و اوست چيره بر فراز بندگان خويش، و نگاهبانانى بر شما مى‌فرستد، تا هنگامى كه يكى از شما را مرگ فرا رسد فرستادگان ما [جان‌] او را برگيرند و آنان در كار خود كوتاهى نكنند.'),
(851, 6, 62, 'سپس به سوى خدا، خداوند راستينشان، بازگردانده شوند. بدانيد كه فرمانروايى از آن اوست و او از همه حسابگران شتابنده‌تر است.'),
(852, 6, 63, 'بگو: چه كسى شما را از تاريكيهاى خشكى و دريا [به هنگام درماندگى‌] مى‌رهاند؟ او را به زارى آشكارا و پنهانى مى‌خوانيد كه اگر ما را از اين [تاريكيها و رنجها] برهاند هر آينه از سپاسگزاران باشيم.'),
(853, 6, 64, 'بگو: خدا شما را از اينها و از هر اندوه سختى مى‌رهاند، آنگاه شما براى او انباز مى‌گيريد.'),
(854, 6, 65, 'بگو: اوست توانا بر آنكه بر شما عذابى از بالاى سرتان يا از زير پاهايتان فرستد يا شما را گروه‌هاى پراكنده كرده به هم اندازد و سختى گزند برخى‌تان را به برخ ديگر بچشاند. بنگر كه چسان آيات را مى‌گردانيم- گوناگون بيان مى‌كنيم- باشد كه دريابند.'),
(855, 6, 66, 'و قوم تو آن'),
(856, 6, 67, 'هر خبرى را- كه از جانب خدا به شما داده شده- قرارگاهى- وقتى مقرر براى وقوع- است و بزودى خواهيد دانست.'),
(857, 6, 68, 'و چون كسانى را ببينى كه در باره آيات ما به ناسزاگويى و ياوه‌سرايى پردازند از آنها روى بگردان تا به سخنى ديگر پردازند و اگر شيطان از ياد تو ببرد، پس از ياد آورى، ديگر با گروه ستمكاران منشين.'),
(858, 6, 69, 'و بر كسانى كه [از سخنان آن ياوه‌گويان‌] مى‌پرهيزند چيزى از حساب آنها نيست، بلكه اين يادآورى و پندى است تا شايد آنها بپرهيزند.'),
(859, 6, 70, 'و كسانى را كه دين خود را به بازى و سرگرمى گرفته‌اند و زندگى اين جهان آنان را فريفته است واگذار و با آن'),
(860, 6, 71, 'بگو: آيا جز خدا چيزهايى را بخوانيم كه نه سودى به ما مى‌رساند و نه زيانى و پس از آنكه خداوند ما را راه نمود بر پاشنه‌هاى خود باز گردانده شويم- به گمراهى باز گرديم- مانند كسى كه شيطان‌ها او را از راه راست به در برده‌اند و در زمين شوريده و سرگشته مانده است؟ در حالى كه او را يارانى است [مشفق و دلسوز] كه به راه راست مى‌خوانندش كه نزد ما بيا. بگو: همانا رهنمونى خدا رهنمونى [راستين‌] است و ما فرمان يافته‌ايم كه پروردگار جهانيان را گردن نهيم.'),
(861, 6, 72, 'و اينكه نماز را برپا داريد و از او پروا كنيد، و اوست آن كه به سويش برانگيخته و فراهم‌آورده مى‌شويد.'),
(862, 6, 73, 'و اوست كه آسمانها و زمين را براستى و درستى آفريد، و روزى كه گويد: باش، پس مى‌باشد. گفتار او راست و درست است، و فرمانروايى او راست در روزى كه در صور- بوق، شاخ- دميده شود- براى زنده شدن مردگان-. داناى نهان و آشكار است و اوست درست‌كردار و آگاه.'),
(863, 6, 74, 'و [ياد كن‌] هنگامى كه ابراهيم به پدر خود آزر- عمويش كه به جاى پدر مربّى او بود- گفت: آيا بتانى را به خدايى مى‌گيرى؟! همانا من تو و گروه تو را در گمراهى آشكار مى‌بينم.'),
(864, 6, 75, 'و بدين‌سان ابراهيم را ملكوت آسمانها و زمين مى‌نموديم [تا گمراهى قوم خود و يگانگى پروردگار را دريابد] و تا از اهل يقين باشد.'),
(865, 6, 76, 'پس چون تاريكى شب بر وى در آمد ستاره‌اى ديد، گفت: اين خداى من است، پس چون فرو شد گفت: فرو شوندگان را دوست ندارم.'),
(866, 6, 77, 'و چون ماه را بر آينده ديد، گفت: اين خداى من است، پس چون فروشد گفت: اگر پروردگارم مرا راه ننمايد بى‌گمان از گمراهان باشم.'),
(867, 6, 78, 'پس چون خورشيد را برآينده ديد گفت: اين خداى من است، اين بزرگتر است، و چون فروشد گفت: اى قوم من، من از آنچه [با خدا] انباز مى‌گيريد بيزارم.'),
(868, 6, 79, 'من يكسره روى [دل‌] خويش به سوى آن كه آسمانها و زمين را آفريده است كردم در حالى كه حق‌گرايم و از مشركان نيستم.'),
(869, 6, 80, 'و قوم او با وى گفت‌وگو و ستيزه كردند. گفت: آيا با من در باره خدا ستيزه مى‌كنيد و حال آنكه او مرا راه راست نموده است و من از آنچه با خدا انباز مى‌گيريد هيچ نمى‌ترسم، مگر آنكه خداى من چيزى را بخواهد، دانش پروردگار من همه چيز را فرا گرفته است. آيا پند نمى‌گيريد؟'),
(870, 6, 81, 'و چگونه از آنچه شما آن را [با خدا] انباز گرفتيد بترسم و حال آنكه شما نمى ترسيد از اينكه چيزى را با خدا انباز گرفته‌ايد كه هيچ حجتى براى آن بر شما فرو نفرستاده است. پس، اگر مى‌دانيد، كدام يك از اين دو گروه- موحّدان و مشركان- به ايمنى سزاوارترند؟'),
(871, 6, 82, 'كسانى كه ايمان آوردند و ايمانشان را به ستمى- شرك- نياميختند، ايشانند كه ايمنى دارند و آنان راه يافتگانند.'),
(872, 6, 83, 'و اينها حجّت ما بود كه به ابراهيم در برابر قومش داديم. هر كه را بخواهيم پايه‌ها بالا مى‌بريم، كه پروردگارت درستكار و داناست.'),
(873, 6, 84, 'و او را اسحاق و يعقوب بخشيديم، همه را راه نموديم و نوح را پيش از آن راه نموديم و از فرزندان او داوود و سليمان و ايّوب و يوسف و موسى و هارون را [راه نموديم‌]، و نيكوكاران را اينچنين پاداش مى‌دهيم'),
(874, 6, 85, 'و زكريّا و يحيى و عيسى و الياس را [راه نموديم‌] كه همه از شايستگان بودند'),
(875, 6, 86, 'و اسماعيل و يسع و يونس و لوط را [راه نموديم‌] و همه را بر مردم جهان برترى داديم 6'),
(876, 6, 87, 'و از پدرانشان و فرزندانشان و برادرانشان نيز و ايشان را برگزيديم و به راهى راست راه نموديم.'),
(877, 6, 88, 'اين است رهنمونى خدا، كه با آن هر كه را از بندگان خويش بخواهد راه نمايد. و اگر با خدا انباز گيرند هر آينه آنچه مى‌كردند تباه و نابود گردد.'),
(878, 6, 89, 'ايشانند كه كتاب و حكم- حكمت يا فرمانروايى و داورى- و پيامبريشان داديم. پس اگر اينان- كافران و منكران- به آنها- كتاب و حكم و نبوّت- كافر شوند هر آينه گروهى را بدانها برگماشته‌ايم كه بدانها كافر نباشند.'),
(879, 6, 90, 'ايشانند كسانى كه خداوند آنان را راه نمود، پس راه راست ايشان را پى گير بگو: از شما بر اين [پيام رسانى و راهنمايى‌] هيچ مزدى نمى‌خواهم، اين نيست مگر يادآورى و پندى براى مردم جهان.'),
(880, 6, 91, 'خداى را چنانكه سزاوار اوست نشناختند و ارج ننهادند آنگاه كه گفتند: خدا بر هيچ آدمى هيچ چيز نفرستاد. بگو: چه كسى آن كتاب را كه موسى آورد و براى مردم روشنايى و رهنمونى بود فرستاد؟ كه شما آن را [به صورت پراكنده‌] در كاغذها كرده- نوشته- [بخشى از] آن را آشكار مى‌كنيد و بسيارى را- كه وصف محمد'),
(881, 6, 92, 'و اين كتابى است كه آن را فرو فرستاديم، با بركت است و باوردارنده آنچه پيش آن بوده، [تا مردم را به پاداشهاى الهى مژده دهى‌] و تا [مردم‌] أم القرى- مكه- و آنها را كه پيرامون آنند بيم دهى، و كسانى كه به جهان پسين ايمان دارند به آن نيز ايمان مى‌آورند، و آنان بر نماز خويش مواظبت مى كنند.'),
(882, 6, 93, 'و چه كسى ستمكارتر است از آن كه بر خدا دروغى سازد يا گويد به من وحى شده و حال آنكه چيزى بدو وحى نشده است و آن كس كه گويد: من هم بزودى مانند آنچه خدا فرو فرستاده فرو مى‌فرستم. و اگر ببينى آنگاه كه ستمكاران در بى‌هوشيهاى مرگ درافتند! در حالى كه فرشتگان دستهاى خويش گسترده [و به آنها گويند:] جانهاى خويش بر آريد امروز- هنگام مرگتان يا وقتى كه به عذاب برزخ و قيامت در مى‌آييد- به سزاى آن سخنان ناروا كه در باره خدا مى‌گفتيد و در برابر آيات او گردنكشى مى‌كرديد به عذاب خواركننده كيفر داده خواهيد شد.'),
(883, 6, 94, 'و هر آينه همان گونه كه شما را بار نخست آفريديم تنها نزد ما آمديد و آنچه به شما داده بوديم پشت سر خويش- در دنيا- واگذاشتيد، و آن شفيعانتان- از بتان- را كه مى‌پنداشتيد در باره [تربيت و تعيين سرنوشت‌] شما شريكان [ما] هستند، با شما نمى‌بينيم. براستى پيوند ميان شما گسسته گشت و آنچه [شريكان ما و شفيعان خود] مى‌پنداشتيد از شما گم [و نابود] شد.'),
(884, 6, 95, 'همانا خداوند شكافنده دانه و هسته است، زنده را از مرده بيرون مى‌آورد و بيرون‌آرنده مرده از زنده است. اين است خداى شما، پس كجا گردانيده مى‌شويد؟!'),
(885, 6, 96, 'شكافنده سپيده‌دم است و شب را براى آرامش قرار داد و خورشيد و ماه را با حساب درست- يا معيار براى حساب- پديد كرد. اين است اندازه نهادن آن بى‌همتا توانمند دانا.'),
(886, 6, 97, 'و اوست كه براى شما ستارگان را آفريد تا در تاريكيهاى خشكى و دريا بدانها راه يابيد. ما نشانه‌ها را براى گروهى كه مى‌دانند به تفصيل بيان كرده ايم.'),
(887, 6, 98, 'و اوست كه شما را از يك تن آفريد پس [شما را] قرارگاهى- زمين- و سپردن جايى- شكم مادر و پشت پدر- است. ما نشانه‌ها را براى گروهى كه در مى‌يابند به تفصيل بيان كرده‌ايم.'),
(888, 6, 99, 'و اوست كه از آسمان آبى فروفرستاد پس با آن هر چيز روييدنى را بيرون آورديم و از آن، سبزه‌اى- گياهى كه از تخم روييده و ساقه و شاخه پيدا كرده- پديد كرديم كه از آن'),
(889, 6, 100, 'و براى خدا شريكانى از پريان انگاشتند و حال آنكه خدا آنها را آفريده است و به بى‌دانشى براى او پسران و دخترانى تراشيدند. او پاك و منزّه و برتر است از آنچه وصف مى‌كنند.'),
(890, 6, 101, 'نوپديدآرنده آسمانها و زمين است. چگونه او را فرزندى باشد و حال آنكه براى او همسرى نبوده است، و هر چيزى را آفريده و او به هر چيزى داناست.'),
(891, 6, 102, 'اين است خداى، پروردگار شما جز او خدايى نيست آفريدگار همه چيزهاست پس او را بپرستيد و او بر همه چيز نگاهبان است'),
(892, 6, 103, 'چشمها او را درنيابد و او چشمها را دريابد و اوست لطيف- بخشنده و نيكوكار يا باريكدان- و آگاه- به آشكار و نهان.'),
(893, 6, 104, 'همانا شما را از پروردگارتان بينشهايى- نشانه‌ها و دلايل روشن و دل آگاهى- آمد پس هر كه بينا [دل‌] گشت به سود خود اوست و هر كه كور [دل‌] شد به زيان خود اوست، و من نگاهبان شما نيستم.'),
(894, 6, 105, 'و بدين سان آيات را گونه گون مى‌گردانيم [تا حق را دريابند] و تا مباد آنكه بگويند: تو [اين را از ديگران‌] آموخته‌اى، و تا آن را براى گروهى كه مى دانند روشن بيان كنيم.'),
(895, 6, 106, 'آنچه را از پروردگارت به تو وحى مى‌شود پيروى كن، خدايى جز او نيست و از مشركان روى بگردان.'),
(896, 6, 107, 'و اگر خدا مى‌خواست شرك نمى‌آوردند و تو را بر آنان نگاهبان قرار نداديم و تو كارساز آنها نيستى.'),
(897, 6, 108, 'و آنها- يعنى خدايان مشركان- را كه به جاى خداى يكتا مى‌خوانند دشنام مدهيد كه آنان نيز خداى را از روى ستم و دشمنى و بى‌دانشى دشنام گويند. اين گونه براى هر گروهى كردارشان را آراسته‌ايم. سپس بازگشتشان به سوى پروردگارشان است، پس آنان را بدانچه مى‌كردند آگاه مى‌سازد.'),
(898, 6, 109, 'و با سخت‌ترين سوگندهاشان به خدا سوگند خوردند كه اگر آيتى- نشانه و معجزه‌اى- به آنان بيايد هر آينه بدان ايمان مى‌آورند. بگو: همانا آيتها- معجزه‌ها- نزد خداوند است و شما- اى مؤمنان- چه مى‌دانيد كه چون آنها'),
(899, 6, 110, 'و ما دلها و ديدگانشان را دگرگون مى‌كنيم [پس بدان ايمان نمى‌آرند] چنانكه نخستين‌بار بدان ايمان نياوردند، و در سركشى‌شان وامى‌گذاريم تا سرگشته باشند.'),
(900, 6, 111, 'و اگر ما فرشتگان را به سوى ايشان مى‌فرستاديم و مردگان با آنان به سخن مى آمدند و هر چيزى را رويارويشان فراهم مى‌آورديم باز هم ايمان نمى آوردند مگر آنكه خداى خواهد، و ليكن بيشترشان نادانى مى‌كنند.'),
(901, 6, 112, 'و همچنين هر پيامبرى را- مانند تو- دشمنى از شيطانهاى آدمى و پرى قرار داديم كه برخى‌شان به برخى ديگر با گفتار آراسته و فريبنده پيام نهانى مى‌فرستند تا فريب دهند، و اگر پروردگار تو مى‌خواست چنين نمى‌كردند. پس آنان را با آنچه برمى‌بافند واگذار'),
(902, 6, 113, '[چنين كرديم تا بيازماييمشان‌] و تا دلهاى كسانى كه به سراى پسين ايمان ندارند به آن [گفتار آراسته فريبا] بگرايد و آن را بپسندند و تا آنچه توانند كرد بكنند.'),
(903, 6, 114, '[بگو:] پس آيا جز خدا داورى بجويم و حال آنكه اوست كه اين كتاب را با شرح و بيان به سوى شما فروفرستاده است؟ و آنها كه كتاب بداديمشان مى دانند كه آن براستى و درستى از جانب پروردگار تو فروفرستاده شده است، پس از دودلان و شك‌داران مباش.'),
(904, 6, 115, 'و سخن پروردگار تو- يعنى قرآن- بر راستى و داد تمام شد سخنان او را دگرگون‌كننده‌اى- به افزودن يا كاستن- نيست، و اوست شنوا و دانا.'),
(905, 6, 116, 'و اگر بيشتر مردم زمين را فرمان برى تو را از راه خدا گمراه خواهند كرد. آنان جز گمان و پندار را پيروى نمى‌كنند و جز به گزاف و تخمين سخن نمى گويند.'),
(906, 6, 117, 'همانا پروردگار تو به كسانى كه از راه او گم مى‌شوند داناتر است و به راه‌يافتگان نيز داناتر است.'),
(907, 6, 118, 'پس، از آنچه [هنگام ذبح‌] نام خدا بر آن ياد شده است بخوريد اگر به آيات خدا ايمان داريد.'),
(908, 6, 119, 'و شما را چيست كه از آنچه نام خدا بر آن ياد شده نمى‌خوريد و حال آنكه آنچه را بر شما حرام كرده است- مگر آنچه به [خوردن‌] آن ناچار شويد- برايتان به تفصيل بيان داشته است؟ و هر آينه بسيارى به سبب آرزوها و هوسهاشان بى‌هيچ دانشى [مردم را] گمراه مى‌كنند و براستى پروردگار تو به ستمكاران- از حد گذرندگان- داناتر است.'),
(909, 6, 120, 'و گناهان آشكار و پنهان را واگذاريد زيرا كسانى كه دست به گناه مى‌يازند بزودى به سزاى آنچه بدان دست مى‌يازيدند كيفر خواهند شد.'),
(910, 6, 121, 'و از آنچه نام خدا بر آن ياد نشده مخوريد كه آن نافرمانى است. و همانا شيطانها نهانى به گوش دوستان خود مى‌خوانند تا با شما ستيزه كنند- كه چه فرقى است ميان حيوان مرده و حيوانى كه خود مى‌كشيد- و اگر آنان را فرمان بريد هر آينه مشرك خواهيد بود.'),
(911, 6, 122, 'آيا كسى كه [به دل‌] مرده- گمراه- بود پس [به ايمان‌] زنده‌اش كرديم و براى او نورى [از علم و معرفت‌] قرار داديم كه بدان در ميان مردم راه مى‌رود همانند كسى است كه در تاريكيهاست و از آن بيرون‌شدنى نيست؟ [همچنان كه ايمان در دل مؤمنان آراسته شده‌] همچنين براى كافران آنچه مى‌كردند آرايش يافته است.'),
(912, 6, 123, 'و بدين سان در هر شهرى سران بزهكارش را چنان كرديم تا در آن نيرنگ- تباهى و فريبكارى- همى‌كنند و نيرنگ نمى‌كنند مگر با خويشتن و خود نمى فهمند.'),
(913, 6, 124, 'و چون آنان را نشانه‌اى آيد گويند: هرگز ايمان نياريم تا آنكه همانند آنچه به فرستادگان خدا داده شده است، به ما نيز داده شود. [بگو:] خدا داناتر است كه پيامبرى خويش را كجا بنهد. بزودى بدانها كه گناه كردند به سزاى آن نيرنگها كه مى كردند خوارى و عذاب سخت از نزد خدا خواهد رسيد.'),
(914, 6, 125, 'پس هر كه را خدا بخواهد كه راه نمايد سينه او را براى [پذيرش‌] اسلام مى‌گشايد، و هر كه را بخواهد كه گمراه كند سينه او را تنگ و بسته مى‌گرداند [تا پذيراى حق نباشد] كه گويى به آسمان بر مى‌شود. بدين سان خدا پليدى- مرده‌دلى و انكار- را بر كسانى كه ايمان نمى‌آورند مى‌نهد.'),
(915, 6, 126, 'و اين است راه راست پروردگار تو هر آينه آيات را براى گروهى كه ياد مى‌كنند و پند مى‌گيرند به تفصيل بيان كرده‌ايم.'),
(916, 6, 127, 'ايشان را نزد پروردگارشان سراى سلامت و ايمنى است و همو به پاداش كارهايى كه مى‌كردند يار و سرپرست آنهاست.'),
(917, 6, 128, 'و روزى كه همه آنان را برانگيزد و فراهم آرد [گويد:] اى گروه پريان، شما از آدميان بسيارى را پيرو خود گرفتيد و دوستانشان از آدميان گويند: پروردگارا، برخى از ما از برخى ديگر برخوردار شدند و به سرآمدى كه براى ما نهادى رسيديم [خداى‌] گويد: جايگاه شما آتش دوزخ است كه جاودانه در آن باشيد مگر آنچه خدا خواهد همانا پروردگار تو درست‌كردار و داناست.'),
(918, 6, 129, 'و بدين گونه ستمكاران را به سزاى آنچه مى‌كردند بر يكديگر مى‌گماريم.'),
(919, 6, 130, 'اى گروه پريان و آدميان، آيا پيامبرانى از جنس خودتان سوى شما نيامدند كه آيات مرا بر شما مى‌خواندند و شما را از ديدار اين روزتان بيم مى‌دادند؟ گويند: ما بر خويشتن گواهى مى‌دهيم و زندگى دنيا آنان را فريفت و بر خويشتن گواهى دادند كه كافر بودند.'),
(920, 6, 131, 'اين [فرستادن پيامبران و بيم‌دادن ايشان‌] براى آن است كه پروردگار تو به ستم هلاك‌كننده [مردم‌] شهرها در حالى كه بى‌خبر و ناآگاه باشند نيست.'),
(921, 6, 132, 'و براى هر كسى از آنچه كرده است پايه‌هاست، و پروردگار تو از آنچه مى‌كنند غافل نيست.'),
(922, 6, 133, 'و پروردگار تو بى‌نياز و خداوند مهر و بخشايش است. اگر بخواهد شما را مى‌برد و پس از شما هر چه [از آفريدگان خود] را خواهد جانشين سازد، چنانكه شما را از نسل گروهى ديگر آفريد.'),
(923, 6, 134, 'همانا آنچه به شما وعده داده مى‌شود آمدنى است و شما عاجزكننده نيستيد- با گريز خود نمى‌توانيد عذاب را دفع كنيد-.'),
(924, 6, 135, 'بگو: اى قوم من، بر جايگاه [و سيرت‌] خويش عمل كنيد كه من نيز عمل‌كننده‌ام پس بزودى خواهيد دانست كه سرانجام آن سراى كه راست، همانا ستمكاران رستگار نمى‌شوند.'),
(925, 6, 136, 'و براى خدا از آنچه آفريده است از كشت و چهارپايان بهره‌اى قرار دادند و گفتند: اين براى خداست، به پندار خودشان، و اين براى شريكان ما- يعنى بتهايى كه براى خدا شريك ساخته‌اند-، اما آنچه براى شريكانشان است به خدا نمى‌رسد و آنچه براى خداست به شريكانشان مى‌رسد! بد است اين داورى كه مى‌كنند.'),
(926, 6, 137, 'و همچنين شريكان آنها- بتها يا خادمان بتخانه- در نظر بسيارى از مشركان كشتن فرزندانشان را- به عنوان قربانى براى خدايان- بياراستند تا آنان را هلاك كنند- گمراهشان گردانند- و دينشان را بر آنها آشفته و پوشيده سازند. و اگر خدا مى‌خواست اين كار را نمى‌كردند- يعنى به اجبار جلو آنان را مى‌گرفت اما سنّت الهى چنين نيست- پس آنان را با آن دروغها كه مى‌سازند واگذار.'),
(927, 6, 138, 'و به پندار خويش گفتند: اينها چهارپايان و كشتزارى است ممنوع، كه آن را نخورد مگر كسى كه ما بخواهيم- مردان خدمتكار خدايان- و چهارپايانى كه [سوارشدن بر] پشت آنها حرام شده است و چهارپايانى كه [هنگام كشتن‌] نام خدا را بر آن ياد نمى‌كنند اينها همه دروغى است كه بر خدا بستند، [و خداوند] بزودى آنها را به سزاى اين دروغها كه مى‌بافتند كيفر مى‌دهد.'),
(928, 6, 139, 'و گفتند: آنچه در شكمهاى اين چهارپايان است [اگر زنده به دنيا آمد] ويژه مردان ماست- براى مردان ما حلال است و بس- و بر آنان ما حرام است. و اگر مرده بود همه- مردان و زنان- در آن شريكند. [خداوند] بزودى آنان را به سزاى اين وصف‌كردنشان كيفر مى‌دهد، كه او با حكمت و داناست.'),
(929, 6, 140, 'همانا كسانى كه فرزندان خود را به نابخردى و بى‌دانشى كشتند و آنچه را خدا روزيشان كرد با دروغ‌بستن بر خدا حرام شمردند زيان كردند براستى كه گمراه شدند و راه‌يافته نبودند.'),
(930, 6, 141, 'و اوست كه بوستانهايى چفته بسته و چفته نابسته و خرمابن و كشت با خوردنى‌هاى گوناگون، و زيتون و انار، مانند و نامانند، بيافريد. از ميوه آن چون ميوه دهد بخوريد و حق آن را روز چيدن و دروكردنش بدهيد و گزافكارى مكنيد، كه او گزافكاران را دوست ندارد.'),
(931, 6, 142, 'و از چهارپايان، باركش و غير باركش- مانند گوسفند و كره چهارپايان باركش- [بيافريد]. از آنچه خدا روزى شما كرده بخوريد و در پى گامهاى شيطان مرويد كه او دشمن آشكار شماست.'),
(932, 6, 143, 'هشت جفت، از ميش دو- نر و ماده- و از بز دو- نر و ماده- [بيافريد]، بگو: آيا [خدا] دو نر را حرام كرده است يا دو ماده را يا آنچه را كه زهدانهاى آن دو ماده در بردارد؟ مرا از روى دانش خبر دهيد، اگر راستگوييد'),
(933, 6, 144, 'و از شتر دو- نر و ماده- و از گاو دو- نر و ماده- [بيافريد] بگو: آيا دو نر را حرام كرده است يا دو ماده را يا آنچه را كه زهدانهاى دو ماده در بردارد؟ يا مگر آنگاه كه خداوند شما را به اين حكم سفارش مى‌كرد حاضر و گواه بوديد؟! پس كيست ستمكارتر از آن كه بر خدا دروغ بافته تا مردم را به بى دانشى گمراه كند؟ همانا خدا گروه ستمكاران را راه ننمايد.'),
(934, 6, 145, 'بگو: در آنچه به من وحى شده حرام شده‌اى- كه شما خود حرام كرده‌ايد- نمى‌يابم بر هيچ خورنده‌اى كه آن را بخورد مگر آنكه مردارى باشد يا خون ريخته‌اى- جهنده‌اى- يا گوشت خوك، كه آن پليد است، يا آن [حيوان سربريده‌اى‌] كه به نافرمانى جز نام خداى بر آن آواز داده باشند. اما هر كه [به خوردن آن‌] ناچار شود بى‌آنكه سركش باشد و از اندازه درگذرنده، پس همانا پروردگار تو آمرزگار و مهربان است.'),
(935, 6, 146, 'و بر كسانى كه يهودى شدند هر ناخندارى را حرام كرديم و از گاو و گوسفند پيه آنها را بر آنان حرام كرديم مگر آنچه- از پيه- بر پشت آن دو باشد يا چربى رودها يا آنچه با استخوان آميخته باشد. اين را به سزاى سركشى‌كردنشان كيفر آنها ساختيم، و ما راستگويانيم.'),
(936, 6, 147, 'پس اگر تو را دروغگو شمرند بگو: پروردگارتان خداوند مهر و بخشايشى گسترده است، و كيفر سخت او از گروه بدكاران بازداشته نشود.'),
(937, 6, 148, 'كسانى كه شرك آوردند خواهند گفت: اگر خدا مى‌خواست نه ما شرك مى آورديم و نه پدرانمان و چيزى را حرام نمى‌كرديم. همين گونه كسانى كه پيش از آنان بودند [پيامبران را] تكذيب كردند تا سرانجام كيفر سخت ما را چشيدند، بگو: آيا نزد شما دانشى هست تا آن را براى ما آشكار كنيد؟! پيروى نمى‌كنيد مگر پندار را و جز به گزاف و تخمين سخن نمى‌گوييد.'),
(938, 6, 149, 'بگو: حجّت رسا خداى راست، پس اگر مى‌خواست همه شما را راه مى‌نمود.'),
(939, 6, 150, 'بگو: گواهان خود را كه گواهى مى‌دهند كه خدا اين را حرام كرده است بياوريد. پس اگر گواهى دادند تو با آنان گواهى مده، و آرزوها و هوسهاى كسانى را كه آيات ما را دروغ شمردند و كسانى را كه به سراى پسين ايمان ندارند و [بتان را] با پروردگار خويش برابر مى‌كنند- براى خدا همتا مى گيرند- پيروى مكن.'),
(940, 6, 151, 'بگو: بياييد آنچه را پروردگارتان بر شما حرام كرده بخوانم: اينكه چيزى را با او شريك مگيريد و به پدر و مادر نيكى كنيد و فرزندان خود را از بيم درويشى و تنگدستى مكشيد، ما شما و آنان را روزى مى‌دهيم، و پيرامون كارهاى زشت، چه آشكار و چه نهانش، مگرديد و كسى را كه خدا [كشتن او را] حرام كرده مكشيد مگر به حق. اينهاست كه شما را به آن سفارش كرده است، باشد كه خرد را كار بنديد.'),
(941, 6, 152, 'و پيرامون مال يتيم مگرديد- مگر به وجهى كه نيكوتر است- تا به جوانى و نيروى خود برسد و پيمانه و ترازو را به عدل و انصاف، تمام بدهيد هيچ كس را تكليف نمى‌كنيم مگر به اندازه توانش و چون سخن گوييد [يا گواهى مى‌دهيد] به عدل و داد گوييد اگر چه خويشاوند باشد، و به پيمان خدا وفا كنيد اينهاست كه [خدا] شما را به آن سفارش كرده است، تا شايد ياد كنيد و پند گيريد.'),
(942, 6, 153, 'و اين است راه راست من، پس آن را پيروى كنيد و به راه‌هاى ديگر مرويد كه شما را از راه او جدا مى‌كند اينهاست كه شما را به آن سفارش كرده است شايد پرهيزگار شويد.'),
(943, 6, 154, 'سپس به موسى كتاب- تورات- داديم براى تمام كردن [نعمت و كرامت‌] بر كسى كه نيكويى كرده، و نيز شرح و بيان هر چيزى و رهنمونى و بخشايشى تا شايد آنان- بنى اسرائيل- به ديدار پروردگارشان- روز رستاخيز- ايمان آورند.'),
(944, 6, 155, 'و اين- قرآن- كتابى است كه آن را فروفرستاديم، پر بركت است، پس آن را پيروى كنيد و پرهيزگار باشيد، شايد در خور رحمت گرديد'),
(945, 6, 156, 'تا مبادا گوييد كه كتاب تنها بر دو گروه پيش از ما- يهود و نصارى- فروفرستاده شده و هر آينه ما از خواندن [كتاب‌] آنان ناآگاه بوده‌ايم'),
(946, 6, 157, 'يا گوييد كه اگر بر ما كتاب فروفرستاده مى‌شد ما راه‌يافته‌تر از آنان بوديم. اينك شما را دليلى روشن از پروردگارتان و رهنمونى و بخشايشى آمد. پس كيست ستمكارتر از آن كه آيات خدا را دروغ انگاشت و از آنها روى گردانيد؟ بزودى كسانى را كه از آيات ما روى مى‌گردانند به سزاى آنكه روى مى‌گرداندند به عذابى بد و سخت كيفر مى‌دهيم.'),
(947, 6, 158, 'آيا [كافران‌] جز اين انتظار دارند كه فرشتگان [به نشانه عذاب‌] نزدشان بيايند يا پروردگار تو بيايد يا برخى از نشانه‌هاى پروردگار تو بيايد؟ روزى كه برخى از نشانه‌هاى پروردگار تو بيايد ديگر هيچ كس را كه پيش از آن ايمان نياورده يا در حال ايمانش كار نيكى نكرده ايمان آوردنش سود ندارد. بگو منتظر باشيد كه ما نيز منتظريم.'),
(948, 6, 159, 'كسانى كه دين خويش را دستخوش پراكندگى ساختند و گروه گروه شدند تو هيچ پيوند و بستگى با آنها ندارى كارشان با خداست و بس، آنگاه- روز رستاخيز- از آنچه مى‌كردند آگاهشان مى‌كند.'),
(949, 6, 160, 'هر كه كار نيكى آورد ده چندان آن پاداش دارد، و هر كه كار بدى آورد جز همانند آن كيفر نبيند و بر آنان ستم نرود- نيكى به نيكى و بدى به بدى پاداش داده شود نه جز آن-.'),
(950, 6, 161, 'بگو: هر آينه پروردگارم مرا به راه راست ره نمود، دينى پايا و درست، آيين ابراهيم پاك و حق‌گراى، و او از مشركان نبود.'),
(951, 6, 162, 'بگو: نماز من و عبادتهاى من- يا قربانى من يا حج من- و زندگانى من و مرگ من همه براى خدا، پروردگار جهانيان، است'),
(952, 6, 163, 'كه او را انبازى نيست، و مرا به اين [آيين‌] فرمان داده‌اند و من نخستين مسلمانم.'),
(953, 6, 164, 'بگو: آيا جز خداى يكتا پروردگارى بجويم و حال آنكه او خداوند همه چيز است؟ و هيچ كس كارى نكند مگر آنكه بر عهده خود اوست و هيچ كس بار گناه ديگرى را بر ندارد- كسى گناه ديگرى را از وى برندارد بلكه هر كس متحمل گناه خود است- سپس بازگشت شما به سوى پروردگارتان است و شما را بدانچه اختلاف مى‌كرديد آگاه مى‌كند.'),
(954, 6, 165, 'و اوست كه شما را جانشينان [پيشينيان در] زمين كرد و برخى از شما را بر برخى ديگر به پايه‌ها بالا برد تا در آنچه به شما داد بيازمايدتان. همانا پروردگار تو زود كيفر است، و هر آينه او آمرزگار و مهربان است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'الف، لام، ميم، صاد.'),
(956, 7, 2, '[اين‌] كتابى است كه به تو فروفرستاده شده- پس در سينه‌ات از آن تنگيى مباد- تا بدان بيم دهى و مؤمنان را يادكرد و پندى باشد.'),
(957, 7, 3, 'آنچه را از پروردگارتان به شما فروفرستاده شده پيروى كنيد، و از دوستان و سرپرستانى جز او پيروى مكنيد [ولى‌] اندك ياد مى‌كنيد و پند مى پذيريد.'),
(958, 7, 4, 'و بسا [مردم‌] شهرها و آباديها را كه هلاك و نابود كرديم، و عذاب ما شبانگاه يا هنگامى كه در خواب نيمروز بودند به آنها رسيد.'),
(959, 7, 5, 'پس چون عذاب ما بديشان رسيد سخن و اعترافشان جز اين نبود كه گفتند: ما ستمكار بوديم.'),
(960, 7, 6, 'پس هر آينه كسانى را كه به سويشان [پيامبر] فرستاده شده پرسش خواهيم كرد- از اجابت پيامبران- و از فرستادگان نيز خواهيم پرسيد- از رسانيدن پيام-.'),
(961, 7, 7, 'و هر آينه [احوالشان را] بر آنان از روى دانش- دانشى گسترده و تمام- بر گوييم، و ما [از آنان‌] غايب- دور و بى‌خبر- نبوديم.'),
(962, 7, 8, 'و سنجش كردارها در آن روز- رستاخيز- حق است پس هر كه كردارهاى سنجيده او گران و سنگين باشد، ايشانند رستگاران.'),
(963, 7, 9, 'و هر كه كردارهاى سنجيده او سبك باشد، آنانند كه به سبب آنكه به آيات ما ستم- انكار- مى‌كردند به خويشتن زيان زدند- خود را تباه ساختند-.'),
(964, 7, 10, 'و هر آينه شما را در زمين جاى داديم و براى شما در آن اسباب زندگى فراهم ساختيم [اما] اندك سپاس مى‌گزاريد.'),
(965, 7, 11, 'و هر آينه شما را بيافريديم سپس شما را در [زهدانها] صورتگرى كرديم، آنگاه به فرشتگان گفتيم آدم را سجده كنيد پس سجده كردند مگر ابليس كه از سجده آرندگان نبود.'),
(966, 7, 12, '[خداى‌] گفت: چه باز داشت تو را كه سجده نكنى آنگاه كه به تو فرمان دادم؟ گفت: من از او بهترم، مرا از آتش آفريدى و او را از گل.'),
(967, 7, 13, 'فرمود: پس، از اين جايگاه فرو شو، تو را نرسد كه در آن بزرگ‌منشى كنى، پس بيرون رو كه تو از خوارشدگانى.'),
(968, 7, 14, 'گفت: مرا تا روزى كه برانگيخته شوند مهلت ده.'),
(969, 7, 15, 'گفت: تو از مهلت‌يافتگانى.'),
(970, 7, 16, 'گفت: پس به سبب آنكه مرا به راه هلاكت افكندى- يا از رحمت خود بى‌بهره و نااميد ساختى- هر آينه آنان را در راه راست تو [به كمين‌] مى‌نشينم'),
(971, 7, 17, 'آنگاه بديشان از پيش روى و از پشت سر و از راست و چپشان در آيم، و [چنان كنم كه‌] بيشترشان را سپاسگزار [نعمت خود] نيابى.'),
(972, 7, 18, 'فرمود: از اينجا بيرون رو، نكوهيده و رانده شده، هر كه از آنان تو را پيروى كند هر آينه دوزخ را از همه شما پر خواهم كرد.'),
(973, 7, 19, 'و اى آدم، تو و همسرت در بهشت جاى گزينيد و از هر جا كه خواهيد بخوريد و نزديك اين درخت مشويد كه از ستمكاران خواهيد شد.'),
(974, 7, 20, 'پس شيطان آن دو را وسوسه كرد تا شرمگاهشان را كه از آنان پوشيده بود در نظرشان نمايان سازد و گفت: خداوندتان شما را از اين درخت باز نداشت مگر براى آنكه مبادا دو فرشته شويد يا جاويدان باشيد.'),
(975, 7, 21, 'و براى آن دو سوگند خورد كه من شما را هر آينه از نيكخواهانم.'),
(976, 7, 22, 'پس آن دو را به فريبى [از آن پايه بلند] فرود آورد و چون از آن درخت بچشيدند ايشان را شرمگاهشان پديدار شد و شروع كردند از برگهاى بهشت بر [شرمگاه‌] خويش چسبانيدن و خداوندشان آن دو را ندا كرد كه مگر شما را از آن درخت باز نداشتم و به شما نگفتم كه شيطان شما را دشمنى هويداست؟'),
(977, 7, 23, 'گفتند: بار خدايا، به خويشتن ستم كرديم و اگر ما را نيامرزى و نبخشايى براستى از زيانكاران باشيم.'),
(978, 7, 24, 'فرمود: [از اينجا] فرو رويد برخى از شما دشمن برخى ديگريد و شما را در زمين قرارگاه و برخوردارى باشد تا هنگامى- مرگ-.'),
(979, 7, 25, '[و] فرمود: در آن'),
(980, 7, 26, 'اى فرزندان آدم، همانا بر شما جامه‌اى فروفرستاديم كه شرمگاه شما را بپوشاند و هم زيبايى و زيور باشد و جامه پرهيزگارى، اين بهتر است. اين از نشانه‌هاى خداست، باشد كه ياد كنند و پند گيرند.'),
(981, 7, 27, 'اى فرزندان آدم، مبادا شيطان فريبتان دهد [و شما را از راه راست بيرون برد] چنانكه پدر و مادرتان را از بهشت بيرون كرد در حالى كه جامه‌شان را از آنها بر مى‌كند تا شرمگاهشان را بديشان بنمايد. او و گروه او از جايى كه آنها را نمى‌بينيد شما را مى‌بينند. ما شيطانها را دوستان و سرپرستان كسانى ساخته‌ايم كه ايمان نمى‌آورند.'),
(982, 7, 28, 'و چون كار زشتى كنند گويند: پدرانمان را بر آن يافتيم و خدا ما را بدان فرمان داده است بگو: خدا به زشتكارى فرمان نمى‌دهد آيا بر خدا چيزى مى گوييد كه نمى‌دانيد؟'),
(983, 7, 29, 'بگو: پروردگار من به انصاف و داد فرمان داده و اينكه روى [و توجه‌] خويش نزد هر مسجدى [و هنگام هر نمازى به سوى او] فرا داريد، و او را بخوانيد در حالى كه دين را ويژه او كرده باشيد- براى او انباز نگيريد-، همچنانكه در آغاز بيافريدتان بازمى‌گرديد.'),
(984, 7, 30, 'گروهى را راه نموده و گروهى گمراهى بر آنها روا شده است زيرا كه آنان شيطانها را به جاى خدا دوستان و سرپرستان گرفتند و مى‌پندارند كه راه‌يافتگانند.'),
(985, 7, 31, 'اى فرزندان آدم، نزد هر مسجدى- به هنگام نماز- آرايش خويش- جامه و آنچه مايه آراستگى است- فرا گيريد، و بخوريد و بياشاميد و اسراف مكنيد، كه خدا اسرافكاران را دوست ندارد.'),
(986, 7, 32, 'بگو: چه كسى آرايشى را كه خدا براى بندگان خود پديد آورده و روزيهاى پاكيزه را حرام كرده است؟ بگو: اينها در زندگى دنيا براى مؤمنان است- ولى كفار هم بهره‌مندند- در حالى كه در روز رستاخيز ويژه ايشان است. بدين گونه آيات- سخنان خود- را براى گروهى كه بدانند به تفصيل بيان مى كنيم.'),
(987, 7, 33, 'بگو: همانا پروردگارم زشتكاريها را، چه آشكار آنها و چه پنهان، و گناهكارى و ستم و سركشى به ناحق را حرام كرده، و اين كه چيزى را با خدا انباز گيريد كه بدان دليل و حجتى فرو نفرستاده، و اين كه بر خدا آنچه نمى‌دانيد بگوييد.'),
(988, 7, 34, 'و هر گروهى را مدتى است. پس چون مدتشان به سر آيد نه ساعتى درنگ توانند كرد و نه پيشى گيرند.'),
(989, 7, 35, 'اى فرزندان آدم، چنانچه پيامبرانى از خودتان نزد شما آيند كه آيات مرا بر شما بخوانند، پس آنان كه پرهيزگارى كنند و به شايستگى آيند نه بيمى بر آنهاست و نه اندوه خورند'),
(990, 7, 36, 'و كسانى كه آيات- نشانه‌ها و سخنان- ما را دروغ انگارند و از آنها به گردنكشى روى برتابند اينان دوزخيانند كه در آن جاودانه باشند.'),
(991, 7, 37, 'پس ستمكارتر از آن كيست كه بر خدا دروغى سازد- براى او انباز گيرد- يا آيات- نشانه‌ها و سخنان- او را دروغ شمرد؟ اينانند كه بهره‌شان از كتاب- سرنوشت و كيفرى كه براى آنها نوشته شده- بديشان رسد، تا چون فرستادگان ما- فرشتگان مرگ- نزدشان آيند كه جانشان بگيرند، گويند: كجايند آنچه جز خدا مى‌خوانديد؟ گويند: از ما گم و ناپديد شدند، و بر خويشتن گواهى دهند كه كافر بوده‌اند.'),
(992, 7, 38, '[خداى‌] فرمايد: با گروه‌هايى از پريان و آدميان كه پيش از شما گذشتند در آتش دوزخ رويد هر گاه كه گروهى در آيد گروه همكيش خود را نفرين كند، تا چون همگى در آن به هم رسند گروه پسين- پيروان- درباره گروه پيشين- رهبران خود- گويند: خداوندا، اينان ما را گمراه كردند، پس آنها را دو چندان عذاب آتش بده. [خداى‌] گويد: هر كدام را [عذاب‌] دو چندان است و ليكن نمى‌دانيد.'),
(993, 7, 39, 'و گروه پيشين به گروه پسين گويند: شما را بر ما هيچ برترى نيست- تا بدان سبب در خور تخفيف عذاب باشيد- پس عذاب را به سزاى آنچه مى‌كرديد بچشيد.'),
(994, 7, 40, 'همانا كسانى كه آيات ما را دروغ شمردند و از آنها به گردنكشى روى برتافتند درهاى آسمان- عالم ملكوت- را براى آنان نگشايند و به بهشت در نشوند تا شتر در سوراخ سوزن در آيد- كه اين هم ناشدنى است-، و گناهكاران را چنين كيفر مى‌دهيم.'),
(995, 7, 41, 'آنان را از دوزخ بسترى است و از بالاى سرشان پوشش‌هايى [از آتش‌] است و ستمكاران را چنين كيفر مى‌دهيم.'),
(996, 7, 42, 'و كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند [هر اندازه عمل نيك كرده‌اند بپذيريم، كه‌] هيچ كس را جز به اندازه توانش تكليف ننهيم. ايشان بهشتيانند و در آن جاودانه باشند'),
(997, 7, 43, 'و هر چه از كينه و دشمنى در دلهاى ايشان بود بر كنيم از زير [كوشكهاى‌] آنان جويها روان است و گويند: سپاس و ستايش خداى راست كه ما را بدين [جايگاه و نعمتها] راه نمود و اگر خدا ما را راه ننمود بود راه نمى‌يافتيم. هر آينه فرستادگان پروردگارمان براستى و درستى آمدند. و ايشان را آواز دهند كه اين را به پاداش آنچه مى‌كرديد به شما ميراث داده‌اند.'),
(998, 7, 44, 'و بهشتيان، دوزخيان را آواز دهند كه همانا ما آنچه را پروردگارمان به ما وعده داد راست و درست يافتيم، آيا شما هم آنچه را پروردگارتان وعده داد راست و درست يافتيد؟ گويند: آرى آنگاه آوازدهنده‌اى در ميانشان آواز دهد كه لعنت خدا بر ستمكاران باد'),
(999, 7, 45, 'همانها كه [مردم را] از راه خدا باز مى‌دارند و آن را كج و ناراست مى‌جويند- بر آنند كه آن را كج جلوه دهند- و به جهان واپسين كافرند.'),
(1000, 7, 46, 'و ميان آن دو گروه- بهشتيان و دوزخيان- پرده‌اى است و بر اعراف- فراز آن پرده- مردانى هستند كه همه را به نشان چهره‌هاشان مى‌شناسند و بهشتيان را كه هنوز به بهشت نرفته‌اند ولى اميد مى‌دارند آواز دهند كه درود بر شما باد!'),
(1001, 7, 47, 'و چون ديدگانشان- ديدگان بهشتيانى كه هنوز به بهشت نرفته‌اند- سوى دوزخيان گردانده شود، گويند: پروردگارا، ما را با گروه ستمكاران قرار مده.'),
(1002, 7, 48, 'و آنان كه بر اعرافند، مردانى را كه به نشان چهره‌هاشان مى‌شناسند آواز دهند، گويند: فراهم‌آمدن شما- جمعيت شما يا گردآوردن اموالتان- و آن گردنكشى و بزرگ‌منشى كه مى‌نموديد هيچ به كارتان نيامد.'),
(1003, 7, 49, 'آيا اينان- بهشتيان- همان كسانند كه سوگند مى‌خورديد كه خدا رحمتى- بخشايش و نعمتى- به ايشان نرساند؟ [اى بهشتيان‌] به بهشت در آييد نه بيمى بر شماست و نه اندوه مى‌داريد.'),
(1004, 7, 50, 'و دوزخيان بهشتيان را آواز دهند كه از آب يا از آنچه خداوند روزيتان كرده بر ما فرو ريزيد. گويند: خداوند اين دو را بر كافران حرام كرده است'),
(1005, 7, 51, 'آنان كه دين خود را به سرگرمى و بازى گرفتند و زندگى دنيا آنان را بفريفت. پس امروز آنها را از ياد مى‌بريم- فرومى‌گذاريم- به سزاى آنكه آنها ديدار امروزشان را از ياد بردند و آيات ما را انكار مى‌كردند.'),
(1006, 7, 52, 'و هر آينه كتابى بديشان آورديم كه آن را از روى دانش به تفصيل بيان كرديم تا براى گروهى كه ايمان مى‌آورند راهنمايى و بخشايشى باشد.'),
(1007, 7, 53, 'آيا جز تأويل آن- تحقق و وقوع وعده‌هاى قرآن، از رستاخيز و حساب و جزا- را انتظار مى‌برند؟ آن روز كه تأويل آن فرا رسد آنها كه از پيش آن را فراموش كردند گويند: همانا فرستادگان پروردگارمان براستى و درستى آمدند آيا ما را شفيعانى هست كه از بهر ما شفاعت كنند، يا بازگردانده شويم و جز آن كنيم كه مى‌كرديم؟ براستى كه خويشتن را زيان كردند و آنچه به دروغ مى‌ساختند- يعنى بتها كه شفيعان خود مى‌پنداشتند- از آنها گم شد.'),
(1008, 7, 54, 'همانا پروردگارتان خدايى است كه آسمانها و زمين را در شش روز- شش دوره- بيافريد سپس بر عرش- در مقام استيلا و تدبير امور جهان- برآمد شب را به روز مى‌پوشاند كه شتابان در پى آن است، و خورشيد و ماه و ستارگان را [بيافريد] در حالى كه رام فرمان اويند آگاه باشيد كه آفريدن و فرمان ويژه اوست. بس بزرگ و بزرگوار- يا با بركت در آفرينش خويش- است خداى، پروردگار جهانيان.'),
(1009, 7, 55, 'پروردگارتان را به زارى و نهانى بخوانيد، [و از اندازه مگذريد] كه او از اندازه درگذرندگان را دوست ندارد.'),
(1010, 7, 56, 'و در زمين پس از به سامان آوردنش تباهى مكنيد، و او را با بيم و اميد بخوانيد، زيرا كه مهر و بخشايش خداوند به نيكوكاران نزديك است.'),
(1011, 7, 57, 'و اوست كه بادها را پيشاپيش بخشايش خود- يعنى باران- مژده‌دهنده مى‌فرستد، تا چون ابرهاى گرانبار را بردارند آن را به سرزمين مرده برانيم آنگاه آب را به آن سرزمين فروفرستيم، پس با آن آب از هر گونه ميوه‌اى بيرون آريم همچنين مردگان را بيرون آريم، شايد كه ياد كنيد و پند گيريد.'),
(1012, 7, 58, 'و سرزمين پاك گياهش به خواست پروردگارش، بيرون آيد- يعنى فراوان و پربار- و زمين ناپاك گياهش بيرون نيايد مگر اندكى. بدين سان آيات را براى گروهى كه سپاس مى‌دارند گونه‌گون بيان مى‌كنيم.'),
(1013, 7, 59, 'هر آينه نوح را به نزد قومش فرستاديم، گفت: اى قوم من، خداى را بپرستيد كه شما را جز او خدايى نيست همانا من از عذاب روزى بزرگ بر شما بيمناكم.'),
(1014, 7, 60, 'مهتران قومش گفتند: هر آينه تو را در گمراهى آشكار مى‌بينيم.'),
(1015, 7, 61, 'گفت: اى قوم من، مرا گمراهيى نيست، بلكه فرستاده‌اى از پروردگار جهانيانم'),
(1016, 7, 62, 'پيامهاى پروردگارم را به شما مى‌رسانم و شما را نيكخواهى مى‌كنم و از خدا چيزها مى‌دانم- در باره پاداش مطيعان و كيفر عاصيان- كه شما نمى دانيد.'),
(1017, 7, 63, 'آيا شگفتى نموديد از اينكه شما را از پروردگارتان بر [زبان‌] مردى از شما يادآورى و پندى آمده است تا شما را بيم دهد و تا پرهيزگارى كنيد و شايد در خور رحمت گرديد؟'),
(1018, 7, 64, 'پس او را دروغزن شمردند، پس او و كسانى را كه با او در كشتى بودند رهانيديم و كسانى را كه آيات- نشانه‌ها و سخنان- ما را دروغ پنداشتند غرق كرديم، زيرا گروهى كوردل بودند.'),
(1019, 7, 65, 'و به قوم عاد برادرشان هود را [فرستاديم‌] گفت: اى قوم من، خداى را بپرستيد كه شما را جز او خدايى نيست، آيا نمى‌پرهيزيد؟'),
(1020, 7, 66, 'مهتران قوم او كه كافر شده بودند گفتند: بى‌گمان تو را در بى‌خردى مى‌بينيم و همانا تو را از دروغزنان مى‌پنداريم.'),
(1021, 7, 67, 'گفت: اى قوم، مرا بى‌خرديى نيست، بلكه فرستاده‌اى از سوى پروردگار جهانيانم'),
(1022, 7, 68, 'پيامهاى پروردگارم را به شما مى‌رسانم و شما را نيكخواهى امينم.'),
(1023, 7, 69, 'آيا شگفتى نموديد از اينكه شما را از پروردگارتان بر [زبان‌] مردى از شما يادآورى و پندى آمده است تا شما را بيم دهد؟ و به ياد آريد آنگاه كه پس از قوم نوح شما را جانشين [آنها] كرد و شما را در آفرينش [تن، نيرومندى‌] افزود، پس نعمتهاى خدا را ياد كنيد تا شايد رستگار شويد.'),
(1024, 7, 70, 'گفتند: آيا سوى ما آمده‌اى تا خدا را به تنهايى بپرستيم و آنچه را پدرانمان مى‌پرستيدند واگذاريم؟ پس اگر از راستگويانى آنچه ما را وعده مى‌دهى- يعنى عذاب خدا- به ما آر.'),
(1025, 7, 71, 'گفت: بى‌گمان عذابى و خشمى از جانب خدايتان بر شما فروآمد. آيا در باره نامهايى- بى‌محتوا و بدون واقعيّت- كه شما و پدرانتان [بر بتان‌] نهاده‌ايد و خدا در باره آنها هيچ حجّتى فرو نفرستاده است با من ستيزه مى‌كنيد؟! پس در انتظار [عذاب‌] باشيد، من هم با شما در انتظارم.'),
(1026, 7, 72, 'پس او و آنها را كه با او بودند به مهر و بخشايش خويش رهانيديم و بنياد كسانى را كه آيات- نشانه‌ها و سخنان- ما را دروغ انگاشتند و مؤمن نبودند برانداختيم.'),
(1027, 7, 73, 'و به قوم ثمود برادرشان صالح را [فرستاديم‌]. گفت: اى قوم من، خداى را بپرستيد كه شما را جز او خدايى نيست. همانا شما را حجّتى روشن و هويدا از جانب پروردگارتان آمد، اين ماده‌شتر خداى، شما را آيتى است، پس او را واگذاريد تا در زمين خدا بخورد و هيچ بدى به او نرسانيد كه شما را عذابى دردناك فرا خواهد گرفت.'),
(1028, 7, 74, 'و ياد كنيد آنگاه كه شما را از پس [قوم‌] عاد جانشين [آنها] كرد و شما را در اين سرزمين جاى داد كه از جاهاى نرم و هموار آن كوشكها مى‌سازيد و كوه‌ها را براى ساختن خانه‌ها مى‌تراشيد پس نعمتهاى خدا را به ياد آريد و در زمين به تباهكارى مكوشيد.'),
(1029, 7, 75, 'مهتران قوم او كه گردنكشى مى‌كردند به ناتوان شمرده‌شدگان، به همانها كه ايمان آورده بودند، گفتند: آيا شما مى‌دانيد كه صالح فرستاده‌اى است از سوى پروردگار خويش؟ گفتند: ما بدانچه او بدان فرستاده شده ايمان داريم.'),
(1030, 7, 76, 'آنان كه گردنكشى كردند گفتند: ما بدانچه شما بدان ايمان آورده‌ايد كافريم.'),
(1031, 7, 77, 'پس ماده‌شتر را پى كردند- كشتند- و از فرمان پروردگارشان سر برتافتند و گفتند: اى صالح، اگر از فرستادگانى آنچه ما را وعده مى‌دهى به ما آر.'),
(1032, 7, 78, 'پس زمين لرزه- در اثر صاعقه آسمانى- آنان را بگرفت تا در خانه‌هاى خويش به رو درافتادند- و مُردند.'),
(1033, 7, 79, 'پس [صالح‌] از آنان روى گردانيد و گفت: اى قوم من، هر آينه پيام پروردگارم را به شما رساندم و براى شما نيكخواهى كردم و ليكن شما نيكخواهان را دوست نداريد.'),
(1034, 7, 80, 'و لوط را [ياد كن‌] آنگاه كه قوم خود را گفت: آيا كار زشت و پليد مى‌كنيد كه هيچ يك از مردم جهان پيش از شما نكرده است؟'),
(1035, 7, 81, 'هر آينه شما از روى شهوت به جاى زنان به مردان درمى‌آييد، بلكه شما گروهى گزافكاريد.'),
(1036, 7, 82, 'و پاسخ قومش جز اين نبود كه گفتند: اينها را از شهرتان بيرون كنيد، كه اينها مردمى پاكيزگى‌ورزند.'),
(1037, 7, 83, 'پس او و كسانش را رهانيديم مگر زنش را كه از ماندگان بود- و هلاك شد-.'),
(1038, 7, 84, 'و بر آنها بارانى- از سنگهاى آتشين- بارانيديم، پس بنگر كه سرانجام بزهكاران چگونه بود.'),
(1039, 7, 85, 'و به سوى [مردم‌] مَدين برادرشان شُعيب را [فرستاديم‌]. گفت: اى قوم من، خداى را بپرستيد كه شما را جز او خدايى نيست. هر آينه شما را از سوى پروردگارتان دليلى روشن و هويدا آمده است، پس پيمانه و ترازو را تمام دهيد و كالاهاى مردم را مكاهيد و در زمين پس از به سامان آوردنش تباهكارى مكنيد. اين براى شما بهتر است، اگر ايمان داشته باشيد'),
(1040, 7, 86, 'و به هر راهى منشينيد تا بترسانيد و باز داريد از راه خدا كسانى را كه به او ايمان آورده‌اند در حالى كه آن'),
(1041, 7, 87, 'و اگر گروهى از شما بدانچه به آن فرستاده شده‌ام ايمان آوردند و گروهى ايمان نياوردند، پس صبر كنيد تا خدا ميان ما داورى كند، و او بهترين داوران است.'),
(1042, 7, 88, 'مهتران قوم او كه گردنكشى كردند گفتند: اى شعيب، بى‌گمان تو و كسانى را كه با تو ايمان آورده‌اند از شهرمان بيرون مى‌كنيم يا به كيش ما باز گرديد. گفت: و گر چه ناخواه باشيم؟'),
(1043, 7, 89, 'اگر به كيش شما باز گرديم پس از آنكه خداوند ما را از آن رهايى داد، بى‌گمان بر خدا دروغ بسته باشيم و ما را نشايد كه به آن بازگرديم مگر آنكه خدا، پروردگار ما، بخواهد. دانش پروردگار ما هر چيزى را فرا گرفته است. بر خدا توكل كرديم. پروردگارا، ميان ما و قوم ما به حق داورى كن- يا: راهى بگشاى- كه تو بهترين داورانى- يا: راهگشايانى-.'),
(1044, 7, 90, 'و مهتران قوم او كه كافر شدند گفتند: اگر از شُعيب پيروى كنيد آنگاه شما هر آينه زيانكاريد.'),
(1045, 7, 91, 'پس زمين‌لرزه- در اثر صاعقه آسمانى- آنان را بگرفت تا در خانه‌هاى خويش به رو درافتادند [و مُردند].'),
(1046, 7, 92, 'كسانى كه شعيب را دروغگو شمردند گويى هرگز در آنجا نبودند. آنها كه شعيب را دروغگو شمردند همانها زيانكار بودند.'),
(1047, 7, 93, 'پس، از آنها روى گردانيد و گفت: اى قوم من، هر آينه پيامهاى پروردگارم را به شما رسانيدم و شما را نيكخواهى كردم پس چگونه بر گروهى كافر اندوه برم؟'),
(1048, 7, 94, 'و ما هيچ پيامبرى را در هيچ آبادى و شهرى نفرستاديم مگر آنكه مردم آن را به سختى- بينوايى- و گزند- بيمارى- گرفتار كرديم تا شايد زارى كنند.'),
(1049, 7, 95, 'سپس به جاى بدى- سختى و رنج- نيكى- فراخى و خوشى- آورديم تا فزونى يافتند و گفتند: پدران ما را نيز سختى و راحت رسيد، پس آنها را ناگهان در حالى كه آگاهى نداشتند- كه عذاب بر آنان فرو خواهد آمد-، بگرفتيم.'),
(1050, 7, 96, 'و اگر مردم آباديها ايمان آورده و پرهيزگارى كرده بودند هر آينه بركتها- نيكيها و نعمتها- از آسمان و زمين بر آنان مى‌گشاديم و ليكن [پيامها و نشانه‌هاى ما را] دروغ شمردند، پس آنها را به سزاى آنچه مى‌كردند گرفتار ساختيم.'),
(1051, 7, 97, 'پس آيا مردم آباديها ايمنند از اينكه شب هنگامى كه خفته‌اند عذاب ما به آنها برسد؟'),
(1052, 7, 98, 'و آيا مردم آباديها ايمنند از اينكه نيمروز هنگامى كه سرگرم بازى‌اند عذاب ما به آنها برسد؟'),
(1053, 7, 99, 'پس آيا از مكر- ناگاه گرفتن- خدا ايمنند؟ و از مكر خداى جز مردم زيانكار ايمن نمى‌نشينند.'),
(1054, 7, 100, 'و آيا [سرگذشتهاى پيشينيان‌] براى كسانى كه زمين را پس از [هلاك‌] مردمش به ارث مى‌برند ره ننمود [و روشن نساخت‌] كه اگر بخواهيم آنان را به گناهانشان گرفتار مى‌سازيم و بر دلهاشان مُهر مى‌نهيم پس [حق را] نمى‌شنوند؟'),
(1055, 7, 101, 'اين آباديها و شهرهاست كه از خبرهاى آنها بر تو مى‌خوانيم و هر آينه پيامبرانشان با نشانه‌هاى روشن و هويدا بديشان آمدند، اما بر آن نبودند كه به آنچه از پيش دروغ شمردند ايمان بياورند اينچنين خدا بر دلهاى كافران مُهر مى‌نهد.'),
(1056, 7, 102, 'و بيشترشان را پايبند هيچ عهد و پيمانى نيافتيم و هر آينه بيشترشان را بدكاران و بيرون‌شوندگان از فرمان يافتيم.'),
(1057, 7, 103, 'سپس از پس ايشان- پيامبران گذشته- موسى را با نشانه‌هاى خويش به سوى فرعون و مهتران [قوم‌] او فرستاديم، اما به آن نشانه‌ها ستم- انكار- كردند پس بنگر كه سرانجام تباهكاران چگونه بود.'),
(1058, 7, 104, 'و موسى گفت: اى فرعون، من فرستاده‌اى از پروردگار جهانيانم'),
(1059, 7, 105, 'سزاوارم كه بر خدا جز راست و درست سخن نگويم همانا شما را از پروردگارتان نشانه و دليلى روشن آورده‌ام، پس فرزندان اسرائيل را با من بفرست.'),
(1060, 7, 106, '[فرعون‌] گفت: اگر تو با نشانه‌اى آمده‌اى آن را بيار، اگر از راستگويانى.'),
(1061, 7, 107, 'پس عصاى خود را بيفكند كه ناگاه اژدهايى هويدا شد'),
(1062, 7, 108, 'و دست خويش [از گريبان‌] بيرون آورد، پس آن براى بينندگان سفيد و تابان مى‌نمود.'),
(1063, 7, 109, 'مهتران قوم فرعون گفتند: همانا اين جادوگرى داناست'),
(1064, 7, 110, 'مى‌خواهد شما را از سرزمينتان بيرون كند، اينك چه رأى مى‌دهيد؟'),
(1065, 7, 111, 'گفتند: [كار] او و برادرش را واپس دار و گردآوران را در شهرها فرست'),
(1066, 7, 112, 'تا هر جادوگر دانا را نزد تو آرند.'),
(1067, 7, 113, 'جادوگران نزد فرعون آمده گفتند: اگر ما پيروز شديم بى‌گمان ما را مزدى هست.'),
(1068, 7, 114, 'گفت: آرى، و هر آينه شما از نزديكان [درگاه من‌] خواهيد بود.'),
(1069, 7, 115, 'گفتند: اى موسى، تو مى‌افكنى يا ما بيفكنيم؟'),
(1070, 7, 116, 'گفت: شما بيفكنيد و چون بيفكندند، چشمهاى مردم را جادو كردند و آنان را سخت ترسانيدند و جادويى بزرگ آوردند.'),
(1071, 7, 117, 'و ما به موسى وحى كرديم كه عصايت را بيفكن ناگاه آن عصا آنچه را [به جادو و دروغ‌] ساخته بودند مى‌بلعيد.'),
(1072, 7, 118, 'پس حق پديدار شد و آنچه آنها مى‌كردند نابود گشت'),
(1073, 7, 119, 'و [فرعونيان‌] در آنجا شكست خوردند و خوار و زبون گشتند.'),
(1074, 7, 120, 'و جادوگران به سجده افكنده شدند.'),
(1075, 7, 121, 'گفتند: به خداوند جهانيان ايمان آورديم'),
(1076, 7, 122, 'خداوند موسى و هارون.'),
(1077, 7, 123, 'فرعون گفت: پيش از آنكه شما را رخصت دهم به او ايمان آورديد؟ اين ترفندى است كه در اين شهر انديشيده‌ايد تا مردمش را از آنجا بيرون كنيد پس بزودى خواهيد دانست.'),
(1078, 7, 124, 'هر آينه دستها و پاهاى شما را به خلاف يكديگر- يكى از راست و يكى از چپ- ببُرم، سپس همه شما را بر دار كشم.'),
(1079, 7, 125, 'گفتند: ما به پروردگار خويش باز مى‌گرديم'),
(1080, 7, 126, 'و تو هيچ خشم و كينه‌اى از ما به دل ندارى مگر اينكه به نشانه‌هاى پروردگارمان آنگاه كه ما را بيامد ايمان آورديم. پروردگارا، بر ما شكيبايى فرو ريز و ما را مسلمان- گردن نهاده و فرمانبردار- بميران.'),
(1081, 7, 127, 'و مهتران قوم فرعون گفتند: آيا موسى و قوم او را وا مى‌گذارى تا در اين سرزمين تباهى كنند و تو و خدايانت را فروگذارند؟ گفت: هر آينه پسرانشان را خواهيم كشت و زنانشان را زنده نگه خواهيم داشت و ما بر آنها چيره‌ايم.'),
(1082, 7, 128, 'موسى قوم خود را گفت: از خدا يارى بخواهيد و شكيبايى ورزيد، كه زمين از آن خداست، آن را به هر كس از بندگانش كه بخواهد به ميراث مى‌دهد، و سرانجام [نيك‌]- پيروزى يا بهشت- پرهيزگاران راست.'),
(1083, 7, 129, '[بنى اسرائيل‌] گفتند: پيش از آنكه نزد ما بيايى و پس از آنكه آمدى آزار و رنج ديده‌ايم. [موسى‌] گفت: اميد است كه پروردگارتان دشمن شما را نابود كند و شما را در اين سرزمين جانشين [آنها] سازد آنگاه بنگرد كه چگونه رفتار مى‌كنيد.'),
(1084, 7, 130, 'و هر آينه فرعونيان را به خشكسالها و كاهش ميوه‌ها- محصولات- گرفتار كرديم، شايد پند گيرند.'),
(1085, 7, 131, 'و چون نيكيى- نعمتى- بديشان مى‌رسيد، مى‌گفتند: اين از آن ماست- ما شايسته و سزاوار آنيم- و اگر بدى و گزندى به ايشان مى‌رسيد، به موسى و كسانى كه با او بودند شگون بد مى‌زدند آگاه باشيد كه همانا سرنوشت نيك و بدشان نزد خداست ولى بيشترشان نمى‌دانند.'),
(1086, 7, 132, 'و گفتند: [اى موسى،] هر نشانه‌اى- معجزه‌اى- كه به ما بيارى تا ما را بدان جادو كنى، تو را باور نخواهيم داشت.'),
(1087, 7, 133, 'پس بر آنها طوفان و ملخ و شپش و غوكان و خون، نشانه‌هاى جدا- و با فاصله- فرستاديم باز هم گردنكشى كردند و گروهى بزهكار بودند.'),
(1088, 7, 134, 'و چون عذاب بر آنان فروآمد، گفتند: اى موسى، خداى خويش را به آن پيمانى كه با تو دارد- كه چون او را بخوانى اجابت كند- بخوان، كه اگر اين عذاب را از ما بردارى براستى به تو ايمان مى‌آوريم و بى‌گمان فرزندان اسرائيل را با تو مى‌فرستيم.'),
(1089, 7, 135, 'پس چون عذاب را تا سرآمدى كه بايد بدان مى‌رسيدند از آنها برداشتيم باز هم پيمان‌شكنى مى‌كردند.'),
(1090, 7, 136, 'پس، از آنان كين ستانديم و آنها را در دريا غرق كرديم زيرا كه نشانه‌هاى ما را دروغ پنداشتند و از آن غافل بودند.'),
(1091, 7, 137, 'و به مردمى كه ناتوان و زبون شمرده مى‌شدند خاورها و باخترهاى آن سرزمين را كه در آنها بركت نهاديم به ميراث داديم و سخن- يعنى وعده- نيكوى پروردگار تو بر فرزندان اسرائيل به پاداش آن شكيبايى كه كردند انجام يافت. و آنچه را فرعون و قوم او مى‌ساختند- از بناها و كوشكها- و آنچه را بر مى‌افراشتند- از داربستها و سايه بانها- ويران كرديم.'),
(1092, 7, 138, 'و فرزندان اسرائيل را از دريا گذرانديم، پس بر گروهى در آمدند كه بر بتانى كه داشتند [به پرستش‌] گرد آمده و روى آورده بودند گفتند: اى موسى، براى ما خدايى قرار ده چنانكه آنها را خدايانى است. گفت: شما گروهى هستيد كه نادانى مى‌كنيد.'),
(1093, 7, 139, 'همانا آنچه اينان در آنند- كفر و شرك- نابود شونده است و آنچه مى‌كردند بيهوده و تباه است.'),
(1094, 7, 140, 'گفت: آيا بجز خداى يكتا براى شما خدايى ديگر بجويم و حال آنكه اوست كه شما را بر جهانيان- مردمان زمانه- برترى داد؟'),
(1095, 7, 141, 'و [به ياد آريد] آنگاه كه شما را از فرعونيان رهانيديم، كه شما را به خوارى سخت شكنجه مى‌كردند، پسرانتان را مى‌كشتند و زنانتان را زنده مى گذاشتند، و شما را در اين [گرفتارى‌] آزمونى بزرگ از پروردگارتان بود.'),
(1096, 7, 142, 'و با موسى سى شب وعده نهاديم و آن را به ده شب ديگر كامل كرديم پس هنگام وعده پروردگارش به چهل شب- شبانه روز- تمام شد. و موسى به برادر خود هارون گفت: در ميان قوم من جانشين من باش و به صلاح و سامان كوش و راه تباهكاران را پيروى مكن.'),
(1097, 7, 143, 'و چون موسى به وعده‌گاه ما آمد و پروردگارش با او سخن گفت- بى‌ميانجى-، گفت: پروردگارا [خود را] به من بنماى تا به تو بنگرم. گفت: هرگز مرا نخواهى ديد، و ليكن به اين كوه بنگر، پس اگر در جاى خود قرار و آرام داشت مرا خواهى ديد. و چون پروردگارش بر آن كوه تجلى كرد، آن را خرد و پراكنده ساخت و موسى مدهوش بيفتاد و چون به هوش باز آمد، گفت: [بارخدايا،] تو پاكى به تو بازگشتم و من نخستين باوردارنده‌ام.'),
(1098, 7, 144, '[خداى‌] گفت: اى موسى، من تو را با پيامهاى خويش و گفتارم- سخن گفتن بى ميانجى- بر مردمان برگزيدم پس آنچه به تو دادم فرا گير و از سپاسداران باش.'),
(1099, 7, 145, 'و براى او در آن لوح‌ها از هر گونه پندى و شرح هر چيزى نوشتيم. پس [او را گفتيم:] آن را با همه نيرو فرا گير و قوم خود را بفرماى تا بهترينش را فرا گيرند- كار بندند- زودا كه سراى نافرمانان بدكار را به شما بنمايم.'),
(1100, 7, 146, 'بزودى كسانى را كه در زمين به ناحق بزرگ منشى مى‌كنند از آيات خويش بگردانم، و اگر هر آيه‌اى ببينند بدان ايمان نياورند و اگر راه رُشد- رهيابى به راه راست و كمال- را ببينند آن را پيش نگيرند و اگر راه كژى و گمراهى را بينند آن را پيش گيرند. اين از آن روست كه آيات ما را دروغ انگاشتند و از آن غافل بودند.'),
(1101, 7, 147, 'و كسانى كه آيات ما و ديدار آن جهان را دروغ پنداشتند كارهايشان تباه و نابود شد. آيا جز آنچه مى‌كردند پاداش مى‌يابند؟'),
(1102, 7, 148, 'و قوم موسى از پس [رفتن‌] او [به طور] از زيورهاشان پيكر بى‌جان گوساله‌اى بر گرفتند- ساختند و پرستيدند- كه بانگى داشت. آيا نديدند كه آن [گوساله‌] با آنها سخن نمى‌گويد و بديشان راهى نمى‌نمايد؟ آن را [به خدايى‌] گرفتند در حالى كه ستمكار بودند- زيرا مى‌دانستند كه آن خدا نيست.'),
(1103, 7, 149, 'و چون [آن زيانكارى‌] در دستشان افتاد- به خود آمدند و پشيمان و شرمنده شدند- و ديدند- يعنى دانستند- كه همانا گمراه شده‌اند، گفتند: اگر پروردگارمان ما را نبخشايد و نيامرزد، هر آينه از زيانكاران باشيم.'),
(1104, 7, 150, 'و چون موسى با خشم و اندوه به قوم خود باز گشت، گفت: مرا پس از رفتنم بد جانشينى كرديد آيا از فرمان پروردگارتان شتاب كرديد؟- چرا به پرستش گوساله پيشى گرفتيد و صبر نكرديد تا باز آيم و فرمان پروردگار را برسانم؟- و لوحها را بيفكند و سر برادر خود را گرفته به سوى خويش مى‌كشيد. [برادرش‌] گفت: پسر مادرم؟ اين قوم مرا بيچاره و ناتوان شمردند و نزديك بود مرا بكشند. پس دشمنان را به سبب [سرزنش‌] من شادمان مگردان و مرا با گروه ستمكاران در شمار ميار.'),
(1105, 7, 151, 'گفت: پروردگارا، مرا و برادرم را بيامرز و ما را در مهر و بخشايش خود در آر، كه تو مهربان‌ترين مهربانانى.'),
(1106, 7, 152, 'همانا كسانى كه گوساله را [به خدايى‌] گرفتند بزودى خشمى از خدايشان و خواريى در زندگى دنيا به آنها رسد و دروغ پردازان را اين گونه كيفر مى‌دهيم.'),
(1107, 7, 153, 'و كسانى كه بديها كردند و پس از آن توبه نمودند و ايمان آوردند همانا پروردگار تو از آن پس آمرزگار و مهربان است.'),
(1108, 7, 154, 'و چون خشم موسى فرو نشست آن لوحها برگرفت و در نوشته آن براى كسانى كه از پروردگارشان بيم دارند راهنمايى و بخشايشى بود.'),
(1109, 7, 155, 'و موسى از قوم خود هفتاد مرد براى وعده‌گاه ما برگزيد، پس چون- پس از درخواست ديدن خداوند- زلزله آنان را بگرفت، گفت: پروردگارا، اگر مى خواستى آنها را پيش از اين هلاك مى‌كردى و مرا نيز آيا ما را بدانچه بى‌خردان ما كردند هلاك مى‌كنى؟ اين نيست مگر آزمون تو هر كه را بخواهى بدان گمراه مى‌كنى و هر كه را بخواهى راه مى‌نمايى تو سرپرست و ياور مايى، پس ما را بيامرز و بر ما ببخشاى و تو بهترين آمرزندگانى'),
(1110, 7, 156, 'و براى ما در اين جهان نيكويى بنويس و در آن جهان نيز، كه ما به تو بازگشتيم. [خداى‌] گفت: عذابم را به هر كه بخواهم مى‌رسانم، و مهر و بخشايشم همه چيز را فرا گرفته است، پس زودا كه آن را براى كسانى بنويسم كه پرهيزگارى مى‌كنند و زكات مى‌دهند و به آيات ما مى‌گروند'),
(1111, 7, 157, 'آنان كه پيروى مى‌كنند آن فرستاده پيامبر درس ناخوانده و خط نانوشته‌اى را كه [نام و نشانه‌هاى‌] او را نزد خويش در تورات و انجيل نوشته مى‌يابند آنان را به نيكى فرمان مى‌دهد و از بدى باز مى‌دارد و چيزهاى پاكيزه را برايشان حلال و پليديها را بر آنها حرام مى‌كند و بار گرانشان- تكاليف سخت و دشوار- و بند و زنجيرشان- امور دست و پاگير- را از آنها فرومى‌نهد- آزادشان مى‌كند پس كسانى كه به او گرويدند و او را بزرگ و گرامى داشتند و يارى‌اش كردند و از آن نورى كه با او فروآمده- قرآن- پيروى نمودند، ايشانند رستگاران.'),
(1112, 7, 158, 'بگو: اى مردم، من فرستاده خدا به همه شما هستم، آن [خدايى‌] كه او راست پادشاهى آسمانها و زمين خدايى جز او نيست، زنده مى‌كند و مى‌ميراند. پس به خدا و فرستاده او، آن پيامبر درس ناخوانده و خط نانوشته‌اى كه به خدا و سخنان او ايمان دارد، ايمان بياوريد و او را پيروى كنيد باشد كه راه يابيد.'),
(1113, 7, 159, 'و از قوم موسى گروهى‌اند كه [مردم را] به حق راه مى‌نمايند و بدان [وسيله‌] دادگرى مى‌كنند.'),
(1114, 7, 160, 'و آنان را به دوازده گروه- كه هر يك شاخه‌اى از نوادگان يعقوب بودند- بخش كرديم و به موسى هنگامى كه قوم وى از او آب خواستند وحى كرديم كه عصايت را بر سنگ بزن، پس دوازده چشمه از آن جوشيد، هر گروهى آبشخور خويش را بشناخت. و ابر را بر آنان سايبان ساختيم و بر آنان مَنّ و سَلوى- دو نوع خوراكى- فروفرستاديم، [و گفتيم:] از پاكيزه‌هاى آنچه روزيتان كرديم بخوريد. و بر ما ستم نكردند بلكه بر خويشتن ستم مى‌كردند.'),
(1115, 7, 161, 'و [ياد كن‌] آنگاه كه به آنان گفته شد در اين شهر- اريحا يا بيت المقدس- جاى گيريد و از نعمت آن هر جا كه خواهيد بخوريد و گوييد: [خدايا،] گناهان ما را فرونه و سجده‌كنان- به نشانه سپاسگزارى، يا سرها فروداشته به نشانه فروتنى- از دروازه در آييد تا گناهانتان را بيامرزيم. زودا كه نيكوكاران را [پاداش نيك‌] بيفزاييم.'),
(1116, 7, 162, 'اما كسانى از آنها كه ستم كردند سخنى جز آن كه به آنان گفته شده بود به جاى آن گفتند پس به سزاى آنكه ستم مى‌كردند عذابى از آسمان بر آنها فرستاديم.'),
(1117, 7, 163, 'و آنان را از [مردم‌] شهرى كه كنار دريا بود- ايله، از بلاد فلسطين ميان مَدين و طور كنار درياى طبريه- بپرس آنگاه كه در باره شنبه از اندازه درمى گذشتند- حكم روز شنبه را رعايت نمى‌كردند-، هنگامى كه ماهيانشان در روز شنبه آشكارا- بر روى آب- و به انبوهى نزد آنها مى‌آمدند و روزى كه شنبه نمى‌گرفتند به نزدشان نمى‌آمدند. اين گونه آنان را به سبب آنكه نافرمانى مى‌كردند مى‌آزموديم.'),
(1118, 7, 164, 'و آنگاه كه گروهى از آنان گفتند: چرا گروهى را پند مى‌دهيد كه خدا هلاك‌كننده آنهاست يا عذاب‌كننده آنها به عذابى سخت است؟ گفتند: تا عذرى باشد نزد پروردگارتان، و شايد كه پرهيزگارى كنند.'),
(1119, 7, 165, 'پس چون پندى را كه به آنها داده شده بود فراموش كردند، كسانى را كه از بدى- نافرمانى، يعنى شكار ماهى در روز شنبه- باز مى‌داشتند رهانيديم و كسانى را كه ستم كردند به سزاى نافرمانى كه مى‌كردند به عذابى سخت بگرفتيم.'),
(1120, 7, 166, 'و چون از [ترك‌] آنچه از آن باز داشته شدند سرپيچى كردند، به آنان گفتيم: بوزينگان و رانده‌شدگان باشيد.'),
(1121, 7, 167, 'و [ياد كن‌] آنگاه كه پروردگار تو اعلام نمود كه هر آينه تا روز رستاخيز [پيوسته‌] كسى را بر آنها برانگيزاند و بگمارد كه عذاب سخت را به آنها بچشاند. همانا پروردگار تو زود كيفر است، و هم او آمرزگار و مهربان است.'),
(1122, 7, 168, 'و آنها را در زمين گروه گروه پراكنده كرديم، از آنان برخى شايستگانند و برخى فروتر از آن- كه عوام يهودند-. و آنها را به نيكيها و بديها- خوشيها و ناخوشيها- بيازموديم شايد باز گردند.'),
(1123, 7, 169, 'پس، از پى ايشان- كه نيكان و دادگران بودند- آيندگانى [بدكار] جانشين شدند كه كتاب- تورات- را به ميراث بردند، كالاى ناپايدار اين جهان فروتر- دنيا- را مى‌ستانند و گويند بزودى ما را بيامرزند و اگر كالايى همانند آن بديشان رسد- و پيش آيد- آن را نيز مى‌گيرند آيا از آنان در آن كتاب، كه آنچه را در آن است خوانده‌اند، پيمان گرفته نشد كه بر خدا جز راست و درست نگويند؟ و سراى واپسين براى كسانى كه پرهيزگارى كنند بهتر است. آيا خرد را به كار نمى‌بنديد؟'),
(1124, 7, 170, 'و كسانى كه به كتاب [خدا] چنگ درزنند و نماز به پاى بدارند، براستى ما مزد شايسته‌كاران را تباه نمى‌كنيم.'),
(1125, 7, 171, 'و [ياد كن‌] آنگاه كه كوه را بركنديم و بالاى سرشان برديم كه گويى سايبانى است و پنداشتند كه بر سرشان افتادنى است [گفتيم:] آنچه را به شما داديم- يعنى كتاب- با همه نيرو بگيريد و آنچه را در آن است به ياد آريد شايد پرهيزگار شويد.'),
(1126, 7, 172, 'و [ياد كن‌] آنگاه كه پروردگار تو از فرزندان آدم، از پشتهاى ايشان، فرزندانشان را گرفت و آنان را بر خودشان گواه كرد [گفت:] آيا من پروردگار شما نيستم؟ گفتند: چرا، گواه شديم [و اين گواهى را گرفتيم‌] تا روز رستاخيز نگوييد كه ما از آن بى‌خبر بوديم.'),
(1127, 7, 173, 'يا نگوييد كه پدران ما از پيش شرك آوردند و ما فرزندانى از پس آنان بوديم آيا ما را به سزاى آنچه كجروان و تبه‌كاران- كافران- كردند هلاك مى كنى؟!'),
(1128, 7, 174, 'و اين گونه، آيات- سخنان خويش- را به تفصيل بيان مى‌كنيم [تا آيات ما را بفهمند] و شايد باز گردند.'),
(1129, 7, 175, 'و بر آنان خبر آن كس- بلعم باعورا- را برخوان كه آيتهاى خويش- برخى كرامات يا اجابت دعا- را به وى داديم و او از آنها بيرون رفت- خود را از دانش آن برهنه ساخت- پس شيطان در پى او افتاد تا از گمراهان گشت.'),
(1130, 7, 176, 'و اگر مى‌خواستيم هر آينه او را بدانها- آن آيتها- [به جايگاهى بلند] بر مى‌داشتيم، و ليكن او به زمين- دنيا و مال و جاه آن- چسبيد و كام و خواهش دل خود را پيروى كرد. پس داستان او چون داستان سگ است، اگر بر او بتازى زبان از دهان بيرون آرد يا واگذاريش باز زبان از دهان آويخته دارد. اين است داستان گروهى كه آيات ما را دروغ انگاشتند پس اين سرگذشت را [بر آنان‌] بازگو، شايد بينديشند.'),
(1131, 7, 177, 'بد نمونه‌اى هستند گروهى كه آيات ما را دروغ شمردند و به خويشتن ستم مى‌كردند.'),
(1132, 7, 178, 'هر كه را خداى راه نمايد او راه‌يافته است، و هر كه را گمراه كند آنانند زيانكاران.'),
(1133, 7, 179, 'و هر آينه بسيارى از پريان و آدميان را براى دوزخ بيافريديم- سرانجامشان به دوزخ مى‌رسد-، [زيرا] دلهايى دارند كه به آن [حق را] در نمى‌يابند و چشمهايى دارند كه به آن [حق را] نمى‌بينند، و گوشهايى دارند كه به آن [حق را] نمى شنوند. اينان بسان چارپايانند بلكه گمراه‌ترند همينانند غافلان.'),
(1134, 7, 180, 'و نيكوترين نامها خداى راست، پس او را بدانها بخوانيد و كسانى را كه در باره نامهاى او به كژى ميل مى‌كنند- يا به گفتگو و ستيزه مى‌پردازند- واگذاريد. بزودى [به سزاى‌] آنچه مى‌كردند كيفر داده شوند.'),
(1135, 7, 181, 'و از آنان كه بيافريديم گروهى هستند كه [مردم را] به حق راه مى‌نمايند و بدان [وسيله‌] به عدل و داد رفتار مى‌كنند.'),
(1136, 7, 182, 'و كسانى كه آيات ما را دروغ شمردند، اندك اندك از جايى كه ندانند [به كيفر خود] گرفتارشان خواهيم ساخت.'),
(1137, 7, 183, 'و آنان را مهلت مى‌دهم زيرا كه تدبير من استوار است.'),
(1138, 7, 184, 'آيا نينديشيدند كه در يارشان- رسول ما- هيچ گونه ديوانگى نيست؟ او جز بيم‌دهنده‌اى آشكارا نيست.'),
(1139, 7, 185, 'آيا در ملكوت- پادشاهى و فرمانروايى- آسمانها و زمين و هر چه خداى آفريده است نمى‌نگرند، و اينكه شايد سرآمد- مرگ- ايشان نزديك شده باشد؟ پس به كدام سخن پس از اين- قرآن- ايمان خواهند آورد؟'),
(1140, 7, 186, 'كسى را كه خدا گمراه كند او را راه نمايى نيست و آنان را واميگذارد تا در سركشى خويش سرگشته و كوردل بمانند.'),
(1141, 7, 187, 'تو را از رستاخيز پرسند، كه فرارسيدن آن كى باشد؟ بگو: دانش آن تنها نزد پروردگار من است جز او آن را به هنگامش آشكار نكند. [فرارسيدن آن‌] در آسمانها و زمين گران است، فرا نرسد شما را مگر ناگاه. از تو چنان مى‌پرسند كه گويا از آن آگاهى. بگو: دانش آن تنها نزد خداست، ولى بيشتر مردم نمى‌دانند.'),
(1142, 7, 188, 'بگو: من مالك هيچ سود و زيانى براى خويش نيستم- من براى خود جلب سود و دفع زيان نتوانم- مگر آنچه خداى خواهد، و اگر غيب مى‌دانستم هر آينه نيكى و خواسته بسيار گرد مى‌كردم، و هيچ بدى و گزندى به من نمى‌رسيد. من جز بيم‌دهنده و نويدرسانى براى مردمى كه ايمان بياورند نيستم.'),
(1143, 7, 189, 'اوست كه شما را از يك تن آفريد و همسر او را از [نوع‌] او پديد كرد تا بدو آرام گيرد. و چون بر او در آمد بار گرفت بارى سبك، پس با آن [مدتى‌] گذرانيد. و چون گرانبار گشت، آن دو پروردگار خويش را خواندند كه اگر ما را فرزندى نيك و شايسته- تندرست- دهى بى‌گمان از سپاسگزاران خواهيم بود.'),
(1144, 7, 190, 'پس چون آن دو را فرزندى نيك و شايسته- تندرست- داد در آنچه ايشان را بداد براى او شريكانى قرار دادند همانا خدا از آنچه [با او] شريك مى‌سازند برتر است.'),
(1145, 7, 191, 'آيا چيزهايى را [با خدا] شريك مى‌سازند كه هيچ چيزى را نمى‌آفرينند و خود آفريده مى‌شوند؟!'),
(1146, 7, 192, 'و نه مى‌توانند آنان را يارى دهند و نه خودشان را يارى توانند كرد'),
(1147, 7, 193, 'و اگر [شما مشركان‌] آنها- بُتان- را به راه راست بخوانيد شما را پيروى نكنند. بر شما يكسان است كه آنها را بخوانيد يا خاموش باشيد'),
(1148, 7, 194, 'همانا كسانى را كه به جاى خداى يكتا مى‌خوانيد بندگانى- آفريدگانى- مانند شمايند. پس آنها را بخوانيد كه اگر راستگوييد بايد شما را پاسخ دهند.'),
(1149, 7, 195, 'آيا آنها را پاهايى هست كه با آن بروند، يا آنها را دستهايى هست كه با آن بگيرند- داد و ستد و حمله و دفاع كنند-، يا آنها را چشمهايى هست كه با آن ببينند، يا آنها را گوشهايى هست كه با آن بشنوند. بگو: انبازان خود را- كه براى خدا ساخته‌ايد- بخوانيد آنگاه در باره من هر ترفند و نيرنگى كه توانيد به كار بريد و مرا مهلت مدهيد'),
(1150, 7, 196, 'همانا دوست و سرپرست من خداى يكتاست كه [اين‌] كتاب را فروفرستاد و او نيكان و شايستگان را سرپرستى و كارسازى مى‌كند.'),
(1151, 7, 197, 'و آنان كه به جاى او مى‌خوانيد- بُتها- نه مى‌توانند شما را يارى كنند و نه خود را يارى مى‌كنند'),
(1152, 7, 198, 'و اگر آنها را به راه راست بخوانيد نمى‌شنوند، و مى‌بينى كه به تو مى‌نگرند و حال آنكه نمى‌بينند.'),
(1153, 7, 199, 'عفو را پيشه كن- كار را بر مردم آسان گير و گذشت و ميانه‌روى را پيشه ساز- و به كار نيك و پسنديده فرمان ده و از نادانان و نابخردان روى بگردان.'),
(1154, 7, 200, 'و اگر وسوسه‌اى از شيطان تو را برانگيزاند به خداى پناه بر، كه او شنوا و داناست.'),
(1155, 7, 201, 'كسانى كه پرهيزگارى كردند چون وسوسه‌اى از شيطان به آنان رسد [خداى را] ياد كنند، پس آنگاه بينايان باشند.'),
(1156, 7, 202, 'و برادرانشان- برادران كافران و مشركان يعنى شياطين- آنان را در گمراهى مى‌كشند و هيچ كوتاهى نمى‌كنند.'),
(1157, 7, 203, 'و چون آيتى بديشان نياورى، گويند: چرا آيتى فراهم نساختى بگو: فقط چيزى را پيروى مى‌كنم كه از سوى پروردگارم به من وحى مى‌شود. اين [قرآن‌] بينش‌ها و حجتهاى روشن از پروردگار شماست و رهنمونى و بخشايشى است براى مردمى كه ايمان دارند.'),
(1158, 7, 204, 'و چون قرآن خوانند بدان گوش فرا داريد و خاموش باشيد شايد در خور رحمت شويد.'),
(1159, 7, 205, 'و پروردگارت را در درون خويش به زارى و بيم و بدون آواى بلند گفتار، بامدادان و شبانگاهان ياد كن و از غافلان مباش.'),
(1160, 7, 206, 'همانا آنان كه نزد پروردگار تواند- فرشتگان و مقرّبان- از پرستش او سرپيچى و گردنكشى نكنند و وى را به پاكى مى‌ستايند و او را سجده مى‌آرند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'تو را از انفال- غنيمتهاى جنگى- مى‌پرسند، بگو: آن غنيمتها از آن خدا و پيامبر است. پس، از خدا پروا داشته باشيد و ميان خود آشتى و سازش كنيد، و خداى و پيامبر او را فرمان بريد اگر ايمان داريد.'),
(1162, 8, 2, 'جز اين نيست كه مؤمنان كسانى‌اند كه چون خدا ياد شود دلهاشان ترسان گردد- از عظمت يا عقوبت او- و چون آيات او بر آنان خوانده شود بر ايمانشان بيفزايد و بر پروردگارشان توكّل كنند'),
(1163, 8, 3, 'آنان كه نماز را برپا مى‌دارند و از آنچه روزيشان كرده‌ايم انفاق مى‌كنند.'),
(1164, 8, 4, 'اينانند كه براستى مؤمن‌اند ايشان را نزد پروردگارشان پايه‌هاست و آمرزشى و روزى نيكو و بزرگوارانه- در بهشت-.'),
(1165, 8, 5, 'چنانكه پروردگارت تو را از خانه‌ات بحق بيرون آورد- براى جنگ بدر- و همانا گروهى از مؤمنان ناخشنود بودند.'),
(1166, 8, 6, 'با تو در باره آن حق- جهاد- پس از آنكه روشن و هويدا شد ستيزه مى‌كنند گويى آنها را به سوى مرگ مى‌رانند و خود مى‌نگرند.'),
(1167, 8, 7, 'و [ياد كنيد] آنگاه كه خدا يكى از آن دو گروه- كاروان بازرگانى و سپاه كافران- را به شما وعده مى‌داد كه از آن شما خواهد بود، و شما دوست مى داشتيد كه گروه بى‌خار و بى‌سلاح- كاروان بازرگانى- شما را باشد و حال آنكه خدا مى‌خواست كه حق را با سخنان خود- نهادها و سنتهايى كه براى يارى كردن پيامبران و پيروان حق قرار داده- استوار و پايدار كند و بنياد كافران را براندازد.'),
(1168, 8, 8, 'تا حق- اسلام- را پايدار و باطل را نيست گرداند، هر چند بزهكاران خوش ندارند.'),
(1169, 8, 9, 'آنگاه كه از پروردگارتان فريادرسى [و يارى‌] مى‌خواستيد، پس شما را پاسخ داد كه من شما را به هزارى از فرشتگان كه از پى يكديگر در آيند يارى‌كننده‌ام.'),
(1170, 8, 10, 'و خداوند آن را پديد نكرد مگر براى مژدگانى و تا دلهاتان بدان آرام گيرد و يارى نيست مگر از نزد خدا، كه خدا تواناى بى‌همتا و داناى با حكمت است.'),
(1171, 8, 11, 'آنگاه كه از سوى خود براى ايمنى و آرامش خوابى سبك بر شما مى‌پوشانيد و از آسمان آبى فرو مى‌آورد تا شما را بدان پاك كند و پليدى وسوسه شيطان را از شما ببرد و تا بر دلهاتان [شكيبايى و پايدارى‌] بربندد و گامها را بدان استوار بدارد.'),
(1172, 8, 12, 'آنگاه كه پروردگارت به فرشتگان وحى كرد كه من با شمايم، پس آنان را كه ايمان آورده‌اند استوار سازيد، زودا كه در دلهاى آنان كه كافر شدند بيم افكنم، پس فراز گردنها- سرهاشان- بزنيد و از آنها هر انگشتى را بزنيد- تا نتوانند سلاح برگيرند-'),
(1173, 8, 13, 'اين از آن روست كه با خدا و فرستاده او مخالفت كردند و هر كه با خدا و فرستاده او مخالفت كند پس [بداند كه‌] خدا سخت كيفر است.'),
(1174, 8, 14, 'اين است [كيفر ما] پس آن را بچشيد، و همانا كافران را عذاب آتش است.'),
(1175, 8, 15, 'اى كسانى كه ايمان آورده‌ايد، چون [در ميدان جنگ‌] با انبوه حمله‌ور كافران روبرو شديد، پس پشتها را به آنها مگردانيد- از آنها مگريزيد-.'),
(1176, 8, 16, 'و هر كه در آن روز- روز جنگ- پشت خويش به آنها بگرداند- مگر براى آنكه در كارزار كامگارتر باشد به سويى ديگر شود يا در گروهى [ديگر از سپاه مسلمانان‌] جاى گيرد- پس به خشمى از خدا بازگشته- گرفتار خشم خدا شده- و جاى او دوزخ است، و بد بازگشتگاهى است.'),
(1177, 8, 17, 'پس شما آنها را نكشتيد- به نيروى خود- بلكه خدا آنان را كشت- به امدادهاى غيبى- و تو [تير يا خاك‌] نيفكندى آنگاه كه افكندى بلكه خدا افكند [تا كافران را مرعوب و مغلوب گرداند] و تا مؤمنان را به آزمونى نيكو از جانب خويش بيازمايد- يا نعمتى نيكو كه همان پيروزى است به آنان ارزانى دارد- كه خدا شنوا و داناست.'),
(1178, 8, 18, 'اين است شما را [يارى و پيروزى‌]، و خدا سست‌كننده نيرنگ كافران است.'),
(1179, 8, 19, '[اى مشركان،] اگر فتح و پيروزى خواهيد، [على رغم خواست شما] اينك فتح و پيروزى- كه از آن مؤمنان است- شما را آمد و اگر [از كفر و دشمنى با پيامبر] باز ايستيد پس آن شما را بهتر است، و اگر [به جنگ با مسلمانان‌] بازگرديد ما نيز بازگرديم و هرگز گروه شما هر چند بسيار باشد برايتان سودى نخواهد داشت و همانا خدا با مؤمنان است.'),
(1180, 8, 20, 'اى كسانى كه ايمان آورده‌ايد، خدا و پيامبر او را فرمان بريد و از او روى مگردانيد در حالى كه [فرمان او را] مى‌شنويد.'),
(1181, 8, 21, 'و مانند كسانى مباشيد كه گفتند شنيديم و حال آنكه نمى‌شنوند.'),
(1182, 8, 22, 'همانا بدترين جنبندگان به نزد خداوند، كران و گنگانند- از شنيدن و گفتن حق- كه خرد را كار نمى‌بندند.'),
(1183, 8, 23, 'و اگر خداى در آنان خيرى مى‌شناخت، شنواشان مى‌ساخت و اگر [در حالى كه دلهاشان شنوايى ندارد] شنواشان مى‌ساخت هر آينه رويگردان شده بر مى‌گشتند.'),
(1184, 8, 24, 'اى كسانى كه ايمان آورده‌ايد، خداى و پيامبر را پاسخ دهيد هنگامى كه شما را به چيزى خوانند كه زنده‌تان مى‌سازد- سبب زنده‌دلى شماست- و بدانيد كه خدا ميان مرد و دل او حائل مى‌شود و [بدانيد كه‌] به سوى او برانگيخته و فراهم مى‌شويد.'),
(1185, 8, 25, 'و از فتنه‌اى بپرهيزيد كه تنها به كسانى از شما كه ستم كردند نرسد، و بدانيد كه خدا سخت كيفر است.'),
(1186, 8, 26, 'و به ياد آريد هنگامى را كه در زمين گروهى اندك و ناتوان شمرده شده بوديد، بيم آن داشتيد كه مردمان- مشركان مكه و قبيله‌هاى عرب- شما را بربايند، پس شما را جاى داد- در مدينه- و به يارى خود نيرومند گردانيد و از چيزهاى پاكيزه روزيتان داد تا مگر سپاس داريد.'),
(1187, 8, 27, 'اى كسانى كه ايمان آورده‌ايد، به خداى و پيامبر خيانت مكنيد و در امانتهاى خود خيانت مورزيد در حالى كه مى‌دانيد [كه خيانت مى‌كنيد].'),
(1188, 8, 28, 'و بدانيد كه همانا مالها و فرزندانتان آزمونى‌اند- پس دوستى مال و فرزندان شما را به گناه واندارد- و بدانيد كه پاداشى بزرگ نزد خداوند است.'),
(1189, 8, 29, 'اى كسانى كه ايمان آورده‌ايد، اگر از خدا پروا داشته باشيد براى شما مايه تميز [ميان حق و باطل‌] قرار مى‌دهد و بديهاتان را از شما مى‌زدايد و شما را مى‌آمرزد و خدا، خداوند فزونى و بخشش بزرگ است.'),
(1190, 8, 30, 'و [ياد كن‌] آنگاه كه كسانى كه كافر شدند بدانديشى مى‌كردند- در دار الندوة- كه تو را به بند كشند يا بكشند يا بيرون كنند، آنها بدانديشى مى‌كردند و خدا چاره نهان مى‌كرد- مكرشان را بديشان برمى‌گرداند- و خدا بهترين چاره‌كنندگان است.'),
(1191, 8, 31, 'و چون آيات ما بر آنها خوانده شود گويند: شنيديم، اگر بخواهيم ما هم مانند اين- قرآن- را خواهيم گفت، اين نيست مگر افسانه‌هاى پيشينيان.'),
(1192, 8, 32, 'و [ياد كن‌] آنگاه كه گفتند: خدايا، اگر اين- قرآن- راست و درست است از نزد تو، پس بر ما سنگهايى از آسمان ببار، يا ما را عذابى دردناك بيار.'),
(1193, 8, 33, 'و خدا بر آن نيست كه آنان را عذاب كند در حالى كه تو در ميان ايشانى، و خدا عذاب‌كننده آنان نيست در حالى كه آمرزش مى‌خواهند.'),
(1194, 8, 34, 'و چيست آنان را- چه جاى آن است- كه خدا عذابشان نكند و آنهايند كه [مردم را] از مسجد الحرام باز مى‌دارند، و حال آنكه سرپرستان- سزاوار توليت- آن نيستند سرپرستان آن جز پرهيزگاران نباشند و ليكن بيشترشان نمى‌دانند'),
(1195, 8, 35, 'و نماز و دعاى آنان به نزد آن خانه جز سوت‌كشيدن و دست‌زدن نيست، پس عذاب را به سزاى آنكه كفر مى‌ورزيديد بچشيد.'),
(1196, 8, 36, 'آنان كه كافر شدند مالهاى خود را هزينه مى‌كنند تا [مردم را] از راه خدا بازدارند و بگردانند. زودا كه آن را هزينه كنند آنگاه بر آنها مايه پشيمانى و حسرت شود و سپس شكست خورند و كسانى كه كافر شدند به سوى دوزخ برانگيخته و فراهم شوند.'),
(1197, 8, 37, 'تا خدا پليد را از پاك جدا سازد و پليد را برخى بر برخى نهد پس همه را توده كند و در دوزخ افكند اينانند زيانكاران.'),
(1198, 8, 38, 'به كسانى كه كافر شدند بگو: اگر باز ايستند، آنان را آنچه گذشته بخشوده و آمرزيده مى‌شود و اگر [به روش خود و جنگ و فتنه‌انگيزى‌] بازگردند پس همان روش و نهاد است كه بر پيشينيان گذشت.'),
(1199, 8, 39, 'و با آنان كارزار كنيد تا آنگاه كه فتنه‌اى- كفر و شرك و فتنه‌انگيزى- نماند و دين يكسره خداى را باشد. پس اگر بازايستند همانا خدا به آنچه مى‌كنند بيناست.'),
(1200, 8, 40, 'و اگر روى بگردانند بدانيد كه خدا سرپرست و ياور شماست، نيكو سرپرست و نيكو ياورى است.'),
(1201, 8, 41, 'و بدانيد كه هر چه به چنگ آريد، پس پنج يك آن براى خداى و پيامبر و خويشان [پيامبر] و يتيمان و تهيدستان و در راه‌ماندگان [از خاندان پيامبر] است، اگر به خداى و آنچه [از قرآن‌] بر بنده خود روز جداشدن [حق از باطل‌]- روز بدر-، روزى كه آن دو گروه- كافران و مؤمنان- به هم رسيدند، فروفرستاديم ايمان آورده‌ايد و خدا بر هر چيزى تواناست.'),
(1202, 8, 42, 'آنگاه كه شما به كناره نزديكتر- كناره خشك رود كه به مدينه نزديكتر است- بوديد و آنها- ابو جهل و سپاه او- به كناره دورتر- از مدينه- بودند و آن كاروان- شترسواران ابو سفيان- از شما فروتر بود- نزديكتر به جانب دريا-. و اگر با آنها وعده [مكان و زمان جنگ‌] مى‌نهاديد، در وعده‌گاه با يكديگر اختلاف مى‌كرديد- و اين گونه با هم روبرو نمى‌شديد- و ليكن [چنان شد] تا خدا كارى را كه كردنى بود به انجام رساند، تا آن كه [به گمراهى‌] هلاك مى‌شود به حجتى روشن هلاك شود و آن كه [به هدايت‌] زنده مى‌ماند به حجتى روشن زنده بماند و هر آينه خدا شنوا و داناست.'),
(1203, 8, 43, '[ياد كن‌] آنگاه كه خدا آنها را در خوابت به تو اندك نمود، و اگر آنها را بسيار مى‌نمود سست و بددل مى‌شديد و در كار [جنگ‌] با يكديگر ستيزه مى‌كرديد و ليكن خدا [شما را از اين سستى و ستيزه‌] به سلامت داشت همانا او به آنچه در دلهاست داناست.'),
(1204, 8, 44, 'و آنگاه كه آنان را چون با هم روبرو شديد در چشمهاى شما اندك و شما را در چشمهاى آنان اندك نمود تا خدا كارى را كه كردنى بود به انجام رساند و كارها به خدا بازگردانده مى‌شود.'),
(1205, 8, 45, 'اى كسانى كه ايمان آورده‌ايد، هر گاه به گروهى [از دشمن‌] برخورديد، پايدارى نماييد و خدا را بسيار ياد كنيد باشد كه پيروز و رستگار شويد.'),
(1206, 8, 46, 'و خدا و پيامبرش را فرمان بريد و با يكديگر ستيزه و كشمكش مكنيد كه سست و بددل شويد و نيرو و دولتتان برود، و شكيبايى كنيد، كه خدا با شكيبايان است.'),
(1207, 8, 47, 'و مانند آن كسان- كفار قريش- مباشيد كه از خانه‌هاى خويش با سرمستى از نعمت و خودنمايى به مردم بيرون آمدند و [مردم را] از راه خدا بازمى‌دارند و مى گردانند و خدا بدانچه مى‌كنند [دانايى‌] فراگيرنده است.'),
(1208, 8, 48, 'و آنگاه كه شيطان كارهاى آنها را برايشان بياراست و گفت: امروز از مردمان بر شما چيره‌شونده‌اى نيست و من پناه شمايم. و چون آن دو گروه روياروى شدند بر دو پاشنه خود گرديد- پشت كرد و گريخت- و گفت: من از شما بيزارم، من چيزى- فرشتگان يارى‌كننده مؤمنان- را مى‌بينم كه شما نمى‌بينيد، من از خداى مى‌ترسم، و خدا سخت كيفر است.'),
(1209, 8, 49, 'آنگاه كه منافقان و كسانى كه در دلهاشان بيمارى است گفتند: اينان را دينشان فريفته است. و هر كس بر خدا توكل كند همانا خدا تواناى بى‌همتا و داناى استواركار است- از اين‌رو متوكلان را يارى مى‌كند و تنها نمى‌گذارد-.'),
(1210, 8, 50, 'و اگر مى‌ديدى آنگاه كه فرشتگان جان كافران را برگيرند! بر روى و پشتشان مى‌زنند و [گويند:] عذاب سوزان را بچشيد'),
(1211, 8, 51, 'اين به سزاى كارهايى است كه دستهاى شما پيش فرستاده- خودتان كرده‌ايد- و هرگز خدا بر بندگان ستمگر نيست.'),
(1212, 8, 52, 'همانند عادت و روش فرعونيان و كسانى كه پيش از آنان بودند كه به آيات خدا كافر شدند، پس خدا آنها را به سزاى گناهانشان بگرفت، زيرا كه خدا نيرومند و سخت كيفر است.'),
(1213, 8, 53, 'اين [كيفر] از آن روست كه خدا هرگز بر آن نبوده است كه نعمتى را كه بر مردمى ارزانى داشته دگرگون كند تا آنكه آنها آنچه را كه در خودشان است دگرگون سازند- حال خود را به كفر و ناسپاسى و ستمگرى بگردانند-، و خدا شنوا و داناست.'),
(1214, 8, 54, 'همانند عادت و روش فرعونيان و كسانى كه پيش از آنها بودند كه نشانه‌هاى پروردگارشان را دروغ شمردند پس آنان را به سزاى گناهانشان هلاك كرديم و فرعونيان را غرق كرديم و همگى- فرعونيان و كفار قريش- ستمكار بودند.'),
(1215, 8, 55, 'همانا بدترين جنبندگان به نزد خدا كسانى‌اند كه كافر شدند، پس آنها ايمان نخواهند آورد'),
(1216, 8, 56, 'كسانى از آنها- كافران- كه با آنان پيمان بسته‌اى سپس پيمان خود را هر بار مى‌شكنند و هيچ پروا نمى‌كنند.'),
(1217, 8, 57, 'پس اگر در جنگ به آنان دست يابى، با سختگيرى به آنها آنان را كه پشت سر ايشانند پراكنده ساز شايد ياد كنند و پند گيرند.'),
(1218, 8, 58, 'و اگر از گروهى بيمِ خيانت- پيمان شكنى- داشتى [پيمانشان را] برابر [عملكرد خودشان‌] به نزدشان افكن- اعلام نقض پيمان كن-، زيرا خدا خائنان را دوست ندارد.'),
(1219, 8, 59, 'و كسانى كه كافر شدند نپندارند كه پيشى گرفتند [و از عذاب ما جستند]، زيرا كه آنها نمى‌توانند [ما را] عاجز كنند.'),
(1220, 8, 60, 'و براى [كارزار با] آنها آنچه مى‌توانيد نيرو و اسبان بسته [و زين كرده‌] آماده سازيد تا بدين وسيله دشمن خدا و دشمن خودتان و ديگرانى جز اينان را كه شما نمى‌شناسيد خدا آنها را مى‌شناسد بترسانيد و آنچه در راه خدا انفاق كنيد بتمامى به شما باز دهند و بر شما هيچ ستمى نخواهد شد.'),
(1221, 8, 61, 'و اگر به صلح و آشتى گراييدند- تمايل نشان دادند- تو نيز به آن بگراى و بر خدا توكل كن، كه اوست شنوا و دانا.'),
(1222, 8, 62, 'و اگر بخواهند تو را بفريبند، خداوند تو را بسنده است، اوست كه تو را به يارى خود و به مؤمنان نيرومند كرد'),
(1223, 8, 63, 'و در ميان دلهاشان الفت و پيوند داد اگر همه آنچه در زمين است هزينه مى‌كردى نمى‌توانستى ميان دلهاشان الفت پديد كنى و ليكن خداوند ميان آنها الفت داد، كه او تواناى بى‌همتا و داناى با حكمت است.'),
(1224, 8, 64, 'اى پيامبر، خدا و مؤمنانى كه از تو پيروى كرده‌اند تو را بسنده است.'),
(1225, 8, 65, 'اى پيامبر، مؤمنان را بر كارزار برانگيز اگر از شما بيست تن شكيبا باشند- كه در جنگ پايدارى كنند- بر دويست تن [از كافران‌] چيره شوند و اگر از شما صد تن باشند بر هزار تن از آنها كه كافر شدند چيره شوند زيرا كه آنها گروهى نافهم و نادانند- ايمان و انگيزه‌اى ندارند و از اينرو در كارزار شكيبايى و پايدارى نمى‌كنند.'),
(1226, 8, 66, 'اكنون- پس از روز بَدر- خدا بار شما را سبك كرد و دانست كه در شما سستى و ناتوانى هست پس اگر از شما صد تن شكيبا باشند بر دويست تن چيره شوند و اگر از شما هزار تن باشند بر دو هزار تن چيره شوند- به خواست خدا-، و خدا با شكيبايان است.'),
(1227, 8, 67, 'هيچ پيامبرى را نسزد كه او را بنديان- اسيران جنگى براى فديه‌گرفتن يا فروختن- باشد تا آنگاه كه با كشتار [و خستن كافران در ميدان جنگ‌] در زمين چيره و پيروز شود- يعنى روانيست كه پيش از شكستن و سركوب‌كردن دشمن، نيروى رزمندگان اسلام صرف گرفتن اسير شود- شما كالاى اين جهان مى‌خواهيد و خدا [براى شما پاداش و نعمت جاويد] آن جهان را مى‌خواهد و خدا تواناى بى‌همتا و داناى با حكمت است.'),
(1228, 8, 68, 'اگر نوشته و فرمانى از خدا پيشى نگرفته بود- كه مجاهدان را گرفتن غنيمت حلال و مباح خواهد شد-، در آنچه [از آن اسيران‌] ستانديد شما را عذابى بزرگ مى‌رسيد.'),
(1229, 8, 69, 'اينك از آنچه غنيمت گرفتيد حلال و پاكيزه بخوريد و ترس از خدا را پيشه كنيد، كه خدا آمرزگار و مهربان است.'),
(1230, 8, 70, 'اى پيامبر، به بنديانى كه در دست شمايند بگو: اگر خداوند در دلهاى شما نيكيى بداند به شما بهتر از آنچه از شما ستاندند بدهد و شما را بيامرزد، و خدا آمرزگار و مهربان است.'),
(1231, 8, 71, 'و اگر بخواهند به تو خيانت كنند همانا پيش از اين به خدا خيانت كردند، پس اينك [تو را] بر آنها توانايى و دستيابى داد، و خدا دانا و استواركار است.'),
(1232, 8, 72, 'همانا كسانى كه ايمان آوردند و هجرت كردند و با مالها و جانهاى خويش در راه خدا جهاد كردند و كسانى كه [مهاجران را] جاى و پناه دادند و [پيامبر را] يارى كردند، آنان دوستان و پيوندداران يكديگرند، و كسانى كه ايمان آوردند و هجرت نكردند شما را از دوستى و پيوند با آنان هيچ نيست تا هجرت كنند و اگر از شما در [كار] دين يارى بخواهند بر شماست يارى‌كردن آنها، مگر بر ضد گروهى كه ميان شما و آنها پيمانى باشد، و خداوند بدانچه مى‌كنيد بيناست.'),
(1233, 8, 73, 'و كسانى كه كافر شدند دوستان و پيوندداران يكديگرند اگر آن كار را نكنيد- يعنى اگر آن يارى را نكنيد و بستگى و پيوندتان با كافران باشد نه با مؤمنان- فتنه و تباهى بزرگى در زمين روى خواهد نمود.'),
(1234, 8, 74, 'و كسانى كه ايمان آوردند و هجرت كردند و در راه خدا جهاد كردند و آنان كه جاى و پناه دادند و يارى كردند، اينانند كه براستى مؤمن‌اند ايشان راست آمرزش و روزى بزرگوارانه.'),
(1235, 8, 75, 'و كسانى كه پس از آن- يعنى پس از ايمان آوردن و هجرت‌كردن شما- ايمان آوردند و هجرت كردند و همراه شما جهاد كردند، اينان از شمايند. و خويشاوندان در كتاب خدا [در باره ميراث‌] به يكديگر سزاوارترند همانا خدا به همه چيز داناست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'اين [اعلام‌] بيزارى- رفع امان- است از خداى و پيامبر او به سوى آن كسان از مشركان كه با آنان پيمان بسته‌ايد [و آنان پيمان شكنى كردند].'),
(1237, 9, 2, 'پس [اى مشركان‌] چهار ماه- از دهم ذو الحجه تا دهم ربيع الثانى سال نهم هجرت- در زمين [آزادانه و ايمن از تعرض‌] بگرديد و بدانيد كه شما ناتوان‌كننده خدا نيستيد- از عذاب خدا نتوانيد گريخت- و خدا خواركننده كافران است.'),
(1238, 9, 3, 'و اين آگهى و هشدارى است از خداى و پيامبرش به مردم در روز حج اكبر- روز حج بزرگ يا روز بزرگ حج- كه خدا و پيامبر او از مشركان بيزارند. پس اگر [از شرك‌] توبه كنيد براى شما بهتر است و اگر روى بگردانيد پس بدانيد كه شما ناتوان‌كننده خدا نيستيد. و كسانى را كه كافر شدند به عذابى دردناك مژده ده'),
(1239, 9, 4, 'مگر آن كسان از مشركان كه با آنها پيمان بسته‌ايد، و شما را چيزى [از پيمان‌] نكاستند و با هيچ كس بر ضد شما همپشت نگشتند پس پيمان آنان را تا سر آمد مدتشان به پايان رسانيد، كه خدا پرهيزگاران را دوست دارد.'),
(1240, 9, 5, 'پس چون ماه‌هاى حرام- چهار ماهى كه به آنها مهلت داده شده بود- سپرى شود مشركان را هر جا كه يابيد بكشيد و بگيريدشان و در تنگنا قرارشان دهيد- بازداشتشان كنيد- و در هر گذرگاهى به كمينشان بنشينيد، پس اگر توبه كنند و نماز برپا دارند و زكات بدهند راهشان را بگشاييد- متعرض آنها نشويد- كه خدا آمرزگار و مهربان است.'),
(1241, 9, 6, 'و اگر يكى از مشركان از تو زنهار- امان- خواست او را زنهار ده تا سخن خداى را بشنود، سپس او را به جاى امنش برسان، اين [زنهاردادن‌] از آن روست كه آنان مردمى نادانند.'),
(1242, 9, 7, 'چگونه مشركان را نزد خداى و نزد پيامبر او پيمانى تواند بود؟ مگر آنان كه نزديك نمازگاه شكوهمند- مسجد الحرام- با آنان پيمان بستيد پس تا هنگامى كه شما را [بر پيمان‌] استوار و پايدار ماندند شما نيز آنها را [بر پيمان‌] استوار و پايدار بمانيد، كه خدا پرهيزگاران را دوست دارد.'),
(1243, 9, 8, 'چگونه [آنان را پيمان باشد]؟ و حال آنكه اگر بر شما دست يابند در باره شما نه [حق‌] خويشاوندى را نگاه دارند و نه پيمانى را. با دهانشان- سخنانشان- شما را خشنود مى‌سازند ولى دلشان سر باز مى‌زند و بيشترشان بدكار [و پيمان‌شكن‌] اند.'),
(1244, 9, 9, 'به بهاى آيات خدا چيز اندكى ستاندند پس [مردم را] از راه او بازداشتند، براستى بد است آنچه مى‌كردند.'),
(1245, 9, 10, 'در باره هيچ مؤمنى نه [حق‌] خويشى را نگاه مى‌دارند و نه پيمانى را و اينانند تجاوزكاران.'),
(1246, 9, 11, 'پس اگر توبه كنند و نماز برپا دارند و زكات بدهند، برادران دينى شما هستند و ما آيات را براى مردمى كه بدانند روشن بيان مى‌كنيم.'),
(1247, 9, 12, 'و اگر سوگندهاى خويش پس از پيمان‌بستن بشكنند و در دين شما زبان به عيب‌گويى و نيش‌زدن بگشايند، پس با پيشوايان كفر كارزار كنيد زيرا كه آنها را [پايبندى به‌] سوگند [و پيمان‌] نيست باشد كه [از كفر و شرك و پيمان شكنى‌] بازايستند.'),
(1248, 9, 13, 'آيا- چرا- با گروهى كارزار نمى‌كنيد كه سوگند [و پيمان‌] خويش را بشكستند و آهنگ بيرون‌كردن پيامبر را نمودند، و آنها بودند كه نخستين بار [دشمنى و پيكار با شما را] آغاز كردند؟ آيا از آنان مى‌ترسيد؟ و خدا سزاوارتر است كه از او بترسيد، اگر مؤمنيد.'),
(1249, 9, 14, 'با آنان كارزار كنيد تا خدا آنها را به دستهاى شما عذاب كند و خوارشان سازد و شما را بر آنان يارى دهد و سينه‌هاى مردمى مؤمن را بهبود بخشد'),
(1250, 9, 15, 'و خشم را از دلهاى ايشان ببرد. و خداى بر هر كه خواهد [به بخشايش خويش‌] باز مى‌گردد و توبه هر كه را خواهد مى‌پذيرد و خدا داناى درستكار است.'),
(1251, 9, 16, 'يا مگر پنداشته‌ايد كه شما را واگذارند [و كارزار با كافران نفرمايند]، و حال آنكه خداوند هنوز كسانى از شما را كه جهاد كردند و بجز خدا و پيامبرش و مؤمنان يار همرازى نگرفتند شناخته نكرده است، و خدا بدانچه مى‌كنيد آگاه است.'),
(1252, 9, 17, 'مشركان را نسزد كه مسجدهاى خداى را آباد كنند در حالى كه بر كفر خويش گواهند آنانند كه كارهايشان تباه و بى‌بر است و در آتش دوزخ جاودانه باشند.'),
(1253, 9, 18, 'همانا مسجدهاى خداى را آن كس [بايد] آباد كند كه به خداى و روز بازپسين ايمان آورده و نماز برپا داشته و زكات داده و جز از خدا نهراسيده است پس اميد است كه آنان از راه‌يافتگان باشند.'),
(1254, 9, 19, 'آيا آب دادن حاجيان و آبادكردن مسجد الحرام را با [ايمان و عمل‌] آن كس كه به خداى و روز واپسين ايمان آورده و در راه خدا جهاد كرده برابر مى داريد؟ نزد خدا هرگز برابر و يكسان نيستند، و خدا گروه ستمكاران را راه ننمايد.'),
(1255, 9, 20, 'آنان كه ايمان آوردند و هجرت كردند و در راه خدا با مالها و جانهاى خويش جهاد كردند نزد خداوند پايه‌اى بزرگتر دارند، و اينانند رستگاران و كامگاران.'),
(1256, 9, 21, 'پروردگارشان آنان را به مهر و بخشايش و خشنوديى از سوى خويش و بهشت‌هايى كه در آنها نعمتهاى پايدار دارند مژده مى‌دهد'),
(1257, 9, 22, 'در آنها هماره جاويدانند. همانا نزد خداوند مزدى بزرگ است.'),
(1258, 9, 23, 'اى كسانى كه ايمان آورده‌ايد، پدران و برادران خود را، اگر كفر را بر ايمان برگزيدند، دوست و سرپرست مگيريد پس هر كه از شما آنها را دوست و سرپرست بگيرد، آنانند ستمكاران.'),
(1259, 9, 24, 'بگو: اگر پدرانتان و فرزندانتان و برادرانتان و همسرانتان و خويشاوندانتان و مالهايى كه به دست آورده‌ايد و بازرگانيى كه از ناروايى و بى‌رونقى آن مى‌ترسيد و خانه‌هايى كه به آنها دلخوشيد، به نزد شما از خدا و پيامبر او و جهاد در راه او دوست‌داشتنى‌ترند، پس منتظر باشيد تا خدا فرمان خويش- عذاب يا نصرت دين خود- بيارد و خدا مردم نافرمان را راه ننمايد.'),
(1260, 9, 25, 'هر آينه خداوند شما را در جايهاى بسيار يارى كرد و در روز حُنين آنگاه كه فزونى [لشكرتان‌] شما را به شگفت [و خودبينى‌] آورد، اما شما را هيچ سودى نداشت و زمين با همه فراخى بر شما تنگ آمد، پس پشت كرده گريختيد.'),
(1261, 9, 26, 'آنگاه خداوند آرامش خود را بر پيامبرش و بر مؤمنان فروفرستاد و سپاهيانى كه آنها را نمى‌ديديد فروفرستاد، و كسانى را كه كافر شدند عذاب كرد و سزاى كافران همين است.'),
(1262, 9, 27, 'سپس [با اين همه‌] خداوند از آن پس [به بخشايش خويش‌] بر هر كه خواهد باز مى‌گردد و توبه هر كه را خواهد مى‌پذيرد، و خدا آمرزگار و مهربان است.'),
(1263, 9, 28, 'اى كسانى كه ايمان آورده‌ايد، جز اين نيست كه مشركان پليدند، پس بعد از اين سال نبايد به مسجد الحرام نزديك شوند و اگر [به سبب قطع داد و ستد] از بينوايى و درويشى مى‌ترسيد خداوند اگر خواهد شما را از بخشش خويش بى‌نياز خواهد كرد، زيرا كه خدا دانا و با حكمت است.'),
(1264, 9, 29, 'با كسانى از كتاب داده‌شدگان- جهودان و ترسايان- كه به خداى و روز واپسين ايمان نمى‌آورند و آنچه را كه خدا و پيامبر او حرام كرده‌اند حرام نمى شمارند و دين حق را نمى‌پذيرند كارزار كنيد تا آنگاه كه به دست خود جزيه دهند در حالى كه خواران باشند.'),
(1265, 9, 30, 'جهودان گفتند: عزير پسر خداست و ترسايان گفتند: مسيح پسر خداست. اين گفتار آنهاست به دهانشان- كه حقيقتى ندارد- و گفتارشان به سخن كسانى ماند كه پيش از اين كافر شدند- مانند آنان كه فرشتگان را دختران خدا مى‌گفتند- خدايشان بكشد، چگونه و به كجا [از حق‌] گردانيده مى شوند؟!'),
(1266, 9, 31, 'حبرها- دانشمندان يهود- و راهبان- صومعه‌داران ترسا- خويش و مسيح پسر مريم را به جاى خداى يكتا به خدايى گرفتند و حال آنكه جز اين فرمان نداشتند كه خداى يگانه را بپرستند كه جز او خدايى نيست، پاك و منزه است از آنچه با وى انباز مى‌آرند.'),
(1267, 9, 32, 'مى‌خواهند نور- دين و حجت- خدا را با دهانشان خاموش كنند و خدا جز اين نمى خواهد كه روشنايى خود را تمام و آشكار سازد، اگر چه كافران خوش ندارند.'),
(1268, 9, 33, 'اوست كه پيامبر خود را با راهنمايى به راه راست و دين حق فرستاد تا آن را بر همه دينها چيره گرداند، هر چند مشركان خوش ندارند.'),
(1269, 9, 34, 'اى كسانى كه ايمان آورده‌ايد، هر آينه بسيارى از حبرها و راهبان مالهاى مردم را به ناشايست مى‌خورند و [آنها را] از راه خدا باز مى‌دارند و كسانى كه زر و سيم مى‌اندوزند و آن را در راه خدا انفاق نمى‌كنند، پس آنان را به عذابى دردناك مژده ده'),
(1270, 9, 35, 'روزى كه آن [زر و سيم‌] را در آتش دوزخ بتابند و با آن پيشانيها و پهلوها و پشتهاى آنان را داغ نهند [و گويند:] اين است آنچه براى خويشتن مى اندوختيد، پس [طعم‌] آنچه مى‌اندوختيد بچشيد.'),
(1271, 9, 36, 'همانا شمار ماه‌ها نزد خدا دوازده ماه است در كتاب خدا- كتاب آفرينش يا لوح محفوظ- روزى كه آسمانها و زمين را بيافريد از آنها چهار ماه- ذو القعده و ذو الحجه و محرم و رجب- حرام- شكوهمند و با حرمت- است. اين است آيين استوار. پس در آن ماه‌ها [با جنگ و خونريزى‌] بر خويشتن ستم مكنيد، و با همه مشركان بجنگيد همچنانكه با همه شما مى‌جنگند و بدانيد كه خداى با پرهيزگاران است.'),
(1272, 9, 37, 'همانا تأخير [و تغيير] ماه حرام به ماهى ديگر- نسي‌ء- افزايش در كفر است كه كسانى كه كافر شده‌اند بدان گمراه مى‌شوند آن را سالى حلال مى شمارند و سالى ديگر حرام تا با شمار آنچه خداى حرام كرده است همسان و برابر سازند پس [بدان سبب‌] آنچه را خداى حرام كرده است حلال مى‌كنند. كارهاى بد و ناپسندشان در نظرشان آراسته شده، و خدا گروه كافران را راه ننمايد.'),
(1273, 9, 38, 'اى كسانى كه ايمان آورده‌ايد، شما را چه شده است كه چون گويندتان [براى جهاد] در راه خدا بيرون رويد گرانى مى‌كنيد و به زمين مى‌چسبيد؟! آيا به زندگانى اين جهان به جاى آن جهان خشنود و دلخوش شده‌ايد؟ پس [بدانيد كه‌] كالاى زندگانى اين جهان در برابر آن جهان جز اندكى نيست.'),
(1274, 9, 39, 'اگر [براى جهاد] بيرون نرويد، شما را عذاب كند عذابى دردناك و گروهى ديگر به جاى شما آورد و او را هيچ زيانى نمى‌رسانيد، و خداى بر هر چيزى تواناست.'),
(1275, 9, 40, 'اگر او- پيامبر- را يارى نكنيد هر آينه خدا او را يارى كرد آنگاه كه كسانى كه كافر شدند او را بيرون كردند، در حالى كه يكى از دو تن بود- پيامبر و ابو بكر- هنگامى كه هر دو در غار بودند آنگاه كه به همراه خويش مى‌گفت: اندوه مدار، خدا با ماست پس خدا آرامش خود را بر او'),
(1276, 9, 41, 'سبكبار و گرانبار [براى جهاد] بيرون رويد و با مالها و جانهاى خويش در راه خدا جهاد كنيد، كه اين براى شما بهتر است، اگر مى‌دانستيد.'),
(1277, 9, 42, 'اگر كالايى- غنيمتى- نزديك و دسترس و سفرى كوتاه و آسان بود هر آينه [منافقان‌] تو را پيروى مى‌كردند اما آن راه بر آنان دراز و دشوار آمد. و زودا كه به خدا سوگند خورند كه اگر مى‌توانستيم- توشه راه و ساز و برگ داشتيم- با شما بيرون مى‌شديم. خويشتن را تباه و هلاك مى‌كنند، و خداى مى‌داند كه آنها دروغگويند.'),
(1278, 9, 43, 'خداى از تو درگذرد، چرا پيش از آنكه كسانى كه راست گفتند برايت پديدار و شناخته شوند و دروغگويان را بشناسى، به آنان [در نشستن از جنگ‌] رخصت دادى؟'),
(1279, 9, 44, 'كسانى كه به خداى و روز واپسين ايمان دارند براى اينكه با مالها و جانهاى خويش جهاد كنند از تو رخصت [نشستن از جنگ‌] نخواهند، و خداوند به حال پرهيزگاران داناست.'),
(1280, 9, 45, 'همانا كسانى از تو رخصت مى‌خواهند كه به خداى و روز واپسين ايمان ندارند و دلهاشان به شك آلوده است پس در شك خويش دودل و سرگردانند.'),
(1281, 9, 46, 'و اگر بيرون‌شدن را مى‌خواستند هر آينه سازوبرگ آن را آماده مى‌ساختند و ليكن خدا انگيخته و بسيج‌شدن آنان را ناپسند داشت، پس آنها را از [بيرون‌شدن‌] باز داشت و واپس نشاند و به آنها گفته شد كه با وانشستگان بنشينيد.'),
(1282, 9, 47, 'اگر در ميان شما بيرون آمده بودند شما را جز تباهى نمى‌افزودند و ميانتان به سخن‌چينى و خرابكارى مى‌شتافتند در حالى كه در باره شما فتنه انگيزى و آشوب مى‌خواهند، و در ميان شما جاسوسانند براى ايشان- كه خبرهاى شما را به آنها مى‌رسانند- و خدا به حال ستمكاران داناست.'),
(1283, 9, 48, 'هر آينه پيش از اين نيز فتنه بسيار جستند و كارها را براى تو واژگونه نمودند تا آنكه حق بيامد و فرمان خداى آشكار شد در حالى كه ناخشنود بودند.'),
(1284, 9, 49, 'و از آنان كس هست كه مى‌گويد مرا رخصت ده و مرا در فتنه ميفكن آگاه باش كه در فتنه- كفر و نفاق- درافتاده‌اند و همانا دوزخ كافران را فرا گرفته است.'),
(1285, 9, 50, 'اگر تو را نيكيى- پيروزى و غنيمتى- فرا رسد آنان را اندوهگين سازد و اگر تو را مصيبتى- شكستى و زيانى- رسد گويند ما [احتياط و چاره‌] كار خويش از پيش فرا گرفته بوديم، و در حالى كه شادمانند روى گردانده باز مى‌گردند.'),
(1286, 9, 51, 'بگو: به ما نرسد مگر آنچه خدا براى ما نوشته است اوست سرپرست و كارساز ما، پس مؤمنان بايد بر خدا توكّل كنند و بس.'),
(1287, 9, 52, 'بگو: آيا جز يكى از دو نيكى- پيروزى يا شهادت- را براى ما چشم مى‌داريد؟ ولى ما چشم مى‌داريم كه خدا به شما عذابى از نزد خود يا به دست ما برساند، پس انتظار بريد كه ما هم با شما منتظريم.'),
(1288, 9, 53, 'بگو: به خوشى و دلخواه يا به ناخوشى و ناخواه انفاق كنيد هرگز از شما پذيرفته نخواهد شد، زيرا كه شما گروهى بدكار و نافرمانيد.'),
(1289, 9, 54, 'و چيزى آنان را از پذيرفته‌شدن انفاقشان باز نداشت مگر اينكه به خدا و به پيامبر او كافر شدند و نماز نمى‌گزارند مگر با حال سستى و كاهلى و انفاق نمى‌كنند مگر به ناخواه و دشوارى.'),
(1290, 9, 55, 'مالها و فرزندانشان تو را به شگفت نيارد. همانا خداى مى‌خواهد تا بدانها آنان را در زندگانى اين جهان عذاب كند و در حالى كه كافرند جانشان بر آيد.'),
(1291, 9, 56, 'و به خدا سوگند مى‌خورند كه از شمايند و حال آنكه از شما نيستند و ليكن گروهى‌اند كه [از شما] مى‌ترسند.'),
(1292, 9, 57, 'اگر پناهگاهى يا نهانگاه‌هايى يا جايى براى اندرشدن بيابند هر آينه شتابان و گريزان به آن روى آورند.'),
(1293, 9, 58, 'و از آنان كسانى‌اند كه بر تو در صدقه‌ها- تقسيم زكات- خرده مى‌گيرند، پس اگر از آن بدهندشان خشنود مى‌شوند و اگر از آن ندهندشان آنگاه ناخشنود و خشمگين مى‌گردند.'),
(1294, 9, 59, 'و [چه خوب بود] اگر بدانچه خداى بديشان دهد و پيامبرش، خرسند گردند و گويند: خداى ما را بس است، بزودى خداى ما را از فزون‌بخشى خويش بدهد و پيامبرش نيز كه ما به خدا گراينده و اميدواريم.'),
(1295, 9, 60, 'همانا صدقه‌ها- زكات- براى نيازمندان و درماندگان و كاركنان بر [گردآورى‌] آن، و نواخته‌دلان- تا به اسلام روى آورند-، و در راه آزادى بردگان و وامداران و تاوان‌زدگان و در راه خدا- هزينه جهاد- و در راه‌ماندگان است، فريضه‌اى- فرموده‌اى- است از خداى، و خدا دانا و با حكمت است.'),
(1296, 9, 61, 'و از آنان كسانى‌اند كه پيامبر را مى‌آزارند و مى‌گويند: او [سراپا] گوش است- شنواى سخن هر كسى است- بگو: گوشِ نيكوست براى شما، به خدا ايمان دارد و مؤمنان را باور مى‌دارد- سخن و مشورت ايشان را با خوش‌گمانى پذيراست- و رحمتى است براى كسانى از شما كه ايمان آورده‌اند و كسانى كه پيامبر خداى را بيازارند ايشان را عذابى است دردناك.'),
(1297, 9, 62, 'براى شما به خدا سوگند مى‌خورند تا شما را خشنود كنند، و حال آنكه اگر مؤمن بودند سزاوارتر اين بود كه خداى و پيامبرش را [از خود] خشنود كنند.'),
(1298, 9, 63, 'آيا ندانستند كه هر كه با خدا و پيامبرش دشمنى و مخالفت كند، او راست آتش دوزخ كه در آن جاويدان باشد؟ اين است خوارى و رسوايى بزرگ.'),
(1299, 9, 64, 'منافقان مى‌ترسند از اينكه سوره‌اى بر آنان فروآيد كه آنها را بدانچه در دلهاشان دارند آگاه سازد. بگو: مسخره كنيد، همانا خدا پديدآرنده آن چيزى است كه از آن مى‌ترسيد و مى‌پرهيزيد.'),
(1300, 9, 65, 'و اگر از آنان- منافقان- بپرسى [كه چرا استهزا مى‌كرديد؟]، گويند: جز اين نيست كه ما به سرگرمى و شوخى سخنانى مى‌گفتيم و بازى مى‌كرديم. بگو: آيا به خدا و آيات او و پيامبرش استهزا مى‌كرديد؟'),
(1301, 9, 66, '[و چون به عذرخواهى آمدند، خداى گفت:] هيچ عذر مياريد، براستى از پس ايمانتان كافر شديد. اگر از گروهى از شما درگذريم گروهى ديگر را عذاب كنيم زيرا كه آنان بزهكار بودند.'),
(1302, 9, 67, 'مردان و زنان منافق برخى‌شان از برخى ديگرند- بر آيين و روش يكديگرند-، به كار بد و ناشايست فرمان مى‌دهند و از كار نيك و شايسته باز مى‌دارند، و دست خويش [از انفاق در راه خدا] بسته مى‌دارند، خداى را فراموش كردند پس خداى نيز آنان را به فراموشى سپرد، زيرا كه منافقان، بدكاران و بيرون‌شدگان از فرمانند.'),
(1303, 9, 68, 'خداوند مردان و زنان منافق و كافران را آتش دوزخ وعده كرده است كه در آن جاويدانند همان بس است آنها را و خداى لعنتشان كرده، و آنان را عذابى است پاينده.'),
(1304, 9, 69, '[شما كافران و منافقان‌] همانند كسانى [هستيد] كه پيش از شما بودند، در حالى كه از شما نيرومندتر و داراى مال و فرزندان بيشتر بودند، پس از بهره خويش برخوردار شدند و شما نيز از بهره خود برخوردار شديد همچنانكه آنان كه پيش از شما بودند از بهره خويش برخوردار شدند. و [در گفتگوى بيهوده و باطل‌] فرورفتيد چنانكه آنها فرورفتند. آنان كارهايشان در اين جهان و آن جهان تباه و بر باد شد و آنانند زيانكاران.'),
(1305, 9, 70, 'آيا خبر كسانى كه پيش از آنان بودند بديشان نرسيده است؟ قوم نوح و عاد و ثمود و قوم ابراهيم و مردم [شهر] مَدين و دهكده‌هاى زير و رو شده- آباديهاى قوم لوط- كه پيامبرانشان نشانه‌هاى روشن و هويدا بديشان آوردند و خداى بر آن نبود كه بر آنان ستم كند بلكه خود بر خويشتن ستم مى كردند.'),
(1306, 9, 71, 'و مردان و زنان مؤمن دوستان و ياوران يكديگرند به كار نيك و پسنديده فرمان مى‌دهند و از كار زشت و ناپسند باز مى‌دارند و نماز را به پاى مى دارند و زكات مى‌دهند و خدا و پيامبرش را فرمان مى‌برند. اينانند كه خداى بزودى برايشان مِهر و بخشايش آرد كه خدا تواناى بى‌همتا و داناى درستكار است.'),
(1307, 9, 72, 'خداوند به مردان و زنان مؤمن بوستانهايى وعده داده است كه از زير [درختان‌] آنها جويها روان است، در آنها جاويدانند و نيز جايهاى خوش و پاكيزه در بهشت‌هاى پاينده، و خشنودى خدا از همه برتر و بزرگتر است. اين است رستگارى و كاميابى بزرگ.'),
(1308, 9, 73, 'اى پيامبر، با كافران و منافقان جهاد كن و بر آنان سخت گير و درشتى نما، و جاى آنها دوزخ است و بد بازگشتگاهى است.'),
(1309, 9, 74, 'به خدا سوگند مى‌خورند كه [چيزى‌] نگفته‌اند و هر آينه سخن كفر را گفته‌اند و پس از آنكه اسلام آوردند كافر شدند و آهنگ چيزى كردند كه بدان نرسيدند- كشتن رسول خدا [ص‌] در عقبه- و [از پيامبر و مؤمنان‌] كينه نداشتند مگر از آن رو كه خداى آنان را از بخشش و فزونى خود بى‌نياز و توانگر ساخت و پيامبرش نيز. پس اگر توبه كنند برايشان بهتر است و اگر روى بگردانند و پشت كنند خداى آنها را عذاب خواهد كرد عذابى دردناك در اين جهان و آن جهان، و ايشان را در زمين هيچ سرپرست و ياورى نباشد.'),
(1310, 9, 75, 'و از آنان كسانى هستند كه با خداى پيمان بستند كه اگر ما را از فزونى و بخشش خود بدهد هر آينه صدقه- زكات- دهيم و از نيكان و شايستگان باشيم.'),
(1311, 9, 76, 'پس چون از فزون‌بخشى خويش بديشان داد به آن بخل ورزيدند، و [به پيمان‌] پشت كرده روى بگردانيدند.'),
(1312, 9, 77, 'پس در دلهاى آنان تا روزى كه به ديدار او رسند- مرگ يا رستاخيز- دورويى از پى در آورد از آن رو كه با خداى در آنچه پيمان بسته بودند خلاف كردند و بدان سبب كه دروغ مى‌گفتند.'),
(1313, 9, 78, 'آيا ندانستند كه خدا پنهانشان و رازگفتنشان را مى‌داند، و همانا خداوند داناى همه نهانها و ناپيداهاست.'),
(1314, 9, 79, 'كسانى كه در باره صدقه‌ها بخشندگان بدلخواه از مؤمنان را عيب و خرده مى‌گيرند و كسانى را كه جز به اندازه توان و تلاش خويش بيشتر نيابند [كه ببخشند] مسخره مى‌كنند خداوند هم آنان را مسخره خواهد كرد- تمسخرشان را كيفر مى‌دهد- و آنان راست عذابى دردناك.'),
(1315, 9, 80, 'خواهى برايشان آمرزش بخواه يا برايشان آمرزش مخواه، اگر برايشان هفتاد بار آمرزش بخواهى هرگز خدا آنها را نيامرزد. اين از آن روست كه به خدا و پيامبرش كافر شدند و خدا گروه بدكار نافرمان را راه ننمايد.'),
(1316, 9, 81, 'واپس گذاشتگان، به نشستن خود [از كارزار] بر خلاف [فرمان‌] رسول خدا شاد گشتند و خوش نداشتند كه با مالها و جانهاى خويش در راه خدا جهاد كنند و گفتند: در گرما [براى جنگ‌] بيرون مرويد. بگو: آتش دوزخ گرمتر و سخت‌تر است، اگر درمى‌يافتند.'),
(1317, 9, 82, 'پس بايد اندك بخندند و بسيار بگريند به سزاى آنچه مى‌كردند.'),
(1318, 9, 83, 'پس اگر خداى تو را به گروهى از آنان بازگردانيد- پس از جنگ تبوك- آنگاه براى بيرون شدن اجازه خواستند، بگو: هرگز با من بيرون نخواهيد شد و هرگز همراه من با هيچ دشمنى كارزار نخواهيد كرد زيرا شما نخستين‌بار نشستن [از جنگ‌] را پسنديديد، پس با واپس‌ماندگان بنشينيد.'),
(1319, 9, 84, 'و هيچگاه بر هيچ يك از آنها كه بميرد نماز مكن و بر گورش مايست، زيرا كه آنان به خدا و پيامبرش كافر شدند و در حالى مردند كه بدكاران نافرمان بودند.'),
(1320, 9, 85, 'و مالها و فرزندانشان تو را به شگفت نيارد، همانا خدا مى‌خواهد كه بدان وسيله آنان را در اين جهان عذاب كند و در حالى كه كافرند جانشان در آيد.'),
(1321, 9, 86, 'و چون سوره‌اى فروفرستاده شود كه به خداى ايمان آوريد و همراه پيامبر او كارزار كنيد، توانگرانشان از تو رخصت خواهند و گويند: ما را بگذار تا با نشستگان- خانه‌نشينان- باشيم.'),
(1322, 9, 87, 'به اين خشنود شدند كه با واپس‌ماندگان باشند، و بر دلهاشان مهر نهاده شده پس در نمى‌يابند.'),
(1323, 9, 88, 'اما پيامبر و كسانى كه با او ايمان آوردند با مالها و جانهاى خويش جهاد كردند، و اينانند كه نيكيها از آن ايشان است، و ايشانند رستگاران.'),
(1324, 9, 89, 'خداوند براى ايشان بهشتها آماده كرده است كه از زير [درختان‌] آنها جويها روان است، جاودانه در آنجا باشند اين است كاميابى بزرگ.'),
(1325, 9, 90, 'عذردارندگان از باديه‌نشينان- با اينكه جهاد بر آنان واجب نبود- آمدند تا به آنها رخصت داده شود [كه به كارزار نروند]، و كسانى كه به خداى و پيامبر او دروغ گفته بودند- منافقان- بازنشستند- با اينكه جهاد بر آنان واجب بود-. زودا كه به كسانى از آنان كه كافر شدند عذابى دردناك برسد.'),
(1326, 9, 91, 'بر ناتوانان و بيماران و كسانى كه چيزى نيابند- ندارند- كه [در راه جهاد] هزينه كنند، هر گاه خدا و پيامبر او را نيكخواه باشند تنگى و باكى [در بازنشستنشان از جنگ‌] نيست زيرا بر نيكوكاران هيچ راهى [براى سرزنش و عقوبت‌] نيست و خدا آمرزگار و مهربان است'),
(1327, 9, 92, 'و نه بر كسانى كه چون نزد تو آمدند تا [براى رفتن به جنگ بر ستورى‌] برنشانى‌شان، گفتى: چيزى نمى‌يابم كه شما را بر آن برنشانم، بازگشتند در حالى كه ديدگانشان از اين اندوه كه چيزى ندارند تا [در رفتن به جهاد] هزينه كنند سرشك مى‌باريد.'),
(1328, 9, 93, 'راه [سرزنش و عقوبت‌] تنها بر كسانى است كه در حالى كه توانگرند از تو رخصت مى‌خواهند، خشنودند كه با واپس‌ماندگان باشند و خدا بر دلهاى آنها مهر نهاده است، از اين رو در نمى‌يابند- سرانجام بد و كيفر نافرمانى خويش را-.'),
(1329, 9, 94, 'چون بديشان بازگرديد نزد شما عذر مى‌آورند، بگو: عذر مياوريد، هرگز شما را باور نداريم، هر آينه خداوند ما را از خبرهاى شما آگاه ساخت و زودا كه خدا و پيامبر او كردار شما را بنگرند، سپس به سوى داناى نهان و آشكار بازگردانده مى‌شويد، آنگاه شما را بدانچه مى‌كرديد آگاه كند.'),
(1330, 9, 95, 'چون به سوى آنان بازگرديد براى شما به خدا سوگند خواهند خورد تا از [سرزنش‌] آنها چشم بپوشيد، پس از ايشان چشم بپوشيد، كه آنان پليدند و جايگاهشان به سزاى كارهايى كه مى‌كردند، دوزخ است.'),
(1331, 9, 96, 'براى شما سوگند مى‌خورند تا از آنها خشنود شويد، پس اگر شما از آنان خشنود شويد همانا خدا از مردم بدكار نافرمان خشنود نخواهد شد.'),
(1332, 9, 97, 'باديه‌نشينان در كفر و نفاق سخت‌ترند، و سزاوارتر به اينكه مرزها و اندازه‌هاى آنچه را خدا بر پيامبرش فروفرستاده ندانند، و خدا دانا و درستكار است.'),
(1333, 9, 98, 'و از باديه‌نشينان كسانى هستند كه آنچه را انفاق مى‌كنند تاوان و زيان مى‌شمرند و پيشامدهاى بد را براى شما چشم مى‌دارند. پيشامد بد بر آنها باد! و خدا شنوا و داناست.'),
(1334, 9, 99, 'و از باديه‌نشينان كسانى هستند كه به خدا و روز واپسين ايمان دارند و آنچه را انفاق مى‌كنند و دعاهاى پيامبر را اسباب تقرب به خدا مى‌شمرند. آگاه باشيد كه اينها سبب تقرب براى ايشان است زودا كه خداوند آنان را در مهر و بخشايش خويش در آورد، كه خدا آمرزگار و مهربان است.'),
(1335, 9, 100, 'و آن پيشى‌گيرندگان نخستين از مهاجران و انصار- كه پيش از ديگران به اسلام گرويدند-، و كسانى كه با نيكوكارى آنان را پيروى كردند خداى از ايشان خشنود است و ايشان از خداى خشنودند، و براى آنان بوستانهايى آماده ساخته كه زير [درختان‌] آنها جويها روان است، هماره در آنها جاويدانند اين است رستگارى و كاميابى بزرگ.'),
(1336, 9, 101, 'و از باديه‌نشينان پيرامونتان كسانى منافق‌اند و نيز برخى از مردم مدينه پيوسته بر نفاق خو كرده‌اند، تو آنها را نمى‌شناسى، ما آنها را مى‌شناسيم، زودا كه آنان را دو بار- يكى در دنيا و بار ديگر به هنگام مرگ و عالم برزخ- عذاب كنيم، سپس به عذابى بزرگ بازگردانده شوند.'),
(1337, 9, 102, 'و گروهى ديگر به گناهان خود اعتراف كرده و كارى نيك و شايسته را با كارى بد و ناشايست آميخته‌اند اميد است كه خداوند [به بخشايش خويش‌] بر آنان بازگردد و توبه آنان را بپذيرد، كه خدا آمرزگار و مهربان است.'),
(1338, 9, 103, 'از مالهاى آنان صدقه‌اى- زكات- بگير كه بدان وسيله پاكشان سازى و آنان را بركت و فزونى مى‌بخشى- اموال يا حسناتشان را نشو و نما مى‌دهى- و ايشان را دعا كن كه دعاى تو آرامشى است براى آنان، و خدا شنوا و داناست.'),
(1339, 9, 104, 'آيا ندانستند كه خداست كه توبه را از بندگان خود مى‌پذيرد و صدقه‌ها را مى‌ستاند، و اوست بسيار توبه‌پذير و مهربان؟'),
(1340, 9, 105, 'و بگو: كار كنيد، كه خدا و پيامبر او و مؤمنان- گواهان اعمال- كردار شما را خواهند ديد، و بزودى به داناى نهان و آشكار بازگردانده مى‌شويد پس شما را بدانچه مى‌كرديد آگاه خواهد كرد.'),
(1341, 9, 106, 'و گروهى ديگر واپس داشتگانند براى فرمان خدا، يا عذابشان كند و يا [به بخشايش خويش‌] بر آنان بازگردد و توبه‌شان را بپذيرد، و خدا دانا و درستكار است.'),
(1342, 9, 107, 'و [گروهى از منافقانند] آنان كه مسجدى گرفتند- ساختند- براى گزندرساندن و كفر ورزيدن و جدايى افكندن ميان مؤمنان و ساختن كمينگاهى براى كسانى كه با خدا و پيامبر او از پيش در جنگ بودند، و هر آينه سوگند مى‌خورند كه ما جز نيكى نخواستيم و خدا گواهى مى‌دهد كه آنان دروغگويند.'),
(1343, 9, 108, 'هيچگاه در آنجا [به نماز] مايست. هر آينه مسجدى كه از نخستين روز بر پرهيزگارى بنياد يافته- مسجد قبا- سزاوارتر است كه در آن [به نماز] بايستى، كه در آنجا مردانى‌اند كه دوست دارند پاكى ورزند و خدا پاكى‌ورزان را دوست دارد.'),
(1344, 9, 109, 'آيا كسى كه بنياد كارش را بر پرواى از خدا و خشنودى او نهاده بهتر است يا آن كه بنياد كارش را بر لبه پرتگاهى سست و فروريختنى نهاده پس او را به آتش دوزخ در اندازد؟ و خدا مردم ستمكار را راه ننمايد.'),
(1345, 9, 110, 'آن بنايشان كه بر آوردند هميشه در دلهاشان مايه شك و دودلى است [و اين شك برطرف نشود] مگر آنكه دلهاشان پاره پاره شود- به پشيمانى و توبه يا به مرگ- و خدا دانا و استواركار است.'),
(1346, 9, 111, 'خداى از مؤمنان جانها و مالهاشان را بخريد به بهاى آنكه بهشت براى آنان باشد، در راه خدا كارزار مى‌كنند پس مى‌كشند و كشته مى‌شوند. وعده‌اى است راست و درست بر خداى در تورات و انجيل و قرآن، و كيست كه به پيمان خويش از خدا وفادارتر است؟ پس به اين خريد و فروخت كه كرديد شادمان باشيد. و اين است رستگارى و كاميابى بزرگ'),
(1347, 9, 112, 'همان توبه‌كنندگان و پرستندگان و ستايندگان سپاسدار و گردش‌كنندگان [در راه خدا]- يا روزه‌داران- و ركوع‌كنندگان و سجود كنندگان و فرمايندگان به كار نيك و بازدارندگان از كار زشت و نگهداران مرزهاى- حدود احكام- خداى و مؤمنان را مژده ده.'),
(1348, 9, 113, 'پيامبر و كسانى را كه ايمان آورده‌اند نرسد كه براى مشركان، هر چند خويشاوند باشند، آمرزش خواهند پس از آنكه برايشان روشن شد كه آنها دوزخيانند.'),
(1349, 9, 114, 'و آمرزش‌خواهى ابراهيم براى پدرش- عموى مشركش كه به جاى پدر سرپرست او بود- نبود مگر از براى وعده‌اى كه به او داده بود پس چون براى او روشن شد كه وى دشمن خداست از او بيزار شد، همانا ابراهيم بسيار آه‌كننده- از ترس خدا- و بردبار بود.'),
(1350, 9, 115, 'و خدا بر آن نيست كه مردمى را پس از آنكه راهشان نمود گمراه كند تا برايشان آنچه را كه بايد [از آن‌] بپرهيزند بيان كند، همانا خدا به هر چيزى داناست.'),
(1351, 9, 116, 'همانا خداى راست پادشاهى آسمانها و زمين، زنده كند و بميراند، و شما را بجز خدا هيچ سرپرست و ياورى نيست.'),
(1352, 9, 117, 'هر آينه خدا [به بخشايش خويش و قبول توبه‌] بر پيامبر و مهاجران و انصار كه در هنگام سختى و دشوارى- رفتن به جنگ تبوك- از او پيروى كردند باز گشت پس از آنكه دلهاى گروهى از آنان در آستانه لغزش و كجروى بود- كه چرا بايد با كمبود وسايل و غذا در شدت گرما راه دراز تبوك را در پيش گيريم- و باز [به بخشايش خود] بديشان روى كرد و توبه‌شان را پذيرفت، كه او به آنان رؤوف- دلسوز- و مهربان است'),
(1353, 9, 118, 'و نيز بر آن سه تن كه [با عذرتراشى خود، از كارزار] واپس نهاده شدند- و مردم با قهر و نفرت از آنها بريدند- تا آنگاه كه زمين با همه فراخى‌اش بر آنها تنگ شد و دلهاشان به تنگ آمد و دانستند كه از خدا هيچ پناهى نيست مگر به خود او، آنگاه [به بخشايش خود] بر آنان باز گشت تا توبه كنند، كه خدا توبه‌پذير و مهربان است.'),
(1354, 9, 119, 'اى كسانى كه ايمان آورده‌ايد، از خداى پروا كنيد و با راستگويان باشيد.'),
(1355, 9, 120, 'مردم مدينه و باديه‌نشينان پيرامونشان را نسزد كه از [همراهى با] پيامبر خدا واپس مانند و نه اينكه به [حفظ] جانهاى خويش رغبت نمايند و از جان وى روگردان شوند- يعنى جانهاى خويش از جان او عزيزتر شمارند-. اين از آن روست كه در راه خدا هيچ تشنگى و رنج و گرسنگى بديشان نرسد و هيچ گامى در جايى كه كافران را به خشم آرد ننهند و به هيچ كامى از دشمن دست نيابند- از كشتن و زخمى كردن و اسير گرفتن و دستبرد زدن- مگر اينكه به پاداش آن براى ايشان كارى نيك و شايسته نويسند، كه خدا مزد نيكوكاران را تباه نمى‌كند'),
(1356, 9, 121, 'و هيچ هزينه‌اى كوچك يا بزرگ نكنند، و هيچ واديى- درّه يا زمينى- نپيمايند مگر آنكه برايشان [كار شايسته‌] نوشته شود، تا خداى به نيكوترين آنچه مى‌كردند پاداششان دهد.'),
(1357, 9, 122, 'و همه مؤمنان را نشايد كه [براى جهاد] بيرون روند، پس چرا از هر گروهى از ايشان جمعى بيرون نروند [و دسته‌اى بمانند] تا دانش دين بياموزند- و آيات و احكام جديد را فرا گيرند- و مردم خويش را چون به سوى ايشان باز گردند هشدار و بيم دهند شايد كه بترسند و بپرهيزند.'),
(1358, 9, 123, 'اى كسانى كه ايمان آورده‌ايد، با كافرانى كه به شما نزديكند كارزار كنيد و بايد كه در شما درشتى و سختى بيابند. و بدانيد كه خداى با پرهيزگاران است.'),
(1359, 9, 124, 'و چون سوره‌اى فرستاده شود برخى از آنان- منافقان- گويند: اين سوره ايمان كدام يك از شما را بيفزود؟ اما آنان كه ايمان آورده‌اند ايمانشان را بيفزايد و ايشان [به آن‌] شادمان مى‌شوند.'),
(1360, 9, 125, 'و اما كسانى كه در دلهاشان بيمارى- شك و كفر و نفاق- است پس آن سوره آنان را ناپاكيى بر ناپاكيشان بيفزايد و در حالى كه كافرند مى‌ميرند.'),
(1361, 9, 126, 'آيا نمى‌بينند كه در هر سال يك يا دو بار آزمون مى‌شوند؟ آنگاه نه توبه مى‌كنند و نه پند مى‌گيرند.'),
(1362, 9, 127, 'و چون سوره‌اى فرستاده شود- كه در آن از حال منافقان ياد شده باشد- برخى از آنان به برخى ديگر مى‌نگرند [و مى‌پرسند:] آيا كسى شما را مى بيند- زيرا نگرانند كه آثار ترس از رسواشدن را در چهره‌شان ببينند- آنگاه [از آن مجلس، آهسته و پنهانى‌] باز مى‌گردند خدا دلهاشان را [از حق‌] بگردانيد- يا برگرداناد-، زيرا كه آنان گروهى نافهم‌اند.'),
(1363, 9, 128, 'هر آينه شما را پيامبرى از خودتان آمد كه به رنج افتادنتان بر او گران و دشوار است به [هدايت‌] شما دلبسته است، و به مؤمنان دلسوز و مهربان است.'),
(1364, 9, 129, 'پس اگر پشت كنند و برگردند، بگو: خداى مرا بس است، جز او خدايى نيست، تنها بر او توكّل كردم، و اوست خداوند عرش بزرگ.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الف، لام، را. اين آيات كتاب حكيم- درست و استوار و حكمت‌آميز- است.'),
(1366, 10, 2, 'آيا براى [اين‌] مردم شگفت‌آور است كه به مردى از ايشان وحى كرديم كه مردم را بيم كن و كسانى را كه ايمان آورده‌اند مژده ده كه نزد پروردگارشان پايگاه راستين- يا پايه بلند و ارجمند يا سابقه نيك يا پاداش نيكو- دارند؟ كافران گويند: هر آينه اين جادوگرى آشكار است.'),
(1367, 10, 3, 'همانا پروردگار شما خداى يكتاست كه آسمانها و زمين را در شش روز- شش دوره- بيافريد، آنگاه بر عرش- مقام فرماندهى بر جهان هستى- بر آمد. كار [جهان‌] را تدبير مى‌كند. هيچ ميانجى و شفيعى نيست مگر پس از خواست و فرمان او اين است خداى، پروردگارتان پس او را بپرستيد، آيا ياد نمى كنيد و پند نمى‌گيريد؟'),
(1368, 10, 4, 'بازگشت همه شما به اوست. وعده راستِ خداست. همانا او آفرينش را آغاز مى‌كند و سپس همو آن را باز مى‌گرداند تا كسانى را كه ايمان آورده و كارهاى نيك و شايسته كرده‌اند به عدل و داد پاداش دهد و آنان كه كافر شدند به سزاى آنكه كفر مى‌ورزيدند آشاميدنيى از آب جوشان و عذابى دردناك دارند.'),
(1369, 10, 5, 'اوست آن [خداى‌] كه خورشيد را درخشنده و روشنايى‌دهنده و ماه را روشن ساخت و براى آن ماه جاى‌ها- منزلها- معيّن كرد تا شمار سالها و حساب [وقتها] را بدانيد. خدا آن را [كه ياد كرد] جز براستى و درستى نيافريد. نشانه‌ها را براى گروهى كه [بخواهند] بدانند به تفصيل بيان مى‌كند.'),
(1370, 10, 6, 'همانا در آمد و شد شب و روز و آنچه خدا در آسمانها و زمين آفريده است براى گروهى كه پرهيزگارى مى‌كنند نشانه‌هاست.'),
(1371, 10, 7, 'آنان كه به ديدار ما- يعنى بازگشت به خداوند- اميد [و باور] ندارند و به زندگى اين جهان خشنود شدند و به آن دل‌آرام گشتند و كسانى كه از آيات ما غافلند،'),
(1372, 10, 8, 'اينان جايگاهشان، به سزاى آنچه مى‌كردند آتش دوزخ است.'),
(1373, 10, 9, 'كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند، پروردگارشان آنان را به سبب ايمانشان راه مى‌نمايد، از زير [كوشكهاى‌] ايشان در بهشت هاى پرنعمت جويها روان است'),
(1374, 10, 10, 'دعايشان در آنجا اين است كه بار خدايا، پاكى تو راست و درودشان در آنجا «سلام» است و واپسين دعايشان اين است كه سپاس و ستايش خداى راست، پروردگار جهانيان.'),
(1375, 10, 11, 'و اگر خدا براى مردم بدى- عذاب- را با همان شتاب مى‌خواست كه آنان نيكى را مى‌خواهند هر آينه مدتشان سپرى شده بود- مرگشان با نزول عذاب بر آنها فرا رسيده بود- پس كسانى را كه به ديدار ما اميد [و باور] ندارند واميگذاريم تا در سركشى [و گمراهى‌] خود سرگردان بمانند.'),
(1376, 10, 12, 'و چون آدمى را گزندى فرا رسد ما را بخواند به پهلو خفته، يا نشسته، يا ايستاده. پس همين كه گزند وى از او برداريم چنان برود كه گويى هرگز ما را براى گزندى كه به وى رسيده بود نخوانده است. اينچنين براى گزافكاران كارهايى كه مى‌كردند آرايش يافته است.'),
(1377, 10, 13, 'و هر آينه نسلهاى پيش از شما را چون ستم كردند و به پيامبرانشان كه با نشانه‌ها و دلايل روشن بديشان آمدند ايمان نمى‌آوردند هلاك كرديم اينچنين گروه بزهكاران را كيفر مى‌دهيم.'),
(1378, 10, 14, 'سپس شما را از پس ايشان در زمين جانشين كرديم تا بنگريم چگونه عمل مى‌كنيد.'),
(1379, 10, 15, 'و چون آيات روشن ما بر آنان خوانده شود كسانى كه به ديدار ما اميد ندارند گويند: قرآنى جز اين بيار يا آن را دگرگون كن. بگو: مرا نرسد كه آن را از پيش خود دگرگون كنم. من پيروى نمى‌كنم مگر آنچه را كه به من وحى مى‌شود. من اگر پروردگارم را [به دگرگون‌ساختن قرآن‌] نافرمانى كنم از عذاب روزى بزرگ مى‌ترسم.'),
(1380, 10, 16, 'بگو: اگر خدا مى‌خواست آن را بر شما نمى‌خواندم و او شما را بدان آگاه نمى‌كرد، هر آينه عمرى- چهل سال- پيش از اين در ميان شما بوده‌ام- و چنين ادعايى نكردم- آيا خِرد را كار نمى‌بنديد؟'),
(1381, 10, 17, 'پس كيست ستمكارتر از آن كس كه بر خداى دروغ بندد يا آيات او را دروغ شمارد؟ براستى كه بزهكاران رستگار نمى‌شوند.'),
(1382, 10, 18, 'و به جاى خدا چيزهايى را مى‌پرستند كه نه زيانشان مى‌رساند و نه سودشان مى دهد و گويند: اينها شفيعان ما نزد خدايند. بگو: آيا خداى را به چيزى خبر مى دهيد كه در آسمانها و زمين، خود نمى‌داند؟! پاك است و برتر از آنچه [براى او] انباز مى‌گيرند.'),
(1383, 10, 19, 'مردم جز امتى يگانه [و بر يك دين‌] نبودند، سپس اختلاف كردند، و اگر نبود سخنى از پروردگارت كه از پيش رفته است- مهلت‌يافتن مردم براى آزمايش و تأخير عذاب- هر آينه در باره آنچه در آن اختلاف مى‌كنند داورى شده بود- به عذاب زودرس گرفتار مى‌شدند-.'),
(1384, 10, 20, 'و گويند: چرا نشانه‌اى- معجزه‌اى- از پروردگارش بر او فرو نيامده است؟ بگو: همانا نهان- غيب- خداى راست- آمدن معجزه به اختيار من نيست-، پس منتظر باشيد كه من نيز با شما از منتظرانم.'),
(1385, 10, 21, 'و چون مردم را از پس گزندى كه بديشان رسيده باشد رحمتى- نعمت و آسايشى- بچشانيم آنگاه در آيات ما بدانديشى و نيرنگ مى‌كنند- بهانه‌هاى بيجا مى‌آورند-. بگو: خداى زودتر تدبير و نيرنگ تواند كرد. همانا فرستادگان ما- فرشتگان- آنچه بدانديشى مى‌كنيد مى‌نويسند.'),
(1386, 10, 22, 'اوست آن كه شما را در خشكى و دريا مى‌راند، تا آنگاه كه در كشتى‌ها باشيد و كشتى‌ها با بادى خوش و سازوار براندشان و بدان شادمان شوند، ناگاه بادى تند و سهمگين بر آنان وزيدن گيرد و موج از هر سو بديشان رسد و چنان دانند كه فراگرفته شده و به هلاكت افتاده‌اند، خداى را در حالى كه دين را ويژه او كنند- با اخلاص- بخوانند كه اگر ما را از اين [بلا و ورطه هلاكت‌] برهانى هر آينه از سپاسگزاران باشيم.'),
(1387, 10, 23, 'و چون ايشان را برهاند آنگاه در زمين به ناحق ستم و سركشى مى‌كنند. اى مردم! ستم و سركشى شما تنها به زيان خودتان است، بهره‌اى از زندگى دنيا مى‌بريد سپس بازگشت شما به سوى ماست و شما را بدانچه مى‌كرديد آگاه كنيم.'),
(1388, 10, 24, 'جز اين نيست كه مَثَل- وصف- زندگى اين جهان مانند آبى است كه از آسمان فروفرستاده‌ايم پس رستنى‌هاى زمين از آنچه مردم و چارپايان مى‌خورند به آن درآميخت [و روييد] تا چون زمين آرايش خود [از شكوفه‌ها و گلها] فراگرفت و آراسته شد و اهل آن پنداشتند كه خود بر آن توانايى [بهره‌مندى‌] دارند، [ناگاه‌] فرمان ما شبى يا روزى دررسيد پس آن را چنان درويده كرديم كه گويى ديروز هيچ نبوده است. اينچنين آيات را براى گروهى كه مى انديشند به تفصيل بيان مى‌كنيم.'),
(1389, 10, 25, 'و خداى به سراى سلامت- بهشت- مى‌خواند و هر كه را خواهد به راه راست راه مى‌نمايد.'),
(1390, 10, 26, 'براى كسانى كه نيكويى كردند [پاداشى‌] نيكوتر باشد و فزونيى- افزون بر استحقاق- و رخسارشان را گرد تيره [گناهان‌] و خوارى نپوشاند. اينان بهشتيانند كه در آن جاويدان باشند.'),
(1391, 10, 27, 'و كسانى كه كارهاى بد كردند كيفر بد كارى را همانند آن دارند و آنان را خوارى بپوشد، آنان را نگهدارنده‌اى از خداى نيست گويى كه پاره‌هايى از شب تاريك بر رخسارشان پوشانده شده است، اينان دوزخيانند كه در آن جاويدان باشند.'),
(1392, 10, 28, 'و روزى كه همگيشان را برانگيزيم و فراهم آريم، سپس به كسانى كه شرك آوردند گوييم: شما و شريكانتان بر جاى خويش باشيد، پس ميان آنان [و بُتانشان‌] جدايى افكنيم و شريكانشان گويند: شما ما را نمى‌پرستيديد'),
(1393, 10, 29, 'پس خدا ميان ما و شما گواهى بسنده است كه هر آينه ما از پرستش شما بى‌خبر بوديم.'),
(1394, 10, 30, 'آنجا هر كسى آنچه را پيش فرستاده بيازمايد- سود و زيان آن را باز يابد- و به سوى خداوند راستينشان بازگردانده شوند، و دروغى كه مى‌بافتند از آنها گم شود.'),
(1395, 10, 31, 'بگو كيست كه شما را از آسمان و زمين روزى مى‌دهد؟ يا كيست كه بر شنوايى و بينايى‌ها فرمانرواست؟ و كيست كه زنده را از مرده بيرون مى‌آورد و مرده را از زنده بيرون مى‌آورد؟ و كيست كه كار [جهان و جهانيان‌] را تدبير مى‌كند؟ خواهند گفت: خداى بگو: پس آيا [از كيفر پرستش غير او] نمى پرهيزيد؟!'),
(1396, 10, 32, 'پس آن خداى يكتا پروردگار راستين شماست و بعد از راستى- بيان حق- جز گمراهى چيست؟ پس چگونه و به كجا [از حقّ‌] گردانيده مى‌شويد؟'),
(1397, 10, 33, 'اينچنين سخن پروردگارت بر كسانى كه از فرمان [او] بيرون شدند سزا گشت كه آنها ايمان نمى‌آورند.'),
(1398, 10, 34, 'بگو: آيا كسى از شريكان شما- يعنى بُتان كه شريك خدا مى‌گيريد- هست كه آفرينش آفريدگان را آغاز كند سپس بازشان گرداند؟ بگو: خدا آفرينش آفريدگان را آغاز مى‌كند و سپس بازشان مى‌گرداند؟ پس به كجا [از حق‌] گردانيده مى‌شويد؟'),
(1399, 10, 35, 'بگو: آيا از شريكانتان- بُتان- كسى هست كه به حق- راه راست و درست يا دين اسلام- راه نمايد؟ بگو: خداى به حق راه مى‌نمايد. آيا كسى كه به حق راه مى‌نمايد سزاوارتر است كه پيروى شود يا آن كه خود راه نيابد مگر آنكه او را راه نمايند؟ پس شما را چه شده؟! چگونه حكم مى‌كنيد؟!'),
(1400, 10, 36, 'و بيشترشان پيروى نمى‌كنند مگر گمانى را و گمان در باره حق هيچ بى‌نيازى نيارد- براى رسيدن به حق سود ندارد-، همانا خدا بدانچه مى‌كنند داناست.'),
(1401, 10, 37, 'و نتواند بود كه اين قرآن ساخته و پرداخته غير خدا باشد. بلكه باور داشت آن [كتابها] است كه پيش آن است- تورات و انجيل- و بيان كتاب است- از معانى مجمل و احكام و اوامر و نواهى يا تفصيل كتابهاى آسمانى- [و] شكّى در آن نيست كه از پروردگار جهانيان است.'),
(1402, 10, 38, 'بلكه مى‌گويند: آن را خود بافته است، بگو: اگر راستگوييد، پس سوره‌اى مانند آن بياوريد و هر كه را جز خدا توانيد [به يارى‌] بخوانيد.'),
(1403, 10, 39, '[چنين نيست‌] بلكه آنچه را كه به دانش آن نرسيدند و هنوز تأويل آن- سرانجام كارشان و وقت كيفرشان- بديشان نيامده دروغ پنداشتند. همچنين كسانى كه پيش از آنان بودند تكذيب كردند، پس بنگر كه سرانجام ستمكاران چگونه بود.'),
(1404, 10, 40, 'و از آنان كس هست كه به آن'),
(1405, 10, 41, 'و اگر تو را دروغگو شمردند، بگو: مراست كردار من و شما راست كردار شما شما از آنچه من مى‌كنم بيزاريد و من از آنچه شما مى‌كنيد بيزارم.'),
(1406, 10, 42, 'و [چون قرآن برخوانى‌] كسانى از آنان به تو گوش فرا دارند [ولى گوش دل نمى‌سپارند] آيا تو مى‌توانى كران را بشنوانى گر چه خرد را كار نبندند؟'),
(1407, 10, 43, 'و از آنان كس هست كه به تو مى‌نگرد [ولى نه با چشم دل‌] آيا تو مى‌توانى كوران را راه نمايى گر چه نبينند؟'),
(1408, 10, 44, 'براستى خدا بر مردم هيچ ستم نكند بلكه مردم بر خود ستم مى‌كنند.'),
(1409, 10, 45, 'و روزى كه آنان را برانگيزد، كه گويى [در دنيا] جز ساعتى از روز درنگ نكرده‌اند، يا يكديگر آشنايى كنند [در آن روز] كسانى كه ديدار خدا- معاد و قيامت- را دروغ پنداشتند براستى زيان كردند و راه‌يافتگان نبودند.'),
(1410, 10, 46, 'و اگر برخى از آنچه به آنان- منكران- وعده مى‌دهيم- از عذاب- به تو بنماييم يا تو را [پيش از كيفر آنان‌] برگيريم- بميرانيم-، به هر حال بازگشتشان به سوى ماست وانگهى خدا بر آنچه مى‌كنند گواه است.'),
(1411, 10, 47, 'و هر امتى را پيامبرى است، پس چون پيامبرشان بيامد [و برخى مؤمن و برخى كافر شدند] ميانشان به عدل و داد حكم شود و بر آنان ستم نرود.'),
(1412, 10, 48, 'و گويند: اگر راست مى‌گوييد اين وعده [عذاب‌] كى خواهد بود؟'),
(1413, 10, 49, 'بگو: من در باره خود، زيان و سودى را در اختيار ندارم مگر آنچه خداى خواهد. هر امتى را مدت و سرآمدى است، و چون مدتشان سر آيد نه ساعتى باز پس توانند شد و نه به پيش توانند رفت.'),
(1414, 10, 50, 'بگو: مرا خبر دهيد، اگر عذاب او به شب يا روز شما را فراآيد، بزهكاران چه چيز را از او به شتاب مى‌خواهند؟'),
(1415, 10, 51, 'آيا پس از آنكه [عذاب‌] واقع شود به آن ايمان مى‌آريد؟! اكنون؟! و حال آنكه [پيشتر] آن را [از راه انكار و تمسخر] به شتاب مى‌خواستيد.'),
(1416, 10, 52, 'آنگاه به كسانى كه ستم كردند گويند: عذاب جاويد را بچشيد آيا جز بدانچه مى كرديد كيفر مى‌شويد؟'),
(1417, 10, 53, 'و از تو خبر [عذاب‌] پرسند كه آيا آن راست است؟ بگو: آرى، به پروردگارم سوگند كه آن راست است، و شما ناتوان‌كننده [خدا از عذاب‌كردن‌] نيستيد.'),
(1418, 10, 54, 'و اگر هر كسى كه ستم كرده- با كفر و نفاق يا گناهان- آنچه در زمين است داشته باشد، هر آينه آماده است تا آن را براى بازخريد [خود از عذاب‌] بدهد و چون عذاب را ببينند پشيمانى در دل نهان دارند- تا شماتت نشوند- و ميانشان به انصاف و داد حكم شود، و بر آنان ستم نرود.'),
(1419, 10, 55, 'آگاه باشيد كه آنچه در آسمانها و زمين است خداى راست. آگاه باشيد كه وعده خدا راست است و ليكن بيشترشان نمى‌دانند.'),
(1420, 10, 56, 'اوست كه زنده كند و بميراند، و بازگشت شما به سوى اوست.'),
(1421, 10, 57, 'اى مردم، همانا شما را از پروردگارتان پندى آمد و بهبودى براى آنچه در سينه‌هاست- بيمارى دلها- و رهنمونى و بخشايشى براى مؤمنان.'),
(1422, 10, 58, 'بگو: پس بايد تنها به فضل خداى و بخشايش او شاد باشند، [كه‌] آن بهتر است از آنچه- از خواسته دنيا- فراهم مى‌آورند.'),
(1423, 10, 59, 'بگو: مرا خبر دهيد، آنچه خداى از روزى براى شما فرستاده است پس شما از آن حلالى و حرامى [به خواست خود] قرار داديد، بگو: آيا خدا شما را دستورى داده است يا بر خدا دروغ مى‌بنديد؟'),
(1424, 10, 60, 'و كسانى كه بر خدا دروغ مى‌بندند به روز رستاخيز چه گمان دارند- كه چه سزا بينند-؟ هر آينه خداوند داراى فزونى و بخشش است بر مردمان و ليكن بيشترشان سپاس نمى‌دارند.'),
(1425, 10, 61, 'و [تو] در هيچ حالى و كارى نباشى و از آن- از قرآن- هيچ برنخوانى، و [شما مردم‌] هيچ كارى نمى‌كنيد مگر اينكه آنگاه كه به آن مى‌پردازيد ما بر شما گواهيم و از پروردگارت همسنگ ذره‌اى در زمين و در آسمان پوشيده نيست، و نه كوچكتر از آن و نه بزرگتر، مگر آنكه در كتابى روشن- لوح محفوظ- هست.'),
(1426, 10, 62, 'آگاه باشيد كه دوستان خداى نه بيمى بر آنهاست و نه اندوهگين مى‌شوند'),
(1427, 10, 63, 'آنان كه ايمان آوردند و پرهيزگارى مى‌كردند.'),
(1428, 10, 64, 'ايشان را در زندگى اين جهان و در آن جهان مژدگان است، سخنان خداى را دگرگونى نيست. اين است رستگارى و كامگارى بزرگ.'),
(1429, 10, 65, 'و گفتار آنان تو را اندوهگين مكناد. زيرا كه همه عزت- توانايى و غلبه- خداى راست، كه اوست شنوا و دانا.'),
(1430, 10, 66, 'آگاه باشيد كه خداى راست هر كه در آسمانها و هر كه در زمين است. و كسانى كه جز خدا شريكانى [براى او] مى‌خوانند از چه پيروى مى‌كنند؟ پيروى نمى‌كنند مگر گمان را و جز به تخمين- يا دروغ- سخن نمى‌گويند.'),
(1431, 10, 67, 'اوست كه شب را براى شما پديد كرد تا در آن آرام گيريد و روز را روشن ساخت [تا در آن ببينيد و به كار پردازيد] همانا در آن براى گروهى كه بشنوند نشانه‌هاست.'),
(1432, 10, 68, '[مشركان‌] گفتند: خدا فرزندى گرفت پاك و منزه است او اوست بى‌نياز او راست آنچه در آسمانها و آنچه در زمين است. نزد شما هيچ حجتى بر اين [گفتار] نيست. آيا بر خدا چيزى مى‌گوييد كه نمى‌دانيد؟!'),
(1433, 10, 69, 'بگو: همانا كسانى كه بر خدا دروغ مى‌بندند رستگار نمى‌شوند.'),
(1434, 10, 70, 'آنها در اين جهان برخورداريى دارند سپس بازگشتشان به ماست، آنگاه به سزاى آنكه كفر مى‌ورزيدند عذاب سخت به آنان بچشانيم.'),
(1435, 10, 71, 'و داستان نوح را براى آنان بخوان، هنگامى كه به قوم خود گفت: اى قوم من، اگر قيام من- دعوت من به خدا، يا مقام پيامبرى من- و يادآورى من به آيات خدا بر شما گران است، من بر خدا توكل كردم، پس شما نيز كار خويش را فراهم و استوار كنيد و شريكان خود را [كه با خدا انباز مى‌گيريد] گرد آوريد تا كارتان بر شما پوشيده- يا مايه اندوه و نگرانيتان- نباشد آنگاه [هر چه در دل داريد و بتوانيد] در باره من كار بنديد و مهلتم مدهيد.'),
(1436, 10, 72, 'پس اگر رويگردان شويد [بدانيد كه‌] من از شما مزدى نمى‌خواهم. مزد من جز بر خداى نيست و فرمان يافته‌ام كه از گردن‌نهادگان و فرمانبرداران باشم.'),
(1437, 10, 73, 'پس او را دروغگو شمردند و ما او و كسانى را كه در كشتى با او بودند رهانيديم و آنان را جانشين [پيشينيان‌] ساختيم و كسانى را كه آيات ما را دروغ پنداشتند غرق كرديم. پس بنگر كه سرانجام آن بيم‌داده‌شدگان چگونه بود.'),
(1438, 10, 74, 'سپس از پس او پيامبرانى به سوى قومشان فرستاديم پس با حجتهاى روشن- معجزات- بديشان آمدند- يا حجتهاى روشن را بديشان آوردند-، ولى آنان بر آن نبودند كه بدانچه از پيش دروغ شمردند ايمان آورند. اينچنين بر دلهاى از حدگذرندگان مُهر مى‌نهيم.'),
(1439, 10, 75, 'سپس از پس ايشان موسى و هارون را با نشانه‌هاى خويش به سوى فرعون و مهترانش فرستاديم پس گردنكشى كردند و مردمى بزهكار بودند.'),
(1440, 10, 76, 'پس چون حق- معجزات و نشانه‌هاى روشن- از نزد ما بديشان آمد، گفتند: هر آينه اين جادويى است آشكار.'),
(1441, 10, 77, 'موسى گفت: آيا حق را هنگامى كه به شما آمد [جادو] مى‌گوييد؟ آيا اين جادوست و حال آنكه جادوگران پيروز و رستگار نمى‌شوند؟'),
(1442, 10, 78, 'گفتند: آيا نزد ما آمده‌اى تا ما را از آنچه پدرانمان را بر آن يافته‌ايم بگردانى و بزرگى در اين سرزمين شما دو تن را باشد؟ ما هرگز به شما ايمان نمى آوريم.'),
(1443, 10, 79, 'و فرعون گفت: هر جادوگر دانا و كارآزموده را نزد من آوريد.'),
(1444, 10, 80, 'چون جادوگران بيامدند موسى به آنها گفت: آنچه خواهيد افكند بيفكنيد.'),
(1445, 10, 81, 'پس چون [طنابها و چوبها] بيفكندند، موسى گفت: آنچه آورديد همه جادوست، همانا خداوند بزودى آن را باطل مى‌كند، كه خدا عمل تباهكاران را به سامان نمى‌آرد.'),
(1446, 10, 82, 'و خداى با سخنان- حكم يا نهادهاى- خود حق را ثابت و پايدار كند اگر چه بزهكاران خوش ندارند.'),
(1447, 10, 83, 'پس به موسى جز فرزندانى- ضعيفان و زيردستان- از قوم او ايمان نياوردند آنهم با ترسى از فرعون و مهترانشان كه [مبادا] شكنجه و عقوبتشان كند و همانا فرعون در زمين، سركش و برترى‌جوى و از گزافكاران بود.'),
(1448, 10, 84, 'و موسى گفت: اى قوم من، اگر به خداى ايمان آورده‌ايد، پس بر او توكل كنيد اگر مسلمانيد- گردن نهاده‌ايد-.'),
(1449, 10, 85, 'پس گفتند: بر خدا توكل كرديم پروردگارا، ما را مايه آزمون گروه ستمكاران- دستخوش شكنجه و آزار آنها- قرار مده.'),
(1450, 10, 86, 'و ما را به مِهر و بخشايش خود از گروه كافران برهان.'),
(1451, 10, 87, 'و به موسى و برادرش وحى كرديم كه براى قوم خود در مصر خانه‌هايى آماده سازيد و خانه‌هاى خود را پرستشگاه- يا روبروى هم- قرار دهيد و نماز را برپا داريد، و مؤمنان را مژده ده.'),
(1452, 10, 88, 'و موسى گفت: پروردگارا، همانا تو به فرعون و مهترانش در زندگى اين جهان زيورها و مالها داده‌اى، پروردگارا، تا [بدان وسيله مردم را] از راه تو گمراه كنند خداوندا، مالهاشان را نابود كن و بر دلهاشان سخت بند نِه كه ايمان نياورند تا عذاب دردناك را ببينند.'),
(1453, 10, 89, '[خداى‌] گفت: دعاى شما دو تن اجابت شد، پس استوار باشيد و هرگز از راه نادانان پيروى مكنيد.'),
(1454, 10, 90, 'و فرزندان اسرائيل را از دريا گذرانديم، پس فرعون و سپاهيانش از راه ستم و سركشى و تجاوز، آنان را دنبال كردند تا چون غرقاب او را بگرفت، گفت: ايمان آوردم كه خدايى نيست مگر آن كه فرزندان اسرائيل به او ايمان آوردند و من از گردن‌نهادگانم.'),
(1455, 10, 91, 'آيا اكنون؟! و حال آنكه پيش از اين نافرمانى كردى و از تباهكاران بودى.'),
(1456, 10, 92, 'پس امروز تن [بى‌جان‌] تو را بر بالا [ى ساحل‌] افكنيم- يا رهايى دهيم- تا پسينيانت را نشانه و عبرتى باشى، و هر آينه بسيارى از مردم از نشانه‌هاى ما بى‌خبرند.'),
(1457, 10, 93, 'و هر آينه ما فرزندان اسرائيل را در جايگاهى درست و نيكو جاى داديم و از چيزهاى پاكيزه روزيشان كرديم، و اختلاف نكردند تا آنكه آگاهى و دانش- دانش به حقانيت قرآن و پيامبر اسلام- به آنان رسيد. همانا پروردگار تو روز رستاخيز در ميان آنان در باره آنچه اختلاف مى‌كردند داورى خواهد كرد.'),
(1458, 10, 94, 'پس اگر در آنچه به تو فروفرستاديم شك دارى، از آنها كه كتاب [آسمانى‌] پيش از تو را مى‌خوانند بپرس- كه نشانه‌هاى حقانيت قرآن در آن كتابها هست-. هر آينه حق از سوى پروردگارت به تو آمد، پس از شك‌داران مباش.'),
(1459, 10, 95, 'و از كسانى مباش كه آيات ما را دروغ پنداشتند، كه از زيانكاران خواهى شد.'),
(1460, 10, 96, 'همانا كسانى كه سخن پروردگارت- وعده عذاب- بر آنان سزا گشت ايمان نمى‌آورند،'),
(1461, 10, 97, 'گر چه هر نشانه و معجزه‌اى بديشان آيد، تا آنكه عذاب دردناك را ببينند.'),
(1462, 10, 98, 'پس چرا هيچ آبادى و شهرى نبود كه مردمش ايمان بياورند و ايمانشان سودشان دهد مگر قوم يونس، كه چون ايمان آوردند عذاب خوارى و رسوايى را در زندگى اين جهان از آنان برداشتيم و آنان را تا هنگامى- تا زمان مرگشان- برخوردار كرديم.'),
(1463, 10, 99, 'و اگر پروردگار تو مى‌خواست هر آينه هر كه در زمين است همگيشان يكسره ايمان مى‌آوردند، پس آيا تو مردم را به ناخواه واميدارى تا مؤمن شوند؟'),
(1464, 10, 100, 'و هيچ كس را توان آن نيست كه ايمان بياورد مگر به خواست خدا، و [خداوند] پليدى- شك و ترديد- را بر كسانى مى‌نهد كه خِرد را كار نمى‌بندند.'),
(1465, 10, 101, 'بگو: بنگريد كه در آسمانها و زمين چه چيزهايى است و نشانه‌ها و بيم‌دادنها- يا بيم‌كنندگان- گروهى را كه ايمان نمى‌آورند چه سود دهد؟- يا سود نمى‌دهد-.'),
(1466, 10, 102, 'پس آيا جز همانند روزگار كسانى را كه پيش از آنها گذشته‌اند انتظار مى‌برند؟ بگو: منتظر باشيد كه من نيز با شما از منتظرانم.'),
(1467, 10, 103, 'سپس فرستادگانمان و كسانى را كه ايمان آورده‌اند مى‌رهانيم، بدين سان، از آن رو كه بر ما سزاست، مؤمنان را مى‌رهانيم.'),
(1468, 10, 104, 'بگو: اى مردم، اگر از [درستى‌] دين من به شك اندريد، پس [بدانيد كه‌] من آنان را كه شما به جاى خدا مى‌پرستيد نمى‌پرستم بلكه خدايى را مى‌پرستم كه جان شما را مى‌ستاند و فرمان يافته‌ام كه از مؤمنان [به آن خدا] باشم'),
(1469, 10, 105, 'و اينكه روى خود را براى دين حنيف- حق‌گرا و خالص يعنى يكتاپرستى- فرادار و از مشركان مباش.'),
(1470, 10, 106, 'و بجز خدا آنچه تو را سود ندارد و زيان نرساند مخوان، كه اگر كنى- بخوانى- آنگاه از ستمكاران باشى.'),
(1471, 10, 107, 'و اگر خدا گزندى به تو رساند، آن را جز او هيچ بازبرنده‌اى نيست و اگر براى تو نيكى بخواهد، پس فضل او را هيچ بازگرداننده‌اى نيست، آن را به هر كس از بندگان خود بخواهد مى‌رساند، و اوست آمرزگار و مهربان.'),
(1472, 10, 108, 'بگو: اى مردم، اينك شما را حقّ- قرآن يا دين حقّ- از سوى پروردگارتان آمد، پس هر كه راه يابد جز اين نيست كه به سود خويشتن راه مى‌يابد و هر كه گمراه شود همانا به زيان خويش گمراه مى‌شود، و من بر شما نگاهبان نيستم.'),
(1473, 10, 109, 'و آنچه را كه به تو وحى مى‌شود پيروى كن، و شكيبايى كن تا خداى داورى كند و او بهترين داوران است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الف، لام، را. [اين‌] كتابى است كه آيتهاى آن از نزد دانايى با حكمت و آگاه استوار و پايدار گشته- كه به كتابى ديگر منسوخ نشود- و آنگاه به تفصيل بيان شده است،'),
(1475, 11, 2, 'كه جز خداى را نپرستيد، كه من شما را از سوى او بيم‌كننده و نويددهنده‌ام،'),
(1476, 11, 3, 'و اينكه از پروردگارتان آمرزش بخواهيد و آنگاه به او باز گرديد تا شما را به بهره‌اى نيكو- زندگى نيك دنيا- تا سرآمدى نامبرده- پايان عمر مقدّر- برخوردارى دهد، و به هر بافضيلتى فزونى [پاداش‌] او را ببخشد. و اگر روى بگردانيد، من بر شما از عذاب روزى بزرگ بيمناكم.'),
(1477, 11, 4, 'بازگشت شما به سوى خداست و او بر همه چيز تواناست.'),
(1478, 11, 5, 'آگاه باشيد كه آنان'),
(1479, 11, 6, 'و هيچ جنبنده‌اى در زمين نيست مگر آنكه روزى او بر خداست و آنها را كه در قرارگاه خويش‌اند- چون ماهى در دريا- و آنها را كه در جايگاه موقّت‌اند- چون پرنده در فضا و جنين در رحم- همه را مى‌داند، همه در كتابى روشن- لوح محفوظ- هست.'),
(1480, 11, 7, 'و اوست آن [خداى‌] كه آسمانها و زمين را در شش روز- شش دوران مراد از روز، مطلق وقت است خواه كوتاه يا بسيار دراز- بيافريد و عرش او بر آب بود، تا شما را بيازمايد كه كدامتان نيكوكارتريد، و اگر بگويى كه شما پس از مرگ برانگيخته مى‌شويد هر آينه كسانى كه كافر شدند گويند: اين نيست مگر جادويى آشكار.'),
(1481, 11, 8, 'و اگر عذاب را از آنان تا مدّتى چند به تأخير اندازيم، [به ريشخند] خواهند گفت: چه چيز آن را باز مى‌دارد؟ آگاه باشيد، روزى كه آنان را فرارسد از آنها باز گردانده نشود و آنچه بدان ريشخند مى‌كردند آنان را فراگيرد.'),
(1482, 11, 9, 'و اگر آدمى را مهر و بخشايشى از سوى خويش بچشانيم و سپس آن را از او باز گيريم هر آينه نوميد و ناسپاس خواهد شد.'),
(1483, 11, 10, 'و اگر او را پس از گزندى كه به وى رسيده باشد نعمت و را حتى بچشانيم خواهد گفت: بديها و ناگواريها از من برفت همانا او شادمان و خودستاى است'),
(1484, 11, 11, 'مگر آنان كه شكيبايى ورزيدند و كارهاى نيك و شايسته كردند، كه آنان را آمرزش و مزدى بزرگ است.'),
(1485, 11, 12, 'پس شايد از اينكه [كافران مكه‌] مى‌گويند: چرا گنجى بر او فرونيامده يا فرشته‌اى با او نيامده، برخى از آنچه را به تو وحى مى‌شود فروگذارى و سينه‌ات از آن تنگ شود جز اين نيست كه تو بيم‌دهنده‌اى، و خدا بر هر چيز نگاهبان است.'),
(1486, 11, 13, 'بلكه مى‌گويند كه اين [قرآن‌] را خود بافته است بگو: اگر راستگوييد شما هم ده سوره بر بافته مانند اين بياريد و هر كه را جز خدا توانيد [به يارى‌] بخوانيد'),
(1487, 11, 14, 'پس اگر [آن ياوران،] شما را پاسخ ندهند، بدانيد كه جز اين نيست كه [اين قرآن‌] از دانش خدا فروآمده، و اينكه جز او خدايى نيست پس آيا شما مسلمان- گردن‌نهاده- ايد؟'),
(1488, 11, 15, 'هر كه زندگانى اين جهان و آرايش آن را خواهد، در اين جهان [پاداش‌] كارهايشان را بديشان تمام بدهيم و آنان در آن- زندگى دنيا- كم داده نشوند'),
(1489, 11, 16, 'اينانند كه در آن جهان جز آتش دوزخ ندارند، و آنچه در دنيا كرده‌اند ناچيز و بى‌بر شده و آنچه مى‌كردند تباه و نابود است.'),
(1490, 11, 17, 'پس آيا كسى كه بر حجّتى روشن از جانب پروردگار خويش است- يعنى رسول خدا'),
(1491, 11, 18, 'و كيست ستمكارتر از آن كه بر خدا دروغ مى‌بافد؟ اينان را بر خدايشان بگذرانند و گواهان گويند: اينهايند كه بر خداى خود دروغ بافتند. هان كه لعنت خدا بر ستمكاران باد،'),
(1492, 11, 19, 'آنان كه [مردم را] از راه خدا باز مى‌دارند و آن را كج مى‌خواهند- از آن عيب مى‌گيرند و آن را به كجى و انحراف از راستى وصف مى‌كنند-، و به سراى واپسين كافرند.'),
(1493, 11, 20, 'اينان در زمين ناتوان‌كننده [خداى از عذاب خود] نيستند و آنان را بجز خدا دوستان و سرپرستانى نيست، عذابشان دو چندان شود، كه نه مى‌توانستند [حق را] بشنوند و نه مى‌ديدند.'),
(1494, 11, 21, 'اينانند كه خويشتن را زيان زدند- خود را به هلاكت انداختند- و آنچه به دروغ مى‌بافتند از آنها گم شد.'),
(1495, 11, 22, 'ناگزير آنها در آن جهان از همه زيانكارترند.'),
(1496, 11, 23, 'همانا كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند و به پروردگار خويش دل نهادند، آنان بهشتيانند [و] در آن جاويدانند.'),
(1497, 11, 24, 'داستان اين دو گروه داستان كور و كر و بينا و شنواست، آيا داستان اين دو همسان است؟ [هرگز!] پس آيا ياد نمى‌كنند و پند نمى‌گيرند؟'),
(1498, 11, 25, 'و هر آينه نوح را به سوى قومش فرستاديم كه: من شما را بيم‌دهنده‌اى آشكارم'),
(1499, 11, 26, 'كه جز خداى يكتا را مپرستيد، كه من بر شما از عذاب روزى دردناك بيمناكم.'),
(1500, 11, 27, 'مهتران قوم او كه كفر مى‌ورزيدند گفتند: ما تو را جز بشرى مانند خود نمى‌بينيم، و نمى‌بينيم كه جز فرومايگان ما در ابتداى نظر- يعنى بى‌تفكر و تأمل- از تو پيروى كرده باشند، و براى شما هيچ برتريى بر خود نمى‌بينيم، بلكه شما را دروغگو مى‌پنداريم.'),
(1501, 11, 28, '[نوح‌] گفت: اى قوم من، مرا گوييد كه اگر بر حجتى از پروردگارم باشم- يعنى معجزات آورم- و مرا از جانب خويش رحمتى- علم و كتاب- داده باشد كه بر شما پوشيده گرديده است آيا [مى‌توانيم‌] شما را به [پذيرفتن‌] آن وادار كنيم و حال آنكه آن را خوش نداريد؟'),
(1502, 11, 29, 'و اى قوم من، در برابر آن- رساندن پيامهاى خداوند- از شما مالى نمى‌خواهم و مزد من جز بر خداى نيست، و كسانى را كه ايمان آورده‌اند از خود نمى رانم، كه آنان ديداركننده پروردگار خويشند- به معاد ايمان دارند- و ليكن شما را گروهى نادان و نابخرد مى‌بينم.'),
(1503, 11, 30, 'و اى قوم من، اگر آنان را برانم چه كسى مرا در برابر خدا يارى خواهد داد؟ پس آيا به ياد نمى‌آريد و پند نمى‌گيريد؟'),
(1504, 11, 31, 'و به شما نمى‌گويم كه گنجهاى خدا نزد من است و من غيب نمى‌دانم و نمى‌گويم من فرشته‌ام و در باره كسانى كه چشمهاى شما آنان را خوار مى‌نگرد نمى‌گويم كه خداوند هرگز به آنان نيكيى نمى‌دهد- خدا به آنچه در دلهاى ايشان است داناتر است- كه آنگاه از ستمكاران باشم.'),
(1505, 11, 32, 'گفتند: اى نوح، براستى با ما جدال كردى و بسيار هم جدال كردى، پس اگر راست مى‌گويى آنچه ما را وعده همى‌دهى بيار- يعنى عذاب را-.'),
(1506, 11, 33, 'گفت: بى‌گمان خداوند اگر بخواهد آن را به شما مى‌آورد و شما ناتوان‌كننده [او از عذاب‌كردن‌] نيستيد.'),
(1507, 11, 34, 'و اگر بخواهم شما را اندرز دهم، اندرزدادنم شما را سود ندهد اگر خداى بخواهد كه شما را [به كفرتان عقوبت كند و] به راه هلاكت افكند، اوست پروردگار شما و به او بازگردانده مى‌شويد.'),
(1508, 11, 35, 'بلكه مى‌گويند كه اين [قرآن‌] را بربافته است بگو: اگر من آن را بر بافته باشم بزهكارى من بر عهده خودم است، و من از بزهكارى شما بيزارم.'),
(1509, 11, 36, 'و به نوح وحى شد كه از قوم تو جز آنان كه ايمان آورده‌اند ديگر كسى ايمان نخواهد آورد. بنابراين از آنچه مى‌كردند اندوه مبر.'),
(1510, 11, 37, 'و كشتى را زير نظر ما و به وحى- راهنمايى- ما بساز و در باره كسانى كه ستم كردند با من سخن مگو، كه آنها غرق‌شدگانند.'),
(1511, 11, 38, 'و [نوح‌] كشتى را مى‌ساخت و هر گاه گروهى از قومش بر او مى‌گذشتند مسخره‌اش مى‌كردند. وى گفت: اگر ما را مسخره مى‌كنيد، ما نيز شما را مسخره خواهيم كرد چنانكه شما ما را مسخره مى‌كنيد'),
(1512, 11, 39, 'پس بزودى خواهيد دانست كه كيست آن كه عذابى بدو رسد كه وى را رسوا و خوار كند و عذابى پاينده بر او فرود آيد.'),
(1513, 11, 40, 'تا آنگاه كه فرمان ما آمد و [آب از] آن تنور جوشيد- كه نشانه عذاب بود- گفتيم: از هر جنسى يك جفت- نر و ماده- و خاندان خود را- مگر آن كس- همسر و فرزندت- كه سخن بر [هلاكت‌] او از پيش رفته است- و هر كس را كه ايمان آورده در آن'),
(1514, 11, 41, 'و گفت: در آن سوار شويد، كه رفتن و ايستادنش به نام خداست، همانا پروردگار من آمرزگار و مهربان است.'),
(1515, 11, 42, 'و آن'),
(1516, 11, 43, 'گفت: بزودى به كوهى پناه مى‌برم كه مرا از آب نگاه دارد. [نوح‌] گفت: امروز از فرمان- عذاب- خداى هيچ نگهدارنده‌اى نيست مگر آن كه [خدا] بر او رحم آرد. و موج ميان آن دو جدايى افكند، پس از غرق‌شدگان گشت.'),
(1517, 11, 44, 'و گفته شد: اى زمين آب خود فروبر و اى آسمان [از باريدن‌] باز ايست و آب كاسته گشت- فرورفت- و كار گزارده شد و [كشتى‌] بر [كوه‌] جودى آرام گرفت، و گفته شد: گروه ستمكاران را لعنت باد.'),
(1518, 11, 45, 'و نوح پروردگار خود را بخواند و گفت: پروردگارا، پسرم از خاندان من است و همانا وعده تو راست است- كه خاندانم را رهايى مى‌بخشى- و تو داورترين داورانى.'),
(1519, 11, 46, '[خداى‌] گفت: اى نوح، او از خاندان تو- خاندان نبوّت- نيست، او [را] كردارى ناشايسته است پس چيزى را كه بدان دانش ندارى از من مخواه من تو را پند مى‌دهم كه مبادا از نابخردان باشى.'),
(1520, 11, 47, 'گفت: پروردگارا، من به تو پناه مى‌برم از اينكه چيزى از تو بخواهم كه مرا بدان دانشى نيست. و اگر مرا نيامرزى و بر من نبخشايى از زيانكاران باشم.'),
(1521, 11, 48, 'گفته شد: اى نوح، [از كشتى‌] فرودآى با سلامت و بركتهايى از ما بر تو و بر گروه‌هايى از آنان كه با تو هستند، و گروه‌هايى هستند كه برخوردارشان خواهيم ساخت سپس عذاب دردناك از ما به آنها خواهد رسيد.'),
(1522, 11, 49, '[اى رسول ما،] اين از خبرهاى غيب است كه به تو وحى مى‌كنيم، كه پيش از اين نه تو آنها را مى‌دانستى و نه قوم تو پس شكيبايى ورز، كه سرانجام [نيك‌] از آن پرهيزگاران است.'),
(1523, 11, 50, 'و به سوى قوم عاد برادرشان هود را [فرستاديم‌]. گفت: اى قوم من، تنها خداى يكتا را بپرستيد، شما را جز او خدايى نيست شما جز دروغبافانى نيستيد- كه بر خدا به گرفتن شريك دروغ مى‌بنديد-.'),
(1524, 11, 51, 'اى قوم من، از شما مزدى بر آن- رسالت- نمى‌خواهم مزد من نيست مگر بر آن كس كه مرا آفريده است، آيا خرد را كار نمى‌بنديد؟'),
(1525, 11, 52, 'و اى قوم من، از پروردگارتان آمرزش بخواهيد آنگاه به او باز گرديد- توبه كنيد- تا باران را بر شما پياپى و فراوان فرستد، و نيرويى بر نيرويتان بيفزايد، و بزهكارانه پشت مكنيد و روى مگردانيد.'),
(1526, 11, 53, 'گفتند: اى هود، ما را دليلى روشن- كه مورد قبول ما باشد- نياوردى، و ما به گفتار تو از خدايانمان دست بردار نيستيم و ما به تو نخواهيم گرويد'),
(1527, 11, 54, 'و جز اين نمى‌گوييم كه برخى از خدايان ما به تو گزندى رسانده‌اند- يعنى ديوانه شده‌اى كه مى‌گويى خدا يكى است-. [هود] گفت: خداى را گواه مى گيرم و شما نيز گواه باشيد كه من از آنچه [براى او] انباز مى‌گيريد بيزارم'),
(1528, 11, 55, 'از غير او- خداى يگانه-، پس همگى در باره من چاره‌انديشى و بدسگالى كنيد و مهلتم مدهيد'),
(1529, 11, 56, 'من بر خداى يكتا، پروردگار من و پروردگار شما، توكل كردم هيچ جنبنده‌اى نيست مگر آنكه او گيرنده موى پيشانى اوست- در حيطه قدرت و فرمانروايى خداست- همانا پروردگار من بر راه راست است.'),
(1530, 11, 57, 'پس اگر روى بگردانيد براستى آنچه را به آن به سوى شما فرستاده شده‌ام به شما رسانيدم و پروردگارم قومى غير شما را جانشين [شما] مى‌كند، و شما هيچ زيانى به او نمى‌رسانيد همانا پروردگار من بر همه چيز نگاهبان است.'),
(1531, 11, 58, 'و چون فرمان ما- عذاب- بيامد هود و كسانى را كه با او ايمان آوردند به مِهر و بخشايش خويش رهانيديم، و ايشان را از عذابى سخت و دشوار رهايى داديم.'),
(1532, 11, 59, 'اين قوم عاد بودند كه نشانه‌هاى پروردگارشان را انكار كردند و پيامبران او را نافرمانى نمودند و فرمان هر گردنكش ستيزه‌گرى را پيروى كردند.'),
(1533, 11, 60, 'و در اين جهان و در روز رستاخيز در پى آنان نفرينى روانه شود آگاه باشيد كه عاديان به پروردگارشان كافر شدند. هان! دورى [از رحمت خدا] بر عاديان، قوم هود، باد!'),
(1534, 11, 61, 'و به قوم ثمود برادرشان صالح را [فرستاديم‌]. گفت: اى قوم من، خداى يكتا را بپرستيد، شما را جز او خدايى نيست او شما را از زمين آفريد و شما را در آن زندگانى داد- يا شما را به آبادانى آن واداشت- پس، از او آمرزش بخواهيد و آنگاه به سوى او بازگرديد، كه پروردگار من نزديك است و پاسخ دهنده.'),
(1535, 11, 62, 'گفتند: اى صالح، پيش از اين تو در ميان ما مايه اميدوارى بودى آيا ما را از اينكه آنچه را پدرانمان مى‌پرستيدند بپرستيم باز مى‌دارى؟! همانا از آنچه ما را بدان مى‌خوانى سخت به شك اندريم.'),
(1536, 11, 63, 'گفت: اى قوم من، مرا گوييد كه اگر بر حجتى روشن از پروردگار خويش باشم و مرا از نزد خود رحمتى داده باشد، اكنون اگر او را نافرمانى كنم چه كسى مرا در برابر خدا يارى مى‌كند؟ پس شما مرا جز زيانكارى نمى‌افزاييد.'),
(1537, 11, 64, 'و اى قوم من، اين ماده‌شتر خدا براى شما نشانه‌اى است، پس واگذاريدش تا در زمين خدا بخورد، و گزندى به او مرسانيد كه شما را عذابى نزديك بگيرد.'),
(1538, 11, 65, 'پس آن را پِى كردند- دست و پا بريدند و كشتند- پس [صالح‌] گفت: سه روز در خانه‌هاتان- يا در شهرتان- برخوردار شويد اين وعده‌اى است كه دروغ نيست.'),
(1539, 11, 66, 'پس چون فرمان ما آمد صالح و كسانى را كه با او ايمان آوردند به مِهر و بخشايشى از خود، [از عذاب‌] و از رسوايى و خوارى آن روز رهانيديم، كه پروردگار تو نيرومند و تواناى بى‌همتاست.'),
(1540, 11, 67, 'و كسانى را كه ستم كردند صيحه- بانگ كشنده آسمانى- فروگرفت، پس در خانه‌هاى خود بر روى در افتادند- مردند-.'),
(1541, 11, 68, 'چنانكه گويى هرگز در آنجا نبودند آگاه باشيد كه قوم ثمود به خداى خود كافر شدند. هان كه ثمود را دورى [از رحمت پروردگار] باد!'),
(1542, 11, 69, 'و هر آينه فرستادگان ما- فرشتگان- ابراهيم را مژده- به داشتن پسر- آورده، سلام گفتند گفت: سلام، و بى‌درنگ گوساله‌اى بريان آورد.'),
(1543, 11, 70, 'پس چون ديد كه دستشان به آن نمى‌رسد، آنان را ناشناخت و بيگانه شمرد و از آنها بيمى به دل گرفت. گفتند: مترس كه ما به قوم لوط فرستاده شده ايم.'),
(1544, 11, 71, 'و زن او ايستاده بود، خنديد پس او را به اسحاق و از پس اسحاق به يعقوب مژده داديم.'),
(1545, 11, 72, 'گفت: واى بر من، آيا مى‌زايم و حال آنكه من پيرزنى هستم و اين شوهرم پير است؟! براستى اين چيزى شگفت‌انگيز است!'),
(1546, 11, 73, 'گفتند: آيا از كار خدا شگفتى مى‌نمايى و حال آنكه مِهر و بخشايش خدا و بركات او بر شما خاندان است؟ براستى او ستوده و بزرگوار است.'),
(1547, 11, 74, 'پس چون آن ترس از ابراهيم برفت و او را مژدگان رسيد، در باره قوم لوط با ما به گفت‌وگو- درخواست برداشتن عذاب- پرداخت.'),
(1548, 11, 75, 'هر آينه ابراهيم بردبار و بسيار آه‌كشنده و بازآينده [به درگاه ما] بود.'),
(1549, 11, 76, 'اى ابراهيم، از اين [سخن‌] بگرد، كه فرمان پروردگارت بيامده، و همانا بر آنان عذابى برگشت‌ناپذير آمدنى است.'),
(1550, 11, 77, 'و چون فرستادگان ما- فرشتگان- نزد لوط آمدند به [آمدن‌] آنها اندوهگين شد و در كارشان در تنگنا ماند و گفت: اين روزى سخت و سهمگين است.'),
(1551, 11, 78, 'و قومش شتابان به سوى او آمدند، و پيش از آن كارهاى بد و زشت مى‌كردند. گفت: اى قوم من، اين دختران من- كه مى‌توانيد با آنها ازدواج كنيد- آنها براى شما پاكيزه‌ترند پس، از خدا بترسيد و مرا در [روى‌] مهمانانم رسوا و شرمسار مكنيد آيا در ميان شما مردى خردمند و راه‌يافته نيست؟'),
(1552, 11, 79, 'گفتند: تو خود مى‌دانى كه ما را به دختران تو حقى- رغبت و نيازى- نيست، و تو خود مى‌دانى كه ما چه مى‌خواهيم.'),
(1553, 11, 80, 'گفت: كاش در برابر شما نيرويى مى‌داشتم يا مى‌توانستم به تكيه‌گاهى استوار- مانند قوم و عشيره براى يارى‌گرفتن- پناه ببرم- تا شما را از مهمانان خود دفع كنم-.'),
(1554, 11, 81, '[فرشتگان‌] گفتند: اى لوط، ما فرستادگان پروردگار توايم، آنها هرگز به تو دست نيابند پس خاندان خود را در پاسى از شب بيرون بر، هيچ يك از شما به واپس ننگرد- به شتاب بيرون رويد-، مگر زنت را كه بدو آن رسد كه به آنان خواهد رسيد. وعده‌گاه آنها بامداد است، آيا بامداد نزديك نيست؟'),
(1555, 11, 82, 'پس چون فرمان ما- عذاب- بيامد آنجا را زير و زبر كرديم و بر آن [ديار] بارانى از سنگ گل بر هم نشسته- كلوخ- باريديم،'),
(1556, 11, 83, '[سنگهايى‌] نشان كرده نزد پروردگارت، و آن از ستمكاران دور نيست.'),
(1557, 11, 84, 'و به [اهل‌] مدين برادرشان شعيب را [فرستاديم‌]. گفت: اى قوم من، خداى يكتا را بپرستيد، شما را جز او خدايى نيست و پيمانه و ترازو را مكاهيد. من شما را در خوشى و آسايش مى‌بينم و بر شما از عذاب روزى فراگير مى‌ترسم.'),
(1558, 11, 85, 'و اى قوم من، پيمانه و ترازو را به انصاف و داد، تمام دهيد و از مردم چيزهايشان- كالايشان- را مكاهيد و در زمين به تباهكارى مكوشيد.'),
(1559, 11, 86, 'اگر ايمان داريد، باقى گذاشته خداى [از حلال‌]- آن مال اندك كه پس از تمام دادن پيمانه و ترازو، خدا به شما ارزانى دارد- براى شما بهتر است و من بر شما نگهبان نيستم.'),
(1560, 11, 87, 'گفتند: اى شعيب، آيا نمازت به تو فرمان مى‌دهد كه ما آنچه را پدرانمان مى پرستيدند واگذاريم يا اينكه در مالهاى خويش نه آن كنيم كه مى‌خواهيم؟ تو كه بردبار و خردمندى راه‌يافته‌اى!'),
(1561, 11, 88, 'گفت: اى قوم من، مرا گوييد اگر من بر حجت و دليلى روشن از پروردگارم باشم و او مرا روزى نيكو- نبوّت- داده باشد [آيا دعوت من نابخردانه است؟] و من نمى‌خواهم در آنچه شما را از آن باز مى‌دارم با شما مخالفت كنم- شما را باز دارم و خود مرتكب آن شوم- جز اصلاح [كارتان‌] چندان كه بتوانم نمى خواهم، و توفيق من جز به [فضل و خواست‌] خداوند نيست بر او توكل كردم و به او بازمى‌گردم.'),
(1562, 11, 89, 'و اى قوم من، مخالفت با من شما را بر آن ندارد [كه كارى كنيد] كه به شما آن رسد كه به قوم نوح يا قوم هود يا قوم صالح رسيد، و قوم لوط از شما دور نيستند.'),
(1563, 11, 90, 'و از پروردگارتان آمرزش بخواهيد و آنگاه به او بازگرديد- توبه كنيد-، كه پروردگار من مهربان و دوستدار [تائبان‌] است.'),
(1564, 11, 91, 'گفتند: اى شعيب، بسيارى از آنچه را كه گويى در نمى‌يابيم و هر آينه تو را در ميان خود ناتوان مى‌بينيم، و اگر كسان و خويشانت نبودند سنگسارت مى‌كرديم و تو نزد ما توانمند- يا گرامى- نيستى.'),
(1565, 11, 92, 'گفت: اى قوم من، آيا كسان و خويشان من، نزد شما از خدا توانمندتر- يا گرامى‌تر- اند؟! و او'),
(1566, 11, 93, 'و اى قوم من، شما بر جاى خود- آنچه توانيد يا به روش خود- عمل كنيد، من نيز عمل‌كننده‌ام بزودى خواهيد دانست كه كيست آن كه به او عذابى رسد كه او را رسوا و خوار كند و كيست آن كه دروغگوست و چشم به راه باشيد كه من نيز با شما چشم به را هم.'),
(1567, 11, 94, 'و چون فرمان ما- عذاب- بيامد، شعيب و كسانى را كه با او ايمان آوردند به مِهر و بخشايش خود رهانيديم و آنان را كه ستم كردند صيحه- بانگ كشنده آسمانى- بگرفت، پس در خانه‌هاى خويش به روى در افتادند- مردند-،'),
(1568, 11, 95, 'چنانكه گويى هرگز در آنجا نبودند، هان كه نابودى و تباهى باد اهل مدين را، چنانكه ثمود نابود شدند.'),
(1569, 11, 96, 'و هر آينه موسى را با نشانه‌هاى خود و حجتى روشن فرستاديم،'),
(1570, 11, 97, 'به سوى فرعون و مهترانش، پس اينان فرمان فرعون را پيروى كردند و حال آنكه فرمان فرعون بر راه راست نبود.'),
(1571, 11, 98, 'در روز رستاخيز قوم خويش را پيشوا شود و آنها را به آتش دوزخ در آورد، و بد در آمدن جايى است.'),
(1572, 11, 99, 'و در پى آنان در اين جهان و در روز رستاخيز نفرينى روانه شود، [و] بد بخششى است كه به آنها بخشند.'),
(1573, 11, 100, 'اين از خبرهاى آن آباديها و شهرهاست كه بر تو بر مى‌گوييم، برخى از آنها برجا و برپاست و برخى درويده [و ويران‌].'),
(1574, 11, 101, 'و ما بر آنها ستم نكرديم بلكه آنها بر خويشتن ستم كردند پس خدايانشان، كه به جاى خداى يكتا مى‌خواندند، آنگاه كه فرمان پروردگارت- عذاب- بيامد برايشان هيچ سودى نداشتند و آنان را جز هلاكت و تباهى نيفزودند.'),
(1575, 11, 102, 'و اينچنين است گرفتن پروردگار تو آنگاه كه [مردم‌] آباديها و شهرها را كه ستمكار بودند گرفت همانا گرفتن او دردناك و سخت است.'),
(1576, 11, 103, 'هر آينه در اين [سرگذشتها] نشانه‌اى و عبرتى است براى كسى كه از عذاب آن جهان بترسد. آن، روزى است كه مردم را براى آن فراهم آرند و آن، روز حضور همگان است.'),
(1577, 11, 104, 'و آن را واپس نمى‌داريم مگر تا مدتى بر شمرده- معين-.'),
(1578, 11, 105, 'روزى كه [آن مدت‌] فرا رسد هيچ كس جز به اذن او سخن نگويد، پس برخى از آنان بدبخت باشند و برخى نيك‌بخت.'),
(1579, 11, 106, 'اما كسانى كه بدبخت شدند در آتشند، كه در آنجا ناله‌اى زار و فريادى چون بانگ خران- دم و بازدمى با آه و ناله و فرياد و اندوه- دارند،'),
(1580, 11, 107, 'تا آسمانها و زمين هست در آنجا جاويدانند مگر آنكه پروردگار تو خواهد، كه پرودگارت هر چه خواهد مى‌كند.'),
(1581, 11, 108, 'و اما كسانى كه نيك‌بخت شوند پس در بهشت جاويدانند تا آسمانها و زمين هست مگر آنكه پروردگار تو خواهد، بخششى هميشگى و نابريده.'),
(1582, 11, 109, 'پس، از آنچه آنان مى‌پرستند در گمان مباش نمى‌پرستند مگر همان گونه كه پدرانشان از پيش مى‌پرستيدند و ما بهره آنان را تمام و بى‌كم و كاست بديشان مى دهيم.'),
(1583, 11, 110, 'و هر آينه به موسى كتاب داديم، پس در آن اختلاف پديد آمد. و اگر از پروردگار تو سخنى [به تأخير عذاب‌] از پيش نرفته بود، هر آينه ميانشان داورى شده بود، و همانا آنان در باره آن سخت در شك‌اند.'),
(1584, 11, 111, 'و پروردگار تو هر آينه [پاداش‌] كارهاى همه آنان را تمام مى‌دهد، كه او به آنچه مى‌كنند آگاه است.'),
(1585, 11, 112, 'پس چنانكه فرمان يافته‌اى استوار و پايدار باش، و [نيز] آنان كه با تو توبه كرده‌اند- يعنى مؤمنانى كه با تو به درگاه خدا روى آورده‌اند يا از شرك و كفر بازگشته و ايمان آورده‌اند- و سركشى مكنيد كه او بدانچه مى‌كنيد بيناست.'),
(1586, 11, 113, 'و به كسانى كه ستم كردند مگراييد- به آنان پشتگرم مشويد- كه آنگاه آتش دوزخ به شما هم خواهد رسيد و براى شما جز خدا هيچ دوست و سرپرستى نباشد و آنگاه يارى نشويد.'),
(1587, 11, 114, 'و نماز را برپا دار و در دو طرف روز- نماز بامداد و نماز ظهر و عصر- و در ساعاتى از آغاز شب- نماز شام و نماز خفتن'),
(1588, 11, 115, 'و شكيبايى كن كه خدا مزد نيكوكاران را تباه نمى‌كند.'),
(1589, 11, 116, 'پس چرا در ميان مردمانى كه پيش از شما بودند، جز اندكيشان كه آنان را [از عذاب‌] رهانيديم، خردمندان و نيكان بافضيلتى نبودند كه از تباهكارى در زمين باز دارند؟ و كسانى كه ستم كردند در پى آنچه در آن كامرانى يافتند رفتند- از آرزوهاى نفس پيروى كرده همه كوشش خود را صرف به دست آوردن اسباب شهوات گردانيدند- و بزهكار بودند.'),
(1590, 11, 117, 'و پروردگار تو بر آن نبود كه آباديها و شهرهايى را به ستم نابود كند در حالى كه مردم آنها شايسته‌كار باشند.'),
(1591, 11, 118, 'و اگر پروردگار تو مى‌خواست همه مردم را يك امّت- بر يك دين- كرده بود ولى پيوسته در اختلاف و جدايى خواهند بود،'),
(1592, 11, 119, 'مگر كسانى كه پروردگار تو بر آنها مِهر و بخشايش آورد، و براى همين آفريدشان و سخن پروردگارت [بر اين‌] تمام شد كه: هر آينه دوزخ را از همه پريان و آدميان [كافر] پُر مى‌كنم.'),
(1593, 11, 120, 'و از خبرهاى پيامبران اين همه را بر تو مى‌گوييم آنچه را كه بدان دل تو را بر جاى و استوار بداريم، و تو را در اين [سرگذشتها] آنچه حقّ است و مؤمنان را پند و يادآورى است آمده است.'),
(1594, 11, 121, 'و به كسانى كه ايمان نمى‌آورند بگو: بر جاى خود- بر توانايى خود يا بر روش خود- عمل كنيد كه ما نيز عمل‌كننده‌ايم'),
(1595, 11, 122, 'و منتظر باشيد كه ما هم منتظريم.'),
(1596, 11, 123, 'و خداى راست نهان و ناپيداى آسمانها و زمين و بازگشت همه كارها به اوست، پس او را بپرست و بر او توكّل كن، و پروردگار تو از آنچه مى‌كنيد غافل نيست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الف، لام، را. اين است آيه‌هاى كتاب روشن و روشن‌كننده.'),
(1598, 12, 2, 'همانا ما آن را قرآنى به زبان تازى فرو فرستاديم تا مگر شما- كه تازى هستيد- به خرد دريابيد.'),
(1599, 12, 3, 'ما با اين قرآن كه به تو وحى كرديم نيكوترين داستان را بر تو بر مى‌گوييم و هر آينه تو پيش از آن از بى‌خبران بودى.'),
(1600, 12, 4, 'آنگاه كه يوسف پدر خويش را گفت: اى پدر، من در خواب يازده ستاره و مهر و ماه را ديدم كه مرا سجده مى‌كنند.'),
(1601, 12, 5, 'گفت: اى پسرك من، خواب خود را به برادرانت باز مگو، كه [از روى حسد] در باره تو بدانديشى مى‌كنند، زيرا كه شيطان آدمى را دشمنى هويداست'),
(1602, 12, 6, 'و اينچنين پروردگارت تو را برمى‌گزيند و از تعبير خوابها- وقوع و سرانجام آنها- به تو مى‌آموزد و نعمت خويش را بر تو و بر خاندان يعقوب تمام مى‌كند چنانكه آن را پيش از اين بر دو پدرت، ابراهيم و اسحاق، تمام كرد همانا پروردگار تو دانا و استواركار است.'),
(1603, 12, 7, 'براستى در [سرگذشتِ‌] يوسف و برادرانش نشانه‌ها- نشانه‌هاى قدرت و حكمت خداوند يا هدايت- و عبرتها براى پرسندگان است.'),
(1604, 12, 8, 'آنگاه كه گفتند: يوسف و برادرش- بنيامين، برادر هم‌مادر يوسف- نزد پدرمان از ما كه گروهى نيرومنديم محبوب‌ترند همانا پدر ما [در اين مهرورزى‌] در گمراهى آشكار است.'),
(1605, 12, 9, 'يوسف را بكشيد يا او را به سرزمينى بيفكنيد تا روى- توجّه- پدرتان تنها براى شما باشد و پس از او [با توبه‌كردن‌] گروهى نيك و شايسته باشيد.'),
(1606, 12, 10, 'گوينده‌اى از آنان گفت: يوسف را نكشيد، و [اگر كارى خواهيد كرد] او را در تاريكى بن چاه بيفكنيد تا برخى از كاروانيان او را برگيرند.'),
(1607, 12, 11, 'گفتند: اى پدر، تو را چيست كه ما را بر يوسف امين نمى‌شمارى و حال آنكه هر آينه ما نيكخواه اوييم؟'),
(1608, 12, 12, 'فردا او را با ما بفرست تا در چمنزار بگردد و بازى كند و همانا ما او را نگهبانيم.'),
(1609, 12, 13, 'گفت: اينكه او را ببريد بى‌گمان مرا اندوهگين مى‌كند و مى‌ترسم كه گرگ او را بخورد و شما از او غافل باشيد.'),
(1610, 12, 14, 'گفتند: اگر گرگ او را بخورد در حالى كه ما گروهى نيرومنديم آنگاه براستى زيانكار باشيم.'),
(1611, 12, 15, 'پس چون او را بردند و همداستان شده دل بنهادند كه او را در بن چاه افكنند، [به اين كار دست يازيدند] و ما به او وحى كرديم كه هر آينه تو آنان را بدين كارشان آگاه خواهى كرد در حالى كه آنها درنيابند [كه تو يوسفى‌]- يا: در حالى كه آنان از اين وحى و برطرف‌كردن وحشت از او آگاهى ندارند-.'),
(1612, 12, 16, 'و شبانگاه گريه كنان نزد پدرشان آمدند'),
(1613, 12, 17, 'گفتند: اى پدر، ما به مسابقه مى‌دويديم و يوسف را نزد كالاى خود گذاشته بوديم كه گرگ او را خورد، و تو [سخن‌] ما را باور ندارى اگر چه راستگو باشيم'),
(1614, 12, 18, 'و خونى دروغين را بر پيراهنش آوردند- يعنى پيراهن يوسف را به خون آلوده ساخته به دروغ، نزد پدر آوردند- [يعقوب‌] گفت: [چنين نيست‌] بلكه نفس شما كارى [بزرگ‌] را در نظرتان آراسته و آسان نموده است پس [به هر حال مرا] شكيبايى نيكوست، و خداست كه بايد بر آنچه بيان مى‌كنيد از او يارى خواست.'),
(1615, 12, 19, 'و كاروانى بيامد، پس آب‌آورشان را فرستادند، و او دلو خويش [در چاه‌] فروانداخت گفت: اى مژدگان! اين پسرى است. و او را همچون كالايى [براى فروش‌] پنهان ساختند، و خدا به آنچه مى‌كردند دانا بود.'),
(1616, 12, 20, 'و او را به بهايى اندك، دِرمى چند، فروختند و در باره او بى‌رغبت بودند.'),
(1617, 12, 21, 'و آن كس از [مردم‌] مصر- يعنى عزيز مصر- كه او را بخريد زن خويش- زليخا- را گفت: جايگاهش گرامى دار، شايد ما را سود دهد يا او را به فرزندى گيريم. و بدين سان يوسف را در آن سرزمين جاى [و توان‌] داديم [تا آنچه مى‌خواستيم تحقّق بخشيم‌] و تا او را از تعبير خوابها- يعنى سرانجام و وقوع آنها- بياموزيم. و خداوند بر كار خويش چيره و تواناست و ليكن بيشتر مردم نمى‌دانند.'),
(1618, 12, 22, 'و چون باليد و به نيروى جوانى خود رسيد او را حكم- نبوّت يا حكمت- و دانش داديم، و نيكوكاران را چنين پاداش مى‌دهيم.'),
(1619, 12, 23, 'و آن زن كه او در خانه‌اش بود از وى كام خواست، و درها را ببست و گفت: بيا [كه تو را آماده‌ام‌]. گفت: پناه بر خدا! كه او پروردگار من است، جايگاهم را نيكو داشته براستى كه ستمكاران رستگار نشوند.'),
(1620, 12, 24, 'و هر آينه آن زن آهنگ او كرد، و اگر نه آن بود كه او برهان پروردگار خويش بديد آهنگ وى كرده بود. اينچنين [برهان خويش به او نموديم‌] تا بدى و زشتكارى را از او بگردانيم، كه او از بندگان ويژه و برگزيده ما بود.'),
(1621, 12, 25, 'و آن دو به سوى در از يكديگر پيشى گرفتند، و آن زن پيراهن او را از پشت دريد، و شوهر آن زن را نزديك در بيافتند. زن گفت: سزاى كسى كه به خانواده تو آهنگ بدى كرده باشد چيست، جز اينكه زندانى شود يا شكنجه‌اى دردناك بيند؟'),
(1622, 12, 26, '[يوسف‌] گفت: او از من كام خواست و گواهى از كسان آن زن گواهى داد كه اگر پيراهن او'),
(1623, 12, 27, 'و اگر پيراهنش از پشت دريده شده آن زن دروغگوست و او راستگوست.'),
(1624, 12, 28, 'پس چون پيراهن او را ديد كه از پشت دريده شده، گفت: اين از مكر شما زنان است، براستى مكر شما بزرگ است.'),
(1625, 12, 29, 'اى يوسف، از اين [رويداد] درگذر، و تو هم براى گناه خويش آمرزش بخواه، كه از لغزشكاران بوده‌اى.'),
(1626, 12, 30, 'و زنانى در شهر گفتند: زن عزيز از غلام خود كام مى‌خواهد، همانا او'),
(1627, 12, 31, 'چون نيرنگ [و بدگويى‌] آنان را شنيد، به نزدشان كس فرستاد و از بهر آنان تكيه‌گاهى آماده ساخت و به هر كدام از آنان كاردى [براى بريدن ميوه‌] داد و [يوسف را] گفت: بر آنها بيرون آى. همين كه او را ديدند، [براى زيبايى شگفتش‌] بزرگش يافتند و دستهاى خويش [به جاى ميوه‌] بريدند و گفتند: پاكا خدايا! اين نه آدمى است، اين جز فرشته‌اى بزرگوار نيست!'),
(1628, 12, 32, 'گفت: اين همان است كه مرا در باره او سرزنش مى‌كرديد، و همانا من از او كام خواستم، پس او خويشتن نگاه داشت و اگر آنچه مى‌فرمايمش نكند بى گمان زندانى مى‌شود و هر آينه از خوارشدگان باشد.'),
(1629, 12, 33, '[يوسف‌] گفت: پروردگارا، زندان نزد من خوشتر است از آنچه مرا بدان مى‌خوانند، و اگر نيرنگ آن زنان را از من نگردانى به آنها گرايم و از نادانان باشم.'),
(1630, 12, 34, 'پس پروردگارش او را پاسخ داد و نيرنگشان را از او بگردانيد، كه اوست شنوا و دانا.'),
(1631, 12, 35, 'آنگاه پس از آنكه نشانه‌ها را ديدند- با اينكه به پاكدامنى يوسف پى بردند- به نظرشان رسيد- رأيشان بر اين قرار گرفت- كه او را تا مدّتى به زندان افكنند.'),
(1632, 12, 36, 'و با او دو جوان- از غلامان شاه- در زندان شدند. يكى از آنها گفت: من [در خواب‌] خود را ديدم كه انگور مى‌فشارم [تا شراب بسازم‌]، و ديگرى گفت: من خود را ديدم كه بر سرم نانى مى‌برم كه پرندگان از آن مى‌خورند. ما را به تأويل- تعبير و سرانجام- آن آگاه كن، كه تو را از نيكوكاران مى‌بينيم.'),
(1633, 12, 37, 'گفت: هيچ خوردنى كه روزى شما سازند شما را نرسد مگر به تأويل آن- تعبير و سرانجام خواب يا آن خوردنى- پيش از آنكه به شما رسد آگاهتان كنم. اين [خبر غيبى‌] از آنهاست كه پروردگارم مرا آموخته است، زيرا كه من كيش گروهى را كه به خدا ايمان نمى‌آورند و جهان واپسين را باور ندارند رها كرده‌ام'),
(1634, 12, 38, 'و كيش پدرانم ابراهيم و اسحاق و يعقوب را پيروى كرده‌ام ما را نرسد كه چيزى را با خدا انباز گيريم اين از فزون‌بخشى خداى بر ما و بر مردم است و ليكن بيشتر مردم سپاس نمى‌دارند.'),
(1635, 12, 39, 'اى دو يار زندانى من، آيا خدايان پراكنده بهترند يا آن خداى يگانه چيره‌شونده بر همه؟'),
(1636, 12, 40, 'شما جز او'),
(1637, 12, 41, 'اى دو يار زندانى من، يكى از شما خواجه خود را شراب دهد و آن ديگرى بر دار آويخته گردد و پرندگان از سر او بخورند [فرمان‌] كارى كه در باره آن نظر مى خواستيد گزارده شده است.'),
(1638, 12, 42, 'و به يكى از آن دو، كه دانست رها مى‌شود، گفت: مرا نزد خواجه خويش ياد كن ولى شيطان يادآورى خواجه‌اش را فراموشش ساخت پس [يوسف‌] سالى چند در زندان بماند.'),
(1639, 12, 43, 'و [روزى‌] شاه گفت: من در خواب ديدم كه هفت گاو فربه را هفت گاو لاغر مى‌خورند، و هفت خوشه سبز و هفت خوشه ديگر خشك [ديدم‌] اى مهتران! مرا در باره خوابم نظر بدهيد اگر شما خوابگزاريد.'),
(1640, 12, 44, 'گفتند: خوابهايى آشفته و پريشان است و ما به تعبير خوابهاى پريشان دانا نيستيم.'),
(1641, 12, 45, 'و آن كس، از آن دو، كه رهايى يافته بود و پس از مدتها به يادش آمد گفت: من شما را از تعبير آن آگاه مى‌كنم، پس مرا [نزد يوسف‌] بفرستيد.'),
(1642, 12, 46, 'يوسفا! اى بسيار راستگو، ما را درباره هفت گاو فربه كه هفت گاو لاغر آنها را مى‌خورند و هفت خوشه سبز و هفت خوشه ديگر خشك نظر ده، باشد كه نزد مردم باز گردم، شايد [سرانجام خواب را] بدانند.'),
(1643, 12, 47, 'گفت: هفت سال پياپى كشت كنيد، و آنچه را درويديد، جز اندكى كه مى‌خوريد، در خوشه‌اش بگذاريد'),
(1644, 12, 48, 'آنگاه پس از آن، هفت سال سخت بيايد كه آنچه از پيش براى آنها نهاده باشيد بخورند مگر اندكى از آن كه [براى كشت‌] نگه مى‌داريد.'),
(1645, 12, 49, 'سپس، از پى آن سالى بيايد كه در آن براى مردمان باران مى‌بارد- يا گياهان مى‌رويد، يا مردم از تنگى و سختى مى‌رهند- و در آن [ميوه- انگور، زيتون، ...] مى‌فشرند.'),
(1646, 12, 50, 'و شاه گفت: او را نزد من آريد، پس چون فرستاده نزد وى آمد، [يوسف‌] گفت: سوى خواجه‌ات باز گرد و از او بپرس كه حال و كار آن زنان كه دستهاى خويش بريدند چه بوده است- چرا دست خود بريدند-؟ همانا پروردگار من به مكر آنها داناست.'),
(1647, 12, 51, '[شاه، آن زنان را] گفت: شما را چه بوده است آنگاه كه از يوسف كام خواستيد؟ گفتند: پاك است خدا، ما بر او هيچ بدى نمى‌دانيم زن عزيز- زليخا- گفت: اكنون حق پديدار شد، من از او كام خواستم، و او از راستگويان است.'),
(1648, 12, 52, '[يوسف به آن يار زندانى گفت:] اين- درخواست من و بيرون نشدن از زندان- براى آن است تا [عزيز] بداند كه من در نهان به او خيانت نكرده‌ام، و خداوند نيرنگ خائنان را رهبرى نمى‌كند- به مقصد نمى‌رساند-'),
(1649, 12, 53, 'و من خويشتن را [به خودستايى‌] بى‌گناه نمى‌شمارم، كه نفس [آدمى‌] بسى به بدى و گناه فرمان مى‌دهد مگر آنكه پروردگارم رحمت آرد همانا پروردگار من آمرزگار و مهربان است.'),
(1650, 12, 54, 'و شاه گفت: او را نزد من آوريد تا وى را ويژه خود سازم. پس چون با او به سخن پرداخت، گفت: تو امروز به نزد ما ارجمند و امينى.'),
(1651, 12, 55, 'گفت: مرا بر خزانه‌هاى اين سرزمين برگمار، كه نگاهبان و دانايم.'),
(1652, 12, 56, 'و اينچنين يوسف را در آن سرزمين- مصر- جاى و توان داديم تا هر جا از آن كه بخواهد جاى گزيند مهر و بخشايش خويش را به هر كه خواهيم مى‌رسانيم، و پاداش نيكوكاران را تباه نمى‌كنيم،'),
(1653, 12, 57, 'و هر آينه پاداش آن جهان براى كسانى كه ايمان آورده و پرهيزگارى مى‌كرده‌اند بهتر است.'),
(1654, 12, 58, 'و برادران يوسف آمدند و بر او اندر شدند پس آنها را شناخت و آنها به او ناشناسنده بودند.'),
(1655, 12, 59, 'و چون بارهايشان را برايشان آماده ساخت، گفت: برادرى را كه از پدرتان داريد نزد من آريد آيا نمى‌بينيد كه من پيمانه را تمام مى‌دهم و بهترين ميزبانانم؟'),
(1656, 12, 60, 'و اگر او را نزد من نياوريد، پيمانه‌اى- خواربار- نزد من نخواهيد داشت و نزديك من نشويد.'),
(1657, 12, 61, 'گفتند: او را از پدرش خواهيم خواست و اين كار را خواهيم كرد.'),
(1658, 12, 62, 'و به جوانان- غلامان- خويش گفت: سرمايه آنان را در باردانشان بگذرايد، تا مگر هنگامى كه پيش كسانشان باز گردند آن را باز شناسند، شايد كه باز آيند.'),
(1659, 12, 63, 'چون نزد پدرشان باز گشتند، گفتند: اى پدر، پيمانه- خواربار- از ما بازداشتند، پس برادرمان را با ما بفرست تا خواربار بگيريم و ما هر آينه نگاهبان او خواهيم بود.'),
(1660, 12, 64, '[يعقوب‌] گفت: آيا در باره او'),
(1661, 12, 65, 'و چون كالاى خويش گشودند، سرمايه خويش را يافتند كه به آنها باز گردانده شده، گفتند: اى پدر، ديگر چه مى‌خواهيم؟ اين سرمايه ماست كه به ما بازگردانده شده و [ما بدين وسيله‌] خاندان خود را خواربار مى‌آوريم و برادرمان را نگهدارى مى‌كنيم و بار شترى را [به نام بنيامين‌] افزون مى‌گيريم كه اين بارى اندك است [در نزد پادشاه آن سرزمين‌].'),
(1662, 12, 66, 'گفت: هرگز او را با شما نخواهم فرستاد تا مرا پيمانى استوار از خداى دهيد- يعنى به نام خدا سوگند خوريد، تا گروگانى باشد- كه هر آينه او را به من باز مى‌آريد مگر آنكه گرفتار شويد. پس چون او را پيمان استوار خويش بدادند، گفت: خداى بر آنچه گوييم [گواه و] نگاهبان است.'),
(1663, 12, 67, 'و گفت: اى پسران من، از يك دروازه درون نشويد و از درهاى پراكنده درون شويد و من شما را در برابر [قضا و قدر] خدا هيچ سودى نتوانم داشت. حكم [و فرمانروايى‌] جز خداى را نيست. بر او توكل كردم، و توكل كنندگان بايد بر او توكل كنند و بس.'),
(1664, 12, 68, 'و چون از همانجا كه پدرشان فرموده بود درون شدند، آنان را در برابر [قضا و قدر] خدا هيچ سودى نداشت مگر آرزو و نيازى كه در دل يعقوب بود و آن را آشكار كرد- بگفت و دل از آن بپرداخت- و همانا وى خداوند دانش بود- مى‌دانست كه گريز نتواند كسى را از قضا و قدر برهاند- از آن رو كه ما به او آموخته بوديم ولى بيشتر مردم نمى‌دانند.'),
(1665, 12, 69, 'و چون بر يوسف در آمدند برادرش را نزد خويش جاى داد گفت: همانا من برادر [هم‌مادر] توام، پس از كارهايى كه مى‌كردند اندوه مدار.'),
(1666, 12, 70, 'پس چون بار و بنه آنان را آماده ساخت جام را در باردان- خرجين- برادر خود نهاد، سپس بانگزنى بانگ برآورد كه اى كاروانيان بى‌گمان شما دزدانيد.'),
(1667, 12, 71, '[برادران يوسف‌] در حالى كه روى بديشان نهادند، گفتند: چه گم كرده‌ايد؟'),
(1668, 12, 72, 'گفتند: پيمانه- جام- شاه را گم كرده‌ايم، و [يوسف گفت‌] هر كه آن را باز آرد او را بار شترى است و من آن را ضامنم.'),
(1669, 12, 73, 'گفتند: به خدا سوگند كه شما مى‌دانيد كه ما نيامده‌ايم تا در اين سرزمين تباهى كنيم و ما دزد نبوده‌ايم.'),
(1670, 12, 74, 'گفتند: سزاى آن كار- يا آن كس- چيست، اگر دروغگو باشيد؟'),
(1671, 12, 75, 'گفتند: سزايش آن است كه هر كه [جام‌] در بار وى يافت شود او خود سزاى آن است، ما ستمكاران- دزدان- را اينچنين كيفر مى‌دهيم.'),
(1672, 12, 76, 'پس به جست‌وجوى باردانهاى آنان پيش از باردان برادر خويش آغاز كرد، سپس آن را از باردان برادرش بيرون آورد. بدين سان براى يوسف چاره‌انديشى كرديم- تا بتواند برادرش را نزد خود نگه دارد- [زيرا] در آيين آن شاه روا نبود كه برادرش را [گروگان‌] بگيرد، مگر آنكه خداى خواهد- چنانكه تدبير آن را به يوسف آموخت-، هر كه را بخواهيم به پايه‌ها بالا بريم و بالاى هر دانايى، بسياردانى هست.'),
(1673, 12, 77, '[برادران‌] گفتند: اگر او دزدى كرده، [شگفت نيست، كه‌] برادرش نيز پيش از اين دزدى كرده بود. اما يوسف آن را در دل خويش پنهان داشت و بر آنها آشكار نساخت، گفت: شما به جايگاه بدتريد [از آنچه وانمود مى‌كنيد]، و خداى بدانچه بيان مى‌كنيد داناتر است.'),
(1674, 12, 78, 'گفتند: اى عزيز، او را پدر پيرى است سالخورده، پس يكى از ما را به جاى او بگير، كه تو را از نيكوكاران مى‌بينيم.'),
(1675, 12, 79, 'گفت: پناه بر خدا، كه جز آن كسى را بگيريم كه كالاى خويش نزد او يافته‌ايم، كه آنگاه- اگر چنين كنيم- از ستمكاران باشيم.'),
(1676, 12, 80, 'پس چون از او نوميد گشتند رازگويان به يك سو شدند. مهترشان گفت: مگر نمى‌دانيد كه پدرتان بر شما پيمانى استوار از خداى- يعنى به نام او- گرفته است و پيش از اين در باره يوسف چه كوتاهى كرده‌ايد؟ پس من هرگز از اين سرزمين راهى نشوم تا آنكه پدرم مرا اجازه دهد يا خداى در باره من داورى كند- راهى برايم بگشايد- و او بهترين داوران است.'),
(1677, 12, 81, 'نزد پدرتان باز گرديد و بگوييد: اى پدر، پسرت دزدى كرد، و ما جز بدانچه دانستيم- كه جام شاه از بار او بيرون آمد- گواهى نداديم و نگاهبان غيب هم نبوديم- نمى‌دانستيم كه او دزدى كرده و گرنه گواهى نمى‌داديم كه حكم دزد آن است كه او را بنده گيرند-،'),
(1678, 12, 82, 'و از [مردم‌] شهرى كه در آن بوديم و از كاروانى كه با آن آمديم بپرس، و هر آينه ما راستگوييم.'),
(1679, 12, 83, '[يعقوب‌] گفت: بلكه نفس شما كارى را برايتان بياراسته، پس [به هر حال مرا] شكيبايى نيكوست، اميد است كه خداوند همه آنها را با هم به من رساند، كه اوست داناى با حكمت.'),
(1680, 12, 84, 'و از آنها روى بگردانيد و گفت: اى دريغا بر يوسف! و دو چشم او از اندوه سپيد شد و او خشم خويش [از فرزندان‌] فرو مى‌خورد.'),
(1681, 12, 85, 'گفتند: به خدا سوگند كه تو پيوسته يوسف را ياد مى‌كنى تا سخت بيمار شوى يا از هلاك‌شدگان گردى.'),
(1682, 12, 86, 'گفت: همانا از درد و اندوه خويش به خدا مى‌نالم و چيزى از خدا مى‌دانم كه شما نمى‌دانيد.'),
(1683, 12, 87, 'اى پسران من، برويد از يوسف و برادرش جست‌وجو كنيد و خبر يابيد و از رحمت خدا نوميد مباشيد، كه جز گروه كافران از رحمت خدا نوميد نمى‌شوند.'),
(1684, 12, 88, 'پس چون بر او'),
(1685, 12, 89, 'گفت: آيا مى‌دانيد كه به يوسف و برادرش آنگاه كه نادان بوديد چه كرديد؟'),
(1686, 12, 90, 'گفتند: آيا راستى تو يوسفى؟ گفت: من يوسفم و اين برادر من است. هر آينه خداى بر ما منت نهاد براستى هر كه پرهيزگارى و شكيبايى كند پس [بداند كه‌] خدا مزد نيكوكاران را تباه نمى‌كند.'),
(1687, 12, 91, 'گفتند: به خدا سوگند كه هر آينه خدا تو را بر ما برگزيد و برترى داد، و بى‌گمان ما لغزشكار بوديم.'),
(1688, 12, 92, 'گفت: امروز بر شما هيچ سرزنش نيست، خدا شما را بيامرزد، و او مهربان‌ترين مهربانان است'),
(1689, 12, 93, 'اين پيراهن مرا ببريد و بر روى پدرم افكنيد تا بينا شود و همه خاندان خود را نزد من آريد.'),
(1690, 12, 94, 'و چون كاروان بيرون شد، پدرشان گفت: همانا من بوى يوسف مى‌يابم، اگر مرا كم خرد نشماريد.'),
(1691, 12, 95, 'گفتند: به خدا سوگند كه تو هنوز در گمراهى ديرينه خويشى- يعنى دوستى شديد به يوسف-.'),
(1692, 12, 96, 'و چون مژده‌رسان بيامد، آن'),
(1693, 12, 97, 'گفتند: اى پدر، براى گناهان ما آمرزش بخواه، همانا ما گناهكار بوديم.'),
(1694, 12, 98, 'گفت: بزودى براى شما از پروردگارم آمرزش مى‌خواهم، كه او بسى آمرزنده و مهربان است.'),
(1695, 12, 99, 'و چون بر يوسف در آمدند، پدر و مادر خويش را در كنار خود جاى داد و گفت: به مصر در آييد كه اگر خداى بخواهد ايمن خواهيد بود.'),
(1696, 12, 100, 'و پدر و مادر خود را بر تخت بالا برد و همگان پيش او سجده‌كنان درافتادند، و گفت: اى پدر، اين تعبير همان خواب من است كه پيشتر ديده بودم، كه پروردگارم آن را راست و درست ساخت و براستى با من نيكويى نمود آنگاه كه مرا از زندان بيرون آورد و شما را از بيابان [كنعان‌] بياورد پس از آنكه شيطان ميان من و برادرانم آشوب كرد و تباهى افكند همانا پروردگار من آن را كه خواهد لطف‌كننده است- به نرمى و نيكى رفتار كند-، كه اوست دانا و با حكمت.'),
(1697, 12, 101, 'پروردگارا، مرا از پادشاهى [بهره‌] بدادى و از تعبير خوابها بياموختى، اى پديدآرنده آسمانها و زمين، تو يار و سرپرست من در اين جهان و آن جهانى، مرا مسلمان- گردن‌نهاده و فرمانبردار- بميران و به شايستگان نيكوكار بپيوند.'),
(1698, 12, 102, 'اين [سرگذشت‌] از خبرهاى غيب است كه به تو وحى مى‌كنيم و تو نزد آنان'),
(1699, 12, 103, 'و بيشتر مردم مؤمن نخواهند شد هر چند [بر ايمان آوردنشان‌] بسى آرزو برى.'),
(1700, 12, 104, 'و تو بر اين [كار- تبليغ رسالت-] مزدى از آنان نمى‌خواهى اين'),
(1701, 12, 105, 'و بسا نشانه‌ها در آسمانها و زمين هست كه بر آن مى‌گذرند و از آن رويگردانند.'),
(1702, 12, 106, 'و بيشترشان به خدا ايمان نمى‌آورند مگر اينكه [در همان حال‌] مشركند- يعنى به حقيقت توحيد نرسيده‌اند-.'),
(1703, 12, 107, 'پس مگر ايمن‌اند از اينكه عذابى فراگير از سوى خدا به آنان رسد، يا در حالى كه آگاهى ندارند قيامت ناگاه آنان را فرا رسد؟'),
(1704, 12, 108, 'بگو: اين راه من است كه با بينايى به سوى خدا مى‌خوانم، من و هر كه از من پيروى كند. و پاك است خداى، و من از مشركان نيستم.'),
(1705, 12, 109, 'و پيش از تو نفرستاديم مگر مردانى را از مردم شهرها كه بديشان وحى مى‌كرديم پس آيا در زمين، نگشته‌اند تا بنگرند كه سرانجام كسانى كه پيش از آنان بودند چگونه بود؟ و هر آينه سراى بازپسين براى پرهيزگاران بهتر است، آيا خرد را كار نمى‌بنديد؟'),
(1706, 12, 110, '[و دعوت پيامبران و انكار كافران ادامه داشت‌] تا چون پيامبران [از ايمان آوردن قوم خود] نوميد شدند و [كافران‌] چنين دانستند كه به آنان دروغ گفته شده- كه كافران عذاب سخت دارند-، يارى ما بديشان رسيد، پس هر كه را خواستيم رهانيده شد، و عذاب سخت ما از گروه بزهكاران باز گردانده نشود.'),
(1707, 12, 111, 'هر آينه در سرگذشت آنان براى خردمندان عبرتى- مايه پندگرفتن- بوده است. اين- قرآن- سخنى نيست كه به دروغ بافته شده باشد، بلكه باورداشت آن [كتابى‌] است كه پيش آن است- پيش از آن آمده است-، و شرح و بيان هر چيزى و رهنمونى و بخشايشى است براى مردمى كه ايمان آورند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'الف، لام، ميم، را. اينهاست آيه‌هاى اين كتاب- قرآن- و آنچه از پروردگارت به سوى تو فروفرستاده شده، حقّ است ولى بيشتر مردم باور نمى‌دارند.'),
(1709, 13, 2, 'خداست آن كه آسمانها را بى‌ستونهايى كه آنها را ببينيد برافراشت، سپس بر عرش- مقام فرمانروايى بر جهان هستى- برآمد و خورشيد و ماه را رام كرد، كه هر يك تا سرآمدى نامبرده- معيّن- روانند. كار [جهان‌] را تدبير مى‌كند نشانه‌ها را به تفصيل بيان مى‌كند تا شايد ديدار پروردگارتان- روز رستاخيز- را بى‌گمان باور كنيد.'),
(1710, 13, 3, 'و اوست آن كه زمين را بگسترد و در آن كوه‌ها و رودها قرار داد، و در آن از همه ميوه‌ها دو گونه- ترش و شيرين و تابستانى و زمستانى و ...- پديد كرد. شب را بر روز مى‌پوشاند. همانا در اين [امور] براى گروهى كه بينديشند نشانه هاست.'),
(1711, 13, 4, 'و در زمين پاره‌هايى است نزديك يكديگر، و بوستانهايى از تاكها و كشتها و خرمابنهايى روييده از يك ريشه و نه از يك ريشه كه با يك آب سيراب مى‌شوند. و برخى از آنها را در ميوه بر برخى ديگر برترى مى‌نهيم. هر آينه در اين [امور] براى گروهى كه خرد را كار بندند نشانه‌هاست.'),
(1712, 13, 5, 'و اگر شگفتى نمايى، شگفت است گفتار آنان كه: آيا هنگامى كه خاك شويم، براستى در آفرينشى نو در آييم؟ اينانند كه به پروردگار خويش كافر شدند، و اينان در گردنهاشان زنجيرهاست، و اينان دوزخيانند كه در آن جاويدانند.'),
(1713, 13, 6, 'و از تو پيش از نيكى- سلامت و رستگارى- بدى- عذاب و هلاكت- را به شتاب مى‌خواهند، و حال آنكه پيش از آنان عقوبتهاى عبرت‌آموز رفته است. و همانا پروردگار تو براى مردم بر ستمكاريشان خداوند آمرزش است و همانا پروردگار تو سخت كيفر است.'),
(1714, 13, 7, 'و كسانى كه كافر شدند گويند: چرا بر او از پروردگارش نشانه‌اى [به دلخواه ما] فرونمى‌آيد؟ همانا تو بيم‌كننده‌اى و بس، و هر گروهى را راهنمايى است.'),
(1715, 13, 8, 'خدا مى‌داند كه هر مادينه‌اى چه بار دارد و زهدانها چه مى‌كاهد و چه مى‌افزايد، و هر چيزى نزد او به اندازه است.'),
(1716, 13, 9, 'داناى نهان و آشكار است، بزرگ و برتر است- از همه چيز و از هر نسبت ناروا-.'),
(1717, 13, 10, '[براى او] يكسان است از شما هر كه سخن نهان دارد و هر كه آن را هويدا كند و هر كه به شب پنهان شود يا به روز آشكارا رونده باشد'),
(1718, 13, 11, 'او- آدمى- را فرشتگانى در پى است از پيش روى و از پس وى كه او را به فرمان خدا نگاه مى‌دارند. همانا خداوند آنچه را گروهى دارند [از نعمتها]، دگرگون نكند تا آنگاه كه آنچه را در خودشان است دگرگون كنند. و چون خدا براى گروهى بدى خواهد هرگز آن را بازگشت نباشد و آنان را جز او هيچ دوست و كارسازى نيست.'),
(1719, 13, 12, 'اوست كه برق- آذرخش- را براى بيم و اميد- بيم مسافران و اميد كشاورزان- به شما مى‌نمايد، و ابرهاى گرانبار را پديد مى‌آورد،'),
(1720, 13, 13, 'و رعد- تندر- با ستايش او و فرشتگان از بيمش او را به پاكى ياد مى‌كنند و صاعقه‌ها- آتشهاى آسمانى- را مى‌فرستد، پس آن را به هر كه خواهد مى رساند در حالى كه آنان- كافران- در باره خدا جدل و ستيزه مى‌كنند، و او سخت كيفر- يا سخت‌گيرنده يا سخت نيرو- است.'),
(1721, 13, 14, 'تنها او راست دعوت حقّ و آنان كه جز او را مى‌خوانند، [آن پرستيدگان‌] هيچ پاسخى بديشان ندهند [و هيچ نيازى از آنان بر نياورند] مگر مانند كسى كه دو دست خويش به سوى آب گشاده تا [آب‌] به دهانش برسد ولى به دهان او رسنده نيست. و خواندن كافران جز در گمراهى- بى‌حاصلى- نباشد.'),
(1722, 13, 15, 'و هر كه در آسمانها و زمين است خواه و ناخواه، و سايه‌هاشان، بامدادان و شبانگاهان خداى را سجده مى‌آرند.'),
(1723, 13, 16, 'بگو: پروردگار آسمانها و زمين كيست؟ بگو: خداى يكتا. بگو: پس آيا به جاى او دوستان و سرپرستانى گرفته‌ايد كه مالك هيچ سود و زيانى براى خويش نيستند؟ بگو: آيا نابينا و بينا برابرند؟ يا تاريكيها و روشنايى يكسانند؟ يا مگر براى خدا انبازهايى گرفته‌اند كه مانند آفرينش او آفريده‌اند و [اين‌] آفرينش بر آنها به هم ماننده- مشتبه- شده است؟ بگو: خداى يكتا آفريننده همه چيزهاست و اوست يگانه و سخت چيره بر همگان.'),
(1724, 13, 17, 'از آسمان آبى فروآورد پس رودهايى به اندازه خود روان شد آنگاه سيل كفى بر روى خود بر آورد، و از آنچه براى جستن زيورى يا كالايى در آتش مى‌گدازند- فلزات- نيز كفى مانند آن [برآورد]. اينچنين خدا حق و باطل را [مثَل‌] مى‌زند- حق را پايدار مى‌كند و باطل را از ميان مى‌برد- اما كف به كنارى رود و نيست شود، و اما آنچه مردم را سود مى‌رساند- آب و فلزات- در زمين بماند. اين گونه، خدا مثَلها مى‌زند.'),
(1725, 13, 18, 'كسانى كه پروردگارشان را پاسخ دادند، سرانجام نيك دارند و كسانى كه او را پاسخ ندادند، اگر همه آنچه در زمين است و مانند آن را با آن مى داشتند همانا آماده بودند براى بازخريد خويش [از عذاب‌] بپردازند. اينان را بدى- سختى- حساب باشد و جايگاهشان دوزخ است و بد آرامگاهى است.'),
(1726, 13, 19, 'پس آيا كسى كه مى‌داند آنچه از پروردگارت به تو فروفرستاده شده راست و درست است همچون كسى است كه نابينا- كوردل- است؟ تنها خردمندان پند مى‌گيرند'),
(1727, 13, 20, 'آنان كه پيمان خدا را به سر مى‌برند و پيمان را نمى‌شكنند'),
(1728, 13, 21, 'و آنان كه آنچه را خداى فرموده است كه بپيوندند مى‌پيوندند و از پروردگارشان مى‌ترسند و از بدى حساب بيمناكند'),
(1729, 13, 22, 'و آنان كه براى جستن خشنودى پروردگارشان شكيبايى نمودند و نماز را برپا داشتند و از آنچه روزيشان كرديم در نهان و آشكار انفاق كردند و بدى را با نيكى دور مى‌كنند آنان راست سرانجام [نيك‌] آن سراى،'),
(1730, 13, 23, 'بهشت هايى پاينده كه به آنها در آيند و نيز هر يك از پدران و همسران و فرزندان شايسته و نيكوكار آنها و فرشتگان از هر درى بر آنان در آيند'),
(1731, 13, 24, '[و گويند:] سلام بر شما به پاداش صبرى كه كرديد پس چه نيك است سرانجام اين سراى.'),
(1732, 13, 25, 'و كسانى كه پيمان خداى را پس از بستن استوار آن مى‌شكنند و آنچه را خداى فرموده است كه بپيوندند- پيوند با خويشان- مى‌برند، و در زمين تباهى مى‌كنند، آنان راست لعنت و آنان راست بدى و سختى آن سراى.'),
(1733, 13, 26, 'خداوند روزى را براى هر كه بخواهد فراخ كند و [براى هر كه بخواهد] تنگ گرداند و [دنياخواهان‌] به زندگى اين جهان شادمان شدند و حال آنكه زندگى اين جهان در برابر آن جهان جز بهره‌اى [اندك‌] نيست.'),
(1734, 13, 27, 'و كسانى كه كافر شدند گويند: چرا نشانه و معجزه‌اى [به دلخواه ما] از پروردگارش بر او فرونيامد؟ بگو: همانا خدا هر كه را بخواهد گمراه كند و هر كه را باز گردد- به درگاه او روى آورد- به سوى خويش راه نمايد'),
(1735, 13, 28, 'آنان كه ايمان آوردند و دلهاشان به ياد خدا آرام يابد. آگاه باشيد كه دلها تنها به ياد خدا آرام گيرد.'),
(1736, 13, 29, 'كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند ايشان راست زندگانى خوش و بازگشت نيكو.'),
(1737, 13, 30, 'همچنين، تو را به ميان امتى كه پيش از ايشان امتهايى بگذشتند فرستاديم تا آنچه را به تو وحى كرديم بر آنان بر خوانى و [حال آنكه‌] آنها به خداى مهربان كفر مى‌ورزند. بگو: اوست پروردگار من، جز او خدايى نيست، بر او توكل كردم و بازگشت من به سوى اوست.'),
(1738, 13, 31, 'و اگر قرآنى مى‌بود كه كوه‌ها بدان روان مى‌شد يا زمين بدان شكافته و پاره پاره مى‌گشت يا بدان وسيله با مردگان سخن گفته مى‌شد [باز هم ايمان نمى‌آوردند مگر به خواست خدا]. بلكه كار و فرمان همه از آن خداست. آيا كسانى كه ايمان آورده‌اند [از ايمان آوردن كافران هنوز] نوميد نشده‌اند [و نمى‌دانند] كه اگر خدا مى‌خواست همه مردم را راه مى‌نمود؟ و پيوسته به كسانى كه كفر ورزيدند به سزاى كارهايى كه كرده‌اند رويدادى كوبنده مى رسد يا نزديك خانه‌شان فرود مى‌آيد تا آنگاه كه وعده خدا برسد، كه خدا خلاف وعده نمى‌كند.'),
(1739, 13, 32, 'و هر آينه فرستادگان پيش از تو نيز استهزاء شدند، پس به كسانى كه كافر شدند مهلت دادم سپس آنها را بگرفتم، پس [بنگر كه‌] كيفر من چگونه بود.'),
(1740, 13, 33, 'آيا آن [خداى‌] كه بر هر كسى بدانچه كرده ايستاده و نگاهبان است [با غير او همانند است‌]؟ و براى خدا شريكانى قرار دادند بگو: آنها را نام ببريد- وصف كنيد- يا مگر او'),
(1741, 13, 34, 'آنان را در زندگى اين جهان عذابى است، و براستى عذاب آن جهان سخت‌تر است، و آنها را هيچ نگاهدارنده‌اى از [عذاب‌] خدا نيست.'),
(1742, 13, 35, 'داستان آن بهشتى كه به پرهيزگاران وعده داده شده چنان است كه از زير آن جويها روان است ميوه‌ها و سايه‌اش هميشگى است. اين است سرانجام كسانى كه پرهيزگارى كردند، و سرانجام كافران آتش است.'),
(1743, 13, 36, 'و كسانى كه به آنان كتاب داده‌ايم- يهود و نصارى- بدانچه به تو فروفرستاده شده شادمانند و از گروه‌ها [ى اهل كتاب‌] كسانى هستند كه برخى از آن را انكار مى‌كنند. بگو: جز اين نيست كه فرمان يافته‌ام كه خداى را بپرستم و به او انباز نيارم. به سوى او مى‌خوانم و بازگشت من به سوى اوست.'),
(1744, 13, 37, 'و همچنين اين'),
(1745, 13, 38, 'و هر آينه پيش از تو پيامبرانى فرستاديم و ايشان را همسران و فرزندان داديم. و هيچ پيامبرى را نشايد كه جز به خواست و فرمان خدا نشانه‌اى- معجزه‌اى- بيارد. هر سرآمدى را نوشته‌اى- حكمى معلوم- است.'),
(1746, 13, 39, 'خداى آنچه را خواهد بسترد و از ميان ببرد و [يا] بر جاى و استوار بدارد، و مادر كتاب- كتاب مادر يا لوح محفوظ- نزد اوست.'),
(1747, 13, 40, 'و اگر برخى از آنچه به آنها وعده مى‌كنيم به تو بنماييم، يا تو را [پيش از كيفرشان از ميان آنها] برگيريم، جز اين نيست كه بر تو رساندن پيام است و بر ما حسابرسى.'),
(1748, 13, 41, 'آيا نديدند- ندانستند- كه ما به زمين مى‌پردازيم و از كناره‌هاى آن مى‌كاهيم و خدا حكم مى‌كند و حكم او را هيچ بازپس‌برنده‌اى نيست و او زود حساب است.'),
(1749, 13, 42, 'و همانا كسانى كه پيش از آنها بودند نيرنگ ساختند [ولى براى آنها سودى نكرد] زيرا كه همه نيرنگها خداى راست. مى‌داند كه هر كسى چه مى‌كند، و زودا كه كافران بدانند كه سرانجام آن سراى كه راست.'),
(1750, 13, 43, 'و كسانى كه كفر ورزيدند گويند: تو فرستاده [خدا] نيستى بگو: ميان من و شما خدا گواهى بسنده است و آن كه دانش كتاب- قرآن- نزد اوست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الف، لام، را. [اين‌] كتابى است كه آن را به سوى تو فروفرستاديم تا مردم را به خواست و فرمان پروردگارشان از تاريكيها [ى گمراهى‌] به روشنايى [هدايت‌] بيرون آرى، به راه آن تواناى بى‌همتا و ستوده'),
(1752, 14, 2, 'آن خدايى كه او راست آنچه در آسمانها و آنچه در زمين است، و واى بر كافران از عذابى سخت'),
(1753, 14, 3, 'آنان كه زندگى اين جهان را بر آن جهان برمى‌گزينند، و [مردم را] از راه خدا باز مى‌دارند و آن را كج مى‌خواهند- بد جلوه مى‌دهند- آنان در گمراهى دورند.'),
(1754, 14, 4, 'و ما هيچ پيامبرى را جز به زبان قومش نفرستاديم، تا [بتواند] براى آنان بروشنى بيان كند. پس خدا هر كه را خواهد گمراه كند و هر كه را خواهد راه نمايد و اوست تواناى بى‌همتا و داناى استواركار.'),
(1755, 14, 5, 'و هر آينه موسى را با نشانه‌هاى خويش فرستاديم كه: قوم خود را از تاريكيها به روشنايى بيرون بر، و روزهاى خدا را به يادشان آر همانا در اين [يادآورى‌] براى هر شكيباى سپاسگزارى نشانه‌هاست.'),
(1756, 14, 6, 'و [ياد كن‌] آنگاه كه موسى قوم خود را گفت: نعمت خداى را بر خودتان ياد كنيد هنگامى كه شما را از فرعونيان رهانيد، كه شكنجه سختى به شما مى‌رسانيدند و پسرانتان را سر مى‌بريدند و زنانتان را [براى خدمت‌] زنده نگه مى‌داشتند، و شما را در اين كار آزمونى بزرگ از پروردگارتان بود.'),
(1757, 14, 7, 'و هنگامى كه پروردگارتان به شما آگاهى داد كه اگر سپاس گزاريد همانا شما را [نعمت‌] بيفزايم، و اگر ناسپاسى كنيد هر آينه عذاب من سخت است.'),
(1758, 14, 8, 'و موسى گفت: اگر شما و هر كه در زمين است همگى كافر شويد، همانا خداوند بى نياز و ستوده است.'),
(1759, 14, 9, 'آيا خبر كسانى كه پيش از شما بودند، قوم نوح و عاد و ثمود، و كسانى كه پس از آنها آمدند، كه جز خدا كسى از آنان آگاهى [كامل‌] ندارد، به شما نرسيده است؟ پيامبرانشان با دلايل روشن بديشان آمدند، پس دستهاشان را در دهنهاشان باز گرداندند و گفتند: ما به آنچه بدان فرستاده شده‌ايد كافريم، و ما در باره آنچه ما را به آن مى‌خوانيد سخت به شك اندريم.'),
(1760, 14, 10, 'پيامبرانشان گفتند: آيا در باره خدا، پديدآرنده آسمانها و زمين، شكى هست؟ شما را مى‌خواند تا برخى از گناهانتان را بيامرزد و تا سرآمدى نامبرده شما را واپس بدارد- زنده بدارد-. گفتند: شما جز آدميانى مانند ما نيستيد كه مى‌خواهيد ما را از آنچه پدرانمان مى‌پرستيدند باز داريد و بگردانيد، پس براى ما حجتى روشن و آشكار بياريد.'),
(1761, 14, 11, 'پيامبرانشان به آنها گفتند: ما جز آدميانى مانند شما نيستيم و ليكن خدا بر هر كه از بندگان خويش بخواهد [به نعمت نبوت‌] منت مى‌نهد. و ما را نسزد كه شما را حجتى آريم جز به خواست و فرمان خدا پس مؤمنان بايد بر خدا توكل كنند و بس.'),
(1762, 14, 12, 'و ما را چيست كه بر خدا توكل نكنيم و حال آنكه او راه‌هاى [راست‌] ما را به ما بنمود و ما بى‌گمان بر آزارى كه به ما مى‌كنيد شكيبايى خواهيم كرد پس توكل كنندگان بايد بر خدا توكل كنند و بس.'),
(1763, 14, 13, 'و كسانى كه كافر شدند به پيامبرانشان گفتند: هر آينه شما را از سرزمينمان بيرون مى‌كنيم يا به كيش ما باز گرديد. پس پروردگارشان به آنان وحى كرد كه البته ستمكاران را هلاك مى‌كنيم'),
(1764, 14, 14, 'و هر آينه شما را از پس آنها در آن سرزمين جاى خواهيم داد. اين براى آن كس است كه از مقام من بترسد و از بيم‌كردنم بهراسد.'),
(1765, 14, 15, 'و [پيامبران‌] گشايش و پيروزى خواستند، و هر گردنكش ستيزه‌گرى نوميد و زيانكار شد،'),
(1766, 14, 16, 'فراروى او دوزخ است و او را زردابى از چرك خون‌آلود بنوشانند'),
(1767, 14, 17, 'كه آن را جرعه جرعه مى‌آشامد و او را فروبردن آن آسان و گوارا نيست- از گندى و تلخى نمى‌تواند آن را به آسانى فروبرد تا رهايى يابد- و مرگ از هر جا به او روى آورد اما او مردنى نيست، و فراروى او عذابى سخت و سهمگين است.'),
(1768, 14, 18, 'داستان آنان كه به پروردگارشان كافر شدند [اين است كه‌] كردارشان چون خاكسترى است كه بادى سخت در روزى تندباد بر آن بوزد، كه بر هيچ چيزى از آنچه كرده‌اند دست نتوانند يافت. اين است گمراهى دور.'),
(1769, 14, 19, 'آيا نديده‌اى- ندانسته‌اى- كه خدا آسمانها و زمين را بحق آفريده است؟ اگر خواهد شما را مى‌برد و آفريده‌اى نو مى‌آورد'),
(1770, 14, 20, 'و اين بر خدا دشوار نيست.'),
(1771, 14, 21, 'و همگى در پيشگاه خدا پديدار شوند، آنگاه ناتوانان به كسانى كه گردنكشى و بزرگ‌منشى مى‌كردند گويند: ما [در دنيا] پيرو شما بوديم، پس آيا [امروز] شما مى‌توانيد چيزى از عذاب خدا را از ما باز داريد؟ گويند: اگر خدا ما را [به رهايى از عذاب‌] راه نموده بود ما نيز شما را راه مى‌نموديم، بر ما يكسان است چه زارى و بى‌تابى كنيم يا شكيبايى نماييم ما را هيچ گريزگاهى نيست.'),
(1772, 14, 22, 'و هنگامى كه كار گزارده شد- رستاخيز بپا شد- شيطان گويد: همانا خدا به شما وعده داد، وعده راست و درست، و من به شما وعده دادم و با شما خلاف آن كردم، و مرا بر شما هيچ دستى نبود جز اينكه شما را خواندم و مرا پاسخ داديد، پس مرا سرزنش مكنيد و خود را سرزنش كنيد نه من فريادرس شمايم و نه شما فريادرس من من به اينكه پيش از اين مرا [با خدا در فرمانبردارى‌] شريك مى‌گرفتيد باور ندارم. همانا ستمكاران را عذابى است دردناك.'),
(1773, 14, 23, 'و كسانى را كه ايمان آوردند و كارهاى نيك و شايسته كردند به بهشت‌هايى در آورند كه از زير آنها جويها روان است، به فرمان پروردگارشان جاودانه در آنجا باشند درود آنان در آنجا «سلام» است.'),
(1774, 14, 24, 'آيا نديده‌اى كه خدا چگونه مَثَلى زده است؟ سخن پاكيزه- اعتقاد درست و ايمان راستين- را چون درخت پاكيزه [قرار داده است‌] كه ريشه آن [در زمين‌] استوار و شاخه آن در آسمان است- به آسمان سر برآورده است-'),
(1775, 14, 25, 'ميوه خود را هر هنگام به فرمان پروردگارش مى‌دهد. و خدا براى مردم مَثَلها مى زند شايد به ياد آرند و پند گيرند.'),
(1776, 14, 26, 'و داستان سخن پليد- اعتقاد باطل و كفر- همچون درختى است پليد [و آفت‌زده‌] كه از روى زمين بركنده شده و آن را هيچ قرارى- ريشه و بيخى- نباشد.'),
(1777, 14, 27, 'خداوند كسانى را كه ايمان آوردند، با گفتار استوار- اعتقاد درست و پايدار- در زندگى اين جهان و در آن جهان برجاى و استوار مى‌دارد، و خدا ستمكاران- كافران- را گمراه كند، و خدا هر چه خواهد مى‌كند.'),
(1778, 14, 28, 'آيا ننگريستى به كسانى كه [سپاس‌] نعمت خدا را به كفران و ناسپاسى- يا به كفر- بدل كردند، و قوم خود را به سراى هلاكت درآوردند؟'),
(1779, 14, 29, 'دوزخ، كه به آن در آيند- يا در آن بسوزند-، و بد قرارگاهى است.'),
(1780, 14, 30, 'و براى خدا همتايانى قرار دادند تا [مردم را] از راه او گمراه كنند بگو: بهره‌مند و كامياب شويد كه سرانجام و بازگشتتان به آتش است.'),
(1781, 14, 31, 'به بندگان من كه ايمان آورده‌اند بگو: نماز را برپا دارند و از آنچه روزيشان كرده‌ايم نهان و آشكارا انفاق كنند پيش از آنكه روزى بيايد كه در آن نه خريد و فروختى باشد و نه دوستيى [كه در غير راه خدا باشد].'),
(1782, 14, 32, 'خداست آن كه آسمانها و زمين را آفريد و از آسمان آبى فرو فرستاد پس به آن آب از ميوه‌ها براى شما روزى بيرون آورد و كشتيها را رام شما كرد تا به فرمان او در دريا روان شوند و رودها را رام شما كرد'),
(1783, 14, 33, 'و خورشيد و ماه را كه پيوسته در جنبش و گردشند براى شما رام كرد و شب و روز را نيز رام شما ساخت'),
(1784, 14, 34, 'و از هر چه خواستيد به شما داد، و اگر [بخواهيد] نعمت خدا را بشمريد آن را شمار نتوانيد كرد هر آينه آدمى بسى ستمگر و ناسپاس است.'),
(1785, 14, 35, 'و [ياد كن‌] آنگاه كه ابراهيم گفت: اى پروردگار من، اين شهر- مكه- را ايمن ساز و مرا و فرزندانم را از پرستش بتها دور دار.'),
(1786, 14, 36, 'پروردگارا، آنها'),
(1787, 14, 37, 'پروردگارا، من برخى از فرزندانم را به دره‌اى بى‌كشت، نزديك خانه شكوهمند تو جاى دادم، پروردگارا، تا نماز به پا دارند، پس دلهايى از مردمان را چنان كن كه به سوى آنان گرايند، و از ميوه‌ها روزيشان ده، باشد كه سپاس گزارند'),
(1788, 14, 38, 'پروردگارا، هر چه را پنهان كنيم يا آشكار سازيم تو مى‌دانى و هيچ چيز در زمين و آسمان بر خدا پوشيده نيست'),
(1789, 14, 39, 'سپاس و ستايش آن خداى راست كه مرا در پيرى اسماعيل و اسحاق بخشيد، هر آينه پروردگارم شنواى دعاست'),
(1790, 14, 40, 'پروردگارا، مرا برپا دارنده نماز كن و از فرزندانم نيز و پروردگارا، دعاى مرا بپذير'),
(1791, 14, 41, 'پروردگارا، مرا برپا دارنده نماز كن و از فرزندانم نيز پروردگارا، و دعاى مرا- يعنى نماز و عبادت مرا- بپذير'),
(1792, 14, 42, 'و خداى را از آنچه ستمكاران مى‌كنند غافل مپندار، همانا [كيفر] آنان را واپس مى‌دارد براى روزى كه در آن، چشمها [ى كافران و ستمگران از حيرت و سراسيمگى‌] خيره بماند،'),
(1793, 14, 43, 'در حالى كه گردنها فرازكرده سرهاى خويش بالا گرفته، چشمشان بر هم نخورد و دلهاشان [از بسيارى سرگشتگى‌] تهى [از فهم و چاره‌انديشى‌] است.'),
(1794, 14, 44, 'و مردم را بيم كن از آن روز كه عذاب بديشان آيد، پس كسانى كه ستم كرده‌اند گويند: خداوندا، ما را تا مدتى نزديك- كوتاه- واپس دار تا دعوت تو را پاسخ دهيم و فرستادگان را پيروى كنيم. [به آنها گفته شود:] آيا شما نبوديد كه پيش از اين سوگند مى‌خورديد كه شما را هرگز زوالى نباشد؟!'),
(1795, 14, 45, 'و [مگر نه اين است كه‌] در خانه‌هاى كسانى كه بر خويشتن ستم كردند جاى گرفتيد و براى شما روشن گشت كه با آنان چه كرديم، و برايتان مَثَلها زديم!'),
(1796, 14, 46, 'و همانا نيرنگ خويش به كار بردند و نيرنگشان نزد خداست- خدا به آن آگاه است- هر چند كه از نيرنگشان كوه‌ها از جا كنده شود.'),
(1797, 14, 47, 'پس مپندار كه خدا وعده خود را با فرستادگان خويش خلاف مى‌كند، همانا خدا تواناى بى‌همتا و كين‌ستان است'),
(1798, 14, 48, '[در] روزى كه زمين غير اين زمين گردد و آسمانها [نيز غير اين آسمانها شود]، و [همه‌] در پيشگاه خداى يگانه سخت‌چيره پديدار شوند.'),
(1799, 14, 49, 'و گناهكاران را در آن روز ببينى كه در زنجيرها به هم بسته شده‌اند،'),
(1800, 14, 50, 'پيراهنشان از قطِران- مس گداخته يا قير- است و آتش رويهاشان را فرو پوشد.'),
(1801, 14, 51, 'تا خداوند هر كس را سزاى آنچه كرده است بدهد، كه خدا زود حساب است.'),
(1802, 14, 52, 'اين براى مردم پيامى رسا و بسنده است [تا بدان پند داده شوند] و تا بدان بيم كرده شوند و بدانند كه او خداى يگانه است و تا خردمندان ياد كنند و پند گيرند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الف، لام، را. اين است آيه‌هاى كتاب و قرآن روشن و روشن‌كننده.'),
(1804, 15, 2, 'چه بسا كسانى كه كافر شدند آرزو كنند كه كاش مسلمان بودند.'),
(1805, 15, 3, 'واگذارشان تا بخورند و برخوردار شوند و آرزو سرگرمشان كند، زودا كه بدانند.'),
(1806, 15, 4, 'و [مردم‌] هيچ شهرى را هلاك نكرديم مگر آنكه آن را نوشته‌اى- حكمى و سرنوشتى و زمانى- معلوم بود.'),
(1807, 15, 5, 'هيچ امتى از سرآمد خود نه پيشى گيرد و نه واپس افتد.'),
(1808, 15, 6, 'و گفتند: اى آن كسى كه قرآن بر او فرو آمده، همانا تو ديوانه‌اى'),
(1809, 15, 7, 'اگر راست مى‌گويى، چرا فرشتگان را نزد ما نمى‌آرى؟'),
(1810, 15, 8, 'ما فرشتگان را جز بحق فرو نفرستيم، و آنگاه ديگر [اين كافران‌] مهلت نيابند.'),
(1811, 15, 9, 'همانا ما اين ذكر- ياد و پند قرآن- را فرو فرستاديم، و هر آينه ما نگاهدار آنيم.'),
(1812, 15, 10, 'و هر آينه پيش از تو [فرستادگان خود را] در ميان گروه‌هاى پيشين فرستاديم.'),
(1813, 15, 11, 'و هيچ فرستاده‌اى نمى‌آمدشان مگر آنكه او را استهزاء مى‌كردند.'),
(1814, 15, 12, 'اينچنين آن'),
(1815, 15, 13, '[و حال آنكه‌] به آن ايمان نمى‌آورند، و آيين و روش پيشينيان [بر اين‌] رفته است [كه پيامبران را تكذيب و نعمت خداوند را ناسپاسى كنند].'),
(1816, 15, 14, 'و اگر درى از آسمان بر آنان بگشاييم تا پيوسته در آن بالا روند [و قدرت و معجزات ما را بنگرند]،'),
(1817, 15, 15, 'هر آينه گويند: چشمان ما را پوششى بر نهاده‌اند- ما را چشم‌بندى كرده‌اند-، بلكه ما گروهى جادوزده‌ايم.'),
(1818, 15, 16, 'و هر آينه در آسمان برجهايى پديد كرديم و آن را از بهر بينندگان بياراستيم'),
(1819, 15, 17, 'و آن را از [ورود] هر شيطان رانده‌شده‌اى نگاه داشتيم'),
(1820, 15, 18, 'مگر آن كه دزدانه سخنى بشنود، كه شهابى روشن و آشكار دنبالش كند.'),
(1821, 15, 19, 'و زمين را گسترديم و در آن كوه‌هاى استوار افكنديم و در آن از هر چيز سنجيده و به اندازه رويانيديم.'),
(1822, 15, 20, 'و در آن براى شما و آنها كه شما روزى‌دهنده آنها نيستيد- پرندگان و حيوانات وحشى- اسباب زندگى و روزى قرار داديم.'),
(1823, 15, 21, 'و هيچ چيزى نيست مگر آنكه گنجينه‌هاى آن نزد ماست، و آن را جز به اندازه معلوم فرو نمى‌فرستيم.'),
(1824, 15, 22, 'و ما بادها را بارداركننده [درختان‌] فرستاديم، و از آسمان آبى فروفرستاده شما را از آن سيراب ساختيم و شما ذخيره‌كننده و نگاهدار آن نيستيد.'),
(1825, 15, 23, 'و هر آينه ما زنده مى‌كنيم و مى‌ميرانيم و ما ميراث برانيم- پس از شما همه چيز در اختيار ماست-.'),
(1826, 15, 24, 'و هر آينه ما پيشينيان شما را مى‌دانيم و پسينيان را هم مى‌دانيم.'),
(1827, 15, 25, 'و همانا پروردگار تو [هنگام رستاخيز] آنان را برانگيزد و فراهم آرد، كه او استواركار و داناست.'),
(1828, 15, 26, 'و هر آينه آدمى را از گلى خشك برآمده از لجنى بويناك آفريديم.'),
(1829, 15, 27, 'و پريان را پيش از آن از آتش سوزان بى‌دود و نفوذكننده آفريديم.'),
(1830, 15, 28, 'و [ياد كن‌] آنگاه كه پروردگار تو فرشتگان را گفت كه همانا من آفريننده‌ام آدميى را از گلى خشك برآمده از لجنى بويناك'),
(1831, 15, 29, 'پس چون اندام او را درست و آراسته كردم و از روح خويش در او دميدم او را سجده‌كنان درافتيد.'),
(1832, 15, 30, 'پس فرشتگان همگى سجده كردند،'),
(1833, 15, 31, 'مگر ابليس كه سر باز زد از اينكه با سجده‌كنان باشد.'),
(1834, 15, 32, '[خداى‌] گفت: اى ابليس، تو را چيست كه با سجده‌كنان نباشى؟'),
(1835, 15, 33, 'گفت: من نه آنم كه براى بشرى كه او را از گِلى خشك برآمده از لجنى بويناك آفريده‌اى سجده كنم.'),
(1836, 15, 34, 'گفت: پس از آنجا بيرون رو كه تو رانده‌اى'),
(1837, 15, 35, 'و همانا تا روز پاداش و كيفر بر تو لعنت است.'),
(1838, 15, 36, 'گفت: خداوندا، پس مرا تا روزى كه برانگيخته مى‌شوند مهلت ده.'),
(1839, 15, 37, 'گفت: تو از مهلت‌يافتگانى'),
(1840, 15, 38, 'تا روز آن هنگام دانسته.'),
(1841, 15, 39, 'گفت: خداوندا، به سبب آنكه مرا به راه هلاكت افكندى هر آينه در زمين [باطل را] در نظرشان مى‌آرايم- آنان را خواهم فريفت- و همگيشان را به راه هلاكت مى‌برم،'),
(1842, 15, 40, 'مگر آن بندگانت، از ميان آنان، كه پاك و ويژه و برگزيده تو باشند.'),
(1843, 15, 41, '[خداى‌] گفت: اين راهى است راست كه [نگاهداشت آن‌] بر من است.'),
(1844, 15, 42, 'همانا تو را بر بندگان [ويژه‌] من دستيابى و چيرگى نيست، مگر روندگان راه هلاكت كه تو را پيروى كنند.'),
(1845, 15, 43, 'و همانا دوزخ وعده‌گاه همه آنهاست'),
(1846, 15, 44, 'آن را هفت در است كه هر درى را بخشى جداگانه از آنان'),
(1847, 15, 45, 'همانا پرهيزگاران در بوستانها و چشمه‌سارهايند.'),
(1848, 15, 46, '[گويندشان:] به سلامت و ايمنى در آنها در آييد.'),
(1849, 15, 47, 'و هر بدخواهى و كينه‌اى كه در سينه‌هاشان بود بركنديم برادرانى بر تخت هايى روياروى هم‌اند.'),
(1850, 15, 48, 'در آنجا رنجى بديشان نرسد و از آنجا بيرونشان نكنند.'),
(1851, 15, 49, 'بندگانم را خبر ده كه من آمرزگار و مهربانم'),
(1852, 15, 50, 'و اينكه عذاب من عذاب دردناك است.'),
(1853, 15, 51, 'و آنان را از مهمانان ابراهيم خبر ده.'),
(1854, 15, 52, 'آنگاه كه بر او در آمدند و سلام گفتند. [ابراهيم‌] گفت: ما از شما هراسانيم.'),
(1855, 15, 53, 'گفتند: مترس، ما تو را به پسرى دانا مژده مى‌دهيم.'),
(1856, 15, 54, 'گفت: آيا مرا با آنكه پيرى به من رسيده مژده مى‌دهيد؟ به چه چيز مژده مى‌دهيد؟'),
(1857, 15, 55, 'گفتند: تو را درست مژده داديم، پس از نوميدان مباش.'),
(1858, 15, 56, 'گفت: جز گمراهان چه كسى از رحمت پروردگار خويش نوميد مى‌شود؟'),
(1859, 15, 57, 'گفت: اى فرستادگان، كار شما چيست؟'),
(1860, 15, 58, 'گفتند: ما به سوى گروهى بزهكار فرستاده شده‌ايم.'),
(1861, 15, 59, 'مگر خاندان لوط، كه همگيشان را رهاننده‌ايم،'),
(1862, 15, 60, 'جز زن او، كه مقدّر كرده‌ايم كه از ماندگان باشد [و هلاك شود].'),
(1863, 15, 61, 'پس چون فرستادگان به نزد خاندان لوط آمدند،'),
(1864, 15, 62, '[لوط] گفت: شما گروهى ناشناخته و بيگانه‌ايد.'),
(1865, 15, 63, 'گفتند: بلكه تو را چيزى آورده‌ايم كه در باره آن شك مى‌كردند- يعنى عذاب-.'),
(1866, 15, 64, 'و تو را [خبر] راست و درست آورده‌ايم و ما راستگويانيم.'),
(1867, 15, 65, 'پس به پاسى از شب خاندان خويش را بيرون بر و خود از پى آنها روانه شو، و هيچ يك از شما به واپس ننگرد، و به آنجا كه فرمانتان دهند برويد.'),
(1868, 15, 66, 'و آن كار را به او وحى كرديم- يا خبر داديم- كه بيخ و بنياد آنان هنگامى كه بامداد كنند بريده و بركنده خواهد شد.'),
(1869, 15, 67, 'و اهل آن شهر شادى‌كنان بيامدند.'),
(1870, 15, 68, 'گفت: اينها مهمانان من‌اند، مرا رسوا مكنيد'),
(1871, 15, 69, 'و از خدا بترسيد و مرا شرمنده مسازيد.'),
(1872, 15, 70, 'گفتند: مگر ما تو را از [ميزبانى و حمايت‌] مردمان باز نداشته بوديم؟'),
(1873, 15, 71, 'گفت: اينان دختران من‌اند- مى‌توانيد با آنان ازدواج كنيد- اگر مى‌خواهيد كارى بكنيد- يعنى اگر قصد زناشويى داريد-.'),
(1874, 15, 72, 'به جان تو سوگند كه آنها در مستى و گمراهى خويش سرگشته و كوردل مى‌شدند.'),
(1875, 15, 73, 'پس هنگام برآمدن آفتاب بانگ هولناك كشنده بگرفتشان'),
(1876, 15, 74, 'آنگاه آن شهر را زير و رو كرديم و بر آنها سنگهايى از سجيل- سنگ گِل، كلوخى از سنگ و گِل- باريديم'),
(1877, 15, 75, 'هر آينه در اين [داستان‌] هوشمندان را نشانه‌ها و عبرتهاست.'),
(1878, 15, 76, 'و آن شهر- سدوم- هنوز بر سر راهى است كه بر جاى مانده است- كاروانيان در آن رفت‌وآمد دارند-.'),
(1879, 15, 77, 'و هر آينه مؤمنان را در آن نشانه‌اى و عبرتى است.'),
(1880, 15, 78, 'و همانا مردم ايكه- قوم شُعيب- ستمكار بودند.'),
(1881, 15, 79, 'پس، از آنان كين ستانديم و هر آينه آن دو شهر- شهر لوط و شهر شعيب- بر رهگذرى آشكار و هويداست.'),
(1882, 15, 80, 'و هر آينه مردم حِجر- ثمود، قوم صالح- فرستادگان را دروغگو شمردند.'),
(1883, 15, 81, 'و نشانه‌هاى خود را به آنان داديم پس، از آنها رو گردانيدند.'),
(1884, 15, 82, 'و از كوه‌ها خانه‌ها مى‌تراشيدند در حالى كه ايمن و آسوده بودند.'),
(1885, 15, 83, 'پس بانگ عذاب هنگامى كه به بامداد در آمدند بگرفتشان.'),
(1886, 15, 84, 'و آنچه مى‌كردند هيچ سودشان نداشت- عذاب را از آنان باز نداشت-.'),
(1887, 15, 85, 'و ما آسمان و زمين و آنچه را كه ميان آنهاست جز براستى و درستى نيافريديم، و قيامت هر آينه آينده است، پس درگذر، درگذشتن نيكو.'),
(1888, 15, 86, 'همانا پروردگار تو هموست آفريدگار دانا.'),
(1889, 15, 87, 'و هر آينه تو را «سبع المثانى» و قرآن بزرگ [قدر] داديم.'),
(1890, 15, 88, 'چشمان خويش را به آنچه گروه‌هايى از آنان- كافران- را بدان برخوردارى داديم مگشاى- به دنيا داران منگر-، و بر آنان اندوه مخور [كه چرا ايمان نمى آورند] و بال خويش براى مؤمنان فرودآر- مؤمنان را فروتن باش، يا با ايشان به نرمى و مدارا زندگانى كن-.'),
(1891, 15, 89, 'و بگو: همانا من بيم‌دهنده آشكارم [به عذابى كه بر شما فرود آيد]،'),
(1892, 15, 90, 'آن سان كه [عذابى‌] بر بخش‌كنندگان فروفرستاديم'),
(1893, 15, 91, 'آنها كه قرآن را پاره‌ها كردند- به سحر و شعر و كهانت و افسانه‌هاى پيشينيان نسبت دادند-'),
(1894, 15, 92, 'به پروردگارت سوگند كه همگيشان را بازخواست مى‌كنيم،'),
(1895, 15, 93, 'از آنچه مى‌كردند.'),
(1896, 15, 94, 'پس آنچه را بدان فرمان يافتى آشكار كن و از مشركان روى بگردان.'),
(1897, 15, 95, 'هر آينه ما تو را از استهزا كنندگان كفايت كرديم'),
(1898, 15, 96, 'آنان كه با خداى يكتا خدايى ديگر گيرند، پس زودا كه [سرانجام گمراهى و كيفر خويش‌] بدانند.'),
(1899, 15, 97, 'و هر آينه ما مى‌دانيم كه سينه‌ات بدانچه مى‌گويند تنگ مى‌گردد.'),
(1900, 15, 98, 'پس پروردگارت را با سپاس و ستايش به پاكى ياد كن و از سجده آرندگان باش.'),
(1901, 15, 99, 'و پروردگارت را پرستش كن تا آنگاه كه تو را يقين- مرگ- فرا رسد.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'فرمان خدا- به عذاب مشركان و كافران با غلبه اسلام- آمد، پس آن را به شتاب مخواهيد. پاك است و برتر از آنچه [با او] انباز مى‌گيرند.'),
(1903, 16, 2, 'فرشتگان را با روح- فرشته بزرگوارى كه همراه وحى است- به خواست و فرمان خويش بر هر كس از بندگانش كه بخواهد فرو مى‌فرستد كه [مردم را] بيم دهيد كه خدايى جز من نيست پس، از من پروا داشته باشيد.'),
(1904, 16, 3, 'آسمانها و زمين را به حق آفريد برتر است از آنچه [با وى‌] انباز مى‌گيرند.'),
(1905, 16, 4, 'آدمى را از نطفه‌اى [بويناك‌] بيافريد، و آنگاه او ستيزه‌گرى آشكار است.'),
(1906, 16, 5, 'و چارپايان- گوسفند و گاو و شتر و مانند اينها- را آفريد كه در آنها براى شما گرمى- پوشش گرم- و سودهاست، و از [شير و گوشت‌] آنها مى‌خوريد.'),
(1907, 16, 6, 'و شما را در آنها شبانگاه كه از چراگاه باز مى‌آريد و بامدادان كه به چراگاه مى‌بريد نشانى از آرايش و شكوه است'),
(1908, 16, 7, 'و بارهاى شما را به شهرى مى‌برند كه جز به دشوارى و رنج خويشتن نمى‌توانستيد به آن رسيد همانا پروردگار شما رؤوف- دلسوز و پُرمهر- و مهربان است'),
(1909, 16, 8, 'و اسبان و استران و خران را [آفريد] تا بر آنها سوار شويد و [شما را] آرايشى باشد و چيزها مى‌آفريند كه نمى‌دانيد.'),
(1910, 16, 9, 'و [نمودن‌] راه راست بر خداست، و برخى از آنها- راه‌ها- كج است. و اگر خدا مى‌خواست همه شما را راه مى‌نمود.'),
(1911, 16, 10, 'اوست آن كه از آسمان آبى فرو فرستاد كه شما را از آن آشاميدنى است و از آن درخت و گياه مى‌رويد كه در آن [چارپايان را] مى‌چرانيد'),
(1912, 16, 11, '[و] با آن براى شما كشت- گندم و جو و حبوبات- و زيتون و خرمابنها و تاكها و از همه ميوه‌ها بروياند همانا در آن نشانه‌اى است براى مردمى كه مى انديشند.'),
(1913, 16, 12, 'و شب و روز و خورشيد و ماه را براى شما رام كرد، و ستارگان به فرمان او رام شده‌اند. همانا در آن نشانه‌هاست براى مردمى كه خرد را كار مى‌بندند'),
(1914, 16, 13, 'و آنچه براى شما در زمين به رنگهاى گوناگون آفريد [مسخّر شما كرد] همانا در آن نشانه‌اى است براى مردمى كه ياد كنند و پند گيرند.'),
(1915, 16, 14, 'و اوست آن كه دريا را رام كرد تا از آن گوشت تازه بخوريد و از آن زيورى بيرون آريد كه آن را مى‌پوشيد و در آن كشتى‌ها را بينى كه شكافندگان [آب‌] اند، [تا بدان برخوردار شويد- از راه دريا براى سياحت و تجارت و تحصيل دانش و مانند اينها سفر كنيد-] و تا از فزونى و بخشش خدا- يعنى روزى- بجوييد، و شايد سپاس داريد.'),
(1916, 16, 15, 'و در زمين كوه‌هاى استوار درافكند تا شما را نجنباند، و رودها و راه‌ها [پديد كرد]، شايد راه يابيد.'),
(1917, 16, 16, 'و نشانه‌ها [قرار داد]، و به ستارگان راه [خود را] مى‌يابند.'),
(1918, 16, 17, 'آيا آن كه مى‌آفريند، مانند كسى است كه [هيچ چيز] نمى‌آفريند؟ آيا ياد نمى‌كنيد و پند نمى‌گيريد؟!'),
(1919, 16, 18, 'و اگر [بخواهيد] نعمت خداى را بشمريد، شماركردن آن نتوانيد. همانا خدا آمرزگار و مهربان است.'),
(1920, 16, 19, 'و خدا آنچه را نهان مى‌داريد و آنچه را آشكار مى‌كنيد مى‌داند.'),
(1921, 16, 20, 'و آنهايى كه به جاى خداى يكتا مى‌خوانند- مى‌پرستند- چيزى نمى‌آفرينند و خود آفريده مى‌شوند،'),
(1922, 16, 21, 'مُردگانند نه زندگان و نمى‌دانند كى برانگيخته مى‌شوند.'),
(1923, 16, 22, 'خداى شما خدايى يگانه است، پس كسانى كه به سراى واپسين ايمان ندارند دلهاشان ناشناسنده است- با آن بيگانه است- و آنان گردنكشانند.'),
(1924, 16, 23, 'بى‌گمان خدا آنچه را نهان مى‌دارند و آنچه را آشكار مى‌كنند مى‌داند. همانا او گردنكشان را دوست ندارد.'),
(1925, 16, 24, 'و چون گويندشان: پروردگارتان چه چيز فرو فرستاده؟ گويند: افسانه‌هاى پيشينيان،'),
(1926, 16, 25, 'تا بار گناهان خويش در روز رستاخيز به تمامى بردارند، و هم از بار گناهان كسانى كه به بى‌دانشى آنان را گمراه مى‌كنند آگاه باشيد كه بد بارى برمى‌دارند.'),
(1927, 16, 26, 'همانا كسانى كه پيش از آنان بودند نيرنگ و بدانديشى كردند پس خدا آهنگ بناهاى ايشان كرد و آنها را از پايه‌ها برانداخت و سقف از بالاى سرشان بر آنها فرو افتاد و از جايى كه نمى‌فهميدند عذاب بديشان رسيد'),
(1928, 16, 27, 'سپس در روز رستاخيز رسواشان كند و گويد: كجايند آن انبازان من كه شما در باره آنها با ديگران- پيامبر و مؤمنان- ستيزه مى‌كرديد؟ كسانى كه دانش دادنشان گويند: همانا امروز رسوايى و بدى- يعنى سختى عذاب- بر كافران است'),
(1929, 16, 28, 'آنان كه فرشتگان جانشان را مى‌گيرند در حالى كه [به كفر و شرك و كردار بد] بر خويش ستمكار بوده‌اند، پس سر تسليم فرود آرند [و گويند] كه ما هيچ كار بدى نمى‌كرديم. آرى، خدا به آنچه مى‌كرديد داناست.'),
(1930, 16, 29, 'پس به درهاى دوزخ در آييد كه جاودانه در آنجا خواهيد ماند، و براستى بد است جايگاه گردنكشان.'),
(1931, 16, 30, 'و به كسانى كه پرهيزگارى كردند گفته شود: پروردگار شما چه چيز فرو فرستاده است؟ گويند: خير را براى كسانى كه نيكويى كردند در اين جهان نيكى است، و همانا سراى واپسين بهتر است، و هر آينه نيكوست سراى پرهيزگاران'),
(1932, 16, 31, 'بهشت هايى پاينده كه به آنها در آيند، از زير [درختان‌] آنها جويها روان است، در آنجا ايشان راست هر چه بخواهند. خداوند پرهيزگاران را اينچنين پاداش مى‌دهد'),
(1933, 16, 32, 'آنان كه [از آلودگى‌هاى شرك و گناه‌] پاكند، فرشتگانى كه جانشان را مى‌ستانند، گويند: درود بر شما، به پاداش كارهايى كه مى‌كرديد به بهشت در آييد.'),
(1934, 16, 33, 'آيا [كافران‌] جز اين چشم مى‌دارند كه فرشتگان [براى گرفتن جانشان‌] بديشان بيايند يا فرمان پروردگارت [به عذاب آنان‌] بيايد؟ كسانى كه پيش از آنان بودند نيز چنين مى‌كردند، و خدا به آنها ستم نكرد و ليكن آنها بر خويش ستم مى‌كردند'),
(1935, 16, 34, 'پس كيفر كارهاى بدشان به آنان رسيد و همان چيزى كه به آن استهزا مى‌كردند- يعنى عذاب- آنان را فرو گرفت.'),
(1936, 16, 35, 'و كسانى كه شرك آوردند گفتند: اگر خدا مى‌خواست جز او هيچ چيز نمى‌پرستيديم، نه ما و نه پدران ما، و بى [خواست و فرمان‌] او هيچ چيز را حرام نمى‌كرديم. كسانى كه پيش از آنها بودند نيز چنين كردند. پس آيا بر پيامبران جز رساندن پيام روشن و آشكار [تكليفى‌] هست؟'),
(1937, 16, 36, 'و هر آينه در هر امتى پيامبرى برانگيختيم كه خداى را بپرستيد و از طاغوت- هر معبودى جز خدا- دورى جوييد، پس برخى از آنان را خدا راه نمود و برخى ديگر گمراهى بر آنها سزا گشت، پس در زمين بگرديد و بنگريد كه سرانجام دروغ‌انگاران چگونه بود.'),
(1938, 16, 37, 'اگر بر راهنمايى آنان حرص ورزى، [بدان كه‌] خدا هر كه را گمراه كند راه نخواهد نمود و آنها را هيچ ياورانى نيست.'),
(1939, 16, 38, 'و به خدا سوگند خوردند، سوگندهاى سخت، كه خدا آن كس را كه بميرد بر نيانگيزد. آرى، [بر انگيختن و رستاخيز] وعده‌اى است راست و درست بر او [كه آن را وفا مى‌كند]، و ليكن بيشتر مردم نمى‌دانند'),
(1940, 16, 39, 'تا آنچه را كه در آن اختلاف مى‌كنند برايشان روشن و آشكار كند و تا كافران بدانند كه دروغزن بودند.'),
(1941, 16, 40, 'همانا گفتار ما براى چيزى- چون [پديد آمدن‌] آن را بخواهيم- اين است كه آن را مى‌گوييم: باش، پس مى‌باشد.'),
(1942, 16, 41, 'و كسانى كه پس از آنكه ستم ديدند در [راه‌] خدا هجرت كردند، آنان را در اين جهان جايگاهى نيكو دهيم، و هر آينه مزد آن جهان بزرگتر است، اگر مى‌دانستند'),
(1943, 16, 42, 'آنان كه شكيبايى نمودند و بر پروردگارشان توكل مى‌كنند.'),
(1944, 16, 43, 'و پيش از تو نفرستاديم مگر مردانى را كه به ايشان وحى مى‌كرديم- و اگر خود نمى‌دانيد از اهل ذكر- يعنى دانشمندان اهل كتاب- بپرسيد-،'),
(1945, 16, 44, 'با حجتهاى روشن و كتابهاى پندآموز [فرستاديم‌]، و اين ذكر- ياد و پند قرآن- را به تو فروفرستاديم تا براى مردم آنچه را كه به سوى آنان فروفرستاده شده است روشن بيان كنى و تا شايد بينديشند.'),
(1946, 16, 45, 'پس آيا كسانى كه بديها به نيرنگ كردند ايمن شده‌اند از اينكه خداوند آنان را به زمين فرو ببرد، يا از جايى كه آگاهى ندارند عذاب بديشان آيد؟'),
(1947, 16, 46, 'يا آنها را در آمد و شدشان- سرگرميهاى دنيا- بگيرد، پس [خداى را به گريز خود] ناتوان‌كننده نيستند'),
(1948, 16, 47, 'يا آنان را بر حال ترس و بيم- يا كاستن نعمتها- بگيرد، همانا پروردگارتان رؤوف و مهربان است- كه در عقوبتتان شتاب نمى‌كند-.'),
(1949, 16, 48, 'و آيا به آنچه خداى آفريده است ننگريستند كه سايه‌هاى آن از راست و چپ- يعنى از هر سو- خداى را سجده‌كنان باز گردد در حالى كه [در برابر او] خاكسارند؟'),
(1950, 16, 49, 'و هر چه در آسمانها و هر چه در زمين است از جنبندگان و فرشتگان، خداى را سجده مى‌آرند و سرپيچى و گردنكشى نمى‌كنند'),
(1951, 16, 50, 'از پروردگار خود كه بر فراز آنهاست مى‌ترسند و آنچه فرمان داده مى‌شوند همان مى‌كنند.'),
(1952, 16, 51, 'و خداى گفت: دو خدا مگيريد- مپرستيد-، جز اين نيست كه او خدايى يگانه است، پس از من بترسيد و بس.'),
(1953, 16, 52, 'و او راست آنچه در آسمانها و زمين است و پرستش و فرمانبردارى هميشه و پيوسته براى اوست. آيا از غير خداى يكتا پروا مى‌كنيد؟!'),
(1954, 16, 53, 'و هر نعمتى كه شما راست از خداست، و چون گزندى به شما رسد به پيشگاه او ناله و زارى مى‌كنيد'),
(1955, 16, 54, 'و چون آن گزند را از شما برداشت آنگاه گروهى از شما به پروردگارشان شرك مى‌آورند،'),
(1956, 16, 55, 'تا بدانچه بداديمشان ناسپاسى كنند. پس برخوردار شويد، زودا كه بدانيد.'),
(1957, 16, 56, 'و براى آنچه نمى‌دانند- بتها- بهره‌اى از آنچه روزيشان كرده‌ايم قرار مى‌دهند. به خدا سوگند كه از آن دروغى كه مى‌بافيد پرسيده خواهيد شد.'),
(1958, 16, 57, 'و براى خدا دختران مى‌انگارند- او پاك و منزه است [از داشتن فرزند]- و براى خودشان آنچه آرزو مى‌كنند و مى‌خواهند- يعنى پسران-!'),
(1959, 16, 58, 'و چون يكى از آنان را به دختر مژده دهند رويش سياه گردد- از خشم بر زن خود كه چرا دختر زاده است- و خشم خود فرو مى‌خورد'),
(1960, 16, 59, 'از بدى [و اندوه‌] آنچه به او مژده داده‌اند از مردم پنهان مى‌شود [و با خود مى‌انديشد كه‌] آيا با خوارى و سرافكندگى نگاهش دارد يا در خاك پنهانش كند؟ آگاه باشيد كه بد است آن داورى كه مى‌كنند.'),
(1961, 16, 60, 'كسانى را كه به جهان واپسين ايمان نمى‌آورند صفت بد است، و خداى راست صفت والا و برتر و اوست تواناى بى‌همتا و داناى با حكمت.'),
(1962, 16, 61, 'و اگر خدا مردم را به [سزاى‌] ستمشان بگيرد هيچ جنبنده‌اى را بر آن'),
(1963, 16, 62, 'و آنچه را خوش ندارند- يعنى دختران- براى خدا قرار مى‌دهند و زبانهاشان دروغ مى‌سازد كه نيكويى- زندگانى نيك يا پاداش نيكو در آخرت- آنان راست براستى آتش دوزخ آنان راست و آنها [سوى آن‌] پيشگامانند.'),
(1964, 16, 63, 'به خدا سوگند كه هر آينه ما به سوى امتهايى كه پيش از تو بودند [پيامبرانى‌] فرستاديم پس شيطان كارهاشان را برايشان بياراست. از اين‌رو امروز او دوست و سرپرست آنهاست و آنان را عذابى است دردناك.'),
(1965, 16, 64, 'و بر تو اين كتاب فرو نفرستاديم مگر از بهر آنكه آنچه در آن اختلاف كردند برايشان بيان كنى، و راهنما و بخشايشى باشد براى مردمى كه ايمان مى آورند.'),
(1966, 16, 65, 'و خداست كه از آسمان آبى فرو فرستاد پس با آن زمين را پس از مردگى‌اش زنده كرد. همانا در آن نشانه‌اى است براى مردمى كه بشنوند.'),
(1967, 16, 66, 'و هر آينه براى شما در چارپايان عبرتى است. شما را از آنچه در شكمهاى آنهاست از ميان سرگين- فضله حيوانات كه درون روده‌هاست- و خون، شيرى ناب و پاكيزه مى‌نوشانيم كه آشامندگان را گواراست.'),
(1968, 16, 67, 'و نيز از ميوه‌هاى خرمابنها و تاكها كه از آنها شراب مست‌كننده و روزيى نيكو برمى‌گيريد همانا در آن نشانه‌اى است براى گروهى كه خرد را به كار برند.'),
(1969, 16, 68, 'و پروردگار تو به زنبور عسل الهام كرد كه از كوه‌ها و درختان و از داربستهايى كه [مردمان از تاكها] مى‌سازند خانه‌هايى برگير'),
(1970, 16, 69, 'آنگاه از همه ميوه‌ها بخور و در راه‌هاى پروردگارت رام و فرمانبردار مى‌رو- يا در راه‌هاى هموار پروردگارت مى‌رو- از شكمش آشاميدنيى رنگارنگ بيرون مى‌آيد كه در آن براى مردم شفاست همانا در آن نشانه‌اى است براى مردمى كه بينديشند.'),
(1971, 16, 70, 'و خدا شما را آفريد سپس جان شما را برگيرد و از شما كس هست كه به فروترين [دوران‌] عمر- پيرى و فرتوتى- بازگردانده شود تا [سرانجام‌] پس از دانايى چيزى نداند. همانا خدا دانا و تواناست.'),
(1972, 16, 71, 'و خداوند برخى از شما را در روزى بر برخى فزونى داد پس كسانى كه فزونى داده شده‌اند بر آن نيستند كه روزى خويش به زيردستان و بردگان خود باز دهند تا همه در روزى برابر و يكسان گردند- پس چرا بندگان و آفريدگان خدا را با او انباز مى‌گيرند و در عبادت به آنها روى مى‌آورند؟! زهى نادانى و ناسپاسى!- پس آيا نعمت خداى را انكار مى‌كنند؟!'),
(1973, 16, 72, 'و خدا براى شما از [جنس‌] خودتان همسرانى و از همسرانتان برايتان فرزندان و نوادگان و دامادان پديد كرد و شما را از پاكيزه‌ها روزى داد. پس آيا به باطل مى‌گروند و به نعمت خدا كافر مى‌شوند و ناسپاسى مى‌كنند؟!'),
(1974, 16, 73, 'و به جاى خدا چيزهايى را مى‌پرستند كه مالك هيچ روزيى از آسمانها و زمين براى آنها نيستند و هرگز نمى‌توانند [كه روزى دهند].'),
(1975, 16, 74, 'پس براى خدا مَثَلها [ى ناروا] مزنيد- او را به داشتن زن و فرزند و انباز وصف مكنيد- همانا خدا مى‌داند و شما نمى‌دانيد.'),
(1976, 16, 75, 'خدا بنده زرخريدى را مَثَل مى‌زند كه بر هيچ چيز توانايى ندارد و آن [انسان آزادى‌] كه از جانب خويش او را روزى نيكو داده‌ايم و او نهان و آشكارا از آن انفاق مى‌كند. آيا هرگز برابرند؟- هنگامى كه توانا و ناتوان برابر نيستند، پس چگونه سنگ و خدايى كه بر هر چه بخواهد تواناست و همه آفريدگان را روزى مى‌دهد برابر باشند-؟! همه سپاس و ستايش خداى راست، بلكه بيشترشان نمى‌دانند.'),
(1977, 16, 76, 'و خدا دو مرد را مَثَل مى‌زند: يكى از آن دو گنگ است كه بر هيچ چيز توانايى ندارد و سربار خواجه خويش است، هر جا كه او را فرستد نيكى و سودى نيارد آيا او با كسى كه به عدل و داد فرمان مى‌دهد و بر راه راست است برابر است؟'),
(1978, 16, 77, 'و خداى راست نهان و ناپيداى آسمانها و زمين و كار [برپايى‌] رستاخيز جز مانند يك چشم بر هم زدن نيست يا نزديكتر از آن است همانا خداوند بر هر چيزى تواناست.'),
(1979, 16, 78, 'و خدا شما را از شكم مادرانتان بيرون آورد در حالى كه هيچ چيز نمى‌دانستيد و براى شما گوش و ديدگان و دلها قرار داد تا شايد سپاس داريد.'),
(1980, 16, 79, 'آيا به پرندگان رام‌شده در فضاى آسمان ننگريستند؟ هيچ كس جز خدا آنها را نگاه نمى‌دارد. همانا در آن براى مردمى كه ايمان آورند نشانه‌هاست.'),
(1981, 16, 80, 'و خدا براى شما از خانه‌هايتان جاى سكونت و آرامش ساخت و براى شما از پوستهاى چارپايان خانه‌ها- خيمه‌ها و خرگاه‌ها- قرار داد تا روز سفرتان و روز ماندنتان سبكبار باشيد، و از پشمها و كركها و مويهاى آنها اثاث خانه و كالا پديد كرد تا هنگامى- هنگام از ميان رفتن آنها يا مرگ شما-.'),
(1982, 16, 81, 'و خدا از آنچه آفريد براى شما سايبانها- مانند درختان و سقفها- كرد و براى شما از كوه‌ها نهانگاه‌ها- غارها- قرار داد و براى شما جامه‌ها ساخت كه شما را از گرما نگاه مى‌دارد و جامه هايى- زره- كه از سختى و آسيب جنگتان نگاه مى‌دارد اينچنين نعمت خويش را بر شما تمام مى‌كند باشد كه گردن نهيد و فرمان بريد.'),
(1983, 16, 82, 'پس اگر پشت‌كرده برگردند، همانا بر تو رساندن روشن و آشكار پيام است و بس.'),
(1984, 16, 83, 'نعمت خداى را مى‌شناسند آنگاه انكارش مى‌كنند و بيشترشان كافر و ناسپاسند.'),
(1985, 16, 84, 'و [ياد كن‌] روزى كه از هر امتى گواهى برانگيزيم آنگاه به كسانى كه كافر بودند نه اجازه [سخن گفتن‌] دهند و نه از آنها خواهند كه عذرخواهى كنند- يا كارى كنند كه خداى از آنها خشنود گردد زيرا كه آخرت سراى تكليف نيست-.'),
(1986, 16, 85, 'و چون كسانى كه ستم كردند عذاب را ببينند، نه عذابشان سبك شود و نه مهلت داده شوند.'),
(1987, 16, 86, 'و چون كسانى كه شرك ورزيدند شريكان خويش را- بتها يا كسانى را كه شريك خدا گرفته بودند- ببينند گويند: بار خدايا، اينها شريكان مايند كه به جاى تو مى‌خوانديم آنها پاسخشان دهند كه شما دروغگوييد.'),
(1988, 16, 87, 'و آن روز به پيشگاه خدا سر تسليم افكنند و آن دروغها كه مى‌بافتند از آنها گم و نابود شود.'),
(1989, 16, 88, 'كسانى كه كافر شدند و [مردم را] از راه خدا بازداشتند، آنان را به سزاى آن تباهكاريها كه مى‌كردند عذابى بر عذابشان بيفزاييم.'),
(1990, 16, 89, 'و روزى كه در هر امتى گواهى بر آنان از خودشان برانگيزيم و تو را گواه بر اينان- اين امّت- بياريم و ما اين كتاب را كه بيانى رسا براى هر چيزى و راهنما و بخشايش و مژده براى مسلمانان است بر تو فروفرستاديم.'),
(1991, 16, 90, 'همانا خداى به داد و نيكوكارى و دادن [حقّ‌] خويشاوند- صله رحم و نيكى كردن به خويشان- فرمان مى‌دهد و از زشتكارى و كار ناپسند و ستم و سركشى باز مى‌دارد شما را پند مى‌دهد شايد كه ياد كنيد و پند گيريد.'),
(1992, 16, 91, 'و به پيمان خدا- هر كار نيكى كه با خدا عهد كرده باشيد مانند نذر و جز آن-، چون پيمان بستيد، وفا كنيد و سوگندها را، پس از استواركردن آنها، مشكنيد، كه خداى را بر خويشتن ضامن- يا گواه و نگهبان- كرده‌ايد همانا خدا مى‌داند كه چه مى‌كنيد.'),
(1993, 16, 92, 'و مانند آن زن مباشيد كه رشته خويش را، پس از استوار تابيدن، بازگشود و گسست سوگندهاى خويش را [دستاويز] فريب و خيانت ميان خودتان مى گيريد به سبب آنكه گروهى- كفار قريش- افزونتر از گروه ديگر- مؤمنان- است- فزونى افراد و مال نبايد سبب پيمان‌شكنى باشد-، همانا خدا شما را بدان مى‌آزمايد و هر آينه آنچه را در آن اختلاف مى‌كرديد در روز رستاخيز برايتان روشن مى‌كند.'),
(1994, 16, 93, 'و اگر خدا مى‌خواست شما را يك امّةت كرده بود- همه را جَبراً بر ايمان جمع مى‌كرد تا ميان شما هيچ اختلافى نباشد ولى اين جبر و اكراه خلاف مشيّت و سنّت الهى است- و ليكن هر كه را خواهد گمراه كند و هر كه را خواهد راه نمايد و هر آينه از كارهايى كه مى‌كرديد پرسيده و بازخواست خواهيد شد.'),
(1995, 16, 94, 'و سوگندهاى خويش را [دستاويز] فريب و خيانت ميان خودتان مسازيد تا مبادا گامى پس از استوارشدنش بلغزد- پيمانها شكسته شود-، و به سزاى آنكه از راه خدا- يعنى وفاى به پيمان- باز ايستاده و رويگردان شده‌ايد بدى- كيفر پيمان‌شكنى- را بچشيد و شما را عذابى بزرگ باشد.'),
(1996, 16, 95, 'و در برابر پيمان خدا بهاى اندكى نستانيد، كه آنچه نزد خداست براى شما بهتر است، اگر مى‌دانستيد.'),
(1997, 16, 96, 'آنچه نزد شماست پايان پذيرد و آنچه نزد خداست ماندنى است، و كسانى كه شكيبايى ورزيدند هر آينه مزدشان را بر پايه نيكوترين كارى كه مى كردند پاداش خواهيم داد.'),
(1998, 16, 97, 'هر كس از مرد و زن كه كارى نيك و شايسته كند در حالى كه مؤمن باشد هر آينه او را به زندگانى پاك و خوشى زنده بداريم، و مزدشان را بر پايه نيكوترين كارى كه مى‌كردند پاداش دهيم.'),
(1999, 16, 98, 'و چون قرآن بخوانى از شيطان رانده‌شده به خداى پناه جوى'),
(2000, 16, 99, 'همانا او را بر كسانى كه ايمان دارند و بر پروردگارشان توكّل مى‌كنند تسلطى نيست'),
(2001, 16, 100, 'تسلّط او تنها بر كسانى است كه او را دوست و سرپرست خود گرفته‌اند و كسانى كه براى او'),
(2002, 16, 101, 'و چون آيه‌اى را جايگزين آيه‌اى كنيم- و خدا به آنچه فرومى‌فرستد داناتر است- گويند: همانا تو دروغبافى. [چنين نيست‌] بلكه بيشترشان نادانند.'),
(2003, 16, 102, 'بگو: آن را روح پاك- جبرئيل- از سوى پروردگارت براستى و درستى فروفرستاده تا كسانى را كه ايمان آورده‌اند استوار و برجاى بدارد، و مسلمانان- گردن‌نهادگان- را راهنمايى و مژده‌اى باشد.'),
(2004, 16, 103, 'و هر آينه مى‌دانيم كه آنان مى‌گويند: آدميى به او مى‌آموزد زبان آن كس كه [اين قرآن را] به او نسبت مى‌دهند گنگ و نارساست و اين [قرآن‌] به زبان تازى روشن است.'),
(2005, 16, 104, 'كسانى كه به آيات خدا ايمان نمى‌آورند خداوند آنان را راه ننمايد و آنها راست عذابى دردناك.'),
(2006, 16, 105, 'همانا كسانى دروغ مى‌بافند كه به آيات خدا ايمان ندارند و آنانند دروغگويان.'),
(2007, 16, 106, 'هر كه پس از ايمان آوردنش به خدا كافر شود، مگر آن كه به ناخواه وادار شود [كه سخنى خلاف ايمانش بگويد] در حالى كه دلش به ايمان آرام است، و ليكن هر كس كه سينه را به كفر گشاده كرد، پس خشم خدا بر آنهاست و آنان را عذابى است بزرگ.'),
(2008, 16, 107, 'اين از آن روست كه آنان زندگى اين جهان را بر آن جهان برگزيده‌اند، و خدا گروه كافران را راه نمى‌نمايد.'),
(2009, 16, 108, 'اينانند آن كسان كه خداى بر دلها و گوش و ديدگانشان مهر نهاده است، و اينانند بى خبران.'),
(2010, 16, 109, 'بى‌گمان آنان در آن جهان زيانكارند.'),
(2011, 16, 110, 'سپس پروردگار تو براى كسانى كه پس از آن شكنجه‌ها كه [از كفار] ديدند هجرت كردند و آنگاه به جهاد برخاستند و شكيبايى نمودند، هر آينه پروردگار تو از آن پس آمرزگار و مهربان است.'),
(2012, 16, 111, 'روزى كه هر كسى بيايد در حالى كه از خويشتن دفاع مى‌كند و به هر كس آنچه كرده تمام دهند و به آنان ستم نشود.'),
(2013, 16, 112, 'و خداوند شهرى را مَثَل مى‌زند كه امن و آرام بود، روزى آن به فراوانى از هر جاى مى‌رسيد، اما [مردم آن‌] نعمتهاى خداى را ناسپاسى كردند، پس خداوند به سزاى آن كارها كه مى‌كردند جامه گرسنگى و ترس به آنها چشانيد.'),
(2014, 16, 113, 'و هر آينه آنان را فرستاده‌اى از خودشان بيامد، اما او را دروغگو انگاشتند، پس عذاب آنان را در حالى كه ستمكار بودند فروگرفت.'),
(2015, 16, 114, 'پس، از آن چيزهاى پاكيزه كه خدا روزيتان كرده بخوريد، و نعمت خداى را سپاس بداريد، اگر او را مى‌پرستيد و بس.'),
(2016, 16, 115, 'جز اين نيست كه بر شما حرام كرده است: مردار و خون و گوشت خوك و آنچه را كه [به هنگام كشتن‌] نام غير خدا بر آن برداشته‌اند، و هر كه [به خوردن اينها] ناچار شود بى‌آنكه ستمگر باشد- كه از روى سركشى يا براى لذت‌جويى بخورد- و از حد بگذرد- بيش از نياز بخورد- پس همانا خدا آمرزگار و مهربان است.'),
(2017, 16, 116, 'و با دروغى كه بر زبانتان مى‌رود مگوييد: اين حلال است و آن حرام، تا بر خدا دروغ بنديد. همانا كسانى كه بر خدا دروغ بندند رستگار نخواهند شد.'),
(2018, 16, 117, '[اين كه دارند] بهره‌اى است اندك، و آنان را عذابى است دردناك.'),
(2019, 16, 118, 'و بر جهودان آنچه را كه پيش از اين بر تو برگفتيم حرام كرديم و ما بر آنان ستم نكرديم بلكه آنها بر خود ستم مى‌كردند.'),
(2020, 16, 119, 'سپس پروردگار تو آنان را كه بدى به نادانى كردند آنگاه از پس آن بازگشتند- توبه نمودند- و به كار نيك و شايسته پرداختند هر آينه پروردگارت پس از آن آمرزگار و مهربان است.'),
(2021, 16, 120, 'همانا ابراهيم [به تنهايى‌] يك امّت بود، خداى را با فروتنى فرمانبردار بود و حقّ‌گراى و يكتاپرست بود، و از مشركان نبود،'),
(2022, 16, 121, 'سپاسدار نعمت‌هاى او بود، [خداى‌] او را برگزيد و به راه راست راه نمود.'),
(2023, 16, 122, 'و او را در اين جهان نيكويى داديم، و هر آينه در آن جهان از شايستگان است.'),
(2024, 16, 123, 'سپس به تو وحى كرديم كه آيين ابراهيم حق‌گراى را پيروى كن، و او از مشركان نبود.'),
(2025, 16, 124, 'همانا [حكم‌] شنبه- شنبه گرفتن، بزرگداشت روز شنبه و تعطيل آن- بر كسانى قرار داده شد كه در آن اختلاف كردند. و هر آينه پروردگارت در روز رستاخيز در باره آنچه اختلاف مى‌كردند ميانشان داورى خواهد كرد.'),
(2026, 16, 125, '[مردم را] با حكمت- گفتار درست و استوار- و پند نيكو به راه پروردگارت بخوان، و با آنان به شيوه‌اى كه نيكوتر است مجادله و گفتگو كن همانا پروردگار تو داناتر است به آن كه از راه او گم شده و او داناتر است به راه‌يافتگان.'),
(2027, 16, 126, 'و اگر سزا مى‌دهيد بمانند آنچه با شما كرده‌اند سزا دهيد. و اگر شكيبايى كنيد هر آينه آن براى شكيبايان بهتر است.'),
(2028, 16, 127, 'و شكيبايى كن و شكيبايى تو جز به [يارى و توفيق‌] خداى نيست، و بر آنان اندوه مخور، و از آن نيرنگ و ترفندى كه مى‌سازند دلتنگ مباش.'),
(2029, 16, 128, 'همانا خدا با كسانى است كه پرهيزگارى كنند و كسانى كه نيكوكارند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'پاك است آن [خداى‌] كه بنده خود- محمد'),
(2031, 17, 2, 'و موسى را كتاب داديم و آن را رهنمونى براى فرزندان اسرائيل قرار داديم كه: جز من كارسازى مگيريد'),
(2032, 17, 3, 'اى فرزندان كسانى كه آنان را با نوح [در كشتى‌] سوار كرديم- يعنى اى بنى اسرائيل- همانا او بنده‌اى سپاسگزار بود- پس شما هم مانند او سپاسگزار باشيد-.'),
(2033, 17, 4, 'و در كتاب- تورات- به فرزندان اسرائيل اعلام كرديم كه هر آينه دو بار در اين سرزمين- بيت المقدس- تباهكارى خواهيد كرد و سركشى و بزرگى خواهيد نمود'),
(2034, 17, 5, 'پس چون هنگام [كيفر] نخستين تباهكارى فرا رسد، بندگانى از خود را، كه سخت جنگاورند، بر شما بگماريم كه به درون خانه‌ها [ى شما] در آيند [و شما را سركوب و زير دست خود كنند] و اين وعده‌اى است انجام‌شدنى'),
(2035, 17, 6, 'سپس شما را دولت باز گردانيم و بر آنها پيروزى دهيم و شما را به مالها و فرزندان يارى كنيم، و بر شمارتان بيفزاييم'),
(2036, 17, 7, 'اگر نيكويى كنيد براى خودتان نيكويى كرده‌ايد، و اگر بدى كنيد به خود كرده‌ايد. پس چون هنگام وعده [كيفر] ديگر رسد [باز هم دشمنان را بر شما چيره گردانيم‌] تا شما را اندوهگين و سيه‌روى سازند، و تا در آن نمازگاه در آيند چنانكه بار نخست در آمدند، و تا به هر چه دست يابند نابود كنند.'),
(2037, 17, 8, 'اميد است كه پروردگارتان بر شما ببخشايد و اگر [به تباهى‌] باز گرديد، [ما نيز به كيفر شما] باز مى‌گرديم و دوزخ را تنگنا و زندان كافران كرده‌ايم.'),
(2038, 17, 9, 'همانا اين قرآن به آيينى درست‌تر و استوارتر راه مى‌نمايد، و مؤمنانى را كه كارهاى نيك و شايسته مى‌كنند مژده مى‌دهد كه ايشان راست مزدى بزرگ.'),
(2039, 17, 10, 'و كسانى را كه به آن جهان ايمان ندارند، عذابى دردناك آماده كرده‌ايم.'),
(2040, 17, 11, 'و آدمى [از روى نادانى و شتاب‌] بدى را مى‌خواهد همان گونه كه نيكى را مى‌خواهد، و آدمى شتابزده است.'),
(2041, 17, 12, 'و شب و روز را دو نشانه ساختيم، پس نشانه شب را زدوديم- تاريكى آن را به تابش خورشيد محو كرديم- و نشانه روز را روشن گردانيديم، تا از پروردگارتان فزونى و بخشش- روزى- بجوييد و تا شمار سالها و ديگر شمارها را بدانيد و هر چيزى را به تفصيل بيان كرديم.'),
(2042, 17, 13, 'و [سرنوشت‌] كردار هر انسانى را در گردنش بسته‌ايم و روز رستاخيز براى او نوشته‌اى بيرون آوريم كه آن را گشوده ببيند'),
(2043, 17, 14, '[گوييم:] نامه‌ات را بخوان، امروز تو بر خود حسابرسى بسنده‌اى.'),
(2044, 17, 15, 'هر كه راه [راست‌] يافت جز اين نيست كه به سود خويش راه يافته و هر كه گمراه شد همانا به زيان خود گمراه شده است و هيچ كس بار گناه ديگرى را بر ندارد و ما [هيچ مردمى را] عذاب‌كننده نباشيم تا آنگاه كه پيامبرى برانگيزيم.'),
(2045, 17, 16, 'و چون بخواهيم [مردم‌] شهرى را هلاك كنيم، كامرانان- نازپرورده‌ها و توانگران- آنجا را فرماييم- توان يا حكومت دهيم- تا در آنجا نافرمانى و گناه كنند، آنگاه آن گفتار- وعده ما به عذاب- بر [مردم‌] آن سزا شود، پس آن را بسختى نابود كنيم.'),
(2046, 17, 17, 'چه بسيار مردمى را كه پس از نوح هلاك كرديم، و همين بس كه پروردگار تو به گناهان بندگانش آگاه و بيناست- گواه ديگرى نخواهد-.'),
(2047, 17, 18, 'هر كه [زندگانى‌] اين جهان شتافته و زودگذر را خواهد- و از آخرت رويگردان باشد-، هر چه بخواهيم [و] براى هر كه بخواهيم در اينجا زودش بدهيم، سپس دوزخ را برايش قرار دهيم كه نكوهيده و به خوارى رانده شده در آنجا در آيد- يا به آتش آن بسوزد-.'),
(2048, 17, 19, 'و هر كه [زندگانى‌] آن جهان خواهد و براى آن كوششى در خور آن كند و مؤمن باشد، پس كوشش آنان سپاس داشته خواهد بود.'),
(2049, 17, 20, 'همه را، اين گروه را و آن گروه را، از دهش پروردگارت مدد رسانيم، و دهش پروردگارت [از كسى‌] باز داشته نيست.'),
(2050, 17, 21, 'بنگر كه چگونه [در دنيا] برخى از آنان- مردم- را بر برخى برترى و فزونى نهاده‌ايم، و هر آينه درجات و پايه‌هاى آن جهان بزرگتر و برتريهايش بيشتر و والاتر است.'),
(2051, 17, 22, 'با خداى يكتا، خداى ديگر مگير، كه نكوهيده و فروگذاشته خواهى نشست.'),
(2052, 17, 23, 'و پروردگار تو حكم كرد كه جز او را نپرستيد و به پدر و مادر نيكى كنيد. اگر يكى از آن دو يا هر دو نزد تو به پيرى رسند پس به آنها «اف»- سخن بيزارى و ناخوشايند- مگو و بر ايشان بانگ مزن- يا: ايشان را از خود مران-، و به ايشان گفتارى نيكو و درخور گراميداشت بگو.'),
(2053, 17, 24, 'و آن دو را از روى مهربانى بال فروتنى فرود آر، و بگو: پروردگارا، آن دو را به پاس آنكه مرا در خردى بپروردند، ببخشاى.'),
(2054, 17, 25, 'پروردگارتان به آنچه در دلهاى شماست داناتر است، اگر نيكان و شايستگان باشيد، همانا او بازگشت‌كنان- توبه‌كنندگان- را آمرزگار است.'),
(2055, 17, 26, 'و حق خويشاوند و درويش بينوا و در راه‌مانده را بده و مال خود را بيهوده و به گزاف مريز و مپاش- تباه مكن-.'),
(2056, 17, 27, 'همانا ريخت و پاش كنندگان- كسانى كه مال را به گزاف تباه مى‌كنند- برادران شيطانهايند و شيطان خداوند خويش را ناسپاس است.'),
(2057, 17, 28, 'و اگر به جستن بخشايشى- به انتظار گشايشى- كه از پروردگارت اميد آن دارى، از آنان روى مى‌گردانى پس [اكنون‌] با آنان سخنى نرم و نيكو گوى.'),
(2058, 17, 29, 'و دست خويش به گردنت مبند- بخل و امساك مكن- و آن را يكسره مگشاى- هر چه دارى به گزاف و زياده روى مده- كه نكوهيده و درمانده بنشينى.'),
(2059, 17, 30, 'همانا پروردگار تو روزى را براى هر كه خواهد فراخ كند و تنگ گرداند، كه او به بندگان خويش آگاه و بيناست.'),
(2060, 17, 31, 'و فرزندان خود را از بيم درويشى مكشيد ما آنها و شما را روزى مى‌دهيم. براستى كشتن آنها خطا و گناهى بزرگ است.'),
(2061, 17, 32, 'و گرد زنا مگرديد، كه آن كارى زشت و راهى بد است.'),
(2062, 17, 33, 'و كسى را كه خدا [كشتن او را] حرام كرده است مكشيد مگر به حق و كسى كه به ستم كشته شود همانا براى ولىّ- وارث و خونخواه- او تسلطى- اختيار اينكه قصاص كند يا ديه بستاند- قرار داده‌ايم پس نبايد در كشتن زياده‌روى كند- به اينكه مانند عادت دوران جاهليت غير از قاتل را بكشد يا قاتل را مُثْله كند-، زيرا كه او'),
(2063, 17, 34, 'و به مال يتيم جز به شيوه‌اى كه نيكوتر است نزديك مشويد تا به نيرو و بلوغ خود برسد. و به پيمان خويش وفا كنيد، كه از پيمان پرسيده خواهد شد.'),
(2064, 17, 35, 'و چون پيمانه كنيد، پيمانه را تمام و درست بدهيد و با ترازوى راست و درست بسنجيد اين بهتر و سرانجامش نيكوتر است.'),
(2065, 17, 36, 'و از پى آنچه بدان دانش ندارى مرو، كه گوش و چشم و دل، از همه اينها بازخواست خواهد شد.'),
(2066, 17, 37, 'و در زمين با بزرگ منشى و سرمستى راه مرو، كه تو زمين را نتوانى شكافت و در بلندى به كوه‌ها نتوانى رسيد.'),
(2067, 17, 38, 'همه اينها گناهش نزد پروردگار تو بد و ناپسند است.'),
(2068, 17, 39, 'اين از آن حكمت- دانش درست- است كه پروردگارت به تو وحى كرده و با خداى يكتا خداى ديگر مگير كه نكوهيده و رانده‌شده در دوزخ افكنده شوى.'),
(2069, 17, 40, 'آيا پروردگارتان شما را به داشتن پسران ويژه ساخت و خود از فرشتگان دخترانى گرفت؟! هر آينه بزرگ‌سخنى مى‌گوييد!'),
(2070, 17, 41, 'و همانا در اين قرآن سخن را گوناگون گردانيديم تا ياد كنند و پند گيرند ولى آنان را جز رميدن نمى‌افزايد.'),
(2071, 17, 42, 'بگو: اگر با خداى، چنانكه مى‌گويند، خدايانى بود آنگاه به خداوند عرش راهى جسته بودند- تا فرمانروايى جهان را از او بستانند-.'),
(2072, 17, 43, 'پاك است او و برتر است از آنچه مى‌گويند، برترى بزرگ.'),
(2073, 17, 44, 'آسمانهاى هفتگانه و زمين و هر كه در آنهاست او را به پاكى مى‌ستايند، و هيچ چيز نيست مگر آنكه او را به پاكى ياد مى‌كند و مى‌ستايد و ليكن شما تسبيح آنها را درنمى‌يابيد. همانا او بردبار و آمرزگار است.'),
(2074, 17, 45, 'و چون قرآن خوانى، ميان تو و ميان كسانى كه به آن جهان ايمان ندارند پرده‌اى پوشيده- ناديدنى، يا: پوشاننده- افكنيم.'),
(2075, 17, 46, 'و بر دلهاى آنان پوششها قرار داديم تا آن را درنيابند، و در گوشهاشان گرانى و سنگينى نهاديم [تا حق را نشنوند]. و چون پروردگارت را در قرآن به يگانگى ياد كنى، رو مى‌گردانند و پشت كرده مى‌رمند.'),
(2076, 17, 47, 'ما داناتريم بدانچه [كافران‌]، آنگاه كه به تو گوش مى‌سپارند، چرا گوش مى‌سپارند و هنگامى كه با هم راز مى‌گويند آن دم كه ستمكاران [به ديگران‌] گويند: شما جز مردى جادوزده را پيروى نمى‌كنيد.'),
(2077, 17, 48, 'بنگر كه چگونه براى تو مَثَلها زدند، پس گمراه شدند و از اين رو راه نتوانند يافت.'),
(2078, 17, 49, 'و گفتند: آيا هنگامى كه ما استخوان و خاك- بهم ريخته و ريز ريز- شديم، آيا ما با آفرينشى نو برانگيخته مى‌شويم؟!'),
(2079, 17, 50, 'بگو: سنگ باشيد يا آهن،'),
(2080, 17, 51, 'يا آفريده‌اى از آنها كه در دلهاتان بزرگ مى‌نمايد، [باز هم شما را پس از مرگ برانگيزند]. پس خواهند گفت: چه كسى ما را باز مى‌گرداند؟ بگو: همان كه شما را بار نخست بيافريد پس سرهاى خويش به سوى تو خواهند جنبانيد و گويند: آن كى خواهد بود؟ بگو: شايد كه نزديك باشد.'),
(2081, 17, 52, 'روزى كه شما را فرا مى‌خواند، پس همراه با سپاس و ستايش او پاسخ مى‌دهيد، و پنداريد كه جز اندكى- در دنيا يا در گور- درنگ نكرده‌ايد.'),
(2082, 17, 53, 'و بندگان مرا بگو سخنى گويند كه نيكوتر است. همانا شيطان ميانشان دشمنى و آشوب مى‌افكند، زيرا كه شيطان آدمى را دشمنى هويداست.'),
(2083, 17, 54, 'پروردگارتان به شما داناتر است. اگر خواهد بر شما ببخشايد، يا اگر خواهد شما را عذاب كند. و ما تو را بر آنان كارساز و نگهبان نفرستاديم.'),
(2084, 17, 55, 'و پروردگار تو به آنچه در آسمانها و زمين است داناتر است، و هر آينه برخى از پيامبران را بر برخى برترى نهاديم و به داوود زبور داديم.'),
(2085, 17, 56, 'بگو: آن كسان را كه به جاى او [معبود و كارساز] پنداشتيد بخوانيد، پس [ببينيد كه‌] نتوانند گزند را از شما بردارند و نه [به سوى ديگر] بگردانند.'),
(2086, 17, 57, 'آنهايى كه [مشركان به خدايى‌] مى‌خوانند، خود به سوى پروردگارشان وسيله‌اى مى جويند تا كداميك از ايشان [به خدا] نزديكتر باشد، و به بخشايش او اميد مى‌دارند و از عذاب او مى‌ترسند، زيرا كه عذاب پروردگارت ترسناك و حذركردنى است.'),
(2087, 17, 58, 'و هيچ آبادى و شهرى نيست مگر آنكه [مردم‌] آن را پيش از روز رستاخيز [به مرگ‌] هلاك كنيم يا به عذابى سخت- قحطى و مانند آن- گرفتار سازيم. اين در آن كتاب- لوح محفوظ- نوشته شده است.'),
(2088, 17, 59, 'و ما را از فرستادن نشانه‌ها- معجزات دلخواهشان- باز نداشت مگر اينكه پيشينيان آن را دروغ انگاشتند، و به ثمود- قوم صالح- آن ماده‌شتر را حجت روشن داديم، پس به آن ستم كردند- آن را كشتند-. و ما نشانه‌ها را جز براى بيم‌كردن نمى‌فرستيم.'),
(2089, 17, 60, 'و [ياد كن‌] آنگاه كه تو را گفتيم: همانا پروردگارت بر مردم احاطه دارد- به قدرت و علم- و آن خوابى را كه به تو نموديم و آن درخت نفرين شده- درخت زقوم- در قرآن را جز براى آزمايش مردم نكرديم- زيرا گفتند آتش درخت را مى‌سوزاند، پس چگونه در آن مى‌رويد-؟! و مى‌ترسانيمشان، ولى آنان را جز سركشى بزرگ نمى‌افزايد.'),
(2090, 17, 61, 'و [ياد كن‌] آنگاه كه فرشتگان را گفتيم: به آدم سجده كنيد، پس سجده كردند مگر ابليس گفت: آيا كسى را سجده كنم كه از گِلى آفريدى؟!'),
(2091, 17, 62, '[و] گفت: مرا بگو: اين است كه بر من بگزيدى و گرامى داشتى؟! اگر مرا تا روز رستاخيز واپس بدارى- مهلت دهى-، هر آينه فرزندان او را لگام و افسار زنم [و به گمراهى كشانم‌] مگر اندكى را.'),
(2092, 17, 63, '[خداى‌] گفت: برو، كه هر كه از آنها تو را پيروى كند پاداشتان دوزخ است، پاداشى تمام.'),
(2093, 17, 64, 'و با آواز خود هر كه را از آنها توانى برانگيز و از جاى ببر، و با سواران و پيادگانت بر آنها بانگ زن [تا به سوى گناه بتازند]، و در مالها و فرزندان انبازشان باش، و به ايشان وعده ده و شيطان آنان را وعده نمى‌دهد مگر به فريب- يا وعده فريبنده-.'),
(2094, 17, 65, 'همانا تو را بر بندگان [ويژه‌] من تسلطى نيست و پروردگار تو [اى پيامبر، براى ايشان‌] كارسازى بسنده است.'),
(2095, 17, 66, 'پروردگار شما آن است كه براى شما كشتى را در دريا مى‌راند تا از فزون‌بخشى او- يعنى روزى- بجوييد، كه او به شما مهربان است.'),
(2096, 17, 67, 'و چون در دريا گزندى به شما رسد، همه آنهايى كه همى‌خوانيد گم شوند- از ياد و خاطر شما بروند- مگر او- خداى يكتا-، پس چون شما را برهاند و به خشكى رساند روى مى‌گردانيد و آدمى بسيار ناسپاس است.'),
(2097, 17, 68, 'آيا ايمنيد از اينكه شما را در سوى خشكى فروبرد يا بر شما تندبادى سنگباران فرستد آنگاه براى خود هيچ كارسازى نيابيد؟'),
(2098, 17, 69, 'يا ايمن شديد از اينكه بار ديگر شما را در آن دريا باز گرداند، پس باد سخت درهم‌شكننده بر شما فرستد تا شما را به سزاى آنكه كافر و منكر شديد- يا ناسپاسى كرديد، كه شما را بار نخست رهانيد- غرق گرداند، آنگاه براى خويش در برابر ما به آن غرقه كردن پى جويى- بازخواست كننده يا كين ستانى- نياييد؟'),
(2099, 17, 70, 'و هر آينه فرزندان آدم را گرامى داشتيم، و در خشكى و دريا [بر مركبها و كشتى‌] برنشانديم، و از چيزهاى پاكيزه روزيشان داديم و آنان را بر بسيارى از آفريدگان خويش برترى كامل بخشيديم.'),
(2100, 17, 71, '[به ياد آر] روزى كه هر گروه از مردم را به پيشوايشان بخوانيم، پس هر كه نامه او را به دست راستش دهند آنان نامه خويش را بخوانند و به اندازه رشته باريك ميان هسته خرما- يعنى اندكى- ستم نبينند.'),
(2101, 17, 72, 'و هر كه در اين جهان كور [دل‌] است، در آن جهان نيز كور و گمراه‌تر باشد.'),
(2102, 17, 73, 'و همانا نزديك بود كه تو را از آنچه به تو وحى كرديم بلغزانند و بگردانند تا بر ما جز آن را [كه وحى كرديم‌] بربافى و آنگاه تو را به دوستى گيرند.'),
(2103, 17, 74, 'و اگر نه آن بود كه تو را استوار داشتيم، نزديك بود كه اندكى به آنان گرايش كنى'),
(2104, 17, 75, 'آنگاه- در صورت گرايش- تو را دو چندان [عذاب‌] در اين زندگى و دو چندان پس از مرگ مى‌چشانديم، و آنگاه براى خويش در برابر ما ياورى نمى‌يافتى.'),
(2105, 17, 76, 'و همانا نزديك بود كه تو را از اين سرزمين برانگيزانند- بى‌آرام كنند و از جا به در برند- تا از آنجا بيرونت كنند، و آنگاه پس از تو درنگ نمى‌كردند مگر اندكى'),
(2106, 17, 77, '[مانند] نهاد و روش [ما در باره‌] آن فرستادگانى كه پيش از تو فرستاديم و در نهاد و روش ما دگرگونى نيابى.'),
(2107, 17, 78, 'نماز را از هنگام گشتن آفتاب- ظهر- تا در آمدن تاريكى شب به پاى دار و [بويژه‌] نماز بامداد را، كه خواندن نماز بامداد در ديد و حضور [فرشتگان شب و روز] است.'),
(2108, 17, 79, 'و پاره‌اى از شب را به آن- خواندن قرآن در نماز- بيدار باش كه اين براى تو افزون [از نمازهاى واجب‌] است. باشد كه پروردگارت تو را در جايگاهى ستوده بدارد.'),
(2109, 17, 80, 'و بگو: پروردگارا، مرا در آر در آوردنى راست و نيكو، و بيرون آر بيرون آوردنى راست و نيكو، و براى من از نزد خويش حجتى يارى‌دهنده قرار ده.'),
(2110, 17, 81, 'و بگو: حق بيامد و باطل نابود شد، كه باطل نابودشدنى است.'),
(2111, 17, 82, 'و قرآن را فروفرستيم كه براى مؤمنان بهبود و بخشايش است، و ستمكاران- كافران- را جز زيانكارى نيفزايد.'),
(2112, 17, 83, 'و چون به آدمى نعمت دهيم، روى بگرداند و [از طاعت ما] دور شود- سركشى و بزرگى نمايد- و چون او را بدى رسد نوميد گردد.'),
(2113, 17, 84, 'بگو: هر كس فرا خور سرشت و عادت و روش خويش كار مى‌كند، و پروردگار شما داناتر است به آن كه راه يافته‌تر است.'),
(2114, 17, 85, 'و تو را از روح پرسند، بگو: روح از فرمان پروردگار من است، و شما را از دانش جز اندكى نداده‌اند.'),
(2115, 17, 86, 'و اگر خواهيم هر آينه آنچه را به تو وحى كرده‌ايم ببريم، آنگاه در باره آن- بردن وحى- براى خويش در برابر ما كارساز و پايمردى نخواهى يافت،'),
(2116, 17, 87, '[و هر چه دارى، نيست‌] مگر بخشايشى از پروردگارت، كه فزونى و بخشش او بر تو بزرگ بوده است.'),
(2117, 17, 88, 'بگو: اگر آدميان و پريان فراهم آيند تا مانند اين قرآن بيارند هرگز مانند آن نيارند هر چند برخى از آنان يار و همپشت برخى باشند.'),
(2118, 17, 89, 'و براستى در اين قرآن براى مردم از هر گونه مثال آورديم- تا راه يابند-، پس بيشتر مردمان سر باز زدند و جز كفر و ناسپاسى نخواستند'),
(2119, 17, 90, 'و گفتند: هرگز تو را باور نداريم تا براى ما از زمين [مكّه‌] چشمه‌اى روان سازى،'),
(2120, 17, 91, 'يا تو را بوستانى باشد از درختان خرما و انگور كه ميانش جويها روان گردانى،'),
(2121, 17, 92, 'يا از آسمان، چنانكه دعوى كردى، پاره‌هايى فروافكنى يا خدا و فرشتگان را روياروى ما- براى گواهى درستى گفتار خود- بيارى،'),
(2122, 17, 93, 'يا تو را خانه‌اى از زر باشد، يا در آسمان بالا روى، و بالارفتنت را هرگز باور نداريم تا بر ما نوشته‌اى فروآرى كه آن را بخوانيم. بگو: پاك و منزه است پروردگار من- از اينكه كسى بر او تحكّم كند-، مگر من جز آدمى پيامبرى هستم؟'),
(2123, 17, 94, 'و چيزى مردم را، آنگاه كه هدايت بديشان آمد، از ايمان آوردن باز نداشت مگر اينكه گفتند: آيا خدا آدميى را به پيامبرى برانگيخته است؟'),
(2124, 17, 95, 'بگو: اگر در زمين فرشتگانى مى‌بودند كه با آرامش راه مى‌رفتند هر آينه بر آنان از آسمان فرشته‌اى را به پيامبرى فرومى‌فرستاديم.'),
(2125, 17, 96, 'بگو: خدا ميان من و شما گواهى بسنده است، كه او به بندگانش آگاه و بيناست.'),
(2126, 17, 97, 'و كسى را كه خدا راه نمايد، پس هموست راه يافته و هر كه را گمراه كند هرگز براى آنان دوستان و ياورانى جز او [كه وى را راه نمايند] نيابى و روز رستاخيز آنان را كور و گنگ و كر برانگيزيم، بر رويهاشان [كشيده مى‌شوند]، جايشان دوزخ است، كه هر گاه [آتش آن‌] فرونشيند آنان را آتش سوزان بيفزاييم.'),
(2127, 17, 98, 'اين پاداش آنهاست به سزاى آنكه به آيات ما كافر شدند و گفتند: آيا چون استخوانها شديم و بهم ريخته و ريزه ريزه، آيا به آفرينشى نو برانگيخته مى‌شويم؟'),
(2128, 17, 99, 'آيا نديدند- ندانستند- كه آن خدايى كه آسمانها و زمين را آفريد تواناست بر اينكه مانند آنان را بيافريند؟ و برايشان سرآمدى- مرگ- نهاده كه شكى در آن نيست. پس ستمكاران سر باز زدند و جز كفر و انكار نخواستند.'),
(2129, 17, 100, 'بگو: اگر شما خزانه‌هاى بخشايش پروردگار مرا مى‌داشتيد، آنگاه از بيم هزينه‌كردن و درويشى دست باز مى‌داشتيد، و آدمى بخيل و تنگ چشم است.'),
(2130, 17, 101, 'و هر آينه موسى را نه نشانه روشن و آشكار داديم. از فرزندان اسرائيل بپرس چون [موسى‌] بديشان آمد [ميان او و فرعون چه گذشت‌]، پس فرعون او را گفت: همانا تو را، اى موسى، جادوزده‌اى مى‌پندارم.'),
(2131, 17, 102, 'گفت: هر آينه مى‌دانى كه اينها- نشانه‌ها- را جز خداوند آسمانها و زمين نفرستاده است، حجّتهايى روشن و هويدا [كه بدانها حق را دريابند]، و همانا من تو را، اى فرعون، هلاك‌شده مى‌پندارم.'),
(2132, 17, 103, 'پس خواست كه آنان را از آن سرزمين براند و براندازد، ولى ما او و همه آنها را كه با وى بودند غرق كرديم.'),
(2133, 17, 104, 'و پس از او به فرزندان اسرائيل گفتيم: در آن سرزمين جاى گيريد. پس چون هنگام وعده واپسين فرا رسد همه شما را با هم بياريم.'),
(2134, 17, 105, 'و آن'),
(2135, 17, 106, 'و قرآن را جدا جدا- آيه آيه و سوره سوره به حسب مصلحت و نياز- فرستاديم تا آن را بر مردم با درنگ بخوانى و آن را فروفرستاديم فروفرستادنى- به تدريح-.'),
(2136, 17, 107, 'بگو: خواه به آن ايمان بياوريد و خواه ايمان نياوريد، همانا آن كسان كه پيش از آن ايشان را دانش داده‌اند- از اهل كتاب- چون بر آنان خوانده شود سجده‌كنان بر زنخهاى- رويهاى- خويش درافتند،'),
(2137, 17, 108, 'و گويند: پاك و منزه است پروردگار ما، همانا وعده پروردگار ما انجام‌شدنى است.'),
(2138, 17, 109, 'و بر زنخها- رويها- افتاده، مى‌گريند و بر فروتنى و نرمدليشان مى‌افزايد.'),
(2139, 17, 110, 'بگو: خواه خداى را «اللّه» بخوانيد يا «رحمان» بخوانيد، هر كدام را كه بخوانيد او راست نامهاى نيكو. و نماز خويش به آواز بلند مخوان و آن را آهسته هم مخوان، و ميان آن راهى بجوى.'),
(2140, 17, 111, 'و بگو: سپاس و ستايش خدايى راست كه فرزندى نگرفت و او را در پادشاهى و فرمانروايى شريكى نيست و او را يار و كارسازى از خوارى و ناتوانى نيست. و او را به بزرگى بستاى بزرگداشتى در خور.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'سپاس و ستايش خداى راست كه اين كتاب را بر بنده‌اش فروفرستاد و آن را هيچ كژى ننهاد،'),
(2142, 18, 2, '[كتابى‌] راست و استوار تا از عذابى سخت از نزد خويش بيم كند و مؤمنان را كه كارهاى نيك و شايسته مى‌كنند نويد دهد كه ايشان را مزدى نيكوست- بهشت-،'),
(2143, 18, 3, 'در حالى كه هميشه در آن خواهند ماند'),
(2144, 18, 4, 'و كسانى را كه گفتند: خداوند فرزندى گرفت، بترساند.'),
(2145, 18, 5, 'آنان را هيچ دانشى بدان نيست و نه پدرانشان را [دانشى بود]. بزرگ سخنى است كه از دهانشان بيرون مى‌آيد نمى‌گويند مگر دروغى را.'),
(2146, 18, 6, 'شايد تو بر پى [اعراض‌] آنان اگر به اين سخن- قرآن- ايمان نياورند خود را از اندوه خواهى كشت!'),
(2147, 18, 7, 'ما آنچه را بر زمين است- از معادن و گياهان و حيوانات- آرايش آن ساخته‌ايم تا آنان را بيازماييم كه كدامشان نيكوكارترند.'),
(2148, 18, 8, 'و ما آنچه را بر روى آن است- از درختان و بناها- خاكى هموار و بيابانى بى‌گياه خواهيم ساخت- پس از آنكه آن را آباد و آراسته كرديم-.'),
(2149, 18, 9, 'مگر پنداشته‌اى كه ياران آن غار- كَهف- و آن سنگ نبشته- رقيم- از نشانه‌هاى شگفت‌انگيز ما بوده‌اند؟- يعنى داستان ايشان نسبت به آيات قدرت ما چندان شگفت نيست-.'),
(2150, 18, 10, 'آنگاه كه آن جوانمردان به غار پناه بردند و گفتند: پروردگارا، به ما از نزد خويش بخشايشى ارزانى دار و براى ما در كارمان رهيابى [به صلاح و كمال‌] را فراهم ساز.'),
(2151, 18, 11, 'پس در آن غار سالهايى چند بر گوشهاشان [پرده‌] زديم- به خوابشان فروبرديم-'),
(2152, 18, 12, 'سپس آنان را برانگيختيم- بيدارشان كرديم- تا معلوم كنيم كه كدام يك از آن دو گروه مدتى را كه درنگ كردند درست‌تر شمار كرده است.'),
(2153, 18, 13, 'ما سرگذشت آنان را براستى و درستى بر تو بر مى‌گوييم: آنان جوانانى- يا جوانمردانى- بودند كه به پروردگار خود ايمان آوردند و ما بر هدايتشان افزوديم.'),
(2154, 18, 14, 'و دلهاى آنان را بربستيم- نيرومند و استوار ساختيم- آنگاه كه به پا خاستند و گفتند: پروردگار ما خداوند آسمانها و زمين است، هرگز جز او خدايى نمى‌خوانيم، كه آنگاه- اگر چنين كنيم- هر آينه سخنى ناروا و گزاف گفته باشيم'),
(2155, 18, 15, 'اين قوم ما به جاى او خدايانى گرفته‌اند. چرا بر خدايان خود حجتى روشن نمى‌آورند؟ پس كيست ستمكارتر از آن كه بر خدا دروغى بربست- كه خدا شريك گرفته است-؟'),
(2156, 18, 16, 'و اينك كه از آنان و از آنچه جز خداى يكتا مى‌پرستيدند كناره گرفتيد، پس به غار پناه بريد تا پروردگارتان از مهر و بخشايش خويش بر شما بگستراند و از كارتان سودمندى و آسايشى براى شما فراهم سازد.'),
(2157, 18, 17, 'و آفتاب را بينى كه چون برآيد، از غارشان به سوى راست ميل كند، و چون فروشود آنان را از سوى چپ واگذارد، و ايشان در فراخناى غارند- در گشادگى غار از گرماى آفتاب و تعفن ايمنند-. اين از نشانه‌هاى خداست. هر كه را خداى راه نمايد پس او راه يافته است، و هر كه را گمراه كند هرگز براى او دوست و كارسازى راهنما نيابى.'),
(2158, 18, 18, 'و آنان را پندارى كه بيدارند- زيرا چشمانشان باز است- و حال آنكه خفتگانند و ايشان را به جانب راست و جانب چپ مى‌گردانيم و سگشان دو بازوى خويش به آستانه غار گسترانيده است اگر بر ايشان ديده‌ور مى‌گشتى هر آينه به آنها پشت كرده مى‌گريختى و از بيمشان آكنده مى‌شدى.'),
(2159, 18, 19, 'و همچنان [كه خوابشان كرديم‌] آنان را [از خواب‌] برانگيختيم تا ميان خود از يكديگر بپرسند گوينده‌اى از ايشان گفت: چه مدت [در اين غار] درنگ كرده ايد؟ [برخى‌] گفتند: روزى يا پاره‌اى از روزى درنگ كرده‌ايم. [و برخى ديگر] گفتند: پروردگارتان داناتر است بدانچه درنگ كرديد، پس يكى از خود را با اين پولتان به شهر بفرستيد و بنگرد كه كدامشان طعام پاكتر دارند و خوردنيى از آن برايتان بيارد، و [در رفت‌وآمد و خريد] مدارا و نرمى نمايد [تا شما را نشناسند] و كسى را به [حال‌] شما آگاه نكند'),
(2160, 18, 20, 'زيرا اگر بر شما آگاهى و دست يابند سنگسارتان كنند يا شما را در كيش خويش درآرند و آنگاه هرگز رستگار نخواهيد شد.'),
(2161, 18, 21, 'و اينچنين [آن مردم را] بر آنان آگاه كرديم تا بدانند كه وعده خدا- برانگيختن و رستاخيز- راست است و در رستاخيز هيچ شك نيست، آنگاه كه با يكديگر در كار خويش- مساله معاد- گفتگو و ستيزه مى‌كردند، پس گفتند: بر آنان بنائى بسازيد- تا كسى بر آنان آگاه نشود-. پروردگارشان به حالشان داناتر است. كسانى كه بر كارشان آگاهى و دست يافتند- يكتا پرستان- گفتند: ما بر آنان نمازگاهى مى‌سازيم.'),
(2162, 18, 22, 'خواهند گفت: سه تن بودند چهارميشان سگشان بود، و گويند: پنج تن بودند ششميشان سگشان بود، تيرى به ناپيدا پرتاب مى‌كنند- پندارى بى تحقيق دارند-، و نيز گويند: هفت تن بودند و هشتميشان سگشان بود. بگو: پروردگار من به شمار آنها داناتر است، [و شمار] آنان را ندانند مگر اندكى، پس در باره ايشان ستيزه مكن مگر ستيزه‌اى ظاهر- يعنى آنچه را ظاهر كرده‌ايم و آگاهى داده‌ايم، پس تنها تلاوت كتاب بر آنها در باره كارشان كافى است- و در باره آنان از هيچ كس نظر مخواه.'),
(2163, 18, 23, 'و هيچ چيز و هيچ كارى را مگوى كه من فردا كننده‌ام آن را،'),
(2164, 18, 24, 'مگر آنكه [گويى اگر] خداى خواهد و چون فراموش كنى [كه بگويى: اگر خداى خواهد] پروردگارت را ياد كن- يعنى وقتى به ياد آوردى، كلمه استثنا را بر زبان آر- و بگو: اميد است كه پروردگارم مرا به راه صوابى كه نزديكتر از اين باشد راه نمايد.'),
(2165, 18, 25, 'و آنان در غار خود سيصد سال درنگ كردند و نه سال افزودند.'),
(2166, 18, 26, 'بگو: خدا داناتر است بدانچه- به مدتى كه- درنگ كردند، او راست نهان و ناپيداى آسمانها و زمين چه بيناست او و چه شنواست! آنان- اهل آسمانها و زمين- را جز او هيچ يار و سرپرستى نيست، و هيچ كس را در حكم و فرمان خويش انباز نسازد.'),
(2167, 18, 27, 'و از كتاب پروردگارت- قرآن يا لوح محفوظ- آنچه به تو وحى مى‌شود برخوان سخنان او را دگرگون‌كننده‌اى نيست، و جز او هيچ پناهگاهى هرگز نيابى.'),
(2168, 18, 28, 'و خودت را با كسانى شكيبا بدار كه پروردگارشان را بامداد و شبانگاه مى‌خوانند و او- ذات يا خشنودى او- را مى‌خواهند، و ديدگانت از آنان برنگردد [از آن رو] كه آرايش زندگانى اين جهان بخواهى و از آن كس فرمان مبر كه دل او را از يادمان غافل كرده‌ايم و از خواهش و هوس خويش پيروى كرده و كارش گزافكارى است.'),
(2169, 18, 29, 'و بگو: سخن راست و درست از پروردگار شماست، پس هر كه بخواهد ايمان بياورد و هر كه بخواهد كافر شود همانا براى ستمكاران آتشى آماده كرده‌ايم كه سراپرده‌هايش آنها را فراگيرد، و اگر فريادرسى خواهند آنان را به آبى چون مس گداخته فرياد رسند كه چهره‌ها را بريان كند بد آشاميدنى و بد آسايشگاهى است.'),
(2170, 18, 30, 'همانا كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند، ما مزد كسى را كه كار نيكو كرده تباه نكنيم.'),
(2171, 18, 31, 'ايشانند كه بهشتهاى پاينده دارند كه از زير [كوشكهاى‌] آنان جويهاى فراخ روان است در آنجا با دستبندهايى از زر آرايش يابند و جامه‌هايى از ديباى نازك و ديباى ستبر پوشند [و] بر تختها تكيه زنند. نيكوپاداش و نيكو آسايشگاهى است.'),
(2172, 18, 32, 'و براى آنان [داستان‌] دو مردى را مَثَل بزن كه به يكى از آنها دو بوستان از تاكها داديم و بر گِرد آنها خرمابنها و در ميان آنها كشتزارى پديد كرديم'),
(2173, 18, 33, 'كه هر دو بوستان ميوه خود را مى‌داد و چيزى از آن نمى‌كاست، و ميان آن دو بوستان جويى فراخ روان ساختيم'),
(2174, 18, 34, 'و او را ميوه‌ها- انواع فراورده‌ها- بود، پس به يار خود كه با وى گفت‌وگو مى‌كرد گفت: من به مال از تو بيشتر و به افراد- فرزندان و غلامان و خادمان و حشم- توانمندترم.'),
(2175, 18, 35, 'و به بوستان خويش در آمد در حالى كه بر خويشتن ستمكار بود، گفت: نپندارم كه اين بوستان هيچگاه تباه و نابود گردد'),
(2176, 18, 36, 'و نپندارم كه رستاخيز برپا شود، و اگر هم به سوى خداوندم بازگردانده شوم هر آينه بهتر از اين بازگشتگاهى بيابم.'),
(2177, 18, 37, 'يارش كه با او گفت و شنود مى‌كرد گفت: آيا به آن [خداى‌] كه تو را از خاك و سپس از نطفه آفريد و آنگاه مردى راست‌بالا و تمام اندام كرد كافر شده اى؟!'),
(2178, 18, 38, 'ليكن من [گويم:] اوست خداى يكتا، پروردگار من و هيچ كس را با پروردگارم انباز نگيرم'),
(2179, 18, 39, 'و چرا آنگاه كه به بوستانت در آمدى نگفتى: هر چه خداى خواهد [همان شود]، هيچ نيرويى نيست مگر به خداى؟ اگر مرا به مال و فرزند كمتر از خود مى‌بينى،'),
(2180, 18, 40, 'اميد است كه پروردگارم مرا بهتر از بوستان تو دهد و بر بوستان تو از آسمان عذابى آتشبار- صاعقه- بفرستد تا زمينى هموار و بى‌گياه گردد'),
(2181, 18, 41, 'يا آب آن به زمين فروشود تا جستن آن هرگز نتوانى.'),
(2182, 18, 42, 'و ميوه او تباه و نابود گشت، پس بامداد دو دست خويش [به پشيمانى و اندوه‌] بر آنچه در آن'),
(2183, 18, 43, 'و او را جز خدا گروهى نبود كه ياريش كنند و نه خود يارى‌كننده خويش بود.'),
(2184, 18, 44, 'آنجا- يعنى به هنگام زوال نعمت يا در روز رستاخيز- يارى كردن و كارسازى از آن خداى حق است و بس، كه او به پاداش، بهتر و به سرانجام، نيكوتر است- يعنى سرانجام طاعت او بهتر است از سرانجام طاعت غير او-.'),
(2185, 18, 45, 'و براى آنان زندگانى اين جهان را مَثَل بزن كه مانند آبى است كه آن را از آسمان فروفرستاديم پس گياه زمين با آن بياميخت [و روييد] سپس [چنان‌] خشك و شكسته شد كه بادها آن را پراكنده مى‌گرداند. و خداى بر هر چيزى تواناست.'),
(2186, 18, 46, 'مال و فرزندان آرايش زندگانى اين جهان است، و كارهاى نيك پايدار به نزد پروردگار تو به پاداش، بهتر و اميدداشتن به آنها نيكوتر است.'),
(2187, 18, 47, 'و [ياد كن‌] روزى كه كوه‌ها را روان گردانيم- از جا بركنيم و پراكنده سازيم- و زمين را آشكار بينى- زيرا كوه‌ها از ميان رفته و زمين هموار شده- و همه را برانگيزيم و گِرد آوريم و هيچ يك از آنان را فرونگذاريم.'),
(2188, 18, 48, 'و همگى صف كشيده بر پروردگارت عرضه شوند [و خداى گويد:] هر آينه نزد ما آمده‌ايد- تنها و تهيدست- همان گونه كه نخستين بار شما را بيافريديم بلكه پنداشتيد كه هرگز شما را وعده‌گاهى ننهيم.'),
(2189, 18, 49, 'و نامه [اعمال در ميان‌] نهاده شود، پس بزهكاران را بينى كه از آنچه در آن است ترسان و نگرانند و گويند: اى واى بر ما، اين چه نامه‌اى است كه هيچ [گفتار و كردار] كوچك و بزرگى را فرونگذاشته مگر آن را در شمار آورده است و هر چه كرده‌اند پيش رو بيابند و پروردگار تو به هيچ كس ستم نكند.'),
(2190, 18, 50, 'و [ياد كن‌] آنگاه كه به فرشتگان گفتيم كه آدم را سجده كنيد پس سجده كردند مگر ابليس كه از پريان بود و از فرمان پروردگارش بيرون شد. پس آيا او و فرزندانش را به جاى من دوستان و سرپرستان [خود] مى‌گيريد و حال آنكه شما را دشمنند؟! [و ابليس‌] ستمكاران را بد بدلى- به جاى خداوند- است.'),
(2191, 18, 51, 'من آنان- شيطان و فرزندان او- را هنگام آفرينش آسمانها و زمين حاضر و گواه نساختم و نه به هنگام آفرينش خودشان، و من هرگز گمراه‌كنندگان را يار و مددكار نگرفتم.'),
(2192, 18, 52, 'و روزى كه گويد: بخوانيد آنها را كه انبازان من مى‌پنداشتيد، پس بخوانندشان اما آنان را پاسخ ندهند، و ميانشان جايگاه هلاكت- از دوزخ- ساختيم.'),
(2193, 18, 53, 'و بزهكاران آتش دوزخ را ببينند و بى‌گمان بدانند كه در آن خواهند افتاد و از آنجا راه برگشتى نيابند.'),
(2194, 18, 54, 'و هر آينه در اين قرآن براى مردم از هر گونه مثالى آورديم، و آدمى بيش از همه چيز در ستيز و چون و چراست.'),
(2195, 18, 55, 'و مردم را، آنگاه كه رهنمونى- پيامبر و قرآن- بديشان آمد، باز نداشت از اينكه ايمان بياورند و از پروردگارشان آمرزش بخواهند مگر [طلب و انتظار] اينكه روش و نهاد [ما در باره‌] پيشينيان بديشان آيد يا عذاب روياروى به آنان رسد.'),
(2196, 18, 56, 'و ما پيامبران را جز مژده‌دهندگان و بيم‌كنندگان نمى‌فرستيم و آنان كه كافر شدند به [سخنان‌] باطل و بيهوده ستيزه مى‌كنند تا [سخن‌] حق را بدان پايمال سازند، و آيات مرا و هشدارها را به ريشخند گرفتند.'),
(2197, 18, 57, 'و ستمكارتر از آن كس كيست كه به آيات پروردگارش پند داده شود پس، از آنها روى بگرداند و آنچه را دو دستش پيش فرستاده- كردارهاى زشتش- فراموش كند. ما بر دلهاشان پوششها نهاده‌ايم تا آن'),
(2198, 18, 58, 'و پروردگار تو آمرزگار و خداوند مِهر و بخشايش است اگر آنان را به سزاى آنچه كرده‌اند بازخواست مى‌كرد هر آينه در عذابشان شتاب مى‌نمود، اما آنان را هنگامى و وعده‌گاهى است كه از آن گريزگاه و پناهگاهى نيابند.'),
(2199, 18, 59, 'و [مردم‌] آن آباديها و شهرها را چون ستم كردند هلاك كرديم و براى هلاكتشان وعده‌گاهى نهاديم.'),
(2200, 18, 60, 'و [ياد كن‌] آنگاه كه موسى شاگرد خود- يوشع پسر نون- را گفت: من پيوسته خواهم رفت تا به جاى به هم آمدن دو دريا برسم، يا سالهايى دراز راه پويم.'),
(2201, 18, 61, 'پس چون به جاى به هم آمدن آن دو دريا رسيدند ماهى خود را فراموش كردند و آن'),
(2202, 18, 62, 'و چون از آنجا گذشتند، به شاگرد خود گفت: چاشتمان را بيار، كه در اين سفرمان رنجى سخت ديده‌ايم.'),
(2203, 18, 63, '[شاگردش‌] گفت: آيا به ياد دارى آنگاه كه به آن سنگ جاى گرفته بوديم؟ من [داستان به دريا افتادن‌] ماهى را فراموش كردم [كه با تو بگويم‌]، و جز شيطان فراموشم نساخت از اينكه آن را ياد كنم، و به شيوه‌اى شگفت راه خود را در دريا پيش گرفت.'),
(2204, 18, 64, '[موسى‌] گفت: آن همان است كه مى‌جستيم- يعنى جاى فراموش شدن ماهى همان جايگاه خضر است-، پس بر نشانه گامهاى خويش پى‌جويان بازگشتند.'),
(2205, 18, 65, 'پس بنده‌اى از بندگان ما را يافتند كه او را از نزد خويش بخشايشى داده بوديم و او را از نزد خود دانشى آموخته بوديم.'),
(2206, 18, 66, 'موسى به او گفت: آيا [اجازه مى‌دهى كه‌] تو را پيروى كنم بر اينكه مرا از آنچه به تو آموخته‌اند بياموزى تا به راه راست و كمال راه يابم؟'),
(2207, 18, 67, 'گفت: تو همراه من نتوانى شكيبايى كرد'),
(2208, 18, 68, 'و چگونه بر آنچه بدان آگاهى و دانشى ندارى شكيبايى كنى؟'),
(2209, 18, 69, 'گفت: اگر خدا خواهد مرا شكيبا خواهى يافت و تو را در هيچ كارى نافرمانى نكنم.'),
(2210, 18, 70, 'گفت: اگر از من پيروى خواهى كرد، پس مرا از چيزى مپرس تا خود براى تو سخنى از آن پديد آرم.'),
(2211, 18, 71, 'پس برفتند تا آنگاه كه به كشتى سوار شدند، آن را سوراخ كرد. [موسى‌] گفت: آيا آن را سوراخ كردى تا مردمش را غرق كنى؟ بى‌گمان كارى ناروا و شگفت كردى.'),
(2212, 18, 72, 'گفت: نگفتم كه تو همراه من نتوانى شكيبايى كرد؟'),
(2213, 18, 73, 'گفت: مرا بدانچه فراموش كردم بازخواست مكن و كارم را بر من سخت مگير.'),
(2214, 18, 74, 'پس روانه شدند تا آنگاه كه نوجوانى را ديدار كردند و او را كشت، [موسى‌] گفت: آيا جان پاكى- بى‌گناهى- را بى‌آنكه كسى را كشته باشد كشتى؟ براستى كارى زشت و ناشايسته كردى.'),
(2215, 18, 75, 'گفت: آيا تو را نگفتم كه همراه من نتوانى شكيبايى كرد؟'),
(2216, 18, 76, 'گفت: اگر از اين پس تو را از چيزى بپرسم با من همراهى مكن، كه از سوى من هر آينه به عذرى رسيده‌اى- در ترك مصاحبت من معذور باشى-.'),
(2217, 18, 77, 'پس برفتند تا چون به مردم شهرى رسيدند از مردمش طعام خواستند، ولى از مهمان كردن آنها سر باز زدند، آنگاه ديوارى يافتند كه مى‌خواست فروريزد، پس آن را راست كرد، [موسى‌] گفت: اگر مى‌خواستى در برابر اين كار مزدى مى‌گرفتى!'),
(2218, 18, 78, 'گفت: اينك جدايى ميان من و توست. اكنون تو را به معنى و فرجام آنچه بر آن نتوانستى شكيبايى كرد آگاه مى‌كنم.'),
(2219, 18, 79, 'اما آن كشتى از آن بينوايانى بود كه در دريا كار مى‌كردند. خواستم كه آن را معيوب كنم زيرا در پيش راهشان پادشاهى بود كه هر كشتى را به ناروا و ستم مى‌گرفت.'),
(2220, 18, 80, 'و اما آن نوجوان، پدر و مادرش مؤمن بودند، پس ترسيديم كه آنان را به سركشى و كفر وادارد.'),
(2221, 18, 81, 'از اين رو خواستيم تا پروردگارشان به جايش آن دهدشان كه به پاكى بهتر از او و به مهربانى- يا: رعايت خويشاوندى- نزديكتر از وى باشد.'),
(2222, 18, 82, 'و اما آن ديوار از آن دو پسر يتيم در آن شهر بود و زير آن گنجى براى آنان بود و پدرشان مردى نيك بود، پروردگار تو خواست كه آنها به نيرو و جوانى خود برسند و گنج خويش را بيرون آورند، از روى مِهر و بخشايشى از پروردگار تو، و من آن را از پيش خود نكردم. اين است تأويل- توضيح و تبيين حقيقت و راز و سرانجام- آنچه نتوانستى بر آن شكيبايى كرد.'),
(2223, 18, 83, 'و تو را از ذو القرنين پرسند، بگو: اينك از وى خبرى و سخنى بر شما مى‌خوانم.'),
(2224, 18, 84, 'ما او را در زمين نيرو و توان داديم، و وسيله [رسيدن به‌] هر چيزى را به وى داديم.'),
(2225, 18, 85, 'او هم وسيله‌اى را پى گرفت'),
(2226, 18, 86, 'تا چون به جاى فروشدن خورشيد رسيد آن را چنين يافت كه در چشمه‌اى- يا دريايى- گِل‌آلود فرو مى‌رود و نزديك آن قومى را يافت. گفتيم- به او الهام كرديم- اى ذو القرنين، يا اينان را عذاب مى‌كنى و يا ميانشان رفتارى نيكو پيش مى‌گيرى.'),
(2227, 18, 87, 'گفت: اما هر كه ستم كند- كفر ورزد- او را عذاب خواهيم كرد، سپس به سوى پروردگارش بازگردانده شود آنگاه او را عذابى سخت و ناشناخته كند- عذاب دشوارى كه مانند آن ديده و شناخته نشده-.'),
(2228, 18, 88, 'و اما هر كه ايمان آورد و كار نيك و شايسته كند او را پاداش، نيكويى است و زودا كه براى او از فرمان خويش سخنى آسان گوييم- كارى آسان فراخور توان او فرماييم-.'),
(2229, 18, 89, 'سپس وسيله‌اى [ديگر] را پى گرفت'),
(2230, 18, 90, 'تا چون به جاى برآمدن آفتاب رسيد، آن را ديد كه بر قومى مى‌تابد كه براى آنان در برابر آن پوششى قرار نداده بوديم.'),
(2231, 18, 91, 'چنين بود و ما به آنچه نزد او بود آگاهى فراگير داشتيم.'),
(2232, 18, 92, 'سپس وسيله‌اى [ديگر] را پى گرفت'),
(2233, 18, 93, 'تا چون به ميان دو كوه رسيد، در پيش آن دو كوه گروهى را يافت كه هيچ سخنى را درنمى‌يافتند'),
(2234, 18, 94, 'گفتند: اى ذو القرنين، همانا يأجوج و مأجوج در اين سرزمين تبهكارى مى‌كنند آيا تو را هزينه‌اى قرار دهيم كه ميان ما و آنها سدى بسازى؟'),
(2235, 18, 95, 'گفت: آنچه پروردگارم مرا در آن توانايى داده بهتر است. مرا به نيرو يارى كنيد تا ميان شما و آنان سدى استوار بسازم'),
(2236, 18, 96, 'پاره‌هاى آهن براى من بياوريد، تا چون ميان آن دو كوه را برابر ساخت- پُر كرد-، گفت: بدميد، تا آنگاه كه آن را مانند آتشى بگداخت، گفت: مس گداخته برايم بياوريد تا بر آن فروريزم.'),
(2237, 18, 97, 'پس نه توانستند بر آنها بالا روند و نه آن را سوراخ كنند.'),
(2238, 18, 98, 'گفت: اين بخشايشى است از پروردگار من، پس چون وعده پروردگارم فرا رسد، آن'),
(2239, 18, 99, 'و در آن روز واگذاريم كه در هم آميزند، و در صور دميده شود پس همه آنها را فراهم آوريم.'),
(2240, 18, 100, 'و در آن روز دوزخ را آشكارا به كافران بنماييم'),
(2241, 18, 101, 'آنان كه ديدگانشان از ياد من در پوشش بود و شنيدن نمى‌توانستند.'),
(2242, 18, 102, 'آيا كسانى كه كافر شدند پنداشته‌اند كه بندگان مرا به جاى من دوست و كارساز مى‌گيرند؟! همانا ما دوزخ را براى كافران جايگاه- يا وسيله- پذيرايى ساخته‌ايم.'),
(2243, 18, 103, 'بگو: آيا شما را به زيانكارترين [مردم‌] در كردار، آگاه كنيم؟'),
(2244, 18, 104, 'آنهايند كه كوشش آنان در زندگى اين جهان گم و تباه شده و خود مى‌پندارند كه كار نيكو مى‌كنند.'),
(2245, 18, 105, 'آنانند كه به آيات پروردگارشان و ديدار او كافر شدند، پس كارهاشان تباه و نابود شد و از اين‌رو در روز رستاخيز براى آنها هيچ ترازويى ننهيم.'),
(2246, 18, 106, 'اين است كه پاداش آنان دوزخ است به سزاى آنكه كفر ورزيدند و آيات و فرستادگان مرا به ريشخند گرفتند.'),
(2247, 18, 107, 'همانا كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند بهشتهاى فردوس جايگاه- يا وسيله- پذيرايى ايشان است'),
(2248, 18, 108, 'در آنجا جاودانه‌اند و دگرگونى از آن نخواهند.'),
(2249, 18, 109, 'بگو: اگر دريا براى [نوشتن‌] كلمات- آفريده‌هاى- پروردگار من مركب بود هر آينه دريا به پايان مى‌رسيد پيش از آنكه كلمات پروردگارم به پايان رسد هر چند مانند آن را به يارى بياريم.'),
(2250, 18, 110, 'بگو: همانا من آدميى هستم همچون شما كه به من وحى مى‌شود كه خداى شما خداى يگانه است. پس هر كه اميد ديدار پروردگار خويش دارد بايد كار نيك و شايسته كند و هيچ كس را در پرستش پروردگارش شريك نسازد.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'كاف، ها، يا، عين، صاد.'),
(2252, 19, 2, '[اين‌] يادكرد بخشايش پروردگار توست بر بنده خود، زكريا'),
(2253, 19, 3, 'آنگاه كه پروردگار خويش را بخواند، خواندنى در نهان'),
(2254, 19, 4, 'گفت: پروردگارا، استخوانم سست شده و سرم از پيرى سپيد گشته، و به خواندن تو، پروردگارا، بدبخت [و از اجابت بى‌بهره‌] نبوده‌ام.'),
(2255, 19, 5, 'و من از خويشاوندانم- پسر عموهايم- از پس [مرگ‌] خويش بيمناكم، و زنم نازاست، پس مرا از نزد خود وليى- فرزندى كه پس از من سرپرست امور من باشد- ببخش،'),
(2256, 19, 6, 'كه مرا ميراث برد و از خاندان يعقوب نيز ميراث گيرد، و او را، پروردگارا، پسنديده ساز.'),
(2257, 19, 7, 'اى زكريا، ما تو را به پسرى كه نام او يحيى است مژده مى‌دهيم، كه پيش از اين همنامى براى او پديد نكرديم.'),
(2258, 19, 8, 'گفت: پرودگارا، چگونه مرا پسرى باشد و حال آنكه زنم نازاست و من از پيرى به فرتوتى رسيده‌ام؟'),
(2259, 19, 9, 'گفت: چنين است پروردگار تو گفت كه اين بر من آسان است و همانا پيش از اين تو را بيافريدم در حالى كه چيزى نبودى.'),
(2260, 19, 10, 'گفت: پروردگارا، براى من نشانه‌اى قرار ده گفت: نشانه تو آن است كه سه شبانه روز با مردم سخن نگويى- نتوانى گفت- با اينكه تندرستى.'),
(2261, 19, 11, 'پس، از نمازگاه به ميان قوم خويش بيرون آمد، و به آنان اشارت كرد كه بامداد و شبانگاه [خداى را] به پاكى ياد كنيد.'),
(2262, 19, 12, 'اى يحيى، كتاب را با نيرو و بجد بگير، و او را در كودكى حكمت- گفتار و كردار درست و استوار- داديم،'),
(2263, 19, 13, 'و نيز مهربانى و پاكى و پارسايى از نزد خويش و پرهيزگار بود.'),
(2264, 19, 14, 'و به پدر و مادر خويش نيكوكار بود و گردنكش و نافرمان نبود.'),
(2265, 19, 15, 'و درود بر او، روزى كه زاده شد و روزى كه مى‌ميرد و روزى كه زنده برانگيخته مى‌شود.'),
(2266, 19, 16, 'و در اين كتاب، مريم را ياد كن آنگاه كه از كسان خويش در جايگاهى شرقى- از بيت المقدس- كناره گزيد.'),
(2267, 19, 17, 'و ميان خود و آنان پرده‌اى گرفت، پس ما روح خود- جبرئيل- را بدو فرستاديم و براى او چون آدميى درست اندام نمودار شد.'),
(2268, 19, 18, '[مريم‌] گفت: من به خداى رحمان پناه مى‌برم از تو، اگر پرهيزگار باشى.'),
(2269, 19, 19, 'گفت: همانا من فرستاده پروردگار توام تا تو را پسرى پارسا و پاكيزه ببخشم.'),
(2270, 19, 20, 'گفت: از كجا- يا چگونه- مرا پسرى باشد و حال آنكه دست هيچ آدمى به من نرسيده است و بدكاره هم نبوده‌ام؟'),
(2271, 19, 21, 'گفت: چنين است، پروردگار تو گفت كه اين بر من آسان است، [تا بدان قدرت ما را دريابى‌] و تا او را نشانه‌اى براى مردم و بخشايشى از سوى خويش كنيم، و اين كارى است گزارده و شدنى.'),
(2272, 19, 22, 'پس به او (عيسى) بار گرفت، و با او به جايگاهى دور -از شهر و مردم- بيرون رفت.'),
(2273, 19, 23, 'آنگاه درد زاييدن او را به تنه درخت خرمايى كشانيد گفت: اى كاش پيش از اين مرده بودم و به فراموشى سپرده شده بودم.'),
(2274, 19, 24, 'پس [كودك‌] او را از زير او- يعنى در شكم او- آواز داد: اندوه مخور، پروردگار تو از زير پايت جويى روان ساخت'),
(2275, 19, 25, 'و خرمابن را به سوى خويش بجنبان تا بر تو خرماى تر تازه چيده فروريزد.'),
(2276, 19, 26, 'پس، بخور و بياشام، و چشم روشن مى‌دار. و اگر از آدميان كسى را بينى، [به اشارت‌] بگو: من براى خداى رحمان روزه‌اى نذر كرده‌ام- روزه سكوت- و امروز با هيچ آدمى سخن نخواهم گفت.'),
(2277, 19, 27, 'پس او'),
(2278, 19, 28, 'اى خواهر هارون، نه پدر تو مرد بدى بود و نه مادرت بدكاره بود.'),
(2279, 19, 29, 'پس به او'),
(2280, 19, 30, '[عيسى به سخن آمد و] گفت: من بنده خدايم، به من كتاب داده و مرا پيامبر ساخته است'),
(2281, 19, 31, 'و مرا هر جا كه باشم با بركت ساخته، و تا زنده باشم به نماز و زكات سفارشم كرده است'),
(2282, 19, 32, 'و مرا به مادرم نيكوكار كرده و مرا گردنكشى بدبخت نگردانيده است'),
(2283, 19, 33, 'و درود باد بر من، روزى كه زاده شدم و روزى كه بميرم و روزى كه زنده برانگيخته شوم'),
(2284, 19, 34, '- اين است عيسى پسر مريم، گفتار درست و راستى كه در باره آن به شك اندرند.'),
(2285, 19, 35, 'خداى را نسزد كه هيچ فرزندى گيرد، پاك و منزه است، چون خواهد كه كارى برگزارد همانا آن را گويد: باش، پس مى‌باشد-.'),
(2286, 19, 36, 'و خداى يكتا- الله- پروردگار من و پروردگار شماست پس او را بپرستيد اين است راه راست.'),
(2287, 19, 37, 'و آن گروه‌ها- دسته‌هاى ترسايان- در ميان خود اختلاف كردند پس واى بر آنها كه كافر شدند از ديدار و حضور در روزى بزرگ- رستاخيز-.'),
(2288, 19, 38, 'چه شنوا و چه بينايند آن روز كه نزد ما آيند! ليكن ستمكاران در آن روز در گمراهى آشكارند.'),
(2289, 19, 39, 'و آنان را از روز دريغ و پشيمانى بترسان آنگاه كه كار برگزار شود. و [امروز] ايشان در غفلت‌اند و [آن روز را] باور نمى‌دارند.'),
(2290, 19, 40, 'همانا ما زمين و هر كه را بر آن است به ميراث بريم و به ما بازگردانده شوند.'),
(2291, 19, 41, 'و در اين كتاب، ابراهيم را ياد كن كه او راست‌گفتار و درست‌كردار پيامبرى بود.'),
(2292, 19, 42, 'آنگاه كه پدر خويش را گفت: اى پدر، چرا چيزى را مى‌پرستى كه نه مى‌شنود و نه مى‌بيند و در هيچ چيز تو را سود ندارد و به كار نيايد؟'),
(2293, 19, 43, 'اى پدر، همانا از دانش به من آن رسيده كه تو را نرسيده است پس مرا پيروى كن تا تو را راهى راست بنمايم.'),
(2294, 19, 44, 'اى پدر، شيطان را مپرست، كه شيطان خداى رحمان را نافرمان بود.'),
(2295, 19, 45, 'اى پدر، من مى‌ترسم كه عذابى از خداى رحمان به تو رسد آنگاه شيطان را دوست و ياور باشى.'),
(2296, 19, 46, 'گفت: اى ابراهيم، آيا از خدايان من روگردانى؟ اگر [از گفتار خود] باز نايستى هر آينه سنگسارت مى‌كنم، و ديرگاهى از من دور باش.'),
(2297, 19, 47, 'گفت: با تو بدرود مى‌كنم، از پروردگارم براى تو آمرزش خواهم خواست كه او به من مهربان است،'),
(2298, 19, 48, 'و از شما و آنچه جز خداى يكتا مى‌خوانيد كناره مى‌گيرم، و پروردگارم را مى خوانم، اميد است كه به خواندن پروردگار خويش بدبخت [و از اجابت بى بهره‌] نباشم.'),
(2299, 19, 49, 'پس چون از آنان و آنچه بجز خداى يكتا مى‌پرستيدند كناره گرفت به او اسحاق و يعقوب داديم و همه را پيامبر كرديم.'),
(2300, 19, 50, 'و از رحمت خويش به ايشان بخشيديم و براى آنان [در ميان مردم‌] نام نيكو و آوازه بلند نهاديم.'),
(2301, 19, 51, 'و در اين كتاب، موسى را ياد كن همانا او برگزيده و ويژه شده [خدا] بود و فرستاده‌اى پيامبر بود.'),
(2302, 19, 52, 'و او را از سوى راست كوه طور ندا داديم و به رازگويى نزديكش ساختيم.'),
(2303, 19, 53, 'و به او از رحمت خويش برادرش هارون پيامبر را بخشيديم.'),
(2304, 19, 54, 'و در اين كتاب، اسماعيل را ياد كن او راست‌وعده و فرستاده‌اى پيامبر بود'),
(2305, 19, 55, 'و خاندان خود را به نماز و زكات فرمان مى‌داد و نزد پروردگارش پسنديده بود.'),
(2306, 19, 56, 'و در اين كتاب، از ادريس ياد كن، همانا او راست‌گفتار پيامبرى بود'),
(2307, 19, 57, 'و او را به جايگاهى بلند و والا برداشتيم.'),
(2308, 19, 58, 'ايشانند- از زكريا تا الياس- كه خداوند به آنان نعمت ارزانى داشته- نيكويى كرده- پيامبرانى از فرزندان آدم و از [فرزندان‌] آنها كه با نوح [در كشتى‌] برنشانديم و از فرزندان ابراهيم و يعقوب و از آنان كه راه نموديم و برگزيديم چون آيات خداى رحمان بر آنان خوانده شود سجده‌كنان و گريان به روى درافتند.'),
(2309, 19, 59, 'آنگاه از پى ايشان جانشينان بد و ناشايسته‌اى آمدند كه نماز را فروگذاشتند- در آن سهل‌انگارى نمودند- و كامها و آرزوهاى دل را پيروى كردند، پس زودا كه [سزاى‌] گمراهى خود را ببينند'),
(2310, 19, 60, 'مگر آن كه باز گشت- توبه كرد- و ايمان آورد و كار نيك و شايسته كرد، پس اينان به بهشت در آيند و هيچ ستمى بر آنان نرود،'),
(2311, 19, 61, 'بهشتهايى پاينده كه خداى رحمان بندگان خود را به ناديده و ناپيدا وعده داده همانا وعده او آمدنى است.'),
(2312, 19, 62, 'در آنجا سخن ياوه و بيهوده نشنوند، [و گفتارشان‌] جز سلام [نيست‌]. و در آنجا هر بامداد و شبانگاه روزى دارند.'),
(2313, 19, 63, 'اين بهشتى است كه به هر كس از بندگانمان كه پرهيزگار باشد به ميراث دهيم.'),
(2314, 19, 64, 'و ما [فرشتگان‌] جز به فرمان پروردگار تو فرود نمى‌آييم. او راست آنچه فراپيش ماست- امور آينده- و آنچه پشت سر ماست- امور گذشته- و آنچه ميان آن است. و پروردگار تو فراموشكار نيست'),
(2315, 19, 65, 'خداوند آسمانها و زمين و آنچه در ميان آنهاست، پس او را بپرست و بر پرستش او شكيبا باش آيا براى او هيچ همنامى- كه او را الله خوانند- مى شناسى؟'),
(2316, 19, 66, 'و آدمى گويد: آيا هنگامى كه بميرم، مرا زنده [از گور] بيرون خواهند آورد؟'),
(2317, 19, 67, 'آيا آدمى به ياد نمى‌آورد كه ما او را از پيش بيافريديم و هيچ چيز نبود؟'),
(2318, 19, 68, 'به پروردگار تو سوگند كه آنان را با شيطانها برانگيزيم و فراهم آريم، سپس آنها را گرداگرد دوزخ به زانو در آمده حاضر كنيم.'),
(2319, 19, 69, 'سپس از ميان هر گروهى هر كدامشان را كه بر خداى رحمان گستاخ‌تر و سركش‌تر بوده بيرون كشيم'),
(2320, 19, 70, 'آنگاه هر آينه ما به كسانى كه به در آمدن و سوختن در آن سزاوارترند داناتريم.'),
(2321, 19, 71, 'و هيچ كس از شما نيست مگر آنكه به آن در آيد- يا بر آن بگذرد- اين [حكم و وعده‌اى است كه‌] بر پروردگار تو بايسته و گزاردنى- انجام دادنى- است.'),
(2322, 19, 72, 'آنگاه كسانى را كه پرهيزگارى كردند برهانيم و ستمكاران را در آنجا به زانو در آمده بگذاريم.'),
(2323, 19, 73, 'و چون آيات روشن ما بر آنان خوانده شود كسانى كه كافر شدند به كسانى كه ايمان آورده‌اند گويند: كداميك از اين دو گروه را جايگاهى بهتر و انجمنى نيكوتر است.'),
(2324, 19, 74, 'و چه بسا گروه‌ها را كه پيش از آنان هلاك كرديم كه به كالاى خانه و به ديدار- هيئت و منظر- [از اينها] نيكوتر بودند.'),
(2325, 19, 75, 'بگو: هر كه در گمراهى باشد، پس خداى رحمان بايد كه او را فزونى دهد- به او مهلت دهد تا گمراهى‌اش بيشتر شود- تا آنگاه كه آنچه را به آنان وعده داده مى‌شود ببينند: يا عذاب [اين جهانى‌] را- مانند كشته يا اسير شدن- و يا رستاخيز را پس زودا كه بدانند چه كسى را جايگاهى بدتر و سپاهى ناتوان‌تر است.'),
(2326, 19, 76, 'و خداوند كسانى را كه راه راست يافتند رهنمونى همى‌افزايد، و ماندگارهاى نيك و شايسته نزد پروردگار تو پاداشى بهتر و بازگشت و سرانجامى بهتر دارد.'),
(2327, 19, 77, 'آيا ديدى آن كس را كه به آيات ما كافر شد و گفت: البته به من مال و فرزند داده شود؟'),
(2328, 19, 78, 'آيا بر غيب آگاهى يافته يا از نزد خداى رحمان پيمانى گرفته؟'),
(2329, 19, 79, 'نه چنان است آنچه مى‌گويد خواهيم نوشت و عذاب او را پيوسته مى‌افزاييم.'),
(2330, 19, 80, 'و آنچه را كه مى‌گويد- مال و فرزند- از وى ميراث مى‌بريم و تنها نزد ما مى‌آيد.'),
(2331, 19, 81, 'و بجز خداى يكتا خدايانى گرفتند تا آنها را مايه بزرگى و نيرو باشند.'),
(2332, 19, 82, 'نه چنان است زودا كه پرستش آنان را انكار كنند و مخالف و دشمنشان باشند.'),
(2333, 19, 83, 'آيا نديده‌اى- ندانسته‌اى- كه ما شياطين را بر كافران مى‌فرستيم تا آنها را [به وسوسه به سوى بديها و گناهان‌] سخت بجنبانند؟'),
(2334, 19, 84, 'پس بر آنان شتاب مكن- در فرود آمدن عذاب- جز اين نيست كه ما براى آنها به دقت مى‌شماريم- روز شمارى مى‌كنيم تا مهلتشان سرآيد-.'),
(2335, 19, 85, 'روزى كه پرهيزگاران را به سوى خداى رحمان به مهمانى و ديدار فراهم آريم.'),
(2336, 19, 86, 'و بزهكاران را تشنه به سوى دوزخ برانيم.'),
(2337, 19, 87, 'توان شفاعت ندارند مگر آن كه از نزد خداى رحمان پيمانى گرفته باشد.'),
(2338, 19, 88, 'و گفتند: خداى رحمان فرزندى گرفت'),
(2339, 19, 89, 'هر آينه چيزى سخت زشت و ناپسند آورده‌ايد'),
(2340, 19, 90, 'نزديك است كه آسمانها از اين سخن بشكافند و زمين شكافته شود و كوه‌ها در هم شكسته فرو ريزند،'),
(2341, 19, 91, 'كه براى خداى رحمان فرزندى خواندند- فرشتگان را فرزند خدا خواندند-.'),
(2342, 19, 92, 'و خداى رحمان را نسزد كه فرزندى گيرد.'),
(2343, 19, 93, 'در آسمانها و زمين هيچ كس نيست مگر آنكه خداى رحمان را بنده‌وار آيد.'),
(2344, 19, 94, 'هر آينه آنان را در شمار آورده و همه را درست برشمرده است'),
(2345, 19, 95, 'و همه آنها روز رستاخيز تنها [و تهيدست‌] نزد او آيند.'),
(2346, 19, 96, 'كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند خداى رحمان براى آنان دوستيى [در دلها] خواهد نهاد.'),
(2347, 19, 97, 'همانا [خواندن‌] اين'),
(2348, 19, 98, 'و چه بسا مردمان گذشته را پيش از آنان هلاك كرديم. آيا هيچ يك از آنان را درمى‌يابى- مى‌بينى- يا هيچ آواى آهسته‌اى از آنها مى‌شنوى؟');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طا، ها.'),
(2350, 20, 2, 'ما قرآن را بر تو فرو نفرستاديم تا در رنج افتى،'),
(2351, 20, 3, 'مگر آنكه يادكردى و پندى باشد براى كسى كه مى‌ترسد.'),
(2352, 20, 4, 'فرو فرستاده‌اى است از سوى آن كه زمين و آسمانهاى بلند را آفريده.'),
(2353, 20, 5, 'خداى رحمان بر عرش- تخت فرمانروايى بر جهان هستى- بر آمده است- استيلا دارد-.'),
(2354, 20, 6, 'او راست آنچه در آسمانها و آنچه در زمين است و آنچه ميان آنها و آنچه در زير خاك- درون زمين- است.'),
(2355, 20, 7, 'و اگر سخن را بلند گويى [يا پنهان كنى‌]، همانا او نهان و نهان‌تر را نيز مى‌داند.'),
(2356, 20, 8, 'خداى يكتا، جز او خدايى نيست او راست نامهاى نيكو- صفات برتر-.'),
(2357, 20, 9, 'آيا خبر داستان موسى به تو رسيده است؟'),
(2358, 20, 10, 'آنگاه كه آتشى ديد، پس به خانواده خويش گفت: درنگ كنيد كه من آتشى ديدم، شايد پاره‌اى برگرفته از آن براى شما بيارم يا بر آن آتش رهنمونى بيابم.'),
(2359, 20, 11, 'پس چون نزد آن'),
(2360, 20, 12, 'همانا منم پروردگار تو، پايپوش خويش بيرون كن، كه تو در وادى مقدس طوى هستى.'),
(2361, 20, 13, 'و من تو را برگزيده‌ام، پس به آنچه وحى مى‌شود گوش فرا دار:'),
(2362, 20, 14, 'همانا منم خداى يكتا، خدايى جز من نيست پس مرا بپرست و نماز را براى يادكرد من برپا دار'),
(2363, 20, 15, 'همانا رستاخيز آمدنى است، مى‌خواهم [هنگام‌] آن را پنهان دارم تا هر كسى به آنچه مى‌كند پاداش داده شود.'),
(2364, 20, 16, 'پس آن كس كه بدان ايمان نمى‌آورد و پيرو هواى خويش است تو را از آن نگرداند كه هلاك مى‌شوى.'),
(2365, 20, 17, 'و آن چيست به دست راست تو، اى موسى؟'),
(2366, 20, 18, 'گفت: اين عصاى من است، بر آن تكيه مى‌كنم و با آن براى گوسفندانم [از درختان‌] برگ فرو مى‌ريزم، و مرا در آن، نيازها و سودهاى ديگر است.'),
(2367, 20, 19, 'گفت: آن را بيفكن، اى موسى.'),
(2368, 20, 20, 'پس آن را بيفكند، و ناگهان مارى گشت كه مى‌شتافت.'),
(2369, 20, 21, 'گفت: بگير آن را و مترس زودا كه آن را به حالت نخستينش بازگردانيم.'),
(2370, 20, 22, 'و دستت را به بغل خويش بَرْ تا سپيد و روشن بيرون آيد نه از آسيب و گزند- يعنى نه آن سفيدى كه از آفت پيسى باشد- كه نشانه‌اى ديگر است،'),
(2371, 20, 23, 'تا از نشانه‌هاى بزرگ خويش به تو بنماييم.'),
(2372, 20, 24, 'به سوى فرعون برو، كه او سركشى كرده و از اندازه درگذشته- دعوى خدايى مى‌كند-.'),
(2373, 20, 25, 'گفت: پروردگارا، سينه‌ام را برايم گشاده گردان'),
(2374, 20, 26, 'و كارم را برايم آسان ساز'),
(2375, 20, 27, 'و گِرِه از زبانم بگشاى،'),
(2376, 20, 28, 'تا سخنم را دريابند.'),
(2377, 20, 29, 'و از خاندانم ياورى و پشتيبانى برايم قرار ده'),
(2378, 20, 30, 'برادرم هارون را'),
(2379, 20, 31, 'پشتم را بدو استوار ساز'),
(2380, 20, 32, 'و او را در كار من شريك گردان'),
(2381, 20, 33, 'تا تو را بسيار به پاكى بستاييم'),
(2382, 20, 34, 'و تو را بسيار ياد كنيم.'),
(2383, 20, 35, 'همانا تو به حال ما بينايى.'),
(2384, 20, 36, 'گفت: اى موسى، همانا خواست تو به تو داده شد.'),
(2385, 20, 37, 'و هر آينه بار ديگر بر تو منت نهاديم،'),
(2386, 20, 38, 'آنگاه كه به مادرت آنچه الهام‌كردنى بود الهام كرديم،'),
(2387, 20, 39, 'كه او را در صندوقى بِنِه و در دريا- رود نيل- افكنش پس بايد كه دريا او را به كرانه اندازد تا دشمن من و دشمن او وى را برگيرد و دوستيى از خود بر تو افكندم [تا بر تو مِهر ورزند] و تا زير نظر من ساخته و پرورده شوى'),
(2388, 20, 40, 'آنگاه كه خواهرت [در پى آن صندوق‌] مى‌رفت و مى‌گفت: آيا شما را بر كسى راه نمايم كه او را نگهدارى و پرستارى كند؟ پس تو را به مادرت بازگردانديم تا چشمش روشن شود و اندوه نخورد. و كسى- يعنى آن قبطى، از قوم فرعون- را كشتى پس تو را از اندوه رهانيديم و تو را بارها آزموديم، پس سالى چند در ميان مردم مَدْين درنگ كردى، سپس براندازه و بهنگام- براى رسالت- بيامدى، اى موسى.'),
(2389, 20, 41, 'و تو را براى خود پروردم و برگزيدم.'),
(2390, 20, 42, 'تو و برادرت با نشانه‌هاى من- معجزه‌ها- برويد و در يادكرد من- رسانيدن پيام توحيد من- سستى مكنيد.'),
(2391, 20, 43, 'برويد به سوى فرعون كه او سركشى كرده و از حد گذشته است.'),
(2392, 20, 44, 'پس با وى سخنى نرم بگوييد، شايد كه پند پذيرد يا [از خداى‌] بترسد.'),
(2393, 20, 45, 'گفتند: پروردگارا، ما مى‌ترسيم كه بر ما پيشى و شتاب گيرد- پيشدستى كند و پيش از آنكه سخن گوييم و معجزه‌اى بدو بنماييم، ما را بكشد- يا [در رفتار و گفتار خود] از حد بگذرد.'),
(2394, 20, 46, 'فرمود: مترسيد، كه من با شمايم، مى‌شنوم و مى‌بينم.'),
(2395, 20, 47, 'پس نزد او برويد و بگوييد: ما فرستادگان خداوندگار توايم، پس فرزندان اسرائيل را با ما بفرست و شكنجه‌شان مكن همانا نزد تو با نشانه‌اى از آفريدگارت آمده‌ايم و درود بر آن كه راه راست را پيروى كند.'),
(2396, 20, 48, 'همانا به ما وحى شده كه عذاب بر آن كسى است كه [حق را] دروغ انگاشت و [از آن‌] روى برتافت.'),
(2397, 20, 49, 'گفت: پروردگار شما كيست، اى موسى؟'),
(2398, 20, 50, 'گفت: پروردگار ما آن [خداى‌] است كه به هر چيزى آفرينش- وجود خاص- آن را داد و سپس راه نمود.'),
(2399, 20, 51, 'گفت: پس حال مردمان پيشين چيست.'),
(2400, 20, 52, 'گفت: دانش آن در كتابى است نزد پروردگار من پروردگار من نه خطا مى‌كند و نه فراموش.'),
(2401, 20, 53, 'آن [خداى‌] كه زمين را براى شما بسترى- فرشى گسترده- قرار داد و در آن براى شما راه‌هايى پديد كرد، و از آسمان آبى فرستاد، پس با آن انواع گوناگون از گياهان و رستنيها را بيرون آورديم.'),
(2402, 20, 54, 'بخوريد و چارپايانتان را بچرانيد، همانا در اين، خردمندان را نشانه‌هاست.'),
(2403, 20, 55, 'شما را از آن'),
(2404, 20, 56, 'و هر آينه همه نشانه‌هاى خويش را به او فرعون بنموديم، اما دروغ شمرد و سر باز زد.'),
(2405, 20, 57, 'گفت: اى موسى، آيا نزد ما آمده‌اى تا ما را به جادوى خود از سرزمينمان بيرون كنى؟'),
(2406, 20, 58, 'هر آينه جادويى مانند آن براى تو بياريم، پس ميان ما و خودت وعده‌گاهى در جايى هموار قرار ده- تا مردم نظاره توانند كرد- كه نه ما آن را خلاف كنيم و نه تو.'),
(2407, 20, 59, '[موسى‌] گفت: وعده‌گاه شما روز آرايش- روز عيد- است و آنگاه كه مردمان در نيمروز گرد آورده شوند.'),
(2408, 20, 60, 'پس فرعون برگشت و ترفند خويش فراهم ساخت و باز آمد.'),
(2409, 20, 61, 'موسى به آنان گفت: واى بر شما، بر خدا دروغ مبنديد كه شما را به عذابى نابود و ريشه‌كن كند، و همانا آن كه [بر خدا] دروغ بست نوميد و زيانكار گشت.'),
(2410, 20, 62, 'پس [ساحران‌] در كارشان ميان خود گفت‌وگو كردند و آن راز را پنهان داشتند.'),
(2411, 20, 63, '[فرعونيان‌] گفتند: اين دو هر آينه جادوگرند كه مى‌خواهند شما را به جادوى خويش از سرزمينتان بيرون كنند و آيين برتر شما را از ميان ببرند.'),
(2412, 20, 64, 'پس ترفندتان- اسباب جادو- را با هم آريد- همسخن و همدل و هماهنگ شويد- آنگاه صف كشيده بياييد، و بى‌گمان هر كه امروز برترى يافت پيروز و كامياب شده است.'),
(2413, 20, 65, 'گفتند: اى موسى، تو مى‌افكنى يا ما نخستين كسى باشيم كه بيفكند؟'),
(2414, 20, 66, 'گفت: بلكه شما بيفكنيد. پس ناگاه ريسمانها و چوبدستيهاشان از جادوى آنان به او'),
(2415, 20, 67, 'پس موسى در دل خويش بيمى دريافت.'),
(2416, 20, 68, 'گفتيم: مترس كه همانا تويى برتر.'),
(2417, 20, 69, 'و آنچه در دست راست توست بيفكن تا آنچه ساخته‌اند فرو برد. همانا آنچه ساخته‌اند فريب و ترفند جادوگر است، و جادوگر هر جا كه آيد پيروز نگردد.'),
(2418, 20, 70, 'پس جادوان به سجده درافتاده گفتند: به پروردگار موسى و هارون ايمان آورديم.'),
(2419, 20, 71, '[فرعون‌] گفت: آيا پيش از آنكه شما را اجازه دهم به او گرويديد؟ بى‌گمان او بزرگ- استاد مهين- شماست كه شما را جادو آموخته است. هر آينه دستها و پاهاى شما را بر خلاف يكديگر مى‌برم و بر تنه‌هاى درخت خرما بر دارتان مى‌كنم و خواهيد دانست كه شكنجه كدام يك از ما سخت‌تر و پاينده‌تر است.'),
(2420, 20, 72, 'گفتند: هرگز تو را بر آنچه به ما از نشانه‌هاى روشن آمده است و بر آن [خداى‌] كه ما را آفريده است برنگزينيم پس هر چه را تو حكم‌كننده‌اى حكم كن- هر چه خواهى بكن-، كه تنها در زندگى اين جهان حكم توانى كرد.'),
(2421, 20, 73, 'ما به پروردگارمان ايمان آورديم تا لغزشهاى ما و آنچه را از جادوگرى كه بر آن وادارمان كردى بيامرزد و خدا بهتر و پاينده‌تر است.'),
(2422, 20, 74, 'همانا هر كس كه گناهكار نزد پروردگار خويش آيد پس او راست دوزخ، در آنجا نه بميرد [تا برهد] و نه [به خوشى‌] زندگانى كند.'),
(2423, 20, 75, 'و هر كه با ايمان نزد او آيد در حالى كه كارهاى نيك و شايسته كرده باشد پس اينان را پايه‌هاى برترين باشد،'),
(2424, 20, 76, 'بهشت‌هاى پاينده كه از زير آنها جويها روان است، در آنجا جاويدانند و اين است پاداش كسى كه [از آلودگيهاى كفر و گناه‌] پاكى ورزيد.'),
(2425, 20, 77, 'و هر آينه به موسى وحى كرديم كه بندگان مرا شبانگاه ببر، پس براى آنان راهى خشك در دريا فرا گير- با زدن عصا به آب دريا و خشك شدن آن- كه نه از دست‌يافتن [فرعونيان‌] بترسى و نه [از غرق‌گشتن‌] بيم دارى.'),
(2426, 20, 78, 'پس فرعون با سپاهيانش از پى آنان روانه شدند، و از دريا بپوشيدشان آنچه پوشيدشان- آب دريا غرقشان ساخت-.'),
(2427, 20, 79, 'و فرعون قوم خود را گمراه كرد و راه ننمود.'),
(2428, 20, 80, 'اى فرزندان اسرائيل، شما را از دشمنتان رهانيديم و با شما كنار راست كوه طور را وعده كرديم و بر شما منّ و سَلْوى فرو فرستاديم.'),
(2429, 20, 81, 'از پاكيزه‌هايى كه روزيتان كرديم بخوريد و در آن از اندازه مگذريد كه خشم من بر شما فرو آيد، و هر كه خشم من بر او فرو آيد بى‌گمان هلاك شود- يا در دوزخ افتد-.'),
(2430, 20, 82, 'و هر آينه من آمرزگارم مر آن را كه توبه كند و ايمان آورد و كار نيك و شايسته كند سپس [پيوسته‌] در راه راست باشد.'),
(2431, 20, 83, 'اى موسى، چه چيز تو را بر آن داشت تا از قومت با شتاب پيشى گيرى- پيش از همه در «طور» حاضر شوى-؟'),
(2432, 20, 84, 'گفت: آنان اينك بر پى من مى‌آيند و به سوى تو، پروردگارا، شتافتم تا خشنود شوى.'),
(2433, 20, 85, '[خداى‌] گفت: پس [بدان كه‌] ما قوم تو را پس از [آمدن‌] تو آزمون كرديم و سامرى گمراهشان كرد.'),
(2434, 20, 86, 'پس موسى خشمناك و اندوهگين به نزد قوم خود بازگشت. گفت: اى قوم من، آيا پروردگارتان به شما وعده‌اى نيكو نداد- كه به شما تورات دهد يا شما را از فرعونيان برهاند-؟ آيا اين مدت- زمان غيبت من- بر شما دراز آمد يا خواستيد كه خشمى از پروردگارتان بر شما فرود آيد كه وعده مرا خلاف كرديد؟'),
(2435, 20, 87, 'گفتند: ما به اختيار و توان خود وعده تو را خلاف نكرديم، و ليكن بارهايى از پيرايه و زيور آن قوم- قوم فرعون- بر ما نهادند، پس آنها را [در آتش‌] افكنديم و همچنين سامرى [آنچه با خود داشت در آتش‌] افكند'),
(2436, 20, 88, 'و براى آنان كالبد گوساله‌اى برون آورد كه او را بانگى بود پس [سامرى و پيروانش‌] گفتند: اين خداى شما و خداى موسى است، و [سامرى خداى راستين را] از ياد برد.'),
(2437, 20, 89, 'آيا نمى‌ديدند كه [آن گوساله‌] سخنى به آنها باز نمى‌گرداند و آنان را هيچ زيان و سودى ندارد؟'),
(2438, 20, 90, 'و هر آينه هارون از پيش- پيش از آمدن موسى- به آنها گفته بود: اى قوم من، همانا شما به اين'),
(2439, 20, 91, 'گفتند: ما پيوسته بر اين [گوساله‌پرستى‌] مى‌مانيم تا موسى به سوى ما بازگردد.'),
(2440, 20, 92, '[هنگامى كه موسى باز آمد] گفت: اى هارون، چون آنها را ديدى كه گمراه شدند چه چيز تو را بازداشت،'),
(2441, 20, 93, 'از اين كه مرا پيروى كنى- كه آنها را از پرستيدن گوساله بازدارى، يا از پى من آيى و خود را به من رسانى-؟ آيا فرمان مرا نافرمانى كردى؟'),
(2442, 20, 94, 'گفت: اى پسر مادرم، ريش و سر مرا مگير، من ترسيدم كه بگويى ميان فرزندان اسرائيل جدايى انداختى و گفتار- سفارش- مرا نگاه نداشتى.'),
(2443, 20, 95, 'گفت: اى سامرى، اين چه كار- فتنه بزرگ- است كه كردى؟'),
(2444, 20, 96, 'گفت: من چيزى ديدم كه آنها نديدند. مشتى [خاك‌] از پى [مركب‌] آن فرستاده- يعنى جبرئيل- برگرفتم و آن را [در كالبد گوساله‌] افكندم، و نفس من بدين سان [اين كار را] در نظرم بياراست.'),
(2445, 20, 97, 'گفت: پس برو- دور شو- كه تو را در زندگى- تا زنده باشى- اين است كه بگويى: «به من دست نزنيد». و تو را وعده‌گاهى است كه هرگز آن را با تو خلاف نكنند، و [اينك‌] به خدايت كه پيوسته بر آن مقيم بودى بنگر كه آن را بسوزانيم- يا به سوهان بساييم- سپس يكسره در دريا پراكنده‌اش سازيم.'),
(2446, 20, 98, 'همانا خداى شما خداى يكتاست كه جز او هيچ خدايى نيست دانش او همه چيز را فراگيرنده است.'),
(2447, 20, 99, 'اينچنين از خبرهايى كه گذشته است بر تو بر مى‌گوييم، و براستى تو را از نزد خويش يادكردى- قرآن- داده‌ايم'),
(2448, 20, 100, 'هر كه از آن روى بگرداند، همانا در روز رستاخيز بارى سنگين- يا بارى بد: كفر و تكذيب- بردارد،'),
(2449, 20, 101, 'در آن- بار گناه و عقوبتش- هميشه بمانند، و آنها را بدبارى است در روز رستاخيز،'),
(2450, 20, 102, 'روزى كه در صور دميده شود، و بزهكاران را در آن روز كبود چشم برانگيزيم'),
(2451, 20, 103, 'ميان خود راز همى گويند كه شما جز ده روز- در دنيا- درنگ نكرده‌ايد.'),
(2452, 20, 104, 'ما به آنچه مى‌گويند داناتريم آنگاه كه بهترينشان در رفتار و گفتار گويد: درنگ نكرده‌ايد مگر يك روز.'),
(2453, 20, 105, 'و تو را از كوه‌ها پرسند بگو: پروردگارم همه آنها را بر كند و خرد و پراكنده سازد.'),
(2454, 20, 106, 'پس آن'),
(2455, 20, 107, 'در آن هيچ كجى- يعنى پستى- و بلندى نبينى.'),
(2456, 20, 108, 'در آن روز آن خواننده- دمنده صور- را بى‌هيچ كژى- انحراف و نافرمانى- پيروى كنند، و آوازها در برابر خداى رحمان فرونشيند، پس آوايى نشنوى مگر آوايى آهسته.'),
(2457, 20, 109, 'در آن روز شفاعت سود ندارد مگر [شفاعت‌] كسى كه خداى رحمان به او اجازه دهد و سخن او را بپسندد.'),
(2458, 20, 110, 'آنچه پيش روى و آنچه پشت سرشان است مى‌داند، و دانش آنان او را فرانگيرد.'),
(2459, 20, 111, 'و چهره‌ها در برابر خداى زنده پاينده خوار و فروتن باشند و هر كه بار ستم برداشت بى‌گمان تهيدست و نوميد گردد.'),
(2460, 20, 112, 'و هر كه از كارهاى نيك و شايسته كند و مؤمن باشد، پس از هيچ ستمى نترسد و نه از كاستنى- كاستن پاداش كردار-.'),
(2461, 20, 113, 'و اينچنين آن را قرآنى به زبان تازى فروفرستاديم و در آن از بيمها و هشدارها گونه‌گون آورديم تا شايد بپرهيزند يا آنان را ياد كرد و پندى پديد آورد.'),
(2462, 20, 114, 'پس والا و برتر است خداى يكتا آن پادشاه راستين. و به خواندن قرآن پيش از آنكه پيام آن به تو گزارده شود شتاب مكن، و بگو: پروردگارا، مرا دانش افزاى.'),
(2463, 20, 115, 'و هر آينه از پيش به آدم سفارش كرديم، پس فراموش كرد و او را [در نگاهداشت آن‌] آهنگى استوار نيافتيم.'),
(2464, 20, 116, 'و [ياد كن‌] آنگاه كه فرشتگان را گفتيم: آدم را سجده كنيد، پس سجده كردند مگر ابليس كه سر باز زد.'),
(2465, 20, 117, 'گفتيم: اى آدم، اين دشمن تو و همسر توست، مبادا شما را از بهشت بيرون كند كه در رنج افتى- يا بدبخت شوى-'),
(2466, 20, 118, 'كه تو را در آنجا اين هست كه نه گرسنه مى‌شوى و نه برهنه'),
(2467, 20, 119, 'و در آنجا نه تشنه مى‌شوى و نه در گرمى آفتاب بمانى.'),
(2468, 20, 120, 'پس شيطان او را وسوسه كرد، گفت: اى آدم، آيا تو را به درخت جاودانى و پادشاهيى كه كهنه و نابود نشود راه بنمايم؟'),
(2469, 20, 121, 'پس، از آن درخت خوردند و شرمگاهشان بر آنها نمودار شد، و شروع كردند از برگ [درختان‌] بهشت بر خود چسباندن،- شرمگاه خود را مى‌پوشاندند- و آدم پروردگار خويش را نافرمانى نمود پس راه راست را گم كرد.'),
(2470, 20, 122, 'سپس پروردگارش او را برگزيد و به مهر و بخشايش خويش بر او بازگشت و توبه‌اش بپذيرفت و او را راه نمود.'),
(2471, 20, 123, 'گفت: هر دو با هم از آنجا فروشويد، برخى از شما- آدميان- دشمن برخى خواهيد بود. پس اگر از من شما را رهنمونى- كتاب و پيامبر- آمد، هر كه از رهنمونى من پيروى كند نه گمراه شود و نه به رنج افتد- به كيفر آن جهان گرفتار نشود-'),
(2472, 20, 124, 'و هر كه از يادكرد و پند من روى بگرداند پس زيستنى تنگ دارد و روز رستاخيز او را نابينا برانگيزيم.'),
(2473, 20, 125, 'گويد: پروردگارا، چرا مرا نابينا برانگيختى و حال آنكه بينا بودم؟!'),
(2474, 20, 126, 'گويد: بدين گونه [امروز كور محشور شدى زيرا] آيات ما به تو رسيد و تو آنها را به فراموشى سپردى- از آنها تغافل كردى- و همچنان امروز فراموش مى‌شوى.'),
(2475, 20, 127, 'و بدين‌سان هر كه را زياده‌روى كرده و به آيات پروردگار خويش ايمان نياورده است كيفر مى‌دهيم، و هر آينه عذاب آن جهان سخت‌تر و پاينده‌تر است.'),
(2476, 20, 128, 'آيا براى آنان- مشركان قريش- راه ننمود كه چه نسلها را پيش از آنها هلاك كرديم كه اينان در مسكنهاى آنان راه مى‌روند- و آثار عذاب و هلاك آنان را مى‌بينند-؟ هر آينه در آن براى خردمندان نشانه‌ها و عبرتهاست.'),
(2477, 20, 129, 'و اگر نه اين بود كه سخنى از پروردگار تو از پيش رفته و سرآمدى معين شده است هر آينه عذاب بر آنها لازم مى‌گرديد- گريبانشان را مى‌گرفت-.'),
(2478, 20, 130, 'پس هر آنچه مى‌گويند شكيبايى ورز و پروردگارت را پيش از برآمدن آفتاب- نماز بامداد- و پيش از فروشدن آن- نماز عصر- همراه با ستايش و سپاس به پاكى ياد كن، و از ساعتهاى شب- نماز شام و نماز خفتن- و كناره‌هاى روز- نماز پيشين ظهر، كه پايان نيمه اول روز است و آغاز نيمه دوم روز- نيز او را تسبيح گوى، باشد كه خشنود شوى.'),
(2479, 20, 131, 'و ديدگانت را مدوز بدانچه برخوردارى داده‌ايم گروه‌هايى از آنان را از آرايش زندگى دنيا تا در باره آن بيازماييمشان، و روزى پروردگارت بهتر و پاياتر است.'),
(2480, 20, 132, 'و خاندان خويش را به نماز فرمان ده و بر آن شكيبا باش- مداومت نما- از تو روزى نمى‌خواهيم، ما خود به تو روزى مى‌دهيم و سرانجام نيك پرهيزگارى راست.'),
(2481, 20, 133, 'و [كافران مكه‌] گفتند: چرا ما را از پروردگار خويش نشانه‌اى- معجزه‌اى به دلخواه ما- نمى‌آورد؟ آيا آنها را نشانه روشن آنچه در كتابهاى پيشين است نيامده است؟'),
(2482, 20, 134, 'و اگر ما آنان را پيش از آن- فرستادن كتاب و پيامبر- به عذابى هلاك مى‌كرديم هر آينه مى‌گفتند: پروردگارا، چرا به سوى ما پيامبرى نفرستادى تا پيش از آنكه خوار و رسوا شويم آيات تو را پيروى كنيم؟'),
(2483, 20, 135, 'بگو: هر يك [از ما] منتظر است، پس منتظر باشيد. بزودى خواهيد دانست كه رهروان راه راست كيانند و راه‌يافته كيست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'مردم را [هنگام رسيدگى به‌] حسابشان- يعنى رستاخيز- نزديك شد و آنان در ناآگاهى- از حساب و بازخواست- رويگردانند.'),
(2485, 21, 2, 'آنان را هيچ يادكرد و پند تازه‌اى از پروردگارشان نيايد مگر اينكه آن را بشنوند در حالى كه بازى مى‌كنند'),
(2486, 21, 3, 'دلهاشان [از حق غافل و به چيزى ديگر] سرگرم است، و كسانى كه ستم كردند رازگويى خود را پنهان داشتند كه: آيا اين جز آدميى مانند شماست؟ پس آيا به آن جادو رو مى‌كنيد و حال آنكه مى‌بينيد [كه جادو و دروغ است‌]؟!'),
(2487, 21, 4, '[پيامبر] گفت: پروردگار من هر سخنى را كه در آسمان و زمين باشد مى‌داند، و اوست شنوا و دانا.'),
(2488, 21, 5, 'بلكه گفتند: [سخنان او] خوابهاى آشفته و پريشان است، بلكه آن را بربافته، بلكه او شاعر است پس بايد براى ما نشانه‌اى- معجزه‌اى- بياورد چنانكه پيشينيان- پيامبران پيشين- [با آن‌] فرستاده شده‌اند.'),
(2489, 21, 6, 'پيش از آنان [مردم‌] هيچ آبادى و شهرى كه آن را هلاك كرديم ايمان نياوردند آيا اينها ايمان مى‌آورند؟!'),
(2490, 21, 7, 'و پيش از تو نفرستاديم مگر مردانى را كه به ايشان وحى مى‌كرديم. پس اگر خود نمى‌دانيد از اهل ذكر- اهل كتاب- بپرسيد- تا بدانيد كه پيامبران آنها هم آدمى بوده‌اند-.'),
(2491, 21, 8, 'و آنان را كالبدى نساختيم كه طعام نخورند، و جاويدان هم نبودند.'),
(2492, 21, 9, 'سپس آن وعده را كه به ايشان داديم- نابودى دشمنانشان و يارى و پيروزى ايشان- راست كرديم، پس آنان و هر كه را خواستيم رهانيديم و گزافه كاران را نابود ساختيم.'),
(2493, 21, 10, 'هر آينه به سوى شما كتابى فروفرستاديم كه در آن يادكرد شماست. آيا خرد را كار نمى‌بنديد؟'),
(2494, 21, 11, 'و چه بسيار [مردم‌] شهرها را كه ستمكار بودند فروشكستيم- هلاك ساختيم- و پس از آن قومى ديگر پديد كرديم.'),
(2495, 21, 12, 'پس چون عذاب را دريافتند آنگاه به تك از آن مى‌گريختند.'),
(2496, 21, 13, 'مگريزيد، و به كامرانى و خانه‌هاى خويش بازگرديد تا مگر از شما بپرسند.'),
(2497, 21, 14, '[اما چون عذاب را ديدند و پشيمان شدند] گفتند: واى بر ما كه ستمكار بوديم.'),
(2498, 21, 15, 'پس پيوسته گفتارشان اين بود تا آنها را دروشده و خاموش و بيجان كرديم.'),
(2499, 21, 16, 'و ما آسمان و زمين و آنچه را ميان آنهاست به بازى نيافريديم.'),
(2500, 21, 17, 'اگر مى‌خواستيم سرگرمى و بازيچه‌اى بگيريم- مثل آنكه گفتند خدا همسر و فرزند و شريك گرفت-، آن را از نزد خويش مى‌گرفتيم، اگر كننده [اين كار] بوديم.'),
(2501, 21, 18, 'بلكه حق را بر باطل مى‌افكنيم پس آن را فروشكند و باطل بى‌درنگ نابود شود، و واى بر شما از وصفى كه مى‌كنيد- كه به خدا نسبت زناشويى و فرزند مى‌دهيد-.'),
(2502, 21, 19, 'و او راست هر كه در آسمانها و زمين است، و آنان كه نزد اويند- فرشتگان- از پرستش وى گردنكشى و بزرگ‌منشى نمى‌كنند و خسته و مانده نمى شوند،'),
(2503, 21, 20, 'شب و روز خداى را به پاكى مى‌ستايند و سستى نمى‌كنند.'),
(2504, 21, 21, 'آيا [اين مردم‌] خدايانى از زمين گرفته‌اند كه آنها مردگان را زنده كنند؟!'),
(2505, 21, 22, 'اگر در آن دو- آسمان و زمين- خدايانى جز خداى يكتا مى‌بود هر آينه هر دو تباه مى‌شدند. پاك و منزه است خداى، پروردگار عرش، از وصفى كه مى كنند.'),
(2506, 21, 23, 'او را از آنچه مى‌كند نپرسند و آنان را بپرسند.'),
(2507, 21, 24, 'بلكه بجز او خدايانى گرفته‌اند؟! بگو: حجتهاى خويش را بياوريد. اين- آنچه در قرآن است- يادكرد كسانى است كه با من‌اند و يادكرد كسانى است كه پيش از من بوده‌اند. بلكه بيشترشان حق- توحيد- را نمى‌دانند و از اين رو رويگردانند.'),
(2508, 21, 25, 'و پيش از تو هيچ پيامبرى نفرستاديم مگر آنكه به او وحى كرديم كه خدايى جز من نيست، پس تنها مرا بپرستيد.'),
(2509, 21, 26, 'و [مشركان‌] گفتند: خداى رحمان فرزندى گرفت. [نه،] او پاك و منزه است، بلكه [آن فرشتگان‌] بندگانى گرامى‌اند'),
(2510, 21, 27, 'كه به گفتار بر او پيشى نگيرند و آنان به فرمان او كار مى‌كنند'),
(2511, 21, 28, 'آنچه را پيش روى آنهاست و آنچه را واپس آنهاست- كارهايى را كه مى‌كنند و خواهند كرد- مى‌داند و جز براى كسانى كه او بپسندد و خشنود باشد شفاعت نمى‌كنند و از ترس او- از ترس عقوبت او يا عظمت و هيبت او- بيمناكند.'),
(2512, 21, 29, 'و هر كس از آنها كه گويد: من خدايى هستم بجز او، پس وى را دوزخ كيفر دهيم اينچنين ستمكاران را كيفر مى‌دهيم.'),
(2513, 21, 30, 'آيا كسانى كه كافر شدند نديدند كه آسمانها و زمين بسته بودند پس آنها را بازگشاديم و هر چيز زنده‌اى را از آب پديد كرديم؟ پس آيا ايمان نمى آورند؟'),
(2514, 21, 31, 'و در زمين كوه‌هاى استوار گران آفريديم تا نلرزاندشان و در آن- در زمين يا در ميان كوه‌ها- راه‌هاى فراخ قرار داديم، باشد كه [در سفرها به مقاصد خود] راه يابند.'),
(2515, 21, 32, 'و آسمان را سقفى نگاه‌داشته ساختيم و آنان از نشانه‌هاى آن رويگردانند.'),
(2516, 21, 33, 'و اوست كه شب و روز و خورشيد و ماه را آفريد، هر يك در چرخه‌اى شناورند.'),
(2517, 21, 34, 'و ما براى هيچ آدمى پيش از تو جاودانگى ننهاديم، آيا اگر تو بميرى آنان جاويدان باشند؟!'),
(2518, 21, 35, 'هر كسى چشنده مرگ است و شما را به بدى- سختى و بلا و مصيبت- و نيكى- آسانى و نعمت و دولت- مى‌آزماييم، آزمايشى، و به ما بازگردانده مى شويد.'),
(2519, 21, 36, 'و چون كسانى كه كافر شدند تو را ببينند جز به ريشخندت نگيرند [گويند:] آيا اين است آن كه خدايانتان را [به بدى و عيب‌] ياد مى‌كند؟ و حال آنكه خود به يادكرد خداى رحمان كافرند- پس آنها خود سزاوار ريشخندند-.'),
(2520, 21, 37, 'آدمى از شتاب آفريده شده زودا كه نشانه‌هاى خويش- عذاب- را به شما بنمايم، پس [عذاب را] از من به شتاب مخواهيد.'),
(2521, 21, 38, 'و گويند: اگر راستگو هستيد هنگام اين وعده- رستاخيز و عذاب- كى خواهد بود؟'),
(2522, 21, 39, 'اگر كسانى كه كافر شدند آن هنگام را مى‌دانستند كه نتوانند آن آتش را از رويهاى خود و از پشتهاى خويش باز دارند و نه يارى كرده شوند، [البته عذاب را به شتاب نمى خواستند].'),
(2523, 21, 40, 'بلكه [عذاب‌] ناگاه بديشان رسد پس چنان فرومانده و سرگشته‌شان سازد كه بازداشتن آن نتوانند و نه [بر تأخير عذاب‌] مهلت يابند.'),
(2524, 21, 41, 'و هر آينه پيامبرانى هم كه پيش از تو بودند استهزاء شدند، پس كسانى را كه ايشان را تمسخر مى‌كردند آنچه بدان استهزا مى‌كردند- يعنى عذاب- فروگرفت.'),
(2525, 21, 42, 'بگو: كيست كه شما را شب و روز از [عذاب‌] خداى رحمان نگاه مى‌دارد؟ بلكه آنان از ياد پروردگارشان- يا از قرآن و پند آن- رويگردانند.'),
(2526, 21, 43, 'يا مگر خدايانى دارند بجز ما كه آنان را [از عذاب‌] نگاه مى‌دارند؟! آنها يارى‌كردن خود نتوانند و نه از سوى ما يارى و همراهى شوند- يا پناه و زنهار يابند-.'),
(2527, 21, 44, 'بلكه آنها و پدرانشان را برخوردارى داديم تا عمرهاشان دراز گشت. آيا نمى‌بينند كه ما زمين را از كناره‌هاى آن مى‌كاهيم- برخى از امتها را هلاك و نابود مى‌كنيم-؟ پس آيا آنها- كافران قريش- چيره و پيروزند [و نابود نخواهند شد]؟!'),
(2528, 21, 45, 'بگو: همانا من شما را به وحى- اين قرآن- بيم مى‌كنم، و كران آنگاه كه بيم‌كرده شوند خواندن را نمى‌شنوند- بانگ دعوت به حق را درنيابند-.'),
(2529, 21, 46, 'و اگر يك دم- اندكى- از عذاب پروردگار تو به آنان رسد هر آينه گويند: اى واى بر ما، ما ستمكار بوديم.'),
(2530, 21, 47, 'و ما ترازوهاى دادگرى را در روز رستاخيز مى‌نهيم- براى حسابرسى و سنجش اعمال نيك و بد- پس به كسى هيچ ستمى نرود و اگر [كردارشان‌] همسنگ دانه خردلى باشد آن را بياريم، و ما حسابگرانى بسنده‌ايم.'),
(2531, 21, 48, 'و هر آينه موسى و هارون را فرقان- جداكننده حق و باطل- و روشنايى و يادآورى و پندى- تورات- براى پرهيزگاران داديم'),
(2532, 21, 49, 'آنان كه از پروردگار خويش در نهان مى‌ترسند و از رستاخيز بيمناكند.'),
(2533, 21, 50, 'و اين [قرآن‌] يادآورى و پندى است با بركت كه آن را فروفرستاديم، پس آيا شما آن را منكريد؟!'),
(2534, 21, 51, 'و هر آينه ابراهيم را از پيش- پيش از موسى- راه شناسى و راه‌يابى در خور وى داديم و به [حال‌] او دانا بوديم.'),
(2535, 21, 52, 'آنگاه كه به پدرش و قومش گفت: اين تنديسها چيست كه بدانها روى آورده و به پرستش آنها ايستاده‌ايد؟'),
(2536, 21, 53, 'گفتند: پدرانمان را پرستندگان آنها يافته‌ايم.'),
(2537, 21, 54, 'گفت: هر آينه شما و پدرانتان در گمراهى آشكار بوده‌ايد.'),
(2538, 21, 55, 'گفتند: آيا ما را بحق آمده‌اى يا تو از بازيگرانى- بجِد سخن مى‌گويى يا بهزل-؟'),
(2539, 21, 56, 'گفت [نه، بازيگر نيستم‌] بلكه پروردگار شما خداوند آسمانها و زمين است، آن كه آنها را آفريده است و من بر اين [سخن‌] از گواهانم.'),
(2540, 21, 57, 'و [در نهان گفت:] به خدا سوگند كه پس از آنكه پشت‌كرده برگرديد بتهاى شما را چاره كنم.'),
(2541, 21, 58, 'پس آنها را خرد كرد مگر بت بزرگشان را تا مگر به آن- يا به او يعنى ابراهيم- بازگردند.'),
(2542, 21, 59, '[چون باز آمدند] گفتند: چه كسى به خدايان ما اين كار كرده است؟ همانا او از ستمكاران است.'),
(2543, 21, 60, 'گفتند: شنيده‌ايم جوانى كه او را ابراهيم گويند از آنها [به بدى‌] ياد مى‌كند.'),
(2544, 21, 61, 'گفتند: پس او را به ديدار چشم مردم بياريد شايد گواهى دهند- كه اين است كه از بتها نكوهش مى‌كند-.'),
(2545, 21, 62, 'گفتند: اى ابراهيم، آيا تو با خدايان ما اين كار كرده‌اى؟'),
(2546, 21, 63, 'گفت: بلكه اين كار را [بت‌] بزرگشان كرده است اگر سخن مى‌گويند از آنها بپرسيد.'),
(2547, 21, 64, 'پس به خويشتن خويش بازگشتند- به خود آمدند- و [با يكديگر] گفتند: همانا شماييد ستمكاران.'),
(2548, 21, 65, 'آنگاه نگونسار شدند [و گفتند:] هر آينه تو مى‌دانى كه اينان سخن نمى‌گويند.'),
(2549, 21, 66, 'گفت: آيا چيزهايى را به جاى خدا مى‌پرستيد كه شما را هيچ سود و زيانى نرسانند؟'),
(2550, 21, 67, 'اف بر شما و بر آنچه جز خدا مى‌پرستيد آيا خِرَد را كار نمى‌بنديد؟'),
(2551, 21, 68, 'گفتند: او را بسوزانيد و خدايان خويش را يارى كنيد، اگر كننده‌ايد- يعنى اگر يارى كنندگان بتان خويشيد-.'),
(2552, 21, 69, 'گفتيم: اى آتش، بر ابراهيم سرد و سلامت باش.'),
(2553, 21, 70, 'و خواستند در باره او چاره بد سازند، پس آنان را زيانكارتر ساختيم.'),
(2554, 21, 71, 'و او و لوط را [با مهاجرت‌] به آن سرزمينى كه در آن براى جهانيان بركت نهاده‌ايم- سرزمين شام- رهانيديم.'),
(2555, 21, 72, 'و او را [پسرى چون‌] اسحاق بخشيديم و [فرزندزاده‌اى چون‌] يعقوب را به فزونى داديم و همه را نيكوكاران و شايستگان گردانيديم.'),
(2556, 21, 73, 'و ايشان را پيشوايانى كرديم كه به فرمان ما راه نمايند، و به آنان كارهاى نيك كردن و برپا داشتن نماز و دادن زكات را وحى كرديم، و ما را پرستندگان بودند.'),
(2557, 21, 74, 'و لوط را حكم- حكمت يا نبوت- و دانش داديم و او را از آن شهرى كه كارهاى پليد مى‌كردند رهانيديم. همانا آنها مردمى بدكار و نافرمان بودند.'),
(2558, 21, 75, 'و او را در مِهر و بخشايش خويش درآورديم، كه او از نيكان و شايستگان بود.'),
(2559, 21, 76, 'و نوح را [ياد كن‌] آنگاه كه پيش از اين- پيش از ابراهيم و لوط- ما را بخواند پس وى را پاسخ داديم و او و خاندانش را از اندوه بزرگ- طوفان و غرق- رهانيديم.'),
(2560, 21, 77, 'و او را در برابر گروهى كه آيات ما را دروغ انگاشتند يارى داديم [و از آنان رهايى بخشيديم‌]، همانا آنها مردمى بد بودند پس همگيشان را غرق ساختيم.'),
(2561, 21, 78, 'و داوود و سليمان را [ياد كن‌] آنگاه كه در باره كشتزارى داورى مى‌كردند كه شبانه گوسفندان آن قوم در آن چريده بودند و ما داورى كردن آنها را حاضر و گواه بوديم.'),
(2562, 21, 79, 'پس [حكم‌] آن را به سليمان فهمانديم، و هر يك را حكم- حكمت يا نبوت يا حكومت يا داورى ميان خصمها- و دانش داديم و كوه‌ها را رام داوود كرديم كه با او تسبيح مى‌گفتند، و مرغان را [نيز رام او كرديم‌] و ما كننده [اينها] بوديم.'),
(2563, 21, 80, 'و براى شما ساختن زره را به او آموختيم تا شما را از سختى و گزندتان- در كارزار- نگاه دارد، پس آيا شما سپاس مى‌داريد؟'),
(2564, 21, 81, 'و باد تند و سخت را براى سليمان [رام كرديم‌] كه به فرمان او به سوى سرزمينى كه در آن بركت نهاده‌ايم- سرزمين شام- روان بود و ما به همه چيز دانا بوديم.'),
(2565, 21, 82, 'و از ديوان، كسانى براى او به دريا فرو مى‌شدند. [تا جواهر آرند]، و جز آن كارها مى‌كردند- ساختن محرابها، مجسمه‌ها، ظروف سنگى آب و ...- و ما نگاهدارنده آنها بوديم- تا از فرمان سليمان بيرون نروند-.'),
(2566, 21, 83, 'و ايوب را [ياد كن‌] آنگاه كه پروردگارش را بخواند كه مرا سختى و گزند رسيده و تو مهربان‌ترين مهربانانى.'),
(2567, 21, 84, 'پس او را اجابت كرديم و سختى و گزندى كه به وى بود برداشتيم و خاندانش را و همچند آنها را با آنها به او باز داديم تا بخشايشى باشد از نزد ما و تا يادبود و پندى براى پرستندگان [خداى‌] باشد.'),
(2568, 21, 85, 'و اسماعيل و ادريس و ذو الكفل را [ياد كن‌] كه همه از شكيبايان بودند'),
(2569, 21, 86, 'و ايشان را در مهر و بخشايش خويش در آورديم، كه آنان از نيكان و شايستگان بودند.'),
(2570, 21, 87, 'و ذو النون- صاحب نهنگ ماهى، يونس- را [ياد كن‌] آنگاه كه خشمناك برفت و پنداشت كه هرگز بر او تنگ نمى‌گيريم پس در آن تاريكيها- تاريكى شب و تاريكى دريا و تاريكى شكم نهنگ‌ماهى- آواز داد كه خدايى جز تو نيست، پاك و منزهى تو، همانا من از ستمكاران بودم.'),
(2571, 21, 88, 'پس اجابتش كرديم و او را از اندوه رهانيديم، و مؤمنان را اينچنين مى‌رهانيم.'),
(2572, 21, 89, 'و زكريّا را [ياد كن‌] آنگاه كه پروردگار خويش را بخواند: اى پروردگار من، مرا تنها- بى‌فرزند- مگذار و تو بهترين وارثانى.'),
(2573, 21, 90, 'پس اجابتش كرديم و يحيى را به او بخشيديم و همسر او را برايش شايسته- زاينده يا خوشخو- گردانيديم. اينان در كارهاى نيك مى‌شتافتند و ما را با اميد و بيم مى‌خواندند، و ما را فروتن و فرمانبردار بودند.'),
(2574, 21, 91, 'و آن زن- مريم- را [ياد كن‌] كه دامان عفت خويش نگاه داشت، پس، از روح خود در او دميديم و او و پسرش را براى جهانيان نشانه‌اى ساختيم.'),
(2575, 21, 92, '[اى مردم،] همانا اين آيين شماست، آيينى يگانه- يعنى دين اسلام كه همه پيامبران بر آن بودند- و منم پروردگار شما، پس مرا بپرستيد.'),
(2576, 21, 93, 'و [مردم‌] كار- آيين- خويش را در ميان خود پاره پاره كردند- در دين گروه گروه شدند- [و] همه به سوى ما بازخواهند گشت.'),
(2577, 21, 94, 'پس هر كه كارهاى نيك و شايسته كند در حالى كه مؤمن باشد كوشش وى را ناسپاسى نباشد و ما براى او نويسنده‌ايم.'),
(2578, 21, 95, 'و بر مردم شهرى كه آنها را هلاك كرديم حرام- ناشدنى- است كه [به اين دنيا] بازگردند [تا از كفر توبه كنند].'),
(2579, 21, 96, 'تا آنگاه كه [سدّ] يأجوج و مأجوج گشوده شود و آنان از هر بلندى و تپه‌اى به شتاب بيرون آيند.'),
(2580, 21, 97, 'و آن وعده راست- رستاخيز- نزديك شود، پس ناگهان ديدگان كسانى كه كافر شدند خيره ماند [و گويند:] واى بر ما، براستى ما از اين [حال‌] در غفلت بوديم، بلكه ما ستمكار بوديم.'),
(2581, 21, 98, 'همانا شما و آنچه جز خدا مى‌پرستيد- يعنى بتان- هيمه دوزخيد شما به آنجا درون خواهيد شد.'),
(2582, 21, 99, 'اگر اينها خدايان بودند به دوزخ در نمى‌آمدند و حال آنكه همه- بتان و بت‌پرستان- در آنجا جاويدانند.'),
(2583, 21, 100, 'آنها در آنجا نعره‌اى- ناله‌هاى دردناك مانند بانگ خران- دارند و در آنجا سخنى نمى‌شنوند- كه بدان شاد شوند-.'),
(2584, 21, 101, 'همانا كسانى كه از پيش وعده نيكو- بهشت- به ايشان داده‌ايم، اينان از آن'),
(2585, 21, 102, 'آواى آن- آتش دوزخ- را هم نشنوند، و ايشان در آنچه دلهاشان بخواهد و آرزو كند جاويدانند.'),
(2586, 21, 103, 'آن بيم بزرگ ايشان را اندوهگين نكند، و فرشتگان به پيشبازشان آيند [و گويند:] اين همان روز شماست كه وعده داده مى‌شديد.'),
(2587, 21, 104, 'روزى كه آسمان را در نورديم مانند در نورديدن طومار مرنوشته‌ها را و چنانكه نخستين بار آفرينش آفريدگان را آغاز كرديم آن را بازگردانيم، وعده‌اى است بر ما و همانا ما كننده آنيم.'),
(2588, 21, 105, 'و هر آينه در زبور- كتاب داوود- پس از ذكر- تورات، يا پس از ياد اين امت در زبور- نوشتيم كه زمين را بندگان نيك و شايسته من به ميراث مى‌برند.'),
(2589, 21, 106, 'همانا در اين [كه ياد كرديم‌] آنچه مردم خداپرست را براى رسانيدن به مطلوب بسنده باشد، هست.'),
(2590, 21, 107, 'و ما تو را نفرستاديم مگر رحمتى براى جهانيان.'),
(2591, 21, 108, 'بگو: همانا به من وحى مى‌شود كه خداى شما خدايى يگانه است، پس آيا شما گردن نهاده و فرمانبردار خواهيد بود؟'),
(2592, 21, 109, 'پس اگر روى بگردانند بگو: من همه شما را يكسان آگاه كردم و هشدار دادم و نمى دانم كه آنچه وعده داده مى‌شويد- از عذاب و رستاخيز- نزديك است يا دور'),
(2593, 21, 110, 'همانا او گفتار آشكار و بلند را مى‌داند و مى‌داند آنچه را نهان مى‌داريد'),
(2594, 21, 111, 'و نمى‌دانم شايد اين [واپس داشتن عذاب و نشتابيدن بدان‌] شما را آزمونى باشد و برخورداريى تا هنگامى- مرگ يا هنگام فرارسيدن عذاب-.'),
(2595, 21, 112, '[پيامبر] گفت: پروردگارا، به حق حكم كن. و پروردگار ما خداى رحمان است كه در برابر آنچه [او را به ناسزا] وصف مى‌كنيد از او يارى خواسته مى شود.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'اى مردم، از پروردگارتان پروا كنيد، كه زلزله رستاخيز چيزى است بزرگ.'),
(2597, 22, 2, 'روزى كه آن را ببينيد، [از هول آن‌] هر شيردهنده‌اى آن را كه شير مى‌دهد از ياد ببرد و هر زن باردارى بار خويش بنهد، و مردم را مستان بينى- پندارى- و حال آنكه مست نيستند و ليكن عذاب خدا سخت است.'),
(2598, 22, 3, 'و از مردمان كس هست كه در باره خدا بى‌هيچ دانشى جدل و ستيزه مى‌كند و از هر شيطان نافرمان و سركشى پيروى مى‌نمايد.'),
(2599, 22, 4, 'بر وى'),
(2600, 22, 5, 'اى مردم، اگر از برانگيخته شدن پس از مرگ به شك اندريد، پس [بدانيد كه‌] ما شما را از خاك و سپس از نطفه و پس از آن از خونى بسته و آنگاه از پاره گوشتى، با آفرينشى تمام يا ناتمام، آفريديم تا [توانايى خويش را بر بازگرداندنتان‌] براى شما روشن و پديدار سازيم. و آنچه را بخواهيم تا مدتى معين- حدود نه ماه- در زهدانها نگاه مى‌داريم آنگاه شما را كودكى بيرون مى‌آوريم سپس [پرورشتان مى‌دهيم‌] تا به نيرومندى خويش برسيد. و از برخى جانشان [در خردى و جوانى‌] برگرفته شود و برخى تا فروترين دوره زندگى بازبرده شود تا [آنجا كه‌] در پى دانايى چيزى نداند. و زمين را [در زمستان‌] فسرده و مرده بينى، پس چون آب [باران‌] بر آنها فروآريم بجنبد و بردمد و از هر گونه گياه زيبا و بهجت‌انگيز بروياند.'),
(2601, 22, 6, 'اين- مراحل آفرينش آدمى و رويانيدن گياهان- از آن روست كه خدا حق است و اينكه مردگان را زنده مى‌كند و اينكه بر هر كارى تواناست،'),
(2602, 22, 7, 'و اينكه رستاخيز آمدنى است، هيچ شكى در آن نيست، و اينكه خداوند هر كه را در گورهاست برمى‌انگيزد.'),
(2603, 22, 8, 'و از مردمان كس هست كه بى‌هيچ دانشى و هدايتى و كتابى روشن در باره خدا جدل و ستيزه مى‌كند،'),
(2604, 22, 9, 'در حالى كه پهلوى خود را به نشانه تكبر [و روگرداندن از حق‌] مى‌پيچاند، تا [مردم را] از راه خدا گمراه كند. او را در اين جهان خوارى و رسوايى است و در روز رستاخيز عذاب آتش سوزان را به او مى‌چشانيم.'),
(2605, 22, 10, 'اين [خوارى و عذاب‌] به سزاى آن چيزى است كه دستهايت پيش فرستاده و از آن رو كه خدا بر بندگان ستمكار نيست.'),
(2606, 22, 11, 'و از مردمان كس هست كه خداى را يكسويه و با دودلى مى‌پرستد، پس اگر نيكيى بدو رسد بدان آرام گيرد و اگر آزمونى- رنجى و گزندى- بدو رسد روى بگرداند، در اين جهان و آن جهان زيانكار است، اين است آن زيانكارى آشكار.'),
(2607, 22, 12, 'جز خدا چيزى را مى‌خواند كه نه زيانش رساند و نه سودش دهد، اين است آن گمراهى دور.'),
(2608, 22, 13, 'كسى را مى‌خواند كه بى‌گمان زيانش نزديكتر از سود اوست، بد ياور و سرپرستى است- آن بت- و بد يار و همدمى است.'),
(2609, 22, 14, 'همانا خداوند كسانى را كه ايمان آورده‌اند و كارهاى نيك و شايسته كرده‌اند به بهشت‌هايى درآرد كه از زير آنها جويها روان است، كه خدا آنچه خواهد مى‌كند.'),
(2610, 22, 15, 'هر كه مى‌پندارد كه خدا او- پيامبر خود- را در اين جهان و آن جهان هرگز يارى نخواهد كرد- و اكنون يارى او را مى‌بيند و از اين رو به خشم آمده- بايد ريسمانى به آسمان- سقف و هر چه بر فراز سر آدمى است- كشد سپس خود را [با دار آويختن‌] خفه كند، و آنگاه بنگرد كه آيا اين ترفند و نيرنگش خشم او را از ميان مى‌برد؟'),
(2611, 22, 16, 'بدين‌سان اين [قرآن‌] را آياتى روشن فروفرستاديم و خداى هر كه را خواهد راه نمايد.'),
(2612, 22, 17, 'همانا كسانى كه ايمان آوردند- مسلمانان- و كسانى كه جهود شدند و صابئان و ترسايان و گبران و كسانى كه شرك آوردند، خداوند ميانشان روز رستاخيز داورى مى‌كند- يا جدايى مى‌افكند-، كه خداى بر هر چيزى گواه است.'),
(2613, 22, 18, 'آيا نديدى كه هر كه در آسمانها و هر كه در زمين است و خورشيد و ماه و ستارگان و كوه‌ها و درختان و جنبندگان و بسيارى از مردمان خداى را سجده مى‌كنند؟ و بسيارى هم عذاب بر آنان سزا گشته است، و هر كه را خدا خوار كند پس او را هيچ گرامى دارنده‌اى نيست، كه خدا هر چه خواهد مى‌كند.'),
(2614, 22, 19, 'اينها- مؤمنان و كافران- دو گروه دشمن يكديگرند كه در باره [هستى و يگانگى‌] پروردگارشان با هم ستيزه كردند پس كسانى كه كافر شدند برايشان جامه‌هايى از آتش بريده‌اند [و] از بالاى سرشان آب جوشان مى‌ريزند'),
(2615, 22, 20, 'كه با آن'),
(2616, 22, 21, 'و آنان راست گرزهاى آهنين'),
(2617, 22, 22, 'هر گاه از سختى اندوه بخواهند از آن'),
(2618, 22, 23, 'همانا خداوند كسانى را كه ايمان آوردند و كارهاى نيك و شايسته كردند به بهشت‌هايى در آورد كه از زير آنها جويها روان است، در آنجا با دستبندهاى زرين و مرواريد آراسته شوند، و جامه‌شان در آنجا ديباست'),
(2619, 22, 24, 'و به گفتار پاك- لا اله الا الله يا سپاس و ستايش خداوند- راه نموده شوند و به راه راست [خداى‌] ستوده راهنمايى شوند.'),
(2620, 22, 25, 'كسانى كه كفر ورزيدند و [مردم را] از راه خدا و مسجد الحرام- كه آن را براى مردم قرار داديم و مقيم و مسافر در آن يكسانند- باز مى‌دارند، و نيز هر كه در آنجا كجروى و ستمكارى- آزار مردم- خواهد او را از عذابى دردناك بچشانيم.'),
(2621, 22, 26, 'و [ياد كن‌] آنگاه كه جاى آن خانه را براى ابراهيم جايگاه [عبادت‌] ساختيم، [و گفتيم‌] كه هيچ چيز را با من انباز مگير و خانه مرا براى طواف‌كنندگان و بر پاايستادگان و ركوع‌كنان و سجده آرندگان- نمازگزاران- پاك دار.'),
(2622, 22, 27, 'و در ميان مردمان به حج آواز ده تا پياده و سوار بر شتران باريك ميان سبك‌رو از راه‌هاى دور سوى تو آيند'),
(2623, 22, 28, 'تا نزد سودهايى كه ايشان راست حاضر آيند و خداى را در روزهايى دانسته- ايام نحر و تشريق يا دهه اول ذو الحجه- بر [ذبح‌] آنچه از چهارپايان زبان بسته روزيشان كرده ياد كنند. پس، از آن بخوريد و به درمانده درويش هم بخورانيد.'),
(2624, 22, 29, 'سپس بايد چرك از خود بزدايند و نذرهاى خويش- كه تعهد كرده‌اند- به جاى آرند و بر آن خانه كهن طواف كنند.'),
(2625, 22, 30, 'اين است [احكام حج‌] و هر كه حرمتهاى خداى- يعنى مناسك حج و عمره- را بزرگ شمارد، براى او نزد پروردگارش بهتر است. و چهارپايان برايتان حلال شد مگر آنچه بر شما خوانده مى‌شود. پس، از بتهاى پليد بپرهيزيد و از گفتار دروغ- يا گواهى دروغ- دورى كنيد،'),
(2626, 22, 31, 'در حالى كه خداى را راست‌دينان با اخلاص باشيد نه انبازگيرندگان به او، و هر كه براى خدا انباز گيرد چنان است كه از آسمان فرو افتد پس مرغان او را بربايند يا باد او را در جايى دور اندازد.'),
(2627, 22, 32, 'اين است [احكام و مراسم حج‌]، و هر كه نشانه‌هاى عبادت خداى- يعنى مناسك حج- را بزرگ شمارد، همانا اين بزرگداشت از پرهيزگارى دلهاست.'),
(2628, 22, 33, 'شما را در آنها- چهارپايان- سودهاست تا مدتى نامبرده- وقت قربانى- سپس وقت قربانى آنها هنگام رسيدن به آن خانه كهن است.'),
(2629, 22, 34, 'و براى هر امتى آيين عبادتى [در قربانى كردن‌] قرار داديم تا نام خداى را بر [ذبح‌] آنچه از چهارپايان زبان‌بسته روزيشان كرده است ياد كنند. خداى شما خداى يگانه است، پس او را گردن نهيد، و فروتنان پاكدل را مژده ده،'),
(2630, 22, 35, 'آنان كه چون خدا ياد كرده شود دلهاشان بترسد و آنان كه بر آنچه بديشان رسد شكيبا باشند و برپادارندگان نماز و آنان كه از آنچه روزيشان داده‌ايم انفاق مى‌كنند.'),
(2631, 22, 36, 'و شتران قربانى را براى شما از نشانه‌هاى عبادت خدا قرار داديم، شما را در آن خيرى [و ثوابى‌] است. پس نام خداى را بر آنها [كه نحر مى‌كنيد] در حالى كه ايستاده‌اند ياد كنيد، و چون پهلوشان بر زمين افتد- جان دهند- از آنها بخوريد و به نيازمند خرسند و بينواى خواهنده بخورانيد. اينچنين آنها را براى شما رام كرديم، باشد كه سپاس داريد.'),
(2632, 22, 37, 'گوشت و خون آنها- قربانيها- هرگز به خدا نمى‌رسد و ليكن پرهيزگارى شما به او مى‌رسد. بدين گونه آنها- چهارپايان- را براى شما رام كرد تا خداى را به پاس آنكه شما را راه نمود به بزرگى ياد كنيد. و نيكوكاران را مژده ده.'),
(2633, 22, 38, 'همانا خداوند از كسانى كه ايمان آورده‌اند دفاع مى‌كند، كه خدا هيچ خيانت‌پيشه ناسپاس را دوست ندارد.'),
(2634, 22, 39, 'به كسانى كه با آنان كارزار كرده‌اند، از آن رو كه ستم ديده‌اند رخصت كارزار داده شد و هر آينه خداوند بر يارى ايشان تواناست،'),
(2635, 22, 40, 'آنان كه به ناروا از ديار خويش رانده شدند تنها بدين سبب كه گفتند: پروردگار ما خداى يكتاست. و اگر خداوند برخى از مردم را به برخى باز نمى‌داشت هر آينه ديرها [ى راهبان‌] و كليساها [ى ترسايان‌] و كنشتها [ى جهودان‌] و مسجدها [ى مسلمانان‌] كه در آنها نام خدا بسيار ياد مى‌شود ويران مى گرديد و البته خدا كسى را كه [دين‌] او را يارى كند يارى خواهد كرد، كه خدا هر آينه نيرومند و تواناى بى‌همتاست،'),
(2636, 22, 41, 'همان كسان كه اگر ايشان را در زمين جايگاه و قدرت دهيم نماز را برپا دارند و زكات دهند و به كار نيك و پسنديده فرمان دهند و از كار زشت و ناپسند بازدارند و خداى راست فرجام كارها.'),
(2637, 22, 42, 'و اگر تو را تكذيب كنند، پيش از آنها قوم نوح و عاد و ثمود [پيامبرانشان را] تكذيب كردند،'),
(2638, 22, 43, 'و قوم ابراهيم و قوم لوط،'),
(2639, 22, 44, 'و مردم مدين نيز و موسى نيز تكذيب شد، و كافران را مهلت دادم آنگاه [به كيفر] بگرفتمشان، پس [بنگر كه‌] انكار- ناپسنديدن و كيفر- من چگونه بود.'),
(2640, 22, 45, 'چه بسا شهرها و آباديها را كه [اهل آنها] ستمكار بودند نابود كرديم، پس [ديوارهاى‌] آن آباديها بر سقفهايش فروافتاده و بسا چاه رها كرده و كوشكهاى بلند گچكارى شده [بى‌صاحب‌] بازمانده است.'),
(2641, 22, 46, 'آيا در زمين به گردش و تماشا نرفتند تا دلهايى داشته باشند كه با آن [به هشيارى پند را] دريابند يا گوشهايى كه با آن [اندرز را] بشنوند؟! زيرا چشمها [ى سر] نابينا نيست بلكه [چشم‌] دلهايى كه در سينه‌هاست كور و نابيناست.'),
(2642, 22, 47, 'و عذاب را به شتاب از تو مى‌خواهند، و خدا هرگز وعده خود را خلاف نكند و همانا يك روز نزد پروردگار تو چون هزار سال است از آنچه مى‌شماريد.'),
(2643, 22, 48, 'و چه بسيار [اهل‌] شهرها و آباديها را كه مهلت دادم، در حالى كه ستمكار بودند، آنگاه [در دنيا به كيفر] بگرفتمشان و بازگشت به سوى من است.'),
(2644, 22, 49, 'بگو: اى مردم، همانا من شما را بيم‌كننده‌اى آشكارم.'),
(2645, 22, 50, 'پس كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند ايشان راست آمرزش و روزىِ بزرگوارانه.'),
(2646, 22, 51, 'و كسانى كه در باره آيات ما كوشيده‌اند كه [با انكار آنها] ما را عاجز كنند، آنان ياران دوزخند.'),
(2647, 22, 52, 'و پيش از تو هيچ فرستاده و پيامبرى نفرستاديم مگر آنكه چون [آيات را] برمى‌خواند- يا آرزوى گرويدن مردم و كاميابى در رسالت خويش مى‌داشت- شيطان در خواندن او- يا در آرزوى او- چيزى مى‌افكند پس خدا آنچه را شيطان بيفكند از ميان مى‌برد و آنگاه آيات خويش را استوار مى‌كند، و خدا دانا و استواركار است،'),
(2648, 22, 53, 'تا آنچه را شيطان افكند براى كسانى كه در دلهاشان بيمارى است و نيز سنگدلان آزمونى سازد، و هر آينه ستمكاران در ستيز و مخالفتى دور [از حق‌] اند'),
(2649, 22, 54, 'و تا آنان كه دانش داده شده‌اند بدانند كه آن- آنچه پيامبر قرائت يا آرزو كرده- راست است از پروردگار تو و بدان ايمان آورند و دلهاشان براى آن نرم گردد و آرام گيرد، و هر آينه خدا كسانى را كه ايمان آورده‌اند به راه راست رهنمون است.'),
(2650, 22, 55, 'و كسانى كه كافر شدند هميشه از آن در شك و گمانند تا آنگاه كه رستاخيز ناگهان بديشان فرا رسد يا عذاب روزى نازا به آنها رسد'),
(2651, 22, 56, 'پادشاهى در آن روز خداى راست، ميانشان حكم مى‌كند پس كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند در بهشت‌هاى پُر نعمتند.'),
(2652, 22, 57, 'و كسانى كه كافر شدند و آيات ما را دروغ انگاشتند آنان را عذابى است خواركننده.'),
(2653, 22, 58, 'و كسانى كه در راه خدا هجرت كردند سپس كشته شدند يا مردند، هر آينه خداوند ايشان را روزىِ نيكو دهد و خدا بهترين روزى‌دهندگان است.'),
(2654, 22, 59, 'هر آينه آنان را به جايى در آورد كه مى‌پسندند، و همانا خدا دانا و بردبار است- به عقوبت شتاب نمى‌كند-.'),
(2655, 22, 60, 'اين است [حكم خدا در باره كافر و مؤمن‌]، و كسى كه عقوبت كند بمانند آنكه او را عقوبت كرده‌اند، و پس از آن باز بر او ستم كنند هر آينه خدا يارى‌اش مى‌كند، [و اگر دست از تجاوز برداشتند] همانا خدا بخشنده و آمرزگار است.'),
(2656, 22, 61, 'اين [يارى كردن مظلوم و پيروزى او بر ستمگر] از آن روست كه خدا [قادر است كه چيزى را بر چيزى غالب گرداند چنانكه‌] شب را در روز درمى‌آورد و روز را در شب درمى‌آورد- با افزودن و كاستن- و اينكه خدا شنوا و بيناست.'),
(2657, 22, 62, 'اين از آن روست كه خدا حق است و آنچه جز او به خدايى مى‌خوانند باطل است و خداست والا و بزرگ.'),
(2658, 22, 63, 'آيا نديده‌اى كه خداى از آسمان آبى فرو فرستاد پس زمين سرسبز مى‌گردد؟ همانا خدا لطف‌كننده- يا باريكدان- و آگاه است.'),
(2659, 22, 64, 'او راست آنچه در آسمانها و آنچه در زمين است و همانا خداست بى‌نياز و ستوده.'),
(2660, 22, 65, 'آيا نديده‌اى كه خداى آنچه را در زمين است براى شما رام كرد و كشتيها در دريا به فرمان او مى‌روند، و آسمان را نگاه مى‌دارد تا بر زمين نيفتد مگر به فرمان او هر آينه خدا به مردم رؤوف و مهربان است.'),
(2661, 22, 66, 'و اوست آن كه شما را زنده كرد، سپس بميراند و پس از آن زنده كند. همانا آدمى بسى ناسپاس است.'),
(2662, 22, 67, 'براى هر امتى شريعتى- آيين يا راه و روشى براى عبادت يا پرستشگاهى- نهاده‌ايم كه بر آن باشند. پس بايد كه در اين كار با تو جدل و ستيزه نكنند، و به سوى پروردگارت بخوان، كه تو براستى بر رهنمونى [راه‌] راستى.'),
(2663, 22, 68, 'و اگر با تو جدل و ستيزه‌كنند بگو: خداى بدانچه مى‌كنيد داناتر است.'),
(2664, 22, 69, 'خداى در روز رستاخيز ميان شما در آنچه اختلاف مى‌كرديد داورى خواهد كرد.'),
(2665, 22, 70, 'آيا ندانسته‌اى كه خداى آنچه را در آسمانها و زمين است مى‌داند؟ و اين در كتابى- لوح محفوظ- ثبت است، همانا اين- ثبت در كتاب- بر خدا آسان است.'),
(2666, 22, 71, 'و بجز خداى يكتا چيزى را مى‌پرستند كه [خدا] هيچ حجتى به [پرستيدن‌] آن فرونفرستاده و هيچ دانشى هم بدان ندارند و ستمكاران را هيچ ياورى نيست.'),
(2667, 22, 72, 'و چون آيات روشن ما بر آنان خوانده شود در چهره كسانى كه كافرند نشانه ترشرويى و ناخوشى را مى‌شناسى نزديك است به كسانى كه آيات ما را بر آنان مى‌خوانند بتازند بگو: آيا شما را به بدتر از اين آگاه كنم؟ آتش، كه خدا آن را به كسانى كه كافر شدند وعده داده است و بد بازگشتگاهى است.'),
(2668, 22, 73, 'اى مردم، مَثَلى زده شده- در باره مشركان و پرستش بتها- آن را گوش فرا داريد: كسانى كه به جاى خداى يكتا مى‌خوانيد مگسى را نتوانند آفريد گر چه همگى براى اين كار گرد آيند و اگر مگس چيزى از آنها بربايد بازستاندن آن نتوانند طالب و مطلوب- خواستار و خواسته يعنى پرستنده و پرستيده يا بت و مگس رباينده- هر دو بيچاره و ناتوانند.'),
(2669, 22, 74, 'خداى را چنانكه سزاوار شناخت و بزرگداشت اوست نشناختند و بزرگ نداشتند. هر آينه خداوند نيرومند و تواناى بى‌همتاست.'),
(2670, 22, 75, 'خدا از فرشتگان و از مردمان فرستادگانى برمى‌گزيند همانا خدا شنوا و بيناست'),
(2671, 22, 76, 'آنچه را پيش روى ايشان است و آنچه را از پس ايشان است مى‌داند، و كارها به خدا بازگردانده مى‌شود.'),
(2672, 22, 77, 'اى كسانى كه ايمان آورده‌ايد، ركوع كنيد و سجده آريد و پروردگارتان را بپرستيد و كار نيكو كنيد، باشد كه رستگار شويد'),
(2673, 22, 78, 'و در راه خدا جهاد كنيد چنانكه سزاوار جهاد اوست، او شما را [بر همه امتها] برگزيده است، و بر شما در كار دين هيچ تنگى و دشوارى ننهاده است اين همان آيين پدرتان ابراهيم است- يا آيين پدرتان ابراهيم را پيروى كنيد- او');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'براستى كه مؤمنان رستگار شدند،'),
(2675, 23, 2, 'آنان كه در نمازشان ترسان و فروتن‌اند،'),
(2676, 23, 3, 'و آنان كه از بيهوده رويگردانند،'),
(2677, 23, 4, 'و آنان كه زكات [مال خويش‌] را دهنده‌اند،'),
(2678, 23, 5, 'و آنان كه شرمگاه خود را [از حرام‌] نگهدارنده‌اند،'),
(2679, 23, 6, 'مگر بر همسران يا كنيزان خويش، كه [براى آميزش با آنان‌] سرزنش نشوند،'),
(2680, 23, 7, 'پس هر كه افزون از اين [كه ياد كرديم‌] بجويد، اينان از حد درگذرنده‌اند،'),
(2681, 23, 8, 'و آنان كه امانتها و پيمان خويش را نگهدارنده‌اند،'),
(2682, 23, 9, 'و آنان كه بر نمازهاى خويش نگهبانند- مواظبت مى‌كنند-،'),
(2683, 23, 10, 'ايشانند ميراث‌بران،'),
(2684, 23, 11, 'كه بهشت برين را به ميراث برند و در آن جاويدانند.'),
(2685, 23, 12, 'و هر آينه ما آدمى را از خلاصه و چكيده‌اى از گِل آفريديم.'),
(2686, 23, 13, 'سپس او را نطفه‌اى- در قطره آبى- ساختيم در قرارگاهى استوار- يعنى زهدان مادر-.'),
(2687, 23, 14, 'آنگاه نطفه را خون بسته‌اى ساختيم و آن خون بسته را پاره گوشتى كرديم و آن پاره گوشت را استخوانها گردانيديم، و بر آن استخوانها گوشت پوشانديم سپس او را به آفرينشى ديگر باز آفريديم. پس بزرگ و بزرگوار است خداى يكتا كه نيكوترين آفرينندگان است.'),
(2688, 23, 15, 'سپس شما از پس آن هر آينه مردگانيد.'),
(2689, 23, 16, 'سپس روز رستاخيز برانگيخته مى‌شويد.'),
(2690, 23, 17, 'و هر آينه بر فرازتان هفت راه- هفت آسمان- بيافريديم و ما از آفريدگان ناآگاه نيستيم.'),
(2691, 23, 18, 'و از آسمان آبى به اندازه فروفرستاديم، پس آن را در زمين- در چشمه‌ها و منبعها- جاى داديم، و ما بر بردن آن تواناييم.'),
(2692, 23, 19, 'پس براى شما بدان آب بوستانهايى از درختان خرما و انگور بيافريديم شما را در آن بوستانها ميوه‌هاى بسيار است و از آن ميوه‌ها مى‌خوريد.'),
(2693, 23, 20, 'و درختى [بيافريديم‌] كه از كوه سينا بيرون مى‌آيد- يعنى درخت زيتون- كه روغن مى‌آورد و نان خورشى براى خورندگان است.'),
(2694, 23, 21, 'و هر آينه شما را در چهارپايان عبرتى است. از آنچه در شكمشان است- يعنى شير- شما را مى‌نوشانيم و براى شما در آنها سودهاى بسيار است و از [گوشت‌] آنها مى‌خوريد'),
(2695, 23, 22, 'و بر آنها و بر كشتيها سوار مى‌شويد.'),
(2696, 23, 23, 'و هر آينه نوح را به سوى قومش فرستاديم، پس گفت: اى قوم من، خداى يكتا را بپرستيد، شما را جز او خدايى نيست، آيا پروا نمى‌كنيد؟'),
(2697, 23, 24, 'مهتران قومش كه كافر بودند گفتند: اين جز آدميى همچون شما نيست كه مى‌خواهد بر شما فزونى و برترى جويد و اگر خداى مى‌خواست فرشتگانى را فرو مى‌فرستاد، ما اين را [كه نوح مى‌گويد] در ميان پدران پيشين خود نشنيده‌ايم'),
(2698, 23, 25, 'او نيست مگر مردى كه او را ديوانگى است، پس او را تا هنگامى انتظار بريد- شايد از ديوانگى باز آيد و چنين سخنانى نگويد، يا بزودى بميرد و از وى باز رهيم-.'),
(2699, 23, 26, 'گفت: پروردگارا، در برابر آنكه مرا تكذيب كردند يارى‌ام كن.'),
(2700, 23, 27, 'پس به او وحى كرديم كه كشتى را زير نظر ما و پيام- آموزش- ما بساز، و چون فرمان ما [به عذاب‌] بيايد و [آب از] آن تنور بجوشد از هر گونه [حيوان‌] دو- نرى و ماده‌اى- در آن [كشتى‌] در آر و نيز خاندان خود را، مگر آن كه سخن [به هلاكت وى‌] از پيش بر او رفته است- پسر و همسرت-، و در باره كسانى كه ستم كردند با من سخن مگوى كه آنها غرق شدنى‌اند.'),
(2701, 23, 28, 'پس چون تو و همراهانت بر كشتى آرام گرفتيد، بگو: سپاس و ستايش خداى را كه ما را از اين قوم ستمكار رهايى داد.'),
(2702, 23, 29, 'و بگو: پروردگارا، مرا در جايى با بركت فرود آر و تو بهترين فرودآرندگانى.'),
(2703, 23, 30, 'همانا در اين [داستان‌] نشانه‌ها و عبرتهاست و براستى ما آزمون كننده بوديم.'),
(2704, 23, 31, 'سپس از پى آنان مردمى ديگر- قوم عاد يا ثمود- آفريديم،'),
(2705, 23, 32, 'و در ميان آنان از خودشان پيامبرى- هود يا صالح- فرستاديم كه: خداى را بپرستيد، شما را هيچ خدايى جز او نيست. آيا پروا نمى‌كنيد؟'),
(2706, 23, 33, 'و مهتران قومش كه كافر بودند و ديدار آن جهان- يعنى رستاخيز- را باور نداشتند و آنان را در زندگى اين جهان كامرانى داده بوديم، گفتند: اين [مرد] نيست مگر آدميى همچون شما، از آنچه شما مى‌خوريد مى‌خورد و از آنچه مى‌آشاميد مى‌آشامد.'),
(2707, 23, 34, 'و اگر از انسانى همانند خود فرمان بريد آنگاه بى‌گمان از زيانكاران باشيد.'),
(2708, 23, 35, 'آيا شما را وعده مى‌دهد كه چون مرديد و خاك و استخوان شديد، از [گور] بيرون آورده مى‌شويد؟'),
(2709, 23, 36, 'چه دور است، دور، آنچه وعده داده مى‌شويد!'),
(2710, 23, 37, 'زندگانى جز همين زندگى دنيا نيست، مى‌ميريم و مى‌زييم- نسل بعد از نسل- و ما [هرگز پس از مرگ‌] برانگيخته نمى‌شويم.'),
(2711, 23, 38, 'او نيست مگر مردى كه بر خدا دروغ مى‌بافد و ما به او ايمان‌آور نيستيم.'),
(2712, 23, 39, 'گفت: پروردگارا، در برابر آنكه مرا تكذيب كردند يارى‌ام كن.'),
(2713, 23, 40, 'گفت: پس از اندك زمانى هر آينه پشيمان مى‌گردند.'),
(2714, 23, 41, 'پس بانگ آسمانى- عذاب- به حق- وعده يا فرمان راستين خداوند- آنان را بگرفت، و همچون خاشاكشان كرديم پس دورى [و هلاكت‌] باد مردم ستمكار را.'),
(2715, 23, 42, 'سپس از پى آنان نسلهايى ديگر بيافريديم.'),
(2716, 23, 43, 'هيچ امتى از سرآمد خويش نه پيشى گيرد و نه واپس رود.'),
(2717, 23, 44, 'سپس فرستادگان خود را پى در پى فرستاديم، هر گاه به امتى پيامبرشان آمد او را دروغگو شمردند، پس آنان را از پى يكديگر [به هلاكت‌] رسانديم و آنها را داستانها كرديم- تا مردمان عبرت گيرند-. پس دورى [و هلاكت‌] باد بر مردمى كه ايمان نمى‌آورند.'),
(2718, 23, 45, 'سپس موسى و برادرش هارون را با نشانه‌هاى خود و حجتى روشن فرستاديم،'),
(2719, 23, 46, 'به سوى فرعون و مهتران [قوم‌] او، پس گردنكشى كردند و گروهى برترى‌جوى بودند'),
(2720, 23, 47, 'گفتند: آيا به دو آدمى مانند خود ايمان بياوريم و حال آنكه قوم آن دو ما را بندگان- خدمتكاران و فرمانبران- اند.'),
(2721, 23, 48, 'پس آن دو را تكذيب كردند و از هلاك‌شدگان گشتند.'),
(2722, 23, 49, 'و هر آينه موسى را كتاب داديم تا شايد راه يابند.'),
(2723, 23, 50, 'و پسر مريم و مادرش را نشانه‌اى [بر قدرت كامل و شگفت‌آورمان‌] كرديم، و آن دو را در جايى بلند كه آرامشگاهى و آبى خوش و روان داشت جاى داديم.'),
(2724, 23, 51, 'اى پيامبران، از چيزهاى پاكيزه بخوريد و كار نيك و شايسته كنيد، كه من به آنچه مى‌كنيد دانايم.'),
(2725, 23, 52, 'و همانا اين آيين- يا امت- شماست آيينى- يا امتى- يگانه و من پروردگار شمايم پس، از من پروا كنيد.'),
(2726, 23, 53, 'ولى كار [دين‌] خويش ميان خود پاره پاره كردند. هر گروهى به آنچه نزدشان است دلخوشند.'),
(2727, 23, 54, 'پس تا چندى آنها را در گرداب غفلت [و نادانى و گمراهى‌] شان واگذار.'),
(2728, 23, 55, 'آيا مى‌پندارند كه آنچه از مال و فرزندان به آنان مى‌دهيم،'),
(2729, 23, 56, 'براى آنها در [رساندن‌] نيكيها شتاب مى‌كنيم؟ [نه،] بلكه نمى‌فهمند.'),
(2730, 23, 57, 'همانا آنان كه از بيم پروردگارشان ترسانند،'),
(2731, 23, 58, 'و آنان كه به آيات پروردگار خويش ايمان مى‌آورند،'),
(2732, 23, 59, 'و آنان كه با پروردگار خود انباز نمى‌گيرند،'),
(2733, 23, 60, 'و آنان كه مى‌دهند آنچه مى‌دهند- از زكات و ديگر انفاقها- و با اين حال دلهاشان ترسان است از اينكه به پروردگار خويش بازمى‌گردند،'),
(2734, 23, 61, 'ايشانند كه در نيكيها مى‌شتابند و به آن [بر يكديگر] پيشى مى‌گيرند.'),
(2735, 23, 62, 'و ما بر هيچ كس جز به اندازه توانش تكليف نمى‌نهيم. و نزد ما كتابى است كه براستى و درستى [در باره اعمال بندگان‌] سخن مى‌گويد- گواهى مى‌دهد- و بر آنها ستم نمى‌رود.'),
(2736, 23, 63, 'بلكه دلهاشان از اين- فهم قرآن- در گرداب غفلت- و جهل و سرگردانى و گمراهى- است، و آنان را كارهايى است جز اين- مانند دنيادوستى و حرص بر آن- كه همى‌كنند.'),
(2737, 23, 64, 'تا چون ناز و نعمت پروردگانشان را به عذاب بگيريم ناگهان فرياد زارى برآرند.'),
(2738, 23, 65, 'امروز فرياد زارى برنياريد كه از سوى ما يارى نخواهيد شد.'),
(2739, 23, 66, 'همانا آيات من بر شما خوانده مى‌شد و شما بر پاشنه‌هاى خود مى‌گشتيد و واپس مى‌رفتيد،'),
(2740, 23, 67, '[و] با گردنكشى و بزرگ‌منشى بر آن- قرآن- در گفت‌وگوهاى شب‌نشينى ياوه‌سرايى مى‌كرديد.'),
(2741, 23, 68, 'آيا در اين گفتار- قرآن- نينديشيده‌اند يا مگر چيزى بديشان آمده كه به پدران پيشينشان نيامده بود؟!'),
(2742, 23, 69, 'يا پيامبر خويش را نشناخته‌اند كه او را منكرند.'),
(2743, 23, 70, 'يا مى‌گويند او را ديوانگى است؟ [نه،] بلكه به حق- با سخن راست يعنى قرآن يا دين درست يعنى اسلام- بديشان آمده، و بيشترشان حق را خوش ندارند.'),
(2744, 23, 71, 'و اگر حق از هوسها و آرزوهاى آنان پيروى مى‌كرد هر آينه آسمانها و زمين و هر كه در آنهاست تباه مى‌شد، ولى ما يادكرد و پندشان- قرآن- را بديشان آورديم و آنها از ياد و پند خويش رويگردانند.'),
(2745, 23, 72, 'يا مگر از آنان مزدى مى‌خواهى؟! و حال آنكه مزد- خير و پاداش و روزى- پروردگارت بهتر است و او بهترين روزى‌دهندگان است.'),
(2746, 23, 73, 'و هر آينه تو آنان را به راه راست مى‌خوانى.'),
(2747, 23, 74, 'و آنان كه به جهان واپسين ايمان نمى‌آورند همانا از راه راست بگرديده‌اند.'),
(2748, 23, 75, 'و اگر بر آنها مِهر آوريم و گزندى را كه به آنها رسيده برداريم هر آينه سرگشته و كوردل در سركشى خويش بستيزند.'),
(2749, 23, 76, 'و هر آينه به عذاب بگرفتيمشان، اما پروردگارشان را فروتنى نكردند و زارى ننمودند'),
(2750, 23, 77, 'تا هنگامى كه درى از عذاب سخت بر آنها بگشوديم، آنگاه در آن'),
(2751, 23, 78, 'و اوست كه براى شما گوش و ديدگان و دلها بيافريد، [و شما] اندكى سپاس مى‌گزاريد.'),
(2752, 23, 79, 'و اوست كه شما را در زمين پديد كرد و [سرانجام‌] به سوى او برانگيخته و فراهم مى‌شويد.'),
(2753, 23, 80, 'و اوست كه زنده كند و بميراند، و او راست آمد و شد شب و روز آيا خرد را كار نمى‌بنديد؟!'),
(2754, 23, 81, 'بلكه همان گفتند كه پيشينيان گفتند'),
(2755, 23, 82, 'گفتند: آيا چون بمرديم و خاك و استخوان شديم باز هم برانگيخته مى‌شويم؟!'),
(2756, 23, 83, 'هر آينه به ما و پدرانمان از پيش اين وعده داده بودند، اين نيست مگر افسانه‌هاى پيشينيان.'),
(2757, 23, 84, 'بگو: زمين و هر كه [و هر چه‌] در آن است از آن كيست، اگر مى‌دانيد؟'),
(2758, 23, 85, 'خواهند گفت: خداى راست، بگو: پس آيا ياد نمى‌كنيد و پند نمى‌گيريد؟!'),
(2759, 23, 86, 'بگو: پروردگار آسمانهاى هفتگانه و پروردگار عرش بزرگ كيست؟'),
(2760, 23, 87, 'خواهند گفت: خداى راست بگو: پس آيا [از عذاب او] پروا نمى‌كنيد؟!'),
(2761, 23, 88, 'بگو: اگر مى‌دانيد، كيست كه فرمانروايى هر چيز به دست اوست و او [از عذاب‌] زنهار مى‌دهد و بر او زنهار ندهند- كسى نمى‌تواند كسى را از عذاب او پناه دهد-.'),
(2762, 23, 89, 'خواهند گفت: خداى راست بگو: پس چگونه فريب داده مى‌شويد؟'),
(2763, 23, 90, 'بلكه حق- سخن راست- را بديشان آورديم و هر آينه آنان دروغگويند.'),
(2764, 23, 91, 'خداى هيچ فرزندى نگرفته و با او هيچ خدايى نيست، كه آنگاه هر خدايى آنچه آفريده بود مى‌برد- ويژه خود مى‌كرد- و البته برخى بر برخى برترى مى‌يافت، پاك و منزه است خداى يكتا از آنچه وصف مى‌كنند،'),
(2765, 23, 92, 'داناى نهان و آشكارا، پس برتر است از آنچه [با وى‌] انباز مى‌گيرند.'),
(2766, 23, 93, 'بگو: پروردگارا، اگر آن [عذاب‌] كه به آنان وعده داده مى‌شود به من مى‌نمايى،'),
(2767, 23, 94, 'پس مرا، پروردگارا، در گروه ستمكاران قرار مده- مرا قرين آنان در عذاب مساز-.'),
(2768, 23, 95, 'و هر آينه ما تواناييم بر اين كه آنچه را به آنان وعده مى‌دهيم به تو بنماييم.'),
(2769, 23, 96, 'بدى- آزار و جفاى آنان- را به آنچه نيكوتر است پاسخ ده ما به آنچه وصف مى‌كنند- تو را به شعر و سحر يا مرا به گرفتن فرزند و شريك- داناتريم.'),
(2770, 23, 97, 'و بگو: پروردگارا، از وسوسه‌هاى شياطين به تو پناه مى‌آرم'),
(2771, 23, 98, 'و به تو پناه مى‌آرم، پروردگارا، از اينكه پيرامون من بگردند.'),
(2772, 23, 99, 'تا چون يكى از آنان- آن كافران- را مرگ فرا رسد گويد: پروردگارا، مرا بازگردانيد،'),
(2773, 23, 100, 'شايد در آنچه واگذاشته‌ام كار نيك و شايسته كنم. نه چنين است، اين سخنى است كه او گوينده آن است، و فرارويشان برزخى است تا روزى كه برانگيخته شوند.'),
(2774, 23, 101, 'پس چون در صور دميده شود، آن روز نسبت و خويشاوندى ميانشان نماند، و از حال يكديگر نپرسند- يا از يكديگر درخواست نتوانند كرد-.'),
(2775, 23, 102, 'پس هر كه ترازوى او سنگين باشد- به داشتن كار نيك- آنانند رستگاران.'),
(2776, 23, 103, 'و هر كه ترازوى او سبك باشد- از آن رو كه كارهاى نيك نكرده است- آنانند كه خويشتن را زيان كردند- زيرا سرمايه عمر را به باد غفلت دادند-، [و] در دوزخ جاويدانند'),
(2777, 23, 104, 'آتش چهره‌هايشان را مى‌سوزاند و آنان در آنجا زشترويند.'),
(2778, 23, 105, 'آيا آيات من بر شما خوانده نمى‌شد و شما آنها را دروغ مى‌انگاشتيد؟'),
(2779, 23, 106, 'گويند: پروردگارا، بدبختى ما بر ما چيره شد و ما گروهى گمراه بوديم.'),
(2780, 23, 107, 'پروردگارا، ما را از اينجا بيرون بر، اگر [به كفر و گناه‌] بازگشتيم پس همانا ستمكار باشيم.'),
(2781, 23, 108, '[خداى‌] گويد: در آن'),
(2782, 23, 109, 'زيرا گروهى از بندگان من مى‌گفتند: پروردگارا، ما ايمان آورديم، ما را بيامرز و بر ما ببخشاى و تو بهترين بخشايندگانى،'),
(2783, 23, 110, 'پس شما مسخره‌شان كرديد تا يادكرد مرا از يادتان بردند، و شما به آنان مى‌خنديديد'),
(2784, 23, 111, 'امروز من ايشان را از آن رو كه شكيبايى كردند پاداش مى‌دهم، همانا ايشانند رستگاران.'),
(2785, 23, 112, '[به آنان‌] گويد: به شمار سالها چه مدت در زمين- در دنيا- درنگ كرديد؟'),
(2786, 23, 113, 'گويند: روزى يا برخى از روزى [و ما بخوبى نمى‌دانيم‌] پس شمارندگان را بپرس.'),
(2787, 23, 114, 'گويد: اگر مى‌دانستيد، جز اندكى درنگ نكرديد.'),
(2788, 23, 115, 'آيا پنداشته‌ايد كه شما را بيهوده آفريديم و شما به سوى ما بازگردانده نمى‌شويد؟'),
(2789, 23, 116, 'پس برتر است خداى، آن پادشاه راستين، [از اينكه كارى بيهوده كند] خدايى جز او نيست، خداوند عرش گرامى.'),
(2790, 23, 117, 'و هر كه با خداى، خداى ديگر بخواند- يعنى بپرستد- كه او را بدان حجت نيست، پس حساب او نزد پروردگارش است همانا كافران رستگارى نمى‌يابند.'),
(2791, 23, 118, 'و بگو [اى محمد]: پروردگارا، بيامرز و ببخشاى، و تو بهترين بخشايندگانى.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'سوره‌اى است كه آن را فرو فرستاده‌ايم و [احكام‌] آن را واجب كرده‌ايم، و در آن آيتهاى روشن فرو فرستاده‌ايم تا مگر يادآور شويد و پند گيريد.'),
(2793, 24, 2, 'زن و مرد زناكار را هر يك صد تازيانه بزنيد و اگر به خدا و روز واپسين ايمان داريد، مبادا شما را در دين خدا، در باره آن دو، مهربانى و دلسوزى بگيرد- كه از اجراى حكم باز دارد-. و بايد كه گروهى از مؤمنان شكنجه آن دو را حاضر و گواه باشند.'),
(2794, 24, 3, 'مرد زناكار جز زن زناكار يا مشرك را به زنى نمى‌گيرد، و زن زناكار را جز مرد زناكار يا مشرك به زنى نمى‌گيرد و اين [زناشويى‌] بر مؤمنان حرام است.'),
(2795, 24, 4, 'و كسانى كه زنان پاكدامن را به زنا نسبت دهند آنگاه چهار گواه نياورند پس هشتاد تازيانه بزنيدشان و هرگز گواهى آنها را نپذيريد، و آنانند بدكاران نافرمان'),
(2796, 24, 5, 'مگر آنان كه پس از آن توبه كنند و به كار شايسته بپردازند، كه خدا آمرزگار و مهربان است.'),
(2797, 24, 6, 'و كسانى كه زنان خويش را به زنا نسبت دهند و آنان را گواهانى نباشد مگر خودشان پس گواهى يكى از آنان چهار بار گواهى با سوگند به خداست كه او هر آينه از راستگويان است'),
(2798, 24, 7, 'و بار پنجم اينكه [بگويد:] لعنت خدا بر او باد اگر از دروغگويان باشد.'),
(2799, 24, 8, 'و چهار بار گواهى و سوگند آن زن به خدا كه آن مرد هر آينه از دروغگويان است، عذاب- حد- را از وى باز مى‌دارد'),
(2800, 24, 9, 'و بار پنجم اينكه [بگويد:] خشم خدا بر او باد اگر آن مرد از راستگويان باشد.'),
(2801, 24, 10, 'و اگر فضل خدا و مِهر و بخشايش او بر شما نبود- كه بر شما آسان مى‌گيرد و گناهتان را مى‌پوشاند- و اينكه خدا توبه‌پذير و با حكمت است [احكامى سخت بر شما مقرر مى‌داشت و بر كيفر شما در دنيا شتاب مى‌كرد].'),
(2802, 24, 11, 'همانا كسانى كه آن دروغ بزرگ- اتهام كار زشت به يكى از همسران پيامبر- را [ساخته و] آورده‌اند گروهى همدست از شمايند. شما آن را براى خود شر مپنداريد، بلكه براى شما خير است. هر مردى از آنان را- كه آن دروغ بستند- [كيفر] چيزى است كه كسب كرده- گناهى كه مرتكب شده-، و آن كس از آنان كه [سهم‌] بزرگتر آن [دروغ‌] را پذيرفت- ابن ابَى رئيس منافقان- عذابى بزرگ دارد.'),
(2803, 24, 12, 'چرا آنگاه كه آن را شنيديد مردان و زنان مؤمن به خودشان گمان نيك نبردند و نگفتند: اين دروغى روشن و هويداست؟'),
(2804, 24, 13, 'چرا چهار گواه بر آن نياوردند؟ پس چون گواهان نياوردند، نزد خدا دروغگويند.'),
(2805, 24, 14, 'و اگر فضل خدا و مِهر و بخشايش او در اين جهان و آن جهان بر شما نبود هر آينه بدانچه در آن وارد شديد- سخنان بى‌دليل و اتهام بى‌گواه- عذابى بزرگ به شما مى‌رسيد'),
(2806, 24, 15, 'آنگاه كه آن را به زبانهاتان فرا مى‌گرفتيد- زبان به زبان و بدون تحقيق در ميانتان مى‌گشت- و چيزى را با دهانهاتان مى‌گفتيد كه شما را هيچ دانشى بدان نبود، و آن را [سخنى كوچك و] آسان مى‌پنداشتيد و حال آنكه در نزد خداوند بزرگ است.'),
(2807, 24, 16, 'و چرا هنگامى كه آن را شنيديد نگفتيد: ما را نرسد كه در اين باره سخن گوييم- پاكى تو [اى خدا]- اين [تهمت‌]، دروغى است بزرگ؟'),
(2808, 24, 17, 'خدا شما را پند مى‌دهد كه مبادا هرگز به چنين سخنى باز گرديد، اگر مؤمنيد.'),
(2809, 24, 18, 'و خدا آيات را براى شما روشن بيان مى‌كند، و خداوند دانا و با حكمت است.'),
(2810, 24, 19, 'همانا كسانى كه دوست مى‌دارند كه زشتكارى در ميان آنان كه ايمان آورده‌اند فاش و آشكار شود، آنها را در اين جهان و آن جهان عذابى است دردناك و خدا مى‌داند و شما نمى‌دانيد.'),
(2811, 24, 20, 'و اگر فضل خدا و مهر و بخشايش او بر شما نمى‌بود، و اينكه خدا رؤوف و مهربان است [در كيفر گناهتان شتاب مى‌كرد و عقوبتى سخت به شما مى رسيد].'),
(2812, 24, 21, 'اى كسانى كه ايمان آورده‌ايد از پى گامهاى شيطان مرويد، و هر كه گامهاى شيطان را پيروى كند [بداند كه‌] او به زشتكارى و كارهاى ناپسند فرمان مى‌دهد. و اگر فضل و بخشايش خدا بر شما نبود هيچ كس از شما هرگز پاك نمى‌شد، و ليكن خداوند هر كه را خواهد پاك مى‌سازد، و خدا شنوا و داناست.'),
(2813, 24, 22, 'و خداوندان فزونى و فراخى- توانگران و صاحبان نعمت- از شما مبادا سوگند بخورند كه به خويشان و درويشان و هجرت‌كنندگان در راه خدا چيزى ندهند و بايد درگذرند و چشم بپوشند- از بدزبانى و بدرفتارى نيازمندان- آيا دوست نداريد كه خدا شما را بيامرزد؟ و خدا آمرزگار و مهربان است.'),
(2814, 24, 23, 'همانا كسانى كه زنان پاكدامن بى‌خبر با ايمان را به زنا نسبت مى‌دهند در اين جهان و آن جهان لعنت شده‌اند و آنان راست عذابى بزرگ،'),
(2815, 24, 24, 'روزى كه زبانها و دستها و پاهاى آنان به آنچه مى‌كردند بر [زيان‌] آنها گواهى دهند'),
(2816, 24, 25, 'آن روز خداوند پاداش بسزاى آنان را تمام دهد و بدانند كه خداست حق روشن و آشكار.'),
(2817, 24, 26, 'زنان پليد براى مردان پليدند و مردان پليد براى زنان پليدند، و زنان پاك براى مردان پاك‌اند و مردان پاك براى زنان پاك‌اند اينان از آنچه درباره‌شان مى‌گويند پاك و بيزارند ايشان راست آمرزش و روزى بزرگوارانه.'),
(2818, 24, 27, 'اى كسانى كه ايمان آورده‌ايد، به خانه‌هايى جز خانه‌هاى خودتان وارد نشويد تا آنكه خبر دهيد و اجازه خواهيد و بر اهل آن سلام كنيد. اين براى شما بهتر است، باشد كه به ياد آريد و پند گيريد'),
(2819, 24, 28, 'پس اگر كسى را در آنها نيابيد وارد آنجا مشويد تا آنگاه كه به شما اجازه داده شود، و اگر شما را گويند: باز گرديد، پس باز گرديد اين براى شما پاكيزه‌تر است و خدا بدانچه مى‌كنيد داناست.'),
(2820, 24, 29, 'باكى و گناهى بر شما نيست كه به خانه‌هاى نامسكون- اماكن عمومى- كه كالا و بهره‌اى- برخوردارى و نفعى- در آن داريد وارد شويد و خدا آنچه را آشكار كنيد و آنچه را پنهان داريد مى‌داند.'),
(2821, 24, 30, 'مؤمنان را بگو كه ديدگان خويش را فرو دارند و شرمگاه‌هاى خود را- به پوشيدن آنها و از ديد ديگران- نگاه دارند اين براى آنان پاكيزه‌تر است، كه خدا به كارهايى كه مى‌كنند آگاه است.'),
(2822, 24, 31, 'و زنان مؤمن را بگو كه ديدگان خويش را فرو دارند و شرمگاه‌هاى خود را- از ديد ديگران- نگاه دارند و [جاهاى‌] آرايش و زيور خويش را آشكار نكنند مگر آنچه پيداست- مانند چهره و دست و روى پا-، و مقنعه‌هاشان را بر گريبانهاشان بيندازند و زيور و آرايش خود را آشكار نكنند مگر براى شوهرانشان يا پدرانشان يا پدران شوهرانشان يا پسرانشان يا پسران شوهرانشان يا برادرانشان يا پسران برادرانشان يا پسران خواهرانشان يا زنانشان- زنان همكيش خود- يا آنچه مالك شده‌اند- كنيزان- يا مردان تابع خانواده- طفيلى و كم خرد كه با آنان زندگى مى‌كنند- كه به زنان رغبت و نيازى ندارند يا كودكانى كه از شرمگاه زنان آگاه نگشته‌اند.- به سن تميز و بلوغ نرسيده‌اند- و پاهاى خود را چنان بر زمين نزنند تا آنچه از زيور خويش پنهان مى‌دارند دانسته شود- آواز پيرايه خود يعنى خلخال را به گوش مردم نرسانند-. و اى مؤمنان، همگى به خدا باز گرديد تا شايد رستگار شويد.'),
(2823, 24, 32, 'و عزبهايتان- مردان بى‌زن و زنان بى‌شوهر- را و شايستگان از بندگان و كنيزان خود را به زناشويى دهيد. اگر تنگدست باشند خداوند آنان را از بخشش خويش بى‌نياز مى‌گرداند و خدا فراخى‌بخش و داناست.'),
(2824, 24, 33, 'و آنان كه [وسيله‌] زناشويى نيابند بايد خويشتندارى و پاكدامنى پيش گيرند تا خداوند از بخشش خود بى‌نيازشان گرداند. و از غلامان و كنيزانتان آنان كه [با كسب و كار خود] خواستار بازخريد و آزادى خويش‌اند، اگر در آنها نيكيى دانستيد- شايستگى ديديد- بازخريدشان را بپذيريد، و از مال خداوند كه به شما داده است به آنان بدهيد- از زكات يا بخشى از بازخريد- و كنيزان خود را اگر خواهند كه پارسا باشند به ستم بر زنا وادار مكنيد تا كالاى زندگى دنيا بجوييد. و هر كه آنان را به زنا وادارد خداوند پس از وادارشدن آنها آمرزنده و مهربان است.'),
(2825, 24, 34, 'و هر آينه به سوى شما آياتى با بيان روشن و داستانى- نمونه و وصفى- از كسانى كه پيش از شما درگذشتند و پندى براى پرهيزگاران فرو فرستاديم.'),
(2826, 24, 35, 'خدا نور آسمانها و زمين- يعنى همه جهان هستى- است. داستان نور او چون چراغدانى- قنديلى- است كه در آن چراغى باشد [و] آن چراغ در آبگينه‌اى، [و] آن آبگينه گويى ستاره‌اى است تابان، كه [آن چراغ‌] از روغن درخت پر بركت زيتونى كه نه خاورى است و نه باخترى برافروخته شود كه روغنش [از درخشندگى‌] گر چه آتشى به آن نرسد نزديك است روشنى دهد، روشنى بر روشنى است خداى هر كه را خواهد به نور خود راه نمايد و خداى براى مردم مثلها مى‌زند و خداى به همه چيز داناست.'),
(2827, 24, 36, '[آن چراغ‌] در خانه‌هايى است كه خداى رخصت داده است كه بالا برده و بزرگ داشته شوند و نام وى در آنها ياد شود و او را در آنجا بامدادان و شبانگاهان به پاكى ستايند،'),
(2828, 24, 37, 'مردانى كه بازرگانى و خريد و فروش، ايشان را از ياد خدا و برپا داشتن نماز و دادن زكات مشغول نكند، و از روزى مى‌ترسند كه دلها و ديدگان در آن روز دگرگون- سرگشته و پريشان- شود'),
(2829, 24, 38, 'تا خداى ايشان را بر پايه نيكوترين آنچه كرده‌اند پاداش دهد و از فزون‌بخشى خويش بيفزايدشان و خدا هر كه را خواهد بى‌شمار- بى‌حساب يا بسيار- روزى دهد.'),
(2830, 24, 39, 'و كسانى كه كافر شدند كردارهاشان- كه به پندارشان نيكوست- چون سرابى است در بيابانى هموار كه تشنه آن را آب پندارد تا چون بدانجا رسد چيزى نيابد و خداى را نزد آن يابد كه حساب- كيفر- او را تمام بدهد، و خدا زود حساب است.'),
(2831, 24, 40, 'يا مانند تاريكيهايى است در دريايى ژرف و بسيار موج كه پيوسته موجى آن را بپوشاند و بالاى آن موجى ديگر است و بر فراز آن ابرى [تيره‌] تاريكيهايى است روى يكديگر- تاريكى شب و تاريكى دريا و تاريكى ابر-، گاهى كه دست خويش بيرون آرد نتواند كه ببيندش و هر كه را خداى نورى نداده او را هيچ نورى نيست.'),
(2832, 24, 41, 'آيا نديده‌اى كه خداى را هر كه در آسمانها و زمين است و پرندگان بال‌گشاده به پاكى مى‌ستايند؟ همگى نماز- نيايش- و تسبيح- ستايش- خويش مى دانند و خدا بدانچه مى‌كنند داناست.'),
(2833, 24, 42, 'و خداى راست فرمانروايى آسمانها و زمين و بازگشت به سوى خداست.'),
(2834, 24, 43, 'آيا نديده‌اى كه خداوند ابرها را [به بادها] مى‌راند سپس ميان [پاره‌هاى‌] آن پيوند مى‌دهد، آنگاه آن را توده و انبوه مى‌كند، پس باران را بينى كه از ميان آن بيرون مى‌آيد، و از آسمان از كوه‌هايى كه در آن است- انبوه ابرهاى منجمد شده- تگرگ مى‌فرستد و [زيان‌] آن را به هر كه خواهد مى‌رساند و از هر كه خواهد مى‌گرداند درخشندگى برقش نزديك است كه [روشنى‌] ديدگان را ببرد.'),
(2835, 24, 44, 'خدا شب و روز را مى‌گرداند- شب را مى‌آورد و روز را مى‌برد و بالعكس-، هر آينه در اين [آيت‌] خداوندان بينش را عبرتى است.'),
(2836, 24, 45, 'و خداوند هر جنبنده‌اى را از آب آفريد. پس برخى از آنها بر شكم خود مى‌روند و برخى از آنها بر دو پا مى‌روند و برخى از آنها بر چهار پا مى‌روند خداى آنچه خواهد مى‌آفريند، كه خدا بر هر چيزى تواناست.'),
(2837, 24, 46, 'هر آينه آياتى روشن و روشنگر فرو فرستاديم، و خدا هر كه را خواهد به راه راست راه نمايد.'),
(2838, 24, 47, 'و [منافقان‌] گويند: به خدا و به پيامبر ايمان آورده‌ايم و فرمانبرداريم، آنگاه پس از آن گروهى از آنان روى مى‌گردانند، و اينان ايمان ندارند.'),
(2839, 24, 48, 'و چون به سوى خدا و پيامبر او خوانده شوند تا ميانشان داورى كند آنگاه گروهى از آنان رويگردان شوند،'),
(2840, 24, 49, 'و اگر [حكم‌] حق به سود آنها باشد گردن نهاده و فرمانبردار سوى وى آيند.'),
(2841, 24, 50, 'آيا در دلهاشان بيمارى- نفاق و شك در نبوت تو- است يا [در باره عدالت تو] به شك افتاده‌اند يا مى‌ترسند كه خدا و پيامبرش بر آنان ستم كنند؟! [نه،] بلكه آنان خود ستمكارند.'),
(2842, 24, 51, 'همانا گفتار مؤمنان آنگاه كه به سوى خدا و پيامبرش خوانده شوند تا ميانشان داورى كند اين است كه گويند: شنيديم و فرمان برديم و ايشانند رستگاران.'),
(2843, 24, 52, 'و هر كه خدا و پيامبر او را فرمان برد و از خدا بترسد و از [نافرمانى‌] او بپرهيزد، پس ايشانند پيروزى يافتگان و كاميابان- كه به خشنودى خدا و نعمت‌هاى بهشت رسيده‌اند-.'),
(2844, 24, 53, 'و به خدا سوگندهاى سخت خوردند كه اگر آنان را بفرمايى- براى بيرون رفتن به جهاد- البته بيرون آيند بگو: سوگند مخوريد، فرمانبردارى نيكو [مطلوب‌] است- نه سوگند دروغ و منافقانه-. همانا خدا به آنچه مى‌كنيد آگاه است.'),
(2845, 24, 54, 'بگو: خداى را فرمان بريد و پيامبر را فرمان بريد. پس اگر برگرديد جز اين نيست كه بر پيامبر است آنچه او را تكليف نهاده‌اند و بر شماست آنچه شما را تكليف نهاده‌اند و اگر او را فرمان بريد راه يابيد، و بر پيامبر جز رساندن آشكار پيام نيست.'),
(2846, 24, 55, 'خدا آن كسان از شما را كه ايمان آوردند و كارهاى نيك و شايسته كردند وعده داده كه هر آينه آنان را در زمين جانشين [ديگران‌] كند- يا حكومت بخشد- چنانكه كسانى را كه پيش از ايشان بودند جانشين كرد، و دينشان را كه برايشان پسنديده است جايگير و برپاى سازد و البته به جاى بيمشان ايمنى دهد، به گونه‌اى كه مرا پرستند و چيزى را با من انباز نگيرند و هر كه پس از اين كفر ورزد و ناسپاسى كند آنانند بدكاران نافرمان.'),
(2847, 24, 56, 'و نماز را برپا داريد و زكات بدهيد و پيامبر را فرمان بريد شايد بر شما ببخشايد.'),
(2848, 24, 57, 'كسانى را كه كافر شدند مپندار كه در زمين ناتوان‌كننده خدا از گرفتن آنها به كيفر گناهانشان هستند [بلكه او تواناست‌]، و جايگاه آنها آتش دوزخ است، و آن بد بازگشتگاهى است.'),
(2849, 24, 58, 'اى كسانى كه ايمان آورده‌ايد، بايد كسانى كه مالكشان شده‌ايد و كسانى از شما كه به حد بلوغ نرسيده‌اند [براى در آمدن به نزد شما] در سه هنگام اجازه خواهند: پيش از نماز بامداد و هنگام نيمروز كه جامه‌هاى خويش فرو مى‌نهيد و پس از نماز خفتن اينها سه وقت خلوت شماست. و در غير از اين سه هنگام باكى بر شما و آنان نيست [كه اجازه نگيريد]، زيرا كه پيرامون يكديگر مى‌گرديد- و اجازه‌خواستن مشقت دارد-. اينچنين خدا آيات را براى شما روشن بيان مى‌كند، و خدا دانا و با حكمت است.'),
(2850, 24, 59, 'و چون كودكان شما به حد بلوغ رسند بايد اجازه [ورود] خواهند چنانكه كسانى كه پيش از آنان بودند اجازه مى‌خواستند. اينچنين خدا آيات خود را براى شما روشن بيان مى‌كند، و خدا دانا و با حكمت است.'),
(2851, 24, 60, 'و بازنشتسگان از زنان كه اميد زناشويى ندارند، بر آنان باكى و گناهى نيست كه جامه‌هاى- چادرهاى- خويش فرونهند بى‌آنكه آرايش و زيورى نمودار سازند، و اينكه خويشتن‌دارى كنند برايشان بهتر است و خدا شنوا و داناست.'),
(2852, 24, 61, 'بر نابينا و لنگ و بيمار و خودتان تنگى و گناهى نيست كه از خانه‌هاى خويش- كه شامل خانه‌هاى فرزندانتان نيز هست- يا از خانه‌هاى پدرانتان يا خانه‌هاى مادرانتان يا خانه‌هاى برادرانتان يا خانه‌هاى خواهرانتان يا خانه‌هاى عموهاتان يا خانه‌هاى عمه‌هاتان يا خانه‌هاى داييهاتان يا خانه‌هاى خاله‌هاتان يا خانه‌هايى كه كليدهاى آنها به دست شماست يا [خانه‌هاى‌] دوستانتان [چيزى‌] بخوريد تنگى و گناهى نيست كه همگى با هم يا جدا جدا و پراكنده بخوريد. پس چون به خانه‌هايى در آييد بر خودتان- يكديگر- سلام گوييد، كه سلام درودى از سوى خدا است، درودى مبارك و پاكيزه. بدين گونه خدا آيات را براى شما روشن بيان مى‌كند شايد خرد را كار بنديد.'),
(2853, 24, 62, 'همانا مؤمنان كسانى‌اند كه به خدا و پيامبر او ايمان آورده‌اند و چون با او'),
(2854, 24, 63, 'فراخواندن پيامبر را ميان خود مانند فراخواندن برخى از شما برخ ديگر را مسازيد براستى خدا كسانى را كه خود را از ميان شما پنهانى و در پناه يكديگر بيرون مى‌كشند مى‌شناسد. پس آنان كه از فرمان او'),
(2855, 24, 64, 'بدانيد كه خداى راست آنچه در آسمانها و زمين است. بدرستى مى‌داند كه شما در چه حال و بر چه كاريد- از كفر و اسلام و نافرمانى و طاعت-. و روزى كه به او بازگردانده شوند آنان را بدانچه كرده‌اند آگاه مى‌كند [و جزاشان خواهد داد]، و خدا به هر چيزى داناست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'بزرگ و بزرگوار است آن [خداى‌] كه اين فرقان- جداكننده ميان حق و باطل: قرآن- را بر بنده خويش فرو فرستاد تا جهانيان را بيم‌كننده باشد.'),
(2857, 25, 2, 'آن كه پادشاهى آسمانها و زمين او راست، و هيچ فرزندى نگرفت و او را هيچ انبازى در فرمانروايى نيست، و هر چيز را بيافريد و آن را اندازه نهاد، اندازه‌اى درست و تمام.'),
(2858, 25, 3, 'و [مشركان‌] به جاى او- خداى يكتا- خدايانى گرفتند كه هيچ چيز نمى‌آفرينند و خود آفريده مى‌شوند، و زيان و سودى براى خويشتن در توان و اختيار ندارند، و نه مرگى و نه زندگانيى و نه برانگيختنى به دست آنهاست.'),
(2859, 25, 4, 'و كسانى كه كافر شدند گفتند: اين- قرآن- نيست مگر دروغى كه [محمد] بربافته است و گروهى ديگر او را بر آن يارى كرده‌اند. براستى كه ستم و دروغى ناروا آوردند.'),
(2860, 25, 5, 'و گفتند: افسانه‌هاى پيشينيان است كه او آنها را كه هر بامداد و شبانگاه بر او خوانده مى‌شود مى‌نويساند- براى او مى‌نويسند چه خود نمى‌تواند نوشت-.'),
(2861, 25, 6, 'بگو: اين [قرآن‌] را كسى فرو فرستاده است كه نهان را در آسمان و زمين مى‌داند، و همانا او آمرزگار و مهربان است.'),
(2862, 25, 7, 'و گفتند: اين پيامبر را چيست كه غذا مى‌خورد و در بازارها راه مى‌رود؟! چرا فرشته‌اى بر او فرو نيامده تا با وى بيم‌كننده باشد؟'),
(2863, 25, 8, 'يا چرا گنجى [از آسمان‌] به سويش افكنده نمى‌شود، يا چرا او را بوستانى نيست كه از آن بخورد؟ و ستمكاران گفتند: جز مردى جادوزده را پيروى نمى‌كنيد.'),
(2864, 25, 9, 'بنگر كه چگونه براى تو مَثَلها زدند- چگونه تو را وصف كردند-، پس گمراه شدند و نتوانند كه راهى بيابند.'),
(2865, 25, 10, 'بزرگ و بزرگوار است آن [خداى‌] كه اگر خواهد تو را بهتر از اين دهد، بوستانهايى كه از زير [درختان‌] آنها جويها روان باشد و براى تو كوشكها پديد كند.'),
(2866, 25, 11, '[اينها نه تنها تو را باور ندارند] بلكه رستاخيز را دروغ انگاشتند و ما براى هر كه رستاخيز را دروغ انگارد آتش سوزان آماده كرده‌ايم،'),
(2867, 25, 12, 'كه چون از جايگاه دور آنان را بيند آواى خشم و خروش آن را بشنوند.'),
(2868, 25, 13, 'و چون آنان را كه با زنجير به هم بسته‌اند- يا دستها و گردنهاشان با زنجير بسته شده- در جايى تنگ از آن آتش بيفكنند در آنجا فرياد واى و واويلاه برآرند و مرگ خويش خواهند.'),
(2869, 25, 14, '[فرشتگان گويند:] امروز نه يك بار بلكه بسى واى و واويلاه از دل بركشيد و مرگ خويش بسيار خواهيد- اگر چه شما را مرگى نيست-.'),
(2870, 25, 15, 'بگو: آيا اين بهتر است يا بهشت جاويدان كه به پرهيزگاران وعده داده شده؟ آن [بهشت‌] براى ايشان پاداش و بازگشتگاه است'),
(2871, 25, 16, 'ايشان را در آن هر چه بخواهند هست در حالى كه جاويدانند وعده‌اى است بر پروردگار تو فراخواسته.'),
(2872, 25, 17, 'و روزى كه آنان و آنچه را جز خدا مى‌پرستند برانگيزد، پس [به پرستيدگان‌] گويد: آيا شما اين بندگان مرا گمراه كرديد يا خود راه را گم كردند؟'),
(2873, 25, 18, 'گويند: پاكى تو [بار خدايا]، ما را سزاوار نبود كه جز تو دوستانى بگيريم و ليكن آنها و پدرانشان را برخوردار كردى تا ياد و پند تو را فراموش كردند و آنها گروهى هلاك‌شده بودند.'),
(2874, 25, 19, '[خداى به پرستندگان فرمايد:] آنها آنچه را شما مى‌گوييد دروغ شمردند، پس نتوانيد عذاب را از خويشتن بگردانيد و نه [خود را] يارى دهيد و هر كه از شما- پرستندگان و پرستيدگان- كه ستم كند او را عذابى بزرگ بچشانيم.'),
(2875, 25, 20, 'و پيش از تو پيامبران را نفرستاديم مگر آنكه ايشان نيز طعام مى‌خوردند و در بازارها راه مى‌رفتند. و برخى از شما را وسيله آزمون برخى ديگر ساختيم، كه آيا شكيبايى مى‌كنيد؟ و پروردگار تو بيناست.'),
(2876, 25, 21, 'و كسانى كه ديدار ما- يعنى رستاخيز- را باور و اميد ندارند گويند: چرا بر ما فرشتگان فرو فرستاده نشدند يا چرا پروردگار خود را نمى‌بينيم؟ هر آينه درباره خويشتن بزرگ‌منشى نمودند و سركشى كردند، سركشى بزرگ.'),
(2877, 25, 22, 'روزى كه فرشتگان را ببينند- هنگام مرگ يا روز حشر- آن روز بزهكاران را مژده‌اى نيست، و [به آنها] گويند: [بهشت بر شما] بسته و حرام است.'),
(2878, 25, 23, 'و به كارهايى كه كردند پردازيم و آن را گردى پراكنده كنيم.'),
(2879, 25, 24, 'بهشتيان در آن روز بهترين جايگاه و نيكوترين آسايشگاه دارند.'),
(2880, 25, 25, 'و [ياد كن‌] روزى كه آسمان با ابرها [يش‌] بشكافد و فرشتگان فروشوند، فروشدنى.'),
(2881, 25, 26, 'آن روز، پادشاهى براى خداى رحمان ثابت است، و بر كافران روزى دشوار است.'),
(2882, 25, 27, 'و روزى كه [آن كافر] ستمكار دو دست خويش به دندان گزد [و] گويد: اى كاش با پيامبر راهى فرا مى‌گرفتم'),
(2883, 25, 28, 'واى بر من، كاش فلان را به دوستى نمى‌گرفتم'),
(2884, 25, 29, 'همانا مرا از اين يادكرد و پند- قرآن- پس از آنكه به من رسيد گمراه كرد. و شيطان آدمى را فروگذارنده و خواركننده است.'),
(2885, 25, 30, 'و پيامبر گويد: پروردگارا، قوم من اين قرآن را كنار نهاده و فروگذاشتند.'),
(2886, 25, 31, 'و همچنين براى هر پيامبرى دشمنى از بزهكاران پديد كرديم و پروردگار تو راهنما و ياورى بسنده است.'),
(2887, 25, 32, 'و كسانى كه كافر شدند گفتند: چرا قرآن بر او به يك بار فرو فرستاده نشد؟ اينچنين [فرستاديم‌] تا دل تو را بدان استوار و آرام گردانيم، و [از اين رو] آن را جدا جدا- برخى از پى برخى- و به آهستگى برخوانديم.'),
(2888, 25, 33, 'و هيچ مَثَلى- از خرده‌گيريها و طعن در نبوت و كتاب تو- براى تو نيارند مگر آنكه تو را [پاسخ‌] راست و درست و به نيكوتر بيانى بياريم.'),
(2889, 25, 34, 'آنان كه برانگيزندشان و بر رويهاشان به سوى دوزخ همى‌كشند اينان را بدترين جاى و گم‌ترين راه است.'),
(2890, 25, 35, 'و هر آينه موسى را كتاب داديم و برادر او هارون را يار و مددكارش ساختيم.'),
(2891, 25, 36, 'و گفتيم: برويد به سوى قومى كه آيات ما را دروغ انگاشتند پس آنان را بسختى هلاك كرديم.'),
(2892, 25, 37, 'و قوم نوح چون پيامبران را دروغگو شمردند آنان را غرق گردانيديم و براى مردم- بازماندگان و آيندگان- عبرتى كرديم و ستمكاران را عذابى دردناك آماده ساخته‌ايم.'),
(2893, 25, 38, 'و عاد و ثمود و اصحاب رسّ و بسى نسلها را كه ميان آنها بودند [هلاك كرديم‌].'),
(2894, 25, 39, 'و هر يك را مَثَلها [براى پند گرفتن‌] زديم و [چون پند نگرفتند] همه را بسختى نابود كرديم.'),
(2895, 25, 40, 'و هر آينه [كافران قريش‌] به شهرى- شهر لوط- گذر كردند كه بر آن باران بد- سنگباران- باريده شد. آيا آن را نمى‌ديدند [تا پند گيرند]؟ بلكه برانگيخته شدن- رستاخيز و روز حسابرسى- را باور و اميد نداشتند.'),
(2896, 25, 41, 'و چون تو را بينند جز به مسخره‌ات نگيرند [و گويند:] آيا اين است آن كه خداى به پيامبرى برانگيخته است؟!'),
(2897, 25, 42, 'نزديك بود كه ما را از خدايانمان بگرداند و گمراه كند اگر بر آنها شكيبايى و پايدارى نمى‌ورزيديم. و آنگاه كه عذاب را ببينند خواهند دانست كه چه كسى گمراه‌تر است.'),
(2898, 25, 43, 'آيا كسى را كه خداى خويش را كام و خواهش خويش گرفت ديدى؟ پس آيا تو بر او نگهبان خواهى بود؟'),
(2899, 25, 44, 'يا مگر پندارى كه بيشترشان [حق را] مى‌شنوند يا درمى‌يابند؟! آنها جز مانند چهارپايان نيستند بلكه گمراه‌ترند.'),
(2900, 25, 45, 'آيا به [صنع‌] پروردگار خويش ننگريستى كه چگونه سايه را بكشيد و اگر مى‌خواست، آن را ايستا مى‌گردانيد [تا يكسره شب و تاريك بودى‌] سپس آفتاب را بر آن'),
(2901, 25, 46, 'باز آن سايه را به آسانى- اندك اندك- به سوى خويش بگرفتيم.'),
(2902, 25, 47, 'و اوست آن كه شب را براى شما پوششى كرد و خواب را آسايشى، و روز را رستاخيز قرار داد- تا در پى كار و كوشش رويد-.'),
(2903, 25, 48, 'و اوست آن كه بادها را پيشاپيش رحمت خويش- باران- مژدگانها فرستاد و از آسمان آبى پاك و پاك‌كننده فروفرستاديم،'),
(2904, 25, 49, 'تا با آن سرزمين مرده را زنده كنيم و آن را به آفريده‌هاى خويش، چهارپايان و آدميان بسيار، بنوشانيم.'),
(2905, 25, 50, 'و هر آينه آن'),
(2906, 25, 51, 'و اگر مى‌خواستيم هر آينه در هر آبادى و شهرى بيم‌كننده‌اى برمى‌انگيختيم.'),
(2907, 25, 52, 'پس كافران را فرمان مبر و بدين [قرآن‌]- به حكمى كه در قرآن است يا به ترك طاعت آنان- با آنان جهاد كن، جهادى بزرگ.'),
(2908, 25, 53, 'و اوست آن كه دو دريا را به هم پيوست، اين يكى خوش و گواراست و آن ديگر شور و تلخ و ميان آن دو حائلى و بندى بازدارنده پديد كرد- تا در هم نشوند-.'),
(2909, 25, 54, 'و اوست آن كه از آب- آب منى: نطفه-، آدمى را آفريد پس او را نژاد- پيوستگى نسب- و پيوند- پيوستگى ازدواج- كرد و پروردگار تو [بر هر چيز] تواناست.'),
(2910, 25, 55, 'و به جاى خدا چيزها مى‌پرستند كه نه سودشان دارد و نه زيانشان رساند و كافر بر ضد خداى خويش [با شيطان‌] همپشت است.'),
(2911, 25, 56, 'و ما تو را جز مژده‌دهنده و بيم‌كننده نفرستاديم.'),
(2912, 25, 57, 'بگو: از شما بر اين [رسالت‌] هيچ مزدى نمى‌خواهم مگر اين كه هر كه خواهد راهى به سوى پروردگار خويش فرا گيرد.'),
(2913, 25, 58, 'و بر آن زنده‌اى توكل كن كه هرگز او را مرگ نباشد و او را همراه با سپاس و ستايش به پاكى ياد كن، و او به گناهان بندگان خويش آگاهى بسنده است.'),
(2914, 25, 59, 'آن كه آسمانها و زمين و آنچه را ميان آنهاست در شش روز بيافريد، سپس بر عرش- مقام فرماندهى بر جهان هستى- بر آمد. [اوست‌] خداى رحمان، پس [در اين باره- معناى خلق و استوى يا خداى رحمان و صفات او-] از آن كه آگاه است بپرس.'),
(2915, 25, 60, 'و چون آنان را گويند كه خداى رحمان را سجده كنيد، گويند: رحمان چه باشد؟! آيا آن را كه تو به ما فرمان مى‌دهى سجده كنيم؟! و آنها را رميدن [از ايمان و راه حق‌] بيفزايد.'),
(2916, 25, 61, 'بزرگ و بزرگوار است آن كه در آسمان برجها- منازل خورشيد، كه دوازده برج باشند- نهاد و در آن چراغى- خورشيد- و ماهى تابان پديد كرد.'),
(2917, 25, 62, 'و اوست كه شب و روز را پياپى كرد براى هر كه خواهد به ياد آرد و پند گيرد يا خواهد سپاسدارى كند.'),
(2918, 25, 63, 'و بندگان رحمان آنانند كه بر زمين با فروتنى و نرمى راه مى‌روند، و چون نادانان ايشان را [به سخنى ناروا] مخاطب سازند، سخنى مسالمت آميز- يا سلام- گويند.'),
(2919, 25, 64, 'و آنان كه شب را براى پروردگار خويش در سجده و به نماز ايستاده به روز آرند.'),
(2920, 25, 65, 'و آنان كه گويند: پروردگارا، عذاب دوزخ را از ما بگردان، كه عذاب آن پيوسته و بر دوام است'),
(2921, 25, 66, 'همانا آن بد آرامگاهى و بد ماندن جايى است.'),
(2922, 25, 67, 'و آنان كه چون هزينه كنند نه اسراف كنند و نه تنگ گيرند و ميان اين دو به راه اعتدال باشند.'),
(2923, 25, 68, 'و آنان كه با خداى يكتا خداى ديگر نخوانند- نپرستند- و كسى را كه خداوند كشتن وى را حرام كرده نكشند مگر بحق، و زنا نكنند و هر كه اين كارها كند كيفر بزه خويش ببيند،'),
(2924, 25, 69, 'عذاب او به روز رستاخيز دوچندان شود و در آن [عذاب‌] به خوارى جاويدان باشد،'),
(2925, 25, 70, 'مگر كسانى كه توبه كنند و ايمان آورند و كار نيك و شايسته كنند، پس اينانند كه خداوند بديهاشان را به نيكيها بدل گرداند، و خدا آمرزگار و مهربان است.'),
(2926, 25, 71, 'و هر كه توبه كند و كار شايسته كند، همانا او به خدا باز مى‌گردد بازگشتى بسزا.'),
(2927, 25, 72, 'و آنان كه گواهى دروغ- باطل و به ناحق- ندهند- يا در مجالس باطل حضور نيابند-، و چون بر بيهوده و ناپسند بگذرند با بزرگوارى بگذرند.'),
(2928, 25, 73, 'و آنان كه چون به آيات پروردگارشان يادآور و پند داده شوند بر آنها مانند كران و كوران نيفتند- بلكه آنها را به گوش هوش بشنوند و به ديده بصيرت ببينند-.'),
(2929, 25, 74, 'و آنان كه گويند: پروردگارا، از همسرانمان و فرزندانمان ما را روشنى چشمها ببخش و ما را پيشواى پرهيزگاران كن.'),
(2930, 25, 75, 'ايشانند كه بدان شكيبايى كه كردند غرفه- جايگاه بلند- بهشت را پاداش يابند، و در آنجا با درود و سلام روبرو شوند'),
(2931, 25, 76, 'جاودانه در آنجا باشند، نيكو آرامگاهى و نيكو ماندن جايى است.'),
(2932, 25, 77, 'بگو: اگر خواندن- دعا و عبادت- شما نباشد پروردگار من به شما اعتنا و عنايتى نكند- شما را قدر و ارزشى ننهد-، [ولى كافران‌] شما [پيامبر و آيات خدا را] تكذيب كرديد پس [كيفرتان‌] پيوسته و بر دوام خواهد بود.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طا، سين، ميم.'),
(2934, 26, 2, 'اين آيتهاى كتاب روشن و روشنگر است.'),
(2935, 26, 3, 'شايد تو خويشتن را از [اندوه‌] اينكه ايمان نمى‌آورند خواهى كشت.'),
(2936, 26, 4, 'اگر بخواهيم، از آسمان آيتى بر آنان فرو فرستيم تا گردنهاشان در برابر آن به فرمانبردارى فرود آيد- يا: تا مهترانشان آن را گردن نهند-.'),
(2937, 26, 5, 'هيچ يادكرد و پند تازه‌اى از خداى رحمان بديشان نرسد مگر آنكه از آن رويگردان شوند.'),
(2938, 26, 6, 'همانا تكذيب كردند پس زودا كه خبرهاى آنچه مسخره‌اش مى‌كردند- از رستاخيز و پاداش و كيفر و بهشت و دوزخ- بديشان رسد.'),
(2939, 26, 7, 'آيا به زمين ننگريسته‌اند كه بسا از هر گونه گياه نيكو در آن رويانيده‌ايم.'),
(2940, 26, 8, 'هر آينه در اين نشانه‌اى است ولى بيشترشان مؤمن نيستند.'),
(2941, 26, 9, 'و براستى پروردگار تو هموست تواناى بى‌همتا و مهربان.'),
(2942, 26, 10, 'و [ياد كن‌] آنگاه كه پروردگار تو موسى را بخواند كه به سوى آن گروه ستمكار برو،'),
(2943, 26, 11, 'گروه فرعون آيا [از كفر و سركشى‌] نمى‌پرهيزند؟'),
(2944, 26, 12, 'گفت: پروردگارا، من بيم دارم كه دروغگويم خوانند'),
(2945, 26, 13, 'و سينه‌ام تنگ است و زبانم روان و گشاده نيست، پس به هارون پيام فرست'),
(2946, 26, 14, 'و آنان را بر من [دعوى‌] گناهى است، از اين رو مى‌ترسم مرا بكشند.'),
(2947, 26, 15, 'گفت: نه چنين است- تو را نمى‌كشند-، پس هر دو با نشانه‌هاى ما برويد، ما با شماييم، مى‌شنويم.'),
(2948, 26, 16, 'نزد فرعون رويد و بگوييد: ما فرستاده پروردگار جهانيانيم،'),
(2949, 26, 17, '[پيام او اين است‌] كه فرزندان اسرائيل را با ما بفرست.'),
(2950, 26, 18, 'گفت: آيا تو را در كودكى در ميان خودمان نپرورديم و چند سال از عمرت را در ميان ما نبودى؟'),
(2951, 26, 19, 'و آن كرده خويش كه كردى، كردى و تو از ناسپاسانى.'),
(2952, 26, 20, 'گفت: آن [كار] را آنگاه كردم كه از ناآگاهان بودم- آگاه نبودم كه به مشت‌زدن من آن كس كشته مى‌شود-.'),
(2953, 26, 21, 'پس چون از شما ترسيدم گريختم، و پروردگارم مرا حكمى- حكمت: درستى انديشه و گفتار و كردار يا حكم نبوت- داد و مرا از پيامبران كرد'),
(2954, 26, 22, 'و آن، [چه‌] نعمتى است كه بر من منت مى‌نهى كه فرزندان اسرائيل را برده ساخته‌اى؟'),
(2955, 26, 23, 'فرعون گفت: خداى جهانيان چيست؟'),
(2956, 26, 24, 'گفت: خداوند آسمانها و زمين و هر چه ميان آنهاست، اگر بى‌گمان باور مى‌داريد.'),
(2957, 26, 25, '[فرعون‌] به كسانى كه گرد او بودند گفت: آيا نمى‌شنويد [چه مى‌گويد]؟'),
(2958, 26, 26, '[موسى‌] گفت: پروردگار شما و پروردگار نياكان شما.'),
(2959, 26, 27, '[فرعون‌] گفت: اين پيامبرتان كه به سوى شما فرستاده شده ديوانه است.'),
(2960, 26, 28, '[موسى‌] گفت: خداوند خاور و باختر و هر چه ميان آنهاست، اگر خرد داريد.'),
(2961, 26, 29, 'گفت: اگر خدايى جز من بگزينى هر آينه تو را از زندانيان گردانم.'),
(2962, 26, 30, 'گفت: اگر چه تو را حجتى روشن آورده باشم؟'),
(2963, 26, 31, 'گفت: آن را بيار، اگر از راستگويانى.'),
(2964, 26, 32, 'پس عصاى خويش بيفكند و هماندم اژدهايى هويدا گشت.'),
(2965, 26, 33, 'و دست خويش را بيرون آورد و آنگاه براى بينندگان سپيد و روشن نمود.'),
(2966, 26, 34, '[فرعون‌] به مهترانى كه گرد او بودند گفت: براستى اين جادوگرى داناست'),
(2967, 26, 35, 'مى‌خواهد شما را به جادوى خود از سرزمينتان بيرون كند. پس چه مى‌فرماييد؟'),
(2968, 26, 36, 'گفتند: او و برادرش را واپس دار- كارشان را به تأخير انداز- و گردآوران را به شهرها فرست،'),
(2969, 26, 37, 'تا هر جادوگر دانايى را نزد تو آورند.'),
(2970, 26, 38, 'پس جادوگران را در وعده‌گاه روزى دانسته فراهم آوردند.'),
(2971, 26, 39, 'و مردم را گفتند: آيا شما نيز فراهم مى‌آييد؟'),
(2972, 26, 40, 'باشد كه از جادوگران پيروى كنيم، اگر آنان پيروز شوند.'),
(2973, 26, 41, 'چون جادوگران بيامدند به فرعون گفتند: اگر ما پيروز باشيم آيا ما را هيچ مزدى خواهد بود؟'),
(2974, 26, 42, 'گفت: آرى، و شما آنگاه از نزديكان [دربار من‌] خواهيد بود.'),
(2975, 26, 43, 'موسى به آنان گفت: آنچه را خواهيد افكند، بيفكنيد.'),
(2976, 26, 44, 'پس ريسمانها و عصاهاشان را بيفكندند و گفتند: به جاه و شكوه فرعون سوگند كه هر آينه ما پيروزيم.'),
(2977, 26, 45, 'پس موسى عصاى خويش بيفكند، ناگاه هر چه را به دروغ مى‌ساختند فرو مى‌برد.'),
(2978, 26, 46, 'پس جادوگران به سجده درافتادند.'),
(2979, 26, 47, 'گفتند: به پروردگار جهانيان ايمان آورديم،'),
(2980, 26, 48, 'پروردگار موسى و هارون.'),
(2981, 26, 49, '[فرعون‌] گفت: آيا پيش از آنكه شما را اجازه دهم او را باور داشتيد- به او گرويديد-؟ همانا او بزرگ شماست كه شما را جادو آموخته. پس بزودى خواهيد دانست، هر آينه دستها و پاهايتان را بر خلاف يكديگر مى‌برم و همه شما را بردار مى‌كنم.'),
(2982, 26, 50, 'گفتند: باكى نيست، همانا ما به پروردگارمان باز مى‌گرديم'),
(2983, 26, 51, 'ما اميد داريم كه پروردگارمان گناهانمان را بيامرزد كه ما نخستين مؤمنانيم.'),
(2984, 26, 52, 'و به موسى وحى كرديم كه بندگان مرا شبانه ببر زيرا كه در پى شما خواهند آمد.'),
(2985, 26, 53, 'پس فرعون گردآورانى را به شهرها فرستاد'),
(2986, 26, 54, '[و گفتند:] همانا اينان- بنى اسرائيل- گروهى اندكند،'),
(2987, 26, 55, 'و هر آينه ما را به خشم آورده‌اند،'),
(2988, 26, 56, 'و بى‌گمان ما گروهى همپشت و سلاح‌داران آماده پيكاريم.'),
(2989, 26, 57, 'پس آنان را از بوستانها و چشمه‌سارها بيرون آورديم،'),
(2990, 26, 58, 'و از گنجها و جايگاه نيكو و بزرگوارانه.'),
(2991, 26, 59, 'اينچنين [كرديم‌]، و آنها را به فرزندان اسرائيل به ميراث داديم.'),
(2992, 26, 60, 'پس [سپاه فرعون‌] آفتابدم از پى ايشان رفتند.'),
(2993, 26, 61, 'چون آن دو گروه يكديگر را ديدند، ياران موسى گفتند: همانا ما گرفتارشدگانيم.'),
(2994, 26, 62, '[موسى‌] گفت: هرگز، كه پروردگار من با من است، مرا راه خواهد نمود.'),
(2995, 26, 63, 'پس به موسى وحى كرديم كه عصاى خود را بر دريا بزن پس [دريا] بشكافت و هر پاره‌اى چون كوهى بزرگ گشت.'),
(2996, 26, 64, 'و آن ديگران- فرعونيان- را نزديك آنجا آورديم.'),
(2997, 26, 65, 'و موسى و همه كسانى را كه با او بودند رهانيديم.'),
(2998, 26, 66, 'سپس آن ديگران را غرق ساختيم.'),
(2999, 26, 67, 'هر آينه در اين نشانه و عبرتى است، و بيشترشان مؤمن نبودند.'),
(3000, 26, 68, 'و همانا پروردگار تو هموست تواناى بى‌همتا و مهربان.'),
(3001, 26, 69, 'و خبر ابراهيم را بر آنان برخوان،'),
(3002, 26, 70, 'آنگاه كه به پدر خويش- آزر، سرپرست خويش- و قوم خود گفت: چه مى‌پرستيد؟'),
(3003, 26, 71, 'گفتند: بتانى را مى‌پرستيم و پيوسته سر بر آستانشان داريم.'),
(3004, 26, 72, 'گفت: آيا هنگامى كه آنها را مى‌خوانيد آواز شما را مى‌شنوند؟'),
(3005, 26, 73, 'يا شما را سود يا زيان مى‌رسانند؟'),
(3006, 26, 74, 'گفتند: بلكه پدرانمان را يافته‌ايم كه چنين مى‌كردند.'),
(3007, 26, 75, 'گفت: آيا ديده‌ايد- يا دانسته‌ايد، يعنى ببينيد و بدانيد- آنچه مى‌پرستيده‌ايد،'),
(3008, 26, 76, 'شما و پدران پيشترتان؟'),
(3009, 26, 77, 'همانا آنها دشمن من‌اند، مگر پروردگار جهانيان،'),
(3010, 26, 78, 'آن كه مرا آفريد پس همو را هم نمايد،'),
(3011, 26, 79, 'و آن كه طعام و آشاميدنيم دهد،'),
(3012, 26, 80, 'و چون بيمار شوم بهبودم بخشد،'),
(3013, 26, 81, 'و آن كه مرا بميراند و سپس زنده‌ام كند،'),
(3014, 26, 82, 'و آن كه اميد دارم كه روز حساب و پاداش لغزشم را بيامرزد.'),
(3015, 26, 83, 'پروردگارا، مرا حكمى- حكمت: انديشه و گفتار و كردار درست- ببخش و مرا به نيكان و شايستگان بپيوند.'),
(3016, 26, 84, 'و براى من در پسينيان نام و ياد نيك نِه.'),
(3017, 26, 85, 'و مرا از ميراث‌بران بهشت پرنعمت گردان'),
(3018, 26, 86, 'و پدرم را بيامرز، كه از گمراهان است.'),
(3019, 26, 87, 'و در آن روز كه [مردمان‌] برانگيخته مى‌شوند مرا رسوا و خوار مساز،'),
(3020, 26, 88, 'روزى كه نه مال سود دارد و نه پسران،'),
(3021, 26, 89, 'مگر آن كه با دلى پاك و رسته [از شرك و كفر و گناه‌] به نزد خدا آيد.'),
(3022, 26, 90, 'و بهشت براى پرهيزگاران نزديك شود.'),
(3023, 26, 91, 'و دوزخ براى گمراهان پديدار و آشكار گردد.'),
(3024, 26, 92, 'و آنان را گويند: كجايند آنچه جز خدا مى‌پرستيديد،'),
(3025, 26, 93, 'آيا شما را يارى مى‌كنند يا خود را يارى مى‌دهند؟'),
(3026, 26, 94, 'پس آنها و گمراهان نگونسار در آنجا افكنده شوند.'),
(3027, 26, 95, 'و سپاهيان ابليس نيز همگان.'),
(3028, 26, 96, 'و در حالى كه در آنجا با يكديگر ستيزه مى‌كنند، گويند:'),
(3029, 26, 97, 'به خدا سوگند كه هر آينه ما در گمراهى آشكارى بوديم،'),
(3030, 26, 98, 'آنگاه كه شما را با پروردگار جهانيان برابر مى‌داشتيم'),
(3031, 26, 99, 'و ما را جز بزهكاران گمراه نساختند.'),
(3032, 26, 100, 'اينك ما را هيچ شفاعت‌كننده و خواهشگرى نيست'),
(3033, 26, 101, 'و نه دوست دلسوز و مهربانى.'),
(3034, 26, 102, 'كاش ما را [به دنيا] بازگشتى مى‌بود تا از مؤمنان مى‌شديم.'),
(3035, 26, 103, 'همانا در اين نشانه و عبرتى است و بيشترشان مؤمن نبودند.'),
(3036, 26, 104, 'و هر آينه پروردگار تو هموست تواناى بى‌همتا و مهربان.'),
(3037, 26, 105, 'قوم نوح پيامبران را دروغگو شمردند.'),
(3038, 26, 106, 'آنگاه كه برادرشان نوح به آنان گفت: آيا [از شرك به خدا] نمى‌پرهيزيد؟'),
(3039, 26, 107, 'من شما را پيامبرى امينم.'),
(3040, 26, 108, 'پس، از خدا پروا كنيد و مرا فرمان بريد.'),
(3041, 26, 109, 'و بر اين [پيامبرى‌] از شما مزدى نمى‌خواهم، مزد من جز بر پروردگار جهانيان نيست.'),
(3042, 26, 110, 'پس، از خدا بترسيد و مرا فرمان بريد.'),
(3043, 26, 111, 'گفتند: آيا به تو ايمان بياوريم و حال آنكه فرومايگان از تو پيروى كرده‌اند؟'),
(3044, 26, 112, 'گفت: مرا بدانچه آنان مى‌كردند چه دانشى است؟'),
(3045, 26, 113, 'حسابشان جز بر پروردگار من نيست، اگر مى‌فهميد.'),
(3046, 26, 114, 'و من مؤمنان را نخواهم راند.'),
(3047, 26, 115, 'من جز بيم‌دهنده‌اى آشكار نيستم.'),
(3048, 26, 116, 'گفتند: اى نوح، اگر [از اين سخنان‌] بازنايستى، هر آينه سنگسار مى‌شوى.'),
(3049, 26, 117, 'گفت: پروردگارا، همانا قوم من مرا تكذيب كردند.'),
(3050, 26, 118, 'پس ميان من و آنان حكم كن، و مرا و مؤمنانى را كه با من‌اند رهايى بخش.'),
(3051, 26, 119, 'پس او و كسانى را كه با او در كشتى گرانبار بودند رهانيديم.'),
(3052, 26, 120, 'سپس آن ماندگان را غرق كرديم.'),
(3053, 26, 121, 'هر آينه در اين نشانه و عبرتى است، و بيشترشان مؤمن نبودند.'),
(3054, 26, 122, 'و همانا پروردگار تو هموست تواناى بى‌همتا و مهربان.'),
(3055, 26, 123, 'قوم عاد فرستادگان را دروغگو شمردند'),
(3056, 26, 124, 'آنگاه كه برادرشان هود گفت: آيا [از شرك‌] نمى‌پرهيزيد؟'),
(3057, 26, 125, 'من براى شما پيامبرى امينم.'),
(3058, 26, 126, 'پس، از خدا پروا كنيد و مرا فرمان بريد.'),
(3059, 26, 127, 'و بر اين [پيامبرى‌] از شما مزدى نمى‌خواهم، مزد من جز بر پروردگار جهانيان نيست.'),
(3060, 26, 128, 'آيا به هر جاى بلندى نشانه‌اى- بنايى بلند- به بازى و بيهودگى بنا مى‌كنيد؟'),
(3061, 26, 129, 'و كوشكهاى بلند و استوار مى‌سازيد گويى جاويدان مى‌مانيد.'),
(3062, 26, 130, 'و چون به خشم دست مى‌گشاييد مانند گردنكشان [بيرحمانه‌] دست مى‌گشاييد.'),
(3063, 26, 131, 'پس، از خدا پروا كنيد و مرا فرمان بريد.'),
(3064, 26, 132, 'و از آن [خداى‌] پروا كنيد كه شما را بدانچه مى‌دانيد كمك و يارى داد'),
(3065, 26, 133, 'شما را به چهارپايان و پسران مدد كرد'),
(3066, 26, 134, 'و به بوستانها و چشمه‌سارها.'),
(3067, 26, 135, 'من بر شما از عذاب روزى بزرگ بيمناكم.'),
(3068, 26, 136, 'گفتند: براى ما يكسان است چه پند دهى و چه از پنددهندگان نباشى.'),
(3069, 26, 137, 'اين جز فرابافته و دروغ پيشينيان نيست.'),
(3070, 26, 138, 'و ما هرگز عذاب نخواهيم شد.'),
(3071, 26, 139, 'پس او را دروغگو انگاشتند، و آنها را نابود كرديم. همانا در اين نشانه و عبرتى است و بيشترشان مؤمن نبودند.'),
(3072, 26, 140, 'و هر آينه پروردگار تو هموست تواناى بى‌همتا و مهربان.'),
(3073, 26, 141, 'قوم ثمود نيز فرستادگان را تكذيب كردند.'),
(3074, 26, 142, 'آنگاه كه برادرشان صالح به آنان گفت: آيا [از شرك به خدا] نمى‌پرهيزيد؟'),
(3075, 26, 143, 'من براى شما پيامبرى امينم.'),
(3076, 26, 144, 'پس، از خدا پروا كنيد و مرا فرمان بريد.'),
(3077, 26, 145, 'و بر اين [پيامبرى‌] از شما مزدى نمى‌خواهم. مزد من جز بر پروردگار جهانيان نيست.'),
(3078, 26, 146, 'آيا شما را در آنچه اينجاست- نعمتها- ايمن خواهند گذاشت؟'),
(3079, 26, 147, 'در باغها و چشمه‌سارها؟'),
(3080, 26, 148, 'و كشتزارها و خرمابنها كه شكوفه‌اش- يعنى ميوه‌اش- نازك و لطيف است.'),
(3081, 26, 149, 'و از كوه‌ها استادانه- يا شادمانه- خانه‌ها مى‌تراشيد.'),
(3082, 26, 150, 'پس، از خدا پروا كنيد و مرا فرمان بريد'),
(3083, 26, 151, 'و فرمان گزافكاران را گردن منهيد،'),
(3084, 26, 152, 'آنان كه در زمين تباهكارى مى‌كنند و كار نيك و شايسته نمى‌كنند.'),
(3085, 26, 153, 'گفتند: جز اين نيست كه تو از جادوزدگانى'),
(3086, 26, 154, 'تو جز آدميى همچون ما نيستى، پس اگر از راستگويانى نشانه‌اى بيار.'),
(3087, 26, 155, 'گفت: اين ماده‌شترى است، آن را آبشخورى است و شما را نيز آبشخور روزى دانسته- ديگر-'),
(3088, 26, 156, 'و هيچ گزندى بدان مرسانيد كه شما را عذاب روزى بزرگ فراگيرد.'),
(3089, 26, 157, 'پس آن را پى كردند- كشتند- آنگاه پشيمان گشتند.'),
(3090, 26, 158, 'پس عذاب آنان را فرو گرفت. همانا در اين نشانه و عبرتى است و بيشترشان مؤمن نبودند.'),
(3091, 26, 159, 'و هر آينه پروردگار تو هموست تواناى بى‌همتا و مهربان.'),
(3092, 26, 160, 'قوم لوط نيز فرستادگان را تكذيب كردند.'),
(3093, 26, 161, 'آنگاه كه برادرشان لوط به آنها گفت: آيا [از زشتكارى‌] نمى‌پرهيزيد؟'),
(3094, 26, 162, 'من براى شما پيامبرى امينم'),
(3095, 26, 163, 'پس، از خدا پروا كنيد و مرا فرمان بريد'),
(3096, 26, 164, 'و بر اين [پيامبرى‌] از شما مزدى نمى‌خواهم مزد من جز بر پروردگار جهانيان نيست'),
(3097, 26, 165, 'چرا از مردم جهان با نران مى‌آميزيد؟!'),
(3098, 26, 166, 'و آنچه را پروردگارتان از همسرانتان براى شما آفريده است وا مى‌گذاريد، بلكه شما گروهى تجاوزكاريد.'),
(3099, 26, 167, 'گفتند: اى لوط، اگر [از اين سخنان‌] بازنايستى هر آينه از بيرون‌راندگان باشى.'),
(3100, 26, 168, 'گفت: من كردار شما را دشمنم'),
(3101, 26, 169, 'پروردگارا، مرا و خاندان مرا از آنچه مى‌كنند رهايى بخش.'),
(3102, 26, 170, 'پس او و همه خاندان او را رهانيديم،'),
(3103, 26, 171, 'مگر پيرزنى- زن لوط- را كه در بازماندگان بود.'),
(3104, 26, 172, 'سپس ديگران- بازماندگان- را هلاك كرديم'),
(3105, 26, 173, 'و بر آنها سنگبارانى باريديم، پس بد بود باران آن بيم‌كردگان.'),
(3106, 26, 174, 'همانا در اين نشانه و عبرتى است و بيشترشان مؤمن نبودند.'),
(3107, 26, 175, 'و هر آينه پروردگار تو هموست تواناى بى‌همتا و مهربان.'),
(3108, 26, 176, 'مردم ايْكه نيز فرستادگان را تكذيب كردند.'),
(3109, 26, 177, 'آنگاه كه شعيب به آنان گفت: آيا [از شرك به خدا و نافرمانى او] نمى‌پرهيزيد؟'),
(3110, 26, 178, 'من براى شما پيامبرى امينم'),
(3111, 26, 179, 'پس، از خدا پروا كنيد و مرا فرمان بريد.'),
(3112, 26, 180, 'و بر اين [پيامبرى‌] از شما مزدى نمى‌خواهم مزد من جز بر پروردگار جهانيان نيست.'),
(3113, 26, 181, 'پيمانه را تمام دهيد و از كم‌دهندگان مباشيد'),
(3114, 26, 182, 'و با ترازوى راست بسنجيد'),
(3115, 26, 183, 'و كالاهاى مردم را مكاهيد و در زمين به تباهكارى مكوشيد.'),
(3116, 26, 184, 'و از آن [خداى‌] كه شما و آفريدگان پيشين را بيافريد پروا كنيد.'),
(3117, 26, 185, 'گفتند: جز اين نيست كه تو از جادوزدگانى.'),
(3118, 26, 186, 'و تو جز آدميى همانند ما نيستى، و هر آينه تو را از دروغگويان مى‌پنداريم.'),
(3119, 26, 187, 'اگر از راستگويانى پس پاره‌اى از آسمان را بر ما فرو افكن.'),
(3120, 26, 188, 'گفت: پروردگار من بدانچه مى‌كنيد داناتر است.'),
(3121, 26, 189, 'پس تكذيبش كردند، آنگاه عذاب روز سايه- ابر سياهى كه از آن آتش مى‌باريد- آنان را فرو گرفت براستى كه آن، عذاب روزى بزرگ بود.'),
(3122, 26, 190, 'همانا در اين نشانه و عبرتى است و بيشترشان مؤمن نبودند.'),
(3123, 26, 191, 'و هر آينه پروردگار تو هموست تواناى بى‌همتا و مهربان.'),
(3124, 26, 192, 'و همانا اين [قرآن‌] فرو فرستاده پروردگار جهانيان است،'),
(3125, 26, 193, 'كه روح امين- جبرئيل- آن را فرود آورده است،'),
(3126, 26, 194, 'بر قلب تو تا از بيم‌دهندگان باشى،'),
(3127, 26, 195, 'به زبان تازى روشن.'),
(3128, 26, 196, 'و همانا [ياد و مژده و گواهى‌] آن در نوشته‌هاى پيشينيان هست.'),
(3129, 26, 197, 'آيا اين براى آنان نشانه‌اى نبود كه دانايان بنى اسرائيل آن را مى‌دانند؟'),
(3130, 26, 198, 'و اگر آن'),
(3131, 26, 199, 'و وى آن را بر ايشان مى‌خواند، بدان ايمان نمى‌آوردند.'),
(3132, 26, 200, 'اينچنين آن را در دلهاى آن بزهكاران راه داديم'),
(3133, 26, 201, 'به آن ايمان نمى‌آورند تا آنگاه كه عذاب دردناك را ببينند،'),
(3134, 26, 202, 'كه ناگهان و در حالى كه آگاه نباشند بديشان رسد.'),
(3135, 26, 203, 'پس گويند: آيا مهلتى به ما خواهند داد؟'),
(3136, 26, 204, 'آيا به عذاب ما مى‌شتابند؟'),
(3137, 26, 205, 'آيا ديدى- دانستى- كه اگر آنان را سالها برخوردارى دهيم،'),
(3138, 26, 206, 'سپس آنچه وعده داده مى‌شدند بديشان آيد،'),
(3139, 26, 207, 'آنچه برخوردارى يافته بودند سودى برايشان [در دفع عذاب‌] نخواهد داشت.'),
(3140, 26, 208, 'و [مردم‌] هيچ شهرى را نابود نكرديم مگر آنكه آن را بيم‌دهندگانى بود،'),
(3141, 26, 209, 'تا يادآورى و پندى باشد، و ما ستمكار نبوديم.'),
(3142, 26, 210, 'و اين قرآن را ديوان فرود نياوردند'),
(3143, 26, 211, 'نه آنان را سزد و نه توانند'),
(3144, 26, 212, 'همانا آنان از شنيدن [وحى و گفتار فرشتگان‌] دور داشته و بركنارشدگانند.'),
(3145, 26, 213, 'پس با خداى يكتا خدايى ديگر مخوان كه آنگاه از عذاب‌شوندگان باشى.'),
(3146, 26, 214, 'و خويشاوندان نزديك خود را بيم كن.'),
(3147, 26, 215, 'و بال [مِهر و نرمى‌] خويش را براى كسانى از مؤمنان كه تو را پيروى كردند فرود آر.'),
(3148, 26, 216, 'پس اگر تو را نافرمانى كنند، بگو: من از آنچه مى‌كنيد بيزارم.'),
(3149, 26, 217, 'و بر آن تواناى بى‌همتا و مهربان توكل كن،'),
(3150, 26, 218, 'آن كه تو را آنگاه كه [تنها به نماز شب‌] بر مى‌خيزى مى‌بيند،'),
(3151, 26, 219, 'و هم گشتن تو را در ميان سجده‌كنان- نماز گزاران-،'),
(3152, 26, 220, 'كه اوست شنوا و دانا.'),
(3153, 26, 221, 'آيا شما را آگاه كنم كه ديوان بر چه كسى فرود مى‌آيند؟'),
(3154, 26, 222, 'بر هر دروغزن بزهكارى فرود مى‌آيند،'),
(3155, 26, 223, '[ديوان‌] شنيدنيها [ى دروغ‌] را القا مى‌كنند و بيشترشان دروغگويند.'),
(3156, 26, 224, 'و [پيامبر شاعر نيست، زيرا كه‌] شاعران را گمراهان پيروى مى‌كنند.'),
(3157, 26, 225, 'مگر نديده‌اى كه آنان'),
(3158, 26, 226, 'و مى‌گويند آنچه نمى‌كنند'),
(3159, 26, 227, 'مگر آنان كه ايمان آورده و كارهاى نيك و شايسته كرده و خداى را بسيار ياد كردند و پس از آنكه ستم ديدند كين‌ستانند و آنان كه ستم كردند، زودا كه بدانند به كدام بازگشتگاه بازخواهند گشت.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طا، سين، اين آيات قرآن و كتاب روشن و روشنگر است.'),
(3161, 27, 2, 'رهنمونى و مژده است براى مؤمنان،'),
(3162, 27, 3, 'آنان كه نماز برپا مى‌دارند و زكات مى‌دهند و جهان پسين را بى‌گمان باور دارند.'),
(3163, 27, 4, 'همانا كسانى كه به جهان پسين ايمان ندارند كارهاى [باطل‌] آنان را برايشان بياراستيم پس سرگشته و كوردل‌اند.'),
(3164, 27, 5, 'آنانند كه عذاب بد و سختى دارند و آنها در آن جهان زيانكارترند.'),
(3165, 27, 6, 'و هر آينه تو را اين قرآن از نزد استواركارى دانا دهند- يعنى آن را فرا مى‌گيرى-.'),
(3166, 27, 7, '[ياد كن‌] آنگاه كه موسى به همسر خود گفت: من آتشى ديدم بزودى شما را از آن خبرى بيارم يا پاره‌اى آتش افروخته نزد شما آورم باشد كه گرم شويد.'),
(3167, 27, 8, 'پس چون به نزد آن آمد آوازش دادند كه با بركت است آن كه در آتش است و آن كه پيرامون آن است، و پاك و منزه است خداى، پروردگار جهانيان.'),
(3168, 27, 9, 'اى موسى، همانا منم خداى توانمند بى‌همتا و با حكمت'),
(3169, 27, 10, 'و عصايت را بيفكن، پس چون آن را ديد كه مى‌جنبد گويى مارى سبكخيز است، [از بيم‌] پشت كرده گريخت و واپس ننگريست. [ندا آمد:] اى موسى، مترس كه به نزد من فرستادگان نترسند'),
(3170, 27, 11, 'مگر آن كه ستم كند و سپس نيكى را پس از بدى جايگزين گرداند، كه منم آمرزگار و مهربان.'),
(3171, 27, 12, 'و دستت را در گريبانت بَر تا روشن نه از آسيب [پيسى‌] بيرون آيد [و اين‌] در ميان نِه نشانه است به سوى فرعون و گروهش، زيرا كه آنان گروهى بدكار نافرمانند.'),
(3172, 27, 13, 'پس چون نشانه‌هاى ما روشن و هويدا بديشان آمد، گفتند: اين جادويى است آشكار.'),
(3173, 27, 14, 'و آنها را از روى ستم و سركشى انكار كردند در حالى كه دلهاشان بدانها يقين داشت پس بنگر كه سرانجام تباهكاران چگونه بود.'),
(3174, 27, 15, 'و هر آينه داوود و سليمان را دانشى بداديم و گفتند: سپاس و ستايش خداى را كه ما را بر بسيارى از بندگان مؤمن خود برترى داد.'),
(3175, 27, 16, 'و سليمان از داوود ميراث برد، و گفت: اى مردم، ما را سخن مرغان آموختند و ما را از هر چيزى [بهره‌اى‌] دادند. همانا اين است فزون‌بخشى و برترى آشكار.'),
(3176, 27, 17, 'و سليمان را سپاهيانش از پريان و آدميان و مرغان گرد آمدند، پس آنها [از پراكندگى و بى‌نظمى‌] باز داشته مى‌شدند.'),
(3177, 27, 18, 'تا آنگاه كه بر- از بالاى- وادى مورچگان فرا آمدند، مورچه‌اى گفت: اى مورچگان به خانه‌هاى خود در رويد مبادا سليمان و سپاهيانش پايمالتان كنند در حالى كه آگاه نباشند.'),
(3178, 27, 19, 'پس [سليمان‌] از گفتار آن مورچه به لبخندى خندان شد، و گفت: پروردگارا، مرا الهام كن- توفيق ده- كه نعمتت را كه بر من و بر پدر و مادرم ارزانى داشته‌اى سپاس دارم و كارى نيك و شايسته كنم كه آن را مى‌پسندى و مرا به بخشايش خود در ميان بندگان نيك و شايسته‌ات در آر.'),
(3179, 27, 20, 'و مرغان را جويا شده گفت: مرا چيست كه هدهد را نمى‌بينم يا مگر از غايبان است؟'),
(3180, 27, 21, 'هر آينه او را شكنجه‌اى سخت كنم يا سرش را ببرم يا مرا حجتى- عذرى- روشن و آشكار بيارد.'),
(3181, 27, 22, 'پس درنگش- درنگ سليمان يا هدهد- ديرى نپاييد [كه هدهد آمد] و گفت: چيزى دريافته‌ام كه تو درنيافته‌اى، و از سبا- شهرى در يمن- برايت خبرى درست آورده‌ام.'),
(3182, 27, 23, 'من زنى- بلقيس نام- را يافتم كه بر آنان پادشاهى مى‌كند و از هر چيزى- كه پادشاهان را به كار آيد يا از هر نعمتى- به او داده‌اند و او را تختى است بزرگ.'),
(3183, 27, 24, 'او را و قومش را يافتم كه به جاى خداى يكتا خورشيد را سجده مى‌كنند و شيطان كارهاى [باطل‌] آنان را برايشان بياراسته و آنها را از راه [راست‌] بگردانيده پس به راه راست نيستند،'),
(3184, 27, 25, 'تا سجده نكنند خدايى را كه نهانيها را كه در آسمانها و زمين است بيرون مى‌آورد و آنچه را پنهان مى‌داريد و آنچه را آشكار مى‌كنيد مى‌داند'),
(3185, 27, 26, 'خداى يكتا، خدايى جز او نيست، پروردگار عرش بزرگ است.'),
(3186, 27, 27, '[سليمان‌] گفت: خواهيم ديد كه راست مى‌گويى يا از دروغگويانى.'),
(3187, 27, 28, 'اين نامه مرا ببر و آن را به نزد آنها بيفكن سپس از آنها روى بگردان- به يك سو شو- و بنگر چه پاسخ گويند.'),
(3188, 27, 29, '[آن زن‌] گفت: اى مهتران، نامه‌اى گرامى به سوى من افكنده شده'),
(3189, 27, 30, 'آن از سليمان و به نام خداى بخشاينده مهربان است'),
(3190, 27, 31, '[به اين مضمون‌] كه بر من برترى مجوييد و به نزد من آييد گردن‌نهادگان.'),
(3191, 27, 32, '[و] گفت: اى مهتران، مرا در كارم رأى و نظر دهيد، كه من گزارنده هيچ كارى نبوده‌ام تا اينكه شما در نزد من حاضر باشيد.'),
(3192, 27, 33, 'گفتند: ما نيرومندان پرتوان و خداوندان پيكار سختيم، و فرمان تو راست بنگر تا چه فرمايى.'),
(3193, 27, 34, 'گفت: پادشاهان هر گاه به آبادى و شهرى در آيند تباهش كنند و عزيزان مردم آن را خوار گردانند. [آرى‌] و چنين مى‌كنند.'),
(3194, 27, 35, 'و من به سوى ايشان پيشكشى فرستم پس مى‌نگرم كه فرستادگان با چه چيز باز مى‌گردند- چه پاسخى آورند-.'),
(3195, 27, 36, 'پس چون [فرستاده‌] نزد سليمان آمد، [سليمان‌] گفت: آيا مرا به مال يارى مى‌كنيد؟! آنچه خداى مرا داده بهتر است از آنچه شما را داده است، بلكه شما به پيشكش خود شادمانيد.'),
(3196, 27, 37, 'سوى آنان باز گرد كه هر آينه با سپاهى بديشان در آييم كه آنان را در برابر آن تاب و توان نباشد و همانا از آنجا به خوارى و سرافكندگى بيرونشان رانيم.'),
(3197, 27, 38, '[سپس‌] گفت: اى مهتران، كدامتان تخت او را پيش از آنكه گردن نهاده نزد من آيند برايم مى‌آورد؟'),
(3198, 27, 39, 'ديوى از پريان گفت: من آن را پيش از آنكه از جاى خويش برخيزى نزد تو آرم و من بر اين كار نيرومند و امينم.'),
(3199, 27, 40, 'آن كه دانشى از كتاب- كتابى آسمانى يا لوح محفوظ- نزدش بود گفت: من آن را پيش از آنكه چشم بر هم زنى- يا چون در چيزى نگرى تا چشم از آن بردارى- برايت مى‌آورم. پس چون آن را نزد خود قرار يافته ديد، گفت: اين از فزون‌بخشى پروردگارم است تا بيازمايدم كه سپاس مى‌دارم يا ناسپاسى مى‌كنم، و هر كه سپاس گزارد جز اين نيست كه به سود خويش سپاس مى‌گزارد، و هر كه ناسپاسى كند همانا پروردگار من بى‌نياز است و بزرگوار.'),
(3200, 27, 41, 'گفت: تخت او را برايش ناشناخته سازيد تا بنگريم آيا [به بازشناختن آن‌] راه مى‌برد يا از آنهاست كه راه نمى‌برند.'),
(3201, 27, 42, 'پس چون بيامد به او گفته شد: آيا تخت تو چنين است؟ گفت: گويى اين همان است. و ما را پيش از اين دانش داده‌اند- بر قدرت و حقانيت سليمان- و ما گردن‌نهاده بوده‌ايم.'),
(3202, 27, 43, 'و آنچه جز خدا مى‌پرستيد او را [از پرستش خدا] بازداشته بود، زيرا كه از گروه كافران بود.'),
(3203, 27, 44, 'به او گفته شد: به صحن كاخ درآى چون آن را بديد پنداشت كه آبگيرى ژرف است. و جامه از دو ساق خويش بالا كشيد. [سليمان‌] گفت: اين صحنى است صاف و هموار، [ساخته‌] از آبگينه- آب نيست-. گفت: پروردگارا، من به خود ستم كردم و [اينك‌] با سليمان خداى، پروردگار جهانيان، را گردن نهادم.'),
(3204, 27, 45, 'و هر آينه به سوى قوم ثمود برادرشان صالح را فرستاديم كه خداى يكتا را بپرستيد آنگاه دو گروه شدند كه با يكديگر ستيزه مى‌كردند.'),
(3205, 27, 46, '[صالح‌] گفت: اى قوم من، چرا پيش از نيكى- توبه- بدى- عذاب- را به شتاب مى‌خواهيد؟ چرا از خدا آمرزش نمى‌خواهيد تا شايد درخور رحمت شويد؟'),
(3206, 27, 47, 'گفتند: ما تو و آنها را كه با تواند به شگون بد گرفته‌ايم. گفت: شگون بدتان نزد خداوند است بلكه شما گروهى گرفتار آزمونيد- شما را مى‌آزمايند-.'),
(3207, 27, 48, 'و در آن شهر نه مرد- يا نه گروه- بودند كه در زمين تباهى مى‌كردند و شايسته‌كار و درست‌كردار نبودند.'),
(3208, 27, 49, 'گفتند: به خدا سوگند خوريد- يا: در حالى كه به خدا سوگند خوردند گفتند- كه بر او و خاندانش شبيخون بريم، سپس به خونخواه او گوييم: ما هلاك شدن [او و] خاندان او را نديده‌ايم- يا حاضر نبوده‌ايم- و همانا ما راستگوييم.'),
(3209, 27, 50, 'و نيرنگى ساختند، و ما نيز نيرنگى ساختيم ولى [نيرنگ ما را] در نمى‌يافتند.'),
(3210, 27, 51, 'پس بنگر كه سرانجام نيرنگشان چسان بود، آنها و قومشان همه را هلاك كرديم'),
(3211, 27, 52, 'اينك خانه‌هايشان به ستمى كه كردند ويران و تهى مانده است. هر آينه در آن براى گروهى كه بدانند نشانه و عبرتى است.'),
(3212, 27, 53, 'و آنان را كه ايمان آوردند و پرهيزگارى مى‌كردند رهانيديم.'),
(3213, 27, 54, 'و لوط را [ياد كن‌] آنگاه كه به قوم خود گفت: آيا پيرامون زشتكارى مى‌گرديد و حال آنكه مى‌بينيد- زشتى آن را درمى‌يابيد، يا در ديد يكديگر اين زشتكارى مى‌كنيد-؟!'),
(3214, 27, 55, 'آيا شما به كامرانى به جاى زنان به مردان مى‌گراييد؟! بلكه شما گروهى نادانيد.'),
(3215, 27, 56, 'پس پاسخ قومش جز اين نبود كه گفتند: خاندان لوط را از شهر خود بيرون كنيد، كه آنان مردمى‌اند كه پاكى مى‌ورزند.'),
(3216, 27, 57, 'پس او و خاندانش را رهانيديم جز زنش كه مقدر كرديم از بازماندگان [و هلاك شدگان‌] باشد.'),
(3217, 27, 58, 'و بر آنها بارانى [از سنگ‌] باريديم پس بد است باران بيم‌شدگان.'),
(3218, 27, 59, 'بگو: سپاس و ستايش خداى راست و سلام بر بندگان او آنان كه برگزيدشان آيا خدا بهتر است يا آنچه انباز [خدا] مى‌گيرند؟'),
(3219, 27, 60, 'يا آن كه آسمانها و زمين را آفريد و برايتان از آسمان آبى فرو آورد كه بدان بوستانهاى زيبا و خرم رويانيديم كه شما را نرسد درختش را برويانيد [بهتر است يا آنچه انباز مى‌گيرند]؟ آيا با خداى يكتا خدايى هست؟ بلكه آنان مردمى كجروند.'),
(3220, 27, 61, 'يا آن كه زمين را آرامگاه- يا: آرام- قرار داد و در ميان آنها جويها روان كرد و براى آن لنگرها- كوه‌ها- ساخت و ميان دو دريا بندى نهاد- تا به يكديگر نياميزند- [بهتر است يا آنچه انباز مى‌گيرند]؟ آيا با خداى يكتا خدايى هست؟ بلكه بيشترشان نمى‌دانند.'),
(3221, 27, 62, 'يا آن كه درمانده را چون او را بخواند پاسخ دهد و گزند و آسيب را از او بردارد و شما را نمايندگان خود- يا جانشين پيشينيان- در زمين سازد [بهتر است يا آنچه انباز مى‌گيرند]؟ آيا با خداى يكتا خدايى هست؟ اندكى ياد مى‌كنند و پند مى‌پذيرند.'),
(3222, 27, 63, 'يا آن كه شما را در تاريكيهاى خشكى و دريا راه مى‌نمايد، و آن كه بادها را پيشاپيش رحمت خود- يعنى باران- مژدگان فرستد [بهتر است يا آنچه انباز مى‌گيرند]؟ آيا با خداى يكتا خدايى هست؟ برتر است خداى از آنچه انباز مى‌گيرند.'),
(3223, 27, 64, 'يا آن كه آفرينش را آغاز مى‌كند سپس آن را باز مى‌گرداند، و آن كه شما را از آسمان و زمين روزى مى‌دهد [بهتر است يا آنچه انباز مى‌گيرند]؟ آيا با خداى يكتا خدايى هست؟ بگو: حجت خود را بياوريد اگر راستگوييد.'),
(3224, 27, 65, 'بگو: كسى در آسمانها و زمين نهان- غيب- را نمى‌داند مگر خدا. و نمى‌دانند كه كى برانگيخته مى‌شوند.'),
(3225, 27, 66, 'مگر دانش آنان درباره جهان پسين- رستاخيز- به كمال رسيده است [كه آن را انكار مى‌كنند]؟ [نه،] بلكه از آن در شك‌اند، بلكه از [دانش و دريافت‌] آن كورند.'),
(3226, 27, 67, 'و كسانى كه كافر شدند گفتند: آيا هنگامى كه ما و پدرانمان خاك شديم، آيا ما را [از گور] بيرون مى‌آورند؟!'),
(3227, 27, 68, 'هر آينه اين را به ما و پيش از اين به پدران ما، وعده داده‌اند اين نيست مگر افسانه‌هاى پيشينيان.'),
(3228, 27, 69, 'بگو: در زمين بگرديد، پس بنگريد كه سرانجام بزهكاران چگونه بود.'),
(3229, 27, 70, 'و بر آنان اندوه مخور و از نيرنگهايى كه مى‌سازند دلتنگ مباش.'),
(3230, 27, 71, 'و گويند: اين وعده- عذاب در دنيا يا رستاخيز- كى خواهد بود، اگر راستگوييد؟'),
(3231, 27, 72, 'بگو: شايد برخى از آنچه به شتاب مى‌خواهيد بزودى به شما فرا رسد.'),
(3232, 27, 73, 'هر آينه پروردگار تو خداوند افزون‌بخشى بر مردم است ولى بيشترشان سپاس نمى‌گزارند.'),
(3233, 27, 74, 'و همانا پروردگار تو آنچه را سينه‌هاشان پنهان مى‌دارد- در دل نهان مى‌دارند- و آنچه را آشكار مى‌كنند مى‌داند.'),
(3234, 27, 75, 'و هيچ ناپيدايى در آسمان و زمين نيست مگر آنكه در كتابى روشن- لوح محفوظ- هست.'),
(3235, 27, 76, 'همانا اين قرآن بر فرزندان اسرائيل بيشتر آنچه را كه در آن اختلاف مى‌كنند باز مى‌گويد- خبر مى‌دهد-'),
(3236, 27, 77, 'و هر آينه مؤمنان را رهنمونى و بخشايشى است.'),
(3237, 27, 78, 'همانا پروردگار تو به حكم خويش ميانشان داورى خواهد كرد، و اوست توانمند بى‌همتا و دانا.'),
(3238, 27, 79, 'پس بر خداى توكل كن- كارها را به خدا واگذار- كه تو بر حق روشن و هويدايى.'),
(3239, 27, 80, 'تو نتوانى كه مردگان را بشنوانى، و نه به كران آواى دعوت را بشنوانى آنگاه كه پشت كرده برمى‌گردند.'),
(3240, 27, 81, 'و تو راهنماى كوران- كوردلان- از گمراهيشان نيستى نشنوانى مگر كسانى را كه به آيات- نشانه‌ها و سخنان- ما ايمان دارند و مسلمانند- گردن‌نهاده و فرمانبردارند-.'),
(3241, 27, 82, 'و چون [هنگام آن رسد كه‌] آن گفتار- وعده ما به عذاب- بر آنان واقع شود، براى آنان از زمين جنبنده‌اى بيرون آريم كه با آنان اين سخن گويد زيرا كه مردم به نشانه‌هاى ما يقين- باور بى‌گمان- نمى‌داشتند.'),
(3242, 27, 83, 'و روزى كه از هر امتى گروهى از آنها كه نشانه‌هاى ما را دروغ شمردند برانگيزيم و فراهم آريم، پس [از پراكنده شدن‌] بازداشته شوند.'),
(3243, 27, 84, 'تا آنگاه كه [به پيشگاه خدا] آيند، گويد: آيا نشانه‌هاى مرا دروغ انگاشتيد و حال آنكه بدانها دانشى نداشتيد، يا چه بود آنچه مى‌كرديد؟'),
(3244, 27, 85, 'و آن گفتار- فرمان عذاب- بر آنان به سزاى ستمى كه كردند واقع شود و هيچ سخنى نگويند.'),
(3245, 27, 86, 'آيا نديده‌اند كه ما شب را پديد كرديم تا در آن بيارامند و روز را روشن ساختيم [تا در آن ببينند و به كار پردازند]؟ هر آينه در اين براى گروهى كه ايمان آورند نشانه‌هاست.'),
(3246, 27, 87, 'و روزى كه در صور دميده شود پس، هر كه در آسمانها و هر كه در زمين است بهراسد، مگر آنكس كه خداى خواهد و همه سرافكنده و فروتن به نزد او آيند.'),
(3247, 27, 88, 'و كوه‌ها را بينى، پندارى كه بر جاى ايستاده‌اند و حال آنكه همچون ابر مى‌روند، كار خداست كه هر چيزى را استوار ساخته. همانا او بدانچه مى‌كنيد آگاه است.'),
(3248, 27, 89, 'هر كه نيكى آرد پس او را بهتر از آن باشد و آنان در آن روز از هراس ايمن‌اند.'),
(3249, 27, 90, 'و هر كه بدى آرد، پس چهره‌شان را به آتش درافكنند- به رو در آتش افكنده‌شوند- آيا جز آنچه مى‌كرديد پاداش داده مى‌شويد؟'),
(3250, 27, 91, '[بگو:] جز اين نيست كه فرمان يافته‌ام كه خداوند اين شهر را، همان كه آن را بشكوه داشت و همه چيز از آن اوست، بپرستم، و فرمان يافته‌ام كه از مسلمانان- گردن‌نهادگان- باشم'),
(3251, 27, 92, 'و قرآن را [بر شما] بخوانم. پس هر كه راه يابد به سود خويش راه يافته، و هر كه گمراه شود، بگو: جز اين نيست كه من از بيم‌كنندگانم.'),
(3252, 27, 93, 'و بگو: سپاس و ستايش خداى راست. زودا كه شما را نشانه‌هاى- قدرت يا عذاب- خويش بنمايد پس آنها را خواهيد شناخت. و پروردگار تو از آنچه مى كنيد غافل نيست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طا، سين، ميم.'),
(3254, 28, 2, 'اين آيات كتاب روشن و روشنگر است.'),
(3255, 28, 3, 'بر تو از داستان موسى و فرعون براى مردمى كه باور مى‌دارند براستى و درستى مى‌خوانيم.'),
(3256, 28, 4, 'همانا فرعون در آن سرزمين- مصر- برترى جست و مردمش را گروه گروه ساخت. گروهى از آنان- بنى اسرائيل- را زبون و ناتوان مى‌گرفت، پسرانشان را سر مى‌بريد و زنانشان را [براى خدمت‌] زنده مى‌گذاشت. همانا او از تباهكاران بود.'),
(3257, 28, 5, 'و مى‌خواستيم بر كسانى كه در آن سرزمين ناتوان شمرده مى‌شدند- يعنى فرزندان اسرائيل- منت نهيم و آنان را پيشوايان كنيم و آنان را وارثان سازيم'),
(3258, 28, 6, 'و آنان را در آن سرزمين جاى و توان دهيم و به فرعون و هامان- وزير او- و سپاهيانشان از سوى آنان- بنى اسرائيل- آنچه را كه از آن مى‌ترسيدند بنماييم.'),
(3259, 28, 7, 'و به مادر موسى وحى كرديم كه او را شير ده، و چون بر او بيمناك شدى به دريايش بيفكن و نترس و اندوه مدار، كه ما او را به تو باز مى‌گردانيم و از پيامبرانش مى‌سازيم.'),
(3260, 28, 8, 'پس خاندان فرعون او را برگرفتند تا سرانجام آنان را دشمنى باشد و [مايه‌] اندوهى همانا فرعون و هامان و سپاهيان آن دو خطاكار بودند.'),
(3261, 28, 9, 'و زن فرعون گفت: [اين كودك‌] مرا و تو را روشنى چشم است، او را مكشيد، اميد است كه ما را سودى دهد يا او را به فرزندى گيريم و آنها در نمى يافتند- كه سرانجام اين كودك چيست-.'),
(3262, 28, 10, 'و مادر موسى را دل [از ترس و اندوه‌] تهى گشت، و هر آينه نزديك بود كه آن- داستان موسى- را فاش سازد اگر نه آن بود كه دل او را استوار كرديم- بر صبر و ثبات- تا از باوردارندگان [وعده ما] باشد.'),
(3263, 28, 11, 'و به خواهر او گفت: از پى او برو پس وى از دور به او مى‌نگريست بى‌آنكه آنان [پى‌گيرى وى را] دريابند.'),
(3264, 28, 12, 'و دايگان- يا شيرخوارگاه‌ها يعنى پستانهاى دايگان- را از پيش- پيش از رسيدن خواهرش- بر او حرام كرده بوديم- پستان آنان را به دهان نمى‌گرفت-، پس [خواهر موسى‌] گفت: آيا شما را به خانواده‌اى راهنمايى كنم كه سرپرستى [و دايگى‌] او را براى شما بپذيرند و نيكخواه او باشند؟'),
(3265, 28, 13, 'پس او را به مادرش بازگردانيديم تا چشم وى روشن گردد و اندوه نخورد و تا بداند كه وعده خدا راست است ولى بيشتر آنان نمى‌دانند.'),
(3266, 28, 14, 'و چون به نيرومندى خود رسيد و جوانى توانمند شد او را حكمى- حكمت: درستى انديشه و گفتار و كردار، يا نبوت- و دانشى داديم، و نيكوكاران را چنين پاداش دهيم.'),
(3267, 28, 15, 'و به آن شهر- مصر- هنگام غفلت مردمش- هنگام تعطيل كه به لهو و بازى مشغول بودند- در آمد، و دو مرد را ديد كه با يكديگر پيكار مى‌كردند، اين يك از پيروان وى- بنى اسرائيل- بود و آن يك از دشمنانش- فرعونيان-. پس آن كه از پيروانش بود بر آن كه از دشمنانش بود از وى يارى خواست. پس موسى او'),
(3268, 28, 16, 'گفت: پروردگارا، من به خود ستم كردم- به كشتن قبطى-، مرا بيامرز پس او را آمرزيد، كه اوست آمرزگار و مهربان.'),
(3269, 28, 17, 'گفت: پروردگارا، به پاس آنكه بر من نعمت ارزانى داشتى هرگز پشتيبان بزهكاران نخواهم شد.'),
(3270, 28, 18, 'پس در آن شهر ترسان و نگران و انديشناك- از اينكه خبر آشكار شود و او را بگيرند و بكشند- مى‌گشت كه ناگاه همان كه ديروز از او يارى خواسته بود باز هم از او فرياد خواست، موسى به او گفت: همانا تو آشكارا گمراهى- كه هر روز با كسى نزاع مى‌كنى-.'),
(3271, 28, 19, 'و چون خواست تا به آن كه دشمن هر دوشان بود دست دراز كند، [فرياد خواه‌] گفت: اى موسى، آيا مى‌خواهى مرا بكشى چنانكه ديروز يكى را كشتى؟ تو جز اين نمى‌خواهى كه در زمين ستمگرى باشى و نمى‌خواهى از شايسته‌كاران باشى.'),
(3272, 28, 20, 'و مردى- از دوستداران موسى- از آن سر شهر شتابان بيامد، گفت: اى موسى، همانا مهتران [شهر] در باره تو رأى مى‌زنند تا بكشندت، پس بيرون شو، كه من تو را از نيكخواهانم.'),
(3273, 28, 21, 'پس، از آن [شهر] ترسان و نگران بيرون رفت، گفت: پروردگارا، مرا از گروه ستمكاران رهايى بخش.'),
(3274, 28, 22, 'و چون به سوى مَدْيَن روى نهاد، گفت: اميد است پروردگارم مرا راه راست- راه خير و رستگارى، يا راهى كه به مَدْيَن بينجامد- بنمايد.'),
(3275, 28, 23, 'و چون به آب مَدْيَن رسيد گروهى از مردم را بر آن يافت كه [گوسفندان را] آب مى دادند، و فروتر- يا نزديك- آنان دو زن را يافت كه [گوسفندان خود را از آب‌] باز مى‌داشتند، گفت: كار شما چيست؟ گفتند: ما [گوسفندان را] آب ندهيم تا اين شبانان [گوسفندان خود را] بازبرند، و پدر ما پيرى است كهنسال.'),
(3276, 28, 24, 'پس [گوسفندان را] براى آنها آب داد، آنگاه به سايه بازگشت و گفت: پروردگارا، من بدانچه از نيكى- روزى، نان- به سويم فرستى نيازمندم.'),
(3277, 28, 25, 'پس يكى از آن دو زن كه با شرم و آزرم راه مى‌رفت نزد او آمد، گفت: پدرم تو را مى خواند تا تو را مزد آنكه براى ما آب دادى بدهد. پس چون به نزد او- پدر آنها، شعيب- آمد و داستان [خود] را بر او بر گفت، [شعيب‌] گفت: بيم مدار كه از گروه ستمكاران رهايى يافتى.'),
(3278, 28, 26, 'يكى از آن دو گفت: اى پدر، او را به مزد گير، زيرا بهترين كسى كه به مزد گيرى، آن است كه نيرومند و امين باشد.'),
(3279, 28, 27, 'گفت: مى‌خواهم يكى از اين دو دخترم را به همسرى تو دهم بر اين [شرط] كه هشت سال مزدبگير باشى، و اگر ده سال را به پايان برى آن به خواست و اختيار توست و نمى‌خواهم كه بر تو سخت گيرم، زودا كه به خواست خدا مرا از نيكان و شايستگان يابى.'),
(3280, 28, 28, 'گفت: اين [عهد] ميان من و توست، هر يك از دو مدت را كه به پايان برم، تعدى و ستمى بر من نباشد- بازخواست نشوم-، و خدا بر آنچه مى‌گوييم گواه و نگاهبان است.'),
(3281, 28, 29, 'پس چون موسى مدت را به پايان برد و با خانواده خويش روان گشت از سوى كوه طور آتشى بديد، به خانواده خود گفت: درنگ كنيد كه من آتشى ديدم شايد از آن خبرى آورم يا اخگرى از آتش، تا گرم شويد.'),
(3282, 28, 30, 'و چون نزد آن'),
(3283, 28, 31, 'و چوبدست خود را بيفكن، پس چون آن را ديد كه مانند مارى سبكخيز مى‌جنبد پشت كرده برگشت و به واپس ننگريست. [خداى گفت:] اى موسى، روى آر و مترس كه تو از ايمنانى.'),
(3284, 28, 32, 'دستت را در گريبانت بر تا سپيد، بى‌آسيبى،- بى‌پيسى-، بيرون آيد و دست خود را [براى آرامش و آسودگى‌] از بيم، به سوى خويش به هم آر اينها دو حجت روشن از پروردگار تو به سوى فرعون و مهتران اوست، كه آنان مردمى بدكار و نافرمانند.'),
(3285, 28, 33, 'گفت: پروردگارا، من از آنان كسى را كشته‌ام و مى‌ترسم كه مرا بكشند'),
(3286, 28, 34, 'و برادرم هارون از من به زبان- سخن گفتن- شيواتر است، پس او را با من به مددكارى بفرست كه مرا تصديق كند- راستى و درستى دعوى مرا بيان كند-، زيرا مى‌ترسم مرا دروغگو شمرند.'),
(3287, 28, 35, 'گفت: بزودى بازوى تو را به برادرت استوار سازيم و براى شما هر دو حجتى قرار دهيم پس به سبب نشانه‌هاى ما به شما دست نيابند شما و هر كه شما را پيروى كند پيروزيد.'),
(3288, 28, 36, 'پس چون موسى با نشانه‌هاى روشن و آشكار ما بديشان آمد، گفتند: اين جز جادويى فرابافته و دروغين نيست، و اين [ادعا و دعوت‌] را در ميان پدران پيشينمان نشنيده‌ايم- يعنى ادعا و دعوتى كه مى‌كند و آنچه آورده دينى است نوظهور كه از پدران پيشين ما برايمان نقل نشده است-.'),
(3289, 28, 37, 'و موسى گفت: پروردگار من داناتر است به آن كس كه هدايت را از نزد او آورده و آن كه سرانجام سراى- سرانجام نيك در اين جهان يا رستگارى در آن جهان- از آن اوست همانا ستمكاران رستگار نشوند.'),
(3290, 28, 38, 'و فرعون گفت: اى مهتران، من براى شما خدايى جز خود ندانم پس اى هامان- وزير فرعون- برايم بر گِل آتش برافروز- آجر بپز- و برايم كوشكى [با طارمى بلند] بساز [تا بالا روم‌] شايد به خداى موسى ديده‌ور شوم، كه من او را از دروغگويان مى‌پندارم.'),
(3291, 28, 39, 'و او و سپاهيانش به ناروا سركشى و بزرگ‌منشى كردند و پنداشتند كه به سوى ما بازگردانده نمى‌شوند.'),
(3292, 28, 40, 'پس او و سپاهيانش را گرفتيم و به دريا افكنديمشان، پس بنگر كه سرانجام ستمكاران چگونه بود.'),
(3293, 28, 41, 'و آنان را پيشوايانى گردانيديم كه به آتش دوزخ مى‌خوانند- يعنى به كفرى كه به آتش دوزخ مى‌انجامد دعوت مى‌كنند-، و روز رستاخيز يارى نشوند.'),
(3294, 28, 42, 'و در اين جهان از پى آنها لعنت فرستاديم، و روز رستاخيز از زشترويانند.'),
(3295, 28, 43, 'و هر آينه موسى را، پس از آنكه نسلهاى پيشين را نابود كرديم، كتاب داديم تا براى مردم بينش‌ها و رهنمونى و بخشايشى باشد شايد ياد كنند و پند گيرند.'),
(3296, 28, 44, 'و تو در جانب غربى [كوه طور] نبودى آنگاه كه به موسى فرمان [پيامبرى‌] داديم و تو از حاضران نبودى،'),
(3297, 28, 45, 'و ليكن ما نسلهايى آفريديم كه عمرشان دراز شد- و اين خبرها كهنه و منحرف شد- [از آن رو داستان موسى را بر تو باز گفتيم‌] و تو در ميان مردم مَدْيَن مقيم نبودى [تا از خبر موسى آگاه شوى‌] كه اينك آيات ما را بر اينان- مشركان مكه- مى‌خوانى ولى ماييم كه فرستندگان بوديم- تو را فرستاديم و اين داستانها را به تو خبر داديم-.'),
(3298, 28, 46, 'و تو در كنار طور نبودى آنگاه كه [موسى را] ندا داديم ولى [اين پيامبرى‌] بخشايشى است از پروردگار تو تا مردمى را كه پيش از تو بيم‌دهنده‌اى بديشان نيامده بترسانى شايد ياد آرند و پند گيرند.'),
(3299, 28, 47, 'و اگر نه اين بود كه چون بديشان به سزاى آنچه دستهايشان پيش فرستاده است مصيبتى- عقوبتى- رسد، پس [حجت آرند و] گويند: پروردگارا، چرا به سوى ما پيامبرى نفرستادى تا آيات تو را پيروى كنيم و از مؤمنان باشيم؟ [پيامبرى به سوى آنها نمى‌فرستاديم‌].'),
(3300, 28, 48, 'پس چون حق- قرآن- از نزد ما بديشان آمد، [كافران مكه‌] گفتند: چرا مانند آنچه به موسى داده شده به او داده نشده است؟ آيا بدانچه به موسى پيش از اين داده شد كافر نشدند؟ گفتند: دو جادويند- تورات و قرآن- پشتيبان يكديگر، و گفتند: ما به همه آنها كافريم.'),
(3301, 28, 49, 'بگو: اگر راست مى‌گوييد، كتابى از نزد خداوند بياريد كه رهنمون‌تر از آن دو باشد تا آن را پيروى كنم.'),
(3302, 28, 50, 'پس اگر تو را پاسخ ندادند، بدان كه از هوسها و كامهاى خويش پيروى مى‌كنند و كيست گمراه‌تر از آن كس كه بى‌رهنمونى از سوى خدا كام و هوس خويش را پيروى كند؟ همانا خدا گروه ستمكاران را راه ننمايد.'),
(3303, 28, 51, 'و هر آينه براى آنان اين سخن- قرآن- را پياپى و پيوسته كرديم- آيه‌اى پس از آيه‌اى و سوره‌اى پس از سوره‌اى فرستاديم- تا شايد به ياد آرند و پند گيرند'),
(3304, 28, 52, 'آنان كه پيش از اين'),
(3305, 28, 53, 'و چون بر آنان خوانده شود گويند: به آن ايمان داريم كه آن راست و درست است از پروردگار ما، همانا پيش از آن مسلمان- گردن‌نهاده و فرمانبردار- بوديم'),
(3306, 28, 54, 'اينانند كه براى آن شكيبايى كه كردند مزدشان را دو بار دهند، و ايشانند كه بدى را به نيكى دفع مى‌كنند و از آنچه روزيشان داده‌ايم انفاق مى‌كنند.'),
(3307, 28, 55, 'و چون سخن بيهوده بشنوند از آن روى بگردانند و گويند: ما راست كردارهاى ما و شما راست كردارهاى شما. سلام بر شما، ما خواستار نادانان نيستيم.'),
(3308, 28, 56, 'همانا تو نتوانى هر كه را دوست دارى راه نمايى بلكه خداست كه هر كه را خواهد راه مى‌نمايد، و او به راه‌يافتگان داناتر است.'),
(3309, 28, 57, 'و گفتند: اگر با تو راه راست را پيروى كنيم از سرزمينمان ربوده شويم. آيا آنان را در حرمى امن جاى نداديم كه به سوى آن ميوه‌هاى هر چيز، كه روزيى است از نزد ما، آورده مى‌شود؟ ولى بيشترشان نمى‌دانند.'),
(3310, 28, 58, 'و چه بسيار [مردم‌] آباديها و شهرها را نابود كرديم كه در زندگانى خويش از خوشى سركشى و نافرمانى نمودند، و اينك اين خانه‌هايشان است كه پس از آنان جز اندكى در آنها نشيمن نگرفتند و ما وارث آنان بوديم.'),
(3311, 28, 59, 'و پروردگار تو نابودكننده شهرها و آباديها نيست مگر تا آنكه در مركز آنها پيامبرى برانگيزد كه آيات ما را بر آنان بخواند و ما نابودكننده شهرها نبوديم مگر آنكه مردم آنها ستمكار بودند.'),
(3312, 28, 60, 'آنچه به شما داده شده كالاى زندگانى دنيا و آرايش آن است، و آنچه نزد خداوند است بهتر و پايدارتر است، آيا خرد را كار نمى‌بنديد؟'),
(3313, 28, 61, 'آيا آن كس كه به او وعده نيكو داده‌ايم و او بدان خواهد رسيد مانند كسى است كه وى را به برخوردارى از زندگى اين جهان بهره‌مندش كرده‌ايم سپس در روز رستاخيز از حاضرشدگان [براى حساب و كيفر] است؟'),
(3314, 28, 62, 'و روزى كه [خداوند] آنان را بخواند و گويد: كجايند شريكانى كه براى من مى پنداشتيد؟'),
(3315, 28, 63, 'آنان كه آن گفتار- فرمان عذاب- بر آنان سزا گشته- يعنى پرستيده‌ها-، گويند: خداوندا، اينانند كسانى كه گمراهشان كرديم گمراهشان كرديم همچنانكه خود گمراه بوديم، [از آنها] به سوى تو بيزارى مى‌جوييم، آنان ما را نمى‌پرستيدند.'),
(3316, 28, 64, 'و گفته شود: شريكان خود- بتان- را بخوانيد، پس بخوانندشان ولى آنان را پاسخى ندهند و عذاب [دوزخ‌] را ببينند، و آرزو كنند كه كاش راه‌يافته بودند.'),
(3317, 28, 65, 'و روزى كه [خداوند] آنان را بخواند و گويد: پيامبران را چه پاسخ داديد؟'),
(3318, 28, 66, 'پس در آن روز خبرها بر آنان پوشيده شود و از يكديگر هم نپرسند- كه چه پاسخ گوييم، زيرا همه درمانده باشند-.'),
(3319, 28, 67, 'و اما آن كه توبه كرده و ايمان آورده و كار نيك و شايسته كرده پس اميد است كه از رستگاران باشد.'),
(3320, 28, 68, 'و پروردگارا تو آنچه خواهد مى‌آفريند و برمى‌گزيند. [اما] آنان را [توان‌] برگزيدن نيست. پاك و منزه است خداى، و از آنچه انباز مى‌گيرند برتر است.'),
(3321, 28, 69, 'و پروردگار تو آنچه را سينه‌هاشان- دلهاشان- نهان مى‌دارد و آنچه را [به زبان‌] آشكار مى‌سازند مى‌داند.'),
(3322, 28, 70, 'و اوست خداى يكتا، خدايى جز او نيست، او راست سپاس و ستايش در اين جهان و آن جهان و او راست فرمان، و به سوى او بازگردانيده مى‌شويد.'),
(3323, 28, 71, 'بگو: مرا گوييد كه اگر خداوند شب را بر شما تا روز رستاخيز هميشگى و پاينده گرداند، كدام خدايى است جز خداى يكتا كه شما را روشنايى بياورد؟ پس آيا نمى‌شنويد [تا دريابيد و پند گيريد]؟!'),
(3324, 28, 72, 'بگو: مرا گوييد كه اگر خداوند روز را بر شما تا روز رستاخيز پاينده كند، كدام خدايى است جز خداى يكتا كه شب را براى شما آورد تا در آن بياراميد؟ پس آيا [اين نشانه‌ها را] نمى‌بينيد [تا ايمان آوريد و راه يابيد]؟!'),
(3325, 28, 73, 'و از بخشايش اوست كه شب و روز را براى شما قرار داد تا در آن'),
(3326, 28, 74, 'و روزى كه [خداوند] آنان را بخواند، پس گويد: كجايند شريكانى كه براى من مى‌پنداشتيد؟'),
(3327, 28, 75, 'و از هر امتى گواهى بيرون آريم و گوييم: حجت خود را [بر شركتان‌] بياريد، پس بدانند كه حق خداى راست و آنچه فرا مى‌بافتند از آنان گم شود.'),
(3328, 28, 76, 'همانا قارون از قوم موسى بود و بر آنها ستم و سركشى كرد، و او را از گنجها چندان بداديم كه كليدهاى آن بر گروه نيرومند هم گرانبار بود آنگاه كه قومش بدو گفتند: شادمانى مكن- به نعمت دنيا مغرور و سركش مباش- كه خدا شادمانان را دوست ندارد.'),
(3329, 28, 77, 'و در آنچه خدا به تو داده است سراى واپسين را بجوى و بهره خويش را از دنيا فراموش مكن و نيكى- بخشش- كن چنانكه خدا به تو نيكى كرده، و در زمين تباهكارى مجوى، كه خدا تباهكاران را دوست ندارد.'),
(3330, 28, 78, 'گفت: همانا آنچه به من داده شده بنا بر دانشى است كه نزد من است. آيا ندانست كه خدا پيش از او نسلهايى را نابود كرد كه نيرويشان از او فزونتر و فراهم آوردنشان [از مال دنيا] از او بيشتر بود؟ و بزهكاران را از گناهانشان نپرسند.'),
(3331, 28, 79, 'پس بر قوم خود در آرايش خويش بيرون آمد كسانى كه خواستار زندگانى اين جهان بودند گفتند: اى كاش مانند آنچه به قارون داده شده ما را مى‌بود، كه همانا او داراى بهره‌اى بزرگ است.'),
(3332, 28, 80, 'و كسانى كه به آنان دانش داده شده گفتند: واى بر شما، پاداش خدا براى كسى كه ايمان آورده و كار نيك و شايسته كرده بهتر است، و اين را فرا نمى‌گيرند مگر شكيبايان.'),
(3333, 28, 81, 'پس او و خانه‌اش را به زمين فروبرديم، و هيچ گروهى نبود كه او را در برابر خدا يارى كنند و خود نيز از بازدارندگان عذاب از خويش- يا از يارى‌كنندگان خويش يا از انتقام گيرندگان- نبود.'),
(3334, 28, 82, 'و آنان كه ديروز جايگاه او را آرزو مى‌كردند در حالى بامداد كردند كه مى‌گفتند: واى!- يا: وه!- گويى خداست كه روزى را براى هر كه از بندگان خود بخواهد فراخ مى‌كند و تنگ مى‌گرداند اگر نه اين بود كه خدا بر ما منت نهاد هر آينه ما را نيز- به سبب آن آرزوها كه داشتيم- به زمين فرو مى‌برد واى! گويى كافران رستگار نمى‌شوند.'),
(3335, 28, 83, 'اين سراى واپسين را براى كسانى ساخته‌ايم كه در زمين برترى و تباهى نخواهند و سرانجام [نيك‌] از آن پرهيزگاران است.'),
(3336, 28, 84, 'هر كه كارى نيك آرد او را بهتر از آن باشد- چند برابر در سراى واپسين-، و هر كه كارى بد آرد آنان كه كارهاى بد كردند جز آنچه مى‌كردند كيفر نبينند.'),
(3337, 28, 85, 'بى‌گمان همان [خداى‌] كه قرآن را بر تو واجب كرد- تبليغ آن را يا عمل بدان را- تو را به بازگشتگاه- جاى نخست: مكه- باز گرداند. بگو: پروردگار من آن را كه رهنمونى [به راه راست‌] آورده- يعنى پيامبر'),
(3338, 28, 86, 'و تو اميد نداشتى كه اين كتاب به سوى تو فرستاده شود جز آنكه بخشايشى است از پروردگارت پس هرگز پشتيبان كافران مباش.'),
(3339, 28, 87, 'و [كافران‌] تو را از آيات خدا پس از آنكه به سويت فروفرستاده شد بايد باز ندارند، و [مردم را] به پروردگار خويش بخوان و از مشركان مباش.'),
(3340, 28, 88, 'و با خداى يكتا خدايى ديگر مخوان، جز او خدايى نيست، هر چيزى جز ذات او نيست شدنى است، فرمان او راست و به سوى او بازگردانيده مى‌شويد.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الف، لام، ميم.'),
(3342, 29, 2, 'آيا مردم پنداشته‌اند كه همين كه [به زبان‌] گويند ايمان آورديم آنان را وامى‌گذارند- رها مى‌كنند- و آزموده نمى‌شوند؟'),
(3343, 29, 3, 'و هر آينه كسانى را كه پيش از آنان بودند آزموديم و همانا خداوند كسانى را كه راست گفتند مى‌شناسد و دروغگويان را نيز مى‌شناسد.'),
(3344, 29, 4, 'يا مگر كسانى كه بديها مى‌كنند گمان كرده‌اند كه بر ما پيشى مى‌گيرند- از كيفر ما مى گريزند-؟ بد است آن داورى كه مى‌كنند.'),
(3345, 29, 5, 'هر كه ديدار خداى- روز رستاخيز- را اميد دارد پس [بداند كه‌] وعده خدا آمدنى است، و اوست شنوا و دانا.'),
(3346, 29, 6, 'و هر كه جهاد كند جز اين نيست كه به سود خويش جهاد مى‌كند، زيرا كه خداوند از جهانيان بى‌نياز است.'),
(3347, 29, 7, 'و آنان كه ايمان آوردند و كارهاى نيك و شايسته كردند هر آينه بديهاشان را از آنان بزداييم و ايشان را [بر پايه‌] نيكوترين آنچه مى‌كردند پاداش دهيم.'),
(3348, 29, 8, 'و آدمى را سفارش كرديم كه به پدر و مادر خويش نيكويى كند و اگر بر تو سخت گيرند و بكوشند تا آنچه را كه بدان دانشى ندارى با من انباز گيرى پس فرمان ايشان مبر بازگشت همه شما به من است، پس شما را بدانچه مى‌كرديد آگاه مى‌كنم.'),
(3349, 29, 9, 'و آنان كه ايمان آوردند و كارهاى نيك و شايسته كردند هر آينه ايشان را در ميان نيكان و شايستگان درآريم.'),
(3350, 29, 10, 'و از مردمان كسانى هستند كه گويند: به خدا ايمان آورديم، و چون درباره خدا رنجى و آزارى ببينند رنج و آزار مردم را مانند عذاب خدا شمرند- يعنى از ترس آزار مردم ترك ايمان مى‌كنند و حال آنكه مى‌بايست از ترس عذاب خدا ترك كفر كنند- و چون [شما را] يارى و پيروزيى از پروردگارت بيايد گويند: ما با شما بوديم! آيا خدا بدانچه در سينه‌هاى مردم جهان است داناتر نيست؟'),
(3351, 29, 11, 'و هر آينه خدا كسانى را كه ايمان آوردند مى‌شناسد و منافقان را نيز مى‌شناسد.'),
(3352, 29, 12, 'و كسانى كه كافر شدند به آنان كه ايمان آورده‌اند گويند: راه ما را پيروى كنيد و ما بار گناهان شما را برمى‌داريم، و حال آنكه بردارنده چيزى از گناهان آنان نيستند، براستى آنها دروغگويند.'),
(3353, 29, 13, 'و البته بارهاى گران خود و بارهايى را همراه بارهاى گران خويش بردارند، و بى گمان روز رستاخيز از آنچه به دروغ مى‌بافتند بازخواست خواهند شد.'),
(3354, 29, 14, 'و هر آينه نوح را به سوى قومش فرستاديم، پس هزار سال، مگر پنجاه سال، در ميان آنان [به پيامبرى‌] درنگ كرد، پس [عذاب‌] طوفان آنها را بگرفت در حالى كه ستمكار بودند.'),
(3355, 29, 15, 'پس او و ياران كشتى را رهانيديم و آن- كشتى يا داستان- را براى جهانيان نشانه و عبرتى ساختيم.'),
(3356, 29, 16, 'و ابراهيم را [فرستاديم‌] آنگاه كه به قوم خود گفت: خداى يكتا را بپرستيد و از او پروا كنيد، اين براى شما بهتر است اگر مى‌دانستيد.'),
(3357, 29, 17, 'جز اين نيست كه به جاى خداى يكتا بتانى را مى‌پرستيد و دروغى مى‌سازيد- كه آنها را معبود يا خدايان مى‌ناميد-. آنان كه به جاى خداى يكتا مى پرستيد شما را توان روزى‌دادن ندارند، پس روزى را از نزد خدا بخواهيد و او را بپرستيد و او را سپاس گزاريد، كه به سوى او بازگردانده مى‌شويد.'),
(3358, 29, 18, 'و اگر [شما اى اهل مكه، رسالت پيامبر را] دروغ مى‌انگاريد همانا امتهاى پيش از شما نيز [پيامبران را] به دروغ نسبت دادند، و بر پيامبر جز رسانيدن آشكار پيام نيست.'),
(3359, 29, 19, 'آيا نديده‌اند كه چگونه خدا آفرينش آفريدگان را آغاز مى‌كند، سپس آن را باز مى‌گرداند- به زنده‌كردن مردگان در روز رستاخيز-؟ همانا اين كار بر خدا آسان است.'),
(3360, 29, 20, 'بگو در زمين بگرديد و بنگريد كه چگونه [خدا] آفرينش را آغاز كرد، و سپس خدا آفرينش واپسين را پديد مى‌كند، همانا خدا بر هر چيزى تواناست.'),
(3361, 29, 21, 'هر كه را خواهد عذاب كند و هر كه را خواهد ببخشايد و به او بازگردانده مى‌شويد.'),
(3362, 29, 22, 'و شما عاجزكننده [خدا] نيستيد- از عذاب الهى نتوانيد گريخت- نه در زمين و نه در آسمان، و شما را جز او هيچ سرپرست و ياورى نيست.'),
(3363, 29, 23, 'و كسانى كه به آيات خدا و ديدار او كافر شدند آنان از مهر و بخشايش من نوميدند و ايشان راست عذابى دردناك.'),
(3364, 29, 24, 'پس پاسخ قوم او جز اين نبود كه گفتند: او را بكشيد يا بسوزانيد. پس خداى از آتش برهانيدش. براستى در آن [سرگذشت‌] نشانه‌ها و عبرتهاست براى مردمى كه باور دارند.'),
(3365, 29, 25, 'و [ابراهيم‌] گفت: جز اين نيست كه شما به جاى خداى يكتا بتانى را براى دوستيى كه در زندگانى دنيا ميان شماست [به خدايى‌] گرفته‌ايد، سپس در روز رستاخيز برخى از شما برخى را منكر مى‌شوند و يكديگر را لعنت مى‌كنند، و جايگاهتان آتش است و شما را ياورانى نيست.'),
(3366, 29, 26, 'پس لوط به او ايمان آورد. و [ابراهيم‌] گفت: من به سوى پروردگار خويش هجرت كننده‌ام، همانا اوست تواناى بى‌همتا و داناى استواركار.'),
(3367, 29, 27, 'و او را اسحاق و يعقوب بخشيديم و در ميان فرزندانش پيامبرى و كتاب بنهاديم، و در اين جهان پاداش او را بداديم و همانا او در جهان ديگر از نيكان و شايستگان است.'),
(3368, 29, 28, 'و لوط را [فرستاديم‌]، آنگاه كه به قوم خود گفت: هر آينه شما كارى زشت مى‌كنيد كه هيچ يك از مردم جهان بدان پيشى نگرفته است.'),
(3369, 29, 29, 'آيا شما [به آهنگ شهوت‌] به مردان درمى‌آييد و راه [نسل‌] را مى‌بريد و در انجمن خويش كارهاى ناپسند مى‌كنيد؟! پس جواب قومش جز اين نبود كه گفتند: عذاب خداى را به ما بيار، اگر از راستگويانى.'),
(3370, 29, 30, 'گفت: پروردگارا، مرا بر اين مردم تباهكار يارى ده.'),
(3371, 29, 31, 'و چون فرستادگان ما ابراهيم را [به اسحاق و يعقوب‌] مژده آوردند، گفتند: ما مردم اين شهر را هلاك خواهيم كرد، زيرا كه مردم آن ستمكارند.'),
(3372, 29, 32, 'گفت: در آنجا لوط است گفتند: ما به هر كس كه در آنجاست داناتريم هر آينه او و خاندان او را برهانيم مگر آنش را كه از بازماندگان- هلاك‌شوندگان- خواهد بود.'),
(3373, 29, 33, 'و چون فرستادگان ما نزد لوط آمدند، به سبب ايشان اندوهگين شد و در كارشان تنگدل گشت، و گفتند: مترس و اندوه مدار كه ما تو و خاندان تو را مى رهانيم مگر زنت را كه از بازماندگان است.'),
(3374, 29, 34, 'ما بر مردم اين شهر به سزاى آن نافرمانى و بدكارى كه مى‌كردند عذابى از آسمان فرو مى‌آريم.'),
(3375, 29, 35, 'و هر آينه از آن'),
(3376, 29, 36, 'و به شهر مدين برادرشان شعيب را [فرستاديم‌] گفت: اى قوم من، خداى را بپرستيد و روز بازپسين را اميد بداريد و در زمين به تباهكارى مكوشيد.'),
(3377, 29, 37, 'پس او را دروغگو انگاشتند و آنان را زلزله بگرفت تا در خانه‌هاى خويش بيجان به رو بر زمين افتادند.'),
(3378, 29, 38, 'و عاد و ثمود را [نيز هلاك كرديم‌]، و همانا [اين هلاكت‌] براى شما از جايگاه‌هاى ايشان پديدار است، و شيطان كارهايشان را برايشان بياراست تا آنان را از راه [حق‌] باز داشت و بگردانيد، و حال آنكه بينا بودند.'),
(3379, 29, 39, 'و قارون و فرعون و هامان را [نيز هلاك كرديم‌]، و هر آينه موسى با نشانه‌هاى روشن نزد آنها آمد پس در زمين سركشى و بزرگ‌منشى نمودند و پيشى‌گيرندگان نبودند- نتوانستند بر عذاب خداوند پيشى گيرند و از آن بگريزند-.'),
(3380, 29, 40, 'پس همه را به گناهشان بگرفتيم. از آنان كس بود كه بر او سنگباران فرستاديم- يعنى قوم لوط- و از آنها كس بود كه او را بانگ كشنده آسمانى فروگرفت- يعنى قوم ثمود و اهل مدين-، و از آنان كس بود كه به زمين فرو برديمش- چون قارون- و از آنها كس بود كه به آب غرق كرديم- چون قوم نوح و فرعون- و خداى بر آن نبود كه بر آنان ستم كند و ليكن آنها بر خود ستم مى‌كردند.'),
(3381, 29, 41, 'داستان آنان كه جز خدا دوستانى گرفته‌اند مانند داستان عنكبوت است كه خانه‌اى ساخته است و هر آينه سست‌ترين خانه‌ها خانه عنكبوت است، اگر مى‌دانستند.'),
(3382, 29, 42, 'همانا خداى مى‌داند هر آنچه را كه جز او [به خدايى‌] مى‌خوانند- چون بت و فرشته و آدمى و ستارگان- و اوست تواناى بى‌همتا و داناى استواركار.'),
(3383, 29, 43, 'و اين مَثَلها را براى مردم مى‌زنيم و جز دانايان آنها را درنمى‌يابند.'),
(3384, 29, 44, 'خداوند آسمانها و زمين را براستى و درستى- نه بيهوده و به بازى- آفريد، هر آينه در اين براى مؤمنان نشانه‌اى است.'),
(3385, 29, 45, 'آنچه را از اين كتاب به تو وحى شده است بر خوان و نماز را برپا دار، كه نماز از زشتكارى و ناشايست باز مى‌دارد، و هر آينه يادكرد خدا بزرگتر است، و خدا آنچه مى‌كنيد مى‌داند.'),
(3386, 29, 46, 'و با اهل كتاب جز به شيوه‌اى كه نيكوتر است مجادله مكنيد مگر با كسانى از آنان كه ستم كردند، و گوييد: به آنچه به ما فرو فرستاده شده و آنچه به شما فرو فرستاده شده ايمان داريم، و خداى ما و خداى شما يكى است و ما او را گردن‌نهاده و فرمانبرداريم.'),
(3387, 29, 47, 'و بدين گونه [كه به پيامبران پيشين كتاب فرو فرستاديم‌] اين كتاب را به تو فرو فرستاديم، پس كسانى كه به آنان كتاب داده‌ايم به آن ايمان مى‌آورند، و از اينان- اهل مكه يا مشركان- كس هست كه بدان مى‌گرود، و جز كافران آيات ما را انكار نمى‌كنند.'),
(3388, 29, 48, 'و تو پيش از آن'),
(3389, 29, 49, 'بلكه آن آياتى است روشن و هويدا در سينه كسانى كه بديشان دانش داده‌اند، و آيات ما را جز ستمكاران انكار نمى‌كنند.'),
(3390, 29, 50, 'و گفتند: چرا از پروردگارش نشانه‌هايى- معجزه‌هايى- بر او فرستاده نشده؟ بگو: همانا نشانه‌ها- معجزات- نزد خداست- هر گاه خواهد و بر هر كه خواهد فرو فرستد- من تنها بيم‌كننده آشكارم.'),
(3391, 29, 51, 'آيا آنان را بسنده نيست كه اين كتاب را كه بر آنها خوانده مى‌شود بر تو فرو فرستاده‌ايم؟ هر آينه در اين [كتاب‌] بخشايشى و يادكرد و پندى است براى مردمى كه ايمان آورند.'),
(3392, 29, 52, 'بگو: خداوند ميان من و شما گواهى بسنده است، آنچه را در آسمانها و زمين است مى‌داند، و كسانى كه به باطل گرويدند و به خدا كافر شدند، آنانند زيانكاران.'),
(3393, 29, 53, 'و به شتاب از تو عذاب مى‌خواهند. و اگر سرآمدى نامبرده نبود بى‌گمان عذاب بديشان مى‌رسيد، و هر آينه ناگهان بديشان آيد در حالى كه نفهمند و بى‌خبر باشند.'),
(3394, 29, 54, 'از تو به شتاب عذاب مى‌خواهند، و هر آينه دوزخ كافران را فراگيرنده است،'),
(3395, 29, 55, 'روزى كه عذاب از بالاى سر و از زير پايشان فروگيردشان و [خداى‌] گويد: اينك بچشيد آنچه [در دنيا] مى‌كرديد.'),
(3396, 29, 56, 'اى بندگان من كه ايمان آورده‌ايد، همانا زمين من فراخ است، پس [به جايى رويد كه بتوانيد] تنها مرا بپرستيد.'),
(3397, 29, 57, 'هر كسى چشنده مرگ است، سپس به سوى ما بازگردانده مى‌شويد.'),
(3398, 29, 58, 'و كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند هر آينه ايشان را در كوشكهايى از بهشت جاى دهيم كه از زير آنها جويها روان است، در آنجا جاويدان باشند نيكو مزدى است مزد عمل‌كنندگان'),
(3399, 29, 59, 'آنان كه شكيبايى كردند و بر پروردگارشان توكل مى‌كنند.'),
(3400, 29, 60, 'و بسا جنبندگانى كه روزى خويش برنمى‌دارند- نتوانند روزى خود را با خود برگيرند يا نتوانند آن را ذخيره كنند-، خدا آنها و شما را روزى مى‌دهد، و اوست شنوا و دانا.'),
(3401, 29, 61, 'و اگر از آنان بپرسى: چه كسى آسمانها و زمين را آفريده و آفتاب و ماه را رام گردانيده هر آينه گويند: خداى. پس چگونه [از حق‌] رويگردان مى شوند؟'),
(3402, 29, 62, 'خداست كه روزى را براى هر كه از بندگانش بخواهد مى‌گستراند و [يا] تنگ مى‌گرداند، زيرا كه خداى به هر چيزى داناست.'),
(3403, 29, 63, 'و اگر از آنان بپرسى: چه كسى از آسمان آبى فرستاد كه زمين را بعد از مردنش بدان زنده كرد؟ هر آينه گويند: خداى. بگو: سپاس و ستايش خداى راست- بر اعترافشان به حق-، بلكه بيشترشان خرد را كار نمى‌بندند- سخن متناقض مى‌گويند: به خالق بودن او اقرار دارند و در همان حال مخلوق را شريك او مى‌دانند-.'),
(3404, 29, 64, 'و اين زندگانى دنيا جز سرگرمى و بازى نيست، و هر آينه سراى واپسين زندگانى [راستين‌] است، اگر مى‌دانستند.'),
(3405, 29, 65, 'و چون در كشتى سوار شوند خداى را با اخلاص و پاكدينى- يكتاگرايى- بخوانند، و چون به سوى خشكى برهاندشان آنگاه شرك مى‌ورزند'),
(3406, 29, 66, 'تا [سرانجام‌] بدانچه به آنان داديم كافر شوند و ناسپاسى كنند و تا برخوردار باشند، زودا كه [حقيقت را] بدانند.'),
(3407, 29, 67, 'آيا نديده‌اند كه ما حرمى امن پديد كرديم و حال آنكه مردم پيرامونشان ربوده مى‌شوند- در امنيت نيستند و پيوسته قتل و غارت مى‌شوند-؟ آيا به باطل مى‌گروند و به نعمت خدا كفر مى‌ورزند- براى او شريك قائل مى‌شوند- و ناسپاسى مى‌كنند؟!'),
(3408, 29, 68, 'و كيست ستمكارتر از آن كه بر خدا دروغ بندد يا حق را آنگاه كه بدو آيد دروغ انگارد؟ آيا در دوزخ جايگاهى براى كافران نيست؟'),
(3409, 29, 69, 'و آنان كه در [راه يارى دين‌] ما بكوشند هر آينه ايشان را به راه‌هاى خويش رهبرى كنيم، و براستى خدا با نيكوكاران است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الف، لام، ميم.'),
(3411, 30, 2, 'روميان مغلوب شدند،'),
(3412, 30, 3, 'در نزديكترين سرزمين و آنان پس از آنكه مغلوب شدند بزودى پيروز شوند،'),
(3413, 30, 4, 'در چند سال- هفت سال- [آينده‌]. كار و فرمان، از پيش و از پس [اين شكست و پيروزى‌]، خداى راست و در آن روز مؤمنان شادمان شوند،'),
(3414, 30, 5, 'به يارى خدا هر كه را خواهد يارى دهد، و اوست تواناى بى‌همتا و مهربان.'),
(3415, 30, 6, 'وعده خداست خدا وعده خويش خلاف نكند، ولى بيشتر مردم نمى‌دانند.'),
(3416, 30, 7, 'آنان ظاهرى- آنچه به ديد چشم مى‌آيد از آرايش و نمايش- از زندگانى دنيا مى‌دانند و از زندگانى پسين بى‌خبرند.'),
(3417, 30, 8, 'آيا در درون خويش نينديشيده‌اند كه خدا آسمانها و زمين و آنچه را ميان آنهاست جز به حق و سرآمدى نامبرده نيافريده است؟ و هر آينه بسيارى از مردم ديدار پروردگارشان را باور ندارند.'),
(3418, 30, 9, 'آيا در زمين نگشتند تا بنگرند كه سرانجام كسانى كه پيش از آنان بودند چگونه شد؟ آنها از اينان نيرومندتر بودند، و زمين را شيار كردند- براى كِشت- و بيش از آنچه اينان آبادش كردند آن را آباد ساختند، و پيامبرانشان با حجت‌هاى روشن- معجزه‌ها- بديشان آمدند پس خدا بر آن نبود كه بر آنان ستم كند بلكه خود بر خويشتن ستم مى‌كردند.'),
(3419, 30, 10, 'سپس سرانجام كسانى كه كارهاى بد كردند اين شد كه آيات خدا را دروغ انگاشتند و بدانها استهزا مى‌كردند.'),
(3420, 30, 11, 'خداست كه آفرينش آفريدگان را آغاز مى‌كند، سپس [ديگر بار] آن را بازمى‌گرداند- پس از مرگ زنده مى‌كند-، آنگاه به سوى او بازگردانده مى‌شويد.'),
(3421, 30, 12, 'و روزى كه رستاخيز برپا شود بزهكاران نااميد- و سرگشته و اندوهگين- گردند.'),
(3422, 30, 13, 'و براى آنان از شريكانشان- بتان- شفيعانى نباشد، و به شريكانشان كافر شوند.'),
(3423, 30, 14, 'و روزى كه رستاخيز برپا شود، در آن روز [گروه‌ها] پراكنده و از هم جدا شوند.'),
(3424, 30, 15, 'اما كسانى كه ايمان آوردند و كارهاى شايسته كردند پس ايشان در باغى و مرغزارى- بهشت- شادمان باشند.'),
(3425, 30, 16, 'و اما كسانى كه كافر شدند و آيات ما و ديدار آن جهان را دروغ انگاشتند پس آنان حاضرشدگان در عذابند.'),
(3426, 30, 17, 'پس پاكى خداى راست آنگاه كه به شب در آييد و آنگاه كه به بامداد در آييد.'),
(3427, 30, 18, 'و او راست سپاس و ستايش در آسمانها و زمين و در پايان روز و آنگاه كه به نيمروز مى‌رسيد.'),
(3428, 30, 19, 'زنده را از مرده بيرون آرد و مرده را از زنده و زمين را پس از مردگى‌اش زنده كند، و همچنين شما [از گورها] بيرون آورده شويد.'),
(3429, 30, 20, 'و از نشانه‌هاى او آن است كه شما را از خاك بيافريد سپس آدميانى شديد كه [در زمين‌] پراكنده مى‌شويد.'),
(3430, 30, 21, 'و از نشانه‌هاى او اين است كه براى شما از [جنس‌] خودتان همسرانى بيافريد تا بديشان آرام گيريد و ميان شما دوستى و مهربانى نهاد هر آينه در اين كار براى مردمى كه بينديشند نشانه‌ها و عبرتهاست.'),
(3431, 30, 22, 'و از نشانه‌هاى او آفرينش آسمانها و زمين و گوناگونى زبانها و رنگهاى شماست هر آينه در اين [دگرگونيها] براى دانشمندان نشانه‌هاست.'),
(3432, 30, 23, 'و از نشانه‌هاى او خواب شماست به شب و روز و جستن شماست از فزون‌بخشى او- روزى- هر آينه در اين كار براى مردمى كه مى‌شنوند نشانه‌هاست.'),
(3433, 30, 24, 'و از نشانه‌هاى او آنست كه برق را براى بيم- تا مسافران بترسند و احتياب كنند- و اميد- تا مايه اميد كشاورزان باشد- به شما مى‌نمايد و از آسمان آبى فرو مى‌ريزد تا زمين را پس از مردگى‌اش زنده كند همانا در آن براى مردمى كه خرد را كار بندند نشانه‌هاست.'),
(3434, 30, 25, 'و از نشانه‌هاى او آن است كه آسمان و زمين به فرمان او برپاست سپس چون شما را بخواند خواندنى از زمين- زنده شدن در رستاخيز- ناگاه [از گورها] بيرون آييد.'),
(3435, 30, 26, 'و او راست هر كه در آسمانها و زمين است، همه او را با فروتنى فرمانبردارند.'),
(3436, 30, 27, 'و اوست آن كه آفرينش آفريدگان را آغاز مى‌كند سپس آن را بازمى‌گرداند- دوباره زنده مى‌كند- و اين كار بر او آسانتر است. و او راست صفت برتر در آسمانها و زمين، و اوست تواناى بى‌همتا و داناى با حكمت.'),
(3437, 30, 28, 'براى شما از خودتان مَثلَى مى‌زند- در نفى و ابطال شرك-: آيا شما را از بردگانتان شريكانى در آنچه روزيتان داده‌ايم هست كه شما در آن [روزى با زيردستانتان‌] برابر و يكسان باشيد؟ [نه، بلكه‌] از آنها [در باره اموالتان‌] بيم داريد چنانكه از خودتان- آزادان- بيم داريد- پس چگونه براى خدا، كه همه مملوك اويند، شريك روا مى‌داريد؟- اينچنين آيات را براى مردمى كه خرد را كار بندند به تفصيل بيان مى‌كنيم.'),
(3438, 30, 29, 'بلكه آنان كه ستم كردند- مشركان- بى‌هيچ دانشى كامها و آرزوهاى دل خويش را پيروى نمودند. پس آن را كه خدا گمراه كرده است چه كسى راه نمايد؟ و آنها را ياورانى نيست.'),
(3439, 30, 30, 'پس روى خويش را به سوى دين يكتا پرستى فرادار، در حالى كه از همه كيشها روى برتافته و حق‌گراى باشى، به همان فطرتى كه خدا مردم را بر آن آفريده است. آفرينش خداى- فطرت توحيد- را دگرگونى نيست، اين است دين راست و استوار، ولى بيشتر مردم نمى‌دانند.'),
(3440, 30, 31, 'در حالى كه [به دل‌] به او روى آورده باشيد- يعنى به او روى آريد- و از او پروا كنيد و نماز را برپا داريد و از مشركان نباشيد'),
(3441, 30, 32, 'از آنان كه دين خود را پراكنده ساختند و گروه گروه شدند، هر گروهى بدانچه خود دارند دلخوشند.'),
(3442, 30, 33, 'و چون مردم را گزندى رسد پروردگار خويش را در حالى كه روى دل بدو مى‌كنند بخوانند، و چون آنان را از سوى خود رحمتى- مِهر و آسايشى- بچشاند آنگاه گروهى از آنها به پروردگار خويش انباز مى‌آرند'),
(3443, 30, 34, 'تا بدانچه بديشان داديم ناسپاسى كنند، پس بهره‌مند شويد، زودا كه [سرانجام خويش‌] بدانيد.'),
(3444, 30, 35, 'يا مگر بر آنان حجتى فرو فرستاديم تا بدانچه آنان شريك [ما] مى‌سازند سخن گويد؟!'),
(3445, 30, 36, 'و چون مردم را رحمتى- مِهر و آسايشى- بچشانيم بدان شادمان گردند، و اگر به سزاى آنچه دستهاى ايشان پيش فرستاده- كارهايى كه خود كرده‌اند- بدى و رنجى به آنان رسد ناگهان نوميد مى‌شوند.'),
(3446, 30, 37, 'آيا نديده‌اند كه خدا روزى را براى هر كه بخواهد فراخ و [يا] تنگ مى‌گرداند؟ همانا در اين [فراخى و تنگى‌] براى مردمى كه ايمان آورند نشانه‌ها و عبرتهاست.'),
(3447, 30, 38, 'پس حق خويشاوند و درويش و در راه‌مانده را بده اين بهتر است براى آنان كه [خشنودى‌] خداى را مى‌خواهند، و ايشانند رستگاران.'),
(3448, 30, 39, 'و آنچه از ربا مى‌دهيد تا [براى شما] در مالهاى مردم بيفزايد پس [بدانيد كه‌] نزد خدا افزون نمى‌شود، و آنچه از زكات مى‌دهيد كه [بدان وسيله‌] خشنودى خداى را مى‌خواهيد پس ايشانند افزون يافتگان.'),
(3449, 30, 40, 'خداست آن كه شما را بيافريد آنگاه روزيتان داد سپس شما را بميراند و باز زنده‌تان مى‌كند. آيا هيچ يك از شريكان شما- كه با خدا انباز مى‌گيريد- چيزى از اين كارها مى‌كند؟ او پاك و منزه است و از آنچه انباز مى‌آرند برتر است.'),
(3450, 30, 41, 'تباهى در خشكى و دريا به سبب كرده‌هاى مردمان پديدار شد تا [خداوند جزاى‌] برخى از آنچه كرده‌اند به آنها بچشاند، باشد كه باز گردند.'),
(3451, 30, 42, 'بگو در زمين بگرديد و بنگريد كه سرانجام پيشينيان چگونه بود، بيشترشان مشرك بودند.'),
(3452, 30, 43, 'پس روى خود را به سوى دين درست و استوار فرادار پيش از آنكه روزى بيايد كه آن را از [جانب‌] خداى بازگشتى- يا بازگرداننده‌اى- نيست آن روز همه از هم جدا و پراكنده مى‌شوند- گروهى به بهشت مى‌روند و گروهى به دوزخ-.'),
(3453, 30, 44, 'هر كه كافر شود، بر اوست [وبال‌] كفر او و هر كه كار نيك و شايسته كند، براى خويشتن جايگاه آرامش و آسايش- در جهان ديگر- آماده مى‌سازند.'),
(3454, 30, 45, 'تا [خداى‌] به كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند از فزون‌بخشى خود پاداش دهد، كه او كافران را دوست ندارد.'),
(3455, 30, 46, 'و از نشانه‌هاى او آن است كه بادها را مژده‌دهنده مى‌فرستد- كه شما را مژدگان باشد- و تا شما را از رحمت خويش- يعنى باران- بجشاند و تا كشتى‌ها به فرمان او روان شوند و تا از فزون‌بخشى او- روزى- بجوييد و تا سپاس [نعمتهاى او] گزاريد.'),
(3456, 30, 47, 'و هر آينه پيش از تو پيامبرانى به سوى قومشان فرستاديم، پس حجتهاى روشن بديشان آوردند، آنگاه از كسانى كه بدكارى كردند كين ستانديم، و يارى‌دادن مؤمنان بر ما سزا بود.'),
(3457, 30, 48, 'خداست آن كه بادها را مى‌فرستد كه ابر را بر مى‌انگيزانند پس آن را در آسمان آنگونه كه خواهد بگستراند و پاره پاره‌اش گرداند، پس باران را بينى كه از خلال آن بيرون مى‌آيد، و چون آن'),
(3458, 30, 49, 'و هر آينه پيش از آنكه [باران‌] بر آنان فرو فرستاده شود [و] پيش از [پديدار شدن‌] آن'),
(3459, 30, 50, 'پس به نشانه‌هاى رحمت خدا- باران- بنگر كه چگونه زمين را پس از مردگى‌اش زنده مى‌كند، هر آينه آن [خداى‌] زنده‌كننده مردگان است، و او بر هر چيزى تواناست.'),
(3460, 30, 51, 'و اگر بادى [سرد سوزان‌] فرستيم كه آن [كشت‌] را زرد شده ببينند هر آينه پس از آن ناسپاس مى‌گردند.'),
(3461, 30, 52, 'تو مردگان- مرده‌دلان- را نتوانى شنواند و بانگ و آواز را به كران نتوانى شنواند آنگاه كه پشت كرده برگردند.'),
(3462, 30, 53, 'و تو راهنماى كوران- كوردلان- از گمراهيشان نيستى. تو نشنوانى مگر كسانى را كه به آيات ما ايمان مى‌آورند و گردن نهاده باشند.'),
(3463, 30, 54, 'خداست آن كه شما را از سستى- در سستى و ناتوانى- بيافريد سپس از پس سستى و ناتوانى نيرو و توان داد، آنگاه پس از نيرومندى سستى و پيرى پديد كرد، هر چه خواهد مى‌آفريند و اوست دانا و توانا.'),
(3464, 30, 55, 'و روزى كه رستاخيز برپا شود بدكاران سوگند مى‌خورند كه جز ساعتى درنگ نكردند- در دنيا يا در گور- اينچنين [از راستى به ناراستى‌] گردانيده مى‌شوند- كار و عادت ايشان در اين جهان و آن جهان نادرستى و ناراستى است-.'),
(3465, 30, 56, 'و كسانى كه ايشان را دانش و ايمان داده‌اند، گويند: هر آينه در نوشته خداى، [در گور] تا روز رستاخيز درنگ كرده‌ايد، اينك اين است روز رستاخيز، ولى شما نمى‌دانستيد [كه رستاخيز حق است‌].'),
(3466, 30, 57, 'پس در آن روز پوزش‌خواهى آنان كه ستم كردند سودشان ندهد و نه از آنان خواسته شود كه پوزش خواهند.'),
(3467, 30, 58, 'و هر آينه در اين قرآن براى مردم از هر گونه مثلى زديم، و چون نشانه‌اى بديشان آورى آنان كه كافر شدند بى‌گمان گويند: شما جز بيهوده‌گويان و باطل‌آوران نيستيد.'),
(3468, 30, 59, 'اينچنين خداوند بر دلهاى آنان كه نمى‌دانند- حق و رستاخيز را باور ندارند- مهر مى‌نهد.'),
(3469, 30, 60, 'پس شكيبايى كن، كه وعده خدا راست است، و مباد آنان كه [رستاخيز را] بى‌گمان باور ندارند تو را سبكسار كنند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الف، لام، ميم.'),
(3471, 31, 2, 'اين است آيتهاى كتاب با حكمت'),
(3472, 31, 3, 'رهنمونى و بخشايشى براى نيكوكاران.'),
(3473, 31, 4, 'آنان كه نماز را برپا مى‌دارند و زكات مى‌دهند و به سراى پسين بى‌گمان باور دارند.'),
(3474, 31, 5, 'ايشان بر راهى راست از پروردگار خويشند، و اينانند رستگاران.'),
(3475, 31, 6, 'و از مردمان كس هست كه سخن بيهوده را مى‌خرد تا بى‌هيچ دانشى [مردم را] از راه خدا- شنيدن آيات قرآن- گمراه سازد و آن را به مسخره مى‌گيرد اينان را عذابى است خواركننده.'),
(3476, 31, 7, 'و چون آيات ما بر او خوانده شود با حالت گردنكشى روى بگرداند گويى كه آنها را نشنيده [و] گويى در گوشهاى وى گرانى است، پس او را به عذاب دردناك مژده ده.'),
(3477, 31, 8, 'همانا كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند ايشان راست بهشتهاى پر نعمت.'),
(3478, 31, 9, 'در آنجا جاودانه‌اند وعده راست خداست، و اوست تواناى بى‌همتا و داناى استواركار.'),
(3479, 31, 10, 'آسمانها را بى‌ستونى كه ببينيدش بيافريد، و در زمين كوه‌هاى پا برجا و استوار بيفكند تا [زمين‌] شما را نجنباند و از هر جنبنده‌اى در آن بپراكند. و از آسمان آبى فرو فرستاديم پس در آن'),
(3480, 31, 11, 'اين است آفرينش خداى، پس به من بنماييد آنها كه غير اويند- بتها- چه آفريده‌اند بلكه ستمكاران در گمراهى آشكارند.'),
(3481, 31, 12, 'و هر آينه لقمان را حكمت- درستى انديشه و گفتار و كردار- داديم كه: خداى را سپاس دار، و هر كه خداى را سپاس گزارد آن سپاسگزارى به سود خويش كرده، و هر كه ناسپاسى كند همانا خداوند بى‌نياز و ستوده است.'),
(3482, 31, 13, 'و [ياد كن‌] آنگاه كه لقمان پسر خويش را پند همى داد كه: اى پسرك من، به خدا انباز مگير، كه انبازگرفتن [با خداى‌] هر آينه ستمى بزرگ است.'),
(3483, 31, 14, 'و آدمى را درباره پدر و مادرش كرديم- [از آن رو كه‌] مادرش وى را برداشت- در شكم- در حال ناتوانى بر ناتوانى- حمل او برايش سستى و ناتوانى روزافزون بود- و بازگرفتنش از شير در دو سال است- كه مرا و پدر و مادرت را سپاس دار بازگشت [همه‌] به سوى من است.'),
(3484, 31, 15, 'و اگر آن دو بر تو سخت گيرند و بكوشند كه چيزى را با من انباز گيرى كه بدان دانشى ندارى، پس فرمان ايشان مبر و در اين جهان با آنان به شيوه نيكو و پسنديده زيست كن و راه كسى را پيروى كن كه [به اخلاص و توحيد] سوى من باز گردد، سپس بازگشت همه شما به سوى من است پس شما را بدانچه مى‌كرديد آگاه مى‌كنم.'),
(3485, 31, 16, 'اى پسرك من، همانا كردار آدمى اگر همسنگ دانه خردلى- خردترين دانه، كنايه از كمترين كردار- باشد و در درون سنگ سخت يا در آسمانها يا در زمين [نهفته‌] باشد خدا آن را [براى حساب‌] بيارد- حاضر كند-، كه خدا باريكدان و آگاه است.'),
(3486, 31, 17, 'اى پسرك من، نماز را برپا دار و به كار نيك و پسنديده فرمان ده و از كار زشت و ناپسند باز دار و بر آنچه به تو رسد شكيبايى كن، كه اين از كارهاى استوار است.'),
(3487, 31, 18, 'و روى خود [به تكبر] از مردمان مگردان و در زمين خرامان و نازان راه مرو، كه خدا هيچ خرامنده نازنده را دوست ندارد.'),
(3488, 31, 19, 'و در راه رفتن ميانه‌رو باش و آواز خويش فرود آر، كه زشت‌ترين بانگها بانگ خران است.'),
(3489, 31, 20, 'آيا نديده‌ايد كه خدا آنچه را در آسمانها و زمين است از بهر شما رام كرد و نعمتهاى آشكار و نهان خويش را بر شما گسترده و فراوان كرد؟ و از مردمان كس هست كه درباره خدا بى‌هيچ دانشى و رهيافتى و كتاب روشنى گفت‌وگو و ستيزه مى‌كند.'),
(3490, 31, 21, 'و چون به آنان گفته شود: آنچه را خداى فروفرستاده پيروى كنيد، گويند: بلكه ما از آنچه پدرانمان را بر آن يافته‌ايم پيروى مى‌كنيم. آيا [شيطان را پيروى مى‌كنند] هر چند شيطان آنها را به عذاب سوزان بخواند؟'),
(3491, 31, 22, 'و هر كه روى خويش به خداى يكتا سپارد- دين و عمل و طاعت خود را براى خدا خالص سازد- و نيكوكار باشد پس براستى به دستاويز استوار چنگ زده است، و سرانجام [و بازگشت‌] كارها به سوى خداست.'),
(3492, 31, 23, 'و هر كه كافر شود كفر او تو را اندوهگين مكناد بازگشتشان به ماست، پس آنان را بدانچه كرده‌اند آگاه مى‌كنيم. همانا خدا به آنچه در سينه‌هاست داناست.'),
(3493, 31, 24, 'اندكى [در دنيا] برخوردارشان مى‌كنيم سپس به عذاب سخت گرفتارشان مى‌سازيم.'),
(3494, 31, 25, 'و اگر از آنها بپرسى: چه كسى آسمانها و زمين را بيافريد، هر آينه گويند: خدا، بگو: سپاس و ستايش خداى راست بلكه بيشترشان نمى‌دانند- كه اين اقرار عليه‌شان است-.'),
(3495, 31, 26, 'خداى راست آنچه در آسمانها و زمين است. همانا خداست بى‌نياز و ستوده.'),
(3496, 31, 27, 'و اگر هر چه درخت در زمين است قلم گردد و دريا مركب، و هفت درياى ديگر از پس [نابودى‌] آن بيفزايندش [و با آن بنويسند، كار نوشتن‌] سخنان خدا پايان نيابد همانا خدا تواناى بى‌همتا و داناى با حكمت است.'),
(3497, 31, 28, 'آفريدن و برانگيختن شما نيست مگر همچون [آفريدن و برانگيختن‌] يك تن. همانا خدا شنوا و بيناست.'),
(3498, 31, 29, 'آيا نديده‌اى كه خدا شب را در روز در مى‌آورد و روز را در شب- اشاره است به كوتاه و بلند شدن شب و روز، يا رفتن روز و آمدن شب و به عكس-، و خورشيد و ماه را رام ساخت كه هر يك تا سرآمدى نامبرده- معين- روان است؟ و خدا بدانچه مى‌كنيد آگاه است.'),
(3499, 31, 30, 'اين [همه توانايى و آگاهى‌] از آن روست كه خداست كه حق است و آنچه جز او مى‌خوانند باطل است و خداست والا و بزرگ.'),
(3500, 31, 31, 'آيا نديده‌اى كه كشتى به [سبب‌] نعمت خدا- نگاه‌داشتن آن بر روى آب و وزيدن باد و لطافت آب كه حركت كشتى را ميسر مى‌سازد- در دريا مى‌رود تا [خداى‌] برخى از نشانه‌هاى [قدرت‌] خود را به شما بنمايد؟ هر آينه در اين [كار] براى هر شكيباى سپاسگزارى نشانه‌ها و عبرتهاست.'),
(3501, 31, 32, 'و چون آنان- سرنشينان كشتى- را موجى مانند سايبانها فرا گيرد خداى را بخوانند در حالى كه دين- عبادت و طاعت- را ويژه او كنند، و چون آنان را به سوى خشكى برهاند پس [تنها] برخى از ايشان ميانه‌رو- بر راه راست توحيد- باشند و جز فريبكاران و پيمان‌شكنان ناسپاس نشانه‌هاى ما را انكار نكنند.'),
(3502, 31, 33, 'اى مردم، از پروردگارتان پروا كنيد و از روزى بترسيد كه هيچ پدرى به كار فرزند خويش نيايد- او را سود ندهد- و نه هيچ فرزندى از پدر خويش چيزى را كفايت كند- سودمند افتد يا عذابى را از او دفع كند-. همانا وعده خدا راست است، پس زندگانى دنيا شما را فريب ندهد- كه با آرزوى دراز و تكيه به عفو و مهلت‌دادن خدا، فريفته و بر گناهان دلير شويد- و آن فريبنده- شيطان يا هر فريبنده‌اى- شما را نفريبد.'),
(3503, 31, 34, 'همانا خداست كه دانش هنگام رستاخيز نزد اوست، و باران را فرو فرستد- داند كه باران كى فرستد- و آنچه در زهدانهاست مى‌داند و هيچ كس نمى‌داند كه فردا چه خواهد كرد، و كسى نمى‌داند به كدام زمين خواهد مرد همانا خدا دانا و آگاه است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الف، لام، ميم.'),
(3505, 32, 2, 'فروفرستادن اين كتاب، كه هيچ شكى در آن نيست، از پروردگار جهانيان است.'),
(3506, 32, 3, 'بلكه مى‌گويند اين را خود فرابافته است، [نه چنين است،] بلكه آن [سخن‌] راست و درست است از پروردگار تو تا مردمى را بيم كنى كه هيچ بيم‌كننده‌اى پيش از تو بديشان نيامده است، شايد راه يابند.'),
(3507, 32, 4, 'خداست آن كه آسمانها و زمين و آنچه را ميان آنهاست در شش روز- دوره- بيافريد، سپس بر عرش- مقام فرمانروايى بر جهان آفرينش- برآمد، شما را جز او هيچ سرپرست و كارسازى و هيچ شفيعى نيست، آيا ياد نمى‌كنيد و پند نمى‌گيريد؟'),
(3508, 32, 5, 'كار- وحى يا كار دنيا- را از آسمان تا زمين تدبير مى‌كند، سپس [آن كار] به سوى او بالا مى‌رود در روزى كه اندازه آن هزار است سال از آنچه شما مى‌شمريد.'),
(3509, 32, 6, 'اين است [وصف‌] داناى نهان و آشكارا، همان تواناى بى‌همتا و مهربان'),
(3510, 32, 7, 'آن كه هر چه آفريد به نيكوترين شيوه ساخت و آفرينش آدمى را از گِل آغاز كرد.'),
(3511, 32, 8, 'سپس نسل- فرزندان- او را از چكيده‌اى از آبى سست و پست پديد كرد.'),
(3512, 32, 9, 'آنگاه اندام او را درست و تمام ساخت و از روح خويش در او دميد و شما را گوش و ديدگان و دلها پديد كرد [ولى‌] اندك سپاس مى‌داريد.'),
(3513, 32, 10, 'و گفتند: آيا هنگامى كه در زمين گم و ناپديد شويم، باز به آفرينشى نو درخواهيم آمد؟! [نه چنين است كه مى‌گويند] بلكه آنان به ديدار پروردگارشان- روز رستاخيز- كافرند.'),
(3514, 32, 11, 'بگو: فرشته مرگ- عزرائيل- كه بر شما گماشته شده جان شما را برگيرد- پس گم و نابود نمى‌شويد-، سپس به سوى پروردگارتان بازگردانيده شويد.'),
(3515, 32, 12, 'و اگر ببينى آنگاه كه اين بزهكاران سرهاى خويش نزد خداوندشان [به خوارى و شرم‌] در پيش افكنده باشند [گويند:] بار خدايا، ديديم و شنيديم، ما را بازگردان تا كارى نيك و شايسته كنيم، كه ما بى‌گمان باوردارنده‌ايم.'),
(3516, 32, 13, 'و اگر مى‌خواستيم، هر آينه به هر كسى رهنمونى‌اش را مى‌داديم و ليكن اين سخن از من براستى [از پيش‌] رفته است كه همانا دوزخ را از پريان و آدميان همگى پُر كنم- يعنى همه كسانى را كه گمراهى را بر راه راست برگزيدند و سزاوار عذاب شدند-.'),
(3517, 32, 14, 'پس [عذاب را] بچشيد به سزاى آنكه ديدار اين روزتان را فراموش كرديد، ما هم شما را به فراموشى سپرديم- فرو گذاشتيم- و عذاب جاويدان را به سزاى آنچه مى‌كرديد بچشيد.'),
(3518, 32, 15, 'جز اين نيست كه كسانى به آيات ما ايمان مى‌آورند كه چون بدانها يادآورى شوند سجده‌كنان بيفتند و پروردگار خويش را همراه با سپاس و ستايش به پاكى ياد كنند و گردنكشى نكنند'),
(3519, 32, 16, 'پهلوهاشان از بسترها [براى نماز شب‌] دور مى‌شود، پروردگار خويش را با بيم و اميد مى‌خوانند و از آنچه روزيشان داده‌ايم انفاق مى‌كنند.'),
(3520, 32, 17, 'پس هيچ كس نمى‌داند آنچه از روشنى چشمها براى ايشان نهفته شده، به پاداش آنچه مى‌كردند.'),
(3521, 32, 18, 'آيا كسى كه مؤمن است همچون كسى است كه از فرمان [خداى‌] بيرون رفته؟ هرگز برابر نيستند.'),
(3522, 32, 19, 'اما آنان كه ايمان آوردند و كارهاى نيك و شايسته كردند، ايشان را بهشتهايى است كه براى پذيرايى در آنها جاى مى‌گزينند، به پاداش آنچه مى كردند.'),
(3523, 32, 20, 'و اما آنان كه از فرمان بيرون شدند جايگاهشان آتش- دوزخ- است، هر گاه بخواهند از آن بيرون آيند به آن برگردانده مى‌شوند، و گويندشان: بچشيد عذاب آتشى را كه دروغ مى‌انگاشتيد.'),
(3524, 32, 21, 'و هر آينه آنان را از عذاب نزديكتر و كهتر- خوارى و فروماندگى يا قحط و گزند- پيش از عذاب بزرگتر بچشانيم باشد كه بازگردند.'),
(3525, 32, 22, 'و كيست ستمكارتر از كسى كه به آيات پروردگارش يادآورى شده سپس از آن روى بگردانيد؟ همانا ما از بزهكاران انتقام گيرنده‌ايم.'),
(3526, 32, 23, 'و هر آينه موسى را كتاب- تورات- داديم. پس، از ديدار او- يعنى ديدار خداوند، كنايه از رستاخيز- در شك مباش و آن'),
(3527, 32, 24, 'و از آنان پيشوايانى قرار داديم كه به فرمان ما راه مى‌نمودند، از آن رو كه شكيبايى ورزيدند و آيات ما را بى‌گمان باور مى داشتند.'),
(3528, 32, 25, 'همانا پروردگار تو در روز رستاخيز ميان آنان در آنچه اختلاف مى كردند- در باره دين- داورى مى‌كند.'),
(3529, 32, 26, 'و آيا آنان را راه ننمود و برايشان روشن نساخت كه پيش از ايشان چه اندازه مردمانى را هلاك كرديم كه اكنون در خانه‌هاى آنها راه مى‌روند؟ هر آينه در اين نشانه‌ها و عبرتهاست آيا [به گوش دل‌] نمى‌شنوند؟'),
(3530, 32, 27, 'آيا نديدند كه ما آب را به سوى زمين خشك و بى‌گياه مى‌رانيم، پس بدان كشتى را بيرون مى‌آريم كه چهارپايانشان و خودشان از آن مى‌خورند؟ آيا نمى‌بينند؟!'),
(3531, 32, 28, 'و مى‌گويند: اگر راستگوييد اين پيروزى- يعنى پيروزى مؤمنان بر كافران كه به مؤمنان وعده داده شده- كى خواهد بود؟'),
(3532, 32, 29, 'بگو: در روز پيروزى، آنان را كه كافر شدند ايمان آوردنشان سود ندهد و نه مهلت داده شوند.'),
(3533, 32, 30, 'پس، از آنها روى بگردان و منتظر باش، كه آنان نيز منتظرند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, '‌اى پيامبر، خداى را پروا داشته باش و از كافران و منافقان فرمان مبر. همانا خدا دانا و با حكمت است.'),
(3535, 33, 2, 'و آنچه را كه از پروردگارت به تو وحى مى‌شود پيروى كن، كه خدا بدانچه مى‌كنيد آگاه است'),
(3536, 33, 3, 'و بر خدا توكل كن، و خدا كارسازى بسنده است.'),
(3537, 33, 4, 'خدا براى هيچ مردى دو دل در درون او ننهاده است و زنانتان را كه آنان را ظهار مى‌كنيد مادرانتان نگردانيده و پسرخواندگانتان را پسرانتان نساخته است، اين گفتار شماست كه به دهان خويش مى‌گوييد: و خدا راست و درست مى‌گويد و اوست كه راه مى‌نمايد.'),
(3538, 33, 5, 'آنان'),
(3539, 33, 6, 'پيامبر به مؤمنان از خودشان سزاوارتر است و همسران او مادران ايشانند و در كتاب خدا خويشاوندان برخى به برخى [در ميراث‌] سزاوارترند از مؤمنان و مهاجران، مگر آنكه بخواهيد به دوستان همكيش خود نيكى كنيد. اين [حكم‌] در كتاب- لوح محفوظ يا قرآن- نوشته شده است.'),
(3540, 33, 7, 'و [ياد كن‌] آنگاه كه از پيامبران پيمان ايشان گرفتيم، و از تو و از نوح و ابراهيم و موسى و عيسى پسر مريم و از همه ايشان پيمانى سخت و استوار گرفتيم،'),
(3541, 33, 8, 'تا [خدا] راستگويان را از راستى ايشان بپرسد، و كافران را عذابى دردناك آماده كرده است.'),
(3542, 33, 9, 'اى كسانى كه ايمان آورده‌ايد، نعمت خداى را بر خود به ياد آريد آنگاه كه شما را لشكرهايى- از طايفه‌هاى عرب- بيامد پس بادى و لشكرهايى كه نمى‌ديديد بر آنها فرستاديم، و خدا بدانچه مى‌كنيد بيناست.'),
(3543, 33, 10, 'آنگاه كه از فراز شما- بالاى وادى- و از فرود شما- پايين وادى- بر شما در آمدند، و آنگاه كه ديدگان خيره شد و دلها به گلوها رسيد و به خدا گمانها [ى گوناگون‌] برديد.'),
(3544, 33, 11, 'در آنجا مؤمنان آزمون شدند و سخت به لرزه در افتادند.'),
(3545, 33, 12, 'و آنگاه كه منافقان و آنان كه در دلهاشان بيمارى- شك و نفاق- است گفتند: خدا و پيامبرش به ما جز وعده‌اى فريبنده- فتح و پيروزى- ندادند.'),
(3546, 33, 13, 'و آنگاه كه گروهى از آنان گفتند: اى اهل مدينه شما را جاى ماندن نيست، پس بازگرديد- از لشكرگاه به مدينه-، و گروهى از آنها از پيامبر رخصت [رفتن‌] خواسته مى گفتند: خانه‌هاى ما بى‌حفاظ است- از دزد و دشمن در امان نيست- و حال آنكه آن خانه‌ها بى‌حفاظ نبود، اينان آهنگى جز گريختن نداشتند.'),
(3547, 33, 14, 'و اگر [سپاه كفار] از اطراف آن- شهر يا خانه‌ها- بر آنان در آيند و سپس از آنها فتنه و آشوب- بازگشت به شرك يا جنگ با مسلمانان- خواسته شود، هر آينه به سوى آن روند و بر [اجابت‌] آن'),
(3548, 33, 15, 'و هر آينه اينان پيش از اين با خدا پيمان بسته بودند كه پشت نكنند و نگريزند، و پيمان خدا بازخواست شدنى است.'),
(3549, 33, 16, 'بگو: اگر از مرگ يا كشته شدن بگريزيد اين گريختن هرگز شما را سود ندارد، وانگهى جز اندكى برخوردار نشويد.'),
(3550, 33, 17, 'بگو: كيست كه شما را از خداى نگاه دارد اگر در باره شما بديى يا بخشايشى خواهد. و آنان براى خويش جز خدا هيچ كارساز و ياورى نخواهند يافت.'),
(3551, 33, 18, 'همانا خدا مى‌شناسد بازدارندگان از شما را- كه مردم را از يارى‌كردن پيامبر'),
(3552, 33, 19, 'بخيلانند بر شما- در راه جهاد هيچ كمك مالى و جانى به شما نمى‌كنند- پس چون بيم [جنگ‌] پيش آيد، آنان را بينى كه به تو مى‌نگرند در حالى كه چشمهاشان [از ترس‌] مى‌گردد همچون كسى كه بيهوشى مرگ او را فرا گرفته باشد. و چون ترس [جنگ‌] از ميان برود با زبانهاى تيز و تند به سبب بخلى كه بر خير- مال و غنيمت- دارند شما را برنجانند- زبان‌درازى كنند- اينان ايمان نياورده‌اند پس خدا هم كارهاشان را بى‌برّ و نابود ساخت، و اين كار بر خدا آسان است.'),
(3553, 33, 20, 'مى‌پندارند كه [هنوز] لشكرهاى احزاب نرفته‌اند و اگر آن لشكرها بازآيند، دوست دارند كه در ميان اعراب باديه‌نشين مى‌رفتند و از خبرهاى شما مى پرسيدند و اگر هم در ميان شما باشند جز اندكى كارزار نكنند- پس سودى در ماندن آنها نيست-.'),
(3554, 33, 21, 'هر آينه شما را در [خصلت‌ها و روش‌] پيامبر خدا نمونه و سرمشق نيكو و پسنديده‌اى است، براى كسى كه به خدا و روز بازپسين اميد مى‌دارد و خداى را بسيار ياد مى‌كند.'),
(3555, 33, 22, 'و چون مؤمنان آن لشكرها را ديدند گفتند: اين است آنچه خدا و پيامبرش ما را وعده داده‌اند و خدا و پيامبرش راست گفتند، و [ديدن آن لشكرها] ايشان را جز باورداشتن و گردن‌نهادن نيفزود.'),
(3556, 33, 23, 'از مؤمنان مردانى‌اند كه آنچه را با خداى بر آن پيمان بسته بودند- بذل جان- براستى بجاى آوردند، پس برخى از ايشان پيمان خويش گزاردند- به شهادت رسيدند- و برخى از ايشان [كارزار و شهادت را] چشم همى‌دارند و [پيمان خويش را] هيچ دگرگون نساخته‌اند'),
(3557, 33, 24, 'تا خدا راستگويان را به راست گفتن و وفاداريشان پاداش دهد، و منافقان را اگر خواهد عذاب كند يا [به بخشايش خود] بر آنان باز گردد و توبه‌شان را بپذيرد، كه خدا آمرزگار و مهربان است.'),
(3558, 33, 25, 'و خدا كافران را با خشمشان بازگردانيد در حالى كه هيچ پيروزى و غنيمتى نيافتند، و خدا مؤمنان را از كارزار بسندگى كرد، و خدا نيرومند و تواناى بى‌همتاست.'),
(3559, 33, 26, 'و كسانى از اهل كتاب- جهودان- را كه با آنها- احزاب- همپشت گشتند از قلعه‌هاشان فروآورد و در دلهاشان بيم افكند، گروهى را مى‌كشتيد و گروهى را اسير مى‌گرفتيد'),
(3560, 33, 27, 'و زمينشان- مزارع و باغهاشان- و خانه‌هاشان و مالهاشان و زمينى را كه بر آن گام ننهاديد به شما ميراث داد و خداوند بر هر چيزى تواناست.'),
(3561, 33, 28, 'اى پيامبر، زنان خود را بگو: اگر زندگانى دنيا و زيور و آرايش آن مى‌خواهيد، پس بياييد تا شما را برخوردارى دهم و به روشى نيكو رهاتان سازم'),
(3562, 33, 29, 'و اگر خداى و پيامبر او و سراى واپسين مى‌خواهيد، پس [بدانيد كه‌] خداوند براى نيكوكاران از شما پاداشى بزرگ آماده كرده است.'),
(3563, 33, 30, 'اى زنان پيامبر، هر كه از شما كار زشت و گناهى آشكار كند او را عذاب دوچندان باشد، و اين بر خدا آسان است.'),
(3564, 33, 31, 'و هر كه از شما خدا و پيامبر او را با فروتنى فرمان برد و كار نيك و شايسته كند، پاداش او را دو بار بدهيم و براى او روزى نيكو و بزرگوارانه‌اى- بهشت- آماده كرده‌ايم.'),
(3565, 33, 32, 'اى زنان پيامبر، شما مانند يكى از ديگر زنان نيستيد، اگر پرهيزگار باشيد، پس در گفتار نرمى منماييد كه آن كس كه در دلش بيمارى است [در شما] طمع كند، و به شيوه‌اى پسنديده سخن گوييد.'),
(3566, 33, 33, 'و در خانه‌هاتان آرام گيريد، و به شيوه جاهليت پيشين به خودنمايى بيرون نياييد، و نماز برپا داريد و زكات بدهيد و خدا و پيامبر او را فرمان بريد. همانا خداوند مى‌خواهد از شما، خاندان پيامبر، پليدى را ببرد و شما را پاك كند، پاكى كامل.'),
(3567, 33, 34, 'و آنچه را در خانه‌هاتان از آيات خدا و حكمت- سخنان پيامبر'),
(3568, 33, 35, 'همانا مردان و زنان مسلمان و مردان و زنان مؤمن و مردان و زنان فروتن فرمانبردار و مردان و زنان راستگو و مردان و زنان شكيبا و مردان و زنان فروتن خداترس و مردان و زنان صدقه‌دهنده و مردان و زنان روزه‌دار و مردان و زنانى كه شرمگاه خود [از ناشايست‌] نگاه مى‌دارند و مردان و زنانى كه خداى را بسيار ياد مى كنند، خداوند براى آنان آمرزش و مزدى بزرگ آماده كرده است.'),
(3569, 33, 36, 'و هيچ مرد و زن مؤمنى را نرسد كه چون خداى و پيامبر او كارى را فرمايند آنان را در آن كارشان بگزينى- اختيارى- باشد. و هر كه خداى و پيامبرش را نافرمانى كند براستى گمراه شده گمراهيى آشكار.'),
(3570, 33, 37, 'و [ياد كن‌] آنگاه كه به آن كس كه خداى به او نعمت داده بود- نعمت اسلام و ايمان- و تو نيز به او نعمت داده بودى- نعمت آزادى- گفتى: همسرت را براى خود نگاه دار و از خدا پروا داشته باش، و در دل خويش چيزى را پنهان مى‌داشتى كه خدا آشكار كننده آن است، و از مردم بيم داشتى و حال آنكه خداوند سزاوارتر است كه از او بيم بدارى. پس چون زيد حاجت [خويش‌] از او برآورد- و وى را طلاق داد- او را به زنى به تو داديم تا بر مؤمنان درباره [ازدواج با] زنان پسرخواندگانشان تنگى و باكى نباشد هر گاه كه [پسرخواندگانشان‌] حاجت خود را از آنان برآورده باشند و فرمان خدا شدنى است.'),
(3571, 33, 38, 'بر پيامبر هيچ تنگى و گناهى نيست در آنچه خداوند براى او مقرر و روا داشته- يعنى تزويج زن پسرخوانده او، تا حكم جاهليت را باطل كند- نهاد خداست درباره آنان كه پيش از اين گذشتند- پيامبران پيشين-. و كار و فرمان خدا به اندازه و حساب‌شده است'),
(3572, 33, 39, 'همانان كه پيامهاى خداى را مى‌رسانند و از او مى‌ترسند و از هيچ كس جز او نمى‌ترسند، و خدا حسابگرى بسنده است.'),
(3573, 33, 40, 'محمد- ص- پدر هيچ يك از مردان شما نيست و ليكن فرستاده خدا و خاتم- سرآمد و پايان‌بخش- پيامبران است، و خداى به هر چيزى داناست.'),
(3574, 33, 41, 'اى كسانى كه ايمان آورده‌ايد، خداى را بسيار ياد كنيد،'),
(3575, 33, 42, 'و او را بامداد و شبانگاه- در همه شبانه روز- تسبيح گوييد.'),
(3576, 33, 43, 'اوست آن كه بر شما درود- رحمت- مى‌فرستد و فرشتگان او نيز تا شما را از تاريكيها- كفر و گمراهى- به روشنايى- ايمان و طاعت- بيرون آورد، و او به مؤمنان مهربان است.'),
(3577, 33, 44, 'درودشان- درودى كه به آنان گفته شود- آن روز كه با او ديدار كنند «سلام» است، و ايشان را مزدى نيكو و بزرگوارانه آماده كرده است.'),
(3578, 33, 45, 'اى پيامبر، ما تو را گواه و مژده‌دهنده و بيم‌كننده فرستاديم،'),
(3579, 33, 46, 'و خواننده به سوى خدا به فرمان او و چراغ تابان.'),
(3580, 33, 47, 'و مؤمنان را نويد ده كه آنان را از جانب خدا فزون‌بخشى بزرگ باشد.'),
(3581, 33, 48, 'و كافران و منافقان را فرمان مبر و رنج و آزارشان را [كه به تو رسيد] رها كن- مكافات مكن- و بر خدا توكل كن، و خدا كارسازى بسنده است.'),
(3582, 33, 49, 'اى كسانى كه ايمان آورده‌ايد، هر گاه زنان مؤمنى را كه به همسرى گرفته‌ايد پيش از آنكه بديشان برسيد- آميزش كنيد- طلاق دهيد شما را بر آنها هيچ عده‌اى كه آن را بشماريد- تا به سر آرند- نيست. پس ايشان را برخوردار كنيد و به شيوه نيكو رهاشان سازيد.'),
(3583, 33, 50, 'اى پيامبر، ما آن زنان تو را كه كابين ايشان را داده باشى و آن [كنيزانى‌] را كه از آنچه خداوند از بهره جنگ بر تو ارزانى داشته مالك شده‌اى، و دختران عمويت و دختران عمه‌هايت و دختران دايى‌ات و دختران خاله‌هايت را كه با تو مهاجرت كرده باشند و نيز زن مؤمنى را چنانچه خويشتن را به پيامبر ببخشد- بدون مهر-، اگر پيامبر خواهد كه او را به زنى گيرد، براى تو حلال كرديم [و اين‌] تنها ويژه توست نه ديگر مؤمنان- همانا مى‌دانيم كه بر آنان درباره همسرانشان و آنچه مالك آن شده‌اند- كنيزان- چه مقرر كرده‌ايم- تا بر تو هيچ تنگى نباشد و خدا آمرزگار و مهربان است.'),
(3584, 33, 51, 'هر كه را از ايشان خواهى [مى‌توانى‌] واپس دارى و هر كه را خواهى نزد خويش جاى دهى و نگاه دارى. و اگر هر يك از آنان را كه از او كناره گرفته باشى باز بجويى باكى و گناهى بر تو نيست. اين نزديكتر است به آنكه چشمهاى ايشان روشن شود و اندوهناك نباشند و همگى به آنچه بداديشان خشنود شوند- يعنى چون دانستند كه آنچه مى‌كنى به فرمان خداست دلتنگ نمى‌شوند و گردن نمى‌نهند- و خدا آنچه را در دلهاى شماست مى‌داند، و خدا دانا و بردبار است.'),
(3585, 33, 52, 'از اين پس- يعنى پس از اين زنان- [ديگر] زنان تو را حلال نباشند و نه اينكه به جاى ايشان زنانى بگزينى اگر چه زيبايى آنان تو را به شگفت آورد، مگر آنچه دست تو مالك شود- يعنى كنيزان-، و خداوند بر هر چيزى نگاهبان است.'),
(3586, 33, 53, 'اى كسانى كه ايمان آورده‌ايد، به خانه‌هاى پيامبر درنياييد مگر آنكه شما را به خوردن طعامى رخصت دهند بى‌آنكه چشم به راه ظرف آن باشيد- زودتر از وقت مقرر نرويد- و ليكن چون شما را بخوانند، در آييد، و چون خورديد پراكنده شويد بى‌آنكه [پس از خوردن‌] سرگرم سخن شويد همانا اين كار پيامبر را رنج مى‌دهد و از شما شرم مى‌دارد- كه خواهش كند بيرون رويد- ولى خدا از [گفتن سخن‌] حق شرم نمى‌دارد. و چون از آنان'),
(3587, 33, 54, 'اگر چيزى را آشكار كنيد يا پنهان داريد، همانا خدا به هر چيزى داناست.'),
(3588, 33, 55, 'بر آنان باكى و گناهى نيست در [نداشتن حجاب‌] پيش پدرانشان و پسرانشان و برادرانشان و پسران برادرانشان و پسران خواهرانشان و زنان [همكيش‌] شان و آنچه دستهاشان مالك آن است- بردگان-، و [اى آنان‌] از خداى پروا داشته باشيد، كه خدا بر هر چيزى گواه است.'),
(3589, 33, 56, 'همانا خداى و فرشتگان او بر پيامبر درود مى‌فرستند، اى كسانى كه ايمان آورده‌ايد، بر او درود فرستيد- يعنى بگوييد: اللّهم صلّ على محمّد و آل محمّد- و سلام گوييد سلامى در خور و شايسته- يا فرمان او را چنانكه شايسته است گردن نهيد-.'),
(3590, 33, 57, 'همانا كسانى كه خدا و پيامبرش را مى‌آزارند خدا آنان را در اين جهان و آن جهان لعنت كرده و براى آنان عذابى خواركننده آماده ساخته است.'),
(3591, 33, 58, 'و آنان كه مردان و زنان مؤمن را بى‌آنكه بدى و گناهى كرده باشند مى‌آزارند هر آينه بار دروغ و گناهى آشكار را برداشته‌اند.'),
(3592, 33, 59, 'اى پيامبر، زنان و دختران خويش و زنان مؤمنان را بگو: چادرهاى خود را بر خويشتن فروپوشند- خود را بپوشانند-. اين نزديكتر است به آنكه شناخته شوند- به صلاح و عفت- و آزارشان ندهند و خدا آمرزگار و مهربان است.'),
(3593, 33, 60, 'اگر منافقان و كسانى كه در دلهاشان بيمارى است و آنان كه در مدينه خبرهاى دروغ مى‌پراكنند [از اين كار بد خود] باز نايستند هر آينه تو را بر ضد آنان برانگيزيم سپس در آن'),
(3594, 33, 61, 'لعنت‌شدگانند هر جا يافته شوند بايد گرفته و بسختى كشته شوند.'),
(3595, 33, 62, 'نهاد خداست در باره كسانى كه پيش از اين گذشتند، و هرگز نهاد خدا را دگرگونى نيابى.'),
(3596, 33, 63, 'مردم تو را از هنگام رستاخيز مى‌پرسند، بگو: همانا دانش آن نزد خداست و تو را چه آگاه كرد- تو چه دانى- شايد رستاخيز نزديك باشد.'),
(3597, 33, 64, 'همانا خدا كافران را لعنت كرده و براى آنان آتش افروخته آماده ساخته است،'),
(3598, 33, 65, 'هميشه در آن'),
(3599, 33, 66, 'روزى كه رويهاشان در آتش گردانده شود، گويند: اى كاش خداى را فرمان مى‌برديم و [اى كاش‌] پيامبر را فرمان مى‌برديم.'),
(3600, 33, 67, 'و گويند: پروردگارا، ما مهتران و بزرگان خود را فرمان برديم پس ما را گمراه كردند.'),
(3601, 33, 68, 'پروردگارا، آنان را از عذاب دو چندان ده- زيرا هم گمراه بودند و هم گمراه‌كننده- و آنها را لعنت كن لعنتى بزرگ.'),
(3602, 33, 69, 'اى كسانى كه ايمان آورده‌ايد، مانند آن كسان مباشيد كه موسى را بيازردند پس خدا او را از آنچه گفته بودند پاك ساخت، و او نزد خدا آبرومند بود.'),
(3603, 33, 70, 'اى كسانى كه ايمان آورده‌ايد، از خدا پروا كنيد و سخن درست و استوار گوييد،'),
(3604, 33, 71, 'تا كارهاى شما را به صلاح و سامان آرد و گناهان شما را بيامرزد، و هر كه خدا و پيامبرش را فرمان برد براستى به كاميابى و پيروزى بزرگى دست يافته است.'),
(3605, 33, 72, 'همانا ما امانت را بر آسمانها و زمين و كوه‌ها عرضه كرديم، ولى از برداشتن- پذيرفتن- آن سر باز زدند و از آن ترسيدند و آدمى آن را برداشت- پذيرفت-، براستى كه او ستمگر و نادان است،'),
(3606, 33, 73, 'تا [سرانجام‌] خدا مردان و زنان منافق و مردان و زنان مشرك را عذاب كند، و [به مهر و بخشايش خويش‌] بر مردان و زنان مؤمن باز گردد- به جهت حفظ امانت-، و خداوند آمرزگار و مهربان است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'ستايش و سپاس خداى را كه او راست هر چه در آسمانها و هر چه در زمين است، و در آن جهان [نيز] ستايش و سپاس از آن اوست، و اوست داناى درست كار و آگاه.'),
(3608, 34, 2, 'هر چه در زمين فرورود و هر چه از آن بيرون آيد و هر چه از آسمان فرود آيد و هر چه در آن بر شود [همه را] مى‌داند. و اوست مهربان و آمرزگار.'),
(3609, 34, 3, 'و كسانى كه كافر شدند گفتند: ما را رستاخيز نيايد. بگو: [نه آنست كه شما مى‌گوييد] آرى، به پروردگارم سوگند كه هر آينه شما را خواهد آمد، [پروردگارى كه‌] داناى نهان و ناپيداست در آسمانها و در زمين همسنگ ذره‌اى از او پوشيده و پنهان نيست و نه خردتر از آن و نه بزرگتر مگر آنكه در كتابى روشن- لوح محفوظ- هست'),
(3610, 34, 4, 'تا كسانى را كه ايمان آوردند و كارهاى نيك و شايسته كردند پاداش دهد. ايشانند كه آمرزش و روزى نيكو و بزرگوارانه دارند.'),
(3611, 34, 5, 'و كسانى كه در [انكار و ابطال‌] آيات ما كوشيدند كه [به پندار خويش‌] ما را به عجز آرند، آنان را عذابى است سخت و دردناك.'),
(3612, 34, 6, 'و آنان كه به ايشان دانش داده‌اند- اصحاب پيامبر'),
(3613, 34, 7, 'و كسانى كه كافر شدند گويند: آيا شما را به مردى راه نمائيم كه خبر مى‌دهد كه چون [در خاك‌] پاره پاره و ريز ريز شويد هر آينه در آفرينشى نو درخواهيد آمد؟!'),
(3614, 34, 8, 'آيا بر خدا دروغى بافته يا او را ديوانگى است؟ [نه،] بلكه كسانى كه سراى واپسين را باور ندارند در عذاب و گمراهى دور [از حق‌] اند.'),
(3615, 34, 9, 'آيا به آنچه فرارويشان و آنچه پشت سرشان است از آسمان و زمين ننگريسته‌اند؟ اگر خواهيم آنان را در زمين فرو بريم يا پاره‌اى از آسمان را بر آنان فروافكنيم. هر آينه در اين [هشدار] براى هر بنده بازگردنده [به خداوند] نشانه و عبرتى است.'),
(3616, 34, 10, 'و هر آينه داوود را از نزد خويش فزونى و برتريى داديم: اى كوه‌ها و اى مرغان هوا، با او [به تسبيح‌] همنوا شويد. و آهن را براى او نرم گردانيديم،'),
(3617, 34, 11, 'كه زره‌هايى فراخ و گشاده بساز و در بافتن و پيوستن [حلقه‌هاى آن‌] اندازه نگاه دار و [تو اى داوود و قوم تو] كارهاى نيك و شايسته كنيد، كه من بدانچه مى كنيد بينايم.'),
(3618, 34, 12, 'و باد را براى سليمان [رام كرديم‌]، كه بامدادان يك ماهه راه مى‌پيمود و شبانگاه يك ماهه راه مى‌پيمود. و چشمه مس [گداخته‌] را براى او روان ساختيم، و از ديوان كسانى بودند كه به فرمان پروردگارش پيش او كار مى‌كردند و هر كه از فرمان ما كجروى مى‌كرد- فرمان سليمان نمى‌برد- او را از عذاب آتش افروخته مى‌چشانديم.'),
(3619, 34, 13, 'براى او آنچه مى‌خواست از نمازگاه‌ها- يا كوشكها- و تنديسها و كاسه‌هايى به اندازه حوضها و ديگهاى بزرگ پابرجا و استوار [در زمين‌] مى‌ساختند. اى خاندان داود، به سپاسدارى [اين نعمتها] عمل [و عبادت‌] كنيد، و اندكى از بندگان من پيوسته سپاسگزارند.'),
(3620, 34, 14, 'پس چون فرمان مرگ را بر او رانديم، آنان را بر مرگ او رهنمونى نكرد مگر جنبنده زمين- كرم چوب‌خوار موريانه- كه عصايش را مى‌خورد، پس چون [سليمان‌] بيفتاد ديوان دريافتند كه اگر غيب مى‌دانستند- از مرگ سليمان آگاهى داشتند- در آن عذاب خواركننده درنگ نمى‌كردند.'),
(3621, 34, 15, 'هر آينه مردم سبا- در يمن- را در جاى سكونتشان نشانه‌اى- بر قدرت خداوند- بود: دو بوستان، از دست راست و از دست چپ [مسكنهاشان قرار داشت‌]. [گفتيم:] از روزى پروردگارتان بخوريد و او را سپاس گزاريد، شهرى است خوش [و پر نعمت‌] و خداوندى آمرزگار.'),
(3622, 34, 16, 'ولى روى گردانيدند- از فرمان خدا يا از سپاسگزارى- پس سيل بسيار سخت بر آنان فرستاديم و دو بوستانشان را به دو بوستانى ديگر بدل كرديم با ميوه‌اى تلخ و شوره‌گز و اندكى از كُنار- سدر-.'),
(3623, 34, 17, 'آن [كيفر] را به سزاى آنكه كافر شدند و ناسپاسى كردند به آنان داديم، و آيا جز ناسپاس را كيفر مى‌دهيم؟'),
(3624, 34, 18, 'و ميان آنان و آباديهايى كه در آنها بركت نهاده بوديم- سرزمين شام- آباديهاى پيدا و پيوسته پديد آورديم، و در آنها آمد و شد را به اندازه كرديم- فاصله آباديها برابر بود-، [و گفتيم:] در آنها شبها و روزها ايمن و بى‌بيم رفت و آمد كنيد.'),
(3625, 34, 19, 'ولى گفتند: پروردگارا، ميان شهرهاى ما دورى افكن و بر خويشتن ستم كردند. پس آنان را افسانه‌ها و داستانها كرديم و سخت پراكنده‌شان ساختيم. هر آينه در اين- سرگذشت آنها- براى هر شكيباى سپاسگزارى نشانه‌ها و عبرتهاست.'),
(3626, 34, 20, 'و هر آينه ابليس گمان خود را در باره آنان- مردم سبا يا همه مردم- راست داشت پس او را پيروى كردند مگر گروهى اندك از مؤمنان.'),
(3627, 34, 21, 'و او را بر آنان هيچ چيرگى و دستى نبود جز براى آنكه [مى‌خواستيم‌] كسانى را كه به سراى واپسين ايمان دارند از كسانى كه از آن به شك اندرند باز شناسيم، و پروردگار تو بر هر چيزى نگاهبان است.'),
(3628, 34, 22, 'بگو: آنها را كه به جاى خداى يكتا [خدا] مى‌پنداريد بخوانيد، همسنگ ذره‌اى را در آسمانها و در زمين مالك نيستند، و آنان را در آن دو- آسمان و زمين- هيچ شركتى نيست و او خداى يكتا را از آنها هيچ يار و مددكارى نيست.'),
(3629, 34, 23, 'و شفاعت نزد او'),
(3630, 34, 24, 'بگو: چه كسى شما را از آسمانها و زمين روزى مى‌دهد؟ بگو: خداى و هر آينه ما يا شما بر راه راستيم يا در گمراهى آشكار.'),
(3631, 34, 25, 'بگو: شما را از بزهكارى ما نپرسند و ما را از آنچه شما مى‌كنيد نخواهند پرسيد.'),
(3632, 34, 26, 'بگو: پروردگارمان، ما و شما را [روز رستاخيز] گرد آورد، سپس ميان ما براستى و درستى داورى كند- يا جدايى افكند-، و اوست داور دانا.'),
(3633, 34, 27, 'بگو: آنها را كه به انبازى به او بسته‌ايد به من بنماييد- آيا صفات شايسته خدايى در آنها هست؟- هرگز، بلكه اوست خداى توانمند بى‌همتا و داناى با حكمت.'),
(3634, 34, 28, 'و تو را نفرستاديم مگر براى همه مردم، مژده‌دهنده و بيم‌كننده، ولى بيشتر مردم نمى‌دانند.'),
(3635, 34, 29, 'و گويند: اگر راستگوييد، اين وعده- رستاخيز- كى خواهد بود؟'),
(3636, 34, 30, 'بگو: شما را وعده‌گاه روزى است كه ساعتى از آن واپس نرويد و پيش نيفتيد.'),
(3637, 34, 31, 'و كسانى كه كافر شدند گفتند: هرگز به اين قرآن ايمان نياوريم و نه به آن [كتاب‌] كه پيش از آن بوده و اگر ببينى آنگاه كه آن ستمكاران- كافران- نزد خداوندشان [براى حسابرسى‌] بازداشته شوند! برخى‌شان سخن را به برخى ديگر برمى‌گردانند، آنها كه ناتوان و زبون گرفته شده‌اند- زيردستان- به آنان كه گردنكشى كردند- رهبران و پيشوايان قدرتمند خود-، گويند: اگر شما نبوديد، هر آينه ما مؤمن بوديم.'),
(3638, 34, 32, 'آنان كه گردنكشى كردند به آنها كه ناتوان و زبون گرفته شده‌اند گويند: آيا ما شما را از راه راست، پس از آنكه به شما آمد، باز داشتيم؟! [نه،] بلكه شما خود گناهكار بوديد.'),
(3639, 34, 33, 'و زبون‌شدگان به گردنكشان گويند: بلكه ترفند و نيرنگ شب و روز [شما] بود كه ما را فرمان داديد كه به خدا كافر شويم و براى او همتايانى فراگيريم. و چون عذاب را ببينند [همه آنها] پشيمانى نهان دارند- از بيم سرزنش ديگران-. و ما غُلها را در گردن آنان كه كافر شدند بنهيم. آيا جز آنچه مى‌كردند كيفر داده مى‌شوند؟'),
(3640, 34, 34, 'و ما هيچ بيم‌دهنده‌اى در هيچ آبادى و شهرى نفرستاديم مگر آنكه كامرانان و توانگران آن گفتند: ما به آنچه بدان فرستاده شده‌ايد كافريم'),
(3641, 34, 35, 'و گفتند: ما مالها و فرزندان بيشتر داريم و ما عذاب‌شونده نيستيم- يعنى چون خدا ما را به نعمت مال و فرزند گرامى داشته، ما را عذاب نخواهد كرد-.'),
(3642, 34, 36, 'بگو: پروردگار من است كه روزى را براى هر كه بخواهد فراخ مى‌سازد و تنگ مى‌گرداند و ليكن بيشتر مردم نمى‌دانند'),
(3643, 34, 37, 'و مالها و فرزندانتان آن [فضيلتى‌] نيست كه شما را به ما نزديك گرداند مگر كسانى كه ايمان آوردند و كار نيك و شايسته كردند، پس اينانند كه بدانچه كردند پاداشى دو چندان دارند و ايشان در كوشكهاى بلند بهشت ايمن و آسوده باشند.'),
(3644, 34, 38, 'و آنان كه در [انكار و ابطال‌] آيات ما مى‌كوشند كه [به گمان خود] ما را به عجز آرند، اينان در عذاب حاضرشدگانند.'),
(3645, 34, 39, 'بگو: همانا پروردگار من روزى را براى هر كه از بندگانش كه بخواهد فراخ مى‌سازد و تنگ مى‌گرداند. و هر چه انفاق كنيد پس او به جاى آن باز دهد، و او بهترين روزى‌دهندگان است.'),
(3646, 34, 40, 'و روزى كه همه آنان- مشركان- را برانگيزد، سپس به فرشتگان گويد: آيا اينان شما را مى‌پرستيدند؟'),
(3647, 34, 41, 'گويند: پاك و منزهى تو، تويى خداوند- يا دوست- ما، نه آنها. بلكه ديوان را مى‌پرستيدند، كه بيشترشان به آنها گرويده بودند.'),
(3648, 34, 42, 'پس امروز- روز رستاخيز- برخى از شما براى برخ ديگر هيچ سود و زيانى در دست ندارند. و ستمكاران را گوييم: بچشيد عذاب آتشى را كه دروغ مى انگاشتيد.'),
(3649, 34, 43, 'و چون آيات روشن ما بر آنان خوانده شود گويند: اين- محمد'),
(3650, 34, 44, 'و ما به آنان- مشركان عرب- [پيش از اين‌] هيچ كتابى كه آن را بخوانند نداده‌ايم، و پيش از تو هيچ بيم‌كننده‌اى به سوى آنها نفرستاده‌ايم.'),
(3651, 34, 45, 'و كسانى كه پيش از آنها بودند [پيامبران را] دروغگو شمردند، و [اين كافران قوم تو] به ده يك آنچه به آنان داديم نرسيده‌اند، ولى [آن كافران‌] پيامبران مرا به دروغ نسبت دادند، پس [بنگر كه‌] ناپسنديدن و كيفر من چگونه بود.'),
(3652, 34, 46, 'بگو: همانا شما را به يك سخن پند مى‌دهم، و آن اينكه دو دو و يك يك براى خدا به پا خيزيد، سپس بينديشيد كه اين يار شما- پيامبر- را هيچ ديوانگى نيست. او نيست مگر بيم‌كننده‌اى براى شما پيش از فرارسيدن عذابى سخت- در قيامت-.'),
(3653, 34, 47, 'بگو: هر مزدى كه از شما خواسته‌ام پس آن شما را باد- يعنى هيچ مزدى نمى‌خواهم- مزد من نيست مگر بر خداى، و او بر هر چيزى گواه است.'),
(3654, 34, 48, 'بگو: همانا پروردگار من حق- قرآن يا سخن راست و درست- را [به وحى بر قلب من‌] مى‌افكند، پروردگارى كه داناى نهانهاست.'),
(3655, 34, 49, 'بگو: حق- قرآن يا اسلام- آمد و باطل- شيطان يا بت- نه [چيزى نو] آغاز كند و نه [چيزى را] باز گرداند- يا باطل را آغاز و انجامى نيست-.'),
(3656, 34, 50, 'بگو: اگر گمراه شوم، جز اين نيست كه به زيان خويشتن گمراه مى‌شوم- وبال آن به خودم برمى‌گردد- و اگر راه راست يابم به سبب آن است كه پروردگارم به من وحى مى‌كند، همانا او شنوا و نزديك است.'),
(3657, 34, 51, 'و اگر [كافران را] ببينى آنگاه كه ترسان و هراسان شوند پس گريز و رهايى نباشد و آنان را از جايى نزديك [به عذاب‌] فرا گيرند.'),
(3658, 34, 52, 'و گويند: [اينك‌] به آن'),
(3659, 34, 53, 'و حال آنكه پيش از اين به آن كافر شدند، و از جايگاهى دور- در دنيا- به ناديده- از روى جهل و گمان- سخن مى‌پراكندند.'),
(3660, 34, 54, 'و ميان آنان و آنچه آرزو كنند- از لذتهاى مادى دنيا- جدايى افتد چنانكه با همانندان آنها نيز پيش از اين چنين رفتار شد، كه آنان نيز [در باره رستاخيز] سخت در شك و ترديد بودند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'سپاس و ستايش خداى راست، آفريننده آسمانها و زمين همو كه فرشتگان را فرستادگانى كرد داراى بالهايى دو گانه و سه گانه و چهار گانه در آفرينش هر چه خواهد مى‌افزايد همانا خدا بر هر چيزى تواناست.'),
(3662, 35, 2, 'هر بخشايشى كه خداى براى مردمان بگشايد آن را بازدارنده‌اى نيست و هر چه را بازگيرد آن را پس از [بازگرفتن‌] وى فرستنده‌اى نباشد، و اوست تواناى بى‌همتا و داناى با حكمت.'),
(3663, 35, 3, 'اى مردم، نعمت خداى را بر خود ياد كنيد آيا جز خدا آفريدگارى هست كه شما را از آسمان و زمين روزى دهد؟ هيچ خدايى جز او نيست، پس چگونه و به كجا گردانيده مى‌شويد؟'),
(3664, 35, 4, 'و اگر تو را دروغگو خوانند همانا فرستادگانى پيش از تو نيز دروغگو خوانده شدند، و كارها به خداى بازگردانده مى‌شود.'),
(3665, 35, 5, 'اى مردم، همانا وعده خدا راست است، پس مبادا زندگانى اين جهان شما را بفريبد، و مبادا آن فريبكار- شيطان- شما را به [بخشايش و كرم‌] خدا بفريبد.'),
(3666, 35, 6, 'همانا شيطان دشمن شماست، پس او را دشمن گيريد. جز اين نيست كه گروه خويش را مى‌خواند تا از دوزخيان شوند.'),
(3667, 35, 7, 'كسانى كه كافر شدند عذابى سخت دارند، و كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند آمرزش و مزدى بزرگ دارند.'),
(3668, 35, 8, 'پس آيا كسى كه كردار زشتش در نظرش آراسته شده و آن را نيكو بيند، [مانند كسى است كه براستى نيكوكار است‌]؟ پس خدا هر كه را خواهد- سزاوار بداند- گمراه كند و هر كه را خواهد- شايسته بداند- راه نمايد. پس مبادا جان تو بر سر اندوه و دريغ بر آنها برود- هلاك شوى-، همانا خداوند بدانچه مى‌كنند داناست.'),
(3669, 35, 9, 'و خداست كه بادها را فرستاد تا ابر را برمى‌انگيزد، پس آن'),
(3670, 35, 10, 'هر كه بزرگى و ارجمندى خواهد، پس [بداند كه‌] بزرگى و ارجمندى همه از آن خداست.- و به هر كه خواهد دهد- سخن نيكو و پاك به سوى او بالا مى رود و كردار نيك و شايسته آن را بالا مى‌برد. و كسانى كه بديها مى‌انديشند آنان را عذابى سخت است، و ترفند و نيرنگ اينان نابود مى‌گردد.'),
(3671, 35, 11, 'و خدا شما را از خاكى و سپس از نطفه‌اى آفريد آنگاه شما را جفتها- مردان و زنان- گردانيد، و هيچ زنى بار نگيرد- باردار نشود- و بار ننهد- نزايد- مگر به دانش او و به هيچ كسى زندگانى دراز داده نشود و از عمر هيچ كس كاسته نگردد مگر آنكه در كتابى- لوح محفوظ- ثبت است، همانا اين كار بر خدا آسان است.'),
(3672, 35, 12, 'و دو دريا يكسان و برابر نيستند: اين يك شيرين و خوشكام كه آشاميدنش گوارا است و آن ديگر شور و تلخ. و از هر يك گوشت تازه- ماهى- مى‌خوريد و پيرايه و زيورى- مرواريد- بيرون مى‌آريد كه مى‌پوشيد. و كشتيها را در آن مى‌بينى كه آب را مى‌شكافند تا از فزونى و بخشش او- روزى- بجوييد و باشد كه سپاس گزاريد.'),
(3673, 35, 13, 'شب را در روز درمى‌آورد و روز را در شب- اشاره است به كاهش و افزايش شب و روز- و خورشيد و ماه را رام كرد و هر يك تا سرآمدى نامبرده روان است. اين است خداى، پروردگار شما، پادشاهى او راست و آنان كه به جاى او مى‌خوانيد- مى‌پرستيد- پوست هسته خرمايى- كمترين چيزى- را دارا نيستند.'),
(3674, 35, 14, 'اگر بخوانيدشان خواندن شما را نشنوند، و اگر بشنوند پاسختان ندهند، و روز رستاخيز به انباز آوردنتان كافر شوند، و هيچ كس تو را مانند [خداى‌] آگاه [به حقيقت كارها] آگاه نكند.'),
(3675, 35, 15, 'اى مردم، شماييد نيازمندان به خدا، و خداست بى‌نياز و ستوده.'),
(3676, 35, 16, 'اگر خواهد، شما را ببرد و آفريده‌اى نو بيارد.'),
(3677, 35, 17, 'و اين كار بر خداى دشوار نيست.'),
(3678, 35, 18, 'و هيچ كس بار گناه ديگرى را بر ندارد. و اگر گرانبارى كسى را به برداشتن بار خويش بخواند، آن كس چيزى از آن بر ندارد گرچه خويشاوند [وى‌] باشد. جز اين نيست كه تو كسانى را بيم مى‌دهى- بيم‌دادن تو تنها در كسانى اثر مى‌كند- كه از پروردگارشان در نهان مى‌ترسند و نماز برپا داشته‌اند و هر كه پاكى ورزد- از پليدى گناهان- همانا به سود خويشتن پاكى مى‌ورزد، و بازگشت همه به سوى خداست.'),
(3679, 35, 19, 'و نابينا و بينا- كافر با مؤمن، نادان با دانا، نابخر با خردمند- برابر نباشند'),
(3680, 35, 20, 'و نه تاريكيها و روشنايى- كفر با ايمان-'),
(3681, 35, 21, 'و نه سايه و گرماى سوزان- بهشت با دوزخ-'),
(3682, 35, 22, 'و نيز زندگان و مردگان- مؤمنان با كافران- برابر نيستند. همانا خدا آن را كه خواهد- شايسته بداند- مى‌شنواند. و تو كسانى را كه در گورند- مرده‌دلان كافران- نشنوانى'),
(3683, 35, 23, 'تو جز بيم‌كننده‌اى نيستى.'),
(3684, 35, 24, 'همانا تو را براستى و درستى مژده دهنده و بيم‌كننده فرستاديم و هيچ امتى نبود مگر آنكه در ميانشان بيم‌كننده‌اى- پيامبرى- گذشت.'),
(3685, 35, 25, 'و اگر تو را دروغگو شمرند پس همانا كسانى كه پيش از آنان بودند پيامبرانشان را كه با حجتهاى روشن- معجزه‌ها- و نوشته‌ها و كتاب روشنگر به نزدشان آمده بودند دروغگو شمردند.'),
(3686, 35, 26, 'سپس آنان را كه كفر ورزيدند بگرفتم، پس ناپسندداشتن و كيفر من چگونه بود؟'),
(3687, 35, 27, 'آيا نديده‌اى كه خداى از آسمان- از ابر- آبى فرود آورد، پس با آن ميوه‌هاى رنگارنگ بيرون آورديم، و از كوه‌ها راه‌هايى به رنگهاى گوناگون سفيد و سرخ و سخت سياه است؟'),
(3688, 35, 28, 'و همچنين از مردم و جنبندگان و چهارپايان رنگهاى گوناگون هست. از بندگان خدا تنها دانشوران- عالمان ربانى- از او مى‌ترسند. همانا خدا تواناى بى‌همتا و آمرزگار است.'),
(3689, 35, 29, 'همانا كسانى كه كتاب خداى را مى‌خوانند و نماز برپا داشته‌اند و از آنچه روزيشان كرده‌ايم نهان و آشكارا انفاق كرده‌اند، تجارتى را اميد مى‌دارند كه هرگز زيان نكند و ناروا- كساد و بى‌رونق- نشود'),
(3690, 35, 30, 'تا [خداوند] مزدهاى ايشان را تمام بدهد و از فزون‌بخشى خويش بيفزايدشان، كه او آمرزگار و سپاسدار است- مزد بندگان را تباه نمى‌سازد-.'),
(3691, 35, 31, 'و آنچه از اين كتاب به تو وحى كرديم راست و درست است، آن [كتابهايى‌] را كه در پيش آن است باور مى‌دارد و راست مى‌انگارد. هر آينه خداى به بندگان خود آگاه و بيناست.'),
(3692, 35, 32, 'سپس [اين‌] كتاب- قرآن- را به كسانى از بندگانمان كه برگزيديم ميراث داديم. پس، از آنان برخى بر خود ستمكار بودند و برخى ميانه رو، و از ايشان برخى به خواست و فرمان خدا به نيكيها پيشى گيرنده‌اند. اين است فزونى و بخشش بزرگ'),
(3693, 35, 33, 'بهشت‌هايى پاينده كه در آنها در آيند، در آنجا با دستواره‌هايى از طلا و با مرواريد زيور شوند، و پوشش ايشان در آنجا پرنيان است.'),
(3694, 35, 34, 'و گويند: سپاس و ستايش خداى راست كه اندوه از ما ببرد، هر آينه پروردگار ما آمرزگار و سپاسدار است.'),
(3695, 35, 35, 'آن [خدايى‌] كه ما را از فضل خويش در [اين‌] سراى ماندنى و جاويدان فرود آورد، در آنجا نه رنجى به ما رسد و نه درماندگى.'),
(3696, 35, 36, 'و كسانى كه كافر شدند آنان را آتش دوزخ است، نه حكم شود بر آنها [به مرگ‌] تا بميرند و نه عذابش از آنها سبك شود. اينچنين هر ناسپاسى را سزا مى‌دهيم.'),
(3697, 35, 37, 'و آنها در آنجا فرياد كنند: خداوندا، ما را بيرون‌آر تا جز آن كه مى‌كرديم كار نيك و شايسته كنيم. [گوييم:] آيا شما را چندان عمر نداديم كه هر كه پندپذير است در آن مدت پند گيرد؟ و شما را بيم‌دهنده هم آمد، پس بچشيد كه ستمكاران را هيچ ياورى نيست.'),
(3698, 35, 38, 'همانا خداوند داننده ناپيداهاى آسمانها و زمين است، و او به آنچه در سينه‌ها نهفته است داناست.'),
(3699, 35, 39, 'اوست كه شما را در زمين جانشين [پيشينيان‌] گردانيد، پس هر كه كافر شود كفرش به زيان اوست. و كافران را كفرشان به نزد خداوندشان جز دشمنى و خشم نيفزايد و كافران را كفرشان جز زيانكارى نيفزايد.'),
(3700, 35, 40, 'بگو: آيا ديده‌ايد شريكان خود- بتها- را كه به جاى خداى يكتا مى‌خوانيد؟ مرا بنماييد كه از زمين چه چيز آفريده‌اند. يا آنان را در آسمانها شركتى هست؟ يا به آنها كتابى داده‌ايم كه بر حجّتى از آنند؟ [نه،] بلكه ستمكاران برخى به برخ ديگر جز فريب وعده نمى‌دهند.'),
(3701, 35, 41, 'خدا آسمانها و زمين را نگاه مى‌دارد از اينكه از جايگاه- مسير و مدار- خود بلغزند و به در روند، و اگر بلغزند هيچ كس، پس از او، نتواند آنها را نگاه داشت. همانا اوست بردبار و آمرزگار- كه آنها را به كيفر شما بر سرتان فرونمى‌افكند-.'),
(3702, 35, 42, 'و [مشركان عرب‌] به خدا سوگند خوردند، سخت‌ترين سوگندها، كه اگر آنان را بيم‌دهنده‌اى بيايد بى‌گمان راه‌يافته‌تر از هر امتى خواهند بود پس چون بيم‌دهنده‌اى بديشان آمد آنان را جز رميدن و دورى [از حق‌] نيفزود'),
(3703, 35, 43, 'از روى بزرگ‌منشى و گردنكشى در زمين و نيرنگ بد. و نيرنگ بد جز سازنده آن را فرا نگيرد. پس آيا جز روش و نهادى را كه بر پيشينيان رفته است- يعنى عذاب را- چشم مى‌دارند؟ پس روش و نهاد خداى را هرگز دگرگونى نيابى، و روش و نهاد خداى را هرگز گردانيدنى نيابى.'),
(3704, 35, 44, 'آيا در زمين گردش نكردند تا ببينند كه سرانجام كسانى كه پيش از آنها بودند و نيرومندتر از آنها بودند چگونه شد؟ و هيچ چيز در آسمانها و در زمين نيست كه خداى را ناتوان سازد، همانا او دانا و تواناست.'),
(3705, 35, 45, 'و اگر خداى مردم را به سزاى آنچه كرده‌اند مى‌گرفت هيچ جنبنده‌اى بر پشت آن- زمين- نمى‌گذاشت، و ليكن آنان را تا سرآمدى نامبرده واپس مى‌دارد، پس چون سرآمدشان فرارسد همانا خداوند به [احوال‌] بندگان خود بيناست- و از اينرو هر كس را به كردارش جزا مى‌دهد-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'يا، سين.'),
(3707, 36, 2, 'سوگند به قرآن با حكمت- حكمت‌آميز و درست و استوار-.'),
(3708, 36, 3, 'كه همانا تو از فرستادگانى'),
(3709, 36, 4, '[و] بر راه راستى'),
(3710, 36, 5, '[قرآنى كه‌] فرو فرستاده آن تواناى بى‌همتا و مهربان [است‌].'),
(3711, 36, 6, 'تا مردمى را بيم دهى كه پدرانشان بيم داده نشده‌اند، از اينرو ناآگاهند.'),
(3712, 36, 7, 'هر آينه گفتار [خداى‌] بر بيشترشان سزا گشته، و از اينرو ايمان نمى‌آورند.'),
(3713, 36, 8, 'همانا بر گردنهاشان زنجيرها نهاده‌ايم كه تا زير چانه‌هاست، چنانكه سرهاشان بالا مانده است'),
(3714, 36, 9, 'و از پيش رويشان ديوارى و از پشت سرشان ديوارى نهاده‌ايم و بر ديدگانشان پرده‌اى افكنده‌ايم، از اين رو هيچ نمى‌بينند- كنايه از اينكه از همه سو فروگرفته شده‌اند و راه حق را كه هدايت و رستگارى است نمى‌بينند-.'),
(3715, 36, 10, 'و بر آنها يكسان است چه بيمشان كنى يا بيمشان نكنى، ايمان نمى‌آورند.'),
(3716, 36, 11, 'جز اين نيست كه تو كسى را هشدار و بيم مى‌دهى- بيم‌دادن تو كسى را سود دارد- كه اين ذكر- قرآن- را پيروى كند و از خداى رحمان در نهان بترسد پس او را به آمرزش و مزدى بزرگوارانه نويد ده.'),
(3717, 36, 12, 'همانا ماييم كه مردگان را زنده مى‌كنيم و آنچه را كه پيش فرستاده‌اند و اثرهاشان را- آنچه از آنها باز ماند- مى‌نويسيم، و هر چيزى را در راهنماى- نوشته- روشن- لوح محفوظ- به شمار آورده‌ايم.'),
(3718, 36, 13, 'و براى آنان- اهل مكّه- داستان مردم آن شهر- انطاكيه- را مَثَل آر، آنگاه كه فرستادگان بديشان آمدند'),
(3719, 36, 14, 'آنگاه كه دو تن به سوى آنان فرستاديم و ايشان را دروغزن خواندند، پس ايشان را به فرستاده سومى نيرومند كرديم و گفتند: همانا ما به سوى شما فرستاده شده‌ايم.'),
(3720, 36, 15, 'گفتند: شما جز آدميانى مانند ما نيستيد و خداى رحمان چيزى- از وحى و پيامبرى- فرو نفرستاده، [و] شما جز دروغ نمى‌گوييد.'),
(3721, 36, 16, 'گفتند: پروردگار ما مى‌داند كه هر آينه ما به سوى شما فرستاده شده‌ايم'),
(3722, 36, 17, 'و بر ما جز رسانيدن روشن و آشكار پيام نيست.'),
(3723, 36, 18, 'گفتند: ما شما را به شگون بد گرفتيم، اگر باز نايستيد هر آينه سنگسارتان مى‌كنيم و بى‌گمان از ما شكنجه‌اى دردناك به شما خواهد رسيد.'),
(3724, 36, 19, 'گفتند: شگون بدتان با شماست. آيا اگر شما را پند دهند [به شگون بد مى‌گيريد]؟! بلكه شما مردمى گزافكاريد.'),
(3725, 36, 20, 'و مردى- حبيب نجّار- از كرانه آن شهر شتابان بيامد، گفت: اى قوم من، اين فرستادگان [الهى‌] را پيروى كنيد،'),
(3726, 36, 21, 'كسانى را پيروى كنيد كه از شما مزدى نمى خواهند و خود رهيافته‌اند.'),
(3727, 36, 22, 'و چيست مرا كه آن [خداى‌] را نپرستم كه مرا پديد آورد، و به سوى او بازگردانيده خواهيد شد؟'),
(3728, 36, 23, 'آيا به جاى او خدايانى فراگيرم كه اگر خداى رحمان براى من گزندى خواهد شفاعت آنان مرا سود ندهد و به كار نيايد، و مرا نتوانند رهانيد؟!'),
(3729, 36, 24, 'آنگاه من بى‌گمان در گمراهى آشكارى خواهم بود.'),
(3730, 36, 25, 'من به پروردگارتان- خداى يگانه- ايمان آوردم پس بشنويد از من- و مرا فرمان بريد-.'),
(3731, 36, 26, '[پس از كشته‌شدن به او] گفته شد: به بهشت- بهشت برزخ- درآى. گفت: اى كاش قوم من مى‌دانستند،'),
(3732, 36, 27, 'كه پروردگار من مرا آمرزيد و از گرامى‌داشتگان و نواختگانم ساخت.'),
(3733, 36, 28, 'و پس از [كشتن‌] او بر قوم وى- اهل انطاكيه- هيچ لشكرى از آسمان نفرستاديم و ما فروفرستنده [لشكر عذاب‌] نبوديم- يعنى آن كفار خوارتر و بى ارزش‌تر از آن بودند كه هلاكشان را لشكرى از آسمان بيايد-'),
(3734, 36, 29, '[عذاب آنها] جز يك بانگ سهمناك نبود، كه ناگهان مرده و خاموش شدند.'),
(3735, 36, 30, 'اى دريغا بر بندگان! هيچ پيامبرى بديشان نيامد مگر آنكه او را مسخره مى‌كردند.'),
(3736, 36, 31, 'آيا نديده‌اند- به ديده بصيرت- كه پيش از آنان چه بسيار از مردم روزگاران را نابود كرديم كه آنان- هلاك‌شدگان- سوى اينان- به دنيا- باز نگردند؟'),
(3737, 36, 32, 'و كس نيست مگر آنكه همگيشان با هم نزد ما حاضر شدگانند- در روز رستاخيز-.'),
(3738, 36, 33, 'و براى آنان زمين مرده- خشك و بى‌گياه- نشانه‌اى است [بر قدرت ما] كه آن را زنده كرديم- به آب باران- و از آن دانه‌اى بيرون آورديم كه از آن مى خورند.'),
(3739, 36, 34, 'و در آن بوستانهايى از درختان خرما و انگور پديد كرديم و در آن چشمه‌ها روان ساختيم،'),
(3740, 36, 35, 'تا از ميوه‌هاى آن بخورند و حال آنكه دستهاى ايشان آن را نساخته است. آيا سپاس نمى‌گزارند؟'),
(3741, 36, 36, 'پاك و منزه است آن [خداى‌] كه همه جفتها- نر و ماده- را بيافريد از آنچه زمين مى‌روياند و از خودشان- آدميان- و از آنچه نمى‌دانند.'),
(3742, 36, 37, 'و شب براى آنان نشانه‌اى [ديگر] است، كه روز را از آن بيرون مى‌آوريم پس آنگاه در تاريكى فرومى‌روند.'),
(3743, 36, 38, 'و خورشيد به سوى قرارگاه خود مى‌رود. اين اندازه نهادن- طرح و تدبير- آن تواناى بى‌همتا و داناست.'),
(3744, 36, 39, 'و ماه را جايگاه‌ها به اندازه معين كرديم تا همچون شاخه خرماى ديرينه- خشكيده خميده به شكل هلال- گردد.'),
(3745, 36, 40, 'نه خورشيد را سزد كه ماه را دريابد و نه شب بر روز پيشى گيرنده است، و همه در چرخ خود- در مدار خود- شناورند.'),
(3746, 36, 41, 'و نشانه‌اى براى آنان اين است كه فرزندانشان را در كشتى پُربار برداشتيم.'),
(3747, 36, 42, 'و برايشان مانند آن'),
(3748, 36, 43, 'و اگر بخواهيم آنان را غرق مى‌كنيم، پس آنها را هيچ فريادرسى نباشد و نه رهايى يابند،'),
(3749, 36, 44, 'مگر به بخشايشى از ما و برخورداريى تا هنگامى- مرگ-.'),
(3750, 36, 45, 'و چون به ايشان گفته شود از آنچه فراروى شماست- گناهان- و آنچه از پس شماست- كيفر گناهانتان- بترسيد و بپرهيزيد شايد بر شما ببخشايند، [روى بگردانند و بر ستيزه و گردنكشى خود بيفزايند].'),
(3751, 36, 46, 'و هيچ نشانه‌اى از نشانه‌هاى پروردگارشان بديشان نيايد جز آنكه از آن رويگردانند.'),
(3752, 36, 47, 'و چون به آنان گفته شود كه از آنچه خداى روزيتان كرده انفاق كنيد، كسانى كه كافر شدند به آنان كه ايمان آورده‌اند گويند: آيا كسى را بخورانيم كه اگر خدا مى‌خواست او را مى‌خورانيد؟! شما جز در گمراهى آشكار نيستيد.'),
(3753, 36, 48, 'و مى‌گويند: اگر شما راستگوييد، [هنگام‌] اين وعده كى خواهد بود؟'),
(3754, 36, 49, 'اينان جز يك بانگ آسمانى را انتظار نمى‌برند كه ناگهان در حالى كه ستيزه و كشمكش مى‌كنند بگيردشان.'),
(3755, 36, 50, 'پس نه وصيتى توانند كرد و نه توانند سوى كسان خويش بازگردند.'),
(3756, 36, 51, 'و در صور دميده شود و آنان ناگاه از گورها به سوى پروردگارشان مى‌شتابند.'),
(3757, 36, 52, 'گويند: اى واى بر ما، چه كسى ما را از خوابگاهمان برانگيخت؟ اين همان است كه خداى رحمان وعده داده بود و پيامبران راست گفتند.'),
(3758, 36, 53, 'آن- هنگام رستاخيز- جز يك بانگ آسمانى نيست كه ناگاه همه ايشان نزد ما حاضرشدگانند.'),
(3759, 36, 54, 'پس امروز بر كسى هيچ ستمى نخواهد شد و جز آنچه مى‌كرديد پاداش داده نشويد.'),
(3760, 36, 55, 'همانا بهشتيان، امروز به خوشى و شادمانى سرگرم‌اند.'),
(3761, 36, 56, 'ايشان و همسرانشان در سايه‌ها بر تختهاى آراسته تكيه زنند.'),
(3762, 36, 57, 'ايشان را در آنجا [از هر گونه‌] ميوه هست، و آنان راست هر چه بخواهند.'),
(3763, 36, 58, '[آنان راست‌] سلامى، كه گفتارى از پروردگار مهربان است.'),
(3764, 36, 59, 'و [ندا آيد كه‌] اى بزهكاران، امروز جدا شويد.'),
(3765, 36, 60, 'اى فرزندان آدم، آيا به شما سفارش نكردم كه شيطان را نپرستيد كه او شما را دشمنى است آشكار؟'),
(3766, 36, 61, 'و اينكه مرا بپرستيد، كه اين است راه راست؟'),
(3767, 36, 62, 'و هر آينه از شما آدميان مردمانى بسيار را گمراه كرد. آيا خرد را كار نمى‌بستيد؟'),
(3768, 36, 63, 'اين است آن دوزخى كه به شما وعده داده مى‌شد.'),
(3769, 36, 64, 'امروز به سزاى كافربودنتان در آن در آييد- يا به آتش آن بسوزيد-.'),
(3770, 36, 65, 'امروز بر دهانهاشان مُهر نهيم، و دستهاشان با ما سخن گويد و پاهاشان گواهى دهد بدانچه مى‌كردند.'),
(3771, 36, 66, 'و اگر خواهيم هر آينه ديدگانشان را محو و ناپديد- نابينا- كنيم آنگاه به سوى راه پيشى گيرند، ولى كجا و چگونه مى‌بينند؟'),
(3772, 36, 67, 'و اگر خواهيم هر آينه آنها را بر جايشان مسخ كنيم- به شكل زشتى چون بوزينه و خوك درآوريم-، پس نه پيش‌رفتن [و گذشتن از آنجا] توانند و نه [به صورت اول‌] بازگردند.'),
(3773, 36, 68, 'و هر كه را عمر دراز دهيم در آفرينش نگونسارش كنيم- نيرومندى‌اش را به سستى و ناتوانى دگرگون سازيم-، آيا خرد را كار نمى‌بندند؟'),
(3774, 36, 69, 'و او- محمد'),
(3775, 36, 70, 'تا بيم دهد هر كه را زنده- با فهم و خردمند يا مؤمن- باشد و آن گفتار- عذاب و هلاكت- بر كافران سزا گردد.'),
(3776, 36, 71, 'آيا نديده‌اند كه ما از آنچه دستهاى [قدرت‌] ما [بى‌كمك آفريدگان‌] ساخته است چهارپايان را براى آنان آفريديم پس ايشان دارندگان آنهايند؟'),
(3777, 36, 72, 'و آنها'),
(3778, 36, 73, 'و آنان را در آنها سودهايى است- از پوست و پشم و كود آنها- و آشاميدنيهايى- از شيرشان-. آيا سپاس نمى‌گزارند؟'),
(3779, 36, 74, 'و به جاى خداى يكتا خدايانى گرفتند بدان اميد كه يارى شوند'),
(3780, 36, 75, '[و حال آنكه آن بتان‌] يارى‌كردن ايشان نتوانند و آنان'),
(3781, 36, 76, 'پس سخن آنان تو را اندوهگين نكند، همانا ما آنچه را پنهان مى‌دارند و آنچه را آشكار مى‌كنند مى‌دانيم.'),
(3782, 36, 77, 'آيا آدمى نديده- ندانسته- است كه ما او را از نطفه‌اى آفريديم، كه اكنون ستيزه‌گرى- يا دشمنى- آشكار است؟!'),
(3783, 36, 78, 'و براى ما مَثَلى زد- در زنده‌كردن مردگان- و آفرينش خود را فراموش كرده گفت: كيست كه استخوانها را در حالى كه پوسيده و خاك شده زنده مى‌كند؟'),
(3784, 36, 79, 'بگو: همان [خداى‌] كه نخستين بار آفريدش زنده‌اش مى‌كند، و او به همه آفرينش- يا آفريدگان- داناست.'),
(3785, 36, 80, 'همان [خداى‌] كه براى شما از درخت سبز آتشى پديد كرد پس آنگاه شما از آن آتش مى‌افروزيد.'),
(3786, 36, 81, 'آيا آن كه آسمانها و زمين را آفريد بر آفريدن مانند اينها توانا نيست؟ چرا [تواناست‌]، و اوست آفريدگار دانا.'),
(3787, 36, 82, 'جز اين نيست كه كار و فرمان او، چون چيزى را بخواهد، اين است كه گويدش: باش، پس مى‌باشد.'),
(3788, 36, 83, 'پس پاك و منزه است آن [خداى‌] كه پادشاهى- مالكيت و حاكميت- همه چيز به دست اوست و به سوى او بازگردانيده مى‌شويد.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'سوگند به آن صف‌زدگان- فرشتگان صف‌كشيده- كه [به طاعت و بندگى‌] صف زده‌اند.'),
(3790, 37, 2, 'سوگند به آن رانندگان كه سخت مى‌رانند- فرشتگانى كه ابرها يا ديوان را مى‌رانند-.'),
(3791, 37, 3, 'و سوگند به آن خوانندگان ياد و پند- آيات قرآن-'),
(3792, 37, 4, 'كه هر آينه خداى شما يكى است'),
(3793, 37, 5, 'خداوند آسمانها و زمين و آنچه ميان آنهاست و خداوند خاورها- جايهاى برآمدن آفتاب و ستارگان-.'),
(3794, 37, 6, 'همانا ما آسمان دنيا- نزديكتر- را به زيور ستارگان بياراستيم،'),
(3795, 37, 7, 'و از هر ديو سركش و نافرمان نگاه داشتيم،'),
(3796, 37, 8, 'كه نمى‌توانند به [گفتار] گروه والاتر- فرشتگان عالم بالا- گوش فرا دارند، و از هر سو [با شهابها] تير انداخته شوند'),
(3797, 37, 9, 'تا رانده و دور گردند، و آنان راست عذابى پيوسته و هميشه'),
(3798, 37, 10, 'مگر آن كس [از ديوان‌] كه سخنى [از سخنان فرشتگان عالم بالا] را دزدانه بربايد، پس شهابى از پى او افتد و به او رسد.'),
(3799, 37, 11, 'از آنها- مشركان- بپرس كه آنان در آفرينش سخت‌تر و دشوارترند يا آن چيزها [ى ديگر] كه آفريديم- فرشتگان و آسمانها و زمين و ...-، ما آنها را از گِلى چسبنده آفريده‌ايم.'),
(3800, 37, 12, 'بلكه تو شگفتى نمودى- از ايمان نياوردنشان- و آنها مسخره مى‌كنند.'),
(3801, 37, 13, 'و چون پند داده شوند، ياد نكنند و پند نپذيرند.'),
(3802, 37, 14, 'و چون نشانه‌اى ببينند به ريشخند گيرند.'),
(3803, 37, 15, 'و گويند: اين جز جادويى آشكار نيست.'),
(3804, 37, 16, 'آيا چون بمُرديم و خاك و استخوانى چند شديم باز برانگيخته مى‌شويم؟!'),
(3805, 37, 17, 'و آيا پدران پيشين ما نيز [برانگيخته مى‌شوند]؟'),
(3806, 37, 18, 'بگو: آرى، و شما خوار و درمانده باشيد.'),
(3807, 37, 19, 'پس جز اين نيست كه آن'),
(3808, 37, 20, 'و گويند: اى واى بر ما، اين روز حساب و پاداش است.'),
(3809, 37, 21, '[گفته شود:] اين، روز جدايى [نيكوكاران از بدكاران‌]- يا روز داورى- است كه آن را دروغ مى‌انگاشتيد.'),
(3810, 37, 22, '[اى فرشتگان،] كسانى را كه ستم كردند- مشركان- و همانندانشان- همراهانشان از ديوان- گرد آوريد، و آنچه را مى‌پرستيدند'),
(3811, 37, 23, 'به جاى خدا، و آنها را به راه دوزخ راه نماييد.'),
(3812, 37, 24, 'و بازداريدشان كه آنان پرسش خواهند شد.'),
(3813, 37, 25, 'شما را چيست كه يكديگر را يارى نمى‌كنيد؟'),
(3814, 37, 26, '[نه،] بلكه آنها امروز [به عذاب خداى‌] گردن‌نهادگانند.'),
(3815, 37, 27, 'و برخى از ايشان بر برخى ديگر- پيروان و پيشوايان- روى كرده يكديگر را مى‌پرسند.'),
(3816, 37, 28, '[پيروان به پيشوايان‌] گويند: شما بوديد كه از جانب راست- كه نشانه خير و نيكبختى مى‌پنداشتيم- بر ما درمى‌آمديد.'),
(3817, 37, 29, '[پيشوايان‌] گويند: [نه،] بلكه شما خود مؤمن نبوديد'),
(3818, 37, 30, 'و ما را بر شما هيچ دستى و تسلطى نبود، بلكه شما خود گروهى سركش بوديد.'),
(3819, 37, 31, 'پس گفتار پروردگارمان- وعده عذاب- بر ما سزا شد كه ما چشندگان عذابيم'),
(3820, 37, 32, 'شما را گمراه كرديم زيرا كه خود گمراه بوديم.'),
(3821, 37, 33, 'پس بى‌گمان همه ايشان در آن روز در عذاب شريكند.'),
(3822, 37, 34, 'همانا ما با بزهكاران- مشركان- چنين مى‌كنيم'),
(3823, 37, 35, 'آنان بودند كه چون به آنها گفته مى‌شد خدايى جز خداى يكتا نيست بزرگ‌منشى و گردنكشى مى‌كردند.'),
(3824, 37, 36, 'و مى‌گفتند: آيا ما از بهر شاعرى ديوانه دست از خدايان خويش بداريم!'),
(3825, 37, 37, 'بلكه [آن پيامبر] به حق آمد- يا حق را آورد- و [ديگر] پيامبران را باور داشت.'),
(3826, 37, 38, 'هر آينه شما چشنده عذاب دردناك خواهيد بود.'),
(3827, 37, 39, 'و جز آنچه مى‌كرديد پاداش داده نشويد.'),
(3828, 37, 40, 'مگر بندگان ويژه و برگزيده خداى،'),
(3829, 37, 41, 'كه ايشان راست روزى معلوم،'),
(3830, 37, 42, 'ميوه‌ها [از هر گونه‌]، و ايشان گرامى‌داشتگان و نواختگانند،'),
(3831, 37, 43, 'در بهشتهاى پر نعمت'),
(3832, 37, 44, 'بر تختهايى روياروى هم'),
(3833, 37, 45, 'جامى از نوشيدنى پاكيزه بر آنان مى‌گردانند،'),
(3834, 37, 46, 'سپيد و روشن، خوشگوار براى آشامندگان'),
(3835, 37, 47, 'نه در آن تباهى و بى‌خردى است و نه از آن مست شوند.'),
(3836, 37, 48, 'و نزد ايشان زنان ديده فروهشته درشت‌چشم باشند،'),
(3837, 37, 49, 'كه گويى تخمهاى شترمرغ‌اند پوشيده.'),
(3838, 37, 50, 'پس برخى از ايشان- بهشتيان- پرسش‌كنان روى به برخى ديگر كنند'),
(3839, 37, 51, 'گوينده‌اى از ايشان گويد: همانا مرا [در دنيا] همنشينى بود،'),
(3840, 37, 52, 'كه مى‌گفت: آيا تو از باوردارندگانى- به رستاخيز-؟'),
(3841, 37, 53, 'آيا چون بمرديم و خاك و استخوانى چند شديم، آيا ما را پاداش و كيفر مى‌دهند؟'),
(3842, 37, 54, '[سپس‌] گويد: آيا شما فرو مى‌نگريد- تا آن همنشين را ببينيد-؟'),
(3843, 37, 55, 'پس بنگرد و او را در ميان دوزخ ببيند.'),
(3844, 37, 56, 'گويد: به خدا سوگند هر آينه نزديك بود كه مرا به هلاكت افكنى.'),
(3845, 37, 57, 'و اگر نيكويى و بخشايش پروردگارم نبود هر آينه من هم از حاضرشدگان [در دوزخ‌] بودم'),
(3846, 37, 58, '[آنگاه از فرط خوشحالى گويد:] پس آيا ما ديگر نخواهيم مرد؟'),
(3847, 37, 59, 'مگر همان مرگ نخستين خود [كه گذشت‌] و ما از عذاب‌شدگان نيستيم؟'),
(3848, 37, 60, 'هر آينه اين است پيروزى و كاميابى بزرگ.'),
(3849, 37, 61, 'براى چنين [پاداشى‌] بايد كوشندگان بكوشند.'),
(3850, 37, 62, 'آيا اين براى پذيرايى و مهمانى بهتر است يا درخت زقوم [كه در ميان دوزخ رسته است‌]؟'),
(3851, 37, 63, 'همانا آن'),
(3852, 37, 64, 'آن درختى است كه در بن دوزخ مى‌رويد.'),
(3853, 37, 65, 'خوشه و بار آن [از زشتى‌] گويى سرهاى ديوان است'),
(3854, 37, 66, 'پس همانان- دوزخيان- از آن بخورند و شكمها را از آن پر كنند.'),
(3855, 37, 67, 'آنگاه براى آنان بر بالاى آن [طعام‌] آميزه‌اى است از آب جوشان.'),
(3856, 37, 68, 'سپس هر آينه بازگشتشان به دوزخ است'),
(3857, 37, 69, 'كه آنان پدران خود را گمراه يافتند،'),
(3858, 37, 70, 'پس بر پى آنان مى‌شتافتند.'),
(3859, 37, 71, 'و هر آينه پيش از آنها بيشتر پيشينيان گمراه شدند.'),
(3860, 37, 72, 'و هر آينه در ميان ايشان بيم‌كنندگان فرستاديم.'),
(3861, 37, 73, 'پس بنگر كه سرانجام آن بيم‌شدگان چگونه بود.'),
(3862, 37, 74, 'مگر بندگان ويژه و برگزيده خداى.'),
(3863, 37, 75, 'و هر آينه نوح ما را بخواند، پس چه نيك پاسخ‌دهنده بوديم.'),
(3864, 37, 76, 'و او و كسانش- خاندان و پيروانش- را از اندوه بزرگ- عذاب طوفان- رهانيديم.'),
(3865, 37, 77, 'و فرزندان او را بازماندگان گردانيديم.'),
(3866, 37, 78, 'و از او نام و ثناى نيك- يا نام و دعوت او را- در ميان پسينيان باقى گذاشتيم.'),
(3867, 37, 79, 'سلام بر نوح در ميان جهانيان.'),
(3868, 37, 80, 'ما نيكوكاران را اينچنين پاداش مى‌دهيم'),
(3869, 37, 81, 'همانا او از بندگان مؤمن ما بود.'),
(3870, 37, 82, 'سپس ديگران را غرق كرديم.'),
(3871, 37, 83, 'و هر آينه از پيروان او ابراهيم بود.'),
(3872, 37, 84, 'آنگاه كه با دلى پاك و رسته به سوى پروردگار خويش آمد.'),
(3873, 37, 85, 'هنگامى كه به پدر- سرپرست- و قوم خود گفت: اين چيست كه مى‌پرستيد؟'),
(3874, 37, 86, 'آيا به جاى خداى يكتا خدايانى دروغين- بتان- را مى‌خواهيد؟'),
(3875, 37, 87, 'پس گمان شما به پروردگار جهانيان چيست؟'),
(3876, 37, 88, 'پس با نگاهى به ستارگان درنگريست.'),
(3877, 37, 89, 'و آنگاه گفت: همانا من بيمارم.'),
(3878, 37, 90, 'پس، از او روى گردانده برگشتند.'),
(3879, 37, 91, 'پس پنهانى به سوى خدايانشان رفت و گفت: آيا چيزى نمى‌خوريد؟'),
(3880, 37, 92, 'شما را چيست كه سخن نمى‌گوييد؟'),
(3881, 37, 93, 'پس نهانى بر آنها با دست راست- به قوت- ضربتى زد.'),
(3882, 37, 94, 'و آنان شتابان به نزد او آمدند.'),
(3883, 37, 95, 'گفت: آيا آنچه را خود مى تراشيد مى‌پرستيد؟'),
(3884, 37, 96, 'و حال آنكه خداوند شما را و آنچه مى‌كنيد بيافريد.'),
(3885, 37, 97, '[نمرود و قومش‌] گفتند: برايش بنائى سازيد و او را در آتش اندازيد.'),
(3886, 37, 98, 'و براى او نيرنگ و ترفندى خواستند، پس آنان را فروتر ساختيم.'),
(3887, 37, 99, 'و [ابراهيم‌] گفت: همانا من به سوى پروردگار خويش رونده‌ام- از ميان شما هجرت مى كنم-، كه او مرا راه خواهد نمود.'),
(3888, 37, 100, 'پروردگار، مرا [فرزندى‌] از نيكان و شايستگان ببخش.'),
(3889, 37, 101, 'پس او را به پسرى بردبار- اسماعيل- مژده داديم.'),
(3890, 37, 102, 'و چون [پسر] به حد كار و كوشش با وى- پدر- رسيد، گفت: اى پسركم، من در خواب مى‌بينم كه تو را گلو مى‌برم، بنگر تا رأى تو چيست؟ گفت: اى پدر من، آنچه فرمان يافته‌اى بكن، كه اگر خداى خواهد مرا از شكيبايان خواهى يافت.'),
(3891, 37, 103, 'پس چون هر دو [فرمان خداى را] گردن نهادند و او را بر پيشانى بيفكند ...،'),
(3892, 37, 104, 'و او را ندا داديم كه اى ابراهيم،'),
(3893, 37, 105, 'بدرستى كه آن خواب را راست كردى. همانا ما نيكوكاران را چنين پاداش مى‌دهيم.'),
(3894, 37, 106, 'هر آينه اين آزمايشى روشن و آشكار بود.'),
(3895, 37, 107, 'و او را به ذبحى- گوسفندى- بزرگ باز خريديم.'),
(3896, 37, 108, 'و از او نام و ثنا- يا دعوت به حق- در ميان پسينيان باقى گذاشتيم.'),
(3897, 37, 109, 'سلام بر ابراهيم.'),
(3898, 37, 110, 'ما نيكوكاران را بدين گونه پاداش مى‌دهيم.'),
(3899, 37, 111, 'همانا او از بندگان مؤمن ما بود.'),
(3900, 37, 112, 'و او را به اسحاق، پيامبرى از نيكان و شايستگان، مژده داديم.'),
(3901, 37, 113, 'و بر او و بر اسحاق بركت داديم، و از فرزندان ايشان برخى نيكوكارند و برخى ستمكارى آشكار بر خويشتن.'),
(3902, 37, 114, 'و هر آينه بر موسى و هارون منت نهاديم- به قطع گزندها و آسيب‌ها از ايشان، يا به نعمت نبوت-.'),
(3903, 37, 115, 'و آن دو و قومشان را از اندوه بزرگ رهانيديم.'),
(3904, 37, 116, 'و ياريشان كرديم تا چيره و پيروز شدند.'),
(3905, 37, 117, 'و كتابى روشن و روشنگر بداديمشان.'),
(3906, 37, 118, 'و هر دو را راه راست بنموديم.'),
(3907, 37, 119, 'و برايشان نام و ثناى نيك در پسينيان باقى گذاشتيم.'),
(3908, 37, 120, 'سلام بر موسى و هارون.'),
(3909, 37, 121, 'همانا ما نيكوكاران را چنين پاداش مى‌دهيم.'),
(3910, 37, 122, 'زيرا كه هر دو از بندگان مؤمن ما بودند.'),
(3911, 37, 123, 'و همانا الياس از پيامبران بود.'),
(3912, 37, 124, 'آنگاه كه به قوم خود گفت: آيا پروا نمى‌كنيد؟'),
(3913, 37, 125, 'آيا بت بعل را مى‌خوانيد- مى‌پرستيديد- و نيكوترين آفرينندگان را وا مى‌گذاريد؟'),
(3914, 37, 126, 'همان خداى را، كه پروردگار شما و پروردگار پدران نخستين شماست.'),
(3915, 37, 127, 'پس او را دروغگو شمردند، و هر آينه آنان حاضرشدگانند- براى عذاب-.'),
(3916, 37, 128, 'مگر بندگان ويژه و برگزيده خداى.'),
(3917, 37, 129, 'و براى او نام و ثناى نيك در پسينيان باقى گذاشتيم.'),
(3918, 37, 130, 'سلام بر الياس.'),
(3919, 37, 131, 'همانا ما نيكوكاران را چنين پاداش مى دهيم،'),
(3920, 37, 132, 'زيرا كه او از بندگان مؤمن ما بود.'),
(3921, 37, 133, 'و هر آينه لوط از پيامبران بود.'),
(3922, 37, 134, 'آنگاه كه او و همه خاندان او را رهانيديم،'),
(3923, 37, 135, 'مگر پيرزنى را در ميان بازماندگان- عذاب‌شدگان-.'),
(3924, 37, 136, 'سپس آن ديگران را هلاك كرديم.'),
(3925, 37, 137, 'و هر آينه شما [اى قريش،] بامدادان بر [ويرانه‌] آنان مى‌گذريد- هنگامى كه كاروانهاى شما از حجاز به شام سفر مى‌كنند-'),
(3926, 37, 138, 'و شامگاهان نيز، آيا خرد را كار نمى‌بنديد؟'),
(3927, 37, 139, 'و هر آينه يونس از پيامبران بود.'),
(3928, 37, 140, 'آنگاه كه به آن كشتى پُر و گرانبار- آكنده از مردم و كالا- گريخت.'),
(3929, 37, 141, 'پس با آنها قرعه افكند و مغلوب شد- قرعه بر او افتاد-.'),
(3930, 37, 142, 'پس آن ماهى او را بلعيد و او در خور سرزنش بود- يا ملامت‌كننده خود بود-.'),
(3931, 37, 143, 'پس اگر نه اين بود كه وى از تسبيح‌گويان بود،'),
(3932, 37, 144, 'هر آينه تا روزى كه [مردم‌] برانگيخته مى‌شوند در شكم آن مى‌ماند- يعنى شكم آن ماهى گور او مى‌شد-.'),
(3933, 37, 145, 'پس او را در حالى كه بيمار بود به بيابان- دشت خشك و بى‌گياه- بينداختيم.'),
(3934, 37, 146, 'و بر [سر] او كدوبنى رويانيديم.'),
(3935, 37, 147, 'و او را به سوى صد هزار يا بيشتر- مردم نينوا- فرستاديم- يعنى دو باره پس از بيرون آمدن از شكم ماهى-.'),
(3936, 37, 148, 'پس ايمان آوردند و تا هنگامى- تا زنده بودند- برخوردارشان كرديم.'),
(3937, 37, 149, 'از ايشان بپرس، آيا پروردگار تو را دختران باشد و آنان را پسران؟!- مشركان مى‌گفتند كه فرشتگان دختران خدايند-.'),
(3938, 37, 150, 'يا مگر آنان حاضر و گواه بودند كه ما فرشتگان را دخترانى آفريديم؟!'),
(3939, 37, 151, 'آگاه باش كه آنها از دروغ‌پردازيشان است كه مى‌گويند:'),
(3940, 37, 152, 'خدا فرزند زاد، و هر آينه آنها دروغگويند.'),
(3941, 37, 153, 'آيا [خدا] دختران را بر پسران برگزيد؟!'),
(3942, 37, 154, 'چيست شما را، چگونه حكم مى‌كنيد؟- آنچه را خود نمى‌پسنديد به خدا نسبت مى‌دهيد!-.'),
(3943, 37, 155, 'آيا ياد نمى‌كنيد و پند نمى‌گيريد؟'),
(3944, 37, 156, 'يا مگر شما را حجتى روشن است؟'),
(3945, 37, 157, 'پس كتاب خود را [كه از آنجا مى‌گوييد] بياريد، اگر راستگوييد.'),
(3946, 37, 158, 'و ميان او'),
(3947, 37, 159, 'پاك است خداى از آنچه وصف مى‌كنند.'),
(3948, 37, 160, 'مگر بندگان ويژه و برگزيده خداى.'),
(3949, 37, 161, 'همانا شما و آنچه مى‌پرستيد- بتان-،'),
(3950, 37, 162, 'نيستيد بر او'),
(3951, 37, 163, 'مگر كسى را كه به آتش دوزخ در آيد- دوزخى باشد-.'),
(3952, 37, 164, 'و از ما [فرشتگان‌] هيچ كس نيست مگر او را جايگاهى است معلوم.'),
(3953, 37, 165, 'و هر آينه ماييم صف زدگان [براى طاعت‌].'),
(3954, 37, 166, 'و همانا ماييم تسبيح‌گويان.'),
(3955, 37, 167, 'و هر آينه [كافران مكه‌] مى‌گفتند:'),
(3956, 37, 168, 'اگر نزد ما كتابى و پندى از پيشينيان بود- يعنى مانند آنان كتاب آسمانى داشتيم-،'),
(3957, 37, 169, 'هر آينه بندگان ويژه خدا مى‌بوديم.'),
(3958, 37, 170, 'پس به آن- قرآن- كافر شدند، و زودا كه [سرانجام گمراهى خويش را] بدانند.'),
(3959, 37, 171, 'و هر آينه سخن ما براى بندگان فرستاده‌مان پيشى گرفته است،'),
(3960, 37, 172, 'كه همانا ايشان يارى‌شدگانند.'),
(3961, 37, 173, 'و هر آينه سپاه ما- پيروان انبياء- چيره و پيروزند.'),
(3962, 37, 174, 'پس تا مدتى- هنگام يارى ما- از آنان روى بگردان.'),
(3963, 37, 175, 'و [اينك‌] آنان را بنگر- تا هنگام انتقام بازشناسى-، پس بزودى [وبال كفر خود را] خواهند نگريست.'),
(3964, 37, 176, 'آيا عذاب ما را به شتاب مى‌خواهند؟'),
(3965, 37, 177, 'و چون [عذاب‌] به درگاه سراى آنها فرود آيد، پس بدا بامداد آن بيم‌شدگان.'),
(3966, 37, 178, 'و از آنان تا هنگامى روى بگردان.'),
(3967, 37, 179, 'و بنگر، پس آنها نيز خواهند نگريست.'),
(3968, 37, 180, 'پاك است پروردگار تو، خداوند عزت- بزرگى و نيرو و غلبه-، از آنچه [او را بدان‌] وصف مى‌كنند.'),
(3969, 37, 181, 'و سلام بر فرستادگان'),
(3970, 37, 182, 'و سپاس و ستايش خداى راست، پروردگار جهانيان.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'صاد، سوگند به قرآن داراى شرف- يا صاحب ياد و پند- [كه تو بر حقى و پيام تو راست است‌].'),
(3972, 38, 2, 'بلكه كسانى كه كافر شدند در سركشى و ستيزند.'),
(3973, 38, 3, 'بسا نسلها را كه پيش از آنان هلاك كرديم، پس فرياد برآوردند و آن هنگام گاه گريختن و رهايى نبود.'),
(3974, 38, 4, 'و [كافران مكه‌] شگفتى نمودند از اينكه بيم‌كننده‌اى از خودشان بديشان آمد و كافران گفتند: اين جادوگرى پس دروغگوست.'),
(3975, 38, 5, 'آيا خدايان را خدايى يگانه گردانيده؟! هر آينه اين چيزى سخت شگفت است.'),
(3976, 38, 6, 'و مهترانشان- سران قريش- به راه افتادند [و به يكديگر گفتند] كه برويد و بر خدايان خويش شكيبا باشيد. هر آينه اين چيزى است خواسته شده.'),
(3977, 38, 7, 'ما اين را- دينى را كه محمد'),
(3978, 38, 8, 'آيا از ميان ما قرآن بر او فرو فرستاده شده؟! [خداى فرمود:] بلكه آنها از ياد و پند من- وحى و قرآن- در شك‌اند- زيرا از روى سركشى نمى‌خواهند دريابند-، بلكه هنوز عذاب مرا نچشيده‌اند.'),
(3979, 38, 9, 'يا مگر گنجينه‌هاى رحمت پروردگار تواناى بى‌همتا و بخشنده‌ات نزد آنهاست؟!'),
(3980, 38, 10, 'يا مگر فرمانروايى آسمانها و زمين و آنچه ميان آنهاست آنان راست؟! [اگر چنين است‌] پس با نردبانها [بر آسمان‌] بر شوند- تا تدبير امور عالم كنند و وحى را از هر كه بخواهند بازدارند و به هر كه بخواهند بدهند-.'),
(3981, 38, 11, '[اينان‌] در آنجا لشكرى شكست‌خورده از گروه‌ها [ى كافران‌] اند.'),
(3982, 38, 12, 'پيش از آنها قوم نوح و عاد و فرعون، خداوند ميخها، نيز تكذيب كردند،'),
(3983, 38, 13, 'و ثمود و قوم لوط و مردم ايكه نيز، اينانند آن گروه‌ها [ى كافر].'),
(3984, 38, 14, 'هيچ كس از اينان نبود مگر آنكه پيامبران را تكذيب كردند پس كيفر من [بر آنان‌] سزا گشت.'),
(3985, 38, 15, 'و اينان جز يك بانگ [سهمگين عذاب‌] را، كه آن را هيچ بازگشتى نيست، انتظار ندارند.'),
(3986, 38, 16, 'و [از روى استهزا] گفتند: خدايا، بهره ما را [از عذاب‌]- يا نامه عمل ما را- پيش از روز حساب به شتاب ده.'),
(3987, 38, 17, 'بر آنچه مى‌گويند شكيبا باش و بنده ما، داوود نيرومند را ياد كن، كه او بسى باز گردنده [به ما] بود.'),
(3988, 38, 18, 'همانا ما كوه‌ها را رام ساختيم كه شبانگاه و بامداد همراه با او تسبيح مى‌كردند،'),
(3989, 38, 19, 'و نيز پرندگان را [رام و] فراهم آمده همه او را فرمانبردار و بازگردنده [به آواز خود با وى به تسبيح‌] بودند.'),
(3990, 38, 20, 'و پادشاهى‌اش را استوار كرديم و او را حكمت- دانش و كردار درست- و گفتار روشن و پاكيزه- كه ميان حق و باطل جدا كند- داديم.'),
(3991, 38, 21, 'و آيا خبر آن خصمان- شاكيان- به تو رسيد هنگامى كه از ديوار نمازگاه- يا غرفه- [داوود] بالا رفتند.'),
(3992, 38, 22, 'آنگاه كه بر داوود در آمدند- ناگاه و بى‌خبر-، پس از ايشان ترسيد گفتند: مترس، ما دو [گروه‌] شاكى هستيم كه يكى از ما بر ديگرى ستم كرده است پس ميان ما براستى و درستى حكم كن و بيداد مكن و ما را به راه راست راه نماى.'),
(3993, 38, 23, 'اين برادر من است او را نود و نُه ميش است و مرا يك ميش، مى‌گويد: اين يك را هم به من واگذار و در گفتار بر من چيره گشته است- به من زور مى‌گويد-.'),
(3994, 38, 24, '[داوود] گفت: بى‌گمان با خواستن ميش تو [و افزودن آن‌] به ميشان خويش بر تو ستم كرده است، و هر آينه بسيارى از شريكان- كه مال به هم مى‌آميزند- برخى‌شان بر برخى ستم مى‌كنند مگر آنان كه ايمان آورده و كارهاى نيك و شايسته كرده‌اند، و ايشان اندكند. و [در اينجا] داوود دانست كه همانا او را آزمون كرده‌ايم پس، از پروردگار خويش آمرزش خواست و سجده‌كنان بر روى بيفتاد و [به خداى‌] بازگشت- توبه كرد-.'),
(3995, 38, 25, 'پس آن [لغزش‌] را براى او آمرزيديم، و هر آينه او را نزد ما نزديكى است و نيكو بازگشتى- سرانجامى نيك-.'),
(3996, 38, 26, 'اى داوود، همانا تو را در زمين خليفه- نماينده خود- ساختيم پس ميان مردم براستى و درستى حكم كن و خواهش نفس را پيروى مكن، كه تو را از راه خدا گمراه مى‌گرداند. همانا كسانى كه از راه خدا گمراه شوند به سزاى آنكه روز حساب را فراموش كردند عذابى سخت دارند.'),
(3997, 38, 27, 'و آسمان و زمين و آنچه را در ميان آنهاست بيهوده نيافريديم. اين پندار كسانى است كه كفر ورزيدند. پس واى بر آنان كه كفر ورزيدند از آتش [دوزخ‌].'),
(3998, 38, 28, 'مگر كسانى را كه ايمان آوردند و كارهاى نيك و شايسته كردند مانند تباهكاران در زمين مى‌گردانيم؟! يا مگر پرهيزكاران را مانند بدكاران مى سازيم؟!'),
(3999, 38, 29, '[اين قرآن‌] كتابى است فرخنده و با بركت كه آن را به سوى تو فرو فرستاديم تا در آيات آن بينديشند، و تا خردمندان پند گيرند.'),
(4000, 38, 30, 'و سليمان را به داوود بخشيديم، نيكو بنده‌اى بود، همانا او بسى بازگردنده [به سوى ما] بود.'),
(4001, 38, 31, '[ياد كن‌] آنگاه كه در پايان روز اسبان تيزتك را بر او عرضه داشتند.'),
(4002, 38, 32, 'پس گفت: من دوستى خير- يعنى اسبان- را بر يادكرد پروردگارم- يعنى نماز عصر- بگزيديم تا [آفتاب‌] در پرده [شب‌] پنهان گشت- يعنى فرو شد-'),
(4003, 38, 33, 'آنها'),
(4004, 38, 34, 'و هر آينه سليمان را آزموديم و بر تخت او كالبدى افكنديم، آنگاه [به خداى‌] باز گشت- توبه كرد-.'),
(4005, 38, 35, 'گفت: پروردگارا، مرا بيامرز و مرا آن پادشاهى ببخش كه پس از من هيچ كس را نسزد، كه تويى بسيار بخشنده.'),
(4006, 38, 36, 'پس باد را براى او رام گردانيديم، كه به فرمان او به نرمى و خوشى هر جا كه مى‌خواست روان مى‌شد.'),
(4007, 38, 37, 'و ديوان را [رام كرديم‌] هر خانه‌ساز- بنّا- و هر فرورونده به دريا- غواص- را.'),
(4008, 38, 38, 'و نيز ديگران- ديگر ديوان- را كه به هم بسته در بندها بودند [رام او ساختيم‌].'),
(4009, 38, 39, '[گفتيم:] اين است بخشش بى‌شمار ما- كه پايان ندارد يا بى‌حساب است- پس [هر چه خواهى‌] ببخش يا نگاه دار.'),
(4010, 38, 40, 'و هر آينه او راست نزد ما نزديكى و بازگشتى نيكو- سرانجامى نيك-.'),
(4011, 38, 41, 'و بنده ما ايوب را ياد كن آنگاه كه پروردگار خويش را خواند كه شيطان مرا رنجورى و عذاب رسانيده است.'),
(4012, 38, 42, '[او را گفتيم:] پاى خود بر زمين بزن، [پس پاى بر زمين زد و چشمه‌اى پديد آمد، او را گفتيم:] اين آبى است خنك براى شست‌وشو- كه خود را بشويى- و آشاميدنى [خنك‌]- كه بنوشى-.'),
(4013, 38, 43, 'و خانواده‌اش را به او بخشيديم و نيز همچندشان را با ايشان، تا بخشايشى باشد از ما و يادكرد و پندى براى خردمندان.'),
(4014, 38, 44, 'و به دست خود دسته‌اى گياه برگير پس با آن بزن و سوگند مشكن. ما او را شكيبا يافتيم، نيك بنده‌اى بود، همانا بسيار بازگردنده [به ما] بود.'),
(4015, 38, 45, 'و بندگان ما ابراهيم و اسحاق و يعقوب خداوندان دستها- كنايه از نيرو و قدرت براى طاعت و عبادت- و ديدگان- كنايه از بينش و بصيرت در دين و حقيقت- را ياد كن.'),
(4016, 38, 46, 'ما ايشان را ويژه- پاك و خالص- ساختيم به آن ويژگى كه يادكرد آن جهان است.'),
(4017, 38, 47, 'و هر آينه ايشان نزد ما از برگزيدگان و نيكان‌اند.'),
(4018, 38, 48, 'و اسماعيل و يسع و ذو الكفل را ياد كن، و همه از نيكان [و گزيدگان‌] بودند.'),
(4019, 38, 49, 'اين [قرآن‌] يادكرد و پندى است، و هر آينه پرهيزگاران را بازگشتى نيكو- سرانجامى نيك- است،'),
(4020, 38, 50, 'بهشتهاى پاينده كه درها [ى آن‌] برايشان گشاده باشد.'),
(4021, 38, 51, 'در حالى كه در آنها تكيه زنند، در آنجا ميوه‌هاى فراوان [و گوناگون‌] و نوشيدنى مى‌خواهند.'),
(4022, 38, 52, 'و نزد ايشان زنان فروهشته چشم همسال‌اند.'),
(4023, 38, 53, '[گويندشان:] اين است آنچه براى روز حساب وعده داده مى‌شديد.'),
(4024, 38, 54, 'هر آينه اين روزى [و بخشش‌] ماست كه پايانى ندارد.'),
(4025, 38, 55, 'اين است [پاداش پرهيزگاران‌]، و هر آينه سركشان را بدترين بازگشتى- سرانجامى بد- است،'),
(4026, 38, 56, 'دوزخ، كه در آن در آيند- يا به آن بسوزند- پس بد بسترى است.'),
(4027, 38, 57, 'اين است آب جوشان و زرداب چركين گندآلود، پس بايد بچشند آن را،'),
(4028, 38, 58, 'و همسان آن عذابهاى ديگرى از انواع گوناگون.'),
(4029, 38, 59, '[و چون سران كفار به دوزخ در آيند پيروانشان را نيز درآرند، و فرشتگان به آن سران گويند:] اين گروهى است فراهم‌آمده كه با شما [به دوزخ‌] در آيند، [گويند:] شادى و خوشامد بر آنان مباد، كه آنها به آتش دوزخ در آيند.'),
(4030, 38, 60, '[پيروان‌] گويند: بلكه شما را شادى و خوشامد مباد، شما آن- عذاب- را فراپيش ما نهاده‌ايد، پس بد قرارگاهى است [دوزخ‌].'),
(4031, 38, 61, 'گويند: پروردگارا، هر كه اين [عذاب‌] را فراپيش ما نهاد، او را عذابى دوچندان در آتش دوزخ بيفزاى.'),
(4032, 38, 62, 'و گويند: چيست ما را كه مردانى كه آنان را از بدان مى‌شمرديم نمى‌بينيم؟'),
(4033, 38, 63, 'آيا به مسخره گرفتيمشان [و به دوزخ درنيامده‌اند]، يا ديدگان [ما] از ايشان بگرديده است- يعنى در دوزخند و ما ايشان را نمى‌بينيم-.'),
(4034, 38, 64, 'هر آينه اين ستيزه‌گرى دوزخيان با يكديگر است كه راست و سزاست.'),
(4035, 38, 65, 'بگو: همانا من بيم‌كننده‌اى هستم و خدايى نيست جز خداى يگانه بر همه چيره.'),
(4036, 38, 66, 'خداوند آسمانها و زمين و آنچه ميان آنهاست، آن تواناى بى‌همتا و آمرزگار.'),
(4037, 38, 67, 'بگو: آن- رسالت من يا قرآن يا رستاخيز- خبرى بزرگ است،'),
(4038, 38, 68, 'كه شما از آن رويگردانيد'),
(4039, 38, 69, 'مرا هيچ دانشى به آن گروه برين- فرشتگان- نبود آنگاه كه با يكديگر گفت‌وگو مى‌كردند- در باره آفرينش آدم-.'),
(4040, 38, 70, 'به من وحى نمى‌شود مگر از آن رو كه من آشكارا بيم‌كننده‌ام.'),
(4041, 38, 71, '[ياد كن‌] آنگاه كه پروردگار تو به فرشتگان گفت: من آدميى از گِل مى‌آفرينم.'),
(4042, 38, 72, 'پس چون او را راست و تمام گردانيدم و در وى از روح خود دميدم، او را سجده‌كنان درافتيد.'),
(4043, 38, 73, 'پس فرشتگان همه با هم سجده كردند،'),
(4044, 38, 74, 'مگر ابليس كه گردنكشى و بزرگ‌منشى نمود و از كافران شد.'),
(4045, 38, 75, '[خداى‌] گفت: اى ابليس، تو را چه باز داشت از آنكه سجده كنى آن را كه با دو دست [قدرت‌] خويش آفريدم؟ آيا خود را بزرگ ديدى يا از برترى‌جويان بودى؟'),
(4046, 38, 76, 'گفت: من از وى بهترم، مرا از آتش آفريدى و او را از گِل.'),
(4047, 38, 77, 'گفت: پس، از آنجا بيرون شو، كه همانا تو رانده‌شده‌اى'),
(4048, 38, 78, 'و همانا لعنت من تا روز رستاخيز و پاداش بر توست.'),
(4049, 38, 79, 'گفت: خداوندا، پس مرا تا روزى كه [از نو زنده و] برانگيخته شوند مهلت ده.'),
(4050, 38, 80, 'گفت: همانا تو از مهلت‌يافتگانى،'),
(4051, 38, 81, 'تا روز هنگامى معلوم.'),
(4052, 38, 82, 'گفت: پس به عزت تو سوگند كه البته همه آنان را گمراه كنم،'),
(4053, 38, 83, 'مگر بندگان ويژه و برگزيده‌ات از آنان- كه مرا بر ايشان راهى نيست-.'),
(4054, 38, 84, 'گفت: راست است- و راست همى‌گويم-،'),
(4055, 38, 85, 'هر آينه دوزخ را از تو و از همه كسانى از آنان كه تو را پيروى كنند پر مى‌كنم.'),
(4056, 38, 86, 'بگو: من از شما بر اين [پيامبرى‌] هيچ مزدى نمى‌خواهم، و من از تكلف‌كنندگان نيستم.'),
(4057, 38, 87, 'نيست اين [قرآن‌] مگر يادكرد و پندى براى جهانيان.'),
(4058, 38, 88, 'و هر آينه خبر آن را- يعنى آنچه در آن است از وعده و وعيد- پس از چندى- پيروزى اسلام يا مرگتان يا روز رستاخيز- خواهيد دانست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'فرو فرستادن اين كتاب از سوى خداوند تواناى بى‌همتا و داناى با حكمت است.'),
(4060, 39, 2, 'همانا ما اين كتاب را براستى و درستى به تو فرو فرستاديم، پس خداى را بپرست در حالى كه دين را ويژه او كرده باشى.'),
(4061, 39, 3, 'آگاه باشيد كه خداى راست دين پاك و ويژه و آنان كه جز او دوستان و سرپرستانى گرفتند، [گويند:] ما آنها- بتان- را نمى‌پرستيم مگر براى اينكه ما را به خدا نزديك سازند همانا خدا ميانشان- ميان مشركان و موحدان- در آنچه در باره آن اختلاف مى‌كنند حكم خواهد كرد. همانا خدا كسى را كه دروغگو و كافر و ناسپاس است راه ننمايد.'),
(4062, 39, 4, 'اگر خدا مى‌خواست كه فرزندى گيرد، از ميان آنچه مى‌آفريند آنچه مى‌خواست برمى‌گزيد. پاك و منزه است او اوست خداى يگانه بر همه چيره.'),
(4063, 39, 5, 'آسمانها و زمين را براستى و درستى آفريد. شب را به روز مى‌پيچد و روز را بر شب- يا شب را در روز و روز را در شب درمى‌آرد، يعنى يكى را مى‌برد و ديگرى را مى‌آورد- و خورشيد و ماه را رام گردانيد، هر يك تا سرآمدى نامبرده روان است، آگاه باشيد كه اوست تواناى بى‌همتا و آمرزگار.'),
(4064, 39, 6, 'شما را از يك تن- يعنى آدم'),
(4065, 39, 7, 'اگر ناسپاسى كنيد، خداى از شما بى‌نياز است و ناسپاسى را براى بندگان خود نمى‌پسندد. و اگر سپاس بداريد- بر نعمت توحيد و نعمت نبوت محمد'),
(4066, 39, 8, 'و چون آدمى را گزندى رسد پروردگار خويش را با روى‌آوردن و بازگشت به وى بخواند سپس چون او را نعمتى دهد، آنچه را كه از پيش به [برداشتن‌] آن [خداى را] مى‌خواند فراموش كند و براى خدا همتايانى قرار دهد تا [مردم را] از راه او گمراه سازد. بگو: اندكى از كفر خويش بهره‌مند باش، كه تو از دوزخيانى.'),
(4067, 39, 9, '[آيا آن كافر ناسپاس بهتر است‌] يا كسى كه در ساعات شب در حال سجده و ايستاده با فروتنى به طاعت مشغول است از [عذاب‌] آن سراى مى‌ترسد و به بخشايش پروردگار خويش اميد مى‌دارد؟ بگو: آيا آنان كه مى‌دانند- خداشناسان- و آنان كه نمى‌دانند- كافران- برابرند؟ تنها خردمندان پند مى پذيرند.'),
(4068, 39, 10, 'بگو: اى بندگان من كه ايمان آورده‌ايد، از پروردگار خويش پروا كنيد. براى آنان كه در اين جهان نيكى كرده‌اند پاداش نيكوست. و زمين خدا فراخ است- از سرزمين كفرى كه شما را از خداپرستى باز مى‌دارند هجرت كنيد-. همانا به شكيبايان مزدشان را تمام و بى‌شمار- يا بى‌حسابرسى و بازخواست- بدهند.'),
(4069, 39, 11, 'بگو: فرمان يافته‌ام كه خداى را بپرستم در حالى كه دين را ويژه او كنم،'),
(4070, 39, 12, 'و فرمان يافته‌ام كه نخستين مسلمان باشم.'),
(4071, 39, 13, 'بگو: همانا من، اگر پروردگار خويش را نافرمانى كنم، از عذاب روزى بزرگ- روز رستاخيز- مى‌ترسم.'),
(4072, 39, 14, 'بگو: تنها خداى را مى‌پرستم در حالى كه دينم را براى او ويژه كننده‌ام،'),
(4073, 39, 15, 'پس شما هم آنچه را خواهيد جز او بپرستيد- كه شما را سودى نخواهد داشت-، بگو: همانا زيانكاران آن كسانند كه خويشتن و خانواده خود را به روز رستاخيز زيان كنند، آگاه باشيد كه اين است آن زيانكارى آشكار.'),
(4074, 39, 16, 'آنان را از فرازشان سايبانهايى از آتش است و از زيرشان نيز سايبانهايى، اين است [آن عذاب‌] كه خدا بندگان خود را بدان مى‌ترساند، پس اى بندگان من، از من پروا كنيد.'),
(4075, 39, 17, 'و آنان كه از پرستش طاغوت- بتها و سران گردنكش كفر- پرهيز كردند و به خداى بازگشتند، ايشان راست مژده، پس بندگان مرا مژده ده،'),
(4076, 39, 18, 'آنان كه سخن را مى‌شنوند و بهترين آن را پيروى مى‌كنند، اينانند كسانى كه خداوند راهشان نموده، و ايشانند خردمندان.'),
(4077, 39, 19, 'پس آيا كسى كه سخن- وعده- عذاب بر او سزا گشته [مى‌تواند از آن رهايى يابد]؟ آيا تو آن را كه در آتش دوزخ است توانى رهانيد؟!'),
(4078, 39, 20, 'ليكن آنان كه از پروردگار خويش پروا كنند ايشان را غرفه‌هايى است بر فراز آنها غرفه‌هايى ساخته شده كه از زير آنها جويها روان است وعده خداست، خدا وعده را خلاف نكند.'),
(4079, 39, 21, 'آيا نديده‌اى كه خدا از آسمان آبى فرو آورد پس آن را به چشمه‌سارهايى در زمين روان ساخت، آنگاه بدان [آب‌] كشتهايى به رنگهاى گوناگون بيرون مى‌آورد سپس خشك گردد پس آن را زرد شده بينى، آنگاه آن را شكسته و خرد مى‌سازد. هر آينه خردمندان را در آن يادكرد و پندى است.'),
(4080, 39, 22, 'آيا كسى كه خدا دل او را براى اسلام گشاده و او بر روشنائى از پروردگار خويش است [مانند كسى است كه دل او از پذيرش اسلام تنگ است و در تاريكى كفر گرفتار]؟ پس واى بر سختدلانى كه خدا را ياد نكنند. آنانند در گمراهى آشكار.'),
(4081, 39, 23, 'خدا نيكوترين سخن- قرآن- را فرو فرستاد كتابى ماننده به يكديگر و دو دو- دوباره و مكرر-، كه از [خواندن و شنيدن‌] آن پوست كسانى كه از پروردگار خويش مى‌ترسند از لرز منقبض- گرفته و فراهم- شود، و سپس پوستها و دلهاشان به ياد خدا آرام و نرم گردد. اين است رهنمونى خدا كه هر كه را خواهد بدان راه مى‌نمايد، و هر كه را خدا گمراه كند پس او را هيچ راهنمايى نيست.'),
(4082, 39, 24, 'آيا كسى كه به روى خويش از عذاب بد و سخت روز رستاخيز مى‌پرهيزد- زيرا دستهايش به گردن بسته است و به دست از روى دفاع نتواند كرد- [همانند كسى است كه از عذاب ايمن باشد]؟ و ستمكاران را گويند: بچشيد آنچه مى‌كرديد.'),
(4083, 39, 25, 'كسانى كه پيش از آنان بودند [حق را] تكذيب كردند، پس عذاب از جايى كه نمى‌دانستند- و انتظار نداشتند- بديشان آمد.'),
(4084, 39, 26, 'پس خداوند خوارى و رسوايى را در زندگانى اين جهان بدانها چشانيد و بى‌گمان عذاب آن جهان بزرگتر است، اگر مى‌دانستند.'),
(4085, 39, 27, 'و هر آينه براى مردمان در اين قرآن از هر گونه مثَلى آورديم شايد به ياد آرند و پند گيرند'),
(4086, 39, 28, 'قرآنى تازى- به زبان عرب- كه هيچ كژى- انحراف و تناقض- در آن نيست، باشد كه پرهيزگارى كنند.'),
(4087, 39, 29, 'خداوند مثَلى زد: مردى كه چند خواجه بدخو و ناسازگار در او شريك باشند، و مردى كه از آنِ يك مرد باشد- اين مثَل مشرك و موحّد است- آيا اين دو در مثَل با هم برابرند؟ سپاس و ستايش خداى راست، بلكه بيشترشان نمى‌دانند.'),
(4088, 39, 30, 'همانا تو مى‌ميرى و آنان نيز خواهند مرد.'),
(4089, 39, 31, 'سپس شما در روز رستاخيز نزد پروردگارتان با يكديگر ستيزه خواهيد كرد.'),
(4090, 39, 32, 'پس چه كسى ستمكارتر است از آن كه بر خدا دروغ بست و سخن راست- قرآن- را چون بدو آمد دروغ انگاشت؟ آيا كافران را جاى در دوزخ نيست؟'),
(4091, 39, 33, 'و آن كه سخن راست آورد و آن را راست شُمرد و باور داشت، ايشانند پرهيزگاران.'),
(4092, 39, 34, 'ايشان راست نزد پروردگارشان آنچه بخواهند اين است پاداش نيكوكاران،'),
(4093, 39, 35, 'تا خداى بدترين آنچه كردند از ايشان بزدايد و مزدشان را بر پايه نيكوترين آنچه مى كردند پاداش دهد.'),
(4094, 39, 36, 'آيا خداوند بنده خويش را كار گذارى بسنده نيست، و تو را از كسانى كه جز اويند- خدايان دروغين- مى‌ترسانند و هر كه را خدا گمراه كند پس او را هيچ راهنمايى نيست.'),
(4095, 39, 37, 'و هر كه را خدا راه نمايد پس او را هيچ گمراه‌كننده‌اى نباشد. آيا خدا تواناى بى‌همتا و كين‌ستان نيست؟'),
(4096, 39, 38, 'و اگر از آنان بپرسى: چه كسى آسمانها و زمين را آفريده است؟ هر آينه گويند: خدا. بگو: چه گوييد در باره آنچه جز خدا مى‌خوانند؟ اگر خداى خواهد كه به من گزندى رسد آيا آنها بازبرنده گزند او هستند؟ يا [اگر] به من نيكويى و بخشايشى خواهد آيا آنها بازدارنده بخشايش او هستند؟ بگو: خدا مرا بس است توكّل كنندگان تنها بر او توكّل مى‌كنند.'),
(4097, 39, 39, 'بگو: اى قوم من، بر جاى- وضع و موقع- خود عمل كنيد- آنچه توانيد بكنيد-، من نيز عمل مى‌كنم- آنچه توانم مى‌كنم-، پس بزودى خواهيد دانست،'),
(4098, 39, 40, 'كه كيست آن كه عذابى بدو آيد كه رسوا و خوارش كند- عذاب دنيا- و عذابى پايدار كه بر او فرود آيد.'),
(4099, 39, 41, 'همانا ما اين كتاب را براستى و درستى براى مردمان بر تو فرو فرستاديم پس هر كه راه يافت به سود خود اوست، و هر كه گمراه شد جز اين نيست كه به زيان خود گمراه مى‌شود و تو بر آنها گماشته و نگهبان نيستى.'),
(4100, 39, 42, 'خداست كه جانها را هنگام مرگشان مى‌گيرد و آن را كه نمرده باشد در خوابش [مى‌گيرد]. پس آن [جان‌] را كه حكم مرگ بر آن رفته است نگاه مى‌دارد و آن ديگر را تا سرآمدى نامبرده بازمى‌فرستد- كسى را كه هنگام مرگ او نرسيده از خواب بيدار مى‌كند-. هر آينه در اين براى مردمى كه مى‌انديشند نشانه‌هاست.'),
(4101, 39, 43, 'بلكه جز خدا شفيعانى- كارگزاران و ميانجى‌ها- گرفته‌اند. بگو: آيا هر چند آنان به چيزى توانايى نداشته باشند و چيزى درنيابند [باز هم آنها را شفيع مى‌گيريد]؟!'),
(4102, 39, 44, 'بگو: خداى راست همه شفاعت- يعنى اذن و فرمان شفاعت- او راست پادشاهى آسمانها و زمين، سپس به او بازگردانده مى‌شويد.'),
(4103, 39, 45, 'و چون خداوند به يگانگى ياد شود دلهاى كسانى كه به آن جهان ايمان ندارند برمد، و چون آنان كه جز او هستند- معبودهاشان- ياد كرده شوند ناگاه شادمان گردند.'),
(4104, 39, 46, 'بگو: بارخدايا، اى پديدآرنده آسمانها و زمين، داناى نهان و آشكار، تويى كه ميان بندگان خود در آنچه اختلاف مى‌كردند حكم مى‌كنى.'),
(4105, 39, 47, 'و اگر براى آنان كه ستم كردند- كافر شدند- همه آنچه در زمين است و همانندش با آن باشد، هر آينه آن را براى بازخريد خود از عذاب بد و سخت روز رستاخيز بدهند. و از جانب خدا براى آنان آنچه گمان نمى‌بردند پديدار شود.'),
(4106, 39, 48, 'و آنان را بديهاى- يعنى عقوبتهاى- آنچه به دست آوردند نمودار شود و آنچه مسخره‌اش مى‌كردند بديشان دررسد.'),
(4107, 39, 49, 'و چون آدمى را گزندى فرا رسد ما را بخواند سپس چون او را از نزد خويش نعمتى بدهيم، گويد: جز اين نيست كه آن'),
(4108, 39, 50, 'همانا اين [سخن‌] را كسانى نيز كه پيش از آنها بودند گفتند، ولى آنچه به دست مى‌آوردند- از مال و كالاى دنيا- آنان را سود نكرد و به كارشان نيامد.'),
(4109, 39, 51, 'پس بديهاى- عقوبتهاى- آنچه كرده‌اند بديشان رسيد و از اينان نيز كسانى كه ستم كردند بديهاى- عقوبتهاى- آنچه كرده‌اند بديشان خواهد رسيد و ناتوان‌كننده [ما از عذاب‌كردنشان‌] نيستند.'),
(4110, 39, 52, 'آيا ندانسته‌اند كه خدا روزى را براى هر كه خواهد فراخ كند و تنگ گرداند؟ هر آينه در اين براى مردمى كه ايمان دارند نشانه‌ها و عبرتهاست.'),
(4111, 39, 53, 'بگو: اى بندگان من كه بر خويشتن گزافكارى كرده‌ايد- در گناهان از حد گذاشته‌ايد و به خود ستم كرده‌ايد- از بخشايش خداى نوميد مباشيد، كه همانا خداوند همه گناهان را مى‌آمرزد، كه اوست آمرزگار و مهربان.'),
(4112, 39, 54, 'و به پروردگار خويش بازگرديد و او را گردن نهيد پيش از آنكه شما را عذاب آيد آنگاه يارى نشويد.'),
(4113, 39, 55, 'و از نيكوترين آنچه از پروردگارتان به شما فرو فرستاده شده- يعنى قرآن- پيروى كنيد پيش از آنكه شما را ناگهان عذاب آيد و شما آگاه نباشيد'),
(4114, 39, 56, '[پيش از آن‌] كه كسى گويد: دريغا بر آن كوتاهى كه در باره خدا كردم، و هر آينه من از مسخره‌كنندگان بودم.'),
(4115, 39, 57, 'يا بگويد: اگر خداى مرا راه مى‌نمود هر آينه از پرهيزگاران مى‌بودم.'),
(4116, 39, 58, 'يا آنگاه كه عذاب را بيند گويد: كاش مرا بازگشتى [به دنيا] مى‌بود تا از نيكوكاران مى‌شدم.'),
(4117, 39, 59, 'آرى، تو را نشانه‌هاى من آمد پس آنها را دروغ انگاشتى و گردنكشى و بزرگ منشى كردى و از كافران بودى.'),
(4118, 39, 60, 'و روز رستاخيز آنان را كه بر خدا دروغ بستند- يعنى خداى را به گرفتن شريك و فرزند وصف كردند- بينى كه رويهاشان سياه است. آيا گردنكشان را جاى در دوزخ نيست؟'),
(4119, 39, 61, 'و خداى كسانى را كه پرهيزگارى كردند با رستگارى و كاميابيشان برهاند، كه نه بدى بديشان رسد و نه اندوهگين شوند.'),
(4120, 39, 62, 'خداست آفريدگار هر چيزى، و او بر همه چيز كارساز و نگاهبان است.'),
(4121, 39, 63, 'او راست كليدهاى [گنجينه‌هاى‌] آسمانها و زمين و آنان كه به نشانه‌هاى خدا كافر شدند همانان زيانكارانند.'),
(4122, 39, 64, 'بگو: آيا مرا مى‌فرماييد كه جز خداى را بپرستم، اى نادانان؟!'),
(4123, 39, 65, 'و هر آينه به تو وحى شده است، و نيز به آنان كه پيش از تو بودند، كه اگر شرك‌ورزى بى‌گمان كار تو تباه و نابود گردد و از زيانكاران باشى.'),
(4124, 39, 66, 'بلكه تنها خداى را بپرست و از سپاسداران باش.'),
(4125, 39, 67, 'و خداى را چنانكه شايسته اوست نشناختند، و روز رستاخيز زمين يكسره در قبضه [قدرت‌] اوست و آسمانها به دست راست- يعنى به قدرت- او پيچيده شوند پاك است او، و برتر است از آنكه با او انباز مى‌گيرند.'),
(4126, 39, 68, 'و در صور دميده شود، پس هر كه در آسمانها و هر كه در زمين است بيهوش شود- بميرد- مگر آنان كه خداى خواهد، سپس بار ديگر در آن دميده شود، پس ناگهان ايستادگانى باشند كه مى‌نگرند.'),
(4127, 39, 69, 'و [در آن روز] زمين به نور خداوند خود روشن شود و نامه [هاى اعمال‌] را پيش نهند و پيامبران و گواهان را بياورند و ميان ايشان- آدميان- براستى و درستى داورى شود و بر آنان ستم نرود.'),
(4128, 39, 70, 'و هر كسى را آنچه كرده باشد تمام بدهند، و او داناتر است بدانچه مى‌كنند.'),
(4129, 39, 71, 'و كسانى كه كافر شدند گروه گروه به سوى دوزخ رانده شوند، تا چون بدانجا رسند درهاى آن گشوده شود و نگهبانان آن گويندشان: آيا شما را پيامبرانى از خودتان نيامدند كه آيات- سخنان و پيامهاى- پروردگارتان را بر شما مى‌خواندند و شما را از ديدار اين روزتان مى‌ترساندند؟ گويند: چرا- آمدند- و ليكن سخن- يعنى حكم- عذاب بر كافران سزا گشته است.'),
(4130, 39, 72, 'گفته شود: به دروازه‌هاى دوزخ در آييد، در آنجا جاويدان باشيد، پس بد است جايگاه گردنكشان.'),
(4131, 39, 73, 'و آنان كه از پروردگار خويش پروا كرده‌اند گروه گروه به بهشت برده شوند تا آنگاه كه بدانجا'),
(4132, 39, 74, 'و گويند: سپاس و ستايش خداى راست كه وعده خويش را با ما راست داشت، و زمين- يعنى زمين بهشت- را به ما ميراث داد كه از بهشت هر جا كه خواهيم جاى گيريم. پس نيكوست مزد عمل‌كنندگان.'),
(4133, 39, 75, 'و فرشتگان را بينى كه گرد عرش را فرا گرفته‌اند، پروردگارشان را همراه با سپاس و ستايش به پاكى ياد مى‌كنند، و ميانشان براستى و درستى داورى شود، و گفته شود: سپاس و ستايش خداى راست، پروردگار جهانيان.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حاميم.'),
(4135, 40, 2, 'فرو فرستادن اين كتاب از سوى خداى توانمند بى‌همتا و داناست'),
(4136, 40, 3, 'آمرزنده گناه و پذيرنده توبه، سخت كيفر [و] خداوند بخشش پاينده. هيچ خدايى جز او نيست بازگشت همه به سوى اوست.'),
(4137, 40, 4, 'در باره آيات خدا جز كسانى كه كافر باشند ستيزه نمى‌كنند، پس آمد شدشان در شهرها- و اينكه فرصت و مهلتى دارند و به تجارت و تصرّف در كارها سرگرم‌اند- تو را نفريبد.'),
(4138, 40, 5, 'پيش از آنان قوم نوح و گروه‌هاى پس از آنها [پيامبران را] تكذيب كردند، و هر امّتى آهنگ آن كردند تا پيامبرشان را بگيرند- و از گفتن حق بازدارند يا بكشند و يا تبعيد كنند-، و به باطل- كژى و ناراستى- ستيزه كردند تا بدان وسيله حق- راستى- را پامال و تباه سازند. پس آنان را [به عذاب‌] بگرفتم، پس [بنگر كه‌] كيفر من چگونه بود.'),
(4139, 40, 6, 'و بدين سان سزا گشته است سخن پروردگارت بر كافران كه آنها دوزخيانند.'),
(4140, 40, 7, 'آنان كه عرش را برمى‌دارند- حمل مى‌كنند- و آنان كه گرداگرد آنند، پروردگارشان را همراه با سپاس و ستايش به پاكى ياد مى‌كنند و به او ايمان مى آورند و براى كسانى كه ايمان آورده‌اند آمرزش مى‌خواهند [و گويند:] پروردگارا، به بخشايش و دانش همه چيز را فرا گرفته‌اى، پس كسانى را كه توبه كردند و راه تو را پيروى نمودند بيامرز و ايشان را از عذاب دوزخ نگاه دار.'),
(4141, 40, 8, 'پروردگارا، و ايشان را به بهشتهاى پاينده كه به آنان و هر شايسته‌كارى از پدران و همسران و فرزندانشان وعده داده‌اى درآر، كه تويى تواناى بى‌همتا و داناى با حكمت'),
(4142, 40, 9, 'و از بديها- كيفر گناهان- نگاهشان دار، و هر كه را در آن روز- روز رستاخيز- از بديها نگاه دارى پس همانا بر او رحمت آورده‌اى، و اين است رستگارى و كاميابى بزرگ.'),
(4143, 40, 10, 'همانا كسانى را كه كافر شدند، [در دوزخ‌] ندا دهند كه هر آينه دشمنى خدا [با شما] از دشمنى شما با خودتان آنگاه كه [در دنيا] به ايمان خوانده مى شديد پس كفر مى‌ورزيديد، بزرگتر است.'),
(4144, 40, 11, 'گويند: پروردگارا، ما را دو بار بميراندى و دو بار زنده گرداندى- اكنون به زندگانى آخرت يقين كرديم-، اينك به گناهانمان اقرار داريم، پس آيا هيچ راهى به سوى بيرون شدن [از اين دوزخ‌] هست؟'),
(4145, 40, 12, 'اين [عذاب‌] براى آن است كه چون خدا به يگانگى خوانده مى‌شد شما كافر شديد، و اگر براى او انباز مى‌آوردند باور مى‌داشتيد، پس فرمان خداى والا و بزرگ راست.'),
(4146, 40, 13, 'اوست كه نشانه‌هاى خود را به شما مى‌نمايد و براى شما از آسمان روزى- باران- فرومى‌فرستد، و پند نگيرد مگر كسى كه [به دل به خدا] بازمى‌گردد.'),
(4147, 40, 14, 'پس خداى را بخوانيد در حالى كه دين را براى او ويژه كرده باشيد گر چه كافران خوش ندارند.'),
(4148, 40, 15, 'بلندكننده پايه‌ها- يا داراى پايه‌هاى بلند- است، خداوند عرش كه از فرمان خويش بر هر يك از بندگانش كه بخواهد روح- وحى يا فرشته وحى- را مى افكند- القاء مى‌كند- تا [مردم را] از روز ديدار- روز رستاخيز- بترساند'),
(4149, 40, 16, 'روزى كه نمايان شوند، هيچ چيز از آنان بر خدا پوشيده نباشد، [و ندا آيد كه‌] امروز پادشاهى كه راست؟ خداى راست، آن يگانه بر همه چيره.'),
(4150, 40, 17, 'امروز هر كسى بدانچه كرده است پاداش داده شود، امروز هيچ ستمى نيست، همانا خدا زود حساب است.'),
(4151, 40, 18, 'و ايشان را از آن روز نزديك- روز قيامت- بيم ده، آنگاه كه دلها به گلوها رسد در حالى كه غمگين و پر از اندوه‌اند ستمكاران را هيچ دوست نزديك و دلسوزى نيست و نه شفاعت‌كننده‌اى كه سخنش [در باره آنها] پذيرفته شود.'),
(4152, 40, 19, 'خيانت چشمها- دزديده نگريستن- و آنچه را سينه‌ها پنهان مى‌دارند مى‌داند.'),
(4153, 40, 20, 'و خدا به راستى و درستى حكم مى‌كند، و كسانى كه اينان به جاى خدا مى‌خوانند هيچ حكمى نمى‌كنند، همانا خداست شنوا و بينا.'),
(4154, 40, 21, 'آيا در زمين گردش نكرده‌اند تا بنگرند كه سرانجام كسانى كه پيش از آنان بودند چگونه بود؟ آنان از اينها نيرومندتر و داراى آثار بازمانده بيشترى در زمين بودند، پس خداوند آنها را به سزاى گناهانشان بگرفت و آنان را از [عذاب‌] خدا هيچ نگاهدارنده‌اى نبود.'),
(4155, 40, 22, 'اين از آن روست كه پيامبرانشان با نشانه‌هاى و دلايل روشن- معجزه و كتاب و حجت- بديشان مى‌آمدند ولى كفر ورزيدند، پس خدا بگرفتشان، كه او نيرومند و سخت كيفر است.'),
(4156, 40, 23, 'و هر آينه موسى را با نشانه‌هاى خويش و حجتهاى روشن فرستاديم،'),
(4157, 40, 24, 'به سوى فرعون و هامان و قارون، پس گفتند: جادوگرى است دروغگو.'),
(4158, 40, 25, 'پس چون [موسى‌] براستى و درستى از جانب ما بديشان آمد، [فرعون و قوم او] گفتند: پسران كسانى را كه با او ايمان آوردند بكشيد و زنانشان را زنده بگذاريد- زيرا از آنها كارزار نيايد و نيز تا نسل بنى اسرائيل منقطع شود-. و نيرنگ و ترفند كافران جز در گمراهى نيست.'),
(4159, 40, 26, 'و فرعون گفت: مرا بگذاريد تا موسى را بكشم و او خداى خويش را بخواند [تا به فريادش رسد] من مى‌ترسم كه كيش شما را دگرگون كند يا در اين سرزمين تباهى پديد آورد.'),
(4160, 40, 27, 'و موسى گفت: من به پروردگار خويش و پروردگار شما پناه مى‌برم از هر گردنكشى كه به روز حساب ايمان ندارد.'),
(4161, 40, 28, 'و مردى مؤمن از آل فرعون كه ايمان خويش پوشيده مى‌داشت گفت: آيا مردى را مى كشيد كه مى‌گويد: پروردگار من خداى يكتاست و با نشانه‌هاى روشن- معجزات- از پروردگارتان نزد شما آمده است؟ و اگر دروغگو باشد پس دروغش بر اوست، و اگر راستگو باشد برخى از آنچه شما را وعده مى كند به شما مى‌رسد. همانا خدا آن را كه گزافكار و دروغگوست راه ننمايد'),
(4162, 40, 29, 'اى قوم من، امروز پادشاهى شما راست كه در اين سرزمين چيره‌ايد، پس كيست كه ما را از عذاب خدا اگر بر سر ما آيد يارى كند؟ فرعون گفت: شما را جز آنچه [صواب‌] مى‌بينم نمى‌نمايم، و جز به راه راست و درست رهنمونى نمى‌كنم.'),
(4163, 40, 30, 'و آن كه ايمان آورده بود، گفت: اى قوم من، همانا من بر شما از [روزى‌] مانند روز [هلاك‌] آن گروه‌ها [كه پيامبران را دروغگو شمردند] مى‌ترسم'),
(4164, 40, 31, 'مانند روش [خداوند در باره‌] قوم نوح- به طوفان- و عاد- قوم هود، به باد صرصر- و ثمود- قوم صالح، به صاعقه- و كسانى كه پس از آنها بودند، و خدا خواهان ستمى به بندگان نيست.'),
(4165, 40, 32, 'و اى قوم من، همانا من بر شما از روز آوازدادن يكديگر مى‌ترسم،'),
(4166, 40, 33, 'آن روز كه پشت كرده روى بگردانيد- از موقف حساب به سوى دوزخ- و شما را از [عذاب‌] خدا نگهدارنده‌اى نيست. و هر كه خدا گمراهش كند او را هيچ راهنمايى نيست.'),
(4167, 40, 34, 'و هر آينه يوسف پيش از اين با حجتهاى روشن نزد شما آمد، پس پيوسته از آنچه شما را آورده بود در شك بوديد، تا چون درگذشت، گفتيد كه خدا پس از او هرگز پيامبرى نخواهد فرستاد. اينچنين خدا هر گزافكار و شك‌دارنده‌اى را گمراه مى‌كند،'),
(4168, 40, 35, 'آنان كه در باره آيات خدا بى‌آنكه حجتى بديشان آمده باشد ستيزه و جدل مى‌كنند، [اين‌] به نزد خدا و نزد كسانى كه ايمان آورده‌اند دشمنى بزرگى است، و اين گونه خداى بر دل هر گردنكش خودكامه‌اى مُهر مى‌نهد.'),
(4169, 40, 36, 'و فرعون گفت: اى هامان، براى من كوشكى- برجى- افراشته بساز تا مگر به آن راه‌ها- يا درها- برسم،'),
(4170, 40, 37, 'راه‌هاى- يا درهاى- آسمانها، تا به خداى موسى ديده‌ور شوم، و هر آينه او را دروغگو مى‌پندارم. و بدين سان براى فرعون بدى كردارش آراسته گشت و از راه [راست‌] بازداشته و گردانيده شد و نيرنگ و ترفند فرعون جز در تباهى نبود.'),
(4171, 40, 38, 'و آن كه ايمان آورده بود، گفت: اى قوم من، مرا پيروى كنيد تا شما را راه راست و درست بنمايم.'),
(4172, 40, 39, 'اى قوم من، جز اين نيست كه زندگانى اين جهان برخورداريى [اندك و زودگذر] است و همانا جهان‌پسين سراى پايندگى است.'),
(4173, 40, 40, 'هر كه كار بدى كند جز مانند آن كيفر داده نشود، و هر كس از زن يا مرد كار نيك و شايسته‌اى كند و مؤمن باشد پس ايشانند كه به بهشت در آيند و در آن بى‌حساب روزى داده شوند.'),
(4174, 40, 41, 'و اى قوم من، چون است كه من شما را به رهايى [از عذاب‌] مى‌خوانم و شما مرا به آتش دوزخ مى‌خوانيد؟!'),
(4175, 40, 42, 'مرا مى‌خوانيد تا به خدا كافر شوم و چيزى را با وى انباز گردانم كه دانشى بدان ندارم، و من شما را به آن [خداى‌] تواناى بى‌همتا و آمرزگار مى‌خوانم.'),
(4176, 40, 43, 'بى‌گمان آنچه مرا به [پرستش‌] آن مى‌خوانيد نه در اين جهان و نه در آن جهان دعوتى ندارند- كسى را به سوى خود نمى‌خوانند-، و بازگشت ما به خداست، و گزافكاران همان دوزخيانند.'),
(4177, 40, 44, 'پس زودا كه آنچه شما را مى‌گويم ياد كنيد، و من كار خود به خدا وامى‌گذارم، كه خدا به بندگان بيناست.'),
(4178, 40, 45, 'پس خدا او را از بديها و سختيهاى آن نيرنگها كه ساختند نگاه داشت و عذاب بد و سخت فرعونيان را فرو گرفت'),
(4179, 40, 46, 'آتش دوزخ، كه بامداد و شامگاه بر آن عرضه مى‌شوند، و روزى كه رستاخيز برپا شود [ندا آيد:] فرعونيان را به سخت‌ترين عذاب درآوريد.'),
(4180, 40, 47, 'و [ياد كن‌] آنگاه كه در آتش دوزخ ستيزه و پرخاش كنند، پس ناتوانان- زبونان قوم- به آنان كه گردنكشى و بزرگ‌منشى كردند گويند: ما [در دنيا] پيروان شما بوديم، پس آيا مى‌توانيد بهرى از آتش را از ما باز داريد؟'),
(4181, 40, 48, 'آنان كه گردنكشى كردند گويند: ما همگى در آتشيم- اگر مى‌توانستيم عذاب را اول از خود باز مى‌داشتيم-، همانا خدا ميان بندگان حكم كرده است- حكمى در خورد هر يك-.'),
(4182, 40, 49, 'و آنان كه در آتشند به نگهبانان دوزخ گويند: پروردگارتان را بخوانيد تا يك روز از عذاب ما سبك گرداند.'),
(4183, 40, 50, 'گويند: آيا پيامبران شما با نشانه‌ها و حجتهاى روشن نزد شما نمى‌آمدند؟ گويند: چرا. گويند: پس دعا كنيد، و دعاى كافران جز در گمراهى و تباهى نيست.'),
(4184, 40, 51, 'هر آينه ما فرستادگان خود و آنان را كه ايمان آورده‌اند در زندگانى اين جهان و در آن روز كه گواهان برپا ايستند يارى مى‌كنيم'),
(4185, 40, 52, 'روزى كه ستمكاران را پوزش خواهى‌شان سود ندهد و آنان راست لعنت و آنان راست بدترين سراى- دوزخ-.'),
(4186, 40, 53, 'و هر آينه موسى را رهنمونى داديم و به فرزندان اسرائيل كتاب- تورات- را به ميراث داديم،'),
(4187, 40, 54, 'كه رهنمونى و يادكرد و پند است مر خردمندان را.'),
(4188, 40, 55, 'پس شكيبايى ورز، كه وعده خدا راست است و براى گناهت آمرزش بخواه و پروردگارت را شبانگاه و بامداد همراه با سپاس و ستايش به پاكى ياد كن.'),
(4189, 40, 56, 'همانا كسانى كه در آيتهاى خدا بى‌دليل و حجتى كه بديشان آمده باشد ستيزه و جدل مى‌كنند در سينه‌هاشان جز بزرگ‌منشى كه بدان نرسند نيست پس به خدا پناه ببر، كه اوست شنوا و بينا.'),
(4190, 40, 57, 'هر آينه آفرينش آسمانها و زمين از آفرينش مردم بزرگتر است ولى بيشتر مردم نمى‌دانند.'),
(4191, 40, 58, 'و نابينا و بينا برابر نيستند، و نه آنان كه ايمان آوردند و كارهاى نيك و شايسته كردند و بدكاران اندك پند مى‌پذيريد.'),
(4192, 40, 59, 'هر آينه رستاخيز آمدنى است، هيچ شكى در آن نيست، ولى بيشتر مردم باور نمى‌دارند.'),
(4193, 40, 60, 'و پروردگارتان گفت: مرا بخوانيد تا شما را پاسخ گويم همانا كسانى كه از پرستش من سركشى كنند زودا كه خوار و سرافكنده به دوزخ در آيند.'),
(4194, 40, 61, 'خدا آن است كه شب را براى شما پديد كرد تا در آن بياراميد و روز را روشن ساخت- تا در آن ببينيد و روزى بجوييد- همانا خدا خداوند فزون‌بخشى است بر مردم، ولى بيشتر مردم سپاس نمى‌گزارند.'),
(4195, 40, 62, 'اين است خداى، پروردگار شما، كه آفريننده هر چيزى است، خدايى جز او نيست، پس كجا و چگونه [از وى‌] گردانيده مى‌شويد؟'),
(4196, 40, 63, 'اينچنين آنان كه آيات خدا را انكار مى‌كردند [از حق‌] گردانيده مى‌شوند.'),
(4197, 40, 64, 'خدا آن است كه زمين را براى شما آرامگاهى و آسمان را بنائى- سقفى- برافراشته ساخت، و شما را صورت نگاشت و نيكو صورت نگاشت، و از پاكيزه‌ها به شما روزى داد. اين است خداى، پروردگار شما، پس بزرگ و بزرگوار است خداى، پروردگار جهانيان.'),
(4198, 40, 65, 'اوست زنده [جاويد]، خدايى جز او نيست، پس او را بخوانيد در حالى كه دين را ويژه او كرده باشيد، سپاس و ستايش خداى راست، پروردگار جهانيان.'),
(4199, 40, 66, 'بگو: همانا من باز داشته شده‌ام از اينكه آنها را كه شما به جاى خدا مى‌خوانيد بپرستم آنگاه كه مرا نشانه‌ها و دلايل روشن از پروردگارم آمد، و فرمان يافته‌ام كه [تنها] پروردگار جهانيان را گردن نهم.'),
(4200, 40, 67, 'اوست آن [خداى‌] كه شما را از خاك آفريد، پس از آن از نطفه و سپس از خون بسته، آنگاه شما را كودكى خرد [از رحم مادر] بيرون آورد، سپس [امكان داد] تا به جوانى و نيرومندى خود برسيد و سپس تا [هنگامى كه‌] پير شويد. و از شما كسانى از پيش- پيش از نيرومندى و پيرى- جانشان گرفته شود و [امكان داد] تا به سرآمدى نامبرده- هنگام مرگ- برسيد و باشد كه خرد را كار بنديد.'),
(4201, 40, 68, 'اوست آن كه زنده كند و بميراند، و چون كارى را [خواهد و] حكم كند، همانا آن را گويد: باش، پس مى‌باشد.'),
(4202, 40, 69, 'آيا به آنان كه در باره آيات خدا ستيزه مى‌كنند ننگريستى كه چگونه [از تصديق و ايمان بدان‌] گردانيده مى‌شوند؟'),
(4203, 40, 70, 'همانان كه كتاب [الهى‌]- اين كتاب يا هر كتاب آسمانى- و آنچه را- از احكام و مواعظ- كه بدان پيامبران خود را فرستاديم دروغ شمردند، پس بزودى خواهند دانست.'),
(4204, 40, 71, 'آنگاه كه غلها و زنجيرها در گردنهاشان باشد و بر روى كشيده شوند،'),
(4205, 40, 72, 'در آب جوشان، سپس در آتش سوخته و بريان شوند.'),
(4206, 40, 73, 'سپس به آنها گفته شود: كجايند آنچه شريك مى‌گرفتيد،'),
(4207, 40, 74, 'براى خدا؟ گويند: از ما گم و نابود شدند، بلكه پيش از اين چيزى را نمى‌خوانديم- نمى پرستيديم-. اينچنين خداوند كافران را گمراه مى‌كند.'),
(4208, 40, 75, 'اين [عذاب‌] بدان سبب است كه در زمين به ناروا شادى همى‌كرديد و شادمانه به ناز و سرمستى مى‌خراميديد.'),
(4209, 40, 76, 'به دروازه‌هاى دوزخ در آييد، كه در آن جاودانه باشيد، پس بد است جايگاه گردنكشان.'),
(4210, 40, 77, 'پس [بر آزار قوم‌] شكيبايى كن، همانا وعده خدا راست است، كه اگر برخى از آن [عذاب‌] كه آنان را وعده مى‌كنيم به تو بنماييم يا جان تو را برگيريم- پيش از عذاب آنها-، آنها به سوى ما بازگردانيده مى‌شوند- و عذابشان خواهيم كرد-.'),
(4211, 40, 78, 'و هر آينه پيش از تو پيامبرانى فرستاديم، بهرى از ايشان آنانند كه داستانشان را بر تو گفتيم و داستان برخى را بر تو نگفتيم و هيچ پيامبرى را نرسد كه جز به خواست و فرمان خدا آيه‌اى بياورد پس آنگاه كه فرمان خدا بيايد بحق حكم كرده شود، و در آنجا ناراستان و ياوه‌گويان- كافران- زيانكار گردند.'),
(4212, 40, 79, 'خداست آن كه چارپايان را از بهر شما آفريد تا بر برخى از آنها سوار شويد و برخى از آنها را بخوريد'),
(4213, 40, 80, 'و براى شما در آنها سودهاست- از پشم و پوست و شير آنها- و تا [با سوار شدن‌] بر آنها به حاجتى كه در دلهاى خود داريد- از داد و ستد و معامله- برسيد و بر آنها و بر كشتيها برداشته- سوار- شويد.'),
(4214, 40, 81, 'و نشانه‌هاى خويش را به شما مى‌نمايد، پس كدام يك از نشانه‌هاى خداى را انكار مى‌كنيد؟'),
(4215, 40, 82, 'آيا در زمين گردش نكرده‌اند تا بنگرند كه سرانجام كسانى كه پيش از آنان بودند چگونه بود؟ آنان كه از ايشان نيرومندتر بودند و نشانه‌هاى بجامانده بيشترى در زمين داشتند پس آنچه فراهم مى‌كردند به كارشان نيامد- و عذاب را از آنها دفع نكرد-.'),
(4216, 40, 83, 'پس چون پيامبرانشان با نشانه‌ها و دلايل روشن بديشان آمدند آنان بدانچه از دانش- دانش معاش مانند پيشه‌ها و صناعات- نزدشان بود دل خوش كردند، و آنچه بدان استهزا مى‌كردند آنها را فروگرفت.'),
(4217, 40, 84, 'پس چون عذاب سخت ما را ديدند گفتند: به خداى يگانه ايمان آورديم و بدانچه شرك مى‌آورديم كافر شديم.'),
(4218, 40, 85, 'پس ايمانشان آنگاه كه عذاب ما را ديدند آنان را سود نبخشيد. اين- سود نبخشيدن ايمان در آن حال- نهاد و روش خداست كه در باره بندگانش گذشته است، و آنجاست- آنگاه كه عذاب خداى دررسيد- كه كافران زيانكار شدند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حاميم.'),
(4220, 41, 2, '[اين‌] فرو فرستاده‌اى است از سوى آن بخشاينده مهربان.'),
(4221, 41, 3, 'كتابى است كه آيات آن به روشنى بازگشاده و بيان شده است، قرآنى به زبان تازى، براى مردمى كه بدانند'),
(4222, 41, 4, 'مژده‌دهنده و بيم‌كننده، ولى بيشترشان رويگردان شدند پس نمى‌شنوند [و نمى‌پذيرند].'),
(4223, 41, 5, 'و گفتند: دلهاى ما از آنچه ما را بدان مى‌خوانى در پوششهايى است و در گوشهاى ما سنگينى و گرانى است و ميان ما و تو پرده‌اى است، پس [هر چه توانى‌] كار مى‌كن كه ما نيز [هر چه توانيم‌] كاركننده‌ايم.'),
(4224, 41, 6, 'بگو: جز اين نيست كه من آدمى‌ام مانند شما، به من وحى مى‌شود كه همانا خداى شما خدايى است يگانه، پس به سوى او روى آريد و راست باشيد- به يكتا پرستى و طاعت و پايدارى بر آن- و از او آمرزش بخواهيد، و واى بر مشركان،'),
(4225, 41, 7, 'آنان كه زكات نمى‌دهند و به جهان واپسين كافرند.'),
(4226, 41, 8, 'همانا كسانى كه ايمان آورده‌اند و كارهاى نيك و شايسته كرده‌اند مزدى بى‌پايان- قطع نشدنى يا ناكاسته يا بى‌منت- دارند.'),
(4227, 41, 9, 'بگو: آيا شما به آن [خداى‌] كه زمين را در دو روز- دو دوران- بيافريد كافر مى‌شويد و براى او همتايان مى‌سازيد؟ اين است پروردگار جهانيان'),
(4228, 41, 10, 'و در آن كوه‌هايى استوار بر روى آن پديد آورد و در آن بركت نهاد و روزيهايش را در آن در چهار روز- چهار دوران- به اندازه مقرر كرد، يكسان و درست- بى‌زيادت و نقصان- براى خواهندگان.'),
(4229, 41, 11, 'سپس به آسمان پرداخت و آن دودى- گاز يا بخارى دودمانند- بود، پس به آن و به زمين گفت: خواه يا ناخواه بياييد، گفتند: فرمانبردار آمديم.'),
(4230, 41, 12, 'آنگاه آنها را- كه به صورت دود بودند- در دو روز، هفت آسمان بساخت و در هر آسمانى فرمان- يا كار- آن را وحى كرد، و آسمان دنيا- نزديكتر- را به چراغهايى- ستارگان- بياراستيم و [آن را] نگاه داشتيم- از آسيب و خلل، يا از ورود شياطين- اين است [آفريدن و] اندازه‌نهادن آن بى‌همتا تواناى دانا.'),
(4231, 41, 13, 'پس اگر روى برتافتند، بگو: شما را از عذابى مانند عذاب قوم عاد و ثمود- كه به باد صرصر و بانگ كشنده آسمانى گرفتار شدند- بيم مى‌كنم.'),
(4232, 41, 14, 'آنگاه كه فرستادگان از پيش روى و از پشت سرشان- يعنى از هر سو و در هر زمان- بديشان آمدند [و گفتند] كه جز خداى يكتا را نپرستيد، گفتند: اگر پروردگار ما مى‌خواست [كه فرستادگانى بفرستد] هر آينه فرشتگانى فرومى‌فرستاد، پس ما بدانچه شما به آن فرستاده شده‌ايد كافريم.'),
(4233, 41, 15, 'اما عاد- قوم هود- در زمين به ناروا گردنكشى كردند و گفتند: كيست نيرومندتر از ما؟ آيا نديدند- يعنى ندانستند- كه آن خداى كه آنان را آفريده، از ايشان نيرومندتر است؟ و آنها آيات ما را انكار مى‌كردند.'),
(4234, 41, 16, 'پس بر آنان تندبادى سرد و سخت آواز در روزهايى شوم- يا پُر گرد و غبار- فرستاديم تا در زندگانى اين جهان عذاب خوارى و رسوايى را به آنان بچشانيم، و هر آينه عذاب آن جهان بدتر و رسواكننده‌تر است و آنها يارى نشوند.'),
(4235, 41, 17, 'و اما قوم ثمود- قوم صالح-، آنان را راه نموديم ولى كورى- گمراهى- را بر رهيابى برگزيدند پس به سزاى آنچه مى‌كردند بانگ عذاب خواركننده آنان را بگرفت.'),
(4236, 41, 18, 'و كسانى را كه ايمان آورده بودند و پرهيزگارى مى‌كردند رهانيديم.'),
(4237, 41, 19, 'و روزى كه دشمنان خداى [براى رفتن‌] به سوى آتش دوزخ برانگيخته و فراهم شوند پس آنها را باز دارند- تا پسينيان برسند و همگى يكجا گرد آيند-'),
(4238, 41, 20, 'تا چون به دوزخ رسند گوش و ديدگان و پوستهاشان- يعنى اندامهاشان- بدانچه مى‌كردند گواهى دهند.'),
(4239, 41, 21, 'و به پوستهاى خويش گويند: چرا بر ما گواهى داديد؟ گويند: خدايى كه هر چيزى را گويا كرده است ما را به سخن آورد. و اوست كه شما را نخستين بار بيافريد و به سوى او بازگردانده مى‌شويد.'),
(4240, 41, 22, 'و شما [هنگام گناهكارى در دنيا] از اينكه گوش و ديدگان و پوستهاتان بر ضد شما گواهى دهند نمى‌توانستيد پنهان شويد و ليكن [به سبب پنهانى گناه‌كردن‌] پنداشتيد كه خدا بسيارى از آنچه مى‌كنيد نمى‌داند.'),
(4241, 41, 23, 'و اين بود پندارتان كه به پروردگار خود گمان مى‌برديد شما را هلاك كرد پس از زيانكاران گشتيد.'),
(4242, 41, 24, 'اگر شكيبايى كنند جايشان آتش است و اگر عفو و خشنودى [خدا] جويند- به پوزش‌خواهى- درخواستشان پذيرفته نشود.'),
(4243, 41, 25, 'و براى آنان همگنانى [از شياطين‌] برانگيختيم پس آنچه را كه پيش رويشان و پشت سرشان است- لذتهاى حاضر و آنچه در آينده آرزو داشتند- برايشان آراسته نمودند و آن گفتار- كلمه عذاب- بر آنان سرا شد در حالى كه همچون گروه‌هايى از پريان و آدميان بودند كه پيش از ايشان گذشتند همانا آنها زيانكار بودند.'),
(4244, 41, 26, 'و كسانى كه كافر شدند گفتند: به اين قرآن گوش فرا مدهيد و [هنگام قرائت وى‌] سخن ياوه در آن افكنيد تا شايد پيروز شويد.'),
(4245, 41, 27, 'پس بى‌گمان كسانى را كه كافر شدند عذابى سخت بچشانيم و آنان را بر پايه بدترين آنچه مى‌كردند كيفر دهيم.'),
(4246, 41, 28, 'اين است كيفر دشمنان خدا، آتش دوزخ آنان را در آن خانه هميشگى است، به سزاى آنكه آيات ما را انكار مى‌كردند.'),
(4247, 41, 29, 'و كسانى كه كافر شدند گويند: بار خدايا، آن دو كس از پريان و آدميان كه ما را گمراه كردند به ما بنماى كه زير گامهاى خود بگذاريمشان تا از فروترينها باشند.'),
(4248, 41, 30, 'همانا كسانى كه گفتند: پروردگار ما خداى يكتاست آنگاه [بر آن‌] پايدارى نمودند، فرشتگان بر آنان فرومى‌آيند [و گويند] كه مترسيد و اندوه مداريد و شما را مژده باد به آن بهشتى كه نويد داده مى‌شديد'),
(4249, 41, 31, 'ما در زندگانى اين جهان و در آن جهان دوستان شماييم، و شما راست در آن جهان آنچه جانهاى شما خواهش كند و شما راست در آن هر چه درخواست كنيد'),
(4250, 41, 32, 'پيشكشى و پذيرائيى [است‌] از سوى آمرزگار مهربان.'),
(4251, 41, 33, 'و كيست نيكوگفتارتر از آن كه به خداى مى‌خواند- يعنى پيامبر اكرم'),
(4252, 41, 34, 'و نيكى و بدى برابر نيست، [بدى ديگران را] به شيوه‌اى كه نيكوتر است دور كن كه [اگر چنين كنى‌] آنگاه آن كه ميان تو و او دشمنى است همچون دوستى نزديك و مهربان گردد.'),
(4253, 41, 35, 'و از اين [صفت‌]- كه بدى را به نيكى پاسخ دهند- برخوردار نشوند مگر آنان كه شكيبايى كنند و از اين [خصلت‌] برخوردار نشود مگر آن كه داراى بهره اى بزرگ [از خردمندى و كمال انسانى‌] است.'),
(4254, 41, 36, 'و اگر تو را وسوسه‌اى از شيطان برسد كه بشوراندت، پس به خداى پناه ببر، كه اوست شنوا و دانا.'),
(4255, 41, 37, 'و از نشانه‌هاى [قدرت‌] او شب و روز و خورشيد و ماه است خورشيد و ماه را سجده نكنيد، و خداى را كه آنها را آفريده سجده كنيد اگر او را مى پرستيد و بس.'),
(4256, 41, 38, 'پس اگر گردنكشى و بزرگ‌منشى نمودند، آنان كه نزد پروردگار تواند- فرشتگان مقرب و اولياى خدا- او را شب و روز به پاكى مى‌ستايند و خسته و ملول نشوند.'),
(4257, 41, 39, 'و از نشانه‌هاى [قدرت‌] او اين است كه زمين را پژمرده و فرسوده مى‌بينى، پس چون آب بر آن فرستيم بجنبد و بردمد، همانا آن [خداى‌] كه آن را زنده كرد زنده‌كننده مردگان است، كه او بر هر چيزى تواناست.'),
(4258, 41, 40, 'همانا كسانى كه در باره آيات ما كجروى مى‌كنند- به سوى باطل مى‌گردند- بر ما پوشيده نيستند. آيا كسى كه در آتش [دوزخ‌] افكنده شود بهتر است يا كسى كه روز رستاخيز ايمن مى‌آيد؟ هر چه خواهيد بكنيد، كه او بدانچه مى‌كنيد بيناست.'),
(4259, 41, 41, 'همانا كسانى كه به اين ذكر- قرآن- هنگامى كه بديشان آمد كافر شدند [بدبخت و هلاك گشتند]، و هر آينه آن كتابى است بى‌همتا و ارجمند- كه مانند آن نتوانند آورد-'),
(4260, 41, 42, 'باطل- كاهش و افزايش و نادرستى- از پيش آن و از پس آن- در حال و آينده- بدان راه نيابد، كه فروفرستاده‌اى است از سوى [خداى‌] با حكمت و ستوده.'),
(4261, 41, 43, '[اين كافران‌] به تو نمى‌گويند مگر آنچه به فرستادگان پيش از تو گفته شد. هر آينه پروردگار تو خداوند آمرزش و خداوند كيفرى دردناك است.'),
(4262, 41, 44, 'و اگر ما آن را قرآنى به زبان عجمى- غير عربى- مى‌ساختيم مى‌گفتند: چرا آيات آن بروشنى بيان نشده است؟ آيا [كتابى‌] عجمى و [پيامبرى‌] عربى؟! بگو: آن براى كسانى كه ايمان آورده‌اند رهنمونى و بهبودبخش است. و آنان كه ايمان نياورند، در گوشهاشان سنگينى و گرانى است و آن بر آنها [مايه‌] كورى است. اينان [گويى‌] از جايى دور خوانده مى‌شوند- كه نه مى‌شنوند و نه مى‌فهمند-.'),
(4263, 41, 45, 'و هر آينه موسى را كتاب داديم، پس در آن اختلاف كردند و اگر نبود سخنى از پروردگارت كه از پيش رفته است هر آينه ميانشان [به عذاب‌] حكم مى شد، و همانا آنان- كفار قوم تو- در باره آن- قرآن يا آنچه ياد كرديم- به شك و ترديد اندرند.'),
(4264, 41, 46, 'هر كه كارى نيك و شايسته كند به سوى خود اوست، و هر كه كارى بد و ناشايست كند به زيان خود اوست، و پروردگار تو به بندگان ستمگر نيست.'),
(4265, 41, 47, 'دانش رستاخيز به او باز گردانيده مى‌شود- تنها او مى‌داند و بس-. و هيچ ميوه‌اى از غلاف و شكوفه‌اش بيرون نيايد و هيچ ماده‌اى باردار نشود و بار ننهد مگر به دانش او. و آن روز كه آنان- مشركان- را خواند كه كجايند آن انبازان من [كه مى‌پنداشتيد] گويند: تو را گفتيم كه از ما هيچ كس گواه نيست [بر اينكه تو را انباز بوده است‌]- يعنى از شرك بيزارى مى‌نمايند-.'),
(4266, 41, 48, 'و آنچه پيش از اين مى‌خواندند- مى‌پرستيدند- از آنها گم شود و باور كنند كه آنان را هيچ گريزگاهى نيست.'),
(4267, 41, 49, 'آدمى از خواستن نيكى- نعمت و تندرستى و مانند اينها- خسته [و سير] نشود و اگر او را بدى- سختى و تنگدستى و بيمارى- برسد پس نوميد و دلسرد گردد.'),
(4268, 41, 50, 'و اگر پس از گزندى كه بدو رسيده وى را رحمتى بچشانيم هر آينه گويد: اين مراست- از دانش و تدبير خود من است و من سزاوار آنم- و نپندارم كه رستاخيز برپا شود، و اگر به پروردگدارم باز گردانده شوم مرا نزد او پاداشى نيكوتر خواهد بود. پس هر آينه كسانى را كه كافر شدند بدانچه كردند آگاه سازيم، و بى‌گمان آنان را عذابى سخت بچشانيم.'),
(4269, 41, 51, 'و چون به آدمى نيكويى كنيم و نعمت ارزانى داريم، [از ما و طاعت ما] روى بگرداند و [با حال تكبر و استغنا] خود را [از ما] دور دارد، و چون بدى به او رسد پس دعائى دور و دراز خواهد داشت.'),
(4270, 41, 52, 'بگو: مرا خبر دهيد كه اگر [اين قرآن‌] از نزد خدا باشد و شما بدان كافر شويد، كيست گمراه‌تر از آن كه در مخالفتى دور [از حق و صواب‌] است؟'),
(4271, 41, 53, 'زودا كه آنان را نشانه‌هاى خويش در كناره‌هاى جهان و در جانهاى خودشان بنماييم تا آنكه برايشان روشن و آشكار شود كه همانا او- يا آن- حق است. آيا اين بس نيست كه پروردگار تو بر هر چيزى گواه و آگاه است؟'),
(4272, 41, 54, 'آگاه باش كه آنان- كافران- از ديدار پروردگار خويش- روز رستاخيز- در شك‌اند، آگاه باش كه همانا او همه چيز را [به علم و قدرت خود] فراگيرنده است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حاميم.'),
(4274, 42, 2, 'عين، سين، قاف.'),
(4275, 42, 3, 'خداى تواناى بى‌همتا و داناى با حكمت اينچنين به تو و به آنان كه پيش از تو بودند وحى مى‌فرستد.'),
(4276, 42, 4, 'او راست آنچه در آسمانها و آنچه در زمين است، و اوست والا مرتبه و بزرگ.'),
(4277, 42, 5, 'نزديك است كه آسمانها [از عظمت و هيبت او] از فراز يكديگر- يعنى نخست آسمان بلندتر و پس از آن يكايك- بشكافند، و فرشتگان پروردگارشان را همراه با سپاس و ستايش به پاكى ياد مى‌كنند و براى كسانى كه در زمين‌اند آمرزش مى‌خواهند. آگاه باشيد كه خداست آمرزگار و مهربان.'),
(4278, 42, 6, 'و كسانى كه به جاى خدا دوستان و كارسازانى گرفته‌اند، خداوند بر [گفتار و كردار] آنان نگاهبان است و تو بر آنها گماشته و نگهبان نيستى.'),
(4279, 42, 7, 'و اينچنين قرآنى به زبان تازى به تو وحى كرديم تا اهل مكه و هر كه را كه پيرامون آن است بيم كنى و [بويژه‌] از روز فراهم‌آمدن- روز قيامت- كه هيچ شكى در آن نيست بترسانى كه گروهى در بهشت‌اند و گروهى در آتش سوزان.'),
(4280, 42, 8, 'و اگر خدا مى‌خواست هر آينه آنان را يك امت مى‌ساخت و ليكن هر كه را خواهد در بخشايش خود در آورد، و ستمكاران را هيچ دوست و ياورى نيست.'),
(4281, 42, 9, 'بلكه جز او دوستان و سرپرستانى گرفته‌اند و حال آنكه خداست كه دوست و سرپرست است، و همو مردگان را زنده مى‌كند، و او بر هر چيزى تواناست.'),
(4282, 42, 10, 'و آنچه در آن اختلاف كرديد، هر چه باشد، پس حكمش با خداست، اين است خداى، پروردگار من، تنها بر او توكل كردم و به او بازمى‌گردم- روى دل به او مى‌كنم-.'),
(4283, 42, 11, 'پديدآرنده آسمانها و زمين است براى شما از جنس خودتان همسرانى بيافريد، و از چارپايان نيز جفتها بيافريد، شما را در اين [زناشويى و تناسل‌] بسيار و پراكنده مى‌گرداند. چيزى همانند او نيست، و اوست شنوا و بينا.'),
(4284, 42, 12, 'او راست كليدهاى [خزائن‌] آسمانها و زمين روزى را براى هر كه بخواهد فراخ مى‌كند و تنگ مى‌گرداند، همانا او به هر چيزى داناست.'),
(4285, 42, 13, 'براى شما از دين چيزى را مقرر كرد كه نوح را بدان سفارش كرده بود و نيز آنچه را به تو وحى كرديم و آنچه ابراهيم و موسى و عيسى را به آن سفارش كرديم كه اين دين را برپا داريد و در آن پراكندگى و اختلاف مكنيد آنچه بدان مى‌خوانى بر مشركان گران و دشوار است. خدا هر كه را خواهد به سوى خود- يعنى براى رسالت خود- برمى‌گزيند و هر كه را [به دل‌] به او بازگردد به سوى خود راه مى‌نمايد.'),
(4286, 42, 14, 'و پراكندگى و اختلاف نكردند مگر پس از آنكه دانش بديشان رسيد، آنهم از روى ستم و حسدى كه ميان خود داشتند و اگر نبود سخنى كه از پروردگارت از پيش رفته است [به مهلت‌دادن ايشان‌] تا سرآمدى نامبرده، هر آينه ميانشان حكم مى‌شد- عذاب آنها را فرو مى‌گرفت-. و همانا كسانى كه پس از آنان كتابشان داده‌اند در باره آن- كتاب يا دين- به شك و ترديد اندرند.'),
(4287, 42, 15, 'پس براى اين- براى وحدت دين و عدم پراكندگى در آن- [آنان را به برپا داشتن دين‌] بخوان و چنانكه فرمان يافته‌اى استوار و پايدار باش و از كامها و هوسهاى آنان پيروى مكن، و بگو: به كتابى كه خداى فروفرستاده است ايمان آوردم و فرمان يافته‌ام تا ميان شما به عدل و داد رفتار كنم. خداى يكتا پروردگار ما و پروردگار شماست. ما را كارهاى ما و شما را كارهاى شما ميان ما و شما هيچ ستيزه‌اى نيست. خدا ميان ما گرد آرد- هنگام رستاخيز-، و بازگشت [همه‌] به سوى اوست.'),
(4288, 42, 16, 'و آنان كه در باره خدا- يا دين او- پس از آنكه دعوت او اجابت شده است- يعنى به فطرت خودشان- گفت‌وگو و ستيزه مى‌كنند حجتشان نزد پروردگارشان تباه و ناچيز است و بر آنهاست خشمى [از خداوند] و آنان راست عذابى سخت.'),
(4289, 42, 17, 'خداست كه كتاب را بدرستى فروفرستاد و نيز ترازو را- تا چون ترازو راست باشند و حق و عدل و انصاف را رعايت كنند- و تو چه مى‌دانى؟ شايد كه قيامت نزديك باشد.'),
(4290, 42, 18, 'كسانى آن را به شتاب مى‌خواهند كه آن را باور ندارند، و كسانى كه ايمان دارند از آن بيمناكند و مى‌دانند كه آن راست است. آگاه باش، كسانى كه در [آمدن‌] قيامت ستيزه و جدل مى‌كنند- اصرار بر انكار آن دارند- هر آينه در گمراهى دورند.'),
(4291, 42, 19, 'خداى به بندگان خويش مهربان و نيكوكار- يا در كارهاشان دقيق و باريكدان- است، هر كه را خواهد روزى دهد، و اوست نيرومند و تواناى بى‌همتا.'),
(4292, 42, 20, 'هر كه كشت- ثواب- آن جهان خواهد براى او در كشتش مى‌افزاييم، و هر كه كشت اين جهان خواهد به وى از آن مى‌دهيم و در آن جهان هيچ بهره‌اى ندارد.'),
(4293, 42, 21, 'يا مگر آنان- مشركان- را انبازانى است- بتان يا شياطين كه شريك خدا مى‌پنداشتند- كه از دين آنچه را كه خداى بدان رخصت نداده است برايشان بنهاده اند؟ و اگر نبود آن سخن پايانى- فيصله‌دهنده- [در تأخير كيفرشان‌]، هر آينه ميانشان حكم كرده مى‌شد- عذابشان مى‌آمد-، و همانا ستمكاران را عذابى است دردناك.'),
(4294, 42, 22, 'ستمكاران را بينى كه از آنچه كرده‌اند ترسانند و حال آنكه آن- وبال آنچه كرده‌اند- به ايشان رسد. و كسانى كه ايمان آورده‌اند و كارهاى نيك و شايسته كرده‌اند در باغهاى بهشت باشند، آنان راست نزد پروردگارشان هر چه خواهند. اين است آن فزون‌بخشى بزرگ.'),
(4295, 42, 23, 'اين است آنچه خداوند به بندگان خود، آنان كه ايمان آوردند و كارهاى نيك و شايسته كردند، نويد مى‌دهد. بگو: بر اين [رساندن پيام‌] هيچ مزدى از شما نمى‌خواهم مگر دوستى در باره خويشاوندان [نزديكم‌] و هر كه نيكويى ورزد او را در آن، نيكويى بيفزاييم همانا خدا آمرزنده و سپاسدار است.'),
(4296, 42, 24, 'بلكه گويند كه بر خدا دروغ بافته است! پس اگر خداى خواهد بر دل تو مُهر مى‌نهد و خدا باطل را نابود كند و حق را با سخنان خود استوار و پايدار سازد همانا او بدانچه در سينه‌هاست داناست.'),
(4297, 42, 25, 'و اوست آن [خداى‌] كه توبه را از بندگان خود مى‌پذيرد و از بديها- گناهانشان- درمى‌گذرد و آنچه مى‌كنيد مى‌داند.'),
(4298, 42, 26, 'و آنان را كه ايمان آوردند و كارهاى نيك و شايسته كردند پاسخ مى‌دهد و از فزون‌بخشى خويش مى‌افزايدشان و كافران را عذابى سخت است.'),
(4299, 42, 27, 'و اگر خداوند روزى را بر بندگان خود فراخ مى‌كرد هر آينه در زمين ستم و سركشى مى‌كردند و ليكن به اندازه‌اى كه بخواهد فرو مى‌آرد، كه او به بندگان خويش آگاه و بيناست- كه چگونه و چند فراخور حالشان روزى دهد-.'),
(4300, 42, 28, 'و اوست آن [خداى‌] كه باران سودمند را پس از آنكه [از آمدنش‌] نااميد شدند فرو مى‌فرستد و رحمت خويش را مى‌گستراند- باران را مى‌پراكند و انواع گياهان و درختان و ميوه‌ها را پديد مى‌آورد-، و اوست دوست و كارساز [مؤمنان‌] و ستوده.'),
(4301, 42, 29, 'و از نشانه‌هاى او آفريدن آسمانها و زمين است و آنچه در آنها از جنبندگان پراكنده كرده است و او بر فراهم‌آوردن آنها هر گاه بخواهد تواناست.'),
(4302, 42, 30, 'و هر مصيبتى كه به شما رسد به سبب كارهايى- خطاها و گناهانى- است كه دستهايتان كرده، و از بسيارى [گناهان‌] درمى‌گذرد.'),
(4303, 42, 31, 'و شما در زمين عاجزكننده [خدا] نيستيد- نمى‌توانيد از فرو آمدن مصيبتها به سزاى نافرمانى‌تان بگريزيد-، و شما را جز خدا دوست كارساز و ياورى نيست.'),
(4304, 42, 32, 'و از نشانه‌هاى [قدرت‌] او كشتيهايى است چون كوه‌ها كه در دريا روانند'),
(4305, 42, 33, 'اگر خواهد باد را ساكن گرداند تا كشتيها بر روى آن- دريا- ايستاده بمانند. هر آينه در آن- روان‌شدن باد و كشتيها- براى هر شكيباى سپاسگزارى نشانه‌ها و عبرتهاست.'),
(4306, 42, 34, 'يا آنها را بدانچه آنان- كشتى‌نشينان- كرده‌اند هلاك كند- غرق گرداند- و [اگر خواهد] از بسيارى [گناهان‌] درمى‌گذرد،'),
(4307, 42, 35, '[تا مؤمنان عبرت گيرند] و تا كسانى كه در آيات ما ستيزه مى‌كنند بدانند كه آنان را هيچ گريزگاهى نيست.'),
(4308, 42, 36, 'پس هر چه- كه تعلق به اين جهان دارد از مال و فرزند- به شما داده‌اند، [اندك‌] بهره‌اى است از زندگانى [ناپايدار] دنيا و آنچه نزد خداوند است بهتر و پاينده‌تر است براى آنان كه ايمان آورده‌اند و بر پروردگارشان توكل مى‌كنند'),
(4309, 42, 37, 'و آنان كه از گناهان بزرگ و كارهاى زشت دورى مى‌جويند و چون به خشم آيند درمى‌گذرند'),
(4310, 42, 38, 'و آنان كه [دعوت‌] پروردگار خويش را پاسخ داده‌اند و نماز را برپا داشته‌اند و كارشان با مشورت ميان خودشان باشد و از آنچه ايشان را روزى كرده ايم انفاق مى‌كنند'),
(4311, 42, 39, 'و آنان كه چون ستمى بديشان رسد دادخواهى كنند- و به يكديگر يارى رسانند-.'),
(4312, 42, 40, 'و كيفر بدى بديى باشد مانند آن- نه بيشتر-، پس هر كه درگذرد و اصلاح كند- ميان خود و ستمكار خود- پاداش او بر خداست. همانا او ستمكاران را دوست ندارد.'),
(4313, 42, 41, 'و هر آينه هر كه كين ستاند پس از آنكه بر او ستم رفته باشد، پس هيچ راهى [از تعرض و سرزنش‌] بر آنان نيست.'),
(4314, 42, 42, 'همانا راه [تعرض و ملامت‌] تنها بر كسانى است كه به مردم ستم مى‌كنند و در زمين به ناروا ستم و سركشى مى‌كنند- يا از حد درمى‌گذرند و فزونى مى‌جويند- اينان را عذابى است دردناك.'),
(4315, 42, 43, 'و هر كه شكيبايى ورزد و درگذرد، هر آينه آن از كارهاى ستوده و استوار است.'),
(4316, 42, 44, 'و هر كه را خدا گمراه كند پس او را هيچ دوست و كارسازى پس از وى نيست و ستمكاران را بينى كه چون عذاب را بينند گويند: آيا راهى به بازگشت هست؟'),
(4317, 42, 45, 'و آنان را بينى كه بر آن'),
(4318, 42, 46, 'و آنان را جز خدا دوستان و كارسازانى كه ياريشان كنند نيست و هر كه را خدا گمراه كند او را هيچ راهى [به رستگارى‌] نيست.'),
(4319, 42, 47, 'پروردگارتان را پاسخ دهيد- به پذيرش فرمان او- پيش از آنكه روزى بيايد- روز رستاخيز- كه آن را بازگشتى از سوى خدا نيست- زيرا به آن حكم كرده و آن حكم باطل‌نشدنى است- در آن روز هيچ پناهگاهى نداريد و شما را ياراى هيچ انكارى نيست.'),
(4320, 42, 48, 'پس اگر روى گرداندند، ما تو را بر آنها نگهبان نفرستاده‌ايم بر تو جز رساندن پيام نيست. و هر گاه آدمى را از جانب خويش رحمتى- نعمتى- بچشانيم بدان شادمان شود- و از ما غافل گردد- و اگر بديى- گزندى- به آنان رسد بدانچه دستهايشان از پيش فرستاده- از كارهاى ناشايسته-، پس آنگاه آدمى ناسپاس است- نعمت را فراموش مى‌كند-.'),
(4321, 42, 49, 'خداى راست پادشاهى آسمانها و زمين آنچه خواهد مى‌آفريند به هر كه خواهد دختران مى‌بخشد و به هر كه خواهد پسران مى‌بخشد'),
(4322, 42, 50, 'يا آنان را پسران و دختران هر دو مى‌دهد، و هر كه را خواهد نازا و بى‌فرزند مى‌گرداند، كه او دانا و تواناست.'),
(4323, 42, 51, 'و هيچ آدمى را نرسد كه خداى با او سخن گويد مگر به وحى يا از پس پرده، يا فرستاده‌اى- فرشته‌اى- فرستد پس به فرمان او آنچه خواهد [به وى‌] وحى كند. همانا او والا مرتبه و با حكمت است.'),
(4324, 42, 52, 'و همچنين از فرمان خويش روحى- قرآن- را به تو وحى كرديم. تو نمى‌دانستى كه كتاب و ايمان چيست و ليكن ما آن را نورى ساختيم كه بدان هر كه را از بندگان خويش بخواهيم راه نماييم و هر آينه تو به راهى راست راه مى‌نمايى'),
(4325, 42, 53, 'راه آن خدايى كه او راست آنچه در آسمانها و آنچه در زمين است. آگاه باشيد كه همه كارها به خدا باز مى‌گردد.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حاميم.'),
(4327, 43, 2, 'سوگند به [اين‌] كتاب روشن و روشن‌كننده،'),
(4328, 43, 3, 'كه همانا ما آن را قرآنى به زبان تازى كرديم باشد كه شما به خرد دريابيد.'),
(4329, 43, 4, 'و هر آينه آن'),
(4330, 43, 5, 'آيا اين ذكر- يادآورى و پند قرآن- را از شما از آن رو كه مردمى گزافكاريد از روى اعراض باز داريم؟- نه، بلكه شما را بدان پند مى‌دهيم-.'),
(4331, 43, 6, 'و بسا پيامبران را در ميان پيشينيان فرستاديم'),
(4332, 43, 7, 'و هيچ پيامبرى بديشان نيامد مگر آنكه بدو استهزا مى‌كردند.'),
(4333, 43, 8, 'پس آنان را كه از اينان- كفار قريش- نيرومندتر بودند هلاك كرديم، و داستان پيشينيان گذشت- كه چه كردند و ما با آنها چه كرديم-.'),
(4334, 43, 9, 'و اگر از آنها بپرسى چه كسى آسمانها و زمين را آفريده است؟ هر آينه گويند آنها را آن بى‌همتا تواناى دانا آفريده است'),
(4335, 43, 10, 'همان كه زمين را آرامگاه شما كرد و برايتان در آن راه‌ها پديد آورد، باشد كه راه يابيد- به جاها و شهرهايى كه خواهيد، يا به شناخت حق تعالى-'),
(4336, 43, 11, 'و آن كه از آسمان آبى به اندازه فروفرستاد پس با آن زمينى مرده را زنده كرديم، همچنين شما [از گور] بيرون آورده شويد'),
(4337, 43, 12, 'و آن كه همه اصناف [آفريدگان‌]- يا جفتها- را آفريد و براى شما آنچه سوار مى‌شويد از كشتيها و چارپايان پديد كرد،'),
(4338, 43, 13, 'تا بر پشت آنها قرار گيريد آنگاه نعمت پروردگارتان را چون بر آنها برنشينيد ياد كنيد و گوييد: پاك و منزه است آن كه اين را رام ما كرد و ما خود بر آن توانا نبوديم،'),
(4339, 43, 14, 'و ما به سوى پروردگارمان بازمى‌گرديم.'),
(4340, 43, 15, 'و او'),
(4341, 43, 16, 'آيا از آفريدگان خود دختران را برگرفت و شما را به پسران ويژه ساخت؟!'),
(4342, 43, 17, 'و [حال آنكه‌] چون يكى از آنان را به آنچه براى خداى رحمان مَثَل آورد- يعنى به تولد دختر كه براى خود نپسنديد و براى خدا پسنديد- مژده دهند رويش سياه گردد در حالى كه پُر از خشم و اندوه باشد.'),
(4343, 43, 18, 'آيا آن كه در پيرايه و زيور پرورش مى‌يابد و در مجادله و حجت‌آوردن بيان روشن و رسا ندارد- يعنى دختر- [شايسته فرزندى خداست‌]؟!'),
(4344, 43, 19, 'و فرشتگان را كه بندگان خداى رحمان‌اند دختران نام نهادند. آيا به هنگام آفرينش ايشان حاضر و گواه بوده‌اند- تا بدانند كه از كدام جنس‌اند-؟! زودا كه گواهيشان نوشته شود و از آنان پرسش و بازخواست خواهد شد.'),
(4345, 43, 20, 'و گفتند: اگر خداى رحمان مى‌خواست آنها را نمى‌پرستيديم. آنان را بدان [سخن‌] هيچ دانشى نيست اينان جز دروغ و گزاف نمى‌گويند.'),
(4346, 43, 21, 'يا مگر [در اين باره‌] آنان را پيش از اين [قرآن‌] كتابى داده‌ايم كه بدان چنگ زده‌اند- به آن احتجاج مى‌كنند-؟!'),
(4347, 43, 22, '[نه،] بلكه گفتند: ما پدرانمان را بر آيينى يافته‌ايم و ما بر پى ايشان رهيافته‌ايم.'),
(4348, 43, 23, 'و همچنين پيش از تو در هيچ آبادى و شهرى هيچ بيم‌كننده‌اى نفرستاديم مگر آنكه توانگران و كامرانانش گفتند كه ما پدران خود را بر آيينى يافته‌ايم و همانا ما بر پى ايشان مى‌رويم.'),
(4349, 43, 24, '[آن بيم‌كننده‌] گفت: آيا هر چند شما را رهنمون‌تر از آنچه پدرانتان را بر آن يافته‌ايد بياورم [باز هم بر پيروى از آنها باقى مى‌مانيد]؟ گفتند: ما بدانچه شما به آن فرستاده شده‌ايد كافريم.'),
(4350, 43, 25, 'پس، از آنها كين ستانديم، و بنگر كه سرانجام تكذيب‌كنندگان چگونه بود!'),
(4351, 43, 26, 'و [ياد كن‌] آنگاه كه ابراهيم به پدر و قوم خود گفت: من از آنچه مى‌پرستيد بيزارم،'),
(4352, 43, 27, 'مگر آن [خداى‌] كه مرا آفريده كه او مرا راهنمايى خواهد كرد.'),
(4353, 43, 28, 'و آن- يعنى كلمه توحيد- را در فرزندان خود سخنى پاينده كرد تا شايد [قومش و ديگر مشركان از شرك به دين توحيد و خدا پرستى‌] باز گردند.'),
(4354, 43, 29, 'بلكه اينان- كفار مكه- و پدرانشان را برخوردار كردم تا حق- قرآن- و فرستاده‌اى آشكار و روشنگر- محمد'),
(4355, 43, 30, 'و آنگاه كه حق بديشان آمد گفتند: اين جادوست و ما به آن كافريم.'),
(4356, 43, 31, 'و گفتند: چرا اين قرآن بر مردى بزرگ- از جهت مال و جاه- از اين دو شهر- مكّه و طائف- فروفرستاده نشده است؟'),
(4357, 43, 32, 'آيا آنها رحمت پروردگار تو- نبوت- را بخش مى‌كنند؟ ماييم كه ميان آنان مايه گذرانشان را در زندگانى دنيا بخش كرده‌ايم، و پايه‌هاى برخى را بر برخى برتر داشته‌ايم- در روزى و جاه- تا برخى برخ ديگر را به خدمت گيرند و بخشايش پروردگار تو از آنچه گرد مى‌آورند بهتر است.'),
(4358, 43, 33, 'و اگر نه آن بود كه مردمان همه يك امت مى‌گشتند- همه دنياجو مى‌شدند يا به كفر مى گراييدند- هر آينه كسانى را كه به خداى رحمان كافر مى‌شوند براى خانه‌هاشان سقفهايى از نقره مى‌ساختيم و نيز نردبانهايى [از نقره‌] كه بر آنها بالا روند.'),
(4359, 43, 34, 'و نيز براى خانه‌هاشان درها و تختهايى- از زر يا سيم- [مى‌ساختيم‌] كه بر آنها تكيه زنند،'),
(4360, 43, 35, 'و نيز زيورهايى [زرين‌]. و اين همه نيست مگر كالا و برخوردارى زندگانى اين جهان، و آن جهان نزد پروردگار تو ويژه پرهيزگاران است.'),
(4361, 43, 36, 'و هر كه از يادكرد خداى رحمان روى برتابد- يا: چشم بپوشد و بگردد-، شيطانى براى او برانگيزيم- برگماريم- كه او را دمساز و همنشين باشد.'),
(4362, 43, 37, 'و هر آينه آنها'),
(4363, 43, 38, 'تا آنگاه كه [يكى از آنان‌] نزد ما آيد- در روز رستاخيز-، [به شيطان همدمش‌] گويد: اى كاش ميان من و تو چندان دورى بود كه ميان خاور و باختر، و بد همنشينى هستى.'),
(4364, 43, 39, 'و امروز گردآمدنتان در عذاب، شما را سود ندهد چون [بر خود] ستم كرديد.'),
(4365, 43, 40, 'پس مگر تو مى‌توانى كران را بشنوانى يا كوران- كوردلان- و آن كس را كه در گمراهى آشكار است راه بنمايى؟'),
(4366, 43, 41, 'پس اگر تو را ببريم- بميرانيم-، همانا از آنان كين مى‌ستانيم'),
(4367, 43, 42, 'يا آنچه را [از عذاب‌] به ايشان وعده كرده‌ايم به تو مى‌نماييم، كه ما بر آنان تواناييم.'),
(4368, 43, 43, 'پس بدانچه به تو وحى شده است چنگ زن، همانا تو بر راه راستى.'),
(4369, 43, 44, 'و هر آينه اين [قرآن‌] براى تو و قوم تو يادى [از خداوند] است- يا: يادگارى از عزت و شرف است-، و زودا كه پرسيده شويد- از قرآن و سپاسدارى از اين نعمت-.'),
(4370, 43, 45, 'و از پيامبران ما كه پيش از تو فرستاديم بپرس: آيا جز خداى رحمان خدايانى قرار داده‌ايم كه پرستيده شوند؟- يعنى همه پيامبران بر توحيد متفق و همصدا بوده‌اند-.'),
(4371, 43, 46, 'و هر آينه موسى را با نشانه‌هاى خويش به سوى فرعون و مهتران او فرستاديم گفت: همانا من فرستاده پروردگار جهانيانم.'),
(4372, 43, 47, 'پس چون با نشانه‌هاى ما بديشان آمد- يا نشانه‌هاى ما را بديشان آورد- هماندم از آن نشانه‌ها خنده سردادند.'),
(4373, 43, 48, 'و آنان را هيچ نشانه‌اى- معجزه‌اى- ننموديم مگر آنكه بزرگتر از نشانه ديگر بود. و به عذاب گرفتارشان كرديم تا شايد باز گردند.'),
(4374, 43, 49, 'و گفتند: اى جادوگر، پروردگار خويش را به آن عهدى كه با تو كرده است براى ما بخوان [كه اين عذاب را از ما بردارد] كه ما راهيافته‌ايم.'),
(4375, 43, 50, 'و چون عذاب را از آنها برداشتيم هماندم پيمان شكستند.'),
(4376, 43, 51, 'و فرعون در ميان قوم خود بانگ برآورد و گفت: اى قوم من، آيا پادشاهى مصر و اين جويها كه از زير [كوشك‌] من روانند از آن من نيستند؟ آيا [عظمت و حشمت مرا] نمى‌بينيد؟'),
(4377, 43, 52, 'بلكه من بهترم از اين [مرد] كه خوار و زبون است و نمى‌تواند سخن را روشن بيان كند.'),
(4378, 43, 53, '[اگر حق است و شايسته رياست‌] پس چرا دستبندهاى زرّين بر او افكنده نشده؟ يا چرا فرشتگان همراه وى [براى تصديق رسالتش‌] نيامده‌اند؟'),
(4379, 43, 54, 'پس قوم خود را [بدين نيرنگ‌] به نابخردى گرفت، و آنان هم از او فرمان بردند، زيرا كه مردمى بدكار و بيرون از فرمان خداى بودند.'),
(4380, 43, 55, 'پس چون ما را به خشم آوردند از آنان كين ستانديم و همگى را غرق كرديم.'),
(4381, 43, 56, 'پس آنان را گذشتگانى- پيشروانى براى كافران آينده- و پندى و عبرتى براى پسينيان كرديم.'),
(4382, 43, 57, 'و چون پسر مريم مثَل آورده شد- كه تولد او مانند آدم'),
(4383, 43, 58, 'و گفتند: خدايان ما بهترند يا او؟ اين [مثَل‌] را براى تو نزدند مگر از روى ستيزه و جدل، بلكه آنان مردمى ستيزه گرند.'),
(4384, 43, 59, 'او'),
(4385, 43, 60, 'و اگر مى‌خواستيم هر آينه به جاى شما فرشتگانى پديد مى‌كرديم كه در زمين جانشين [شما] باشند.'),
(4386, 43, 61, 'و هر آينه او'),
(4387, 43, 62, 'و شيطان شما را [از راه راست‌] باز ندارد و مگرداند، كه او دشمن آشكار شماست.'),
(4388, 43, 63, 'و چون عيسى با نشانه‌ها و دلايل روشن آمد، گفت: همانا براى شما حكمت- سخن درست و استوار در عقايد و اخلاق- آورده‌ام [تا بدان راه يابيد] و تا برخى از آنچه را كه در باره آن اختلاف مى‌كنيد برايتان بيان كنم. پس، از خداى پروا كنيد و مرا فرمان بريد.'),
(4389, 43, 64, 'همانا خداى يكتا پروردگار من و پروردگار شماست، پس او را بپرستيد، اين است راه راست.'),
(4390, 43, 65, 'اما آن گروه‌ها- ترسايان- ميان خود [در باره او] اختلاف كردند، پس واى بر ستمكاران از عذاب روزى دردناك.'),
(4391, 43, 66, 'آيا جز رستاخيز را چشم براه‌اند كه ناگهان بديشان آيد در حالى كه آگاه نباشند؟'),
(4392, 43, 67, 'دوستان- كه دوستيشان در راه كفر و گناه است- در آن روز برخى دشمن برخ ديگرند، مگر پرهيزگاران- كه دوستى ايشان براى خدا و در راه خداست-.'),
(4393, 43, 68, 'اى بندگان من، امروز نه بيمى بر شماست و نه اندوهگين مى‌شويد'),
(4394, 43, 69, 'آنان كه به آيات ما ايمان آوردند و مسلمان بودند'),
(4395, 43, 70, '[به آنان گفته شود:] شما و همسرانتان به بهشت در آييد شادان و خرمان- گرامى‌داشتگان-.'),
(4396, 43, 71, 'كاسه‌هايى از زر- پُر از طعام- و كوزه‌هايى- از شراب- بر آنان بگردانند، و در آن'),
(4397, 43, 72, 'و اين است بهشتى كه به پاداش آنچه مى‌كرديد به ميراث مى‌بريد.'),
(4398, 43, 73, 'شما را در آنجا ميوه بسيار هست كه از آن مى‌خوريد.'),
(4399, 43, 74, 'همانا بزهكاران در عذاب دوزخ جاويدانند'),
(4400, 43, 75, '[عذاب‌] از آنان سبك نشود و آنها در آن نوميد باشند- از بيرون‌شدن از آتش-'),
(4401, 43, 76, 'و ما به آنان ستم نكرديم و ليكن آنان خود ستمكار بودند'),
(4402, 43, 77, 'و [چون اميد رهايى ندارند] آواز دهند كه اى مالك- نگهبان آتش دوزخ-، بايد كه پروردگارت ما را بميراند گويد: همانا شما درنگ‌كننده و ماندنى هستيد'),
(4403, 43, 78, '[و خدا فرمايد:] هر آينه حق را براى شما آورديم و ليكن بيشترتان حق را ناخوش مى‌داشتيد.'),
(4404, 43, 79, 'بلكه [كافران‌] كارى استوار- در ردّ حق- ساختند، پس ما هم [براى كيفر آنها] كارى استوار مى‌سازيم.'),
(4405, 43, 80, 'يا مگر مى‌پندارند كه ما انديشه نهانى و رازگويى آنان با يكديگر را نمى‌شنويم؟ چرا، [مى‌شنويم‌]، و فرستادگان ما- فرشتگان نويسنده اعمال- در نزد آنها مى‌نويسند.'),
(4406, 43, 81, 'بگو: اگر خداى رحمان را فرزندى مى‌بود، پس من نخستين پرستنده [وى‌] بودم.'),
(4407, 43, 82, 'پاك است پروردگار آسمانها و زمين، پروردگار عرش، از آنچه [او را به ناشايست‌] وصف مى‌كنند.'),
(4408, 43, 83, 'پس واگذارشان تا بيهوده گفت‌وگو كنند و به بازى سرگرم باشند تا آن روزشان را كه وعده داده مى‌شوند ديدار كنند.'),
(4409, 43, 84, 'و اوست آن كه در آسمان خداست و در زمين خداست و اوست با حكمت و دانا.'),
(4410, 43, 85, 'و بزرگ و بزرگوار است آن [خداى‌] كه او راست پادشاهى آسمانها و زمين و آنچه ميان آنهاست، و دانش هنگام رستاخيز نزد اوست و به سوى او بازگردانده مى‌شويد.'),
(4411, 43, 86, 'و آنان كه جز خداى را مى‌خوانند- مى‌پرستند- ياراى شفاعت‌كردن ندارند مگر كسانى كه به حق- توحيد- گواهى داده باشند و بدانند- كه از چه كسى شفاعت كنند، و شفاعت نخواهند كرد مگر از مؤمنان گناهكار-.'),
(4412, 43, 87, 'و اگر از آنان بپرسى چه كسى آنان را آفريده است هر آينه گويند: خدا. پس كجا و چگونه [از پرستش خدا به پرستش غير او] گردانيده مى‌شوند؟'),
(4413, 43, 88, 'و [نزد اوست علم به‌] گفتار او- پيامبر'),
(4414, 43, 89, 'پس، از آنان روى بگردان و سلام گو زودا كه بدانند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حاميم.'),
(4416, 44, 2, 'سوگند به اين كتاب روشن و روشنگر.'),
(4417, 44, 3, 'همانا آن را در شبى با بركت فرو فرستاديم كه ما [هماره‌] بيم‌كننده بوده‌ايم.'),
(4418, 44, 4, 'در آن شب هر كار استوارى جدا كرده و بازنموده شود- از اجمال به تفصيل درمى‌آيد-،'),
(4419, 44, 5, 'فرمانى از نزد ماست كه ما [هماره‌] فرستنده [پيامبران‌] بوده‌ايم'),
(4420, 44, 6, 'از روى بخشايشى از پروردگارت، كه اوست شنوا و دانا'),
(4421, 44, 7, 'همان پروردگار آسمانها و زمين و آنچه ميان آنهاست، اگر بى‌گمان باور داريد.'),
(4422, 44, 8, 'هيچ خدايى جز او نيست، زنده مى‌كند و مى‌ميراند، پروردگار شما و پروردگار پدران پيشين شماست.'),
(4423, 44, 9, '[كافران اين را باور ندارند،] بلكه آنها در شك و ترديد سرگرم بازى‌اند.'),
(4424, 44, 10, 'پس روزى را چشم مى‌دار كه آسمان دودى آشكار بياورد،'),
(4425, 44, 11, 'كه مردم را فروگيرد [گويند:] اين است عذاب دردناك'),
(4426, 44, 12, 'پروردگارا، اين عذاب را از ما بردار كه ما ايمان آرنده‌ايم.'),
(4427, 44, 13, 'كجا و چگونه آنان را پندگرفتن باشد؟ و حال آنكه ايشان را پيامبرى آشكار و روشنگر آمد،'),
(4428, 44, 14, 'آنگاه از او روى گرداندند و گفتند: آموخته‌اى است ديوانه.'),
(4429, 44, 15, '[اينك‌] ما عذاب را اندكى- زمانى اندك- برمى‌داريم، [ولى‌] شما [به كفر] بازمى‌گرديد.'),
(4430, 44, 16, 'روزى كه [كافران را] سخت فروگيريم، كه ما كين‌ستانيم.'),
(4431, 44, 17, 'و هر آينه پيش از آنان قوم فرعون را آزموديم، و ايشان را پيامبرى بزرگوار آمد،'),
(4432, 44, 18, '[به اين‌] كه بندگان خدا- بنى اسرائيل- را به من سپاريد، كه من شما را پيامبرى امينم،'),
(4433, 44, 19, 'و بر خدا سركشى و گردنفرازى مكنيد، كه من شما را حجتى روشن- همچون عصا و يد بيضا- آورده‌ام.'),
(4434, 44, 20, 'و همانا به پروردگار خود و پروردگار شما پناه مى‌برم از اينكه سنگسارم كنيد- يا دشنامم دهيد-.'),
(4435, 44, 21, 'و اگر به من نمى‌گرويد، از من كناره گيريد- مرا ميازاريد يا مانع گرويدن مردم مشويد-.'),
(4436, 44, 22, 'آنگاه پروردگار خويش را بخواند كه اينها قومى بزهكارند.'),
(4437, 44, 23, 'پس [به او گفتيم:] بندگان من- يعنى بنى اسرائيل- را شب هنگام بيرون بَر همانا [فرعون و پيروان او] از پى شما خواهند آمد.'),
(4438, 44, 24, 'و دريا را آرام- ساكن و راهى خشك- پشت سر بگذار همانا آنها- قوم فرعون- لشكرى غرق‌شدنى‌اند.'),
(4439, 44, 25, 'چه بسيار باغها و چشمه‌ها كه بگذاشتند،'),
(4440, 44, 26, 'و كشتزارها و جايهاى نيكو و آراسته،'),
(4441, 44, 27, 'و نعمتها و آسايشى كه در آن برخوردار بودند.'),
(4442, 44, 28, 'اينچنين [بود سرگذشتشان‌]، و آنها را به قومى ديگر- بنى اسرائيل- ميراث داديم.'),
(4443, 44, 29, 'پس آسمان و زمين بر آنان نگريست، و مهلت هم نيافتند.'),
(4444, 44, 30, 'و هر آينه فرزندان اسرائيل را از عذاب خواركننده رهانيديم،'),
(4445, 44, 31, 'از فرعون، كه او برترى‌جوى و سركشى از گزافكاران بود.'),
(4446, 44, 32, 'و هر آينه آنان را از روى دانش بر جهانيان- مردم زمانه- برگزيديم.'),
(4447, 44, 33, 'و ايشان را از نشانه‌ها آنچه در آن آزمايشى هويدا بود، بداديم.'),
(4448, 44, 34, 'هر آينه اينان- كافران مكه- مى گويند:'),
(4449, 44, 35, 'آن- سرانجام كار- جز همين مرگ نخستين ما نيست و ما [از گور] برانگيخته نمى‌شويم'),
(4450, 44, 36, 'پس اگر راست مى‌گوييد پدران ما را بازآريد.'),
(4451, 44, 37, 'آيا اينها بهترند يا قوم تُبّع و كسانى كه پيش از آنان بودند- مانند قوم عاد و ثمود و فرعونيان-؟ همه را هلاك كرديم، زيرا كه آنها بزهكار بودند.'),
(4452, 44, 38, 'و ما آسمانها و زمين و آنچه را ميان آنهاست به بازى نيافريديم'),
(4453, 44, 39, 'آن دو را جز بحق نيافريديم، ولى بيشترشان نمى‌دانند.'),
(4454, 44, 40, 'همانا روز جدايى- جدايى ميان حق و باطل و مؤمن و كافر و نيكوكار و بدكار، يا روز داورى قيامت- وعده گاه همه آنان است.'),
(4455, 44, 41, 'روزى كه هيچ دوستى براى دوست خود سودى ندارد و به كار نيايد- و عذاب را از او دفع نكند-، و نه آنها يارى شوند،'),
(4456, 44, 42, 'مگر كسى كه خداى بر او رحمت آرد، كه اوست تواناى بى‌همتا و مهربان.'),
(4457, 44, 43, 'همانا درخت زقوم،'),
(4458, 44, 44, 'خوردنى بزه‌مند است،'),
(4459, 44, 45, 'مانند مس و روى گداخته است كه در شكمها مى‌جوشد،'),
(4460, 44, 46, 'همچون جوشيدن آب جوشان.'),
(4461, 44, 47, '[فرشتگان را ندا آيد:] بگيريدش و به سختى و خوارى به ميان دوزخش بكشيد،'),
(4462, 44, 48, 'سپس بر فراز سرش از عذاب آب جوشان فرو ريزيد،'),
(4463, 44, 49, 'بچش، كه تو همان توانمند بزرگوارى'),
(4464, 44, 50, 'اين همان است كه به آن شك مى‌كرديد.'),
(4465, 44, 51, 'همانا پرهيزگاران در جايگاهى امن و بى‌بيم باشند،'),
(4466, 44, 52, 'در بوستانها و چشمه سارها،'),
(4467, 44, 53, 'از جامه‌هاى پرنيان نازك و ستبر مى‌پوشند، روياروى هم [مى‌نشينند].'),
(4468, 44, 54, 'اينچنين [است حالشان‌]، و ايشان را حوران فراخ چشم به همسرى دهيم.'),
(4469, 44, 55, 'در آنجا هر ميوه‌اى بخواهند فراخوانند، در ايمنى [از زيان و آوال آن‌].'),
(4470, 44, 56, 'آنجا مرگ را نچشند مگر همان مرگ نخستين- كه در دنيا چشيده‌اند- و [خداوند] ايشان را از عذاب دوزخ نگاه داشته است،'),
(4471, 44, 57, 'از روى بخششى از پروردگار تو، اين است پيروزى و كاميابى بزرگ.'),
(4472, 44, 58, 'پس جز اين نيست كه ما آن قرآن را به زبان تو آسان ساختيم، باشد كه ياد كنند و پند گيرند.'),
(4473, 44, 59, 'پس منتظر باش كه آنان نيز منتظرند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حاميم.'),
(4475, 45, 2, 'فرو فرستادن اين كتاب از نزد خداى تواناى بى‌همتا و داناى با حكمت است.'),
(4476, 45, 3, 'هر آينه در آسمانها و زمين براى مؤمنان نشانه‌هاست- براى استدلال بر حكمت خداوند-.'),
(4477, 45, 4, 'و در آفرينش شما و آنچه از جنبندگان [در زمين‌] مى‌پراكند براى مردمى كه بى گمان باور دارند نشانه هاست.'),
(4478, 45, 5, 'و در آمد و شد شب و روز- و افزونى و كاستى آنها- و روزيى- يعنى بارانى- كه خداى از آسمان- ابر- فروآورده و زمين را به آن پس از مردگى‌اش زنده كرده و در وزيدن و گردانيدن بادها نشانه هاست براى مردمى كه خرد را كار بندند.'),
(4479, 45, 6, 'اينها آيات خداست كه بر تو به راستى و درستى مى‌خوانيم، پس به كدام سخن پس از [سخن‌] خداى و آيات او ايمان مى‌آورند؟'),
(4480, 45, 7, 'واى بر هر دروغزن گنه پيشه‌اى'),
(4481, 45, 8, 'كه آيات خداى را كه بر او خوانده مى‌شود مى شنود، آنگاه به گردنكشى و بزرگ منشى [بر كفر و عناد] اصرار مى‌ورزد، گويى كه هرگز آن را نشنيده است، پس او را به عذابى دردناك مژده ده.'),
(4482, 45, 9, 'و چون چيزى از آيات ما بداند به ريشخندش مى‌گيرد، اينانند كه عذابى خوار كننده دارند.'),
(4483, 45, 10, 'فرا روى ايشان دوزخ است، و آنچه به دست آورده‌اند- از اموال- و آنچه به جاى خدا دوستان گرفته‌اند آنان را هيچ سودى ندارد و به كار نيايد و ايشان را عذابى است بزرگ.'),
(4484, 45, 11, 'اين [قرآن‌] رهنمونى است، و كسانى كه به آيات پروردگارشان كفر ورزيده‌اند آنان را عذابى است از گونه عذابى سخت دردناك.'),
(4485, 45, 12, 'خداست آن كه دريا را براى شما رام كرد تا كشتيها در آن به فرمان او روان شوند و تا از بخشش او- روزى- بجوييد و باشد كه سپاس گزاريد،'),
(4486, 45, 13, 'و آنچه در آسمانها و آنچه در زمين است همه را كه از سوى اوست براى شما رام ساخت. هر آينه در اين [كارها] نشانه هاست براى مردمى كه مى انديشند.'),
(4487, 45, 14, 'به كسانى كه ايمان آورده‌اند، بگو: از كسانى كه روزهاى [سخت و ناگوار] خداى را باور و اميد ندارند در گذرند تا [خدا خود] گروهى را بدانچه مى كردند سزا دهد.'),
(4488, 45, 15, 'هر كه كارى نيك و شايسته كند به سود خود اوست، و هر كه كار بد و ناشايست كند به زيان اوست، سپس به سوى پروردگارتان باز گردانده مى شويد.'),
(4489, 45, 16, 'و هر آينه فرزندان اسرائيل را كتاب- تورات- و حكم- حكمت يا فرمانروايى- و پيامبرى داديم و ايشان را از چيزهاى پاكيزه روزى كرديم، و آنان را بر جهانيان- مردم زمانه- برترى نهاديم.'),
(4490, 45, 17, 'و ايشان را نشانه‌ها و دلايل روشن از كار [دين خود و رسالت محمد ص‌] بداديم، پس اختلاف نكردند- در احكام تورات يا در باره پيامبر [ص‌]- مگر پس از آنكه دانش بديشان رسيد، از روى ستم و بدخواهى ميان خود. همانا پروردگار تو ميان آنان در روز رستاخيز در باره آنچه در آن اختلاف مى‌كردند داورى خواهد كرد.'),
(4491, 45, 18, 'سپس تو را بر راه و روشى از كار [دين و شريعت‌] نهاديم، پس آن را پيروى كن و از هوسها و خواهشهاى دل كسانى كه نمى‌دانند پيروى مكن.'),
(4492, 45, 19, 'همانا آنان تو را از خدا- در برابر خدا- هيچ سودى ندارند و به كار نيايند، و ستمكاران برخى دوستان برخى ديگرند و خدا دوست و كارساز پرهيزگاران است.'),
(4493, 45, 20, 'اين- دين يا قرآن- بينش هايى است براى مردم، و رهنمونى و بخشايشى است براى گروهى كه بى‌گمان باور دارند.'),
(4494, 45, 21, 'يا مگر كسانى كه به كارهاى بد دست يازيدند مى‌پندارند كه آنان را همانند كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند قرار مى‌دهيم كه زندگانى و مرگشان يكسان و برابر باشد؟ بد داورى مى‌كنند.'),
(4495, 45, 22, 'و خدا آسمانها و زمين را به راستى و درستى آفريد [تا نشانه‌هاى قدرت و خدايى او آشكار شود] و تا هر كسى بدانچه كرده است پاداش يابد و به آنان ستم نمى‌شود.'),
(4496, 45, 23, 'آيا ديدى كسى را كه خداى خود را خواهش و هوس دل خويش گرفت و خدا از روى دانش گمراهش كرد و بر گوش و دلش مهر نهاد و بر چشمش پرده افكند؟ پس كيست كه او را پس از [فرو گذاشتن‌] خدا راه نمايد؟ آيا پند نمى‌گيريد؟'),
(4497, 45, 24, 'و [كافران‌] گفتند: اين- يعنى زندگى- نيست مگر زندگى ما در اين جهان، مى‌ميريم و زنده مى شويم و ما را جز دهر- روزگار و زمانه- هلاك نكند، و آنان را هيچ دانشى به آن نيست، آنان جز در گمان و پندار نيستند.'),
(4498, 45, 25, 'و چون آيات روشن ما- در باره زنده شدن مردگان به هنگام رستاخيز- بر آنان خوانده شود حجت ايشان جز اين نباشد كه گويند: پدران ما را بياريد- زنده كنيد- اگر راستگوييد.'),
(4499, 45, 26, 'بگو: خدا شما را زنده مى‌كند و باز مى‌ميراند و سپس شما را به روز رستاخيز كه شكى در آن نيست فراهم مى‌آورد، ولى بيشتر مردم نمى دانند.'),
(4500, 45, 27, 'و خداى راست پادشاهى آسمانها و زمين، و روزى كه رستاخيز بر پا شود در آن روز باطل گرايان- حق ناباوران و كجروان- زيان مى‌كنند.'),
(4501, 45, 28, 'و هر گروهى را به زانو در آمده- نيم خيز آماده برخاستن از هول آن روز- بينى. هر گروهى را به نامه [كردار] ش فراخوانند. امروز شما را پاداش آنچه مى‌كرديد بدهند.'),
(4502, 45, 29, 'اين است نامه ما كه بر شما- به زيان و بر ضد شما- به راستى سخن مى‌گويد. همانا ما از كارهايى كه مى‌كرديد رو نوشت بر مى‌داشتيم.'),
(4503, 45, 30, 'اما كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند، پس پروردگارشان ايشان را در رحمت خود- در بهشت- درآرد. اين است كاميابى و پيروزى بزرگ.'),
(4504, 45, 31, 'و اما كسانى كه كافر شدند [به آنها گويند:] آيا نه اين بود كه آيات من بر شما خوانده مى‌شد پس شما گردنكشى كرديد و مردمى‌بزهكار بوديد؟'),
(4505, 45, 32, 'و هنگامى كه گفته مى‌شد: همانا وعده خدا راست است و رستاخيز شكى در آن نيست، مى‌گفتيد: ما نمى‌دانيم رستاخيز چيست. ما جز پندارى گمان نبريم، و ما [بدان‌] باور بى‌گمان نداريم.'),
(4506, 45, 33, 'و آنان را [در آن روز] كارهاى بدى كه مى‌كردند پديدار شود و آنچه بدان استهزا مى‌كردند آنان را فروگيرد.'),
(4507, 45, 34, 'و گفته شود: امروز شما را فراموش مى‌كنيم- فرو مى‌گذاريم- چنانكه شما ديدار اين روزتان را فراموش كرديد، و جاى شما دوزخ است و شما را يارى كننده‌اى نيست.'),
(4508, 45, 35, 'اين [عذاب‌] به سبب آن است كه شما آيات خدا را به مسخره گرفتيد و زندگى دنيا شما را بفريفت، پس امروز از آن'),
(4509, 45, 36, 'پس سپاس و ستايش خداى راست، خداوند آسمانها و زمين، پروردگار جهانيان.'),
(4510, 45, 37, 'و او راست بزرگى در آسمانها و زمين و اوست تواناى بى‌همتا و داناى با حكمت.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حاميم.'),
(4512, 46, 2, 'فروفرستادن اين كتاب از سوى خداى تواناى بى‌همتا و داناى با حكمت است.'),
(4513, 46, 3, 'ما آسمانها و زمين و آنچه را ميان آنهاست نيافريديم مگر بحق و [تا] سرآمدى نامبرده، و كسانى كه كافر شدند از آنچه بيم‌داده شدند رويگردانند.'),
(4514, 46, 4, 'بگو: مرا گوييد كه آنچه جز خدا مى‌خوانيد به من بنماييد كه چه چيز از اين زمين- يا كدام بخش زمين- را آفريده‌اند يا مگر در [آفرينش‌] آسمانها شركت داشته‌اند؟! اگر راستگوييد، براى من [دليلى از] كتابى كه پيش از اين [قرآن‌] آمده باشد يا بازمانده‌اى از دانش [پيشينيان‌] بياريد.'),
(4515, 46, 5, 'و كيست گمراه‌تر از كسى كه به جاى خدا كسى- يا چيزى- را مى‌خواند كه تا روز رستاخيز او را پاسخ نمى‌دهد و آنها از خواندن اينان بى‌خبرند؟'),
(4516, 46, 6, 'و چون مردم [در رستاخيز] برانگيخته و فراهم شوند، [پرستيدگان‌] دشمن آنان- پرستندگان- باشند و پرستش ايشان را انكار كنند.'),
(4517, 46, 7, 'و چون آيات روشن ما بر آنان خوانده شود، كافران [سخن‌] حق- قرآن- را آنگاه كه بديشان آيد، گويند: اين جادويى است آشكار.'),
(4518, 46, 8, 'بلكه مى‌گويند كه آن'),
(4519, 46, 9, 'بگو: من نوآمده‌اى از پيامبران نيستم- نخستين پيامبرى نيستم كه مبعوث شده‌ام پيش از من پيامبران بوده‌اند-، و نمى‌دانم كه با من و با شما چه خواهند كرد. پيروى نمى‌كنم مگر آنچه را كه به من وحى مى‌شود، و من جز بيم‌دهنده‌اى آشكار نيستم.'),
(4520, 46, 10, 'بگو: مرا گوييد كه اگر آن'),
(4521, 46, 11, 'و كافران در باره مؤمنان گفتند: اگر [دين مسلمانى‌] بهتر بود [اينان‌] به سوى آن بر ما پيشى نمى‌گرفتند- بلكه ما پيشى مى‌گرفتيم- و چون به آن راه نيافته‌اند خواهند گفت كه اين دروغى است كهن.'),
(4522, 46, 12, 'و حال آنكه پيش از آن، كتاب موسى راهنما و رحمت بود، و اين كتاب است باوردارنده [آن‌] به زبان تازى تا كسانى را كه ستم كردند بيم دهد و نيكوكاران را نويد باشد.'),
(4523, 46, 13, 'همانا كسانى كه گفتند: پروردگار ما خداى يكتاست و سپس [بر آن‌] استوار و پايدار ماندند پس نه بيمى بر آنهاست و نه اندوهگين شوند.'),
(4524, 46, 14, 'اينان اهل بهشت‌اند كه در آن جاودانه‌اند، به پاداش آنچه مى‌كردند.'),
(4525, 46, 15, 'و آدمى را به نيكوكارى با پدر و مادر خويش سفارش كرديم [بويژه مادر، زيرا كه‌] مادرش او را به دشوارى بار برداشت و به دشوارى بنهاد، و بار برداشتن و از شير گرفتن او سى‌ماه شد. تا آنگاه كه به نيروى جوانى خود رسيد و به چهل سالگى رسيد، گفت: پروردگارا، بر دلم افكن- مرا الهام كن و توفيق ده- تا نعمت تو را كه بر من و بر پدر و مادرم ارزانى داشتى سپاس گزارم و تا كارى نيك و شايسته كنم كه بپسندى، و براى من در فرزندانم شايستگى پديد آر، همانا من سوى تو بازگشتم و همانا من از مسلمانانم- از گردن‌نهادگان و فرمانبردارانم-.'),
(4526, 46, 16, 'اينانند كه از ايشان نيكوترين آنچه را كرده‌اند مى‌پذيريم و از بديهاشان درمى‌گذريم، در زمره بهشتيان، وعده راستى است كه به آنها داده مى‌شد.'),
(4527, 46, 17, 'و آن كه به پدر و مادرش [آنگاه كه او را به ايمان به آخرت مى‌خواندند] گفت: اف بر شما، آيا به من وعده مى‌دهيد كه [از گور] بيرون آورده شوم و حال آنكه پيش از من مردمان بسيار بگذشته‌اند [و كسى باز نيامده است‌]؟! و آن دو از خدا فرياد مى‌خواستند [و به فرزند كافر خود مى‌گفتند:] واى بر تو، ايمان بيار، همانا وعده خدا- روز رستاخيز- راست است و او مى‌گفت: اين [وعده‌] جز افسانه‌هاى پيشينيان نيست'),
(4528, 46, 18, 'اينانند كه آن گفتار- وعده عذاب- بر آنان در زمره گروه‌هايى از پريان و آدميان كه پيش از آنها بگذشتند سزا گشت، همانا ايشان زيانكار بودند.'),
(4529, 46, 19, 'و هر كدام [از دو گروه مؤمن و كافر] را پايه‌هايى است از آنچه كرده‌اند [تا عدل خدا در باره آنها آشكار شود] و تا به آنان [پاداش‌] كارهايشان را تمام بدهد و به ايشان ستم نخواهد شد.'),
(4530, 46, 20, 'و روزى كه كافران را بر آتش عرضه كنند، [به آنان گفته شود:] خوشيها و لذتهاى خود را در زندگى دنياتان برديد و بدانها بهره‌مند شديد، پس امروز عذاب خوارى پاداش داده مى‌شويد به سزاى آنكه در زمين به ناروا گردنكشى مى‌نموديد و به كيفر آنكه از فرمان [حق‌] بيرون مى‌شديد.'),
(4531, 46, 21, 'و ياد كن برادر قوم عاد- هود- را آنگاه كه قوم خود را در سرزمين احقاف بيم مى‌داد- و پيش از او و پس از او بيم‌كنندگان بگذشتند- كه جز خداى را نپرستيد، كه من بر شما از عذاب روزى بزرگ مى‌ترسم.'),
(4532, 46, 22, 'گفتند: آيا نزد ما آمده‌اى تا ما را از خدايانمان بگردانى؟ پس آنچه را به ما وعده مى‌دهى بيار اگر راستگويى.'),
(4533, 46, 23, 'گفت: همانا دانش [اين كه عذاب كى خواهد بود] نزد خداست، و من آنچه را بدان فرستاده شده‌ام به شما مى‌رسانم، و ليكن شما را مردمى مى‌بينم كه نادانى مى‌كنيد.'),
(4534, 46, 24, 'پس چون آن [ابر عذاب‌] را ديدند كه روى به واديهاى آنها نهاده، گفتند: اين براى ما ابرى باران‌زاست. [نه،] بلكه اين همان [عذابى‌] است كه به شتاب مى‌خواستيد، بادى است كه در آن عذابى دردناك است'),
(4535, 46, 25, 'كه به فرمان پروردگارش هر چيز را [كه بر آن بگذرد] هلاك مى‌كند پس چنان گشتند كه بجز خانه‌هايشان ديده نمى‌شد. اينچنين مردم بزهكار را كيفر مى‌دهيم.'),
(4536, 46, 26, 'و هر آينه آنان'),
(4537, 46, 27, 'و هر آينه [مردم‌] آباديهايى را كه پيرامون شما- اهل مكه- بودند هلاك ساختيم، و آيات را گونه‌گون بيان كرديم شايد كه بازگردند.'),
(4538, 46, 28, 'پس چرا آنهايى- خدايانى- كه به جاى خدا براى تقرب [به خدا] به خدايى گرفتند آنان را يارى نكردند؟ بلكه از آنان گم شدند، و اين بود [سرانجام‌] دروغ و بافته‌هاى آنان.'),
(4539, 46, 29, 'و [ياد كن‌] آنگاه كه گروهى از پريان را به سوى تو روانه كرديم كه قرآن را مى‌شنيدند، و چون نزدش حاضر آمدند گفتند: خاموش باشيد و گوش فراداريد، و چون [خواندن قرآن‌] به پايان رسيد، براى بيم‌دادن به سوى قوم خود بازگشتند.'),
(4540, 46, 30, 'گفتند: اى قوم ما، همانا ما كتابى را شنيديم كه پس از موسى فرو فرستاده شده، باوردارنده كتابهايى است كه در پيش آن است، به حق و به راه راست راه مى‌نمايد'),
(4541, 46, 31, 'اى قوم ما، خواننده [به سوى‌] خدا- پيامبر- را اجابت كنيد و به او ايمان آوريد تا [خدا] برخى از گناهانتان را بيامرزد و شما را از عذابى دردناك پناه دهد'),
(4542, 46, 32, 'و هر كه خواننده خداى را اجابت نكند، پس [بداند كه‌] در زمين ناتوان‌كننده [خداى از عذاب‌كردن‌] نيست، و او را جز او'),
(4543, 46, 33, 'آيا نديده‌ايد- ندانسته‌اند- كه آن خداى كه آسمانها و زمين را آفريد و در آفريدن آنها ناتوان و وامانده نشد تواناست بر اينكه مردگان را زنده كند؟ آرى، او بر هر چيزى تواناست.'),
(4544, 46, 34, 'و روزى كه كافران را بر آتش دوزخ عرضه كنند [به آنها گويند:] آيا اين حق نيست- كه شما آن را انكار مى‌كرديد-؟ گويند: آرى، به پروردگارمان سوگند [كه حق است خداى‌] گويد: پس بچشيد عذاب را به سزاى آنكه كفر مى‌ورزيديد.'),
(4545, 46, 35, 'پس شكيبايى كن، همچنان كه پيامبران أولو العزم- خداوندان آهنگ استوار و جد و پايدارى، يا صاحبان شريعت و كتاب- شكيبايى كردند و براى آنها [عذاب را] به شتاب مخواه. آن روز كه آنچه را وعده داده مى‌شوند ببينند چنان است كه گويى جز ساعتى از يك روز- در دنيا- درنگ نكرده‌اند. [اين قرآن‌] رسانيدن پيام [خدا] است، پس آيا جز مردم بدكار نافرمان هلاك مى‌شوند؟');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'كسانى كه كافر شدند و [مردم را] از راه خدا باز داشتند [خدا] كردارشان را گم و تباه گردانيد.'),
(4547, 47, 2, 'و كسانى كه ايمان آوردند و كارهاى شايسته كردند و به آنچه بر محمد'),
(4548, 47, 3, 'اين از آن روست كه كسانى كه كافر شدند باطل را پيروى كردند و كسانى كه ايمان آوردند حق را كه از پروردگارشان است پيروى كردند. بدين سان خداوند براى مردم حالهاشان را بيان مى‌كند.'),
(4549, 47, 4, 'پس چون با كافران [در كارزار] روبرو شديد، گردنها [شان‌] را بزنيد تا آنگاه كه [با كشتنشان‌] بر آنها چيره شويد پس [اسير گيريد و] بند را استوار كنيد- تا نگريزند- آنگاه يا منّت نهيد [و آزادشان كنيد] و يا [آنها را] بازفروشيد- به مال يا معاوضه اسيران- تا جنگ بارهاى خود را بنهد- به پايان رسد-. اين است [فرمان خدا]، و اگر خدا مى‌خواست از آنان كين مى‌ستاند- بى‌آنكه شما را فرمان جنگ دهد- و ليكن خواست تا برخى‌تان را به برخى بيازمايد. و كسانى كه در راه خدا كشته شدند، كردارهاشان را هرگز گم و تباه نمى‌كند'),
(4550, 47, 5, 'زود باشد كه ايشان را راه نمايد و حال و كارشان را به سامان آورد'),
(4551, 47, 6, 'و به بهشتى درآوردشان كه آن را بديشان شناسانده است.'),
(4552, 47, 7, 'اى كسانى كه ايمان آورده‌ايد، اگر خداى را يارى كنيد- با يارى‌كردن دينش-، شما را يارى كند و گامهاى شما را استوار بدارد.'),
(4553, 47, 8, 'و كسانى كه كفر ورزيدند پس نگونسارى و هلاكت باد بر آنان و [خدا] كارهاشان را گم و تباه گردانيد.'),
(4554, 47, 9, 'اين [هلاكت و نگونسارى‌] از آن روست كه آنچه را خدا فروفرستاده است ناخوش داشتند، پس كردارهاشان را تباه و ناچيز ساخت.'),
(4555, 47, 10, 'آيا در زمين گردش نكردند تا بنگرند سرانجام كسانى كه پيش از آنان بودند چگونه بود؟ خدا آنها را نابود كرد و براى اين كافران نيز همانند آن است.'),
(4556, 47, 11, 'اين از آن روست كه خدا يار و ياور مؤمنان است و كافران را يار و ياورى نيست.'),
(4557, 47, 12, 'همانا خدا كسانى را كه ايمان آوردند و كارهاى نيك و شايسته كردند به بهشتهايى درآرد كه از زير آنها جويها روان است. و كسانى كه كافر شدند [از دنيا] كام و بهره مى‌گيرند و همانند چارپايان مى‌خورند و آتش دوزخ جايگاهشان است.'),
(4558, 47, 13, 'و بسا شهرها كه [مردمش‌] از [مردم‌] شهر تو كه بيرونت كردند نيرومندتر بودند، هلاكشان كرديم و هيچ ياورى نداشتند.'),
(4559, 47, 14, 'آيا كسى كه بر حجتى روشن از پروردگار خويش است مانند كسى است كه كردار بدش در نظرش آراسته شده، و آرزوها و خواهشهاى دل خويش را پيروى كرده‌اند؟'),
(4560, 47, 15, 'وصف آن بهشتى كه پرهيزگاران را وعده داده‌اند اين است كه در آن جويهايى است از آبى دگرگون‌نشدنى- نگندد و بوى و مزه آن بر نگردد- و جويهايى از شيرى كه مزه‌اش بر نگردد و جويهايى از باده‌اى كه لذت‌بخش آشامندگان است و جويهايى از عسل ناب و ايشان را در آن'),
(4561, 47, 16, 'و از آنان- منافقان- كسانى‌اند كه به [گفتار] تو گوش فرا مى‌دارند تا چون از نزد تو بيرون آيند به كسانى [از يارانت‌] كه دانش داده شده‌اند گويند: اكنون چه گفت؟- به ريشخند مى‌گفتند: ما نفهميديم چه گفت- اينانند كه خدا بر دلهاشان مهر نهاده، و هواهاى خويش را پيروى كرده‌اند.'),
(4562, 47, 17, 'و كسانى كه راه يافته‌اند، [خدا] ايشان را رهيابى بيفزايد و [مايه دوام و پاداش‌] پرهيزگاريشان را به آنان بدهد.'),
(4563, 47, 18, 'پس آيا جز رستاخيز را كه ناگهان بديشان آيد انتظار مى‌برند؟ همانا نشانه‌هاى آن آمده است. پس كجا و چگونه ايشان را يادكرد و پندگرفتنشان باشد آنگاه كه رستاخيز بديشان آيد؟'),
(4564, 47, 19, 'پس بدان كه هيچ خدايى جز خداى يكتا نيست، و براى گناه خود و براى مردان و زنان مؤمن آمرزش بخواه و خداوند رفت و آمد و آرميدن شما را مى‌داند- به تمام حالات شما آگاه است-.'),
(4565, 47, 20, 'و كسانى كه ايمان آورده‌اند گويند چرا سوره‌اى [براى جهاد] فروفرستاده نمى‌شود؟ پس چون سوره‌اى محكم- يعنى از محكمات كه معناى آن روشن است- فرستاده شود و در آن از كارزار سخن رفته باشد، كسانى را كه در دلهاشان بيمارى- شك و نفاق- است بينى كه به تو مى‌نگرند همچون نگريستن كسى كه بيهوشى مرگ به او رسيده باشد پس ايشان را همان [مرگ‌] سزاوارتر است- يا: پس واى بر آنان-.'),
(4566, 47, 21, '[كارشان به ظاهر] فرمانبردارى و گفتار نيكوست، پس چون كار [جنگ‌] استوار شود اگر با خدا راست گويند- در وفاى به كارزار- هر آينه برايشان بهتر است.'),
(4567, 47, 22, 'پس [اى منافقان‌] آيا [جز اين‌] انتظار مى‌داريد كه اگر سرپرست امور مردم شديد- به قدرت و حكومت رسيديد- در زمين تباهى كنيد و از خويشاوندانتان ببريد؟'),
(4568, 47, 23, 'اينانند كه خدا لعنتشان كرده، پس آنان را كر ساخته و ديدگانشان را كور كرده است.'),
(4569, 47, 24, 'آيا در قرآن نمى‌انديشند يا بر دلها [شان‌] قفلهاست؟'),
(4570, 47, 25, 'كسانى كه پس از آنكه راه راست برايشان روشن و آشكار شد، پشت كرده بازگشتند- مرتد و كافر شدند-، شيطان [باطل را] براى آنها بياراست و آنان را در آرزوهاى دراز نهاد'),
(4571, 47, 26, 'اين همه از آن روست كه آنان به كسانى كه آنچه را خداى فرو فرستاده ناخوش داشتند گفتند: ما در برخى از كارها شما را فرمان خواهيم برد، و خدا نهان‌داشتن آنها- پنهان سخن‌گفتن و رازهاى آنان- را مى‌داند.'),
(4572, 47, 27, 'پس چون‌اند [آن منافقان‌] آنگاه كه فرشتگان جانشان را مى‌گيرند بر رويها و پشتهاشان مى‌زنند؟!'),
(4573, 47, 28, 'اين [عذاب‌] از آن روست كه آنان از آنچه خداى را به خشم آورد پيروى كردند و خشنودى او را خوش نداشتند، پس كارهاشان را تباه و بى‌اثر ساخت.'),
(4574, 47, 29, 'بلكه كسانى كه در دلهاشان بيماريى- شك و نفاق- است مى‌پندارند كه خدا كينه‌هاشان را آشكار نخواهد ساخت.'),
(4575, 47, 30, 'و اگر خواهيم آنان را به تو مى‌نماييم پس آنها را به سيمايشان بازشناسى و هر آينه ايشان را از شيوه سخنشان خواهى شناخت، و خدا كارهاى شما را مى‌داند.'),
(4576, 47, 31, 'و هر آينه شما را بيازماييم تا مجاهدان و شكيبايان- پايداران- شما را معلوم كنيم و خبرهاى شما- اعمالتان- را بيازماييم.'),
(4577, 47, 32, 'همانا كسانى كه كافر شدند و [مردم را] از راه خدا بازداشتند- يا: و از راه خدا بگرديدند- و پس از آنكه راه راست برايشان روشن و هويدا شد با پيامبر مخالفت كردند هرگز به خدا هيچ گزندى نرسانند و زودا كه كارهاشان را بى‌اثر و نابود سازد.'),
(4578, 47, 33, 'اى كسانى كه ايمان آورده‌ايد، خداى را فرمان بريد و پيامبر را فرمان بريد و كارهاتان را [به شك و نفاق و ريا و عجب‌] بيهوده و تباه مكنيد.'),
(4579, 47, 34, 'همانا كسانى كه كافر شدند و از راه خدا بگرديدند- يا مردم را از راه خدا بازداشتند- سپس در حالى كه كافر بودند مردند، هرگز خداوند آنان را نخواهد آمرزيد.'),
(4580, 47, 35, 'پس سستى مكنيد و [كافران را] به آشتى مخوانيد و حال آنكه شما برتريد، و خدا با شماست و هرگز از [پاداش‌] كردار شما نخواهد كاست.'),
(4581, 47, 36, 'همانا زندگانى اين جهان بازيچه و سرگرمى است، و اگر ايمان داشته باشيد و پرهيزگارى كنيد مزدتان را خواهد داد و مالهاتان را نخواهد خواست'),
(4582, 47, 37, 'اگر آن را از شما بخواهد و در خواستن پافشارى نمايد، آنگاه بخل مى‌ورزيد و [به سبب مالدوستى‌تان‌] كينه‌هاى شما را آشكار مى‌سازد.'),
(4583, 47, 38, 'هان شماييد كه خوانده مى‌شويد تا در راه خدا انفاق كنيد، ولى از شما كس هست كه بخل مى‌ورزد، و هر كه بخل ورزد جز اين نيست كه با خويشتن بخل مى‌ورزد، و خدا بى‌نياز است و شما نيازمنديد، و اگر روى بگردانيد گروهى ديگر جز شما را به جاى شما آرد كه همچون شما- سست ايمان و بخيل- نباشند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'ما براى تو گشايش و پيروزى نمايانى را بگشوديم.'),
(4585, 48, 2, 'تا خدا گناه تو- كارهاى ناروايى كه مشركان و دشمنان درباره تو روا داشتند- را، آنچه در گذشته بوده و آنچه اكنون و سپس‌تر باشد، بيامرزد- بپوشاند و بزدايد- و نعمت خود را بر تو تمام كند و تو را راه راست بنمايد،'),
(4586, 48, 3, 'و خداى تو را يارى كند يارى‌كردنى توانمندانه- كه در آن عزت و پيروزى باشد-.'),
(4587, 48, 4, 'اوست آن كه آرامش را در دلهاى مؤمنان فرو آورد تا ايمانى بر ايمان خويش بيفزايند. و خداى راست لشكرهاى آسمانها و زمين، و خدا دانا و با حكمت است.'),
(4588, 48, 5, 'تا مردان و زنان مؤمن را به بهشتهايى درآرد كه از زير [درختان‌] آنها جويها روان است، در آنجا جاويدانند، و بديهاشان را از ايشان بزدايد. و اين در نزد خدا رستگارى و كاميابى بزرگى است.'),
(4589, 48, 6, 'و مردان و زنان منافق و مردان و زنان مشرك را كه به خداى گمان بد مى‌برند عذاب كند. گردنده بد- پيشامد بد- بر آنان باد، و خداى بر آنان خشم گرفت و لعنتشان كرد و برايشان دوزخ را آماده ساخته است، و بد بازگشتگاهى است.'),
(4590, 48, 7, 'و خداى راست لشكرهاى آسمانها و زمين، و خدا تواناى بى‌همتا و داناى استواركار است.'),
(4591, 48, 8, 'همانا تو را گواه- بر اعمال مردمان- و مژده‌دهنده و بيم‌كننده فرستاديم،'),
(4592, 48, 9, 'تا [شما مردم‌] به خدا و پيامبرش ايمان بياوريد و او'),
(4593, 48, 10, 'همانا كسانى كه با تو بيعت مى‌كنند جز اين نيست كه با خداى بيعت مى‌كنند دست خداى بالاى دستهاشان است. پس هر كه پيمان بشكند جز اين نيست كه به زيان خويش مى‌شكند، و هر كه بدانچه بر آن با خداى پيمان بسته است وفا كند پس او را مزدى بزرگ خواهد داد.'),
(4594, 48, 11, 'واپس‌ماندگان [از جنگ‌]، از باديه‌نشينان، خواهند گفت: مالها و زنان و فرزندانمان ما را سرگرم داشتند- گرفتار آنها بوديم و از جنگ باز مانديم- پس براى ما آمرزش بخواه. به زبانهاشان چيزى مى‌گويند كه در دلهاشان نيست. بگو: اگر خدا درباره شما زيانى خواهد يا سودى، كيست كه براى شما در برابر [خواست‌] خدا چيزى در توان داشته باشد؟ بلكه خداوند از آنچه مى‌كنيد آگاه است.'),
(4595, 48, 12, 'بلكه پنداشتيد كه پيامبر و مؤمنان هرگز به نزد كسان خويش باز نخواهند گشت و اين پندار در دلهاتان آراسته شد- به آن دلخوش شديد- و گمان بد برديد- كه پيامبر و ياران او هلاك شوند و دين اسلام برافتد- و مردمى هلاك‌شده گشتيد.'),
(4596, 48, 13, 'و هر كه به خدا و پيامبر او ايمان نياورده باشد، پس [بداند كه‌] ما براى كافران آتشى افروخته آماده كرده‌ايم.'),
(4597, 48, 14, 'و خداى راست پادشاهى آسمانها و زمين هر كه را خواهد- شايسته بيند- بيامرزد و هر كه را خواهد- سزاوار بيند- عذاب كند. و خدا آمرزگار و مهربان است.'),
(4598, 48, 15, 'واپس‌ماندگان، چون به سوى غنيمتها برويد تا آنها را بگيريد- در جنگ خيبر-، خواهند گفت: بگذاريد تا از پى شما بياييم. مى‌خواهند سخن خداى را دگرگون سازند. بگو: هرگز از پى ما نخواهيد آمد، خدا از پيش [درباره شما] چنين فرموده است. پس خواهند گفت: بلكه بر ما حسد مى‌بريد. [نه،] بلكه جز اندكى نمى‌فهمند.'),
(4599, 48, 16, 'واپس‌ماندگان، از باديه‌نشينان، را بگو: بزودى به جنگ با مردمى سخت زورمند جنگاور فرا خوانده مى‌شويد كه با آنها كارزار كنيد يا آنكه مسلمان شوند. پس اگر فرمان بريد خدا شما را مزدى نيك بدهد، و اگر رويگردان شويد چنانكه پيش از اين رويگردان شديد شما را به عذابى دردناك عذاب كند.'),
(4600, 48, 17, 'بر نابينا تنگى و گناهى نيست و بر لنگ تنگى و گناهى نيست و بر بيمار تنگى و گناهى نيست [اگر به كارزار نروند]، و هر كه خداى و پيامبرش را فرمان برد او را به بهشتهايى در آورد كه از زير آنها جويها روان است، و هر كه روى بگرداند به عذابى دردناك عذابش كند.'),
(4601, 48, 18, 'هر آينه خداى از مؤمنان خشنود شد آنگاه كه با تو در زير آن درخت- در حديبيه- بيعت مى‌كردند، آنچه را در دلهاشان بود- از راستى و پاكى نيت و وفا- دانست، پس آرامش را بر آنان فرو آورد و ايشان را پيروزيى نزديك- فتح خيبر- پاداش داد،'),
(4602, 48, 19, 'و نيز غنيمتهاى بسيار كه مى‌گيرند، و خدا تواناى بى‌همتا و داناى با حكمت است.'),
(4603, 48, 20, 'خدا به شما غنيمتهاى بسيار را وعده داده است كه مى‌گيريد، و اين را زود به شما ارزانى داشت و دستهاى مردم را از شما باز داشت [تا همپيمانان يهود ترسيده به جنگ بيرون نيايند]، و تا نشانه‌اى براى مؤمنان باشد و شما را به راهى راست رهبرى كند.'),
(4604, 48, 21, 'و غنيمتهاى ديگرى [هست‌] كه هنوز بر آنها دست نيافته‌ايد. همانا خداوند به آنها احاطه دارد- از آنها آگاه است- و خدا بر هر چيزى تواناست.'),
(4605, 48, 22, 'و اگر كسانى كه كافر شدند با شما كارزار كنند هر آينه پشت كرده بگريزند و آنگاه هيچ كارساز و ياورى نيابند.'),
(4606, 48, 23, 'نهاد خداست كه پيش از اين گذشته است، و هرگز براى نهاد خداى دگرگونى نخواهى يافت.'),
(4607, 48, 24, 'و اوست كه دستهاى آنان را از شما و دستهاى شما را از آنان در وادى مكه- زمين نزديك مكه در حديبيه- باز داشت پس از آنكه شما را بر آنها پيروزى داد- و توانستيد بى جنگ و خونريزى به سرزمين آنها در آييد- و خدا بدانچه مى‌كنيد بيناست.'),
(4608, 48, 25, 'ايشانند كه كافر شدند و شما را از مسجد الحرام- نمازگاه شكوهمند- بازداشتند و قربانى- شترانى كه براى قربانى آورده بوديد- را باز داشتند و نگذاشتند كه به قربانگاهش- منى- برسد، و اگر [در مكه‌] مردان و زنان مؤمنى نبودند كه شما ايشان را نمى‌شناختيد و بى‌آگاهى در زير پاى مى گرفتيد- مى‌كشتيد- پس، از آنان گزندى ناخوشايند- اندوهى به سبب قتل مؤمنان و پرداخت ديه كه كفاره كشتن مؤمن به خطاست- به شما مى‌رسيد، [خداوند دست شما را از آنان باز نمى‌داشت، ليكن شما را از كشتن اهل مكه باز داشت‌] تا خداى هر كه را خواهد به مهر و بخشايش خويش در آرد. اگر از هم جدا مى‌بودند هر آينه كافرانشان را عذاب مى‌كرديم عذابى دردناك.'),
(4609, 48, 26, '[به ياد آريد] آنگاه كه كافران [مكه‌] در دلهاى خويش تعصب جاهليت- خشم و كين و تكبر ناروا- را پروردند پس خداوند آرامش خود را بر پيامبرش و بر مؤمنان فروآورد- تا جنگ نكردند و به صلح تن دادند و به مدينه بازگشتند- و سخن پرهيزگارى- توحيد، وفادارى، روح ايمان- را با آنان همراه داشت و ايشان سزاوارتر به آن و اهل آن بودند، و خدا به هر چيزى داناست.'),
(4610, 48, 27, 'همانا خدا خواب پيامبرش را بحق راست آورد- تحقق بخشيد- كه اگر خداى خواهد هر آينه به مسجد الحرام درخواهيد آمد، در ايمنى و سر تراشيده و كوتاه موى، بى آنكه بيمى داشته باشيد. پس مى‌دانست آنچه شما نمى‌دانستيد- از صلاح كارتان در صلح حديبيه- و جز اين [براى شما] فتحى نزديك- فتح خيبر- قرار داد- تا دل مؤمنان به آن فتح شاد شد و اندوه تأخير عمره از ميان برفت-.'),
(4611, 48, 28, 'اوست كه پيامبرش را با رهنمونى [به راه راست‌] و دين حق فرستاد تا آن را بر همه دينها چيره گرداند، و خدا گواهى بسنده است- بر صدق پيامبرش-.'),
(4612, 48, 29, 'محمد');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'اى كسانى كه ايمان آورده‌ايد، [در هيچ كارى‌] فراپيش خدا و پيامبرش نرويد، و از خداى پروا كنيد، كه خدا شنوا و داناست.'),
(4614, 49, 2, 'اى كسانى كه ايمان آورده‌ايد، آوازهاى خود را از آواز پيامبر بلندتر مكنيد، و با او بلند سخن مگوييد مانند بلند سخن‌گفتن برخى از شما با برخ ديگر، كه كردارهاى شما تباه و بيهوده مى‌شود و خود آگاه نيستيد.'),
(4615, 49, 3, 'كسانى كه در نزد پيامبر خدا آوازهاى خود را فرومى‌دارند آنانند كه خداوند دلهاشان را براى پرهيزگارى بيازموده است- تا پاك و صافى شوند- ايشان را آمرزش و مزدى بزرگ است.'),
(4616, 49, 4, 'كسانى كه از فراسوى حجره‌ها- اطاقها يا خانه‌هاى خرد- تو را آواز مى‌دهند، بيشترشان نابخردند- زيرا انسانيت و ادب نگاه نمى‌دارند-.'),
(4617, 49, 5, 'و اگر صبر كنند تا به نزد آنان بيرون شوى هر آينه برايشان بهتر است، و خدا آمرزگار و مهربان است.'),
(4618, 49, 6, 'اى كسانى كه ايمان آورده‌ايد، اگر دروغگويى برون شده از فرمان خداى به شما خبرى آورد نيك بررسى كنيد تا مبادا نادانسته به مردمى آسيب رسانيد، و آنگاه بر آنچه كرديد پشيمان گرديد.'),
(4619, 49, 7, 'و بدانيد كه پيامبر خدا در ميان شماست- و شما بايد از او فرمان بريد نه او از شما-، كه اگر در بسيارى از كارها شما را فرمان برد هر آينه به رنج افتيد و هلاك شويد، و ليكن خداوند ايمان را در نزد شما دوست‌داشتنى ساخت و آن را در دلهاى شما بياراست، و كفر و بدكردارى و نافرمانى را ناخوشايندتان داشت. ايشانند راه‌يافتگان.'),
(4620, 49, 8, '[و اين‌] از روى بخشش و نعمتى است از خداى، و خدا دانا و با حكمت است.'),
(4621, 49, 9, 'و اگر دو گروه از مؤمنان با هم كارزار كنند، ميانشان آشتى دهيد. پس اگر يكى از آن دو بر ديگرى ستم و تجاوز كند، با آن كه ستم و تجاوز مى‌كند بجنگيد تا به فرمان خداى بازگردد. پس اگر باز گشت ميانشان به عدل و داد آشتى دهيد، و دادگرى كنيد، كه خدا دادگران را دوست مى‌دارد.'),
(4622, 49, 10, 'همانا مؤمنان برادران [يكديگر] اند، پس ميان [آن دو گروه‌] برادرانتان آشتى افكنيد و از خداى پروا كنيد، باشد كه درخور رحمت گرديد.'),
(4623, 49, 11, 'اى كسانى كه ايمان آورده‌ايد، گروهى [از مردان‌] گروه ديگر را مسخره نكنند، شايد آنان- كه مسخره شده‌اند- از اينان- كه مسخره كرده‌اند- بهتر باشند و نه زنانى زنان ديگر را، شايد آنان از اينان بهتر باشند. و از خودتان- از يكديگر- عيبجويى مكنيد و يكديگر را به لقبها [ى بد و ناخوش‌] مخوانيد. بد نامى است نام‌كردن [مردم‌] به بدكردارى پس از ايمان آوردن [آنها]. و هر كه توبه نكرد پس اينانند ستمكاران.'),
(4624, 49, 12, 'اى كسانى كه ايمان آورده‌ايد، از بسيارى از گمانها- گمانهاى بد- دور باشيد، زيرا برخى از گمانها گناه است. و [در احوال و عيب‌هاى پنهان مردم‌] كاوش مكنيد، و از پس يكديگر بدگويى- غيبت- مكنيد. آيا يكى از شما دوست دارد كه گوشت برادر مرده خود را بخورد؟ بى‌گمان آن را ناخوش و ناپسند مى‌داريد. و از خداى پروا كنيد، كه خدا توبه‌پذير و مهربان است.'),
(4625, 49, 13, 'اى مردم، ما شما را از يك مرد و زن بيافريديم، و شما را شاخه‌ها و تيره‌ها كرديم تا يكديگر را باز شناسيد. هر آينه گرامى‌ترين شما نزد خدا پرهيزگارترين شماست. همانا خدا دانا و آگاه است.'),
(4626, 49, 14, 'باديه‌نشينان گفتند: ايمان آورديم. بگو: ايمان نياورده‌ايد، بلكه بگوييد: اسلام آورديم- به زبان تسليم شديم-، و هنوز ايمان در دلهاتان درنيامده است. و اگر خدا و پيامبرش را فرمان بريد، از [پاداش‌] كردارهاى شما جيزى نكاهد، همانا خدا آمرزگار و مهربان است.'),
(4627, 49, 15, 'جز اين نيست كه مؤمنان كسانى‌اند كه به خدا و پيامبر او ايمان آورده‌اند و سپس شك نكرده‌اند، و با مالها و جانهاى خويش در راه خدا جهاد كرده‌اند. ايشانند راستگويان.'),
(4628, 49, 16, 'بگو: آيا مى‌خواهيد خداى را به دين خودتان آگاه كنيد؟ و حال آنكه خدا آنچه را در آسمانها و آنچه را در زمين است مى‌داند، و خدا به همه چيز داناست.'),
(4629, 49, 17, 'بر تو منت مى‌نهند كه اسلام آورده‌اند بگو: به اسلام خود بر من منت منهيد، بلكه خداى بر شما منت مى‌نهد كه شما را به ايمان راه نموده است اگر [در ايمان خود] راستگوييد.'),
(4630, 49, 18, 'همانا خدا نهان آسمانها و زمين را مى‌داند، و خدا بدانچه مى‌كنيد بيناست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'قاف. سوگند به اين قرآن ارجمند [كه اين پيامبر راستگوست و رستاخيز حق است‌].'),
(4632, 50, 2, 'بلكه شگفتى نمودند از اينكه از ميان خودشان بيم‌دهنده‌اى بديشان آمد از اين رو كافران گفتند: اين چيزى شگفت است.'),
(4633, 50, 3, 'آيا هنگامى كه مُرديم و خاك شديم [باز زنده مى‌شويم‌]؟ اين بازگشتى است دور [از عقل و عادت‌].'),
(4634, 50, 4, 'همانا ما مى‌دانيم كه زمين چه چيز از ايشان مى‌كاهد. و نزد ما كتابى است نگاهدارنده- لوح محفوظ-.'),
(4635, 50, 5, 'بلكه سخن حق را چون بديشان آمد دروغ انگاشتند. پس آنها در كارى شوريده و آشفته‌اند.'),
(4636, 50, 6, 'آيا به آسمان بالاى سرشان ننگريستند كه چگونه آن را برافراشتيم و [با ستارگان‌] بياراستيم و آن را هيچ شكافى- نقص و خلل و ناموزونى- نيست؟'),
(4637, 50, 7, 'و زمين را بگسترديم و در آن كوه‌هاى بلند و استوار نهاديم و از هر گونه گياه زيبا و بهجت‌انگيز در آن رويانيديم'),
(4638, 50, 8, 'تا بينشى باشد و پندى براى هر بنده‌اى كه [با روى دل به خدا] بازمى‌گردد.'),
(4639, 50, 9, 'و از آسمان آبى با بركت فرو آورديم پس بدان بوستانها و دانه‌هاى دروكردنى- مانند گندم و جو و ارزن- رويانيديم'),
(4640, 50, 10, 'و نيز خرمابنهاى بلند را كه ميوه‌هاى بر هم نشانده دارند'),
(4641, 50, 11, 'تا روزى بندگان باشد و بدان [آب‌] سرزمين مرده را زنده كرديم. همچنين است بيرون‌آمدن [از گور].'),
(4642, 50, 12, 'پيش از اينان- اهل مكه- قوم نوح و اصحاب رس و ثمود- قوم صالح- تكذيب كردند،'),
(4643, 50, 13, 'و نيز عاد- قوم هود- و فرعون و قوم لوط،'),
(4644, 50, 14, 'و اهل ايكه- قوم شعيب- و قوم تُبّع همگى پيامبران را دروغگو شمردند پس وعده من به عذاب بر آنها سزا گشت.'),
(4645, 50, 15, 'مگر در آفرينش نخستين درمانده شديم- تا از بازآفريدن درمانده باشيم-؟ نه، بلكه آنهايند كه از آفرينش نو در شك‌اند.'),
(4646, 50, 16, 'و همانا ما آدمى را آفريده‌ايم و آنچه را نفس او وسوسه مى‌كند مى‌دانيم، و ما به او از رگ گردن نزديكتريم- به احاطه علمى-.'),
(4647, 50, 17, 'آنگاه كه آن دو [فرشته‌] فراگيرنده از جانب راست و از جانب چپ [آدمى‌] نشسته [اعمال او را] فرامى‌گيرند.'),
(4648, 50, 18, 'هيچ سخنى بر زبان نيارد مگر آنكه در نزد او نگهبانى آماده است.'),
(4649, 50, 19, 'و بيهوشى مرگ براستى فرارسد، اين است آنچه از آن مى‌گريختى.'),
(4650, 50, 20, 'و در صور دميده شود، اين است روز وعده عذاب- روز رستاخيز-.'),
(4651, 50, 21, 'و هر كسى مى‌آيد در حالى كه با او [فرشته‌] راننده‌اى و [فرشته‌] گواهى‌دهنده‌اى هست.'),
(4652, 50, 22, '[خداى فرمايد:] هر آينه از اين غافل بودى، پس ما پوشش تو از [ديده‌] تو برداشتيم- تا آنچه به خبر مى‌شنيدى خود ببينى- از اين رو امروز چشمت تيزبين است- يعنى در آنچه مى‌بينى شك نمى‌كنى-.'),
(4653, 50, 23, 'و همنشين او- فرشته نگهبان او- گويد: اين است آنچه نزد من آماده است- نامه اعمال-.'),
(4654, 50, 24, '[و به آن دو فرشته ندا آيد:] هر كافر ستيهنده سركش را در دوزخ افكنيد'),
(4655, 50, 25, 'هر بازدارنده از نيكى [و] تجاوزگر شك‌دار را'),
(4656, 50, 26, 'آن كه با خداى يكتا خدايى ديگر گرفت، پس او را در عذاب سخت بيفكنيد.'),
(4657, 50, 27, '[شيطان‌] همنشين او گويد: پروردگارا، من او را به سركشى و نافرمانى وانداشتم و ليكن او خود در گمراهى دورى بود.'),
(4658, 50, 28, '[خداى‌] گويد: نزد من ستيزه مكنيد كه همانا وعده عذاب را پيش از اين به شما داده بودم.'),
(4659, 50, 29, 'سخن نزد من- وعده و وعيد من- دگرگون نشود و من بر بندگان هيچ ستم نكنم.'),
(4660, 50, 30, 'روزى كه دوزخ را گوييم: آيا پُر شدى؟ و گويد: آيا بيش از اين هم هست؟'),
(4661, 50, 31, 'و بهشت را براى پرهيزگاران نزديك آرند و دور نباشد.'),
(4662, 50, 32, 'اين است آنچه نويد داده مى‌شديد، به هر بازگردنده- به خدا- نگاهدار [فرمان خدا]'),
(4663, 50, 33, 'آن كسى كه در نهان از خداى رحمان بترسد و با دلى بازگردنده آيد.'),
(4664, 50, 34, 'به سلامت در آن'),
(4665, 50, 35, 'ايشان راست در آن هر چه خواهند و نزد ما افزونى است- افزون بر آنچه خواهند-.'),
(4666, 50, 36, 'و چه بسيار مردمانى را پيش از آنها نابود كرديم كه از اينها- اين كافران- زورمندتر بودند، پس در شهرها گردش و كاوش كردند آيا [آنان را] هيچ پناه و گريزگاهى هست- از عذاب و هلاك و مرگ؟'),
(4667, 50, 37, 'هر آينه در اين [سخن كه گفته شد] يادكردى است براى آن كه او را دل باشد- عقل بيدار دارد و غافل نباشد- يا گوش فرادارد و حاضر باشد.'),
(4668, 50, 38, 'و هر آينه آسمانها و زمين و آنچه را ميان آنهاست در شش روز- شش دوران- آفريديم، و هيچ رنج و ماندگى به ما نرسيد.'),
(4669, 50, 39, 'پس بر آنچه مى‌گويند شكيبا باش و پيش از برآمدن آفتاب- يعنى نماز بامداد- و پيش از فروشدن آن- نماز پيشين'),
(4670, 50, 40, 'و در پاسى از شب- نماز شام'),
(4671, 50, 41, 'و گوش فرادار- منتظر باش- روزى را كه آوازدهنده- اسرافيل- از جايى نزديك آواز دهد'),
(4672, 50, 42, 'آن روز كه آن بانگ حق- دميدن دوم- را بشنوند، آن است روز بيرون‌شدن [از گور].'),
(4673, 50, 43, 'همانا ما زنده مى‌كنيم و مى‌ميرانيم و بازگشت به سوى ماست.'),
(4674, 50, 44, 'روزى كه زمين از آنان بشكافد شتابنده باشند- شتابان بيرون آيند- اين برانگيختنى است كه بر ما آسان است.'),
(4675, 50, 45, 'ما بدانچه مى‌گويند داناتريم، و تو بر آنها [گماشته‌اى‌] چيره نيستى، پس آن را كه از وعده عذاب من مى‌ترسد بدين قرآن پند ده.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'سوگند به بادهاى‌دمنده كه پراكنده مى‌كنند- خاك و جز آن را- پراكندنى'),
(4677, 51, 2, 'و سوگند به ابرهاى باردار- به باران-'),
(4678, 51, 3, 'و سوگند به كشتيها كه به آسانى روانند'),
(4679, 51, 4, 'و سوگند به بخش‌كنندگان كار- فرشتگانى كه تقسيم‌كننده كارهاى مربوط به خودند-'),
(4680, 51, 5, 'كه هر آينه آنچه وعده داده مى‌شويد راست است.'),
(4681, 51, 6, 'و همانا كيفر و پاداش- حساب و جزا- فرارسيدنى و بودنى است.'),
(4682, 51, 7, 'و سوگند به آسمان داراى راه‌ها- يا آراسته به ستارگان-.'),
(4683, 51, 8, 'هر آينه شما در گفتارى گونه‌گونه‌ايد.'),
(4684, 51, 9, 'از آن- حق- گردانيده مى‌شود آن كس كه گردانيده شده است.'),
(4685, 51, 10, 'مرگ بر آن دروغزنان- كه سخن به گمان و تخمين مى‌گويند-.'),
(4686, 51, 11, 'آنان كه در گرداب [نادانى و گمراهى‌] فرورفته [و از حق‌] غافل و بى‌خبرند.'),
(4687, 51, 12, 'مى‌پرسند روز پاداش كى خواهد بود؟'),
(4688, 51, 13, 'روزى [است‌] كه در آتش دوزخ به عذاب سوخته شوند.'),
(4689, 51, 14, 'عذاب سوختن خود را بچشيد، اين همان است كه به شتاب مى‌خواستيد.'),
(4690, 51, 15, 'همانا پرهيزگاران در بوستانها و چشمه‌هاى آب باشند.'),
(4691, 51, 16, 'گيرنده‌اند آنچه را خداى بديشان دهد- از نعمتها-، زيرا پيش از اين نيكوكار بودند.'),
(4692, 51, 17, 'اندكى از شب را مى‌خفتند- و بيشتر آن را به نماز و عبادت برمى‌خاستند-.'),
(4693, 51, 18, 'و سحرگاهان آمرزش مى‌خواستند.'),
(4694, 51, 19, 'در مالهاشان براى خواهنده نيازمند و تنگ‌روزى بى‌بهره حقى بود- زكات و صدقه-.'),
(4695, 51, 20, 'و در زمين اهل يقين را نشانه‌هاست.'),
(4696, 51, 21, 'و نيز در خودتان، آيا نمى‌نگريد؟'),
(4697, 51, 22, 'و روزى شما- يعنى اسباب روزى شما كه باران است- و آنچه وعده داده مى‌شويد- ثواب الهى و بهشت- در آسمان است.'),
(4698, 51, 23, 'سوگند به پروردگار آسمان و زمين كه اين سخن راست است همچنانكه شما سخن مى‌گوييد.'),
(4699, 51, 24, 'آيا داستان مهمانان گرامى ابراهيم به تو رسيده است؟'),
(4700, 51, 25, 'آنگاه كه بر او در آمدند و سلام گفتند، گفت: سلام، گروهى ناشناخته‌ايد.'),
(4701, 51, 26, 'پس پنهانى به نزد خانواده خود رفت و گوساله فربهى- بريان شده- بياورد.'),
(4702, 51, 27, 'پس آن را نزد آنان گذاشت و گفت: آيا نمى‌خوريد؟- و نخوردند-.'),
(4703, 51, 28, 'پس، از آنان بيمى به دل گرفت. گفتند: مترس [ما فرشتگانيم‌]. و او را به پسرى دانا مژده دادند.'),
(4704, 51, 29, 'پس زنش- ساره- فريادكنان پيش آمد و بر روى خود زد و گفت: پيرزنى نازا [چگونه بزايد و پسرى آورد]؟!'),
(4705, 51, 30, 'گفتند: پروردگار تو چنين گفته است، كه اوست با حكمت و دانا.'),
(4706, 51, 31, 'گفت: اكنون كار شما اى فرستادگان، چيست؟'),
(4707, 51, 32, 'گفتند: ما به سوى مردمى بزهكار فرستاده شده‌ايم،'),
(4708, 51, 33, 'تا بر آنها سنگى از گل- كلوخ- بفرستيم- فرو ريزيم-،'),
(4709, 51, 34, 'كه نزد پروردگارت براى گزافكاران نشان‌كرده شده.'),
(4710, 51, 35, 'پس هر كه را از مؤمنان در آن [شهر] بود بيرون برديم.'),
(4711, 51, 36, 'اما از مسلمانان جز يك خانه- يعنى خانواده لوط- در آنجا نيافتيم.'),
(4712, 51, 37, 'و در آنجا براى كسانى كه از عذاب دردناك مى‌ترسند نشانه و عبرتى گذاشتيم.'),
(4713, 51, 38, 'و در [داستان‌] موسى [نيز نشانه و عبرتى است‌] آنگاه كه او را با حجتى روشن و آشكار به نزد فرعون فرستاديم.'),
(4714, 51, 39, 'پس با نيروى- سپاه- خود روى برتافت و گفت: [اين موسى‌] جادوگر است يا ديوانه.'),
(4715, 51, 40, 'پس او و لشكرش را بگرفتيم و در دريا افكنديم، و او سزاوار سرزنش بود.'),
(4716, 51, 41, 'و نيز در [داستان‌] عاد- قوم هود- آنگاه كه بر آنها باد نازا فرستاديم.'),
(4717, 51, 42, 'هيچ چيزى را [از عاديان‌] كه بر آن بگذشت فرو نگذاشت مگر آنكه آن را خرد و تباه كرد.'),
(4718, 51, 43, 'و نيز در [داستان‌] ثمود- قوم صالح- آنگاه كه به آنها گفته شد تا هنگامى- سه روز پس از كشتن آن شتر- برخوردارى گيريد- مهلت داريد-.'),
(4719, 51, 44, 'آنان از فرمان پروردگارشان سرپيچى كردند پس صاعقه- آتش آسمانى- بگرفتشان در حالى كه خود مى‌نگريستند- يا انتظار مى‌بردند-.'),
(4720, 51, 45, 'پس برخاستن نتوانستند و ياراى دفاع از خود نداشتند.'),
(4721, 51, 46, 'و قوم نوح را پيش از اين- پيش از عاد و ثمود- [هلاك كرديم‌]، زيرا مردمى بدكار و نافرمان بودند.'),
(4722, 51, 47, 'و آسمان را به نيرو برافراشتيم و هر آينه ما تواناييم- يا گسترش‌دهنده‌ايم-.'),
(4723, 51, 48, 'و زمين را گسترانيديم و چه نيكو گسترانيده‌ايم.'),
(4724, 51, 49, 'و از هر چيزى دو گونه- كه يكى جفت ديگرى است- آفريديم، باشد كه ياد كنيد و پند گيريد.'),
(4725, 51, 50, 'پس به سوى خداى بگريزيد- به خداى باز گرديد- همانا من از سوى او شما را بيم‌دهنده‌اى آشكارم.'),
(4726, 51, 51, 'و با خداى يكتا خدايى ديگر مگيريد، همانا من از سوى او شما را بيم‌دهنده‌اى آشكارم.'),
(4727, 51, 52, 'همچنين بدان كسان نيز كه پيش از اينان- كفار مكه- بودند هيچ پيامبرى نيامد مگر آنكه گفتند: جادوگر است يا ديوانه.'),
(4728, 51, 53, 'آيا يكديگر را بدين [سخن‌] سفارش كرده بودند؟ [نه،] بلكه آنها مردمى سركش بودند.'),
(4729, 51, 54, 'پس، از آنان روى گردان، كه در خور سرزنش نيستى- زيرا تكليف خود را انجام داده‌اى-.'),
(4730, 51, 55, 'و يادآورى كن و پند ده، كه يادآورى و پند، مؤمنان را سود دارد.'),
(4731, 51, 56, 'و پريان و آدميان را نيافريدم مگر تا مرا [به يگانگى‌] بپرستند.'),
(4732, 51, 57, 'از آنان روزيى نمى‌خواهم و نمى‌خواهم كه مرا طعام دهند'),
(4733, 51, 58, 'زيرا خداست روزى‌دهنده و با نيرو و توانمند.'),
(4734, 51, 59, 'همانا كسانى را كه [با كفر ورزيدن به خود] ستم كردند بهره‌اى است از عذاب همانند بهره ياران [گذشته‌] آنها. پس [آن را] به شتاب از من نخواهند.'),
(4735, 51, 60, 'پس واى بر كافران از آن روزشان كه وعده داده مى‌شوند- روز رستاخيز-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'سوگند به كوه طور- كوهى كه در آنجا خدا با موسى سخن گفت-'),
(4737, 52, 2, 'و سوگند به كتابى نوشته شده- تورات، يا قرآن، يا هر كتاب آسمانى يا لوح محفوظ يا نامه اعمال-'),
(4738, 52, 3, 'در صفحه‌اى- يا پوستى- گشوده و گسترده'),
(4739, 52, 4, 'و سوگند به آن خانه آباد- كعبه كه به زيارت حاجيان آباد است-'),
(4740, 52, 5, 'و سوگند به آن سقف برافراشته- آسمان-'),
(4741, 52, 6, 'و سوگند به آن درياى آكنده و افروخته [از آتش‌]'),
(4742, 52, 7, 'كه عذاب پروردگارت هر آينه روى‌دادنى و فرودآمدنى است.'),
(4743, 52, 8, 'آن را بازدارنده‌اى نيست.'),
(4744, 52, 9, 'روزى كه آسمان سخت بجنبد.'),
(4745, 52, 10, 'و كوه‌ها با شتاب روان گردند.'),
(4746, 52, 11, 'پس در آن روز، واى بر دروغ‌انگاران'),
(4747, 52, 12, 'آنان كه به بيهوده‌گويى و ياوه‌سرايى- درباره آيات الهى- بازى همى كنند'),
(4748, 52, 13, 'روزى كه آنان را بسختى به سوى آتش دوزخ برانند.'),
(4749, 52, 14, 'اين است آن آتشى كه دروغش مى‌پنداشتيد.'),
(4750, 52, 15, 'آيا اين جادوست يا شما نمى‌بينيد؟'),
(4751, 52, 16, 'به آن در آييد- يا به آن بسوزيد- و شكيبايى كنيد يا نكنيد، بر شما يكسان است. جز اين نيست كه آنچه مى‌كرديد پاداش داده مى‌شويد.'),
(4752, 52, 17, 'همانا پرهيزگاران در بوستانها و نعمتها باشند.'),
(4753, 52, 18, 'به آنچه پروردگارشان به ايشان داده است شادمانند، و پروردگارشان آنان را از عذاب دوزخ نگاه داشته است.'),
(4754, 52, 19, 'بخوريد و بياشاميد نوش و گوارا، به پاداش آنچه مى‌كرديد.'),
(4755, 52, 20, 'تكيه‌زدگان بر تختهاى به صف نهاده، و حوران فراخ‌چشم را به همسريشان دهيم.'),
(4756, 52, 21, 'و كسانى كه ايمان آوردند و فرزندانشان در ايمان از آنان پيروى كردند، فرزندانشان را بديشان بپيونديم و از [پاداش‌] عملشان چيزى نكاهيم. هر كسى در گرو كارى است كه كرده.'),
(4757, 52, 22, 'و ايشان را پى در پى ميوه و گوشتى كه بخواهند همى دهيم.'),
(4758, 52, 23, 'در آنجا جامى گيرند و دست به دست دهند كه در [نوشيدن شراب‌] آن نه بيهوده‌گويى باشد و نه گناهكارى.'),
(4759, 52, 24, 'و پسرانى كه آنان راست بر گردشان مى‌گردند- براى خدمت- كه [در صفا و لطافت‌] گويى مرواريدى پوشيده [در صدف‌] اند.'),
(4760, 52, 25, 'برخى‌شان پرسش‌كنان بر برخى روى فرا كنند'),
(4761, 52, 26, 'گويند: ما پيش از اين- در دنيا- در ميان كسان خود ترسان بوديم- از عذاب خدا-.'),
(4762, 52, 27, 'پس خداى بر ما منت نهاد و ما را از عذاب سوزنده و نفوذكننده نگاه داشت.'),
(4763, 52, 28, 'زيرا ما پيش از اين او را مى‌خوانديم، كه اوست نيكوكار و مهربان.'),
(4764, 52, 29, 'پس يادآورى كن و پند ده كه تو به نعمت پروردگارت- به لطف او يا به بركت نعمت نبوت و عصمت- نه كاهنى و نه ديوانه.'),
(4765, 52, 30, 'بلكه گويند: شاعرى است كه ما رويداد مرگ را درباره وى چشم مى‌داريم.'),
(4766, 52, 31, 'بگو: چشم بداريد- مرگ مرا- كه من هم چشم مى‌دارم- پيروزى اسلام و نابودى شما را-.'),
(4767, 52, 32, 'يا مگر خردشان اين را به آنها فرمان مى‌دهد، يا خود مردمى سركش‌اند؟'),
(4768, 52, 33, 'بلكه گويند اين'),
(4769, 52, 34, 'پس سخنى مانند اين بيارند، اگر راستگويند.'),
(4770, 52, 35, 'يا مگر از هيچ آفريده شده‌اند- بى‌آفريدگارى- يا خود آفريننده [خويش‌] اند؟!'),
(4771, 52, 36, 'يا مگر آسمانها و زمين را آفريده‌اند؟! [نه،] بلكه به يقين نرسيده‌اند.'),
(4772, 52, 37, 'يا مگر گنجينه‌هاى پروردگارت نزد آنهاست؟!- كه نبوت را به هر كه خواهند بدهند- يا خود چيره‌دست‌اند- كه نبوت را از هر كه خواهند بازدارند-؟!'),
(4773, 52, 38, 'يا مگر آنان را نردبانى است كه با آن [به آسمان برشده خبرهاى وحى را] مى‌شنوند؟! پس بايد كه شنونده‌شان حجتى روشن و هويدا بيارد.'),
(4774, 52, 39, 'يا مگر او'),
(4775, 52, 40, 'يا مگر از آنان مزدى مى‌خواهى كه از تاوان آن گرانبارند؟!'),
(4776, 52, 41, 'يا مگر دانش نهان نزدشان است كه [از آنجا] مى‌نويسند؟!'),
(4777, 52, 42, 'يا مگر نيرنگى- درباره تو- مى‌خواهند؟! ولى كافران خود گرفتار نيرنگ‌اند.'),
(4778, 52, 43, 'يا مگر آنان را خدايى جز خداى يگانه است؟! پاك است خداى از آنچه [با او] انباز مى‌گيرند.'),
(4779, 52, 44, 'و اگر ببينند كه پاره‌اى از آسمان فرو مى‌افتد [باز هم ايمان نيارند و] گويند: ابرى است توده شده.'),
(4780, 52, 45, 'پس واگذارشان تا آن روزشان را ببينند كه در آن به بيهوشى مرگ افتند.'),
(4781, 52, 46, 'روزى كه نيرنگ و ترفندشان آنان را هيچ سود نكند و يارى نشوند.'),
(4782, 52, 47, 'و همانا كسانى را كه ستم كردند عذابى است غير از اين- يا: پيش از اين، در دنيا يا در برزخ- ولى بيشترشان نمى‌دانند.'),
(4783, 52, 48, 'و براى فرمان پروردگارت شكيبايى ورز، كه تو در ديد- رعايت و حفظ- مايى و پروردگارت را آنگاه كه برخيزى همراه سپاس و ستايش به پاكى ياد كن.'),
(4784, 52, 49, 'و او را در پاسى از شب و به هنگام رفتن و ناپيدايى ستارگان به پاكى ياد كن.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'سوگند به ستاره چون فرو شود.'),
(4786, 53, 2, 'كه يار شما- رسول خدا'),
(4787, 53, 3, 'و از هواى نفس سخن نمى‌گويد.'),
(4788, 53, 4, 'نيست اين'),
(4789, 53, 5, 'او را آن [فرشته‌] بس نيرومند- جبرئيل- آموخته است.'),
(4790, 53, 6, 'توانمندى كه راست بايستاد- با صورت اصلى و تمام هيأت خود يا با جمال و صورت نيكوى خود-.'),
(4791, 53, 7, 'و او در افق برين- يا به كناره بلند آسمان- بود.'),
(4792, 53, 8, 'سپس نزديك شد و نزديكتر شد،'),
(4793, 53, 9, 'تا به اندازه دو كمان يا نزديكتر.'),
(4794, 53, 10, 'پس به بنده‌اش وحى كرد آنچه را وحى كرد.'),
(4795, 53, 11, 'دل [محمد ص‌] آنچه را ديد دروغ نگفت- دروغ داخل آن نكرد، يا به خطا نديد-.'),
(4796, 53, 12, 'آيا در آنچه مى‌بيند با وى ستيزه مى‌كنيد؟'),
(4797, 53, 13, 'و هر آينه او'),
(4798, 53, 14, 'نزد سدرة المنتهى'),
(4799, 53, 15, 'كه نزديك آن، بهشت جاى آرامش است.'),
(4800, 53, 16, 'آنگاه كه [آن فرشته را ديد] آن درخت سدره را مى‌پوشاند آنچه مى‌پوشاند.'),
(4801, 53, 17, 'چشم [پيامبر] نه كژ ديد و نه از حد در گذشت- نه آنچه را حقيقت نداشت بديد-.'),
(4802, 53, 18, 'هر آينه از نشانه‌هاى بزرگتر پروردگار خويش بديد.'),
(4803, 53, 19, 'آيا لات و عزى را ديده‌ايد؟'),
(4804, 53, 20, 'و ديگر، منات را كه سومين آنهاست [آيا اينها را كه انبازان خداى گرفته‌ايد شايسته خدايى‌اند]؟!'),
(4805, 53, 21, 'آيا شما را پسر باشد و او'),
(4806, 53, 22, 'آنگاه اين بخش‌كردنى است ناروا- كژ و ستمكارانه-.'),
(4807, 53, 23, 'اينها نيستند مگر نامهايى كه شما و پدرانتان نهاده‌ايد. خدا هيچ حجتى به [خدايى‌] آنها فرو نفرستاده است. [اينان‌] جز پندار و آنچه را كه نفسهاشان خوش دارد پيروى نمى‌كنند، و حال آنكه از سوى پروردگارشان رهنمونى بديشان آمده است.'),
(4808, 53, 24, 'مگر براى آدمى هر چه آرزو كند فراهم است؟ [هرگز!]'),
(4809, 53, 25, 'پس خداى راست آن جهان و اين جهان- هر چه خواهد به هر كه خواهد مى‌دهد-.'),
(4810, 53, 26, 'و بسا فرشتگانى در آسمانها كه شفاعتشان هيچ سود نكند مگر پس از آنكه خداى براى هر- شفيع يا براى هر انسانى- كه بخواهد و پسندد، اجازه دهد.'),
(4811, 53, 27, 'هر آينه كسانى كه به جهان پسين ايمان ندارند فرشتگان را به نام دختران مى‌نامند.'),
(4812, 53, 28, 'و آنان را هيچ دانشى بدان نيست. جز گمان را پيروى نمى‌كنند، و همانا گمان براى [شناخت‌] حقيقت هيچ سودى ندارد- كفايت نكند-.'),
(4813, 53, 29, 'پس روى بگردان از هر كه از يادكرد ما- يا قرآن- روى مى‌گرداند و جز زندگى اين جهان را نمى‌خواهد.'),
(4814, 53, 30, 'اين است منتهاى دستيابى آنها به دانش. همانا پروردگار تو داناتر است به آن كه از راه او گمراه شده و هم او داناتر است به آن كه راه‌يافته است'),
(4815, 53, 31, 'و خداى راست آنچه در آسمانها و آنچه در زمين است تا آنان را كه بدى كردند بدانچه كردند كيفر دهد و نيكوكاران را به پاداش نيكو پاداش دهد'),
(4816, 53, 32, 'آنان كه از گناهان بزرگ و زشتكاريها دورى كنند، مگر گناهان خرد و اندك همانا پروردگار تو فراخ‌آمرزش است. او به شما داناتر است آنگاه كه شما را از زمين بيافريد و آنگاه كه جنين‌هايى بوديد در شكمهاى مادرانتان. پس خود را مستاييد، كه او داناتر است به آن كه پرهيزگارى كرده است.'),
(4817, 53, 33, 'آيا ديدى آن كس را كه [از پذيرفتن حق‌] رويگردان شد؟'),
(4818, 53, 34, 'و اندكى [از مال‌] بداد و دست باز داشت- در انفاق بخل ورزيد-؟'),
(4819, 53, 35, 'آيا نزد او علم غيب است كه خود مى‌بيند- كه تا كى خواهد زيست، كه انفاق نمى‌كند-؟!'),
(4820, 53, 36, 'يا مگر او را خبر نداده‌اند بدانچه در صحيفه‌هاى موسى- يعنى تورات- است؟'),
(4821, 53, 37, 'و [بدانچه در صحيفه‌هاى‌] ابراهيم [است‌] كه وفا كرد- به پيمانهاى الهى-؟:'),
(4822, 53, 38, 'كه هيچ بار بردارى- از گناه- بار [گناه‌] ديگرى را برندارد'),
(4823, 53, 39, 'و اينكه براى آدمى جز آنچه به كوشش خود كرده است نيست'),
(4824, 53, 40, 'و بزودى كوشش او را به وى بنمايند'),
(4825, 53, 41, 'سپس پاداش تمام آن را به او بدهند.'),
(4826, 53, 42, 'و پايان و بازگشت [همگان‌] به سوى پروردگار توست.'),
(4827, 53, 43, 'و اوست كه بخنداند و بگرياند.'),
(4828, 53, 44, 'و اوست كه بميراند و زنده كند.'),
(4829, 53, 45, 'و اوست كه دو گونه: نر و ماده بيافريد،'),
(4830, 53, 46, 'از نطفه‌اى، آنگاه كه در رحم ريخته شود.'),
(4831, 53, 47, 'و بر اوست آفرينش ديگر- آفرينش نو هنگام رستاخيز-.'),
(4832, 53, 48, 'و اوست كه توانگرى دهد و مال بخشد- يا خرسندى و خشنودى دهد-.'),
(4833, 53, 49, 'و اوست خداوند شعرى.'),
(4834, 53, 50, 'و اوست كه عاد نخستين- قوم هود- را هلاك كرد'),
(4835, 53, 51, 'و ثمود- قوم صالح- را كه [از كافران‌] هيچ كس را باز نگذاشت'),
(4836, 53, 52, 'و قوم نوح را پيش از آن، كه ستمكارتر و سركش‌تر بودند.'),
(4837, 53, 53, 'و مؤتفكه- شهرهاى قوم لوط- را واژگون ساخت.'),
(4838, 53, 54, 'پس آنها را فرو پوشانيد آنچه فرو پوشانيد- سنگهايى كه بر آنها بارانيد-.'),
(4839, 53, 55, 'پس [اى آدمى‌] به كدام يك از نعمتهاى پروردگارت شك مى‌آرى و ستيزه مى‌كنى؟'),
(4840, 53, 56, 'اين بيم‌كننده‌اى است از همان بيم كنندگان پيشين.'),
(4841, 53, 57, 'رستاخيز نزديك شد.'),
(4842, 53, 58, 'آن را جز خداى، آشكار كننده‌اى نيست.'),
(4843, 53, 59, 'آيا از اين سخن شگفتى مى‌نماييد؟'),
(4844, 53, 60, 'و مى‌خنديد و نمى‌گرييد؟'),
(4845, 53, 61, 'و شما [از اين سخن‌] غافل و سرگرم باطليد.'),
(4846, 53, 62, 'پس خداى را سجده كنيد و [او را] بپرستيد.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'رستاخيز نزديك شد و ماه بشكافت.'),
(4848, 54, 2, 'و اگر نشانه‌اى- معجزه‌اى- ببينند روى بگردانند و گويند: اين جادويى است نيرومند.'),
(4849, 54, 3, 'و دروغ انگاشتند و پيرو خواهشهاى [دل‌] خويش شدند و هر كارى [به جاى خود] قرار مى‌گيرد- نيكى به نيكان و بدى به بدان-.'),
(4850, 54, 4, 'و هر آينه از خبرها آنچه در آن، مايه [پندگرفتن و] بازايستادن است بديشان آمده است.'),
(4851, 54, 5, '[آن پندها] حكمتى است رسا، ولى بيم‌كنندگان [چون امتهاشان نشنوند و كار نبندند] چه سودى توانند رسانيد؟'),
(4852, 54, 6, 'پس، از آنان روى بگردان [و چشم مى‌دار] روزى را كه آن خواننده [آنان را] به چيزى سخت ناخوش- دوزخ- فرامى‌خواند،'),
(4853, 54, 7, 'چشمهاشان [از هول‌] فرو شده، از گورها بيرون آيند گويى ملخهايى پراكنده‌اند'),
(4854, 54, 8, 'به سوى آن خواننده شتابانند. كافران گويند: اين روزى است دشوار.'),
(4855, 54, 9, 'پيش از اينان قوم نوح تكذيب كردند بنده ما را دروغگو شمردند و گفتند: ديوانه است. و [او از دعوت به حق‌] باز داشته شد- به سبب آزار و سنگ‌زدن و تهديد-.'),
(4856, 54, 10, 'پس پروردگار خويش را بخواند كه من شكست يافته‌ام، پس انتقام گير.'),
(4857, 54, 11, 'پس درهاى آسمان را به آبى ريزان بگشاديم.'),
(4858, 54, 12, 'و زمين را به چشمه‌هاى روان بشكافتيم پس آب [آسمان و زمين‌] بر كارى مقدر فراهم آمد.'),
(4859, 54, 13, 'و او را بر [كشتى‌] داراى تخته‌ها و ميخها سوار كرديم'),
(4860, 54, 14, 'كه به ديدار ما- زير نظر ما- مى‌رفت، تا پاداش كسى باشد كه به او كافر شدند- يعنى نوح'),
(4861, 54, 15, 'و هر آينه آن'),
(4862, 54, 16, 'پس [بنگر كه‌] عذاب و بيم‌دادن من چگونه بود؟'),
(4863, 54, 17, 'و هر آينه قرآن را براى يادآورى و پندگرفتن آسان ساختيم، پس آيا يادآرنده و پندگيرنده‌اى هست؟'),
(4864, 54, 18, 'قوم عاد [هود را] دروغگو انگاشتند پس [بنگر كه‌] عذاب و بيم‌دادن من چگونه بود؟'),
(4865, 54, 19, 'ما بر آنها باد سرد و سخت را در روزى پيوسته شوم فرستاديم.'),
(4866, 54, 20, 'مردمان را برمى‌كند، گويى كه آنها خرمابنهايى بودند از بيخ بركنده شده.'),
(4867, 54, 21, 'پس [بنگر كه‌] عذاب و بيم‌دادن من چگونه بود؟'),
(4868, 54, 22, 'و هر آينه قرآن را براى يادآورى و پندگرفتن آسان ساختيم، پس آيا يادآرنده و پندگيرنده‌اى هست؟'),
(4869, 54, 23, 'قوم ثمود نيز بيم‌كنندگان را دروغگو انگاشتند.'),
(4870, 54, 24, 'و گفتند: آيا يك نفر آدمى از ميان خود- صالح- را پيروى كنيم؟! ما آنگاه در گمراهى و ديوانگى باشيم.'),
(4871, 54, 25, 'آيا از ميان همه ما ياد و پند- سخن و وحى خدا- بر او افكنده شده؟! بلكه او دروغ پردازى خودپسند و برترى‌جوست.'),
(4872, 54, 26, 'فردا خواهند دانست كه دروغ‌پرداز خودپسند كيست.'),
(4873, 54, 27, 'ما ماده‌شترى براى آزمون آنان مى‌فرستيم پس [سرانجام‌] آنها را چشم مى‌دار و شكيبا باش.'),
(4874, 54, 28, 'و آنان را آگاه كن كه آب ميانشان [و ميان ماده‌شتر] بخش‌بندى شده، هر نوبت آبشخور [صاحبش‌] حاضر آيد- يك روز براى آنها و يك روز براى شتر-.'),
(4875, 54, 29, 'پس يار خود را آواز دادند تا [سلاح‌] برگرفت و [ماده‌شتر را] پى كرد- كشت-.'),
(4876, 54, 30, 'پس [بنگر كه‌] عذاب و بيم‌دادن من چگونه بود؟'),
(4877, 54, 31, 'بر آنها تنها يك بانگ سخت فرستاديم، پس همچون كاهى كه صاحب آغل براى گوسفندان آماده مى‌كند- خرد و در هم شكسته- شدند.'),
(4878, 54, 32, 'و هر آينه قرآن را براى يادآورى و پندگرفتن آسان ساختيم، پس آيا يادآرنده و پندگيرنده‌اى هست؟'),
(4879, 54, 33, 'قوم لوط بيم‌كنندگان را دروغگو شمردند.'),
(4880, 54, 34, 'ما بر آنها بادى سنگبار فرستاديم مگر خاندان لوط را كه ايشان را سحرگاهان رهانيديم'),
(4881, 54, 35, 'تا نعمتى باشد از نزد ما اينگونه هر كه را سپاسدار است پاداش مى‌دهيم.'),
(4882, 54, 36, 'و همانا [لوط] آنها را از عذاب سخت ما بيم داد، ولى با آن هشدارها- يا با بيم‌دهندگان- به ستيزه برخاستند.'),
(4883, 54, 37, 'و مهمانانش را از او خواستند- تا به آنان دست‌درازى كنند-، پس چشمانشان را كور كرديم، [و گفتيم:] اينك بچشيد عذاب و هشدارهاى مرا.'),
(4884, 54, 38, 'و همانا بامداد عذابى پايدار بديشان رسيد.'),
(4885, 54, 39, 'پس بچشيد عذاب و هشدارهاى مرا.'),
(4886, 54, 40, 'و همانا قرآن را براى يادآورى و پندگرفتن آسان ساختيم، پس آيا يادآرنده و پندگيرنده‌اى هست؟'),
(4887, 54, 41, 'و هر آينه فرعونيان را هشدارهاى من رسيد.'),
(4888, 54, 42, '[ولى‌] همه نشانه‌هاى- سخنان و معجزه‌هاى- ما را دروغ انگاشتند، پس بگرفتيمشان گرفتن بى‌همتا تواناى نيرومند.'),
(4889, 54, 43, 'آيا كافران شما- اهل مكه- از آنان بهترند يا براى شما در نبشته‌هاى آسمانى امان‌نامه‌اى [از عذاب‌] هست؟!'),
(4890, 54, 44, 'بلكه مى‌گويند: ما گروهى هستيم هميار- يا انتقام گيرنده-.'),
(4891, 54, 45, 'بزودى آن جمع شكست مى‌خورند و پشت‌كرده مى‌گريزند.'),
(4892, 54, 46, 'بلكه وعدگاهشان رستاخيز است و رستاخيز سخت‌تر و تلختر است.'),
(4893, 54, 47, 'بزهكاران در گمراهى و آتش سوزانند.'),
(4894, 54, 48, 'روزى كه آنان را بر رويهاشان در آتش مى‌كشند، [و گويندشان:] بچشيد عذاب سودن دوزخ را'),
(4895, 54, 49, 'همانا ما هر چيزى را به اندازه آفريديم.'),
(4896, 54, 50, 'و فرمان ما نيست جز يكى- يك سخن-، همچون يك چشم بر هم‌زدن.'),
(4897, 54, 51, 'و هر آينه همانندان شما را- كه گمراه بودند- هلاك ساختيم، پس آيا يادآرنده و پندگيرنده‌اى هست؟'),
(4898, 54, 52, 'و هر كارى كه كرده‌اند در نبشته‌ها- نامه‌هاى اعمال- هست.'),
(4899, 54, 53, 'و هر [كار] خرد و كلانى نوشته شده است.'),
(4900, 54, 54, 'همانا پرهيزگاران در بوستانها و [كنار] جويها باشند.'),
(4901, 54, 55, 'در نشستگاهى راستين- بهشت جاودان- نزد پادشاهى توانا- خداى متعال-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'خداى رحمان،'),
(4903, 55, 2, 'قرآن را آموخت- به پيامبر'),
(4904, 55, 3, 'انسان را بيافريد'),
(4905, 55, 4, 'او را سخن‌گفتن آموخت.'),
(4906, 55, 5, 'خورشيد و ماه به حساب [معينى‌] در كارند.'),
(4907, 55, 6, 'و گياه- گياه بى‌ساقه- و درخت سجده مى‌آرند.'),
(4908, 55, 7, 'و آسمان را برافراشت و ترازو را- در ميان بندگان- بنهاد،'),
(4909, 55, 8, 'كه در ترازو از حد مگذريد- تجاوز و خيانت مكنيد-.'),
(4910, 55, 9, 'و سنجش را به داد و انصاف برپا داريد- ترازو را راست داريد- و ترازو را مكاهيد- در سنجيدن و وزن‌كردن با كم‌دادن خيانت مكنيد-.'),
(4911, 55, 10, 'و زمين را براى آدميان بنهاد.'),
(4912, 55, 11, 'در آن ميوه‌هاست و خرمابنهاى غلاف‌دار- كه ميوه‌اش در غلاف است-.'),
(4913, 55, 12, 'و دانه‌هاى با برگ و كاه، و گياهان خوشبو.'),
(4914, 55, 13, 'پس كدام يك از نعمتهاى پروردگارتان را [اى پريان و آدميان،] دروغ مى‌انگاريد؟'),
(4915, 55, 14, 'آدمى را از گِلى خشك مانند سفال- گِل پخته كوزه‌گران- بيافريد.'),
(4916, 55, 15, 'و پريان را از زبانه آتشى بى‌دود آفريد.'),
(4917, 55, 16, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4918, 55, 17, 'خداوند دوخاور و خداوند دو باختر.'),
(4919, 55, 18, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4920, 55, 19, 'آن دو دريا- آب شيرين و آب شور- را راه داد كه به هم رسند.'),
(4921, 55, 20, 'ميان آن دو حائلى است تا از حد نگذرند- به هم نياميزند-.'),
(4922, 55, 21, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4923, 55, 22, 'از آن دو [دريا] مرواريد درشت و خرد بيرون مى‌آيد.'),
(4924, 55, 23, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌پنداريد؟'),
(4925, 55, 24, 'و او راست كشتيهاى روان‌شده- يا بادبان برافراشته- در دريا همچون كوه‌ها- در بزرگى-.'),
(4926, 55, 25, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4927, 55, 26, 'هر كه بر روى آن'),
(4928, 55, 27, 'و ذات پروردگار تو، آن خداوند شكوه و ارجمندى، بماند.'),
(4929, 55, 28, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4930, 55, 29, 'هر كه در آسمانها و زمين است از او [حاجت‌] مى‌خواهند هر روز وى در كارى است.'),
(4931, 55, 30, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4932, 55, 31, 'اى پريان و آدميان، زودا كه به [حساب‌] شما پردازيم.'),
(4933, 55, 32, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌پنداريد؟'),
(4934, 55, 33, 'اى گروه پريان و آدميان، اگر توانيد از مرزهاى آسمانها و زمين بگذريد- تا از حكم و قضاى من بگريزيد- پس بگذريد، نتوانيد گذشت مگر به توان و نيرويى- كه آن را نداريد-.'),
(4935, 55, 34, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4936, 55, 35, 'بر شما پاره‌اى از آتش و دودى- يا مسى گداخته- فرستاده شود پس دفاع از يكديگر نتوانيد.'),
(4937, 55, 36, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4938, 55, 37, 'پس آنگاه كه آسمان شكافته شود، و همچون اديم سرخ‌فام گردد'),
(4939, 55, 38, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4940, 55, 39, 'پس در آن روز هيچ آدمى و پرى را از گناهش نپرسند- زيرا رخساره هر كس نماينده حال اوست-.'),
(4941, 55, 40, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4942, 55, 41, 'بزهكاران به نشان رويشان- سياه رويى- شناخته شوند، پس به موهاى پيشانى و پاهاشان گرفته شوند.'),
(4943, 55, 42, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4944, 55, 43, 'اين است دوزخى كه بزهكاران دروغش مى‌پنداشتند'),
(4945, 55, 44, 'ميان دوزخ و ميان آب جوشان سوزان مى‌گردند.'),
(4946, 55, 45, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4947, 55, 46, 'و براى كسى كه از ايستادن در پيشگاه پروردگار خويش- جايگاه حساب- بترسد دو بهشت است.'),
(4948, 55, 47, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4949, 55, 48, 'كه پُر از شاخسارهايند- يا: داراى ميوه‌هاى گوناگون‌اند-.'),
(4950, 55, 49, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌پنداريد؟'),
(4951, 55, 50, 'در آنها، دو چشمه روان است.'),
(4952, 55, 51, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4953, 55, 52, 'در آنها، از هر ميوه‌اى دو گونه است.'),
(4954, 55, 53, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4955, 55, 54, 'بر بالشها و بسترهايى تكيه زنند كه آستر آنها از ديباى ستبر است، و ميوه‌هاى آن دو بهشت نزديك و در دسترس است.'),
(4956, 55, 55, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4957, 55, 56, 'در آنها زنان فروهشته چشم باشند كه پيش از ايشان- بهشتيان- هيچ آدمى و پرى آنان را نسوده باشد.'),
(4958, 55, 57, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌پنداريد؟'),
(4959, 55, 58, 'گويى كه ايشان بسان ياقوت و مرجان‌اند- در سرخى و روشنى و سپيدى و درخشش-.'),
(4960, 55, 59, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4961, 55, 60, 'آيا پاداش نيكوكارى جز نيكى‌كردن است؟'),
(4962, 55, 61, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4963, 55, 62, 'و فروتر از آنها- نزديك آن دو بهشت- دو بهشت ديگر هست.'),
(4964, 55, 63, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4965, 55, 64, 'آنچنان سبز كه به سياهى زند- چنان پُردرخت است كه سبز سير مى‌نمايد.'),
(4966, 55, 65, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌پنداريد؟'),
(4967, 55, 66, 'در آنها دو چشمه جوشنده هست.'),
(4968, 55, 67, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4969, 55, 68, 'در آنها [درختان‌] ميوه و خرمابن و [درخت‌] انار هست.'),
(4970, 55, 69, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4971, 55, 70, 'در آنها زنان گزيده نيكو و زيبا هستند.'),
(4972, 55, 71, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4973, 55, 72, 'حورانى- سياه چشمانى- دور از چشم بيگانگان در سراپرده‌ها هستند.'),
(4974, 55, 73, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌پنداريد؟'),
(4975, 55, 74, 'كه پيش از ايشان هيچ آدمى و پرى آنان را نسوده است.'),
(4976, 55, 75, 'پس كدام يك از نعمتهاى پروردگارتان را دروغ مى‌انگاريد؟'),
(4977, 55, 76, 'بر بالشهاى سبز و فرشها و بسترهاى گرانمايه و نيكو تكيه زنند.'),
(4978, 55, 77, 'پس كدام يك از نعمتهاى پروردگارتان را انكار مى‌كنيد؟'),
(4979, 55, 78, 'بزرگ و والاست نام پروردگار تو، خداوند شكوه و ارجمندى.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'آنگاه كه آن رخداد- رستاخيز- رخ دهد'),
(4981, 56, 2, 'كه در رخدادش دروغى نيست'),
(4982, 56, 3, 'فرونهنده است و بردارنده.'),
(4983, 56, 4, 'آنگاه كه زمين سخت جنبانده شود'),
(4984, 56, 5, 'و كوه‌ها خرد و ريز ريز شوند'),
(4985, 56, 6, 'پس چون گرد و غبار پراكنده گردند.'),
(4986, 56, 7, 'و شما سه گروه باشيد:'),
(4987, 56, 8, 'خجستگان و سعادتمندان، خجستگان چه باشند؟'),
(4988, 56, 9, 'و شومان و بدبختان، شومان و بدبختان چه باشند؟'),
(4989, 56, 10, 'و پيشى‌گيرندگان- به ايمان و جهاد و طاعت-، آن پيشى‌گيرندگان'),
(4990, 56, 11, 'ايشانند نزديك‌داشتگان'),
(4991, 56, 12, 'در بهشتهاى پُر نعمت'),
(4992, 56, 13, 'بسيارى از پيشينيان‌اند- از امّتهاى پيامبران پيشين-'),
(4993, 56, 14, 'و اندكى از پسينيان- امت رسول اللّه'),
(4994, 56, 15, 'بر تختهاى بافته- به زر و گوهر و مرواريد-،'),
(4995, 56, 16, 'روياروى هم بر آنها تكيه زده‌اند.'),
(4996, 56, 17, 'پسرانى هماره نوجوان [به خدمت‌] پيرامون ايشان مى‌گردند،'),
(4997, 56, 18, 'با صراحى‌ها و كوزه‌ها و جامهايى از مىْ صافى گوارا،'),
(4998, 56, 19, 'كه از [نوشيدن‌] آن سر درد نگيرند و مست و بى‌خرد نشوند.'),
(4999, 56, 20, 'و از هر ميوه‌اى كه برگزينند.'),
(5000, 56, 21, 'و از گوشت مرغانى كه بخواهند.'),
(5001, 56, 22, 'و دوشيزگانى با چشمهاى سياه و درشت،'),
(5002, 56, 23, 'همچون مرواريد پوشيده در صدف- كه هوا در آن اثر نكند و صفا و روشنى‌اش بر جاى باشد-'),
(5003, 56, 24, 'به پاداش آنچه مى‌كردند.'),
(5004, 56, 25, 'در آنجا سخن بيهوده و گنه‌آلود نشنوند.'),
(5005, 56, 26, 'مگر گفتارى: سلام، سلام.'),
(5006, 56, 27, 'و ياران دست راست- خجستگان و سعادتمندان-، دست راستيان چه باشند؟'),
(5007, 56, 28, 'در [زير] كنارهايى بى‌خار،'),
(5008, 56, 29, 'و درخت موزى كه ميوه‌اش بر هم چيده باشد،'),
(5009, 56, 30, 'و سايه‌اى كشيده و پيوسته،'),
(5010, 56, 31, 'و آبى هماره روان،'),
(5011, 56, 32, 'و ميوه‌اى بسيار،'),
(5012, 56, 33, 'نه بريده- قطع نشود- و نه بازداشته- از خوردن-.'),
(5013, 56, 34, 'و فرشهايى برافراشته- بر تختهاى بلند افكنده‌شده، يا گرانبها-.'),
(5014, 56, 35, 'ما ايشان را آفريده‌ايم، آفرينشى نو- يا: آفرينشى ويژه كه هرگز دگرگونى نيابد، پير نشوند و زيبايى خود را از دست ندهند-.'),
(5015, 56, 36, 'و آنان را دوشيزگانى ساخته‌ايم،'),
(5016, 56, 37, 'شوى‌دوستانى همسال- با يكديگر و با شوهران-،'),
(5017, 56, 38, 'براى ياران دست راست- خجستگان و سعادتمندان-.'),
(5018, 56, 39, 'كه گروهى از پيشينيانند،'),
(5019, 56, 40, 'و گروهى از پسينيان.'),
(5020, 56, 41, 'و ياران دست چپ- شومان و بدبختان-، دست چپيان چه باشند؟'),
(5021, 56, 42, 'در آتشين بادى سوزان و آبى جوشان،'),
(5022, 56, 43, 'و سايه‌اى از دودى سخت سياه،'),
(5023, 56, 44, 'نه سرد- زيرا از دود دوزخ است- و نه خوش.'),
(5024, 56, 45, 'زيرا كه اينان پيش از اين- عذاب- سرمست از كامرانى بودند.'),
(5025, 56, 46, 'و بر گناه بزرگ- شرك، يا پيمان‌شكنى، يا سوگند بزرگ بر انكار بعث- پاى مى‌فشردند،'),
(5026, 56, 47, 'و مى‌گفتند: آيا چون بميريم و خاك و استخوان شويم باز زنده و برانگيخته مى شويم؟'),
(5027, 56, 48, 'و آيا پدران پيشين ما نيز [زنده و برانگيخته مى‌شوند]؟'),
(5028, 56, 49, 'بگو: هر آينه پيشينيان و پسينيان،'),
(5029, 56, 50, '[همگان‌] به هنگام آن روز دانسته شده گردآورى و فراهم مى‌شوند.'),
(5030, 56, 51, 'و آنگاه شما اى گمراهان دروغ‌انگار،'),
(5031, 56, 52, 'هر آينه از درخت زقّوم مى‌خوريد،'),
(5032, 56, 53, 'و شكمها را از آن پُر مى‌كنيد،'),
(5033, 56, 54, 'آنگاه روى آن، از آب جوشان مى‌آشاميد،'),
(5034, 56, 55, 'و چنان آشامنده‌ايد كه شتران تشنه مى‌آشامند.'),
(5035, 56, 56, 'اين است پذيرائى آنها در روز حساب و پاداش.'),
(5036, 56, 57, 'ما شما را آفريده‌ايم- و از آينده شما باخبريم-، پس چرا باور نمى‌داريد؟'),
(5037, 56, 58, 'آيا آب منى را كه مى‌ريزيد- نطفه‌اى را كه در رحم زنان مى‌افكنيد- ديده‌ايد؟'),
(5038, 56, 59, 'آيا شما آن را مى‌آفرينيد- نقشبندى مى‌كنيد- يا ما آفريننده‌ايم؟'),
(5039, 56, 60, 'ما مرگ را ميان شما مقدّر كرديم، و كسى بر ما پيشى نگرفته و ناتوان نيستيم،'),
(5040, 56, 61, 'از آنكه به جاى شما ديگرانى همچون شما آوريم. و شما را در آنچه- در صورت و هيئتى يا در عالمى كه- نمى‌دانيد از نو بيافرينيم.'),
(5041, 56, 62, 'و هر آينه شما آفرينش نخست- زندگى دنيوى- را دانسته‌ايد، پس چرا ياد نمى‌كنيد و پند نمى‌گيريد؟'),
(5042, 56, 63, 'آيا آنچه مى‌كاريد ديده‌ايد؟'),
(5043, 56, 64, 'آيا شما آن را مى‌رويانيد يا ما روياننده‌ايم؟'),
(5044, 56, 65, 'اگر بخواهيم آن را گياهى در هم شكسته- يا كاهى بى‌دانه- مى‌گردانيم پس سراسيمه و در شگفت بمانيد،'),
(5045, 56, 66, '[و گوييد:] راستى كه ما زيان كرده و تاوان زده‌ايم.'),
(5046, 56, 67, 'بلكه ما بى‌بهره مانده‌ايم.'),
(5047, 56, 68, 'آيا آبى را كه مى‌آشاميد ديده‌ايد؟'),
(5048, 56, 69, 'آيا شما آن را از ابر فرو آورده‌ايد يا ما فروآرنده‌ايم؟'),
(5049, 56, 70, 'اگر بخواهيم آن را تلخ و شور مى‌گردانيم پس چرا سپاس نمى‌گزاريد؟'),
(5050, 56, 71, 'آيا آتشى را كه مى‌افروزيد ديده‌ايد؟'),
(5051, 56, 72, 'آيا درخت آن را شما پديد آورده‌ايد يا ما پديدآرنده‌ايم؟'),
(5052, 56, 73, 'ما آن را يادكردى ساختيم- كه چون آن را ببينند آتش دوزخ را ياد كنند- و كالايى براى صحرانشينان.'),
(5053, 56, 74, 'پس نام پروردگار بزرگ خود را به پاكى ياد كن.'),
(5054, 56, 75, 'پس سوگند به فروشدنگاه- يا جايگاه- ستارگان'),
(5055, 56, 76, 'و همانا اين سوگندى است بزرگ، اگر بدانيد'),
(5056, 56, 77, 'كه هر آينه اين قرآنى است گرامى و ارجمند،'),
(5057, 56, 78, 'در كتابى پوشيده و نگاه‌داشته- لوح محفوظ-'),
(5058, 56, 79, 'كه جز پاك‌شدگان و پاكيزگان به آن دست نمى‌رسانند،'),
(5059, 56, 80, 'فروفرستاده‌اى است از پروردگار جهانيان.'),
(5060, 56, 81, 'پس آيا به اين سخن بى‌اعتنايى و سهل‌انگارى مى‌كنيد؟!'),
(5061, 56, 82, 'و روزى- بهره- خود را [از آن‌] اين مى‌سازيد كه [آن را] دروغ انگاريد؟!'),
(5062, 56, 83, 'پس چرا چون [جان‌] به گلوگاه رسد- هنگام مرگ-،'),
(5063, 56, 84, 'و شما آن هنگام مى‌نگريد- به آن كه در حال جان‌دادن است-'),
(5064, 56, 85, 'در حالى كه ما به او از شما نزديكتريم، ولى شما نمى‌بينيد'),
(5065, 56, 86, 'پس چرا اگر شما [برانگيختنى و] جزادادنى نيستيد- يعنى اگر رستاخيز را باور نداريد-،'),
(5066, 56, 87, 'او را باز نمى‌گردانيد، اگر راست مى‌گوييد؟!'),
(5067, 56, 88, 'و اما اگر از نزديك‌داشتگان- مقربان- باشد،'),
(5068, 56, 89, 'پس آسانى- هنگام مرگ و پس از آن در برزخ و رستاخيز- و روزى جاودانى- يا گياه خوشبو- و بهشت پُر نعمت [فراروى اوست‌].'),
(5069, 56, 90, 'و اما اگر از ياران دست راست- اهل سعادت- باشد،'),
(5070, 56, 91, 'پس تو را از ياران دست راست سلام است.'),
(5071, 56, 92, 'و اما اگر از دروغ‌انگاران گمراه باشد،'),
(5072, 56, 93, 'پس پذيرايى [او] از آبى جوشان است.'),
(5073, 56, 94, 'و به دوزخ در آمدن و سوختن.'),
(5074, 56, 95, 'هر آينه اين، آن سخن راست بى‌گمان و درست است.'),
(5075, 56, 96, 'پس نام پروردگار بزرگت را به پاكى ياد كن.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'هر چه در آسمانها و زمين است خداى را به پاكى مى‌ستايد، و اوست تواناى بى‌همتا و داناى با حكمت.'),
(5077, 57, 2, 'او راست پادشاهى آسمانها و زمين، زنده كند و بميراند، و او بر هر چيزى تواناست.'),
(5078, 57, 3, 'اوست اول و آخر- پيش از هر چيز بوده و پس از هر چيز هم باشد- و پيدا- به هستى- و نهان- به چگونگى-، و او به همه چيز داناست.'),
(5079, 57, 4, 'اوست كه آسمانها و زمين را در شش روز- شش دوره- بيافريد، سپس بر عرش- مقام فرمانروايى و تدبير امور- برآمد. آنچه در زمين فرو رود و آنچه از آن بيرون آيد، و هر چه از آسمان فرود آيد و هر چه در آن بالا رود، همه را مى‌داند، و او هر جا كه باشيد با شماست، و خدا بدانچه مى‌كنيد بيناست.'),
(5080, 57, 5, 'او راست پادشاهى آسمانها و زمين، و كارها همه به خداى باز مى‌گردد.'),
(5081, 57, 6, 'شب را در روز درمى‌آورد و روز را در شب- اشاره به كاهش و افزايش شب و روز- و او بدانچه در سينه‌هاست- از نيتها و قصدها- داناست.'),
(5082, 57, 7, 'به خدا و پيامبرش ايمان آريد، و از آنچه شما را در آن جانشين [گذشتگان‌] گردانيده- اموالتان- انفاق كنيد، پس كسانى از شما كه ايمان آورده و انفاق كرده‌اند مزدى بزرگ دارند.'),
(5083, 57, 8, 'و شما را چيست كه به خداى ايمان نمى‌آوريد- ايمانى كه آثار آن، از جمله انفاق مال، نمودار باشد- و حال آنكه پيامبر شما را مى‌خواند تا به پروردگارتان ايمان بياوريد؟! و هر آينه [خداوند] از شما پيمان گرفته است- بر اقرار بر ربوبيت و وحدانيت خود-، اگر باوردارنده‌ايد.'),
(5084, 57, 9, 'اوست آن كه بر بنده خود آيات روشن و هويدا فرومى‌فرستد تا شما را از تاريكيها به روشنايى در آورد، و هر آينه خداوند به شما رؤوف و مهربان است.'),
(5085, 57, 10, 'و شما را چيست- چه عذر داريد- كه در راه خدا- هنگام جهاد- هزينه نمى‌كنيد و حال آنكه خداى راست ميراث آسمانها و زمين؟! كسانى از شما كه پيش از فتح- فتح مكه يا صلح حديبيه- انفاق و كارزار كرده‌اند [با ديگران‌] برابر نيستند، پايه ايشان بزرگتر است از آنان كه پس از آن'),
(5086, 57, 11, 'كيست كه خداى را وام دهد وامى نيكو تا او را دوچندان- يا چند برابر- باز دهد و او را مزدى گرانمايه باشد'),
(5087, 57, 12, 'در روزى كه مردان و زنان مؤمن را ببينى كه نورشان در پيش ايشان و از راست ايشان شتابان مى‌رود- مى‌درخشد تا به آسانى بگذرند-. امروز شما را مژدگان بهشتهايى است كه از زير [درختان‌] آنها جويها روان است در حالى كه در آنجا جاويدان باشيد اين است كاميابى و رستگارى بزرگ.'),
(5088, 57, 13, 'روزى كه مردان و زنان منافق به كسانى كه ايمان آورده‌اند گويند: به ما بنگريد- يا درنگ كنيد- تا از نور شما [روشنايى‌] فرا گيريم. گفته شود- مؤمنان يا فرشتگان گويند-: به پشت سر خويش- يعنى به دنيا- بازگرديد و [از آنجا] روشنايى بجوييد- يعنى روشنايى را بايد از دنيا با خود آورد-. پس ميانشان ديوارى برآرند كه آن را درى باشد در اندرون آن رحمت- بهشت- است و بيرون آن از پيشش عذاب- دوزخ- است.'),
(5089, 57, 14, '[منافقان‌] ايشان'),
(5090, 57, 15, 'پس امروز از شما و نه از كسانى كه كافر شدند بازخريدى نستانند. جايگاهتان آتش دوزخ است. آن به شما سزاوارتر و نزديكتر است، و بد بازگشتگاهى است.'),
(5091, 57, 16, 'آيا كسانى را كه ايمان آورده‌اند هنگام آن نرسيده است كه دلهاى ايشان از ياد خدا و آنچه از سخن راست- وحى الهى- فروآمده نرم و ترسان شود؟ و مانند كسانى نباشند كه پيش از اين به آنها كتاب- تورات- داده شد، پس زمان بر آنان دراز گشت- مدتى گذشت- آنگاه دلهاشان سخت [و تاريك‌] شد، و بسياريشان نافرمان شدند.'),
(5092, 57, 17, 'بدانيد كه خدا زمين را پس از مردگى‌اش زنده مى‌كند- همچنان دلهاى مرده و سخت را زنده و نرم مى‌گرداند-. ما نشانه‌ها [ى قدرت خود] را براى شما روشن كرديم شايد خرد را كار بنديد.'),
(5093, 57, 18, 'همانا مردان و زنان صدقه‌دهنده و آنان كه خداى را وام دادند وامى نيكو، به آنان دوچندان- افزون- بازداده شود و ايشان مزدى گرانمايه دارند.'),
(5094, 57, 19, 'و كسانى كه به خدا و پيامبرانش ايمان آورده‌اند، ايشانند راستى‌پيشگان و شهيدان- يا گواهان بر امتها- نزد پروردگارشان آنان راست پاداش و نورشان. و كسانى كه كافر شدند و آيات ما را دروغ انگاشتند اينان دوزخيانند.'),
(5095, 57, 20, 'بدانيد كه زندگانى دنيا بازى و بيهودگى و آرايش و فخركردن با يكديگر و نازيدن- يا افزون‌جويى- در مالها و فرزندان است- همچون بارانى كه گياه رويانيدنش كشاورزان را خوش آيد و به شگفت آرد، سپس پژمرده شود و آن را زرد بينى و آنگاه خشك و شكسته و خرد گردد- و در آن جهان عذابى سخت و نيز آمرزشى از سوى خدا و خشنودى اوست. و زندگانى اين جهان جز كالاى فريبندگى نيست.'),
(5096, 57, 21, 'به سوى آمرزشى از پروردگارتان و بهشتى كه پهناى آن- يعنى وسعت و فراخى آن- همچون پهناى آسمان و زمين است [و] براى كسانى كه به خداى و پيامبرانش ايمان آورده‌اند آماده شده، [بر يكديگر] پيشى گيريد. اين فزون‌بخشى خداست كه آن را به هر كه خواهد مى‌دهد، و خدا داراى فزون‌بخشى بزرگ است.'),
(5097, 57, 22, 'هيچ مصيبتى در زمين- چون تنگدستى و سختى و قحطى- و نه در جانهاتان- چون بيمارى و اندوه- نرسد مگر پيش از آنكه بيافرينمش در نبشته‌اى است اين بر خدا آسان است'),
(5098, 57, 23, 'تا بر آنچه از دست شما رفت اندوه مخوريد و بدانچه شما را داد شادمان نشويد، و خدا هيچ گردنكش خودستا را دوست ندارد،'),
(5099, 57, 24, 'آنان كه بخل مى‌ورزند و مردم را به بخل فرمان مى‌دهند. و هر كه روى بگرداند- از ايمان و انفاق-، پس [بداند كه‌] خداست بى‌نياز و ستوده.'),
(5100, 57, 25, 'هر آينه پيامبرانمان را با حجتهاى روشن و هويدا فرستاديم و با ايشان كتاب- كه وسيله تمييز حق از باطل است- و ترازو فرو فرستاديم تا مردم به داد و انصاف برخيزند و آهن را فرو آورديم كه در آن نيرويى سخت- سلاح و وسايل كارزار و مانند آن- و سودهايى [ديگر] براى مردم است، و تا خدا كسى را كه او و پيامبرانش را به ناديده يارى مى‌كند بازشناسد. همانا خدا نيرومند و تواناى بى‌همتاست.'),
(5101, 57, 26, 'و هر آينه نوح و ابراهيم را فرستاديم و در ميان فرزندانشان پيامبرى و كتاب نهاديم. پس برخى‌شان رهيافته بودند و بسياريشان از فرمان [حق‌] بيرون شدند.'),
(5102, 57, 27, 'سپس از پى ايشان- پيامبرانى كه ياد كرديم- پيامبران خود را فرستاديم، و عيسى پسر مريم را از پى درآورديم و به او انجيل داديم، و در دلهاى كسانى كه از او پيروى كردند مهربانى و نرمى نهاديم و رهبانيتى- گوشه‌گيرى- كه از خود ساختند، ما آن را بر آنان ننوشتيم- مقرر نكرديم- جز [آنكه آن را] براى جستن خشنودى خداى [اختراع كردند]، ولى آن را آنسان كه سزاوار است نگاه نداشتند، پس مزد كسانى از آنان را كه ايمان آوردند داديم، و بسيارى از آنها نافرمان بودند.'),
(5103, 57, 28, 'اى كسانى كه ايمان آورده‌ايد، از خداى پروا كنيد و به پيامبر او ايمان آوريد تا شما را از بخشايش خود دو بهره دهد، و براى شما نورى قرار دهد كه بدان راه [راست‌] رويد و شما را بيامرزد، و خدا آمرزگار و مهربان است.'),
(5104, 57, 29, 'تا اهل كتاب- جهودان و ترسايان- بدانند كه بر هيچ چيز از فضل خدا- مانند پيامبرى- توانايى ندارند، و افزونى و بخشش به دست خداست، به هر كه خواهد مى‌دهد. و خدا، داراى فزون‌بخشى بزرگ است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'هر آينه خدا سخن آن زن را كه در باره شوى خود با تو گفت‌وگو و چون و چرا مى‌كرد و به خداى ناله و شكايت مى‌نمود شنيد، و خداوند گفت و شنود شما را مى‌شنيد. همانا خدا شنوا و بيناست.'),
(5106, 58, 2, 'كسانى از شما كه آنان خويش را ظهار كنند آن زنان مادرانشان نيستند. مادرانشان جز زنانى كه آنان را زاده‌اند نباشند و هر آينه آنان سخنى ناشايست و دروغ مى‌گويند، و خدا درگذرنده و آمرزگار است.'),
(5107, 58, 3, 'و كسانى كه زنان خويش را ظهار مى‌كنند و سپس از آنچه گفته‌اند برمى‌گردند- تا آن را بشكنند و به همسر خود بازگردند- پيش از آنكه به هم رسند آزادكردن برده‌اى بايد. اين است كه بدان پند داده مى‌شويد، و خداى بدانچه مى‌كنيد آگاه است.'),
(5108, 58, 4, 'و هر كه [برده‌] نيابد- يا نتواند-، پس روزه‌داشتن دو ماه پياپى بايد، پيش از آنكه به هم رسند و هر كه نتواند، پس طعام‌دادن شصت مستمند ببايد. اين براى آن است كه به خدا و پيامبرش ايمان آوريد- و دست از احكام جاهليت برداريد- و اين مرزهاى- احكام- خداست، و كافران را عذابى است دردناك.'),
(5109, 58, 5, 'همانا كسانى كه با خداى و پيامبر او دشمنى و مخالفت مى‌كنند خوار و نگونسار شوند چنانكه كسانى كه پيش از آنان بودند خوار و نگونسار شدند. و ما آياتى روشن و هويدا فروفرستاديم و كافران را عذابى است خواركننده'),
(5110, 58, 6, 'در روزى كه خداوند همه آنان را بر انگيزد آنگاه بدانچه كرده‌اند آگاهشان سازد، كه خدا آن'),
(5111, 58, 7, 'آيا نديده‌اى- ندانسته‌اى- كه خدا آنچه را در آسمانها و آنچه را در زمين است مى‌داند؟ راز گفتن هيچ سه تنى نيست مگر آنكه او چهارم ايشان است، و نه هيچ پنج تنى مگر آنكه او ششم ايشان باشد و نه كمتر از آن و نه بيشتر از آن مگر آنكه او با آنهاست هر جا كه باشند، سپس در روز رستاخيز آنان را بدانچه كرده‌اند آگاه مى‌كند، كه خدا به همه چيز داناست.'),
(5112, 58, 8, 'آيا به كسانى- جهودان و منافقان- ننگريستى كه از رازگفتن بازداشته شدند و سپس بدانچه بازداشته شدند بازمى‌گردند و در باره گناه و ستم و نافرمانى پيامبر با يكديگر راز مى‌گويند، و چون نزد تو آيند تو را درود گويند نه بدان گونه كه خداى درود گفته است، و در دل خود مى‌گويند: چرا خدا ما را بدانچه مى‌گوييم عذاب نمى كند؟! بس است آنها را دوزخ كه بدان در آيند، و بد بازگشتگاهى است.'),
(5113, 58, 9, 'اى كسانى كه ايمان آورده‌ايد، چون با يكديگر راز گوييد رازى مگوييد كه در آن گناه و ستم و نافرمانى پيامبر باشد، و با يكديگر به نيكوكارى و پرهيزگارى راز گوييد. و از خدايى كه به سوى او برانگيخته و فراهم مى‌شويد پروا كنيد.'),
(5114, 58, 10, 'جز اين نيست كه راز گفتن- راز بد و بيهوده- از شيطان است تا كسانى را كه ايمان آورده‌اند اندوهگين كند و حال آنكه جز به اذن- علم و خواست- خدا هيچ زيانى به ايشان نمى‌رساند. و مؤمنان بايد بر خدا توكل كنند.'),
(5115, 58, 11, 'اى كسانى كه ايمان آورده‌ايد، چون شما را گويند كه در مجلسها- مجالسى كه با پيامبر'),
(5116, 58, 12, 'اى كسانى كه ايمان آورده‌ايد، چون [خواهيد كه‌] با پيامبر راز گوييد، پيش از رازگفتن خود صدقه‌اى بدهيد. اين براى شما بهتر و پاكتر است. پس اگر [صدقه‌اى‌] نيافتيد، همانا خدا آمرزگار و مهربان است- مى‌توانيد بى‌صدقه راز گوييد-.'),
(5117, 58, 13, 'آيا ترسيديد كه پيش از رازگفتن خود صدقه‌ها بدهيد؟! پس چون نكرديد- يعنى نتوانستيد كرد و از دل و دستتان برنيامد- و خدا هم [به بخشايش خويش‌] بر شما بازگشت- اين حكم را برداشت-، پس نماز را برپا داريد و زكات بدهيد و خدا و پيامبر او را فرمان بريد و خدا بدانچه مى‌كنيد آگاه است.'),
(5118, 58, 14, 'آيا به كسانى ننگريستى كه با مردمى كه خدا بر آنها خشم گرفته است- يعنى جهودان- دوستى داشتند؟ اينان- منافقان- نه از شمايند و نه از آنها، و به دروغ سوگند مى‌خورند [كه ما با شماييم‌] و خود مى‌دانند [كه دروغ مى‌گويند]،'),
(5119, 58, 15, 'خداى برايشان عذابى سخت آماده ساخته است، زيرا كه بد است آنچه مى‌كردند.'),
(5120, 58, 16, 'سوگندهاشان را سپر گرفته‌اند و [مردم را] از راه خدا بگردانيده‌اند، پس آنان راست عذابى خواركننده.'),
(5121, 58, 17, 'مالها و فرزندانشان آنان را در برابر [عذاب‌] خدا هيچ سود ندارد- عذاب خدا را از آنها باز ندارد-، اينان دوزخيانند، در آن جاودانه‌اند'),
(5122, 58, 18, 'روزى كه خدا همه آنان- منافقان- را برانگيزد، پس براى خدا سوگند مى‌خورند چنانكه [امروز] براى شما سوگند مى‌خورند، و مى‌پندارند كه بر چيزى دست دارند- يعنى از سوگند خود سودى مى‌برند- آگاه باشيد كه آنها دروغگويانند.'),
(5123, 58, 19, 'شيطان بر آنان دست يافته است، پس يادكرد خداى را از يادشان برده است. اينان گروه شيطانند آگاه باشيد كه گروه شيطان زيانكارند.'),
(5124, 58, 20, 'همانا كسانى كه با خداى و پيامبرش دشمنى و مخالفت مى‌كنند آنان در ميان خوارترين [مردمان‌] اند.'),
(5125, 58, 21, 'خداى نوشته- حكم كرده- است كه من و فرستادگانم البته پيروز خواهيم شد همانا خدا نيرومند و تواناى بى‌همتاست.'),
(5126, 58, 22, 'مردمى را نيابى كه به خداى و روز واپسين ايمان آورند در حالى كه با كسانى كه با خداى و پيامبرش دشمنى و مخالفت كرده‌اند دوستى بدارند اگر چه پدران يا پسران يا برادران يا خويشانشان باشند، اينانند كه [خداوند] ايمان را در دلهاشان نوشته- پايدار ساخته- است و ايشان را به روحى از نزد خويش نيرومند گردانيده و به بهشتهايى در آورد كه از زير آنها جويها روان است، در آنجا جاويدانند خدا از آنها خشنود است و ايشان از خدا خشنودند. ايشانند گروه خدا آگاه باشيد كه گروه خدا رستگارانند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'آنچه در آسمانها و آنچه در زمين است خداى را به پاكى مى‌ستايند، و اوست تواناى بى‌همتا و داناى با حكمت.'),
(5128, 59, 2, 'اوست آن كه كسانى از اهل كتاب- جهودان بنى النضير- را كه كفر ورزيدند در نخستين بيرون‌راندن دسته‌جمعى از خانه‌شان بيرون راند. شما گمان نداشتيد كه [به اين آسانى‌] بيرون روند و خود پنداشتند كه باروهاشان بازدارنده آنها از [فرمان‌] خداست، پس [فرمان‌] خداى از آنجا كه گمان نمى‌بردند بديشان آمد- گرفتارشان كرد- و در دلهاشان بيم افكند، خانه‌هاى خويش را به دستهاى خودشان و دستهاى مؤمنان ويران مى‌كردند، پس اى خداوندان بينش، عبرت گيريد.'),
(5129, 59, 3, 'و اگر نه اين بود كه خدا بيرون‌شدن از خانمان- جلاى وطن- را بر آنان نوشته بود هر آينه در اين جهان عذابشان مى‌كرد، و آنها را در آن جهان عذاب دوزخ است.'),
(5130, 59, 4, 'اين [عذاب‌] به كيفر آنست كه با خداى و پيامبرش دشمنى و مخالفت ورزيدند، و هر كه با خداى دشمنى و مخالفت ورزد پس [بداند كه‌] خدا سخت كيفر است.'),
(5131, 59, 5, 'آنچه از درختان خرما بريديد يا آنها را ايستاده بر پاى خود واگذاشتيد همه به خواست و فرمان خدا بود [تا شما را يارى دهد و پيروز گرداند] و تا بدكاران نافرمان- جهودان- را خوار و رسوا كند.'),
(5132, 59, 6, 'و آنچه خداى از [مال و زمين‌] آنها- جهودان- به پيامبر خود باز گردانيد- يعنى به غنيمت به وى ارزانى داشت- شما هيچ اسبى و شترى بر آن نتاخته بوديد- از اين رو حقى در آن نداريد-، و ليكن خداست كه پيامبران خود را بر هر كه خواهد چيره مى‌گرداند، و خدا بر هر چيزى تواناست.'),
(5133, 59, 7, 'آنچه خدا از [مال و زمين‌] مردم آباديها به پيامبرش باز گردانيد از آن خدا و پيامبر و خويشاوندان و يتيمان و مستمندان و در راه‌ماندگان- همه از ذريه پيامبر- است تا ميان توانگرانتان دست‌گردان نباشد. و آنچه را پيامبر به شما داد بگيريد و از آنچه شما را بازداشت باز ايستيد و از خدا پروا كنيد، كه خدا سخت كيفر است.'),
(5134, 59, 8, '[و نيز آن غنيمت‌] براى نيازمندان هجرت‌كننده است كه از خانه‌ها و مالهاى خود بيرون رانده شدند در حالى كه فضل- روزى- خداى و خشنودى او را مى‌جويند و [دين‌] خداى و پيامبر او را يارى مى‌كنند، ايشانند راستگويان'),
(5135, 59, 9, 'و نيز كسانى راست كه در سراى هجرت و ايمان- يعنى مدينه- پيش از ايشان جاى گرفته‌اند- يعنى انصار كه در ديار خود ايمان آوردند-، هر كه را كه به سوى آنان هجرت كرده است دوست مى‌دارند و در سينه‌هاى خويش از آنچه به آنان'),
(5136, 59, 10, 'و نيز كسانى راست كه پس از آنان آمدند- پس از فتح مكه مهاجرت كردند- مى‌گويند: پروردگارا، ما و آن برادران ما را كه به ايمان بر ما پيشى گرفته اند بيامرز، و در دلهاى ما بدخواهى كسانى را كه ايمان آورده‌اند منه پروردگارا، همانا تو رؤوف و مهربانى.'),
(5137, 59, 11, 'آيا به كسانى كه نفاق ورزيدند ننگريستى كه به برادران كافرشان از اهل كتاب- جهودان- مى‌گفتند: اگر شما را بيرون كردند هر آينه با شما بيرون مى آييم و در باره شما هيچ كس را فرمان نمى‌بريم و اگر با شما جنگ كردند بى‌گمان شما را يارى مى‌كنيم و خدا گواهى مى‌دهد كه آنان دروغگويند.'),
(5138, 59, 12, 'اگر بيرونشان كنند، با آنان بيرون نشوند و اگر با آنها كارزار كنند ياريشان ندهند و اگر هم به ياريشان بيايند هر آينه پشت كرده برگردند- بگريزند- و آنگاه يارى نشوند- يعنى آن جهودان-.'),
(5139, 59, 13, 'هر آينه بيمى كه از شما در دل دارند از ترس از خدا بيشتر است. اين از آن‌روست كه گروهى نافهمند.'),
(5140, 59, 14, 'همه آنها- منافقان و جهودان- با شما كارزار نمى‌كنند مگر در آباديهاى استوارشده- به خندق و برج و بارو- يا از پس ديوارها- به سنگ و تير-. زورمندى آنها در ميان خودشان سخت است- ولى در برابر مسلمانان سست است و ياراى رويارويى دارند-. آنان را با هم و به هم ساخته مى‌پندارى ولى دلهاشان پراكنده است. اين بدان سبب است كه مردمى بيخردند.'),
(5141, 59, 15, '[داستان آنها]- جهودان- همچون داستان كسانى است كه اندك زمانى پيش از آنها بودند- جهودان بنى قينقاع يا كافران مكه- كه فرجام بد كار خود را چشيدند، و آنان را عذابى است دردناك.'),
(5142, 59, 16, '[داستان منافقان با جهودان‌] همانند داستان شيطان است آنگاه كه به آدمى گفت: كافر شو، و چون كافر شد، گفت: من از تو بيزارم من از خداى، پروردگار جهانيان، مى‌ترسم.'),
(5143, 59, 17, 'پس سرانجام آن دو اين شد كه هر دو جاودانه در آتش باشند و اين است كيفر ستمكاران.'),
(5144, 59, 18, 'اى كسانى كه ايمان آورده‌ايد، از خدا پروا كنيد، و هر كسى بايد بنگرد كه براى فردا- روز رستاخيز- چه پيش فرستاده است و از خدا پروا داشته باشيد، كه خدا بدانچه مى‌كنيد آگاه است.'),
(5145, 59, 19, 'و مانند كسانى مباشيد كه خداى را فراموش كردند و خدا خودشان- يعنى تدبير حال خويشتن- را فراموششان ساخت. اينانند بدكاران نافرمان.'),
(5146, 59, 20, 'دوزخيان و بهشتيان برابر نيستند بهشتيان همان رستگارانند.'),
(5147, 59, 21, 'اگر اين قرآن را بر كوهى فرو مى‌فرستاديم هر آينه آن را از بيم خدا ترسان و شكافته مى‌ديدى. و اين مثَلها را براى مردم مى‌آوريم شايد بينديشند.'),
(5148, 59, 22, 'اوست خداى يكتا كه جز او خدايى نيست داناى نهان و آشكارا اوست بخشاينده و مهربان.'),
(5149, 59, 23, 'اوست خداى يكتا كه جز او خدايى نيست پادشاه [بسزا و راستين‌]، پاك از هر عيب- و وصفى كه در خور نباشد-، ايمنى‌بخش، نگاهبان [بر همه چيز]، تواناى بى‌همتا، بر همه چيره، در خور كبريا و بزرگى، پاك و منزه است خداى از آنچه [با او] انباز مى‌گيرند.'),
(5150, 59, 24, 'اوست خداى آفريدگار، هستى‌بخش، نگارنده صورتها، او راست نامهاى بهين آنچه در آسمانها و زمين است او را به پاكى مى‌ستايند، و اوست تواناى بى‌همتا و داناى با حكمت.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'اى كسانى كه ايمان آورده‌ايد، دشمنان من و دشمنان خود را دوست مگيريد اگر [از خانمان و وطن خويش‌] براى جهاد در راه من و جستن خشنودى من بيرون آمده‌ايد با آنها طرح دوستى مى‌افكنيد و حال آنكه آنان به سخن راستى كه به شما آمده است- يعنى قرآن- كافر شدند، پيامبر و شما را از آن رو كه به خداى، پروردگارتان، ايمان آورده‌ايد بيرون مى‌كنند به پنهانى با آنان دوستى مى‌كنيد و حال آنكه من به آنچه پنهان مى‌داريد و آنچه آشكار مى‌كنيد داناترم. و از شما هر كه چنين كند همانا راه راست را گم كرده است.'),
(5152, 60, 2, 'اگر شما را بيابند دشمنان شما باشند و دستها و زبانهاى خود را به بدى- آزار و گزند- به سوى شما مى‌گشايند و دوست دارند كه كاش شما هم كافر شويد.'),
(5153, 60, 3, 'خويشان و فرزندانتان- كه به خاطر آنها خدا و پيامبرش را نافرمانى مى‌كنيد- در روز رستاخيز هرگز شما را سودى ندارند، [خدا] ميان شما جدايى مى افكند، و خدا بدانچه مى‌كنيد بيناست.'),
(5154, 60, 4, 'همانا براى شما در [روش‌] ابراهيم و كسانى كه با او بودند پيروى نيكوست، آنگاه كه به قوم خود گفتند: ما از شما و از آنچه جز خدا مى‌پرستيد بيزاريم، به [آيين و پرستيده‌] شما كافر و ناباوريم، و ميان ما و شما براى هميشه دشمنى و كين پديد آمده تا به خداى يگانه ايمان آوريد، مگر گفتار ابراهيم به پدر خود- سرپرست خود آزر- كه براى تو آمرزش خواهم خواست و براى تو از خداى هيچ چيز در توان ندارم- كه عذاب را از تو باز دارم-. پروردگارا، بر تو توكل كرديم و به سوى تو بازگشتيم، و بازگشت [همه‌] به سوى توست.'),
(5155, 60, 5, 'پروردگارا، ما را مايه آزمون كافران مكن و ما را، بارخدايا، بيامرز، كه همانا تويى تواناى بى‌همتا و داناى با حكمت.'),
(5156, 60, 6, 'هر آينه شما را در [روش‌] آنها پيروى نيكوست، براى كسى كه به [پاداش‌] خداى و روز واپسين اميد مى‌دارد و هر كه روى بگرداند پس [بداند كه‌] خداست بى‌نياز و ستوده.'),
(5157, 60, 7, 'اميد است كه خدا ميان شما و كسانى از آنان كه با آنها دشمنى داريد دوستى پديد آرد- با اسلام‌آوردن آنها- و خدا تواناست- كه دشمنى را به دوستى بدل كند-، و خدا آمرزگار و مهربان است.'),
(5158, 60, 8, 'خداوند شما را از نيكى‌كردن و دادگرى‌نمودن با كسانى كه در دين با شما كارزار نكردند و شما را از خانمانتان بيرون نراندند، باز نمى‌دارد. همانا خدا دادگران را دوست دارد.'),
(5159, 60, 9, 'جز اين نيست كه خدا شما را از دوستى‌كردن با آنان كه در دين با شما كارزار كردند و از خانمانتان بيرون راندند و بر بيرون‌كردنتان همپشتى نمودند باز مى‌دارد و هر كه با آنان دوستى بدارد، پس اينانند ستمكاران.'),
(5160, 60, 10, 'اى كسانى كه ايمان آورده‌ايد، هر گان زنان با ايمان هجرت‌كنان به نزد شما آيند پس آنان را بيازماييد خداى به ايمانشان داناتر است پس اگر آنان را با ايمان دانستيد، به سوى كافران بازشان مگردانيد. نه اينان براى آنان حلالند و نه آنان براى اينان حلال باشند، و آنچه هزينه كرده‌اند- كابينى كه به اين آنان داده‌اند- بازشان دهيد. و باكى بر شما نيست كه اينان را به زنى گيريد چنانچه كابينشان را به آنان بدهيد. و به عصمت‌هاى- نكاح- آنان كافر شده- و به ارتداد به مكه بازگشته- چنگ مزنيد- يعنى آنان كافر را نگاه مداريد و به همسرى آنها ادامه مدهيد- و آنچه هزينه كرده‌ايد [از آن مردان كافر] بخواهيد و آنها نيز آنچه هزينه كرده‌اند [از شما] بخواهند. اين حكم خداست كه ميان شما حكم مى‌كند و خدا دانا و با حكمت است.'),
(5161, 60, 11, 'و اگر كسى از زنان شما سوى كافران رفت- به كفار پيوست و هزينه شما به دستتان نيامد- سپس غنيمتى [از كافران‌] به دست آورديد، پس به كسانى كه زنانشان رفته‌اند مانند آنچه هزينه كرده‌اند بدهيد، و از خداى كه به او ايمان داريد پروا كنيد.'),
(5162, 60, 12, 'اى پيامبر، هر گاه زنان مؤمن نزد تو آيند كه با تو بيعت كنند بر اين شرط كه چيزى را با خداى انباز نگيرند و دزدى و زنا نكنند و فرزندان خود را نكشند و دروغى را كه در ميان دستها و پاهاشان فرابافته باشند پيش نيارند- يعنى به شوهرانشان جز فرزندان خود ايشان را نسبت ندهند- و در هيچ كار نيكو و پسنديده‌اى- كه ايشان را فرمايى- نافرمانى تو نكنند، پس با آنها بيعت كن و برايشان از خداى آمرزش بخواه، كه خدا آمرزگار و مهربان است.'),
(5163, 60, 13, 'اى كسانى كه ايمان آورده‌ايد، گروهى را كه خداوند بر آنها خشم گرفته است- يعنى جهودان را- به دوستى مگيريد، همانا آنان از [پاداش‌] آن جهان نوميدند چنانكه كافران از اهل گورها- از بازآمدن مردگان خود- نوميدند- زيرا رستاخيز را باور ندارند-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'هر چه در آسمانها و هر چه در زمين است خداى را به پاكى ستايند، و اوست تواناى بى‌همتا و داناى با حكمت.'),
(5165, 61, 2, 'اى كسانى كه ايمان آورده‌ايد، چرا مى‌گوييد آنچه نمى‌كنيد؟'),
(5166, 61, 3, 'بزرگ دشمنى و خشمى است به نزد خداى كه بگوييد آنچه نمى‌كنيد.'),
(5167, 61, 4, 'همانا خدا دوست مى‌دارد كسانى را كه در راه او صف‌زده كارزار مى‌كنند كه گويى بنائى به هم پيوسته و استوارند.'),
(5168, 61, 5, 'و [ياد كنيد] آنگاه كه موسى به قوم خود گفت: اى قوم من، چرا مرا مى‌آزاريد و حال آنكه مى‌دانيد كه من فرستاده خدا به سوى شمايم؟! پس چون [از حق‌] بگشتند و كجروى كردند خدا هم دلهاشان را بگردانيد و كج ساخت. و خدا مردم بدكار نافرمان را راه ننمايد.'),
(5169, 61, 6, 'و [ياد كن‌] آنگاه كه عيسى پسر مريم گفت: اى فرزندان اسرائيل، من فرستاده خدا به سوى شمايم، باوردارنده آنچه در پيش من است كه تورات است، و مژده‌دهنده‌ام به پيامبرى كه پس از من مى‌آيد، نام او احمد است پس چون با نشانه‌هاى روشن بديشان آمد، گفتند: اين جادويى است هويدا.'),
(5170, 61, 7, 'و كيست ستمكارتر از آن كس كه بر خدا دروغ بافت- يعنى پيامبر او را دروغگو انگاشت و آيات او را سحر خواند- در حالى كه به اسلام خوانده مى شود؟ و خدا مردم ستمكار را راه نمى‌نمايد.'),
(5171, 61, 8, 'مى‌خواهند كه نور خدا را با دهانهاشان خاموش كنند، و حال آنكه خدا تمام‌كننده نور- يعنى دين- خويش است هر چند كه كافران خوش ندارند.'),
(5172, 61, 9, 'اوست آن كه پيامبر خويش را با رهنمونى [به راه راست‌] و دين حق فرستاد تا آن را بر همه دينها چيره سازد، گر چه مشركان خوش ندارند.'),
(5173, 61, 10, 'اى كسانى كه ايمان آورده‌ايد، آيا شما را بر آن بازرگانى راه نمايم كه شما را از عذاب دردناك برهاند؟،'),
(5174, 61, 11, 'به خدا و پيامبر او ايمان آوريد- بر ايمان به خدا و پيامبر او استوار باشيد- و در راه خدا با مالها و جانهاى خويش جهاد كنيد، اين براى شما بهتر است اگر مى‌دانستيد'),
(5175, 61, 12, 'تا گناهانتان را بيامرزد و شما را به بهشتهايى كه از زير آنها جويها روان است و خانه‌هايى خوش در بهشتهايى پاينده درآرد اين است رستگارى و كاميابى بزرگ.'),
(5176, 61, 13, 'و [نعمت‌] ديگرى كه آن را دوست مى‌داريد: ياريى از خداى و پيروزيى نزديك و مؤمنان را مژده ده.'),
(5177, 61, 14, 'اى كسانى كه ايمان آورده‌ايد، ياران [دين‌] خداى باشيد چنانكه عيسى پسر مريم به حواريان- پيروان ويژه خود- گفت: ياران من به سوى خدا- براى خدا و در راه خدا- كيانند؟ حواريان گفتند: ماييم ياران خداى. پس گروهى از فرزندان اسرائيل ايمان آوردند و گروهى كافر شدند پس كسانى را كه ايمان آوردند بر دشمنانشان يارى و نيرو داديم تا چيره و پيروز شدند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'هر چه در آسمانها و هر چه در زمين است خداى، آن پادشاه پاك تواناى بى‌همتا و داناى با حكمت، را به پاكى مى‌ستايد.'),
(5179, 62, 2, 'اوست آن كه در ميان مردم درس‌ناخوانده- عرب- پيامبرى از خودشان برانگيخت كه آيات او را بر آنان مى‌خواند و پاكشان مى‌سازد- از آلودگى كفر و اخلاق زشت- و كتاب- قرآن- و حكمت- انديشه و گفتار و كردار درست- به آنان مى‌آموزد، و هر آينه پيش از اين در گمراهى آشكار بودند.'),
(5180, 62, 3, 'و [نيز آن پيامبر را برانگيخت به‌] ديگران از ايشان- مؤمنان- كه هنوز بديشان نپيوسته‌اند و اوست تواناى بى‌همتا و داناى با حكمت.'),
(5181, 62, 4, 'اين [پيامبرى‌] فزون‌بخشى خداست كه به آن كس كه خواهد مى‌دهد، و خدا خداوند فزون‌بخشى بزرگ است.'),
(5182, 62, 5, 'داستان كسانى كه تورات بر آنها نهاده شد- يعنى مكلف شدند كه آن را كار بندند- و آنگاه آن را برنداشتند- يعنى آن را كار نبستند- همچون داستان خرى است كه كتابهايى را بردارد بد [داستانى‌] است داستان گروهى كه آيات خداى را دروغ شمردند، و خدا گروه ستمكار را راه ننمايد.'),
(5183, 62, 6, 'بگو اى كسانى كه يهودى شديد، اگر پنداريد كه شما دوستان خداييد نه ديگر مردمان، پس آرزوى مرگ كنيد- تا شما را به او برساند- اگر راستگوييد.'),
(5184, 62, 7, 'و هرگز آن را آرزو نخواهند كرد به سبب آنچه دستهاشان پيش فرستاده است- يعنى كارهايى كه كرده‌اند- و خدا به ستمكاران داناست.'),
(5185, 62, 8, 'بگو: همانا مرگى كه از آن مى‌گريزيد شما را ديدار مى‌كند، سپس به سوى داناى نهان و آشكارا بازگردانيده مى‌شويد آنگاه شما را بدانچه مى‌كرديد آگاه مى‌كند.'),
(5186, 62, 9, 'اى كسانى كه ايمان آورده‌ايد، چون براى نماز روز آدينه آواز دهند، به ذكر خدا- يعنى نماز جمعه- بشتابيد، و خريد و فروش را رها كنيد. اين براى شما بهتر است اگر دانسته باشيد.'),
(5187, 62, 10, 'پس چون نماز گزارده شد، [مى‌توانيد] در زمين پراكنده شويد و از فضل خدا بجوييد- يعنى با كسب و كار روزى بجوييد- و خداى را بسيار ياد كنيد، شايد كه رستگار شويد.'),
(5188, 62, 11, 'و چون بازرگانيى- يعنى كاروان بازرگانى- يا سرگرميى را ببينند به سوى آن پراكنده مى‌شوند و تو را ايستاده- در حال خواندن خطبه- رها مى‌كنند! بگو: آنچه نزد خداست از سرگرمى و از بازرگانى بهتر است و خدا بهترين روزى‌دهندگان است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'چون منافقان- آنها كه دل و زبانشان يكى نيست- نزد تو آيند، گويند: گواهى مى‌دهيم كه هر آينه تو فرستاده خدايى. و خدا مى‌داند كه تو فرستاده اويى، و خدا گواهى مى‌دهد كه همانا منافقان دروغگويند.'),
(5190, 63, 2, 'سوگندهاى [دروغ‌] خود را سپرى گرفته‌اند، پس از راه خدا روى گردانده‌اند- يا مردم را از راه خدا بازداشته‌اند-. همانا بد است آنچه مى‌كردند- نفاق ورزى-.'),
(5191, 63, 3, 'اين [نفاق و بدكاريشان‌] بدان سبب است كه ايمان آوردند- به ظاهر- سپس كافر شدند- در نهان-، پس بر دلهاشان مُهر نهاده شد از اين رو [حق را] در نمى‌يابند.'),
(5192, 63, 4, 'و چون آنان را ببينى پيكرهاشان تو را به شگفت آرد، و اگر سخن گويند گفتارشان را بشنوى- از بس چرب‌زبانند-، [و حال آنكه‌] گويى چوبهايى‌اند به ديوار تكيه نهاده- بى‌ايمان و مردگانى بى‌روح‌اند-. هر بانگى را بر [زيان و هلاك‌] خود پندارند. اينان دشمن‌اند پس از آنها بر حذر باش. خدا بكشدشان- يا لعنت بر آنها باد- به كجا و چگونه [از حق‌] گردانيده مى‌شوند؟!'),
(5193, 63, 5, 'و چون به آنها گفته شود: بياييد تا پيامبر خدا برايتان آمرزش بخواهد، سرهاى خويش را بپيچانند و آنان را بينى كه [از حق‌] روى مى‌گردانند در حالى كه گردنكش‌اند.'),
(5194, 63, 6, 'بر آنها يكسان است چه برايشان آمرزش بخواهى يا آمرزش نخواهى هرگز خدا نيامرزدشان. همانا خدا مردم بدكار را راه ننمايد.'),
(5195, 63, 7, 'اينان همان كسانند كه مى‌گويند: بر آنان كه نزد پيامبر خدايند هزينه مكنيد- به آنها چيزى ندهيد- تا [از نزد وى‌] پراكنده شوند و حال آنكه خداى راست گنجينه‌هاى آسمانها و زمين، و ليكن منافقان درنمى‌يابند.'),
(5196, 63, 8, 'گويند: چون به مدينه بازگرديم، آن كه عزيزتر است آن را كه خوارتر است از آنجا بيرون مى‌كند، و حال آنكه خداى راست عزّت- بزرگى و ارجمندى- و پيامبر او و مؤمنان راست، و ليكن منافقان نمى‌دانند.'),
(5197, 63, 9, 'اى كسانى كه ايمان آورده‌ايد، مالها و فرزندانتان شما را از يادكرد خدا سرگرم نكنند و هر كه چنان كند- به سبب مال و فرزندان از طاعت خدا بازماند- ايشانند زيانكاران.'),
(5198, 63, 10, 'و از آنچه شما را روزى كرده‌ايم انفاق كنيد پيش از آنكه يكى از شما را مرگ فرا رسد پس گويد: پروردگارا، چرا مرا تا سرآمدى نزديك بازپس نداشتى- مرگ مرا اندكى به تأخير نيفكندى- تا صدقه دهم و از نيكان و شايستگان باشم؟'),
(5199, 63, 11, 'و هرگز خدا كسى را چون سرآمدش رسيده باشد بازپس ندارد، و خدا بدانچه مى‌كنيد آگاه است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'آنچه در آسمانها و آنچه در زمين است خداى را به پاكى مى‌ستايند، او راست پادشاهى- پادشاهى حقيقى و مطلق- و او راست سپاس و ستايش، و او بر هر چيزى تواناست.'),
(5201, 64, 2, 'اوست كه شما را آفريد پس برخى از شما كافر و برخى از شما مؤمن‌ايد و خدا بدانچه مى‌كنيد بيناست.'),
(5202, 64, 3, 'آسمانها و زمين را بحق آفريد و شما را صورت نگاشت پس صورتهاى شما را نيكو نگاشت، و بازگشت [همه‌] به سوى اوست.'),
(5203, 64, 4, 'آنچه را در آسمانها و زمين است مى‌داند، و آنچه را پنهان مى‌كنيد و آنچه را آشكار مى‌كنيد مى‌داند، و خدا به آنچه در سينه‌هاست- نيّتها و رازها- داناست.'),
(5204, 64, 5, 'آيا خبر كسانى كه پيش از اين كافر شدند به شما نيامده است؟ كه سرانجام بدِ كارِ خويش چشيدند و آنان را عذابى است دردناك.'),
(5205, 64, 6, 'آن [عذاب‌] از اين روست كه پيامبرانشان با حجتهاى روشن بديشان مى‌آمدند، ولى آنها مى‌گفتند: آيا آدميان ما را راه مى‌نمايند؟! پس كافر شدند و [از حق‌] برگشتند و خداى [از ايمانشان‌] بى‌نيازى نمود، و خدا بى‌نياز است و ستوده.'),
(5206, 64, 7, 'كسانى كه كافر شدند پنداشتند كه هرگز زنده و برانگيخته نمى‌شوند. بگو: چرا، به پروردگارم سوگند كه هر آينده زنده و برانگيخته مى‌شويد، سپس بدانچه كرده‌ايد خبر داده شويد و اين بر خدا آسان است.'),
(5207, 64, 8, 'پس به خدا و پيامبرش و نورى كه فرو فرستاده‌ايم- قرآن- ايمان بياوريد. و خدا بدانچه مى‌كنيد آگاه است.'),
(5208, 64, 9, '[زنده و برانگيخته مى‌شويد در] روزى كه شما را در «روز فراهم آوردن»- روز رستاخيز- فراهم آورد. آن روز، روز زيان [و حسرت و افسوس‌]- زيان كافران و سود مؤمنان- است و هر كه به خدا ايمان آرد و كار نيك و شايسته كند بديهايش را از او بزدايد و او را به بهشتهايى درآرد كه جويها از زير آنها روان است، هميشه و جاودانه در آنجا خواهند بود، اين است رستگارى و كاميابى بزرگ.'),
(5209, 64, 10, 'و كسانى كه كافر شدند و آيات ما را دروغ انگاشتند، اينانند دوزخيان كه در آن جاودانه‌اند، و بد بازگشتگاهى است.'),
(5210, 64, 11, 'هيچ مصيبتى جز به خواست و فرمان خدا نرسد. و هر كه به خدا ايمان آرد [خدا] دلش را راه نمايد- به خشنودى و شكيبايى-، و خدا به هر چيزى داناست.'),
(5211, 64, 12, 'و خداى را فرمان بريد و پيامبر را فرمان بريد پس اگر روى بگردانيد [بدانيد كه‌] بر پيامبر ما رساندن آشكار پيام است و بس.'),
(5212, 64, 13, 'خداى يكتا جز او خدايى نيست، پس مؤمنان بايد بر او توكّل كنند و بس.'),
(5213, 64, 14, 'اى كسانى كه ايمان آورده‌ايد، همانا برخى از همسران و فرزندانتان دشمن شمايند، پس از آنها حذر كنيد و اگر درگذريد و چشم بپوشيد و ببخشاييد، همانا خدا آمرزگار و مهربان است.'),
(5214, 64, 15, 'جز اين نيست كه مالها و فرزندانتان [براى شما] آزمون‌اند، و مزد بزرگ نزد خداوند است.'),
(5215, 64, 16, 'پس هر چه توانيد از خدا پروا كنيد و بشنويد و فرمان بريد و انفاق كنيد كه براى خودتان بهتر است، و هر كه از بخل و آز خويش نگاه داشته شود پس ايشانند رستگاران.'),
(5216, 64, 17, 'اگر خداى را وام دهيد وامى نيكو- در راه خدا هزينه كنيد- آن را براى شما افزون- يا دو چندان- كند و شما را بيامرزد، و خدا سپاسدار و بردبار است'),
(5217, 64, 18, 'داننده نهان و آشكارا، تواناى بى‌همتا و داناى با حكمت است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'اى پيامبر، چون خواهيد زنان را طلاق دهيد پس آنان را به هنگام عدّه ايشان طلاق دهيد و شمار عدّه را نگاه داريد، و از خداى، پروردگارتان، پروا كنيد و آنان را از خانه هاشان بيرون مكنيد و [آنها هم‌] بيرون نروند مگر آنكه كار زشت آشكارى كرده باشند. و اين مرزهاى- احكام- خداست و هر كه از مرزهاى خدا درگذرد براستى به خود ستم كرده است. تو- اى طلاق‌دهنده- چه دانى، شايد خداوند پس از اين كارى پديد آرد.'),
(5219, 65, 2, 'پس چون به سرآمد خويش نزديك شدند، يا به شيوه‌اى نيكو و پسنديده نگاهشان داريد يا از آنان بخوبى جدا شويد، و دو تن عادل از خودتان را گواه گيريد، و گواهى را براى خدا برپا داريد- ادا كنيد-، اين است كه به آن پند داده مى‌شود هر كه به خدا و روز بازپسين ايمان دارد، و هر كه از خدا پروا كند براى او راه بيرون شدن [از هر دشوارى و اندوهى‌] پديد آرد،'),
(5220, 65, 3, 'و او را از جايى كه گمان ندارد روزى مى‌دهد. و هر كه بر خدا توكل كند خدا او را بسنده است. همانا خدا كار خود را رساننده است [به آنجا كه خواهد]. براستى كه خداوند براى هر چيزى اندازه‌اى نهاده است.'),
(5221, 65, 4, 'و آن زنانى از شما كه از حيض- قاعدگى- نااميد شده باشند، اگر شك داشتيد- علت آن ندانستيد- پس عده [طلاق‌] ايشان سه ماه است و نيز زنانى كه حيض نديده‌اند- با آنكه در سنين حيض‌ديدن هستند-. و زنان باردار سرآمدشان اين است كه بار خود را بنهند. و هر كه از خداى پروا كند- به طاعت او- او را در كارش آسانى پديد آرد.'),
(5222, 65, 5, 'اين فرمان خداست كه آن را به سوى شما فرو فرستاد، و هر كه از خداى پروا كند بديهايش را از او بزدايد و مزدش را بزرگ سازد.'),
(5223, 65, 6, 'آنان- زنان طلاق‌داده- را [تا سر آمد عدّه‌شان‌] آنجا سكونت دهيد كه خود سكونت مى‌كنيد از همان توانايى كه داريد و به ايشان گزند مرسانيد- در نفقه و مسكن- تا بر آنها تنگ گيريد- كه از آنجا بيرون روند- و اگر بار دارند بر آنها هزينه كنيد تا بار خويش بنهند. پس اگر كودكان شما را شير دادند، مزدشان را بدهيد و با يكديگر به شايستگى و خوبى مشورت و سازش كنيد- در شيردادن مادر و پرداخت اجرت از سوى پدر- و اگر دشوارى كنيد- به توافق نرسيديد- زنى ديگر بايد او را شير دهد- پدر دايه بگيرد-.'),
(5224, 65, 7, 'فراخدست از فراخدستى خود هزينه كند، و هر كه روزى‌اش بر او تنگ شده باشد از آنچه خداوند به او داده است هزينه كند- يعنى به اندازه توان خود- خدا هيچكس را تكليف نمى‌كند مگر [به اندازه‌] آنچه به او داده است. خداوند پس از سختى و دشوارى آسانى و فراخى پديد خواهد آورد.'),
(5225, 65, 8, 'و بسا [مردم‌] ديه و شهر كه از فرمان پروردگارشان و فرستادگان او سر باززدند پس سخت به حساب آنها رسيديم و عذابشان كرديم عذابى دشوار و ناشناخته.'),
(5226, 65, 9, 'پس سرانجامِ بدِ كارِ خود را چشيدند و عاقبت كارشان زيانكارى بود.'),
(5227, 65, 10, 'خداوند براى آنان- مشركان و كافران- عذابى سخت- عذاب دوزخ- آماده كرده است پس اى خردمندان كه ايمان آورده‌ايد از خداى پروا كنيد، همانا خدا به سوى شما ياد و پندى- قرآن- فروفرستاده است'),
(5228, 65, 11, '[و نيز بفرستاد] پيامبرى كه آيات روشن و روشنگر خدا را بر شما مى‌خواند تا كسانى را كه ايمان آورده‌اند و كارهاى شايسته كرده‌اند از تاريكيها به روشنايى بيرون آرد. و هر كه به خدا ايمان آورد و كار شايسته كند او را به بهشتهايى درآرد كه از زير آنها جويها روان است، هميشه در آنجا جاودانه اند. همانا خدا روزى او را نيكو ساخته است.'),
(5229, 65, 12, 'خداى همان است كه هفت آسمان و از زمين همانند آن- هفت زمين- بيافريد فرمان [او] ميان آنها فرو مى‌آيد- جارى است- تا بدانيد كه خداى بر هر چيزى تواناست و دانش خدا همه چيز را فراگرفته است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'اى پيامبر، چرا آنچه را خدا براى تو حلال كرده [بر خود] حرام مى‌كنى؟ [با اين كار] خشنودى همسران خود را مى‌خواهى، و خدا آمرزگار و مهربان است.'),
(5231, 66, 2, 'همانا خدا براى شما گشودن سوگندهاتان را [به كفاره‌] مقرر كرد و خدا سرپرست و كارساز شماست، و اوست داناى با حكمت.'),
(5232, 66, 3, 'و [ياد كنيد] آنگاه كه پيامبر با يكى از زنان خويش سخنى به راز گفت، پس چون آن زن- حفصه- آن راز را فاش كرد- به عايشه باز گفت- و خدا او'),
(5233, 66, 4, 'اگر [شما دو زن‌] به خداى بازگرديد- توبه كنيد- [بجاست‌]، زيرا كه دلهاى شما به كژى و گناه- فاش‌كردن سِرّ پيامبر'),
(5234, 66, 5, 'اگر شما را طلاق دهد شايد پروردگارش همسرانى بهتر از شما به او عوض دهد: مسلمان، مؤمن، فرمانبر، توبه‌كننده، خداپرست، روزه‌دار، شوهركرده و دوشيزه.'),
(5235, 66, 6, 'اى كسانى كه ايمان آورده‌ايد، خود و خانواده خود را از آتشى كه هيمه آن مردم و سنگ- كافران و بتها- باشند نگاه داريد كه بر آن فرشتگانى درشت خو و سختگير نگهبانند كه خداى را در آنچه به آنان فرمايد نافرمانى نكنند و هر چه فرمايندشان همان كنند.'),
(5236, 66, 7, 'اى كسانى كه كافر شديد امروز- روز رستاخيز- عذر مخواهيد، جز اين نيست كه آنچه مى‌كرده‌ايد كيفر داده مى‌شويد.'),
(5237, 66, 8, 'اى كسانى كه ايمان آورده‌ايد، به خداى بازگرديد- توبه كنيد- بازگشتى به صدق و اخلاص- كه از روى پشيمانى بر گناه و ترك آن و استغفار باشد- اميد است پروردگارتان بديهاتان را از شما بزدايد و شما را به بهشتهايى درآرد كه از زير آنها جويها روان است، روزى كه خدا پيامبر و كسانى را كه با او ايمان آورده‌اند خوار نسازد- بلكه با واردكردنشان به بهشت گرامى‌شان دارد-، نورشان پيشاپيش ايشان و از سوى راستشان مى‌رود، مى‌گويند: پروردگارا، نور ما را براى ما كامل ساز و ما را بيامرز، كه تو بر هر چيزى توانايى.'),
(5238, 66, 9, 'اى پيامبر، با كافران و منافقان كارزار كن و بر آنها درشتى و سختگيرى نما، و جايشان دوزخ است، و بد بازگشتگاهى است.'),
(5239, 66, 10, 'خدا براى كسانى كه كافر شدند مَثَلى آورده است: زن نوح و زن لوط را، كه اين دو زير [فرمان‌] دو بنده از بندگان شايسته ما بودند، پس به آن دو خيانت كردند، و آن دو [پيامبر] در برابر خدا هيچ سودى براى آن دو [زن‌] نداشتند- عذاب را از آنها دور نكردند- و [به آنان‌] گفته شد: به آتش در آييد با در آيندگان.'),
(5240, 66, 11, 'و خدا براى كسانى كه ايمان آورده‌اند مَثَلى آورده است: زن فرعون را، آنگاه كه گفت: پروردگارا، براى من نزد خويش در بهشت خانه‌اى بساز و مرا از فرعون و كردار او برهان، و مرا از گروه ستمكاران برهان'),
(5241, 66, 12, 'و مريم دختر عمران را كه دامن خويش [از حرام‌] نگاه داشت، پس ما از روح خود در آن دميديم. و او كلمات پروردگار خويش و كتابهايش را باور داشت و از فرمانبرداران بود.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'بزرگ و بزرگوار است آن كه پادشاهى به دست اوست و او بر هر چيزى تواناست'),
(5243, 67, 2, 'آن كه مرگ و زندگى را بيافريد تا شما را بيازمايد كه كدام يك از شما نيكوكارتر است، و اوست تواناى بى‌همتا و آمرزگار.'),
(5244, 67, 3, 'آن كه هفت آسمان را مطابق- يا بر- يكديگر بيافريد. در آفرينش خداى رحمان هيچ تفاوت- اختلاف و خلل و بى‌نظمى- نمى‌بينى پس ديگر بار ديده را بگردان و بنگر، آيا هيچ شكافى- خلل و نقصى- مى‌بينى؟'),
(5245, 67, 4, 'سپس بار ديگر نيز ديده بگردان و بنگر تا ديده‌ات خوار و فرومانده به سويت بازگردد- بى‌آنكه عيب و نقصى در آسمانها بيابد-.'),
(5246, 67, 5, 'و هر آينه آسمان دنيا- نزديكتر به زمين- را به چراغهايى- ستارگان- بياراستيم و آنها'),
(5247, 67, 6, 'و كسانى را كه به پروردگارشان كافر شدند عذاب دوزخ است، و بد بازگشتگاهى است'),
(5248, 67, 7, 'چون در آن افكنده شوند، در حالى كه مى‌جوشد- و آنها را فرو مى‌برد- آوازى دلخراش مانند آواز خران از آن بشنوند'),
(5249, 67, 8, 'نزديك است كه از خشم پاره پاره شود. هر گاه گروهى- از دوزخيان- در آن افكنده شوند، نگهبانانش [به نكوهش و سرزنش‌] از آنها پرسند: مگر شما را بيم‌كننده‌اى نيامد؟'),
(5250, 67, 9, 'گويند: چرا، همانا ما را بيم‌كننده آمد، ولى تكذيب كرديم و گفتيم: خدا هيچ چيز فرونفرستاده است، شما جز در گمراهى بزرگ نيستيد.'),
(5251, 67, 10, 'و گويند: اگر ما مى‌شنيديم و خِرَد را كار مى‌بستيم در زمره دوزخيان نبوديم.'),
(5252, 67, 11, 'پس به گناه خويش اقرار كنند، پس نابودى باد اهل آتش افروخته را!'),
(5253, 67, 12, 'همانا كسانى كه از پروردگارشان در نهان مى‌ترسند، آمرزش و مزد بزرگ دارند.'),
(5254, 67, 13, 'و گفتارتان را چه پنهان داريد يا آن را آشكار كنيد، همانا او بدانچه در سينه‌هاست داناست.'),
(5255, 67, 14, 'آيا كسى كه آفريده است نمى‌داند- آيا آفريدگار از آفريده خود با خبر نيست-، و حال آنكه باريكدان و آگاه است؟!'),
(5256, 67, 15, 'اوست كه زمين را براى شما رام كرد پس در اطراف و كنارهاى آن راه رويد و از روزى او بخوريد و [بدانيد كه‌] برانگيختن- در روز رستاخيز- به سوى اوست.'),
(5257, 67, 16, 'آيا از آن كه در آسمان است ايمن شده‌ايد از اينكه شما را به زمين فروبرد و آنگاه آن'),
(5258, 67, 17, 'يا از آن كه در آسمان است ايمن شده‌ايد از اينكه بر شما بادى سنگبار فرستد؟ پس بزودى خواهيد دانست كه بيم‌دادن من چگونه است.'),
(5259, 67, 18, 'و هر آينه كسانى كه پيش از آنان بودند تكذيب كردند، پس انكار [و كيفر] من چگونه بود؟'),
(5260, 67, 19, 'و آيا به مرغان بر فراز سرشان ننگريسته‌اند كه بالها گشايند و به هم آرند؟ [تا ببينند كه‌] هيچ كس آنها را [در هوا] جز خداى رحمان نگاه نمى‌دارد همانا او به هر چيزى بيناست.'),
(5261, 67, 20, 'يا كيست آن كه سپاه شما باشد كه شما را در برابر خداى رحمان يارى كند؟ كافران جز در فريفتگى- فريب‌خورده شيطان- نيستند.'),
(5262, 67, 21, 'يا كيست آن كه شما را روزى دهد، اگر او روزى خويش بازگيرد؟ بلكه اينان در سركشى و رميدن [خويش‌] فرورفته‌اند.'),
(5263, 67, 22, 'آيا كسى كه نگونسار بر روى خود مى‌رود راه‌يافته‌تر است يا آن كه راست ايستاده بر راه راست مى‌رود؟'),
(5264, 67, 23, 'بگو: اوست كه شما را آفريد و براى شما گوش و ديدگان و دلها پديد آورد. [ولى‌] اندك سپاس مى‌گزاريد.'),
(5265, 67, 24, 'بگو: اوست كه شما را در زمين بيافريد و به سوى او برانگيخته و فراهم مى‌شويد.'),
(5266, 67, 25, 'و مى‌گويند: اين وعده كى خواهد بود، اگر راستگوييد؟'),
(5267, 67, 26, 'بگو: همانا دانش [آن‌] نزد خداست و من بيم‌كننده‌اى آشكارم و بس.'),
(5268, 67, 27, 'پس چون آن [وعده‌] را نزديك‌شده بينند، روى آنان كه كافر شدند بد- زشت و تاريك و ناخوش- گردد و گفته شود: اين است آنچه مى‌خواستيد- مى طلبيديد-.'),
(5269, 67, 28, 'بگو: مرا گوييد كه اگر خداى مرا و كسانى را كه با من‌اند هلاك كند يا بر ما مِهر و بخشايش آرد، چه كسى [شما] كافران را از عذاب دردناك پناه مى دهد؟'),
(5270, 67, 29, 'بگو: او خداى رحمان است، به او ايمان آورديم و بر او توكل كرديم. پس بزودى خواهيد دانست چه كسى در گمراهى آشكار است.'),
(5271, 67, 30, 'بگو: مرا گوييد كه اگر آب شما [به زمين‌] فرو شود، چه كسى شما را آب روان مى‌آورد؟');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'نون، سوگند به قلم و آنچه مى‌نويسند،'),
(5273, 68, 2, 'كه تو به نعمت پروردگارت- نبوت و رسالت- ديوانه نيستى.'),
(5274, 68, 3, 'و هر آينه تو راست مزد بى‌پايان.'),
(5275, 68, 4, 'و هر آينه تويى بر خويى بس بزرگ.'),
(5276, 68, 5, 'پس زودا كه ببينى و آنها نيز ببينند،'),
(5277, 68, 6, 'كه كدامين شما ديوانه و شوريده است.'),
(5278, 68, 7, 'همانا پروردگارت داناتر است به كسى كه از راه او گم گشته و داناتر است به ره‌يافتگان.'),
(5279, 68, 8, 'پس دروغ‌انگاران را فرمان مبر.'),
(5280, 68, 9, 'دوست دارند كه نرمى كنى تا نرمى كنند.'),
(5281, 68, 10, 'و هر بسيار سوگند خورنده خوار و بى‌ارزشى را فرمان مبر،'),
(5282, 68, 11, 'كه عيبجو، بدگو، رونده به سخن‌چينى،'),
(5283, 68, 12, 'بازدارنده نيكى، از حد درگذرنده، گنه‌پيشه،'),
(5284, 68, 13, '[و] پس از اين همه، سختدل و درشت‌خو و بى‌تبار است'),
(5285, 68, 14, 'از آن رو كه داراى مال و پسران است'),
(5286, 68, 15, 'چون آيات ما بر او خوانده شود گويد: افسانه‌هاى پيشينيان است.'),
(5287, 68, 16, 'بر خرطوم- بينى- او داغ خواهيم نهاد.'),
(5288, 68, 17, 'ما آنها- اهل مكه- را بيازموديم- به آنها نعمت داديم ولى كفر ورزيدند- چنانكه خداوندان آن بوستان را آزموديم آنگاه كه سوگند خوردند كه هر آينه بامداد پگاه- پنهان از مستمندان- ميوه آن را خواهيم چيد'),
(5289, 68, 18, 'و استثنا نكردند- نگفتند اگر خداى خواهد-'),
(5290, 68, 19, 'پس شبانگاه كه خفته بودند آفتى از سوى پروردگارت بر گرد آن'),
(5291, 68, 20, 'پس مانند باغى شد كه ميوه آن چيده و بريده باشند- درختانش سوخته و سياه شد-.'),
(5292, 68, 21, 'پس بامدادان يكديگر را ندا دادند:'),
(5293, 68, 22, 'اگر ميوه‌چين هستيد پگاه به كشتزار خود در آييد.'),
(5294, 68, 23, 'پس به راه افتادند و با هم آهسته مى‌گفتند:'),
(5295, 68, 24, 'امروز مبادا درويشى در آن'),
(5296, 68, 25, 'و پگاه برفتند بدين آهنگ كه بر [چيدن و] منع درويشان توانايند.'),
(5297, 68, 26, 'پس چون آن را ديدند- خشك و سوخته- گفتند: هر آينه ما گمراهيم- اين باغ ما نيست-.'),
(5298, 68, 27, '[نه،] بلكه ما ناكام شده‌ايم.'),
(5299, 68, 28, 'بهترينشان- به خِرَد و ميانه‌روى- گفت: آيا شما را نگفتم: چرا خداى را به پاكى ياد نمى‌كنيد؟'),
(5300, 68, 29, 'گفتند: پاك است پروردگار ما، همانا ما ستمكار بوده‌ايم.'),
(5301, 68, 30, 'پس برخى از ايشان بر برخ ديگر سرزنش‌كنان روى آورده،'),
(5302, 68, 31, 'گفتند: واى بر ما، همانا ما سركش بوده‌ايم.'),
(5303, 68, 32, 'اميد است پروردگار ما بهتر از آن را به ما باز دهد، ما به پروردگارمان گراينده‌ايم.'),
(5304, 68, 33, 'چنين است عذاب [اين جهانى‌]، و هر آينه عذاب آن جهان بزرگتر است، اگر مى‌دانستند.'),
(5305, 68, 34, 'همانا پرهيزگاران را نزد پروردگارشان بهشتهاى پرنعمت است.'),
(5306, 68, 35, 'پس آيا مسلمانان- گردن‌نهادگان- را همچون بزهكاران مى‌سازيم- يكسان و برابر-؟!'),
(5307, 68, 36, 'شما را چيست؟! چگونه داورى مى‌كنيد؟!'),
(5308, 68, 37, 'يا مگر شما را كتابى است كه در آن مى‌خوانيد،'),
(5309, 68, 38, 'كه شما راست در آن [جهان‌] هر چه گزينيد؟!'),
(5310, 68, 39, 'يا مگر شما را بر ما پيمانهايى است رسا و پيوسته تا روز رستاخيز، كه هر چه حكم كنيد شما را باشد؟!'),
(5311, 68, 40, 'از آنها بپرس كه كدامشان ضامن اين [دعوى‌] اند'),
(5312, 68, 41, 'يا مگر ايشان را انبازانى است- بتها و معبودانى جز خدا كه پشتيبان آنها باشند-؟! پس اگر راستگويند انبازان خويش بيارند،'),
(5313, 68, 42, 'در روزى كه كار [بر كافران‌] سخت و دشوار شود، و آنان به سجود خوانده شوند ولى نتوانند'),
(5314, 68, 43, 'در حالى كه ديدگانشان از ترس فروشده، خوارى و زبونى آنها را فرو گرفته است و همانا به سجود خوانده مى‌شدند و حال آنكه تندرست بودند- مى توانستند سجده كنند ولى اجابت نمى‌كردند-.'),
(5315, 68, 44, 'پس مرا با كسانى كه اين سخن- قرآن- را دروغ مى‌انگارند واگذار آنها را اندك اندك و پايه پايه از آنجا كه ندانند فرو گيريم- به هلاكت و نابودى كشانيم-.'),
(5316, 68, 45, 'و آنان را مهلت دهم، كه تدبير من استوار است.'),
(5317, 68, 46, 'يا مگر از آنان مزدى مى‌خواهى كه از تاوان گرانبارند؟!'),
(5318, 68, 47, 'يا مگر نزدشان علم غيب است كه [آن را] مى‌نويسند- و به ديگران مى‌دهند-؟!'),
(5319, 68, 48, 'پس بر حكم پروردگار خود شكيبايى كن و مانند صاحب ماهى- يونس- مباش آنگاه كه با دلى پراندوه [در شكم ماهى‌] ندا داد.'),
(5320, 68, 49, 'اگر نه آن بود كه نعمتى- بخشايش و نيكو داشتى- از پروردگارش او را دريافت هر آينه به صحرايى بى‌گياه افكنده مى‌شد و نكوهيده مى‌بود.'),
(5321, 68, 50, 'پس پروردگارش او را برگزيد و از نيكان و شايستگانش ساخت.'),
(5322, 68, 51, 'و هر آينه نزديك است آنان كه كافر شدند- چون قرآن را شنيدند- كه مى‌خواندى- تو را با چشمهاى خود بلغزانند و بيفكنند و مى‌گويند: همانا او ديوانه است-.'),
(5323, 68, 52, 'و نيست اين [قرآن‌] مگر ياد و پندى براى جهانيان.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'آن روز راست- روز رستاخيز كه وقوعش راست است-.'),
(5325, 69, 2, 'چيست آن روز راست؟'),
(5326, 69, 3, 'و چه آگاه كرد تو را كه آن روز راست چيست؟'),
(5327, 69, 4, 'ثمود- قوم صالح- و عاد- قوم هود- روز كوبنده و درهم‌شكننده را دروغ شمردند.'),
(5328, 69, 5, 'اما ثمود، پس به سبب سركشى- يا به عذاب صيحه سركش- هلاك شدند.'),
(5329, 69, 6, 'و اما عاد، به بادى سرد و سخت و از حد گذشته هلاك شدند'),
(5330, 69, 7, 'كه آن را هفت شب و هشت روز پياپى بر آنان گماشت، پس آن قوم را در آن'),
(5331, 69, 8, 'پس آيا هيچ برجاى‌مانده‌اى از آنان مى‌بينى؟'),
(5332, 69, 9, 'و فرعون و كسانى كه پيش از وى بودند و [مردم‌] آباديهاى زير و رو شده- شهرهاى قوم لوط- همگى با بدكارى و گناه آمدند- يا بدكارى و گناه آوردند-'),
(5333, 69, 10, 'فرستاده پروردگار خود را نافرمانى كردند پس [خداى هم‌] آنها را به كيفرى سخت بگرفت.'),
(5334, 69, 11, 'همانا ما هنگامى كه آب از حد درگذشت شما را در آن كشتى- كشتى نوح- سوار كرديم،'),
(5335, 69, 12, 'تا آن را براى شما ياد كرد و پندى سازيم و گوشهاى فراگيرنده آن را فرا گيرد.'),
(5336, 69, 13, 'و چون يك بار در صور دميده شود،'),
(5337, 69, 14, 'و زمين و كوه‌ها [از جاى‌] برداشته شوند و يكباره درهم شكنند،'),
(5338, 69, 15, 'پس آن روز آن واقعه- رستاخيز- رخ نمايد.'),
(5339, 69, 16, 'و آسمان بشكافد پس در آن روز سست و پراكنده باشد'),
(5340, 69, 17, 'و فرشتگان بر كناره‌هاى آن'),
(5341, 69, 18, 'آن روز شما را پيش آرند بگونه‌اى كه هيچ [كار] پوشيده‌اى از شما، پنهان نماند.'),
(5342, 69, 19, 'پس آن كه كارنامه او به دست راستش داده شود [با خوشحالى‌] گويد: بياييد و كارنامه مرا بگيريد و بخوانيد- زيرا در آن عملى نيست كه از آشكار شدنش شرم داشته باشم-.'),
(5343, 69, 20, 'من مى‌دانستم كه حساب خويش را خواهم ديد.'),
(5344, 69, 21, 'پس او در زندگانيى پسنديده باشد،'),
(5345, 69, 22, 'در بهشتى برين،'),
(5346, 69, 23, 'كه ميوه‌هاى آن نزديك و در دسترس است.'),
(5347, 69, 24, 'بخوريد و بياشاميد، نوش و گوارا، به پاداش آنچه در روزگار گذشته پيش فرستاديد.'),
(5348, 69, 25, 'و اما آن كه كارنامه او به دست چپش داده شود پس [با بد حالى‌] گويد: اى كاش كارنامه‌ام به من داده نمى‌شد.'),
(5349, 69, 26, 'و نمى‌دانستم حسابم چيست.'),
(5350, 69, 27, 'اى كاش مرگ پايان‌دهنده كار بود.'),
(5351, 69, 28, 'مالم مرا سود نبخشيد- عذاب را از من بازنداشت-.'),
(5352, 69, 29, 'حجت- يا نيروى- من از دستم برفت.'),
(5353, 69, 30, '[ندا آيد:] او را بگيريد و دربندش كنيد.'),
(5354, 69, 31, 'سپس به دوزخش در آريد- يا بسوزانيد-.'),
(5355, 69, 32, 'سپس در زنجيرى كه درازاى آن هفتاد گز باشد در آريدش.'),
(5356, 69, 33, 'زيرا كه او به خداى بزرگ ايمان نمى‌داشت،'),
(5357, 69, 34, 'و بر طعام دادن بينوايان بر نمى‌انگيخت- ترغيب نمى‌كرد-.'),
(5358, 69, 35, 'پس امروز او را در آنجا هيچ دوستى نباشد،'),
(5359, 69, 36, 'و نه طعامى مگر از زردابه- چرك و خونى كه از تن دوزخيان مى‌آيد-،'),
(5360, 69, 37, 'كه آن را جز بدكاران نمى‌خورند.'),
(5361, 69, 38, 'پس سوگند به آنچه مى‌بينيد،'),
(5362, 69, 39, 'و آنچه نمى‌بينيد،'),
(5363, 69, 40, 'كه اين [قرآن‌] گفتار فرستاده‌اى است بزرگوار- رسول اكرم'),
(5364, 69, 41, 'و اين سخن شاعرى نيست، چه اندك ايمان مى‌آوريد'),
(5365, 69, 42, 'و سخن كاهنى- پيشگو يا اخترگو- هم نيست، چه اندك پند مى‌گيريد.'),
(5366, 69, 43, 'فروفرستاده‌اى است از پروردگار جهانيان.'),
(5367, 69, 44, 'و اگر وى برخى سخنان را بر ما برمى‌بافت،'),
(5368, 69, 45, 'هر آينه او را به دست راست- كنايه از قوت و توانايى يا سختى و شدت- مى‌گرفتيم،'),
(5369, 69, 46, 'سپس رگ جان- دل- او را مى‌بريديم'),
(5370, 69, 47, 'پس هيچ يك از شما بازدارنده [عذاب‌] از او نيست.'),
(5371, 69, 48, 'و هر آينه اين [قرآن‌] ياد و پندى است براى پرهيزگاران.'),
(5372, 69, 49, 'و هر آينه ما مى‌دانيم كه از شما تكذيب‌كنانى هستند.'),
(5373, 69, 50, 'و هر آينه آن'),
(5374, 69, 51, 'و همانا آن حق اليقين است.'),
(5375, 69, 52, 'پس به نام پروردگار بزرگت تسبيح گوى.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'پُرسنده‌اى عذابى را كه بودنى- واقع شدنى- است درخواست نمود'),
(5377, 70, 2, 'كه كافران را هيچ بازدارنده‌اى از آن نيست،'),
(5378, 70, 3, 'از سوى خداى كه خداوند پايه‌هاى بلند است.'),
(5379, 70, 4, 'فرشتگان و روح- فرشته همراه وحى- در روزى كه درازى آن پنجاه هزار سال است- از سالهاى دنيا- به سوى او بالا مى‌روند.'),
(5380, 70, 5, 'پس شكيبايى كن شكيبايى نيكو.'),
(5381, 70, 6, 'آنها آن- عذاب يا روز قيامت- را دور مى‌بينند،'),
(5382, 70, 7, 'و ما نزديكش مى‌بينيم.'),
(5383, 70, 8, 'روزى كه آسمان چون مس گداخته شود'),
(5384, 70, 9, 'و كوه‌ها مانند پشم رنگين [زده‌شده‌] گردد'),
(5385, 70, 10, 'و هيچ خويشاوندى- يا دوستى- از [حال‌] خويشاوندى- يا دوستى- نپرسد.'),
(5386, 70, 11, 'آنان- يعنى خويشانشان- را بديشان بنمايند- ولى كارى از آنها ساخته نيست-. بزهكار آرزو كند كه كاش مى‌توانست [براى فرار] از عذاب آن روز فرزندان خود را عوض دهد،'),
(5387, 70, 12, 'و زن و برادر خود را،'),
(5388, 70, 13, 'و خويشانى را كه او را جاى مى‌دادند- و به خود نزديك مى‌ساختند-،'),
(5389, 70, 14, 'و هر كه را در زمين است، همه را يكجا، تا او را [از عذاب‌] برهاند،'),
(5390, 70, 15, 'هرگز، كه آن آتشى است زبانه‌كشنده،'),
(5391, 70, 16, 'بركننده پوست سر- يا همه تن-،'),
(5392, 70, 17, 'مى خواند هر كه را [به ايمان و طاعت‌] پشت كرد و روى بگردانيد.'),
(5393, 70, 18, 'و [مال را] گرد كرد و بيندوخت.'),
(5394, 70, 19, 'همانا آدمى ناشكيبا- يا حريص- آفريده شده'),
(5395, 70, 20, 'چون بدى [و رنجى‌] بدو رسد بيتاب است'),
(5396, 70, 21, 'و چون نعمتى [و رفاهى‌] بدو رسد بازدارنده است- بخل ورزد-'),
(5397, 70, 22, 'مگر نمازگزاران'),
(5398, 70, 23, 'آنان كه بر نماز خويش پيوسته و پاينده‌اند'),
(5399, 70, 24, 'و آنان كه در مالهاشان حقى است معلوم- مانند زكات و صدقات-،'),
(5400, 70, 25, 'براى نيازمند خواهنده و بينواى درمانده'),
(5401, 70, 26, 'و آنان كه روز پاداش را باور دارند'),
(5402, 70, 27, 'و آنان كه از عذاب پروردگار خود ترسانند'),
(5403, 70, 28, 'همانا از عذاب پروردگارشان ايمنى نيست'),
(5404, 70, 29, 'و آنان كه شرمگاه خويش نگاه مى‌دارند،'),
(5405, 70, 30, 'مگر از همسران خود يا كنيزانى كه آنها را مالكند، كه [در باره آنها] سرزنش نشوند.'),
(5406, 70, 31, 'پس هر كه فراتر از اين جويد اينانند از حدگذشتگان'),
(5407, 70, 32, 'و آنان كه امانتها و پيمانهاى خود را رعايت مى‌كنند'),
(5408, 70, 33, 'و آنان كه به گواهيهاى خويش ايستاده‌اند- متعهد و پايدارند-'),
(5409, 70, 34, 'و آنان كه بر نماز خود نگاهدار و پاسدارند- به نيكوترين و كاملترين وجه نماز مى‌گزارند-.'),
(5410, 70, 35, 'ايشانند كه در بهشتها گرامى داشتگانند.'),
(5411, 70, 36, 'پس كافران را چيست كه به تو چشم دوخته و به سويت شتابانند،'),
(5412, 70, 37, 'از راست و از چپ گروه گروه'),
(5413, 70, 38, 'آيا هر مردى از آنها طمع دارد كه به بهشت پر نعمت در آيد؟!'),
(5414, 70, 39, 'هرگز، همانا ما آنان را از آنچه مى‌دانند- نطفه- آفريده‌ايم'),
(5415, 70, 40, 'پس سوگند به خداوند خاورها و باخترها كه هر آينه ما تواناييم،'),
(5416, 70, 41, 'بر آنكه بهتر از ايشان را به جايشان آريم، و ما واپس مانده- ناتوان و درمانده- نيستيم.'),
(5417, 70, 42, 'پس واگذارشان تا بيهوده گفت‌وگو و بازى كنند تا آن روزشان را كه وعده داده مى‌شوند ببينند.'),
(5418, 70, 43, 'روزى كه از گورها شتابان بيرون آيند گويى كه به سوى نشانه‌اى [كه برپا شده‌] مى‌شتابند'),
(5419, 70, 44, 'در حالى كه ديدگانشان از ترس فروشده، خوارى و زبونى آنها را فروگرفته است اين است آن روزى كه وعده داده مى‌شدند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'همانا نوح را به سوى قومش فرستاديم كه قوم خود را بيم ده پيش از آنكه عذابى دردناك بديشان آيد.'),
(5421, 71, 2, 'گفت: اى قوم من، همانا من شما را بيم‌كننده‌اى آشكارم،'),
(5422, 71, 3, 'كه خداى يكتا را بپرستيد و از او پروا كنيد و مرا فرمان بريد،'),
(5423, 71, 4, 'تا گناهانتان را بيامرزد و شما را تا سرآمدى نامبرده واپس دارد، كه سرآمد خداى چون فرا رسد واپس نيفتد، اگر مى‌دانستيد.'),
(5424, 71, 5, 'گفت: پروردگارا، همانا من قوم خود را شب و روز خواندم،'),
(5425, 71, 6, 'پس خواندن من آنان را جز رميدن و گريختن نيفزود'),
(5426, 71, 7, 'و هر گاه كه ايشان را بخواندم تا آنها را بيامرزى انگشتان خويش در گوشهاشان كردند- تا نشنوند- و جامه‌هاى خود را بر سر كشيدند- تا مرا نبينند- و [بر كفر خويش‌] پاى فشردند و بسى سركشى و بزرگ‌منشى نمودند.'),
(5427, 71, 8, 'و آنگاه آنان را آشكارا به آواز بلند بخواندم،'),
(5428, 71, 9, 'باز آنها را آشكارا بگفتم و در نهان بگفتم.'),
(5429, 71, 10, 'پس گفتم: از پروردگارتان آمرزش بخواهيد كه او آمرزگار است،'),
(5430, 71, 11, 'تا آسمان- ابر- را بر شما بارنده فرستد.'),
(5431, 71, 12, 'و شما را به مالها و پسران يارى كند و به شما بوستانها دهد و برايتان جويها پديد آرد.'),
(5432, 71, 13, 'چيست شما را كه خداى را به بزرگى باور نداريد- يا از عظمت خدا نمى‌ترسيد-؟'),
(5433, 71, 14, 'و حال آنكه شما را گوناگون- به مراحل يا از حالى پس از حالى- آفريده است.'),
(5434, 71, 15, 'آيا نديده‌ايد كه خدا هفت آسمان را چگونه مطابق- يا بر- يكديگر آفريد،'),
(5435, 71, 16, 'و ماه را در آنها تابان ساخت و خورشيد را چراغ درخشان كرد؟'),
(5436, 71, 17, 'و خدا شما را از زمين برويانيد روييدنى- چون گياه-.'),
(5437, 71, 18, 'سپس شما را به آن بازگرداند و باز شما را بيرون آرد بيرون آوردنى- به هنگام رستاخيز-.'),
(5438, 71, 19, 'و خدا زمين را براى شما بسترى گسترده كرد،'),
(5439, 71, 20, 'تا از آن به راه‌هاى فراخ- گشاده يا مختلف- برويد.'),
(5440, 71, 21, 'نوح گفت: پروردگارا، اينان مرا نافرمانى كردند و از كسى پيروى كردند كه وى را مال و فرزندانش جز زيانكارى نيفزود.'),
(5441, 71, 22, 'و نيرنگ ساختند نيرنگى بزرگ- در دين خدا، يا در هلاكت من-.'),
(5442, 71, 23, 'و گفتند: خدايانتان را وامگذاريد، و وَدّ و سُواع و يَغوث و يَعوق و نَسر را رها مكنيد.'),
(5443, 71, 24, 'و براستى كه بسيارى را گمراه كردند، و [بار خدايا،] ستمكاران- يعنى كافران- را جز گمراهى ميفزاى.'),
(5444, 71, 25, 'به سزاى گناهانشان غرق شدند- به طوفان- و به آتشى- آتش دوزخ- درآورده شدند، پس به جاى خدا براى خود هيچ ياورانى نيافتند.'),
(5445, 71, 26, 'و نوح گفت: پروردگارا، بر روى زمين از اين كافران هيچ كسى را مگذار،'),
(5446, 71, 27, 'كه اگر بگذاريشان، بندگان تو را گمراه مى‌كنند و جز بدكار و كافر نمى‌زايند.'),
(5447, 71, 28, 'پروردگارا، مرا و پدر و مادرم را و هر كه را كه با ايمان به خانه من در آمده باشد و همه مردان و زنان مؤمن را بيامرز و ستمكاران را جز هلاكت و نابودى ميفزاى.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'بگو: به من وحى شده است كه گروهى از پريان [به اين قرآن‌] گوش فرا داشتند، پس گفتند كه ما قرآنى شگفت- بسيار فصيح كه مانند سخن بشر نيست- شنيديم'),
(5449, 72, 2, 'كه به راه راست [و كمال‌] راه مى‌نمايد، پس به آن ايمان آورديم و هرگز كسى را به پروردگارمان انباز نگيريم'),
(5450, 72, 3, 'و همانا برتر است بزرگى و شكوه پروردگارمان كه نه همسرى گرفته و نه فرزندى'),
(5451, 72, 4, 'و همانا نابخرد از ما بر خدا دروغ و ناروا گفت- نسبت زن و فرزند به او داد-'),
(5452, 72, 5, 'و ما مى‌پنداشتيم كه هرگز آدمى و پرى بر خدا سخن دروغ نگويند'),
(5453, 72, 6, 'و همانا مردانى از آدميان به مردانى از پريان پناه مى‌بردند پس بر سركشى و گناهكارى آنان'),
(5454, 72, 7, 'و چنانكه شما پنداشتيد آنها نيز پنداشتند كه خداوند هرگز كسى را برنينگيزد- به پيامبرى يا به زندگى ديگر-'),
(5455, 72, 8, 'و ما آسمان را بسوديم- كنايه از اينكه به آسمان بالا رفتيم- پس آن را پر از نگهبانان نيرومند- يعنى فرشتگان- و شهابها- درخشها يا تيرهاى آتشين- يافتيم'),
(5456, 72, 9, 'و ما در آنجا'),
(5457, 72, 10, 'و ما نمى‌دانيم كه آيا در باره كسانى كه در زمين‌اند- آدميان- بدى خواسته شده، يا پروردگارشان براى آنها رهيابى [به نيكى و راه راست‌] خواسته است'),
(5458, 72, 11, 'و همانا از ما برخى نيك و شايسته‌اند و برخى جز اين. ما گروه‌هايى به راه‌هاى گوناگون بوديم'),
(5459, 72, 12, 'و ما مى‌دانستيم كه هرگز خداى را در زمين ناتوان نتوانيم ساخت و با گريختن- از عذاب او- ناتوانش نتوانيم كرد'),
(5460, 72, 13, 'و چون رهنمونى [به راه راست‌]- قرآن- را شنيديم به آن ايمان آورديم، كه هر كه به پروردگار خويش ايمان آورد نه از كاستى- در پاداش- ترسد و نه از ستم- در كيفر-'),
(5461, 72, 14, 'و همانا از ما برخى مسلمانند و برخى بيدادگر. پس هر كه [فرمان خداى را] گردن نهاد اينانند كه راه راست را جسته و يافته‌اند.'),
(5462, 72, 15, 'و اما بيدادگران، پس دوزخ را هيمه باشند.'),
(5463, 72, 16, 'و [به من وحى شده كه‌] اگر [آدميان و پريان‌] بر راه راست پايدارى كنند، هر آينه از آبى فراوان- كنايه از روزى فراوان- سيرابشان كنيم،'),
(5464, 72, 17, 'تا در باره آن بيازماييمشان، و هر كه از ياد و پند پروردگارش روى بگرداند او را به عذابى سخت درآرد.'),
(5465, 72, 18, 'و همانا مسجدها- يا اندامهاى سجده- براى خداست، پس با خداى يكتا كسى را مخوانيد- نپرستيد-.'),
(5466, 72, 19, 'و چون بنده خدا- پيامبر'),
(5467, 72, 20, 'بگو: همانا من تنها پروردگارم را مى‌خوانم- مى‌پرستم- و هيچ كس را با وى انباز نمى‌گيرم.'),
(5468, 72, 21, 'بگو: من براى شما نه هيچ زيانى را در توان دارم و نه هيچ رهنمونى به خير و صلاح و كمالى را [بى‌هدايت خدا].'),
(5469, 72, 22, 'بگو: همانا مرا هيچ كس از [عذاب‌] خداى پناه و زنهار ندهد- اگر در باره من عذابى خواسته باشد- و هرگز جز او پناهگاهى نيابم،'),
(5470, 72, 23, 'جز تبليغى از جانب خداى و رساندن پيامهاى او- كه تنها پناه من است-. و آنان كه خداى و پيامبرش را نافرمانى كنند، ايشان راست آتش دوزخ كه براى هميشه در آن جاويدان باشند.'),
(5471, 72, 24, 'تا آنگاه كه آنچه به آنها وعده داده شده ببينند خواهند دانست كه چه كسى را يارانى ناتوان‌تر و به شمار كمتر است.'),
(5472, 72, 25, 'بگو: نمى‌دانم كه آيا آنچه وعده داده مى‌شويد- عذاب- نزديك است يا پروردگارم براى آن مدتى مى‌نهد'),
(5473, 72, 26, 'داناى نهان و ناپيداست، پس كسى را بر غيب خود آگاه نسازد،'),
(5474, 72, 27, 'مگر آن را كه به پيامبرى پسندد و برگزيند، زيرا كه از پيش روى و از پشت سرش نگهبانانى گسيل مى‌دارد'),
(5475, 72, 28, 'تا معلوم كند كه پيامهاى پروردگارشان را رسانده‌اند، و [خداى‌] آنچه را نزد ايشان');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'اى جامه به خود پيچيده'),
(5477, 73, 2, 'شب را [به نماز] برخيز مگر اندكى'),
(5478, 73, 3, 'نيمى از آن را، يا اندكى از نيمه بكاه- تا به ثلث رسد-'),
(5479, 73, 4, 'يا بر آن بيفزاى- تا به دو ثلث رسد- و قرآن را شمرده و آرام- واضح و با درنگ- بخوان.'),
(5480, 73, 5, 'ما بر تو سخنى سنگين و دشوار- يا گرانمايه- خواهيم افكند- يعنى اين قرآن را بر تو فروخواهيم آورد-.'),
(5481, 73, 6, 'همانا ساعتهاى شب- يا قيام و عبادت در شب- استوارتر و پابرجاتر و به گفتار درست‌تر است- از عبادت روز-.'),
(5482, 73, 7, 'زيرا تو را در روز آمد و شد- كار و شغل- دراز و بسيار است- پس شب را ويژه عبادت و خلوت و مناجات كن-.'),
(5483, 73, 8, 'و نام پروردگارت را ياد كن و از همه بريده شو و يكسره روى دل بدو آر،'),
(5484, 73, 9, 'خداوند خاور و باختر، هيچ خدايى جز او نيست، پس او را كارساز خويش گير.'),
(5485, 73, 10, 'و بر آنچه مى‌گويند شكيبا باش و به شيوه‌اى نيكو از آنان كناره بگير.'),
(5486, 73, 11, 'و مرا با آن دروغ انگاران كامران و توانگر- صناديد قريش- واگذار، و اندكى مهلتشان ده.'),
(5487, 73, 12, 'هر آينه نزد ما بندهاى گران و آتش افروخته است،'),
(5488, 73, 13, 'و خوراكى گلوگير و عذابى دردناك،'),
(5489, 73, 14, 'به روزى كه زمين و كوه‌ها به لرزه در آيد، و كوه‌ها توده‌اى ريگ روان گردد.'),
(5490, 73, 15, 'همانا به سوى شما پيامبرى فرستاديم كه بر [اعمال‌] شما گواه است همچنانكه به سوى فرعون پيامبرى فرستاديم.'),
(5491, 73, 16, 'اما فرعون آن پيامبر را نافرمانى كرد، پس او را بگرفتيم گرفتنى سخت.'),
(5492, 73, 17, 'اگر [امروز] كافر باشيد، پس چگونه پروا مى‌كنيد از [عذاب‌] روزى كه كودكان را سپيد موى- پير- مى‌گرداند؟!'),
(5493, 73, 18, 'آسمان در آن روز شكافته شود وعده او شدنى است.'),
(5494, 73, 19, 'همانا اين آيتها يادآورى و پندى است، پس هر كه خواهد راهى به سوى پروردگار خويش فرا گيرد.'),
(5495, 73, 20, 'همانا پروردگارت مى‌داند كه تو نزديك دو ثلث شب و نيم آن و ثلث آن را به نماز برمى‌خيزى و گروهى از آنان كه با تواند نيز [چنين مى‌كنند]، و خدا شب و روز را اندازه مى‌كند- به اندازه آن داناست و مى‌داند كه شما چه اندازه از شب را به نماز مى‌ايستيد- مى‌داند كه شمار آن نتوانيد داشت پس [به مِهر و بخشايش خود] بر شما بازگشت- آن را بر شما سبك ساخت- پس هر چه ميسر شود از قرآن بخوانيد، مى‌داند كه برخى از شما بيمار و برخى ديگر به جست‌وجوى فضل خدا- روزى- در سفرند و گروهى ديگر در راه خدا كارزار مى‌كنند، پس هر چه ميسر شود از آن بخوانيد، و نماز را برپا داريد و زكات بدهيد و خداى را وام دهيد وامى نيكو- در راه خدا انفاق كنيد- و هر كار نيكى كه براى خويشتن پيش فرستيد آن را نزد خداى بازيابيد- به پاداش آن مى‌رسيد-، كه آن بهتر و مزدش بزرگتر است، و از خداى آمرزش بخواهيد، كه خدا آمرزگار و مهربان است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'اى جامه به خود پيچيده- يا اى جامه بسركشيده-،'),
(5497, 74, 2, 'برخيز و بيم كن'),
(5498, 74, 3, 'و پروردگارت را به بزرگى ياد كن'),
(5499, 74, 4, 'و جامه خويش را پاك ساز'),
(5500, 74, 5, 'و از عذاب دورى گزين'),
(5501, 74, 6, 'و چيزى به كسى مده كه بيشتر بستانى- يا منّت مَنِه كه كارت را بزرگ شمارى يا فزونى جويى-'),
(5502, 74, 7, 'و براى پروردگارت شكيبايى ورز.'),
(5503, 74, 8, 'پس چون در صور دميده شود- براى زنده‌شدن مردگان-،'),
(5504, 74, 9, 'آن روز روزى سخت و دشوار است،'),
(5505, 74, 10, 'بر كافران آسان نيست.'),
(5506, 74, 11, 'مرا با آن كه به تنهايى آفريدمش واگذار.'),
(5507, 74, 12, 'و او را مال فراوان دادم،'),
(5508, 74, 13, 'و پسرانى حاضر [در نزد وى‌]،'),
(5509, 74, 14, 'و براى او [نعمت دنيا را] بتمامى آماده و فراهم ساختم.'),
(5510, 74, 15, 'و باز طمع دارد كه [مال و فرزندان او را] بيفزايم.'),
(5511, 74, 16, 'هرگز، زيرا كه او با آيات ما- سخنان و نشانه‌هاى ما- ستيهنده بود.'),
(5512, 74, 17, 'زودا كه او را به سختى و دشوارى- عذاب- افكنم.'),
(5513, 74, 18, 'وى انديشيد و اندازه كرد.'),
(5514, 74, 19, 'پس مرگ بر او باد، چگونه اندازه كرد؟!'),
(5515, 74, 20, 'باز هم مرگ بر او باد، چگونه اندازه كرد؟!'),
(5516, 74, 21, 'آنگاه انديشه كرد.'),
(5517, 74, 22, 'سپس روى ترش نمود و چهره در هم كشيد.'),
(5518, 74, 23, 'سپس روى بگردانيد- از حق يا از ايمان يا از رسول- و گردنكشى كرد.'),
(5519, 74, 24, 'پس گفت: نيست اين [قرآن‌] مگر جادويى كه [از تعليم ساحران‌] باز گفته مى‌شود'),
(5520, 74, 25, 'اين نيست مگر گفتار آدمى.'),
(5521, 74, 26, 'زودا كه او را به سَقَر درآرم.'),
(5522, 74, 27, 'و تو را چه آگاه كرد كه سَقَر چيست؟!'),
(5523, 74, 28, '[آتشى است كه‌] نه باقى گذارد و نه رها كند.'),
(5524, 74, 29, 'سوزاننده و سياه كننده پوست [كافران‌] است.'),
(5525, 74, 30, 'بر آن نوزده [فرشته‌] گماشته است.'),
(5526, 74, 31, 'و ما دوزخبانان را جز فرشتگان نساختيم، و شمار آنها- نوزده- را جز آزمونى براى كسانى كه كافرند نكرديم، تا اهل كتاب- جهودان و ترسايان- يقين كنند- كه پيامبر ما راستگو و بر حق است و آنچه مى‌گويد از وحى مى‌گويد چنانكه نظير آن در تورات و انجيل هست- و كسانى را كه ايمان آورده‌اند ايمان بيفزايد- چون از اهل كتاب بشنوند كه آنچه پيامبر ما گفت حق است دلگرم‌تر و استوارتر شوند- و تا اهل كتاب و مؤمنان شك نكنند، و نيز براى آنكه كسانى كه در دلهاشان بيمارى است- منافقان- و كافران بگويند: خدا از اين وصف- شمار دوزخيان- چه خواسته است؟ بدين گونه خداوند هر كه را خواهد گمراه مى‌كند و هر كه را خواهد راه مى‌نمايد و [شمار] سپاهيان پروردگارت را جز او نداند، و اين- ذكر سَقَر و دوزخبانان- براى آدمى جز ياد و پندى نيست.'),
(5527, 74, 32, 'نه چنان است [كه كافران و منكران گويند] سوگند به ماه،'),
(5528, 74, 33, 'و سوگند به شب چون پشت كند و برود،'),
(5529, 74, 34, 'و سوگند به صبح چون رخ نمايد و روشن شود،'),
(5530, 74, 35, 'كه آن- سَقَر- يكى از پديده‌هاى بزرگ است'),
(5531, 74, 36, 'كه بيم‌كننده آدميان است.'),
(5532, 74, 37, 'براى هر كس از شما كه بخواهد پيشى گيرد- در كار نيك و طاعت- يا باز ايستد و واپس ماند- از كار نيك و طاعت-.'),
(5533, 74, 38, 'هر كسى در گرو كارى است كه كرده است.'),
(5534, 74, 39, 'مگر ياران دست راست- اهل سعادت-'),
(5535, 74, 40, 'كه در بهشتها باشند، مى‌پرسند،'),
(5536, 74, 41, 'از بزهكاران:'),
(5537, 74, 42, 'چه چيز شما را به دوزخ در آورد؟'),
(5538, 74, 43, 'گويند: ما از نمازگزاران نبوديم'),
(5539, 74, 44, 'و بينوا را طعام نمى‌داديم'),
(5540, 74, 45, 'و با ياوه‌گويان ياوه مى‌گفتيم'),
(5541, 74, 46, 'و روز پاداش را دروغ مى‌انگاشتيم.'),
(5542, 74, 47, 'تا ما را مرگ فرا رسيد.'),
(5543, 74, 48, 'پس شفاعت شفيعان به آنها سود ندهد.'),
(5544, 74, 49, 'پس چيست آنها را كه از اين پند رويگردانند؟'),
(5545, 74, 50, 'گويى كه گورخرانى رميده‌اند،'),
(5546, 74, 51, 'كه از شير- يا شكارچى- گريخته‌اند.'),
(5547, 74, 52, 'بلكه هر مردى از ايشان مى‌خواهد كه او را نامه‌هايى گشاده بدهند.'),
(5548, 74, 53, 'نه چنين است [كه مى‌پندارند]، بلكه از [عذاب‌] آن جهان نمى‌ترسند.'),
(5549, 74, 54, 'نه چنين است [كه مى‌گويند]، همانا اين قرآن يادآورى و پند است.'),
(5550, 74, 55, 'پس هر كه خواهد آن را ياد كند و پند گيرد.'),
(5551, 74, 56, 'و ياد نكنند و پند نگيرند مگر آنكه خدا خواهد اوست سزاوار پروا كردن- از وى- و سزاوار آمرزيدن.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'سوگند مى‌خورم به روز رستاخيز.'),
(5553, 75, 2, 'و سوگند مى‌خورم به نفس سرزنشگر [كه هر آينه آدمى را به روز رستاخيز برخواهم انگيخت‌].'),
(5554, 75, 3, 'آيا آدمى پندارد كه استخوانهايش را فراهم نخواهيم آورد؟'),
(5555, 75, 4, 'چرا، ما قادريم بر اينكه سرانگشتان او را هم راست و درست كنيم.'),
(5556, 75, 5, 'بلكه آدمى مى‌خواهد فراپيش خود- يعنى در آينده- نيز بدكارى كند.'),
(5557, 75, 6, 'مى‌پرسد: روز رستاخيز كى خواهد بود؟'),
(5558, 75, 7, 'پس آنگاه كه چشم [از ترس‌] خيره شود'),
(5559, 75, 8, 'و ماه تيره گردد'),
(5560, 75, 9, 'و خورشيد و ماه جمع شوند- به گونه‌اى كه نورشان برود و هر دو تيره گردند-.'),
(5561, 75, 10, 'آن روز آدمى گويد: گريزگاه كجاست؟'),
(5562, 75, 11, 'هرگز پناهگاهى نيست.'),
(5563, 75, 12, 'آن روز [بازگشت و] قرارگاه به سوى پروردگار توست.'),
(5564, 75, 13, 'آن روز آدمى را بدانچه پيش فرستاده و واپس نهاده آگاه كنند.'),
(5565, 75, 14, 'بلكه آدمى بر نفس خويش بيناست- كه چه كرده و چه مى‌كند-'),
(5566, 75, 15, 'هر چند كه عذرهاى خود را [بر گناه‌] در ميان آرد.'),
(5567, 75, 16, 'زبان خود را به اين [قرآن‌] مجنبان تا بدان- خواندن و حفظ آن- شتاب كنى'),
(5568, 75, 17, 'همانا فراهم آوردن و خواندن آن بر ماست.'),
(5569, 75, 18, 'پس چون آن را بخوانيم- به زبان جبرئيل- خواندنش را پيروى كن.'),
(5570, 75, 19, 'آنگاه بيان آن بر ماست.'),
(5571, 75, 20, '[كافران را بگو:] نه چنان است [كه مى‌گوييد،] بلكه شما اين جهان شتابان و زودگذر را دوست مى‌داريد'),
(5572, 75, 21, 'و آن جهان را فرو مى‌گذاريد.'),
(5573, 75, 22, 'آن روز چهره‌هايى تازه و خرم است،'),
(5574, 75, 23, 'كه [پاداش‌] پروردگار خويش را چشم مى‌دارند'),
(5575, 75, 24, 'و چهره‌هايى تيره و ترش است'),
(5576, 75, 25, 'گمان دارند كه با آنها رفتارى كمرشكن مى‌كنند.'),
(5577, 75, 26, 'آرى، آنگاه كه جان به چنبر گردن- گلوگاه- رسد،'),
(5578, 75, 27, 'گفته شود- فرشتگان يا كسان محتضر گويند-: كيست افسون‌كننده- شفا دهنده؟!'),
(5579, 75, 28, 'و بداند كه گاه جدايى- از دنيا- است،'),
(5580, 75, 29, 'و ساقهاى پا به هم پيچد.'),
(5581, 75, 30, 'آن روز، روانه شدن به سوى پروردگار توست.'),
(5582, 75, 31, 'نه باور داشت و نه نماز گزارد'),
(5583, 75, 32, 'و ليكن دروغ انگاشت و روى گرداند'),
(5584, 75, 33, 'سپس خرامان به سوى كسانش رفت'),
(5585, 75, 34, 'واى بر تو پس واى بر تو'),
(5586, 75, 35, 'باز هم واى بر تو پس واى بر تو.'),
(5587, 75, 36, 'آيا آدمى مى‌پندارد كه او را بيهوده و رها فرو گذارند- كه در دنيا مكلّف نباشد و در آخرت برانگيخته و حسابرسى نشود و پاداش و كيفر نبيند-؟!'),
(5588, 75, 37, 'آيا او نطفه‌اى از منى كه [در رحم‌] ريخته مى‌شد، نبود؟'),
(5589, 75, 38, 'سپس خون بسته بود، پس [خدايش‌] بيافريد و درست اندام كرد؟'),
(5590, 75, 39, 'پس، از آن دو گونه: مرد و زن، ساخت.'),
(5591, 75, 40, 'آيا آن [خداى‌] قادرى نيست كه مردگان را زنده كند؟');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'آيا بر آدمى روزگارى بر آمد كه چيزى يادكردنى نبود؟'),
(5593, 76, 2, 'ما آدمى را از نطفه‌اى آميخته- نطفه مرد و زن- بيافريديم، او را مى‌آزماييم و از اين رو شنوا و بينايش ساختيم'),
(5594, 76, 3, 'ما راه را به او بنموديم، يا سپاسگزار باشد و يا ناسپاس.'),
(5595, 76, 4, 'ما براى كافران زنجيرها و بندها و آتش سوزان آماده ساخته‌ايم.'),
(5596, 76, 5, 'همانا نيكوكاران از جامى مى‌نوشند كه آميزه آن كافور است- سرد و خوشبوست-.'),
(5597, 76, 6, 'چشمه‌اى كه بندگان خدا از آن مى‌آشامند هر گونه و به هر جا كه بخواهند روانش مى‌سازند.'),
(5598, 76, 7, 'به نذر [خود] وفا مى‌كنند و از روزى كه بدى و سختى آن فراگير است مى‌ترسند.'),
(5599, 76, 8, 'و طعام را با دوستى آن- با آنكه خود به آن مايل‌تر و نيازمندترند- به بينوا و يتيم و اسير مى‌خورانند'),
(5600, 76, 9, '[زبان حالشان اين است كه‌] همانا شما را براى خشنودى خداى مى‌خورانيم از شما پاداش و سپاسى نمى‌خواهيم'),
(5601, 76, 10, 'ما از پروردگارمان در روزى كه گرفته و دژم و سخت و سهمگين است مى‌ترسيم.'),
(5602, 76, 11, 'پس خداى ايشان را از بدى و سختى آن روز نگاه دارد و ايشان را تازگى و شادمانى پيش آرد.'),
(5603, 76, 12, 'و ايشان را به سبب آنكه شكيبايى كردند بهشت و پرنيان پاداش دهد.'),
(5604, 76, 13, 'در آنجا بر تختها [ى آراسته‌] تكيه زنند، در آنجا نه [گرمى‌] آفتابى بينند و نه سرمايى.'),
(5605, 76, 14, 'و سايه‌هاى [درختان‌] آن بر آنان نزديك باشد و ميوه‌هايش به آسانى رام گشته- در دسترس- است.'),
(5606, 76, 15, 'و جامهايى از نقره و سبوهايى از آبگينه بر ايشان مى‌گردانند'),
(5607, 76, 16, 'آبگينه‌هايى از سيم- سفيد و شفاف به صفاى آبگينه- كه [فراخور نوشندگان‌] به اندازه كرده باشند.'),
(5608, 76, 17, 'و در آنجا جامى بنوشانندشان كه آميزه آن زنجبيل است.'),
(5609, 76, 18, '[از] چشمه‌اى در آنجا كه سلسبيل- روان و گوارا- نامند.'),
(5610, 76, 19, 'و پسرانى جاودانى و پاينده بر گردشان مى‌گردند- به خدمت- كه چون آنان را ببينى پندارى كه مرواريد پراكنده‌اند.'),
(5611, 76, 20, 'و چون به آنجا بنگرى نعمتها بينى- كه در وصف نگنجد- و پادشاهيى بزرگ.'),
(5612, 76, 21, 'بر بالايشان جامه‌هاى سبز از ديباى نازك و ستبر است، و با دستواره‌هاى سيمين زيور شده‌اند، و پروردگارشان شرابى پاكيزه بنوشاندشان.'),
(5613, 76, 22, 'همانا اين براى شما پاداشى است و كوشش شما پذيرفته و سپاس داشته است.'),
(5614, 76, 23, 'همانا ما قرآن را بر تو فرو فرستاديم، فرستادنى- به تدريج، سوره سوره و آيه آيه-.'),
(5615, 76, 24, 'پس بر فرمان پروردگارت شكيبايى كن و از آنان هيچ گنه‌پيشه يا ناسپاسى را فرمان مبر.'),
(5616, 76, 25, 'و نام پروردگارت را بامداد و شبانگاه ياد كن- در نماز و بيرون نماز-.'),
(5617, 76, 26, 'و در پاسى از شب او را سجده آر- نماز بگزار- و شبى دراز او را به پاكى بستاى.'),
(5618, 76, 27, 'همانا اينان اين [دنياى‌] شتابان و زودگذر را دوست مى‌دارند و روزى گران- يعنى رستاخيز- را پشت سرشان مى‌افكنند- فراموش مى‌كنند-.'),
(5619, 76, 28, 'ما ايشان را آفريديم و آفرينش- يا بندهاى اندامهاى- آنان را استوار ساختيم، و هر گاه بخواهيم به جاى آنها مانندشان را بياوريم آوردنى- كه از آن ناتوان نيستيم-.'),
(5620, 76, 29, 'همانا اين- آيات قرآن يا اين سوره- يادآورى و پند است، پس هر كه خواهد به سوى پروردگار خويش راهى فرا گيرد.'),
(5621, 76, 30, 'و شما نمى‌خواهيد مگر آنكه خداى بخواهد. همانا خداوند دانا و با حكمت است.'),
(5622, 76, 31, 'هر كه را خواهد در مهر و بخشايش خويش درآرد و ستمكاران را عذابى دردناك آماده ساخته است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'سوگند به آن فرستاده‌شدگان پياپى'),
(5624, 77, 2, 'پس به آن بادهاى سخت وزنده'),
(5625, 77, 3, 'و سوگند به آن پراكنده كنندگان پراكندنى'),
(5626, 77, 4, 'پس به آن جداكنندگان جداكردنى- ميان حق و باطل-.'),
(5627, 77, 5, 'پس به آن القاكنندگان ذكر- يادها و پندها-'),
(5628, 77, 6, 'براى باز نمودن عذر و براى بيم‌كردن'),
(5629, 77, 7, 'كه همانا آنچه وعده داده مى‌شويد- رستاخيز- شدنى و بودنى است.'),
(5630, 77, 8, 'پس آنگاه كه ستارگان تاريك و ناپديد شوند'),
(5631, 77, 9, 'و آنگاه كه آسمان شكافته شود'),
(5632, 77, 10, 'و آنگاه كه كوه‌ها از بيخ بركنده شوند'),
(5633, 77, 11, 'و آنگاه كه پيامبران را ميعادگاهى نهند- تا بر امتهاى خود گواهى دهند-.'),
(5634, 77, 12, 'براى كدام روز اينها واپس داشته شده؟'),
(5635, 77, 13, 'براى روز جدايى.'),
(5636, 77, 14, 'و چه دانى كه روز جدايى- يا داورى- چيست؟'),
(5637, 77, 15, 'در آن روز واى بر دروغ‌انگاران.'),
(5638, 77, 16, 'آيا پيشينيان را هلاك نكرديم؟'),
(5639, 77, 17, 'سپس پسينيان- مانند كفّار مكّه- را از پى آنها در آريم- هلاك سازيم-.'),
(5640, 77, 18, 'با بزهكاران چنين مى‌كنيم.'),
(5641, 77, 19, 'در آن روز واى بر دروغ انگاران.'),
(5642, 77, 20, 'آيا شما را از آبى پست و بى‌ارج نيافريديم؟'),
(5643, 77, 21, 'پس آن را در جايگاهى استوار نهاديم،'),
(5644, 77, 22, 'تا اندازه‌اى دانسته- دوران باردارى-.'),
(5645, 77, 23, 'پس توانا بوديم و نيكو توانا بوديم- يا: پس سرنوشت شما را تقدير كرديم و نيكو تقدير كرديم-.'),
(5646, 77, 24, 'در آن روز واى بر دروغ انگاران.'),
(5647, 77, 25, 'آيا زمين را فراهم‌آرنده نساختيم،'),
(5648, 77, 26, 'مر زندگان و مردگان را- كه زندگان و مردگان را در خود جاى دهد-؟'),
(5649, 77, 27, 'و در آن كوه‌هاى سخت استوار و بلند پديد كرديم و شما را آبى خوشگوار نوشانديم.'),
(5650, 77, 28, 'در آن روز واى بر دروغ‌انگاران.'),
(5651, 77, 29, 'برويد به سوى آنچه دروغش مى‌انگاشتيد- دوزخ-.'),
(5652, 77, 30, 'برويد به سوى سايه‌اى سه شاخه- از دود-،'),
(5653, 77, 31, 'كه نه سايه گستر است و نه از آتش بازدارد.'),
(5654, 77, 32, '[آتشى‌] كه شراره‌ها مى‌افكند همچون كوشكها- از بزرگى-'),
(5655, 77, 33, 'تو گويى شترانى زرد رنگ است.'),
(5656, 77, 34, 'در آن روز واى بر دروغ‌انگاران.'),
(5657, 77, 35, 'اين است روزى كه سخن نيارند گفت'),
(5658, 77, 36, 'و آنان را اجازه ندهند تا عذر بخواهند.'),
(5659, 77, 37, 'در آن روز واى بر دروغ‌انگاران.'),
(5660, 77, 38, 'اين است روز داورى، كه شما- اى منكران اين امت- و پيشينيان را فراهم آورديم-.'),
(5661, 77, 39, 'پس اگر نيرنگ و ترفندى داريد، در باره [عذاب‌] من به كار بريد.'),
(5662, 77, 40, 'در آن روز واى بر دروغ‌انگاران.'),
(5663, 77, 41, 'همانا پرهيزگاران در سايه‌ها و [كنار] چشمه‌ها باشند.'),
(5664, 77, 42, 'و ميوه‌هايى از آنچه بخواهند.'),
(5665, 77, 43, 'بخوريد و بياشاميد، نوش و گوارا، به پاداش آنچه مى‌كرديد.'),
(5666, 77, 44, 'همانا نيكوكاران را چنين پاداش مى‌دهيم.'),
(5667, 77, 45, 'در آن روز واى بر دروغ‌انگاران.'),
(5668, 77, 46, 'بخوريد و اندكى [در اين جهان‌] برخوردار شويد، كه شما بزهكارانيد.'),
(5669, 77, 47, 'در آن روز واى بر دروغ‌انگاران.'),
(5670, 77, 48, 'و چون به آنان گفته شود ركوع كنيد- نماز گزاريد- ركوع نكنند.'),
(5671, 77, 49, 'در آن روز واى بر دروغ‌انگاران.'),
(5672, 77, 50, 'پس به كدام سخن بعد از آن');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, '[كافران‌] از چه مى‌پرسند- از تو يا از يكديگر-؟'),
(5674, 78, 2, 'از آن خبر بزرگ- رستاخيز-.'),
(5675, 78, 3, 'همان كه در باره آن اختلاف دارند.'),
(5676, 78, 4, 'نه چنان است [كه مى‌پندارند]، بزودى بدانند.'),
(5677, 78, 5, 'باز هم نه چنان است [كه مى‌پندارند]، بزودى بدانند.'),
(5678, 78, 6, 'آيا زمين را بستر و قرارگاه [شما] نگردانيديم؟'),
(5679, 78, 7, 'و كوه‌ها را ميخهايى- تا زمين بدان استوار باشد و نجنبد-؟'),
(5680, 78, 8, 'و شما را جفت جفت- نر و ماده- آفريديم.'),
(5681, 78, 9, 'و خوابتان را مايه آسايش و را حتى ساختيم.'),
(5682, 78, 10, 'و شب را پوششى قرار داديم.'),
(5683, 78, 11, 'و روز را هنگام جستن مايه و اسباب زندگانى- وقت كار و كوشش شما- كرديم.'),
(5684, 78, 12, 'و بر فرازتان هفت آسمان استوار بساختيم.'),
(5685, 78, 13, 'و چراغى درخشان- خورشيد- پديد كرديم.'),
(5686, 78, 14, 'و از ابرهاى باردار آبى سخت روان و ريزان فرو فرستاديم،'),
(5687, 78, 15, 'تا بدان دانه و گياه بيرون آريم.'),
(5688, 78, 16, 'و بوستانهايى از درختان انبوه.'),
(5689, 78, 17, 'همانا روز جدايى- يادآورى- وعده‌گاه است'),
(5690, 78, 18, 'روزى كه در صور دميده شود، پس شما گروه گروه بياييد'),
(5691, 78, 19, 'و آسمان شكافته و گشوده شود پس درهايى گردد- براى نزول فرشتگان يا براى راه‌يافتن انسان به عالم فرشتگان-.'),
(5692, 78, 20, 'و كوه‌ها به گردش آيد پس سرابى شود- يعنى حقيقت خود را از دست بدهد و ريگى روان گردد-.'),
(5693, 78, 21, 'همانا دوزخ كمينگاه است،'),
(5694, 78, 22, 'سركشان- كافران لجوج و معاند- را بازگشتگاه است.'),
(5695, 78, 23, 'روزگارى دراز- يعنى هميشه- در آن درنگ كنند.'),
(5696, 78, 24, 'در آنجا نه هيچ خنكى- نسيمى- چشند و نه هيچ آشاميدنيى،'),
(5697, 78, 25, 'مگر آب جوشان و خونابه دوزخيان،'),
(5698, 78, 26, 'پاداشى فراخور [كارهاى آنان‌].'),
(5699, 78, 27, 'همانا آنان حساب [قيامت‌] را اميد- باور- نمى‌داشتند،'),
(5700, 78, 28, 'و آيات ما را همى‌دروغ انگاشتند.'),
(5701, 78, 29, 'و ما همه چيز را در نوشته‌اى به شمار آورده‌ايم- و مطابق همان پاداش و كيفر مى‌دهيم-.'),
(5702, 78, 30, 'پس بچشيد كه نيفزاييم شما را مگر عذاب.'),
(5703, 78, 31, 'همانا پرهيزگاران را رستگارى است'),
(5704, 78, 32, 'باغها و تاكها'),
(5705, 78, 33, 'و زنانى نار پستان همسال.'),
(5706, 78, 34, 'و جامهاى لبريز از شراب،'),
(5707, 78, 35, 'در آنجا سخن بيهوده و دروغ نشنوند- بر خلاف شرابخواران دنيا كه هذيان و دروغ گويند و شنوند-'),
(5708, 78, 36, 'پاداشى است از پروردگارت، بخششى حساب شده.'),
(5709, 78, 37, 'پروردگار آسمانها و زمين و آنچه ميان آنهاست، خداى رحمان، كه كسى را ياراى گفتگو با او نيست،'),
(5710, 78, 38, 'در روزى كه روح- فرشته همراه وحى- و فرشتگان به صف ايستند سخن نگويند مگر كسى كه خداى رحمان او را اجازه دهد و سخن درست گويد.'),
(5711, 78, 39, 'اين است آن روز حق- متحقق و بودنى- پس هر كه خواهد به سوى پروردگار خويش راه بازگشتى پيش گيرد.'),
(5712, 78, 40, 'همانا ما شما را از عذابى نزديك بيم داديم، روزى كه آدمى آنچه را دستهايش پيش فرستاده است مى‌نگرد، و كافر گويد: اى كاش خاك بودمى- تا برانگيخته و حسابرسى نمى‌شدم-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'سوگند به آن كشندگان- يا بركنندگان- به نيرو و به سختى- فرشتگانى كه جانهاى كافران را به قوت و شدت گيرند-'),
(5714, 79, 2, 'و سوگند به آن بازگيرندگان بازگرفتنى به نرمى و آسانى- فرشتگانى كه جانهاى مؤمنان و نيكان را به نرمى از تن بازگيرند و بيرون برند-'),
(5715, 79, 3, 'و سوگند به آن شناكنندگان شناكردنى- فرشتگانى كه آمد و شد مى‌كنند، يا شناوران آسمانى مانند ستارگان و ماه و خورشيد-'),
(5716, 79, 4, 'پس به آن پيشى‌گيرندگان پيشى‌گرفتنى- در فرمانبردارى-'),
(5717, 79, 5, 'پس به آن تدبيركنندگان كارها- فرشتگانى كه تدبير امور خلق مى‌كنند- [كه هر آينه برانگيخته مى‌شويد]،'),
(5718, 79, 6, 'در روزى كه آن لرزاننده- دميدن نخست- همه آفريدگان را بلرزاند،'),
(5719, 79, 7, 'در حالى كه لرزاننده ديگرى در پى آن در آيد- دميدن دوم كه برانگيختن با آن است-.'),
(5720, 79, 8, 'دلهايى در آن روز تپنده و لرزان است.'),
(5721, 79, 9, 'ديدگانشان فروخفته است- از ترس-.'),
(5722, 79, 10, '[منكران بعث‌] گويند: آيا ما [پس از مرگ‌] به حال نخست بازگردانيده مى‌شويم؟!'),
(5723, 79, 11, 'آيا آنگاه كه استخوانهايى پوسيده گشتيم؟!'),
(5724, 79, 12, 'گفتند: آنگاه آن بازگشتى زيانبار است- از روى استهزا مى‌گفتند كه اگر ما را بازگشتى باشد زيانكار باشيم زيرا آن را دروغ مى‌انگاشتيم-.'),
(5725, 79, 13, 'پس همانا آن بازگشت يك بانگ انگيزنده است و بس- كه اسرافيل دردمد-'),
(5726, 79, 14, 'كه ناگاه بر روى زمين فراآيند.'),
(5727, 79, 15, 'آيا داستان موسى به تو رسيده است- تا تو را دلدارى باشد بر تكذيب قوم-؟'),
(5728, 79, 16, 'آنگاه كه پروردگارش او را در وادى مقدس طوى بخواند'),
(5729, 79, 17, 'كه به سوى فرعون برو كه او سركشى كرده است.'),
(5730, 79, 18, 'پس بگو: آيا تو را هيچ ميل و رغبتى هست كه به پاكى گرايى؟'),
(5731, 79, 19, 'و تو را به سوى پروردگارت راه نمايم تا بترسى- و سركشى نكنى-؟'),
(5732, 79, 20, 'پس آن نشانه بزرگتر- عصا و يد بيضا- را به او بنمود.'),
(5733, 79, 21, 'پس تكذيب كرد و نافرمانى نمود.'),
(5734, 79, 22, 'سپس پشت كرده مى‌كوشيد- در هلاكت موسى-.'),
(5735, 79, 23, 'پس [همه را] گرد آورد آنگاه آواز داد'),
(5736, 79, 24, 'گفت: منم خداى برتر شما.'),
(5737, 79, 25, 'پس خداوند او را به كيفر آن جهان- دوزخ- و اين جهان- غرق‌شدن- بگرفت.'),
(5738, 79, 26, 'هر آينه در اين عبرتى است براى هر كه بترسد- از خدا يا از عذاب-.'),
(5739, 79, 27, 'آيا شما به آفرينش سخت‌تريد يا آسمان كه آن را برافراشت'),
(5740, 79, 28, 'سقفش را فراداشت و آن را راست و بسامان كرد.'),
(5741, 79, 29, 'و شبش را تاريك ساخت و روزش را بيرون آورد.'),
(5742, 79, 30, 'و زمين را پس از آن بگسترانيد.'),
(5743, 79, 31, '[و] از آن آبش و گياهانش- يا چراگاهش- را بيرون آورد.'),
(5744, 79, 32, 'و كوه‌ها را [بر آن‌] استوار و پابرجا گردانيد.'),
(5745, 79, 33, 'تا شما و چارپايانتان را بهره‌مندى و كالايى باشد.'),
(5746, 79, 34, 'پس چون آن بلاى سخت بزرگ- روز رستاخيز- بيايد،'),
(5747, 79, 35, 'روزى كه آدمى آنچه كوشيده است به ياد آرد'),
(5748, 79, 36, 'و دوزخ براى هر كه ببيند- او را بينايى باشد- آشكار شود [و مردم در آن روز دو گروه شوند]'),
(5749, 79, 37, 'و اما هر كه سركشى كرده از حد درگذشت،'),
(5750, 79, 38, 'و زندگى اين جهان را برگزيد،'),
(5751, 79, 39, 'پس همانا دوزخ جايگاه اوست.'),
(5752, 79, 40, 'و اما هر كس كه از مقام پروردگار خود- يا از ايستادن در پيشگاه پروردگارش- ترسيد و خويشتن را از آرزو و كام دل بازداشت،'),
(5753, 79, 41, 'پس همانا بهشت جايگاه اوست.'),
(5754, 79, 42, 'تو را از روز رستاخيز مى‌پرسند كه هنگام آمدن آن كى خواهد بود؟'),
(5755, 79, 43, 'تو از ياد آن در چه چيزى؟- از هنگام آمدن آن چه خبر دارى-؟'),
(5756, 79, 44, '[دانش‌] پايانه و سرانجام آن با پروردگار توست.'),
(5757, 79, 45, 'تو تنها هر كس را كه از آن مى‌ترسد بيم‌دهنده‌اى و بس- آنچه به تو مربوط است همين است-.'),
(5758, 79, 46, 'گويى كه آنان روزى كه آن را ببينند درنگ نكرده‌اند- در دنيا يا در گور- مگر شامگاهى يا چاشتگاهى.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'روى در هم كشيد و رخ برتافت'),
(5760, 80, 2, 'چون آن نابينا به سويش آمد.'),
(5761, 80, 3, 'و تو چه دانى شايد او به پاكى گرايد- به سبب آنچه شنود-.'),
(5762, 80, 4, 'يا پند گيرد و آن پند سودش دهد.'),
(5763, 80, 5, 'اما آن كه بى‌نياز است- به مال و جاه-'),
(5764, 80, 6, 'پس تو بدو روى مى‌آرى و به او مى‌پردازى!'),
(5765, 80, 7, 'و چيست بر تو- تو را چه زيان دارد- كه [آن كافر] پاكى نورزد؟- يا: و تو را باكى نيست كه او پاكى نمى‌ورزد-.'),
(5766, 80, 8, 'و اما آن كه با كوشش و شتاب نزد تو آمده،'),
(5767, 80, 9, 'در حالى كه [از خداى‌] مى‌ترسد،'),
(5768, 80, 10, 'پس تو از او [به ديگرى‌] مى‌پردازى.'),
(5769, 80, 11, 'همانا اين [آيات قرآن‌] يادآورى و پندى است- براى آدميان-'),
(5770, 80, 12, 'پس هر كه خواهد، آن را ياد كند و پند گيرد.'),
(5771, 80, 13, '[آنها] در دفترهايى است گرامى‌داشته،'),
(5772, 80, 14, 'بلندپايه و پاكيزه- از عيب و بيهوده و دروغ و از دستهاى شياطين-'),
(5773, 80, 15, 'در دست سفيرانى'),
(5774, 80, 16, 'بزرگوار و نيكوكار.'),
(5775, 80, 17, 'مرده‌باد آدمى، چه كافر و ناسپاس است!'),
(5776, 80, 18, 'او را از چه چيز آفريد؟'),
(5777, 80, 19, 'از نطفه‌اى آفريدش پس به اندازه‌اش نهاد.'),
(5778, 80, 20, 'سپس راه [به ايمان و طاعت‌] را برايش آسان ساخت.'),
(5779, 80, 21, 'سپس او را بميرانيد و در گور كرد.'),
(5780, 80, 22, 'سپس آنگاه كه بخواهد او را برانگيزد- باز زنده گرداند-.'),
(5781, 80, 23, 'نه چنين است كه هنوز آنچه را [خدا] به او فرمان داده نگزارده و بجاى نياورده است.'),
(5782, 80, 24, 'پس آدمى بايد به خوراك خود بنگرد- تا عبرت گيرد-.'),
(5783, 80, 25, 'همانا ما آب [باران‌] را فرو ريختيم فروريختنى'),
(5784, 80, 26, 'سپس زمين را [با سر برآوردن گياهان‌] بشكافتيم شكافتنى.'),
(5785, 80, 27, 'پس در آن دانه‌ها رويانيديم'),
(5786, 80, 28, 'و انگور و سبزى،'),
(5787, 80, 29, 'و درخت زيتون و خرما،'),
(5788, 80, 30, 'و بوستانهاى پر درخت،'),
(5789, 80, 31, 'و ميوه و علف،'),
(5790, 80, 32, 'تا برخوردارى باشد براى شما و چارپايان شما.'),
(5791, 80, 33, 'پس چون آن بانگ هول‌انگيز [رستاخيز] بيايد'),
(5792, 80, 34, 'روزى كه آدمى از برادر خود مى‌گريزد'),
(5793, 80, 35, 'و از مادر و پدر خويش'),
(5794, 80, 36, 'و از همسر و فرزندانش'),
(5795, 80, 37, 'هر مردى از آنان را در آن روز كارى است كه بدان مى‌پردازد- كسى به ديگرى نپردازد-.'),
(5796, 80, 38, 'رويهايى در آن روز تابان و درخشان است'),
(5797, 80, 39, 'خندان و شادمان.'),
(5798, 80, 40, 'و رويهايى در آن روز بر آنها گَرد نشسته است'),
(5799, 80, 41, 'تيرگى و سياهى آنها را فرو پوشد'),
(5800, 80, 42, 'اينانند كافران بدكار گنه‌پيشه.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'آنگاه كه خورشيد در هم پيچيده و بى‌نور شود.'),
(5802, 81, 2, 'و آنگاه كه ستارگان تيره گردند و فروريزند.'),
(5803, 81, 3, 'و آنگاه كه كوه‌ها از جاى رانده شوند- و چون ريگ روان گردند-.'),
(5804, 81, 4, 'و آنگاه كه شتران آبستن رهاگذاشته شوند- بى‌صاحب بمانند-.'),
(5805, 81, 5, 'و آنگاه كه جانوران وحشى گردآورده شوند.'),
(5806, 81, 6, 'و آنگاه كه درياها به جوش آيند- يا با اتصال به يكديگر پر شوند-.'),
(5807, 81, 7, 'و آنگاه كه كسان [با يكديگر] جفت و قرين شوند- نيكان با نيكان و بدان با بدان-.'),
(5808, 81, 8, 'و آنگاه كه از دختر زنده به گورشده پرسند:'),
(5809, 81, 9, 'به كدام گناه كشته شده است؟'),
(5810, 81, 10, 'و آنگاه كه نامه‌ها- نامه‌هاى اعمال- گشوده شود.'),
(5811, 81, 11, 'و آنگاه كه آسمان بركنده و درنَوَرْديده شود.'),
(5812, 81, 12, 'و آنگاه كه دوزخ افروخته و شعله‌ور گردد.'),
(5813, 81, 13, 'و آنگاه كه بهشت نزديك گردانيده شود- براى اهلش-.'),
(5814, 81, 14, '[در آن روز] هر كسى بداند آنچه را فراآورده باشد.'),
(5815, 81, 15, 'پس سوگند به ستارگان بازگردنده'),
(5816, 81, 16, 'آن روندگان پنهان‌شونده- در روز-'),
(5817, 81, 17, 'و سوگند به شب چون پشت كند و برود- يا در آيد-.'),
(5818, 81, 18, 'و سوگند به بامداد چون بردمد'),
(5819, 81, 19, 'كه هر آينه اين [قرآن‌] گفتار فرستاده‌اى بزرگوار- جبرئيل'),
(5820, 81, 20, 'نيرومندى كه نزد خداوند عرش، گرامى و داراى مكانت و منزلت است'),
(5821, 81, 21, 'در آنجا- در ميان فرشتگان- فرمانروا و [در پيشگاه خدا] امين است- در رساندن وحى-.'),
(5822, 81, 22, 'و يار شما- پيامبر'),
(5823, 81, 23, 'و هر آينه او- يعنى جبرئيل- را در افق روشن- كناره جهان برين- ديده است.'),
(5824, 81, 24, 'و او بر غيب- وحى- بخيل نيست- كه چيزى از آن بكاهد و ناگفته بگذارد-.'),
(5825, 81, 25, 'و آن سخن شيطان رانده‌شده نيست.'),
(5826, 81, 26, 'پس كجا مى‌رويد؟!'),
(5827, 81, 27, 'آن جز يادآورى و پندى براى جهانيان نيست'),
(5828, 81, 28, 'براى هر كس از شما كه بخواهد به راه راست رود.'),
(5829, 81, 29, 'و نمى‌خواهيد مگر آنكه خداى، پروردگار جهانيان، بخواهد.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'آنگاه كه آسمان بشكافد،'),
(5831, 82, 2, 'و آنگاه كه ستارگان پراكنده شوند- فروريزند-،'),
(5832, 82, 3, 'و آنگاه كه درياها شكافته و در هم روان شوند،'),
(5833, 82, 4, 'و آنگاه كه گورها زير و زبر گردند- تا مردگان زنده بيرون آيند-،'),
(5834, 82, 5, 'هر كسى بداند آنچه پيش فرستاده و آنچه واپس انداخته است- سنتهايى كه از خود بجا گذاشته و با ديگران در پاداش عمل به آنها شريك است-.'),
(5835, 82, 6, 'اى آدمى، چه چيز تو را به پروردگار بزرگوارت- بخشنده و نيكوكارت- بفريفت؟!'),
(5836, 82, 7, 'آن كه تو را آفريد پس راست و بسامان كرد و [اندامهايت را] معتدل و هماهنگ گردانيد.'),
(5837, 82, 8, 'به هر صورتى كه خود خواست- زن و مرد، زيبا و زشت، بلند و كوتاه و ...- تركيبت كرد.'),
(5838, 82, 9, 'نه چنان است [كه مى‌پنداريد]، بلكه رستاخيز- روز پاداش- را دروغ مى‌انگاريد.'),
(5839, 82, 10, 'و هر آينه بر شما نگهبانانى باشد،'),
(5840, 82, 11, 'بزرگوارانى نويسنده'),
(5841, 82, 12, 'كه مى‌دانند آنچه مى‌كنيد.'),
(5842, 82, 13, 'هر آينه نيكان در بهشت پر نعمت‌اند.'),
(5843, 82, 14, 'و همانا بدكاران و گنه‌پيشگان در دوزخ‌اند'),
(5844, 82, 15, 'كه در روز حساب و پاداش بدان در آيند- يا به آتش آن بسوزند-.'),
(5845, 82, 16, 'و از آن ناپيدا و ناپديد نيستند- يعنى بيرون نيايند-.'),
(5846, 82, 17, 'و تو چه دانى كه روز حساب و پاداش چيست؟'),
(5847, 82, 18, 'باز چه دانى تو كه روز حساب و پاداش چيست؟'),
(5848, 82, 19, '[به ياد آر] روزى كه هيچ كس براى هيچ كس چيزى- بازداشتن عذابى يا رساندن سودى- در توان ندارد، و فرمان در آن روز همه خداى راست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'واى بر كم‌فروشان'),
(5850, 83, 2, 'آنان كه چون از مردم پيمانه ستانند تمام مى‌ستانند'),
(5851, 83, 3, 'و چون خود براى آنها بپيمايند يا بسنجند- با ترازو وزن كنند- كم مى‌دهند.'),
(5852, 83, 4, 'آيا اينان گمان نمى‌برند كه برانگيخته خواهند شد،'),
(5853, 83, 5, 'به روزى بزرگ،'),
(5854, 83, 6, 'روزى كه همه مردم در برابر پروردگار جهانيان بايستند- براى حساب-؟'),
(5855, 83, 7, 'حقا كه كارنامه بدكاران در سجين است.'),
(5856, 83, 8, 'و چه دانى تو كه سجين چيست؟'),
(5857, 83, 9, 'نامه‌اى است نوشته- و نشاندار و خوانا-.'),
(5858, 83, 10, 'در آن روز واى بر دروغ‌انگاران.'),
(5859, 83, 11, 'آنان كه روز حساب و پاداش را دروغ مى‌شمرند.'),
(5860, 83, 12, 'و آن را دروغ نينگارد مگر هر تجاوزگر گنه‌پيشه‌اى'),
(5861, 83, 13, 'كه هر گاه آيات ما بر او خوانده شود، گويد: افسانه‌هاى پيشينيان است.'),
(5862, 83, 14, 'نه چنان است [كه مى‌گويند] بلكه آنچه مى‌كردند- گناهانشان- بر دلهاشان چيره شده و آنها را پوشانده- يا زنگار بسته- است.'),
(5863, 83, 15, 'حقا كه آنان در آن روز از [رحمت‌] پروردگارشان در پرده‌اند- محروم‌اند-.'),
(5864, 83, 16, 'پس هر آينه آنان به دوزخ در آيند- به آتش آن بسوزند-.'),
(5865, 83, 17, 'سپس به آنها گفته شود: اين است آنچه دروغش مى‌شمرديد.'),
(5866, 83, 18, 'حقا كه كارنامه نيكوكاران در علّيّين است.'),
(5867, 83, 19, 'و چه دانى تو كه علّيّين چيست؟'),
(5868, 83, 20, 'نامه‌اى است نوشته- و نشاندار و خوانا-.'),
(5869, 83, 21, 'آن نامه را مقرّبان- فرشتگان مقرّب- حاضر و گواهند- آن را نگاه مى‌دارند و در رستاخيز بدان گواهى خواهند داد-.'),
(5870, 83, 22, 'هر آينه نيكوكاران در نعمتهايى فراوانند.'),
(5871, 83, 23, 'بر تختها [تكيه زده‌] مى‌نگرند- به هر چه بخواهند-.'),
(5872, 83, 24, 'در رويهاشان خرّمى و تازگى نعمتها [ى بهشت‌] را مى‌شناسى.'),
(5873, 83, 25, 'از شرابى سر به مُهر سيراب شوند،'),
(5874, 83, 26, 'كه مُهر آن از مُشك است و در آن- شراب يا نعمتهاى بهشتى- راغبان و كوشندگان بايد بكوشند و بدان پيشى گيرند.'),
(5875, 83, 27, 'و آميزه‌اش از [چشمه‌] تسنيم- بهترين نوشيدنى بهشت- است.'),
(5876, 83, 28, 'چشمه‌اى كه مقرّبان از آن مى‌نوشند.'),
(5877, 83, 29, 'همانا بزهكاران- يعنى كافران- به كسانى كه ايمان آورده‌اند مى‌خنديدند'),
(5878, 83, 30, 'و چون بر آنان مى‌گذشتند به يكديگر چشمك مى‌زدند- با چشم و ابرو به طعنه و مسخره اشاره مى‌كردند'),
(5879, 83, 31, 'و چون به سوى كسان خويش بازمى‌گشتند شادان و نازان- از ريشخندى كه كرده‌اند- بازمى‌گشتند'),
(5880, 83, 32, 'و چون آنان را مى‌ديدند مى‌گفتند: هر آينه اينها گمراهانند'),
(5881, 83, 33, 'و حال آنكه آنها'),
(5882, 83, 34, 'پس امروز- روز رستاخيز- كسانى كه ايمان آورده‌اند به كافران مى‌خندند'),
(5883, 83, 35, 'در حالى كه بر تختها [ى آراسته تكيه زده‌اند] مى‌نگرند- به دوزخيان كه به انواع عذاب گرفتارند-.'),
(5884, 83, 36, 'آيا كافران بدانچه مى‌كردند پاداش داده شدند؟');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'آنگاه كه آسمان شكافته شود.'),
(5886, 84, 2, 'و به پروردگار خود گوش سپارد و فرمان برد و اين سزاوار اوست.'),
(5887, 84, 3, 'و آنگاه كه زمين كشيده و گسترده شود- به اينكه كوه‌ها از ميان برداشته و زمين هموار گردد-.'),
(5888, 84, 4, 'و آنچه را درون خود دارد- مُردگان- بيرون افكند و تهى گردد.'),
(5889, 84, 5, 'و به پروردگار خود گوش سپارد و فرمان برد و اين سزاوار اوست.'),
(5890, 84, 6, 'اى آدمى، همانا تو به سوى پروردگارت سخت كوشنده و رونده‌اى، پس او را ديدار خواهى كرد.'),
(5891, 84, 7, 'پس آن كه كارنامه‌اش به دست راستش داده شود،'),
(5892, 84, 8, 'زودا كه با او حساب كنند حسابى آسان.'),
(5893, 84, 9, 'و شادان به سوى كسان خويش- از مؤمنان و بهشتيان- باز گردد.'),
(5894, 84, 10, 'و اما آن كه كارنامه‌اش از پشت سرش داده شود،'),
(5895, 84, 11, 'پس زودا كه به هلاكت و نابودى- و گفتن واى بر من- فرياد برآرد'),
(5896, 84, 12, 'و به آتش افروخته و سوزان در آيد.'),
(5897, 84, 13, 'همانا وى در ميان كسان خود [به ناروا] شادان بود.'),
(5898, 84, 14, 'همانا او مى‌پنداشت كه هرگز باز نخواهد گشت،'),
(5899, 84, 15, 'آرى، همانا خدايش به وى- و كردارش- بينا بود.'),
(5900, 84, 16, 'پس سوگند مى‌خورم به شفق- سرخى بعد از غروب آفتاب كه در مغرب ديده مى‌شود-.'),
(5901, 84, 17, 'و سوگند به شب و آنچه فراهم آرد و فرو پوشد- شبانگاه كه چارپايان و جانوران در آرامگاه خود گرد آيند-.'),
(5902, 84, 18, 'و سوگند به ماه آنگاه كه [فروغش‌] كامل گردد- شب چهاردهم-،'),
(5903, 84, 19, 'كه هر آينه شما حالى را از پس حالى- از مرگ و بعث و حساب و ...- پشت سر مى‌گذاريد.'),
(5904, 84, 20, 'پس آنان را چيست كه ايمان نمى‌آورند؟!'),
(5905, 84, 21, 'و چون بر آنان قرآن خوانده شود به سجده درنمى‌آيند- خضوع نمى‌كنند-؟!'),
(5906, 84, 22, 'بلكه آنان كه كافر شدند تكذيب مى‌كنند- قرآن يا رستاخيز را-.'),
(5907, 84, 23, 'و خدا بدانچه در دل نهان مى‌دارند داناتر است.'),
(5908, 84, 24, 'پس آنها را به عذابى دردناك مژده ده!'),
(5909, 84, 25, 'مگر آنان كه ايمان آورده و كارهاى نيك و شايسته كرده‌اند كه ايشان را مزد بى‌پايان- يا بى‌منت- است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'سوگند به آسمان داراى برجها'),
(5911, 85, 2, 'و به روز وعده داده شده- روز رستاخيز-'),
(5912, 85, 3, 'و به گواه و گواهى‌شده [كه پاداش اعمال راست است‌].'),
(5913, 85, 4, 'مرگ بر صاحبان آن خندق،'),
(5914, 85, 5, 'آن آتش داراى هيمه بسيار.'),
(5915, 85, 6, 'آنگاه كه بر [كنار] آن نشسته بودند- تا شكنجه آن آتش افكنده‌شدگان را تماشا كنند-.'),
(5916, 85, 7, 'و آنان بر آنچه با مؤمنان مى‌كردند حاضر و تماشاگر بودند.'),
(5917, 85, 8, 'و از آنها چيزى را ناپسند نشمردند- يا از آنها كينه نداشتند- جز اينكه به خداى تواناى بى‌همتا و ستوده ايمان آورده بودند'),
(5918, 85, 9, 'آن كه او راست پادشاهى آسمانها و زمين، و خدا بر هر چيزى گواه است.'),
(5919, 85, 10, 'همانا كسانى كه مردان و زنان مؤمن را آزار و شكنجه كردند- ياران اخدود و مشركان مكه-، سپس توبه نكردند، آنان راست عذاب دوزخ- مانند زقّوم و گرزهاى آهنين- و آنان راست عذاب سوزان.'),
(5920, 85, 11, 'همانا كسانى كه ايمان آورده و كارهاى نيك و شايسته كردند ايشان راست بهشتهايى كه از زير آنها جويها روان است، اين است كاميابى و رستگارى بزرگ.'),
(5921, 85, 12, 'هر آينه گرفتن پروردگار تو بسى سخت است.'),
(5922, 85, 13, 'همانا اوست كه [آفرينش آفريدگان را] آغاز مى‌كند و باز مى‌گرداند- به هنگام رستاخيز-.'),
(5923, 85, 14, 'و اوست آمرزگار و دوستدار [مؤمنان‌]'),
(5924, 85, 15, 'خداوند بزرگوار عرش.'),
(5925, 85, 16, 'هر چه را بخواهد كننده است.'),
(5926, 85, 17, 'آيا خبر آن لشكرها به تو رسيده است؟'),
(5927, 85, 18, '[لشكرهاى‌] فرعون و ثمود- قوم صالح-.'),
(5928, 85, 19, 'بلكه آنها كه كافر شدند در تكذيب و باور نداشتن به سر مى‌برند.'),
(5929, 85, 20, 'و خداوند از همه سو ايشان را فراگيرنده است- به دانش و قدرت بر آنها احاطه دارد-.'),
(5930, 85, 21, 'بلكه آن [سخن‌]، قرآنى ارجمند است،'),
(5931, 85, 22, 'در لوحى محفوظ- كه تغيير و تحريف به آن راه ندارد-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'سوگند به آسمان و آينده در شب،'),
(5933, 86, 2, 'و تو چه دانى كه آينده در شب چيست؟'),
(5934, 86, 3, 'ستاره درخشان است،'),
(5935, 86, 4, 'كه هيچ كس نيست مگر آنكه بر او نگهبانى هست.'),
(5936, 86, 5, 'پس آدمى بايد بنگرد كه از چه آفريده شده؟'),
(5937, 86, 6, 'از آبى جهنده آفريده شده،'),
(5938, 86, 7, 'كه از پشت [پدر] و استخوانهاى سينه مادر بيرون مى‌آيد.'),
(5939, 86, 8, 'هر آينه او'),
(5940, 86, 9, 'در روزى كه نهانها- نيتها و رازها- آشكار شود.'),
(5941, 86, 10, 'پس او را نه نيرويى است و نه ياورى.'),
(5942, 86, 11, 'سوگند به آسمان بازگردنده،'),
(5943, 86, 12, 'و به زمين شكاف‌دارنده- شكافنده به گياهان-.'),
(5944, 86, 13, 'كه هر آينه آن'),
(5945, 86, 14, 'و سخنى بيهوده و شوخى نيست.'),
(5946, 86, 15, 'همانا آنان نيرنگ و ترفند مى‌انديشند.'),
(5947, 86, 16, 'و من نيز تدبير نهانى مى‌كنم.'),
(5948, 86, 17, 'پس كافران را مهلت ده، اندكى فروگذارشان و درنگشان ده- تا عذاب ما بديشان فرا رسد-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'نام پروردگار والاترت را- كه برتر از همه چيزهاست- به پاكى بستاى.'),
(5950, 87, 2, 'آن كه آفريد- آفريدگان را- و راست و هموار ساخت- سامان بخشيد-.'),
(5951, 87, 3, 'و آن كه اندازه كرد پس راه نمود.'),
(5952, 87, 4, 'و آن كه [گياه‌] چراگاه را بيرون آورد- برويانيد-.'),
(5953, 87, 5, 'پس آن را خاشاكى خشك و سياه گردانيد.'),
(5954, 87, 6, 'زودا كه تو را [به قرآن‌] خوانا گردانيم پس فراموش نمى‌كنى'),
(5955, 87, 7, 'مگر آنچه خداى خواهد همانا او آشكار را مى‌داند و نيز آنچه را پنهان و پوشيده است.'),
(5956, 87, 8, 'و تو را براى آسانترين راه آماده مى‌سازيم- تو را توفيق پيمودن راه آسان در تبليغ اسلام مى‌دهيم يا به شريعت آسان راه مى‌نماييم-.'),
(5957, 87, 9, 'پس پند ده و يادآورى كن اگر پنددادن و يادآورى سود داشته باشد.'),
(5958, 87, 10, 'زودا كه پند گيرد آن كه [از خداى‌] مى‌ترسد.'),
(5959, 87, 11, 'و بدبخت‌ترين [مردم‌] از آن'),
(5960, 87, 12, 'آن كه به آن آتش بزرگ- دوزخ- درمى‌آيد'),
(5961, 87, 13, 'آنگاه در آن آتش نه مى‌ميرد و نه زنده مى‌ماند.'),
(5962, 87, 14, 'براستى رستگار شد هر كه پاكى ورزيد- از كفر و شرك و گناهان-'),
(5963, 87, 15, 'و نام پروردگارش را ياد كرد- به توحيد و تكبير- پس نماز گزارد.'),
(5964, 87, 16, 'بلكه شما زندگى اين جهان را برمى‌گزينيد'),
(5965, 87, 17, 'و حال آنكه آن جهان بهتر و پاينده‌تر است.'),
(5966, 87, 18, 'هر آينه اين- كه ياد شد- در كتابهاى پيشينيان هست'),
(5967, 87, 19, '[در] كتابهاى ابراهيم و موسى.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'آيا خبر آن پوشنده فراگير- يعنى رستاخيز- به تو رسيده است؟'),
(5969, 88, 2, 'رويهايى در آن روز فروشكسته‌اند- ترسناك و خوارند-.'),
(5970, 88, 3, 'كوشنده‌اند- در اين جهان- و رنج كشنده- در آن جهان-.'),
(5971, 88, 4, 'در آتشى سخت سوزنده در آيند- يا به آتشى سخت سوزنده بسوزند-.'),
(5972, 88, 5, 'از چشمه‌اى بسيار گرم آبشان دهند.'),
(5973, 88, 6, 'آنها را هيچ خوردنى نيست مگر خار درشت تلخ- كه هيچ چارپايى نمى‌خورد-،'),
(5974, 88, 7, 'كه نه فربه مى‌كند و نه از گرسنگى سودى دهد- گرسنگى را از ميان نمى‌برد-.'),
(5975, 88, 8, 'رويهايى در آن روز تازه و نازنده- به نعمت- باشند'),
(5976, 88, 9, 'از كردار خود خشنود'),
(5977, 88, 10, 'در بهشتى برين.'),
(5978, 88, 11, 'در آنجا سخن بيهوده و ياوه نشنوند.'),
(5979, 88, 12, 'در آنجا چشمه‌اى روان باشد.'),
(5980, 88, 13, 'در آنجا تختهايى است بلند.'),
(5981, 88, 14, 'و سبوهايى نهاده- سرشار از مىْ-،'),
(5982, 88, 15, 'و بالشهايى به رديف چيده،'),
(5983, 88, 16, 'و فرشهايى گسترده.'),
(5984, 88, 17, 'پس آيا به شتر نمى‌نگرند كه چگونه آفريده شده؟'),
(5985, 88, 18, 'و به آسمان كه چگونه برافراشته شده؟'),
(5986, 88, 19, 'و به كوه‌ها كه چگونه نهاده شده و استوار گشته؟'),
(5987, 88, 20, 'و به زمين كه چگونه گسترانيده شده؟'),
(5988, 88, 21, 'پس يادآورى كن و پند ده كه تو يادآور و پنددهنده‌اى و بس.'),
(5989, 88, 22, 'بر آنان گماشته و چيره نيستى.'),
(5990, 88, 23, 'مگر آن كس را كه پشت كرد و كفر ورزيد- كه او را سود نكند-.'),
(5991, 88, 24, 'پس خدا او را عذاب كند بزرگترين عذاب.'),
(5992, 88, 25, 'همانا بازگشتشان به سوى ماست.'),
(5993, 88, 26, 'آنگاه حسابشان بر ماست.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'سوگند به سپيده‌دم- هر بامداد يا بامداد دهم ذو الحجه-'),
(5995, 89, 2, 'و به شبهاى ده‌گانه'),
(5996, 89, 3, 'و به جفت و طاق'),
(5997, 89, 4, 'و به شب چون برود.'),
(5998, 89, 5, 'آيا در آن- كه ياد شد- براى خردمند سوگندى [بسنده‌] هست؟'),
(5999, 89, 6, 'آيا نديده‌اى كه پروردگارت با عاد- قوم هود- چه كرد؟'),
(6000, 89, 7, '[با] ارم كه داراى ستونها- يا كاخهاى بلند- بود؟'),
(6001, 89, 8, 'كه مانند آن در شهرها آفريده نشده بود.'),
(6002, 89, 9, 'و [با] ثمود- قوم صالح-، آنان كه در آن وادى- ميان عمّان و حَضْرَمُوت- سنگ مى‌بريدند- براى ساختمانهاشان-.'),
(6003, 89, 10, 'و [با] فرعون داراى ميخها- كه به چهار ميخ مى‌كشيد و شكنجه مى‌كرد-.'),
(6004, 89, 11, 'آنان كه در شهرها سركشى كردند و از حد درگذشتند.'),
(6005, 89, 12, 'و در آنها بسى تباهى كردند.'),
(6006, 89, 13, 'پس پروردگار تو تازيانه عذاب را بر آنها فرو ريخت.'),
(6007, 89, 14, 'هر آينه پروردگارت در كمينگاه است.'),
(6008, 89, 15, 'اما آدمى را چون پروردگارش بيازمايد و او را گرامى دارد و نعمتش دهد، گويد: پروردگارم مرا گرامى داشت- چون شايسته اين گراميداشت بودم-.'),
(6009, 89, 16, 'و اما چون او را بيازمايد و روزى‌اش را بر او تنگ سازد، گويد: پروردگارم مرا خوار و زبون كرد- به سبب اين ابتلا-.'),
(6010, 89, 17, 'نه چنان است- نه آن كرامت است و نه اين اهانت-، بلكه يتيم را گرامى نمى‌داريد- او را از ارثش محروم مى‌كنيد و مالش را مى‌خوريد-'),
(6011, 89, 18, 'و يكديگر را بر طعام‌دادن بينوا برنمى‌انگيزيد.'),
(6012, 89, 19, 'و ميراث- ميراث خود و يتيم- را مى‌خوريد خوردنى پُر و يكجا- بتمامى يا حلال و حرام را با هم-.'),
(6013, 89, 20, 'و مال را دوست مى‌داريد دوست‌داشتنى تمام و بسيار.'),
(6014, 89, 21, 'حقّا كه چون زمين سخت در هم شكسته و كوبيده شود'),
(6015, 89, 22, 'و [فرمان‌] پروردگارت بيايد، و فرشتگان صف به صف بيايند'),
(6016, 89, 23, 'و در آن روز دوزخ آورده شود- هويدا گردد- در آن روز آدمى به ياد آرد- و به خود آيد-، و به يادآوردن كجا او را سود دهد؟'),
(6017, 89, 24, 'گويد: اى كاش براى زندگانى‌ام [كار نيك‌] پيش مى‌فرستادم.'),
(6018, 89, 25, 'پس در آن روز، هيچ كس به [مانند] عذاب او'),
(6019, 89, 26, 'و هيچ كس به [مانند] بندكشيدن او به بند نكشد'),
(6020, 89, 27, 'اى جان آرام- آرامش و اطمينان يافته-،'),
(6021, 89, 28, 'به سوى پروردگارت بازگرد، تو از او خشنود و او از تو خشنود.'),
(6022, 89, 29, 'پس در ميان بندگان [ويژه‌] من درآى'),
(6023, 89, 30, 'و در بهشت [ويژه‌] من درآى.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'سوگند ياد مى‌كنم به اين شهر- مكه-.'),
(6025, 90, 2, 'در حالى كه تو به اين شهر فرود آمده‌اى- تو در آن اقامت دارى و زيست مى‌كنى-.'),
(6026, 90, 3, 'و سوگند به پدرى [بزرگ‌]- ابراهيم'),
(6027, 90, 4, 'هر آينه آدمى را در سختى و رنج آفريده‌ايم.'),
(6028, 90, 5, 'آيا مى‌پندارد كه هرگز هيچ كس بر او توانا نيست- كه سركشى و نافرمانى مى‌كند-؟'),
(6029, 90, 6, 'گويد: مالى فراوان را نابود كردم- در دشمنى با پيامبر'),
(6030, 90, 7, 'آيا مى‌پندارد كه هيچ كس او را نمى‌بيند؟!'),
(6031, 90, 8, 'آيا براى او دو چشم نساخته‌ايم- كه بدان مى‌بيند-؟'),
(6032, 90, 9, 'و زبانى- كه بدان سخن مى‌گويد- و دو لب- كه دهان او را مى‌پوشاند و در سخن گفتن و خوردن و آشاميدن بدو كمك مى‌كند-؟'),
(6033, 90, 10, 'و او را دو راه- خير و شر- ننموديم؟'),
(6034, 90, 11, 'پس به آن گريوه- گردنه يا راه سخت و دشوار در كوه مراد راه خير است كه برخى از مصاديق مهمّ آن در آيات بعد مى‌آيد- در نيامد.'),
(6035, 90, 12, 'و تو چه دانى كه آن گريوه چيست؟'),
(6036, 90, 13, 'آزادكردن گردنى- يعنى بنده‌اى-'),
(6037, 90, 14, 'يا طعام‌دادن در روز گرسنگى- كمبود غذا-،'),
(6038, 90, 15, 'به يتيمى خويشاوند،'),
(6039, 90, 16, 'يا به بينوايى خاكنشين'),
(6040, 90, 17, 'وانگهى از كسانى باشد كه ايمان آورده‌اند و يكديگر را به شكيبايى- پايدارى بر ايمان- اندرز مى‌دهند و به مهربانى و بخشايش سفارش مى‌كنند.'),
(6041, 90, 18, 'آنان خجستگان و سعادتمندانند.'),
(6042, 90, 19, 'و كسانى كه به آيات ما كافر شدند، آنان شومان و بدبختانند'),
(6043, 90, 20, 'كه بر آنهاست آتشى فراگير و پوشنده- آتش دوزخ كه در آن روزنى نيست و همه را در خود فرومى‌پوشد-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سوگند به خورشيد و روشنايى آن'),
(6045, 91, 2, 'و به ماه چون از پى آن برآيد- كه بعد از غروب آفتاب با جلوه بيشتر پديدار مى‌شود-.'),
(6046, 91, 3, 'و به روز چون آن را آشكار و پديدار كند.'),
(6047, 91, 4, 'و به شب آنگاه كه آن را فروپوشد.'),
(6048, 91, 5, 'و به آسمان و آن كه برافراشتش.'),
(6049, 91, 6, 'و به زمين و آن كه گسترانيدش.'),
(6050, 91, 7, 'و به نفس- جان، روان- و آن كه آن را راست و درست ساخت- يا سامان بخشيد-.'),
(6051, 91, 8, 'پس بدكارى و پرهيزگارى‌اش را به وى الهام كرد.'),
(6052, 91, 9, 'براستى رستگار شد هر كه آن را پاك گردانيد و پاكيزه داشت- از كفر و گناهان-.'),
(6053, 91, 10, 'و نوميد و بى بهره گشت هر كه آن را بيالود- به كفر و گناهان-.'),
(6054, 91, 11, 'ثمود از روى سركشى خود، [صالح و دين حق را] تكذيب كردند.'),
(6055, 91, 12, 'آنگاه كه بدبخت‌ترينشان انگيخته شد و برخاست.'),
(6056, 91, 13, 'پس پيامبر خدا- صالح- به آنان گفت: ماده‌شتر خداى را با آبشخورش واگذاريد- آزارش مكنيد و از آب خوردن بازش مداريد-.'),
(6057, 91, 14, 'پس او را دروغگو شمردند و آن را پى كردند- كشتند- پس خدايشان به سزاى گناهشان آنان را هلاك و نابود كرد و [با خاك‌] يكسانشان ساخت.'),
(6058, 91, 15, 'و از سرانجام آن بيم نمى‌داشت.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'سوگند به شب آنگاه كه فرو پوشد- روز يا زمين يا خورشيد را-.'),
(6060, 92, 2, 'و به روز آنگاه كه روشن و پديدار شود.'),
(6061, 92, 3, 'و به آن كه نر و ماده را آفريد.'),
(6062, 92, 4, 'كه هر آينه كوشش شما بسى پراكنده است- مختلف است نه يكسان-.'),
(6063, 92, 5, 'پس هر كه بخشيد- مال خود را- و پرهيزگارى كرد'),
(6064, 92, 6, 'و آن [وعده‌] نيكو- پاداش اخروى- را باور داشت و راست انگاشت'),
(6065, 92, 7, 'پس زودا كه او را براى راه آسان- خير يا راه راست و شريعت آسان يا بهشت يا همه اينها- آماده سازيم.'),
(6066, 92, 8, 'و اما هر كه بخل ورزيد- حق خدا را در مال ادا نكرد- و بى‌نيازى نمود- از دين حق و ثواب الهى-'),
(6067, 92, 9, 'و آن [وعده‌] نيكو را انكار كرد و دروغ انگاشت'),
(6068, 92, 10, 'پس زودا كه او را براى راه دشوار- شرّ كه گمراهى و كفر و آتش دوزخ است- آماده سازيم.'),
(6069, 92, 11, 'و چه سود دارد او را مالش آنگاه كه به هلاكت درافتد.'),
(6070, 92, 12, 'هر آينه رهنمونى بر ماست.'),
(6071, 92, 13, 'و هر آينه ما راست آن جهان و اين جهان.'),
(6072, 92, 14, 'پس شما را از آتشى كه زبانه مى‌كشد بيم مى‌دهم.'),
(6073, 92, 15, 'در آن درنيايد و بدان نسوزد مگر آن بدبخت‌تر'),
(6074, 92, 16, 'آن كه تكذيب كرد و [از حق‌] برگشت.'),
(6075, 92, 17, 'و زودا كه آن پرهيزگارتر، از آن'),
(6076, 92, 18, 'آن كه مال خود را مى‌دهد تا پاك و پيراسته شود- از پليدى بخل و امساك-.'),
(6077, 92, 19, 'و هيچ كس را نزد او نعمتى نيست كه بايست پاداش داده شود- انفاق او به تلافى يا به پس‌دادن حقوق ديگران نيست-.'),
(6078, 92, 20, 'جز آنكه براى جستن خشنودى پروردگار والاى خويش [انفاق مى‌كند].'),
(6079, 92, 21, 'و هر آينه خشنود خواهد شد- آنگاه كه به ثواب و پاداش خود برسد-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'سوگند به روشنايى و برآمدن روز.'),
(6081, 93, 2, 'و سوگند به شب چون در آيد و آرام گيرد- تاريكى آن همه را فرو پوشد-.'),
(6082, 93, 3, 'كه پروردگارت نه تو را فروگذاشته و نه دشمن داشته است.'),
(6083, 93, 4, 'و هر آينه آن جهان براى تو بهتر است از اين جهان.'),
(6084, 93, 5, 'و هر آينه پروردگارت تو را بخششى خواهد كرد كه خشنود شوى.'),
(6085, 93, 6, 'آيا تو را يتيمى نيافت پس جاى و پناه داد- به وسيله عمويت ابو طالب-؟'),
(6086, 93, 7, 'و راه گم‌كرده‌ات يافت پس راه نمود.'),
(6087, 93, 8, 'و نيازمندت يافت پس بى‌نياز و توانگر ساخت- به مال خديجه، يا به قناعت-.'),
(6088, 93, 9, 'پس، يتيم را خوار مدار و مران.'),
(6089, 93, 10, 'و خواهنده- سائل نيازمند- را بانگ مزن و مران.'),
(6090, 93, 11, 'و به نعمت پروردگارت- نبوّت و قرآن و هدايت و ...- سخن گوى- كه شكر آن است-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'آيا ما سينه تو را برايت گشاده نكرديم؟'),
(6092, 94, 2, 'و بار گرانت را از تو فروننهاديم؟'),
(6093, 94, 3, 'آن [بار گرانى‌] كه پشتت را بشكسته بود.'),
(6094, 94, 4, 'و نام و آوازه‌ات را بلند نكرديم؟'),
(6095, 94, 5, 'پس، همانا با دشوارى آسانى است.'),
(6096, 94, 6, 'همانا با دشوارى آسانى است.'),
(6097, 94, 7, 'پس چون [از كار] فارغ شدى به عبادت كوش و رنج آن را بر خود هموار كن.'),
(6098, 94, 8, 'و به سوى پروردگارت روى آر- آهنگ او كن و هر چه خواهى از او بخواه-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'سوگند به انجير و زيتون.'),
(6100, 95, 2, 'و سوگند به كوه سينا.'),
(6101, 95, 3, 'و سوگند به اين شهر ايمن و ايمنى‌بخش- يعنى مكه-.'),
(6102, 95, 4, 'كه هر آينه ما آدمى را در نيكوترين نگاشت- صورت و تركيب و اعتدالى كه بتواند به پايه‌هاى بلند دست يابد- بيافريديم.'),
(6103, 95, 5, 'سپس- آنگاه كه ناسپاسى كرده و كفر ورزيد و براى آنچه آفريديمش قيام نكرد- او را فروتر از همه فروتران گردانيديم.'),
(6104, 95, 6, 'مگر كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند، پس ايشان راست مزدى بى‌پايان- يا بى‌منت-.'),
(6105, 95, 7, 'پس با اين حال- بعد از ظهور دلايل- چه چيز تو را- اى آدمى- به دروغ‌انگاشتن حساب و پاداش وا مى‌دارد؟'),
(6106, 95, 8, 'آيا خدا داورترين همه داوران نيست؟');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'بخوان به نام پروردگارت كه بيافريد- همه آفريدگان را-.'),
(6108, 96, 2, 'آدمى را از خونى بسته آفريد.'),
(6109, 96, 3, 'بخوان، و پروردگار تو بزرگوارترين [بخشندگان‌] است،'),
(6110, 96, 4, 'آن كه [نوشتن‌] با قلم بياموخت.'),
(6111, 96, 5, 'آدمى را آنچه نمى‌دانست بياموخت.'),
(6112, 96, 6, 'آرى، هر آينه آدمى سركشى مى‌كند و از حد مى‌گذرد،'),
(6113, 96, 7, 'از آن رو كه خود را بى‌نياز و توانگر بيند.'),
(6114, 96, 8, 'همانا بازگشت [همه‌] به سوى پروردگار توست.'),
(6115, 96, 9, 'مرا بگو كه آيا آن [كافر سركش‌] كه باز مى‌دارد،'),
(6116, 96, 10, 'بنده‌اى [گزين و بهين‌] را آنگاه كه نماز گزارد [آيا نمى‌داند كه خدا او را مى‌بيند]؟'),
(6117, 96, 11, 'مرا بگو كه اگر [آن بنده‌] بر راه راست باشد،'),
(6118, 96, 12, 'يا به پرهيزگارى فرمان دهد [حال آن كافر چه خواهد بود]؟'),
(6119, 96, 13, 'مرا بگو [از حال آن كافر] اگر [حق را] تكذيب كند و [از ايمان و كار نيك‌] روى گرداند [سزاوار چه كيفرى است؟]'),
(6120, 96, 14, 'آيا ندانسته است كه خدا مى‌بيند [و او را كيفر مى‌دهد]؟'),
(6121, 96, 15, 'نه، اگر باز نايستد هر آينه موى پيشانى او را بگيريم و بكشيمش [به دوزخ‌].'),
(6122, 96, 16, 'موى پيشانى دروغزنى بزهكار را.'),
(6123, 96, 17, 'پس او انجمنش را بخواند.'),
(6124, 96, 18, 'ما نيز آن دوزخبانان را خواهيم خواند.'),
(6125, 96, 19, 'نه، او را فرمان مبر، و سجده كن و [به پروردگار خويش‌] نزديكى جوى.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'همانا آن'),
(6127, 97, 2, 'و تو چه دانى كه شب قدر چيست؟'),
(6128, 97, 3, 'شب قدر بهتر از هزار ماه است- كه در آن شب قدر نباشد-.'),
(6129, 97, 4, 'فرشتگان و روح- جبرئيل يا فرشته همراه وحى- در آن شب به فرمان پروردگارشان از براى هر كارى- كه خداى حكم كرده باشد- فرومى‌آيند.'),
(6130, 97, 5, 'سلامت است آن');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'كسانى از اهل كتاب- جهودان و ترسايان- و مشركان كه [اكنون به اسلام‌] كافر شدند، [از آيين خود] جدا نمى‌شدند- باز نمى‌ايستادند- تا آن حجت روشن- پيامبر'),
(6132, 98, 2, 'فرستاده‌اى از خداى كه نامه‌هايى پاك- پاره‌هايى از قرآن- را بر آنان مى‌خواند،'),
(6133, 98, 3, 'كه در آنها نوشته‌هايى است راست و درست.'),
(6134, 98, 4, 'و كسانى كه به آنها كتاب داده شده- جهودان و ترسايان- پراكنده نشدند- اختلاف نكردند- مگر پس از آنكه ايشان را حجت روشن- پيامبر و قرآن- آمد.'),
(6135, 98, 5, 'و آنان را جز اين نفرموده بودند كه خداى را بپرستند در حالى كه دين را ويژه او كنند و حق‌گراى باشند و نماز را برپا دارند و زكات بدهند، و اين است آيين [آن نوشته‌هاى‌] راست و استوار- يا: اين است دين راست و استوار-.'),
(6136, 98, 6, 'همانا كافران اهل كتاب و مشركان در آتش دوزخ جاويدانند، اينانند بدترين آفريدگان.'),
(6137, 98, 7, 'همانا كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند، ايشانند بهترين آفريدگان.'),
(6138, 98, 8, 'پاداش ايشان نزد پروردگارشان بهشتهايى پاينده است كه از زير آنها جويها روان است، در آنجا هميشه جاودانه باشند خداى از آنان خشنود است و آنان از خدا خشنودند اين [پاداش‌] براى كسى است كه از پروردگار خود بترسد.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'آنگاه كه زمين جنبانده شود جنباندنش را- يعنى جنبشى سخت كه براى برپايى رستاخيز نوشته شده-.'),
(6140, 99, 2, 'و زمين بارهاى گرانش- مردگان- را بيرون آرد.'),
(6141, 99, 3, 'و آدمى گويد كه آن را چه شده- كه اينگونه سخت مى‌جنبد-؟'),
(6142, 99, 4, 'در آن روز زمين خبرهايش را باز گويد،'),
(6143, 99, 5, 'بدان سبب كه پروردگارت به او وحى كرده است.'),
(6144, 99, 6, 'آن روز مردمان از گورها گروه گروه باز گردند تا كردارهاشان را بديشان بنمايند.'),
(6145, 99, 7, 'پس هر كه همسنگ ذره‌اى- يا مورچه‌اى خرد- نيكى كند آن را ببيند.'),
(6146, 99, 8, 'و هر كه همسنگ ذره‌اى بدى كند آن را ببيند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'سوگند به اسبان دونده كه گاه دويدن نفس نفس زنند،'),
(6148, 100, 2, 'پس سوگند به اسبانى كه با سم خود از سنگ آتش مى‌جهانند.'),
(6149, 100, 3, 'پس سوگند به يورش‌برندگان بامدادى،'),
(6150, 100, 4, 'كه بدان گردى برانگيزند،'),
(6151, 100, 5, 'و بدان- در همان بامداد، يا به سبب دوانيدن اسب و پراكندن غبار- در ميان شوند- در ميان دشمن در آيند-،'),
(6152, 100, 6, 'كه هر آينه آدمى پروردگار خويش را ناسپاس است'),
(6153, 100, 7, 'و همانا خود بر آن [ناسپاسى‌] گواه است.'),
(6154, 100, 8, 'و هر آينه وى به سبب دوستى خير- مال بسيار- سخت بخيل است- يا: هر آينه وى سخت مال‌دوست است-.'),
(6155, 100, 9, 'پس آيا [سرانجام را] نمى‌داند آنگاه كه آنچه را در گورهاست زير و رو كنند و برآرند'),
(6156, 100, 10, 'و آنچه در سينه‌هاست- از ايمان و كفر و خير و شرى كه ورزيده‌اند- فراديد آرند'),
(6157, 100, 11, 'هر آينه پروردگارشان در آن روز بديشان- به اعمال و پاداش آنان- آگاه است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'آن كوبنده- يا آن روز كوبنده-،'),
(6159, 101, 2, 'چيست آن كوبنده؟'),
(6160, 101, 3, 'و چه دانى تو كه چيست آن كوبنده؟'),
(6161, 101, 4, 'روزى كه مردمان همچون پروانگان پراكنده باشند'),
(6162, 101, 5, 'و كوه‌ها مانند پشم رنگين زده‌شده.'),
(6163, 101, 6, 'پس هر كه ترازوى كردارش سنگين باشد- كارهاى با ارزش داشته باشد-،'),
(6164, 101, 7, 'پس او در زندگانى پسنديده و خوشايندى است.'),
(6165, 101, 8, 'و اما هر كه ترازوى كردارش سبك باشد- كار شايسته نداشته باشد-،'),
(6166, 101, 9, 'پس جايگاه او دوزخ است- كه در آن سرنگون شود-.'),
(6167, 101, 10, 'و تو چه دانى كه آن چيست؟'),
(6168, 101, 11, 'آتشى است سخت سوزنده.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'شما را افتخار بر يكديگر به افزونى- در مال و افراد- سرگرم كرد،'),
(6170, 102, 2, 'تا گورستانها را ديدار كرديد- تا آنگاه كه بمرديد، يا تا آنجا كه براى فخرفروشى و برترى‌جويى به گورستانها رفتيد و مردگان را نيز به حساب آورديد.'),
(6171, 102, 3, 'چنين نيست [كه مى‌پنداريد]، بزودى خواهيد دانست.'),
(6172, 102, 4, 'باز چنين نيست [كه مى‌پنداريد]، بزودى خواهيد دانست.'),
(6173, 102, 5, 'هرگز،! اگر مى‌دانستيد، دانستن بى‌گمان، [از فزون‌خواهى و برترى‌جويى باز مى‌ايستاديد].'),
(6174, 102, 6, 'سوگند كه دوزخ را خواهيد ديد.'),
(6175, 102, 7, 'باز هر آينه آن را به ديده يقين خواهيد ديد.'),
(6176, 102, 8, 'سپس هر آينه در آن روز از نعمتها پرسيده خواهيد شد- كه آيا سپاس گزارديد يا ناسپاسى كرديد-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'سوگند به عصر- روزگار يا هر عصرى يا وقت نماز عصر يا عصر نبوت يا عصر مهدى'),
(6178, 103, 2, 'كه هر آينه آدمى در زيانكارى است'),
(6179, 103, 3, 'مگر كسانى كه ايمان آوردند و كارهاى نيك و شايسته كردند و يكديگر را به راستى و درستى اندرز دادند و يكديگر را به شكيبايى- و پايدارى- سفارش كردند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'واى بر هر بدگوى طعنه‌زننده [در پشت‌سر و پيش‌روى‌]'),
(6181, 104, 2, 'آن كه مالى را فراهم ساخته و آن را پى در پى مى‌شمرد- به سبب دوست‌داشتنش و لذت‌بردن از شمردنش-.'),
(6182, 104, 3, 'مى‌پندارد كه مالش وى را جاويد و پاينده مى‌دارد.'),
(6183, 104, 4, 'چنين نيست [كه مى‌پندارد]، هر آينه در آن شكننده خردكننده- دوزخ- افكنده شود.'),
(6184, 104, 5, 'و تو چه دانى كه آن شكننده خردكننده چيست؟'),
(6185, 104, 6, 'آتش افروخته خداست.'),
(6186, 104, 7, 'آتشى كه بر دلها برآيد و چيره شود- دلها را فرا گيرد-.'),
(6187, 104, 8, 'همانا آن آتش بر آنها فروبسته شده است- درهاى آن را بسته‌اند و گريزى از آن نيست-'),
(6188, 104, 9, 'در ستونهايى دراز و كشيده.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'آيا نديدى- ندانستى- كه پروردگارت با پيلداران- ابرهه و لشكرش- چه كرد؟'),
(6190, 105, 2, 'آيا نيرنگ و ترفندشان را- براى ويران‌كردن كعبه- تباه و بيهوده نساخت؟'),
(6191, 105, 3, 'و بر آنان پرندگانى گروه گروه فرستاد،'),
(6192, 105, 4, 'كه آنان را سنگ‌گِل- كلوخى كه در آن سنگ‌ريزه است- مى‌انداختند.'),
(6193, 105, 5, 'پس آنها را مانند كاهبرگهاى ريز و خورده‌شده گردانيد.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'براى [سپاس‌] الفت و پيوستن قريش [به يكديگر]'),
(6195, 106, 2, 'الفت و پيوندشان در سفر زمستان و تابستان'),
(6196, 106, 3, 'پس بايد خداوند اين خانه را پرستش كنند'),
(6197, 106, 4, 'آن كه ايشان را از گرسنگى [رهانيد و] طعام داد، و از بيم ايمنشان ساخت.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'آيا ديدى آن كس را كه دين- دين خداى يا روز حساب و جزا- را دروغ مى‌انگارد؟'),
(6199, 107, 2, 'پس آن، كسى است كه يتيم را به سختى- با ستم و بى‌مهرى- مى‌راند'),
(6200, 107, 3, 'و بر طعام‌دادن بينوا [خود و ديگران را] برنمى‌انگيزد.'),
(6201, 107, 4, 'پس واى بر نمازگزاران،'),
(6202, 107, 5, 'آنان كه از نمازشان غافلند'),
(6203, 107, 6, 'همانان كه به ديگران مى‌نمايانند- در عبادت ريا مى‌كنند-،'),
(6204, 107, 7, 'و نيازمنديها- يا زكات- را [از نيازمندان‌] دريغ مى‌دارند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'همانا تو را كوثر- خير بسيار- داديم.'),
(6206, 108, 2, 'پس براى پروردگارت نماز بگزار و شتر قربانى كن.'),
(6207, 108, 3, 'همانا دشمن تو، همو دنبال بريده- بى‌نسل و بى‌دنباله- است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'بگو: اى كافران،'),
(6209, 109, 2, 'نمى‌پرستم آنچه را كه شما مى‌پرستيد.'),
(6210, 109, 3, 'و نه شما پرستنده‌ايد آنچه را كه من مى‌پرستم'),
(6211, 109, 4, 'و نه من پرستنده‌ام آنچه را كه شما پرستيده‌ايد'),
(6212, 109, 5, 'و نه شما پرستنده‌ايد آنچه را كه من مى‌پرستم.'),
(6213, 109, 6, 'شما راست دين شما و مراست دين من- يعنى نه من در عبادت بتها و خدايان‌ساختگى با شما هماهنگ خواهم شد و نه شما توفيق پرستش خداى يكتاى سزاوار پرستش را خواهيد داشت-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'آنگاه كه يارى خدا و آن فتح- فتح مكه- فرا رسد'),
(6215, 110, 2, 'و مردمان را ببينى كه گروه گروه در دين خدا در آيند [بدان كه رفتن تو به جهان پاينده نزديك است‌]'),
(6216, 110, 3, 'پس پروردگارت را با سپاس و ستايش و به پاكى ياد كن- حمد و تسبيح گوى- و از او آمرزش خواه، كه او توبه‌پذير است.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'زيانكار- يا بريده- باد دو دست ابو لهب- يعنى كردارش- و نابود باد خود او.'),
(6218, 111, 2, 'مالش و آنچه به دست آورده او را سود ندهد- عذاب را از او دفع نكند-.'),
(6219, 111, 3, 'زودا كه بر آتشى زبانه‌دار در آيد و بسوزد'),
(6220, 111, 4, 'و زنش نيز كه هيمه‌كش و آتش‌افروز باشد'),
(6221, 111, 5, 'در گردنش ريسمانى است تابيده از ليف [خرما]- يا زنجير آتشين دوزخ-.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'بگو: حق اين است كه خدا يكتا و يگانه است،'),
(6223, 112, 2, 'خدا تنها بى‌نيازى است كه نيازها بدو برند.'),
(6224, 112, 3, 'نزاده و زاده نشده است.'),
(6225, 112, 4, 'و هيچ كس مر او را همتا و همانند نبوده و نباشد.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'بگو: پناه مى‌برم به خداوند سپيده‌دم،'),
(6227, 113, 2, 'از شر آنچه آفريده است'),
(6228, 113, 3, 'و از شر شب تاريك آنگاه كه در آيد'),
(6229, 113, 4, 'و از شر زنان دمنده در گره‌ها- زنان جادوگر-'),
(6230, 113, 5, 'و از شر هر بدخواه- حسود- آنگاه كه بدخواهى كند.');
INSERT INTO `fa_mojtabavi` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'بگو: پناه مى‌برم به پروردگار مردمان،'),
(6232, 114, 2, 'پادشاه مردمان،'),
(6233, 114, 3, 'خداى مردمان،'),
(6234, 114, 4, 'از شر وسوسه‌گر نهانى- شيطان، كه به گناه وسوسه مى‌كند و پنهان مى‌گردد-'),
(6235, 114, 5, 'آن كه در سينه‌هاى- دلهاى- مردمان وسوسه مى‌كند،'),
(6236, 114, 6, 'از پريان و آدميان- شيطانهاى انس و جن-.');
