INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'به نام خدای رحمتگر بر آفریدگان، رحمتگر بر ویژگان‌'),
(2, 1, 2, 'هر ستایش ویژه‌ی خدا -پروردگار جهانیان- است.'),
(3, 1, 3, '(خدای) رحمتگر بر آفریدگان، رحمتگر بر ویژگان.'),
(4, 1, 4, 'مالک روز بُروزِ طاعت.'),
(5, 1, 5, 'تنها تو را می‌پرستیم، و تنها تو را (به) کمک می‌طلبیم'),
(6, 1, 6, 'ما را (به) راه راست هدایت فرما'),
(7, 1, 7, 'راه کسانی که بر (سر و سامان)شان نعمت فروریختی، نه (راه) غضب‌‌شدگان، و نه (راه) گمراهان.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الم.'),
(9, 2, 2, 'این همان کتاب است (که) در (حقانیت) آن هیچ شکّ مستندی نیست، (و) رهنمودی است برای تقواپیشگان.'),
(10, 2, 3, 'کسانی که به غیب (وحیانی) ایمان می‌آورند و نماز را بر پا می‌دارند و از آنچه روزیشان داده‌ایم انفاق می‌کنند.'),
(11, 2, 4, 'و کسانی که به آنچه سوی تو فرود آمده، و آنچه پیش از تو فرود آمده، ایمان می‌آورند. و (هم) آنان‌(اند که) به آخرت یقین می‌آورند.'),
(12, 2, 5, 'ایشان بر هدایتی از (سوی) پروردگارشان (استوار)ند، و ایشان، (هم)اینان رستگارکنندگان (خود و دیگران)اند'),
(13, 2, 6, 'بی‌گمان کسانی که کافر شدند [:حق را پوشاندند] -چه هشدارشان دهی یا هشدارشان ندهی- برایشان یکسان است. (اینان) ایمان نمی‌آورند.'),
(14, 2, 7, 'خدا بر دل‌هاشان و بر گوش‌هایشان مُهر (پایانی) زده و بر چشم‌هایشان پرده‌ای (پوشانده) و برایشان عذابی بزرگ است.'),
(15, 2, 8, 'و از مردمان، کسانی (هستند که به زبان) می‌گویند: «ما به خدا و به روز واپسین ایمان آورده‌ایم.» حال آن که اینان (از) ایمان‌آورندگان نیستند.'),
(16, 2, 9, '(آنها می‌پندارند) به خدا و کسانی که ایمان آوردند نیرنگ می‌زنند، ولی جز بر خویشتن نیرنگ نمی‌زنند، و باریک‌بینی نمی‌کنند.'),
(17, 2, 10, 'در دل‌هاشان مرضی است، پس خدا بر مرضشان افزود و به (سزای) آنچه دروغ می‌گفتند، برایشان عذابی دردناک است.'),
(18, 2, 11, 'و هنگامی که برایشان گفته شود: «در زمین افساد مکنید»، گویند: «تنها ما اصلاح‌کنندگانیم.»'),
(19, 2, 12, 'هان که آنان خودشان افسادکنندگانند و لیکن باریک‌بینی نمی‌کنند.'),
(20, 2, 13, 'و هنگامی که به آنان گفته شود: «همان‌گونه که مردمان ایمان آوردند، (شما هم) ایمان بیاورید»، گویند: «آیا همان گونه که سبک مغزان ایمان آوردند (ما هم) ایمان بیاوریم‌؟» هان (که) آنان بی‌گمان همان سفیهانند، ولی نمی‌دانند.'),
(21, 2, 14, 'و هنگامی که با کسانی که ایمان آورده‌اند، برخورد کنند، گویند: «(ما هم) ایمان آوردیم.‌» و چون با شیاطینشان خلوت کنند، گویند: «در واقع ما با شماییم، ما فقط مسخره‌کنندگان (ایشان) هستیم.»'),
(22, 2, 15, 'خدا آنان را مسخره می‌کند، و ایشان را در (ژرفای) طغیانشان (هم‌چنان) سرگردان (از این سو به آن سو) می‌کِشاند.'),
(23, 2, 16, 'اینانند کسانی که گمراهی را به (عوضِ) هدایت خریدند. پس داد و ستدشان سودی در بر نداشت. و (آنان از) هدایت‌یافتگان نبوده‌اند.'),
(24, 2, 17, 'مَثَل آنان، همانند مَثَل کسی است که آتشی افروخته، پس هنگامی که پیرامونش را روشنایی داد، خدا نورشان را برد و در میان تاریکی‌هایی (که در آنها هیچ) نمی‌بینند رها‌شان کرد.'),
(25, 2, 18, '(اینان) کران، لالان، (و) کورانند، پس اینان (به سوی حق) برنمی‌گردند.'),
(26, 2, 19, 'یا چون (کسانی که در معرض) رگباری از آسمان، که در آن تاریکی‌ها و رعد و برقی است، (قرار گرفته‌اند) از (نهیب) آذرخش‌ها (و) بیم مرگ‌، سرانگشتانشان را در گوش‌هایشان می‌نهند، حال آنکه خدا بر کافران احاطه دارد.'),
(27, 2, 20, 'نزدیک است برق، چشمانشان را برباید، هرگاه (آن برق) برایشان روشنی بخشد؛ در آن گام زنند، و هنگامی که بر (سر و سامان)شان تاریکی نهد (بر جای خود) بایستند. و اگر خدا می‌خواست شنوایی‌ها و بینایی‌هایشان را حتماً می‌گرفت. به‌راستی خدا بر هر چیزی (بسی) تواناست.'),
(28, 2, 21, 'هان ای مردمان! پروردگارتان را، که شما و کسانی پیش از شما را آفرید، بپرستید؛ شاید شما تقوا پیشه کنید.'),
(29, 2, 22, 'کسی که زمین را برای شما بستری (راهوار و گسترده) گردانید، و آسمان را بنایی (افراشته) قرار داد، و از آسمان آبی فرود آورد، پس با آن رزقی از میوه‌ها برایتان برون آوَرَد. پس برای خدا همتایانی قرار ندهید، حال آنکه شما می‌دانید (که خدا همتاندارد).'),
(30, 2, 23, 'و اگر از آنچه بر بنده‌ی (ویژه)‌مان به تدریج نازل کردیم در (ژرفای) شکی مستند بوده‌اید پس - اگر (از) راستان (در گفتار و کردار) بوده‌اید - سوره‌ای از (شخصی) همانندش [:این بندة ما] و از (کتابی) همانندش [:این کتاب] بیاورید؛ و گواهان خود را (هم) – از غیر خدا – فراخوانید.'),
(31, 2, 24, 'پس اگر (چنان) نکردید- و هرگز (هم) نتوانید (چنان) کرد- از آن آتشی که گیرانه و سوختش مردم و سنگ است (و) برای کافران آماده شده، بپرهیزید.'),
(32, 2, 25, 'و کسانی را که ایمان آوردند و کارهای شایسته‌(ی ایمان) کردند، مژده ده که ایشان را بی‌گمان باغ‌هایی است که از زیر (درختانِ سر در هم)شان نهرهایی روان است. هرگاه ثمره‌ای از آن روزیشان شود، گویند: «این همان است که پیش از این (نیز) به ما روزی داده شد.‌» و آنجا رزقی همانند به آنان (از هم اکنون) داده شد، و در آنجا برایشان [:مردان و زنان] همسرانی پاکیزه است، و اینان در آن جاودانه‌اند.'),
(33, 2, 26, 'همانا خدا از اینکه به پشه‌ای - چه فروتر، چه فراتر- از آن مَثَلی زند، شرم نمی‌کند؛ پس اما کسانی که ایمان آوردند می‌دانند که آن (مَثَل) به‌راستی از (جانب) پروردگارشان کاملاً بجاست‌؛ ولی کسانی که به کفر گراییدند گویند: «خدا با (بیان) این مَثَل چه چیزی را خواسته است‌؟» بسیاری را با آن گمراه می‌کند و بسیاری را (هم) با آن به راه می‌آورد. و (اما) جز خلافکاران را با آن گمراه نمی‌کند.'),
(34, 2, 27, 'کسانی که پیمان خدا را پس از بستنش می‌شکنند و آنچه را خدا به پیوندش امر فرموده، می‌گسلند، و در زمین افساد می‌کنند؛ اینان همان زیانکارانند.'),
(35, 2, 28, 'چگونه به خدا کفر می‌ورزید؟ حال آنکه مردگانی بودید پس شما را زنده کرد؛ سپس شما را می‌میراند، پس از آن شما را زنده می‌کند؛ پس آن‌گاه به سوی او بازگردانده می‌شوید.'),
(36, 2, 29, 'او کسی است که آنچه در زمین است، همه را برای (همه‌ی) شما آفرید؛ سپس به (آفرینش) آسمان پرداخت، پس آسمان‌ها را (به صورت) هفت آسمان سازماندهی کرد. و او به هر چیزی بسیار داناست.'),
(37, 2, 30, 'و چون پروردگارت به فرشتگان گفت: «همانا من قرار‌دهندة جانشینی (از گذشتگان) در زمین می‌باشم»، گفتند: «آیا در آن کسی را (جانشین) قرار می‌دهی که در آن افساد کند و خون‌ها بریزد؟ حال آنکه ما به وسیلة ستایشت (تو را از کلّ نقایص) تنزیه می‌کنیم، و برایت تقدیس می‌نماییم.» (خدا) فرمود: «به‌راستی من چیزی می‌دانم که شما نمی‌دانید.»'),
(38, 2, 31, 'و (خدا) همه‌ی اسم‌ها را به آدم آموخت. سپس ایشان را برای فرشتگان به نمایش گذاشت. پس فرمود: «اگر از راستان بوده‌اید، از نام‌های ایشان به من خبری مهم دهید.»'),
(39, 2, 32, 'گفتند: «منزهی تو! ما را دانشی نیست جز آنچه خود به ما آموخته‌ای. به‌راستی تویی، تو، بسیار دانای حکیم.»'),
(40, 2, 33, 'فرمود: «ای آدم! ایشان را از اسامی آنان خبری مهم ده.» پس چون (آدم) ایشان را از اسامی آنان خبری مهم داد، (خدا) فرمود: «آیا برای شما نگفتم که به‌راستی من غیب (ویژه‌ی) آسمان‌ها و زمین را می‌دانم‌؛ و آنچه را آشکار می‌کنید و آنچه را پنهان داشته‌اید، می‌دانم‌؟»'),
(41, 2, 34, 'و چون به فرشتگان فرمودیم: «به خاطر (شکرانة تعلیم گرفتن از) آدم (برای خدا) سجده کنید»، پس (همه) به سجده در افتادند، مگر ابلیس که سر باز زد و کبر ورزید و (هماره) از کافران بود.'),
(42, 2, 35, 'و گفتیم: «ای آدم! تو و همسرت در (این) باغ، آرام و قرارگیر، و از هر کجای آن هر چه (خوردنی) خواهید پاکیزه و فراوان بخورید و به این درخت نزدیک نشوید، که از ستمکاران خواهید بود.»'),
(43, 2, 36, 'پس شیطان هر دو را از آن بلغزانید، تا از آنچه در آن بودند ایشان را به درآورد. و فرمودیم: «(به این زمین) فرود آیید حال آنکه دشمنان همدیگرید و برای شما در زمین قرارگاهی است، و تا چندی - از نعمت‌ها- برخوردارید.»'),
(44, 2, 37, 'پس آدم از پروردگارش کلماتی را (با کوشش و کاوش) دریافت تا خدا به او برگشت‌؛ بی‌گمان اوست بسیار برگشت‌‌کننده (بر بندگان) و (اوست) پذیرنده‌ی برگشت(شان) و رحمتگر بر ویژگان.'),
(45, 2, 38, 'فرمودیم: «جملگی از آن (باغ آسمانی به این زمین) فرود آیید. پس اگر به‌راستی از (جانب) من شما را رهنمایی در رسد، آنان که هدایتم را پیروی کنند نه بیمی بر آنهاست و نه ایشان غمگین می‌شوند.»'),
(46, 2, 39, 'و کسانی که کفر ورزیدند و با نشانه‌های ما (همان‌ها و ما را) تکذیب کردند، اینانند همراهان آتش، (و هم)ایشان در آن ماندگارانند.'),
(47, 2, 40, 'ای فرزندان اسرائیل! نعمت (ویژه)‌ام را که بر شما ارزانی داشتم به یاد آرید و به پیمانم وفا کنید، تا به پیمانتان وفا کنم، و تنها از من، با پرهیز و اضطراب بهراسید.'),
(48, 2, 41, 'و بدانچه نازل کرده‌ام - حال آنکه مؤید همان چیزی است که با شماست - ایمان آرید؛ و نخستین کافر (نسبت) به آن مباشید، و آیات مرا با بهایی اندک «نخرید»: معاوضه نکنید، و تنها از من پروا کنید.'),
(49, 2, 42, 'و حق را با باطل مَپوشانید، و حق را کتمان نکنید در حالی که خود‌تان (هم) می‌دانید.'),
(50, 2, 43, 'و نماز را بر پا دارید و زکات را بدهید و با رکوع‌کنندگان رکوع کنید.'),
(51, 2, 44, 'آیا مردمان را به نیکی فرمان می‌دهید و خودتان را فراموش می‌کنید، حال آنکه شما کتاب (خدا) را می‌خوانید؟ پس آیا خردورزی نمی‌کنید؟'),
(52, 2, 45, 'و با شکیبایی و نماز (در راه خدا) یاری جویید و به‌راستی این (کار) بی‌گمان (باری) بزرگ است، مگر بر دلباختگان (به خدا).'),
(53, 2, 46, '(اینانند) کسانی که (در دل‌هاشان) گمان دارند به‌راستی ملاقات کنندگان پروردگارشانند؛ و (اینکه) به‌درستی تنها سوی او بازگردنده‌اند.'),
(54, 2, 47, 'ای فرزندان اسرائیل! نعمت (ویژه)‌ام را که بر شما ارزانی داشتم، و اینکه به‌راستی من شما را بر جهانیان (زمانتان) برتری دادم، به یاد آرید.'),
(55, 2, 48, 'و پروا کنید از روزی که هیچ کس چیزی (از عذاب خدا) را از کسی کفایت نمی‌کند؛ و نه از او شفاعتی پذیرفته گردد و نه به جای وی بدلی گرفته شود؛ و نه ایشان یاری خواهند شد.'),
(56, 2, 49, 'و چون شما را از (چنگ) فرعونیان رهانیدیم‌؛ حال آنکه شما را شکنجه‌‌ای بد می‌کردند: پسرانتان را سر می‌بریدند و زنانتان را زنده می‌گذاشتند و از آنان سلب حیا می‌کردند. و این برای شما بلا و آزمایشی بزرگ از جانب پروردگارتان (بوده) است.'),
(57, 2, 50, 'و چون دریا را برایتان شکافتیم، پس شما را نجات دادیم، و فرعونیان را - در حالی که شما نظاره می‌کردید - غرق کردیم.'),
(58, 2, 51, 'و چون با موسی چهل شب وعده گذاشتیم، سپس شما بعد از (رفتن) وی، گوساله را (به پرستش) گرفتید. حال آنکه شما ستمکارانید.'),
(59, 2, 52, 'سپس بعد از آن از (گناه) شما چشم‌پوشی کردیم، شاید شما شکرگزاری کنید.'),
(60, 2, 53, 'و چون موسی را کتاب و فرقان [:جدا کننده‌ی حق از باطل] دادیم، شاید شما راه یابید.'),
(61, 2, 54, 'و چون موسی برای قومش گفت: «ای قوم من! همانا شما با (به پرستش) گرفتن گوساله، بر خودتان ستم کردید، پس به درگاه آفریدگارِ سازمان‌‌دهنده‌ی خود بازگردید؛ پس خودهاتان (یکدیگر) را بکشید. این کار بزرگ نزد (خدای) سازمان‌دهنده‌تان برای شما نیک است.» پس (خدا نیز) بر شما بازگشت، (که) او، بی‌گمان همو بسیار برگشت‌کننده‌ی رحمتگر بر ویژگان است.'),
(62, 2, 55, 'و چون گفتید: «ای موسی! «هرگز برایت ایمان نمی‌آوریم تا خدا را آشکارا بنگریم.» پس در حالی که می‌نگریستید، آذرخش آتشین شما را در بر گرفت.'),
(63, 2, 56, 'سپس شما را بعد از مرگتان برانگیختیم‌؛ شاید شکرگزاری کنید.'),
(64, 2, 57, 'و بر (سر و سامان)تان ابر را سایه‌گستر کردیم، و بر شما «آرامش» و «ترنجبین» را فرو فرستادیم (و گفتیم:) «از خوراکی‌های پاکیزه‌ای که به شما روزی دادیم، بخورید.» و (آنان) بر ما ستم نکردند، بلکه بر خودهاشان ستم می‌کرده‌اند.'),
(65, 2, 58, 'و چون گفتیم: «بدین شهر در آیید، پس از (نعمت‌های) آن - هر گونه و هر کجا خواستید - پاکیزه و فراوان بخورید و سجده‌کنان از درش داخل شوید و بگویید “(ما خواهان) ریزش [:بخشایش] گناهانمان‌(ایم)”، تا خطاهایتان را ببخشاییم. و (پاداش) نیکوکاران را به زودی می‌افزاییم.»'),
(66, 2, 59, 'پس کسانی که ستم کردند، (و آن) سخن را به (سخنی دیگر) غیر از آنچه به ایشان گفته شده بود تبدیل کردند؛ پس ما (نیز) بر آنان که ستم کردند، - به پیامد آنچه سرپیچی می‌کرده‌اند - عذابی پراضطراب از آسمان فرو فرستادیم'),
(67, 2, 60, 'و چون موسی برای قومش در پی آبی برآمد، پس (بدو) گفتیم: «با عصایت (برآن) تخته سنگ بزن.» پس دوازده چشمه از آن جوشیدن گرفت‌؛ (به گونه‌ای که) هر قبیله‌ای آبشخورشان را همی‌دانستند (و گفتیم): «از روزی خدا بخورید و بیاشامید و در زمین به افسادگری نکوشید.»'),
(68, 2, 61, 'و چون گفتید: «ای موسی! ما هرگز بر یک (نوع) خوراکی شکیب (و تاب) نیاوریم، پس پروردگارت را برایمان بخوان تا از آنچه زمین می‌رویاند -از (قبیل) سبزی و خیار و سیر و عدس و پیاز- برایمان برون آوَرَد!» (موسی) گفت: «آیا به جای چیزی بهتر [:من و سلوی]، چیزی فروتر (از آن) می‌طلبید؟ پس به شهری فرود آیید که آنچه خواسته‌اید برای شما همانا در آنجاست.» و (داغ) خواری و ناداری بر آنان زده شد، و به (سوی) خشمی از خدا برگشتند. این بدین سبب بود که آنان همانا به نشانه‌های خدا کفر می‌ورزیده‌اند، و پیامبران را به ناحق می‌کشته‌اند؛ این بدان روی بود که سرکشی نموده و تجاوز می‌کرده‌اند.'),
(69, 2, 62, 'بی‌گمان، کسانی که ایمان آوردند و کسانی که یهودی شدند، و نصرانیان و صابئان [:التقاطیان] - هر کس (از آنان و سایر موحدان) به خدا و روز بازپسین ایمان آوَرَد و کار شایسته (ی ایمان) کند، - پس برایشان اجرشان نزد پروردگارشان است و نه بیمی بر آنهاست و نه آنان اندوهناک خواهند شد.'),
(70, 2, 63, 'و چون پیمانتان را (از شما) گرفتیم، و (کوه) طور را بر فرازتان افراشتیم (فرمودیم:) «آنچه را به شما داده‌ایم به نیرویی (شایسته همچون کوه بر فرازتان) برگیرید و آنچه را در آن است به یاد آرید، شاید شما (از گناهان) پرهیز کنید.»'),
(71, 2, 64, 'سپس (شما) بعد از آن (پیمان) رویگرداندید، پس اگر فضل خدا و رحمتش بر شما نبود، همانا از زیانکاران می‌بودید.'),
(72, 2, 65, 'و به‌راستی کسانی از شما را - که در روز شنبه (از فرمان خدا) تجاوز کردند - به خوبی شناختید. پس بدیشان گفتیم: «بوزینگانی رانده‌شده باشید.»'),
(73, 2, 66, 'پس ما آن‌ها [:بوزینگان] را برای حاضران و (نسل‌های) پس از آنان، بازدارنده (از نافرمانی) و برای پرهیزگاران، پندی قرار دادیم.'),
(74, 2, 67, 'و چون موسی به قومش گفت: «خدا به‌راستی شما را فرمان می‌دهد که ماده گاوی را سر ببرید»، گفتند: «آیا ما را همواره به مسخره می‌گیری‌؟» گفت: «پناه می‌برم به خدا که از جاهلان باشم.»'),
(75, 2, 68, 'گفتند: «پروردگارت را برای ما بخوان، (تا) برایمان روشن سازد که آن (گاو) چگونه است‌؟» گفت: «به‌راستی او [:خدا] می‌فرماید “بی‌گمان آن ماده گاوی است نه پیر و نه خردسال‌؛ میانگینی است بین این دو؛ پس آنچه را (بدان) امر می‌شوید به جای آرید.”»'),
(76, 2, 69, 'گفتند: «پروردگارت را برایمان بخوان، (تا) برای ما روشن کند رنگش چگونه است‌؟» گفت: «به‌راستی وی می‌فرماید “بی‌گمان آن ماده گاوی است (که) رنگش زرد یکدست و خالص است و (این گاو) بینندگان را شاد می‌کند.”»'),
(77, 2, 70, 'گفتند: «پروردگارت را برایمان بخوان، (تا) برای ما روشن گرداند که آن چگونه (گاوی) است‌؟ (زیرا ویژگی‌های) این ماده گاو، به‌راستی بر ما (با گاوهایی دیگر) مشتبه شده، و (با توضیحاتی بیشتر) بی‌گمان اگر خدا بخواهد ما به‌راستی هدایت‌‌یافتگانیم.»'),
(78, 2, 71, 'گفت: «به‌راستی خدا می‌فرماید “بی‌چون، آن ماده گاوی است که نه رام است تا زمین را شخم زند، و نه کشتزار را آبیاری می‌کند؛ بسی سالم است (و) هیچ لکه‌ای (هم) در آن نیست.”» گفتند: «اینک (سخنی) کاملاً درست آوردی.» پس آن را سر بریدند، و (هرگز) نزدیک [:مایل] به انجام این کار (هم) نبوده‌اند.'),
(79, 2, 72, 'و چون شخصی را کشتید، سپس درباره‌ی (قاتلِ) او با یکدیگر به ستیزه برخاستید، و حال آنکه خدا آنچه را کتمان می‌کرده‌اید، برون‌آورنده است.'),
(80, 2, 73, 'پس فرمودیم: «(جسد) آن مقتول را به پاره‌ای از آن (گاو سر بریده) بزنید (تا زنده شود).» بدین‌سان خدا مردگان را زنده می‌کند و آیات خود را به شما می‌نمایاند، شاید شما بیندیشید.'),
(81, 2, 74, 'سپس دل‌هایتان پس از این (واقعه) سخت گردید، پس آنها همانند سنگ، یا سخت‌تر از آنند؛ حال آنکه بی‌چون از برخی سنگ‌ها همانا از (فوران آب نهفته در) آن، نهر‌های بسیاری به شدت روان می‌گردد؛ و به‌راستی برخی از آنها می‌شکافند؛ پس از آن آب خارج می‌شود؛ و بی‌چون برخی از آنها از بیم خدا فرو می‌ریزند. و خدا از آنچه می‌کنید غافل نیست.'),
(82, 2, 75, 'پس آیا طمع دارید که (اینان) برایتان [:به سودتان] ایمان بیاورند؟ حال آنکه گروهی از آنان بی‌گمان سخنان خدا را می‌شنیده‌اند، سپس آن را بعد از دریافتنش تحریف می‌کنند، حال آنکه خودشان (هم) می‌دانند.'),
(83, 2, 76, 'و (همین یهودیان) هنگامی که با کسانی که ایمان آورده‌اند برخورد کنند، گویند: «(ما) ایمان آوردیم‌.» و هنگامی که با همدیگر خلوت کنند، گویند: «آیا از آنچه خدا بر شما گشوده است، برای آنان بازگو می‌کنید تا آنان با (استناد به) آن، نزد پروردگارتان بر (ضد) شما استدلال کنند؟ پس آیا خردورزی نمی‌کنید؟»'),
(84, 2, 77, 'آیا و نمی‌دانند که خدا آنچه را پوشیده می‌دارند و آنچه را آشکار می‌کنند، همی می‌داند؟'),
(85, 2, 78, 'و (بعضی) از آنان بی‌سوادانی هستند که (معنای درستِ) کتاب (تورات) را بجز آرزوهای بی‌پایه نمی‌دانند، و فقط (به آن) گمان می‌برند.'),
(86, 2, 79, 'پس وای بر کسانی که کتاب را با دست‌هایشان می‌نویسند، سپس می‌گویند: «این از جانب خداست‌» تا بدان (وسیله) بهای ناچیزی به دست آورند؛ پس وای بر ایشان از آنچه دست‌هایشان نوشته، و وای بر ایشان از آنچه (از این راه) به دست می‌آورند.'),
(87, 2, 80, 'و گفتند: «جز روزهایی چند، هرگز آتش به ما نخواهد رسید.» بگو: «آیا نزد خدا پیمانی (محکم) گرفتید؟ پس خدا پیمان خود را هرگز خلاف نخواهد کرد. یا آنچه را نمی‌دانید به خدا نسبت می‌دهید؟»'),
(88, 2, 81, 'آری، کسی که بدی‌ای را به دست آوَرْدْ و خطایش او را در برگرفت، اینان همراهان آتشند، (و) ایشان در آن ماندگارند.'),
(89, 2, 82, 'و کسانی که ایمان آوردند، و کارهای شایسته(ی ایمان) کردند، اینان بهشتیانند؛ ایشان در آن جاودانند.'),
(90, 2, 83, 'و چون از فرزندان اسراییل پیمان گرفتیم (که) : «جز خدا را نپرستید، و به پدر و مادر و نزدیکترین خویشان و یتیمان و مستمندان احسان کنید، و برای مردمان با (زبانِ) خوشی سخن بگویید و نماز را به پا دارید و زکات بدهید.» سپس - بجز اندکی از شما - (همگی) به حالت اعراض کنندگان (از پیمان الهی) روی برتافتید'),
(91, 2, 84, 'و چون (از شما) پیمانتان را گرفتیم (که) : «خون یکدیگر را مریزید، و همدیگر را از خانه‌ها‌یتان بیرون نکنید.» سپس (به این پیمان) اقرار کردید، حال آنکه (خودتان) گواهید.'),
(92, 2, 85, 'سپس شما همان‌هایید که همدیگر را می‌کشید، و گروهی از خودتان را از خانه‌هاشان بیرون می‌رانید، در حالی که به گناه و تجاوز، بر ضد آنان یکدیگر را پشتیبانی می‌کنید، و اگر به اسارت نزدتان آیند، به (دادن) فدیه، آنان را آزاد می‌کنید، حال آنکه همان (بیرون راندنشان) بر شما حرام شده است‌؛ پس آیا به پاره‌ای از کتاب [:تورات] ایمان می‌آورید، و به پاره‌ای (هم) کفر می‌ورزید؟ پس چیست کیفرکسی از شما که چنان کند؟ جز خواری (و بی‌مقداری) در زندگی دنیا؛ و روز رستاخیز ایشان به سخت‌ترین عذاب‌ها بازگردانده می‌شوند، و خدا از آنچه می‌کنید غافل نیست.'),
(93, 2, 86, 'اینانند کسانی که زندگی(ناچیز) دنیا را به (بهای) آخرت خریدند، (که به همان بسنده کردند) پس نه عذاب از آنان تخفیف داده می‌شود، و نه ایشان یاری می‌شوند.'),
(94, 2, 87, 'و همانا به موسی کتاب [:تورات] را به‌راستی دادیم، و پس از او پیامبرانی (از اسرائیلیان) را پشت سر هم فرستادیم، و به عیسی پسر مریم نشانه‌های آشکار دادیم، و او را با «روح‌القدس‌» تأیید کردیم‌؛ پس چرا هر گاه پیامبری چیزی را که خوشایندتان نیست برایتان آوَرْدْ، کبر ورزیدید؟ پس گروهی را تکذیب کردید، و گروهی را هم می‌کشید.'),
(95, 2, 88, 'و گفتند: «دل‌های ما را پوششی (فراگرفته) است.» (چنان نیست بلکه آنان به خوبی می‌فهمند؛) بلکه خدا به سزای کفرشان لعنتشان کرده است‌. پس چه اندک ایمان می‌آورند.'),
(96, 2, 89, 'و هنگامی که از طرف خداوند، کتابی برای آنها آمد که موافق نشانه‌هایی بود که با خود داشتند، و پیش از این، به خود نوید پیروزی بر کافران می‌دادند (که با کمک آن، بر دشمنان پیروز گردند.) با این همه، هنگامی که این کتاب، و پیامبری را که از قبل شناخته بودند نزد آنها آمد، به او کافر شدند؛ لعنت خدا بر کافران باد!'),
(97, 2, 90, 'بدا (به) آنچه خودِ (آلوده به شهوات)شان را با آن خریدند. همانا به آنچه خدا نازل کرد از سر ستم و تجاوز، کفر می‌ورزند که (چرا) خدا از فضل خویش بر هر کس از بندگانش بخواهد کتابی وحیانی فرو می‌فرستد؛ پس به خشمی (افزون) بر خشمی دیگر بازگشتند. و برای کافران عذابی خوارکننده است.'),
(98, 2, 91, 'و هنگامی که به آنان گفته شود: «به آنچه خدا نازل کرده ایمان بیاورید،» گویند: «ما به آنچه بر (پیامبران)مان نازل شده ایمان می‌آوریم.» در حالی که به غیر آن [:قرآن] کفر می‌ورزند، حال آنکه همان تمام حق است و تصدیق‌کننده‌ی همان چیزی است که با آنان است. بگو: «اگر مؤمن بوده‌اید، پس چرا از پیش، پیامبران خدا را (و اینک رسالتش را) می‌کُشید؟»'),
(99, 2, 92, 'و همانا موسی برای شما به‌راستی نشانه‌های آشکار آورد، سپس گوساله را پس از (رفتن) وی (با خدا شریک) گرفتید، در حالی که شما ستمکارانید.'),
(100, 2, 93, 'و چون پیمانتان را گرفتیم و (کوه) طور را بر فرازتان برافراشتیم (و گفتیم:) «آنچه را به شما داده‌ایم با نیرویی (فزاینده) در برگیرید و (به دستورهای آن) گوش فرا دهید»، گفتند: «شنیدیم و نافرمانی کردیم.» و بر اثر کفرشان، (مِهر) گوساله در دل‌ها‌شان سرشته شد. بگو: «اگر مؤمن بوده‌اید، بدا (به) آنچه ایمانتان شما را بدان فرمان می‌دهد.»'),
(101, 2, 94, 'بگو: «اگر نزد خدا، سرای آخرت یکسره برای شما اختصاص داشته است (و) نه دیگر مردم، پس اگر (از) راستان بوده‌اید، آرزوی مرگ کنید.»'),
(102, 2, 95, 'ولی به سبب آنچه با دست‌های [:نیروهای] خود از پیش فرستاده‌اند، هرگز آن را هیچ‌گاه آرزو نخواهند کرد. و خدا به (حال) ستمکاران بسی داناست.'),
(103, 2, 96, 'و همواره آنان را بی‌گمان آزمندترین مردم به زندگی‌ای (دنیوی) - و (حتی) حریص‌تر از کسانی که شرک می‌ورزند - خواهی یافت. هر یک از ایشان آرزو دارد که کاش هزار سال عمر کند، حال آنکه اگر چنین عمری (هم به او) داده شود، دور کننده‌ی او از عذاب نیست‌؛ و خدا به آنچه می‌کنند بسی بیناست.'),
(104, 2, 97, '(به یهودیان) بگو: «کسی که دشمن جبرئیل بوده است (دشمن خداست). به‌راستی او [:جبرئیل]، به فرمان خدا، آن [:قرآن] را بر قلبت نازل کرده، در حالی که تصدیق‌کننده‌ی آنچه [:از کتاب‌های آسمانی] پیش روی اوست، و هدایت و بشارتی برای مؤمنان است.»'),
(105, 2, 98, 'هر کس دشمن خدا و فرشتگان و فرستادگانش و جبرئیل و میکائیل بوده است (کافر است)؛ پس خدا بی‌گمان دشمنی برای کافران است.'),
(106, 2, 99, 'و همانا به‌راستی بر تو آیاتی روشن فرو فرستادیم، و جز فاسقان (کسانی) به آنها کافر نمی‌شوند.'),
(107, 2, 100, 'آیا و هر گاه (یهودیان) پیمانی محکم بستند، گروهی از ایشان آن را به دور افکندند؟ (آری چنان است) بلکه بیشترشان ایمان نمی‌آورند.'),
(108, 2, 101, 'و هنگامی که فرستاده‌ای از جانب خدا برایشان آمد – که آنچه را با آنان است تصدیق‌کننده است - گروهی از کسانی که کتاب (وحیانی) داده شده‌اند، کتاب (ویژه‌ی) خدا را پشت سرشان افکندند، چنان که گویی ایشان (اصلاً) نمی‌دانند.'),
(109, 2, 102, 'و آنچه را که شیطان‌ها بر سلطنت سلیمان می‌خوانند، پیروی کردند؛ و سلیمان (بر خلاف گمان آنان هرگز) کافر نشد، لیکن (همان) شیطان‌ها(یند که) به کفر گراییدند؛ حال آنکه به مردم سحر می‌آموزند. و (نیز از) آنچه بر آن دو فرشته -هاروت و ماروت‌- در بابل فروفرستاده شده بود (می‌آموختند)؛ در حالی که آن دو (فرشته) هیچ کس را تعلیم (سحر) نمی‌کردند، مگر آنکه (قبلاً به او) می‌گفتند: «ما تنها (وسیله‌ی) آزمایشی (برای شما) هستیم، پس (زنهار که) کافر نشوی.» پس، از آن دو (فرشته) چیزهایی می‌آموختند (که) به وسیله‌ی آن میان مرد و همسرش جدایی بیفکنند، در حالی که بدون اذن خدا نمی‌توانستند به وسیله‌ی آن به احدی زیان‌رسان باشند. و (خلاصه) چیزی می‌آموزند که آنان را زیان دارد و سودی برایشان ندارد. و بی‌گمان (یهودیان) بی‌چون دریافته بودند که هر کس این متاع را خرید، هرگز در آخرت بهره‌ای ندارد. (وای که) چه بد بود آنچه بدان خودفروشی کردند؛ اگر می‌دانسته‌اند.'),
(110, 2, 103, 'و اگر آنها به‌درستی گرویده و پرهیزگاری کرده بودند، بی‌چون پاداشی (که) از جانب خدا (می‌یافتند) بهتر بود؛ اگر می‌دانسته‌اند.'),
(111, 2, 104, 'هان ای کسانی که ایمان آوردید! نگویید «راعنا» و بگویید «انظرنا» و (سخنان پیامبر را) بشنوید. و کافران را عذابی دردناک است.'),
(112, 2, 105, 'نه کسانی از اهل کتاب که کافر شدند و نه مشرکان (هیچ کدام) دوست نمی‌دارند هیچ خیری از جانب پروردگارتان برایتان فرود آید. حال آنکه خدا هر که را خواهد به رحمت خود اختصاص می‌دهد. و خدا دارای فزون‌بخشیِ بزرگ است.'),
(113, 2, 106, 'هر آیت و نشانه‌ای (عینی یا لفظی) را (که) از میان برداریم، یا آن را از یاد(تان) ببریم، بهتر از آن یا مانند آن را می‌آوریم‌؛ مگر ندانستی که خدا همواره بر هر کاری بسیار تواناست‌؟'),
(114, 2, 107, 'مگر ندانستی که سلطنت آسمان‌ها و زمین به‌راستی تنها از آنِ خداست و شما جز خدا هیچ سرور و یاوری ندارید؟'),
(115, 2, 108, 'یا می‌خواهید از پیامبر خود همان را بخواهید که از پیش، از موسی خواسته شده بود؟ و هرکس کفر را جایگزین ایمان کند، به‌راستی راه راهوارِ راست را گم کرده است.'),
(116, 2, 109, 'بسیاری از اهل کتاب از روی حسدی که از نزد خودشان بود، دوست داشتند که شما را بعد از ایمانتان -پس از اینکه تمام حق برایشان به خوبی آشکار گردید- کافر گردانند. پس چشم بپوشید و چهره بگردانید تا خدا فرمان خویش را صادر کند. همواره خدا بر هر کاری بسیار تواناست.'),
(117, 2, 110, 'و نماز را به پا دارید و زکات بدهید و هر نیکی که برای خویش از پیش می‌فرستید، آن را نزد خدا باز خواهید یافت. بی‌گمان، خدا به آنچه می‌کنید بیناست.'),
(118, 2, 111, 'و گفتند: «هرگز کسی به بهشت در نیاید، مگر کسی که (از) یهودیان یا نصرانیان بوده است.» اینها آرزوهای (واهیِ) ایشان است. بگو: «اگر (از) راستان بوده‌اید برهانتان را بیاورید.»'),
(119, 2, 112, 'آری، هر کس -در حالی که نیکوکار است- چهره‌ی خود را به خدا تسلیم کند، پاداش وی نزد پروردگار اوست و بیمی بر آنان نیست و غمگین (هم) نخواهند شد.'),
(120, 2, 113, 'و یهودیان گفتند: «نصرانیان بر هیچ (حقی) نیستند.» و نصرانیان (هم) گفتند: «یهودیان بر هیچ (حقّی) نیستند.» حال آنکه آنان هر دو کتاب (وحیانی) را می‌خوانند. و کسانی که نمی‌دانند (و از کتاب‌های وحیانی آگاهی ندارند) نیز (سخنی) همانند گفته‌ی ایشان گفتند. پس خدا روز رستاخیز در آنچه با هم اختلاف می‌کرده‌اند، میان آنان داوری خواهد کرد.'),
(121, 2, 114, 'و کیست بیدادگرتر از آن کس که نگذاشته در مساجد خدا، نام خدا یاد شود و در ویرانی آنها کوشیده است‌؟ برای آنان نبوده است جز اینکه اینان ترسان (و لرزان) در آن مسجدها درآیند؛ در دنیا ایشان را خواری و برایشان در آخرت عذابی بزرگ است.'),
(122, 2, 115, 'و مشرق و مغرب تنها از آنِ خداست‌؛ پس به هر سو رو کنید، آنجا روی (سوی) خداست. بی‌گمان، خدا گشایگری بسیار داناست.'),
(123, 2, 116, 'و گفتند: «خدا فرزندی (برای خود) برگرفته.» (او) منزّه است. بلکه هر چه در آسمان‌ها و زمین است، تنها از آنِ اوست (و) همه(ی آفریدگان) برای او (بندگان و) خاضعانند‌.'),
(124, 2, 117, '(او) پدیدآورنده‌ی آغازین آسمان‌ها و زمین بدون هیچ سابقه‌ای است و هنگامی که (تکوین) چیزی را اراده فرماید، پس تنها برای آن می‌گوید: «شو»، پس می‌شود.'),
(125, 2, 118, 'و کسانی (همچون مشرکان) که (از نظر وحیانی) نمی‌دانند، گفتند: «چرا خدا با ما سخن نمی‌گوید؟ یا برای ما نشانه‌ای نمی‌آید؟» این‌گونه، کسانی که پیش از اینان بودند (نیز) مثل همین گفته‌ی ایشان را گفتند، (و) دل‌ها(و افکار)شان همانند یکدیگر بوده است. ما بی‌چون نشانه‌ها(ی خود) را برای گروهی که یقین می‌آورند، نیک روشن گردانیده‌ایم.'),
(126, 2, 119, 'ما همواره تو را به تمامی حق فرستادیم، حال آنکه بشارتگر و هشداردهنده‌‌ای، و درباره‌ی دوزخیان تو مسئول نیستی.'),
(127, 2, 120, 'و هرگز یهودیان و نصرانیان از تو راضی نمی‌شوند، تا آنکه از کیش آنان پیروی کنی. بگو: «در حقیقت، هدایت، تنها، هدایتِ خداست.‌» و اگر بی‌گمان پس از آن علم (وحیانی) که تو را حاصل آمد، از هوس‌های آنان پیروی کنی، از خدا برای تو هیچ سرپرست و هیچ یاوری نیست.'),
(128, 2, 121, 'کسانی که کتاب (وحیانی) به آنان دادیم، حال آنکه آن را با دیده‌ی حق‌(نگر) می‌خوانند (و پیروی می‌کنند)، ایشان بدان [:قرآن] ایمان می‌آورند، و کسانی که بدان کفر ورزند، همانا زیان‌کارانند.'),
(129, 2, 122, 'ای فرزندان اسرائیل! نعمتِ ویژه‌ام را - که بر شما ارزانی داشتم، و اینکه شما را بر جهانیان بی‌گمان برتری دادم - یاد کنید.'),
(130, 2, 123, 'از روزی بترسید که هیچ کس از دیگری دفاع نمی‌کند؛ و هیچ‌گونه عوضی از او قبول نمی‌شود؛ و شفاعت، او را سود نمی‌دهد؛ و (از هیچ سوئی) یاری نمی‌شوند!'),
(131, 2, 124, 'و چون ابراهیم را پروردگارش با کلماتی (نشانگر اخلاصی ویژه) آزمونی آتشین کرد، پس وی همه‌ی آنها را به اتمام رسانید. (خدا به او) فرمود: «من تو را پیشوای مردمان (و کلّ مکلفان) قراردهنده‌ام.» (ابراهیم) گفت: « و از دودمانم (نیز) ؟» فرمود: «این پیمانِ (ویژه‌ی) من به بیدادگران‌(شان) در نرسد.»'),
(132, 2, 125, 'و چون خانه [:کعبه] را برای مردم جایگاه و پناهگاه امنی قرار دادیم (و فرمودیم): «از مقام ابراهیم، جایگاه نمازی برای خود اختیار کنید.» و به ابراهیم و اسماعیل فرمان دادیم که: «خانه‌ی مرا برای طواف‌کنندگان و معتکفان و رکوع کنندگان و سجودکنندگان پاکیزه گردانید.»'),
(133, 2, 126, 'و چون ابراهیم گفت: «پروردگارم! این (سرزمین) را شهری امین و با امان گردان و مردمش را - هر کس از آنان که به خدا و روز بازپسین ایمان بیاورد - از همه‌ی ثمرات و فرآورده‌ها(ی جهان) روزی بخش‌»، (خدا) فرمود: « و هر کس (هم) کفر بورزد، پس اندکی برخوردارش می‌کنم، سپس او را با اضطرار [:به ناچار] سوی عذاب آتش (دوزخ) می‌کشانم، و چه بد سرانجامی است.»'),
(134, 2, 127, 'و چون ابراهیم و اسماعیل پایه‌های خانه [:کعبه] را بالا می‌برند (می‌گفتند:) «پروردگارمان! از ما بپذیر. بی‌گمان تو همان شنوای دانایی.»'),
(135, 2, 128, '«پروردگارمان! ما را در (انحصار) تسلیمی برای خود قرار ده‌؛ و از نسل ما (هم) ، امتی تسلیم برای خود پدید آر و آداب دینی (حج) ما را به ما نشان ده‌؛ و بر ما بازگشت فرمای، همواره تویی تو، بس بازگردنده‌ی (بر بندگان و) رحمتگر بر ویژگان.»'),
(136, 2, 129, '«پروردگارمان! و در میان آنان، فرستاده‌ای از خودشان برانگیز حال آنکه آیات تو را بر آنان می‌خواند و کتاب و حکمت به آنان می‌آموزد و پاکیزه‌شان می‌کند. بی‌گمان تو خود، (همان) عزیز حکیمی.»'),
(137, 2, 130, 'و چه کسی - جز آن کس که خویشتن را سبک داشته - از آیین ابراهیم روی بر می‌تابد؟ مگر کسی که خود را سبک [:سفیه] داشته. و به‌راستی ما او را در این دنیا بی‌چون برگزیدیم و به‌درستی همو در آخرت بی‌گمان از شایستگان است.'),
(138, 2, 131, 'چون پروردگارش به او فرمود: «تسلیم (من) شو»، گفت: «تنها برای پروردگار جهانیان تسلیم شدم.»'),
(139, 2, 132, 'و ابراهیم و یعقوب، پسران خود را به همان (توحید ابراهیمی) سفارش کردند (و هر دو در وصیتشان چنین گفتند:) «ای پسران من! خدا برای شما (این) دین را برگزید؛ پس، به‌راستی هرگز نباید جز با تسلیم کامل بمیرید.»'),
(140, 2, 133, 'یا چون یعقوب را مرگ فرا رسید، حاضر بودید؟ چون به پسران خود گفت: «پس از من، چه چیز را خواهید پرستید؟» گفتند: «معبود تو را، و معبود پدرانت، ابراهیم و اسماعیل و اسحاق را. معبودی یگانه را می‌پرستیم‌؛ و ما تنها برای حضرتش تسلیم‌کنندگان (خود و دیگران)ایم.»'),
(141, 2, 134, 'آنان گروهی هستند که روزگارشان به سر آمده‌؛ دستاوردشان برای خودشان و دستاوردتان برای خودتان است‌؛ و شما از آنچه می‌کرده‌اند بازخواست نخواهید شد.'),
(142, 2, 135, 'و (اهل کتاب) گفتند: « (از) یهودیان یا نصرانیان باشید، تا هدایت یابید.» بگو: « (نه،) بلکه باید در روش (توحیدی) ابراهیم (باشیم،) در حالی که (او) رویگردان از باطل بود و از مشرکان نبود.»'),
(143, 2, 136, 'بگویید: «ما به خدا و به آنچه سوی ما فرود آمده و به آنچه سوی ابراهیم و اسحاق و یعقوب و فرزندان و نوادگان(‌شان) نازل شده، و به آنچه به موسی و عیسی داده شده و به آنچه به همه‌ی پیامبران از سوی پروردگارشان داده شده، ایمان آورده‌ایم‌؛ میان هیچ یک از ایشان جدایی نمی‌اندازیم‌؛ و ما تنها برای او تسلیم‌کنندگان (خود و دیگران)ایم.»'),
(144, 2, 137, 'پس اگر (آنان هم) به مانند آنچه شما بدان ایمان آوردید، ایمان آوردند، بی‌گمان هدایت یافته‌اند و اگر روی برتافتند، جز این نیست که سر ستیز و جدایی دارند. پس به زودی خدا تو را (از شرّ آنان) کفایت خواهد کرد، حال آنکه او بسی شنوا و داناست.'),
(145, 2, 138, '(نگارگری و) رنگ‌آمیزی خدایی [:درونی عقل و فطرت و برونی شریعت] را (نگهبان باشید)؛ و کیست خوش‌نگارتر از خدا؟ «و ما تنها او را پرستندگانیم.»'),
(146, 2, 139, 'بگو: «آیا در(باره‌ی) خدا با ما بحث و گفتگو و ستیز می‌کنید؛ حال آنکه او پروردگار ما و پروردگار شماست؟ و اعمال ما از آنِ ما، و اعمال شما از آنِ شماست و ما تنها برای او اخلاص داریم.»'),
(147, 2, 140, 'یا می‌گویید: «همانا ابراهیم و اسماعیل و اسحاق و یعقوب و فرزندان و نوادگان(شان) یهودیان یا نصرانیان بوده‌اند؟» بگو: «آیا شما بهتر می‌دانید یا خدا؟» و کیست ستمکارتر از کسی که شهادتی از خدا را در نزد خویش پوشیده بدارد؟ و خدا از آنچه می‌کنید غافل نیست.'),
(148, 2, 141, 'آن گروه را بی‌گمان روزگاران سپری شد؛ تنها برای ایشان است آنچه به دست آوردند و تنها برای شماست آنچه به دست آوردید؛ و از آنچه آنان می‌کرده‌اند، شما بازخواست نمی‌شوید.'),
(149, 2, 142, 'به زودی مردم سبک‌مغز خواهند گفت: «چه چیز آنان را از قبله‌ی خودشان که بر آن بوده‌اند رویگردان کرد؟» بگو: «مشرق و مغرب تنها از خداست‌؛ هر که را بخواهد به راهی راست هدایت می‌کند.»'),
(150, 2, 143, 'و بدین گونه ما شما [:معصومان محمدی (ص)] را امتی واسطه (میان رسول و امت) قرار دادیم تا بر مردمان گواه باشید، و پیامبر (نیز) بر شما گواه باشد. و قبله‌ای را که (چندی) بر آن بودی، ننهادیم جز برای آنکه کسی را که از پیامبر پیروی می‌کند، از آن کس که به عقبش بازگشت می‌کند، نشانه نهیم‌؛ هر چند (این کار) جز بر کسانی که خدا هدایت(شان) کرده، سخت گران بود و خدا بر آن نبوده است تا ایمان شما را ضایع گرداند، (زیرا) خدا به مردمان بی‌گمان مهربان و رحمتگر بر ویژگان است.'),
(151, 2, 144, 'ما همواره نگرانی صورتت را در آسمان می‌نگریم، پس تو را به‌راستی و درستی به قبله‌ای که بدان خشنودی بازمی‌گردانیم. پس روی خود را سوی مسجدالحرام کن‌؛ و هر جا بودید، روی خود را سوی آن بگردانید. و بی‌گمان، کتاب‌داده‌شدگان نیک می‌دانند که این (تغییر قبله) به‌راستی از جانب پروردگارشان حق است. و خدا از آنچه می‌کنند غافل نیست.'),
(152, 2, 145, 'و اگر به‌درستی هر (گونه) نشانه‌ای برای اهل کتاب بیاوری (باز) قبله‌ی تو را پیروی نمی‌کنند و تو (نیز) پیرو قبله‌ی آنان نیستی. و خود آنان پیروی‌کنندگان قبله‌ی یکدیگر نیستند. و پس از علم (وحی) که تو را (حاصل) آمده، اگر همواره از هواهای (غیر وحیانی) ایشان پیروی کنی، همانا در آن صورت بی‌گمان از ستمکارانی.'),
(153, 2, 146, 'کسانی که ایشان را کتاب (وحیانی) دادیم، همان گونه که پسران خود را می‌شناسند، او [:محمد و قرآن] را (هم) می‌شناسند؛ و همواره گروهی از ایشان حقیقت را بی‌گمان پنهان می‌دارند، در حالی که خودشان (هم) می‌دانند.'),
(154, 2, 147, '(کل) حق از جانب پروردگار توست. پس مبادا از دودلان باشی‌.'),
(155, 2, 148, 'برای هر امتی وجهه‌ای [:جهت و شریعتی] است (که) او (به همان وجهه) توجه‌شان می‌دهد. پس در کارهای نیک بر یکدیگر پیشی گیرید. هر کجا باشید، خدا همه‌ی شما را (بی‌استثنا) می‌آورد. همانا خدا بر همه چیز تواناست.'),
(156, 2, 149, 'و از هر کجا بیرون آمدی، روی خود را سوی مسجدالحرام گردان و البته این به‌راستی حقی از جانب پروردگار تو است‌. و خدا از آنچه می‌کنید غافل نیست.'),
(157, 2, 150, 'و از هر کجا بیرون آمدی (به هنگام نماز) روی خود را سوی مسجدالحرام بگردان و هر کجا بودید روی‌های خود را سوی آن (سامان) بگردانید، تا برای مردمان - بجز کسانی از آنان (که) ستم کردند - بر شما حجتی نباشد. پس از آنان نهراسید و از من بهراسید و تا نعمت خود را بر شما کامل گردانم و شاید شما هدایت شوید.'),
(158, 2, 151, 'چنان که در میان شما، فرستاده‌ای از خودتان روانه کردیم، در حالی که آیات ما را بر شما می‌خوانَد و پاکتان می‌گردانَد و شما را کتاب و حکمت می‌آموزد و آنچه توان یادگیریش را نداشته‌اید به شما یاد می‌دهد.'),
(159, 2, 152, 'پس مرا یاد کنید تا شما را یاد کنم‌؛ و سپاسم را به جای آرید و کفر وکفران نورزید.'),
(160, 2, 153, 'هان ای کسانی که ایمان آوردید! از شکیبایی و نماز یاری جویید (که) خدا بی‌گمان(همواره) با شکیبایان است.'),
(161, 2, 154, 'و درباره‌ی کسانی که در راه خدا کشته می‌شوند، نگویید مرده‌هایند؛ بلکه زندگانند، ولی شما باریک‌بینی نمی‌کنید.'),
(162, 2, 155, 'و بی‌چون و بی‌گمان شما را به چیزی از ترس و گرسنگی و کاهشی از اموال و جان‌ها و محصولات(تان) می‌آزماییم‌؛ و شکیبایان را مژده ده‌؛'),
(163, 2, 156, '(همان) کسانی که زمانی (که) مصیبتی به آنان در رسد، گویند: «ما بی‌گمان از آنِ خداییم و ما بی‌چون تنها سوی او بازگردندگانیم.»'),
(164, 2, 157, 'ایشان بر (سر و سامان)شان درودها و رحمتی (سرشار) از پروردگارشان است و تنها ایشان، هم‌اینان، راه‌یافتگانند.'),
(165, 2, 158, '«صفا» و «مروه‌» همواره از شعائر [:نشانگرهای دقیق] خدایند‌؛ پس هر که خانه‌ی خدا را زیارت کند یا عمره گزارد (در این دو صورت) بر او گناهی نیست که میان آن دو بسیار بگردد [:سعی به جای آورد]. و هر کس کار نیکی را به سختی انجام دهد، همواره خدا بسی شکرگزار داناست.'),
(166, 2, 159, 'بی‌گمان کسانی که نشانه‌های روشن و رهنمودی را که فرو فرستاده‌ایم - بعد از آنکه آنها را برای مردم در کتاب [:قرآن] توضیح داده‌ایم - پنهان می‌دارند، خدا ایشان را لعنت می‌کند و لعنت‌کنندگان (نیز) لعنتشان می‌کنند.'),
(167, 2, 160, 'مگر کسانی که توبه کردند و (فسادکاریشان را) اصلاح نمودند و (حقیقت را) آشکار کردند. پس بر ایشان برگشت می‌کنم و منم بسیار برگشت‌کننده‌ی رحمتگرِ بر ویژگان.'),
(168, 2, 161, 'بی‌گمان کسانی که کافر شدند و در حال کفر مردند، لعنت خدا و فرشتگان و تمام مردمان، همگی، بر آنان است.'),
(169, 2, 162, 'حال آنکه در آن (لعنت) جاودانه‌اند؛ نه عذابشان کاسته گردد و نه ایشان مهلت یابند.'),
(170, 2, 163, 'و معبود شما، معبودی یگانه است. جز او هیچ معبودی نیست. او رحمتگر بر همگان، (و) رحمتگر بر ویژگان است.'),
(171, 2, 164, 'به‌راستی در آفرینش آسمان‌ها و زمین و پی‌درپی آمدن شب و روز و کشتی‌هایی که در دریا روانند -با آنچه به مردم سود می‌رساند- و از آبی که خدا از آسمان فرو فرستاده، پس با آن زمین را بعد از مردنش زنده گردانید و در آن از هر گونه جنبنده‌ای پراکنده کرده و (نیز در) جابه‌جا کردن بادها و ابری که میان آسمان و زمین مسخّر است، برای گروهی که خردورزی می‌کنند همواره نشانه‌هایی (گویا) وجود دارد.'),
(172, 2, 165, 'و برخی از مردمان، کسانی‌اند که بجز خدا شریکانی (در خدایی) برمی‌گزینند، (و) آنها را همچون دوستی با خدا دوست می‌دارند. و کسانی که ایمان آوردند، برای خدا (در دل‌هاشان) محبت بیشتری دارند. کسانی که (با برگزیدن بت‌ها، به خود) ستم نموده‌اند، کاش می‌دانستند چون عذاب را مشاهده کنند؛ تمام نیرو(ها) از آنِ خداست و اینکه همانا خدا سخت‌کیفر است.'),
(173, 2, 166, 'چون پیشوایان از پیروانشان بیزاری جستند و عذاب(شان) را مشاهده کردند و توسط خودشان تمامی اسباب (برگزیده‌شان از ایشان) گسیخته گشت.'),
(174, 2, 167, 'و کسانی که پیروی(شان) کردند، گفتند: «کاش برای ما بازگشتی بود، پس همان گونه که (آنان) از ما بیزاری جستند (ما نیز) از آنان بیزاری می‌جستیم.» این گونه خدا کارهایشان را - که بر آنان (مایه‌ی) حسرت‌هاست - به ایشان نشان می‌دهد و از آتش (هم) بیرون‌شدنی نیستند.'),
(175, 2, 168, 'هان ای مردمان! از آنچه در زمین است حلالی پاکیزه را بخورید و گام‌های شیطان را پیروی نکنید، (که) همواره او برای شما دشمنی آشکارگر است.'),
(176, 2, 169, '(او) شما را فقط به بدی و زشتی تجاوزگر وامی‌دارد و اینکه علیه خدا چیزی را که نمی‌دانید، بگویید.'),
(177, 2, 170, 'و هنگامی که برایشان گفته شود: «آنچه را خدا نازل کرده است پیروی کنید» گویند: «(نه،) بلکه از چیزی که پدرانمان را بر آن یافتیم، پیروی می‌کنیم.» آیا و اگر هم پدرانشان هرگز چیزی را -با خردورزی- در نمی‌یافته‌اند و هدایت شده نبوده‌اند(باز هم در خور پیروی هستند)؟'),
(178, 2, 171, 'و مَثَل کسانی که کافر شدند، همچون مَثَل کسی است که حیوانی را -که جز صدا و ندایی (مبهم، چیزی) نمی‌شنود- بانگ می‌زند. (آری) کرانند، لالانند، کورانند. پس هم‌آنان خردورزی نمی‌کنند.'),
(179, 2, 172, 'هان ای کسانی که ایمان آوردید! از پاکیزه‌های آنچه روزیتان داده‌‌ایم، بخورید، و اگر تنها خدا را می‌پرستید فقط او را سپاس گزارید.'),
(180, 2, 173, '(خدا) تنها مردار و خون و گوشت خوک و آنچه را که (هنگام کشتنش) برای غیر خدا به آن ندا داده شده، بر شما حرام گردانیده است. پس هرکس بی‌اختیار ناچار شود، حال آنکه ظالمانه گِردش نپوید، بر او گناهی نیست. همانا خدا بسی پوشنده‌‌ی رحمتگر بر ویژگان است.'),
(181, 2, 174, 'بی‌گمان کسانی که آنچه را خدا از کتاب (وحی) نازل کرده، پنهان می‌دارند و در عوضِ آن‌، بهای اندکی را خریداری می‌کنند، (هم)آنان جز آتش در درونشان نمی‌خورند و خدا به روز قیامت با ایشان سخن نخواهد گفت و پاکشان نخواهد کرد و برایشان عذابی دردناک است.'),
(182, 2, 175, 'اینان کسانی‌اند که گمراهی را به بهای هدایت، و عذاب را به بهای مغفرت خریدند؛ پس (شگفتا!) چه چیز آنان را بر آتش شکیبا کرده است‌؟'),
(183, 2, 176, 'آن بی‌گمان بدان جهت است (که) خدا این کتاب (وحی) را به تمامی حقانیت نازل کرده است، و بی‌چون کسانی که در(باره‌ی این) کتاب (با یکدیگر) به اختلاف پرداختند، همانا در ژرفای جداسازی‌ای (و خودخواهیِ) دور و درازی فرو رفته‌اند'),
(184, 2, 177, 'نیکی (تنها، همه) این نیست که چهره‌های خود را سوی مشرق و مغرب بگردانید ولی (همه‌ی) برّ و طاعت شایسته، کسی است که به خدا و روز بازپسین و فرشتگان و کتاب (وحیانی) و پیامبران برجسته ایمان آورده و مال (خود) را -با وجود دوست داشتنش و بر پایه‌ی دوستی خدا- به نزدیکترین خویشان و (به) یتیمان و بینوایان و در راه ماندگان و درخواست‌کنندگان و در (راه آزاد کردن) گرفتارانی در بند بدهد و نماز را بر پای دارد و زکات را بپردازد و (نیز) وفا کنندگان به پیمانشان هنگامی که (با یکدیگر) هم‌پیمان شدند و (به ویژه) شکیبایان در سختی و زیان و به هنگام جنگ‌؛ اینانند کسانی که راست رفتند و اینان همان پرهیزگارانند.'),
(185, 2, 178, 'هان ای کسانی که ایمان آوردید! درباره‌ی کشتگانتان بر شما (حقّ) پیگیری مقرر شده: آزاد به آزاد و بنده به بنده و مؤنث به مؤنث. پس هر کس که از جانب برادر (دینی)اش [:ولیّ مقتول] چیزی (از حق قصاص یا خون‌بها) به او بخشیده شود (باید از گذشت ولیّ مقتول) به طور پسندیده پیروی گردد و با (رعایت) احسان، (خون‌بها) به او پرداخت شود. این (حکم)، تخفیف و رحمتی از پروردگار شماست‌؛ پس هر کس، بعد از این (بخشش)، تجاوز کند، (سرانجام) برای او عذابی دردناک است.'),
(186, 2, 179, 'و ای خردمندان! برای شما در پیگیری (جانیان)، زندگانی‌ای است؛ شاید (از تجاوز به جان‌های محترم دیگران) بپرهیزید.'),
(187, 2, 180, 'بر شما نوشته شد که چون یکی از شما را مرگ در رسد، اگر خیری [:مالی شایسته و افزون بر نیاز وارثان] بر جای گذارد، برای پدر و مادر و نزدیکتران (خود) به گونه‌ای پسندیده وصیّت کند؛ (حال آنکه) حقی است بر (عهده‌ی) پرهیزگاران.'),
(188, 2, 181, 'پس هر کس این (وصیّت واجب) را -پس از (اینکه) آن را شنید- تغییر دهد، گناهش تنها بر (گردن) کسانی است که آن را تغییر می‌دهند. خدا همواره بسیار شنوا و داناست.'),
(189, 2, 182, 'پس هر کس از انحراف (و تمایل بیجای) وصیت‌کننده‌ای یا از گناه تأخیرکننده‌ی خیر (در وصیت بر خلاف مصلحت) بیم داشته باشد، پس میانشان را سازش دهد، بر او هیچ گناهی نیست. همانا خدا پوشنده‌ی رحمتگر بر ویژگان است.'),
(190, 2, 183, 'هان ای کسانی که ایمان آوردید! روزه بر شما نوشته شده است، همان گونه که بر کسانی پیش از شما نیز نوشته شده بود. شاید پرهیزگاری کنید.'),
(191, 2, 184, '(روزه‌ی واجب تنها در) روزهای معدودی (است). پس هر کس از شما بیمار یا در سفر(عذرآور) بوده است (به همان شماره) تعدادی از روزهای دیگر را (روزه بدارد) و بر کسانی که (روزه) طاقت‌فرساست، کفاره‌ای است: خوراک دادن (به) بینوایی. و هر کس به طاقت‌فرسایی روزه را تحمل کند، همان برای او بهتر است. و اگر بدانید، (این)روزه گرفتن برای شما (بی‌طاقتان) بهتر است.'),
(192, 2, 185, 'ماه رمضان (همان ماه) است که در آن، قرآن (به گونه‌ای فشرده، شب‌قدر) فروفرستاده شده است؛ حال آنکه برای مردم راهبرد، و (متضمن) دلایلی آشکار از هدایت و (میزان) تشخیص حقّ از باطل است. پس هر کس از شما این ماه را (در وطنش) درک کند، باید آن را روزه بدارد و کسی که بیمار یا در سفر بوده است (باید به شماره‌ی آن) تعدادی از روزهای دیگر را (روزه بدارد). خدا برای شما آسانی را می‌خواهد و برای شما دشواری زیان‌آور را نمی‌خواهد. و باید همان شماره‌ی (مقرّر) را تکمیل کنید و خدا را به (پاس) آنچه رهنماییتان کرده است بس بزرگ دارید. و شاید شکر گزارید.'),
(193, 2, 186, 'و هر گاه (از) بندگان من، (کسی) از تو درباره‌ی من پرسید، (بگو) من بی‌گمان (به آنان) نزدیکم، و دعای دعا کننده را - هنگامی که مرا بخواند - اجابت می‌کنم. پس (آنان) باید فرمان مرا گردن نهند و به من ایمان آورند، شاید رشد یابند.'),
(194, 2, 187, '(در) شب روزه، همخوابگی با زنانتان حلال گردیده است . آنان برای شما پوششی هستند و شما (هم) برای آنان پوششی هستید. خدا (می)دانست که شما به خودتان خیانت می‌کرده‌اید، پس بر شما برگشت و از شما درگذشت‌؛ پس اکنون (در شب‌های ماه رمضان می‌توانید) با آنان همخوابگی کنید و آنچه را خدا برای شما (از فرزندان) مقرر داشته طلب کنید و بخورید و بیاشامید تا خط سفید (فجر) از خط سیاه (شب) برایتان نمایان گردد؛ سپس روزه را تا (فرا رسیدن) شب به اتمام رسانید. و در حالی که در مساجد معتکف هستید با آنان [:زنان] در نیامیزید. اینها حدود خداست. پس بدان‌ها ‍(در تجاوز از آن‌ها) نزدیک نشوید. این گونه خدا آیات خود را برای مردمان بیان می‌کند. شاید پروا کنند.'),
(195, 2, 188, 'و اموالتان را میان خودتان به ناروا مخورید، حال آنکه (به عنوان رشوه قسمتی از) آنها را سوی حاکمان می‌افکنید، تا بخشی از اموال مرد‌مان را به گناه -تأخیرکننده‌ی خیر- بخورید، در حالی که خودتان (هم خوب) می‌دانید.'),
(196, 2, 189, 'در باره‌ی (حکمت) هلال‌ها(ی ماه‌ها) از تو می‌پرسند. بگو: «آنها (شاخص‌های) گاه‌شماری برای مردم و (موسم) حج‌اند.» و نیکی آن نیست که از پشتِ خانه‌ها درآیید، بلکه نیکی کسی است که تقوا پیشه کرده است‌. و به خانه‌ها از درهای (ورودیِ) آنها درآیید. و از خدا بهراسید، شاید (خود و دیگران را) رستگار کنید.'),
(197, 2, 190, 'و در راه خدا، با کسانی که با شما کشتار می‌کنند کشتار کنید و (از عدالت) تجاوز نکنید؛ بی‌گمان خدا تجاوزکاران را دوست نمی‌دارد.'),
(198, 2, 191, 'و هر کجا بر ایشان - با پی‌جویی و کاردانی دقیق - دست یافتید، آنان را (از آن جا) بکشید و همان گونه که شما را (از مسجدالحرام و...) بیرون راندند، آنان را بیرون برانید. و فتنه از قتل شدیدتر است. (با این همه) در کنار مسجدالحرام با آنان کشتار مکنید تا (هنگامی که) با شما در آن‌جا کشتار کنند؛ پس اگر با شما (در آن‌جا) کشتار کردند، آنان را بکشید (که) کیفر کافران چنان است.'),
(199, 2, 192, 'پس اگر باز ایستادند، در نتیجه خدا پوشنده‌ی رحمتگر بر ویژگان است.'),
(200, 2, 193, 'با آنان کشتار کنید، تا دیگر هیچ فتنه‌ای (در سراسر جهان و زمان اسلام) نباشد و طاعت مخصوص خدا باشد؛ پس اگر دست (از فتنه) برداشتند، (در این صورت) جز بر ستمکاران هیچ گونه تجاوزی روا نیست.'),
(201, 2, 194, 'ماهِ حرام در برابر ماه حرام است و (هتک) حرمت‌ها (مورد) پیگیری است. پس هر کس بر شما تعدّی کرد، همان گونه که بر شما تعدّی کرده (همسان) بر او تعدّی کنید و از خدا پروا بدارید و بدانید که خدا بی‌گمان با تقواپیشگان است.'),
(202, 2, 195, 'و در راه خدا انفاق کنید و با دست خود (خود یا دیگران را) به هلاکت میفکنید و نیکی کنید (که) خدا به‌راستی نیکوکاران را دوست می‌دارد.'),
(203, 2, 196, 'و برای خدا حجّ و عمره را به اتمام برسانید. پس اگر (به علت موانعی از آن) بازداشته شدید، آنچه از قربانی میسر است (قربانی کنید) و تا قربانی به قربان‌گاه نرسیده سر خود را متراشید؛ پس هر کس از شما بیمار بوده یا در سرش ناراحتی باشد (و ناچار شود در احرام سر نتراشد) در این صورت (به جای آن،) باید (به عنوان) فدیه، روزه‌ای بدارد یا صدقه‌ای بدهد یا عبادتی (دیگر، مناسب با حج، همچون قربانی انجام دهد). پس هنگامی که ایمنی یافتید، (پس) هر کس با عمره‌ی تمتع سوی حج رهسپار شد اینجا آنچه از قربانی میسّر است (قربانی کند) و آن کس که (قربانی) نیافت (باید) در (ضمن) حج، سه روز روزه بدارد و چون برگشتید هفت روز دیگر (روزه بدارید). این ده، (روزهایی) کامل است (و) این (حجّ تمتّع) برای کسی است که اهلش [:خود و کسانش] ساکن در مسجدالحرام [:مکّه‌] نباشند. و از خدا بترسید و بدانید که خدا سخت‌کیفر است.'),
(204, 2, 197, 'حجّ (در) ماه‌های معلوم است؛ پس هر کس حج را در آن‌ها(برخود) واجب گرداند (که مُحرم شود، بداند که) در اثنای حج، هرگز هیچ گونه کارهای شهوانی جنسی و نه هیچ فسقی و نه هیچ جدالی نیست‌. و هر کار نیکی انجام می‌دهید، خدا آن را می‌داند. و برای خود توشه برگیرید که در حقیقت بهترین توشه، پرهیزگاری است‌. و ای خردمندان! از من پروا کنید.'),
(205, 2, 198, 'بر شما گناهی نیست که (در سفر حج) از فضل پروردگارتان بجویید. پس چون از عرفات کوچ نمودید، خدا را نزد مشعرالحرام یاد کنید و یادش کنید چنان که شما را هدایت کرد و گرچه پیش از آن بی‌گمان از گمراهان بوده‌اید.'),
(206, 2, 199, 'سپس، از همان جا که (انبوه) مردمان روانه می‌شوند، شما نیز روانه شوید و از خدا پوشش بخواهید که خدا همواره بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(207, 2, 200, 'پس چون آداب ویژه‌ی حجّ خود را به جای آوردید، همان گونه که یادآور پدرانتانید -یا با یادی بیشتر- خدا را به یاد آورید. پس از مردمان کسی‌(است که) می‌گوید: «پروردگارمان! به ما در (همین) دنیا (نعمت و برکت) عطا کن.» حال آنکه برای او در آخرت هرگز بهره‌ای نیست.'),
(208, 2, 201, 'و از آنان کسی می‌گوید: «پروردگارمان! در این دنیا ما را (زندگی) نیکی بده، و در آخرت (نیز زندگی) نیکی ده و ما را از عذاب آتش (دور) نگه دار.»'),
(209, 2, 202, 'ایشانند که از آنچه به دست آوردند، آنچه بهره‌ای بزرگ خواهند داشت. و خدا زودشمار است.'),
(210, 2, 203, 'و خدا را در روزهایی برشمرده [:مُنی] یاد کنید؛ پس هر کس در دو روز شتاب کند، (و اعمال مُنی را در آن دو روز انجام دهد) هیچ گناهی بر او نیست، و (نیز) هر که تأخیر کند (و اعمال مُنی را در سه روز انجام دهد) هیچ گناهی بر او نیست (و این اختیار) برای کسی (است) که (از محرّمات در حال احرام حج تا کنون) پرهیز کرده باشد (وگرنه سه روز باید در منی بماند). و از خدا پروا کنید و بدانید که شما تنها سوی او گردآورده خواهید شد.'),
(211, 2, 204, 'و از مردم کسی است که در زندگی (این) دنیا سخنش تو را به شگفتی می‌آورد و خدا را بر آنچه در دل دارد گواه می‌گیرد؛ و حال آنکه او سخت‌ترین دشمنان (خدا و مردم) است.'),
(212, 2, 205, 'و چون روی گرداند و یا سلطه‌ای به دست آورد، در زمین همی‌کوشد که افساد کند و کِشت و نسل را نابود سازد. و خدا فساد را دوست نمی‌دارد.'),
(213, 2, 206, 'و هنگامی که به او گفته شود: «از خدا پروا کن‌»، نخوت، وی را به گناه - تأخیرکننده‌ی خیر - می‌کشاند. پس جهنّم برای او بس است، و (این) همواره چه بد آرامگاهی است.'),
(214, 2, 207, 'و از مردمان کسی است که جان خود را برای طلب خشنودی خدا می‌فروشد. و خدا نسبت به بندگان بسی مهربان است.'),
(215, 2, 208, 'هان ای کسانی که ایمان آوردید! در سلم و سلامتی درآیید، در حالی که (این سلم) نگهبان شماست؛ و گام‌های شیطان را دنبال مکنید، (که) همواره او برای شما دشمنی آشکارگر است.'),
(216, 2, 209, 'پس اگر- بعد از آنکه برای شما دلایل آشکار آمد - دچار لغزشی شدید، بدانید که خدا همواره بسیار با عزت و حکمت است.'),
(217, 2, 210, 'آیا انتظار و نگرش اینان جز این است که خدا در (زیر) سایبان‌هایی از ابر سویشان بیاید، و فرشتگان هم (بیایند)؛ و کار (داوری) یکسره تمام شد؟ و همه‌ی کارها، فرمان‌ها و چیزها سوی خدا باز می‌گردند.'),
(218, 2, 211, 'از فرزندان اسرائیل بپرس (که) چه بسیار از نشانه‌های روشنی (که) به آنان دادیم. و هر کس نعمت خدا را - پس از آنکه برایش آمد - (به کفر یا کفران) تبدیل کند، پس خدا همواره سخت‌کیفر است.'),
(219, 2, 212, 'زندگی دنیا در چشم کافران آراسته شده، در حالی که مؤمنان را مسخره می‌کنند؛ و کسانی که تقواپیشه بوده‌اند در روز رستاخیز بالادستِ آنانند. و خدا هر که را خواهد، بی‌شمار روزی می‌دهد.'),
(220, 2, 213, 'مردمان (در گمراهی) امّتی یگانه بودند؛ پس خدا پیامبران برجسته را نویدآورنده و هشداردهنده برانگیخت، و با آنان کتاب (خود) را با تمامی حقانیت فرو فرستاد، تا میان مردمان در آنچه با هم اختلاف داشته‌اند حکم و پیوند دهد. و جز کسانی که (کتاب) به آنان داده شد – پس از آنکه دلایل روشن برایشان آمد – به خاطر ستم (و حسدی) که میانشان بود- هیچ کس در آن اختلاف نکرد. پس خدا آنان را که ایمان آوردند، به اذن خود به حقیقتِ آنچه (دیگران) در آن اختلاف داشتند، هدایت کرد. و خدا هر که را بخواهد سوی راهی راست (و راهوار) هدایت می‌کند.'),
(221, 2, 214, 'یا پنداشتید که داخل بهشت می‌شوید و حال آنکه هنوز مانند آنچه بر (سرِ) پیشینیان‌تان آمده، بر (سرِ) شما نیامده است‌؟ آنان دچار بسی سختی و زیان شدند، و به (هول و) تکان در آمدند، تا (جایی) که پیامبر (خدا) و کسانی که با وی ایمان آوردند گفتند: «پیروزی خدا کی خواهد بود؟» هشدار (که) بی‌گمان یاری خدا نزدیک است.'),
(222, 2, 215, 'از تو می‌پرسند چه چیزی انفاق کنند؟ بگو: «هر خیری انفاق کردید، برای پدر و مادر و نزدیکتران و یتیمان و مسکینان و در راه ماندگان است‌، و از هر خیری (که) انجام دهید همواره خدا به آن داناست.»'),
(223, 2, 216, 'کشتار (با مهاجمان) بر شما نوشته شده، در حالی که آن برایتان بسی ناگوار است. و چه بسا چیزی را خوش نمی‌دارید حال آنکه برایتان خوب است، و چه بسا چیزی را دوست می‌دارید در حالی که برایتان شرّ است. و خدا می‌داند و شما نمی‌دانید.'),
(224, 2, 217, 'از تو درباره‌ی ماهی که کشتار در آن حرام است می‌پرسند. بگو: «کشتار در آن (گناهی) بزرگ و بازداشتن از راه خدا و کفر ورزیدن به او و مسجدالحرام [:حج یا عمره] است‌؛ و بیرون راندنِ اهل آن از آنجا، نزد خدا (گناهی) بزرگ‌تر، و (این گونه) فتنه، از کشتار بزرگ‌تر است. و آنان پیوسته با شما کشتار می‌کنند تا – اگر بتوانند – شما را از دینتان برگردانند. و کسی از شما که از دین خود برگردد و در حال کفر بمیرد، (ایشان) کردارهایشان در دنیا و آخرت تباه است، و (هم)اینان اهل آتشند، (و) ایشان در آن ماندگارند.»'),
(225, 2, 218, 'بی‌گمان کسانی که ایمان آوردند، و کسانی که مهاجرت کردند، و در راه خدا جهاد نمودند، (هم)ایشان به رحمت خدا امیدوارند. و خدا بسی پوشنده‌‌ی رحمتگر بر ویژگان است.'),
(226, 2, 219, 'از شراب و آسان‌کننده‌ی گناه (همچون قمار) از تو می‌پرسند. بگو: «در آن دو گناه، بازدارنده‌ای بزرگ از خیر، و سودهایی (هم) برای مردمان است، و (اما) گناهشان از سودشان بزرگ‌تر است.» و از تو می‌پرسند: «چه چیزی انفاق کنند؟» بگو: «عفو [:بخشش] را.» این گونه خدا آیات (خود) را برایتان روشن می‌گرداند، شاید شما بیندیشید؛'),
(227, 2, 220, '(اندیشه‌ای) در(باره‌ی) دنیا و آخرت‌. و درباره‌ی یتیمان از تو می‌پرسند. بگو: «اصلاح‌کردنی (درست) برایشان خیر است (و غیر از آن شرّ است) و اگر با آنان همزیستی کنید، پس اینان برادران شمایند. و خدا افسادکننده را از اصلاح‌کننده باز می‌شناسد.» و اگر خدا بخواهد (در این باره) شما را به دشمنی هلاکت‌باری می‌اندازد. به‌راستی خدا عزیزی با حکمت است.'),
(228, 2, 221, 'و با زنان مشرک ازدواج مکنید تا (هنگامی که) ایمان بیاورند. بی‌گمان کنیزِ باایمان بهتر از زنِ مشرک است، هر چند او شما را به شگفت آورَد. و مردان مشرک را زن (باایمان) مدهید، تا (هنگامی که) ایمان بیاورند؛ و بی‌گمان برده‌ی باایمان بهتر از مردِ آزادِ مشرک است، هر چند شما را به شگفت آورَد. آنان (شما را) سوی آتش می‌خوانند، و خدا به اذن خودش، (شما را) سوی بهشت و پوشش فرا می‌خواند؛ و آیات خود را برای مردم روشن می‌گرداند، شاید متذکّر شوند.'),
(229, 2, 222, 'و از تو درباره‌ی عادت ماهانه‌(ی زنان) می‌پرسند. بگو: «آن، رنجی است (برای آنان و شوهرانشان). پس هنگام عادت ماهانه، از (این) زنان کناره‌گیری کنید و به آنان نزدیک نشوید تا پاک شوند. پس چون خود را پاک (و غسل) کردند، از همان جا و همان گونه که خدا به شما اجازه داده است، با آنان درآمیزید.» خدا همواره توبه‌کاران و پاکیزگان را دوست می‌دارد.'),
(230, 2, 223, '(فرمان خدا درباره‌ی آمیزش جنسی با زنان این گونه است:) زنانتان کشتزار(ان) شمایند؛ پس هر گاه و هر گونه که خواهید به کشتزار خود درآیید، و (از این کشتزار) حاصلی برای خود پیش فرستید. و از خدا پروا کنید و بدانید که او را ملاقات‌کننده‌اید. و مؤمنان را (به این ملاقات و دیگر نعمت‌های خدا) مژده ده.'),
(231, 2, 224, 'و خدا را در معرض سوگندهای خود قرار مدهید، (به این منظور) که نیکوکاری و پرهیزگاری کنید. و میان مردمان سازش دهید. حال آنکه خدا بس شنوای بسیار داناست.'),
(232, 2, 225, 'خدا شما را به لغو در سوگندهایتان مؤاخذه(ی اصلی) نمی‌کند، ولی شما را به آنچه دل‌هایتان (با این لغو) فراهم آورده، مؤاخذه می‌کند؛ و خدا پوشنده‌ا‌ی بردبار است.'),
(233, 2, 226, 'برای کسانی که به ترک همخوابگی با زنانشان سوگند می‌خورند [:ایلاء] چهار ماه انتظار (و مهلت) است‌؛ پس اگر (به آشتی) باز آمدند، خدا پوشنده‌‌ی رحمتگر بر ویژگان است‌؛'),
(234, 2, 227, 'و اگر آهنگ طلاق کردند، خدا همواره شنوای داناست.'),
(235, 2, 228, 'و (گروهی از) زنانِ طلاق داده شده [:آمیزش دیده] باید مدّت سه پاکی و سه حیض انتظار کشند، و اگر به خدا و روز بازپسین ایمان دارند، برای آنان حلال نیست که آنچه را خدا در رحم‌هاشان آفریده پنهان دارند؛ و شوهرانشان -اگر سرِ اصلاح دارند- به باز آوردن آنان در این (مدّت) سزاوارتر (از این همسرانشان و دیگران)اند، و مانند همان (وظایفی) که بر عهده‌ی زنان است، به طور شایسته، برای [:بر عهده‌ی] آنان [:مردان] است، و برای مردان بر آنان درجه‌ای برتری است (مانند طلاق و حق رجوع به شرط اصلاح). و خدا توانای حکیم است.'),
(236, 2, 229, 'طلاق دو بار است؛ پس (از آن) یا به خوبی نگاه داشتن (زن) یا به شایستگی آزاد کردن اوست. و برایتان حلال نیست که از آنچه به آنان داده‌اید، چیزی بازستانید، مگر آنکه طرفین بترسند که حقوق خدا را (در استمرار زناشویی) به‌پا ندارند. پس اگر بیم دارید که آن دو، حدود خدا را بر پا نمی‌دارند، در آنچه (زن برای آزاد کردن خود، از آنچه گرفته) فدیه دهد، گناهی بر این دو نیست. این‌ها حدود الهی است، پس از آن تجاوز مکنید. و کسانی که از حدود الهی تجاوز کنند، اینان، (هم)ایشان ستمکارانند.'),
(237, 2, 230, 'پس اگر (شوهر برای بار سوّم) او را طلاق گفت، پس از آن دیگر (آن زن) برای او (به هیچ وجه) حلال نیست، تا اینکه با شوهری غیر از او همخوابگی نماید. پس چنانچه (شوهر دوّم) وی را طلاق گفت، اگر آن دو گمان دارند که حدود خدا را بر پا می‌دارند، گناهی بر آن دو نیست که به یکدیگر باز گردند. و اینها حدود الهی است (که) آنها را برای گروهی که می‌دانند، بیان می‌کند.'),
(238, 2, 231, 'و هنگامی که زنان را طلاق گفتید، و عده‌ی خود را به پایان رساندند، (در این صورت) به شایستگی نگاهشان دارید یا به شایستگی آزادشان کنید و آنان را برای زیان‌رسانی طرفینی نگاه مدارید، تا (به حقوقشان) تعدّی کنید؛ و هر کس چنان کند، بی‌چون بر خود ستم کرده است. و آیات خدا را به مسخره مگیرید و نعمت خدا را بر خود و آنچه را که از کتاب و حکمت بر شما نازل کرده و به (وسیله‌ی) آن اندرزتان می‌دهد، به یاد آورید. و از خدا پروا کنید، و بدانید که خدا به هر چیزی داناست.'),
(239, 2, 232, 'و چون زنان را طلاق گفتید، و عدّه‌ی خود را به پایان رساندند، آنان را از ازدواج با همسران (پیشین یا کنونی)شان، چنانچه به شایستگی با یکدیگر تراضی نمایند، جلوگیری مکنید. هر کس از شما به خدا و روز بازپسین ایمان دارد، به این (دستورها) پند داده می‌شود؛ (مراعات) این برای شما پاکیزه‌تر و پاک‌تر است. و خدا می‌داند و شما نمی‌دانید.'),
(240, 2, 233, 'و مادران، فرزندان خود را دو سال تمام باید شیر دهند. (این حکم) برای مردی است که بخواهد دوران شیرخوارگی را تکمیل کند، و روزی و پوشاک آنان [:مادران] به شایستگی، بر (عهده‌ی) پدران است. هیچ کس جز به اندازه‌ی وسعش مکلّف نمی‌شود. هیچ مادری نباید به سبب فرزندش زیان طرفینی ببیند و نه هیچ پدری به خاطر فرزندش (زیان طرفینی ببیند). و مانند همین (احکام) بر (عهده‌ی) وارث (نیز) هست. پس اگر (پدر و مادر) بخواهند با رضایت و مشورت یکدیگر کودک را (از شیر) بازگیرند، گناهی بر آن دو نیست و اگر خواستید مادران به فرزندانشان شیر دهند بر شما گناهی نیست‌؛ هنگامی که چیزی را که (به آنان) پرداخت می‌کنید، به شایستگی بپردازید. و از خدا پروا بدارید و بدانید که همواره خدا به آنچه انجام می‌دهید بیناست.'),
(241, 2, 234, 'و کسانی از شما که مرگ گریبانگیرشان می‌شود و همسرانی بر جای می‌گذارند، (این همسران بایستی) چهار ماه و ده شب انتظار برند؛ پس هنگامی که به زمان پایانیشان رسیدند، در آنچه آنان به گونه‌ای پسندیده درباره‌ی خود انجام دهند، هرگز گناهی بر شما نیست. و خدا به آنچه انجام می‌دهید آگاه است.'),
(242, 2, 235, 'و در(باره‌ی) آنچه شما از زنان (در عدّه‌ی وفات) خواستگاری کردید، یا (آن را) در دل پوشیده داشتید، بر شما گناهی نیست. خدا می‌دانست که بی‌گمان (شما) به زودی به یاد آنان خواهید افتاد. ولی با آنان قول و قرار پنهانی مگذارید، مگر آنکه (با آنان) سخنی پسندیده بگویید؛ و به عقد زناشویی تصمیم مگیرید تا زمان مقرّر حتمی (آنها) سر آید. و بدانید که خدا آنچه را در درونتان (پنهان) است همی می‌داند. پس از او پروا کنید، و بدانید خدا همواره پوشنده‌ا‌ی بردبار است.'),
(243, 2, 236, 'اگر زنان را - مادامی که با آنان نزدیکی نکرده، یا برایشان مَهری معیّن نکرده‌اید - طلاق گویید، بر شما هرگز گناهی نیست و آنان را به شایستگی به مالی(افزون بر مهریه) بهره‌مند کنید - توانگر به اندازه‌ی گشایشش، و تنگدست به اندازه‌ی تنگناییش‌- حال آنکه (این) حقی است بر عهده‌ی نیکوکاران.'),
(244, 2, 237, 'و اگر پیش از آنکه با آنان نزدیکی کنید، طلاقشان دادید، حال آنکه برایشان مهری معیّن کرده‌اید، نصف آنچه را تعیین نموده‌اید (به آنان بدهید) مگر اینکه خودشان آن را (هم) ببخشند؛ یا کسی که پیوند نکاح (و طلاق) تنها به دست اوست (که همان شوهر است) ببخشد (که تمامی مهر مقرر را بپردازد) و گذشت کردن شما شوهران (از گذشت زنان) به تقوا نزدیکتر است‌. و در میان یکدیگر (بزرگواری و) فضیلت را فراموش مکنید. همواره خدا به آنچه انجام می‌دهید بیناست.'),
(245, 2, 238, 'بر نمازها و نماز میانه (مانند نماز صبح) محافظت و نگهبانی کنید و خاضعانه برای خدا به‌پا خیزید.'),
(246, 2, 239, 'پس اگر بیم داشتید، پیاده یا سواره (نماز را به جای آورید). پس هنگامی که ایمن شدید خدا را یاد کنید؛ همان‌گونه که آنچه نمی‌توانستید بدانید به شما آموخت.'),
(247, 2, 240, 'و کسانی از شما که مرگشان در می‌رسد و همسرانی بر جای می‌گذارند، سفارشی است برای همسرانشان (که وارثان)، آنان را تا یک سال (در نیاز‌های زندگیشان) بهره‌مند سازند و (از خانه‌ی شوهرانشان) بیرونشان نکنند. پس اگر (زودتر) بیرون بروند، در آنچه آنان به شایستگی درباره‌ی خودشان انجام دهند، هرگز گناهی بر شما نیست. و خدا عزیز حکیم است.'),
(248, 2, 241, 'و برای زنان طلاق داده شده (بر عهده‌ی شوهرانشان) هدیه‌ای است شایسته (که اضافه بر مهریه‌هاشان به آنان بدهند). حال آنکه (این خود) حقی است بر (عهده‌ی) پرهیزگاران.'),
(249, 2, 242, 'بدین گونه، خدا آیات خود را برایتان بیان می‌کند، شاید شما خردورزی کنید.'),
(250, 2, 243, 'آیا سوی کسانی که ترسان از خانه‌هاشان برون رفتند، حال آنکه هزاران تن بودند ننگریستی‌؟ پس خدا به آنان گفت: «بمیرید.» سپس آنان را زنده کرد. بی‌گمان خدا نسبت به مردم به‌راستی صاحبِ فضیلت است، ولی بیشتر مردمان سپاس نمی‌گزارند.'),
(251, 2, 244, 'و در راه خدا کشتار کنید و بدانید که خدا همواره بس شنوایی بسیار داناست.'),
(252, 2, 245, 'کیست آن کس که به (بندگانِ) خدا وام نیکویی دهد، پس خدا آن را برای او چندان برابر بیفزاید؟ و خدا(ست که در معیشت بندگان) تنگی و گشایش پدید می‌آورد؛ و تنها سوی او بازگردانده می‌شوید.'),
(253, 2, 246, 'آیا سوی گروهی چشمگیر از بنی‌اسرائیل پس از موسی ننگریستی؟ چون به پیامبر برجسته‌شان گفتند: «فرماندهی جنگی برایمان برگمار تا در راه خدا کشتار کنیم.» (آن پیامبر) گفت: «اگر کشتار بر شما مقرّر گردد، شاید شما کشتار نکنید.» گفتند: «چرا در راه خدا کشتار نکنیم با آنکه ما بی‌گمان از دیارمان و از (نزد) فرزندانمان بیرون رانده شدیم‌؟» پس هنگامی که کشتار بر آنان مقرّر شد، جز اندکی از آنان، (همگی) پشت کردند. و خدا به (حالِ) ستمگران بسی داناست.'),
(254, 2, 247, 'و پیامبر برجسته‌شان به آنان گفت: «به‌راستی، خدا طالوت را بر شما همواره به فرماندهی جنگ برگماشته است.» گفتند: «چگونه او را بر ما فرماندهی است با آنکه ما به فرماندهی از وی سزاوارتریم، در حالی که به او گشایشی مالی داده نشده است‌؟» پیامبر برجسته‌شان گفت: «به‌درستی، خدا او را بر شما برگزیده و (هم) او را در دانش و بینش و جسم(اش) بر شما گشایشی داده است. و خدا فرماندهیش را به هر کس که بخواهد می‌دهد و خدا گشایشگری داناست.»'),
(255, 2, 248, 'و پیامبر برجسته‌شان برایشان گفت: «در حقیقت، نشانه‌ی فرماندهی او این است که آن صندوق (عهد) که در آن آرامش خاطری از جانب پروردگارتان و بازمانده‌ای است از آنچه خاندان موسی و خاندان هارون (در آن) بر جای نهادند - در حالی که فرشتگان آن را حمل می‌کنند - برایتان خواهد آمد. اگر مؤمن بوده‌اید، برایتان به‌راستی در این (رویداد) نشانه‌ای بزرگ است.»'),
(256, 2, 249, 'پس چون طالوت با لشکریان (خود از آنان) جدا شد، گفت: «خدا همانا شما را به وسیله‌ی نهری آزماینده است. پس هر کس از آن بنوشد، از (پیروان) من نیست، و هر کس از آن نچشد، به‌راستی او از من است.» مگر کسی که با دستش کفی برگرفت، پس (همگی) جز اندکی از آنان، از آن (نهر) نوشیدند. پس هنگامی که (طالوت) با کسانی که همراه وی ایمان آورده بودند، از آن (نهر) گذشتند، گفتند: «امروز ما را یارای (مقابله‌) با جالوت و سپاهیانش نیست.» کسانی که به ملاقات خدا گمان (شایسته) دارند، گفتند: «بسا گروهی اندک که بر گروهی بسیار، به اذن پروردگار پیروز شدند و خدا با شکیبایان است.»'),
(257, 2, 250, 'و هنگامی که با جالوت و سپاهیان‌اش روبه‌رو شدند، گفتند: «پروردگارمان! بر (سراپای)‌مان سیل شکیبایی فرو ریز و گام‌هایمان را (در کارزار) استوار بدار و ما را بر گروه کافران پیروز فرما.»'),
(258, 2, 251, 'پس آنان را به اذن خدا شکست دادند و داوود، جالوت را کشت و خدا به او فرماندهی رسالت و حکومت و حکمت داد، و از آنچه می‌خواست به او آموخت. و اگر جلوگیری خدا برخی از مردم را به وسیله‌ی برخی دیگر نبود، زمین (و زمینه‌ی تکلیف) همواره تباه می‌گردید، لیکن خدا نسبت به جهانیان کانون فضلیت است.'),
(259, 2, 252, 'این‌ها آیات خداست، (که) ما آنها را به تمامی حق بر تو می‌خوانیم و به‌راستی تو بی‌گمان از پیامبرانی.'),
(260, 2, 253, 'اینان تمامی پیامبرانند؛ برخی از آنان را بر برخی (دیگر) از ایشان (در جهت یا جهاتی رسالتی) برتری دادیم. از آنان کسی است (که) خدا با او سخن گفت، و بعضی را (به) درجاتی (بر دیگران) بالا برد. و به عیسی پسر مریم دلایل آشکار دادیم و او را به وسیله‌ی روح‌القدس تأیید کردیم. و اگر خدا می‌خواست، کسانی که پس از آنان بودند –بعد از آن همه دلایل روشن که برایشان آمد- به کشتار یکدیگر نمی‌پرداختند؛ ولی (با هم) اختلاف کردند. پس بعضی از آنان کسانی (بودند که) ایمان آوردند و بعضی از آنان کسانی (بودند که) کافر شدند؛ و اگر خدا می‌خواست (با یکدیگر) کشتار نمی‌کردند، ولی خدا آنچه را می‌خواهد انجام می‌دهد.'),
(261, 2, 254, 'هان ای کسانی که ایمان آوردید! از آنچه به شما روزی داده‌ایم انفاق کنید، پیش از آنکه روزی فرا رسد که در آن نه داد و ستدی است و نه دوستی و نه شفاعتی. و کافران خودشان ستمکارانند.'),
(262, 2, 255, 'خدا(ست که) معبودی جز او نیست‌؛ زنده و بسی پاینده و بر پا دارنده است‌؛ نه هیچ چُرتی او را فرو می‌گیرد، و نه هیچ خوابی‌؛ آنچه در آسمان‌ها و آنچه در زمین است، تنها از آنِ اوست‌؛ کیست آن کس که جز به اذن او در پیشگاهش شفاعت کند؟ آنچه را در پیش روی آنان و آنچه را در پشت سرشان است می‌داند و به چیزی از علمِ او، جز به آنچه (خود) خواست، احاطه نمی‌یابند؛ کرسیِ (علم و قدرت و رحمت) او آسمان‌ها و زمین را به وسعت (بی‌کران) در برگرفته و نگهداری آنها بر او دشوار نیست. و اوست والای با عظمت.'),
(263, 2, 256, 'در دین هیچ (گونه) اجباری نیست، (زیرا) راه از بی‌راه به‌درستی آشکار شده‌؛ پس هر کس به طاغوت کفر ورزد و به خدا ایمان آورَد، به یقین به دستاویزی استوار - که آن را هرگز گسستی نیست - چنگ زده است. و خدا بس شنوایی بسیار داناست.'),
(264, 2, 257, 'خدا (تنها) ولی [:کارگزار و پشتوانه‌ی] کسانی است که ایمان آوردند؛ آنان را از تاریکی‌ها سوی روشنایی برون می‌برد. و (اما) کسانی که کفر ورزیدند، سرپرستانشان طغیان‌گرانند، که آنان را از روشنایی سوی تاریکی‌ها به در می‌برند. آنان همراهان آتشند، (و) ایشان در آن جاودانند.'),
(265, 2, 258, 'آیا سوی آن کس که از آن رو که خدا به او پادشاهی داده بود (و بدان می‌نازید، و) درباره‌ی پروردگار خود با ابراهیم محاجّه کرد، ننگریسته‌ای‌؟ چون ابراهیم گفت: «پروردگارم همان کس است که زنده می‌کند و می‌میراند.» (او) گفت: «من (هم) زنده می‌کنم و می‌میرانم.» ابراهیم گفت: «خدا به‌راستی خورشید را از خاور بر می‌آورد، تو آن را از باختر بر آر.» پس آن کس که کفر ورزیده بود مبهوت شد. و خدا گروه ستمکاران را هدایت نمی‌کند.'),
(266, 2, 259, 'یا همانند آن کس را که به مجتمعی که بلنداها و ساختمان‌هایش یکسره فروریخته بود، عبور کرد (و با خود می)گفت: «کی خدا (اهل) این (ویرانکده)ها را پس از مرگشان زنده می‌کند؟» پس خدا، او را (مدّت) صد سال میراند؛ سپس او را برانگیخت (و به او) گفت: «چقدر درنگ کردی‌؟» گفت: «یک روز یا پاره‌ای از روز را درنگ کرده‌ام.» گفت: « (نه،) بلکه صد سال درنگ کرده‌ای، پس به خوراکی و نوشیدنیِ خود بنگر (که هرگز طعم و رنگشان) تغییر نکرده است و به درازگوش خود بنگر (که چگونه متلاشی شده است). و برای اینکه تو را نشانه‌ای برای مردم (بر معاد) قرار دهیم، و به (این) استخوان‌ها(ی درازگوش هم) بنگر، (که) چگونه آنها را برداشته (و) به هم پیوند می‌دهیم‌؛ سپس گوشت بر آنها می‌پوشانیم.» پس هنگامی که (چگونگیِ زنده ساختن مردگان) برای او آشکار شد، گفت: «می‌دانم که خدا بر هر چیزی بسی تواناست.»'),
(267, 2, 260, 'و چون ابراهیم گفت: «پروردگارم! مرا نشان ده، چگونه مردگان را زنده می‌کنی‌؟» فرمود: «مگر ایمان نیاوردی‌؟» گفت: «چرا! ولی تا دلم (با دیدن این کیفیت ملکوتی بیشتر) آرامش یابد.» فرمود: «پس چهار پرنده برگیر؛ پس آنها را با خود بس مأنوس گردان‌؛ سپس بر هر کوهی پاره‌ای از آنها را قرار ده‌؛ آن‌گاه آنها را فراخوان، (و ببین که) شتابان سوی تو می‌آیند. و بدان که همواره خدا عزیزی است حکیم.»'),
(268, 2, 261, 'مَثَل کسانی که اموال خود را در راه خدا انفاق می‌کنند، همانند دانه‌ای است که هفت خوشه رویانید، (که) در هر خوشه صد دانه است‌؛ و خدا برای هر کس که بخواهد (آن را) چند برابر می‌کند. و خدا گشایشگری داناست.'),
(269, 2, 262, 'کسانی که اموالشان را در راه خدا انفاق می‌کنند، سپس در پیِ آنچه انفاق کرده‌اند، منّت و آزاری روا نمی‌دارند، پاداش آنان برایشان تنها نزد پروردگارشان است، و هرگز بیمی بر آنان نیست، و نه ایشان اندوهگین می‌شوند.'),
(270, 2, 263, 'گفتاری پسندیده (در برابر نیازمندان) و گذشتی (از تندی به آنان و یا تندیشان) بهتر از صدقه‌ای است که آزاری به دنبال دارد. و خدا بی‌نیازی بردبار است.'),
(271, 2, 264, 'هان ای کسانی که ایمان آوردید! صدقه‌های خود را با منّت و آزار، باطل مکنید؛ مانند کسی که مالش را برای ریا و خودنمایی به مردم انفاق می‌کند و به خدا و روز بازپسین ایمان نمی‌آورد. پس مَثَل او همچون مَثلِ سنگ خارایی است که روی آن خاکی (نشسته) است، و رگباری به آن رسیده، و آن (سنگ) را سخت و صاف (و بی‌غبار) بر جای نهاده است. آنان [:ریاکاران نیز] بدانچه به دست آوردند، دست نمی‌یابند. و خدا گروه کافران را هدایت نمی‌کند.'),
(272, 2, 265, 'و مَثَل کسانی که اموالشان را برای طلب خشنودی خدا و استواری خودشان انفاق می‌کنند، همچون مَثَل باغی است که بر فراز تپه‌ای قرار دارد (و) رگباری به آن رسیده، پس دو چندان خوردنیش را برآورد، پس اگر رگباری (هم) به آن نرسد، بارانی کم (همچون نم به آن رسیده). و خدا به آنچه می‌کنید بیناست.'),
(273, 2, 266, 'آیا یکی از شما دوست دارد که باغی سر در هم از درختان خرما و انگور داشته باشد که از زیر (درختان)شان نهرها روان است (و) برای او در آن (باغ) از هر گونه میوه‌ای (فراهم) است و در حالی که او را پیری در رسیده و فرزندانی خردسال دارد (ناگهان) بادی آتشین با تندی در آن (باغ) بوزد و (باغ یکسره) بسوزد؟ این گونه، خدا نشانه‌ها(ی خود) را برای شما روشن می‌گرداند، شاید شما بیندیشید.'),
(274, 2, 267, 'هان ای کسانی که ایمان آوردید! از چیزهای پاکیزه‌ای که به دست آوردید و از آنچه برای شما از زمین بر آورده‌ایم، انفاق کنید؛ و آنچه را خود ناخوش دارید هرگز مجویید (که از آن) انفاق کنید، در حالی که (اگر آن را به خودتان بدهند) جز با چشم‌پوشی (و بی‌میلی) گیرنده‌ی آن نیستید. و بدانید که خدا بی‌نیازی ستوده است.'),
(275, 2, 268, 'شیطان شما را به تهی‌دستی وعده می‌دهد و شما را به زشتی متجاوز از حد فرمان می‌دهد. و (اما) خدا شما را از جانب خود وعده‌ی پوشش و فضیلت و بخشش می‌دهد. و خدا گشایشگری داناست.'),
(276, 2, 269, '(خدا) به هر کس بخواهد حکمت می‌دهد و به هر کس حکمت داده شود، بی‌گمان، خیری فراوان داده شده است. و جز خردمندان ژرف‌نگر کسی پند نمی‌گیرد.'),
(277, 2, 270, 'و هر نفقه‌ای را که انفاق کردید، یا هر نذری که کردید، به‌راستی خدا آن را می‌داند. و برای ستمکاران هرگز یارانی نیست.'),
(278, 2, 271, 'اگر صدقه‌ها را آشکار کنید، این (برای تشویق دیگران هم) کارِ خوبی است، و اگر آن را پنهان دارید و به مستمندان بدهید، این برای شخص شما بهتر (که از ریا دورتر) است، و بخشی از گناهانتان را می‌زداید. و خدا به آنچه می‌کنید آگاه است.'),
(279, 2, 272, 'هدایتِ (اثربخش حتمی) آنان بر عهده‌ی تو نیست، بلکه خدا هر که را بخواهد (این گونه) هدایت می‌کند. و هر چه انفاق کنید برای خود شماست، و (اما) جز برای طلب وجهه‌ی خدایی انفاق نمی‌کنید و هر چه انفاق کنید به طور کامل به شما داده خواهد شد، حال آنکه ستمی بر شما نخواهد رفت.'),
(280, 2, 273, '(تمامی این انفاق‌ها) برای نیازمندانی کمرشکسته است که در راه خدا فرومانده‌اند و نمی‌توانند (برای تأمین هزینه‌ی زندگی) در زمین پا بزنند [:کوشش کنند و] از شدّت خویشتن‌داری، فردِ بی‌اطّلاع، آنان را توانگر می‌پندارد؛ آنها را از چهره و سیمایشان می‌شناسی، در حال پنهان کردن نیازشان (چیزی) از مردم نمی‌خواهند، و هر خیری (به آنان) انفاق کنید بی‌گمان خدا از آن آگاه است.'),
(281, 2, 274, 'کسانی که اموال خود را در شب و روز و نهان و آشکار انفاق می‌کنند، پاداش آنان نزد پروردگارشان تنها برای خودشان است، و نه بیمی بر آنان است و نه آنان اندوهگین می‌شوند.'),
(282, 2, 275, 'کسانی که ربا می‌خورند، (در زندگی‌هاشان) برنمی‌خیزند مگر برخاستن کسی که شیطان بر اثر تماس، دیوانه و پریشانش می‌کند. این بدین سبب است که آنان گفتند: «داد و ستد (بی‌ربا) صرفاً مانند رباست‌» و حال آنکه خدا (این) داد و ستد را حلال و ربا (و مفتخواری) را حرام گردانیده است. پس هر کس اندرزی از جانب پروردگارش بدو در رسد، آن‌گاه (از رباخواری) باز ایستاد، آنچه گذشته، از آنِ اوست و کارش سوی خداست. و کسانی که (به رباخواری) بازگردند، آنان اهل آتشند و همانان در آن ماندگارند.'),
(283, 2, 276, 'خدا ربا را محو می‌سازد و صدقات را پربار می‌سازد و خدا هیچ ناسپاسِ کافر (گناه‌پیشه‌ی) بس واپس‌زننده‌ی خیر را دوست نمی‌دارد.'),
(284, 2, 277, 'بی‌گمان کسانی که ایمان آوردند و کارهای شایسته‌(ی ایمان) کرده و نماز بر پا داشته و زکات داده‌اند، پاداش آنان نزد پروردگارشان تنها برای خودشان خواهد بود. و نه بیمی بر آنان است و نه اندوهگین می‌شوند.'),
(285, 2, 278, 'هان ای کسانی که ایمان آوردید! از خدا پروا کنید، و اگر مؤمن بوده‌اید آنچه را از ربا باقی مانده است واگذارید.'),
(286, 2, 279, 'و اگر (چنین) نکردید، پس اعلام به جنگی از طرف خدا و فرستاده‌ی وی کنید و اگر توبه کنید، سرمایه‌های شما برای خودتان است؛ نه ستمی می‌کنید و نه ستم می‌بینید.'),
(287, 2, 280, 'و اگر (بدهکاری) تنگدست باشد، تا (هنگام) گشایشی، مهلتی (برایش مقرر) است. و اگر دانسته بودید، بخشیدن آن برایتان بهتر است.'),
(288, 2, 281, 'و بپرهیزید از روزی که در آن، سوی خدا باز گردانده می‌شوید، سپس هر کس آنچه به دست آورده، تماماً به او داده شود و آنان مورد ستم (هم) قرار نمی‌گیرند.'),
(289, 2, 282, 'هان ای کسانی که ایمان آوردید! هر گاه به وامی تا سررسیدی معیّن با یکدیگر معامله کردید، آن را بنویسید؛ و باید نویسنده‌ای (صورت معامله را) بر اساس عدالت میانتان بنویسد و هیچ نویسنده‌ای نباید (در حد امکان) از نوشتن آن، بدان گونه که خدا تعلیمش داده خودداری کند؛ و کسی که (این) حق بر عهده‌ی اوست باید املا کند [:بگوید] و او [:نویسنده] (همان را) بنویسد و از خدا - پروردگارش - پروا نماید و از آن چیزی نکاهد؛ پس اگر کسی که حق بر ذمّه‌ی اوست، سفیه یا ناتوان است یا خود نمی‌تواند املا کند، ولیّ او باید با (رعایت) عدالت املا نماید و دو گواه از مردانتان را به گواهی بطلبید؛ پس اگر دو مرد نبودند، مردی را با دو زن، از میان گواهانی که (از آنان) رضایت دارید (گواه بگیرید) که (اگر) یکی از آن دو (زن از خاطرش) گم گشت، زن دیگر وی را یادآوری کند . و چون گواهان احضار شوند، نباید (از گواهی‌شان) خودداری کنند. و از نوشتن (بدهیتان) چه خرد و چه کلان -تا پایان زمان (مقرر)ش- نگران نباشید؛ این خود برای شهادت، عادلانه‌تر و استوارتر و برای اینکه دچار شک نشوید (به احتیاط) نزدیکتر است ، مگر آنکه تجارتی حاضر باشد که در میان خودتان انجام می‌دهید، پس بر شما گناهی نیست که آن را ننویسید. و (در هر حال) هر گاه داد و ستدی کردید گواه بگیرید و هیچ نویسنده و نه گواهی نباید زیان‌کاری طرفینی کند؛ و اگر چنین کنید، همواره این (خود) فسقی است به وسیله‌ی شما (به زیان خودتان و دیگران). و از خدا پروا کنید و خدا (بدین‌گونه) به شما آموزش می‌دهد و خدا به هر چیزی بسی داناست.'),
(290, 2, 283, 'و اگر بر حال سفری بوده‌اید و گواهی نیافتید، پس گروگانی دریافت‌شده، (بایسته است). پس اگر بعضی بعض دیگر را امین دانست آن‌که را امین دانسته، باید امانتش را (به او) بازپس دهد و از خدا -پروردگارش‌- به‌راستی بپرهیزد. و گواهی را کتمان مکنید و هر که آن را کتمان کند او بی‌گمان قلبش گناهکار است‌. و خدا به آنچه می‌کنید داناست‌.'),
(291, 2, 284, 'آنچه در آسمان‌ها و آنچه در زمین است تنها از خداست و اگر آنچه در دل‌های خود دارید، آشکار یا پنهان کنید، خدا شما را به آن محاسبه می‌کند؛ پس هر کس را بخواهد می‌بخشد و هر که را بخواهد عذاب می‌کند. و خدا بر هر چیزی بسی تواناست.'),
(292, 2, 285, 'پیامبر بدانچه از جانب پروردگارش بر او نازل شده ایمان آورده است و مؤمنان همگی به خدا و فرشتگان، و کتاب‌های (وحیانی) و فرستادگانش ایمان آورده‌اند (و گویند) : «میان هیچ یک از فرستادگانش جدایی نمی‌افکنیم‌.» و گفتند: «شنیدیم و اطاعت کردیم، پروردگارمان! پوشش تو را (خواستاریم)، و بازگشت همگان سوی تو است.»'),
(293, 2, 286, 'خدا هیچ کس را جز به اندازه‌ی توانش تکلیف نمی‌کند؛ آنچه (از خوبی با روشی درست و راهوار) به دست آورده به سود اوست و آنچه (از بدی بر خلاف روش فطری، عقلانی و شرعی) به دست آورده به زیان اوست. «پروردگارمان! اگر فراموش کردیم یا به خطا رفتیم بر ما مگیر. پروردگارمان! بار گرانی بر (دوش) ما مگذار، همچنان که بر (دوش) کسانی که پیش از ما بودند (به تنگنایی) نهادی. پروردگارمان! و آنچه هرگز تاب و توانش را نداریم بر ما تحمیل مکن و از ما درگذر و برایمان بپوشان و بر ما رحم کن. سرور ما تنها تویی، پس ما را بر گروه کافران پیروز گردان.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الم.'),
(295, 3, 2, 'خدا، (که) هیچ خدایی جز او نیست، زنده و بسی پاینده و نگهدارنده و ارزنده است.'),
(296, 3, 3, 'کتاب [:قرآن] را در تأیید آنچه (از کتاب‌های آسمانی) برابرش می‌باشد، به تمامی حقّ و به تدریج بر تو نازل کرد. و (نیز هر یک از) تورات و انجیل را یکجا نازل فرمود،'),
(297, 3, 4, 'از پیش، برای رهنمود مردمان. و فرقان [:قرآن، جدا کننده‌ی حق از باطل] را نازل کرد. همانا کسانی که به آیات خدا کفر ورزیدند، برایشان عذابی سخت است. و خدا عزیزی صاحب انتقام است.'),
(298, 3, 5, 'همواره هیچ چیزی (نه) در زمین و نه در آسمان بر خدا پنهان نیست.'),
(299, 3, 6, 'اوست کسی که شما را آن‌گونه که می‌خواهد در رحم‌ها صورتگری می‌کند. هیچ معبودی جز او -کانون عزت و حکمت- نیست.'),
(300, 3, 7, 'اوست کسی که این کتاب [:قرآن] را بر تو فرو فرستاد. پاره‌ای از آن، آیات محکم [:صریح و روشن] است (که) آنها مادر (و مرجع تفسیر و فهم) کتابند؛ و (پاره‌ای) دیگر (از نظر لفظی با آیاتی دیگر) همانندند. پس اما کسانی که در دل‌هایشان انحراف است، (روی این اصل نااصل) برای فتنه‌‌جویی و طلب تأویل آن (به دلخواه خود)، از متشابه آن پیروی می‌کنند (حال آنکه از نظر معنوی با هم متفاوتند). در حالی‌که تأویل قرآن را جز خدا (کسی) نمی‌داند و (هم‌چنین) پای برجایان در علم (ایمانی). می‌گویند: «ما بدان ایمان آورده‌ایم. همه‌ی قرآن (چه محکم و چه متشابه‌اش‌) از جانب پروردگار ماست و جز خردمندان عمیق کسی متذکّر نمی‌شود.»'),
(301, 3, 8, '(می‌گویند:) «پروردگارمان! پس از آنکه ما را هدایت کردی دل‌ها‌یمان را منحرف مگردان، و از جانب ویژه‌ی خود برایمان رحمتی ببخشای، همانا تویی تو بسی بخشایشگر.»'),
(302, 3, 9, '«پروردگارمان! به یقین تو در روزی که هیچ شک مستندی در آن نیست، گردآورنده‌ی (همه‌ی) مردمان (و کل مکلفان) هستی.» همواره خدا بی‌چون وعده‌‌(ی خود) را خلاف نمی‌کند.'),
(303, 3, 10, 'بی‌گمان کسانی که کافر شدند، هرگز نه اموالشان و نه اولادشان، چیزی (از عذاب خدا) را از آنان بی‌نیاز نخواهد کرد و آنان خودشان گیرانه‌ی آتشند.'),
(304, 3, 11, '(شیوه‌ی آنان) همچون شیوه‌ی فرعونیان و پیشینیانشان است (که) با آیاتمان (همان و ما را) تکذیب کردند؛ پس خدا به (سزای) گناهانشان آنان را برگرفت‌. و خدا سخت‌‌کیفر است.'),
(305, 3, 12, 'به کسانی که کفر ورزیدند بگو: «به زودی مغلوب خواهید شد و سوی جهنم گرد آورده می‌شوید. و چه بد آرامگاهی است.'),
(306, 3, 13, 'بی‌گمان در برخورد میان دو گروه، برای شما نشانه‌ای (و درس عبرتی) بود. گروهی در راه خدا کشتار می‌کنند و (گروه) دیگر کافرانند؛ حال آنکه آنان مؤمنان را - به چشم- خود دو برابر می‌بینند. و خدا هر که را بخواهد به یاری خود تأیید می‌کند. بی‌گمان در این (جریان) بی‌اَمان برای صاحبان بینش، عبرتی است.'),
(307, 3, 14, 'دوستی [:خواستنی]های شهوانی (گوناگون، اعم) از زنان و پسران و اموال فراوان، از زر و سیم و اسب‌های نشان‌دار و دام‌ها و کشتزار(ان)، برای مردمان آراسته شده‌؛ (لیکن) این‌ها بهره و سرمایه‌ی زندگی دنیاست. و بازگشت‌‌گاه نیکو تنها نزد خداست.'),
(308, 3, 15, 'بگو: «آیا شما را به بهتر از این‌ها خبری مهم دهم‌؟ برای کسانی که تقوا پیشه کردند، نزد پروردگارشان باغستان‌هایی سر در هم است، که از زیر (درختان)شان نهرها روان است‌؛ حال آنکه در آنها جاودانه‌اند و (نیز برایشان) همسرانی پاکیزه و خشنودی‌ای از خداست.» و خدا به بندگان بسی بیناست.'),
(309, 3, 16, 'کسانی که می‌گویند: «پروردگارمان! ما بی‌گمان ایمان آوردیم، پس گناهانمان را برایمان بپوشان و ما را از عذاب آتش نگهدار.»'),
(310, 3, 17, '(به ویژه) شکیبایان و راستان و خاشعان و انفاق‌‌کنندگان، و پوشش‌خواهان در سحرگاهان را.'),
(311, 3, 18, 'خدا -که همواره به فضیلت قیام دارد- (خود) گواهی داده است که جز او هیچ خدایی نیست و فرشتگان و دانشمندان (نیز گواهی می‌دهند) جز او که توانای با عزت و حکمت است، هیچ خدایی نیست.'),
(312, 3, 19, 'بی گمان، دین [:اطاعت راستین] نزد خدا همان اسلام [:تسلیم شایسته و بایسته در برابر او] است. و کسانی که کتاب (آسمانی) به آنان داده شده، با یکدیگر اختلاف نکردند، مگر پس از آنکه علم (وحیانی) برایشان آمد؛ در حال تجاوز ظالمانه‌ای که میانشان بود. و هر کس به آیات خدا کافر شود، همواره خدا زودشمار است.'),
(313, 3, 20, 'پس اگر با تو به محاجّه برخاستند، بگو: «من (تمام) چهره‌ی (هستی) خود را برای خدا تسلیم نمودم و (نیز) هر که مرا پیروی کرد و برای کسانی که به آنان کتاب (وحیانی) داده شده.» و (نیز) به ناآگاهان (از کتاب‌های وحیانی) بگو: آیا (در برابر خدا) تسلیم شدید؟» پس اگر تسلیم شدند، به‌راستی هدایت یافته‌اند و اگر روی برتافتند، تنها رساندن پیام (ربانی) بر (عهده‌ی) تو است‌. و خدا به (امور) بندگان بسی بیناست.'),
(314, 3, 21, 'بی‌گمان کسانی‌که به آیات خدا کفر می‌ورزند، و پیامبران برجسته را به ناحق می‌کشند و مردمانی را (هم) که به فضیلت فرمان می‌دهند می‌کشند، آنان را از عذابی دردناک نوید ده.'),
(315, 3, 22, 'ایشان کسانی‌اند که در دنیا و آخرت، اعمالشان به هدر رفته و برایشان هیچ یاورانی نیست.'),
(316, 3, 23, 'آیا فراسوی کسانی که بهره‌ای از کتاب (وحیانی) یافته‌اند ننگریسته‌ای؟ حال آنکه سوی کتاب خدا فراخوانده می‌شوند تا میانشان حکم کند، سپس گروهی از آنان به حال اعراض، روی بر می‌تابند.'),
(317, 3, 24, 'همانا این از آن‌رو بود که آنان (به پندار خود) گفتند: «هرگز آتش جز چند روزی ما را لمس نخواهد کرد.» و آنچه که بر خدا افترا می‌بسته‌اند آنان را در دینشان فریفته و مغرور کرده است.»'),
(318, 3, 25, 'پس چگونه خواهد بود آن هنگام (و هنگامه) که آنان را - برای روزی که هرگز شکّی مستند در آن نیست - گرد آوریم! به هر کس دستاوردش به تمام (و کمال) داده شود، در حالی که به آنان ستمی نرسد.'),
(319, 3, 26, 'بگو: «بار خدایا! ای مالک فرمانفرمایی! هر آن کس را که خواهی فرمان‌روایی بخشی و از هر که خواهی، فرمان‌روایی را بازستانی و هر که را خواهی، عزّت می‌بخشی و هر که را خواهی، خوار می‌گردانی. همه‌ی خوبی‌ها تنها به دست تو است‌، تو به‌درستی بر هر چیزی توانایی.‌»'),
(320, 3, 27, '«شب را در روز فرو می‌بری و روز را در شب فرو می‌بری، و زنده را از مرده بیرون می‌آوری، و مرده را از زنده بیرون می‌آوری، و هر که را خواهی بی‌حساب روزی می‌دهی.»'),
(321, 3, 28, 'مؤمنان هرگز نباید کافران را - به جای مؤمنان - (به عنوان اولیاء) سرپرستان و دوستانی برگیرند، و هر که چنان کند، در هیچ چیزی او را از (ولایت) خدا (بهره‌ای) نیست، مگر اینکه از آنان به گونه‌ای (شایسته) تقیّه (و خودنگهبانی) کنید. خدا شما را از خود بر حذر می‌دارد. و بازگشت (همه) تنها سوی خداست.'),
(322, 3, 29, 'بگو: «اگر آنچه در سینه‌هایتان نهان دارید یا آشکار کنید، خدا آن را می‌داند و (نیز) آنچه را در آسمان‌ها و آنچه را در زمین است می‌داند و خدا بر هر چیزی تواناست.'),
(323, 3, 30, 'روزی که هر کس آنچه (کار) نیک به جای آورده حاضر شده می‌یابد، دوست دارد کاش میان او و کارهای بدش، به‌راستی فاصله‌ای دور باشد. و خدا شما را از خود بر حذر می‌دارد. و خدا به بندگان‌(اش) بسی مهربان است.'),
(324, 3, 31, 'بگو: «اگر شما خدا را دوست داشته‌اید، از من پیروی کنید، تا خدا دوستتان بدارد، و تا گنا‌هان دنباله‌دارتان را برایتان پوشیده بدارد. و خدا بسی پوشنده‌‌ی رحمتگر بر ویژگان است.»'),
(325, 3, 32, 'بگو: «خدا و پیامبر(ش) را اطاعت کنید.» پس اگر رویگردانند، خدا هرگز کافران را دوست نمی‌دارد.'),
(326, 3, 33, 'بی‌گمان، خدا، آدم و نوح و خاندان ویژه‌ی ابراهیم و خاندان ویژه‌ی عمران را بر جهانیان برگزیده است‌؛'),
(327, 3, 34, 'فرزندانی را که بعضی از آنان از (نسل) بعضی دیگرند. و خدا بس شنوایی بسیار داناست.'),
(328, 3, 35, 'چون زن عمران گفت: «پروردگارم! آنچه در شکم دارم به‌راستی برایت نذر کردم تا (از غیر تو) آزاد شده باشد، پس از من به شایستگی بپذیر، که همواره تویی تو بسیار شنوای دانا.»'),
(329, 3, 36, 'پس چون او را فرونهاد گفت: «پروردگارم! من او را به‌راستی دختر زادم» - در حالی‌که خدا به آنچه او زاییده داناتر است- «و پسر چون دختر نیست، و من نامش را مریم نهادم و به‌راستی او و فرزندانش را از شیطان رانده‌شده به تو پناه می‌دهم.»'),
(330, 3, 37, 'پس پروردگارش وی [:مریم] را با حُسن قبول پذیرا شد و او را نیکو به بار آورد و زکریا را سرپرست وی قرار داد. زکریا هر بار که در محراب بر او وارد می‌شد، نزد او خوراکی‌ای (بی‌مانند) می‌یافت (و) می‌گفت: «مریم! این کی و از کجا (برایت آمده) است‌؟» (او در پاسخ) گفت: «این از جانب خداست، همواره خدا به هر کس بخواهد، بی‌حساب روزی می‌دهد.»'),
(331, 3, 38, 'اینجا (بود که) زکریا پروردگارش را خواند (و) گفت: «پروردگارم! از جانب ویژه‌ی خود، فرزندی پاک و پسندیده به من عطا فرمای. همواره تو بس شنونده‌ی درخواستی.»'),
(332, 3, 39, 'پس در حالی که وی ایستاده در محراب نماز می‌خواند، فرشتگان او را ندا در دادند: «خدا تو را به یحیی مژده می‌دهد؛ حال آنکه تصدیق‌کننده‌ی (حقانیت) کلمه‌ای از خداست و بزرگوار و خویشتندار (از تمامی گناهان) و پیامبری از شایستگان است.»'),
(333, 3, 40, 'گفت: «پروردگارم! کی مرا فرزندی خواهد بود؟ در حالی که همواره مرا پیری در رسیده و زنم نازاست!» (فرشته) گفت: « (کار پروردگار) همین‌گونه (بزرگ و فرزانه) است. خدا هر چه بخواهد انجام می‌دهد.»'),
(334, 3, 41, 'گفت: «پروردگارم! برایم (بر این اعجاز) نشانه‌ای قرار ده.» فرمود: «نشانه‌ات این است که سه روز با مردم - جز به اشاره - سخن نگویی و پروردگارت را بسیار یاد کن و شامگاهان و بامدادان (او را) تسبیح گوی.»'),
(335, 3, 42, 'و چون فرشتگان گفتند: «مریم! خدا تو را به‌درستی برگزیده و تو را پاک ساخته، و تو را بر زنان جهانیان برتری داده است.»'),
(336, 3, 43, '«مریم! برای پروردگارت دل‌‌باخته (و فرمانبر) باش و (برایش) سجده کن و با رکوع‌کنندگان رکوع نمای.»'),
(337, 3, 44, 'این (خود) از اخبار مهم غیب است (که) فراسویت وحی می‌کنیم و چون آنان قلم‌های خود را (برای قرعه‌کشی به آب) می‌افکندند -تا کدام‌یک سرپرستی مریم را به عهده گیرد- نزد آنان نبودی و (نیز) وقتی با یکدیگر کشمکش می‌کردند، نزدشان نبودی.'),
(338, 3, 45, 'چون فرشتگان گفتند: «مریم! به‌راستی خدا تو را به کلمه‌ای از جانب خود -که نامش مسیح عیسی‌بن‌مریم است‌- مژده می‌دهد. در حالی که در دنیا و آخرت آبرومند و از مقربان (درگاه خدا) است.»'),
(339, 3, 46, '«و در گهواره و میان‌‌سالی با مرد‌مان سخنی (وحیانی) گوید و از شایستگان (ویژه) است.»'),
(340, 3, 47, '(مریم) گفت: «پروردگارم! کی مرا فرزندی خواهد بود، حال آنکه بشری با من هم‌‌بستر نشده‌است‌؟» گفت: «چنین است (کار پروردگار). هر چه بخواهد می‌آفریند، هنگامی که به کاری فرمان دهد، فقط به آن می‌گوید: “شو.” پس می‌شود.»'),
(341, 3, 48, '«و خدا او را کتاب و حکمت و تورات و انجیل می‌آموزد.»'),
(342, 3, 49, 'و حال آنکه پیامبری است سوی بنی‌‌اسرائیل. (به آنان می‌گوید:) «بی‌گمان من از جانب پروردگارتان برایتان نشانه‌ای (ربانی) آورده‌ام؛ که من به‌راستی از گل برای شما (چیزی) به شکل پرنده می‌سازم، آن‌گاه در آن می‌دمم، پس به اذن خدا پرنده‌ای می‌شود؛ و به اذن خدا نابینای مادرزاد و پیس را بهبود می‌بخشم، و مردگان را به اذن خدا زنده می‌گردانم، و شما را از آنچه می‌خورید و در خانه‌‌هایتان ذخیره می‌کنید، خبری مهم می‌دهم. به‌راستی در این کارهای بزرگ، برایتان - اگر مؤمن بوده‌اید- بی‌گمان نشانه‌ای (ربانی) است.»'),
(343, 3, 50, '«و آنچه از تورات را که پیش روی من است تصدیق‌‌کننده‌ام. و تا پاره‌ای از آنچه را که بر شما حرام گردیده، برایتان حلال کنم. و از جانب پروردگارتان برایتان نشانه‌ای آورده‌ام، پس از خدا پروا بدارید و مرا اطاعت کنید.»'),
(344, 3, 51, '«همواره، خدا پروردگار من و پروردگار شماست، پس او را بپرستید؛ این است راهی بس راهوار و راست.»'),
(345, 3, 52, 'پس چون عیسی از آنان احساس کفر کرد، گفت: «یاران من سوی خدا کیانند؟» حواریان گفتند: «ما یاران (تو به سوی) خداییم، به خدا ایمان آوردیم و گواه باش که ما بی‌گمان تسلیم (او) هستیم.»'),
(346, 3, 53, '«پروردگارمان! (ما) به آنچه تو نازل کردی ایمان آوردیم و فرستاده(ات) را پیروی کردیم، پس ما را با (زمره‌ی) گواهان (در این سامان) ثبت فرمای.»'),
(347, 3, 54, 'و (دشمنان) مکر کردند و خدا (هم در پاسخشان) مکر کرد، و خدا بهترین مکرکنندگان است‌.'),
(348, 3, 55, 'چون خدا گفت: «عیسی! من تو را به تمامی برگیرنده و سوی (آسمانِ) خویش بالا‌برنده‌ام. و تو را از (آلایش) کسانی که کفر ورزیده‌اند پاک‌‌کننده‌ام، و تا روز رستاخیز، کسانی را که از تو پیروی کردند، برتر از کسانی که کافر شدند قراردهنده‌ام. سپس بازگشت شما تنها سوی من است. پس در آنچه در(باره‌ی) آن اختلاف می‌کرده‌اید، میان شما داوری خواهم کرد.»'),
(349, 3, 56, '«پس اما کسانی که کفر ورزیدند، در دنیا و آخرت به سختی عذابشان کنم، و هیچ‌(گونه) یاورانی ندارند.»'),
(350, 3, 57, '«و اما کسانی که ایمان آورده و کارهای شایسته(ی ایمان) کرده‌اند، (خدا) مزدهاشان را تماماً بدیشان می‌دهد. و خدا بیدادگران را دوست نمی‌دارد.»'),
(351, 3, 58, 'این از آیات (با عظمت قرآنی) و یادواره‌ی پُر حکمت است (که) ما آن را بر تو می‌خوانیم.'),
(352, 3, 59, 'همواره، مَثَلِ (آفرینش) عیسی نزد خدا همچون مَثَلِ (آفرینش) آدم است: او را از خاک آفرید، سپس بدو گفت: «شو.» پس می‌شود.'),
(353, 3, 60, 'تمامی حق (تنها) از پروردگار تو است، پس از تردیدکنندگان مباش.'),
(354, 3, 61, 'پس هر کس در این (حقیقت) بعد از علم وحیانی که تو را (حاصل) آمده، با تو محاجّه کند، بگو: «بیایید پسرانمان و پسرانتان را و زنانمان و زنانتان را و خودهامان و خودهاتان را فراخوانیم، سپس مباهله (و تقاضای لعنت) کنیم، پس لعنت خدا را بر دروغگویان قرار دهیم.»'),
(355, 3, 62, 'بی‌گمان این به‌راستی همان تمام حقیقت داستان گلچین شده و پیگیر گشته (ی مسیح) است؛ و هیچ معبودی جز خدا نیست و خدا همواره به‌درستی همان عزیز فرزانه است.'),
(356, 3, 63, 'پس اگر (از آن) رویگردانند، بی‌گمان خدا به (حال) مفسدان بسی داناست.'),
(357, 3, 64, 'بگو: «هان ای اهل کتاب! فرا سوی سخنی -که میان ما و شما یکسان است- در آیید که: جز خدا را نپرستیم و چیزی را شریک او نپنداریم و بعضی از ما بعضی دیگر را به جای خدا (به عنوان) پروردگارانی برنگیریم.» پس اگر (از این پیشنهاد) رویگردان شدند، بگویید: «شما شاهد باشید که ما بی‌چون و چرا تسلیم (خدا) هستیم.»'),
(358, 3, 65, 'هان ای اهل کتاب! چرا درباره‌ی ابراهیم محاجّه می‌کنید، حال آنکه تورات و انجیل جز بعد از او نازل نشده‌؟ پس آیا خردورزی نمی‌کنید؟'),
(359, 3, 66, 'هان شما (ای اهل کتاب!) همانان هستید (که) درباره‌ی آنچه نسبت به آن (بینش و) دانشی داشتید محاجّه کردید، پس چرا درباره‌ی چیزی که هرگز بدان دانشی ندارید محاجّه می‌کنید؟ حال آنکه خدا می‌داند و شما نمی‌دانید.'),
(360, 3, 67, 'ابراهیم نه یهودی بود و نه نصرانی، لیکن از باطل رویگردان و تسلیم (حق) بود، و از مشرکان نبود.'),
(361, 3, 68, 'بی‌گمان نزدیکترین مردمان به ابراهیم، همواره همان کسانی هستند که او را پیروی کردند؛ و (به ویژه) این پیامبر برجسته و کسانی که (به آیین او) ایمان آوردند. و خدا ولیِّ مؤمنان است.'),
(362, 3, 69, 'گروهی از اهل کتاب دوست داشتند (که) ای کاش شما را گمراه می‌کردند، حال آنکه جز خودشان (کسی) را گمراه نمی‌کنند و باریک‌بینی نمی‌نمایند.'),
(363, 3, 70, 'هان ای اهل کتاب! چرا به آیات خدا کفر می‌ورزید؟ حال آنکه خود (به‌درستیِ آنها) گواهید؟'),
(364, 3, 71, 'هان ای اهل کتاب! چرا حق را به لباس باطل می‌پوشانید؟ حال آنکه حقیقت را کتمان می‌کنید؟ و خود (هم) می‌دانید.'),
(365, 3, 72, 'و گروهی از اهل کتاب گفتند: «در آغاز روز به آنچه بر مؤمنان نازل شده، ایمان بیاورید و در پایان آن (همان را) انکار کنید؛ شاید آنان (از این ایمان) برگردند.»'),
(366, 3, 73, 'و (گفتند:) «جز برای کسی که دین شما را پیروی کند، ایمان (و اطمینان) نیاورید.» بگو: «هدایت، به‌راستی، هدایت خداست. (اینان چنان می‌کنند که) مبادا به کسی نظیر آنچه به شما داده شده، داده شود، یا در پیشگاه پروردگارتان با شما محاجّه کنند.» بگو: «فضیلت به دست خداست؛ آن را به هر کس بخواهد می‌دهد. و خدا گشایشگری بسیار داناست؛»'),
(367, 3, 74, '«رحمت خود را به هر کس بخواهد اختصاص می‌دهد. و خدا دارای فضیلت بزرگ است.»'),
(368, 3, 75, 'و برخی از اهل کتاب، کسانی‌اند که اگر ایشان را بر قطار شتری از طلا امین شمری، آن را به تو (بدون کم و کاستی) برمی‌گردانند، و از آنان کسی است که اگر ایشان را بر دیناری امین شمری، آن را به تو بر نمی‌گردانند، مگر تا هنگامی که بر آن ایستاده باشی. این همانا بدین سبب است که آنان (به پندار خود) گفتند: «در مورد مادر زادگان [:بومیان بی‌سواد و بی‌کتاب] بر زیان ما (اهل کتاب) راهی نیست.‌» و بر خدا دروغ می‌بندند، حال آنکه خودشان (هم) می‌دانند.'),
(369, 3, 76, 'آری، هر که به پیمان خود وفا کرد و پرهیزگاری نمود، بی‌گمان خدا پرهیزگاران را دوست می‌دارد.'),
(370, 3, 77, 'کسانی که با پیمان خدا و سوگندهایشان بهایی ناچیز را خریداری می‌کنند، آنان را بی‌چون در آخرت هیچ بهره‌ای نیست و خدا روز قیامت با آنان سخن (محبت‌آمیز) نمی‌گوید و به ایشان (از روی محبت) نمی‌نگرد و پاکشان نمی‌گرداند و برای آنان عذابی دردناک است.'),
(371, 3, 78, 'و بی‌گمان از (میان) آنان همواره گروهی هستند (که) زبان خود را به (خواندن) کتاب (وحی به گونه‌ای وحیانی) می‌پیچانند، تا (شما) آن (بربافته) را از (مطالب) کتاب (وحی) پندارید، با اینکه آن از (این) کتاب نیست. و می‌گویند: «آن از جانب خداست.» حال آنکه آن از جانب خدا نیست و بر خدا دروغ می‌بندند، حال آنکه خودشان (هم) می‌دانند (چیست).'),
(372, 3, 79, 'هرگز برای هیچ بشری چنان نبوده است که خدا به او کتاب و حکم و پیامبریِ برجسته دهد، سپس او به مردم بگوید: «بدون خدا، بندگان من باشید.» بلکه (همی گوید:) «به سبب آنکه کتاب (آسمانی) را تعلیم می‌دادید و از آن رو که از دانش‌‌پژوهان و دانشوران ربانی (بوده‌اید) و درس (وحیانی) می‌خوانده‌اید (مؤمنانی) ربانی باشید.»'),
(373, 3, 80, 'و (نیز) شما را فرمان نمی‌دهد که فرشتگان و پیامبران را به خدایی برگیرید. آیا پس از آنکه سر به فرمان (خدا) هستید (باز) شما را به کفر فرمان می‌دهد؟'),
(374, 3, 81, 'و چون خدا از تمامی پیامبران برجسته(ی صاحب کتاب) پیمان گرفت، که هرگاه به شما کتاب و حکمتی دادم، سپس شما را فرستاده‌ای آمد در حالی که آنچه را با شماست تصدیق‌‌کننده است، بی‌چون و بی‌گمان به او ایمان بیاورید، و به‌راستی و درستی یاریش کنید. (آن‌گاه) فرمود: «آیا اقرار کردید و بر این (جریان پیمانِ) بار گرانم را برگرفتید؟» گفتند: «(آری،) اقرار (پایدار) کردیم.» فرمود: «پس گواه باشید و من با شما از گواهانم.»'),
(375, 3, 82, 'پس، کسانی که بعد از آن (پیمان) روی برتابند، آنان (تنها) خودهاشان نافرمانانند.'),
(376, 3, 83, 'آیا پس دین غیر خدا را می‌جویند؟ حال آنکه هر که در آسمان و زمین است - خواه و ناخواه - سر به فرمان او نهاده‌اند و تنها سوی او باز گردانیده می‌شوند.'),
(377, 3, 84, 'بگو: «به خدا و آنچه بر ما نازل شده و آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط نازل گردیده و آنچه موسی و عیسی و (دیگر) انبیا از جانب پروردگارشان داده شدند، ایمان آوردیم و میان هیچ یک از آنان (با خدا و میان خودهاشان) تفرقه و جدایی نمی‌اندازیم و ما تنها او را فرمانبرداریم.»'),
(378, 3, 85, 'و هر که جز تسلیم (در برابر خدا) دین و طاعتی دیگر برجوید، هرگز از او پذیرفته نشود و وی در آخرت از زیان‌کاران است.'),
(379, 3, 86, 'چگونه خدا گروهی را -که پس از ایمانشان کافر شدند- هدایت می‌کند؟ حال آنکه مشاهده کردند (این) رسول به راستی حق است و برایشان دلایل روشن آمد. و خدا گروه بیدادگر را هدایت نمی‌کند.'),
(380, 3, 87, 'ایشان، سزایشان این است که بی‌گمان لعنت خدا و فرشتگان و مردمان بر ایشان است.'),
(381, 3, 88, 'در آن (لعنت) جاودانه‌اند؛ نه عذاب از ایشان کاسته گردد و نه مهلت یابند.'),
(382, 3, 89, 'مگر کسانی که پس از آن توبه کردند و (گذشته‌ی فاسدشان را) اصلاح نمودند. پس خدا بسی پوشنده‌‌ی رحمتگر بر ویژگان است.'),
(383, 3, 90, 'بی‌گمان کسانی که پس از ایمانشان کافر شدند، سپس بر کفر(شان) افزودند، هرگز توبه‌ی آنان پذیرفته نخواهد شد و ایشان‌، (هم)اینان گمراهانند.'),
(384, 3, 91, 'به‌راستی، کسانی که کافر شدند و در حال کفر مردند، گر چه (فراخنای) زمین پر از طلا باشد و آن را (برای رهایی خود) فدیه دهند، هرگز از هیچ یک از آنان پذیرفته نگردد. ایشان را عذابی دردناک است و هیچ یاورانی برایشان نیست.'),
(385, 3, 92, 'هرگز به نیکی [:احسان ربانی] نخواهید رسید، تا از آنچه دوست دارید (در راهش) انفاق کنید، و از هر چه انفاق کنید بی‌گمان خدا بدان بسی داناست.'),
(386, 3, 93, 'همه‌ی خوراکی(‌ها) بر فرزندان اسرائیل حلال بود، جز آنچه پیش از نزول تورات، اسرائیل [:یعقوب] بر خویشتن حرام کرد. بگو: «اگر (جز این است و) راستگو بوده‌اید، پس تورات را بیاورید. پس آن را بخوانید.»'),
(387, 3, 94, 'پس هر کس بعد از این، بر خدا دروغ بندد، ایشان خودشان ستمکارانند.'),
(388, 3, 95, 'بگو: «خدا راست گفت‌؛ پس از آیین [:روش] توحیدی ابراهیم - در حالی‌که از باطل رویگردان بود و از مشرکان نبود - پیروی کنید.»'),
(389, 3, 96, 'بی‌گمان، نخستین خانه‌ای که برای مردمان، نهاده شده، همواره همان است که در بَکّه [:مکّه] است (که کاملاً) در حال برکت و هدایت برای کل جهانیان است.'),
(390, 3, 97, 'در آن، نشانه‌هایی روشن -(از جمله) مقام [:جایگاه] ابراهیم‌- است و هر که بدان درآید در امان است و برای خدا، حجِّ آن خانه، بر (عهده‌ی) مردمان است‌؛ (البته بر) کسی که بتواند سوی آن راهی (درست و راهوار) بیابد. و هر که کفر ورزد (یا کفران کند، بداند) بی‌گمان خدا از جهانیان بی‌نیاز است.'),
(391, 3, 98, 'بگو: «هان ای اهل کتاب! چرا به آیات خدا کفر می‌ورزید؟ حال آنکه خدا بر آنچه می‌کنید گواه است.»'),
(392, 3, 99, 'بگو: «هان ای اهل کتاب! چرا کسی را که ایمان آورده است، از راه خدا باز می‌دارید، (و) آن (راه) را به کجی (و نابسامانی) می‌جویید، با آنکه خود (به‌راستی آن) گواهید؟» و خدا از آنچه می‌کنید غافل نیست.'),
(393, 3, 100, 'هان ای کسانی که ایمان آوردید! اگر (از) فرقه‌ای از کسانی که به آنها کتاب وحی داده شد فرمان برید، شما را پس از ایمانتان به حال کفر بر می‌گردانند.'),
(394, 3, 101, 'و چگونه کافر می‌شوید؟ حال آنکه آیات خدا بر شما خوانده می‌شود، و پیامبرش (هم) میان شماست‌؟ و هر کس به (وسیله‌ی) خدا (از هر گونه خطا) عصمت و بازداری جوید، و راه خدا را پوید، بی‌چون به راهی راست هدایت شده است.'),
(395, 3, 102, 'هان ای کسانی که ایمان آوردید! از خدا -آن گونه که حق پروا کردن از اوست- پروا کنید و زنهار، جز به حالت تسلیم نمیرید.'),
(396, 3, 103, 'و همگی با هم از تمامی ریسمان خدا عصمت بطلبید، و پراکنده نشوید و نعمت خدا را بر خود یاد کنید: آن‌گاه که دشمنان (یکدیگر) بودید، پس میان دل‌هاتان الفت انداخت، تا به نعمت (و لطف) او برادران (یکدیگر) شدید؛ و بر کناره‌ی پرتگاه آتش بودید، پس شما را از آن برهانید. این گونه، خدا نشانه‌های خود را برایتان روشن می‌کند، شاید شما راه یابید.'),
(397, 3, 104, 'و باید از میان شما، گروهی (مردمان را) سوی نیکی دعوت کنند و به کار معروف [:شایسته‌] وادارند، و از منکر [:ناشایست‌]، بازدارند و آنان همان رستگارکنندگانند.'),
(398, 3, 105, 'و مانند کسانی مباشید که پس از آنکه دلایل آشکار برایشان آمد، پراکنده شدند و با هم اختلاف کردند. و ایشان برایشان عذابی بس بزرگ است.'),
(399, 3, 106, 'روزی که چهره‌هایی بس سپید و چهره‌هایی بس سیاه گردند؛ پس اما کسانی که چهره‌هایشان سیاه شد (به آنان گفته شود:) «آیا بعد از ایمانتان کافر شدید؟ پس به (نتیجه‌ی) آنکه کافر شدید عذاب را بچشید.»'),
(400, 3, 107, 'و اما آنان که چهره‌هاشان بس سپید شد، پس (ایشان همچنان) در ژرفای رحمت خدا جاویدانند.'),
(401, 3, 108, 'اینهاست آیات خدا، (که) آنها را به تمامی حق بر تو می‌خوانیم. و خدا هیچ ستمی برای جهانیان نمی‌خواهد.'),
(402, 3, 109, 'و آنچه در آسمان‌ها و آنچه در زمین است تنها از خداست، و همه‌ی چیزها و کارها و فرمان‌ها تنها سوی خدا بازگردانده می‌شوند.'),
(403, 3, 110, 'شما بهترین امتّی بودید که برای مردمان برون آمدید. به کار معروف [:پسندیده] فرمان می‌دهید، و از کار منکر [:ناپسند] باز می‌دارید، و به خدا ایمان می‌آورید. و اگر اهل کتاب ایمان آورده بودند، بی‌گمان برایشان بهتر بود. برخی از آنان مؤمنانند و بیشترشان فاسقانند.'),
(404, 3, 111, '(شما مؤمنانی که شروط گذشته را انجام داده‌اید) کافران جز آزاری اندک، هرگز به شما زیانی نتوانند رسانید، و اگر با شما بجنگند، (پس) به شما پشت کنند، سپس یاری (هم) نیابند.'),
(405, 3, 112, 'هر کجا -با پیگیری‌(تان)- یافت شوند، مُهر خواری و بی‌مقداری بر (سر و سامان)‌شان زده شده، مگر به وسیله‌ی ریسمانی از خدا و ریسمانی از مردم. و مُهر (و مهار) گداصفتی بر آنان زده شد، (و) این بدین سبب بود که به آیات خدا همی کفر می‌ورزیده‌اند و پیامبران را به ناحق می‌کشته‌اند. (نیز) این (عقوبت) به سزای آن بود که نافرمانی و تجاوز می‌کرده‌اند.'),
(406, 3, 113, '(آنان) یکسان نیستند؛ از (میان) اهل کتاب، گروهی‌، ایستا و راستایند (که) آیات الهی را در دل شب می‌خوانند، در حالی که سر به سجده می‌نهند.'),
(407, 3, 114, 'به خدا و روز پایانی ایمان می‌آورند و به کار معروف[:پسندیده] فرمان می‌دهند و از کار منکر[:ناپسند] باز می‌دارند، و در کارهای نیک شتاب می‌کنند و آنان از شایستگانند.'),
(408, 3, 115, 'و هر کار نیکی انجام دهند، هرگز درباره‌ی آن ناسپاسی نبینند. و خدا به (حال) تقواپیشگان داناست.'),
(409, 3, 116, 'بی‌گمان کسانی که کافر شدند، هرگز اموالشان و اولادشان چیزی (از عذاب خدا) را از آنان بی‌نیاز نمی‌کند [:نمی‌زداید] و آنان همدمان آتشند، حال آنکه ایشان در آن جاودانه‌اند.'),
(410, 3, 117, 'مَثَل آنچه آنان در این زندگی دنیا (بی‌مهابا) خرج می‌کنند، همانند بادی است که در آن سرمای سختی است (که) به کشتزار قومی که بر خود ستم نموده‌اند برخورد کرده، پس آن را تباه ساخته است. و خدا به آنان ستم نکرده، بلکه آنان خود بر خویشتن ستم می‌کنند.'),
(411, 3, 118, 'هان ای کسانی که ایمان آوردید! از غیر خودهاتان همرازی برمگیرید. (آنان) از هیچ (کار) نابکار آشوبگری، در حق شما کوتاهی نمی‌نمایند، (و) آرزو دارند که در رنج بیفتید. بی‌گمان دشمنی از لحن و سخنشان آشکار است، و آنچه سینه‌هایشان نهان می‌دارد (از این دشمنی) بزرگ‌تر است. به‌راستی ما همه‌ی نشانه‌ها(ی ربانی) را برایتان بیان کردیم، اگر خردورزی می‌کرده‌اید.'),
(412, 3, 119, 'هان! شما همان کسانی هستید که آنان را دوست دارید، حال آنکه آنان شما را دوست ندارند و شما به همه‌ی کتاب‌ها(ی وحیانی) ایمان دارید. و هنگامی که با شما برخورد کنند گویند: «ایمان آوردیم‌» و هنگامی که (با یکدیگر) خلوت کنند، از خشم(شان) بر شما، سر انگشتانشان را می‌گزند. بگو: «با خشمتان بمیرید، بی‌گمان خدا به راز درون سینه‌ها(تان) بسی داناست.»'),
(413, 3, 120, 'اگر به شما خوشی در رسد، آنان را ناخوش آید، و اگر به شما گزندی در رسد، بدان شاد شوند و اگر صبر کنید و پرهیزگاری نمایید، نیرنگشان هیچ زیانی به شما نمی‌رساند؛ همانا خدا به آنچه می‌کنند محیط است.'),
(414, 3, 121, 'و چون (در جنگ احد) بامدادان از نزد کسانت بیرون آمدی، حال آنکه برای مؤمنان پناهگاه‌هایی را فراهم می‌سازی. و خدا بس شنوای بسیار داناست.'),
(415, 3, 122, 'چون دو گروه از شما همت گماردند که سستی کنند، حال آنکه خدا ولیّ هر دو است. و مؤمنان باید تنها بر خدا توکل کنند.'),
(416, 3, 123, 'و بی‌گمان خدا همواره شما را در (جنگ) بدر -با آنکه ناتوان و بی‌امان بودید- یاری کرد. پس از خدا پروا کنید، شاید سپاسگزاری نمایید.'),
(417, 3, 124, 'چون برای مؤمنان می‌گویی: «آیا هرگز برایتان کافی نیست که پروردگارتان، شما را با سه هزار فرشته‌ی فرو فرستاده شده، یاری می‌کند؟»'),
(418, 3, 125, '«آری، اگر صبر کنید و پرهیزگاری نمایید و (با همین جوش و خروش) بر شما بتازند، (همان‌گاه) پروردگارتان شما را با پنج‌هزار فرشته‌ی نشان گذارنده یاری خواهد کرد.»'),
(419, 3, 126, 'و خدا آن [:فرود آمدن فرشتگان] را جز مژده‌ای برایتان قرار نداد، و تا دل‌هاتان بدان آرامش یابد. و پیروزی جز از جانب خدای عزیز فرزانه نیست.'),
(420, 3, 127, 'تا برخی از کسانی را که کافر شدند (جان یا توانشان) را قطع و بریده کند یا آنان را خوار و بی‌مقدار سازد؛ پس نومید (و زیان‌بار) بازگردند.'),
(421, 3, 128, 'هیچ یک از کارها(ی خدا) برای تو نیست. یا (خدا) بر آنان ببخشاید، یا عذابشان کند؛ بی‌گمان آنان ستمکارانند..'),
(422, 3, 129, 'و آنچه در آسمان‌ها و آنچه در زمین است تنها از آنِ خداست. برای هر که بخواهد پوشش می‌نهد، و هرکه را بخواهد عذاب می‌کند. و خدا پوشنده‌ی رحمتگر بر ویژگان است..'),
(423, 3, 130, 'هان ای کسانی که ایمان آوردید! ربا را (با سود) چندین برابر (اصل مالتان) مخورید، و از خدا پروا کنید، شاید (خود و دیگران را) رستگار کنید.'),
(424, 3, 131, 'و از آتشی که برای کافران آماده شده است بپرهیزید.'),
(425, 3, 132, 'خدا و رسول را فرمان برید، شاید رحمت خدا فراگیرتان گردد.'),
(426, 3, 133, 'و رو به مغفرت و پوششی از پروردگارتان و بهشتی که وسعتش آسمان‌ها و زمین است (و) برای پرهیزگاران آماده شده، شتابان از یکدیگر پیشی جویید.'),
(427, 3, 134, 'کسانی که در گشایش و تنگی زیان‌بار انفاق می‌کنند، و فرو برندگان خشمشان و درگذرندگان از مردمان و آنان که از (خشم بر) مردم در‌گذرنده‌اند و خدا نکوکاران را دوست دارد.'),
(428, 3, 135, 'و کسانی که چون کار زشتی کنند، یا بر خودهاشان ستم روا دارند، خدا را یاد کنند، پس برای گناهانشان همی پوشش خواهند. و چه کسی جز خدا گناهان دنباله‌دار را می‌پوشد؟ و (نیز) بر آنچه مرتکب شده‌اند، از آنجا که (به حرمت فحشا و ظلم به خود و عاقبت وخیم‌) آگاهند، بر هر کار زشتی که کرده‌اند پافشاری نمی‌کنند.'),
(429, 3, 136, 'ایشان، پاداششان پوششی از جانب پروردگارشان است و بوستان‌هایی که از زیر (درختان)شان نهرها روان است. و چه خوب است اجر عمل‌کنندگان (به خوبی‌ها).'),
(430, 3, 137, 'همواره پیش از شما سنتّ‌هایی سپری شده است. پس در زمین بگردید (و) سپس بنگرید که فرجام تکذیب‌‌کنندگان چگونه بوده است‌.'),
(431, 3, 138, 'این (قرآن) بیانی است (وحیانی) برای مردمان و رهنمون و اندرزی است برای پرهیزگاران.'),
(432, 3, 139, 'و سستی مکنید و غمگین مشوید، حال آنکه شما (از دیگران) برترید اگر مؤمن بوده‌اید.'),
(433, 3, 140, 'اگر به شما جراحتی در رسد، آن قوم را نیز بی‌گمان جراحتی نظیر آن رسیده. و ما این روزها(ی شکست و پیروزی) را میان مردمان به نوبت می‌گردانیم (تا آنان پند گیرند) و برای اینکه خدا کسانی را که ایمان آورده‌اند نشانه‌ای بگذارد و از (میان) شما گواهانی برگیرد. و خدا ستمکاران را دوست نمی‌دارد.'),
(434, 3, 141, 'و برای اینکه خدا کسانی را که ایمان آورده‌اند خالص و پیراسته گرداند و کافران را محو و نابود سازد.'),
(435, 3, 142, 'یا پنداشتید که داخل بهشت می‌شوید، حال آنکه هنوز خدا کسانی را از شما که جهاد کردند و (نیز) شکیبایان شما را نشان نگذارده است.'),
(436, 3, 143, 'و شما بی‌چون و بی‌گمان مرگ را - پیش از آنکه با آن روبه‌رو شوید - سخت آرزو می‌کرده‌اید، پس همانا آن را دیدید، حال آنکه می‌نگرید.'),
(437, 3, 144, 'و محمد، جز فرستاده‌ای (ربانی) - که پیش از او (هم) فرستادگانی آمده‌اند- نیست. پس آیا اگر او بمیرد یا کشته شود، بر گذشته‌هایتان باز می‌گردید؟ و هر کس بر گذشته‌ی (نابسامان) خود باز گردد، هرگز هیچ زیانی به خدا نمی‌رساند، و به زودی خدا سپاسگزاران را پاداش می‌دهد.'),
(438, 3, 145, 'و هیچ نفسی [:کسی] را هرگز نبوده است که جز به اذن خدا بمیرد؛ (حال آنکه) ثبت‌گشته‌ای است زمان‌بندی شده. و هر که پاداش دنیا را بخواهد به او برخی از آن را می‌دهیم و هر که پاداش سرای پایانی را بخواهد از آن به او می‌دهیم. و به زودی سپاسگزاران را پاداش خواهیم داد.'),
(439, 3, 146, 'و چه بسیار از پیامبرانی که بسیاری (از) دست‌پروردگان (خدا) همراهشان کشتار کردند؛ پس برای آنچه در راه خدا بدیشان رسید ناتوانی ننموده و (در برابر دشمن،) جویای سکون و بی‌‌تفاوتی و خواری نشدند. و خدا شکیبایان را دوست می‌دارد.'),
(440, 3, 147, 'و سخن آنان جز این نبوده که گفتند: «پروردگارمان! گناهانمان را، و زیاده‌‌رویمان را در کارمان بپوشان و گام‌های ما را استوار بدار و ما را بر گروه کافران یاری فرمای.»'),
(441, 3, 148, 'پس خدا، پاداش دنیا و پاداش نیک آخرت را به آنان عطا کرد. و خدا نیکوکاران را دوست می‌دارد.'),
(442, 3, 149, 'هان ای کسانی که ایمان آوردید! اگر کسانی را که کافر شدند پیروی کنید، شما را به گذشته‌ها(ی کفرآمیز)تان برگشت می‌دهند، پس بازگشتی زیان‌بار خواهید داشت.'),
(443, 3, 150, 'بلکه خدا (تنها) مولای شماست و او بهترین یاری‌‌دهندگان است.'),
(444, 3, 151, 'به زودی در دل‌های کسانی که کفر ورزیده‌اند بیم خواهیم افکند، به سبب آنکه چیزی را با خدا شریک گردانیده‌اند که خدا بر (حقانیت) آن، هیچ دلیل قاطعی نازل نکرده است و پناهگاهشان آتش است. و پایگاه ستمگران چه بد است.'),
(445, 3, 152, 'و (در نبرد احد) خدا همواره وعده‌ی خود را به‌راستی با شما راست آورد؛ چون با اذن او، با آنان برخوردی حساس و مرگ‌بار کردید؛ تا هنگامی که سست شدید و در کار (جنگ و بر سر تقسیم غنایم) با یکدیگر به نزاع پرداختید. و پس از آنکه آنچه را دوست دارید (از غنیمت و پیروزی) به شما نشان داد نافرمانی نمودید. برخی از شما دنیا را و برخی از شما آخرت را می‌خواهد. سپس برای آنکه شما را بیازماید، از (تعقیب) آنان بازتان داشت، و از شما در گذشت. و خدا برای مؤمنان، کانون فضلی (بزرگ) است.'),
(446, 3, 153, '(خدا وعده‌اش را به شما راست آورد) چون در حال گریز (از کوه، خودتان و نیروهاتان را) بالا می‌بردید و بر هیچ کس روی نمی‌آوردید؛ در حالی که پیامبر، شما را – دورادور از پشت سرتان - فرا می‌خواند. پس خدا به پاداشتان اندوهی برابر اندوهشان رسانید، تا (سرانجام) - (نه) بر آنچه از کف داده‌اید و نه آنچه به شما رسیده است- اندوهگین نشوید. و خدا به آنچه می‌کنید آگاه است.'),
(447, 3, 154, 'سپس (خدا) پس از آن اندوه، آرامشی (به صورت) خوابی کم و سبک، بر شما فرو فرستاد، (که) گروهی از شما را فرا گرفت. و گروهی (که) همتشان (تنها) درباره‌ی خودشان بود، درباره‌ی خدا، گمان‌های ناروا، همچون گمان‌های (دوران) جاهلیّت می‌برند (و) گویند: «آیا ما را در این کار اختیاری هست‌؟» بگو: «(سررشته‌ی) کارها به‌راستی یکسره برای خداست.» آنان چیزی را در خودهاشان نهان می‌دارند که برای تو آشکار نمی‌دارند. گویند: «اگر ما را در این کار اختیاری بود (و وعده‌ی پیامبر واقعیت داشت) در اینجا کشته نمی‌شدیم.» بگو: «اگر شما در خانه‌های خود (هم) بودید، کسانی که کشته شدن بر آنان نوشته شده، بی‌گمان (با پای خودشان) سوی بسترها(ی مرگ‌)شان می‌رفتند؛ و برای اینکه خدا آنچه را در سینه‌هایتان (پنهان) است بیازماید، و برای آنکه آنچه در دل‌هایتان (پنهان) می‌دارید، پاک و خالص گرداند. و خدا راز درون سینه‌ها را بسی می‌داند.»'),
(448, 3, 155, 'به‌راستی روزی که دو گروه (در احد) رویارو شدند، کسانی که از میان شما (به دشمن) پشت کردند، همواره جز این نبود که به سبب پاره‌ای از آنچه (از گناه) حاصل کرده بودند، شیطان آنان را بلغزانید. و همانا خدا از ایشان بی‌گمان در گذشت، زیرا خدا همواره پوشنده‌ای بردبار است.'),
(449, 3, 156, 'هان ای کسانی که ایمان آوردید! مانند کسانی نباشید که کافر شدند و برای برادرانشان - هنگامی که (به سختی پای) در زمین زدند (که راهی سخت پیمودند) و یا جهادگر شدند (و کشته شدند) - گفتند: «اگر نزد ما (مانده) بودند، نمی‌مردند و کشته نمی‌شدند.» تا خدا آن را در دل‌هایشان حسرتی قرار دهد. و خدا(ست که) زنده می‌کند و (هم اوست که) می‌میراند و خدا(ست که) به آنچه می‌کنید بسی بیناست.'),
(450, 3, 157, 'و به‌راستی اگر در راه خدا کشته شوید یا بمیرید، بی‌گمان پوششی از خدا(یتان) و رحمتی از او (برای شماست که) از (همه‌ی) آنچه آنان گرد هم می‌آورند بهتر است.'),
(451, 3, 158, 'و بی‌چون اگر بمیرید یا کشته شوید، همانا تنها سوی خدا گرد آورده خواهید شد.'),
(452, 3, 159, 'پس (ای پیامبر!) به (برکتِ) رحمتی از خدا برایشان نرم‌خو (و پر مهر) شدی، و اگر تندخو و سخت‌‌دل بودی همواره از پیرامونت پراکنده می‌شدند. پس از آنان در گذر و برایشان پوشش بخواه و در کار (جنگ) با آنان مشورت کن و چون تصمیم گرفتی بر خدا توکل کن، زیرا خدا توکل‌کنندگان را به راستی دوست می‌دارد.'),
(453, 3, 160, 'اگر خدا شما را یاری کند، هیچ کس برایتان غالب نخواهد بود و اگر خوارتان کند (و دست از یاریتان بردارد) پس چه کسی بعد از او یاریتان خواهد کرد؟ پس مؤمنان باید تنها بر خدا توکل کنند.'),
(454, 3, 161, 'و برای هیچ پیامبر برجسته‌ای شایسته نبوده است که خیانت ورزد. و هر کس خیانت ورزد، روز قیامت با آنچه در آن خیانت کرده، بیاید. سپس به هر کس (پاداش) آنچه کسب کرده به تمامی داده شود و بر آنان ستم نرود.'),
(455, 3, 162, 'پس آیا کسی که خشنودی خدا را پی می‌گیرد، همچون کسی است که به خشمی از خدا دچار گردیده و پناهگاهش جهنّم است‌؟ و چه بد بازگشتگاهی است.'),
(456, 3, 163, 'ایشان نزد خدا درجاتی هستند، و خدا به آنچه می‌کنند بیناست‌.'),
(457, 3, 164, 'بی‌گمان، خدا بر مؤمنان همی منّت نهاد؛ چون پیامبری از خودشان در میانشان برانگیخت که آیات خدا را برایشان می‌خواند، و پاکشان می‌گرداند و کتاب و حکمت به آنان می‌آموزد، گرچه پیش از آن در ژرفای گمراهی آشکارگری بوده‌اند.'),
(458, 3, 165, 'آیا و هنگامی که به شما (در نبرد احد) مصیبتی در رسید - که همواره دو برابرش را (به دشمنان خود) رساندید - گفتید: «(این) از کجاست؟» بگو: «آن از خود شما (و ناشی از نافرمانی خودتان) است.» به‌راستی خدا به هر چیزی تواناست.'),
(459, 3, 166, 'و روزی که (در احد) آن دو گروه به هم برخوردند، آنچه به شما رسید به اذن خدا بود (تا شما را بیازماید) و مؤمنان را نشانه‌ای نهد.'),
(460, 3, 167, 'و (همچنین برای اینکه) کسانی را که دورویی نمودند (نیز) نشانه‌ای نهد و به ایشان گفته شد: «بیایید در راه خدا کشتار یا دفاع کنید.» گفتند: «اگر کشتاری می‌دانستیم، به‌راستی از شما پیروی می‌کردیم.» آن روز، آنان در آن هنگام (و هنگامه) به کفر نزدیکترند تا به ایمان. با دهان‌ها‌شان چیزی می‌گویند که در دل‌هاشان نیست. و خدا به آنچه پنهان می‌کنند داناتر است.'),
(461, 3, 168, '(همان) کسانی که درباره‌ی برادرانشان [:هم مسلکانشان] - حال آنکه در خانه‌هاشان نشسته بودند - گفتند: «اگر از ما پیروی می‌کردند کشته نمی‌شدند.» بگو: «اگر (از)راستان بوده‌اید مرگ را از خودتان دور کنید.»'),
(462, 3, 169, 'هرگز کسانی را که در راه خدا کشته شده‌اند، مردگان مپندار، بلکه زندگانی هستند (که) نزد پروردگارشان روزی داده می‌شوند؛'),
(463, 3, 170, 'حال آنکه به آنچه خدا از فضل خود به آنان داده است شادمانند و برای کسانی که (هنوز) به آنان - از پی ایشان- نپیوسته‌اند طلب بشارت و نوید می‌کنند که نه بیمی بر ایشان است و نه ایشا‌ن اندوهگین می‌شوند.'),
(464, 3, 171, 'به نعمت و فضیلتی از خدا - و اینکه خدا پاداش مؤمنان را تباه نمی‌گرداند- بشارت می‌خواهند؛'),
(465, 3, 172, 'کسانی که (در نبرد احد) – پس از آنکه زخم برداشتند – دعوت خدا و پیامبر(ش) را اجابت کردند. برای کسانی از آنان که نیکی و پرهیزگاری کردند پاداشی بزرگ است.'),
(466, 3, 173, '(همان) کسانی که مردمی به ایشان گفتند: «مردمان برای (جنگ با) شما (نیرو) گرد آورده‌اند، پس از آنان بترسید.» پس (این سخن) بر ایمانشان افزود و گفتند: «خدا ما را بس است و چه کارگزار نیکی است.»'),
(467, 3, 174, 'پس با نعمت و فضیلتی از جانب خدا، (از میدان نبرد) بازگشتند، در حالی که هیچ آسیبی به آنان نرسیده بود و خشنودی خدا را پیروی کردند. و خدا دارای فضیلتی بزرگ است.'),
(468, 3, 175, 'تنها، این شیطان است که دوستان و پیروانش را (از اینان) می‌ترساند. پس اگر مؤمنید از آنان مترسید، و از من بهراسید.'),
(469, 3, 176, 'و کسانی که در کفر شتاب می‌کنند، تو را اندوهگین نسازند. آنان، بی‌امان، هرگز به خدا هیچ زیانی نمی‌توانند برسانند. خدا می‌خواهد در آخرت برای آنان بهره‌ای قرار ندهد و برایشان عذابی بزرگ است.'),
(470, 3, 177, 'بی‌چون کسانی که کفر را به (بهای) ایمان خریدند هرگز به خدا هیچ زیانی نتوانند رسانید و برایشان عذابی دردناک است.'),
(471, 3, 178, 'و هرگز نباید کسانی که کافر شده‌اند بپندارند اینکه به ایشان همواره مهلت می‌دهیم به‌راستی برای آنان نیکوست. ما فقط به ایشان مهلت می‌دهیم، تا بر گناهشان بیفزایند و (آن‌گاه) عذابی خفّت‌آور خواهند داشت.'),
(472, 3, 179, 'خدا بر آن نبوده است تا مؤمنان را به این (حالی) که شما بر آن هستید، واگذارد، تا آنکه پلید را از پاک جدا کند. و خدا بر آن نبوده است که شما را بر پنهان آگاه گرداند، ولی خدا از میان فرستادگانش هر که را بخواهد بر می‌گزیند. پس به خدا و پیامبرانش ایمان بیاورید و اگر بگروید و پرهیزگاری کنید، برایتان پاداشی بزرگ است.'),
(473, 3, 180, 'و کسانی که - به آنچه خدا از فضل خود به آنان عطا کرده - بخل می‌ورزند، هرگز مَپندارند (که) آن (بخل) برایشان خوب است؛ بلکه همان برایشان بد است، (و) به زودی آنچه که بدان بخل ورزیده‌اند، روز قیامت طوق گردنشان می‌گردد. میراث آسمان‌ها و زمین از آنِ خداست و خدا به آنچه می‌کنید آگاه است.'),
(474, 3, 181, 'همواره خدا، سخن کسانی (از یهودیان) را که گفتند: «خدا نیازمند است و ما بی‌‌نیازیم‌»، به‌درستی شنید. آنچه را گفتند و (نیز) به‌ناحق کشتن پیامبران را، ثبت و ضبط خواهیم کرد، و خواهیم گفت: «بچشید عذاب بس سوزان را.»'),
(475, 3, 182, 'این (عقوبت) پیش‌فرستاده‌ی دستاورد شماست، و خدا بی‌گمان برای بندگان (خود) زیاد بیدادگر نیست.'),
(476, 3, 183, 'کسانی گفتند: «خدا با ما پیمان بسته که به هیچ پیامبری ایمان نیاوریم، تا برای ما قربانی‌ای بیاورد که آتش آن را (به نشانه‌ی قبول) بخورد.» بگو: «همواره پیش از من (هم) پیامبرانی آمدند که دلایل آشکار را -با آنچه گفتید- برایتان آوردند. اگر راست می‌گویید، پس چرا آنان را کشتید؟»'),
(477, 3, 184, 'پس اگر تو را تکذیب کردند، بی‌گمان پیامبرانی (هم) پیش از تو، (که با) دلایل روشن و نوشته‌ها و کتاب روشن آورده بودند، تکذیب شدند.'),
(478, 3, 185, 'هر کسی چشنده‌ی (طعم) مرگ است و تنها روز رستاخیز پاداش‌هایتان به طور کامل به شما داده می‌شود؛ پس هر کس از آتش نجات یابد و به بهشت در آید، همواره کامیاب شده است. و زندگی دنیا جز مایه‌ی فریب نیست.'),
(479, 3, 186, 'به‌راستی در مال‌هایتان و جان‌هایتان بی‌گمان آزموده خواهید شد، و از کسانی که پیش از شما به آنان کتاب داده شده، و (نیز) از کسانی که به شرک گراییده‌اند (سخنان دل)آزار بسیاری خواهید شنید؛ و اگر صبر و پرهیزگاری کنید، این (خود) به‌راستی (نشانی) از عزم استوار (شما) در کارهاست.'),
(480, 3, 187, 'و چون خدا از کسانی که به آنان کتاب داده شده، پیمان گرفت که باید آن را برای مردمان بی‌گمان بیان کنید، و کتمانش نکنید. پس آن (عهد) را پشت (سر)شان انداختند، و با آن، بهایی ناچیز دریافتند. پس چه بد چیزی را خریداری می‌کنند.'),
(481, 3, 188, 'هرگز گمان مبر کسانی را که بدانچه آورده‌اند شادمانی کنند، و دوست دارند به آنچه نکرده‌اند مورد ستایش قرار گیرند. پس هرگز گمان مدار که آنان را نجاتی از عذاب است، در حالی که برایشان عذابی دردناک است.'),
(482, 3, 189, 'و فرمانروایی آسمان‌ها و زمین تنها برای خداست. و خدا بر هر چیزی تواناست.'),
(483, 3, 190, 'بی‌گمان در آفرینش آسمان‌ها و زمین، و در پی هم آمدن شب و روز، برای خردمندان نشانه‌هایی (قانع کننده) است.'),
(484, 3, 191, 'کسانی که خدا را (در همه‌ی احوالشان)، ایستادگان و نشستگان و به پهلوآرمیدگان یاد می‌کنند، و در آفرینش آسمان‌ها و زمین می‌اندیشند (و گویند): «پروردگارمان! این‌ها را بیهوده نیافریده‌ای. تو منزّهی! پس ما را از عذاب آتش دوزخ نگهدار.»'),
(485, 3, 192, '«پروردگارمان! هر که را تو بی‌چون به آتش در آوری، همواره خوار و بی‌مقدارش کرده‌ای. و برای ستمکاران هیچ یاورانی نیستند.»'),
(486, 3, 193, '«پروردگارمان! ما شنیدیم دعوتگری (ما را) به ایمان فرا می‌خواند که: “به پروردگارتان ایمان آورید.” پس ایمان آوردیم. پروردگارمان! گناهانمان را برایمان بپوشان و بدی‌هامان را جبران فرما و ما را با نیکان بمیران.»'),
(487, 3, 194, '«پروردگارمان! و آنچه را بر (عهده‌ی) فرستادگانت برایمان وعده داده‌ای به ما بده و ما را روز رستاخیز رسوا مگردان. همانا تو وعده‌(ات) را خلاف نمی‌کنی.»'),
(488, 3, 195, 'پس پروردگارشان (دعای آنان را) برایشان اجابت کرد (و فرمود): «من همواره عمل هیچ صاحب عملی از شما را، از مرد یا زنی - حال آنکه برخی از برخی دیگرید- تباه نمی‌کنم. پس کسانی که هجرت کرده و از خانه‌های خود رانده شده و در راه من آزار دیده و جنگیده و کشته شده‌اند، بی‌گمان گناهانشان را به‌راستی از ایشان می‌زدایم و آنان را همواره در باغ‌هایی که از زیر (درختان)‌شان نهرها روان است، به‌درستی، در می‌آورم. حال آنکه (این) پاداشی است از جانب خدا، و پاداش نیکو تنها نزد خداست.»'),
(489, 3, 196, 'هرگز مباد (که) رفت و آمد با جنب‌و‌جوش کافران در شهرها تو را فریب دهد.'),
(490, 3, 197, '(این) کالایی ناچیز (و برخورداری اندکی) است. سپس پناهگاهشان دوزخ است و چه بد آرامگاهی است.'),
(491, 3, 198, 'ولی کسانی که از پروردگارشان پروا دارند، برایشان باغ‌هایی است که از زیر (درختان)شان نهرها روان است، (که) در آن جاودانه‌اند. حال آنکه (این) مهمان‌سرایی از جانب خداست و آنچه نزد خداست برای نیکان بهتر است.'),
(492, 3, 199, 'و بی‌گمان از میان اهل کتاب کسانی هستند که به خدا - و بدانچه سوی شما نازل شده و به آنچه سوی خودشان فرود آمده - ایمان می‌آورند، در حالی که برای خدا خاشعند، و با آیات خدا (چیزی را به) بهایی ناچیز نمی‌خرند. ایشانند که نزد پروردگارشان پاداش خود را خواهند داشت. همواره، خدا زودشمار است.'),
(493, 3, 200, 'هان ای کسانی که ایمان آورد‌ید! صبر کنید، و (هم) در صبر هماهنگی نمایید، و با یکدیگر رابطه و پیوند داشته باشید، و مرزها را نگهبانی نمایید، و از خدا پروا نمایید. شاید (خود و دیگران را) رستگار کنید.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'هان ای مردمان! از پروردگارتان پروا کنید: کسی که همه‌ی شما را از یک تن آفرید و همسرش را (نیز) از او پدید آورد، و از این دو، مردان و زنانی بسیار پراکند. و خدا را پروا بدارید که به (وسیله‌ی) او (از یکدیگر) درخواست (و با هم همکاری) می‌کنید و (نیز) ارحامتان را (پروا بدارید). همواره خدا بر (سر و سامان)‌تان بسی نگهبان بوده است.'),
(495, 4, 2, 'و اموال یتیمان را به آنان بازپس دهید و مال پاکیزه و مرغوب آنان را با (مال) ناپاکیزه(‌ی خودتان) هرگز جایگزین نکنید و اموال آنان را در شمار اموال خودتان مخورید (که) بی‌گمان این گناهی بزرگ بوده است.'),
(496, 4, 3, 'و اگر در (اجرای) قسط [:فوق عدالت] میان یتیمان بیمناکید (دست کم) هر چه از زنان که شما را پسند افتاد -دو دو، سه سه و چهار چهار- به همسری برگزینید. پس اگر بیم دارید (که نسبت به آنان یا خودتان از نظر اجتماعی، اقتصادی، دینی و سایر جهات) به عدالت رفتار نکنید، به یک زن (آزاد) یا به آنچه دسترس دارید (مانند مُتعه یا کنیز یا عزوبت) اکتفا کنید (که) این (خودداری) نزدیکتر است به اینکه سنگین‌بار و زیان‌کار نشوید.'),
(497, 4, 4, 'و مهریه‌ی زنان را به عنوان هدیه‌ی زناشویی بدون منّتی به شیرینی به ایشان بدهید. پس اگر به میل خودشان چیزی از آن را به شما واگذاشتند، آن را گوارا بخورید.'),
(498, 4, 5, 'و اموالتان را -که آنها را خدا (وسیله‌ی) قوام و به پا داشتن (زندگی) شما قرار داده- به سفیهان مدهید، و در آن نیازهایشان را تأمین کنید و آنان را پوشاک دهید، و با آنان سخنی پسندیده بگویید.'),
(499, 4, 6, 'و یتیمان را بیازمایید تا هنگامی که به (سن) زناشویی رسند؛ پس اگر در ایشان رشدی (اقتصادی) یافتید، اموالشان را به آنان واگذارید، و اموال آنها را از (بیم) آنکه مبادا به (این) رشد رسند به اسراف و شتاب مخورید. و آن کس که توانگر بوده باید (از گرفتن مزد سرپرستی،) عفاف و خودداری جوید و هر کس تهی‌دست بوده است می‌تواند مطابق عرف شرعی (و به اندازه‌ی ضرورت و تحمل مال یتیم از آن) بخورد. پس هرگاه اموالشان را به آنان رد کردید برایشان گواه بگیرید. و خدا برای حسابگری کافی است.'),
(500, 4, 7, 'برای مردان، (و کلّ ذکور) از (تمامی) آنچه پدران و مادران و خویشاوندان نزدیکترشان بر جای گذاشته‌اند سهمی است و برای زنان (و کل نسوان نیز) از (تمامی) آنچه پدران و مادران و خویشاوندان نزدیکترشان برجای گذاشته‌اند سهمی است - خواه آن (مال) کم باشد یا زیاد - در حالی‌که (برای هر دو) نصیبی مفروض [:قطعی] است.'),
(501, 4, 8, 'و هنگامی که خویشاوندان نزدیکتر(تان‌) که ارث نمی‌برند -و (نیز) یتیمان و مستمندان- در تقسیم حاضر شدند، (چیزی) از آن به ایشان ارزانی دارید و با آنان سخنی پسندیده گویید.'),
(502, 4, 9, 'و آنان که اگر فرزندانی ناتوان (و محروم از ارث همچون این محرومان) از خود بر جای بگذارند، (که) بر (آینده‌ی) آنان بیم دارند، باید (از ستم بر این محرومان نیز) بترسند. پس باید از خدا پروا بدارند و سخنی نگهبان [:بازدارنده از پریشانیشان] برایشان بگویند.'),
(503, 4, 10, 'بی‌گمان کسانی که اموال یتیمان را به ستم می‌خورند، جز این نیست که (آنها) آتشی (است که‌) در شکم‌هاشان می‌خورند، و به زودی آتشی سخت شعله‌ور را فروزان کنند.'),
(504, 4, 11, 'خدا به شما درباره‌ی فرزندانتان (در زندگی و مرگتان) سفارش می‌کند: سهم پسر، چون سهم دو دختر است و اگر همه‌ی وارثان، دخترند (و) از دو تن بیشترند، سهم آنان دو سوم ماترک است و اگر تنها یکی باشد، نیمی از میراث از آنِ اوست، و برای هر یک از پدر و مادرش یک‌ششم از ماترک است، اگر فرزندی داشته باشد. پس اگر فرزندی نداشته و پدر و مادرش از او ارث بردند، برای مادرش یک‌سوم (و برای پدرش همان یک‌ششم) است. پس اگر (مورث) برادران یا خواهرانی داشته باشد، مادرش یک‌ششم می‌برد. (البته) همه‌ی اینها پس از انجام وصیّتی است -که او بدان سفارش می‌کند- یا دِینی (که هر دو باید استثنا شود). شما نمی‌دانید کدام‌یک از پدران و فرزندانتان برایتان سودمندترند، حال آنکه (این) فریضه‌ای است از جانب خدا. همواره خدا بسی دانای حکیم بوده است.'),
(505, 4, 12, 'و نیمی از (کل) میراث همسرانتان از آنِ شما (مردان) است، اگر آنان فرزندی نداشته باشند؛ و اگر فرزندی داشته باشند؛ یک‌چهارم (کل) آن از آنِ شماست، (البته) پس از انجام وصیّتی که بدان سفارش می‌کنند یا دِینی. و یک چهارم از (کل) میراث شما (مردان) برای زنانتان است اگر شما فرزندی نداشته باشید و اگر فرزندی داشته باشید، یک‌هشتم (کل) آن برای آنان است، (البته) پس از (انجام) وصیّتی که بدان سفارش می‌کنید یا دِینی. و اگر مرد یا زنی که (دیگران) از او ارث می‌برند (که نه پدران و مادران و نه فرزندان) باشند و برای او برادر یا خواهری باشد، برای هر یک از آن دو (که ابوینی نیستند) یک‌ششم (ماترک) است و اگر آنان بیش از این باشند، در یک‌سوم (ماترک) سهم دارند، (البته) پس از انجام وصیّتی که به آن سفارش می‌شود یا دِینی؛ حال آنکه در وصیّتش زیانی (به وارثان) نرساند. این است سفارش خدا و خداست که دانا و بردبار است.'),
(506, 4, 13, 'اینها حدود الهی است و هر کس از خدا و پیامبرش اطاعت کند، (خدا) او را به باغ‌هایی در آورد که از زیر (درختان)شان نهرها روان است، حال آنکه در آنها جاودانه‌اند و این (همان) کامیابی بزرگ است.'),
(507, 4, 14, 'و هر کس خدا و پیامبرش را نافرمانی کند و از حدود مقرّر او تجاوز نماید، وی را به آتشی در آورد که در آن خواهد بود، و برای او عذابی اهانت‌‌بار است.'),
(508, 4, 15, 'و از زنانتان، کسانی که مرتکب فحشا(ی جنسی آشکارا) می‌شوند، چهار مرد از میان خودتان بر آنان گواه گیرید. پس اگر شهادت دادند، آن زنان را در خانه‌ها(تان‌) بازداشت کنید، تا مرگشان فرا رسد، یا خدا برای آنان راهی (دیگر) نهد.'),
(509, 4, 16, 'و از میان شما، آن دو تن را که مرتکب زشتکاری (جنسی) می‌شوند، آزارشان دهید؛ پس اگر توبه نمودند و اصلاح کردند از آنان صرف نظر کنید. بی‌گمان خدا بسی برگشت‌کننده‌ی رحمتگر بر ویژگان بوده است.'),
(510, 4, 17, 'توبه (و برگشت‌) بر(عهده‌ی) خدا، تنها برای کسانی است که از روی نادانی مرتکب گناهی شوند، سپس به زودی توبه کنند؛ پس اینانند که خدا به آنان برگشت می‌کند. و خدا بس دانایی فرزانه بوده است.'),
(511, 4, 18, 'و (این) توبه برای کسانی که گناه می‌کنند (و) تا هنگامی که مرگ یکی از ایشان در رسد، می‌گوید: «اکنون توبه کردم‌» (پذیرفته) نیست و (نیز توبه‌ی) کسانی که در حال کفر می‌میرند؛ اینانند که برایشان عذابی دردناک آماده کرده‌ایم.'),
(512, 4, 19, 'هان ای کسانی که ایمان آوردید! برای شما حلال نیست که (خود) زنان (یا اموالشان) را ارث برید و آنان را زیر فشار مگذارید تا بخشی از آنچه را به آنان داده‌اید از ایشان به در آرید، مگر آنکه مرتکب زشتکاری آشکارگری شوند. و با آنان به شایستگی رفتار کنید و (حتی) اگر از آنان خوشتان نیامد. شاید چیزی را خوش نمی‌دارید و خدا در آن خیری فراوان قرار می‌دهد.'),
(513, 4, 20, 'و اگر خواستید همسری (دیگر) به جای همسر (پیشین خود) برگزینید و به یکی از آنان بار شتری طلا داده‌اید، پس چیزی از او باز پس مگیرید. آیا می‌خواهید آن (مال) را به بهتان و گناهی پیگیر و آشکارگر (از آنان) باز پس گیرید؟'),
(514, 4, 21, 'و چگونه آن مال را (از آنان) باز می‌ستانید با آنکه همواره با یکدیگر خلوت کرده‌اید (تنگاتنگ بهره‌ی زناشویی و کام گرفته‌اید و (آنان هم) از شما پیمانی استوار گرفته‌اند؟'),
(515, 4, 22, 'و با زنانی که پدرانتان به ازدواج خود در آورده (و یا آمیزشی به حرام کرده)اند نکاح مکنید؛ مگر آنچه پیشتر رخ داده. این همواره زشتکاری و (مایه‌ی) دشمنی و بد راهی بوده است.'),
(516, 4, 23, '(این زنان در همه‌ی ابعاد زنانگی) بر شما حرام شده‌اند: مادرانتان، دخترانتان، خواهرانتان، عمّه‌هایتان، خاله‌هایتان، دختران برادر، دختران خواهر، مادرهایتان که به شما شیر داده‌اند، خواهران رضاعی شما، مادران زنانتان، و دختران همسرانتان که در دامان شما پرورش یافته‌اند و با آن همسران همبستر شده‌اید؛ پس اگر با آنها همبستر نشده‌اید بر شما گناهی نیست (که دختران یا مادرانشان را نکاح کنید) و زنان پسرانی که از صلب‌های خودتان هستند و جمع میان دو خواهر با همدیگر؛ مگر آنچه که در گذشته رخ داده (که اکنون حرامند) خدا بی‌گمان پوشنده‌ی رحمتگر بر ویژگان بوده است.'),
(517, 4, 24, 'و زنان شوهردار (نیز بر شما حرام شده‌اند) به استثنای زنانی که مالک آنان شده‌اید (و شوهرانشان همچنان در حال کفرند). (این) فریضه‌ی الهی است که بر شما مقرر گردیده و غیر از این (زنان) برای شما حلالند که با اموال خود جویا شوید، در حالی که نگهدارنده‌ی پاکی بوده و زناکار نباشید. پس از زنانی (موقت) که از آنان بهره‌ای شهوانی برده‌اید مهرشان را به عنوان فریضه‌ای به آنان بدهید و بر شما گناهی نیست که پس از تعیین مبلغ مقرّر، با یکدیگر توافق کنید (که مدّت عقد موقت یا مهرشان را کم یا زیاد کنید). همواره خدا بسی دانای حکیم بوده است.'),
(518, 4, 25, 'و هر کس از شما، از نظر مالی (یا حالی) نمی‌تواند زنان (آزاد) پاکدامن باایمان را به همسری (خود) در آورد، پس با دخترانی باایمان که مالک آنان هستید (ازدواج کنید). و خدا به ایمان شما داناتر است. همه‌ی آزادانتان و زرخریدانتان از (جنس و پیوند) یکدیگرید؛ پس آنان را با اجازه‌ی مالکان و خانواده‌شان به همسری (خود) در آورید و مهریه‌هاشان را به طور پسندیده به آنان بدهید در حالی‌که پاکدامن باشند نه زناکاران و نه دوست‌گیران پنهانی. پس چون به ازدواج (شما) در آمدند، اگر مرتکب فحشایی شدند، در این صورت بر آنان نیمی از عذاب [:مجازات] زنان آزاد است. این (پیشنهاد زناشویی با کنیزان) برای کسی از شماست که از تعب عزوبت بیم دارد و صبر کردن برای شما بهتر است. و خدا پوشنده‌ی رحمتگر بر ویژگان است.'),
(519, 4, 26, 'خدا می‌خواهد (حقایق را) برای شما روشن کند و سنت‌های کسانی را که پیش از شما بوده‌اند به شما بنمایاند و بر شما برگردد. و خدا بسی دانای حکیم است.'),
(520, 4, 27, 'و خدا می‌خواهد بر شما بازگردد و کسانی که از خواسته‌ها(ی شهوانیشان) پیروی می‌کنند می‌خواهند شما دستخوش انحرافی بزرگ شوید.'),
(521, 4, 28, 'خدا می‌خواهد (تا بارتان را) از شما سبک گرداند. و انسان ناتوان آفریده شده است.'),
(522, 4, 29, 'هان ای کسانی که ایمان آوردید! اموال همدیگر را به ناروا مخورید مگر آنکه داد و ستدی با تراضی یکدیگر (بر مبنای عقل و شرع)، از شما (انجام) بشود؛ و خودهاتان (و دیگران) را مکشید، زیرا همواره خدا نسبت به شما رحمتگری ویژه بوده است.'),
(523, 4, 30, 'و هر کس از روی تجاوز و ستم چنین کند، به زودی وی را گیرانه‌ی آتشی کنیم، و این کار بر خدا آسان بوده است.'),
(524, 4, 31, 'اگر از گناهان بزرگی که از آنها نهی می‌شوید دوری گزینید، گناهان کوچکتان را از شما می‌زداییم و شما را در جایگاهی ارجمند و پرکرامت در می‌آوریم.'),
(525, 4, 32, 'و (زنهار) آنچه را خدا بعضی از شما را بر بعضی (دیگر) به آن برتری داده آرزو مکنید. برای مردان از آنچه کسب کرده‌اند بهره‌ای و برای زنان (نیز) از آنچه کسب کرده‌اند بهره‌ای است. و از فضل خدا درخواست کنید که خدا به هر چیزی بسی دانا بوده است.'),
(526, 4, 33, 'و از آنچه پدر و مادر و خویشاوندان نزدیکتر(تان‌) و کسانی که شما (با آنان) پیمان بسته‌اید بر جای گذاشته‌اند برای هر یک (از مردان و زنان) وارثانی قرار داده‌ایم‌؛ پس نصیبشان را بدیشان بدهید. همواره خدا بر هر چیزی گواه بوده است.'),
(527, 4, 34, 'مردان بسی پاسداران زنان(شان)اند ، بدین سبب که خدا برخی از ایشان را بر برخی (دیگر) برتری داده و (نیز) به جهت آنکه مردان از اموالشان به زنانشان پرداختند. پس زنان شایسته، فرمانبردارند (و) به پاس آنچه خدا (برای آنان) حفظ کرده، در پنهان نگهبان (حقوق شوهرانشان)اند. و زنانی را که از سرپیچی آنان (در واجبات زناشوییشان) بیم دارید (نخست) پندشان دهید و (سپس) در خوابگاهتان از ایشان دوری گزینید و (اگر تأثیر نکرد) آنان را (از باب نهی از منکر) بزنید، پس اگر از (نهی) شما اطاعت کردند (دیگر) بر آنها هیچ راهی (برای سرزنش) مجویید، که خدا به‌راستی والا و بزرگ بوده است.'),
(528, 4, 35, 'و اگر (شما حاکمان شرع و مانندتان) از جدایی میان آن دو (همسر) بیم دارید، داوری از خانواده‌ی شوهر و داوری از خانواده‌ی زن برانگیزید؛ اگر سر سازگاری دارند، خدا میان آن دو سازش خواهد داد. خدا به‌راستی بسی دانا و آگاه بوده است.'),
(529, 4, 36, 'و خدا را بپرستید و چیزی را با او شریک مگردانید. و به پدر و مادرتان احسان کنید و (نیز) درباره‌ی خویشاوندان نزدیکتر و یتیمان و مستمندان و همسایه‌ی نزدیکتر و همسایه‌ی دور و همنشین نزدیک و در راه مانده و آنان که تحت سرپرستی شمایند (و عهده‌دار زندگیشان هستید احسان کنید). بی‌گمان خدا کسی را که متکبری فخر فروش بوده است دوست نمی‌دارد:'),
(530, 4, 37, '(همان) کسانی که بخل می‌ورزند و مردمان را به بخل فرمان می‌دهند و آنچه را خدا از فضل خویش بدان‌ها ارزانی داشته پوشیده می‌دارند. و برای کافران عذابی خوارکننده آماده کرده‌ایم‌.'),
(531, 4, 38, 'و (نیز) کسانی (هم) که اموالشان را برای خودنمایی برابر دیدگانِ مردمان انفاق می‌کنند و به خدا ایمان نمی‌آورند و نه به روز بازپسین. و هر کس شیطان همدمش باشد، چه بد همدمی است.'),
(532, 4, 39, 'و اگر به خدا و روز بازپسین ایمان می‌آوردند و از آنچه خدا به آنان روزی داده، انفاق می‌کردند، چه (زیانی) بر ایشان داشت‌؟ و خدا به آنان بسی دانا بوده است.'),
(533, 4, 40, 'خدا همواره، (حتی) هم‌وزن ذرّه‌ای (بر هیچ کس و ناکس) ستم نمی‌کند، و اگر (آن ذرّه، کار) نیکی باشد افزونش می‌کند و از نزد خویش پاداشی بزرگ (به نیکوکاران) می‌بخشد.'),
(534, 4, 41, 'پس چگونه است (حالشان) آن هنگام (و هنگامه‌ای) که از هر امّتی گواهی آوریم و تو را بر همه‌ی آنان [:امت‌ها وگواهانشان] گواه آوریم‌؟'),
(535, 4, 42, 'چنان روزی کسانی که کافر شدند و پیامبر (خدا) را نافرمانی کردند، آرزو می‌کنند که ای کاش با زمین یکسان می‌شدند؛ در حالی که از خدا هیچ پدیده‌ای را پوشیده نمی‌دارند.'),
(536, 4, 43, 'هان ای کسانی که ایمان آوردید! در حال مستی به نماز نزدیک مشوید تا زمانی که بدانید چه می‌گویید و (نیز) در حال جنابت (وارد نماز یا مسجد نشوید) - مگر اینکه رهگذر (مسجد) باشید - تا غسل کنید و اگر بیمار یا در سفرید یا یکی از شما از قضای حاجت آمد، یا با زنان آمیزش (جنسی) کرده‌اید، پس آبی نیافتید، بلندایی پاکیزه را جستجو کنید و بخشی از صورت‌ها و دست‌هایتان را (با آن) مسح نمایید. خدا بی‌گمان بخشنده و پوشنده بوده است.'),
(537, 4, 44, 'آیا سوی کسانی که بهره‌ای از کتاب یافته‌اند ننگریستی‌؟ گمراهی را می‌خرند و می‌خواهند شما (نیز) گمراه شوید.'),
(538, 4, 45, 'و خدا به (حال) دشمنانتان داناتر است. و کافی است که خدا سرپرست (شما) باشد و کافی است که خدا یاور (شما) باشد.'),
(539, 4, 46, 'برخی از آنان که یهودی شدند، کلمات (وحی) را از جاهای خود بر می‌گردانند، و با گرداندن زبان‌هاشان و به قصد طعنه زدن در دین (اسلام، با در آمیختن عبری به عربی، به جای اینکه بگویند شنیدیم و اطاعت کردیم) می‌گویند: «شنیدیم و نافرمانی کردیم و (از ما) بشنو بدون شنواییمان.» و (نیز از روی استهزا می‌گویند:) «راعنا.» و اگر آنان می‌گفتند: «شنیدیم و فرمان بردیم و بشنو و به ما بنگر.» بی‌گمان برای آنان بهتر و پایدارتر بود، ولی خدا آنان را به علّت کفرشان لعنت کرد و در نتیجه جز اندکی ایمان نمی‌آورند.'),
(540, 4, 47, 'هان ای کسانی که کتاب داده شدید! به آنچه فرو فرستادیم - حال آنکه تصدیق‌کننده‌ی همان چیزی است که با شماست - ایمان بیاورید، پیش از آنکه چهره‌هایی را محو کنیم و در نتیجه آنها را به قهقرا بازگردانیم، یا همچنان که اصحاب سبت [:شنبه] را لعنت کردیم، آنان را (نیز) لعنت کنیم. و فرمان خدا همواره تحقّق یافته بوده است.'),
(541, 4, 48, 'خدا، این (انحراف) را - که به او شرک ورزیده شود - هرگز نمی‌پوشاند و غیر از آن را برای هر که بخواهد می‌پوشاند و هر کس به خدا شرک ورزد، همواره گناهی با پی‌آمدی بزرگ (نسبت به خدا) بر بافته است.'),
(542, 4, 49, 'آیا سوی کسانی که خویشتن را پاک می‌شمارند ننگریسته‌ای‌؟ (چنین نیست،) بلکه خداست که هر که را بخواهد پاک می‌گرداند و به قدر نَخَک روی هسته‌ی خرمایی هم (به کس و ناکس) ستم نمی‌بینند.'),
(543, 4, 50, 'بنگر چگونه بر خدا دروغ می‌بندند و این (خود) بس است که گناهی بدعاقبت (و) آشکارگر است.'),
(544, 4, 51, 'آیا سوی کسانی که از کتاب (آسمانی) نصیبی یافته‌اند ننگریسته‌ای‌؟ که به (دو بُت) «جِبْت‌» و «طاغوت‌» ایمان می‌آورند و درباره‌ی کسانی که کفر ورزیده‌اند می‌گویند: «اینان از کسانی که ایمان آورده‌اند راه‌یافته‌ترند.»'),
(545, 4, 52, 'اینانند که خدا لعنتشان کرده و هر که را خدا لعنت کند، هرگز برای او یاوری نخواهی یافت.'),
(546, 4, 53, 'یا اینان را نصیبی از حکومت (ربانی‌) است‌؟ پس در این هنگام به قدر نقطه‌ی پشت هسته‌ی خرمایی (هم از آن) به مردمان نمی‌دهند.'),
(547, 4, 54, 'یا به مردمان بر آنچه خدا از فضل خویش به آنان عطا کرده، رشک می‌برند؟ پس ما به‌راستی به خاندان ویژه‌ی ابراهیم، کتاب و حکمت دادیم و به آنان مُلکی بزرگ دادیم.'),
(548, 4, 55, 'پس برخی از ایشان کسانی‌اند که به وی ایمان آوردند و برخی از آنان کسانی‌اند که (بر ضد ایمان قیام و) از آن جلوگیری کردند و (برای آنان) دوزخ پرشراره بس است.'),
(549, 4, 56, 'به زودی کسانی که به آیات ما کفر ورزیدند، آنان را گیرانه‌ی آتشی خواهیم کرد (که) هر چه پوست‌هاشان [:بدن‌هاشان] بریان گردد، تبدیل به پوست‌های [:بدن‌های] دیگرشان می‌کنیم، تا عذاب را بچشند. آری، خدا عزیز حکیم بوده است.'),
(550, 4, 57, 'و کسانی را که ایمان آورده و کارهای شایسته کرده‌اند، به زودی در باغ‌هایی در می‌آوریم که (از) زیر (درختان‌)شان نهرها روان است، حال آنکه در آنها جاودانند و برایشان همسرانی بس پاک است و آنان را در سایه‌ای بس پوشا درآوریم.'),
(551, 4, 58, 'خدا به‌راستی شما را فرمان می‌دهد که امانت‌ها را به صاحبانشان بازگردانید. و هنگامی که میان مردم داوری می‌کنید، به عدالت داوری کنید. همواره چه نیکوست چیزی که خدا به آن پند می‌دهد. به‌راستی خدا بسی شنوای بینا بوده است.'),
(552, 4, 59, 'هان ای کسانی‌که ایمان آوردید! خدا را اطاعت کنید، و پیامبر و اولیای امر (رسالت) را -که از خودتان می‌باشند- (نیز) اطاعت کنید. پس اگر در امری (دینی) اختلاف کردید، اگر به خدا و روز پایانی ایمان دارید، آن را به (کتاب) خدا و (سنت) پیامبرش عرضه بدارید، این بهتر و بازتابش نیکوتر است.'),
(553, 4, 60, 'آیا نظر نیفکنده‌ای سوی کسانی که می‌پندارند به آنچه سوی تو نازل شده و (به) آنچه پیش از تو نازل گردیده ایمان آورنده‌اند (و با این همه) می‌خواهند داوریِ میان خود را سوی طاغوت ببرند، حال آنکه بی‌چون فرمان یافته‌اند که بدان کفر ورزند؟ و (اما) شیطان همی خواهد آنان را به گمراهی دوری در اندازد.'),
(554, 4, 61, 'و هنگامی که به ایشان گفته شود: «سوی آنچه خدا نازل کرده [:قرآن] و سوی پیامبرش [:سنت وحیانیش] بیایید»، منافقان را می‌بینی که (مکلفان را) از (راه و روش) تو واژگونه باز می‌دارند.'),
(555, 4, 62, 'پس چگونه است هنگامی که به (سزای) دستاوردهایشان - که از پیش فرستادند - مصیبتی به آنان در رسد، نزد تو می‌آیند و به خدا سوگند یاد می‌کنند که: «ما جز نیکویی و کارسازی قصدی نداشتیم‌»؟'),
(556, 4, 63, 'ایشان همان کسانند که خدا می‌داند چه در دل‌هاشان دارند؛ پس از آنان روی بگردان و پندشان ده و برایشان سخنی در (ژرفای) جان‌هاشان برگوی.'),
(557, 4, 64, 'و ما هیچ پیامبری را نفرستادیم مگر برای آنکه به اذن خدا از او اطاعت شود. و اگر آنان چون به خود ستم کردند، پیش تو آیند، پس از خدا پوشش و آمرزش بخواهند و پیامبر (نیز) برای آنان (از خدا) پوشش بخواهد، همواره خدا را بسی برگشت‌کننده‌ی (بر خودهاشان و) رحمتگر بر ویژگان می‌یافتند.'),
(558, 4, 65, 'پس نه، (چنان نیست‌.) به پروردگارت قسم که ایمان نمی‌آورند، تا آنکه تو را در آنچه میانشان مایه‌ی مشاجره است به داوری برگمارند؛ سپس از حکمی که کرده‌ای در دل‌هاشان احساس تنگی (و تردید) نکنند و کاملاً سر تسلیم فرود آورند.'),
(559, 4, 66, 'و اگر بر آنان می‌نوشتیم که خودهاتان (همدیگر) را بکشید، یا از خانه‌هاتان به درآیید، جز اندکی از ایشان آن را به کار نمی‌بستند و اگر آنان آنچه را بدان پند داده می‌شوند به کار می‌بستند، بی‌گمان برایشان بهتر و در ثبات (قدم‌)شان مؤثرتر بود.'),
(560, 4, 67, 'و در آن هنگام (ما هم) از نزد خویش، بی‌چون پاداشی بزرگ به آنان می‌دادیم،'),
(561, 4, 68, 'و به‌راستی آنان را به راهی راست هدایت می‌کردیم.'),
(562, 4, 69, 'و کسانی که از خدا و پیامبرش اطاعت کنند، ایشان با کسانی هستند که خدا بر (سر و سامان)شان نعمت فرود آورده: از پیامبران بزرگ و راستان و شهیدان و شایستگان. و آنان چه نیکو همدمانند.'),
(563, 4, 70, 'این فضیلت از خداست و خدا در دانایی بسنده است.'),
(564, 4, 71, 'هان ای کسانی که ایمان آوردید! وسایل دفاعی خود را (برابر دشمنانتان) برگیرید (و آماده‌ی دفاع باشید). پس گروه گروه بر دشمن هجوم کنید یا یکجا بر آنها هجوم آورید.'),
(565, 4, 72, 'و بی‌گمان از میان شما کسی است که بی‌امان بسی کندی و لاابالی‌گری می‌کند. پس اگر آسیبی به شما در رسد گوید: «به‌راستی خدا بر من نعمت بخشید، چون با آنان حاضر (در معرکه‌ی جنگ) نبودم.»'),
(566, 4, 73, 'و اگر به‌راستی فضیلتی [:غنیمت یا غلبه‌ای] از خدا به شما در رسد – چنان که گویی میان شما و میان او دوستی نبوده - همانا بی‌گمان خواهند گفت: «کاش من با آنان بودم و بی هیچ زیانی به نوای بزرگی می‌رسیدم!»'),
(567, 4, 74, 'پس باید کسانی که زندگی دنیا را به (زندگی) آخرت سودا می‌کنند در راه خدا کشتار کنند و هر کس در راه خدا کشتار کند، پس کشته یا پیروز شود، در آینده پاداشی بزرگ به او خواهیم داد.'),
(568, 4, 75, 'و چیست شما را (که) در راه خدا و (در راه نجات) مستضعفان (اعم) از زنان و مردان و فرزندان کوچکتان، کشتار نمی‌کنید؟ همانان که می‌گویند: «پروردگارمان! ما را از این شهری که مردمش ستم‌پیشه‌اند، بیرون بر و از جانب خود برایمان سرپرستی قرار ده و از نزد خویش یاوری برایمان تعیین فرما.»'),
(569, 4, 76, 'کسانی که ایمان آوردند، در راه خدا کشتار می‌کنند و کسانی که کافر شدند، در راه طغیانگران کشتار می‌کنند. پس با یاران و پیروان شیطان کشتار کنید (که) نیرنگ شیطان بی‌گمان ضعیف بوده است.'),
(570, 4, 77, 'آیا ندیدی کسانی را که به آنان گفته شد: «دست (از جنگ) بدارید و نماز را بر پا دارید و زکات را بدهید.» پس چون کشتار بر آنان نوشته شد، ناگاه گروهی از آنان از مردمان می‌ترسند - مانند ترس از خدا یا ترسی سخت‌تر - و گفتند: «پروردگارمان! چرا بر ما آن کشتار را برنبشتی‌؟ چرا تا مدتی کوتاه مهلتمان ندادی‌؟» بگو: «برخورداری(‌تان از) دنیا کم (است)، و برای کسی که تقوا پیشه کرده، آخرت بهتر است و (در آنجا) به قدر نَخَک هسته‌ی خرمایی (هم) بر شما ستم نخواهد رفت.»'),
(571, 4, 78, 'هر کجا باشید، شما را مرگ در می‌یابد، هر چند در برج‌های استواری باشید. و اگر (پیشامد) خوبی به آنان در رسد، گویند: «این از جانب خداست‌» و چون صدمه‌ای به ایشان رسد، گویند: «این از جانب تو است.» بگو: «همه از جانب خداست.» (آخر) این گروه را چه شده است که نزدیک (به این حقیقت) نیستند، (که) سخنی یا هر حادثه‌ای را با دقت بررسند؟'),
(572, 4, 79, 'هر چه از خوبی به تو رسد از خداست و آنچه از بدی به تو رسد، از تو است. و تو [:محمد] را به پیامبری، برای مردم فرستادیم و گواه بودن خدا کافی است.'),
(573, 4, 80, 'هر که از پیامبر فرمان برد، بی‌گمان، از خدا فرمان برده و هر کس (از او) رویگردان شود، ما تو را بر ایشان به نگهبانی نفرستاده‌ایم.'),
(574, 4, 81, 'و می‌گویند: «اطاعت (می‌کنیم).» سپس هنگامی که از نزد تو بیرون می‌روند، گروهی از آنان شبانه و پنهانی، غیر از آنچه تو می‌گویی برنامه‌ریزی می‌کنند، و خدا آنچه را که پنهانی در شب برنامه‌ریزی می‌کنند ثبت می‌کند. پس از ایشان روی برتاب و بر خدا توکل کن و کارسازی خدا بس است.'),
(575, 4, 82, 'پس آیا در قرآن تدبر نمی‌کنند و اگر (بر فرض محال، قرآن) از نزد غیر خدا بود همواره در آن اختلاف بسیاری می‌یافتند.'),
(576, 4, 83, 'و هنگامی که خبری (حاکی) از ایمنی یا ترس، آنان را در رسد، (آشکارا و بلندگویان) انتشارش دهند و اگر آن را به پیامبر و به فرماندهان جنگی از خودشان ارجاع کنند، همانا آن (امر) را کسانی از میانشان در می‌یابند که آن را عالمانه ژرف‌کاوی می‌کنند. و اگر فضل و رحمت خدا بر شما نبود، همواره جز اندکی، (همگی) از شیطان پیروی می‌کردید.'),
(577, 4, 84, 'پس در راه خدا کشتار کن -تو جز شخص خود را مکلف نیستی- و مؤمنان را (به مبارزه) برانگیز. امید که خدا آسیب کسانی را که کفر ورزیدند (از شما مؤمنان) باز دارد و خداست که برخوردش شدیدتر و کیفرش سخت‌تر است.'),
(578, 4, 85, 'هر کس میانجیگری پسندیده‌ای کند، برای وی از آن نصیبی خواهد بود و هر کس میانجیگری ناپسندی کند، برای او از آن (بخشی) خواهد بود. و خدا همواره بر هر چیزی توانا و گواه و نگهبان بوده است.'),
(579, 4, 86, 'و چون به شما زنده‌باد داده شد [:گفته شد]، شما (هم) بهتر از آن یا (مانند) همان را پاسخ دهید. خدا همواره بر(ای) هر چیزی بسی حسابرس بوده است.'),
(580, 4, 87, 'خدا، (که) هیچ معبودی جز او نیست، همواره در روز رستاخیز - که هیچ شک مستندی در آن نیست - همانا شما را گرد خواهد آورد. و کیست راستاتر از خدا در هر گفتاری.'),
(581, 4, 88, 'پس چیست شما را در حال دودستگیِ منافقان، حال آنکه خدا آنان را در گمراهی وانهاده. آیا می‌خواهید کسی را که خدا(یش) گمراه کرده به راه آورید؟ و هر که را خدا در گمراهیش وانهد هرگز راهی (راهوار) برایش نخواهی یافت.'),
(582, 4, 89, 'همان‌گونه که خودشان کافر شدند، دوست داشتند (که) ای کاش (شما نیز) کافر شوید، تا با هم برابر باشید. پس از میان ایشان برای خودتان، (نه) دوستان و (نه) یارانی اختیار مکنید، تا آنکه در راه خدا هجرت کنند. پس اگر (باز هم) روی بر تافتند، هر کجا آنان را یافتید بگیریدشان و بکشیدشان و از آنها نه دوستی و نه یاری برای خود مگیرید.'),
(583, 4, 90, 'مگر کسانی را که با گروهی که میان شما و آنان پیمانی است، به ایشان برسند، یا نزد شما بیایند، در حالی که سینه‌‌هاشان از کشتار با شما یا کشتار با قومشان به تنگ آمده و اگر خدا می‌خواست، همانا آنان را بر شما چیره می‌کرد، (و آنان) حتماً با شما کشتار می‌کردند. پس اگر از (برخورد با) شما کناره‌گیری کردند و با شما کشتار نکردند و سوی شما (طرح) تسلیم افکندند، (دیگر) خدا برای شما هیچ راهی (برای تجاوز) بر آنان قرار نداده است.'),
(584, 4, 91, 'به زودی، گروهی دیگر را خواهید یافت که می‌خواهند شما را آسوده خاطر کنند و قوم خود را (نیز) امنیت دهند. هر بار که به فتنه بازگردانده شوند، در (ژرفای) آن سرنگون می‌گردند. پس اگر از شما کناره‌گیری نکردند و سویتان تسلیم(شان) را نیفکندند و از شما دست بر نداشتند، هر کجا با پی‌جویی آنان را یافتید، دستگیرشان کرده و بکشیدشان و اینانند که ما برای شما بر ایشان تسلّطی روشنگر قرار داده‌ایم.'),
(585, 4, 92, 'و برای هیچ مؤمنی هرگز (شایسته) نبوده است که مؤمنی را - جز به اشتباه (مطلق ناخودآگاه)- بکشد و هر کس مؤمنی را به اشتباه کشت، باید مؤمن (گرفتار و) دربندی را آزاد کرده و به خانواده‌اش خون‌‌بهایی را (هم) پرداخت کند، مگر اینکه آنان (خون‌بهایش را) صدقه دهند [:ببخشند]. پس اگر (مقتول) از گروهی است که دشمنان شمایند و خود وی مؤمن است، (قاتل) باید به خانواده‌ی وی خون‌‌بهایی تسلیم کند و مؤمنی دربند را (هم) آزاد نماید و هر کس از اینها چیزی نیافت، باید دو ماه پیاپی - به عنوان بازگشتی از جانب خدا - روزه بدارد. و خدا همواره دانای سنجیده‌کار بوده است.'),
(586, 4, 93, 'و هر کس به‌عمد مؤمنی را بکشد، کیفرش دوزخ است حال آنکه در آن ماندگار خواهد بود و خدا بر او خشم می‌گیرد و لعنتش می‌کند و عذابی بزرگ برایش آماده ساخته است.'),
(587, 4, 94, 'هان ای کسانی که ایمان آوردید! چون در راه خدا به دشواری رهسپار شوید، کوشش و کاوش کنید و به کسی که (سویتان) تسلیم افکند [:ادعای تسلیم و یا اسلام کرد] مگویید: «تو مؤمن نیستی‌» در حالی که متاع زندگی دنیا را می‌جویید. چرا که غنیمت‌های فراوان نزد خداست. قبلاً خودتان (نیز) همین‌گونه بودید، تا خدا بر شما منّت نهاد. پس خوب بررسی کنید، که خدا همواره به آنچه انجام می‌دهید آگاه بوده است.'),
(588, 4, 95, 'مؤمنان خانه‌نشین که با ترک جبهه‌ی جنگ زیانی وارد نمی‌کنند و عذری هم ندارند، با مجاهدانی که با مال و جانشان در راه خدا جهاد می‌کنند برابر نیستند. خدا کسانی را که با مال و جانشان جهاد می‌کنند به درجه‌ای بر (این) خانه‌نشینان مزیّتی بخشیده و هر دو (گروه‌) را وعده‌ی نیکوترین (پاداش) داده و خدا مجاهدان را بر خانه‌نشینان به پاداشی بزرگ برتری داده است.'),
(589, 4, 96, 'حال آنکه (این درجه‌ی جهاد بر حسب درجات مجاهدان به عنوان) درجات و پوشش و رحمتی از جانب اوست (و نصیب آنان می‌شود). و خدا پوشنده‌ی رحمتگر بر ویژگان بوده است.'),
(590, 4, 97, 'بی‌گمان کسانی که بر خود ستمکارند (هنگامی که) فرشتگان جانشان را گرفتند (به آنان) گفتند: «در چه (حال) بودید؟» پاسخ دادند: «ما در زمین از مستضعفان بودیم.» گفتند: «مگر زمین خدا وسیع نبود تا در آن مهاجرت کنید؟» پس آنان جایگاهشان دوزخ است و (دوزخ) چه بد بازگشتگاهی است.'),
(591, 4, 98, 'مگر مستضعفان از مردان و زنان و کودکانی که چاره‌جویی نتوانند و راهی (نیز) نیابند.'),
(592, 4, 99, 'پس ایشان، امید است خدا از آنان درگذرد و خدا همواره خطا بخش و پوشنده بوده است.'),
(593, 4, 100, 'و هر که در راه خدا هجرت کند، در زمین (و زمینه‌ی هجرتش) جایگاهی آرام و بی‌خطر (و) فراوانی و گشایشی خواهد یافت و هر کس به حالت مهاجرت در راه خدا و پیامبرش از خانه‌اش به درآید، سپس مرگش در رسد، همانا پاداش او همواره بر خداست. و خدا بسی پوشنده‌ی رحمتگر بر ویژگان بوده است.'),
(594, 4, 101, 'و چون در زمین (به زحمت پای) زدید [:رهسپار جهاد شدید]، اگر بیم داشتید آنان که کافر شدند بر شما فتنه‌ای آتشین (بر پا) کنند (که بر شما هجوم آورند)، پس گناهی بر شما نیست که از (کیفیت) نماز بکاهید. کافران بی‌گمان پیوسته برای شما دشمنانی آشکارگر بوده‌اند.'),
(595, 4, 102, 'و هنگامی که در میانشان [:این مجاهدان] بودی پس برایشان نماز برپا داشتی، باید گروهی از آنان با تو (به نماز) بایستند و باید جنگ‌افزارهای خود را در بر گیرند. پس چون به سجده(ی نخستین) رفتند (برای اتمام نمازشان و مراقبت) باید پشت سر شما قرار گیرند و گروهی دیگر که هنوز نماز نگزارده‌اند باید بیایند و با تو (در ادامه‌ی جماعت) نماز گزارند و باید (حالت) بیمشان و جنگ‌افزارهایشان را (با خود) برگیرند. کسانی که کافر شدند دوست داشتند که ای کاش شما از جنگ‌افزارهایتان و (از) متاع‌هایتان غافل شوید، پس یکجا بر شما هجوم آورند. و هیچ گناهی بر شما نیست (که) اگر آزاری از بارانی شما را در رسید یا بیمار بودید، جنگ‌افزارهایتان را واگذارید و (همواره از ایشان) بر حذر باشید. بی‌گمان، خدا برای کافران عذابی خفّت‌بار آماده کرده است.'),
(596, 4, 103, 'پس چون نماز را (این‌گونه) به جای آوردید، خدا را (در همه حال) ایستاده و نشسته و بر پهلو آرمیده، یاد کنید. پس چون آرام شدید، نماز را (به طور کامل و بدون کم و کاست) به پا دارید، زیرا نماز بر مؤمنان، در اوقات معیّن مقرّر بوده است.'),
(597, 4, 104, 'و در پی‌جویی مجدانه‌ی این دشمنان سستی نورزید. اگر شما درد می‌کشید، آنان (نیز) همان گونه که شما درد می‌کشید، درد می‌کشند؛ حال آنکه شما چیزی از خدا امید دارید که آنها امید ندارند. و خدا همواره بس دانای فرزانه بوده است.'),
(598, 4, 105, 'ما به‌راستی این کتاب را به کل حقّ (و حقانیت) بر تو نازل کردیم، تا میان مردمان به (موجب) آنچه خدا به تو نشان داده داوری کنی و زنهار که برای خیانتکاران جانب‌دار مباش.'),
(599, 4, 106, 'و از خدا پوشش بخواه، (که) همواره خدا بسی پوشنده‌ی رحمتگر بر ویژگان بوده است.'),
(600, 4, 107, 'و از کسانی که به خودشان خیانت می‌کنند دفاع مکن. بی‌گمان خدا هر که را که خیانتکار و گناه‌پیشه بوده دوست نمی‌دارد.'),
(601, 4, 108, '(کار و اسرار خود را) از مردم بسی پنهان می‌دارند و از خدا هیچ پنهان نمی‌دارند. و چون شبان‌گاه به چاره‌اندیشی می‌پردازند سخنانی می‌گویند که خدا (بدان‌ها) خشنود نیست. حال آنکه خدا (همچنان) با آنان است (و) خدا به آنچه انجام می‌دهند همواره احاطه داشته است.'),
(602, 4, 109, 'هان! شما همانان هستید که در زندگی دنیا(تان) از اینان جانبداری کردید. پس چه کس به روز رستاخیز از آنان در برابر خدا جانبداری خواهد کرد؟ یا چه کسی حمایتگر و کارگزار آنان خواهد بود؟'),
(603, 4, 110, 'و هر کس بدی‌ای کند، یا بر خویشتن ستمی روا دارد، سپس از خدا پوشش بخواهد، خدا را پوشنده‌ی رحمتگر بر ویژگان خواهد یافت.'),
(604, 4, 111, 'و هر کس خطایی مرتکب شود، آن را به زیان خود مرتکب شده و خدا همواره بس دانای فرزانه بوده است.'),
(605, 4, 112, 'و هر کس خطا یا گناهی -مانع از خیری- به دست آرد، سپس آن را به بی‌گناهی نسبت دهد، پس بی‌گمان بهتان و گناه دنباله‌دار آشکارگری بر دوش کشیده است.'),
(606, 4, 113, 'و اگر فضل و رحمت خدا بر (سر و سامان) تو نبود، گروهی از آنان همت می‌گماردند تا گمراهت کنند و بجز خودشان را گمراه نمی‌کنند و به تو هیچ زیانی نمی‌رسانند. و خدا بر تو کتاب و حکمت فرو فرستاد و آنچه نمی‌توانسته‌ای بدانی به تو آموخت و فزون‌بخشی خدا بر تو، با عظمت (و بزرگوارانه‌) بوده است.'),
(607, 4, 114, 'در بسیاری از رازگویی‌هایشان هرگز خیری نیست، مگر کسی که (بدین وسیله) به صدقه یا کار پسندیده یا سازشی میان مردمان، فرمان دهد. و هر کس برای طلب خشنودی خدا چنان کند، در آینده‌ای [:رجعت، برزخ و قیامت] او را پاداش بزرگی خواهیم داد.'),
(608, 4, 115, 'و هر کس - پس از آنکه راه هدایت برایش آشکار شد - با پیامبر اختلاف کند (و میان خود و پیامبر جدایی افکند) و (راهی) جز راه مؤمنان را (که همان راه پیامبر سوی خداست) در پیش گیرد، او را بدانچه روی خود را بدان سو کرده واگذاریم و گیرانه‌ی جهنمش کنیم و چه بازگشت‌گاه بدی است.'),
(609, 4, 116, 'خدا از اینکه به او شرک آورده شود، هرگز در نمی‌گذرد و فروتر از آن را بر هر که بخواهد (با شروطش) می‌پوشاند و هر کس به خدا شرک ورزد، بی‌چون دچار گمراهی دور و درازی شده است.'),
(610, 4, 117, '(مشرکان) به جای او، جز مادینه‌ای را نمی‌خوانند، و جز شیطانی سرکش را نمی(خواهند و نمی)خوانند.'),
(611, 4, 118, 'خدا لعنتش کرده حال آنکه گفت: «بی‌گمان، از میان بندگانت همواره نصیبی جداسازی شده (برای خود) بر خواهم گرفت‌؛»'),
(612, 4, 119, '«و همانا آنان را بی‌گمان سخت گمراه و دچار آرزوهای دور و درازی خواهم کرد و همواره وادارشان می‌کنم تا بی‌چون گوش‌های دام‌ها را بشکافند و (نیز) وادارشان می‌کنم تا خَلْق‌الله [:فطرت خداداد] را دگرگون سازند.» و هر کس به جای خدا، شیطان را ولیّ خود برگیرد، بی‌گمان دستخوش زیان آشکارگری شده است.'),
(613, 4, 120, '(آری،) شیطان به آنان وعده می‌دهد و ایشان را در (ژرفای) آرزوها می‌افکند و شیطان جز فریبی به آنان وعده نمی‌دهد.'),
(614, 4, 121, 'آنان پناهگاهشان جهنّم است و از آن راه گریزی نیابند.'),
(615, 4, 122, 'و کسانی که ایمان آورده و کارهای شایسته(‌ی ایمان) کردند، در آینده‌ای نزدیک، آنان را در بوستان‌هایی که از زیر (درختان)شان، نهرها روان است در می‌آوریم، حال آنکه در آن جاودانه‌اند. وعده‌ی حقانی خدا را بپذیرید و چه کسی در سخن، از خدا راستگوتر است‌؟'),
(616, 4, 123, '(پاداش و کیفر، هرگز) به دلخواه و آرزوی شما و نه به دلخواه و آرزوی اهل کتاب نیست. هر کس بدی کند، در برابر آن کیفر بیند و جز خدا برای خود نه سرپرستی و نه مددکاری نمی‌یابد.'),
(617, 4, 124, 'هر کس که کارهایی شایسته کند - مرد باشد یا زن - در حالی که مؤمن است، ایشان داخل بهشت می‌شوند و به اندازه‌ی گودی پشت هسته‌ی خرمایی (هم) ستم نمی‌شوند.'),
(618, 4, 125, 'و دین چه کسی بهتر است از آن کس که خود را تسلیم خدا کرده، در حالی که نیکوکار است و از آیین ابراهیم (که) از هر باطلی رویگردان بوده پیروی نموده است‌؟ و خدا ابراهیم را دوستی بس تنگاتنگ گرفت (که گویی سراسر وجودش در خدا ادغام گردیده است).'),
(619, 4, 126, 'و آنچه در آسمان و آنچه در زمین است تنها از خداست و خدا همواره بر هر چیزی احاطه داشته است.'),
(620, 4, 127, 'و درباره‌ی زنان، رأی تازه‌ی تو را می‌پرسند. بگو: «خدا درباره‌ی آنان به شما فتوا می‌دهد و (نیز) درباره‌ی آنچه در کتاب [:قرآن] بر شما تلاوت می‌شود: در مورد زنان یتیمی که حق مکتوب و نگاشته شده برایشان را به ایشان نمی‌دهید و تمایل به ازدواج با آنان دارید و (نیز درباره‌ی) کودکان ناتوان و اینکه با یتیمان (باید) به فضیلتی برتر از عدالت قیام کنید. و هر کار نیکی (را که) انجام دهید همواره خدا به آن بسی دانا بوده است.'),
(621, 4, 128, 'و اگر زنی از ناسازگاری همسرش یا از روی گردانیدنش (از زندگی زناشویی) بیم داشت، بر آن دو هرگز گناهی نیست که از راه صلح با یکدیگر (در آیند و) به آشتی گرایند و (این) سازش خیر (و استمرار ناسازگاری، شرّ) است. و بخل (و بی‌گذشت بودن) در نفوس، حضور (و غلبه) یافته و اگر نیکی کنید و پرهیزگاری پیشه نمایید، همواره خدا به آنچه انجام می‌دهید بسی آگاه بوده است.'),
(622, 4, 129, 'و شما هرگز نمی‌توانید میان زنان عدالت (همه‌جانبه) برقرار کنید، هر چند (بر این عدالت) حریص (هم) باشید! پس (از آنان) یکسره تمایل (و تغافل) نورزید، تا آنان را همچون آویزان در این میان [:بلاتکلیف و سرگشته] رها کنید و اگر سازش نمایید و (از این تمایل ناروا) بپرهیزید، (بدانید که) همواره خدا پوشنده‌ی رحمتگر بر ویژگان بوده است.'),
(623, 4, 130, 'و اگر آن دو همسر، از یکدیگر جدا شوند، خدا هر یک را از گشایش خویش بی‌نیاز می‌گرداند. و خدا همواره گشایشگر حکیم بوده است.'),
(624, 4, 131, 'و آنچه در آسمان‌ها و آنچه در زمین است تنها از خداست و ما همواره به کسانی که پیش از شما (به آنها) کتاب داده شده و (نیز) به شما به‌راستی سفارش کردیم که از خدا پروا کنید و اگر کفر ورزید (چه باک‌؟) آنچه در آسمان‌ها و آنچه در زمین است از خداست و خدا بی‌نیاز ستوده بوده است.'),
(625, 4, 132, 'و آنچه در آسمان‌ها و آنچه در زمین است از خداست و وکالت [:کارسازی] خدا کافی است.'),
(626, 4, 133, '(ای مردمان!) اگر (خدا) بخواهد، شما را (از میان) می‌برد و دیگرانی را به جای شما (پدید) می‌آورد و خدا بر این (کار) توانا بوده است.'),
(627, 4, 134, 'هر کس پاداش دنیا می‌خواسته، پاداش دنیا و آخرت تنها نزد خداست. و خدا بس شنوایی بسیار بینا بوده است.'),
(628, 4, 135, 'هان ای کسانی که ایمان آوردید! پیوسته -و بسی شایسته و بایسته- در قسط [:تقسیم عادلانه] بسیار پایدار باشید و برای خدا گواهی دهید، هر چند به زیان خودتان یا (به زیان) پدر و مادر(تان) و خویشاوندان نزدیکتر(تان) باشد. اگر (یکی از دو طرف دعوا) توانگر یا نیازمند باشد، خدا به آن دو (از شما) سزاوارتر است. پس از هوس پیروی نکنید، که (در نتیجه از حق) عدول کنید و اگر به انحراف گرایید یا (از حق) اعراض کنید، خدا همواره به آنچه انجام می‌دهید آگاه بوده است.'),
(629, 4, 136, 'هان ای کسانی که ایمان آوردید! به خدا و پیامبرش و کتابی که بر پیامبرش به تدریج فرو فرستاد و کتاب‌هایی که قبلاً نازل کرده، بگروید و هر کس به خدا و فرشتگانش و کتاب‌ها و پیامبرانش و روز بازپسین کافر شود، در حقیقت دچار گمراهی دور و درازی شده است.'),
(630, 4, 137, 'بی‌گمان کسانی که ایمان آوردند، سپس کافر شدند، سپس ایمان آوردند، سپس کافر شدند، پس آن‌گاه به کفر خود افزودند، هرگز خدا بر آن نبوده است که آنان را ببخشاید و نه به راهی راهوار هدایتشان کند.'),
(631, 4, 138, 'منافقان را نوید ده که برایشان عذابی دردناک است.'),
(632, 4, 139, 'همانان که غیر از مؤمنان، کافران را اولیای خود می‌گیرند. آیا سربلندی را نزد آنان همی جویند؟ (این خیالی خام است.) پس همواره عزّت، همه از آن خداست.'),
(633, 4, 140, 'و همانا (خدا) در (آن) کتاب [:قرآن] بر شما نازل کرد که: هرگاه شنیدید آیات خدا مورد کفر (و انکار) و مسخره قرار می‌گیرد، با آنان منشینید، تا به سخنی غیر از آن فرو روند (و در صورت تخلف شما بی‌گمان مانند آنان خواهید بود. خدا بی‌چون همگیِ منافقان و کافران را در دوزخ گرد آورنده است.'),
(634, 4, 141, 'آنان که در انتظاری (ناروا و بی‌پروا) بر (زیان‌) شمایند؛ پس اگر از جانب خدا به شما فتحی در رسد، گویند: «مگر ما با شما نبودیم‌؟» و اگر برای کافران نصیبی باشد، گویند: «مگر ما بر (سروسامانتان) نگهبانی نمی‌کردیم و از تهاجم مؤمنان بر شما جلوگیری نمی‌نمودیم‌؟» پس خدا، روز قیامت میانتان داوری می‌کند و خدا هرگز بر (زیان) مؤمنان، برای (سود) کافران راهی راهوار قرار نداده است.'),
(635, 4, 142, 'منافقان همواره به خدا نیرنگ می‌زنند، حال آنکه او (هم) به آنان نیرنگ‌زننده است. و چون به نماز ایستند با کسالت برخیزند، در حالی‌که برابر مردم ریا می‌کنند و خدا را جز اندکی یاد نمی‌کنند.'),
(636, 4, 143, 'میان آن دو گروه نگران و دو دلند؛ نه سوی آنانند و نه سوی اینان. و هر که را خدا گمراه کند، هرگز راهی راهوار برای (نجات) او نخواهی یافت.'),
(637, 4, 144, 'هان ای کسانی که ایمان آوردید! به جای مؤمنان، کافران را اولیای خود مگیرید. آیا می‌خواهید برای خدا بر ضد خودتان سلطه‌ای آشکارگر قرار دهید؟'),
(638, 4, 145, 'منافقان بی‌گمان در (ژرفای) فروترین بخش از درکات (هفتگانه‌ی) آتشند و هرگز برایشان یاوری نخواهی یافت.'),
(639, 4, 146, 'مگر کسانی که توبه کردند و (خود و دیگران را) اصلاح نمودند و به (یاری) خدا با کوشش و کاوش، نگهبان (خودشان و دیگران) شدند و طاعت خود را برای خدا خالص گردانیدند. پس هم‌اینان با آن مؤمنان (راستین) همراهند و در آینده خدا این مؤمنان را پاداشی بزرگ خواهد داد.'),
(640, 4, 147, 'اگر سپاس بدارید و ایمان آرید، خدا با عذاب شما چه کاری خواهد داشت‌؟ و خدا همواره سپاس‌گزار [:حق‌شناس] بسیار دانا بوده است.'),
(641, 4, 148, 'خدا، افشاگری به بد زبانی را دوست نمی‌دارد، مگر (از) کسی که بر او ستم رفته باشد. و خدا بس شنوایی بسیار داناست.'),
(642, 4, 149, 'اگر خیری را آشکار کنید یا پنهانش دارید، یا از بدی درگذرید، خدا (هم) درگذرنده‌ای توانا بوده است.'),
(643, 4, 150, 'همواره کسانی که به خدا و پیامبرانش کفر می‌ورزند و می‌خواهند میان خدا و پیامبرانش جدایی اندازند و می‌گویند: «ما به بخشی ایمان داریم و به بخشی کفر می‌ورزیم‌» و می‌خواهند میان آن (دو) راهی راهوار برای خود اختیار کنند،'),
(644, 4, 151, 'همانان حقاً کافرانند (که گویی کافران دیگر کافر نیستند) و ما برای کافران عذابی خفت‌آور آماده کرده‌ایم.'),
(645, 4, 152, 'و کسانی که به خدا و پیامبرانش ایمان آورده و میان احدی از آنان جدایی نیفکندند، در آینده (خدا) پاداششان می‌دهد و خدا پوشنده‌ی رحمتگر بر ویژگان بوده است.'),
(646, 4, 153, 'اهل کتاب از تو می‌خواهند که کتابی از آسمان بر ایشان فرود آوری. پس بی‌چون از موسی بزرگ‌تر از این را خواستند و گفتند: «خدا را آشکارا به ما بنمای.» پس به سزای ظلمشان (آذرخش) صاعقه آنان را فرو گرفت. سپس، بعد از آنکه دلایل آشکار برایشان آمد، گوساله را (به پرستش) گرفتند. پس ما از آن (هم) چشم‌پوشی نمودیم و به موسی برهانی روشنگر دادیم.'),
(647, 4, 154, 'و (کوه) طور را به سبب (نقض) پیمانشان بالای سرشان برافراشتیم و به آنان گفتیم: «سجده‌کنان از درب (بیت‌المقدس) در آیید.» و (نیز) به آنان گفتیم: «در روز شنبه تجاوز مکنید.» و از ایشان پیمانی سخت بی‌امان بر گرفتیم.'),
(648, 4, 155, 'پس به (سزای) پیمان‌شکنی‌شان و کفرشان (نسبت‌) به آیات خدا و کشتار ناحقشان پیمبران را و گفتارشان که: «دل‌هایمان در بسته است‌» (لعنتشان کردیم)؛ بلکه خدا به خاطر کفرشان بر دل‌هایشان مهر زده. پس جز اندکی ایمان نمی‌آورند.'),
(649, 4, 156, 'و (نیز) به سزای کفرشان و آن تهمت بزرگی که به مریم زدند،'),
(650, 4, 157, 'و گفته‌ی ایشان که: «ما بی‌چون مسیح، عیسی‌بن مریم، پیامبر خدا را کشتیم‌.» حال آنکه آنان او را نکشتند و به دارش (هم) نیاویختند؛ لیکن امر بر آنان مشتبه شد و کسانی که بی‌چون درباره‌ی او اختلاف کردند، بی‌گمان در مورد آن در (ژرفای) شکّی غرق شده و هیچ علمی بر آن ندارند، جز پیروی از گمان. و به یقین او را نکشتند؛'),
(651, 4, 158, 'بلکه خدا او را سوی خود بالا برد. و خدا عزیز بوده است.'),
(652, 4, 159, 'و هیچ کس از اهل کتاب، نیست مگر آنکه بی‌چون پیش از مرگش [:مرگ مسیح] بی‌گمان به او ایمان می‌آورد و روز قیامت (عیسی نیز) بر آنان گواه است.'),
(653, 4, 160, 'پس به سزای ستمی که از یهودیان سر زد و به سبب آنکه (مردمان را) بسیار از راه خدا بازداشتند، چیزهای پاکیزه‌ای را که برایشان حلال شده بود بر آنان حرام کردیم.'),
(654, 4, 161, 'و (نیز به سبب) ربا گرفتنشان - حال آنکه از آن نهی شده بودند- و (به جهت) مفتخواری اموال مردمان. و ما برای کافرانشان عذابی دردناک آماده کردیم.'),
(655, 4, 162, 'لیکن پای‌برجایان استوارشان در علم [:معرفت الهی] و مؤمنان، به آنچه بر تو نازل شده و آنچه پیش از تو نازل گشته ایمان می‌آورند و به ویژه بر پا دارندگان نماز و (نیز) زکات‌دهندگان و مؤمنان به خدا و روز واپسین. زودا (که) به آنان پاداشی بزرگ خواهیم داد.'),
(656, 4, 163, 'ما بی‌گمان - همچنان که به نوح و پیامبران پس از او، وحی کردیم - به تو (نیز) وحی کردیم و به ابراهیم و اسماعیل و اسحاق و یعقوب و فرزندان(او) و عیسی و ایّوب و یونس و هارون و سلیمان (نیز) وحی کردیم و به داوود زبور دادیم.'),
(657, 4, 164, 'و پیامبرانی (را فرستادیم) که در حقیقت (ماجرای) آنان را از پیش بر تو حکایت کردیم و پیامبرانی (را نیز برانگیختیم) که (سرگذشت) ایشان را بر تو بازگو نکرده‌ایم و خدا با موسی سخنی (وحیانی) گفت.'),
(658, 4, 165, 'پیامبرانی را که بشارتگر و هشداردهنده بودند، تا برای مردمان پس از پیامبران، در برابر خدا (بهانه و) حجّتی (در کار) نباشد. و خدا عزیز حکیم بوده است.'),
(659, 4, 166, 'اما خدا به (حقانیّت) آنچه بر تو نازل کرده است گواهی می‌دهد. او آن را به علم (مطلق) خویش نازل کرده و فرشتگان (نیز به آن) گواهی می‌دهند و گواهی خدا کافی است.'),
(660, 4, 167, 'بی‌گمان، کسانی که کفر ورزیدند و (خود و مردم را) از راه خدا بازداشتند، بی‌چون به گمراهی دور و درازی افتادند.'),
(661, 4, 168, 'همواره کسانی که کفر ورزیدند و ستم کردند، خدا بر آن نبوده است که برایشان (گناهانشان را) بپوشد و به راهی (راست) هدایتشان کند:'),
(662, 4, 169, 'مگر راه جهنّم، حال آنکه در آن جاودانند و این (کار) برای خدا آسان بوده است.'),
(663, 4, 170, 'هان ای مردمان! (آن) پیامبر (یگانه‌ی موعود)، تمامی حق را از سوی پروردگارتان به‌راستی برایتان آورده است. پس (به او) ایمان بیاورید که برای شما خیر است و اگر کافر شوید (بدانید که) آنچه در آسمان‌ها و زمین است از خداست و خدا بس دانایی حکیم بوده است.'),
(664, 4, 171, 'هان ای اهل کتاب! در دین خود غلوّ [:زیاده‌روی] مکنید و درباره‌ی خدا جز (سخن) حق مگویید. مسیح، عیسی‌بن‌مریم، تنها پیامبر خدا و کلمه [:نشانه‌ی] اوست (که) آن را سوی مریم افکنده و روحی (بزرگ و آفریده‌ای ممتاز) از جانب اوست. پس به خدا و پیامبرانش ایمان بیاورید و نگویید (خدا اقنوم‌های) سه‌گانه است. (از این کفر) فراسوی خیری برای خودتان باز ایستید. خدا فقط معبودی یگانه، و منزّه از آن است که برای او فرزندی باشد. آنچه در آسمان‌ها و آنچه در زمین است از اوست و کارسازی خدا کافی است.'),
(665, 4, 172, 'مسیح از اینکه بنده‌ی خدا باشد هرگز خودداری نمی‌کند و نه فرشتگان مقّرب (نیز). و هر کس از پرستش او خودداری و تکبر کند (بداند که) زودا (که) خدا همه‌ی آنان را سوی خود گرد می‌آوَرَد.'),
(666, 4, 173, 'پس اما کسانی که ایمان آورده و کارهای شایسته(ی ایمان) کرده‌اند، پاداش‌هاشان را به تمام (و کمال) خواهد داد و از فزون‌بخشیِ خود به ایشان افزون می‌بخشد. و امّا کسانی که امتناع ورزیده و تکبر کردند، پس آنان را به عذابی دردناک دچار می‌سازد و بجز خدا برای خود (نه‌) سرپرست و نه یاوری نخواهند یافت.'),
(667, 4, 174, 'هان ای مردمان! شما را از جانب پروردگارتان برهانی (بس درخشان) آمده و فراسوی شما نوری روشنگر فرو فرستادیم.'),
(668, 4, 175, 'پس اما کسانی که به خدا گرویدند و به (یاری) او با کوشش و کاوش، نگهبانی (برای خود و دیگران) یافتند، به زودی خدا آنان را در جوار رحمت و فضلی از جانب خویش در آورد و ایشان را سوی خود، به راهی راست هدایت کند.'),
(669, 4, 176, 'از تو فتوا می‌طلبند. بگو: «خدا درباره‌ی کلاله [:برادران و خواهران ابوینی] فتوا می‌دهد (که) : اگر مردی بمیرد و فرزندی ندارد و خواهری دارد، پس نصف آنچه واگذاشته از آنِ اوست و آن (مرد نیز) از خواهرش تمامی ارث را می‌برد، (البته) اگر برای او [:خواهر] فرزندی نباشد. پس اگر (ورثه فقط) دو خواهر (یا بیشتر) باشند، دو سوم میراث برای آنهاست. اگر چند خواهر و برادرند، نصیب مذکر، مانند نصیب دو مؤنث است. خدا برای شما (حقیت را) بیان می‌کند که گمراه نشوید و خدا به هر چیزی داناست.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'هان ای کسانی که ایمان آوردید! به تمام قراردادها(ی خود و دیگران) وفا کنید. برای شما تمامی حیوانات زبان‌بسته‌ی نعمت‌وار حلال گردیده، جز آنچه (حکمش) بر شما خوانده می‌شود؛ حال آنکه شکار را در حال احرام یا حرم حلال نشمرید. خدا هر چه را بخواهد حکم می‌دهد.'),
(671, 5, 2, 'هان ای کسانی که ایمان آوردید! حرمت شعائر خدا و ماه حرام و قربانی (بی‌نشان) و قربانی‌های نشانه‌دار و راهیان بیت‌الحرام را - در حالی که فضل و خشنودی پروردگار خود را می‌طلبند - نگه دارید. و چون از احرام و یا حرم بیرون آمدید (می‌توانید) شکار کنید. و هرگز مبادا کینه‌توزی گروهی که شما را از مسجدالحرام بازداشتند، به تجاوزتان وادارد. و در نیکوکاری و پرهیزگاری با یکدیگر همکاری کنید و در گناه -تأخیراندازنده از خیر- و در دشمنی، همکاری نکنید. و از (خشم) خدا پروا نمایید که خدا همواره سخت‌کیفر است.'),
(672, 5, 3, '(اینها) بر شما حرام شده است: مردار و خون (ریخته شده) و گوشت خوک و آنچه به نام غیر خدا فروافکنده و کشته شده و (حیوان) خفه شده و چوب‌خورده و از بلندی افتاده و شاخ خورده و آنچه درنده (از آن) خورده - مگر آنچه را (از اینها) زنده دریافته، سر ببُرید - و (همچنین) آنچه روی بت‌ها سر بریده شده و (نیز) اینکه با تیرهای قرعه (حیوانی را، میان خود) بخش کنید؛ اینها فسق و نافرمانی (خدا)ست. امروز کسانی که کافر شدند از (کارشکنی در) دین شما نومید گردیدند، پس از اینان مترسید و از من بهراسید. امروز دین شما را برایتان کامل و نعمت خود را بر شما تمام گردانیدم و این (اسلام) را برای شما (به عنوان) طاعتی (شایسته و بایسته و پیوسته) پسندیدم‌؛ پس هرکس (در این جریان و مانند آن) دچار تنگی و تنگنایی شود، بی آنکه به گناهی متمایل باشد، (اگر آنچه را منع شده است انجام دهد) خدا همواره پوشنده‌ی رحمتگر بر ویژگان است.'),
(673, 5, 4, 'از تو می‌پرسند چه چیز برایشان حلال شده‌؟ بگو: «چیزهای پاکیزه برایتان حلال گشته و (نیز) صید به وسیله‌ی حیوانات درنده (زمینی، دریایی و هوایی) که شما (مربیان شایسته‌ی حیوانات شکاری) از آنچه خدایتان به شما آموخته، به آنها تعلیم داده‌اید (برایتان حلال شده). پس از آنچه آنها برای شما گرفته و نگاه داشته‌اند بخورید و نام خدا را (در حال صید) بر آنها ببرید و خدای را پروا بدارید (که) خدا همواره زودشمار است.»'),
(674, 5, 5, 'امروز چیزهای پاکیزه برایتان حلال شده و طعام کسانی که کتاب (وحیانی) داده شدند، برایتان حلال و طعام شما (هم) برای آنان حلال است و (نیز ازدواج با) زنان پاکدامن باایمان و زنان پاکدامن از کسانی که پیش از شما کتاب (آسمانی) به آنان داده شده (حلال است)، هنگامی‌که مهریه‌هایشان را به ایشان بدهید و (خودتان و آنان) پاکدامن و نگه‌دار (خود و همسرانتان) باشید. نه زناکاران و نه زنانی را (از روی شهوت) به دوستی برگیرید. و هرکس به ایمان کافر شود، بی‌گمان عملش تباه شده و او در آخرت از زیانکاران است.'),
(675, 5, 6, 'هان ای کسانی که ایمان آوردید! چون به نماز برخاستید، صورت و دست‌هایتان را تا آرنج‌هاتان بشویید و بخشی از سرهایتان و (تمام روی) پاهایتان را تا دو برآمدگی (نخستین) مسح کنید. و اگر جُنُب بودید، خود را (سر تا پا هرگونه بخواهید) پاک نمایید [:غسل کنید] و اگر بیمار یا در سفری بودید یا یکی از شما از موضع قضای حاجت آمد یا با زنان نزدیکی کردید، پس آبی نیافتید، در این صورت بلندای پاکیزه‌ای را بجویید و از آن به بخشی از صورت و (بخشی از) دست‌هایتان بکشید. خدا نمی‌خواهد بر شما هیچ تنگی بگیرد، ولی می‌خواهد شما را پاک کند و نعمتش را بر شما تمام گرداند، شاید سپاس گزارید.'),
(676, 5, 7, 'و نعمتی را که خدا بر شما (ارزانی‌) داشته و (نیز) پیوندش را -که با شما پیوندی پایدار بسته- به یادآورید، چون گفتید: «شنیدیم و اطاعت کردیم.‌» و خدای را پروا بدارید که خدا به (راز) ذاتی سینه‌ها بسی داناست.'),
(677, 5, 8, 'هان ای کسانی که ایمان آوردید! برای خدا بسیار به فضیلتْ پایدار، ارج‌گذار و استوار باشید، در حالی‌که به تقسیم کردن (عادلانه) شهادت‌دهنده‌اید و هرگز نباید دشمنی گروهی شما را بر آن وادارد که عدالت نکنید. عدالت کنید (که) آن به تقوا نزدیکتر است و خدا را پروا بدارید. به‌راستی خدا به‌آنچه انجام می‌دهید بسی آگاه است.'),
(678, 5, 9, 'خدا کسانی را که ایمان آورده و کارهای شایسته(ی ایمان) کردند، برایشان پوشش و پاداشی بزرگ وعده داده است.'),
(679, 5, 10, 'و کسانی که کافر شدند و با آیات ما (همان‌ها و ما را) دروغ انگاشتند، اینان دوزخیانند.'),
(680, 5, 11, 'هان ای کسانی که ایمان آوردید! نعمت خدا را بر (سر و سامان) خودتان یاد کنید. چون قومی آهنگ (آن) نمودند (که) سویتان دست یازند، پس (خدا) دستشان را از شما بازداشت و خدا را پروا بدارید. و مؤمنان باید بی‌گمان تنها بر خدا توکل کنند.'),
(681, 5, 12, 'و خدا از فرزندان اسرائیل همواره پیمان گرفت و از آنان دوازده (سر کرده‌ی) پیگیر کارگشا (درباره‌ی آنان) برانگیختیم و خدا فرمود: «من حتماً با شما هستم، اگر نماز را بر پا بدارید و زکات را بدهید و به فرستادگانم ایمان بیاورید. و بر گرامی‌داشتشان یاریشان کنید و وام نیکویی به خدا دهید. بی‌گمان گناهانتان را از شما می‌زدایم و شما را همواره به‌راستی در باغ‌هایی که از زیر (درختان)شان نهرها روان است در می‌آورم. پس هر کس از شما بعد از این کافر شود، به‌راستی راه راست را گم کرده است.»'),
(682, 5, 13, 'پس به (سزای) پیمان شکستنشان لعنتشان کردیم و دل‌هایشان را سخت گردانیدیم، در حالی‌که کلمات (وحیانی) را از‌جایشان جابه‌جا می‌کنند (و) بخشی از آنچه را بدان یادآور شده بودند به فراموشی سپردند. و تو بی‌وقفه بر خیانتی بزرگ از آنان آگاه می‌شوی؛ مگر اندکی از ایشان (که خیانتکار نیستند). پس از آنان درگذر و روی بگردان که خدا نیکوکاران را همی دوست می‌دارد.'),
(683, 5, 14, 'و برخی از کسانی که گفتند: «ما نصرانیانیم‌» از ایشان (نیز) پیمانشان را گرفتیم. پس بهره‌ای بس شایسته از آنچه را بدان یادآوری شده بودند به فراموشی سپردند. پس ما (هم) تا روز قیامت میانشان دشمنی و کینه‌توزی را درآمیختیم و به زودی خدا آنان را از آنچه می‌ساخته‌اند خبری بزرگ خواهد داد.'),
(684, 5, 15, 'هان ای اهل کتاب! پیامبر (بسیار برجسته‌ی) ما به‌راستی سوی شما آمده است که بسیاری از چیزهایی از کتاب (آسمانی خود) را که پوشیده می‌داشته‌اید، برای شما بیان می‌کند و از بسیاری (هم در بیان وحیانی ویژه‌اش) چشم‌پوشی می‌نماید. بی‌گمان برایتان از جانب پروردگار(تان) نور و کتابی روشنگر آمده است.'),
(685, 5, 16, 'خدا هر کس را که خشنودیش را پیروی کند، به وسیله‌ی آن (کتاب) به راه‌های سلامت رهنمون می‌شود و به اذن خویش، آنان را از تاریکی‌ها سوی روشنایی برون می‌برد و به راهی راست رهبریشان می‌کند.'),
(686, 5, 17, 'بی‌گمان کسانی که گفتند: «خدا، همو مسیح پسر مریم است‌» (که توحیدشان در خدایی مسیح است) بی‌چون کافر شدند. بگو: «اگر (خدا) اراده کند که مسیح پسر مریم و مادرش و هر که در زمین است جملگی را به هلاکت رساند، پس چه‌کس از سوی خدا اختیاری دارد؟ حال آنکه فرمانروایی آسمان‌ها و زمین و آنچه میان آن دو است تنها از آنِ خداست‌؛ هر چه بخواهد می‌آفریند و خدا بر هر چیزی بسی تواناست.»'),
(687, 5, 18, 'و یهودیان و نصرانیان گفتند: «ما پسران و دوستان خداییم.» بگو: «پس چرا خدا شما را به (کیفر) گناهانتان عذابتان می‌کند؟ (نه،) بلکه شما (هم) بشری هستید از جمله‌ی آفریدگان.» (خدا) برای هر که بخواهد می‌پوشاند و هر که را بخواهد عذاب می‌کند و فرمانروایی آسمان‌ها و زمین و آنچه میان آن دو است تنها از خداست و بازگشت (همه) تنها سوی اوست.'),
(688, 5, 19, 'هان ای اهل کتاب! بی‌گمان پیامبرمان سویتان آمده که در حالت فترت رسولان (تمام حقایق را) برای شما بیان می‌کند تا مبادا بگویید: «برای ما هیچ بشارتگر و هشداردهنده‌ای نیامد.» پس همانا برای شما بشارتگر و هشداردهنده‌ای آمده و خدا بر هر چیزی تواناست.'),
(689, 5, 20, 'و چون موسی به قوم خود گفت: «ای قوم من! نعمت خدا را بر خودتان یاد کنید، آن‌گاه که در میان شما پیامبرانی برجسته قرارداد و شما را مالکان خود ساخت (و از بندگی فرعون نجاتتان داد) و آنچه را که به هیچ‌کس از جهانیان نداده بود به شما داد.»'),
(690, 5, 21, '«ای قوم من! به سرزمین مقدسی که خدا برایتان مقرر داشته درآیید و بر گذشته‌هاتان بازنگردید که (این خود) بازگشتی زیان‌بار است.»'),
(691, 5, 22, 'گفتند: «ای موسی! در آنجا مردمی زورمندِ ستم‌پیشه‌اند و تا آنان از آنجا بیرون نروند ما هرگز داخل آن نمی‌شویم. پس اگر آنان از آنجا بیرون روند ما (هم) بی‌چون (به آنجا) وارد می‌شویم.»'),
(692, 5, 23, 'دو مرد از (زمره‌ی) کسانی که (از خدا) می‌هراسیدند (و) خدا به آنان نعمت داده بود گفتند: «از آن دروازه (ناگهان) بر ایشان درآیید که اگر از آن داخل شدید، بی‌گمان پیروزمند خواهید شد. و اگر مؤمن بوده‌اید پس بر خدا توکل کنید.»'),
(693, 5, 24, 'گفتند: «ای موسی! تا هنگامی که آنان در آن (شهر) هستند ما بی‌گمان هرگز پای در آن ننهیم، پس تو برو و (با) پروردگارت با آنها جنگ کنید، ما همواره همین‌جا نشستگانیم.»'),
(694, 5, 25, '(موسی) گفت: «پروردگارم! من جز اختیار شخص خود و برادرم را (در رسالت) ندارم، پس میان ما و میان این قوم نافرمان جدایی بیفکن.»'),
(695, 5, 26, '(خدا به موسی) فرمود: «پس (ورود به) آن سرزمین بی‌گمان چهل سال بر ایشان حرام گشت (و) از آن محرومند حال آنکه در زمین(شان) سرگردان خواهند بود. پس تو بر گروه نافرمانان اندوه مخور.»'),
(696, 5, 27, 'و خبر دو پسر آدم را به تمامی حقیقت بر ایشان بخوان. چون (هر یک از آن دو) قربانی‌ای پیش‌کش داشتند، پس از یکی از آن دو پذیرفته شد و از دیگری پذیرفته نشد. (قابیل) گفت: «بی‌چون و بی‌امان تو را خواهم کشت.» (هابیل) گفت: «خدا بی‌گمان تنها از تقواپیشگان می‌پذیرد؛»'),
(697, 5, 28, '«اگر ناگزیر دستت را سوی من دراز کنی - تا مرا بکشی - من دستم را سوی تو درازکننده نیستم تا تو را بکشم. من به‌راستی از خدا - پروردگار جهانیان - می‌ترسم.»'),
(698, 5, 29, '«من همی خواهم که تو با گناهِ من و گناه خودت (سوی خدا) بازگردی؛ پس از همدمان آتش باشی و همین است سزای ستمگران.»'),
(699, 5, 30, 'پس نفس (امّاره)اش برایش قتل برادرش را هموار و راهوار و (او را) وادار کرد؛ پس او را کشت و در نتیجه از زیانکاران شد.'),
(700, 5, 31, 'پس خدا زاغی را برانگیخت که زمین را می‌کاوید تا به او نشان دهد چگونه جسد برادرش را پنهان کند. (قابیل) گفت: « وای بر من! آیا عاجز بودم که مثل این زاغ باشم تا جسد برادرم را پنهان کنم‌؟» پس از (زمره‌ی) پشیمانان گردید.'),
(701, 5, 32, 'برای همین (هم) بر فرزندان اسرائیل نبشتیم که هر کس کسی را- جز به (جزای کشتن) کسی، یا فسادی در زمین - بکشد، چنان است که گویی همه‌ی مردم را کشته و هر کس کسی را زنده بدارد، چنان است که گویی تمامی مردمان را زنده کرده. و همواره پیامبران ما تمامی روشنایی‌ها را برایشان آوردند، سپس بسیاری از ایشان همواره در زمین بی‌گمان زیاده‌رو می‌باشند.'),
(702, 5, 33, 'سزای کسانی که با (ربانیت) خدا و (با رسالت) پیامبرش می‌جنگند و در زمین به افساد می‌کوشند، جز این نیست که پیاپی کشته شوند یا بر دار آویخته گردند یا دست‌ها و پاهایشان در خلاف جهت یکدیگر بریده شود یا از سرزمین(شان به‌گونه‌ای متناسب با گناهشان) رانده شوند. این رسوایی برایشان در دنیاست و برایشان در آخرت عذابی بزرگ است.'),
(703, 5, 34, 'مگر کسانی که پیش از آنکه بتوانید بر ایشان دست یابید توبه کرده باشند. پس بدانید که (در این صورت) خدا پوشنده‌ی رحمتگر بر ویژگان است.'),
(704, 5, 35, 'هان ای کسانی که ایمان آوردید! از خدا پرواکنید و سوی او از (آن) وسیله(ای که خشنود است) جستجو کنید و در راهش جهاد نمایید، شاید رستگار کنید.'),
(705, 5, 36, 'بی‌گمان کسانی که کفر ورزیدند، اگر تمامی آنچه در زمین است برای آنان باشد و مثل آن را (نیز) با آن (داشته باشند) تا بدان وسیله خود را از عذاب روز رستاخیز بازخرید کنند از ایشان پذیرفته نشود و برایشان عذابی پردرد است.'),
(706, 5, 37, 'می‌خواهند که از آتش برون آیند، در حالی‌که از آن بیرون‌آمدنی نیستند و برای آنان عذابی پایدار است.'),
(707, 5, 38, 'و مرد و زن دزد را - به (سزای) آنچه به دست آورده‌اند - دست‌هایشان را به کیفری از جانب خدا قطع کنید. و خدا عزیز حکیم است.'),
(708, 5, 39, 'پس هر کس پس از ستمش توبه و اصلاح کند، خدا به‌راستی بر او بازگشت می‌کند. خدا بس پوشنده‌ی رحمتگر بر ویژگان است.'),
(709, 5, 40, 'آیا ندانستی که فرمانروایی آسمان‌ها و زمین از آنِ خداست؛ هر که را بخواهد عذاب می‌کند و برای هر که بخواهد (گناهش را) می‌پوشد و خدا بر هر چیزی بسی تواناست.'),
(710, 5, 41, 'هان ای پیامبر! کسانی که در کفر(شان) شتاب می‌ورزند، تو را غمگین نسازند؛ آنان که با زبان خود گفتند: «ایمان آوردیم‌» و حال آنکه دل‌هایشان ایمان نیاورده. و برخی از یهودیان بسیار به سخنان گزاف گوش می‌سپارند و برای گروهی دیگر که (خود) نزد تو نیامده‌اند، گوش فرا می‌دارند و کلمات را از جاهای خود دگرگون می‌گردانند (و) می‌گویند: «اگر این (حکم) به شما داده شد، آن را بپذیرید و اگر آن به شما داده نشد، (از آن) دوری کنید.» و هر که را خدا بخواهد به آزمونی آتشین درافکند، هرگز در برابر خدا برای او از دست تو چیزی بر نمی‌آید. اینانند که خدا نخواسته دل‌های‌شان را پاک گرداند. در دنیا برایشان رسوایی و در آخرت عذابی بزرگ است.'),
(711, 5, 42, 'بسی شنوایانی پذیرای دروغند (و) بسیار حرام‌خوارند. پس اگر نزد تو آیند میانشان داوری کن یا از ایشان روی بگردان و اگر از آنان روی برتابی هرگز زیانی به تو نتوانند رسانید و اگر داوری کنی، به عدالت در میانشان داوری کن (که) خدا دادگران را همی دوست می‌دارد.'),
(712, 5, 43, 'و چگونه تو را داور قرار می‌دهند حال آنکه نزدشان تورات است (که) در آن حکم خداست‌؟ سپس بعد از این (طلب داوری) ، پشت می‌کنند و ایشان (از) مؤمنان نیستند.'),
(713, 5, 44, 'ما تورات را -که در آن رهنمود و نوری است- به‌راستی نازل کردیم (و) پیامبرانی برجسته که تسلیم (فرمان خدا) بودند به موجب آن برای یهود داوری می‌کردند؛ و (همچنین) دانشمندان ربانی یهود و دانشمندان نصرانی با آنچه از کتاب خدا بدان نگهبانی شایسته شدند، حال‌آنکه بر آن گواه بودند. پس از مردم نترسید و از من بهراسید و با آیات من بهای ناچیزی را نخرید [:معامله نکنید]. و کسانی که به آنچه خدا نازل کرده داوری نکنند، آنان خودشان کافرانند.'),
(714, 5, 45, 'و در آن [:تورات] بر آنان نبشتیم که بی‌امان جان در برابر جان و چشم در برابر چشم و گوش در برابر گوش و دندان در برابر دندان است و زخم‌ها (نیز به همان ترتیب) قصاص [:پیگیری] دارند. پس هر کس از آن (قصاص در صورت مصلحت) درگذرد کفّاره‌ای برای اوست و کسانی که به آنچه خدا نازل کرده داوری نکرده‌اند، آنان خود ستمگرانند.'),
(715, 5, 46, 'و عیسی پسر مریم را به دنبال آنان [:پیامبران اسرائیلی]آوردیم، در حالی‌که تورات را - که در برابرش بود - تصدیق کرد و انجیل را بدو دادیم، حال آنکه در آن هدایتی و نوری و تصدیق‌کننده‌ی تورات است (که) پیش روی خود اوست و برای پرهیزگاران رهنمود و اندرزی است.'),
(716, 5, 47, 'و انجیلیان باید به آنچه خدا در آن نازل کرده داوری کنند و کسانی که به آنچه خدا نازل کرده حکم نکنند، آنان خودشان فاسقانند.'),
(717, 5, 48, 'و ما این کتاب [:قرآن] را به تمامی حقّ سوی تو فروفرستادیم، حال آنکه تصدیق‌کننده‌ی کتاب‌های پیشین برابرش و حاکم و محیط و نگهبان بر آنهاست. پس میان آنان برآنچه خدا نازل کرده حکم کن و هواهایشان را - با تجاوز از حقّی که سوی تو آمده - پیروی مکن. برای هر یک از شما (امّت‌های ولایت عزم ) شریعت و راهی روشنگر قرار داده‌ایم. و اگر خدا می‌خواست شما را (در تکوین و دین) همواره امّت واحدی قرار می‌داد، ولی همی‌خواهد شما را در آنچه به شما داده است بیازماید. پس در کارهای نیک بر یکدیگر سبقت بجویید. بازگشت همه‌ی شما سوی خداست؛ پس درباره‌ی آنچه در آن اختلاف می‌کرده‌اید به خوبی خبری مهم خواهد داد.'),
(718, 5, 49, 'و (دیگر) اینکه میان اینان به (موجب) آنچه خدا نازل کرده داوری کن و از هواهای‌شان پیروی مکن و از آنان برحذر باش، مبادا تو را در بخشی از آنچه خدا بر تو نازل کرده به فتنه در اندازند. پس اگر پشت کردند، بدان که خدا می‌خواهد فقط با پاره‌ای از گناهان‌شان گزندی بدیشان رساند. و بی‌گمان بسیاری از مردمان بسی نافرمان‌اند.'),
(719, 5, 50, 'پس آیا حکم جاهلیت را می‌جویند (که بپویند) ؟ و برای مردمی که یقین می‌آورند، داوری چه کسی از خدا بهتر است‌؟'),
(720, 5, 51, 'هان ای کسانی که ایمان آوردید! یهودیان و نصرانیان را اولیاء [:دوستان و سرپرستان](خود) مگیرید. بعضی از آنان اولیاء بعضی دیگرند (و) هرکس از شما آنان را به ولایت برگیرد، بی‌گمان (خود) از آنان است. خدا بی‌چون گروه ستمگران را هدایت نمی‌کند.'),
(721, 5, 52, 'پس می‌بینی کسانی که در دل‌هایشان بیماری‌ای است، در (رابطه‌ی با) آنان شتاب می‌ورزند.گویند: «می‌ترسیم (در غیر این صورت) حادثه‌ی ناگواری گریبان‌گیرمان شود.» امید است خدا فتح یا امر دیگری را (به ویژگی) از جانب خویش پیش آورد، پس (در نتیجه آنان) از آنچه در (دل‌های) خودشان پنهان داشته‌اند پشیمان باشند.'),
(722, 5, 53, 'و کسانی که ایمان آوردند گویند: «آیا ایشان کسانی‌اند که به خدا سوگندهای سختشان را می‌خوردند (که) بی‌گمان با شمایند؟» اعمالشان تباه شد؛ پس ناگهان زیانکار گردیدند.'),
(723, 5, 54, 'هان ای کسانی که ایمان آوردید! هرکس از شما از دین خود برگردد خدا - در آینده - گروهی (دیگر) را می‌آورد که آنان را دوست می‌دارد و آنان (نیز) او را دوست دارند. (اینان) بر مؤمنان، فروتن (و) بر کافران، سرفراز و فراتن‌اند (و) در راه خدا جهاد می‌کنند و از ملامت هیچ ملامتگری نمی‌ترسند؛ این فضل خداست، آن را به هر که بخواهد می‌دهد و خدا گشایشگری بسیار داناست.'),
(724, 5, 55, 'ولیّ شما تنها خدا و پیامبرش و کسانی‌اند که ایمان آوردند؛ (همان) کسانی که نماز را بر پا می‌دارند و در حال رکوع زکات می‌دهند.'),
(725, 5, 56, 'و هر کس خدا و پیامبرش و کسانی را که ایمان آوردند ولیّ خود برگیرد (پیروزمند است). پس حزب خدا بی‌گمان همانا پیروزمندانند.'),
(726, 5, 57, 'هان ای کسانی که ایمان آوردید! کسانی را که دین شما را به مسخره و بازی گرفته‌اند (چه) از کسانی که پیش از شما به آنان کتاب داده شده و (چه از) کافران (دیگر، اینان را) اولیائی (برای خودتان) مگیرید و اگر مؤمن بوده‌اید از خدا پروا بدارید.'),
(727, 5, 58, 'و هنگامی‌که (با اذان و اقامه مردمان را) به نماز می‌خوانید آن را به مسخره و بازی می‌گیرند، از آن روی که آنان بی‌گمان گروهی هستند (که)خردورزی نمی‌کنند.'),
(728, 5, 59, 'بگو: «هان ای اهل کتاب! آیا جز برای این از ما انکار و انگار و پیگیری دارید که ما به خدا و آنچه سوی ما نازل شده و به آنچه پیش از این فرود آمده‌است، ایمان آوردیم و اینکه بی‌گمان بیشترتان فاسقانید؟»'),
(729, 5, 60, 'بگو: «آیا شما را به بدتر از (گرفتاران به) این کیفر در پیشگاه خدا خبری مهم از جهت ثوابشان نزد خدا بدهم‌؟ کسانی که خدا لعنتشان کرده و بر آنان خشم گرفته و از آنان بوزینگان و خوکان قرار داده. و آنان که طاغوت را پرستش کردند اینانند که از نظر جایگاه، بدتر و از راه راست (از همگان) گمراه‌ترند.»'),
(730, 5, 61, 'و هنگامی که نزد شما آیند گویند: «ایمان آوردیم.‌» در حالی که بی‌چون با کفر وارد شده و بی‌گمان با (آن) بیرون رفتند و خدا به آنچه پنهان می‌داشته‌اند داناتر است.'),
(731, 5, 62, 'و بسیاری از آنان را می‌بینی که در گناه پیگیر و دشمنی و حرام‌خواریِ هلاکت‌بارشان شتاب می‌کنند؛ همواره چه کارهای بدی انجام می‌داده‌اند.'),
(732, 5, 63, 'چرا ربّانیان آنان را از گفتار گناه‌آلودشان و (از) حرام‌خوارگی هلاکت‌بارشان باز نمی‌دارند؟ به‌راستی چه بد است آنچه می‌ساخته‌اند.'),
(733, 5, 64, 'و یهودیان گفتند: «دست خدا بسته است.» دست‌های خودشان بسته است و به سبب آنچه گفتند از رحمت خدا دورند. بلکه (خدا) هر دو دستش گشاده است، هرگونه بخواهد می‌بخشد و همانا آنچه از جانب پروردگارت سوی تو فرود آمده، بی‌گمان بر طغیان و کفر بسیاری از ایشان خواهد افزود و تا روز قیامت میانشان دشمنی و کینه افکندیم. هربار که آتشی برای پیکار برافروختند، خدا آن را خاموش کرد و (آنان) در زمین به اِفساد می‌کوشند و خدا مفسدان را دوست نمی‌دارد.'),
(734, 5, 65, 'و اگر اهل کتاب ایمان آورند و پرهیزگاری کنند، از ایشان گناهانشان را بی‌گمان می‌زداییم و به‌راستی آنان را به بوستان‌های پرنعمت درمی‌آوریم.'),
(735, 5, 66, 'و اگر آنان تورات و انجیل و آنچه را از جانب پروردگارشان سویشان نازل شده‌است بر پا دارند، بی‌چون از بالای سرشان (از برکات آسمانی) و از زیر پاهایشان (از برکات زمینی) خورده و برخوردار می‌شوند (تا چه رسد به مسلمانان!)؛ از میان آنان گروهی میانه‌رو هستند و بسیاری از ایشان چه بد عمل می‌کنند.'),
(736, 5, 67, 'هان ای پیامبر! آنچه را از جانب پروردگارت سوی تو نازل شده، ابلاغ کن و اگر (ابلاغ) نکنی پیام (سرنوشت‌ساز) رسالتش را نرسانده‌ای و خدا تو را از (گزند) مردمان نگاه می‌دارد. خدا گروه کافران را بی‌گمان هدایت نمی‌کند.'),
(737, 5, 68, 'بگو: «هان ای اهل کتاب! بر هیچ (آیین حقّی) نیستید تا (هنگامی‌که) تورات و انجیل و آنچه [:قرآن] را از جانب پروردگارتان سویتان نازل شده‌است بر پا دارید.» و همواره آنچه از جانب پروردگارت سوی تو نازل شده، همانا بر طغیان و کفر بسیاری از آنان خواهد افزود. پس بر گروه کافران اندوه مدار.'),
(738, 5, 69, 'بی‌گمان کسانی که ایمان آورده و کسانی (هم) که یهودی شده و (یا) صابئی [:التقاطی] و (یا) نصرانی‌اند، هر کس به خدا و روز بازپسین ایمان آورد و کار نیکو کند، نه بیمی بر ایشان است و نه ایشان اندوهگین می‌شوند.'),
(739, 5, 70, 'ما به‌راستی از فرزندان اسرائیل پیمانی سخت گرفتیم و سویشان پیامبرانی روانه کردیم. هر بار پیامبری چیزی برخلاف هواهایشان برایشان آورد، گروهی را تکذیب کردند و گروهی را می‌کشند.'),
(740, 5, 71, 'و پنداشتند که آزمایشی آتشین در کار نیست. پس کور و کر شدند. سپس خدا بر ایشان بازگشت (تا سویش بازگردند). سپس بسیاری از ایشان کور و کر شدند و خدا به آنچه انجام می‌دهند بسی بیناست.'),
(741, 5, 72, 'کسانی که گفتند: «خدا همان مسیح پسر مریم است‌» بی‌چون و بی‌امان کافر شدند. حال آنکه مسیح گفت: «ای فرزندان اسرائیل! خدا را، (که) پروردگار من و پروردگار خودتان (است) بپرستید. بی‌شک هرکس به خدا شرک آورد، همانا خدا بهشت را همواره بر او حرام ساخته و جایگاهش آتش است و برای ستمکاران هیچ یاورانی نیستند.»'),
(742, 5, 73, 'کسانی که (به تثلیث قایل شده و) گفتند: «خدا سومین (شخص از) سه (شخص یا سه اقنوم) است‌» بی‌شک و بی‌چون کافر شدند. حال آنکه هیچ معبودی جز خدای یکتا نیست و اگر از آنچه می‌گویند باز نایستند، به کسانی از ایشان که کافر شدند بی‌چون و بی‌امان عذابی دردناک خواهد رسید.'),
(743, 5, 74, 'پس آیا (از این گناه) سوی خدا برگشت نمی‌کنند، و پوشش نمی‌خواهند؟ و خدا بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(744, 5, 75, 'مسیح پسر مریم جز پیامبری نیست که پیش از او (نیز) پیامبرانی گذشتند و مادرش زنی بسیار راست بود. هر دو غذا می‌خوردند. بنگر چگونه آیات (خود) را برای آنان به روشنی جداسازی می‌کنیم‌؛ سپس بنگر کجا و کی به دروغ (از حقیقت) دور افکنده می‌شوند.'),
(745, 5, 76, 'بگو: «آیا غیر از خدا -چیزی را که اختیار زیان و سودتان را ندارد-می‌پرستید؟ حال آنکه خدا -(هم) او- بسیار شنوای بس داناست.»'),
(746, 5, 77, 'بگو: «ای اهل کتاب! در دین خود، غلوّ (و زیاده روی) نکنید! و غیر از حق نگویید! و از هوسهای جمعیّتی که پیشتر گمراه شدند و دیگران را گمراه کردند و از راه راست منحرف گشتند، پیروی ننمایید!»'),
(747, 5, 78, 'از میان فرزندان اسرائیل، آنان که کفر ورزیدند، به زبان داوود و عیسی پسر مریم لعنت شدند. این (کیفر) به خاطر آن بود که عصیان ورزیده و (از فرمان خدا) تجاوز می‌کردند.'),
(748, 5, 79, 'از کار زشتی که آن را مرتکب می‌شدند، یکدیگر را باز نمی‌داشتند. راستی چه بد بود آنچه می‌کرده‌اند.'),
(749, 5, 80, 'بسیاری از آنان را می‌بینی که با کسانی که کفر ورزیدند پیوند تنگاتنگ ولایت دارند. راستی چه زشت است آنچه ایشان برای خودشان پیش فرستادند که (در نتیجه) خدا بر ایشان خشم گرفت و هم ایشان در عذاب ماندگارانند.'),
(750, 5, 81, 'و اگر به خدا و پیامبر برجسته و آنچه که سوی او فرود آمده ایمان می‌آوردند، آنان را به ولایت نمی‌گرفتند. لیکن بسیاری از ایشان فاسقانند.'),
(751, 5, 82, 'همواره یهودیان و کسانی را که شرک آورده‌اند، بی‌گمان دشمن‌ترین مردمان نسبت به مؤمنان می‌یابی، و کسانی را که گفتند: «ما نصرانی هستیم‌»، همواره به‌راستی نزدیکترین مردم در دوستی با مؤمنان می‌یابی، زیرا برخی از آنان کشیشان و تارکان دنیا می‌باشند و به‌راستی آنان تکبر نمی‌ورزند.'),
(752, 5, 83, 'و هنگامی که آنچه را سوی این پیامبر نازل شده، بشنوند، می‌بینی بر اثر آن حقیقتی که شناخته‌اند، اشک از چشم‌هایشان می‌جوشد (و) گویند: «پروردگارمان! ما ایمان آوردیم، پس ما را در زمره‌ی گواهان بنویس‌.»'),
(753, 5, 84, '«و برای ما چیست [:چه عذری است‌] که به خدا و آنچه از حقّ به ما رسیده ایمان نیاوریم و با این حال چشم داریم پروردگارمان ما را با گروه شایستگان درآورد.»'),
(754, 5, 85, 'پس به (پاس) آنچه گقتند، خدا به آنان باغستان‌هایی پاداش داد که از زیر (درختان)شان نهرها روان است، حال آنکه در آنها جاودانند و این پاداش نیکوکاران است.'),
(755, 5, 86, 'و کسانی که کفر ورزیدند و با آیات ما (همان‌ها و ما را) تکذیب کردند، آنان همدمان آتشند.'),
(756, 5, 87, 'هان ای کسانی که ایمان آوردید! چیزهای پاکیزه‌ای را که خدا برای (استفاده‌ی) شما حلال کرده، حرام مشمارید (و از حکم خدا) تجاوز نکنید که خدا تجاوزکنندگان را بی‌گمان دوست نمی‌دارد.'),
(757, 5, 88, 'و از آنچه خدا روزیتان گردانیده، حلالی پاکیزه را بخورید و خدایی را که شما به او ایمان دارید پروا بدارید.'),
(758, 5, 89, 'خدا شما را به سوگندهای بیهوده‌تان (بدون پیمان در آن) مؤاخذه نمی‌کند، ولی به سوگندهایی که از روی پیمان می‌خورید (و می‌شکنید) شما را مؤاخذه می‌کند. پس کفّاره‌اش خوراک دادن به ده بینواست - از غذاهای متوسطی که به کسان خود می‌خورانید- یا پوشانیدن آنان یا آزاد کردنِ در بندی. پس کسی که (هیچ‌یک از این‌ها را) نیابد (باید) سه روز روزه بدارد. این است کفّاره‌ی سوگندهای شما که از روی پیمان خوردید. و نگهبان سوگندهای خود باشید. این‌گونه خدا آیات خود را برای شما بیان می‌کند، شاید شما سپاسگزاری کنید.'),
(759, 5, 90, 'هان ای کسانی که ایمان آوردید! شراب و کار آسانگر گناه [:محرماتی مانند قمار] و بت‌ها و تیرهای قرعه، پلیدی‌(هایی‌) از عمل شیطانند پس از آنها دوری گزینید؛ شاید (خود و دیگران را) رستگار کنید.'),
(760, 5, 91, 'جز این نیست (که) شیطان همی خواهد با شراب و کار آسانگرِ گناه، میان شما دشمنی و کینه‌توزی افکند و شما را از یاد خدا و از نماز بازدارد. پس آیا شما (از این‌ها) دست برمی‌دارید؟'),
(761, 5, 92, 'و خدا را اطاعت کنید و پیامبر را (نیز) اطاعت کنید و (از مخالفت با آنان) برحذر باشید. پس اگر روی گرداندید، بدانید که برعهده‌ی پیامبرمان فقط پیام‌رسانی روشنگر (ربّانی) است.'),
(762, 5, 93, 'بر کسانی که ایمان آورده و کارهای شایسته کرده‌اند گناهی در آنچه خورده‌اند نیست، در حالی که تقوا پیشه کردند و ایمان آوردند و کارهای شایسته(ی ایمان) نمودند؛ سپس تقوا پیشه کردند و ایمان آوردند؛ پس آن‌گاه تقوا پیشه کردند و (در تقوا و ایمان و عمل صالح) بهترین راه را انتخاب کردند. خدا نیکوکاران را دوست می‌دارد.'),
(763, 5, 94, 'هان ای کسانی که ایمان آوردید! خدا همواره شما را به چیزی از شکار -که در دسترس شما و نیزه‌هایتان باشد- بی‌چون می‌آزماید تا نشانه گذارد کسی را که در نهان از او می‌هراسد؛ پس هرکس پس (از آن) تجاوز کند برایش عذابی دردناک است.'),
(764, 5, 95, 'هان ای کسانی که ایمان آوردید! در حالی که مُحرِمید (یا داخل حَرَمید)، صید را مکشید و هرکس از شما عمداً آن را بکشد، باید نظیر آنچه کشته است کفّاره‌ای از اَنعام [:نعمت‌های حیوانی] بدهد (که) بر آن دو تن عادل از میان شما حکم کنند - در حالی‌که (این) هدیه‌ای است که به کعبه می‌رسد- یا به کفّاره‌ای مستمندانی را خوراک بدهد یا معادلش روزه بدار تا سزای زشتکاری خود را بچشد. خدا از آنچه گذشته، عفو کرده است و هرکس (آن را) تکرار کند، خدا از او انتقام می‌گیرد. خدا عزیزی صاحب‌انتقام است.'),
(765, 5, 96, 'صید دریا و خوراکیش برای شما حلال گشته، حال آنکه برایتان و برای راهیان بهره‌ای است، و صید بیابان - مادامی که مُحرِم (یا در حرم) می‌باشید- بر شما حرام گردیده است. از خدایی که سویش محشور می‌شوید پروا بدارید.'),
(766, 5, 97, 'خدا کعبه -بیت‌الحرام- را وسیله‌ی قیام [:به پاخاستن] مردمان قرار داده و ماه حرام و قربانی‌های نشان‌دار را (نیز به همین منظور مقرّر فرموده). این برای آن است تا بدانید خدا بی‌گمان آنچه را در آسمان‌ها و آنچه را در زمین است می‌داند و همواره خدا بر هر چیزی بسیار داناست.'),
(767, 5, 98, 'بدانید خدا سخت‌کیفر است و خدا بی‌گمان بسی پوشنده‌ای رحمتگر بر ویژگان است.'),
(768, 5, 99, 'بر پیامبر (خدا وظیفه‌ای) جز ابلاغ رَسا(ی رسالت) نیست. و خدا آنچه را آشکار و آنچه را پنهان می‌کنید می‌داند.'),
(769, 5, 100, 'بگو: «پاک و پلید یکسان نیستند، هرچند کثرتِ پلید، تو را به شگفت آورد. پس ای اندیشمندان از خدا پروا کنید، شاید (خود و دیگران را) رستگار کنید.»'),
(770, 5, 101, 'هان ای کسانی که ایمان آوردید! از چیزهایی مپرسید که اگر برای شما آشکار شود، بدآیندتان است و اگر هنگامی‌که قرآن نازل می‌شود درباره‌ی آنها بپرسید برایتان آشکار می‌شود. خدا از آنها [:پرسش‌های بیجا] در گذشت. و خدا بسی پوشنده‌ی بردبار است.'),
(771, 5, 102, 'گروهی پیش از شما (نیز) بی‌چون از این (گونه) پرسش‌ها کردند، سپس به سبب آنها (از) کافران یا کفران‌کنندگان شدند.'),
(772, 5, 103, 'خدا (چیزهای ممنوعی از قبیل) بحیره و سائبه وصیله و حام را (حرام) قرار نداده است، ولی کسانی که کفر ورزیدند، بر خدا دروغ می‌بندند و بیشترشان خردورزی نمی‌کنند.'),
(773, 5, 104, 'و هنگامی که به آنان گفته شود: «سوی آنچه خدا نازل کرده و سوی پیامبر(ش) بیایید» گویند: «آنچه پدرانمان را بر آن یافته‌ایم ما را بس است.» آیا و هرچند (که) پدرانشان چیزی نمی‌دانند و راهی نمی‌یابند؟'),
(774, 5, 105, 'هان ای کسانی که ایمان آوردید! بر (عهده‌ی) شماست (که) به خودتان بپردازید؛ هرگاه هدایت یافتید آن کس که گمراه شده است به شما زیانی نمی‌رساند. بازگشت همه‌ی شما سوی خداست، پس شما را از آنچه انجام می‌داده‌اید آگاهی مهمی می‌دهد.'),
(775, 5, 106, 'هان ای کسانی که ایمان آوردید! گواهی میانتان در وقت وصیت، هنگامی‌که یکی از شما را (نشانه‌های) مرگ در رسد، دو مرد عادل از شماست. یا اگر راهیِ راهی دشوار شوید و مصیبت مرگ، شما را فرا رسید (و شاهد مسلمانی هم نبود) دو تن از غیر (همکیشان) خود را (به شهادت بطلبید) و اگر (در صداقت آنان) شکّ کردید، پس از نماز(شان) آن دو را نگهدارید، آن گاه به خدا سوگند یاد کنند که: «ما این (حقّ) را به هیچ قیمتی نمی‌فروشیم، هرچند (پای) خویشاوند نزدیکترمان (در کار) باشد و شهادت الهی را کتمان نمی‌کنیم و بی‌گمان (اگر حق را کتمان کنیم) در این صورت از گناهکاران خواهیم بود.»'),
(776, 5, 107, 'و اگر دریافته شد که آن دو دستخوش گناهی پیگیر شدند، پس دو تن دیگر که بر آنان ستم رفته است و هر دو (به میّت) سزاوارترند به جای آن دو (شاهد پیشین) قیام کنند. پس به خدا سوگند یاد می‌کنند که: «گواهی ما همواره از گواهی آن دو شایسته‌تر است و (از حق) تجاوز نکرده‌ایم، اگر چنان کنیم از ستمکاران خواهیم بود.»'),
(777, 5, 108, 'این (روش) -برای اینکه شهادت را به صورت درستش ادا کنند یا بترسند که بعد از سوگند خوردنشان، سوگندهایی (به وارثان میّت) برگردانده شود- (به صواب) نزدیکتر است. و از خدا پروا بدارید و (این پندها را) بشنوید. و خدا گروه فاسقان را هدایت نمی‌کند.'),
(778, 5, 109, 'روزی که خدا پیامبران را گرد آوَرَد، پس فرماید: «چه پاسخی به شما داده‌شد؟» گویند: «ما هیچ نمی‌دانیم، همواره این تویی تو بسیار دانای رازهای نهان.»'),
(779, 5, 110, 'چون خدا فرمود: «ای عیسی پسر مریم! نعمتم را بر خود و بر مادرت به یاد آور: چون تو را با روح‌القُدُس تأیید کردم که در گهواره و (اینک) در میان‌سالی با مردم سخن (وحیانی) می‌گویی؛ و چون تو را کتاب و حکمت و تورات و انجیل آموختم؛ و چون به اذن من از گِل، (چیزی) شکل پرنده می‌ساختی، پس در آن می‌دمیدی و به اذن من پرنده‌ای می‌شد؛ و کور مادرزاد و پیس را به اذن من شفا می‌دادی؛ و آن‌گاه که مردگان را به اذن من (زنده از قبر) برون می‌آوردی؛ و چون (آسیب) بنی‌اسرائیل را- هنگامی که برای آنان حجت‌های آشکار آورده بودی - از تو بازداشتم، پس کسانی از آنان که کافر شده بودند گفتند این(ها چیزی) جز افسونی آشکارگر نیست.»'),
(780, 5, 111, '«و چون فراسوی حواریوّن وحی (غیررسالتی) کردم که به من و فرستاده‌ام ایمان آورید، گفتند: ایمان آوردیم، گواه باش به اینکه ما به‌راستی تسلیم شدگانیم.»'),
(781, 5, 112, 'چون حواریّون گفتند: «ای عیسی پسر مریم! آیا پروردگارت می‌تواند از آسمان، خوانی بر ما فرود آورد؟» (عیسی) گفت: «اگر مؤمن بوده‌اید از خدا پروا بدارید.»'),
(782, 5, 113, 'گفتند: «می‌خواهیم از آن بخوریم و دل‌هایمان (بدان) آرامش یابد و بدانیم که به ما راست گفته‌ای و بر آن از گواهان باشیم.»'),
(783, 5, 114, 'عیسی پسر مریم گفت: «بار خدایا! پروردگارمان! از آسمان، خوانی برایمان فرو فرست تا عیدی برای اوّل و آخرمان باشد و (نیز) نشانه‌ای از جانب تو (برای حواریان) و ما را روزی ده، حال آنکه تو بهترین روزی‌دهندگانی.»'),
(784, 5, 115, 'خدا فرمود: «من آن را بر شما فرو فرستنده‌ام، پس هرکس از شما بعد (از آن) انکار ورزد، بی‌گمان وی را (چنان) عذابی کنم که هیچ‌یک از جهانیان را (آن چنان) عذاب نکنم.»'),
(785, 5, 116, 'و چون خدا فرمود: «ای عیسی پسر مریم! آیا تو به مردم گفتی: “من و مادرم را (همچون) دو خدا به جای خدا(ی یکتا) بپرستید؟”» گفت: «منزّهی تو، مرا نسزد که (درباره‌ی خویشتن) چیزی را که حقّ من نیست بگویم. اگر آن را گفته بودم بی‌گمان همان را می‌دانستی. آنچه در ذات من است تو می‌دانی و آنچه در ذات توست من نمی‌دانم. به‌راستی تو خود بس دانای رازهای نهان هستی.»'),
(786, 5, 117, '«جز آنچه مرا بدان فرمان دادی (چیزی) به آنان نگفتم که: “خدا پروردگار من و پروردگار خودتان را عبادت کنید.” و تا هنگامی که در میانشان بودم بر آنان گواه بودم. پس چون مرا برگرفتی، تو خود بر آنان دیده‌بان بودی و تو بر هر چیزی گواهی.»'),
(787, 5, 118, '«اگر عذابشان کنی، پس آنان بندگان تو هستند و اگر برایشان بپوشانی، پس همواره تو خود توانای حکیم هستی.»'),
(788, 5, 119, 'خدا فرمود: «این روزی است که راستان را راستیشان سود بخشد؛ برایشان باغ‌هایی است که از زیر (درختان)شان نهرها روان است، حال آنکه در آن جاودانند. خدا از آنان خشنود است و آنان (نیز) از او خشنودند. این است رستگاری بی‌رنج بزرگ.»'),
(789, 5, 120, 'فرمانروایی آسمان‌ها و زمین و آنچه در آنهاست تنها از آنِ خداست و او بر هر چیزی تواناست.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, '(تمامی) ستایش‌ها خدایی را سزاست که آسمان‌ها و زمین را آفرید و تاریکی‌ها و روشنایی را نهاد. سپس کسانی که به پروردگارشان کفر ورزیدند، (غیر او را) با او برابر می‌شمرند.'),
(791, 6, 2, 'اوست کسی که شما را (در آغاز) از گل آفرید، سپس مدتی را مقرّر داشت و اَجَلی (حتمی) نامیده شده نزد اوست. سپس (همین) شما (در ربوبیت او) شکی دارید.'),
(792, 6, 3, 'و اوست خدا در آسمان‌ها و در زمین. نهانتان و آشکارتان را می‌داند. و آنچه را به دست می‌آورید می‌داند.'),
(793, 6, 4, 'و هیچ نشانه‌ای از نشانه‌های پروردگارشان برایشان نمی‌آید، مگر آنکه (آنان) اعراض‌کنندگان از آن بوده‌اند.'),
(794, 6, 5, 'پس همانا هنگامی که (حق) سویشان آمد با حق (همان را) تکذیب کردند. پس در آینده (حقیقتِ) خبرهای مهمِ آنچه را که به مسخره می‌گرفته‌اند، به آنان خواهد رسید.'),
(795, 6, 6, 'آیا ندیدند -در روزگارانی پیش از آنان- چه بسیار نسل‌هایی (از مردمان) را هلاک کردیم‌؟ در حالی‌که آنان را (به گونه‌ای) در زمین تمکّن و امکاناتی دادیم که برای شما آن تمکّن و امکانات را ندادیم و (باران‌ها و برف‌ها و بادهای) آسمان را پی‌درپی بر آنان فرو فرستادیم و رودبارها را از زیر جایگاهشان روان ساختیم. پس ایشان را به (همان) گناهانشان هلاک کردیم و پس از آنان نسل‌های دیگری پدید آوردیم.'),
(796, 6, 7, 'و اگر نوشته‌ای در کاغذی بر تو نازل می‌کردیم، پس آنان (هم) آن را با دست‌های خود لمس می‌کردند، کسانی که کفر ورزیدند همانا می‌گفتند: «این جز سحری آشکارگر نیست.»'),
(797, 6, 8, 'و گفتند: «چرا فرشته‌ای بر او نازل نشده‌؟» و اگر فرشته‌ای فرود می‌آوردیم، همواره کار(شان) تمام می‌شد؛ و پس از آن مهلت داده نمی‌شوند.'),
(798, 6, 9, 'و اگر او را فرشته‌ای قرار می‌دادیم، بی‌گمان وی را (به صورت) مردی در می‌آوردیم و (همانا او را) بر آنان مشتبه می‌ساختیم، چنان که (خودشان هم) آن را مشتبه می‌دارند.'),
(799, 6, 10, 'و بی‌گمان پیش از تو همانا پیامبرانی به مسخره گرفته شدند. پس آنچه را مسخره می‌کردند، همان، گریبان‌گیر کسانی شد که آنان را استهزا می‌کرده‌اند.'),
(800, 6, 11, 'بگو: «در زمین بگردید، سپس بنگرید (که) فرجام تکذیب‌کنندگان چگونه بوده است.»'),
(801, 6, 12, 'بگو: «آنچه در آسمان‌ها و زمین است از آنِ کیست‌؟» بگو: «از آنِ خداست.» رحمت را بر خودش مقرر گردانیده است، (که) بی‌گمان شما را فراسوی روز قیامت - که در آن هیچ شکّ مستندی نیست- به‌راستی، بدون برگشت گرد خواهد آورد. کسانی که خودشان را باخته (و) زیان کرده‌اند، (هم)آنان ایمان نمی‌آورند.'),
(802, 6, 13, 'و آنچه در شب و روز سکنی و سکونت دارد، تنها از آنِ اوست. و او بس شنوای بسیار داناست.'),
(803, 6, 14, 'بگو: «آیا جز خدا را- (که) پدید آورنده‌ی آسمان‌ها و زمین بر مبنای فطرت توحیدی است- به سرپرستی برگزینم‌؟ حال آنکه او خوراک می‌دهد و خوراک داده نمی‌شود.» بگو: «من به‌راستی مأمور شدم نخستین کسی باشم که تسلیم خدا شده و (نیز به من فرمان داده شده که:) “هرگز از مشرکان مباش”.»'),
(804, 6, 15, 'بگو: «اگر به پروردگارم عصیان ورزم ، بی‌گمان از عذاب روزی بزرگ هراسانم.»'),
(805, 6, 16, 'در چنان روزی، کسی که عذاب از او برگردانده شود، همانا خدا به او رحم کرده و این همان رستگاریِ بی‌زیانِ روشنگر است.'),
(806, 6, 17, 'و اگر خدا به تو زیانی رساند، پس هیچ کس جز او برطرف‌کننده‌ی آن (زیان) نیست و اگر خیری به تو رساند (نیز همین‌گونه است). پس او بر هر چیزی بسیار تواناست.'),
(807, 6, 18, 'و اوست که بر (سر و سامان) بندگانش چیره است و اوست بسی فرزانه‌ی آگاه.'),
(808, 6, 19, 'بگو: « گواهی چه چیزی (از همه) بزرگ‌تر است‌؟» بگو: «خدا میان من و شما گواهی است. و این قرآن فراسوی من وحی شد تا به وسیله‌ی آن، شما و هر کس را که بالغ شد و (قرآن) به او رسیده هشدار دهم‌؛ آیا به‌درستی و بی‌گمان شما گواهی می‌دهید که با خدا، خدایان دیگری هستند؟» بگو: «من گواهی نمی‌دهم.» بگو: «تنها او خدایی یگانه است و من همانا از آنچه (به او) شرک می‌ورزید بیزارم.»'),
(809, 6, 20, 'کسانی که کتاب (وحیانی) به آنان داده‌ایم، همان‌گونه که پسران خود را می‌شناسند او [:پیامبر و کتابش] را (هم) می‌شناسند. پس کسانی که خودشان را (باخته و) زیان کرده‌اند، (هم)آنان ایمان نمی‌آورند.'),
(810, 6, 21, 'و کیست ستمکارتر از کسی که بر خدا دروغی بست، یا با نشانه‌های(ربانی) او (همو و فرستادگانش را) تکذیب کرد‌؟ بی‌گمان، ستمکاران (خودشان و دیگران را) رستگار نمی‌کنند.'),
(811, 6, 22, 'و روزی همه‌ی آنان را گرد (هم) می‌آوریم، سپس به کسانی که شرک آوردند می‌گوییم: «کجایند شریکانتان که می‌پنداشته‌اید (شریکان خدایند)؟»'),
(812, 6, 23, 'سپس آشوب آتشبارشان (آشوبگریشان) جز این نبوده است که گفتند: «به خدا -پروردگارمان- سوگند که ما مشرک نبوده‌ایم.»'),
(813, 6, 24, 'بنگر، چگونه بر زیان خودهاشان دروغ گفتند و آنچه به گزاف (به خدا) نسبت می‌داده‌اند از ایشان گم شد.'),
(814, 6, 25, 'و برخی از آنان سوی تو گوش فرا می‌دارند، در حالی‌که ما بر دل‌هایشان پرده‌هایی افکندیم، تا آن را نفهمند و در گوش‌هایشان سنگینی (قرار داده‌ایم) و اگر هر (گونه) نشانه‌ای را ببینند به آن ایمان نمی‌آورند، تا هنگامی که نزد تو می‌آیند (و) با تو جدال می‌کنند. کسانی که کفر ورزیدند، گویند: «این (کتاب) چیزی جز افسانه‌ها، نوشته‌ها و بافته‌های پیشینیان نیست.»'),
(815, 6, 26, 'حال آنکه آنان (مکلفان را) از آن باز می‌دارند و (خود نیز) از آن دوری می‌کنند و جز خویشتن را به هلاکت نمی‌افکنند و باریک‌بینی نمی‌کنند.'),
(816, 6, 27, 'و کاش اینان را - هنگامی که بر آتش بازداشت شدند - ببینی. پس گویند: «کاش ما باز گردانده می‌شدیم و (تا دیگر)با آیات پروردگارمان (همان‌ها و او را) تکذیب نکنیم و از مؤمنان باشیم.»'),
(817, 6, 28, 'بلکه آنچه را پیش از آن نهان می‌داشته‌اند، برایشان آشکار شد و اگر (هم) بازگردانده شوند، حتماً به آنچه از آن منع شده بودند بر می‌گردند و بی‌گمان آنان همان دروغگویانند.'),
(818, 6, 29, 'و گفتند: «(زندگی) جز زندگیِ دنیای ما نیست و ما (از) برانگیخته شوندگان نیستیم.»'),
(819, 6, 30, 'و کاش بنگری چون برابر پروردگارشان بازداشت شدند و (خدا) فرمود: «آیا این حقّ نیست‌؟» گفتند: «آری، سوگند به پروردگارمان.» گفت: «پس به (سزای) آنچه کفر می‌ورزیدید، (این) عذاب را بچشید.»'),
(820, 6, 31, 'کسانی که به لقای الهی تکذیب کردند، همواره زیان دیدند، تا آن‌گاه که ساعت [:قیامت] به ناگاه آنان را در رسد، گویند: «دریغا بر ما، بر آنچه (درباره‌اش) کوتاهی کردیم.» حال آنکه آنان بار سنگین گناهانشان را بر پشت‌هایشان می‌کشند؛ هان! چه بد است باری که می‌کشند.'),
(821, 6, 32, 'و (این) پست‌ترین و نزدیکترین زندگی [:دنیا] جز بازیچه و بازدارنده‌ای (از حق‌) نیست و سرای واپسین همواره برای کسانی که پرهیزگاری می‌کنند بهتر است‌. آیا پس خردورزی نمی‌کنید؟'),
(822, 6, 33, 'به‌راستی، می‌دانیم که آنچه می‌گویند همانا تو را بسی سخت غمگین می‌کند. پس در واقع آنان تو را تکذیب نمی‌کنند، ولی (این) ستمکاران با آیات خدا (همان‌ها و او را) انکار می‌کنند.'),
(823, 6, 34, 'و بی‌گمان پیش از تو نیز پیامبرانی بسی تکذیب شدند، پس بر آنچه تکذیب شدند و آزار دیدند شکیبایی کردند، تا یاری ما به آنان در رسید. و برای کلمات خدا هیچ تبدیل‌کننده‌ای نیست و همانا برخی از اخبار مهم پیامبران به‌راستی به تو رسیده است.'),
(824, 6, 35, 'و گر چه اعراض کردن آنان بر تو گران است، پس اگر بتوانی رخنه‌ای در زمین یا نردبانی در آسمان بجویی، تا نشانه‌ای برایشان بیاوری (پس چنان کن). و اگر خدا می‌خواست حتماً آنان را بر هدایت گرد می‌آورد؛ پس هرگز از نادانان مباش.'),
(825, 6, 36, 'تنها کسانی (دعوت تو را) اجابت می‌کنند، که (سخن حقّ را) می‌شنوند. و (اما) مردگان را خدا (در قیامت) بر خواهد انگیخت، سپس تنها سوی او باز گردانیده می‌شوند.'),
(826, 6, 37, 'و گفتند: «چرا نشانه‌ای از جانب پروردگارش بر او نازل نشده است‌؟» بگو: «بی‌تردید، خدا تواناست بر اینکه نشانه‌ای فرو فرستد.» ولی بیشترشان نمی‌دانند.'),
(827, 6, 38, 'و هیچ جنبنده‌ای در زمین نیست و نه هیچ پرنده‌ای که با دو بال خود پرواز می‌کند، مگر آنکه (آنها نیز) امت‌هایی مانند شمایند. ما هیچ چیزی را در کتاب (تکوین و تشریع) فروگذار نکردیم. سپس (همه‌ی این جنبندگان) سوی پروردگارشان ناگریز و ناگزیر گردآورده خواهند شد.'),
(828, 6, 39, 'و کسانی که با آیات ما (همان‌ها و ما را) دروغ پنداشتند، در تاریکی‌ها(یِ کفر) کران و لالانند. هر که را خدا بخواهد گمراهش می‌کند و هر که را بخواهد بر راهی راست قرارش می‌دهد.'),
(829, 6, 40, 'بگو: «آیا خودتان را دیدید، اگر عذاب خدا شما را در رسد، یا ساعت [:قیامت] شما را فرا رسد؟ در آن هنگام اگر (از) راستان بوده‌اید، آیا (باز هم) غیرِ خدا را می‌خوانید؟»'),
(830, 6, 41, '(نه!) بلکه تنها او را می‌خوانید. پس اگر بخواهد (بطلان) آنچه (خود و مردمان را) به سوی آن فرا می‌خواندید آشکار می‌سازد. حال آنکه آنچه را شریک (او) می‌گردانید فراموش می‌کنید.'),
(831, 6, 42, 'و ما به‌راستی سوی امّت‌هایی که پیش از تو بودند همواره (پیامبرانی) فرستادیم. پس آنان را به تنگنایی و زیان‌باری دچار ساختیم، شاید ایشان به (خاکساری و) زاری درآیند.'),
(832, 6, 43, 'پس چرا هنگامی که عذاب ما به آنان رسید تضرّع نکردند؟ ولی (حقیقت این است که) دل‌هایشان سخت شده و شیطان آنچه را انجام می‌داده‌اند برایشان آراسته است'),
(833, 6, 44, 'پس چون آنچه را که بدان تذکّر داده شده بودند فراموش کردند، درهای هر چیزی (از نعمت‌های ظاهری) را بر آنان گشودیم، تا هنگامی که به آنچه داده شدند شاد گردیدند. ناگهان (گریبان) آنان را گرفتیم، پس آن‌گاه آنان (از شدت گرفتاری) غم‌زدگان و نومیدانند.'),
(834, 6, 45, 'پس ریشه‌ی آن گروهی که ستم کردند برکنده شد. و هر ستایشی برای خدا -پروردگار جهانیان- است.'),
(835, 6, 46, 'بگو: «آیا دیدید، اگر خدا شنوایی‌تان و دیدگانتان را برگیرد و بر دل‌هایتان مُهر نهد، آیا غیر از خدا کدام معبودی است که آنها را برایتان باز آورد؟» بنگر چگونه آیات (خود) را گوناگون (بیان) می‌کنیم، سپس همانان به شدت رویگردانند.'),
(836, 6, 47, 'بگو: «آیا خود را دیدید (که) اگر عذاب خدا ناگهان یا آشکارا به شما در رسد، آیا جز گروه ستمگران (کسی) هلاک خواهد شد؟»'),
(837, 6, 48, 'و ما پیامبران (خود) را جز بشارتگر و هشداردهنده نمی‌فرستیم. پس هر کس که ایمان آوَرَد و اصلاح کند بیمی بر آنان نیست و نه ایشان اندوهگین می‌شوند.'),
(838, 6, 49, 'و کسانی که با آیاتمان (همان‌ها و ما را) تکذیب کردند، به (سبب) آنکه نافرمانی می‌کرده‌اند، عذاب به آنان خواهد رسید.'),
(839, 6, 50, 'بگو: «به شما نمی‌گویم گنجینه‌های خدا نزد من است و غیب (هم) نمی‌دانم و به شما نمی‌گویم من به‌راستی فرشته‌ام. (من) جز آنچه را که به سویم وحی می‌شود پیروی نمی‌کنم.» بگو: «آیا نابینا و بینا یکسانند؟ پس آیا تفکّر نمی‌کنید؟»'),
(840, 6, 51, 'و با این (قرآن) - کسانی را که بیم دارند سوی پروردگارشان محشور شوند - هشدار ده (چرا که) غیر از او برای آنها ولی و شفیعی نیست‌؛ شاید پروا کنند.'),
(841, 6, 52, 'و کسانی را که پروردگار خود را بامدادان و شامگاهان می‌خوانند - در حالی‌که چهره‌ی (خشنودی و شناخت) او را می‌خواهند - (از خود) مران. از حساب آنان چیزی بر عهده‌ی تو نیست و از حساب تو (نیز) چیزی بر عهده‌ی آنان نیست تا ایشان را برانی؛ پس از ستمکاران باشی.'),
(842, 6, 53, 'و بدین‌گونه ما برخی از آنان را با برخی دیگر آزمونی آتشین کردیم تا گویند: «آیا (هم) اینانند از میان ما (که) خدا بر ایشان منّت نهاده است‌؟» آیا خدا به (حال) شاکران داناتر (از دیگران) نیست‌؟'),
(843, 6, 54, 'و هنگامی‌که کسانی به آیات ما ایمان می‌آورند نزدت آیند، بگو: «درود بر شما؛ پروردگارتان رحمت را بر خود همی نبشته، که هر کس از شما همانا به نادانی کار بدی کند، سپس به توبه و اصلاح گراید، پس (از آن) خدا بی‌گمان بسی پوشنده‌ی رحمتگر بر ویژگان است.»'),
(844, 6, 55, 'و این‌گونه، آیات (خود) را بیان می‌کنیم و برای اینکه راه و رسم مجرمان به خوبی روشن شود.'),
(845, 6, 56, 'بگو: «من همواره نهی شده‌ام کسانی را که شما غیر از خدا می‌خوانید بپرستم!» بگو: «من هوس‌هایتان را پیروی نمی‌کنم. (زیرا اگر چنین کنم) در این هنگام (و هنگامه) همواره گمراه شده‌ام و از راه‌یافتگان نیستم.»'),
(846, 6, 57, 'بگو: «من به‌راستی از جانب پروردگارم (مسلط) بر دلیلی آشکار هستم، در حالی‌که شما با آن (همان و او را) تکذیب کردید. آنچه را (شما) به شتاب خواستارید نزد من نیست. فرمان جز برای خدا نیست (که او) حقّ را بازگو می‌کند. و او بهترین فیصله‌دهندگان است.»'),
(847, 6, 58, 'بگو: «اگر آنچه را با شتاب خواستارید نزد من بود، بی‌گمان میان من و شما کار به پایان رسیده بود و خدا به (حال) ستمکاران داناتر (از دیگران) است.»'),
(848, 6, 59, 'و درب‌های غیب، تنها نزد اوست، (و) جز او (کسی) آنها را نمی‌داند و آنچه در خشکی و دریاست می‌داند و هیچ برگی فرو نمی‌افتد مگر (اینکه) آن را می‌داند و هیچ دانه‌ای (نهفته) در تاریکی‌های زمین و هیچ تر و خشکی نیست، مگر اینکه در کتابی روشنگر (ثبت) است.'),
(849, 6, 60, 'و اوست کسی که شبانگاه، (روح انسانی) شما را (به هنگام خواب) برمی‌گیرد، حال آنکه آنچه را در روز (از بد و خوب) انجام داده‌اید می‌داند. سپس شما را در آن برمی‌انگیزد، تا مدتی مشخص به سرآید. پس (آن‌گاه) بازگشت شما تنها سوی اوست. سپس شما را به آنچه می‌کرده‌اید آگاهی بزرگ خواهد داد.'),
(850, 6, 61, 'و تنها اوست که بر (سر و سامان) بندگانش قاهر (و غالب) است و نگهبانانی بر شما می‌فرستد، تا هنگامی که یکی از شما را مرگ فرا رسد؛ (آن‌گاه) فرشتگان ما او را (از این جهان) کامل و بی‌کم و کاست بستانند، در حالی که کوتاهی نمی‌کنند.'),
(851, 6, 62, 'سپس سوی خدا - مولای بحق‌شان - برگردانیده شوند. حال آنکه حکم (تکوین و تشریع) تنها برای اوست و او سریع‌ترین حسابگران است.'),
(852, 6, 63, 'بگو: «چه کسی شما را از تاریکی‌های خشکی و دریا می‌رهاند؟ در حالی‌که او را به زاری و در نهان می‌خوانید: “اگر به‌راستی ما را از این (مهلکه) برهاند، بی‌چون (و) بی‌گمان از سپاسگزاران خواهیم بود.”»'),
(853, 6, 64, 'بگو: «خداست که شما را از آن (تاریکی‌ها) و از هر نگرانی و نابسامانی می‌رهاند، سپس (همین) شما (باز هم) شرک می‌ورزید.»'),
(854, 6, 65, 'بگو: «همو تواناست بر اینکه از بالای (سر)تان، یا از زیر پاهایتان عذابی بر شما بفرستد، یا شما را لباس تفرقه بپوشاند (و گروه گروه به جان هم اندازد) و گرفتاری بعضی از شما را به بعضی (دیگر) بچشاند.» بنگر (که) چگونه آیات (خود) را گوناگون باز (و ممتاز) بیان می‌کنیم، شاید آنان دریابند.'),
(855, 6, 66, 'و قوم تو با آن [:قرآن] (همان را) تکذیب کردند، در حالی‌که آن تمامی حقّ است. بگو: «من بر شما نگهبان نیستم.»'),
(856, 6, 67, 'برای هر خبری مهم قرارگاهی است و در آینده‌ای دور خواهید دانست.'),
(857, 6, 68, 'و هنگامی که ببینی کسانی (به قصد تخطئه) در ژرفای مشکلاتی در آیات ما غرقه می‌شوند، از ایشان روی برتاب، تا در سخنی دیگر فرو روند. و اگر شیطان پس از یادواره(ات) تو را (در این باره) بی‌چون به فراموشی اندازد، (دیگر) با ستمکاران منشین.'),
(858, 6, 69, 'و چیزی از حساب آنان [:ستمکاران] بر عهده‌ی کسانی که (از خدا) پروا دارند نیست، لیکن یادواره‌ای است، شاید (از این هم‌نشینان بپرهیزند و) پروا بدارند.'),
(859, 6, 70, 'و کسانی را که دین خود را به بازی و انحراف گرفتند و (این) پست‌ترین زندگی آنان را فریفته است، رها کن و (مردم را) با این (قرآن) اندرز ده، مبادا کسی با دستاوردش به هلاکت افتد، در حالی که برای او از غیر خدا سرپرستی نباشد و نه شفاعتگری. و اگر (برای رهایی خود) هرگونه جایگزینی (همسنگ خود) دهد، از او گرفته نشود. اینانند که با آنچه به دست آورده‌اند به هلاکت افتادند و به (سبب) آنچه کفر می‌ورزیدند، آشامیدنی‌ای از آب جوشان و عذابی پر درد خواهند داشت.'),
(860, 6, 71, 'بگو: «آیا پس از خدا چیزی را (به خدایی) بخوانیم که نه سودی به ما می‌رساند و نه زیانی می‌دهد و پس از اینکه خدا ما را هدایت کرده از عقیده‌ی خود به گذشته‌ها(ی تاریک)مان باز گردیم‌؟ مانند کسی که شیطان‌ها او را در زمین از راه به در بردند، حال آنکه حیران (بر جای مانده) است‌؟» برای او یارانی است که وی را سوی راه راست می‌خوانند (که:) «سوی ما بیا.» بگو: «هدایتِ خداست که به‌راستی هدایت (راستین) است و (ما) دستور یافته‌ایم که تسلیم پروردگار جهانیان باشیم.»'),
(861, 6, 72, 'و اینکه نماز بر پا دارید و از او بهراسید و (هم)اوست که تنها به سویش ناگزیر گرد آورده خواهید شد.'),
(862, 6, 73, 'و اوست کسی که آسمان‌ها و زمین را به حقّ آفرید و روزی که (به چیزی) گوید «شو» پس می‌شود. سخنش(تمامی) حقّ است. روزی که در صور (با صدایی جانکاه و جان افزا) دمیده شود، فرمانروایی تنها از آنِ اوست. داننده‌ی غیب و شهود است و او حکیم بسیار آگاه است.'),
(863, 6, 74, 'و چون ابراهیم به پدر (تربیتی) خود -آزر- گفت: «آیا بتان را معبودان (خود و دیگران) می‌گیری‌؟ من همانا تو و گروهت را در (ژرفای) گمراهی آشکارگری می‌بینم.»'),
(864, 6, 75, 'و بدین‌سان، ملکوت و حقیقت آسمان‌ها و زمین را به ابراهیم (در حد توانش‌) می‌نمایانیم و برای اینکه از یقین‌کنندگان (به حقیقت ملکوتی از جریان زنده کردن مردگان) باشد.'),
(865, 6, 76, 'پس چون شب بر او پرده افکنْد، ستاره‌ای دید (و) گفت: «این پروردگار من است!» آن‌گاه که غروب کرد، گفت: «غروب‌کنندگان را دوست نمی‌دارم.»'),
(866, 6, 77, 'پس چون ماه را در حال برآمدنی فراگیر دید، گفت: «این پروردگار من است!» پس چون ناپدید شد گفت: «اگر پروردگارم مرا هدایت نکند بی‌گمان همواره از گروه گمراهان خواهم بود.»'),
(867, 6, 78, 'پس چون خورشید را بر آمده و فراگیر دید، گفت: «این پروردگار من است! این بزرگ‌تر است!‌» و هنگامی که (آن هم) پنهان شد، گفت: «ای گروه من! من از آنچه (برای خدا) شریک می‌سازید بسی بیزارم.»'),
(868, 6, 79, '«من از روی اخلاص، پاکدلانه روی خود را سوی کسی گردانیده‌ام که آسمان‌ها و زمین را به فطرت توحیدی پدید آورد و (من‌) از مشرکان نیستم.»'),
(869, 6, 80, 'و (چون) گروهش با او به ستیزه پرداختند، گفت: «آیا با من درباره‌ی خدا محاجّه می‌کنید؟ حال آنکه همواره او مرا راهنمایی کرده است. و من از آنچه شریک او می‌سازید بیمی ندارم، مگر آنکه پروردگارم چیزی بخواهد. علم پروردگارم بر هر چیزی فراگیر است. آیا پس (از این همه برهان) متذکّر نمی‌شوید؟»'),
(870, 6, 81, '«و چگونه از آنچه شریک (خدا) پنداشته‌اید بترسم، حال آنکه شما خود از اینکه چیزی را شریک خدا ساخته‌اید - که (خدا) دلیلی سلطه‌جو درباره‌ی آن بر شما فرو نفرستاده است - نمی‌هراسید؟ پس اگر می‌دانسته‌اید، کدام یک از (ما) دو دسته به ایمنی سزاوارتر است‌؟»'),
(871, 6, 82, 'کسانی‌که ایمان آورده و ایمان خود را به ستم نیالودند، ایشان برایشان آرامش است و (هم)اینان راه یافتگانند.'),
(872, 6, 83, 'و آن حجت ماست که به ابراهیم علیه قومش دادیم. (و ما) هر کس را بخواهیم (به‌) درجاتی فرامی‌بریم. همواره پروردگارت حکیمی بس داناست.'),
(873, 6, 84, 'و برای او اسحاق و یعقوب را بخشیدیم (و) همه را (به راه راست وحیانی) هدایت کردیم و نوح را از پیش راه نمودیم و از فرزندانش، داوود و سلیمان و ایّوب و یوسف و موسی و هارون را (نیز). و این‌گونه، نیکوکاران را پاداش می‌دهیم.'),
(874, 6, 85, 'و زکریّا و یحیی و عیسی و الیاس را (که) همه‌ی آنان از شایستگانند.'),
(875, 6, 86, 'و اسماعیل و یَسَع و یونس و لوط و همگی را بر جهانیان برتری دادیم.'),
(876, 6, 87, 'و از پدران و فرزندان و نوادگان و برادارنشان (برخی را بر جهانیان برتری دادیم) و آنان را برگزیدیم و به راهی راست راهنماییشان کردیم.'),
(877, 6, 88, 'این هدایت خداست که هر کس از بندگانش را بخواهد، بدان هدایت می‌کند و اگر آنان (بر فرض محال‌) شرک آورده بودند، همواره آنچه به دست آورده بودند از دستشان می‌رفت.'),
(878, 6, 89, 'اینانند کسانی که کتاب و حاکمیت و نبوّتشان دادیم. پس اگر آنان [:مشرکان] بدان کفر ورزند، بی‌گمان، گروهی (دیگر) را عهده‌دار کرده‌ایم که به آن کافر نیستند.'),
(879, 6, 90, 'اینانند کسانی که خدا هدایت(شان) کرده. پس به راه راهوارشان اقتدا کن. بگو: «من از شما هیچ مزدی بر این (رسالت قرآنی) نمی‌طلبم. این جز یادواره‌ای برای جهانیان نیست.»'),
(880, 6, 91, 'و چون (یهودیان) گفتند: «خدا چیزی (وحیانی) بر بشری نازل نکرده‌»، قدر (الوهیت و ربوبیت) خدا را چنان‌که شایسته‌ی اوست نشناختند. بگو: «چه کسی آن کتابی را که موسی با آن (سوی شما) آمد نازل کرد‌؛ حال آنکه برای مردمان نور و رهنمودی است (و) شما آن را به صورت کاغذهایی (بی‌محتوا) نهاده و آشکار می‌کنید و بسیاری (از آن) را (هم) پنهان می‌دارید، در صورتی که چیزی که نه شما می‌دانستید و نه پدر‌‌انتان، به شما به خوبی آموخته شد؟» بگو: «خدا (و بس).» سپس آنها را (به حالشان) واگذار تا در فرورفتگی‌ای فراگیر (باطل) بازی کنند.'),
(881, 6, 92, 'و این خجسته کتابی است (که) ما آن را فرو فرستادیم حال آنکه کتاب‌هایی را که پیشاپیش آن است، تصدیق‌کننده است و برای اینکه (مردمِ) امّ‌القری [:مکّه] و کسانی را که در (سراسر جهانِ تکلیف) پیرامون آنند هشدار دهی. و کسانی که به آخرت ایمان می‌آورند، به آن (قرآن) ایمان می‌آورند، حال آنکه آنان بر نمازشان نگهبانی می‌کنند.'),
(882, 6, 93, 'و کیست ستمکارتر از کسی که بر خدا به گزافی افترا بسته، یا بگوید: «به من وحی شده‌» در حالی که چیزی به او وحی نشده؟ و (نیز) آن کس که گفت: «به زودی همانند آنچه را خدا نازل کرده نازل می‌کنم‌»؟ و کاش ستمکاران را در گرداب‌های مرگ بنگری، حال آنکه فرشتگان (به سوی آنان) دست‌هایشان را گشوده‌اند (و نهیب می‌زنند:) «جان‌هایتان را بیرون دهید. امروز به آنچه بناحقّ بر خدا دروغ می‌بسته‌اید و از آیات او تکبّر می‌کرده‌اید، به عذاب خوارکننده کیفر می‌یابید.»'),
(883, 6, 94, '«و همان‌گونه که شما را نخستین بار آفریدیم، بی‌چون (اکنون نیز) تنها همواره سوی ما آمدید و آنچه را به شما با مسئولیت دادیم پشت سر خود نهادید و شفیعانتان را که در (میان) خودتان، شریکان (خدا) می‌پنداشتید، با شما نمی‌بینیم. همانا پیوند شما (از اینان) بس بریده شده و آنچه را که می‌پنداشته‌اید از شما گم گشته است.»'),
(884, 6, 95, '«خدا همواره شکافنده‌ی هر دانه و هسته است. زنده را از مرده بیرون می‌آورد و مرده را از زنده بیرون آورنده است. این است خدا(یتان). پس کجا به گزاف رانده (و پراکنده) می‌شوید؟»'),
(885, 6, 96, '(هموست که) شکافنده‌ی صبحدم است و شب را برای آرامش و خورشید و ماه را معیار حساب قرار داد. این اندازه‌گیریِ (همان) عزیز بس داناست.'),
(886, 6, 97, 'و اوست کسی که ستارگان درخشان را برایتان نهاد، تا با آنها در تاریکی‌های خشکی و دریا راه یابید. ما همواره، این نشانه‌ها را برای گروهی که می‌دانند (به روشنی) گونه‌گون، جداسازی (و بیان) کرده‌ایم.'),
(887, 6, 98, 'و او کسی است که شما را (در آغاز) از یک تن پدیدآورد، پس (برای شما) قرارگاه و جایگاه امانتی (مقرر کرد). بی‌تردید، ما آیات (خود) را برای مردمی که با بررسی درست می‌فهمند (به روشنی بیان و) جداسازی کردیم.'),
(888, 6, 99, 'و اوست کسی که از آسمان آبی فرود آورد؛ پس با آن هرگونه روییدنی را (از زمین) برون آوردیم؛ پس (از آن گیاه) جوانه‌ی سبزی خارج ساختیم (که) از آن، دانه‌های متراکم روی هم چیده‌شده، برمی‌آوریم و از شکوفه‌ی درخت خرما خوشه‌هایی نزدیک به هم و (نیز) باغ‌هایی از انگور و زیتون و انار- همانند و ناهمانند- (برون آوردیم). هنگامی که ثمر دهد، به میوه‌اش و به رسیدنش بنگرید. همواره در این‌ها به‌راستی برای مردمی که ایمان می‌آورند نشانه‌هاست.'),
(889, 6, 100, 'و برای خدا، جنیان را شریکانی قرار دادند، حال آنکه خدا آنان را خلق کرده است. و برای او به نادانی، پسران و دخترانی تراشیدند. او از آنچه وصف می‌کنند پاک و برتر است.'),
(890, 6, 101, 'پدید آورنده‌ی آغازین آسمان‌ها و زمین بدون هیچ‌گونه نمونه و سابقه‌ای، کِی برایش فرزندی است، حال آنکه برای او همسری نبوده‌است و هر چیزی را آفرید و او به هر چیزی بسیار داناست.'),
(891, 6, 102, 'این خداست، پروردگار شما (که) هیچ معبودی جز او نیست (و) آفریننده‌ی هر چیزی است. پس او را بپرستید، حال آنکه او بر(سروسامان) هر چیزی کارساز است.'),
(892, 6, 103, 'دیده‌ها(ی سر، سِرّ) او را نمی‌یابند، حال آنکه او (کل) دیدگان را درمی‌یابد و او همان نادیدنی باریک‌بین (و) بسی آگاه است.'),
(893, 6, 104, 'به‌راستی بینش‌ها و دیدگاه‌هایی از جانب پروردگارتان برایتان آمد. پس هر کس به دیده‌ی بصیرت بنگرد، به سود خود اوست و هر کس از سر بصیرت ننگرد به زیان خود اوست و من بر شما نگهبانی نیستم.'),
(894, 6, 105, 'و این‌گونه آیات (خود) را گوناگون بیان می‌کنیم و (بگذار) تا بگویند تو (نزد دگران) درس خوانده‌ای‌؛ و تا اینکه آن را برای گروهی که می‌دانند روشن سازیم.'),
(895, 6, 106, 'از آنچه از پروردگارت به تو وحی شده پیروی کن (که) هیچ معبودی جز او نیست و از مشرکان روی بگردان.'),
(896, 6, 107, 'و اگر خدا می‌خواست، آنان شرک نمی‌آوردند و ما تو را بر ایشان نگهبانی ننهادیم و تو وکیل (ما) بر آنان نیستی.'),
(897, 6, 108, 'و آنهایی را که جز خدا را می‌خوانند دشنام مدهید تا (مبادا) آنان (هم) از روی دشمنی، به نادانی، خدا را دشنام دهند. این‌گونه برای هر گروهی کردارشان را آراستیم. سپس بازگشتشان -بدون برگشت- سوی پروردگارشان خواهد بود، پس (خدا) ایشان را از آنچه کرده‌اند آگاهی بزرگی خواهد داد.'),
(898, 6, 109, 'و با سوگندهای سختشان به خدا سوگند خوردند (که) اگر به‌راستی نشانه‌ای برای آنان بیاید، همواره بی‌گمان به آنها ایمان می‌آورند. بگو: «نشانه‌ها تنها نزد خداست.‌» و چه (چیزی) شما را می‌فهماند؟ اگر نشانه‌هایی‌- که می‌خواهند- (برایشان) بیاید، باز (هم) ایمان نمی‌آورند.'),
(899, 6, 110, 'و دل‌ها و دیدگانشان را واژگون کنیم - چنان‌که نخستین بار (هم) به آن (نشانه)ها ایمان نیاوردند- و رهایشان می‌کنیم تا در ژرفای طغیانشان سرگردان بمانند.'),
(900, 6, 111, 'و اگر بی‌گمان فرشتگان را پیاپی سویشان فرو فرستیم و مردگان با آنان سخن گویند و همه چیز را (به عنوان گواه بدون برگشت) دسته دسته در برابر آنان گرد آوریم، باز هم چنان نبوده‌اند که ایمان آورند- جز آنکه خدا بخواهد- ولی بیشترشان نادانی می‌کنند.'),
(901, 6, 112, 'و این‌گونه برای هر پیامبری برجسته، دشمنانی- شیطان‌های انس و جنّ - را نهادیم (که) بعضی از آنها به بعضی برای فریب (یکدیگر) گفتار آراسته را رمز می‌دهند و اگر پروردگارت بخواهد، چنان نمی‌کنند. پس آنان را با آنچه (به خدا) افترا می‌بندند واگذار.'),
(902, 6, 113, 'و (چنان مقرر شده است) تا کسانی که به آخرت ایمان نمی‌آورند، به آن (سخن باطل،گوش) دلشان را فرادارند و برای اینکه آن را بپسندند و تا اینکه آنچه را بی مهابا به دشواری به دست آورنده‌اند، به دست آورند.'),
(903, 6, 114, 'پس آیا داوری جز خدا برجویم‌؟ حال آنکه اوست که این کتاب را به تفصیل سویتان نازل کرده و کسانی که کتاب (آسمانی) بدیشان دادیم، می‌دانند که آن از جانب پروردگارت (تمامی) حق (و) به تدریج فرو فرستاده شده‌است. پس تو هرگز از تردیدکنندگان مباش.'),
(904, 6, 115, 'و سخن پروردگارت [:قرآن] در حال راستی و عدالت تمام شده است و هیچ تغییر دهنده‌ای (حتی خدا) برای کلمات او نیست و او بسی شنوای بسیار داناست.'),
(905, 6, 116, 'و اگر از بیشتر کسانی که در زمین (تکلیف)اند پیروی کنی، تو را از راه خدا به بیراهه می‌برند. آنان جز از گمان پیروی نمی‌کنند و جز به حدس و گمان (بیجا) نمی‌پردازند.'),
(906, 6, 117, 'همواره پروردگارت، (هم)او به (حال) کسی که از راهش منحرف می‌شود داناتر است و او به (حال) راه‌یافتگان (نیز) داناتر است.'),
(907, 6, 118, 'پس، اگر به آیات او ایمان داشته‌اید، از آنچه نام خدا (به هنگام کشتن) بر آن برده شده است، بخورید.'),
(908, 6, 119, 'و شما را چه شده که از آنچه نام خدا بر آن برده شده نمی‌خورید؟ حال آنکه همواره خدا، آنچه را بر شما حرام کرده، به تفصیل (در قرآن) بیان کرده است، جز آنچه (از محرمات) که بدون اختیار به آن ناچار شده‌اید (که به اندازه‌ی ضرورت بر شما حلال است). و بی‌چون بسیاری (از مردمان، دیگران را) از روی نادانی، با هوس‌های خود گمراه می‌کنند. بی‌امان پروردگارت، (هم)او به (حال) تجاوزکاران داناتر است.'),
(909, 6, 120, 'و آشکار و پنهانِ گناه- دورکننده‌ی از خیرات- را رها کنید، زیرا کسانی که به (این) گناه آلوده می‌شوند، به زودی - در برابر آنچه نابسامان با کاوش به دست می‌آورده‌اند - کیفر خواهند یافت.'),
(910, 6, 121, 'و از آنچه نام خدا بر آن برده نشده است مخورید، و (خوردن) آن بی‌گمان سرپیچی سختی (از فرمان خدا) است و همانا شیطان‌ها به دوستان خود همواره وسوسه می‌کنند، تا با شما ستیزه نمایند و اگر اطاعتشان کنید، بی‌گمان شما (هم) بی‌چون (به گونه‌ای) مشرکانید.'),
(911, 6, 122, 'آیا و کسی که مرده (دل) بوده، پس زنده (دل)اش گردانیدیم و برای او نوری نهادیم تا در پرتو آن در میان مردمان راه برود، همچون کسی است که نمود و نمادش در تاریکی‌هاست (که) از آن‌ها بیرون آمدنی نیست‌؟ این‌گونه برای کافران آنچه را انجام می‌داده‌اند زینت داده شده است.'),
(912, 6, 123, 'و بدین‌گونه، در هر مجتمعی بزرگانشان را (از) مجرمان نهادیم، تا در آن به نیرنگ پردازند و آنان جز به خودشان نیرنگ نمی‌زنند و باریک‌بینی نمی‌کنند.'),
(913, 6, 124, 'و چون نشانه‌ای (ربانی) برایشان بیاید گویند: «هرگز ایمان نمی‌آوریم تا آنکه نظیر آنچه به فرستادگان خدا داده شده است به ما (نیز) داده شود.» خدا بهتر می‌داند رسالتش را کجا و چگونه قرار دهد. زودا کسانی را که مرتکب جرم شدند - به (سزای) آنچه نیرنگ می‌کردند - در پیشگاه خدا خواری و بی‌مقداری و شکنجه‌ای سخت خواهد رسید.'),
(914, 6, 125, 'پس کسی را که خدا بخواهد هدایتش کند، سینه‌اش را برای اسلام می‌گشاید و هر کس را که بخواهد گمراهش کند، سینه‌اش را سخت تنگ می‌گرداند، (چنان که) گویی به زحمت در آسمان بالا می‌رود. این‌گونه خدا پلیدی را بر کسانی که ایمان نمی‌آورند می‌نهد.'),
(915, 6, 126, 'و این راه راست پروردگار تو است. ما آیات (خود) را برای گروهی که (حقیقت را) به خوبی به یاد می‌آورند جداسازی (و بیان) نموده‌ایم.'),
(916, 6, 127, 'برایشان، نزد پروردگارشان سرای سلامت است و به سبب آنچه انجام می‌داده‌اند، (هم)او سرورشان است.'),
(917, 6, 128, 'و روزی که همه‌ی آنان را گرد می‌آورد (فرماید:) «ای گروه جنّیان، از آدمیان (پیروان) فراوان سویتان کشاندید.» و سرپرستان انسانیشان گفتند: «پروردگارمان! برخی از ما از بعضی دیگر بسی بهره‌برداری کردیم و به زمان پایانیمان که برایمان معیّن کردی رسیدیم.» (خدا) فرمود: «آتش پایگاهتان است، در آن ماندگارید، مگر آنچه را خدا خواست (که خود تخفیف دهد). به‌راستی، پروردگارت فرزانه‌ای بسیار داناست.»'),
(918, 6, 129, 'و این‌گونه برخی از ستمکاران را به آنچه به دست می‌آورده‌اند، در پی برخی دیگرشان می‌گردانیم.'),
(919, 6, 130, 'ای گروه جنیان و انسان! آیا از میان شما فرستادگانی برایتان نیامدند، در حالی که آیات مرا بر شما می‌خوانند و از دیدار این روزتان شما را هشدار می‌دهند؟ گفتند: «ما بر (زیان) خود گواهی دادیم.‌» (آری، آمدند) و زندگی دنیا فریبشان داد و بر ضدّ خود گواهی دادند که همواره ایشان کافران بوده‌اند.'),
(920, 6, 131, 'این (هشدار) بدین سبب است، که پروردگارت هیچ گاه مجتمعات را به ستم هلاک‌‌‌کننده نبوده، در حالی که مردمشان ناآگاهند.'),
(921, 6, 132, 'و برای هر یک (از مکلفان) از آنچه انجام داده‌اند، درجاتی است و پروردگارت از آنچه می‌کنند غافل نیست.'),
(922, 6, 133, 'و پروردگارت (تنها) بی‌نیازِ کانون رحمت است. اگر بخواهد شما را می‌برد و پس از شما، هر چه را بخواهد جانشین (شما) می‌آورد؛ چنان که شما را از نسل گروهی دیگر پدید آورده است.'),
(923, 6, 134, 'همواره آنچه به شما وعده داده می‌شود به‌راستی آمدنی است و شما درمانده کنندگان (خدا) نیستید.'),
(924, 6, 135, 'بگو: «ای قوم من! هر چه در توانتان است انجام دهید، من (هم) همواره (در خور توانم) انجام‌دهنده‌ام. پس دیرا که خواهید دانست زندگی پایانی این خانه(ی دنیا) از آنِ کیست. بی‌گمان، ستمکاران رستگار نمی‌کنند.»'),
(925, 6, 136, 'و (مشرکان) برای خدا - از آنچه از کِشت و دام‌ها که پدید آورده است - سهمی گذاشتند، و به پندار خودشان گفتند: «این ویژه‌ی خداست و این ویژه‌ی شریکانمان.» پس آنچه ویژه‌ی شریکانشان بود به خدا نرسد و آنچه در اختصاص خدا بود به شریکانشان رسد. چه بد داوری می‌کنند!'),
(926, 6, 137, 'و این‌گونه برای بسیاری از مشرکان، شریکانشان، کشتن فرزندانشان را آراستند، تا (در پایان) هلاکشان کنند و دینشان را بر آنان مشتبه سازند و اگر خدا می‌خواست چنان نمی‌کردند. پس ایشان را با آنچه (به خدا) افترا می‌زنند واگذار.'),
(927, 6, 138, 'و گفتند: «این‌ها دام‌ها و کشتزارهایی است ممنوع (و) جز کسی که ما بخواهیم» - به گمانشان‌- «آنها را نمی‌خورد»و دام‌هایی است که (سوار شدن بر) پشت آنها حرام شده است و دام‌هایی که (هنگام ذبح) از روی افترا بر خدا نام خدا را بر آنها نمی‌برند. به زودی خدا آنان را به خاطر آنچه افترا می‌بسته‌اند کیفر می‌دهد.'),
(928, 6, 139, 'و گفتند: «آنچه در شکم‌های این چهارپایان است، اختصاص به مردان ما دارد و بر همسرانمان حرام است و اگر (آن جنین) مرده باشد، همه‌ی آنان (از زنان و مردان) در آن شریکند.» به زودی او [:خدا] توصیف آنان را جزا خواهد داد (و) همواره او حکیمی بسیار داناست.'),
(929, 6, 140, 'بی‌گمان کسانی که از روی سبک‌مغزی و نادانی، فرزندان خود را کشتند و آنچه را خدا روزیشان کرده بود - از روی افترای به خدا - حرام شمردند، سخت زیان کرده‌اند (و) آنان همواره گمراه شدند و (از) هدایت‌یافتگان نبوده‌اند.'),
(930, 6, 141, 'و اوست کسی که باغ‌هایی با داربست‌ها و بدون داربست‌ها و خرما‌بُن و کشتزار با خوراک گوناگون آن و زیتون و انار را- همانند و ناهمانند- پدید آورد و سازمان داد. از میوه‌ی آن - چون ثمر داد - بخورید و حقّ [:زکات بینوایان] را از آن به روزِ بهره‌برداری بدهید، و زیاده‌روی مکنید (که‌) همواره خدا اسرافکاران را دوست نمی‌دارد.'),
(931, 6, 142, 'و (نیز) از دام‌ها، حیوانات باربر و سواری را (پدید آورد). از آنچه خدا روزیتان کرده است بخورید، و گام‌های شیطان را پیروی مکنید (که) همانا او برای شما دشمنی آشکارگر است.'),
(932, 6, 143, '(انعامی را که‌) هشت زوج [:فرد قرین‌]، مشابه (آفرید و بر شما حلال کرد) : از گوسفند دو تا، از بز دو تا. بگو: «آیا خدا نرهای آن دو را حرام کرده یا ماده‌های آن دو را؟ یا آنچه را که رحم‌های آن دو ماده در بر گرفته است‌؟ اگر راستگو بوده‌اید- از روی علم (وحیانی)- این خبر مهم را به من گزارش دهید.»'),
(933, 6, 144, 'و از شتر دو و از گاو دو (نر و ماده را). بگو: «آیا (خدا) نرهای آن دو را حرام کرده یا ماده‌های آن دو را؟ یا آنچه را که رَحِم آن دو ماده در بر گرفته است‌؟ یا چون خدا شما را به این (تحریم) سفارش کرد گواه بودید؟ پس کیست ستمکارتر از آن کس که بر خدا دروغ بندد، تا از روی نادانی، مردم را گمراه کند؟ بی‌گمان، خدا گروه ستمکاران را راهنمایی نمی‌کند.»'),
(934, 6, 145, 'بگو: «در آنچه به من (درباره‌ی انعام) وحی شده - بر خورنده‌ای که آن را می‌خورد - هیچ حرامی نمی‌یابم، مگر مردار یا خون ریخته شده (پس از کشتن یا پیش از آن) یا گوشت خوک را، پس این همواره پلید است؛ یا (قربانی که) از روی نافرمانی (به هنگام ذبح) نام غیر خدا بر آن برده شده. پس کسی که بدون سرکشی و (یا) بازگشت (به خوردن آنها) بی‌اختیار ناچار گردد (بر او گناهی نیست).» به‌راستی پروردگار تو بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(935, 6, 146, 'و بر یهودیان، هر (حیوان حلال) چنگال‌داری را حرام کردیم و از گاو و گوسفند، چربی‌های آن دو را بر آنان حرام کردیم، به استثنای آن (چربی)هایی که بر پشت آن دو است یا در امعایشان یا آنچه با استخوان در آمیخته است. این (تحریم) را به سزای تجاوزشان، به آنان کیفر دادیم و ما به‌راستی بس (در گفتار و کردار) راستاییم.'),
(936, 6, 147, '(ای پیامبر!) پس اگر تو را تکذیب کردند، بگو: «پروردگار شما دارای رحمتی گسترده است و (با این حال) سختگیریش از گروه مجرمان باز گردانده نمی‌شود.»'),
(937, 6, 148, 'زودا کسانی که شرک آوردند گویند: «اگر خدا می‌خواست، نه ما و نه پدرانمان شرک نمی‌آوردیم و چیزی را (خودسرانه) تحریم نمی‌کردیم.» کسانی هم که پیش از آنان بودند، همین گونه (پیامبران خود را) تکذیب کردند، تا برخورد شدید ما را چشیدند. بگو: «آیا نزد شما علمی (وحیانی) است، تا آن را برایمان برون آورید؟ شما جز از گمان پیروی نمی‌کنید و جز به گزاف تیری به تاریکی نمی‌افکنید.»'),
(938, 6, 149, 'بگو: «پس برهان رسا ویژه‌ی خداست و اگر خدا می‌خواست همواره همه‌ی شما را هدایت می‌کرد.»'),
(939, 6, 150, 'بگو: «گواهان خود را که گواهی می‌دهند خدا این(‌ها) را بی‌چون حرام کرده، بیاورید.» پس اگر (هم) شهادت دادند تو با آنان شهادت مده و هوس‌های کسانی را که به (وسیله‌ی) آیات ما (همان‌ها را) تکذیب کردند و کسانی که به آخرت ایمان نمی‌آورند و (معبودان دروغین را) با پروردگارشان همتا و همسان قرار می‌دهند، پیروی مکن.'),
(940, 6, 151, 'بگو: «بیایید تا آنچه را پروردگارتان بر شما حرام کرده (برایتان) بخوانم: اینکه چیزی را با او شریک قرار مدهید و به پدران و مادران(تان) احسان کنید و فرزندانتان را از (بیم) تنگدستی مکشید، (زیرا) تنها ما (هستیم که) شما و آنان را روزی می‌دهیم. و به کارهای زشت تجاوزگر - چه آشکار و چه پنهان - نزدیک نشوید و کسی را که خدا محترم داشته - جز بحقّ - مکشید. این است که خدا شما را به آن سفارش کرده، شاید خردورزی کنید.»'),
(941, 6, 152, 'و به مال یتیم - جز بحقّ به گونه‌ای هر چه نیکوتر - نزدیک مشوید، تا به (حدّ) رشدهایش برسد. و پیمانه و ترازو را با تقسیم کردن (عادلانه) تمام انجام دهید. هیچ کس را جز به اندازه‌ی توانش تکلیف نمی‌کنیم. و هنگامی که (سخنی) گویید عادلانه سخن بگویید، هر چند (درباره‌ی) نزدیکترین خویشاوند(تان) باشد. و به پیمان خدا وفا کنید. این است که (خدا) شما را به آن سفارش کرده، شاید به شایستگی، (او و احکامش را) یاد کنید.'),
(942, 6, 153, 'و اینکه به‌راستی این راهِ بس راست من است. پس آن را پیروی کنید و راه‌ها(ی دیگر) را - که شما را از راه وی پراکنده می‌سازد - پیروی مکنید. این است که (خدا) شما را به آن سفارش کرده، شاید به تقوا گرایید.'),
(943, 6, 154, 'سپس به موسی کتاب (وحی) را دادیم، برای اینکه (هدایت را) بر کسی که نیکی کرده تمام کنیم؛ و برای اینکه هر چیزی را به گونه‌ای روشن جداسازی نماییم و (نیز) هدایت و رحمتی باشد، شاید به لقای پروردگارشان ایمان بیاورند.'),
(944, 6, 155, 'و این خجسته و فراگیر کتابی است که ما آن را نازل کردیم. پس، از آن پیروی کنید و (از خدا) پروا بدارید، شاید مورد رحمت (او) قرار گیرید.'),
(945, 6, 156, 'تا نگویید: «کتاب (وحیانی) تنها بر دو طایفه‌ی پیش از ما نازل شده و ما بی‌گمان از آموزششان بی‌خبر بوده‌ایم.»'),
(946, 6, 157, 'یا گویید: «اگر کتاب (وحیانی) بر ما نازل می‌شد، به‌راستی از آنان هدایت‌یافته‌تر بودیم.» پس (اکنون) بی‌گمان بیانی بس روشن از پروردگارتان برایتان آمده و (نیز) رهنمود و رحمتی (شایسته). پس کیست ستمکارتر از آن کس که آیات خدا (و او) را (با همان‌ها) تکذیب کرده و از آنها (خود و دیگران را) رویگردان کند؟ به زودی کسانی را که (مکلفان را) از آیات ما رویگردان می‌کنند، به (خاطر) اینکه رویگردانی می‌کرده‌اند، به عذابی بد مجازات خواهیم کرد.'),
(947, 6, 158, 'آیا جز این نگرش و انتظار را دارند، که فرشتگان سویشان بیایند، یا پروردگارت (سویشان) آید، یا پاره‌ای از نشانه‌های پروردگارت بیاید؟ روزی که پاره‌ای از نشانه‌های پروردگارت آید، کسی که قبلاً ایمان نیاورده یا خیری در ایمانش به دست نیاورده، ایمان آوردنش سودی نمی‌بخشد. بگو: «منتظر باشید (که) همواره ما (هم) منتظرانیم.»'),
(948, 6, 159, 'بی‌گمان کسانی که دینشان را پراکندند و پیروانی پراکنده بودند، تو با آنها هیچ کاری نداری. کارشان تنها سوی خداست، سپس (خدا) به آنچه می‌کرده‌اند آگاهی مهمشان خواهد داد.'),
(949, 6, 160, 'هر کس به نیکی بیاید، برایش ده برابر (پاداش) است و هر کس به بدی آید، جز مانند آن جزا نیابد، حال آنکه بر آنان ستم (هم) نرود.'),
(950, 6, 161, 'بگو: «پروردگارم مرا همواره به راهی راست هدایت کرده: دینی پایدار و بهادار و پای بر جا کننده، آیین ابراهیم؛ حال آنکه (او) رویگردان از باطل بوده و از مشرکان نبوده است.»'),
(951, 6, 162, 'بگو: «به‌راستی، نمازم و سایر عباداتم و زندگی و مرگم، (همه و همه) تنها برای خدا- پروردگار جهانیان- است.»'),
(952, 6, 163, 'او را هرگز شریکی نیست و بدین (دین پایدار) دستور یافته‌ام و من (در مرتبه‌ی) نخستینِ تسلیم‌شدگانم.»'),
(953, 6, 164, 'بگو: «آیا جز خدا(ی یکتا) پروردگاری بجویم، حال آنکه او پروردگار همه چیز است، و هیچ کس بجز آنچه (خود) به دست آورده بر عهده‌اش نیست، و هیچ باربرداری بار دیگران را بر نمی‌دارد. سپس شما بازگشتتان سوی پروردگارتان است‌؛ پس (شما را) به آنچه در آن اختلاف می‌کرده‌اید آگاهی بزرگی خواهد داد.»'),
(954, 6, 165, 'و اوست کسی که شما را در زمین جانشینان (یکدیگر و گذشتگانی دیگر ) نهاد و بعضی از شما را بر برخی دیگر به درجاتی (در این جهان) برتری داد، تا شما را – در آنچه به شما داده است – بیازماید. همواره، پروردگارت پیگیرِ سریع (در کیفر) است و به‌راستی (هم)او بس پوشنده‌ی رحمتگر بر ویژگان است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'المص'),
(956, 7, 2, 'کتابی است (که) بر تو نازل شده- پس نباید در سینه‌ات از آن تنگنایی باشد- برای اینکه با آن (مکلفان را) بیم دهی. و (این‌) یادواره‌ای است برای مؤمنان.'),
(957, 7, 3, 'آنچه را از پروردگارتان برایتان نازل گشته پیروی کنید و از اولیائی دیگر پیروی مکنید. بسی اندک است آنچه را متذکر می‌شوید.'),
(958, 7, 4, 'و چه بسیار گروهی را هلاکشان کردیم که برخورد سختمان برایشان به هنگام خواب(شان) یا در میان روز فرا رسید.'),
(959, 7, 5, 'پس چون ناگواری سختمان آنان را فرا رسید، خواسته‌ای نداشتند جز اینکه گفتند: «ما همانا (از) ستمکاران بوده‌ایم.»'),
(960, 7, 6, 'پس بی‌گمان از کسانی که (پیامبران) سوی آنان فرستاده شد(ه‌اند)، بی‌چون خواهیم پرسید و همانا از (خود) آن فرستادگان (نیز) همواره خواهیم پرسید.'),
(961, 7, 7, 'پس به‌راستی از روی علم بر آنان گزارش خواهیم داد، حال آنکه ما (از احوال آنان) غایب نبوده‌ایم.'),
(962, 7, 8, 'و در آن روز، میزان و سنجش اعمال، (خود، تمامی) حق است. پس آنان که میزان‌های سنجش اعمالشان سنگین باشد همانان خودشان رستگارکنندگانند.'),
(963, 7, 9, 'و آنان که میزان‌های اعمالشان سبک باشد، آنانند که خودهاشان را زیان کرده‌اند، (چرا) که به آیات ما ستم می‌کرده‌اند.'),
(964, 7, 10, 'و به‌راستی ما شما را در زمین همواره تمکین (در حد امکان و توانتان) دادیم و برای شما در آن وسایلی برای معیشت نهادیم؛ (اما) چه‌کم سپاسگزاری می‌کنید.'),
(965, 7, 11, 'و به‌راستی شما را به‌درستی خلق کردیم. سپس به صورتگری شما پرداختیم. پس از آن به فرشتگان گفتیم: «برای (شکرانه‌ی تعلیم) آدم (برای من) سجده کنید.» پس (همه) سجده کردند، جز ابلیس (که) از سجده‌کنندگان نبود.'),
(966, 7, 12, '(خدا) فرمود: «چون تو را به سجده امر کردم چه چیز تو را بازداشت از اینکه (برای من) سجده کنی‌؟» گفت: «من از او بهترم، مرا از آتشی و او را از گِلی آفریدی.»'),
(967, 7, 13, 'فرمود: «پس از این جایگاه فرو شو. پس تو را نرسد که در آن تکبّر نمایی. پس برون شو که تو بی‌گمان از خواران و بی‌مقدارانی.»'),
(968, 7, 14, 'گفت: «مرا تا روزی که (مکلفان) برانگیخته خواهند شد مهلت ده.»'),
(969, 7, 15, 'فرمود: «همانا تو از مهلت‌یافتگانی.»'),
(970, 7, 16, 'گفت: «پس از آن رو که مرا به بی‌راهه افکندی من نیز همواره برای (گمراه کردن)شان بر سر راه راستت همی می‌نشینم.»‌'),
(971, 7, 17, '.« سپس بی‌گمان از پیش رویشان [:آخرت] و از پشت سرشان [:دنیا] و از طرف راستشان [:دینشان] و از طرف چپشان [:شهواتشان] همواره بر آنها می‌تازم و بیشترشان را شکرگزار نخواهی یافت.»'),
(972, 7, 18, 'فرمود: «از این بهشت در حالی‌که مذمت شده (و) رانده گشته‌ای برون شو. بی‌گمان کسی از آنان که از تو پیروی کند، همانا جهنم را از همه‌ی شما (ابلیسیان) همواره پر‌خواهم کرد.»'),
(973, 7, 19, '« و ای آدم! تو با همسر خویش در (این) باغ سردرهم سکونت گیر، پس از هر جا و هرگونه که بخواهید (از خوردنی‌هایش) بخورید، و به این درخت نزدیک مشوید که (در این صورت) از ستمکاران می‌شوید.»'),
(974, 7, 20, 'پس شیطان، برای آن دو وسوسه کرد تا آنچه را از عورت‌هایشان از ایشان پوشیده مانده بود برایشان نمایان گرداند و گفت: «پروردگارتان شما را از این درخت منع نکرد، جز برای آنکه (مبادا) دو فرشته گردید، یا از (زمره‌ی) جاودانان باشید.»'),
(975, 7, 21, 'و برای آن دو سوگند یاد کرد که: «من به‌راستی همواره از خیرخواهان شمایم.»'),
(976, 7, 22, 'پس آن دو را با دلو فریب (به چاه سقوط‌) افکند. پس چون آن دو از آن درخت (ممنوع) چشیدند، زشتی‌هایشان برایشان آشکار شد و به چسبانیدن برگ آن باغ بر خود نزدیک شدند و پروردگارشان آن دو را بانگ زد: «مگر شما را از این درخت منع نکردم و به شما نگفتم (که‌) شیطان بی‌گمان برای شما دشمنی آشکارگر است‌؟»'),
(977, 7, 23, 'گفتند: «پروردگارمان! ما بر خویشتن ستم کردیم و اگر بر ما (گناهمان را) نپوشانی و به ما رحم نکنی، بی‌گمان (و) بی‌امان از زیانکاران خواهیم بود.»'),
(978, 7, 24, 'فرمود: « (هرسه) فرود آیید، حال آنکه بعضی از شما دشمن بعضی دیگرید و برای شما در زمین تا هنگامی (معیّن) قرارگاه و برخورداری است.»'),
(979, 7, 25, 'فرمود: «در آن زندگی می‌کنید و در آن می‌میرید و از آن بیرون برده می‌شوید.»'),
(980, 7, 26, 'ای فرزندان آدم! ما همواره برای شما لباسی فروفرستادیم که عورت‌هایتان را بپوشاند، (این پوشش زیرین شماست) و (نیز پوشش) زینتی (برایتان فرستادیم)؛ و (اما) بهترین جامه(ی زیرین و رویین) لباس تقوی است. این از نشانه‌های خداست، شاید به خوبی یاد آورند.'),
(981, 7, 27, 'ای فرزندان آدم! زنهار تا شیطان هرگز شما را به فتنه [:آزمایشی آتشبار] نیندازد، چنان که پدر و مادرتان را از بهشت بیرون راند، حال آنکه لباسشان را (هم) از ایشان می‌کَنْد تا عورت‌هایشان را برایشان نمایان کند. او و همانندانش بی‌گمان شما را می‌بیند، بدان‌گونه که شما آنان را نمی‌بینید. ما بی‌چون شیطان‌ها را سرپرستان و دوستان کسانی نهادیم که ایمان نمی‌آورند.'),
(982, 7, 28, 'و چون کار زشت تجاوزگرانه‌ای کنند، گویند: «پدران خود را بر آن یافتیم و خدا بدان فرمانمان داد.» بگو: «خدا بی‌گمان به کار زشت تجاوزگرانه فرمان نمی‌دهد. آیا چیزی را که نمی‌دانید به خدا نسبت می‌دهید؟»'),
(983, 7, 29, 'بگو: «پروردگارم به قسط [:تقسیم عادلانه] فرمان داده است و (اینکه) در هر مسجدی [:سجده‌گاهی] روی‌های خود را (با استقامت برای خدا) به پا دارید و در حالی‌که طاعت خود را برای او خالص گردانیده‌اید وی را بخوانید. همان گونه که آفرینش شما را آغاز کرد (سرانجام) به سوی او باز می‌گردید.»'),
(984, 7, 30, 'گروهی را هدایت کرد و گروهی (دیگر) گمراهی بر آنان حق (و ثابت) است، زیرا آنان شیاطین را به جای خدا، اولیا(ی خود) گرفته‌اند و می‌پندارند که همواره راه‌یافتگانند.'),
(985, 7, 31, 'ای فرزندان آدم! زینت خود را در نزد هر سجده‌گاهی برگیرید. و بخورید و بیاشامید و (اما) زیاده‌روی نکنید که او بی‌گمان اسرافکاران را دوست نمی‌دارد.'),
(986, 7, 32, '(ای پیامبر!) بگو: «زیورهای خدایی را که برای بندگانش پدید آورده -و (نیز) روزی‌های پاکیزه را- چه کسی حرام گردانیده‌؟» بگو: «این (نعمت)ها در زندگی دنیا برای کسانی است که ایمان آورده‌اند، در حالی که روز قیامت ویژه‌ی آنان می‌باشد.» این‌گونه آیات (خود) را برای گروهی که می‌دانند جداسازی و روشن می‌کنیم.'),
(987, 7, 33, 'بگو: «پروردگار من فقط زشتکاری‌های متجاوز - چه آشکارش و چه پنهانش - را و گناه دنباله‌دار را و تجاوز بنا حق را حرام گردانیده است و (نیز) اینکه چیزی را شریک خدا پندارید که دلیلی بر (حقانیت) آن نازل نکرده و اینکه چیزی را که نمی‌دانید به خدا نسبت دهید.»'),
(988, 7, 34, 'و برای هر امتّی زمانی پایانی [:اجلی] است‌؛ پس چون اجلشان فرا رسد نه (می‌توانند) لحظه‌ای (از آن) تأخیر بخواهند و نه (بر آن) پیشی جویند.'),
(989, 7, 35, 'ای فرزندان آدم! اگر به‌راستی پیامبرانی از خودتان برایتان بیایند حال آنکه آیات مرا بر شما می‌خوانند، پس هر کس پرهیزگاری و اصلاح کند، نه بیمی بر آنان خواهد بود و نه ایشان اندوهگین می‌شوند.'),
(990, 7, 36, 'و کسانی که با آیات ما (همان‌ها و ما را) تکذیب کردند و از (پذیرش) آنها تکبّر ورزیدند، ایشان همدمان آتشند (و) آنان در آن جاودانند.'),
(991, 7, 37, 'پس کیست ستمکارتر از کسی که بر خدا افترایی دروغین زند، یا با آیات او (همان‌ها و او را) تکذیب کند؟ اینان کسانی‌اند که نصیبشان از آنچه نبشته شده به ایشان خواهد رسید تا آن‌گاه که فرشتگان ما به سراغشان بیایند. درحالی‌که جان‌هاشان را به کلی می‌ستانند (فرشتگان) گویند: «آنچه غیر از خدا می‌خوانده‌اید کجاست‌؟» گویند: «از (چشم) ما گم گشتند.» و علیه خود گواهی می‌دهند که بی‌گمان (آنان) کافران بوده‌اند!'),
(992, 7, 38, 'فرمود: «در میان امّت‌هایی از جن و انس - که پیش از شما بوده‌اند - داخل آتش شوید.» هر بار امتی (به آتش) در آید، همگنان خود را لعنت کند تا هنگامی که همگان در آن آماده شوند. (آن‌گاه) آخرینشان درباره‌ی اولینشان گوید: «پروردگارمان! اینان ما را گمراه کردند، پس عذابی از آتشی افزون به آنان بده.» (خدا) فرمود: «برای هر کدام (آتشی) افزون است، ولی شما نمی‌دانید.»'),
(993, 7, 39, 'و اولینشان به آخرینشان گفت: «شما را بر ما هیچ برتری نبوده، پس به سزای آنچه به دست می‌آوردید عذاب را بچشید.»'),
(994, 7, 40, 'بی‌گمان کسانی که با آیات ما (همان‌ها و ما را) تکذیب کردند و از (پذیرفتن) آن‌ها تکبّر ورزیدند، درهای آسمان برایشان هرگز گشوده نشود و به بهشت در نیایند، تا آنکه طناب شتر در سوراخ سوزن داخل شود. و بدین‌سان مجرمان را کیفر می‌دهیم.'),
(995, 7, 41, 'برایشان از جهنّم بسترهایی و از بالایشان سرپوش‌هایی است و بدین‌گونه بیدادگران را سزا می‌دهیم.'),
(996, 7, 42, 'و کسانی که ایمان آورده و کارهای شایسته(ی ایمان) کرده‌اند- هیچ کسی را جز به اندازه‌ی گشایشش تکلیف نمی‌کنیم - آنان همدمان بهشتند. ایشان در آن جاودانند.'),
(997, 7, 43, 'و هرگونه کینه‌ای را از سینه‌هایشان زدودیم. حال آنکه از زیر (قصرها و باغ‌های)‌شان نهرها روان است و گفتند: «ستایش خدای را که ما را به این (راه) هدایت نمود و اگر خدا ما را رهبری نمی‌نمود ما خود هرگز راه نمی‌یافتیم. بی‌گمان فرستادگان پروردگارمان به‌راستی (تمامی) حق را آوردند.» و ندا داده شدند که:«این همان بهشت است که آن را به (سبب) آنچه انجام می‌داده‌اید به میراث یافته‌اید.»'),
(998, 7, 44, 'و بهشتیان، دوزخیان را ندا در دادند که: «ما آنچه را پروردگارمان به ما وعده داده بود راستی بحق یافتیم، آیا شما (نیز) آنچه را پروردگارتان وعده داده بود درست یافتید؟» گفتند: «آری.» پس ندا دهنده‌ای میانشان ندا در داد که: «لعنت خدا بر ستمکاران باد!»'),
(999, 7, 45, '(آنان) کسانی (هستند) که (خود و مردمان را) از راه خدا باز می‌دارند و آن را به کژی می‌طلبند و همانان به (زندگی) آخرین کافرند.'),
(1000, 7, 46, 'و میان آن دو (گروه) پرده‌ای است و بر فراز اعراف [:بلندی‌های میان بهشت و جهنم] رادمردانی هستند، که هر یک (از آن دو دسته) را از چهره‌هایشان می‌شناسند و بهشتیان را- که هنوز وارد آن نشده در حالی‌که (بدان) امید دارند- آواز می‌دهند که: «سلام بر شما!» داخل آن نشده‌اند، حال آنکه طمع (داخل شدن) دارند.'),
(1001, 7, 47, 'و هنگامی که چشمانشان سوی دوزخیان گردانیده شود گویند: «پروردگارمان! ما را با گروه ستمکاران قرار مده.»'),
(1002, 7, 48, 'و اهل اعراف مردانی را که از سیمایشان می‌شناسند ندا در دادند (و) گفتند: «گردآوری شما (،گرد‌آوری خودهاتان و اموالتان) و آنچه تکبّر و گردنکشی می‌کرده‌اید، (این‌ها) شما را (هرگز در این جهانِ پُرنیاز) بی‌نیاز نکرد.»'),
(1003, 7, 49, '«آیا اینان همان کسانی بودند که سوگند یاد می‌کردید خدا آنان را به رحمتی نخواهد رسانید؟ (اینک) به بهشت در آیید، نه بیمی بر شماست و نه اندوهگین می‌شوید.»'),
(1004, 7, 50, 'و دوزخیان بهشتیان را ندا در دادند که: «از آب یا از آنچه خدا روزیتان کرده، بر (سر و سامان)مان بهره و فیضی دهید.» گفتند: «بی‌گمان خدا آنها را بر کافران حرام کرده است.»'),
(1005, 7, 51, '(همان) کسانی که دین خود را (تنها) بازداری از حق و به بازی گرفتند و زندگی دنیا مغرورشان کرد. پس همان‌گونه که آنان دیدار امروز خود را از یاد بردند و آیات ما را انکار (و انگار) می‌کردند، ما (هم) امروز آنان را از یاد می‌بریم.'),
(1006, 7, 52, 'و به‌راستی و درستی ما با کتابی آمدیمشان (که) از روی علم جداسازیش کردیم، حال آنکه برای گروهی که ایمان می‌آورند هدایت و رحمتی است.'),
(1007, 7, 53, 'آیا (آنان) جز نظر و انتظار تأویل آن را دارند؟روزی که تأویلش فرا رسد، کسانی که آن را پیش از آن به فراموشی سپرده‌اند گویند: «حقا (که‌) فرستادگان پروردگارمان (تمامی) حق را آوردند. پس آیا (امروز) ما را شفاعتگرانی هستند که برای ما شفاعت کنند یا (به دنیا) باز گردانیده شویم، تا غیر از آنچه انجام می‌داده‌ایم انجام دهیم‌؟» به‌راستی آنان خود را باخته و زیان کرده‌اند و آنچه را که افترا می‌زده‌اند از آنان گم گشته است.'),
(1008, 7, 54, 'بی‌گمان پروردگارتان خدایی است که آسمان‌ها و زمین را در شش زمان آفرید، سپس بر عرش (جهان‌داری) استیلا یافت. روز را به شب - حال آنکه شتابان آن را می‌طلبد - می‌پوشاند، حال آنکه خورشید و ماه و ستارگان به فرمان او رامند. هان (که عالم) خلق و امر (خلقت) از آنِ اوست. فرخنده است خدا، پروردگار جهانیان.'),
(1009, 7, 55, 'پروردگارتان را در حال زاری و نهانی بخوانید؛ بی‌گمان او از حدّ گذرندگان را دوست نمی‌دارد.'),
(1010, 7, 56, 'و در زمین پس از اصلاح آن افساد مکنید و با بیم و امید او را بخوانید. همواره رحمت خدا به نیکوکاران نزدیک است.'),
(1011, 7, 57, 'و اوست کسی که بادها را پیشاپیش (باران) رحمتش مژده‌رسان می‌فرستد، تا آن‌گاه که ابرهایی (برخاسته از بخار) گران‌بار را (سبک‌بار) بر کَنَند. آن را برای (زنده ساختن) سرزمینی مرده برانیم، پس با آن، باران فرود آوریم؛ در نتیجه از هر گونه ثمره‌ای (از خاک و آب) بر آوریم. بدین‌سان مردگان را (نیز از قبرهاشان) بیرون آوریم؛ شاید شما به‌درستی یاد آورید.'),
(1012, 7, 58, 'و روییدنی زمین پاکیزه، به اذن پروردگارش برون می‌آید و آن (زمینی) که ناپاکیزه شد (گیاهش) جز به سختی و نکبت‌باری برون نمی‌آید. این‌گونه، آیات (خود) را برای گروهی که شکر می‌گزارند، گونه‌گون (بیان) می‌کنیم.'),
(1013, 7, 59, 'همانا نوح را به‌راستی سوی قومش فرستادیم. پس گفت: «ای قوم من! خدا را بپرستید، برای شما هیچ معبودی جز او نیست، به‌راستی من از عذاب روزی بزرگ بر شما بیمناکم.»'),
(1014, 7, 60, 'سرشناسان قومش گفتند: «بی‌چون ما تو را بس در ژرفای گمراهی آشکارگری می‌بینیم.»'),
(1015, 7, 61, 'گفت: «ای قوم من! هیچ‌گونه گمراهی در من نیست، بلکه من فرستاده‌ای از جانب پروردگار جهانیا‌نم.»'),
(1016, 7, 62, '«رسالت‌های پروردگارم را به شما مکرر (و پیگیر) می‌رسانم و اندرزتان می‌دهم و چیزهایی از خدا می‌دانم که (شما) نمی‌دانید.»'),
(1017, 7, 63, '«آیا و تعجب کردید که بر مردی از خودتان، یادواره‌ای از جانب پروردگارتان برایتان آمد، تا شما را هشدار دهد و تا شما پرهیزگاری کنید و شاید مورد رحمت قرار گیرید؟»'),
(1018, 7, 64, 'پس او را تکذیب کردند. در نتیجه ما او و کسانی را که با وی در کشتی بودند نجات دادیم و کسانی را که آیات ما را تکذیب نمودند غرق کردیم. بی‌گمان آنان گروهی کور(دل) بوده‌اند.»'),
(1019, 7, 65, 'و سوی عادیان، برادرشان هود را (فرستادیم). گفت: «ای قوم من! خدا را بپرستید (که) برای شما معبودی جز او نیست. پس آیا پرهیز نمی‌کنید؟»'),
(1020, 7, 66, 'سرشناسان قومش - که کافر بودند - گفتند: «بی‌چون ما تو را بی‌گمان غرق در (ژرفای) سفاهت می‌بینیم و بی‌تردید تو را از دروغگویان گمان می‌کنیم.»'),
(1021, 7, 67, 'گفت: «ای قوم من! در من هیچ سفاهتی نیست، ولی من فرستاده‌ای از جانب پروردگار جهانیانم.»'),
(1022, 7, 68, '«به خوبی و رسایی رسالت‌های پروردگارم را به شما می‌رسانم و من برای شما نصیحت‌کننده‌ای امینم.»'),
(1023, 7, 69, '«آیا و تعجب کردید که بر مردی از خودتان، یادواره‌ای از پروردگارتان، برایتان آمد تا شما را بیم دهد؟ و به یاد آرید چون (خدا) شما را پس از قوم نوح، جانشین (آنان) قرار داد و در آفرینش(و) آفریدگان بر نیرویتان گشایشی افزون داد. پس نعمت‌های خدا را به یاد آرید، شاید شما رستگار کنید.»'),
(1024, 7, 70, 'گفتند: «آیا سویمان آمدی که تنها خدا را بپرستیم و آنچه را که پدرانمان می‌پرستیدند رها کنیم‌؟ اگر از راستان بوده‌ای، آنچه را به ما وعده می‌دهی برایمان بیاور.»'),
(1025, 7, 71, 'گفت: «به‌راستی پلیدی و خشمی (سخت) از پروردگارتان بر سر (و سامان‌)تان واقع شده. آیا درباره‌ی نام‌هایی که خود‌تان و پدرانتان (با آنها بت‌هایتان را) نامیده‌اید، (و) خدا بر (حقانیت) آنها هیچ سلطه‌ای فرو نفرستاده، با من مجادله می‌کنید؟ پس منتظر باشید که بی‌گمان من (هم) با شما از منتظرانم.»'),
(1026, 7, 72, 'پس او و کسانی را که با او بودند با رحمتی (بزرگ) از جانب خود رهانیدیم و دنباله‌ی [:اصل و نسل] کسانی را که با آیات ما (همان‌ها و ما را) تکذیب کردند و مؤمن نبوده‌اند برافکندیم.'),
(1027, 7, 73, 'و سوی ثمودیان برادرشان، صالح را (فرستادیم). گفت: «ای قوم من! خدا را بپرستید، برای شما هیچ معبودی جز او نیست. بی‌گمان برای شما از جانب پروردگارتان دلیلی آشکار آمده. این ماده شترِ خدا برای شما نشانه‌ای (شگرف) است. پس آن را واگذارید تا در زمین خدا بخورد (و بچرد) و گزندی به او نرسانید، تا (مبادا) شما را عذابی دردناک در بر گیرد.»'),
(1028, 7, 74, '«و به یاد آورید، چون خدا شما را پس از عادیان جانشینان (آنان) گردانید و در زمین به شما جایگاه گشاده (و پاکیزه) داد حال آنکه در دشت‌های آن (برای خودتان) کاخ‌هایی اختیار می‌کنید و از کوه‌ها خانه‌هایی می‌تراشید. پس نعمت‌های خدا را به یاد آورید و در زمین سر به افساد بر مدارید.»'),
(1029, 7, 75, 'از (میان) قومش، (آن) سرشناسان که استکبار ورزیدند، به مستضعفانی که ایمان آورده بودند، گفتند: «آیا می‌دانید به‌راستی صالح از طرف پروردگارش فرستاده شده است‌؟» گفتند: «بی‌گمان ما به آنچه وی بدان رسالت یافته است ایمان داریم.»'),
(1030, 7, 76, 'کسانی که استکبار کردند، گفتند: «ما به راستی به آنچه شما بدان ایمان آوردید کافریم.»'),
(1031, 7, 77, 'پس (آن) ماده شتر را پی کردند و از فرمان پروردگارشان سر پیچیدند و گفتند: «ای صالح! اگر تو از پیامبرانی، آنچه را به ما وعده می‌دهی برایمان بیاور.»'),
(1032, 7, 78, 'پس زمین‌لرزه آنان را فرو گرفت؛ در نتیجه در خانه‌شان از پا در آمده (و) زمین‌گیر شدند.'),
(1033, 7, 79, 'پس (صالح) از ایشان رویگردان شد و گفت: «ای قوم من! به‌راستی، من پیام پروردگارم را به شما به شایستگی و رسایی رساندم و برایتان نصیحت کردم، ولی شما نصیحتگران را دوست نمی‌دارید.»'),
(1034, 7, 80, 'و لوط را (فرستادیم) هنگامی که به قوم خود گفت: «آیا (آن) کار زشت تجاوزگرانه را مرتکب می‌شوید، (در حالی که) هیچ کسی از جهانیان در آن بر شما پیشی نگرفته‌؟»'),
(1035, 7, 81, '«شما بی‌گمان از روی شهوت، همواره به جای زنان با مردان در می‌آمیزید. بلکه شما گروهی (از) اسراف‌کارانید.»'),
(1036, 7, 82, 'و پاسخ قومش جز این نبود که گفتند: «آنان را از شهرتان بیرون برانید، بی‌گمان آنان انسان‌هایی‌اند که به پاکی تظاهر می‌کنند.»'),
(1037, 7, 83, 'پس او و خانواده‌اش را - غیر از زنش که از (غبار آلوده‌های) گذشتگان (و باقی مانده در ژرفای گمراهی) بوده است - نجات دادیم.'),
(1038, 7, 84, 'و بر سر (و سامان)شان بارشی (از سنگ‌گل) بارانیدیم. پس بنگر فرجام مجرمان چگونه بود.'),
(1039, 7, 85, 'و سوی (مردم) مَدْیَن، برادرشان -شعیب- را (فرستادیم). گفت: «ای قوم من! خدا را بپرستید (که) برای شما هیچ معبودی جز او نیست. بی‌گمان، شما را از جانب پروردگارتان برهانی روشن آمده. پس پیمانه و ترازو را تمام نهید و چیزهای مردمان را زیان‌بار و بی‌مقدار نگردانید و در زمین - پس از اصلاح آن - افساد مکنید. این (رهنمودها) اگر مؤمن بوده‌اید برای شما خوب است.»'),
(1040, 7, 86, '«و به هر راهی منشینید -حال آنکه مردمان را می‌ترسانید- و کسی را که به خدا ایمان آورده از راه خدا باز می‌دارید و راه او را کج می‌خواهید. و به یاد آورید، چون اندک بودید، پس (خدا) شما را بسیار گردانید. و بنگرید فرجام افسادکاران چگونه بوده است.»'),
(1041, 7, 87, '«و اگر گروهی از شما - به آنچه من بدان فرستاده شده‌ام - ایمان آورده بودند و گروهی (هم) ایمان نیاوردند، پس صبر کنید تا خدا میان ما داوری کند و او بهترین داوران است.»'),
(1042, 7, 88, 'کسانی از سرکردگان قومش -که تکبّر ورزیدند- گفتند: «ای شعیب! تو و کسانی را که با تو ایمان آورده‌اند، از شهر خودمان بی‌چون و بی‌گمان بیرون خواهیم راند، یا (آنکه) به‌راستی همواره به ملتمان برگردید.» گفت: «آیا و هر چند از کراهت‌کنندگان بوده‌ایم‌؟»'),
(1043, 7, 89, '«اگر بعد از هنگامی‌که خدا ما را از ملتتان نجات بخشید (باز) به کیش شما برگردیم، همانا به خدا دروغ بسته‌ایم و برای ما (سزاوار) نیست که به آن بازگردیم، مگر آنکه خدا، پروردگارمان، بخواهد. علم پروردگار ما هر چیزی را در بر دارد (و ما) بر خدا توکل کردیم. پروردگارمان! میان ما و قوم ما به (تمامی) حق فیصله و گشایشی ده، حال آنکه تو بهترین گشایش‌دهندگانی.»'),
(1044, 7, 90, 'و کسانی از سرکردگان قومش که کافر شدند گفتند: «اگر از شعیب پیروی کنید، همانا شما در این هنگام بی‌گمان زیان‌کارید.»'),
(1045, 7, 91, 'پس زمین‌لرزه‌ی شدید آنان را فرو گرفت؛ سپس در خانه‌هایشان از پا در آمده (و) زمین‌گیر شدند.'),
(1046, 7, 92, 'کسانی که شعیب را تکذیب کردند، گویی خود در آن (دیار) مدتی در آن با نیازی (مبرم) سکونت نداشتند. کسانی که شعیب را تکذیب کردند، ایشان زیان‌کاران بوده‌اند.'),
(1047, 7, 93, 'پس (شعیب) از ایشان روی برتافت و گفت: «ای قوم من! به‌راستی من همی رسالت‌های پروردگارم را به گونه‌ای رسا به شما رسانیدم و پندتان دادم. پس دیگر چگونه بر گروهی (از) کافران تأسف بخورم‌؟»'),
(1048, 7, 94, 'و در هیچ شهری، هیچ پیامبر بزرگی نفرستادیم، مگر آنکه مردمش را به سختی و رنج و زیان دچار کردیم، تا شاید بسی زاری کنند.'),
(1049, 7, 95, 'سپس به جای بدی، نیکی قرار دادیم، تا انبوه شدند و گفتند: «پدران ما را (هم به حکم طبیعت) بی‌چون بسی رنج‌ها و راحتی‌ها می‌رسیده است.» پس در حالی که باریک‌بینی نمی‌کردند (ناگاه) آنان را (در) گرفتیم.'),
(1050, 7, 96, 'و اگر اهل شهرها و آبادی‌ها ایمان آورده و به تقوا گراییده بودند (ما هم) بی‌گمان برکاتی از آسمان و زمین بر ایشان می‌گشودیم. ولی تکذیب کردند؛ پس آنان را به آنچه به دست آورده بوده‌اند برگرفتیم.'),
(1051, 7, 97, 'آیا پس (از این) ساکنان شهرها و آبادی‌ها ایمن شدند، (از این) که عذاب ما شامگاهان - در حالی که خوابند - به آنان در رسد؟'),
(1052, 7, 98, 'آیا و ساکنان شهرها و آبادی‌ها ایمن شده‌اند، (از این) که مکر ما نیمروز - در حالی که به بازی سرگرمند - به ایشان در رسد؟'),
(1053, 7, 99, 'آیا پس مگر خود را از مکر خدا ایمن داشتند؟ ولی جز مردم زیان‌کار (کسی) خود را از مکر خدا ایمن نمی‌دارد.'),
(1054, 7, 100, 'و آیا برای کسانی که زمین را پس از ساکنان پیشینش به ارث می‌برند، رهنمون نشده است که اگر می‌خواستیم آنان را به (کیفر) گناهانشان آسیبی می‌رساندیم و بر دل‌هایشان مُهر می‌نهادیم‌؛ پس ایشان نمی‌شنوند.'),
(1055, 7, 101, 'این‌ها مجتمعات انسانی است که برخی از خبرهای مهم آن را بر تو حکایت می‌کنیم. و پیامبرانشان بی‌گمان برایشان به‌راستی دلایل روشن آوردند. پس آنان - به آنچه قبلاً تکذیب کرده بودند - هرگز ایمان نمی‌آورند. این‌گونه خدا بر دل‌های کافران مُهر می‌نهد.'),
(1056, 7, 102, 'و برای بیشتر آنان هیچ عهدی (استوار) نیافتیم (و) بیشترشان را همواره نافرمانان یافتیم.'),
(1057, 7, 103, 'سپس، بعد از آنان، موسی را با آیات خود سوی فرعون و سران قومش فرستادیم. پس آنها به آن (آیات) ستم ورزیدند. پس ببین فرجام مفسدان چگونه بوده است.'),
(1058, 7, 104, 'و موسی گفت: «ای فرعون! به‌راستی من فرستاده‌ای از سوی پروردگار جهانیانم.»'),
(1059, 7, 105, '«شایسته و بایسته است که بر خدا جز (سخن) حق نگویم. من بی‌گمان (دلیل) روشنی از سوی پروردگارتان برایتان آورده‌ام، پس فرزندان اسرائیل را با من بفرست.»'),
(1060, 7, 106, '(فرعون) گفت: «اگر تو نشانه‌ای آورده بوده‌ای - اگر از راستان بوده‌ای - پس آن را (با خود) بیاور.»'),
(1061, 7, 107, 'پس (موسی) عصایش را افکند. سپس به‌ناگاه اژدری آشکارگر است.'),
(1062, 7, 108, 'و دستش را (از گریبانش بیرون) کشید، پس ناگهان برای تماشاگران سپید و درخشان است.'),
(1063, 7, 109, 'سران قوم فرعون گفتند: «بی‌شک این (مرد) ساحری بس داناست.»'),
(1064, 7, 110, '«می‌خواهد شما را از سرزمینتان بیرون کند. پس چه دستور می‌دهید؟»'),
(1065, 7, 111, 'گفتند: «او و برادرش را بازداشت کن و گردآورندگانی را به شهرها بفرست‌،»'),
(1066, 7, 112, '«تا هر ساحر بسیار دانایی را نزدت بیاورند.»'),
(1067, 7, 113, 'و ساحران نزد فرعون آمدند (و) گفتند: «اگر ما، (همین) ما پیروز شوندگان بوده‌ایم، به‌راستی برایمان پاداشی بس بزرگ خواهد بود؟»'),
(1068, 7, 114, 'گفت: «آری! و بی‌گمان شما به‌راستی از مقربان (دربار من) می‌باشید.»'),
(1069, 7, 115, 'گفتند: «ای موسی! آیا تو می‌افکنی و یا اینکه (همین) ما خودمان افکنندگانیم!»'),
(1070, 7, 116, 'گفت: « (همین) شما بیفکنید.» پس چون افکندند، دیدگان مردم را افسون کردند و آنان را به ترس انداختند و سحری بزرگ (به میان) آوردند.'),
(1071, 7, 117, 'و سوی موسی وحی کردیم که: «عصایت را بیفکن.» پس (انداخت، ناگهان اژدری شد و) آنچه را به دروغ می‌ساختند فرو می‌بلعد.»'),
(1072, 7, 118, 'پس حقیقت پای‌برجا گردید و کارهایی که می‌کردند باطل گشت.'),
(1073, 7, 119, 'پس در آن‌جا مغلوب گشته و به خواری و بی‌مقداری برگشتند.'),
(1074, 7, 120, 'و ساحران (بی‌اختیار) به سجده افکنده شدند.'),
(1075, 7, 121, 'گفتند: «به پروردگار جهانیان ایمان آوردیم‌؛»'),
(1076, 7, 122, '«پروردگار موسی و هارون.»'),
(1077, 7, 123, 'فرعون گفت: «آیا پیش از آنکه به شما رخصت دهم به او ایمان آوردید؟ بی‌چون این (خود) نیرنگی است که همواره در شهر به راه انداخته‌اید تا مردمش را از آن بیرون کنید، پس در آینده‌ای دور خواهید دانست.»'),
(1078, 7, 124, '«دست‌ها و پاهایتان را بی‌گمان برخلاف یکدیگر بی‌امان خواهم برید، سپس بی‌چون همه‌ی شما را همواره به دار خواهم آویخت.»'),
(1079, 7, 125, 'گفتند: «به‌راستی سوی پروردگارمان بازگشت‌کنندگانیم.‌»'),
(1080, 7, 126, '«و تو از ما انتقام نمی‌گیری، مگر برای اینکه ما به نشانه‌های پروردگارمان - هنگامی‌که برایمان آمد - ایمان آوردیم. پروردگارمان! بر (سر و سامان)مان شکیبایی فرو ریز و ما را در حال تسلیم بمیران.»'),
(1081, 7, 127, 'و سران چشمگیر قوم فرعون گفتند: «آیا موسی و قومش را رها می‌کنی تا در (این) سرزمین افساد کنند، و تو و خدایانت را رها کند؟» (فرعون) گفت: «به‌زودی پسرانشان را پیاپی همی می‌کشیم و زنانشان را زنده و بی‌حیا نگه می‌داریم و ما در بلندا(ی قدرت) بر (سر و سامان) آنان چیره‌ایم.»'),
(1082, 7, 128, 'موسی به قوم خود گفت: «از خدا یاری جویید و (پایداری و) شکیبایی ورزید. بی‌گمان زمین از آنِ خداست؛ آن را به هر کس از بندگانش که بخواهد به ارث (از دیگران) می‌دهد و (آن بزرگْ زندگی و) فرجام (نیک) برای پرهیزگاران است.»'),
(1083, 7, 129, '(قوم موسی) گفتند: «پیش از آنکه تو نزد ما بیایی و بعد از این (هم) که سوی‌مان آمدی آزار شدیم.» گفت: «امید است پروردگارتان دشمن‌تان را هلاک کند و شما را روی زمین جانشین (آنان) سازد، پس بنگرد چگونه عمل می‌کنید.»'),
(1084, 7, 130, 'و بی‌گمان ما فرعونیان را بی‌چون به خشکسالی و کمبودی از محصولات دچار کردیم، شاید به خوبی (حقیقت را) یاد کنند.'),
(1085, 7, 131, 'پس هنگامی که (حالت) نیکی (به دلخواهشان) به آنان روی آورد گویند: «این برای (شایستگی) خود ماست و چون گزندی به آنان در رسد به موسی و همراهانش شگون بد می‌زنند. هان (که سرچشمه‌ی) شگونشان نزد خداست، لیکن بیشترشان نمی‌دانند.'),
(1086, 7, 132, 'و گفتند: «هر (زمان)و هرگونه نشانه‌ی ربانی - که به وسیله‌ی آن ما را افسون کنی - برایمان بیاوری، ما برایت ایمان‌آورندگان نیستیم.»'),
(1087, 7, 133, 'پس بر آنان طوفان و ملخ و شپش و قورباغه‌ها و خون را - حال آنکه نشانه‌های جداسازی شده بود- فرستادیم. پس سرکشی کردند و گروهی مجرم بودند.'),
(1088, 7, 134, 'و هنگامی که اضطراب و نکبت بر آنان فرود آمد گفتند: «ای موسی! پروردگارت را به عهدی که نزد تو کرده برایمان بخوان، اگر این اضطراب را از ما بگشایی بی‌گمان برایت به‌راستی ایمان خواهیم آورد و بنی‌اسرائیل را بی‌چون با تو به‌درستی روانه خواهیم ساخت.»'),
(1089, 7, 135, 'پس چون اضطراب نکبت‌بار را - تا سر رسیدی که آنان بدان رسنده‌اند - از آنها برگشودیم، ناگهان ایشان پیمان می‌شکنند.'),
(1090, 7, 136, 'پس (سرانجام) از آنان انتقام گرفتیم، آن‌گاه در دریا غرقشان ساختیم، به سزای آنکه با آیات ما (همان‌ها و ما را) تکذیب کردند و از آنها غافل بوده‌اند.'),
(1091, 7, 137, 'و به آن گروهی که مستضعف بوده‌اند (بخش‌های) باختر و خاور سرزمین (فلسطین) را - که در آن، برکت نهاده بودیم برایشان به میراث (از فرعونیان) عطا کردیم و به پاس آنکه صبر کردند نیکوترین کلمه‌ی پروردگارت به فرزندان اسرائیل تحقق یافت و آنچه را که فرعون و قومش می‌ساخته و می‌افراشته‌اند ویران کردیم.'),
(1092, 7, 138, 'و فرزندان اسرائیل را از دریا گذراندیم. پس بر (سر و سامان) قومی (بر) آمدند دیدند بر (پرستش) بت‌هایی که برای خودشان بود همچنان پایدار و برقرارند. گفتند: «ای موسی! همان گونه که برای آنان خدایانی است، برای ما (نیز) خدایی قرار ده.» گفت: «به‌راستی شما گروهی هستید که نادانی می‌کنید.»'),
(1093, 7, 139, 'بی‌گمان آنچه ایشان در آنند نابود و بریده شده است و آنچه انجام می‌داده‌اند باطل است.'),
(1094, 7, 140, 'گفت: «آیا غیر از خدا معبودی برایتان بجویم، حال آنکه او شما را بر جهانیان برتری داده است‌؟»'),
(1095, 7, 141, 'و چون شما را از فرعونیان نجات دادیم، در حالی که شما را سخت شکنجه می‌کردند، پسرانتان را می‌کشتند و زنانتان را زنده و بی‌حیا باقی می‌گذاشتند. حال آنکه در این (عذاب بزرگ) برای شما بلایی بزرگ از جانب پروردگارتان است.'),
(1096, 7, 142, 'و با موسی، سی شب وعده گذاشتیم و آنها را با ده شب دیگر به اتمام رساندیم؛ پس وقت (معینِ) پروردگارش در چهل شب به سر آمد و موسی (هنگام رفتن به کوه طور) به برادرش هارون گفت: «در میان قومم جانشینم باش و (کار آنان را) اصلاح کن، و راه افسادگران را پیروی مکن.»'),
(1097, 7, 143, 'و هنگامی‌که موسی به میعادگاه ما آمد و پروردگارش با او سخن گفت، عرض کرد: «پروردگارم! خود را به من بنمای تا سویت بنگرم.» فرمود: «هرگز مرا نتوانی نگریست. لیکن به کوه بنگر، پس اگر بر جای خود قرار گرفت، دیرا (که) مرا خواهی دید.» پس چون پروردگارش بر کوه جلوه (ی قدرتی برتر از توانش) نمود، آن را خرد کرد و موسی بیهوش بر زمین افتاد و چون به خود آمد، گفت: «تو منزهی. سویت بازگشتم و من نخستین مؤمنانم.»'),
(1098, 7, 144, 'فرمود: «ای موسی! من تو را به‌راستی با رسالت‌هایم و با سخنم بر مردمان (در دوران رسالتت) برگزیدم. پس آنچه را به تو دادم برگیر و از سپاسگزاران باش.»'),
(1099, 7, 145, 'و در الواح [:تورات] برای او از هر چیزی پندی و تفصیلی نگاشتیم. پس: «آن را به جد و جهدی کامل برگیر و قوم خود را فرمان ده با بهترین روش [:کاوش و کوشش] آن را فراگیرند. به زودی سرای نافرمانان را به شما می‌نمایانم.»'),
(1100, 7, 146, 'زودا کسانی را که در زمین به ناحق تکبر می‌ورزند، از آیاتم رویگردان سازم. (به طوری که) اگر هر نشانه‌ای (ربانی) را بنگرند، بدان ایمان نیاورند و اگر راه سراشیب رشد را ببینند، آن را راهی (درست) برنمی‌گیرند و اگر راه سراشیب گمراهی را ببینند آن را راهی (درست برای خود) برمی‌گیرند. این بدین سبب است که آنان با آیات ما (همان‌ها و ما را) تکذیب کردند و (از)غافلان بوده‌اند.'),
(1101, 7, 147, 'و کسانی که با آیات ما (همان‌ها و ما) و دیدار آخرت را تکذیب کردند اعمالشان تباه شده است. آیا جز آنچه می‌کردند کیفر می‌بینند؟'),
(1102, 7, 148, 'قوم موسی پس از (رهسپار شدن) او (به میعادگاهش) از زیورهای خود مجسمه‌ی گوساله‌ای برای خود برگرفتند که (صدایی) همچون صدای گوساله داشت. آیا ندیدند که آن مجمسمه(ی گوساله) هرگز با ایشان سخن نمی‌گوید و راهی راهوار به آنها نمی‌نماید؟ آن را (به پرستش) گرفتند حال آنکه ستمکار بوده‌اند.'),
(1103, 7, 149, 'و چون (جریان شومشان) در برابرشان آشکارا سقوط داده شد و دیدند که واقعاً گمراه شده‌اند، گفتند: «اگر پروردگارمان به ما همواره رحم نکند و برایمان نبخشاید، همانا بی‌گمان از زیانکاران خواهیم بود.»'),
(1104, 7, 150, 'و هنگامی‌که موسی، خشمگین و اندوهگین سوی قوم خود بازگشت، گفت: «پس از من، چه بدچیزی وانهادید. آیا فرمان [:عذاب‌] پروردگارتان را شتاب کردید؟» و الواح را افکند و سر برادرش را بر گرفت حال آنکه او را به طرف خود می‌کشید. (هارون) گفت: «ای پسر مادرم! این قوم بی‌گمان مرا ناتوان یافتند و چیزی نمانده بود مرا بکشند. پس دشمن‌شادم مکن و در شمار گروه ستمکارانم قرار مده.»'),
(1105, 7, 151, '(موسی) گفت: «پروردگارم! برای من و برادرم پوشش بنه و ما را در (پناه) رحمت خود در آور، حال آنکه تو رحم کننده‌ترین رحم کنندگانی.»'),
(1106, 7, 152, 'بی‌گمان، کسانی که گوساله را (به پرستش) گرفتند، به زودی خشمی از پروردگارشان و ذلّتی در زندگی دنیاشان به ایشان خواهد رسید و ما این‌گونه افترازنندگان را کیفر می‌دهیم.'),
(1107, 7, 153, 'و کسانی که مرتکب گناهان شدند، سپس بعد از آن توبه کردند و ایمان آوردند، همواره پروردگارت پس از آن بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(1108, 7, 154, 'و چون خشم از موسی فرو نشست، الواح را برگرفت، حال آنکه در نوشته‌ی آن - برای کسانی که برای پروردگارشان با اضطراب هراسانند- هدایت و رحمتی بود.'),
(1109, 7, 155, 'و موسی از گروه خود هفتاد مرد برای میقات ما برگزید. پس چون آن زلزله(‌ی جان‌فرسا) آنان را در گرفت، گفت: «پروردگارم! اگر می‌خواستی، آنان را پیش از این و مرا (نیز) هلاک می‌ساختی. آیا ما را به آنچه کم‌خردان ما کرده‌اند هلاک می‌کنی‌؟ این جز آزمایش آتشین تو نیست. هر که را بخواهی با آن گمراه می‌کنی و هر که را بخواهی هدایت می‌فرمایی. تو سرور مایی، پس برایمان بپوشان و به ما رحم کن و تو بهترین پوشندگانی.»'),
(1110, 7, 156, '« و برایمان در این (زندگی) دنیا نیکی ثبت و ضبط فرمای و در آخرت (نیز)؛ ما همواره به سوی تو رهنمون شدیم.» فرمود: «عذاب خود را به هر کس بخواهم می‌رسانم و رحمتم همه چیز را فرا گرفته است. پس به زودی آن را برای کسانی که پرهیزگاری می‌کنند و زکات می‌دهند و آنان که به آیات ما ایمان می‌آورند، می‌نویسم.»'),
(1111, 7, 157, 'کسانی که فرستاده‌ی برجسته، پیامبر درس نخوانده را - که (نام) او را نزد خود، در تورات و انجیل نوشته می‌یابند - پیروی می‌کنند، حال آنکه آنان را به کار پسندیده فرمان می‌دهد و از کار ناپسند باز می‌دارد و برایشان چیزهای پاکیزه را حلال و ناپاکیزه‌ها را برایشان حرام می‌گرداند و بار گرانشان را و غل‌هایی که بر (سر و سامان)شان بود از ایشان بر می‌دارد، پس کسانی که بدو ایمان آوردند و بزرگش داشتند و یاریش کردند و نوری را که با او نازل شده است پیروی کردند، ایشان همان رستگارکنندگان (خود و دیگران)اند.'),
(1112, 7, 158, 'بگو: «هان ای مردمان! من به‌راستی پیامبر خدا سوی همه‌ی شمایم. کسی که فرمانروایی آسمان‌ها و زمین تنها از آن اوست. هیچ معبودی جز او نیست. زنده می‌کند و می‌میراند. پس به خدا و فرستاده‌ی او- پیامبر برجسته و درس ناخوانده‌ای که به خدا و کلمات او ایمان دارد- بگروید و او را پیروی کنید، شاید شما هدایت شوید.»'),
(1113, 7, 159, 'و از (میان) قوم موسی گروهی هستند که به حق (مردمان را) راهنمایی می‌کنند و به عدالت، داوری می‌نمایند.'),
(1114, 7, 160, 'و آنان را به دوازده نواده - که هر یک امتی بودند - از یکدیگر جدا ساختیم و به موسی - چون قومش از او آب خواستند - وحی کردیم که: «با عصایت به سنگ بزن.» پس، از آن دوازده چشمه جستن گرفت (و) هر گروهی آبشخور خود را همی شناخت. و ابر را بر فرازشان سایبان کردیم و گزانگبین و آرامش را برایشان فروفرستادیم (و گفتیم:) «از چیزهای پاکیزه‌ای که روزیتان کرده‌ایم بخورید.» و بر ما ستم نکردند، ولی بر خودهاشان ستم می‌کرده‌اند.'),
(1115, 7, 161, 'و چون بدیشان گفته شد: «در این مجتمَع سکونت گزینید و از آن - هرگونه و (از) هرجا خواستید - بخورید و بگویید "پروردگارمان! گناهان ما را فرو ریز،" و سجده‌کنان از دروازه‌(ی شهر) در آیید، تا گناهانتان را برایتان بپوشانیم. به زودی نیکوکاران را (پاداشی) افزون می‌دهیم.»'),
(1116, 7, 162, 'پس، کسانی از آنان که ستم کردند، سخنی را که به ایشان گفته شده بود به سخنی مغایر با آن تبدیل کردند. پس به سزای آنکه ستم می‌نموده‌اند، عذابی اضطراب‌آمیز از آسمان بر آنان فرو فرستادیم.'),
(1117, 7, 163, 'و درباره‌ی اهالی مجتمعی که کنار دریا بودند، از ایشان بپرس چون در روز شنبه تجاوز می‌کردند؛ آن‌گاه که روز شنبه‌ی آنان، ماهیانشان بسیار و نمایان روی آب می‌آمدند، و روزهای غیر شنبه سویشان نمی‌آمدند. این‌گونه ما آنان را به سبب آنکه نافرمانی می‌کرده‌اند، می‌آزمودیم.'),
(1118, 7, 164, 'و چون امتی از اینان گفتند: «چرا گروهی را که خدا هلاک‌کننده‌ی ایشان، یا شدیداً عذاب‌کننده‌ی ایشان است پند می‌دهید؟» گفتند: «تا پوزشی سوی پروردگارتان باشد و (تا) شاید آنان پرهیزگاری کنند.»'),
(1119, 7, 165, 'پس هنگامی که آنچه را بدان یادآوری شده بودند، فراموش کردند، کسانی را که از این (کار) بد باز می‌داشتند نجات دادیم و کسانی را (هم) که ستم کردند، به سزای آنکه نافرمانی می‌نمودند، به عذابی بس ناخوشایند گرفتار کردیم.'),
(1120, 7, 166, 'پس هنگامی که از آنچه از آن نهی شده بودند سرپیچی کردند [:حیله زدند] به آنان گفتیم: «بوزینگانی رانده شده باشید.»'),
(1121, 7, 167, 'و چون پروردگارت اعلام داشت همانا تا روز قیامت بر آنان [:یهودیان] کسانی را بی‌گمان خواهد گماشت که ایشان را به عذابی بد گرفتار کنند. به‌راستی پروردگارت همانا زودکیفر است و بی‌چون او همواره بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(1122, 7, 168, 'و آنان را در زمین (به صورت) گروه‌هایی پراکنده ساختیم: برخی از آنان درستکارند و برخی از آنان بجز اینانند. و آنها را به نیکی‌ها و بدی‌ها آزمودیم، شاید ایشان (سوی خدا) بازگردند.'),
(1123, 7, 169, 'پس بعد از آنان جانشینانی ناخلف وارث کتاب (آسمانی) شدند در حالی‌که متاع این دنیای پست را می‌گیرند و می‌گویند: «به زودی بخشوده خواهیم شد.» و اگر متاعی مانند آن به ایشان در رسد (باز) آن را می‌ستانند. آیا از آنان پیمان کتاب (وحیانی) گرفته نشده که جز حق نسبت به خدا سخنی نگویند، حال آنکه آنچه در آن (کتاب) است آموختند؟ و سرای آخرت برای کسانی که پروا می‌کنند بهتر است. آیا پس خردورزی نمی‌کنید؟'),
(1124, 7, 170, 'و کسانی که به وسیله‌ی کتاب (وحیانی، خودهاشان و دیگران را) نگهبانی می‌کنند و نماز بر پا داشتند (بدانند که) ما بی‌گمان اجر اصلاح‌کنندگان را تباه نخواهیم ساخت.'),
(1125, 7, 171, 'و چون (کوه) طور را (بر) فرازشان همچون سایبانی برکندیم (و برافراشتم) و چنان پنداشتند که (کوه) به‌راستی بر (سر)شان فرود آینده است. (و گفتیم:) «آنچه را که به شما داده‌ایم به جد و جهدی شدید برگیرید و آنچه را در آن است به یاد آرید. شاید شما (از این گناهان) پرهیز کنید.»'),
(1126, 7, 172, 'و چون پروردگارت از پشت فرزندان آدم، ذریّه‌ی آنان [:‌‌‌فطرت‌هاشان را در بلندای دیدگان تکوین] برگرفت و ایشان را بر خودهاشان به گواهی گرفت (و گفت:) «آیا پروردگار شما نیستم‌؟» (در بعد تکوینشان با زبان حالشان) گفتند: «چرا، گواهی دادیم.‌» تا مبادا روز قیامت بگویید ما از این (امر) غافل بوده‌ایم.'),
(1127, 7, 173, 'یا بگویید: «تنها پدران ما از پیش مشرک بودند و ما فرزندان و نوادگانی ناچیز (و گوش به فرمان) پس از ایشان بودیم. آیا پس ما را به خاطر آنچه باطل‌کنندگان (حکم فطرت و عقل و شریعت) انجام داده‌اند هلاک می‌کنی‌؟»'),
(1128, 7, 174, 'و این گونه آیات (خود) را جداسازی می‌کنیم و شاید آنان (سوی حق) بازگردند.'),
(1129, 7, 175, 'و خبر مهم آن کس که آیاتی از خود را به او دادیم بر ایشان بخوان. پس از آنها جدایی گزید. آن‌گاه شیطان او را بی‌امان دنبال کرد. و (او) از گمراهان بود.'),
(1130, 7, 176, 'و اگر بخواهیم (قدر) او را به وسیله‌ی آنها بالا می‌بریم‌؛ اما او به زمین (و زمینه‌ای) گرایید و از هوای نفس خود پیروی کرد. پس داستانش چون داستان سگ است (که) اگر بر آن حمله‌ور شوی به له‌له زبان از کام بر آرد و اگر رهایش کنی (باز هم) زبان از کام به له‌له بر آرد. این نمایه‌ی آن گروهی است که با آیات ما (همان‌ها و ما را) تکذیب کردند. پس این داستان‌ها را (برایشان) برخوان، شاید آنان بیندیشند.'),
(1131, 7, 177, 'چه زشت است داستان گروهی که با آیات ما (همان‌ها و ما را) تکذیب و به خود ستم می‌نموده‌اند.'),
(1132, 7, 178, 'هر کسی را خدا هدایت کند، هم(او) به خوبی راه یافته است و کسانی را که گمراه کند، آنان خودهاشان زیان‌کارانند.'),
(1133, 7, 179, 'بی‌گمان بسیاری از جنیان و آدمیان را بی‌چون برای دوزخ (در اثر اعمالشان) نمایان ساخته‌ایم (چرا که) دل‌هایی دارند که با آنها (حقایق را) دریافت نمی‌کنند و چشمانی دارند که با آنها نمی‌بینند و گوش‌هایی دارند که با آنها نمی‌شنوند. آنان همانند چهارپایان بلکه گمراه‌ترند. (آری،) ایشان (هم) اینان غافلانند.'),
(1134, 7, 180, 'و نیکوترین نام‌ها ویژه‌ی خداست. پس او را با آنها بخوانید و کسانی را که در مورد نام‌های او به ژرفای کژی می‌گرایند رها کنید. زودا (که) به (سزای) آنچه انجام می‌داده‌اند کیفر خواهند یافت.'),
(1135, 7, 181, 'و از (میان) کسانی که آفریدیم، گروهی هستند که با حق (و‌حیانی) هدایت می‌کنند و با آن عدالت و تعادل برقرار می‌سازند.'),
(1136, 7, 182, 'و کسانی که به وسیله‌ی آیات ما (همان‌ها و ما را) تکذیب کردند، به‌گونه‌ای که نمی‌دانند غفلت‌زده آنان را در ژرفای گمراهی می‌نهیم،'),
(1137, 7, 183, 'و برایشان مهلت می‌دهم. به‌راستی مَکر من پایدار و استوار است.'),
(1138, 7, 184, 'آیا و نیندیشیدند (که) همنشین آنان [:رسول گرامی] هیچ جنونی ندارد؟ او جز هشداردهنده‌ای روشنگر نیست.'),
(1139, 7, 185, 'آیا و در ملکوت و حقیقت آسمان‌ها و زمین - و هر چیزی که خدا آفریده است - ننگریسته‌اند، و اینکه شاید اجلشان نزدیک شده باشد؟ پس به کدامین حدیثی بعد از آن [:خدا و قرآن و رسولِ قرآن] ایمان می‌آورند؟'),
(1140, 7, 186, 'هر که (را) خدا گمراه کند، برای او هیچ راهبری نیست و (خدا) آنان را در ژرفای طغیانشان سرگردان وامی‌گذارد.'),
(1141, 7, 187, 'از تو درباره‌ی ساعت [:قیامت] می‌پرسند، که وقفه‌اش [:تحویل ساعت دنیا به ساعت آخرت] چه وقت است‌؟ بگو: «علم آن، تنها نزد پروردگار من است. جز او (هیچ کس) آن را به موقع خود آشکار نمی‌گرداند. (این حادثه) بر آسمان‌ها و زمین گران است (و) جز (به) ناگهان شما را در نرسد.» از تو (به گونه‌ای) می‌پرسند (که) گویا تو از (زمان وقوع) آن آگاهی. بگو: «علم آن تنها نزد خداست، ولی بیشتر مردمان نمی‌دانند.»'),
(1142, 7, 188, 'بگو: «برای خودم سود و زیانی در توان ندارم، جز آنچه خدا خواسته، و اگر غیب می‌دانستم، به راستی از خیر فزونی بر می‌گرفتم و بدی به من نمی‌رسید. من جز هشداردهنده و بشارت‌دهنده‌ای - برای گروهی که ایمان می‌آورند - نیستم.»'),
(1143, 7, 189, 'اوست کسی که شما را از نفس واحدی آفرید و جفت وی را (نیز) از آن پدید آورد تا بدان آرام گیرد. پس چون (نسل وی) زنانی را باردار کردند، خدا، پروردگار خود را خواندند که: «اگر به ما (فرزندی) شایسته عطا کنی بی‌گمان از سپاسگزاران خواهیم بود.»'),
(1144, 7, 190, 'پس چون به آن دو، فرزندی شایسته داد، در آنچه (خدا) به ایشان داده بود، برای او شریکانی قرار دادند. پس خدا از آنچه (با او) شریک می‌گردانند برتر است.'),
(1145, 7, 191, 'آیا چیزی را (با او) شریک می‌کنند که چیزی را نمی‌آفریند، حال آنکه خودشان (هم) آفریده می‌شوند؟'),
(1146, 7, 192, 'و نمی‌توانند آنان را یاری کنند و نه خویشتن را یاری دهند.'),
(1147, 7, 193, 'و اگر آنها را به راه (راست) فراخوانید، از شما پیروی نمی‌کنند؛ چه آنها را بخوانید یا خاموش بمانید، بر شما یکسان است.'),
(1148, 7, 194, 'کسانی را که به جای خدا می‌خوانید، بی‌گمان بندگانی همانند شمایند. پس آنها را (در بد و خوب) بخوانید. اگر راست می‌گویید باید شما را اجابت کنند.'),
(1149, 7, 195, 'آیا برایشان پاهایی است که با آنها راه می‌روند، یا دست‌هایی دارند که با آنها حمله‌ور می‌شوند، یا چشم‌هایی دارند که با آنها می‌نگرند، یا گوش‌هایی دارند که با آنها می‌شنوند؟ بگو: «شریکانتان را بخوانید، سپس درباره‌ی من حیله‌ای (ناروا) کنید. پس مرا مهلت ندهید.»'),
(1150, 7, 196, '«بی‌گمان سرور من خدایی است که قرآن را فرستاد و او شایستگان را سروری و پشتیبانی می‌کند.»'),
(1151, 7, 197, '«و کسانی را که بجز او می‌خوانید، نتوانند شما را کمک کنند و نه خودشان را کمک می‌کنند.»'),
(1152, 7, 198, '«و اگر آنها را به راه راست فرا خوانید، نمی‌شنوند و آنها را می‌بینی که سوی تو می‌نگرند در حالی که نمی‌بینند.»'),
(1153, 7, 199, '«گذشت را (پیش) بگیر و به (کار) شناخته شده [:پسندیده] فرمان ده و از نادانان روی بگردان.»'),
(1154, 7, 200, '«و اگر بی‌گمان از شیطان وسوسه‌ای افسادگر به تو در رسد (که تو را به کاری افسادگر با حیله برگمارد) پس به خدا پناه بر. به‌راستی او بسیار شنوای بس داناست.»'),
(1155, 7, 201, 'بی‌گمان کسانی که (از خدا) پروا کرده‌اند، چون وسوسه‌ای مماس، از گردشگری شیطان بدیشان در رسد (خدای را) بسی به یاد آورند؛ پس به ناگاه بینایند.'),
(1156, 7, 202, 'و برادرانشان (در شیطنت) آنان را در (ژرفای) گمراهی همی کشانند؛ سپس کوتاهی (هم) نمی‌کنند.'),
(1157, 7, 203, 'و هنگامی که برای آنان نشانه‌ای (ربانی، موافق میلشان) نیاوری، گویند: «چرا آن را برنگزیدی‌؟» بگو: «من فقط آنچه را که از پروردگارم به من وحی می‌شود پیروی می‌کنم. این (قرآن) دیدگاه‌هایی است از جانب پروردگارتان، و برای گروهی که ایمان می‌آورند هدایت و رحمتی (بزرگ) است.»'),
(1158, 7, 204, 'و هنگامی که قرآن خوانده شود، گوش بدان فرا دارید و (خود و دیگران را) خاموش کنید ، شاید مورد رحمت قرار گیرید.'),
(1159, 7, 205, 'و در خودت [:جانت] پروردگارت را - بامدادان و شامگاهان - با تضرّع و ترس - بی‌صدای بلند - یاد کن و از غافلان مباش.'),
(1160, 7, 206, 'کسانی که نزد [:قرب معرفتی] پروردگارت هستند، از پرستش او تکبّر نمی‌ورزند و او را به پاکی می‌ستایند و تنها برای او سجده می‌کنند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, '(ای پیامبر!) تو را از انفال [:اموال همگانی افزون بر کوشش‌هاشان] می‌پرسند. بگو: «این اموال اختصاص به خدا و فرستاده‌(ی او) دارد. پس از خدا پروا بدارید و میان یکدیگر سازش دهید و اگر ایمان داشته‌اید از خدا و پیامبرش اطاعت کنید.»'),
(1162, 8, 2, 'مؤمنان، تنها همان کسانی‌اند که هنگامی که خدا یاد شود دل‌هایشان (از عظمتش) بهراسد و چون آیات او بر آنان خوانده شود بر ایمانشان بیفزاید، و بر پروردگارشان توکل می‌کنند.'),
(1163, 8, 3, 'کسانی که نماز را بر پا می‌دارند و از آنچه به ایشان روزی داده‌ایم انفاق می‌کنند.'),
(1164, 8, 4, 'برایشان نزد پروردگارشان درجات و پوشش و روزی‌ای گرامی و پرکرامت است.'),
(1165, 8, 5, 'همان‌گونه که پروردگارت تو را از خانه‌ات بحق بیرون آورد و (حال آنکه) دسته‌ای از مؤمنان بی‌گمان ناخوشایندشان است.'),
(1166, 8, 6, 'با تو درباره‌ی حق - بعد از آنکه روشن گردید - مجادله می‌کنند؛ گویی بی‌چون به سوی مرگ رانده می‌شوند، در حالی که آنان (با چشمان باز) می‌نگرند.'),
(1167, 8, 7, 'و چون خدا یکی از دو دسته(ی کاروان تجارتی قریش یا سپاه ابوسفیان) را به شما وعده داد که بی‌گمان از آنِ شما باشند و شما دوست داشتید که دسته‌ی بی‌سازوبرگ برای شما باشد حال آنکه خدا می‌خواهد حق [:اسلام] را با کلمات خود ثابت و کافران را ریشه‌کن کند،'),
(1168, 8, 8, 'تا حق را ثابت و باطل را نابود گرداند؛ هر چند بزهکاران را هرگز خوش نیاید.'),
(1169, 8, 9, 'چون پروردگارتان را با ناله به پناه جستید، پس دعای شما را برایتان اجابت کرد که: «من همانا شما را با هزار فرشته‌ی جلودار، صف‌اندر‌صف یاری خواهم کرد.»'),
(1170, 8, 10, 'و خدا این (وعده) را جز نویدی (برای شما) قرار نداد و برای آنکه دل‌هایتان بدان آرامش یابد. و پیروزی جز از نزد خدا نیست. خدا همواره کانون عزت و حکمت است.'),
(1171, 8, 11, 'چون (خدا) خواب سبک آرامش‌بخش را، از سوی خود بر (سراسر وجود)تان فراگیر می‌کند و از آسمان بارانی بر شما فرو می‌ریزد، تا شما را با آن پاک گرداند و وسوسه‌ی شیطان را از شما بزداید و دل‌هایتان را محکم سازد و گام‌ها(یتان) را بدان استوار دارد.'),
(1172, 8, 12, 'چون پروردگارت به فرشتگان وحی می‌کند (که): «من بی‌گمان با شمایم، پس کسانی را که ایمان آورده‌اند ثابت (قدم) بدارید. به زودی در دل کافران وحشت می‌افکنم، پس بالای گردن‌ها [:سرها]ی‌شان را بزنید و همه‌ی انگشتانشان را قلم کنید.»'),
(1173, 8, 13, 'این (کیفر) بدین سبب است که آنان بی‌چون با خدا و پیامبرش به جدایی و سرپیچی برخاستند و هر کس با خدا و پیامبرش به جدایی (و دوگانگی) برخیزد، خدا بی‌گمان سخت‌کیفر است.'),
(1174, 8, 14, 'این (عذاب دنیای شماست)؛ پس آن را بچشید و (بدانید) برای کافران بی‌چون عذابِ آتش است.'),
(1175, 8, 15, 'هان ای کسانی که ایمان آوردید! هر گاه با کافران روبه‌رو شدید، پشت‌ها(تان) را به آنان مکنید.'),
(1176, 8, 16, 'و هر کس در آن هنگام (و هنگامه) به آنان پشت کند - مگر آنکه (هدفش) کناره‌گیری برای جنگی یا پیوستن به جمعی (دیگر از همرزمانش) باشد - همواره به خشم خدا برگشته (و گرفتار شده) و چه بد سرانجامی است.'),
(1177, 8, 17, 'پس شما آنان را نکشتید، ولی خدا آنان را کشت و چون (ریگ به طرف آنان) افکندی، تو (خود) نیفکندی، بلکه خدا افکند (تا کافران را مغلوب کند) و تا بدین وسیله مؤمنان را از آن (کشتار) با آزمایشی نیکو بیازماید. به‌راستی خدا بسیار شنوای بس داناست.'),
(1178, 8, 18, '(ماجرایتان) این (بزرگْ‌جریان) بود و (بدانید که) خدا بی‌گمان سست‌کننده‌ی نیرنگ کافران است.'),
(1179, 8, 19, 'اگر (شما) پیروزی را می‌طلبید، اینک پیروزی به سراغتان آمد و اگر باز ایستید، آن برایتان بهتر است و اگر (به جنگ) برگردید ما هم بر (علیه شما) می‌گردیم و (بدانید که) گروه شما هر چند زیاد باشد، هرگز از شما چیزی را دفع نتوانند کرد. و بی‌گمان خدا با مؤمنان است.'),
(1180, 8, 20, 'هان ای کسانی که ایمان آوردید! خدا و فرستاده‌‌اش را فرمان برید و از وی روی بر متابید، حال آنکه (سخنان او را) می‌شنوید.'),
(1181, 8, 21, 'و مانند کسانی مباشید که گفتند: «شنیدیم.‌» حال آنکه نمی‌شنوند.'),
(1182, 8, 22, 'بی‌چون بدترین جنبندگان نزد خدا کرها و گُنگ‌هایی‌اند که خردورزی نمی‌کنند.'),
(1183, 8, 23, 'و اگر خدا در آنان خیری می‌دانست بی‌گمان (حق را) به آنان می‌شنوانید، و اگر به آنان می‌شنوانید، همانا (باز) در حال اعراض، روی بر می‌تافتند.'),
(1184, 8, 24, 'هان ای کسانی که ایمان آوردید! چون خدا و پیامبر(ش) شما را برای چیزی فراخواند که به شما حیات می‌بخشد، آنان را اجابت کنید و بدانید که خدا بی‌گمان بین انسان و دلش حایل می‌گردد. و بی‌چون تنها سوی او محشور خواهید شد.'),
(1185, 8, 25, 'و از فتنه‌ای که بی‌امان تنها به ستمکارانتان نمی‌رسد، بپرهیزید و بدانید که خدا سخت‌کیفر است.'),
(1186, 8, 26, 'و به یاد آورید چون شما در زمین، گروهی اندک و مستضعف بودید، می‌ترسیدید مردم شما را بربایند. پس (خدا) به شما پناه داد و به یاری خود شما را تأیید کرد و از چیزهای پاکیزه به شما روزی داد. شاید سپاسگزاری کنید.'),
(1187, 8, 27, 'هان ای کسانی که ایمان آوردید! به خدا و پیامبرش خیانت مکنید و (نیز) در امانت‌های خود خیانت نورزید، حال آنکه (خود) می‌دانید (خیانت است).'),
(1188, 8, 28, 'و بدانید به‌درستی اموال و فرزندانتان همواره برایتان آزمایشی آتشین‌اند؛ و اینکه به راستی نزد خدا پاداشی بزرگ است.'),
(1189, 8, 29, 'هان ای کسانی که ایمان آوردید! اگر از خدا پروا بدارید برای شما (نیرویی در) جداسازی (حق از باطل) قرار می‌دهد و گناهانتان را از شما جبران می‌کند و برای شما پوشش می‌نهد، و خدا دارنده‌ی فضل بزرگ است.'),
(1190, 8, 30, 'و چون کافران درباره‌ی تو نیرنگ می‌کنند تا تو را در بند کِشند یا بکُشند یا (از مکه) اخراج کنند و نیرنگ می‌زنند و خدا (نیز به آنان) نیرنگ می‌زند و خدا بهترین نیرنگ‌زنندگان است.'),
(1191, 8, 31, 'و هنگامی که آیات ما بر آنان خوانده شود گویند: «به‌راستی شنیدیم. اگر بخواهیم همانا ما نیز همانند این را می‌گوییم. این جز افسانه‌ها و گردآورده‌های پیشینیان نیست.»'),
(1192, 8, 32, 'و چون گفتند: «خدایا! اگر این (کتاب، هم)آن (کل) حقّ از جانب تو است، پس بر ما از آسمان سنگ‌هایی بباران یا عذابی دردناک بر (سرو سامان‌)مان بیاور.»'),
(1193, 8, 33, 'و تا تو در میان آنان هستی خدا بر آن نبوده است که ایشان را (در دنیا) عذاب کند و خدا عذاب‌کننده‌ی ایشان نبوده است، حال آنکه آنان پوشش (و پوزش) همی‌خواهند.'),
(1194, 8, 34, 'و برایشان چیست [:چه حقی است] که خدا عذابشان نکند، حال آنکه آنان [:مردمان] را از (زیارت) مسجدالحرام باز می‌دارند، درحالی‌که ایشان دوستان (خدا) و سرپرستان آن [:مسجدالحرام] نبوده‌اند؟ دوستان (خدا) و سرپرستان آن [:مسجدالحرام] جز پرهیزگاران نیستند، ولی بیشترشان نمی‌دانند.'),
(1195, 8, 35, 'و نمازشان نزد خانه(ی خدا) جز سوت کشیدن و کف زدن نبود: «پس به آن چه کفر می‌ورزیده‌اید، این عذاب را بچشید.»'),
(1196, 8, 36, 'بی‌گمان کسانی که کفر ورزیدند اموال خود را انفاق می‌کنند تا (خود و دیگران را) از راه خدا باز دارند. پس به زودی آن اموال را انفاق می‌کنند؛ سپس بر آنان حسرتی خواهد بود. سپس مغلوب می‌شوند و کسانی که کفر ورزیدند، سوی دوزخ گرد آورده می‌شوند.'),
(1197, 8, 37, 'تا خدا ناپاک را از پاک جدا(سازی) کند و ناپاک(ها) را روی یکدیگر نهد تا همه(ی این‌ها) را انباشته سازد؛ پس آنها را در جهنّم نهد. اینان، (هم)ایشان زیان‌کارانند.'),
(1198, 8, 38, 'به کسانی که کافر شدند بگو: «اگر (از تعدی بر مسلمانان یا از کفرشان) باز ایستند، آنچه (از تعدی یا کفرشان) گذشته است برایشان پوشیده می‌شود و اگر (بدان) باز گردند، بی‌گمان سنّت (خدا در مورد) پیشینیان، گذشته است.»'),
(1199, 8, 39, 'و با آنان کشتار کنید تا فتنه‌ای (بر جای) نماند و دین یکسره از آنِ خدا باشد. پس اگر باز ایستند، همواره خدا به آنچه انجام می‌دهند بیناست.'),
(1200, 8, 40, 'و اگر روی برتافتند، بدانید (که) خدا به راستی سرور شماست (و) چه نیکو سرور و چه نیکو یاوری است.'),
(1201, 8, 41, 'و بدانید که هر چیزی را به‌راستی به غنیمت گرفتید، یک‌پنجم آن برای خدا و برای پیامبر خدا و برای نزدیکترین نزدیکان (پیامبر) و (نیز برای) (کل) یتیمان و بینوایان و در راه مانده(ها‌)ست؛ اگر به خدا و (نیز) آنچه بر بنده‌ی خود در آن روز جدایی (حقّ از باطل) نازل کردیم ایمان آوردید؛ روزی که آن دو گروه با هم روبه‌رو شدند. و خدا بر هر چیزی تواناست.'),
(1202, 8, 42, 'چون شما بر دامنه‌ی نزدیکتر (کوه) بودید و آنان در دامنه‌ی دورتر (کوه) و سواران (دشمن) پایین‌تر از شما (موضع گرفته) بودند و اگر با یکدیگر وعده گذارده بودید، همانا در وعده‌گاه خود اختلاف می‌کردید. ولی (چنین شد) تا خدا کاری را که انجام‌شدنی بود به انجام رساند تا کسی که هلاک شد با دلیلی روشن هلاک گردد و کسی که زنده گشت، با دلیلی روشن زنده گردد. و خداست که به‌راستی بسیار شنوای بس داناست.'),
(1203, 8, 43, 'چون خدا آنان [:سپاه دشمنان] را در خواب به تو اندک نشان می‌دهد و اگر ایشان را به تو زیاد نشان می‌داد همواره سست می‌شدید و بی‌چون در کار (جهاد) منازعه می‌کردید. ولی خدا (شما را به حال خود) واگذاشت. به‌راستی او به (راز) ذاتی سینه‌ها بسی داناست.'),
(1204, 8, 44, 'و چون با هم برخورد کردید، آن هنگام آنان را در دیدگانتان اندک جلوه می‌دهد و شما را (نیز) در دیدگان آنان کم نمودار می‌کند تا خدا کاری را که انجام‌شدنی بوده تحقق بخشد. و تمامی امور تنها سوی او باز می‌گردد.'),
(1205, 8, 45, 'هان ای کسانی که ایمان آوردید! چون با گروهی برخورد کردید، پایداری ورزید و خدا را بسیار یاد کنید. شاید (خود و دیگران را) رستگار کنید.'),
(1206, 8, 46, 'و خدا و پیامبرش را اطاعت کنید و با هم نزاع مکنید، که سست می‌شوید و آبرو (و چیرگی)‌تان (از میان‌) می‌رود. و صبر کنید (که‌) خدا بی‌گمان با صابران است.'),
(1207, 8, 47, 'و مانند کسانی مباشید که از خانه‌هایشان - با حالت سرمستی و برای نمایش به مردم - خارج شدند، در حالی که (مردم را) از راه خدا باز می‌دارند. و خدا به آنچه می‌کنند احاطه دارد.'),
(1208, 8, 48, 'و چون شیطان اعمالشان را برایشان بیاراست و گفت: «امروز هیچ کس از مردمان بر شما پیروز نیست و من همواره همسایه‌ای برای شما هستم.‌» پس هنگامی که دو گروه، یکدیگر را دیدند (شیطان) کلاّ پشت کرد و گفت: «من از شما بیزارم، من همواره چیزی را می‌بینم که شما نمی‌بینید. من بسی از خدا بیمناکم.‌» و خدا سخت‌کیفر است.'),
(1209, 8, 49, 'چون منافقان و کسانی که در دل‌هایشان بیماری بود می‌گویند: «این مؤمنان را دینشان فریفته است.‌» و هر کس بر خدا توکل کند (بداند که) خدا همواره با عزت و حکمت است.'),
(1210, 8, 50, 'و کاش ببینی چون فرشتگان جانِ کافران را می‌ستانند، بر چهره‌هاشان و پشت‌هاشان می‌زنند و (می‌گویند:) «عذاب سوزان را بچشید.»'),
(1211, 8, 51, '«این (کیفر) به سبب دستاوردهای شماست که از پیش فرستادید و اینکه خدا هرگز برای بندگان (خود) بسیار ستمکار نیست.»'),
(1212, 8, 52, '(رفتارشان) مانند رفتار خاصّان فرعون و کسانی است که پیش از آنان بودند: به آیات خدا کفر ورزیدند، پس خدا به گناهان وخیمشان گرفتارشان کرد. بی‌گمان خدا توانمندی سخت پیگیر است.'),
(1213, 8, 53, 'این (کیفر) همواره بدین سبب است، که خدا نعمتی را که بر قومی ارزانی داشته تغییر دهنده نبوده تا آنکه آنچه را در خودهاشان است تغییر دهند. و بی‌گمان خدا بسیار شنوای بس داناست.'),
(1214, 8, 54, '(این شیوه) چون شیوه فرعونیان و پیشینیانشان است (که) با آیات پروردگارشان (همان‌ها و پروردگارشان را) تکذیب کردند. پس ما آنان را به گناهانشان هلاک نمودیم و فرعونیان را غرق کردیم؛ حال آنکه همه‌ی آنان ستمکار بوده‌اند.'),
(1215, 8, 55, 'بی‌گمان، بدترین جنبندگان نزد خدا کسانی هستند که کفر ورزیدند، پس اینان ایمان نمی‌آورند.'),
(1216, 8, 56, 'کسانی‌که از ایشان پیمان گرفتی، سپس در هر بار پیمان خود را می‌شکنند، حال آنکه ایشان پروا نمی‌دارند.'),
(1217, 8, 57, 'پس اگر همواره - با کوشش و کاوش- در جنگ بر آنان دست می‌یابی، پس به وسیله‌ی آنان، کسانی که در پی آنانند تار و مار کن؛ شاید آنان متذکر شوند.'),
(1218, 8, 58, 'و اگر به‌راستی از گروهی بیم خیانت داری (پیمانشان را) به گونه‌ای برابر (چنانچه آنان هم پیمان شکستند) سویشان بیفکن. بی‌گمان خدا خائنان را دوست نمی‌دارد.'),
(1219, 8, 59, 'و کسانی که کافر شدند هرگز گمان نکنند (که بر ما) پیشی جسته‌اند. آنان بی‌گمان نمی‌توانند (ما را) درمانده کنند.'),
(1220, 8, 60, 'و هر چه در توان و امکان دارید، از نیروها و اسب‌های جنگی آماده و بسیج کنید تا با این (تدارکات) دشمن خدا و دشمن خودتان و دیگرانی را بجز ایشان - که شما نمی‌شناسیدشان و خدا آنان را می‌شناسد - بترسانید. و هر چه در راه خدا انفاق کنید پاداشش به خود شما به شایستگی باز گردانیده می‌شود، حال آنکه بر شما ستم نرود.'),
(1221, 8, 61, 'و اگر برای صلح (و تسلیم، به سوی تو) بال گشودند [:پیش آمدند]، تو نیز برای آن بال بگشا [: بپذیر] و بر خدا توکل نمای. همواره او، (هم)او بسیار شنوای بس داناست.'),
(1222, 8, 62, 'و اگر بخواهند تو را بفریبند، به‌راستی خدا تو را بس است. اوست که تو را با یاری خود و با (یاری) مؤمنان تأیید کرد.'),
(1223, 8, 63, 'و میان دل‌هایشان الفت انداخت. اگر آنچه در زمین است همه را انفاق می‌کردی، بین دل‌هایشان الفت برقرار نمی‌کردی، ولی خدا میان آنان الفت انداخت. به‌راستی او توانایی حکیم است.'),
(1224, 8, 64, 'هان ای پیامبر برجسته! خدا تو را بس است و (نیز) کسانی از مؤمنان که پیرو تو هستند.'),
(1225, 8, 65, 'هان ای پیامبر برجسته! مؤمنان را بر کشتار (کافران) برانگیز. اگر از شما بیست تن، شکیبا باشند بر دویست تن (از آنان‌) باید چیره شوند و اگر از شما یکصد تن باشند بر هزار تن از کسانی که کافر شدند باید پیروز شوند، زیرا آنان قومی هستند که (در دریافت حقایق) بررسی نمی‌کنند.'),
(1226, 8, 66, 'اکنون خدا از (این بازدهی) به شما تخفیف داد و حال آنکه می‌دانست همواره در شما ضعفی هست. پس اگر از (میان) شما یکصد تن شکیبا باشند بر دویست تن (از آنان) باید پیروز گردند، و اگر از شما هزار تن باشند، به اذن الهی بر دو هزار تن باید غلبه کنند و خدا با شکیبایان است.'),
(1227, 8, 67, 'برای هیچ پیامبر برجسته‌ای (چنین) نبوده است که برایش اسیرانی باشند تا در زمین (و زمینه‌ی) جبهه‌ی جنگ پیروزی کامل بیاید. شما متاع زودگذر دنیا را می‌خواهید و خدا آخرت را (برایتان) می‌خواهد. و خدا عزیز حکیم است.'),
(1228, 8, 68, 'اگر در آنچه (از غنیمت) گرفتید، از جانب خدا نبشته‌ای نبود، همانا به شما عذابی بزرگ می‌رسید.'),
(1229, 8, 69, 'پس از آنچه به غنیمت برده‌اید، حلال پاکیزه‌ای را بخورید و از خدا پروا بدارید. خدا بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(1230, 8, 70, 'هان ای پیامبر برجسته! به کسانی که در دست‌های شما اسیرند بگو: «اگر خدا در دل‌های شما خیری بداند، بهتر از آنچه از شما گرفته شده به شما می‌دهد و برایتان پوشش می‌نهد. و خدا بس پوشنده‌ی رحمتگر بر ویژگان است.»'),
(1231, 8, 71, 'و اگر برایت خیانت بخواهند (بدان که) همواره پیش از این (هم) به خدا خیانت کردند. پس خدا تو را بر آنان مسلط ساخت. و خدا بس دانایی حکیم است.'),
(1232, 8, 72, 'بی‌گمان کسانی که ایمان آوردند و (در راه ایمان، از جایگاه بی‌ایمانی) هجرت کردند و با اموال و جان‌هاشان در راه خدا جهاد کردند، و کسانی که (آنان را) پناه دادند و کمک(شان) نمودند، ایشان بعضیشان اولیای بعضی (دیگر)ند و کسانی‌که ایمان آوردند و هجرت نکردند، برایتان از ولایتشان چیزی نیست تا هجرت کنند و اگر (هم) از شما (علیه دشمنانتان) یاری طلبیدند، یاریشان بر (عهده‌ی) شماست؛ مگر بر گروهی که میان شما و ایشان پیمانی است. و خدا به آنچه می‌کنید بسی بیناست.'),
(1233, 8, 73, 'و کسانی که کافر شدند اولیای یکدیگرند. اگر (به مسلمانانی که از شما یاری می‌طلبند) کمک نکنید، در زمین فتنه و فسادی بزرگ خواهد بود.'),
(1234, 8, 74, 'و کسانی که ایمان آوردند و مهاجرت کردند و در راه خدا جهاد نمودند و کسانی (هم) که (آنان را) پناه دادند و کمک کردند، اینان، به‌راستی (هم)ایشان مؤمنانند. برایشان پوشش و روزی‌ای با کرامت و وسعت است.'),
(1235, 8, 75, 'و کسانی که پس از آن ایمان آوردند و به معیت شما جهاد کردند، ایشان از شمایند و ارحام‌، بعضی به بعضی دیگر در کتاب خدا برترند. به‌راستی خدا به هر چیزی بسی داناست.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'بیزاری‌ای است (از سوی) خدا و پیامبرش فرا سوی آن مشرکانی که با ایشان پیمان بسته‌اید.'),
(1237, 9, 2, 'پس (ای مشرکان!) چهار ماه (دیگر با امنیّت) در زمین (حرم) بگردید و بدانید که شما نمی‌توانید بی‌امان خدا را به ستوه آورید. و بی‌گمان خداست که رسوا کننده‌ی کافران است.'),
(1238, 9, 3, 'و (این) اعلامی است از جانب خدا و پیامبرش در روز حجّ اکبر [:حج مقابل عمره] فراسوی مردم که خدا به‌راستی از مشرکان بیزار است و (نیز) پیامبرش (هم از مشرکان بیزار است). پس اگر (از کفر) توبه کنید، (هم)آن برای شما خوب است، و اگر روی برگردانید بدانید که شما هرگز درمانده‌کنندگان خدا نیستید. و کسانی را که کفر ورزیدند از عذابی دردناک نوید ده.'),
(1239, 9, 4, 'مگر کسانی از مشرکان که با آنان پیمان بسته‌اید، سپس چیزی از (تعهّدات خود نسبت به) شما فروگذار نکردند، و کسی را بر ضدّ شما پشتیبانی ننمودند. پس پیمانشان را فرا سویشان تا مدّت (مقرر)‌شان به اتمام رسانید. بی‌گمان خدا پرهیزگاران را دوست دارد.'),
(1240, 9, 5, 'پس چون ماه‌های حرام سپری شد، مشرکان را هر کجا یافتید بکشید و دستگیرشان کنید و به محاصره در آورید و در هر کمینگاهی به کمین آنان بنشینید. پس اگر توبه کردند و نماز بر پاداشتند و زکات دادند، رهاشان کنید. خدا بی‌گمان پوشنده‌ی رحمتگر بر ویژگان است.'),
(1241, 9, 6, 'و اگر یکی از مشرکان از تو پناه خواست، پناهش ده، تا سخن خدا را بشنود، سپس او را به مکان امنش برسان. این تأمین برای آن است که آنان بی‌گمان گروهی نادانند.'),
(1242, 9, 7, 'چگونه مشرکان را نزد خدا و نزد فرستاده‌اش پیمانی است‌؟ مگر کسانی که کنار مسجدالحرام با هم پیمان بسته‌اید. پس تا برایتان (بر این پیمان) پایداری بخواهند، بر پیمانشان پایداری بخواهید. خدا بی‌گمان پرهیزگاران را دوست می‌دارد.'),
(1243, 9, 8, 'چگونه (برای آنان پیمانی است) با آنکه اگر بر شما دست یابند، درباره‌ی شما هیچ گونه رابطه‌ای و هیچ گونه پیمانی را مراعات نمی‌کنند. شما را با دهانهاشان راضی می‌کنند حال آنکه دل‌هایشان امتناع می‌ورزد. و بیشترشان فاسقانند.'),
(1244, 9, 9, 'با آیات خدا بهای اندکی را خریدند، پس (خودشان و دیگران را) از راه خدا بازداشتند. به‌راستی آنان چه بد اعمالی انجام می‌داده‌اند.'),
(1245, 9, 10, 'درباره‌ی هیچ مؤمنی هیچ‌گونه رابطه و پیمانی را مراعات نمی‌کنند. و ایشان، (هم)اینان تجاوزکارانند.'),
(1246, 9, 11, 'پس اگر توبه کنند و نماز بر پادارند و زکات بدهند، (هم‌اینان) برادران دینی شمایند. و ما آیات (خود) را برای گروهی که می‌دانند جداسازی (و تبیین) می‌کنیم.'),
(1247, 9, 12, 'و اگر سوگندهای خود را پس از پیمانشان شکستند و در دینتان طعنه زدند، (شما هم) با پیشوایان کفر بجنگید - ایشان بی‌گمان پایبند (به هیچ سوگندی) نیستند - شاید (از پیمان‌شکنی و یا کفرشان) باز ایستند.'),
(1248, 9, 13, 'چرا با گروهی که سوگندهای خود را شکستند و برای بیرون راندن رسول همت گماشتند - و (هم)آنان نخستین بار (جنگ را) با شما آغاز کردند - کشتار نمی‌کنید؟ آیا از آنان می‌ترسید؟ پس اگر مؤمن بوده‌اید، خدا سزاوارتر است که از او بهراسید.'),
(1249, 9, 14, 'با آنان کشتار کنید (تا) خدا آنان را به دست شما عذاب نماید و خوارشان کند و شما را بر ایشان پیروز گرداند و سینه‌های (جریحه‌دار) گروه مؤمنان را شفا بخشد،'),
(1250, 9, 15, 'و خشمِ دل‌هایشان را بزداید. و خدا به هر که بخواهد برگشت می‌کند. و خدا بس دانایی حکیم است.'),
(1251, 9, 16, 'یا پنداشتید که (از آزمون خدا) رها می‌شوید، حال آنکه خدا کسانی را از میان شما که جهاد کرده و غیر از خدا و فرستاده‌ی او و مؤمنان، محرم درونی اسرارشان نگرفته‌اند، هنوز نشانه‌ای نگذارده‌؟ و خدا به آنچه انجام می‌دهید آگاه است.'),
(1252, 9, 17, 'هرگز بت‌پرستان را (چنان حقی) نبوده‌است که مساجد خدا را آباد کنند، در حالی که گواهانی بر کفر خودهاشان می‌باشند. آنانند که اعمالشان به هدر رفته و ایشان در آتش جاودانند.'),
(1253, 9, 18, 'مساجد خدا را تنها کسی آباد می‌کند که به خدا و روز بازپسین ایمان آورده و نماز بر پا داشته و زکات داده و بجز خدا (از احدی) نهراسیده. پس امید است اینان از راه‌یافتگان باشند.'),
(1254, 9, 19, 'آیا سیراب ساختن حاجیان و آباد کردن مسجدالحرام را همانند کسی پنداشته‌اید که به خدا و روز بازپسین ایمان آورده و در راه خدا جهاد کرده‌؟ (اینان) نزد خدا یکسان نیستند. و خدا بیدادگران را هدایت نمی‌کند.'),
(1255, 9, 20, 'کسانی که ایمان آورده و هجرت کرده و در راه خدا با مال و جانشان به جهاد پرداخته‌اند، درجه‌ی آنان نزد خدا بزرگ‌تر است و اینان، (هم)ایشان، به سلامت رستگارانند.'),
(1256, 9, 21, 'پروردگارشان آنان را از (جانب ویژه‌ی) خود، به رحمت و خشنودی و باغ‌هایی (در بهشت) که در آنها نعمت‌هایی پایدار دارند، مژده می‌دهد.'),
(1257, 9, 22, 'در آن باغ‌ها جاودانه‌اند. تنها خداست که همواره نزد او پاداشی بس بزرگ است.'),
(1258, 9, 23, 'هان ای کسانی که ایمان آوردید! اگر پدرانتان و برادران‌تان کفر را علیه ایمان به دوستی برگزیدند (آنان را) به سرپرستی و دوستی مگیرید، و هر کس از میان شما آنان را سرپرست (خود) گیرد، اینان، (هم)ایشان ستمکارانند.'),
(1259, 9, 24, 'بگو: «اگر پدران و برادران و زنان و خاندان شما و اموالی که گرد آوردید و تجارتی را که از کسادش بیمناکید و جایگاه‌هایی را که (آنها را) خوش می‌دارید، نزد شما از خدا و پیامبرش و جهاد در راهش محبوب‌ترند، پس منتظر باشید تا خدا فرمانش را فرود آورد.» و خدا گروه فاسقان را راهنمایی نمی‌کند.'),
(1260, 9, 25, 'خدا همواره شما را در جایگاه‌های بسیاری، به‌راستی یاری کرده است؛ و (نیز) در روز حُنین، آن هنگام که شمارِ زیادتان شما را به شگفت آورده (و مغرور) و خوشحالتان کرده بود. پس (اینها) به هیچ وجه از شما دفع (خطر) نکرد و زمین با همه‌ی فراخیش بر شما تنگ گردیده بود. سپس شما پشت‌کنان(به دشمن) بودید، در حالی که فرار کردید.'),
(1261, 9, 26, 'سپس خدا آرامش (ویژه‌ای از)خود بر فرستاده‌اش و بر مؤمنان فرستاد و سپاهیانی (هم) فروفرستاد (که) آنها را نمی‌دیدید و کسانی را که کفر ورزیدند عذاب کرد و این (همان) سزای کافران بود.'),
(1262, 9, 27, 'سپس خدا پس از این (واقعه) به هرکس بخواهد بازگشت می‌کند. و خدا بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(1263, 9, 28, 'هان ای کسانی که ایمان آوردید! بت‌پرستان تنها (از نظر عقیده و اعمالشان) ناپاکند، پس نباید از سال آینده به مسجدالحرام نزدیک شوند و اگر (در این قطع رابطه) از فقر بیمناکید، اگر خدا بخواهد در آینده‌ای شما را به فضل خویش بی‌نیاز می‌گرداند. خدا همواره بسی دانای حکیم است.'),
(1264, 9, 29, 'با کسانی از اهل کتاب که به خدا و روز بازپسین ایمان نمی‌آورند و آنچه را خدا و فرستاده‌اش حرام کرده حرام نمی‌دارند و به دین حق نمی‌گروند، کشتار کنید، تا به دست خود در حال کوچک‌منشی جزیه دهند.'),
(1265, 9, 30, 'و یهودیان گفتند: «عزیر، پسر خداست‌.» و نصرانیان گفتند: «مسیح پسر خداست.» (در) این گفتارشان (که) به دهانهاشان (می‌آورند)، با گفتار کسانی که پیش از این کافر شده‌اند همانندی و هماهنگی دارند. خدا با آنان کشتارکرده. (و) کجا و چگونه (از حق) بازگردانده می‌شوند؟'),
(1266, 9, 31, '(آنان) دانشمندان یهودیشان و دانشمندان مسیحیشان و (نیز) مسیح پسر مریم را خدایانی غیر از خدا گرفتند، با آنکه امر نشده بودند جز (به اینکه) خدایی یگانه را بپرستند که هیچ معبودی جز او نیست (و) منزّه است از آنچه (با وی) شریک می‌گردانند.'),
(1267, 9, 32, 'می‌خواهند نور خدا را با دهان‌هاشان خاموش کنند، و خدا اِبا دارد بجز اینکه نورش را به اتمام رساند، هر چند کافران را بسی ناخوش آید.'),
(1268, 9, 33, 'اوست کسی که پیامبرش را با هدایت و طاعتِ پای برجا فرستاد، تا دینش را بر هر چه دین است پیروز گرداند، هر چند مشرکان را بسی ناخوش آید.'),
(1269, 9, 34, 'هان ای کسانی که ایمان آوردید! بسیاری از دانشمندان یهودیان و راهبان نصرانیان همواره اموال مردمان را به ناروا می‌خورند و (خود و دیگران را) از راه خدا باز می‌دارند و کسانی که زر و سیم می‌اندوزند و آنها را در راه خدا انفاق نمی‌کنند، ایشان را به عذابی دردناک نوید ده.'),
(1270, 9, 35, 'روزی که آن اندوخته‌ها در آتش دوزخ بسی داغ شوند، پس پیشانی و پهلو و پشتِ آنان با آنها تافته گردد (به آنها گویند:) «این است آنچه برای خود اندوختید، پس آنچه را می‌اندوختید بچشید.»'),
(1271, 9, 36, 'همواره، شماره‌ی ماه‌ها نزد خدا- از روزی که آسمان‌ها و زمین را آفرید- در کتاب (علمِ) خدا دوازده ماه است‌؛ از این‌ها، چهار ماه، حرام [:محترم] است. این است (همان) دین قیام و پابرجای پربها. پس در این چهار ماه بر خود ستم مکنید و با مشرکان در حال بازدارندگی کامل (از گزندشان) کشتار کنید، چنان‌که آنان به حال بازدارندگی از گزندتان با شما کشتار می‌کنند. و بدانید (که) خدا همواره با پرهیزگاران است.'),
(1272, 9, 37, 'جز این نیست (که) تأخیر انداختن (ماه‌های حرام) فزونی در کفر است که کافران بدان سبب گمراه می‌شوند؛ آن را یک سال حلال می‌شمارند و یک سال (دیگر) آن را حرام می‌کنند تا با شماره‌ی ماه‌هایی که خدا حرام کرده است هماهنگ سازند. پس آنچه را خدا حرام کرده (بر خود) حلال گردانند (و) زشتی اعمالشان برایشان آراسته شده است. و خدا گروه کافران را هدایت نمی‌کند.'),
(1273, 9, 38, 'هان ای کسانی که ایمان آوردید! شما را چه شده؟ هنگامی که به شما گفته شود: «با کوشش در راه خدا (به جنگ‌) رهسپار شوید» سخت زمین‌گیر می‌شوید. آیا به جای آخرت به زندگی دنیا خشنود شده‌اید؟ پس متاع زندگی دنیا، در (برابر) آخرت جز اندکی نیست.'),
(1274, 9, 39, 'اگر (برای نبرد) کوچ نکنید خدا شما را به عذابی دردناک عذابتان می‌کند و گروهی دیگر به جای شما بر می‌گمارد. به خدا هرگز زیانی نتوانید رسانید. و خدا بر هر چیزی تواناست.'),
(1275, 9, 40, 'اگر او [:پیامبر] را یاری نکنید همواره خدا(یش) به‌راستی یاریش کرده‌، چون او را کسانی که کفر ورزیدند (از مکه) برون راندند. وی نفر دوم از دو تن بود، آن‌گاه که این دو در غار (ثور) بودند. وقتی (حضرتش) به همراهش می‌گفت: «اندوه مدار که خدا به‌راستی با ماست.» پس خدا آرامش خود را بر او [:پیامبر و نه بر نفر دیگر] فرو فرستاد و او را با سپاهیانی که آنها را نمی‌دیدید تأیید کرد و کلمه‌ی کسانی را که کفر ورزیدند پست‌ترین (کلمه) گردانید. و کلمه‌ی خداست که (هم)او برترین (کلمه) است و خدا عزیزی حکیم است.'),
(1276, 9, 41, 'سبکباران و گرانباران (به سوی دشمنانتان) کوچ کنید و با مال و جانتان در راه خدا جهاد نمایید. اگر می‌دانسته‌اید، این برای شما خوب (و خوب‌تر) است.'),
(1277, 9, 42, 'اگر حال و مالی (در دسترسشان) و سفری راهور موافق میلشان بود، بی‌چون از پی تو می‌آمدند، ولی آن راه پر مشقّت بر آنان دور نمود و به زودی به خدا سوگند خواهند خورد: «اگر می‌توانستیم به‌راستی با شما بیرون می‌آمدیم.» اینان خودهاشان را به هلاکت می‌کشانند و خدا می‌داند که بی‌گمان آنان سخت دروغگویانند.'),
(1278, 9, 43, 'خدا از تو درگذشت؛ چرا پیش از آنکه (حال) راستگویان بر تو روشن شود، و دروغگویان را بازشناسی، به آنان اجازه دادی (که به جنگ نروند)؟'),
(1279, 9, 44, 'کسانی که به خدا و روز پایانی ایمان می‌آورند، در جهاد با مال و جانشان از تو اجازه نمی‌خواهند. و خدا به (حال) تقواپیشگان داناست.'),
(1280, 9, 45, 'تنها کسانی از تو اجازه می‌خواهند (به جهاد نروند) که به خدا و روز واپسین ایمان نمی‌آورند و دل‌هایشان بدون هیچ مستندی به شکّ افتاده‌؛ پس همانان در (ژرفای) شکّشان سرگردانند.'),
(1281, 9, 46, 'و اگر (برای جهاد) اراده‌ی بیرون رفتن داشتند، بی‌گمان برای آن ساز و برگی تدارک می‌دیدند، ولی خدا (این) انگیزششان را بسی ناپسند داشت. پس ایشان را زمین‌گیر کرد و (به آنان) گفته شد: «با درماندگان و ناتوانان (همچنان) بمانید.»'),
(1282, 9, 47, 'اگر با شما بیرون آمده بودند، جز فساد و دگرگونی و نابسامانی را برای شما نمی‌‌افزودند و به سرعت (افساد جنگی) خود را میان شما می‌انداختند، که شما را به فتنه(‌ی جنگی) می‌طلبیدند و حال آنکه در میانتان جاسوسانی بس شنوای فتنه دارند (که به نفع آنان اقدام می‌کنند). و خدا به (حال) ستمکاران داناست.'),
(1283, 9, 48, 'همواره، پیش از این (نیز) همانا در صدد فتنه‌جویی برآمدند و کارها را برای تو دگرگون ساختند؛ تا (اینکه‌) حقّ (به تمام و کمال) آمد، و امر خدا آشکار شد، حال آنکه آنان بسی ناخشنودند.'),
(1284, 9, 49, 'و برخی از آنان کسی است که می‌گوید: «مرا (در ماندن) اجازه ده و به فتنه‌ام مینداز.» هان! که آنان خود در ژرفای فتنه سقوط کردند. و به‌راستی جهنّم بی‌گمان بر کافران احاطه دارد.'),
(1285, 9, 50, 'اگر حالت نیکی‌ای تو را در رسد، آنان را بدحال می‌سازد و اگر پیشامد ناگواری تو را در رسد گویند: «ما پیش از این، تصمیم خود را به‌راستی گرفته‌ایم‌.» و شادمان روی بر می‌تابند.'),
(1286, 9, 51, 'بگو: « جز آنچه خدا برایمان مقرر داشته هرگز (آسیبی) به ما نرسد. او سرپرست ماست و مؤمنان باید تنها بر خدا توکّل کنند.»'),
(1287, 9, 52, 'بگو: «آیا شما جز یکی از این دو نیک‌ترین (زندگی) را برای ما انتظار دارید؟ در حالی که ما انتظار می‌کشیم خدا از جانب خود یا به دست ما عذابی به شما برساند. پس انتظار بکشید، که ما هم بی‌چون با شما (از) منتظرانیم.»'),
(1288, 9, 53, 'بگو: «چه با طاعت (و) چه با کراهت انفاق کنید، هرگز از شما پذیرفته نخواهد شد. بی‌گمان شما گروهی فاسقان بوده‌اید.»'),
(1289, 9, 54, 'و هیچ چیزی از پذیرفته شدن انفاق‌هایشان جلوگیری نکرد، جز آنکه همواره به خدا و به پیامبرش کفر ورزیدند و جز با (حالت) کسالت به نماز نمی‌آیند و ایشان جز در حالی بس ناخوشایند انفاق نمی‌کنند.'),
(1290, 9, 55, 'پس (نه) اموال و نه فرزندانشان تو را به شگفتی نیاورد. جز این نیست که خدا می‌خواهد در زندگی دنیا با آنها عذابشان کند و جانشان در حال کفر بر کنده شود.'),
(1291, 9, 56, 'به خدا سوگند یاد می‌کنند (که) آنان بی‌گمان به‌راستی از شمایند، در حالی که آنان از شما نیستند، لیکن گروهی هستند که (مؤمنان را از یکدیگر) جدا می‌کنند.'),
(1292, 9, 57, 'اگر پناهگاه یا غارها یا فرارگاهی می‌یافتند، بی‌گمان بس شتابزده سویش روان می‌شدند.'),
(1293, 9, 58, 'و برخی از آنان در (تقسیم) صدقات بر تو خرده می‌گیرند. پس اگر از آن (اموال) به ایشان داده شود، خشنود می‌گردند و اگر از آن به آنان داده نشود ناگهان به خشم می‌آیند.'),
(1294, 9, 59, 'و اگر آنان بدانچه خدا و پیامبرش به ایشان داده خشنود می‌گشتند و می‌گفتند: «خدا ما را بس است، به زودی خدا از فضل خود به ما می‌دهد و (نیز) پیامبرش (به ما می‌بخشد) ، ما همانا تنها سوی خدا راغبانیم» (این خود برایشان شایسته بود).'),
(1295, 9, 60, 'صدقات، تنها به کمرشکستگان (زمین‌گیر) و تهی‌دستان (خاک‌نشین) و متصدّیان (گردآوری و پخش) آن و کسانی که دلشان (برای رغبت بیشتر به ایمان) به دست آورده شده و در (راه آزادی) گرفتاران در بند و وامداران و در راه خدا اختصاص دارد. حال آنکه (این) فریضه‌ای است از جانب خدا و خدا بس دانای حکیم است.'),
(1296, 9, 61, 'و از ایشان کسانی هستند که پیامبر را آزار می‌دهند و می‌گویند: «او گوش است (و زود باور می‌کند). » بگو: « (او) به سود شما گوش است‌؛ به خدا ایمان می‌آورد و به سود مؤمنان امانشان می‌دهد و برای کسانی از شما که ایمان آورده‌اند رحمتی بزرگ است.» و کسانی که پیامبر خدا را آزار می‌رسانند، برایشان عذابی است پر درد.'),
(1297, 9, 62, 'برای (اغفال) شما به خدا سوگند یاد می‌کنند تا شما را خشنود گردانند. حال آنکه اگر مؤمن بودند سزاوارتر است که خدا و فرستاده‌ی او را خشنود سازند.'),
(1298, 9, 63, 'آیا ندانستند هر که میان خود و خدا و پیامبرش حدی [:فاصله‌ای] اندازد، بی‌گمان برای او آتش جهنّم است، حال آنکه در آن جاودانه خواهد بود. این همان خواری و بی‌مقداری بزرگ است.'),
(1299, 9, 64, 'منافقان بیم دارند از اینکه (مبادا) سوره‌ای بر آنان نازل شود که ایشان را از آنچه در دل‌هایشان هست خبری مهم دهد. بگو: «مسخره کنید. بی‌تردید خدا آنچه را که (از آن) می‌ترسید برون آورنده است.»'),
(1300, 9, 65, 'و بی‌گمان اگر از ایشان بپرسی، بی‌‌اَمان همی خواهند گفت: «ما فقط (در کار نابسامانمان) فرورفته و بازی می‌کرده‌ایم.» بگو: «آیا خدا و آیات او و پیامبرش را مسخره می‌کرده‌اید؟»'),
(1301, 9, 66, 'عذر نیاورید. بی‌چون شما بعد از ایمانتان کافر شدید. اگر از گروهی از شما (به‌خاطر پوزششان) درگذریم، گروهی (دیگر) را به علت اینکه همواره تبهکار بوده‌اند عذاب خواهیم کرد.'),
(1302, 9, 67, 'مردان و زنان منافق همانند یکدیگرند؛ (دیگران و خودشان را) به کار ناشناخته [:ناپسند] وا می‌دارند و از کار شناخته شده [:پسندیده] باز می‌دارند، و دست‌های خود را (از خوبی‌ها) فرو می‌بندند. خدا را فراموش کردند، پس او (هم) فراموششان کرد. منافقان، بی‌گمان (هم)اینان فاسقانند.'),
(1303, 9, 68, 'خدا به مردان و زنان منافق و کافران، آتش جهنّم را وعده داده است، (که) در آن جاودانه‌اند. همان (آتش) برایشان کافی است و خدا لعنتشان کرده، و برایشان عذابی پایدار کننده است.'),
(1304, 9, 69, '(حالِ شما منافقان) همچون (حال) کسانی است که پیش از شما بودند. آنان از شما نیرومندتر و دارای اموال و فرزندانی بیشتر بوده‌اند. پس بر حسب خوی (ناپسند) خودشان از نصیب خودشان برخوردار شدند. و شما (هم) به خوی (بد) خود بهره(ها) بردید، چنان که پیشینیانتان به خوی (بد)شان بهره‌(ها) بردند. و شما (در باطل) فرو رفتید، همان گونه که آنان (در باطل) فرو رفتند. آنان اعمالشان در دنیا و آخرت به هدر رفت و اینان، (هم)ایشان زیان‌کارانند.'),
(1305, 9, 70, 'آیا گزارش مهم کسانی که پیش از آنان بودند - قوم نوح و عاد و ثمود و قوم ابراهیم و اصحاب مَدیَن و شهرهای زیر و رو شده - به ایشان نرسیده است‌؟ پیامبرانشان دلایل آشکار برایشان آوردند. پس خدا هرگز بر آن نبوده تا به آنان ستم کند، ولی آنان بر خود ستم روا می‌داشته‌اند.'),
(1306, 9, 71, 'و مردان و زنان باایمان، دوست‌دار، نگهدار و پشتوانه‌ی یکدیگرند. همدیگر را به کارهای شناخته شده [:پسندیده] وامی‌دارند و از کارهای ناشناخته [:ناپسند] باز می‌دارند و نماز را بر پا می‌دارند و زکات را می‌دهند و خدا و پیامبرش را فرمان می‌برند. اینانند که خدا به زودی مشمول رحمتشان قرار خواهد داد. به‌راستی خدا عزیزی حکیم است.'),
(1307, 9, 72, 'خدا به مردان و زنان باایمان باغ‌هایی وعده داده است که از زیر (درختان)شان نهرها روان است (و اینان) در آنها جاودانه‌اند؛ و (نیز) سراهایی پاکیزه در باغ‌های جاودان (به آنان وعده داده است) و خشنودی‌ای از (سوی‌) خدا بزرگ‌تر (از این باغ‌ها و سراها) است. این است همان کامیابی و سعادت با سلامت و عظمت.'),
(1308, 9, 73, 'هان ای پیامبر برجسته! با کافران و منافقان جهاد کن و بر آنان سخت بگیر. و پناهگاهشان دوزخ است و چه بد سرانجامی است.'),
(1309, 9, 74, 'به خدا سوگند می‌خورند که (سخن ناروا) نگفته‌اند، در حالی که همواره همانا سخن کفر گفته‌اند و پس از اسلام آوردنشان کفر ورزیده‌اند و به آنچه به آن دست نیافتند همّت گماشتند و به انکار (و انگارتان) به پیگیری انتقام‌آمیز(تان) بر نخاستند مگر (برای) آنکه خدا و پیامبرش از فضل خود آنان را بی‌نیاز گردانید. پس اگر توبه کنند برای آنان خوب است (و) اگر روی برتابند، خدا آنان را در دنیا و آخرت عذابی دردناک می‌کند و در روی زمین پشتوانه و یاوری برایشان نیست.'),
(1310, 9, 75, 'و از آنان کسانی(اند که) با خدا عهد کردند: «اگر همواره از کرم خویش به ما عطا کند، به‌راستی همواره از تصدیق‌کنندگان باشیم و بی‌چون همچنان از شایستگان خواهیم بود.»'),
(1311, 9, 76, 'پس چون (خدا) از فضل خویش به آنان داد، بدان بخل ورزیدند و به حال اعراض روی برتافتند.'),
(1312, 9, 77, 'پس به سزای آنکه با خدا خُلف وعده کردند و از آن روی که دروغ می‌گفتند، در دل‌هایشان - تا روزی که او را دیدار کنند - نفاقی پایدار قرار داد.'),
(1313, 9, 78, 'آیا ندانسته‌اند که خدا بی‌گمان رازشان و نجوایشان را می‌داند و اینکه خدا همواره بس دانای رازهای نهان است‌؟'),
(1314, 9, 79, 'کسانی که از مؤمنان -که از روی میل صدقات می‌دهند- در پنهانی عیب می‌گیرند. و (همچنین) از کسانی که (در انفاق) جز به اندازه‌ی توان رنج‌آمیزشان نمی‌یابند، (عیب‌جویی می‌کنند) پس آنان را به مسخره می‌گیرند، (بدانند که) خدا آنان را به مسخره گرفته و برایشان عذابی پردرد است.'),
(1315, 9, 80, 'برایشان پوشش بخواهی یا برایشان پوشش نخواهی - (حتی‌) اگر هفتاد بار (هم) برایشان پوشش بخواهی‌- هرگز خدا (گناهان‌) آنان را نخواهد پوشید، (چرا) که آنان بی‌گمان به خدا و فرستاده‌اش کفر ورزیدند. و خدا گروه فاسقان را هدایت نمی‌کند.'),
(1316, 9, 81, 'تخلف یافتگان، با زمین‌گیر شدنشان بر خلاف رسول خدا شادمان شدند و از اینکه با مال‌ها و جان‌هاشان در راه خدا جهاد کنند بسی ناخوش گشتند و گفتند: «در (این) گرما (برای جنگ) کوچ نکنید!» بگو: «اگر دریافته بودند آتش جهنّم سوزان‌تر (از سوز جنگ) است.»'),
(1317, 9, 82, '(از این) پس به سزای آنچه به دست می‌آورده‌اند باید کم بخندند و بسیار بگریند.'),
(1318, 9, 83, 'پس اگر خدا تو را سوی طایفه‌ای از آنان بازگردانید (و) در نتیجه برای بیرون آمدن (به جنگ دیگری) از تو اجازه خواستند، بگو: «شما هیچ‌گاه با من خارج نخواهید شد و هیچ‌گاه همراه من با هیچ دشمنی کشتار نخواهید کرد. شما نخستین بار بی‌گمان با معذوران و خانه‌نشینان به قعودتان خشنود شدید، پس (اکنون نیز) با (هم)آنان بنشینید.»'),
(1319, 9, 84, 'و هیچ‌گاه بر هیچ مرده‌ای از آنان نماز مگزار و بر سر قبرش نایست. اینان بی‌چون به خدا و پیامبرش کافر شدند و در حال فسق مردند.'),
(1320, 9, 85, 'و اموال و فرزندانشان، تو را به شگفتی نیندازد. خدا تنها همی خواهد ایشان را در دنیا بدان‌ها عذاب کند، و جان‌هاشان در حال کفرشان برکنده شود.'),
(1321, 9, 86, 'و هنگامی که سوره‌ای نازل شود که: «به خدا ایمان آورید و همراه پیامبرش جهاد کنید»، توانمندانشان از تو اجازه می‌خواهند و می‌گویند: «بگذار ما با (باز)نشستگان باشیم.»'),
(1322, 9, 87, 'راضی شدند که با بازنشستگان [:معذوران] باشند. و حال آنکه بر دل‌هایشان مُهر نهاده شده است، پس دریافتی (درست از حقایق) ندارند.'),
(1323, 9, 88, 'ولی پیامبر و کسانی که با او ایمان آورده‌اند، با اموال و جان‌هاشان جهاد کردند. و اینانند که همه‌ی خوبی‌ها برایشان است و ایشان، (هم)اینان رستگار کنندگانند.'),
(1324, 9, 89, 'خدا برای آنان باغ‌هایی آماده ساخته، که از زیر (درختان)شان نهرها روان است، (و) در آنها جاودانه‌اند. این (همان) بهره‌ی (سالم و) بزرگ است.'),
(1325, 9, 90, 'و عذرخواهان بادیه‌نشین آمدند تا به آنان اجازه(ی ترک جهاد) داده شود و کسانی که به خدا و فرستاده‌اش دروغ گفتند نیز (در خانه‌هاشان) نشستند. به زودی به کسانی از آنان که کفر ورزیدند عذابی دردناک خواهد رسید.'),
(1326, 9, 91, 'بر ناتوانان و بیماران و بر کسانی که چیزی نمی‌یابند (تا در راه جهاد) خرج کنند - در صورتی که برای خدا و پیامبرش خیرخواهی کنند - حرجی نیست. هنگامی که برای خدا و فرستاده‌اش (کسانی را) نصیحت کنند، علیه احسان‌کنندگان هیچ راهی (بر مذمت یا عقوبتشان) نیست. و خدا بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(1327, 9, 92, 'و همچنین بر کسانی که چون پیش تو آمدند تا راهی (جهادشان) کنی (زیانی) نیست. (چون) گفتی: «چیزی نمی‌یابم تا بر آن راهیتان کنم‌»، برگشتند؛ حال آنکه در اثر اندوه از چشمانشان اشک فرو می‌ریزد که (چرا) چیزی نمی‌یابند تا (در راه جهاد) خرج کنند.'),
(1328, 9, 93, 'همانا راه علیه کسانی است که در حال توانگریشان از تو اجازه‌ی (ترک جهاد) می‌خواهند (و به این) راضی شده‌اند که با خانه‌نشینان (همانند زنان و سالخوردگان و بیماران) باشند. و خدا بر دل‌هایشان مُهر نهاد؛ پس آنان نادانی می‌کنند.'),
(1329, 9, 94, 'هنگامی‌که سوی آنان باز گردید، به سویتان عذر می‌آورند. بگو: «عذر نیاورید. هرگز برای شما ایمنی نداریم (و) خدا همواره ما را از خبرهای مهم شما آگاه گردانیده و به‌زودی خدا و رسولش عمل شما را خواهد دید. سپس سوی دانای نهان و آشکار بازگردانیده می‌شوید. پس به آنچه انجام می‌دادید به شما خبری مهم می‌دهد.»'),
(1330, 9, 95, 'هنگامی‌که سوی آنان بازگشتید، به‌زودی برایتان به خدا سوگند یاد می‌کنند تا از ایشان چشم‌پوشی کنید. پس از آنان روی برتابید (که‌) آنان بی‌گمان پلیدند و به (سزای) آنچه به دست می‌آورده‌اند پناهگاهشان دوزخ است.'),
(1331, 9, 96, 'برایتان سوگند یاد می‌کنند تا از آنان خشنود گردید. پس اگر شما (هم) از ایشان خشنود شوید، خدا از گروه فاسقان هرگز خشنود نخواهد شد.'),
(1332, 9, 97, 'بادیه‌نشینان در کفر و نفاق (از دیگران) سخت‌ترند و به اینکه حدود آنچه را خدا بر فرستاده‌اش نازل کرده، ندانند سزاوارترند. و خدا بس دانای حکیم است.'),
(1333, 9, 98, 'و برخی از بادیه‌نشینان کسانی‌اند که آنچه را (در راه خدا) انفاق می‌کنند خسارتی (برای خود) می‌گیرند و منتظرند که تمامی دگرگونی‌ها سراپایتان را فراگیرد؛ دگرگونی‌های بد بر خودشان است. و خدا بسیار شنوای بس داناست.'),
(1334, 9, 99, 'و برخی (دیگر) از بادیه‌نشینان کسانی‌اند که به خدا و روز بازپسین ایمان می‌آورند و آنچه را انفاق می‌کنند مایه‌ی تقرب‌هایی نزد خدا و رحمت‌های پیامبر می‌گیرند. هان! که انفاقشان برایشان مایه‌ی تقرب است (و) به‌زودی خدا ایشان را در رحمت خویش می‌آورد. خدا بی‌گمان بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(1335, 9, 100, 'و پیشگامان نخستین، از مهاجران و انصار [:یارانشان] و کسانی که با نیکوکاری از آنان پیروی کردند، خدا از ایشان خشنود است و آنان (نیز) از او خشنودند. و برایشان باغ‌هایی آماده کرده که از زیر (درختان)شان نهرها روان است (و) در آنها جاودانه‌اند: این است همان کامیابی راحتِ باعظمت.'),
(1336, 9, 101, 'و برخی از بادیه‌نشینانی که پیرامون شمایند منافقند. و از ساکنان مدینه نیز گروهی بر (مبنای) نفاق (از تمامی ایمان) عریان شدند. تو آنها را نشانه نمی‌زنی بلکه ما آنها را نشانه می‌زنیم. ما به‌زودی دو بار عذابشان می‌کنیم‌؛ سپس به عذابی بزرگ بازگردانیده می‌شوند.'),
(1337, 9, 102, 'و دیگرانی هستند که به گناهان خود اعتراف کردند (که‌) کاری شایسته را با کار بد دیگری درآمیخته‌اند. امید است خدا بر آنان بازگشت کند (و) خدا بی‌گمان بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(1338, 9, 103, 'از اموال آنان صدقه‌ای بگیر تا بدان پاک و پاکیزه‌شان سازی، و برایشان (از خدا) طلب رحمت کن (که‌) به‌راستی درخواست رحمت تو برای آنان رامش و آرامشی است. و خدا بسیار شنوای داناست.'),
(1339, 9, 104, 'آیا ندانستند (که) خدا به‌درستی (هم)او از بندگانش توبه را می‌پذیرد و صدقات را (از ایشان) می‌گیرد و خداست که خود به‌راستی بسی توبه‌پذیرِ رحمتگر بر ویژگان است‌؟'),
(1340, 9, 105, 'و (به این نابکاران) بگو: « (هر کاری می‌خواهید) انجام دهید، پس به‌زودی خدا کردارتان را (می‌نگارد و) می‌نگرد و پیامبرش و مؤمنان نیز (کردارتان را خواهند نگریست). و به‌زودی سوی (خدای) دانای نهان و آشکار باز گردانیده می‌شوید. پس شما را به آنچه انجام می‌داده‌اید آگاهی مهمی خواهد داد.»'),
(1341, 9, 106, 'و عدّه‌ای دیگر امیدیافتگان برای فرمان خدایند: یا آنان را عذاب می‌نماید و یا به آنان برگشت می‌کند. و خدا بس دانایی حکیم است.'),
(1342, 9, 107, 'و کسانی که مسجدی را بر گرفتند در حالی که (این مسجد ضرار) زیان‌بار و کفر(آمیز) و (برای‌) جدایی افکندن میان مؤمنان است و (نیز) کمین‌نهادنی است برای کسی که قبلاً با خدا و پیامبرش به جنگ برخاسته بود و بسی سخت سوگند یاد می‌کنند که ما بجز نیکوترین (زندگی اسلامی) قصدی نداشتیم، در حالی‌که خدا گواهی می‌دهد که آنان بی‌گمان دروغگویانند.'),
(1343, 9, 108, 'هرگز در آن‌جا (به نماز و طاعت) نایست. به‌راستی مسجدی که از روز نخستین بر پایه‌ی تقوا بنا شده، سزاوارتر است که در آن بایستی. در آن مردانی‌اند که دوست دارند بی‌شائبه و پاک شوند. و خدا کسانی را که خواهان پاکی‌اند دوست می‌دارد.'),
(1344, 9, 109, 'آیا پس کسی که بنیان(مسجد)ش را بر تقوا و خشنودی خدا نهاد، بهتر است یا کسی که بنیانش را مشرف بر لب پرتگاهی نهاد‌؟ پس با آن در آتش دوزخ سقوط کرد. و خدا گروه بیدادگران را هدایت نمی‌کند.'),
(1345, 9, 110, 'پیوسته بنیانشان را که بنا کرده‌اند، در دل‌هایشان مایه‌ی شک مستندی (باطل) است، جز آنکه دل‌هایشان به سختی پاره‌پاره شود. و خدا دانای بس حکیم است.'),
(1346, 9, 111, 'بی‌گمان ،خدا از مؤمنان، جان‌ها و اموالشان را به (بهای) اینکه بهشت به‌راستی برایشان است خریده؛ حال آنکه در راه خدا کشتار می‌کنند، پس می‌کشند و کشته می‌شوند؛ وعده‌ی حقّی است در تورات و انجیل و قرآن که بر عهده‌ی اوست. و چه کسی از خدا به عهد خویش وفادارتر است‌؟ پس به این معامله‌ای که با او کرده‌اید نوید همی بخواهید و این همان کامیابی بزرگ سالم (و سودمند) است.'),
(1347, 9, 112, '(آن مؤمنان، همان) توبه‌کنندگان، پرستندگان(خدا)، سپاسگزاران، سیر کنندگان (در راه خدا)، رکوع‌کنندگان، سجده‌کنندگان (برای خدا)، وادارندگان به کارهای پسندیده، بازدارندگان از کارهای ناپسند، و پاسداران مقرّرات خدایند. و مؤمنان را بشارت ده.'),
(1348, 9, 113, 'برای پیامبر برجسته و کسانی که ایمان آورده‌اند هرگز سزاوار نبوده است که برای مشرکان - پس از آنکه برایشان آشکار گردید که آنان بی‌گمان اهل دوزخند - پوشش بخواهند؛ هر چند نزدیکترین خویشاوندانشان باشند.'),
(1349, 9, 114, 'و پوشش خواستن ابراهیم برای پدرش جز برای وعده‌ای که به او داده بود، نبود. پس هنگامی که برایش به خوبی روشن شد که وی دشمن خداست، از او بیزاری جست. به‌راستی، ابراهیم بسی دلسوز و بردبار است.'),
(1350, 9, 115, 'و خدا بر آن نبوده است که گروهی را پس از آنکه به راهشان آورد بی‌راهشان کند، تا آنکه چیزی را که باید از آن پروا کنند برایشان بیان کرده باشد (و به آن کفر ورزند). همواره، خدا به هر چیزی بسی داناست.'),
(1351, 9, 116, 'بی‌گمان، فرمانروایی آسمان‌ها و زمین تنها از آنِ خداست. زنده می‌کند و می‌میراند و برای شما جز خدا هیچ سرور و نه یاوری نیست.'),
(1352, 9, 117, 'به‌راستی، خدا بر پیامبر و مهاجران و انصار -که در آن ساعت دشوار از او پیروی کردند- به‌درستی برگشت نمود؛ پس از آنکه چیزی نمانده بود دل‌های دسته‌‌ای از آنان منحرف شود. سپس بر ایشان برگشت نمود. همواره او نسبت به آنان بسی مهربانِ رحمتگر بر ویژگان است.'),
(1353, 9, 118, 'و (نیز) بر آن سه تن که (با اغوای شیطانی) تخلف یافتند تا آن هنگام (و هنگامه‌ای) که زمین با (همه‌ی) فراخیش بر آنان تنگ گردید و خودشان از خودشان به تنگ آمدند و گمان داشتند که پناهی از خدا جز به سوی (خود) او نیست. (خدا) بر آنان برگشت تا به او برگردند. بی‌گمان خدا، (هم)او بسی برگشت‌کننده‌ی (بر گنه‌کاران و) رحمتگر بر ویژگان است.'),
(1354, 9, 119, 'هان ای کسانی که ایمان آوردید! از خدا پروا کنید و با راستان باشید.'),
(1355, 9, 120, 'مردم مدینه و بادیه‌نشینان اطرافشان را چنان نبوده است که از (فرمان) پیامبر خدا سر باز زنند و نه اینکه خودهاشان از حضرتش رویگردانی کنند. این بدین جهت است که همواره هیچ تشنگی شدیدی و نه زحمتی و نه گرسنگی و تنگی‌ای در راه خدا به آنان نمی‌رسد و در هیچ جایگاهی که کافران را به خشم آورد گام نمی‌نهند، جز اینکه (همین‌ها) برایشان کاری شایسته نوشته شده. بی‌گمان خدا اجر نیکوکاران را ضایع نمی‌کند.'),
(1356, 9, 121, 'و هیچ (حال و مال) کوچک و بزرگی را انفاق نمی‌کنند و هیچ گودالی را به سختی نمی‌پیمایند، مگر اینکه به سود آنان نوشته شده، تا خدا به آنان بهترین اعمالی را که می‌کرده‌اند پاداش دهد.'),
(1357, 9, 122, 'هرگز چنان نبوده است که همگی مؤمنان به (اندازه‌ی) کفایت جنگی (از دشمنان) کوچ کنند. پس چرا از هر فرقه‌ای از آنان، دسته‌ای کوچ نمی‌کنند، (که دسته‌ای بمانند) تا (اینان) در دین آگاهی پیدا کنند و قوم (مجاهد) خود را -وقتی سوی آنان بازگشتند هشدار دهند - شاید که آنان (از کیفر ربانی) بهراسند؟'),
(1358, 9, 123, 'هان ای کسانی که ایمان آوردید! با کسانی از کافران - که مجاوران شمایند - کشتار کنید. و (آنان) باید در شما خشونت و صلابت بیابند. و بدانید که خدا بی‌گمان با تقواپیشگان است.'),
(1359, 9, 124, 'و هنگامی‌که سوره‌ای نازل شود، از میان آنان کسی است که می‌گوید: «این (سوره) ایمان کدام یک از شما را افزوده‌؟» پس امّا کسانی که ایمان آوردند بر ایمانشان افزود، حال آنکه (هم)آنان نوید می‌خواهند.'),
(1360, 9, 125, 'و اما کسانی که در دل‌هاشان بیماری‌ای است، پلیدی بر پلیدیشان افزود و در حال کفر درگذشتند.'),
(1361, 9, 126, 'آیا و نمی‌بینند که آنان در هر سال، یک یا دو بار به سختی آزمودنی آتشین می‌شوند؛ سپس نه توبه می‌کنند و نه آنها متذکر می‌شوند.'),
(1362, 9, 127, 'و هنگامی که سوره‌ای نازل شود، برخی از آنان به برخی دگر می‌نگرند (و می‌گویند:) «آیا هرگز کسی شما را می‌بیند؟» سپس (از حضور پیامبر) باز می‌گردند. خدا دل‌هایشان را (از حقّ) برگردانده، زیرا آنان گروهی هستند که هرگز نمی‌فهمند [:نادانی می‌نمایند].'),
(1363, 9, 128, 'بی‌گمان، برایتان پیامبری از (میان) خودتان به‌راستی آمد (که) رنج‌هایتان بر او بسی ناگوار و گران است. بر (هدایت) شما حریص، و نسبت به مؤمنان، دلسوز و رحمتگر بر ویژگان است.'),
(1364, 9, 129, 'پس اگر روی برتافتند، بگو: «خدا مرا بس است. هیچ معبودی جز او نیست. تنها بر او توکّل کردم و تنها او پروردگار عرش باعظمت است.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الر. این است آیات کتاب پر حکمت.'),
(1366, 10, 2, 'آیا برای مردم شگفت‌آور بوده که به مردی از خودشان وحی کردیم که: «مردمان را هشدار ده، و به کسانی که ایمان آوردند مژده ده که برای آنان نزد پروردگارشان به‌راستی قدمی (و اقدامی) راست (و پای‌برجا) است‌؟» کافران گفتند: «این (مرد) بی‌گمان افسونگری آشکارگر است.»'),
(1367, 10, 3, 'به‌راستی پروردگارتان خدایی است که آسمان‌ها و زمین را در شش زمان آفرید. سپس بر عرش (تدبیر) چیره شد، حال آنکه کار (آفرینش) را تدبیر می‌کند. هیچ شفاعتگری جز او - مگر پس از اذنش - نیست. این خدا پروردگار شماست. پس او را بپرستید. آیا پس (از این هم) متذکر نمی‌شوید؟'),
(1368, 10, 4, 'بازگشت همه‌ی شما تنها سوی اوست. وعده‌ی حقانی خدا را (پاس بدارید)؛ هموست که آفرینش را همواره آغاز می‌کند، سپس آن را باز می‌گرداند، تا کسانی را که ایمان آورده و کارهای شایسته‌(ی ایمان) کرده‌اند، افزون (از حقشان) پاداش دهد. و کسانی که کافر شدند به سزای کفرشان آشامیدنی‌ای از مایعی جوشان و عذابی پردرد خواهند داشت.'),
(1369, 10, 5, 'او کسی است که خورشید را روشنایی‌ای و ماه را نوری نهاد و آن (ماه) را (در) جایگاه‌هایی (پیاپی) اندازه گرفت، تا شماره‌ی سال‌ها و حساب را بدانید. خدا آنها را جز به (تمامی) حق، نیافرید (و) نشانه‌ها(ی خود) را برای گروهی که می‌دانند، (به روشنی) جداسازی می‌کند.'),
(1370, 10, 6, 'به‌راستی، پیاپی یکدیگر آمدن شب و روز و آنچه خدا در آسمان‌ها و زمین آفرید، برای مردمی که پروا دارند همواره نشانه‌هایی (آشکار) است.'),
(1371, 10, 7, 'بی‌گمان کسانی که امید (به) لقای [:شناخت و جزای] ما ندارند و به زندگی دنیا دل خوش کرده و بدان اطمینان یافته‌اند، و کسانی هم که ایشان از آیات ما غافلانند،'),
(1372, 10, 8, 'ایشان به (کیفر) آنچه به دست می‌آورده‌اند، پناهگاهشان (همان) آتش است.'),
(1373, 10, 9, 'کسانی که ایمان آورده و کارهای شایسته(ی ایمان) کرده‌اند، بی‌گمان پروردگارشان به (پاس) ایمانشان آنان را هدایت می‌کند؛ حال آنکه در باغ‌های پر نعمت، از زیر (پاهای)شان نهرها روان است.'),
(1374, 10, 10, 'نیایش آنان در آن جا (این) است که: «خدایا! تو پاک و منزّهی.‌» و درودشان در آنجا سلام است و پایان‌بخش نیایش آنان این است که: «ستایش ویژه‌ی پروردگار جهانیان است.»'),
(1375, 10, 11, 'و اگر خدا برای مردمان -به همان شتابی که آنان در کار خیر می‌طلبند- در زیانشان شتاب کند، اجلشان فرا سویشان بی‌چون در رسد. پس کسانی را که امید لقای ما را نمی‌دارند در طغیانشان (همچنان فرو) می‌نهیم حال آنکه سرگردان بمانند.'),
(1376, 10, 12, 'و چون انسان را آسیب فرا رسد، ما را - به پهلو خوابیده، یا ایستاده - می‌خواند. پس چون گرفتاریش را بر طرف سازیم، چنان (بی‌تفاوت از آن) می‌گذرد، که گویی ما را برای زیانی که به او رسیده، نخوانده است. این‌گونه برای اسرافکاران آنچه انجام می‌داده‌اند زینت داده شده است.'),
(1377, 10, 13, 'و بی‌چون ما نسل‌های پیش از شما را - هنگامی که ستم کردند - بی‌امان به هلاکت رساندیم، حال آنکه پیامبرانشان دلایل آشکار برایشان آوردند و (هرگز) بر آن نبوده‌اند که ایمان بیاورند. این‌گونه مردمان بزهکار را جزا می‌دهیم.'),
(1378, 10, 14, 'سپس شما را پس از آنان در زمین جانشین(شان) قرار دادیم، تا (از روی آزمون) بنگریم چگونه رفتار می‌کنید.'),
(1379, 10, 15, 'و هنگامی که آیاتمان به روشنی بر ایشان خوانده شود، آنان که لقای ما را امید ندارند گویند: «قرآن دیگری جز این (قرآن برایمان) بیاور، یا آن را تبدیل (به قرآنی دگر) کن.» بگو: «مرا نرسد که آن را از پیش خود تبدیل کنم (و) جز آنچه را که به من وحی می‌شود پیروی نمی‌کنم. اگر پروردگارم را نافرمانی کنم همواره از عذاب روزی بزرگ می‌ترسم.»'),
(1380, 10, 16, 'بگو: «اگر خدا می‌خواست آن [:قرآن] را بر شما نمی‌خواندم و (خدا) شما را بدان آگاه نمی‌گردانید. پس به‌راستی (به گونه‌ای راستین) پیش از (آوردن) آن، عمری در میانتان به سر برده‌ام. پس آیا خردورزی نمی‌کنید؟»'),
(1381, 10, 17, 'پس کیست ستمکارتر از آن کس که دروغی بر خدا بندد، یا با آیات او (همان‌ها و خدا را) تکذیب کند؟ بی‌گمان (حقیقت‌) چنان است که مجرمان (خود و دیگران را) رستگار نمی‌کنند.'),
(1382, 10, 18, 'و بجز خدا، چیزی را می‌پرستند که نه زیانشان می‌رساند و نه سودشان می‌دهد و می‌گویند: «اینان نزد خدا شفاعتگران مایند.» بگو: «آیا خدا را به چیزی که در آسمان‌ها و در زمین نمی‌داند، آگاهی بزرگی می‌دهید؟» او پاک و برتر است از آنچه (با وی) شریک می‌سازند.'),
(1383, 10, 19, 'و مردم جز یک امت نبوده‌اند. پس اختلاف کردند و اگر سخنی (در تأخیر عذابشان) از جانب پروردگارت در گذشته مقرر نگشته بود، بی‌گمان در آنچه بر سر آن با هم اختلاف می‌کنند، میانشان داوری می‌شد.'),
(1384, 10, 20, 'و می‌گویند: «چرا نشانه‌ای (رسالتی) از جانب پروردگارش بر او نازل نشده‌؟» بگو: «غیب فقط برای خداست، پس انتظار بکشید (که) همواره من (هم) با شما از منتظرانم.»'),
(1385, 10, 21, 'و هنگامی که مردم را - پس از آسیبی که بدیشان رسیده است - رحمتی بچشانیم، ناگهان آنان را در آیات ما نیرنگی است. بگو: «نیرنگ خدا سریع‌تر است.» بی‌امان، فرستادگان (ناطق و صامت)مان. آنچه نیرنگ می‌کنید ثبت و ضبط می‌کنند.'),
(1386, 10, 22, 'او کسی است که شما را در خشکی و دریا همی می‌گرداند: تا هنگامی که در کشتی‌ها باشید و آنها با بادی پاکیزه و خوش آنان را ببرند و ایشان بدان شاد(کام) شوند. (به ناگاه) بادی سخت و هولناک بر آنان وزد و موج از هر سو بر ایشان بتازد و گمان کنند که در محاصره(ی آن) افتاده‌اند. (پس‌) خدا را پاکدلانه در طاعتشان می‌خوانند: «اگر ما را از این (ورطه) بِرَهانی، همانا به‌راستی از سپاسگزاران خواهیم بود.»'),
(1387, 10, 23, 'پس چون آنان را رهانید، ناگهان در زمین به ناحق سرکشی می‌کنند. هان ای مردمان! سرکشی شما فقط به زیان خودتان است (که‌) بهره‌ی زندگی دنیا را (می‌جویید). سپس بازگشت شما تنها سوی ما خواهد بود. پس شما را از آنچه انجام می‌داده‌اید خبری مهم خواهیم داد.'),
(1388, 10, 24, 'مَثَل زندگی دنیا تنها بسان آبی است که آن را از آسمان فرو ریختیم. پس گیاه زمین - از آنچه مردمان و دام‌ها می‌خورند - با آن در آمیخت؛ تا آن‌گاه که زمین پیرایه‌ی خود را بر گرفت و بس آراسته گردید. و اهل آن پنداشتند که آنان بر آن توانایند. شبی یا روزی فرمان (ویرانی) ما آمد. پس آن را آن‌چنان درویده کردیم که گویی دیروز (هرگز) به آرامش وجود نداشته. این‌گونه نشانه‌ها(ی خود) را برای مردمی که اندیشه می‌کنند به روشنی جداسازی می‌کنیم.'),
(1389, 10, 25, 'و خدا به خانه‌ی (پایانی) سلامتی می‌خواند و هر کس را بخواهد فراسوی راهی راست هدایت می‌کند.'),
(1390, 10, 26, 'برای کسانی که نیکی کرده‌اند، (پاداش) نیکوتر و فزون‌تر (از آن) است و چهره‌هایشان را نقصان و کدورت و ذلّتی نمی‌پوشاند. اینان همدمان بهشتند و در آن جاودانه‌اند.'),
(1391, 10, 27, 'و کسانی که دستاوردشان بدی‌ها بوده، جزای بدی‌ای (دست بالا) مانند همان است، و خواری آنان را به‌ناچار فراگیرد (و) از سوی خدا هیچ نگهبانی برایشان نیست‌؛ گویی چهره‌ها‌شان با پاره‌هایی از شبِ بس تاریک‌کننده پوشیده شده است. آنان همدمان آتشند، (و هم)ایشان در آن جاودانه‌اند.'),
(1392, 10, 28, 'و (یاد کن) روزی را که همه‌ی آنان را گرد (هم) آوریم، سپس به کسانی که شرک ورزیده‌اند گوییم: «شما و شریکانتان بر جایگاهتان هم‌چنان باشید.» پس میانشان جدایی افکندیم و شریکانشان گفتند: «شما تنها ما را نمی‌پرستیدید.»'),
(1393, 10, 29, '«پس گواهی خدا میان ما و میان شما بس است، اگر ما از عبادتتان همی غافل بوده‌ایم.»'),
(1394, 10, 30, 'آنجاست که هر کس آنچه را (از گناهان) واپس نهاده است به حقیقت دریابد و سوی خدا - مولای حقشان - بازگردانیده شدند. و آنچه افترا می‌زده‌اند از نظرشان گم گشت.'),
(1395, 10, 31, 'بگو: «کیست که از آسمان و زمین شما را روزی می‌دهد؟ یا کیست که مالک گوش‌ها و دیدگان(تان) است‌؟ و کیست که زنده را از مرده بیرون می‌آورد و مرده را از زنده بیرون می‌آورد؟ و کیست که کار (خلقت) را تدبیر می‌کند؟» پس خواهند گفت: «خداست.» پس بگو: «آیا پروا نمی‌کنید؟»'),
(1396, 10, 32, 'پس این خدا پروردگار شما، تمامی حق است و بعد از حق (مطلق) جز گمراهی چیست‌؟ پس چگونه و به‌کجا و چه زمانی (از حق) باز گردانیده می‌شوید؟'),
(1397, 10, 33, 'این گونه سخن پروردگارت بر (سر و سامان) کسانی که نافرمانی (عمدی و مدام) کردند تحقق یافت، که آنان بی‌گمان ایمان نمی‌آورند (زیرا دل‌هاشان سراسر سیاه گشته).'),
(1398, 10, 34, 'بگو: «آیا از شریکانتان کسی هست که آفرینش را آغاز کند، سپس آن را برگرداند؟» بگو: «خداست که آفرینش را آغاز می‌کند، و سپس آن را باز پس می‌گرداند. پس کی و کجا (از حق) به گزاف بازگردانیده می‌شوید؟»'),
(1399, 10, 35, 'بگو: «آیا از شریکان شما کسی هست که سوی حق رهنمون کند؟» بگو: «خداست که - پایاپا - به حق رهبری می‌کند.» پس آیا کسی که به سوی حق رهبری می‌کند سزاوارتر است پیروی شود یا کسی که راه نمی‌یابد مگر آنکه (خود) راهنمایی شود؟ پس شما را چه شده (و) چگونه داوری می‌کنید؟'),
(1400, 10, 36, 'و بیشترشان جز از گمانی (بی‌مستند) پیروی نمی‌کنند (ولی کلاً) گمان، همانا به هیچ وجه (کسی را) از حقیقت بی‌نیاز نمی‌گرداند. خدا همواره به آنچه می‌کنند بسی داناست.'),
(1401, 10, 37, 'و این قرآن چنان نبوده است که از جانب غیر خدا به دروغ ساخته شده باشد، بلکه تصدیق‌کننده آن چه هست که برابرش (نمودار) است و (خود) جداسازی کتاب (ربانی) است (که) در آن شک مستندی نیست (و) از سوی پروردگار جهانیان است.'),
(1402, 10, 38, 'یا می‌گویند: «آن را (به خدا) افترا زده است!» بگو: «اگر راستگو بوده‌اید، سوره‌ای همانند آن بیاورید و هر که را دون خدا می‌توانید، فراخوانید.»'),
(1403, 10, 39, 'بلکه چیزی را دروغ شمردند که به علم آن احاطه نداشته و هنوز هم تأویل (و بازگردان حقیقی) آن برایشان نیامده. کسانی هم که پیش از آنان بودند، همین گونه (کتاب‌های وحیانی را) تکذیب کردند. پس بنگر پیامد ستمگران چه سان بوده است.'),
(1404, 10, 40, 'و از آنان کسی است که بدان ایمان می‌آورد و از آنان کسی است که بدان ایمان نمی‌آورد. و پروردگارت به (حال) افسادگران داناتر است.'),
(1405, 10, 41, 'و اگر تو را تکذیب کردند، بگو: «عمل من تنها برای من است و عمل شما تنها برای شماست. شما از آنچه من انجام می‌دهم بیزارید و من (هم) از آنچه شما انجام می‌دهید بیزارم.»'),
(1406, 10, 42, 'و برخی از آنان کسانی‌اند که فراسوی تو گوش فرا می‌دارند. آیا پس تو(یی که) کران را - هر چند خردورزی نمی‌کنند - می‌شنوانی‌؟'),
(1407, 10, 43, 'و برخی از آنان کسانی‌اند که سویت (کوردلانه، در ظاهر) می‌نگرند. آیا پس تو(یی که) نابینایان را هر چند نمی‌نگرند رهنمون می‌شوی‌؟'),
(1408, 10, 44, 'بی‌گمان خدا هرگز به مردمان ستم نمی‌کند، لیکن (این) مردمند (که) خود بر خویشتن ستم می‌کنند.'),
(1409, 10, 45, 'و روزی که آنان را گرد هم می‌آورد -گویی جز ساعتی از روز درنگ نکرده‌اند - یکدیگر را بازشناسی می‌کنند (و) کسانی که لقاءالله را دروغ شمردند بی‌گمان زیان کرده‌اند و راه‌یافته نبوده‌اند.'),
(1410, 10, 46, 'و اگر ما بی‌گمان پاره‌ای از آنچه را که به آنان وعده می‌دهیم، به تو می‌نمایانیم، یا تو را بی‌چون بمیرانیم، (در هر دو صورت) بازگشتشان تنها سوی ماست. سپس خدا بر آنچه می‌کنند گواه است.'),
(1411, 10, 47, 'و برای هر امتی پیامبری است. پس چون پیامبرشان بیاید، میانشان به قسط [:فضیلت] داوری شود حال آنکه بر ایشان ستم نرود.'),
(1412, 10, 48, 'و می‌گویند: «اگر (از) راستان بوده‌اید، (تحقق) این وعده کِی است‌؟»'),
(1413, 10, 49, 'بگو: «من برای خود زیان و سودی (در اختیار) ندارم، مگر آنچه را که خدا خواست. برای هر امتی سررسیدی است (و) هنگامی که آن زمان فرا رسد، نه (می‌توانند) لحظه‌ای از آن تأخیری بخواهند و نه (می‌توانند) پیشی جویند.»'),
(1414, 10, 50, 'بگو: «آیا دیدید، اگر عذاب او شب یا روز به شما در رسد، بزهکاران چه چیزی از آن را به شتاب می‌خواهند؟»'),
(1415, 10, 51, '«سپس، آیا هنگامی که (عذاب بر شما) واقع شد، اکنون به آن ایمان آوردید، در حالی‌که به (آمدن) آن بسی شتاب می‌نموده‌اید؟»'),
(1416, 10, 52, 'سپس به کسانی که ستم کردند گفته شد: «عذاب جاوید را بچشید. آیا جز به آنچه به دست می‌آوردید، جزا داده می‌شوید؟»'),
(1417, 10, 53, 'و از تو خبری مهم درخواست می‌کنند (که) : «آیا این (وعده) راست و پای‌برجاست‌؟» بگو: «آری! سوگند به پروردگارم که آن به‌راستی همواره راست است و شما درمانده‌کنندگان (خدا و خداییان) نیستید.»'),
(1418, 10, 54, 'و اگر همواره برای هر کس که ستم کرده، آنچه در زمین است باشد، ناگزیر آن را برای (خلاصی و) باز خرید خود فدیه می‌دهد. و چون عذاب را دیدند پشیمانی خود را پنهان داشتند. و میان آنان به قسط [:فضیلت] داوری شود و ستم (هم) نشوند.'),
(1419, 10, 55, 'هان! (که) به‌راستی آنچه در آسمان‌ها و زمین است از آنِ خداست. هان! بی‌گمان وعده‌ی خدا حق است ولی بیشترشان نمی‌دانند.'),
(1420, 10, 56, 'او زنده می‌کند و می‌میراند و به‌سویش باز گردانیده می‌شوید.'),
(1421, 10, 57, 'هان ای مردمان! به راستی برای شما از جانب پروردگارتان اندرزی و درمانی برای آنچه (بیماری) در سینه‌ها(تان) دارید و رهنمود و رحمتی بزرگ برای مؤمنان آمده است.'),
(1422, 10, 58, 'بگو: «فقط به فضل و رحمت خدا، پس تنها به همین (بزرگْ‌فضیلت رحمت ربانی) باید مؤمنان شادمان شوند.» (هم)او از هر چه گرد می‌آورند بهتر است.'),
(1423, 10, 59, 'بگو: «آیا دیدید، آنچه از روزی که خدا برایتان فرود آورده پس بخشی از آن را حرام و (بخشی را) حلال گردانیده‌اید؟» بگو: «آیا خدا به شما اجازه داده یا بر خدا افترا می‌بندید؟»'),
(1424, 10, 60, 'و کسانی که بر خدا افترا می‌بندند، به روز رستاخیز چه گمانی دارند؟ بی‌گمان، خدا بر مردمان، کانون بخشش است ولی بیشترشان سپاس نمی‌گزارند.'),
(1425, 10, 61, 'و در هیچ حال و کاری نباشی و از او [:خدا] هیچ (آیه‌ای) از قرآن نخوانی. و هیچ کاری نمی‌کنید، مگر اینکه ما بر شما گواه بوده‌ایم، آن‌گاه که بی‌امان در ژرفای آن غوطه‌ورید. و هم‌وزن ذره‌ای - نه در زمین و نه در آسمان - از پروردگارت پنهان نیست. و نه کوچک‌تر از آن و نه بزرگ‌تر، (چیزی) نیست، مگر (اینکه) در کتابی روشنگر است.'),
(1426, 10, 62, 'هان! بی‌گمان بر دوستان (و پیروان) خدا نه هرگز بیمی است و نه آنان اندوهگین می‌شوند.'),
(1427, 10, 63, 'کسانی که ایمان آورده و پرهیزگاری می‌ورزیده‌اند،'),
(1428, 10, 64, 'در زندگی دنیا و در آخرت برترین مژده تنها برای آنان است. کلمات خدا هرگز تبدیل‌پذیر نیست. این همان کامیابی بی‌رنجِ بزرگ است.'),
(1429, 10, 65, 'و سخن (ناهنجار) آنان تو را غمگین نکند. به‌راستی تمامی عزّت تنها از آنِ خداست (و) او بسیار شنوای بس داناست.'),
(1430, 10, 66, 'هان! هر که (و هر چه) در آسمان‌ها و هر که (و هر چه) در زمین است تنها از آنِ خداست و کسانی که غیر از خدا شریکانی را می‌خوانند (اینان) از آنان پیروی نمی‌کنند. اینان جز از گمان پیروی نمی‌نمایند و جز دروغ‌پردازی - بر مبنای گمان (بی‌پایه) - ندارند.'),
(1431, 10, 67, 'او کسی است که برای شما شب را قرار داد تا در آن آرامش گیرید، و روز را دیده‌بان (گردانید). بی‌گمان، در این (امر) برای گروهی که می‌شنوند نشانه‌هایی است.'),
(1432, 10, 68, 'گفتند: «خدا فرزندی برای خود برگرفته.» او منزّه (و) بی‌نیاز است. آنچه در آسمان‌ها و آنچه در زمین است از آن اوست. بر این (ادعا) نزد شما هیچ گونه (برهان و) سلطه‌ای نیست. آیا چیزی را که نمی‌دانید، به دروغ بر خدا می‌بندید؟'),
(1433, 10, 69, 'بگو: «کسانی که بر خدا همواره دروغ می‌بندند، رستگارکنندگان نیستند.»'),
(1434, 10, 70, '(این‌) بهره‌ای است اندک در دنیا؛ سپس بازگشتشان تنها به سوی ماست. پس آن‌گاه به (سزای) آنچه کفر می‌ورزیده‌اند، عذابی سخت به آنان می‌چشانیم.'),
(1435, 10, 71, 'و خبر بزرگ نوح را بر آنان بخوان، چون به قوم خود گفت: «ای قوم من! اگر جایگاه (رسالتی) من (در میان شما) و یادآوردنم با آیات خدا، بر شما گران آمده است، من تنها بر خدا توکل کرده‌ام. پس کارتان را و (کار) شریکان (ساخته‌ی) خودتان را در جمع همدست (و همداستان) کنید، سپس کارتان بر شما پوشیده، مجهول و تحیّربار نماند. پس آن‌گاه درباره‌ی من تصمیم بگیرید و مهلتم (هم) ندهید.»'),
(1436, 10, 72, '«در نتیجه اگر روی گردانیدید، من مزدی از شما نخواستم. پاداش من جز بر خدا نیست. و مأمور شده‌ام که از تسلیم‌شدگان (در برابر خدا) باشم.»'),
(1437, 10, 73, 'پس او را تکذیب کردند. آن‌گاه وی را با کسانی که در کشتی همراه او بودند نجات دادیم و اینان را جانشینان (آنان) ساختیم و کسانی را که با آیات ما (همان‌ها و ما را) تکذیب کردند غرق کردیم. پس بنگر فرجام هشدار داده‌شدگان چگونه بود.'),
(1438, 10, 74, 'سپس، بعد از وی رسولانی را به سوی قومشان برانگیختیم. پس آنان با دلایل آشکار فراسویشان آمدند، ولی ایشان بر آن نبودند به چیزی که پیشتر با آن (همان را) تکذیب کرده بودند ایمان بیاورند. این‌گونه ما بر دل‌های تجاوزکاران مُهر می‌نهیم.'),
(1439, 10, 75, 'سپس، بعد از آنان موسی و هارون را با آیات خود، سوی فرعون و سرانش فرستادیم. پس گردنکشی کردند و گروهی تبهکار بوده‌اند.'),
(1440, 10, 76, 'پس چون حق از نزد ما سویشان آمد گفتند: «بی‌گمان این سحری آشکارگر است.»'),
(1441, 10, 77, 'موسی گفت: «آیا هنگامی که حق سوی شما آمد، می‌گویید آیا این سحری است‌؟ حال آنکه جادوگران رستگار نمی‌کنند.»'),
(1442, 10, 78, 'گفتند: «آیا سوی ما آمده‌ای تا ما را از شیوه‌ای که پدرانمان را بر آن یافته‌ایم رویگردان کنی و بزرگواری در این سرزمین تنها برای شما دو تن باشد؟ در حالی که ما برای شما دو تن ایمان (و اطمینان) آورنده نیستیم.»'),
(1443, 10, 79, 'و فرعون (به کارگردانانش) گفت: «هر جادوگر دانایی را پیش من آورید.»'),
(1444, 10, 80, 'پس چون جادوگران آمدند، موسی به آنان گفت: «آنچه را شما اندازنده‌اید بیندازید.»'),
(1445, 10, 81, 'پس چون افکندند، موسی گفت: «آنچه را شما (به میان) آوردید سحر است. زودا (که) خدا آن را همواره باطل کند. بی‌گمان خدا کار مفسدان را اصلاح نمی‌کند.»'),
(1446, 10, 82, 'و خدا با کلمات خود، حق را پای‌برجا و راست می‌آورد؛ هر چند بزهکاران را هرگز خوش نیاید.'),
(1447, 10, 83, 'پس (سرانجام) کسانی برای موسی ایمان نیاوردند مگر گروهی اندک از قوم وی [:فرعون]، در حالی که بیم داشتند از آنکه مبادا فرعون و سرانشان ایشان را عذابی آتش‌بار رسانند. حال آنکه فرعون در آن سرزمین بی‌چون بسی زبردست و (هم) او بی‌گمان از اسرافکاران است.'),
(1448, 10, 84, 'و موسی گفت: «ای قوم من! اگر به خدا ایمان آورده بوده‌اید (و) اگر (در برابرش) مسلِم بوده‌اید پس بر او توکل کنید.»'),
(1449, 10, 85, 'پس گفتند: «تنها بر خدا توکل کردیم. پروردگارمان! ما را برای گروه ستمگران (مورد) آزمایشی آتش‌بار قرار مده.»'),
(1450, 10, 86, '« و ما را به رحمت خویش، از گروه کافران نجات‌بخش.»'),
(1451, 10, 87, 'و فراسوی موسی و برادرش وحی کردیم که: «شما دو تن برای قوم خود در مصر خانه‌هایی آماده کنید و سراهایتان را رویارویتان و هم قبله‌گاه (و ملجأ و پیشوای) مردمان قرار دهید و نماز را بر پا بدارید.» و مؤمنان را بشارت ده.'),
(1452, 10, 88, 'و موسی گفت: «پروردگارمان! تو به فرعون و اشرافش در زندگی دنیا زیوری و اموالی بسیار داده‌ای - پروردگارمان!- تا (دیگران را) از راه تو به بیراهه کشند. پروردگارمان! اموالشان را محو و نابود کن و آنان را سخت‌دل گردان (چنان)که ایمان نیاورند، تا (هنگامی که) آن عذاب دردناک را ببینند.»'),
(1453, 10, 89, '(پروردگار) فرمود: «درخواست شما هر دو پذیرفته شد. پس همواره پایداری جویید و راه کسانی را که نمی‌دانند هرگز پیروی مکنید.»'),
(1454, 10, 90, 'و فرزندان اسرائیل را از دریا گذراندیم. پس فرعون و سپاهیانش -از روی ستم و تجاوز- به سرعت آنان را دنبال کردند. تا آن هنگام (و هنگامه‌ای) که غرق گریبانش را فرو گرفت. گفت: « (اکنون) ایمان آوردم که هیچ معبودی - جز آنکه فرزندان اسرائیل به او گرویده‌اند - نیست و من از تسلیم‌شوندگانم.»'),
(1455, 10, 91, '(بدو گفته شد): «اکنون (ایمان می‌آوری) ؟! در حالی که پیش از این همواره نافرمانی می‌کردی و از افسادگران بوده‌ای‌؟»'),
(1456, 10, 92, '«پس امروز جسدت را (از این غرقاب) نجات می‌دهیم تا برای کسانی که از پی تو می‌آیند نشانه‌ای باشی و بی‌گمان، بسیاری از مردمان از نشانه‌های ما بسی غافلانند.»'),
(1457, 10, 93, 'و به‌راستی و درستی ما فرزندان اسرائیل را در جایگاه، پایگاه و آرامگاه نیکو پناهگاه و مأوای راست و درستی دادیم و از چیزهای پاکیزه به آنان روزی بخشیدیم. پس اختلاف نکردند مگر پس از آنکه عِلْم برایشان آمد. بی‌گمان پروردگارت روز قیامت درباره‌ی آنچه بر سر آن اختلاف می‌کرده‌اند میانشان داوری خواهد کرد.'),
(1458, 10, 94, 'پس اگر از آنچه سوی تو نازل کرده‌ایم در شکی بوده‌ای، از کسانی که پیش از تو کتاب (آسمانی) می‌خوانند بپرس. به‌راستی تمامی حق به‌درستی از جانب پروردگارت سوی تو آمده. پس زنهار از تردیدکنندگان مباش.'),
(1459, 10, 95, 'و هرگز از کسانی مباش که با آیات ما (همان‌ها و ما را) تکذیب کردند، که از زیانکاران خواهی بود.'),
(1460, 10, 96, 'بی‌گمان، کسانی که سخن پروردگارت (در عذابشان) بر آنان تحقق یافته ایمان نمی‌آورند،'),
(1461, 10, 97, 'و هر چند تمامی نشانه‌ها(ی ربانی) برایشان بیاید؛ تا (هنگامه‌ای که) آن عذاب دردناک را ببینند.'),
(1462, 10, 98, 'پس چرا هرگز هیچ گروهی نبوده است که (به هنگام عذاب) ایمان بیاورد، پس ایمانشان به حالشان سودی بخشد؟ مگر قوم یونس که وقتی (در آخرین لحظات) ایمان آوردند، عذاب رسوایی را در زندگی دنیا از آنان بر طرف کردیم و تا چندی آنان را برخوردار ساختیم (چندان که توبه خود را نشکنند).'),
(1463, 10, 99, 'و اگر پروردگارت می‌خواست همواره هر که در زمین است، همگان یکسره ایمان می‌آوردند. آیا پس تو (هستی که) مردم را ناگزیر می‌کنی (از) ایمان آورندگان باشند؟'),
(1464, 10, 100, 'و هیچ کس را چنان نبوده است که جز به اذن خدا ایمان بیاورد. و خدا بر (سر و سامان) کسانی که خردورزی نمی‌کنند، پلیدی قرار می‌دهد.'),
(1465, 10, 101, 'بگو: «بنگرید در آسمان‌ها و زمین چیست‌؟» و (اما) نشانه‌ها و هشداردهندگان گروهی را که ایمان نمی‌آورند بی‌نیاز نمی‌کنند.'),
(1466, 10, 102, 'پس آیا جز مانند روزهای کسانی را که پیش از آنان در گذشتند، انتظار می‌برند؟ بگو: «انتظار برید که من (نیز) با شما از منتظرانم.»'),
(1467, 10, 103, 'سپس فرستادگان خود را -و کسانی را که گرویدند- (از شکنجه) می‌رهانیم. این‌گونه حقی است بر عهده‌ی ما که مؤمنان را نجات دهیم.'),
(1468, 10, 104, 'بگو: «هان ای مردمان! اگر در دین من تردید داشته‌اید، پس من کسانی را که بجز خدا می‌پرستید نمی‌پرستم. بلکه خدایی را می‌پرستم که شما را می‌میراند و دستور یافته‌ام که از مؤمنان باشم.»'),
(1469, 10, 105, 'و اینکه چهره‌(ی فطرت)‌ات را برای دین - در حال رویگردانی از هر باطلی - بر‌آور و زنهار از مشرکان مباش.'),
(1470, 10, 106, 'و پس از خدا، چیزی را که سود و زیانی به تو نمی‌رساند مخوان. پس اگر چنان کنی، در آن صورت بی‌گمان از ستمکارانی.'),
(1471, 10, 107, 'و اگر خدا به تو زیانی رساند هیچ رهاننده‌ای (از این زیان) جز او نیست و اگر برای تو خیری بخواهد بخشش او را هیچ ردّ کننده‌ای نیست. آن را به هر کس از بندگانش بخواهد می‌رساند و تنها او بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(1472, 10, 108, 'بگو: «هان ای مردمان! بی‌گمان تمامی حق از (جانب) پروردگارتان برایتان آمده است. پس هر کس هدایت یابد تنها به سود خویش هدایت می‌یابد و هر کس گمراه گردد، تنها به زیان خود گمراه می‌گردد و من بر (سر و سامان)تان کارسازی نیستم.»'),
(1473, 10, 109, 'و آنچه را فراسوی تو وحی می‌شود پیروی کن و شکیبایی نمای، تا خدا (میان تو و آنان) حکم کند. و او بهترین حاکمان است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الر. (بگو:) «(این) کتابی است که آیات آن استحکام یافته، سپس از جانب حکیمی آگاه، مفصل و جداسازی شده است (تا)»'),
(1475, 11, 2, '«که جز خدا را نپرستید. به‌راستی من از جانب او برای شما هشداردهنده و بشارتگری (شایسته) هستم.»'),
(1476, 11, 3, '«و اینکه از پروردگارتان پوشش بخواهید (و) سپس به او بازگشت کنید تا شما را با بهره‌ای نیکو تا فرصتی معیّن بهره‌مند سازد، و به هر صاحب فضیلتی، فضیلتش را بدهد. و اگر رویگردان شوید من به‌راستی از عذاب روزی بزرگ بر شما بیمناکم.»'),
(1477, 11, 4, '«بازگشت شما تنها سوی خداست و او بر هر چیزی تواناست.»'),
(1478, 11, 5, 'هان! آنان همواره سینه‌هاشان را دو تَه و پنهان می‌گردانند (و می‌کوشند) تا (رازهای خود را) با کوشش از او نهفته دارند. هان! آگاه باشید آن‌گاه که آنان جامه‌هایشان را بر سر می‌کشند، خدا آنچه را نهفته و آنچه را آشکار می‌دارند می‌داند. همانا او به ذات (و درون) سینه‌ها بسی داناست.'),
(1479, 11, 6, 'و هیچ جنبنده‌ای در زمین نیست مگر (اینکه) روزیش تنها بر عهده‌ی خداست و (همو) قرارگاه و تهی‌گاهشان را (در زمان و مکان) می‌داند. همه‌(ی این‌ها) در کتابی روشنگر است.'),
(1480, 11, 7, 'و اوست کسی که آسمان‌ها و زمین را در شش هنگام آفرید- حال آنکه عرش (آفرینش) او (پیش از این) بر آب [:ماده‌ی نخستین] بود- تا شما را بیازماید (که) کدام یک نیکوکارترید. و اگر بگویی: «شما پس از مرگ بی‌چون برانگیختگانید»، همواره کسانی که کافر شدند همی خواهند گفت: «این (انگیختگی و باز گردانیدن) جز سحری آشکارگر نیست.»'),
(1481, 11, 8, 'و اگر همواره عذاب را تا زمانی چند از آنان به تأخیر اندازیم، بی‌چون همی خواهند گفت: «چه چیزی آن را (از ما) باز می‌دارد؟» هان! روزی که (عذاب) به آنان در رسد، آن را بازگشتی نیست و آنچه را که مسخره می‌کرده‌اند (همان) آنان را فراخواهد گرفت.'),
(1482, 11, 9, 'و اگر همواره از خود رحمتی به انسان بچشانیم، سپس آن را از او باز ستانیم، بی‌گمان بسی نومید و ناسپاس است.'),
(1483, 11, 10, 'و همانا اگر - پس از زیانی که به او رسیده - نعمتی به او بچشانیم، بی‌چون همی خواهد گفت: «گرفتاری‌ها از من دور شد!» بی‌گمان او بسی شادمان و فخرکننده است.'),
(1484, 11, 11, 'مگر کسانی که شکیبایی ورزیده و کارهای شایسته(ی ایمان) کردند (که) برایشان پوشش و پاداشی بزرگ است.'),
(1485, 11, 12, 'پس شاید تو برخی از آنچه را که فراسویت وحی می‌شود ترک‌کننده‌ای و سینه‌ات بدان تنگ است (از آن رو) که می‌گویند: «چرا گنجی بر او فرو فرستاده نشده، یا فرشته‌ای با او نیامده‌؟» تو فقط هشداردهنده‌ای. و خدا بر هر چیزی وکیل (و) کارساز است.'),
(1486, 11, 13, 'یا می‌گویند: «آن [:قرآن] را به دروغ به خدا نسبت داده.» بگو: «اگر راستگو بوده‌اید، پس ده سوره‌ی افترا بسته همچون قرآن بیاورید و غیر از خدا هر که را توانستید (در این فراگیری مقایسه‌ای) فرا خوانید.»'),
(1487, 11, 14, '«پس اگر [:به سود] شما اجابت نکردند، بدانید که قرآن بی‌گمان تنها به علم خدا نازل شده و اینکه معبودی جز او نیست. پس آیا شما (در برابر حق) تسلیمید؟»'),
(1488, 11, 15, 'کسانی که زندگی دنیا و زیور آن را می‌خواسته‌اند (جزای) کارهایشان را در (همین) دنیا بی‌کم و کاست به آنان می‌دهیم‌؛ حال آنکه آنان در آن زیان‌بار نخواهند بود.'),
(1489, 11, 16, 'اینان همانانند که در آخرت جز آتش برایشان نخواهد بود و آنچه در دنیا ساخته‌اند به هدر رفته و آنچه انجام می‌داده‌اند باطل است.'),
(1490, 11, 17, 'آیا پس کسی که از جانب پروردگارش بر حجتی روشن استوار بوده، و شاهدی (هم) از خودش در پی‌اش می‌آید و پیش از وی (نیز) کتاب موسی راهبر (موسویان به این حقیقت) و مایه‌ی رحمت است، آنان به او ایمان می‌آورند؟ و هر کس از گروه‌ها(ی مخالف) به او کفر ورزد، آتش، وعده‌گاه اوست. پس از آن هرگز در تردیدی مباش. همان به‌راستی از جانب پروردگارت حق است ولی بیشتر مردمان ایمان نمی‌آورند.'),
(1491, 11, 18, 'و چه کسی ستمکارتر از آن (نا)کس است که بر خدا دروغی را افترا بندد؟ ایشان بر پروردگارشان نمایش داده می‌شوند و گواهان خواهند گفت: «اینان بودند که بر پروردگارشان دروغ بستند. هان! لعنت خدا بر ستمگران.»'),
(1492, 11, 19, '«کسانی که (مردمان را) از راه خدا باز می‌دارند و آن را به کژی می‌خواهند و آنان، (هم)آنان به آخرت کافرانند.»'),
(1493, 11, 20, 'اینان در زمین درمانده‌کنندگان (خدا) نبوده‌اند، و پس از خدا هرگز اولیائی برای آنان نبوده است. عذاب برایشان چندان می‌شود، زیرا آنان توان شنیدن (حق را) نداشته‌اند و (حق را) هرگز -به‌درستی- نمی‌دیده‌اند.'),
(1494, 11, 21, 'اینانند که خویشتن را زیان کرده‌اند، و آنچه را به دروغ برساخته بودند از آنان گم گشته است.'),
(1495, 11, 22, 'به ناچار آنان در آخرت، (هم)آنان زیانکارترین (مکلفان)‌اند.'),
(1496, 11, 23, 'بی‌گمان کسانی که ایمان آورده و کارهای شایسته(ی ایمان) کرده‌اند و سوی پروردگارشان بینی‌شان را به خاک ساییده‌اند (هم)آنان -جاودانه- همدمان بهشتند.'),
(1497, 11, 24, 'مَثَل (این) دو گروه، چون نابینا و کر و (در مقایسه‌ی با) بینا و شنواست. آیا در مَثَل یکسانند؟ پس آیا متذکر نمی‌شوید؟'),
(1498, 11, 25, 'و به‌راستی نوح را همی سوی قومش فرستادیم. (گفت): «من همواره برای شما هشداردهنده‌ای آشکارکننده‌ام،»'),
(1499, 11, 26, '«که جز خدا را نپرستید؛ همانا من از عذاب روزی دردناک بر شما بیمناکم.»'),
(1500, 11, 27, 'پس، سران قومش -که کافر شدند- گفتند: «ما تو را جز بشری همانند خود نمی‌بینیم و در نگرش آغازین نمی‌بینیم (که) کسی تو را پیروی کرده باشد جز (جماعتی از) فرومایگان ما، و برایتان بر خودمان امتیازی نمی‌بینیم، بلکه شما را دروغگویان می‌انگاریم.»'),
(1501, 11, 28, 'گفت: «ای قوم من! آیا دیدید (که) اگر من از طرف پروردگارم بر حجتی روشن استوار بوده‌ام و مرا از نزد خود رحمتی داده، پس این بر شما (با کوریتان) بسی کور (و مهجور) خوانده شده است. آیا ما شما را - در حالی که برای (پذیرش) آن کراهت (و نفرت) دارید - به آن وادار کنیم‌؟»'),
(1502, 11, 29, '«و ای قوم من! بر این (رسالت) هرگز مالی از شما درخواست نمی‌کنم. مزد من جز بر خدا نیست و من کسانی را که ایمان آورده‌اند طردکننده نیستم. بی‌گمان آنان پروردگارشان را (با معرفت و پاداشش) دیدارکننده‌اند. ولی شما را قومی می‌بینم که نادانی می‌کنید.»'),
(1503, 11, 30, '«و ای قوم من! اگر آنان را برانم، چه کسی مرا از (روی آوردن عذاب) خدا (بر این تخلف رسالتی) یاری خواهد کرد؟ آیا پس متذکر نمی‌شوید؟»'),
(1504, 11, 31, '« و برای شما نمی‌گویم گنجینه‌های خدا نزد من است و نه غیب می‌دانم و (نیز) نمی‌گویم (که‌) من همانا فرشته‌ام، و درباره‌ی کسانی که دیدگان شما به خواری و نقصان در آنان می‌نگرد، نمی‌گویم خدا هرگز خیرشان نمی‌دهد. خدا به آنچه در جان‌های آنان است دانا‌تر است. (اگر جز این بگویم) من در این هنگام(و هنگامه)‌، بی‌گمان از ستمکاران خواهم بود.»'),
(1505, 11, 32, 'گفتند: «ای نوح! بی‌چون با ما جدل کردی، بسیار (هم) با ما جدل نمودی. پس اگر از راستان بوده‌ای آنچه را (از عذاب خدا) به ما وعده می‌دهی برایمان بیاور.»'),
(1506, 11, 33, 'گفت: « (این) تنها خداست که اگر بخواهد، آن را برایتان می‌آورد و شما ناتوان‌کننده(ی او) نیستید.»'),
(1507, 11, 34, '«و اگر بخواهم شما را اندرز دهم - در صورتی که خدا خواسته باشد که شما را گمراه گذارد - اندرز من شما را سودی نمی‌بخشد. او پروردگار شماست و تنها سوی او بازگردانیده می‌شوید.»'),
(1508, 11, 35, 'یا (درباره‌ی قرآن) می‌گویند: «آن را (بر خدا) افترا زده است.» بگو: «اگر آن را افترا زده‌ام جرمم بر عهده‌ی خود من است و من از آنچه شما جرم می‌کنید، (برکنار و) بیزارم.»'),
(1509, 11, 36, 'و سوی نوح وحی شد: «از قوم تو بی‌چون، جز کسانی که (تاکنون) ایمان آورده‌اند هرگز (کسی) ایمان نخواهد آورد، پس به آنچه می‌کرده‌اند بد حال مباش.»'),
(1510, 11, 37, '« و با نظرات ما و به وحی ما کشتی را بساز، و در (باره‌ی) کسانی که ستم کرده‌اند با من هم‌سخن مشو؛ بی‌گمان آنان غرق‌شدگانند.»'),
(1511, 11, 38, 'و [نوح] کشتی را (بدین گونه) می‌سازد و هر بار که گروهی چشمگیر [:اشرافی] از قومش بر او می‌گذشتند، او را مسخره می‌کردند. گفت: «اگر ما را مسخره کنید، همان‌گونه که مسخره‌مان می‌کنید ما (هم) بی‌گمان (در امواج خروشان) مسخره‌تان خواهیم کرد.»'),
(1512, 11, 39, '«پس در آینده‌ای دور خواهید دانست چه کسی را عذابی که خوار(ش) کند در می‌رسد، و بر [سر وسامان‌]ش عذابی پایدار حلول می‌کند.»'),
(1513, 11, 40, 'تا هنگامی که فرمان ما در رسید و تنور (نار یا تنور نور یا تنور غضب الهی) فوران کرد، فرمودیم: «در آن کشتی از هر حیوانی (که نیاز به خشکی دارد) دو زوج، [:یک نر و یک ماده] با کسانت - مگر کسی که قبلاً درباره‌ی او سخن رفته است- و کسانی (از غیر کسانت) که ایمان آورده‌اند - برگیر.» و جز (عده‌ی) اندکی با او ایمان نیاوردند.'),
(1514, 11, 41, 'و (نوح) گفت: «در آن (کشتی‌) سوار شوید، به نام خداست روان شدنش و (به نام خداست) لنگر انداختنش. بی‌گمان پروردگارم بسی پوشنده‌ی رحمتگر بر ویژگان است.»'),
(1515, 11, 42, 'حال آنکه آن (کشتی) ایشان را در میان موجی همانند کوه‌ها می‌بَرَد و نوح پسرش را - که در کناری جدا بود - بانگ در داد: «ای پسرک من! با ما سوار شو و با کافران مباش.»'),
(1516, 11, 43, 'گفت: «به زودی به کوهی پناه می‌جویم که مرا از (سیل) آب نگه می‌دارد.» (نوح) گفت: «امروز نگهدارنده‌ای از امر خدا نیست. جز هر که خدا (بر او) رحم کند.» و موج میان آن دو حایل شد. پس (این پسر) از غرق‌شدگان بود.'),
(1517, 11, 44, 'و گفته شد: «ای زمین! آب خود را فرو بر؛ و ای آسمان! آبت را (از بیخ و بن) بر کن.» و آب فرو کاست و فرمان (خدا) انجام شد و کشتی بر (کوه) جودی قرار گرفت. و گفته شد: «دورباشی برای گروه ستمکاران!»'),
(1518, 11, 45, 'و نوح پروردگار خود را ندا در داد. پس گفت: «پروردگارم! پسرم همانا از کسان من است. و به‌راستی وعده‌ی تو راست و پای‌برجاست. و تو حکیم‌ترین حکم‌کنندگانی.»'),
(1519, 11, 46, 'فرمود: «ای نوح! او (در حقیقت‌) از کسان تو نیست. او کرداری ناشایسته (و از ناکسان) است. پس چیزی را که بدان علم نداری از من مخواه. من به تو بسی اندرز می‌دهم که مبادا از نادانان باشی.»'),
(1520, 11, 47, 'گفت: «پروردگارم! من به‌راستی به تو پناه می‌برم (از این) که از تو چیزی از روی نادانی بخواهم و اگر برایم پوشش ننهی و به من رحم نکنی بی‌گمان از زیانکاران هستم.»'),
(1521, 11, 48, 'گفته شد: «ای نوح! با درودی از ما و برکت‌هایی بر تو و بر گروه‌هایی از کسانی که با تو هستند، فرود آی. و گروه‌هایی (هم هستند) که از نعمت‌ها برخوردارشان خواهیم کرد، سپس از جانب ما عذابی دردناک به آنان خواهد رسید.»'),
(1522, 11, 49, '«این‌ها از خبرهای مهم غیبند که آنها را به تو وحی می‌کنیم. تو چنان نبودی که از پیش آنها را بدانی و نه قومت (چنان بودند که آن را بدانند). پس شکیبایی کن (که) بی‌گمان فرجام (نیک) از آنِ تقواپیشگان است.»'),
(1523, 11, 50, 'و به سوی عادیان، برادرشان هود را (فرستادیم که) گفت: «ای قوم من! خدا را بپرستید (که‌) جز او هیچ معبودی برای شما نیست (و) شما (کسانی) بجز افترازنندگان نیستید.»'),
(1524, 11, 51, '.«ای قوم من! بر این (رسالت) هرگز پاداشی از شما درخواست نمی‌کنم. پاداش من جز بر عهده‌ی کسی نیست که مرا بر فطرت (توحید) آفریده. پس آیا خردورزی نمی‌کنید؟»'),
(1525, 11, 52, '« و ای قوم من! از پروردگارتان پوشش بخواهید و سپس به او برگردید تا از آسمان بر (سر و سامان)‌تان باران فراوان فرو فرستد و نیرویی بر نیرویتان بیفزاید. و مجرمانه (از خدا) روی مگردانید.»'),
(1526, 11, 53, 'گفتند: «ای هود! برای ما (دلیل) روشنی نیاوردی و ما برای سخن تو از خدایانمان دست‌بردار نیستیم، و هرگز برایت ایمان‌آورنده(ی به خدایت) نیستیم.»'),
(1527, 11, 54, '.« (با تو چیزی) نمی‌گوییم، جز اینکه بعضی از خدایان ما تو را به (گونه‌ای) بد (و ناروا از خرد) عریان کرده‌اند.» گفت: «به‌راستی من خدا را گواه می‌گیرم و (شما هم) شاهد باشید که من از آنچه (با او) شریک می‌گیرید به راستی بیزارم؛»'),
(1528, 11, 55, '«بجز او(خدایی نیست). پس همگان در کار من نیرنگ کنید، و سپس مرا مهلت (هم) مدهید.»'),
(1529, 11, 56, '«من همواره بر خدا - پروردگار خودم و پروردگار شما - توکل کردم. هیچ جنبنده‌ای نیست مگر اینکه خدا گیرنده‌ی پیشانی (هستی‌) اوست. به‌راستی پروردگارم بر راهی بس راست (و راهوار) محیط و استوار است.»'),
(1530, 11, 57, '«پس اگر روی برگردانند، به راستی آنچه را که (برای آن‌) به سوی شما فرستاده شده بودم همواره به شما ابلاغ کردم، و پروردگارم قومی جز شما را جانشین(تان) خواهد کرد. حال آنکه به او هیچ زیانی نمی‌رسانید. به‌راستی، پروردگارم بر هر چیزی نگهبان است.»'),
(1531, 11, 58, 'و هنگامی که فرمان ما در رسید، هود و کسانی را که با او (به خدا) گرویده بودند، به رحمتی (بزرگ) از خود، نجات بخشیدیم و آنان را از عذابی پرفشار (و خشونت‌بار) رهانیدیم.'),
(1532, 11, 59, 'آنان عادیانند (که) آیات پروردگارشان را انکار (و انگار) کردند و فرستادگانش را نافرمانی نمودند و به دنبال فرمان هر زورگوی ستیزه‌جویی رفتند.'),
(1533, 11, 60, 'و (سرانجام) در این دنیا و روز قیامت، لعنت، بدرقه‌ی (راه) آنان گردید. هان! عادیان به پروردگارشان کفر ورزیدند. هان! (که) عادیان - قوم هود - از رحمت خدا دورند.'),
(1534, 11, 61, 'و سوی ثمودیان، برادرشان صالح را (فرستادیم). گفت: «ای قوم من! خدا را بپرستید. برای شما هیچ معبودی جز او نیست. او شما را از زمین نشأت داد و در آن شما را به آبادانی برگماشت. پس از او پوشش بخواهید، سپس به او باز گردید. پروردگارم همواره اجابت‌کننده‌ای نزدیک است.»'),
(1535, 11, 62, 'گفتند: «ای صالح! به‌راستی تو پیش از این، میان ما مورد امیدی بودی. آیا ما را از پرستش آنچه پدرانمان می‌پرستیدند باز می‌داری‌؟ و بی‌چون، ما از آنچه تو ما را بدان می‌خوانی سخت در (ژرفای) شکی مستندیم!»'),
(1536, 11, 63, 'گفت: «ای قوم من! آیا دیدید، اگر (در این دعوا) بر حجّتی روشن از پروردگارم استوار بوده‌ام، و او از جانب خود رحمتی به من داده باشد، پس اگر او را نافرمانی کنم، چه کس از (عذاب) خدا مرا یاری می‌کند؟ در نتیجه شما مرا جز زیانباری نمی‌افزایید.»'),
(1537, 11, 64, '«و ای قوم من! این ماده شتر خداست، حال آنکه برای شما نشانه‌ای (شگرف) است. پس واگذاریدش (تا) در زمین خدا(یش) بخورد و با او به بدی‌ای برخورد مکنید، که (در نتیجه‌) عذابی نزدیک گریبانگیرتان شود.»'),
(1538, 11, 65, 'پس آن (ماده شتر) را پی کردند. آن‌گاه (صالح) گفت: «سه روز در خانه‌هایتان برخوردار شوید، این (هشدار عذاب)، وعده‌ای دروغین نیست.»'),
(1539, 11, 66, 'پس چون فرمان ما در رسید، صالح و کسانی را که با او (به خدا) ایمان آورده بودند، به رحمتی (ویژه) از خود، رهانیدیم و (هم) از رسوایی آن روزگار (دشوار، نجاتشان دادیم). بی‌گمان پروردگار تو همان پرتوان با عزت است.'),
(1540, 11, 67, 'و کسانی که ستم کردند، (آن) بانگ (مرگبار) فرو گرفت. پس در خانه‌هایشان زمین‌گیر شده (و) از پا در آمدند.'),
(1541, 11, 68, 'گویا هرگز در آن خانه‌ها با اقامتی آرامش‌بخش سکونت نداشته‌اند. هان! که ثمودیان پروردگارشان را (از خود و دیگران) پنهان داشتند. هان! دورباشی سخت برای (سر و سامان) ثمودیان است.'),
(1542, 11, 69, 'و به‌راستی، فرستادگانمان بی‌گمان برای ابراهیم مژده آوردند (و) سلام گفتند. گفت: «سلام.» پس دیری نپایید که گوساله‌ی بریان شده‌ای را (پیش) آورد.'),
(1543, 11, 70, 'پس چون دست‌هایشان را دید (که‌) بدان نمی‌رسد، انکار (و انگار)‌شان کرد و از ایشان ترسی (به دل) برگرفت. گفتند: «مترس، ما به‌راستی سوی قوم لوط فرستاده شده‌ایم.»'),
(1544, 11, 71, 'در حالی‌که زن او ایستاده بود، خندید. پس وی را به اسحاق و بعد از اسحاق به یعقوب مژده دادیم.'),
(1545, 11, 72, '(همسر ابراهیم) گفت: «ای وای بر من! آیا فرزند آورم، با آنکه من پیرزنی بس عاجزم و این شوهرم در حالت پیری (و سالخوردگی) است‌؟ به‌راستی این چیزی بس شگفت‌انگیز است.»'),
(1546, 11, 73, 'گفتند: «آیا از کار خدا تعجب می‌کنی‌؟ رحمت خدا و برکات او بر شما خاندان (رسالت) است. بی‌گمان او ستوده‌ای بزرگوار [:بزرگ‌منش و بزرگ‌بینش‌] است.»'),
(1547, 11, 74, 'پس هنگامی که (هنگامه‌ی) ترس از ابراهیم برفت و مژده(ی فرزنددار شدن) برایش آمد، درباره‌ی قوم لوط با ما (به قصد شفاعت به امید بازگشتشان سوی خدا) در حال چون و چرا بود.'),
(1548, 11, 75, 'همواره ابراهیم، بسی بردبار و آه کننده‌ای گریان و بازگشت‌کننده‌ای (سوی خدا) است.'),
(1549, 11, 76, '(گفتیم:) «ای ابراهیم! از این (چون و چرا) روی برتاب. بی‌گمان فرمان پروردگارت به‌راستی آمده، و بی‌چون عذابی بازناگشتنی بر آنان آمدنی است.»'),
(1550, 11, 77, 'و چون فرستادگان ما نزد لوط آمدند، آمدنشان برای او بدآمد شد، و توانش از حمایت ایشان (در برابر لوطیان) به تنگ آمد و گفت: «امروز، روزی سخت (و) پرماجراست.»'),
(1551, 11, 78, 'و قوم او ناگزیر شتابان و ترسان سویش روانه شدند و پیش از آن همه‌گونه کارهای زشت را مرتکب می‌شده‌اند. (لوط) گفت: «ای قوم من! اینان دختران من‌اند. ایشان برایتان پاکیزه‌ترند. پس از خدا پروا کنید و مرا نزد میهمانانم خوار و بی‌مقدار مکنید. آیا از (میان) شما رادمردی رشید نیست‌؟»'),
(1552, 11, 79, 'گفتند: «تو بی‌چون همی دانسته‌ای که ما را در دخترانت هیچ حقی نیست، و بی‌گمان هم‌چنان می‌دانی که ما چه می‌خواهیم.»'),
(1553, 11, 80, '(لوط) گفت: «کاش برای مقابله‌ی با شما قدرتی می‌داشتم، یا به تکیه‌گاهی استوار [:خدا] پناه می‌بردم.»'),
(1554, 11, 81, '(فرشتگان) گفتند: «ای لوط! ما فرستادگان پروردگار توایم. (اینان) هرگز به تو دست نخواهند یافت. پس در پاسی از شب خانواده‌ات را حرکت ده -و هیچ کس از شما مبادا (که) واپس بنگرد - مگر زنت، که آنچه بدیشان رسید به او (نیز) رسنده است. بی‌گمان، وعده‌گاه‌شان صبح است. آیا صبح نزدیک نیست‌؟»'),
(1555, 11, 82, 'پس چون فرمان ما در رسید، بلندایش را (به) زیرینش نهادیم و باران سنگ‌پاره‌هایی از (نوع) سنگ گلی لایه لایه و منظم بر آن باریدیم.'),
(1556, 11, 83, '(حال آنکه آن سنگ‌ها) نزد پروردگارت نشان زده بودند، و آن از ستمگران دور نیست.'),
(1557, 11, 84, 'و به سوی (اهل) مَدْیَن، برادرشان - شعیب - را فرستادیم. گفت: «ای قوم من! خدا را بپرستید (که‌) برای شما جز او معبودی نیست و پیمانه و ترازو(ی حال و مالتان) را کم مکنید. به‌راستی شما را در خیری می‌بینم و همواره از عذاب روزی فراگیر بر شما بیمناکم.»'),
(1558, 11, 85, '«و ای قوم من! پیمانه و ترازو را به‌گونه‌ی وافی و همسان بدهید، و به (حقوق) مردمان (در جان و مالشان) زیان نرسانید، و تبهکارانه در زمین سرکشی نکنید.»'),
(1559, 11, 86, '«اگر مؤمن بوده‌اید، باقیمانده‌ی (حجت) خدا برای شما بهتر است و من بر شما نگاهبان نیستم.»'),
(1560, 11, 87, 'گفتند: «ای شعیب! آیا نمازت به تو فرمان می‌دهد که آنچه را پدرانمان می‌پرستند رها کنیم، یا در اموال خود آنچه می‌خواهیم انجام دهیم‌؟ به‌راستی تو، بی‌چون (همین) تو، بردبار رشدیافته‌ای!»'),
(1561, 11, 88, 'گفت: «ای قوم من! آیا دیدید [:اندیشیدید] اگر از جانب پروردگارم بر (دلیل) روشنی استوار باشم و او از سوی خود روزی (معنوی) نیکویی به من داده باشد (آیا باز هم از پرستش او دست‌بردارم‌)؟ و من نمی‌خواهم فراسوی آنچه شما را از آن باز می‌دارم با شما مخالفت کنم. من قصدی جز اصلاح (شما) - تا آنجا که توانستم - ندارم. و توفیق من جز به (یاری) خدا نیست. تنها بر او توکل کرده‌ام، و تنها سوی او پیاپی باز می‌گردم.»'),
(1562, 11, 89, '« و ای قوم من! زنهار تا جدایی (از) من، شما را مجرمانه بدانجا کشاند که (بلایی) مانند آنچه به قوم نوح یا قوم هود یا قوم صالح رسید، به شما (نیز) در رسد. و قوم لوط (هم) از شما دور نیستند.»'),
(1563, 11, 90, '« و از پروردگار خود پوشش بخواهید. سپس سوی او بازگردید (که‌) بی‌گمان پروردگارم رحمتگر بر ویژگان و بسی دوستدار (آنان) است.»'),
(1564, 11, 91, 'گفتند: «ای شعیب! ما بسیاری از آنچه را که می‌گویی نمی‌توانیم بفهمیم، و همواره ما تو را در میان خودمان بس ناتوان می‌بینیم، و اگر کسانت نبودند بی‌چون سنگسارت می‌کردیم، و تو بر (سر و سامان)مان عزتی (و موقعیتی) نداری.»'),
(1565, 11, 92, 'گفت: «ای قوم من! آیا کسان من نزدتان از خدا عزیزترند، حال آنکه او را پشت سر خود برگرفته‌اید (و فراموشش کرده‌اید) ؟ به‌راستی، پروردگارم به آنچه انجام می‌دهید محیط است.»'),
(1566, 11, 93, '«و ای قوم من! شما بر حسب امکان و توانتان عمل کنید، من (نیز) همانا (همان طور) عمل می‌کنم. در آینده‌ای دور خواهید دانست که عذاب رسواکننده چه کسی را در می‌رسد، و چه کسی (هم) او دروغگوست. و بسیار دیده‌بانی (و نظاره) کنید که من (هم) با شما بی‌گمان بس نظاره‌گرم.»'),
(1567, 11, 94, 'و چون فرمان ما آمد، شعیب و کسانی را که با او ایمان آورده بودند، به رحمتی ویژه از جانب خویش نجات دادیم و کسانی را که ستم کرده بودند، فریاد (مرگبار) فرو گرفت. پس در خانه‌هایشان زمین‌گیر شده از پای در آمدند.'),
(1568, 11, 95, 'گویی در آن (خانه) هرگز اقامتی گوارا (و خوشایند) و دیرپا نداشته‌اند. هان! که مردمان مَدْیَن از رحمت خدا دورند؛ همان‌گونه که ثمود(یان) دور شدند.'),
(1569, 11, 96, 'و به‌راستی، موسی را با آیات خود و حجتی روشنگر، همی فرستادیم:'),
(1570, 11, 97, 'به سوی فرعون و سران چشمگیر قومش. پس (این سران) از کار و فرمان فرعون پیروی کردند، حال آنکه فرمان و کار فرعون هرگز راهواری‌ای ندارد.'),
(1571, 11, 98, '(فرعون) روز قیامت پیشاپیش قومش می‌آید، پس آنان را (از پیش) به آتش در آورده (بود) و (دوزخ) چه بد ورودگاه و آبشخور (داغی) است برای واردان.'),
(1572, 11, 99, 'و در این دنیا و روز قیامت به لعنت بدرقه شدند (و) چه بد (خیر مقدم و) کمک و بخشوده‌ای نصیب آنان است.'),
(1573, 11, 100, 'این، از خبرهای بزرگ مجتمع‌هاست. آن را بر تو برگزیده (و روایت می‌کنیم) و پیاپی گزارش می‌دهیم. بعضی از آنها (هنوز) بر سر پا هستند، و (بعضی هم) درو شده‌اند (و از پا درآمده‌اند).'),
(1574, 11, 101, 'و ما به آنان ستم نکردیم، ولی آنان به خودشان ستم کردند. پس چون فرمان پروردگارت آمد، خدایانی که به جای خدا(ی حقیقی) می‌خواندند هرگز بی‌نیازشان نکردند، و ایشان را جز هلاکت و دور کردن (از خدا) نیفزودند.'),
(1575, 11, 102, 'و این‌گونه است برگرفتن پروردگارت، وقتی جمعیت‌ها را در حالی که ستمگرند برگیرد. آری، (به قهر) گرفتن او بسی دردناک و سخت است.'),
(1576, 11, 103, 'به‌راستی در این (یادآوری‌ها) برای کسی که از عذاب آخرت هراسید نشانه‌ای است (و) این، روزی است که مردمان برای آن گرد آورده می‌شوند و همین (روز) مشهود (همگان) است.'),
(1577, 11, 104, 'و ما آن را جز تا زمانی شمارش‌یافته واپس نمی‌اندازیم.'),
(1578, 11, 105, 'روزی (که)، هیچ کس جز به اذن خدا سخن نگوید، پس بعضی از آنان شقاوتمند و (برخی هم) سعادتمندند.'),
(1579, 11, 106, 'پس اما کسانی که شقاوت کرده‌اند، برایشان در آتش، فریادی نفس‌گیرِ مرگبار و ناله‌ای بس سهمگین و پربار است.'),
(1580, 11, 107, 'تا آسمان‌ها و زمین بر جاست در آن ماندگارند؛ مگر آنچه پروردگارت خواسته. بی‌گمان پروردگارت همان را که بخواهد، بی‌چون انجام‌دهنده است.'),
(1581, 11, 108, 'و اما کسانی که سعادتمند شدند، تا آسمان‌ها و زمین بر جاست، جاودانه در بهشتند؛ مگر آنچه پروردگارت خواسته. حال آنکه (این) بخششی است ناگسستنی.'),
(1582, 11, 109, 'پس در(باره‌ی) آنچه آنان [:مشرکان] می‌پرستند، غرق در ژرفای (شک و) تردید مباش. آنان جز همان گونه که از پیش، پدرانشان پرستیدند، نمی‌پرستند. و ما بی‌گمان بهره‌ی ایشان را به‌راستی ناکاسته خواهیم داد.'),
(1583, 11, 110, 'به‌درستی، ما به موسی همانا کتاب (آسمانی) دادیم. پس در آن اختلاف شد، و اگر از جانب پروردگارت کلمه‌ای پیشی نگرفته بود، بی‌گمان میان آنان داوری شده بود. و همواره آنان درباره‌ی آن بی‌چون در شکّی (با) مستند(ی باطل) می‌باشند.'),
(1584, 11, 111, 'همانا پروردگارت (نتیجه‌ی) اعمال هر یک را به‌راستی بی‌کم و کاست در آن هنگام (و هنگامه) به آنان خواهد داد. به‌راستی او به آنچه انجام می‌دهند بسی آگاه است.'),
(1585, 11, 112, 'پس، همان‌گونه که دستور یافته‌ای ایستادگی کن و (نیز) هر که با تو سوی خدا بازگشته استقامت کند. و طغیان مکنید. او به‌راستی به آنچه عمل می‌کنید بسی بیناست.'),
(1586, 11, 113, 'و فراسوی کسانی که ستم کرده‌اند تکیه نزنید، تا (مبادا) آتش به شما (در پی آنان) در رسد، و از غیر خدا برای شما اولیائی نباشد، (که‌) سپس یاری (هم) نشوید.'),
(1587, 11, 114, 'و در دو طرف روز [:اول و آخرش] و بخش‌هایی از شب نماز را بر پا بدار. بی‌گمان (این‌گونه) خوبی‌ها، بدی‌ها را از میان می‌برد. این برای یادکنندگان یادواره‌ای است.'),
(1588, 11, 115, 'و شکیبایی کن. پس خدا بی‌چون پاداش نیکوکاران را ضایع نمی‌گرداند.'),
(1589, 11, 116, 'پس چرا از نسل‌های پیش از شما نگهبانانی نبودند که (مکلفان را) از فساد در زمین باز دارند؟ جز اندکی از کسانی از میان آنان که نجاتشان دادیم. و کسانی که ستم کردند از آنچه ناز و نعمت - که در ژرفای آن غرق بودند - پیروی کردند، در حالی‌که (از) بزهکاران بوده‌اند.'),
(1590, 11, 117, 'و پروردگارت (هرگز) بر آن نبوده است که مجتمع‌ها را که مردمش اصلاح‌گرانند، به اندک ستمی هلاک(شان) کند.'),
(1591, 11, 118, 'و اگر پروردگارت می‌خواست، همه‌ی مردمان را همواره امّت یگانه‌ای، یکسان (در تشریع و تکوین و عقاید و اعمالشان) قرار می‌داد؛ حال آنکه پیوسته در اختلافند.'),
(1592, 11, 119, 'مگر کسانی که پروردگارت به آنان رحم کرده و برای همین (رحم هم) آنان را آفریده. و کلمه‌ی پروردگارت (این‌گونه) تمامیت یافته است (که): «همواره جهنم را از همه‌ی جن و انس بی‌چون پُر خواهم کرد.»'),
(1593, 11, 120, 'و از هر یک، از اخبار مهم پیامبران (خود) را بر تو حکایت می‌کنیم- آنچه که دل فروزانت را بدان استوار می‌گردانیم- و در این(جهان همه‌ی) حقیقت برای تو آمده، و برای مؤمنان اندرز و یادواره‌ای بزرگ است.'),
(1594, 11, 121, 'و به کسانی که ایمان نمی‌آورند بگو: «بر حسب امکان و توانتان عمل کنید، که ما (هم بر حسب امکان و توانمان) بی‌گمان عمل‌کننده‌ایم.»'),
(1595, 11, 122, '« و منتظر باشید که ما (نیز) حتماً منتظرانیم.»'),
(1596, 11, 123, 'و نهان آسمان‌ها و زمین تنها از آنِ خداست و همه چیز و همه کار و هر فرمان تنها سوی او باز گردانده می‌شود؛ پس او را پرستش کن و بر او توکل نمای. و پروردگارت از آنچه می‌کنید غافل نیست.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الر. این‌ها آیات (آن) کتاب روشنگر است.'),
(1598, 12, 2, 'ما بی‌گمان آن را قرآنی روشن‌بیان نازل کردیم، شاید شما (آن را) دریابید.'),
(1599, 12, 3, 'ما نیکوترین برش‌های پیگیر تاریخی را به موجب این قرآن -که به تو وحی کردیم- بر تو حکایت می‌کنیم‌؛ و گرچه پیش از آن همانا از غافلان بوده‌ای.'),
(1600, 12, 4, 'هنگامی که یوسف به پدرش گفت: «پدرم! (در خواب) یازده ستاره را با خورشید و ماه دیدم. آنان را دیدم (که) برای من (به محضر خدا) در حال سجده‌اند.»'),
(1601, 12, 5, '(یعقوب) گفت: «ای پسرک من! خوابت را برای برادرانت حکایت مکن تا برای تو نیرنگی اندیشند. شیطان بی‌امان برای انسان دشمنی آشکارگر است.»'),
(1602, 12, 6, '«و این چنین، پروردگارت تو را بر می‌گزیند و برخی از تعبیر حوادث و خواب‌ها را به تو می‌آموزد، و نعمتش را بر تو و بر خاندان یعقوب به اتمام می‌رساند، همان‌گونه که از پیش بر پدرانت، ابراهیم و اسحاق، به اتمام رسانید. به‌راستی، پروردگارت بس دانایی حکیم است.»'),
(1603, 12, 7, 'به‌راستی و درستی در (سرگذشت) یوسف و برادرانش برای پرسندگان نشانه‌هایی بوده‌است.'),
(1604, 12, 8, 'چون (برادران او) گفتند: «همواره یوسف و برادرش نزد پدرمان از ما - که جمعی نیرومندیم -دوست‌داشتنی‌ترند. بی‌امان پدرمان غرق در (ژرفای) گمراهی آشکارگری است.»'),
(1605, 12, 9, '(گفتند:) «یوسف را بکشید یا او را به سرزمینی بیندازید، تا توجه پدرتان تنها برایتان گردد، و تا پس از او گروهی شایسته باشید.»'),
(1606, 12, 10, 'گوینده‌ای از میان آنان گفت: «یوسف را مکشید و اگر انجام‌دهنده‌ی کاری هستید، او را در نهان‌خانه‌ی (آن) چاه بیفکنید، تا برخی از مسافران او را دریابند.»'),
(1607, 12, 11, 'گفتند: «ای پدر! تو را چه شده که ما را بر یوسف امین نمی‌داری، حال آنکه ما به‌درستی به سود او به‌راستی نصیحت‌گرانیم.»'),
(1608, 12, 12, '«فردا او را با ما بفرست تا در چمنزارها بگردد و بازی کند، و ما به‌راستی نگهبان ویژه‌ی (بایسته و شایسته‌ی) اوییم.»'),
(1609, 12, 13, 'گفت: «اینکه او را ببرید همواره سخت مرا اندوهگین می‌کند، و می‌ترسم -در حالی که از او در غفلتید- گرگ او را بخورد.»'),
(1610, 12, 14, 'گفتند: «اگر به‌راستی گرگ او را بخورد - حال آنکه ما گروهی نیرومند و متحدیم - در آن صورت ما بی‌چون گروهی بس زیانکار (و بی‌بندوبار) خواهیم بود.»'),
(1611, 12, 15, 'پس هنگامی‌که او را بردند و همداستان شدند تا او را در نهان‌خانه‌ی (آن) چاه بیفکنند، و سوی او وحی کردیم که به‌راستی آنان را همانا از این کارشان - در حالی‌که باریک‌بینی نمی‌کنند - با خبری مهم آگاه خواهی‌کرد.'),
(1612, 12, 16, 'و شامگاهان، گریان نزد پدرشان (باز) آمدند.'),
(1613, 12, 17, 'گفتند: «ای پدر! به‌راستی ما رفتیم مسابقه بدهیم، و یوسف را پیش کالای خود نهادیم، پس گرگ او را خورد، ولی تو ما را -هر چند راستگو (هم) باشیم- باورکننده نیستی.»'),
(1614, 12, 18, 'و (پرچم‌وار) با پیراهنش که آغشته به خونی دروغین بود آمدند. (یعقوب) گفت: «(نه!) بلکه نفوس اماره‌ی شما کاری (دروغین) را برایتان آراسته است. پس (اینک جای) صبری زیباست. و بر آنچه توصیف می‌کنید، تنها یاری (از) خداست.»'),
(1615, 12, 19, 'و کاروانی رهسپار آمد. پس (کارگر) آب‌آور خود را فرو فرستادند (و) او دلوش را (به آن چاه) انداخت. گفت: «مژده! این یک پسری است!» و او را چون کالایی پنهان داشتند، حال آنکه خدا به آنچه می‌کردند بسی داناست.'),
(1616, 12, 20, 'و او را به بهایی ناچیز- چند درهم کم شمار- فروختند، و در آن از زاهدان [:بی‌اعتنایان و کم‌جویان] بودند.'),
(1617, 12, 21, 'و آن کس از مصریان که او را خریده به همسرش گفت: «جایگاه و آرامشگاهش را نیکو بدار، شاید به حال ما سود بخشد یا او را به فرزندی برگیریم‌.» و بدین‌گونه ما یوسف را در آن سرزمین مکانت بخشیدیم و برای اینکه به او تأویل حوادث و خواب‌ها را بیاموزیم. و خدا بر کار خویش چیره است ولی بیشتر مردم نمی‌دانند.'),
(1618, 12, 22, 'و چون به رشدهایش رسید، او را حاکمیتی و علمی (بسیار) دادیم. و نیکوکاران را این چنین پاداش می‌دهیم.'),
(1619, 12, 23, 'و آن (بانو) که یوسف در خانه‌اش بود با او (برای کام‌گیری از او) رفت و آمدهایی (شهوت‌زا و محبت‌افزا) کرد و همه‌ی درها(ی عذر و بهانه) را به رویش بست و گفت: «وای بر تو (که به من تن نمی‌دهی)!» (یوسف) گفت: «پناه بر خدا! او پروردگار من است که به من جایگاهی نیکو داده است. بی‌گمان ستمکاران رستگار نمی‌کنند.»'),
(1620, 12, 24, '(این زن) با اصرار و تکرار، همواره آهنگ وی کرد و (یوسف نیز) - اگر برهان پروردگارش را ندیده بود - آهنگ او می‌کرد. چنین (کردیم) تا بدی و زشتکاری تجاوزگر را از او بازگردانیم. به‌راستی او از بندگان پاک‌شده‌ی (ویژه‌ی) ماست.'),
(1621, 12, 25, 'و آن دو، سوی در بر یکدیگر سبقت گرفتند و آن زن پیراهن او را از پشت بدرید. و در آستانه‌ی در ناگهان سرور زن را یافتند. زن گفت: «کیفر کسی که قصد بدی به خانواده‌ی تو کرده چیست‌؟جز اینکه زندانی شود یا (مبتلا به) عذابی دردناک (گردد).»'),
(1622, 12, 26, '(یوسف) گفت: «او با من (برای کام‌گیری) مراوده‌ای (شهوت‌انگیز) داشت.» و شاهدی از خانواده‌ی آن زن شهادت داد: «اگر پیراهن او از جلو چاک خورده، زن راست گفته، و او از دروغگویان است.»'),
(1623, 12, 27, '«و اگر پیراهن او از پشت دریده شده، پس زن دروغ گفته و او از راستان است.»'),
(1624, 12, 28, 'پس چون (شوهرش) دید پیراهنش از پشت چاک خورده گفت: «بی‌گمان، این همواره از نیرنگ شما (زنان) است، بی‌چون نیرنگ شما (زنان) بزرگ است.»'),
(1625, 12, 29, '«یوسف! از این (جریان) روی گردان، و تو (ای زن) برای این گناه بدپایانت پوششی بخواه که بی‌گمان تو از خطاکاران بوده‌ای.»'),
(1626, 12, 30, 'و زنانی در شهر گفتند: «زنِ عزیز با (غلام) جوان خود مراوده‌هایی شهوت‌زا می‌کند [:از او کام می‌خواهد] و سخت دلباخته‌ی او شده. به‌راستی ما او را همواره در گمراهی آشکارگری می‌بینیم.»'),
(1627, 12, 31, 'پس چون (همسر عزیز) مکر زنان را شنید، نزد آنان (کسی را) فرستاد و محفلی با تکیه‌گاهی (فرح‌بخش) برایشان آماده ساخت و به هریک از آنان کاردی داد و (به یوسف) گفت: « بر (دیدار) آنان (از نهان‌گاهت‌) برون آی.» پس چون (زنان) او را دیدند، وی را بس شگرف یافتند و (از شدت هیجان) دست‌های خود را (به جای میوه‌ها) همی بریدند و گفتند: «منزّه است خدا! این بشر نیست! این جز فرشته‌ای بزرگوار نیست!»'),
(1628, 12, 32, '(زلیخا) گفت: «این همان (فرشته‌ی بزرگوار) است که درباره‌ی او سرزنشم می‌کردید. بی‌چون، من همواره با او رفت و آمدهایی شهوت‌زا (برای کام‌گیری از او) داشتم، ولی او بسی جویا و پویای نگهبانی خود بود. و اگر آنچه را که به آن دستورش می‌دهم عمل نکند به‌راستی زندانی شود و بی‌چون و بی‌امان از خوارشدگان گردد.»'),
(1629, 12, 33, '(یوسف) گفت: «پروردگارم! زندان (تن) برای من از آنچه مرا به آن می‌خوانند دوست‌داشتنی‌تر است، و اگر نیرنگ آنان را از من بازنگردانی، کودکانه سوی آنان خواهم گرایید و از (جمله‌ی) نادانان خواهم بود.»'),
(1630, 12, 34, 'پس، پروردگارش خواسته‌اش را برایش اجابت کرد و نیرنگ زنان را از او بازگردانید. بی‌گمان او، (هم)او بسیار شنوای بس داناست.'),
(1631, 12, 35, 'سپس پس از دیدن آن نشانه‌ها، به نظرشان آمد که او را بی‌چون تا چندی بی‌امان به زندان افکنند.'),
(1632, 12, 36, 'و دو جوان با او به زندان در آمدند. یکی از آن دو گفت: «من خویشتن را (به خواب) دیدم که (انگور برای) شرابی می‌فشارم.» و دیگری گفت: «من خود را (به خواب) دیدم که بر روی سرم نانی می‌برم و پرندگان از آن می‌خورند. ما را از تعبیرش خبری مهم ده، که ما تو را به‌راستی از نیکوکاران می‌بینیم.»'),
(1633, 12, 37, 'گفت: «غذایی را که روزی شما می‌گردد برای شما نمی‌آید مگر آنکه من از واقعیت و پیامدش به شما خبری مهم می‌دهم. پیش از آنکه آن غذا برایتان بیاید، این برای شما دو تن از همان چیزهایی است که پروردگارم به من آموخته. من آیین قومی را که به خدا اعتقاد ندارند - و همانانند که به روز بازپسین کافرانند - رها کردم‌»'),
(1634, 12, 38, '«و آیین پدرانم -ابراهیم و اسحاق و یعقوب- را پیروی نمودم. برای ما هرگز سزاوار نبوده که هیچ چیزی را شریک خدا کنیم. این (فضیلت) از فضل خداست بر ما و بر مردم‌؛ ولی بیشتر مردم سپاسگزاری نمی‌کنند.»'),
(1635, 12, 39, '«ای دو همراه زندانیم! آیا خدایان پراکنده بهترند یا خدای یگانه‌ی بسی برومند و چیره‌؟»'),
(1636, 12, 40, '«شما بجز او، جز نام‌هایی (بی‌نشان) را نمی‌پرستید که شما و پدرانتان آنها را نامگذاری کرده‌اید (و) خدا دلیلی سلطه‌گر بر (حقانیت) آنها نازل نکرده است. فرمان جز برای خدا نیست (که‌) فرمان داده جز او را نپرستید. این است دین درست پای‌برجا و راستا و پر بها؛ ولی بیشتر مردم نمی‌دانند.»'),
(1637, 12, 41, '«ای دو همراه زندانیم! اما یکی از شما به سرور خود باده می‌نوشاند، (و) اما دیگری به دار آویخته می‌شود، سپس پرندگان از سرش می‌خورند. امری که شما دو تن از من جویا می‌شوید تحقق یافته است.»'),
(1638, 12, 42, 'و (یوسف) به آن کس از آن دو که گمان می‌کرد نجات‌یافتنی است، گفت: «مرا نزد آقای خود به یاد آور.» و (اما) شیطان، یادآوری به آقایش را از یادش برد (و) در نتیجه (یوسف) چند سالی در زندان بماند.'),
(1639, 12, 43, 'و پادشاه (مصر) گفت: «من (در خواب) می‌بینم هفت گاو فربه را که هفت (گاو) لاغر آنها را می‌خوردند، و هفت خوشه‌ی سبز و (نیز) هفت خوشه‌ی دیگر خشکیده را. ای سران قوم! اگر خواب تعبیر می‌کرده‌اید، درباره‌ی خواب من، به من رأی تازه‌ای بدهید.»'),
(1640, 12, 44, 'گفتند: « (این‌ها) پاره‌هایی است پراکنده از خواب‌هایی پریشان، و ما به تعبیر خواب‌های آشفته دانا نیستیم.»'),
(1641, 12, 45, 'و آن کس از آن دو (به زندانی) که نجات یافته و پس از چندی (سخن یوسف را) به یاد آورده بود گفت: «مرا (به زندان) بفرستید تا شما را از تعبیر این (خواب) خبری مهم بدهم.»'),
(1642, 12, 46, '«یوسف! ای مرد بسی راستگوی راست‌کردار! درباره‌ی (این خواب که) هفت گاو فربه، هفت گاو لاغر آنها را می‌خورند، و هفت خوشه‌ی سبز و هفت (خوشه‌ی) دیگر خشکیده، به ما نظری نو بده. شاید فراسوی مردم برگردم (و) شاید آنان (تعبیر آنها را و صداقت تو را) بدانند.»'),
(1643, 12, 47, 'یوسف گفت: «هفت سال پی‌درپی پویا و کوشا با رنجی مداوم می‌کارید، پس آنچه را درویدید - جز اندکی را که می‌خورید - در خوشه‌اش واگذارید.»'),
(1644, 12, 48, '«سپس بعد از آن، هفت سال سخت در می‌رسد (که) آنچه در آن سال(‌های سبز) از پیش نهاده‌اید - جز اندکی را که می‌اندوزید- (آن سال‌ها) همه را خواهند خورد.»'),
(1645, 12, 49, '«سپس، سالی در می‌رسد که مردم در آن (سال) از بی‌آبی نجات یافته (و) با باران کمک می‌شوند و در آن (میوه) می‌فشارند.»'),
(1646, 12, 50, 'و پادشاه گفت: «او را نزد من آورید.» پس هنگامی که آن فرستاده نزد وی آمد (یوسف) گفت: «نزد آقای خویش برگرد و از او بپرس که حالت مهم آن زنانی که دست‌های خود را به شدت بریدند چگونه (بوده) است‌؟ بی‌گمان پروردگار من به نیرنگ آنان بسی آگاه است.»'),
(1647, 12, 51, '(پادشاه به آن زنان) گفت: « چون از یوسف کام خواستید شما را چه مهمی روی داده بود؟» (زنان) گفتند: «منزّه است خدا! ما هیچ بدی بر یوسف ندانستیم.» همسر عزیز گفت: «اکنون حقیقت به روشنی آشکار شد. من بودم که با او مراوده‌ی شهوت‌زا کردم (و از او کام خواستم) و بی‌گمان او به‌راستی از راستان است.»'),
(1648, 12, 52, '(یوسف گفت:) «این (درخواست اعاده‌ی حیثیّت) برای آن بود تا (عزیز) بداند که من بی‌گمان در نهان به او خیانت نکردم. و خدا بی‌امان نیرنگ خائنان را راهنمایی نمی‌کند.»'),
(1649, 12, 53, '«و من نفس خود را تبرئه نمی‌کنم (که‌) همواره نفس (اماره) همی امرکننده به بدی است، مگر آنچه را که خدای من رحم کند. به‌راستی پروردگار من پوشنده‌ی رحمتگر بر ویژگان است.»'),
(1650, 12, 54, 'و پادشاه گفت: «او را نزد من آورید، تا وی را برای خود برگزینم.» پس چون با او سخن به میان آورد گفت: «تو امروز به‌درستی نزد ما دارای منزلت و مکانت و امانتی بزرگ هستی.»'),
(1651, 12, 55, '(یوسف) گفت: «مرا به خزانه‌داری (این) سرزمین برگمار. بی‌گمان من نگهبانی بسی نیرومند و دانا هستم.»'),
(1652, 12, 56, 'و بدین‌گونه یوسف را در سرزمین (مصر) مقام و منزلت دادیم، که از آن - هر جا که بخواهد - سکونتی هموار و راهوار می‌کرد. (این‌گونه‌) هر که را بخواهیم رحمت خود را به او می‌رسانیم و اجر نیکوکاران را تباه نمی‌سازیم.'),
(1653, 12, 57, 'و به راستی اجر آخرت - برای کسانی که ایمان آورده و پرهیزگاری می‌نموده‌اند - بهتر است.'),
(1654, 12, 58, 'و برادران یوسف آمدند و بر او وارد شدند. پس آنان را شناخت حال آنکه آنان انکار (و انگار)کننده‌ی اویند.'),
(1655, 12, 59, 'و چون آنان را به خواروبار (دلخواه زندگی‌ساز)شان مجهّز کرد گفت: «برادر پدری خود را نزد من آرید، مگر نمی‌بینید که من پیمانه را تمام می‌دهم و من بهترین میزبانانم‌؟»'),
(1656, 12, 60, '«پس اگر او را نزد من نیاوردید، برای شما نزد من هرگز پیمانه‌ای نیست، و به من نزدیک (هم) نشوید.»'),
(1657, 12, 61, 'گفتند: «او را با نیرنگ و رفت و آمدی مکرر از پدرش خواهیم ربود، و به‌راستی ما همی کننده(ی این کار) می‌باشیم.»'),
(1658, 12, 62, 'و (یوسف) به جوانان خود گفت: «سرمایه‌های آنان را در بارهایشان بگذارید، شاید هنگامی که سوی خانواده‌ی خود برگشتند آن را بازیابند، شاید بازگردند.»'),
(1659, 12, 63, 'پس هنگامی که سوی پدرشان بازگشتند، گفتند: «ای پدرمان! پیمانه (‌ی گندم) از ما منع شد، پس برادرمان را با ما بفرست تا پیمانه برگیریم، و ما بی‌گمان برای او نگهبانان ویژه‌ایم.»'),
(1660, 12, 64, '(یعقوب) گفت: «آیا جز همان‌گونه که شما را پیش از این بر برادرش امینتان داشتم، بر او (نیز اکنون هم) امینتان بدارم‌؟! پس خدا بهترین نگهبان است، و اوست رحم‌کننده‌ترین رحم‌کنندگان.»'),
(1661, 12, 65, 'و هنگامی که بار خود را گشودند، دریافتند که سرمایه‌شان بدان‌ها بازگردانیده شده.گفتند: «ای پدرمان! ما (دیگر) چه می‌جوییم؟ این سرمایه‌ی ماست که به ما بازگردانیده شده و قوت خانواده‌ی خود را (هم) می‌افزاییم و برادرمان را (هم) حفظ می‌کنیم و پیمانه‌ی شتری (هم) می‌افزاییم‌؛ این (پیمانه‌ی گذشته‌ی عزیز) پیمانه‌ای ناچیز است.»'),
(1662, 12, 66, 'گفت: «هرگز او را با شما نخواهم فرستاد تا پیمانی از خدایم بیاورید که به‌راستی او را بی‌چون نزد من باز آورید، مگر آنکه ناخودآگاه گرفتار شوید.» پس چون پیمان خود را برای او آوردند (یعقوب) گفت: «خدا بر آنچه می‌گوییم وکیل است.»'),
(1663, 12, 67, 'و گفت: «ای پسران من! (همه) از یک در و راه در نیایید و از راه‌ها و درهای جداگانه درآیید و من (با این سفارش) چیزی از (قضای) خدا را از شما دور نمی‌توانم داشت. فرمان جز برای خدا نیست. تنها بر او توکل کردم، پس توکل‌کنندگان باید تنها بر او توکل کنند.»'),
(1664, 12, 68, 'و چون - همان‌گونه که پدرشان به آنان امر کرده بود - وارد شدند (این کار) چیزی را در برابر خدا از آنان بی‌نیاز نکرده بود؛ جز اینکه یعقوب نیازی را که در دلش بود، بر آورد. و بی‌گمان، او به آنچه بدو آموخته بودیم به‌راستی دارای دانشی (فراوان) بود؛ ولی بیشتر مردمان نمی‌دانند.'),
(1665, 12, 69, 'و هنگامی که بر یوسف وارد شدند، برادرش [:بنیامین] را سوی خود جای داد و گفت: «به‌راستی من، همین من، برادر تو هستم. پس، از آنچه (برادران) می‌کرده‌اند ،غمگین مباش.»'),
(1666, 12, 70, 'پس هنگامی که آنان را به خواروبارشان مجهز کرد، پیمانه(ی زرینِ شاه) را در بارِ برادرش نهاد. سپس (به دستور او) ندا کننده‌ای ندا در داد: «بی‌گمان شما همانا دزدانید.»'),
(1667, 12, 71, '(برادران) در حالی که به آنان روی کردند، گفتند: «در پی چه‌اید؟»'),
(1668, 12, 72, 'گفتند: «در پی پیمانه‌ی شاهیم، و برای هر که آن را بیاورد یک بار شتر است.» و (ندا کننده‌ای گفت:) «و من سخت ضامن آنم.»'),
(1669, 12, 73, 'گفتند: «به خدا سوگند، شما به‌راستی و درستی دانستید که ما نیامده‌ایم تا در این سرزمین افساد کنیم و ما هیچ‌گاه دزد نبوده‌ایم.»'),
(1670, 12, 74, 'گفتند: «پس، اگر دروغگو بوده‌اید کیفرش چیست‌؟»'),
(1671, 12, 75, 'گفتند: «کیفرش کسی است که پیمانه در بارش پیدا شود، پس کیفرش خود اوست. ما ستمکاران را این‌گونه کیفر می‌دهیم.»'),
(1672, 12, 76, 'پس (یوسف) به (بازرسی) بارهای آنان - پیش از بار برادرش - آغاز کرد. سپس (در پایان کار) آن را از بار برادرش [:بنیامین] به پویایی (ظاهری) در آورد. این‌گونه به یوسف شیوه(ای شرعی) آموختیم (چرا که) او در آیین پادشاه نمی‌توانست برادرش را بگیرد؛ مگر اینکه خدا بخواهد (و چنین راهی بدو بنماید). کسانی را که بخواهیم به درجاتی بالا می‌بریم و برتر از هر صاحب دانشی، دانشوری است.'),
(1673, 12, 77, 'گفتند: «اگر او دزدی می‌کند، همانا پیش از این برادرش (یوسف هم) دزدی کرده است.» پس یوسف این (سخن) را در دل خود پنهان داشت و آن را برایشان آشکار نکرد (ولی) گفت: «موقعیت شما بدتر (از او) است، و خدا به آنچه وصف می‌کنید داناتر است.»'),
(1674, 12, 78, 'گفتند: «هان ای عزیز! او را به‌راستی پدری پیر (و) سالخورده است؛ پس یکی از ما را به جای او برگیر. بی‌گمان ما تو را از نیکوکاران می‌بینیم.»'),
(1675, 12, 79, 'گفت: «پناه بر خدا که جز آن کس را که کالای خود را نزد وی یافته‌ایم برگیریم، زیرا در آن صورت بی‌امان ما ستمکارانیم.»'),
(1676, 12, 80, 'پس هنگامی که از او نومید شدند، رازگویان (هم‌چون فراریان خستند و) رستند. بزرگشان گفت: «مگر نمی‌دانید که پدرتان بر شما پیمانی استوار از خدا بر گرفت‌؟ و از پیش (هم) درباره‌ی یوسف آنچه تفریط و کوتاهی کردید (یاد کنید). پس هرگز از این سرزمین بر نخواهم خاست تا پدرم برایم اجازه دهد یا خدا در حق من داوری کند و او بهترین داوران است.»'),
(1677, 12, 81, '«سوی پدرتان بازگردید. پس (به او) بگویید: پدرمان! پسرت دزدی کرده و ما جز به آنچه می‌دانستیم گواهی نمی‌دهیم. و ما نگهبان غیب (هم) نبوده‌ایم.»'),
(1678, 12, 82, '«و از (مردم) مجتمعی که در آن بودیم و (از) کاروانی که از میانشان آمدیم جویا شو و ما به‌راستی راستگویانیم.»'),
(1679, 12, 83, '(یعقوب) گفت: « (چنین نیست!) بلکه نفس‌های (اماره‌ی) شما کاری (نادرست) را برایتان آراسته است. پس صبری زیبا و دل‌ربا (باید). امید که خدا همه‌ی آنان را سوی من (باز) آورد، که او، (هم)او بس دانای حکیم است.»'),
(1680, 12, 84, 'و از آنان روی گردانید و گفت: «اسفا بر یوسف!» و دو چشمانش از اندوه سپید شد. پس او بس فرو نشاننده‌ی خشم است.'),
(1681, 12, 85, '(پسران) گفتند: «به خدا سوگند که پیوسته یوسف را یاد می‌کنی تا به پرتگاه هلاکت افتی یا (در پایان) از هلاک‌شدگان باشی.»'),
(1682, 12, 86, 'گفت: «من شکایت غم و اندوه خود را تنها سوی خدا می‌برم، و از خدا چیزی می‌دانم که شما نمی‌دانید.»'),
(1683, 12, 87, '«ای پسران من! بروید و از یوسف و برادرش با احساسی اکید کاوش و پویش کنید. و از آسایش روح‌افزا و نسیم فریادرسی و شادی و مهربانی و بخشایش ربانی نومید نشوید. بی‌گمان جز گروه کافران (کسانی) از رحمت خدا نومید نشوند.»'),
(1684, 12, 88, 'پس هنگامی که (برادران) بر یوسف وارد شدند، گفتند: «ای عزیز! به ما و خانواده‌ی ما زیان (مرگبار) رسیده و (اکنون) با سرمایه‌ای کم (نزد تو) آمده‌ایم. پس برای ما پیمانه را وافی و کامل گردان و بر ما تصدّق فرمای. به‌راستی خدا صدقه‌دهندگان را پاداش می‌دهد.»'),
(1685, 12, 89, 'گفت: «آیا دانستید با یوسف و برادرش به هنگام (و هنگامه‌ی) نادانی، چه(ها) کردید؟»'),
(1686, 12, 90, 'گفتند: «آیا تو بی‌گمان خودت، به‌راستی یوسفی‌؟»گفت: « (آری) من، (همین) من یوسفم، و این برادر من است. به‌راستی خدا بر ما منّت نهاده است. هر که همواره تقوا و صبر پیشه کند بی‌گمان خدا پاداش نیکوکاران را تباه نمی‌کند.»'),
(1687, 12, 91, 'گفتند: «به خدا سوگند، (که) بی‌چون خدا تو را به‌راستی بر ما برتری داده است و گر چه ما بی‌شک خطاکار بوده‌ایم.»'),
(1688, 12, 92, '(یوسف) گفت: «امروز بر شما سرزنش و رسوایی نیست. خدا برای شما گناهتان را می‌پوشد و او رحم کننده‌ترین رحم‌کنندگان است.»'),
(1689, 12, 93, '«این پیراهن مرا ببرید. پس آن را بر چهره‌ی پدرم بیفکنید تا بینا بیاید و همه‌ی کسان خود را نزد من آورید.»'),
(1690, 12, 94, 'و هنگامی که کاروان جدا شد، پدرشان گفت: «اگر مرا به کم فهمی نسبت ندهید من به‌راستی بوی یوسف را می‌یابم.»'),
(1691, 12, 95, 'گفتند: «به خدا سوگند تو بی‌گمان (هم‌چنان) فرو رفته‌ی در ژرفای گمراهی دیرینت هستی.»'),
(1692, 12, 96, 'پس هنگامی که مژده‌رسان آمد، آن (پیراهن) را بر چهره‌اش انداخت (و یعقوب) به بیناییش برگشت. گفت: «آیا به شما نگفتم (که) بی‌شک من از (عنایت) خدا چیزهایی می‌دانم که شما نمی‌دانید؟»'),
(1693, 12, 97, 'گفتند: «پدرمان! برای گناهانمان (از خدا) پوشش (و پوزش) بخواه (که) ما بی‌گمان خطاکاران بوده‌ایم‌.»'),
(1694, 12, 98, 'گفت: «در آینده‌ای دور از پروردگارم برای شما پوشش می‌خواهم. او، همانا (هم)او بسی پوشنده‌ی رحمتگر بر ویژگان است.»'),
(1695, 12, 99, 'پس هنگامی که (برادران) بر یوسف وارد شدند، پدر و مادر خود را در کنار خویش جای داد و گفت: «ان‌شاءالله، با امن و امان داخل مصر شوید.»'),
(1696, 12, 100, 'و پدر و مادرش را بر تخت به بالا (و بلندا) برد، و برای او (به شکرانه‌ی این نعمت برای خدا) به سجده در افتادند و گفت: «پدرم! (هم) این است تعبیر خواب پیشینم به‌راستی پروردگارم آن را راست و پای‌برجا گردانید، و بی‌گمان به من احسان کرد، چون مرا از زندان خارج ساخت و شما را از بیابان (کنعان به مصر) باز آورد، پس از آنکه شیطان میان من و برادرانم دخالتی افسادگر نمود. بی‌چون، پروردگارم نسبت به آنچه بخواهد دقیق و ریزه‌کار است. به‌راستی او بس دانای حکیم است.»'),
(1697, 12, 101, '«پروردگارم! تو به‌راستی بخشی از پادشاهی را به من دادی. و برخی از تعبیر حوادث و خواب‌ها را به من آموختی. ای پدیدآورنده‌ی آسمان‌ها و زمین بر فطرت توحید! تنها تو در دنیا و آخرت مولای منی. مرا به حالت تسلیم بمیران، و مرا به شایستگان ملحق فرمای.»'),
(1698, 12, 102, 'این (ماجرا) از خبرهای مهم غیب است. آن را به تو وحی می‌کنیم و تو - چون آنان در کارشان هم‌داستان شدند در حالی که مکر می‌کردند -نزدشان نبودی.'),
(1699, 12, 103, 'و بیشتر مردمان - هر چند (برای ایمان آوردنشان) حرص ورزی - (از) ایمان‌آورندگان نیستند.'),
(1700, 12, 104, 'و تو بر این (کار) پاداشی از آنان نخواهی. این (قرآن و پیامبران) جز یادواره‌ای برای جهانیان نیست.'),
(1701, 12, 105, 'و چه بسیار از نشانه‌هایی در آسمان‌ها و زمین است که بر آنها می‌گذرند، در حالی که از آنها رویگردانند.'),
(1702, 12, 106, 'و بیشترشان به خدا ایمان نمی‌آورند مگر آنکه هم‌چنان مشرکانند.'),
(1703, 12, 107, 'پس آیا از اینکه عذابی فراگیر از خدا آنان را در رسد در امانند؟ یا (از) ساعت [:قیامت] - درحالی که بی‌خبرند - ناگهان آنان را فرا رسد (ایمنند)؟'),
(1704, 12, 108, 'بگو: «این راه (راهوار) من است و هر کس را (که‌) پیروی‌ام کرد از روی بصیرتی (شایسته) به راه خدا دعوت می‌کنم. و منزّه است خدا. و من از مشرکان نیستم.»'),
(1705, 12, 109, 'و پیش از تو (نیز) بجز مردانی از اهل مجتمع‌ها - که به آنان وحی می‌کردیم - (برای مکلفان) نفرستادیم. آیا پس (از این وحی) در زمین نگردیدند تا فرجام کسانی را که پیش از آنان بوده‌اند بنگرند؟ و به درستی سرای آخرت -برای کسانی که پرهیزگاری کرده‌اند- بهتر است. پس آیا خردورزی نمی‌کنید؟'),
(1706, 12, 110, 'تا هنگامی که فرستادگان (ما از ایمان کافران) نومید شدند و پنداشتند که به آنان (از جانب مردمان) بی‌گمان دروغ گفته شده، یاریمان آنان را در رسید. پس کسانی را که می‌خواستیم، نجات یافتند. و برخورد شدیدمان از گروه مجرمان برگشت ندارد.'),
(1707, 12, 111, 'بی‌گمان در سرگذشت آنان، برای خردمندان همواره عبرتی بوده است. این سخنی نبوده که به دروغ ساخته شده باشد، بلکه تصدیق‌کننده‌ی آنچه از کتاب‌هایی بوده که در برابرش بوده (و پیش از آن گذشته) و جداسازی همه چیز است و گروهی را که ایمان می‌آورند رهنمودی و رحمتی (بزرگ) است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'المر این‌هاست آیات کتاب [:قرآن]، و آنچه از جانب پروردگارت سوی تو نازل شده، تمامی حق است ولی بیشتر مردمان ایمان نمی‌آورند.'),
(1709, 13, 2, 'خدا کسی است که آسمان‌ها را بدون ستون‌هایی که آنها را ببینید برافراشت. سپس بر عرش (تدبیر) چیره شد و خورشید و ماه را رام (و راهوار) گردانید. هر کدام برای مدتی به سیر خود روانند، حال آنکه (خدا) امر [:کار، فرمان و هر چیز آفریدگان] را تدبیر می‌کند. و آیات (خود) را به روشنی جداسازی و بیان می‌نماید، شاید شما به لقای پروردگارتان یقین آورید.'),
(1710, 13, 3, 'و اوست کسی که زمین را گسترانید و در آن کوه‌های بلند فرو رفته در زمین (و) سر در آسمان و رودهایی نهاد و از هر گونه میوه‌ای در آن نر و ماده قرار داد، حال آنکه روز را به شب می‌پوشاند. به‌راستی در این (امور) برای مردمی که تفکر می‌کنند نشانه‌هایی است.'),
(1711, 13, 4, 'و در زمین قطعاتی است کنار یکدیگر و باغ‌هایی از انگور و کشتزارها و درختان خرما - (چه) همانند و (چه) ناهمانند - (که‌) با یک آب سیراب می‌گردند؛ و برخی از آنها را (از نظر مزه و کیفیت) بر برخی دیگر در خوراک برتری می‌دهیم. به‌راستی در این (امر) برای مردمی که خردورزی می‌کنند همواره نشانه‌هایی است.'),
(1712, 13, 5, 'و اگر شگفتی داری، پس شگفت از سخن آنان [:کافران] دار که: «آیا وقتی خاک شدیم، آیا به‌راستی بی‌چون در آفرینش جدیدی خواهیم بود؟» همان کسانی‌اند که به پروردگارشان کفر ورزیدند و (هم)اینان در گردن‌هایشان زنجیرهاست، و آنان همدمان آتشند، و در آن (به اندازه‌ی کفرشان و کمتر از آن) ماندگارند.'),
(1713, 13, 6, 'و پیش از خوبی، با شتاب از تو بدی می‌خواهند، حال آنکه بی‌گمان پیش از آنان (بر کافران) عقوبت‌هایی پندآور و هشیارگر می‌رفته است. و به‌راستی پروردگارت نسبت به مردمان - با وجود ستمشان - بی‌گمان پوشاننده است، و همانا پروردگار تو بی‌چون به‌راستی سخت‌کیفر است.'),
(1714, 13, 7, 'و آنان که کافر شدند گویند: «چرا نشانه‌ای (آشکار) از طرف پروردگارش بر او نازل نشده است‌؟» (ای پیامبر!) تو فقط هشداردهنده‌ای. و برای هر گروهی رهبری است.'),
(1715, 13, 8, 'خدا می‌داند آنچه را که هر مادینه‌ای (در رَحِمش) بر می‌گیرد و (نیز) آنچه را که (این) رحم‌ها می‌کاهند، و آنچه را فزونی می‌یابند. و هر چیزی را نزد او اندازه‌ای است.'),
(1716, 13, 9, 'دانای نهان و آشکار، همان بزرگِ بلندمرتبه است.'),
(1717, 13, 10, '(برای او) یکسان است (که) کسی از شما سخن (خود) را پنهان کند، و کسی آن را فاش گرداند، و کسی که در شب خویشتن را پنهان دارد و روز آشکارا گرداند.'),
(1718, 13, 11, 'برای او (نسبت به آنان) دنبال‌کنندگان و پیگیرانی است (که) پیاپی یکدیگر او را به فرمان خدا از پیش رو و از پشت سر پاسداری می‌کنند. به‌راستی خدا حال و مالی قومی را تغییر نمی‌دهد، تا آنان خود آن (جان و جانان‌) را تغییر دهند. و هنگامی که خدا برای قومی بدی بخواهد، پس هیچ برگشتی برای آن نمی‌باشد و غیر از او سرپرستی (هم) برایشان نیست.'),
(1719, 13, 12, 'اوست کسی که برق را برای بیم و امید به شما می‌نمایاند، و ابرهای گران‌بار را به وجود می‌آورد.'),
(1720, 13, 13, 'رعد، به حمد او تنزیه(اش) می‌کند، و فرشتگان جملگی از بیمش تسبیح گویند. و صاعقه‌ها را فرو می‌فرستد و با آنها هر که را بخواهد، مورد اصابت قرار می‌دهد؛ در حالی که آنان درباره‌ی خدا مجادله می‌کنند. و او در کیفرهایش (در عین علم و قدرتش) بسی پیگیر (گناهان) است.'),
(1721, 13, 14, 'دعوت حق تنها برای اوست و کسانی که جز او را می‌خوانند، آنان هیچ گونه اجابتی برایشان نمی‌کنند؛ مگر مانند کسی که دو دستش را به سوی آب گشاینده است تا (آب) به دهانش برسد، در حالی که (به آب) هرگز رسنده نیست. و درخواست کافران جز در (ژرفای) گمراهی نیست.'),
(1722, 13, 15, 'و هر که در آسمان‌ها و زمین است - خواه‌وناخواه - با سایه‌هایشان، بامدادان و شامگاهان، تنها برای خدا سجده (و خضوع) می‌کنند.'),
(1723, 13, 16, 'بگو: «پروردگار آسمان‌ها و زمین کیست‌؟» بگو: «خداست!» بگو: «پس آیا جز او سرپرستانی گرفته‌اید، حال آنکه اختیار سود و زیان خودشان را (هم) ندارند؟» بگو: «آیا نابینا و بینا یکسانند؟ یا تاریکی‌ها و روشنایی همانندند؟ یا برای خدا شریکانی نهاده‌اند (که) همانند آفرینش او آفریده‌اند، پس (در نتیجه، این دو) آفرینش بر آنان مشتبه شده‌اند؟» بگو: «خدا آفریننده‌ی هر چیزی است و اوست یگانه‌ی قهار.»'),
(1724, 13, 17, '(همو) از آسمان، آبی فرود آورد. پس (به طرف) گودی‌هایی به اندازه‌ی گنجایش‌شان روان شد و پس سیل، کفی برخاسته بر خود بس بار کرد. و از آنچه برای به دست آوردن زینتی یا کالایی، در آتش می‌گذارند، همانند آن، کفی بر می‌آید. خدا حق و باطل را چنین مَثَل می‌زند. و اما کف، پس بی‌مهابا به هدر می‌رود، ولی‌آنچه به مردم سود می‌رساند، در زمین هم‌چنان (باقی) می‌ماند. خدا مَثَل‌ها را چنین می‌زند.'),
(1725, 13, 18, 'برای کسانی که پروردگارشان را اجابت کردند، نیکوترین پاداش است، و کسانی که برایش اجابت نکرده‌اند، اگر سراسر آنچه در زمین است و مانند آن را با آن داشته باشند، همواره آن را برای بازخرید خود خواهند داد. آنان به سختی بازخواست‌شونده‌ا‌ند، و پناهگاهشان دوزخ است، و چه بد پناهگاهی است.'),
(1726, 13, 19, 'آیا پس،کسی که می‌داند آنچه از جانب پروردگارت، سویت نازل شده است (تنها همان تمامی) حقیقت است، مانند کسی است که کور دل است‌؟ تنها خردمندانند که (حق را) به خوبی یاد می‌کنند؛'),
(1727, 13, 20, 'کسانی که به پیمان خدا وفادارند و عهد(شان) را نمی‌شکنند؛'),
(1728, 13, 21, 'و کسانی که آنچه را خدا به پیوستنش فرمان داده می‌پیوندند و از پروردگارشان می‌هراسند و از بدی [:سختی] حساب بیم دارند.'),
(1729, 13, 22, 'و کسانی که برای طلب خشنودی پروردگارشان شکیبایی کردند، و نماز را بر پا داشتند و از آنچه روزیشان دادیم، نهان و آشکارا انفاق کردند، و بدی را با نیکی می‌زدایند، برایشان عاقبت (خودش در) سرای آینده است.'),
(1730, 13, 23, 'باغ‌های جاودان که آنان با شایستگان - از پدرانشان و همسرانشان - در آنها داخل می‌شوند و فرشتگان از هر دری بر آنان در می‌آیند،'),
(1731, 13, 24, '(و به آنان می‌گویند:) «درود بر شما، به (پاداش) آنچه صبر کردید.» پس چه نکوست عاقبت (آن) سرای.'),
(1732, 13, 25, 'و کسانی که پیمان خدا را پس از بستن آن می‌شکنند، و آنچه را خدا به پیوستن آن فرمان داده می‌گسلند و در زمین افساد می‌کنند، برایشان لعنت بزرگ است، و بدی (عادلانه‌ی) آن سرا ایشان را (سزا)ست.'),
(1733, 13, 26, 'خدا روزی را برای هر که بخواهد گشاده و (یا) تنگ می‌گرداند. و (اما) آنان به زندگی دنیا شاد شدند، و زندگی دنیا در (برابر) آخرت جز بهره‌گیری و متاعی در آخرت نیست.'),
(1734, 13, 27, 'و کسانی که کفر ورزیدند گویند: «چرا از جانب پروردگارش (هیچ) نشانه‌ای بر او نازل نشده است‌؟» بگو: «در حقیقت خداست که هر کس را بخواهد بی‌راه می‌گذارد. و آن کس را که پیاپی فرا رویش روان بوده، سوی خود راه می‌نماید.»'),
(1735, 13, 28, '(همان) کسانی که ایمان آورده‌اند و دل‌هایشان به یاد خدا آرام می‌گیرد. هشدار که با یاد خدا دل‌ها آرامش می‌یابد.'),
(1736, 13, 29, 'کسانی که ایمان آورده و کارهای شایسته(ی ایمان) کرده‌اند، شادکامی و سرانجام خوشی برای آنان است.'),
(1737, 13, 30, 'این‌گونه تو را در میان امتی - که پیش از آن، امت‌هایی همواره روزگارانی به سر بردند - فرستادیم، تا آنچه را به تو وحی کردیم بر آنان بخوانی، در حالی که ایشان به (خدای) رحمان کفر می‌ورزند. بگو: «اوست پروردگارم (و) معبودی جز او نیست. تنها بر او توکل می‌کنم و بازگشتم تنها سوی اوست.»'),
(1738, 13, 31, 'و اگر به راستی قرآنی بود که کوه‌ها بدان بس روان می‌شدند، یا زمین بدان همی قطعه قطعه می‌گردید، یا مردگان بدان به سخن در می‌آمدند (باز هم در آنان اثر نمی‌کرد). بلکه همه‌ی امور تنها برای خداست. پس آیا کسانی که ایمان آوردند، مأیوس نشدند که اگر خدا می‌خواست همواره همه‌ی مردمان را به راه می‌آورد؟ و کسانی که کافر شدند پیوسته به (سزای) آنچه کردند (مصیبت) کوبنده‌ای آنان را دررسد، یا نزدیک خانه‌هایشان نفوذ کند، تا وعده‌ی خدا فرارسد. خدا هرگز وعده(ی خود) را خلاف نمی‌کند.'),
(1739, 13, 32, 'و بی‌گمان، فرستادگانی پیش از تو بی‌امان مسخره شدند. پس برای کسانی که کافر شدند مهلت دادم. سپس آنان را (به کفرشان) برگرفتم. پس چگونه بود پیگیری‌ام‌؟'),
(1740, 13, 33, 'آیا پس کسی که بر (سروسامان) هر شخصی - بدانچه کرده است - ایستا و مراقب است (مانند کسی است که از همه جا بی‌خبر است)؟ حال آنکه برای خدا شریکانی قرار دادند، بگو: «نامشان را ببرید. یا او را به آنچه در زمین نمی‌داند خبری مهم دهید، یا با سخنی سطحی (این خبر مهم را می‌دهید)؟» بلکه برای کسانی که کافر شده‌اند نیرنگشان آراسته شده و از راه (حق) باز داشته شده‌اند. و خدا هر که را بیراه واگذارد، راهبری نخواهد داشت.'),
(1741, 13, 34, 'برای آنان در زندگی دنیا عذابی است و عذاب آخرت بی‌گمان دشوارتر است و برای ایشان از (سوی) خدا هیچ نگهبانی نیست.'),
(1742, 13, 35, 'نماد و نمایانگر بهشتی که پرهیزگاران (به آن) وعده داده شدند (این است که) از زیر (درختان)شان نهرها روان است، خوردنیش پیوسته و سایه‌اش پایدار است؛ این (بزرگْ)پاداش، فرجام کسانی است که پرهیزگاری کردند، و فرجام کافران آتش است.'),
(1743, 13, 36, 'و کسانی که به‌ایشان کتاب (وحیانی) دادیم، به آنچه فراسویت نازل شده شاد می‌شوند و برخی از احزاب کسانی هستند که بخشی از آن را انکار می‌کنند. بگو: «جز این نیست که من مأمورم خدای را بپرستم و به او شرک نورزم. تنها به سوی او می‌خوانم و بازگشتم (هم) تنها سوی اوست.»'),
(1744, 13, 37, 'و بدین‌سان آن [:قرآن] را فرمانی روشن نازل کردیم. و اگر به‌راستی پس از دانشی (وحیانی) که تو را آمده است هوس‌هایشان را پیروی کنی از (جانب) خدا هیچ سرپرست و حمایتگری نخواهی داشت.'),
(1745, 13, 38, 'و همواره پیش از تو (نیز) پیام‌آورانی فرستادیم و برای آنان زنان و فرزندانی نهادیم. و هیچ پیامبری را هرگز (چنان) نبوده است که جز به اذن خدا نشانه‌ای (رسالتی) بیاورد. برای هر سررسیدی (معین) نبشته‌ای (در تقدیر ربانی) است.'),
(1746, 13, 39, 'خدا آنچه را بخواهد محو یا اثبات می‌کند و اصل (و همه‌ی‌) کتاب تنها نزد اوست.'),
(1747, 13, 40, 'و اگر به‌راستی پاره‌ای از آنچه را که به آنان وعده می‌دهیم به تو بنمایانیم، یا تو را بی‌امان می‌میرانیم. پس جز این نیست که بر تو تنها رساندن پیام است، و حساب (آنان) تنها بر ماست.'),
(1748, 13, 41, 'آیا و ندیدند که ما همواره (با علم و قدرت و حکمت) به زمین توجه (ویژه‌ای) می‌کنیم، حال آنکه از اطراف آن می‌کاهیم‌؟ و تنها خداست که حکم می‌کند. هرگز برای حکم او تعقیب‌کننده (یا دیرکننده‌ای) نیست و او زودشمار است.'),
(1749, 13, 42, 'و بی‌گمان، کسانی که پیش از آنان بودند نیرنگ (ظالمانه) زدند. پس همه‌ی نیرنگ‌ها(ی عادلانه) نزد خداست. آنچه را که هر کسی به دست می‌آورد می‌داند و به زودی کافران خواهند دانست، که فرجام آن سرا از آنِ کیست.'),
(1750, 13, 43, 'و کسانی که کافر شدند گویند: «تو فرستاده‌ای (از سوی خدا) نیستی.» بگو: «خدا کافی است که میان من و شما گواه باشد، و نیز کسی که نزد او علم کتاب (وحی) است.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الر. کتابی است که آن را سوی تو فرود آوردیم تا مردمان را - به اذن پروردگارشان - از تاریکی‌ها سوی روشنایی بیرون آوری؛ سوی راه راستای(آن) عزیز ارجمندِ ستوده.'),
(1752, 14, 2, 'خدایی که آنچه در آسمان‌ها و آنچه در زمین است از اوست. و وای بر کافران از عذابی سخت؛'),
(1753, 14, 3, 'کسانی که جویای دوستی این زندگی پستِ نزدیکتر بر زندگی آخرینند و (خود و دیگران را) از راه خدا باز می‌دارند، و آن را به کژی می‌جویند. اینانند که در (ژرفای) گمراهی دور و درازی هستند.'),
(1754, 14, 4, 'و ما هیچ پیامبری را جز به زبان (مفهوم برای) قومش نفرستادیم تا (حقایق را) برایشان بیان کند. پس خدا هر که را بخواهد از راه به در می‌برد، و هر که را بخواهد به راه می‌آورد. و اوست عزیز حکیم.'),
(1755, 14, 5, 'و ما همانا موسی را به‌راستی با نشانه‌های خود فرستادیم (و به او فرمودیم) که: «قوم خود را از تاریکی‌ها سوی روشنایی بیرون آور و روزهای (ویژه‌ی) خدا را به آنان یادآوری کن. بی‌چون در این (یادآوری) برای هر شکیبایی بسیار سپاسگزار، نشانه‌هایی است.»'),
(1756, 14, 6, 'و چون موسی به قوم خود گفت: «نعمت خدا را بر خودتان به یاد آورید، چون شما را از فرعونیان رهانید؛ حال آنکه با اجبار و به‌ناچار شما را عذابی بس نکوهیده می‌کردند، و پسرانتان را سر می‌بریدند و زنانتان را زنده و بی‌حیا وامی‌گذاشتند. در حالی که در این (امر) برای شما از پروردگارتان آزمایشی بزرگ است.»'),
(1757, 14, 7, 'و چون پروردگارتان اعلام کرد: «اگر به‌راستی سپاسگزاری کنید بی‌چون(جمعیت و نعمت) شما را به‌درستی افزون می‌کنم و اگر ناسپاسی نمایید، بی‌گمان(و) بی‌امان عذاب من بسی سخت است.»'),
(1758, 14, 8, 'و موسی گفت: «اگر شما و هر که در روی زمین است، همگان کافر شوید، به‌راستی، خدا بی‌نیاز ستوده (در کل جهان) است.»'),
(1759, 14, 9, 'آیا خبر مهم کسانی که پیش از شما بودند - (یعنی) قوم نوح و عاد و ثمود و آنانی که بعد از ایشان بودند (و) کسی جز خدا از آنان آگاهی ندارد - شما را نیامده است؟ فرستادگانش دلایل آشکار برایشان آوردند، پس آنان دست‌هایشان را (به نشانه‌ی اعتراض) بر دهان‌ها‌شان نهادند و گفتند: «ما به آنچه شما بدان مأموریت دارید همواره کفر ورزیدیم و بی‌گمان از آنچه ما را بدان می‌خوانید در (ژرفای) شکی مستند سخت غرقه‌ایم.»'),
(1760, 14, 10, 'پیامبرانشان گفتند: « مگر درباره‌ی خدا - پدیدآورنده‌ی آسمان‌ها و زمین - شکی است‌؟ او شما را دعوت می‌کند تا پاره‌ای از گناهانتان را برایتان بپوشاند، و سوی زمان معینی شما را واپس اندازد.» گفتند: «شما جز بشری مانند ما نیستید. می‌خواهید ما را از آنچه پدرانمان می‌پرستیده‌اند باز دارید. پس ما را سلطه‌ای آشکارگر بیاورید.»'),
(1761, 14, 11, 'پیامبرانشان برایشان گفتند: «ما جز بشری همانند شما نیستیم، ولی خدا بر هر یک از بندگانش که بخواهد منت می‌نهد. و ما را هیچ‌گاه نبوده است که جز به اذن خدا برای شما حجتی سلطه‌گر بیاوریم. و مؤمنان باید تنها بر خدا توکل کنند.»'),
(1762, 14, 12, '«و ما را چیست که بر خدا توکل نکنیم، حال آنکه به راستی ما را به راه‌های راهوارمان رهبری کرده است‌؟ و البته ما بر آزاری که به ما رساندید باید شکیبایی کنیم، و توکل‌کنندگان باید تنها بر خدا توکل کنند.»'),
(1763, 14, 13, 'و (تمامی) کسانی که (در تاریخ رسالت‌ها) کافر شدند، به (تمامی‌) پیامبرانشان گفتند: «شما را از سرزمین خودمان بی‌چون (و) بی‌گمان بیرون می‌کنیم، یا (اینکه) به‌راستی و یقین به کیش ما باز گردید.» پس پروردگارشان به آنان وحی کرد: «بی‌امان ستمگران را بس هلاک خواهیم کرد.»'),
(1764, 14, 14, '«و به‌راستی و درستی شما را پس از ایشان (به جایشان) در (این) سرزمین سکونت خواهیم داد. این برای کسی است که از پایگاه (ربانی‌) و شأن من بهراسد، و از تهدیدم بیم داشته باشد.»'),
(1765, 14, 15, 'و (پیامبران و کافران توأمان) از خدا گشایش خواستند و (سرانجام) هر زورگوی لجوجی نومید شد.'),
(1766, 14, 16, '(آن کس که) دوزخ از پی اوست و به او آبی مرگبار نوشانده می‌شود،'),
(1767, 14, 17, 'آن را جرعه جرعه می‌نوشد و نتواند آن را به آسانی و خوشی فرو برد و مرگ از هر جانبی سویش می‌آید - حال آنکه مرده نیست- و عذابی سنگین را به دنبال دارد.'),
(1768, 14, 18, 'مَثَل کسانی که به پروردگارشان کافر شدند؛ کردارهایشان به خاکستری می‌ماند که بادی تند در روزی طوفانی بر آن وزید. از آنچه به دست آورده‌اند هیچ (بهره‌ای) نمی‌توانند برد. این است همان گمراهی دور و دراز.'),
(1769, 14, 19, 'آیا ندیدی [:درنیافتی] که خدا به‌راستی آسمان‌ها و زمین را به حق آفرید؟ اگر بخواهد شما را می‌برد و خلق تازه‌ای (به جایتان) می‌آورد.'),
(1770, 14, 20, 'و این کار بزرگ بر خدا(ی بزرگ) دشوار نیست.'),
(1771, 14, 21, 'و همگان به پیشگاه خدای(شان) عیان شدند. پس ناتوانان به مستکبران گفتند: «ما بی‌گمان پیروان شما بوده‌ایم، پس آیا چیزی از عذاب خدا را از ما کفایت و جبران می‌کنید؟» گفتند: «اگر خدا ما را هدایت کرده بود، به‌راستی شما را هدایت می‌کردیم. چه بی‌تابی کنیم و چه تاب آوریم بر ما یکسان است. ما را راه هیچ‌گونه گریزی (از عذاب خدا) نیست.»'),
(1772, 14, 22, 'و هنگامی که کار از کار گذشت (و داوری صورت گرفت) شیطان (از دستگاه فرستنده‌ی شیطانیش) گفت: «به‌راستی خدا به شما وعده داد، وعده‌ای (به پاداشی) درست و پای برجا. و من به شما وعده(ی گزاف و نابجا) دادم. پس با شما (در آن) خلاف کردم و مرا بر شما هیچ سلطه‌ای نبوده است، جز اینکه شما را دعوت کردم، پس (بی‌درنگ‌) اجابتم نمودید. پس مرا ملامت نکنید و خود را ملامت کنید. من فریادرس شما نیستم و شما (هم) فریادرس من نیستید. من به آنچه پیش از این مرا (با خدا) شریک می‌دانستید بی‌چون کفر ورزیدم.» بی‌گمان برای ستمکاران عذابی پردرد است.'),
(1773, 14, 23, 'و کسانی که ایمان آورده و کارهای شایسته کردند، به باغ‌هایی در آورده شدند، که از زیر (درختان)شان نهرها روان است‌؛ حال آنکه به اذن پروردگارشان در آن جاودانه‌اند (و) درودشان در آنجا سلام است.'),
(1774, 14, 24, 'آیا ندیدی خدا چگونه مَثَلی زد: سخنی پاکیزه را مانند درختی پاکیزه، که ریشه‌اش استوار و شاخه‌اش در آسمان است‌؟'),
(1775, 14, 25, '(آن درخت‌) خوردنیش را هر دم به اذن پروردگارش می‌دهد. و خدا مَثَل‌ها(ی شایسته) را برای مردم می‌زند، شاید آنان به خوبی یاد کنند.'),
(1776, 14, 26, 'و مَثَل سخنی ناپاکیزه هم‌چون درختی ناپاکیزه است (که) از روی زمین کنده شده (و) هیچ پایداری ندارد.'),
(1777, 14, 27, 'خدا کسانی را که ایمان آوردند، در زندگی دنیا و در آخرت با سخن استوار (توحید) پایدار می‌گرداند. و خدا ستمگران را بی‌راه می‌گذارد. و خدا هر کاری بخواهد انجام می‌دهد.'),
(1778, 14, 28, 'آیا سوی کسانی که (شکر) نعمت خدا را به کفر تبدیل کردند و قوم خود را به سرای هلاکت فرو آوردند، ننگریستی‌؟'),
(1779, 14, 29, 'جهنم را گیرانه‌اند و (آنجا) چه بد قرارگاهی است.'),
(1780, 14, 30, 'و برای خدا همانندهایی قرار دادند، تا (خود و دیگران را) از راه او گمراه کنند. بگو: «(از بهره‌های حیوانی) برخوردار شوید. پس (از آن) بی‌چون بازگشت شما سوی آتش است.»'),
(1781, 14, 31, 'به [آن] بندگانم که ایمان آورده‌اند بگو (که‌) نماز را بر پا دارند و از آنچه به ایشان روزی دادیم، پنهان و آشکارا انفاق کنند، پیش از آنکه روزی فرا رسد که در آن نه داد و ستدی باشد و نه دوستی‌ها و همبستگی‌هایی.'),
(1782, 14, 32, 'خدا کسی است (که) آسمان‌ها و زمین را آفرید. و از آسمان آبی فرو فرستاد؛ پس با آن (بخشی‌) از ثمرات را برایتان رزقی برون آورد. و کشتی‌ها را برایتان رام گردانید، تا به فرمان او در دریا روان شوند. و نهرها را برایتان مسخّر کرد.'),
(1783, 14, 33, 'و خورشید و ماه را پیوسته برای شما رام گردانید، و شب و روز را (نیز) مسخّر شما ساخت.'),
(1784, 14, 34, 'و برخی از همه‌ی آنچه از او خواستید به شما داد و اگر نعمت خدا را شماره کنید، نمی‌توانید آن را به شمار آورید. بی‌گمان انسان، بی‌امان ستم‌پیشه‌ای بس کفران‌کننده است.'),
(1785, 14, 35, 'و چون ابراهیم گفت: «پروردگارم! این شهر را ایمن گردان و مرا و فرزندانم را از پرستیدن بتان دور بدار.»'),
(1786, 14, 36, '«پروردگارم! آنان بسیاری از مردمان را بی‌چون گمراه کردند. پس هر کس از من پیروی کند بی‌گمان او از من است، و هر که مرا نافرمانی کند همواره تو بسی پوشنده‌ی رحمتگر بر ویژگانی.»'),
(1787, 14, 37, '«پروردگارمان! من برخی از ذریه‌ام را در گودالی تهی از کشت سکونت دادم، نزد خانه‌ی محترمت - پروردگارمان! - برای آنکه نماز را به پا دارند. پس دل‌هایی از مردمان را مقرر فرمای که سوی آنان روان گردند و آنان را از همه‌ی ثمرات (مورد نیازشان) روزی ده، شاید سپاسگزاری کنند.»'),
(1788, 14, 38, '«پروردگارمان! بی‌گمان تو آنچه را که پنهان می‌داریم و آنچه را که آشکار می‌سازیم می‌دانی.» و هیچ چیزی در زمین و نه در آسمان بر خدا پوشیده نیست.'),
(1789, 14, 39, '«تمامی سپاس‌ها خدای راست که با وجود سالخوردگی(ام) اسماعیل و اسحاق را به من بخشود. به‌راستی پروردگارم همواره بسی شنونده‌ی درخواست است.»'),
(1790, 14, 40, '«پروردگارم! مرا بر پا دارنده‌ی نماز قرار ده و (نیز) برخی از فرزندانم را. پروردگارمان! و درخواست مرا بپذیر.»'),
(1791, 14, 41, '«پروردگارمان! روزی که حساب بر پا می‌شود، برای من و پدر و مادرم و برای مؤمنان بپوشان.»'),
(1792, 14, 42, 'و خدا را از آنچه ستمکاران می‌کنند هرگز غافل مپندار. جز این نیست که (کیفر) آنان را برای روزی واپس می‌اندازد که چشم‌ها در آن (روز) خیره می‌شوند.'),
(1793, 14, 43, 'بهت‌زدگان و سرافکندگان، چشمانشان راست و خیره، سویشان برگشتی ندارد و (از وحشت) دل‌هایشان بی‌دل و پریشان است.'),
(1794, 14, 44, 'و مردمان را از روزی که عذاب بر آنان می‌آید هشدار ده‌. پس آنان که ستم کرده‌اند گویند: «پروردگارمان! ما را تا اندکی مهلت بخش تا دعوتت را پاسخ گوییم و از فرستادگانت پیروی کنیم.» (به آنان گفته می‌شود:) «ومگر شما نبودید (که) پیش از این سوگند می‌خوردید که شما را فنایی هرگز نیست‌؟»'),
(1795, 14, 45, '«و در جایگاه‌های کسانی که بر خود ستم روا داشتند جای گرفتید، [:در آنها آرمیدید] و برایتان به خوبی آشکار شد (که) با آنان چگونه معامله کردیم و برایتان مَثَل‌ها زدیم.»'),
(1796, 14, 46, 'و بی‌گمان نیرنگشان را به کار بردند و نیرنگشان تنها نزد خداست‌؛ هر چند از مکرشان کوه‌ها از جای برکنده می‌شده است.'),
(1797, 14, 47, 'پس هرگز مپندار که خدا وعده‌ی خود را به پیامبرانش خلاف‌کننده است. همواره خدا عزیز و صاحب انتقام است.'),
(1798, 14, 48, 'روزی که زمین به غیر (این) زمین و آسمان‌ها به غیر (آن) آسمان‌ها مبدل گردند و (مکلفان) برای خدای یگانه‌ی قهار آشکار شوند؛'),
(1799, 14, 49, 'و گناهکاران را در آن روز چنان بینی که قرین هم در زنجیرها به سختی بسته شده‌اند.'),
(1800, 14, 50, 'تن‌پوش‌هایشان از ماده‌ای آتشین است و چهره‌هایشان را آتش می‌پوشاند.'),
(1801, 14, 51, 'برای اینکه خدا به هر کس هر چه را به دست آورده است، پاداش دهد. به‌راستی خدا زودشمار است.'),
(1802, 14, 52, 'این (قرآن) برای مردمان بلاغ و رسایی‌ای (وحیانی) است و تا بدان هشدار داده شوند و بدانند که خدا بی‌گمان معبودی یگانه است و تا اندیشمندان پندی بزرگ گیرند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الر. آنهاست (تمامی‌) آیات کتاب (وحیانی) و قرآنی روشنگر.'),
(1804, 15, 2, 'چه بسا کسانی که کافر شدند آرزو کنند کاش مسلمان بودند.'),
(1805, 15, 3, 'واگذارشان (تا) بخورند و برخوردار شوند و آرزو(ی شیطانی) سرگرمشان کند. پس در آینده‌ای دور خواهند دانست.'),
(1806, 15, 4, 'و هیچ گروهی را هلاک نکردیم مگر اینکه برایشان (سرنوشت و) نوشته‌ای معلوم است.'),
(1807, 15, 5, 'هیچ امتی ازسرنوشت خویش نه پیشی می‌گیرد ونه (از آن) تأخیر می‌جوید.'),
(1808, 15, 6, 'و گفتند: «ای کسی که قرآن بر او نازل شده است! همانا تو بی‌گمان دیوانه‌ای!»'),
(1809, 15, 7, '«اگر از راستان بوده‌ای چرا فرشته‌ها را پیش ما نمی‌آوری‌؟»'),
(1810, 15, 8, 'فرشتگان را جز به حق فرو نفرستیم، و در آن هنگام (و هنگامه) مهلت‌یافته نبوده‌اند.'),
(1811, 15, 9, 'ما بی‌گمان، (همین) ما (این) یادواره [:قرآن] را به تدریج نازل کردیم و بی‌چون ما برای آن -به‌راستی- نگهبانانیم.'),
(1812, 15, 10, 'و به‌راستی و درستی، ما پیش از تو (نیز پیامبرانی) فرستادیم.'),
(1813, 15, 11, 'و هیچ پیامبری ایشان را نیاید جز آنکه او را به مسخره می‌گرفته‌اند.'),
(1814, 15, 12, 'این گونه آن (استهزا) را در دل بزهکاران بسی نفوذ می‌دهیم (که خلاصی از آن ندارند).'),
(1815, 15, 13, 'بدان ایمان نمی‌آورند، و راه و رسم پیشینیان پیوسته چنان بوده است.'),
(1816, 15, 14, 'و اگر دری از آسمان بر آنان برگشاییم پس همواره در آن بالا روند.'),
(1817, 15, 15, 'بی‌گمان گویند: «در حقیقت، ما چشم‌بندی شده‌ایم، بلکه ما گروهی افسون‌شده‌ایم!»'),
(1818, 15, 16, 'و به‌راستی بی‌گمان ما در آسمان کاخ‌هایی (وحیانی) قرار دادیم و آنها را برای تماشاگران (زمین و آسمان) آراستیم.'),
(1819, 15, 17, 'و (هم) آن‌ها را از هر شیطان رانده‌شده‌ای نگه داشتیم.'),
(1820, 15, 18, 'مگر کسی که دزدانه گوش فرا دهد، پس نیزه‌ی آتشینِ آشکارگری او را دنبال کند.'),
(1821, 15, 19, 'و زمین را گستراندیم، و در آن کوه‌هایی پابرجا (و بلند بالا) فرو افکندیم و از هر چیز سنجیده شده‌ای، به اندازه در آن رویاندیم.'),
(1822, 15, 20, 'و برای شما و هر کس که شما روزی‌دهنده‌اش نیستید، در آن وسایل زندگی قرار دادیم.'),
(1823, 15, 21, 'و هیچ چیزی نیست مگر آنکه گنجینه‌های آن نزد ماست، و ما آن را جز به اندازه‌ای معلوم فرو نمی‌فرستیم.'),
(1824, 15, 22, 'و بادها را باردارکننده فرستادیم. پس آبی از آسمان نازل کردیم، پس شما را بدان سیراب نمودیم، حال آنکه شما نگهدارنده‌ی آن نیستید.'),
(1825, 15, 23, 'و بی‌گمان، این ماییم که زنده می‌کنیم و (این ماییم که) می‌میرانیم، و (هم) ماییم که به‌راستی وارثانیم.'),
(1826, 15, 24, 'و ما همانا بی‌چون خواستاران پیشبردتان را شناختیم. و همانا به‌راستی خواستاران تأخیرتان را (نیز) شناختیم.'),
(1827, 15, 25, 'و بی‌گمان پروردگار تو است که آنان را محشور خواهد کرد. به‌راستی او حکیمی بس داناست.'),
(1828, 15, 26, 'و به‌درستی انسان را از گلی خشک، از گلی سیاه و تغییریافته و دیرپا آفریدیم.'),
(1829, 15, 27, 'و پیش از آن، جنیان را از آتشی مسموم [:زهرآگین] آفریدیم.'),
(1830, 15, 28, 'و چون پروردگارت به فرشتگان گفت: «من همانا بشری را از گلی خشک (و) سیاه [:لجن] و تغییریافته و دیرپا آفریننده‌ام.»'),
(1831, 15, 29, '«پس هنگامی که آن را درست کردم، و از روح ممتاز (آفریده‌ی) خود در آن دمیدم، برای او به سجده درافتید.»'),
(1832, 15, 30, 'پس فرشتگان همگان به سجده فرو افتادند،'),
(1833, 15, 31, 'جز ابلیس (که) خودداری کرد که با سجده‌کنندگان باشد.'),
(1834, 15, 32, 'فرمود: «ابلیس! تو را چه شده که با سجده‌کنندگان نیستی‌؟»'),
(1835, 15, 33, 'گفت: «من (آن کسی) نبوده‌ام که برای بشری که او را از گلی خشک (و) سیاه [:لجن] و تغییریافته و دیرپا آفریده‌ای، سجده کنم.»'),
(1836, 15, 34, 'فرمود: «پس از این (باغ) برون شو. بی‌چون سنگ‌باران (و) رانده‌شده‌ای.»'),
(1837, 15, 35, '«و بی‌گمان تا روز(بُروز) طاعت، لعنت (خدا و خدائیان)بر تواست .»'),
(1838, 15, 36, 'گفت: «پروردگارم! پس مرا تا روزی که برانگیخته خواهند شد مهلت ده.»'),
(1839, 15, 37, 'فرمود: «پس بی‌چون تو از مهلت‌یافتگانی،»'),
(1840, 15, 38, '«تا روز وقت معلوم .»'),
(1841, 15, 39, 'گفت: «پروردگارم! به این که مرا گمراه ساختی، من (هم) بی‌چون و بی‌گمان در زمین (همه چیز را) برایشان می‌آرایم، و همانا همواره گمراه(شان) خواهم ساخت.»'),
(1842, 15, 40, '«مگر خالص‌شدگان (ربانی) از بندگانت را.»'),
(1843, 15, 41, 'فرمود: «این راهی است راست برعهده‌ی من.»'),
(1844, 15, 42, '«بی‌گمان، تو را بر بندگان من هیچ سلطه‌ای نیست، مگر کسانی از گمراهان که تو را پیروی کردند،»'),
(1845, 15, 43, '«و همواره وعده‌گاه همگی آنان بی‌چون دوزخ است.»'),
(1846, 15, 44, '«برای آن هفت در است و از هر دری بخشی معین از آنِ آنان است.»'),
(1847, 15, 45, 'همواره، پرهیزگاران در باغ‌هایی سر در هم و چشمه‌سارانی‌می‌باشند.'),
(1848, 15, 46, '(به آنان گویند:) «با سلامت و ایمنی داخل آنها شوید.»'),
(1849, 15, 47, 'و آنچه کینه (و شائبه‌های نفسانی) در سینه‌هاشان است برکندیم، (و) برادرانه بر تخت‌هایی رویاروی یکدیگرند.'),
(1850, 15, 48, 'نه رنجی در آنها به آنان در رسد و نه از آنجا بیرون شوندگانند.'),
(1851, 15, 49, 'بندگانم را خبری مهم ده که همانا من خود بس پوشنده‌ای رحمتگر بر ویژگانم.'),
(1852, 15, 50, 'و اینکه بی‌گمان عذاب من، آن عذاب دردناک است.'),
(1853, 15, 51, 'و از میهمانان ابراهیم به آنان خبری مهم بده.'),
(1854, 15, 52, 'چون بر او وارد شدند پس (بدو) سلام گفتند. (ابراهیم) گفت: «ما از شما بیمناکیم.»'),
(1855, 15, 53, 'گفتند: «مترس، که ما تو را به پسری بس دانا مژده می‌دهیم.»'),
(1856, 15, 54, 'گفت: «آیا بر حال سالخورد‌گی‌ا‌م بشارت دادید؟ پس به چه مرا بشارت می‌دهید؟»'),
(1857, 15, 55, 'گفتند: «ما تو را به حق بشارت دادیم. پس از نومیدان مباش.»'),
(1858, 15, 56, 'گفت: «و چه کسانی - جز گمراهان‌- از رحمت پروردگارشان نومید می‌شوند؟»'),
(1859, 15, 57, 'گفت: «ای فرستادگان، پس جریان مهم‌تان چیست‌؟»'),
(1860, 15, 58, 'گفتند: «ما بی‌گمان سوی گروهی مجرمان فرستاده شده‌ایم.»'),
(1861, 15, 59, '«مگر خانواده‌ی لوط (که) ما به‌راستی همگی آنان را بی‌چون نجات‌دهنده‌ایم.»'),
(1862, 15, 60, '«جز زنش را که مقدر کردیم او همانا از بازماندگان (در عذاب) است.»'),
(1863, 15, 61, 'پس هنگامی که (آن) فرستادگان نزد خاندان لوط آمدند،'),
(1864, 15, 62, '(لوط) گفت: «همانا شما گروهی ناشناخته‌اید.»'),
(1865, 15, 63, 'گفتند: « (نه،) بلکه برای تو چیزی آورده‌ایم که در آن تردید می‌کرده‌اند.»'),
(1866, 15, 64, '«ما (در حقیقت) با حق نزدت آمده‌ایم و ما بی‌گمان راستانیم.»'),
(1867, 15, 65, '«پس پاسی از شب خانواده‌ات را حرکت ده و (خودت) درپی‌شان برو و هیچ یک از شما نباید به عقب توجه کند، و هر جا به شما دستور داده می‌شود به سرعت روان شوید.»'),
(1868, 15, 66, 'و برایش آن جریان را آگاهی حتمی دادیم که ریشه‌ی آن گروه (و آنچه را تدبیر کرده‌اند) بی‌گمان صبحگاهان بریده شده است.'),
(1869, 15, 67, 'و مردم شهردرحال نوید خواستن، (شادی‌کنان) روی آوردند.'),
(1870, 15, 68, '(لوط) گفت: «اینان مهمانان منند. پس مرا رسوا مکنید.»'),
(1871, 15, 69, '«و از خدا پروا بدارید و مرا خوار نسازید.»'),
(1872, 15, 70, 'گفتند: «آیا و تو را از (دخالت در کار این) مردمان منع نکردیم‌؟»'),
(1873, 15, 71, 'گفت: «اگر انجام‌دهنده‌ی کاری بوده‌اید اینان دختران منند (،با آنان ازدواج کنید).»'),
(1874, 15, 72, 'به‌راستی به جانت سوگند که بی‌گمان آنان در ژرفای مستی خود، سرگردان و حیرت‌زدگانند.'),
(1875, 15, 73, 'پس صبحگاهان، فریاد (مرگبار) آنان را فرا گرفت.'),
(1876, 15, 74, 'پس بلندای آن (شهر) را پست (و آن را زیروزبر) کردیم و بر آنان سنگی از سنگ گل باراندیم.'),
(1877, 15, 75, 'بی‌گمان در این (کیفر) برای پذیرندگان (حق) نشانه‌هایی است.'),
(1878, 15, 76, 'و به‌راستی (آثار) آن (شهر هنوز) بر سر راهی به‌درستی پای‌برجاست.'),
(1879, 15, 77, 'بی‌گمان در این (جریان) همانا برای مؤمنان نشانه‌ای است.'),
(1880, 15, 78, 'و بی‌چون اهل «ایکه‌» بی‌گمان از ظالمان بوده‌اند.'),
(1881, 15, 79, 'پس، از آنان انتقام گرفتیم، و آن دو همانا بر (فراز) امام و پیشوایی (نمایان) آشکارگرند.'),
(1882, 15, 80, 'و همانا به تحقیق اهل «حجر» پیامبران را تکذیب کردند.'),
(1883, 15, 81, 'و تمام آیات [جسمانی و ...] خود را به آنان دادیم. پس از آنها رویگردان بوده‌اند.'),
(1884, 15, 82, 'و (برای خود) از [جای جای] کوه‌ها خانه‌هایی در حال آرامش می‌تراشیده‌اند.'),
(1885, 15, 83, 'پس صبحگاهانشان، فریاد (مرگبار) آنان را فرا گرفت.'),
(1886, 15, 84, 'پس آنچه به دست می‌آورده‌اند از گزندشان بی‌نیازشان نکرد.'),
(1887, 15, 85, 'و ما آسمان‌ها و زمین و آنچه را که میان آن دو است جز به حقیقت نیافریده‌ایم. و بی‌چون ساعت: (قیامت) بی‌گمان آمدنی است. پس (از آنان) روی گردان، رویگرداندنی زیبا.'),
(1888, 15, 86, 'بی‌گمان پروردگارت، (هم)او بس آفریننده‌ی بسیار داناست.'),
(1889, 15, 87, 'و به‌راستی و درستی، تو را به تأکید، هفت (آیه) از مثانی [:سوره‌ی فاتحه] و (نیز) قرآن بزرگ را دادیم.'),
(1890, 15, 88, 'فراسوی آنچه (از) همسرانی از آنان [:کافران] را بدان برخوردار ساخته‌ایم هرگز چشمانت را مدوز و برایشان اندوه مخور. و بال خویش را برای مؤمنان فروگستر.'),
(1891, 15, 89, 'و بگو: «بی‌گمان من، (همین) من ترساننده‌ی روشنگرم.»'),
(1892, 15, 90, 'چنان که (عذاب را) بر تقسیم‌کنندگان ناهنجار (قرآن) نازل کردیم؛'),
(1893, 15, 91, 'کسانی که قرآن را پاره پاره کردند.'),
(1894, 15, 92, 'پس سوگند به پروردگارت از همه‌ی آنان بی‌گمان بازخواست می‌کنیم؛'),
(1895, 15, 93, 'از آنچه انجام می‌داده‌اند.'),
(1896, 15, 94, 'پس به آنچه (بدان) مأمور می‌شوی روی آور و از مشرکان روی بگردان.'),
(1897, 15, 95, 'بی‌چون ما (شرّ) مسخره‌کنندگان را از تو کفایت کردیم [:باز داشتیم].'),
(1898, 15, 96, 'کسانی که با خدا معبودی دیگر می‌نهند. پس در آینده‌ای دور (حقیقت را ناخواسته و نادانسته) خواهند دانست.'),
(1899, 15, 97, 'و به‌راستی همواره می‌دانیم که سینه‌ی تو از آنچه می‌گویند بسی تنگ می‌شود.'),
(1900, 15, 98, 'پس با ستایش پروردگارت (او را) تقدیس کن، و از سجده‌کنندگان باش.'),
(1901, 15, 99, 'و پروردگارت را پرستش کن تا تو را یقین (پیاپی و بی‌پایان) فرا رسد.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'امر خدا در رسید، پس در آن شتاب مکنید. او منزه و فراتر است از آنچه (با وی) شریک می‌سازند.'),
(1903, 16, 2, 'فرشتگان را - با «روح‌» (صادر) از فرمان خود - بر هر کس از بندگانش که بخواهد (با پیامی) فرو می‌فرستد که: «بیم دهید که همانا معبودی جز من نیست، پس از من پروا کنید.»'),
(1904, 16, 3, 'آسمان‌ها و زمین را به حق (ربانی) آفرید. (او) فراتر است از آنچه (با وی) شریک می‌دارند.'),
(1905, 16, 4, 'انسان را از نطفه‌ای آفرید. پس آن‌گاه او ستیزه‌جویی آشکارگر است.'),
(1906, 16, 5, 'و دام‌ها را برایتان آفرید (که‌) در آنها برای شما (وسیله‌ی) گرمی و سودهایی است و از آنها می‌خورید؛'),
(1907, 16, 6, 'و در آنها برای شما زیبایی‌ای است؛ آن‌گاه که (آنها را) از چراگاه برمی‌گردانید و هنگامی که آزاد (شان) می‌کنید.'),
(1908, 16, 7, 'و بارهایتان را به شهری می‌برند که جز با مشقت جا‌نانه بدان‌ها نمی‌توانستید برسید. بی‌گمان پروردگارتان به‌راستی مهربانِ رحمتگر بر ویژگان است.'),
(1909, 16, 8, 'و اسبان و استران و خران را (آفرید) تا بر آنها سوار شوید و (برایتان) زینتی است. و آنچه را نمی‌دانید می‌آفریند.'),
(1910, 16, 9, 'و نمودن راه راست [تنها] بر عهده‌ی خداست. و برخی از راه‌ها بیراهه است. و اگر (خدا) بخواهد بی‌گمان همگی‌تان را هدایت می‌کند.'),
(1911, 16, 10, 'اوست کسی که از آسمان، آبی فرود آورد که برایتان از آن آشامیدنی و روییدنی(هایی) است (که رمه‌های خود را) در آن می‌چرانید.'),
(1912, 16, 11, 'به وسیله‌ی آن، کشت و زیتون و درختان خرما و انگور و از هر (گونه) محصولات (دیگر) برایتان می‌رویاند. همواره در اینها برای گروهی که اندیشه می‌کنند نشانه‌ای است.'),
(1913, 16, 12, 'و شب و روز و خورشید و ماه را برایتان رام گردانید، و ستارگان به فرمانش مسخرند. بی‌گمان در این (کارها) برای مردمی که خردورزی می‌کنند به‌راستی نشانه‌هایی است.'),
(1914, 16, 13, 'و (همچنین) آنچه را در زمین به رنگ‌های گوناگون برای شما پدید آورد [:مسخر شما ساخت]. بی‌گمان، در این (کارها) برای مردمی که (حقایقی را) با کوشش و کاوش به یاد می‌آورند نشانه‌ای است.'),
(1915, 16, 14, 'و اوست کسی که دریا را (برایتان) مسخر گردانید تا از آن گوشت تازه بخورید؛ و پیرایه‌ای که آن را می‌پوشید، از آن با کوشش بیرون آورید. و کشتی‌ها را در آن، شکافنده‌(‌ی آب) می‌بینی. و تا از فضل او بجویید و شاید شما شکر گزارید.'),
(1916, 16, 15, 'و در زمین کوه‌هایی استوار و پایدار افکند، تا (زمین متحرک)، شما را پرتاب نکند و رودها و راه‌هایی را (پدید آورد) تا شاید شما (به مقاصدتان) راه یابید.'),
(1917, 16, 16, 'و نشانه‌هایی (دیگر را نیز) و آنان به وسیله‌ی ستارگان درخشان راه‌یابی می‌کنند.'),
(1918, 16, 17, 'پس آیا کسی که می‌آفریند چون کسی است که نمی‌آفریند؟ آیا پس (از این نشانه‌ها حقایقی را) به یاد نمی‌آورید؟'),
(1919, 16, 18, 'و اگر نعمت(های) خدا را بشمارید آنها را به شماره نیاورید. همانا خدا به‌راستی بس پوشنده‌ای رحمتگر بر ویژگان است.'),
(1920, 16, 19, 'و خدا آنچه پنهان می‌سازید و آنچه را که آشکار می‌دارید می‌داند.'),
(1921, 16, 20, 'و کسانی را که دون خدا را می‌خوانند (اینان) چیزی نمی‌آفرینند و خود آفریده می‌شوند.'),
(1922, 16, 21, 'مردگانی‌اند نه زندگان، و درک نمی‌کنند کی برانگیخته می‌شوند.'),
(1923, 16, 22, 'معبود شما معبودی است یگانه. پس کسانی که به آخرت ایمان نمی‌آورند دل‌هایشان انکارکننده(ی حق) است و اینان کبرجویانند.'),
(1924, 16, 23, 'ناگزیر (و) بی‌گمان خدا آنچه را پنهان می‌دارند و (یا) آشکار می‌سازند می‌داند. به‌راستی او گردنکشان را دوست نمی‌دارد.'),
(1925, 16, 24, 'و هنگامی که به آنان گفته شود: «پروردگارتان چه چیز نازل کرده است‌؟» گویند: «افسانه‌های پیشینیان را.»'),
(1926, 16, 25, 'تا (آنکه) روز قیامت بار گناهان خود را کاملاً بردارند. و (نیز بخشی) از بار گناهان کسانی را که -بدون دانش (و بینش)- آنان را گمراه می‌کنند. هان! چه بد باری را می‌کشند.'),
(1927, 16, 26, 'پیش از آنان بی‌گمان کسانی مکر کردند. پس خدا بنیان آنان را از ریشه‌ها برکند. پس از بالای سرشان سقف بر آنان فرو ریخت. و در حالی که دقت (و بررسی) نمی‌کردند عذاب به سراغشان آمد.'),
(1928, 16, 27, 'سپس روز قیامت رسواشان می‌کند و می‌گوید: «کجایند شریکان من، کسانی که در(باره‌ی) آنان (با پیامبران) جدایی (و مخالفت) می‌داشته‌اید؟»کسانی که به آنان علم داده شده گفتند: «در حقیقت امروز رسوایی و خواری بر (سر و سامان) کافران است.»'),
(1929, 16, 28, 'کسانی که فرشتگان جان‌هاشان را بر می‌گیرند، حال آنکه که به خودشان ستمکار بودند، پس سر تسلیم فرود آوردند (و گویند:) «اما هیچ کار بدی نمی‌کرده‌ایم‌.» آری خدا به آنچه می‌کرده‌اید همواره بسی داناست.'),
(1930, 16, 29, 'پس از درب‌های دوزخ وارد شوید حال آنکه در آن‌ها ماندنی هستید. پس به‌راستی چه بد است جایگاه متکبران.'),
(1931, 16, 30, 'و به کسانی که تقوا پیشه کردند گفته شد: «پروردگارتان چه نازل کرد؟» گفتند: «خوبی‌ای.» برای کسانی که در این دنیا نیکی‌ای کردند (زندگی) نیکویی است و بی‌گمان سرای آخرت بهتر است. و به‌راستی چه نیکوست سرای (پایانی) پرهیزگاران،'),
(1932, 16, 31, 'باغ‌های جاودانه (که) در آنها داخل می‌شوند، حال آنکه رودها از زیر (درختان)شان روانند. در آنجا هر چه بخواهند برایشان (فراهم) است. خدا پرهیزگاران را این‌گونه پاداش می‌دهد.'),
(1933, 16, 32, '(همان) کسانی که فرشتگان جان‌هاشان را بدون کاستی - در حالی که پاکیزه‌اند- می‌گیرند (و) می‌گویند: «درودی بر شما! به (پاداش) آنچه انجام می‌داده‌اید به بهشت در آیید.»'),
(1934, 16, 33, 'آیا (کافران) جز اینکه فرشتگان سویشان آیند، یا فرمان پروردگارت (بر عذابشان) در رسد، انتظاری دارند؟ کسانی که پیش از آنان بودند (نیز) این‌گونه (رفتار) کردند، و خدا به ایشان ستم نکرد، بلکه آنان به خودهاشان ستم می‌کرده‌اند.'),
(1935, 16, 34, 'پس کارهای بدشان به آنان در رسید و آنچه مسخره‌اش می‌کرده‌اند آنان را فرا (و فرو) گرفت.'),
(1936, 16, 35, 'و کسانی که مشرک شدند گفتند: «اگر خدا می‌خواست - نه ما و نه پدرانمان - هیچ چیزی را غیر از او نمی‌پرستیدیم، و بدون (حکم) او چیزی را حرام نمی‌شمردیم.» پیشینیانشان (نیز) چنان رفتار کردند. پس آیا جز ابلاغ آشکارگر بر پیامبران (وظیفه‌ای) است‌؟'),
(1937, 16, 36, 'و بی‌چون و بی‌گمان در میان هر امتی فرستاده‌ای برانگیختیم که:«خدا را بپرستید، و از طغیانگران بپرهیزید.» پس برخی از ایشان را خدا هدایت کرد، و برخی از ایشان کسی است که گمراهی بر او به حق ثابت شد. پس در زمین بگردید پس ببینید فرجام تکذیب‌کنندگان چگونه بوده است.'),
(1938, 16, 37, 'اگر بر هدایتشان حرص می‌ورزی، پس بی‌گمان خدا کسی را که فرو می‌گذارد هدایت نمی‌کند، و برایشان هیچ یاری‌کنندگانی نیستند.'),
(1939, 16, 38, 'و با پربارترین سوگندهایشان به خدا سوگند یاد کردند که «خدا کسی را که می‌میرد بر نخواهد انگیخت.» آری، این وعده(ی انگیختن) بر او حقی است (ربانی)، ولی بیشتر مردم نمی‌دانند.'),
(1940, 16, 39, 'تا (خدا) آنچه را در آن اختلاف می‌کنند، برایشان توضیح دهد و تا کسانی که کافر شدند، بدانند که آنان بی‌گمان دروغگویان بوده‌اند.'),
(1941, 16, 40, 'ما وقتی چیزی را اراده کنیم تنها بدان می‌گوییم: «شو»، پس (بی‌درنگ) می‌شود.'),
(1942, 16, 41, 'و کسانی که پس از ستمدیدگی‌شان در (راه) خدا هجرت کردند، همواره در این دنیا جای نیکویی به آنان می‌دهیم، و اگر می‌دانسته‌اند پاداش آخرت بس بزرگ‌تر است؛'),
(1943, 16, 42, 'کسانی که صبر کردند و تنها بر پروردگارشان توکل می‌کنند.'),
(1944, 16, 43, 'و(اما) پیش از تو جز مردانی که بدیشان وحی می‌کنیم گسیل نداشتیم. پس اگر دانسته‌اید، از یادآورندگان [:پژوهندگان کتاب‌های آسمانی] جویا شوید.'),
(1945, 16, 44, '(و این جویایی) با نشانه‌های آشکار و نوشته‌ها(ی وحیانی است ) ، و این ذکر [:قرآن] را فرا سویت فرود آوردیم تا برای مردمان آنچه را سویشان نازل شده است تبیین کنی. و شاید آنان بیندیشند.'),
(1946, 16, 45, 'پس آیا کسانی که بدی‌ها را دسیسه کردند ایمن شدند از اینکه خدا به سبب نافرمانیشان) زمین را بشکافد، یا از جایی که حدس نمی‌زنند عذاب برایشان بیاید؟'),
(1947, 16, 46, 'یا در حال جابه‌جا شدنشان (گریبان) آنان را بگیرد. پس آنان ناتوان کنند‌گان (خدا) نیستند.'),
(1948, 16, 47, 'یا آنان را در حالی که بس وحشت‌زده‌اند فرو گیرد؟ پس همانا پروردگارتان مهربانِ رحمتگر بر ویژگان است.'),
(1949, 16, 48, 'آیا و فراسوی چیزهایی که خدا آفریده است، ننگریسته‌اند که (چگونه) سایه‌هایشان از (جانب) راست و (از جوانب) چپ‌ها(شان) خوار و بی‌مقدار برای خدا سجده‌کنانند ؟'),
(1950, 16, 49, 'و آنچه در آسمان‌ها و آنچه در زمین از جنبندگان و فرشتگان است، برای خدا سجده می‌کنند حال آنکه تکبر نمی‌ورزند.'),
(1951, 16, 50, 'از پروردگارشان - که (به گونه‌ای مطلق) فوق آنهاست- می‌هراسند و آنچه را مأمورند انجام می‌دهند.'),
(1952, 16, 51, 'و خدا فرمود: «دو معبود (برای خود) بر مگیرید» - تنها او خدایی یگانه است - «پس تنها از من، (همین) من بهراسید.»'),
(1953, 16, 52, 'و آنچه در آسمان‌ها و زمین است از اوست، و طاعت پایدار خالصانه (نیز) از آنِ اوست. پس آیا از غیر خدا پروا می‌دارید؟'),
(1954, 16, 53, 'و هر نعمتی که دارید از خداست. سپس چون آسیبی شما را در رسد سوی او زاری‌کنان همچون وحشیان روی می‌آورید.'),
(1955, 16, 54, 'سپس هنگامی که آن زیان را از شما برگرفت، ناگهان گروهی از شما به پروردگارشان شرک می‌ورزند.'),
(1956, 16, 55, '(بگذار) تا آنچه را به ایشان داده‌ایم کفر یا کفران کنند: «اکنون(از نعمت‌هایم) برخوردار شوید، پس در آینده‌ای دور خواهید دانست.»'),
(1957, 16, 56, 'و از آنچه روزیشان دادیم نصیبی برای آن چه (از بتان و طاغوتان) نمی‌دانند می‌نهند. به خدا سوگند بی‌چون از آنچه به دروغ بر می‌بافتید، بی‌امان بازخواست می‌شوید.'),
(1958, 16, 57, 'و برای خدا دخترانی بر می‌گیرند. منزه است او. حال آنکه برای خودشان است آنچه دلخواهشان است.'),
(1959, 16, 58, 'و هنگامی که یکی از آنان را به دختر مژده آورند، چهره‌اش سیاه می‌گردد در حالی که خشم (و اندوه) خود را فرو برنده است.'),
(1960, 16, 59, 'از بدی آنچه بدو بشارت داده شده، از قبیله(ی خود) بسی پنهان می‌شود. آیا او را با خواری نگه دارد یا در خاک پنهانش کند؟ وه چه بد داوری می‌کنند.'),
(1961, 16, 60, 'نماد زشت برای کسانی است که به آخرت ایمان ندارند. و برترین نماینده از آن خداست و او است عزیز حکیم.'),
(1962, 16, 61, 'و اگر خدا مردم را به (سزای) ستمشان بازخواست کند، هیچ جنبنده‌ای بر روی زمین باقی نگذارد ولی (کیفر) آنان را تا وقتی معین واپس نهد. پس هنگامی که اجلشان در رسد، ساعتی [:زمانی] نه (می‌توانند از آن) تأخیر بخواهند و نه (می‌توانند از آن) پیشی جویند‌.'),
(1963, 16, 62, 'و چیزی را که هرگز خوش نمی‌دارند، برای خدا می‌نهند، و زبانشان دروغ‌پردازی می‌کند که :بی‌گمان نیکوترین(زندگی) از آن ایشان است. بی‌گمان آتش برای آنان است، و همانان به‌راستی از حد در گذشتگانند.'),
(1964, 16, 63, 'به خدا سوگند، به‌راستی سوی امت‌های پیش از تو (رسولانی) به‌درستی فرستادیم. پس شیطان اعمالشان را برایشان آراست. پس امروز (هم) سرپرستشان (هم) اوست و برایشان عذابی دردناک است.'),
(1965, 16, 64, 'و ما (این) کتاب را بر تو نازل نکردیم، مگر برای اینکه آنچه را در آن اختلاف کرده‌اند، برایشان توضیح دهی. حال آنکه برای مردمی که ایمان می‌آورند، هدایت و رحمتی (جاودانه) است.'),
(1966, 16, 65, 'و خدا از آسمان آبی فرود آورد. پس با آن زمین را بعد از مرگش زنده کرد. قطعاً در این (رحمت بزرگ) برای مردمی که (حق را) می‌شنوند به‌راستی نشانه‌ای (بزرگ برای حق‌شناسی) است.'),
(1967, 16, 66, 'و بی‌گمان در چهارپایان برایتان همانا عبرتی است، از آنچه در شکم‌هایشان است از میان سرگین و خونی، شیری ناب به شما می‌نوشانیم (که) برای نوشندگان گواراست.'),
(1968, 16, 67, 'و از میوه‌های درختان خرما و انگور، باده‌ا‌ی مستی‌بخش، و خوراکی نیکو برای خود بر می‌گیرید. بی‌گمان در این برای مردمانی که خردورزی می‌کنند نشانه‌ای بس بزرگ است.'),
(1969, 16, 68, 'و پروردگارت به سوی زنبور عسل وحی [:الهام غریزی] کرد که: «از کوه‌ها و از درختان و از آنچه داربست (و چفته‌سازی) می‌کنند، خانه‌هایی برای خود برگیر.»'),
(1970, 16, 69, '«سپس از همه‌ی میوه‌ها بخور. پس راه‌های پروردگارت را (در این راه) ناگزیر فرمانبردارانه، بپوی.» (آن‌گاه) از درون شکم‌هاشان، آشامیدنی‌ای که به رنگ‌های گوناگون است بیرون می‌آید (و) در آن برای مردمان درمانی (فراوان) است. در این (جریان زنبوران) برای گروهی که اندیشه می‌کنند بی‌گمان نشانه‌ای همواره بزرگ است.'),
(1971, 16, 70, 'و خدا شما را آفرید؛ سپس (جانهای) شما را می‌گیرد. و بعضی از شما تا خوارترین (دوره‌ی) سال‌های زندگی [:فرتوتی] باز گردانده می‌شود، تا پس از دانستنی اندک، (دیگر) چیزی نداند. بی‌گمان خدا بس دانایی بسیار تواناست.'),
(1972, 16, 71, 'و خدا بعضی از شما را در روزی بر بعضی دیگر فزونی داد. پس کسانی که (در روزیشان) فزونی یافته‌اند، روزی خود را به زرخریدان خود نمی‌دهند تا در آن با هم برابر باشند. پس آیا با نعمت خدا (او را) انکار (و اِنگار) می‌کنند؟'),
(1973, 16, 72, 'و خدا برایتان از خودهاتان همسرانی نهاد و از همسرانتان برایتان فرزندان و نوادگانی قرار داد و از پاکیزه‌ها(ی مواد زندگی) روزیتان داد. پس آیا به باطل ایمان می‌آورند و (هم) ایشان به نعمت خدا(شان) کفر می‌ورزند؟'),
(1974, 16, 73, 'و از مادون خدا چیزهایی را می‌پرستند که از آسمان‌ها و زمین به هیچ وجه اختیار روزی‌ای برایشان و (نیز) توان آن را (هم) ندارند.'),
(1975, 16, 74, 'پس برای خدا مَثَل‌هایی (ناروا) نزنید، که خدا به‌راستی می‌داند و شما نمی‌دانید.'),
(1976, 16, 75, 'خدا مَثَلی زده است: «بنده‌ای مملوک را که هیچ کاری از او بر نمی‌آید، آیا (او) با کسی که به وی از جانب خود روزی نیکو داده‌ایم و او از آن در نهان و آشکار انفاق می‌کند یکسانند؟» سپاس خدای راست. (نه) بلکه بیشترشان نادانی می‌کنند.'),
(1977, 16, 76, 'و خدا مَثَلی (دیگر) زد: «دو مرد هستند که یکی از آنها لال است (که) کاری از او بر نمی‌آید حال آنکه سربار سرپرستش(نیز) می‌باشد. به هر جا که او را می‌فرستد هیچ خیری (به همراه) نمی‌آورد. آیا او با کسی که به عدالت فرمان می‌دهد و بر راهی بس راست است یکسان است‌؟»'),
(1978, 16, 77, 'و نهان آسمان‌ها و زمین تنها از خداست. و کار ساعت [:قیامت] جز مانند یک چشم برهم‌زدن یا او نزدیکتر (از آن) نیست. به‌راستی خدا بر هر چیزی بسی تواناست.'),
(1979, 16, 78, 'و خدا شما را از شکم‌های مادرانتان - در حالی که چیزی نمی‌دانید - بیرون آورد، و برای شما گوش‌ها و چشم‌ها و دل‌های فروزان نهاد؛ شاید سپاسگزاری کنید.'),
(1980, 16, 79, 'آیا سوی پرندگانی که در فضای آسمان رام شده‌اند ننگریسته‌اند؟ جز خدا کسی آنها را (از سقوط) نگه نمی‌دارد. به راستی در این (قدرت‌نمایی) برای مردمی که ایمان می‌آورند همانا نشانه‌هایی است.'),
(1981, 16, 80, 'و خدا برایتان از خانه‌هایتان آرامشگاهی قرار داد. و از پوست‌های حیوانات نعمت‌وار برایتان خانه‌هایی نهاد (که) با آن‌ها هنگام جابه‌جا شدنتان و هنگام ماندنتان سبک‌بار باشید، و از پشم‌ها و کرک‌ها و موهای آنها وسایل زندگی و بهره‌ای تا زمانی (معین قرار داد).'),
(1982, 16, 81, 'و خدا از آنچه آفریده برای شما سایه‌بان‌هایی قرار داد. و از کوه‌ها برایتان پناهگاه‌هایی نهاد. و (نیز) برای شما تن‌پوش‌هایی مقرر کرد (که) شما را از گرما حفظ می‌کند، و تن‌پوش‌هایی که شما را از ناراحتی‌ها و برخوردها(ی سرما یا گرما و برخوردهای جنگی و غیر آن) نگه می‌دارد. این گونه (خدا) نعمتش را بر (سروسامان)تان تمام می‌گرداند، شاید شما (به فرمانش) گردن نهید.'),
(1983, 16, 82, 'پس اگر(از حق) رویگردان شوند، بر تو فقط ابلاغ روشنگر است.'),
(1984, 16, 83, 'نعمت خدا را می‌شناسند، سپس منکر آن می‌شوند و بیشترشان کافرند.'),
(1985, 16, 84, 'و روزی از هر امتی گواهی بر می‌انگیزیم. سپس به کسانی که کافر شدند رخصت (سخنی) داده نمی‌شود و ایشان مورد سرزنش هم قرار نمی‌گیرند (که این شایستگی را هم ندارند).'),
(1986, 16, 85, 'و کسانی که ستم کردند هنگامی که عذاب را ببینند (از عذابشان ) کاسته نمی‌گردد و نه ایشان مهلت می‌یابند.'),
(1987, 16, 86, 'و هنگامی که کسانی که شرک ورزیدند شریکان خود را ببینند گویند: «پروردگارمان! این‌هایند شریکان ما که (آن‌ها را) مادون تو می‌خوانده‌ایم.» پس (شریکان) گفته‌ی آنان را به سویشان می‌افکنند که: «شما دروغگویانید.»'),
(1988, 16, 87, 'و آن روز سوی خدا سر تسلیم(شان) را فرود می‌آورند، و آنچه را که به دروغ بر می‌بافته‌اند از نظرشان ناپدید گردد.'),
(1989, 16, 88, 'کسانی که کافر شدند و (دیگران را هم) از راه خدا بازداشتند، به سبب آنچه افساد می‌کرده‌اند عذابی بر عذابشان افزودیم.'),
(1990, 16, 89, 'و (یادآور) روزی که در هر امتی گواهی بر ایشان از خودشان برانگیزیم و تو را (هم) بر (همه‌ی) اینان گواه آوریم. و این کتاب را به تدریج بر تو فرو فرستادیم در حالی که برای هر چیزی روشنگر است و (نیز) هدایت و رحمت و بشارت برای اسلام آورندگان است.'),
(1991, 16, 90, 'محققاً خدا به دادگری و نیکوکاری و بخشش به خویشان نزدیکتر فرمان می‌دهد و از زشتکاری آشکار و (کار) ناپسند و ستم باز می‌دارد (و) شما را اندرز می‌دهد، شاید شما (حقایق) را به خوبی یاد آورید.'),
(1992, 16, 91, 'و هنگامی که با خدا پیمان بستید به پیمانتان وفا کنید، و سوگندها (خودی) را پس از استوار کردنشان مشکنید؛ حال آنکه محققاً خدا را بر خود ضامن (و گواه) قرار دادید. به‌راستی خدا آنچه را انجام می‌دهید می‌داند.'),
(1993, 16, 92, 'و مانند آن زنی نباشید - که رشته‌ی تابیده‌ی خود را پس از محکم کردنی از هم گسست - که سوگندهای خود را میان خویش وسیله‌ی (فریب و) تقلب سازید (به خیال این) که گروهی از گروه دیگر (در داشتن امکانات) افزون باشند. فقط خدا شما را بدین وسیله می‌آزماید و (نیز) برای اینکه روز قیامت در آنچه اختلاف می‌کرده‌اید برای شما آشکار سازد.'),
(1994, 16, 93, 'و اگر خدا بخواهد بی‌چون شما را امتی یگانه می‌کند. ولی هر که را بخواهد بیراه می‌کند و هر که را بخواهد به راه می‌آورد. و به راستی از آنچه انجام می‌داده‌اید بی‌گمان پرسیده خواهید شد.'),
(1995, 16, 94, 'و سوگندهای خودتان را دستاویز تقلب میان خودتان قرار مدهید تا گامی بعد از استواریش بلغزد و شما به (سزای) آنچه (مردم را) از راه خدا بازداشته‌اید بدی را بچشید. و(اگر چنان کنید) برای شما عذابی بزرگ است.'),
(1996, 16, 95, 'و با پیمان خدا بهایی ناچیز را نخرید. تنها آنچه نزد خداست - اگر می‌دانسته‌اید - همان برایتان نیکی‌ای است.'),
(1997, 16, 96, 'آنچه نزد شماست نابود می‌شود و آنچه نزد خداست پایدار است. و همواره کسانی را که شکیبایی کردند به بهتر از آنچه عمل می‌کردند، همانا پاداش خواهیم داد.'),
(1998, 16, 97, 'هر کس - از مرد یا زن - در حال ایمانش (کار) شایسته‌ای انجام دهد همواره او را به‌راستی زندگی‌ای پاکیزه می‌بخشیم و به‌درستی به آنان بهتر از آنچه انجام می‌دادند بس پاداش خواهیم داد.'),
(1999, 16, 98, 'پس چون قرآن بخوانی، از (شرّ) شیطان مطرود، ازخدا همی پناه بخواه.'),
(2000, 16, 99, 'بی‌گمان او را بر کسانی که ایمان آوردند و بر پروردگارشان توکل می‌کنند سلطه‌ای نیست.'),
(2001, 16, 100, 'سلطه‌ی او [:شیطان] تنها بر کسانی است که وی را به سرپرستی بر می‌گیرند و (بر) کسانی که به وسیله‌‌ی او [:شیطان] مشرکانند.'),
(2002, 16, 101, 'و چون (نشانه و) آیتی را به جای آیتی دیگر بیاوریم - و حال آنکه خدا به آنچه به تدریج نازل می‌کند داناتر است‌- گویند: «تو فقط دروغ بافی.» (نه) بلکه بیشتر آنان نادانی می‌کنند.'),
(2003, 16, 102, 'بگو: «آن را روح‌القدس از طرف پروردگارت به تدریج به حق فرود آورده تا کسانی را که ایمان آورده‌اند استوار گرداند و برای کسانی که (در برابر خدا) تسلیمند هدایت و بشارتی بزرگ است.»'),
(2004, 16, 103, 'و به‌راستی و درستی می‌دانیم که آنان بی‌چون می‌گویند: «جز این نیست که بشری به او (قرآن) می‌آموزد.» (چنان نیست زیرا) زبان کسی که (این) نسبت (دروغین) را به او می‌دهید غیر عربی است و این (قرآن) عربی‌ای روشن‌گر است.'),
(2005, 16, 104, 'بی‌گمان کسانی که به آیات خدا ایمان نمی‌آورند خدا آنان را هدایت نمی‌کند و برایشان عذابی درناک است.'),
(2006, 16, 105, 'تنها کسانی دروغ‌پردازی می‌کنند که به آیات خدا ایمان نمی‌آورند و ایشان خودشان دروغگویانند.'),
(2007, 16, 106, '(دروغ زن) کسی است که پس از ایمانش به خدا کفر ورزد مگر آن کس که مجبور شده و قلبش به ایمان اطمینان دارد. لیکن کسانی که سینه‌هاشان را به کفر گشاده کرده، پس خشم خدا بر آنان است و برایشان عذابی بزرگ است.'),
(2008, 16, 107, 'این بدین سبب است که اینان، بی‌گمان، زندگی دنیا را بر آخرت - از روی محبت - برگزیدند، و اینکه خدا گروه کافران را هرگز هدایت نمی‌کند.'),
(2009, 16, 108, 'اینانند کسانی که خدا بر دل‌ها و گوش‌ها و دیدگانشان مهر نهاده و ایشان (هم)اینان غافلانند.'),
(2010, 16, 109, 'ناگزیر اینان در آخرت، (هم)ایشان همواره زیان‌کارانند.'),
(2011, 16, 110, 'سپس پروردگارت برای کسانی که پس از آزمونی آتشین (از محیطی گمراه کننده) هجرت کرده (و) سپس جهاد نموده و شکیبایی کردند، به‌راستی پروردگارت (نسبت به آنان) بعد از آن (همه مصایب) به راستی بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(2012, 16, 111, 'روزی (که) هر کسی می‌آید در حالی که از خود دفاع می‌کند؛ و هر کسی به آنچه کرده بی‌کم‌وکاست پاداش می‌یابد در حالی که بر آنان ستم نرود.'),
(2013, 16, 112, 'و خدا (برای ناسپاسان) گروهی را مَثَل زده است که غرق در امن و امان بودند، در حالی که روزیشان از هر سو برایشان به گونه‌ای فراوان می‌رسید. پس نعمت‌های خدا را کفران کردند و خدا (هم) به سزای آنچه بر می‌ساخته‌اند، لباس گرسنگی و هراس را به آنان چشانید.'),
(2014, 16, 113, 'و به‌راستی و درستی فرستاده‌ای از خودشان برایشان آمد، پس او را تکذیب کردند. پس در حالی که ظالم بودند آنان را عذاب فرو گرفت.'),
(2015, 16, 114, 'پس از آنچه خدا شما را روزی داده است حلال و پاکیزه‌ای بخورید و نعمت خدا را - اگر تنها او را می‌پرستید - شکر گزارید.'),
(2016, 16, 115, '(خدا) تنها مردار و خون (ریخته شده) و گوشت خوک و آنچه را (به هنگام ذبح‌) نام غیر خدا بر آن برده شده حرام گردانیده است. (با این همه) هرکس (که به خوردن آنها) ناگزیر شود، در حالی که ستمکار و جویای اضطرار و (نیز) برگشت‌کننده‌(ی به این) حرام نباشد؛ خدا بی‌گمان بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(2017, 16, 116, 'و به آنچه زبان شما به دروغ می‌پردازد، مگویید: «این حلال است و این حرام.‌» تا بر خدا افترایی دروغین بزنید. به‌راستی کسانی که بر خدا افترای دروغ می‌بندند (خود و دیگران را) رستگار نمی‌کنند.'),
(2018, 16, 117, 'این (بهره‌ی دنیوی) متاعی است اندک و برایشان عذابی است پر درد.'),
(2019, 16, 118, 'و بر کسانی که یهودی شدند، آنچه را قبلاً بر تو حکایت کردیم حرام گردانیدیم. و ما به آنان ستم نکردیم، بلکه آنان (خود) به خودشان ستم می‌کرده‌اند.'),
(2020, 16, 119, 'سپس پروردگارت به راستی برای کسانی که به نادانی مرتکب گناه شدند، سپس بعد از آن توبه نموده و (گناهشان را جبران و) اصلاح کردند، البته پروردگارت پس از آن بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(2021, 16, 120, 'به‌راستی ابراهیم (به تنهایی) امتی بود، خاضع و فرمان‌بردار خدا (و) از هر باطلی رویگردان بود و از مشرکان نبود.'),
(2022, 16, 121, 'حال آنکه نعمت‌هایش را شکرگزار بود، (خدا) او را برگزید و به راهی راست هدایتش فرمود.'),
(2023, 16, 122, 'و در دنیا به او (زندگی) نیکی دادیم و به‌راستی او در آخرت بی‌گمان از شایستگان (ویژه) است.'),
(2024, 16, 123, 'سپس فراسوی تو وحی کردیم که: « از آیین ابراهیم - در حالی که رویگردان از باطل بود - پیروی کن حال آنکه از مشرکان نبود.»'),
(2025, 16, 124, '(جریان) شنبه، تنها بر کسانی - که درباره‌ی آن اختلاف کردند - مقرر گردید. و به راستی پروردگارت روز رستاخیز میان آنان درباره‌ی چیزی که در مورد آن اختلاف می‌کرده‌اند به درستی داوری خواهد کرد.'),
(2026, 16, 125, 'با فرزانگی و اندرز نیکو (مکلفان را) به راه (راهوار) پروردگارت دعوت کن و با آنان با نیکوترین شیوه مجادله نمای. به‌راستی پروردگارت به (حال) کسی که از راه او منحرف شده داناتر و(هم) او به (حال) راه‌یافتگان (نیز) داناتر است.'),
(2027, 16, 126, 'اگر پیگیری کنید، همان گونه که مورد عقوبت قرار گرفته‌اید (متجاوز را) به عقوبت رسانید، و اگر به‌درستی صبر کنید البته آن برای شکیبایان بهتر است.'),
(2028, 16, 127, 'و صبر کن و صبر تو جز به (توفیق) خدا نیست. و بر آنان اندوه مخور و از آنچه نیرنگ می‌کنند در تنگی مباش.'),
(2029, 16, 128, 'بی‌گمان، خدا با کسانی است که پرهیز داشتند و (با) کسانی (است) که (هم)آنان نیکوکارند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'منزّه است کسی که بنده‌(ی ویژه)‌اش را شبان‌گاهی از مسجدالحرام به سوی دورترین مسجد (آسمانی) - که پیرامونش را برکت دادیم - به وسیلة بندگیِ ممتازش سیر داد، برای آنکه برخی از نشانه‌هایمان را به او نشان دهیم، بی‌گمان او همان (خدای) بس شنوای بیناست.'),
(2031, 17, 2, 'و کتاب (وحیانی تورات) را به موسی دادیم و آن را برای فرزندان اسرائیل رهنمودی گردانیدیم که: «(زنهار)، غیر از من کاردانِ کارسازی برمگیرید.»'),
(2032, 17, 3, 'فرزندان (همان) کسانی که با نوح (در کشتی) حمل کردیم؛ به‌راستی که او بنده‌ای بسیار سپاسگزار بود.'),
(2033, 17, 4, 'و به فرزندان اسرائیل در کتاب (آسمانی‌شان) خبری حتمی دادیم: «همانا دو بار در سراسر زمین همواره افساد خواهید کرد. و بی‌گمان (بار دوم) به سرکشی بزرگی بی‌امان بر خواهید خاست‌.»'),
(2034, 17, 5, 'پس آنگاه که (زمان تحقّق) وعده‌ی نخستین آن دو (افساد) فرا رسد، بندگان (ویژه‌ی) خود را - که سخت نیرومندند - بر شما می‌گماریم. پس (در) میان خانه‌ها (برای سرکوبی شما) به جستجویی بی‌امان بپردازند، و این وعده‌ای انجام‌شدنی بوده است.'),
(2035, 17, 6, 'سپس دوباره شما (اسرائیلیان) را بر این بندگان ویژه چیره می‌کنیم و شما را با اموالی و پسرانی (یاری) و مدد می‌دهیم و شما را بیش از پیش (برای جنگ) بسیج می‌گردانیم.'),
(2036, 17, 7, 'اگر نیکی کنید، برای خودهاتان نیکی کرده‌اید و اگر (هم) بدی کنید، به زیان خودتان است. پس آنگاه، وعده‌ی پایانی فرا رسد باید (آن بندگان ما) چهره‌هایتان را زشت و تباه کنند. و باید در آن مسجد (الاقصای زمینی) در آیند چنان که نخستین بار وارد آن شدند و (نیز) باید (با غلبه بر اسرائیلیان) بی‌گمان چیرگی‌شان را یکسره برچینند (چه) برچیدنی.'),
(2037, 17, 8, 'شاید پروردگارتان رحمتان کند. و (اما) اگر (به گناهتان) بازگردید (ما نیز به کیفرتان) باز می‌گردیم. و دوزخ را برای کافران زندانی سخت قرار دادیم.'),
(2038, 17, 9, 'به‌راستی این قرآن به هر آن چه (از راه‌ها) استوارتر، محکم‌تر و ارزشمندتر است هدایت می‌کند. و به آن مؤمنانی که کارهای شایسته می‌کنند، مژده می‌دهد که بی‌گمان برایشان پاداشی بزرگ است.'),
(2039, 17, 10, 'و بی‌تردید برای کسانی که به آخرت ایمان نمی‌آورند عذابی پر درد آماده کرده‌ایم.'),
(2040, 17, 11, 'و انسان (به جای درخواست) خواسته‌اش به وسیله‌ی خیر، (آن) را با شر درخواست می‌کند. و انسان بسی شتابان بوده است.'),
(2041, 17, 12, 'و شب و روز را دو نشانه قرار دادیم؛ پس نشانه‌ی شب را محو کردیم و نشانه‌ی روز را بینا کننده قراردادیم، تا (در آن) فضلی از پروردگارتان بجویید. و تا شماره‌ی سال‌ها و حساب (عمر و رویدادها) را بدانید. و هرچیزی (از احکام و غیره) را (به روشنی)توضیح دادیم (چه) توضیح دادنی.'),
(2042, 17, 13, 'و(برای) هر انسانی پرنده‌اش [صدا و سیمای عملش را که گویی پروازگر و رفتی است] در گردنش [:عمق جانش] ضبط و ثبت کردیم و روز رستاخیز برای او برون می‌آوریم (همان) - کارنامه‌ای را که باز شده - دریافتش می‌کند.'),
(2043, 17, 14, '(و به او می‌گوییم) : «کارنامه‌ات را بخوان‌؛2 کافی است (که) امروز خودت حسابگر (خود) باشی‌.»'),
(2044, 17, 15, 'هر کس (به شایستگی) به راه آید، تنها به سود خود به راه آمده، و هر کس به بی‌راهه رود تنها بر زیان خود به بیراهه رفته. و هیچ بردارنده‌ی باری، بار (گناه) دگری را بر نمی‌دارد. و ما هرگز عذاب‌کننده نبوده‌ایم تا (آنگاه که) فرستاده‌ای (وحیانی) برانگیزانیم.'),
(2045, 17, 16, 'و هنگامی که بخواهیم قریه‌ای[:مجتمعی انسانی] را هلاک کنیم، خودکامگان غرق شده در نعمتِ آن (قریه) را (به خیرات اجتماعی) امر می‌کنیم. پس در آن (قریه) سرپیچی می‌کنند؛ پس گفته [:وعده عذاب] بر آنان محقّق می‌گردد؛ در نتیجه آن (قریه) را یکسره زیر و زبر کنیم (چه) زیر و زبر کردنی.'),
(2046, 17, 17, 'و چه بسیار از نسل‌ها را (که) ما پس از نوح به هلاکت رساندیم. و (همین) کمال آگاهی و بینایی پروردگارت به گناهان پیامددار بندگانش کافی است.'),
(2047, 17, 18, 'هر کس خواهان (دنیای) زودگذر بوده است ما (نیز) با شتاب - برای کسی که اراده کنیم، آنچه از آن را بخواهیم - به او می‌دهیم، سپس جهنم را برایش قرار دهیم (که) آن را می‌افروزد، در حالی‌که سرزنش‌شده‌ی رانده‌شده است.'),
(2048, 17, 19, 'و هر کس خواهان آخرت است و (با) کوششی شایسته‌ی آن، در حال ایمان برای آن بکوشد؛ پس اینان کوشیدنشان سپاسگزاری شده است.'),
(2049, 17, 20, 'اینان و آنان همگی را از بخشش پروردگارت یاری می‌کنیم. و عطای پروردگارت هرگز (برای کسی) ممنوع نبوده است'),
(2050, 17, 21, 'بنگر چگونه (با عدل و فضل خود) بعضی از آنان را بر بعضی دیگر (در دنیا) (بسی) برتری دادیم، و بی‌گمان (در) آخرت درجاتی بزرگتر (از درجات دنیوی) و برتری دادنی بزرگتر (از برتری دادنهای دنیوی) است.'),
(2051, 17, 22, 'با خدا معبود دیگری (را شریک) قرار مده. پس (اگر چنان کنی) در حالی که نکوهیده و خوار شده‌ای، زمین‌گیر می‌شوی.'),
(2052, 17, 23, 'و پروردگارت مقرر کرد که جز او را مپرستید. و به پدر و مادر(تان) احسان کنید. همانا اگر یکی از آن دو یا هر دو، نزد تو به سالخوردگی رسیدند، به آنها (حتی) اُفّ [:لفظی کراهت‌بار] مگو و بر سرشان بانگ مزن و بدیشان سخنی بس با کرامت بگوی.'),
(2053, 17, 24, 'و از سر رحمت، بال فروتنی بر (سر و سامان)شان بگستران و بگو: «پروردگارم! بر آن دو رحم کن، چنان‌که مرا در خردی پروردند.»'),
(2054, 17, 25, 'اگر (از) شایستگان باشید، پروردگارتان به آنچه در خودتان هست آگاه‌تر است. که او بی‌گمان برای زیاد رجوع‌کنندگان پیاپی (به سویش) پوششگر بوده است.'),
(2055, 17, 26, 'و حقِ نزدیکترین خویشاوند را به او بده و (نیز حق) مستمند و و‌امانده در راهِ (حلال از تأمین معاش) را، و تبذیر مکن تبذیری (که مال - یا حالت - را پایمال کنی).'),
(2056, 17, 27, 'بی‌گمان تبذیرکنندگان برادران شیاطین بوده‌اند و شیطان همواره نسبت به پروردگارش بسی کافر (و) ناسپاس بوده است.'),
(2057, 17, 28, 'و اگر به امید رحمتی که از پروردگارت (برایشان) جویای آنی، از ایشان به‌ناچار روی بر می‌گردانی، پس با آنان سخنی نرم و روان بگوی.'),
(2058, 17, 29, 'و دستت را به گردنت زنجیروار منه و به تمامیِ گشادگی (هم) گشاده دستی مکن. که در نتیجه ملامت‌شده و حسرت‌زده بر جای بمانی.'),
(2059, 17, 30, 'به‌راستی، پروردگارت برای هر که بخواهد، روزی را فراخ و تنگ می‌گرداند. بی‌گمان او به (حال) بندگانش بسی آگاه و بینا بوده است.'),
(2060, 17, 31, 'و از بیم تنگدستی، فرزندانتان را مکشید؛ ماییم که به آنها و شما روزی می‌دهیم. کشتن آنان همواره خطایی بزرگ بوده است.'),
(2061, 17, 32, 'و به زنا نزدیک مشوید، (که) آن همواره (گناهی) تجاوزگر و راهی بد (در زندگی بشر) بوده است.'),
(2062, 17, 33, 'و کسی را که خدا محترم داشته جز به حق مکشید. و هر کس مظلومانه کشته شود، بی‌چون برای خونخواهش سلطه‌ای (بر جان قاتل) قرار داده‌ایم. پس (او) نباید در کشتن زیاده‌روی کند (که) همانا او (از طرف خدا) مؤیَّد بوده است.'),
(2063, 17, 34, 'و به مال یتیم - جز به بهترین وجه - نزدیک مشوید، تا به رشدهایش رسد. و به پیمان وفا کنید، که همواره پیمان مورد سؤال بوده است.'),
(2064, 17, 35, 'و هنگامی‌که پیمانه کنید، پیمانه را کامل نمایید، و با ترازوی راست بسنجید (که) این (کار) بهتر و خوش‌فرجام‌تر است.'),
(2065, 17, 36, 'و آنچه را که برایت بدان علمی نیست پیروی مکن ، همانا گوش و چشم و قلب فروزان، همگی اینها مورد پرسش و بازخواست بوده‌اند.'),
(2066, 17, 37, 'و در (روی) زمین به شادکامی، نخوت‌بار گام برمدار، (زیرا) تو بی‌چون زمین را هرگز نتوانی شکافت، و هرگز در بلند بالایی به کوه‌ها نتوانی رسید.'),
(2067, 17, 38, 'همه‌ی این (کارها) بدش نزد پروردگارت بسی ناپسند بوده است.'),
(2068, 17, 39, 'این(ها) از آن حکمت‌هایی است که پروردگارت به سوی تو وحی کرد. و (تو هم) با خدا معبودی دیگر (را شریک) قرار مده، پس سرزنش‌شده و رانده‌شده در جهنم افکنده شوی.'),
(2069, 17, 40, 'پس آیا (پنداشتید) که پروردگارتان شما را به (داشتن) پسران برگزیده، و (خود) از فرشتگان دخترانی بر گرفته؟! بی‌تردید شما قطعاً سخنی بس ناروا می‌گویید.'),
(2070, 17, 41, 'و همانا به‌راستی، ما در این قرآن (حقایق را) گونه‌گون بیان کردیم تا به خوبی (آنها را) یاد کنند؛ و (اما) آنان را جز نفرت نمی‌افزاید.'),
(2071, 17, 42, 'بگو: «اگر – چنان‌که می‌گویند – با او خدایانی بودند، در آن هنگام (وهنگامه) بی‌چون راهی به سوی صاحب تخت فرماندهی جهانی [:خدا] می‌جستند.»'),
(2072, 17, 43, 'او از آنچه (به ناروایی) می‌گویند منزه و والا‌تر است (در) برتری و بزرگی.'),
(2073, 17, 44, 'آسمان‌های هفتگانه و زمین‌ها و هرکس که در آنهاست برای او تسبیح می‌نمایند و هیچ چیز نیست مگر اینکه (خدا را) با ستایش او تسبیح می‌نماید، ولی شما تسبیحشان را نمی‌توانید بفهمید. به‌راستی او بردباری پوششگر بوده است.'),
(2074, 17, 45, 'و هنگامی که قرآن بخوانی، میان تو و میان کسانی که به آخرت ایمان نمی‌آورند پرده‌ای پنهان (از آنان) قرار می‌دهیم.'),
(2075, 17, 46, 'و بر دل‌هاشان پوشش‌هایی می‌نهیم که آن را نفهمند. و در گوش‌هایشان سنگینی(‌ها)یی (قرار می‌دهیم). و هنگامی که پروردگارت را در قرآن به یگانگی‌اش یاد کنی، در حال نفرت (از توحید) پشتشان را بر می‌گردانند.'),
(2076, 17, 47, 'چون به سوی تو گوش فرا می‌دارند، ما داناتریم (که) به چه (منظور) گوش فرا می‌دهند. و (نیز) چون ایشان نجوا (کنان)‌اند، در آن حال ستمکاران گویند: «جز مردی افسون شده را پیروی نمی‌کنید.»'),
(2077, 17, 48, 'بنگر چگونه برای تو مَثَل‌ها زدند؛ تا گمراه شدند؛ پس توان (رفتن) راهی (به سوی حق) را ندارند.'),
(2078, 17, 49, 'و گفتند: «آیا هنگامی که استخوان‌ها و پوسیدگانی پراکنده بودیم، آیا به‌راستی ما (با) آفرینشی جدید، بی‌چون (از) برانگیخته‌شدگانیم‌؟»'),
(2079, 17, 50, 'بگو: «سنگی یا آهنی باشید،»'),
(2080, 17, 51, '«یا آفریده‌ای از آنچه در سینه‌های‌تان بزرگ می‌نماید.» پس خواهند گفت: «چه کسی ما را بر می‌گرداند؟» بگو: «همان کس که نخستین بار شما را (با فطرت توحیدی) آفرید.» باز سرهایشان را (مسخره کنان) به سویت تکان خواهند داد و می‌گویند: «آن (زمان) کِیْ است‌؟» بگو: «شاید که نزدیک باشد.»'),
(2081, 17, 52, 'روزی که شما را فرا می‌خواند، پس او را با ستایشش اجابت خواهید کرد و می‌پندارید که جز اندکی (در گذشته) نمانده‌اید.'),
(2082, 17, 53, 'و برای بندگانم بگو: «آنچه را که بهتر است بگویند.» بی‌گمان شیطان میانشان را بر هم می‌زند؛ همواره شیطان برای انسان دشمنی آشکار کننده‌ی (دشمنی‌اش) بوده است.'),
(2083, 17, 54, 'پروردگارتان به (احوال) شما داناتر است‌؛ اگر بخواهد به شما رحم می‌کند، یا اگر بخواهد شما را عذاب می‌کند. و تو را بر ایشان (برای) کارسازی (تکوینی و تشریعی) نفرستادیم.'),
(2084, 17, 55, 'و پروردگارت به هر که در آسمانها و زمین است (از همگان) داناتر است. و به‌راستی بعضی از پیامبران بزرگ را بر بعضی (از ایشان) بی‌گمان برتری بخشیدیم، و داوود را زبور دادیم.'),
(2085, 17, 56, 'بگو: «کسانی را که بجز او (معبود خود) پنداشتید، بخوانید و (از آن‌ها) بخواهید؛ ولی آنها نه توانی دارند که از شما دفع زیان کنند و نه بلا گردانی (برای شما باشند.)»'),
(2086, 17, 57, 'اینان (پیامبران و مؤمنان) کسانی‌اند که (پروردگارشان) را می‌خوانند و او را می‌خواهند در حالی‌که هر کدام از آنان (که به او) نزدیکترند (نزدیکترین) وسیله (های حرکت) به سوی پروردگارشان را می‌جویند و به رحمت او امیدوارند و از عذابش می‌ترسند. عذاب پروردگارت همواره ترسناک بوده است.'),
(2087, 17, 58, 'و هیچ قریه‌ای [:مجتمعی انسانی] نیست مگر اینکه ما آن را پیش از روز رستاخیز هلاک کننده‌ایم، یا آن را (در صورت استحقاق) به عذابی سخت دچار کننده‌ایم. این (جریان) در کتاب (علم و حکمت و قدرت الهی) ثبت شده بوده است.'),
(2088, 17, 59, 'و (چیزی) ما را از اینکه (خاتم پیامبران را) با نشانه‌ها (ی بصری) بفرستیم باز نداشت، جز اینکه پیشینیان، با آنها (همان) نشانه‌ها را تکذیب کردند. و به ثمود ماده شتر دادیم، در حالی‌که (نشانه‌ای) بینا کننده بود. پس به آن ستم کردند. و ما (پیامبران را) با (آن) نشانه‌ها جز برای ترساندن (مردمان) نمی‌فرستیم.'),
(2089, 17, 60, 'و چون برایت گفتیم: «به‌راستی پروردگارت به مردم احاطه داشته‌» و آن رؤیایی را که به تو نمایاندیم و (نیز) آن درخت لعنت‌شده در قرآن را، جز آزمایشی آتشین برای مردمان قرار ندادیم. و ما بیمشان می‌دهیم، پس ایشان را بجز طغیانی بزرگ نمی‌افزاید.'),
(2090, 17, 61, 'و چون به فرشتگان گفتیم: برای (شکرانة یادگیری از) آدم (خدا را) سجده کنید» پس (همه) سجده کردند، جز ابلیس (که) گفت: «آیا برای کسی که در حال گل بودن (او را) آفریدی سجده کنم‌؟»'),
(2091, 17, 62, '(ابلیس) گفت: «آیا خود را (بخوبی) دیدی‌؟ این کس را (نسبت)به من گرامی داشتی؟ اگر تا روز قیامت (عُمرِ) مرا تأخیر اندازی حتماً نسلش را - جز اندکی - همواره افسار خواهم کرد.»'),
(2092, 17, 63, '(خدا) فرمود: «برو، پس هر کس از آنان تو را پیروی کند، بی‌گمان جهنم - که کیفری فراوان است- سزای شماست.»'),
(2093, 17, 64, '«و از ایشان هر که را توانستی با آوای خود بیهوده تحریک کن و با سوارگان و پیادگانت بر آنان بتاز و در اموال و اولادشان مشارکت کن و «به ایشان وعده بده.‌» و شیطان جز فریبی به آنها وعده نمی‌دهد.'),
(2094, 17, 65, '«بی‌گمان برای تو بر بندگان ویژه‌ی من (هیچ‌گونه) سلطه‌ای نیست. و حمایتگری و کارسازی پروردگارت (برای آنان) بس است‌.»'),
(2095, 17, 66, 'پروردگار شما کسی است که کشتی را در دریا (از امواج سهمگینش) برای شما از جای بر می‌کَند، تا از فضل او (برای خود) بجویید. او (نسبت) به شما رحمتگری ویژه بوده است.'),
(2096, 17, 67, 'و هنگامی که در دریا به شما زیان (فراگیر) در رسد، هر که را جز او می‌خوانید و می‌خواهید ناپدید گردد. پس هنگامی‌که (خدا) شما را فراسوی خشکی رهانید (از همو) رویگردان می‌شوید. و (نوعِ) انسان بسی کافر (یا) کفران‌کننده بوده است.'),
(2097, 17, 68, 'پس آیا ایمن شدید (از این) که (خدا) به واسطه‌ی (شرک) شما بخشی از خشکی (در زمین) را بشکافد، و (با فرو بُردنتان در آن) چهره‌تان را بپوشاند یا بر شما طوفانی از شن بفرستد، سپس برای خودتان نگهبان کارسازی نیابید؟'),
(2098, 17, 69, 'یا ایمن شدید (از این) که بار دیگر شما را به (آن) دریا بازگرداند، پس تندبادی از جای کننده بر شما بفرستد. پس به (سزای) آنچه کفر ورزیدید غرقتان کند، (و) آنگاه برای خودتان در برابر ما (نسبت) به آن (عذاب، دادستان و) پی‌جویی نیابید؟'),
(2099, 17, 70, 'و ما همواره فرزندان آدم را به‌راستی گرامی داشتیم. و آنان را در خشکی و دریا (بر مَرکب‌ها) حمل کردیم. و از پاکیزه‌ها به ایشان روزی دادیم. و آنها را بر بسیاری از کسانی که آفریدیم برتری دادیم (آن هم) برتری (ویژه)‌ای.'),
(2100, 17, 71, 'روزی که هر گروهی (از مکلّفان) را با پیشوایشان فرا می‌خوانیم، پس هر کس کارنامه‌اش به دست راستش داده شود (نشانه‌ی نجات اوست)، پس اینان کارنامه‌ی خودشان را می‌خوانند. و به اندازة نَخَک هسته‌ی خرمایی (هم) به آنان ستم نمی‌شود.'),
(2101, 17, 72, 'و هر کس در این (دنیا) کور (دل) بوده، پس او در آخرت (چشم و دلش) کور و گمراه‌تر است.'),
(2102, 17, 73, 'چیزی نمانده بود (که) تو را ناگزیر و ناگریز از آنچه به سویت وحی کردیم با مکرشان (دچار) آزمایشی سخت کنند، تا غیر آن را بر ما افترا ببندی. و آنگاه بی‌گمان تو را به دوستی تنگاتنگ (خود) برگیرند.'),
(2103, 17, 74, 'و اگر تو را استوار نمی‌داشتیم، همانا بی‌چون نزدیک بود کمی سوی آنان متمایل شوی.'),
(2104, 17, 75, 'در آن هنگام حتماً تو را چند برابر (در) زندگی و چند برابر (در) مرگ (عذاب) می‌چشاندیم، سپس علیه ما برای یاوری توانمند نمی‌یابی'),
(2105, 17, 76, 'و چیزی نمانده بود (که) تو را همواره از این سرزمین (از بُن) بَر کَنند، تا تو را از آنجا بیرون برانند. و در آن هنگام (آنان) جز (زمان و تعداد) اندکی در مخالفت با تو نمی‌مانند.'),
(2106, 17, 77, 'سنّت کسانی از فرستادگانمان را که همواره پیش از تو گسیل داشتیم (استوار بدار). و (بدان که) برای سنت ما (هرگز) جایگزینی نخواهی یافت.'),
(2107, 17, 78, 'نماز را - به‌هنگام زوال آفتاب تا تاریکترین وقتِ شب - و (به ویژه نمازِ) خواندنیِ صبح‌گاهان را به‌پادار. بی‌گمان، خواندنی صبحگاهان در دیدرسِ (فرشتگان شب و روز) بوده است.'),
(2108, 17, 79, 'و پاسی از شب را (به عبادت) زنده بدار، (که) برای تو واجبی فزون (بر دیگران) است. امید است که پروردگارت تو را به مقامی ستوده (تر) برانگیزد.'),
(2109, 17, 80, 'و بگو: «پروردگارم! مرا (در هر کاری) به گونه‌ای راست و درست داخل نما. و مرا (از هر کاری) به گونه‌ای راست و درست خارج ساز، و از جانب خود برای من سلطه‌ای پیروزمندانه قرار ده‌.»'),
(2110, 17, 81, 'و بگو: «کّل حق (در حد امکان) آمد و کل باطل نابود شد، (که) باطل همواره نابود شدنی بوده است‌.»'),
(2111, 17, 82, 'و ما از قرآن آنچه را (که هم) او برای مؤمنان (مایه‌ی) درمان و رحمتی است، به تدریج فرو می‌فرستیم (اما) ستمکاران را جز زیانی نمی‌افزاید.'),
(2112, 17, 83, 'و هنگامی که بر (سروسامان نوعِ) انسان نعمت دهیم، روی می‌گرداند و (از حق) کناره می‌گیرد و هنگامی که به وی بدی (در) رسد بسی نومید می‌شود.'),
(2113, 17, 84, 'بگو: «هر کس بر حسب ساختار (اختیاریِ) خود عمل می‌کند. پس پروردگارتان به هر که راه یافته‌تر باشد (از همگان) داناتر است.‌»'),
(2114, 17, 85, 'و درباره‌ی روح [:قرآن و... ] از تو می‌پرسند. بگو: «روح از کار (های) پروردگار من است و به شما از (کلّ) دانش جز اندکی داده نشده است.‌»'),
(2115, 17, 86, 'و همانا اگر بخواهیم، بی‌گمان آنچه را به سوی تو وحی کردیم بی‌چون (از یادت) می‌بریم، سپس برای (حفظ) آن، علیه ما، برای خود کارسازی نمی‌یابی،'),
(2116, 17, 87, 'مگر رحمتی از (جانب) پروردگارت، (در رسد، که) فضل او بر تو همواره بزرگ بوده است.'),
(2117, 17, 88, 'بگو: «اگر همواره انس و جن با هم پیوسته و هماهنگ گردند بر اینکه مانند این قرآن را بیاورند (هرگز) مانند آن را نمی‌آورند و گر چه (بر فرض محال) آنان (در این جریان) پشتیبان یکدیگر باشند.'),
(2118, 17, 89, 'و بی‌گمان، ما در این قرآن به‌راستی از هر مثلی (نمونه‌های) گوناگون برای مردم آوردیم، پس بیشتر مردمان تنها از (روی) کفر و کفران (از آن) خودداری کردند.'),
(2119, 17, 90, 'و گفتند: « هرگز برای تو ایمان نیاوریم. تا از زمین چشمه‌ای به خاطر ما به شدت روان کنی.»'),
(2120, 17, 91, '«یا (باید) برای تو باغی از درختان سر در هم خرما و انگور باشد، پس میان آنها با جوشاندن (آب)، نهرهای بسیاری را به شدت روان کنی،»'),
(2121, 17, 92, '«یا چنان که گمان کردی، آسمان را پاره پاره بر (سروسامان)مان بیفکنی، یا خدا و فرشتگان را دسته دسته (نزدمان) بیاوری.»'),
(2122, 17, 93, '«یا برای تو خانه‌ای(آراسته) از زینت‌هایی(گوناگون) باشد، یا در آسمان بالا روی، و به بالا رفتنت (هم) هرگز ایمان نخواهیم آورد تا بر ما کتابی نازل کنی (که) آن را بخوانیم.‌» بگو: «(پاک و) منّزه است پروردگارم، آیا من جز بشری فرستاده (ی خدا) بوده‌ام‌؟»'),
(2123, 17, 94, 'و (چیزی) مردم را از اینکه ایمان آورند باز نداشت، - چون هدایت برایشان آمد - جز اینکه گفتند: «آیا خدا بشری را به پیامبری برانگیخته است‌؟»'),
(2124, 17, 95, 'بگو: «اگر در روی زمین فرشتگانی بودند- که با آرامش و اطمینان راه می‌رفتند- بی‌گمان از آسمان بر آنان فرشته‌ای را به پیامبری فرو می‌فرستادیم.»'),
(2125, 17, 96, 'بگو: «بِین من و شما، گواهی خدا(در کتابش و در فطرت و عقلانیت مکلفان) کافی است. به‌راستی او به (حال) بندگانش بسی آگاه و بینا بوده است‌.»'),
(2126, 17, 97, 'و هر که را خدا راه نماید (هم) او ره‌یافته است و هر که را گمراه سازد، جز او برای آنان هرگز رهبرانی نیابی. و روز قیامت آنان را در حالی که کوران و لالان و کرانی هستند (که) بر روی چهره‌هاشان در افتادند، ناگزیرگرد آوریم؛ پناهگاه‌شان دوزخ است، (و) هر بار آتش آن فرو نشیند، شراره‌ای سوزان برآنان می‌افزاییم.'),
(2127, 17, 98, 'این (همان)کیفرشان است به سبب اینکه همواره آنان آیات ما (یا با آیات ما همان‌ها) را انکار کردند و گفتند: «آیا آن هنگام که ما استخوان‌ها و پوسیدگانی پراکنده باشیم، آیا به‌راستی ما با آفرینشی نوین، بی‌گمان (از) برانگیخته‌شدگانیم‌؟»'),
(2128, 17, 99, 'آیا (دلیل‌های دیگر را درنیافتند) و ننگریستندکه خدایی که به‌راستی آسمان‌ها و زمین را آفریده، تواناست بر این که مانندشان را بیافریند؛ حال آنکه (همو) برایشان اجلی مقرر فرموده، (که) در آن هیچ شک مستندی نیست‌؟ پس ستمکاران تنها از روی کفر(یا) کفران‌(از آن) خودداری کردند.'),
(2129, 17, 100, 'بگو: «اگر شما مالک گنجینه‌های رحمت پروردگارم باشید، در این هنگام از ترس نابودی (آنها)، قطعاً امساک می‌ورزید و (نوعِ)ِانسان همواره بخیل بوده است‌.»'),
(2130, 17, 101, 'و همانا ما به موسی به‌راستی نُه نشانه‌ی آشکار دادیم - پس از فرزندان اسرائیل بپرس – چون (موسی) نزد آنان آمد، پس فرعون به او گفت: «موسی! همانا من جداً تو را افسون‌شده‌ای می‌پندارم.‌»'),
(2131, 17, 102, 'گفت: «تو به‌راستی و درستی دانسته‌ای (که) این (نشانه‌ها) را جز پروردگار آسمان‌ها و زمین (به عنوان) دیده‌بان‌هایی نازل نکرده است، و من به‌راستی بی‌چون تو را تباه‌شده و هلاکت‌یافته‌ای می‌پندارم‌.»'),
(2132, 17, 103, 'پس (فرعون) تصمیم گرفت که آنان را از سرزمین (مصر از بُن) بر کَنَد. پس او و هر که را با وی (همگام) بود، همگی را غرق کردیم.'),
(2133, 17, 104, 'و بعد از او به فرزندان اسرائیل گفتیم: «در (این) سرزمین ساکن شوید، پس هنگامی که وعده‌ی آخرت فرا رسد شما را با هم (گرد)آوریم.»'),
(2134, 17, 105, 'و آن (قرآن) را به تمامی حق نازل کردیم و به تمامی حق، فرود آمد، و تو را جز بشارت‌دهنده و هشدار‌دهنده‌ای نفرستادیم.'),
(2135, 17, 106, 'و قرآنی (باعظمت را) بخش‌بخش نمودیم، تا آن را به آرامی بر مردمان بخوانی، و آن را به تدریج فرو فرستادیم فرو فرستادنی(ویژه).'),
(2136, 17, 107, 'بگو: « (چه) به آن ایمان بیاورید یا ایمان نیاورید، بی‌گمان کسانی که پیش از (نزول) قرآن (نسبت به آن) علم داده‌شده‌اند، هنگامی که(این کتاب) بر آنان خوانده‌شود سجده‌کنان بر(روی) چانه‌هاشان فرو می‌افتند؛'),
(2137, 17, 108, 'و می‌گویند: «منزّه است پروردگارمان، که وعده‌ی پروردگار ما حتماً انجام شدنی بوده‌است.»'),
(2138, 17, 109, 'و بر (روی) چانه‌هاشان گریان (بر خاک) فرو می‌افتند، در حالی‌که (این عمل) فروتنی آنان را افزون می‌کند.'),
(2139, 17, 110, 'بگو: «الله را بخوانید یا رحمان را بخوانید، هرکدام را (خواهید) بخوانید و بخواهید، برای او نیکوترین نام‌هاست. و نمازت را به آواز بلند مخوان و آنرا بسیار آهسته (هم) مخوان و در این میان راهی (میانه) برگزین.»'),
(2140, 17, 111, 'و بگو: « هرستایش (شایسته) برای خداست که نه فرزندی برگرفته و نه برای او در جهان‌داری شریکی بوده و نه برایش سرپرستی از (روی) خواری بوده است.» و او را بزرگ بدار، بزرگ‌داشتنی (شایسته).');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'هر ستایش(شایسته) خدا را سزاست که (این) کتاب [:قرآن] را بر بنده (ی ویژه)‌اش فرو فرستاد و برایش (هیچ گونه) کژی ننهاد.'),
(2142, 18, 2, 'حال آنکه (این کتاب) پربها، محکم و استوار است، تا (خدا به وسیله‌ی قرآن) برخوردی شدید را از جانب خود (به کفار) هشدار دهد، و مؤمنان را که کارهای شایسته (ی ایمان) می‌کنند بسی‌نوید‌بخشد،‌که براستی برایشان پاداشی نیکوست.'),
(2143, 18, 3, 'حال آنکه در آن (پاداش نیکو) همیشه ماند‌گارند.'),
(2144, 18, 4, 'و (همچنین) کسانی را که گفتند: «خدا فرزندی (برای خود) برگرفته» هشدار دهد.'),
(2145, 18, 5, 'نه برای آنان و نه برای پدرانشان بر این (ادعا) هیچ گونه دانشی (و بینشی) نیست. سخنی بس گران (و گزاف) است که از دهانشان(و نه از فطرت‌ها و عقلانیتشان) برون می‌آید (و آنان) جز دروغ نمی‌گویند.'),
(2146, 18, 6, 'پس شاید اگر به این سخن ایمان نیاورند، در حال تأسف بر پیامد کارشان خود را هلاک می‌کنی.'),
(2147, 18, 7, 'در حقیقت، ما آنچه را که روی زمین است، زیوری برای آن قرار دادیم، تا آنان را بیازماییم، (که) کدام یک از ایشان نیکوکارترند.'),
(2148, 18, 8, 'و ما همانا آنچه را که برزمین (استوار) است بی‌گمان تپه‌ای بی‌گیاه خواهیم کرد.'),
(2149, 18, 9, 'یا پنداشتی اصحاب کهف و رقیم [:خفتگان غار لوحه‌دار] از آیات شگفت‌انگیز ما بوده‌اند؟'),
(2150, 18, 10, 'چون آن جوانمردان سوی آن غار پناه جستند، پس گفتند: «پروردگارمان! از جانب خود به ما رحمتی (بزرگ) بده، و از کارمان برایمان سامانی برسان.»'),
(2151, 18, 11, 'پس (در آن غار) سالیانی چند بر گوش‌هایشان پرده(ی خواب) زدیم.'),
(2152, 18, 12, 'سپس آنان را (از این خواب مرگبار) برانگیختیم، تا نشانه نهیم (که) کدام یک از آن دو دسته، زمان درنگشان را بهتر حساب کرده‌اند.'),
(2153, 18, 13, 'ما خبر مهمشان را به (کمال) حقیقت بر تو حکایت می‌کنیم؛ آنان رادمردانی هستند که به پروردگارشان ایمان آوردند و ما بر هدایتشان افزودیم.'),
(2154, 18, 14, 'و بر دل‌هایشان پیوند نهادیم (و استوارشان کردیم). چون (به قصد مخالفت با شرک) به پا خاستند، پس گفتند: «پروردگارمان، پروردگار آسمان‌ها و زمین است. جز او هرگز معبودی را نخواهیم خواند که در این صورت قطعاً ناصواب و پراکنده گفته‌ایم.»'),
(2155, 18, 15, 'اینان ]: مشرکان[گروه مایند (که) جز او معبودانی اختیار کردند. چرا بر (حقانیت) آنها برهانی آشکار نمی‌آورند؟ پس کیست ستمکارتر از آن کس که بر خدا دروغ بسته است‌؟'),
(2156, 18, 16, '«و چون از آنها و از آنچه که جز خدا می‌پرستند کناره گرفتید، پس سوی غار پناه گیرید تا پروردگارتان از رحمت (ویژه‌ی) خود بر (سرو سامان)تان بگستراند و برایتان از کارتان (گشایش و) تکیه‌گاهی فراهم سازد.»'),
(2157, 18, 17, 'و آفتاب را می‌بینی که چون بر می‌آید، از غارشان به سمت راست متمایل است. و چون فرو می‌شود از سمت چپشان (دامن) بر می‌چیند، حال آنکه آنان در جایی فراخ از آن غار قرار گرفته‌اند. این از نشانه‌های (قدرت و حکمت و رحمت) خداست. خدا هر که را راهنمایی کند (هم) او راه یافته است. و هر که را بیراه واگذارد، هرگز برای او سرپرستی راهبر نخواهی یافت.'),
(2158, 18, 18, 'و (تو) پنداری ایشان بیدارند، حال آنکه خفته‌اند. و آنان را به پهلوی راست و چپ می‌گردانیم، حال آنکه سگشان سر بر آستانه‌(ی غار) دو دست خود را باز کرده. اگر بر (حال) آنان آگاه شوی، بی‌گمان گریزان روی از آنان بر می‌تاختی، و به‌راستی از (مشاهده‌ی) آنان آکنده از بیم می‌شوی.'),
(2159, 18, 19, 'و این‌چنین آنها را (از این خواب مرگبار) برانگیختیم، تا در میان خود از یکدیگر پرسش کنند. گوینده‌ای از آنان گفت: «چقدر مانده‌اید؟»گفتند: «روزی یا پاره‌ای از روز (را مانده‌ایم).» (گروهی دیگر از ایشان) گفتند: «پروردگارتان به آنچه مانده‌اید داناتر است. پس یکی از خودتان را با (این) پولتان به شهر بفرستید. پس باید بنگرد کجای شهر طعامش پاکیزه‌تر است، تا غذایی برایتان بیاورد. و باید نرمی و زیرکی به خرج دهد و هرگز هیچ کس را بر (حال) شما آگاه نگرداند.»'),
(2160, 18, 20, '«بی‌گمان اگر آنان بر شما دست یابند، سنگسارتان می‌کنند، یا شما را به کیش خویش باز می‌گردانند، و در آن هنگام (و هنگامه) هرگز (خود و دیگران را) رستگار نتوانید کرد.»'),
(2161, 18, 21, 'و بدین‌سان (مردمان آن دیار را) بر حالشان آگاه ساختیم، تا بدانند (که) همانا وعده‌ی خدا حق است و (اینکه) به‌راستی در (فرا رسیدن) ساعت (قیامت) هیچ شک مستندی نیست. چون میان خود در کارشان با یکدیگر نزاع می‌کنند، پس (اینان) گفتند: «بر روی آنها ساختمانی بنا کنید، پروردگارشان به (حال) آنان داناتر است‌.» (سرانجام) کسانی که بر جریانشان آگاه شدند گفتند: «به‌راستی حتماً فرارویشان مسجدی برخواهیم گرفت.»'),
(2162, 18, 22, 'خواهند گفت: «سه تن‌اند، چهارمین‌شان سگشان.» و می‌گویند: «پنج تن‌اند، ششمین‌شان سگشان.» (این دو گفته) تیری در تاریکی انداختن است؛ و (عده‌ای هم) می‌گویند: «هفت تن بودند و هشتمین‌شان سگ‌شان‌.» بگو: «پروردگارم به شمارشان آگاه‌تر است. جز اندکی (کسی شمار) آنان را نمی‌داند.» پس درباره‌ی ایشان جز به صورت ظاهر جدال مکن و در مورد آنان از هیچ کس فتوایی مجوی.'),
(2163, 18, 23, 'و زنهار در مورد چیزی مگوی که من آن را محققاً فردا انجام خواهم داد.'),
(2164, 18, 24, 'مگر آنکه خدا بخواهد. و چون فراموش کردی، پروردگارت را یاد کن و بگو: «امید که پروردگارم مرا به راهی که نزدیکتر از این به صواب است، رهنمون گردد.»'),
(2165, 18, 25, 'و سیصد سال در غارشان درنگ کردند. و نُه سال (نیز) بر آن افزون خواستند.'),
(2166, 18, 26, 'بگو: «خدا به آنچه درنگ کردند داناتر است. (علم و قدرت بر) نهان آسمان‌ها و زمین به او اختصاص دارد. وه! چه بینا و شنواست! برای آنان سروری جز او نیست و هیچ کس را در (هیچ‌گونه) حکمش (در تکوین و تشریع) هرگز شریک نمی‌کند.»'),
(2167, 18, 27, 'و آنچه را که از کتاب پروردگارت سویت وحی شده است (برایشان) بخوان و پیروی کن. کلمات او [:خدا و قرآن] را هیچ‌گاه تبدیل‌کننده‌ای نیست . جز او [:خدا و کتابش] هرگز پایگاه و پناهگاهی (وحیانی) نتوانی یافت.'),
(2168, 18, 28, 'و با کسانی که پروردگارشان را صبح و شام می‌خوانند (و همواره) خشنودی او را می‌خواهند، خود را شکیبا کن، و هرگز دو دیده‌ات از آنان (سوی دیگران) بر نگردد، حال آنکه زیور زندگی دنیا را بخواهی. و از آن کس که قلبش را از یاد خود غافل ساخته‌ایم و از هوس خود پیروی کرده و (اساس) کارش بر زیاده‌روی بوده است، اطاعت مکن.'),
(2169, 18, 29, 'و بگو: «(تمامی) حق از پروردگار شماست. پس هر کس (حق را) بخواهد باید (به پروردگارش) ایمان آورد و هر که (ناحق را) بخواهد (همو) کافر شود. بی‌گمان ما برای ستمگران آتشی آماده کرده‌ایم که سراپرده‌هایش آنان را در بر گرفته و اگر فریادرسی جویند به آبی، چون ته‌مانده‌ی روغن زیتون جوشان - که چهره‌ها را بریان می‌کند - فریادرسی می‌شوند. چه بد آشامیدنی و چه زشت تکیه‌گاه و آرامش‌گاهی است.»'),
(2170, 18, 30, 'بی‌گمان کسانی که ایمان آورده و کارهای شایسته‌‌(ی ایمان) کرده‌اند، ما به‌راستی پاداش کسی را که کاری را به نیکی انجام داده است تباه نمی‌کنیم.'),
(2171, 18, 31, 'آنانند که باغ‌های ماندگار ویژه‌ی ایشان است، (که) از زیر (درختان)شان نهرها روان است. در آنجا با دستبندهایی از طلا بسی آراسته می‌شوند و جامه‌هایی سبز از حریر نازک و حریر ستبر می‌پوشند و در آن بر تخت‌ها تکیه می‌زنند. چه خوش پاداش و نیکوتکیه‌گاهی است.'),
(2172, 18, 32, 'و برای آنان، دو مرد را مَثَل بزن، که به یکی از آنها دو باغ انگور دادیم و پیرامون آن دو (باغ) را با درختان خرما پوشاندیم، و میان آن دو کشتزاری قرار دادیم؛'),
(2173, 18, 33, 'هر دو باغ خوراکی خود را (به شایستگی) آوردند و از آن چیزی را نکاستند. و میان آن دو باغ نهری شکافتیم.'),
(2174, 18, 34, 'و برای آن ثمری فراوان بود. پس به همراهش - در حالی که با او گفت‌وگو می‌کرد - گفت: «مال من از مال تو بیشتر است و از حیث افراد توانمند (هم) از تو نیرومندترم.»'),
(2175, 18, 35, 'و داخل باغش شد در حالی که بر خویشتن ستمکار بود، (و) گفت: «گمان نمی‌برم این (باغ) هرگز زوال پذیرد.»'),
(2176, 18, 36, '«و گمان (هم) نمی‌کنم که ساعت (قیامت) بر پا باشد و اگر (هم) سوی پروردگارم بازگردانده شوم، همانا بی‌چون جایگاهی نوین (و) بهتر (از این) خواهم یافت.»'),
(2177, 18, 37, 'همراهش - در حالی‌که با او گفت‌وگو می‌کرد - به او گفت: «آیا به آن کس که تو را از خاک (و) سپس از نطفه آفرید (و) آن‌گاه تو را مردی بساخت، کافر شدی‌؟»'),
(2178, 18, 38, 'لیکن ما (می‌گوییم) : «اوست خدا، پروردگار من و هیچ کس را با پروردگارم شریک نمی‌سازم.»'),
(2179, 18, 39, 'و چون داخل باغت شدی، چرا نگفتی: «(تنها) آنچه خدا خواسته (شدنی است). هرگز نیرویی جز به (وسیله‌ی) خدا نیست. اگر مرا از حیث مال و فرزند کمتر از خود می‌بینی،»'),
(2180, 18, 40, '«پس امید است که پروردگارم بهتر از باغ تو به من بدهد و بر باغ تو آفتی از آسمان فرو فرستد، تا به تپه‌ای بی‌گیاه تبدیل گردد.»'),
(2181, 18, 41, '«یا آبش فروکش کند، پس هرگز نتوانی آن را بجویی.»'),
(2182, 18, 42, '(تا به او رسید آنچه باید برسد) و ثمره‌اش فرو گرفته شد، پس برای (از کف دادن) آنچه در آن باغ هزینه کرده بود، دست‌هایش را زیر و رو می‌کرد و برهم می‌زد -در حالی که داربست‌های آن فرو ریخته بود- و (با حسرت) می‌گفت: «ای کاش هیچ کس را شریک پروردگارم نمی‌ساختم.»'),
(2183, 18, 43, 'و او را پس از خدا هیچ گروهی نبود تا یاریش کنند و توانی (هم) نداشت تا از کسی یاری‌گیرنده باشد.'),
(2184, 18, 44, '(سرانجام) در آنجا (همه‌ی) ولایت(ها) در انحصار خدای تمام حق است. اوست بهترین پاداش‌دهنده و (اوست) بهترین فرجام (کار).'),
(2185, 18, 45, 'و برای آنان زندگی دنیا را مَثَل بزن: مانند آبی است که آن را از آسمان فرو فرستادیم‌؛ پس روییدنی زمین با آن درآمیخت. پس (از آن، چنان) خشک گردید که بادها پراکنده‌اش می‌کنند. و خدا همواره بر همه چیزی توانا بوده است.'),
(2186, 18, 46, 'مال و فرزندان، زیور زندگی دنیایند و نیکی‌های ماندگار از نظر پاداش نزد پروردگارت بهتر و از نظر آرزومندی (نیز) بهتر است.'),
(2187, 18, 47, 'و روزی را یاد کن که کوه‌ها را به شدت روان سازیم و زمین را آشکارا (و صاف) ببینی و آنان را گرد (هم) آوردیم، پس هیچ یک را فروگذار نکردیم.'),
(2188, 18, 48, 'و (در آن روز که) ایشان به صف بر پروردگارت عرضه شدند (به آنها می‌فرماید:) «به‌راستی همان گونه که نخستین بار شما را آفریدیم سوی ما آمدید، بلکه پنداشتید هرگز برای شما وعده‌گاهی مقرر نکردیم.»'),
(2189, 18, 49, 'و کتاب (اعمال) گزارده شد. آن‌گاه بزه‌کاران را از آنچه در آن است با بزرگداشتش بیمناک می‌بینی، و (همی) گویند: «ای وای بر ما! این نامه را چه برنامه‌ای است که هیچ کوچک و بزرگی را فرو نمی‌گذارد جز اینکه همه را بر شمرده است!‌» و آنچه را انجام دادند حاضر یافتند و پروردگار تو به هیچ کس ستم روا نمی‌دارد.'),
(2190, 18, 50, 'و چون به فرشتگان گفتیم: «برای (شکر درباره‌ی) آدم (برای‌خدا) سجده کنید.» پس (همه) سجده کردند، جز ابلیس (که) از (گروه) جنیان [:پنهان‌گران] بود. پس، از فرمان پروردگارش سرپیچید. آیا پس (از این) او و نسلش را به جای من اولیای خود بر می‌گیرید؟ حال آنکه آنان دشمنان شمایند. شیطان چه بد بدلی (از رحمان بی‌بدیل) برای ستمگران است.'),
(2191, 18, 51, 'من نه آفرینش آسمان‌ها و زمین را بدیشان نشان دادم و نه آفرینش خودهاشان را. و من (آن) نبوده‌ام که کمک گیرنده از گمراه‌کنندگان باشم.'),
(2192, 18, 52, 'و روزی را که (خدا) می‌گوید: «آنهایی راکه شریکان من پنداشتید، بخوانید». پس آنان را خواندند، و(لی) اجابتشان نکردند، و ما میانشان جایگاه تباهی قرار دادیم.'),
(2193, 18, 53, 'و مجرمان آتش (دوزخ) را دیدند. پس گمان بردند که برخوردکنندگان آنند، و از آن گریزگاهی نیافتند.'),
(2194, 18, 54, 'و به‌راستی در این قرآن، برای مردمان بی‌گمان از هر (گونه) مَثَلی آوردیم، و (اما) انسان بیش از هر چیز (سر) جدال داشته است.'),
(2195, 18, 55, 'و چیزی مانع مردم نشد از اینکه - چون هدایت سوی‌شان آمد - ایمان بیاورند، و از پروردگارشان پوشش بخواهند، بجز اینکه سنت (خدا در مورد عذاب) پیشینیان، درباره‌ی آنان (نیز) به کار رود، یا عذاب رویارویشان بیاید.'),
(2196, 18, 56, 'و (ما) پیامبران (خود) را جز بشارت‌دهندگان و بیم‌دهندگان گسیل نمی‌داریم. و کسانی که کافر شدند، به باطل مجادله می‌کنند، تا به (وسیله‌ی) آن، حق را پایمال نمایند. و نشانه‌های مرا و آنچه را (بدان) بیم داده شده‌اند به ریشخند گرفتند.'),
(2197, 18, 57, 'و کیست ستمکارتر از آن کس که به آیات پروردگارش تذکر داده شده، پس از آن روی برتافته و دستاورد پیشینه‌ی خود را که از پیش فرستاده فراموش کند؟ ما بر دلهایشان پوشش‌هایی سخت قرار دادیم، تا آن را در نیابند و در گوش‌هایشان سنگینی (نهادیم). و اگر آنها را سوی هدایت فراخوانی هرگز راه نیابند.'),
(2198, 18, 58, 'و پروردگارت بسی پوشاننده‌ی کانون رحمت است. اگر به (جرم) دستاوردهاشان، آنان را مؤاخذه کند، بی‌گمان در عذابشان تعجیل می‌نماید. (ولی چنین نمی‌کند) بلکه برای آنها سررسیدی است که هرگز پس از آن راه گریزی نمی‌یابند.'),
(2199, 18, 59, 'و چون آن گروه‌ها بیدادگری کردند، هلاکشان کردیم، و برای هلاکتشان موعدی مقرر داشتیم.'),
(2200, 18, 60, 'و چون موسی به جوان [:دستیار] خود گفت: «از این جا دست‌بردار نیستم تا به محل برخورد دو دریا برسم یا زمان‌هایی سیر کنم.»'),
(2201, 18, 61, 'پس چون به محل برخورد دو (دریا) رسیدند، ماهیشان را فراموش کردند. پس ماهی راه راهوارش را به سراشیبی دریا پیش گرفت .'),
(2202, 18, 62, 'و هنگامی که (از آنجا) گذشتند (موسی) به جوان [:دستیار] خود گفت: «غذایمان را بیاور، به‌راستی ما از این سفر به رنجی بسیار دچار شدیم.»'),
(2203, 18, 63, 'گفت: «آیا دیدی‌؟ چون سوی آن صخره پناه جستیم، من بی‌گمان آن ماهی را فراموش کردم و جز شیطان (کسی) آن را از یادم نبرد، که به یادش باشم، و به گونه‌ای شگفت‌انگیز خود راه راهوارش در دریا را پیش گرفت.»'),
(2204, 18, 64, 'گفت: «این (جریان) همان بود که ما آن را می‌جسته‌ایم.‌» پس جستجوکنان برگشته‌(و) دنبال ردّ پاهایشان را گرفتند.'),
(2205, 18, 65, 'پس بنده‌ای از بندگان ما را یافتند که رحمتی (ویژه) از خود به او دادیم، و از نزد خود بدو دانشی (ویژه) آموختیم.'),
(2206, 18, 66, 'موسی بدو گفت: «آیا تو را بر این مبنا که از دانشی (شایسته) که آموخته شده‌ای به من رشیدانه یاد دهی پیروی کنم؟»'),
(2207, 18, 67, 'گفت: «تو هرگز نتوانی همپای من (در این راه) صبر کنی.»'),
(2208, 18, 68, '«و چگونه می‌توانی بر چیزی که به شناخت آن احاطه‌ای نداری صبر کنی‌؟»'),
(2209, 18, 69, 'گفت: «اگر خدا خواهد مرا شکیبا خواهی یافت، و (در) هیچ کاری (که انجام می‌دهی) تو را نافرمانی نخواهم کرد.»'),
(2210, 18, 70, 'گفت: «اگر مرا پیروی می‌کنی، از چیزی مرا مپرس تا (خود) از آن برایت یادی آغاز کنم.»'),
(2211, 18, 71, 'پس (از آنجا خستند و) رستند تا وقتی که سوار (آن) کشتی شدند. (وی) آن را درید. (موسی) گفت: «آیا کشتی را دریدی تا سرنشینانش را غرق کنی‌؟ بی‌گمان کاری منکر[:نابسامان و ناروا] به بار آوردی.»'),
(2212, 18, 72, 'گفت: «آیا نگفتم تو هرگز نتوانی همپای من صبر کنی‌؟»'),
(2213, 18, 73, '(موسی) گفت: «به (سبب) آنچه فراموش کردم، مرا مؤاخذه مکن، و از (این) کارم، مرا به سختی مینداز.»'),
(2214, 18, 74, 'پس (همچنان خستند و) رستند، تا هنگامی که به نوجوانی برخورد کردند. پس وی او را کشت. (موسی بدو) گفت: «آیا شخص پاکی را- بدون اینکه کسی را به (ناپاکی) کشته باشد- کشتی‌؟ بی‌چون همواره منکری (به بار) آوردی.»'),
(2215, 18, 75, 'گفت: «آیا به تو نگفتم هرگز نتوانی –همواره-  پایاپای من صبری کنی‌؟»'),
(2216, 18, 76, '(موسی) گفت: «اگر از این پس چیزی (درباره‌ی آنچه کردی) از تو بپرسم، دیگر با من همراهی مکن. از سوی من بی‌چون به عذری رسیده‌ای.»'),
(2217, 18, 77, 'پس (از اینجا هم خستند و) رستند تا هنگامی که به اهل قریه‌ای رسیدند. از مردم با اهلیَتَش خوراکی خواستند (ولی) آنها از مهمان نمودن آن دو خودداری کردند. پس در آن‌جا دیواری یافتند که می‌خواست فرو ریزد. آن را استوار کرد. (موسی) گفت: «اگر می‌خواستی بی‌چون بر (کارکرد) آن مزدی می‌گرفتی.»'),
(2218, 18, 78, 'گفت: «این (بار، دیگر وقت) جدایی میان من و تو است. تو را از تأویل [:دستاورد حقیقی و پنهان] آنچه که نتوانستی بر آن صبری کنی آگاهی مهمی خواهم داد.»'),
(2219, 18, 79, '«اما کشتی؛ از بینوایانی بود که در دریا کار می‌کردند، پس خواستم آن را معیوب کنم. حال آنکه از پشت آنان پادشاهی بود که هر کشتی (سالمی) را به زور می‌گرفت.»'),
(2220, 18, 80, '«و اما (آن) نوجوان؛ پدر و مادرش مؤمن بودند، پس ترسیدیم (مبادا) آن دو را ناخواسته و به‌ناچار به طغیان و کفر بکشد. »'),
(2221, 18, 81, '«پس خواستیم که پروردگارشان آن دو را به پاکیزه‌تر و رحمت‌وارتر از او عوض دهد.»'),
(2222, 18, 82, '«و اما دیوار؛ پس از آنِ دو پسر (بچه‌ی) یتیم در آن شهر بود و زیر آن، گنجی متعلق به آن دو بود و پدرشان (مردی) شایسته بود. پس پروردگارت خواست آن دو (یتیم) به حدّ رشدهاشان رسند و گنجینه‌ی خود را – به رحمت پروردگارت – با کاوش بیرون آورند. و این (کارها) را من خودسرانه انجام ندادم. این بود تأویل آنچه که نتوانستی بر آن شکیبایی ورزی.»'),
(2223, 18, 83, 'و از تو درباره‌ی ذوالقرنین می‌پرسند. بگو: «از او برای شما یادی خواهم نمود.»'),
(2224, 18, 84, 'ما بی‌گمان در زمین برایش امکان [:توان] نهادیم، و از هر چیزی وسیله‌ای به او دادیم'),
(2225, 18, 85, 'تا وسیله‌ای (ربانی) را [:از] پی [:خود] آورد.'),
(2226, 18, 86, 'تا آن‌گاه که به غروب‌گاه خورشید رسید (و) آن را (چنان) یافت که در چشمه‌ای گل‌آلود و سیاه غروب می‌کند. و نزد آن طایفه‌ای را یافت. گفتیم: «ذوالقرنین! (اختیار با توست) یا عذاب(شان) کنی، یا در میانشان (روش) نیکویی پیش گیری.»'),
(2227, 18, 87, 'گفت: «اما هر کس ستم کرده، در آینده‌ای دور عذابش خواهم کرد. سپس سوی پروردگارش بازگردانیده می‌شود. آن‌گاه او را عذابی منکرخواهد کرد.»'),
(2228, 18, 88, '«و اما هر کس ایمان آورد و کار شایسته‌ای کرد، پس برایش پاداشی نیکوتر است، و زودا (که) از کارمان برایش (جریان) آسانی خواهیم گفت.»'),
(2229, 18, 89, 'سپس وسیله‌ای (دیگر) را از پی خود در آورد'),
(2230, 18, 90, 'تا آنگاه که به جایگاه بر آمدن خورشید رسید (و) آن را (چنان) یافت که بر قومی طلوع می‌کرد، که برایشان در برابر آن پوششی قرار ندادیم.'),
(2231, 18, 91, 'این چنین (می‌رفت) در حالی‌که به آنچه نزد او بود همانا احاطه‌ی علمی داشتیم.'),
(2232, 18, 92, 'سپس وسیله‌ای (دیگر) را [:از] پی [:خود] آورد.'),
(2233, 18, 93, 'تا وقتی به میان آن دو سدّ رسید (و) در برابر آن دو، طایفه‌ای را یافت که نزدیک نیستند (تا) هیچ سخنی (شایسته‌ی تفکر) را بفهمند.'),
(2234, 18, 94, 'گفتند: «ذوالقرنین! یأجوج و مأجوج سخت در زمین افساد کننده‌اند. پس آیا خرجی‌ای در اختیار تو قرار دهیم تا میان ما و آنان سدّی قرار دهی‌؟»'),
(2235, 18, 95, 'گفت: «آنچه پروردگارم به من در آن تمکّن داده (از کمک مالی شما) بهتر است. (تنها) مرا با نیرویی (جسمانی) یاری کنید تا میان شما و آنان سدی آهنین قرار دهم.»'),
(2236, 18, 96, '«برایم قطعات بزرگ آهن بیاورید.» (آوردند) تا آن‌گاه که میان دو قله‌ی کوه برابر شد. گفت: «بدمید تا وقتی که آن قطعات را آتش گردانید.» (پس) گفت: «مس گداخته برایم بیاورید تا روی آن بریزم.»'),
(2237, 18, 97, 'پس [:یأجوج و مأجوج] نتوانستند از آن بالا بروند و (هم) نتوانستند آن را سوراخی کنند.'),
(2238, 18, 98, '(ذوالقرنین) گفت: «این رحمتی بزرگ از (جانب) پروردگار من است. پس هنگامی‌که وعده‌ی پروردگارم فرا رسد، آن سدّ را از هم فرو پاشد. و وعده‌ی هراسناک پروردگارم حق بوده است.»'),
(2239, 18, 99, 'و (در آن روز) آنان را رها کنیم، تا موج‌آسا بعضی با بعضی دیگر درآمیزند، و در صور [:بوق جان‌افزا] دمیده شود. پس همه‌ی آنها را گرد (هم) آوریم.'),
(2240, 18, 100, 'و آن روز، جهنم را آشکارا به کافران بنمایانیم.'),
(2241, 18, 101, '(به) همانان که چشمان (بصیرت)شان از یاد من غرق در پرده‌ای (ضخیم) بود، و هرگز توان شنیدن (حق) را نداشتند.'),
(2242, 18, 102, 'آیا پس کسانی که کفر ورزیدند، پنداشتند که به جای من، بندگانم را سرپرست برگیرند؟ ما بی‌گمان جهنم را برای کافران آماده کرده‌ایم.'),
(2243, 18, 103, 'بگو: «آیا شما را از زیانکارترین مردم آگاه گردانیم‌؟»'),
(2244, 18, 104, 'کسانی که کوشش‌شان در (ژرفای) زندگی دنیا گم گشته، حال آنکه می‌پندارند کاری خوب انجام می‌دهند.'),
(2245, 18, 105, 'ایشان کسانی‌اند که آیات پروردگارشان و لقای او را انکار کردند؛ در نتیجه اعمالشان تباه گردید. پس روز قیامت برایشان هیچ وزنی (:میزانی) به پا نخواهیم داشت.'),
(2246, 18, 106, 'این (جهنم) سزای آنان است، در پیامد (اینکه) کافر شدند، و آیات من و پیامبرانم را به مسخره گرفتند.'),
(2247, 18, 107, 'بی‌گمان کسانی که ایمان آورده و کارهای شایسته‌(ی ایمان) کردند، باغ‌های سردرهم فردوس جایگاه پذیرایی آنان بوده است.'),
(2248, 18, 108, 'جاودانه در آن خواهند بود (و) از آنجا درخواست انتقال و تحولی (به جایی دیگر) نکنند.'),
(2249, 18, 109, 'بگو: «اگر دریا برای نبشتن کلمات پروردگارم مرکّب بود، پیش از آنکه کلمات پروردگارم پایان پذیرد، بی‌گمان دریا پایان می‌یافت هر چند نظیرش را به مدد(ش) بیاوریم.»'),
(2250, 18, 110, 'بگو: «من تنها بشری همانند شمایم. (اما) به من وحی می‌شود که خدای شما خدایی یگانه است. پس هر کس به لقای پروردگار خود امید دارد، باید کاری شایسته کند و هیچ کس را، در پرستش پروردگارش (با او) شریک نسازد.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کهیعص‌'),
(2252, 19, 2, '(این) یادی است از رحمت پروردگارت، بنده‌اش زکریا را.'),
(2253, 19, 3, 'چون پروردگارش را ندا (و آوا)یی پنهانی در داد.'),
(2254, 19, 4, 'گفت: «پروردگارم! من استخوانم به‌راستی سست گردیده و (موی) سرم از پیری شعله‌ور (و سپید) شده. پروردگارم! من هرگز به درخواست (از) تو سنگدل نبوده‌ام.»'),
(2255, 19, 5, '«و بی‌گمان من از پی‌آمدگان پس از خودم بیم داشتم و زنم نازا بوده است. پس از جانب خودت ولیّ و دنباله‌روی (از رسالتم) بنه»'),
(2256, 19, 6, '«(که) از من ارث برد و از خاندان یعقوب (نیز) ارث برد. و پروردگارم! او را پسندیده(ی خود) گردان. »'),
(2257, 19, 7, '(خدا فرمود:) «زکریا! ما همانا به پسری - که نامش یحیی است - مژده‌ات می‌دهیم (که) از پیش همنامی برایش قرار ندادیم.»'),
(2258, 19, 8, 'گفت: «پروردگارم! چه زمانی (و چگونه) مرا پسری خواهد بود؟ حال آنکه زنم نازا بوده، و من از سالخوردگی به‌راستی به ناتوانی و ناچاری رسیده‌ام‌؟»'),
(2259, 19, 9, '(خدا) فرمود: « (فرمان) چنان است. پروردگارت گفت که این (کار) بر من آسان است. و همانا تو را از پیش آفریدم حال آنکه چیزی نبودی.»'),
(2260, 19, 10, 'گفت: «پروردگارم! نشانه‌ای برایم بنه‌.» فرمود: «نشانه‌ی تو این است که سه شب -حال آنکه سلامتی- با مردم سخن نگویی.»'),
(2261, 19, 11, 'پس، از محراب بر قوم خویش در آمد، و ایشان را رمز داد که: «صبح و شام به نیایش (خدا) بپردازید.»'),
(2262, 19, 12, '(به یحیی وحی شد:) «‌یحیی! (آن) کتاب (وحیانی) را با نیرویی (فزاینده) برگیر.» و در حال کودکی حکم (رسالتی را) به او دادیم.'),
(2263, 19, 13, 'و (نیز) از جانب ویژه‌ی خود، مهربانی و پاکی را (به او دادیم) و او پرهیزگار بوده است.'),
(2264, 19, 14, 'و با پدر و مادر خود نیک‌رفتار بود و زورگویی نافرمان نبود.'),
(2265, 19, 15, 'و درودی (شایسته) بر اوست، روزی که زاده شد، و روزی که می‌میرد، و روزی (هم) - در حالی‌که زنده است - برای رستاخیز برانگیخته می‌شود.'),
(2266, 19, 16, 'و درکتاب، مریم را یاد کن، چون از کسانِ خود، به مکانی شرقی، به کناری افکنده شد.'),
(2267, 19, 17, 'پس پرده‌ای میان خود و آنان برگرفت، تا روح ویژه‌ی خودمان [:جبرئیل] را سوی او فرستادیم. پس برای مریم به چهره‌ی بشری خوش‌اندام نمایان شد.'),
(2268, 19, 18, '(مریم) گفت: «اگر پرهیزگار بوده‌ای، به‌راستی من از تو به خدای رحمتگر بر آفریدگان پناه می‌برم.»'),
(2269, 19, 19, 'گفت: «من - تنها - فرستاده‌ی پروردگار تو هستم، برای اینکه برایت پسری پاک ببخشایم.»'),
(2270, 19, 20, '(مریم) گفت: «کی و کجا مرا پسری باشد، حال آنکه بشری با من تماس نگرفته و ناپاک (هم) نبوده‌ام‌؟»'),
(2271, 19, 21, '(جبرئیل) گفت: « (فرمان) چنان است - پروردگارت گفته که آن بر من آسان است- و تا او را نشانه‌ای برای مردمان، و رحمتی از جانب خودمان قرار دهیم و (این) کاری گذرا بوده است.»'),
(2272, 19, 22, 'پس مریم او [:عیسی] را بار گرفت. پس با وی به مکان دور افتاده‌ای افکنده شد.'),
(2273, 19, 23, 'پس درد زایمان او را سوی شاخه‌ی درخت خرمایی رسانید. گفت: «ای کاش پیش از این مرده و فراموش‌شده بودم.»'),
(2274, 19, 24, 'پس (مسیح) از زیر (پای) او [:مریم] وی را ندا داد: «غم مدار، همانا پروردگارت زیر پایت (شخصی) سربلند و والا مقام نهاده است.»'),
(2275, 19, 25, '«و شاخه‌ی درخت خرما را سوی خود (برگیر و) به شدت بتکان، تا بر تو خرمایی تازه فرو ریزد.»'),
(2276, 19, 26, '«پس بخور و بنوش و دیده‌ای روشن دار. پس اگر به‌راستی کسی از آدمیان را دیدی بگوی “من بی‌گمان برای (خدای) رحمان روزه‌ای نذر کرده‌ام، پس امروز هرگز با هیچ انسانی سخن نخواهم گفت.”»'),
(2277, 19, 27, '«پس (مریم) در حالی که او را می‌برد نزد قومش آورد. گفتند: «مریم! همانا به‌راستی چیزی افتراانگیز آورده‌ای.»'),
(2278, 19, 28, '«ای خواهر هارون! پدرت مرد بدی نبوده و مادرت (نیز) بدکاره نبوده است.»'),
(2279, 19, 29, 'پس (مریم) سوی عیسی اشاره کرد. گفتند: «چگونه با کسی که در گهواره، کودک بوده سخن بگوییم‌؟»'),
(2280, 19, 30, '(کودک) گفت: «همانا من بنده‌ی خدایم، به من کتاب داد و مرا پیامبری برجسته نهاد. »'),
(2281, 19, 31, '«و هر جا که بوده‌ام مرا برکت‌یافته ساخت و تا زنده‌ام به نماز و زکات سفارشم کرد.»'),
(2282, 19, 32, '«و (مرا) نسبت به مادرم نیکوکار نمود. و زورگو و سخت‌دلم نگردانید.»'),
(2283, 19, 33, '«و سلام (و سلامتی) بر من، روزی که زاده شدم، و روزی که می‌میرم، و روزی (هم) -در حالی‌که (در برزخ) زنده‌ام- برانگیخته می‌شوم.»'),
(2284, 19, 34, 'این است عیسی پسر مریم؛ گفته‌ی حقی که در آن دو دلی می‌کنند.'),
(2285, 19, 35, 'برای خدا هرگز (شایسته) نبوده است که هیچ فرزندی برگزیند. (او) بسی منزّه است‌. چون چیزی را اراده کند، تنها به آن می‌گوید: «شو»، پس (بی‌درنگ) می‌شود.'),
(2286, 19, 36, 'و به‌راستی، خدا پروردگار من و پروردگار شماست، پس او را بپرستید (که) این راهی است راست.'),
(2287, 19, 37, 'پس آن گروه‌ها(ی گوناگون) از میانشان به اختلاف پرداختند. پس وای برای کسانی که کافر شدند از حضور (در) دیدگاه روزی بزرگ.'),
(2288, 19, 38, '(ببین) چه شنوا و بینایند روزی که سوی ما می‌آیند! ولی ستمگران امروز در گمراهی آشکارگرند.'),
(2289, 19, 39, 'و آنان را (از) آن روز حسرت بیم ده، چون داوری انجام گرفت. حال آنکه آنان در (ژرفای) غفلتند و آنان ایمان نمی‌آورند.'),
(2290, 19, 40, 'بی‌گمان ما ،(همین) ما زمین را -با هر که بر آن است- به میراث می‌بریم، و تنها سوی ما بازگردانیده می‌شوند.'),
(2291, 19, 41, 'و در (این) کتاب ابراهیم را یاد کن. به‌راستی او بسی راست (و) پیامبری برجسته بود.'),
(2292, 19, 42, 'چون به پدرش گفت: «پدرم! چرا چیزی را که نمی‌شنود و نمی‌بیند و تو را از چیزی بی‌نیاز نمی‌کند می‌پرستی‌؟»'),
(2293, 19, 43, '«پدرم! به‌راستی مرا به‌درستی از دانش وحی، حقایقی آمده که تو را نیامده. پس مرا پیروی کن، تا تو را به راهی راست (بدون افراط و تفریط) هدایت کنم.»'),
(2294, 19, 44, '«پدرم! شیطان را نپرست. شیطان بی‌گمان بر (خدای) رحمتگر بر آفریدگان عصیانگر بوده است.»'),
(2295, 19, 45, '«پدرم! من بسی می‌ترسم که از جانب (خدای) رحمان عذابی به تو در رسد، پس تو دنباله‌رو شیطان باشی.»'),
(2296, 19, 46, '(آزر) گفت: «ابراهیم! آیا تو از خدایان من رویگردانی‌؟ هر آینه اگر (به این رویگردانی) پایان ندهی، بی‌چون تو را به‌راستی سنگسار خواهم کرد. (برو) و مدتی طولانی از من دور شو.»'),
(2297, 19, 47, '(ابراهیم) گفت: «درود بر تو! به زودی از پروردگارم برایت پوشش می‌خواهم، او همواره نسبت به من پُر مهر و لطف بوده است.»'),
(2298, 19, 48, '«و از شما و (از) آنچه غیر از خدا می‌پرستید کناره می‌گیرم، و خدایم را می‌خوانم، و از او درخواست کنم؛ امید که به خواندن و درخواست از پروردگارم سخت‌دل نباشم.»'),
(2299, 19, 49, 'پس هنگامی که از آنها و (از) آنچه به جای خدا می‌پرستیدند کناره گرفت، اسحاق و یعقوب را به او بخشیدیم، و هر یک را پیامبری برجسته گردانیدیم.'),
(2300, 19, 50, 'و برخی از رحمت خویش را به آنان بخشیدیم، و زبانی راستین (و) بلندمرتبه برایشان قرار دادیم.'),
(2301, 19, 51, 'و در کتاب، موسی را یاد کن (که) همواره او پاک‌شده‌ای (ربانی) و (نیز) فرستاده‌ای برجسته بود.'),
(2302, 19, 52, 'و از پربرکت‌ترین جانب (کوه) طور، او را ندا در دادیم. و در حالی‌که با وی راز می‌گفتیم، او را نجات‌یافته(به خود) نزدیک ساختیم.'),
(2303, 19, 53, 'و به رحمت خویش برادرش هارون - پیامبری برجسته- را به او بخشیدیم.'),
(2304, 19, 54, 'و در کتاب، اسماعیل را یاد کن، همانا او راست‌وعده و فرستاده‌ای برجسته بود'),
(2305, 19, 55, 'و خاندان خود را به نماز و زکات فرمان می‌داد. و نزد پروردگارش پسندیده بود.'),
(2306, 19, 56, 'و در کتاب، ادریس را یاد کن. او بسی راست(کار و) پیامبری برجسته بود'),
(2307, 19, 57, 'و (ما) او را به جایی بلند [:معراج] بالا بردیم.'),
(2308, 19, 58, 'ایشان کسانی از پیامبران برجسته‌اند-که خدا بر ایشان نعمت ارزانی داشت- از دودمان آدم و از کسانی که با نوح (در کشتی) سوار(شان) کردیم و (نیز) از دودمان ابراهیم و اسرائیل و از کسانی که (آنان را) هدایت نمودیم و برگزیدیم‌؛ هنگامی که آیات (خدای) رحمان بر ایشان خوانده شود، سجده‌کنان و گریان به خاک فرو افتند.'),
(2309, 19, 59, 'پس بعد از آنان جانشینانی ناپسند بر جای ماندند (که) نماز را ضایع ساخته و از هوس‌ها پیروی کردند. پس در آینده‌ای دور گمراهی را (از روی اعتقاد فاسد) خواهند یافت.'),
(2310, 19, 60, 'مگر کسانی که توبه کرده و ایمان آورده و کاری شایسته(ی ایمان) انجام دادند. پس به بهشت در آیند و هیچ ستمی نخواهند دید:'),
(2311, 19, 61, 'باغ‌های جاودانه که (خدای) رحمان بندگانش را (به آن‌ها) در غیب وعده داده است. بی‌گمان وعده‌ی او آمدنی بوده است.'),
(2312, 19, 62, 'در آنجا سخن بیهوده‌ای نمی‌شنوند، جز سلامی، و روزیشان برایشان صبح و شام در آنجا (آماده) است.'),
(2313, 19, 63, 'آن (بزرگْ مکان) همان بهشتی است که هر یک از بندگانمان را که پرهیزگار بوده‌اند به میراث می‌دهیم.'),
(2314, 19, 64, 'و (ما فرشتگان) جز به امر پروردگارت نازل نمی‌شویم. آنچه پیش روی ما و آنچه پشت سر ما و آنچه میان این دو است (همه) تنها برای اوست و پروردگارت هرگز فراموشکار نبوده است.'),
(2315, 19, 65, 'پروردگار آسمان‌ها و زمین و آنچه میان آن دو است. پس او را بپرست و برای پرستش او بسی شکیبا باش. آیا برای او(همتا و) همنامی می‌شناسی‌؟'),
(2316, 19, 66, 'و انسان می‌گوید: «آیا هنگامی‌که بمیرم، به‌راستی در آینده‌ای دور زنده (از قبر) بیرون آورده می‌شوم‌؟»'),
(2317, 19, 67, 'آیا و (این) انسان به یاد نمی‌آورد (که) ما او را به‌درستی از پیش آفریدیم. حال آنکه (هرگز) چیزی نبوده است‌؟'),
(2318, 19, 68, 'پس، به پروردگارت سوگند (که) همانا آنان را بی‌گمان با شیطان‌‌ها-بدون برگشت- گردهم می‌آوریم؛ سپس در حالی که به زانو در آمده‌اند، بی‌گمان آنان را بی‌چون گرداگرد دوزخ - به پای درافتاده - حاضر خواهیم کرد.'),
(2319, 19, 69, 'سپس از هر دسته‌ای، کسانی از آنان را که بر (خدای) رحمان سرکش‌ترند همانا به‌راستی بیرون خواهیم کشید.'),
(2320, 19, 70, 'سپس به کسانی که برای آتش‌افروزی جهنم سزاوارترند، همانا خود داناتریم.'),
(2321, 19, 71, 'و هیچ کس از شما نیست مگر (اینکه) در آن وارد است. (این) بر عهده‌ی پروردگارت حکمی قطعی بوده است.'),
(2322, 19, 72, 'سپس کسانی را که پرهیزگاری می‌کرده‌اند می‌رهانیم، و ستمگران را در حالی که به زانو در افتاده‌اند در دوزخ وا می‌گذاریم.'),
(2323, 19, 73, 'و هنگامی که آیات ما -روشنگرانه- بر آنان خوانده شود، کسانی که کفر ورزیده‌اند، به آنان که ایمان آورده‌اند گویند: «کدام یک از (ما) دو گروه جایگاهش بهتر و جلسه‌ی اُنسش نیکوتر است‌؟»'),
(2324, 19, 74, 'و چه بسیار نسل‌ها را پیش از آنان نابود کردیم، (که) آنها اثاثی نیکوتر و ظاهری فریباتر داشتند.'),
(2325, 19, 75, 'بگو: «هر کس در (ژرفای) گمراهی بوده است، پس باید (خدای) رحمان به او تا زمانی مهلت دهد، تا هنگامی که آنچه به آنان وعده داده می‌شود ببینند، یا عذاب را، یا ساعت [:قیامت] را. پس به زودی خواهند دانست جایگاه چه کسی بدتر و سپاهش ناتوان‌تر است.»'),
(2326, 19, 76, 'و خدا کسانی را که هدایت‌یافته‌اند بر هدایتشان می‌افزاید. و ماندنی‌های شایسته(ی ایمان)، نزد پروردگارت از حیث پاداش بهتر و بازگشتشان خوبتر است.'),
(2327, 19, 77, 'پس آیا دیدی کسی را که به آیات ما کفر ورزید؟ و گفت: «هر آینه بی‌گمان به من مال و فرزندی (بسیار) داده خواهد شد.»'),
(2328, 19, 78, 'آیا بر غیب آگاه شده، یا نزد (خدای) رحمان پیمانی بر گرفته است‌؟'),
(2329, 19, 79, 'هرگز! (چنان نیست). به زودی آنچه را می‌گوید، ثبت و ضبط می‌کنیم. و برخی از عذاب را برای او امتدادی مستمر خواهیم داد.'),
(2330, 19, 80, 'و آنچه را می‌گوید، از او به ارث می‌بریم، و سوی ما به تنهایی خواهد آمد.'),
(2331, 19, 81, 'و بجز خدا، معبودانی برگرفتند، تا برای آنان (مایه‌ی) عزت باشند.'),
(2332, 19, 82, 'هرگز نه چنان است. به زودی (آن معبودان) عبادت ایشان را انکار (و‌انگار) می‌کنند، و بر ضدّشان خواهند بود.'),
(2333, 19, 83, 'آیا ندیدی که همواره ما شیطان‌ها را بر (سر و سامان) کافران فرستادیم، حال آنکه آنان را به جنبش و جوشش می‌آوردند؟'),
(2334, 19, 84, 'پس بر ضد آنان شتاب مکن، (که) فقط برایشان (روز)شماری بس دقیق می‌کنیم.'),
(2335, 19, 85, 'روزی که پرهیزگاران را سوی (خدای) رحمان (به صورت) گروهی جمع می‌کنیم'),
(2336, 19, 86, 'و مجرمان را با تشنگی سوزان (و) سرگردانی سوی دوزخ می‌رانیم.'),
(2337, 19, 87, 'آنان دارای شفاعت نیستند، جز آن کس که نزد (خدای) رحمان پیمانی برگرفته است.'),
(2338, 19, 88, 'و گفتند: « (خدای) رحمان فرزندی بر گرفته.»'),
(2339, 19, 89, 'بی‌گمان چیز زشتی را همانا (بر زبان) آوردید.'),
(2340, 19, 90, 'نزدیک است آسمان‌ها از آن (سخن) بی‌مهابا بشکافند، و زمین چاک خورَد و کوه‌ها به سختی با صدایی جانکاه فرو ریزند،'),
(2341, 19, 91, '(از) اینکه برای (خدای) رحمان فرزندی (را) خواندند.'),
(2342, 19, 92, 'و (خدای) رحمان را هرگز نسزد که فرزندی برگیرد.'),
(2343, 19, 93, 'هیچ کس در آسمان‌ها و زمین نیست جز اینکه در حالت بندگی نزد (خدای) رحمان آینده است.'),
(2344, 19, 94, 'همواره بی‌گمان آنان را اِحاطه و شماره‌ای ممتاز کرده است.'),
(2345, 19, 95, 'و روز قیامت همه‌ی آنها تنها، سوی او آینده‌اند.'),
(2346, 19, 96, 'بی‌گمان کسانی که ایمان آورده و عمل شایسته(ی ایمان) کرده‌اند، (خدای) رحمان به زودی برایشان دوستی‌ای خواهد نهاد.'),
(2347, 19, 97, 'پس تنها ما این قرآن را به زبان تو آسان(فهم) کردیم، تا پرهیزگاران را نوید، و گروهی سخت‌دل را هشدار دهی.'),
(2348, 19, 98, 'و چه بسیار از نسل‌هایی پیش از اینان را هلاک کردیم. آیا احدی از ایشان را (اکنون) احساس می‌کنی، و یا صدا و آوایی از آنان می‌شنوی؟');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طه [:ای طاهر هادی!].'),
(2350, 20, 2, 'ما قرآن را بر تو نازل نکردیم تا به رنج افتی،'),
(2351, 20, 3, 'جز اینکه برای هر که (از خدا) می‌هراسد یادواره‌ای (بزرگ) باشد.'),
(2352, 20, 4, 'در حالی که فرود آمده‌ای است به تدریج از جانب کسی که زمین و آسمان‌های بلند را آفریده است.'),
(2353, 20, 5, '(خدای) رحمتگر بر آفریدگان بر عرش (ربوبیت ) چیره شده است.'),
(2354, 20, 6, 'آنچه در آسمان‌ها و آنچه در زمین و آنچه میان آن دو و آنچه زیر خاک است از اوست.'),
(2355, 20, 7, 'و اگر سخن آشکار گویی پس او نهان و نهان‌تر را می‌داند.'),
(2356, 20, 8, 'خدایی که جز او معبودی نیست (و) نیکوترین نام‌ها (ونشانه‌ها) ویژه‌ی اوست.'),
(2357, 20, 9, 'و آیا گزارش (وحیانی) موسی تو را رسید؟'),
(2358, 20, 10, 'چون آتشی دید، پس به خانواده‌ی خود گفت: «درنگ کنید، همانا من با آتشی مأنوس شدم. امید که شعله‌ای از آن برایتان بیاورم یا در پرتو آتش راهی بیابم.»'),
(2359, 20, 11, 'پس چون بدان رسید ندا داده شد: «موسی!»'),
(2360, 20, 12, '«بی‌گمان منم، من، پروردگارت، پس دو پای‌پوشت را برکن، که در حقیقت تو پیچیده در پوشش وادی مقدسی.»'),
(2361, 20, 13, '«و من تو را (برای خودم) برگزیدم. پس برای آنچه (به تو) وحی می‌شود گوش فرادار.»'),
(2362, 20, 14, '«(این) منم، من، (همان) خدایی که جز من خدایی نیست. پس مرا بپرست و برای یادم نماز را بر پا بدار.»'),
(2363, 20, 15, '«به‌راستی ساعت [:قیامت] آمدنی است. نزدیک است آن را (بر خود هم) پوشیده دارم، تا هر کسی به (موجب) آنچه می‌کوشد جزا یابد.»'),
(2364, 20, 16, '«پس هرگز نکند کسی که به آن ایمان ندارد و از هوس خویش پیروی کرده است، تو را از (ایمان به) آن باز دارد، پس سقوط کنی.»'),
(2365, 20, 17, '«و ای موسی! این چیست در دست راستت؟»'),
(2366, 20, 18, 'گفت: «این عصای من است. بر آن تکیه می‌دهم و با آن بر گوسفندانم (برگ) می‌تکانم، و در آن برای من نیازهای مهم دیگری (نیز) هست.»'),
(2367, 20, 19, 'فرمود: «موسی! (هم) آن را بیفکن.»'),
(2368, 20, 20, 'پس آن را افکند. پس ناگهان، (هم)آن ماری شد که به سرعت می‌خزد.'),
(2369, 20, 21, 'فرمود: «آن را برگیر و مترس، به زودی آن را به حال واقعی نخستین‌(اش) باز می‌گردانیم.»'),
(2370, 20, 22, '«و دست خود را به پهلویت ببر تا سپیدی بی‌گزند بر آید. حال آنکه (این) نشانه‌ای دیگر (بر رسالت تو) است،»'),
(2371, 20, 23, '«برای اینکه به تو از بزرگ‌ترین نشانه‌های (چشمگیر)‌مان بنماییم.»'),
(2372, 20, 24, '«سوی فرعون برو که همواره او به سرکشی برخاسته است.»'),
(2373, 20, 25, 'گفت: «پروردگارم! سینه‌ام را گشاده گردان،»'),
(2374, 20, 26, '«و کارم را برایم آسان گردان،»'),
(2375, 20, 27, '«و از زبانم گرهی بگشای»'),
(2376, 20, 28, '«تا سخنم را با بررسی بفهمند،»'),
(2377, 20, 29, '«و برایم دستیاری از کسانم قرار ده:»'),
(2378, 20, 30, '«هارون برادرم را.»'),
(2379, 20, 31, '«پشتم را به او استوار گردان،»'),
(2380, 20, 32, '«و او را در کارم شریک کن،»'),
(2381, 20, 33, '«تا تو را فراوان تسبیح کنیم،»'),
(2382, 20, 34, '«و بسیار تو را یاد کنیم،»'),
(2383, 20, 35, '«بی‌گمان تو همواره به (حال) ما بسی بینا بوده‌ای.»'),
(2384, 20, 36, 'فرمود: «موسی! به‌راستی خواسته‌ات به تو داده شد.»'),
(2385, 20, 37, '«و همواره به‌راستی بار دیگر (هم) بر تو منت نهادیم،»'),
(2386, 20, 38, '«چون به مادرت آنچه را که وحی می‌شود وحی کردیم:»'),
(2387, 20, 39, '«که او را در صندوق بیفکن، پس در دریایش بینداز. پس (آنگاه) دریا بایستی او را به کرانه و کناره افکند تا دشمنی برای من و دشمنی برای وی، او را برگیرد. و مهری (بزرگ) از خودم بر تو افکندم. و تا زیر نظر من سازمان (و سامان) یابی.»'),
(2388, 20, 40, '«چون خواهرت می‌رود پس می‌گوید: آیا شما را بر کسی که او را کفالت کند دلالت کنم‌؟ پس تو را سوی مادرت بازگردانیدیم تا دیده‌اش (با دیدار تو) روشن شود و غم نخورد. و شخصی را کُشتی. پس (ما) تو را از اندوه رهانیدیم، و تو را بارها (به) آزمایش‌هایی آتشبار آزمودیم. پس سالیانی چند در میان اهل مَدْیَن ماندی. سپس، موسی! بر (زمانی) مقدر (و مقتضی برای رسالت) آمدی.»'),
(2389, 20, 41, '«و تو را برای خود به شایستگی همی ساختم.»'),
(2390, 20, 42, '«تو و برادرت با نشانه‌های (رسالتی) من برو، و در یاد کردن من سستی مکنید.»'),
(2391, 20, 43, '«سوی فرعون بروید که او همواره به سرکشی برخاسته‌.»'),
(2392, 20, 44, '«پس برایش سخنی نرم بگویید، شاید تذکر یابد یا بهراسد.»'),
(2393, 20, 45, '(آن دو) گفتند: «پروردگارمان! ما بی‌گمان می‌ترسیم که (او) در زیان رساندن بر ما زیاده‌روی کند یا که سرکشی نماید.»'),
(2394, 20, 46, 'فرمود: «مترسید. من همواره با شمایم، می‌شنوم و می‌بینم.»'),
(2395, 20, 47, '«پس سوی او بروید و بگویید: “ما به‌راستی دو فرستاده‌ی پروردگار توایم. پس فرزندان اسرائیل را با ما بفرست و عذابشان مکن. به‌راستی ما برای تو از جانب پروردگارت نشانه‌ای (رسالتی)آورده‌ایم، و سلام (و سلامتی) بر کسی است که هدایت را پیروی کند.”»'),
(2396, 20, 48, '«به‌راستی سوی ما وحی شده که عذاب محققاً بر کسی است که (حق را) تکذیب کرد و (از آن) روی گردانید.»'),
(2397, 20, 49, '(فرعون) گفت: «موسی! پس پروردگار شما دوتن کیست‌؟»'),
(2398, 20, 50, 'گفت: «پروردگار ما کسی است که آفرینش هر چیزی را بدو بخشود؛ سپس (آن را) هدایت فرمود. »'),
(2399, 20, 51, 'گفت: «پس نسل‌های گذشته را چه خاطره‌ی مهمی بود (که کافر بوده‌ا‌ند)؟»'),
(2400, 20, 52, 'گفت: «علمش در کتابی (ربانی) نزد پروردگار من است. پروردگارم نه گمراه می‌شود و نه فراموش می‌کند.»'),
(2401, 20, 53, '(همان) کسی که زمین را برایتان گهواره‌ای ساخت و برای شما در آن راه‌هایی (راهوار) گشود و از آسمان آبی فرود آورد. پس با آن رستنی‌های گوناگون، بیرون آوردیم.'),
(2402, 20, 54, 'بخورید و دام‌هایتان را بچرانید. بی‌گمان در اینها برای بازدارندگان عاقلانه از زشتی‌ها، نشانه‌هایی است.'),
(2403, 20, 55, 'از این (زمین) شما را آفریدیم و در آن شما را باز می‌گردانیم و بار دیگر شما را از آن بیرون می‌آوریم.'),
(2404, 20, 56, 'و بی‌چون به‌راستی (ما) همه‌ی آیات (متناسب)مان را به او [:فرعون] نشان دادیم، پس (آنها را) تکذیب نمود و (از پذیرفتنشان) خودداری کرد.'),
(2405, 20, 57, 'گفت: «موسی! آیا نزد ما آمده‌ای تا با سحرت ما را از سرزمینمان بیرون برانی‌؟»'),
(2406, 20, 58, 'پس ما همانا برای تو به‌راستی سحری همانند آن خواهیم آورد. پس میان ما و خودت موعدی بگذار که نه ما از آن تخلف کنیم، و نه تو: در مکانی برابر و یکسان.»'),
(2407, 20, 59, '(موسی) گفت: «موعد شما روز آرایش (و جشن) عمومی است، و اینکه مردم در روشنایی روز گردآورده شوند.»'),
(2408, 20, 60, 'پس فرعون پشت کرد. پس نیرنگ خود را گرد آورد. سپس (باز) آمد.'),
(2409, 20, 61, 'موسی به آنان گفت: «وای بر شما! به خدا دروغ نبندید که شما را به عذابی سخت از بن برمی‌کند. و هر که (به خدا) افترا زد بی‌گمان زیان کرده است.»'),
(2410, 20, 62, 'پس ساحران میان خود، درباره‌ی کارشان به تبادل رأی برخاستند و نجوا(شان) را پنهان کردند.'),
(2411, 20, 63, '(فرعونیان) گفتند: «قطعاً این دو تن ساحرند، (و) می‌خواهند شما را با سحرشان از سرزمینتان بیرون برانند و والاترین راهتان را (از میانتان) بردارند.»'),
(2412, 20, 64, '«پس نیرنگتان را گرد آورید، سپس در صفی (پیراسته و آراسته) پیش آیید. حال آنکه به‌راستی امروز هر کس پیروزی جست (همو) رستگار کرده است.»'),
(2413, 20, 65, '(ساحران) گفتند: «موسی! یا اینکه تو می‌افکنی یا (ما) نخستین کس باشیم که افکنده است‌؟»'),
(2414, 20, 66, 'گفت: « (نه،) بلکه شما بیفکنید.» پس ناگهان ریسمان‌ها و چوب‌دستی‌هایشان از (اثر) سحرشان، در خیال او (چنان) می‌نمود که به‌راستی آنها می‌‌شتابند.'),
(2415, 20, 67, 'پس موسی در درونش بیمی احساس کرد .'),
(2416, 20, 68, 'گفتیم: «مترس، تو بی‌گمان خود (از اینان) برتری،»'),
(2417, 20, 69, '«و آنچه در دست راستت داری بیفکن تا هر چه را ساختند با چالاکی ببلعد. آنچه ساختند تنها مکر افسونگر است، و افسونگر هر جا (و هرگونه) آید رستگار نمی‌کند.»'),
(2418, 20, 70, 'پس ساحران (بی‌اختیار و بی‌قرار) به سجده افکنده شدند.گفتند: «به پروردگار هارون و موسی ایمان آوردیم.»'),
(2419, 20, 71, '(فرعون) گفت: «آیا پیش از آنکه برایتان اجازه دهم به او ایمان آوردید؟ به‌راستی او بزرگِ شماست که به شما سحر آموخته. پس بی‌مهابا دست‌هایتان و پاهایتان را -بی‌چون- بر خلاف (یکدیگر) قطع می‌کنم، و شما را بی‌امان همچنان در شاخسارهای درختان خرما به دار می‌آویزم و همواره به راستی خواهید دانست کدامین (از) ما عذابش سخت‌تر و پایدارتر است.»'),
(2420, 20, 72, 'گفتند: «ما هرگز تو را بر نشانه‌های روشنی که سویمان آمده و (بر) آن کس که ما را (با فطرت توحیدی) پدید آورده است، ترجیح نخواهیم داد. پس تو هر حکمی (که) می‌خواهی بکن؛ تنها (در این) زندگی دنیاست که (در آن) حکم می‌رانی.»'),
(2421, 20, 73, '«ما به‌راستی به پروردگارمان ایمان آوردیم تا خطاهایمان را- و آن سحری که ما را بدان واداشتی- بر ما بپوشاند. و خدا بهتر و پاینده‌تر است.»'),
(2422, 20, 74, 'حال چنان است (که) بی‌چون هر کس نزد پروردگارش گنهکار آید، بی‌گمان جهنم برای اوست. در آن نه می‌میرد و نه زندگی می‌کند.'),
(2423, 20, 75, 'و هر کس به حال ایمان نزدش آید که به‌راستی کارهای شایسته‌(ی ایمان) انجام داده باشد، ایشان برایشان عالی‌ترین درجات است.'),
(2424, 20, 76, 'باغ‌های پای‌برجا که از زیر (درختان)شان نهرها روان است، (جایی است که) جاودانه در آن می‌مانند، و این پاداش کسی است که به پاکی گراید.'),
(2425, 20, 77, 'و به‌راستی و درستی سوی موسی وحی کردیم که: « بندگانم را شبانه کوچ ده، پس راهی خشک در دریا برایشان (با عصایت) برزن (حال آنکه) نه از فرا رسیدن (دشمن) بترسی و نه از غرق شدن، و نه بیمی داشته باشی.»'),
(2426, 20, 78, 'پس فرعون با لشکریانش آنها را پی گرفت. پس، از دریا آنچه آنان را فرو پوشانید، فرو پوشانیدشان.'),
(2427, 20, 79, 'و فرعون قوم خود را گمراه کرد و هدایت نکرد.'),
(2428, 20, 80, '«فرزندان اسرائیل! ما بی‌گمان شما را از (دست) دشمنتان رهانیدیم. و در پربرکت‌ترین جانب (کوه) طور با شما وعده نهادیم. و بر شما ترنجبین و امنیت فرو فرستادیم.»'),
(2429, 20, 81, '«(و گفتیم) از خوراکی‌های پاکیزه‌ای که روزیتان دادیم بخورید، و در آن طغیان نکنید که خشم من بر شما فرود آید. و هرکس خشم من بر او فرود آید بی‌چون سقوط کرده است.»'),
(2430, 20, 82, 'و من بی‌گمان برا‌ی کسی که توبه کرده و ایمان آورده و کاری شایسته(ی‌ایمان) انجام داده، سپس راه یافته؛ به‌درستی بسی پوشاننده‌ام.'),
(2431, 20, 83, '«موسی! چه چیز تورا از (برابر) قومت (سوی طور) شتابان کرد؟»'),
(2432, 20, 84, 'گفت: «هم اینان بر پی من‌اند، و - پروردگارم!- سویت شتافتم تا (از من) خشنود شوی.»'),
(2433, 20, 85, 'فرمود: «پس ما به‌راستی و درستی قوم تو را پس از (عزیمت) تو آزمایشی آتشبار کردیم، و سامری آنها را گمراه ساخت.»'),
(2434, 20, 86, 'پس موسی خشمگین (و) اندوهناک سوی قومش بازگشت (و) گفت: «ای قوم من! آیا پروردگارتان به شما وعده‌ای نیکو نداد؟ آیا پس این مدت بر شما طولانی بود، یا خواستید خشمی از پروردگارتان بر شما درآید، پس با وعده‌ی من مخالفت کردید؟»'),
(2435, 20, 87, 'گفتند: «ما به اختیار خود از وعده‌‌ات تخلف نکردیم، ولی از زینت‌آلات قوم، بارهایی سنگین همواره بر دوشمان نهاده شد. پس آنها را افکندیم. پس سامری (هم زینت‌آلاتش را) همین گونه بیفکند.»'),
(2436, 20, 88, 'پس برایشان پیکر گوساله‌ای که صدای گوساله داشت بیرون آورد. پس (او و پیروانش) گفتند: «این خدای شما و خدای موسی است.» پس (خدا را) فراموش کرد.'),
(2437, 20, 89, 'پس مگر نمی‌بینند که (گوساله) سخنی فرا سویشان بر نمی‌گرداند، و برایشان سود و زیانی ندارد؟'),
(2438, 20, 90, 'و به‌راستی بی‌گمان هارون از پیش به آنان گفت: «ای قوم من! جز این نیست (که) شما به وسیله‌ی این (گوساله) مورد آزمایشی آتشین قرار گرفته‌اید، و پروردگارتان به‌راستی خدای رحمتگر بر آفریدگان است، پس از پی من آیید و فرمانم را پیروی کنید.'),
(2439, 20, 91, 'گفتند: «ما هرگز در پرستشی ماندگار از آن دست برنخواهیم داشت، تا موسی سوی ما بازگردد.»'),
(2440, 20, 92, '(موسی) گفت: «هارون! چون دیدی آنها گمراه شدند چه چیز مانع تو شد،»'),
(2441, 20, 93, '«که از من پیروی نکنی‌؟ آیا از فرمانم سر باز زدی‌؟»'),
(2442, 20, 94, 'گفت: «پسر مادرم! نه ریش مرا بگیر و نه سرم را، من به‌راستی ترسیدم که بگویی میان بنی‌اسرائیل تفرقه انداختی، و سخنم را مراقبت نکردی.»'),
(2443, 20, 95, '(موسی) گفت: «سامری! پس هدف مهم تو (از این کار) چه بود؟»'),
(2444, 20, 96, 'گفت: «به چیزی که آنان به آن پی نبردند پی بردم، پس مشتی از اثر (رسالتی آن) رسول [:موسی] برگرفتم، سپس آن را فرو انداختم (که حق را به چهره‌ی باطل نمودم و با این آنان را از توحید به شرک افکندم) و نفس اماره‌ام برایم اینچنین فریبکاری کرد.»'),
(2445, 20, 97, 'گفت: «پس برو که بهره‌ی تو بی‌گمان در زندگی این باشد که (به هر که نزدیک تو آمد) بگویی (با من) هیچ‌گونه تماسی نیست.” و بی‌گمان برایت وعده‌گاهی است که هرگز از آن بازداشته نخواهی شد. و (اینک) به آن خدایی که پیوسته برایش سر فرود آوردی بنگر. همانا آن را به شدت همی سوزانیم، سپس همانا به خاکسترش همواره تبدیل می‌کنیم، پس آن‌گاه در دریا غباروار فرو می‌پاشیم، فرو پاشیدنی (چشمگیر)'),
(2446, 20, 98, '«معبود شما تنها (آن) خدایی است که جز او هرگز معبودی نیست. و علمش همه چیز را در برگرفته است.»'),
(2447, 20, 99, 'این گونه (ما) از اخبار مهم گذشته، بر تو گزینش می‌کنیم، و بی‌گمان تو را از جانب خودمان یادواره‌ای (روشنگر) دادیم.'),
(2448, 20, 100, 'هر کس از (پیروی) آن روی گرداند، او بی‌گمان روز قیامت باری گران بر دوش می‌گیرد.'),
(2449, 20, 101, 'حال آنکه در آن ماندگارند و روز رستاخیز چه بد باری است برایشان.'),
(2450, 20, 102, '(همان) روزی که در صور [:بوق جان‌افزا] دمیده می‌شود و در آن روز مجرمان را کبودچشم - بدون برگشت- گردآوری می‌کنیم.'),
(2451, 20, 103, 'میان خود، پنهانی با یکدیگر می‌گویند: «پیش از این (در برزخ) بیش از ده روز نمانده‌اید.»'),
(2452, 20, 104, 'ما به آنچه می‌گویند داناتریم. چون شایسته‌ترین (و) راهوارترینشان می‌گوید: «بیش از یک روز نمانده‌اید.»'),
(2453, 20, 105, 'و از تو درباره‌ی کوه‌ها می‌پرسند. پس بگو: «پروردگارم آنها را (در قیامت) غباری سخت پراکنده خواهد ساخت،»'),
(2454, 20, 106, '«پس آنها را پهن و هموار و تهی از گیاه (و تباه) خواهد کرد،»'),
(2455, 20, 107, '«نه در آن کژی می‌بینی و نه ناهمواری.»'),
(2456, 20, 108, 'در چنان روزی (همه‌ی مردم) داعی (حق) را - که هیچ انحرافی برایش نیست - (خواه ناخواه) پیروی می‌کنند. و کل صداها برای (خدای) رحمان فرو کاسته بوده. پس هرگز جز صدایی آهسته نمی‌شنوی.'),
(2457, 20, 109, 'در آن هنگام (و هنگامه)، هیچ‌گونه شفاعتی سود نبخشد، مگر کسی را که (خدای) رحمان اجازه داده و سخنش خدا را پسند آمده.'),
(2458, 20, 110, 'آنچه را که آنان در پیش دارند و آنچه را در پشت (سر)شان دارند می‌داند، و(آنان) هیچ‌گونه احاطه‌ی علمی به او ندارند.'),
(2459, 20, 111, 'و همه‌ی چهره‌ها برای آن (خدای) زنده‌ی پاینده‌ی نگهدارنده توجه کردند، حال آنکه بی‌چون آن کس که ظلمی بر دوش داشته زیان کرده است.'),
(2460, 20, 112, 'و هر کس در حال ایمان کارهای شایسته کند از هیچ ستمی و نه از هیچ کم و کاستی نمی‌هراسد.'),
(2461, 20, 113, 'و این‌گونه آن را (به صورت) قرآنی (بس) روشن نازل کردیم، و در آن از انواع هشدارها (سخن) آوردیم (تا) شاید آنان تقوا پیشه کنند یا (این کتاب) یاد تازه‌ای برایشان ایجاد کند.'),
(2462, 20, 114, 'پس بسی بلندمرتبه است خدا، (آن) فرمانروای (تمام) حق (و حقیقت). و در (خواندن) قرآن- پیش از آنکه وحی آن فراسویت حکم شود (و در رسد)- شتاب مکن، و بگو: «پروردگارم! بر دانشم بیفزای‌.»'),
(2463, 20, 115, 'و همانا به‌راستی پیش از این سوی آدم پیمان بستیم. پس (آن را) فراموش کرد، و برای او عزمی (استوار) نیافتیم.'),
(2464, 20, 116, 'و چون به فرشتگان گفتیم: «برای آدم سجده کنید.» پس (همه) سجده کردند جز ابلیس (که) سر باز زد.'),
(2465, 20, 117, 'پس گفتیم: «آدم! بی‌چون این (ابلیس) برای تو و همسرت دشمنی (خطرناک) است. زنهار تا شما را از (این) باغ بی‌چون به در نکند، پس به زحمت افتی،»'),
(2466, 20, 118, '«به‌راستی برای تو در آنجا این (امتیاز) است که نه گرسنه می‌شوی و نه برهنه می‌مانی.»'),
(2467, 20, 119, '«و (هم) اینکه به‌راستی در آن‌جا نه تشنه می‌گردی و نه آفتاب‌زده می‌شوی.»'),
(2468, 20, 120, 'پس شیطان سویش وسوسه افکند. گفت: «آدم! آیا تو را بر درخت جاودانگی و مُلکی کهنه‌ناشدنی راه بنمایم‌؟»'),
(2469, 20, 121, 'پس از آن (درخت ممنوع) خوردند. در نتیجه عورت‌هاشان برایشان نمایان شد و شروع کردند به چسباندن برگ‌های بهشتی بر خودشان. و (این گونه) آدم پروردگارش را عصیان کرد، پس گمراه شد.'),
(2470, 20, 122, 'سپس پروردگارش او را برگزید. پس بر او برگشت و (وی را به رسالت) هدایت کرد.'),
(2471, 20, 123, 'فرمود: «هر دو از این (باغ) فرود آیید، در حالی که بعضی از شما دشمن بعضی دیگرید، تا اگر برای شما از جانب من حتماً رهنمودی در رسد، پس هر کس از هدایتم پیروی کند نه گمراه می‌شود و نه به رنج می‌افتد.»'),
(2472, 20, 124, '«و هر کس از یاد من (دل) بگرداند، در حقیقت برایش زندگی‌ای تنگ (و سخت) است و روز رستاخیز او را نابینا محشور می‌کنیم.»'),
(2473, 20, 125, 'گفت: « پروردگارم! چرا مرا نابینا محشور کردی حال آنکه به‌راستی بینا بوده‌ام‌؟»'),
(2474, 20, 126, '(خدا) فرمود: «همان‌گونه که نشانه‌های ما برایت آمد پس آن‌ها را فراموش کردی، و امروز (هم) همان گونه فراموش می‌شوی.»'),
(2475, 20, 127, 'و بدین‌سان هر که را به اسراف [:زیاده‌روی] گراییده و به نشانه‌های پروردگارش نگرویده سزا می‌دهیم. و بی‌چون عذاب آخرت سخت‌تر و پایدارتر است.'),
(2476, 20, 128, 'آیا پس برای هدایتشان کافی نبود که (ببینند) چه اندازه سده‌ها [:نسل‌ها] را پیش از آنان نابود کردیم که (اینان) در جایگاه‌هایشان راه می‌روند؟ به‌راستی برای بازدارندگان عاقلانه از زشتی‌ها در این (امر) نشانه‌هایی (عبرت‌انگیز) است.'),
(2477, 20, 129, 'و اگر سخنی از پروردگارت (برای عذابشان) پیشی نگرفته بود ناگزیر (و ناگریز هلاکت و) سررسیدی یادشده لازمه‌‌ی آنها بود.'),
(2478, 20, 130, 'پس بر آنچه می‌گویند شکیبایی کن و پیش از برآمدن آفتاب و پیش از فرو شدن آن با ستایش پروردگارت (او را) تسبیح گوی و برخی از ساعات شب و کناره‌های روز را (هم) به نیایش بپرداز، شاید خشنود گردی.'),
(2479, 20, 131, 'و زنهار سوی آنچه همسرانی از ایشان را از آن برخوردار کردیم -(و فقط) زیور زندگی دنیاست تا ایشان را در آن بیازماییم- هرگز دیدگان خود را مدوز- و روزیِ پروردگارت بهتر و پایدار‌تر است'),
(2480, 20, 132, 'و کسان خود را به نماز وادار و خود بر آن شکیبا باش. ما از تو جویای (هیچ‌گونه) روزی نیستیم. ما به تو روزی می‌دهیم. و فرجام (نیک) برای پرهیزگاری است.'),
(2481, 20, 133, 'و گفتند: «چرا از جانب پروردگارش نشانه‌ای برای ما نمی‌آورد؟ آیا و روشنای آنچه در صحیفه‌های نخستین است برایشان نیامده است‌؟'),
(2482, 20, 134, 'و اگر ما آنان را قبل از آن (رسولان) به عذابی هلاک می‌کردیم بی‌گمان می‌گفتند: «پروردگارمان! چرا پیامبری سویمان نفرستادی تا پیش از آنکه خوار و بی‌مقدار و رسوا شویم از آیات تو پیروی کنیم‌؟»'),
(2483, 20, 135, 'بگو: «همگان در انتظارند. پس (شما هم) در انتظار باشید. زودا (که) بدانید صاحبان راه راست کیانند و چه کسی (به آن) راه یافته است.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'برای مردمان، (زمانِ) حسابشان نزدیک شد، حال آنکه آنان در (ژرفای) غفلتی رویگردانند.'),
(2485, 21, 2, 'هیچ‌گونه یادواره‌ی تازه‌ای از پروردگارشان برایشان نمی‌آید، مگر اینکه آن را به‌خوبی شنیدند، در حالی‌که ایشان بازی می‌کنند.'),
(2486, 21, 3, 'حال آنکه دل‌هایشان (از یاد خدا) رویگردان است. و آنان که ستم کردند پنهانی به نجوا برخاستند: «آیا این (مرد) جز بشری مانند شماست‌؟ آیا پس سوی سحر می‌روید در حالی‌که شما می‌بینید؟»'),
(2487, 21, 4, '(پیامبر) گفت: «پروردگارم (همه‌گونه) گفتار را در آسمان و زمین می‌داند و اوست بس شنوای بسیار دانا.»'),
(2488, 21, 5, 'بلکه گفتند: « (این‌ها) پاره‌هایی پراکنده از خیالاتی (شوریده) است، بلکه آن را بربافته، بلکه او شاعری است. پس همان‌گونه که پیشینیان [:پیامبران پیشین] (با نشانه‌ای ربّانی) فرستاده شدند، باید برای ما (همانند آنها) نشانه‌ای بیاورد.»'),
(2489, 21, 6, 'پیش از آنان هیچ مجتمعی - که آن را نابود کردیم - (به آیات ما) ایمان نیاوردند. پس آیا اینان (به نشانه‌ای ربّانی) ایمان می‌آورند؟'),
(2490, 21, 7, 'و پیش از تو جز مردانی را که به آنان وحی کنیم گسیل نداشتیم و اگر نمی‌دانسته‌اید (و نتوانید هم بدانید) پس از یادآوران (کتاب‌های آسمانی) بپرسید.'),
(2491, 21, 8, 'و ایشان را جسدهایی که غذا نخورند قرار ندادیم و (در زندگی دنیا هم) جاودان نبوده‌اند.'),
(2492, 21, 9, 'سپس وعده(‌ی خود) را به آنان راست آوردیم، پس آنها و هر که را خواستیم نجات دادیم و افراط‌کاران را به هلاکت رساندیم.'),
(2493, 21, 10, 'همانا به راستی کتابی سویتان نازل کردیم که یادِ شما در آن است. آیا پس (از این هم) نمی‌اندیشید؟'),
(2494, 21, 11, 'و چه بسیار مجتمعاتی را که ستمکار بودند در هم شکستیم و پس از آنها گروهی دیگر پدید آوردیم.'),
(2495, 21, 12, 'پس چون برخورد شدیدمان را احساس کردند ناگهان از آن می‌گریزند.'),
(2496, 21, 13, '(هان! از آن برخورد) مگریزید و سوی آنچه در آن متنعم شدید و (سوی) مسکن‌هایتان بازگردید، شاید شما مورد پرسش قرار گیرید.'),
(2497, 21, 14, 'گفتند: «ای وای بر ما! ما بی‌گمان ستمگر بوده‌ایم.»'),
(2498, 21, 15, 'پس خواستشان پیوسته همان بود، تا آنان را دروشدگانی (بی‌جان و) خاموشان گردانیدیم.'),
(2499, 21, 16, 'و ما آسمان و زمین و آنچه را میان آن دو است بازی‌کنان نیافریدیم.'),
(2500, 21, 17, 'اگر بخواهیم بازیچه‌ای برگیریم، به‌راستی آن را از پیش خود اختیار می‌کنیم‌؛ اگر کننده‌(ی این کار) بوده‌ایم.'),
(2501, 21, 18, 'بلکه با (نیروی) حق بر باطل می‌افکنیم، پس بینی‌اش را به خاک می‌کشاند، پس ناگهان (هم) آن نابود است. و وای بر شما از آنچه توصیف می‌کنید.'),
(2502, 21, 19, 'و هر که در آسمان‌ها و زمین است تنها برای اوست و کسانی که نزد اویند [:به او نزدیکند] از پرستش وی تکبر نمی‌ورزند و (هرگز) از آن حسرت بار (خسته و رنجور) نمی‌گردند.'),
(2503, 21, 20, 'شباروز، بی‌آنکه سستی ورزند (تنها او را) تنزیه می‌کنند.'),
(2504, 21, 21, 'یا برای خود خدایانی از زمین اختیار کرده‌اند (که) همانان (مردگان را) زنده می‌کنند؟'),
(2505, 21, 22, 'اگر در آن دو [:زمین و آسمان] جز خدا، خدایانی (دیگر) بودند ، ناگزیر (این خدایان و زمین و آسمان) تباه می‌شدند. پس منزّه است خدا، پروردگار عرشِ (فرماندهی) از آنچه توصیف می‌کنند.'),
(2506, 21, 23, '(خدا) از آنچه می‌کند بازخواست نمی‌شود و (حال آنکه) آنان بازخواست می‌شوند.'),
(2507, 21, 24, 'یا غیر از او خدایانی برای خود برگرفته‌اند؟ بگو: «برهانتان را بیاورید.» این است یادواره‌ی هر که با من است و یادواره‌ی هر که پیش از من بوده‌است. (نه!) بلکه بیشترشان حق را نمی‌دانند، پس از آن رویگردانند.'),
(2508, 21, 25, 'و ما پیش از تو هیچ رسولی نفرستادیم، جز (آنکه) سویش وحی می‌کنیم: «به‌راستی هیچ معبودی جز من نیست، پس تنها مرا بپرستید.»'),
(2509, 21, 26, 'و گفتند: « (خدای) رحمان فرزندی برگرفته.‌» او منزّه است، بلکه (آنان) بندگانی گرامی شده‌اند.'),
(2510, 21, 27, 'در سخن بر او پیشی نمی‌گیرند، حال آنکه تنها به دستور او کار می‌کنند .'),
(2511, 21, 28, 'آنچه پیش رویشان و آنچه پشت (سر)شان است می‌داند، و جز برای کسی که (او) رضایت دهد شفاعت نمی‌کنند، حال آنکه از بیم او با تعظیم (وی) هراسانند.'),
(2512, 21, 29, 'و هرکس از آنان بگوید: «بی‌گمان من خدایی غیر از او هستم‌» پس آن گمراه دور را در دوزخ، کیفر می‌کنیم. (آری،) سزای ستمکاران را این گونه می‌دهیم.'),
(2513, 21, 30, 'آیا و کسانی که کافر شدند ندانستند که آسمان‌ها و زمین هر دو به هم پیوسته بوده‌اند، پس ما آن دو را از هم جدا ساختیم، و هر چیز زنده‌ای را از آب نهادیم؟ آیا پس (از این هم) ایمان نمی‌آورند؟'),
(2514, 21, 31, 'و در زمین کوه‌هایی استوار نهادیم تا مبادا (زمین) آنان [:مردمان] را سرنگون سازد، و در آن راه‌های سراشیب فراخ پدید آوردیم، شاید راه یابند.'),
(2515, 21, 32, 'و آسمان را سقفی محفوظ (از سقوط و دستبرد شیطان‌ها) قرار دادیم، در حالی‌که آنان از نشانه‌های آن رویگردانند.'),
(2516, 21, 33, 'و اوست آن کس که شب و روز و خورشید و ماه را پدید آورده است، هر یک در جاده‌ای (فضایی) شناورند.'),
(2517, 21, 34, 'و پیش از تو برای هیچ بشری جاودانگی (در دنیا) قرار ندادیم. آیا اگر تو از دنیا بروی پس (از تو) آنان جاودانانند؟'),
(2518, 21, 35, 'هر نفسی[:کسی] چشنده‌ی مرگ است. و شما را در حال آزمون با بد و نیک بسی گیرودار می‌کنیم، و تنها سوی ما بازگردانیده می‌شوید.'),
(2519, 21, 36, 'و هنگامی‌که تو را - کسانی که کافر شدند - بنگرند، جز (به) مسخره‌ات نمی‌گیرند. (گویند:) «آیا این همان کس است که خدایانتان را (به بدی) یاد می‌کند؟» حال آنکه آنان خودشان، به خدای رحمان کافرند.'),
(2520, 21, 37, 'انسان از شتابی آفریده شده است. به زودی آیاتم را به شما نشان می‌دهم، پس بر من (در نشانگری این آیات و عذاب) شتاب نمی‌کنید.'),
(2521, 21, 38, 'و می‌گویند: «اگر راست می‌گویید، این وعده‌(ی قیامت) کی خواهد بود؟»'),
(2522, 21, 39, 'کاش آنان که کافر شده‌اند آن هنگام (و هنگامه) را می‌دانستند که اینان آتش را نه از چهره‌هاشان و نه از پشت‌هاشان باز نمی‌دارند و نه ایشانی یاری می‌شوند.'),
(2523, 21, 40, 'بلکه (آتش) ناگهان به آنان در می‌رسد؛ پس ایشان را بهت‌زده می‌کند؛ پس نه می‌توانند آن را از خود برگردانند و نه آنان مهلت داده می‌شوند.'),
(2524, 21, 41, 'و بی‌گمان پیامبرانی پس از تو (نیز) همانا ریشخند شدند. پس کسانی که آنان را مسخره می‌کردند، آنچه که آن را به ریشخند می‌گرفتند. بحق گریبان‌شان را گرفت.'),
(2525, 21, 42, 'بگو: «چه کسی شما را در شب و روز از (عذاب خدای) رحمان نگهبانی پایدار می‌کند؟» بلکه آنان از یاد پروردگارشان رویگردانانند.'),
(2526, 21, 43, 'یا برای آنان خدایانی (غیر از ما) هستند که ایشان را از گزندمان حمایت می‌کنند؟ (آنان) نه توان یاری خودشان را دارند و نه از جانب ما همراهی می‌شوند.'),
(2527, 21, 44, 'بلکه اینان و پدرانشان را (از نعمت‌هایی) برخوردار کردیم، تا عمرشان آنان به درازا کشید. پس آیا نمی‌بینند که ما زمین را می‌آیم [:درمی‌گیریم،] در حالی‌که آن را از اطرافش فرومی‌کاهیم؟ پس آیا آنان پیروزمندانند؟'),
(2528, 21, 45, 'بگو: «من شما را فقط به وسیله‌ی (این) وحی هشدار می‌دهم.‌» و کَران -هنگامی که هشدار داده شوند- نمی‌شنوند.'),
(2529, 21, 46, 'و اگر وزشی از (باد) عذاب پروردگارت به آنان در رسد، آنان بی‌چون و بی‌گمان گویند: «ای وای بر ما! (که) همواره ستمکاران بوده‌ایم.‌»'),
(2530, 21, 47, 'و ترازوهای قسط (فاضلانه) را، در روز رستاخیز می‌نهیم. پس هیچ کس (هرگز) چیزی ستم نبیند و اگر (هم کار و کرداری) هم‌وزن دانه‌ی خردلی باشد (هم)آن را می‌آوریم. و در حسابگری ما کفایت است.'),
(2531, 21, 48, 'به‌راستی و درستی، ما به موسی و هارون فرقان [:جداسازی حق را از باطل] و روشنایی و یادواره‌ای برای پرهیزگاران دادیم.'),
(2532, 21, 49, '(همان) کسانی که از پروردگارشان در نهان می‌هراسند، حال آنکه از (آن) ساعت - از روی عظمتش - هراسناکند.'),
(2533, 21, 50, 'و این (کتاب) یادواره‌ای مبارک است (که) آن را نازل کردیم. آیا پس (همین) شما، همان را انکار می‌کنید؟'),
(2534, 21, 51, 'و به‌راستی و درستی، از پیش به ابراهیم رشدش (و رشادتش) را دادیم، حال آنکه ما به (شایستگی) او بسی دانا بوده‌ایم.'),
(2535, 21, 52, 'چون که به پدرش و قومش گفت: «این مجسمه‌هایی که شما با تعظیم دل داده و ملازم آنها شده‌اید چیستند؟»'),
(2536, 21, 53, 'گفتند: «پدران خود را پرستندگان آنها یافتیم.»'),
(2537, 21, 54, 'گفت: «همواره شما و پدرانتان -بی‌گمان- در گمراهی آشکارگری بوده‌اید.»'),
(2538, 21, 55, 'گفتند: «آیا حق را برای ما آورده‌ای. یا تو از بازی‌کنندگانی‌؟»'),
(2539, 21, 56, 'گفت: « (نه،) بلکه پروردگارتان، پروردگار آسمان‌ها و زمین است. (همان) کسی که آنها را با فطرت توحیدی پدید آورده و من بر این (حقیقت) از گواهانم.»'),
(2540, 21, 57, '«و سوگند به خدا، پس از آنکه در حال پشت نمودن برفتید، همانا بی‌گمان در کار بتانتان کید و مکری (بحق) خواهم کرد.»'),
(2541, 21, 58, 'پس آنان را - جز بزرگشان را - شکسته و تکه تکه کرد، شاید ایشان سوی آن [:بزرگ] برگردند.'),
(2542, 21, 59, 'گفتند: «کسی که با خدایان ما چنان کرده، او بی‌چون و بی‌گمان از ستمکاران است‌.»'),
(2543, 21, 60, 'گفتند: « جوانی را شنیدیم (که) آنها را (به بدی) یاد می‌کرد (و) به او ابراهیم گفته می‌شود.»'),
(2544, 21, 61, 'گفتند: «پس او را برابر دیدگان مردمان بیاورید، شاید آنان (او را) بنگرند.»'),
(2545, 21, 62, 'گفتند: «ابراهیم! آیا تو با خدایان ما این(چنین) کردی‌؟»'),
(2546, 21, 63, 'گفت: «بلکه بزرگشان چنان کرده است. پس از آنان بپرسید؛» اگر سخن می‌گفته‌اند.'),
(2547, 21, 64, 'پس به (فطرت و عقل) خود باز گشتند. آن‌گاه (به یکدیگر) گفتند: «در حقیقت، شما (همین) شما، ستمکارانید.»'),
(2548, 21, 65, 'سپس سرافکنده شدند (و گفتند:) «به‌راستی و درستی دانسته‌ای که این‌ها سخن نمی‌گویند.»'),
(2549, 21, 66, 'گفت: «پس آیا مادون خدا چیزی را می‌پرستید، که نه سودی به شما می‌رساند و نه زیانی؟»'),
(2550, 21, 67, '«اُف برای شما و برای آنچه غیر از خدا می‌پرستید. آیا پس خرد‌ورزی نمی‌کنید؟»'),
(2551, 21, 68, '(به یکدیگر) گفتند: «اگر کننده‌ی کاری بوده‌اید، او را بسوزانید و خدایانتان را یاری دهید.»'),
(2552, 21, 69, 'گفتیم: «آتش! برای ابراهیم سردی و سلامتی‌ای باش.»'),
(2553, 21, 70, 'و خواستند به او نیرنگی (بزرگ) زنند، پس آنان را زیانکارترین (جهانیان) قرار دادیم.'),
(2554, 21, 71, 'و او و لوط را فراسوی آن سرزمینی که برای عالمیان (آن سامان) در آن برکت نهاده بودیم، رهانیدیم.'),
(2555, 21, 72, 'و اسحاق و یعقوب را- افزون- بدو بخشودیم، و همه‌ی اینان را از شایستگان نهادیم.'),
(2556, 21, 73, 'و آنان را پیشوایانی نهادیم (که) به امرمان (مکلفان را) رهبری می‌کنند و کار خیرات را و بر پاداشتن نماز را و دادن زکات را فراسویشان وحی کردیم ، حال آنکه آنان پرستندگان ما بوده‌اند.'),
(2557, 21, 74, 'و لوط را حکمت و دانشی دادیم و او را از آن گروهی که کارهای (جنسی) بس پلید می‌کرده‌اند نجات دادیم. آنان بی‌گمان گروهی بد و فاسق بوده‌اند.'),
(2558, 21, 75, 'و او را در رحمت خویش داخل کردیم. به‌راستی او از شایستگان است.'),
(2559, 21, 76, 'و نوح را (نیز در رحمتمان داخل کردیم) چون از پیش (ما را) ندا کرد و ما او را اجابت کردیم و وی را با خانواده‌اش از (آن) بلای بزرگ رهانیدیم.'),
(2560, 21, 77, 'و او را از (شر) مردمی که با نشانه‌های ما (همان‌ها و ما را) به دروغ گرفته بودند، پیروزی بخشیدیم؛ آنان بی‌چون مردم بدی بودند؛ پس همه‌ی ایشان را غرق کردیم.'),
(2561, 21, 78, 'و داوود و سلیمان را (نیز) چون درباره‌ی آن کشتزار - که گوسفندان قوم، شب‌هنگام در آن همچنان پراکنده شدند - داوری می‌کردند؛ و ما شاهد داوری آنان بوده‌ایم.'),
(2562, 21, 79, 'پس آن (داوری) را به سلیمان فهماندیم و به هر یک (از آن دو) حکم [:سلطه] و دانشی دادیم، و کوه‌ها را با داوود تسبیح‌گویان مسخر کردیم و (نیز) پرندگان را. و ما کننده‌ی (این کارها) بوده‌ایم.'),
(2563, 21, 80, 'و برای شما به او [:داوود] فن زره(سازی) را آموختیم، تا شما را از درگیری‌ها و خطراتتان نگهبانی کند. پس آیا شما سپاسگزارید؟'),
(2564, 21, 81, 'و برای سلیمان، بادِ وزان را - در حالی‌که به فرمان او سوی سرزمینی که در آن برکت نهاده بودیم جریان می‌یافت - مسخر کردیم. و ما به هر چیزی دانا بوده‌ایم.'),
(2565, 21, 82, 'و برخی از شیاطین کسانی (بودند که) برایش غواصی می‌کردند و عملی (هم) جز این انجام می‌دادند، و برایشان نگهبان بوده‌ایم.'),
(2566, 21, 83, 'و ایوب را (نیز) چون پروردگارش را ندا در داد که: «همواره به من زیان رسیده و تویی رحم‌کننده‌ترین رحم‌کنندگان.»'),
(2567, 21, 84, 'پس (دعای) او را اجابت نمودیم؛ پس زیان‌هایش را برطرف کردیم. و کسان او و همانندشان را با ایشان به او دادیم، حال آنکه رحمتی از جانب ما و یادواره‌ای برای عبادت‌کنندگان بود.'),
(2568, 21, 85, 'و (نیز) اسماعیل و ادریس و ذوالکفل را (که) همه از شکیبایانند.'),
(2569, 21, 86, 'و آنان را در رحمت خود داخل نمودیم (و) به‌راستی ایشان از شایستگان (رسالتی)اند.'),
(2570, 21, 87, 'و «ذاالنون » [:یونس] را چون خشمگین (از نزد قومش) برفت، پس گمان (شایسته‌ای) برد که ما بر او هرگز تنگ نمی‌گیریم. پس در (دل) تاریکی‌ها (ی دریا و شکم ماهی) ندا در داد: «معبودی جز تو نیست، تو منزّهی، من بی‌گمان از ستمکاران (به خود) بوده‌ام.»'),
(2571, 21, 88, 'پس درخواستش را برایش بر آورده کردیم، و او را از آن اندوه رهانیدیم. و مؤمنان را این‌گونه نجات می‌دهیم.'),
(2572, 21, 89, 'و زکریا را چون پروردگار خود را خواند: «پروردگارم! مرا تنها وا‌مگذار، حال آنکه تو بهترین وارثانی.»'),
(2573, 21, 90, 'پس (دعای) او را برایش اجابت نمودیم و یحیی را بدو بخشیدیم و همسرش را برایش شایسته (و آماده‌ی حمل) کردیم. آنان به‌راستی در نیکی‌ها همگام با یکدیگر شتاب می‌نموده‌اند و ما را از روی رغبت و بیم می‌خوانده‌اند، و تنها برای ما فروتن بوده‌اند.'),
(2574, 21, 91, 'و آن زنی[:مریم] را که عورتش را نگهداشت، پس در او از روح (برجسته‌ای از آفریدگان) خود دمیدیم و او و پسرش را برای جهانیان آیتی قرار دادیم.'),
(2575, 21, 92, '(ای پیامبران!) اینان بی‌گمان امت شمایند؛ امتی یگانه. و منم پروردگارتان، (پروردگاری یگانه). پس تنها مرا بپرستید.'),
(2576, 21, 93, 'و (اما) کارشان را میان خودشان پاره پاره کردند. همگان تنها سوی ما برگشت‌کنندگانند.'),
(2577, 21, 94, 'پس هر کس در حال ایمان کارهایی شایسته انجام دهد، برای تلاش او هرگز ناسپاسی‌ای نیست، و همواره ماییم که (اعمالش را) برایش ثبت‌کنندگانیم.'),
(2578, 21, 95, 'و بر گروهی که هلاکشان کردیم، بی‌چون عدم بازگشتشان (در رجعت به دنیا) حرام است (که ناگزیر و ناگریز رجعت خواهند کرد).'),
(2579, 21, 96, 'تا هنگامی‌که یأجوج و مأجوج گشایش یابند در حالی‌که آنان از هر بلندایی فرو می‌گسلند.'),
(2580, 21, 97, 'و وعده‌ی حق نزدیک شد. پس ناگهان دیدگان کسانی که کفر ورزیده‌اند در آن (هنگامه) خیره است (و می‌گویند:) «ای وای بر ما! از این (روز) بی‌گمان در (ژرفای) غفلت بوده‌ایم، بلکه ما ستمگران بوده‌ایم.»'),
(2581, 21, 98, 'همواره شما و آنچه غیر از خدا می‌پرستید، گیرانه‌ی دوزخید. شما برای آن (در همان،) داخل شدگانید.'),
(2582, 21, 99, 'اگر اینان خدایانی (حقیقی) بودند، در آن (برای عذابشان) وارد نمی‌شدند، حال آنکه جملگی در آن ماندگارند.'),
(2583, 21, 100, 'برایشان در آنجا ناله‌ای پرهیجان است، و (هم)آنان در آنجا (ندای یکدیگر را) نمی‌شنوند.'),
(2584, 21, 101, 'بی‌گمان کسانی که در گذشته از جانبمان به آنان نیکوترین (وعده) گذشت، ایشان از آن (آتش) دورشدگانند.'),
(2585, 21, 102, 'صدای (آتش‌باران) محسوس آن را نمی‌شنوند، و (هم)آنان در میان آنچه دل‌هایشان خواسته، جاودانند.'),
(2586, 21, 103, 'بزرگ‌ترین دلهره و وحشت، آنان را غمگین نمی‌کند، و فرشتگان آنان را با کوششی تمام پیاپی در برمی‌گیرند (و به آنان می‌گویند:) «این همان روز شماست که (بدان) وعده داده می‌شده‌اید.»'),
(2587, 21, 104, 'روزی که آسمان را - همچون در پیچیدن صفحه‌ی سخت نامه‌ها- طوماروار درهم می‌پیچیم. همان گونه که بار نخست آفرینش را آغاز کردیم، دوباره آن را باز می‌گردانیم. حال آنکه (این) وعده‌ای است بر عهده‌ی ما که بی‌گمان انجام‌دهنده‌(ی آن) بوده‌ایم.'),
(2588, 21, 105, 'و به‌راستی و درستی در زبور - پس از یادواره(ی تورات و انجیل و ...) - نوشتیم: «زمین را بی‌چون بندگان شایسته‌ام به ارث خواهند برد.»'),
(2589, 21, 106, 'بی‌گمان در این جریان برای مردمان عبادت‌پیشه نویدی حتمی و رساست.'),
(2590, 21, 107, 'و ما تو را جز رحمتی برای تمام جهانیان (در مثلث زمان) نفرستادیم.'),
(2591, 21, 108, '(به اینان) بگو: «تنها به من (درباره‌ی خدا) وحی می‌شود، که خدای شما خدایی است یگانه. پس آیا شما (در برابرش) تسلیم‌شونده‌اید؟»'),
(2592, 21, 109, 'پس اگر روی برتافتند بگو: «به همه‌ی شما - به گونه‌ای یکسان- (رستاخیز جهان را) اعلام کردم و نمی‌دانم آنچه وعده داده شده‌اید آیا نزدیک است یا دور.»'),
(2593, 21, 110, '«همواره او سخن آشکار را می‌داند و آنچه را (هم) پوشیده می‌دارید می‌داند.»'),
(2594, 21, 111, '«و نمی‌دانم؛ شاید آن برای شما آزمایشی آتشین، و تا چند گاهی (‌وسیله‌ی) برخورداری باشد.»'),
(2595, 21, 112, 'گفت: «پروردگارم! به حق داوری فرمای. و پروردگارمان (همان) رحمتگر بر آفریدگان است، (که) بر آنچه (خلاف حقیقت) توصیف می‌کنید، مورد درخواستِ یاری (یاوران حق) است.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'هان ای مردمان! پروردگارتان را پاس دارید، بی‌گمان زلزله‌ی (مرگبار) ساعت [:قیامت] چیزی بس بزرگ است.'),
(2597, 22, 2, 'روزی که آن را ببینید، هر شیردهنده‌ای آن را که شیر می‌دهد (از شدت هراس) به فراموشی می‌سپارد و هر زن بارداری بار خود را فرو می‌نهد و مردم را مست می‌بینی، حال آنکه مست نیستند. ولی عذاب خدا شدید است.'),
(2598, 22, 3, 'و برخی از مردمان کسانی(اند که) درباره‌ی خدا بدون هیچ (بینش و) دانشی مجادله (و ستیز) می‌کنند و از هر شیطان سرکشی پیروی می‌نمایند.'),
(2599, 22, 4, 'بر زیانش نبشته شده است که همانا هر کس او را به سرپرستی برگیرد، پس همواره او را گمراه می‌کند و سوی عذاب (آن) آتش زبانه‌دار رهنمونش می‌سازد.'),
(2600, 22, 5, 'هان ای مردمان! اگر درباره‌ی برانگیخته شدن (پس از مرگتان) در (ژرفای) شکی مستند بوده‌اید، پس همانا (بدانید که) ما شما را از خاک آفریدیم، سپس از نطفه، سپس از علقه [:خونی آویزک]، سپس از مضغه [:گوشتکی له شده] به بار آمده و به بار نیامده تا (حقایقی ربّانی را) برای شما روشن گردانیم. و آنچه را می‌خواهیم تا مدتی معین در رحم‌ها پایدار می‌داریم، سپس شما را به حالت کودکی برون می‌آوریم، سپس (حیات شما را ادامه می‌دهیم) تا به حد رشدهاتان برسید و برخی از شما (پیش از پیری) مرگش در رسد، و برخی از شما (هم) به پست‌ترین زمان عمر(ش) بر‌گردانده شود، به گونه‌ای که پس از دانستنی (اندک) آن را (هم) نداند. و زمین را پژمرده می‌بینی، پس هنگامی که آب (باران) بر آن فرود آوریم به جنبش درآید و نمو کند و از هر نوع (رستنی‌های) همسان (و) نیکوی فرح‌بخشی برویاند.'),
(2601, 22, 6, 'این برای آن است که به‌راستی خدا، (هم)او تمامی حق است و اینکه مردگان را - به‌درستی- زنده می‌کند و بر هر چیزی بسی تواناست.'),
(2602, 22, 7, 'و اینکه ساعت (مرگ و زندگی همگانی) همواره آمدنی است (و) هرگز شکی مستند (هم) در آن نیست و همانا خدا کسانی را که در گورهایند برمی‌انگیزد.'),
(2603, 22, 8, 'و از (میان) مردمان کسی است که درباره‌ی خدا - بدون هیچ دانایی و نه رهنمودی و نه کتاب روشنگری - به مجادله و ستیز می‌پردازد،'),
(2604, 22, 9, 'حال آنکه (از سرِ نخوت) پهلو در هم پیچنده [:کمر بسته] است تا (مردم را) از راه خدا گمراه کند. و در این دنیا برای او رسوایی و خواری است و در روز رستاخیز عذاب آتش بس سوزان را به او می‌چشانیم.'),
(2605, 22, 10, 'این (کیفر بزرگ) به سزای (همان) چیزهایی است که دست‌هایت [:نیروهایت] پیش فرستاده است. و اینکه خدا همواره به بندگان خود زیاد بیدادگر نیست.'),
(2606, 22, 11, 'و از میان مردم کسی است که خدا را فقط بر (مبنای) جانبی (و هوسی مصلحتی) می‌پرستد. پس اگر خیری به او رسد بدان اطمینان یابد و چون بلایی آتشین بدو رسد روی برتابد. (او) دنیا و آخرت را زیان کرده (و) این همان زیان آشکارگر است.'),
(2607, 22, 12, 'از غیر خدا همی خواند و می‌خواهد چیزی را که نه زیانش رساند و نه سودش دهد. این است همان گمراهی دور و دراز.'),
(2608, 22, 13, 'همواره کسی را می‌خواند و می‌خواهد که بی‌گمان زیانش از سودش نزدیکتر است. وه! به‌راستی چه بد سروری و چه بد دمسازی است!'),
(2609, 22, 14, 'بی‌گمان، خدا کسانی را که ایمان آورده و کارهای شایسته (ی‌ایمان) کرده‌اند، در باغ‌هایی سردرهم درمی‌آورد که از زیر (درختان)شان (:نهرها و رودبارها) روان است. خدا همواره هر چه اراده کند انجام می‌دهد.'),
(2610, 22, 15, 'هر که می‌پنداشته است (که) خدا او را در دنیا و آخرت هرگز یاری نخواهد کرد، پس وسیله‌ای سوی آسمان در کشد (و خود را حلق‌آویز کند)؛ سپس (آن را) بی‌چون بِبُرَد؛ آن‌گاه همی بنگرد (که) آیا نیرنگش چیزی را که مایه‌ی خشم اوست حتماً از میان خواهد برد؟'),
(2611, 22, 16, 'و این‌گونه قرآن را - حال آنکه آیاتی روشن است - فرو فرستادیم و خدا به‌راستی هر که را اراده کند راه می‌نماید.'),
(2612, 22, 17, 'به‌راستی کسانی که ایمان آوردند و کسانی که یهودی شدند و التقاطیان و نصرانیان و زرتشتیان و کسانی که شرک ورزیدند ، بی‌گمان خدا روز قیامت میانشان را جدا خواهد کرد، زیرا خدا همواره بر هر چیزی بس گواه است.'),
(2613, 22, 18, 'آیا ندیدی که خدا هر کس در آسمان‌ها و هر کس که در زمین است- و خورشید و ماه و ستارگان و کوه‌ها و درختان و جنبندگان- به‌راستی برایش (همگی) سجده می‌کنند و (نیز) بسیاری از مردمان و (حتّی‌) بسیاری از آنان هم که عذاب بر آنان محقق شده است (برای او سجده می‌کنند)؟ و هر که را خدا خوار کند، برایش هیچ گرامی‌دارنده‌ای نیست. همواره خدا هر چه بخواهد انجام می‌دهد.'),
(2614, 22, 19, 'این دو (گروه با یکدیگر) دشمنانند (که) درباره‌ی پروردگارشان با هم بسی دشمنی (و ستیزه) کردند. پس کسانی که کفر ورزیدند، جامه‌هایی بسیار از آتش برایشان بریده شده است (و) از بالای سرهاشان مایع جوشان ریخته می‌شود.'),
(2615, 22, 20, 'در شکم‌های آنهاست با پوست‌ها [:بدن‌ها]شان بدان گداخته و ذوب می‌گردد.'),
(2616, 22, 21, 'و برای (وارد کردن ضربت بر سر) آنان گرزهایی از آهن است.'),
(2617, 22, 22, 'هر بار خواستند از (شدت) اندوهی، از آن بیرون روند در آن بازگردانیده می‌شوند و (به آنان گویند: «هان!) بچشید عذاب آتش بس سوزان را.»'),
(2618, 22, 23, 'بی‌گمان خدا کسانی را که ایمان آورده و کارهای شایسته (ی‌ایمان) کردند در باغ‌هایی سردرهم - که از زیر (درختان)شان نهرها روان است - در می‌آورد: در آنجا با دستبندهایی از طلا و مروارید آراسته می‌شوند و لباس‌هاشان در آنجا حریر [:پرنیان] است.'),
(2619, 22, 24, 'و راهنمایی شدند فرا‌سوی گفتار پاکیزه، و راهنمایی شدند فراسوی راه (خدای) بس مورد سپاس.'),
(2620, 22, 25, 'بی‌گمان، کسانی که کافر شدند و از راه خدا و مسجدالحرام - که آن را برای مردمان اعم از اقامت‌گزیدگان و مسافران، یکسان قرار داده‌ایم - جلوگیری می‌کنند و هر که بخواهد در آنجا به (انحرافی) پوشاننده(‌ی حق) و ظالمانه درآید، او را از عذابی دردناک می‌چشانیم.'),
(2621, 22, 26, 'و چون برای ابراهیم جای آن خانه [:کعبه] را آماده کردیم (بدو گفتیم:) «چیزی را با من شریک مگردان و خانه‌ام را- برای طواف‌کنندگان و قیام‌کنندگان و رکوع‌کنندگانِ سجده‌کننده - پاکیزه گردان.»'),
(2622, 22, 27, '«و در میان مردمان برای (ادای) حج اعلان کن (که) پیادگان سویت آیند و (نیز) سواران بر شترانی لاغر - از هر راهی دور - سویت [:فرا سوی کعبه] روان شوند.»'),
(2623, 22, 28, '«تا شاهدانِ (دیدار و برخورداری) منافعی برای خویش باشند و نام خدا را -در روزهای معلومی بر دام‌های زبان‌بسته‌ای که روزی آنان کرده است- ببرند؛ پس از آنها بخورید و بدحالِ کمرشکسته‌(ی فقیر) را (از آن) بخورانید.»'),
(2624, 22, 29, 'سپس باید آلودگی خود را بزدایند و به نذرهای خود وفا کنند و بر گِرد آن خانه‌ی کهن آزاد [:کعبه] بسیار طواف کنند.'),
(2625, 22, 30, 'این (کاری با کرامت) است. و هر کس احترامات (ربانی) خدا را بزرگ بدارد، پس (همان) برایش نزد پروردگارش پسندیده و بهتر است. و برای شما نعمت‌های حیوانی [:حیوانات رام] حلال شده است، مگر آنچه (در قرآن) بر شما خوانده می‌شود. پس از پلیدی - همچون بتان - همواره دوری گزینید و از گفتار باطل (هم) اجتناب ورزید.'),
(2626, 22, 31, 'در حالی که از غیر خدا رویگردان شده (و) به او شرک نورزید. و کسی که به خدا شرک ورزد چنان است که گویی از آسمان فرو افتاده، پس مرغان او را می‌ربایند، یا باد او را به پرتگاهی دور - خردکننده- و نابسامان می‌افکند.'),
(2627, 22, 32, 'این است (فرمان بزرگ الهی) و هر کس شعائر خدا را بزرگ بدارد، همانا، پس (هم) آنها (حاکی) از پرهیزگاری دل‌هاست.'),
(2628, 22, 33, 'برایتان در آن (قربانی)ها تا مدتی معین سودهایی است، سپس جایگاه (قربانی کردن) آنها سوی خانه‌ی کهن [:کعبه] آزاد است.'),
(2629, 22, 34, 'و برای هر امتی روش ویژه‌ای (برای حج) نهادیم، تا نام خدا را بر دام‌های زبان‌‌‌‌بسته‌ای که روزیشان گردانیده یاد کنند. پس (بدانید که) معبود شما معبودی یگانه است، پس تنها برای او گردن نهید. و خاکساران و بینی بر خاک نهندگان (در برابر خدا) را بشارت ده.'),
(2630, 22, 35, 'کسانی (را) که چون خدا یاد شود، دل‌هایشان (از عظمتش) هراس یابد، و (آنان که) برهر چه بر سرشان آید صبر پیشگانند، و (نیز) کسانی (که) بر پا دارندگان نمازند و از آنچه روزیشان داده‌ایم انفاق می‌کنند.'),
(2631, 22, 36, 'و شتران درشت اندام را برای شما از (جمله‌ی) شعائر [:بلندگوهای دین] خدا قرار دادیم- در آنها برای شما خیری است- پس نام خدا را بر آنها - در حالی که صف اندر صف‌اند - یاد کنید، و چون به پهلوهاشان فروافتادند از آنها بخورید و به قناعت‌پیشه و (به) عابری بسیار مستمند (که چشم‌داشت هم دارد) بخورانید. این‌گونه آنها را برای شما رام کردیم، شاید شما شکرگزارید.'),
(2632, 22, 37, 'هرگز نه گوشت‌هایشان و نه خون‌هایشان به خدا نرسد ولی (این) تقوای شماست که به او می‌رسد. این‌گونه (خدا) آنها را برای شما رام کرد، تا خدا را به پاس آنکه شما را هدایت نموده بزرگ دارید. و نیکوکاران را مژده ده.'),
(2633, 22, 38, 'همواره خدا از کسانی که ایمان آورده‌اند دفاع می‌کند، زیرا خدا هیچ خیانت‌پیشه‌ای بس کافر (یا) کفران‌کننده‌ای بس ناسپاس را دوست نمی‌دارد.'),
(2634, 22, 39, 'برای کسانی که کشتار می‌شوند، اجازه(ی کشتار) داده شده، چه آنکه همواره ستم دیده‌اند و البته خدا بر یاری آنان بی‌گمان بس تواناست.'),
(2635, 22, 40, 'کسانی که به ناحق از خانه‌هایشان بیرون رانده شدند، و (گناهی نداشتند) جز اینکه گویند: «پروردگار ما خداست‌.» و اگر خدا (زیان) بعضی از مردم را به وسیله‌ی بعضی دیگر دفع نمی‌کرد، جایگاه عبادت مسیحیان و یهودیان و نمازها و نمازخانه‌ها(ی مسلمانان) که نام خدا در آنها بسیار برده می‌شود، سخت ویران می‌شد. و همواره خدا به کسی که (دین) او را یاری می‌کند بی‌گمان یاری می‌دهد. به‌راستی خدا بسی نیرومند و کانون عزّت است.'),
(2636, 22, 41, 'همان کسانی که چون در زمین به آنان توان و امکان دهیم، نماز را (به شایستگی) بر پا می‌دارند و زکات می‌دهند و (مکلفان را) به کارهای پسندیده وامی‌دارند و از کارهای ناپسند بازمی‌دارند. و فرجام همه‌ی امور تنها از آنِ خداست.'),
(2637, 22, 42, 'و اگر تو را تکذیب می‌کنند، پس بی‌گمان پیش از آنان (نیز) قوم نوح و عاد و ثمود (هم پیامبران را) تکذیب کردند.'),
(2638, 22, 43, 'و (نیز) قوم ابراهیم و قوم لوط.'),
(2639, 22, 44, 'و (همچنین) اصحاب مَدْیَن (تکذیب کردند) و موسی سخت تکذیب شد. پس برای کافران مهلت دادم، سپس (گریبان) آنان را بر گرفتم. پس (بنگر،) انکار (و انگار) ما چگونه بود؟'),
(2640, 22, 45, 'پس چه بسیار از مجتمعاتی را که در حال ستمکاریشان هلاکشان کردیم. در نتیجه سقف‌های جایگاه‌هایشان زیر و زبر شد و (بسا) چاهی متروک و قصری سخت‌بنیان و افراشته را (نیز ویران کردیم).'),
(2641, 22, 46, 'پس آیا در زمین گردش نکردند، تا برایشان دل‌هایی باشد که با آنها خردورزی کنند، یا گوش‌هایی که با آنها بشنوند؟ پس بی‌گمان، (این) چشم‌ها نابینا نیست ولی (چشم‌های) دل‌هایی که در سینه‌هاست کور است.'),
(2642, 22, 47, 'و از تو عذاب‌(شان) را با شتاب می‌طلبند، حال آنکه خدا هرگز وعده‌ی (عذاب) خود را تخلف نمی‌کند. و یک روز (از قیامت) نزد پروردگارت مانند هزار سال است از آنچه بر می‌شمرید.'),
(2643, 22, 48, 'و چه بسا از اجتماعاتی که در حال ستمکاریشان برایشان مهلت دادم، سپس (گریبان) ایشان را برگرفتم. و بازگشت (همگان) تنها سوی من است.'),
(2644, 22, 49, 'بگو: «هان ای مردمان! من برای شما تنها هشداردهنده‌ای روشنگرم.»'),
(2645, 22, 50, 'پس کسانی که ایمان آورده و کارهای شایسته(ی ایمانشان) انجام دادند، پوشش و روزی‌ای بس گسترده و با کرامت تنها برای ایشان خواهد بود.'),
(2646, 22, 51, 'و کسانی که در (خدشه‌دار کردن) آیات ما می‌کوشیدند حال آنکه (به گمانشان) با ما دست و پنجه نرم می‌کنند، آنان همراهان آتشی بس سوزانند.'),
(2647, 22, 52, 'و (ما) پیش از تو هیچ رسول و فرستاده‌ی برجسته‌ای را نفرستادیم جز اینکه هر گاه آرزو(ی هدایت مکلفان را) می‌کرد، شیطان در آرزویش القا(ی شبهه) می‌نمود. پس خدا آنچه را شیطان القا می‌نماید محو می‌کند، سپس خدا آیات خود را استوار می‌دارد. و خدا بس دانای حکیم است.'),
(2648, 22, 53, 'تا آنچه را شیطان القا می‌کند، برای کسانی که در دل‌هایشان بیماری است - و (نیز) برای سنگدلان- آزمایشی آتش‌بار گرداند. و بی‌گمان ستمگران همواره در (ژرفای) جدایی (از حق) و ستیزه‌ای بس دور و درازند.'),
(2649, 22, 54, 'و تا آنان که دانش (وحیانی) یافته‌اند بدانند همانا این (نسخ القائات شیطانی و تحکیم آیات ربانی)، به تمامی حق از سوی پروردگار تو است، تا بدو ایمان آورند. پس دل‌هایشان برای او بسی خاکسار و خاضع گردد. و به‌راستی خدا کسانی را که ایمان آورده‌اند، همانا سوی راهی راست راهبر است.'),
(2650, 22, 55, 'و کسانی که کفر ورزیدند، همیشه از آن (راه راست) در تردیدی شدیدند، تا ناگهان ساعت (زندگی آخرین) برایشان فرا رسد، یا عذاب روزی نازا به سراغشان آید.'),
(2651, 22, 56, 'تمامی فرماندهی (حق) در چنان روزی از آنِ خداست، میانشان فرمان می‌دهد. پس کسانی که ایمان آورده و کارهای شایسته(ی ایمانشان) را انجام دادند در باغ‌های سردرهم و پر ناز و نعمتند.'),
(2652, 22, 57, 'و کسانی که کفر ورزیده و به وسیله‌ی نشانه‌های ما (همان‌ها و ما را) تکذیب کردند، برایشان عذابی توهین‌آمیز است.'),
(2653, 22, 58, 'و کسانی که در راه خدا مهاجرت کرده، سپس کشته شدند یا مردند، همانا خدا همواره به آنان روزی نیکویی می‌دهد. و به‌راستی خدا، (هم)او به‌درستی بهترین روزی‌دهندگان است.'),
(2654, 22, 59, 'بی‌گمان آنان را در جایگاهی که آن را می‌پسندند همی در خواهد آورد. و همانا خدا به‌راستی بسی دانای بردبار است.'),
(2655, 22, 60, 'چنین است و هرکس نظیر آنچه بر او عقوبت رفته است دست به عقوبتی همسان زند، سپس مورد ستم قرار گیرد، همواره خدا او را بی‌گمان یاری خواهد کرد. به‌راستی خدا همانا بخشایشگرِ پوشنده است.'),
(2656, 22, 61, 'این به‌راستی بدین سبب است که خدا شب را در روز فرو می‌برد و روز را در شب فرو می‌برد. و اینکه خدا همواره بسی شنوای بسیارِ بیناست.'),
(2657, 22, 62, '(آری،) این بی‌گمان بدین سبب است که خدا خود تمامی حق است و آنچه به جای او می‌خوانند، (هم)او تمامی باطل است. و اینکه خدا بس والای بزرگ است.'),
(2658, 22, 63, 'آیا ندیدی که خدا از آسمان به‌راستی آبی فرو فرستاد؟ پس زمین (با آن) سرسبز می‌گردد. همانا خدا بسی دقیق، (و) ناپیدای بسیار آگاه است.'),
(2659, 22, 64, 'آنچه در آسمان‌ها و آنچه در زمین است تنها از اوست و همانا خدا، (هم)او، بی‌نیازِ بسیار ستوده است.'),
(2660, 22, 65, 'آیا ندیدی که خدا به‌درستی آنچه را در زمین است برای شما مسخر کرد. و (نیز) کشتی‌ها را در حالی‌که در دریا به فرمان او روانند، و آسمان را نگه می‌دارد، تا مبادا بر زمین فرو اُفتد، مگر به اذن او. به‌راستی خدا نسبت به مردمان بسی مهربان و رحمتگر بر ویژگان است.'),
(2661, 22, 66, 'و او کسی است که شما را زنده کرد. سپس شما را می‌میراند. باز پس شما را زنده می‌کند. (اما) انسان بی‌گمان سخت کافر (یا) ناسپاس است.'),
(2662, 22, 67, 'برای هر امتی رسم و جایگاهی برای عبادت و قربانی قرار دادیم که آنها بدان سوی و روش روانند. پس نباید در این امر با تو به ستیزه برخیزند. و سوی پروردگارت دعوت کن. همانا تو همواره بر راهی راست، چیره‌ای.'),
(2663, 22, 68, 'و اگر با تو مجادله کردند، بگو: «خدا به آنچه می‌کنید داناتر است.»'),
(2664, 22, 69, '(این تنها) خداست که روز قیامت در آنچه با یکدیگر در آن اختلاف می‌کرده‌اید، داوری خواهد کرد.'),
(2665, 22, 70, 'آیا ندانستی که خدا همواره آنچه را در آسمان و زمین است می‌داند؟ بی‌گمان این‌ها (همه) در کتاب (علم خدا) است. همواره این بر خدا آسان است.'),
(2666, 22, 71, 'و (بعضی از آفریدگان،) از غیر خدا، چیزی را می‌پرستند که (خدا) بر (تأیید) آن حجتی نازل نکرده و بدان (بینش و) دانشی (هم) ندارند. و برای ستمکاران هیچ یاوری نیست.'),
(2667, 22, 72, 'و چون آیات ما با روشنایی(و روشنگری) بر آنان خوانده شود، در چهره‌های کسانی که کافر شدند (اثر انگار و) انکارشان را می‌شناسی؛ چیزی نمانده که بر کسانی که آیات ما را بر ایشان تلاوت می‌کنند سخت حمله‌ور شوند. بگو: «آیا پس شما را به بدتر از این خبری مهم دهم‌؟ (آن، همان) آتش است که خدا آن را به کسانی که کفر ورزیده‌اند وعده داده و چه بد سرانجامی است!»'),
(2668, 22, 73, 'هان ای مردمان! مَثَلی زده شد، پس آن را به گوش برگیرید. بی‌گمان کسانی را که از (آفریدگانِ) غیر خدا می‌خوانید، هرگز (حتی) مگسی هم نیافریدند، و گرچه برای (آفریدن) آن اجتماع کنند. و اگر (هم) آن مگس چیزی از آنان برباید، آن را با هیچ کوششی از او باز پس نگیرند؛ طالب [:این جویندگان] و مطلوب [:آن مگس] هر دو ناتوانند.'),
(2669, 22, 74, 'قدر (و منزلت الوهیت و ربوبیت) خدا را چنان که سزاوار اوست نشناختند. در حقیقت، خداست که همواره بسی نیرومندِ عزیز است.'),
(2670, 22, 75, 'خدا از میان فرشتگان رسولانی برمی‌گزیند و (نیز) از میان مردمان. بی‌گمان خدا بسی شنوای بسیار بیناست.'),
(2671, 22, 76, 'آنچه را پیش روی آنان و آنچه پشت(سر)شان است می‌داند و همه‌ی امور [:کارها، چیزها و فرمان‌ها] تنها به خدا بازگردانیده می‌شوند.'),
(2672, 22, 77, 'هان ای کسانی که ایمان آوردید! رکوع و سجود کنید و پروردگارتان را بپرستید و کار خیر را انجام دهید، شاید (خود و دیگران را) رستگار کنید.'),
(2673, 22, 78, 'و در راه خدا چنان که حقّ جهاد (در راه) اوست جهاد کنید. اوست که شما را برگزید و در دین بر شما هیچ گونه حرج و تنگنایی قرار نداد. (نگهبان) روش (ربانی) پدرتان ، ابراهیم، باشید. او بود که از پیش شما را مسلمان نامید و در این (قرآن نیز همان آمده است) تا پیامبر بر شما گواه باشد و شما (هم) بر مردمان گواه باشید. پس نماز را بر پا بدارید و زکات را بدهید و به خدا پناه و عصمت گیرید. او مولای شماست‌؛ چه نیکو مولایی و چه نیکو یاوری.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'مؤمنان بی‌گمان (خودشان و دیگران را) رستگار کردند.'),
(2675, 23, 2, 'کسانی که ایشان در نمازشان دلباختگانند.'),
(2676, 23, 3, 'و کسانی که ایشان از (کارهای) بیهوده رویگردانانند.'),
(2677, 23, 4, 'و کسانی که ایشان برای زکات، فاعلان (و کارگزاران)اند.'),
(2678, 23, 5, 'و کسانی که ایشان برای عورت‌هاشان نگهبانانند.'),
(2679, 23, 6, 'مگر بر همسرانشان یا دستاوردشان (مانند کنیزان که) بی‌گمان بر آنان نکوهشی نیست.'),
(2680, 23, 7, 'پس هر کس فراتر از این جوید، آنان، (هم) آنان متجاوزانند.'),
(2681, 23, 8, 'و کسانی که برای امانت‌ها و پیمان‌هاشان رعایت‌کنندگانند.'),
(2682, 23, 9, 'و کسانی که بر نمازهایشان نگهبانانند.'),
(2683, 23, 10, 'ایشان خودشان وارثانند.'),
(2684, 23, 11, 'کسانی که بهشت را به ارث می‌برند، و در آنجا ایشان جاودانانند'),
(2685, 23, 12, 'و بی‌گمان انسان را به‌راستی از عصاره‌ای، از گلی آفریدیم.'),
(2686, 23, 13, 'سپس او را (به صورت) نطفه‌ای در جایگاهی استوار قرار دادیم.'),
(2687, 23, 14, 'سپس نطفه را (به صورت) علقه‌ای [:آویزکی] در آوردیم. پس علقه را (به صورت) گوشتکی لهیده آفریدیم و آن‌گاه (هم) آن گوشتک لهیده را استخوان‌هایی آفریدیم. پس (از آن)، استخوان‌ها را با گوشتی پوشاندیم (و) سپس جنین را آفریده‌ای دگر [:روح] ایجاد نمودیم. پس خدا بسی مبارک (و) بهترین آفرینندگان است.'),
(2688, 23, 15, 'سپس شما به‌راستی پس از این (مراحل) بی‌چون مردگانید.'),
(2689, 23, 16, 'سپس بی‌گمان شما در روز رستاخیز(تان) برانگیخته خواهید شد.'),
(2690, 23, 17, 'و به‌راستی بی‌چون ما بر سر (و سامان)تان هفت راه (راهوار) بیافریدیم. و از آفرینش و آفریدگان غافل نبوده‌ایم.'),
(2691, 23, 18, 'و از آسمان آبی به اندازه‌ای (معین) فرود آوردیم. پس آن را در زمین جای دادیم. و ما همچنان بر بازگردانیدن آن بی‌گمان تواناییم.'),
(2692, 23, 19, 'پس برای شما با آن، باغ‌هایی از درختان خرما و انگور به وجود آوردیم، (که) در آنها برایتان میوه‌های فراوان است و از آنها می‌خورید.'),
(2693, 23, 20, 'و درختی را از طور سینا آفریدیم، که برای خورندگان، روغن و خوراکی و رنگ و جلا(ی معده‌ها) است.'),
(2694, 23, 21, 'و به‌راستی برای شما در حیوانات نعمت‌وار همواره عبرتی است؛ از آنچه که در شکم‌های آنهاست به شما می‌نوشانیم، و برایتان در آنها سودهای فراوان است و از آنها می‌خورید.'),
(2695, 23, 22, 'و بر آنها و بر کشتی‌ها (سوار و) بار می‌شوید.'),
(2696, 23, 23, 'و به‌راستی نوح را سوی قومش همی فرستادیم. پس (به آنان) گفت: «ای قوم من! خدا را بپرستید، برایتان جز او خدایی نیست. پس مگر پروا نمی‌دارید؟»'),
(2697, 23, 24, 'پس اشراف از (میان) قومش - که کافر شدند- گفتند: «این جز بشری همچون شما نیست، می‌خواهد بر شما برتری جوید و اگر خدا می‌خواست بی‌گمان فرشتگانی (برایتان) می‌فرستاد. ما در میان پدران نخستینمان چنان (چیزی) نشنیدیم.»'),
(2698, 23, 25, '«او جز مردی نیست که در وی جنونی است، پس تا چندی درباره‌اش به‌درستی انتظار کشید.»'),
(2699, 23, 26, '(نوح) گفت: «پروردگارم! به (سزای) آنچه مرا تکذیب کردند، یاریم ده.»'),
(2700, 23, 27, 'پس به او وحی کردیم که: «برابر چشم‌هایمان و وحی‌مان کشتی (نجات) را بساز و چون فرمان ما در رسید و تنور به فوران آمد، پس در آن از هر نوع حیوان (خشکی) دو تا (، یکی نر و دیگری ماده،) با خانواده‌ات روان کن - بجز کسی از آنان که کلمه(ی عذاب) بر او پیشی گرفته است - و درباره‌ی کسانی که ظلم کرده‌اند با من هرگز سخن مگوی، (زیرا) آنان بی‌گمان غرق‌شوندگانند.»'),
(2701, 23, 28, '«پس هنگامی که تو با همراهانت بر کشتی آرمیدی، بگو ستایش خدایی را (سزا)ست که ما را از (چنگ) گروه ظالمان رهانید.»'),
(2702, 23, 29, 'و بگو: «پروردگارم! مرا در فرودگاهی پر برکت فرود آر، حال آنکه تو نیک‌ترین فرودآورندگانی.»'),
(2703, 23, 30, 'به‌راستی در این (ماجرا) بی‌چون نشانه‌هایی است. ما به‌راستی همواره آزماینده بوده‌ایم.'),
(2704, 23, 31, 'سپس، از پی آنان نسلی دیگر پدید آوردیم.'),
(2705, 23, 32, 'پس در میانشان پیامبری از خودشان فرستادیم که: «خدا را بپرستید، جز او برای شما هرگز معبودی دگر نیست، پس آیا سر پرهیزگاری ندارید؟»'),
(2706, 23, 33, 'و اشراف قومش - که کافر شدند و دیدار آخرت را تکذیب کردند و در زندگی دنیا آنان را مرفه ساخته بودیم - گفتند: «این (مرد) جز بشری همچون شما نیست؛ از آنچه می‌خورید می‌خورد و از آنچه می‌نوشید، می‌نوشد.»'),
(2707, 23, 34, '«و بی‌گمان اگر بشری همانند خودتان را اطاعت کنید، در آن هنگام (و هنگامه) بی‌چون زیانکارانید.»'),
(2708, 23, 35, '«آیا به‌راستی شما را وعده می‌دهد که وقتی مردید و خاک و استخوان بودید (باز هم) شما هماره (از گور زنده) بیرون‌شوندگانید؟»'),
(2709, 23, 36, '«وه! وه! چه دور است آنچه وعده می‌شوید.»'),
(2710, 23, 37, '«زندگی جز این زندگانی دنیای ما نیست (که) می‌میریم و زنده می‌شویم و (دیگر) ما از برانگیخته‌شدگان نیستیم.»'),
(2711, 23, 38, '«او جز مردی که بر خدا دروغ بسته است نیست و ما (هرگز) برای او ایمان‌آورندگان نیستیم.»'),
(2712, 23, 39, 'گفت: «پروردگارم! از آن روی که مرا تکذیب کردند یاریم ده.»'),
(2713, 23, 40, 'فرمود: «به زودی از آن (کارشان) بی‌گمان (و) بی‌امان (از) پشیمانانند.»'),
(2714, 23, 41, 'پس فریاد (مرگبار) آنان را بحق فرو گرفت. پس آنها را (چون) خاشاکی گردانیدیم. پس دورباش بر گروه ستمکاران!'),
(2715, 23, 42, 'سپس، از پی آنان نسل‌های دیگری پدید آوردیم.'),
(2716, 23, 43, 'هیچ امتی نه از اجل خود پیشی می‌جوید و نه دیرکرد آن را می‌طلبد.'),
(2717, 23, 44, 'سپس فرستادگان خود را پیاپی فرستادیم. هر بار برای (هدایت) امتی پیامبرش آمد، او را تکذیب کردند؛ پس ما بعضی (از آنان) را (از) پی بعضی دیگر آوردیم و آنها را زبانزد (مردمان) گردانیدیم. پس دورباش برای گروهی که ایمان نمی‌آورند!'),
(2718, 23, 45, 'موسی و برادرش هارون را با آیات خودمان و حجتی با سلطه‌ای روشنگر فرستادیم،'),
(2719, 23, 46, 'سوی فرعون و ویژگانش. پس تکبر نمودند و مردمی سلطه‌گر و بلندپرواز بودند.'),
(2720, 23, 47, 'پس گفتند: «آیا ما به دو بشر که مانند خودمانند - در حالی که طایفه‌ی آنها بندگان مایند - ایمان بیاوریم‌؟»'),
(2721, 23, 48, 'نتیجه، آن دو را تکذیب کردند، پس از زمره‌ی هلاک‌شدگان بودند.'),
(2722, 23, 49, 'و به‌راستی و درستی به موسی کتاب (وحیانی) دادیم، شاید آنان راه یابند.'),
(2723, 23, 50, 'و پسر مریم و مادرش را نشانه‌ای گردانیدیم و آن دو را سوی سرزمین بلندی که آسایشگاه و جایگاه آب زلال بود پناهگاه دادیم.'),
(2724, 23, 51, 'هان ای پیامبران! از چیزهای پاکیزه بخورید و کار شایسته(ی رسالت) کنید، من بی‌گمان به آنچه انجام می‌دهید بسی دانایم.'),
(2725, 23, 52, 'و این‌ها [:جمع مکلّفان] به‌راستی (همگان) امت شمایند، امتی یگانه (درجهت رسالت وحیانی) و من پروردگار (یگانه‌ی) شمایم‌؛ پس مرا پروا بدارید.'),
(2726, 23, 53, 'پس (آن‌گاه) کار (دین)شان را میان خودشان (با) نوشته‌هایی گوناگون قطعه قطعه کردند (و) هر دسته‌ای به آنچه نزدشان است، خوش(حال)اند.'),
(2727, 23, 54, 'پس در ورطه‌ی گمراهیشان تا چندی واگذارشان.'),
(2728, 23, 55, 'آیا (چنان) می‌پندارند که بی‌گمان (با) آنچه از مال و پسران بدیشان مدد می‌کنیم (و) یاریشان می‌رسانیم؟'),
(2729, 23, 56, '(این از آن روست که) می‌خواهیم برایشان در خیرات شتاب ورزیم‌؟ (نه!) بلکه دقت و باریک‌بینی نمی‌کنند.'),
(2730, 23, 57, 'بی‌گمان کسانی که ایشان از خشیت پروردگارشان با بزرگداشت(اش) هراسانند؛'),
(2731, 23, 58, 'و کسانی که (هم)ایشان به نشانه‌های پروردگارشان ایمان می‌آورند؛'),
(2732, 23, 59, 'و کسانی که (هم)ایشان با پروردگارشان (کسی را) شریک نمی‌آورند؛'),
(2733, 23, 60, 'و کسانی که آنچه دادند (در راه خدا) می‌دهند، حال آنکه که دل‌هایشان هراسان است، که بی‌گمان سوی پروردگارشان بازگشت‌کنندگانند؛'),
(2734, 23, 61, 'اینان در کارهای نیک شتاب می‌ورزند، و در انجام آنها (از یکدیگر) پیشی گیرندگانند.'),
(2735, 23, 62, 'و ما هیچ کس را جز به اندازه‌ی توان و گشایشش تکلیف نمی‌کنیم و نزد ما کتابی است که به تمامی حق سخن می‌گوید، حال آنکه به آنان ستم نمی‌شود.'),
(2736, 23, 63, 'بلکه دل‌هایشان از این (حقیقت) در (غرقاب) غفلت است و برایشان بجز این (گناهان) کردارهایی (دیگر) است، که برای (رسیدن به) آنها کارگزارند.'),
(2737, 23, 64, 'تا (هنگامی) که خوشگذران‌هایشان را به عذاب برگرفتیم، ناگاه - چون وحشیان - خشمناکان فریاد می‌زنند.'),
(2738, 23, 65, '(به آنان گفته شود:) «امروز نعره‌ی وحشیانه نزنید! بی‌چون شما از جانب ما یاری نخواهید شد.»'),
(2739, 23, 66, '«به‌راستی آیات من بر شما خوانده می‌شد، پس شما بودید که همواره به عقب بر گذشته‌هاتان زیر و زبر می‌شوید.»'),
(2740, 23, 67, 'حال آنکه به (پذیرفتن) آن استکبار و برتری می‌جویید و شب‌هنگام (در محافل خود) هذیان می‌گویید.»'),
(2741, 23, 68, 'آیا پس (از این) در این سخن نیندیشیدند، یا چیزی برای آنان آمده که برای پدران نخستینشان نیامده‌؟'),
(2742, 23, 69, 'یا پیامبر خود را نشناخته‌اند؟ پس انکار (و انگار)کنندگان اویند؟'),
(2743, 23, 70, 'یا می‌گویند در او جنونی است‌؟ (نه!) بلکه او تمامی حق را برای ایشان آورده حال آنکه بیشترشان حق را بسی ناپسند می‌دارند.'),
(2744, 23, 71, 'و اگر حق (بر فرض محال) از هوس‌های آنان پیروی می‌کرد، بی‌گمان آسمان‌ها و زمین و آنان که در آنهایند تباه می‌شدند. بلکه یادنامه‌شان را به ایشان دادیم. پس آنها از (پیروی) یادنامه‌ی خود رویگردانند.'),
(2745, 23, 72, 'آیا از ایشان هزینه‌ای مطالبه می‌کنی؟ پس خراج و رحمت پروردگارت بهتر است. و اوست که بهترین روزی‌دهندگان است.'),
(2746, 23, 73, 'و بی‌گمان تو اینان را به راهی بس راست می‌خوانی.'),
(2747, 23, 74, 'و بی‌چون کسانی که به آخرت ایمان ندارند، از (این) راه (راست) سخت منحرفند.'),
(2748, 23, 75, 'و اگر ایشان را رحم کنیم و آنچه زیان در خود دارند (زدوده و) برگشاییم، بی‌گمان در طغیانشان در حال سرگردانی لجاجت می‌کنند.'),
(2749, 23, 76, 'و بی‌گمان ایشان را بی‌چون به عذاب گرفتار کردیم. پس نسبت به پروردگارشان فروتنی نکردند و همی زاری (و خاکساری) نمی‌کنند.'),
(2750, 23, 77, 'تا هنگامی که دری - دارای عذابی سخت - بر آنان گشودیم. ناگهان ایشان در آن (حال) نومید و بسی اندوهگینند‌.'),
(2751, 23, 78, 'و اوست کسی که برای شما گوش‌ها و چشم‌ها و دل‌های فروزان پدید آورد. (پس چه) اندکی سپاس می‌گزارید.'),
(2752, 23, 79, 'و اوست کسی که شما را در زمین نمایان کرد و تنها سوی اوست که گردآورده خواهید شد.'),
(2753, 23, 80, 'و اوست کسی که زنده می‌کند و می‌میراند و (توانایی ایجاد) اختلاف شب و روز تنها برای اوست. پس مگر خردورزی نمی‌کنید؟'),
(2754, 23, 81, 'بلکه آنان (نیز) همانند آنچه پیشینیان(شان) گفته بودند، گفتند.'),
(2755, 23, 82, 'گفتند: «آیا هنگامی که مردیم و خاک و استخوان بودیم، آیا بی‌چون، و بی‌گمان ما بازگشت‌شوندگانیم‌؟»'),
(2756, 23, 83, '«درست همین را - از (زمانی) پیش - ما و پدرانمان همواره وعده داده شدیم. این جز افسانه‌ها و بافته‌های پیشینیان(شان چیزی) نیست.»'),
(2757, 23, 84, 'بگو: «اگر می‌دانسته‌اید، زمین و هر که در آن است برای کیست‌؟»'),
(2758, 23, 85, 'به زودی خواهند گفت: «برای خداست.‌» بگو: «پس آیا (او را) به خوبی یاد نمی‌کنید؟»'),
(2759, 23, 86, 'بگو: «پروردگار آسمان‌های هفت‌گانه و پروردگار عرش بزرگ کیست‌؟»'),
(2760, 23, 87, 'زودا (که) گویند: «(این‌ها) برای خداست.» بگو: «پس آیا (از گناهان) پرهیز نمی‌کنید؟»'),
(2761, 23, 88, 'بگو اگر می‌دانسته‌اید ملکوت و واقعیت هر چیزی به دست (علم و قدرت) کیست، در حالی که هم او پناه می‌دهد و بر زیانش (کسی) پناه داده نمی‌شود؟'),
(2762, 23, 89, 'زودا (که) خواهند گفت: «(ملکوت همه چیز) برای خداست.‌» بگو: «پس کجا و چه زمانی (و چرا) دچار افسون می‌شوید؟»'),
(2763, 23, 90, '(نه!) بلکه با تمامی حق نزدشان آمدیم و بی‌گمان آنان دروغگویانند.'),
(2764, 23, 91, 'خدا هیچ فرزندی را بر نگرفت و با او هیچ معبودی (دیگر) نبود زیرا در این هنگام (و هنگامه) بی‌شک هر خدایی آنچه را آفریده (بود) با خود می‌بُرد و بی‌چون بعضی از آنان بر بعضی دیگر تفوّق می‌جستند. منزّه است خدا از آنچه وصف(اش) می‌کنند.'),
(2765, 23, 92, 'دانای نهان و آشکار است، پس از آنچه (با او) شریک می‌گردانند بسی برتر است.'),
(2766, 23, 93, 'بگو: «پروردگارم! اگر آنچه را که به آنان وعده می‌شود، بی‌گمان به من نشان می‌دهی.»'),
(2767, 23, 94, 'پروردگارم! پس مرا در (این هنگامه، میان) ستمکاران قرار مده.»'),
(2768, 23, 95, 'و ما به‌راستی تواناییم که آنچه را به آنان وعده داده‌ایم به‌درستی بر تو بنمایانیم.'),
(2769, 23, 96, 'بدی را به نیکوترین (شیوه) دفع کن. ما به آنچه وصف می‌کنند داناتریم.'),
(2770, 23, 97, 'و بگو: «پروردگارم! از وسوسه‌های پنهانی فشارآورِ شیطان‌ها به تو پناه می‌برم.»'),
(2771, 23, 98, '«و از اینکه (آنها) نزدم حاضر شوند، پروردگارم! به تو پناه می‌برم.»'),
(2772, 23, 99, 'تا آن‌هنگام که مرگْ یکی از ایشان را در رسد، گوید: «پروردگارم! مرا بازگردانید.»'),
(2773, 23, 100, '«شاید من در آنچه وانهادم کار نیکی انجام دهم.» (بدو گفته شود:) «هرگز! این سخنی است که او (تنها) گوینده‌ی آن است، حال آنکه از دنبالشان (میانه‌ی دو زندگی دنیا و آخرت) برزخی است تا روزی که برانگیخته خواهند شد.»'),
(2774, 23, 101, 'پس آن‌گاه که در صور [:بوق جان‌افزا] دمیده شود، پس هرگز میانشان نسبت‌های خویشاوندی وجود ندارد و از (حال) یکدیگر (هم) نمی‌پرسند.'),
(2775, 23, 102, 'پس کسانی که میزان‌هایشان سنگین است، در نتیجه ایشان همان رستگارکنندگانند.'),
(2776, 23, 103, 'و کسانی که میزان‌هایشان سبک است، ایشان خویشتن را زیان کرده (و) در جهنّم ماندگارانند.'),
(2777, 23, 104, 'آتش چهره‌ی آنها را دگرگون و تباه می‌سازد حال آنکه در آنجا شکافته‌لبان (و) گشاده‌دهان با لب‌های آویزان و دندان‌های نمایان و چهره‌هایی دگرگونند.'),
(2778, 23, 105, '(خدا به آنان گوید:) «آیا آیات من بر شما خوانده نمی‌شده است. پس به وسیله‌ی آنها (همان‌ها و ما را) تکذیب می‌کرده‌اید!»'),
(2779, 23, 106, 'گفتند: «پروردگارمان! شقاوتمان بر (سروسامان)مان چیره شد و ما گروهی گمراه بوده‌ایم.»'),
(2780, 23, 107, '«پروردگارمان! ما را از اینجا برون بر، پس اگر (بازهم به آنچه بودیم) برگشتیم، بی‌گمان ما ستمگرانیم.»'),
(2781, 23, 108, 'فرمود: «در آن (آتش همچو سگان)- خوار و بی‌مقدار- گم شوید و با من سخن مگویید.»'),
(2782, 23, 109, '«به‌راستی، دسته‌ای از بندگان من بودند که می‌گفتند: “پروردگارمان! ما ایمان آوردیم، بر ما بپوشان و به ما رحم کن حال آنکه تو بهترین رحم‌کنندگانی.»'),
(2783, 23, 110, '«پس شما آنان را به ریشخند برگرفتید، تا (با این کار) یاد مرا از خاطرتان بردند، حال آنکه شما از (ایمان) ایشان پوزخند می‌زده‌اید.»'),
(2784, 23, 111, '«من به‌راستی امروز به (پاس) شکیبایی‌شان، به آنان پاداش دادم، زیرا هم‌ایشان به سلامتی دست‌یابندگان به خیرند.»'),
(2785, 23, 112, 'فرمود: «تعداد چند سال در زمین ماندید؟»'),
(2786, 23, 113, 'گفتند: «یک روز یا پاره‌ای از یک روز ماندیم، پس از شمار‌گران بپرس.»'),
(2787, 23, 114, 'فرمود: «جز اندکی درنگ نکردید، اگر شما به‌راستی می‌دانسته‌اید.»'),
(2788, 23, 115, '«پس آیا پنداشتید همواره شما را بیهوده آفریدیم و اینکه بی‌چون شما سوی ما باز گردانیده نمی‌شوید؟»'),
(2789, 23, 116, 'پس والاترست خدا؛ فرمانروای تمام حق (و حقیقت). معبودی جز او نیست. اوست پروردگار عرش پرکرامت.'),
(2790, 23, 117, 'و هر کس با خدا معبود دیگری بخواند، برای او هرگز برهانی بر آن نیست و حسابش تنها نزد پروردگارش می‌باشد. و کافران بی‌گمان رستگار کنندگان نیستند.'),
(2791, 23, 118, 'و بگو: «پروردگارم! (بر من) بپوشان و رحم کن، حال آنکه تو بهترین رحم‌کنندگانی.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, '(این) سوره‌ای است (که) آن را نازل و (بر مکلفان) حتمی گردانیدیم و در آن آیاتی روشن فرو فرستادیم، شاید شما به خوبی یاد آورید.'),
(2793, 24, 2, '(هر) زن زناکار و مرد زناکار را صد تازیانه بزنید و اگر به خدا و روز بازپسین ایمان داشته‌اید، در دین خدا نسبت به آن دو هیچ دلسوزی شما را برنگیرد و باید گروهی از مؤمنان (دست‌کم دو نفر) در کیفر آن دو حضور یابند.'),
(2794, 24, 3, 'مرد زناکار، جز زن زناکار یا مشرک را به همسری نگیرد و زن زناکار، جز مرد زناکار یا مشرک را به همسری نگیرد و این‌گونه ازدواج بر مؤمنان حرام گردیده است.'),
(2795, 24, 4, 'و کسانی که به زنان پاکدامن نسبت زنا می‌دهند، سپس چهار گواه مرد (عادل بر این نسبت) نیاورده‌اند، آنان را هشتاد تازیانه بزنید و هیچ‌گاه شهادتی از آنها نپذیرید و اینان، (هم)ایشان فاسقانند.'),
(2796, 24, 5, 'مگر کسانی که بعد از آن (بهتان) توبه و (آن بهتان را) اصلاح کردند. پس خدا بی‌گمان پوشنده‌ی رحمتگر بر ویژگان است.'),
(2797, 24, 6, 'و مردانی که به همسران خود نسبت زنا می‌دهند و جز خودهاشان گواهانی (دیگر) ندارند، پس گواهی هر یک از آنان چهار بار سوگند به خداست، که او [:شوهر] بی‌گمان همانا از راستگویان است.'),
(2798, 24, 7, 'و گواه پنجمین این است که (شوهر بگوید که) لعنت خدا بر او باد اگر از دروغگویان بوده است.'),
(2799, 24, 8, 'و عذابش را برطرف می‌سازد که (اگر) این زن چهار مرتبه خدا را گواه گیرد که همسرش بی‌گمان از دروغگویان بوده است.'),
(2800, 24, 9, 'و (نیزگواهی) پنجمین را - که همانا خشم خدا بر او باد - اگر (شوهرش) از راستگویان بوده است.'),
(2801, 24, 10, 'و اگر فضل خدا و رحمتش بر شما نبود و اینکه خدا بسی برگشت‌کننده(ی بر بندگان) و سنجیده کار است (رسوا می‌شدید).'),
(2802, 24, 11, 'بی‌گمان کسانی که بهتان [:داستان افک ] را (به میان) آوردند، دسته‌ای متعصب از شمایند. این (تهمت) را شرّی برای خود به‌حساب نیاورید، بلکه آن برای شما خیری است و هر مردی از آنان (که در این کار دست داشته همان) گناه دنباله‌داری که مرتکب شده برای(خود) اوست و آن کس از ایشان که (قسمت) عمده‌ی آن را به گردن گرفته، برای او عذابی بزرگ است.'),
(2803, 24, 12, 'چرا چون آن (بهتان) را شنیدید، مردان و زنان مؤمن گمان نیک به خودشان نبردند و نگفتند: «این بهتانی آشکارگر است»؟'),
(2804, 24, 13, 'چرا چهار گواه مرد (عادل) بر آن (بهتان) نیاوردند؟ پس چون (این) گواهان را نیاوردند، اینان نزد خدا، (هم)اینان دروغگویانند.'),
(2805, 24, 14, 'و اگر فضل خدا و رحمتش در دنیا و آخرت بر شما نبود، همواره - به (سزای) آنچه در آن غور کرده (و) اشاعه دادید - به شما عذابی بزرگ می‌رسید.'),
(2806, 24, 15, 'چون آن (بهتان) را با زبان‌هاتان بس می‌پذیرید و با دهان‌هاتان چیزی را که برایتان به آن علمی نیست، می‌گویید و گمان می‌کنید که آن (کاری) سهل و ساده است، حال اینکه آن (بهتان) نزد خدا بس بزرگ است.'),
(2807, 24, 16, 'و چرا وقتی آن را شنیدید نگفتید: «برای ما سزاوار نیست که در این (بهتان) سخن گوییم. خدایا تو منزّهی. این بهتانی بزرگ است.»'),
(2808, 24, 17, 'خدا اندرزتان می‌دهد که (مبادا) هیچ‌گاه به همانند آن - اگر مؤمن بوده‌اید - برگشت کنید.'),
(2809, 24, 18, 'و خدا برای شما نشانه‌ها(ی ربانی) را بیان می‌کند. و خدا بسی دانای سنجیده‌کار است.'),
(2810, 24, 19, 'بی‌گمان آنان که دوست دارند تا زشتکاری تجاوزگر در میان کسانی که ایمان آورده‌اند، فرا‌گیر شود، برایشان در دنیا و آخرت عذابی پر درد است. و خدا می‌داند و شما نمی‌دانید.'),
(2811, 24, 20, 'و اگر فضل و رحمت خدا بر (سروسامان)تان نبود و اینکه خدا رئوفِ رحمتگر بر ویژگان است (فضیحت ومجازات سختی در انتظارتان بود).'),
(2812, 24, 21, 'هان ای کسانی که ایمان آورد‌ید! گام‌های شیطان را پیروی مکنید و هر کس گام‌های شیطان را پیروی کند همواره او [:شیطان] به زشتکاری متجاوز و کاری ناپسند وامی‌دارد. و اگر فضل خدا و رحمتش بر شما نبود، هرگز هیچ کس از شما پاک نمی‌شد. ولی خداست که هر کس را بخواهد پاک می‌گرداند. و خدا بس شنوای بسیار داناست.'),
(2813, 24, 22, 'و صاحبان فضیلت از شما، نباید از دادن [:بخشش مالی] به نزدیکترین خویشاوندان و تهیدستان و مهاجران راه خدا دریغ ورزند و باید عفو کنند و همواره گذشت نمایند. مگر دوست ندارید که خدا برای شما بپوشد؟ و خدا بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(2814, 24, 23, 'بی‌گمان، کسانی که به زنان پاکدامن بی‌خبرِ غافلِ باایمان نسبت (انحراف جنسی) می‌دهند، در دنیا و آخرت لعنت شده‌اند و برای آنان عذابی سخت است.'),
(2815, 24, 24, 'روزی که زبان‌ها و دست‌ها و پاهایشان - بر ضد آنان - بدانچه انجام می‌داده‌اند، گواهی می‌دهند.'),
(2816, 24, 25, 'در آن هنگام (و هنگامه)، خدا پاداش طاعت شایسته‌ی آنان را به‌طور کامل به ایشان می‌دهد و (آنان) می‌دانند که خدا همواره، (هم)او تمام حقیقتِ روشنگر است.'),
(2817, 24, 26, 'پلید‌ی‌ها برای پلیدان و پلیدان برای پلیدی‌‌ها و پاکیزه‌ها برای پاکیزگان و پاکیزگان برای پاکیزه‌هایند. اینان از آنچه (دیگران) درباره‌ی ایشان می‌گویند برکنارند و برای آنان پوشش و روزی‌ای نیکو و باکرامت است.'),
(2818, 24, 27, 'هان ای کسانی که ایمان آوردید! در خانه‌هایی غیر از خانه‌های خودتان داخل نشوید، تا (با آنان) انس (و اجازه‌ای) بگیرید. و بر اهل آن (خانه)ها سلام گویید. این (کارتان) برایتان خوب است. شاید شما متذکر شوید.'),
(2819, 24, 28, 'پس اگر کسی را در آن خانه‌ها نیافتید، داخل آنها نشوید تا به شما اجازه داده شود و اگر به شما گفته شد «برگردید»، برگردید، (که) آن (برگشتن) برای شما پاکیزه‌تر است. و خدا به آنچه انجام می‌دهید بسی داناست.'),
(2820, 24, 29, 'بر شما گناهی نیست که به خانه‌هایی غیر مسکونی [:عمومی] - که در آنها برای شما متاعی است - در آیید. و خدا آنچه را آشکار می‌کنید و آنچه را پنهان می‌دارید می‌داند.'),
(2821, 24, 30, 'به مردان باایمان بگو (که عورت‌های دیگران را) از دیده‌هاشان فرو نهند و عورت‌های خودشان را (از دیدگان دیگران) نگهبانی کنند. این برای آنان پاکیزه‌تر است. به‌راستی خدا به آنچه می‌کنند بسی آگاه است.'),
(2822, 24, 31, 'و به زنان باایمان بگو (که) دیدگان خود را (از دیدن عورت‌های دیگران) فرو نهند و عورت‌هایشان را (از هر گونه نگرشی) حفظ کنند و زینت‌های خودشان را (برابر مردان نامحرم) نمایان نگردانند -مگر آنچه (که طبعاً) از آنها پیدا بود- و باید روسری‌های خود را (بر سینه‌هاشان) فرونهند و زینتشان را جز برای شوهرانشان، یا پدرانشان، یا پدران شوهرانشان، یا پسرانشان، یا پسران شوهرانشان، یا برادرانشان، یا پسران برادرانشان، یا پسران خواهرانشان، یا زنان (خدمتگزار غیر مسلمان)شان، یا زرخریدهاشان، یا مردانی که در زندگی تابع و در حاشیه‌ی آنانند و نیاز جنسی (هم) ندارند، یا کودکانی که بر عورت‌های زنان سلطه‌ای حاصل نکرده‌اند (زینت‌های خود را) آشکار نکنند؛ و پاهای خود را (به گونه‌ای به زمین) نکوبند تا آنچه از زینتشان نهفته می‌دارند آشکار گردد. ای مؤمنان! همگان (از مردان و زنان) به خدا بازگشت کنید، شاید (خود و دیگران را) رستگار کنید.'),
(2823, 24, 32, 'و بی‌همسران از خود [:آزادان] را و غلامان و کنیزان شایسته‌تان را همسر دهید. اگر تنگدستند، خدا آنان را از فضل خویش بی‌نیاز می‌کند. و خدا گشایشگری بس داناست.'),
(2824, 24, 33, 'و کسانی که (وسیله‌ی) زناشویی نمی‌یابند، باید پاکدامنی ورزند، تا خدا آنان را از فضل خویش بی‌نیاز گرداند. و از میان غلامانتان، کسانی که در صددند با قرارداد کتبی، خود را آزاد کنند، اگر در آنان خیری (از ایمان و آمال و مال) یافتید، (قرار) بازخرید آنها را بنویسید و از آن مالی که خدا به شما داده است به ایشان بدهید (تا خود را آزاد کنند) و کنیزان خود را - در صورتی که تصمیم به پاکدامنی دارند - برای اینکه متاع زندگی دنیا را بجویید، به زنا وادار مکنید، و هر کس آنان را به اجبار (به زنا) وادار کند بی‌گمان خدا پس از اجبار نمودنشان، پوشنده‌ی (گناه اجبارشدگان و) رحمتگر بر ویژگان است.'),
(2825, 24, 34, 'و همانا به‌راستی سوی شما آیاتی روشنگر و نمادی افشاگر -از کسانی که پیش از شما روزگاران را به سر بردند- و موعظه‌ای برای پرهیزگاران فرود آورده‌ایم.'),
(2826, 24, 35, 'خدا نور آسمان‌ها و زمین است. نماینده‌ی نورش چون چراغدانی است که در آن چراغی و آن چراغ درون شیشه‌ای است؛ آن شیشه گویی اختری درخشان است که از درخت خجسته‌ی زیتونی -که نه شرقی است و نه غربی- برافروخته می‌شود؛ نزدیک است (که) روغنش - هر چند بدان آتشی نرسیده باشد - روشنی بخشد؛ نوری است فرا روی نوری (دگر). خدا هر که را بخواهد سوی خویش هدایت می‌کند و خدا برای مردم مَثَل‌ها می‌زند و خدا به هر چیزی بسی داناست.'),
(2827, 24, 36, '(این نماد نور) در خانه‌هایی است که خدا رخصت داده تا رفعت یابند و نامش در آنها یاد شود. او را در آن خانه‌ها صبحگاهان و شامگاهان (نیایش و) تنزیه می‌کنند.'),
(2828, 24, 37, '(راد) مردانی که نه (هیچ) تجارت و نه (هیچ) داد و ستدی، آنان را از یاد خدا و بر پا داشتن نماز و دادن زکات باز نمی‌دارد، و از روزی که دل‌ها و دیده‌ها در آن دگرگون می‌شوند می‌هراسند.'),
(2829, 24, 38, 'تا خدا بهترین اعمالی را که انجام داده‌اند به ایشان پاداش دهد و از فضل خود برایشان بیفزاید. و خدا هر که را بخواهد بی‌حساب روزی می‌دهد.'),
(2830, 24, 39, 'و کسانی که کافر شدند، کارهایشان چون سرابی در زمینی هموار و بی‌برگ و بار است که تشنه آن را آبی می‌پندارد؛ تا آن هنگام که بدان رسد آن را چیزی نیابد و خدا را نزد خویش یابد، پس حسابش را به تمامی به او بدهد. و خدا زودشمار است.'),
(2831, 24, 40, 'یا مانند تاریکی‌هایی در دریای طغیان‌گر ژرفی است، که موجی بر رویش موجی دیگر، (و) بالای آن ابری است. تاریکی‌هایی است که بعضی بر روی بعضی انباشته است. هر گاه دستش را (از آن) بیرون آورد، هرگز آن را نبیند. و خدا برای هر کس (که) نوری ننهاده، او را هیچ نوری نیست.'),
(2832, 24, 41, 'آیا ندیدی همانا خدا، هر که (و هر چه) در آسمان‌ها و زمین است برای او همچنان تسبیح و تنزیه می‌کنند و پرندگان (نیز) در حالی که (در آسمان) صف‌اندرصف به پروازند، همه نماز و نیایش خود و تنزیهشان را همواره دانسته‌اند؟ و خدا به آنچه می‌کنند بسی داناست.'),
(2833, 24, 42, 'و فرمانروایی آسمان‌ها و زمین تنها از خداست و بازگشت (همه) تنها سوی خداست.'),
(2834, 24, 43, 'آیا ندیدی [:ندانستی] که خدا ابر را (از بخار) می‌کَنَد (و) سپس میان (اجزای) آن پیوند می‌دهد. سپس آن را روی هم انباشته و متراکم می‌سازد. پس قطره‌های نخستین باران را می‌بینی که از خلال آن بیرون می‌آید. و از آسمان از کوه‌هایی (از یخ) که در آنجاست تگرگی فرو می‌ریزد؛ پس هر کس را بخواهد با آن گزندی می‌رساند و آن را از هر که بخواهد باز می‌دارد. نزدیک است روشنی آذرخش، بلندای برقش دیدگان را بِبَرَد.'),
(2835, 24, 44, 'خدا شب و روز را زیر و زبر می‌کند (و) بی‌گمان در این (جریان) برای صاحبان بصیرت همچنان (درس) عبرتی است.'),
(2836, 24, 45, 'و خدا هر جنبنده‌ای را از آبی آفرید، پس پاره‌ای از آنان روی شکمش راه می‌رود و برخی از آنان روی دو پا(یش) و بعضی از آنها روی چهارپا(یش) روان است. خدا هر چه بخواهد می‌آفریند. به‌راستی خدا بر هر چیزی بسیار تواناست.'),
(2837, 24, 46, 'همواره به‌درستی آیاتی روشنگر فرود آوردیم. و خدا هر که را بخواهد به راهی بس راست هدایت می‌کند.'),
(2838, 24, 47, 'و می‌گویند: « (ما) به خدا ایمان آوردیم و به پیامبر (او هم) گرویدیم و اطاعت‌(شان) کردیم.‌» سپس دسته‌ای از ایشان پس از این روی بر می‌گردانند و ایشان (از) مؤمنان نیستند.'),
(2839, 24, 48, 'و هنگامی که سوی خدا و پیامبرش خوانده شوند -تا میانشان داوری کند- به ناگاه گروهی از آنان رویگردانند.'),
(2840, 24, 49, 'و اگر (در این حکم) برایشان حق (دلخواهشان) باشد، اذعان‌کنندگان، سویش روانند.'),
(2841, 24, 50, 'آیا در دل‌هایشان بیماری‌ای است، یا شک مستندی داشتند، یا می‌ترسند که خدا بر آنان ستم روا دارد و فرستاده‌اش (نیز ستم کند)؟ (نه،) بلکه اینان خودشان ستمکارانند.'),
(2842, 24, 51, 'گفتار مؤمنان – هنگامی که سوی خدا و پیامبرش خوانده شوند تا میانشان داوری کند - تنها این بود که: «شنیدیم و اطاعت کردیم.‌» و اینانند، (هم)ایشان رستگارکنندگان (خود و دیگران).'),
(2843, 24, 52, 'و کسی که خدا و فرستاده‌ی او را فرمان برد و از خدا بهراسد و از او پروا کند، پس اینان، (هم)ایشان به‌سلامتی‌رسندگان به خیرند.'),
(2844, 24, 53, 'و با سوگندهای مؤکدشان به خدا سوگند یاد کردند (که) اگر فرمانشان دهی، بی‌گمان (برای جهاد) همواره بیرون خواهند آمد. بگو: «سوگند مخورید. (تنها) اطاعتی شناخته‌شده (باید و) خدا به‌درستی به آنچه می‌کنید بسی داناست.»'),
(2845, 24, 54, 'بگو: «خدا را اطاعت کنید و از پیامبر(ش نیز) پیروی نمایید.» پس اگر پشت نمودند، (بدانید که) تنها بر عهده‌ی اوست آنچه (بدان) موظف شده و بر عهده‌ی شماست آنچه بدان موظف شدید. و اگر اطاعتش کنید راه خواهید یافت. و بر فرستاده(ی خدا) جز ابلاغ روشنگر (مأموریتی) نیست.'),
(2846, 24, 55, 'خدا - به کسانی از شما که ایمان آورده و کارهای شایسته(ی ایمان) کردند - وعده داده است که همواره (این کسان را) در زمین (و زمینه‌ی تکلیف) به‌راستی جانشین (ناکسان) کند؛ همان‌گونه که کسانی را که پیش از آنان بودند جانشین (ناکسانی) کرد و همواره دینشان را - که برایشان خشنود بود - همچنان برایشان پای‌برجا کند و همانا بیمشان را بی‌گمان به ایمنی (کامل) مبدل گرداند، در حالی‌که مرا عبادت کنند (و) چیزی را با من شریک نگردانند. و هر کس پس از آن به کفر گراید آنان، (هم) آنان فاسقانند.'),
(2847, 24, 56, 'و نماز را بر پا بدارید و زکات را بدهید و پیامبر (خدا) را فرمان برید، شاید مورد رحمت قرار گیرید.'),
(2848, 24, 57, 'و هرگز مپندار کسانی که کافر شدند (ما را) در زمین درمانده‌کنندگانند؛ حال آنکه پناهگاهشان آتش است و بی‌امان (چه) بد بازگشتگاهی است.'),
(2849, 24, 58, 'هان ای کسانی که ایمان آوردید! غلامان و کنیزان شما و کسانی از شما که به (حال) ازدواج نرسیده‌اند باید بی‌چون سه‌بار در شباروز از شما اجازه بخواهند: پیش از نماز بامدادان و هنگامی‌که جامه‌های خود را پس از نماز ظهر (برای استراحت) بیرون می‌آورید و پس از نماز عشاء. (این‌ها) سه عورت (و برهنگی) شماست. نه بر شما و نه بر آنان گناهی نیست که غیر از این سه هنگام گرد یکدیگر در حال گردش باشید، بعضی از شما بر (گرد) بعضی دیگر(تان). خدا آیات (خود) را این‌گونه برایتان بیان می‌کند و خدا بس دانایی سنجیده‌کار است.'),
(2850, 24, 59, 'و چون کودکانتان به (حال) احتلام (و شهوت جنسی) رسیدند، پس باید از شما اجازه بخواهند؛ همانند آنان که پیش از اینان بودند (و) اجازه خواستند. خدا آیات خود را این گونه برایتان بیان می‌کند، و خدا بس دانایی سنجیده‌کار است.'),
(2851, 24, 60, 'و بر زنان بازنشسته (و از کار افتاده)ای که (دیگر) امید زناشویی (جنسی) ندارند، پس بر آنان گناهی نیست که لباس‌های (حجاب) خود را کنار نهند، حال آنکه زینتی جالب نظر و آشکارگر نکنند؛ و (اما همان) پوشش برایشان بهتر است. و خدا بس شنوای بسیار داناست.'),
(2852, 24, 61, 'نه بر نابینا و نه بر لنگ و نه بر بیمار و نه بر شما (سالم‌ها) تنگنایی نیست، که از (غذای) خانه‌های خودتان بخورید، یا از (غذای) خانه‌های پدرانتان، یا خانه‌های مادرانتان، یا خانه‌های برادرانتان، یا خانه‌های خواهرانتان، یا خانه‌های عموهایتان، یا خانه‌های عمه‌هایتان، یا خانه‌های دایی‌هایتان، یا خانه‌های خاله‌هایتان، یا آن (خانه‌)ها(یی) که درب‌هاشان را در اختیار دارید، یا (خانه‌ی) دوستتان. (هم‌چنین) بر شما باکی نیست که با هم بخورید یا پراکنده. پس هنگامی که به خانه‌هایی در آمدید، بر خودهاتان [:یکدیگر] سلام کنید: زنده‌بادی که نزد خدا مبارکی پاکیزه است. خدا آیات (خود) را این‌گونه برای شما بیان می‌کند، شاید خردورزی کنید.'),
(2853, 24, 62, 'مؤمنان تنها کسانی‌اند که به خدا و پیامبرش گرویده‌اند و هنگامی که با او بر سر کاری اجتماعی بودند، تا از وی اجازه نخواهند (از نزد حضرتش) به در نروند. بی‌گمان کسانی که از تو اجازه خواهند، (هم)آنانند که به خدا و پیامبرش ایمان دارند. پس چون برای برخی از کارهاشان از تو اجازه خواستند، برای هر کس از آنان که (بنا بر مصلحت) خواستی اجازه ده و برایشان پوشش بخواه، که خدا بی‌گمان پوشنده‌ای رحمتگر بر ویژگان است.'),
(2854, 24, 63, 'خطاب کردن پیامبر را در میان خود، مانند خطاب کردن بعضی از خودتان به بعضی (دیگرتان) قرار مدهید. خدا به‌درستی می‌داند (و نشانه می‌نهد) کسانی از شما را (که) دزدانه (از نزد او) می‌گریزید. پس کسانی که از فرمان او تمرّد می‌کنند بپرهیزند؛ مبادا بلایی آتشین بدیشان در رسد، یا عذابی درناک بدیشان رسد.'),
(2855, 24, 64, 'هان! آنچه در آسمان‌ها و زمین است، بی‌گمان تنها از خداست. به‌درستی آنچه را که برآنید می‌داند (و نشانه می‌نهد) و روزی که سوی او باز گردانیده می‌شوند آنان را به (حقیقت) آنچه انجام داده‌اند، خبری مهم می‌دهد. و خدا به هر چیزی بسی داناست.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'مبارک (و خجسته) است خدایی که بر (قلب نورانی) بنده‌ی ویژه‌ی خود، فرقان [:کتاب جدا سازنده‌ی حق از باطل] را فرو فرستاد، تا برای جهانیان هشداردهنده‌ای باشد.'),
(2857, 25, 2, 'کسی که فرمانروایی آسمان‌ها و زمین تنها از اوست، و فرزندی برنگرفته و برای او شریکی در ملک [:فرماندهی ربانی] نبوده. و هر چیزی را آفرید، پس آن را اندازه‌گیری فرمود.'),
(2858, 25, 3, 'و جز او خدایانی برای خود برگرفتند، که (آنان) چیزی نمی‌آفرینند و خودشان آفریده می‌شوند و برای خود نه زیانی را در اختیار دارند و نه سودی را و نه مرگی را در اختیار دارند و نه حیاتی را و نه پراکنده شدنی (از گورهاشان) را (در رستاخیز).'),
(2859, 25, 4, 'و کسانی که کافر شدند، گفتند: «این (کتاب) جز دروغی که آن را بربافته (چیزی) نیست، حال آنکه گروهی دیگر او را بر(جعل) آن یاری کردند.» پس همواره (با چنین نسبتی) ظلم و گزافی (فراوان) آوردند.'),
(2860, 25, 5, 'و گفتند: «(این) افسانه‌های پیشینیانشان است. آنها را (از ایشان) با تکلف نسخه‌برداری کرده، پس این (نوشتار)ها صبح و شام بر او خوانده می‌شوند.»'),
(2861, 25, 6, 'بگو: «کسی آن را فرو فرستاده که راز نهان را در آسمان‌ها و زمین می‌داند. او بی‌گمان بس پوشنده‌ای رحمتگر بر ویژگان بوده است.»'),
(2862, 25, 7, 'و گفتند: «این فرستاده‌ی الهی را چه (شده) است (که) غذا می‌خورد و در بازارها راه می‌رود؟ چرا فرشته‌ای سویش فرود نیامده، تا همراهش هشداردهنده‌ای باشد؟»'),
(2863, 25, 8, '«یا گنجی سویش افکنده شود، یا باغی سردرهم داشته باشد (که) از (میوه‌های) آن بخورد؟» و ستمکاران گفتند: «جز مردی سحر شده را پیروی نمی‌کنید!»'),
(2864, 25, 9, 'بنگر چگونه برای تو مَثَل‌ها(ی باطلی) زدند، پس گمراه شدند؛ در نتیجه نمی‌توانند راهی (راست) بیابند.'),
(2865, 25, 10, 'مبارک (و خجسته) است کسی که اگر بخواهد بهتر از این را برایت می‌نهد. باغ‌هایی که نهرها از زیر (درختان)شان روان است و برایت کاخ‌هایی قرار می‌دهد.'),
(2866, 25, 11, '(نه!) بلکه آنها با ساعت [:قیامت] (همان را) تکذیب کردند و برای هر کس که با ساعت (همان را) تکذیب کند آتشی شعله‌ور و بسی سوزان آماده کرده‌ایم.'),
(2867, 25, 12, 'چون (این آتش) آنان را از فاصله‌ای دور ببیند، خشم و خروشی شدید برایش می‌شنوند.'),
(2868, 25, 13, 'و هنگامی‌که آنان در جایگاهی تنگ از آن فرو افتند، در حالی که بس نزدیک (به زنجیر) کشیده شده‌اند، آنجاست که فریادی مرگبار بر آرند.'),
(2869, 25, 14, 'امروز یک بار فریاد مرگبار برنکشید؛ بلکه فریادهای فسادانگیزِ مرگبار زیادی برآرید.'),
(2870, 25, 15, 'بگو: «آیا این (عقوبت) بهتر است، یا بهشت جاویدان، که پرهیزگاران (به آن) وعده داده شده‌اند؛ (همان که) پاداشی و سرانجامی برایشان بوده است‌؟»'),
(2871, 25, 16, 'در آنجا برای آنان است (هر) آنچه می‌خواهند، حال آنکه در آن جاودانه‌اند. مسئولیت این وعده بر پروردگارت بوده است.'),
(2872, 25, 17, 'و روزی که آنان را - با آنچه پس از خدا می‌پرستند - گرد هم آورد، پس فرماید: «آیا شما این بندگان مرا به بیراهه کشاندید، یا خودشان گمراه شدند؟»'),
(2873, 25, 18, 'گفتند: «تو منزّهی. برای ما هرگز شایسته نبوده که پس از تو هیچ اولیاء و سرپرستانی برای خود برگیریم. ولی تو آنان و پدرانشان را از نعمت‌هایی برخوردار کردی، تا (آنجا که) یادت را فراموش کردند و گروهی در ژرفای کساد (و فساد) بوده‌اند.»'),
(2874, 25, 19, 'پس همانا (معبودانتان ) آنچه می‌گفتید، بی‌گمان شما را تکذیب کردند؛ در نتیجه نه می‌توانید (عذاب را از خود) دفع کنید و نه (از آن‌ها) یاری گیرید. و هر که از شما ستم کند او را عذابی بزرگ می‌چشانیم.'),
(2875, 25, 20, 'و پیش از تو هیچ یک از پیامبران را نفرستادیم جز اینکه آنان (نیز) همواره غذا می‌خوردند و در بازارها راه می‌رفتند و برخی از شما را برای برخی دیگر وسیله‌ی آزمایشی آتشین نهادیم. آیا شکیبایی می‌کنید؟ حال آنکه پروردگارت همواره بسیار بینا بوده است.'),
(2876, 25, 21, 'و کسانی که به لقای (معرفتی، وحیانی و پاداش) ما امید ندارند، گفتند: «چرا فرشتگان بر ما نازل نشدند، یا (چرا) پروردگارمان را نمی‌بینیم‌؟» بی‌گمان همواره در خودهاشان کبر و برتری جستند، و سرکشی بزرگی کردند.'),
(2877, 25, 22, 'روزی که فرشتگان را ببینند، در آن هنگام (و هنگامه) برای مجرمان هرگز بشارتی نیست، و (فرشتگان به آنان) گویند: «(از پروردگار و نعمت‌هایش) دورادور باشید.»'),
(2878, 25, 23, 'و به هر گونه کاری که کرده‌اند قدم (و اِقدام قدرتمندانه) نهادیم. پس آن را (چون) گردی پراکنده ساختیم.'),
(2879, 25, 24, 'آن هنگام قرارگاه بهشتیان بهتر و استراحتگاهشان در نیمروز نیکوتر است.'),
(2880, 25, 25, 'و روزی که آسمان با (آن) ابر پوشنده(ی عذاب) از هم می‌شکافد و فرشتگان، شگفت‌انگیز، پیاپی یکدیگر فرود آورده شدند؛'),
(2881, 25, 26, 'در آن هنگام، حق فرماندهی کل ویژه‌ی (خدای) رحمتگر بر آفریدگان است. و این (خود) روزی بوده که بر کافران بسی دشوار بوده است.'),
(2882, 25, 27, 'و روزی که ستمکار بر دست‌هایش (با دندانش) همی‌گزد (و) گوید: «ای کاش با پیامبر راهی (سوی خدا) بر می‌گرفتم.»'),
(2883, 25, 28, '«ای وای بر من! کاش فلانی را دوست تنگاتنگ (خود)بر نگرفته بودم.»'),
(2884, 25, 29, '«هم او (بود که) همواره مرا از یادواره(ی حق) بس گمراه کرد، پس از آنکه برایم آمده بود.» و شیطان همواره خوار و بی‌مقدارکننده‌ی انسان بوده است.'),
(2885, 25, 30, 'و پیامبر گفت: «پروردگارم! قوم من بی‌گمان این قرآن را بس دور و مهجور گرفتند.»'),
(2886, 25, 31, 'و این گونه برای هر پیامبری دشمنی از مجرمان نهادیم و همین بس که پروردگارت راهبر و یاوری (بس توانا) است.'),
(2887, 25, 32, 'و کسانی که کافر شدند، گفتند: «چرا قرآن یک‌جا بر او نازل نشده‌؟» این گونه (ما آن را به تدریج فرو فرستادیم) تا دل فروزانت را پای‌برجا نهیم و آن را بر تو به تدریج (به گونه‌ای دلربا) برخواندیم، خواندنی (زیبا).'),
(2888, 25, 33, 'و تو را مَثَلی نمی‌آورند، مگر آنکه ما حق را با نیکوترین تفسیر (و بیان) برایت آوردیم.'),
(2889, 25, 34, 'کسانی که - بر رویشان در افتاده - سوی جهنم گرد آورده می‌شوند، آنان جایگاهشان بدترین و (خودشان) گمراه‌ترینند.'),
(2890, 25, 35, 'و همانا (ما) به موسی به‌راستی کتاب وحیانی (تورات) را دادیم و برادرش هارون را با او [:در پوشش رسالتش]، وزیری نهادیم.'),
(2891, 25, 36, 'پس گفتیم: «هر دو سوی قومی - که با نشانه‌های ما (همان‌ها و ما را) تکذیب کردند – بروید.» پس (ما) آنان [:تکذیب‌کنندگان] را در هلاکتی بسیار سخت سرنگون کردیم سرنگون کردنی.'),
(2892, 25, 37, 'و گروه نوح را - آن هنگام (و هنگامه‌ای) که پیامبران (خدا) را تکذیب کردند - غرقشان نمودیم و آنان را برای مردمان نشانه‌ای نهادیم. و برای ستمکاران عذابی پر درد آماده کردیم.'),
(2893, 25, 38, 'و (نیز) عادیان و ثمودیان و اصحاب رَسّ[:گودال] و نسل‌های بسیاری را در این میان (همان‌گونه هلاک کردیم).'),
(2894, 25, 39, 'و برای همه‌ی آنان مَثَل‌هایی (روشنگر) زدیم و همه را هلاک نمودیم.'),
(2895, 25, 40, 'و (آنان) بی‌گمان - بر مجتمع و ساکنانش که باران بلا بر آنان بارانده شد - همواره آمدند؛ پس آیا چنان نبوده‌اند که آن را بنگرند؟ (چرا،) ولی امید به (زنده و) پراکنده شدن نداشتند.'),
(2896, 25, 41, 'و هنگامی که تو را ببینند، جز به ریشخندت نگیرند (و گویند:) «آیا این همان کسی است که خدا او را به رسالتی برانگیخته است‌؟»'),
(2897, 25, 42, '«نزدیک بود که ما را بی‌گمان از خدایانمان – اگر بر (پرستش) آنان شکیبایی نمی‌کردیم – گمراه کند.» و هنگامی که عذاب را می‌بینند در آینده‌ای دور [:رستاخیز] خواهند دانست چه کسی گمراه‌تر است.'),
(2898, 25, 43, 'آیا کسی را که معبودش را هوای نفسش بر گرفته است دیدی‌؟ آیا پس تو بر او کاردار (و نگهدار)ی؟'),
(2899, 25, 44, 'یا گمان داری که بیشترشان می‌شنوند یا خردورزی می‌کنند؟ آنان جز مانند چهارپایان نیستند، بلکه اینان (از آنان هم) گمراه‌ترند.'),
(2900, 25, 45, 'آیا سوی پروردگارت (در ربوبیّتش) ننگریسته‌ای (که) چگونه سایه(ی ربانی) را گسترده‌؟ و اگر می‌خواست، همواره آن را ساکن می‌نهاد. آن‌گاه خورشید را بر آن دلیل گردانیدیم.'),
(2901, 25, 46, 'سپس آن (سایه) را به آسانی سوی خودمان باز گرفتیم.'),
(2902, 25, 47, 'و اوست کسی که شب را پوششی نهاد و خواب را (مایه‌ی) باز گرفتن از کار و آرامشی قرار داد، و روز را (برای) به‌پاخاستن و پراکنده شدن (برای دست‌یابی به نیازهای زندگی) نهاد.'),
(2903, 25, 48, 'و اوست کسی که بادها را نویدی پیشاپیش رحمت خویش (مانند باران و برف و تگرگ) فرستاد. و از آسمان آبی پاک‌کننده فرود آوردیم،'),
(2904, 25, 49, 'تا به (وسیله‌ی) آن شهری مرده (و مرگبار) را زنده کنیم و - در شمار آفریدگانمان - حیواناتی نعمت‌وار و انسان‌هایی بسیار را (از این آب) بنوشانیم.'),
(2905, 25, 50, 'ما بی‌گمان قرآن را به‌راستی میان آنان گوناگون بیان کردیم، تا به خوبی (حقایقی وحیانی را) یاد کنند ولی بیشتر مردمان - بجز از کفر و کفران- خودداری کردند.'),
(2906, 25, 51, 'و اگر می‌خواستیم همواره در هر مجتمعی هشداردهنده‌ای بر می‌انگیختیم (گرچه در مراکز مجتمعات همواره فرستاده‌ایم).'),
(2907, 25, 52, 'پس، کافران را پیروی مکن، و به وسیله‌ی قرآن با آنان به جهادی (پیگیربا این برهان وحیانی) بزرگ بپرداز.'),
(2908, 25, 53, 'و اوست کسی که دو دریا را با یکدیگر آمیخت: این (یکی) شیرین گوارا، و آن (یکی) شور تلخ (و ناگوار) است‌؛ و میان آن دو، فاصله‌ی پرده‌ای ناپیدا قرار داد.'),
(2909, 25, 54, 'و اوست کسی که از آب (منی) بشری آفرید. پس او را (دارای خویشاوندی) نسبی و دامادی نهاد. و پروردگارت بس توانا بوده است.'),
(2910, 25, 55, 'و غیر خدا چیزی را می‌پرستند که نه سودشان می‌دهد و نه زیانشان می‌رساند؛ و کافر همواره علیه پروردگار خود پشتیبان (کافران) بوده است.'),
(2911, 25, 56, 'و ما تو را جز بشارتگر و هشداردهنده‌ای نفرستادیم.'),
(2912, 25, 57, 'بگو: «بر این (رسالت) هیچ پاداشی از شما نمی‌طلبم، جز (از) کسی که بخواهد راهی سوی پروردگارش برگیرد.»'),
(2913, 25, 58, 'و بر (آن خدای) زنده‌ای - که نمی‌میرد - توکل کن و با ستایشش او را تسبیح نمای‌؛ و (هم) او بس است که به گناهان بندگانش بسی آگاه است.'),
(2914, 25, 59, 'کسی که آسمان‌ها و زمین و آنچه را که میان آن دو است، در شش روز [:زمان] آفرید. سپس این رحمتگر بر آفریدگان بر عرش (ربوبیت) چیره شد. پس به‌وسیله‌ی او -حال آنکه بسی آگاه است- (آنچه خواهی) جویا و پُرسا شو.'),
(2915, 25, 60, 'و زمانی که بدیشان گفته شود: «برای (خدای) رحمان سجده کنید» گویند: «رحمان چیست‌؟ آیا برای چیزی که ما را (بدان) فرمان می‌دهی سجده کنیم‌؟» و (این) بر رمیدنشان می‌افزاید.'),
(2916, 25, 61, 'مبارک و بزرگوار است کسی که در آسمان برج‌هایی نهاد و در آن چراغی[:خورشیدی] و ماهی نوربخش قرار داد.'),
(2917, 25, 62, 'و اوست کسی که - برای هر کس (که) بخواهد به‌خوبی تذکری یابد یا بخواهد به‌خوبی سپاسگزاری کند - شب و روز را از پی یکدیگر نهاد.'),
(2918, 25, 63, 'و بندگان خدای رحمان کسانی‌اند که روی زمین به فروتنی و آرامی گام بر می‌دارند؛ و چون نادانان با ایشان هم‌سخن شوند (به آنان) سلامی گویند (که خدا شما را از نادانی برهاند و سالم بدارد).'),
(2919, 25, 64, 'و آنان که سجده‌کنندگان و برپاخاستگان برای پروردگارشان شب‌زنده‌داری می‌کنند؛'),
(2920, 25, 65, 'و کسانی که می‌گویند: «پروردگارمان! عذاب جهنم را از ما بازگردان (که) همانا عذابش بسی سخت و ناگوار بوده است.»'),
(2921, 25, 66, '«همواره جهنم چه بدقرارگاه و پایگاهی است.»'),
(2922, 25, 67, 'و کسانی که چون انفاق کنند، نه زیاده‌روی می‌کنند و نه تنگ می‌گیرند و (انفاقشان) میان این دو راستا و میانگین بوده است.'),
(2923, 25, 68, 'و کسانی که با خدا معبودی دیگر را نمی‌خوانند و کسی را که خدا (خونش را) حرام کرده است جز به تمامی حق نمی‌کشند، و زنا نمی‌کنند و هر کس این‌ها را انجام دهد (عذاب مناسب و) اثر گناهش را دریافت خواهد کرد.'),
(2924, 25, 69, 'برای او در روز قیامت عذاب چند برابر می‌شود و پیوسته در آن خوار (و بی‌مقدار) می‌ماند.'),
(2925, 25, 70, 'مگر کسی که توبه کند و ایمان آورد و کار شایسته‌ای انجام دهد. پس ایشان، خدا بدی‌هایشان را به نیکی‌ها تبدیل می‌کند و خدا همواره بسی پوشنده‌ی رحمتگر بر ویژگان بوده است.'),
(2926, 25, 71, 'و هر کس توبه کند و کار شایسته انجام دهد، پس همانا اوست که سوی خدا بازگشتی (شایسته) دارد.'),
(2927, 25, 72, 'و کسانی که گواهی دروغ نمی‌دهند؛ و چون بر لغو بگذرند با بزرگواری می‌گذرند.'),
(2928, 25, 73, 'و کسانی که چون به آیات پروردگارشان تذکر داده شوند، (همچون) کران و کوران فریادکنان بر آن‌ها فرود نمی‌آیند.'),
(2929, 25, 74, 'و کسانی که می‌گویند: «پروردگارمان! برایمان از همسران، همگنان و اطرافیان‌مان آن را ببخشای که مایه‌ی روشنی چشمان(مان) باشد، و ما را پیشوای پرهیزگاران بگردان.»'),
(2930, 25, 75, 'اینانند که به پاس آنکه صبر کردند، استراحتگاه (روح و بدن) را پاداش خواهند یافت و در آن‌جا با سلام و درود به خوبی مواجه خواهند شد.'),
(2931, 25, 76, 'در آن‌جا، جاودانه خواهند ماند. چه خوش قرارگاه و جایگاهی است!'),
(2932, 25, 77, 'بگو: «اگر درخواست و بندگی شما نباشد، پروردگارم (هیچ) اعتنایی به شما نمی‌کند.(که) بی‌چون بسی (خدا را) تکذیب کرده‌اید. پس در آینده (این تکذیب) گریبانگیر(تان) خواهد بود.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طسم‌'),
(2934, 26, 2, 'آن‌هاست آیات کتاب روشنگر.'),
(2935, 26, 3, 'شاید تو از اینکه (کافران) ایمان‌آورندگان نیستند جان خود را از روی اندوه تباه‌سازنده‌ای.'),
(2936, 26, 4, 'اگر بخواهیم، آیتی از آسمان بر آنان فرود می‌آوریم، پس برای آن، گردن‌هایشان (و گردن کشانشان) خاضعانه دگرگون شوند.'),
(2937, 26, 5, 'و هیچ یادواره‌ی جدیدی از سوی رحمان برایشان نیاید جز اینکه بی‌گمان از آن رویگردان بوده‌اند.'),
(2938, 26, 6, 'همواره بس به تکذیب پرداختند و به زودی خبرهای مهمی که بدان ریشخند می‌کرده‌اند، بدیشان خواهد رسید.'),
(2939, 26, 7, 'و آیا به زمین ننگریسته‌اند (که) چه اندازه در آن از هر گونه جفت‌های کرامت‌بار رویانیده‌ایم‌؟'),
(2940, 26, 8, 'همانا در این (هنرنمایی) همواره نشانه‌ای است. حال آنکه بیشترشان ایمان‌آورندگان نبوده‌اند.'),
(2941, 26, 9, 'و در حقیقت، پروردگارت (هم)او به‌درستی کانون عزت (و) رحمت است.'),
(2942, 26, 10, 'و چون پروردگارت موسی را ندا در داد که : «نزد آن گروه ستمکار برو؛»'),
(2943, 26, 11, '.«گروه فرعون. آیا پروا ندارند؟!»'),
(2944, 26, 12, 'گفت: «پروردگارم! همواره من می‌ترسم که تکذیبم کنند.»'),
(2945, 26, 13, '«و سینه‌ام تنگ می‌گردد و زبانم نمی‌رهد، پس سوی هارون بفرست.»'),
(2946, 26, 14, '«و (از طرفی دیگر) برای آنان بر گردن من (در کشتن شخصی فرعونی) پیامدی بد است، پس می‌ترسم مرا بکشند.»'),
(2947, 26, 15, 'فرمود: «هرگز (چنین نیست)! پس به (همراه) نشانه‌های ما (سوی آنان) بروید. همانا ما با شما شنوندگانیم.»'),
(2948, 26, 16, '«پس نزد فرعون بروید (و) بگویید: “ما دو نفر به‌راستی پیامبر پروردگار جهانیانیم.”»'),
(2949, 26, 17, '(و بگویید) که: « فرزندان اسرائیل را با ما بفرست.»'),
(2950, 26, 18, '(فرعون) گفت: « آیا تو را به حال کودکی در (دامن) خودمان نپروراندیم؛ حال آنکه سالیانی چند از عمرت را در میان ما سپری کردی‌.»'),
(2951, 26, 19, '.«و (سرانجام) کاری را که می‌خواستی انجام دادی، حال آنکه (نسبت به من) از ناسپاسانی.»'),
(2952, 26, 20, 'گفت: «آن را هنگامی مرتکب شدم در حالی‌که من (در راه) از گمشدگانم.»'),
(2953, 26, 21, '«و در آن هنگامی که از شما ترسیدم، پس از شما گریختم، تا پروردگارم به من حکمت و حاکمیتی بخشید و مرا از فرستادگان (وحیانی) نهاد.»'),
(2954, 26, 22, '«و (آیا) بر (اساس) اینکه فرزندان اسراییل را بنده و در بند (خود) ساخته‌ای، این خود نعمتی است که منّتش را بر من می‌نهی‌؟»'),
(2955, 26, 23, 'فرعون گفت: « و پروردگار جهانیان چیست‌؟»'),
(2956, 26, 24, 'گفت: «پروردگار آسمان‌ها و زمین و آنچه میان آن دو است، اگر از یقین‌آورندگان بوده‌اید.»'),
(2957, 26, 25, '(فرعون) به اطرافیانش گفت: «آیا درست نمی‌شنوید؟»'),
(2958, 26, 26, '(موسی) گفت: «پروردگار شما و پروردگار پدران نخستین شما.»'),
(2959, 26, 27, '(فرعون) گفت: «به‌راستی این پیامبری که فراسوی شما فرستاده شده سخت دیوانه است!»'),
(2960, 26, 28, '(موسی) گفت: «پروردگار خاور و باختر و آنچه میان آن دو است؛ اگر خردورزی می‌کرده‌اید.»'),
(2961, 26, 29, '(فرعون) گفت: «اگر خدایی غیر از من برگزینی همانا تو را بی‌گمان از زندانیان قرار خواهم داد.»'),
(2962, 26, 30, '(موسی) گفت: « و گرچه برایت چیزی روشنگر بیاورم‌؟»'),
(2963, 26, 31, '(فرعون) گفت: «اگر از راستان بوده‌ای آن را بیاور.»'),
(2964, 26, 32, 'پس (موسی) عصای خود را بیفکند پس (فرعون دید که) ناگهان (هم) آن اژدری نمایان است.'),
(2965, 26, 33, 'و دستش را (از گریبانش) بیرون کشید، پس ناگهان (هم) آن برای تماشاگران سپید است.'),
(2966, 26, 34, '(فرعون) به سران چشمگیر (از درباریانش که) پیرامونش بودند گفت: «همانا این همواره ساحری بسیار داناست.»'),
(2967, 26, 35, '«می‌خواهد با سحر خود، شما را از سرزمینتان بیرون کند. پس (اکنون) چه می‌فرمایید؟»'),
(2968, 26, 36, 'گفتند: «او و برادرش را مهلتی ده و گردآورندگان [:بسیج کنندگان] را به شهرها بفرست،»'),
(2969, 26, 37, '«پس هر ساحر بسیار دانایی را نزدت آورند.»'),
(2970, 26, 38, 'پس ساحران برای وعده‌گاه روزی معلوم گردآوری شدند.'),
(2971, 26, 39, 'و به مردم گفته شد: «آیا شما هم جمع‌شوندگانید؟»'),
(2972, 26, 40, '«شاید اگر ساحران همان پیروزمندان بوده‌اند ما (هم) از آنان پیروی کنیم‌!»'),
(2973, 26, 41, 'پس چون ساحران آمدند، به فرعون گفتند: «آیا اگر ما همان پیروزمندان بوده‌ایم، به‌راستی برایمان مزدی خواهد بود؟»'),
(2974, 26, 42, 'گفت: «آری و (افزون بر آن) بی‌گمان شما در این هنگام به‌راستی از نزدیک‌شدگان (به دربار) هستید.»'),
(2975, 26, 43, 'موسی به آنان گفت: «آنچه را شما افکنندگانید بیفکنید.»'),
(2976, 26, 44, 'پس ریسمان‌هاشان و عصاهاشان را افکندند و گفتند: «به عزّت فرعون بی‌گمان به‌راستی ما همان پیروزمندانیم.»'),
(2977, 26, 45, 'پس موسی عصایش را افکند که ناگهان همان (عصا) هر چه را به دروغ می‌ساختند با سرعتی حاذقانه می‌بلعد.'),
(2978, 26, 46, 'پس ساحران (ناگهان و ناخودآگاه) به حالت سجده فرو افکنده شدند.'),
(2979, 26, 47, 'گفتند: «به پروردگار جهانیان ایمان آوردیم،»'),
(2980, 26, 48, '«پروردگار موسی و هارون.»'),
(2981, 26, 49, '(فرعون) گفت: «آیا پیش از آنکه به شما اجازه دهم برای او ایمان آوردید؟ همواره او بی‌گمان همان بزرگ شماست که به شما سحر آموخته است. پس در آینده خواهید دانست (با شما چه خواهم کرد). حتماً دست‌ها و پاهایتان را از چپ و راست به سختی خواهم برید و همانا بی‌چون همه‌تان را به دار خواهم آویخت.»'),
(2982, 26, 50, 'گفتند: «هیچ زیانی (و باکی) نیست. ما همواره سوی پروردگارمان (روی‌آورندگان و) دگرگون‌شوندگانیم.»'),
(2983, 26, 51, '«‌ما بی‌گمان امیدواریم که پروردگارمان گناهانمان را بر ما بپوشاند، (چرا) که (در جمع فرعونیان) نخستین ایمان‌آورندگان بوده‌ایم.»'),
(2984, 26, 52, 'و سوی موسی وحی کردیم که: «بندگان مرا شبانه حرکت ده، زیرا همواره شما تعقیب‌شد‌گانید.»'),
(2985, 26, 53, 'پس فرعون مأمورانی برای جمع‌آوری (کسانی) به تمامی شهرها(ی مصر) فرستاد.'),
(2986, 26, 54, '(فرعون و فرعونیان گفتند:) «این گروهی -بی‌گمان و بی‌امان- اندکند.»'),
(2987, 26, 55, '«و به‌راستی آنان ما را بسی خشم‌آورندگانند.»'),
(2988, 26, 56, '«و محققاً ما نیروی مهم به هم پیوسته و وابسته‌ایم، (که) به حال آماده‌باش (درآمده‌ایم).'),
(2989, 26, 57, 'پس آنان را از باغستان‌های سردرهم و چشمه‌سارانی بیرون راندیم؛'),
(2990, 26, 58, 'و (نیز از) گنجینه‌ها و جایگاه‌هایی پر کرامت [:ناز و نعمت].'),
(2991, 26, 59, '(اراده‌ی ما) چنین بود و آن (نعمت‌)ها را به فرزندان اسرائیل میراث دادیم.'),
(2992, 26, 60, 'پس (فرعونیان) در حال برآمدن آفتاب، آنان را تعقیب کردند.'),
(2993, 26, 61, 'پس چون دو گروه، همدیگر را دیدند، همراهان موسی گفتند: «بی‌گمان ما ناگزیر(و ناگریز) مورد دستیابی هستیم.»'),
(2994, 26, 62, 'گفت: «هرگز چنان نیست؛ همواره پروردگارم با من است و به زودی مرا (معجزه آسا) راهنمایی خواهد کرد.»'),
(2995, 26, 63, 'پس سوی موسی وحی کردیم که: «با عصای خود بر (این) دریا بزن‌.» پس دریا از هم شکافت. پس هر پاره‌ای همچون کوهی سترگ بود.'),
(2996, 26, 64, 'و دیگران [:فرعونیان] را بدانجا (با صف‌آراییشان به غرق) نزدیک گردانیدیم.'),
(2997, 26, 65, 'و موسی را با همه‌ی کسانی که همراهش بودند نجات دادیم.'),
(2998, 26, 66, 'سپس دیگران را غرق کردیم.'),
(2999, 26, 67, 'بی‌شک در این (واقعه‌ی عبرت‌انگیز) همانا نشانه‌ی (ربانیِ) بزرگی است. حال آنکه بیشترشان (از) مؤمنان نبوده‌اند.'),
(3000, 26, 68, 'و بی‌گمان پروردگار تو، همواره او همان کانون عزت (و) رحمت است.'),
(3001, 26, 69, 'و بر آنان گزارش مهم ابراهیم را برخوان.'),
(3002, 26, 70, 'چون به پدر (تربیتی)اش و قومش گفت: «چه (چیزی را) می‌پرستید؟»'),
(3003, 26, 71, 'گفتند: «بتانی را می‌پرستیم. پس همواره بر درگاه آنان همچنان باشندگان و خاضعانیم.»'),
(3004, 26, 72, 'گفت: «آیا وقتی از آنها درخواستی می‌کنید، از شما می‌شنوند؟»'),
(3005, 26, 73, '«یا شما را سود یا زیانی می‌رسانند؟»'),
(3006, 26, 74, 'گفتند: «(نه!) بلکه پدران خود را یافتیم که چنان می‌کردند.»'),
(3007, 26, 75, 'گفت: «آیا پس آنچه را می‌پرستیده‌اید نگریستید؛»'),
(3008, 26, 76, '«شما و پدران پیشترین شما؟»'),
(3009, 26, 77, '«پس بی‌گمان همه‌ی آنان – جز پروردگار جهانیان – دشمن من‌اند.»'),
(3010, 26, 78, '«آن کس که مرا آفرید، پس همو راهنمایی‌ام می‌کند؛»'),
(3011, 26, 79, '«و کسی (که) او به من خوردنی و آشامیدنی می‌دهد،»'),
(3012, 26, 80, '«و چون بیمار شوم پس او درمانم می‌کند،»'),
(3013, 26, 81, '«و آن کس که مرا می‌میراند سپس زنده‌ام می‌گرداند،»'),
(3014, 26, 82, '«و کسی که امید دارم روز بُروز طاعت، خطایم را بر من بپوشاند.»'),
(3015, 26, 83, '«پروردگارم! به من حاکمیت و حکمتی (وحیانی) عطا کن و مرا به (آن) صالحان [:پیامبران] ملحق گردان؛»'),
(3016, 26, 84, '«و برای من در (میان) آیندگان آخرین زبان [:آوازه‌ای] راست‌واره قرار ده؛»'),
(3017, 26, 85, '«و مرا از وارثان بهشت پر نعمت گردان؛»'),
(3018, 26, 86, '«و پدرم را ببخشای که او بی‌گمان از گمراهان بوده است.»'),
(3019, 26, 87, '«و روزی که (مکلفان) برانگیخته می‌شوند خوارم مکن.»'),
(3020, 26, 88, 'روزی که هیچ مال و فرزندانی سودی نمی‌دهند؛'),
(3021, 26, 89, 'مگر کسی که با دلی پاک سوی خدا آمد.'),
(3022, 26, 90, 'و (آن روز) بهشت برای پرهیزگاران زینت‌یافته نزدیک شد.'),
(3023, 26, 91, 'و جهنّم برای فاسد نظران نادان بس نمودار گشت.'),
(3024, 26, 92, 'و به آنان گفته شد: «آنچه می‌پرستیده‌اید کجایند،»'),
(3025, 26, 93, '«جز خدا؟ آیا یاریتان می‌کنند یا (از شما) یاری می‌خواهند؟»'),
(3026, 26, 94, 'پس آنان و (همه‌ی) گمراهان در آن آتش شعله‌ور به رو افکنده شدند.'),
(3027, 26, 95, 'و (نیز) همه‌ی سپاهیان ابلیس.'),
(3028, 26, 96, 'حال آنکه آنان در آتش شعله‌ور با یکدیگر نزاع می‌کنند، گفتند:'),
(3029, 26, 97, '«سوگند به خدا که ما همواره در ژرفای گمراهی آشکارگری بوده‌ایم.»'),
(3030, 26, 98, '«چون شماها را با پروردگار جهانیان برابر می‌نمودیم.»'),
(3031, 26, 99, 'و جز مجرمان ما را گمراه نکردند.»'),
(3032, 26, 100, '«پس هیچ گونه شفاعتگرانی نداریم،»'),
(3033, 26, 101, '«و نه دوستی خون‌گرم.»'),
(3034, 26, 102, '«پس کاش (بر فرض محال،) بازگشتی برای ما بود تا بی‌گمان از مؤمنان باشیم؟!‌»'),
(3035, 26, 103, 'همانا در این (سرگذشت) همواره نشانه‌ای است و بیشترشان (از) مؤمنان نبوده‌اند (که یا کافر بودند، و یا قاصر که نه مؤمن و نه کافر بودند).'),
(3036, 26, 104, 'و به‌راستی، پروردگار تو همواره همان کانون عزت (و) رحمتگر بر ویژگان است.'),
(3037, 26, 105, 'گروه نوح پیامبران را تکذیب کردند.'),
(3038, 26, 106, 'چون برادرشان نوح به آنان گفت: «آیا پروا نمی‌دارید؟»'),
(3039, 26, 107, '«من برای شما همواره فرستاده‌ای در خور اعتمادم.»'),
(3040, 26, 108, 'پس از خدا پروا کنید و فرمانم ببرید.»'),
(3041, 26, 109, '«و من از شما هیچ مزدی نمی‌خواهم، و اجر من جز بر (عهده‌ی) پروردگار جهانیان نیست.»'),
(3042, 26, 110, '«پس، از خدا پروا کنید و فرمانم ببرید.»'),
(3043, 26, 111, 'گفتند: «آیا برایت (به خدا) ایمان بیاوریم حال آنکه پست‌ترین فرومایگان از تو پیروی کرده‌اند؟»'),
(3044, 26, 112, '(نوح) گفت: «و من چه می‌دانم (که) به آنآنچه می‌کرده‌اند‌؟»'),
(3045, 26, 113, '«اگر باریک‌بینی کنید، حسابشان جز بر پروردگارم نیست.»'),
(3046, 26, 114, '«و من هرگز طرد‌کننده‌ی مؤمنان نیستم.»'),
(3047, 26, 115, '«من جز هشداردهنده‌ی آشکارگری (بیش) نیستم.»'),
(3048, 26, 116, 'گفتند: «ای نوح! همانا اگر (به دعوتت‌) پایان ندهی بی‌گمان و بی‌چون از سنگسارشدگان خواهی بود.»'),
(3049, 26, 117, 'گفت: «پروردگارم! قوم من همواره مرا تکذیب کردند.»'),
(3050, 26, 118, '«پس میان من و آنان راهکاری [:چاره‌ای] بگشای و من و هر آن کس از مؤمنان را که با من‌اند نجات بخش.»'),
(3051, 26, 119, 'پس او و هر که را در آن کشتیِ آکنده با او بودند، رهانیدیم.'),
(3052, 26, 120, 'سپس باقی‌ماندگان را غرق کردیم.'),
(3053, 26, 121, 'همانا در این (ماجرا) همواره نشانه‌ی بزرگی است، و بیشترشان ایمان‌آورندگان نبوده‌اند.'),
(3054, 26, 122, 'و بی‌گمان پروردگارت به‌راستی همان کانون عزت (و) رحمتگر بر ویژگان است.'),
(3055, 26, 123, 'عادیان، پیامبران را تکذیب کردند.'),
(3056, 26, 124, 'چون برادرشان هود برایشان گفت: «آیا پروا نمی‌دارید؟»'),
(3057, 26, 125, '«من به‌راستی برایتان فرستاده‌ای در خور اعتمادم.»'),
(3058, 26, 126, '«پس از خدا پروا کنید و فرمانم ببرید.»'),
(3059, 26, 127, '«و بر این (رسالت) هیچ اجری از شما نمی‌خواهم. اجر من جز بر (عهده‌ی) پروردگار جهانیان نیست.»'),
(3060, 26, 128, '«آیا بر هر تپّه‌ای زیبا و رعنا نشانه‌ا‌ی (برای قدرت و رفاهیت) می‌سازید که (بر این سازمان و در آن) دست به بیهوده‌کاری و بازی بزنید؟»'),
(3061, 26, 129, '«و سازمان‌هایی استوار بر می‌گیرید شاید (بیشتر) بمانید؟»'),
(3062, 26, 130, '«و هنگامی که حمله‌ور می‌شوید در حال زورگویی حمله‌ور می‌شوید؟»'),
(3063, 26, 131, '«پس، از خدا پروا بدارید و فرمانم ببرید.»'),
(3064, 26, 132, '«و از کسی که شما را به آنچه می‌دانید مدد کرد پروا بدارید؛»'),
(3065, 26, 133, '«(کسی را که) با دام‌ها و پسران، شما را مدد و گشایش داد،»'),
(3066, 26, 134, '«و باغستان‌هایی سردرهم و چشمه‌سارانی (به شما داد).»'),
(3067, 26, 135, '«من همواره از عذاب روزی بزرگ بر شما می‌ترسم.»'),
(3068, 26, 136, 'گفتند: «خواه اندرز(مان) دهی، خواه از اندرزدهندگان نباشی، برایمان یکسان است.»'),
(3069, 26, 137, '«این جز شیوه و اخلاق پیشینیان نیست.»'),
(3070, 26, 138, '«و ما عذاب‌شوندگان نیستیم.»'),
(3071, 26, 139, 'پس همواره تکذیبش کردند. پس هلاکشان کردیم. همانا در این (جریان) بی‌گمان نشانه‌ی بزرگی است و بیشترشان ایمان‌آورندگان نبوده‌اند.'),
(3072, 26, 140, 'و به‌راستی، پروردگارت همواره (همان) کانون عزت (و) رحمتگر بر ویژگان است.'),
(3073, 26, 141, 'ثمودیان، پیامبران را تکذیب کردند.'),
(3074, 26, 142, 'چون برادرشان صالح به آنان گفت: «آیا پروا نمی‌دارید؟»'),
(3075, 26, 143, '«من همواره برای شما فرستاده‌ای در خور اعتمادم.»'),
(3076, 26, 144, '«پس از خدا پروا بدارید و فرمانم ببَرید.»'),
(3077, 26, 145, '«و (من) بر این (رسالت) هیچ اجری از شما نمی‌خواهم. اجر من جز بر عهده‌ی پروردگار جهانیان نیست.»'),
(3078, 26, 146, '«آیا شما در آنچه همین‌جاست به امن و امان رها می‌شوید؟»'),
(3079, 26, 147, '«در باغ‌هایی سردرهم و چشمه‌سارهایی،»'),
(3080, 26, 148, '«و کشتزارهایی و خرماهایی (که) دستاوردهایشان لطیف (و) زود هضم است‌؟»'),
(3081, 26, 149, '«و در حال فرح و آسایش برای خود از کوه‌ها خانه‌هایی می‌تراشید.»'),
(3082, 26, 150, '«پس از خدا پروا کنید و فرمانم ببرید.»'),
(3083, 26, 151, '«و کار و فرمان افراط‌گران را پیروی مکنید.»'),
(3084, 26, 152, '«آنان که در زمین افساد می‌کنند و اصلاح نمی‌کنند.»'),
(3085, 26, 153, 'گفتند: «جز این نیست که تو سخت از افسون‌شدگانی!»'),
(3086, 26, 154, '«تو جز بشری مانند ما (بیش) نیستی. پس اگر از راستان بوده‌ای نشانه‌ای (ربانی برایمان) بیاور.»'),
(3087, 26, 155, 'گفت: «این ماده شتری است که برای او آشامیدنی‌ای و (نیز) روزی معیّن نوبت آب شماست.»'),
(3088, 26, 156, '«و به آن گزندی نرسانید تا (مبادا) عذابِ روزی بزرگ شما را در برگیرد.»'),
(3089, 26, 157, 'پس آن شتر را پی کردند و (از) پشیمانان گشتند.'),
(3090, 26, 158, 'پس آنان را عذاب در برگرفت. بی‌گمان و بی‌چون در این(جریان) نشانه‌ای است و (اما) بیشترشان (از) مؤمنان نبوده‌اند.'),
(3091, 26, 159, 'و به‌راستی پروردگار تو همواره (همان) کانون عزت (و) رحمتگر بر ویژگان است.'),
(3092, 26, 160, 'گروه لوط فرستادگان(مان) را تکذیب کردند.'),
(3093, 26, 161, 'چون برادرشان -لوط- بدیشان گفت: «آیا پروا نمی‌دارید؟»'),
(3094, 26, 162, '«من همواره برای شما فرستاده‌ای در خور اعتمادم.»'),
(3095, 26, 163, '«پس خدا را پروا بدارید و فرمانم ببرید.»'),
(3096, 26, 164, '«و بر این (رسالت) هیچ اجری از شما نمی‌خواهم. اجر من جز بر عهده‌ی پروردگار جهانیان نیست.»'),
(3097, 26, 165, '«آیا از (میان) مردمان جهان،(تنها) با مردان در می‌آمیزید؟»'),
(3098, 26, 166, '«و آنچه را پروردگارتان از همسرانتان برایتان آفریده وا می‌گذارید؟ بلکه شما مردمی تجاوزکارید.»'),
(3099, 26, 167, 'گفتند: «ای لوط! بی‌گمان اگر (به کارت) پایان ندهی، به‌راستی همواره از اخراج‌شوندگانی.»'),
(3100, 26, 168, 'گفت: «من همانا از خشمناکان سرسختِ کردار شمایم.»'),
(3101, 26, 169, '«پروردگارم! مرا و کسانم را از آنچه انجام می‌دهند رهایی بخش.»'),
(3102, 26, 170, 'پس او و همگی کسانش را رهانیدیم.'),
(3103, 26, 171, 'جز پیرزنی که از باقی‌ماندگان در غبار (گمراهی) بود.'),
(3104, 26, 172, 'سپس دیگران را سخت هلاک (و) زیر و زبر کردیم.'),
(3105, 26, 173, 'و بر (سروسامان‌)شان بارانی (از سنگ گل) فرو ریختیم؛ پس چه بد بود سنگ‌بارانِ بیم‌داده‌شدگان.'),
(3106, 26, 174, 'به‌راستی در این (عقوبت) همواره نشانه‌(ی بزرگی) است و (اما) بیشترشان ایمان آورنده نبوده‌اند.'),
(3107, 26, 175, 'و بی‌گمان، پروردگارت همان کانون عزت (و) رحمتگر بر ویژگان است.'),
(3108, 26, 176, 'اصحاب اَیْکه [:درختان انبوه] فرستادگان (ما) را تکذیب کردند.'),
(3109, 26, 177, 'چون شعیب به آنان گفت: «آیا پروا نمی‌دارید؟»'),
(3110, 26, 178, '«من همواره برای شما فرستاده‌ای در خور اعتمادم.»'),
(3111, 26, 179, '«از خدا پروا بدارید و فرمانم ببرید.»'),
(3112, 26, 180, '«و بر این (رسالت) هیچ اجری از شما نمی‌خواهم. اجر من جز بر عهده‌ی پروردگار جهانیان نیست.»'),
(3113, 26, 181, '«پیمانه را تمام دهید و از زیان‌رسانان مباشید.»'),
(3114, 26, 182, '«و با ترازویِ استوار و راستا وزن کنید.»'),
(3115, 26, 183, 'و چیزهای مردمان [:جان و جانانشان] را زیان مرسانید، و افسادگرانه در زمین کوشش نکنید.»'),
(3116, 26, 184, '«و از آن کسی که شما و گروه‌های نخستین را همچون کوه (با فطرت‌های توحیدی) آفریده است پروا کنید.»'),
(3117, 26, 185, 'گفتند: «تو فقط از افسون‌شدگانی.»'),
(3118, 26, 186, '« و تو جز بشری مانند ما (بیش) نیستی و تو را بی‌گمان از دروغگویان گمان داریم.»'),
(3119, 26, 187, '«پس اگر از راستان بوده‌ای، پاره‌ای از آسمان بر (سر و سامان‌)مان بیفکن.»'),
(3120, 26, 188, '(شعیب) گفت: «پروردگارم به آنچه می‌کنید داناتر است.»'),
(3121, 26, 189, 'پس او را تکذیب کردند. پس عذاب روزِ ظلمت‌زا آنان را فرو گرفت. به‌راستی آن عذابِ روزی بزرگ بود.'),
(3122, 26, 190, 'همانا در این (جریان) بی‌گمان نشانه‌ا‌ی (بزرگ) است و (اما) بیشترشان ایمان‌آورندگان نبوده‌اند.'),
(3123, 26, 191, 'و به‌راستی، پروردگارت همواره همان عزیز رحیم است.'),
(3124, 26, 192, 'و بی‌گمان این (قرآن) همواره فرستاده‌ی تدریجی پروردگار جهانیان است.'),
(3125, 26, 193, 'روح‌الامین آن را فرود آورد،'),
(3126, 26, 194, 'بر دلت تا از (جمله‌ی) بیم‌دهندگان باشی؛'),
(3127, 26, 195, 'به زبانی آشکارای بیانگر.'),
(3128, 26, 196, 'و همانا (هم)آن همواره در کتاب‌های پشینیان است.'),
(3129, 26, 197, 'آیا و برای آنان، (این خود) نشانه‌ای بزرگ نبوده است که علمای بنی‌اسرائیل بدان آگاهند؟'),
(3130, 26, 198, 'و اگر آن را بر برخی از عجم‌ها [:غیرعرب‌زبانان] نازل می‌کردیم،'),
(3131, 26, 199, 'پس پیامبر آن را بر ایشان می‌خواند، به آن ایمان نمی‌آورده‌اند.'),
(3132, 26, 200, 'این گونه در دل‌های مجرمان، آن (قرآن) را راهی نفوذی دادیم.'),
(3133, 26, 201, 'تا عذاب پر دردشان را ننگرند، به آن نگروند.'),
(3134, 26, 202, 'پس (آن عذاب) ناگهان -در حالی که باریک‌بینی نمی‌کنند- بدیشان در رسد.'),
(3135, 26, 203, 'پس بگویند: «آیا ما مهلت داده شدگانیم؟»'),
(3136, 26, 204, 'پس آیا عذاب ما را بس شتابان می‌خواهند؟'),
(3137, 26, 205, 'پس مگر دیدی اگر سال‌ها آنان را برخوردار کنیم؛'),
(3138, 26, 206, '(و) سپس آنچه (که) وعده داده شده‌اند به سراغشان آمد؛'),
(3139, 26, 207, 'آنچه از آن برخوردار می‌شده‌اند، بی‌نیازشان نکرد (و عذاب را از آنان دفع ننمود).'),
(3140, 26, 208, 'و (ما) هیچ مجتمعی را هلاک نکردیم مگر آنکه برایشان بیم‌دهندگانی باشند،'),
(3141, 26, 209, '(تا آنان را) یادواره‌ای (باشد). و ما ستمکار نبوده‌ایم.'),
(3142, 26, 210, 'و شیطان‌ها آن را فرود نیاورده‌اند.'),
(3143, 26, 211, 'و هرگز برایشان سزاوار (هم) نیست و (هم) نمی‌توانسته‌اند (آن را فرود آورند).'),
(3144, 26, 212, 'آنان بی‌گمان از شنیدن (آن وحی ربانی) همواره وازده‌شدگانند.'),
(3145, 26, 213, 'پس با خدا، معبودی دیگر مخوان. (مباد که) از عذاب‌شدگان باشی.'),
(3146, 26, 214, 'و نزدیکترین نزدیکانت را (از هنگامه‌ی رستاخیز) بیم ده.'),
(3147, 26, 215, 'و برای آن مؤمنانی که تو را پیروی کرده‌اند، بال خود را فرو گستر.'),
(3148, 26, 216, 'پس اگر (کافران) تو را نافرمانی کردند، (بدیشان) بگو: «من همواره از آنچه می‌کنید بیزارم.»'),
(3149, 26, 217, 'و بر (آن خدایِ) عزیزِ رحمتگر بر ویژگان توکل کن.'),
(3150, 26, 218, 'آن کس که چون بر پا می‌خیزی تو را می‌بیند،'),
(3151, 26, 219, 'و جابه‌جا شدنت را در میان سجده‌کنندگان می‌نگرد.'),
(3152, 26, 220, 'بی‌گمان او، (هم)او بسیار شنوای بس داناست.'),
(3153, 26, 221, 'آیا شما را خبر مهمی دهم (که) شیاطین بر چه کسانی دم به دم فرود می‌آیند؟'),
(3154, 26, 222, 'بر هر دروغ‌زن گناهکاری پیاپی فرود می‌آیند،'),
(3155, 26, 223, 'که (دزدانه) گوش‌هاشان را (برای شنیدن وحی) فرا می‌افکنند و بیشترشان دروغگویانند.'),
(3156, 26, 224, 'و شاعران را نادانان فاسد پیروی می‌کنند.'),
(3157, 26, 225, 'آیا ندیده‌ای که آنان در هر وادی‌ای در مدح و ذم (این و آن) بسی دلباخته‌اند؟'),
(3158, 26, 226, 'و همانان چیزهایی می‌گویند که بی‌امان انجام نمی‌دهند.'),
(3159, 26, 227, 'مگر کسانی که ایمان آورده و کارهای شایسته‌ی (ایمان) کرده و خدا را بسیار به یاد آورده، و پس از آنکه مورد ستم قرار گرفته‌اند یاری خواسته‌اند. و کسانی که ستم کرده‌اند به زودی خواهند دانست (به) چه دگرگونی‌ای (در زمان و مکانی) دگرگون می‌گردند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طس. این‌هاست آیات قرآن و کتابی روشنگر.'),
(3161, 27, 2, 'حال آنکه هدایت و بشارتی است برای مؤمنان.'),
(3162, 27, 3, 'کسانی که نماز را بر پا می‌دارند و زکات را می‌دهند و آنان، (هم)آنان به آخرت یقین می‌آورند.'),
(3163, 27, 4, 'بی‌گمان کسانی که به آخرت ایمان نمی‌آورند، کردارهایشان را برایشان بیاراستیم؛ پس ایشان سرگشته می‌گردند.'),
(3164, 27, 5, 'آنان کسانی‌اند که بدی عذاب برایشان است و آنان در آخرت، همانا زیانکارترین (مکلفان)اند.'),
(3165, 27, 6, 'و همواره تو قرآن را به‌راستی از نزد حکیمی بس دانا به‌درستی - به گونه‌ای مماس - دریافت می‌داری.'),
(3166, 27, 7, 'چون موسی به خانواده‌ی خود گفت: «من به‌راستی با آتشی انس گرفتم، به زودی برای شما خبری از آن خواهم آورد، یا شعله‌ی آتشی برای شما می‌آورم، شاید گیرانه‌ای (از آن) برگیرید.»'),
(3167, 27, 8, 'پس چون نزد آن آتش آمد، آوازی بلند در رسیدش: «مبارک گردید آن که در این آتش [:نور وحی] و آن که پیرامون آن است و منزّه است خدا، پروردگار جهانیان.»'),
(3168, 27, 9, '«ای موسی! همانا او، منم؛ خدای عزیز حکیم.»'),
(3169, 27, 10, '«و اینکه عصایت را بیفکن.» پس چون آن را همچون ماری کوچک بدید که می‌جنبد، (بدان) پشت گردانید و به عقب (هم نگاهِ خود را) باز نگردانید: «ای موسی! مترس که فرستادگان (من) هرگز نزد من نمی‌ترسند؛»'),
(3170, 27, 11, '«مگر کسی که ستم کرد. سپس - بعد از بدی - نیکی را جایگزین (آن) گردانید، پس من همواره بسی پوشنده‌ی رحمتگر بر ویژگانم.»'),
(3171, 27, 12, '«و دستت را در گریبانت ببر تا سپیدی بی‌عیب برون آید؛ در نشانه‌های نُه‌گانه‌ای (که باید) سوی فرعون و قومش (ببری). آنان بی‌گمان مردمی نافرمان بوده‌اند.»'),
(3172, 27, 13, 'پس چون نشانه‌‌های ما -بینا کننده- برایشان آمد گفتند: «این سحری آشکارگر است.»'),
(3173, 27, 14, 'و حال آنکه (از عمق) جان‌هایشان به آن (نشانه‌های ربانی) یقین داشتند، از روی ظلم و تکبّر آنها را انکار (و انگار) کردند. پس بنگر (که) فرجام افسادگران چگونه بود.'),
(3174, 27, 15, 'و همواره به داوود و سلیمان به‌راستی علمی (وحیانی) دادیم، و آن دو گفتند: «تمام ستایش‌ها در انحصار خدایی است که ما را بر بسیاری از بندگانِ باایمانش برتری داده است.»'),
(3175, 27, 16, 'و سلیمان از داوود میراث یافت و گفت: «ای مردمان! ما زبان پرندگان را آموخته‌ایم و از هر چیزی (همانندش) داده شده‌ایم. به‌راستی این همانا فضلیت روشنگر است.»'),
(3176, 27, 17, 'و برای سلیمان، سپاهیانش - از جنّ و انس و پرندگان- جمع‌آوری شدند، پس آنان (از پراکندگی) نگهبانی می‌شوند]: متفرق نمی‌شوند[.'),
(3177, 27, 18, 'تا آن‌گاه که به مکان مورچگان رسیدند. مورچه‌ای (از فرماندهان مورچگان) گفت: «ای مورچگان! به جایگاه‌هایتان داخل شوید، مبادا سلیمان و سپاهیانش - ندیده و ندانسته - به سختی شما را پایمال کنند.»'),
(3178, 27, 19, 'پس (سلیمان) از گفتارش- در حال خنده‌اش- تبسمی کرد و گفت: «پروردگارم! مرا از گسیختگی نگه‌دار تا نعمتی را که به من و پدر و مادرم داده‌ای سپاس بگزارم و به کار شایسته‌ای که آن را می‌پسندی بپردازم و مرا به رحمت خویش در میان شایستگانِ (از) بندگانت داخل گردان.»'),
(3179, 27, 20, 'و در جستجوی پرندگان برآمد. پس گفت: «مرا چه شده که هدهد را نمی‌بینم‌؟ یا (اینکه) از غایبان بوده است‌؟»'),
(3180, 27, 21, '«بی‌گمان او را به عذابی سخت همواره عذاب می‌کنم یا سرش را همی بی‌چون می‌برم، مگر آنکه به‌راستی دلیلی روشنگر به‌درستی (بر غیبت خود) برایم بیاورد.»'),
(3181, 27, 22, 'پس دیری نپایید (که هدهد آمد). پس گفت: «از چیزی آگاهی یافتم که (تو) بدان آگاهی نیافته‌ای و برایت از (کشور) سبا گزارشی مهم (و) یقینی آورده‌ام.»'),
(3182, 27, 23, '«من به‌راستی زنی را یافتم که مالک (و اختیاردار) آنهاست و از هر چیزی (از مال و منال دنیا) به او داده شده، و برای او تخت (پادشاهی) بزرگی است.»'),
(3183, 27, 24, '«او و قومش را چنان یافتم که به جای خدا برای خورشید سجده می‌کنند، حال آنکه شیطان اعمالشان را برایشان آراسته، در نتیجه آنان را از راه (راست) باز داشته، پس ایشان راه (حق) را نمی‌یابند.»'),
(3184, 27, 25, '(آری، شیطان چنین کرده بود) تا برای خدایی که نهان را در آسمان‌ها و زمین بیرون می‌آورد و آنچه را پنهان می‌دارید و آنچه را آشکار می‌نمایید می‌داند، سجده نکنند.'),
(3185, 27, 26, 'خدا -هیچ معبودی جز او نیست- پروردگار عرشِ بزرگ است.'),
(3186, 27, 27, 'گفت: «خواهیم دید آیا راست گفتی یا از دروغگویان بوده‌ای.»'),
(3187, 27, 28, '«این نامه‌ی مرا ببر. پس آن را سویشان بیفکن. سپس از ایشان روی بگردان، تا بنگری (که) چه بازگشتی (و پیامدی) دارند.»'),
(3188, 27, 29, '(ملکه‌ی سبا) گفت: «هان ای سران چشمگیر (کشور)! بی‌گمان نامه‌ای ارجمند سویم افکنده شده است.»'),
(3189, 27, 30, '«به‌راستی این نامه از طرف سلیمان است و به‌درستی (مضمونش این است): به نام خدای رحمتگر بر آفریدگان، رحمتگر بر ویژگان.»'),
(3190, 27, 31, '«اینکه بر من برتری مجویید و با حالت تسلیم نزد من آیید.»'),
(3191, 27, 32, '(ملکه‌ی سبا) گفت: «ای سران چشمگیر (کشورم)! مرا در کارم (با مشورت) رأیی تازه و شایسته دهید، بی‌گواهیتان به نزدم کاری را (هرگز) فیصله نمی‌داده‌ام.»'),
(3192, 27, 33, 'گفتند: «ما سخت نیرومند و دلاوریم و (اما) کل فرمان (تنها) سوی توست. پس بنگر چه فرمان می‌دهی‌؟»'),
(3193, 27, 34, '(ملکه) گفت: «همواره پادشاهان چون به مجتمعی درآیند، آن را تباه و بی‌مقدار و عزیزانش را خوار می‌گردانند و همین‌گونه (ویرانی) می‌کنند.»'),
(3194, 27, 35, '«و من حتماً فرستنده‌ی هدیه‌ای سویشان هستم. پس نگرانم (که) فرستادگان با چه (حالی) باز می‌گردند.»'),
(3195, 27, 36, 'پس هنگامی که (فرستاده‌اش) نزد سلیمان آمد، (سلیمان) گفت: «آیا مرا با مالی (سوی خود) می‌کشید و کمک می‌کنید؟ پس آنچه خدا به من داده، بهتر است از آنچه به شما داده. (نه،) بلکه به ارمغان خود شادمانی می‌کنید.»'),
(3196, 27, 37, '«به سویشان بازگرد تا بی‌چون (و) بی‌گمان با سپاهیانی سویشان روان می‌شویم که در برابر آنان تاب ایستادگی نداشته باشند و بی‌امان از آن (دیار) همواره به خواری و بی‌مقداری بیرونشان می‌کنیم.»'),
(3197, 27, 38, '(سپس) گفت: «ای سران (کشور)! کدام یک از شما تخت او را - پیش از آنکه مطیعانه نزدم آیند - برایم می‌آورد؟»'),
(3198, 27, 39, 'بزرگواری از جنیان گفت: «من آن را پیش از آنکه از جایگاه خود برخیزی برایت می‌‌آورم. همانا من بر این(کار) سخت توانا و امانت‌دارم.»'),
(3199, 27, 40, 'کسی که نزد او دانشی از کتاب (ربانی) بود، گفت: «من آن را پیش از آنکه چشم خود را بر هم زنی برایت آورنده‌ام.» پس چون (سلیمان) آن (تخت) را نزد خود مستقر دید، گفت: «این از فضل پروردگار من است، تا مرا بیازماید (که) آیا شکر می‌گزارم یا کفران می‌ورزم. و هر که سپاس گزارد، تنها به سود خویش سپاس می‌گزارد و هر که ناسپاسی کند، پس همانا پروردگارم بسی بی‌نیاز کریم است.»'),
(3200, 27, 41, 'گفت: «تخت (ملکه) را برایش ناشناس گردانید تا ببینیم آیا (بدان) رهنمون می‌شود یا از کسانی است که (به آن) راه نمی‌یابند.»'),
(3201, 27, 42, 'پس چون (ملکه) آمد، (بدو) گفته شد: «آیا تخت تو همین‌گونه است‌؟» گفت: «گویا این همان است و پیش از این هم ما آگاه شده و از تسلیم‌شدگان بوده‌ایم.»'),
(3202, 27, 43, 'و آنچه را غیر از خدا می‌پرستید مانع (ایمان) او شده بود. همانا او از گروه کافران بود.'),
(3203, 27, 44, 'به او گفته شد: «وارد ساحت کاخ (پادشاهی) شو.» پس چون دیدش، آن را برکه‌ای پنداشت و ساق‌های پایش را برگشود. سلیمان گفت: «بی‌گمان این کاخی مفروش از آبگینه‌هایی است.» (بلقیس) گفت: «من بی‌گمان به خود ستم کردم، و با (همراهی) سلیمان به پروردگار جهانیان (ایمان و) اسلام آوردم.»'),
(3204, 27, 45, 'و همانا سوی ثمود، برادرشان صالح را به‌راستی فرستادیم که: «خدا را بپرستید.» پس به ناگهان آنان دو دسته‌اند (که) با هم بسی دشمنی می‌کنند.'),
(3205, 27, 46, '(صالح) گفت: «ای قوم من! چرا پیش از نیکی، به بدی شتاب می‌جویید؟ چرا از خدا پوشش نمی‌خواهید؟ شاید شما مورد رحمت قرار گیرید.»'),
(3206, 27, 47, 'گفتند: «ما به تو و به هر کس (که) با تو است شگونِ (بد) زده‌ایم‌». گفت: «شگونتان نزد خداست. بلکه شما مردمی هستید که مورد آزمایشی آتشبار [:دشوار] قرار می‌گیرید.»'),
(3207, 27, 48, 'و در (آن) شهر نُه دسته بودند، که در آن سرزمین فساد می‌کردند و اصلاح نمی‌نمودند.'),
(3208, 27, 49, '(با هم) گفتند: «با همدیگر به خدا سوگند یاد کنید که حتماً به او [:صالح] و کسانش شبیخون زنیم. سپس بی‌چون به ولیِّ (دم و وارث) او همی خواهیم گفت: ما در محلّ هلاکت اهلش [:صالحیان] حاضر نبودیم. و به‌راستی ما همواره راستگویانیم.»'),
(3209, 27, 50, 'و دست به نیرنگی (بزرگ) زدند و (ما نیز) دست به نیرنگی (بزرگ) زدیم در حالی‌که (آنان) باریک‌بینی نمی‌کنند.'),
(3210, 27, 51, 'پس بنگر فرجام نیرنگشان چگونه بود، که ما بی‌گمان آنان و قومشان، همگی را هلاک کردیم.'),
(3211, 27, 52, 'پس آنها (همان) خانه‌های آنان است که به (سزای) بیدادگریشان ویران است. به‌راستی در این (کیفر) برای مردمی که می‌دانند همواره نشانه‌ای است.'),
(3212, 27, 53, 'و کسانی را که ایمان آورده‌اند و پرهیزگاری می‌کرده‌اند رهانیدیم.'),
(3213, 27, 54, 'و (یاد کن) لوط را چون به قومش گفت: «آیا در حالی (که) می‌نگرید مرتکب (این) عمل ناشایست تجاوزگر می‌شوید؟»'),
(3214, 27, 55, '«آیا شما بی‌گمان به جای زنان، همواره از روی شهوت با مردان در می‌آمیزید؟ بلکه شما مردمی هستید (که) نادانی می‌کنید.»'),
(3215, 27, 56, 'پس پاسخ قومش غیر از این نبود که گفتند: «خاندان لوط را از مجتمعتان بیرون کنید. آنها مردمانی هستند که سخت به پاکیز‌گی تظاهر می‌نمایند.»'),
(3216, 27, 57, 'پس او و خانواده‌اش را نجات دادیم، جز زنش را که مقدّر کردیم از بازماندگان در غبار کفر باشد.'),
(3217, 27, 58, 'و بارانی (شدید از سنگ گل) بر سر (و سامان)‌شان فرو بارانیدیم. پس بارانِ هشدارداده‌شدگان چه بد بود.'),
(3218, 27, 59, 'بگو: «همه‌ی سپاس‌ها برای خداست و درودی (فراوان) بر بندگان ویژه‌اش که آنان را به شایستگی برگزید.» آیا خدا بهتر است یا آنچه (با او) شریک می‌گردانند؟'),
(3219, 27, 60, '(آیا آنچه شریک می‌پندارند بهتر است) یا کسی که آسمان‌ها و زمین را آفرید و برای شما آبی از آسمان فرود آورد؛ پس با آن بستان‌هایی فرح‌انگیز به بار آورد؟ برای شما (هرگز چنان) نبوده است که درختش را برویانید. آیا با خدا، خدایی است؟ بلکه اینان گروهی می‌باشند که برای خدا همتا برمی‌گزینند.'),
(3220, 27, 61, '(آیا شریکانی که می‌پندارند بهترند) یا آن کس که زمین را قرارگاهی ساخت و در جایاجایش رودهایی پدید آورد و برای آن (زمین کشتی‌وار) کوه‌هایی همچون میخ‌ها (مانند لنگرها) قرار داد و میان دو دریا پرده‌ای (ناپیدا) گذاشت‌؟ آیا معبودی با خداست‌؟ (نه) بلکه بیشترشان نمی‌دانند [:نادانی می‌کنند].'),
(3221, 27, 62, 'آیا کیست (کسی) که درمانده را - چون وی را بخواند - (به خوبی) اجابت می‌کند و گرفتاری(اش) را برطرف می‌گرداند و شما را جانشینان (دیگران در) زمین قرار می‌دهد؟ آیا معبودی با خداست‌؟ چه کم است آنچه را یادآور می‌شوید.'),
(3222, 27, 63, 'یا کیست (آن‌که) شما را در (دل) تاریکی‌های خشکی و دریا راه می‌نماید و آن کس که بادها(ی رحمت‌زا) را پیشاپیش رحمتش به بشارتگری می‌فرستد؟ آیا معبودی با خداست‌؟ خدا برتر (و بزرگ‌تر) است از آنچه (با او) شریک می‌گردانند.'),
(3223, 27, 64, 'یا کیست (که) آفرینش را آغاز می‌کند، سپس آن را باز می‌گرداند؟ و کیست (که) از آسمان و زمین به شما روزی می‌دهد؟ آیا معبودی با خداست‌؟ بگو: «اگر (از) راستان بوده‌اید برهانتان را بیاورید.»'),
(3224, 27, 65, 'بگو: «هر (که) در آسمان‌ها و زمین است - جز خدا غیب (ویژه‌ی خدا) را نمی‌داند و هرگز نمی‌‌‌فهمند کی برانگیخته می‌شوند.»'),
(3225, 27, 66, '(نه،) بلکه علم آنان در آخرت تدارک یافته ! (که درآن جهل از روی عنادشان برطرف می‌شود)، بلکه ایشان در ژرفای شکی از آنند. بلکه آنان در مورد آن کور(دلان‌)اند.'),
(3226, 27, 67, 'و کسانی که کافر شدند، گفتند: «آیا وقتی ما خاک باشیم و پدرانمان (نیز خاک باشند) آیا به‌راستی همی (زنده از گور) بیرون‌شوندگانیم‌؟»'),
(3227, 27, 68, 'بی‌گمان، ما و پدرانمان از پیش بی‌چون (به) این (رستاخیز) وعده داده شدیم. این جز افسانه و بافته‌های پیشینیان نیست.»'),
(3228, 27, 69, 'بگو: «در زمین سیر (و نگرش) کنید؛ پس بنگرید فرجام مجرمان چگونه بوده است.»'),
(3229, 27, 70, 'و بر آنان غم مخور و از آنچه مکر می‌کنند (هرگز) در تنگنایی مباش.'),
(3230, 27, 71, 'و می‌گویند: «اگر (از) راستان بوده‌اید، این وعده کی خواهد بود؟»'),
(3231, 27, 72, 'بگو: «شاید برخی از آنچه را به شتاب همی خواهید، برایتان ردیف (و) پیاپی بیاید.»'),
(3232, 27, 73, 'و به‌راستی پروردگارت بی‌گمان بر مردمان کانون فضیلت است ولی بیشترشان سپاس نمی‌گزارند.'),
(3233, 27, 74, 'و همانا پروردگار تو آنچه را در سینه‌هایشان نهفته دارند و آنچه را آشکار می‌دارند همواره می‌داند.'),
(3234, 27, 75, 'و هیچ (حالتی) پنهانی در آسمان و زمین نیست، مگر اینکه در کتابی روشنگر است.'),
(3235, 27, 76, 'بی‌گمان، این قرآن بر فرزندان اسرائیل بیشترِ آنچه را که آنان درباره‌اش اختلاف دارند گزارش [:و برش تاریخی پیگیر] می‌دهد.'),
(3236, 27, 77, 'و به‌راستی این (قرآن) برای مؤمنان همواره هدایت و رحمتی (کامل) است.'),
(3237, 27, 78, 'به‌درستی پروردگارت به حکم خود میان آنان داوری قطعی می‌کند. و اوست همان عزیز حکیم.'),
(3238, 27, 79, 'پس بر خدا توکّل کن که تو همواره بر(سرو سامان) تمامی حقِ آشکارگری.'),
(3239, 27, 80, 'البته تو مردگان را نمی‌شنوانی و خواسته‌ات را به کران - چون پشت گردانند - نمی‌شنوانی.'),
(3240, 27, 81, 'و تو راهبر کوردلان (و بازگرداننده‌ی آنان) از گمراهیشان نیستی. تو جز کسانی را که به نشانه‌های ما ایمان می‌آورند پس (برابر خدا) تسلیمند، نمی‌توانی بشنوانی.'),
(3241, 27, 82, 'و چون سخن (عذاب) بر ایشان فرود آید، جنبنده‌ای را از زمین برایشان بیرون می‌آوریم، در حالی‌که با ایشان (این‌گونه) سخن می‌گوید؛ مردمان بی‌گمان به نشانه‌های ما یقین نمی‌آورده‌اند.'),
(3242, 27, 83, 'و روزی از هر امّتی گروهی شتابان (در تکذیب حق) از کسانی را که با نشانه‌های ما (همان‌ها و ما را) تکذیب می‌کنند محشور می‌گردانیم ، تا آنان، همگان، با نگهبانی (ربانی) گردآوری شوند؛ پس پراکنده نگردند.'),
(3243, 27, 84, 'تا چون (آن تکذیب‌کنندگان) بیایند، فرماید: «آیا با نشانه‌هایم (همان‌ها و ما را) تکذیب کردید، در حالی که احاطه‌ی علمی به آنها نداشتید؟ یا چه می‌کرده‌اید؟»'),
(3244, 27, 85, 'و به (کیفر) آنچه ستم کردند، (آن) گفته و وعده(ی عذاب) بر آنان فرود آید. پس ایشان دم برنیاورند.'),
(3245, 27, 86, 'آیا ندیده‌اند که ما شب را قرار داده‌ایم تا در آن بیاسایند و روز را روشنی‌بخش (گردانیدیم)؟ همانا در آن (جریان) برای مردمی که ایمان می‌آورند بی‌گمان نشانه‌هایی (بزرگ) است.'),
(3246, 27, 87, 'و روزی که در صور [:بوق جان‌فرسا] دمیده شود، پس هر کس در آسمان‌ها و زمین است (سخت) به هراس (و بی‌هوشی) اُفتد - مگر آن کس که خدا خواسته - و جملگی با زبونی رو سوی او آوردند.'),
(3247, 27, 88, 'و کوه‌ها را می‌بینی در حالی‌که آنها را بی‌حرکت می‌پنداری، حال آنکه آنها (همچون) مرور ابر حرکت می‌کنند. ساخته‌ی خدا را (ببینید) که هر چیز را سازوار کرده. او بی‌گمان به آنچه انجام می‌دهید بسی آگاه است.'),
(3248, 27, 89, 'کسانی که با نیکی بیایند، در نتیجه پاداشی بهتر از آن برایشان است و حال آنکه آنان از هراسی بزرگ در چنان روزی در امانند.'),
(3249, 27, 90, 'و کسانی که با بدی بیایند، در نتیجه به رو در آتش (دوزخ) سرنگون شوند، (و به آنان گفته شود:) «آیا جز آنچه می‌کرده‌اید سزا داده می‌شوید؟»'),
(3250, 27, 91, '«جز این نیست که من مأمور شدم تنها پروردگار این مجتمع را -که محرّم و محترم و قُرُق‌گاه شمرده و هر چیزی از اوست – بپرستم و فرمان یافتم (در برابرش) از تسلیم‌کنندگان (خود و دیگران) باشم.»'),
(3251, 27, 92, '«و اینکه قرآن را بخوانم و (آن را) پیروی کنم.» پس هر کس راه یابد در نتیجه تنها به سود خود راه می‌یابد و هر کس گمراه شود پس بگو: «من تنها از هشداردهندگانم.»'),
(3252, 27, 93, 'و بگو: «تمام ستایش‌ها برای خداست. به زودی آیاتش را به شما نشان خواهد داد، پس آنها را خواهید شناخت.» و پروردگارت از آنچه می‌کنید غافل نیست.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طسم.'),
(3254, 28, 2, 'آنها آیات آن کتاب روشنگر است.'),
(3255, 28, 3, 'بر تو برخی از خبر مهم موسی و فرعون را به تمامیِ حق برای (راهنمایی) گروهی که ایمان می‌آورند می‌خوانیم.'),
(3256, 28, 4, 'فرعون همواره در زمین بلندپروازی کرد و اهل زمین (مصر) را گونه‌گون گردانید؛ حال آنکه گروهی را به ضعف و ناتوانی می‌کشانَد، پسرانشان را می‌کُشد و زنانشان را زنده و بی‌حیا وا می‌گذارد. بی‌گمان او از مفسدان است.'),
(3257, 28, 5, 'و (ما) می‌خواهیم بر کسانی که در زمین ناتوان برگرفته شده‌اند منت نهیم و آنان را پیشوایان (دیگران) کنیم، و وارثان (فرعونیان) قرارشان دهیم.'),
(3258, 28, 6, 'و برایشان در زمین تمکین دهیم و (از طرفی هم) به فرعون و هامان و لشکریانشان آنچه را که از آن بیمناک بوده‌اند - از آنان [:مستضعفان] - (علیه فرعونیان) بنمایانیم.'),
(3259, 28, 7, 'و به مادر موسی وحی کردیم که: «او را (همی) شیر ده. پس هنگامی که بر او (از فرعونیان) بیمناک شدی او را در دریا بیفکن و مترس و اندوه مدار. ما او را به تو، بی‌چون، بازگرداننده‌ایم و (بی‌گمان) از پیمبرانش قراردهنده‌ایم.»'),
(3260, 28, 8, 'پس خاندان فرعون، او را با کاوش و کوشش (از آب) یافتند، تا (ناخودآگاه و ناخواسته) سرانجام برایشان دشمنی و (مایه‌ی) اندوهی باشد. فرعون و هامان و لشکریانشان بی‌گمان خطاکاران بوده‌اند.'),
(3261, 28, 9, 'و همسر فرعون گفت: « (این کودک) نور چشم من و توست. او را مکشید. شاید سودمان دهد یا او را به فرزندی برگیریم.» در حالی که اینان باریک‌بینی نمی‌کردند.'),
(3262, 28, 10, 'و دل مادر موسی (از هر چیزی، جز از فکر فرزندش) تهی گشت، (و) اگر قلبش را استوار نساخته بودیم، تا از ایمان‌آورندگان (به آن وعده‌ی ربانی) باشد، چیزی نمانده بود که آن (راز) را به‌راستی آشکار کند.'),
(3263, 28, 11, 'و مادر موسی به خواهرش [:خواهر موسی] گفت: «از پی او به جستجو برو.» پس او را با دقت، زیرچشمی، از دور بدید. حال آنکه آنان [:فرعونیان] دقت نمی‌کنند.'),
(3264, 28, 12, 'و (ما) از پیش، شیر دایگان را بر او حرام گردانیدیم. پس (خواهرش آمد و) گفت: «آیا شما را بر (حالت) خانوداه‌ای راهنمایی کنم که برای شما او را کفالت [:و سرپرستی] کنند، در حالی‌که نصیحت‌گرانش باشند؟»'),
(3265, 28, 13, 'پس او را به مادرش باز گردانیدیم، تا چشمش (بدو) روشن شود و غم نخورد و تا بداند که وعده‌ی خدا بی‌گمان درست است؛ ولی بیشترشان نمی‌دانند.'),
(3266, 28, 14, 'و هنگامی که به رشدهایش رسید و (به پای خود) ایستاد، به او حاکمیت و دانشی (وحیانی) دادیم. و این‌گونه نیکوکاران را پاداش می‌دهیم.'),
(3267, 28, 15, 'و داخل شهر شد، بر (آستانه‌ی) دمی از غفلت اهلش. پس دو مرد را یافت (که) با هم کشتار می‌کنند؛ (یکی از) این (دو مرد) از پیروانش و این (دیگری) از دشمنانش (بود). پس آن کس که از پیروانش بود، بر (ضدّ) کسی که از دشمنانش بود، از او یاری خواست. پس موسی مشتی پُر [:محکم] بدو نواخت. پس او را (ناخواسته) کشت (و) گفت: «این (نزاع و کشتن ناخواسته) کار شیطان است، چرا که او بی‌چون دشمنی گمراه‌کننده‌‌ی آشکارگر است.»'),
(3268, 28, 16, 'گفت: «پروردگارم! من بر خویشتن ستم کردم، پس برایم پوششی (از این کشتن ناخواسته) بنه.» در نتیجه خدا (هم) برای او (این جریان را) پوشید. او، بی‌گمان (هم)او، بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(3269, 28, 17, '(موسی) گفت: «پروردگارم! به پاس نعمتی که بر من ارزانی داشتی هرگز پشتیبان مجرمان نخواهم بود.»'),
(3270, 28, 18, 'پس در شهر به حال بیم و هراس در انتظار (لطفی ربانی) بود، که ناگهان (دید) همان کس که دیروز از وی یاری خواسته بود (باز) فریادکنان از او یاری می‌خواهد. موسی بدو گفت: «همانا تو بی چون نادانِ بس مفسده‌جوی آشکارگری.»'),
(3271, 28, 19, 'پس چون خواست به آن‌که دشمن هر دوشان بود حمله برد، (فرد فرعونی) گفت: «موسی! آیا می‌خواهی مرا بکشی چنان که دیروز شخصی را کشتی‌؟ تو نمی‌خواهی در این سرزمین جز زورگویی بوده و نمی‌خواهی از اصلاحگران باشی.»'),
(3272, 28, 20, 'و از دور افتاده‌ترین نقطه‌ی شهر، مردی دوان دوان آمد (و) گفت: «ای موسی! سران قوم درباره‌ی تو از یکدیگر نظرخواهی می‌کنند تا تو را بکشند، پس (از شهر) برون شو. همانا من برایت از نصیحت‌گرانم.»'),
(3273, 28, 21, 'پس (موسی) ترسان و نگران از آن جایگاه (بی‌سامان، بی‌امان) به انتظار (نصرت ربانی) برون رفت (و) گفت: «پروردگارم! مرا از گروه ستمکاران نجات بخش.»'),
(3274, 28, 22, 'و هنگامی که سوی (شهر) مَدْیَن روی نهاد (با خود) گفت: «امید است پروردگارم مرا به میانه‌ی راه راهوار هدایت کند.»'),
(3275, 28, 23, 'و چون به آب‌گاهِ مَدْیَن وارد شد، گروهی از مردم را بر آن یافت، (که دام‌های خود را) آب می‌دهند و پشت سرشان دو زن را یافت که (مردان را) از (اطراف) خود دور می‌کنند. (موسی به آنان) گفت: «کار مهمتان چیست‌؟» گفتند: «ما (به‌ناچار) به گوسفندان خود آب نمی‌دهیم تا شبانان (همه‌ی گوسفندانشان را) برگردانند. و پدرمان پیری سالخورده است.»'),
(3276, 28, 24, 'پس برای آن دو، (گوسفندانشان را) آب داد. سپس سوی سایه برگشت. پس گفت: «پروردگارم! به‌راستی من برای خیری که سویم فرستادی نیازمندم.»'),
(3277, 28, 25, 'پس یکی از آن دو زن - حال آنکه به آزرم گام بر می‌داشت - نزد وی آمد (و) گفت: «پدرم تو را می‌طلبد تا تو را به پاداش آب دادن (گوسفندان) برایمان، مزد دهد.» پس چون (موسی) نزدش آمد و سرگذشت‌ها(ی خود) را بر او حکایت کرد، (وی) گفت: «مترس (که) از گروه ستمگران نجات یافتی.»'),
(3278, 28, 26, 'یکی از آن دو (زن) گفت: «پدرم! او را به کاری برگمار، که بی‌گمان بهترین کس را به کارگری گرفتی؛ (او) نیرومند (و) در خور اعتماد است.»'),
(3279, 28, 27, '(شعیب) گفت: «من همی خواهم که تو را به نکاح یکی از این دو دختر خود در آورم، بر این (شرط) که هشت سال برایم اجیر شوی ، پس اگر ده سال را تمام گردانی از نزد (خود) تو است و نمی‌خواهم بر تو سخت بگیرم. زودا (که) مرا – اگر خدا بخواهد - از شایستگان بیابی.»'),
(3280, 28, 28, '(موسی) گفت: «این (قرار داد) میان من و تو است، (که) هر یک از دو مدّت را به انجام رسانیدم، پس بر من هرگز تجاوزی نیست و خدا بر آنچه می‌گوییم وکیل است.»'),
(3281, 28, 29, 'پس چون موسی آن مدّت را به پایان رسانید و خانواده‌اش را (همراهش به سفر) برد، با آتشی از جانب (کوه) طور انس گرفت. به خانواده‌ی خود گفت: «(اینجا) درنگ کنید، که من به‌راستی با آتشی (فراسوی طور) انس گرفتم، شاید خبری از آن، یا شعله‌ای آتش برایتان بیاورم، شاید گیرانه‌ای (از آن) بر گیرید.»'),
(3282, 28, 30, 'پس چون به آن (آتش) رسید، از سمت مبارک‌ترین جای آن وادی، در آن جایگاه مبارک، از آن درخت ندا آمد: «ای موسی! به‌راستی منم من، خدا، پروردگار جهانیان.»'),
(3283, 28, 31, '«و اینکه عصایت را بیفکن.» پس چون دید آن (عصا) مثل ماری می‌خزد، پشت کرد و هرگز برنگشت. (گفتیم:) «موسی! پیش آی و مترس، تو بی‌گمان از آرامش‌یافتگانی.»'),
(3284, 28, 32, '«دستت را به خوبی در گریبانت ببر، تا (با) سپیدی بی‌گزندی برون آید و (برای رهایی) از این هراس بازویت را به پهلویت بچسبان. پس این دو (نشانه)، دو برهان از جانب پرودگار تو است سوی فرعون و ویژگانش. آنان بی‌گمان قومی نافرمان بوده‌اند.»'),
(3285, 28, 33, 'گفت: «پروردگارم! من به‌راستی کسی از ایشان را کشته‌ام، پس می‌ترسم مرا بکشند.»'),
(3286, 28, 34, '«و برادرم هارون، او از من روشن‌بیان‌تر است. پس او را با من به دستیاری و بازداری گسیل دار تا مرا تصدیق کند. همواره من می‌ترسم تکذیبم کنند.»'),
(3287, 28, 35, 'خدا فرمود: «به زودی بازویت را با برادرت نیرومند خواهیم کرد و با آیاتمان برای هر دو سلطه‌ای خواهیم نهاد. پس با آیاتمان به شما دست نخواهند یافت. شما و هر که پیرویتان کند چیرگانید.»'),
(3288, 28, 36, 'پس هنگامی‌که موسی آیات روشن ما را برایشان آورد، گفتند: «این جز سحری افترا زده (بر خدا) نیست، و ما از پدارن نخستینمان چنان چیزی نشنیده‌ایم.»'),
(3289, 28, 37, 'و موسی گفت: «پروردگارم به (حال) کسی که از نزدش رهنمود آورده و کسی که فرجام (نیکوی) آن سرا برای اوست، داناتر است. ظالمان بی‌گمان رستگار نمی‌کنند.»'),
(3290, 28, 38, 'و فرعون گفت: «هان ای بزرگان (دربار)! من جز خویشتن برای شما خدایی ندانستم. پس ای هامان! برایم بر گل آتشی بیفروز. پس برجی (بلند) برایم بساز، شاید سوی خدای موسی اطّلاع یابم. و من بی‌گمان او را به‌راستی از دروغگویان گمان دارم.»'),
(3291, 28, 39, 'و او و سپاهیانش در آن سرزمین به ناحق کبر جستند و پنداشتند که سوی ما هرگز بازگردانیده نمی‌شوند.'),
(3292, 28, 40, 'پس او و سپاهیانش را (فرو) گرفتیم و در پی‌اش آنان را در دریا افکندیم. پس بنگر فرجام (کار) ستمکاران چگونه بود.'),
(3293, 28, 41, 'و آنان را پیشوایانی گردانیدیم که (خود و دیگران را) سوی آتش می‌خوانند و روز رستاخیز یاری نخواهند شد.'),
(3294, 28, 42, 'و در این دنیا لعنتی بدرقه‌ی آنان کردیم، و روز قیامت (نیز) ایشان از (جمله‌ی) زشت‌شدگانند.'),
(3295, 28, 43, 'و پس از آنکه نسل‌های نخستین را هلاک کردیم، به‌راستی همانا به موسی کتاب دادیم، حال آنکه دیدگاه‌ها و رهنمود و رحمتی برای مردمان بود. شاید آنان با کوشش و کاوش یاد کنند.'),
(3296, 28, 44, 'و چون فرمان (پیامبری) را به موسی واگذاشتم، تو در جانب غربی (طور) نبودی و از گواهان حاضر (نیز) نبودی.'),
(3297, 28, 45, 'ولی ما نسل‌هایی پدیدآوردیم. پس عمرهاشان بر آنان به درازا (و چیرگی و تکبر) کشید، و تو در میان ساکنان (شهر) مَدْیَن ساکنی پایدار نبودی، تا آیاتمان را برایشان بخوانی، لیکن ما فرستندگان (پیامبران و نشانه‌های رسالت‌ها) بوده‌ایم.'),
(3298, 28, 46, 'و چون (موسی را) ندا در دادیم، تو[:محمد] درکنارِ طور نبودی. ولی (با) رحمتی از پروردگارت (قرآن را بر تو وحی کردیم) تا قومی را - که هیچ هشداردهنده‌ای پیش از تو برایشان نیامده است - هشدار دهی. شاید آنان به‌خوبی به خود آیند.'),
(3299, 28, 47, 'و اگر (این‌گونه) نبود که به (سزای) پیش‌فرست دست‌ها و نیروهایشان مصیبتی به ایشان در رسد، (بهانه‌ای بود) تا بگویند: «پروردگارمان! چرا پیامبری سویمان نفرستادی تا از آیات تو پیروی کنیم و از مؤمنان باشیم؟»'),
(3300, 28, 48, 'پس چون حق از نزد ما برایشان آمد، گفتند: «چرا مانند آنچه به موسی داده شده، به او داده نشد‌؟» آیا و مگر به آنچه از پیش به موسی داده شد کفر نورزیدند؟ گفتند: «دو ساحر (با هم در آمیخته و) پشت به هم داده و خودنمایی کرده‌اند.» و گفتند: «همواره ما به همه‌ی این‌ها کافریم.»'),
(3301, 28, 49, 'بگو: «پس اگر (از) راستان بوده‌اید، کتابی از جانب خدا بیاورید که از این دو هدایت‌کننده‌تر باشد، تا پیرویش کنم.»'),
(3302, 28, 50, 'پس اگر برایت اجابت نکردند، بدان که تنها هوس‌های خود را پیروی می‌کنند و کیست گمراه‌تر از آن کس (که) - به‌غیر رهنمودی از خدا - هوسش را پیروی کند؟ بی‌گمان خدا گروه ستمگران را راهنمایی نمی‌کند.'),
(3303, 28, 51, 'و به‌راستی، بی‌گمان گفتار (وحیانی) را برای آنان پی در پی و به هم پیوسته ساختیم، شاید آنان به‌درستی یاد کنند.'),
(3304, 28, 52, 'کسانی که قبل از آن[:قرآن]، کتاب (آسمانی) بدیشان داده‌ایم، اینان به آن می‌گروند.'),
(3305, 28, 53, 'و هنگامی‌که بر ایشان فرو خوانده شود، گویند: «بدان ایمان آوردیم، همان بی‌گمان تمامی حق از طرف پروردگار ماست‌؛ ما پیش از آن (هم) بی‌چون از تسلیم‌شوندگان بوده‌ایم.»'),
(3306, 28, 54, 'ایشان - به (پاس) آنکه صبر کردند و بدی را با نیکی دفع می‌نمایند و از آنچه روزیشان داده‌ایم انفاق می‌کنند - پاداششان دو چندان به آنان داده می‌شود.'),
(3307, 28, 55, 'و هنگامی که لغو بشنوند، از آن روی برتابند و گویند: «کردارهای ما از آنِ ما و کردارهای شما از آنِ شماست! سلام بر شما! (ما) جویای دستیابی به نادانان نیستیم.»'),
(3308, 28, 56, 'به‌راستی تو هر که را دوست داری راهنمایی نکنی، لیکن خداست که هر که را بخواهد راهنمایی می‌کند و او به راه‌یافتگان (راستین، از دیگران) داناتر است.'),
(3309, 28, 57, 'و گفتند: «اگر با تو از (این) رهنمون پیروی کنیم، از سرزمین خود بس ربوده خواهیم شد!» آیا و برایشان حرمی پر از آرامش را جایگاهی نداده‌ایم، که محصولات هر چیزی - که رزقی از جانب ماست - سوی آن گزینه و فراورده می‌شود؟ ولی بیشترشان نمی‌دانند.'),
(3310, 28, 58, 'و چه بسیار گروه‌هایی را – که زندگی خوش آنها را سرمست کرده بود – به هلاکت رسانیدیم. پس اینهاست سراهایشان که پس از آنان - جز اندکی- مورد سکونت قرار نگرفته و ماییم، ما که وارث آنان بوده‌ایم.'),
(3311, 28, 59, 'و پروردگارت (هرگز) هلاک‌کننده‌ی مجتمعات نبوده تا (مگر اینکه) در مرکز آنها پیامبری بفرستد (که) آیاتمان را برایشان می‌خواند، و ما ویران‌کننده‌ی قریه‌‌ها نبوده‌ایم جز (در حالی که) اهلشان ستمکارانند.'),
(3312, 28, 60, 'و (هر) آنچه به شما داده شده است، پس کالای زندگی دنیا و زیور آن است. و آنچه نزد خداست بهتر و پایدارتر است. پس مگر خردورزی نمی‌کنید؟'),
(3313, 28, 61, 'آیا پس کسی که وعده‌ی نیکو به او داده‌ایم (و) در نتیجه او بدان رسنده است، مانند کسی است که از کالای زندگی دنیا بهره‌مندش گردانیده‌ایم، سپس او روز قیامت از (جمله‌ی) احضار شدگان (برای حساب و جزا)ست؟'),
(3314, 28, 62, 'و روزی که (خدا) آنان را ندا در می‌دهد، پس فرماید: «شریکان من - که می‌پنداشته‌اید - کجایند؟»'),
(3315, 28, 63, 'آنان که قول (عذاب) بر ایشان حق و ثابت آمده، گفتند: «پروردگارمان! اینانند کسانی که گمراه کردیم، گمراهشان کردیم همچنان که خود گمراه شدیم. (از آنان) سوی تو بیزاری جستیم. (آنها) تنها ما را نمی‌پرستیدند (بلکه نخست هواهای نفسانی خود را می‌پرستیدند).»'),
(3316, 28, 64, 'و (به آنان) گفته شد: «شریکانتان را بخوانید.» پس آنان را خواندند، در نتیجه پاسخشان ندادند و عذاب را دیدند (و آرزو کردند که) ای کاش هدایت‌یافته بودند.'),
(3317, 28, 65, 'و روزی را که (خدا) آنان را ندا در دهد، پس فرماید: «فرستادگان(مان) را چه پاسخ دادید؟»'),
(3318, 28, 66, 'در چنان روزی اخبار مهم بر ایشان بسی پوشیده گشت؛ پس از یکدیگر نمی‌پرسند.'),
(3319, 28, 67, 'پس اما کسی که توبه کرد و ایمان آورد و به کار شایسته پرداخت، شاید از رستگارکنندگان باشد.'),
(3320, 28, 68, 'و پروردگارت هر چه بخواهد می‌آفریند و بر می‌گزیند. برای آنان (هرگز) اختیار (آفرینش) نبوده است. منزّه است خدا و از آنچه (با او) شریک می‌گردانند برتر است.'),
(3321, 28, 69, 'و پروردگارت آنچه را سینه‌هایشان فرو می‌پوشاند یا آشکار می‌دارد، می‌داند.'),
(3322, 28, 70, 'و اوست خدا. جز او معبودی نیست. در دنیا و آخرت، تنها سپاس از آنِ اوست و فرمان تنها او راست و تنها سوی او باز گردانیده می‌شوید.'),
(3323, 28, 71, 'بگو: «آیا (چنان) دیدید (که) اگر خدا تا روز رستاخیز شب را بر شما جاودان بدارد، جز خدا کدامین معبود برای شما روشنی می‌آورد؟ آیا پس (از این هم) نمی‌شنوید؟»'),
(3324, 28, 72, 'بگو: «آیا (چنان) دیدید (که) اگر خدا تا روز قیامت روز را بر شما جاودان بدارد، جز خدا کدامین معبود برای شما شبی می‌آورد که در آن آرام گیرید؟ آیا پس (از این هم) درست نمی‌نگرید؟»'),
(3325, 28, 73, 'و از رحمتش شب و روز را برایتان قرار داد، تا در این (یک) بیارمید و (در آن یک) از فزون‌بخشی او (روزی خود را) بجویید و شاید شما سپاس گزارید.'),
(3326, 28, 74, 'و روزی را که (خدا) ندایشان دهسد، پس فرماید: «شریکان من که می‌پنداشته‌اید کجایند؟»'),
(3327, 28, 75, 'و از میان هر امتی گواهی برون کشیدیم. پس (به مشرکان) گفتیم: «برهان خود را بیاورید.» پس دانستند که تمامی حق از خداست و آنچه بر می‌بافته‌اند از (دیدگان) آنان گم گشت.'),
(3328, 28, 76, 'به‌راستی قارون از قوم موسی بود. پس بر آنان ستم کرد و از گنجینه‌ها آن قدر به او داده بودیم که درب‌هایشان به‌راستی با گروهی از نیرومندان به حرکت می‌آمد. چون قوم وی بدو گفتند: «شادی (بی‌حساب) مکن که خدا (این‌گونه) شادی‌کنندگان را هرگز دوست نمی‌دارد،»'),
(3329, 28, 77, '«و در آنچه خدایت داده سرای آخرت را بجوی و سهم خود را از دنیا فراموش مکن و چنان که خدا به تو نیکی کرده (به دیگران) نیکی کن و در زمین افساد مجوی که خدا افسادگران را بی‌گمان‌دوست نمی‌دارد.»'),
(3330, 28, 78, '(قارون) گفت: «من این‌ها را تنها بر (محور) بینش و دانشی از خودم یافته‌ام‌.» آیا و ندانست که خدا به‌راستی نسل‌هایی پیش از او را بی‌چون نابود کرد که از او نیرومندتر و جمع‌آوریشان و جمعیتشان بیشتر (بوده) است؟ و از گناهان مجرمان پرسش (هم) نمی‌شود.'),
(3331, 28, 79, 'پس (قارون) با آرایش و کوکبه‌ی خویش برابر (دیدگان) قومش برون شد. کسانی که همواره خواستار زندگی دنیایند گفتند: «ای کاش مثل آنچه به قارون داده شده به ما (هم) داده می‌شد. به‌راستی او همواره بس بهره‌ی بزرگی (از ثروت) دارد.»'),
(3332, 28, 80, 'و کسانی که علم و ایمان داده شده بودند، گفتند: « وای بر شما! برای کسی که گرویده و کار شایسته کرده پاداش خدا بهتر است و جز شکیبایان، ثواب (خدا) به شایستگی به آنان رسانده نشود.»'),
(3333, 28, 81, 'پس قارون را با خانه‌اش در زمین فرو بردیم. در نتیجه هیچ گروهی پس از خدا نداشت تا یاریش کنند و (خود نیز) از یاری‌جویندگان نبوده است.'),
(3334, 28, 82, 'و (همان) کسانی که دیروز آرزو داشتند جایگاهی همانند او می‌داشتند گفتند: « وای (بر ما)! گویی خدا روزی را برای هر کس از بندگانش بخواهد گشاده می‌کند. و (یا) تنگ می‌گرداند. و اگر خدا بر ما منّت ننهاده بود، ما را (هم به زمین) فرو برده بود. وای (بر ما)! گویی کافران بی‌گمان رستگار نمی‌کنند.»'),
(3335, 28, 83, 'آن است (همان) سرای آخرت؛ برای کسانی قرارش می‌دهیم که در زمین خواستار هیچ برتری و هیچ فسادی نیستند، و فرجام (خوش) تنها از آنِ پرهیزگاران است.'),
(3336, 28, 84, 'هر کس نیکی (به میان) آورد، برای او (پاداشی) بهتر از آن خواهد بود و هر کس بدی (به میان) آورد، کسانی که کارهای بد کرده‌اند جز (سزای) آنچه می‌کرده‌اند نخواهند یافت.'),
(3337, 28, 85, 'بی‌گمان (همان) کس که قرآن را بر تو فرض و حتمی کرد، همانا تو را سوی وعده‌گاهی بازگرداننده است. بگو: «پروردگارم بهتر می‌داند چه کس با هدایت آمده و چه کس در گمراهی آشکارگری است.»'),
(3338, 28, 86, 'و تو امیدوار نبوده‌ای که (این) کتاب سویت افکنده گردد، جز رحمتی از پروردگارت. پس تو هرگز پشتیبان کافران مباش.'),
(3339, 28, 87, 'و هرگز تو را (چیزی) از آیات خدا - بعد از آنکه بر تو نازل شده است - باز ندارد و سوی پروردگارت دعوت کن، و زنهار از مشرکان مباش.'),
(3340, 28, 88, 'و با خدا معبودی دیگر مخوان. خدایی جز او نیست، جز وجه [:ذات و وجهه‌ی] او همه چیز نابودشونده است. فرمان از اوست و تنها سوی او بازگردانیده می‌شوید.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الم.'),
(3342, 29, 2, 'آیا مردمان پنداشتند که (تا) گفتند ایمان آوردیم، رها می‌شوند، حال آنکه مورد آزمایشی آتشین قرار نمی‌گیرند؟'),
(3343, 29, 3, 'و (ما) بی‌گمان، کسانی را که پیش از اینان بودند همی سخت آزمودیم. پس خدا به‌راستی آنان را که راست گفته‌اند همواره (با علامتی ویژه) نشانه‌ای گذارد و دروغگویان را (نیز) بی‌چون و بی‌امان نشانه می‌نهد.'),
(3344, 29, 4, 'یا کسانی که کارهای بد می‌کنند، می‌پندارند که بر ما پیشی می‌گیرند؟ بدا (بر) آنچه حکم می‌کنند.'),
(3345, 29, 5, 'هر که امید لقای خدا را داشته، پس زمان (ویژه‌ی) خدا بی‌گمان (و) به راستی آمدنی است. و اوست بسی شنوای بسیار دانا.'),
(3346, 29, 6, 'هر که جهاد کند، تنها برای خود جهاد می‌کند. خدا بی‌چون از جهانیان به‌راستی بی‌نیاز است.'),
(3347, 29, 7, 'و کسانی که ایمان آورده و کارهای شایسته‌(ی ایمان) کردند، به راستی گناهانشان را از آنان به‌درستی می‌زداییم، و بهترین کاری که انجامش می‌داده‌اند بی‌گمان همان را به گونه‌ای بهتر پاداششان می‌دهیم.'),
(3348, 29, 8, 'و انسان را سفارش کردیم که به پدر و مادر خود نیکی‌‌‌‌‌‌ای (شایسته) کند و اگر(هم) آنها علیه تو (همی) کوشند تا چیزی را که بدان علم نداری با من شریک گردانی، پس از ایشان اطاعت مکن. سرانجامتان تنها سوی من است. پس شما را از (صورت و سیرت) آنچه انجام می‌داده‌اید، خبری مهم خواهم داد.'),
(3349, 29, 9, 'و کسانی که ایمان آوردند و کارهای شایسته(ی ایمان) کردند، بی‌گمان آنان را به‌راستی در (زمره‌ی) شایستگان در می‌آوریم.'),
(3350, 29, 10, 'و برخی از مردمان کسانی(اند که) گویند: «به خدا ایمان آوردیم‌.» پس چون در (راه) خدا آزار ببینند، فتنه‌ی مردمان را همانند عذاب خدا می‌نهند. و اگر به‌راستی از جانب پروردگارت یاری‌ای در رسد همواره بی‌چون خواهند گفت: «ما (همچنان) با شما بوده‌ایم‌.» آیا خدا به آنچه در سینه‌های جهانیان است داناتر (از کل آفریدگان) نیست‌؟'),
(3351, 29, 11, 'و خدا به‌راستی کسانی را که ایمان آورده‌اند به درستی نشانه می‌نهد و منافقان را نیز بی‌چون (و) بی‌امان نشانه می‌نهد.'),
(3352, 29, 12, 'و کسانی که کافر شدند، به کسانی که ایمان آوردند گفتند: «راه ما را پیروی کنید و ما باید گناهتان را بر عهده گیریم‌.» حال آنکه آنان عهده‌دار چیزی از گناهانشان نیستند(تا چه رسد به گناهان دیگران و) بی‌شک آنان دروغگویانند.'),
(3353, 29, 13, 'و همواره بارهای گران خودشان و بارهای گران (از دیگران) را با بارهای گران خودشان بی‌گمان بر دوش خواهند گرفت. و روز قیامت بی‌چون از آنچه به دروغ بر می‌بستند به‌درستی پرسیده خواهند شد.'),
(3354, 29, 14, 'و به‌راستی و درستی، نوح را سوی قومش فرستادیم. پس در میان آنان نهصدوپنجاه سال (در رسالتش) درنگ کرد. پس طوفان آنان را در حالی که (از) ستمکاران بودند فرو گرفت.'),
(3355, 29, 15, 'پس او را و کشتی‌نشستگان را رهانیدیم و آن کشتی را برای جهانیان نشانه‌ای نهادیم.'),
(3356, 29, 16, 'و (نیز) ابراهیم را (فرستادیم) چون به قومش گفت: «خدا را بپرستید و از او پروا بدارید. اگر می‌دانسته‌اید این (کار) برای شما خوب است‌.»'),
(3357, 29, 17, '«آنچه را که شما بجز خدا می‌پرستید تنها بتانی هستند و (شما) دروغی جعل می‌کنید. بی‌گمان کسانی را که جز خدا می‌پرستید اختیار هیچ‌گونه روزی را برای شما در دست ندارند. پس روزی را نزد خدا بجویید و (تنها) او را بپرستید و برایش سپاس گویید. تنها سوی او باز گردانیده می‌شوید.»'),
(3358, 29, 18, '«و اگر تکذیب کنید همواره امّت‌هایی پیش از شما (هم) تکذیب کردند و بر عهده‌ی پیامبر جز رساندن رسای وحی روشنگر (وظیفه‌ای) نیست.»'),
(3359, 29, 19, 'آیا و ندیدند که خدا چگونه آفرینش را آغاز می‌کند، سپس آن را باز می‌گرداند؟ بی‌گمان این کار بزرگ تنها بر خدا آسان است.'),
(3360, 29, 20, 'بگو: «در زمین بگردید. پس بنگرید چگونه خدا آفرینش را آغاز کرد؛ سپس (همان) خداست که نشأت بازپسین را پدید می‌آورد. به‌راستی خدا بر هر چیزی تواناست.»'),
(3361, 29, 21, '«هر که را بخواهد عذاب می‌کند و هر که را بخواهد رحم می‌کند و تنها سوی او دگرگون می‌شوید.»'),
(3362, 29, 22, 'و شما - نه در زمین و نه در آسمان - درمانده‌کننده(ی ما) نیستید. و برای شما بجز خدا هیچ ولی و هیچ یاوری نیست.'),
(3363, 29, 23, 'و کسانی که به آیات خدا و لقای او کافر شدند (هم)آنانند که از رحمت من نومیدند و ایشان برایشان عذابی پردرد است.'),
(3364, 29, 24, 'پس پاسخ قومش جز این نبود که گفتند: «بکشیدش، یا بسوزانیدش.‌» پس خدا او را از آتش نجات بخشید. بی‌گمان در این (نجات‌بخشیِ خدا) - برای گروهی که ایمان می‌آورند - به‌راستی نشانه‌هایی است.'),
(3365, 29, 25, 'و (ابراهیم) گفت: «از غیر خدا، بت‌هایی را اختیار کرده‌اید برای دوستی (و پیوستگی) میان شما در زندگی دنیا. سپس روز قیامت بعضی از شما دیگری را انکار و برخی هم دیگری را لعنت می‌کنید و پناهگاهتان آتش است و برای شما هیچ یاورانی نیست.»'),
(3366, 29, 26, 'پس لوط برایش (به رسالتش) ایمان آورد و (ابراهیم) گفت: «من به‌راستی سوی پروردگارم هجرت‌کننده‌ام. اوست بی‌گمان، (هم)او ارجمند حکیم.»'),
(3367, 29, 27, 'و اسحاق و یعقوب را به (سود) او بخشیدیم و در میان فرزندانش پیامبریِ باعظمت و کتاب نهادیم، و در دنیا پاداشش را به او دادیم و او بی‌گمان در آخرت به‌راستی از شایستگان (ویژه) است.'),
(3368, 29, 28, 'و لوط را (گسیل داشتیم) به قوم خود گفت: «شما همواره به کاری زشت تجاوزگر می‌پردازید، (کاری که) هیچ گروهی از مردمان در آن (کار) بر شما پیشی نگرفته‌اند.»'),
(3369, 29, 29, '«آیا شما با مردان در می‌آمیزید و راه (خدا) را قطع می‌کنید و در محفل (انس) خود آن (کار) منکر را انجام می‌دهید؟» پس پاسخ قومش جز این نبود که گفتند: «اگر از راستان بوده‌ای عذاب خدا را برایمان بیاور.»'),
(3370, 29, 30, '(لوط) گفت: «پروردگارم! مرا بر قوم افسادگر یاری فرما.»'),
(3371, 29, 31, 'و چون فرستادگان ما برای ابراهیم (آن) مژده را آوردند، گفتند: «ما بی‌چون هلاک‌کننده‌ی اهل این مجتمعیم (که) همواره مردمش ستمکار بوده‌اند.»'),
(3372, 29, 32, '(ابراهیم) گفت: «همانا لوط در آنجاست.‌» گفتند: «ما بهتر می‌دانیم چه کسی در آنجاست‌؛ به‌راستی او و کسانی را – جز زنش که از بازماندگان در غبار کفر بوده است - بی‌گمان نجات خواهیم داد.»'),
(3373, 29, 33, 'و هنگامی که فرستادگان ما سوی لوط آمدند، به (علّت حضور) ایشان برایش نگرانی رخ داد و دستش از (حمایت) آنان[:میهمانان] تنگ گردید. و (آنان) گفتند: «مترس و غم مدار. ما تو و خانواده‌ات را - جز زنت که از بازماندگان در غبار کفر بوده است - حتماً می‌رهانیم.»'),
(3374, 29, 34, '«ما بر مردم این قریه به (سزای) فسقی که می‌کرده‌اند، بی‌چون فرودآورنده‌ی عذابی پلید [:سنگ گل] از آسمانیم.»'),
(3375, 29, 35, 'و بی‌چون از آن (قریه‌ی ویران شده) - برای مردمی که خردورزی می‌کنند - به‌راستی نشانه‌ای روشن باز نهادیم.'),
(3376, 29, 36, 'و به سوی (مردم) مَدْیَن، برادرشان شعیب را (فرستادیم). پس (بدیشان) گفت: «ای قوم من! خدا را بپرستید و به روز بازپسین امیدوار باشید و در زمین سر به فسادکاری برمدارید.»'),
(3377, 29, 37, 'پس او را تکذیب کردند. در نتیجه زلزله آنان را (فرو) گرفت؛ (پس) بامدادان در خانه‌شان زمین‌گیر شدند.'),
(3378, 29, 38, 'و عاد و ثمود را (نیز هلاک نمودیم). حال آنکه بی‌گمان (فرجام آنان) از جایگاه‌ها(ی ویران)شان بر شما آشکار گردید، و شیطان کارهایشان را در نظرشان بیاراست؛ پس از راه (راست) بازشان داشت، در حالی که بینایی می‌جسته‌اند (ولی نابینایی می‌کرده‌اند).'),
(3379, 29, 39, 'و قارون و فرعون و هامان را (هلاک کردیم). و به‌راستی و درستی موسی برایشان دلایل آشکار آورد. پس در آن سرزمین سرکشی نمودند و (با این همه بر ما) پیشی‌گیرندگان (بر زیانمان) نبوده‌اند.'),
(3380, 29, 40, 'پس هر گروه (از ایشان) را به گناهش گرفتار کردیم: از آنان کسانی بودند که بر (سر و سامان)شان سنگ گل فرو فرستادیم‌؛ و از آنان کسانی بودند که فریاد (مرگبار) آنها را فرو گرفت؛ و از آنان کسانی را در زمین فرو بردیم؛ و بعضی از آنان را غرق کردیم. و (این) خدا نبوده که بدیشان ستم کند، بلکه خودهاشان بر خود ستم می‌کرده‌اند.'),
(3381, 29, 41, 'مَثَل کسانی که غیر از خدا سرپرستان و دوستانی اختیار کردند، همچون عنکبوت است، (که با آب دهان خود) با کوشش، خانه‌ای (برای خویش) برگرفت. و در حقیقت - اگر می‌دانستند - سست‌ترین خانه‌ها همان خانه‌ی عنکبوت است.'),
(3382, 29, 42, 'به‌راستی خدا هر آنچه را که جز او می‌خوانند، می‌داند و (هم) اوست عزیز حکیم.'),
(3383, 29, 43, 'و این‌ها مَثَل‌هاست (که) برای مردمان می‌زنیم و جز دانایان آنها را در نیابند.'),
(3384, 29, 44, 'خدا آسمان‌ها و زمین را به تمامی حق آفرید؛ بی‌گمان در این (آفرینش) برای ایمان‌آورندگان نشانه‌ای است.'),
(3385, 29, 45, 'آنچه از آن کتاب (وحیانی) سوی تو وحی شده است (بر مردمان) بخوان (و) آن را پیروی کن و نماز را بر پا بدار (که) به‌راستی نماز از گناه تجاوزگر و منکر باز می‌دارد و به‌درستی یاد خدا (از هر یادی) بزرگ‌تر است. و خدا می‌داند چه می‌سازید.'),
(3386, 29, 46, 'و با اهل کتاب، جز با بهترین (شیوه،) مجادله مکنید. مگر (با) کسانی از آنان که ستم کردند. و بگویید: «به آنچه سوی ما نازل شده و (آنچه) سوی شما نازل گشته ایمان آوردیم‌؛ و خدای ما و خدای شما یکی است، و ما تنها برای او تسلیمیم.»'),
(3387, 29, 47, 'و بدین‌سان (با بیان و برهان)، ما کتاب [:قرآن] را بر تو نازل کردیم. پس آنان را که کتاب دادیم بدان (قرآن) ایمان می‌آورند. و از میان مشرکان (نیز) کسانی‌اند که به آن می‌گروند و جز کافران (و تاریک‌دلان کسی) آیات ما را (انگار و) انکار نمی‌کند.'),
(3388, 29, 48, 'و تو هیچ کتابی را پیش از این نمی‌خوانده‌ای و با دست راست [:نیروی نوشتار راستینت] (کتابی) نمی‌نوشته‌ای. (وگرنه) در این هنگام باطل‌اندیشان بی‌گمان به شک مستندی می‌افتادند.'),
(3389, 29, 49, 'بلکه آن [:قرآن] آیاتی روشن در سینه‌های کسانی است که علم یافته‌اند. و جز ستمگران منکر آیاتمان نمی‌شوند.'),
(3390, 29, 50, 'و گفتند: «چرا بر او از جانب پروردگارش نشانه‌هایی (ربانی) نازل نشده؟» بگو: «نشانه‌ها(ی ربانی) تنها نزد خداست، و من تنها هشداردهنده‌ای آشکارگرم.»'),
(3391, 29, 51, 'آیا و ایشان را بس نبود که این کتاب را (که) بر آنان خوانده می‌شود به‌راستی بر تو فرو فرستادیم‌؟ به‌درستی در این (کار پرمقدار) برای مردمانی که ایمان می‌آورند، به‌راستی رحمت و یادواره‌ای است.'),
(3392, 29, 52, 'بگو: «کافی است (که) خدا میان من و شما گواهی باشد. آنچه را که در آسمان‌ها و زمین است می‌داند و آنان که به باطل ایمان آورده و خدا را انکار (و انگار) کرده‌اند، اینان (هم) ایشان زیانکارانند.»'),
(3393, 29, 53, 'و از تو با شتاب درخواست عذاب دارند و اگر اجل و سرآمد (ربانی) یاد شده‌ای نبود، به‌درستی عذاب به آنان می‌رسید و بی‌آنکه هشیار شوند، بی‌گمان و بی‌امان غافلگیرشان خواهد کرد.'),
(3394, 29, 54, 'و شتابزده از تو عذاب می‌خواهند، حال آنکه جهنّم بی‌گمان و بی‌امان بر کافران احاطه دارد.'),
(3395, 29, 55, 'روزی را که عذاب -از بالای (سر)شان و از زیر پاهایشان - آنها را فراگیرد و (خدا) فرماید: «(پیامد) آنچه را می‌کرده‌اید بچشید.»'),
(3396, 29, 56, '«ای بندگان من که ایمان آوردید! زمین من بس فراخ است‌؛ پس تنها مرا بپرستید.»'),
(3397, 29, 57, 'هر کسی چشنده‌ی مرگ است، سپس سوی ما باز گردانیده می‌شوید.'),
(3398, 29, 58, 'و کسانی که ایمان آورده و کارهای شایسته(ی ایمان) انجام داده‌اند، آنان را به‌راستی در غرفه‌هایی از بهشت پناه می‌دهیم، حال آنکه از زیر (درختان)‌شان نهرها روان است (و آنان) جاودانه در آنجا بمانند. چه نیکوست پاداش عمل‌کنندگان؛'),
(3399, 29, 59, '(همان) کسانی که شکیبایی ورزیدند و تنها بر پروردگارشان توکّل می‌کنند.'),
(3400, 29, 60, 'و چه بسیار از جاندارانی که نمی‌توانند روزی خود را بر دوش‌هاشان کشند. خداست که آنها و شما را روزی می‌دهد و اوست بس شنوای بسیار دانا.'),
(3401, 29, 61, 'و اگر از ایشان بپرسی: «چه کسی آسمان‌ها و زمین را آفرید و خورشید و ماه را رام کرد؟» به‌راستی همی گویند: «خدا.» پس چگونه به دروغ (از خدا) بازداشته می‌شوند؟'),
(3402, 29, 62, 'خدا برای هر کس از بندگانش که بخواهد روزی را گشاده می‌گرداند و (یا برایش) تنگ می‌سازد. به‌راستی خدا به هر چیزی بسی داناست.'),
(3403, 29, 63, 'و اگر از آنان بپرسی: «چه کسی از آسمان، آبی فرو فرستاد، پس زمین را بعد از مرگش بدین وسیله زنده گردانید‌؟» به‌راستی بی‌چون گویند «خدا.» بگو: «ستایش تنها برای خداست.» بلکه بیشترشان خردورزی نمی‌کنند.'),
(3404, 29, 64, 'و این زندگی دنیا جز بازدارنده‌ای (از حق) و بازیچه‌ای بیش نیست و به‌راستی خانه‌ی آخرین - اگر می‌دانسته‌اند - بی‌گمان همان زندگی پرحرکت و پر برکت(حقیقی) است.'),
(3405, 29, 65, 'پس هنگامی که در کشتی سوار شوند، خدا را- با اخلاص دینی(شان) - پاکدلانه می‌خوانند. پس چون سوی خشکی نجاتشان داد، به ناگاه شرک می‌ورزند.'),
(3406, 29, 66, '(این بدین مقصود است) تا به آنچه بدیشان داده‌ایم انکار (و انگار) آورند و تا (از دنیا) برخوردار شوند. پس در آینده‌ای دور خواهند دانست.'),
(3407, 29, 67, 'آیا و ندیده‌اند که ما به‌راستی (برای آنان) حرمی سرشار از امن قرار دادیم، حال آنکه مردمان از اطرافشان بس ربوده می‌شوند؟ پس آیا به باطل ایمان می‌آورند و به نعمت خدا کفر و کفران می‌ورزند؟'),
(3408, 29, 68, 'و کیست ستمکارتر از آن کس که بر خدا دروغی را افترا بندد، یا هنگامی که حق سوی او آید آن را به همان وسیله تکذیب کند. آیا پناهگاه کافران در جهنّم نیست‌؟'),
(3409, 29, 69, 'و کسانی که در (راه) ما کوشیدند، به یقین راه‌های راهوار خود را همچنان برایشان رهبری می‌کنیم. بی‌گمان خدا به‌راستی با نیکوکاران است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الم.'),
(3411, 30, 2, 'رومیان شکست خوردند:'),
(3412, 30, 3, 'در نزدیکترین سرزمین (به مکه). و (اما) بعد از شکستشان، به زودی پیروز خواهند گردید؛'),
(3413, 30, 4, 'در ظرف چند سالی. هر کار، فرمان و چیزی -از گذشته و آینده- تنها از خداست و در چنان روزی است که مؤمنان شاد می‌گردند،'),
(3414, 30, 5, 'به یاری خدا. (و او) کسی را که بخواهد یاری می‌کند و او عزیز رحمتگر بر ویژگان است.'),
(3415, 30, 6, 'وعده‌ی (یاری) خدا را (بپذیر). خدا وعده‌اش را خلاف نمی‌کند ولی بیشتر مردم نمی‌دانند.'),
(3416, 30, 7, 'از زندگی دنیا (تنها) ظاهری (ناچیز) را می‌دانند، حال آنکه ایشان از آخرت، (هم)اینان غافلانند.'),
(3417, 30, 8, 'آیا و در خودهاشان اندیشه‌نکرده‌اند؟ (که) خدا آسمان‌ها و زمین و آنچه را که میان آن دو است، جز به تمامی حق و برای سررسیدی یاد شده [:معین] نیافریده است؟ و بی‌گمان بسیاری از مردمان به لقای پروردگارشان سخت کافرند.'),
(3418, 30, 9, 'آیا و در زمین نگردیده‌اند تا ببینند فرجام کسانی را که پیش از آنان بودند، چگونه بوده است‌؟ آنان بس نیرومندتر از اینان بودند و زمین را زیر و زبر کردند و بیش از آنچه آنها آبادش کردند آن را آباد ساختند و پیامبرانشان دلایل آشکار را برایشان آوردند. پس خدا (هرگز و هیچ گاه) بر آن نبوده است که بدیشان ستم کند، لیکن ایشان بر خودهاشان ستم می‌کرده‌اند.'),
(3419, 30, 10, 'سپس فرجام کسانی که بد را بدترین کردند، این بود که با آیات خدا (همان‌ها و خدا را) تکذیب کردند، در حالی‌که آنها را همواره به مسخره می‌گرفته‌اند.'),
(3420, 30, 11, 'خدا آفرینش را آغاز و سپس آن را تجدید می‌کند. سپس تنها سوی او بازگردانیده می‌شوید.'),
(3421, 30, 12, 'و روزی که ساعت [:قیامت] بر پا شود، مجرمان (از بد حادثه) بسی اندوهگین می‌گردند.'),
(3422, 30, 13, 'و برایشان از شریکانشان شفیعانی نبوده و خودشان (نیز) به شریکانشان (در عمق فطری و عقلانی از) کافران بوده‌اند.'),
(3423, 30, 14, 'و روزی که رستاخیز بر پا گردد، در آن هنگام (و هنگامه) پراکنده می‌شوند.'),
(3424, 30, 15, 'پس اما کسانی که ایمان آورده و کارهای شایسته کرده‌اند. در نتیجه آنان در باغستانی (فرح‌بخش) با پیامدی خوشایند خواهند بود.'),
(3425, 30, 16, 'و اما کسانی که کافر شدند و آیاتمان و دیدار آخرت را تکذیب کردند آنان در عذاب حاضرشدگانند.'),
(3426, 30, 17, 'پس خدا را تسبیح (گویید)، آن‌گاه که به عصر در می‌آیید و آن‌گاه که به بامداد در می‌شوید.'),
(3427, 30, 18, 'و هر ستایشی در آسمان‌ها و زمین برای اوست، و (نیز) در شامگاهان و وقتی که به نیمروز می‌رسید (ستایش ویژه‌ی اوست).'),
(3428, 30, 19, 'زنده را از مرده بیرون می‌آورد و مرده را از زنده بیرون می‌آورد و زمین را پس از مرگش زنده می‌سازد. و بدین گونه (از گورهایتان) بیرون آورده می‌شوید.'),
(3429, 30, 20, 'و از نشانه‌های اوست که شما را از خاک آفرید؛ سپس به ناگاه شما بشری هستید که (به هر سویی) پراکنده می‌شوید .'),
(3430, 30, 21, 'و از نشانه‌های اوست که از جنس خودتان همسرانی برایتان آفرید، تا بدان‌ها آرام گیرید و میانتان دوستی و رحمتی نهاد. به‌راستی در این (رحمت‌ها) برای مردمی که می‌اندیشند بی‌گمان نشانه‌هایی است.'),
(3431, 30, 22, 'و از نشانه‌های (قدرت و رحمت) او آفرینش آسمان‌ها و زمین و اختلاف زبان‌ها و رنگ‌های شماست. بی‌گمان در این (امر نیز) برای جهانیان همواره نشانه‌هایی است.'),
(3432, 30, 23, 'و از نشانه‌های او خواب شما در شب و روز و جستجوی شما از افزون‌بخشی اوست. در این (رحمت‌ها) برای مردمی که (با گوش دل سخنان حق را) می‌شنوند، نشانه‌هایی است.'),
(3433, 30, 24, 'و از نشانه‌های اوست (که) برق را برای شما بیم‌آور و امیدبخش می‌نمایاند و از آسمان به‌تدریج آبی فرو می‌فرستد. پس با آن، زمین را پس از مرگش زنده می‌گرداند. به‌راستی در این (نعمت‌ها) برای مردمی که خردورزی می‌کنند، به‌درستی نشانه‌هایی است.'),
(3434, 30, 25, 'و از نشانه‌های اوست که آسمان و زمین به فرمانش بر پایند. پس چون شما را از زمین یکباره فراخواند، به ناگاه از (درون) زمین برون می‌شوید.'),
(3435, 30, 26, 'و هر که در آسمان‌ها و زمین است تنها برای اوست‌؛ همه برای او خاشع (و فرمانبردار)ند.'),
(3436, 30, 27, 'و اوست کسی که آفرینش را آغاز می‌کند و باز آن را تجدید می‌نماید؛ و این تجدید بر او آسان‌تر (از آغاز) است و در آسمان‌ها و زمین والاترین نماینده از اوست و (هم) اوست عزیز حکیم.'),
(3437, 30, 28, '(خدا) برایتان از خودتان مَثَلی زده است: آیا در آنچه به شما روزی داده‌ایم هیچ شریکانی از بردگانتان دارید؟ پس (آیا) در آن با هم برابرید، حال آنکه همان‌گونه که شما از یکدیگر بیم دارید، از آنها (هم) هراسانید؟ این‌گونه آیات خود را برای گروهی که می‌اندیشند، جدا سازی می‌کنیم.'),
(3438, 30, 29, 'بلکه کسانی که ستم کردند، بدون هیچ‌گونه دانایی، هوس‌های خود را پیروی نمودند. پس آن کس را که خدا گمراه کرده، چه کسی هدایت می‌کند؟ و برای آنان هیچ یاورانی نخواهد بود.'),
(3439, 30, 30, 'پس چهره‌ی (انسانی) خود را به حالِ رویگردانی از هر باطلی برای دین به پادار؛ فطرت و سرشت خداساخته(ای) را که مردم را بر پایه‌ی آن (استوار) ساخت. هرگز تبدیلی برای (این) آفریده‌ی (ویژه‌ی) خدا نیست. این (همان) دین پر ارزشِ استوارِ پای‌برجاست ولی بیشتر مردمان نمی‌دانند.'),
(3440, 30, 31, '(به پا داشتن چهر‌ه‌ی فطرت) در حالی (است) که مکرر سویش باز می‌گردید، و از او پروا کنید و نماز را بر پا دارید و از مشرکان مباشید؛'),
(3441, 30, 32, 'از کسانی که دین خود را گسیخته کردند و پیروانی گوناگون (از ناکسان) بوده‌اند. هر حزبی [:گروه هماهنگی] بدانچه نزدشان هست دل‌خوشند.'),
(3442, 30, 33, 'و هنگامی که مردمان را زیانی در رسد، پروردگارشان را - در حالی که به درگاه او برگشت می‌کنند - می‌خوانند. سپس آن‌گاه که از خود رحمتی به آنان چشاند، به ناگاه دسته‌ای از ایشان با پروردگارشان شریک برمی‌گیرند،'),
(3443, 30, 34, 'تا به آنچه به آنها عطا کرده‌ایم کافر شوند. (بگو:) «پس (از نعمت‌های خدا) برخوردار شوید؛ پس در آینده‌ای دور خواهید دانست.»'),
(3444, 30, 35, 'یا بر آنان سلطه‌ای فرود آوردیم که به آنچه به آن (با خدا) شرک می‌ورزیده‌اند سخن می‌گوید.'),
(3445, 30, 36, 'و هنگامی که مردم را رحمتی بچشانیم، بدان شاد(کام) می‌گردند؛ و چون به (سزای) آنچه با دستاوردشان پیش فرستاده‌اند، صدمه‌ای بدیشان در رسد، به ناگاه نومید می‌شوند.'),
(3446, 30, 37, 'آیا و ندیدند که همواره خدا روزی را برای هر کس بخواهد فراخ می‌گرداند و (یا) تنگ می‌کند؟ بی‌گمان در این (جریان) برای گروهی که ایمان می‌آورند نشانه‌هاست.'),
(3447, 30, 38, 'پس حق خویشاوند نزدیکتر را و (حق) تنگدست و در راه مانده را بده. این (انفاق) برای کسانی که خواهان خشنودی خدایند خوب و خوب‌تر است و ایشان، (هم)اینان رستگارکنندگانند.'),
(3448, 30, 39, 'و آنچه از ربا دادید تا در اموال مردمان (سود و) افزایشی کند، نزد خدا فزونی ندارد و (اما) آنچه را از زکات - در حالی که خشنودی خدا را خواستارید - دادید، ایشان، (هم)اینان فزونی‌دهندگانند.'),
(3449, 30, 40, 'خدا که شما را آفرید، سپس به شما روزی داد، سپس شما را می‌میراند، سپس شما را زنده می‌گرداند؛ آیا در میان شریکانتان هرگز کسی هست که هیچ کاری از این (قبیل) کند؟ او منزّه و برتر است از آنچه (با وی) شریک می‌گردانند.'),
(3450, 30, 41, 'با دستاوردهای مردم، فساد در خشکی و دریا نمودار شد، تا (خدا) بعضی از آنچه را که کرده‌اند به آنان بچشاند، شاید باز گردند.'),
(3451, 30, 42, 'بگو: «در زمین بگردید. پس بنگرید فرجام کسانی که از پیش بودند چگونه بوده است. (آنان) بیشترشان مشرکان بوده‌اند.»'),
(3452, 30, 43, 'پس برای دینِ پرارزشِ به‌پاخاسته‌ی پایدار، چهرة (فطری و عقلانی)‌ات را به پادار؛ پیش از آنکه روزی فرا رسد که از جانب خدا هرگز برگشتی ندارد. در آن روز (مردم) از پیوندهای سختشان - و از یکدیگر- به سختی از هم گسسته می‌گردند.'),
(3453, 30, 44, 'هر کس کفر ورزد، کفرش به زیان خود اوست و کسانی که کار شایسته‌ای کنند، به سود خودهاشان (گهواره‌وار) آماده می‌سازند.'),
(3454, 30, 45, 'تا (خدا) کسانی را که ایمان آورده و کارهای شایسته(ی ایمانشان) کرده‌اند، از فضل خویش پاداش دهد. او بی‌گمان کافران را دوست نمی‌دارد.'),
(3455, 30, 46, 'و از نشانه‌های اوست که بادها را - در حالی‌که بشارت‌آورند - می‌فرستد و تا بخشی از رحمتش را به شما بچشاند و تا کشتی به فرمانش روان گردد و تا از فضل او (روزی) بجویید و شاید شما سپاسگزاری کنید.'),
(3456, 30, 47, 'و به‌راستی، پیش از تو فرستادگانی را سوی قومشان به‌درستی گسیل داشتیم، پس دلایل آشکار برایشان آوردند. پس از کسانی که مرتکب جرم شدند انتقام گرفتیم. و یاری کردن مؤمنان بر عهده‌ی ما حقی (ثابت) بوده است.'),
(3457, 30, 48, 'خداست که بادها را می‌فرستد، پس ابری بر می‌انگیزد، پس آن را در آسمان - هر گونه بخواهد - می‌گستراند و انبوهش می‌گرداند. پس می‌بینی دانه‌های باران از لابه‌لای آن بیرون می‌آید. پس چون آن را به هر کس از بندگانش که بخواهد، رسانید، به ناگاه آنان (از آن رحمت) نوید می‌طلبند؛'),
(3458, 30, 49, 'گر چه پیش از آنکه بر (سر و سامان)شان فرو ریزد، پیش از آن، ناامید و مأیوس بوده‌اند.'),
(3459, 30, 50, 'پس به پیامدهای رحمت خدا بنگر (که) چگونه زمین را پس از مرگش زنده می‌گرداند. این (خدای توانا) بی‌گمان مردگان را به‌راستی زنده می‌گرداند و او بر هر چیزی بسی تواناست.'),
(3460, 30, 51, 'و اگر بی‌چون بادی (آفت‌زا) بفرستیم، پس آن را بسی زرد ببینند (مأیوس شده)، پس از آن بی‌گمان کفران و کفر می‌ورزند.'),
(3461, 30, 52, 'پس در حقیقت، تو نمی‌توانی صدای خود را به گوش مردگان برسانی و نه (می‌توانی) صدایت را به گوش کران برسانی، هنگامی‌که پشت‌کنان روی بر می‌گردانند.'),
(3462, 30, 53, 'و تو کوران را از گمراهیشان راهنما نیستی. تو سخنت را نمی‌رسانی مگر به گوش کسانی که به آیاتمان ایمان می‌آورند، پس (در برابر حق) تسلیمند.'),
(3463, 30, 54, 'خدا کسی است که شما را از (حال) ناتوانایی(تان) آفرید. سپس از ناتوانی، توانی (به شما) بخشید. سپس بعد از توانایی، ناتوانی و پیری‌ای داد. (او) هر چه بخواهد می‌آفریند و هم اوست بس دانای بسیار توانا.'),
(3464, 30, 55, 'و روزی که ساعت [:قیامت] بر پا شود، مجرمان سوگند یاد می‌کنند (که) جز ساعتی (بیش) درنگ نکرده‌اند. (در دنیا هم) این گونه به دروغ کشانیده می‌شده‌اند.'),
(3465, 30, 56, 'و کسانی که دانش و ایمان داده شده‌اند، گفتند: «به‌راستی شما (به موجب آنچه) در کتاب خدا(ست) تا روز رستاخیز همچنان مانده‌اید. پس این، روز رستاخیز است ولی شما نمی‌دانسته‌اید.»'),
(3466, 30, 57, 'پس در چنان روزی (دیگر) پوزش آنان که ستم کرده‌اند سودی نمی‌بخشد و (حتی) سرزنش (هم) نمی‌شوند (که کارشان این‌گونه زار است).'),
(3467, 30, 58, 'و بی‌گمان در این قرآن برای مردمان به‌راستی از هر گونه مَثَلی (بحق) آوردیم و اگر برایشان نشانه‌ای بیاوری، آنان که کفر ورزیده‌اند بی‌چون و بی‌گمان خواهند گفت: «شما جز باطل‌کننده(ی حق) نیستید.»'),
(3468, 30, 59, 'بدین‌سان، خدا بر دل‌های کسانی که نادانی می‌کنند مُهر می‌نهد.'),
(3469, 30, 60, 'پس صبر کن (که) به‌راستی وعده‌ی خدا حق است و (زنهار) هرگز کسانی که یقین نمی‌آورند، تو را خفیف و سبک برنگیرند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الم.'),
(3471, 31, 2, 'آنها آیات کتاب لبریز از حکمت است.'),
(3472, 31, 3, 'حال آنکه برای نیکوکاران رهنمودی (بزرگ) و رحمتی (سترگ) است.'),
(3473, 31, 4, 'کسانی که نماز بر پا می‌دارند و زکات می‌دهند و اینان، (هم)ایشان به آخرت یقین می‌آورند.'),
(3474, 31, 5, 'اینانند که از جانبِ پروردگارشان بر هدایتی (ویژه) استوارند و (هم) ایشانند که رستگارکنندگانند.'),
(3475, 31, 6, 'و برخی از مردمان کسانی‌اند که گفتار منحرف‌کننده را خریداری می‌کنند، تا (مردم را) بی(هیچ) دانایی از راه خدا گمراه کنند و آن را به ریشخند گیرند؛ ایشان، برایشان عذابی خوارکننده (در پیش) است.'),
(3476, 31, 7, 'و هنگامی که آیات ما بر او خوانده شود، با نخوت روی برمی‌گرداند، چونان که آنها را نشنیده، (یا) گویی در (هر) دو گوشش سنگینی‌ای است‌؛ پس او را به عذابی پردرد نوید ده.'),
(3477, 31, 8, 'بی‌گمان، کسانی که ایمان آورده و کارهای شایسته(ی ایمان) کردند، برایشان باغ‌های با درختان سردرهمِ پرنعمت است.'),
(3478, 31, 9, 'حال آنکه در آنها جاودانند. وعده‌ی حقانی خدای را (منظور بدار) و او عزیز و حکیم است.'),
(3479, 31, 10, 'آسمان را بی‌هیچ ستونی که آن را ببینید آفرید و در زمین کوه‌هایی استوار همچون میخ‌ها بیفکند تا مبادا زمین شما را بجنباند (و پرتاب کند) و در آن از هرگونه جنبنده‌ای پراکند. و از آسمان آبی فرو فرستادیم، پس از هر جفت گرامی (باکرامت) در آن رویانیدیم.'),
(3480, 31, 11, 'این، آفریده‌ی خداست. پس به من نشان دهید کسانی که غیر از اویند چه آفریده‌اند؟ (هیچ!) بلکه ستمگران در گمراهی‌ای آشکارگرند.'),
(3481, 31, 12, 'به‌راستی و درستی لقمان را حکمت دادیم که : «برای خدا شکر کن و هر کس شکر گزارد، تنها به سود خود شکر می‌گزارد؛ و هر کس کافر شود یا کفران کند، پس، خدا همانا بی‌نیازی ستوده است.»'),
(3482, 31, 13, 'و چون لقمان به پسرش - در حالی که وی او را اندرز می‌داد - گفت: «ای پسرکم! به خدا شرک میاور، به‌راستی شرک همواره ستمی بزرگ است.»'),
(3483, 31, 14, 'و انسان را درباره‌ی پدر و مادرش سفارش کردیم‌؛ مادرش به او باردار شد، در حال سستی بر سستی و از شیر باز گرفتنش در دو سال است. (آری، به او سفارش کردیم) که برای من و پدر و مادرت سپاس‌گزار. بازگشت (همه) تنها، سوی من است.'),
(3484, 31, 15, 'و اگر همی بکوشند، تا درباره‌ی چیزی که تو را بدان دانشی (در شرکت الوهیت) نیست به من شرک ورزی، از آنان فرمان مَبَر (و) در دنیا به خوبی با آنان مصاحبتی شایسته نمای و راه کسی را پیروی کن که سوی من پیاپی بازگشته‌؛ سپس بازگشت شما تنها سوی من است، پس از (حقیقت) آنچه انجام می‌داده‌اید شما را خبری بزرگ خواهم داد.'),
(3485, 31, 16, '«ای پسرکم! اگر - بی‌چون - طاعتت در شرک هم‌وزن دانه‌ی خردلی، پس درون تخته سنگی یا در (دل) آسمان‌ها یا در ژرفای زمین باشد، خدا آن را (برون) می‌آورد. همواره خدا بسی دقیق و آگاه است.»'),
(3486, 31, 17, '«پسرکم! نماز را بر پا بدار و به کار پسندیده وادار و از کار ناپسند بازدار و بر آسیبی که (در راه امر و نهی) بر تو وارد آمده شکیبایی کن. به‌راستی این (حاکی) از عزم و اراده‌ی تو در کارهاست.»'),
(3487, 31, 18, '«و برای مردمان چهره‌ات را متکبرانه برمگردان [:گردن‌فرازی مکن] و در زمین خرامان و خودخواهان راه مرو. خدا بی‌چون هیچ خودپسندِ لافزنی را دوست نمی‌دارد.»'),
(3488, 31, 19, '«در (روش و) راهت میانه‌رو باش و از صدایت بِکاه؛ به‌راستی ناخوشایندترین صداها بی‌گمان صدای خران است.»'),
(3489, 31, 20, 'آیا ندیدید [:ندانستید] که خدا به‌‌راستی آنچه را که در آسمان‌ها و آنچه را که در زمین است، برایتان مسخّر کرده و نعمت‌های آشکار و نهانش را بر (سر و سامان)تان تمام و گشاده و ریزان نموده است‌؟ و برخی از مردمان درباره‌ی خدا بدون هیچ دانش و بینشی، و رهنمود و کتابی روشنگر به مجادله و ستیز برمی‌خیزند.'),
(3490, 31, 21, 'و هنگامی که به آنان گفته شود: «آنچه را که خدا نازل کرده پیروی کنید.» گویند: « (نه!) بلکه آنچه که پدرانمان را بر آن یافته‌ایم پیروی می‌کنیم‌.» آیا و هر چند شیطان آنان را سوی عذابِ شعله‌ور و سوزان فراخواند (باز هم از آنان پیروی می‌کنند)؟'),
(3491, 31, 22, 'و هر کس (چهره‌ی خود را) - در حالی‌که نیکوکار باشد- برای خدا تسلیم کند، همواره با پایدارترین چنگ‌آویز (خود را) نگهبانی کرده. و فرجام کارها تنها سوی خداست.'),
(3492, 31, 23, 'و هر کس کفر ورزد، نباید کفرش تو را غمگین سازد. بازگشتشان تنها سوی ماست. پس به آنچه کرده‌اند به‌خوبی آگاهی مهمشان خواهیم داد. به‌راستی خدا به ذات (و درون) سینه‌ها بسی داناست.'),
(3493, 31, 24, '(ما) آنان را (در دنیا) اندکی برخوردار می‌سازیم، سپس در عذابی پرفشار ناگزیر و ناگریز گرفتارشان می‌گردانیم.'),
(3494, 31, 25, 'و به‌راستی اگر از آنها بپرسی: «چه کسی آسمان‌ها و زمین را آفریده است‌؟» همانا بی‌چون خواهند گفت: «خدا.» بگو: « کل ستایش‌ها از آنِ خداست‌.» ولی بیشترشان نمی‌دانند.'),
(3495, 31, 26, 'آنچه در آسمان‌ها و زمین است، تنها از آنِ خداست. بی‌شک، خدا، (هم)او بی‌نیاز ستوده است.'),
(3496, 31, 27, 'و اگر آنچه درخت در زمین است به‌راستی قلم‌هایی باشد و دریا را هفت دریای دیگر پس از آن به یاری و مددکاری کشد، (که کلمات خدا را با آنها بنویسند) کلمات خدا هرگز پایان نپذیرد. همواره خدا عزیز و حکیم است.'),
(3497, 31, 28, 'آفرینش و برانگیختنتان (در نزد ما) جز مانند (آفرینش) یک شخص نیست. به‌راستی خدا بس شنوایی بسیار بیناست.'),
(3498, 31, 29, 'آیا ندیدی که خدا همواره شب را در روز و روز را در شب فرو می‌برد و خورشید و ماه را مسخر کرده است (که) هر یک تا وقت معلومی روانند و اینکه خدا به‌راستی به آنچه می‌کنید آگاه است‌؟'),
(3499, 31, 30, 'این (همه قدرت‌نمایی‌ها) بدین جهت است، که خدا خود کلّ حقّ است و بی‌چون غیر از او هر چه را (به خدایی) می‌خوانند و می‌خواهند باطل است. و خدا بی‌گمان همان بلندمرتبه‌ی بزرگ است.'),
(3500, 31, 31, 'آیا ندیدی که کشتی‌ها به‌راستی به نعمت خدا در دریا روان می‌گردند، تا (خدا) برخی از نشانه‌های خود را به شما بنمایاند؟ همواره در این (قدرت‌نمایی) برای هر شکیبای سپاسگزاری، بی‌چون نشانه‌هایی است.'),
(3501, 31, 32, 'هنگامی که موجی کوه‌آسا همچون ابرهای سیاه آنان را فراگیرد، خدا را بخوانند، در حالی‌که دین (و طاعت خود) را برای او خالص گردانند. پس چون سوی بیابان نجاتشان داد و به خشکی(شان) رساند، برخی از آنان میانه‌رو هستند. و نشانه‌های ما را جز هر خائن ناسپاسی انکار (و انگار) نمی‌کند.'),
(3502, 31, 33, 'هان ای مردمان! پروردگارتان را پروا بدارید و بترسید از روزی که هیچ پدری (هرگز) به کار فرزندش نیاید و هیچ فرزندی (نیز) برای پدرش به کاری نیاید. به‌راستی وعده‌ی خدا حقّ است. (زنهار) پس این زندگی دنیا، هرگز شما را نفریبد و شیطان شما را هرگز گول نزند.'),
(3503, 31, 34, 'همواره خدا(ست که) علم (به) قیامت تنها نزد اوست، و باران را فرو می‌فرستد و آنچه را که در رحم‌هاست می‌داند و کسی نمی‌داند فردا چه به دست می‌آورد و کسی نمی‌داند در کدامین سرزمین می‌میرد. همواره خدا بسیار دانای آگاه است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الم‌'),
(3505, 32, 2, 'فرو فرستادن تدریجی این کتاب - که هیچ شکی مستند در آن نیست - تنها از طرف پروردگار جهانیان است.'),
(3506, 32, 3, 'یا می‌گویند: «آن را (به خدا) افترا بسته است‌؟» (چنان نیست.) بلکه آن تمامی حقّ است از جانب پروردگارت، تا مردمی را که پیش از تو هیچ هشداردهنده‌ای برایشان نیامده است، هشدار دهی؛ شاید راه یابند.'),
(3507, 32, 4, 'خدایی که آسمان‌ها و زمین و آنچه را که میان آن دو است، در شش هنگام آفرید. سپس بر عرش (آفرینش) چیره شد. حال آنکه برای شما غیر از او نه هیچ سرپرست و نه هیچ شفاعتگری نیست‌. آیا پس (باز هم) متذکر نمی‌شوید؟'),
(3508, 32, 5, 'همه چیز را از آسمان تا زمین (به اراده‌ی خود) اداره می‌کند؛ سپس در روزی که مقدارش - آن چنان که شما (آدمیان) برمی‌شمارید - هزار سال بوده است، سوی او بالا می‌رود.'),
(3509, 32, 6, 'اوست (همان) دانای نهان و آشکار، (آن) شکوهمند رحمتگر بر ویژگان.'),
(3510, 32, 7, 'کسی که همه چیز را نیکو آفرید و آفرینش انسان را از گِل آغاز کرد‌؛'),
(3511, 32, 8, 'سپس (تداوم) نسل او را از خلاصه و برگزیده‌ای از آبی پست نهاد؛'),
(3512, 32, 9, 'سپس او را سامان و سازمان داد و از روح ویژه‌‌اش (از آفریدگانش) در او دمید. و برای شما گوش‌ها و چشم‌ها و دل‌های فروزان قرار داد. چه اندک سپاس می‌گزارید!'),
(3513, 32, 10, 'و گفتند: «آیا وقتی ما در (دل) زمین گم شدیم، آیا (باز هم همین) ما به‌راستی بی‌چون در آفرینش نوینی هستیم‌؟» بلکه آنان به لقای پروردگارشان کافرانند.'),
(3514, 32, 11, 'بگو: «فرشته‌ی مرگ که بر شما گمارده شده، تمامی وجود شما (تن و جانتان) را به تمام و کمال برمی‌گیرد، سپس سوی پروردگارتان بازگشت می‌کنید.»'),
(3515, 32, 12, 'و ای کاش ببینی چون مجرمان، سرافکندگان نزد پروردگارشانند (گویند): «پروردگارمان! دیدیم و شنیدیم‌؛ پس ما را بازگردان تا کار شایسته‌ای کنیم. ما (اکنون) همواره (از) یقین‌آورندگانیم.‌»'),
(3516, 32, 13, 'و اگر می‌خواستیم، همانا به هر کسی (ناگزیر) هدایتش را می‌دادیم، لیکن این سخن از من محقّق گردیده که: «هر آینه به‌راستی جهنّم را از همه‌ی جنّیان و آدمیان خواهم آکند.»'),
(3517, 32, 14, '«پس به (سزای) آنکه دیدار این روزتان را نادیده گرفتید (عذاب را) بچشید؛ به‌راستی ما (نیز) فراموشتان کردیم و به (سزای) آنچه انجام می‌داده‌اید عذاب جاودان را بچشید.»'),
(3518, 32, 15, 'تنها کسانی به آیاتمان می‌گروند که اگر هنگامی آنها را به ایشان یادآوری کنند، سجده‌کنان به روی درمی‌افتند و با ستایش پروردگارشان، تسبیح می‌گویند، حال آنکه ایشان کبرجو نیستند.'),
(3519, 32, 16, 'پهلوهایشان از خوابگاه‌ها(شان) جدا می‌گردد حال آنکه پروردگارشان را از روی بیم و طمع می‌خوانند و از آنچه روزیشان دادیم انفاق می‌کنند.'),
(3520, 32, 17, 'پس هیچ کسی نمی‌داند، چه چیزی از روشنی‌بخش دیدگان - به (پاداش) آنچه انجام می‌دادند- برایشان پنهان شده است.'),
(3521, 32, 18, 'آیا پس کسی که مؤمن بوده، چون کسی است که نافرمان بوده‌؟ (نه! اینان) یکسان نیستند.'),
(3522, 32, 19, 'امّا کسانی که ایمان آورده و کارهای شایسته(ی ایمان) کرده‌اند، پس برایشان - به آنچه می‌کرده‌اند - باغ‌های با درختانِ سردرهم، جایگاه‌هایی است مهمان‌سرا.'),
(3523, 32, 20, 'و امّا کسانی که نافرمانی کردند، پس پناهگاهشان آتش است. هربار که بخواهند از آن بیرون بیایند، در آن بازگردانیده می‌شوند و به آنان گفته می‌شود: «عذابِ آتش را -که دروغش می‌پنداشتید- بچشید.»'),
(3524, 32, 21, 'و بی‌‌گمان (و) بی‌چون آنان را از عذاب کمتر (که) ناچیزتر از عذاب بزرگ‌تر است می‌چشانیم، شاید آنها (به خدا) بازگردند.'),
(3525, 32, 22, 'و کیست بیدادگرتر از آن کس که به آیات پروردگارش یادآوری شده، پس آن‌گاه از آن روی بگرداند؟ ما بی‌گمان از مجرمان انتقام‌کشنده‌ایم.'),
(3526, 32, 23, 'و به‌راستی ما به موسی کتاب (تورات) را به‌درستی دادیم: «پس، از لقای با خدا تردید مکن.» و آن کتاب را برای فرزندان اسرائیل رهنمودی قرار دادیم.'),
(3527, 32, 24, 'و هنگامی که شکیبایی کردند و به آیات ما یقین داشتند، برخی از آنان را پیشوایانی قرار دادیم که به فرمان ما (مردم را) هدایت کنند.'),
(3528, 32, 25, 'به‌راستی، پروردگارت، (هم)او روز قیامت در آنچه با یکدیگر درباره‌ی آن اختلاف می‌کرده‌اند، میانشان جداسازی خواهد کرد.'),
(3529, 32, 26, 'آیا و برای آنان رهنمون نشده است (که) چه بسیار نسل‌ها را پیش از آنها نابود گردانیدیم، حال آنکه اینان در سراهای آنان راه می‌روند؟ بی‌گمان در این (امر) نشانه‌هایی است. پس مگر نمی‌شنوند؟'),
(3530, 32, 27, 'آیا و ننگریسته‌اند که ما همواره باران را سوی زمین بایر می‌رانیم، پس به آن کِشته‌ای را برون می‌آوریم، که دام‌هایشان و خودهاشان از آن می‌خورند؟ پس مگر نمی‌نگرند؟'),
(3531, 32, 28, 'و می‌گویند: «اگر (از) راستان بوده‌اید، این گشایش (شما) چه وقت است‌؟»'),
(3532, 32, 29, 'بگو: «روز گشایش، ایمانِ کسانی که کافر شده‌اند سودی نمی‌بخشد و آنان مهلت (هم) نمی‌یابند.»'),
(3533, 32, 30, 'پس، از ایشان روی برتاب و منتظر باش. بی‌گمان آنان (نیز) منتظرانند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'هان ای پیامبر برجسته! از خدا پروا بدار و کافران و منافقان را فرمان مدار. خدا بس دانای حکیم بوده است.'),
(3535, 33, 2, 'و آنچه را که از پروردگارت سویت وحی می‌شود، پیروی کن. خدا همواره به آنچه می‌کنید بسی آگاه بوده است.'),
(3536, 33, 3, 'و بر خدا توکّل کن. همین بس که وکیل (همان) خداست.'),
(3537, 33, 4, 'خدا برای هیچ مردی در درونش دو دل ننهاده است و آن همسرانتان را که مورد «ظهار» قرار می‌دهید، مادرانتان نگردانیده و پسرخواندگانتان را پسرانتان قرار نداده. این سخنانتان است با دهان‌هایتان (و نه از روی واقعیت)؛ و خدا تمامی حق را می‌گوید و او به راه راست رهبری می‌کند.'),
(3538, 33, 5, 'آنان را برای پدرانشان بخوانید (که) آن نزد خدا عادلانه‌‌تر است. پس اگر (هم) پدرانشان را نمی‌شناسید، برادرانتان در دین و پیروان و دوستان شمایند. و گناهی بر شما نیست در آنچه خطا کردید ولی آنچه دل‌هاتان (بدان) تعمّد کرده (گناه است). و خدا همواره پوشنده‌ی رحمتگر بر ویژگان بوده است.'),
(3539, 33, 6, 'پیامبر بزرگ به مؤمنان از خودهاشان برتر(و نزدیکتر) است و همسرانش مادران ایشانند و خویشاوندان در کتاب خدا - بعضیشان (نسبت) به بعضی دیگر - از مؤمنان و مهاجران برترند؛ مگر آنکه بخواهید برای دوستان (باایمان)تان کاری [:وصیّت یا احسانی] به شایستگی کنید. و این در کتاب (وحیانی) نگاشته بوده است.'),
(3540, 33, 7, 'و چون از همه‌ی پیامبران برجسته پیمانشان را گرفتیم و (به ویژه) از تو و از نوح و ابراهیم و موسی و عیسی پسر مریم، و از همه‌ی آنان پیمانی سخت گرفتیم؛'),
(3541, 33, 8, 'تا راستان را از راستیشان باز پرسد. و برای کافران عذابی دردناک آماده کرده است.'),
(3542, 33, 9, 'هان ای کسانی که ایمان آوردید! نعمت خدا را بر خودهاتان به یاد آورید، چون لشکریانی سویتان درآمدند. پس بر (سر و سامان)شان (تند)بادی فرستادیم و (نیز) لشکریانی (هم) که آنها را ندیدید. و خدا به آنچه می‌کنید بینا بوده است.'),
(3543, 33, 10, 'چون از بالای (سر)تان و از زیرترین (جایگاه)تان آمدند و چون چشم‌ها(یتان) خیره شد و دل‌ها(تان) به گلوگاه‌ها(تان) رسید، و به خدا آن گمان‌ها(ی نابجا) را می‌برید.'),
(3544, 33, 11, 'اینجا (بود که) مؤمنان در آزمونی بس دشوار گرفتار شدند و به سختی متزلزل گشتند.'),
(3545, 33, 12, 'و چون منافقان و کسانی که در دل‌هایشان بیماری است می‌گویند: «خدا و فرستاده‌اش وعده‌ای جز فریب به ما ندادند.»'),
(3546, 33, 13, 'و چون گروهی از آنان گفتند: «ای مردم مدینه! دیگر شما را (اینجا) هیچ جایگاهی (و درنگی) نیست. پس برگردید.» و گروهی از آنان از پیامبر برجسته اجازه می‌خواهند (و) می‌گویند: «خانه‌هایمان بی‌حفاظ است‌.» حال آنکه خانه‌هایشان بی‌حفاظ نیست (بلکه خودهاشان بی‌حفاظند). آنان جز گریزی (از جهاد) چیزی نمی‌خواهند.'),
(3547, 33, 14, 'و اگر از اطراف اصلی مدینه (بر سروسامانشان) می‌گشتند، سپس آنان به آشوبگریِ دشوارِ آتشبار [:جنگ] خوانده می‌شدند، همواره سویش می‌آمدند و جز اندکی در مدینه درنگ نمی‌کردند؛'),
(3548, 33, 15, 'و همواره از پیش با خدا همی پیمان بسته بودند (که) پشت‌ها(شان را به دشمنانشان) نکنند. و پیمان خدا مورد بازخواست بوده است.'),
(3549, 33, 16, 'بگو: «اگر از مرگ یا کشته شدن فرار کنید هرگز فرار، شما را سودی نبخشد، و در این هنگام (و هنگامه) جز اندکی برخوردار نخواهید شد.»'),
(3550, 33, 17, 'بگو: «کیست آن کس (که) از (اراده‌ی) خدا شما را نگهبانی کند، اگر او بخواهد برای شما بدی بیاورد، یا بخواهد شما را رحمتی کند؟» و غیر از خدا برای خود نه سرپرست و نه یاوری نخواهند یافت.'),
(3551, 33, 18, 'خدا واپس اندازندگان شما و آن کسانی را که به برادرانشان می‌گفتند: «نزد ما بیایید» و جز اندکی روی به جنگ نمی‌آورند، می‌داند [:می‌شناسد] (و) نشانه می‌نهد.'),
(3552, 33, 19, 'علیه شما بسی بخیلند. پس چون هنگام ترس فرا رسد، آنان را می‌بینی که مانند کسی که به سبب مرگ بی‌هوش می‌شود - درحالی که چشمانشان در حدقه می‌چرخد- سوی تو (با نگرانی) می‌نگرند؛ پس هنگامی‌که ترسشان برطرف شود، شما را با زبان‌هایی تند نیش می‌زنند. حال آنکه بر خیر[:مال و منال] حریصند. آنان ایمان نیاورده‌اند. پس خدا اعمالشان را تباه گردانیده و این کار بر خدا آسان بوده‌است.'),
(3553, 33, 20, 'اینان (چنان) می‌پندارند که حزب‌های دشمن نرفته‌اند و اگر (این) احزاب برگردند دوست دارند (که) کاش میان اعراب - بادیه‌نشینان- بودند. حال آنکه از اخبار مهم (مربوط به) شما جویا می‌شوند، و اگر در میان شما بودند، جز اندکی جنگ نمی‌کردند.'),
(3554, 33, 21, 'به‌راستی همواره برای شما در (اقتدای به) رسول خدا سرمشقی نیکو بوده‌است؛ برای کسی که به خدا و روز بازپسین امید می‌داشته و خدا را زیاد یاد کرده است.'),
(3555, 33, 22, 'و هنگامی‌که مؤمنان حزب‌ها(ی دشمن) را دیدند، گفتند: «این همان است که خدا و فرستاده‌اش به ما وعده دادند و خدا و فرستاده‌اش راست گفتند.» و جز بر ایمان و تسلیمشان نیفزود.'),
(3556, 33, 23, 'از مؤمنان رادمردانی هستند (که) آنچه را با خدا بر آن پیمان بسته‌اند راست آمدند. پس برخی از آنان به نیازشان رسیدند و برخی از آنان در (همین) انتظارند و هرگز (پیمان خود را) هیچ‌گونه تبدیلی نکردند.'),
(3557, 33, 24, 'تا خدا راستان را به‌راستیشان پاداش دهد. و منافقان را - اگر بخواهد- عذاب(شان) کند یا بر ایشان برگشت نماید. همواره خدا پوشنده‌ی بسی رحمتگر بر ویژگان بوده است.'),
(3558, 33, 25, 'و خدا آنان را که کفر ورزیدند، با غیظ (و حسرتشان) برگرداند؛ حال آنکه به هیچ خیری نرسیدند. و خدا (خطر) جنگ را از مؤمنان کفایت کرد و خدا همواره نیرومند عزیز بوده‌است.'),
(3559, 33, 26, 'و کسانی از اهل کتاب را که مشرکان پشتیبانی (و با آنان همکاری) کرده بودند، از دژهایشان به زیر آورد و در دل‌هایشان هراس افکند؛ حال آنکه گروهی را می‌کُشید و گروهی را اسیر می‌کنید.'),
(3560, 33, 27, 'و زمینشان و خانه‌هاشان و اموالشان و سرزمینی را که در آن پا ننهاده بودید، به شما میراث داد. و خدا بر هر چیزی بسیار توانا بوده است.'),
(3561, 33, 28, 'هان ای پیامبر برجسته! به همسرانت بگو: «اگر خواهان زندگی دنیا و زینت آن بوده‌اید، بیایید تا مَهرهاتان را - با فزونی بر آن - به شما ببخشایم و شما را زیبنده (خوش و خرّم) رها و آزاد کنم.'),
(3562, 33, 29, 'و اگر خواستار خدا و فرستاده‌ی وی و سرای آخرت بوده‌اید، بی‌گمان خدا برای نیکوکارانتان پاداش بزرگی آماده کرده است.'),
(3563, 33, 30, 'ای زنان پیامبر برجسته! هر کس از شما مبادرت به کار زشتِ نمایانگری کند، عذابش دو چندان خواهد شد. و این بر خدا آسان بوده‌است.'),
(3564, 33, 31, 'و هر کس از شما برای خدا و رسولش، (تداوم در) خشوع داشته باشد و عمل شایسته(ی خشوع) انجام دهد، اجرش را دو چندان می‌دهیم و برایش روزیِ فراوانی آماده می‌سازیم.'),
(3565, 33, 32, 'ای زنان پیامبر برجسته! اگر سَرِ پروا دارید، شما مانند هیچ یک از زنان (دیگر) نیستید. پس به عشوه و ناز سخن مگویید، تا آنکه در دلش بیماری (شهوت) است طمع ورزد؛ و (با نامحرمان) گفتاری شناخته شده (در جو ایمان) بگویید.'),
(3566, 33, 33, 'و در خانه‌هایتان با وقار قرار گیرید و مانند روزگاران جاهلیتِ نخستین، زینت‌های خود را آشکارا مسازید و نماز بر پا دارید و زکات بدهید و خدا و فرستاده‌اش را فرمان برید. خدا فقط می‌خواهد هرگونه آلودگی را از شما (مردان) خاندان (رسالت) بزداید و شما را به گونه‌ای ویژه پاکیزه گرداند.'),
(3567, 33, 34, 'و آنچه را که از آیات خدا و (سخنان) حکمت (آمیز)ش در خانه‌های شما خوانده می‌شود، یاد کنید. همواره خدا دقیق بسیار آگاه بوده است.'),
(3568, 33, 35, 'به‌راستی مردان و زنان مسلمان و مردان و زنان باایمان و مردان و زنان خاضع و مردان و زنان راستین و مردان و زنان شکیبا و مردان و زنان خاشع و مردان و زنان صدقه‌دهنده(ی مال و جانشان) و مردان و زنان روزه‌دار و مردان و زنان پاکدامن و مردان و زنانی که خدا را فراوان یاد می‌کنند، خدا برای (همه‌ی) آنان پوشش و پاداشی بزرگ فراهم ساخته است.'),
(3569, 33, 36, 'و برای هیچ مرد مؤمن و نه زن مؤمنی (شایسته) نبوده است، هنگامی که خدا و رسولش امری حتمی (به ایشان) فرماید، برایشان از کار (پنهان و آشکار)شان اختیاری باشد؛ و هر کس خدا و فرستاده‌اش را نافرمانی کند، همواره دچار گمراهیِ آشکارگری گردیده است.'),
(3570, 33, 37, 'و چون به کسی که خدا بر (سروسامان) او نعمتی داده و تو (نیز) به او نعمتی دادی، می‌گویی: «همسرت را نزدت نگهدار و از خدا پروا بدار.» و آنچه را که خدا آشکارکننده‌ی آن است در دل خود نهان می‌کنی، و از مردم پروا می‌داری، با آنکه خدا سزاوارتر بود که از او پروا بداری. پس چون زید از آن زن کام برگرفت (و عده‌‌ی همسرش سر رسید) تو را به همسری او درآوردیم، تا بر مؤمنان در ازدواجشان با زنان پسرخواندگانشان - هرگاه از آنان کام برگرفتند و ترک گفتند- هرگز تنگنایی نباشد و فرمان خدا انجام شده بوده است.'),
(3571, 33, 38, 'بر پیامبر برجسته - در آنچه خدا برایش حتمی کرده است- هرگز (گیرودار) طاقت‌فرسایی نبوده است. سنّت خدا را که در پیشینیان از دیرباز بوده (نگه‌دار). و امر خدا همواره به اندازه‌ی مقرّر و در خور توان (مکلفان) بوده است.'),
(3572, 33, 39, '(حاملان سنّت ربّانی) کسانی هستند، که پیام‌های خدا را به خوبی ابلاغ می‌کنند و از سر تعظیم از او هراس دارند و از احدی جز خدا نمی‌هراسند. و خدا برای حسابرسی سریع و دقیق کافی است.'),
(3573, 33, 40, 'محمّد (هرگز) پدر هیچ یک از مردان شما نبوده ولی فرستاده‌ی خدا و خاتم پیامبران بوده است. و خدا همواره بر هر چیزی بسی دانا بوده است.'),
(3574, 33, 41, 'هان ای کسانی که ایمان آوردید! خدا را یاد کنید، یادی بسیار.'),
(3575, 33, 42, 'و صبح و شام او را به پاکی تنزیه کنید.'),
(3576, 33, 43, 'اوست کسی که بر شما رحمت می‌فرستد و (نیز) فرشتگانش (برای شما از خدا رحمت می‌طلبند) تا شما را از تاریکی‌ها سوی روشنایی برون آورد و (نسبت) به مؤمنان همواره رحمتگر بوده است.'),
(3577, 33, 44, 'روزی که دیدارش کنند درودشان (نسبت به یکدیگر) سلام است و برای آنان پاداشی گرامی و نیکو آماده کرده است.'),
(3578, 33, 45, 'ای پیامبر برجسته! ما همواره تو را (به عنوان) گواه و بشارتگر و هشداردهنده‌ای فرستادیم؛'),
(3579, 33, 46, 'و دعوت‌کننده‌ای سوی خدا- به اجازه‌ی او- و چراغی نور‌بخش.'),
(3580, 33, 47, 'و مؤمنان را مژده ده، به اینکه بی‌گمان برایشان از جانب خدا بخشایشی بزرگ است.'),
(3581, 33, 48, 'و کافران و منافقان را فرمان مَبَر و از آزارشان بگذر و بر خدا اعتماد کن. و (تنها) کارسازی خدا کافی است.'),
(3582, 33, 49, 'هان ای کسانی که ایمان آوردید! هنگامی که زنان مؤمن را به نکاح خود در آوردید، سپس - پیش از آنکه با آنان همبستری کنید - طلاقشان دادید، دیگر بر عهده‌ی آنان برایتان عدّه‌ای - که آن را به شمار آرید - نیست‌؛ پس مَهرشان را افزون بدهید و (خوش و خرّم) به زیبایی و زیبندگی آنان را رها کنید.'),
(3583, 33, 50, 'هان ای پیامبر برجسته! ما همواره برای تو (آن) همسرانت را که مَهرشان را داده‌ای حلال کردیم و کنیزانی را که خدا از غنیمت بدون جنگ بر تو به شایستگی برگشت داده و دختران عمویت را، و دختران عمّه‌هایت را و دختران دایی‌ات را و دختران خاله‌هایت را، که (همگی) با تو مهاجرت کرده‌اند، و زن مؤمنی را که خود را (داوطلبانه) به پیامبر ببخشد، در صورتی که پیامبر بخواهد او را به همسری برگزیند، بدین‌حال ویژ‌ه‌ی توست و نه دیگر مؤمنان. ما نیک می‌دانیم که در مورد زنان و کنیزانشان چه بر آنان - به گونه‌ای حتمی - مقرّر کرده‌ایم، تا برای تو تنگنایی پیش نیاید. و خدا بی‌گمان بسی پوشنده‌ی رحمتگر بر ویژگان بوده است.'),
(3584, 33, 51, 'هر کدام از زنانت را که می‌خواهی (از خود) دور می‌کنی، و هر کدام را که می‌خواهی به پیش خود پناه و جای می‌دهی. بر تو گناهی نیست که هر کدام را که ترک کرده‌ای (دوباره) طلب کنی. این نزدیکتر است برای آنکه چشمانشان روشن گردد و غمگین نشوند و همگیشان به آنچه به همه‌ی آنان داده‌ای خشنود گردند. و آنچه در دل‌های شماست خدا می‌داند و خدا (همواره) بس دانای بسیار بردبار بوده‌است.'),
(3585, 33, 52, 'از این پس ، زناشویی با زنانی دیگر و نیز اینکه به جای آنان، زنان دیگری را جایگزین کنی، بر تو حلال نیست - هر چند زیبایی آنها تو را به شگفتی آورد- به استثنای زنانی (دیگر) که دستاوردگان [:کنیزان] تواند. و خدا (همواره) بر هر چیزی مراقب بوده‌است.'),
(3586, 33, 53, 'هان ای کسانی که ایمان آوردید! داخل خانه‌های پیامبر نشوید -مگر آنکه برای (خوردن) طعامی به شما اجازه داده شود- بی‌آنکه در انتظار آماده‌شدن آن (اجازه یا طعام) باشید؛ ولی هنگامی که دعوت شدید داخل شوید، تا چون غذا خوردید پراکنده گردید و (نیز) در حال انس به گفتاری (در حضور حضرتش) نباشید. این (رفتار شما) پیامبر را می‌رنجانیده؛ پس از شما شرم می‌داشته است و خدا از حق شرم نمی‌دارد. و چون از زنانش چیزی خواستید، از پشت پرده‌ای از آنان بخواهید؛ این برای دل‌های شما و دل‌های آنان پاکیزه‌تر است و برای شما هرگز (حقّی) نبوده که رسول خدا را بیازارید و هرگز نباید پس از (طلاقشان یا مرگ) وی با همسرانش ازدواج کنید(که) همواره این (کار) نزد خدا (گناهی) بزرگ بوده‌است.'),
(3587, 33, 54, 'اگر چیزی را فاش کنید، یا آن را پنهان بدارید، (بدانید که) همواره خدا به هر چیزی بسی دانا بوده است.'),
(3588, 33, 55, 'بر ایشان [:زنان پیامبر] در مورد پدرانشان و نه پسرانشان و نه برادرانشان و نه پسرانِ برادرانشان و نه پسرانِ خواهرانشان و نه زنان [:همکیش]شان و نه بردگانشان هیچ گناهی نیست (که دیده شوند) و باید از خدا پروا بدارید که خدا همواره بر هر چیزی بسی گواه بوده‌است.'),
(3589, 33, 56, 'خدا و فرشتگانش به‌راستی بر پیامبر رحمت می‌فرستند. هان ای کسانی که ایمان آوردید! بر او (از خدا) رحمت بخواهید و به فرمانش به‌خوبی گردن نهید.'),
(3590, 33, 57, 'بی‌گمان کسانی که خدا و پیامبرِ او را آزار می‌رسانند، خدا آنان را در دنیا و آخرت لعنت کرده و برایشان عذابی اهانت‌بار آماده ساخته است.'),
(3591, 33, 58, 'و کسانی که مردان و زنان مؤمن را - بی‌آنکه مرتکب عمل ناهنجاری شده باشند - آزار می‌دهند، همواره بار تهمت و گناهی دنباله‌دار (و) آشکارگر بر دوش گرفته‌اند.'),
(3592, 33, 59, 'هان ای پیامبر برجسته! به زنان و دخترانت و به زنان مؤمنان بگو (که) برخی از پوشش‌های سرتاسری خود را بر خود فرو گیرند. این (خود) به آن نزدیکتر است که (به پاکدامنی) شناخته شوند؛ پس مورد آزار قرار نگیرند. و خدا بسی پوشنده‌ی رحمتگر بر ویژگان بوده است.'),
(3593, 33, 60, 'به‌راستی اگر منافقان و کسانی که در دل‌هایشان مرضی هست (و دگرگون‌کنندگان) و زلزله‌افکنان در مدینه (از کارشان) باز نایستند، همواره تو را سخت بر آنان به گونه‌ای تنگاتنگ چیره می‌کنیم. سپس جز اندکی در جوار (و همسایگی) تو نپایند.'),
(3594, 33, 61, 'حال آنکه اینان لعنت‌شدگانند و هر کجا - با پیگیری دقیق و کارساز- یافته شدند دستگیر و کشته شوند، (چه) کشتاری!'),
(3595, 33, 62, 'روش کسانی را که پیشتر بوده‌اند (نگه‌دار و ارج بدار). برای سنّت خدا هرگز جایگزینی نخواهی یافت.'),
(3596, 33, 63, 'مردم از تو درباره‌ی ساعت (آخرت) می‌پرسند. بگو: «علم آن تنها نزد خداست‌.» و چه به تو می‌فهماند؟ شاید (این) ساعت نزدیک باشد.'),
(3597, 33, 64, 'خدا کافران را بی‌گمان لعنت نموده و برایشان آتشی بس (سوزان و) فروزان آماده کرده است.'),
(3598, 33, 65, 'جاودانه در آن می‌مانند، (حال آنکه) نه سرپرستی می‌یابند و نه یاوری.'),
(3599, 33, 66, 'روزی که چهره‌هایشان در آتش دگرگون شود، گویند: «ای کاش ما خدا را فرمان می‌بردیم و پیامبر را (نیز) اطاعت می‌کردیم.»'),
(3600, 33, 67, 'و گفتند: «پروردگارمان! ما همواره سران و بزرگانمان را اطاعت کردیم، پس ما را از راه (راست) به بیراهه بردند.»'),
(3601, 33, 68, '«پروردگارمان! آنان را دو چندان عذاب بده و لعنتشان کن، لعنتی بزرگ.»'),
(3602, 33, 69, 'هان ای کسانی که ایمان آوردید! مانند کسانی مباشید که موسی را (با تهمتی) آزردند، پس خدا او را از آنچه گفتند مبّرا ساخت و نزد خدا آبرومند بود.'),
(3603, 33, 70, 'هان ای کسانی که ایمان آوردید! از خدا پروا بدارید و سخنی خویشتن‌دار و پاسدار بگویید؛'),
(3604, 33, 71, 'تا اعمالتان را به صلاح آورد و گناهان دنباله‌دارتان را بر شما بپوشاند. و هر کس خدا و پیامبرش را فرمان بَرَد، همواره به رستگاری بی‌آزار (و) بزرگی دست یافته است.'),
(3605, 33, 72, 'ما همواره امانت (الهی و بار تکلیف) را بر آسمان‌ها و زمین و کوه‌ها نمایش دادیم، پس از خیانت در آن سر باز زدند و از آن با بزرگ داشتش هراسناک شدند، پس انسان به آن خیانت کرد؛ همواره او کانون ستم و نادانی بوده است.'),
(3606, 33, 73, '(آری، چنان است) تا خدا مردان و زنان منافق و مردان و زنان مشرک را عذاب کند، و بر مردان و زنان با ایمان برگشت فرماید . و خدا همواره بسی پوشنده‌ی رحمتگر بر ویژگان بوده‌است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'همه‌ی سپاس‌ها ویژه‌ی خدایی است که آنچه در آسمان‌ها و آنچه در زمین است تنها از اوست و در آخرت (نیز) هرگونه سپاس برای اوست و (هم) اوست بسی دانای حکیم.'),
(3608, 34, 2, 'آنچه در زمین فرو می‌رود و آنچه از آن بر می‌آید و آنچه از آسمان فرود می‌آید و آنچه در آن بالا می‌رود (همه را) می‌داند و اوست رحمتگر بر ویژگان (و)، بسی پوشنده(ی نقصان).'),
(3609, 34, 3, 'و کسانی که کافر شدند، گفتند: «ساعت (قیامت) برایمان نخواهد آمد.» بگو: «چرا، سوگند به پروردگارم که همواره برایتان بی‌گمان خواهد آمد. (همان) دانای نهان(ها) که وزن ذره‌ای، نه در آسمان‌ها و نه در زمین، از وی پوشیده نیست و نه کوچک‌تر از آن و نه بزرگ‌تر از آن است مگر اینکه در کتابی روشنگر است.»'),
(3610, 34, 4, 'تا کسانی را که ایمان آورده و کارهای شایسته(ی ایمانشان) کرده‌اند پاداش دهد؛ آنانند که پوشش و روزیِ گرامی و فراخ برایشان خواهد بود.'),
(3611, 34, 5, 'و کسانی که در (ابطال) آیات ما در (خیال) درمانده کردن(مان) کوشیدند، برایشان عذابی از اضطرابی بس دردناک است.'),
(3612, 34, 6, 'و کسانی‌که بهره‌ی دانش (و بینش) یافته‌اند، می‌بینند آنچه از جانب پروردگارت سویت نازل شده، تمامی حق است، و به راه آن عزیز ستوده (در کل جهات) راهبری می‌کند.'),
(3613, 34, 7, 'و کسانی که کفر ورزیدند، گفتند: «آیا بر (سر و سامان) مردی راهنماییتان کنیم که شما را خبر مهمّی می‌دهد: هنگامی که کاملاً متلاشی شدید، همواره در آفرینشی نوین خواهید بود؟»'),
(3614, 34, 8, '«آیا (این مرد) بر خدا دروغ بسته یا جنونی در اوست‌؟» (نه!) بلکه آنان که به آخرت ایمان ندارند در ژرفای عذاب و گمراهیِ دور و درازند.'),
(3615, 34, 9, 'پس آیا به آنچه از آسمان و زمین رو در رویشان و پشت (سر)شان است ننگریسته‌اند؟ اگر بخواهیم آنان را در زمین فرو می‌بریم، یا پاره‌سنگ‌هایی از آسمان بر سرشان می‌افکنیم. همواره در این (تهدید) برای هر بنده‌ی پیاپی بازگشت‌کننده (سوی خدا) نشانه‌ای است.'),
(3616, 34, 10, 'و همانا به داوود از جانب خویش همواره فضیلتی دادیم (و گفتیم:) «ای کوه‌ها! با او سوی خدا بازگشتی پیاپی کنید.» و پرندگان را (گفتیم که با او هماهنگی نمایید). و آهن را برای او نرم گردانیدیم،'),
(3617, 34, 11, '(و گفتیم) که: «زره‌هایی فراخ بساز و حلقه‌ها را درست اندازه‌گیری کن. و کار شایسته‌ای کنید، زیرا من به آنچه انجام می‌دهید بسی بینایم.»'),
(3618, 34, 12, 'و باد را برای سلیمان (راهوار کردیم). رفتن آن بامدادان، یک ماه و آمدنش عصر‌گاهان، یک ماه (راه) بود. و معدن مس را برای او روان گردانیدیم و برخی از جنّیان به فرمان پروردگارشان پیش او کار می‌کردند، در حالی‌که هر کس از آنان از دستورمان سر برتابد، از عذاب شعله‌ور سوزان به او می‌چشانیم.'),
(3619, 34, 13, '(آن جنّیان) برای او آنچه می‌خواست -از محراب‌ها و مجسّمه‌ها و ظروف بزرگ مانند حوضچه‌ها و دیگ‌های فرورفته در زمین- می‌ساختند. ای خاندان داوود! شکری بزرگ انجام دهید. و اندکی از بندگانم بسیار سپاسگزارند.'),
(3620, 34, 14, 'پس چون مرگ را بر او مقرّر داشتیم، جز جنبنده‌ای خاکی [:موریانه]، حال آنکه عصای او را می‌خورد، جنّیان را از مرگ او آگاه نگردانید. پس چون سلیمان فرو افتاد برای جنّیان روشن گردید که اگر غیب می‌دانستند، در عذاب خفّت‌بار (باقی) نمی‌ماندند.'),
(3621, 34, 15, 'همواره برای (مردمان) سبا در محلّ سکونتشان بس نشانه‌ای (رحمت‌بار) بود: دو باغ از راست و چپ. (به آنان گفتیم:) «از روزی پروردگارتان بخورید و برای او شکر کنید. (این) شهری است پاکیزه و (آن) خدایی است بس پوشاننده.»'),
(3622, 34, 16, 'پس روی گردانیدند، در نتیجه بر آنان سیل (سدّ) عَرِم را روانه کردیم و دو باغ آنها را به دو باغ که میوه‌های تلخ و شوره‌گز و چیزکی از اندکی سدر داشت تبدیل کردیم.'),
(3623, 34, 17, 'این (عقوبت) را به (سزای) آنچه کفر و کفران کردند به آنان جزا دادیم‌. و آیا جز کافرِ بس ‌ناسپاس را مجازات می‌کنیم‌؟'),
(3624, 34, 18, 'و میان آنان و میان آبادی‌های(شان) که در آنها برکت نهاده بودیم، آبادی‌هایی آشکارِ متصل به هم قرار دادیم و در میان آنها پیمایش را، به‌اندازه‌، مقرّر داشتیم. (و گفتیم:) «در این راه‌ها، شبها و روزها در امان بگردید.»'),
(3625, 34, 19, 'پس (از آن) گفتند: «پروردگارمان! میان (جایگاه‌های) سفرهایمان فاصله بینداز.» و بر خویشتن ستم کردند. پس آنها را (برای آیندگان، نمونه‌ی) حکایت‌هایی تازه گردانیدیم و به کلی تار و مارشان کردیم. بی‌چون در این (ماجرا) برای هر شکیبایی بس سپاسگزار نشانه‌هایی است.'),
(3626, 34, 20, 'و بی‌گمان شیطان گمان خود را در مورد آنان بی‌امان راست آورد . پس جز گروهی از مؤمنان، (دیگران) از او پیروی کردند.'),
(3627, 34, 21, 'و شیطان را بر آنان هیچ‌گونه تسلّطی نبود، جز آنکه کسی را که به آخرت ایمان دارد، از کسی که درباره‌ی آن در تردید است نشانه گذاریم. و پروردگار تو بر هر چیزی بسی نگاهبان است.'),
(3628, 34, 22, 'بگو: «کسانی را که جز خدا (معبود خود) پنداشته‌اید بخوانید؛ هم‌وزن ذرّه‌ای را نه در آسمان‌ها و نه در زمین مالک نیستند و در آن دو هیچ شرکتی (هم با خدا) ندارند و برای آنان هیچ پشتیبانی (هم) نیست.»'),
(3629, 34, 23, 'و شفاعتگری در پیشگاه او سودشان نمی‌دهد، مگر برای کسی که خدا برایش اجازه داد. تا هنگامی‌که هراسی سخت بر دل‌هایشان گرفته شد، گفتند: «پروردگارتان چه فرمود؟» گفتند: «‌حقیقت را؛ و اوست بلندمرتبه‌ی بزرگ.»'),
(3630, 34, 24, 'بگو: «کیست (که) شما را از آسمان‌ها و زمین روزی می‌دهد؟» بگو: «خداست‌؛ و در حقیقت یا ما یا شما بر راهی راست یا گمراهی‌ای آشکارگریم.»'),
(3631, 34, 25, 'بگو: « (شما) از آنچه ما جرم کرده‌ایم بازخواست نمی‌شوید و (ما نیز) از آنچه شما انجام می‌دهید بازخواست نمی‌شویم.»'),
(3632, 34, 26, 'بگو: «پروردگارمان میان ما و شما جمع خواهد کرد؛ سپس میان ما به بحقّ جدایی می‌افکند و اوست بس کارگشای بسیار دانا.»'),
(3633, 34, 27, 'بگو: «کسانی را که (به گمانتان به عنوان) شریکانی به او ملحق گردانیده‌اید، به من نشان دهید.» چنان نیست؛ بلکه اوست خدای عزیز حکیم.'),
(3634, 34, 28, 'و ما تو را جز (به عنوان) بشارتگر و هشداردهنده‌ای برای نگهبانی کامل مردمان (از گزند این و آن) نفرستادیم‌؛ لیکن بیشتر مردمان نمی‌دانند.'),
(3635, 34, 29, 'و می‌گویند: «اگر (از) راستان بوده‌اید، این وعده [:هنگامه] چه هنگامی است‌؟»'),
(3636, 34, 30, 'بگو: « وعده‌گاه برای شما روزی است که نه ساعتی [:لحظه‌ای] از آن درنگ توانید خواست و نه پیشی توانید جُست.»'),
(3637, 34, 31, 'و کسانی که کافر شدند گفتند: «نه به این قرآن و نه به آن کتاب‌هایی که روبه‌روی آن است هرگز ایمان نخواهیم آورد.» و ای کاش بیدادگران را هنگامی که در پیشگاه پروردگارشان بازداشت می‌باشند می‌دیدی (که چگونه) برخی از آنان با برخی سخن رد و بدل می‌کنند؛ کسانی که مستضعف و زیردست بودند به کسانی‌که مستکبر و زبردست بودند، می‌گویند: «اگر شما نبودید بی‌گمان ما (از) مؤمنان بودیم.»'),
(3638, 34, 32, 'کسانی که بزرگ‌خواهی و زبردستی کردند، به کسانی که زیردست شدند، گفتند: «مگر ما بودیم که شما را از هدایت - پس از آنکه سوی شما آمد - بازداشتیم‌؟ (نه،) بلکه خودتان مجرمان بوده‌اید.»'),
(3639, 34, 33, 'و کسانی که زیردست شدند به کسانی که زبردستی کردند، گفتند: « (نه،) بلکه نیرنگ شب و روز (شما بود) چون ما را وادار می‌کردید که به خدا کافر شویم و برای او همتایانی قرار دهیم.» و هنگامی که عذاب را دیدند پشیمانی خود را پنهان کردند و در گردن‌های کسانی که کافر شده‌اند غُل‌ها نهادیم‌؛ آیا جز به آنچه انجام می‌داده‌اند پاداش داده می‌شوند.'),
(3640, 34, 34, 'و (ما) در هیچ گروهی هشداردهنده‌ای نفرستادیم جز آنکه فرو رفتگان (نادان) در نعمت‌ها(ی‌شان) گفتند: «ما همواره به آنچه شما بدان فرستاده شده‌اید کافریم.»'),
(3641, 34, 35, 'و گفتند: «ما از حیث دارایی و فرزندان(مان از شما) افزون‌تریم و ما (هرگز از) عذاب‌شوندگان نیستیم.»'),
(3642, 34, 36, 'بگو: «پروردگارم همواره روزی را برای هر کس بخواهد گشاده یا تنگ می‌گرداند؛ ولی بیشتر مردم نمی‌دانند.»'),
(3643, 34, 37, 'و اموال و فرزندانتان چیزی نیستند که شما را به پیشگاه ما نزدیک گردانند، مگر کسی که ایمان آورده و کار شایسته کرده باشد. پس برای آنان چند برابر آنچه انجام داده‌اند پاداشی است و حال آنکه آنان در غرفه‌ها‌(شان) در امانند.'),
(3644, 34, 38, 'و کسانی که در (ژرفای) آیات و نشانه‌های ما (به حال نشیب و فرازشان) برای به زانو درآوردن آنها شتابان می‌کوشند (هم) آنانند احضارشدگان (برای حساب و عذاب).'),
(3645, 34, 39, 'بگو: «پروردگار من بی‌گمان، روزی را برای هر کس از بندگانش که بخواهد گشاده یا برای او تنگ می‌گرداند و هر چه را انفاق کنید عوضش را (هم) او می‌دهد. حال آنکه او بهترین روزی‌دهندگان است.»'),
(3646, 34, 40, 'و (یاد کن) روزی را که (خدا) همه‌ی آنان را گرد هم می‌آورد، سپس به فرشتگان می‌فرماید: «آیا اینان بودند که تنها شما را می‌پرستیدند؟»'),
(3647, 34, 41, 'گفتند: «منزّهی تو، سرپرست ما تنها تویی (و) نه آنان! بلکه اینان جنّیان را می‌پرستیده‌اند؛ حال آنکه بیشترشان به آنان ایمان دارند.»'),
(3648, 34, 42, 'پس امروز شما برای یکدیگر سود و زیانی ندارید، و به کسانی که ستم کرده‌اند می‌گوییم: «بچشید عذاب آتشی را که بدان (حق را) دروغ می‌شمرده‌اید.»'),
(3649, 34, 43, 'و هنگامی‌که آیاتمان به روشنایی بر آنان خوانده می‌شود گویند: «این جز مردی نیست که می‌خواهد شما را از آنچه پدرانتان می‌پرستیدند باز دارد.» و (نیز) می‌گویند: «این جز دروغی بربافته نیست.» و کسانی که کافر شدند، درباره‌ی حقّ - چون فرا سویشان آمد - گفتند: «این جز افسونی آشکارگر نیست.»'),
(3650, 34, 44, 'حال آنکه ما کتاب‌هایی به آنان نداده بودیم (تا) آن‌ها را یادگیرند و پیش از تو (هم) هشداردهنده‌ای سویشان نفرستادیم.'),
(3651, 34, 45, 'و کسانی که پیش از اینان بودند، (نیز رسولان را) تکذیب کردند، در حالی که اینان به ده یک آنچه بدیشان داده بودیم نرسیدند، پس فرستادگان مرا دروغ شمردند؛ پس کیفر من چگونه بود؟'),
(3652, 34, 46, 'بگو: «من تنها شما را به یک چیز اندرز می‌دهم (که) دو دو و یک یک برای خدا به پاخیزید، سپس بیندیشید که (مصاحب و) همراه شما هیچگونه دیوانگی‌ای ندارد. او برای شما - برابر عذاب سختی که در پیش است - جز هشداردهنده‌ای بیش نیست.'),
(3653, 34, 47, 'بگو: «هر مزدی که از شما خواستم پس همان به سود خودتان است. مزد من جز بر خدا نیست و او بر هر چیزی بسی گواه است.»'),
(3654, 34, 48, 'بگو: «بی‌گمان، پروردگارم با تمامی حقیقت (بر باطل) فرو می‌افکند (و حق را فراتر می‌کند. او) بسی دانای پنهان‌هاست.»'),
(3655, 34, 49, 'بگو: «تمامی حق آمد و (دیگر) هیچ باطلی سر نمی‌گیرد و (خود را) برگشت (هم) نمی‌دهد.»'),
(3656, 34, 50, 'بگو: «اگر گمراه شوم، تنها به زیان خود گمراه می‌شوم و اگر راه یابم (این) از (برکت) چیزی است که پروردگارم سویم وحی می‌کند. همانا او بسی شنوای نزدیک است.»'),
(3657, 34, 51, 'و ای کاش می‌دیدی چون (کافران) وحشت‌زده‌اند پس هرگز گریزی نمانده است و از جایی نزدیک برگرفته شدند.'),
(3658, 34, 52, 'و گفتند: «به او ایمان آوردیم.» و چه زمانی از جایی (چنان) دور، دست یافتن (به ایمان) برایشان (میسّر) است‌؟'),
(3659, 34, 53, 'حال آنکه از پیش همواره منکر آن شدند و از جایی دور، به غیب (تیر تهمت به تاریکی) می‌افکنند.'),
(3660, 34, 54, 'و میان آنان و میان آنچه اشتها دارند فاصله‌ای قرار گرفت؛ همان گونه که از دیرباز با امثال ایشان چنان رفت. به‌راستی اینان در شکی (به گمانشان) مستند بوده‌اند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'کل ستایش‌ها خدای را سزاست (که) آفریدگار آسمان‌ها و زمین است با فطرت توحیدی. قرار دهنده‌ی فرشتگان رسولانی (که) دارای بال‌های دوگانه و سه‌گانه و چهارگانه‌اند. در آفرینش ، هر چه بخواهد می‌افزاید. همانا خدا بر هر چیزی تواناست.'),
(3662, 35, 2, 'هر رحمتی را که خدا برای مردمان برگشاید، هیچ بازدارنده‌ای برای آن نیست و آنچه را که باز بدارد، پس از بازگرفتن(اش) هیچ گشاینده‌ای ندارد و اوست بسی با عزت و سنجیده‌کار.'),
(3663, 35, 3, 'هان ای مردمان! نعمتِ خدا را بر خود یاد کنید، آیا غیر از خدا آفریدگاری هست (که) شما را از آسمان و زمین روزی دهد؟ هرگز خدایی جز او نیست. پس چه زمان و چگونه (از حق) به گزاف رانده می‌شوید؟!'),
(3664, 35, 4, 'و اگر تو را تکذیب می‌کنند، پیش از تو (هم) همواره فرستادگانی تکذیب شده‌اند. و تمامی امور تنها سوی خدا بازگردانیده می‌شود.'),
(3665, 35, 5, 'هان ای مردمان! بی‌گمان وعده‌ی خدا حقّ است. پس زنهار تا (این) زندگی دنیا شما را فریب ندهد و زنهار (تا شیطان) بس فریبنده، شما را درباره‌ی خدا نفریبد.'),
(3666, 35, 6, 'شیطان بی‌چون برای شما دشمنی (بزرگ) است. پس شما (نیز) او را به دشمنی برگیرید. (او) فقط دار و دسته‌ی خود را می‌خواند تا آنان از یاران آتشِ سوزانِ زبانه‌دار باشند.'),
(3667, 35, 7, 'برای کسانی که کافر شدند، عذابی سخت است. و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، برای آنان پوشش و پاداشی بزرگ است.'),
(3668, 35, 8, 'پس آیا کسی که زشتیِ کردارش برایش آراسته شده، سپس آن را زیبا دیده (مانند مؤمن نیکوکار است)؟ پس خدا همواره هر که را بخواهد گمراه می‌کند و هر که را بخواهد به راه می‌آورد. پس مبادا به حال حسرت‌هایی (گوناگون) بر آنان، جانت (از میان) برود. خدا همواره به آنچه می‌سازند بسی داناست.'),
(3669, 35, 9, 'و خدا کسی است که بادها را روانه کرد؛ پس (‌آن‌ها) ابری را پراکنده می‌نمایند. و (ما) آن را سوی سرزمینی مرده راندیم. سپس آن زمین را بدان (وسیله) پس از مرگش زنده کردیم. رستاخیز(تان نیز) این چنین است.'),
(3670, 35, 10, 'هر کس عزت می‌خواسته است پس عزت یکسره از آنِ خداست. سخنان پاکیزه تنها سوی او بالا می‌رود و کار شایسته را (خدا) رفعت می‌بخشد. و کسانی که بدی‌ها را مکرآمیز انجام می‌دهند، برایشان عذابی سخت است و نیرنگشان همچنان تباه می‌گردد.'),
(3671, 35, 11, 'و خدا شما را از خاکی آفرید، سپس از نطفه‌ای. سپس شما را جفت جفت گردانید و هیچ مادینه‌ای باری بر نمی‌گیرد و باری نمی‌نهد مگر به علم او، و هیچ سالخورده‌ای عمری دراز نمی‌یابد و از عمرش کاسته نمی‌شود، مگر آنکه در کتاب (علم خدا) است. همواره، این (کارِ سخت) بر خدا آسان است!'),
(3672, 35, 12, 'و آن دو دریا (با هم) یکسان نیستند: این یک، گوارای تشنگی‌زدا (و) نوشیدنش (نیز) گواراست‌؛ و آن یک، شورِ تلخ (و تشنگی‌زا). و از هر یک گوشتی تازه می‌خورید و زیوری که آن را می‌پوشید بیرون می‌آورید؛ و کشتی‌ها را در آن، موج‌شکنان می‌بینی تا از فضلِ او (روزی خود را) جستجو کنید. و شاید شما سپاس بگزارید.'),
(3673, 35, 13, 'شب را در روز فرو می‌برد و روز را در شب فرو می‌برد و آفتاب و ماه را تسخیر کرده است (که) هر یک تا هنگامی معیّن روانند؛ این است خدا، پروردگار شما؛ پادشاهی تنها از آنِ اوست. و کسانی را که بجز او می‌خوانید (و می‌خواهید) مالک سوراخی از هسته‌ی خرمایی (هم) نیستند.'),
(3674, 35, 14, 'اگر آنها را بخوانید، درخواست شما را نمی‌شنوند و اگر (هم) بشنوند برایتان اجابت نمی‌کنند و روز قیامت (این شریکان خدا)، شرکتان را (نسبت به خودشان) انکار (و انگار) می‌کنند. و هیچ کس چون او [:خدای آگاه] تو را از مهمّات آگاه نمی‌کند.'),
(3675, 35, 15, 'هان ای مردمان! شما همه به خدا نیازمندید و خداست (که) او بی‌نیازِ ستوده است.'),
(3676, 35, 16, 'اگر بخواهد شما را می‌بَرد و خلقی نوین (بر سر کار) می‌آورد.'),
(3677, 35, 17, 'و این (امر مهمّ) برای خدا دشوار نیست.'),
(3678, 35, 18, 'و هیچ باربردارنده‌ای بار (گناه) دیگری را برنمی‌دارد و اگر گران‌باری (دیگری را به یاری) سوی (برداشتن) بارش فراخواند چیزی از آن برداشته نمی‌شود؛ هر چند نزدیکترین نزدیکان باشد. (تو) تنها کسانی را که از پروردگارشان در نهان می‌ترسند و نماز بر پا داشتند، هشدار می‌دهی‌؛ و هر کس پاکیزگی برگیرد تنها برای خود پاکیزگی می‌گیرد و فرجام، تنها، سویِ خداست.'),
(3679, 35, 19, 'و نابینا و بینا یکسان نیستند،'),
(3680, 35, 20, 'و نه تاریکی‌ها و نه روشنایی،'),
(3681, 35, 21, 'و نه سایه و نه گرمای شدید.'),
(3682, 35, 22, 'و نه زندگان و نه مردگان، یکسان نیستند. همواره خداست که هر که را بخواهد شنوا می‌گرداند؛ و تو (هرگز) شنواکننده‌ی کسانی که در گورهایند نیستی.'),
(3683, 35, 23, 'تو جز هشداردهنده‌ای (بیش) نیستی.'),
(3684, 35, 24, 'همواره ما تو را به تمامی حق، در حال بشارتگری و هشدار دادن گسیل داشتیم، و هیچ امّتی نبوده مگر اینکه در آن، هشداردهنده‌ای ماندگار بوده است.'),
(3685, 35, 25, 'و اگر تو را تکذیب می‌کنند، بی‌گمان کسانی که پیش از آنها ]: کفار[ (نیز) بودند (پیامبران ما را) تکذیب کردند. پیامبرانشان دلایل آشکار و نوشته‌ها و کتاب روشنگر برایشان آوردند.'),
(3686, 35, 26, 'سپس کسانی را که کافر شده بودند فروگرفتم‌؛ پس چگونه بود انکار (و انگارِ) من‌؟'),
(3687, 35, 27, 'آیا ندیدی که خدا از آسمان، آبی فرود آورد، پس به (وسیله‌ی) آن میوه‌هایی که رنگ‌هایشان گوناگون است بیرون آوردیم‌؟ و از برخی کوه‌ها، راه‌های [:رگه‌های] سپید و گلگون که رنگ‌هایشان مختلف و (همچون) کلاغ‌های سیاه است برون آوردیم.'),
(3688, 35, 28, 'و برخی از مردمان و جانوران و دام‌ها که رنگ‌هایشان گوناگون است (برون آوردیم). از بندگان خدا تنها دانایان (خداشناس) از او هراسانند. آری، همواره خدا بسی ارجمندِ پوشنده است.'),
(3689, 35, 29, 'بی‌گمان، کسانی که کتاب خدا را می‌خوانند و از آن پیروی می‌کنند و نماز بر پا داشتند و از آنچه بدیشان روزی داده‌ایم -نهان و آشکارا- انفاق کردند، (اینان) امید به تجارتی بسته‌اند که هرگز زوال نمی‌پذیرد.'),
(3690, 35, 30, 'تا پاداششان را به شایستگی بدیشان عطا کند و از فزون‌بخشیِ خود در حق آنان بیفزاید. همواره اوست بسی پوشنده‌ی بسیار شکرگزار.'),
(3691, 35, 31, 'و آنچه از کتاب [:قرآن] سوی تو وحی کردیم، (هم)او تمام حق است حال آنکه تصدیق‌کننده است آنچه را در برابر اوست. همواره خدا نسبت به بندگانش بس آگاهی بسیار بیناست.'),
(3692, 35, 32, 'سپس (این) کتاب را به (آن) کسان از بندگان خود که (آنان را) برگزیده بودیم، به میراث دادیم. پس برخی از بندگان ما بر خود ستمکارند و برخی از ایشان میانه‌رو و برخی (هم) از آنان در تمامی کارهای نیک به فرمان خدا (در میان همگان) پیشگامانند. و این خود همان فضیلت بزرگ است.'),
(3693, 35, 33, '(در) باغ‌های همیشگی (که) به آنها در خواهند آمد، در آنجا با دستبندهایی از زر و مروارید زیور یابند و در آنجا جامه‌هاشان پرنیان است.'),
(3694, 35, 34, 'و گفتند: «تمامی سپاس تنها خدایی را سزاست که اندوه را از ما بزدود. به‌راستی پروردگارمان، بی‌گمان بسی پوشنده‌ی شکرگزار است‌.»'),
(3695, 35, 35, '«کسی‌که ما را به فضل خویش در سرای اقامتگاه ابدی جای داد. در اینجا رنجی به ما نرسد، و در اینجا درماندگی و سختی به ما دست ندهد.»'),
(3696, 35, 36, 'و (اما) کسانی که کافر شدند، آتش جهنّم برایشان خواهد بود. در آن، حکم (به مرگ) بر ایشان (جاری) نمی‌شود تا بمیرند و نه از عذاب آن از آنان کاسته شود. (آری،) هر کافر سخت‌انکاری را چنان کیفر می‌دهیم.'),
(3697, 35, 37, 'حال آنکه آنان در آنجا با هم سخت فریاد برمی‌آورند: «پروردگارمان! ما را (از این آتشبار) بیرون بیار تا غیر از آنچه می‌کردیم، کار شایسته کنیم.» آیا و مگر شما را آن قدر عمر ندادیم که هر کس باید در آن عبرت گیرد، عبرت گرفته‌؛ حال آنکه برای شما هشداردهنده آمد؟ پس بچشید که برای ستمگران هیچ یاوری نیست.'),
(3698, 35, 38, 'بی‌گمان خدا(ست که) دانای نهان آسمان‌ها و زمین است. به‌راستی اوست که به راز ذاتی سینه‌ها بسی داناست.'),
(3699, 35, 39, 'اوست کسی که شما را در سرزمین جانشینان (گذشتگان و یکدیگر) گردانید. پس هر کس کفر ورزد در نتیجه کفرش به زیان خود اوست و کافران را کفرشان جز دشمنی‌ای نزد پروردگارشان نمی‌افزاید و کافران را کفرشان غیر از زیانی نمی‌افزاید.'),
(3700, 35, 40, 'بگو: «آیا شریکان خودتان را دیدید: کسانی را که به جای خدا می‌خوانید؛ به من نشان دهید چه چیزی از زمین را آفریدند؟ یا آنان در (سازمان) آسمان‌ها (با خدا) همکاری داشته‌اند؟ یا به ایشان کتابی دادیم، پس آنان دلیلی روشن بر (حقّانیت) خود از آن دارند؟» (نه،) بلکه ستمکاران جز فریبی به یکدیگر، وعده نمی‌دهند.'),
(3701, 35, 41, 'خدا همانا آسمان‌ها و زمین را (همی) نگاه می‌دارد تا (در فضا و بر یکدیگر) فرو نیفتند و اگر فرو افتند بعد از او هیچ کس آنها را نگه نمی‌دارد. همانا او بسی بردبار بسیار پوشنده(ی گناهان و ...) بوده است.'),
(3702, 35, 42, 'و با سوگندهای سخت خود به خدا سوگند یاد کردند: اگر به‌راستی هشداردهنده‌ای برایشان بیاید، بی‌گمان از هر یک از امّت‌ها(ی دیگر) به‌درستی راه‌یافته‌تر شوند. پس چون هشداردهنده‌ای برای ایشان آمد، جز بر نفرت شدیدشان نیفزود.'),
(3703, 35, 43, '(این) در حال استکبار و گردنکشی در زمین و نیرنگ زشتشان بود، و نیرنگ زشت جز (دامن) صاحبش را برنگیرد. پس آیا جز سنّت (و سرنوشتِ شوم) پیشینیان را می‌نگرند و انتظار می‌برند؟ پس هرگز برای سنّت خدا تبدیلی نتوانی یافت، و هرگز برای سنت خدا تحویلی نتوانی یافت.'),
(3704, 35, 44, 'آیا و در زمین نگردیدند، تا فرجام (کار) کسانی را که پیش از ایشان (زیسته) و نیرومندتر از ایشان (هم) بوده‌اند بنگرند؟ و خدا (چنان) نبوده است که چیزی در آسمان‌ها و زمین او را عاجز کند. همانا او بسی دانا و بسیار توانا بوده است.'),
(3705, 35, 45, 'و اگر خدا مردم را به (سزای) آنچه انجام داده‌اند (اینجا) مؤاخذه کند، هیچ جنبنده‌ای را بر پشت زمین باقی نگذارد؛ ولی تا مدّتی معیّن مهلتشان می‌دهد و چون اجلشان فرا رسد، همانا خدا به بندگانش بینا بوده است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یس‌.'),
(3707, 36, 2, 'سوگند به قرآن سرشار از (هرگونه) حکمت [:پیوند شایسته‌ی گسسته‌ها].'),
(3708, 36, 3, 'همانا تو همواره از (جمله‌ی) پیامبرانی؛'),
(3709, 36, 4, 'بر راهی راست چیره و استواری!'),
(3710, 36, 5, '(قرآنی را که) فروفرستاده‌ای از جانب (آن) عزیزِ رحمتگر بر ویژگان است‌؛'),
(3711, 36, 6, 'تا قومی را هشدار دهی که پدرانشان هشدار داده نشدند. پس آنان (همچنان) در (ژرفای) غفلتند.'),
(3712, 36, 7, 'بی‌چون، گفته‌ی (خدا) بی‌گمان به زیان بیشترشان محقّق گردیده است. پس ایشان ایمان نمی‌آورند.'),
(3713, 36, 8, 'ما همواره در گردن‌هایشان، (گردن‌گیرها و) غُل‌هایی نهادیم. پس آنها تا چانه‌هایشان است. پس هم‌اینان (با چشم‌های بسته از روی شرمساری) گردن‌هاشان بسته و در بند و زنجیر بلند و راستاست.'),
(3714, 36, 9, 'و ما فرا روی آنان بی‌گمان سدّی و پشت (سر)شان سدّی نهادیم. در نتیجه پرده‌ای بر (سراسر وجود)شان فرو گسترده‌ایم. پس اینان (هرگز) نمی‌بینند.'),
(3715, 36, 10, 'و آنان را چه بیم دهی (و) چه بیم ندهی (زیانشان یکسان است): ایمان نخواهند آورد.'),
(3716, 36, 11, 'تو تنها کسی را هشدار می‌دهی که این یادواره را پیروی کرد و در پنهانی از خدای رحمانِ پنهان هراسید. پس او را به پوشش و اجری پرکرامت [:بی‌شمار و پدیدار] نوید ده.'),
(3717, 36, 12, 'آری! همین ماییم که همواره مردگان را زنده می‌کنیم و آنچه را از پیش فرستاده‌اند و (نیز) آثارشان را ثبت و ضبط می‌کنیم و هر چیزی را در پیشوا و امامی روشنگر برشمرده‌ایم.'),
(3718, 36, 13, 'و (داستان) مردم مجتمعی را که (آن) رسولان بدانجا آمدند برایشان مَثَلی بزن.'),
(3719, 36, 14, 'چون دو تن از ایشان را سوی آنان فرستادیم، پس آن دو را دروغگو پنداشتند. پس با (فرستاده‌ی) سوّمین تأیید(شان) کردیم. پس (رسولان) گفتند: «ما همواره سوی شما فرستاده‌شدگانیم.»'),
(3720, 36, 15, '(ناباوران آن دیار) گفتند: «شما جز بشری مانند ما نیستید و (خدای) رحمان چیزی نفرستاده و شما جز دروغ نمی‌پردازید.»'),
(3721, 36, 16, 'گفتند: «پروردگارمان می‌داند که ما بی‌گمان به‌راستی سویتان فرستاده‌شدگانیم.»'),
(3722, 36, 17, 'و بر ما (کاری) جز رسانیدن رسای آشکارگر (پیام ربّانی) نیست.»'),
(3723, 36, 18, '(گفتند:) «ما همانا شما را به شگون بد گرفته‌ایم. اگر دست برندارید، بی‌امان به سختی سنگ‌بارانتان می‌کنیم و همواره عذاب دردناکی از ما بی‌چون به شما خواهد رسید.»'),
(3724, 36, 19, '(رسولان) گفتند: «شومی شما با خودِ شماست. آیا اگر تذکر داده شدید (باز به ما شگون بد می‌زنید؟ نه!) بلکه شما (در انکار حقیقت)، قومی اسرافکارید.»'),
(3725, 36, 20, 'و (در این میان) مردی از دورترین جای قریه دوان دوان آمد (و) گفت: «ای قوم من! از (این) فرستادگان پیروی کنید.»'),
(3726, 36, 21, '«از کسانی پیروی کنید که پاداشی از شما نمی‌خواهند، حال آنکه خود (نیز) راه به‌خوبی یافته‌اند.»'),
(3727, 36, 22, '«مرا چیست؛ کسی را نپرستم که به فطرت توحیدی‌ام آفرید و شما تنها سوی او بازگشت می‌یابید؟»'),
(3728, 36, 23, '«آیا به جای او خدایانی را برگیرم، که اگر (خدای) رحمان بخواهد به من گزندی برساند، نه شفاعتشان هرگز به حالم سودی می‌بخشد و نه آنان مرا می‌رهانند؟»'),
(3729, 36, 24, '«همواره در آن هنگام (و هنگامه) بی‌چون (غرق) در گمراهی آشکارگری هستم.»'),
(3730, 36, 25, '«به‌راستی من به پروردگارتان ایمان آوردم. پس (اقرار) مرا بشنوید.»'),
(3731, 36, 26, '(بدو) گفته شد: «به بهشت درآی.» گفت: «ای کاش گروهم (حقیقت را) می‌‌دانستند،»'),
(3732, 36, 27, '«آنچه را پروردگارم برایم پوشش نهاد و در زمره‌ی گرامیانم قرارم داد.»'),
(3733, 36, 28, 'پس از (شهادت) وی، هیچ سپاهی از آسمان بر قومش فرود نیاوردیم و (پیش از این هم) فروفرستنده نبوده‌ایم.'),
(3734, 36, 29, 'تنها یک فریاد (مرگبار) بود و بس؛ ناگهان اینان خاموش‌شدگانند.'),
(3735, 36, 30, 'دریغا بر (این) بندگان! هیچ فرستاده‌ای سویشان نمی‌آید مگر آنکه او را مسخره می‌کرده‌اند.'),
(3736, 36, 31, 'آیا و ندیدند چه بسیار نسل‌ها را پیش از آنان هلاک کردیم، که بی‌گمان آنان سوی ایشان باز نمی‌گردند؟'),
(3737, 36, 32, 'و همواره همه‌ی آنان در آن هنگام (و هنگامه) به پیشگاهمان احضار شدگانند.'),
(3738, 36, 33, 'و زمین مرده، نمادی است برای (معاد)شان: آن را زنده گردانیدیم و دانه‌ای از آن برون آوردیم، پس از آن می‌خورند.'),
(3739, 36, 34, 'و در آن (زمین) باغ‌هایی از درختان سردرهم خرما و انگورها قرار دادیم و چشمه‌هایی در آن بس روان کردیم؛'),
(3740, 36, 35, 'تا از میوه‌ی وبازده آن - و (از) کارکردِ دست‌هایِ خودشان و یا آنچه خود به دست نیاورده‌اند- بخورند. آیا پس (باز هم) سپاس نمی‌گزارند؟'),
(3741, 36, 36, 'منزه است کسی که از آنچه زمین می‌رویاند و (نیز) از خودهاشان و از آنچه نمی‌دانند، همه را جفت‌ها آفریده است.'),
(3742, 36, 37, 'و نشانه‌ای (دیگر) برای آنها (افق) شب است که روز را (مانند پوست) از آن برمی‌کنیم. پس به ناگاه آنان در تاریکی فرو روندگانند.'),
(3743, 36, 38, 'و خورشید برای قرارگاهی (ویژه) روانه است (که) تقدیر [:اندازه‌گیری]ِآن عزیز بس دانا همین است.'),
(3744, 36, 39, 'و ماه را (در) منزلگاه‌هایی به اندازه نهادیم، تا چون شاخکِ خشکِ خوشه‌ی خرما برگشت.'),
(3745, 36, 40, 'نه خورشید را سِزَد که به ماه رسد و نه شب بر روز پیشی‌گیرنده است و هر کدام (از زمین و ماه و خورشید) در گردونه‌ای (فضایی) شناورند.'),
(3746, 36, 41, 'و نشانه‌ای (دیگر) برایشان اینکه ما آنان را به‌درستی به حالت ذریه در کشتیِ انباشته (در صلب‌های پدران و سینه‌های مادران) سوار کردیم.'),
(3747, 36, 42, 'و (نیز) از مانند آن برایشان - آنچه بر آن سواری گیرند - آفریدیم.'),
(3748, 36, 43, 'و اگر بخواهیم غرقشان می‌کنیم، پس هیچ فریادرسی برایشان نیست و نه اینان نجات می‌یابند.'),
(3749, 36, 44, 'مگر (اینکه) تا چندی رحمتی و برخورداری‌ای از ما (برگیرند).'),
(3750, 36, 45, 'و هنگامی (که) به ایشان گفته شود: «آنچه را در پیش رو و پشت سر دارید (هراس و) پاس دارید، شاید مورد رحمت قرار گیرید» (نمی‌شنوند).'),
(3751, 36, 46, 'و هیچ نشانه‌ای از نشانه‌های پروردگارشان ایشان را نمی‌آید، جز اینکه از آن رویگردان بوده‌اند.'),
(3752, 36, 47, 'و چون به آنان گفته شود: «از آنچه خدا روزیتان داده انفاق کنید»، کسانی که کافر شدند، برای آنان که ایمان آوردند، گویند: «آیا کسی را بخورانیم که اگر خدا می‌خواست (خودش) وی را می‌خورانید؟ شما جز در گمراهی آشکارگری (بیش) نیستید.»'),
(3753, 36, 48, 'و می‌گویند: «اگر (از) راستان بوده‌اید، پس این وعده کی خواهد بود؟»'),
(3754, 36, 49, 'جز یک فریاد (مرگبار) را نظر و انتظار ندارند، (تا) هنگامی که سرگرم جدالند درگیردشان.'),
(3755, 36, 50, 'پس (آن‌گاه) نه می‌توانند سفارشی کنند و نه سوی کسان خود برگردند.'),
(3756, 36, 51, 'و در صور [:بوق جان‌افزا] دمیده شد، پس به ناگاه از گورها(شان) شتابان سویِ پروردگارشان جدا شوند.'),
(3757, 36, 52, 'گفتند: «ای وای بر ما! چه کسی ما را از خوابگاهمان برانگیخت‌؟» (به آنان گویند:) «این است همان وعده‌ی رحمان، و پیامبران راست گفتند.»'),
(3758, 36, 53, '(این) بجز یک فریادی (جانخواه) نبود، پس ناگهان همگان در پیشگاه‌مان حاضرشدگانند.'),
(3759, 36, 54, 'پس امروز بر هیچ کسی هیچ ستمی نمی‌رود و جز آنچه می‌کرده‌اید پاداشی نخواهید یافت.'),
(3760, 36, 55, 'همانا امروز، بهشتیان کار و باری خوش در پیش دارند.'),
(3761, 36, 56, 'آنان با همگنانشان در (زیر) سایه‌هایی بر تخت‌ها تکیه‌کنندگانند.'),
(3762, 36, 57, 'در آنجا برای آنان میوه‌ای است و در آنجا هر چه بخواهند هست.'),
(3763, 36, 58, 'از جانب پروردگاری رحمان سخنی سلامت‌وار برایشان است.'),
(3764, 36, 59, 'و ای گناهکاران! امروز (از بی‌گناهان) جدا و نمایان شوید.'),
(3765, 36, 60, 'ای فرزندان آدم! مگر با شما عهد (فطری، عقلانی و وحیانی) نکردم که شیطان را نپرستید (که) بی‌گمان وی دشمنی آشکارگر برای شماست‌؟'),
(3766, 36, 61, 'و اینکه مرا بپرستید (که) این راهی است بس راست!'),
(3767, 36, 62, 'و (شیطان) بی‌چون گروهی سخت و انبوه از میان شما را بی‌گمان گمراه کرد. آیا عقل‌های خود(تان) را نمی‌توانستید به کار برگیرید؟'),
(3768, 36, 63, 'این است جهنّمی که به شما وعده داده می‌شده است!'),
(3769, 36, 64, 'به (جرم) آنکه کفر می‌ورزیدید، اکنون آن را بیفروزید!'),
(3770, 36, 65, 'امروز بر دهان‌هایشان مُهر می‌نهیم، درحالی که دست‌هایشان با ما سخن می‌گویند و پاهایشان بدانچه فراهم می‌ساخته‌اند گواهی می‌دهند.'),
(3771, 36, 66, 'و اگر بخواهیم، همانا بر (چشم‌ها)شان مُهر (نابینایی) می‌زنیم. پس در آن راه راست (بهشت) بر هم پیشی جویند؛ پس (راه راست را) کی و کجا می‌توانند ببینند؟'),
(3772, 36, 67, 'و اگر بخواهیم، ایشان را بی‌گمان در عین توان و موقعیتشان مسخ می‌کنیم (به گونه‌ای) که نه بتوانند بروند و نه بر‌گردند.'),
(3773, 36, 68, 'و هر که را عمر دراز دهیم، او را در آفرینش فروکاسته (و نگونسار) گردانیم. آیا پس خردورزی نمی‌کنید؟'),
(3774, 36, 69, 'و (ما) به او شعر نیاموختیم و شایسته‌ی او (هم) نیست. این (پیامبر، خود) جز یادواره و قرآنی روشنگر نیست.'),
(3775, 36, 70, 'تا هر که را (از نظر فطری، عقلانی و دانایی) زنده بوده است هشدار دهد و گفته(‌ی خدا) بر (علیه) کافران محقّق گردد.'),
(3776, 36, 71, 'آیا و ندیدند که ما به دستاوردهای خویش برای ایشان نعمت‌هایی حیوانی آفریدیم؛ پس آنان مالک آنهایند؟'),
(3777, 36, 72, 'و آنها را برایشان رام گردانیدیم، پس از آنها سواری می‌گیرند و از همان‌ها می‌خورند.'),
(3778, 36, 73, 'و برایشان در آن‌ها سودها و نوشیدنی‌هاست. پس آیا سپاس نمی‌گزارند؟'),
(3779, 36, 74, 'و غیر از خدا(یِ یگانه) خدایانی (به پرستش) برگرفتند، تا شاید (به وسیله‌ی آنان) یاری شوند.'),
(3780, 36, 75, '(ولی آن بتان) نمی‌توانند آنان را یاری کنند، حال آنکه اینان (چون) سپاهی برایشان (در نگهبانیشان) احضارشدگانند.'),
(3781, 36, 76, 'پس، گفتار آنان تو را غمگین نگرداند (زیرا) ما آنچه را پنهان می‌دارند و آنچه را آشکار می‌کنند، می‌دانیم.'),
(3782, 36, 77, 'آیا و مگر آدمی ندیده است که ما او را به‌راستی از نطفه‌ای آفریدیم. پس به ناگاه وی ستیزه‌گری آشکارگر است.'),
(3783, 36, 78, 'و برای ما مَثَلی زد- حال آنکه آفرینش خود را فراموش کرد. - گفت: «چه کسی این استخوان‌ها را در حالی‌که (پوسیده و) خاکستر شده‌اند زنده می‌کند؟»'),
(3784, 36, 79, 'بگو: « همان کس که نخستین بار آنها را - بدون سابقه‌- پدید آورد، آنها را زنده می‌کند و اوست که به هر (گونه) آفرینشی بسی داناست.»'),
(3785, 36, 80, 'کسی که برایتان از درخت سبزفام اخگری نهاد، که از آن ناگهان (آتشی‌) می‌افروزید.'),
(3786, 36, 81, 'آیا و کسی که آسمان‌ها و زمین را آفریده توانا نیست که (باز) مانند آنها را بیافریند؟ آری، اوست بسیار آفریننده‌ی بس دانا.'),
(3787, 36, 82, 'هنگامی که چیزی را اراده فرماید، کارش این بس که می‌گوید: «شو‌»، پس (بی‌درنگ) می‌شود.'),
(3788, 36, 83, 'پس منزه است آن کسی که ملکوت (و واقعیت کلی) هر چیزی در دست (علم وقدرت و رحمت) اوست. و تنها سوی او بازگردانیده می‌شوید.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'سوگند به صف‌بستگان ، صف‌بستنی (زیبا).'),
(3790, 37, 2, 'پس به زجرکنندگان، زجر کردنی (بی‌مهابا).'),
(3791, 37, 3, 'پس به خوانندگان و پیروان یادواره‌ا‌ی (وحیانی).'),
(3792, 37, 4, '(که) به‌راستی معبود شما به‌درستی یگانه است!'),
(3793, 37, 5, 'پروردگار آسمان‌ها و زمین و آنچه میان آن دو است و پروردگار خاورها!'),
(3794, 37, 6, 'ما نزدیکترین آسمان (به این زمین) را به زیوری آراستیم (که همان) کل ستارگان [:درخشان و غیر درخشان] است!'),
(3795, 37, 7, 'و (آنها را) برای نگهبانی از هر شیطان سرکشی به پاسداری گماشتیم!'),
(3796, 37, 8, '(بدان‌گونه که) نمی‌توانند به (سخنان) انبوه برتران (و) بالاتران [:فرشتگان] در بالاترین جایگاهشان گوش فرا دهند و از هر سو با نیزه‌ها(ی آتشین) انداخته می‌شوند.'),
(3797, 37, 9, 'حال آنکه رانده‌شدگانند، و برایشان عذابی دایم و خالص است.'),
(3798, 37, 10, 'مگر کسی که به شنودی دزدانه (و) سریع (سخنانی را) بدزدد، پس شهابی شکافنده از پی او می‌تازد!'),
(3799, 37, 11, 'پس، (از اینان) رأیی نوین بخواه؛ آیا ایشان در آفرینش سخت‌ترند یا کسانی (دیگر) که آفریدیم‌؟ ما آنان را از گِلی چسبنده آفریدیم.'),
(3800, 37, 12, 'بلکه عجب داشتی و (آنها) ریشخند می‌کنند!'),
(3801, 37, 13, 'و هنگامی که یادآوری شوند یاد نمی‌کنند.'),
(3802, 37, 14, 'و هنگامی (هم که) آیتی ببینند در پی تمسخر آنند!'),
(3803, 37, 15, 'و گویند: «این جز سحری آشکارگر نیست.»'),
(3804, 37, 16, '«آیا چون مُردیم و خاک و استخوان‌هایی بودیم، آیا به‌راستی و بی‌چون برانگیخته می‌شویم‌؟»'),
(3805, 37, 17, '«آیا و پدران نخستینمان (نیز برانگیخته می‌گردند)؟!»'),
(3806, 37, 18, 'بگو: «آری! و شما (نیز) خوار و بی‌مقدارید!»'),
(3807, 37, 19, 'پس آن (فریاد جان‌افزا) تنها یک فریاد برکَنَنده است (و بس! ) پس ناگهان آنان می‌نگرند!'),
(3808, 37, 20, 'و گویند: «ای وای بر ما! این روز بُروز طاعت است!»'),
(3809, 37, 21, '(گفته شود) «این است روز جدایی (کسان از ناکسان) که بدان تکذیب می‌کردید.»'),
(3810, 37, 22, '«کسانی را که ستم کرده‌اند و همردیفانشان و آنچه را می‌پرستیده‌اند.»'),
(3811, 37, 23, '«غیر از خدا، گرد آورید و سوی راه مستقیم جهنّم رهبریشان کنید!»'),
(3812, 37, 24, '«و بازداشتشان نمایید، (که) آنان همواره مورد بازخواستند!»'),
(3813, 37, 25, '«برایتان چیست [:چه بیماری است] (که) همدیگر را یاری نمی‌کنید؟!»'),
(3814, 37, 26, 'بلکه امروز آنان به ناچار از دَر تسلیم درآمدگانند!'),
(3815, 37, 27, 'و بعضی روی به بعضی دیگر آوردند حال آنکه از یکدیگر می‌پرسند!'),
(3816, 37, 28, '(و) گویند: «شما (با باطنی دروغین) از درِ راستی با ما در می‌آمده‌اید.»'),
(3817, 37, 29, 'گویند: « (نه!) بلکه خودتان باایمان نبوده‌اید.»'),
(3818, 37, 30, '«و ما را بر شما هیچ سلطه‌ای نبود، بلکه خودتان سرکش بوده‌اید.»'),
(3819, 37, 31, '«پس گفته‌ی پروردگارمان بر ما پابرجا و درست آمد؛ ما همواره بی‌چون چشنده‌ی عذابیم!»'),
(3820, 37, 32, '«پس شما را گمراه کردیم، (زیرا) بی‌گمان خودمان (هم) گمراه بوده‌ایم!»'),
(3821, 37, 33, 'در نتیجه، آنان در آن روز بی‌چون در (ژرفای) عذاب شریک یکدیگرند!'),
(3822, 37, 34, '(آری،) ما به‌راستی با مجرمان چنان می‌کنیم!'),
(3823, 37, 35, 'بی‌گمان همانان بودند (که) وقتی به ایشان گفته می‌شد: «خدایی جز خدای یگانه نیست‌» بزرگ‌خواهی می‌کردند (و سر باز می‌زدند).'),
(3824, 37, 36, 'و گویند: «آیا ما به‌راستی برای شاعری دیوانه دست از خدایانمان برداریم‌؟!»'),
(3825, 37, 37, 'بلکه تمامی حق (حقیقت) را آورده و فرستادگان (خدا) را تصدیق نموده است.'),
(3826, 37, 38, 'بی‌چون، شما همی چشنده‌ی عذابی پر درد هستید!'),
(3827, 37, 39, 'و جز آنچه می‌کرده‌اید جزا نمی‌یابید!'),
(3828, 37, 40, 'مگر بندگان پاک شده‌ی (از طرف) خدا!'),
(3829, 37, 41, 'ایشان برایشان روزی‌ای معلوم است؛'),
(3830, 37, 42, '(از انواع) میوه‌ها! حال آنکه آنان گرامی‌شدگانند.'),
(3831, 37, 43, 'در باغ‌های (پر) نعمت!'),
(3832, 37, 44, '(تکیه‌زنان) بر تخت‌هایی در حالی‌که برابر یکدیگرند.'),
(3833, 37, 45, 'با جامی از نوشابه‌ای روان (که) پیرامونشان به گردش می‌آید؛'),
(3834, 37, 46, '(که) سپید است و نوشندگان را لذّتی (خاص) می‌دهد.'),
(3835, 37, 47, 'نه در آن هلاکتی ناخودآگاه است و نه ایشان از آن (از عقل) تهی می‌گردند!'),
(3836, 37, 48, 'و در نزدشان (زنانی) با چشمان فروهشته‌ی درشتند!'),
(3837, 37, 49, 'گویی ایشان به‌راستی تخمی پنهانند،'),
(3838, 37, 50, 'پس برخیشان به برخی روی نموده، در حالی‌که از همدیگر پرس‌وجو می‌کنند.'),
(3839, 37, 51, 'گوینده‌ای از آنان گفت: «به‌راستی من (در دنیا) همنشینی داشتم،»'),
(3840, 37, 52, '«(که به من) می‌گوید: آیا واقعاً تو بی‌گمان از تصدیق‌کنندگانی‌؟»'),
(3841, 37, 53, '«آیا هنگامی (که) ما مردیم و خاک و استخوان‌هایی بودیم آیا بی‌چون ما به‌درستی جزا یافتگانیم‌؟»'),
(3842, 37, 54, 'گفت: «آیا شما سر بر (جهنم) آورندگانید؟»'),
(3843, 37, 55, 'پس سر بر آورد تا او را در وسط آتش فروزان دید!'),
(3844, 37, 56, 'گفت: «به خدا سوگند، چیزی نمانده بود که تو مرا بی‌چون به سقوط کشانی.»'),
(3845, 37, 57, '«و اگر نعمت پروردگارم نبود، بی‌گمان من (نیز) از احضارشدگان (برای پرسش و در آن آتش) بودم.»'),
(3846, 37, 58, '(و از رویِ شوق می‌گوید:) «آیا پس ما دیگر (از) میرندگان نیستیم؟»'),
(3847, 37, 59, '«جز همان مرگ نخستینمان؟ و ما هرگز از معذبان نیستیم‌؟!»'),
(3848, 37, 60, '«به‌راستی همواره این همان کامیابی سالم بزرگ است!»'),
(3849, 37, 61, 'پس برای همانند این (پاداش) باید عمل‌کنندگان عمل کنند.'),
(3850, 37, 62, 'آیا این (منزلگاه والا) برای پذیرایی واردان بهتر است، یا درخت زقّوم‌؟!'),
(3851, 37, 63, 'همواره، ما آن را برای ستمگران مایه‌ی آزمایشی آتشین گردانیدیم.'),
(3852, 37, 64, 'آن بی‌گمان (همان) درختی است که در (ژرفای) آتش سوزان برون می‌تند.'),
(3853, 37, 65, 'ثمره‌ی آغازینش گویی - آن- همانا سرهای شیاطین است.'),
(3854, 37, 66, 'پس بی‌گمان آنان [:دوزخیان] بی‌‌چون خورندگان از آن و پُرکنندگان شکم‌هاشان از آنند.'),
(3855, 37, 67, 'سپس ایشان را (افزون) بر آن، بی‌چون آمیخته‌ای به‌راستی از مایعی بس جوشان است.'),
(3856, 37, 68, 'سپس همواره بازگشتشان بی‌امان سوی آتش بس زبانه‌دارِ سوزان است.'),
(3857, 37, 69, 'آنان همانا پدران خود را گمراهان یافتند.'),
(3858, 37, 70, 'پس (هم) ایشان بر پیِ‌شان با سرعتی برق‌آسا ترسان و شتاب‌زدگانند!'),
(3859, 37, 71, 'و بی‌گمان پیش از آنان بیشتر پیشینیان همی به گمراهی افتادند.'),
(3860, 37, 72, 'و همواره در میانشان بس هشداردهندگانی فرستادیم.'),
(3861, 37, 73, 'پس بنگر (که) فرجام هشدار‌شدگان چگونه بوده است؛'),
(3862, 37, 74, 'بجز بندگان ‎پاک شده (ی از طرف) خدا.'),
(3863, 37, 75, 'و همانا نوح، ما را به‌درستی ندا در داد. پس به‌راستی چه نیک اجابت‌کنندگان بوده‌ایم.'),
(3864, 37, 76, 'و او و کسانش را از اندوه بس بزرگ رهانیدیم.'),
(3865, 37, 77, 'و تنها فرزندان (صالح) و پیروانش را باقی گذاشتیم.'),
(3866, 37, 78, 'و در میان آیندگان بر او (آوازه‌ی نیک) بر جای نهادیم.'),
(3867, 37, 79, 'سلامی (ربانی) بر نوح در (میان) جهانیان!'),
(3868, 37, 80, 'ما همانا این گونه نیکوکاران را پاداش (شایسته) می‌دهیم.'),
(3869, 37, 81, 'به‌راستی او از بندگان باایمان ماست.'),
(3870, 37, 82, 'سپس دیگران را غرق کردیم.'),
(3871, 37, 83, 'و بی‌گمان، ابراهیم به‌راستی از پیروان او بود.'),
(3872, 37, 84, 'چون با دلی بس سالم به (پیشگاهِ) پروردگارش آمد.'),
(3873, 37, 85, 'چون به پدر (تربیتی) خود و قوم خود گفت: «چه چیزی را می‌پرستید؟»'),
(3874, 37, 86, '«آیا به گزاف و انحراف خدایانی (دروغین) را جز خدا(ی راستین) می‌خواهید؟!'),
(3875, 37, 87, '«پس گمانتان به پروردگار جهانیان چیست‌؟»'),
(3876, 37, 88, 'پس (آن‌گاه) نظری (ظاهری) در ستارگان افکند.'),
(3877, 37, 89, 'پس گفت: «من به‌راستی بیمارم!»'),
(3878, 37, 90, 'پس پشت‌کنان از او روی برتافتند!'),
(3879, 37, 91, 'پس نهانی با حیله‌ای (ایمانی) به سوی خدایانشان تمایل کرد. پس گفت: «آیا (غذا) نمی‌خورید؟»'),
(3880, 37, 92, '«شما را چه شده که سخن نمی‌گویید؟!»'),
(3881, 37, 93, 'پس با دست راست، به گونه‌ای پنهان و حیله‌گرانه بر (سرها)شان کوبید!'),
(3882, 37, 94, 'پس شتابان سویش روان شدند.'),
(3883, 37, 95, '(ابراهیم) گفت: «آیا آنچه را (خود) می‌تراشید، می‌پرستید؟»'),
(3884, 37, 96, '«حال آنکه خدا شما و آنچه را که می‌‌سازید آفریده است!»'),
(3885, 37, 97, 'گفتند: «برایش سازمانی (جهنمی) بسازید، پس او را در (آن) جهنم بیفکنید.»'),
(3886, 37, 98, 'پس خواستند به او نیرنگی زنند. پس ما آنان را پست‌ترین (آفریدگان) گردانیدیم.'),
(3887, 37, 99, 'و (ابراهیم) گفت: «من همواره سوی پروردگارم رهسپارم. زودا (که) مرا راه نماید!»'),
(3888, 37, 100, '«پروردگارم! برایم (فرزندی) از شایستگان ببخش.»'),
(3889, 37, 101, 'پس او را به پسری بردبار مژده دادیم.'),
(3890, 37, 102, 'پس هنگامی که با او به سَعْی (میان صفا و مروه) رسید، گفت: «فرزندم! من بی‌گمان در خواب (چنان) می‌بینم که تو را بی‌چون سر می‌بُرَم. پس بنگر چه (در) نظر داری‌؟» گفت: «پدرم! آنچه را مأموری به انجام رسان! زودا اگر خدا بخواهد، مرا از شکیبایان خواهی یافت.»'),
(3891, 37, 103, 'پس چون هر دو تن (در برابر امر خدا) سر تسلیم فرود آوردند و پسرک را به پیشانی (بر خاک) افکند،'),
(3892, 37, 104, 'و او را ندا در دادیم: «ای ابراهیم!»'),
(3893, 37, 105, '«رؤیا(ی خود) را به‌راستی راست آوردی! ما بی‌گمان نیکوکاران را چنان پاداش می‌دهیم!»'),
(3894, 37, 106, 'به‌درستی این همانا آزمایش روشنگر است!'),
(3895, 37, 107, 'و او را در ازای کشتنِ [:قربانیِ] بزرگی رهانیدیم.'),
(3896, 37, 108, 'و در (میان) آیندگان بر او (آوازه‌ی نیک) بر جای گذاشتیم.'),
(3897, 37, 109, 'سلامی (ربانی) بر ابراهیم!'),
(3898, 37, 110, '(ما) نیکوکاران را بدین‌سان پاداش می‌دهیم.'),
(3899, 37, 111, 'بی‌گمان، او از بندگان باایمان ماست.'),
(3900, 37, 112, 'و او را به (ولادت) اسحاق - حال آنکه پیامبری برجسته از (جمله‌ی) شایستگان است - مژده دادیم.'),
(3901, 37, 113, 'و بر (سر و سامان) او و اسحاق برکت فرو ریختیم. حال آنکه از نسل آن دو برخی نیکوکار و برخی به گونه‌ای آشکارگر، به خود ستمکار است.'),
(3902, 37, 114, 'و به‌راستی بر موسی و هارون بسی منّت نهادیم.'),
(3903, 37, 115, 'و آن دو و قومشان را از بلای بزرگ رهانیدیم.'),
(3904, 37, 116, 'و آنان را یاری دادیم، پس (هم)آ‎نان چیرگان بودند.'),
(3905, 37, 117, 'و آن دو را کتاب روشنی‌خواه دادیم.'),
(3906, 37, 118, 'و هر دو را به راه راست - به‌گونه‌ای مماس - رهبری کردیم.'),
(3907, 37, 119, 'و بر آن دو در (میان) آیندگان (نام نیک) واگذاشتیم.'),
(3908, 37, 120, 'سلامی (ربانی) بر موسی و هارون!'),
(3909, 37, 121, 'بی‌گمان ما نیکوکاران را چنان پاداش می‌دهیم.'),
(3910, 37, 122, 'همانا آنان از بندگان باایمان مایند.'),
(3911, 37, 123, 'و به‌راستی الیاس به‌درستی از فرستادگان ماست.'),
(3912, 37, 124, 'چون به قوم خود گفت: «آیا پروا نمی‌دارید؟»'),
(3913, 37, 125, '«آیا (بتِ) بعل را می‌خوانید (و می‌پرستید) و بهترین آفرینندگان را وامی‌گذارید؟!»'),
(3914, 37, 126, '«خدا، پروردگارتان و پروردگار پدران نخستینتان را؟!»'),
(3915, 37, 127, 'پس او را تکذیب کردند، پس (هم)ایشان همانا (برای حساب) احضار شدگانند.'),
(3916, 37, 128, 'مگر بندگان اخلاص‌یافته‌ی (از سوی) خدا (که بی‌حساب در رحمتند).'),
(3917, 37, 129, 'و بر (سر و سامان)ش در (میان) آیندگان (آوازه‌ی نیک) واگذاشتیم.'),
(3918, 37, 130, 'درودی (ربانی) بر اِل یاسین!'),
(3919, 37, 131, 'ما بی‌گمان نیکوکاران را اینگونه پاداش می‌دهیم.'),
(3920, 37, 132, 'به‌راستی او از بندگان باایمان ماست.'),
(3921, 37, 133, 'و به‌راستی، لوط همانا از زمره‌ی فرستادگان (ما) است.'),
(3922, 37, 134, 'چون او را و همه‌ی کسانش را رهانیدیم.'),
(3923, 37, 135, 'جز زنی سالخورده (و) ناتوان که در میان باقی‌ماندگان (از گمراهان) بود.'),
(3924, 37, 136, 'سپس دیگران را نابود کردیم.'),
(3925, 37, 137, 'و بی‌گمان، شما همواره بر آنان صبحگاهان'),
(3926, 37, 138, 'و شامگاهان می‌گذرید! آیا پس خردورزی نمی‌کنید؟!'),
(3927, 37, 139, 'و به‌راستی، یونس همانا از زمره‌ی فرستادگان (ما) است.'),
(3928, 37, 140, 'چون سوی آن کشتی پُر (از سرنشینان) بگریخت!'),
(3929, 37, 141, 'پس با آنان قرعه انداخت (و) در نتیجه از رانده‌شدگان (از آن) بود.'),
(3930, 37, 142, '(او را به دریا افکندند.) پس (آن) ماهی بزرگ او را بلعید، در حالی که او نکوهش کننده‌ی (خود) بود!'),
(3931, 37, 143, 'پس اگر او از زمره‌ی تسبیح‌کنندگان نبود.'),
(3932, 37, 144, 'بی‌گمان تا روزی که برانگیخته می‌شوند، در شکم آن ماهی (همچنان) می‌ماند!'),
(3933, 37, 145, 'پس او را در حالی که ناخوش بود، به ساحل (عریان و بی‌سامان) افکندیم!'),
(3934, 37, 146, 'و بر (سر و سامان)ش درختی از کدو بن رویاندیم.'),
(3935, 37, 147, 'و او را سوی یکصدهزار (نفر) یا بیشتر (به رسالت) فرستادیم.'),
(3936, 37, 148, 'پس ایمان آوردند. در نتیجه تا چندی برخوردارشان کردیم.'),
(3937, 37, 149, 'پس، از مشرکان جویای رأیی نوین شو. آیا پروردگارت را دختران و آنان را پسران است‌؟!'),
(3938, 37, 150, 'یا فرشتگان را مادینه آفریدیم، حال آنکه آنان شاهد بودند؟'),
(3939, 37, 151, 'هان! (که) آنان از (گزاف و) انحرافشان بی‌گمان گویند:'),
(3940, 37, 152, '«خدا (فرزند) بزاد!» در حالی که آنان بی‌گمان دروغگویانند!'),
(3941, 37, 153, 'آیا (خدا) دختران را بر پسران برگزیده است‌؟'),
(3942, 37, 154, 'پس شما را چه شده‌؟ چگونه داوری می‌کنید؟'),
(3943, 37, 155, 'آیا پس (از این هم) متذکر نمی‌شوید؟!'),
(3944, 37, 156, 'یا برایتان (در دعوایتان) سلطه‌ای آشکارگر است‌؟'),
(3945, 37, 157, 'پس اگر (از) راستان بوده‌اید، کتاب (وحیانی)تان را بیاورید.'),
(3946, 37, 158, 'و بین خدا و جنّیان نسبی قرار دادند، حال آنکه به‌راستی جنّیان نیک دانسته‌اند (که برای حساب پس‌دادن) همواره خودشان بی‌چون احضارشد‌گانند.'),
(3947, 37, 159, 'خدا منزّه است از آنچه (به غلط و نارسایی) توصیف می‌کنند.'),
(3948, 37, 160, 'بجز بندگان اخلاص‌شده (از سوی) خدا (که او را به حقیقیت توصیف می‌کند).'),
(3949, 37, 161, 'پس همانا، شما و آنچه (که شما آن را) می‌پرستید،'),
(3950, 37, 162, 'بر ضدّ او [:خدا] فتنه‌گر نتوانید بود.'),
(3951, 37, 163, 'مگر کسی که آتش‌افروز (دوزخ) پرسوز است!'),
(3952, 37, 164, 'و هیچ یک از ما (فرشتگان) نیست، مگر (اینکه) برای او مقام و مرتبه‌ای معلوم است.'),
(3953, 37, 165, 'و به‌راستی، ماییم که (برای انجام فرمان خدا) به‌درستی صف اندر صفیم.'),
(3954, 37, 166, 'و همانا ما، خودهامان همواره تسبیح‌گویانیم.'),
(3955, 37, 167, 'و گرچه آنان بی‌‌امان می‌گفته‌اند:'),
(3956, 37, 168, '«اگر همواره یادواره‌ای از پیشینیان نزدمان بود،»'),
(3957, 37, 169, '«بی‌چون بندگان خالص‌شده‌ی خدا بوده‌ایم!»'),
(3958, 37, 170, 'در نتیجه به آن کافر شدند، پس در آینده‌ای دور خواهند دانست!'),
(3959, 37, 171, 'و به‌راستی و درستی کلمه‌ی ما برای بندگان فرستاده‌شده‌ی ما از پیش (چنان) رفته است.'),
(3960, 37, 172, 'آنان به‌راستی (بر دشمنانشان) بی‌گمان یاری‌شدگاند.'),
(3961, 37, 173, 'و بی‌گمان سپاهمان همان پیروزمندانند.'),
(3962, 37, 174, 'پس تا مدّتی (معیّن) از آنان روی برتاب.'),
(3963, 37, 175, 'و آنان را بنگر. پس (همانان) در دور زمان خواهند نگریست.'),
(3964, 37, 176, 'پس آیا عذاب ما را شتابزده خواستارند؟'),
(3965, 37, 177, 'پس، آن‌گاه که (عذابمان) به جایگاه و پایگاه وسیعشان فرود آید، چه بد صبحگاهی برای هشدارشدگان است!'),
(3966, 37, 178, 'و از ایشان تا مدّتی روی برتاب.'),
(3967, 37, 179, 'و بنگر. پس در دُور زمان خواهند نگریست!'),
(3968, 37, 180, 'منزّه است پروردگار تو، پروردگار با عزت، از آنچه وصف می‌کنند.'),
(3969, 37, 181, 'و سلامی بر تمامی فرستادگان‌(مان)!'),
(3970, 37, 182, 'و تمامی ستایش ویژه‌ی خدای جهانیان است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'ص. سوگند به قرآن دارای یادواره.'),
(3972, 38, 2, 'بلکه آنان که کفر ورزیدند در (ژرفای) بزرگ‌منشی، (مخالفت) و جداسازی (از حقّ)‌اند.'),
(3973, 38, 3, 'چه بسیار از نسل‌ها (که) پیش از ایشان هلاک(شان) کردیم. پس (ما را) به فریاد خواندند، حال آنکه دیگر مجال گریزی نبود.'),
(3974, 38, 4, 'و از اینکه هشداردهنده‌ای از خودشان برایشان آمد در شگفت شدند، و کافران گفتند: «این ساحری بس دروغ‌پرداز است.»'),
(3975, 38, 5, '«آیا خدایان (متعدّد) را خدایی یکتا قرار داده‌؟ این به‌راستی چیزی بس شگفت‌انگیز است.»'),
(3976, 38, 6, 'و بزرگانشان (از این گیرودار خستند و) رستند (و گفتند:) «بروید و بر خدایان خود شکیبایی کنید. این (انحصارطلبی) همواره چیزی است (که بر ضد ما) اراده می‌شود.»'),
(3977, 38, 7, '«ما این را در گروه دیگر نشنیدیم. این بجز (بافته‌ای) جعلی نیست.»'),
(3978, 38, 8, '«آیا از میان ما یادواره [:قرآن] تنها بر او نازل شده‌؟» بلکه آنان درباره‌ی یادواره‌ی من دودل‌اند. بلکه هنوز عذاب مرا نچشیده‌اند.'),
(3979, 38, 9, '(آیا اینان خود خدایانند؟) یا گنجینه‌های رحمت پروردگار باعزت و عظمتِ بخشایشگرت نزد ایشان است‌؟'),
(3980, 38, 10, 'یا فرمانروایی آسمان‌ها و زمین و آنچه میان آن دو است از آنان است‌؟ پس باید در اسباب (آسمانی و) بالا روند (تا هر چه خواهند دریابند)!'),
(3981, 38, 11, '(این) سپاهکی از دسته‌های دشمن در آنجا (اسباب) در هم شکستنی‌اند.'),
(3982, 38, 12, 'پیش از ایشان (هم) قوم نوح و عاد و فرعونِ صاحب بنیان‌ها و نیروها و میخ‌بن‌ها، (پیامبران را) تکذیب کردند.'),
(3983, 38, 13, 'و ثمودیان و لوطیان و اصحاب اَیْکَه (نیز به تکذیب پرداختند). اینان احزاب (تکذیب‌کننده)اند.'),
(3984, 38, 14, 'هیچ کدام نبودند جز اینکه پیامبران (ما) را تکذیب کردند. پس عقوبت من (بر آنان) بجا و پای‌برجا آمد.'),
(3985, 38, 15, 'و اینان جز یک فریاد (مرگبار) را انتظار نمی‌برند و نمی‌نگرند (که) هیچ راحت و برگشتی ندارد.'),
(3986, 38, 16, 'و گفتند: «پروردگارمان! پیش از (رسیدنِ) روز حساب، باشتاب بهره‌ی قطعی (از عذابمان) را برایمان برسان.»'),
(3987, 38, 17, 'بر آنچه می‌گویند شکیبایی کن و داوود، بنده‌ی ما را (که) دارای امکانات و نیروهای گوناگون بود به یاد آور؛ (آری) او بسیار بازگشت‌کننده (سوی خدا) بود.'),
(3988, 38, 18, 'ما همواره کوه‌ها را با او مسخّر ساختیم. حال آنکه شامگاهان و بامدادان خدا را نیایش می‌کنند.'),
(3989, 38, 19, 'و پرندگان را (نیز با او مسخّر کردیم) که بر گِردش گردآورده شده‌اند (و) همگان برایش بسی بازگشت‌کنندگانند.'),
(3990, 38, 20, 'و پادشاهیش را استوار کردیم و او را حکمت و خطابه‌ی فیصله‌دهنده دادیم.'),
(3991, 38, 21, 'و آیا خَبرِ مهم دشمنان، چون از محراب (داوود به سختی) بالا رفتند، به تو رسید؟'),
(3992, 38, 22, 'چون (ناگهان) بر داوود درآمدند، پس او از آنان به هراس افتاد. گفتند: «مترس (ما) دو دشمنیم؛ یکی از ما بر دیگری تجاوز کرده. پس میان ما به حقّ داوری کن و افراط و پراکنده‌گویی مکن و ما را به میانه‌ی راه راست رهبری کن.»'),
(3993, 38, 23, '«به‌راستی این برادر من است. او را نَودونُه میش و مرا یک میش است. پس (به من) گفت آن (یک) را (هم) به من واگذار و در سخن(اش) با من چیرگی و زورگویی کرد.»'),
(3994, 38, 24, '(داوود) گفت: «همانا او در مطالبه‌ی میش تو - (اضافه) بر میش‌های خودش - بر تو همواره ستم کرده. و بی‌گمان بسیاری از شریکان به همدیگر بی‌چون ستم روا می‌دارند؛ جز کسانی که ایمان آورده و کارهای شایسته کرده‌اند و اینان اندکند.» و داوود گمان (درستی) برد که ما او را بی‌گمان سخت آزمایش کرده‌ایم. پس از پروردگارش پوشش خواست و در حال رکوعش (به سجده) فرو افتاد و (سوی پروردگار) پیاپی بازگشت.'),
(3995, 38, 25, 'پس برای او این (ماجرا) را پوشاندیم‌. و به‌راستی برای او پیش ما بسی تقرّب و بازگشتی خوش است.'),
(3996, 38, 26, '(به او گفتیم:) «داوود! ما تو را در زمین جانشینی (رسالتی از رسولان) گردانیدیم‌؛ پس میان مردمان بحقّ حکم کن و از هوا(ی نفس) پیروی مکن، که از راه خدا گمراهت کند.» بی‌گمان کسانی که از راه خدا به در می‌روند - به (سزای) آنکه روز حساب را فراموش کرده‌اند - برایشان عذابی سخت است!'),
(3997, 38, 27, 'و ما آسمان و زمین و آنچه را که میان این دو است باطل نیافریدیم. این گمان کسانی است که کافر شدند. پس وای از آتش برای کسانی که کافر شدند!'),
(3998, 38, 28, 'یا (مگر) کسانی را که گرویده و کارهای شایسته کردند، چون مفسدانِ در زمین می‌نهیم، یا پرهیزگاران را چون فاجران قرار می‌دهیم‌؟'),
(3999, 38, 29, 'کتابی که آن را سوی تو نازل کردیم مبارک است، تا آیاتش را بیندیشند، و برای اینکه خردمندان ویژه (بدان) پند گیرند.'),
(4000, 38, 30, 'و سلیمان را به داوود بخشیدیم. چه نیکو بنده‌ای است. به‌راستی او (سوی آفریدگار) بسی رهسپار است.'),
(4001, 38, 31, 'چون شباهنگام، اسب‌های اصیل صف‌اندرصف برایش به نمایش گذارده شدند،'),
(4002, 38, 32, 'پس (سلیمان) گفت: «من به‌راستی دوستی آن خیر [:اسب‌های جهاد] را دوست داشته‌ام [:برگزیده‌ام] (که) نشأت‌گرفته و برخاسته از یاد پروردگار من است. تا اینکه (آن اسبان) در پرده(ی شب) پنهان گشتند.»'),
(4003, 38, 33, '(گفت: ) «اسب‌ها را نزد من باز آورید.» پس شروع کرد به دست کشیدن بر ساق‌ها و گردن‌هایشان.'),
(4004, 38, 34, 'و همواره سلیمان را بسی آزمودیم و بر تختش جسدی بیفکندیم ؛ سپس (به ما) پی‌درپی برگشت.'),
(4005, 38, 35, 'گفت: «پروردگارم! پوششی برایم بنه و مُلکی به من ببخش که هیچ کس را پس از من سزاوار نباشد. به‌درستی تو (همین) تو بسی بخشنده‌ای.»'),
(4006, 38, 36, 'پس باد را در اختیار او قرار دادیم، حال آنکه هر جا به‌درستی برفت به فرمان او به نرمی و راهواری روان می‌شد.'),
(4007, 38, 37, 'و شیطان‌ها(ی رام) را (نیز که) هر یک را بنّا و غوّاصند؛'),
(4008, 38, 38, 'و دیگرانی (از شیطان‌ها) را که با زنجیرها سخت بسته شده بودند (تحت فرمانش درآوردیم).'),
(4009, 38, 39, '(گفتیم:) «این بخشش ماست. پس (آن را) بی‌شمار منّت بگذار یا نگاه دار.»'),
(4010, 38, 40, 'و بی‌گمان برای او در پیشگاهمان به‌راستی تقرب و فرجامی نیکوست.'),
(4011, 38, 41, 'و بنده‌ی ما ایّوب را به یاد آر، چون پروردگارش را ندا در داد: «شیطان همواره مرا به رنج‌ها و عذابی بزرگ مبتلا کرده.»'),
(4012, 38, 42, '(به او گفتیم:) «با پای خود روان شو (که) این، شستنگاهی سرد و آشامیدنی است.»'),
(4013, 38, 43, 'و (دوباره) کسانش و مانندشان را، همراهشان بدو بخشیدیم، تا رحمتی از جانبمان و یادواره‌ای برای خردمندان باشد.'),
(4014, 38, 44, 'و (به او گفتیم:) «یک بسته ترکه‌ی نازک نرم به دستت بگیر. پس (همسرت را) با آن (به نرمی) بزن و سوگند(ت) را مشکن.‌» ما همواره او را شکیبا یافتیم. چه نیکو بنده‌ای! به‌راستی او بسیار بازگشت‌کننده (سوی ما) است.'),
(4015, 38, 45, 'و بندگانمان ابراهیم و اسحاق و یعقوب را - که نیرومندان و دیده‌وران بودند- به یاد آور.'),
(4016, 38, 46, 'ما همواره آنان را با پاک‌سازی ویژه‌ای - (که) یادآوریِ سرای (دنیا و آخرت است) - خالص گردانیدیم.'),
(4017, 38, 47, 'و آنان به‌راستی در پیشگاهمان بی‌گمان از برگزیدگان (و) نیکانند.'),
(4018, 38, 48, 'و اسماعیل و یَسَع و ذالکفل را به یاد آور و (اینان) همگان از نیکانند.'),
(4019, 38, 49, 'این یادواره‌ای است (بزرگ) و همواره برای پرهیزگاران بی‌گمان بازگشتی پیاپی و بس نیک است:'),
(4020, 38, 50, 'باغ‌های با درختان سر‌درهم جاودان، حال آنکه درب‌ها(شان) برایشان بس گشوده‌است.'),
(4021, 38, 51, 'در آنجا تکیه‌زنان، میوه‌های فراوان و نوشیدنی(شان را) بخواهند.'),
(4022, 38, 52, 'و در نزدشان (همسرانی) با چشمان فروهشته، همسال و همسانند.'),
(4023, 38, 53, 'این است آنچه برای روز حساب وعده داده می‌شوید.'),
(4024, 38, 54, '(می‌گویند:) «همواره، این به‌راستی روزیِ ماست. آن را هیچ نابودی نیست.»'),
(4025, 38, 55, 'این است (حال بهشتیان) و (امّا) برای طغیانگران بی‌گمان بدترین بازگشتی است،'),
(4026, 38, 56, 'جهنم (که) می‌افروزندش. پس چه بد آرامگاهی است.'),
(4027, 38, 57, 'این جوشاب و چرکابی بس گلوگیر است. پس باید آن را بچشند.'),
(4028, 38, 58, 'و از همین‌گونه، نوعی دیگر همانندهاست!'),
(4029, 38, 59, 'این گروهی است که با شما ناگزیر (در آتش) درمی‌آیند (و) هیچ‌گونه شادباشی برایشان نیست. بی‌گمان آنان آتش‌افروزانند.'),
(4030, 38, 60, 'گفتند: «بلکه شما هرگز شادباشی برایتان نیست، به سبب آنچه پیش فرستادید. پس چه بد قرارگاهی است.»'),
(4031, 38, 61, 'گفتند: «پروردگارمان! هر کس این (عذاب) را برای ما پیش فرستاد، عذابش را در آتش چندان کن.»'),
(4032, 38, 62, 'و گفتند: «ما را چه شده است، که مردانی را که ما آنان را از (زمره‌ی) اشرار برمی‌شمرده‌ایم (اینجا) نمی‌بینیم‌؟»'),
(4033, 38, 63, '«آیا آنان را (در دنیا) به ریشخند می‌گرفتیم یا چشم‌ها(ی‌مان) از آنان منحرف و منصرف شده است‌؟»'),
(4034, 38, 64, 'این (ذلت) به‌راستی راست و بسی پای‌برجاست: مجادله‌ی اهل آتش.'),
(4035, 38, 65, 'بگو: «من تنها هشداردهنده‌ای هستم و جز خدای یگانه‌ی قهّار معبودی دگر نیست.»'),
(4036, 38, 66, '«پروردگار آسمان‌ها و زمین و آنچه میان آن دو است. (همان) عزیزِ بسی پوشنده‌.»'),
(4037, 38, 67, 'بگو: «آن خبری مهم (و) بزرگ است.»'),
(4038, 38, 68, '«شما از آن روی برمی‌تابید.»'),
(4039, 38, 69, '«مرا درباره‌ی ملأ اعلی هیچ آگاهی نبوده است، چون با هم مجادله می‌کردند.»'),
(4040, 38, 70, '«به من هیچ (چیز) وحی نمی‌شود، جز اینکه تنها من به‌راستی هشداردهنده‌ای روشنگرم.»'),
(4041, 38, 71, 'چون پروردگارت به فرشتگان گفت: «من بی‌گمان آفریننده‌ی بشری از گل هستم‌.»'),
(4042, 38, 72, '«پس هنگامی‌که درستش کردم و از روح برگزیده‌(ی آفرینش)ام در او دمیدم، سجده‌کنان برای او (در برابر من) فرو اُفتید.»'),
(4043, 38, 73, 'پس فرشتگان همگان با هم سجده کردند.'),
(4044, 38, 74, 'مگر ابلیس (که) تکبّر جُست و از کافران بود.'),
(4045, 38, 75, 'فرمود: «ای ابلیس! چه چیز تو را مانع شد برای چیزی که به دو دست (قدرت) خویش آفریدم سجده آوری‌؟ آیا تکبّر نمودی یا از مهتران و برجستگان بوده‌ای‌؟»'),
(4046, 38, 76, 'گفت: «من از او بهترم‌؛ مرا از آتش آفریدی و او را از گل آفریدی.»'),
(4047, 38, 77, 'فرمود: «پس، از آن (باغ) برون شو، پس تو بی‌گمان رانده شده‌ای.»'),
(4048, 38, 78, '«و تا روز بُروز طاعت [:قیامت] همواره بر تو لعنت و دورباش من است.»'),
(4049, 38, 79, 'گفت: «پروردگارم! پس مرا تا روزی که برانگیخته می‌شوند مهلت ده.»'),
(4050, 38, 80, 'فرمود: «پس بی‌گمان، تو از مهلت‌یافتگانی»'),
(4051, 38, 81, '«تا روز وقت معلوم» [:قیامت مرگ].'),
(4052, 38, 82, 'گفت: «پس به عزّتت سوگند (که) همگان را بی‌گمان بس گمراه می‌کنم.»'),
(4053, 38, 83, '«مگر بندگان اخلاص‌یافته‌ات را.»'),
(4054, 38, 84, 'فرمود: «پس (من تمامی) حق (هستم) و (همه‌ی) حقّ را می‌گویم (که)،»'),
(4055, 38, 85, '«همواره جهنّم را از تو و از هر کس از آنان که تو را پیروی کرد، از همگیشان، بی‌گمان خواهم انباشت.»'),
(4056, 38, 86, 'بگو: «(من) هیچ مزدی بر این (قرآن) از شما نمی‌خواهم. و من از کسانی نیستم که خود را (برای شما) به زحمت و رنج طاقت‌فرسا افکنم.»'),
(4057, 38, 87, '«این (قرآن) جز یادواره‌ای برای جهانیان نیست.»'),
(4058, 38, 88, '«و همواره پس از چندی خبر بزرگش را به‌راستی خواهید دانست‌.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'فرو فرستادن (این کتاب) از جانب خدای عزیز حکیم است.'),
(4060, 39, 2, 'ما (این) کتاب را به‌راستی با تمامی حق سوی تو فرود آوردیم. پس خدا را، در حالی که طاعت (خود) را برای او خالص‌کننده‌ای عبادت کن.'),
(4061, 39, 3, 'هان (که) دین خالص تنها از آنِ خداست، و کسانی که به جای او اولیائی برای خود برگرفته‌اند (به بهانه‌ی اینکه) ما آنها را جز برای اینکه ما را هر چه بیشتر به خدا نزدیک گردانند نمی‌پرستیم، البته خدا میان آنان درباره‌ی آنچه که بر سر آن اختلاف دارند داوری می‌کند. به‌راستی خدا کسی را که دروغ‌پردازِ بسی ناسپاس است هدایت نمی‌کند.'),
(4062, 39, 4, 'اگر خدا می‌خواست برای خود فرزندی برگیرد. هر آینه از (میان) آنچه می‌آفریند آنچه را می‌خواست برمی‌گزید. منزه است او. اوست خدای یگانه‌ی بس چیره.'),
(4063, 39, 5, 'آسمان‌ها و (زمین) را به (تمامی) حق آفرید. شب را بر روز در پیچد، و روز را بر شب در پیچد. و خورشید و ماه را تسخیر کرد، (چنان که) هر کدام تا زمانی معین روانند. هان (که) او همان عزیز بسی پوشنده است.'),
(4064, 39, 6, 'شما را از نفسی (:شخصی) واحد آفرید سپس جفتش را از او قرار داد، و برای شما از دام‌ها (ی نعمت‌وار) هشت جفت فرود آورد. شما را در شکم‌های مادرانتان آفرینشی پس از آفرینشی (دیگر) در تاریکی‌های سه‌گانه [:مشیمه و رحم و شکم] می‌آفریند. این است خدا، پروردگارتان (که) فرمانروایی (و حکومت مطلق) تنها از اوست. معبودی جز او نیست. پس چگونه و کجا از حق برگردانیده می‌شوید؟'),
(4065, 39, 7, 'اگر کفر یا کفران می‌ورزید، پس خدا بی‌گمان از شما بی‌نیاز است. و برای بندگانش کفر و کفران را خوش نمی‌دارد و اگر سپاس دارید آن را برایتان می‌پسندد. و هیچ (بار)بردارنده‌ای بار (گناه) دیگری را بر نمی‌دارد. آن‌گاه بازگشت شما سوی پروردگارتان است. پس شما را به آنچه می‌کردید خبری مهم می‌دهد. همانا او به (راز) ذات سینه‌ها بسی داناست.'),
(4066, 39, 8, 'و چون به انسان آسیبی در رسد، پروردگارش را می‌خواند، درحالی که سوی او بازگشت‌کننده است. سپس چون او را از جانب خود نعمتی در اختیارش نهد آنچه را از خدا درخواست می‌کرده فراموش کند و برای خدا همتایانی قرار دهد تا (خود و دیگران را) از راه او سرگشته گرداند. بگو: «با کفرت اندکی برخوردار شو که همانا تو از همراهان آتشی‌.»'),
(4067, 39, 9, '(آیا چنین کسی بهتر است) یا آن کسی که او در طول شب در سجده و قیام برای خدا خاشع است در حالی که از آخرت می‌هراسد و رحمت پروردگارش را امید دارد؟ بگو: «آیا کسانی که می‌دانند و کسانی که نمی‌دانند یکسانند؟» تنها خردمندان ژرف‌نگرند که به خوبی یاد می‌کنند.'),
(4068, 39, 10, 'بگو: «ای بندگان من که ایمان آوردید! پروردگارتان را پروا بدارید. برای کسانی که در این دنیا خوبی کرده‌اند نیکی است، و زمین خدا (برای تقوی) فراخ است. فقط شکیبایان پاداش خود را بی‌حساب خواهند یافت.»'),
(4069, 39, 11, 'بگو: «من به‌راستی مأمور شدم که خدا را – در حالی که طاعتم را تنها برای او خالص گردانیده‌ام‌- بپرستم،»'),
(4070, 39, 12, '«و مأمور شدم به آنکه (برابرش) نخستین تسلیم‌کنندگان خود باشم.»'),
(4071, 39, 13, 'بگو: «من اگر به پروردگارم عصیان ورزم، از عذاب روزی بزرگ بس می‌ترسم.»'),
(4072, 39, 14, 'بگو: «تنها خدا را می‌پرستم. در حالی که دینم [:طاعتم] را برای او خالص و بی‌آلایش گرداننده‌ام.»'),
(4073, 39, 15, '«پس هر چه را از مادونش می‌خواهید، بپرستید.»! (ولی به آنان) بگو: «زیانکاران در حقیقت کسانی‌اند که به خود و کسانشان در روز قیامت زیان رسانده‌اند. هان! این همان خسران آشکارگر است.»'),
(4074, 39, 16, 'برایشان از بالای سر(ها)شان چترهایی از آتش است، و از زیر (پاها)شان (نیز) چترهایی آتشین است. این (کیفری) است که خدا بندگانش را به آن بیم می‌دهد. ای بندگان من! پس مرا پرواکنید.'),
(4075, 39, 17, 'و کسانی که خود را از طغیانگران بس دور داشته‌اند، (که) ایشان را بپرستند، و سوی خدا بازگشته‌اند، آنان را مژده باد. پس بندگانم را بشارت ده،'),
(4076, 39, 18, 'کسانی که به سخن گوش فرا می‌دهند، پس بهترینش را پیروی می‌کنند. اینانند که خدایشان راه نموده و ایشانند، (هم)اینان اندیشمندان خالص.'),
(4077, 39, 19, 'آیا پس کسی که فرمان عذاب بر او به حق و ثابت آمده (کجا روی رهایی دارد)؟ آیا تو کسی را که در آتش است می‌رهانی‌؟'),
(4078, 39, 20, 'لیکن کسانی که از پروردگارشان پروا داشتند برایشان غرفه‌هایی است که از بالای آنها غرفه‌هایی (دیگر) بنا شده (و) نهرها از زیر آنها روان است. وعده‌ی خدا را (بپذیر که) خدا خلاف وعده نمی‌کند.'),
(4079, 39, 21, 'آیا ندیدی که به‌راستی خدا از آسمان، آبی فرود آورد. پس آن را به چشمه‌هایی که در (طبقات زیرین) زمین است نفوذ داد؛ آن‌گاه با آن کشتزاری را که رنگ‌های آن گوناگون است بیرون می‌آورد. سپس (آن کشتزارها) به هیجان آمده. پس آن را بسی زرد می‌بینی، سپس خاشاکش می‌گرداند. بی‌گمان در این (دگرگونی‌ها) برای خردمندان یادواره‌ای است.'),
(4080, 39, 22, 'پس آیا کسی که خدا سینه‌اش را برای (پذیرش) اسلام گشوده، پس او (هم) بر نوری از پروردگارش سلطه و سیطره دارد (همانند فرد تاریک‌دل است)؟ پس وای بر سخت‌دلان از یاد خدا. اینانند که در ژرفای گمراهیِ آشکارگرند.'),
(4081, 39, 23, 'خدا زیباترین حدیث (وحیانی) را (به صورت) کتابی متشابه (که آیاتش همانند با یکدیگرند) نازل کرده. آنان که از پروردگارشان می‌هراسند، پوست‌های بدنشان از آن می‌لرزد و سخت می‌شود (و) سپس پوست‌هاشان و دل‌هاشان سوی یاد خدا نرم می‌گردد. این است هدایت خدا. هر که را بخواهد به آن (قرآن) راه نماید. و هر که را خدا گمراه گذارد او را راهبری نیست.'),
(4082, 39, 24, 'پس آیا آن کس که (به جای دست‌ها) با چهره‌ی خود گزند عذاب را (در) روز قیامت به سختی دفع می‌کند (مانند کسی است که از عذاب ایمن است)؟ و به ستمگران گفته شد: «آنچه را که دستاوردتان بوده است بچشید.»'),
(4083, 39, 25, 'کسانی (هم) که پیش از آنان بودند تکذیب کردند، و از آنجا که دقت و گمان نمی‌کردند، (نابهنگام) عذاب برایشان آمد.'),
(4084, 39, 26, 'پس خدا در زندگی دنیا خواری (و رسوایی) را به آنان چشانید. و اگر می‌دانسته‌اند به‌راستی عذاب آخرت بزرگ‌تر است.'),
(4085, 39, 27, 'و بی‌گمان در این قرآن از هرگونه مثلی به‌راستی برای مردمان آوردیم شاید آنان پند گیرند.'),
(4086, 39, 28, 'قرآنی روشن(بیان) را بی‌هیچ کژی (فرو فرستادیم). شاید آنان پروا کنند.'),
(4087, 39, 29, 'خدا مَثَلی زده: مردی که چند (تن خواجه) با یکدیگر در (مالکیت) او ناسازگارند. و مردی که تنها فرمانبر یک مرد است؛ آیا این دو در مَثَل همانند یکدیگرند؟ سپاس خدای را. (نه،) بلکه بیشترشان نمی‌دانند.'),
(4088, 39, 30, 'همانا تو مرده‌ای و آنان همواره مرده‌اند.'),
(4089, 39, 31, 'سپس شما روز قیامت پیش پروردگارتان مجادله خواهید کرد.'),
(4090, 39, 32, 'پس کیست ستمگرتر از کسی که بر خدا دروغ بست، و حق را چون سویش آمد تکذیب کرد. آیا در جهنم جایگاه و پناهگاهی برای کافران نیست؟'),
(4091, 39, 33, 'و آن کس که با راستی آمد و بدان تصدیق نمود، همانانند که خود پرهیزگارانند.'),
(4092, 39, 34, 'تنها برایشان، هر چه بخواهند پیش پروردگارشان است. این پاداش نیکی‌کنندگان است.'),
(4093, 39, 35, 'تا خدا بدترین عملی را که کرده‌اند، از ایشان بزداید، و آنان را به بهترین کاری که می‌کرده‌اند، پاداش دهد.'),
(4094, 39, 36, 'آیا خدا برای بنده‌اش کافی نیست‌؟ حال آنکه (کافران) تو را از آنها که غیر اویند می‌ترسانند و هر که را خدا گمراه گرداند هرگز برایش هیچ راهبری نیست.'),
(4095, 39, 37, 'و هر که را خدا هدایت کند هرگز برایش گمراه‌کننده‌ای نیست. مگر خدا عزیز دارای انتقام نیست؟'),
(4096, 39, 38, 'و اگر از آنها بپرسی: «چه کسی آسمان‌ها و زمین را آفریده‌؟» بی‌چون به‌راستی گویند: «خدا.» بگو: «آیا پس دیدید اگر خدا بخواهد زیانی به من رساند، آنچه را به جای خدا می‌خوانید، آیا آنان برطرف‌کنندگان زیان‌شان هستند؟ یا (اگر) رحمتی برایم بخواهد آیا آنان بازدارندگان رحمتش می‌باشند؟» بگو: «خدا مرا بس است. توکل‌کنندگان تنها بر او توکل می‌کنند.»'),
(4097, 39, 39, 'بگو: «ای قوم من! شما بر (حسب) امکانتان (به کژی) عمل کنید، من (نیز) به راستی عمل‌کننده‌ام. پس در آینده‌ای دور خواهید دانست (که)»'),
(4098, 39, 40, '«چه کسی را عذابی که رسوایش می‌کند خواهد آمد و عذابی پایدار بر او حلول می‌کند.»'),
(4099, 39, 41, 'ما همانا (این) کتاب را برای (هدایت) مردم به تمامی حق بر تو فرو فرستادیم. پس هرکس هدایت شود، به سود خود اوست، و هر کس گمراه شود به زیان خودش گمراه می‌شود. و تو بر آنها وکیل نیستی.'),
(4100, 39, 42, 'خدا جان‌های انسانی مردمان را هنگام مرگشان (به تمامی) همی می‌ستاند، و جان‌هایی را که در خوابشان نمرده‌اند (قبض می‌کند). پس جان‌های آنان را که مرگ را برشان حتمی کرده (برون بدن) نگاه می‌دارد، و آن دیگران را تا هنگامی معین (به زندگی دنیا) باز پس می‌فرستد. قطعاً در این (امر) برای مردمی که می‌اندیشند نشانه‌هایی (از قدرت و رحمت خدا) است.'),
(4101, 39, 43, 'غیر از خدا شفاعتگرانی (برای خودشان) گرفته‌اند؟ بگو: «آیا هر چند اختیار چیزی را ندارند. و خردورزی (هم) نمی‌کنند؟»'),
(4102, 39, 44, 'بگو: «شفاعت یکسره از آنِ خداست. فرمانروایی آسمان‌ها و زمین تنها برای اوست. سپس تنها سوی او بازگردانیده می‌شوید.»'),
(4103, 39, 45, 'هنگامی که خدا به تنهایی یاد شود دل‌های کسانی که به آخرت ایمان ندارند، منزجر می‌گردد، و چون کسانی که غیر از اویند یاد شوند، ناگهان ایشان از یکدیگر نوید می‌خواهند.'),
(4104, 39, 46, 'بگو: «بارالها! ای پدید آورنده‌ی آسمان‌ها و زمین! دانای نهان و آشکار! تو (خود) در میان بندگانت بر سر آنچه اختلاف می‌کرده‌اند، داوری می‌کنی.»'),
(4105, 39, 47, 'و اگر آنچه در زمین است، (یکسره) برای کسانی که ظلم کرده‌اند و نظیرش (نیز) با آن باشد، قطعاً (همه‌ی) آن را برای رهایی خودشان از سختی عذاب روز قیامت فدیه خواهند داد، و آنچه حسابش را (هرگز) نمی‌کرده‌اند (و گمان هم نداشتند) از (جانب) خدا برایشان آشکار می‌گردد.'),
(4106, 39, 48, 'و پیامدهای بد آنچه که مرتکب شدند، برایشان آشکار شد، و آنچه را که بدان ریشخند زده‌اند آنان را فرو گرفت.'),
(4107, 39, 49, 'پس چون انسان را آسیبی در رسد ما را فرا می‌خواند. سپس چون نعمتی از جانب خود به او عطا کنیم گوید: «تنها آن را به دانش و بینش خویش یافته‌ام.» (نه چنان است) بلکه آن آزمایشی آتشین است ولی بیشترشان نمی‌دانند.'),
(4108, 39, 50, 'بی‌گمان کسانی که پیش از آنان بودند (نیز) این (سخن) را گفتند، پس آنچه به دست می‌آورده‌اند بی‌نیازشان نکرد.'),
(4109, 39, 51, 'پس بدی‌های آنچه مرتکب شده‌اند بدیشان در رسید. و کسانی از این گروه که ستم کردند به زودی بدی‌های آنچه مرتکب شده‌اند، بدیشان خواهد رسید، و آنان درمانده‌کننده(ی ما) نیستند.'),
(4110, 39, 52, 'آیا و ندانستند که خدا بی‌چون روزی را برای هرکس که بخواهد، گشاده یا تنگ می‌گرداند؟ قطعاً در این (اندازه‌گیری) برای مردمی که ایمان می‌آورند نشانه‌هایی (ربانی) است.'),
(4111, 39, 53, 'بگو: «ای بندگان من که بر زیان خودهاشان زیاده‌روی کرده‌‌اند! از رحمت خدا نومید مشوید. بی‌گمان خدا همه‌ی گناهان کوچک را می‌پوشاند. به‌راستی او، (هم)او پوشنده‌ی رحمتگر بر ویژگان است.»'),
(4112, 39, 54, '«و پیش از آنکه شما را عذاب در رسد و سپس یاری نشوید، سوی پروردگارتان نوبه نوبه (خود و دیگران را از گناه) باز گردانید، و برایش تسلیم شوید.»'),
(4113, 39, 55, '«و پیش از آنکه (ناگهان) و در حالی که حدس نمی‌زنید شما را عذاب در رسد، نیکوترین (کتابی) را که از جانب پروردگارتان سویتان فرود آمده است پیروی کنید.»'),
(4114, 39, 56, 'تا آنکه (مبادا) کسی بگوید: «دریغا بر آنچه در حضور خدا کوتاهی ورزیدم، و من بی‌گمان همواره از مسخره‌کنندگان بوده‌ام.»'),
(4115, 39, 57, 'یا بگوید: «اگر خدا به‌راستی هدایتم می‌کرد، به‌درستی از پرهیزگاران بودم.»'),
(4116, 39, 58, 'یا چون عذاب را ببیند، گوید: «کاش مرا برگشتی بود تا از نیکوکاران باشم.»'),
(4117, 39, 59, 'آری، نشانه‌های من همواره برایت آمد. پس با آنها (حق را) تکذیب کردی و تکبر ورزیدی و از کافران بوده‌ای.'),
(4118, 39, 60, 'و روز قیامت کسانی راکه بر خدا دروغ بستند روسیاه می‌بینی. آیا برای سرکشان پناهگاه و پایگاهی در جهنم نیست‌؟'),
(4119, 39, 61, 'و خدا کسانی را که تقوا پیشه کرده‌اند، نجات می‌دهد. بدی به آنان نمی‌رسد و غمگین (هم) نخواهند گردید.'),
(4120, 39, 62, 'خدا آفریدگار هرچیزی است ، و او بر هر چیزی پاسدار (و) کاردار است.'),
(4121, 39, 63, 'درب‌های بسته و پیوسته‌‌ی آسمان‌ها و زمین تنها از اوست. و کسانی که به نشانه‌های خدا کفر ورزیدند ایشان خودشان زیانکارانند.'),
(4122, 39, 64, 'بگو: «هان ای نادانان! آیا پس مرا وادار می‌کنید که جز خدا را بپرستم‌؟»'),
(4123, 39, 65, 'و به‌راستی و درستی به تو و به کسانی که پیش از تو بودند وحی شده است: «اگر شرک بورزی همانا کردارت به‌راستی تباه است و بی‌امان و بی‌چون از زیانکاران خواهی بود.»'),
(4124, 39, 66, '«بلکه تنها خدا را بپرست و از سپاسگزاران باش.»'),
(4125, 39, 67, 'و خدا را ‎آنچنان که شایسته‌ی قدر و منزلتش می‌باشد قدر ندانستند. حال آنکه روز قیامت زمین یکسره در قبضه‌ی (علم و قدرت و حکمت) اوست، و آسمان‌ها در هم پیچیده به دست توانای اوست. او منزه و برتر است از آنچه (با وی) شریک می‌گردانند.'),
(4126, 39, 68, 'و در صور [:بوق جانکاه] دمیده شد. پس هر که در آسمان‌ها و هر که در زمین است بیهوش در افتاد مگر کسی که خدا خواسته. سپس بار دیگر در آن (بوق جان‌خواه) دمیده شد. پس به‌ناگاه آنان (بر پای) ایستاد‌گان می‌نگرند.'),
(4127, 39, 69, 'و زمین به نور پروردگارش روشن شد و کتاب (وحی و اعمال در میان) نهاده گشت و پیامبران و شاهدان آورده شدند و میانشان بحق داوری گشت، در حالی که ایشان مورد ستم قرار نگیرند.'),
(4128, 39, 70, 'و به هر کسی آنچه انجام داده است بی‌کم و کاست (و برتر از آن) داده شد. و او به آنچه می‌کنند داناتر است.'),
(4129, 39, 71, 'و کسانی که کافر شدند گروه گروه سوی جهنم رانده شدند، تا هنگامی که بدان درآمدند، درهای آن (به رویشان) گشوده شد، و نگهبانانش به آنان گفتند: «مگر فرستادگانی از خودتان برایتان نیامدند حال آنکه آیات پروردگارتان را بر شما می‌خوانند و به دیدار چنین روزی شما را هشدار می‌دهند؟» گفتند: «چرا.» ولی فرمان عذاب بر کافران بحق فرود آمد.'),
(4130, 39, 72, 'و گفته شد: «از درهای دوزخ درآیید، حال آنکه جاودانه در آن بمانید. پس چه بد (جایی) است پایگاه دائمی سرکشان!»'),
(4131, 39, 73, 'و کسانی که از پرودگارشان پروا داشته‌اند، گروه گروه سوی بهشت سوق داده شدند، تا هنگامی که بدان رسند و درهای آن (به رویشان) گشوده گردد، و نگهبانان آن به ایشان گویند: «سلام بر شما، خوش آمدید، پس در آن جاودانه درآیید.»'),
(4132, 39, 74, 'و گویند: «سپاس خدایی را که وعده‌اش را برایمان راست گردانید. و سرزمین (بهشت) را به ما به میراث داد. از هر جای آن که بخواهیم جای می‌گزینیم.» پس چه نیک است پاداش عمل‌کنندگان.'),
(4133, 39, 75, 'و فرشتگان را می‌بینی که پیرامون عرش در گردشند، و با سپاس پروردگارشان او را تنزیه می‌کنند، و در میانشان [:مؤمنان] حکم به تمامی حق شده و گفته شود: «تمام سپاس برای خدا پروردگار جهانیان است.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حم.'),
(4135, 40, 2, 'فرو فرستادن تدریجی این کتاب، از جانب خدای عزیز بسیار داناست،'),
(4136, 40, 3, '(که) گناه‌بخشِ توبه‌پذیرِ سخت‌کیفرِ فراخ‌نعمت است. خدایی جز او نیست (و) بازگشت تنها سوی اوست.'),
(4137, 40, 4, 'جز آنهایی که کفر ورزیدند، (کسی) در آیات خدا ستیزه نمی‌کند. پس جابه‌جا شدن و رفت و آمدشان در شهرها تو را هرگز نفریبد.'),
(4138, 40, 5, 'پیش از اینان قوم نوح و بعد از آنان حزب‌ها(ی کافر) به تکذیب پرداختند. و هر امّتی آهنگ پیامبر خود را کردند، تا او را درگیرند. و به (وسیله‌ی) باطل جدال نمودند تا حقیقت را با آن پایمال کنند. پس آنان را فرو گرفتیم. پس آیا کیفر من چگونه بود؟'),
(4139, 40, 6, 'و بدین‌سان فرمان پروردگارت درباره‌ی کسانی که کفر ورزیده بودند، به حقیقت پیوست، که ایشان همواره همدمان آتشند.'),
(4140, 40, 7, 'کسانی که عرش (آفریده‌ی ربانی) را حمل می‌کنند و نیز آنها را که پیرامون آنند (هر دو گروه) با سپاس پروردگارشان (او را) تسبیح می‌گویند و به او ایمان می‌آورند و برای کسانی که گرویده‌اند پوشش می‌خواهند که: «پروردگارمان! تو از جهت علم و رحمت (خود) بر هر چیز احاطه داری، پس برای کسانی که بازگشته و راه تو را دنبال کرده‌اند پوشش بنه و آنها را از عذاب آتش شعله‌ور نگهدار.»'),
(4141, 40, 8, '«پروردگارمان! و آنان را در باغ‌هایی (با درختان) سر درهم جاوید که وعده‌شان داده‌ای، با هر که از پدران و همسران و فرزندان و پیروانشان که شایسته‌اند، داخل کن، زیرا تو خود عزیز حکیمی.»'),
(4142, 40, 9, '«و آنان را از بدی‌ها نگه‌دار، و هر که را در آن روز از بدی‌ها نگه‌داری، همانا رحمتش کرده‌ای، و آن همان کامیابی بزرگ است.»'),
(4143, 40, 10, 'همانا کسانی که کافر شدند صدا زده می‌شوند که: «به‌راستی غضب شدید خدا از غضب شدید شما نسبت به خودتان سخت‌تر است، چون سوی ایمان فرا خوانده می‌شوید، پس کفر می‌ورزید.»'),
(4144, 40, 11, 'گفتند: «پروردگارمان! دوبار ما را میراندی و دوبار ما را زنده کردی، پس به گناهانمان اعتراف کردیم. پس آیا راه بیرون شدنی (از آتش) هست‌؟»'),
(4145, 40, 12, 'این (کیفر از آن جهت است) که چون خدا به راستی به تنهایی‌اش خوانده شود، کفر می‌ورزید؛ و چون به او شرک آورده شود، ایمان می‌آورید. پس (امروز) فرمان از آنِ خدای بسی والای بزرگ است.'),
(4146, 40, 13, 'او کسی است که نشانه‌های خود را به شما می‌نمایاند و برای شما از آسمان روزی‌ای می‌فرستد و جز آن کس که همواره سوی خدا رهسپار است (کسی) پند نمی‌گیرد.'),
(4147, 40, 14, 'پس خدا را برای طاعتش پاکدلانه فراخوانید، هر چند کافران را بس ناخوش افتد!'),
(4148, 40, 15, '(خدا) بالا برنده‌ی درجات، خدای عرش (فرماندهی)، به هر کس از بندگانش که خواهد روح (وحی) را، به فرمان خویش می‌فرستد، تا (مردم را) از روز ملاقات (با خدا) هشدار دهد.'),
(4149, 40, 16, 'آن روز که آنان آشکارند (و) چیزی از آنها بر خدا پوشیده نیست. امروز فرمانروایی از آنِ کیست‌؟ از خدای یکتای قهّار.'),
(4150, 40, 17, 'امروز هر کسی به (موجب) آنچه به دست آورده است کیفر می‌یابد. امروز هرگز ستمی نیست. همانا خدا زودشمار است.'),
(4151, 40, 18, 'و آنان را از (آن) روز (رستاخیز) بس نزدیک بترسان. چون (که) جان‌ها(شان) نزدیک گلوگاه‌ها(شان) است، در حالی که اندوه خود را فرو می‌خورند، برای ستمگران نه هیچ یاری است و نه (هیچ) شفاعتگری که شفاعتش پذیرفته شود.'),
(4152, 40, 19, '(خدا) نگاه‌های دزدانه و آنچه را که در سینه‌ها(شان) نهان می‌دارند، می‌داند.'),
(4153, 40, 20, 'و خدا به حق داوری می‌کند. و کسانی که فروتر از او را می‌خوانند (عاجزند و) به چیزی (درست) داوری نمی‌کنند. همانا خدا، (هم) او بسیار شنوای بس بیناست.'),
(4154, 40, 21, 'آیا و در زمین سیر (تاریخی و جغرافیایی) نکردند تا ببینند فرجام کسانی که پیش از آنها بودند چگونه بوده است‌؟ آنان از ایشان نیرومندتر (بوده) و آثاری (پایدارتر) در روی زمین از خود باقی گذاشتند. با این همه، خدا آنان را به کیفر گناهانشان گرفتار کرد و در برابر خدا هیچ نگهبانی نداشتند.'),
(4155, 40, 22, 'این (کفر) از آن روی بود، که پیامبرانشان همواره دلایل آشکار برایشان می‌آورده‌اند ولی (آنها) انکار (و انگار) می‌کردند. پس خدا (گریبان) آنان را بر گرفت. همانا او نیرومند سختْ‌پیگرد است.'),
(4156, 40, 23, 'و به‌راستی موسی را با آیات خود و سلطه‌ای آشکارگر فرستادیم،'),
(4157, 40, 24, 'سوی فرعون و هامان و قارون. پس گفتند: «افسونگری (شیّاد و) بس دروغگوست.»'),
(4158, 40, 25, 'پس چون حقیقت را از جانب ما برای آنان آورد، گفتند: «پسران کسانی را که با او ایمان آورده‌اند بکشید و زنانشان را زنده بگذارید و حیا را از آنان سلب کنید.» و (اما) نیرنگ کافران جز در (ژرفای) گمراهی نیست.'),
(4159, 40, 26, 'و فرعون گفت: «مرا واگذارید تا موسی را بکشم و (در این حال) پروردگارش را باید بخوانَد. من به‌راستی می‌ترسم آیین شما را تغییر دهد یا در (این) سرزمین فساد را آشکار کند.»'),
(4160, 40, 27, 'و موسی گفت: «همانا من از هر متکبّری که به روز حساب ایمان نمی‌آورد به پروردگار خود و پروردگار شما پناه برده‌ام.»'),
(4161, 40, 28, 'و مردی مؤمن از خاندان فرعون - در حالی که ایمان خود را (از آنان) نهان می‌داشت- گفت: «آیا مردی را می‌کشید (تنها به این جرم) که می‌گوید پروردگار من خداست‌؟ و به‌راستی برایتان از جانب پروردگارتان دلایل آشکاری آورده و اگر دروغگو باشد دروغش به زیان خود اوست و اگر راستگو باشد (دست کم) برخی از آنچه به شما وعده می‌دهد به شما خواهد رسید. همانا خدا کسی را که زیاده‌رو بس دروغزن است هدایت نمی‌کند.»'),
(4162, 40, 29, '«ای قوم من! امروز فرمانروایی از شماست، حال آنکه در (این) سرزمین چیره‌اید. پس چه کسی ما را از بلای خدا - اگر ما را در رسد- حمایت خواهد کرد؟» فرعون گفت: «جز آنچه می‌بینم، به شما نمی‌نمایانم و شما را جز به راه راستین رهنمایی نمی‌کنم.»'),
(4163, 40, 30, 'و کسی که ایمان آورده بود گفت: «ای قوم من! همانا من از (روزی) مانند روز احزابِ (منکر خدا) بر شما می‌ترسم.»'),
(4164, 40, 31, '«(از عادت دیرینه(ای)) همانند قوم نوح و عاد و ثمود و کسانی که پس از آنهایند. و (گرنه) خدا هرگز برای بندگان(اش) ستمی نمی‌خواهد.»'),
(4165, 40, 32, '«و ای قوم من! من همواره بر شما از روز ندا دادن به یکدیگر بیم دارم (که میان خدا و دوزخیان و هم میان آنان و بهشتیان سخنانی در پی است).»'),
(4166, 40, 33, '«روزی که پشت‌کنان (به عنف) باز می‌گردید. برای شما در برابر خدا هیچ نگهبانی نیست.» و هر که را خدا گمراه کند او را هیچ راهبری نیست.'),
(4167, 40, 34, 'و به‌راستی و درستی، یوسف پیش از این نزد شما با دلایل آشکار آمد. پس از آنچه برای شما آورد همواره (غرق) در تردید بودید، تا وقتی که رحلت کرد. گفتید: «خدا بعد از او هرگز فرستاده‌ای را بر نخواهد انگیخت‌.» این‌گونه، خدا هر که را زیاده‌رو شکّاک بی‌مستند است، بی‌راه می‌گذارد.'),
(4168, 40, 35, 'کسانی که درباره‌ی آیات خدا - بدون هیچ حجّتی (روشنگر) که برای آنان آمده باشد - مجادله می‌کنند (این ستیزه) در نزد خدا و نزد کسانی که ایمان آورده‌اند (مایه‌ی) خشمی بس بزرگ است. این گونه، خدا بر تمامی دل (هر) متکبّر بس زورگویی مُهر می‌نهد.'),
(4169, 40, 36, 'و فرعون گفت: «ای هامان! برای من آسمان‌خراشی بلند بساز، شاید من به وسیله‌ها برسم؛»'),
(4170, 40, 37, '«وسایل (دست‌یابی به) آسمان‌ها. پس سوی خدای موسی راه یابم و من او را سخت دروغ‌پرداز می‌پندارم.‌» و این گونه برای فرعون، زشتی کارش آراسته گشت و از راه (راست) بازمانده شد و نیرنگ فرعون جز در (ژرفای) تباهی نیست.'),
(4171, 40, 38, 'و آن کس که ایمان آورد گفت: «ای قوم من! مرا پیروی کنید تا شما را (از بی‌راهه) به راه راهوار شایسته هدایت کنم.»'),
(4172, 40, 39, '«ای قوم من! این زندگی دنیا تنها کالایی (ناچیز) است و همانا آخرت است که سرای پُر قرار پایدار است.»'),
(4173, 40, 40, '«هر که بدی کند، جز به مانند آن کیفر نیابد؛ و هر که کار شایسته کند - مرد باشد یا زن – در حالی که ایمان داشته باشد، پس ایشان داخل بهشت می‌گردند و در آنجا بی‌حساب روزی داده می‌شوند.»'),
(4174, 40, 41, '«و ای قوم من! مرا چیست که شما را به نجات فرا می‌خوانم و (شما) مرا به آتش فرا می‌خوانید؟»'),
(4175, 40, 42, '«مرا فرا می‌خوانید تا به خدا کافر شوم و چیزی را که بدان علمی ندارم با او شریک گردانم. و من شما را به سوی (آن) عزیز پوشنده(ی نقصان‌ها) دعوت می‌کنم.»'),
(4176, 40, 43, '«آنچه مرا سوی آن دعوت می‌کنید، ناچار نه در دنیا و نه در آخرت هیچ‌گونه دعوتی (راستا برای الوهیت) ندارد و برگشت ما همانا سوی خداست. و زیاده‌روها، (هم)آنان همدمان آتشند.»'),
(4177, 40, 44, '«پس به زودی آنچه را به شما می‌گویم به یاد خواهید آورد و کارم را به خدا می‌سپارم‌؛ بی‌گمان خداست که به (حال) بندگان (خود) بسی بیناست.»'),
(4178, 40, 45, 'پس خدا او را از پیامدهای بد آنچه نیرنگ می‌کردند نگه‌داشت و فرعونیان را بدی عذاب فرو گرفت.'),
(4179, 40, 46, '(اینک هر) صبح و شام بر آتش نمایش داده می‌شوند و روزی که رستاخیز بر پا شود (فریاد می‌رسد که:) «فرعونیان را در سخت‌ترین (انواع) عذاب در آورید.»'),
(4180, 40, 47, 'و آن‌گاه که در آتش با یکدیگر محاجه و نزاع می‌کنند، مستضعفان به مستکبران می‌گویند: «ما همواره پیرو شما بوده‌ایم، پس آیا می‌توانید بخشی از این آتش را از ما برگیرید؟»'),
(4181, 40, 48, 'کسانی که گردنکشی کردند، گفتند: «(اکنون) همه‌ی ما در آن (آتش) می‌باشیم. خدا بی‌گمان میان بندگان (خود) داوری کرده است.»'),
(4182, 40, 49, 'و کسانی که در آتش‌اند، به نگهبانان جهنّم گفتند: «پروردگارتان را بخوانید تا زمانی اندک از این عذاب را از ما تخفیف دهد.»'),
(4183, 40, 50, 'گفتند: « و مگر پیامبرانتان با نشانه‌ها(ی روشن) فراسویتان نمی‌آمدند؟» گفتند: «چرا.» گفتند: «پس بخواهید. و درخواست کافران جز در (ژرفای) گمراهی نیست.»'),
(4184, 40, 51, 'همانا ما فرستادگان خود و کسانی را که گرویده‌اند، در زندگی دنیا و روزی که گواهان بر پای می‌ایستند به‌راستی یاری می‌کنیم.'),
(4185, 40, 52, '(همان) روزی که ستمگران را پوزششان سودی ندهد و برای آنان تنها لعنت است و برایشان بدی آن سراست‌.'),
(4186, 40, 53, 'همانا موسی را هدایت دادیم و به فرزندان اسرائیل تورات را به میراث نهادیم.'),
(4187, 40, 54, 'حال آنکه رهنمود و یادواره‌ای برای خردمندان ویژه است.'),
(4188, 40, 55, 'پس صبر کن که به‌راستی وعده‌ی خدا حق (و) پای برجا است و برای کار دنباله‌دارت پوشش بخواه و به سپاس پروردگارت، شامگاهان و بامدادان ستایشگر باش.'),
(4189, 40, 56, 'بی‌گمان آنان که درباره‌ی نشانه‌های خدا - بی آنکه حجّتی برایشان آمده باشد - به مجادله بر می‌خیزند، در دل‌هایشان بجز خودبزرگ‌بینی (خیالی) چیزی نیست، که هرگز به آن رسنده نیستند. پس به خدا پناه جوی (که) همانا، (هم)او بس شنوای بسیار بیناست.'),
(4190, 40, 57, 'همواره آفرینش آسمان‌ها و زمین بزرگتر از آفرینش مردم است، ولی بیشتر مردم نمی‌دانند.'),
(4191, 40, 58, 'و نابینایان و بینایان یکسان نیستند، و کسانی که ایمان آورده و کارهای شایسته کرده‌اند (یکسان) نیستند و نه بزه‌کاران. چه اندک یاد می‌کنید.'),
(4192, 40, 59, 'بی‌چون ساعت (رستاخیز) به‌راستی آمدنی است (و) در آن شکی مستند نیست، ولی بیشتر مردمان ایمان نمی‌آورند.'),
(4193, 40, 60, 'و پروردگارتان فرمود: «مرا بخوانید تا شما را اجابت کنم. در حقیقت، کسانی که از پرستشم کبر می‌ورزند، به زودی در دوزخ به خواری در آیند.»'),
(4194, 40, 61, 'خداست که شب را برایتان پدید آورد، تا در آن آرام گیرید و روز را بینایی‌بخش (قرار داد). همانا خدا بر (سرو سامان) مردمان، بسیار صاحب فضیلت است ولی بیشتر مردم سپاس نمی‌دارند.'),
(4195, 40, 62, 'این است خدا، پروردگار شما (که) آفریننده‌ی هر چیزی است. خدایی جز او نیست. پس چگونه و چه زمانی (از او) به دروغ باز گردانیده می‌شوید؟'),
(4196, 40, 63, 'کسانی که نشانه‌های خدا را انکار (و انگار) می‌کرده‌اند این‌گونه (از خدا) رویگردان می‌شوند.'),
(4197, 40, 64, 'خدایی که زمین را برای ما قرارگاه (شایسته‌ی سکونت) نهاد و آسمان را (بر فرازش) بنیانی قرار داد و شما را صورتگری کرد و صورت‌های شما را نیکو نمود و از چیزهای پاکیزه به شما روزی داد. این است خدا پروردگارتان. پس (بلندمرتبه و بزرگ و) مبارک است خدا، پروردگار جهانیان.'),
(4198, 40, 65, 'اوست تنها زنده‌ی پاینده (و) خدایی جز او نیست. پس او را -در حالی‌که دین (خود) را برای وی بی‌‌آلایش گردانیده‌اید- بخوانید. سپاس(ها همه) ویژه‌ی خدا، پروردگار جهانیان است.'),
(4199, 40, 66, 'بگو: «من همواره نهی شده‌ام از اینکه جز خدا کسانی را که (شما) می‌خوانید پرستش کنم‌. هنگامی که از جانب پروردگارم مرا دلایل روشن رسیده، امر شده‌ام که تنها فرمانبردار پروردگار جهانیان باشم.»'),
(4200, 40, 67, 'اوست کسی که شما را از خاکی آفرید، سپس از نطفه‌ای، آن‌گاه از علقه‌ای و بعد شما را (به صورت) کودکی برمی‌آورَد و تا به نیروهایتان برسید (و) پس (از آن) سالخورده باشید. و از میان شما کسی است که مرگ پیش‌‌رس می‌یابد. و برای اینکه به مدتی که مقرّر است برسید و شاید اندیشه کنید.'),
(4201, 40, 68, 'او همان کسی است که زنده می‌کند و می‌میراند. پس چون به چیزی حکم کند، همین قدر به آن می‌گوید: «شو‌» ، پس (بی‌درنگ) می‌شود.'),
(4202, 40, 69, 'آیا کسانی را که در (ابطال) آیات خدا مجادله می‌کنند ندیدی (که) تا کجا و تا کی (از حقیقت آیات الهی منحرف) می‌شوند.'),
(4203, 40, 70, 'کسانی که کتاب (خدا) و آنچه را که فرستادگان خود را بدان گسیل داشته‌ایم، تکذیب کردند. پس در آینده‌ای دور خواهند دانست‌؛'),
(4204, 40, 71, 'هنگامی که غُل‌ها در گردن‌هایشان است و (نیز) زنجیرها، حال آنکه (با آن‌ها) کشانیده می‌شوند.'),
(4205, 40, 72, 'در میان جوشاب سپس در آتش (پرهیجان) بریان می‌گردند.'),
(4206, 40, 73, 'سپس به آنان گفته شد: «آنچه را در برابر خدا شریک می‌ساختید کجایند؛»'),
(4207, 40, 74, '«بجز خدا؟!» گفتند: «گمشان کردیم، بلکه پیشتر (هم) ما چیزی را (جز خدا به خدایی) نمی‌خواندیم.» این‌گونه خدا کافران را بی‌راه می‌گذارد.'),
(4208, 40, 75, 'این (عقوبت) بدین جهت است که در زمین به ناروا شادی و سرمستی می‌کردید و اینکه در خوشحالی زیاد می‌نازیدید.'),
(4209, 40, 76, 'از درهای دوزخ جاودانه در آیید، پس چه بد است اقامتگاه سرکشان.'),
(4210, 40, 77, 'پس صبر کن که همانا وعده‌ی خدا حق است. پس اگر به‌راستی پاره‌ای از آنچه را که به آنان وعده داده‌ایم بی‌گمان به تو بنمایانیم و یا تو را از دنیا بی‌چون بیرون ببریم، در هر صورت آنان سوی ما بازگردانیده می‌شوند.'),
(4211, 40, 78, 'و همانا به‌راستی پیش از تو فرستادگانی را فرستادیم (و ماجرای) برخی از آنان را بر تو حکایت کردیم و برخی از ایشان را بر تو حکایت نکردیم‌. و هیچ فرستاده‌ای را (چنان) نبوده است که نشانه‌ای آورد جز به اذن خدا. پس هنگامی که فرمان خدا در رسد بحق داوری شود و آنجاست که باطل‌کنندگان (حق) زیان می‌کنند.'),
(4212, 40, 79, 'همان خدایی که چهارپایان نعمت‌وار را برایتان نهاد، تا از برخی از آنها سواری گیرید و از برخی از آنها می‌خورید.'),
(4213, 40, 80, 'و در آنها برای شما سودهایی است. و برای اینکه بر آنها به نیازی که در سینه‌هایتان است برسید، و بر آنها و بر کشتی‌ها حمل می‌شوید.'),
(4214, 40, 81, 'و نشانه‌های خویش را به شما می‌نمایاند، پس کدام یک از نشانه‌های خدا را انکار (و انگار) می‌کنید؟'),
(4215, 40, 82, 'آیا پس در (تاریخ و جغرافیای) زمین نگشته‌اند، تا ببینند فرجام کسانی که پیش از آنان بودند چگونه بوده است‌؟ (آنان) بیشتر از اینان و در نیرویی و در آثاری در زمین، استوارتر بودند و (اما) آنچه به دست می‌آورده‌اند، به حالشان سودی نبخشید.'),
(4216, 40, 83, 'پس چون پیامبرانشان دلایل آشکار برایشان آوردند، به آن چیز (مختصری) از دانش که نزدشان بود خرسند شدند و (سرانجام) آنچه به مسخره می‌گرفتند آنان را فرو گرفت.'),
(4217, 40, 84, 'پس چون سختی (عذاب) ما را دیدند گفتند: «فقط به خدا ایمان آوردیم و بدانچه با او شریک می‌گردانیدیم کافریم.»'),
(4218, 40, 85, 'پس هنگامی که عذاب ما را مشاهده کردند، دیگر ایمانشان برایشان سودی نداشت. سنّت خدا را که از (دیر باز) درباره‌ی بندگانش چنین جاری شده (پروا بدار) و آنجاست که کافران زیان کرده‌اند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حم.'),
(4220, 41, 2, '(این وحی) فروفرستاده‌ای است تدریجی از (جانب) خدای رحمتگر بر آفریدگان، رحمتگر بر ویژگان.'),
(4221, 41, 3, 'کتابی است (که) آیات آن، (با همه‌ی فشردگیش) با کمال روشنی تبیین شده. قرآنی است روشن‌‌زبان برای مردمی که (زبان روشن را) می‌دانند.'),
(4222, 41, 4, 'حال آنکه بشارتگر و هشداردهنده است و (اما) بیشتر آنان (از آن) رویگرداندند. پس آنان (این شنیدنی‌ها را) نمی‌شنوند.'),
(4223, 41, 5, 'و گفتند: «دل‌های ما از آنچه ما را سوی آن می‌خوانی (و می‌خواهی‌) سخت در پوشش‌هایی است، و در گوشهایمان سنگینی و میان ما و تو پرده‌ای است‌؛ پس تو کار خود را انجام بده، (که) ما (نیز) همواره (کار خود را) انجام دهنده‌ایم.»'),
(4224, 41, 6, 'بگو: «من تنها بشری چون شمایم (جز اینکه) به من وحی می‌شود که خدای شما خدایی است یگانه. پس (بعد از هر گونه کژی) فراسوی او پایداری پربها و راستا و پای برجا بجویید و از او پوشش بخواهید. و وای بر مشرکان!»'),
(4225, 41, 7, 'کسانی که زکات نمی‌دهند و ایشان به آخرت کافرانند.'),
(4226, 41, 8, 'کسانی که ایمان آورده و کارهای شایسته‌‌(ی ایمان) کرده‌اند، بی‌گمان برایشان پاداشی بی‌‌پایان و بی‌‌منت و ناگسستنی است.'),
(4227, 41, 9, 'بگو: «آیا (این) شمایید، که بی‌گمان به آن کسی که زمین را در دو روز [:زمان] آفرید، همی کفر می‌ورزید و برای او همتایانی قرار می‌دهید؟ این (بزرگ)، پروردگار جهانیان است‌.»'),
(4228, 41, 10, 'و در (سفینه‌ی) زمین، از فراز آن (لنگر آسا) کوه‌ها(یی همچون میخ در بن زمین) و در آن برکتی فراوان پدید آورد و خوراکی‌های آن را در چهار روز [:زمان] (دیگر) اندازه‌گیری کرد (که) برای خواهندگان (به گونه‌ای) برابر (با یکدیگر و با نیازشان) است.'),
(4229, 41, 11, 'سپس آهنگ (آفرینش) آسمان کرد، در حالی که بخاری (گازین) بود. پس به آن و به زمین (با سخنی تکوینی) فرمود: «خواه یا ناخواه بیایید.» آن دو گفتند: «فرمان‌پذیر آمدیم.»'),
(4230, 41, 12, 'پس آنها را (به صورت) هفت آسمان، در دو روز [:زمان] مقرّر داشت و در هر آسمان کار (مربوط به) آن را وحی فرمود. و آسمان نزدیکتر را به چراغ‌هایی (از ستارگان) بیاراستیم و (نیز) برای نگهبانی (از دزدان آسمانی). این است اندازه‌گیری آن عزیز بسیار دانا.'),
(4231, 41, 13, 'پس اگر روی برتافتند بگو: «شما را از خروشی آتشین، چون آذرخش عاد و ثمود بر حذر داشتم.»'),
(4232, 41, 14, 'چون فرستادگان (ما) از پیش رویشان و از پشت (سر)شان سویشان آمدند (و گفتند:) «جز خدا را نپرستید»، گفتند: «اگر پروردگار ما می‌خواست، همانا (برایمان) فرشتگانی فرو می‌فرستاد. پس بی‌گمان ما به آنچه بدان فرستاده شده‌اید کافریم.»'),
(4233, 41, 15, 'پس اما عادیان، بناحق در زمین سر تکبر برافراشتند و گفتند: «از ما نیرومندتر کیست‌؟» آیا و ندیدند که آن خدایی که خلقشان کرده، (هم)او از ایشان نیرومندتر است‌؟ حال آنکه آنان آیات ما را انکار (و انگار) می‌کرده‌اند.'),
(4234, 41, 16, 'پس بر آنان تندبادی توفنده و زیان‌بار و بسی سرد، در روزهایی شوم فرستادیم تا در زندگی دنیا عذاب رسوایی را بدانان بچشانیم و عذاب آخرت بی‌گمان رسواکننده‌تر است؛ حال آنکه آنان یاری نشوند.'),
(4235, 41, 17, 'و اما ثمودیان؛ پس آنان را راه نمودیم؛ پس کوردلی (و گمراهی) را بر هدایت ترجیح دادند. پس به (کیفر) آنچه مرتکب می‌شده‌اند، خروش آتشِ زبانه‌دارِ عذابِ خفّت‌بار آنان را بر گرفت.'),
(4236, 41, 18, 'و کسانی را که ایمان آوردند و پروا می‌داشته‌اند رهانیدیم.'),
(4237, 41, 19, 'و (یاد کن) روزی را که دشمنان خدا فراسوی آتش گرد آورده شده‌اند. پس ناگزیر و ناگریز (در آن) جمع می‌گردند.'),
(4238, 41, 20, 'تا چون بدان رسند، گوش‌هاشان و دیدگانشان و پوست‌ها [:بدن‌ها]شان به آنچه می‌کرده‌اند گواهی می‌دهند.'),
(4239, 41, 21, 'و به پوست‌ها [:بدن‌ها]شان گفتند: «چرا بر ضدّ ما شهادت دادید؟» (در پاسخ‌) گفتند: «همان خدایی که هر چیزی را به زبان در آورده، ما را (نیز) گویا گردانیده است، حال آنکه او نخستین بار شما را آفرید و تنها سوی او برگردانیده می‌شوید.»'),
(4240, 41, 22, 'و (شما) از اینکه مبادا گوش‌هاتان و دیدگانتان و پوست‌هایتان بر ضدّ شما گواهی دهند تاب و توان پوششی (از آن‌ها) نمی‌داشتید. لیکن گمان داشتید که خدا بسیاری از آنچه را عمل می‌کنید نمی‌داند.'),
(4241, 41, 23, 'و همین است گمانتان که درباره‌ی پروردگارتان بردید؛ شما را با نکوهش هلاک کرد و از زیانکاران شدید.'),
(4242, 41, 24, 'پس اگر شکیبایی نمایند، جایگاهشان (همان) آتش است و اگر (هم) از در سرزنش (خود) در آیند (این هم) از آنان پذیرفتنی نیست.'),
(4243, 41, 25, 'و برای آنان دمسازانی (همچون خودشان) برگماشتیم. پس آنچه را پیش رویشان و آنچه در پی آنان بود در نظرشان زیبا جلوه دادند، و فرمان (عذاب) در میان امت‌هایی از جن و انس که پیش از آنان همچنان درگذشتند برایشان واجب آمد؛ چرا که آنان بی‌امان (از) زیانکاران بوده‌اند.'),
(4244, 41, 26, 'و کسانی که کافر شدند گفتند: «برای این قرآن (و پیشبردِ آن) گوش فرا مدهید و در آن لغو و سخنان بیجا بیفکنید، شاید شما پیروز شوید.»'),
(4245, 41, 27, 'پس کسانی را که کافر شدند همانا بی‌گمان عذابی سخت می‌چشانیم و آنان را بی‌چون و بی‌امان به بدترین اعمالشان که می‌کرده‌اند جزا می‌دهیم.'),
(4246, 41, 28, 'این است سزای دشمنان خدا: آتش، که در آن برایشان خانه‌ی ماندنی است‌. حال آنکه جزایی به کیفر آن است که نشانه‌های ما را (دانسته) انکار (و انگار) می‌کرده‌اند.'),
(4247, 41, 29, 'و کسانی که کفر ورزیدند گفتند: «پروردگارمان! آن دو -از جن و انس‌- که ما را گمراه کردند به ما نشان ده، تا آنها را زیر قدم‌هایمان بگذاریم، تا از پست‌ترین (کسان) باشند.»'),
(4248, 41, 30, 'به راستی کسانی که گفتند: «پروردگار ما خداست‌» سپس (در گفتارشان) ایستادگی کردند، فرشتگان دمادم بر آنان فرود می‌آیند (و می‌گویند:) «بیم مدارید و غمین نباشید و یکدیگر را به بهشتی که وعده یافته‌بودید شادباش‌گویید.»'),
(4249, 41, 31, '«در زندگی دنیا و در آخرت ما دوستان و یاوران شماییم و در آن (بهشت) هر چه میل داشته باشید برای شماست و هر چه خواستار باشید در آنجا خواهید داشت؛»'),
(4250, 41, 32, '«میهمان‌سرای آماده‌ای که از سوی (خدای) پوشنده‌ی رحمتگر بر ویژگان است.»'),
(4251, 41, 33, 'و کیست خوش‌گفتارتر از آن کس که سوی خدا دعوت کرد و کاری شایسته نمود و گفت‌: «من همواره (در برابر خدا) از تسلیم‌شدگانم‌.»'),
(4252, 41, 34, 'و (هرگز) نه نیکی و نه بدی (با یکدیگر) برابر نیستند. بدی را بدان‌گونه که بهتر است دفع کن‌؛ آن‌گاه کسی که میان تو و میان او دشمنی است، گویی دوستی بس (خون)گرم (و هم جوش) است.'),
(4253, 41, 35, 'و این (خصلت) را جز کسانی که شکیبا بودند درنیابند و آن را جز صاحب بهره‌ای بزرگ، در نخواهد یافت.'),
(4254, 41, 36, 'و اگر بی‌گمان کششی (دمدمه‌ای) از شیطان تو را از جای برکَنَد، پس به خدا پناه ببر. همانا، همو بسیار شنوای بسی داناست.'),
(4255, 41, 37, 'و از نشانه‌های او شب و روز و خورشید و ماه است‌؛ نه برای خورشید سجده کنید و نه برای ماه. و اگر تنها او را می‌پرستید، تنها برای خدایی سجده کنید که آنها را آفریده است.'),
(4256, 41, 38, 'پس اگر کبر ورزیدند، کسانی که (در عبودیت و معرفت) نزد پروردگارشانند برایش شباروز تسبیح گویند، حال آنکه آنان رنجور (و خسته) نمی‌شوند.'),
(4257, 41, 39, 'و از نشانه‌های اوست، که تو زمین را به‌راستی افسرده می‌نگری. پس چون باران بر آن فرو ریزیم به جنبش در آید و بر دمد. (آری،) همان کسی که آن را زندگی بخشید، همانا زنده‌‌کننده‌ی مردگان است. به‌راستی او بر هر چیزی بس تواناست.'),
(4258, 41, 40, 'بی‌امان کسانی که آیات ما را (از آشکاری) در خاک (عناد و جهالت) پوشیده می‌دارند بر ما پوشیده نیستند. آیا پس آنکه در آتش افکنده می‌شود بهتر است، یا کسی که در روز قیامت آسوده خاطر و با آرامش می‌آید؟ هر چه خواهید بکنید (که) به‌راستی او به آنچه انجام می‌دهید بیناست.'),
(4259, 41, 41, 'کسانی که به این یادواره [:قرآن] - چون بدیشان رسید- کفر ورزیدند (به کیفر خود می‌رسند). و به‌راستی آن کتابی است عزیز.'),
(4260, 41, 42, 'از پیش رویش و از پشت (سر)ش باطل سویش نیاید؛ فرو فرستاده‌ای پیاپی از حکیمی بس ستوده است.'),
(4261, 41, 43, 'به تو جز آنچه به پیامبران پیش از تو گفته شده است گفته نمی‌شود. به‌راستی پروردگارت همواره صاحب پوشش و دارنده‌ی عِقابی ]:پیگیری‌ای[ پردرد است.'),
(4262, 41, 44, 'و اگر این (کتاب‌) را قرآنی عجمی گردانیده بودیم بی‌‌چون می‌گفتند: «چرا آیه‌های آن جداسازی و تبیین نشده‌؟ آیا (کتابی) عجمی و (مخاطب آن) عرب زبان‌؟» بگو: «این (کتاب) برای کسانی که ایمان‌‌آورنده‌اند رهنمود و درمانی است‌. و کسانی که ایمان نمی‌آورند در گوش‌هایشان سنگینی است و قرآن برایشان کوری (و ره‌‌گم‌‌کردگی) است. ایشان از مکانی دور ندا داده می‌شوند.'),
(4263, 41, 45, 'و به‌راستی و درستی موسی را کتاب [:تورات] دادیم‌؛ پس در آن اختلاف شد و اگر از جانب پروردگارت فرمان [:مهلت] سبقت نگرفته بود، بی‌گمان میانشان داوری شده بود و اما آنان در (باره‌ی) آن در (ژرفای) شکّی مستندند.'),
(4264, 41, 46, 'هر که کار شایسته‌ای کند، به سود (خود) اوست‌؛ و هر که بدی کند، به زیان (خود) اوست‌. و پروردگار تو به بندگان (خود) بسیار ستمکار نیست.'),
(4265, 41, 47, 'علمِ رستاخیز تنها سوی او برگشت می‌شود. و میوه‌ها از غلاف‌هایشان بیرون نمی‌آیند و هیچ مادینه‌ای باری نمی‌گیرد و باری نمی‌گذارد، مگر در (پرتوِ) علم او. و روزی که (خدا) آنان را ندا در دهد: «شریکان من کجایند؟» گویند: «با بانگ رسا به تو اعلام می‌کنیم (که) هیچ گواهی (بر شریکان) از میان ما نیست.»'),
(4266, 41, 48, 'و آنچه از پیش می‌خواندند از (نظر) آنان گم و ناپدید شد و گمان (درستی) کردند (که) آنان را هرگز راه گریزی نیست.'),
(4267, 41, 49, 'انسان از درخواست خیر خسته نمی‌شود و چون آسیبی بدو در رسد کانون یأس و نومیدی است.'),
(4268, 41, 50, 'و اگر به‌راستی از جانب خود بدو رحمتی - پس از زیانی که به او رسیده است - بچشانیم، (هم)او همانا بی‌چون خواهد گفت: «من سزاوار آنم و گمان ندارم که رستاخیز بر پا شونده است و اگر هم بر فرض محال سوی پروردگارم باز گردانیده شوم بی‌گمان نزد او برایم (زندگی) بس بهتر خواهد بود.» پس همواره کسانی را که کافر شدند (یا) کفران کردند، به آنچه انجام داده‌اند همانا به خوبی آگاه خواهیم کرد و بی‌گمان از عذابی سخت بی‌امان به آنان خواهیم چشانید.'),
(4269, 41, 51, 'و چون انسان را نعمتی دادیم، روی برتابد و خود را (از روی تکبر) به پهلویش خرامان کنار کشد و چون آسیبی بدو در رسد دارای درخواستی وسیع است.'),
(4270, 41, 52, 'بگو: «آیا دیدید اگر (قرآن) از نزد خدا باشد، سپس به آن کافر شدید، چه کسی گمراه‌تر از آن کس خواهد بود که در جداسازی و مخالفتی دور و دراز (دچارشده) است؟»'),
(4271, 41, 53, 'به زودی نشانه‌های خود را در تمامی افق‌ها و دیدگاه‌ها و در خودهاشان بدیشان می‌نمایانیم، تا برایشان به‌راستی روشن گردد که او تمامی حق است. آیا و کافی نیست که پروردگارت خود همواره بر هر چیزی بسی گواه است‌؟'),
(4272, 41, 54, 'هان! (که) آنان در لقای پروردگارشان در (ژرفای) شکی بی‌مستند فرو رفته‌اند. هان! که او بی‌گمان بر هر چیزی احاطه‌(ی علمی و قدرت و حکمت) دارد.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حم.'),
(4274, 42, 2, 'عسق.'),
(4275, 42, 3, 'این گونه، خدای عزیز و حکیم سوی تو و سوی کسانی که پیش از تو بودند وحی می‌کند.'),
(4276, 42, 4, 'آنچه در آسمان‌ها و آنچه در زمین است تنها از اوست و او تنها بلندمرتبه‌ی بزرگ است.'),
(4277, 42, 5, 'چیزی نمانده که (در پی‌آمد شرک) آسمان‌ها از فرازشان بشکافند. و فرشتگان با سپاس پروردگارشان، تسبیح می‌گویند، و برای کسانی که در زمین هستند بسی پوشش می‌طلبند. آگاه باش! همانا خداست که همو بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(4278, 42, 6, 'و کسانی که به جای او سرپرستانی (برای خود) گرفته‌اند، (بدانند که) خداست که بر آنان (و اینان) نگهبان است، و تو بر اینان نگهبانی نیستی.'),
(4279, 42, 7, 'و بدین‌سان قرآنی آشکار سوی تو وحی کردیم تا مرکز مجتمعاتِ مکلفان [:مکه] و کسانی را که پیرامون آنند هشدار دهی و (نیز) روزِ گرد آمدن (مکلّفان) را - که شکی مستند در آن نیست - هشدار دهی‌؛ (در آن روز) گروهی در بهشتند و گروهی در آتش سوزان فروزان.'),
(4280, 42, 8, 'و اگر خدا می‌خواست، همانا آنان را (به اجبار) امّتی یگانه می‌گردانید. و اما هر که را بخواهد، به رحمت خویش درمی‌آورد. و ستمگران (هرگز) نه هیچ پشتیبانی دارند و نه یاوری.'),
(4281, 42, 9, 'یا از مادون او سرپرستانی (برای خود) گرفته‌اند؟ پس خداست که (هم)او سرپرست (راستین‌) است و اوست که مردگان را زنده می‌کند و (هم) اوست که بر هر چیزی بسی تواناست.'),
(4282, 42, 10, 'و درباره‌ی هر چیزی اختلاف کردید، پس حکمش تنها سوی خداست. این پروردگار من است. تنها بر او توکّل کردم و تنها سوی او پیاپی بازمی‌گردم.'),
(4283, 42, 11, 'پدیدآورنده‌ی آسمان‌ها و زمین (بر فطرت توحید) است. از سنخ خودتان برایتان جفت‌هایی قرار داد، و از دام‌ها (نیز) جفت‌هایی‌(نهاد). بدین وسیله شما را در آفرینش آشکار و نمودار می‌گرداند. هرگز چیزی (در ذات و صفات و افعالش) همانند او نیست و اوست بسیار شنوای بس بینا.'),
(4284, 42, 12, 'گنجینه‌ها، سازمان‌ها و سامان‌ها و درب‌های آسمان‌ها و زمین(و همه چیز آن‌ها) تنها از آنِ اوست. برای هر کس که بخواهد روزی را گشاده یا تنگ می‌گرداند. همواره اوست که به هر چیزی بس داناست.'),
(4285, 42, 13, 'از کل دین، آنچه را که به نوح درباره‌ی آن سفارش کرد، برایتان راهی بگشود. و آنچه را به تو وحی کردیم و آنچه را که درباره‌ی آن به ابراهیم و موسی و عیسی سفارش نمودیم که: «دین را بر پا بدارید و در آن از یکدیگر جدا نشوید.» بر مشرکان آنچه که ایشان را سوی آن فرامی‌خوانی، گران می‌آید. خدا هر که را بخواهد، سوی خود برمی‌گزیند و هر که را به او برگشت پیاپی می‌کند، سوی خود راه می‌نماید.'),
(4286, 42, 14, 'و (در دین) متفرق نشدند، مگر پس از آنکه علم (وحیانی) برایشان آمد، در حال ستم‌کاری (و برتری جویی) میان یکدیگر. و اگر سخنی (دایر بر تأخیر عذاب) از جانب پروردگارت تا زمانی معیّن، پیشی نگرفته بود، بی‌گمان (در دنیا) میانشان (عملاً) داوری می‌شد. و کسانی که بعد از آنان کتاب (وحیانی) را به میراث یافتند، به‌راستی در(باره‌ی) آن در (ژرفای) شکی مستند غوطه‌ورند.'),
(4287, 42, 15, 'پس بنابراین (در برابر گسیختگی مکلفان از حق)، تو همچنان به دعوت (رسالتی پایانی) بپرداز و همان گونه که مأموری ایستادگی کن و هوس‌های آنان را پیروی مکن و بگو: «به هر کتابی که خدا نازل کرده است ایمان آوردم و مأمور شدم میانتان عدالت و ساماندهی کنم‌؛ خدا، پروردگار ما و پروردگار شماست‌؛ اعمال ما از آنِ ما و اعمال شما از آنِ شماست‌؛ حجتی (جز حجت الهی) در میان ما و میان شما نیست‌؛ خدا بین ما را جمع می‌کند و بازگشت همه تنها سوی اوست.»'),
(4288, 42, 16, 'و کسانی که در(باره‌ی) خدا- پس از آنکه دعوتش پذیرفته شد -به مجادله و ستیز برخیزند، حجّتشان پیش پروردگارشان باطل و ناچیز است و خشمی (از خدا) بر ایشان بوده و برایشان عذابی سخت است.'),
(4289, 42, 17, 'خداست که کتاب را به تمامی حقّ و وسیله‌ی سنجش را فرود آورد. و چه به تو می‌فهماند (که) شاید (آن) ساعت (پایانی) نزدیک باشد؟'),
(4290, 42, 18, 'کسانی که به آن ایمان ندارند بسی شتابزده آن را می‌خواهند، و کسانی که ایمان آورده‌اند، از آن با تعظیم هراسناکند و می‌دانند که آن حقّ است. هان‌! همانا آنان که در (باره‌ی) قیامت تردیدی بی‌مستند می‌ورزند، همواره در گمراهی دور و درازی می‌باشند.'),
(4291, 42, 19, 'خدا نسبت به بندگانش بسی کانون لطف است؛ هر که را بخواهد روزی می‌دهد و اوست (همان) نیرومند عزیز.'),
(4292, 42, 20, 'کسی که کِشت آخرت را می‌خواسته‌، برای وی در کشته‌اش می‌افزاییم و کسی که کِشت این دنیا را می‌خواسته‌، به او برخی از آن را می‌دهیم؛ حال آنکه در آخرت او را بهره و نصیبی نیست.'),
(4293, 42, 21, 'یا برای آنان شریکانی است که در آنچه خدا بدان اجازه نداده، برایشان راهی از (آن) دین گشودند؟ و اگر کلمه‌ی (ربّانی.) جداسازی (درباره‌ی جدایی رستاخیز از دنیا) نبود، بی‌گمان (در همین دنیا) میانشان (عملاً) داوری می‌شد. و برای ستمکاران شکنجه‌ای پر درد است.'),
(4294, 42, 22, '(پس از مرگ) ستمکاران را از آنچه انجام دادند، با بزرگ‌داشتی هراسناک می‌بینی و حال آنکه (همان اعمال) به (سر و سامان) ایشان فرود آینده است‌. و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، در سبزه‌زارها و باغ‌هایند (و) آنچه را بخواهند نزد پروردگارشان خواهند داشت. این است همان فضلیت بزرگ.'),
(4295, 42, 23, 'این (نماد) بزرگ همان پاداشی است، که خدا بندگان خود را که ایمان آورده و کارهای شایسته کرده‌اند (بدان) مژده می‌دهد. بگو: «بر آن (رسالت) پاداشی از شما نمی‌خواهم، مگر دوستی درباره‌ی نزدیکتران (رسالتی خود).» و هر کس نیکی را با دشواری به جای آورد، برای او در آن نیکی‌ای (بسیار) خواهیم افزود. به‌راستی خدا بسی پوشنده‌‌ی شکرگزار است.'),
(4296, 42, 24, 'یا می‌گویند: «بر خدا دروغی بسته است!» پس اگر خدا بخواهد بر دلت مُهر پایانی می‌نهد. و خدا باطل را محو و حق را با کلمات خویش پا برجا می‌کند. اوست که به (راز) ذات سینه‌ها بسی داناست.'),
(4297, 42, 25, 'و اوست کسی که توبه را از بندگان خود می‌پذیرد و از گناهان(شان) درمی‌گذرد و آنچه می‌کنید می‌داند.'),
(4298, 42, 26, 'و (درخواستِ) کسانی را که ایمان آورده و کارهای شایسته(ی ایمان) کرده‌اند، به شایستگی اجابت می‌کند و از فضل خویش به آنان افزون می‌بخشد. و برای کافران عذابی سخت است.'),
(4299, 42, 27, 'و اگر خدا روزی را بر بندگانش فراخ گرداند، بی‌گمان در زمین سر به طغیان و تجاوز برمی‌دارند. ولی آنچه را بخواهد به اندازه‌ای (که مصلحت است) به تدریج فرو می‌فرستد. به‌راستی او به (حال) بندگانش (بسی) آگاه و بیناست.'),
(4300, 42, 28, 'و اوست کسی که باران را - پس از آنکه (مردمان) نومید شدند - فرود می‌آورد و رحمت خویش را (بر آنان) می‌گسترد و (هم) اوست سرپرست بسی ستوده.'),
(4301, 42, 29, 'و از نشانه‌های (ربوبیتِ) اوست آفرینش آسمان‌ها و زمین و آنچه از جنبندگان در آن دو پراکند و او هرگاه می‌خواهد بر گردآوردنِ (عاقلان)شان بسی تواناست.'),
(4302, 42, 30, 'و هر (گونه) مصیبتی به شما در رسید ناشی از دستاورد خود شماست و (خدا) از بسیاری (از این‌ها در دنیا) چشم‌پوشی می‌کند.'),
(4303, 42, 31, 'و شما در زمین درمانده‌کننده‌ی (خدا و خداییان) نیستید و جز خدا برای شما هیچ سرپرست و یاوری (هم) نیست.'),
(4304, 42, 32, 'و از نشانه‌های (ربانی) او کشتی‌های دریانورد کوه‌‌پیکر -چون پرچم‌ها- در دریاست.'),
(4305, 42, 33, 'اگر بخواهد باد را ساکن می‌گرداند، پس کشتی‌ها همچنان بر روی آن ]:دریا[ ساکن بمانند. همانا در این (امر) برای هر شکیبای بسیار شکرگزاری نشانه‌هاست.'),
(4306, 42, 34, 'یا به (سزایِ) آنچه (کشتی‌نشینان) مرتکب شده‌اند هلاکشان می‌کند و از بسیاری از آنها (اکنون) درمی‌گذرد.'),
(4307, 42, 35, 'و (تا) آنان را که در آیات ما مجادله می‌کنند، نشانه نهد. ایشان را هیچ گریزگاهی نیست.'),
(4308, 42, 36, 'پس آنچه به شما داده شده، برخورداری (از کالای) زندگی دنیاست و آنچه پیش خداست برای کسانی که ایمان آورده و به پروردگارشان اعتماد دارند بهتر و پایدارتر است.'),
(4309, 42, 37, 'و کسانی که از گناهان بزرگ‌تر و زشتکاری‌های تجاوزگر خود را دور می‌دارند و چون به خشم در می‌آیند، (هم)آنان درمی‌گذرند.'),
(4310, 42, 38, 'و کسانی که (ندای) پروردگارشان را پاسخ مثبت داده و نماز بر پا داشته و کارشان در میانشان مشورت است و از آنچه روزیشان داده‌ایم انفاق می‌کنند.'),
(4311, 42, 39, 'و کسانی که چون ستم برایشان رسد، (هم)ایشان بی‌قرار، کوشا و جویای یاری می‌باشند.'),
(4312, 42, 40, 'و جزای بدی، بدی‌ای همانند آن است. پس هر کس درگذرد و نیکوکاری کند، پاداش او بر خداست. بی‌گمان او ستمگران را دوست نمی‌دارد.'),
(4313, 42, 41, 'و کسانی که پس از ستم (دیدنِ) خود، با کوششی همی یاری جویند (و انتقام گیرند) پس راهی [:نکوهشی] بر ایشان نیست.'),
(4314, 42, 42, 'راه (نکوهش) تنها بر کسانی است که به مردم ستم می‌کنند و در زمین (و زمینه‌ی تکلیف) بناحق تجاوز می‌نمایند. اینان برایشان عذابی دردناک است.'),
(4315, 42, 43, 'و هر که همواره صبر کند و (از تجاوز دیگران) چشم پوشد، بی‌چون این (خویشتن‌‌داری، حاکی) از اراده‌ی قوی (در) کارهاست.'),
(4316, 42, 44, 'و هر که را خدا بی‌راه کند، برای او هیچ سرپرستی پس از او نیست. و ستمگران را می‌بینی که چون عذاب را بنگرند گویند: «آیا راهی برای بازگشت (به دنیا) هست‌؟»'),
(4317, 42, 45, 'و آنان را در حالت ذلت و فروتنی می‌بینی (که) بر آتش نمایش داده می‌شوند، در حالی‌که زیر چشمی می‌نگرند. و کسانی که گرویدند گفتند: «زیانکاران - بی‌گمان - کسانی‌اند که روز قیامت خودهاشان و کسانشان را زیان کرده‌اند.» هان! ستمکاران همواره در عذابی پایدارند.'),
(4318, 42, 46, 'و جز خدا برای آنان اولیای دیگری نبوده (که) یاریشان کنند. و هر کس را خدا بی‌راه گذارد پس‌هیچ راهی برای او نیست.'),
(4319, 42, 47, 'پیش از آنکه روزی فرا رسد (که) از جانب خدا برگشتی ندارد، پروردگارتان را به‌راستی اجابت کنید. در آن هنگام (و هنگامه) نه برای شما بازگشت و پناهی و نه برایتان (مجال) هیچ (انگار و) انکاری است.'),
(4320, 42, 48, 'پس اگر روی برتابند، ما تو را بر آنان (به عنوان) نگهبان نفرستاده‌ایم (و) بر عهده‌ی تو جز رسانیدن پیام رسا‌(ی رسالتی) نیست‌. و ما همواره چون رحمتی از جانب خود به انسان بچشانیم، بدان شاد (و سرمست) گردد و چون به (سزای) دستاورد پیش‌‌فرستاده‌شان به آنان بدی در رسد، انسان بی‌گمان پس از آن ناسپاس و کفران‌کننده است.'),
(4321, 42, 49, 'فرمانرواییِ (مطلق) آسمان‌ها و زمین تنها از خداست‌؛ هر چه بخواهد می‌آفریند؛ برای‌هر کس بخواهد فرزندِ دختر و برای هرکس بخواهد فرزندِ پسر می‌بخشد.'),
(4322, 42, 50, 'یا برایشان پسران و دخترانی (توأمان) با یکدیگر می‌گرداند و هر که را بخواهد عقیم می‌سازد و همواره اوست بسی دانای بسیار توانا.'),
(4323, 42, 51, 'و هیچ بشری را نرسیده است که خدا با او سخن گوید، جز به وحی، بدون پرده و حجاب یا از فراسوی حجابی. یا فرستاده‌ای بفرستد و به اذن او هر چه بخواهد وحی کند. به‌راستی اوست بلندمرتبه‌ا‌ی سنجیده‌کار.'),
(4324, 42, 52, 'وهمین‌گونه (جهان‌شمول و پایدار) از امر خودمان سوی تو روحی (جان‌فزا) وحی کردیم. تو نمی‌دانسته‌ای نه (این) کتاب و نه (این) ایمان چیست‌؟ ولی آن را نوری گردانیدیم که هر که از بندگان خود را بخواهیم با آن راه می‌نماییم. و به‌راستی تو همواره سوی راهی راست هدایت می‌کنی.'),
(4325, 42, 53, 'راه (همان) خدایی که آنچه در آسمان‌ها و آنچه در زمین است تنها از آنِ اوست. هان! همه‌ی کارها و (همه‌ی) چیزها تنها سوی خدا بازمی‌گردند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حم.'),
(4327, 43, 2, 'سوگند به (این) کتاب روشنگر.'),
(4328, 43, 3, 'ما بی‌گمان آن را قرآنی روشن‌‌بیان نهادیم، شاید شما عقل‌هاتان را به کار گیرید.'),
(4329, 43, 4, 'و همواره این (قرآن)، در کتاب اصلی (علم ربّانی) نزد ما بسی والا و پر حکمت و فشرده است.'),
(4330, 43, 5, 'آیا پس به (صِرفِ) اینکه شما قومی مسرف بوده‌اید (باید) این یادواره را از شما باز داریم‌؟'),
(4331, 43, 6, 'و چه بسیار پیامبرانی بزرگ (که‌) در (میان) پیشینیان روانه کردیم.'),
(4332, 43, 7, 'و هیچ پیامبر بزرگی سوی ایشان نیاید، مگر اینکه او را به مسخره می‌گرفته‌اند.'),
(4333, 43, 8, 'پس حمله‌‌ورتر از اینان را به هلاکت رسانیدیم و همانند (و نماد) پیشینیانِ گذشته است.'),
(4334, 43, 9, 'و بی‌گمان اگر از آنان بپرسی: «آسمان‌ها و زمین را چه کسی آفرید؟» به‌راستی همواره گویند: «خدای عزیز بس دانا آفریدشان.»'),
(4335, 43, 10, 'کسی که زمین را برای شما گهواره‌ای گردانید، و برای شما در آن راه‌هایی هموار نهاد؛ شاید راه یابید.'),
(4336, 43, 11, 'و کسی که آبی به‌تدریج و به‌اندازه از آسمان فرو فرستاد. پس با آن، شهری مرده را زنده کردیم. (بدانید که) همین گونه (از گورها) بیرون آورده می‌شوید.'),
(4337, 43, 12, 'و کسی که تمامی جُفت‌ها را آفرید و برای شما از کشتی‌ها و حیوانات رام آنچه را سوار می‌شوید قرار داد،'),
(4338, 43, 13, 'تا بر پشت‌های آن(ها) قرار و آرامش گیرید. سپس چون بر آن(ها) نشستید، نعمت پروردگارتان را یاد کنید و بگویید: «پاک است کسی که این(ها) را برای ما رام کرد، حال آنکه ما را یارای این گونه نزدیک کردن (و بهره‌گیری از) آن نبوده است.»'),
(4339, 43, 14, '« و به‌راستی ما بی‌گمان سوی پروردگارمان بازخواهیم گشت.»'),
(4340, 43, 15, 'و برای او بعضی از بندگانش را جزئی [:چون فرزند و شریک] قرار دادند. به‌راستی که انسان بسیار ناسپاسی آشکارگر است.'),
(4341, 43, 16, 'یا از آنچه می‌آفریند، برای خویش دخترانی برگرفته، و شما را با (دادن) پسران (بر خود) برگزیده است‌؟'),
(4342, 43, 17, 'و هنگامی که یکی از آنان را به آنچه به رحمان نسبت می‌دهد خبر دهند، چهره‌ی او (دگرگون و) سیاه می‌گردد، در حالی که خشم و تأسف خود را فروبرنده است.'),
(4343, 43, 18, 'آیا و کسی (را شریک خدا می‌کنند) که همواره در زر و زیور پرورش می‌یابد، در حالی که برابر درگیرکنندگانْ آشکارکننده(ی خود و خودی‌هایش) نیست؟'),
(4344, 43, 19, 'و فرشتگانی را که خود، بندگان رحمانند، مادینه (و دختران او) قرار دادند. آیا در خلقت آنان حضور داشتند؟ گواهی (گزاف) ایشان به زودی نوشته می‌شود و (از آن) پرسیده می‌شوند.'),
(4345, 43, 20, 'و گفتند: «اگر رحمان می‌خواست، ما آنها را نمی‌پرستیدیم.» آنان به این (دعوی) هیچ دانایی ندارند (و) جز حدسی به گزاف نمی‌زنند.'),
(4346, 43, 21, 'یا به آنان پیش از آن (قرآن) کتابی داده‌ایم؛ پس ایشان بدان تمسک جویندگانند؟'),
(4347, 43, 22, 'بلکه گفتند: «ما بی‌گمان پدرانمان را بر روشی (هماهنگ) یافتیم و ما همچنان بر پی آثارشان هدایت‌‌یافتگانیم.»'),
(4348, 43, 23, 'و بدین گونه در هیچ گروهی پیش از تو هشداردهنده‌ای نفرستادیم، مگر آنکه بی‌بندوبارهای غرق در نعمت‌ها گفتند: «ما پدران خود را بر روشی (هماهنگ در بت‌پرستی) یافته‌ایم و ما از پی ایشان راه‌یافته‌ایم.»'),
(4349, 43, 24, 'گفت‌: «آیا و هر چند من راهوارتر از آنچه پدرانتان را بر آن یافته‌اید برایتان بیاورم‌؟» گفتند: «ما (نسبت) به آنچه بدان فرستاده شده‌اید بی‌چون کافریم.»'),
(4350, 43, 25, 'پس، از آنان انتقام گرفتیم. پس بنگر فرجام تکذیب‌کنندگان چگونه بوده است.'),
(4351, 43, 26, 'و چون ابراهیم به پدر (تربیتی) خود و قومش گفت: «من همواره از آنچه و آن‌گونه که می‌پرستید بیزارم.»'),
(4352, 43, 27, '«مگر آن که بر فطرت (توحیدی)ام آفرید. پس (هم) او مرا همواره به زودی راهنمایی خواهد کرد.»'),
(4353, 43, 28, 'و آن (فطرت توحیدی) را در پی بازماندگانش کلمه‌ای استوار ]: درست و ثابت[ قرار داد، شاید آنان (به خدا) بازگردند.'),
(4354, 43, 29, 'بلکه اینان و پدرانشان را برخوداری‌ای دادم، تا حقیقت و فرستاده‌ای آشکارگر سویشان آمد.'),
(4355, 43, 30, 'و چون آن حقیقت سویشان آمد، گفتند: «این افسونی است، و ما بی‌گمان به آن کافرانیم.»'),
(4356, 43, 31, 'و گفتند: «چرا این قرآن بر مردی بزرگ‌(منش) از (آن) دو مجتمع [:مکه وطائف] فرود نیامده است‌؟»'),
(4357, 43, 32, 'آیا (هم) ایشان رحمت پروردگارت را تقسیم می‌کنند؟ ماییم که (‌وسایل) معاشِ آنان را در زندگی دنیا میانشان تقسیم کرده‌ایم و برخی از آنان را درجاتی (مادی و نه روحانی) بالاتر از بعضی (دیگر) داده‌ایم، تا بعضی از آنها بعضی (دیگر) را به خدمت برگیرند. و رحمت پروردگارت از آنچه آنان می‌اندوزند بهتر است.'),
(4358, 43, 33, 'و اگر (چنان) نبود که مردم (در انکار خدا) امّتی واحد می‌گشتند، همواره برای خانه‌هایشان - برای کسانی‌که به رحمان کفر می‌ورزند - سقف‌هایی از نقره و نردبان‌هایی که بر آنها آشکار گردند می‌نهادیم.'),
(4359, 43, 34, 'و برای خانه‌هاشان نیز درها و تخت‌هایی که بر آنها تکیه زنند، مقرر می‌داشتیم؛'),
(4360, 43, 35, 'و زر و زیورهایی (دیگر را نیز). و همه‌ی این‌ها همواره جز متاع زندگی دنیا نیست و آخرت پیش پروردگارت تنها برای پرهیزگاران است.'),
(4361, 43, 36, 'و هر کس از یاد (خدای) رحمان دل بگرداند، برایش شیطانی بر می‌گماریم؛ پس برای وی قرین و همنشین است.'),
(4362, 43, 37, 'و بی‌گمان آنان ایشان را همواره از راه (خدا) باز می‌دارند، حال آنکه می‌پندارند راه‌‌یافتگانند.'),
(4363, 43, 38, 'تا آن‌گاه که او (با دمسازش) به حضورمان آید (به شیطان) گوید: «ای کاش میان من و تو، فاصله‌ی خاور و باختر بود. پس چه بد دمسازی (هستی)!»'),
(4364, 43, 39, 'و امروز هرگز شرکت شما در عذاب بی‌گمان سودی برایتان ندارد؛ چون (هم‌سان) ستم کردید.'),
(4365, 43, 40, 'آیا پس تو کران را می‌شنوانی، یا نابینایان و کسی را که همواره در گمراهی آشکارگری بوده راه می‌نمایی‌؟'),
(4366, 43, 41, 'پس اگر ما تو را حتماً (از دنیا) می‌بریم، همواره از آنان انتقام‌کشندگانیم.'),
(4367, 43, 42, 'یا (اگر) آنچه را به آنان وعده داده‌ایم به‌راستی به تو نشان دهیم‌، همواره ما بر آنان قدرتمندانیم.'),
(4368, 43, 43, 'پس به (وسیله‌ی) آنچه سوی تو وحی شده است با کوشش، (خود و دیگران را از بی‌راهی‌ها به راه آر و) نگهدار، که تو همواره بر راهی راست استواری.'),
(4369, 43, 44, 'و به‌راستی این (قرآن) برای تو و برای قوم تو به‌درستی یادواره‌ای (بزرگ) است و در آینده‌ای دور(از آن) پرسیده خواهید شد.'),
(4370, 43, 45, 'و از رسولان ما - که پیش از تو گسیل داشتیم - جویا شو (که) آیا در برابر (خدای) رحمان، خدایانی که مورد پرستش قرار گیرند مقرّر داشته‌ایم‌؟'),
(4371, 43, 46, 'و همانا موسی را با نشانه‌های خویش سوی فرعون و فرعونیان روانه کردیم. پس گفت: «من به راستی فرستاده‌ی پروردگار جهانیانم.»'),
(4372, 43, 47, 'پس چون آیات ما را برایشان آورد، ناگهان ایشان به آنها (همی) می‌خندند.'),
(4373, 43, 48, 'و ما هیچ نشانه‌ای (ربانی) به ایشان نمی‌نماییم، مگر اینکه آن از (نشانه) همانندش بزرگ‌تر است‌. و به عذاب گرفتیمشان، تا مگر (به راه) برگردند.'),
(4374, 43, 49, 'و گفتند: «هان ای فسونگر! پروردگارت را - به (پاس) آنچه با تو عهد کرده - برایمان بخوان، (که) ما به‌راستی (در این صورت) بی‌گمان راه‌یافتگانیم.»'),
(4375, 43, 50, 'پس چون (پرده‌ی) عذاب را از آنها برگرفتیم، ناگهان (هم)آنان پیمان می‌شکنند.'),
(4376, 43, 51, 'و فرعون در میان گروهش ندا در داد (و) گفت: «ای مردمان (کشور) من! آیا پادشاهی مصر و این نهرها - که از زیر (کاخ‌ها و باغ‌های) من روان است - از آنِ من نیست‌؟ پس مگر نمی‌نگرید؟»'),
(4377, 43, 52, '«یا من بهترم از این کس ]:موسی[ که (هم) او بسی بی‌مقدار است و نزدیک به این (هم) نیست (که خود را و افکارش را) نشان دهد؟»'),
(4378, 43, 53, '«پس چرا بر او دستبندهایی زرّین (از سوی خدایش) افکنده نشده‌؟ یا با او فرشتگانی پیوسته نیامده‌اند؟»'),
(4379, 43, 54, 'پس قوم خود را سبک گرفت، تا پیرویش کردند. آنان بی‌گمان مردمانی منحرف بوده‌اند.'),
(4380, 43, 55, 'پس هنگامی‌که ما را به (خشم و) اندوه درآوردند، از آنان انتقام گرفتیم. پس همه‌ی آنان را غرق کردیم.'),
(4381, 43, 56, 'در نتیجه آنان را گذشته‌‌ای و نمادی برای آیندگان نهادیم.'),
(4382, 43, 57, 'و هنگامی که پسر مریم (برایشان به عنوان) مثال و نمونه‌ای آورده شد، به ناگاه قوم تو از او به شدت منصرف (و منحرف) می‌شوند.'),
(4383, 43, 58, 'و گفتند: «آیا معبودان ما بهترند یا او؟» آن مثال را جز از راه جدل برای تو نزدند، بلکه آنان مردمی ستیزه‌جو و جدل‌پیشه‌اند.'),
(4384, 43, 59, '(عیسی) جز بنده‌ای نیست که بر وی منّت نهادیم و او را برای فرزندان اسرائیل نماد و نمودی (وحیانی) گردانیده‌ایم.'),
(4385, 43, 60, 'و اگر بخواهیم بی‌گمان از شما فرشتگانی - که در (روی) زمین جانشین (یکدیگر) باشند - قرار می‌دهیم.'),
(4386, 43, 61, 'و همانا فرود آمدن ملائکه، علمی است برای ساعت (آخرین). پس زنهار در آن تردید نکنید و مرا پیروی کنید؛ این راهی است راست!'),
(4387, 43, 62, 'و مبادا شیطان شما را (از حق) جلوگیری کند (که) همانا او برای شما دشمنی آشکارگر است.'),
(4388, 43, 63, 'و چون عیسی با دلایل آشکار آمد، گفت: «به‌راستی شما را حکمت آوردم و تا درباره‌ی بعضی از آنچه در آن اختلاف می‌کنید برایتان توضیح دهم. پس از خدا پروا کنید و فرمانم ببرید.»'),
(4389, 43, 64, '«همانا خداست (که) او پروردگار من و پروردگار شماست. پس او را بپرستید؛ این راهی است بس راست.»'),
(4390, 43, 65, 'پس از میانشان، احزاب (ستمگر) دست به اختلاف زدند. پس وای از عذاب روزی دردناک برای کسانی که ستم کردند.'),
(4391, 43, 66, 'آیا جز (این) انتظار می‌برند و می‌نگرند، که ساعت (قیامت) - در حالی که حدس نمی‌زنند - ناگهان آنان را در رسد؟'),
(4392, 43, 67, 'در آن روز، همگی دوستان - جز پرهیزگاران - بعضیشان دشمن بعضی دیگرند.'),
(4393, 43, 68, 'ای بندگان من! امروز بر شما بیمی نیست و نه شما غمگین می‌شوید:'),
(4394, 43, 69, 'کسانی که به آیاتمان ایمان آورده و تسلیم (ما) بوده‌اند.'),
(4395, 43, 70, 'شما با همگنانتان به شادمانی داخل بهشت شوید.'),
(4396, 43, 71, 'سینی‌هایی از طلا و جام‌هایی بر گرد آنان گردانده می‌شود و در آنجا آنچه را دل‌ها(شان) اشتها کند و دیدگان‌(شان) لذت برد، هست. و شما در آن جاودانید.'),
(4397, 43, 72, 'و آن است همان بهشتی که به آنچه می‌کرده‌اید میراث یافتید.'),
(4398, 43, 73, 'در آنجا برای شما میوه‌هایی (فراوان) است (که) از آنها می‌خورید.'),
(4399, 43, 74, 'مجرمان بی‌گمان در عذاب جهنّم ماندگارند.'),
(4400, 43, 75, 'آن (عذاب) از آنان هرگز سستی نمی‌یابد و آنان در آنجا (از سستی آن) نومیدند.'),
(4401, 43, 76, 'و ما به ایشان ستم نکردیم، بلکه خودشان ستمکاران بوده‌اند.'),
(4402, 43, 77, 'و ندا کردند: «ای مالک! باید پروردگارت جان ما را بستاند.» پاسخ داد: «همانا شما ماندگارید.»'),
(4403, 43, 78, 'همواره حق را برایتان آوردیم، لیکن بیشترتان حق را بسی ناپسند می‌‌دارید.'),
(4404, 43, 79, 'یا در کاری (بد) ابرام و استقامت ورزیده‌اند؟ پس ما (نیز در مکافاتشان) همواره استقامت‌کنندگانیم.'),
(4405, 43, 80, 'یا می‌پندارند که ما بی‌گمان رازشان و نجوایشان را نمی‌شنویم‌؟ چرا! حال آنکه فرستادگان ما نزدشان (اعمال پیدا و نهانشان را یکسان) می‌نویسند.'),
(4406, 43, 81, 'بگو: «اگر برای رحمان فرزندی بود، پس من نخستین پرستندگانم.»'),
(4407, 43, 82, 'پروردگار آسمان‌ها و زمین (و) پروردگار عرش، از آنچه توصیفش می‌کنند منزّه است.'),
(4408, 43, 83, 'پس آنان را رها کن، تا (در یاوه‌گویی خود) فرو روند و بازی کنند، (و) تا (آن) روزشان را که بدان وعده داده می‌شوند دریابند.'),
(4409, 43, 84, 'و اوست کسی که در آسمان خداست و در زمین خداست و او حکیم بسیار داناست.'),
(4410, 43, 85, 'و مبارک (و خجسته) است کسی که فرمانروایی آسمان‌ها و زمین و آنچه میان آن دو است از آنِ اوست و علم ساعت (پایانی) نزد اوست‌. و تنها سوی او برگردانیده می‌شوید.'),
(4411, 43, 86, 'و کسانی (را) که به جای او می‌خوانند (و می‌پرستند، آنان) اختیار شفاعت(شان) را ندارند، مگر آن کسانی که آگاهانه به حقّ گواهی دادند.'),
(4412, 43, 87, 'و اگر هر آینه از آنان بپرسی: «چه کسی ایشان را خلق کرده‌؟» بی‌چون و به‌راستی خواهند گفت: «خدا.» پس چگونه و به کجا (از حقیقت) بازگردانیده می‌شوند؟»'),
(4413, 43, 88, 'و گفته‌اش (که): «پروردگارم! اینان گروهی‌اند که ایمان نمی‌آورند.»'),
(4414, 43, 89, 'پس از ایشان روی گردان و بگو: «سلام!‌» پس در آینده‌ای دور خواهند دانست.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حم.'),
(4416, 44, 2, 'سوگند به کتاب روشنگر.'),
(4417, 44, 3, 'همانا ما آن را در شبی پر برکت نازل کردیم (و) ما همواره هشداردهنده بوده‌ایم.'),
(4418, 44, 4, 'در آن (شب) هر امر فشرده‌ای، جدا (و از هم باز) می‌شود.'),
(4419, 44, 5, 'کاری [:فیصله‌ای] را از جانب ما. همواره ما فرستنده(ی پیامبران و فرشتگان) بوده‌ایم:'),
(4420, 44, 6, 'رحمتی از پروردگارت. او همانا او بسیار شنوای بس داناست.'),
(4421, 44, 7, 'پروردگار آسمان‌ها و زمین و آنچه میان آن دو است؛ اگر یقین داشته‌اید.'),
(4422, 44, 8, 'معبودی جز او نیست‌؛ او زنده می‌کند و (همو) می‌میراند؛ پروردگار شما و پروردگار پدران نخستینتان.'),
(4423, 44, 9, 'بلکه آنان در (ژرفای) شکی، بازی می‌کنند.'),
(4424, 44, 10, 'پس در انتظار روزی باش که آسمان دودی نمایانگر بر آوَرَد،'),
(4425, 44, 11, '(که) مردم را فرا گیرد، این است عذابی پر درد.'),
(4426, 44, 12, '(گویند:) «پروردگارمان! این عذاب را از ما بازگردان. بی‌گمان ما ایمان‌آورندگانیم.»'),
(4427, 44, 13, 'آنان را کجا (جای آن) یادواره باشد، حال آنکه به‌راستی برایشان پیامبری روشنگر آمده است.'),
(4428, 44, 14, 'سپس، از او روی برتافتند و گفتند: «تعلیم‌یافته‌ای دیوانه است.»'),
(4429, 44, 15, 'بی‌چون ما اندکی باز دارنده‌ی عذابیم (ولی) همواره شما برگشت‌کنندگان (به همان گناهان) می‌باشید.'),
(4430, 44, 16, 'روزی (در‌رسد) که دست به بزرگ‌ترین حمله می‌زنیم. همانا ما انتقام‌‌گیرندگانیم.'),
(4431, 44, 17, 'و بی‌گمان، پیش از آنان قوم فرعون را آزمونی آتشین نمودیم، حال آنکه پیامبری با کرامت برایشان آمد.'),
(4432, 44, 18, '(به آنان گفت:) «بندگان خدا را به من باز سپارید. همانا من برای شما فرستاده‌ای امینم.»'),
(4433, 44, 19, '«و اینکه بر خدا برتری و (زبردستی) ننمایید. من همانا برای شما آورنده‌ی سلطه‌ای بیانگرم .»'),
(4434, 44, 20, '«و به‌راستی من به پروردگار خود و پروردگار شما پناه بردم، (از این) که مرا سنگباران کنید.»'),
(4435, 44, 21, '«و اگر برای من ایمان (به خدا) نمی‌آورید، پس از من بس کناره بگیرید.»'),
(4436, 44, 22, 'پس پروردگار خود را خواند که: «همانا اینان گروهی مجرمانند.»'),
(4437, 44, 23, '(فرمود:) «پس بندگانم را شبانه ببر، (که) همواره شما مورد پیگردید.»'),
(4438, 44, 24, '«و دریا را حال آنکه آرام است پشت سر بگذار. بی‌گمان آنان سپاهیانی غرق‌شدگانند.»'),
(4439, 44, 25, '(وه!) چه باغ‌ها و چشمه‌سارانی (که آنها بعد از خود) بر جای نهادند.'),
(4440, 44, 26, 'و کشتزارهایی و جایگاهی گرامی.'),
(4441, 44, 27, 'و نعمتی نقمت‌‌وار که از آن با بی‌بندوباری و خوشحالی برخوردار بوده‌اند.'),
(4442, 44, 28, '(آری) این چنین (بود) و آنها را به گروهی دیگر به میراث دادیم.'),
(4443, 44, 29, 'پس آسمان و زمین بر آنان زاری نکردند. و مهلت‌یافتگان (هم) نبوده‌اند.'),
(4444, 44, 30, 'و به‌راستی، فرزندان اسرائیل را از عذاب خفت‌آور به‌درستی رهانیدیم؛'),
(4445, 44, 31, 'از (دستِ) فرعون که بی‌امان سرآمدی از اسراف‌گران بود.'),
(4446, 44, 32, 'و بی‌گمان آنان را بر (پایه‌ی) علمی (مطلق) بر جهانیان (در ظاهر) به‌راستی برگزیدیم.'),
(4447, 44, 33, 'و از نشانه‌ها(ی ربّانی) آنچه را که در آن آزمایشی آتشین (و) آشکارگر بود، بدیشان دادیم.'),
(4448, 44, 34, 'همواره اینان [:کافران] همی گویند:'),
(4449, 44, 35, '«جز مرگِ نخستین، دیگر (مرگی) نیست و ما پراکنده‌‌شدگان (از گورهامان پس از مرگمان) نیستیم.»'),
(4450, 44, 36, '«اگر (از) راستان بوده‌اید، پس پدران ما را (باز پس) بیاورید.»'),
(4451, 44, 37, 'آیا ایشان بهترند، یا قوم تُبَّع و کسانی که پیش از آنها بودند؟ آنان را هلاک کردیم. بی‌گمان ایشان (از) مجرمان بوده‌اند.'),
(4452, 44, 38, 'و ما آسمان‌ها و زمین و آنچه را که میان آن دو است، بازی‌کنان نیافریدیم‌.'),
(4453, 44, 39, 'ما آن دو را جز به حق نیافریدیم ولی بیشترشان نمی‌دانند.'),
(4454, 44, 40, 'همواره روز جداسازی آخرین وقت مقرر (و جایگاه و پایگاه) همه‌ی آنهاست.'),
(4455, 44, 41, 'روزی که هیچ دوستی از هیچ دوستی (توان) هیچ‌گونه کفایتی را ندارد و نه آنان (به‌وسیله‌ای دیگر) یاری می‌شوند؛'),
(4456, 44, 42, 'مگر کسی را که خدا(یش) رحمت کرده است. همانا اوست‌، (هم)او عزیز رحمتگر بر ویژگان.'),
(4457, 44, 43, 'همواره درخت زقّوم،'),
(4458, 44, 44, 'خوراک گناه‌پیشه است.'),
(4459, 44, 45, 'چون لِرد داغ روغن زیتون در شکم‌ها می‌جوشد؛'),
(4460, 44, 46, 'همانند جوشش (مایعی) سخت داغ.'),
(4461, 44, 47, 'او [:گناه‌پیشه] را بگیرید؛ پس با قوت و شدت و اهانت گریبانش را برگیرید و در میان دوزخش بکشانید.'),
(4462, 44, 48, 'سپس از عذابِ مایعی داغ بر سرش فرو ریزید.'),
(4463, 44, 49, '«بچش (که) تو، (همین) تو بی‌چون عزیزی بس گرامی هستی!»'),
(4464, 44, 50, 'بی‌گمان این است (همان) چیزی که درباره‌ی آن تردید می‌کردید.'),
(4465, 44, 51, 'به‌راستی پرهیزگاران در جایگاهی امن هستند.'),
(4466, 44, 52, 'در بوستان‌هایی (با درختان) سر به هم آورده و (کنار) چشمه‌سارها،'),
(4467, 44, 53, 'از پرنیانی نازک و دیبایی ستبر می‌پوشند، حال آنکه برابر یکدیگرند.'),
(4468, 44, 54, '(آری،) چنان است و آنها را با حوریان درشت‌چشم همسر گردانیدیم.'),
(4469, 44, 55, 'در آنجا هر میوه‌ای را (که بخواهند) آسوده‌خاطر (همی) می‌خوانند (و می‌خواهند).'),
(4470, 44, 56, 'در آنجا جز مرگِ نخستین، مرگی نخواهند چشید، و (خدا) آنها را از عذاب دوزخ نگاه داشته است.'),
(4471, 44, 57, 'حال آنکه (این) بخششی است از جانب پروردگارت. این است همان کامیابی راحت بزرگ.'),
(4472, 44, 58, 'پس تنها آن (قرآن) را به زبان تو آسان گردانیدیم، شاید با کاوشی پند گیرند.'),
(4473, 44, 59, 'پس بس مراقب باشید (که) اینان بی‌گمان همواره مراقبند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حم.'),
(4475, 45, 2, 'فرو فرستادن تدریجی این کتاب، از جانب خدای عزیزِ حکیم است.'),
(4476, 45, 3, 'به‌راستی در آسمان‌ها و زمین، برای مؤمنان به‌درستی نشانه‌هایی است.'),
(4477, 45, 4, 'و در آفرینشتان و آنچه از (انواع) جنبنده‌ها (در زمین و آسمان) پراکنده می‌گرداند، برای مردمی که یقین می‌آورند نشانه‌هایی است.'),
(4478, 45, 5, 'و (نیز در) پیاپی آمدن شب و روز، و آنچه خدا، روزی از آسمان فرود آورده، پس به (وسیله‌ی) آن، زمین را پس از مرگش زنده گردانیده و (همچنین در) گوناگون کردن و جابه‌جا نمودن بادها (به هر سو) -برای مردمی که می‌اندیشند- نشانه‌هایی است.'),
(4479, 45, 6, 'این‌هاست نشانه‌های خدا. به حق آنها را بر تو می‌خوانیم، پس بعد از خدا و نشانه‌هایش به کدام حدیث [:تازه‌ای] خواهند گروید؟'),
(4480, 45, 7, 'وای برای هر دروغزن گناه‌پیشه‌ای!'),
(4481, 45, 8, 'آیات خدا را که بر او خوانده می‌شود می‌شنود، سپس به حال تکبّر، چنان که گویی آن را نشنیده (بر آنچه خود می‌خواهد و می‌گوید) سماجت می‌ورزد. پس او را به عذابی پردرد بشارت ده.'),
(4482, 45, 9, 'و هنگامی که از نشانه‌های ما چیزی بداند، آنها را به ریشخند گیرد. آنان برایشان عذابی خفت‌بار است.'),
(4483, 45, 10, 'از پشت سر آنان دوزخ است و نه آنچه را اندوخته و نه آن اولیایی را که غیر از خدا اختیار کردند، پشیزی به کارشان نمی‌آیند. و برایشان عذابی بزرگ است.'),
(4484, 45, 11, 'این رهنمودی (بزرگ) است‌؛ و کسانی که آیات پروردگارشان را انکار کردند، برایشان عذابی از اضطرابی بس دردناک است.'),
(4485, 45, 12, 'خدایی است که دریا را برایتان رام گردانید، تا کشتی‌ها در آن به فرمان‌اش روان شوند و تا از فضلش جویا گردید و شاید شکرگزارید.'),
(4486, 45, 13, 'و آنچه را در آسمان‌ها و آنچه را در زمین است، به سود شما رام کرد؛ حال آنکه همه(ی این رامش) از اوست. همواره در این (جریان) برای مردمانی که می‌اندیشند بی‌گمان نشانه‌هایی است.'),
(4487, 45, 14, 'به کسانی که ایمان آوردند بگو تا از کسانی که به روزها ی (پیروزی حق بر باطل) خدا امید ندارند (اکنون) درگذرند، تا خدا گروهی را به سبب آنچه مرتکب می‌شده‌اند به مجازات رساند.'),
(4488, 45, 15, 'هر که (کار) شایسته‌ای کند، به سود خودش و هر که بدی کند بر زیان خودش است. سپس تنها سوی پروردگارتان برگردانیده می‌شوید.'),
(4489, 45, 16, 'و بی‌گمان همواره فرزندان اسرائیل را کتاب [:تورات] و حکم [:سیاست] و پیامبری [:شریعت] دادیم و از چیزهای پاکیزه روزیشان کردیم و آنان را بر جهانیان [:از گذشتگان و همزمانانشان] برتری دادیم.'),
(4490, 45, 17, 'و روشنی‌هایی از امر [:دین‌] به آنان دادیم. پس جز بعد از آنکه علم برایشان (حاصل) آمد- از روی ظلم و رقابت میان خودشان- دستخوش اختلاف نشدند. به‌راستی پروردگارت روز قیامت میانشان درباره‌ی آنچه در آن اختلاف می‌کرده‌اند، داوری خواهد کرد.'),
(4491, 45, 18, 'سپس تو را بر راهواره‌ای (بس روشن) از امر (دین) نهادیم. پس آن را پیروی کن و هوس‌های کسانی را که نمی‌دانند پیروی مکن.'),
(4492, 45, 19, 'بی‌چون آنان در برابر خدا از تو هرگز هیچ‌گونه کفایت و حمایتی نمی‌کنند (و به هیچ‌وجه به کار تو نمی‌آیند ) و ستمگران، بی‌گمان بعضیشان پشتوانه‌ی بعضی (دیگر)ند. و خدا ولی و پشتوانه‌ی پرهیزگاران است.'),
(4493, 45, 20, 'این (کتاب) برای مردم، دیدگاه‌هایی و برای قومی که یقین می‌آورند، هدایت و رحمتی بزرگ است.'),
(4494, 45, 21, 'یا کسانی که مرتکب کارهای بد شده‌اند پنداشته‌اند که آنان را مانند کسانی قرار می‌دهیم که ایمان آورده و کارهای شایسته کردند؛ حال آنکه زندگیشان و مرگشان یکسان است‌؟ چه بد داوری می‌کنند!'),
(4495, 45, 22, 'و خدا آسمان‌ها و زمین را به حقّ آفرید و تا هر کسی به موجب آنچه به دست آورده جزا داده شود و اینان ستم (هم) نمی‌شوند.'),
(4496, 45, 23, 'پس آیا دیدی کسی را که معبودش را هوای نفسش برگرفته و خدا او را آگاهانه گمراه گردانیده و بر گوش و دلش مُهر زده و بر دیده‌اش پرده‌ای نهاده‌‌؟ پس آیا بعد از خدا چه کسی او را هدایت خواهد کرد؟ آیا پس (از این) به‌درستی (درستی‌ها را) یاد نمی‌کنید؟'),
(4497, 45, 24, 'و گفتند: «غیر از زندگی دنیای ما (زندگی دیگری) نیست‌؛ می‌میریم و زنده می‌شویم و ما را جز دهر [:طبیعت] هلاک نمی‌کند.» حال آنکه به این (گزافه) هیچ علمی ندارند (و) اینان بجز گمان نمی‌بافند.'),
(4498, 45, 25, 'و هنگامی که آیات ما به روشنی بر آنان خوانده شود، دلیلشان همواره جز این نبوده است که گویند: «اگر (از) راستان بوده‌اید پدرانمان را بیاورید.»'),
(4499, 45, 26, 'بگو: «خدا شما را زنده می‌کند، پس آنگه شما را می‌میراند، سپس شما را فراسوی روز رستاخیز - که شکی مستند در آن نیست - گرد می‌آورد ولی بیشتر مردم نمی‌دانند.»'),
(4500, 45, 27, 'و فرمانروایی آسمان‌ها و زمین تنها از آنِ خداست و روزی که ساعت (قیامت) بر پا شود، در آن هنگام (و هنگامه) باطل‌کنندگان (حق) زیان خواهند دید.'),
(4501, 45, 28, 'و هر امّتی را به زانو در آمده بینی‌؛ هر امّتی سوی کتابش [:کارنامه‌ی اعمالش] فراخوانده می‌شود (و بدیشان می‌گویند): «آنچه را می‌کرده‌اید امروز درمی‌یابید.»'),
(4502, 45, 29, '«این کتاب و نوشته‌ی ماست، که علیه شما حق را بازگو (و افشاگری) می‌کند. ما بی‌چون از آنچه می‌کرده‌اید پیوسته نسخه‌‌برداری می‌کرده‌ایم.»'),
(4503, 45, 30, 'پس امّا کسانی که ایمان آورده و کارهای شایسته‌‌(ی ایمان) کرده‌اند، در نتیجه پروردگارشان آنان را در جوار رحمت خویش داخل می‌گرداند. این همان کامیابی سالم روشنگر است.'),
(4504, 45, 31, 'و امّا کسانی که کافر شدند (به آن‌ها گفته شود:) «پس مگر آیات من بر شما خوانده نمی‌شده پس تکبّر نمودید و مردمی بدکار بوده‌اید؟»'),
(4505, 45, 32, 'و هنگامی که گفته شود: « وعده‌ی خدا همواره حق است و هرگز شکی مستند در ساعت (قیامت) نیست.‌» گویید: «ما نمی‌دانیم ساعت چیست. ما جز اندک گمانی نداریم و از جویندگان یقین (هم) نیستیم.»'),
(4506, 45, 33, 'و (صورت وحقیقت) بدی‌های آنچه کردند، بر آنان پدیدار شد و آنچه را که بدان ریشخند می‌کردند، آنان را سراسر فرا گرفت.'),
(4507, 45, 34, 'و گفته شد: «همان‌گونه که دیدار امروزتان را فراموش کردید، امروز (هم) شما را فراموش می‌کنیم و پناهگاهتان آتش است (و) برای شما هیچ یاورانی (هم) نیستند.»'),
(4508, 45, 35, 'این به‌راستی بدین سبب است، که شما آیات خدا را به ریشخند گرفتید و زندگی دنیا فریبتان داد.» پس امروز نه از این (آتش) بیرون آورده می‌شوند و نه عتاب و سرزنش می‌گردند (که ارزش سرزنش را هم ندارند).'),
(4509, 45, 36, 'پس سپاس تنها از آنِ خداست؛ پروردگار آسمان‌ها و پروردگار زمین و پروردگار جهانیان‌.'),
(4510, 45, 37, 'و در آسمان‌ها و زمین، تنها بزرگی از آنِ اوست و تنها اوست عزیز حکیم.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حم.'),
(4512, 46, 2, 'فرو فرستادن تدریجی این کتاب، از جانب خدای عزیز حکیم است.'),
(4513, 46, 3, '(ما) آسمان‌ها و زمین و آنچه را که میان آن دو است جز به حقّ و (تا) زمانی معیّن نیافریدیم‌. و آنان که کافر شدند، از آنچه هشدار داده شده‌اند رویگردانند.'),
(4514, 46, 4, 'بگو: «آیا آنچه را به جای خدا فرا می‌خوانید دیده (و دریافته)اید؟ به من نشان دهید (که) چه چیزی از زمین را آفریده‌اند، یا (مگر) آنان را در (کار) آسمان‌ها (با خدا) مشارکتی است‌؟ اگر راست می‌گویید، کتابی پیش از این [:قرآن] یا اثر و بازمانده‌ای از دانشی (وحیانی) نزد من آرید.»'),
(4515, 46, 5, 'و چه کسی گمراه‌تر از کسی است، که جز خدا را می‌خواند؛ کسی را که تا روز قیامت (به آنان) پاسخ نمی‌دهد، حال آنکه آنان (هم) از درخواست اینان غافلانند؟'),
(4516, 46, 6, 'و هنگامی که مردمان (در آن هنگامه) گرد هم شوند (اینان) دشمنان آنانند و عبادتشان را انکار می‌کنند.'),
(4517, 46, 7, 'و زمانی که آیاتمان به روشنی بر ایشان خوانده شود، آنان که کفر ورزیدند - هنگامی که حق سویشان آمد - گویند: «این سحری آشکارگر است.»'),
(4518, 46, 8, 'یا می‌گویند: «این (کتاب) را (بر خدا) افترا زده است.‌» بگو: «اگر آن را افترا زده‌ام‌، پس شما از (سوی) خدا برایم مالک چیزی نیستید. او آگاه‌تر است به آنچه در آن (افترا) پر و لبریز (از باطل) می‌کنید. گواهی او میان من و شما بس است، و اوست بسی پوشنده‌ی رحمتگر بر ویژگان.»'),
(4519, 46, 9, 'بگو: «من از (میان) پیامبران، نودرآمدی (بی‌مانند) نبوده‌ام و نمی‌دانم با من و با شما چه معامله‌ای خواهد شد (و) جز آنچه را که به من وحی می‌شود، پیروی نمی‌کنم‌؛ و من جز هشداردهنده‌ای روشنگر (بیش) نیستم.»'),
(4520, 46, 10, 'بگو: «آیا دیدید اگر این (قرآن) از نزد خدا باشد و شما بدان کافر شده باشید و شاهدی از فرزندان اسرائیل بر همانندیِ (وحیانی بودن) آن (با تورات و انجیل) گواهی داده، پس (بدان) ایمان آورده باشد و شما تکّبر نموده باشید (آیا باز هم شما ستمکار نیستید)؟ البته خدا گروه ستمگران را هدایت نمی‌کند.»'),
(4521, 46, 11, 'و کسانی که کافر شدند – برای کسانی که ایمان آوردند – گفتند: «اگر این ایمان خوب بود، آنان بر ما (در آن) بدان پیشی نمی‌گرفتند.» و چون (اینان) بدان هدایت نیافته‌اند، پس به زودی خواهند گفت: «این (خود) گمراهی سابقه‌داری است.»'),
(4522, 46, 12, 'حال آنکه پیش از آن، کتاب موسی، به حال راهبری(شان) و (مایه‌ی) رحمتی (برایشان) بود. و این (قرآن) کتابی است به زبانی آشکارا که تصدیق‌کننده‌(ی آن) است، تا کسانی را که ستم کردند هشدار دهد و برای نیکوکاران مژده‌ای باشد.'),
(4523, 46, 13, 'بی‌گمان کسانی که گفتند: «پروردگار ما خداست‌» سپس (در گفتار و کردارشان) ایستادگی کردند، نه بیمی بر آنان است و نه ایشان غمگین می‌شوند.'),
(4524, 46, 14, 'ایشان بهشتیانند که به پاداش آنچه انجام می‌دادند جاودانه در آن می‌مانند.'),
(4525, 46, 15, 'و انسان را (نسبت) به پدر و مادرش به احسانی (بسیار) سفارش کردیم (که) مادرش با تحمل رنجی شدید به او باردار شد و با تحمل رنجی شدید او را به دنیا آورد، و باربرداشتن و از شیرگرفتن او سی ماه است. تا آن‌گاه که به رشدهای خود دست یافت و به چهل‌‌سالگی رسید. می‌گوید: «پروردگارم! مرا همی پاس‌دار، تا نعمتی را که به من و به پدر و مادرم ارزانی داشته‌ای سپاس گزارم و اینکه کار شایسته‌ای انجام دهم که آن را خوش داری و در (باره‌ی) فرزندانم برایم اصلاح فرمای. همواره من سوی تو روی‌آورده‌ام و به‌راستی من از تسلیم‌شدگان (در برابر تو) می‌باشم.»'),
(4526, 46, 16, 'اینانند کسانی‌که در میان بهشتیان، بهترینِ آنچه را انجام داده‌اند از ایشان - به خوبی - خواهیم پذیرفت و از گناهان کوچکشان در می‌گذریم‌؛ (یعنی) وعده‌ی راستین که بدان وعده داده می‌شده‌اند.'),
(4527, 46, 17, 'و آن کس که به پدر و مادر خود گفت: «اف بر شما! آیا به من وعده می‌دهید که (از گور) برون خواهم شد؟ و حال آنکه همواره پیش از من نسل‌ها سپری (و نابود) شده‌اند.» در حالی که آن دو خدا را به یاری همی طلبند (‌گویند): « وای بر تو! ایمان بیاور. به‌راستی وعده‌‌ی (عذاب) خدا حق است‌.» پس پسر می‌گوید: «این‌ها جز افسانه‌های پراکنده‌ی گذشتگان نخستین نیست.»'),
(4528, 46, 18, 'اینان کسانی هستند که گفته‌ی (خدا) علیه ایشان - همراه با امّت‌هایی از جنّیان و امت‌هایی که همواره پیش از آنان (روزگارانی) به سر بردند - به حقیقت پیوست. بی‌گمان آنان زیانکاران بوده‌اند.'),
(4529, 46, 19, 'و برای هر یک در نتیجه‌ی آنچه انجام داده‌اند درجاتی است و تا (خدا) اعمالشان را بهره‌ای وافی دهد؛ حال آنکه آنان ستم (هم) نمی‌شوند.'),
(4530, 46, 20, 'و (به یاد آور) روزی را که آنهایی که کفر ورزیده‌اند، بر آتش نمایش داده می‌شوند (به آنان گفته شود:) «نعمت‌های پاکیزه‌ی خود را در زندگی دنیاتان (خودخواهانه) از میان بردید و با آنها برخوردار شدید؛ پس امروز به (سزای) آنچه در زمین به ناحق سرکشی می‌نموده‌اید و به آنچه نافرمانی می‌کرده‌اید، به عذاب خفّت‌بار گرفتار شوید.»'),
(4531, 46, 21, 'و (هود) برادر عادیان را به یاد آر، چون قوم خویش را در ریگستان‌ها بیم داد - حال آنکه از روبه‌رویش و از پشت سرش همواره هشداردهندگان گذشته‌اند - که: «جز خدا را مپرستید؛ همواره من بر شما از عذاب روزی بزرگ بیمناکم.»'),
(4532, 46, 22, 'گفتند: «آیا آمده‌ای که ما را از خدایانمان - با دروغ‌پردازی - برانی و برگردانی‌؟ پس اگر از راستان بوده‌ای، آنچه به ما وعده می‌دهی برایمان بیاور.»'),
(4533, 46, 23, 'گفت: «تمامی علم تنها نزد خداست و آنچه را بدان فرستاده شده‌ام به شما به‌درستی می‌رسانم. ولی من شما را گروهی می‌بینم که نادانی می‌کنید.»'),
(4534, 46, 24, 'پس هنگامی که آن (عذاب) را (به صورت) نمادین (چون) ابری سوی وادی‌های خود دیدند، گفتند: «این ابری نمادین است که برای ما باران‌آور است.‌» (هود گفت:) «بلکه آن چیزی است که بدان بسی شتاب می‌کردید. (این ابرنما) بادی است که در آن عذابی پر درد است.»'),
(4535, 46, 25, '«همه چیز را به دستور پروردگارش از بنیان بر می‌کند.» پس چنان شدند که جز جایگاه‌هاشان دیده نمی‌شد. این چنین گروه مجرمان را سزا می‌دهیم.'),
(4536, 46, 26, 'و به‌راستی همچنان در چیزی به آنان امکان دادیم، که به شما در آن (چنان) امکانی ندادیم و برای آنان گوش‌ها و دیده‌ها و دل‌هایی شعله‌‌ور [:نیرومندتر از شما] قرار دادیم و چون به نشانه‌های خدا انکار می‌ورزیدند، نه گوش‌هاشان و نه دیدگانشان و نه دل‌های شعله‌ورشان به هیچ وجه از چیزی بی‌نیازشان نکرد و آنچه بدان ریشخندش می‌کردند آنان را فراگرفت.'),
(4537, 46, 27, 'و بی‌گمان، گروه‌هایی (در) پیرامونتان را بی‌چون هلاک کردیم، و آیات خود را (برایشان) گونه‌گون بیان داشتیم، شاید آنان (از بیراهه‌شان به راه خدا) بازگردند.'),
(4538, 46, 28, 'پس چرا کسانی را که فروتر از خدا را، به منزله‌ی معبودانی، برای تقرّب (به خدا) اختیار کرده بودند، آنان را یاری نکردند، بلکه از آنان پنهان شدند؟ و این بود دروغشان و آنچه به گزاف برمی‌بافته‌اند.'),
(4539, 46, 29, 'و چون تنی چند از جنّیان را سوی تو روانه کردیم، حال آنکه قرآن را به‌خوبی می‌شنوند؛ پس هنگامی که به محضرش رسیدند (به یکدیگر) گفتند: «(خودتان و دیگران را) ساکت کنید.» و چون به انجام رسید، هشداردهندگان سوی قوم خود بازگشتند.'),
(4540, 46, 30, 'گفتند: «ای قوم ما! همانا ما کتابی را شنیدیم. که بعد از موسی نازل شده، حال آنکه تصدیق‌کننده‌ی کتاب‌هایی برابر اوست و سوی حقّ و سوی راهی راست راهبری می‌کند.»'),
(4541, 46, 31, '«ای قوم ما! دعوت‌کننده‌ی (سوی) خدا را اجابت کنید و به او ایمان آرید، تا خدا برخی از گناهانِ دنباله‌دارتان را برایتان بپوشاند و از عذابی پر درد پناهتان دهد.»'),
(4542, 46, 32, '«و هر کس دعوت‌کننده‌ی سوی خدا را اجابت نکند، در زمین درمانده‌کننده‌ی (خدا و خداییان) نیست و پس از او اولیایی ندارد. اینان در گمراهی آشکارگرند.»'),
(4543, 46, 33, 'آیا و ندانستند آن خدایی که آسمان‌ها و زمین را آفرید -حال آنکه در آفریدن آنها درمانده نگردید- می‌تواند مردگان را (نیز) زنده کند؟ آری، همواره اوست که بر همه چیزی بسیار تواناست.'),
(4544, 46, 34, 'و روزی که کافران بر آتش نمایش داده می‌شوند (از آنان پرسیده شود:) «آیا این حق نیست‌؟» گویند: «سوگند به پروردگارمان (که) آری!‌» فرمود: «پس به (سزای) آنچه کفر می‌ورزیده‌اید عذاب را بچشید.»'),
(4545, 46, 35, 'پس چنان که پیامبران اولوالعزم صبر کردند، صبر کن و برای (عذاب) آنان [:کافران] شتابزده مباش. روزی که آنچه را وعده داده می‌شوند بنگرند، گویی آنان جز ساعتی از روز را (در گذشته) نمانده‌اند؛ (این خود) رسانه‌ای (بزرگ) است. پس آیا جز گروه نافرمانان هلاک می‌شوند؟');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'کسانی که کفر ورزیدند و (مردمان را) از راه خدا باز داشتند (خدا) اعمال آنان را (تباه و) گمراه کرد.'),
(4547, 47, 2, 'و کسانی که ایمان آورده و کارهای شایسته کردند و به آنچه تدریجاً بر محمد (ص)فرود آمد گرویدند - حال آنکه آن (تمامی) حقّ از جانب پروردگارشان است- (خدا نیز) بدی‌های‌شان را زدود و (خاطره‌ی) مهمشان را اصلاح کرد.'),
(4548, 47, 3, 'این بدین سبب است که آنان که کفر ورزیدند، بی‌گمان از باطل پیروی کردند. و کسانی که ایمان آوردند از (تمامی) حق - که از جانب پروردگارشان است - به‌راستی پیروی کردند. این‌گونه خدا برای (بیداری) مردمان مثال‌هایشان را می‌زند.'),
(4549, 47, 4, 'پس چون با کسانی که کفر ورزیده‌اند برخورد کنید، پس (باید همتتان) زدن گردن‌هاشان و گردن‌کشانشان (باشد) تا هنگامی که آنان را (در کشتار) از پای درآوردید. پس (اسیران را) به شدت در بند کشید؛ در نتیجه یا (بر آنان) منّت نهید (و آزادشان کنید) و یا فدیه بگیرید (که عوضشان را از اسیرانتان بازپس گیرید) تا جنگ، سنگینی‌ها‌یش را فرونهد. این است (دستور خدا) و اگر خدا بخواهد، از ایشان انتقام می‌کشد، ولی (فرمان پیکار داد) تا برخی از شما را به وسیله‌ی برخی (دیگر) بیازماید. و کسانی که در راه خدا کشته شده‌اند، هرگز (خدا) کارهایشان را ضایع نمی‌کند.'),
(4550, 47, 5, 'به زودی آنان را راه می‌نماید و یادآوری مهمشان را اصلاح می‌کند.'),
(4551, 47, 6, 'و در بهشتی که آن را برایشان شناسایی داده، ایشان را درمی‌آورد.'),
(4552, 47, 7, 'هان ای کسانی که ایمان آوردید! اگر (دین) خدا را یاری کنید، خدا (هم) یاریتان می‌کند، و گام‌هایتان را استوار می‌دارد.'),
(4553, 47, 8, 'و کسانی که کفر ورزیدند، پس (خدا) نگونساری بر ایشان نهاد و اعمالشان را بر باد داد.'),
(4554, 47, 9, 'این همواره بدین سبب است که اینان آنچه را خدا نازل کرده هرگز خوش نداشتند، پس (خدا نیز) کارهایشان را فرو ریخت و بی‌ثمر کرد.'),
(4555, 47, 10, 'پس مگر در زمین نگشته‌اند تا بنگرند فرجام کسانی که پیش از آنها بودند، چگونه بود‌؟ خدا بر (سروسامان)شان هلاکتی ویرانگر فرود آورد، و برای کافران همانندهایش می‌باشد.'),
(4556, 47, 11, 'این همواره برای آن است که خدا مولا و سرپرست کسانی است که ایمان آورده‌اند. همانا کافران را هیچ مولایی (ربّانی) نیست.'),
(4557, 47, 12, 'خدا بی‌گمان کسانی را که ایمان آورده و کارهای شایسته(ی ایمانشان) کرده‌اند، در باغ‌هایی که از زیر(درختان سر درهم)‌شان نهرها روان است در می‌آورد. و کسانی که کافر شدند، بهره(ی حیوانی) می‌برند و همان‌گونه که چارپایان می‌خورند، می‌خورند و جایگاه و پایگاهشان آتش است.'),
(4558, 47, 13, 'و چه بسیار از گروه‌هایی که- نیرومندتر از آن گروهی بودند که تو را از قریه‌ات بیرون راندند- ما هلاکشان کردیم. پس برایشان هرگز یاوری نیست.'),
(4559, 47, 14, 'آیا پس کسی که بر حجّتی روشن از جانب پروردگار خویش بوده است، چون کسی است که بدی کردارش برای او زیبا جلوه داده شده‌؟ و (اینان) هوس‌های خودشان را پیروی کردند؟'),
(4560, 47, 15, 'مَثَلِ بهشتی که پرهیزگاران (به آن) وعده داده شدند، (این است که) در آن نهرهایی است از آبی که رنگ و بو و طعمش برنگشته‌؛ و نهرهایی از شیری که مزه‌اش دگرگون نشده‌؛ و نهرهایی از باده‌هایی که برای نوشندگان لذّتی است (عاقلانه)؛ و نهرهایی از انگبینِ ناب. و در آنجا از هر گونه ثمراتی برایشان (فراهم) است و (از همه بالاتر) پوششی از پروردگار آنهاست‌. آیا چنین کسی (در چنین باغی دل‌انگیز) مانند کسی است که جاودانه در آتش است و (اینان) آبی جوشان به خوردشان داده شد، پس معده‌هایشان را از هم فرو پاشید؟'),
(4561, 47, 16, 'و از (میان) ایشان [:منافقان] کسانی‌اند که (در ظاهر) به (سخنان) تو همی گوش فرا می‌دهند، تا هنگامی که از نزد تو بیرون روند. به دانش‌یافتگان (وحیانی) گویند: «لحظاتی پیش چه گفت‌؟» اینان همانانند که خدا بر دل‌هایشان مُهْر زده است‌. و (اینان) هوس‌های خود را پیروی کردند.'),
(4562, 47, 17, 'و (اما) کسانی که به هدایت گراییدند (خدا) آنان را (هر چه) بیشتر هدایت کرد و (پاداش) پرهیزگاریشان را (شایسته‌ی هدایتشان) به آنان داد.'),
(4563, 47, 18, 'پس آیا (کافران) جز این انتظار می‌برند و می‌نگرند که رستاخیز به ناگهان آنان را در رسد؟ در صورتی که نشانه‌ها و شروطش اینک به‌راستی پدید آمده است. پس اگر (رستاخیز) آنان را در رسد، دیگر چه زمانی (جای) یادواره‌ی آنان است‌؟'),
(4564, 47, 19, 'پس بدان که هیچ معبودی جز خدا نیست‌؛ و برای کار دنباله‌دار رسالتت (از شرّ کافران از خدا) پوششی بجوی و برای مردان و زنان باایمان (نیز) پوشش بخواه و خداست که جابه‌جایی و پناهگاه و جایگاه شما را می‌داند.'),
(4565, 47, 20, 'و کسانی که ایمان آوردند می‌گویند: «چرا سوره‌ای [:مجموعه‌ای از آیات درباره‌ی جهاد] نازل نشد‌؟» پس هنگامی‌که سوره‌ای محکم نازل شد و در آن نامِ کشتار آمد، آنان که در دل‌هاشان مرضی هست - مانند کسی که به حال بیهوشی مرگ افتاده - به تو می‌نگرند. پس (همین حالت) برایشان بهتراست.'),
(4566, 47, 21, 'فرمان‌پذیری و سخنی شایسته (برایشان بایسته) است و هنگامی که کار به تصمیم کشد، همواره خیر آنان در این است که با خدا در عمل و سخن راست باشند (که این) برایشان خوب و بهتر است.'),
(4567, 47, 22, 'پس (ای منافقان!) آیا امید بستید که اگر سرپرست مردم شدید، در زمین افساد کنید و خویشاوندی‌های خود را از هم بگسلید؟'),
(4568, 47, 23, 'اینان (همان) کسانی‌اند که خدا آنان را لعنت کرده؛ پس گوش‌ها(ی جان‌ها)شان را کر و چشم‌ها(ی دل‌ها)شان را کور کرده است.'),
(4569, 47, 24, 'آیا پس آیات قرآن را با نگرشی جامع (درست) نمی‌اندیشند؟ یا (مگر) بر دل‌هایی قفل‌هایشان نهاده شده است‌؟'),
(4570, 47, 25, 'بی‌گمان، کسانی که -پس از آنکه (راهِ) هدایت بر آنان روشن شد- به گذشته‌ها(ی باطل)شان برگشتند، شیطان برایشان (خواسته‌های شیطانیشان را) آراسته و به آرزوهای دور و درازشان انداخته است.'),
(4571, 47, 26, 'این به‌راستی بدین جهت است که آنان به کسانی که آنچه را خدا نازل کرده بسی بدحال و بدنوال بودند. گفتند: «ما در برخی کارها از شما اطاعت خواهیم کرد.» حال آنکه خدا از پنهان کردن رازشان آگاه است.'),
(4572, 47, 27, 'پس چگونه است هنگامی که فرشتگان (عذاب) جانشان را می‌ستانند، حال آنکه بر چهره‌ها و پشت‌هاشان می‌زنند؟'),
(4573, 47, 28, 'این بدین سبب است که آنان از آنچه خدا را به خشم آورده پیروی کرده‌اند، و خوشامدش را بس ناخوش داشتند. پس اعمالشان را تباه و ناچیز کرد.'),
(4574, 47, 29, 'یا کسانی که در دل‌هاشان مرضی هست، پنداشتند که خدا هرگز کینه‌هاشان را برون [:افشا] نخواهد کرد؟'),
(4575, 47, 30, 'و اگر بخواهیم، همواره آنان را به تو می‌نمایانیم، پس ایشان را به چهره‌هاشان همواره بشناسی. و به‌راستی در اشاره‌ی گزافِ سخن(شان)، به (حال) آنان به‌خوبی پی خواهی بُرد. و خداست که کارهای شما را می‌داند.'),
(4576, 47, 31, 'و به‌راستی شما را بی‌گمان می‌آزماییم، تا مجاهدان و شکیبایانِ از شما را نشانه نهیم و گزارش‌هایتان را ارزیابی کنیم.'),
(4577, 47, 32, 'همانا کسانی که کافر شدند و (مردم را) از راه خدا باز داشتند و پس از آنکه راه هدایت بر آنان آشکار شد، با پیامبر (خدا) به جدایی درافتادند، هرگز به خدا گزندی نمی‌رسانند؛ و به زودی (خدا) کرده‌هایشان را تباه خواهد کرد.'),
(4578, 47, 33, 'هان ای کسانی که ایمان آوردید! خدا را اطاعت کنید و پیامبرش را (نیز) اطاعت کنید و (با تخلفتان در این میان) اعمالتان را باطل مکنید.'),
(4579, 47, 34, 'بی‌گمان آنان که کفر ورزیدند و از راه خدا جلوگیری کردند، سپس در حال کفر مردند، هرگز خدا از آنان درنخواهد گذشت.'),
(4580, 47, 35, 'پس سستی نورزید حال آنکه (کافران را) به آشتی می‌خوانید، با آنکه شما (از آنان) برترید. و خدا با شماست و (ارزش) کارهایتان را هرگز نخواهد کاست.'),
(4581, 47, 36, 'زندگی این دنیا تنها بازی و بازدارنده‌ا‌ی (از حق) است و اگر ایمان بیاورید و (خدا را) پروا بدارید، خدا پاداش‌هاتان را می‌دهد و اموالتان را (در عوض) از شما نمی‌خواهد.'),
(4582, 47, 37, 'اگر اموال شما را بخواهد، پس به اصرار و اجبار از شما طلب کند، بخل می‌ورزید. و (بدین ترتیب) کینه‌های شدیدتان را برملا می‌کند.'),
(4583, 47, 38, 'هان! شما همان (مردمی) هستید (که) برای انفاق در راه خدا فرا خوانده می‌شوید. پس برخی از شما بخل می‌ورزد و هر کس بخل ورزد، تنها به زیان خود بخل می‌ورزد. و خدا بی‌نیاز است و شما نیازمندید. و اگر روی برتابید، خدا جای شما را به مردمی غیر از شما خواهد داد (و) سپس همانندتان نیستند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'ما به‌راستی برای تو گشایش و پیروزی بخشیدیم، (چه) پیروزی روشنگری.'),
(4585, 48, 2, 'تا خدا پی‌آمد خطرناک دنیوی کار (رسالت)تو را - گذشته و آینده‌اش - را بپوشاند و نعمت خود را بر تو تمام گرداند و تو را به راهی راست هدایت کند.'),
(4586, 48, 3, 'و خدا تو را به نصرتی ارجمند یاری نماید.'),
(4587, 48, 4, 'او کسی است که در دل‌های مؤمنان آرامش (ویژه) را فرو فرستاد تا ایمانی با (داشتنِ) ایمانشان بیفزایند. و سپاهیان آسمان‌ها و زمین تنها از خدایند. و خدا بس دانایی سنجیده‌کار بوده است.'),
(4588, 48, 5, 'تا مردان و زنانی را که ایمان آورده‌اند در باغ‌هایی که از زیر (درختان)‌شان نهرها روان است، جاودانه در آوَرَد و بدی‌هایشان را از آنان بزداید؛ و این (فرجام نیک) در پیشگاه خدا به‌سلامتی کامیابی بزرگی بوده است.'),
(4589, 48, 6, 'و تا مردان و زنان نفاق‌پیشه و مردان و زنان مشرک بدگمان به خدا را عذاب کند، بر آن حالت گردونه‌ی بد (در نشیب و فراز زمان) است. و خدا بر ایشان خشم نموده و لعنتشان کرده و جهنّم را برای آنان آماده گردانیده و (چه) بد سرانجامی است!'),
(4590, 48, 7, 'و سپاهیان آسمان‌ها و زمین تنها از خدایند و خدا همواره عزیز سنجیده‌کار بوده است.'),
(4591, 48, 8, '(ای پیامبر!) ما تو را همواره گواهی‌دهنده‌ای و بشارتگر‌ی و هشداردهنده‌ای فرستادیم.'),
(4592, 48, 9, 'تا (شما مکلفان) به خدا و فرستاده‌اش ایمان آورید و بزرگش بدارید و ارجش نهید و خدا را بامدادان و شامگاهان به پاکی بستایید.'),
(4593, 48, 10, 'بی‌گمان کسانی که با تو بیعت می‌کنند، به‌راستی تنها با خدا بیعت می‌کنند. دست خدا بالای دست‌های آنان است. پس هر کس پیمان شکند، تنها به زیان خود پیمان می‌شکند، و هر کسی بر آنچه با خدا بر عهده‌ی خود عهدی بسته وفادار بماند، زودا (که) خدا پاداشی بزرگ به او می‌بخشد.'),
(4594, 48, 11, 'سر باز زدگان از جنگ، از بادیه‌نشینان، به زودی به تو خواهند گفت: «اموالمان و کسانمان ما را (به خود) مشغول داشتند، پس برای ما پوشش [:بخشش] بخواه‌.» چیزی را که در دل‌هایشان نیست بر زبان‌هایشان می‌رانند. بگو: «پس اگر خدا بخواهد به شما زیانی یا سودی برساند چه کسی از (عذاب) خدا برای شما اختیار چیزی را دارد؟ بلکه (این) خداست که به آنچه می‌کنید همواره آگاه بوده است.»'),
(4595, 48, 12, '«(نه چنان بوده.) بلکه پنداشتید که پیامبر و مؤمنان هرگز به خاندان خود برنخواهند گشت، و این (پندار) در دل‌هایتان نمودی زیبا یافته بود و گمان بد کردید. شما مردمی بس زیان‌بار بوده‌اید.»'),
(4596, 48, 13, 'و هر کس به خدا و پیامبرش ایمان نیاورد، ما برای کافران آتشی سوزان آماده کرده‌ایم.'),
(4597, 48, 14, 'و فرمانروایی آسمان‌ها و زمین تنها از خداست. برای هر که بخواهد می‌پوشد و هر که را بخواهد عذاب می‌کند و خدا بی‌گمان بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(4598, 48, 15, 'هنگامی که به (قصد) گرفتن غنایم روانه شدید، به زودی بر جای‌ماندگان متخلف خواهند گفت: «بگذارید ما (هم) به دنبال شما بیاییم‌.» (این‌گونه) می‌خواهند کلام خدا را تبدیل کنند. بگو: «هرگز از پی ما نخواهید آمد. خدا از پیش درباره‌ی شما چنین فرمود.» پس به زودی خواهند گفت: « (نه،) بلکه بر ما رشک می‌برید.» (نه چنین است.) بلکه جز اندکی در نمی‌یابند.'),
(4599, 48, 16, 'به بر جای ماندگان متخلف‌، از بادیه‌نشینان، بگو: «زودا سوی قومی (رزمنده‌ی) سخت زورمند دعوت خواهید شد (که) یا با آنان بجنگید یا اسلام آورند. پس اگر فرمان ببرید خدا شما را پاداشی نیک می‌دهد و اگر - همچنان که از پیش‌، پشت کردید - (باز هم) روی بگردانید، شما را به عذابی پر درد معذّب می‌دارد.»'),
(4600, 48, 17, 'بر نابینا تنگنایی نیست و بر لنگ تنگنایی نیست و بر بیمار تنگنایی نیست (که در جهاد و مانندش شرکت نکنند). و هر کس خدا و پیامبرش را فرمان بَرَد وی را در باغ‌هایی که از زیر (درختان)شان نهرها روان است در می‌آورد و هر کس را (که) روی برتابد به عذابی دردناک معذّبش می‌دارد.'),
(4601, 48, 18, 'خدا بی‌گمان هنگامی که مؤمنان، زیر (آن) درخت با تو بیعت می‌کردند از آنان خشنود شد. پس آنچه در دل‌هایشان بود دانست و بر آنان آرامش فرو فرستاد و پیروزی نزدیکی را به آنان پاداش داد.'),
(4602, 48, 19, 'و (نیز) غنیمت‌های فراوانی را (که آنها) می‌گیرند. و خدا همواره نیرومند سنجیده‌کار بوده است.'),
(4603, 48, 20, 'خدا به شما غنیمت‌های فراوان (دیگری) وعده داده (که) آنها را خواهید گرفت. پس این (پیروزی) را برای شما پیش انداخت و دست‌های مردمان را از شما کوتاه ساخت، و تا برای مؤمنان نشانه‌ای باشد و شما را به راهی راست هدایت کند.'),
(4604, 48, 21, 'و (غنیمت‌های) دیگری (نیز هست) که شما بر آنها دست نیافته‌اید (و) خدا بر آنها همواره احاطه داشته و خدا بر هر چیزی بسیار توانا بوده است.'),
(4605, 48, 22, 'و اگر کسانی که کافر شدند، با شما کشتار کنند، بی‌چون (به شما) پشت خواهند کرد، سپس پشتوانه و یاوری نخواهند یافت.'),
(4606, 48, 23, 'سنّت الهی را (که) از پیش همین بوده (پاس‌دار و انتظار داشته باش) و برای سنّتِ الهی هرگز جایگزینی نخواهی یافت.'),
(4607, 48, 24, 'و اوست کسی که در دل مکّه - پس از پیروز کردنتان بر آنان - دست‌هایشان را از شما و دست‌هایتان را از ایشان بازداشت، پس از آنکه شما را بر آنان چیره ساخت. و خدا به آنچه می‌کنید بسی بینا بوده است.'),
(4608, 48, 25, 'آنان کسانی می‌باشند که کافر شدند و از (ورود) شما (به) مسجدالحرام جلوگیری کردند و نگذاشتند قربانی نگه‌داشته‌ی شما را به محلش برسد و اگر (در مکّه) مردان و زنان باایمانی نبودند که (ممکن بود) بی‌آنکه آنان را بشناسید، ندانسته پایمالشان کنید. و عار و بی‌مقداری از ایشان برایتان بماند (فرمان حمله به مکّه می‌دادیم) تا خدا هر که را بخواهد در رحمت خویش آورد. اگر (کافر و مؤمن) از هم متمایز می‌شدند، همواره کافران را به عذاب دردناکی معذّب می‌داشتیم.'),
(4609, 48, 26, 'چون کافران در دل‌های خود، تعصّب، (آن هم) تعصّب جاهلیت نهادند، پس خدا آرامشی از خود بر فرستاده‌ی خویش و بر مؤمنان فرو فرستاد و کلمه‌ی تقوا را ملازم آنان ساخت و (در واقع) آنان به (رعایت) آن سزاوارتر و (هم) اهل آن بودند. و خدا همواره بر هر چیزی بسی دانا بوده است.'),
(4610, 48, 27, 'به‌راستی خدا رؤیای پیامبر خود را به‌درستی تحقّق بخشید (که) شما بدون شک، به خواست خدا در حالی که سرهاتان را تراشیده و (موها یا ناخن‌های خود را) کوتاه کرده‌اید، همواره بدون ترسی در مسجدالحرام در خواهید آمد. خدا آنچه را که نمی‌دانستید دانست. در نتیجه پس از این، پیروزیِ نزدیکی (برای شما) قرار داد.'),
(4611, 48, 28, 'اوست کسی که پیامبر خود را به (تمامی) هدایت، با آیین پای‌برجای راستا، و سرشار از حق روانه ساخت، تا آن را بر تمام ادیان پیروز گرداند و گواه بودن خدا کافی بوده است.'),
(4612, 48, 29, 'محمّد پیامبر خداست‌؛ و کسانی که با اویند، بر کافران سختگیران و با خودهاشان مهربانانند. آنان را در رکوع و سجود می‌بینی (که) فضل و خشنودی خدا را همی جویند. علامت (مشخصه‌ی) آنان بر اثر و نمود سجود در چهره‌هایشان پیداست. این نماد ایشان است در تورات و نمادشان در انجیل. چون کِشته‌ای است که جوانه‌ی خود را بر آورده و آن را مایه دهد تا ستبر شود و بر ساقه‌های خود بایستد و زارعان را به شگفت آورد، تا از (انبوهی) آنها (خدا) کافران را به خشم در اندازد. خدا به کسانی از آنان که ایمان آورده و کارهای شایسته(ی ایمان) کرده‌اند، پوشش و پاداشی بزرگ وعده داده است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'هان ای کسانی که ایمان آوردید! در برابر خدا و پیامبر، (خود یا دیگران را) پیش نیندازید و خدا را پروا بدارید. همانا خدا بسی شنوای بسیار داناست.'),
(4614, 49, 2, 'هان ای کسانی که ایمان آوردید! صداهایتان را از صدای پیامبر بلندتر مکنید، و همچنان که بعضی از شما با بعضی دیگر بلند سخن می‌گویید با او به صدای بلند سخن مگویید، که کرده‌هایتان تباه شود، حال آنکه باریک‌بینی نمی‌کنید.'),
(4615, 49, 3, 'بی‌گمان کسانی که نزد پیامبر خدا صدایشان را فرو می‌شکنند همان کسانند که خدا دل‌هایشان را برای پرهیزگاری آزموده (و) برایشان پوششی و پاداشی بزرگ است.'),
(4616, 49, 4, 'بی‌گمان کسانی که از پشت اطاق‌ها(یت) تو را صدا می‌زنند بیشترشان خردورزی نمی‌کنند.'),
(4617, 49, 5, 'و اگر اینان همواره صبر کنند تا بر آنان درآیی، همانا برایشان بهتر است. و خدا پوشنده‌ی رحمتگر بر ویژگان است.'),
(4618, 49, 6, 'هان ای کسانی که ایمان آوردید! اگر فاسقی برایتان خبری آورد، پس نیک وارسی و آشکارگری کنید، مبادا به نادانی، گروهی را آسیب برسانید؛ پس از آنچه کرده‌اید پشیمان شوید.'),
(4619, 49, 7, 'و بدانید که پیامبر خدا در میان شماست. اگر در بسیاری از کارها از (رأی و میل) شما پیروی کند، به‌راستی دچار زحمت می‌شوید. لیکن خدا ایمان را برای شما دوست‌‌داشتنی گردانید و آن را در دل‌های شما بیاراست و کفر و پلیدکاری و سرکشی را در نظرتان بس ناخوشایند ساخت. هم ایشان [:ایمان‌آورندگان] رشدیافتگانند.'),
(4620, 49, 8, 'حال آنکه بخششی از خدا و نعمتی است (از او که به شما داده). خدا بس دانای سنجیده‌کار است.'),
(4621, 49, 9, 'و اگر دو گروه از مؤمنان با یکدیگر کشتار کنند، پس میان آن دو را اصلاح کنید. پس اگر یکی از آنان بر دیگری تعدّی کرد، شما با آن (گروهی) که تعدّی می‌کند کشتار کنید تا به فرمان خدا بازگردد. پس اگر بازگشت، میان آنها را دادگرانه سازش دهید و تقسیمی شایسته کنید؛ خدا همواره دادگران را دوست می‌دارد.'),
(4622, 49, 10, 'مؤمنان فقط با هم برادرند، پس میان برادرانتان را سازش دهید و از خدا پروا بدارید، شاید مورد رحمت قرار گیرید.'),
(4623, 49, 11, 'هان ای کسانی که ایمان آوردید! نباید گروهی (از مردان) گروه دیگر را مسخره کنند؛ شاید آنها از این‌ها بهتر باشند. و نه زنانی، زنانی (دیگر) را (مسخره کنند) شاید آنان از اینان بهتر باشند. و از خودهاتان عیب‌جویی مکنید و به همدیگر لقب‌های زشت ندهید؛ چه ناپسندیده است نام زشت پس از ایمان. پس هر کس توبه نکند آنان خودشان ستمکارانند.'),
(4624, 49, 12, 'هان ای کسانی که ایمان آوردید! از بسیاری گمان‌ها بپرهیزید که پاره‌ای از گمان‌ها بدفرجام است. و تجسس مکنید (و) بعضی از شما از بعضی دیگر غیبت نکند. آیا کسی از شما دوست دارد که گوشت برادر (مؤمن) مرده‌اش را بخورد؟ پس از آن بسی بدتان می‌آید و خدای را پروا بدارید. خدا بی‌گمان بسی برگشت‌‌کننده‌ی رحمتگر بر ویژگان است.'),
(4625, 49, 13, 'هان ای مردمان! ما شما را به‌راستی از مرد و زنی آفریدیم و شما را ملّت‌هایی و قبیله‌هایی گردانیدیم تا یکدیگر را (متقابل) شناسایی کنید. همواره ارجمندترین شما نزد خدا پرهیزگارترین شماست. همانا به‌راستی خدا بسیار دانای بس آگاه است.'),
(4626, 49, 14, 'بادیه‌نشینان گفتند: «ایمان آوردیم‌.» بگو: «ایمان نیاوردید. لیکن بگویید اسلام آوردیم.‌» و هنوز در دل‌هایتان ایمان داخل نشده است و اگر خدا و پیامبر او را فرمان برید، او از (ارزش) کرده‌هایتان چیزی کم نمی‌کند. همواره خدا بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(4627, 49, 15, 'مؤمنان، تنها کسانی‌اند که به خدا و پیامبرش گرویده، سپس شک نیاورده و با مال و جانشان در راه خدا جهاد کردند. ایشان، (هم)اینان راستانند.'),
(4628, 49, 16, 'بگو: «آیا خدا را به دین خود آگاهی می‌دهید؟ و حال آنکه خدا آنچه را که در آسمان‌ها و در زمین است می‌داند. و خدا به هر چیزی بسیار داناست.»'),
(4629, 49, 17, '(اینان) از اینکه اسلام آورده‌اند بر تو منّت می‌نهند. بگو: «بر من از اسلام آوردنتان منّت مگذارید؛ بلکه خدا با هدایت کردنتان برای ایمان، بر شما منّت می‌نهد، اگر (از) راستان بوده‌اید.»'),
(4630, 49, 18, '«همواره (این) خداست که نهفته‌ی آسمان‌ها و زمین را می‌داند و خدا(ست که) به آنچه می‌کنید بیناست.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'ق. سوگند به قرآن باشکوه (و) جلالت و وسعت جهانگیرش،'),
(4632, 50, 2, '(که آنان نگرویدند) بلکه از اینکه هشداردهنده‌ای از خودشان برایشان آمد در شگفت شدند. پس کافران گفتند: «این (محمّد و حکایت معاد) چیزی عجیب است.»'),
(4633, 50, 3, '«آیا چون مُردیم و خاک شدیم (از نو زنده می‌شویم)؟ این بازگشتی بعید (و دور از عقل و منطق) است!»'),
(4634, 50, 4, '(ما) همانا دانسته‌ایم آنچه را که زمین از (کیفیت) اجسادشان فرو می‌کاهد. و پیش ما کتابی [:نوشته‌ای] نگهبان است.'),
(4635, 50, 5, 'بلکه با حق هنگامی که برایشان آمده (همو را) تکذیب کردند. پس آنها در ژرفای کاری در هم و سر در گم (مانده)اند.'),
(4636, 50, 6, 'پس مگر به آسمان بالای سرشان ننگریسته‌اند که چگونه آن را ساخته و پرداخته و زینتش داده‌ایم و برای آن هیچ گونه شکاف‌ها و سوراخ‌هایی نیست.'),
(4637, 50, 7, 'و زمین را فرو کشیدیم و در آن (لنگرآسا) کوه‌هایی را فرو افکندیم و در آن از هرگونه جفت دل‌انگیزی رویاندیم.'),
(4638, 50, 8, '(تا) برای هر بنده‌ی راهوار توبه‌کاری، بینش‌افزا و یادواره‌ای باشد.'),
(4639, 50, 9, 'و از آسمان، آبی پربرکت به‌تدریج فرود آوردیم، پس با آن باغ‌ها و دانه‌های دروکردنی را رویانیدیم.'),
(4640, 50, 10, 'و درختان خرما را (هم) در حالی که فراورده‌هایی روی هم چیده دارند.'),
(4641, 50, 11, 'حال آنکه (این‌ها همه) برای روزی بندگان (من) است. و با آن (آب) سرزمین مرده‌ای را زنده گردانیدیم. بیرون شدن مردگان (نیز) بدان‌گونه است.'),
(4642, 50, 12, 'پیش از ایشان قوم نوح و اصحاب رسّ و ثمود،'),
(4643, 50, 13, 'و عاد و فرعون، برادران لوط،'),
(4644, 50, 14, 'و بیشه‌نشینان و قوم تُبّع، همه‌، فرستادگان (ما) را تکذیب کردند. پس وعده‌ی (عذاب) مرا سزاوار شدند.'),
(4645, 50, 15, 'پس مگر (ما) به آفرینش نخستین(مان) ناتوان شده‌ایم‌؟ (نه!) بلکه آنان از آفرینش جدید در شبهه‌اند.'),
(4646, 50, 16, 'و ما بی‌گمان انسان را به‌راستی آفریدیم حال آنکه می‌دانیم نفس او چه وسوسه‌ای به او می‌کند و ما از شاهرگ (او) به او نزدیکتریم.'),
(4647, 50, 17, 'چون دو (فرشته‌ی) دریافت کننده‌- نشسته‌ای از راست و نشسته‌ای از چپ- (از اعمالش را) به خوبی دریافت می‌دارند.'),
(4648, 50, 18, '(آدمی) هیچ جریانی را به لفظ در نمی‌آورد مگر اینکه مراقبی آماده و نیرومند و حسابگر نزد اوست (و آن را به خوبی و درستی ضبط و ثبت می‌کند).'),
(4649, 50, 19, 'و بیهوشی مرگ، به‌راستی در رسید (و) این همان است که از آن گریزان بوده‌ای.'),
(4650, 50, 20, 'و در صور [:شیپور جان‌افزا] دمیده شد. این است روز تهدید (من).'),
(4651, 50, 21, 'و هر کسی آمد (در حالی که) با او سوق‌دهنده و گواهی‌دهنده‌ای است،'),
(4652, 50, 22, '(به او می‌گویند:) «همواره از این (حال) سخت در (ژرفای) غفلت بوده‌ای. پس ما پرده‌ات را (از جلو دیدگانت) برداشتیم‌. پس دیده‌ات امروز تیزبین است.»'),
(4653, 50, 23, 'و (فرشته‌ی) همنشین او می‌گوید: «این است آنچه پیش من (به گونه‌ای محکم و روشن) نگهبانی شده است.»'),
(4654, 50, 24, '(به آن دو فرشته خطاب می‌شود:) «هر کافر سرسختی را در جهنم فرو افکنید.»'),
(4655, 50, 25, '«(که) بسی باز دارنده‌ی از هر خیری (بوده) است و (هر) متجاوزِ شک مستند آورنده‌ای را نیز.»'),
(4656, 50, 26, '«کسی که با خدا، خدایی دیگر قرار داده. پس (ای دو فرشته!) او را در عذاب شدید فرو افکنید.»'),
(4657, 50, 27, 'همدمش [:شیطان] گفت: «پروردگارمان! من او را به طغیان وا نداشتم، ولی (خودش) در گمراهی دور و درازی بوده است.»'),
(4658, 50, 28, '(خدا) فرمود: «در پیشگاه من (با همدیگر) مستیزید حال آنکه به‌راستی از پیش به شما هشدار این وعده‌ی عذاب را داده بودم.»'),
(4659, 50, 29, '«پیش من (این) گفتمان دگرگون نمی‌گردد و من (نسبت) به بندگانم زیاد بیدادگر نیستم.»'),
(4660, 50, 30, '(همی بنگر) روزی را که (ما) به دوزخ می‌گوییم: «آیا پر شدی‌؟» و (او) می‌گوید: «آیا بیشتر (هم) هست‌؟»'),
(4661, 50, 31, 'و بهشت برای پرهیزگاران با آرایشی (ویژه) نزدیک آورده شد، حال آنکه دور (هم) نیست.'),
(4662, 50, 32, '(و به آنان گویند:) «این همان است که وعده داده می‌شوید (و) برای هر بسیار راهوار (سوی خدا و) نگهدار (حدود خدا) است.»'),
(4663, 50, 33, 'کسی که (خدای) رحمان را به پنهان هراس داشت و با دلی پیاپی بازگشت‌کننده‌ی سوی خدا آمد.'),
(4664, 50, 34, '«به سلامتی (و شادکامی) در آن درآی (که) این روز جاودانگی است.»'),
(4665, 50, 35, 'هر چه بخواهند در آنجا برایشان (آماده) است و پیش ما فزون‌تر (از خواستارشان) است.'),
(4666, 50, 36, 'و چه بسیار نسل‌هایی را پیش از ایشان هلاک کردیم (که) ایشان حمله‌ورتر از اینان بودند. پس در شهرها پرسه‌ای زده بودند. (اما سرانجام) مگر گریزگاهی هست‌؟'),
(4667, 50, 37, 'همانا در این (عقوبت‌ها) برای هر که دلی داشته و گوش (دل برای دریافت حق) افکنده - در حالی که خود هم گواه است- بی‌گمان یادواره‌ای (درخشان) است.'),
(4668, 50, 38, 'به‌راستی ما آسمان‌ها و زمین و آنچه را که میان آن دو است بی‌چون در شش روز [:زمان] آفریدیم. و هرگز ما را هیچ‌گونه خستگی لمس نکرد.'),
(4669, 50, 39, 'پس بر آنچه می‌گویند صبر کن و پیش از برآمدن آفتاب و پیش از غروب با ستایش پروردگارت تسبیح گوی.'),
(4670, 50, 40, 'و پاره‌ای از شب و به دنباله‌های سجود او را تسبیح گوی.'),
(4671, 50, 41, 'و روزی که منادی از جایی نزدیک ندا در دهد، به‌گوش (و به‌هوش) باش.'),
(4672, 50, 42, 'روزی که فریاد رستاخیز [:جان‌خواه] را بحقّ می‌شنوند، آن (روز)، روز بیرون آمدن (از گورستان مرگ به آستانه‌ی زندگی) است.'),
(4673, 50, 43, 'بی‌گمان ما (هم)آنیم که خود زنده می‌کنیم و (هم‌) خود می‌میرانیم و همه‌ی برگشت‌ها تنها سوی ماست.'),
(4674, 50, 44, 'روزی که زمین به سرعت از (اجساد) آنان جدا و شکافته می‌شود. این گردآوری برما آسان است.'),
(4675, 50, 45, 'ما بدانچه می‌گویند داناتریم و تو به اجبار وادار کننده‌ی آنان (به ایمان) نیستی. پس با قرآن هر که را از تهدید من می‌ترسد یادآوری کن.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'سوگند به نیروهای پراکننده‌، (چه) پراکندنی.'),
(4677, 51, 2, 'پس سوگند به بارداران باری سنگین‌، (چه) سنگینی‌ای.'),
(4678, 51, 3, 'پس سوگند به روانان به آسانی.'),
(4679, 51, 4, 'پس قسمت‌کنندگان اَمری [:کار یا فرمان و یا چیزی].'),
(4680, 51, 5, 'همواره آنچه (به آن) وعده داده شده‌اید همانا راست است.'),
(4681, 51, 6, 'و (روز) بُروز طاعت و معصیت همانا روی‌دادنی است.'),
(4682, 51, 7, 'قسم به آسمان (که) دارای راه‌هاست؛'),
(4683, 51, 8, 'همانا شما (درباره‌ی رستاخیز) در (ژرفای) سخنی گوناگونید.'),
(4684, 51, 9, 'هر که از آن به دروغ برگردانده شود، (بخت‌) برگشته است.'),
(4685, 51, 10, 'مرگ بر دروغ‌پردازان [:دروغ‌پردازان کشته شده‌اند].'),
(4686, 51, 11, 'کسانی که در ورطه‌(ی نادانی) سهوناکند.'),
(4687, 51, 12, 'پرسند: «روز بُروز طاعت و معصیت کی است‌؟»'),
(4688, 51, 13, 'همان روزی که آنان بر آتش بس آزموده شوند.'),
(4689, 51, 14, '(و به آنان گفته شود:) «فتنه و آشوب خود را بچشید. این است همان (بلایی) که با شتاب خواستار آن بوده‌اید.»'),
(4690, 51, 15, 'پرهیزگاران بی‌گمان در باغستان‌ها و چشمه‌سارانند.'),
(4691, 51, 16, 'حال آنکه آنچه را پروردگارشان به آنان عطا فرموده گیرندگانند. بی‌گمان آنان پیش از این نیکوکاران بوده‌اند.'),
(4692, 51, 17, 'و از شب اندکی را می‌غنوده‌اند.'),
(4693, 51, 18, 'و آنان سحرگاهان (از خدا) پوشش می‌خواهند.'),
(4694, 51, 19, 'و در اموالشان برای سائل و محروم‌، حقّی (معلوم) است.'),
(4695, 51, 20, 'و در زمین برای اهل یقین نشانه‌هایی است؛'),
(4696, 51, 21, 'و (همچنان) در خودتان. پس مگر نمی‌نگرید؟'),
(4697, 51, 22, 'و روزیِ شما و آنچه (از بهشت) وعده داده می‌شوید در آسمان است.'),
(4698, 51, 23, 'پس سوگند به پروردگار آسمان و زمین، که بی‌گمان آن حق است؛ همان‌گونه که خود شما (به روشنی) سخن می‌گویید.'),
(4699, 51, 24, 'آیا خبر مهمانان ارجمند و گرامی ابراهیم به تو رسید؟'),
(4700, 51, 25, 'چون بر او درآمدند، پس سلامی گفتند. گفت: «سلام (بر) مردمی ناشناس.»'),
(4701, 51, 26, 'پس به گونه‌ای پنهان به سوی اهلش روان شد و گوساله‌ای فربه (و بریان شده) بیاورد.'),
(4702, 51, 27, 'پس آن را نزدیکشان برد. گفت: «مگر نمی‌خورید؟»'),
(4703, 51, 28, 'پس، از ایشان ترسی به دل برگرفت. گفتند: «مترس.» و او را به پسری بسی دانا نوید دادند.'),
(4704, 51, 29, 'پس زنش پریشان (و فریادزنان) پیش آمد. پس سیلی بر چهره‌ی خود زد و گفت: «پیره‌زالی نازایم (چگونه بزایم)؟»'),
(4705, 51, 30, 'گفتند: «پروردگارت چنین فرموده است. او، (هم)او حکیمی بس داناست.»'),
(4706, 51, 31, '(ابراهیم) گفت: « ای فرستادگان! مهم شما (در این مأموریت) چیست‌؟»'),
(4707, 51, 32, 'گفتند: «ما همانا سوی گروهی (از) مجرمان فرستاده شده‌ایم،»'),
(4708, 51, 33, '«تا سنگ‌هایی از گل بر (سر و سامان)‌شان فرو فرستیم.»'),
(4709, 51, 34, '«حال آنکه نزد پروردگارت برای مسرفان نشانه‌گذاری شده است.»'),
(4710, 51, 35, '«پس هر کس از مؤمنان را در آن (سامان) بود بیرون راندیم.»'),
(4711, 51, 36, '«پس در آنجا جز یک خانه از تسلیم‌شدگان (برابر خدا بیشتر) نیافتیم.»'),
(4712, 51, 37, '«و در آنجا برای آنان که از عذاب پر درد می‌ترسند، نشانه‌ای بر جا نهادیم.»'),
(4713, 51, 38, 'و (نیز) در (ماجرای) موسی، چون او را با حجّتی مسلط و روشنگر سوی فرعون گسیل داشتیم.'),
(4714, 51, 39, 'پس (فرعون) با رکن (فرعونیت‌)اش روی برتافت و گفت: «(این شخص،) ساحری یا دیوانه‌ای است.»'),
(4715, 51, 40, 'پس او و سپاهیانش را برگرفتیم و آنان را در دریا افکندیم در حالی که او (در آخرین لحظه) نکوهشگر (خود و فرعونیان) بود.'),
(4716, 51, 41, 'و در (ماجرای) عادیان (نیز) ، چون بر سر (و سامان)‌شان باد نازا را فرستادیم.'),
(4717, 51, 42, 'هیچ چیزی را که بر آن وزد فروگذار نکند جز آنکه همچون خاکسترش کند.'),
(4718, 51, 43, 'و در (ماجرای) ثمودیان (نیز عبرتی بود) چون به ایشان گفته شد: «تا چندی برخوردار شوید.»'),
(4719, 51, 44, 'پس از فرمان پروردگارشان سر برتافتند تا در حالی که آنها می‌نگریستند، آذرخش (جانکاه) آنان را فرو گرفت.'),
(4720, 51, 45, 'پس هرگز نه توانستند به پای خیزند و نه جویندگان یارانی بوده‌اند.'),
(4721, 51, 46, 'و گروه نوح را (نیز) از پیش (از آن اقوام همین گونه هلاک کردیم). آنان بی‌گمان گروهی فاسق [:نافرمان] بوده‌اند.'),
(4722, 51, 47, 'و آسمان را به نیروهایی برافراشتیم و بی‌گمان، همانا ما توسعه‌‌دهند‌گانیم.'),
(4723, 51, 48, 'و زمین را رام و راهوار گسترانیدیم‌؛ پس چه نیکو گسترندگانیم.'),
(4724, 51, 49, 'و از هر چیزی دو گونه [:جفت یکدیگر] آفریدیم، شاید شما یاد کنید.'),
(4725, 51, 50, 'پس فراسوی خدا فرار کنید. من بی‌گمان برایتان از (سوی) او ترساننده‌ای روشنگرم.'),
(4726, 51, 51, '«و با خدا معبودی دیگر قرار مدهید. همانا من از جانب او هشداردهنده‌ای روشنگرم.»'),
(4727, 51, 52, 'بدین‌سان کسانی راکه پیش از آنان بودند هیچ پیامبری نیامد جز (اینکه) گفتند: «ساحری یا دیوانه‌ای است.»'),
(4728, 51, 53, 'آیا همدیگر را به این (سخن) سفارش کردند؟ (نه!) بلکه آنان مردمی سرکشند.'),
(4729, 51, 54, 'پس، از آنان روی بگردان، پس تو در خور سرزنش نیستی.'),
(4730, 51, 55, 'و یاد(شان) آور، که مؤمنان را بی‌گمان، یادواره سودمند است.'),
(4731, 51, 56, 'و جنّ و انس را نیافریدم جز برای آنکه مرا بپرستند.'),
(4732, 51, 57, 'از آنان هیچ روزی نمی‌خواهم و نمی‌خواهم که مرا خوراک دهند.'),
(4733, 51, 58, 'همواره خداست که خود بسی روزی‌دهنده‌ی نیرومند استوار است.'),
(4734, 51, 59, 'پس بی‌گمان برای کسانی که ستم کردند پیامدی بد است؛ همانند پیامد بد یارانشان. پس در خواستن عذاب از من شتاب نجویند.'),
(4735, 51, 60, 'پس وای برای کسانی که کافر شدند از آن روزشان که وعده داده می‌شوند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'سوگند به (کوه) طور.'),
(4737, 52, 2, 'و به کتابی نگاشته شده'),
(4738, 52, 3, 'در طوماری گسترده.'),
(4739, 52, 4, 'سوگند به آن خانه‌ی آباد [:کعبه و قلب حضرت محمدص]'),
(4740, 52, 5, 'سوگند به بام بلند [:آسمان و آسمان قلب محمدص]'),
(4741, 52, 6, 'و به دریای افروخته [:دریای دل فروزان محمدص ]؛'),
(4742, 52, 7, '(که) همواره عذاب پروردگارت همانا واقعی و شدنی است.'),
(4743, 52, 8, 'برایش هیچ بازدارنده‌ای نیست.'),
(4744, 52, 9, 'روزی که آسمان روان گردد، چه روان‌شدنی!'),
(4745, 52, 10, 'و کوه‌ها (جمله) به حرکتی سریع درآیند، چه سرعتی!'),
(4746, 52, 11, 'پس در آن هنگامه وای بر تکذیب‌کنندگان.'),
(4747, 52, 12, 'کسانی که در فرو رفتنی (در باطل) بازی می‌کنند.'),
(4748, 52, 13, 'روزی که با پرتابی سوی آتش جهنم به شدت کشیده می‌شوند، چه کشیدنی!'),
(4749, 52, 14, '(و به آنان گویند:) «این همان آتشی است که به آن (همان را) تکذیب می‌کردید.»'),
(4750, 52, 15, '«آیا پس این افسونی است‌؟ یا شما (درست‌) نمی‌نگرید؟»'),
(4751, 52, 16, '«آن را بیفروزید. پس خواه شکیبایی کنید و خواه شکیبایی نکنید بر زیانتان یکسان است. تنها جزای شما کارهایی است که می‌کرده‌اید.»'),
(4752, 52, 17, 'پرهیزگاران بی‌گمان در باغ‌هایی و (در) ناز و نعمتی می‌باشند.'),
(4753, 52, 18, 'به آنچه پروردگارشان به آنان داده سرگرم و دلشادند و پروردگارشان آنها را از عذاب بس زبانه‌دار دوزخ مصون داشته است.'),
(4754, 52, 19, '(به آنان گویند:) «به (پاداش) آنچه به جای می‌آورید به گوارایی بخورید و بنوشید.»'),
(4755, 52, 20, 'در حالی‌که بر تخت‌هایی ردیف (یکدیگر) تکیه زده‌اند اینان را همسران و حوران درشت‌چشم گردانده‌ایم.'),
(4756, 52, 21, 'و کسانی که ایمان آوردند و فرزندان و پیروانشان در ایمان از آنان پیروی کردند، ایشان را به آنان ملحق کردیم و چیزی از کار ایشان نکاستیم. هر کسی در گرو دستاورد خویش است.'),
(4757, 52, 22, 'در حالی‌که با (هر نوع) میوه‌ای و گوشتی که دلخواه آنهاست آنان را مدد (و تقویت) کردیم.'),
(4758, 52, 23, 'در آنجا جامی از دست هم می‌ربایند که در آن (ربودن جام) نه یاوه‌ای است و نه بازدارندگی از خیری.'),
(4759, 52, 24, 'و برای (خدمت) آنان پسرانی است که بر گردشان همی‌گردند، انگار بی‌گمان (آنان‌) مرواریدی‌اند که (در صدف) نهفته است.'),
(4760, 52, 25, 'و برخیشان رو به برخی کردند، حال آنکه از هم می‌پرسند.'),
(4761, 52, 26, 'گفتند: «ما پیشتر در میان خانواده‌ی خود با مهربانی (از عذاب) بیمناک بودیم.»'),
(4762, 52, 27, '«پس خدا بر ما منّت گذاشت و ما را از عذاب سمّی نگه داشت.»'),
(4763, 52, 28, '«ما بی‌گمان از دیرباز او را می‌خواندیم، که او، (هم)او بس نیکویِ رحمتگر بر ویژگان است.»'),
(4764, 52, 29, 'پس یادآوری(شان) کن که تو به نعمت پروردگارت نه کاهنی و نه دیوانه‌ای.'),
(4765, 52, 30, 'یا می‌گویند: «شاعری است (که) بسی انتظار مرگش را می‌بریم (و چشم به راهِ بَدِ زمانه بر اوییم).»'),
(4766, 52, 31, 'بگو: « منتظر باشید، پس من (نیز) با شما از منتظرانم.»'),
(4767, 52, 32, 'یا پندارهای خواب‌آلودشان، آنان را به این (موضعگیری) وا می‌دارد یا آنان گروهی سرکشند؟'),
(4768, 52, 33, 'یا می‌گویند: «آن را بربافته.» بلکه ایمان نمی‌آورند.'),
(4769, 52, 34, 'پس اگر (از) راستان بوده‌اند، سخنی همانند آن بیاورند.'),
(4770, 52, 35, 'یا از هیچ خلق شده‌اند یا آنکه خودشان آفریدگار (خود)ند؟'),
(4771, 52, 36, 'یا آسمان‌ها و زمین را (هم) خلق کرده‌اند؟ (نه)، بلکه یقین ندارند.'),
(4772, 52, 37, 'یا ذخایر پروردگار تو نزد آنهاست‌؟ یا (هم)ایشان تسلّط (تامّ بر آنها) دارند؟'),
(4773, 52, 38, 'یا نردبانی دارند که در آن می‌شنوند؟ پس باید شنونده‌ی آنان برهانی آشکارگر بیاورد.'),
(4774, 52, 39, 'یا خدا را دختران است و شما را پسران؟'),
(4775, 52, 40, 'یا از آنها مزدی مطالبه می‌کنی، پس آنان از (تعهّد ادای) تاوانی گرانبارند؟'),
(4776, 52, 41, 'یا (علم) غیب نزد آنهاست‌، پس آنان (آن را) می‌نویسند؟'),
(4777, 52, 42, 'یا می‌خواهند نیرنگی بزنند؟پس آنان که کافر شده‌اند، خودشان دچار نیرنگند.'),
(4778, 52, 43, 'یا ایشان را جز خدا معبودی است‌؟ منزّه است خدا از آنچه (با او) شریک می‌گیرند.'),
(4779, 52, 44, 'و اگر پاره‌هایی از ابر سیاه را در حال سقوط از آسمان ببینند گویند: «ابری متراکم است.»'),
(4780, 52, 45, 'پس بگذارشان تا به آن روزشان که در آن بیهوش می‌افتند برسند.'),
(4781, 52, 46, 'روزی که نیرنگشان به هیچ وجه به کارشان نیاید و نه آنان حمایت می‌شوند.'),
(4782, 52, 47, 'و بی‌گمان، برای کسانی که ظلم کرده‌اند غیر از این (عذاب) ، عذابی (دیگر) است ولی بیشترشان نمی‌دانند.'),
(4783, 52, 48, 'و برای حکم پروردگارت شکیبایی کن‌. پس تو خود در دیدگاه‌های مایی، و هنگامی که به پا خیزی با نیایش پروردگارت (همو را) تسبیح گوی.'),
(4784, 52, 49, 'و (پس از آن) پاره‌ای از شب و در فروشدن ستارگان، تسبیح‌گوی او باش.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'سوگند به اختر فروزان‌[:قرآن و آورنده‌اش] آن هنگام که فرود آمد؛'),
(4786, 53, 2, '(که) همنشین شما نه گمراه شده و نه در نادانی بر عقیده‌ی فاسدی است؛'),
(4787, 53, 3, 'و از سر هوا(ی نفس، یا هوای عقل یا هوای مشورت) هرگز سخنی نمی‌گوید.'),
(4788, 53, 4, 'گفتارش (و کردار و رفتارش) بجز وحی‌ای که (به او) وحی می‌شود نیست.'),
(4789, 53, 5, 'آن را (خدای) شدیدالقُوا به او آموخت.'),
(4790, 53, 6, 'او خود مرور کرده‌ای مستحکم (و) نیرومند است‌. پس (مسلّط بر جهان) ایستاد.'),
(4791, 53, 7, 'حال آنکه او در افق اعلی است.'),
(4792, 53, 8, 'سپس به خدا نزدیک شد؛ پس به او درآویخت و نزدیکتر شد.'),
(4793, 53, 9, 'پس (فاصله‌اش) به قدر (اتصال) دو (انتهای) کمان یا نزدیکتر از آن بود.'),
(4794, 53, 10, 'پس (همان خدای شدیدالقوا) به بنده‌اش آنچه را باید وحی فرماید وحی فرمود.'),
(4795, 53, 11, 'آنچه را دل لبریز از شعله‌ی نور(ش) دید انکار(ش) نکرد.'),
(4796, 53, 12, 'آیا پس در آنچه می‌بیند با او جدال می‌کنید؟'),
(4797, 53, 13, 'و همواره در حال فرود آمدنی دیگر هم، او را (با دل فروزانش) دید.'),
(4798, 53, 14, 'نزد سدرةالمنتهی [:آخرین مکان و مکانت عالم امکان].'),
(4799, 53, 15, '(که) بهشت جاودان نزد آن است.'),
(4800, 53, 16, 'چون «سدره» را آنچه می‌پوشد، می‌پوشدش.'),
(4801, 53, 17, 'دیده(اش) منحرف نگشت و از حدّ در نگذشت.'),
(4802, 53, 18, 'بی‌گمان (برخی) از آیات بزرگ پروردگار خود را همچنان بدید.'),
(4803, 53, 19, 'پس آیا شما (هم) لات و عزّی را دیدید؟'),
(4804, 53, 20, 'و (هم) منات‌، (آن) سومین دیگر را.'),
(4805, 53, 21, 'آیا (به خیالتان) برای شما پسر است و برای او دختر؟'),
(4806, 53, 22, 'این قسمت و بخشی است بسی ستم‌کارانه (و نابرابر).'),
(4807, 53, 23, '(این بتان) جز نام‌هایی (تهی) بیش نیستند که شما و پدرانتان برایشان (این گونه) نامگذاری کرده‌اید (و) خدا بر (حقّانیت) آنها هیچ دلیل چیره‌گری نفرستاده است‌. (آنان) جز گمان و آنچه را که دلخواهشان است پیروی نمی‌کنند، به‌راستی (و) درستی از جانب پرودگارشان برایشان هدایت آمده‌.'),
(4808, 53, 24, 'برای انسان است آنچه را آرزو کرد؟'),
(4809, 53, 25, 'پس آن سرا و این سرا تنها از آنِ خداست.'),
(4810, 53, 26, 'و چه بسیار فرشتگانی که در آسمان‌هایند حال آنکه شفاعتشان به کاری نیاید، مگر پس از آنکه خدا برای هر که خواهد و خشنود باشد اجازه دهد.'),
(4811, 53, 27, 'کسانی که به آخرت ایمان نمی‌آورند، بی‌گمان، هم‌آنان، فرشتگان را در نامگذاری به نام مؤنث نام می‌نهند.'),
(4812, 53, 28, 'و ایشان را به این (کار) هیچ (بینش و) دانشی نیست. جز گمان را پیروی نمی‌کنند، و گمان از حقیقت سود و بهره‌ای ندهد.'),
(4813, 53, 29, 'پس، از هر کس که از یاد ما روی برتافته و جز زندگی دنیا را نخواسته، روی برتاب.'),
(4814, 53, 30, 'این همان رسایی (نارسای) دانش آنان است. همواره پروردگار تو، خود به (حال) کسی که از راه او منحرف شده داناتر و او به کسی که راه یافته (نیز) آگاه‌تر است.'),
(4815, 53, 31, 'و هر چه در آسمان‌ها و هر چه در زمین است تنها از خداست، تا کسانی را که بد کرده‌اند، به آنچه انجام داده‌اند کیفر دهد و آنان را که نیکی کرده‌اند، به نیک‌تر (از آن) پاداش دهد.'),
(4816, 53, 32, 'آنان که از گناهان بزرگ و بزرگتر [:تجاوزگر] - جز لغزش‌های کوچک - خودداری می‌ورزند، پروردگارت (نسبت به آنها) پوششی فزاینده دارد. چون شما را از زمین پدید آورد، و چون در شکم‌های مادرانتان (در زهدان‌هاشان) نهفته بودید، به (حال) شما داناتر است، پس خودهاتان را پاک مشمارید. او به (حال) کسی که پرهیزگاری کرده داناتر است.'),
(4817, 53, 33, 'آیا پس آن کس را که (از حق) روی برتافته دیدی‌؟'),
(4818, 53, 34, 'اندکی بخشید و (از مابقی) بخل ورزید.'),
(4819, 53, 35, 'آیا نزدش علم غیب است؛ پس (هم)او (آن را) می‌بیند؟'),
(4820, 53, 36, 'یا بدانچه در صحیفه‌های موسی است خبر نیافته است‌؟'),
(4821, 53, 37, 'و (نیز در صحیفه‌های) ابراهیم که به خوبی (در انجام رسالتش) وفا کرد.'),
(4822, 53, 38, 'که هیچ بردارنده‌ی باری، بار (گناه) دیگری را بر نمی‌دارد.'),
(4823, 53, 39, 'و اینکه برای انسان جز حاصل تلاش خودش نیست.'),
(4824, 53, 40, 'و همواره (نتیجه‌ی) کوشش او به زودی (در برزخ) دیده خواهد شد.'),
(4825, 53, 41, 'سپس - هر چه تمام‌تر- بدان (در قیامت) پاداش داده شود.'),
(4826, 53, 42, 'و اینکه به‌راستی فرجام (کار) سوی پروردگار تو است.'),
(4827, 53, 43, 'و اینکه به‌راستی اوست که خنداند و گریاند.'),
(4828, 53, 44, 'و اینکه به‌راستی (هم)او میرانْد و زنده کرد.'),
(4829, 53, 45, 'و هم اوست که به درستی دو نوع آفرید: نر و ماده.'),
(4830, 53, 46, 'از نطفه‌ای آن هنگام که (ضمن منی) می‌جهد.'),
(4831, 53, 47, 'و هم پدید آوردن (عالم) دیگر به‌راستی تنها بر (عهده‌ی) اوست.'),
(4832, 53, 48, 'و هم اوست که (شما را) بی‌نیاز کرد و کفایت بخشید.'),
(4833, 53, 49, 'و هم اوست که پروردگار (ستاره‌ی) «شعری‌» است.'),
(4834, 53, 50, 'و هم اوست که عادیان نخستین را هلاک کرد.'),
(4835, 53, 51, 'و ثمودیان را (نیز هلاک کرد،) پس (کسی از آنان را) باقی نگذاشت.'),
(4836, 53, 52, 'و پیشتر (از همه‌ی آنان) قوم نوح را، (که) بی‌گمان آنان ستمگرترین (ستمگران) و سرکش‌ترین (سرکشان) بودند'),
(4837, 53, 53, 'و دروغ‌پردازان را فرو افکند.'),
(4838, 53, 54, 'پس پوشاند بر آنان (از باران گوگردی) آنچه پوشاند.'),
(4839, 53, 55, 'پس به کدام یک از نعمت‌های پروردگارت تردید داری‌؟'),
(4840, 53, 56, 'این (پیامبر نیز) بیم‌دهنده‌ای از (جمله‌ی) بیم‌‌دهندگان نخستین است.'),
(4841, 53, 57, 'نزدیک‌شونده [:رستاخیز] نزدیک شد.'),
(4842, 53, 58, 'جز خدا کسی آشکارکننده‌ی آن (از نهان) نیست.'),
(4843, 53, 59, 'آیا پس از این سخن عجب دارید؟'),
(4844, 53, 60, 'و می‌خندید و نمی‌گریید؟'),
(4845, 53, 61, 'و شما در لهو، متکبر و سر به هوایید.'),
(4846, 53, 62, 'پس برای خدا سجده کنید و هم‌او را بپرستید.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'ساعت نزدیک شد و ماه (فرو) شکافت.'),
(4848, 54, 2, 'و اگر نشانه‌ای بینند روی گردانند و گویند: «سحری همیشگی است.»'),
(4849, 54, 3, 'و تکذیب نمودند و هوس‌های خویش را پیروی کردند. و هر امری [:فرمان، کار و چیزی] قرارگیرنده است.'),
(4850, 54, 4, 'و همواره به‌راستی از اخبار مهم آنچه را جایگاه انزجار (از کفر) است به ایشان رسید.'),
(4851, 54, 5, 'حکمتی‌رسا(ست). پس ترسانندگان بی‌نیاز(شان) نمی‌کنند.'),
(4852, 54, 6, 'پس روزی که داعیِ (حق) سوی امری دهشتناک (و ناشناخته) دعوت می‌کند، از آنان روی برتاب.'),
(4853, 54, 7, 'در حالی که دیدگانشان بسی فروهشته است، چون ملخ‌های پراکنده از گورها(ی خود) بر می‌آیند.'),
(4854, 54, 8, 'سوی آن دعوتگر، چشم دوخته و نگرانند. کافران می‌گویند: «این روزی دشوار است.»'),
(4855, 54, 9, 'پیش از آنان، قوم نوح (نیز) به تکذیب پرداختند. پس بنده‌ی ما را تکذیب کردند و گفتند: «دیوانه‌ای است.‌» و بسی زجرکشیده.'),
(4856, 54, 10, 'پس پروردگارش را خواند که: «بسی مغلوب شده‌ام‌؛ پس بس یاری(ام) فرمای!»'),
(4857, 54, 11, 'پس درهای آسمان را به آبی بس ریزان و فراوان (بر سروسامانشان) گشودیم.'),
(4858, 54, 12, 'و زمین را با انفجاری سراسری تبدیل به چشمه‌هایی کردیم. پس آب (فراگیر) بر کاری که به‌راستی مقدر شده بود در رسید.'),
(4859, 54, 13, 'و او را بر (کشتیِ) دارای الوارهای میخ‌آجین سوار کردیم.'),
(4860, 54, 14, 'حال آنکه (کشتی) زیر نظرهای (علم و توان و حکمت) ما روان است. (این) پاداش کسی بود که مورد انکار واقع شده بود.'),
(4861, 54, 15, 'و به‌راستی آن (سفینه) را بی‌چون (بر جایی) نهادیم (تا) عبرتی مستمر (باشد)؛ پس آیا هیچ پندگیرنده‌ای هست‌؟'),
(4862, 54, 16, 'پس چگونه بود عذابم و هشداردهندگان (از سوی) من‌؟'),
(4863, 54, 17, 'و قرآن را بی‌گمان برای هشداری آسان کردیم. پس آیا هیچ پندگیرنده‌ای هست‌؟'),
(4864, 54, 18, 'عادیان به تکذیب پرداختند. پس چگونه بود عذابم و هشداردهندگان (از سوی) من‌؟'),
(4865, 54, 19, 'ما بی‌گمان بر (سر و سامان)شان در روز شومی، به طور مداوم، تند بادی سخت سردِ توفنده فرو فرستادیم،'),
(4866, 54, 20, '(که) مردم را از جای می‌کَنَد؛ گویی تنه‌های نخلی بودند که ریشه کن شده‌اند.'),
(4867, 54, 21, 'پس چگونه بود عذاب من و هشداردهندگان (از سوی) من‌؟'),
(4868, 54, 22, 'و ما قرآن را بی‌گمان برای پند‌گیری بس آسان کردیم‌؛ پس آیا هیچ پندگیرنده‌ای هست‌؟'),
(4869, 54, 23, 'ثمودیان به‌وسیله‌ی هشداردهندگان (همانان را) تکذیب کردند.'),
(4870, 54, 24, 'پس گفتند: «آیا بشری از خودمان را (که) تنها (و بی‌‌یار و گواه است) پیروی کنیم‌؟ در این صورت، ما همواره در گمراهی و آتشباری هستیم.»'),
(4871, 54, 25, '«آیا از میان ما این یادواره (تنها) بر او القا شده است‌؟ (نه،) بلکه او بس دروغ‌پردازی بس خوشحال (و خودپسند) است.»'),
(4872, 54, 26, 'زودا خواهید دانست چه کسی بس دروغ‌پردازی، بس خوشحال است.'),
(4873, 54, 27, 'ما همواره برای آزمایش آتشینشان (آن) ماده شتر را فرستنده‌ایم‌. پس (به صالح گفتیم:) «منتظرشان باش و بسی شکیبایی کن.»'),
(4874, 54, 28, '«و به آنان (این) خبر مهم را بده که آب میانشان قسمت شده است: هرکدام را آشامیدنی (به نوبت آمده) و حاضر شده است.»'),
(4875, 54, 29, 'پس (سرکرده‌ی) همگامشان را ندا در دادند. پس او شمشیر برگرفت و (شتر را) پی کرد و از پا درآورد.'),
(4876, 54, 30, 'پس چگونه بود عذابم و هشداردهندگان (از سوی) من‌؟'),
(4877, 54, 31, 'ما بر (سر و سامان)شان یک بانگ (مرگبار) فرو فرستادیم و چون گیاه خشکیده‌(ی کومه‌ها) ریز ریز و آماده‌(ی سوختن یا خوردن حیوانات) شدند.'),
(4878, 54, 32, 'و ما همواره قرآن را برای پند‌گیری(شان) آسان کردیم‌؛ پس آیا پند گیرنده‌ای هست‌؟'),
(4879, 54, 33, 'قوم لوط با هشداردهندگان (همان‌ها و ما را) را تکذیب کردند.'),
(4880, 54, 34, 'ما بر (سر و سامان)شان سنگبارانی انفجاری فرو فرستادیم (و) فقط خانواده‌ی لوط بودند که سحرگاهان نجاتشان دادیم.'),
(4881, 54, 35, 'حال آنکه نعمتی از جانب ما بود. هر که پاس و سپاس دارد، بدین‌سان (او را) پاداش می‌دهیم.'),
(4882, 54, 36, 'و (لوط) آنها را همواره از حمله‌ی ناگهانیمان سخت بیم داده بود. پس درباره‌ی تهدیدکنندگان تردید کرده‌، به جدال برخاستند.'),
(4883, 54, 37, 'و همواره از مهمان(های) او با حیله و مراوده بسی کام دل خواستند. پس فروغ دیدگانشان را ستردیم. پس عذاب مرا و هشداردهندگان مرا بچشید.'),
(4884, 54, 38, 'و به‌راستی صبحگاهان بی‌امان عذابی ماندنی به سراغ آنان آمد.'),
(4885, 54, 39, 'عذاب و هشداردهندگان مرا بچشید.'),
(4886, 54, 40, 'و بی‌گمان قرآن را برای هشدار آسان کردیم‌؛ پس آیا هیچ هشدارگیرنده‌ای هست‌؟'),
(4887, 54, 41, 'و به‌درستی هشداردهندگان سوی فرعونیان آمدند.'),
(4888, 54, 42, '(اما آنها) با همه‌ی نشانه‌های ما (همان‌ها و ما را) تکذیب کردند. پس ما با عزت و اقتدار (گریبان) آنان را فرو گرفتیم.'),
(4889, 54, 43, 'آیا کافرانتان، از اینان (که برشمردیم) برترند، یا شما را در نوشته‌ها(ی وحیانی‌‌تان) خط امانی (از تکلیف و عذاب) است‌؟'),
(4890, 54, 44, 'یا می‌گویند: «ما با یکدیگر پیوند و جمع‌ایم (که) از هم کمک یافته (و در نتیجه) چیره‌ایم!»'),
(4891, 54, 45, 'زودا که این جمع در هم شکسته شوند و پشت کنند.'),
(4892, 54, 46, 'بلکه موعدشان‌، ساعت [:قیامت] است و ساعت‌، هراسناک‌تر و تلخ‌تر است.'),
(4893, 54, 47, 'مجرمان بی‌گمان در گمراهی و آتشِ زبانه‌دارند.'),
(4894, 54, 48, 'روزی که در آتش به رو کشیده می‌شوند (و به آنان گفته شود:) «لهیب آتش‌سوزان (کبود کننده‌ی چهره و هلاکت‌بار) را بچشید و احساسش کنید.»'),
(4895, 54, 49, 'به‌راستی ما هر چیزی را به اندازه‌ای (حکیمانه و آگاهانه) آفریده‌ایم.'),
(4896, 54, 50, 'و فرمان ما جز یک (اراده) نیست‌، (آن هم) چون چشم بر هم زدنی (یا کمتر).'),
(4897, 54, 51, 'و همواره (هم‌مسلکان و) همراهان‌تان را سخت به هلاکت رساندیم. پس آیا هشدارگیرنده‌ای هست‌؟'),
(4898, 54, 52, 'و هر چه کردند در کتاب‌ها(ی اعمالشان و کتاب علم خدا) درج است.'),
(4899, 54, 53, 'و هر خُرد و بزرگی (در آن) به خوبی نوشته شده است.'),
(4900, 54, 54, 'همواره پرهیزگاران در میان باغ‌ها و نهرها،'),
(4901, 54, 55, 'در نشست‌گاهی راستین نزد شاهنشاهی توانمندند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, '(خدای) رحمتگر بر آفریدگان'),
(4903, 55, 2, 'قرآن را تعلیم داد (و)'),
(4904, 55, 3, 'انسان را آفرید (و)'),
(4905, 55, 4, 'به او بیان آموخت.'),
(4906, 55, 5, 'خورشید و ماه (سخت) زیر پوشش مراقبت و حسابند.'),
(4907, 55, 6, 'و ستاره و بوته و درخت سجده‌کنانند.'),
(4908, 55, 7, 'و آسمان را برافراشت و میزان (تکوین و تشریع و تکالیف) را فروگذاشت.'),
(4909, 55, 8, 'که مبادا در این میزان طغیان کنید.'),
(4910, 55, 9, 'و وزن را به انصاف بر پا بدارید و سنجش را زیان نرسانید .'),
(4911, 55, 10, 'و زمین را برای آدمیان و پریان نهاد.'),
(4912, 55, 11, 'که) در آن، میوه‌ها و نخل‌ها با خوشه‌های غلاف‌دار،'),
(4913, 55, 12, 'و دانه‌های پوست‌دار و گیاه خوش‌بوی ریحانی است.'),
(4914, 55, 13, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4915, 55, 14, 'انسان را از گل خشکیده‌ای همانند کوزه‌گر آفرید.'),
(4916, 55, 15, 'و جنّ را از مخلوطی آتشین آفرید.'),
(4917, 55, 16, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4918, 55, 17, 'پروردگار دو خاور و پروردگار دو باختر.'),
(4919, 55, 18, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4920, 55, 19, 'دو دریا را (به گونه‌ای) در هم آمیخت در حالی‌که با هم برخوردی نمی‌کنند.'),
(4921, 55, 20, 'میان آن دو، فاصله‌ای است که به هم تجاوز نمی‌کنند.'),
(4922, 55, 21, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4923, 55, 22, 'از هر دو (دریا) مروارید و مرجان برآید.'),
(4924, 55, 23, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4925, 55, 24, 'و او را در دریا کشتی‌های سازمان‌یافته(‌ای است که) همچون پرچم‌ها است.'),
(4926, 55, 25, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4927, 55, 26, 'هر چه بر زمین [:طبیعت] تکیه زده، فانی است.'),
(4928, 55, 27, 'و ذات باشکوه و ارجمند کرامت‌دهنده و وجهه‌ی پروردگارت باقی است.'),
(4929, 55, 28, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4930, 55, 29, 'هر که در آسمان‌ها و زمین است از او درخواست می‌کند. هر روزی [:زمانی]، او در کار و فرمانی است.'),
(4931, 55, 30, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4932, 55, 31, 'ای جنّ و انس! زودا که ما به خوبی به شما بپردازیم.'),
(4933, 55, 32, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4934, 55, 33, 'ای گروه جنّیان و انسیان! اگر می‌توانید از کرانه‌های آسمان‌ها و زمین به بیرون رخنه کنید، پس رخنه کنید، (ولی) جز در سلطه‌(ی ربانی) در نیایید.'),
(4935, 55, 34, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4936, 55, 35, 'بر سر شما شراره‌هایی از آتش و از مس فرو فرستاده خواهد شد. پس (از کسی) یاری نتوانیدگرفت.'),
(4937, 55, 36, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4938, 55, 37, 'پس آن‌گاه که آسمان از هم شکافد و چون ته‌مانده‌ی روغن زیتون گلگون گردد.'),
(4939, 55, 38, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4940, 55, 39, 'پس در آن روز، هیچ انس و جنّی از گناهش پرسیده نشود.'),
(4941, 55, 40, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4942, 55, 41, 'تبهکاران از سیمایشان شناخته می‌شوند و از پیشانی‌هایشان و پاهایشان گرفته می‌شوند.'),
(4943, 55, 42, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4944, 55, 43, 'این همان جهنمی است که تبهکاران با آن (همان و او را) تکذیب می‌کنند.'),
(4945, 55, 44, '(اینان) میان جهنم و مایعی بس داغ و لبریز می‌گردند.'),
(4946, 55, 45, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4947, 55, 46, 'و هر کس را که از مقام پروردگارش بترسد دو باغ (سردرهم) است.'),
(4948, 55, 47, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4949, 55, 48, 'که دارای شاخساران رنگارنگند.'),
(4950, 55, 49, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4951, 55, 50, 'در آن دو (باغ) دو چشمه‌سار روان است.'),
(4952, 55, 51, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4953, 55, 52, 'در آن دو (باغ) از هر میوه‌ای دو گونه همسان است.'),
(4954, 55, 53, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4955, 55, 54, 'بر بسترهایی که آستر آنها از ابریشم درشت‌بافت است تکیه زنند و چیدن میوه‌ی آن دو باغ (به آسانی) در دسترس است.'),
(4956, 55, 55, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4957, 55, 56, 'در آن (باغ‌ها، دلبرانی) فروهشته‌نگاهند که دست هیچ انس و جنّی پیش از ایشان به آنان نرسیده است.'),
(4958, 55, 57, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4959, 55, 58, 'گویی که آنها یاقوت و مرجان‌اند.'),
(4960, 55, 59, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4961, 55, 60, 'آیا پاداش احسان جز احسان است‌؟'),
(4962, 55, 61, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4963, 55, 62, 'و پایین‌تر از آن دو (باغ)، دو باغ (دیگر نیز) هست.'),
(4964, 55, 63, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4965, 55, 64, '(که از شدّت سبزی) آن دو سیه‌گون می‌نمایند.'),
(4966, 55, 65, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4967, 55, 66, 'در آن دو (باغ) دو چشمه‌ساران همچنان جوشانند.'),
(4968, 55, 67, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4969, 55, 68, 'در آن دو، میوه‌ای و درخت خرمایی و اناری است‌.'),
(4970, 55, 69, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4971, 55, 70, 'در آنجا (زنانی) نکوخویان و نکورویانند.'),
(4972, 55, 71, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4973, 55, 72, '(و) حورانی، پرده‌نشینان در (دل) خیمه‌ها.'),
(4974, 55, 73, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4975, 55, 74, 'هیچ انس و جنّی پیش از ایشان با آنان هم بستر نشده است.'),
(4976, 55, 75, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4977, 55, 76, 'بر بالش‌های سبز و فرش‌هایی بس نیکو تکیه زده‌اند.'),
(4978, 55, 77, 'پس به کدام یک از نعمت‌های پروردگارتان (همان و او را) تکذیب می‌کنید؟'),
(4979, 55, 78, 'خجسته است نام پروردگار شکوهمند و گرامی‌دارنده.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'هنگامی که (قیامت) واقع شونده وقوع یابد؛'),
(4981, 56, 2, 'در وقوعش هرگز گزافی نیست.'),
(4982, 56, 3, 'پست‌کننده‌ای بالابرنده است.'),
(4983, 56, 4, 'چون زمین با تکان و اضطراب سختی لرزانده شود؛'),
(4984, 56, 5, 'و کوه‌ها به گونه‌ای دهشت‌آور خُرد گردند؛'),
(4985, 56, 6, 'پس غباری پراکنده شوند؛'),
(4986, 56, 7, 'و شما سه دسته گردید:'),
(4987, 56, 8, 'پس صاحبان برکت راستین؛ چیست صاحبان برکت‌؟'),
(4988, 56, 9, 'و صاحبان شوم و رذیلت؛ چیست صاحبان رذیلت‌؟'),
(4989, 56, 10, 'و سبقت‌گیرندگان (این سرا) همان سبقت‌گیرندگان (آن سرای)اند.'),
(4990, 56, 11, 'همان مقرّبان (به خدای منان).'),
(4991, 56, 12, 'در باغستان‌های پرنعمت.'),
(4992, 56, 13, '(اینان) گروهی بسیار از پیشینیان،'),
(4993, 56, 14, 'و اندکی از پسینیان(اند).'),
(4994, 56, 15, 'بر تخت‌هایی بافته همچون زره.'),
(4995, 56, 16, 'روبه‌روی هم بر آنها تکیه‌زنندگانند.'),
(4996, 56, 17, 'نوجوانانی، جاودان بر گردشان می‌گردند.'),
(4997, 56, 18, 'با جام‌ها و آبریزها و پیاله‌‌ای از باده‌ی ناب روان.'),
(4998, 56, 19, '(که) نه از آن سردرد گیرند و نه (آن را) پایان دهند.'),
(4999, 56, 20, 'و میوه‌ای از هر چه همی‌پسندند.'),
(5000, 56, 21, 'و (از) گوشت پرنده‌ای، هر چه اشتها کنند.'),
(5001, 56, 22, 'و حوریانی چشم‌‌درشت.'),
(5002, 56, 23, 'همانند مروارید (ناسفته‌ی) پنهان (در صدف).'),
(5003, 56, 24, 'حال آنکه پاداشی است به آنچه می‌کرده‌اند.'),
(5004, 56, 25, 'در آنجا نه بیهوده‌ای می‌شنوند و نه (سخنی) گناه‌‌آلود با پی‌آمدی بد.'),
(5005, 56, 26, '(سخنی) بجز گفته‌ی سلامی سالم نیست.'),
(5006, 56, 27, 'و صاحبان برکت و راستین. و چیست این صاحبان برکت و راستین؟'),
(5007, 56, 28, 'در (زیر) درختان کُناری بی‌خار.'),
(5008, 56, 29, 'و درخت‌های موز که میوه‌اش خوشه‌خوشه روی هم انباشته است.'),
(5009, 56, 30, 'و سایه‌ای پایدار (و کشش‌دار).'),
(5010, 56, 31, 'و آبی از بلندایی ریزان.'),
(5011, 56, 32, 'و میوه‌ای فراوان.'),
(5012, 56, 33, 'نه انقطاع یافته و نه ممنوع.'),
(5013, 56, 34, 'و همخوابگانی بالا بلند در بلنداها(ی تخت‌ها).'),
(5014, 56, 35, 'ما بی‌گمان آنان را پدید آورده‌ایم، پدید آوردنی (بس نیکو)!'),
(5015, 56, 36, 'پس ایشان را دوشیزه و دست‌‌نخورده قرار دادیم.'),
(5016, 56, 37, 'دارای تمامی زیبایی آشکار زنان؛ همسالان با شوهران.'),
(5017, 56, 38, 'برای راستان و برکت یافتگان.'),
(5018, 56, 39, 'گروهی از پیشینیان،'),
(5019, 56, 40, 'و گروهی از پسینیان.'),
(5020, 56, 41, 'و چپی‌های نکوهیده. چیست چپی‌های نکوهیده‌؟'),
(5021, 56, 42, 'غرق شدگان در سمّی فراوان و (مایعی) جوشان.'),
(5022, 56, 43, 'و سایه‌ای از دود تار پر خفقان.'),
(5023, 56, 44, 'نه خنک و نه ملایم.'),
(5024, 56, 45, 'اینان بودند که همواره پیش از این نازپروردگانی در نعمتی فراوان غرق بوده‌اند.'),
(5025, 56, 46, 'و بر گناه بزرگِ پیمان‌شکنی پافشاری می‌کرده‌اند؟'),
(5026, 56, 47, 'و می‌گفته‌اند: «آیا هنگامی(که) مردیم و خاک و استخوان شدیم، آیا همین ما (باز هم) همانا برانگیختگانیم؟»'),
(5027, 56, 48, '«آیا و پدران نخستینمان (نیز)؟!»'),
(5028, 56, 49, 'بگو: «بی‌گمان پیشینیان و پسینیان.»'),
(5029, 56, 50, '«به‌راستی همگان در موعد روزی معلوم گردآوری شوندگانند.»'),
(5030, 56, 51, 'سپس بی‌گمان شما ای گمراهان و تکذیب‌‌کنندگان!'),
(5031, 56, 52, 'همواره از درختی (که) از زقّوم (است) خورندگانید.'),
(5032, 56, 53, 'پس پر کنندگان شکم‌ها(ی‌تان) از آنید.'),
(5033, 56, 54, 'پس روی آن از آن مایه‌ی جوشان نوشندگانید.'),
(5034, 56, 55, 'پس نوشندگان، (چونان) نوشیدن اشتران تشنه.'),
(5035, 56, 56, 'این است مهمانسرای آنان به روز جزا.'),
(5036, 56, 57, 'ما شما را آفریدیم، پس چرا تصدیقمان نمی‌کنید؟'),
(5037, 56, 58, 'آیا پس آنچه را (که به صورت نطفه) فرو می‌جهانید دیده‌اید؟'),
(5038, 56, 59, 'آیا شما آن را می‌آفرینید یا ما آفریننده‌ایم‌؟'),
(5039, 56, 60, 'ماییم که میان شما مرگ را مقدّر کرده‌ایم و ما هرگز (درماندگان و) پیشی‌گرفته‌شدگان نیستیم،'),
(5040, 56, 61, 'بر اینکه (شما را به) همانندتان تبدیل کنیم و شما را در آنچه نمی‌دانید (دیگر بار) پدیدار سازیم.'),
(5041, 56, 62, 'و همواره بی‌چون پدیدار شدن نخستین خود را شناختید. پس چرا به شایستگی (آن را) یاد نمی‌کنید؟'),
(5042, 56, 63, 'آیا پس آنچه را کشت می‌کنید، دیده‌اید؟'),
(5043, 56, 64, 'آیا شما آن را (بی‌یاری ما) زراعت می‌کنید، یا ماییم که زراعت‌کنندگانیم‌؟'),
(5044, 56, 65, 'اگر بخواهیم همواره خاشاکش می‌گردانیم، پس در افسوس (و تعجب) می‌افتید.'),
(5045, 56, 66, '(و می‌گویید:) «همانا ما فریب‌خوردگان و زیان‌‌دید‌گانیم.»'),
(5046, 56, 67, '«بلکه ما محرومانیم.»'),
(5047, 56, 68, 'آیا پس آبی را که می‌نوشید دیده‌اید؟'),
(5048, 56, 69, 'آیا شما آن را از (دلِ) ابر آبستن به آب فرود آوردید، یا ما فرود آورندگانیم‌؟'),
(5049, 56, 70, 'اگر بخواهیم آن را تلخ می‌گردانیم. پس چرا سپاس نمی‌دارید؟'),
(5050, 56, 71, 'آیا پس آتشی را که بر می‌افروزید نگریسته‌اید؟'),
(5051, 56, 72, 'آیا شما درختش را - بی‌سابقه- پدیدار کردید یا ما پدید آورند‌گانیم؟'),
(5052, 56, 73, 'ما آن را یادواره و برخورداری برای نیازمندان- که نیروی زندگی‌بخش می‌خواهند- قرار دادیم.'),
(5053, 56, 74, 'پس به نام پروردگار بزرگت (او را) تنزیه کن.'),
(5054, 56, 75, 'پس به فرودگاه‌های ستارگان [:دل‌‌های فروزان پیمبران] سوگند نمی‌خورم.'),
(5055, 56, 76, 'و اگر بدانید، آن سوگندی سخت بزرگ است!'),
(5056, 56, 77, 'همانا این (پیام وحیانی) همواره قرآنی دارای برکاتی همگانی است.'),
(5057, 56, 78, 'در کتابی (از دستبرد) نگهبانی شده.'),
(5058, 56, 79, 'که جز پاک‌شدگان بدان دست نیازند.'),
(5059, 56, 80, 'فرود آمده‌ای است تدریجی از جانب پروردگار جهانیان.'),
(5060, 56, 81, 'آیا شما این گفتار نوین را سبک‌گیرندگانید؟'),
(5061, 56, 82, 'و تنها نصیب خودتان را همواره در تکذیب (آن) قرار می‌دهید؟'),
(5062, 56, 83, 'پس چرا نه، آن‌گاه که (جانتان) به گلویتان رسد.'),
(5063, 56, 84, 'و حال آنکه- در این هنگام- خود نظاره‌گرید.'),
(5064, 56, 85, 'و ما به آن (محتضر) از شما نزدیکتریم ولی نمی‌بینید.'),
(5065, 56, 86, 'پس چرا، نه اگر شما جزا نایافتگانید (و حساب و کتابی در کار نیست)،'),
(5066, 56, 87, 'چرا اگر راست می‌گویید، روح را بر نمی‌گردانید؟'),
(5067, 56, 88, 'پس اما اگر او از مقرّبان باشد،'),
(5068, 56, 89, 'در نتیجه (در) آسایش و راحت و بهشت پُرنعمت (است).'),
(5069, 56, 90, 'و اما اگر از راستان و برکت‌یافتگان باشد؛'),
(5070, 56, 91, 'پس، از این راستان برایت سلامی است.'),
(5071, 56, 92, 'و اما اگر از تکذیب‌کنندگان گمراه بوده است،'),
(5072, 56, 93, 'پس (برایشان) مهمانخانه‌ای از مایعی جوشان است؛'),
(5073, 56, 94, 'و (نیز) افروختن آتشی فروزان.'),
(5074, 56, 95, 'به‌راستی این همان حق‌الیقین است.'),
(5075, 56, 96, 'پس به نام پروردگار بزرگت تسبیح گوی.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'آنچه در آسمان‌ها و زمین است، خدا را به پاکی ستوده‌اند و اوست عزیز حکیم.'),
(5077, 57, 2, 'فرماندهی آسمان و زمین تنها از اوست، و او بر هر چیزی بسی تواناست.'),
(5078, 57, 3, 'اوست اول و آخر و ظاهر و باطن و او به هر چیزی بسی داناست.'),
(5079, 57, 4, 'اوست کسی که آسمان‌ها و زمین را در شش روز [:زمان] آفرید. سپس بر عرش (ربوبیت) چیره شد، در حالی‌که آنچه در زمین فرو رود و آنچه از آن برآید و آنچه از آسمان فرود آید و آنچه در آن بالا رود (همه را) می‌داند. و هر کجا بودید او با شماست و خدا به هر چه می‌کنید بسیار بیناست.'),
(5080, 57, 5, 'فرمانروایی (مطلق) آسمان‌ها و زمین تنها از اوست و همه‌ی چیزها تنها سوی خدا بازگردانیده می‌شوند.'),
(5081, 57, 6, 'شب را در روز می‌آورد و روز را در شب می‌آورد. و او به (راز) ذاتی سینه‌ها بسی داناست.'),
(5082, 57, 7, 'به خدا و پیامبرش ایمان آورید و از آنچه شما را در (استفاده از) آن، جانشین (دیگران) کرده، انفاق کنید. پس کسانی از شما که ایمان آورده و انفاق کردند، پاداشی بزرگ برایشان (مقرر) است.'),
(5083, 57, 8, 'و شما را چیست (که) به خدا ایمان نمی‌آورید و حال آنکه پیامبر (خدا) شما را دعوت می‌کند تا به پروردگارتان ایمان آورید و اگر مؤمن بوده‌اید، و خدا بی‌گمان از شما پیمان گرفته است.'),
(5084, 57, 9, 'او کسی است که بر بنده‌ی خود آیات روشنی (را) به‌تدریج فرو می‌فرستد، تا شما را از تاریکی‌ها به نور برون آورد و خدا همواره (نسبت) به شما بسی مهربان (و) رحمتگر بر ویژگان است.'),
(5085, 57, 10, 'و شما را چیست که در راه خدا انفاق نمی‌کنید؟ حال آنکه میراث آسمان‌ها و زمین تنها از آنِ اوست. کسانی از شما که پیش از (آن) پیروزی (بزرگ)، انفاق و کشتار کردند، (با دیگران) یکسان نیستند. ایشان از حیث درجه بزرگ‌تر از کسانی‌اند که پس از فتح به انفاق و کشتار پرداخته‌اند. و خدا به هر کدام وعده‌ی نیکوترین (زندگی) را داده و خدا به آنچه می‌کنید بسی آگاه است.'),
(5086, 57, 11, 'کیست آن کس که خدا را وامی نیکو دهد تا برای وی چندانش گرداند و او را پاداشی پرکرامت است.'),
(5087, 57, 12, 'روزی که مردان و زنان مؤمن را می‌بینی که نورشان پیشاپیش آنها و از جانب راستشان می‌شتابد (به آنان گویند:) «امروز مژده‌ی شما باغ‌هایی است که از زیر (درختان)شان نهرها روان است حال آنکه در آن‌ها جاودانید. این است همان کامیابی سالم بزرگ.»'),
(5088, 57, 13, 'روزی که مردان و زنان منافق به کسانی که ایمان آورده‌اند گویند: «ما را بنگرید تا مقداری از نورتان را بهره برگیریم.‌» گفته شد: «واپس روید. پس نوری درخواست کنید.» پس میانشان دیواری زده شد که آن را دربی است: باطنش در (ژرفای) رحمت و از سوی ظاهرش عذاب است.'),
(5089, 57, 14, '(در آن روز) دو رویان، آنان را ندا در می‌دهند: «آیا ما با شما نبودیم‌؟» گفتند: «چرا ولی شما خودتان را در فتنه افکندید و امروز و فردا کردید و تردید آوردید، و آرزوها(ی باطل)، شما را مغرور کرد تا فرمان خدا آمد و (شیطانِ) غرورآفرین، شما را درباره‌ی خدا به غرور آورد.»'),
(5090, 57, 15, '«پس امروز نه از شما و نه از کسانی که کافر شده‌اند عوضی گرفته نشود. پناهگاهتان آتش است. (هم) آن سرپرست و پشتوانه‌ی شماست و چه بد سرانجامی است.»'),
(5091, 57, 16, 'آیا برای کسانی که ایمان آورده‌اند هنگام آن نرسیده که دل‌هایشان به یاد خدا و آنچه از حق نازل شده خاشع گردد و مانند کسانی نباشند که از پیش به آنها کتاب داده شد و زمان بر آنان به درازا کشید، پس دل‌هایشان سخت گردید. در حالی که بسیاری از آنان فاسقانند.'),
(5092, 57, 17, 'بدانید که خدا همانا زمین را پس از مرگش زنده می‌گرداند. به‌راستی آیات (خود) را برای شما روشن گردانیدیم، شاید شما خردورزی کنید.'),
(5093, 57, 18, 'بی‌گمان، مردان و زنان صدقه‌دهنده حال آنکه به خدا وامی نیکو داده‌اند، ایشان را (رحمتی) مضاعف و برایشان پاداشی با کرامت است.'),
(5094, 57, 19, 'و کسانی که به خدا و پیامبرانش ایمان آوردند، ایشان، (هم)آنان راستان و گواهان نزد پروردگارشانند (و) ایشان راست پاداششان و نورشان‌. و کسانی که کفر ورزیده و به آیاتمان (همان‌ها و ما را) تکذیب کردند آنان همدمان آتشند.'),
(5095, 57, 20, 'بدانید که زندگی دنیا، تنها بازی و سرگرمی بازدارنده‌ی از حق و آرایش و فخرفروشی شما به یکدیگر، و فزون‌خواهی در اموال و فرزندان است. (مَثَل آنها) همچون بارانی است که کافر را رُستنی آن (باران) به شگفتی اندازد. سپس (آن کِشت،) به هیجان آید، پس تا آن را زرد بینی؛ سپس خاشاک شود. و در آخرت (دنیاپرستان را) عذابی سخت است و (مؤمنان را) از (جانب) خدا پوششی و خشنودی بزرگی است‌. و زندگانی دنیا جز کالایی فریبنده نیست.'),
(5096, 57, 21, '(برای رسیدن) فراسوی پوششی از پروردگارتان و بهشتی که پهنایش چون پهنای آسمان و زمین است (و) برای کسانی آماده شده که به خدا و پیامبرانش ایمان آوردند؛ بر یکدیگر سبقت جویید. این فضل خداست که به هر کس بخواهد آن را می‌دهد. و خدا کانون فزونی‌بخش بزرگی است.'),
(5097, 57, 22, 'هیچ مصیبتی نه در زمین و نه در خودهاتان به شما نرسید، مگر اینکه - پیش از آنکه شما را سازمان دهیم- (علمش) در کتابی است (ربانی). این (کار) همواره بر خدا آسان است.'),
(5098, 57, 23, 'تا بر آنچه از دست شما رفته اندوهگین نشوید، و به آنچه به شما داده است شادمانی نکنید. و خدا هیچ خودپسند فخرکننده‌ای را دوست نمی‌دارد؛'),
(5099, 57, 24, 'کسانی که بخل می‌ورزند و مردمان را به بخل ورزیدن فرمان می‌دهند. و هر که روی گرداند خدا بی‌گمان (هم) او بی‌نیاز ستوده است.'),
(5100, 57, 25, '(ما) پیامبرانمان را به‌راستی و درستی با (همه‌ی) دلایل آشکار فرستادیم و با آنها کتاب و میزان فرود آوردیم تا مردم به انصاف (عادلانه و فاضلانه) برخیزند. و آهن را که در آن برای مردمان نیرویی سخت و سودهایی است، فرو فرستادیم، تا خدا نشانه گذارد چه کسی در نهان، او و پیامبرانش را یاری می‌کند. خدا به‌راستی نیرومندی عزیز است.'),
(5101, 57, 26, 'و به‌راستی و درستی، نوح و ابراهیم را فرستادیم و در میان فرزندانشان نبّوت و کتاب را قرار دادیم. پس برخی از آنان راه‌یافته‌اند و بسیاری از آنان فاسقانند.'),
(5102, 57, 27, 'سپس به دنبال آنان پیامبران خود را، پی در پی آوردیم. و عیسی پسر مریم را در پی (آنان) آوردیم و به او انجیل دادیم و در دل‌های کسانی که از او پیروی کردند رأفت و رحمت نهادیم‌. و (اما) رهبانیتی که از پیش خود در آوردند ما آن را بر ایشان مقرّر نکردیم مگر برای آنکه کسب خشنودی خدا کنند. پس آن را چنان که حقّ رعایتش بود منظور نداشتند. پس پاداش کسانی از ایشان را که ایمان آوردند به آنها دادیم‌. و بسیاری از آنان فاسقانند.'),
(5103, 57, 28, 'هان ای کسانی که ایمان آوردید! از خدا پروا بدارید و به پیامبر او بگروید تا از رحمت خویش شما را دو بهره‌ی کفالت‌‌کننده، عطا کند و برای شما نوری قرار دهد که به وسیله‌ی آن راه سپرید و (خطاهایتان را) برایتان بپوشاند و خدا بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(5104, 57, 29, 'تا اهل کتاب ندانند که به هیچ وجه فزون‌بخشی خدا در (حیطه‌ی) قدرت آنان نیست و اینکه بخشایشگری تنها در دست خداست؛ به هرکس بخواهد آن را می‌دهد و خدا دارای فزون‌‌بخشی بسیار است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'خدا به‌درستی گفته‌ی آن زنی را که درباره‌ی شوهرش با تو مجادله می‌نمود - در حالی‌که سوی خدا شکایت می‌بَرَد – شنید. خدا گفتگوی شما هر دو را می‌شنود. خدا همواره بسی شنوای بسیار بیناست.'),
(5106, 58, 2, 'از میان شما کسانی که زنانشان را ظهار می‌کنند، آنان مادرانشان نیستند. مادران(حقیقی) آنان تنها کسانی‌اند که ایشان را زاده‌اند و همانا آنان سخنی ناپسند و دروغ می‌گویند. و خدا به‌راستی و درستی درگذرنده‌ی پوشاننده است.'),
(5107, 58, 3, 'و کسانی که زنانشان را ظهار می‌کنند، سپس از آنچه گفته‌اند برمی‌گردند، پیش از آنکه با یکدیگر همخوابگی کنند آزاد کردن دربندی (برعهده‌ی آنان) است. این (حکمی) است که بدان پند داده می‌شوید و خدا به آنچه انجام می‌دهید بسیار آگاه است.'),
(5108, 58, 4, 'پس کسی که (این را) نمی‌یابد، باید پیش از تماسشان دو ماه پیاپی روزه بدارد و هر که نتواند، خوراندن شصت بینوا برعهده‌ی او است. این (حکم) برای آن است که به خدا و فرستاده‌ی او (درست) ایمان بیاورید و این‌هاست حدود خدا و برای کافران عذابی دردناک است.'),
(5109, 58, 5, 'بی‌گمان کسانی که با خدا و فرستاده‌ی او جدایی می‌کنند، ناچار با ذلت (به عقب) برگشت داده شده‌اند؛ همان گونه که آنان که پیش از ایشان بودند چنان شدند. و به‌راستی آیات روشن (خود) را فرستادیم و برای کافران عذابی خفّت‌بار است.'),
(5110, 58, 6, 'روزی که خدا همه‌ی آنان را (از گورهاشان) برمی‌انگیزد پس به دستاوردهاشان آگاهی مهمشان می‌دهد. خدا کارشان را به خوبی برشمرده است، حال آنکه آنان، آن را فراموش کرده‌اند. و خدا بر هر چیزی بسی گواه است.'),
(5111, 58, 7, 'آیا ندیدی (که) خدا به‌راستی آنچه را که در آسمان‌ها و آنچه را در زمین است می‌داند؟ هیچ گفتگوی محرمانه‌ای میان سه تن نیست مگر آنکه او چهارمین آن‌ها است و نه پنج تن جز آنکه او ششمین آن‌ها است، و نه کمتر یا بیش‌تر جز آنکه او با آنهاست، هر جا (که) باشند. سپس روز قیامت آنان را به آنچه کرده‌اند آگاهی بس مهم خواهد داد. همواره خدا به هر چیزی بسی داناست.'),
(5112, 58, 8, 'آیا کسانی را که از نجوا منع شدند، ندیدی (که) سپس به آنچه از آن منع گردیدند بر می‌گردند و با همدیگر به گناه دنباله‌دار و عدوان و عصیان پیامبر، محرمانه گفتگو می‌کنند و هنگامی که نزد تو آیند، تو را بدانچه خدا به آن (شیوه) زنده‌باد نگفته، زنده‌باد می‌گویند و در میان خودشان می‌گویند: «چرا به آنچه می‌گوییم خدا ما را عذاب نمی‌کند؟» جهنم برای آنان کافی است، آن را می‌افروزند. پس چه بد سرانجامی است.'),
(5113, 58, 9, 'هان ای کسانی که ایمان آوردید! چون با یکدیگر محرمانه گفتگو کنید، به آنچه دارای پیامدی بد و یا تعدّی و نافرمانی پیامبر است با همدیگر محرمانه گفتگو نکنید و به نیکوکاری و پرهیزگاری نجوا کنید و از خدایی که سویش محشور خواهید شد پروا بدارید.'),
(5114, 58, 10, '(اینگونه) نجوا بی‌گمان تنها از شیطان است، تا کسانی که ایمان آوردند غمگین شوند. و (این نجوا) جز به اذن خدا هیچ آسیبی به آنان نمی‌رساند. و مؤمنان باید تنها بر خدا اعتماد کنند.'),
(5115, 58, 11, 'هان ای کسانی که ایمان آوردید! چون به شما گفته شود: «در مجالس تا اندازه‌ی توان‌(تان برای دیگران) جای بگشایید»، پس جای گشایید، تا خدا برای شما گشایش دهد. و هنگامی که گفته شود: «برخیزید»، برخیزید. تا خدا کسانی از شما را که گرویده و کسانی را که دانش (ایمانی) داده شده‌اند به مراتبی بلند گرداند. و خدا به آنچه می‌کنید بسی آگاه است.'),
(5116, 58, 12, 'هان ای کسانی که ایمان آوردید! هنگامی که با پیامبر گفتگویی محرمانه می‌کنید، پیش از گفتگوی محرمانه‌ی خود صدقه‌ای پیشکش کنید. این برای شما بهتر و پاکیزه‌تر است. پس اگر چیزی نیافتید خدا بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(5117, 58, 13, 'آیا با بزرگداشت (حضرتش) ترسیدید که پیش از گفتگوی محرمانه‌ی خود صدقه‌هایی تقدیم دارید؟ پس چون (چنان) نکردید و خدا (هم) بر شما بازگشت (کرد)، پس نماز را بر پا بدارید و زکات را بدهید و خدا و پیامبرش را فرمان برید. و خدا به آنچه می‌کنید بسی آگاه است.'),
(5118, 58, 14, 'آیا کسانی را ننگریسته‌ای که گروهی را که خدا بر آنان خشم کرده به دوستی گرفته‌اند؟ آنان نه از شمایند و نه از ایشان و به دروغ سوگند یاد می‌کنند در حالی که خودشان (هم) می‌دانند.'),
(5119, 58, 15, 'خدا برایشان عذابی سخت آماده کرده است. بی‌گمان آنان چه بد می‌کرده‌اند.'),
(5120, 58, 16, 'سوگندهای خود را (چون) سپری (برای خود) قرار دادند. پس از راه خدا باز داشتند. پس برایشان عذابی خفّت‌بار است.'),
(5121, 58, 17, 'از (عذاب‌) خدا نه از اموالشان و نه از اولادشان هرگز کاری ساخته نیست. آنان همگنان دوزخند، حال آنکه در آن جاودانه‌اند.'),
(5122, 58, 18, 'روزی که خدا همه‌ی آنان را (از گورهاشان) برمی‌انگیزد، پس همان گونه که برای شما سوگند یاد می‌کنند برای او (نیز) سوگند یاد کنند، و چنان پندارند که بر چیزی (از حق)اند. هان! آنان بی‌گمان، (هم)ایشان دروغگویانند.'),
(5123, 58, 19, 'شیطان بر آنان بس چیره شده. پس خدا را از یادشان برده. آنان حزب شیطانند؛ هان! حزب شیطان (هم)ایشان بی‌گمان زیانکارانند.'),
(5124, 58, 20, 'کسانی که با خدا و پیامبرش جدایی می‌طلبد، آنان همواره در (زمره‌ی) خوارترین (مردمان)اند.'),
(5125, 58, 21, 'خدا (چنان) نبشته است که: «حتماً من و فرستادگانم چیره خواهیم گشت.» آری، خدا نیرومندی عزیز است.'),
(5126, 58, 22, '(هرگز) گروهی را نخواهی یافت که به خدا و روز بازپسین ایمان می‌آورند (ولی) با کسانی (که‌) با خدا و رسولش جدایی می‌کنند، طرح دوستی افکنند؛ هر چند پدرانشان یا پسرانشان یا برادرانشان یا عشیره‌ی آنان باشند. این‌هایند (که) خدا ایمان را در دل‌هایشان نقشی پای‌برجا داده و آنها را با روحی (ایمانی) از جانب خود تأیید کرده، و آنان را به باغ‌هایی که از زیر (درختان)شان نهرها روان است در می‌آورد، حال آنکه (آنجا) ماند‌گارند. خدا از ایشان خشنود و آنها از او خشنودند. اینانند حزب خدا. آری، حزب خدا همان رستگارکنندگانند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'آنچه در آسمان‌ها و آنچه در زمین است تسبیح‌گوی خدا بوده‌اند و اوست عزیز سنجیده‌کار.'),
(5128, 59, 2, 'اوست کسی که، از میان اهل کتاب کسانی را که کافر شدند در نخستین جمع (از مدینه) بیرون راند، (حال آنکه) گمان نمی‌کردید که بیرون روند و خودشان همواره گمان داشتند که دژهایشان از (عذاب) خدا نگهبانشان است. پس خدا از جایی که هرگز گمان نمی‌کردند بر آنان درآمد و در دل‌هایشان بیم افکند، در حالی که خودشان به دست‌های خودشان و دست‌های مؤمنان خانه‌های خود را خراب می‌کنند. پس ای صاحب‌نظران! عبرت گیرید.'),
(5129, 59, 3, 'و اگر خدا جلای وطن را بر آنان مقرّر نکرده بود، همواره آنان را در دنیا عذاب می‌کرد و در آخرت (هم) برایشان عذاب آتش است.'),
(5130, 59, 4, 'این (دربه‌دری) برای آن بود که آنان همواره با خدا و پیامبرش جدایی کردند و هر که با خدا جدایی کند همانا خدا سخت‌کیفر است.'),
(5131, 59, 5, 'آنچه از نهال (خرما) بریدید یا آنها را (دست نخورده) بر ریشه‌هایشان بر جای نهادید، به اذن خداست و برای اینکه (خدا) نافرمانان را خوار گرداند.'),
(5132, 59, 6, 'و آنچه را خدا از آنان (به رسم غنیمت) بر پیامبر خود برگردانید، شما (برای تصاحب آن) اسب یا سواری (دگری) بر آن نتاختید. ولی خدا فرستادگانش را بر هر که بخواهد چیره می‌گرداند. و خدا بر هر چیزی بس تواناست.'),
(5133, 59, 7, 'آنچه خدا از (دارایی) ساکنان آن قریه‌ها بر پیامبرش برگردانید، از آنِ خدا و از آنِ پیامبرش و از آنِ خویشاوندان نزدیکتر(ش) و (برای) یتیمان و بینوایان و در راه مانده(ها) است، تا میان توانگرانتان دست به دست نگردد. و آنچه را فرستاده‌(ی او) برایتان آورد (هم) آن را بگیرید، و از آنچه شما را از آن بازداشت، باز ایستید. و از خدا پروا بدارید که خدا سخت‌کیفر است.'),
(5134, 59, 8, '(این غنایم، نخست) اختصاص به بینوایان از مهاجران دارد که از دیارشان و اموالشان رانده شدند، در حالی‌که فضلی از خدا و خشنودی‌ای از او می‌جویند و خدا و پیامبرش را یاری می‌کنند. اینان (هم)اینان راستانند.'),
(5135, 59, 9, 'و (نیز) کسانی که پیش از مهاجران در مدینه جای امان و ایمان گرفته‌اند (و) هر کس را سوی آنان کوچ کرده دوست دارند، و نسبت به آنچه به ایشان داده شده است در سینه‌هایشان نیازی نمی‌یابند، و هر چند برای خودشان احتیاجی مبرم باشد، آنها را بر خودشان مقدّم می‌دارند. و هر کس از خسّت نفس خود مصون مانَد، ایشان (هم) ایشان (راست‌‌کاران و) رستگارکنندگانند.'),
(5136, 59, 10, 'و (نیز) کسانی که بعد از آنان [:مهاجران و انصار] آمدند گویند: «پروردگارمان! برایمان و برای آن برادرانمان که در ایمان آوردن بر ما پیشی گرفتند بپوشان، و در دل‌هایمان نسبت به کسانی که ایمان آورده‌اند (هیچ گونه) کینه‌ای وامگذار. پروردگارمان! راستی که تو مهربانِ رحمتگر بر ویژگانی.»'),
(5137, 59, 11, 'آیا فراسوی کسانی که دورویی کردند ننگریستی (که) به برادرانشان از اهل کتاب - که کافر شدند - می‌گویند: «اگر همانا اخراج شدید، همواره با شما بی‌چون بیرون خواهیم آمد و درباره‌ی شما هرگز از کسی فرمان نخواهیم برد و اگر کشتار شدید به‌راستی حتماً یاریتان خواهیم کرد.» و خدا گواهی می‌دهد که همانا آنان‌، بس دروغگویانند.'),
(5138, 59, 12, 'به‌راستی اگر (یهودیان) اخراج شوند، آنان با ایشان بیرون نخواهند رفت و اگر کشتار شدند، (منافقان) آنان را یاری نخواهند کرد و اگر (هم) یاریشان کنند بی‌چون (در جنگ) بی‌امان پشت خواهند کرد (و) سپس یاری نمی‌شوند.'),
(5139, 59, 13, 'شما بی‌گمان در سینه‌های آنان بیش از خدا هراسناکید، چرا که آنان گروهی‌اند که دقت و بررسی نمی‌کنند.'),
(5140, 59, 14, 'آنان، دسته‌‌جمعی - جز در قریه‌هایی که دارای استحکاماتی است، یا از پشت دیوارها- با شما کشتار نمی‌کنند. گرفتاریشان میان خودهاشان سخت است. آنان را متّحد می‌پنداری حال آنکه دل‌هایشان پراکنده است، زیرا آنان گروهی‌اند که درنمی‌یابند (و نمی‌فهمند).'),
(5141, 59, 15, '(درست) مانند کسانی‌که اخیراً (در واقعه‌ی بدر) سزای کار (بد) خود را چشیدند و برایشان عذابی دردناک است.'),
(5142, 59, 16, 'همانند شیطان، چون به انسان گفت: «کافر شو!» پس چون کافر شد، گفت: «من همواره از تو بیزارم، زیرا من به‌راستی از خدا، پروردگار جهانیان، می‌ترسم.»'),
(5143, 59, 17, 'پس فرجامشان آن بود که هر دو بی‌گمان در آتش جاودانند، و همان سزای ستمگران است.'),
(5144, 59, 18, 'هان ای کسانی که ایمان آوردید! از خدا پروا بدارید و هر کس باید بنگرد که برای فردایی از پیش چه فرستاده است، و (باز) از خدا پروا بدارید خدا همواره به آنچه می‌کنید بسی آگاه است.'),
(5145, 59, 19, 'و همانند کسانی مباشید که خدا را فراموش کردند، پس خدا نیز آنان را از یاد خودهاشان ببرد (و) آنان همان نافرمانانند.'),
(5146, 59, 20, 'دوزخیان با بهشتیان یکسان نیستند. بهشتیان همان کامیابانند.'),
(5147, 59, 21, 'اگر این قرآن را بر کوهی فرو می‌فرستادیم، همواره آن کوه را از بیم خدا فروتن (و) از هم پاشیده می‌دیدی. و این مَثَلها را برای مردمان می‌زنیم، شاید آنان بیندیشند.'),
(5148, 59, 22, 'او خدایی است که جز او معبودی نیست؛ داننده‌ی پنهان و آشکار است؛ اوست رحمتگر بر آفریدگان‌، رحمتگر بر ویژگان.'),
(5149, 59, 23, 'او خدایی است که جز او معبودی نیست‌؛ همان فرمانروای دارای قداست و پاکی بسیار، امن‌دهنده‌ی پناهندگان، نگهبان (آفریدگان)، عزیز، پیونددهنده‌ی گسسته‌ها (و) متکبّر (به حقیقت کبریایی راستین). پاک است خدا از آنچه با او شریک می‌گردانند.'),
(5150, 59, 24, 'اوست خدای خالق سازنده‌ی صورتگر (که) بهترین نام‌ها از اوست. آنچه در آسمان‌ها و زمین است (جمله) برای او تسبیح می‌گویند، و او عزیز حکیم است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'هان ای کسانی که ایمان آوردید! دشمن من و دشمن خودتان را اولیا(ی خود) مگیرید، در حالی‌که (خودتان و دیگران را) فراسوی‌شان (با اَسراری از مسلمانان) با مودت می‌افکنید، حال آنکه بی‌گمان به آن حقیقت که برایتان آمده کافرند؛ در حالی که پیامبر (خدا) و شما را (از مکّه) بیرون می‌کنند، (چرا) که به خدا - پروردگارتان - ایمان می‌آورید. اگر برای جهاد در راه راهوار من و طلب خشنودی‌ام برون آمده‌اید (شما) پنهانی با آنان رابطه‌ی دوستی برقرار می‌کنید، حال آنکه من به آنچه پنهان داشتید و آنچه آشکار نمودید داناترم و هر که از شما چنین کند، همواره راه راست را گم کرده است.'),
(5152, 60, 2, 'اگر بر شما با کاوش دست یابند، دشمنان شمایند. و سویتان به بدی دست‌هاشان و زبان‌هاشان را می‌گشایند و دوست دارند (که ای) کاش کافر شوید.'),
(5153, 60, 3, 'نه خویشانتان و نه فرزندانتان به شما هرگز سودی نمی‌رسانند. (خدا) روز رستاخیز میانتان جدایی می‌افکند، و خدا به آنچه انجام می‌دهید بیناست.'),
(5154, 60, 4, 'همواره برای شما در (قصّه‌ی رسالتی) ابراهیم و کسانی که با اویند سرمشقی نیکو بوده است؛ چون به قوم خود گفتند: «ما از شما و از آنچه به جای خدا می‌پرستید بیزاریم. به شما کفر ورزیدیم و میان ما و شما دشمنی و کینه‌ی همیشگی پدیدار شد، تا تنها به خدا ایمان آورید.» جز (در این) سخن ابراهیم (که) به (نا)پدر(ی) خود (گفت:) «همانا به‌راستی برایت (از خدا) پوشش می‌خواهم و از طرف خدا برایت هیچ چیزی در دست ندارم. پروردگارمان! بر تو توکل کردیم و سوی تو – بارها – بازگشتیم و فرجام تنها سوی توست.»'),
(5155, 60, 5, '«پروردگارمان! ما را وسیله‌ی آزمایشی آتش‌‌بار برای کسانی که کفر ورزیدند مگردان و برایمان بپوشان که تو خود عزیز حکیمی.»'),
(5156, 60, 6, 'بی‌گمان برایتان در (پیروی از) آنان به‌راستی سر مشقی نیکو بوده است؛ برای آنان که به خدا و روز بازپسین امید داشته‌اند. و هر کس روی برتابد خدا - (هم) او- بی‌نیاز ستوده است.'),
(5157, 60, 7, 'شاید خدا میان شما و میان کسانی از آنان که (ایشان را) دشمن داشتید، نوعی دوستی برقرار کند، و خدا تواناست و خدا پوشنده‌ای رحمتگر بر ویژگان است.'),
(5158, 60, 8, 'خدا شما را از (دوستی با) کسانی که در دین با شما کشتار نکرده و شما را از دیارتان بیرون نکرده‌اند، باز نمی‌دارد، که با آنان نیکی کنید، و با ایشان عدالت - و فراتر از آن- ورزید. خدا بی‌گمان دادگران را دوست می‌دارد.'),
(5159, 60, 9, 'تنها خدا شما را از دوستی با کسانی باز می‌دارد که در دین با شما کشتار کرده و شما را از خانه‌هایتان بیرون رانده، و در بیرون راندنتان با یکدیگر پشتیبانی کردند و هر کس آنان را به ولایت برگیرد، ایشان‌، (هم)اینان ستمگرانند.'),
(5160, 60, 10, 'هان ای کسانی که ایمان آوردید! هنگامی که زنان باایمان به حال مهاجرت نزدتان آیند، آنان را بیازمایید؛ خدا به ایمان آنان داناتر (از دیگران) است. پس اگر آنان را باایمان دانستید، دیگر ایشان را سوی کافران باز نگردانید؛ نه آن زنان برایشان حلالند و نه آن مردان برای این زنان حلالند. و هر چه خرج (این زنان) کرده‌اند، به شوهرانشان باز پس دهید، و بر شما هیچ گناهی نیست که - در صورتی که مَهرشان را به آنان بدهید - با ایشان ازدواج کنید و عصمت‌های زنان کافر را (در زناشویی خود) بر مگیرید و آنچه را شما (برای زنان کافر خود که به کفّار پناهنده شده‌اند) خرج کرده‌اید (از کافران) مطالبه کنید و آنها هم باید آنچه را (برای زنان هجرت کرده بر خود) خرج کرده‌اند (از شما) مطالبه کنند. این حکم خداست (که) میان شما داوری می‌کند و خدا بس دانایی حکیم است.'),
(5161, 60, 11, 'و اگر کسی از همسرانتان سوی کفّار رفت، پس پیگیری کردید، در نتیجه به کسانی که همسران‌شان (از دستشان) رفته‌اند، برابر آنچه خرج کرده‌اند بازدهید. و از آن خدایی که به او ایمان دارید بهراسید.'),
(5162, 60, 12, 'هان ای پیامبر! هنگامی که زنان باایمان نزد تو آیند در حالی که با تو بیعت می‌کنند که چیزی را با خدا شریک نسازند و دزدی ننمایند و زنا نکنند و فرزندان خود را نکشند و (بچه‌های حرام‌زاده‌ی) پیش دست و پای خود را با بهتان (و حیله) به شوهرانشان نبندند. و در (کار) معروف و پسندیده‌ی ایمانی، تو را نافرمانی نکنند، پس با آنان بیعت کن و از خدا برایشان پوشش بخواه (که) بی‌گمان خدا بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(5163, 60, 13, 'هان ای کسانی که ایمان آوردید! مردمی را که خدا بر آنان خشم کرده هرگز به دوستی و سرپرستی برنگیرید. آنان بی‌گمان از آخرت نومید شدند، همان گونه که کافران از صاحبان گورها نومید شدند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'آنچه در آسمان‌ها و آنچه در زمین است برای خدا تسبیح گفتند و اوست عزیز حکیم.'),
(5165, 61, 2, 'هان ای کسانی که ایمان آوردید! چرا چیزی می‌گویید که (آن را) انجام نمی‌دهید.'),
(5166, 61, 3, 'بزرگ‌خشمی است نزد خدا که بگویید آنچه را انجام نمی‌دهید.'),
(5167, 61, 4, 'خدا همواره کسانی را دوست دارد که در راه او صف‌کشید‌گان - چنان که گویی بنایی ریخته شده از سُرب هستند - کشتار می‌کنند.'),
(5168, 61, 5, 'و چون موسی به قوم خود گفت: «ای قوم من! چرا آزارم می‌دهید، با اینکه به‌درستی می‌دانید من به راستی فرستاده‌ی خدا فراسوی شما هستم‌؟» پس چون (از حق) برگشتند، خدا (هم) دل‌هایشان را (همچنان از آن) برگردانید و خدا مردم نافرمان را هدایت نمی‌کند.'),
(5169, 61, 6, 'و چون عیسی پسر مریم گفت: «ای فرزندان اسرائیل! من به‌راستی فرستاده‌ی خدا سوی شمایم، حال آنکه تورات را که در برابر من است تصدیق کننده‌ام و به فرستاده‌ای که پس از من می‌آید و نام او احمد (ص) است بشارتگرم.» پس هنگامی‌که برایشان دلایلی روشن آورد، گفتند: «این سحری آشکارگر است.»'),
(5170, 61, 7, 'و چه کسی ستمگرتر از آن (نا)کس است که با وجود آنکه سوی تسلیم (در برابر خدا) فرا خوانده می‌شود، بر خدا دروغ می‌بندد؟ و خدا ستمگران را راه نمی‌نماید.'),
(5171, 61, 8, 'می‌خواهند نور خدا را با دهان‌هاشان خاموش کنند حال آنکه خدا -گر چه کافران را بسی ناخوش آید- نور خود را کامل‌کننده است.'),
(5172, 61, 9, 'اوست کسی که فرستاده‌ی خود را به تمامی هدایت و آیین راستای پابرجا روانه کرد تا آن را بر هر چه دین است برتر گرداند؛ هر چند مشرکان را بسی ناخوش آید.'),
(5173, 61, 10, 'هان ای کسانی که ایمان آوردید! آیا شما را بر تجارتی راه نمایم (که) شما را از عذابی دردناک می‌رهاند؟'),
(5174, 61, 11, 'به خدا و فرستاده‌اش ایمان می‌آورید و در راه خدا با اموالتان و جان‌هاتان جهاد کنید. این (گذشت و فداکاری) اگر می‌دانسته‌اید، برای شما بهتر است؛'),
(5175, 61, 12, 'تا گناهان دنباله‌دارتان را برایتان بپوشاند و شما را در باغ‌هایی که از زیر (درختان)شان نهرها روان است و (در) سراهایی خوش، در باغ‌های همیشگی در آورد. این (خود) کامیابی راحت (و) بزرگ است.'),
(5176, 61, 13, 'و (رحمتی) دیگر (که) آن را دوست دارید، یاری و پیروزی نزدیکی از جانب خداست و مؤمنان را (بدان) بشارت ده.'),
(5177, 61, 14, 'هان ای کسانی که ایمان آوردید! یاران خدا باشید. همان گونه که عیسی پسر مریم به حواریان گفت: «یاران من سوی خدا، چه کسانی‌اند؟» حواریان گفتند: «ما یاران خداییم‌.» پس گروهی از اسرائیلیان ایمان آوردند و گروهی کافر شدند. پس کسانی را که ایمان آوردند علیه دشمنانشان تأیید کردیم تا (بر آنان) چیره شدند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'آنچه در آسمان‌ها و آنچه در زمین است، برای خدا تسبیح گویند. (خدایی که) تنها او پادشاهیِ بس مقدس (و) عزیز حکیم است.'),
(5179, 62, 2, 'کسی که در میان مادرزادگان [:بی‌بهرگان از وحی] فرستاده‌ای از خودشان برانگیخت‌، حال آنکه آیات او را بر آنان می‌خواند و پاکشان می‌گرداند و کتاب و حکمت بدیشان تعلیم می‌دهد. و گرچه آنان همواره پیش از آن در (ژرفای) گمراهی آشکارگری بوده‌اند.'),
(5180, 62, 3, 'و (نیز) بر گروه‌هایی دیگر از ایشان، هنگامی که در پی‌شان می‌آیند و اوست (همان) عزیز حکیم.'),
(5181, 62, 4, 'این (بزرگ‌فضیلت) فضل خداست. آن را به هر که بخواهد می‌دهد و خدا دارای فضل بسیاری است.'),
(5182, 62, 5, 'مَثَل کسانی که (علم و عمل) تورات بر عهده‌ی آنان گذارده شد، سپس (با کوتاهی کردنشان) آن را بر دوشهاشان نگرفتند، هم‌چون مَثَل خری است که کتاب‌هایی را بر پشت می‌کشد. وه چه زشت است نماد آن قومی که با آیات خدا (همان‌ها و خدا را) تکذیب کردند. و خدا ستمگران را راه نمی‌نماید.'),
(5183, 62, 6, 'بگو: «ای کسانی که یهودی شده‌اید! اگر پنداشتید که (تنها) شما به‌راستی دوستان خدایید نه دیگر مردمان، اگر (از) راستان بوده‌اید، پس درخواست مرگ کنید.»'),
(5184, 62, 7, 'و (اما) هرگز - به سبب آنچه از پیش با دست‌های خویش فرستاده‌اند - آرزوی مرگ نمی‌کنند. و خدا به (حال) ستمگران بسی داناست.'),
(5185, 62, 8, 'بگو: «بی‌گمان مرگی که از آن می‌گریزید، بی‌چون همان گریبانگیرتان است‌؛ آن‌گاه سوی (خدای) دانای نهان و آشکار (جهان) برگردانیده خواهید شد؛ پس به آنچه - انجام می‌داده‌‌اید – به خوبی آگاهتان خواهد کرد.»'),
(5186, 62, 9, 'هان ای کسانی که ایمان آوردید! هنگامی که برای نماز از روز جمعه ندا(ی اذان) در داده شد، سوی یاد خدا بشتابید و دادوستد را واگذارید. اگر می‌دانسته‌اید همین برای شما بهتر است.'),
(5187, 62, 10, 'پس چون نماز گزارده شد در (روی) زمین (و زمینه‌ی کارتان) پراکنده گردید و از فضل خدا جویا شوید و خدا را بسیار یاد کنید، شاید شما رستگار کنید.'),
(5188, 62, 11, 'و هنگامی که دادوستد یا سرگرمی بازدارنده‌ی از خدا را ببینند، سوی آن پراکنده می‌شوند و تو را در حالی که ایستاده‌ای وا می‌نهند. بگو: «آنچه نزد خداست از لهو و (حتی) از دادوستد بهتر است و خدا بهترین روزی‌دهندگان است.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'هنگامی که منافقان نزد تو آیند گویند: «گواهی می‌دهیم که تو همی به‌راستی پیامبر خدایی‌.» و خدا (هم) می‌داند که تو به‌راستی و درستی پیامبر او هستی، و خدا گواهی می‌دهد که مردم دورو بی‌گمان سخت دروغگویانند.'),
(5190, 63, 2, 'سوگندهای خود را (چون) سپری بر خود بر گرفته، پس (مکلفان را) از راه خدا باز داشته‌اند. به‌راستی که آنان چه بد می‌کرده‌اند.'),
(5191, 63, 3, 'این بدین سبب است که آنان ایمان آورده، سپس کافر شدند، در نتیجه بر دل‌هایشان مُهر زده شد و (دیگر) نمی‌توانند بفهمند.'),
(5192, 63, 4, 'و چون آنان را ببینی، هیکل‌هایشان تو را به شگفتی در می‌آورد و چون سخن گویند به گفتارشان گوش فرا می‌‌داری‌. ایشان دشمن (حق)اند. پس از آنان بپرهیز. خدا با آنان کشتار کرده (که) به گزاف (از حق منحرف و کنده و) افکنده می‌شوند.'),
(5193, 63, 5, 'و هنگامی که برایشان گفته شود: «بیایید تا پیامبر خدا برایتان پوشش بخواهد»، سرهای خود را بر می‌گردانند، و آنان را می‌بینی که متکبرانه (از راه خدا) باز می‌دارند.'),
(5194, 63, 6, 'بر آنان یکسان است: چه برایشان پوشش بخواهی، یا برایشان پوشش نخواهی، خدا هرگز برایشان نخواهد پوشاند. خدا بی‌گمان فاسقان را راهنمایی نمی‌کند.'),
(5195, 63, 7, 'آنان (همان) کسانی‌اند که می‌گویند: «به کسانی که نزد پیامبر خدایند انفاق مکنید، تا (از گِردَش) پراکنده شوند حال آنکه تمامی گنجینه‌های آسمان‌ها و زمین از خداست ولی منافقان در نمی‌یابند.'),
(5196, 63, 8, 'گویند: «اگر به مدینه بر گردیم همواره آنکه عزیزتر است‌، آن زبون‌تر را بی‌چون از آنجا بیرون خواهد راند.» حال آنکه عزت تنها برای خدا و پیامبر او و مؤمنان است‌؛ لیکن این دورویان نمی‌دانند.'),
(5197, 63, 9, 'هان ای کسانی که ایمان آوردید! (زنهار که) اموالتان و فرزندانتان شما را از یاد خدا غافل نگردانند و هر کس چنان کند، پس هم‌آنان خودشان زیانکارانند.'),
(5198, 63, 10, 'و از آنچه روزی شما گردانیدیم، انفاق کنید، پیش از آنکه یکی از شما را مرگ فرا رسد. پس بگوید: «پروردگارم! چرا تا مدتی نزدیک (مرگ) مرا به تأخیر نینداختی تا به‌راستی تصدیق کنم و از شایستگان باشم‌؟»'),
(5199, 63, 11, 'و هر کس اجلش فرا رسد، هرگز خدا آن را به تأخیر نمی‌افکند. و خدا به آنچه می‌کنید بسی آگاه است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'هر چه در آسمان‌ها و هر چه در زمین است برای خدا تسبیح گویند و او راست پادشاهی و او راست (همه‌ی) سپاس و (هم)او بر هر چیزی بسیار تواناست.'),
(5201, 64, 2, 'اوست کسی که شما را آفرید؛ پس برخی از شما (به هنگام تکلیف) کافرند و برخی (درآن هنگام) مؤمنند، و خدا به آنچه می‌کنید بس بیناست.'),
(5202, 64, 3, 'آسمان‌ها و زمین را به (تمامی) حق آفرید و شما را صورتگری کرد و صورت‌هایتان را نیکو ساخت. و بازگشت تنها سوی اوست.'),
(5203, 64, 4, 'آنچه را که در آسمان‌ها و زمین است می‌داند و آنچه را که پنهان می‌کنید و آنچه را که آشکار می‌دارید (نیز) می‌داند. و خدا به (راز) ذاتی سینه‌ها(تان) بسی داناست.'),
(5204, 64, 5, 'آیا خبر مهم کسانی که پیش از این کافر شدند -پس فرجام سخت بدکاریشان را چشیدند، حال آنکه عذاب پر دردی برای آنان است- به شما نرسیده است‌؟'),
(5205, 64, 6, 'این (بد فرجامی) از آن روست که بی‌گمان پیامبرانشان دلایل آشکار برایشان می‌آورده‌اند، پس آنان می‌گفتند: «آیا بشرهایی ما را هدایت می‌کنند؟» پس کافر شدند و روی گردانیدند، و خدا (از آنان) بی‌نیازی کرد. و خدا بی‌نیازی بس ستوده است.'),
(5206, 64, 7, 'کسانی که کافر شدند، پنداشتند که هرگز برانگیخته نخواهند شد. بگو: «آری، سوگند به پروردگارم، همانا به‌راستی برانگیخته خواهید شد. سپس شما همواره به آنچه کرده‌اید بی‌چون خبری مهم داده خواهید شد و این تنها بر خدا آسان است.»'),
(5207, 64, 8, 'پس به خدا و پیامبر او و آن نوری که ما فرو فرستادیم ایمان آورید و خدا به آنچه می‌کنید بسیار آگاه است.'),
(5208, 64, 9, 'هنگامی که شما را برای روز گردآوری (آخرت) گرد می‌آورد، آن (روز)، روز زیان‌باری و فریب‌خوردگی در میان شماست و هر کس به خدا ایمان آورد و کار شایسته‌ای کند، بدی‌هایش را از او بسترد، و او را در باغ‌هایی جاودانه (که) از زیر (درختان)شان نهرها روان است داخل کند. این است کامیابی بزرگ.'),
(5209, 64, 10, 'و کسانی که کافر شدند و با آیاتمان (همان‌ها و ما را) تکذیب کردند، آنان -جاودانه- همراهان آتشند. و چه بد سرانجامی است.'),
(5210, 64, 11, 'هیچ مصیبتی جز به اذن خدا نرسد، و هر که به خدا ایمان آورد دلش را به راه آورد. و خدا به هر چیزی بسی داناست.'),
(5211, 64, 12, 'و خدا را فرمان برید و پیامبر (او) را پیروی کنید. پس اگر روی بگردانید، بر (عهده‌ی) پیامبرمان، فقط پیام‌رسانیِ روشنگر است.'),
(5212, 64, 13, 'خدا(ست که) جز او معبودی نیست. پس مؤمنان باید تنها بر خدا توکل کنند.'),
(5213, 64, 14, 'هان ای کسانی که ایمان آوردید! بی‌گمان برخی از همسرانتان و فرزندانتان دشمنان شمایند. پس از آنان بر حذر باشید و اگر ببخشایید و (از آنان) روی بگردانید و پوشش نهید (بهتر است). به‌راستی خدا بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(5214, 64, 15, 'اموالتان و فرزندانتان تنها وسیله‌ی آزمایشی آتشینند، و خداست که نزد او پاداشی بزرگ است.'),
(5215, 64, 16, 'پس تا می‌توانید از خدا پروا بدارید و بشنوید و فرمان ببرید و آنچه را برایتان خیر است (در راه خدا) انفاق کنید و کسی که خسّت نفسش نگهبانی شود، همانان رستگارکنندگانند.'),
(5216, 64, 17, 'اگر خدا را وامی نیکو دهید، آن را برایتان افزون می‌گرداند و برای شما می‌پوشاند. و خدا بسی شکرگزار بسیار بردبار است.'),
(5217, 64, 18, 'دانای نهان و آشکار و عزیز حکیم است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'هان ای پیامبر برجسته! چون زنان را طلاق گویید، پس از (زمان‌بندی) عدّه‌ی آنان طلاقشان گویید و حساب عدّه را نگه دارید. و خدا - پروردگارتان - را پروا بدارید. آنان را از خانه‌هاشان بیرون مرانید و آنها هم از خانه‌هاشان بیرون نروند، مگر آنکه مرتکب فحشایی آشکارگر گردند. و این است مرزبندی‌های الهی و هر کس از مرزهای خدا پای فراتر نهد، بی‌چون به خودش ستم کرده. نمی‌دانی، شاید خدا پس از این، جریانی (خوشایند) پدید آورد.'),
(5219, 65, 2, 'پس هنگامی که عدّه‌ی آنان سر رسید به شایستگی (پیش از پایان آن) نگاهشان دارید، یا به شایستگی از آنان جدا شوید و دو مرد عادل را از میان خود (بر طلاق یا رجوع) گواه گیرید و این گواهی را برای خدا به پا دارید. این است اندرزی که - به هر کس که به خدا و روز بازپسین ایمان می‌آورده - داده می‌شود. و هر کس از خدا پروا کند، خدا برای او راه بیرون‌شدنی (از گرفتاری‌ها) قرار می‌دهد،'),
(5220, 65, 3, 'و از جایی که حسابش را نمی‌کند، (خدا) روزیش می‌دهد. و هر کس بر خدا توکل کند، پس (هم) او برایش بس است. خدا کار و فرمانش را انجام‌دهنده است. به‌راستی خدا برای هر چیزی ارزش و اندازه‌ای قرار داده است.'),
(5221, 65, 4, 'و آن زنان که از خون حیض (ماهانه) نومیدند، اگر شک دارید (که خون حیضی خواهند دید یا نه، ) عدّه‌ی آنان سه ماه است و دخترانی (هم) که هنوز خون حیض ندیده‌اند (در حالی‌که در سنِ خون دیدن‌اند نیز عدّه‌شان سه ماه است) و زنان باردارِ سرآمد (عده‌)شان این است که وضع حمل کنند. و هر کس از خدا پروا بدارد خدا برایش از کارش آسانی‌‌ای می‌نهد.'),
(5222, 65, 5, 'این است فرمان خدا که آن را سویتان فرستاده است‌. و هر کس از خدا پروا کند، بدی‌های کوچکش را از او بزداید، و پاداشش را بزرگ گرداند.'),
(5223, 65, 6, 'از آن‌گونه (مسکن) که (خود) سکونت داشتید - از موجودی و توانتان - آنان را سکنا دهید و به آنان آسیب (و زیان) مرسانید، تا عرصه را بر ایشان تنگ کنید و اگر باردارند نفقه‌شان را بدهید تا بارشان را فرونهند. پس اگر برای شما (فرزندانتان را) شیر دهند مزدشان را به ایشان بدهید، و (در این باره) به شایستگی میان خودتان مشورت کنید و اگر کارتان (در این مورد) با هم به دشواری کشید، به زودی زن دیگری برای او (انتخاب کنید)، پس او را شیر خواهد داد.'),
(5224, 65, 7, 'هر که دارای گشایش است باید از گشایشش هزینه کند و هر که روزیش تنگ شده، باید از آنچه خدا به او داده هزینه کند. خدا هیچ کس را جز (به اندازه‌ی) آنچه به او داده است تکلیف نمی‌کند. خدا به زودی پس از دشواری آسانی می‌نهد.'),
(5225, 65, 8, 'و چه بسیار (از) گروه‌ها که از فرمان پروردگارشان و از پیامبرانش سرپیچیدند. پس‌، از آنها حسابی سخت کشیدیم و آنان را به عذابی بس (ناهنجار و) منکر دچار کردیم.'),
(5226, 65, 9, 'پس کیفر زشت عمل خود را چشیدند و پایان کارشان زیان بود.'),
(5227, 65, 10, 'خدا برای آنان عذابی سخت آماده کرده است. پس ای خردمندان که ایمان آوردید! از خدا پروا بدارید. به‌راستی خدا فراسوی شما یادواره‌ای فرو فرستاده است؛'),
(5228, 65, 11, 'فرستاده‌ای که آیات روشنگر خدا را بر شما تلاوت می‌کند، تا کسانی را که ایمان آوردند و کارهای شایسته(ی ایمان) کردند از تاریکی‌ها سوی روشنایی بیرون برد و هر کس به خدا بگرود و کار شایسته کند او را در باغ‌هایی- سردرهم- که از زیر (درختان)شان نهرها روان است، در می‌آورد (که) جاودانه برای همیشه در آن هم‌چنان ماندنی هستند. بی‌گمان خدا روزی‌ای را برایش به‌خوبی آورده است.'),
(5229, 65, 12, 'خدا کسی است که هفت آسمان را و همانند آنها (هفت) زمین را آفرید. فرمان (تکوین و تشریع خدا) در میان آنها به تدریج فرود می‌آید تا بدانید که خدا به‌درستی بر هر چیزی بسیار تواناست و به‌راستی علمش همواره هر چیزی را در بر گرفته است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'هان ای پیامبر برجسته! چرا آنچه را خدا برای تو حلال گردانیده (بر خود) حرام می‌کنی؟ حال آنکه خشنودی همسرانت را می‌طلبی. و خدا بسی پوشنده‌ی رحمتگر بر ویژگان است.'),
(5231, 66, 2, 'خدا بی‌گمان برایتان باز کردن (این‌گونه) سوگندهایتان را اکیداً واجب کرده است. و خدا سرپرست شماست، و اوست بسیار دانای حکیم.'),
(5232, 66, 3, 'و چون (این) پیامبر برجسته با یکی از همسرانش سخنی گفت، همین که وی آن را به برخی (از همسران‌) گزارشی مهم داد و خدا او را بر آن مطّلع گردانید، (حضرتش‌) بخشی از آن را اظهار کرد و از بخشی (دیگر) اعراض نمود. پس چون همان را به آن زن خبری مهم داد، وی گفت: «چه کسی این خبر مهم را به تو داده‌؟» گفت: «مرا آن دانای آگاه (این‌گونه) خبر مهمی داده است.»'),
(5233, 66, 4, 'اگر شما دو زن سوی خدا بازگشت کنید (که) بس. بی‌گمان دل‌هایتان انحراف پیدا کرده است و اگر علیه او با یکدیگر تظاهر کنید، به‌راستی خدا خود سرپرست اوست و (نیز) جبرئیل و صالح مؤمنان و فرشتگان (هم) پس از این (ولایت ربانی) پشتیبان اویند.'),
(5234, 66, 5, 'اگر پیامبر، شما را طلاق گوید، امید است پروردگارش همسرانی بهتر از شما، مسلمان، مؤمن، فرمانبر، توبه‌‌کار، عابد، روزه‌دار، بیوه و دوشیزه را به او عوض دهد.'),
(5235, 66, 6, 'هان ای کسانی که ایمان آوردید! خودهاتان و کسانتان را از آتشی که سوختِ آن، مردم و سنگ است نگه دارید. بر آن (آتش) فرشتگانی خشن (و) سختگیر (گمارده شده)اند (که) از آنچه خدا به آنان دستور داده سرپیچی نمی‌کنند و آنچه را که مأمور می‌شوند انجام می‌دهند.'),
(5236, 66, 7, 'هان ای کسانی که کافر شدید! امروز عذر نیاورید، تنها همان را که عمل می‌کرده‌اید کیفر می‌یابید.'),
(5237, 66, 8, 'هان ای کسانی که ایمان آوردید! سوی خدا بازگشتی بسیار ناصحا‌نه کنید. امید است که پروردگارتان بدی‌هایتان را از شما بزداید و شما را به باغ‌هایی که از زیر (درختان)شان نهرها روان است در آورد. روزی که خدا پیامبر برجسته(ی خود) و کسانی را که با او ایمان آورده بودند خوار نمی‌گرداند، نورشان از پیشاپیش آنان و سمت راستشان می‌شتابد، گویند: «پروردگارمان! نورمان را برایمان کامل گردان و برایمان (گناهانمان را) بپوشان، که تو بر هر چیزی بسیار توانایی.»'),
(5238, 66, 9, 'هان ای پیامبر برجسته! با کافران و منافقان جهاد کن و بر آنان سخت بگیر. و پناه‌گاهشان جهنم است و چه بد سرانجامی است.'),
(5239, 66, 10, 'خدا برای کسانی که کافر شدند، زن نوح و زن لوط را مَثَل آورده (که) هر دو تحت (نکاح) دو بنده‌ی شایسته از بندگان ما بودند. پس (اینان) به آنان خیانت کردند. و کاری از دست آن دو [:شوهرانشان] درباره‌ی آنان از (عذاب) خدا ساخته نبود. و (به آن دو زن) گفته شد: «با داخل‌شوندگان، در آتش داخل شوید.»'),
(5240, 66, 11, 'و برای کسانی که ایمان آورده‌اند، خدا همسر فرعون را مَثَل آورد، آن‌گاه که گفت: «پروردگارم! نزد خود در بهشت خانه‌ای برایم بساز و مرا از فرعون و کردارش نجات ده و مرا از دست گروه ستمگران برهان.»'),
(5241, 66, 12, 'و مریم‌، دخت عمران را، همان کسی که عورت خود را (پاک) نگاه داشت. پس در آن از روح ویژه‌مان (از میان آفریدگان) دمیدیم و (وی) سخنان پروردگار خود و کتاب‌های او را تصدیق کرد و از عابدان مخلص بود.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'مبارک است کسی که فرمانروایی (جهان) تنها به دست اوست، و (هم) او بر هر چیزی بسی تواناست.'),
(5243, 67, 2, 'کسی که مرگ و زندگی را آفرید، تا شما را بیازماید (که) کدامتان نیکوکارترید. و اوست عزیزی بس پوشنده.'),
(5244, 67, 3, 'کسی که هفت آسمان را، طبقاتی (روی هم) بیافرید. در آفرینش (خدای) رحمان هیچ‌گونه (تضاد و) تفاوتی نمی‌بینی. پس باز (هم) بنگر، آیا هیچ‌گونه سستی (در آن) می‌بینی‌؟'),
(5245, 67, 4, 'سپس بار دیگر بنگر، تا دیده‌ات زبون و به حال درماندگی سویت بازگردد.'),
(5246, 67, 5, 'و همانا ما نزدیکترین آسمان (به زمین) را همواره با چراغ‌هایی زینت دادیم و آنها را (مراکزی) برای سنگباران‌هایی برای شیطان‌ها نهادیم و برایشان عذابی بس سوزان آماده کردیم.'),
(5247, 67, 6, 'و برای کسانی که به پروردگارشان کافر شدند، عذاب جهنم است و چه بد سرانجامی است.'),
(5248, 67, 7, 'هنگامی‌که در آنجا افکنده شوند، از آن خروشی (خراش‌دار) می‌شنوند، در حالی که زبانه می‌کشد.'),
(5249, 67, 8, 'نزدیک است از خشم شکافته شود. هر بار گروهی در آن افکنده شوند، نگاهبانان آن از ایشان پرسند: «مگر شما را هشداردهنده‌ای نیامد؟»'),
(5250, 67, 9, 'گفتند: «چرا، بی‌گمان هشداردهنده‌ای سویمان آمد، پس تکذیب(اش) کردیم و گفتیم خدا چیزی فرو نفرستاده است‌؛ شما جز در گمراهی بزرگی نیستید.»'),
(5251, 67, 10, 'و گفتند: «اگر شنیده بودیم، یا خردورزی می‌کردیم، در (میان) دوزخیان نبودیم.»'),
(5252, 67, 11, 'پس به گناه خود اعتراف کردند. پس برای جهنمیان بس فروپاشی (و نابسامانی) مقرر است.'),
(5253, 67, 12, 'بی‌گمان کسانی که از پروردگارشان در نهان با احترام می‌هراسند، برایشان همواره پوششی و پاداشی بزرگ است.'),
(5254, 67, 13, 'و سخنتان را (چه) پنهان بدارید، یا آشکارش نمایید، خدا همواره به (راز) ذاتی سینه‌ها بسی آگاه است.'),
(5255, 67, 14, 'آیا کسی که (جهان را) آفریده است نمی‌داند؟ در حالی که او بس باریک‌بینِ آگاه است‌.'),
(5256, 67, 15, 'اوست کسی که زمین را (همچون حیوانی راهوار) برای شما رام گردانید. پس در فراخناهای آن رهسپار شوید و از روزیش بخورید. و رستاخیز تنها فراسوی اوست‌.'),
(5257, 67, 16, 'آیا از آن کس که در آسمان (قدرت و علم و عدالت) است، ایمن شدید (از این) که شما را در زمین فرو برد؟ پس زمین (در آن هنگامه) ناگهان روان گردد.'),
(5258, 67, 17, 'یا از آن کسی که در آسمان است ایمن شدید، که بر شما (تندبادی با) سنگریزه‌ای فرو فرستد؟ پس به زودی خواهید دانست که بیم‌دهنده‌ی (از سوی) من چگونه است!'),
(5259, 67, 18, 'و همانا پیش از آنان (نیز) بی‌گمان کسانی تکذیب کردند. پس انکار (و انگار) من چگونه بود؟'),
(5260, 67, 19, 'آیا و بالای سرشان، سوی پرندگان ننگریسته‌اند، حال آنکه صف کشیدگان و نگهبانان (خود)اند (و) جز (خدای) رحمان (کسی) آنها را نگه نمی‌دارد؟ به‌راستی او به هر چیزی بسیار بیناست.'),
(5261, 67, 20, 'یا آن‌که برایتان سپاهی است تا از (عذاب) رحمان یاریتان کند؟ کافران جز در (ژرفای) فریب نیستند.'),
(5262, 67, 21, 'یا کیست آن کس که شما را روزی می‌دهد، اگر (خدا) روزی خود را (از شما) باز دارد؟ (نه!) بلکه در سرکشی و نفرت‌، لجاجت و پافشاری کردند.'),
(5263, 67, 22, 'آیا پس آن کس که نگونسار بر چهره‌اش راه می‌پیماید راه‌یافته‌تر است، یا آن کس که (راستا) بر راهی بس راست می‌رود؟'),
(5264, 67, 23, 'بگو: «اوست آن کسی که شما را پدید آورد، و برای شما گوش‌ها و دیده‌ها و دل‌هایی فروزان آفرید. چه کم سپاسگزارید.»'),
(5265, 67, 24, 'بگو: «اوست آن کس که شما را در زمین پدیدار (و) آشکار کرد و تنها فراسوی او(ست که) گرد آورده خواهید شد.»'),
(5266, 67, 25, 'و می‌گویند: «اگر (از) راستان بوده‌اید، (تحقق) این وعده کی خواهد بود؟»'),
(5267, 67, 26, 'بگو: «(تمامی) علم تنها نزد خداست و من تنها هشداردهنده‌ای روشنگرم.»'),
(5268, 67, 27, 'پس آن‌گاه که آن (لحظه‌ی موعود) را نزدیک دیدند، چهره‌های آنانی که کافر شدند، بسی به نابسامانی در هم رفت و گفته شد: «این همان چیزی است که آن را فرا می‌خوانده‌اید.»'),
(5269, 67, 28, 'بگو: «آیا دیدید، اگر خدا مرا و هر که را با من است هلاک کند، یا ما را مورد رحمت قرار دهد، پس چه کسی کافران را از عذابی پردرد (فراسوی خود) پناه خواهد داد؟»'),
(5270, 67, 29, 'بگو: «او تنها رحمتگر بر آفریدگان است. به او ایمان آوردیم و تنها بر او توکل کردیم، پس به زودی خواهید دانست چه کسی است که خود در گمراهی آشکارگری است.»'),
(5271, 67, 30, 'بگو: «آیا دیدید اگر آبِ شما، فروکشد، پس چه کس برایتان آبی جاری خواهد آورد؟»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'ن، سوگند به قلم و آنچه (با آن) می‌نگارند،'),
(5273, 68, 2, 'که) تو، با نعمت (پرورش وحی از) پروردگارت، دیوانه نیستی.'),
(5274, 68, 3, 'و بی‌گمان، تو را همواره پاداشی بی‌‌منت و پایدار است.'),
(5275, 68, 4, '(که) در کدام یک از شما (عقلی) آتشبار (و) آشفته است.'),
(5276, 68, 5, 'و به‌راستی که تو بر خُلقی بس بزرگ سلطه داری.'),
(5277, 68, 6, 'پس به زودی خواهی دید و خواهند دید،'),
(5278, 68, 7, 'همانا پروردگارت، (هم)او بهتر می‌داند چه کسی از راهش منحرف شده، و (هم) او به راه‌یافتگان داناتر است.'),
(5279, 68, 8, 'پس، از تکذیب‌کنندگان فرمان مبر.'),
(5280, 68, 9, 'دوست داشتند اگر (با آنان) نرمی کنی تا (با تو) نرمی نمایند.'),
(5281, 68, 10, 'و از هر قسم‌خورنده‌ی بی‌مبالات بس فرومایه‌ای فرمان مبر،'),
(5282, 68, 11, '(که) بسیار عیب‌جوست و برای خبرچینی پیاپی گام برمی‌دارد،'),
(5283, 68, 12, 'و بسی مانع از خیر، متجاوز و گناه‌پیشه است،'),
(5284, 68, 13, 'ترک‌کننده‌ی تمامی فضیلت‌ها و عمل‌کننده‌ی به همه‌ی رذیلت‌ها (و) پس از آن بی‌تبار است.'),
(5285, 68, 14, '(برای) اینکه صاحب مال و پسرانی بوده،'),
(5286, 68, 15, 'هنگامی که آیاتمان بر او خوانده شود گوید: «(این‌ها) افسانه‌های پیشینیان است.»'),
(5287, 68, 16, 'زودا که بر (بینی همچون) خرطوم(اش) داغ (رسوایی) نهیم.'),
(5288, 68, 17, 'ما بی‌گمان آنان را همان گونه که باغداران را آزمودیم، -چون سوگند خوردند بی‌چون صبحگاهان برخیزند و (میوه‌ی) آن (باغ) را همواره برچینند- مورد آزمایش قرار دادیم.'),
(5289, 68, 18, 'حال آنکه (این اموال را برای بینوایان) استثنا نکنند.'),
(5290, 68, 19, 'پس در حالی که آنان خواب بودند، (بلای) گردشگری (بر سروسامانشان) از جانب پروردگارت بر آن (باغ) به گردش درآمد.'),
(5291, 68, 20, 'پس (آن) باغ (آکنده) همچون برکنده شد.'),
(5292, 68, 21, 'پس (باغداران) بامدادان یکدیگر را ندا در دادند.'),
(5293, 68, 22, 'که: « اگر میوه‌چین هستید بامدادان سوی کشت خویش روید.»'),
(5294, 68, 23, 'پس (خستند و) رستند درحالی که (خود را از بینوایان) بسی پنهان می‌کردند.'),
(5295, 68, 24, 'که: «امروز هرگز نباید در باغ، بینوایی بر (مَنظر) شما درآید.»'),
(5296, 68, 25, 'و صبحگاهان در حالی که خود را بر منع (بینوایان) توانایان و تقدیرکنان می‌دیدند، رفتند.'),
(5297, 68, 26, 'پس چون آنان را دیدند گفتند: «همواره ما همی (از) گمراهانیم.»'),
(5298, 68, 27, '«(نه!) بلکه ما (از) محرومانیم.»'),
(5299, 68, 28, 'میانه‌روتر‌شان گفت: «آیا به شما نگفتم (چرا خدا را) به پاکی نمی‌ستایید؟»'),
(5300, 68, 29, 'گفتند: «پروردگارمان! تو را به پاکی می‌ستاییم‌. ما همانا ستمگران بوده‌ایم.»'),
(5301, 68, 30, 'پس بعضیشان رو به بعضی دیگر آوردند، در حالی که همدیگر را به نکوهش گرفتند.'),
(5302, 68, 31, 'گفتند: «ای وای برما که همواره سرکش بوده‌ایم!»'),
(5303, 68, 32, '«امید است که پروردگارمان بهتر از آن را به ما عوض دهد. ما به‌راستی سوی پروردگارمان مشتاقانیم.»'),
(5304, 68, 33, 'عذاب (دنیا) چنان است، و عذاب آخرت - اگر می‌دانستند- بی‌چون بزرگتر است.'),
(5305, 68, 34, 'بی‌گمان برای پرهیزگاران، نزد پروردگارشان باغستان‌های پرناز و نعمت است.'),
(5306, 68, 35, 'پس آیا فرمانبرداران را چون مجرمان و (نافرمانان) قرار خواهیم داد؟'),
(5307, 68, 36, 'شما را چه شده‌؟ چگونه داوری می‌کنید؟'),
(5308, 68, 37, 'یا شما را کتابی (وحیانی) است که در آن (وحی) فرا می‌گیرید،'),
(5309, 68, 38, 'که هرچه را برمی‌گزینید، بی‌گمان برای شما در آن (کتاب) خواهد بود؟'),
(5310, 68, 39, 'یا اینکه شما تا روز قیامت (از ما) سوگندهایی رسا گرفته‌اید که هر چه دلتان خواست همواره (به آن) حکم کنید؟'),
(5311, 68, 40, 'از آنان بپرس کدامشان بدین گمان است؟'),
(5312, 68, 41, 'یا برایشان شریکانی است‌؟ پس اگر راست می‌گویند شریکانشان را بیاورند.'),
(5313, 68, 42, 'روزی که از ساق (و سوق اعمالشان) پرده برگرفته شود به سجده فراخوانده شوند، پس نتوانند (سجده کنند).'),
(5314, 68, 43, 'چشمانشان خاشعانه [:برهم] است و ذلت آنان را فراگرفته‌. در حالی که همواره به سجود دعوت می‌شدند (سجده نمی‌کردند) با آنکه سالم بودند.'),
(5315, 68, 44, 'پس مرا رها کن با کسی که به این حدیث (قرآن) تکذیب می‌کند. زودا (که) آنان را در عذاب پیاپی داخل می‌کنیم، بدان گونه که نمی‌دانند.'),
(5316, 68, 45, 'و برایشان مهلت می‌دهیم. بی‌گمان کید من پای‌برجاست.'),
(5317, 68, 46, 'یا از آنان اجری می‌خواهی، پس ایشان از زیانشان گران‌بارند.'),
(5318, 68, 47, 'یا نزدشان غیب است، پس ایشان (آن را) می‌نویسند؟'),
(5319, 68, 48, 'پس برای حکم پروردگارت صبر کن، و مانند همراه ماهی [:یونس] مباش، چون (خدا را) ندا در داد در حالی که غضبش فرونشسته بود.'),
(5320, 68, 49, 'اگر نعمتی از پروردگارش او را در نیافته بود، بی‌گمان در بیابان (خشک و بی‌آب و گیاه) افکنده می‌شد در حالی که مذموم بود.'),
(5321, 68, 50, 'پس پروردگارش او را برگزید و از شایستگانش نهاد.'),
(5322, 68, 51, 'و بی‌گمان کسانی که کافر شدند، نزدیکند بی‌امان تو را به هنگامی که ذکر [:قرآن] را شنیدند با چشم‌هایشان متزلزل کنند، و گویند او همواره بس مجنون است.'),
(5323, 68, 52, 'در حالی که او جز یادواره‌ای برای جهانیان نیست.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'حقیقت حتمی [:معاد]'),
(5325, 69, 2, 'چیست (آن) حقیقت؟'),
(5326, 69, 3, 'و چه فهماندت (این) حقیقت چیست‌؟'),
(5327, 69, 4, 'ثمودیان و عادیان به کوبنده‌ی شدید [:قیامت] را تکذیب کردند.'),
(5328, 69, 5, 'پس اما ثمودیان با (عذابی) طغیان‌کننده به هلاکت رسیدند.'),
(5329, 69, 6, 'و اما عادیان به بادی بس سرد و تجاوزکننده هلاک شدند.'),
(5330, 69, 7, '(که خدا) آن را بر (سر و سامان)شان هفت شب و هشت روز چیره کرد، حال آنکه (این روزهای طوفانی) ریشه‌کَنَنده‌‌هایی بودند. پس می‌بینی (این) گروه را که در آن (باد) از بنیاد برکنده شدند، گویی آنان پایه‌های پوک درختان خرمایی می‌باشند.'),
(5331, 69, 8, 'پس آیا برایشان هیچ (حالت) ماندگاری‌ای می‌بینی‌؟'),
(5332, 69, 9, 'و فرعون و کسانی که پیش از او بودند و دورافتادگان (از حق) به خطاپیشگی سخت در آمدند.'),
(5333, 69, 10, 'پس فرستاده‌ی پروردگارشان را عصیان کردند، در نتیجه خدا (گریبان‌) آنان را به سختی برگرفت.'),
(5334, 69, 11, 'ما به‌راستی هنگامی که آب طغیان کرد شما را در (کشتیِ) روان حمل کردیم (در حالی که ذریه‌هایی در صلب‌های مردان و ترائب ]: سینه‌های زنان[ (بودید).'),
(5335, 69, 12, 'برای اینکه این کشتی را برایتان یادواره‌ای نهیم و گوش‌هایی شنوا آن را فرا گیرند،'),
(5336, 69, 13, 'پس هنگامی که در صور [:بوق جان‌فرسا] یک‌جا دمیده شود،'),
(5337, 69, 14, 'و زمین و کوه‌ها (با زلزله‌ی فراگیر) حمل شوند، پس یکجا نرم (و ویران) گردند.'),
(5338, 69, 15, 'پس در این هنگام واقعه(‌ی قیامت‌) رخ دهد،'),
(5339, 69, 16, 'و آسمان بشکافد. پس آن در این هنگام بسی سست (بنیاد) است.'),
(5340, 69, 17, 'و فرشته بر اطراف آن (آسمان شکافته‌شده) است. و عرش پروردگارت را بالای سر (و سامان)شان در آن روز هشت نفر (از عرشیان) حمل می‌کنند.'),
(5341, 69, 18, 'چنان روزی (بر پروردگار) عرضه می‌شوید، حال آنکه هیچ (چیزِ) پنهان از شما (بر او) مخفی نباشد.'),
(5342, 69, 19, 'پس اما کسی که کتاب (اعمال)ش به دست راستش داده شود، گوید: «هان! کتاب مرا بخوانید.»'),
(5343, 69, 20, '«من به‌راستی گمان (شایسته‌ی ایمان) داشتم که بی‌گمان حسابم را ملاقات خواهم کرد.»'),
(5344, 69, 21, 'پس او در (ژرفای) معیشتی رضایت‌بخش است.'),
(5345, 69, 22, 'در باغی والا، با درختانی سردرهم،'),
(5346, 69, 23, '(که) چیدنی‌هایش (به او) نزدیک است.'),
(5347, 69, 24, '(به آنان گفته شود:) «به گوارایی بخورید و بیاشامید، به سبب آن چه در زمان‌های گذشته از پیش فرستادید.»'),
(5348, 69, 25, 'و اما کسی که کتابش به دست چپش داده شود، پس گوید: «ای کاش کتابم به من داده نمی‌شد.»'),
(5349, 69, 26, '«و نمی‌دانستم حسابم چیست‌؟»'),
(5350, 69, 27, '«ای کاش (قیامت هرگز) نبود.»'),
(5351, 69, 28, '«مالم مرا بی‌نیاز نکرد.»'),
(5352, 69, 29, '«از من سلطه و توانم [:حال و مالم] ناچیز و نابود شد.»'),
(5353, 69, 30, '(گفته شود:) «او را بگیرید و زنجیرش کنید.»'),
(5354, 69, 31, '«سپس او را گیرانه‌ی جهنم نمایید.»'),
(5355, 69, 32, '«سپس در رشته‌ای زنجیری که طولش هفتاد ذراع است فروگیریدش.»'),
(5356, 69, 33, '«او به‌راستی به خدای بزرگ ایمان نمی‌آورده.'),
(5357, 69, 34, '«و (خود و دیگران را) بر خوراک دادن بینوا وانمی‌داشته.»'),
(5358, 69, 35, '«پس امروز برای او این‌جا دوستی خون‌گرم نیست.»'),
(5359, 69, 36, '«و نه خوراکی جز چرکابه‌ای (از دوزخیان)‌»'),
(5360, 69, 37, '«که آن را جز خطاکاران نمی‌خورند.» !'),
(5361, 69, 38, 'پس‌، به آنچه می‌بینید، سوگند یاد نمی‌کنم.'),
(5362, 69, 39, 'و (نیز) به آنچه نمی‌بینید،'),
(5363, 69, 40, '(که) این (قرآن) به‌راستی بی‌گمان گفتار فرستاده‌ای بزرگوار است.'),
(5364, 69, 41, 'و آن گفتار شاعری نیست‌. (چه) کم است آنچه ایمان می‌آورید.'),
(5365, 69, 42, 'و نه گفتار کاهنی است. (چه) کم است آنچه متذکر می‌شوید.'),
(5366, 69, 43, '(پیام) فرود آمده‌ای است از جانب پروردگار جهانیان.'),
(5367, 69, 44, 'و اگر (او) پاره‌ای از گفته‌ها(ی ناروا) را بر ما همی بربندد،'),
(5368, 69, 45, 'با قدرت (بی‌نهایت‌)، (وحی ربانی را) از او برمی‌گیریم.'),
(5369, 69, 46, 'سپس رگ (زندگی وحیانی)اش را پاره می‌کنیم.'),
(5370, 69, 47, 'پس هیچ یک از شما مانع از (عذاب) او نیست.'),
(5371, 69, 48, 'و بی‌گمان (قرآن) یادواره‌ای برای پرهیزگاران است.'),
(5372, 69, 49, 'و ما به‌راستی می‌دانیم که از (میان) شما همواره تکذیب‌کنندگانی هستند.'),
(5373, 69, 50, 'و آن همواره بر کافران بی‌گمان حسرتی است.'),
(5374, 69, 51, 'و این (قرآن) به‌راستی بی‌شبهه (در راستای) حق یقین است.'),
(5375, 69, 52, 'پس به (پاس) نام پروردگار بزرگت (او را) تسبیح گوی.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'پرسنده‌ای به (علت) عذاب واقع‌شونده‌ای (از آنکه اگر واقعیت دارد پس چرا اکنون از آن اثری نیست،) پرسید.'),
(5377, 70, 2, '(عذابی) که اختصاص به کافران دارد (و) آن را بازدارنده‌ای نیست‌.'),
(5378, 70, 3, '(و) از جانب خدا (است)، صاحب (بخشایش) کل درجات‌.'),
(5379, 70, 4, 'فرشتگان و روح، در روزی که مقدارش پنجاه هزار سال است سویش بالا می‌روند.'),
(5380, 70, 5, 'پس صبرکن، صبری نیکو.'),
(5381, 70, 6, 'بی‌گمان آن (عذاب) را دور می‌بینند،'),
(5382, 70, 7, 'و (ما) آن را نزدیکش می‌بینیم.'),
(5383, 70, 8, 'روزی که آسمان‌ها چون ته‌مانده‌ی روغن زیتون گداخته است‌.'),
(5384, 70, 9, 'و کوه‌ها چون پشمِ زده شده است.'),
(5385, 70, 10, 'و هیچ دوست صمیمی دوست صمیمی(اش) را نپرسد.'),
(5386, 70, 11, 'آنان به ایشان بس نشان داده می‌شوند. گناهکار دوست می‌دارد کاش برای رهایی از عذاب آن روز، می‌توانست پسران خود را (به جای خود) عوض دهد،'),
(5387, 70, 12, 'و (نیز) همسرش را و برادرش را،'),
(5388, 70, 13, 'و جداساخته‌اش [:مادرش] را که پناهش می‌دهد،'),
(5389, 70, 14, 'و هر که را که در روی زمین است‌، همه را (عوض دهد) و سپس خود را نجات دهد.'),
(5390, 70, 15, 'نه چنین است. همانا آن (آتش) زبانه‌دار است.'),
(5391, 70, 16, 'پوست سر و اندام) بریان شده را بس برکَنَنده است.'),
(5392, 70, 17, 'هر که را پشت کرده و روی برتافته است (به خود) می‌خواند.'),
(5393, 70, 18, 'و گردآورده(هایی) پس (آن را) انباشته‌.'),
(5394, 70, 19, 'به‌راستی انسان سخت آزمند (و بی‌تاب) خلق شده است.'),
(5395, 70, 20, 'چون صدمه‌ای به او در رسد جزع و لابه‌کُننده است.'),
(5396, 70, 21, 'و چون خیری به او رسد (آن را از دیگران) بازدارنده است.'),
(5397, 70, 22, 'بجز نمازگزاران،'),
(5398, 70, 23, 'آنان که ایشان بر نمازشان پایدار‌انند.'),
(5399, 70, 24, 'و (هم) آنان که در اموالشان حقی معلوم است،'),
(5400, 70, 25, 'برای سائل و محروم.'),
(5401, 70, 26, 'و کسانی که روز جزا را باور دارند،'),
(5402, 70, 27, 'و کسانی که از عذاب پروردگارشان با بزرگ‌داشتنش می‌هراسند.'),
(5403, 70, 28, 'بی‌گمان عذاب پروردگارشان (از کافران) بازداشته نیست.'),
(5404, 70, 29, 'و کسانی که برای (نگهداری) عورت‌هاشان حافظانند.'),
(5405, 70, 30, 'مگر بر همسران خود یا کنیزانشان؛ پس (در این صورت) مورد نکوهش نیستند.'),
(5406, 70, 31, 'پس هرکس پا از این (حد) فراتر نهد آنان، (هم) ایشان از حد درگذرندگانند.'),
(5407, 70, 32, 'و کسانی که امانتشان و پیمانشان را رعایت‌کنندگانند.'),
(5408, 70, 33, 'و آنان که به شهادتهایشان ایستایند.'),
(5409, 70, 34, 'و کسانی که برنمازشان محافظت دارند.'),
(5410, 70, 35, 'اینانند که در باغ‌هایی (در بهشت) گرامی‌اند.'),
(5411, 70, 36, 'پس آنان را که کفر ورزیده‌اند، چه شده (که) سویت نگرانند؟'),
(5412, 70, 37, '(گروه گروه)، از راست و چپ (از تو) فراری و متنفرند.'),
(5413, 70, 38, 'آیا هر یک از آنان طمع می‌بندد که در بهشت پرنعمت در آورده شود؟'),
(5414, 70, 39, 'نه چنان است. ما آنان را بی‌گمان از آنچه (خود) می‌دانند آفریدیم.'),
(5415, 70, 40, 'پس به پروردگار خاوران و باختران سوگند یاد نمی‌کنم (که) بی‌گمان ما تواناییم،'),
(5416, 70, 41, 'بر اینکه (آن‌ها را) تبدیل به همانند‌شان گردانیم. و ما درمانده نیستیم.'),
(5417, 70, 42, 'پس واگذارشان (تا در باطلشان) فرو روند و بازی کنند تا روزی را که وعده داده می‌شوند ملاقات کنند.'),
(5418, 70, 43, 'روزی که از گورهاشان برون آیند گویی با یکدیگر فراسوی جایگاه‌هایی معین شده روانند.'),
(5419, 70, 44, 'حال آنکه دیدگانشان (از ترس) فرو هشته، خواری فراگیرشان می‌گردد. این (همان) روزی است (که) وعده داده می‌شده‌اند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'ما نوح را به‌راستی سوی قومش فرستادیم که: «قومت را پیش از آنکه عذابی دردناک به آنان در رسد، هشدار ده.»'),
(5421, 71, 2, '(نوح) گفت: «ای قوم من! همواره من برای شما بیم‌دهنده‌ای روشنگرم‌؛»'),
(5422, 71, 3, '«که خدا را بپرستید و از او پروا بدارید و مرا فرمان ببرید.»'),
(5423, 71, 4, '«تا برخی از از گناهانتان را بر شما بپوشاند. و شما را سوی اجلی نامبرده ]:ناگزیر و ناگریز[ به تأخیر اندازد. اگر می‌دانستید، چون وقت مقرّر خدا در رسد، هرگز تأخیر نخواهد داشت.»'),
(5424, 71, 5, '(نوح) گفت: «پروردگارم! من قوم خود را همواره شب و روز (پیاپی) دعوت کردم.»'),
(5425, 71, 6, '«پس دعوت من جز بر گریزشان نیفزود.»'),
(5426, 71, 7, '«و من بی‌قرار، هربار که آنان را دعوت کردم تا برایشان (گناهانشان را) ‌بپوشانی، انگشتانشان را در گوش‌هایشان نهادند و لباس‌هاشان را بر (سرها)شان کشیدند و (بر کفرشان) اصرار ورزیدند و هر چه بیشتر بر کبر خود افزودند.»'),
(5427, 71, 8, 'سپس آشکارا آنان را دعوت کردم.»'),
(5428, 71, 9, '«سپس به‌درستی برایشان اعلان کردم و در نهان و پنهان برایشان همی اندرز دادم.»'),
(5429, 71, 10, '« پس گفتم: “از پروردگارتان پوشش بخواهید که او به‌راستی بسی پوشاننده بوده است.”»'),
(5430, 71, 11, '«تا (خدا باران و برفِ) آسمان را بر (سروسامان)تان بسی ریزان بفرستد»'),
(5431, 71, 12, '«و شما را به اموال و پسرانی، امداد نماید و برایتان باغ‌هایی (با درختان سردر هم) قرار دهد و برایتان نهرها نهد.»'),
(5432, 71, 13, '«شما را چه (بیماری) است که هیچ امیدی به وقار و شکوهی برای خدا ندارید؟»'),
(5433, 71, 14, '«و حال آنکه (خدا) شما را به‌راستی گوناگون آفریده است.»'),
(5434, 71, 15, 'مگر ندیدید [:ندانستید] (که) چگونه خدا هفت آسمان را طبقاتی بر روی هم آفریده است‌؟»'),
(5435, 71, 16, '«و ماه را در میانشان روشنایی‌بخش گردانید و خورشید را چراغی (بسی روشن) قرار داد.»'),
(5436, 71, 17, '«و خدا شما را از زمین رویانید روییدنی (ممتاز).»'),
(5437, 71, 18, '«سپس شما را در آن بازمی‌گرداند و بیرون می‌آورد، برون آوردنی.»'),
(5438, 71, 19, '«و خدا زمین را برای شما فرشی (گسترده) ساخت.»'),
(5439, 71, 20, '«تا از راه‌های فراخ و پست و بلند آن راهوار راه یابید.»'),
(5440, 71, 21, 'نوح گفت: «پروردگارم! آنان مرا نافرمانی کردند و کسی را پیروی نمودند که مال و فرزندش جز بر زیان وی نیفزود.»'),
(5441, 71, 22, '«و دست به نیرنگی بس بزرگ زدند؛»'),
(5442, 71, 23, '«و گفتند: “زنهار! خدایان خود را رها مکنید و نه هرگز ودّ را و نه سواع و نه یغوث و یعوق و نسر را.”»'),
(5443, 71, 24, '«و بسیاری را همواره گمراه کرده‌اند و جز بر گمراهی ستمکاران میفزای.»'),
(5444, 71, 25, 'از (اثر) گناهانشان غرق گشتند ، پس (همان جا) داخل آتش شدند، پس برای خودشان از غیر خدا یارانی نیافتند.'),
(5445, 71, 26, 'و نوح گفت: «پروردگارم! هیچ کس از کافران را بر روی زمین باقی مگذار.»'),
(5446, 71, 27, '«بی‌گمان اگر تو آنان را رها کنی، بندگانت را گمراه می‌کنند و جز فاجرِ بسی کافر نسلی به وجود نیاورند.»'),
(5447, 71, 28, '«پروردگارم! برای من و پدر و مادرم و برای هر که در سرایم به حالت ایمان درآمد و برای (کل) مردان و زنان باایمان بپوشان و جز بر هلاکت ستمکاران میفزای.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'بگو: «به من وحی شده است که به‌راستی تنی چند از جنّیان گوش فرا داشتند. پس گفتند: به‌درستی ما قرآنی شگفت‌آور را شنیدیم،»'),
(5449, 72, 2, '«(که) به راه راست هدایت می‌کند، پس به آن ایمان آوردیم و هرگز احدی را شریک پروردگارمان قرار نخواهیم داد.»'),
(5450, 72, 3, '«و اینکه پروردگار بسی والاست، (و) همسر و فرزندی اختیار نکرده است.»'),
(5451, 72, 4, '«و اینکه بی‌گمان کم‌‌خردمان، درباره‌ی خدا سخنانی یاوه و پراکنده می‌سراییده است.»'),
(5452, 72, 5, 'و اینکه ما بسی پنداشتیم که انس و جنّ هرگز بر خدا دروغی نمی‌بندند.»'),
(5453, 72, 6, '«و اینکه مردانی از آدمیان به مردانی از جنّیان بسی پناه می‌برده‌اند، پس قاهرانه (در گمراهیشان) افزودند.»'),
(5454, 72, 7, '«و اینکه آنان (نیز) آن‌گونه که (شما) پنداشته‌اید، همواره گمان بردند که خدا هرگز کسی را زنده نخواهد گردانید.»'),
(5455, 72, 8, '«و اینکه ما بی‌گمان بر آسمان دست یافتیم، پس آن را پر شده از نگهبانان و پاسداران توانا و نیزه‌های آتشین یافتیم.»'),
(5456, 72, 9, '«و اینکه، به‌راستی (در آسمان) برای شنیدن، همواره در جایگاه‌هایی (به کمین) می‌نشسته‌ایم، پس اکنون هر که بخواهد به گوش باشد، (تیر) شهابی در کمین خود می‌یابد.»'),
(5457, 72, 10, '«و اینکه ما به درستی نمی‌دانیم آیا برای کسانی که در زمینند، بدی خواسته شده یا پروردگارشان برایشان رشد و نموی خواسته است‌؟»'),
(5458, 72, 11, '«و اینکه به‌راستی از میان ما برخی شایستگانند و برخی دونشان [:ناشایستگان]. ما فرقه‌هایی جدای از یکدیگر بوده‌ایم.»'),
(5459, 72, 12, '«و اینکه ما گمان شایسته‌ای داشتیم که هرگز نمی‌توانیم در زمین خدای را به ستوه آوریم و هرگز او را با گریز(مان) درمانده نتوانیم کرد.»'),
(5460, 72, 13, '«و اینکه ما چون هدایت را شنیدیم، به د‌رستی بدان گرویدیم. پس هر کس که به پروردگار خود ایمان آورد، از هیچ‌گونه کاستی ستمگرانه و نه در برگیری قاهرانه، بیم نداریم.»'),
(5461, 72, 14, '«و اینکه برخی از ما بی‌گمان مسلمان و برخی (هم) نامسلمانند. پس هر کس تسلیم (خدا) شد آنان در جستجوی راهی راهوار بوده‌اند.»'),
(5462, 72, 15, 'و اما بخش‌کنندگان (میان خدا و آفریدگان) ، پس هیزم جهنم بوده‌اند.'),
(5463, 72, 16, 'و اینکه اگر بر راه (خدا) پایداری کنند، همواره آنان را آبی بسیار گوارا بنوشانیم.'),
(5464, 72, 17, 'تا در این‌باره آنان را آزمایشی آتشین بنماییم و هرکس از یاد پروردگارش روی گرداند وی را در قید عذابی بسی بالا و بلند فرو می‌نهد.'),
(5465, 72, 18, 'و اینکه مساجد، به‌راستی ویژه‌ی خداست. پس هیچ کس را با خدا مخوانید.'),
(5466, 72, 19, 'و اینکه چون بنده‌ی خدا به‌راستی، برخاست، حال آنکه خدا را می‌خواند، چیزی نمانده بود که بر سر و رویش فرو افتند.'),
(5467, 72, 20, 'بگو: «من تنها پروردگار خود را می‌خوانم و احدی را با او شریک نمی‌گردانم.»'),
(5468, 72, 21, 'بگو: «من هرگز برای شما مالک هیچ زیان و سودی نیستم.»'),
(5469, 72, 22, 'بگو: «هرگز کسی مرا در برابر خدا پناه نمی‌دهد و هرگز پناهگاهی غیر از او نمی‌یابم.»'),
(5470, 72, 23, '«(وظیفه‌ی من) تنها ابلاغی از سوی خدا و (رساندن) پیام‌های اوست و هر کس خدا و پیامبرش را نافرمانی کند، بی‌گمان برای او آتش دوزخ جاودانه است.»'),
(5471, 72, 24, '«تا هنگامی‌که آن چه را وعده داده می‌شوند ببینند. پس به زودی دریابند که یاور چه کسی کمکش ضعیف‌تر و کدام یک شمار‌ش کمتر است.»'),
(5472, 72, 25, 'بگو: «من نمی‌دانم آن چه وعده داده می‌شوید، نزدیک است یا خدا برایش مدتی دور قرار می‌دهد.»'),
(5473, 72, 26, '(خدا) عالِم (کل) غیب‌(ها) است، پس احدی را بر غیبش آگاه نمی‌کند.'),
(5474, 72, 27, 'مگر کسی از رسولان را. پس او بی‌گمان از پیش و پس او مترصد است،'),
(5475, 72, 28, 'تا نشانه نهد که به‌درستی، رسالت‌های پروردگارشان را ابلاغ کرده‌اند. حال آنکه به آن چه نزدشان است، احاطه کرده و عدد هر چیزی (به‌خوبی) را برشمرده است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'ای جامه بر (جان و تن خویشتن) فرو پیچیده‌،'),
(5477, 73, 2, 'شب را به‌پا خیز مگر اندکی.'),
(5478, 73, 3, 'نیمش را، یا اندکی از نیمش را بکاه،'),
(5479, 73, 4, 'یا بر آن (نیم) بیفزای، و قرآن را شمرده بخوان شمردنی (روشن).'),
(5480, 73, 5, 'ما همواره به زودی بر تو گفتاری گران‌بار فرو می‌افکنیم‌.'),
(5481, 73, 6, 'بی‌گمان عبادت نشأت‌‌یافته از شب گامی استوارتر و فراتر و گفته‌اش پای‌برجاتر و گرانبارتر است.'),
(5482, 73, 7, 'به‌راستی برایت، در روز، شناوری ای دور و دراز است.'),
(5483, 73, 8, 'و نام پروردگار خود را یاد کن و فراسویش (آن گونه) انقطاعی یاب که (دیگران را نیز سویش) منقطع سازی.'),
(5484, 73, 9, 'پروردگار خاور و باختر (که) خدایی جز او نیست؛ پس او را کارساز خویش برگیر.'),
(5485, 73, 10, 'و بر آنچه می‌گویند شکیبا باش و از آنان با دوری‌گزیدنی زیبا، دور باش.'),
(5486, 73, 11, 'و مرا با تکذیب‌کنندگان نعمت‌وار واگذار و اندکی مهلتشان ده.'),
(5487, 73, 12, 'به‌راستی پیش ما عذاب‌هایی سخت و آتش پرزبانه،'),
(5488, 73, 13, 'و طعامی گلوگیر و عذابی پردرد است،'),
(5489, 73, 14, '(در) روزی که زمین و کوه‌ها به لرزه درآیند، حال آنکه کوه‌ها ریگی انباشته‌‌اند.'),
(5490, 73, 15, 'بی‌گمان، ما سوی شما فرستاده‌ای که گواه بر شماست روانه کردیم‌؛ همان گونه که فرستاده‌ای سوی فرعون فرستادیم.'),
(5491, 73, 16, 'پس فرعون (آن) فرستاده را عصیان کرد. پس ما او را به گونه‌ای مرگبار بر گرفتیم.'),
(5492, 73, 17, 'پس اگر کفر بورزید، چگونه از روزی که کودکان را پیر می‌گرداند پرهیز می‌کنید؟'),
(5493, 73, 18, 'آسمان از حادثه‌ی آن (روز) از هم شکافته (و) وعده‌ی او شدنی بوده است.'),
(5494, 73, 19, 'همانا این‌ها یادواره‌ای است تا هر که بخواهد سوی پروردگارش راهی در پیش گیرد.'),
(5495, 73, 20, 'پروردگارت به‌راستی می‌داند که تو و گروهی از کسانی که با تواَند، نزدیک به دوسوم از شب، و نصف آن را، یا یک‌‌سوم آن را (به عبادت) بر می‌خیزید. خداست که شب و روز را تقدیر و اندازه می‌دهد. (او) می‌داند که (شما) هرگز آن را ضبط نتوانید کرد. پس (از حکم نخستین) بر شما برگشت. پس هر چه از قرآن مُیسر می‌شود بخوانید. (خدا) دانست که به زودی در میانتان بیمارانی خواهند بود، و دیگرانی (که) در زمین (پای) می‌زنند (هم) هستند حال آنکه از فضل خدا می‌جویند، و دیگرانی (هم) در راه خدا کشتار می‌کنند. پس هر چه از آن [:قرآن] میسر شد بخوانید و نماز را بر پا بدارید و زکات را بپردازید و وامی نیکو به خدا دهید. و هر کار خوبی برای خویش از پیش فرستید، آن را نزد خدا بهتر و با پاداشی بیشتر بازخواهید یافت. و از خدا پوشش بخواهید. خدا بی‌گمان بسی پوشنده‌ی رحمتگر بر ویژگان است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'ای خموش در خود فرورفته (و) خویشتن را پوشیده‌،'),
(5497, 74, 2, 'برخیز، پس هشدارده.'),
(5498, 74, 3, 'و پروردگارت را (نزد دیگران که کوچکش داشته‌اند) بزرگ گردان‌.'),
(5499, 74, 4, 'و لباس‌هایت را پاک بدار.'),
(5500, 74, 5, 'و از (هرگونه) پلیدی دور شو.'),
(5501, 74, 6, 'و منت مگذار در حالی که فزونی‌طلب و برتری‌جویی.'),
(5502, 74, 7, 'و برای پروردگارت شکیبایی کن.'),
(5503, 74, 8, 'پس چون در ناقور [:بوق جان‌افزا] دمیده شود،'),
(5504, 74, 9, 'پس آن روز، روزی ناگوار است!'),
(5505, 74, 10, 'بر کافران آسان نیست.'),
(5506, 74, 11, 'مرا با آنکه (او را) تنها [:بی‌تبار] آفریدم واگذار.'),
(5507, 74, 12, 'و مالی بسیار راهوار و کشش‌دار برایش قرار دادم'),
(5508, 74, 13, 'و پسرانی حاضر و آماده (به خدمت دادم،)'),
(5509, 74, 14, 'و برایش (همه چیز زندگی مادی را) بسی آماده کردم.'),
(5510, 74, 15, 'سپس طمع دارد که (برآن) بیفزایم.'),
(5511, 74, 16, 'هرگز چنان نیست (که می‌پندارد). او بی‌امان دشمن آیات ما بوده.'),
(5512, 74, 17, 'به زودی او را به بالا رفتن از گردنه‌ا‌ی سخت وادار می‌کنم.'),
(5513, 74, 18, 'بی‌‌چون (آن دشمن بداندیش) اندیشید و سنجید.'),
(5514, 74, 19, 'پس (این) کشته(ی هوا و عِناد خود)، چگونه سنجید؟'),
(5515, 74, 20, 'سپس کُشته شد. چگونه سنجید؟'),
(5516, 74, 21, 'آن‌گاه نظر افکند.'),
(5517, 74, 22, 'سپس رو ترش نمود و چهره درهم کشید.'),
(5518, 74, 23, 'پس پشت گردانید و تکبر ورزید.'),
(5519, 74, 24, 'پس گفت: «این (قرآن) جز سحری ماندنی و پیروی‌شدنی نیست‌.»'),
(5520, 74, 25, '«این غیر از سخن بشر نیست.»'),
(5521, 74, 26, 'زودا که او را گیرانه‌ی سقر [:آتش ذوب‌کننده] کنیم.'),
(5522, 74, 27, 'و تو چه دانی که سقر چیست‌؟'),
(5523, 74, 28, 'نه باقی می‌گذارد و نه رها می‌کند.'),
(5524, 74, 29, 'بشر (و بشره) را بسی دگرگون و ذوب می‌سازد.'),
(5525, 74, 30, '(و) بر آن نوزده (نگهبان) است.'),
(5526, 74, 31, 'و ما موکلان آتش را جز فرشتگان نگردانیدیم، و شماره‌ی آنان را جز آزمایشی آتشبار برای کسانی که کافر شده‌اند قرار ندادیم. تا آنان که اهل کتابند یقین کنند و ایمان کسانی که ایمان آوردند افزون گردد و آنان که کتاب به ایشان داده شده و (نیز) مؤمنان‌، به شک مستندی نیفتند. و تا کسانی که در دل‌هایشان بیماری است و (نیز) کافران بگویند: «خدا از این مَثَل چه چیزی را اراده کرده است‌؟» این گونه خدا هر که را بخواهد بی‌راه می‌گذارد و هرکه را بخواهد به راه می‌آورد. و (شماره‌ی) سپاهیان پروردگارت را جز او نمی‌داند. و این (آیات) جز یادواره‌ای برای بشر نیست.'),
(5527, 74, 32, 'نه چنین است (که می‌پندارند)! سوگند به ماه،'),
(5528, 74, 33, 'و سوگند به شامگاه چون پشت کند،'),
(5529, 74, 34, 'و سوگند به بامداد چون چهره گشاید،'),
(5530, 74, 35, 'به‌راستی آیات (قرآن) بی‌گمان یکی از (آیات) بزرگ است.'),
(5531, 74, 36, 'حال آنکه هشداردهنده‌ی بشر است.'),
(5532, 74, 37, 'برای هر کس از شما که بخواهد پیشی گیرد یا پس بماند.'),
(5533, 74, 38, 'هر کسی در گرو دستاورد خویش است.'),
(5534, 74, 39, 'بجز یاران دست راست [:راستان].'),
(5535, 74, 40, 'در میان باغ‌هایی (با درختان) سردرهم، از یکدیگر می‌پرسند،'),
(5536, 74, 41, 'از مجرمان:'),
(5537, 74, 42, 'چه چیز شما را در سقر فرود آورد؟»'),
(5538, 74, 43, 'گویند: «ما از نمازگزاران نبوده‌ایم»'),
(5539, 74, 44, '«و بینوایان را غذا نمی‌داده‌ایم»'),
(5540, 74, 45, '«و با هرزه‌درایان هرزه‌درایی می‌کرده‌ایم»'),
(5541, 74, 46, '«و با روز جزا (همان را) تکذیب می‌کرده‌ایم.»'),
(5542, 74, 47, '«تا یقینمان در رسید.»'),
(5543, 74, 48, 'پس شفاعت شفاعت‌کنندگان سودشان ندهد.'),
(5544, 74, 49, 'پس چیست برایشان (که) از یادواره رویگردانند؟!'),
(5545, 74, 50, 'که گویی بی‌گمان گورخرانی رمیده‌اند،'),
(5546, 74, 51, '(که‌) از مقابل شیری ژیان فرار کرده‌اند!'),
(5547, 74, 52, 'بلکه هرکدام از آنها انتظار دارد نامه‌ی جداگانه‌ای بس گشوده و انتشار یافته (از سوی خدا) برایش فرستاده شود!'),
(5548, 74, 53, 'چنان نیست (که آنان می‌گویند)، بلکه از آخرت نمی‌هراسند!'),
(5549, 74, 54, 'چنان نیست (که آنها می‌گویند)! این (قرآن) بی‌گمان یادواره‌ای است.'),
(5550, 74, 55, 'پس هرکس بخواهد یادش آورد.'),
(5551, 74, 56, 'و هیچ کس به یاد نمی‌آورد، مگر اینکه خدا بخواهد؛ او اهل تقوی و اهل پوشش است!');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'به روز قیامت سوگند ندارم!'),
(5553, 75, 2, 'و نه سوگند به نفس لوامه [:وجدان بیدار و ملامتگر].'),
(5554, 75, 3, 'آیا انسان می‌پندارد که هرگز استخوان‌هایش را جمع نخواهیم کرد؟!'),
(5555, 75, 4, 'آری‌! قادریم که (حتی خطوط سر) انگشتان او را سازمان دهیم!'),
(5556, 75, 5, 'بلکه انسان همی خواهد تا پیش رویش را بشکافد [:خدا را علنی بنگرد و قیامت را ببیند و پیوسته فجور کند]!'),
(5557, 75, 6, '(از این رو) می‌پرسد: «قیامت کی خواهد بود؟!»'),
(5558, 75, 7, 'پس در آن هنگام که چشم (از شدتِ وحشت) برق زند (و به گردش در آید)،'),
(5559, 75, 8, 'و ماه بی‌‌نور گردد،'),
(5560, 75, 9, 'و خورشید و ماه یک‌جا جمع شوند،'),
(5561, 75, 10, 'انسان در آن‌هنگام گوید: «گریزگاه کجاست‌؟!»'),
(5562, 75, 11, 'هرگز (چنین نیست.)! راه فراری (از این گیرودار) و پناهگاهی نیست!'),
(5563, 75, 12, 'آن روز قرارگاه (نهایی) تنها سوی پروردگار تو است‌.'),
(5564, 75, 13, 'و در آن روز انسان از تمام آنچه پیش فرستاده و واگذاشته بسی آگاهی مهم داده می‌شود.'),
(5565, 75, 14, 'بلکه انسان خودش بر (وضع) خودش بسی آگاه است!'),
(5566, 75, 15, 'و هرچند (در ظاهر برای خود) عذرهایی بیفکند!'),
(5567, 75, 16, '(ای پیامبر!) زبانت را به‌خاطر عجله برای خواندن آن [:قرآن] حرکت مده.'),
(5568, 75, 17, 'همانا گردآوریش و خواندنش تنها بر عهده‌ی ماست!'),
(5569, 75, 18, 'پس هنگامی که آن را خواندیم، از خواندنش پیروی کن!'),
(5570, 75, 19, 'سپس بیان آن (نیز) بی‌گمان بر عهده‌ی ماست!'),
(5571, 75, 20, 'هرگز (چنان نیست که شما می‌پندارید)؛ بلکه شما (دنیای) زودگذر را دوست می‌دارید!'),
(5572, 75, 21, 'و آخرت را وامی‌گذارید!'),
(5573, 75, 22, 'چهره‌هایی در آن روز باطروات است،'),
(5574, 75, 23, '(که) سوی (رحمت) پروردگارشان نگرانند!'),
(5575, 75, 24, 'و در آن روز صورت‌هایی عبوس و درهم‌‌کشیده است،'),
(5576, 75, 25, '(که) گمان دارند عذابی در پیش دارند که پشت را می‌شکند!'),
(5577, 75, 26, 'چنان نیست (که انسان می‌پندارد! او ایمان نمی‌آورد) تا موقعی که جان به گلوگاهش رسد،'),
(5578, 75, 27, 'و گفته شود: «آیا کیست که (مرا از مرگ) نجات دهد؟!»'),
(5579, 75, 28, 'و گمان کرد که این همواره (همان) جدایی است.'),
(5580, 75, 29, 'و ساق (زندگی) به ساق (مرگ) در هم بپیچید.'),
(5581, 75, 30, 'در آن روز مسیر همه تنها سوی (دادگاه) پروردگارت خواهد بود!'),
(5582, 75, 31, 'پس او نه تصدیق کرد و نه نماز خواند.'),
(5583, 75, 32, 'بلکه رویگرداند.'),
(5584, 75, 33, 'سپس سوی خانه‌ی خود بازگشت در حالی که چون خران (خرامان) قدم برمی‌داشت!'),
(5585, 75, 34, '(این ناهنجاری) شایسته‌تر است برای تو، پس شایسته‌تر.'),
(5586, 75, 35, 'سپس (این ناهنجاری) برای تو شایسته‌تر است، پس شایسته‌تر.'),
(5587, 75, 36, 'آیا انسان گمان می‌کند که بی‌هدف و مهمل رها می‌شود؟!'),
(5588, 75, 37, 'آیا او نطفه‌ای از منی‌ای که می‌جهد نبود؟!'),
(5589, 75, 38, 'سپس به صورت خون بسته‌‌ای [:آویزک] در آمد. پس خدا او را آفرید و سازمان داد،'),
(5590, 75, 39, 'تا از او دو زوج : مرد و زن آفرید!'),
(5591, 75, 40, 'آیا (چنان نیرومندی‌) بر زنده کردن مردگان توانا نیست‌؟!');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'آیا زمانی از دهر [:طول زمان جهان] بر انسان گذشت که چیزی قابل ذکر نبود؟!'),
(5593, 76, 2, 'ما بی‌گمان انسان را از نطفه‌ای همسازان [:مخلوط] آفریدیم -‌حال آنکه او را همواره می‌آزماییم‌- پس شنوا و بینایش کردیم.'),
(5594, 76, 3, 'ما همانا راه را به او نشان دادیم، خواه سپاسگزار باشد یا بسی ناسپاس!'),
(5595, 76, 4, 'ما برای کافران، زنجیرها و غل‌ها و شعله‌های سوزان آتش را آماده کرده‌ایم!'),
(5596, 76, 5, 'همانا ابرار [:نیکان] از جامی می‌نوشند که با آمیزه‌ای عطرآگین (آمیخته) بوده است.'),
(5597, 76, 6, 'چشمه‌ای را (که) بندگان خدا از آن می‌نوشند، حال آنکه آن را به شدت روان می‌کنند، روان کردنی.'),
(5598, 76, 7, '(همان بندگانی که) به نذر (خود) وفا می‌کنند، و از روزی که گزند آن جویای پرواز بوده است ، می‌ترسند.'),
(5599, 76, 8, 'و بر (مبنای) دوستی خدا (و طعامشان)، آن را به بینوا و یتیم و اسیر می‌خورانند.'),
(5600, 76, 9, '(و به آنان می‌گویند:) «ما تنها برای خشنودی خداست که به شما می‌خورانیم و پاداش و سپاسی (هم) از شما نمی‌خواهیم.»'),
(5601, 76, 10, '«ما از پروردگارمان، از روزی (که) با چهره‌ای درهم نهاده است سخت هراسناکیم.»'),
(5602, 76, 11, 'پس خدا (هم) آنان را از آسیب آن روز نگه داشت‌، و شادابی و شادمانی به آنان بسی ارزانی داشت.'),
(5603, 76, 12, 'و به (پاس) آنکه صبر کردند، بهشت و پرنیان پاداششان داد.'),
(5604, 76, 13, 'حال آنکه در آن (بهشت) بر تخت‌ها(ی خویش) تکیه‌زنندگانند. در آن‌جا نه (حرارت) آفتابی بینند و نه سرمایی طاقت‌فرسا.'),
(5605, 76, 14, 'حال آنکه سایه‌ها(ی درختان) بر آنان نزدیک است و چیدنی‌هایش (به‌گونه‌ای شگفت‌آور) رام.'),
(5606, 76, 15, 'و ظروفی سیمین و جام‌هایی که بلورین بوده، بر (پیرامون) آنان گردانده می‌شود.'),
(5607, 76, 16, 'جام‌هایی از نقره را که درست به‌اندازه (و با کمال ظرافت) آنها را از کار درآورده‌اند.'),
(5608, 76, 17, 'و در آن‌جا از جامی که آمیزه‌ی زنجبیل داشته به آنان می‌نوشانند،'),
(5609, 76, 18, 'چشمه‌ای در آن‌جا که سلسبیل نامیده می‌شود.'),
(5610, 76, 19, 'و بر گِردشان پسرانی جاودان می‌گردند. هنگامی که آنان را ببینی، گویی که مرواریدهایی (بر گرد خود) پراکنده‌اند.'),
(5611, 76, 20, 'و هنگامی که بدانجا نگری، (سرزمینی از) نعمتی (بارور) و کشوری پهناور می‌بینی.'),
(5612, 76, 21, 'جامه‌های ابریشمین سبز و دیبای ستبر در بَرشان است و با دست‌بندهای سیمین زینت شده، و پروردگارشان آشامیدنی‌ای پاک‌کننده به آنان نوشانید.'),
(5613, 76, 22, '«همانا این برای شما پاداشی بوده است و کوشش شما مشکور بوده است.»'),
(5614, 76, 23, 'در حقیقت ما قرآن را بر تو به تدریج فروفرستادیم فرستادنی (رسا).'),
(5615, 76, 24, 'پس برای (رسیدن) فرمان پروردگارت شکیبایی کن و از (میان) آنان گناهکاری یا بسی ناسپاس را فرمان مبر.'),
(5616, 76, 25, 'و نام پروردگارت را بامدادان و شامگاهان یاد کن.'),
(5617, 76, 26, 'و پاسی از شب را پس برایش سجده کن، و شبی دراز او را به پاکی بستای.'),
(5618, 76, 27, 'اینان همواره (دنیای) شتابانِ زودگذر را دوست دارند و روزی گرانبار را (به غفلت) پشت (سر) می‌افکنند.'),
(5619, 76, 28, 'ما آنان را آفریده و پیوند (مفاصل) آنها را استوار کردیم و هنگامی که بخواهیم، آنان را به مانندهاشان تبدیل می‌کنیم.'),
(5620, 76, 29, 'بی‌گمان این (آیات) یادواره‌ای است. پس هر کس بخواهد، راهی سوی پروردگارش برگیرد.'),
(5621, 76, 30, 'و تا خدا نخواهد، (شما) نخواهید خواست‌؛ همواره خدا بسیار دانای حکیم بوده است.'),
(5622, 76, 31, 'هر که را بخواهد، در رحمت خویش در آورد، و برای ظالمان عذابی پردرد آماده کرده است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'سوگند به فرستادگان شناخته شده (بر محور ایمان)؛'),
(5624, 77, 2, 'پس (آن) توفندگان توفانی؛'),
(5625, 77, 3, 'سوگند به پخش‌کنندگان پخش‌کردنی؛'),
(5626, 77, 4, 'پس به جدایی‌افکنان (آن) جدایی‌افکندنی‌؛'),
(5627, 77, 5, 'پس به افکنندگان یادواره‌ای‌؛'),
(5628, 77, 6, 'معذوران یا هشدار‌دهندگان‌.'),
(5629, 77, 7, 'همواره آنچه وعده یافته‌اید بی‌چون شدنی است.'),
(5630, 77, 8, 'پس هنگامی که ستارگان محو شوند،'),
(5631, 77, 9, 'و آن‌گاه که آسمان سوراخ شود،'),
(5632, 77, 10, 'و آن‌گاه که کوه‌ها از جا کنده شوند،'),
(5633, 77, 11, 'و آن‌گاه که پیامبران برایشان وقت مقرر گردد.'),
(5634, 77, 12, 'برای چه روزی [:وقتشان] تأخیر شده است‌؟'),
(5635, 77, 13, 'برای روز جدایی‌.'),
(5636, 77, 14, 'و تو را چه فهماند روز جدایی چیست‌؟'),
(5637, 77, 15, '(در) چنان روزی وای بر تکذیب‌کنندگان!'),
(5638, 77, 16, 'مگر پیشینیان را هلاک نکردیم‌؟'),
(5639, 77, 17, 'سپس از پی آنان دیگران را می‌بریم‌.'),
(5640, 77, 18, 'با مجرمان چنان می‌کنیم.'),
(5641, 77, 19, '(در) چنان روزی وای بر تکذیب‌‌کنندگان‌!'),
(5642, 77, 20, 'مگر شما را از آبی بی‌مقدار نیافریدیم‌؟'),
(5643, 77, 21, 'پس آن را در جایگاهی استوار نهادیم،'),
(5644, 77, 22, 'تا اندازه‌ای معین،'),
(5645, 77, 23, 'پس اندازه‌گیری کردیم، پس چه خوب توانای اندازه‌گیریم.'),
(5646, 77, 24, '(در) چنان روزی وای بر تکذیب‌کنندگان!'),
(5647, 77, 25, 'مگر زمین را پرنده‌ای تندپرواز و نگهبان ننهادیم‌؟'),
(5648, 77, 26, 'نگهبانِ سرنشینانش، چه زندگان و چه مردگان‌،'),
(5649, 77, 27, 'و کوه‌های سر بلند در آن نهادیم. و به شما آبی گوارا نوشانیدیم.'),
(5650, 77, 28, '(در) چنان روزی وای بر تکذیب‌کنندگان!'),
(5651, 77, 29, 'رها شوید سوی همان چیزی که آن را تکذیب می‌کردید.'),
(5652, 77, 30, 'رها شوید سوی سایه‌ای آتشین (که) سه گوشه دارد.'),
(5653, 77, 31, 'نه سایه‌بان است و نه از شعله(ی آتش) بی‌نیاز می‌کند.'),
(5654, 77, 32, '(این آتش سیاه‌فام) چون کاخی (آتشبار) شراره می‌افکند،'),
(5655, 77, 33, 'گویی به‌راستی شترانی زرد رنگند'),
(5656, 77, 34, '(در) چنان روزی وای بر تکذیب‌کنندگان‌!'),
(5657, 77, 35, 'این روزی است که دم نمی‌زنند،'),
(5658, 77, 36, 'و رخصت (هم) نمی‌یابند تا پوزش بخواهند.'),
(5659, 77, 37, '(در) چنان روزی وای بر تکذیب‌کنندگان!'),
(5660, 77, 38, 'این (همان) روز جدایی است! شما و (جمله‌ی) پیشینیان را گرد آوردیم.'),
(5661, 77, 39, 'پس اگر حیله‌ای دارید، با من حیله کنید.'),
(5662, 77, 40, '(در) چنان روزی وای بر تکذیب‌کنندگان!'),
(5663, 77, 41, 'پرهیزگاران بی‌گمان (در) زیر سایه‌ها و در (کنار) چشمه‌سارانند،'),
(5664, 77, 42, 'و (کنار) هر میوه‌ای از آنچه خوش دارند.'),
(5665, 77, 43, '«به (پاداش) آنچه می‌کردید، با گوارایی بخورید و بیاشامید،»'),
(5666, 77, 44, '«ما به‌راستی نیکوکاران را چنان پاداش می‌دهیم.»'),
(5667, 77, 45, '(در) چنان روزی وای بر تکذیب‌کنندگان!'),
(5668, 77, 46, '(ای کافران! در دنیا) بخورید و اندکی برخوردار شوید (که) شما بی‌امان مجرمانید.'),
(5669, 77, 47, '(در) چنان روزی وای بر تکذیب‌کنندگان‌!'),
(5670, 77, 48, 'و هنگامی که به آنان گفته شود: «رکوع کنید.» رکوع نمی‌کنند.'),
(5671, 77, 49, '(در) چنان روزی وای بر تکذیب‌کنندگان!'),
(5672, 77, 50, 'پس به کدامین حدیثی پس از خدا (و قرآن) ایمان می‌آورند؟');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'از چه چیز (از یکدیگر و از تو) می‌پرسند؟'),
(5674, 78, 2, 'از خبر بس بزرگ.'),
(5675, 78, 3, 'چیزی که آنان در(باره‌ی) آن با هم در (ژرفای) اختلافند.'),
(5676, 78, 4, 'نه چنان است. زودا (در برزخ) خواهند دانست.'),
(5677, 78, 5, 'سپس نه چنان است. در آینده‌ای دور [:رستاخیز] خواهند دانست.'),
(5678, 78, 6, 'آیا زمین را گهواره‌ای ننهادیم؟'),
(5679, 78, 7, 'و کوه‌ها را (چون) میخ‌هایی قرار دادیم.'),
(5680, 78, 8, 'و شما را جفت‌هایی آفریدیم.'),
(5681, 78, 9, 'و خوابتان را (مایه‌ی) آرامش(تان) قرار دادیم.'),
(5682, 78, 10, 'و شب را (برایتان) پوششی نهادیم.'),
(5683, 78, 11, 'و روز را (برای) معاش (شما) نهادیم.'),
(5684, 78, 12, 'و بر فرازتان هفت (آسمان) استواران را بنا کردیم.'),
(5685, 78, 13, 'و چراغی بس فروزان گذاردیم.'),
(5686, 78, 14, 'و از ابرهای فشرده(ی بارنده) آبی بسیار، نهروار فرود آوردیم‌.'),
(5687, 78, 15, 'تا بدان دانه و گیاهی برویانیم،'),
(5688, 78, 16, 'و باغ‌هایی سردرهم پیچیده و انبوه را.'),
(5689, 78, 17, 'همواره وعده‌گاه، روز جدایی و داوری بوده‌است.'),
(5690, 78, 18, 'روزی که در صور [:بوق جان‌افزا] دمیده شود. پس در پی آن، گروه گروه بیایید.'),
(5691, 78, 19, 'و آسمان، گشوده گردد. پس (از آنکه) درب‌هایی بوده است.'),
(5692, 78, 20, 'و کوه‌ها به سرعت روانه شوند. پس سراب بودند.'),
(5693, 78, 21, 'همانا جهنم کمینگاهی بوده.'),
(5694, 78, 22, 'برای سرکشان پناهگاهی (بوده)‌.'),
(5695, 78, 23, 'حال آنکه روزگارانی در آن درنگ‌کنندگانند.'),
(5696, 78, 24, 'آنجا نه خنکی چشند و نه آشامیدنی.'),
(5697, 78, 25, 'جز مایعی جوشان و چرکابه‌ای بس گلوگیر.'),
(5698, 78, 26, '(این است) کیفری مناسب (با جرمشان).'),
(5699, 78, 27, 'بی‌گمان آنان بوده‌اند که به (روز) حساب امیدی نداشتند.'),
(5700, 78, 28, 'و با آیاتمان (همان‌ها و ما را) سخت تکذیب می‌کردند، چه تکذیب کردنی!'),
(5701, 78, 29, 'و هر چیزی را نوشته شده بر شمردیم.'),
(5702, 78, 30, 'پس بچشید که جز عذاب، هرگز (چیزی) برایتان نمی‌افزاییم.'),
(5703, 78, 31, 'بی‌گمان برای پرهیزگاران کامیابی‌ای بی‌رنج است.'),
(5704, 78, 32, 'باغ‌ها و انگورها.'),
(5705, 78, 33, 'و دخترانی فربه و پستان بر آمده (و با آنان) همسال و همسان.'),
(5706, 78, 34, 'و پیاله‌(هایی) لب‌ریزان‌.'),
(5707, 78, 35, 'در آنجا نه بیهوده‌ای شنوند و نه دروغ پردازی‌ای.'),
(5708, 78, 36, 'حال آنکه پاداشی از پروردگار تو و عطایی است حساب‌شده.'),
(5709, 78, 37, 'پروردگار آسمان‌ها و زمین و آنچه میان آن دو است. رحمتگر بر آفریدگان. ایشان اختیار خِطابی از او ندارند.'),
(5710, 78, 38, 'روزی که روح و فرشتگان صف‌اندرصف به‌پاخیزند. سخن نگویند، مگر کسی که رحمان به او رخصت دهد، و سخنی شایسته گوید.'),
(5711, 78, 39, 'آن روز پای برجا (و) حق است‌؛ پس هر که خواهد، راه بازگشتی فراسوی پروردگارش برگیرد.'),
(5712, 78, 40, 'ما همواره شما را از عذابی نزدیک هشدار دادیم؛ روزی که آدمی آنچه را با دستاوردش پیش فرستاده است بنگرد و کافر گوید: «ای کاش من خاک بودم!»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'سوگند به کَنَنْدگان (جان و جانان) در حالی که (در آنان) غرقند.'),
(5714, 79, 2, 'و به گره‌زنندگان گره‌زدنی.'),
(5715, 79, 3, 'و به نیروهایی شناکنان شناکردنی.'),
(5716, 79, 4, 'پس به پیشی‌گیرندگان پیشی‌گرفتنی.'),
(5717, 79, 5, 'پس به تدبیرکنندگان کار (آفریدگان) تدبیر کردنی.'),
(5718, 79, 6, 'آن روز که (زمین) لرزنده بلرزد.'),
(5719, 79, 7, '(و) از پی آن (لرزه‌‌ی دیگری) در ردیف(اش) آید.'),
(5720, 79, 8, '(در) چنان روزی، دل‌هایی بس نگران (و تپنده)‌اند.'),
(5721, 79, 9, 'دیدگانشان فرو افتاده‌اند.'),
(5722, 79, 10, 'گویند: «آیا ما (باز) همواره در گودال‌ها(ی گورهامان به زندگی) بازگشت شوندگانیم؟'),
(5723, 79, 11, '«آیا هنگامی (که) ما استخوان‌ریزه‌های پوسیده بوده‌ایم (زندگی را از سر می‌گیریم)؟»'),
(5724, 79, 12, 'گویند: «این (زندگی پس از مرگ) در این هنگام (و هنگامه) برگشتی زیانبار است.»'),
(5725, 79, 13, 'پس جز این نیست که آن بازگشت (بسته به) یک فریاد جان‌افزاست (و بس).'),
(5726, 79, 14, 'پس ناگهان آنان در زمینِ هموار خواهند بود.'),
(5727, 79, 15, 'آیا داستان موسی تو را آمد؟'),
(5728, 79, 16, 'چون پروردگارش او را در وادی مقدّس به حال پیچیدگی (نورانیش) ندا در داد:'),
(5729, 79, 17, '«فراسوی فرعون برو، که وی بی‌امان طغیان کرده است.»'),
(5730, 79, 18, '«پس (به او) بگو: “آیا سر آن داری که به پاکیزگی گرایی؟‌”»'),
(5731, 79, 19, '«“و تو را سوی پروردگارت راه نمایم تا پروا بداری‌؟”»'),
(5732, 79, 20, 'پس بزرگ‌ترین نشانه‌ی (چشمگیر دیدنی) خدا را بدو نمود.'),
(5733, 79, 21, 'پس (فرعون) تکذیب کرد و عصیان نمود.'),
(5734, 79, 22, 'سپس شتابان پشت کرد.'),
(5735, 79, 23, 'پس (گروهی را) گرد آورد. آن‌گاه ندا در داد.'),
(5736, 79, 24, 'پس گفت: «من برترین پروردگار شمایم!»'),
(5737, 79, 25, 'پس خدا (هم) او را به کیفر سخت دنیا و آخرت برگرفت.'),
(5738, 79, 26, 'به‌راستی برای هر کس که (از خدا) پروا بدارد، در این (ماجرا) به‌درستی عبرتی است.'),
(5739, 79, 27, 'آیا آفرینش شما بنیادین‌تر است، یا آسمان (که آن را) سازمان داده است‌؟'),
(5740, 79, 28, 'بلندای جذب‌شده‌اش را بر افراشت، پس آن را سازمان داد.'),
(5741, 79, 29, 'و شبش را تیره کرد و روزش را (روشن) برون آورد.'),
(5742, 79, 30, 'و پس از آن (حرکات ناهموار)، زمین را حرکتی هموار داد.'),
(5743, 79, 31, 'آبش و چراگاهش را از آن برون آورد.'),
(5744, 79, 32, 'و کوه‌ها را در عمقش کوبید.'),
(5745, 79, 33, 'حال آنکه (این آب و گیاه) بهره‌ای است برایتان و برای حیوانات نعمت‌وارتان.'),
(5746, 79, 34, 'پس آن‌گاه که آن هنگامه‌ی لبریز فراگیر از دشواری در رسد.'),
(5747, 79, 35, 'روزی که انسان آنچه (را) کوشیده است به یاد آورد!'),
(5748, 79, 36, 'و جهنم برای هر کس می‌نگرد بسی آشکار گردد.'),
(5749, 79, 37, 'پس اما هر کس طغیان کرد؛'),
(5750, 79, 38, 'و زندگی (پست) دنیا را (بر آخرت) برگزید؛'),
(5751, 79, 39, 'بی‌گمان (جایگاه و) پناهگاهش آتش زبانه‌دار است.'),
(5752, 79, 40, 'و اما کسی که از مقام پروردگارش هراسید، و نفس (خود) را از هوس باز داشت،'),
(5753, 79, 41, 'پس بی‌گمان پناهگاهش بهشت است.'),
(5754, 79, 42, 'درباره‌ی ساعت (پایانی جهان تکلیف) از تو می‌پرسند: «استواریش چه هنگام است؟»'),
(5755, 79, 43, 'تو در چه (شناختی) از یادواره‌ی آنی‌؟'),
(5756, 79, 44, 'پایانش فراسوی پروردگار توست.'),
(5757, 79, 45, 'تو تنها کسی را که از آن می‌هراسد هشدار می‌دهی.'),
(5758, 79, 46, 'روزی که آن را می‌بینند، گویی آنان بی‌گمان جز شبی یا روزش را درنگ نکرده‌اند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, '(ای پیمبر خوش‌خو! آن تندخو) چهره در هم کشید، و روی گردانید،'),
(5760, 80, 2, '(هنگامی) که (آن) مرد نابینا سویش آمد.'),
(5761, 80, 3, 'و تو را (ای بدخو) چه می‌فهماند؟ شاید (هم) او به پاکی گراید.'),
(5762, 80, 4, 'یا یاد پذیرد، پس یادواره سودش دهد.'),
(5763, 80, 5, 'اما کسی که خود را بی‌نیاز پندارد.'),
(5764, 80, 6, 'پس تو برایش بانگی لطیف و پربرگ‌وساز می‌نوازی.'),
(5765, 80, 7, 'و چیزی (هم) بر تو نیست که خود را پاک نگرداند!'),
(5766, 80, 8, 'و اما آن کس که شتابان نزدت آمد،'),
(5767, 80, 9, 'در حالی‌که (از خدا) می‌هراسد،'),
(5768, 80, 10, 'پس تو از او -با بی‌‌اعتنایی- سهل‌انگاری می‌کنی.'),
(5769, 80, 11, 'زنهار (چنان نیست!) این (آیات) همواره یادواره‌‌ای است.'),
(5770, 80, 12, 'پس هر کس بخواهد، آن را یاد کند.'),
(5771, 80, 13, 'در صحیفه‌هایی بس گرامی شده.'),
(5772, 80, 14, 'بالا بلندی بسی پاک‌شده.'),
(5773, 80, 15, 'به دست‌‌های فرستادگانی.'),
(5774, 80, 16, 'ارجمندان و نیکوکاران‌.'),
(5775, 80, 17, 'کشته شده است انسان! چه چیز او را به کفر و کفران واداشته؟'),
(5776, 80, 18, '(خدا) او را از چه چیز آفریده‌؟'),
(5777, 80, 19, 'از نطفه‌ای آفریدش‌. پس اندازه‌ای بخشیدش.'),
(5778, 80, 20, 'سپس او را فراسوی راه آسان گردانید.'),
(5779, 80, 21, 'سپس میراندش، پس در گورش نهاد.'),
(5780, 80, 22, 'سپس هنگامی که بخواهد او را باز می‌گرداند.'),
(5781, 80, 23, 'نه چنان است‌! هنوز آنچه را به او دستور داده، انجام نداده است.'),
(5782, 80, 24, 'پس انسان باید به خوراک (روح و تن) خود بنگرد.'),
(5783, 80, 25, 'که همانا ما آب را به صورت بارشی تند فرو ریختیم.'),
(5784, 80, 26, 'سپس زمین را با شکافتنی (شایسته) شکافتیم.'),
(5785, 80, 27, 'پس در آن دانه‌ای رویانیدیم،'),
(5786, 80, 28, 'و انگوری و سبزی‌ای،'),
(5787, 80, 29, 'و (درخت) زیتونی و خرمایی،'),
(5788, 80, 30, 'و باغ‌هایی انبوه،'),
(5789, 80, 31, 'و میوه‌ای و چراگاهی.'),
(5790, 80, 32, 'برای استفاده‌ی شما (مردمان) و دام‌هایتان.'),
(5791, 80, 33, 'پس چون فریاد گوش‌خراش و دل‌خراش و جان‌ا‌فزا در رسد؛'),
(5792, 80, 34, 'روزی که آدمی از برادرش،'),
(5793, 80, 35, 'و از مادرش و پدرش،'),
(5794, 80, 36, 'و از همسرش و فرزندانش می‌گریزد،'),
(5795, 80, 37, '(در آن روز) هر کس از آنان را کاری است، که او را تنها به خودش مشغول می‌دارد.'),
(5796, 80, 38, 'در چنان روزی چهره‌هایی درخشان است،'),
(5797, 80, 39, '(که) خندان (و) و نویدگیرندگانند.'),
(5798, 80, 40, 'و چهره‌هایی است که بر آنها غبارهایی نشسته،'),
(5799, 80, 41, '(و) آنها را تنگدستی و تنگی و خواری سخت پوشانده!'),
(5800, 80, 42, 'ایشان، (هم)آنان کافران و فاجرانند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'آن‌گاه که خورشید در هم پیچیده گردد.'),
(5802, 81, 2, 'و آن‌گاه که ستارگان درخشان تیره شوند.'),
(5803, 81, 3, 'و آن‌گاه که کوه‌ها به شدت حرکت داده شوند.'),
(5804, 81, 4, 'و آنگاه که شتران ماده (به هنگام زادن) وانهاده شوند.'),
(5805, 81, 5, 'و آن‌گاه که وحشیان - بدون برگشت- گرد آورده شوند.'),
(5806, 81, 6, 'و آن‌گاه که دریاها به شدت به جوش آورده شوند.'),
(5807, 81, 7, 'و آن‌گاه که نفوس، به هم در پیوسته شوند.'),
(5808, 81, 8, 'و آن‌گاه که دختران (زنده) به گور شده پرسیده شوند،'),
(5809, 81, 9, '(که) به کدامین گناه کشته شدند؟'),
(5810, 81, 10, 'و آن‌گاه که نامه‌ها (ی وحیانی و اعمال مکلفان) باز و آشکار گردند.'),
(5811, 81, 11, 'و آن‌گاه که پوست آسمان - هم‌چون شتران- برکنده شود (و به حالت نخستین برگردد).'),
(5812, 81, 12, 'و آن‌گاه که آتش پرهیجان جهنم به شدت برافروخته گردد.'),
(5813, 81, 13, 'و آن‌گاه که بهشتِ زینت‌یافته پیش آورده شود.'),
(5814, 81, 14, 'هر نفسی بداند چه پیش آورده.'),
(5815, 81, 15, 'پس سوگند نمی‌خورم به اختران بسی باز گردنده.'),
(5816, 81, 16, 'روان‌شده‌ی پنهان‌شونده.'),
(5817, 81, 17, 'سوگند به شب چون پس و پیش آید.'),
(5818, 81, 18, 'و سوگند به صبح چون تنفس کند.'),
(5819, 81, 19, 'که (قرآن) بی‌گمان سخن فرستاده‌ی بزرگواری است.'),
(5820, 81, 20, 'نیرومندی (که) نزد صاحب عرش فرماندهی بامکنت و مکانت است.'),
(5821, 81, 21, '(در) آنجا (هم) مطاع (و هم) امین است.'),
(5822, 81, 22, 'و (این) مصاحب و همنشین شما مجنون نیست.'),
(5823, 81, 23, 'و همانا او خدا را در افقی روشنگر (با دیده‌ی بصیرت) دید.'),
(5824, 81, 24, 'و او بر غیب بخیل نیست.'),
(5825, 81, 25, 'و قرآن سخن شیطانی سنگ‌باران شده نیست.'),
(5826, 81, 26, 'پس به کجا می‌روید؟'),
(5827, 81, 27, 'این قرآن به جز یادواره‌ای برای عالمیان نیست.'),
(5828, 81, 28, '(بلکه) برای هر کس از شما که بخواهد (در سایه‌ی وحی) مستقیم گردد.'),
(5829, 81, 29, 'و تا خدا، پروردگار جهانیان، نخواهد، (شما نیز) نخواهید خواست.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'هنگامی که آسمان از هم بشکافد.'),
(5831, 82, 2, 'و هنگامی که اختران پراکنده شوند.'),
(5832, 82, 3, 'و هنگامی که دریاها بسیار روان شوند (و فوران کنند).'),
(5833, 82, 4, 'و هنگامی که گورها (زیر و زبر و) انگیخته شوند.'),
(5834, 82, 5, 'هر نفسی آنچه را پیش فرستاده و باز پس نهاده، بداند.'),
(5835, 82, 6, 'هان ای انسان! چه چیز تو را به پروردگار کریمت مغرور ساخت‌؟'),
(5836, 82, 7, '(همان) کسی که تو را آفرید؛ پس سازمانت داد، و بعد تو را سامان بخشید.'),
(5837, 82, 8, '(و) در هر چهره‌ای [:روحی و جسمی] آنچه می‌خواست، تو را ترکیب داد.'),
(5838, 82, 9, 'هرگز نه چنان است! شما دین را (با همان) تکذیب می‌کنید.'),
(5839, 82, 10, 'و همواره بر شما بی‌گمان نگهبانانی (گماشته شده)اند؛'),
(5840, 82, 11, 'کریمانِ نویسندگان (اعمال شما)،'),
(5841, 82, 12, 'آنچه را می‌کنید، می‌دانند.'),
(5842, 82, 13, 'به‌راستی نیکان بی‌گمان در (ژرفای) نعمتی اندرند.'),
(5843, 82, 14, 'و بدکاران همی بی‌امان در آتشی شعله‌ورند.'),
(5844, 82, 15, 'روز بُروز طاعت، آن را می‌افروزند.'),
(5845, 82, 16, 'و آنها از آن (عذاب) پنهان نیستند.'),
(5846, 82, 17, 'و تو را چه فهمانید که روزِ دین [:بُروز طاعت] چیست‌؟'),
(5847, 82, 18, 'چه فهماندت که روز دین چیست‌؟'),
(5848, 82, 19, 'روزی را که هیچ‌کس برای کسی هیچ اختیاری ندارد و در آن روز، فرمان تنها از خداست.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'وای بر کم‌‌کنندگان!'),
(5850, 83, 2, 'همانان که چون از مردمان پیمانه‌ای ستانند، تمام ستانند.'),
(5851, 83, 3, 'و چون آنان را پیمانه یا وزن کنند، به ایشان زیان رسانند.'),
(5852, 83, 4, 'آیا ایشان گمان نمی‌دارند که بی‌گمان برانگیختگانند،'),
(5853, 83, 5, 'برای روزی بزرگ.'),
(5854, 83, 6, 'روزی که مردمان در برابر پروردگار جهانیان به پای ایستند.'),
(5855, 83, 7, 'هرگز نه چنان است (که می‌پندارند)! کتاب (اعمال) فاجران بی‌چون و بی‌امان در (ژرفای) سجین است.'),
(5856, 83, 8, 'و تو را چه آگاه کرد که سجّین چیست‌؟'),
(5857, 83, 9, 'کتابی است رقم زده و نوشته شده.'),
(5858, 83, 10, 'وای در آن هنگام (و هنگامه) بر تکذیب‌کنندگان‌!'),
(5859, 83, 11, 'آنان که با روز جزا (همان را) تکذیب می‌کنند.'),
(5860, 83, 12, 'و جز هر تجاوزپیشه‌ی گناهکاری به آن تکذیب نمی‌کند.'),
(5861, 83, 13, 'هنگامی که آیاتمان بر او خوانده شود، گوید: « (این‌ها) بافته‌ها و افسانه‌های پیشینانتان است.»'),
(5862, 83, 14, 'هرگز (نه چنان است)! بلکه آنچه مرتکب می‌شده‌اند زنگار بر دل‌هایشان بسته است.'),
(5863, 83, 15, 'هرگز (نه چنان است)! بلکه آنان بی‌گمان در آن روز، از پروردگارشان سخت محجوبند.'),
(5864, 83, 16, 'سپس بی‌چون، آنان جهنم را بی‌امان گیرانه‌اند.'),
(5865, 83, 17, 'سپس (به ایشان) گفته می‌شود: «این (همان) است که به آن تکذیب می‌کردید.»'),
(5866, 83, 18, 'هرگز (نه چنان است)! همواره کتاب نیکان به‌راستی در (ژرفای) علّیّون است.'),
(5867, 83, 19, 'و چه (چیز) تو را شناساند (که) علّیّون چیست‌؟'),
(5868, 83, 20, 'کتابی است رقم زده شده.'),
(5869, 83, 21, 'مقرّبان آن را مشاهده می‌کنند.'),
(5870, 83, 22, 'بی‌گمان نیکوکاران همواره در (ژرفای) نعمت پربار (الهی)اند.'),
(5871, 83, 23, 'بر تخت‌ها (نشسته) می‌نگرند.'),
(5872, 83, 24, 'در چهره‌هایشان طراوت نعمت (بهشت) را می‌شناسی.'),
(5873, 83, 25, 'از باده‌ای مُهر زده شده نوشانیده شوند.'),
(5874, 83, 26, '(باده‌ای که) مُهر آن، مشک است‌، و در این (نعمت‌ها) کوشندگان در پیشگیری (از دیگران) باید از یکدیگر پیشی گیرند.'),
(5875, 83, 27, 'و ترکیبش از (بلندای) چشمه‌ای سراشیب است.'),
(5876, 83, 28, 'چشمه‌ای را که نزدیک‌شدگان (به خدا) از آن می‌نوشند.'),
(5877, 83, 29, 'کسانی که گناه می‌کردند، همواره از (کار) آنان که ایمان آورده بودند ریشخند می‌زدند.'),
(5878, 83, 30, 'و چون بر ایشان می‌گذشتند، (به مسخره) اشاره(‌ی چشم و ابرو) با هم ردّ و بدل می‌کردند.'),
(5879, 83, 31, 'و هنگامی که نزد خانواده‌(های) خود باز می‌گشتند، به شوخ‌طبعی می‌پرداختند.'),
(5880, 83, 32, 'و هنگامی که مؤمنان را می‌دیدند، می‌گفتند: «اینان بی‌امان گمراهانند.»'),
(5881, 83, 33, 'و حال آنکه آنان (به عنوان) نگهبانانی بر اینان فرستاده نشده‌اند.'),
(5882, 83, 34, 'پس امروز، کسانی که ایمان آوردند از (کار) کافران می‌خندند.'),
(5883, 83, 35, 'بر تخت‌ها(ی خود نشسته و) نظاره می‌کنند.'),
(5884, 83, 36, '(با این جریان) آیا کافران به پاداش نیک! آنچه می‌کرده‌اند رسیده‌اند؟');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'آن‌گاه که آسمان از هم بشکافد.'),
(5886, 84, 2, 'و پروردگارش را فرمان برد و (چنان) سزد.'),
(5887, 84, 3, 'و آن‌گاه که زمین کشیده شود،'),
(5888, 84, 4, 'و آنچه را در درون دارد برون افکند و تهی گردد.'),
(5889, 84, 5, 'و پروردگارش را فرمان برد و (چنان) سزد.'),
(5890, 84, 6, 'هان ای انسان! بی‌گمان تو سوی پروردگار خود در تلاشی. پس او را ملاقات‌کننده‌ای.'),
(5891, 84, 7, 'پس اما کسی که کارنامه‌اش به دست راستش داده شود،'),
(5892, 84, 8, 'پس در آینده‌ای زود با او حسابی آسان شود.'),
(5893, 84, 9, 'و شادمان سوی کسانش باز گردد.'),
(5894, 84, 10, 'و اما کسی که کارنامه‌اش (از) پشت سرش به او داده شود،'),
(5895, 84, 11, 'پس در آینده‌ای دور هلاک و تباهی (خویش) را (همی) خواهد.'),
(5896, 84, 12, 'و آتشی زبانه‌دار بیفروزد.'),
(5897, 84, 13, 'او بی‌گمان در (میان) خانواده‌ی خود شادمان بود.'),
(5898, 84, 14, 'او همانا پنداشت که هرگز حیرت‌زا بر نخواهد گشت.'),
(5899, 84, 15, 'آری، همواره پروردگارش به او بینا بود.'),
(5900, 84, 16, 'پس به روشنیِ میانه‌ی روز و شب سوگند نمی‌خورم.'),
(5901, 84, 17, 'سوگند به شب و آنچه (شب) فرو پوشاند.'),
(5902, 84, 18, 'و سوگند به ماه، چون بدرتمام شود.'),
(5903, 84, 19, 'بی‌چون و بی‌امان از حالی به حالی بر خواهید نشست.'),
(5904, 84, 20, 'پس آنان را چیست (که) ایمان نمی‌آورند؟'),
(5905, 84, 21, 'و (چرا) چون بر آنان قرآن تلاوت شود به‌راستی (برایش) سجده [:خشوع‌] نمی‌کنند؟'),
(5906, 84, 22, '(نه!) بلکه آنان که کفر ورزیده‌اند، (به آن همان را) تکذیب می‌کنند.'),
(5907, 84, 23, 'و خدا به آنچه در پنهان نگاه می‌دارند داناتر است.'),
(5908, 84, 24, 'پس آنان را به عذابی دردناک نوید ده‌.'),
(5909, 84, 25, 'مگر کسانی که ایمان آورده و کارهای شایسته(ی ایمان) کرده‌اند، (که) آنان را پاداشی بی‌منّت و ناگسستنی خواهد بود.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'سوگند به آسمان دارای کاخ‌ها.'),
(5911, 85, 2, 'و سوگند به روز موعود [:قیامت].'),
(5912, 85, 3, 'و سوگند به گواه و گواهی‌شونده.'),
(5913, 85, 4, 'مرگ بر آدم‌سوزان زمینِ شکاف‌دار .'),
(5914, 85, 5, 'آتش مایه‌دار و گیرانه‌ی انبوه.'),
(5915, 85, 6, 'چون آنان بر بلندای آن نشسته‌اند.'),
(5916, 85, 7, 'و ایشان بر آنچه بر (سر و سامان) مؤمنان می‌آوردند، گواهانند.'),
(5917, 85, 8, 'و از آنان عیبی انتقام‌آور نگرفته بودند، جز اینکه (آنان) به خدای عزیز ستوده ایمان آوردند.'),
(5918, 85, 9, 'کسی (که) فرمانروایی آسمان‌ها و زمین از اوست‌، و خدا بر هر چیزی بسیار گواه است.'),
(5919, 85, 10, 'بی‌امان کسانی که مردان و زنان مؤمن را به آتش در کشیده، سپس توبه نکردند، برایشان عذاب جهنم است و ایشان را عذاب بس سوزان است.'),
(5920, 85, 11, 'بی‌گمان کسانی که ایمان آورده و کارهای شایسته‌(ی ایمان) کردند، برایشان باغ‌هایی (با درختان) سردرهم است که از زیر (درختان)شان نهرها روان است‌؛ این است (همان) کامیابی سالم بزرگ.'),
(5921, 85, 12, 'به‌راستی حمله‌ی پروردگارت سخت سنگین است.'),
(5922, 85, 13, 'همواره (هم)اوست که (آفرینش را) می‌آغازد و (هم او) باز می‌گرداند.'),
(5923, 85, 14, 'و اوست بس پوشنده‌ی (گناهان و نقصان‌ها و) بسی دوستدار (مؤمنان).'),
(5924, 85, 15, 'صاحب عرش و دارای کرامت و رحمتی گشاده (در عرش و فرش).'),
(5925, 85, 16, 'آنچه می‌خواهد (نسبت به آن) فعّال است.'),
(5926, 85, 17, 'آیا تو را حدیث سپاهیان آمد؟'),
(5927, 85, 18, '(سپاهیان) فرعون و ثمود.'),
(5928, 85, 19, 'بلکه آنان که کافر شده‌اند در (ژرفای) تکذیبند.'),
(5929, 85, 20, 'حال آنکه خدا از پشتشان (بر ایشان) محیط است.'),
(5930, 85, 21, 'بلکه آن قرآنی، با کرامتی گشاده است،'),
(5931, 85, 22, '(که) در لوحی [:جایگاهی آشکار] محفوظ است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'سوگند به آسمان و فرودآینده(ای) از آن.'),
(5933, 86, 2, 'و چه فهماندت که آن فرودآینده چیست‌؟'),
(5934, 86, 3, 'اختر فروزان سوراخ‌کن.'),
(5935, 86, 4, 'هیچ کس نیست مگر اینکه در آن هنگام (و هنگامه)، نگاهبانی (ربّانی) بر او (گماشته شده) است.'),
(5936, 86, 5, 'پس انسان باید بنگرد که از چه آفریده شده است‌؟'),
(5937, 86, 6, 'از آبی‌جهنده آفریده شده،'),
(5938, 86, 7, '(که) از صُلب (مرد) و از میان استخوان‌های سینه(‌ی زن،) برون می‌آید.'),
(5939, 86, 8, 'همواره او [:خدا] بر باز گردانیدن وی به‌راستی تواناست.'),
(5940, 86, 9, 'آن روز که رازها فاش شوند،'),
(5941, 86, 10, 'پس برای او [:انسان و...] نه هیچ نیرویی است و نه هیچ یاوری‌.'),
(5942, 86, 11, 'سوگند به آسمان دارای (حالت) بازگردانیدن (امانت‌ها) و بازگشتن (به حالت گازی نخستین).'),
(5943, 86, 12, 'و سوگند به زمین شکاف‌دار (آماده‌ی کِشت).'),
(5944, 86, 13, '(که) همانا این قرآن بی‌گمان گفتاری قاطع و جداکننده(ی حق از باطل) است.'),
(5945, 86, 14, 'و آن (هرگز) شوخی نیست.'),
(5946, 86, 15, 'آنان بی‌امان دست به نیرنگی بزرگ می‌زنند.'),
(5947, 86, 16, 'و (من نیز برابرشان) دست به نیرنگی بزرگ می‌زنم.'),
(5948, 86, 17, 'پس کافران را مهلت ده و اندکی آنان را به حال خود واگذار.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'نام پروردگار -والاترین‌- خود را به پاکی بستای.'),
(5950, 87, 2, 'همان که آفرید، پس سامان بخشید.'),
(5951, 87, 3, 'و آن که اندازه‌گیری کرد، پس راهنمایی نمود.'),
(5952, 87, 4, 'و کسی که چراگاه را برون آورد.'),
(5953, 87, 5, 'پس (از چندی) آن را خاشاکی تیره‌گون گردانید.'),
(5954, 87, 6, 'ما به زودی تو را خواننده(ی آیات وحیانی) خواهیم کرد، پس (آن را) فراموش نمی‌کنی.'),
(5955, 87, 7, 'جز آنچه (را) خدا خواسته (که برایت نخواند). همواره او آشکار و آنچه را که نهان است می‌داند.'),
(5956, 87, 8, 'و (کار) تو را برای (پیمودنِ) آسان‌ترینِ راه آسان کنیم.'),
(5957, 87, 9, 'پس به یاد(شان) آور، اگر یادواره سودمند است.'),
(5958, 87, 10, 'آن کس که بهراسد، زودا به‌راستی پند گیرد.'),
(5959, 87, 11, 'و شقی‌ترین (مکلفان) خود را از آن با کوشش، دور کند.'),
(5960, 87, 12, 'کسی که آتشی بزرگ را شعله زند.'),
(5961, 87, 13, 'سپس در آن نه بمیرد و نه زندگی کند.'),
(5962, 87, 14, 'به‌راستی رستگار کرد آن کس که خود را بسی پاک گرداند.'),
(5963, 87, 15, 'و نام پروردگارش را یاد کرد، پس نماز گزارد.'),
(5964, 87, 16, 'بلکه (شما) زندگی دنیا را (بر آخرت) بر می‌گزینید.'),
(5965, 87, 17, 'حال آنکه آخرت نیکوتر و پایدارتر است‌.'),
(5966, 87, 18, 'بی‌گمان در صحیفه‌های گذشته (همچنان) این (وحی) موجود است؛'),
(5967, 87, 19, '(در) صحیفه‌های ابراهیم و موسی.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'آیا تو را داستان (حادثه‌ی فراگیر) بس پوشنده(ی چهره‌ها) رسیده است‌؟'),
(5969, 88, 2, 'در چنان روزی، چهره‌هایی زبونند؛'),
(5970, 88, 3, 'تلاش‌کننده‌ی رنج‌برنده‌اند؛'),
(5971, 88, 4, 'آتشی بس سوزان را شعله زنند؛'),
(5972, 88, 5, 'از چشمه‌ای داغ (و) جوشان لبریز، نوشانیده شوند؛'),
(5973, 88, 6, 'خوراکی جز آنچه به تضرعشان آوَرَد ندارند؛'),
(5974, 88, 7, '(خوراکی که) نه فربه کند و نه از گرسنگی بی‌‌نیاز کند؛'),
(5975, 88, 8, 'در آن روزگاران چهره‌هایی شادابند؛'),
(5976, 88, 9, 'برای کوشش خود خشنودند؛'),
(5977, 88, 10, 'در بهشتی بالا و والایند؛'),
(5978, 88, 11, 'سخن بیهوده‌ای در آنجا نشنوند؛'),
(5979, 88, 12, 'در آن چشمه‌ای روان است؛'),
(5980, 88, 13, 'در آن تخت‌هایی بلند است؛'),
(5981, 88, 14, 'و قدح‌هایی بی‌دسته نهاده شده؛'),
(5982, 88, 15, 'و نازبالش‌هایی پهلوی هم، چیده شده؛'),
(5983, 88, 16, 'و فرش‌‌هایی (زربفت)، پراکنده و گسترده.'),
(5984, 88, 17, 'آیا پس به شتر نمی‌نگرند، (که) چگونه آفریده شده‌؟'),
(5985, 88, 18, 'و سوی آسمان که چگونه برافراشته شده‌؟'),
(5986, 88, 19, 'و سوی کوه‌ها که چگونه (بر زمین) نصب شده‌اند؟'),
(5987, 88, 20, 'و به زمین که چگونه گسترده شده‌؟'),
(5988, 88, 21, 'پس یادآور باش که تو، تنها، یادآورنده‌ای (و)'),
(5989, 88, 22, 'بر آنان تسلّطی نداری.'),
(5990, 88, 23, 'مگر کسی که روی گردانید و کفر ورزید.'),
(5991, 88, 24, 'پس خدا او را به بزرگ‌ترین عذاب تعذیب کند.'),
(5992, 88, 25, 'بی‌گمان بازگشت آنان تنها سوی ماست.'),
(5993, 88, 26, 'سپس حسابشان بی‌امان تنها بر ماست.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'سوگند به سپیده‌دم؛'),
(5995, 89, 2, 'و به شب‌هایی ده‌گانه (مانند ده شب اول ذیحجه)؛'),
(5996, 89, 3, 'و به جفت و تاق [:آفریدگان و آفریدگار‌، همانند و بی‌مانند].'),
(5997, 89, 4, 'و به شب، هنگامی که سیر می‌کند.'),
(5998, 89, 5, 'آیا در این، برای خردمندی خویشتن‌دار (نیاز به) سوگندی است‌؟'),
(5999, 89, 6, 'مگر ندیدی که پروردگارت با عادیان (نخستین) چه کرد؟'),
(6000, 89, 7, '(با) عمارات ستون‌دار اِرَم.'),
(6001, 89, 8, 'آن که مانندش در شهرها ساخته نشده‌؟'),
(6002, 89, 9, 'و (با) ثمودیان، همانان که در گودی، تخته‌سنگ‌ها را بریدند؟'),
(6003, 89, 10, 'و (با) فرعون، صاحب (خرگاه‌ها و کاخ‌ها و) میخ‌های بلند.'),
(6004, 89, 11, 'همانان که در شهرها به طغیان سر برافراشتند.'),
(6005, 89, 12, 'پس در آنها بسی فساد کردند.'),
(6006, 89, 13, 'پس پروردگارت بر (سروسامان)‌شان تازیانه‌ی عذابی فرو نواخت.'),
(6007, 89, 14, 'بی‌گمان پروردگارت بی‌امان در کمین (آنان) است.'),
(6008, 89, 15, 'پس اما انسان، هنگامی که پروردگارش وی را بیازماید و گرامیش بدارد، و نعمت به او دهد، گوید: «پروردگارم مرا گرامی داشته است.»'),
(6009, 89, 16, 'و اما هنگامی که بیازمایدش، پس روزی‌اش را بر او تنگ گرداند، گوید: «پروردگارم به من اهانت کرده است.»'),
(6010, 89, 17, 'هرگز نه (چنین و نه چنان است)! بلکه یتیم را گرامی نمی‌دارید؛'),
(6011, 89, 18, 'و بر خوراک (دادن به) بینوا (همدیگر را) برنمی‌انگیزید؛'),
(6012, 89, 19, 'و میراث را چپاول‌گرانه می‌خورید؛'),
(6013, 89, 20, 'و مال را دوست می‌دارید، دوست‌داشتنی بسیار.'),
(6014, 89, 21, 'هرگز (نه چنان است)! آن‌گاه که زمین، در هم کوبیده شود کوبیده شدنی،'),
(6015, 89, 22, 'و پروردگارت با فرشتگان (کارگزار) صف‌اندرصف آیند،'),
(6016, 89, 23, 'و جهنم در آن هنگام (هنگامه) آورده شود، آن روز است که انسان (حق را) به خوبی یاد آورد. و کجا و کی برایش جای یادواره است؟'),
(6017, 89, 24, 'گوید: «کاش برای زندگانی (اصلی) خود (چیزی) پیش فرستاده بودم‌.»'),
(6018, 89, 25, 'پس در این هنگام (و هنگامه) هیچ کس همچون عذاب کردن او، عذاب نکند.'),
(6019, 89, 26, 'و هیچ‌کس (هم) چون در بند کشیدن او، در بند نکشد.'),
(6020, 89, 27, 'هان ای نفس مطمئنه‌[:آرمیده]‌!'),
(6021, 89, 28, 'خشنود (از خدا) و خوشایند سوی پروردگارت باز گرد.'),
(6022, 89, 29, 'پس در میان بندگان ویژه‌‌ام در آی‌،'),
(6023, 89, 30, 'و در بهشت ویژه‌‌ام داخل شو.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'به این شهر [:مکه] سوگند نمی‌خورم.'),
(6025, 90, 2, 'حال آنکه تو در این شهر جای داری، یا حرمتت در آن هتک شده.'),
(6026, 90, 3, 'سوگند(م) به پدری (هم‌چون محمد) است‌. و (نیز) آن (فرزندانی) که تولید کرده‌.'),
(6027, 90, 4, 'به‌راستی انسان را در (ژرفای) سختی(اش) به‌درستی آفریدیم‌؟'),
(6028, 90, 5, 'آیا پندارد (که) هیچ کس هرگز بر او دست نتواند یافت‌؟'),
(6029, 90, 6, 'گوید: «مالی انباشته را تباه کردم.»'),
(6030, 90, 7, 'آیا پندارد که هیچ کس او را ندیده است‌؟'),
(6031, 90, 8, 'آیا برایش دو چشم ننهادیم،'),
(6032, 90, 9, 'و زبانی و دو لب؟'),
(6033, 90, 10, 'و هر دو راه (خیر و شرّ) را در بلندای نمایانشان بدو ننمودیم؟'),
(6034, 90, 11, 'پس هرگز نخواست از گردنه‌(ی عاقبت‌نگر) با کوشش و کاوش بالا رود.'),
(6035, 90, 12, 'و چه شناساندت (که آن) گردنه چیست‌؟'),
(6036, 90, 13, 'در بندی را (از گرفتاری و بندش) آزاد کردن،'),
(6037, 90, 14, 'یا در روز گرسنگی‌ای سخت، طعامی دادن،'),
(6038, 90, 15, 'یتیمی خویشاوند را،'),
(6039, 90, 16, 'یا بینوایی خاک‌نشین را.'),
(6040, 90, 17, 'سپس از (زمره‌ی) کسانی بوده که گرویده‌اند و یکدیگر را به شکیبایی و مرحمت سفارش کرده‌اند.'),
(6041, 90, 18, 'اینانند راستان و برکت‌یافتگان.'),
(6042, 90, 19, 'و کسانی که به انکار (و انگار) نشانه‌های ما پرداخته‌اند، آنانند (ناخجستگان:) شوم‌زدگان‌.'),
(6043, 90, 20, 'بر (سر و سامان)شان آتشی سرپوشیده - هم‌چون طبقه‌ای - احاطه دارد.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سوگند به خورشید و میان‌روزش.'),
(6045, 91, 2, 'سوگند به ماه هنگامی که از پی‌اش می‌رود.'),
(6046, 91, 3, 'سوگند به روز هنگامی که خورشید را نمایان کند.'),
(6047, 91, 4, 'سوگند به شب هنگامی که چون پرده بر آن پوشد.'),
(6048, 91, 5, 'سوگند به آسمان و آنچه آن را سازمان و سامان داد.'),
(6049, 91, 6, 'سوگند به زمین و آنچه که آن را گشاده کرد و به حرکت درآورد.'),
(6050, 91, 7, 'سوگند به نَفْسی و آنچه سازمانش داد.'),
(6051, 91, 8, 'پس پلیدکاری و پرهیزگاریش را به او الهام کرد.'),
(6052, 91, 9, '(که) هر کس نفسش را پاک گردانید، به‌راستی رستگار کرد.'),
(6053, 91, 10, 'و هر کس آلوده‌اش ساخت، همواره زیان کرد.'),
(6054, 91, 11, 'ثمودیان با طغیانشان به تکذیب پرداختند،'),
(6055, 91, 12, 'چون شقی‌ترینشان برانگیخته شد.'),
(6056, 91, 13, 'پس فرستاده‌ی خدا به آنان گفت: «زنهار! ماده شتر خدا را و (نوبت) آب خوردنش را (حرمت نهید).»'),
(6057, 91, 14, 'پس دروغزنش خواندند و آن (ماده شتر) را پی کردند. پس پروردگارشان به (سزای) گناهشان بر (سر و سامان)‌شان هلاکتی پراضطراب فرو فرستاد. پس (دوباره شتر را زنده کرد) و سامانش داد.'),
(6058, 91, 15, 'و از پی‌آمد کار خویش، بیمی نمی‌دارد.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'سوگند به شب هنگامی که پرده افکند.'),
(6060, 92, 2, 'و سوگند به روز هنگامی که جلوه‌گری کند.'),
(6061, 92, 3, 'و سوگند به آن چه نر و ماده را آفرید.'),
(6062, 92, 4, '(که) همانا کوشش‌تان پراکنده است.'),
(6063, 92, 5, 'پس اما آن کس که (حق را) داد و (از ناحق) پروا داشت،'),
(6064, 92, 6, 'و به نیکوترین زندگی تصدیق کرد،'),
(6065, 92, 7, 'به زودی او را برای آسان‌ترین (زندگی)، راهوار و آسان کنیم.'),
(6066, 92, 8, 'و اما آن کس که بخل ورزید و خود را بی‌نیاز دید،'),
(6067, 92, 9, 'و نیکوترین (زندگی) را تکذیب کرد،'),
(6068, 92, 10, 'به زودی او را برای سخت‌ترین (زندگی‌)، راهوار و آسان کنیم.'),
(6069, 92, 11, 'و هنگامی که سقوط کرد، (دیگر) مالش به کارش نیاید.'),
(6070, 92, 12, 'همواره تنها بر (عهده‌ی) ما بی‌شک هدایت‌گری است.'),
(6071, 92, 13, 'و به‌راستی دنیا و آخرت به‌درستی از ماست.'),
(6072, 92, 14, 'پس شما را به آتشی زبانه‌دار هشدار دادم.'),
(6073, 92, 15, 'جز شقی‌ترین (مکلفان) آن را نیفروزد.'),
(6074, 92, 16, 'کسی که تکذیب کرد و رخ برتافت.'),
(6075, 92, 17, 'و زودا (که) پرهیزگارترین (مکلفان) از آن به شایستگی دور داشته خواهد شد.'),
(6076, 92, 18, 'کسی که مال خود را می‌دهد، در حالی که خود را پاک می‌کند.'),
(6077, 92, 19, 'و برای احدی نزد او نعمتی دارای پاداش نیست‌.'),
(6078, 92, 20, 'جز خواستن رضای پروردگارش که بسی برتر است (منظوری ندارد).'),
(6079, 92, 21, 'و همانا در آینده‌ای دور، خشنود خواهد شد.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'سوگند به روشنایی میان روز؛'),
(6081, 93, 2, 'و سوگند به شب چون به نیمه رسید؛'),
(6082, 93, 3, '(که) پروردگارت تو را وانگذاشته و دشمن نداشته است.'),
(6083, 93, 4, 'و همانا (زندگی) آخرین برای تو از (زندگی) نخستین بهتر است.'),
(6084, 93, 5, 'و در آینده‌ای دور، پروردگارت همواره تو را (رحمت بی‌زحمت رسالتی) اعطا خواهد کرد، پس خوشنود گردی.'),
(6085, 93, 6, 'مگر نه (اینکه) تو را یتیم یافت، پس پناه داد؟'),
(6086, 93, 7, 'و تو را سرگشته‌ای یافت، پس هدایت کرد؟'),
(6087, 93, 8, 'و تو را عیال‌وار یافت، پس بی‌نیاز گردانید؟'),
(6088, 93, 9, 'پس اما یتیم را خوار و زار مکن.'),
(6089, 93, 10, 'و اما سائل را پس (هرگز) بانگ مزن.'),
(6090, 93, 11, 'و اما به وسیله‌ی نعمت پروردگارت، پس (آن را) بازگو کن.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'آیا سینه‌ات را برایت نگشادیم‌؟'),
(6092, 94, 2, 'و بار گرانت را از دوشت بر نداشتیم‌؟'),
(6093, 94, 3, '(باری گران) که پشتت را شکسته.'),
(6094, 94, 4, 'و نامت را برایت بلند نگردانیدیم‌؟'),
(6095, 94, 5, 'پس به‌راستی با دشواری، آسانی است.'),
(6096, 94, 6, 'به‌درستی با دشواری، آسانی است.'),
(6097, 94, 7, 'پس چون (از کار رسالتت) فراغت یافتی، (برای خود جایگزینی) نصب کن‌.'),
(6098, 94, 8, 'و با اشتیاق، سوی پروردگارت روی آر.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'سوگند به انجیر و زیتون.'),
(6100, 95, 2, 'و سوگند به طور سینا.'),
(6101, 95, 3, 'و سوگند به این شهر [:مکه‌ی مکرمه] پر از امن.'),
(6102, 95, 4, '(که) به‌راستی انسان را بی‌گمان در نیکوترین سازمان آفریدیم.'),
(6103, 95, 5, 'سپس او را به پَست‌ترینِ پَست‌ها بازگردانیدیم.'),
(6104, 95, 6, 'مگر کسانی را که ایمان آورده و کارهای شایسته(ی ایمان) کردند، که برایشان پاداشی بی‌منّت و بی‌پایان است.'),
(6105, 95, 7, 'پس چه چیز تو را پس از این به تکذیب دین وادار (و بدبین) می‌کند.'),
(6106, 95, 8, 'آیا خدا حاکمترین داوران نیست‌؟');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'بخوان به نام پروردگارت که آفرید.'),
(6108, 96, 2, 'انسان را از علق [:میلیون‌ها آویزه‌ی منی] آفرید.'),
(6109, 96, 3, 'بخوان و پروردگار تو کریم‌ترین است.'),
(6110, 96, 4, 'کسی که با قلم آموزش داد.'),
(6111, 96, 5, 'آنچه را که انسان نمی‌دانست به او آموزش داد.'),
(6112, 96, 6, 'چنان نیست (که انسان سرنهد). همواره انسان بی‌گمان سرکشی می‌کند،'),
(6113, 96, 7, 'که خود را بی‌نیاز دید.'),
(6114, 96, 8, 'بی‌امان بازگشت(شان) سوی پروردگار تو است.'),
(6115, 96, 9, 'آیا دیدی آن کس را که باز می‌داشت‌؛'),
(6116, 96, 10, 'بنده‌ای را آن‌گاه که نماز می‌گزارد؟'),
(6117, 96, 11, 'آیا دیدی اگر آن بنده بر هدایت، راهوار و استوار بوده،'),
(6118, 96, 12, 'یا (دیگران را هم) به پرهیزگاری واداشته؟'),
(6119, 96, 13, 'آیا دیدی اگر (این) بازداشته شده به تکذیب پردازد و روی برگرداند (چه کیفری در پیش دارد)؟'),
(6120, 96, 14, 'مگر ندانست که خدا همواره می‌بیند؟'),
(6121, 96, 15, 'زنهار! اگر به‌راستی باز نایستد، پیشانیش را بی‌امان سخت برگیریم؛'),
(6122, 96, 16, '(همان) پیشانی دروغزنِ خطاکار را.'),
(6123, 96, 17, 'پس باید گروه ویژه‌ی خود را بخواند.'),
(6124, 96, 18, 'به زودی آتش‌بانان را فراخوانیم.'),
(6125, 96, 19, 'زنهار! فرمانش مبر و سجده کن و برای قرب الهی کاوش و پویش نمای.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'ما بی‌گمان این (قرآن) را در شب قدر نازل کردیم.'),
(6127, 97, 2, 'و چه فهمانیدت (که) شب قدر چیست‌؟'),
(6128, 97, 3, 'شب قدر از هزار ماه بهتر است.'),
(6129, 97, 4, 'در آن (شب) فرشتگان، با روح ]: سرپرست فرشتگان[ به فرمان پروردگارشان، از هر فرمانی، چیزی و کاری (که مقرر شده) فرود آیند.'),
(6130, 97, 5, 'آن (شب) تا درخشش شکاف صبحگاهان، صلح و سلام است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'کافران -از اهل کتاب و مشرکان‌- (از انحراف‌شان) دست‌بردار نبودند تا ایشان را حجتی آشکار آید.'),
(6132, 98, 2, 'فرستاده‌ای از جانب خدا که بر آنان صحیفه‌هایی بسی پاک شده‌ی [:وحیانی] را تلاوت کند.'),
(6133, 98, 3, 'در آنها نوشته‌هایی استوار، پای‌برجا و پر بهاست.'),
(6134, 98, 4, 'و اهل کتاب دستخوش پراکندگی نشدند، مگر پس از آنکه ایشان را (برهان) آشکار آمد.'),
(6135, 98, 5, 'و فرمان نیافتند جز اینکه خدا را بپرستند، حال آنکه دین (خود) را برای او خالص‌کننده‌اند (و) از غیر خدا رویگرداننده‌اند و نماز را بر پا بدارند و زکات را بدهند. و دین استوار، پربها و پایدار همین است.'),
(6136, 98, 6, 'بی‌امان کسانی از اهل کتاب که کفر ورزیده‌اند -و (نیز) مشرکان - در آتش دوزخند، حال آنکه در آن ماندگارند؛ اینانند، (هم)ایشان بدترین آفریدگان.'),
(6137, 98, 7, 'بی‌گمان کسانی که ایمان آورده و کارهای شایسته‌ی (ایمان) کردند، اینان، (هم)ایشان بهترین آفریدگانند.'),
(6138, 98, 8, 'پاداششان نزد پروردگارشان باغ‌هایی (با درختان) سردرهم جاودان است، که از زیر (درختان)شان، نهرها روان است. جاودانه و بی‌پایان در آن همی می‌مانند. خدا از آنان خشنود است و (آنان نیز) از او خشنودند. این (پاداش) برای کسی است که دلباخته‌ی پروردگارش بوده با بزرگ‌داشتش (از او) بهراسد.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'هنگامی که زمین به لرزش ویژه‌اش لرزانیده شود،'),
(6140, 99, 2, 'و زمین بارهای سنگین خود را (از درونش) برون افکند،'),
(6141, 99, 3, 'و انسان گوید: «زمین را چه شده است‌؟»'),
(6142, 99, 4, 'در این هنگام (و هنگامه) زمین خبرهای خود را بازگو کند.'),
(6143, 99, 5, 'بدان سبب که پروردگارت به‌راستی برایش وحی کرده است.'),
(6144, 99, 6, 'در آن هنگام (و هنگامه)، مردمان از گورهاشان -به حال پراکندگی- صادر (و بیرون رانده) شوند، تا کارهاشان به آنان نشان داده شود.'),
(6145, 99, 7, 'پس هر کس هم‌وزن ذرّه‌ای نیکی کند، همان را خواهد دید.'),
(6146, 99, 8, 'و هر کس هم‌وزن ذرهّ‌ای بدی کند همان را خواهد دید.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'سوگند به دوندگان- نفس‌زنان- با صدایی بلند.'),
(6148, 100, 2, 'پس (سوگند به) به افروزندگان آتش در حال جهش.'),
(6149, 100, 3, 'پس (سوگند به) هجوم‌کنندگان ناگهان (در) صبحگاهان.'),
(6150, 100, 4, 'پس با آن (یورش)، گَردی برانگیختند.'),
(6151, 100, 5, 'پس بدان (هجوم)، در دل گروهی درآمدند.'),
(6152, 100, 6, '(که) انسان بی‌امان برای پروردگارش سخت ناسپاس است.'),
(6153, 100, 7, 'و او خود همواره بر این (ناسپاسی) همی گواه است.'),
(6154, 100, 8, 'و بی‌چون او سخت شیفته‌ی مال است.'),
(6155, 100, 9, 'پس مگر نمی‌داند هنگامی که آنچه در گورهاست برانگیخته و بیرون ریخته گردد،'),
(6156, 100, 10, 'و آنچه در سینه‌هاست به‌خوبی برون آورده شود؟'),
(6157, 100, 11, 'در چنان روزی بی‌گمان پروردگارشان به (حال) ایشان به‌راستی آگاه است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'کوبنده!'),
(6159, 101, 2, 'چیست کوبنده‌؟'),
(6160, 101, 3, 'و چه فهمانیدت (که) کوبنده چیست‌؟'),
(6161, 101, 4, 'روزی که مردم همچون پروانه پراکنده‌اند،'),
(6162, 101, 5, 'و کوه‌ها مانند پشمِ زده‌شده‌اند.'),
(6163, 101, 6, 'پس اما هر کس میزان‌هایش سنگین بود،'),
(6164, 101, 7, 'هم(او) در زندگی‌ای خوشایند است.'),
(6165, 101, 8, 'و اما هر که میزان‌هایش سبک بود،'),
(6166, 101, 9, 'محور و مقصدش پرتگاهی آتشین است.'),
(6167, 101, 10, 'و چه فهمانیدت (آن) پرتگاه چیست‌؟'),
(6168, 101, 11, 'آتشی است سوزنده و گدازنده.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'تفاخر به افزون‌طلبی، شما را (به خود واداشته و) از خدا باز داشته.'),
(6170, 102, 2, 'تا گذارتان به گورستان‌ها رسید.'),
(6171, 102, 3, 'هرگز (نه چنان است)! در آینده [:برزخ] خواهید دانست.'),
(6172, 102, 4, 'سپس هرگز (نه چنان است)! در آینده [:رستاخیز] خواهید دانست.'),
(6173, 102, 5, 'هرگز چنین نیست، اگر علم‌الیقین دارید!'),
(6174, 102, 6, 'بی‌گمان دوزخ را بی‌امان می‌بینید.'),
(6175, 102, 7, 'سپس آن را بی‌چون به عین‌‌الیقین همواره خواهید دید.'),
(6176, 102, 8, 'سپس به‌راستی در آن هنگام (و هنگامه) از نعمت (پروردگارتان) بی‌امان پرسیده خواهید شد.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'سوگند به عصر [:فشار و زمان فشار زیان‌بار یا سودمند].'),
(6178, 103, 2, '(که) انسان بی‌گمان در (ژرفای) زیان است.'),
(6179, 103, 3, 'مگر کسانی که ایمان آورده و کارهای شایسته(ی ایمان) کرده و همدیگر را به حق سفارش کردند و یکدیگر را به شکیبایی سفارش نمودند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'وای بر هر کوشای در غیبت‌گویی (و) عیب‌جویی!'),
(6181, 104, 2, 'آن کس که مالی گرد آوَرْد و بر شمردش‌.'),
(6182, 104, 3, 'پندارد که مالش او را ماندگار کرده.'),
(6183, 104, 4, 'هرگز (نه چنان است)! همواره در آتش خردکننده همی فروافکنده خواهد شد.'),
(6184, 104, 5, 'و چه فهمانیدت (که) آن آتش خردکننده چیست‌؟'),
(6185, 104, 6, 'آتش افروخته‌ی خدایی است.'),
(6186, 104, 7, '(آتشی‌) که بر دل‌های آتش‌وار فرو افروزد.'),
(6187, 104, 8, '(آتشی) که همواره بر (سروسامان)شان حلقه‌وار فراگیر است.'),
(6188, 104, 9, 'در ستونی بس کشیده شده.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'آیا ندیدی پروردگارت با پیل‌بانان چه کرد؟'),
(6190, 105, 2, 'آیا نیرنگشان را در (ژرفای‌) گمراه کردن ننهاد؟'),
(6191, 105, 3, 'و بر سروسامانشان، پرندگانی صف‌اندرصف فرستاد.'),
(6192, 105, 4, 'حال آنکه بر آنان سنگ‌(هایی) از سنگ گل می‌افکنند.'),
(6193, 105, 5, 'پس (خدا) آنان را مانند علفی جویده‌شده گردانید.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'برای الفت دادن به قریشیان.'),
(6195, 106, 2, 'الفت دادنشان به هنگام کوچ زمستان و تابستان‌.'),
(6196, 106, 3, 'پس باید خدای این خانه را بپرستند.'),
(6197, 106, 4, 'کسی که در گرسنگی غذایشان داد، و از ترس (دشمن) آسوده‌خاطرشان کرد.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'آیا کسی را دیدی که با دین (همین را) تکذیب می‌کند؟'),
(6199, 107, 2, 'پس این همان کس است که یتیم را به سختی می‌راند.'),
(6200, 107, 3, 'و بر خوراک بینوا ترغیب نمی‌کند.'),
(6201, 107, 4, 'پس وای بر نمازگزاران!'),
(6202, 107, 5, 'کسانی که از (انجام به وقت و درست) نمازشان سهل‌انگارند.'),
(6203, 107, 6, 'کسانی که ایشان ریا می‌کنند.'),
(6204, 107, 7, 'و از چیزهایی ناچیز (نیازمندان را) باز می‌دارند.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'ما همواره به تو تمامی (خیرات) بسیار پربار را بخشودیم‌.'),
(6206, 108, 2, 'پس برای پروردگارت نمازگزار و گلو (یت را برایش) فرو نه.'),
(6207, 108, 3, 'همانا دشمنت خود بی‌تبار است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'بگو: «هان ای کافران!»'),
(6209, 109, 2, '«(من) نمی‌پرستم، آنچه را (شما) می‌پرستید.»'),
(6210, 109, 3, '«و نه شما پرستنده‌اید، آنچه را (من) می‌پرستم.»'),
(6211, 109, 4, '«و من پرستنده نیستم، آن‌گونه که (شما) می‌پرستید.»'),
(6212, 109, 5, '«و نه شما پرستنده‌اید آن‌گونه که من می‌پرستم‌.»'),
(6213, 109, 6, '«دین شما برای خودتان و دین من (هم) برای خودم‌.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'هنگامی که یاری خدا و پیروزی(اش) فرا رسد،'),
(6215, 110, 2, 'و ببینی مردم دسته دسته در دین خدا در آیند،'),
(6216, 110, 3, 'پس با ستایش پروردگارت نیایشگر باش و از او پوشش بخواه، (که) وی همواره برگشت‌کننده و پذیرای برگشتگان (سرگشته) بوده است.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'بریده است دو دست ابولهب و (خودش هم) بریده است.'),
(6218, 111, 2, 'داراییش و دستاوردش سودش نداد.'),
(6219, 111, 3, 'به زودی آتشی زبانه‌دار (پس از مرگش) بیفروزد.'),
(6220, 111, 4, 'و زنش، بس کشنده‌ی هیزم است؛'),
(6221, 111, 5, 'بر گردنش طنابی از لیف ]: گردن بندطلایی[ خرماست.');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'بگو: «او خدای یکتا و یگانه است.»'),
(6223, 112, 2, '«خدا، (همان) پروردگار بی‌نیاز مطلق است.»'),
(6224, 112, 3, '«(هرگز) نزاد و زاده نشد.»'),
(6225, 112, 4, '«و برای او هیچ‌گاه همتا و همپایی نبوده است.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'بگو: «پناه می‌برم به پروردگار گشایشگر؛»'),
(6227, 113, 2, '«از شرّ آنچه آفریده است؛»'),
(6228, 113, 3, '«و از شرّ هر شروری، هنگامی که شبانه سوراخ (و پرده‌دری) کند؛»'),
(6229, 113, 4, '«و از شر فوت و فن‌کنندگان در گره‌ها ؛»'),
(6230, 113, 5, '«و از شرّ هر حسودی هنگامی که حسد ورزد.»');
INSERT INTO `fa_sadeqi` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'بگو: «پناه می‌برم به پروردگار مردم؛»'),
(6232, 114, 2, '«(به) مالک و حاکم (بر) مردم؛»'),
(6233, 114, 3, '«(به) معبود مردم.»'),
(6234, 114, 4, '«از شرّ وسوسه‌گر بس فراری و پنهان‌گر؛»'),
(6235, 114, 5, '«آن که در درون سینه‌های مردم وسوسه می‌کند؛»'),
(6236, 114, 6, '«(خواه) از جنیان و (خواه) انسان!»');
