INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'به نام خداوندِ گسترده مهرِ مهربان'),
(2, 1, 2, 'ستایش از آنِ خداست که پروردگار جهانیان است،'),
(3, 1, 3, 'همو که گسترده مهر و مهربان است.'),
(4, 1, 4, 'خداوندگار روزِ پاداش و جزاست.'),
(5, 1, 5, 'بار الـها، تنها تو را می‌پرستیم و در آن، تنها از تو یاری می‌جوییم.'),
(6, 1, 6, 'ما را به راه راست هدایت فرما.'),
(7, 1, 7, 'راه کسانی که به آنان نعمت دادی، همانان که نه به بیراهه رفته‌اند و به خشم تو گرفتار شده‌اند و نه گم گشتگانند که راه درست نشناخته‌اند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الف. لام. میم.'),
(9, 2, 2, 'این قرآن، همان کتاب [کاملی] است که در آسمانی بودنش هیچ تردیدی نیست و راهنمای تقواپیشگان است،'),
(10, 2, 3, 'همانان که به خدا ـ که از دیدگانشان نهان است ـ ایمان دارند و نماز را برپا می‌دارند و در راه تقویت دین و ایمان از آنچه روزی آنان کرده‌ایم انفاق می‌کنند.'),
(11, 2, 4, 'و آنان که به این قرآن که بر تو فرو فرستاده شده و به کتاب‌های آسمانی دیگر که پیش از تو نازل گردیده است ایمان و به جهان آخرت یقین دارند (آن را باور داشته و از یاد نمی‌برند).'),
(12, 2, 5, 'اینان بر هدایتی قرار دارند که از جانب پروردگارشان به آنها داده شده است، و هم اینانند که نیکبخت‌اند.'),
(13, 2, 6, 'ای پیامبر، این کافران برایشان یکسان است، چه هشدارشان دهی یا هشدارشان ندهی، ایمان نمی‌آورند.'),
(14, 2, 7, 'خدا بر دل‌ها و گوش هایشان مهر نهاده است، زیرا بر دیدگانشان پرده‌ای بوده است پس نه خود می‌توانند حق را درک کنند و نه با هدایت دیگران به راه می‌آیند، و برای آنان عذابی بزرگ خواهد بود.'),
(15, 2, 8, 'و از مردم کسانی اند که می‌گویند: به خدا و روز رستاخیز ایمان آورده‌ایم، در حالی که مؤمن نیستند.'),
(16, 2, 9, 'با خدا و کسانی که ایمان آورده‌اند سخت نیرنگ می‌بازند، ولی جز خود را فریب نمی‌دهند، و این را درنمی یابند.'),
(17, 2, 10, 'در دل هایشان بیماری شک و تردید نهفته بود، از این روی به نفاق خو گرفتند، و خدا به کیفر آن بر بیماریشان افزود. و برای آنان به سبب این که دروغ می‌گفتند، عذابی دردناک خواهد بود.'),
(18, 2, 11, 'و چون به آنان گفته شود: در زمین فساد نکنید، گویند: جز این نیست که ما اصلاحگریم.'),
(19, 2, 12, 'بدانید که آنان فسادگرند، ولی خود درنمی یابند.'),
(20, 2, 13, 'و چون به آنان گفته شود: شما نیز همان گونه که مردم ایمان آورده‌اند ایمان بیاورید، گویند: آیا چنان که بی خردان ایمان آورده‌اند ایمان بیاوریم؟ بدانید که آنان خود بی خردند، ولی نمی‌دانند.'),
(21, 2, 14, 'و چون با کسانی که ایمان آورده‌اند برخورد کنند، گویند: ایمان آورده‌ایم، و چون نزد سرانِ شرور خود روند و با آنان خلوت کنند، گویند: ما با شماییم، جز این نیست که ما مؤمنان را به مسخره گرفته‌ایم.'),
(22, 2, 15, 'خداست که آنان را به مسخره می‌گیرد و آنان را مدد می‌دهد که همواره در طغیانشان سرگردان بمانند.'),
(23, 2, 16, 'اینان کسانی اند که گمراهی را به بهای هدایت خریده‌اند، از این رو داد و ستدشان سودی نکرده و رهیافته نبوده‌اند.'),
(24, 2, 17, 'داستان منافقان مانند داستان کسانی است که آتشی برافروختند، و چون پیرامونشان را روشن ساخت، خدا نورشان را از میان برد، و آنان را در تاریکی‌هایی که چیزی نمی‌دیدند رها کرد.'),
(25, 2, 18, 'آنان کر و لال و کورند، نه توانِ شنیدن حق را دارند و نه بر باز گفتن آن توانا هستند و نه می‌توانند نشانه‌های حق را بنگرند، از این رو از گمراهی خود باز نخواهند گشت.'),
(26, 2, 19, 'یا داستانشان مانند داستان کسانی است که در رگباری تند که از آسمان فرو می‌ریزد و در آن تاریکی‌ها و رعد و برقی است، گرفتار شده‌اند. در اثر خروش صاعقه‌ها از ترس مرگ، انگشتان خود را در گوش هایشان می‌نهند، و خدا بر کافران احاطه دارد.'),
(27, 2, 20, 'نزدیک است درخشش برق دیدگانشان را برُباید. هرگاه راه را برای آنان روشن کند، در پرتو آن پیش می‌روند، و چون راه را بر آنان تاریک کند می‌ایستند. و اگر خدا می‌خواست، از آغاز، شنوایی را از گوش‌ها و بینایی را از چشم هایشان می‌گرفت، که خدا بر هر کاری تواناست.'),
(28, 2, 21, 'ای مردم، پروردگارتان را بپرستید، همان کسی که شما و کسانی را که پیش از شما بوده‌اند آفریده است، باشد که تقواپیشه گردید و از گرایش به کفر و نفاق بپرهیزید.'),
(29, 2, 22, 'همان کسی که زمین را برای شما بستری [گسترده] و آسمان را بنایی [برافراشته] قرار داد، و از آسمان آبی فرود آورد و به وسیله آن برای شما از میوه‌ها و محصولات رزقی پدید آورد. پس شما که از دانشی برخوردارید، برای خدا همتایانی قرار ندهید.'),
(30, 2, 23, 'و اگر در آسمانی بودن این قرآن که بر بنده خود محمّد فرو فرستاده‌ایم تردید دارید و در این ادّعا راست می‌گویید، سوره‌ای مانند آن بیاورید و گواهانتان را ـ جز خدا ـ فرا خوانید تا بر همسانی آنچه در ردیف قرآن برمی شمرید گواهی دهند.'),
(31, 2, 24, 'پس اگر چنین نکردید ـ که هرگز نمی‌کنید و نتوانید کرد ـ از آن آتشی که هیزمش انسان‌ها و سنگ‌های مورد پرستش شمایند، و برای کافران آماده شده است پروا کنید.'),
(32, 2, 25, 'و به کسانی که ایمان آورده و کارهای شایسته کرده‌اند نوید ده که برای آنان در آخرت بوستان‌هایی انبوه از درختان است که از زیر آنها جوی‌ها روان است. هرگاه میوه‌ای از آن باغ‌ها روزیِ آنان شود گویند: این همان چیزی است که پیش از این در دنیا روزی ما شده بود، و آن را در حالی که همانند است برایشان می‌آورند، و در آن جا همسرانی پاک و پیراسته خواهند داشت و در آن جا جاودانه خواهند بود.'),
(33, 2, 26, 'خداوند از این که به پشه‌ای یا فراتر از آن مثَل زند پروا ندارد. پس کسانی که ایمان آورده اند می‌دانند که آن حق است و از جانب پروردگارشان است، و امّا کسانی که کافر شده اند می‌گویند: خدا از این مثل چه هدفی داشته است؟ خدا بسیاری را بدان گمراه و بسیاری را بدان هدایت می‌کند، ولی جز فاسقان را به وسیله آن گمراه نمی‌کند.'),
(34, 2, 27, 'فاسقان کسانی اند که عهدشان را که با خدا پس از آن که بر آن پیمانی استوار بسته اند می‌شکنند و آنچه را خداوند به پیوستن آن فرمان داده است می‌گسلند، و با انجام کارهای ناشایسته، در زمین تباهی به بار می‌آورند. آنانند زیانکاران.'),
(35, 2, 28, 'چگونه [ربوبیّت] خدا را انکار می‌کنید؟ در حالی که شما مردگانی بودید که خدا به شما حیات بخشیده است، سپس شما را می‌میراند و بار دیگر زنده می‌کند، آن گاه به سوی او باز گردانده می‌شوید.'),
(36, 2, 29, 'اوست آن که همه آنچه را در زمین است برای شما آفرید، وانگهی به آفرینش آسمان پرداخت و آنها را برای شما به صورت هفت آسمان سامان داد، و او به هر چیزی داناست.'),
(37, 2, 30, 'و آن گاه بر آدمیان نعمت ارزانی شد که پروردگارت به فرشتگان گفت: من در زمین جانشینی قرار خواهم داد. فرشتگان چون می‌دانستند که موجودی که در زمین زندگی می‌کند روی به تبهکاری می‌آورد، گفتند: آیا در زمین کسانی را می‌گماری که در آن فساد می‌کنند و خون‌ها می‌ریزند، در حالی که ما همراه با ستایش تو، تو را تسبیح می‌گوییم و تقدیس می‌کنیم؟ گفت: من چیزهایی می‌دانم که شما نمی‌دانید.'),
(38, 2, 31, 'و نام همگان را به آدم آموخت، سپس آنان را بر فرشتگان عرضه کرد و گفت: اگر راست می‌گویید که شما شایسته مقام خلافتید، مرا از نام آنان با خبر کنید.'),
(39, 2, 32, 'فرشتگان گفتند: پروردگارا، تو از هر عیب و کاستی پیراسته‌ای. ما را دانشی جز آنچه تو به ما آموخته‌ای نیست. بی تردید تویی دانا و حکیم.'),
(40, 2, 33, 'خدا گفت: ای آدم، از نام آنان به فرشتگان خبر ده. هنگامی که آدم اسامی آنها را به فرشتگان خبر داد، خدا به فرشتگان گفت: آیا به شما نگفتم که من نهان آسمان‌ها و زمین را می‌دانم و آنچه را آشکار می‌کنید و آنچه را کتمان کرده اید می‌دانم؟'),
(41, 2, 34, 'و نیز آن گاه بر آدمیان نعمت ارزانی شد که به فرشتگان گفتیم: برای آدم سجده کنید. پس همگان سجده کردند، مگر ابلیس که خود داری کرد و تکبّر ورزید و از کافران بود.'),
(42, 2, 35, 'و گفتیم: ای آدم، تو و همسرت در این بهشت سکونت کنید و از نعمت‌های آن، از هر کجا خواستید، به خوشی بخورید، ولی به این درخت نزدیک نشوید و از آن نخورید که از کسانی خواهید بود که بر خویشتن ستم کرده و رنج و سختی را بر خود روا دانسته‌اند.'),
(43, 2, 36, 'اما شیطان آن دو را با ترغیبشان به آن درخت به لغزش انداخت و از نعمت‌هایی که در آن بودند بیرونشان کرد، و ما به آنان (آدم و حوّا و ابلیس) گفتیم: فرود آیید در حالی که دشمن یکدیگر خواهید بود، و در زمین تا چندی قرارگاه و بهره‌ای خواهید داشت.'),
(44, 2, 37, 'سپس آدم از پروردگارش کلماتی را دریافت کرد و به وسیله آنها توبه نمود. خدا نیز به سوی او بازگشت و توبه اش را پذیرفت، چرا که او بسیار توبه پذیر و مهربان است.'),
(45, 2, 38, 'به آنان گفتیم: همگی از بهشت فرود آیید، پس اگر از جانب من رهنمودی برای شما آمد ـ که خواهد آمد ـ کسانی که رهنمود مرا پیروی کنند، نه ترسی آنان را فرا می‌گیرد و نه اندوهگین می‌شوند.'),
(46, 2, 39, 'و کسانی که کفر ورزند و آیات ما را دروغ انگارند، همدم آتش دوزخند و در آن جاودانه خواهند بود.'),
(47, 2, 40, 'ای بنی اسرائیل، نعمت مرا که به شما ارزانی داشته‌ام به خاطر داشته باشید، و به پیمانی که با من بسته‌اید وفا کنید آنگاه است که من نیز به عهدی که با شما بسته‌ام وفا خواهم کرد، و تنها از من بیم داشته باشید.'),
(48, 2, 41, 'و به این قرآن که آن را فرو فرستاده‌ام و توراتی را که با شماست تصدیق می‌کند ایمان بیاورید و از میان اهل کتاب نخستین کسانی نباشید که بدان کفر میورزند، و آیات مرا به بهای ناچیز دنیا نفروشید، و تنها از من پروا کنید.'),
(49, 2, 42, 'و حق را در چهره باطل آشکار نسازید و حق را کتمان نکنید در حالی که [آن را] می‌دانید.'),
(50, 2, 43, 'و نماز را برپا دارید و زکات بپردازید و با رکوع کنندگان رکوع کنید.'),
(51, 2, 44, 'آیا مردم را به نیکی فرمان می‌دهید و خود را فراموش می‌کنید، با این که کتاب آسمانی را تلاوت می‌کنید؟ آیا درنمی یابید؟'),
(52, 2, 45, 'و از صبر و نماز یاری جویید، و به راستی نماز، کاری بس سنگین و گران است، مگر بر فروتنان،'),
(53, 2, 46, 'همانان که گمان می‌برند که پروردگارشان را دیدار خواهند کرد و به سوی او باز خواهند گشت.'),
(54, 2, 47, 'ای بنی اسرائیل، نعمت مرا که بر شما ارزانی داشته‌ام به خاطر داشته باشید، و یاد کنید که من شما را با دادنِ نعمت‌های فراوان بر سایر امّت‌ها برتری بخشیدم.'),
(55, 2, 48, 'و بترسید از روزی که هیچ کس چیزی از عذاب را از دیگری کفایت نمی‌کند و شفاعتی از او پذیرفته نمی‌گردد و در برابر عذاب، فدیه‌ای معادل آن از او گرفته نمی‌شود و آنان یاری نخواهند شد.'),
(56, 2, 49, 'و به یاد آورید هنگامی را که شما را از فرعونیان رهایی بخشیدیم. آنان پیوسته عذاب ناگوار به شما می‌چشاندند. پسرانتان را سر می‌بریدند تا نسل شما را از بین ببرند، و زنانتان را برای کنیزی و خدمتگذاری زنده می‌گذاشتند، و در این ماجرا از جانب پروردگارتان آزمونی بزرگ بود.'),
(57, 2, 50, 'و هنگامی را که دریا را برای شما شکافتیم و شما را از چنگ دشمنان رهایی بخشیدیم و فرعونیان را در حالی که نظاره می‌کردید در دریا غرق کردیم.'),
(58, 2, 51, 'و هنگامی را که با پیامبرتان موسی چهل شب وعده گذاشتیم که در آن به عبادت به سر بَرَد، آن گاه شما پس از رفتنش به وعده گاه، گوساله را به خدایی گرفتید، در حالی که ستمکار بودید.'),
(59, 2, 52, 'سپس شما را بعد از آن بخشیدیم، باشد که سپاسگزاری کنید.'),
(60, 2, 53, 'و هنگامی را که به موسی کتاب آسمانی تورات و جداکننده حق از باطل را عطا کردیم، باشد که هدایت شوید.'),
(61, 2, 54, 'و هنگامی را که موسی به قومش گفت: ای قوم من، بی تردید شما با برگرفتن گوساله به پرستش، به خود ستم کردید، پس به درگاه آفریدگارتان توبه کنید و بدین منظور گوساله پرستان شما یکدیگر را بکشند، که این کار نزد آفریدگارتان برای شما بهتر است. پس از آن که برخی مجرمان به قتل رسیدند، خدا توبه گنهکاران شما را پذیرفت، که او بسیار توبه پذیر و مهربان است.'),
(62, 2, 55, 'و هنگامی را که گفتید: ای موسی، هرگز سخن تو را مبنی بر این که خدا تورات را بر تو فرو فرستاده است باور نمی‌کنیم تا این که خدا را آشکارا به چشم خود ببینیم و تأیید تورات را از او بشنویم. پس به سزای این سخنتان صاعقه شما را فرو گرفت در حالی که نظاره می‌کردید.'),
(63, 2, 56, 'سپس شما را پس از مرگتان برانگیختیم، باشد که سپاسگزاری کنید.'),
(64, 2, 57, 'و ابرها را ـ آن گاه که در بیابانی سوزان رهسپار بودید ـ بر شما سایه گستر کردیم، و (منّ) و (سلوی) بر شما فرو فرستادیم، و گفتیم: از روزی‌هایی که به شما داده‌ایم و همه خوب و پاکیزه‌اند بخورید. و آنان [با سرکشی ها] بر ما ستم نکردند، بلکه بر خود ستم روا می‌داشتند.'),
(65, 2, 58, 'و یاد کنید هنگامی را که گفتیم: به این شهر درآیید و از نعمت‌های آن، هر جا که خواهید، به خوشی تناول کنید و سجده کنان [= با فروتنی] وارد دروازه آن شوید و بگویید: پروردگارا، گناهانمان را فرو ریز، ما هم گناهانتان را خواهیم بخشود و بر پاداش نیکوکاران خواهیم افزود.'),
(66, 2, 59, 'ولی کسانی که ستم کرده بودند، آن را به سخنی دیگر تغییر دادند به سخنی جز آنچه به آنان گفته شده بود، پس بر ستمکاران به سزای این که نافرمانی کردند، عذابی از آسمان فرو فرستادیم.'),
(67, 2, 60, 'و هنگامی را که موسی برای قومش آب طلبید، پس به او گفتیم: با عصایت بر این سنگ بزن. چون عصایش را بر آن زد، دوازده چشمه آب به تعداد قبایل بنی اسرائیل از آن جوشید و هر مردمی از آن قبایل آبشخور خود را شناخت. پس به آنان گفته شد: از روزیِ خدا بخورید و بیاشامید و فسادگرانه در زمین تباهی مکنید.'),
(68, 2, 61, 'و هنگامی را که گفتید: ای موسی، ما نمی‌توانیم بر یک نوع خوراکی (منّ و سلوی) صبر کنیم، برای ما از پروردگارت بخواه که از آنچه زمین می‌رویاند، از سبزی و خیار و سیر و عدس و پیازش برای ما پدید آورَد که دعای تو را اجابت می‌کند. موسی گفت: آیا می‌خواهید چیزی را که پست‌تر است جایگزین چیزی کنید که بهتر است؟! به شهری درآیید، آنچه درخواست کرده‌اید برای شما در آن جا خواهد بود، و مهرِ خواری و درماندگی بر آنان زده شد، و خود را مورد خشم خدا قرار دادند. این بدان سبب بود که به آیات خدا کفر میورزیدند و پیامبران را به ناحق می‌کشتند و این از آن روی بود که از فرمان‌های خدا سرپیچی کردند و پیش از آن از حدود الهی تجاوز می‌نمودند.'),
(69, 2, 62, 'آنان که ایمان آورده‌اند (مسلمانان) و آنان که یهودی‌اند، و نیز مسیحیان و صابئان بدانند که این عناوین کارساز نیست، بلکه کسانی که واقعاً به خدا و روز قیامت ایمان دارند و کار شایسته می‌کنند، پاداش ایمان و عمل خود را نزد پروردگارشان خواهند داشت و هیچ ترسی آنان را فرانمی گیرد و اندوهگین نمی‌شوند.'),
(70, 2, 63, 'و یاد کنید هنگامی را که از شما پیمان گرفتیم و برای این که قدرت خود را به شما نشان دهیم، آن کوه را بر فراز شما برافراشتیم و گفتیم: کتابی را که به شما داده‌ایم به جِدّ و جهد بگیرید و آنچه را که در آن است به خاطر داشته باشید، باشد که تقوا پیشه کنید.'),
(71, 2, 64, 'سپس شما بعد از آن روی برتافتید، که اگر فضل خدا و رحمت او بر شما نبود، قطعاً از زیانکاران بودید.'),
(72, 2, 65, 'و قطعاً شما کسانی از خودتان را که در روز شنبه به مقرّرات آن روز تجاوز کردند می‌شناسید [و از فرجام کارشان آگاهید]، که ما به آنان گفتیم: بوزینگانی باشید پَست و ناچیز!'),
(73, 2, 66, 'پس، آن عقوبت را برای کسانی که در آن زمان می‌زیستند و برای آیندگانشان، مایه عبرت، و برای تقواپیشگان وسیله پندی قرار دادیم.'),
(74, 2, 67, 'ای پیامبر، یاد کن زمانی را که موسی برای مشخص ساختن قاتلی از میان بنی اسرائیل، به قومش گفت: خدا به شما فرمان می‌دهد که گاوی را سر ببرید. گفتند: آیا ما را به مسخره می‌گیری؟ موسی گفت: به خدا پناه می‌برم از این که از نادانان باشم.'),
(75, 2, 68, 'گفتند: از پروردگارت بخواه تا برای ما روشن کند که آن چگونه گاوی است؟ موسی گفت: او می‌گوید: آن گاوی است نه پیر که باردار نشود و نه کم سال که نزاییده باشد، بلکه میانه این دو است، پس آنچه را که بدان فرمان یافته‌اید انجام دهید و بیش از این توضیح نخواهید.'),
(76, 2, 69, 'گفتند: از پروردگارت بخواه تا برای ما روشن کند که رنگش چگونه است؟ موسی گفت: او می‌گوید: آن گاوی است زرد پررنگ و خالص که با صفای رنگش بینندگان را شاد می‌کند.'),
(77, 2, 70, 'گفتند: از پروردگارت بخواه تا برای ما روشن کند که آن چگونه است؟ زیرا این گاو بر ما مشتبه شده است، و اگر خدا بخواهد قطعاً ما به آن راه خواهیم یافت.'),
(78, 2, 71, 'موسی گفت: او می‌گوید: آن گاوی است که نه رام است که زمین را شخم زند و نه آن که کشتزار را آبیاری کند، سالم وتندرست است و هیچ لکّه‌ای در رنگ آن نیست. گفتند: اکنون ویژگی‌های درست آن را بیان کردی. پس آن را سر بریدند و نزدیک بود این کار را نکنند.'),
(79, 2, 72, 'و آن هنگام که شخصی را کشتید و درباره قتل او با یکدیگر اختلاف کردید و هر گروهی آن را به گروه دیگر نسبت می‌داد و خدا بر آن بود که آنچه را پنهان می‌داشتید، آشکار کند.'),
(80, 2, 73, 'پس گفتیم: بخشی از آن گاو ذبح شده را بر مقتول بزنید تا زنده شود. بدین سان خداوند مردگان را زنده می‌کند و آیات خود را به شما نشان می‌دهد، باشد که بیندیشید.'),
(81, 2, 74, 'امّا چیزی نگذشت که پس از این ماجرا دل‌های شما سخت گردید اینک دلهایتان همچون سنگ یا سخت‌تر از آن است، چرا که از برخی سنگ‌ها نهرها می‌جوشد و برخی از آنها می‌شکافد و آب از آنها خارج می‌شود، و برخی از آنها از ترس خدا فرو می‌غلتد، ولی دل‌های شما به حق گرایش نمی‌یابد و ترس از خدا در آن پدیدار نمی‌شود، و خدا از آنچه می‌کنید بی خبر نیست.'),
(82, 2, 75, 'اینک ای مؤمنان، آیا به این دل بسته‌اید که یهودیانِ حق ناپذیر سخن شما را باور کنند؟ در حالی که گروهی از آنان [= عالمانشان] کلام خدا را می‌شنیدند و پس از آن که آن را به خرد دریافتند تحریف می‌کردند و خود می‌دانستند [چه می‌کنند].'),
(83, 2, 76, 'و چون یهودیان با مؤمنان برخورد کنند می‌گویند: ما باور داریم که آنچه در تورات در وصف آخرین پیامبر آمده همان صفات محمّد است، امّا هنگامی که با یکدیگر خلوت می‌کنند، بزرگانشان می‌گویند: آیا حقایقی را که خدا در تورات برای شما گشوده است به مؤمنان گزارش می‌کنید تا به وسیله آن نزد پروردگارتان بر ضدّ شما احتجاج کنند؟ چرا درنمی یابید؟'),
(84, 2, 77, 'آیا نمی‌دانند که خدا آنچه را نهان می‌دارند و آنچه را آشکار می‌کنند، می‌داند؟'),
(85, 2, 78, 'چگونه امید می‌برید که یهودیان ایمان بیاورند؟ در حالی که برخی از آنان بی سوادانی هستند که از کتاب آسمانی جز دروغ‌هایی که تحریف گرانشان بافته‌اند، نمی‌دانند. آنان [معارف کتاب آسمانی را نمی‌دانند و] تنها گمان پردازی می‌کنند.'),
(86, 2, 79, 'پس وای بر کسانی که به دست خود کتاب می‌نگارند سپس می‌گویند این از نزد خداست، تا در قبال آن بهره ناچیز دنیا را به دست آورند. پس وای بر آنان از آنچه به دست خود می‌نویسند و وای بر آنان از آنچه به دست می‌آورند.'),
(87, 2, 80, 'و گفتند: آتش دوزخ جز چند روزی به ما نمی‌رسد [هر چند گناهان بسیاری مرتکب شویم]. به آنان بگو: آیا از نزد خدا پیمانی گرفته‌اید که عذاب شما اندک باشد و خدا پیمان خود را هرگز خلاف نمی‌کند، یا سخنی را که نمی‌دانید به خدا نسبت می‌دهید؟'),
(88, 2, 81, 'نه، چنین پیمانی در کار نیست، بلکه کسانی که گناهی مرتکب شوند و اثر گناهشان همه وجود آنان را فراگیرد و جایی برای رحمت الهی در آن نباشد، آنان همدم آتشند و در آن جاودانه خواهند بود.'),
(89, 2, 82, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند آنان اهل بهشتند و برای همیشه در آن خواهند ماند.'),
(90, 2, 83, 'و یاد کن هنگامی را که از بنی اسرائیل پیمان گرفتیم که جز خدا را نپرستید و به پدر و مادر و خویشاوند و یتیمان و درماندگان نیکی کنید و با مردم ـ چه مؤمنان و چه کافران ـ با خوشزبانی سخن بگویید و به نیکی معاشرت کنید و نماز را بر پا دارید و زکات بپردازید، ولی جز اندکی از شما، همه پشت کردید و روی برتافتید.'),
(91, 2, 84, 'ای فرزندان اسرائیل، یاد کنید زمانی را که از شما پیمان گرفتیم که خون [همکیشان] خود را نریزید و یکدیگر را از دیار خود بیرون نرانید، آن گاه [بر این پیمان] اقرار کردید و خود گواه بودید.'),
(92, 2, 85, 'سپس شما اینان هستید، یکدیگر را می‌کُشید و گروهی از خودتان را از خانه هایشان بیرون می‌رانید و به گناه و تجاوز [نه از روی استحقاق، دیگران را] بر ضدّ آنان یاری می‌دهید، در حالی که اگر همانان به اسیری نزد شما آیند با پرداخت فدیه آزادشان می‌کنید، با این که بیرون راندن آنها بر شما حرام بوده است. آیا به بخشی از تورات ایمان می‌آورید و اسیران همکیش خود را با دادن فدیه آزاد می‌کنید، ولی بخشی دیگر را انکار می‌کنید و گروهی از خودتان را از دیارشان بیرون می‌رانید؟ کیفر کسانی از شما که چنین کنند چیزی جز خواری در زندگی دنیا نخواهد بود، و روز قیامت به سوی سخت‌ترین عذاب بازگردانده می‌شوند، و خدا از آنچه می‌کنید بی خبر نیست.'),
(93, 2, 86, 'اینان همان کسانی اند که زندگی دنیا را به بهای آخرت خریده‌اند، از این روی نه عذابشان کاهش می‌یابد و نه یاری می‌شوند.'),
(94, 2, 87, 'و همانا ما به موسی کتاب آسمانی دادیم، و پس از او پیامبران بسیاری را یکی پس از دیگری با راه و رسم وی فرستادیم، و به عیسی پسر مریم آن معجزات آشکار را عطا کردیم و او را با (روح القدس) تأیید نمودیم. پس چرا هرگاه پیامبرانی برای شما قوانینی آوردند که دلخواه شما نبود گردنکشی کردید و گروهی از آنان را دروغگو شمردید و گروهی را کشتید؟ و رویه شما چنین است.'),
(95, 2, 88, 'یهودیان گفتند: دل‌های ما در پرده است [و از سخنان تو ـ ای محمّد ـ چیزی نمی‌فهمیم]. نه، چنین نیست، بلکه خداوند آنان را به سزای کفرشان از رحمت خود دور ساخته است، از این رو فقط اندکی از آنان ایمان می‌آورند.'),
(96, 2, 89, 'و هنگامی که از جانب خدا کتابی (قرآن) برایشان آمد و توراتی را که با آنهاست تأیید کرد و پیش از آمدن آن هم با نوید دادن به ظهور پیامبر و هجرت او به مدینه، درپی پیروزی بر کافران عرب بودند، آری هنگامی که همان چیزی را که می‌شناختند برایشان آمد، به آن کافر شدند، پس لعنت خدا بر کافران باد.'),
(97, 2, 90, 'بد بهایی است آنچه خود را بدان فروختند که به کتابی که خدا فرو فرستاده است، کافر می‌شوند، کفرشان از آن روست که بر پیامبر حسد میورزند و رشک می‌برند که خداوند از فزون بخشی خود به هر کس از بندگانش که بخواهد وحی می‌کند، از این رو خشمی از خدا را به سزای کفرشان به قرآن، بر خشم دیگر به کیفر کفرشان به تورات بر خود جای دادند، و برای کفرپیشگان عذابی خوارکننده است.'),
(98, 2, 91, 'و چون به آنان گفته شود: به آنچه خدا فرو فرستاده است ایمان بیاورید، می‌گویند: ما به آنچه بر خودمان نازل شده است ایمان داریم. و آنچه را غیر آن است با این که حق است و راستی و درستیِ کتابی را که با آنهاست تأیید می‌کند انکار می‌کنند. ای پیامبر، به آنان بگو: اگر به آنچه گفتید ایمان دارید، چرا پیش از این آن پیامبران خدا را که از نژاد خودتان بودند می‌کشتید؟'),
(99, 2, 92, 'و به راستی موسی برای شما آن معجزات روشن را آورد، ولی شما در غیاب او آن گوساله را به پرستش برگرفتید در حالی که ستمکار بودید [= هیچ عذری بر این کار نداشتید].'),
(100, 2, 93, 'و هنگامی که از شما پیمان گرفتیم و برای نشان دادن قدرت خود به شما آن کوه را بر فرازتان برافراشتیم و گفتیم: کتابی را که به شما داده‌ایم به جد و جهد بگیرید و به آنچه در آن است گوش بسپارید. گفتند: شنیدیم و نافرمانی کردیم. و به سبب کفرشان محبّت آن گوساله در دل هایشان نفوذ داده شد. ای پیامبر، به آنان بگو: بد است آنچه ایمانتان شما را به آن فرمان می‌دهد اگر ایمان دارید.'),
(101, 2, 94, 'ای پیامبر، به یهودیان بگو: اگر نزد خدا و به حکم او نیکبختی سرای آخرت به شما اختصاص دارد و مردم دیگر از آن محرومند، چنانچه راست می‌گویید، آرزوی مرگ کنید تا هر چه زودتر از این زندگی آمیخته با ناکامی‌ها به آن سرای سعادت درآیید.'),
(102, 2, 95, 'ولی آنان در اثر گناهانی که به دست خود پیش فرستاده‌اند، هرگز آرزوی مرگ نمی‌کنند، و خدا به حال ستمکاران داناست.'),
(103, 2, 96, 'گواه این که یهودیان در بند سرای آخرت نیستند این است که آنان را آزمندترین مردم به این زندگی ناچیز دنیا می‌یابی. آنان حتی از مشرکان که سرای آخرت را باور ندارند به زندگی دنیا آزمندترند. هر یک از آنان دوست دارد که هزار سال عمر به او می‌دادند، و حقیقت این است که عمر هزار ساله یافتن هم او را از عذاب دور نمی‌کند، و خدا به آنچه انجام می‌دهند بیناست.'),
(104, 2, 97, 'یهودیان به این بهانه به قرآن ایمان نمی‌آورند که با جبرئیل که آورنده وحی است دشمنند. ای پیامبر، به آنان بگو: هر کس دشمن جبرئیل است، نباید از کتاب الهی رویگردان شود، چرا که جبرئیل به اذن خدا این کتاب را بر قلب تو نازل کرده است، کتابی که حقانیت کتاب‌های آسمانی پیش از خود، از جمله تورات راتأیید می‌کند و برای کسانی که به آن ایمان می‌آورند مایه هدایت و بشارت است.'),
(105, 2, 98, 'هر کس دشمن خدا و فرشتگان و پیامبران او و دشمن جبرئیل و میکائیل است، کافر است و بی تردید خدا دشمن کافران است.'),
(106, 2, 99, 'و همانا ما آیاتی روشن و روشنگر به سوی تو فرو فرستادیم، و جز فاسقان کسی بدانها کفر نمیورزد.'),
(107, 2, 100, 'چرا این یهودیان هرگاه [با خدا] پیمانی بستند، گروهی از ایشان آن را به دور افکندند؟ [نه تنها گروهی از آنان پیمان شکن اند] بلکه بیشترشان ایمان ندارند.'),
(108, 2, 101, 'و هنگامی که از جانب خدا پیامبری برایشان آمد که توراتی را که با آنان است تصدیق می‌کرد، گروهی از کسانی که کتاب آسمانی به آنها داده شده است [= عالمان یهود و نصاری] کتاب خدا را پشت سرشان افکندند و بشارت‌های آن را در مورد ظهور پیامبر اسلام نادیده گرفتند، چنان که گویی از آن بی خبرند.'),
(109, 2, 102, 'و یهودیان افسون‌هایی را دنبال کردند که جنیانِ شرور آنها را برایشان می‌خواندند و به آنان تعلیم می‌دادند و به دروغ آنها را بر حکومت سلیمان می‌بستند و می‌گفتند سلیمان با همین افسون‌ها بر جن و انس و دیگر موجودات سلطنت می‌کرد، در حالی که سلیمان سحر نکرد و کافر نشد، بلکه آن جنیانِ شرور کفر ورزیدند که به مردم سحر آموختند. و نیز یهودیان افسون‌هایی را دنبال کردند که بر آن دو فرشته به نام‌های هاروت و ماروت در شهر بابل الهام شد. در صورتی که آن دو فرشته به هیچ کس سحر نمی‌آموختند مگر این که به او می‌گفتند: ما فقط وسیله آزمایشیم، پس مبادا با به کار گرفتن آنچه می‌آموزی کافر شوی. ولی یهودیان از آنها افسون‌هایی می‌آموختند که به وسیله آنها میان مرد و همسرش جدایی می‌افکندند. البته جز به اذن خدا نمی‌توانستند به کسی زیان برسانند، چرا که بی اذن خدا هیچ کاری در جهان هستی صورت نمی‌گیرد. آری، یهودیان چیزهایی از هاروت و ماروت فرا می‌گرفتند که برای آنان زیانبار بود و سودی عایدشان نمی‌کرد، و آنها خوب می‌دانستند که هر کس خریدار سحر باشد (آن را بیاموزد و به کار اندازد) در آخرت نصیبی نخواهد داشت. یهودیان خود را به بد چیزی فروختند. ای کاش می‌دانستند.'),
(110, 2, 103, 'و اگر آنان ایمان می‌آوردند و از محرّمات الهی پرهیز می‌کردند، از جانب خدا به پاداشی بهتر از منافع سحر دست می‌یافتند. ای کاش این حقیقت را درمی یافتند.'),
(111, 2, 104, 'ای کسانی که ایمان آورده‌اید، هنگامی که از پیامبر می‌خواهید در سخن گفتن درنگ بیشتری کند تا بتوانید سخنش را به روشنی دریابید، نگویید (راعنا) بلکه بگوید (انظرنا)، و این فرمان را به گوش بسپارید تا از کافران نشوید، که برای کافران عذابی دردناک خواهد بود.'),
(112, 2, 105, 'کافران اهل کتاب (یهود و نصارا) و مشرکان دوست ندارند که از جانب پروردگارتان هیچ خیری بر شما مؤمنان فرو فرستاده شود، در حالی که خداوند رحمتش را ویژه هر که خواهد می‌گرداند، و خداوند دارای فضلی بزرگ است.'),
(113, 2, 106, 'هر نشانه‌ای از نشانه‌های تکوینی یا تشریعی خود را نسخ کنیم یا آن را از خاطره‌ها بزداییم، بهتر از آن یا همانند آن را می‌آوریم. مگر ندانسته‌ای که خداوند بر هر کاری تواناست؟'),
(114, 2, 107, 'مگر ندانسته‌ای که فرمانروایی آسمان‌ها و زمین فقط از آنِ اوست؟ از این رو هیچ کس و هیچ چیز نمی‌تواند او را از نسخ آیات و احکام باز دارد و مگر ندانسته‌ای که برای شما در کارتان هیچ سرپرست و یاوری جز خدا نیست؟ پس اگر خواهد در امور مربوط به شما تغییر و دگرگونی پدید می‌آورد.'),
(115, 2, 108, '[باور داشتن قدرت بی نهایت خدا، برای زدودن هر شبهه‌ای در مورد نسخ احکام و شرایع کافی است،] ولی شما می‌خواهید از پیامبرتان درخواست‌هایی نابجا کنید، همان گونه که پیش از این از موسی کردند [و به کفر روی آوردند] و هر کس کفر را جایگزین ایمان کند قطعاً راه درست را گم کرده است.'),
(116, 2, 109, 'بسیاری از اهل کتاب (یهودیان) با این که حق برایشان آشکار شده است، به سبب حسدی که از درونشان برمی خاست دوست می‌داشتند شما را پس از ایمانتان به کفر بازگردانند. اکنون وقت آن نیست که با آنان بستیزید، پس چشم پوشی کنید و از آنان درگذرید، تا خدا فرمان خود را به میان آورد، که خداوند بر هر کاری تواناست.'),
(117, 2, 110, 'و نماز را برپا دارید و زکات بپردازید، و بدانید که هر کار نیکی برای خود پیش فرستید، آن را نزد خدا بازخواهید یافت، چرا که خداوند به آنچه انجام می‌دهید بیناست.'),
(118, 2, 111, 'و یهودیان گفتند: هرگز کسی به بهشت درنمی آید مگر این که یهودی باشد، و مسیحیان نیز گفتند: جز کسی که مسیحی باشد هیچ کس وارد بهشت نمی‌شود. این است آرزوهایشان. بگو: اگر راست می‌گویید، برهان خود را بیاورید.'),
(119, 2, 112, 'چنین نیست که یهودی یا مسیحی بودن کارساز باشد، بلکه هر کس تسلیم خدا باشد و با تمام وجود به او گرایش یابد و کار شایسته کند، پاداش تسلیم و نیکو کاری خود را نزد پروردگارش خواهد داشت و هیچ ترسی آنان را فرو نمی‌گیرد و اندوهگین نمی‌شوند.'),
(120, 2, 113, 'و یهودیان گفتند: مسیحیان بر چیزی نیستند (آیینشان بی پایه و اساس است) و مسیحیان گفتند: یهودیان بر چیزی نیستند. این در حالی است که هر دو گروه کتاب آسمانی خود را می‌خوانند، و می‌دانند که به حکم کتابشان این داوری صحیح نیست. مشرکان هم که دانشی ندارند مانند سخن اهل کتاب را بر زبان می‌آورند و می‌گویند: یهودیان و مسیحیان بر چیزی نیستند. پس خداوند روز قیامت میان آنان درباره آنچه بر سرش اختلاف کرده‌اند داوری خواهد کرد.'),
(121, 2, 114, 'و کیست ستمکارتر از کسی که مردم را از یاد کردن نام خدا در مساجد او بازداشت و در ویرانی آنها کوشید؟ آنان حق نداشتند جز با ترس و هراس بدانها وارد شوند [تا چه رسد که مسلمانان را از ورود به آنها بازدارند]. برای آنان در دنیا خواری و در آخرت عذابی بزرگ خواهد بود.'),
(122, 2, 115, 'و خاور و باختر فقط از آنِ خداست، پس به هر جا روی کنید، آن جا وجه خداست. این بدان جهت است که خدا بر همه چیز و بر همه جهات احاطه دارد و از توجّه شما به هر سوی آگاه است.'),
(123, 2, 116, 'یهودیان و مسیحیان گفتند: خدا فرزندی برای خود برگرفته است. منزّه است او. بلکه آنچه در آسمان‌ها و زمین است از آنِ او، و همه چیز خاضع و فرمانبردار اوست. پس چگونه می‌تواند فرزندی همسان خود داشته باشد؟'),
(124, 2, 117, 'خداوند آسمان‌ها و زمین را بی آن که الگویی داشته باشد پدید آورده است، و چون پیدایش چیزی را بخواهد، فقط به آن می‌گوید: باش و آن چیز موجود می‌شود.'),
(125, 2, 118, 'و آنان که دانشی ندارند (مشرکان) گفتند: چرا خدا با ما سخن نمی‌گوید یا [از جانب او] نشانه‌ای برای ما نمی‌آید؟ پیش از آنان، یهودیان و مسیحیان نیز همین گونه سخن گفتند. دل هایشان همسان و اندیشه هایشان یکسان است. به راستی، ما نشانه‌های فراوانی را برای مردم آشکار ساخته‌ایم، ولی تنها اهل یقین از آنها بهره می‌برند.'),
(126, 2, 119, 'ای پیامبر، ما تو را به حق به رسالت فرستادیم تا به مؤمنان بشارت و به کافران هشدار دهی، و از تو درباره دوزخیان پرسشی نمی‌شود و تو بازخواست نخواهی شد.'),
(127, 2, 120, 'هرگز یهودیان و مسیحیان از تو خشنود نمی‌شوند مگر این که از آیین آنها پیروی کنی. به آنان بگو: فقط هدایت خدا که قرآن بیانگر آن است، هدایت است. و اگر پس از آن که آگاهی برای تو حاصل شده است، از کیش آنها که برخاسته از هواهای نفسانی آنان است پیروی کنی، در برابر خدا هیچ سرپرست و یاوری نخواهی داشت.'),
(128, 2, 121, 'کسانی که کتاب آسمانی به آنان داده‌ایم حالی که آن را آن گونه که بایسته تلاوت است تلاوت می‌کنند، به قرآن ایمان می‌آورند، و کسانی که بدان کفر ورزند خود زیانکار خواهند بود.'),
(129, 2, 122, 'ای بنی اسرائیل، نعمت مرا که به شما ارزانی کردم به خاطر داشته باشید، و یاد کنید که من شما را با دادنِ نعمت‌های فراوان بر سایر امّت‌ها برتری بخشیدم.'),
(130, 2, 123, 'و بترسید از روزی که هیچ کس چیزی از عذاب را از دیگری کفایت نمی‌کند و در برابر عذاب فدیه‌ای از او پذیرفته نمی‌شود و شفاعتی سودش نمی‌دهد و یاری نمی‌شوند.'),
(131, 2, 124, 'و چون ابراهیم را پروردگارش به کارهایی که او را بدانها امر فرمود بیازمود و وی را به انجام آنها موفق ساخت، گفت: من تو را پیشوای مردم قرار خواهم داد. ابراهیم گفت: آیا از فرزندان من نیز کسی را پیشوای مردم قرار می‌دهی؟ فرمود: امامت، عهدِ من است، و عهدِ من به کسانی از فرزندان تو که ستمکارند نمی‌رسد.'),
(132, 2, 125, 'و یاد کن زمانی را که آن خانه (کعبه) را بازگشتگاهی برای مردم و مکانی امن قرار دادیم، پس برای انجام مراسم حج به آن جا بروید و از مقام ابراهیم جایگاهی برای دعا برگزینید. و به ابراهیم و اسماعیل فرمودیم: خانه‌ام را ویژه عبادت سازید و آن را به طواف کنندگان و معتکفان و نمازگزارانِ رکوع کننده و سجده کننده اختصاص دهید.'),
(133, 2, 126, 'و یاد کن زمانی را که ابراهیم گفت: پروردگارا، این سرزمین را شهری امن قرار ده، و از مردمش کسانی را که به خدا و روز قیامت ایمان آورده‌اند، از میوه‌ها و محصولات روزی عطا فرما. پروردگارش گفت: ولی هر که کافر شود، به او اندک بهره ای می‌دهم، سپس او را به اجبار به سوی عذاب آتش خواهم برد، و آن بد بازگشتگاهی است.'),
(134, 2, 127, 'و در خاطر بیاور زمانی را که ابراهیم و اسماعیل به ساختن و بالا آوردن پایه‌های آن خانه (کعبه) پرداخته و می‌گویند: پروردگارا، این عمل اندک را از ما بپذیر که تو خود نیایش ما را می‌شنوی و از درون ما آگاهی.'),
(135, 2, 128, 'پروردگارا، و ما را در همه امور تسلیم خود گردان و از نسل ما نیز گروهی مقرر دار که در همه حال تسلیم تو باشند، و حقیقت عبادات ما را به ما بنمای، و بر ما ببخشای که تو خود توبه پذیر و مهربانی.'),
(136, 2, 129, 'پروردگارا، در میان آنان پیامبری از خودشان برانگیز که آیات تو را بر آنها بخواند و به آنان کتاب آسمانی و معارف آن را بیاموزد و آنان را از بدی‌ها و زشتی‌ها پاک و پیراسته سازد، به یقین تویی آن شکست ناپذیری که همه کارهایش از روی حکمت است.'),
(137, 2, 130, 'و چه کسی جز آن کس که سبکسر بوده و نیک و بد خویش را باز نشناخته است از آیین ابراهیم روی برمی تابد؟ به راستی ما او را در دنیا به خلوص در بندگی برگزیدیم و قطعاً در آخرت نیز از شایستگان خواهد بود.'),
(138, 2, 131, 'او هنگامی برگزیده شد که پروردگارش به وی گفت: با تمام وجود تسلیم باش. گفت: تسلیم پروردگار جهانیانم.'),
(139, 2, 132, 'و ابراهیم پسرانش را به آن آیین سفارش نمود و یعقوب نیز چنین کرد، و هر یک گفتند: ای پسران من، خداوند این آیین را برای شما برگزیده است، پس مبادا جز در حالی که تسلیم و فرمانبردار او باشید بمیرید.'),
(140, 2, 133, 'آیا شما یهودیان و مسیحیان هنگامی که مرگ یعقوب در رسید حاضر بودید؟ آن گاه که به پسرانش گفت: پس از من چه چیزی را خواهید پرستید؟ گفتند: خدای تو و خدای پدرانت ابراهیم و اسماعیل و اسحاق را که خدایی یگانه است می‌پرستیم و ما تسلیم و فرمانبردار او هستیم.'),
(141, 2, 134, 'آنان گروهی بودند که درگذشتند، و اینک جدال درباره آنها که از کدام طایفه بودند به شما سودی نمی‌رساند. آنچه آنان فراهم آوردند برای خودشان، و آنچه شما فراهم آوردید برای خودتان است، و از شما درباره کارهایی که آنان انجام می‌دادند سؤال نخواهد شد.'),
(142, 2, 135, 'یهودیان به مسلمانان گفتند: یهودی شوید که در آن صورت هدایت می‌یابید، و مسیحیان نیز به آنان گفتند: مسیحی شوید که در آن صورت هدایت خواهید یافت. ای پیامبر، در پاسخ آنان بگو: نه، بلکه ما از آیین ابراهیم پیروی می‌کنیم که از کجروی برکنار بود و از مشرکان نبود.'),
(143, 2, 136, 'شما مسلمانان نیز در پاسخ آنان بگویید: ما به خدا و این قرآن که بر ما نازل شده، و به آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط (پیامبرانی از نسل یعقوب) فرود آمده، و به آنچه به موسی و عیسی داده شده، و به آنچه به همه پیامبران از جانب پروردگارشان عطا گردیده است، ایمان آورده‌ایم، میان هیچ یک از آنان جدایی نمی‌افکنیم و ما تسلیم و فرمانبردار خداییم.'),
(144, 2, 137, 'پس اگر یهودیان و مسیحیان به مانند آنچه شما بدان ایمان آورده‌اید، ایمان بیاورند قطعاً هدایت یافته‌اند، و اگر روی برتابند، جز این نیست که در ستیز و مخالفت‌اند، و به زودی خدا شرّشان را از تو کفایت می‌کند و تو را بر آنان پیروز خواهد کرد، که او شنوا و داناست.'),
(145, 2, 138, 'ایمانِ به خدا و پیامبران و کتاب‌های آسمانی رنگی الهی است و خدا ما را آن گونه که باید به آن رنگ درآورده است، و کیست که رنگ آمیزی او بهتر از خدا باشد؟ و ما پرستشگران اوییم.'),
(146, 2, 139, 'بگو: آیا درباره خدا با ما بحث و گفتوگو می‌کنید؟ با این که او پروردگار ما و پروردگار شماست، و کردارهای ما از آنِ ما و کردارهای شما از آنِ شماست، و ما خالصانه و پیراسته از شرک او را می‌پرستیم.'),
(147, 2, 140, 'یا شما یهودیان می‌گویید: ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط یهودی بودند. و شما مسیحیان می‌گویید: آنان مسیحی بودند؟ به آنان بگو: آیا شما به آیین آنها داناترید یا خدا؟ خدا به ما و شما خبر داده است که آنان پیش از پیدایش یهودیّت و مسیحیّت می‌زیسته‌اند و کیست ستمکارتر از آن کس که حقیقتی را که از جانب خدا نزد اوست و شاهد آن بوده نهان کرده است؟ خدا از آنچه می‌کنید بی خبر نیست.'),
(148, 2, 141, 'آنان گروهی بودند که درگذشتند، و اینک جدال درباره آنها که از کدام طایفه بودند به شما سودی نمی‌رساند. آنچه آنان فراهم آوردند برای خودشان، و آنچه شما فراهم آوردید برای خودتان است، و از شما درباره کارهایی که آنان انجام می‌دادند سؤال نخواهد شد.'),
(149, 2, 142, 'به زودی از میان مردم بی خردان (یهودیان و مشرکان) خواهند گفت: چه چیز مسلمانان را از بیت المقدّس که قبله آنان بود و به سوی آن روی می‌کردند بازگردانید؟ بگو: خاور و باختر از آنِ خداست، و خدا هر نقطه‌ای را که هدایت مردم در آن باشد قبله قرار می‌دهد، و خدا هر که را خواهد به راهی راست هدایت می‌کند.'),
(150, 2, 143, 'آری، همان گونه که قبله را با هدف هدایت مردم تغییر دادیم، شما را نیز از میان مسلمانان به عنوان گروهی برگزیده که میان پیامبر و امّتش واسطه باشید مقرر کردیم تا بر اعمال مردم گواه باشید و پیامبر هم بر شما گواه باشد. و قبله‌ای را که تاکنون بر آن بودی مقرّر نکردیم مگر برای این که کسی را که از پیامبر پیروی می‌کند از آن کس که به عقب بازگشته و از آن روی برمی تابد مشخص سازیم، و البته قبله بودن بیت المقدّس، جز بر کسانی که خدا هدایتشان کرده است، سخت گران بود. و خدا چنین نیست که نمازهایتان را که به سوی قبله اول خوانده‌اید تباه سازد، چرا که خدا به مردم رئوف و مهربان است.'),
(151, 2, 144, 'ای پیامبر، ما چهره تو را که در انتظار وحی به این سو و آن سوی آسمان می‌گردانی، می‌بینیم. اینک تو را به سوی قبله‌ای که از آن خشنود شوی می‌گردانیم. پس در نماز خویش روی خود را به سوی کعبه که بخشی از مسجدالحرام است بگردان، و شما مسلمانان نیز هر جا که باشید روی خود را به سوی آن بگردانید. قطعاً کسانی که به آنان کتاب آسمانی داده شده است می‌دانند که تغییر قبله از بیت المقدّس به کعبه حق و از جانب پروردگارشان است، و خدا از آنچه می‌کنند بی خبر نیست.'),
(152, 2, 145, 'و اگر برای اهل کتاب هر دلیل و نشانه‌ای بر درستی قبله خویش بیاوری از قبله تو پیروی نمی‌کنند و تو نیز پیرو قبله آنان نیستی و خودشان هم پیرو قبله یکدیگر نیستند. و اگر پس از آن که علم برای تو حاصل شده است، از خواسته هایشان پیروی کنی، قطعاً در آن صورت از ستمکاران خواهی بود.'),
(153, 2, 146, 'کسانی که به آنان کتاب آسمانی داده‌ایم، به حکم نویدهایی که از کتابشان در اختیار دارند، همان گونه که پسران خود را می‌شناسند پیامبر را نیز می‌شناسند، و البته گروهی از آنان حق را دانسته کتمان می‌کنند.'),
(154, 2, 147, 'حق از جانب پروردگار توست، پس مبادا از تردیدکنندگان باشی.'),
(155, 2, 148, 'برای هر قومی قبله‌ای است که خدا آن را قبله آنان قرار داده است، پس در این باره کشمکش مکنید و در کارهای خیر از یکدیگر پیشی گیرید. هر جا که باشید خدا همه شما را در صحنه قیامت می‌آورد، که خداوند بر هر کاری تواناست.'),
(156, 2, 149, 'و از هر جا به سفر بیرون آمدی، روی خود را به سوی کعبه که بخشی از مسجدالحرام است بگردان. همانا آن حق و از جانب پروردگار توست، و خدا از آنچه می‌کنید بی خبر نیست.'),
(157, 2, 150, 'آری، از هر جا بیرون آمدی و سفر کردی، روی خود را به سوی کعبه که بخشی از مسجدالحرام است بگردان، و شما مؤمنان نیز هر جا بودید، روی خود را به سوی آن بگردانید. ما قبله را از بیت المقدس به کعبه تغییر دادیم تا مردم ـ چه یهودیان و چه مشرکان ـ هیچ حجّتی بر شما نداشته باشند. لیکن کسانی که بر خود ستم کرده به هواپرستی روی دارند، دست بردار نیستند و همچنان بر ضدّ شما بهانه جویی می‌کنند، از آنها نترسید و از من بترسید و تا نعمت خود را بر شما کامل کنم و باشد که شما هدایت شوید.'),
(158, 2, 151, 'این نعمت را بر شما ارزانی کردیم، همان گونه که در میان شما پیامبری نیز از خودتان به رسالت فرستادیم او آیات ما را بر شما تلاوت می‌کند و شما را از بدی‌ها و زشتی‌ها پاک و پیراسته می‌سازد و کتاب آسمانی و معارف آن را به شما می‌آموزد، و آنچه را که نمی‌توانستید دریابید به شما تعلیم می‌دهد.'),
(159, 2, 152, 'پس مرا با عبادت و اطاعت خویش یاد کنید که من نیز شما را با دادن نعمت‌ها یاد خواهم کرد، و سپاسگزار من باشید و نعمت‌های مرا ناسپاسی نکنید.'),
(160, 2, 153, 'ای کسانی که ایمان آورده‌اید، از صبر و نماز یاری جویید، زیرا خدا با صبرپیشگان است.'),
(161, 2, 154, 'و کسانی را که در راه خدا کشته می‌شوند مرده نخوانید، بلکه زنده‌اند ولی شما درنمی یابید.'),
(162, 2, 155, 'قطعاً ما شما را به چیزی از ترس و گرسنگی و کاهش اموال و نفوس و فرزندان می‌آزماییم. و تو ـ ای پیامبر ـ به شکیبایان نوید ده.'),
(163, 2, 156, 'همانان که چون پیشامد ناگواری به آنان برسد می‌گویند: ما از آنِ خداییم و به سوی او بازمی گردیم.'),
(164, 2, 157, 'اینانند که الطافی از جانب پروردگارشان شامل حالشان خواهد شد و در پی آن، رحمتی آنان را فراخواهد گرفت و در نتیجه به سوی حق هدایت خواهند یافت.'),
(165, 2, 158, 'بی گمان صفا و مروه از شعایر خداست، پس هر که آهنگ آن خانه (کعبه) کند و حج بگزارد، یا زیارت خانه کند و عمره به جای آورد، بر او گناهی نیست که میان آن دو سعی کند. و هر کس کار نیکی را به شوق اطاعت خدا انجام دهد، خدا به او پاداش می‌دهد، چرا که خدا سپاسگزار بندگان خویش و از کارشان آگاه است.'),
(166, 2, 159, 'کسانی که دلایل روشن و رهنمودهای ما را پس از آن که در کتاب آسمانی برای مردم به روشنی بیان کرده‌ایم کتمان می‌کنند، خدا لعنتشان می‌کند و آنان را از رحمت خود دور می‌سازد، و همه لعنت کنندگان نیز آنان را لعنت می‌کنند و از خدا می‌خواهند که آنان را از رحمت و سعادت محروم کند.'),
(167, 2, 160, 'مگر کسانی که توبه کنند و مردمی صالح شوند و حقایقی را که پنهان داشته‌اند بیان کنند، آنانند که من با رحمت خود به سویشان بازمی گردم و توبه شان را می‌پذیرم، زیرا من بسیار توبه پذیر و مهربانم.'),
(168, 2, 161, 'کسانی که بر کفر اصرارورزیده و در حال کفر مرده‌اند، لعنت خدا و فرشتگان و همه مردم بر آنان خواهد بود.'),
(169, 2, 162, 'در این لعنت و عذاب الهی ماندگارند، نه عذاب از آنان کاسته می‌شود و نه در تأخیر عذاب مهلت می‌یابند.'),
(170, 2, 163, 'و معبود شما معبودی یگانه است که هیچ معبودی شایسته پرستش جز او نیست، رحمت گستر و مهربان است.'),
(171, 2, 164, 'بی گمان، در آفرینش آسمان‌ها و زمین و در کوتاهی و بلندی شب و روز، و در کشتی‌هایی که در دریا با کالاهایی که به مردم سود می‌رسانند، روانند و در بارانی که خدا از آسمان فرو فرستاد و زمین را پس از آن که مرده و بی گیاه بوده به وسیله آن حیات بخشید، و از هر جنبنده‌ای در آن پراکنده ساخته، و در فرستادن بادها از سویی به سوی دیگر، و در ابرهای مسخر شده میان آسمان و زمین نشانه‌هایی بر وجود آفریدگار و یکتایی او در تدبیر جهان است. این نشانه‌ها را مردمی درمی یابند که به خرد، حق را از باطل تشخیص می‌دهند.'),
(172, 2, 165, 'و از میان مردم کسانی هستند که غیر خدا را همتایان خدا قرار می‌دهند و آنها را مانند دوست داشتن خدا دوست می‌دارند، ولی کسانی که ایمان آورده‌اند، محبّتشان به خدا بیشتر است. ای کاش ستمکاران (مشرکان) می‌دیدند زمانی را که عذاب را مشاهده می‌کنند، آن گاه درمی یافتند که قدرت به تمامی از آنِ خداست و این که خدا عذابش شدید است،'),
(173, 2, 166, 'آن گاه که سران شرک از پیروان خود تبرّی می‌جویند و همگان عذاب را می‌نگرند و پیوندهایی که آنها را به یکدیگر ارتباط می‌داد، گسسته می‌شود.'),
(174, 2, 167, 'و پیروان می‌گویند: ای کاش، بازگشتی به دنیا داشتیم، آن گاه از آنان تبرّی می‌جستیم، همان گونه که امروز آنان از ما تبرّی جستند. این گونه خداوند کارهایشان را که برایشان مایه حسرت هاست به آنان می‌نمایاند و آنان را به آتش درمی آورد، و آنان از آتش بیرون آمدنی نیستند.'),
(175, 2, 168, 'ای مردم، از آنچه در زمین است بهره برید، که خدا آن را برای شما حلال و پاکیزه ساخته است و گام‌های شیطان را دنبال نکنید که او برای شما دشمنی آشکار است، پس مبادا به پیروی از شیطان آنچه را خدا حلال کرده است حرام بشمارید و آنچه را حرام کرده است روا بدانید.'),
(176, 2, 169, 'شیطان فقط شما را به بدی و زشتی فرمان می‌دهد و از شما می‌خواهد که چیزهایی را بر خدا ببندید که نسبت آنها را به خدا نمی‌دانید.'),
(177, 2, 170, 'و چون به آنان گفته شود: کتابی را که خدا فرو فرستاده است پیروی کنید، می‌گویند: نه، بلکه از آیینی که پدرانمان را بر آن یافته‌ایم پیروی می‌کنیم. آیا هر چند پدرانشان چیزی نمی‌دانستند و به راه راست هدایت نیافته بودند، باز هم از کیش آنها پیروی می‌کنند؟'),
(178, 2, 171, 'ای پیامبر، حکایت تو در فراخواندن این کافران به توحید، همچون حکایت چوپانی است که بر گوسفندان خود با کلماتی نهیب می‌زند، ولی آنها از کلمات او جز خواندن و آوایی نمی‌شنوند. اینان نیز کر و لال و کورند، نه سخن سودمند را می‌شنوند و نه خود درست سخن می‌گویند و نه نشانه‌های حق را می‌نگرند، از این رو هیچ حقیقتی را درنمی یابند.'),
(179, 2, 172, 'ای کسانی که ایمان آورده‌اید، از آنچه روزی شما کرده‌ایم که همه پاک و پاکیزه است بخورید و خدا را سپاسگزاری کنید اگر فقط او را می‌پرستید.'),
(180, 2, 173, 'او تنها مردار و خون و گوشت خوک و حیوانی را که هنگام سر بریدن، نام غیر خدا بر آن برده شده، بر شما حرام کرده است. پس هر کس به خوردن آنها ناچار شود، به شرط آن که ناچاری او در اثر ستمکاری و سرکشی نباشد، بر او گناهی نیست، چرا که خداوند آمرزنده و مهربان است.'),
(181, 2, 174, 'کسانی که حقایق آن کتابی را که خدا فرو فرستاده است کتمان می‌کنند و آن را به بهای ناچیز دنیا می‌فروشند، آنان در شکم‌های خود جز آتش فرونمی برند و روز قیامت خدا به آنان توجّه نمی‌کند و با آنان سخن نمی‌گوید و از زشتی‌ها پاکشان نمی‌کند و برایشان عذابی دردناک است.'),
(182, 2, 175, 'اینان کسانی اند که گمراهی را به هدایت و عذاب را به آمرزش خریده‌اند. پس چقدر بر آتش شکیبایند!'),
(183, 2, 176, 'آن [عذاب] بدین سبب است که خداوند کتاب آسمانی را به حق نازل کرده است و کسانی که در آن کتاب اختلاف می‌کنند، در نقطه مقابل حق قرار گرفته‌اند که با حق فاصله‌ای دور و دراز دارد.'),
(184, 2, 177, 'نیکی آن نیست که [هنگام عبادت] روی خود را به سوی مشرق و مغرب بگردانید و آن را به عنوان قبله تلقی کنید، بلکه نیکی آن است که انسان به خدا و روز واپسین و فرشتگان و کتاب‌های آسمانی و پیامبران ایمان آورده و مال خود را با این که به آن علاقه مند است، به خویشاوندان و یتیمان و بینوایان و درراه مانده و گدایان و در راه آزادی بردگان داده است، و نماز را برپا داشته و زکات خود را پرداخته است، و آن است که چون پیمانی بستند به پیمان خود وفا کردند و آن است که در تنگدستی و زیانکاری و به هنگام کارزار، شکیبایی و پایداری نمودند. اینانند کسانی که راست گفته‌اند و اینانند تقواپیشگان.'),
(185, 2, 178, 'ای کسانی که ایمان آورده‌اید، در مورد کشته شدگان حق قصاص بر شما مقرر شده است. آزاد در برابر آزاد و برده در برابر برده و زن در برابر زن قصاص می‌شود. پس هر کس مرتکب قتل شد، و از سوی ولیّ مقتول، به حکم این که برادر دینی اوست، چیزی از حق قصاص به وی بخشیده شد، باید ولیّ مقتول به طور شایسته درصدد گرفتن دیه برآید و قاتل نیز به خوبی و با پرهیز از تأخیری آزاردهنده دیه را بپردازد. این حکم، تخفیف و رحمتی از جانب پروردگار شماست. پس هر کس بعد از بخشیدن قاتل و گذشت از حق قصاص، از قانون الهی تجاوز کند و قاتل را قصاص نماید، او را عذابی دردناک خواهد بود.'),
(186, 2, 179, 'ای خردمندان، برای شما در قصاص حیاتی نهفته است. قصاص مقرّر شده است تا شاید از کشتن یکدیگر بپرهیزید.'),
(187, 2, 180, 'ای مؤمنان، بر شما مقرّر گردیده است که چون یکی از شما را مرگ دررسد، اگر مالی قابل توجّه برجای می‌گذارد، برای پدر و مادر و خویشاوندانِ نزدیک‌تر به طور پسندیده وصیت کند. این حکم برعهده تقواپیشگان ثابت شده است.'),
(188, 2, 181, 'پس هر کس بعد از شنیدن وصیت آن را تغییر دهد، گناهش تنها بر گردن کسانی است که آن را تغییر می‌دهند. به راستی خدا شنوا و داناست.'),
(189, 2, 182, 'ولی هر که دریابد که وصیت کننده‌ای در وصیتش به کژی گرایش یافته و حقی را پایمال نموده و در نتیجه نزاعی میان وارثان پدید آمده است، پس با تغییر دادن وصیت میان آنان صلح برقرار کند، بر تغییردهنده گناهی نیست و همچنین است اگر به گناهی وصیت کرده باشد، چرا که خدا آمرزنده و مهربان است.'),
(190, 2, 183, 'ای کسانی که ایمان آورده‌اید، روزه بر شما مقرّر شده است، همان گونه که بر امّت‌هایی که پیش از شما بودند مقرّر شده بود، باشد که بر تمایلات خویش فایق آیید و تقوا پیشه کنید.'),
(191, 2, 184, 'روزهایی معدود باید روزه بدارید. پس اگر کسی از شما در آن روزها بیمار یا در سفر بود، روزه شماری از روزهای دیگر برعهده اوست. و بر کسانی که روزه را باید به سختی به پایان برند، روزه مقرّر نشده است، ولی باید کفّاره‌ای بدهند که آن غذایی است که یک بینوا را سیر می‌کند. و هر کس از روی رغبت کار نیکی انجام دهد برای او بهتر است، و روزه داشتن برای شما کار خوبی است، اگر بدانید.'),
(192, 2, 185, 'ایام روزه واجب، ماه رمضان است ماهی که قرآن در آن فرو فرستاده شد، در حالی که برای عموم مردم رهنمود است و برای کسانی که در علم و عمل به کمالی دست یافته و شایسته دریافت حقایقی شده‌اند، دلایل روشن هدایت و وسیله تشخیص حق از باطل است. پس هر کس از شما این ماه را دریابد، باید آن را روزه بدارد، و هر که بیمار یا در سفر باشد، روزه شماری از روزهای دیگر برعهده اوست. خداوند برای شما آسانی می‌خواهد و دشواری برای شما نمی‌خواهد، این است که بر بیماران و مسافران شما روزه را واجب نکرد و قضای روزه را از آن روی بر شما واجب نموده است که تعداد روزهای ماه رمضان را کامل کنید. آری، خدا روزه را بر شما مقرّر داشت تا او را به پاس این که هدایتتان کرده است به بزرگی یاد کنید، و باشد که شکر هدایتش را به جای آورید.'),
(193, 2, 186, 'و هرگاه بندگان من از تو درباره من بپرسند، بدانند که من به آنان نزدیکم، دعای دعاکننده را ـ آن گاه که مرا بخواند ـ اجابت می‌کنم، پس باید دعوت مرا بپذیرند و به درگاه من دعا کنند و به من ایمان بیاورند که دعایشان را اجابت می‌کنم، امید آن که در دعایشان به درگاه خدا راهیاب شوند.'),
(194, 2, 187, 'در شبِ روزه داری بر شما حلال شده است که به سوی زنانتان بروید و با آنان آمیزش کنید. آنان جامه شمایند و شما جامه آنانید. خداوند دانست که شما به خود خیانت می‌کردید و حکم حرمت آمیزش در شب‌های روزه را پاس نمی‌داشتید، از این رو با رحمت خود به سوی شما باز آمد و از شما درگذشت و حرمت آن را برداشت. اکنون می‌توانید با آنان آمیزش کنید و بدین طریق آنچه را خدا برای شما مقدر کرده است طلب نمایید. و شب‌های روزه بخورید و بیاشامید تا وقتی که رشته سپید صبحدم از رشته سیاه شب برای شما آشکار شود. از آن پس خوردن و آشامیدن را ترک کنید و روزه را تا شب به پایان برید. و هنگامی که در مساجد معتکفید، حتی شب‌های آن، با همسرانتان آمیزش نکنید. اینها حدود و مقرّرات خداست، به آنها نزدیک نشوید و آنها را نشکنید. بدین سان خداوند آیات خود را برای مردم بیان می‌کند، باشد که تقوا پیشه کنند.'),
(195, 2, 188, 'و اموال را که به همه شما مردم تعلق دارد و باید بر طبق مقرّرات الهی میان شما تقسیم شود، از راه‌های ناروا به دست نیاورید و مصرف نکنید، و آنها را به رسم رشوه پیش داوران نبرید تا به حُکم آنان بخشی از اموال مردم را به گناه به چنگ آورید، در حالی که خود نادرستی این کار را می‌دانید.'),
(196, 2, 189, 'از تو حکمت هلال‌های ماه را می‌پرسند، بگو: آنها گاهشمارهایی برای مردم و مراسم حج‌اند، و نیکی آن نیست که پس از بستنِ احرام حج، از پشتِ خانه‌ها، به خانه‌ها درآیید، بلکه نیکی آن است که کسی تقوا پیشه کند. و به خانه‌ها از درِ آنها وارد شوید و از خدا پروا کنید، باشد که سعادتمند شوید.'),
(197, 2, 190, 'و در راه خدا با کسانی (مشرکان مکه) که با شما سرِ جنگ دارند بجنگید و از حدود الهی تجاوز نکنید، که خدا تجاوزگران را دوست نمی‌دارد.'),
(198, 2, 191, 'و هر کجا بر آنان دست یافتید بکشیدشان و از مکّه، همان شهری که شما را از آن بیرون کردند، بیرونشان کنید. آنان بر شرک اصرار میورزند و شما را به انواع عذاب‌ها شکنجه دادند، و شرک و شکنجه از کشتار سنگین‌تر است. و کنار مسجدالحرام با مشرکان نجنگید، مگر این که آنان در آن جا به کارزار شما آیند. پس اگر به کارزار شما آمدند بکشیدشان که سزای کافران چنین است.'),
(199, 2, 192, 'و اگر از جنگ در محدوده مسجدالحرام دست کشیدند، از آنان درگذرید، که خدا آمرزنده و مهربان است.'),
(200, 2, 193, 'و با آنان بجنگید تا شرک از بین برود و دین از آنِ خدا گردد و کسی را نرسد که مردم را به پرستش غیر خدا دعوت کند. پس اگر از شرک دست برداشتند و ایمان آوردند با آنان نجنگید، زیرا تعدّی جز بر ضد ستمکاران روا نیست.'),
(201, 2, 194, 'اگر آنان در ماه حرام با شما جنگ را آغاز کردند شما نیز با آنان بجنگید، زیرا ماه حرام در برابر ماه حرام است، و اگر حرمت مکّه و مسجدالحرام و ماه‌های حرام را شکستند شما نیز حرمت آنها را نادیده انگارید که حرمت‌ها قابل تلافی است. پس هر کس بر شما تعدّی کرد، شما نیز به مثل آن بر او تعدّی کنید، و از خدا پروا نمایید و در مقابله به مثل از حدّ تجاوز نکنید. و بدانید که خدا با پرواپیشگان است.'),
(202, 2, 195, 'در راه خدا و برای برپایی دین هزینه جهاد را تأمین کنید و خود را به دست خویش به هلاکت نیفکنید، و نیکی کنید که خدا نیکوکاران را دوست می‌دارد.'),
(203, 2, 196, 'و حج و عمره را ناتمام نگذارید و آن را برای خدا به پایان برید، و اگر پس از احرام، در اثر بیماری یا از سوی دشمن بازداشته شدید و نتوانستید حج یا عمره را تمام کنید، هر دامی که برای شما آسان است قربانی کنید [سپس با تراشیدنِ سر از احرام خارج شوید]. و تا قربانی به جایگاهش (مکّه برای احرام عمره و مِنی برای احرام حجّ) نرسیده است سر خود را نتراشید. پس اگر کسی از شما بیمار یا از ناحیه سرش در رنج باشد [و نتواندتا رسیدن قربانی به قربانگاه تحمّل کند بر او رواست که سر خود بتراشد و از احرام خارج شود سپس] باید به رسم کفّاره روزه‌ای بگیرد یا صدقه‌ای بدهد یا گوسفندی ذبح کند. و هنگامی که ایمنید و مانعی برای به پایان رساندن حجّ و عمره ندارید، هر کس با انجام دادن عمره و خروج از احرام تا موقع پرداختن به حج، از آنچه بر او حرام بوده بهره برده است (عمره تمتّع انجام داده است)، باید برای حج خود هر دامی که آسان است قربانی کند، و هر کس قربانی نیابد، باید سه روز در ایام حجّ و هفت روز هنگامی که به وطن بازگشتید روزه بدارد، که این ده روزِ کامل است. این گونه حج (حج تمتع) برای کسی مقرّر شده است که خانواده اش در کنار مسجدالحرام نباشد. از خدا پروا کنید و بدانید که خدا سخت کیفر است.'),
(204, 2, 197, 'موسم حج ماه‌هایی معیّن (شَوّال، ذِی القَعدَه و ذی الحَجَّه) است. پس هر که در این ماه‌ها با بستنِ احرام، انجام حج را بر خود واجب کند، بداند که در اثنای حج آمیزش با زنان و دروغ گفتن و سوگند خوردن به نام خدا روا نیست، و هر کار نیکی که انجام می‌دهید خدا آن را می‌داند. و بر شماست که توشه برگیرید، و بهترین توشه پرهیزگاری است، و از من ای خردمندان پروا کنید.'),
(205, 2, 198, 'بر شما گناهی نیست که در دوران حج از راه داد و ستد در طلب رزقی که آن بخششی از جانب پروردگار شماست برآیید، و چون از عرفات ـ پس از وقوف در آن ـ کوچ کردید، در مشعرالحرام وقوف نمایید و خدا را یاد کنید آن گونه که او شما را راه نموده است یادش کنید، که قطعاً شما پیش از این از گم گشتگان بودید.'),
(206, 2, 199, 'وانگهی شما ساکنان حرم نیز باید از عرفات، از همان جا که سایر مردم کوچ می‌کنند کوچ کنید و از خدا آمرزش گناهانتان را بخواهید که خدا آمرزنده و مهربان است.'),
(207, 2, 200, 'و هنگامی که مناسک حج خود را به جای آوردید، خدا را آن گونه که پدرانتان را پس از انجام حج یاد می‌کنید، بلکه بیشتر و بهتر، یاد کنید. برخی از مردم دنیاطلبانی هستند که می‌گویند: پروردگارا، در دنیا به ما بهره‌ها عطا کن. آنان در آخرت هیچ بهره‌ای ندارند.'),
(208, 2, 201, 'و برخی از مردم آخرت طلبانی هستند که می‌گویند: پروردگارا، در دنیا به ما بهره‌ای نیکو که خوشایند توست و در آخرت نیز بهره‌ای نیکو که خود می‌پسندی عطا فرما و ما را از عذاب آتش دوزخ نگاه دار.'),
(209, 2, 202, 'اینانند که از آنچه فراهم آورده‌اند بهره‌ای [وافر] خواهند داشت، و خدا کارها را به سرعت ـ همان گاه که انجام می‌شود ـ حسابرسی می‌کند.'),
(210, 2, 203, 'و بر شما حج گزاران است که در روزهایی چند (روزهای یازدهم، دوازدهم و سیزدهم ذی الحجّه) در سرزمین منی ذکر خدا گویید. پس هر که حج گزارد، بر او هیچ گناهی باقی نمی‌ماند، خواه تعجیل کند و در دو روز نخست (بعدازظهر روز دوازدهم) از منی کوچ کند، و خواه کوچ خود را به تأخیر اندازد و روز سوم منی را ترک نماید. البته آمرزش گناهان برای کسی است که از محرمات احرام پرهیز کرده باشد، امّا کسی که آنها را مرتکب شده است باید هر سه روز در منی بماند و به ذکر خدا بپردازد [تا بر او نیز گناهی باقی نماند]. و از خدا پروا کنید و بدانید که به سوی او روانه خواهید شد.'),
(211, 2, 204, 'از میان مردم کسی است که در زندگی دنیا سخنان او در وصف حق و رعایت عدل تو را به شعف می‌آورَد، و خدا را بر آنچه در دل دارد گواه می‌گیرد، در حالی که سخت‌ترین دشمنان حق است.'),
(212, 2, 205, 'و چون به حکومت رسد، در زمین به تکاپو می‌افتد تا در آن تباهی کند و کشت و نسل بشر را به نابودی کشاند، و خداوند تباهی را دوست نمی‌دارد.'),
(213, 2, 206, 'و چون به او گفته شود: از خدا بترس و از تبهکاری دوری گزین، عزّت و شکوه خیالی که با گناه و نفاق به آن دست یافته است، او را فرا می‌گیرد [و پند نمی‌گیرد]. دوزخ او را بس است، و راستی که آن بد جایگاهی است.'),
(214, 2, 207, 'و از میان مردم کسی نیز هست که خود را به خدا می‌فروشد تا در همه کارها خشنودی او را طلب کند. وجود چنین کسی، لطفی از سوی خدا بر آدمیان است، و خدا به بندگانش رئوف است.'),
(215, 2, 208, 'ای کسانی که ایمان آورده‌اید، همگی در برابر خدا تسلیم شوید و از او دستور بگیرید و گام‌های شیطان را دنبال نکنید که او برای شما دشمنی آشکار است.'),
(216, 2, 209, 'و اگر پس از آن که دلایل روشنگر برای شما آمد، بر اثر پیروی از شیطان دچار لغزش و انحراف شدید، بدانید که خدا مقتدری شکست ناپذیر است و براساس حکمت خود با شما رفتار می‌کند.'),
(217, 2, 210, 'آیا مشرکان چیزی جز این انتظار دارند که خدا در سایبان‌هایی از ابرها به سراغشان آید و نیز فرشتگان بیایند و کار پایان داده شود؟ و همه امور به خدا بازگردانده می‌شود.'),
(218, 2, 211, 'از بنی اسرائیل بپرس چقدر نشانه‌های روشن و روشنگر که هر کدام نعمتی به شمار می‌رفت به آنان دادیم، ولی آن نشانه‌ها را نادیده انگاشتند و نعمت‌های الهی را دگرگون ساختند و دچار کیفر شدند. و هر کس نعمت خدا را پس از آن که در اختیارش قرار گیرد دگرگون کند (در مسیری که بایسته است به کار نبندد) دچار کیفر الهی می‌شود، و خدا سخت کیفر است.'),
(219, 2, 212, 'زندگی دنیا برای کافران آراسته شده و به همین سبب است که چشم خود را بر هر حقیقتی می‌بندند و کسانی را که ایمان آورده‌اند به مسخره می‌گیرند، در حالی که تقواپیشگان در روز قیامت از آنان برترند. و خداوند هر که را بخواهد، بدون محاسبه عملش روزی می‌دهد.'),
(220, 2, 213, 'مردم در آغاز، یک امّت بودند و اختلافی نداشتند، سپس تضاد منافع باعث اختلاف میان آنان شد و برای رفع اختلاف به قوانین و مقرراتی نیازمند گشتند. پس خداوند پیامبران را به عنوان نویددهنده و هشدارگر برانگیخت و کتاب آسمانی را به حق در حالی که گواه راستگویی آنان بود، فرو فرستاد، تا میان مردم درباره آنچه در آن اختلاف داشتند داوری کند. سپس در مورد کتاب آسمانی و آیین خدا نیز اختلاف پدید آمد، و تنها عالمانِ دینی که به آنان کتاب آسمانی داده شد در آن اختلاف کردند، آن هم پس از آن که دلایل روشن و روشنگر برایشان آمده بود، و اختلافشان نیز در اثر برتری جویی و ستمی بود که میان آنان پدید آمده بود. در این میان، خداوند کسانی را که ایمان داشتند، به اذن خود به همان حقی که دیگران در آن اختلاف کردند هدایت نمود، و خدا هر که را بخواهد به راهی راست هدایت می‌کند.'),
(221, 2, 214, 'آیا می‌پندارید به بهشت می‌روید و حال آن که هنوز همانند داستان کسانی که قبل از شما درگذشتند برای شما پیش نیامده است؟ محنت‌ها و گزندها به آنان رسید و پیوسته دچار ترس و اضطراب شدند تا آن جا که پیامبر و کسانی که ایمان آورده و با او بودند، گفتند: یاری خدا کی خواهد بود؟ هان، به یقین یاری خدا نزدیک است.'),
(222, 2, 215, 'از تو می‌پرسند: چه چیز انفاق کنند؟ بگو: هر مالی انفاق کنید، برای پدر و مادر و خویشاوندان نزدیک‌تر و یتیمان و بینوایان و درراه مانده است، و هر کار نیکی انجام دهید، قطعاً خدا به آن آگاه است.'),
(223, 2, 216, 'جنگ بر شما واجب شده است با این که آن برای شما دشوار و ناخوشایند است. شاید چیزی را ناخوشایند بدانید در حالی که آن برای شما نیکوست، و شاید چیزی را دوست بدارید در حالی که آن برای شما شرّ است. خداوند می‌داند و شما نمی‌دانید.'),
(224, 2, 217, 'از تو درباره ماه حرام می‌پرسند که پیکار در آن چه حکمی دارد؟ بگو: پیکار در آن [گناهی] بزرگ و باعث بازداشتن مردم از راه خدا و کفر ورزیدن به آن و جلوگیری مردمان از [حضور در] مسجدالحرام است، ولی مشرکان باید بدانند که بیرون راندن اهل آن از آن جا، نزد خدا از پیکار در ماه حرام بزرگ‌تر است، و آنها پیامبر و پیروان او را از آن جا بیرون کردند، و شرک ورزیدن به خدا و شکنجه مؤمنان از کشتار در ماه حرام بزرگ‌تر است و آنها مسلمانان را در مکه آزار فراوان کردند. آنها پیوسته با شما می‌جنگند تا شما را از دینتان برگردانند اگر بر آن توانا باشند و کسانی از شما که از دین خود برگردند و در حال کفر بمیرند، آنان اعمالشان در دنیا و آخرت تباه می‌گردد و آنان همدم آتشند و در آن جاودانه خواهند بود.'),
(225, 2, 218, 'کسانی که ایمان آورده و کسانی که در راه خدا هجرت کرده و جهاد نموده‌اند، آنان به رحمت خدا امیدوارند، و خدا آمرزنده و مهربان است.'),
(226, 2, 219, 'از تو درباره شراب و قمار می‌پرسند. بگو: در آن دو گناهی بزرگ و برای مردمان سودهایی است، و گناه آن دو از سودشان بزرگ‌تر است. و از تو می‌پرسند که چه چیزی انفاق کنند؟ بگو: در انفاق میانه رو باشید، نه بخل ورزید و نه همه اموالتان را ببخشید. بدین سان خداوند آیات و نشانه‌ها را برای شما بیان می‌کند، باشد که درباره سعادت دنیا و آخرت خود بیندیشید.'),
(227, 2, 220, 'آری باشد که درباره سعادت دنیا و آخرت خود بیندیشید. و از تو درباره یتیمان می‌پرسند. بگو: اصلاح امور آنان به گونه‌ای که برایشان سودمند باشد نیکی است، و اگر با آنان همزیستی کنید، آنها برادران شمایند، پس مانند دیگر مسلمانان با آنان رفتار کنید، نه حقشان را تباه سازید و نه خود را سخت به زحمت بیفکنید، که خداوند فسادگر و اصلاحگر را می‌شناسد و آن دو را از هم مشخص می‌سازد. و اگر خدا می‌خواست، شما را در مورد یتیمان به زحمت می‌انداخت [ولی چنین نکرد] چرا که خداوند مقتدری حکیم است.'),
(228, 2, 221, 'و زنان مشرک را به همسری نگیرید تا این که ایمان بیاورند، و قطعاً کنیزی که با ایمان است، بهتر از زنِ آزادی است که مشرک باشد، هر چند به زیبایی اش شما را به شگفت آورَد. و زنان با ایمان را به همسری مشرکان درنیاورید تا این که ایمان بیاورند. بی تردید، برده‌ای که مؤمن است، بهتر از مردِ آزادی است که مشرک باشد، هرچند به مال و ثروتش شما را به شگفت آورد. آنان با گفتار و کردارشان به شرک فرا می‌خوانند که فرجام آن چیزی جز آتش نیست، و خداوند به اذن خود ـ بی آن که کسی یا چیزی او را ناچار کرده باشد ـ به بهشت و آمرزش فرا می‌خواند و آیاتش را برای مردم بیان می‌کند، باشد که حقایق را دریابند.'),
(229, 2, 222, 'از تو درباره عادت ماهانه زنان می‌پرسند. بگو: این برای آنان رنجی است. پس در روزهای عادتشان از آنان کناره گیرید، و با آنها نزدیکی نکنید تا پاک شوند. پس هنگامی که غسل کردند، از همان جا که خدا به شما فرمان داده است با آنان آمیزش کنید، که خداوند کسانی را که بسیار توبه کنند و در پاکی و پاکیزگی کوشا باشند دوست می‌دارد.'),
(230, 2, 223, 'همسرانتان کشتزار شمایند. پس هرگاه خواستید، به کشتزار خود در آیید و برای خود کارهای نیک پیش فرستید و از روز ملاقات و حسابرسی خدا بترسید و بدانید که شما او را دیدار خواهید کرد، و تو ـ ای پیامبر ـ مؤمنان را [بدان] نوید ده.'),
(231, 2, 224, 'و خدا را دستاویز سوگندهای خود قرار ندهید (به نام او سوگند نخورید) که نیکی نکنید و تقوا را پیشه خود نسازید و میان مردم صلح و آشتی برقرار نکنید، که خداوند شنوا و داناست.'),
(232, 2, 225, 'خداوند شما را به سبب سوگندهای بیهوده‌ای که بر زبانتان جاری می‌شود بازخواست نمی‌کند، ولی بر آنچه دل هایتان فراهم آورده (گناهی که بر اثر شکستن سوگندهای جدی کسب کرده است) شما را بازخواست می‌کند، و خداوند آمرزنده و بردبار است.'),
(233, 2, 226, 'کسانی که سوگند یاد می‌کنند که از همسران خود کناره گیری کنند، چهار ماه فرصت دارند و می‌توانند در این مدت به سوگند خویش پای بند باشند، ولی پس از آن یا باید سوگند خود را بشکنند و کفّاره دهند و یا همسر خود را طلاق گویند. پس اگر به وی بازگشتند، خدا آنان را می‌بخشاید و بر آنان رحمت می‌آورد.'),
(234, 2, 227, 'و اگر آهنگ طلاق کردند و همسر خود را طلاق گفتند، [بدانند که] خدا هر سخنی را می‌شنود و از هر نیّتی باخبر است.'),
(235, 2, 228, 'و زنانِ طلاق داده شده سه پاکی را انتظار می‌کشند و از ازدواج خودداری می‌کنند (عدّه نگه می‌دارند). بارداری و عادت ماهانه زنان تعیین کننده دوران عدّه است، پس اگر به خدا و روز قیامت ایمان دارند، نباید آنچه را خدا در رحم آنان آفریده است کتمان کنند. و شوهرانشان اگر خواهان اصلاح باشند، از دیگران سزاوارترند که در این مدّت آنان را به زندگی باز گردانند. و مانند همان وظایفی که به سود مردان برعهده زنان است، به طور شایسته، به سود آنان برعهده مردان نیز می‌باشد، و مردان به درجه‌ای بر زنان برتری دارند، و خدا مقتدری حکیم است.'),
(236, 2, 229, 'طلاقی که شوهر در آن حق رجوع دارد، دو بار است که در هر بار یا او را پیش از پایان یافتن عدّه به شایستگی نگاه می‌دارد و یا به خوبی رهایش می‌سازد و برای شما حلال نیست که از مهریه‌ای که به آنان داده‌اید چیزی باز پس گیرید، مگر این که زن و شوهر بترسند که نتوانند مقرّرات الهی را برپا دارند. پس اگر بیم آن دارید که آن دو تن مقرّرات الهی را برپا ندارند، بر آن دو گناهی نیست که توافق کنند و زن برای گرفتن طلاق چیزی ازمهریه خود را به شوهر ببخشد. اینها حدود و مقرّرات الهی است، به آنها تجاوز نکنید. و کسانی که به حدود الهی تجاوز کنند ستمکارند.'),
(237, 2, 230, 'و اگر بار سوم او را طلاق داد، دیگر آن زن برای او حلال نمی‌شود (نه حق رجوع دارد و نه با عقد مجدّد می‌تواند او را به همسری گیرد) تا این که زن، خود را به همسری مردی دیگر درآورَد. پس اگر همسر دوم او را طلاق داد، بر زن و همسر گذشته اش ـ اگر می‌پندارند که حدود خدا را بر پا می‌دارند ـ گناهی نیست که با عقد مجدد به یکدیگر باز گردند. اینها مقرراتی است که خدا آن‌ها را بیان می‌کند تا مردمی که اهل دانشند از آن بهره برند.'),
(238, 2, 231, 'و هنگامی که زنان را طلاق گفتید، و به پایان عدّه خویش نزدیک شدند، یا با رجوع خود، آنان را به طور پسندیده در همسری خود نگاه دارید و یا به طور پسندیده رهایشان کنید، و آنان را برای این که زیانی به ایشان برسانید نگاه مدارید تا [بدین وسیله به حقوق آنان و حدود الهی] تجاوز کنید. و هر کس چنین کند، به خود ستم کرده و احکام خدا را به مسخره گرفته است. پس مبادا احکام الهی را با این کار به مسخره گیرید. و نعمت خدا را که به شما ارزانی داشته و کتاب و حکمتی را که بر شما فرو فرستاده و شما را به آن پند می‌دهد به یاد آورید، و از حسابرسی خدا و عذاب او پروا کنید و بدانید که خدا به هر چیزی داناست.'),
(239, 2, 232, 'و هنگامی که زنان را طلاق گفتید و به پایان عدّه خود رسیدند، شما خویشاوندانِ زنان، نباید آنان را از ازدواج مجدد با شوهران سابق خود، چنانچه به طور پسندیده میان خود به توافق رسند بازدارید. هر کس از شما به خدا و روز واپسین ایمان دارد، به این حکم پند داده می‌شود. این از نظر ارزش‌ها برای شما پربارتر و برای پاکی روانتان نیکوتر است، و خدا می‌داند و شما نمی‌دانید.'),
(240, 2, 233, 'و مادران ـ هرچند طلاق گرفته باشند ـ فرزندان خود را دو سال تمام شیر می‌دهند. این حکم برای کسی است که بخواهد دوران شیرخوارگی را تکمیل کند. و خوراک و پوشاک مادران در حد متعارف برعهده پدر کودک است، و البته هیچ کس جز به اندازه توانش مکلّف نیست. هیچ مادری نباید از رهگذر فرزندش زیان ببیند، و هیچ پدری نیز نباید از رهگذر فرزندش زیانی به او برسد. و اگر پدرِ کودک بمیرد، مانند همان تکلیفی که برعهده پدر بود، برعهده وارث اوست. و اگر پدر و مادر با رضایت و مشورت یکدیگر بخواهند فرزندشان را قبل از دو سال از شیر باز گیرند بر آن دو گناهی نیست. و شما پدران اگر خواستید برای فرزندانتان دایه‌ای بگیرید بر شما گناهی نیست، به شرط آن که حقوقی را که باید به مادر او می‌دادید، به خوبی و شایستگی بپردازید. و از حسابرسی خدا و عذاب او پروا کنید، و بدانید که خدا به آنچه می‌کنید بیناست.'),
(241, 2, 234, 'و کسانی از شما که جان می‌سپارند و زنانی از خود بر جای می‌گذارند، زنانشان چهار ماه و ده روز انتظار می‌کشند و از ازدواج خودداری می‌کنند. پس هرگاه به پایان مهلت مقرّر خود رسیدند، می‌توانند درباره خود تصمیم بگیرند. بنابراین، بر شما خویشاوندان شوهر، در آنچه آنان به طور پسندیده درباره خود انجام می‌دهند و همسری برمی گزینند گناهی نیست، و خداوند به آنچه انجام می‌دهید آگاه است.'),
(242, 2, 235, 'بر شما گناهی نیست در سخنی که آن را به اشاره برای خواستگاری از زنان در عدّه بر زبان می‌آورید و همچنین در اموری که درباره آنان در دل نهان می‌دارید. خدا می‌دانست که شما ازدواج با آنان را یاد می‌کنید، ازاین رو شما را از آن بازنداشت، ولی با آنان قول و قرار پنهانی مگذارید، مگر آن که به طور پسندیده سخن گویید، و با آنان پیوند ازدواج مبندید تا آن مهلت مقرر به پایان خود رسد، و بدانید که خداوند آنچه را در دل دارید می‌داند، پس، از کیفر او بترسید و بدانید که خداوند آمرزنده و بردبار است.'),
(243, 2, 236, 'اگر همسرانی را که با آنان آمیزش نکرده‌اید، یا همسرانی را که برایشان مهری معیّن نساخته‌اید طلاق گویید، بر شما گناهی نیست، ولی به زنانی که برایشان مهری مقرّر نداشته‌اید، به هنگام طلاق کالایی در حد متعارف بدهید. توانگر به اندازه توان خود و تنگدست نیز به قدر وُسع خود این تکلیف را برعهده دارد. این برعهده نیکوکاران نهاده شده است.'),
(244, 2, 237, 'و اگر پیش از آن که با آنان آمیزش کنید طلاقشان دادید و برایشان مهری معیّن کرده‌اید، پرداختن نیمی از آنچه معیّن کرده‌اید برعهده شماست، مگر این که آنها گذشت کنند یا کسی که پیوند ازدواج به دست اوست (شوهر یا ولیّ زن) ببخشد، و گذشتِ شما به تقوا نزدیک‌تر است. و در میان خود بخشش و بزرگواری را فراموش نکنید، که خدا به آنچه انجام می‌دهید بیناست.'),
(245, 2, 238, 'بر نمازها، به ویژه بر نماز میانه، مواظبت کنید که مبادا از شما فوت شود یا آداب و شرایط آن رعایت نشود، و فرمان‌های خدا را از روی خضوع و با اخلاص انجام دهید.'),
(246, 2, 239, 'پس اگر بیم آن دارید که نتوانید همه آداب و شرایط نماز را رعایت کنید، پیاده یا سواره، در حال توقف یا حرکت نماز بگزارید، ولی هنگامی که ایمن شدید، با برپاداشتن نماز، خدا را یاد کنید آن گونه که نماز و سایر احکامی را که نمی‌دانستید به شما آموخت.'),
(247, 2, 240, 'و کسانی از شما که در آستانه مرگ قرار می‌گیرند و همسرانی برجای می‌گذارند، باید برای همسرانشان وصیت کنند که تا یک سال پس از مرگشان آنان را از اموالی که برجای می‌گذارند بهره مند سازند، و از خانه‌ای که در آن سکونت دارند بیرونشان نکنند، ولی اگر خود بیرون رفتند، بر شما (خویشاوندان شوهر) در آنچه آنها درباره خود به طور پسندیده انجام می‌دهند گناهی نیست، و خدا مقتدری حکیم است.'),
(248, 2, 241, 'برای همه زنانی که طلاق داده می‌شوند، کالایی درخور و شایسته مقرّر گردیده است. این برعهده شوهرانِ تقواپیشه ثابت شده است.'),
(249, 2, 242, 'خداوند آیات خود را این گونه برای شما بیان می‌کند، باشد که به خرد دریابید.'),
(250, 2, 243, 'آیا از داستان کسانی که هزاران نفر بودند و از بیم مرگ از خانه هایشان بیرون رفتند آگاه نشده‌ای که خدا به آنان گفت: بمیرید، سپس آنان را زنده کرد؟ به راستی که خدا دارای بخشش است و از فضل خود به آن مردم حیات دوباره بخشید، ولی بیشتر مردم سپاسگزار نعمت‌های خدا نیستند.'),
(251, 2, 244, 'حیات و نیکبختی شما مؤمنان در جهاد است، پس در راه خدا پیکار کنید و بدانید که خدا سخنان شما را می‌شنود و از راز دل‌ها خبر دارد.'),
(252, 2, 245, 'کیست آن کس که با پرداخت هزینه جهاد وامی نیکو به خدا دهد؟ هر کس چنین کند، خدا آن را برای او چندین برابر می‌کند، و خداست که از اموال شما می‌کاهد، و هموست که آن را گسترش می‌دهد و بر آن می‌افزاید، و شما به سوی او بازگردانده می‌شوید تا پاداش وامی را که به او داده‌اید دریافت کنید.'),
(253, 2, 246, 'آیا از داستانِ آن سران بنی اسرائیل که پس از موسی بودند آگاه نشده‌ای آن گاه که به پیامبری از خودشان گفتند: برای ما فرمانروایی بگمار، که اگر ما فرمانروایی داشته باشیم تحت فرمان او در راه خدا می‌جنگیم. گفت: اگر پیکار در راه خدا بر شما مقرّر شود، آیا تصور می‌کنید که پیکار نکنید؟ گفتند: ما را چه دلیلی است که در راه خدا پیکار نکنیم، در حالی که بر اثر سلطه دشمن از بهره مندی از شهرها و پسرانمان بازداشته شده‌ایم؟ ولی هنگامی که جنگ در راه خدا بر آنان نوشته شد، جز اندکی از آنان همه روی برتافتند و ستمکار شدند، و خدا به ستمکاران داناست.'),
(254, 2, 247, 'و پیامبرشان به آنان گفت: خداوند طالوت را برای شما به فرمانروایی برانگیخته است. گفتند: چگونه او را بر ما فرمانروایی باشد؟ در حالی که ما به فرمانروایی از او سزاوارتریم، زیرا حکومت بر بنی اسرائیل در اختیار نیاکان ما بوده است. علاوه بر این او از گشایش مالی برخوردار نیست. پیامبرشان گفت: به یقین خدا او را برگزیده و بر شما برتری داده، و او را در دانش اداره امور و نیروی بدنی فزونی بخشیده است، و خدا فرمانرواییِ خود را به هر که بخواهد عطا می‌کند، و خدا دارای بخششی گسترده است و می‌داند چه کسی درخورِ بخشش است.'),
(255, 2, 248, 'پیامبرشان به آنان گفت: نشانه الهی بودن فرمانروایی او این است که آن صندوق معهود ـ که فرشتگان آن را حمل می‌کنند ـ به سوی شما خواهد آمد، صندوقی که در آن آرامشی است از جانب پروردگارتان و یادگاری از آنچه موسی و هارون و خاندانشان بر جای نهاده‌اند. اگر مؤمن باشید، قطعاً در آمدنِ آن صندوق، برای شما نشانه‌ای بر درستیِ فرمانروایی اوست.'),
(256, 2, 249, 'و هنگامی که طالوت با سپاهیان خود از شهر فاصله گرفت، گفت: خداوند شما را به وسیله نهری خواهد آزمود. هر کس از آن بیاشامد از سپاهیان من نیست، و هر کس از آن نچشد از سپاهیان من است، مگر کسی که مشتی آب به دست خود برگیرد و بنوشد، که من او را از سپاهیان جدا نمی‌کنم، هر چند او را از خود نمی‌دانم. پس وقتی به آن نهر رسیدند، جز اندکی از ایشان از آن آشامیدند. و هنگامی که طالوت و کسانی که ایمان داشتند و با او بودند، از نهر آب گذشتند، گفتند: ما امروز در برابر جالوت و لشکریانش تاب و توانی نداریم. و آنان که بر این باور بودند که به دیدار خدا خواهند رفت، در پاسخ گفتند: چه بسیار گروهی اندک که به اذن خدا بر گروهی بسیار پیروز شده‌اند، و خدا با صبرپیشگان است.'),
(257, 2, 250, 'و چون با جالوت و سپاهیان او رو به رو شدند، گفتند: پروردگارا، بر ما صبری فراوان فرو ریز، و گام هایمان را استوار دار، و ما را بر این مردم کفرپیشه پیروز گردان.'),
(258, 2, 251, 'پس به اذن خدا آنان را شکست دادند و داود جالوت را کشت و خداوند به او فرمانروایی و حکمت (دانش معارف و احکام دین) داد، و از آنچه می‌خواست به او آموخت. و اگر خدا غریزه دفاع را در انسان‌ها ننهاده بود و به این وسیله تجاوز برخی از مردم را با برخی دیگر دفع نکرده بود سراسر روی زمین را تباهی فراگرفته بود، ولی خدا صاحب تفضّل است و جهانیان را از فضل خود بهره مند می‌سازد.'),
(259, 2, 252, 'اینها آیات خداست که ما آن‌ها را به حق بر تو تلاوت می‌کنیم، و به راستی که تو از فرستادگانی.'),
(260, 2, 253, 'اینان همان پیامبران بلندمرتبه‌ای هستند که برخی شان را بر برخی دیگر برتری دادیم. از میان آنان کسی است که خدا با او سخن گفت و برخی از آنها را به چندین درجه بالا برد، و به عیسی پسر مریم معجزاتی روشن و روشنگر دادیم و او را به وسیله (روح القدس) تأیید نمودیم. و اگر خدا می‌خواست، کسانی که پس از آنان بودند، بعد از آن که دلایلی روشن برایشان آمد، به جنگ با یکدیگر برنمی خاستند، ولی خود اختلاف کردند، از میان آنان کسانی ایمان آوردند و کسانی کافر شدند. و اگر خدا می‌خواست، با وجود اختلافاتی که میانشان وجود داشت، با یکدیگر نمی‌جنگیدند، ولی خدا آنچه را بخواهد انجام می‌دهد، و او خواسته است که امور جهان براساس علل و اسباب جاری پدید آید.'),
(261, 2, 254, 'ای کسانی که ایمان آورده‌اید، از آنچه به شما ارزانی داشته‌ایم انفاق کنید، پیش از آن که روزی بیاید که در آن نه دادوستدی هست و نه دوستی و نه شفاعتی. و کسانی که با ترک انفاق به کفر روی می‌آورند سخت ستمکارند.'),
(262, 2, 255, 'تنها خدا ـ که هیچ معبودی جز او شایسته پرستش نیست ـ زنده و برپادارنده هستی است، نه خوابی سبک او را فرا می‌گیرد و نه خوابی سنگین که از اداره امور جهان بازماند. آنچه در آسمان‌ها و آنچه در زمین است از آنِ اوست و اسباب و علل همه از او فرمان می‌گیرند. کیست آن که جز به اذن او نزد وی شفاعت کند؟ و انجام کاری را سبب شود آنچه را که پیش روی شفاعتگران است و از آن آگاهند و آنچه را که در پی دارند و از آنان نهان است، همه را می‌داند، و آنها به چیزی از دانش او ـ جز آنچه برایشان خواسته است ـ احاطه ندارند. قلمرو فرمانروایی اش آسمان‌ها و زمین را فراگرفته است، و نگهداری آن دو بر او دشوار نیست، و اوست بلندمرتبه و بزرگ.'),
(263, 2, 256, 'در گرایش مردم به دین اجباری نیست، زیرا راه درست به خوبی از راه نادرست مشخص شده است. پس هر کس به طاغوت‌ها کافر شود و به خدا ایمان بیاورد، قطعاً به استوارترین دستاویز که برای آن گسستن نیست چنگ زده است، و خدا شنونده گفتار مردم و آگاه از کفر و ایمان آنهاست.'),
(264, 2, 257, 'خداوند سرپرست کسانی است که ایمان آورده‌اند. آنان را از تاریکی‌ها بیرون می‌بَرد و به سوی روشنایی (شناخت معارف و اطاعت از خدا) هدایت می‌کند. و کسانی که کفر ورزیده‌اند، سرپرستانشان طاغوت هایند که آنان را از نور (فطرت خداشناسی) به در می‌برند و به تاریکی‌های شرک و کفر و عصیان می‌کشانند. اینان همدم آتشند و در آن جاودانه‌اند.'),
(265, 2, 258, 'آیا به داستان کسی که چون خدا به او سلطنت داده بود با ابراهیم درباره پروردگارش به محاجّه برخاست، نظر نینداخته‌ای؟ آن گاه که ابراهیم به او گفت: پروردگار من کسی است که حیات می‌بخشد و می‌میراند. او گفت: منم که حیات می‌بخشم و می‌میرانم، پس من پروردگار تو و پروردگار این جهانم. ابراهیم گفت: خداست که خورشید را از مشرق برمی آورَد، تو آن را از مغرب برآور. پس آن کسی که کفر ورزیده بود درمانده و مبهوت شد. و خدا مردم ستمکار را هدایت نمی‌کند.'),
(266, 2, 259, 'یا مانند داستان کسی که بر دیاری گذر کرد در حالی که دیوارهایِ آن بر بام هایش فروریخته و یکسره ویران شده بود. گفت: چگونه خداوند مردم این سرزمین را پس از مرگشان زنده می‌کند؟ پس خداوند او را به مدّت صد سال میراند، آن گاه زنده اش کرد و به او گفت: چه مدت در این جا مانده‌ای؟ گفت: یک روز یا پاره‌ای از روز درنگ کرده‌ام. فرمود: نه، بلکه صد سال درنگ کرده‌ای. به خوراک و نوشیدنی خود بنگر که در این مدت دگرگون نشده است، و گمان مکن که چون دگرگون نشده‌ای، این مدت طولانی بر تو نگذشته است، به الاغ خود بنگر که به استخوان‌هایی چند تبدیل شده است. ما تو را میراندیم و پس از گذشت صد سال به تو حیات بخشیدیم تا آنچه باید تحقق یابد و تا تو را برای مردم نشانه‌ای [بر روز رستاخیز] قرار دهیم. اکنون به استخوان‌های الاغت بنگر که چگونه آنها را رشد می‌دهیم و فراز می‌آوریم سپس بر آنها گوشت می‌پوشانیم. پس هنگامی که امر زنده شدن مردگان بر او آشکار شد، گفت: می‌دانم که خدا بر انجام هر کاری تواناست.'),
(267, 2, 260, 'و یاد کن هنگامی را که ابراهیم گفت: پروردگارا، به من بنمای که چگونه مردگان را زنده می‌کنی؟ فرمود: آیا علاوه بر ندانستن چگونگی آن، ایمان نیز نیاورده‌ای؟ گفت: چرا، ولی پرسیدم تا دلم آرام گیرد. فرمود: چهار پرنده از این پرندگان را بگیر، سپس آنها را در حالی که با خود مأنوس ساخته‌ای بکُش و قطعه قطعه کن، آن گاه بر هر کوهی بخشی از آنها را قرار ده، سپس آنها را فراخوان که شتابان به سوی تو می‌آیند، و بدان که خداوند مقتدری است که هیچ چیز از او پنهان نیست و حکیمی است که همه کارهایش براساس حکمت است.'),
(268, 2, 261, 'حکایت کسانی که اموال خود را در راه خدا انفاق می‌کنند همچون حکایت دانه‌ای است که هفت خوشه برویاند که در هر خوشه‌ای صد دانه باشد. و خداوند برای هر که بخواهد آن را دو چندان می‌کند، و خداوند را بخششی گسترده است و می‌داند چه کسی درخورِ بخشش است.'),
(269, 2, 262, 'کسانی که اموال خود را در راه خدا انفاق می‌کنند، سپس در پی انفاق خود نه کمترین منّتی می‌گذارند و نه اندک آزاری می‌رسانند، پاداش خود را نزد پروردگارشان خواهند داشت، و نه ترسی آنان را فرو می‌گیرد و نه اندوهگین می‌شوند.'),
(270, 2, 263, 'با تهیدستان سخنی نیکو گفتن و از خطای آنان درگذشتن، بهتر از صدقه‌ای است که آزاری در پی آن باشد. شما نیز خود را به اخلاق الهی متصف کنید، چرا که خداوند بی نیاز است و هرگز بخشش خود را بزرگ نمی‌شمرد تا بدین وسیله به کسی آزاری برساند، و بردبار است و در کیفر خطاکاران شتاب نمی‌کند.'),
(271, 2, 264, 'ای کسانی که ایمان آورده‌اید، صدقه‌های خود را با منّت نهادن و آزار رساندن تباه نکنید، همچون کسی که مال خود را برای نشان دادن به مردم، انفاق می‌کند و خدا را در فراخوانی به انفاق باور نمی‌کند و به سرای آخرت که روز پاداش و جزاست ایمان ندارد. پس حکایت او همچون حکایت صخره‌ای است صاف که بر آن خاکی بوده و بارانی تند به آن رسیده و خاک را شسته و آن را به صورت سنگی صاف برجای گذاشته است. آری همان گونه که کشاورز از این خاک و باران محصولی به دست نمی‌آورد، ریاکاران نیز نمی‌توانند به اندکی از آنچه فراهم کرده‌اند دست بیابند، و خداوند مردم کفرپیشه را هدایت نمی‌کند.'),
(272, 2, 265, 'و مَثَل کسانی که اموال خود را بدون منّت و ریا و تنها برای دستیابی به خشنودی خدا انفاق می‌کنند و در خلوص نیّت، خود را چنان که باید پایدار می‌دارند، همچون مَثَل بوستانی در زمینی حاصلخیز است که باران فراوانی به آن رسیده و محصولش را دو چندان داده و اگر باران فراوان هم بر آن نبارد دست کم اندک بارانی به آن می‌رسد و باروبری می‌دهد. و خدا به آنچه انجام می‌دهید بیناست.'),
(273, 2, 266, 'آیا یکی از شما دوست دارد که او را بوستانی آکنده از درختان خرما و انگور باشد و از زیر درختان آن جویبارها روان و در آن باغ از هر نوع میوه‌ای داشته باشد، آن گاه پیری به سراغش بیاید در حالی که فرزندانی ناتوان دارد و گردبادی همراه با آتش به بوستانش برسد و بوستان یکسره بسوزد؟ این مَثَل کسی است که انفاق کند، سپس منّت نهد یا آزاری برساند. بدین سان خدا آیات و نشانه‌ها را برای شما بیان می‌کند، باشد که بیندیشید.'),
(274, 2, 267, 'ای کسانی که ایمان آورده‌اید، از چیزهای مرغوبی که به دست آورده‌اید و از آنچه از زمین برای شما برآورده‌ایم انفاق کنید، و مبادا در پی نامرغوب آن بروید که آن را انفاق کنید، در صورتی که [اگر به خودتان می‌دادند] آن را نمی‌گرفتید، مگر این که از عوض آن می‌کاستید. بدانید که خدا بی نیاز و ستوده است، پس چنان که شایسته اوست فرمانش را اطاعت کنید.'),
(275, 2, 268, 'این شیطان است که وقتی می‌خواهید از اموال مرغوب خود انفاق کنید شما را به تهیدستی تهدید می‌کند و به بخل که خصلتی زشت است فرمان می‌دهد و خداست که شما را به آمرزش و فضل خود وعده می‌دهد، و خدا را بخششی گسترده و فضلی بی پایان است و می‌داند چه کسی درخورِ بخشش است.'),
(276, 2, 269, 'حکمت را به هر که بخواهد می‌بخشد، و به هر کس حکمت داده شود، خیر فراوان به او داده شده است، و این را جز صاحبان خرد درنمی یابند.'),
(277, 2, 270, 'و هر چه به فرمان خدا به تهیدستان انفاق کرده‌اید و یا هر مالی به نذر بر خود واجب کرده‌اید که به آنان بپردازید، خدا به آن آگاه است. و کسانی که با ترک انفاق و وفا نکردن به نذرِ خود به مستمندان ستم می‌کنند، یاورانی که عذاب خدا را از آنان بازدارند نخواهند داشت.'),
(278, 2, 271, 'اگر صدقه‌ها را آشکارا بپردازید که دیگران را نیز به آن ترغیب کنید نیکوست، و اگر آنها را نهان دارید و به بینوایان بپردازید، از آن روی که به خلوص نیت نزدیک‌تر است برای شما بهتر است و [در هر حال] خداوند پاره‌ای از گناهانتان را از شما می‌زداید، و خدا به آنچه می‌کنید آگاه است.'),
(279, 2, 272, 'ای پیامبر، اگر برخی از مردم شرایط و آداب انفاق را رعایت نمی‌کنند غم مدار، زیرا به راه آوردنِ آنان برعهده تو نیست بلکه خداست که هر کس را بخواهد به راه می‌آورَد. و ای مؤمنان هر مالی را که انفاق می‌کنید به سود خودتان است، بدین شرط که آن را جز برای دستیابی به خشنودی خدا انفاق نکنید. و هر مالی را که می‌بخشید به طور کامل به شما بازگردانده می‌شود و بر شما ستم نمی‌رود.'),
(280, 2, 273, 'صدقات برای آن تهیدستانی است که در راه خدا محصور شده‌اند و نمی‌توانند برای کسب درآمد در زمین سیر و سفر کنند. از آن جا که خویشتن دارند و فقر خود را آشکار نمی‌کنند، کسی که از حالشان بی خبر است آنان را توانگر می‌پندارد و تو از نشانشان بینوایی آنان را درمی یابی. آنان با وجود نیازمندی شدید به اصرار از مردم چیزی نمی‌خواهند. ای مؤمنان، هر مالی را که انفاق کنید خدا به آن داناست.'),
(281, 2, 274, 'کسانی که اموالشان را در شب و روز، نهان و آشکارا انفاق می‌کنند، پاداش کردار خود را نزد پروردگارشان خواهند داشت، و نه ترسی آنان را فرو می‌گیرد و نه اندوهگین می‌شوند.'),
(282, 2, 275, 'کسانی که ربا می‌خورند، امور زندگی و معاش خود را برپا نمی‌دارند مگر مانند برپاداشتن کسی که شیطان بر اثر تماس، او را آشفته کرده و خرد را از او ربوده است. این بدان سبب است که آنان گفتند: جز این نیست که دادوستد هم مانند رباست. و خداوند دادوستد را حلال و ربا را حرام کرده است. پس هر کس از جانب پروردگارش پندی به او برسد و از کار نکوهیده اش دست بردارد، آنچه گذشته است از آنِ اوست و در برابر آن کیفر نمی‌شود و از آن پس کارش با خداست (بسا جبران گذشته را از او بخواهد و بسا هیچ حکمی برای گذشته اش مقرّر نکند.) و کسانی که به آنچه خدا از آن نهی کرده است بازگردند همدم آتشند و در آن جاودانه خواهند بود.'),
(283, 2, 276, 'خداوند ربا را کاهش می‌دهد و به تدریج آن را نابود می‌کند، ولی صدقه‌ها را رشد می‌دهد و آن را افزون می‌گرداند. رباخواران بسیار ناسپاس و گنهکارند، و خدا هیچ ناسپاس و گنه پیشه‌ای را دوست نمی‌دارد.'),
(284, 2, 277, 'کسانی که ایمان آورده و کارهای شایسته کرده و نماز برپا داشته و زکات پرداخته‌اند، نزد پروردگارشان پاداش کردار خویش را خواهند داشت و نه ترسی آنها را فرا می‌گیرد و نه اندوهگین می‌شوند.'),
(285, 2, 278, 'ای کسانی که ایمان آورده‌اید، از خدا پروا کنید، و اگر واقعاً مؤمنید، ربایی را که از شما نزد مردم باقی مانده است واگذارید و آن را مطالبه نکنید.'),
(286, 2, 279, 'پس اگر چنین نکنید و به رباخواری ادامه دهید، بدانید که از جانب خدا و رسولش پیکاری بر ضد شما برپا خواهد شد. و اگر توبه کنید، سرمایه هایتان ـ که آمیخته با ربا نیست ـ از آنِ شماست، نه ربا می‌گیرید که ستم کنید و نه سرمایه‌های شما توقیف می‌شود که به شما ستم رود.'),
(287, 2, 280, 'و اگر تنگدستی که از پرداخت وام خود ناتوان است وجود داشته باشد، باید تا آن گاه که تمکّن یابد به او مهلتی دهید. البته بخشیدن وام به او و صدقه به حساب آوردن آن برای شما بهتر و سودمندتر است، اگر بدانید.'),
(288, 2, 281, 'و بترسید از روزی که در آن به سوی خدا بازگردانده می‌شوید، سپس به هر کسی آنچه فراهم آورده است به تمام و کمال داده می‌شود و بر آنان ستم نمی‌رود.'),
(289, 2, 282, 'ای کسانی که ایمان آورده‌اید، هرگاه برخی از شما [به وسیله قرض یا نسیه] به برخی دیگر بدهکار شدید که باید آن را تا سرآمدی معیّن بپردازید، آن را بنویسید، و باید نویسنده‌ای در میان شما متن قرارداد را به حق و عدل بنویسد، و هیچ نویسنده‌ای نباید از نوشتن آن ـ چنان که خدا به او آموخته است ـ خودداری کند. پس باید او بنویسد و کسی که دیْن برعهده اوست (بدهکار) بر وی املا کند و از حساب و کیفر خدا، پروردگار خود، بترسد و به هنگام املا چیزی از آن نکاهد. و اگر کسی که دَیْن برعهده اوست، کم خرد یا ناتوان باشد، یا نتواند متن قرارداد را املا کند، ولیِّ او به حق و عدل املا نماید، و باید علاوه بر نوشتنِ سند از میان مردانتان دو تن را گواه بگیرید، و اگر دو مرد نبودند، یک مرد و دو زن از گواهانی که می‌پسندید (شهادت آنها پذیرفته است) گواه باشند. تعیین دو زن به جای یک مرد برای این است که بیم آن می‌رود که یکی از آن دو زن فراموش کند که در این صورت باید یکی از آن دو که به خاطر دارد دیگری را یادآوری کند. و گواهان نباید وقتی برای [ادای] شهادت دعوت می‌شوند خودداری کنند. و از نوشتنِ دَیْنی که آن را باید تا سرآمدش بپردازید ـ چه کم باشد یا زیاد ـ ملول نشوید. این کار نزد خدا عادلانه‌تر و برای شهادت استوارتر و به مصون ماندن شما از تردید نزدیک‌تر است. آری، تمامی قراردادهای خود را بنویسید، مگر این که دادوستدی نقدی باشد که کالا و بهای آن را میان خود دست به دست می‌گردانید که در این صورت بر شما باکی نیست که آن را ننویسید، ولی هرگاه خرید و فروش کردید بر آن گواه بگیرید، و مبادا نویسنده و گواه از سوی شما زیانی ببیند که اگر چنین کنید خدا را نافرمانی کرده‌اید. و از خدا پروا کنید و بدانید که خداوند احکام دین را به شما می‌آموزد و خدا به هر چیزی داناست.'),
(290, 2, 283, 'و اگر در سفر بودید و برای نوشتنِ قرارداد نویسنده‌ای نیافتید، وثیقه‌هایی که به طلبکار سپرده می‌شود، جایگزین سندِ مکتوب خواهد شد. و اگر یکی از شما دیگری را امین دانست [و به وسیله قرض یا نسیه به شما بدهکار شد، نوشتن قرارداد و گرفتن شاهد ضروری نیست]، پس آن کسی که امین دانسته شده است باید بدهکاری خود را که در مورد آن او را امین دانسته‌اند، بپردازد و از حساب و کیفر خدا که پروردگار اوست پروا کند و حقی را که برعهده اوست انکار نکند. و اگر بر امری شاهد بودید شهادت خود را کتمان نکنید، و هر که آن را کتمان کند دلش گنهکار است، و خدا به آنچه می‌کنید داناست.'),
(291, 2, 284, 'آنچه در آسمان‌ها و آنچه در زمین است از آنِ خداست، و اگر صفات و حالات نیک و بدی (همچون کفر و ایمان، دوستی و دشمنی و تصمیم و اراده) را که در نفوس شما استقرار یافته است آشکار کنید یا آنها را نهان دارید، خداوند شما را به آن محاسبه می‌کند، سپس هر که را بخواهد می‌آمرزد و هر که را بخواهد عذاب می‌کند، و خدا بر هر کاری تواناست.'),
(292, 2, 285, 'پیامبر به آنچه از جانب پروردگارش به سوی او فرو فرستاده شده ایمان آورده است، و مؤمنان نیز به آن ایمان آورده‌اند. آری، همگی به خدا و فرشتگانش و کتاب‌ها و پیامبرانش ایمان آورده‌اند، و گفتند: ما میان هیچ یک از پیامبران او جدایی نمی‌افکنیم و به همه آنها ایمان داریم و گفتند: دعوت خدا را پذیرفتیم و اطاعت کردیم. پروردگارا، آمرزش تو را می‌خواهیم و بازگشت همگان به سوی توست.'),
(293, 2, 286, 'خداوند هیچ کس را جز به اندازه توانش تکلیف نمی‌کند. آنچه [نیکی] به دست آورده به سود او و آنچه [بدی] فراهم کرده به زیان اوست. پروردگارا، اگر تکلیفی را فراموش نمودیم یا خطایی کردیم ما را بر آن مؤاخذه مکن. پروردگارا، هیچ بار گرانی برعهده ما مگذار، چنان که آن را برعهده امّت‌هایی که پیش از ما بودند نهادی. پروردگارا، و کیفر گناهانمان را که قدرت تحمّل آن را نداریم بر ما تحمیل مکن. گناهانمان را از ما بزدای و ما را ببخشای و بر ما رحمت آور. تویی مولای ما، پس ما را یاری فرما و بر مردم کفرپیشه پیروز گردان.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الف. لام. میم.'),
(295, 3, 2, 'تنها خدا که هیچ معبودی جز او شایسته پرستش نیست، زنده و برپا دارنده هستی است.'),
(296, 3, 3, 'این کتاب را که سراسر حق است و هرگز باطل بدان راه نمی‌یابد و کتاب‌های آسمانی پیش از خود را تصدیق می‌کند، بر تو فرو فرستاد، و تورات و انجیل را پیش از آن نازل کرد،'),
(297, 3, 4, 'تا وسیله هدایتی برای مردم باشند، و جداکننده حق از باطل را نیز فرو فرستاد. به یقین کسانی که به آیات خدا کفر ورزیده‌اند عذابی سخت خواهند داشت، و خداوند شکست ناپذیر و انتقام گیرنده است.'),
(298, 3, 5, 'او به کسانی که درخورِ کیفرند آگاه است، که هیچ چیزی نه در زمین و نه در آسمان بر خدا پوشیده نیست.'),
(299, 3, 6, 'نه تنها به حال و کارتان داناست که بی رخصت او راهی نمی‌پویید چرا که اوست آن که شما را در رحم‌ها هرگونه بخواهد صورتگری می‌کند. معبودی شایسته پرستش جز او که مغلوب نمی‌شود و کارهایش همه از روی حکمت است وجود ندارد.'),
(300, 3, 7, 'اوست آن که این کتاب را بر تو فرو فرستاد. بخشی از آن، آیاتی است با معانی استوار و روشن که بخش مرجع این کتاب است و سایر آیات به آنها تفسیر می‌شود، و بخش دیگر آیاتی است که معنای استوار آنها با معانی نااستوار مشتبه می‌شود. کسانی که در دل هایشان انحرافی هست، از قرآن آنچه را که مشتبه است دنبال می‌کنند تا گمراهی مردم را طلب کنند و تا از این رهگذر بر خاستگاه احکام و معارف قرآن دست یابند، در حالی که هیچ کس جز خدا خاستگاه آنها را نمی‌داند، و امّا کسانی که در شناخت معارف الهی پایدارند، می‌گویند: ما به قرآن ایمان آورده‌ایم، زیرا قرآن ـ چه آیات روشن و استوار آن و چه متشابهاتش ـ همه از جانب پروردگار ماست. و این حقیقت را جز صاحبان خرد درنمی یابند.'),
(301, 3, 8, 'آنان می‌گویند: پروردگارا، پس از آن که ما را هدایت کردی دل هایمان را از شناخت دین منحرف مکن و از جانب خود رحمتی به ما ببخشای که بسیار بخشنده تویی.'),
(302, 3, 9, 'پروردگارا، بی تردید تو مردمان را در روزی که هیچ تردیدی در آن نیست برای داوری گرد می‌آوری. این وعده خداست و قطعاً خدا خلف وعده نمی‌کند.'),
(303, 3, 10, 'کسانی که کافر شده‌اند، هرگز اموال و فرزندانشان چیزی از نیاز آنان را به خدا برطرف نمی‌کند و از عذابی که خدا برایشان آماده کرده است نمی‌کاهد، و اینانند که هیزم آتش دوزخ‌اند.'),
(304, 3, 11, 'شیوه این کافران همچون شیوه فرعونیان و کسانی است که پیش از آنان بودند که آیات ما را دروغ انگاشتند و خدا آنان را به همان گناهانشان عذاب کرد، و خدا سخت کیفر است.'),
(305, 3, 12, 'به کسانی که کفر ورزیده‌اند بگو: دیری نمی‌گذرد که مغلوب خواهید شد و شما را دسته جمعی به سوی جهنم روان خواهند ساخت، و جهنم بد بستری است.'),
(306, 3, 13, 'به آنان بگو: برای شما در مورد آن دو گروهی که در جنگ بدر با یکدیگر روبه رو شدند نشانه‌ای بود بر این که خدا هر که را بخواهد پیروز می‌گرداند. در آن جنگ، گروهی در راه خدا پیکار می‌کردند و گروه دیگر کافر بودند که به هنگام نبرد، پیکارگرانِ راه خدا را به دید چشم دو برابر تعدادی که بودند می‌دیدند، ازاین رو هراسان شدند و گریختند. و خدا هر که را بخواهد به یاری خود تأیید می‌کند. به راستی در این ماجرا برای صاحبان بصیرت عبرتی است.'),
(307, 3, 14, 'دلدادگی به خواستنی‌ها از قبیل: زنان، فرزندان پسر، همیان‌های انباشته از زر و سیم، اسب‌های نشاندار، دام‌ها و کشتزارها برای مردم آراسته شده است. اینها بهره زندگی دنیاست که فانی شدنی است، و سرانجامِ نیکو فقط نزد خداست.'),
(308, 3, 15, 'بگو: آیا شما را به بهتر از اینها خبر دهم؟ برای کسانی که تقوا پیشه کرده‌اند، نزد پروردگارشان باغ‌هایی است انبوه از درختان که از زیر آنها جوی‌ها روان است و در آن جا برای همیشه ماندگارند، و برای آنان همسرانی پاک و پاکیزه و نیز خشنودی خدا خواهد بود، و خدا به بندگان خود بیناست و کسانی را که درخورِ این موهبت‌ها هستند به خوبی می‌شناسد.'),
(309, 3, 16, 'تقواپیشگان کسانی اند که می‌گویند: پروردگارا، ما ایمان آوردیم، پس گناهانمان را بیامرز، و ما را از عذاب آتش در امان دار.'),
(310, 3, 17, 'آنان در اطاعت خدا و پرهیز از گناهان و در برابر ناگواری‌ها شکیبایند، و در سخن راست می‌گویند و در عبادت خدا فروتن اند و از اموال خود به مستمندان انفاق می‌کنند و در سحرگاه‌ها از خدا آمرزش می‌طلبند.'),
(311, 3, 18, 'خداوند که در کارهایش عدالت را برپاداشته گواهی داده است که معبودی شایسته پرستش جز او نیست، و فرشتگان و عالِمان نیز بر آن گواهی داده‌اند. آری، معبودی شایسته پرستش جز او که شکست ناپذیر و حکیم است، نیست.'),
(312, 3, 19, 'دین نزد خدا تسلیم شدن در برابر او و پذیرفتن احکام و حقایقی است که او فرو فرستاده است، و یهودیان و مسیحیان که کتاب آسمانی به آنان داده شده است در دین اختلاف نکردند و مذاهب و ادیان گوناگون پدید نیاوردند مگر پس از آن که دانش بدیشان رسیده بود، و اختلافشان به سبب برتری جویی و ستمی بود که میانشان وجود داشت. آنان به آیات خدا که بر پیامبران فرو فرستاده شده بود کافر شدند، و هر کس به آیات خدا کافر شود، به سزایش خواهد رسید، چرا که خداوند اعمال بندگان را به سرعت و همان گاه که انجام می‌شود حسابرسی می‌کند.'),
(313, 3, 20, 'پس اگر با تو به محاجّه برخاستند و گفتند: اختلاف ما از روی ستم و برتری جویی نیست، بلکه فهم‌ها و برداشت‌ها گوناگون است به آنان بگو: من با تمام وجود تسلیم خدا هستم، و کسانی که مرا پیروی کرده‌اند نیز این گونه‌اند. و به اهل کتاب و به مشرکان که از کتاب آسمانی بی بهره‌اند بگو: آیا شما تسلیم خدا شده‌اید؟ پس اگر تسلیم شوند هدایت یافته‌اند، و اگر روی برتابند بر تو فقط ابلاغ پیام است و خداوند به حال و کار بندگان بیناست.'),
(314, 3, 21, 'کسانی که به آیات خدا کفر میورزند و پیامبران را بی هیچ حقی می‌کشند و از میان مردم کسانی را که به عدل و داد فرمان می‌دهند به قتل می‌رسانند، آنان را به عذابی دردناک در دنیا و آخرت نوید ده.'),
(315, 3, 22, 'آنان کسانی اند که اعمالشان در دنیا و آخرت تباه گشته و یاورانی نخواهند داشت.'),
(316, 3, 23, 'آیا به کسانی که از کتاب آسمانی اندک بهره‌ای به آنان داده شده است ننگریسته‌ای؟ آنان به کتاب خدا فراخوانده می‌شوند تا میانشان حکم کند، سپس گروهی از آنان پشت می‌کنند در حالی که رویگردانند.'),
(317, 3, 24, 'این رویگردانی بدان سبب است که گفتند: آتش دوزخ جز روزهایی اندک به ما نمی‌رسد [هر چند گناهانمان بسیار باشد]، و همین سخن دروغ که آن را ساخته و به خود تلقین کرده تا باورش نموده‌اند، آنان را در دینشان فریفته است.'),
(318, 3, 25, 'پس چگونه خواهد بود حالشان هنگامی که آنان را برای روز قیامت که هیچ تردیدی در آن نیست گرد آوریم و به هر کسی آنچه فراهم آورده است بی کم و کاست داده شود؟ و بر آنان ستم نمی‌رود.'),
(319, 3, 26, 'بگو: خداوندا، ای کسی که فرمانروایی ملک توست، به هر که خواهی فرمانروایی می‌بخشی و از هر که خواهی فرمانروایی باز می‌ستانی. هر که را خواهی ارجمند می‌داری و هر که را خواهی خوار می‌سازی، زیرا نیکی‌ها همه به دست توست، چرا که تو بر هر کاری توانایی.'),
(320, 3, 27, 'پیوسته با کاستن از ساعات روز، و افزودن آن بر شب، شب را در روز فرو می‌بری و با کاستن از ساعات شب و افزودنش بر روز، روز را در شب فرو می‌بری. زنده را از مرده و مرده را از زنده پدید می‌آوری و هر که را خواهی، بدون درخواست عوض از او روزیِ فراوان می‌دهی.'),
(321, 3, 28, 'مؤمنان نباید کافران را به جای مؤمنان دوست و یاور گیرند، و هر کس چنین کند، در هیچ حال از حزب خدا نیست، مگر این که به راستی از آنان بترسید. طرح دوستی با کافران کیفر حتمی خدا را در پی دارد، ازاین رو خدا شما را از کیفر خود می‌ترساند، و بازگشت همگان به سوی اوست.'),
(322, 3, 29, 'بگو: اگر آنچه را که در سینه هایتان است نهان دارید یا آشکارش سازید خدا آن را می‌داند و از آنچه در آسمان‌ها و از آنچه در زمین است خبر دارد، و خدا بر هر کاری تواناست.'),
(323, 3, 30, 'ای پیامبر، یاد کن روزی را که هر کس هر کار نیکی کرده است، هر چند اندک باشد، آن را در برابر خود حاضر شده می‌یابد، و هر کار بدی کرده نیز چنین است، و آرزو می‌کند که میان او و کار ناپسندش زمانی دراز فاصله بود. خدا شما را از کیفر خود بیم می‌دهد و برحذر می‌دارد، چرا که او به بندگان خود رئوف است.'),
(324, 3, 31, 'بگو: اگر خدا را دوست دارید، از من پیروی کنید و به آیینی که از جانب او آورده‌ام پای بند باشید، در آن صورت خدا نیز شما را دوست می‌دارد و گناهانتان را بر شما می‌بخشاید، و خداوند بسیار آمرزنده و مهربان است.'),
(325, 3, 32, 'بگو: خدا و پیامبر را اطاعت کنید، پس اگر روی برتافتند، به کفر گرایش یافته‌اند، و بدانند که خداوند کافران را دوست نمی‌دارد.'),
(326, 3, 33, 'به یقین خدا آدم و نوح و خاندان ابراهیم و خاندان عمران را برگزید و بر جهانیان برتری بخشید.'),
(327, 3, 34, 'او نسلی را برتری داد که در فضایل و خصلت‌های پسندیده مانند یکدیگرند، و خدا شنونده سخنان و آگاه از راز دل هاست و می‌داند چه کسانی درخورِ این موهبت‌اند.'),
(328, 3, 35, 'آن گاه که همسر عمران گفت: پروردگارا، من فرزندی را که در شکم خود دارم برای تو نذر کردم که او را در راه خدمت به تو آزاد بگذارم، پس او را با رضایت از من بپذیر که تو شنونده درخواست‌ها و دانا به راز دل‌هایی.'),
(329, 3, 36, 'پس چون فرزند خود را بزاد و دریافت که او دختر است، از روی حسرت گفت: پروردگارا، من آن را دختر زاییده‌ام. و خدا بهتر می‌دانست که او چه زاییده است، و پسری که او برای خدمت در راه ما می‌خواست مانند این دختر نبود. آن گاه همسر عمران گفت: من او را مریم نام نهادم و او را برای خدمت در اختیار تو نهادم و او و نسلش را از شرّ شیطانِ رانده شده به تو پناه می‌دهم.'),
(330, 3, 37, 'پس پروردگارش او را به طرزی نیکو پذیرفت و او را به طور پسندیده‌ای رشد داد، و سرپرستیِ وی را برعهده زکریّا نهاد. هرگاه زکریّا در محراب عبادت بر مریم وارد می‌شد، نزد او رزقی می‌یافت که در آن فصل انتظارش نمی‌رفت. گفت: ای مریم، این از کجا برای تو آمده است؟ مریم گفت: این از جانب خداست. خدا به هر که بخواهد روزی می‌دهد، بی آن که آن را برای او به شمار آوَرَد و عوضی از او درخواست کند.'),
(331, 3, 38, 'آنجا که زکریّا عنایت خاص خدا به مریم را دید، پروردگارش را خواند، گفت: پروردگارا، از جانب خود فرزندی پاک به من عطا کن که تو دعای بندگانت را می‌شنوی و آن را اجابت می‌کنی.'),
(332, 3, 39, 'پس در حالی که ایستاده بود و در محراب نماز می‌گزارد، فرشتگان او را ندا دادند که خدا تو را به فرزندی که نامش را یحیی نهاده است مژده می‌دهد. او کلمه خدا (مسیح) را تصدیق می‌کند و در میان قومش ارجمند و از زنانْ خویشتندار و پیامبری از شایستگان است.'),
(333, 3, 40, 'زکریا گفت: پروردگارا، چگونه مرا پسری باشد در حالی که مرا سالخوردگی فرا رسیده و همسرم نازاست؟ فرشته در پاسخ گفت: حقیقت همان است که به تو نوید داده شد. خدا هر کاری که بخواهد انجام می‌دهد.'),
(334, 3, 41, 'گفت: پروردگارا، نشانه‌ای برای من قرار ده تا بدانم که آنچه دریافت کرده‌ام از جانب توست. خدا فرمود: نشانه تو این است که سه روز با مردم جز به اشاره سخن نتوانی گفت، و پروردگارت را بسیار یاد کن و عصرگاهان و بامدادان او را تسبیح گوی.'),
(335, 3, 42, 'ای پیامبر، و یاد کن زمانی را که فرشتگان گفتند: ای مریم، خدا تو را برای عبادت خویش برگزیده و تو را پاک ساخته و از گناهان مصون داشته و بر زنان جهانیان برتری داده است.'),
(336, 3, 43, 'ای مریم، فروتنانه بر اطاعت از پروردگار خود پایدار باش و سجده کن و با رکوع کنندگان رکوع نمای.'),
(337, 3, 44, 'ای پیامبر، این از خبرهای غیبی است که آن را به تو وحی می‌کنیم، زیرا تو هنگامی که آنان تیرهای قرعه خود را می‌افکندند تا کدام یک سرپرستی مریم را برعهده گیرد نزدشان نبودی، و نیز هنگامی که در این باره به کشمکش پرداختند نزدشان حضور نداشتی.'),
(338, 3, 45, 'ای پیامبر، یاد کن زمانی را که فرشتگان گفتند: ای مریم، خدا تو را به کلمه‌ای از جانب خود که نامش مسیح، عیسی پسر مریم است، بشارت می‌دهد، در حالی که او هم در دنیا و هم در آخرت پذیرفته شده و نزد خدا از مقربان است.'),
(339, 3, 46, 'و هنگامی که او نوزادی در گهواره است و آن گاه که میانسال است با مردم سخن خواهد گفت و از زمره شایستگان است.'),
(340, 3, 47, 'مریم گفت: پروردگارا، چگونه مرا فرزندی باشد در حالی که هیچ بشری مرا لمس نکرده است؟ فرشته گفت: حقیقت همان است که به تو نوید دادم. خدا هر چه بخواهد می‌آفریند، بدین صورت که چون کاری را مقرّر کند، فقط به آن می‌گوید: باش، و آن چیز موجود می‌شود.'),
(341, 3, 48, 'و خدا به او کتاب آسمانی و حکمت و تورات و انجیل می‌آموزد.'),
(342, 3, 49, 'و پیامبری است که به سوی بنی اسرائیل فرستاده خواهد شد با این سخن که من با نشانه‌ای از جانب پروردگارتان [که رسالت مرا تأیید می‌کند] نزد شما آمده‌ام. من از گِل برای شما چیزی همانند شکل پرنده می‌سازم و در آن می‌دمم و آن به اذن خدا پرنده ای می‌شود، و کور مادر زاد و کسی را که دچار پیسی است، بهبودی می‌بخشم، و به اذن خدا مردگان را زنده می‌کنم، و شما را از آنچه خواهید خورد و از آنچه در خانه هایتان ذخیره می‌کنید خبر می‌دهم. قطعاً در اینها برای شما اگر مؤمن باشید نشانه‌ای است.'),
(343, 3, 50, 'و کتاب آسمانی تورات را که پیشاروی من است تصدیق می‌کنم، و آمده‌ام تا بخشی از آنچه را که بر شما حرام شده بود، برایتان حلال کنم، و با نشانه‌ای از جانب پروردگارتان نزد شما آمده‌ام. پس از خدا بترسید و مرا فرمان برید.'),
(344, 3, 51, 'به یقین، خداوند پروردگار من و پروردگار شماست، پس او را بپرستید. این راهی راست و درست است.'),
(345, 3, 52, 'فرزند مریم متولد شد و به رسالت مبعوث گشت ولی بنی اسرائیل با او مخالفت کردند پس هنگامی که عیسی از آنان احساس کفر نمود، گفت: چه کسانی مرا در مسیرم به سوی خدا یاری می‌دهند؟ حواریون گفتند: ماییم یاران خدا که تو را کمک خواهیم کرد. به خدا ایمان آورده‌ایم، و گواه باش که ما سر بر اطاعت تو نهاده و تسلیم توییم.'),
(346, 3, 53, 'آن گاه گفتند: پروردگارا، ما به همه آنچه تو نازل کرده‌ای (تورات و انجیل) ایمان آوردیم و از این پیامبری که به سوی ما فرستادی پیروی کردیم، پس ما را در زمره کسانی بنویس که بر ابلاغ رسالت فرستادگانت گواهی می‌دهند.'),
(347, 3, 54, 'و بنی اسرائیل با عیسی مکر کردند و خدا نیز با آنان مکر کرد، و خدا بهترین مکرکنندگان است.'),
(348, 3, 55, 'یاد کن زمانی را که خدا گفت: ای عیسی، من تو را به طور کامل برمی گیرم و به سوی خویش بالا می‌برم، و تو را از معاشرت با کافران و ناپاکان دور نگه می‌دارم، و کسانی را که از تو پیروی کرده‌اند، تا روز قیامت بر یهودیانی که کفر ورزیده‌اند برتری و تسلط می‌بخشم. سپس بازگشت همه شما به سوی من است، آن گاه میان شما در آنچه بر سرش اختلاف می‌کردید، داوری خواهم کرد.'),
(349, 3, 56, 'امّا یهودیانی را که کفر ورزیدند و با تو مخالفت کردند، هم در دنیا و هم در آخرت به عذابی سخت عذابشان می‌کنم و برای رهایی از عذاب یاورانی نخواهند داشت.'),
(350, 3, 57, 'و امّا کسانی از پیروان تو که ایمان آورده و کارهای شایسته کرده‌اند، خداوند پاداش هایشان را بی کم و کاست به آنان خواهد داد، و کسانی که خود را پیرو تو شمردند ولی ایمان واقعی نداشتند و کار شایسته نکردند ستمکارند، و خدا ستمکاران را دوست نمی‌دارد.'),
(351, 3, 58, 'ای پیامبر، اینها که بر تو می‌خوانیم، از نشانه‌های رسالت تو و از قرآنی است که خدا را در یادها زنده می‌دارد و آیاتش همه استوار و از هر باطل و ناراستی به دور است.'),
(352, 3, 59, 'بی گمان، حکایت آفرینش عیسی نزد خدا همچون حکایت آفرینش آدم است که خدا او را از خاک آفرید، سپس به او گفت: باش، و او بدون پدر به وجود آمد. پس همان گونه که آدم فرزند خدا نبود، عیسی نیز فرزند خدا نیست.'),
(353, 3, 60, 'حق از جانب پروردگار توست، پس هرگز از تردیدکنندگان مباش.'),
(354, 3, 61, 'پس کسانی که درباره عیسی ـ بعد از آن که آگاهی برای تو حاصل شده و به برهانی که همگان را کافی است دست یافتی ـ با تو محاجّه کنند و بر الوهیت او اصرار ورزند، بگو: بیایید ما پسرانمان را فراخوانیم و شما پسرانتان را و ما زنانمان را و شما زنانتان را و ما خودمان را و شما خودتان را، آن گاه دعا کنیم و لعنت خدا را بر آن گروهی که در دعوت خود دروغگویند قرار دهیم.'),
(355, 3, 62, 'آری، داستان واقعی عیسی همین است که ما از آن یاد کردیم نه آنچه مسیحیان پنداشته‌اند، و هیچ معبودی شایسته پرستش جز خدا نیست. و به راستی خداست آن مقتدری که در اراده خود مغلوب نمی‌گردد و دانایی است که کارهایش همه از روی حکمت است.'),
(356, 3, 63, 'پس اگر از مباهله روی برتافتند، بدانید که آنها در پی یافتن حق نیستند، بلکه فسادگرند و خدا به حال فسادگران داناست.'),
(357, 3, 64, 'بگو: ای صاحبان کتاب آسمانی، بیایید در عمل به سخنی که میان ما و شما یکسان است گرد آییم، و آن این که جز خدای یکتا را نپرستیم و چیزی را شریک او قرار ندهیم، و برخی از ما برخی دیگر را به جای خدا صاحب اختیار خود نگیرد. پس اگر از این دعوت روی برتافتند، بگویید: گواه باشید که ما مسلمانان تسلیم خداییم.'),
(358, 3, 65, 'ای اهل کتاب، چرا درباره ابراهیم محاجّه می‌کنید؟ یهودیان می‌گویند: ابراهیم بر آیین یهود و مسیحیان می‌گویند: او بر آیین مسیحیت بود، در حالی که تورات و انجیل که اساس یهودیت و مسیحیت اند پس از ابراهیم نازل شده‌اند. آیا این حقیقت را درنمی یابید؟'),
(359, 3, 66, 'هان! شما همانهایی هستید که درباره عیسی که از او آگاهی دارید با یکدیگر به حق محاجّه کردید، ولی چرا درباره آیین ابراهیم که از آن هیچ گونه آگاهی ندارید، محاجّه می‌کنید؟ خداست که می‌داند و شما نمی‌دانید.'),
(360, 3, 67, 'ابراهیم نه یهودی بود و نه نصرانی، بلکه حق گرا و تسلیم خدا بود، و از مشرکان نبود.'),
(361, 3, 68, 'بی گمان، سزاوارترین و نزدیک‌ترین مردم به ابراهیم کسانی اند که از او پیروی کرده‌اند، و این پیامبر و کسانی که به او ایمان آورده‌اند نیز از سزاوارترین و نزدیک‌ترین مردم به ابراهیم هستند، و خدا سرپرست مؤمنان است.'),
(362, 3, 69, 'گروهی از اهل کتاب آرزو می‌کنند که شما مسلمانان را گمراه سازند در حالی که جز خودشان کسی را به گمراهی نمی‌افکنند و این را درک نمی‌کنند.'),
(363, 3, 70, 'ای اهل کتاب، چرا آیات خدا را که در تورات و انجیل به عنوان نشانه‌هایی بر رسالت محمد آمده است انکار می‌کنید، در حالی که انطباق آنها را بر او به روشنی درمی یابید؟'),
(364, 3, 71, 'ای اهل کتاب، چرا حق را در چهره باطل آشکار می‌سازید و حق را کتمان می‌کنید، در حالی که حق و باطل را می‌شناسید؟'),
(365, 3, 72, 'و گروهی از اهل کتاب (عالمان یهود) به همکیشان خود گفتند: آنچه را که بامدادان بر مسلمانان فرو فرستاده شد و قبله شما را تأیید کرد، تصدیق کنید، و آنچه را که در پایان روز نازل شد و قبله دیگری برای آنان تعیین نمود، انکار کنید، شاید از آن بازگردند.'),
(366, 3, 73, 'و جز به کسی که از آیین شما پیروی کرده است اعتماد نکنید و نشانه‌های پیامبر موعود را که تغییر قبله از آن جمله است برای او باز مگویید. ای پیامبر، به آنان بگو: هدایت، فقط هدایت الهی است. و گفتند: ای همکیشان، آنچه را که می‌دانید فاش نکنید، مبادا نظیر آنچه به شما داده شده (استقلال در قبله) به مسلمانان نیز داده شود و یا آنان نزد پروردگارتان با شما محاجّه کنند و بر شما پیروز شوند. بگو: فزون بخشی به دست خداست، آن را به هر که بخواهد عطا می‌کند، و خداوند را بخششی گسترده است و کسانی را که درخورِ این بخشش اند به خوبی می‌شناسد.'),
(367, 3, 74, 'رحمتش را به هر که بخواهد اختصاص می‌دهد، و خداوند دارای بخشش بزرگ است.'),
(368, 3, 75, 'و از اهل کتاب کسانی اند که اگر آنان را بر پوستی انباشته از طلا امین شمری، آن را به تو بازمی گردانند، و از آنان کسانی اند که اگر بر دیناری امینشان شمری، آن را به تو بازنمی گردانند، مگر این که پیوسته بر سر آنان بایستی و به اصرار حق خود را از آنها مطالبه کنی. این امانت ناشناسی از آن روست که گفتند: بر ما در مورد امیّین (کسانی که از نژاد اسرائیل نیستند) راهی مقرّر نیست که حقوقی را برای آنان به رسمیت بشناسیم. و بر خدا دروغ می‌بندند و خود می‌دانند.'),
(369, 3, 76, 'چنین نیست که آنان پنداشته‌اند، بلکه هر کس به پیمان خود وفا کند و تقوا پیشه سازد، محبوب خداست، چرا که خدا تقواپیشگان را دوست می‌دارد.'),
(370, 3, 77, 'کسانی که پیمان خدا و سوگندهایشان را در برابر زر و زیور ناچیز دنیا می‌فروشند، در آخرت بهره‌ای نخواهند داشت، و خدا روز قیامت با آنان سخن نمی‌گوید و به ایشان نمی‌نگرد، و در دنیا و آخرت آنان را از رشدی شایسته برخوردار نمی‌سازد، و برایشان در هر دو جهان عذابی دردناک خواهد بود.'),
(371, 3, 78, 'و برخی از آنان گروهی هستند که زبان خود را به خواندن نوشته‌های خویش می‌پیچانند و آن را به صورت کتاب آسمانی قرائت می‌کنند تا آن را از کتاب خدا پندارید، با این که از آن کتاب نیست، و تصریح می‌کنند که از جانب خداست در حالی که از جانب خدا نیست، و بر خدا دروغ می‌بندند و خود می‌دانند.'),
(372, 3, 79, 'هیچ بشری را نشاید که خدا به او کتاب آسمانی و داوری و نبوّت دهد، سپس او به مردم بگوید: شما بندگان من باشید، نه بندگان خدا. بلکه می‌گوید: از آن روی که کتاب آسمانی را به مردم می‌آموزید و نیز بدین سبب که معارف و احکام آن را فرا می‌گیرید، مردانی الهی باشید و در عبادت و بندگی او سخت بکوشید.'),
(373, 3, 80, 'و نیز نشاید که به شما فرمان دهد که فرشتگان و پیامبران را خدایگان بگیرید. آیا پس از آن که تسلیم خدا شده‌اید شما را به کفر فرمان می‌دهد؟'),
(374, 3, 81, 'و هنگامی [را یاد کن] که خداوند از پیامبران و امت هایشان پیمان گرفت که هر کتاب و حکمتی به شما دادم به آن ایمان می‌آورید، سپس پیامبری را که به سوی شما می‌آید و حقانیت آنچه را که با شماست تأیید می‌کند یاری می‌دهید. آن گاه به پیامبران فرمود: آیا اقرار کردید و از امت‌های خود نیز پیمان مرا بر این امر گرفتید؟ گفتند: آری، اقرار کردیم. فرمود: پس شما پیامبران و امت‌ها گواه یکدیگر باشید و من نیز با شما از گواهانم.'),
(375, 3, 82, 'پس کسانی که بعد از این روی برتابند، آنانند فاسقان و از راه خدا خارج شدگان.'),
(376, 3, 83, 'با این وصف، آیا یهودیان و مسیحیان آیینی را غیر از آیین خدا می‌جویند که در برابر خدا تسلیم نمی‌شوند و آیین تو را تأیید نمی‌کنند؟ با این که همه کسانی که در آسمان‌ها و زمینند، خواه و ناخواه در برابر او تسلیم شده‌اند. راستی چگونه از آیین خدا روی برمی تابند با این که سرانجام به سوی او بازگردانده می‌شوند؟'),
(377, 3, 84, 'ای پیامبر، بگو: من و پیروانم به خدا و آنچه بر ما نازل شده و آنچه بر ابراهیم و اسماعیل و اسحاق و یعقوب و اسباط (پیامبرانی از نسل یعقوب) فرو فرستاده شده، و آنچه به موسی و عیسی و دیگر پیامبران از جانب پروردگارشان عطا شده است ایمان آورده‌ایم و میان هیچ یک از پیامبران فرقی نمی‌گذاریم که به برخی ایمان بیاوریم و به برخی دیگر کفر ورزیم، و ما تسلیم و فرمانبردار خداییم.'),
(378, 3, 85, 'و هر کس جز تسلیم بودن در برابر خدا آیین دیگری طلب کند، هرگز از او پذیرفته نمی‌شود و او در آخرت از زیانکاران است.'),
(379, 3, 86, 'چگونه خداوند قومی را که پس از ایمانشان کافر شدند هدایت کند و پیش از این شهادت داده بودند که پیامبر بر حق است و دلایل روشن نیز به آنان رسیده بود؟ آنان از سرِ لجاجت به کفر روی آوردند و به همین سبب ستمکارند، و خدا مردم ستمکار را هدایت نمی‌کند.'),
(380, 3, 87, 'آنان سزایشان این است که لعنت خدا و فرشتگان و همه مردم بر آنها خواهد بود.'),
(381, 3, 88, 'در لعنت خدا ماندگار و برای همیشه در عذاب گرفتارند، نه عذاب از آنان کاسته می‌شود و نه مهلتی می‌یابند.'),
(382, 3, 89, 'مگر کسانی که پس از آن توبه کردند و مردمی شایسته شدند، که خدا آنان را می‌آمرزد و بر آنها رحمت می‌آورد، زیرا خداوند بسیار آمرزنده و مهربان است.'),
(383, 3, 90, 'به یقین، کسانی که پس از ایمانشان بی هیچ بهانه‌ای کافر شدند و سپس بر کفر [خویش] افزودند، هرگز توبه آنها پذیرفته نمی‌شود، چرا که توبه آنها توبه واقعی نیست و اینان گمراهانند.'),
(384, 3, 91, 'به یقین کسانی که کافر شدند و در حال کفر مردند، از هیچ یک از آنان گنجینه‌ای به گستره زمین که آکنده از طلا باشد پذیرفته نمی‌شود، هر چند آن را فدیه دهد تا به بهشت درآید. برای آنان عذابی دردناک خواهد بود و یاورانی که آنها را از عذاب برهانند نخواهند داشت.'),
(385, 3, 92, 'هرگز به نیکی دست نمی‌یابید و از نیکان نمی‌شوید مگر این که از آنچه دوست می‌دارید انفاق کنید، و هر چه را انفاق کنید پاداش آن را نزد خدا خواهید یافت، زیرا خدا به انفاق شما داناست.'),
(386, 3, 93, 'پیش از آن که تورات نازل شود، همه خوراکی‌ها ـ جز آنچه اسرائیل بر خود حرام کرده بود ـ برای بنی اسرائیل حلال بود. به یهودیان بگو: اگر راست می‌گویید که پیش از تورات نیز بعضی از خوراکی‌ها حرام بوده است، تورات را بیاورید و آن را بخوانید تا حقیقت آشکار شود.'),
(387, 3, 94, 'پس اگر چنین نکردند، بدان که بر خدا دروغ بسته‌اند، و هر کس پس از آشکار شدن حقیقت، بر خدا دروغ بندد، چنین کسانی ستمکار خواهند بود.'),
(388, 3, 95, 'بگو: خدا راست گفته است که هیچ یک از خوردنی‌های پاکیزه را پیش از نزول تورات بر بنی اسرائیل حرام نکرده بود. پس ای فرزندان اسرائیل، از آیین ابراهیم که حق گرا بود و از مشرکان نبود پیروی کنید.'),
(389, 3, 96, 'نخستین خانه‌ای که برای مردم به منظور عبادت خدا بنا نهاده شده همان خانه پربرکتی است که در مکّه قرار دارد و مایه هدایت آدمیان است.'),
(390, 3, 97, 'در آن جا نشانه‌های روشنی است که یاد خدا را در دل‌ها زنده می‌دارد. از جمله آنها مقام ابراهیم، امنیت و حج آن خانه است. پس هر که به آن درآید ایمن است. و برای خدا بر عهده مردم است که قصد آن خانه کنند و حج به جای آورند، البتّه این بر کسانی واجب است که بتوانند راهی به سوی آن بیابند. و هر کس حجِ خانه خدا را ترک کند، به خدا زیانی نمی‌رساند، زیرا خدا از همه جهانیان بی نیاز است.'),
(391, 3, 98, 'بگو: ای اهل کتاب، چرا آیات خدا را انکار می‌کنید با این که خدا بر آنچه انجام می‌دهید گواه است؟'),
(392, 3, 99, 'بگو: ای اهل کتاب، چرا کسانی را که ایمان آورده‌اند، از راه خدا باز می‌دارید و کجِ آن را می‌طلبید با این که حقیقت را می‌دانید و بر آن گواهید؟ و خدا از آنچه می‌کنید بی خبر نیست.'),
(393, 3, 100, 'ای کسانی که ایمان آورده‌اید، اگر از گروهی از کسانی که به آنان کتاب آسمانی داده شده است پیروی کنید، شما را پس از آن که ایمان آورده‌اید به کفر باز می‌گردانند.'),
(394, 3, 101, 'چگونه پیرو آنان می‌شوید و پس از ایمانتان به کفر می‌گرایید با اینکه آیات خدا پیوسته بر شما تلاوت می‌شود و پیامبر او نیز در میان شماست؟ شما می‌توانید با اندیشه در آیات قرآن و پیروی از پیامبر در دین خود بصیرت یابید و از گرایش به کفر ایمن شوید، زیرا هر کس به قرآن و رسول خدا تمسک جوید، به خدا تمسک جسته و هر کس به خدا تمسک جوید، قطعاً به راهی راست هدایت شده است.'),
(395, 3, 102, 'ای کسانی که ایمان آورده‌اید، از خدا آن گونه که حق پروا کردن از اوست پروا کنید و به فرمان او همواره گردن نهید، و مبادا جز در حالی که تسلیم اویید بمیرید.'),
(396, 3, 103, 'و همگی به ریسمان الهی (قرآن و پیامبر) که شما را به خدا پیوند می‌دهد، چنگ زنید و از اختلاف و پراکندگی بپرهیزید، و نعمت خدا را بر خود یاد کنید، آن گاه که دشمن یکدیگر بودید و او میان دل‌های شما الفت انداخت و در پرتو نعمت او با یکدیگر برادر شدید، و آن گاه که بر پرتگاه گودالی از آتش بودید پس شما را از آن رهایی بخشید. بدین سان خدا آیات خود را برای شما بیان می‌کند، باشد که شما راه یابید.'),
(397, 3, 104, 'و باید از میان شما گروهی باشند که مردم را به نیکی فراخوانند و آنان را به کار پسندیده وادارند و از کار زشت و نکوهیده نهی کنند. اینانند که نیکبخت خواهند بود.'),
(398, 3, 105, 'و مانند کسانی نباشید که وحدت خود را از دست دادند و پراکنده شدند، و پس از آن که دلایل روشن برای آنها آمد، در باورهای دینی خود اختلاف کردند. اینانند که برایشان عذابی بزرگ خواهد بود.'),
(399, 3, 106, 'در روزی که چهره‌هایی سفید و چهره‌هایی سیاه شود، پس به کسانی که روسیاه شده‌اند گفته می‌شود: آیا بعد از ایمانتان کافر شدید؟ پس به سزای این که کفر میورزیدید عذاب را بچشید.'),
(400, 3, 107, 'امّا کسانی که در آن روز روسفیدند در رحمت خدایند و جاودانه در آن خواهند بود. (۱۰۷'),
(401, 3, 108, 'اینها آیات با عظمت خداست که آنها را به حق بر تو می‌خوانیم، و خدا هیچ ستمی برای جهانیان نمی‌خواهد.'),
(402, 3, 109, 'و آنچه در آسمان‌ها و آنچه در زمین است از آنِ خداست و همه امور به خدا بازگشت می‌یابد و به دست او جاری می‌گردد، پس چگونه ممکن است بر کسی ستم کند؟'),
(403, 3, 110, 'در آغاز ظهور اسلام شما مسلمانان بهترین امتی بودید که برای [اصلاح دنیا و آخرت] مردم پدیدار شدید، زیرا به کار نیک فرمان می‌دادید و از کار ناپسند باز می‌داشتید و دعوت خدا را باور داشتید پس اگر اهل کتاب نیز دعوت خدا را پذیرفته بودند، قطعاً برایشان بهتر بود، ولی تنها گروهی از آنان ایمان آوردند و بیشترشان از فرمان خدا خارج شدند.'),
(404, 3, 111, 'آنان هرگز به شما زیانی نمی‌رسانند، فقط شما را اندک آزاری می‌دهند، و اگر با شما بجنگند، به شما پشت می‌کنند و می‌گریزند، سپس از سوی هیچ کس یاری نخواهند شد.'),
(405, 3, 112, 'هر کجا یافت شوند و تسلطی بر آنان حاصل آید باید مهر خواری بر آنان نهاده شود، مگر آن که به رشته‌ای از جانب خدا تمسّک جویند و به پیمانی از سوی مردم متعهد شوند. آنان خشمی را از خدا بر خود جای دادند و مهر درماندگی بر ایشان زده شد. این بدان سبب بود که به آیات خدا کفر میورزیدند و پیامبران را به ناحق می‌کشتند، و این از آن روی بود که از فرمان خدا سرپیچی کردند و از حدود [الهی و انسانی] تجاوز می‌نمودند.'),
(406, 3, 113, 'همه آنان یکسان نیستند. از میان اهل کتاب گروهی هستند که بر ایمان و اطاعت خدا پایدارند و در ساعاتی از شب آیات خدا را تلاوت می‌کنند و سر به سجده می‌نهند.'),
(407, 3, 114, 'به خدا و روز واپسین ایمان دارند و به کار پسندیده فرمان می‌دهند و از کار ناپسند باز می‌دارند و در انجام کارهای نیک شتاب می‌کنند، و آنان از شایستگانند.'),
(408, 3, 115, 'و هر کار نیکی انجام دهند، هرگز درباره آن از جانب خدا ناسپاسی نبینند، و خدا به تقواپیشگان داناست.'),
(409, 3, 116, 'یهودیان و مسیحیانی که با انکار رسالت پیامبر کافر شدند، هرگز اموال و فرزندانشان چیزی از نیاز آنان را به خدا برطرف نخواهند کرد و از عذابی که خدا برایشان آماده کرده است نخواهند کاست. آنان همدم آتشند و در آن جاودانه‌اند.'),
(410, 3, 117, 'مَثَل آنچه آنان در زندگی دنیا برای دستیابی به اهداف ناروای خود هزینه می‌کنند، همچون مَثَل بادی است که در آن سوز و سرمایی سخت باشد و به کشتزار مردمی که برخود ستم کرده‌اند بوزَد و آن را تباه سازد. خدا بر آنان ستم روا نداشته است، بلکه آنان خود بر خویشتن ستم کرده‌اند که اموال خود را در راه باطل هزینه نموده‌اند.'),
(411, 3, 118, 'ای کسانی که ایمان آورده‌اید، به غیر همکیشان خود اعتماد نکنید و آنان را همراز خود نگیرید. آنان در حق شما از هیچ شرّ و فسادی کوتاهی نمی‌کنند. دوست داشته‌اند که شما در رنج بیفتید و سخت زیان ببینید. دشمنی از دهان هایشان و لحن سخنانشان آشکار است و آنچه سینه هایشان از دشمنی شما پنهان می‌دارد ـ که در بیان نمی‌گنجد ـ بزرگ‌تر است. همانا ما آیات و نشانه‌ها را برای شما به روشنی بیان کردیم، اگر خرد را به کار گیرید درمی یابید.'),
(412, 3, 119, 'هان، شما همانهایی هستید که آنان را دوست می‌دارید، ولی آنها شما را دوست نمی‌دارند، و شما به همه کتاب‌های آسمانی ایمان دارید، ولی آنان به کتاب شما ایمان ندارند، و چون با شما دیدار کنند گویند: ایمان آوردیم. و چون با یکدیگر خلوت کنند، از خشم بر شما سرانگشتان خود را به دندان می‌گزند. بگو: به خشم خود بمیرید که خدا به راز سینه‌ها داناست.'),
(413, 3, 120, 'اگر به شما خوشی رسد مایه اندوهشان می‌شود، و اگر به شما بدی رسد بدان شاد می‌گردند. و اگر بر ناگواری‌ها صبر کنید و تقوا پیشه سازید، نیرنگشان اندک زیانی به شما نمی‌رساند، چرا که خدا به آنچه می‌کنند احاطه دارد.'),
(414, 3, 121, 'و یاد کن زمانی را که بامدادان از میان کسان خود بیرون رفتی در حالی که برای تدارک جنگ اُحُد مؤمنان را در پایگاه‌هایی که مناسب پیکار بود جای می‌دادی، و خدا سخنان آنان را می‌شنید و به راز دل هایشان دانا بود.'),
(415, 3, 122, 'آن گاه که دو گروه از شما هراسی به دل راه دادند و خواستند سستی کنند، در حالی که خدا یاورشان بود. پس مؤمنان باید امورشان را به خدا واگذارند و فقط بر او توکل کنند.'),
(416, 3, 123, 'چرا هراس به دل راه دادید و برای حضور در پیکار اُحُد سستی کردید، با این که خدا شما را در جنگ بدر به رغم این که در برابر دشمن ناتوان بودید، پیروز کرد؟ پس، از عذاب خدا پروا کنید، باشد که او را سپاس بگزارید.'),
(417, 3, 124, 'ای پیامبر، یاد کن هنگامی را که به مؤمنان می‌گفتی: آیا برای شما کافی نیست که پروردگارتان شما را با سه هزار از فرشتگان فرود آمده و حاضر در معرکه نبرد یاری کند؟'),
(418, 3, 125, 'چرا، البته این تعداد فرشته کافی است. و می‌گفتی: اگر شکیبایی کنید و تقوا پیشه سازید و دشمنان هم اکنون در رسند و بر شما هجوم آورند، پروردگارتان شما را با پنج هزار از فرشتگان که بر خود علامت نهاده‌اند یاری می‌دهد.'),
(419, 3, 126, 'البته خداوند فرشتگان را برای جهاد با کافران فرو نفرستاد، بلکه آنها را فرو فرستاد تا برای شما نوید پیروزی باشند و دل هایتان به امداد الهی آرام گیرد. پس بدانید که پیروزی جز از جانب خدای شکست ناپذیرِ حکیم نیست.'),
(420, 3, 127, 'آری، خدا شما را در جنگ بدر پیروز کرد تا کافران را دچار کاستی کند و ناتوانشان سازد، یا آنان را خوار کند و به خشم آورد تا ناکام باز گردند.'),
(421, 3, 128, 'از این کار چیزی در اختیار تو نبود تا به هنگام پیروزی ستایش شوی و به هنگام شکست سرزنش گردی، بلکه کارها به دست خدا انجام می‌گیرد. او شما را پیروز کرد تا اگر مشرکان به سوی خدا بازگشتند، خدا نیز به سوی ایشان باز گردد و بر آنان رحمت آورد، و اگر بر کفر پایداری کردند، آنها را عذاب کند، چرا که آنان ستمگرندو ستمگران سزاوار عذابند.'),
(422, 3, 129, 'و آنچه در آسمان‌ها و آنچه در زمین است از آنِ خداست، هر که را بخواهد می‌آمرزد و هر که را بخواهد عذاب می‌کند، و خدا بسیار آمرزنده و مهربان است.'),
(423, 3, 130, 'ای کسانی که ایمان آورده‌اید، ربا را که با کاسته شدن اموال مردم به چندین و چند برابر افزایش می‌یابد، مخورید و از خدا پروا کنید، باشد که نیکبخت شوید.'),
(424, 3, 131, 'و از آتشی که برای کافران آماده شده است بترسید.'),
(425, 3, 132, 'و خدا و پیامبر را فرمان برید، باشد که مورد رحمت قرار گیرید.'),
(426, 3, 133, 'و به سوی آمرزشی از پروردگارتان و بهشتی که گستره آن آسمان‌ها و زمین است و برای تقواپیشگان آماده شده است بشتابید.'),
(427, 3, 134, 'تقواپیشگان کسانی اند که در آسایش و تنگی از اموال خود در راه خدا انفاق می‌کنند و خشم خود را فرو می‌خورند و از لغزش مردم در می‌گذرند. اینان نیکوکارند و خدا نیکوکاران را دوست می‌دارد.'),
(428, 3, 135, 'و کسانی اند که چون کار زشتی انجام دهند یا در اثر گناهی به خود ستم کنند خدا را به یاد می‌آورند و برای گناهانشان آمرزش می‌طلبند ـ و چه کسی جز خدا گناهان را می‌آمرزد؟ ـ و بر گناهانی که مرتکب شده‌اند اصرار نمیورزند و می‌دانند [که اصرار بر گناه باعث سبک شمردن فرمان خداست].'),
(429, 3, 136, 'اینان سزایشان آمرزشی از جانب پروردگارشان و بهشتی است با بوستان‌هایی انبوه از درختان که از زیر آنها جوی‌ها روان است و در آنها جاودانه‌اند، و پاداش عمل کنندگان پاداشی نیکوست.'),
(430, 3, 137, 'بی گمان، پیش از شما نیز سنت‌هایی بوده و سپری شده است [امت‌ها روش‌هایی داشته‌اند و خدا براساس آن سنت‌هایی را جاری ساخته است]. پس در زمین بگردید و بنگرید که فرجام کسانی که آیات خدا را دروغ شمردند چگونه بوده است.'),
(431, 3, 138, 'این برای عموم مردم نشان دادن [عبرت ها] است و برای تقواپیشگان رهنمود و پندی است.'),
(432, 3, 139, 'ای مؤمنان، در تصمیم خود سستی مکنید و از شکست خویش در جنگ اُحُد اندوهگین نشوید، چرا که اگر شما ایمان داشته باشید برترید.'),
(433, 3, 140, 'اگر در آن جنگ گزندی به شما رسید (گروهی از شما کشته و گروهی زخمی شدند)، به آن مردم کفرپیشه نیز گزندی مانند آن رسید، پس جای اندوه نیست. ما این روزها [= روزهای شکست و پیروزی] را میان مردم می‌گردانیم تا کارهایی که باید، انجام شود و خدا کسانی را که ایمان آورده‌اند معلوم دارد و از میان شما گواهانِ اعمال را برگیرد، و خدا ستمکاران را دوست نمی‌دارد.'),
(434, 3, 141, 'و تا خداوند کسانی را که ایمان آورده‌اند خالص گرداند و ایمانشان را از هر گونه کفر و شرک و نفاق پاک سازد و کافران را به تدریج به نابودی کشاند.'),
(435, 3, 142, 'آیا گمان کرده‌اید که چون به دین حق گرویده‌اید همواره بر دشمنانتان پیروز می‌شوید، و پنداشته‌اید بی آنکه آزمون شوید به بهشت می‌روید، در حالی که خداوند هنوز مجاهدان شما را معلوم نساخته و صبرپیشگان را مشخص نکرده است؟'),
(436, 3, 143, 'شما مرگ در راه خدا را پیش از این که با آن روبه رو شوید، آرزو می‌کردید، ولی در جنگ اُحُد آن را حاضر یافتید و در حالی که آن را می‌نگریستید به آن تن ندادید.'),
(437, 3, 144, 'آیا شایعه کشته شدن پیامبر شما را از جنگ رویگردان ساخت؟ بدانید که محمد جز فرستاده‌ای که پیش از او نیز پیامبران آمدند و درگذشتند، نیست. آیا اگر او بمیرد یا کشته شود، به عقب باز می‌گردید و از دین خدا روی برمی تابید؟ هر کس از آیین خدا روی برتابد، بداند که اندک زیانی به خدا نمی‌رساند. البتّه همه روی برنتافتند، بلکه در میان شما سپاسگزارانی نیز وجود داشتند، و به زودی خدا سپاسگزاران را پاداش خواهد داد.'),
(438, 3, 145, 'هیچ کس را نشاید که جز به اذن خدا بمیرد. خداوند مرگ را سرنوشتی معین شده، قرار داده است. هر کس در پی پاداش دنیا باشد از آن به او می‌دهیم، و هر کس پاداش آخرت جوید از آن به وی می‌دهیم، و پاداش سپاسگزارانی را که خداجویند به زودی خواهیم داد.'),
(439, 3, 146, 'و چه بسیار پیامبرانی که انبوهی از مردان الهی همراه آنان پیکار کردند و در برابر گزندهایی که در راه خدا به آنان رسید، سستی نکردند و ناتوان نشدند و نالان نگشتند، بلکه شکیبایی کردند و خدا شکیبایان را دوست می‌دارد.'),
(440, 3, 147, 'و آنان سخنشان جز این نبود که گفتند: پروردگارا، گناهان ما و زیاده روی ما در کارمان را بر ما ببخشای و گام هایمان را استوار بدار و ما را بر مردم کفرپیشه پیروز گردان.'),
(441, 3, 148, 'پس خداوند پاداش دنیا و پاداش نیکوی آخرت را به آنان عطا کرد، چرا که نیکوکار بودند، و خدا نیکوکاران را دوست می‌دارد.'),
(442, 3, 149, 'ای کسانی که ایمان آورده‌اید، اگر از کافران پیروی کنید، شما را به عقب بازمی گردانند و از دینتان روی گردان می‌کنند و از زیانکاران خواهید شد.'),
(443, 3, 150, 'آنان سرپرست و یاور شما نیستند، بلکه خدا سرپرست شماست و او بهترین یاور است.'),
(444, 3, 151, 'به زودی در دل کافران ـ بدان سبب که چیزهایی را شریک خدا قرار دادند که خدا هیچ برهانی بر شریک بودن آنها فرو نفرستاده است ـ هراس می‌افکنیم، و جایگاهشان آتش است و بد جایی است جایگاه ستمکاران.'),
(445, 3, 152, 'همانا خدا راستی و درستی وعده خود را در مورد پیروزی شما به ثبوت رسانید، آن گاه که در ساعات نخستین نبرد، به اذن او کافران را می‌کشتید و ریشه کن می‌کردید و همچنان پیش می‌رفتید، تا وقتی که در پاسداری از گذرگاه اُحد سستی کردید و در آن کار کشمکش نمودید و سرانجام دستور پیامبر را نافرمانی کردید و گذرگاه را رها ساختید، و این بعد از آن بود که خداوند آنچه را دوست می‌داشتید (غنیمت‌های جنگی) به شما نشان داده بود ـ آری، از میان شما کسانی اند که دنیا را می‌خواهند و از میان شما کسانی اند که سرای آخرت را می‌طلبند ـ سپس شما را از تعقیب دشمن بازداشت تا شما را بیازماید، و سرانجام از گناه شما درگذشت، چرا که خدا دارای تفضّل است و بر مؤمنان تفضّل می‌کند.'),
(446, 3, 153, 'یاد کنید هنگامی را که از صحنه پیکار دور می‌شدید و به هیچ کس توجه نمی‌کردید و آنچنان دور شدید که پیامبر پشت سرِ آخرین نفراتِ شما، شما را به بازگشت فرا می‌خواند ولی اعتنایی نمی‌کردید. پس از آن خداوند غمِ از دست دادن یاری خدا را به جای غمِ از دست دادن غنایم و تحمّل گزندها، به شما عطا کرد تا بر آنچه از دست شما رفته و گزندهایی که به شما رسیده بود اندوه مخورید، و خدا به آنچه می‌کنید آگاه است.'),
(447, 3, 154, 'سپس بعد از آن اندوه، آرامشی که خوابی سبک را در پی داشت بر شما نازل کرد، ولی تنها گروهی از شما را فراگرفت، و گروهی دیگر که از فرار خود پشیمان نشده بودند و به سوی پیامبر بازنگشتند و تنها حفظ جانشان برای آنان اهمیت داشت، آرامشی نیافتند و از بیم و هراس چشم بر هم ننهادند. آنان درباره خدا، همچون گمان دوران جاهلیت، گمانی نادرست بردند و گفتند: آیا چیزی از این کار (پیروزی در جنگ) از آنِ ما هست؟ مگر نه این است که پیروان حق باید پیروز باشند؟ بگو: کارها همه از آنِ خدا و در اختیار اوست. آنان در دل هایشان اندیشه‌هایی را نهان می‌دارند که برای تو آشکار نمی‌کنند و می‌گویند: اگر چیزی از این کار از آنِ ما بود، در این جا کشته نمی‌شدیم. بگو: اگر در خانه هایتان هم بودید، کسانی که کشته شدن بر آنان نوشته شده بود، به سوی آرامگاه‌های خود بیرون می‌رفتند، تا کارهایی که باید، انجام گیرد و تا خدا آنچه را که در سینه‌های شماست بیازماید و آنچه از ایمان و کفر در دل‌های شماست خالص گرداند، و خدا به راز سینه‌ها داناست.'),
(448, 3, 155, 'کسانی از شما که روز برخورد آن دو گروه (مؤمنان و مشرکان) در میدان اُحُد، روی برتافتند و گریختند، تنها شیطان آنان را به سبب برخی از گناهانی که مرتکب شده بودند، لغزانید. ولی خدا از آنان درگذشت، چرا که خدا آمرزنده و بردبار است.'),
(449, 3, 156, 'ای کسانی که ایمان آورده‌اید، مانند کسانی نباشید که کافر شدند و درباره برادرانشان ـ هنگامی که به سفر رفتند یا پیکارگر بودند پس مردند یا کشته شدند ـ گفتند: اگر نزد ما بودند نمی‌مردند و کشته نمی‌شدند. این اندیشه نادرست در نظر آنان به صورت یک واقعیت جلوه می‌کند، تا خداوند آن را در دل هایشان مایه حسرت و اندوه قرار دهد و به وسیله آن رنجشان دهد. و خداست که حیات می‌بخشد و می‌میراند، و خدا به آنچه می‌کنید بیناست.'),
(450, 3, 157, 'و اگر در راه خدا کشته شوید یا بمیرید، قطعاً خدا شما را می‌آمرزد و در پرتو رحمت خویش می‌آورد، و به یقین آمرزش و رحمتی که از جانب خداست، از مال و ثروتی که آنان جمع آوری می‌کنند بهتر است.'),
(451, 3, 158, 'و اگر بمیرید یا کشته شوید، حتماً به سوی خدا محشور می‌شوید.'),
(452, 3, 159, 'ای پیامبر، به سبب لطف و رحمت خدا بود که با آنان نرمخویی و مهربانی کردی، و اگر درشتخوی و سختدل بودی، قطعاً از گرد تو پراکنده شده بودند. پس اکنون هم از خطاهایشان درگذر و از خدا برایشان آمرزش بخواه و در تدبیر امور با آنان مشورت کن، و چون بر کاری تصمیم گرفتی، بر خدا توکل کن که خداوند توکل کنندگان را دوست می‌دارد و آنان را یاری می‌کند.'),
(453, 3, 160, 'اگر خدا شما را یاری کند، هیچ کس بر شما چیره نمی‌شود، و اگر شما را واگذارد، چه کسی بعد از او شما را یاری خواهد کرد؟ پس مؤمنان باید تنها بر خدا توکل کنند.'),
(454, 3, 161, 'و هیچ پیامبری را نشاید که به خدا یا مردم خیانت کند، و هر کس خیانت ورزد، روز قیامت با خیانت خود در پیشگاه خدا حاضر خواهد شد، سپس به هر کسی همان را که فراهم آورده است، بی کم و کاست خواهند داد و در حق آنان ستم نمی‌شود.'),
(455, 3, 162, 'پس آیا کسی که خشنودی خدا را دنبال کرده، مانند کسی است که با خشمی از خدا بازگشته و جایگاهش دوزخ است؟ آن بد بازگشتگاهی است.'),
(456, 3, 163, 'همه آنان ـ چه کسانی که در پی خشنودی خدایند و چه کسانی که دچار خشم او شده‌اند ـ نزد خدا برحسب اعمالشان دارای درجاتی گوناگونند، و خدا به آنچه می‌کنند بیناست.'),
(457, 3, 164, 'به یقین خدا به مؤمنان نعمتی بزرگ ارزانی داشت که پیامبری از خودشان در میان آنان برانگیخت که آیاتش را بر آنان تلاوت کند و آنان را از کارهای زشت و اخلاق ناپسند پاک سازد و کتاب آسمانی و معارف آن را به ایشان بیاموزد، و قطعاً آنان پیش از بعثت پیامبر در گمراهی آشکاری فرو رفته بودند.'),
(458, 3, 165, 'و آیا هنگامی که در جنگ اُحُد آسیبی به شما رسید که دو برابرش را در جنگ بدر به دشمنان وارد کرده بودید، گفتید: این آسیب از کجا به ما رسید؟ بگو: آن از نزد خود شماست و خودتان این گونه خواستید. قطعاً خداوند بر هر کاری تواناست.'),
(459, 3, 166, 'آسیب‌هایی که در جنگ اُحُد ـ روزی که آن دو گروه به مصاف هم رفتند ـ به شما رسید به اذن خدا بود، تا آنچه را که باید انجام شود به انجام رساند و تا مؤمنان را مشخص سازد.'),
(460, 3, 167, 'و تا کسانی را که نفاق پیشه نمودند مشخص کند. به منافقان گفته شد: بیایید در راه خدا بجنگید یا دست کم از هستی خود دفاع کنید، گفتند: اگر می‌دانستیم که جنگی رخ می‌دهد، حتماً در پی شما می‌آمدیم. آنان روزی که چنین گفتند به کفر نزدیک‌تر بودند تا به ایمان. به زبانشان چیزهایی می‌گویند که در دلشان نیست و خداوند به آنچه نهان می‌دارند داناتر است.'),
(461, 3, 168, 'همان کسانی که برجای نشستند و به سوی کارزار بیرون نرفتند و درباره برادرانشان که در راه خدا کشته شدند گفتند: اگر از ما پیروی می‌کردند و در این جا می‌ماندند کشته نمی‌شدند. به آنان بگو: اگر راست می‌گویید، مرگ را از خویشتن دور کنید.'),
(462, 3, 169, 'و کسانی را که در راه خدا کشته شده‌اند مرده مپندار، بلکه آنان زنده‌اند و نزد پروردگارشان از نعمت‌های بهشت به آنان روزی می‌دهند.'),
(463, 3, 170, 'به آنچه خدا از فضل خود به آنان داده است شادمانند، و برای آن مؤمنان راستینی که پس از آنانند و هنوز به ایشان نپیوسته‌اند مسرورند که ترسی آنان را فرانمی گیرد و اندوهگین نمی‌شوند.'),
(464, 3, 171, 'به نعمت و فضلی که از جانب خداست و این که خدا پاداش مؤمنان را تباه نمی‌کند شادمانند.'),
(465, 3, 172, 'کسانی که پس از آسیب رسیدن به آنان در جنگ اُحُد، دعوت خدا و پیامبر را اجابت کردند و به جنگی دیگر رهسپار شدند، برای نیکوکاران و تقواپیشگانشان پاداشی بزرگ خواهد بود.'),
(466, 3, 173, 'همانان که آن مردم (مزدوران دشمن) به ایشان گفتند: مشرکان برای پیکار با شما گرد آمده‌اند، از آنان بترسید. ولی این خبر آنان را ایمان بیفزود و گفتند: خدا ما را بس است و او نیکو کارگزاری است.'),
(467, 3, 174, 'پس با نعمت و بخشش خدا از تعقیب دشمن بازگشتند در حالی که هیچ گزندی به آنان نرسیده بود. آنان خشنودی خدا را پی گرفتند [و خدا نیز فضل خود را از آنان دریغ نکرد] چرا که خداوند دارای فضل و بخششی بزرگ است.'),
(468, 3, 175, 'این فقط شیطان است که شما مؤمنان را از دوستان شرک پیشه خود می‌ترساند، پس اگر به راستی مؤمنید، از آنان نترسید و از عذاب من بیم داشته باشید.'),
(469, 3, 176, 'ای پیامبر، مبادا کسانی که به سوی کفر می‌شتابند و در آن فرو می‌روند، با این تصور که بر اراده خدا چیره شده‌اند تو را اندوهگین کنند. آنان هرگز اندک زیانی به خدا نمی‌رسانند. خدا می‌خواهد در آخرت هیچ بهره‌ای برای آنان قرار ندهد، و آنان را عذابی بزرگ خواهد بود.'),
(470, 3, 177, 'نه تنها آنان، بلکه هیچ یک از کسانی که کفر را به بهای ایمان خریده‌اند، هرگز نمی‌توانند با کفر خویش زیانی به خدا برسانند، و برای آنان عذابی دردناک خواهد بود.'),
(471, 3, 178, 'و کسانی که کفر ورزیده‌اند نپندارند که مهلتی که به آنان می‌دهیم برایشان خیر است، ما فقط به آنان مهلت می‌دهیم تا گناهی بیفزایند، و برای آنان عذابی خوارکننده خواهد بود.'),
(472, 3, 179, 'خدا چنین نیست که مؤمنان را بر حالتی که شما برآنید که ایمان و کفر به هم آمیخته و مؤمنان و منافقان مختلط گشته‌اند رها کند، تا این که ناپاک را از پاک جدا سازد، و نیز خدا چنین نیست که شما را از غیب آگاه گرداند تا بتوانید خود منافق را از مؤمن بازشناسید، ولی خدا هر که را بخواهد برمی گزیند و او را از غیب آگاه می‌سازد، و او پیامبرانش را برای این امر برگزیده است. پس به خدا و پیامبرانش ایمان بیاورید، و اگر ایمان بیاورید و تقوا پیشه کنید، برای شما پاداشی بزرگ خواهد بود.'),
(473, 3, 180, 'و کسانی که به آنچه خدا از فضل خود به آنان داده است بخل میورزند و از آن در راه خدا انفاق نمی‌کنند، گمان نبرند که آن برای ایشان خیر است، بلکه برای آنان شرّ است، زیرا روز قیامت آنچه را که بدان بخل ورزیده‌اند طوقِ گردنشان کنند. آن روز میراث آسمان‌ها و زمین از آنِ خداست، و خدا به آنچه کرده‌اید آگاه است.'),
(474, 3, 181, 'قطعاً خداوند سخن یهودیان را که گفتند: خدا تهیدست است و ما توانگریم، شنید. ما قطعاً آن سخنی را که بر زبان آوردند و این کارشان را که پیامبران را به ناحق کشتند، در کارنامه عملشان خواهیم نوشت و به آنان خواهیم گفت: عذاب آتش را بچشید،'),
(475, 3, 182, 'این ثبت اعمال و کیفر به سزای کارهایی است که به دست خود پیش فرستادید و بدان سبب است که خدا به بندگان خود هیچ ستم نمی‌کند.'),
(476, 3, 183, 'همانان که گفتند: خدا به ما فرمان داده است که به هیچ پیامبری ایمان نیاوریم تا برای ما قربانییی بیاورد که آتش [آسمانی] آن را در کام خود فرو برد. بگو: پیامبرانی پیش از من معجزات و دلایلی روشن و آنچه را که گفتید، برای شما آوردند، اگر راست می‌گویید، چرا آنان را کشتید؟'),
(477, 3, 184, 'ای پیامبر، اگر تو را دروغگو می‌شمرند عجب نیست، زیرا پیش از تو نیز پیامبرانی که معجزات و دلایل آشکار و کتاب‌های مشتمل بر حکمت و اندرز و کتاب‌های روشنی بخش آورده بودند تکذیب شدند.'),
(478, 3, 185, 'هر کسی مرگ را خواهد چشید، و جز این نیست که روز قیامت پاداش‌های شما به طور کامل به شما داده می‌شود. پس هر کس از آتش دور نگاه داشته شود و او را به بهشت درآورند، نیکبخت است، و زندگی دنیا چیزی جز کالای فریب نیست.'),
(479, 3, 186, 'قطعاً شما در مورد مال و جانتان آزموده می‌شوید، و حتماً از کسانی که پیش از شما کتاب آسمانی به آنان داده شده است (یهود و نصارا) و نیز از کسانی که به شرک روی آورده‌اند، سخنان آزاردهنده بسیار خواهید شنید، و اگر شکیبایی کنید و تقوا پیشه سازید، از لغزش و سستی مصون می‌مانید و مردمی با اراده می‌شوید که شکیبایی از تصمیم قاطع در کارهاست.'),
(480, 3, 187, 'و یاد کن هنگامی را که خداوند از کسانی که کتاب آسمانی به آنان داده شده است پیمان گرفت که آن را برای مردم به روشنی بیان کنید و حقایق آن را کتمان نکنید، ولی آن را پشت سرشان انداختند و با کالای ناچیز دنیا مبادله کردند. پس بد است آنچه خریداری می‌کنند.'),
(481, 3, 188, 'مپندار کسانی که به آنچه کرده‌اند شادی می‌کنند و دوست دارند به آنچه [از فرمان‌های خدا] انجام نداده‌اند مورد ستایش قرار گیرند، مپندار که آنان از عذاب رهایی می‌یابند، بلکه برای آنان عذابی دردناک خواهد بود.'),
(482, 3, 189, 'فرمانروایی آسمان‌ها و زمین از آنِ خداست، و خدا بر هر کاری تواناست.'),
(483, 3, 190, 'همانا در آفرینش آسمان‌ها و زمین و در کاستی و فزونی شب و روز نشانه‌هایی [بر اقتدار خدا و فرمانروایی او بر آسمان‌ها و زمین] است. این نشانه‌ها را خردمندان درمی یابند،'),
(484, 3, 191, 'همانان که در هر حال ـ ایستاده و نشسته و بر پهلو آرمیده ـ خدا را یاد می‌کنند، و در آفرینش آسمان‌ها و زمین می‌اندیشند که: پروردگارا، این جهان را بی هدف نیافریده‌ای، تو از هر کار باطل و بیهوده‌ای منزّهی، ازاین رو تحقق رستاخیز و کیفر و پاداش آن حتمی است، پس پروردگارا، ما را از عذاب آتش آن روز نگه دار.'),
(485, 3, 192, 'پروردگارا، به یقین هر که را تو به آتش درآوری او را خوار و بی مقدار کرده‌ای، و ستمکاران یاورانی ندارند که آنان را از عذاب آتش برهانند.'),
(486, 3, 193, 'پروردگارا، ما ندای ندادهنده‌ای را که به ایمان دعوت می‌کرد که: ای مردم، به پروردگارتان ایمان بیاورید شنیدیم و ایمان آوردیم. پروردگارا، پس گناهان ما را بر ما ببخشای و بدی هایمان را از ما بزدای و ما را در حالی بمیران که در زمره نیکان باشیم.'),
(487, 3, 194, 'پروردگارا، و آنچه به ما وعده داده‌ای و پیامبرانت آن را بر عهده تو ضمانت کرده‌اند به ما عطا کن و روز قیامت ما را خوار مگردان که قطعاً تو خلف وعده نمی‌کنی.'),
(488, 3, 195, 'پس پروردگارشان به آنان پاسخ داد که من عمل نیکوی هیچ عمل کننده‌ای از شما را ـ مرد باشد یا زن ـ تباه نمی‌کنم و پاداش آن را به طور کامل به شما می‌دهم. شما همه از یکدیگرید و هیچ کدام بر دیگری برتری ندارید، پس کسانی که هجرت کرده و به خاطر ایمانشان از دیار خود بیرون رانده شده و در راه من آزار دیده و کسانی که جنگیده و کشته شده‌اند، حتماً گناهانشان را از آنها می‌زدایم و آنان را به بوستان‌هایی پوشیده از درختان که از زیر آنها نهرها روان است درمی آورم. این پاداش آنهاست، پاداشی از جانب خدا، و خداست که پاداش نیکو نزد اوست.'),
(489, 3, 196, 'گشت و گذار کافران در شهرها و دستیابی آنان به سودهای فراوان تو را فریب ندهد.'),
(490, 3, 197, 'این بهره‌ای است اندک، چرا که پایان پذیر است، سپس جایگاهشان دوزخ است و آن بد بستری است.'),
(491, 3, 198, 'ولی کسانی که از کیفر پروردگارشان پروا کرده‌اند، برای آنان در بهشت بوستان‌هایی انبوه از درختان خواهد بود که از زیر آنها نهرها روان است و در آن جا جاودانه‌اند. آنان با این نعمت‌ها از جانب خدا پذیرایی می‌شوند، و آنچه نزد خداست برای نیکان بهتر است.'),
(492, 3, 199, 'و قطعاً از میان اهل کتاب (یهود و نصارا) کسانی هستند که به خدا و به آنچه به سوی شما مسلمانان نازل شده و به آنچه به سوی خودشان فرو فرستاده شده است ایمان دارند، در حالی که در برابر خدا فروتن اند و آیات خدا را به متاع ناچیز دنیا نمی‌فروشند. آنان پاداش خود را نزد پروردگارشان خواهند داشت، و خداوند کارها را به سرعت، آن گاه که انجام می‌گیرد، حسابرسی می‌کند.'),
(493, 3, 200, 'ای کسانی که ایمان آورده‌اید، بر ناگواری‌ها و بر پرهیز از گناهان و اطاعت از خدا شکیبایی کنید و با اتکا به صبر و پایداری یکدیگر بر مقاومت خویش بیفزایید و در امور دین و دنیا با یکدیگر همبستگی داشته باشید، و از عذاب خدا پروا کنید، باشد که نیکبخت شوید.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'ای مردم، درباره همنوعان خود از پروردگارتان پروا کنید و حقوق یکدیگر را پاس دارید، همان کسی که شما را از یک تن (آدم) آفرید و همسرش را از نوع او خلق کرد، و از آن دو، مردان و زنان بسیاری را پراکنده ساخت. آری، از خدایی که او را بزرگ و عزتمند می‌دانید و به نام او از یکدیگر درخواست می‌کنید، پروا بدارید و از [پایمال کردن حقوق] خویشاوندان خود نیز پروا کنید و همبستگی میان خود و آنان را محترم شمرید، که خدا بر شما نگهبان است و اعمال شما را برای پاداش و جزا محفوظ می‌دارد.'),
(495, 4, 2, 'و اموال یتیمان را به آنان بدهید، و اموال نامرغوب خود را با اموال مرغوب آنان جا به جا نکنید، و اموالشان را با ضمیمه کردن به اموال خود، مخورید که آن گناهی بزرگ است.'),
(496, 4, 3, 'و اگر بیم دارید که در صورت ازدواج با دختران یتیم عدل و انصاف را رعایت نکنید، از دیگر زنان که مورد پسند شمایند، دو تا یا سه تا یا چهار تا به همسری گیرید، و اگر بیم دارید که نتوانید میان چند همسر به عدالت رفتار کنید، تنها یک زن را به ازدواج خویش درآورید یا به کنیزانی که مالک آنانید بسنده کنید. این برای ستم نکردن به آنان و رعایت حقوقشان نزدیک‌ترین راه است.'),
(497, 4, 4, 'مهر زنان را که هدیه‌ای است، به آنان بدهید. پس اگر چیزی از آن را با رضایت خاطر به شما بخشیدند، آن را بخورید. گوارا و نوش جانتان خواهد بود.'),
(498, 4, 5, 'و اموال یتیمان را که در اختیار شماست و خدا اموال را سبب برپایی زندگی شما مردم قرار داده است به کم خردانشان ندهید که آن را تباه می‌سازند، بلکه از درآمد آن به ایشان خوراک و پوشاک دهید و با آنان به نیکی سخن گویید.'),
(499, 4, 6, 'و یتیمان را بیازمایید تا آن گاه که به حدّ ازدواج برسند، پس اگر در آنان بصیرتی یافتید که می‌توانند براساس آن در مصرف مال خردورزی کنند، اموالشان را به آنان بدهید و آن را به اسراف (بی آن که حق و حقوقی در آن داشته باشید) مخورید، و مبادا از ترس این که چون بزرگ شوند و شما را از تصرف در اموالشان باز دارند، اینک فرصت را مغتنم شمرده از آن بهره برداری کنید. و اگر اموالشان را در تجارت به کار می‌برید، هر که توانگر است، از گرفتن کارمزد خودداری کند، و هر که بینواست، به قدر متعارف از آن بخورد. و هنگامی که اموالشان را به آنان بازمی گردانید بر آنان گواه بگیرید، و خدا حسابرسی کافی است.'),
(500, 4, 7, 'برای مردان، از آنچه پدر و مادر و خویشاوندان نزدیک ترشان بر جای نهاده‌اند سهمی از ارث است و برای زنان نیز از آنچه پدر و مادر و خویشاوندان نزدیک ترشان بر جای گذاشته‌اند سهمی از ارث خواهد بود ـ خواه آن مال کم باشد یا زیاد ـ سهمی است که قطعی شده است.'),
(501, 4, 8, 'و هرگاه خویشاوندان متوفی که از ارث بهره‌ای ندارند، و یتیمان و بینوایان بر سر تقسیم ارث حاضر شدند، شما وارثان چیزی از ارث را به آنان نیز بدهید و به طور پسندیده با آنان سخن بگویید.'),
(502, 4, 9, 'و کسانی که اگر پس از خود فرزندان خردسال و ناتوانی بر جای می‌گذارند که از ستم دیگران در حق آنان نگرانند باید بترسند و از خدا پروا کنند و در حق یتیمان دیگران ستم روا ندارند و حقوقشان را تباه نسازند این راهی است که کسی در حق یتیمان آنان ستم نکند و باید درباره یتیمان سخن استوار گویند و رفتاری درست در پیش گیرند.'),
(503, 4, 10, 'کسانی که اموال یتیمان را به ناحق می‌خورند، جز این نیست که آتشی در شکم خود فرو می‌برند و دیری نمی‌گذرد که به آتش سوزان دوزخ می‌سوزند.'),
(504, 4, 11, 'خدا به شما درباره فرزندانتان سفارش می‌کند که سهم پسر از میراث، مانند سهم دو دختر است. این در صورتی است که وارثان، دختر و پسر باشند، و اگر همه دختر و بیش از دو تن باشند، سهم آنان دو سوم چیزی است که متوفی برجای نهاده است، و اگر فقط یک دختر باشد، سهم او نصف میراث است. و برای هر یک از پدر و مادر متوفی از آنچه بر جای نهاده یک ششم است. این در صورتی است که متوفی فرزند داشته باشد، ولی اگر فرزندی نداشته باشد و تنها پدر و مادرش از او ارث ببرند، یک سوم از آنِ مادر [و بقیه از آنِ پدر] است. این در صورتی است که متوفی برادرانی نداشته باشد، ولی اگر برادرانی داشته باشد، یک ششم میراث از آنِ مادر [و بقیه از آنِ پدر] است. این سهام پس از استثنای موردی است که متوفی بدان وصیت کرده و پس از استثنای دین اوست. شما نمی‌دانید پدرانتان و پسرانتان کدام یک از آنان سودشان به شما نزدیک‌تر است تا میراث را براساس آن تقسیم کنید. پس به سهم‌هایی که از جانب خدا مقرر شده است ملتزم باشید. به راستی خدا دانا و حکیم است.'),
(505, 4, 12, 'و نیمی از آنچه زنانتان بر جای نهاده‌اند سهم شماست اگر آنان فرزندی نداشته باشند، و اگر فرزندی داشته باشند یک چهارم آنچه بر جای نهاده‌اند از آنِ شماست، البته این سهام پس از استثنای موردی است که همسرانتان بدان وصیت کرده‌اند و پس از دینی است که برعهده داشته‌اند و یک چهارم آنچه شما مردان بر جای نهاده‌اید سهم آنان است اگر فرزندی نداشته باشید، و اگر فرزندی داشته باشید یک هشتم میراث برای آنان (همسرانتان) خواهد بود، البته پس از استثنای موردی که بدان وصیت کرده‌اید یا دینی که برعهده داشته‌اید. و اگر مرد یا زنی که از او ارث برده می‌شود، پدر [یا مادر] و یا فرزند وارث نیست و میّت تنها یک برادر مادری یا یک خواهر مادری دارد، برای هر یک از آن دو یک ششم میراث خواهد بود و اگر بیش از یک خواهر و برادر مادری باشند، همه در یک سوم ارث شریکند که باید به طور مساوی میان خود تقسیم کنند، البته این سهام پس از استثنای موردی که بدان وصیت شده یا دینی است که برعهده اوست، به شرط آن که به قصد زیان رساندن به ورثه آن را برعهده نگرفته باشد. پس در عمل به سفارشی که از جانب خداست پایدار باشید که خدا دانا و بردبار است.'),
(506, 4, 13, 'این سهام، احکام و حدود خداست، و هر کس از خدا و پیامبر او پیروی کند، خداوند او را به بوستان‌هایی پوشیده از درختان که از زیر آنها نهرها روان است، درمی آورد. آنان در آن جا جاودانه‌اند، و این است سعادت بزرگ.'),
(507, 4, 14, 'و هر کس خدا و پیامبر او را نافرمانی کند و از احکام و حدود الهی تجاوز نماید، خدا او را به آتشی درمی آورد که در آن جاودانه خواهد بود، و برای او عذابی خوارکننده است.'),
(508, 4, 15, 'و از زنان شوهردار شما مسلمانان، کسانی که مرتکب عمل بسیار زشت زنا می‌شوند، چهار مرد از خودتان (مسلمانان) را بر آنان گواه گیرید، پس اگر گواهی دادند، آنها را در خانه‌ها نگاه دارید تا مرگ آنان را برگیرد یا خداوند راهی دیگر برای کیفرشان مقرر کند.'),
(509, 4, 16, 'و از میان شما مسلمانان، آن زن و مرد مجرّدی که مرتکب فحشا می‌شوند آزارشان دهید، پس اگر توبه کردند و شایسته شدند، از آزارشان دست بردارید که خداوند بسیار توبه پذیر و مهربان است.'),
(510, 4, 17, 'پذیرش توبه از سوی خدا برای کسانی است که از روی نادانی مرتکب گناه می‌شوند، سپس به زودی ـ پیش از آن که با مشاهده آثار مرگ، فرصت توبه را از دست بدهند ـ توبه می‌کنند. آنانند که خدا با رحمت خود به سویشان باز می‌گردد و توبه شان را می‌پذیرد، و خدا به راز دل‌ها داناست و توبه کنندگان واقعی را از غیر آنان باز می‌شناسد و کارهایش نیز همه از روی حکمت است.'),
(511, 4, 18, 'پذیرش توبه برای کسانی نیست که پیوسته مرتکب گناهان می‌شوند تا هرگاه که مرگ به سراغ یکیشان بیاید (آثار آن را مشاهده کند.)، بگوید: اکنون توبه کردم، و نیز برای کسانی نیست که در حال کفر می‌میرند. آنانند که عذابی دردناک برایشان آماده کرده‌ایم.'),
(512, 4, 19, 'ای کسانی که ایمان آورده‌اید، بر شما روا نیست که زنان را به رغم این که خواهان ازدواجند از ازدواج باز دارید تا از آنان ارث ببرید، و نباید همسران خود را در تنگنا قرار دهید تا بخشی از آنچه را که مهرشان قرار داده‌اید در برابر طلاق دادن آنان بازستانید، مگر آن که گناهی مرتکب شوند که زشتی آن آشکار است، و با آنها به نیکی معاشرت کنید، و اگر آنان را خوش نمی‌دارید، باز هم با آنان سازگاری کنید، زیرا چه بسا شما چیزی را خوش نمی‌دارید ولی خداوند خیر فراوانی در آن قرار می‌دهد.'),
(513, 4, 20, 'و اگر خواستید همسر خود را طلاق دهید و به جای او همسر دیگری بگیرید، و به یکی از آنان که می‌خواهید طلاقشان دهید، به اندازه پوستی پر از طلا مهریه داده‌اید، چیزی از آن را بازپس نگیرید. آیا می‌خواهید آن را بازپس بگیرید در حالی که این کار ستمی بهت آور و گناهی آشکار است؟'),
(514, 4, 21, 'و چگونه آن را به ستم بازپس می‌گیرید در حالی که سخت به یکدیگر پیوند خورده بودید و آنها با عقد زوجیت پیمانی استوار از شما گرفته بودند و بدین وسیله مهریه از آنِ ایشان شده بود؟'),
(515, 4, 22, 'و با زنانی که پدرانتان آنها را به همسری گرفته‌اند ازدواج نکنید، مگر آنچه که پیش از بیان این حکم گذشته است، که این کرداری زشت و مایه خشم [خدا] است و بد راه و روشی است.'),
(516, 4, 23, 'بر شما حرام شده است مادرانتان و دخترانتان و خواهرانتان و عمه هایتان و خاله هایتان و دختران برادر و دختران خواهر و مادران رضاعی تان که شما را شیر داده‌اند و خواهران رضاعی تان، و مادران همسرانتان و دختران همسرانتان که در دامان شما پرورش می‌یابند از آن همسرانی که با آنها همبستر شده‌اید، و اگر با آنها همبستر نشده‌اید بر شما گناهی نیست که با دخترانشان ازدواج کنید، و همسران پسرانتان که از صلب شما هستند ـ نه آنهایی که پسرخوانده‌اند ـ نیز بر شما حرام شده است، و این که دو خواهر را باهم به ازدواج خود درآورید، مگر آنچه که پیش از بیان این حکم گذشته است، آنها مورد عفو خدا قرار گرفته و آثار ازدواج مشروع بر آن مترتب است، زیرا خدا آمرزنده و مهربان است.'),
(517, 4, 24, 'و زنان شوهردار نیز بر شما حرام شده است، مگر کنیزانی که مالک آنها هستید. به احکام الهی که بر شما نوشته شده است پایدار باشید. و جز این زنان بر شما حلالند که با اموال خود که مهریه (در مورد زنان آزاده) یا بهای آنان (در مورد کنیزان) قرار می‌دهید آنان را طلب کنید، در حالی که پاکدامن باشید نه آلوده به کار زشت زنا. و از زنان آنهایی را که با ازدواج موقّت به همسری خود درآورده‌اید، مهرشان را که با قرار شما و آنان واجب شده است به آنان بدهید. و بر شما گناهی نیست که پس از تعیین [مهریه و مدّت] در آنچه با یکدیگر به توافق برسید و آن را تغییر دهید. قطعاً خدا به همه امور داناست و کارهایش از روی حکمت است.'),
(518, 4, 25, 'و هر کس از شما قدرت مالی ندارد که با زنان مؤمن آزاد ازدواج کند، از کنیزان با ایمان که شما مسلمانان مالک آنها هستید به همسری گیرد. خدا به ایمان شما داناتر است، و همه نمی‌توانند مؤمنان واقعی را از غیر واقعی باز شناسند پس باید به مقتضای ظاهر عمل کنید. شما همه، اعم از آزاد و برده، از یکدیگرید و نزد خدا باهم برابرید. بنابراین، کنیزان را به این شرط که پاکدامن باشند، نه پلید کار و نه دوست گیرنده از مردان، با اذن سرپرستانشان به همسری گیرید، و مهرشان را برحسب متعارف به آنها بپردازید; و هنگامی که شوهر کردند، اگر مرتکب کار زشتی شدند، نیمی از کیفری که برای زنان آزاد است، برای آنان خواهد بود. این (ازدواج با کنیزان) برای کسی از شماست که بیم آن دارد که با ترک ازدواج در رنج بیفتد; و البته صبر کردن شما و خودداری از ازدواج با آنان برای شما بهتر است، و خدا آمرزنده و مهربان است.'),
(519, 4, 26, 'خدا می‌خواهد احکام و معارف دین را برای شما بیان کند و شما را به سنّت‌های پیامبران و امّت‌های پسندیده‌ای که پیش از شما بودند هدایت نماید و با رحمت و بخشایش خویش به سوی شما باز آید، و خدا داناست و احکام و قوانینی را که مقرّر می‌دارد همه از روی حکمت است.'),
(520, 4, 27, 'آری، خدا می‌خواهد با رحمت و بخشایش خود به سوی شما باز آید، ازاین رو احکام خود را برای شما بیان می‌کند، ولی کسانی که در پی خواسته‌های نفسانی خود هستند، می‌خواهند شما با شکستن حدود الهی دستخوش انحرافی بزرگ شوید.'),
(521, 4, 28, 'خدا می‌خواهد با تشریع ازدواج دائم و موقّت کار را بر شما آسان گیرد، زیرا انسان کم طاقت آفریده شده است.'),
(522, 4, 29, 'ای کسانی که ایمان آورده‌اید، در اموالی که میان شما دست به دست می‌گردد، به ناروا تصرف نکنید و با سبب‌های نادرست آنها را از دست مردم خارج نکنید و صاحب نشوید مگر این که داد و ستدی برخاسته از توافق شما باشد، و یکدیگر را نکشید و خود را نیز در معرض قتل قرار ندهید، از آن روی خدا شما را از قتل باز می‌دارد که به شما مهربان است.'),
(523, 4, 30, 'و هر کس از روی تجاوز و ستم چنین کند، به زودی او را به آتشی خواهیم سوزانید، و این بر خدا آسان است.'),
(524, 4, 31, 'اگر از کبیره‌های گناهانی که از آن نهی می‌شوید اجتناب کنید، گناهان صغیره شما را از شما می‌پوشانیم و آنها را می‌آمرزیم و شما را به جایگاهی ارجمند (بهشت و قرب الهی) درمی آوریم.'),
(525, 4, 32, 'و آنچه را که خداوند برخی از شما را به وسیله آن بر برخی دیگر برتری داده است آرزو مکنید، چرا که مردان از آنچه به اقتضای طبیعت یا کار و کوشش خود فراهم آورده‌اند بهره‌ای دارند، و زنان نیز از آنچه به اقتضای طبیعت یا کسب و کار خویش به دست آورده‌اند بهره‌ای دارند. شما امتیازات و دستاوردهای آنان را مخواهید، بلکه به خدا روی آورید و از او بخواهید تا شما را از فضل خود بهره مند سازد، که خدا به هر چیزی داناست.'),
(526, 4, 33, 'و ما برای همه، از آنچه پدر و مادر و خویشاوندانِ نزدیکتر و همسرانی که با آنها پیمان بسته‌اید، برجای می‌گذارند، وارثانی قرار داده‌ایم، پس سهمشان را از میراث به آنان بدهید که خدا بر هر چیزی گواه است.'),
(527, 4, 34, 'مردان عهده دار امور زندگی زنانند. این از آن روست که خداوند برخی از مردم را بر برخی دیگر به خردمندی و توانمندیِ بیشتر برتری داده، و نیز از آن روست که مردان از اموال خود هزینه زندگی زنان را می‌پردازند. پس زنان شایسته در برابر خواسته‌های بحقِّ شوهرانشان فرمانبردارند و به پاس این که خداوند حقوقشان را حفظ کرده است، در غیاب شوهرانشان نگهبان حقوق آنانند. و زنانی را که از سرکشی آنان بیم دارید پند دهید، اگر نتیجه نداشت، در خوابگاه‌ها از آنان کناره گیری کنید و اگر اثربخش نبود آنان را بزنید. پس اگر در مورد حقوقی که برعهده آنان دارید از شما اطاعت کردند، راهی برای آزارشان مجویید که خدا بلند مرتبه و بزرگ است.'),
(528, 4, 35, 'و اگر از دشمنی و جدایی میان آن دو بیم دارید، داوری از خانواده شوهر و داوری از خانواده زن تعیین کنید. اگر آنها خود بخواهند به نوعی میانشان آشتی برقرار شود، خداوند میان آن دو به وسیله داوران سازگاری ایجاد خواهد کرد، چرا که خدا به حال آنان دانا و به کارشان آگاه است.'),
(529, 4, 36, 'و خدا را بپرستید و چیزی را شریک او نسازید، و به پدر و مادر و خویشاوند و یتیمان و بینوایان و همسایه نزدیک و همسایه دور و همنشین و در راه مانده و برده و کنیزی که مالک آنهایید نیکی کنید. به یقین خداوند کسی را که متکبّر و خیالبافِ فخرفروش است دوست نمی‌دارد.'),
(530, 4, 37, 'همان کسانی که بخل میورزند و با عملکرد زشت خویش مردم را به بخل وامی دارند و نعمت‌هایی را که خدا به فضل خود به آنان عطا کرده است کتمان می‌کنند و خود را فقیر جلوه می‌دهند. و ما برای کسانی که نعمت‌های الهی را به منظور محروم ساختن نیازمندان پوشیده می‌دارند و حق را انکار می‌کنند عذابی خوارکننده، آماده کرده‌ایم.'),
(531, 4, 38, 'متکبّران خیالباف و فخرفروش کسانی اند که اموالشان را برای نشان دادن به مردم انفاق می‌کنند و به خدا و روز قیامت ایمان ندارند. اینانند که همدمشان شیطان است، و هر کس شیطان همدم او باشد، او بد همنشینی است.'),
(532, 4, 39, 'و اگر به خدا و روز قیامت ایمانی راستین داشتند و از آنچه خدا روزیِ آنان ساخته است در راه او انفاق می‌کردند، چه زیانی برایشان داشت؟ و خدا به حال و کار آنان داناست.'),
(533, 4, 40, 'چنین نمی‌کنند با این که خداوند هموزن ذره‌ای به آنان ستم نمی‌کند و از پاداش آنان نمی‌کاهد. بلکه، اگر آن ذره، کاری پسندیده باشد خدا آن را دو چندان می‌کند و از نزد خود پاداشی بزرگ به آنان می‌دهد.'),
(534, 4, 41, 'پس چگونه است حالشان آن گاه که از هر امّتی گواهی بیاوریم تا بر اعمالشان گواهی دهد، و تو را ای پیامبر بر حقّانیت آن گواهان گواه بیاوریم؟'),
(535, 4, 42, 'آن روز که چنین شود، کسانی که کفر ورزیده و پیامبر را نافرمانی کرده‌اند، آرزو می‌کنند که با خاک یکسان شده بودند. آن روز آنان نمی‌توانند هیچ سخنی را از خدا پوشیده دارند.'),
(536, 4, 43, 'ای کسانی که ایمان آورده‌اید، در حال مستی برای نماز گزاردن به هیچ مسجدی نزدیک نشوید شما را از شرابخواری بازداشتیم تا بدانید که در نماز چه می‌گویید، و در حال جنابت نیز وارد مسجد نشوید تا غسل کنید، مگر این که بخواهید از آن جا عبور کنید. و اگر بیمارید یا در سفرید یا یکی از شما از قضای حاجت آمده است یا با زنان همبستر شده‌اید و برای وضو یا غسل آبی نیافتید، به سراغ زمینی که از حالت طبیعی خود خارج نشده است بروید و بخشی از صورت و دست هایتان را با آن مسح کنید، که خدا از شما درمی گذرد و شما را می‌آمرزد.'),
(537, 4, 44, 'آیا به کسانی که به آنان بهره‌ای از کتاب آسمانی داده‌اند ننگریسته‌ای که خریدار گمراهی اند و می‌خواهند شما نیز از راه راست گمراه شوید؟'),
(538, 4, 45, 'آنان دشمنان شمایند، و خدا دشمنان شما را بهتر می‌شناسد، پس مبادا شما را با اظهار محبت و وعده یاری فریب دهند. کافی است که خدا سرپرست شما باشد و کافی است که خدا یاورتان باشد.'),
(539, 4, 46, 'دشمنان شما یهودیانند، آنان سخنان را از جایگاهش می‌گردانند و تحریف می‌کنند و می‌گویند: شنیدیم و نافرمانی کردیم، و در خطاب به پیامبر می‌گویند: بشنو که شنوایی داده نشوی! این سخن را از خطاب (راعنا) در حالی که زبانشان را به آن می‌پیچانند و در دین طعنه می‌زنند، اراده می‌کنند. و اگر می‌گفتند: شنیدیم و فرمانبرداری کردیم، و بشنو و به ما مهلت ده تا سخن خود را به روشنی بیان کنیم، برای آنان بهتر و استوارتر بود، ولی چنین انتظاری از آنان به دور است، زیرا خداوند آنان را به سزای کفرشان لعنت کرده است، ازاین رو جز اندکی از آنان ایمان نخواهند آورد.'),
(540, 4, 47, 'ای کسانی که کتاب آسمانی به شما داده شده است، به این قرآن که به سوی شما فرو فرستاده‌ایم و تورات و انجیلی را که با شماست تأیید می‌کند، ایمان بیاورید، پیش از آن که چهره‌هایی را که باید روی به کمال آورد محو کنیم و در نتیجه آنها را به واپس (روی به تیره بختی) بازگردانیم، یا همان گونه که (اصحاب سبت) را از رحمت خود دور ساختیم، آنان را نیز از رحمت خود دور سازیم، و فرمان خدا انجام شدنی است.'),
(541, 4, 48, 'انکار قرآن شرک است و قطعاً خدا این گناه را که به او شرک ورزیده شود نمی‌آمرزد، و کمتر از آن را برای هر که بخواهد می‌آمرزد. و هر کس به خدا شرک ورزد، قطعاً دروغی بافته و گناهی بزرگ مرتکب شده است.'),
(542, 4, 49, 'آیا به کسانی که خود را پاک و رشدیافته می‌شمرند ننگریسته‌ای؟ آنان حق ندارند زبان به مدح خویش بگشایند، بلکه خداست که هر که را بخواهد به پاکی می‌ستاید و در ستایش آنان به اندازه رشته هسته خرمایی به آنان ستم نمی‌شود.'),
(543, 4, 50, 'بنگر که یهودیان چگونه بر خدا دروغ می‌بندند، و همین بس که این کارشان گناهی آشکار است.'),
(544, 4, 51, 'آیا به کسانی که بهره‌ای از کتاب آسمانی یافته‌اند (یهودیان) ننگریسته‌ای که به (جبت) و (طاغوت) ایمان می‌آورند و درباره کفرپیشگان مشرک می‌گویند: اینان از کسانی که [به محمّد] ایمان آورده‌اند راهیافته ترند؟'),
(545, 4, 52, 'آنان کسانی اند که خدا از رحمت خود دورشان ساخته است، و هر که را خدا از رحمت خود دور سازد، هرگز برای او یاوری نخواهی یافت.'),
(546, 4, 53, 'مگر آنان حق دارند در هر امری داوری کنند با این که مشرکان را راهیافته‌تر از مؤمنان شمرده‌اند؟ یا مگر آنان از فرمانروایی بهره‌ای دارند [تا پیامبری را به کسی بخشند یا از کسی بازدارند]؟ اگر بهره هم داشته باشند، از شدّت بخل، به اندازه آنچه پرنده با منقار خود برمی گیرد چیزی به مردم نمی‌دهند.'),
(547, 4, 54, 'یا به سبب مزیّتی که خدا از فضل خود به مردم داده است بر آنان حسد میورزند؟ حسادت برایشان سودی ندارد چرا که ما خود به خاندان ابراهیم (که پیامبر و اهل بیتش از آنانند) کتاب آسمانی و دانش دین دادیم و به آنان فرمانرواییِ بزرگ (پیامبری، مقام ولایت و هدایت) بخشیدیم.'),
(548, 4, 55, 'البته یهودیان همه یکسان نیستند، برخی از آنان به پیامبر ایمان آورده و برخی، نه تنها به او نگرویده، بلکه دیگران را نیز از گرایش به او بازداشته‌اند. آنان روانه دوزخ خواهند شد و دوزخ کافی است که سوزان و شعلهور باشد.'),
(549, 4, 56, 'کسانی که به آیات ما کفر ورزیده‌اند، به زودی آنان را به آتشی درآورده می‌سوزانیم. هرگاه پوستشان بریان شود، به جای آن پوست دیگری می‌نهیم تا عذاب را بچشند. به راستی که خدا شکست ناپذیر و حکیم است.'),
(550, 4, 57, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، به زودی آنان را به بوستان‌هایی پوشیده از درختان که زیر آنها جوی‌ها روان است درمی آوریم. آنان برای همیشه در آن جا ماندگارند، و برایشان در آن جا همسرانی پیراسته خواهد بود، و آنان را به سایه‌ای پایدار درمی آوریم.'),
(551, 4, 58, 'خدا به شما فرمان می‌دهد که امانت‌ها را به صاحبان آنها رد کنید، و چون میان مردم داوری می‌کنید، به عدالت داوری کنید. آنچه خدا شما را به آن پند می‌دهد نیکوست. به راستی که خدا شنوا و بیناست.'),
(552, 4, 59, 'ای کسانی که ایمان آورده‌اید، خدا را اطاعت کنید و از پیامبر و کسانی که امر شما را در اختیار دارند و از خودتان هستند پیروی نمایید. پس اگر به خدا و روز قیامت ایمان دارید، چنانچه در امری از امور دین اختلاف کردید، آن را به خدا و پیامبر ارجاع دهید. این کار برای شما بهتر و فرجامش نیکوتر است.'),
(553, 4, 60, 'آیا به کسانی که بر این باورند که به آنچه به سوی تو فرو فرستاده شده و به آنچه پیش از تو نازل گردیده ایمان آورده‌اند، ننگریسته‌ای که می‌خواهند داوری در مرافعات خود را نزد طاغوت ببرند، با این که فرمان یافته‌اند بدان کفر ورزند؟ و شیطان می‌خواهد آنان را به بیراهه‌ای که با حق فاصله‌ای دور و دراز دارد سوق دهد.'),
(554, 4, 61, 'و هنگامی که به آنان گفته می‌شود: به سوی آنچه (احکامی که) خدا فرو فرستاده و به سوی این پیامبر که بدانها حکم می‌کند بیایید، منافقان را می‌بینی که از تو سخت روی برمی تابند.'),
(555, 4, 62, 'پس چگونه خواهد بود حالشان آن گاه که بر اثر کاری که به دست خود کرده‌اند گزندی به آنان رسد، سپس نزد تو آیند و به خدا سوگند خورند که هدف ما از بُردنِ داوری نزد دیگران چیزی جز نیکی و سازش میان مشاجره کنندگان نبود؟'),
(556, 4, 63, 'اینان کسانی اند که خدا آنچه را که در دل دارند می‌داند، پس از آنان درگذر و پندشان ده و با آنان سخنی بگوی که بر دل هایشان نشیند.'),
(557, 4, 64, 'ما هیچ پیامبری را نفرستادیم مگر برای این که به اذن خدا در همه امور از او پیروی شود، پس چگونه با بهانه‌هایی مانند برقراری سازش میان دو طرف دعوا، از پیامبر روی می‌گردانند؟ اگر آنان پس از آن که با این عمل بر خود ستم کردند نزد تو می‌آمدند و از خدا آمرزش می‌خواستند و پیامبر نیز برای آنان طلب آمرزش می‌کرد، قطعاً خدا را توبه پذیر و مهربان می‌یافتند.'),
(558, 4, 65, 'پس چنین نیست که اگر مردم رسالت تو را تصدیق کنند مؤمن باشند، به پروردگارت سوگند تا آنان تو را در آنچه میانشان مایه اختلاف است داور نسازند، آن گاه از داوری تو در خود احساس هیچ گونه دلتنگی نکنند و کاملاً تسلیم حکم تو نباشند، مؤمن نخواهند بود.'),
(559, 4, 66, 'و اگر بر آنان مقرّر می‌کردیم که یکدیگر را بکشید یا از خانه‌ها و دیار خود بیرون روید، جز اندکی از آنان این کار را نمی‌کردند، و اگر آنان آنچه را بدان پند داده می‌شوند، انجام می‌دادند، قطعاً برای دنیا و آخرتشان بهتر بود و به ایمانشان ثبات بیشتری می‌بخشید.'),
(560, 4, 67, 'و در آن صورت ما از نزد خود پاداشی بزرگ به آنان می‌دادیم.'),
(561, 4, 68, 'و آنان را به راهی راست هدایت می‌کردیم.'),
(562, 4, 69, 'و کسانی که از خدا و پیامبر اطاعت کنند، با کسانی خواهند بود که خداوند آنان را از نعمت ولایت خویش برخوردار ساخته است. آنان پیامبران و راستی پیشگان و گواهان اعمال و شایستگان مقام ولایت الهی اند و نیکو رفیقانی خواهند بود.'),
(563, 4, 70, 'این است فزونبخشی کامل و آن از جانب خداست و علم خدا به مراتب ایمان مردم برای مشخص ساختن مقام بایسته آنان کافی است.'),
(564, 4, 71, 'ای کسانی که ایمان آورده‌اید، سلاح خود را برگیرید و همواره آماده کارزار باشید و آن گاه که شمار دشمنان اندک است گروه گروه، و چون فراوان است دسته جمعی به سویشان حرکت کنید.'),
(565, 4, 72, 'و قطعاً از میان شما مؤمنان کسی هست که در حرکت به سوی جهاد کُندی می‌کند، پس اگر در جهاد گزندی به شما رسد می‌گوید: خدا به من نعمت ارزانی داشت که با آنان در جهاد حاضر نبودم.'),
(566, 4, 73, 'و اگر از جانب خدا تفضّلی (غنیمت و پیروزی) به شما رسد، چنان که گویی میان شما و او هیچ گونه رابطه دوستی نبوده است، خواهد گفت: ای کاش من با آنان بودم و به کامیابی بزرگی دست می‌یافتم.'),
(567, 4, 74, 'پس کسانی که زندگی دنیا را با آخرت مبادله می‌کنند باید در راه خدا پیکار کنند، و هر کس در راه خدا پیکار کند و کشته شود یا پیروز گردد، دیری نگذرد که او را مزدی بزرگ ببخشیم.'),
(568, 4, 75, 'برای شما چه سودی دارد که در راه خدا و در راه مردان و زنان و کودکانی که ناتوان شمرده شده و مورد ستم کفرپیشگان قرار گرفته اند نمی‌جنگید؟ همانان که می‌گویند: پروردگارا، ما را از این شهری که مردمش ستمگرند بیرون بر، و از جانب خودت برای ما سرپرستی بگمار، و از نزد خویش برای ما یاوری مقرر دار.'),
(569, 4, 76, 'کسانی که ایمان آورده‌اند، در راه خدا پیکار می‌کنند و کسانی که کافر شده‌اند، در راه طاغوت می‌جنگند، پس با دوستان شیطان پیکار کنید که قطعاً نیرنگ شیطان سست است.'),
(570, 4, 77, 'آیا ندیدی کسانی را که به آنان گفته شد: اینک ـ به رغم آزار مشرکان ـ دست از جهاد بدارید و نماز را برپا دارید و زکات بدهید تا امکانات جنگ برای شما فراهم شود. امّا هنگامی که پیکار بر آنان واجب شد، برخلاف انتظار، گروهی از ایشان از مردم [= مشرکان] ترسیدند، مانند ترس از خدا یا ترسی بیشتر، و گفتند: پروردگارا، چرا پیکار را بر ما مقرر داشتی؟ چرا مرگ ما را تا زمان مقرر آن به تأخیر نینداختی که آن سرآمدی نزدیک است؟ ای پیامبر، به آنان بگو: بهره دنیا اندک است و سرای آخرت برای کسی که تقوا پیشه کرده است بهتر است و بر شما به اندازه رشته هسته خرمایی ستم نمی‌رود و از پاداشتان کاسته نمی‌شود، پس نباید از پیکار در راه خدا هراسی در دل داشته باشید.'),
(571, 4, 78, 'گمان نکنید که اگر در جهاد حضور نیافتید از مرگ در امان می‌مانید، هر کجا باشید شما را مرگ در می‌رسد، هر چند در دژهایی استوار و مرتفع به سر برید. و اگر خیری به آنان رسد می‌گویند: این از جانب خداست، و اگر شرّی به ایشان رسد می‌گویند: این از جانب توست. به آنان بگو: همه از نزد خداست. پس این مردم را چه شده که نزدیک است هیچ سخنی را درنیابند.'),
(572, 4, 79, 'هر خیری به تو رسد از جانب خداست و هر ناگواری به تو رسد از خود توست. ما تو را برای مردم به رسالت فرستادیم و به تو سِمتی دیگر ندادیم تا بتوانی خوشی یا ناگواری برایشان بیاوری، و همین بس که خدا بر این امر گواه باشد.'),
(573, 4, 80, 'هر کس از پیامبر فرمان برد از خدا فرمان برده است، و هر کس روی برتابد تو اندوهگین مباش، زیرا ما تو را به عنوان نگهبان بر آنان نفرستاده‌ایم تا مسئول اعمالشان باشی و برای رویگردانی آنان مؤاخذه شوی. (۸۰'),
(574, 4, 81, 'و چون مردم سست ایمان را به جهاد فراخوانی می‌گویند: ما از تو اطاعت می‌کنیم، ولی هنگامی که از نزد تو بیرون می‌روند، گروهی از آنان شبانگاه به چیزی جز آنچه گفته‌ای و با تو وعده کرده اند می‌اندیشند و درباره آن چاره جویی می‌کنند. البته خدا آنچه را شبانه بر ضد تو می‌اندیشند ثبت می‌کند. پس، از آنان روی برتاب و بر خدا توکل کن که خدا برای کارسازی امور بسنده است.'),
(575, 4, 82, 'پس آیا در قرآن نمی‌اندیشند تا به هماهنگی آیات آن پی ببرند و دریابند که آن از جانب خدا نازل شده است؟ اگر قرآن از جانب غیر خدا بود، قطعاً در آن ناسازگاری و ناهماهنگی بسیاری می‌یافتند.'),
(576, 4, 83, 'و چون از سوی دشمنان خبری که حاکی از امنیت یا ترس است به آنان برسد، آن را در میان مؤمنان پخش می‌کنند و باعث سستی آنان در برابر دشمن می‌شوند، و اگر آن را به پیامبر یا صاحبان فرمان خود بازمی گرداندند، کسانی از آنان که حقیقت ماجرا را با بررسی و تحقیق کشف می‌کنند، درستی یا نادرستی آن را تشخیص می‌دادند. و اگر فضل و رحمت خدا شامل حال شما نبود، قطعاً همه تان ـ جز شماری اندک ـ از شیطان پیروی می‌کردید و از همراهی با پیامبر در جهاد با مشرکان سر باز می‌زدید.'),
(577, 4, 84, 'پس تو خود ـ ای پیامبر ـ در راه خدا پیکار کن، هر چند مردم در جهاد سستی کنند، و بدان که جز به کار خود مکلّف نیستی، و مؤمنان را به پیکار برانگیز، باشد که خداوند گزند کافران را از مؤمنان بازدارد، و خداست که عذابش سخت‌تر و کیفرش شدیدتر است.'),
(578, 4, 85, 'هر کس در کار خیری وساطت کند، از فرجام نیک آن بهره‌ای خواهد داشت، و هر کس در کار شرّی میانجیگری کند، از فرجام سوء آن نصیبی خواهد برد، و خدا بر انجام هر کاری تواناست و آن را برپا می‌دارد.'),
(579, 4, 86, 'و چون به شما سلام شود، با سلامی نیکوتر سلام را پاسخ دهید، یا مانند همان را بازگردانید که خداوند حسابگر هر چیزی است.'),
(580, 4, 87, 'به فرمان‌های الهی پای بند باشید، زیرا قطعاً خداوند که معبودی شایسته پرستش جز او نیست، شما را در روز قیامت که هیچ تردیدی در آن وجود ندارد گرد می‌آورد تا به شما در برابر اعمالتان سزا دهد، و چه کسی از خدا راستگوتر است؟'),
(581, 4, 88, 'اکنون که می‌دانید هر کسی در کار ناپسندی وساطت کند، از فرجام بد آن بهره‌ای خواهد داشت، چرا درباره آن منافقان دو گروه شده‌اید، گروهی خواهان پیکار با آنانید و گروهی از آنان حمایت می‌کنید به این امید که شاید هدایتشان کنید؟ در حالی که خداوند آنان را به کیفر اعمال ناپسندشان به گمراهی بازگردانده است. آیا می‌خواهید کسانی را که خدا گمراهشان کرده است هدایت کنید؟ و هر که را خدا گمراه کند هرگز راهی برای هدایت او نمی‌یابی.'),
(582, 4, 89, 'آنان دوست دارند همان گونه که خود کافر شده‌اند شما نیز کافر شوید و همه باهم یکسان باشید. پس مبادا از میان آنان پیش از آن که در راه خدا هجرت کنند دوستانی برگیرید. شما آنان را به هجرت فراخوانید، اگر روی برتافتند هر کجا آنان را یافتید بگیریدشان و بکشیدشان، و از آنها کسی را دوست و یاور خویش مگیرید.'),
(583, 4, 90, 'مگر آن کسانی که به گروهی می‌پیوندند که میان شما و آنان پیمان عدم تعرض است و یا آن کسانی که نزد شما می‌آیند که نه خوش داشته‌اند که همگام قوم خود باشند و با شما بجنگند و نه خوش دارند که همراه شما باشند و با قوم خود پیکار کنند، اگر خدا می‌خواست آنان را بر شما مسلّط می‌ساخت و آنها با شما به جنگ برمی خاستند [پس مبادا ناتوانی آنها شما را به جنگ با آنان ترغیب کند]. اینک اگر از شما کناره گیری کردند و با شما نجنگیدند و صلح و آشتی با شما را مطرح کردند، با آنان درگیر نشوید، که خدا برای شما هیچ راهی بر ضدّ آنان قرار نداده است.'),
(584, 4, 91, 'به زودی گروهی دیگر را خواهید یافت که می‌خواهند از سوی شما و از سوی قوم خویش در امان باشند، به گونه‌ای که نه با شما باشند و نه بر ضد شما. هر چند به قول و قرار آنان اطمینانی نیست، چون هرگاه بر ضدّ شما به سوی فتنه و آشوبی بازگردانده شدند، به آن روی آوردند، [ولی پیشنهادشان را بپذیرید]، پس اگر از تعرض به شما کناره گیری نکردند و به شما پیشنهاد صلح ندادند و از شما دست برنداشتند، هر کجا آنان را یافتید بگیریدشان و بکشیدشان. اینانند که برای شما حجتی روشن بر ضدشان قرار داده‌ایم.'),
(585, 4, 92, 'هیچ مؤمنی نشاید که مؤمنی را جز به اشتباه بکشد، و هر کس مؤمنی را به اشتباه بکشد باید برده با ایمانی را آزاد کند و خونبهایی نیز به کسان مقتول بپردازد، مگر این که آنان خونبها را به عنوان تصدق به قاتل ببخشند. و اگر مقتول از گروهی است که دشمن شمایند و با شما سر جنگ دارند ولی او مؤمن بوده است، قاتل باید برده مؤمنی را آزاد کند و خونبها برعهده او نیست، و اگر مقتولِ مؤمن از مردم کفرپیشه‌ای است که میان شما و آنان پیمانی هست که به مقتضای آن پیمان کافر حَربی به شمار نمی‌آیند، قاتل باید خونبهایی به کسان وی بپردازد و برده مؤمنی را نیز آزاد کند. و هر کس از آزاد کردن برده ناتوان است باید دو ماه پیاپی روزه بگیرد. این حکم، بازگشت رحمتی از جانب خدا بر ناتوانان است. و خدا به شما داناست و احکامی را که مقرر می‌کند از روی حکمت است.'),
(586, 4, 93, 'و هر کس مؤمنی را از روی عمد بکشد سزای او دوزخ است که برای همیشه در آن خواهد ماند، و خداوند بر او خشم می‌گیرد و از رحمت خود دورش می‌کند و برای او عذابی بزرگ آماده کرده است.'),
(587, 4, 94, 'ای کسانی که ایمان آورده‌اید، هنگامی که در راه خدا برای جهاد سفر می‌کنید و با افرادی رو به رو می‌شوید، خوب بررسی کنید تا مؤمنان را از کافران بازشناسید، تا مبادا مؤمنی را به تصور آن که کافر است بکشید، و به کسی که به رسم مسلمانان به شما سلام می‌کند نگویید مؤمن نیستی و او را بدون تحقیق به قتل برسانید که با این کار خواهان متاع ناپایدار زندگی دنیا به شمار می‌آیید. بدانید که غنیمت‌های بسیاری نزد خداست، آنها را طلب کنید. شما نیز پیش از آن که ایمان بیاورید چنین بودید که همواره در پی دستیابی به متاع دنیا بودید، ولی خداوند نعمتی گران به شما عطا کرد و این خصلت را از شما زدود. بنابراین خوب بررسی کنید تا هر کسی را به گمان کفر نکشید، قطعاً خدا به آنچه می‌کنید آگاه است.'),
(588, 4, 95, 'مؤمنانِ وانشسته از جهاد که آسیب مند نیستند، با آنان که با مال و جان خود در راه خدا پیکار می‌کنند یکسان نیستند، زیرا خداوند کسانی را که با مال و جان خود جهاد می‌کنند در مقام و منزلت بر وانشستگان برتری داده، و خدا همه مؤمنان ـ چه مجاهدان و چه آسیب مندان و چه وانشستگان با ایمانی که حضورشان در جهاد ضرورت ندارد ـ را به فرجامی نیکو مژده داده است، و خدا مجاهدان را بر وانشستگان برتری بخشیده و برای آنان پاداشی بزرگ مقرر داشته است.'),
(589, 4, 96, 'آن پاداش درجاتی از جانب او و آمرزش و رحمتی بزرگ است، و خداوند آمرزنده و مهربان است.'),
(590, 4, 97, 'کسانی که با پشت سر افکندن معارف الهی و احکام دین بر خود ستم کرده‌اند، هنگامی که فرشتگان جانشان را می‌گیرند به آنان می‌گویند: در چه حال بودید؟ چرا به دین الهی پای بند نبودید؟ می‌گویند: ما در این سرزمین تحت سیطره کفر و شرک ناتوان شمرده شده بودیم. فرشتگان می‌گویند: مگر زمین خدا گسترده نبود که در آن هجرت کنید و به سرزمینی بروید که بتوانید دین خود را نگه دارید؟ ازاین رو آنان جایگاهشان دوزخ است و آن بدبازگشتگاهی است.'),
(591, 4, 98, 'مگر آن مردان و زنان و کودکانِ زبون شمرده شده‌ای که نمی‌توانند برای رهایی خود چاره‌ای بیندیشند و راهی برای خروج از تحت سیطره کافران نمی‌یابند.'),
(592, 4, 99, 'اینانند که امید است خدا از آنها درگذرد، چرا که خداوند بخشاینده و آمرزنده است.'),
(593, 4, 100, 'هر کس برای رهایی از سیطره کافران و مشرکان در راه خدا هجرت کند، در زمین مکان‌های بسیار و گشایش و فراخی خواهد یافت، و هر کس از خانه خود بیرون رود در حالی که به سوی خدا و رسول او رهسپار می‌شود، سپس مرگ او را دریابد، پاداشش برعهده خدا لازم شده است، و خدا آمرزنده و مهربان است.'),
(594, 4, 101, 'و هنگامی که سفر کردید، اگر بیم آن دارید که کافران بر شما بتازند و شما را بیازارند، بر شما گناهی نیست که از رکعات نماز بکاهید، به یقین کافران برای شما دشمنی آشکارند.'),
(595, 4, 102, 'و اگر آنان از یورش دشمن در امان نبودند و خواستند نماز بگزارند، چنانچه تو ای پیامبر، در میانشان بودی و برای آنان نماز برپا کردی، باید گروهی از ایشان با تو به نماز بایستند و سلاح‌های خود را نیز برگیرند و هنگامی که سجده کردند و نماز را به پایان بردند باید پشت سر شما قرار گیرند و به نگهبانی بپردازند و گروه دیگری که نماز نخوانده‌اند بیایند و با تو نماز بگزارند، و باید جانب احتیاط را نگه دارند و سلاح‌های خود را برگیرند. کافران آرزو می‌کنند که شما از سلاح‌ها و ساز و برگ خود غافل شوید تا یکباره بر شما بتازند. و اگر از باران در زحمتید، یا بیمارید، بر شما گناهی نیست که سلاح‌های خود را بر زمین نهید، ولی جانب احتیاط را نگه دارید. به یقین خدا برای کافران عذابی خوارکننده آماده کرده است.'),
(596, 4, 103, 'و هنگامی که نماز را به جای آوردید، خدا را در هر حال، ایستاده و نشسته و بر پهلوی خویش آرمیده، یاد کنید، و چون به وطن بازگشتید و از هجوم دشمن در امان شدید نماز را به صورت کامل به پا دارید، زیرا نماز بر مؤمنان واجبی همیشگی و تبدیل ناپذیر است.'),
(597, 4, 104, 'و در دستیابی به گروه مشرکان سستی مکنید. اگر شما از جراحات و تلفات خود در جنگ درد می‌برید، آنان نیز مانند شما از زخم‌ها و کشته‌های خود درد می‌برند، ولی با این حال با یکدیگر یکسان نیستید، زیرا شما چیزهایی از خدا امید دارید که آنها امید ندارند، و خدا به مصلحت بندگان داناست و فرمان هایش استوار و حکیمانه است.'),
(598, 4, 105, 'ما این کتاب را که سراسر حق است به سوی تو فرو فرستادیم، تا براساس آنچه خدا آن را به تو نمایانده است، میان مردم داوری کنی، و نباید مدافع خیانتکاران باشی.'),
(599, 4, 106, 'و از خدای بخواه که تو را از پیروی هوای نفس و دفاع از خیانتکاران مصون دارد، چرا که خداوند مصون دارنده و مهربان است.'),
(600, 4, 107, 'و از کسانی که با ارتکاب گناهان به خود خیانت می‌کنند دفاع مکن، که خداوند کسی را که خیانتکار و گناه پیشه است دوست نمی‌دارد.'),
(601, 4, 108, 'آنان از مردم شرم می‌کنند و خیانت‌های خود را پنهان می‌دارند ولی از خدا شرم نمی‌کنند و در حضور او مرتکب گناه می‌شوند، در صورتی که خدا ـ هنگامی که آنان شبانه درباره سخنی که او نمی‌پسندد می‌اندیشند و برای تحقق آن چاره جویی می‌کنند ـ با ایشان است. و خدا نه تنها از این کارشان آگاه است، بلکه به هر کاری که می‌کنند احاطه دارد.'),
(602, 4, 109, 'هان، شمایید کسانی که در زندگی دنیا از آنان دفاع کردید. اگر هم این دفاع شما برای آنان سودمند باشد کیست که روز قیامت در برابر خدا از آنان حمایت کند؟ یا کیست که در آن روز کارهایشان را سامان دهد؟'),
(603, 4, 110, 'پس به سود خیانتکاران است که به درگاه خدا توبه برند، زیرا هر کس به دیگری بدی کند یا با ارتکاب گناه بر خود ستم روا دارد سپس از خدا آمرزش بخواهد، خدا را آمرزنده و مهربان خواهد یافت.'),
(604, 4, 111, 'و هر کس گناهی کند، آن را تنها بر ضد خود انجام می‌دهد، و خدا کسی را که مرتکب گناه شده است می‌شناسد و دیگری را به جای او مؤاخذه نمی‌کند، زیرا او دانا و حکیم است.'),
(605, 4, 112, 'و هر کس دچار لغزش گردد یا گناهی مرتکب شود، سپس آن را به گردن بی گناهی بیفکند، کیفر تهمت و گناه آشکاری را به دوش کشیده است.'),
(606, 4, 113, 'و اگر فضل و رحمت خدا شامل حال تو نبود، گروهی از آنان تصمیم داشتند تو را گمراه سازند و به دفاع از خیانتکاران وادارت کنند و حال آن که جز خویشتن را به گمراهی نمی‌افکنند و هیچ زیانی به تو نمی‌رسانند، زیرا خداوند کتاب و حکمت را بر تو فرو فرستاده و حقایقی را به تو آموخته است که خود نمی‌توانستی آنها را فراگیری، و فضل خدا همواره بر تو بزرگ بوده است.'),
(607, 4, 114, 'در بسیاری از رازگویی‌های آنان خیری نیست، مگر این که کسی در رازگویی خود به دادن صدقه یا انجام کاری نیک یا اصلاح میان مردم فرمان دهد. و هر کس برای دستیابی به خشنودی خدا چنین کند به زودی پاداشی بزرگ به او خواهیم داد.'),
(608, 4, 115, 'و هر کس، پس از آن که مسیر هدایت برای او آشکار شد با پیامبر به مخالفت برخیزد و راهی جز راه مؤمنان در پیش گیرد، در دنیا او را به سوی همان مسیری که برگزیده است سوق می‌دهیم و در آن راه به او یاری می‌رسانیم و در آخرت او را به آتش دوزخ می‌سوزانیم، و آن بدبازگشتگاهی است.'),
(609, 4, 116, 'مخالفت با پیامبر در ردیف شرک به خداست، و قطعاً خداوند این گناه را که به او شرک ورزیده شود نمی‌آمرزد، و کمتر از آن را برای هر که بخواهد می‌آمرزد. و هر کس به خدا شرک ورزد، به یقین در بیراهه‌ای خواهد افتاد که با حق فاصله‌ای دور و دراز دارد.'),
(610, 4, 117, 'آنان به جای خدا جز موجوداتی تأثیرپذیر را که درخور پرستش نیستند، نمی‌پرستند، و جز شیطان را که خیری در او نیست، نمی‌پرستند.'),
(611, 4, 118, 'خدا او را از رحمت خود دور ساخته است. و شیطان به خداوند گفت: من از بندگانت بهره‌ای معین خواهم گرفت.'),
(612, 4, 119, 'و قطعاً آنان را با ترغیب به پرستش معبودانی ساختگی به گمراهی می‌افکنم، و با آرزومند ساختن آنان به سرگرمی‌ها فریبشان می‌دهم و آنان را وا می‌دارم تا گوش دام‌ها را بشکافند (دام‌هایی را که حلال کرده‌ای حرام بشمارند) و به آنان فرمان می‌دهم تا آفرینش خدا را دگرگون سازند و از مسیر فطرت خارج گردند. و هر کس به جای خدا شیطان را کارساز خویش گیرد، قطعاً زیانی آشکار کرده است.'),
(613, 4, 120, 'شیطان به آنان وعده‌های دروغ می‌دهد و آرزوهای پوچ در دلشان می‌افکند و شیطان چیزی جز فریب به آنان وعده نمی‌دهد.'),
(614, 4, 121, 'آنان جایگاهشان دوزخ است و گریزگاهی از آن برای خود نخواهند یافت.'),
(615, 4, 122, 'و بزودی کسانی را که ایمان آورده و کارهای شایسته کرده‌اند به بوستان‌هایی آکنده از درختان که در زیر آنها نهرها روان است درمی آوریم. در آن جا برای همیشه ماندگارند. خدا آن را وعده داده است، وعده‌ای راست و درست. و کیست که از خدا راست گفتارتر باشد؟'),
(616, 4, 123, 'حمایت خدا و پیامبر نه براساس آرزوها و پندارهای شما مسلمانان است و نه براساس آرزوها و پندارهای اهل کتاب. هر کس بدی کند، در دنیا و آخرت به سزای آن، کیفر خواهد دید، و جز خدا کارساز و یاوری برای خود نخواهد یافت.'),
(617, 4, 124, 'و هر کس کارهایی شایسته انجام دهد و با ایمان باشد ـ مرد باشد یا زن ـ به بهشت درمی آید و به اندازه چیزی که پرنده به منقار برمی گیرد، به آنان ستم نمی‌شود.'),
(618, 4, 125, 'و دین چه کسی نیکوتر از دین کسی است که خود را با تمام وجود تسلیم خدا کرده و نیکوکردار است و از آیین ابراهیم که حقگرا و دور از انحراف بود، پیروی نموده است؟ و خدا ابراهیم را چون با تمام وجود تسلیم خدا و نیکوکار و حق گرا بود دوست خویش گرفت.'),
(619, 4, 126, 'با این که آنچه در آسمان‌ها و آنچه در زمین است، از آنِ خداست، و خدا بر همه چیز احاطه دارد.'),
(620, 4, 127, 'ای پیامبر، از تو درباره زنان فتوا می‌خواهند، بگو: خداست که درباره آنان به شما فتوا می‌دهد، چنان که پیش‌تر نیز احکامی را درباره آنان بیان کرد، و نیز خداست که در این کتاب پیرامون آنچه درباره زنان یتیم بر شما تلاوت می‌شود، به شما فتوا می‌دهد، زنان یتیمی که حقوق مقررشان را از آنان دریغ می‌دارید و از این که با آنها ازدواج کنید، دوری می‌کنید. و نیز خداست که در این کتاب درباره کودکانِ ناتوان شمرده شده‌ای که حقوقشان را از میراث نمی‌دهید به شما فتوا می‌دهد [و شما را از پایمال کردن حقشان برحذر می‌دارد.] و همچنین خداست که درباره یتیمان به شما فتوا می‌دهد که با آنان به عدل و داد رفتار کنید. و هر کار نیکی انجام دهید خدا آن را می‌داند.'),
(621, 4, 128, 'و اگر زنی از ناسازگاری یا رویگردانی شوهرش بیم داشته باشد، بر آن دو گناهی نیست که او با گذشت از بخشی از حقوقش میان خودشان صلح و سازش برقرار کنند و سازش بهتر [از جدایی] است. ولی آزمند بودن مردم به حقوق خود خصلتی است که همواره در برابر آنان حضور دارد و آنان را به دفاع از حق خود وامی دارد. پس اگر شما مردان احسان کنید و راه تقوا در پیش گیرید و همسرانتان را به گذشت از حقوق خویش وادار نسازید [سزاوارتر است] که خدا به آنچه می‌کنید آگاه است.'),
(622, 4, 129, 'شما هرگز نمی‌توانید میان زنان عدالت را به طور کامل اجرا کنید، هر چند بر آن کوشا و حریص باشید. پس نباید همه میل خود را به یکی از آنان معطوف دارید و دیگری را بلاتکلیف ـ که نه چون شوهرداران است و نه بی شوهران که بتواند همسری گیرد ـ به خود وانهید. و اگر میان خود صلح و سازش برقرار کنید و تقوا پیشه سازید، خداوند آمرزش و رحمتش را بر شما می‌گستراند که خدا آمرزنده و مهربان است.'),
(623, 4, 130, 'و اگر زن و شوهر به طلاق از یکدیگر جدا شدند، بیمی از درماندگی به خود راه ندهند، زیرا خداوند آن دو را به گشایش خویش بی نیاز خواهد ساخت، که خدا بخششی بی پایان دارد و کارهایش همه از روی حکمت است،'),
(624, 4, 131, 'زیرا آنچه در آسمان‌ها و آنچه در زمین است از آنِ خداست. ما به کسانی که پیش از شما به آنان کتاب آسمانی داده شده و به شما سفارش کردیم که از خدا پروا کنید و در همسرداری حقوق یکدیگر را رعایت نمایید. و اگر با کفرورزی به ترک تقوا کشیده شدند، خدا به شما و تقوایتان نیازی ندارد، چرا که آنچه در آسمان‌ها و آنچه در زمین است از آنِ اوست و خدا بی نیاز و ستوده است.'),
(625, 4, 132, 'و از آنِ خداست آنچه در آسمان‌ها و آنچه در زمین است و خدا برای اداره امور بندگان بس است.'),
(626, 4, 133, 'ای مردم، اگر خدا بخواهد، شما را وامی نهد و مردمی دیگر می‌آورد که فرمان او را اطاعت کنند، و خدا بر این کار تواناست.'),
(627, 4, 134, 'هر کس پاداش (نیکبختی) دنیا را می‌خواهد باید به دین خدا گرایش یابد، زیرا پاداش دنیا و آخرت نزد خداست، و خدا شنوا و بیناست.'),
(628, 4, 135, 'ای کسانی که ایمان آورده‌اید، به عدالت رفتار کنید و در پاسداری از آن سخت بکوشید، و برای خدا گواهی دهید هرچند گواهی شما به زیان خودتان یا پدر و مادر و خویشاوندان نزدیکتان باشد. اگر آن کسی که در حق او شهادت می‌دهید توانگر یا تهیدست باشد، توانگری و تهیدستی او شما را از شهادت به حق باز ندارد که خدا به رعایت حال آنان از شما سزاوارتر است، پس از هوای خویش پیروی نکنید که مبادا از حق منحرف شوید. و اگر زبان خود را به گواهی ناحق بپیچانید یا از گواهی دادن روی برتابید، بدانید که خدا به آنچه می‌کنید آگاه است و کیفرش در انتظار شماست.'),
(629, 4, 136, 'ای کسانی که ایمان آورده‌اید، شما باید همه حقایق دین را تصدیق کنید پس به خدا و رسول او و کتابی که بر پیامبرش فروفرستاده و آن کتاب آسمانی که پیش‌تر نازل کرده است ایمان بیاورید. و هر کس به خدا و فرشتگان او و کتاب‌هایی که فروفرستاده است و به پیامبرانش و روز آخرت کافر شود در بیراهه‌ای که با حق فاصله‌ای دور و دراز دارد گمراه گشته است.'),
(630, 4, 137, 'کسانی که نزد مؤمنان حقایق دین را تصدیق کردند، سپس در معاشرت با کافران به همه یا برخی از آنها کافر شدند، بعد از آن با حضور نزد مؤمنان آنها را تصدیق کردند و باز هم در ملاقات با کافران به آنها کافر شدند، سپس بر کفر خویش افزودند، خدا آنان را نخواهد آمرزید و به راهی [که ایمان را بجویند] هدایتشان نخواهد کرد.'),
(631, 4, 138, 'آنان منافقند، و منافقان را نوید ده که برایشان عذابی دردناک خواهد بود.'),
(632, 4, 139, 'منافقان کسانی اند که به جای مؤمنان، کافران را دوست و یاور خویش می‌گیرند. آیا عزّت را نزد آنان می‌جویند؟ با این که عزت به تمامی از آنِ خداست.'),
(633, 4, 140, 'در حالی چنین می‌کنید که خداوند در این کتاب بر شما نازل کرده است که وقتی می‌شنوید آیات خدا انکار می‌گردد و به استهزا گرفته می‌شود، با منکران و استهزاکنندگان منشینید تا این که وارد سخن دیگری غیر از آن شوند، چرا که شما در این صورت مانند آنان خواهید بود. قطعاً خداوند منافقان و کافران را یکسره در دوزخ گرد می‌آورد.'),
(634, 4, 141, 'منافقان کسانی اند که در انتظار فرجام کار شما به سر می‌برند، پس اگر از جانب خدا پیروزی و گشایشی برای شما حاصل شود می‌گویند: آیا ما با شما نبودیم؟ پس ما نیز در بهره‌ها و غنایم سهمی داریم. و اگر برای کافران نصیبی از پیروزی باشد، به آنان می‌گویند: آیا ما بر شما چیره نشدیم و شما را از پیوستن به مؤمنان بازنداشتیم؟ پس خداوند روز قیامت میان شما داوری می‌کند، و آن روز هرگز به سود کافران راهی بر ضد مؤمنان قرار نخواهد داد.'),
(635, 4, 142, 'منافقان می‌خواهند به خدا نیرنگ زنند، در حالی که خدا با مهلتی که به آنان می‌دهد، به ایشان نیرنگ می‌زند، و چون به نماز بایستند با سستی می‌ایستند، به مردم خودنمایی می‌کنند و خدا را جز اندکی یاد نمی‌کنند.'),
(636, 4, 143, 'آنان به قهر الهی میان ایمان و کفر سرگردان گشته‌اند، نه با مؤمنانند و نه با کافران. خدا آنان را به سزای کردارشان گمراه ساخته است، و هر که را خدا گمراه کند، هرگز راهی برای هدایت او نخواهی یافت.'),
(637, 4, 144, 'ای کسانی که ایمان آورده‌اید، کافران را به جای مؤمنان دوست و یاور خود مگیرید. آیا می‌خواهید برای خدا بر ضد خود حجّتی آشکار پدید آورید؟'),
(638, 4, 145, 'قطعاً منافقان در فروترین طبقه از آتش دوزخ خواهند بود، و هرگز برای آنان یاوری نجات بخش نخواهی یافت.'),
(639, 4, 146, 'مگر کسانی از آنان که توبه کنند و راه صلاح در پیش گیرند و با پیروی از قرآن و سنّت پیامبر به خدا تمسّک جویند و دین خود را برای خدا خالص کنند و از هرگونه شرکی بپرهیزند، که اینان با مؤمنان خواهند بود، و به زودی خداوند پاداشی بزرگ به مؤمنان عطا خواهد کرد.'),
(640, 4, 147, 'اگر خدا را بر نعمت هایش سپاس بگزارید و به او ایمان بیاورید او را با عذاب شما چه کار؟ خدا سپاسگزار شاکران و به شایستگان سپاس آگاه است.'),
(641, 4, 148, 'خداوند دوست ندارد که آشکارا از کسی بدگویی شود، ولی کسی که به او ستم شده است گناهی بر او نیست که آشکارا از ستم کننده به بدی یاد کند. و خدا همه سخنان را می‌شنود و از آنها آگاه است.'),
(642, 4, 149, 'اگر کار نیکی را آشکارا یا به طور پنهانی انجام دهید یا از بدی ای که به شما شده است درگذرید، به اخلاق الهی آراسته گشته‌اید، زیرا خدا درگذرنده است با این که بر کیفر گناهکاران تواناست.'),
(643, 4, 150, 'یهودیان و مسیحیان که به خدا و پیامبرانش کفر میورزند و می‌خواهند میان خدا و پیامبرانش جدایی بیفکنند، زیرا می‌گویند به برخی از پیامبران ایمان داریم و برخی دیگر را انکار می‌کنیم، و می‌خواهند میان ایمان به خدا و همه پیامبران و کفر به خدا و پیامبران راهی برگزینند (به خدا و برخی از پیامبران ایمان داشته باشند)،'),
(644, 4, 151, 'آنان به راستی کافرند، و ما برای کافران عذابی خوارکننده آماده کرده‌ایم.'),
(645, 4, 152, 'و کسانی که به خدا و پیامبرانش ایمان آورده و میان هیچ یک از پیامبران به کفر و ایمان جدایی نیفکنده‌اند، به زودی خدا پاداششان را به آنان خواهد داد، و خدا آمرزنده و مهربان است.'),
(646, 4, 153, 'اهل کتاب (یهودیان و مسیحیان) با وجود قرآن که هیچ گونه تردیدی در آسمانی بودن آن نیست، از تو می‌خواهند که برای آنان از آسمان کتابی یکجا فرود آوری. این درخواستِ جاهلانه و برخاسته از لجاجت از آنها بعید نیست، چرا که بزرگ‌تر از این را از موسی درخواست کردند و گفتند: خدا را آشکارا به ما بنمای تا او را با چشمان خود بنگریم. پس به سزای ستمشان (درخواست نادرستشان) صاعقه آنان را فرو گرفت. گذشته از این، پس از آن که دلایلی روشن بر نادرستی بت پرستی در اختیارشان قرار گرفته بود آن گوساله را به خدایی گرفتند و ما از این هم درگذشتیم، و به موسی تسلطی آشکار بر گوساله پرستان و بر سامری که آن گوساله را ساخته بود عطا کردیم.'),
(647, 4, 154, 'و برای این که از آنان پیمان بگیریم کوه طور را بالای سرشان برافراشتیم، و نیز به آنان گفتیم: سجده کنان [=با فروتنی] به آن دروازه درآیید، و گفتیم: در روز شنبه به مقررات آن روز تجاوز نکنید، و از آنان بر انجام این فرمان‌ها پیمانی استوار گرفتیم.'),
(648, 4, 155, 'پس به سبب این که پیمانشان را شکستند و به آیات خدا کافر شدند و پیامبران را به ناحق کشتند و گفتند: دل‌های ما از دریافت پیام محمد در پرده است ـ چنین نیست، بلکه خدا به سزای کفرشان بر دل‌های آنان مهر نهاده است، ازاین رو جز اندکی از آنان ایمان نمی‌آورند ـ'),
(649, 4, 156, 'و نیز به سبب کفرشان و آن تهمت بزرگی که به مریم زدند،'),
(650, 4, 157, 'و این که گفتند: ما مسیح، عیسی پسر مریم، پیامبر خدا را کشتیم، در حالی که او را نکشتند و او را به صلیب نکشیدند، بلکه امر بر آنان مشتبه شد و دیگری را به جای او کشتند و قطعاً کسانی که درباره چگونگی قتل او اختلاف کرده‌اند که آیا به صلیب کشیده شد یا به گونه‌ای دیگر کشته شد درباره اصل کشته شدن او تردید دارند و از فرجام وی آگاهی ندارند و فقط حدس و گمان را دنبال می‌کنند و مسلماً او را به هیچ طرزی نکشتند،'),
(651, 4, 158, 'بلکه خدا او را به سوی خود بالا برد، و خدا شکست ناپذیر و حکیم است.'),
(652, 4, 159, 'و هیچ کس از اهل کتاب نیست مگر این که به عیسی پیش از آن که او بمیرد ایمان می‌آورد و او روز قیامت بر آنان گواهی می‌دهد.'),
(653, 4, 160, 'آری، در اثر ستمی که از یهودیان برخاست و مکرر از راه خدا روی برتافتند، چیزهای پاکیزه‌ای که برایشان حلال شده بود بر آنان حرام کردیم،'),
(654, 4, 161, 'و نیز به سبب این که ربا می‌گرفتند با این که از آن نهی شده بودند، و این که اموال مردم را به ناروا تصرف و تصاحب می‌کردند. و برای کافرانشان عذابی دردناک آماده ساخته‌ایم که در سرای آخرت به آن خواهند رسید.'),
(655, 4, 162, 'ولی کسانی از آنان که در آگاهی از معارف الهی پایدارند و نیز مؤمنان راستینشان، به کتابی که بر تو نازل شده و به کتاب‌های آسمانی که پیش از تو نازل شده است ایمان می‌آورند، و همچنین برپاکنندگان نماز که سزاوار ستایشند و پرداخت کنندگان زکات، و کسانی که به خدا و روز واپسین ایمان دارند، اینانند که به زودی پاداشی بزرگ عطایشان خواهیم کرد.'),
(656, 4, 163, 'آری، آنان به قرآن ایمان می‌آورند، زیرا ما آن را به تو وحی کرده‌ایم، همان گونه که به نوح و پیامبران بعد از او وحی کردیم و همان گونه که به ابراهیم، اسماعیل، اسحاق، یعقوب، اسباط (پیامبرانی از نسل یعقوب)، عیسی، ایوب، یونس، هارون و سلیمان وحی فرستادیم و به داود کتاب آسمانی دادیم.'),
(657, 4, 164, 'و پیامبرانی که سرگذشتشان را پیش‌تر برای تو خوانده‌ایم و پیامبرانی که داستانشان را برای تو حکایت نکرده‌ایم، و خدا با موسی سخن گفت سخن گفتنی.'),
(658, 4, 165, 'پیامبرانی که مردم را به پاداش الهی بشارت دادند و از کیفر او بیم دادند، خداوند آنان را برای اتمام حجت گسیل داشت تا پس از رسالت پیامبران برای مردم در برابر خدا حجتی نباشد که ما حقیقت را درنیافتیم. و خدا شکست ناپذیر و کارهایش همه از روی حکمت است و کسی نمی‌تواند در احتجاج، بر او چیره شود.'),
(659, 4, 166, 'دلیل حقانیت قرآن تنها همگونی رسالت تو با رسالت دیگر پیامبران نیست، بلکه خداوند خود در مورد آنچه به سوی تو فروفرستاده گواه است. او گواهی می‌دهد که آن را با علم خود به سوی تو نازل کرده است، و فرشتگان نیز که حامل وحی اند به راستی و درستی آن گواهی می‌دهند، و کافی است که خدا بر این گواه باشد.'),
(660, 4, 167, 'بی تردید، کسانی که کفر ورزیده و با رویگردانی از قرآن از راه خدا روی برتافته‌اند، به بیراهه‌ای رفته‌اند که با حق فاصله‌ای دور و دراز دارد.'),
(661, 4, 168, 'کسانی که کافر شده و با انکار قرآن بر خود ستم کرده‌اند نشاید که خدا آنان را بیامرزد و نشاید که آنان را به راهی هدایت کند،'),
(662, 4, 169, 'مگر به راه دوزخ که برای همیشه در آن ماندگارند، آن بر خدا آسان است.'),
(663, 4, 170, 'ای مردم، این پیامبر برای شما از جانب پروردگارتان چیزی را آورده است که سراسر حق است، پس به او ایمان بیاورید که این برای شما بهتر است. و اگر کافر شوید، از حاکمیت و مالکیت خدا چیزی نکاسته‌اید، زیرا آنچه در آسمان‌ها و زمین است از آنِ اوست، و خدا دانا و حکیم است.'),
(664, 4, 171, 'ای اهل کتاب، در دین خود غلوّ نکنید (از آنچه بایسته است فراتر نروید) و درباره خدا چیزی جز حق نگویید. جز این نیست که مسیح، عیسی پسر مریم، پیامبر خدا و کلمه او بود که آن را به سوی مریم افکند و او امری از جانب خدا بود. پس به خدا و یکتایی او در ربوبیت و به فرستادگان او ایمان آورید و نگویید: خدای یگانه سه تاست (پدر، پسر و روح القُدُس)، از این سخن باز ایستید که برای شما بهتر است. جز این نیست که خداوند معبودی یگانه است. او از داشتن فرزند منزّه است، زیرا آنچه در آسمان‌ها و آنچه در زمین است، از جمله عیسی پسر مریم، همه مِلک اوست. و همین بس که خدا عهده دار امور بندگان باشد و کارشان را در تکوین و تشریع تدبیر کند.'),
(665, 4, 172, 'مسیح هرگز از این که بنده خدا باشد ابایی ندارد و آن را ناخوش نمی‌شمرد و فرشتگان مقرب او نیز این گونه‌اند. چگونه از پرستش خدا رویگردان باشند در حالی که هم مسیح و هم فرشتگان می‌دانند هر کس از عبادت خدا رویگردان شود و تکبر ورزد، خداوند آنان را یکسره به سوی خود محشور می‌کند.'),
(666, 4, 173, 'اما کسانی که ایمان بیاورند و کارهای شایسته کنند، خداوند پاداششان را به طور کامل به آنان می‌دهد و از فضل خود بر پاداش آنان می‌افزاید، و اما کسانی که از پرستش خدا خودداری کنند و تکبر ورزند آنان را به عذابی دردناک عذاب می‌کند و آنها برای خود کارساز و یاوری جز خدا نخواهند یافت.'),
(667, 4, 174, 'ای مردم، برای شما از جانب پروردگارتان حجتی روشن آمده است، و قرآن را که نوری آشکار است به سوی شما فروفرستادیم.'),
(668, 4, 175, 'اما کسانی که به خدا ایمان بیاورند و با پیروی از پیامبر و فراگیری قرآن و عمل به احکام آن به او تمسک جویند به زودی آنان را در رحمت و فضلی از جانب خویش درمی آورَد و به سوی خود که راهی راست است هدایت می‌کند.'),
(669, 4, 176, 'از تو درباره ارث کلاله (کسی که پدر یا مادر یا فرزند متوفی نیست) فتوا می‌خواهند. بگو: خداست که درباره آن به شما فتوا می‌دهد. اگر مردی بمیرد و هیچ فرزندی نداشته باشد (و دارای پدر و مادر نیز نباشد) و خواهری داشته باشد، نیمی از میراث برادر از آنِ اوست. و برادر تمام آنچه را که خواهرش بر جای نهاده است به ارث می‌برد به شرط آن که خواهر، فرزند (و نیز پدر و مادر) نداشته باشد. و اگر خواهران متوفی دو تن باشند، دو سوم میراث برادر یا خواهر از آنِ ایشان خواهد بود. و اگر چند برادر و خواهر وارث خواهر یا برادر خود باشند، تمام ترکه را به ارث می‌برند و سهم هر مردی برابر سهم دو زن خواهد بود. خداوند احکام خود را برای شما بیان می‌کند تا گمراه نشوید، و خدا به هر چیزی داناست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'ای کسانی که ایمان آورده‌اید، به تمام پیمان‌ها و قراردادهای خود وفا کنید. چهارپایان دامی (شتر، گاو، گوسفند و بز) ـ جز آنچه بر شما خوانده می‌شود ـ برای شما حلال گردیده است. این در حالی است که شکار را آن گاه که مُحرمید حلال نمی‌شمارید. بی تردید، خداوند هر حکمی بخواهد مقرر می‌دارد.'),
(671, 5, 2, 'ای کسانی که ایمان آورده‌اید، حرمت شعایر الهی و ماه حرام و قربانی حج و قلاده‌هایی که بر گردن قربانی‌های حج می‌آویزند و نیز حرمت راهیان بیت اللّه الحرام را که فضل و خشنودی پروردگارشان را می‌طلبند نشکنید، و هنگامی که از احرام بیرون آمدید می‌توانید شکار کنید، و دشمنی گروهی از مردم که شما را از ورود به مسجدالحرام بازداشتند وادارتان نکند که بر آنان تعدی کنید. شما مؤمنان یکدیگر را در نیکوکاری و تقواپیشگی یاری دهید و در گناه و تجاوز به یکدیگر کمک نکنید، و از خدا پروا کنید که خداوند سخت کیفر است.'),
(672, 5, 3, 'بر شما حرام شده است مردار و خون و گوشت خوک و حیوانی که هنگام ذبح نام غیر خدا بر آن برده شده و حیوانی که خفه شده یا با زدن مرده یا از بلندی پرتاب شده و جان داده یا به ضرب شاخ حیوان دیگر کشته شده و حیوانی که درندگان آن را خورده‌اند ـ مگر آنچه را که زنده بیابید و خود سر ببرید ـ و حیوانی که بر آستان بت‌ها سر بریده‌اند و این که گوشت دام را با تیرهای قمار سهم بندی کنید. ای مؤمنان، اینها نافرمانی خداست. امروز کسانی که کافر شده‌اند از ضربه زدن به دین شما ناامید گشته‌اند. پس از آنان نترسید و از من بترسید. امروز دینتان را برای شما کامل ساختم و نعمتم را بر شما تمام کردم و اسلام را آیین شما پسندیدم. پس هر کس هنگام قحطی و گرسنگی به خوردن گوشت حرام ناچار گردد، در حالی که به گناه تمایل نداشته باشد و از حد ضرورت نگذرد، بر او گناهی نیست که از آن بخورد، زیرا خداوند حرمت را در حال ضرورت برداشته است، چرا که خدا آمرزنده و مهربان است.'),
(673, 5, 4, 'از تو می‌پرسند: چه چیزهایی برای آنان حلال است؟ بگو: همه چیزهای پاکیزه برای شما حلال است، و نیز شکار درندگانی که به آنها آموزش داده‌اید، در حالی که آنها را به سوی شکار روانه می‌کنید و به آنها از آنچه خدا به شما آموخته است یاد می‌دهید. پس، از شکاری که برای شما نگاه داشته‌اند بخورید و نام خدا را بر شکار ببرید تا بر شما حلال گردد و در شکار حیوانات از خدا پروا کنید (در کشتن آنها زیاده روی نکنید و به خاطر سرگرمی و تفریح آنها را نکشید) که خداوند به سرعت حسابرسی می‌کند و سزای ستمگری را تا قیامت به تأخیر نمی‌افکند.'),
(674, 5, 5, 'امروز چیزهای پاکیزه برای شما حلال است، و طعام کسانی که به آنان کتاب آسمانی داده شده نیز بر شما حلال است و طعام شما هم برای آنان حلال است. و زنان پاکدامن مؤمن و زنان پاکدامن از کسانی که پیش از شما کتاب آسمانی به آنان داده شده است نیز بر شما حلالند، به شرط این که مهرشان را به آنان بپردازید و پاکدامن باشید نه آلوده به کار زشت و نه این که از آنها به طور پنهانی دوستانی برای خود برگزینید. و هر کس ایمان داشته باشد ولی به آنچه ایمانش او را بدان فرا می‌خواند کفر ورزد، عملش تباه می‌گردد و در آخرت از زیانکاران خواهد بود.'),
(675, 5, 6, 'ای کسانی که ایمان آورده‌اید، چون خواستید به نماز ایستید، صورت و دست هایتان تا آرنج‌ها را بشویید و بخشی از سر و نیز پاهای خود تا دو برآمدگی آنها را مسح کنید. این در صورتی است که جنب نباشید، امّا اگر جنب بودید باید برای نماز غسل کنید. و اگر بیمار یا در سفر بودید، یا یکی از شما از قضای حاجت آمده یا با زنان همبستر شده‌اید و آبی نیافتید که با آن وضو بگیرید یا غسل کنید، آهنگ زمینی کنید که از طبیعت اصلی اش خارج نشده است، پس بخشی از صورت و دست‌های خود را با آن مسح نمایید. خدا نمی‌خواهد با مقرر کردن وضو و غسل یا تیمم شما را در تنگنا قرار دهد، بلکه می‌خواهد شما را پاک گرداند و با آیینی که فروفرستاده است نعمتش را بر شما تمام گرداند، باشد که سپاسگزاری کنید.'),
(676, 5, 7, 'و نعمت خدا را که بر شما ارزانی داشته و نیز پیمانش که شما را بدان متعهد ساخته آن گاه که گفتید: شنیدیم و اطاعت کردیم، به یاد داشته باشید و از خدا پروا کنید که خدا به راز سینه‌ها داناست.'),
(677, 5, 8, 'ای کسانی که ایمان آورده‌اید، برای خدا به جدّ برپا باشید و به عدل و داد گواهی دهید، و مبادا دشمنی شما با قومی وادارتان کند که به عدالت رفتار نکنید، به عدالت رفتار کنید که آن به تقوا نزدیک‌تر است، و از خدا پروا بدارید که خدا به آنچه می‌کنید آگاه است.'),
(678, 5, 9, 'خدا به کسانی که ایمان آورده و کارهای شایسته کرده‌اند وعده داده است، برای آنان آمرزش و پاداشی بزرگ خواهد بود.'),
(679, 5, 10, 'و کسانی که کفر ورزیده و آیات ما را دروغ شمرده‌اند همدم دوزخ خواهند بود.'),
(680, 5, 11, 'ای کسانی که ایمان آورده‌اید، نعمت خدا را بر خود، به یاد آورید آن گاه که گروهی آهنگ آن کردند که به سوی شما دست دراز کنند و شما و آیینتان را نابود سازند، پس خداوند دستشان را از شما بازداشت. و از خدا پروا کنید و پیمان‌های او را نشکنید، و مؤمنان باید در امر دین و دنیای خود تنها بر خدا توکل کنند (امور خود را در تکوین و تشریع به او وانهند).'),
(681, 5, 12, 'و از سرگذشت بنی اسرائیل عبرت گیرید، زیرا خداوند از آنان پیمان گرفت که بر آیین الهی پایدار باشند، ما از میان آنان دوازده مهتر را که امر دین و دنیای ایشان را تدبیر کنند برانگیختیم، و خدا گفت: من با شمایم، اگر نماز برپا دارید و زکات بدهید و به پیامبران من ایمان بیاورید و آنان را بزرگ دارید و یاری کنید و به خدا وامی نیکو دهید (در راه خدا انفاق کنید) قطعاً گناهانتان را از شما می‌زدایم و شما را به بوستان‌هایی با درختانی انبوه که از زیر آنها جوی‌ها روان است درمی آورم. پس هر کس از شما بعد از این کافر شود، به یقین راه راست را گم کرده است.'),
(682, 5, 13, 'پس به سبب این که پیمانشان را شکستند لعنتشان کردیم و دل هایشان را سخت و حق ناپذیر گردانیدیم، تا آن جا که سخنان خدا را با تفسیرهای نادرست و دگرگون ساختن آنها تحریف کردند و بخشی از باورهای دینی را که بدان یادآوری شده بودند به فراموشی سپردند، و تو ای پیامبر همواره بر گروهی خیانتکار از آنان آگاه می‌شوی، مگر اندکی از آنان که اهل خیانت نیستند. پس از آنان درگذر و چشم پوشی کن که خداوند نیکوکاران را دوست می‌دارد.'),
(683, 5, 14, 'و از کسانی که گفتند: ما مسیحی هستیم. پیمان گرفتیم که به پیامبران ایمان بیاورند و آنان را یاری کنند و احکام و معارف کتاب آسمانی را پاس دارند، ولی بخشی از آنچه را که به آنان یادآوری کرده بودند به فراموشی سپردند، ازاین رو تا روز رستاخیز عداوت و دشمنی با یکدیگر را ملازم آنان ساختیم، و به زودی خدا آنان را به حقیقت آنچه می‌کردند آگاه خواهد ساخت.'),
(684, 5, 15, 'ای اهل کتاب، فرستاده ما به سوی شما آمده است. او بسیاری از حقایق کتاب آسمانی را که شما پنهان می‌کردید برایتان بیان می‌کند و از آشکار ساختن بسیاری از آنها صرف نظر می‌کند. قطعاً از جانب خدا نوری (پیامبر) و کتابی (قرآن) روشن و روشنگر به سوی شما آمده است.'),
(685, 5, 16, 'خداوند به وسیله آن، کسانی را که خشنودی او را دنبال می‌کنند به راه‌های سلامت که تأمین کننده نیکبختی آنهاست رهنمون می‌شود، و آنان را در پرتو علم خود از تاریکی‌ها بیرون می‌آورد و به سوی روشنایی می‌برد و به راهی راست هدایتشان می‌کند.'),
(686, 5, 17, 'قطعاً کسانی که گفتند: خداوند همان مسیح پسر مریم است، کافر شدند. به آنان بگو: اگر خدا بخواهد مسیح پسر مریم و مادرش و همه کسانی را که در روی زمینند یکسره نابود کند، چه کسی در برابر خدا، مالک و صاحب اختیار چیزی است که بتواند با آن بر اراده خدا حاکم شود؟ و حال آن که فرمانروایی آسمان‌ها و زمین و آنچه میان آن دو است از آنِ خداست، چرا که آنچه بخواهد می‌آفریند، و خدا بر هر کاری تواناست.'),
(687, 5, 18, 'و یهودیان و مسیحیان گفتند: ما پسران خدا و دوستان اوییم. بگو: پس چرا شما را به سبب گناهانتان عذاب می‌کند؟ شما نزد خدا امتیازی ندارید بلکه مانند دیگران در زمره کسانی هستید که او آفریده است. هر که را بخواهد می‌آمرزد و هر که را بخواهد عذاب می‌کند، و هیچ چیز و هیچ کس نمی‌تواند مانع او باشد، زیرا فرمانروایی آسمان‌ها و زمین و آنچه میان آن دو است از آنِ خداست و بازگشت همگان فقط به سوی اوست.'),
(688, 5, 19, 'ای اهل کتاب، فرستاده ما محمد پس از مدتی که پیامبری مبعوث نشده بود، به سوی شما آمد. او آنچه را که در دین خود بدان نیازمندید برای شما بیان می‌کند تا اگر گمراه گشتید نگویید: مژده رسان و هشداردهنده‌ای برای ما نیامد اینک پیامبری مژده رسان و هشداردهنده به سویتان آمده است، و خدا بر هر کاری تواناست.'),
(689, 5, 20, 'و یاد کن هنگامی را که موسی به قوم خود گفت: ای قوم من، نعمت‌هایی را که خدا به شما داده است به یاد آورید، آن گاه که در میان شما پیامبرانی قرار داد و شما را از تحت سلطه دیگران بیرون آورد و به شما استقلال بخشید و چیزهایی (نعمت‌ها و معجزاتی) به شما داد که به هیچ یک از جهانیان نداده بود.'),
(690, 5, 21, 'ای قوم من، به این سرزمین مقدس که خداوند سکونت در آن را برای شما مقرر کرده است درآیید و در پیکار با دشمنان به عقب بازنگردید و نگریزید که زیانکار باز خواهید گشت.'),
(691, 5, 22, 'گفتند: ای موسی، در آن جا مردمی زورمند و سلطه گر وجود دارند، و ما هرگز وارد آن جا نمی‌شویم تا آنها از آن جا خارج شوند، اگر آنها خارج شدند ما وارد خواهیم شد.'),
(692, 5, 23, 'دو مرد از کسانی که از مخالفت با خدا و پیامبرش بیمناک بودند و خدا نعمت ولایت خود را به آن دو ارزانی داشته بود به قوم موسی گفتند: از آن دروازه بر آنان درآیید که اگر از آن جا وارد شوید قطعاً پیروز خواهید شد، و اگر ایمان دارید فقط بر خدا توکل کنید.'),
(693, 5, 24, 'گفتند: ای موسی، ما تا آنان در آن سرزمین اند هرگز وارد آن جا نمی‌شویم، پس تو و پروردگارت بروید و با آنان بجنگید که ما همین جا نشسته‌ایم.'),
(694, 5, 25, 'موسی گفت: پروردگارا، من برای انجام دادن فرمان تو، فقط اختیار خودم را دارم و برادرم هارون نیز تنها اختیار خودش را دارد، پس میان ما و این مردم تمردپیشه داوری کن و ما را در انجام فرمان خود یاری رسان.'),
(695, 5, 26, 'خدا فرمود: اینک چنین مقرر کردیم که بنی اسرائیل مدت چهل سال از ورود به آن سرزمین محروم باشند. آنان در طول این مدت بر روی زمین سرگردان خواهند بود، نه چون شهرنشینان در شهری استقرار می‌یابند و نه چون صحرانشینان در بیابان زندگی می‌گذرانند. این کیفر آنان است، پس بر این تمردپیشگان از این که کیفر الهی را می‌چشند اندوه مخور.'),
(696, 5, 27, 'ای پیامبر، برای این یهودیان و مسیحیانی که بر تو رشک می‌برند، داستان واقعی دو پسر آدم، هابیل و قابیل را نقل کن، که هر کدام چیزی را برای تقرّب به درگاه خدا تقدیم نمودند، ولی تنها از یکی از آنها پذیرفته شد و از دیگری پذیرفته نشد. قابیل که هدیه اش قبول نشد به هابیل گفت: حتماً تو را خواهم کشت. هابیل گفت: خداوند تنها از تقواپیشگان می‌پذیرد.'),
(697, 5, 28, 'اگر تو دست خود را به سوی من بگشایی تا مرا بکشی، قطعاً من دست خود را به سوی تو نمی‌گشایم تا تو را بکشم، زیرا من از خدا که پروردگار جهانیان است می‌ترسم.'),
(698, 5, 29, 'من می‌خواهم که تو گناه من و گناه خودت را بر دوش گیری و از همدمان آتش گردی، و سزای ستمگران این است.'),
(699, 5, 30, 'سرانجام نفْس قابیل در کشتن برادرش رام وی گردید پس، او را به قتل رسانید و از زیانکاران گردید.'),
(700, 5, 31, 'آن گاه خداوند زاغی را برانگیخت که زمین را برای پنهان ساختن چیزی می‌کاوید، تا به قابیل نشان دهد چگونه جسد برادرش را پنهان سازد. قابیل گفت: ای وای! آیا ناتوان بودم که مانند این زاغ باشم و جسد برادرم را پنهان کنم و مدّت‌ها سرگردان نباشم؟ پس چون ناتوانی خویش را دریافت کشتن برادر را به سود خود ندانست و از زمره پشیمانان گردید.'),
(701, 5, 32, 'بدین سبب که آدمی با انگیزه‌های واهی (چون حسد و دنیاخواهی) به قتل دیگران برانگیخته می‌شود، برای بنی اسرائیل که در معرض این خطر بودند بیان کردیم که هر کس فردی را بی آن که مرتکب قتل شده یا در زمین فسادی برانگیخته باشد، بکشد مانند آن است که همه مردم را کشته است، و هر کس فردی را زنده بدارد (از مرگ نجاتش دهد) مانند آن است که همه مردم را زنده داشته است، و به راستی پیامبران ما با دلایلی روشن به سویشان آمدند و آنان را از آدم کشی و سایر فسادها برحذر داشتند ولی پس از آن بسیاری از آنان در زمین از حدودی که تعیین شده بود تجاوز کردند.'),
(702, 5, 33, 'جز این نیست که سزای آنان که با خدا و پیامبرش می‌جنگند و در زمین به فسادگری می‌کوشند این است که کشته شوند یا به صلیب کشیده شوند و یا دست و پایشان برخلاف یکدیگر بریده شود و یا از آن سرزمین [به نقطه‌ای دیگر] تبعید گردند. این کیفر، رسوایی آنان در دنیاست و برای آنان در آخرت عذابی بزرگ خواهد بود.'),
(703, 5, 34, 'مگر کسانی که پیش از این که بر ایشان دست یابید توبه کرده باشند، که نباید بر آنان حد جاری کنید، زیرا باید بدانید که خداوند آمرزنده و مهربان است.'),
(704, 5, 35, 'ای کسانی که ایمان آورده‌اید، از خدا پروا نمایید و با اطاعت از او در پی برقراری پیوند با او باشید و در راه او جهاد کنید، باشد که نیکبخت شوید.'),
(705, 5, 36, 'زیرا کسانی که کفر ورزیده‌اند (تقوا پیشه ننمودند، به درگاه او تقرب نجستند و در راه او جهاد نکردند)، اگر همه آنچه را که در زمین است داشته باشند و مانند آن نیز با آن در اختیارشان باشد تا عذاب روز قیامت را به وسیله آن باز خرند، از آنان پذیرفته نمی‌شود و برای آنان عذابی دردناک خواهد بود.‌'),
(706, 5, 37, 'می‌خواهند از آتش دوزخ بیرون آیند، در حالی که از آن بیرون آمدنی نیستند و برای آنان عذابی پایدار است.'),
(707, 5, 38, 'امّا مرد و زن دزد را باید دستشان را ببرید که این سزایی است در برابر کاری که کرده‌اند و کیفری بازدارنده از جانب خداست، و خداوند شکست ناپذیر و حکیم است.'),
(708, 5, 39, 'پس هر که بعد از ستم خود توبه کند و شایسته شود، خداوند با رحمت خود به او باز می‌گردد و توبه اش را می‌پذیرد، چرا که خداوند آمرزنده و مهربان است.'),
(709, 5, 40, 'آیا ندانسته‌ای که پادشاهی آسمان‌ها و زمین از آن خداست، هر که را بخواهد کیفر می‌دهد و هر که را بخواهد می‌آمرزد؟ پس حق اوست که ستمگران را عذاب کند و از توبه کنندگان درگذرد، و خدا بر هر کاری تواناست،'),
(710, 5, 41, 'ای پیامبر، مبادا کسانی که در کفر شتاب میورزند تو را اندوهگین کنند، چه آن منافقانی که به زبان گفتند: ایمان آوردیم ولی دل هایشان ایمان نیاورده است، و چه آن یهودیانی که نزد تو آمده‌اند تا برایشان داوری کنی. اینان به دروغ‌های عالمان خود گوش فرا می‌سپارند با این که می‌دانند آنها دروغ می‌گویند. اینان فرمانبردار مردمی دیگرند که نزد تو نیامده‌اند. عالمانشان سخنان خدا را پس از آن که در جایگاهای خود قرار گرفته است تحریف می‌کنند و به پیروانشان که آنان را نزد تو فرستاده اند می‌گویند: اگر همین حکم (حکم تحریف شده) به شما داده شد آن را بپذیرید و اگر آن به شما داده نشد دوری کنید. خدا می‌خواهد آنان را به عذاب گرفتار سازد. و هر کس را که خداوند عذابش را اراده کند تو هرگز از سوی خدا توان آن را نداری که عذاب را از او بگردانی. اینان کسانی اند که خدا نخواسته است دل هایشان را پاک گرداند. برای آنان در این جهان خواری و در آخرت عذابی بزرگ خواهد بود.'),
(711, 5, 42, 'آری، یهودیان شنونده دروغ‌های عالمان خویشند و عالمانشان با گرفتن رشوه خورنده مال حرام. پس ای پیامبر، اگر برای داوری نزد تو آمدند میانشان داوری کن یا از آنان روی برتاب، و اگر روی برتافتی و میانشان داوری نکردی هرگز اندک زیانی به تو نمی‌رسانند، و اگر داوری کردی به عدل و داد میانشان داوری کن که خداوند دادگران را دوست می‌دارد.'),
(712, 5, 43, 'چگونه یهودیان تو را به داوری فرا می‌خوانند در حالی که تورات نزد آنهاست و حکم خدا در آن است؟ آنان پس از این که حکم الهی را در تورات می‌یابند و آن را نمی‌پسندند از آن روی برمی تابند. در حقیقت آنان به تورات و احکام آن ایمان ندارند.'),
(713, 5, 44, 'ما تورات را که در آن رهنمود و نوری است فروفرستادیم. پیامبران که تسلیم خدا بودند براساس آن برای یهودیان داوری می‌کردند، و عالمان ربّانی و دانشمندان یهود نیز به سبب این که از آنان خواسته شده بود که نگهبان این کتاب باشند بر طبق آن حکم می‌کردند و از کسانی بودند که بر آن و احکام آن گواهی می‌دادند. پس ای یهودیان، در بیان حکم خدا از مردم نترسید و از من بترسید، و آیات مرا به بهایی اندک مفروشید، که هر کس بر طبق آنچه خدا فرو فرستاده است حکم نکند آنانند کافران.'),
(714, 5, 45, 'و در تورات بر یهودیان مقرّر داشتیم که جان در برابر جان و چشم در برابر چشم و بینی در برابر بینی و گوش در برابر گوش و دندان در برابر دندان قصاص می‌شود، و جراحت‌ها نیز قصاص دارند. پس اگر کسی از حق قصاص درگذرد و آن را صدقه به شمار آورد باعث آمرزش گناهان او می‌شود، و اگر از حق خود نگذرد و خواهان قصاص باشد، حاکم باید بر طبق مقررات الهی حکم کند، و هر کس بر طبق آنچه خدا فرو فرستاده است حکم نکند آنانند ستمکاران.'),
(715, 5, 46, 'و عیسی پسر مریم را در پی راه‌هایی که پیامبران پیشین پیمودند فرستادیم، در حالی که حقانیت تورات را که پیش از او آمده بود تأیید می‌کرد، و به او انجیل را که در آن رهنمود و نوری بود عطا کردیم، و انجیل نیز حقانیت تورات را که پیش از آن آمده بود، تصدیق می‌نمود و رهنمود و پندی برای تقواپیشگان بود.'),
(716, 5, 47, 'و پیروان اِنجیل باید براساس آنچه خدا در آن فرو فرستاده است حکم کنند. و هر کس به آنچه خدا نازل کرده است حکم نکند آنانند که از راه خدا بیرون رفته‌اند.'),
(717, 5, 48, 'ای پیامبر، و ما این کتاب (قرآن) را که سراسر حق است به سوی تو فرو فرستادیم در حالی که حقانیت کتاب‌های آسمانی پیش از خود را تأیید می‌کند و بر آنها سیطره و نگهبانی دارد. پس میان همگان به آنچه خدا فرو فرستاده است حکم کن و با رویگردانی از این قرآنِ سراسر حق که به سوی تو آمده است از خواسته‌های نفسانی آنان پیروی مکن. ما برای هر امتی از شما شریعت و راهی روشن مقرّر داشتیم، و اگر خدا می‌خواست همه شما را امتی یگانه قرار می‌داد و برای شما یک شریعت مقرّر می‌کرد، اما چنین نخواست، بلکه برای هر امتی شریعتی قرار داد تا شما را در آنچه عطایتان کرده است بیازماید. پس به سوی نیکی‌ها سبقت گیرید که بازگشت همه شما به سوی خداست، آن گاه شما را به آنچه در آن اختلاف می‌کردید آگاه می‌سازد.'),
(718, 5, 49, 'و به تو فرمان دادیم که میان آنان بر طبق آنچه خدا نازل کرده است، حکم کن و از خواسته هایشان پیروی مکن و از آنان برحذر باش که مبادا تو را بلغزانند و از برخی از آنچه خدا به سوی تو فرو فرستاده است بازگردانند. پس اگر از دعوت تو روی برتافتند بدان که خدا می‌خواهد آنان را به سزای برخی از گناهانشان به گمراهی کشاند، و بی گمان بسیاری از مردم از راه خدا بیرون می‌روند.'),
(719, 5, 50, 'پس آیا حکم دوران جاهلیت را می‌جویند که احکام الهی را نمی‌پذیرند؟ برای مردمی که به آیات خدا یقین دارند حکم چه کسی از حکم خدا نیکوتر است؟'),
(720, 5, 51, 'ای کسانی که ایمان آورده‌اید، یهودیان و مسیحیان را دوستان خود مگیرید، زیرا آنان با وجود این که باهم اختلاف دارند در برابر شما با یکدیگر همدل‌اند، و هر کس از شما آنان را به دوستی گیرد، قطعاً در زمره آنان به شمار می‌آید و از ستمکاران است، و خدا مردم ستمکار را هدایت نمی‌کند.'),
(721, 5, 52, 'به همین سبب است که می‌بینی آنان که در دل هایشان بیماری شک و تردید است، برای طرح دوستی با یهودیان و مسیحیان شتابان به میان آنان می‌روند و به دروغ می‌گویند: می‌ترسیم گزندی به ما برسد و به یاری آنان نیازمند شویم. امید است خداوند برای مؤمنان پیروزی پدید آورد و یا از جانب خود امری پدیدار سازد، آن گاه بیماردلانِ سست ایمان از آنچه در دوستی با یهودیان و مسیحیان در دل هایشان نهان می‌داشتند پشیمان می‌شوند.'),
(722, 5, 53, 'و مؤمنان درباره کسانی که با یهودیان و مسیحیان طرح دوستی افکندند خواهند گفت: آیا اینان همان کسانی اند که با سخت‌ترین سوگندهایشان به خدا قسم خوردند که با شما هستند؟ کارهایشان تباه شد و زیانکار گشتند.'),
(723, 5, 54, 'ای کسانی که ایمان آورده‌اید، هر کس از شما از دین خود برگردد و به کفر گراید بداند که دین خدا بی یاور نمی‌ماند، زیرا دیری نمی‌گذرد که خداوند مردمی را می‌آورَد که آنان را دوست می‌دارد و آنان نیز او را دوست می‌دارند، در برابر مؤمنان فروتن اند و به آنان مهر میورزند ولی بر کافران سرافرازند، در راه خدا پیکار می‌کنند و در کارهایشان از سرزنش هیچ سرزنش کننده‌ای بیم ندارند. این فضل خداست که به هر کس بخواهد عطا می‌کند، و خدا بخششی گسترده و فضلی بی پایان دارد و به کسانی که درخور بخشش اند داناست.'),
(724, 5, 55, 'جز این نیست که عهده دار امور شما مؤمنان، خدا و پیامبر او و کسانی اند که ایمان آورده‌اند، همانان که نماز برپا می‌دارند و در حال رکوع زکات می‌دهند.'),
(725, 5, 56, 'و هر کس خدا و رسولش و کسانی را که ایمان آورده و از خصلت یاد شده برخوردارند، به ولایت و سرپرستی خود بپذیرد پیروز است چرا که از حزب خداست و تنها حزب خدا پیروزمندند.'),
(726, 5, 57, 'ای مؤمنان، کسانی را که دین شما را به مسخره گرفته و آن را بازیچه شمرده‌اند، همانان که پیش از شما کتاب آسمانی به آنان داده شده است دوست خود مگیرید و کافران را نیز به دوستی مگیرید، و اگر مؤمنید از خدا پروا کنید.'),
(727, 5, 58, 'و چون بانگ اذان سر دهید و مردم را بدین وسیله به نماز فراخوانید آن را به استهزا می‌گیرند و بازیچه می‌شمرند، این بدان سبب است که آنان مردمی هستند که درنمی یابند.'),
(728, 5, 59, 'بگو: ای صاحبان کتاب‌های آسمانی، آیا چیزی جز این را از ما ناخوشایند می‌شمرید که ما به خدا و کتابی که بر ما فرو فرستاده شده و کتاب‌های آسمانی دیگری که پیش‌تر نازل شده است ایمان آورده‌ایم؟ و این که بیشتر شما از راه خدا خارج شده‌اید؟'),
(729, 5, 60, 'بگو: اگر ایمان به خدا و کتاب‌های آسمانی از نظر شما بد است آیا می‌خواهید شما را به چیزی که فرجامش نزد خدا بدتر از این است آگاه کنم؟ آنان کسانی اند که خدا لعنتشان کرده و بر ایشان خشم گرفته و برخی از آنان را به بوزینه‌ها و خوک‌ها مبدل ساخته، و کسانی اند که طاغوت را پرستیده‌اند. اینانند که جایگاهی بدتر دارند و از راه راست گمراه ترند و اینان از گروه شمایند و سزاوار است که از آنان ناخشنود باشید نه از کسانی که به خدا و کتاب‌های آسمانی ایمان آورده‌اند.'),
(730, 5, 61, 'و چون نزد شما آیند گویند: ما ایمان آورده‌ایم، در حالی که با کفر بر شما وارد شده و با کفر از نزد شما بیرون رفته‌اند، و خدا به آن نیرنگی که نهان داشته‌اند داناتر است.'),
(731, 5, 62, 'و بسیاری از آنان را می‌بینی که در گناه و تجاوز و خوردن مال حرام شتاب میورزند. راستی که بد کارهایی انجام می‌دهند.'),
(732, 5, 63, 'چرا عالمان ربانی و دانشمندانشان آنان را از سخنان گناه آلود و حرامخواری شان بازنمی دارند؟ راستی که بد کاری می‌کنند.'),
(733, 5, 64, 'و یهودیان گفتند: دست خدا بسته است ـ دست‌های خودشان بسته و بی اقتدار باد، و به سزای آنچه گفتند لعنت بر آنان باد ـ بلکه هر دو دست خدا باز است و قدرتش بی کران. هرگونه بخواهد می‌بخشد. و قطعاً کتابی که از جانب پروردگارت به سوی تو فرو فرستاده شده است بر سرکشی و کفر بسیاری از آنان می‌افزاید، و ما تا روز قیامت میانشان دشمنی و نفرت افکنده‌ایم. هرگاه برای جنگ با پیامبر و مسلمانان آتشی برافروزند خدا آن را خاموش می‌کند. آنان در زمین به فسادگری می‌کوشند، و خدا فسادگران را دوست نمی‌دارد.'),
(734, 5, 65, 'و اگر صاحبان کتاب‌های آسمانی ایمان می‌آوردند و از گناهان بزرگ پرهیز می‌کردند، قطعاً گناهان کوچکشان را از آنان می‌زدودیم و آنان را به بوستان‌های پرنعمت بهشت درمی آوردیم.'),
(735, 5, 66, 'و اگر آنان تورات و انجیل و سایر کتاب‌های آسمانی و احکامی را که از جانب پروردگارشان بر آنان فروفرستاده شده است برپا می‌داشتند، قطعاً از فراز سرشان و از زیر پاهایشان (از آسمان و زمین) بهره‌های فراوان می‌بردند. البته برخی از آنان معتدل اند (تسلیم خدایند و بر دین پایدار) ولی بسیاری از آنان کارهایی که انجام می‌دهند نارواست.'),
(736, 5, 67, 'ای پیامبر، به مردم آنچه را که از جانب پروردگارت در مورد معرّفی پیشوایشان به سوی تو فرو فرستاده شده است ابلاغ کن، و اگر نکنی رسالت او را ابلاغ نکرده‌ای، و از مردم بیمی به خود راه مده، که خداوند تو را از گزند آنان مصون می‌دارد. قطعاً خدا این مردم کفرپیشه را به اهدافشان (تباه کردن دین حق) راه نمی‌نماید.'),
(737, 5, 68, 'بگو: ای اهل کتاب، شما بر جایگاهی استوار قرار ندارید تا بتوانید تورات و انجیل و سایر کتاب‌های آسمانی و احکامی را که از جانب پروردگارتان به سوی شما فرو فرستاده شده است، برپادارید. و بی تردید این قرآن که از جانب پروردگارت به سوی تو فرو فرستاده شده است بسیاری از آنان را سرکشی و کفر بسیاری می‌افزاید، پس بر این مردم کفرپیشه اندوه مخور.'),
(738, 5, 69, 'کسانی که ایمان آورده‌اند (مسلمانان) و کسانی که یهودی اند و صابئان و مسیحیان بدانند که این نام‌ها کارساز نیست. تنها آنان که به خدا و روز قیامت ایمان راستین داشته باشند و کار شایسته کنند نه ترسی ایشان را فرا می‌گیرد و نه اندوهگین می‌شوند.'),
(739, 5, 70, 'به یقین ما از بنی اسرائیل پیمان گرفتیم و پیامبرانی را به رسالت به سوی آنان فرستادیم. هرگاه پیامبرانی برای آنان دستوراتی آوردند که موافق میلشان نبود، گروهی را دروغپرداز شمردند و گروهی را کشتند و این رویه آنان است.'),
(740, 5, 71, 'و یهودیان پنداشتند که چون یهودی هستند، در برابر کارهای ناروایشان کیفری در کار نیست، از این رو کور و کر شدند و از دیدن حق و شنیدن آن ناتوان گشتند. پس از چندی خداوند به رحمت خود به سوی آنان بازگشت و پندارهای باطل را از آنان زدود، ولی باز هم کور و کر شدند البته، نه همگان بلکه بسیاری از آنان، و خدا به آنچه به جای می‌آورند بیناست.'),
(741, 5, 72, 'قطعاً کسانی که گفتند: خدا همان مسیح پسر مریم است، کافر شده‌اند. مسیح خود می‌گفت: ای بنی اسرائیل، خدا را که پروردگار من و پروردگار شماست بپرستید، زیرا حقیقت این است که هر کس به خدا شرک ورزد، به یقین خداوند بهشت را بر او حرام می‌کند و جایگاهش آتش است، و ستمکاران یاورانی ندارند که آنان را از آتش رهایی بخشند.'),
(742, 5, 73, 'خدای یگانه خدایی نیست. اگر از آنچه می‌گویند بازنایستند، به کسانی از آنان که کافر شده‌اند عذابی دردناک خواهد رسید.'),
(743, 5, 74, 'پس چرا به سوی خدا باز نمی‌گردند و از او آمرزش نمی‌خواهند و حال آن که خدا آمرزنده و مهربان است؟'),
(744, 5, 75, 'مسیح پسر مریم جز پیامبری نبود که پیش از او آن همه پیامبران درگذشتند، و مادرش تصدیق کننده همه آیات خدا بود. آنان هر دو غذا می‌خوردند، در حالی که خدا نیازمند خوراکی‌ها نیست. بنگر چگونه نشانه‌ها را برای آنان بیان می‌کنیم، سپس بنگر چگونه از اندیشه باز داشته می‌شوند و به کجا برده می‌شوند؟'),
(745, 5, 76, 'بگو: آیا به جای خدا چیزی را می‌پرستید که هیچ زیان و سودی را برای شما در اختیار ندارد و حال آن که تنها خدا شنونده درخواست‌ها و دانا به نیازهای شماست؟'),
(746, 5, 77, 'بگو: ای صاحبان کتاب‌های آسمانی، در دینتان از آنچه بایسته است فراتر نروید که آن نارواست، و از خواسته‌ها و اندیشه‌های مردمی که پیش از شما به بیراهه رفتند و گروه بسیاری را به گمراهی افکندند و خود از راه راست گمراه شدند پیروی نکنید.'),
(747, 5, 78, 'از میان بنی اسرائیل آنان که کفر ورزیدند، بر زبان داوود و عیسی بن مریم لعنت شدند. این بدان سبب بود که آنان پیامبرانشان را نافرمانی کردند و همواره از حدود خدا تجاوز می‌کردند.'),
(748, 5, 79, 'و هرگز از کارهای ناپسندی که انجام می‌دادند، باز نمی‌ایستادند. به راستی کارهایی که می‌کردند بد بود.'),
(749, 5, 80, 'بسیاری از آنان را می‌بینی که با کسانی که یکتایی خدا را انکار کرده‌اند، طرح دوستی می‌افکنند. راستی بد است آنچه نفسشان برای آنان پیش فرستاده است که خدا بر آنان خشم گرفته و در عذاب جاودانه‌اند.'),
(750, 5, 81, 'و اگر به خدا و پیامبر و آنچه به سوی او فرستاده شده است ایمان می‌آوردند، منکران توحید را به دوستی نمی‌گرفتند، ولی بسیاری از آنان از راه ایمان و اطاعت خدا بیرون رفته‌اند.'),
(751, 5, 82, 'بی گمان، سرسخت‌ترین مردم را در دشمنی با کسانی که ایمان آورده‌اند یهودیان و مشرکان خواهی یافت، و نزدیک‌ترین مردم را در دوستی با آنان که ایمان آورده‌اند کسانی خواهی یافت که گفتند: ما مسیحی هستیم. این از آن روست که برخی از آنان کشیشان و راهبانند، و بدان سبب است که آنان در پذیرش حق تکبر نمیورزند.'),
(752, 5, 83, 'و چون آیات این قرآن را که بر پیامبر نازل شده است می‌شنوند، به دلیل این که حق را شناخته‌اند، چشمانشان را می‌بینی که اشکبار است و می‌گویند: پروردگارا، ایمان آوردیم، پس ما را با سایر مؤمنان در زمره کسانی ثبت کن که بر حقانیت رسالت پیامبر گواهی داده‌اند.'),
(753, 5, 84, 'و می‌گویند: ما را چه سودی است که به خدا و این قرآن که سراسر حق است و به سوی ما آمده است ایمان نیاوریم و امیدواریم که پروردگارمان ما را با مردم شایسته همراه سازد و در میان آنان درآورد؟'),
(754, 5, 85, 'پس خداوند به پاس آنچه گفتند بوستان‌هایی آکنده از درخت که در زیر آنها جوی‌ها روان است و در آن جاودانه‌اند، به آنان پاداش داد، و این است پاداش نیکوکاران.'),
(755, 5, 86, 'و کسانی که به پیامبر و آنچه بر او نازل شده است کافر شده و آیات ما را دروغ انگاشته‌اند آنان همدم دوزخند.'),
(756, 5, 87, 'ای کسانی که ایمان آورده‌اید، چیزهای پاکیزه را که خدا آنها را برای شما حلال کرده است بر خود و دیگران حرام مکنید و بدین وسیله از حدود الهی تجاوز نکنید که خداوند تجاوزگران را دوست نمی‌دارد.'),
(757, 5, 88, 'و از آنچه خدا روزی شما کرده است که همه حلال و پاکیزه‌اند. بخورید و از خدا که به او ایمان دارید پروا کنید و حلال او را حرام مکنید.'),
(758, 5, 89, 'خداوند شما را به سبب سوگندهای بیهوده تان که بدون قصد بر زبان می‌آورید بازخواست نمی‌کند و کفّاره‌ای برای آنها مقرّر نمی‌دارد، ولی بر سوگندهایی که آنها را با اراده خود استوار می‌کنید، اگر آنها را بشکنید بازخواستتان می‌کند و برای آنها کفّاره مقرّر می‌دارد. پس کفّاره شکستن سوگند، غذا دادن به ده درمانده است از غذاهای متوسطی که به خانواده خود می‌دهید یا پوشاک ده مسکین یا آزاد کردن یک برده. پس هر کس هیچ یک از اینها را نیابد باید سه روز روزه بگیرد. این کفّاره سوگندهای شماست آن گاه که سوگند یاد کنید و آن را بشکنید، و باید سوگندهای خود را حفظ کنید. این گونه خداوند احکام خود را برای شما بیان می‌کند، باشد که با فراگرفتن آنها و عمل بدانها سپاسگزاری کنید.'),
(759, 5, 90, 'ای کسانی که ایمان آورده‌اید، جز این نیست که شراب و قمار و سنگ‌های نصب شده برای قربانی‌ها که بدانها تبرک می‌جویید، و تیرهای قمار که گوشت دام‌ها را با آنها تقسیم می‌کنند، همه پلیدی‌هایی هستند که گرایش به آنها برخاسته از وسوسه‌های شیطان است، پس از آنها بپرهیزید، باشد که نیکبخت شوید.'),
(760, 5, 91, 'شیطان چیزی جز این نمی‌خواهد که به وسیله شراب و قمار میان شما دشمنی و نفرت بیفکند و شما را از یاد خدا و از نماز بازدارد. اینک آیا از این کارها بازمی ایستید؟'),
(761, 5, 92, 'و خدا را اطاعت کنید و از پیامبر فرمان برید و از پلیدی‌ها برحذر باشید. پس اگر روی برتافتید بدانید که برعهده فرستاده ما جز ابلاغ آشکار پیام تکلیفی نیست.'),
(762, 5, 93, 'بر کسانی که ایمان آورده و کارهای شایسته کرده‌اند، در مورد حرام‌های یاد شده ـ چنانچه پیش از آگاهی به حکمشان آنها را خورده‌اند ـ گناهی نیست، بدین شرط که تقوا پیشه کرده و به خدا و رسولش ایمانی راستین داشته و کارهای شایسته انجام داده باشند، سپس به مرحله‌ای دیگر از تقوا رسیده و به تمام احکام الهی ایمان آورده باشند و باز هم تقوا پیشه بوده و اعمالشان را نیکو انجام داده باشند، که خداوند کسانی را که اعمالشان را به صورتی نیکو انجام می‌دهند دوست می‌دارد.'),
(763, 5, 94, 'ای کسانی که ایمان آورده‌اید، خداوند شما را به برخی شکارها که دست‌ها و نیزه هایتان به آن می‌رسد می‌آزماید، تا معلوم دارد چه کسی از او به سبب کیفرش که از دیده‌ها نهان است می‌ترسد. پس هر کس بعد از پیش آمدن این آزمون تجاوز کند (به شکار بپردازد) عذابی دردناک خواهد داشت.'),
(764, 5, 95, 'ای کسانی که ایمان آورده‌اید، شکار را در حالی که مُحرمید مکشید، و هر کس از شما از روی عمد آن را بکشد جزایی برعهده اوست. آن جزا که از میان دام‌ها (شتر، گاو و گوسفند) است باید با شکاری که کشته است همانند باشد، و به همانندیِ این دو با یکدیگر، دو مرد عادل از خودتان حکم می‌کنند، و آن دام قربانیی است که باید به کعبه رسد، یا باید کفّاره‌ای بدهد که آن خوراکِ درماندگان است یا معادل آن روزه گرفتن است، تا سزای کارش را بچشد. خدا از آنچه پیش از بیان این حکم روی داده درگذشته و کفّاره‌ای برای آن مقرّر نکرده است. و هر کس از این پس شکاری را بکشد و این کار را تکرار کند خدا از او انتقام می‌گیرد، که خدا مقتدری شکست ناپذیر و صاحب انتقام است.'),
(765, 5, 96, 'شکار حیوانات دریا و خوراکی‌های آن برای شما حلال شده است، در حالی که آن برای شما مُحرمان و دیگر کاروانیان بهره‌ای است، و شکار حیوانات صحرایی بر شما مُحرمان تا هنگامی که در احرام هستید حرام گردیده است، و از خدایی که به سوی او محشور می‌شوید پروا کنید.'),
(766, 5, 97, 'خداوند، کعبه آن خانه درخورِ احترام و نیز ماهِ حرام و قربانی حج و قلاده‌های قربانی‌های حج را مایه برپایی مردم در امور دنیا و آخرتشان قرار داده است. این حقیقت را بیان کردیم تا بدانید که خدا آنچه را در آسمان‌ها و آنچه را در زمین است می‌داند و این که خدا به هر چیزی آگاه است.'),
(767, 5, 98, 'بدانید که خداوند گناه پیشگان را به کیفری سخت می‌رساند و فرمانبرداران را آمرزنده و مهربان است.'),
(768, 5, 99, 'برعهده پیامبر چیزی جز رساندن پیام‌های الهی نیست، و خدا است که آنچه را آشکار می‌کنید و آنچه را نهان می‌دارید می‌داند.'),
(769, 5, 100, 'بگو: پلید و پاک برابر نیستند، هر چند فراوانیِ پلیدها تو را مجذوب خود سازد. پس ای خردمندان، از خدا پروا کنید و بر آیین او که برخاسته از پاکی هاست پایدار باشید، باشد که نیکبخت گردید.'),
(770, 5, 101, 'ای کسانی که ایمان آورده‌اید، از چیزهایی که اگر برای شما آشکار شود بر تکلیف شما می‌افزاید و در نتیجه شما را اندوهگین می‌کند سؤال مکنید. بدانید که ما آنها را در غیر هنگام نزول قرآن بیان نمی‌کنیم، ولی اگر هنگامی که قرآن نازل می‌شود از آنها سؤال کنید برای شما آشکار می‌گردد. خداوند از آنچه نگفته است درگذشته و شما را به آن تکلیف نکرده است، و خدا درگذرنده و بردبار است. پس با پرسش آنها را مجویید.'),
(771, 5, 102, 'به یقین، گروهی که پیش از شما بودند در مورد آنها سؤال کردند ولی بعداً به آنها کافر شدند.'),
(772, 5, 103, 'خداوند درباره بحیره، سائبه، وصیله و حام هیچ حُکمی مقرر نکرده است، ولی کسانی که کافر شده‌اند بر خدا دروغ می‌بندند و چیزهایی را که خود ساخته‌اند حکم خدا معرّفی می‌کنند و بیشترشان درک نمی‌کنند که بر خدا دروغ بافته‌اند.'),
(773, 5, 104, 'و چون به آنان گفته شود: به سوی آنچه خدا فرو فرستاده و به سوی پیامبر بیایید، می‌گویند: آنچه پدرانمان را بر آن یافته‌ایم ما را بس است. آیا در هر حال از راه و رسم پدرانشان پیروی می‌کنند، هرچند پدرانشان خود چیزی نمی‌دانستند و از سوی کسی نیز هدایت نشده بودند؟'),
(774, 5, 105, 'ای کسانی که ایمان آورده‌اید، مراقب خویشتن باشید، مبادا پس از آن که راه یافته‌اید کسانی که گمراه شده‌اند به شما زیانی برسانند و گمراهتان کنند. بازگشت شما و آنان همگی به سوی خداست، آن گاه شما و آنان را از حقیقت فرجام آنچه می‌کردید آگاه خواهد ساخت.'),
(775, 5, 106, 'ای کسانی که ایمان آورده‌اید، چون نشانه‌های مرگ یکی از شما فرا رسید، هنگام وصیت باید دو تن از مردان عادل همکیشتان میان شما گواه باشند، و اگر در سفر بودید و مصیبت مرگ به سراغتان آمد و دو شاهد مسلمان نیافتید باید دو تن از غیر همکیشانتان وصیت شما را گواه باشند. پس اگر در صحّت آنچه وصی اظهار می‌دارد تردید کردید، گواهان را پس از نماز [تا برپایی محکمه] نگاه دارید که به خدا سوگند یاد کنند که ما گواهی خود را به هیچ بهایی نمی‌فروشیم (آنچه را شاهد بوده‌ایم به سود کسی تغییر نمی‌دهیم) هر چند خویشاوند باشد، و شهادتی را که خدا برعهده ما نهاده است کتمان نمی‌کنیم، زیرا در آن صورت از گناهکاران خواهیم بود.'),
(776, 5, 107, 'پس اگر فاش شد که گواهان با گواهی دروغ خود مرتکب گناه شده‌اند، دو تن از کسانی که آن دو گواه اصلی بر آنان جفا کردند، به جای آن دو قرار گیرند و به خدا سوگند یاد کنند که گواهی ما از گواهی آن دو درست‌تر است و ما با گواهی خود بر ضد آنان، در حقشان تعدی نکرده‌ایم، چرا که در آن صورت از ستمکاران خواهیم بود.'),
(777, 5, 108, 'این بهترین روش است که شاهدان شهادت را آن گونه که بایسته است ادا کنند یا بترسند از این که پس از سوگندهایشان که بی اعتبار تلقی شده است سوگندها به دیگران واگذار شود. و از خدا پروا کنید و گوش فرادهید. و خدا مردم فسق پیشه را هدایت نمی‌کند.'),
(778, 5, 109, 'روزی که خداوند همه پیامبران را گرد می‌آورد و به آنان می‌گوید: از سوی مردمی که به سویشان فرستاده شدید چه پاسخی به شما داده شد؟ می‌گویند: ما چنان که باید آگاهی نداریم، تو هستی که همه نهان‌ها را می‌دانی.'),
(779, 5, 110, 'زمانی [را یاد کن] که خدا به عیسی، پسر مریم گفت: نعمت مرا که بر تو و مادرت ارزانی داشتم به یاد آور، آن گاه که تو را به وسیله (روح القدس) تأیید کردم، که با مردم در حالی که نوزادی در گهواره بودی و هنگامی که میانسال شدی سخن می‌گفتی، و نعمت مرا به یاد آور، آن گاه که به تو کتاب آسمانی و حکمت و تورات و انجیل آموختم، و آن گاه که به اذن من از گِل چیزی همانند شکل پرنده می‌ساختی و در آن می‌دمیدی و آن به اذن من پرنده ای می‌شد، و کور مادرزاد و کسی را که دچار پیسی بود به اذن من بهبودی می‌بخشیدی، و آن گاه که مردگان را به اذن من از گور زنده بیرون می‌آوردی، و آن گاه که شرِّ بنی اسرائیل را از تو بازگرداندم، هنگامی که آن معجزات را برایشان آوردی، پس کسانی از آنان که کفر ورزیدند گفتند: این جز جادویی آشکار نیست.'),
(780, 5, 111, 'و یاد کن نعمت مرا آن گاه که به حواریون وحی کردم که به من و پیامبرم ایمان بیاورید. گفتند: ایمان آوردیم، و گواه باش که ما تسلیم توییم.'),
(781, 5, 112, 'یاد کن زمانی را که حواریون گفتند: ای عیسی پسر مریم، آیا پروردگار تو می‌تواند برای ما از آسمان مائده‌ای فروفرستد؟ گفت: اگر ایمان دارید از خدا پروا کنید و پس از این همه نشانه‌ها که بر رسالت من است نشانه دیگر مخواهید.'),
(782, 5, 113, 'گفتند: می‌خواهیم از آن بخوریم و دل هایمان نیز بدان آرامش یابد، و بدانیم که در رسالت خود به ما راست گفته‌ای و می‌خواهیم از زمره گواهان آن مائده آسمانی باشیم.'),
(783, 5, 114, 'عیسی پسر مریم گفت: خداوندا، پروردگارا، از آسمان مائده‌ای بر ما فروفرست تا برای ما عیدی باشد هم برای نخستین گروه امّت ما و هم برای آخرین گروه امّتمان و نیز نشانه‌ای از جانب تو باشد، و بدین وسیله به ما روزی ده که تو بهترین روزی دهندگانی.'),
(784, 5, 115, 'خدا گفت: من آن را بر شما فرو خواهم فرستاد، اما هر کس از شما پس از آن کفر ورزد، او را به گونه‌ای از عذاب کیفر می‌کنم که هیچ یک از جهانیان را آن گونه کیفر نکرده‌ام.'),
(785, 5, 116, 'و یاد کن هنگامی را که خدا به عیسی می‌گوید: ای عیسی پسر مریم، آیا تو به مردم گفتی که غیر از خدا من و مادرم را معبود خویش گیرید؟ عیسی می‌گوید: تو پیراسته‌ای از این که شریکی داشته باشی! مرا نرسد که چیزی بگویم که سزاوار من نیست. اگر آن را گفته بودم قطعاً تو آن را می‌دانستی. تو آنچه را که در ذات من است می‌دانی و من آنچه را که در ذات توست نمی‌دانم، چرا که فقط تو به همه نهان‌ها دانایی.'),
(786, 5, 117, 'به آنان چیزی جز آنچه مرا بدان فرمان دادی نگفتم، به آنان گفتم: خدا را که پروردگار من و پروردگار شماست بپرستید، و تا هنگامی که در میانشان بودم بر اعمالشان گواه بودم، و چون مرا برگرفتی، تو خود بر کارهایشان نگهبان بودی، و تو بر هر چیز گواهی.'),
(787, 5, 118, 'اگر عذابشان کنی آنان بندگان تواَند و اختیار کارشان با توست، و اگر آنان را بیامرزی کسی حق ندارد بر تو ایراد گیرد، زیرا شکست در تو راه ندارد و کارهای تو همه از روی حکمت است.'),
(788, 5, 119, 'آن روز خدا به عیسی می‌گوید: تو از راستگویانی و امروز روزی است که راستگویان را راستی شان سود می‌دهد، برای آنان بوستان‌هایی است پر درخت که از زیر آنها جوی‌ها روان است و برای همیشه در آن ماندگارند. خدا از آنان خشنود است و آنان نیز از خدا خشنودند. این است سعادت بزرگ.'),
(789, 5, 120, 'فرمانروایی آسمان‌ها و زمین و آنچه در آنهاست از آنِ خداست، و او بر هر کاری تواناست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'ستایش از آنِ خداست که آسمان‌ها و زمین را آفریده و تاریکی‌ها و نور را پدید آورده است، با این حال کسانی که کافر شدند غیر او را با پروردگارشان برابر می‌کنند.'),
(791, 6, 2, 'اوست آن که شما آدمیان را از گِلی آفرید، آن گاه برای شما سرآمدی مقرر داشت، و سرآمدی که تعیین شده و دگرگونی ندارد نزد اوست، با این حال شما در یکتایی او در اداره امور جهان تردید می‌کنید.'),
(792, 6, 3, 'و اوست خدا، هم در آسمان‌ها و هم در زمین، کارهای نهان و آشکار شما را می‌داند، و نیز حالات نفسانی شما را که بر اثر اعمال نیک و بد فراهم می‌آورید می‌داند.'),
(793, 6, 4, 'و هیچ نشانه‌ای از نشانه‌های پروردگارشان که آشکارکننده حق است برایشان نیامد مگر این که از آن روی برتافتند.'),
(794, 6, 5, 'زیرا آنان حق را آن گاه که برایشان آمد دروغ انگاشتند، پس به زودی اخبار و نتایج آنچه به مسخره اش می‌گرفتند به آنان خواهد رسید.'),
(795, 6, 6, 'آیا ندیده‌اند که چه بسیار مردمی را که پیش از آنان بودند هلاک کردیم؟ مردمی که به آنان در زمین توانایی‌هایی داده بودیم که به شما آن توانایی‌ها را نداده‌ایم، و ابرهای پرباران به سویشان گسیل کردیم تا باران‌های سودمند بر آنان فروریزند، و نهرهای بسیاری را پدید آوردیم که از زیر خانه‌ها و قصرهایشان روان بود، سپس آنان را به سبب گناهانشان نابود کردیم و پس از آنان مردمی دیگر پدید آوردیم.'),
(796, 6, 7, 'و اگر کتابی نوشته در کاغذ بر تو نازل می‌کردیم و آنها آن را با دست‌های خود لمس می‌کردند، باز هم کسانی که کافر شده اند می‌گفتند: این جز سحری آشکار نیست.'),
(797, 6, 8, 'و گفتند: چرا فرشته‌ای بر او نازل نمی‌شود تا نشانه‌ای بر راستی و درستی رسالت او باشد؟ ولی اگر فرشته‌ای هم نازل می‌کردیم باز آنان ایمان نمی‌آوردند و در نتیجه کار به پایان می‌رسید و هلاک می‌شدند و هیچ مهلتی نمی‌یافتند.'),
(798, 6, 9, 'و اگر ما پیامبری را که باید به سویشان بیاید فرشته‌ای قرار می‌دادیم، باز هم او را به صورت مردی درمی آوردیم، و بر آنان حق را که اینک به بهانه‌ها و شبهاتی واهی در مورد رسالت بشر، بر خود و دیگران پوشیده می‌دارند پوشیده می‌داشتیم.'),
(799, 6, 10, 'و ای پیامبر، به راستی رسولانی که پیش از تو بودند به سبب هشدارهایشان مورد استهزا قرار گرفتند، پس بر کسانی که پیامبران را مسخره می‌کردند، همان عذابی که آن را به استهزا می‌گرفتند فرود آمد و دامنگیرشان شد.'),
(800, 6, 11, 'بگو: در زمین سیر کنید، آن گاه بنگرید فرجام کسانی که پیامبران و اخطارهای آنان را دروغ انگاشتند چگونه بوده است.'),
(801, 6, 12, 'به آنان که معاد را باور ندارند بگو: آنچه در آسمان‌ها و زمین است از آنِ کیست؟ بگو: از آنِ خداست، همو که مهربانی بر بندگان را بر خود واجب کرده است، پس قطعاً شما مردم را در روز قیامت که هیچ تردیدی در آن نیست گرد می‌آورد. کسانی که بر اثر عنادشان با حق خود را باخته و زیانکار ساخته‌اند ایمان نمی‌آورند.'),
(802, 6, 13, 'و آنچه در شب و روز قرار دارد از آنِ اوست، و اوست که شنونده سخنان و دانا به اعمال نیک و بد بندگان است.'),
(803, 6, 14, 'بگو: آیا غیر خدا را ولیّ نعمت خویش بگیرم؟ خدایی که پدید آورنده آسمان‌ها و زمین است، و اوست که به بندگانش خوراک می‌دهد و نیازهایشان را تأمین می‌کند و به او طعام داده نمی‌شود چون از همه چیز بی نیاز است. بگو: من فرمان یافته‌ام که نخستین کسی باشم که در برابر او تسلیم است (بیش از دیگران تسلیم او باشم)، و به من فرمان داده شده است که از مشرکان مباش.'),
(804, 6, 15, 'بگو: من اگر پروردگارم را نافرمانی کنم، از عذاب روز قیامت که روزی بزرگ است می‌ترسم.'),
(805, 6, 16, 'هر کس در آن روز که قیامت برپا شود عذاب از او گردانده شود، قطعاً خدا بر او رحمت آورده و این است آن نیکبختی آشکار.'),
(806, 6, 17, 'و اگر خدا به تو گزندی برساند کسی جز او برطرف کننده آن نیست، و اگر خیری به تو برساند کسی بازدارنده آن نیست، چرا که او بر هر کاری تواناست.'),
(807, 6, 18, 'او بر بندگان خویش چیره است، در حالی که مافوق آنهاست و بر آنان احاطه دارد، و اوست آن حکیمی که کاری به گزاف نمی‌کند و آن آگاهی که دچار خطا نمی‌شود.'),
(808, 6, 19, 'از آنان بپرس گواهی چه چیزی از همه برتر و در اثبات حقیقت رساتر است؟ بگو: گواهی خدا از همه برتر است، و او میان من و شما به حقانیت من گواهی داده است، و بگو: این قرآن به من وحی شده است تا به وسیله آن شما و کسانی را که قرآن به آنان می‌رسد هشدار دهم. آیا واقعاً شما گواهی می‌دهید که با خدا معبودان دیگری است که با او شریک هستند؟ بگو: من گواهی نمی‌دهم. بگو: جز این نیست که او معبودی یگانه است و من از آنچه شریک او می‌پندارید دوری می‌گزینم.'),
(809, 6, 20, 'کسانی که به آنان کتاب آسمانی داده‌ایم (یهودیان و مسیحیان)، پیامبر را با نشانه‌های ثبت شده در آن کتاب‌ها می‌شناسند همان گونه که پسران خود را می‌شناسند، ولی کسانی که با کتمان حق خود را باخته و زیانکار ساخته‌اند ایمان نخواهند آورد.'),
(810, 6, 21, 'و کیست ستمکارتر از کسی که بر خدا دروغ بسته یا آیات او را دروغ انگاشته است؟ حقیقت این است که ستمکاران سعادتمند نمی‌شوند.'),
(811, 6, 22, 'و روزی را یاد کن که آنان را روانه می‌کنیم و یکجا گرد می‌آوریم، سپس به کسانی که برای خدا شریکانی پنداشته اند می‌گوییم: شریکانتان که آنها را شریک خدا می‌پنداشتید کجایند؟'),
(812, 6, 23, 'آن گاه پاسخشان جز این نیست که می‌گویند: به خدا، پروردگارمان سوگند که ما مشرک نبودیم.'),
(813, 6, 24, 'بنگر که چگونه بر خود دروغ می‌بندند. آن روز اوصافی که برای خدایانشان بافته بودند از دستشان می‌رود. (ناراستی اش برای آنان آشکار می‌شود)'),
(814, 6, 25, 'و از میان آنان کسانی هستند که به تو گوش فرا می‌دهند ولی ما بر دل هایشان پرده‌هایی افکنده‌ایم تا آن را درنیابند، و در گوش هایشان سنگینی قرار داده‌ایم. و اگر هر نشانه‌ای را ببینند آن را باور نمی‌کنند، تا آنجا که چون نزد تو آیند با تو مجادله می‌کنند. کسانی که کافر شده اند می‌گویند: این (قرآن) چیزی جز افسانه‌هایی که مردم نخستین بافته‌اند نیست.'),
(815, 6, 26, 'آنان مردم را از پیروی قرآن باز می‌دارند و خود نیز از آن دوری می‌کنند با این پندار که آیین الهی را به تباهی می‌کشند، در حالی که جز خویشتن را تباه نمی‌سازند و این را نمی‌فهمند.'),
(816, 6, 27, 'ای کاش می‌دیدی زمانی را که آنان بر آتش نگاه داشته می‌شوند، پس می‌گویند: ای کاش به دنیا بازگردانده می‌شدیم که در آن صورت نشانه‌های پروردگارمان را دروغ نمی‌شمردیم و از مؤمنان می‌گشتیم.'),
(817, 6, 28, 'نه، چنین نیست که خواهان ایمان باشند، بلکه آنچه پیش از این در دنیا پنهان می‌داشتند (آتش دوزخ) برایشان آشکار شده و آنان را به بازگشت به دنیا آرزومند کرده است، و اگر بازگردانده شوند دوباره به کارهایی که از آن نهی شده بودند برمی گردند، و آنان قطعاً دروغ می‌گویند که اگر بازگردند، از مؤمنان می‌شوند.'),
(818, 6, 29, 'و می‌گفتند: جز این زندگی ما در دنیا زندگی دیگری نیست و پس از مرگ برانگیخته نمی‌شویم.'),
(819, 6, 30, 'و ای کاش می‌دیدی زمانی را که آنان را در حضور پروردگارشان نگاه داشته‌اند و خدا به آنان می‌گوید: آیا این حق نیست؟ می‌گویند: چرا، سوگند به پروردگارمان که این حق است. خدا می‌گوید: پس عذاب را به وسیله همان دوزخی که انکارش می‌کردید، بچشید.'),
(820, 6, 31, 'قطعاً کسانی که دیدار خدا را دروغ پنداشتند زیانکار شدند، تا آن گاه که قیامت ـ بی آن که انتظارش را داشته باشند ـ بر آنان رسد، می‌گویند: دریغا که درباره آن کوتاهی کردیم. آنان در حالی این سخن را می‌گویند که بار سنگین گناهانشان را بر پشت خود حمل می‌کنند. بدانید که بد باری است که آنان برمی دارند.'),
(821, 6, 32, 'و زندگی دنیا چیزی جز بازیچه و سرگرمی نیست، و قطعاً سرای آخرت برای کسانی که تقوا پیشه می‌کنند بهتر است، پس آیا درنمی یابید؟'),
(822, 6, 33, 'و به یقین می‌دانیم که آنچه کافران درباره رسالت تو می‌گویند تو را اندوهگین می‌کند. اندوه مخور، چرا که آنان در حقیقت تو را تکذیب نمی‌کنند، بلکه این ستمکاران نشانه‌های خدا را انکار می‌کنند.'),
(823, 6, 34, 'و همانا پیامبرانی هم که پیش از تو بودند تکذیب شدند، ولی بر تکذیبی که با آن رو به رو گردیدند و بر آزاری که دیدند شکیبایی نمودند تا این که یاری ما به آنان رسید، و سخنان خدا (سنت‌های او) را هیچ دگرگون کننده‌ای نیست، پس نصرت ما را حتمی بدان. و قطعاً بخشی از داستان پیامبران به تو رسیده و با محنت‌های آنان آشنا شده‌ای.'),
(824, 6, 35, 'و اگر رویگردانی آنان بر تو گران است، چنانچه می‌توانی راهی در درون زمین یا نردبانی در آسمان بجویی و بدان جا روی و معجزه‌ای برایشان بیاوری که آنان را به پذیرش حق وادارد چنین کن، ولی بدان که اگر خدا می‌خواست آنان را بر هدایت گرد می‌آورد، پس مبادا از کسانی باشی که به معارف الهی جاهلند.'),
(825, 6, 36, 'فقط کسانی که شنونده حق اند دعوت تو را اجابت می‌کنند، نه آنان که چون مردگان ناشنوایند، و خدا همه مردگان را در روز قیامت برمی انگیزد و حقیقت را به آنان می‌فهماند، آن گاه به سوی او بازگردانده می‌شوند.'),
(826, 6, 37, 'و گفتند: چرا بر محمد از جانب پروردگارش معجزه‌ای که دلخواه ماست نازل نمی‌شود؟ بگو: خدا تواناست بر این که هر نشانه‌ای فروفرستد، ولی بیشتر آنان از قدرت بی کران خدا آگاه نیستند و نیز نمی‌دانند که اگر پس از آمدن معجزه‌ای که پیشنهاد می‌کنند ایمان نیاورند عذاب آنان را فرومی گیرد.'),
(827, 6, 38, 'و هیچ جنبنده‌ای در زمین نیست و نه هیچ پرنده‌ای که با دو بال خود پرواز می‌کند مگر این که آنها نیز مانند شما آدمیان امت‌هایی هستند ـ ما در آن کتاب چیزی را فروگذار نکرده‌ایم ـ وانگهی آنها چون شما آدمیان به سوی پروردگارشان محشور خواهند شد.'),
(828, 6, 39, 'و کسانی از شما که آیات ما را دروغ انگاشته‌اند کرانی هستند که از شنیدن سخن حق ناتوانند و لال‌هایی هستند که از گفتن حق ناتوانند. آنها در تاریکی‌ها به سر می‌برند، از این رو نمی‌توانند راه درست را ببینند. خدا هر که را بخواهد گمراه می‌کند و هر که را بخواهد بر راهی راست قرار می‌دهد.'),
(829, 6, 40, 'ای محمد، به مشرکان بگو: به من خبر دهید، اگر عذاب خدا به سوی شما آید یا لحظه قیامت بر شما دررسد، آیا برای برطرف شدن آن غیر خدا را می‌خوانید؟ اگر راستگویید، به این حقیقت اعتراف کنید.'),
(830, 6, 41, 'نه! غیر خدا را نمی‌خوانید، بلکه فقط خدا را می‌خوانید ـ پس او اگر بخواهد، آنچه را که برطرف شدنش را از او می‌طلبید برطرف می‌کند ـ و چیزهایی را که شریک او پنداشته‌اید از یاد می‌برید.'),
(831, 6, 42, 'همانا پیش از تو نیز پیامبرانی را به سوی امت‌هایی به رسالت فرستادیم [ولی آنها ایمان نیاوردند] ازاین رو آنان را به گزند و محنت گرفتار کردیم، باشد که در برابر خدا فروتنی کنند و او را بخوانند.'),
(832, 6, 43, 'پس چرا هنگامی که عذاب ما بر آنان دررسید فروتنی نکردند و به سوی خدا بازنگشتند؟ بلکه دل هایشان سخت شد و شیطان کارهایی را که می‌کردند برای آنان بیاراست.'),
(833, 6, 44, 'پس وقتی که آنچه را بدان تذکر داده شدند به فراموشی سپردند، درهای هرگونه موهبت را بر آنان گشودیم، تا چون به چیزهایی که به آنان داده شد شادمان گشتند ناگهان آنان را به کیفر گرفتیم و همان دم خاموش گشتند و هیچ حجتی بر ما نداشتند.'),
(834, 6, 45, 'پس ریشه آن مردمی که ستم کردند قطع شد، و ستایش از آنِ خداست که پروردگار جهانیان است.'),
(835, 6, 46, 'ای پیامبر، به مشرکان بگو: به من خبر دهید، اگر خدا گوش‌ها و دیدگانتان را بگیرد و بر دل هایتان مهر نهد جز خدا کدام معبودی است که آن را برای شما بازآورد؟ بنگر چگونه نشانه‌های توحید را به سوی اندیشه‌ها می‌گردانیم، سپس آنان روی برمی تابند.'),
(836, 6, 47, 'بگو: به من خبر دهید، اگر عذاب خدا ناگهان یا آشکارا به شما رسد، آیا جز گروه ستمکار نابود خواهند شد؟'),
(837, 6, 48, 'و ما پیامبران را جز برای مژده رساندن و هشدار دادن به رسالت نمی‌فرستیم. پس کسانی که ایمان بیاورند و صلاح پیشه سازند نه ترسی آنان را فرومی گیرد و نه اندوهگین می‌شوند.'),
(838, 6, 49, 'و کسانی که نشانه‌های ما را دروغ انگاشته‌اند، به سزای این که راه بندگی خدا را وانهاده‌اند، عذاب به آنان خواهد رسید.'),
(839, 6, 50, 'بگو: من به شما نمی‌گویم که گنجینه‌های خدا که پدیده‌ها را از آن به وجود می‌آورد نزد من است، تا خواسته‌های شما را برآورده کنم، و غیب نمی‌دانم تا آنچه را که نهان است برای شما بازگویم، و به شما نمی‌گویم که من فرشته‌ام که خوردن و آشامیدن را بر من عیب می‌گیرید. تفاوت من و شما در این است که خدا به من وحی کرده و مرا بینا نموده است، و من جز آنچه را که به من وحی می‌شود پیروی نمی‌کنم. بگو: آیا نابینا و بینا برابرند؟ پس آیا نمی‌اندیشید که باید نابینا از بینا پیروی کند؟'),
(840, 6, 51, 'و به وسیله این قرآن کسانی را هشدار ده که می‌ترسند به سوی پروردگارشان محشورشان کنند در حالی که جز او سرپرست و شفاعت کننده‌ای ندارند، آری هشدارشان ده، باشد که تقوا پیشه کنند.'),
(841, 6, 52, 'و مبادا به درخواست کفرپیشگان توانگر، مؤمنان تهیدستی را که هر صبح و عصر خدا را می‌خوانند و قرب او را می‌جویند از خود برانی، نه چیزی از حساب اعمال آنان برعهده توست و نه چیزی از حساب اعمال تو برعهده آنان است که بخواهی آنان را از خود برانی. اگر چنین کنی از ستمگران خواهی شد.'),
(842, 6, 53, 'و بدین سان برخی از مردم را به وسیله برخی دیگر آزمودیم، عده‌ای را توانگر و گروهی را تهیدست قرار دادیم، تا کافران متکبر درباره مؤمنان تهیدست بگویند: آیا از میان ما اینانند که خدا به آنها نعمت عطا کرده و به ایمان رهنمونشان ساخته است؟ آری چنین است، مگر نه این است که خدا از همه کس به سپاسگزاران نعمتش داناتر است.'),
(843, 6, 54, 'و چون کسانی که به آیات ما ایمان دارند نزد تو آیند، به آنان بگو: سلام بر شما. پروردگارتان رحمت را بر خود واجب کرده است. چنین است که هر کس از شما از روی نادانی ـ نه از روی تکبر و خودبرتربینی ـ گناهی کند سپس بعد از آن توبه نماید و شایسته گردد، خدا او را می‌بخشد که او آمرزنده و مهربان است.'),
(844, 6, 55, 'و بدین سان آیات (معارف الهی) را شرح می‌دهیم تا اموری که باید، تحقق یابد و راه گنهکاران نیز برای اهل ایمان آشکار شود.'),
(845, 6, 56, 'بگو: من نهی شده‌ام از این که کسانی را بپرستم که شما آنها را به جای خدا می‌خوانید و می‌پرستید. بگو: پرستش غیر خدا برخاسته از هواهای نفسانی شماست و من از هواهای شما پیروی نمی‌کنم، چرا که در این صورت گمراه شده‌ام و از ره یافتگان نخواهم بود.'),
(846, 6, 57, 'بگو: من در اثبات رسالت خود، بر قرآن که دلیلی روشن از جانب پروردگار من است اتکا دارم و شما آن را دروغ انگاشته‌اید، و معجزاتی که درخواست می‌کنید و شتاب در آمدنِ آنها را می‌خواهید در اختیار من نیست. فرمانروایی جز از آنِ خدا نیست، حق را از باطل جدا می‌کند و او بهترین داور میان من و شماست.'),
(847, 6, 58, 'بگو: اگر آنچه از من درخواست می‌کنید و شتاب در آمدنِ آن را می‌خواهید در اختیار من بود و من آن را می‌آوردم، شما باز هم ایمان نمی‌آوردید و کار میان من و شما به پایان رسیده بود، و خدا از هر کسی به ستمکاران داناتر است.'),
(848, 6, 59, 'و گنجینه‌های غیب تنها نزد اوست، جز او کسی آنها را نمی‌داند، و آنچه را که در خشکی و دریاست می‌داند، و هیچ برگی نمی‌افتد مگر این که آن را می‌داند، و هیچ دانه‌ای در تاریکی‌های زمین فرونمی افتد و نه هیچ‌تر و خشکی مگر این که آنها را می‌داند. آری هیچ چیزی فرونمی افتد مگر این که آن در کتابی روشن ثبت شده است.'),
(849, 6, 60, 'اوست آن که شما را در شب (آن گاه که می‌خوابید) برمی گیرد در حالی که می‌داند در روز چه دستاوردی داشته‌اید، سپس در روز (آن گاه که بیدار می‌شوید) شما را برمی انگیزد تا سرآمدی که معین شده است سپری گردد، آن گاه پس از پایان یافتن سرآمدتان بازگشت شما به سوی اوست، سپس شما را از حقیقت کارهایی که می‌کردید آگاه می‌کند.'),
(850, 6, 61, 'و اوست که بر بندگان خویش چیره است در حالی که مافوق آنهاست و بر آنان احاطه دارد، و بر شما فرشتگانی می‌فرستد که از بلاهای ویرانگر نگاهتان دارند، تا آن گاه که مرگ یکی از شما فرارسد، فرستادگان ما جان او را می‌گیرند و در این کار کوتاهی نمی‌کنند.'),
(851, 6, 62, 'و پس از آن که آدمیان مردند برانگیخته می‌شوند و به سوی خدا که مولای حقیقی آنان است بازگردانده می‌شوند. همگان بدانند که فرمانروایی تنها از آنِ اوست و او سریع‌ترین حسابرسان است.'),
(852, 6, 63, 'بگو: چه کسی شما را از دشواری‌هایی که در تاریکی‌های خشکی و دریا برایتان پدید می‌آید رهایی می‌بخشد؟ آنگاه شما او را آشکارا و با فروتنی و نیز در نهان می‌خوانید که اگر ما را از این مهلکه نجات بخشد بی گمان از سپاسگزاران او خواهیم شد.'),
(853, 6, 64, 'بگو: خداست که شما را از آن سختی‌ها و از هر اندوه دیگری نجات می‌بخشد، آن گاه شما پیمان خود را می‌شکنید و همچنان برای او شریک می‌پندارید.'),
(854, 6, 65, 'بگو: خداست که می‌تواند از فراز سرتان یا از زیر پاهایتان عذابی بر شما بفرستد یا شما را به صورت فرقه‌ها و گروه‌های مختلف به هم اندازد و گزند برخی از شما را به برخی دیگر بچشاند. بنگر که چگونه نشانه‌ها را به سوی اندیشه‌ها می‌گردانیم، باشد که آنان بفهمند.'),
(855, 6, 66, 'ای پیامبر، قوم تو عذاب الهی را دروغ انگاشتند در حالی که آن راست و درست است. به آنان بگو: من عهده دار امور شما نیستم تا بتوانم شما را از تکذیب حق بازدارم.'),
(856, 6, 67, 'هر خبری هنگام وقوعی دارد که در آن ظرف تحقق خواهد یافت، و به زودی خواهید دانست چه کیفری برای خود آماده ساخته‌اید.'),
(857, 6, 68, 'و چون ببینی که کسانی از روی انکار و تمسخر در آیات ما فرو می‌روند از آنان روی برتاب تا در سخنی دیگر غیر از آن درآیند، و اگر شیطان تو را به فراموشی انداخت و در آن حال با آنان نشستی، پس از یادآوری با آن مردم ستمگر منشین.'),
(858, 6, 69, 'و بر آنان که تقوا پیشه کرده‌اند چیزی از حساب کسانی که آیات خدا را به مسخره می‌گیرند نخواهد بود و به گناه آنان مؤاخذه نخواهند شد، ولی به آنان یادآور شده‌ایم که با ایشان همنشینی نکنند، باشد که در دام این گناه نیفتند و همچنان از آن بپرهیزند.'),
(859, 6, 70, 'و کسانی را که دین خود را بازیچه و سرگرمی گرفته‌اند و زندگی دنیا آنان را فریفته است رها کن، و با این قرآن حقایق را به مردم یادآوری کن تا مبادا کسی به خاطر گناهانی که فراهم آورده است از پاداش‌های الهی محروم گردد. برای چنین کسی سرپرست و شفاعت کننده‌ای جز خدا نیست، و اگر هر فدیه‌ای برای رهایی خود بدهد از او پذیرفته نمی‌شود. آنان کسانی اند که به سبب آنچه فراهم آورده‌اند از پاداش‌های الهی محرومند، و به سزای این که بر کفر خود اصرار میورزیدند، نوشیدنی ای از آب جوشان و عذابی دردناک خواهند داشت.'),
(860, 6, 71, 'بگو: آیا جز خدا چیزی را بخوانیم که نه سودی به ما می‌رساند و نه زیانی، و آیا پس از آن که خدا ما را هدایت کرده، به گذشته‌های خود که چیزی جز گمراهی نبوده است بازگردانده شویم؟ مانند کسی که شیاطین می‌خواهند او را به زمین فروافکنند، در حالی که سرگشته است و یارانی راه یافته هم دارد که او را به هدایت فرا می‌خوانند که: به سوی ما بیا. بگو: هدایتِ خداست که هدایت واقعی است، و ما فرمان یافته‌ایم که تسلیم پروردگار جهانیان باشیم.'),
(861, 6, 72, 'و به ما گفته شده است که نماز را برپا دارید و از حساب و کیفر خدا پروا کنید، و اوست آن که شما را به سویش روانه خواهند کرد.'),
(862, 6, 73, 'و اوست آن که آسمان‌ها و زمین را به حق آفرید و هدفی برای آن مقرر داشت، و روزی که قیامت را می‌گوید: باش، قیامت پدید می‌آید و سخن او حق است. و روزی که در صور دمیده شود، برای همگان آشکار گردد که پادشاهی فقط از آنِ اوست. به نهان و آشکار داناست، و اوست که تدبیرش حکیمانه است و از همه امور آگاه است.'),
(863, 6, 74, 'و یاد کن هنگامی را که ابراهیم به پدرش آزر گفت: آیا بت‌هایی [ناتوان] را خدایان خود می‌گیری و به پرستش آنها می‌پردازی؟ بی گمان، من تو و قوم تو را در گمراهی آشکاری می‌بینم.'),
(864, 6, 75, 'و بدین سان به ابراهیم ملکوت آسمان‌ها و زمین (وجود ربطی آنها به خدا) را نشان دادیم تا حقایقی را دریابد و از کسانی باشد که به آیات ما یقین دارند.'),
(865, 6, 76, 'از این رو هنگامی که تاریکی شب او را فراگرفت و ستاره‌ای را دید، گفت: این پروردگار و مالک من است که تدبیر امور می‌کند، اما چون غروب کرد گفت: غروب کنندگان را دوست ندارم، چرا که آنها درخور تدبیر نیستند.'),
(866, 6, 77, 'آن گاه ماه در پی آن برآمد و هنگامی که ماه را دید طلوع می‌کند گفت: این پروردگار و مالک من است و تدبیر امور در اختیار اوست، اما وقتی آن هم غروب کرد گفت: اگر آن کس که حقیقتاً پروردگار من است مرا راه ننماید بی گمان در زمره مردم گمراه خواهم بود.'),
(867, 6, 78, 'پس از آن خورشید از افق برآمد و چون خورشید را در حال طلوع دید گفت: این پروردگار من است این از همه بزرگ‌تر است، اما همین که خورشید نیز غروب کرد گفت: ای مردم، من از شرک ورزیدن شما و از آنچه شریک خدا شمرده‌اید دور و برکنارم.'),
(868, 6, 79, 'بی تردید، من به کسی روی کردم که آسمان‌ها و زمین را پدید آورده است و من به غیر او تمایل نخواهم یافت و از مشرکان نخواهم بود.'),
(869, 6, 80, 'و قومش با او بر سر این که تدبیر امور در اختیار خدایان آنهاست به محاجه پرداختند و او را از گزند بت‌ها بیم دادند. ابراهیم گفت: آیا با من درباره پروردگاری خدا محاجّه می‌کنید در حالی که او مرا به سوی خود هدایت کرده است؟ و من از آنچه شما شریک او می‌پندارید بیم ندارم تا به پرستش آن ناگزیر باشم، مگر این که پروردگارم ترس از آن را برای من بخواهد. علم پروردگارم همه چیز را فرا گرفته است، آیا درنمی یابید و متذکّر نمی‌شوید؟'),
(870, 6, 81, 'چگونه من از آنچه شریک خدا می‌پندارید بترسم در حالی که شما نمی‌ترسید از این که چیزهایی را شریک خدا قرار داده‌اید که او هیچ حجّتی بر خدایی آنها بر شما فرو نفرستاده است؟ با این وصف، اگر اهل دانشید کدام یک از ما دو گروه به ایمنی سزاوارتر است؟'),
(871, 6, 82, 'آن گاه گفت: کسانی که به یکتایی خدا در تدبیر جهان ایمان آورده و ایمانشان را با هیچ ستمی نپوشانده و بی اثر نکرده‌اند آنانند که ایمنی دارند و آنان هدایت یافتگانند.'),
(872, 6, 83, 'ای پیامبر، این بود حجت ما که آن را به ابراهیم در برابر قومش عطا کردیم. هر که را بخواهیم به درجاتی از علم و هدایت بالا می‌بریم، و ما به کسانی که درخورِ آنند آگاهیم و آن را براساس حکمت به آنان عطا می‌کنیم. به راستی پروردگار تو حکیم و داناست.'),
(873, 6, 84, 'و به او اسحاق و یعقوب را بخشیدیم و همه آنان را هدایت کردیم و نوح را پیش از این راه نمودیم، و داود و سلیمان و ایّوب و یوسف و موسی و هارون را که از ذریه نوح اند هدایت کردیم. آنان از نیکوکاران بودند و ما نیکوکاران را این گونه پاداش می‌دهیم.'),
(874, 6, 85, 'و نیز از ذریّه نوح، زکریا و یحیی و عیسی و الیاس را راه نمودیم، همه آنان از شایستگان بودند.'),
(875, 6, 86, 'و اسماعیل و یسع و یونس و لوط را که از نسل نوح بودند هدایت کردیم و آنان را بر مردم جهان برتری بخشیدیم.'),
(876, 6, 87, 'و از پدران و فرزندان و برادرانشان گروهی را راه نمودیم و آنان را برگزیدیم و همگی را به راهی مستقیم هدایت کردیم.'),
(877, 6, 88, 'این است هدایت خدا که از بندگانش هر که را بخواهد بدان راه می‌نماید، و پیامبران را بدان هدایت کرده است، و اگر آنان هم شرک میورزیدند قطعاً کارهای نیکی که انجام داده بودند تباه می‌شد و از دستشان می‌رفت.'),
(878, 6, 89, 'آنان کسانی بودند که کتاب آسمانی و داوری و نبوت به ایشان دادیم. ای پیامبر، اگر این مردم به آنچه به پیامبران داده‌ایم کافر شوند، غم مدار، زیرا گروهی را بر آن گمارده‌ایم که هرگز به آن کافر نمی‌شوند.'),
(879, 6, 90, 'اینانند کسانی که خدا آنان را راه نموده است پس راهشان را پیروی کن و مردم را بدان فراخوان و به آنان بگو: من بر ابلاغ آن از شما هیچ مزدی درخواست نمی‌کنم، چرا که آن تذکاری برای همه امّت‌های بشری و غیر بشری است و وسیله کسب درآمد نیست.'),
(880, 6, 91, 'یهودیان خدا را چنان که درخورِ عظمت اوست بزرگ نداشتند آن گاه که گفتند: خدا بر هیچ بشری چیزی (وحی و کتاب آسمانی) فرو نفرستاده است. به آنان بگو: چه کسی آن کتابی را که موسی آورد و برای مردم مایه روشنی و هدایت بود فرو فرستاد؟ شما آن را در کاغذهایی می‌نویسید و بخشی از آن را آشکار می‌کنید و بسیاری را نهان می‌دارید. و به شما معارفی از دین آموخته شده است که نه خود می‌توانستید آنها را فراگیرید و نه پدرانتان. آیا آنها را چه کسی به شما آموخت؟ بگو: خدا آن کتاب را نازل کرد و او آن حقایق را به شما آموخت. پس از این پاسخ آنان را به حال خود واگذار تا در باطلی که فرو رفته‌اند بازی کنند.'),
(881, 6, 92, 'و این قرآن کتابی است پرخیر و برکت که ما آن را فرو فرستادیم و کتابی را که پیش از آن بوده است تصدیق می‌کند. آن را فرو فرستادیم تا به وسیله آن مردمِ ام القری (مکه) و کسانی را که پیرامون آنند هشدار دهی. و کسانی که به سرای آخرت ایمان دارند، به این قرآن ایمان می‌آورند و آنانند که بر نماز خود مراقبت می‌کنند (آن را با حالت خشوع به جای می‌آورند).'),
(882, 6, 93, 'و کیست ستمکارتر از آن کس که با شریک قرار دادن برای خدا بر او دروغ بسته یا آن کس که گفته به من وحی شده در حالی که چیزی بر او وحی نشده است و آن کس که گفته من نیز به زودی مانند آنچه خدا نازل کرده است نازل خواهم کرد؟ ای کاش می‌دیدی زمانی را که این ستمکاران در دشواری‌های مرگ فرو رفته‌اند و فرشتگان دست‌های خود را گشوده‌اند و می‌گویند: جان‌های خود را برآورید، امروز به سزای آن سخنان دروغی که بر خدا می‌بستید و از روی تکبر آیات او را انکار می‌کردید به عذاب خوارکننده کیفر داده می‌شوید.'),
(883, 6, 94, 'به راستی امروز (روز مرگ) همان گونه که نخستین بار شما را آفریدیم تنها نزد ما آمده‌اید، و آنچه را به شما داده بودیم که در زندگی دنیا از آن بهره برید، پشت سر خود نهاده‌اید، و شفاعت کنندگان خود را که می‌پنداشتید در تدبیر امور شما شریک خدا هستند با شما نمی‌بینیم. همانا میان شما جدایی افتاده و اینک با مشاهده حقیقت، آنچه باور کرده بودید از شما دور گشته و ناپدید شده است. (ناراستی اش برای شما آشکار گشته است)'),
(884, 6, 95, 'خداوند دانه و هسته را می‌شکافد و از آن دو، گیاه و درخت می‌رویاند. موجود زنده را از موجود بی جان پدید می‌آورد و مرده را نیز از زنده برمی آورد. ای مردم، آن کس که چنین می‌کند خداست نه غیر او، پس تا کی شما را از حق بازمی دارند و به سوی باطل برمی گردانند؟'),
(885, 6, 96, 'او شکافنده سپیده دم است، و اوست که شب را مایه آرامش قرار داده و حرکت خورشید و ماه را با حساب‌هایی مقرر داشته است. این است اندازه گیری آن شکست ناپذیر دانا.'),
(886, 6, 97, 'و اوست آن که ستارگان را برای شما پدید آورده تا به وسیله آنها در تاریکی‌های خشکی و دریا راه یابید. به راستی ما نشانه‌های یکتایی خود را برای مردمی که می‌دانند به روشنی بیان کرده‌ایم.'),
(887, 6, 98, 'و اوست آن که شما را از یک تن پدید آورده است، پس برخی از شما به جایگاه استقرار خود در زمین آمده‌اید و برخی در جایگاهی به ودیعت نهاده شده‌اید تا به محل استقرار خود برسید. به راستی ما نشانه‌های یکتایی خود را برای مردمی که درمی یابند به روشنی بیان کرده‌ایم.'),
(888, 6, 99, 'و اوست آن که از آسمان آبی فرو فرستاد، پس به وسیله آن هر چیزی را که قابل رویش و رشد است برآوردیم، آن گاه از آن گیاهی سبز رویاندیم، و از آن دانه‌هایی بر هم نشسته برمی آوریم، و از درخت خرما، از شکوفه اش، خوشه‌هایی است نزدیک [به هم]، و باغ‌هایی از درختان انگور و زیتون و انارِ همسان و غیر همسان را رویانیدیم. به میوه هر یک از آنها هنگامی که به بار می‌نشیند و به رسیدنِ میوه اش بنگرید. ای مردم، در این مجموعه برای مردمی که ایمان دارند نشانه‌هایی از توحید هست.'),
(889, 6, 100, 'و جنیان را شریک خدا انگاشته‌اند در حالی که خدا آنها را آفریده است، و از روی نادانی برای او پسران و دخترانی ساخته‌اند. منزه است خدا و برتر از آنچه که او را بدان وصف می‌کنند.'),
(890, 6, 101, 'خدا آسمان‌ها و زمین را بی آن که نمونه قبلی داشته باشند پدید آورده است. چگونه او را فرزندی باشد، در حالی که او را همسری نبوده و هر چیزی را آفریده و او به هر چیزی داناست؟'),
(891, 6, 102, 'آن خداوند دانا تدبیرکننده امور شماست. معبودی شایسته پرستش جز او نیست، زیرا او آفریننده هر چیزی است، پس او را بپرستید که او برپا دارنده و عهده دار هر امری است.'),
(892, 6, 103, 'دیدگان، او را درنمی یابند ولی او همه دیدگان را درمی یابد، و اوست که در ژرفای هر چیزی نفوذ دارد و از ظاهر و باطن آن آگاه است.'),
(893, 6, 104, 'همانا از جانب پروردگارتان روشنگری‌هایی برای شما آمده است، پس هر که در آنها بنگرد و آنها را دریابد به سود خود اوست، و هر که کور گردد و آنها را درنیابد به زیان خود اوست، و من بر شما نگهبان نیستم تا بتوانم دل هایتان را به سویی که می‌خواهم بکشانم.'),
(894, 6, 105, 'و بدین سان دلیل‌ها و نشانه‌ها را با بیان‌های گوناگون می‌آوریم تا آنچه باید به انجام رسد، و تا کفرپیشگان به نهایت نگون بختی برسند و بگویند: ای محمّد، تو آنها را از دیگران فرا گرفته‌ای، و تا قرآن را برای مردمی که می‌دانند به روشنی بیان کنیم.'),
(895, 6, 106, 'آنچه را که از جانب پروردگارت به تو وحی شده است پیروی کن که معبودی شایسته پرستش جز او نیست، و از مشرکان روی بگردان.'),
(896, 6, 107, 'و اگر خدا می‌خواست آنان مشرک نمی‌شدند، و ما تو را نگهبان امور زندگی آنان قرار نداده‌ایم، و تو عهده دار رفتار و باورهای دینی آنان نیستی.'),
(897, 6, 108, 'و کسانی را که مشرکان به جای خدا می‌خوانند دشنام ندهید که آنان نیز خدا را از روی دشمنی که برخاسته از نادانی است دشنام می‌دهند. بدین سان برای هر امتی کردارشان را آراسته‌ایم، پس به آنچه زیبا می‌دانند ناسزا مگویید. آن گاه بازگشت آنان به سوی پروردگارشان خواهد بود و او آنان را از حقیقت آنچه انجام می‌دادند با خبر خواهد ساخت.'),
(898, 6, 109, 'و با سخت‌ترین سوگندهایشان به خدا سوگند خوردند که اگر نشانه‌ای بر رسالت محمد به آنان برسد قطعاً به آن ایمان می‌آورند. بگو: نشانه‌ها فقط نزد خداست و من اختیاردار آنها نیستم. چه بسا آنان را در سوگندهایشان راستگو به شمار آورید ولی چه می‌دانید که چون نشانه‌ها به آنان رسد ایمان نمی‌آورند.'),
(899, 6, 110, 'و ما دل‌ها و دیدگانشان را دگرگون می‌کنیم، ازاین رو به آن نشانه‌ها و معجزه‌ها ایمان نمی‌آورند، همان گونه که بار نخست پیش از آن که معجزات پیشنهادی آنان را ارائه کنیم به قرآن ایمان نیاوردند، و ما آنان را رها می‌کنیم که در سرکشی شان همواره سرگردان بمانند.'),
(900, 6, 111, 'اگر پیشنهادهای آنان را می‌پذیرفتیم و فرشتگان را به سویشان فرو می‌فرستادیم، و مردگان را زنده می‌کردیم تا با آنان سخن گویند و درستی دعوت ما را گوشزد کنند، و هر چیزی را روبه روی آنان گرد می‌آوردیم تا برای آنان گواهی دهند باز هم ایمان نمی‌آوردند، مگر این که خدا بخواهد که آنان ایمان بیاورند، ولی بیشترشان نمی‌دانند که معجزه‌ها و سایر اسباب به اذن خدا اثر می‌گذارند.'),
(901, 6, 112, 'ای پیامبر، همان گونه که تو دشمنانی داری، ما برای هر پیامبری دشمنانی از شیاطین انس و جن مقرر داشتیم. آنان برای فریبکاری، سخنان آراسته را با رمز و اشاره به یکدیگر القا می‌کنند، و اگر خدا می‌خواست چنین نمی‌کردند و با پیامبران دشمنی نمی‌نمودند، پس آنان را با دروغ‌هایی که می‌پردازند واگذار.'),
(902, 6, 113, 'ما چنین کردیم تا اهداف مورد نظرمان تحقق یابد و تا دل‌های کسانی که به آخرت ایمان ندارند به سخنان آراسته و فریبنده شیاطین گوش سپارد و تا ایشان آنها را بپسندند، و تا از تیره بختی سرای آخرت فراهم آورند آنچه را فراهم آورنده‌اند.'),
(903, 6, 114, 'با این وصف، آیا داوری جز خدا بجویم؟ در حالی که اوست که این کتاب را به سوی شما فروفرستاده است، کتابی که احکام و معارف آن به روشنی بیان شده است. و کسانی که به آنان کتاب آسمانی داده‌ایم (یهودیان و مسیحیان) می‌دانند که آن از جانب پروردگارت به حق نازل شده است، پس هرگز از تردیدکنندگان مباش.'),
(904, 6, 115, 'و پیام پروردگارت (دعوت به آیین اسلام) با رسالت تو و نزول قرآن به درستی و بی هیچ کاستی تحقق یافت و کامل گشت. هیچ دگرگون کننده‌ای نیست که سخنان او را تغییر دهد، و اوست که شنونده سخن بندگان و دانا به کارهای آنان است.'),
(905, 6, 116, 'بیشتر کسانی که در زمین اند شایسته پیروی نیستند پس اگر از آنان پیروی کنی تو را از راه خدا گمراه می‌کنند، زیرا آنان جز از گمان پیروی نمی‌کنند و در شناخت معارف الهی تنها به حدس و تخمین اکتفا می‌کنند.'),
(906, 6, 117, 'آری، پروردگارت به کسی که از راه او منحرف می‌شود از همه داناتر است و او به هدایت یافتگان نیز از همه داناتر است.'),
(907, 6, 118, 'پس اگر به آیات او ایمان دارید، از آن دامی که به هنگام سر بریدنش نام خدا بر آن برده شده است بخورید.'),
(908, 6, 119, 'برای شما چه سودی دارد که از آن دامی که هنگام ذبحش نام خدا بر آن برده شده است نمی‌خورید؟ در حالی که خداوند آنچه را بر شما حرام نموده برایتان به تفصیل بیان کرده و آنها را نیز در صورت اضطرار حلال شمرده است، ولی بسیاری از مردم از روی هواهایشان، بی هیچ دانشی، دیگران را به گمراهی می‌کشانند. بی گمان، پروردگارت به کسانی که از حدود الهی تجاوز می‌کنند داناتر است.'),
(909, 6, 120, 'و گناهی را که بدیِ فرجام آن آشکار است و نیز گناهی را که ناخوشایندی عاقبتش نهان است وانهید. قطعاً کسانی که مرتکب گناه می‌شوند، دیری نمی‌گذرد که به همان چیزی که مرتکب شده‌اند سزا داده می‌شوند.'),
(910, 6, 121, 'و از آن دامی که به هنگام ذبحش نام خدا بر آن برده نشده است نخورید که آن فسق است و خروج از مرز الهی به شمار می‌رود، و قطعاً شیاطین با رمز و اشاره بر افکار دوستانشان القا می‌کنند که درباره یکسان بودن دام‌های مرده و ذبح شده با شما مجادله کنند، و اگر از آنان پیروی کنید و سخنشان را بپذیرید، قطعاً مشرک خواهید بود.'),
(911, 6, 122, 'آیا کسی که مرده بود و ما او را به سبب ایمان حیات بخشیدیم و برای او نوری قرار دادیم که به وسیله آن در میان مردم حرکت کند مانند کسی است که حکایتش این است که در تاریکی‌های گمراهی به سر می‌برد و از آن بیرون شدنی نیست؟ این گونه برای کافران اعمالشان آراسته شده است.'),
(912, 6, 123, 'و این گونه در هر شهری سرانِ گنهکارش را پدید آوردیم تا در آن جا درباره رسالت پیامبر و آیات الهی نیرنگ زنند، ولی جز به خود نیرنگ نمی‌زنند و این حقیقت را درنمی یابند.'),
(913, 6, 124, 'و چون برای سران گناه پیشه نشانه‌ای بر رسالت پیامبر بیاید می‌گویند: ما هرگز ایمان نمی‌آوریم تا این که مانند رسالتی که به پیامبران خدا داده شده به ما نیز داده شود. [به آنان بگو:] خدا از هر کسی بهتر می‌داند که رسالتش را کجا قرار دهد و آن را به چه کسی عطا کند. به زودی گنهکاران را به سزای نیرنگ‌هایی که می‌زدند ذلتی از نزد خدا و عذابی سخت خواهد رسید.'),
(914, 6, 125, 'پس هر که را خدا بخواهد هدایت کند، به او شرح صدر می‌دهد تا در برابر حقایقی که از جانب اوست تسلیم شود، و هر که را بخواهد به گمراهی بیفکند، سینه اش را تنگ و محدود می‌کند که گویی در آسمان بالا می‌رود. بدین سان خداوند پلیدی را بر کسانی که ایمان نمی‌آورند قرار می‌دهد.'),
(915, 6, 126, 'و این است راه پروردگار تو که راست و استوار (تغییرناپذیر) است. به راستی ما نشانه‌ها را برای مردمی که پند می‌گیرند به روشنی بیان کرده‌ایم.'),
(916, 6, 127, 'برای هدایت یافتگان، نزد پروردگارشان سرای عافیت است، و به سزای کارهای نیکی که کرده‌اند، او سرپرست و عهده دار امورشان خواهد بود.'),
(917, 6, 128, 'و روزی که خدا آنان (آدمیان و جنیان) را یکسره گرد می‌آوَرَد و روانه می‌کند، می‌گوید: ای گروه جنیان، بسیاری از آدمیان را به پیروی از خود واداشتید و به گمراهی کشاندید، و پیروانشان از آدمیان می‌گویند: پروردگارا، ما و جنیان از یکدیگر بهره گرفتیم، ما با پیروی از آنها به هواها و هوس‌های خود دست یافتیم و آنها با وسوسه‌های خود ما را پیرو خود ساختند، و عاقبت به مرتبه‌ای که برای ما و آنها تعیین کرده بودی رسیدیم. خدا می‌گوید: آتش جایگاه شماست که جاودانه در آن خواهید بود، مگر آنچه را خدا بخواهد. به راستی پروردگار تو حکیم و داناست.'),
(918, 6, 129, 'و این گونه برخی از ستمکاران را بر برخی دیگر به کیفر گناهانی که فراهم آورده‌اند مسلّط می‌کنیم.'),
(919, 6, 130, 'در آن روز خدا می‌گوید: ای گروه جنیان و آدمیان، آیا از خودتان پیامبرانی به سوی شما نیامدند که آیات مرا بر شما حکایت کنند و شما را از دیدار امروزتان بترسانند؟ می‌گویند: ما به زیان خود گواهی می‌دهیم که پیامبرانی آمدند و به ما هشدار دادند. آری، به آنان هشدار داده شد، ولی زندگی دنیا آنان را بفریفت و به زیان خود گواهی دادند که به آنچه پیامبران آوردند کافر شدند.'),
(920, 6, 131, 'فرستادنِ پیامبرانِ هشداردهنده از آن روست که پروردگار تو این گونه نیست که دیاری را از روی ستم تباه کرده باشد در حالی که مردم آن از وظایف خود بی خبر باشند.'),
(921, 6, 132, 'و برای هر یک از آن دو گروه (آدمیان و جنیان) درجاتی برخاسته از کردارشان خواهد بود، و پروردگارت از کارهایی که می‌کنند بی خبر نیست.'),
(922, 6, 133, 'و پروردگار تو بی نیاز و دارای رحمت است و اگر بخواهد شما را از میان می‌برد، زیرا به شما نیازی ندارد. و پس از شما، به رحمت خود، آنچه بخواهد جایگزین می‌کند، همان گونه که شما را به رحمت خود از نسل مردمی دیگر پدید آورد.'),
(923, 6, 134, 'بی گمان، آنچه در مورد قیامت به شما وعده داده‌اند آمدنی است، و شما نمی‌توانید خدا را از آوردن آن ناتوان سازید.'),
(924, 6, 135, 'به مشرکان بگو: ای قوم من، براساس حالت و وضعیتی که دارید عمل کنید که من نیز بر آنچه هستم پایدارم و عمل می‌کنم، و دیری نمی‌گذرد که بدانید فرجام نیک این سرای از آنِ کیست. حقیقت این است که ستمکاران (مشرکان) نیکبخت نخواهند شد.'),
(925, 6, 136, 'مشرکان از کشت‌ها و دام‌ها که خدا پدید آورده است سهمی را برای او مقرر می‌داشتند و براساس پندار خود می‌گفتند: این سهمِ خدا و این سهمِ بت‌های ماست که شریکان خدایند. آنان چنین حکم می‌کردند که آنچه مال شریکان است به خدا نمی‌رسد و آنچه از آنِ خداست به شریکانشان می‌رسد. راستی این بد حکمی است که می‌کنند.'),
(926, 6, 137, 'و بدین سان معبودانِ اهل شرک دل‌های مشرکان را به تسخیر خود درآوردند و برای بسیاری از مشرکان کشتن فرزندانشان را بر آستانِ آن خدایان آراستند تا آنان را تباه سازند، و آیین درستی را که برایشان مقرر شده بود با آمیختن به اموری باطل بر آنان پوشیده دارند. و اگر خدا می‌خواست چنین نمی‌کردند. پس ای پیامبر، آنان را با دروغ‌هایی که می‌پردازند رها کن.'),
(927, 6, 138, 'و براساس گمان خود گفتند: این دام‌ها و کشت‌هایی که تقدیم خدایان می‌شود دام‌ها و کشت‌هایی ممنوع است که جز آن کس که ما بخواهیم نباید از آن بخورد و آنان دام‌هایی دیگر (بَحیرَه، سائِبَه و حامی) دارند که سوار شدن بر آنها حرام شده است، و برای آنان دام‌هایی است که هنگام ذبحشان نام خدا را بر آنها نمی‌برند. اینها را حکم خدا شمردند در حالی که بر او دروغ بستند. به زودی خدا آنان را به سبب افترایی که بسته‌اند کیفر خواهد داد.'),
(928, 6, 139, 'و گفتند: آنچه در شکم این دام‌ها (بحیره و سائبه) است اگر زنده به دنیا آید ویژه مردانِ ماست و بر زنانمان حرام است، و اگر مرده باشد مردان و زنان در آن شریک‌اند. خداوند این حکمشان را که به صورت عذاب درمی آید کیفر آنان قرار خواهد داد، که او حکیم و داناست.'),
(929, 6, 140, 'به یقین، کسانی که فرزندان خود را به بی خردی و نادانی برای تقرب به خدایان خود کشتند، و آنچه را خدا روزی آنان کرده بود از روی دروغ بستن به خدا بر خود حرام ساختند زیان کردند. قطعاً آنان گمراه شدند و هدایت یافته نبودند.'),
(930, 6, 141, 'و اوست آن که بوستان‌هایی با داربست و بوستان‌هایی بدون داربست و درختان خرما و کشتزارهایی با محصولات گوناگون و زیتون و انارِ همگون و ناهمگون پدید آورده است. از میوه اش چون به بار نشیند بخورید و حق آن را هنگام برداشت آن بدهید، و از اسراف بپرهیزید که خدا اسراف کنندگان را دوست ندارد.'),
(931, 6, 142, 'و از دام‌ها برخی را بزرگ و بارکش و برخی را کوتاه آفرید. از آنچه خدا روزی شما کرده است بخورید و گام‌های شیطان را دنبال نکنید که روزی خدا را بر خود حرام سازید، که او دشمن آشکار شماست.'),
(932, 6, 143, 'خداوند از دام‌ها هشت تای پدید آورده است، از گوسفندان دو تا (نر و ماده آنها) و از بزها دو تا (نر و ماده آنها). به مشرکان بگو: آیا نرهای آن دو را خدا حرام کرده یا ماده‌های آن دو یا جنینی را که رحم ماده‌های آنها دربرگرفته است؟ اگر راست می‌گویید، براساس هر نوع دانشی که برای شما حاصل شده باشد به من خبر دهید.'),
(933, 6, 144, 'و از شتران نیز دو تا (نر و ماده آنها) و از گاوها دو تا (نر و ماده آنها) را آفریده است. بگو: آیا نرهای آن دو را خدا حرام کرده یا مادّه‌های آن دو یا جنینی را که رحم ماده‌های آنها دربرگرفته است؟ آیا تحریم اینها را خود به اندیشه دریافته‌اید یا آن گاه که خدا شما را بدان سفارش می‌کرد حضور داشتید؟ هرگز چنین نیست، بلکه بر خدا دروغ بسته‌اید. و کیست ستمکارتر از آن کس که بر خدا دروغی بندد تا مردم را ندانسته به گمراهی افکنَد. شما ستمکارید و خدا مردم ستمکار را هدایت نمی‌کند.'),
(934, 6, 145, 'بگو: در آنچه به من وحی شده است طعام حرامی را نمی‌یابم که بر خورنده‌ای که آن را می‌خورد حرام شده باشد، مگر این که مردار باشد یا خونی ریخته شده یا گوشت خوک باشد که پلید است، یا دامی که آن را به نافرمانی سر بریده، همان که هنگام ذبحش نام غیر خدا را بر آن برده‌اند. و هر کس به خوردن یکی از اینها ناچار شود، به شرط آن که ناچاری او در اثر ستمکاری و سرکشی نباشد بر او حلال می‌شود، چرا که خدا آمرزنده و مهربان است.'),
(935, 6, 146, 'و بر یهودیان [خوردن گوشت] هر حیوان ناخنداری را حرام کردیم، و از گاو و گوسفند (میش و بز) پیه‌های آن دو را بر آنان حرام نمودیم، مگر پیه‌هایی که بر پشت یا روده هایشان باشد یا با استخوانی آمیخته باشد. این حکم را به سزای تجاوزگری شان برای آنان مقرر کردیم، و قطعاً ما راستگوییم.'),
(936, 6, 147, 'پس اگر تو را تکذیب کردند و تهدیدهای الهی را دروغ انگاشتند، به آنان بگو: پروردگار شما دارای رحمتی گسترده است، و عذاب او از مردم گناه پیشه بازگردانده نمی‌شود.'),
(937, 6, 148, 'کسانی که برای خدا شریک پنداشته‌اند خواهند گفت: اگر خدا می‌خواست، نه ما و نه پدرانمان برای خدا شریک قرار نمی‌دادیم و چیزی را حرام نمی‌ساختیم. کسانی هم که پیش از آنان بودند، همین گونه پیامبران خدا و پیام‌های الهی را تکذیب می‌کردند، تا این که عذاب ما را چشیدند. بگو: آیا دانشی نزد شماست که آن را برای ما آشکار کنید؟ شما در مورد معارف الهی هیچ دانشی ندارید و جز از گمان پیروی نمی‌کنید و فقط تخمین می‌زنید.'),
(938, 6, 149, 'بگو: پس حجت رسا تنها از آنِ خداست. اگر می‌خواست همه شما را به اجبار هدایت می‌کرد.'),
(939, 6, 150, 'بگو: گواهان خود را که گواهی می‌دهند به این که خدا اینها را حرام کرده است بیاورید. پس اگر گواهی دادند، تو با آنان گواهی مده، چرا که گواهی آنان از روی هوا و هوس است و تو نباید از هوا و هوس کسانی که آیات ما را دروغ شمرده و کسانی که به آخرت ایمان ندارند و برای پروردگارشان همتا قرار می‌دهند پیروی کنی.'),
(940, 6, 151, 'بگو: بیایید تا آنچه را پروردگارتان بر شما حرام کرده است برای شما بخوانم و آن این که چیزی را شریک او قرار مدهید و به پدر و مادر خود نیکی کنید و فرزندانتان را در اثر تنگدستی مکشید، زیرا ماییم که به شما و آنان روزی می‌دهیم، و به کارهای زشت چه آشکار آن و چه پنهانش نزدیک نشوید، و انسانی را که خدا کشتن او را حرام کرده است جز به حق مکشید. اینهاست که خدا شما را به آن سفارش کرده است، باشد که دریابید.'),
(941, 6, 152, 'و از نزدیک شدن به مال یتیم ـ جز به روشی هرچه نیکوتر که باعث حفظ اموال او شود ـ بپرهیزید تا به توانایی خود برسد (بالغ شود و به رشد لازم برسد)، و پیمانه و ترازو را عادلانه و تمام بسنجید و بدانید که ما هیچ کس را جز به اندازه توانش تکلیف نمی‌کنیم. و چون سخنی گویید که سود و زیانی در پی دارد، عدالت را در آن رعایت کنید، هر چند کسی که درباره او سخن می‌گویید خویشاوند باشد، و به پیمان خدا وفادار باشید. اینهاست که خدا شما را به آن سفارش کرده است، باشد که دریابید و متذکّر شوید.'),
(942, 6, 153, 'اینها که بر شما خواندم راه من است که راهی است راست و استوار (نه در آن اختلافی است نه پوینده اش را از مقصد باز می‌دارد)، پس آن را دنبال کنید و راه‌های دیگر را دنبال مکنید که شما را از راه خدا پراکنده سازد. اینهاست که خدا شما را به آن سفارش کرده است، باشد که تقوا پیشه کنید.'),
(943, 6, 154, 'ما این آموزه‌ها را در آیین موسی نیز آوردیم، سپس به او کتاب آسمانی تورات را دادیم تا بدین وسیله کاستی‌های شریعت آن کسی که نیکوکار بود کامل شود و حقایق آیین بنی اسرائیل به تفصیل بیان گردد و برای آنان رهنمود و رحمتی باشد، شاید به دیدار پروردگارشان ایمان بیاورند.'),
(944, 6, 155, 'و این قرآن کتابی است پرخیر و برکت که ما آن را فرو فرستادیم، پس، از آن پیروی کنید و تقوا پیشه سازید، باشد که مورد رحمت قرار گیرید.'),
(945, 6, 156, 'آری قرآن را فرو فرستادیم تا نگویید: تنها بر دو طایفه (یهودیان و مسیحیان) که پیش از ما بودند کتاب آسمانی نازل شد و ما از تلاوت و فراگیری آنان بی خبر بودیم.'),
(946, 6, 157, 'یا نگویید: اگر بر ما کتاب آسمانی فرو فرستاده می‌شد ما از آنان هدایت یافته‌تر بودیم. اکنون از جانب پروردگارتان دلیلی آشکار و رهنمود و رحمتی به سوی شما آمد، پس کیست ستمکارتر از آن کس که آیات خدا را دروغ شمرَد و از آنها روی برتابد؟ به زودی کسانی را که از آیات ما روی برمی تابند، به سزای روی برتافتنشان به عذاب ناگوار کیفر می‌دهیم.'),
(947, 6, 158, 'آیا کافران پس از آن همه حجّت‌های روشن و اندرزهای رسا جز این انتظار دارند که فرشتگان عذاب به سویشان بیایند یا پروردگارت برای آنان پدیدار شود یا برخی از نشانه‌های پروردگارت بر آنان در رسد؟ بدانند روزی که برخی از نشانه‌های پروردگارت بر آنان در رسد، کسی که قبلاً ایمان نیاورده یا ایمان آورده ولی در دورانی که ایمان داشته کار نیکی نکرده است، ایمانش به او سودی نمی‌بخشد. بگو در انتظار آن روز بمانید که ما نیز منتظریم.'),
(948, 6, 159, 'کسانی که در اثر اختلاف با یکدیگر، در دین خود پراکندگی پدید آورده و گروه گروه شده‌اند، تو در حزب و گروهشان نیستی و با آنان هیچ پیوندی نداری، کارشان فقط به خدا واگذار می‌شود، سپس آنان را به حقیقت آنچه کرده‌اند آگاه خواهد ساخت.'),
(949, 6, 160, 'هر کس نیکی بیاورد ده چندانِ آن پاداش خواهد داشت، و هر کس بدی بیاورد، جز به مانند آن او را کیفر نمی‌دهند و بر آنان ستم نمی‌رود.'),
(950, 6, 161, 'بگو: پروردگارم مرا به راهی راست و استوار که نه در آن اختلافی است و نه پوینده اش را از مقصد باز می‌دارد، هدایت کرده است، آیینی برپادارنده نیکبختی دنیا و آخرت، آیین ابراهیم، همو که از انحراف و شرک برکنار بود و در زمره مشرکان نبود.'),
(951, 6, 162, 'بگو: بی تردید نماز و عبادت من و زندگی و مرگ من برای خدا پروردگار همه جهان هاست.'),
(952, 6, 163, 'او را شریکی نیست، و من به این که همه کارهای خود را برای خدا انجام دهم فرمان یافته‌ام، و در این که تسلیم خدا باشم اولین مرتبه را دارم.'),
(953, 6, 164, 'بگو: آیا جز خدا پروردگاری بجویم با این که او پروردگار همه چیز است؟ و هیچ کس کاری نمی‌کند مگر این که بر دوش خودش خواهد بود، و هیچ باربرداری (گناهکاری) بار دیگری را بر دوش نمی‌کشد، سپس بازگشت شما به سوی پروردگارتان خواهد بود و او شما را از حقیقت آنچه بر سرش اختلاف می‌کردید آگاه خواهد ساخت.'),
(954, 6, 165, 'و اوست آن که شما را در زمین جایگزین پیشینیان ساخت، و برخی از شما را بر برخی دیگر به درجاتی برتری داد تا شما را در آنچه عطایتان کرده است بیازماید. به یقین پروردگار تو به سرعت کیفر می‌دهد، و به راستی او آمرزنده و مهربان است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'الف. لام. میم. صاد.'),
(956, 7, 2, 'این قرآن کتابی گرانقدر است که به سوی تو فرو فرستاده شده است، پس مبادا از ناحیه آن در سینه ات تنگی و فشاری باشد. خدا آن را فروفرستاده تا مردم را به وسیله آن هشدار دهی و برای مؤمنان مایه یادآوری باشد و مقام پروردگارشان را به آنان بشناساند.'),
(957, 7, 3, 'ای مردم، از آنچه از جانب پروردگارتان به سوی شما فروفرستاده شده است پیروی کنید و تحت ولایت و سرپرستی او درآیید و از غیر او پیروی نکنید که سرپرست شما می‌گردند. چه اندک درمی یابید که پروردگار شما خدای یکتاست.'),
(958, 7, 4, 'و چه بسیار شهرهایی که آنها را تباه ساختیم و چنان بود که عذاب ما شب هنگام یا نیمروز در حالی که به خواب فرو رفته بودند، به سراغشان آمد.'),
(959, 7, 5, 'پس هنگامی که عذاب ما به آنان رسید، سخنشان جز این نبود که گفتند: به یقین ما ستمکار بودیم.'),
(960, 7, 6, 'پس قطعاً از مردمی که پیامبران به سوی آنان فرستاده شدند، می‌پرسیم که آیا ایمان آوردند و کارهای شایسته کردند، و از پیامبران نیز که به سوی آنان به رسالت آمدند خواهیم پرسید. که آیا پیام خدا را ابلاغ نمودند'),
(961, 7, 7, 'آنگاه قطعاً از روی علم، آنچه را که بر پیامبران و امّت هایشان گذشته است برای آنان حکایت می‌کنیم، و ما هیچ گاه غایب نبوده‌ایم، بلکه شاهد اعمال و گفتار آنان بوده‌ایم.'),
(962, 7, 8, 'آن روز که قیامت برپا شود و زمان حساب فرارسد، حق تنها وسیله سنجش است، پس هر کس وزنه‌های اعمالش سنگین باشد آنان سعادتمندند.'),
(963, 7, 9, 'و هر کس وزنه‌های اعمالش سبک باشد، آنان کسانی اند که خود را زیانمند کرده‌اند، چرا که از روی ستم آیات ما را انکار نموده‌اند.'),
(964, 7, 10, 'و همانا ما شما را در زمین سکونت دادیم، و برای شما در آن اسباب زندگی نهادیم. شما اندک سپاسگزاری می‌کنید.'),
(965, 7, 11, 'و همانا ما شما را آفریدیم، سپس شما را صورتگری کردیم، آن گاه به فرشتگان گفتیم: برای آدم سجده کنید. پس همه سجده کردند، جز ابلیس که از سجده کنندگان نبود.'),
(966, 7, 12, 'خداوند به او گفت: آن گاه که به تو فرمان دادم برای آدم سجده کنی، چه چیز مانع شد و تو را واداشت که سجده نکنی؟ گفت: من از او بهترم، مرا از آتشی آفریدی و او را از گِلی پدید آوردی.'),
(967, 7, 13, 'خدا گفت: اکنون که گردنفرازی می‌کنی، از این جایگاهی که در آن قرار داری فرود آی. تو را نسزد که در آنجا خودبزرگ بینی کنی، بیرون شو که تو از خوارشدگانی.'),
(968, 7, 14, 'ابلیس گفت: مرا تا روزی که آدمیان از گورها برانگیخته شوند مهلت ده.'),
(969, 7, 15, 'خدا فرمود: تو از مهلت یافتگانی.'),
(970, 7, 16, 'ابلیس گفت: پس بدین سبب که مرا گمراه ساختی، من هم بر سر راه راستی که آدمیان را به تو می‌رساند می‌نشینم تا آنان را از آن منحرف سازم.'),
(971, 7, 17, 'آری بر آن راه کمین می‌کنم، آن گاه از پیش روی آنان و از پشت سرشان و از طرف راست و چپشان به سراغ آنان می‌روم، در نتیجه بیشترشان را شکرگزار نعمت‌های خود نمی‌یابی.'),
(972, 7, 18, 'خدا به او گفت: نکوهیده و مطرود از آن جا بیرون رو. قطعاً هر کس از آدمیان تو را پیروی کند دوزخ را از همه شما (از تو و آنان) پر خواهم کرد.'),
(973, 7, 19, 'و به آدم گفت: ای آدم، تو و همسرت در این بهشت سکونت کنید و از نعمت‌های آن، هر جا که خواهید، بخورید ولی به این درخت نزدیک نشوید که اگر از آن بخورید، از کسانی خواهید بود که بر خود ستم کرده رنج و سختی را بر خود روا شمرده‌اند.'),
(974, 7, 20, 'پس ابلیس آن دو را وسوسه کرد و آنها را به خوردن از آن درخت فراخواند تا شرمگاه هایشان را که از آنان پوشانده شده بود برایشان نمایان سازد، و گفت: پروردگارتان شما را از این درخت باز نداشت مگر برای این که مبادا شما دو فرشته شوید یا از جاودانان گردید.'),
(975, 7, 21, 'و برای آن دو به جدّ و جهد سوگند یاد کرد که قطعاً من از خیرخواهان شما هستم.'),
(976, 7, 22, 'پس با نیرنگی آن دو را به آن درخت ممنوع رسانید، و هنگامی که آدم و همسرش از آن چشیدند، شرمگاه هایشان برایشان آشکار شد، و شروع کردند از برگ درختان بهشت گردآورده و بر خود بچسبانند، و پروردگارشان آن دو را ندا داد که آیا شما را از آن درخت باز نداشتم و به شما نگفتم که شیطان دشمنی آشکار برای شماست؟'),
(977, 7, 23, 'گفتند: پروردگارا، ما بر خود ستم کردیم، و اگر ما را نیامرزی و بر ما رحمت نیاوری قطعاً از زیانکاران خواهیم بود.'),
(978, 7, 24, 'خدا به آدم و همسرش و ابلیس گفت: به زمین فرود آیید در حالی که دشمن یکدیگر خواهید بود و در زمین تا مدتی قرار و بهره‌ای خواهید داشت.'),
(979, 7, 25, 'و به آنان (آدم و همسرش و فرزندانشان) گفت: در زمین زندگی می‌کنید و در آن جا می‌میرید و از آن جا بیرون آورده خواهید شد.'),
(980, 7, 26, 'ای فرزندان آدم، ما برای شما جامه‌ای که شرمگاه‌های شما را بپوشاند و پوششی که مایه آرایش و زیبایی شما باشد پدید آوردیم، و جامه‌ای که شما را از زشتی‌ها و گناهان مصون دارد که آن تقوا و پرهیزگاری است برای شما بهتر و ضروری‌تر است. این از آیات خداوند است، آن را مقرّر داشتیم باشد که توجه کنند و دریابند.'),
(981, 7, 27, 'ای فرزندان آدم، مبادا شیطان شما را بفریبد همان گونه که پدر و مادر شما را با نیرنگ از آن بهشت بیرون کرد در حالی که جامه آن دو را از تنشان بیرون می‌آورد تا شرمگاه هایشان را به آنان بنمایاند. همانا او و گروهش شما را از جایی که آنان را نمی‌بینید می‌بینند. به یقین ما شیطان‌ها را سرپرست کسانی قرار داده‌ایم که ایمان نمی‌آورند.'),
(982, 7, 28, 'و چون کار زشتی کنند می‌گویند: پدرانمان را بر آن یافته‌ایم و خدا نیز ما را بدان فرمان داده است. بگو: خدا به کار زشت فرمان نمی‌دهد. آیا چیزی را که نمی‌دانید به خدا نسبت می‌دهید؟'),
(983, 7, 29, 'بگو: پروردگارم به عدل و داد فرمان داده است و این که در هر عبادتگاهی که به عبادت او می‌پردازید تمام توجه خود را به او کنید و او را بخوانید در حالی که اطاعت و عبادت خود را ویژه او می‌کنید. چنان که در آغاز شما را آفرید و دو گروه شدن را برای شما مقرر کرد و همین گونه به سوی او بازمی گردید.'),
(984, 7, 30, 'گروهی را هدایت کرد و گروهی گمراهی بر آنان حتمی گشت، زیرا شیاطین را به جای خدا سرپرست خویش گرفتند و گمان می‌کنند که راه یافتگانند.'),
(985, 7, 31, 'ای فرزندان آدم، هنگام حضور در هر مسجدی خود را بیارایید، و بخورید و بیاشامید ولی اسراف نکنید که خدا اسرافکاران را دوست ندارد.'),
(986, 7, 32, 'بگو: چه کسی زینت خدا را که برای بندگانش پدید آورده و روزی‌های پاکیزه را حرام کرده است؟ بگو: آنها در روز قیامت از آنِ کسانی خواهد بود که در زندگی دنیا ایمان آورده باشند و به آنان اختصاص خواهد یافت و دیگران از آن بهره‌ای نخواهند برد. ما این گونه آیات خود را برای مردمی که می‌دانند، به تفصیل بیان می‌کنیم.'),
(987, 7, 33, 'بگو: زینت‌ها و روزی‌ها حرام نشده است. پروردگارم فقط کارهای زشت، چه آشکارشان و چه نهانشان و نیز گناه و ستم را که نارواست حرام کرده است و این که چیزهایی را شریک خدا قرار دهید که درباره آنها هیچ برهانی فرونفرستاده است و این که چیزی را که نمی‌دانید به خدا نسبت دهید.'),
(988, 7, 34, 'و برای هر امّتی سرآمدی است، سپس هنگامی که سرآمدش فرارسید، نه ساعتی از آن بازپس می‌مانند و نه از آن پیش می‌افتند.'),
(989, 7, 35, 'ای فرزندان آدم، اگر از میان شما پیامبرانی به سویتان آمدند ـ که قطعاً خواهند آمد ـ و آیات مرا بر شما حکایت کردند، کسانی که تقوا پیشه کنند و شایسته شوند، نه ترسی بر آنان است و نه اندوهگین می‌شوند.'),
(990, 7, 36, 'و کسانی که آیات ما را دروغ انگاشته و از روی تکبّر از پذیرش آنها خودداری کرده‌اند، آنان همدم آتشند و در آن جاودانه‌اند.'),
(991, 7, 37, 'اکنون که آیین الهی به آدمیان ابلاغ شده، چه کسی ستمکارتر است از آن کس که با شرک ورزیدن خود بر خدا دروغ بسته یا آیات او را دروغ انگاشته است. بهره آنان از کتابِ تقدیر به ایشان خواهد رسید، تا این که وقتی فرستادگان ما (فرشتگان مرگ) به سراغشان می‌آیند که جانشان را بستانند، می‌گویند: کجاست آنچه به جای خدا می‌خواندید؟ می‌گویند: از دست ما رفته‌اند (اثری از خدایی آنها نمی‌بینیم) و آنان بر ضدّ خود گواهی می‌دهند که کافر بوده‌اند.'),
(992, 7, 38, 'و خدا به آنان می‌گوید: در میان امّت‌هایی از جنّ و انس که پیش از شما درگذشتند، به آتش دوزخ درآیید. هرگاه گروهی به آن جا وارد شوند، هم مسلکان خود را لعنت کنند، تا آن گاه که همگی در آن جا به هم رسند، به مشاجره پردازند و پسینیانشان (پیروان) درباره پیشینیانشان (پیشوایان) گویند: پروردگارا، اینان خود گمراه بودند و ما را نیز به گمراهی افکندند، پس عذابی دوچندان از آتش به آنان ده. خدا می‌گوید: برای هر دو گروه دوچندان است، شما نیز گمراه بودید و آنان را در گمراه گری پشتیبانی کردید، ولی درنمی یابید که هر دو گروه به عذابی دوچندان گرفتارید.'),
(993, 7, 39, 'و پیشینیانشان به پسینیانشان می‌گویند: شما هیچ برتری بر ما ندارید تا درخور عذابی سبک‌تر باشید، پس به گناهانی که فراهم آورده‌اید عذاب آتش را بچشید.'),
(994, 7, 40, 'آری، کسانی که آیات ما را دروغ انگاشتند و از روی تکبر از پذیرفتن آنها خودداری کردند، درهای آسمان به روی آنان گشوده نمی‌شود و به بهشت درنمی آیند تا این که شتر به سوراخ سوزن فرو شود، و ما گنهکاران را این گونه سزا می‌دهیم.'),
(995, 7, 41, 'برای آنان از دوزخ بستری و از فرازشان پوشش‌هایی از عذاب خواهد بود، و ما ستمکاران را این گونه سزا می‌دهیم.'),
(996, 7, 42, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند ـ ما هیچ فردی را جز به اندازه توانش تکلیف نمی‌کنیم ـ اینان اهل بهشت اند و در آن جاویدانند.'),
(997, 7, 43, 'و هر کینه و دشمنی را از دل هایشان برمی کنیم. آنان در قصرهایی بلندمرتبه و با شکوه که از زیرشان نهرها روان است خواهند بود و می‌گویند: ستایش از آنِ خداست که ما را به این موهبت راه نمود، و اگر خدا ما را راه ننموده بود ما چنان نبودیم که راه یابیم، به راستی فرستادگان پروردگارمان حق را آوردند. و بهشتیان را ندا دهند که این همان بهشتی است که آن را به سزای کارهایی که انجام می‌دادید میراث شما ساخته‌اند.'),
(998, 7, 44, 'و بهشتیان دوزخیان را ندا می‌دهند که بی گمان ما آنچه را پروردگارمان به ما وعده داده بود راست و درست یافتیم، آیا شما نیز آنچه را پروردگارتان وعده داده بود راست و درست یافتید؟ می‌گویند: آری. آن گاه اعلام کننده‌ای در میان آن دو گروه اعلام می‌کند که لعنت خدا بر ستمکاران باد.'),
(999, 7, 45, 'همانان که از راه خدا روی برمی تابند و دیگران را نیز از آن بازمی دارند و کج آن را می‌خواهند و به سرای آخرت کافرند.'),
(1000, 7, 46, 'و میان بهشتیان و دوزخیان حایلی است، و بر بلندی‌های آن مردانی بلندمرتبه قرار دارند که هر یک از آن دو گروه را به نشانه شان می‌شناسند، و بهشتیان را که هنوز به بهشت درنیامده‌اند ولی امید آن را دارند، ندا می‌دهند که: سلام بر شما باد.'),
(1001, 7, 47, 'و چون دیدگانشان به سوی دوزخیان گردانده شود، می‌گویند: پروردگارا، ما را با این مردم ستمکار قرار مده.'),
(1002, 7, 48, 'و آنان که بر بلندای آن حجابند، مردان کفرپیشه‌ای را که به نشانه شان می‌شناسند ندا می‌دهند و می‌گویند: نه جمعیت و نیرویتان شما را کفایت کرد و نه تکبّر و گردنکشی هایتان.'),
(1003, 7, 49, 'آیا این مردم با ایمان همان کسانی اند که شما سوگند یاد کردید که خدا هیچ رحمت و خیری به آنان نمی‌رساند؟ ای مؤمنان، به بهشت درآیید که نه ترسی بر شما خواهد بود و نه اندوهگین خواهید شد.'),
(1004, 7, 50, 'و دوزخیان بهشتیان را ندا می‌دهند که از آب یا از آنچه خدا روزی شما کرده است بر ما فروریزید می‌گویند: خداوند آن دو را بر کافران حرام کرده است.'),
(1005, 7, 51, 'همانان که دینشان را سرگرمی و بازیچه گرفتند و زندگی دنیا آنان را فریب داد. پس امروز آنان را به فراموشی می‌سپریم و به آنان توجهی نمی‌کنیم، چرا که آنان دیدار امروزشان را به فراموشی سپردند و آنچه را باید برای امروز فراهم می‌آوردند فراهم نیاوردند، و نیز بدین سبب که آیات ما را انکار می‌کردند.'),
(1006, 7, 52, 'آری، تکذیب کنندگانِ آیات ما ستمکارترین مردمند، چرا که ما براساس دانش خود برای آنان کتابی آوردیم و آن را به روشنی بیان کردیم و آن راهنمای همگان و برای مردمی که ایمان می‌آورند مایه رحمت است.'),
(1007, 7, 53, 'آیا برای ایمان آوردن خود جز خاستگاه و مبنای معارف و حقایق قرآن را انتظار می‌برند؟ روزی که خاستگاه و مبانی آن با فرارسیدن قیامت آشکار شود، کسانی که پیش از این آن را به فراموشی سپرده بودند، می‌گویند: فرستادگان پروردگارمان حق را آوردند. اکنون آیا برای ما شفیعانی هست که ما را شفاعت کنند یا اینکه به دنیا بازگردانده شویم و کارهایی غیر از آنچه می‌کردیم انجام دهیم؟ آنان از آن رو چنین آرزویی دارند که درمی یابند به خود زیان زده‌اند و آنچه را به دروغ بافته بودند از آنان گم و ناپدید شده است.'),
(1008, 7, 54, 'به یقین، پروردگار شما که امورتان را تدبیر می‌کند خداست، همان که آسمان‌ها و زمین را در شش دوره و مرحله بیافرید، وانگهی بر تخت فرمانروایی عالم استیلا یافت. روز را به وسیله شب که شتابان آن را می‌طلبد، می‌پوشاند. و اوست که خورشید و ماه و ستارگان را ـ که همه به فرمان او رام گشته‌اند ـ پدید آورده است. آگاه باشید که آفرینش موجودات و تدبیر امور آنها کار اوست. پرخیر و برکت است خدا که پروردگار همه جهان هاست.'),
(1009, 7, 55, 'پروردگارتان را با فروتنی و در نهان بخوانید، اگر جز این کنید تجاوزکارید و او تجاوزکاران را دوست نمی‌دارد.'),
(1010, 7, 56, 'و در زمین پس از آن که سامان یافته است تباهی نکنید، و خدا را از روی بیم و امید بخوانید که در آن صورت از نیکوکاران خواهید بود، و رحمت خدا به نیکوکاران نزدیک است.'),
(1011, 7, 57, 'و اوست آن که بادها را پیشاپیش باران که برخاسته از رحمت اوست نویددهنده می‌فرستد، تا چون ابرهای گرانبار را برداشتند، آنها را به سوی سرزمینی مرده و بی گیاه روانه می‌سازیم، پس به وسیله آنها آب فرو می‌فرستیم و به وسیله آن از هرگونه محصولی برمی آوریم. مردگان را نیز در روز قیامت این گونه از گورها زنده بیرون می‌آوریم. [ما حیات مجدّد گیاهان را بیان کردیم،] باشد که دریابند که زنده شدن دوباره مردگان امری شدنی است.'),
(1012, 7, 58, 'و سرزمین پاک (حاصلخیز) گیاهش به اذن پروردگارش می‌روید، و سرزمینی که پلید شده است گیاهش جز اندکی برنمی آید. این گونه آیات و نشانه‌ها را به سوی اندیشه‌ها می‌گردانیم و تنها گروهی که خدا را سپاسگزاری می‌کنند از آنها بهره مند می‌شوند.'),
(1013, 7, 59, 'به یقین، نوح را به سوی قومش به رسالت فرستادیم. او گفت: ای قوم من، خدا را بپرستید که شما را معبودی جز او نیست. من بر شما از عذاب روز قیامت که روزی بزرگ است می‌ترسم.'),
(1014, 7, 60, 'بزرگان و اشراف قومش به او گفتند: بی گمان ما تو را در گمراهی آشکاری می‌بینیم.'),
(1015, 7, 61, 'نوح گفت: ای قوم من، هیچ گونه گمراهی بر من نیست، بلکه من پیامبری از جانب پروردگار جهان‌ها هستم.'),
(1016, 7, 62, 'پیام‌های پروردگارم را به شما می‌رسانم و شما را نصیحت می‌کنم و از جانب خدا چیزهایی می‌دانم که شما نمی‌دانید.'),
(1017, 7, 63, 'و آیا تعجّب کرده‌اید که برای شما از جانب پروردگارتان مایه تذکاری آمده و آن بر مردی از خودتان وحی شده است تا شما را از عذاب الهی بیم دهد و تا شما تقوا پیشه کنید و باشد که مورد رحمت قرار گیرید؟'),
(1018, 7, 64, 'ولی او را دروغ پرداز شمردند و ما عذاب را برای آنان مقرّر کردیم، پس نوح و کسانی را که با او در کشتی بودند نجات دادیم و کسانی را که آیات ما را دروغ انگاشتند غرق کردیم، زیرا آنان مردمی کوردل بودند.'),
(1019, 7, 65, 'و به سوی قوم عاد برادرشان هود را به رسالت فرستادیم. گفت: ای قوم من، خدا را بپرستید که شما را معبودی جز او نیست، پس آیا پروا نمی‌کنید و از خدا نمی‌ترسید؟'),
(1020, 7, 66, 'از میان بزرگان و اشراف قومش آنان که کفر ورزیدند گفتند: بی گمان، ما تو را در بی خردی می‌بینیم و قطعاً تو را از دروغگویان می‌پنداریم.'),
(1021, 7, 67, 'هود گفت: ای قوم من، هیچ گونه بی خردی بر من نیست، بلکه من پیامبری از جانب پروردگار جهان‌ها هستم.'),
(1022, 7, 68, 'پیام‌های پروردگارم را به شما می‌رسانم و من ـ برخلاف پندارتان ـ برای شما خیرخواهی امینم.'),
(1023, 7, 69, 'و آیا تعجّب کرده‌اید که برای شما از جانب پروردگارتان برای شما مایه تذکاری آمده و آن بر مردی از خودتان وحی شده است تا شما را از عذاب الهی بیم دهد؟ و به خاطر آورید زمانی را که خداوند شما را پس از قوم نوح جانشین آنان قرار داد و شما را در آفرینش پیکرهایتان فزونی و گسترش بخشید. پس نعمت‌های خدا را به یاد داشته باشید، باشد که [یکتایی او را باور کنید و به پرستش او روی آورید تا] نیکبخت شوید.'),
(1024, 7, 70, 'گفتند: آیا به سوی ما آمده‌ای که تنها خدا را بپرستیم و خدایانی را که پدرانمان می‌پرستیدند رها سازیم؟ ما چنین نخواهیم کرد، اگر از راستگویانی، عذابی را که به آن وعده مان می‌دهی برای ما بیاور.'),
(1025, 7, 71, 'هود گفت: بی گمان بر شما به سبب اصرارتان بر پرستش بت‌ها، عذاب و خشمی از جانب پروردگارتان مقرر شده است. آیا با من درباره خدایانی که شما و پدرانتان بر آنها نام خدا نهاده‌اید و آنها از حقیقت آن اندک بهره‌ای ندارند مجادله می‌کنید؟ خداوند هیچ برهانی بر خدا بودن آنها فرو نفرستاده است، پس در انتظار عذاب باشید که من نیز با شما از منتظرانم.'),
(1026, 7, 72, 'پس عذاب الهی فرا رسید و ما هود و کسانی را که با او بودند به رحمتی از جانب خود نجات بخشیدیم و کسانی را که نشانه‌های ما را دروغ انگاشتند و مؤمن نبودند، به هلاکت رساندیم و نسلشان را برانداختیم.'),
(1027, 7, 73, 'و به سوی قوم ثمود برادرشان صالح را به رسالت فرستادیم. گفت: ای قوم من، خدا را بپرستید که شما را معبودی جز او نیست. همانا برای شما از جانب پروردگارتان نشانه‌ای روشن آمده است. این ماده شتری است که خدا آن را به اعجاز پدید آورده و برای شما نشانه‌ای بر پیامبری من است، آن را آزاد بگذارید که از زمین خدا بخورد و گزندی به آن نرسانید که عذابی دردناک شما را فرو خواهد گرفت.'),
(1028, 7, 74, 'و یاد کنید زمانی را که خدا شما را پس از قوم عاد جانشین آنان گردانید و شما را در سرزمینشان سکونت داد و شما در نقاط هموار آن قصرهایی ساختید و از کوه‌ها خانه‌هایی تراشیدید، پس نعمت‌های خدا را یاد کنید و در زمین به فساد و تباهی برنخیزید.'),
(1029, 7, 75, 'اشراف قوم او که تکبّر ورزیدند و رسالت او را نپذیرفتند، به ناتوان شمرده شدگانِ قوم خود که ایمان آورده بودند گفتند: آیا می‌دانید که صالح از جانب پروردگارش به رسالت فرستاده شده است؟ گفتند: بی گمان، ما به آنچه او بدان فرستاده شده است ایمان داریم.'),
(1030, 7, 76, 'آن مستکبران که رسالت او را نپذیرفتند گفتند: بی تردید، ما به آنچه شما بدان ایمان آورده‌اید کافریم.'),
(1031, 7, 77, 'پس آن ماده شتر را کشتند و متکبّرانه از فرمان پروردگارشان سرپیچی کردند و گفتند: ای صالح، اگر از پیامبرانی، آن عذابی که به ما وعده می‌دهی برای ما بیاور.'),
(1032, 7, 78, 'پس لرزش آنان را فراگرفت و در پی آن، در خانه هایشان به زانو درآمده و با چهره بر زمین افتادند و هلاک شدند.'),
(1033, 7, 79, 'سپس صالح از آنان روی برگردانید و گفت: ای قوم من، به راستی من پیام پروردگارم را به شما رساندم و شما را پند دادم ولی شما پنددهندگان را دوست نمی‌دارید.'),
(1034, 7, 80, 'و از لوط که او را به رسالت فرستادیم یاد کن، آن گاه که به قوم خود گفت: آیا به این کار زشت و پلید می‌پردازید که از میان همه آدمیان هیچ کس در آن بر شما سبقت نگرفته است؟'),
(1035, 7, 81, 'آیا شما از روی شهوت، به جای زنان به سوی مردان می‌روید و با آنان درمی آمیزید؟! بلکه شما مردمی تجاوزکارید.'),
(1036, 7, 82, 'ولی پاسخ قومش جز این نبود که گفتند: لوط و پیروانش را از شهرتان بیرون کنید، آنان مردمی هستند که از کردار شما دوری می‌کنند و پاکی می­ورزند.'),
(1037, 7, 83, 'پس او و خانواده اش را که تنها ایمان آورندگان به او بودند نجات دادیم، بجز همسرش را که از ماندگاران در عذاب بود.'),
(1038, 7, 84, 'و بر آنان عذابی سخت باراندیم، پس بنگر که فرجام گنهکاران چگونه بود.'),
(1039, 7, 85, 'و به سوی مردم مدین، برادرشان شعیب را به رسالت فرستادیم. گفت: ای قوم من، خدا را بپرستید که شما را معبودی جز او نیست. برای شما از جانب پروردگارتان دلیلی روشن بر پیامبری من آمده است، پس حق پیمانه و ترازو را به طور کامل ادا کنید و هنگام وزن کردن و پیمانه نمودن کالاهای مردم از مقدار آن مکاهید، و در زمین پس از آن که سامان یافته است تباهی نکنید، اگر از حق باوری بهره مند باشید، این حقیقت را به روشنی درمی یابید که سنجش درست کالاها و پرداخت کامل آنها و پرهیز از فساد برای شما بهتر است.'),
(1040, 7, 86, 'و بر سر هر راهی منشینید تا کسی را که به خدا ایمان آورده است تهدید کنید و او را از راه خدا بازدارید در حالی که خود کج آن را می‌طلبید، و یاد کنید زمانی را که مردمی اندک بودید و خدا شما را بسیار گردانید، و بنگرید که فرجام فسادگران چگونه بود.'),
(1041, 7, 87, 'و اگر گروهی از شما به آنچه بدان به رسالت فرستاده شده‌ام ایمان آورده و گروهی ایمان نیاورده‌اند، همگان شکیبایی کنید، کافرانتان صبر پیشه سازند و به اقدامی نسنجیده دست نیازند و مؤمنانتان بر ایمانشان شکیبایی کنند و مضطرب و نگران نباشند تا این که خدا میان ما داوری کند که او بهترین داوران است.'),
(1042, 7, 88, 'از میان اشراف قومش آنان که تکبّر ورزیده و ایمان نیاورده بودند گفتند: ای شعیب، قطعاً تو و کسانی را که ایمان آورده و با تواَند از شهرمان بیرون می‌کنیم، یا این که به کیش ما بازگردید. گفت: آیا هر چند ناخشنود باشیم، باز هم باید به کیش شما درآییم؟'),
(1043, 7, 89, 'ما آیین شما را نمی‌پذیریم، چرا که اگر به آیین شما بازگردیم بعد از این که خدا ما را از آن رهایی داده است قطعاً به خدا دروغ بسته‌ایم. ما را نشاید که به آن بازگردیم مگر این که خدا که تدبیرکننده امور ماست عنایت خود را از ما برگیرد و ما به شرک روی بیاوریم، زیرا دانش پروردگار ما همه چیز را فراگرفته و اوست که می‌داند تا چه زمانی ما درخورِ یکتاپرستی هستیم. ما بر خدا توکّل کرده و امر خود را به او وانهاده‌ایم. پروردگارا میان ما و قوم ما که کافر شده‌اند به حق داوری کن و حق گریزان را نابود کن که تو بهترین داورانی.'),
(1044, 7, 90, 'و از میان اشراف قومش آنان که کفر ورزیده بودند به مؤمنان گفتند: اگر از شعیب پیروی کنید، قطعاً شما در آن صورت زیانکار خواهید بود.'),
(1045, 7, 91, 'آن گاه لرزش آنان را فراگرفت و در خانه هایشان به زانو درآمدند و با چهره بر زمین افتادند و هلاک شدند.'),
(1046, 7, 92, 'کسانی که شعیب را دروغگو شمردند چنان نابود شدند که گویی در دیارشان اقامت نداشتند. کسانی که شعیب را دروغگو انگاشتند آنانند زیانکاران.'),
(1047, 7, 93, 'پس از آن که عذاب به سراغ کافران مدین آمد، شعیب از آنان روی برتافت و گفت: ای قوم من، به راستی من پیام‌های پروردگارم را به شما رساندم و شما را پند دادم، از این رو بر شما اندوه نمی‌خورم. چگونه بر مردمی که کافر شدند اندوه خورم؟'),
(1048, 7, 94, 'و ما در هیچ شهری پیامبری نفرستادیم مگر این که مردم آن جا را به سختی و گزند دچار کردیم، باشد که آنان در برابر خدا فروتنی کنند.'),
(1049, 7, 95, 'پس چون به راه راست نیامدند، به جای سختی، آسایش آوردیم تا این که فزونی یافتند (اموال و افرادشان بسیار شد) گفتند: سختی‌ها و آسایش‌ها بازیِ روزگار است نه آزمون الهی، به پدران ما نیز گزند و مایه سرور (رفاه و آسایش) رسیده است. پس ناگهان آنان را به عذاب فرو گرفتیم در حالی که آن را تصور نمی‌کردند.'),
(1050, 7, 96, 'و اگر مردم آن شهرها که به عذاب گرفتار شدند، ایمان آورده و تقوا پیشه کرده بودند، به یقین از آسمان و زمین برکت‌هایی بر آنان می‌گشودیم، ولی پیامبران و آیات ما را دروغ انگاشتند، ما نیز آنان را به عذابی که نمود گناهانشان بود گرفتار ساختیم.'),
(1051, 7, 97, 'با این حال، آیا مردم شهرها ایمن بودند از این که عذاب ما شبانه آن گاه که خفته‌اند به سراغشان آید؟ (۹۷'),
(1052, 7, 98, 'و آیا مردم شهرها ایمن بودند از این که عذاب ما آن گاه که آفتاب می‌گسترد، در حالی که به بازی روزانه مشغولند، به سراغشان آید؟'),
(1053, 7, 99, 'آیا آنان از مکر خدا ایمن بودند؟ جز مردم زیانکار از مکر خدا احساس ایمنی نمی‌کنند.'),
(1054, 7, 100, 'آیا فرجام شوم پیشینیان، کسانی را که زمین را پس از هلاکت ساکنانش به ارث می‌برند راه ننموده و برای آنان این حقیقت را آشکار نکرده است که اگر بخواهیم، آنان را به گناهانشان گرفتار می‌سازیم و بر دل هایشان مهر می‌نهیم، در نتیجه آنان سخن حق را نخواهند شنید؟'),
(1055, 7, 101, 'ما بخشی از خبرهای این شهرها را بر تو حکایت می‌کنیم، و به راستی پیامبرانشان معجزاتی را که بر حقّانیتشان دلالت داشت برای آنان آوردند ولی آنها ایمان نیاوردند، زیرا چنین نبودند که به چیزی که پیش‌تر آن را دروغ شمرده‌اند ایمان بیاورند. این گونه خداوند بر دل کافران مهر می‌نهد.'),
(1056, 7, 102, 'آنان با خدا پیمان بستند که او را بپرستند و برای او شریکی مپندارند، ولی برای بیشترشان پایبندی به هیچ عهدی نیافتیم، و بیشترشان را این گونه یافتیم که عهدشان را می‌شکنند.'),
(1057, 7, 103, 'سپس بعد از آن پیامبران، موسی را با نشانه‌های خود (معجزات) به سوی فرعون و اشرافیان دربارش به رسالت برانگیختیم ولی آنها را از روی ستم دروغ شمردند و انکار کردند، چرا که مردمی فسادگر بودند. پس بنگر فرجام فسادگران چگونه بود.'),
(1058, 7, 104, 'و موسی گفت: ای فرعون، همانا من پیامبری از سوی پروردگار جهان‌ها هستم.'),
(1059, 7, 105, 'تلاش من این است که جز سخن راست و درست به خدا نسبت ندهم و من سزاوار آنم. من برای شما از جانب پروردگارتان دلیلی روشن (معجزه‌ای آشکار) بر رسالت خود آورده‌ام، پس ای فرعون، بنی اسرائیل را رها کن تا همراه من رهسپار شوند.'),
(1060, 7, 106, 'فرعون گفت: اگر معجزه‌ای آورده‌ای، چنانچه در این ادّعا از راستگویانی، آن را بیاور و نشان بده.'),
(1061, 7, 107, 'پس موسی عصایش را افکند و ناگهان آن اژدهایی شد که هیچ شک و تردیدی در آن راه نداشت.'),
(1062, 7, 108, 'و دست خود را از گریبان بیرون کشید و ناگهان آن برای بینندگان سپید و درخشان گردید.'),
(1063, 7, 109, 'سران و بزرگان قوم فرعون در جلسه مشورتی گفتند: بی تردید، این شخص جادوگری دانا و زبردست است.‌'),
(1064, 7, 110, 'می‌خواهد با پشتیبانی بنی اسرائیل شما را از سرزمینتان بیرون کند. اینک برای مقابله با او چه برنامه‌ای دارید و چه دستوری می‌دهید؟'),
(1065, 7, 111, 'پس بر آن شدند که با سحر به مقابله با وی برخیزند، از این رو به فرعون گفتند: در مجازات او و برادرش هارون شتاب مکن و کارشان را به تأخیر انداز و گروهی را در شهرها برای جمع آوری جادوگران ماهر بسیج کن،'),
(1066, 7, 112, 'که آنان هر جادوگر ماهر و زبردستی را نزد تو می‌آورند.'),
(1067, 7, 113, 'چنین شد، و جادوگران نزد فرعون آمدند و گفتند: اگر ما بر موسی پیروز شویم حتماً پاداشی بزرگ خواهیم داشت.'),
(1068, 7, 114, 'فرعون گفت: آری، و علاوه بر این قطعاً از مقرّبان من خواهید بود.'),
(1069, 7, 115, 'جادوگران با اطمینان از پیروزی خود گفتند: ای موسی، یا تو نخست عصای خود را بیفکن یا ما نخستین کسی باشیم که جادوی خود را می‌افکنیم.'),
(1070, 7, 116, 'موسی گفت: شما بیفکنید. پس هنگامی که عصاها و ریسمان‌های خود را افکندند، دیدگان مردم را افسون کردند و آنان را به وحشت انداختند و جادویی بزرگ به میان آوردند.'),
(1071, 7, 117, 'و ما به موسی وحی کردیم که عصایت را بینداز، و او چنین کرد، پس ناگهان آن اژدهایی شد و آنچه را به دروغ می‌ساختند به سرعت در کام خود فرو می‌برد.'),
(1072, 7, 118, 'پس حق پای گرفت و افسونی که ساختند تباه شد.'),
(1073, 7, 119, 'آن گاه فرعون و یارانش در آن جا مغلوب شدند و خوار و بی مقدار بازگشتند.'),
(1074, 7, 120, 'و جادوگران بی اختیار به سجده درافتادند.'),
(1075, 7, 121, 'گفتند: ما به پروردگار جهان‌ها ایمان آوردیم،'),
(1076, 7, 122, 'همان پروردگار موسی و هارون.'),
(1077, 7, 123, 'فرعون گفت: پیش از آن که به شما رخصت دهم به او ایمان آوردید. قطعاً این نیرنگی است که آن را در این شهر پرداخته‌اید تا مردمش را از آن جا بیرون کنید، پس به زودی کیفر این کارتان را خواهید دانست.'),
(1078, 7, 124, 'سوگند یاد می‌کنم که دست و پای شما را یکی از راست و دیگری از چپ به شدّت خواهم برید، سپس همه شما را در تنه‌های درختان خرما به صلیب خواهم کشید.'),
(1079, 7, 125, 'ساحران گفتند: بدین وسیله به سوی پروردگارمان باز خواهیم گشت و به لقای او خواهیم رسید.'),
(1080, 7, 126, 'و تو از ما ناخشنود نیستی و با ما هیچ دشمنی و کینه‌ای نداری جز به سبب این که چون نشانه‌های پروردگارمان برای ما پدیدار شد، آنها را باور کردیم. پروردگارا، بر ما شکیبایی فرو ریز و ما را در حالی که تسلیم تو هستیم بمیران.'),
(1081, 7, 127, 'و اشراف قوم فرعون به او گفتند: آیا موسی و قومش را وامی گذاری تا در زمین تباهی به بار آورند و موسی تو و خدایانت را رها کند؟ گفت: به زودی ـ همچون گذشته ـ پسرانشان را می‌کشیم و زنانشان را برای خدمتگزاری زنده می‌داریم، و بی تردید ما بر آنان برتری داریم و بر آنان چیره‌ایم.'),
(1082, 7, 128, 'موسی به قوم خود گفت: از خدا یاری بجویید و بر دشواری‌ها شکیبا باشید که این راه و رسم تقواپیشگی است. زمینی را که در دست فرعونیان می‌بینید از آنِ خداست نه فرعون. خدا آن را به هر کس از بندگانش که بخواهد به میراث خواهد داد و فرجام نیکو از آنِ تقواپیشگان است.'),
(1083, 7, 129, 'قوم موسی به او گفتند: هم پیش از آن که نزد ما بیایی و هم بعد از آن که نزد ما آمدی مورد آزار قرار گرفتیم. گفت: امید است پروردگارتان دشمن شما را نابود کند و شما را در این سرزمین جانشین گرداند تا شما را بیازماید و بنگرد که چگونه رفتار می‌کنید.'),
(1084, 7, 130, 'و به راستی خاندان فرعون (قبطیان) را به خشکسالی‌ها و قحطی‌هایی چند و کمبود محصولات گرفتار ساختیم، باشد که متذکر شوند و حقیقت را دریابند.'),
(1085, 7, 131, 'پس هنگامی که نیکی (وفور نعمت و گشایش روزی) به آنان روی می‌آورْد می‌گفتند: این ویژه ماست، و اگر بدی (قحطی و کمبود) به آنان می‌رسید، به موسی و کسانی که با او بودند فال بد می‌زدند. بدانند که عذابشان که برای آنان مایه بدشگونی است فقط نزد خداست و از بین نمی‌رود، ولی بیشترشان این حقیقت را نمی‌دانند.'),
(1086, 7, 132, 'و به موسی گفتند: هر افسونی را که به گمان خود معجزه می‌پنداری برای ما بیاوری تا ما را به وسیله آن جادو کنی ما به تو ایمان نخواهیم آورد.'),
(1087, 7, 133, 'پس بر آنان طوفان آب و ملخ‌ها و کَنَه‌ها و غوک‌ها و خون را که نشانه‌هایی روشن بودند و جدای از یکدیگر رخ می‌نمودند فرستادیم، ولی آنان تکبّر ورزیدند و به موسی ایمان نیاوردند و قومی گناه پیشه بودند.'),
(1088, 7, 134, 'و هرگاه یکی از آن عذاب‌ها بر آنان فرود می‌آمد می‌گفتند: ای موسی، به پیمانی که پروردگارت نزد تو دارد که دعایت را مستجاب می‌کند، برای ما از او درخواست کن. قطعاً اگر این عذاب را از ما برداری به تو ایمان می‌آوریم و بنی اسرائیل را رها می‌کنیم تا با تو رهسپار شوند.'),
(1089, 7, 135, 'و چون عذاب را از آنان برمی داشتیم و آنان به پایان مهلتی که برای وفا به پیمانشان تعیین شده بود می‌رسیدند، بی آن که انتظار رود پیمان خود را می‌شکستند.'),
(1090, 7, 136, 'پس، از آنان انتقام گرفتیم و آنان را در دریا غرق کردیم بدین سبب که آیات ما را دروغ انگاشتند و از آنها غفلت ورزیدند.'),
(1091, 7, 137, 'و به آن مردمی که پیوسته ضعیف و ناتوان نگاه داشته می‌شدند، مشرق‌ها و مغرب‌های سرزمین قدس را که در آن برکت بخشیده بودیم، به میراث دادیم، و قضا و وعده نیکوی پروردگارت برای بنی اسرائیل به پاس صبری که پیشه کردند، تحقق یافت، و قصرها و کاخ‌هایی را که فرعون و قومش ساخته بودند و باغ‌هایی را که با داربست‌ها برافراشته بودند، ویران کردیم.'),
(1092, 7, 138, 'و بنی اسرائیل را از آن دریا عبور دادیم، پس بر مردمی گذر کردند که به بت‌هایی که داشتند روی آورده بودند و به بزرگداشت آنها می‌پرداختند. گفتند: ای موسی، برای ما نیز معبودی قرار ده همان گونه که برای آنان معبودانی قابل رؤیت است. موسی گفت: بی تردید شما مردمی هستید که نادانی می‌کنید.'),
(1093, 7, 139, 'به یقین راهی که آنان در آن قرار دارند نابود است و عباداتی که به جای آورده‌اند تباه و بی ثمر است.'),
(1094, 7, 140, 'گفت: آیا غیر از خدا معبودی برای شما بجویم در حالی که او شما را با آیینی درست و دادن آن معجزات بزرگ بر جهانیان برتری داده است؟'),
(1095, 7, 141, 'و یاد کنید زمانی را که شما را از فرعونیان رهایی بخشیدیم. آنان پیوسته عذابی ناگوار بر شما تحمیل می‌کردند، پسرانتان را می‌کشتند تا نسل شما را از بین ببرند و زنانتان را برای کنیزی و خدمتگزاری زنده می‌گذاشتند، و در آن ماجرا از جانب پروردگارتان آزمونی بزرگ بود.'),
(1096, 7, 142, 'و با موسی سی شب وعده نهادیم که در آن به عبادت و مناجات بپردازد، و آن را با ده شب دیگر کامل ساختیم. پس موعد مقرر پروردگارش به چهل شب کامل شد، و موسی چون خواست به میعاد آید به برادرش هارون گفت: در میان قوم من جانشینم باش و کارها را اصلاح کن و در اداره امور راه و روشی را که فسادگران می‌خواهند پیروی مکن.'),
(1097, 7, 143, 'و چون موسی در موعدی که برای او مقرر کرده بودیم آمد و پروردگارش با او سخن گفت، گفت: پروردگارا، خودت را به من بنمای تا به تو بنگرم. پروردگارش گفت: هرگز مرا نخواهی دید، ولی به این کوه بنگر، اگر پس از آن که من بر آن ظاهر شدم، برجای خود ماند، مرا خواهی دید، و آن گاه که پروردگارش بر آن کوه جلوه کرد، به جلوه اش کوه را از هم پاشید و موسی بیهوش بر زمین افتاد و چون به هوش آمد گفت: پروردگارا، منزهی تو! به سوی تو بازآمدم و توبه نمودم و من از میان کسانی که ایمان دارند که تو دیده نمی‌شوی، نخستین نفرم.'),
(1098, 7, 144, 'و خدا گفت: ای موسی، من با پیام‌های خود که به سویت فرستادم و به سخن گفتنم با تو، تو را برگزیدم و به این موهبت تو را بر مردم برتری بخشیدم، پس آنچه را به تو داده‌ام بگیر (معارف آن را فراگیر و به احکام آن پایبند باش) و از سپاسگزاران باش.'),
(1099, 7, 145, 'و برای موسی در آن لوح‌ها (لوح‌های تورات) پندهایی که گزینش کرده بودیم نوشتیم و نیز شرح و تفصیل آنچه را که قومش در ایمان و عمل بدان نیاز داشتند در آن نگاشتیم، پس ای موسی آنها را به جدّ و جهد بگیر و به قومت فرمان ده که در میان آنها بهترینش را بگیرند، و اگر چنین نکنند از راه درست بیرون شده‌اند، و دیری نمی‌گذرد که سرای کسانی را که از راه درست بیرون شده‌اند به شما می‌نمایانم.'),
(1100, 7, 146, 'به زودی کسانی را که در زمین به ناحق گردنفرازی می‌کنند (خود را از مردم برتر می‌شمرند)، از توجّه و گرایش به آیات خود باز می‌دارم، و نیز کسانی را که اگر هر نشانه‌ای از حق ببینند به آن ایمان نمی‌آورند و اگر راه درست را بنگرند آن را راه خود برنمی گزینند و اگر راه نادرست را ببینند آن راه را پیش می‌گیرند. این بدان سبب است که آنان پیش‌تر آیات ما را دروغ می‌انگاشتند و از آنها غفلت میورزیدند.'),
(1101, 7, 147, 'و کسانی که آیات ما و دیدار سرای آخرت را دروغ پنداشتند اعمال نیکشان تباه شده و پاداشی نخواهند داشت. آیا جز به کارهای نیکی که کرده‌اند پاداش داده می‌شوند؟ ولی برای آنان کار نیکی نمانده است.'),
(1102, 7, 148, 'و قوم موسی پس از رفتن موسی به میعاد پروردگار، از زیورآلات خود تندیس بی جان گوساله‌ای که بانگ گوساله داشت ساختند و به عبادت آن پرداختند. آیا ندیدند که آن تندیس با آنان سخن نمی‌گوید و به راهی هدایتشان نمی‌کند؟ آری آن را به خدایی گرفتند در حالی که ستمکار بودند و هیچ گونه عذر و توجیهی برای آن نداشتند.'),
(1103, 7, 149, 'و چون نادرستی کارشان ـ پس از بازگشت موسی ـ به دستشان افتاد و پشیمانی در آنان آشکار گردید و دیدند که واقعاً گمراه شده‌اند، گفتند: اگر پروردگارمان بر ما رحمت نیاورد و ما را نبخشاید قطعاً از زیانکاران خواهیم بود.'),
(1104, 7, 150, 'و چون موسی خشمگین و اندوهناک به سوی قوم خود بازگشت به آنان گفت: پس از رفتنم مرا بد جانشینی کردید. آیا می‌خواستید کار پروردگارتان (فرو فرستادن تورات) پیش از زمانش تحقق یابد؟ آن گاه صحیفه‌های تورات را افکند و سر برادرش هارون را با چنگ زدن به موهایش گرفت و او را به سوی خود می‌کشید. هارون گفت: ای پسر مادرم، این قوم مرا به ناتوانی کشاندند و نزدیک بود مرا به خاطر مخالفت با گوساله پرستی بکشند. پس دشمنان را با نکوهش من شادمان مساز، و مرا در مخالفت با خود همراه این ستمکاران مشمار.'),
(1105, 7, 151, 'موسی برادرش را معذور دانست و گفت: پروردگارا، من و برادرم را ببخشای و ما را به رحمت خویش درآور که تو مهربانترین مهربانانی.'),
(1106, 7, 152, 'به یقین، کسانی که تندیس گوساله را به خدایی گرفتند، به زودی خشمی شدید از جانب پروردگارشان و ذلّتی فراگیر در زندگی دنیا به آنان خواهد رسید. و ما کسانی را که به خدا دروغ می‌بندند این گونه کیفر می‌دهیم.'),
(1107, 7, 153, 'و کسانی که کارهای ناروا کردند، سپس بعد از آن توبه نمودند و ایمان آوردند، قطعاً پروردگارت پس از توبه آنان آمرزنده گناهانشان است و بر آنان رحمت می‌آورد.'),
(1108, 7, 154, 'و آن گاه که خشم موسی فرو نشست، صحیفه‌های تورات را برگرفت، و در نوشته آنها رهنمود و رحمتی است برای کسانی که از پروردگارشان بیم دارند.'),
(1109, 7, 155, 'و موسی از میان قومش هفتاد مرد را برگزید تا همراه او در آن زمانی که با او وعده نهاده بودیم، به کوه طور آیند. آنان با درخواست رؤیت خدا مرتکب ستمی بزرگ شدند و به سزای آن زمین لرزه‌ای برخاسته از صاعقه آسمانی آنان را بلرزاند و هلاکشان کرد. هنگامی که لرزش آنان را فراگرفت. موسی گفت: پروردگارا، اگر می‌خواستی، آنان را پیش‌تر به هلاکت می‌رساندی و نیز مرا. آیا ما را به کاری که بی خردانمان کرده‌اند نابود می‌کنی؟ این جز آزمون تو نیست که هر که را بخواهی به وسیله آن گمراه و هر که را بخواهی هدایت می‌کنی. تویی سرپرست ما، پس ما را ببخشای و بر ما رحمت آور و تویی بهترین آمرزندگان.'),
(1110, 7, 156, 'و برای ما در این دنیا و در جهان آخرت زندگی نیکویی مقرر فرما، زیرا ما به سوی تو باز آمده و راه تو را پیش گرفته‌ایم. خداوند در پاسخ موسی فرمود: عذابم را به هر که خواهم می‌رسانم ـ چنان که آن هفتاد تن را هلاک کردم ـ و رحمت من همه چیز را فراگرفته است، و به زودی رحمت خاص خود را که تأمین کننده نیکبختی دنیا و آخرت است برای کسانی مقرر می‌کنم که تقوا پیشه سازند و زکات بپردازند، و نیز برای کسانی که به آیات ما ایمان می‌آورند.'),
(1111, 7, 157, 'مؤمنان کسانی اند که از این فرستاده، پیامبرِ درس ناخوانده که اهل کتاب او را نزد خود با همین نام و نشان در تورات و انجیل نوشته می‌یابند، پیروی می‌کنند. می‌یابند که آنان را به کار پسندیده فرمان می‌دهد و از کار ناپسند بازشان می‌دارد و پاکیزه‌ها را برای آنان حلال و پلیدها را بر آنان حرام می‌کند، و بار گرانشان را از دوششان برمی دارد و بندها و زنجیرهایی را که بر آنان تحمیل شده است از آنان فرو می‌گذارد. پس کسانی که به او ایمان آورده و او را بزرگ داشته و یاری اش کرده‌اند و از قرآن، این کتاب روشنگری که از جانب خدا نازل شده و قرین او و گواه راستگویی اوست پیروی نموده‌اند آنان نیکبخت خواهند بود.'),
(1112, 7, 158, 'بگو: ای مردم، من فرستاده خدا به سوی همه شما هستم، خدایی که فرمانروایی آسمان‌ها و زمین از آنِ اوست. معبودی شایسته پرستش جز او نیست. زنده می‌کند و می‌میراند. پس به خدا و رسول او ـ آن پیامبر درس ناخوانده که خود به خدا و سخنان او ایمان دارد ـ ایمان بیاورید و از او پیروی کنید، باشد که به نیکبختی سرای آخرت راه یابید.'),
(1113, 7, 159, 'و از قوم موسی گروهی بودند که به وسیله حق مردم را راه می‌نمودند و به وسیله آن میان خویش به عدالت رفتار می‌کردند.'),
(1114, 7, 160, 'و بنی اسرائیل را به دوازده قبیله که هر یک امّتی بودند تقسیم کردیم، و به موسی آن گاه که قومش از او آب خواستند وحی نمودیم که با عصایت بر این سنگ بزن. و چون عصایش را بر آن زد، دوازده چشمه آب به تعداد قبایل بنی اسرائیل از آن جوشید که هر گروهی از آن قبایل آبشخور خود را شناخت، و ابرها را ـ آن گاه که در بیابانی سوزان در حرکت بودند ـ بر آنان سایه گستر کردیم و (مَنّ) و (سلوی) بر آنان فروفرستادیم و به آنان گفتیم: از روزی‌هایی که به شما داده‌ایم و همه خوب و پاکیزه‌اند بخورید. آنان [با سرکشی ها] بر ما ستم نکردند، بلکه بر خود ستم روا می‌داشتند.'),
(1115, 7, 161, 'و یاد کن هنگامی را که به آنان گفته شد: به این شهر درآیید و از نعمت‌های آن ـ هر جا که خواهید ـ به خوشی بخورید و بگویید: پروردگارا، گناهانمان را فرو ریز، و سجده کنان [= با فروتنی] وارد دروازه آن شهر شوید، ما هم گناهانتان را بر شما خواهیم بخشود و بر پاداش نیکوکاران نیز خواهیم افزود.'),
(1116, 7, 162, 'ولی کسانی از آنان که ستم کرده بودند، آن را به سخنی دیگر تغییر دادند سخنی جز آنچه به آنان گفته شده بود، پس بر آنان به سزای این که ستم می‌کردند، عذابی از آسمان فرستادیم.'),
(1117, 7, 163, 'ای پیامبر، از بنی اسرائیل درباره مردم آن شهری که در ساحل دریا بود بپرس، آن گاه که در روز شنبه به احکامِ آن روز تجاوز می‌کردند، آن گاه که ماهیانشان روز شنبه که می‌بایست صید را ترک کنند آشکارا به سویشان (در کنار دریا) می‌آمدند و در غیر آن روز که کار را تعطیل نمی‌کردند و صید ماهی برای آنان مباح بود نزدشان نمی‌آمدند. این گونه آنان را می‌آزمودیم به سبب این که خدا را نافرمانی می‌کردند.'),
(1118, 7, 164, 'و یاد کن زمانی را که گروهی از آن ساحل نشینان به گروهی دیگر که تجاوزکاران را از نافرمانی خدا نهی می‌کردند گفتند: برای چه مردمی را پند می‌دهید که خداوند آنان را نابود می‌کند یا به عذابی شدید گرفتارشان می‌سازد؟ گفتند: آنان را از نافرمانی خدا نهی می‌کنیم تا نزد پروردگارتان برای ما عذری باشد، و شاید بترسند و از گناه بپرهیزند.'),
(1119, 7, 165, 'پس چون گنهکاران آنچه را که بدان تذکر داده شده بودند به فراموشی سپردند، کسانی را که از گناه نهی می‌کردند نجات بخشیدیم، و کسانی را که ستم کردند، به سزای این که از اطاعت خدا خارج شدند، به عذابی ناگوار دچار کردیم.'),
(1120, 7, 166, 'آری، هنگامی که با شکستن مقررات روز شنبه از ترک آنچه از آن بازداشته می‌شدند سرپیچی کردند، به آنان گفتیم: بوزینگانی باشید مطرود و رانده شده.'),
(1121, 7, 167, 'و یاد کن زمانی را که پروردگارت اعلام کرد که قطعاً کسانی را برمی انگیزیم و بر آنان مسلط می‌کنیم که به آنان تا روز قیامت عذاب ناگوار بچشانند. به راستی پروردگارت برخی از گنهکاران را به سرعت کیفر می‌دهد، و به راستی او آمرزنده و مهربان است.'),
(1122, 7, 168, 'و آنان را در زمین به گروه‌هایی چند تقسیم کردیم. برخی از آنان شایسته و درستکارند و برخی از آنان جز این هستند، و ما آنان را به نعمت‌ها و خوشی‌ها و نیز به سختی‌ها و ناگواری‌ها آزمودیم، باشد که از راه نادرست بازگردند.'),
(1123, 7, 169, 'پس از آن شایستگان، جانشینانی کتاب آسمانی تورات را به ارث بردند و احکام و معارف آن را دریافت کردند، ولی کالای ناپایدار این دنیا را بی هیچ توجهی به حرام یا حلال بودن آن می‌گرفتند، و می‌گفتند: بخشوده خواهیم شد. البته این سخن را از روی توبه نمی‌گفتند، چرا که اگر کالایی مانند آن به ایشان می‌رسید باز هم آن را می‌گرفتند. آیا از آنان پیمانِ ثبت شده در آن کتابی که به میراث بردند گرفته نشد که درباره خدا چیزی جز حق نگویند؟ ولی با این که آن را فراگرفتند و آموختند، به آن پیمان وفا نکردند با این هدف که به دنیای خود برسند، در حالی که سرای آخرت برای کسانی که تقوا پیشه می‌سازند بهتر است، پس آیا درنمی یابید؟'),
(1124, 7, 170, 'و کسانی که به کتاب آسمانی چنگ می‌زنند و نماز را برپا داشته‌اند، اصلاحگران جوامع بشری هستند و آنان را پاداشی است و ما قطعاً پاداش اصلاحگران را تباه نخواهیم کرد.'),
(1125, 7, 171, 'و به بنی اسرائیل زمانی را یادآوری کن که آن کوه را از بن برکندیم و همچون سقفی آن را بر فرازشان قرار دادیم به گونه‌ای که پنداشتند بر سرشان فرو خواهد افتاد، آن گاه گفتیم: کتابی را که به شما داده‌ایم به جدّ و جهد بگیرید و احکام و معارفی را که در آن است به خاطر داشته باشید، باشد که تقوا پیشه­سازید.'),
(1126, 7, 172, 'و یادآور زمانی را که پروردگارت از فرزندان آدم، از پشتشان، ذریه آنان را گرفت و آنان را بر خودشان گواه ساخت که آیا من پروردگار شما نیستم؟ گفتند: چرا، تویی پروردگار ما و ما حقیقتی جز این که مملوک تو باشیم نداریم، ما این را دیده‌ایم و بر آن گواهیم. چنین کردیم تا روز قیامت نگویید ما از این حقیقت بی خبر بودیم.'),
(1127, 7, 173, 'آری ربوبیت خود را نه تنها به پدرانتان بلکه به شما نیز نشان دادیم تا روز قیامت نگویید: جز این نیست که پدرانمان پیش از ما شرک ورزیدند و ما پس از آنان فرزندان ایشان بودیم و اعتقادی جز شرک نمی‌شناختیم، پس آیا ما را به آنچه آن باطل گرایان انجام دادند به هلاکت می‌رسانی [به آتش دوزخ عذاب می‌کنی]؟'),
(1128, 7, 174, 'و بدین سان نشانه‌های خود را به تفصیل بیان می‌کنیم تا آنچه باید، تحقق یابد و باشد که آنان به حق بازآیند.'),
(1129, 7, 175, 'و برای بنی اسرائیل بخوان خبر آن کس را که ما نشانه‌های خود را به او دادیم و راه معرفت حق برای او آشکار شد ولی او خود را از آنها به درآورد و شیطان از پی او روان گردید و در نتیجه او از گمراهان گشت.'),
(1130, 7, 176, 'و اگر می‌خواستیم، با نشانه‌هایی که به او نمایاندیم وی را به جایگاهی رفیع بالا می‌بردیم و به قرب خویش می‌رساندیم، ولی نخواستیم، زیرا او به زمین چسبید و به لذت‌های دنیا گرایش یافت و از هوای نفس خود پیروی کرد. پس حکایت او مانند حکایت سگ تشنه است که اگر بر او هجوم بری له له می‌زند، و اگر رهایش کنی باز هم له له می‌زند. این حکایت مردمی است که آیات ما را دروغ انگاشتند. پس این داستان را برای آنان بخوان، باشد که بیندیشند.'),
(1131, 7, 177, 'مردمی که آیات ما را دروغ شمرده‌اند داستان بدی دارند و با این کارشان بر خود ستم کرده‌اند.'),
(1132, 7, 178, 'هر که را خدا هدایت کند او حقاً هدایت یافته است، و کسانی را که به گمراهی می‌افکند واقعاً زیانکارند.'),
(1133, 7, 179, 'و قطعاً بسیاری از جنیان و آدمیان را برای دوزخ آفریده‌ایم، چرا که آنان را دل‌هایی است که به آنها حقیقت را درنمی یابند و دیدگانی دارند که به آنها آیات حق را نمی‌بینند و برایشان گوش‌هایی است که به آنها سخن حق را نمی‌شنوند. آنان همانند دام‌ها هستند بلکه از آنها گمراه ترند. آنانند بی خبران.'),
(1134, 7, 180, 'نیکوترین نام‌ها که بیانگر کمال مطلق و پیراستگی از هر کاستی است از آنِ خداوند است، پس او را به این نام‌ها بخوانید و بپرستید، و کسانی را که در مورد نام‌های خدا به بیراهه می‌روند و او را به نام و صفت آفریده‌ها می‌شناسند یا غیر او را به نام و صفت او تصور می‌کنند، واگذارید. به زودی به آنچه کرده‌اند جزا داده می‌شوند.'),
(1135, 7, 181, 'از میان کسانی که آفریده‌ایم گروهی هستند که به وسیله حق مردم را هدایت می‌کنند و به وسیله آن میان مردم به عدالت رفتار می‌نمایند.'),
(1136, 7, 182, 'و کسانی که آیات و نشانه‌های ما را دروغ انگاشته‌اند، به تدریج و از جایی که نمی‌دانند آنان را به تباهی و هلاکت نزدیک می‌کنیم.'),
(1137, 7, 183, 'و به آنان مهلت می‌دهم و این کید من با آنان است، به راستی کید من استوار است.'),
(1138, 7, 184, 'آیا نیندیشیده‌اند که همنشینشان محمد هیچ جنونی ندارد؟ آری هیچ جنونی دامنگیر پیامبر نیست او جز پیامبری هشداردهنده که هشدارهایش را به روشنی بیان می‌کند، نیست.'),
(1139, 7, 185, 'آیا در ملکوت آسمان‌ها و زمین (وابستگی آنها را به خدا) و در ملکوت آنچه خدا آفریده است ننگریسته و در این که شاید پایان مهلتشان نزدیک شده باشد نیندیشیده‌اند؟ آنان پس از قرآن که معجزه‌ای آشکار است به کدامین سخن ایمان می‌آورند؟'),
(1140, 7, 186, 'آنان ایمان نمی‌آورند، زیرا هر که را خدا به سزای کردارش به بیراهه افکند او را هدایتگری نیست. خدا آنان را وامی نهد که در طغیانشان سرگردان بمانند.'),
(1141, 7, 187, 'از تو درباره قیامت می‌پرسند که وقوع آن چه زمانی خواهد بود؟ بگو: علم آن تنها نزد پروردگار من است. آن گاه که وقتش فرا رسد جز او آن را پدیدار نمی‌سازد. قیامت بر آسمان‌ها و زمین گران و سنگین آمده است، چرا که ظهورش نابودی آنها را در پی دارد، جز ناگهان به سراغتان نمی‌آید. مکرر از تو زمان وقوع آن را می‌پرسند، گویی تو از آن باخبری. بگو: علم آن تنها نزد خداست، ولی بیشتر مردم این حقیقت را نمی‌دانند.'),
(1142, 7, 188, 'ای پیامبر، بگو: من برای خود مالک هیچ سودی نیستم تا آن را به سوی خود جلب کنم و مالک هیچ زیانی نیز نیستم تا آن را از خود بازدارم، مگر آنچه خدا برای من بخواهد. و اگر غیب می‌دانستم، خیر و نیکی بسیاری برای خود گرد می‌آوردم و بدی به من نمی‌رسید. من جز هشداردهنده و نویدرسان نیستم.'),
(1143, 7, 189, 'خداست آن که شما را از یک تن آفرید و همسرش را از نوع او قرار داد تا در کنارش بیارامد، پس چون با وی درآمیخت، همسرش به نطفه او که باری سبک بود باردار شد و مدتی را با آن سپری کرد تا آن که جنینی سنگین شد، و چون بدان گرانبار گشت، هر دو تن (پدر و مادر) خداوند، پروردگارشان را خواندند و با او چنین پیمان بستند که اگر فرزندی سالم و درخورِ بقا به ما عطا کنی قطعاً از سپاسگزاران تو خواهیم بود.'),
(1144, 7, 190, 'پس هنگامی که به آن دو فرزندی شایسته عطا کرد، برای خدا در آنچه به آنان داده بود شریکانی قرار دادند، پس خداوند برتر از آن است که آدمیان به او شرک ورزند.'),
(1145, 7, 191, 'آیا بت‌هایی را شریک خدا قرار می‌دهند که چیزی نمی‌آفرینند و خودشان آفریده می‌شوند (پدیدآورنده‌ای دارند)؟'),
(1146, 7, 192, 'و نه بر یاری پرستشگران خود توانایی دارند و نه می‌توانند خویشتن را یاری کنند.'),
(1147, 7, 193, 'و اگر آنها را به راه راست فراخوانید از شما پیروی نمی‌کنند. برای شما یکسان است، چه آنها را برای نیازهای خود بخوانید یا خاموش باشید.'),
(1148, 7, 194, 'کسانی را که به جای خدا می‌خوانید بندگانی مانند خود شما هستند، پس آنها را بخوانید و خواسته‌های خود را از آنها بخواهید، اگر راست می‌گویید که بر انجام کارهایی توانایند باید پاسخ شما را بدهند و خواسته شما را اجابت کنند.'),
(1149, 7, 195, 'آیا آنها پاهایی دارند که با آن راه روند، یا دست‌هایی دارند که با آن بگیرند و بر چیزی تسلط یابند، یا دیدگانی دارند که با آن بنگرند، یا گوش‌هایی دارند که با آن بشنوند؟ ای پیامبر، به مشرکان بگو: معبودان خود را که شریک خدا می‌پندارید به یاری فراخوانید آن گاه بر ضد من نیرنگ کنید و مرا مهلت ندهید.'),
(1150, 7, 196, 'ولی بدانید که نیرنگ شما بی ثمر است، زیرا خداوند که این کتاب را فرو فرستاده یاور من است و مرا از شرّ شما در امان می‌دارد، و او بندگان شایسته خود را سرپرستی می‌کند و یاری خود را از آنان دریغ نمی‌دارد.'),
(1151, 7, 197, 'و کسانی را که شما به جای خدا می‌خوانید و می‌پرستید، نه بر یاری شما توانایی دارند و نه می‌توانند خود را یاری کنند.'),
(1152, 7, 198, 'و اگر آنها را به راه راست فراخوانید نمی‌شنوند، و آنان را می‌بینی که با دیدگانی که برای آنها ساخته‌اند به تو می‌نگرند ولی بینایی ندارند.'),
(1153, 7, 199, 'اعتدال و میانه روی را پیشه خود کن و مردم را به انجام کار نیک فرمان ده و با نادانان مدارا کن و از خطاهایشان درگذر.'),
(1154, 7, 200, 'و اگر تحریکی از سوی شیطان تو را به پرخاشگری با جاهلان و انتقام گرفتن از آنان وادارد به خدا پناه بر، که او شنوا و داناست.'),
(1155, 7, 201, 'کسانی که تقوا پیشه کرده‌اند، هنگامی که وسوسه‌ای از شیطان به آنان می‌رسد و بر گرد قلبشان می‌گردد تا در آن جای گیرد، خدا را به یاد می‌آورند و کار خود را به او وامی گذارند و او پرده غفلت را از آنان می‌زداید و ناگهان بینا می‌شوند.'),
(1156, 7, 202, 'این در حالی است که شیاطین که برادران مشرکانند آنان را در گمراهی مدد می‌دهند وانگهی کوتاهی نمی‌کنند و همچنان به گمراه کردن آنان ادامه می‌دهند.'),
(1157, 7, 203, 'و چون آیه‌ای برایشان نیاوری، با تمسخر می‌گویند: چرا آن را از این جا و آن جا جمع آوری نمی‌کنی؟ بگو: من فقط آنچه را از جانب پروردگارم به من وحی می‌شود پیروی می‌کنم. این قرآن روشنگری‌هایی از جانب پروردگار شماست، و برای گروهی که ایمان می‌آورند مایه هدایت و رحمت است.'),
(1158, 7, 204, 'و چون قرآن خوانده شود به آن گوش فرادهید و خاموش باشید، باشد که مورد رحمت الهی قرار گیرید.'),
(1159, 7, 205, 'و پروردگارت را در درون خود از روی خشوع و با ترس و بیم یاد کن، و هر صبح و عصر، بدون آوای بلند، زبان به ذکر او بگشای و از غافلان مباش تا از مقربان او گردی،'),
(1160, 7, 206, 'زیرا آنان که نزد پروردگارت هستند (مقرّبان درگاه او) تکبر نمیورزند و از پرستش او سرباز نمی‌زنند و او را از هر کاستی منزّه می‌شمرند و برای او سجده می‌کنند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'ای پیامبر، مجاهدان بَدر از تو می‌پرسند که این انفال (غنایم جنگی) از آنِ کیست و چگونه تقسیم می‌شود؟ بگو انفال ـ چه غنایم جنگی و چه املاک و اموالی که مالکی ندارند ـ به خدا و پیامبرش اختصاص دارد، پس از خدا پروا کنید و بر سر انفال با یکدیگر مشاجره نکنید و میان خود صلح و آشتی برقرار سازید و اگر واقعاً ایمان دارید، از خدا و پیامبرش فرمان برید.'),
(1162, 8, 2, 'مؤمنان راستین فقط کسانی اند که وقتی خدا نزدشان یاد شود دل هایشان می‌ترسد، و هنگامی که آیات او بر آنان تلاوت گردد بر ایمانشان می‌افزاید، و در کارها و برنامه‌ها تنها بر پروردگارشان توکّل می‌کنند.'),
(1163, 8, 3, 'کسانی اند که نماز را برپا می‌دارند و بخشی از آنچه را روزی آنان کرده‌ایم به نیازمندان انفاق می‌کنند.'),
(1164, 8, 4, 'اینانند مؤمنان راستین که در حدّ ایمانشان درجاتی نزد پروردگارشان دارند و برای آنان آمرزش و رزقی نیکوست که در بهشت نصیبشان خواهد شد.'),
(1165, 8, 5, 'گروهی از مؤمنان از حکم خدا درباره انفال ناراضی بودند، چنان که پروردگارت تو را به حق (براساس مصلحتی درست و به جا) از خانه ات [به سوی بدر] بیرون برد در حالی که گروهی از مؤمنان از آن ناخشنود بودند.'),
(1166, 8, 6, 'با تو درباره حق ـ پس از آن که برایشان آشکار شده است ـ کشمکش می‌کنند، گویی به سوی مرگ که نشانه‌های آن را می‌نگرند، رانده می‌شوند.'),
(1167, 8, 7, 'و ای مؤمنان، از آنچه خدا برای شما مقرّر کرده است نگران نباشید چرا که آن به سود شماست، یاد کنید هنگامی را که خدا به شما وعده داد که یکی از آن دو طایفه از آنِ شما باشد، یا بر کاروان تجاری قریش دست یابید و یا بر سپاه آنان پیروز گردید، و شما دوست می‌داشتید کاروان تجاری که بی سلاح بودند نصیب شما شوند، ولی خدا خلاف آن را اراده کرد، زیرا می‌خواست به حکم تقدیر خود (یاری پیامبران و پیروزی آیین الهی) حق را جلوه گر کند و ریشه کافران را برکند.'),
(1168, 8, 8, 'آری، خدا به شما چنین وعده داد تا حق را ثابت گرداند و باطل را از میان بردارد، هر چند مجرمان از آن ناخشنود باشند.'),
(1169, 8, 9, 'به یاد آورید هنگامی را که خود را در برابر مشرکان ناتوان دیدید و از پروردگارتان یاری خواستید و او بی درنگ شما را اجابت کرد که من شما را با هزار فرشته از فرشتگان که فرشتگانی دیگر را به دنبال می‌آورند یاری می‌رسانم.'),
(1170, 8, 10, 'و خداوند فرشتگان را برای کشتن کافران نفرستاد بلکه آنها را فرستاد تا برای شما بشارتی باشد و تا دل هایتان به امداد الهی آرامش یابد، پس بدانید که پیروزی جز از جانب خدا نیست. به یقین، خدا شکست ناپذیر و حکیم است.'),
(1171, 8, 11, 'هنگامی شما را یاری داد و دل هایتان را آرامش بخشید که خوابی سبک در اثر امنیتی که از جانب او بود بر شما پوشانید و از آسمان آبی بر شما فرو فرستاد تا شما را بدان پاک گرداند و وسوسه‌های پلید شیطان را بدین وسیله از شما بزداید، و تا دل هایتان را محکم کند و در نبرد گام‌های شما را به آن استوار سازد.'),
(1172, 8, 12, 'آن گاه که پروردگارت به فرشتگان وحی فرستاد که من با شما هستم، پس کسانی را که ایمان آورده‌اند ثابت قدم دارید، به زودی در دل کسانی که کافر شده‌اند بیم خواهم افکند، و به مؤمنان گفتیم فراز گردن آنان را بزنید و همه سر پنجه هایشان را قطع کنید.'),
(1173, 8, 13, 'آن کیفر برای آنان بدان سبب بود که با خدا و رسولش به مخالفت برخاستند، و هر کس با خدا و رسول او مخالفت کند و بر آن اصرار ورزد، کیفری سخت خواهد دید، زیرا خداوند سخت کیفر است.'),
(1174, 8, 14, 'ای کافران، این کیفر دنیای شماست، آن را بچشید و بدانید که در آخرت برای کافران عذاب آتش خواهد بود.'),
(1175, 8, 15, 'ای کسانی که ایمان آورده‌اید، هنگامی که با کافران رو به رو می‌شوید در حالی که دسته جمعی برای جنگ به سویشان رفته‌اید به آنان پشت نکنید و نگریزید.'),
(1176, 8, 16, 'و هر کس در آن هنگام پشت به آنان کند و بگریزد، با خشمی از خدا بازگشته و جایگاهش دوزخ است و آن بد بازگشتگاهی است، مگر این که گریز و روی گردانی اش برای جنگ در ناحیه دیگر باشد، یا بخواهد به گروهی از رزمندگان بپیوندد و در کنار آنان با دشمن بجنگد.'),
(1177, 8, 17, 'شما را توان مقابله با مشرکان بدر نبود پس آن گاه که پیروز شدید، شما آنان را نکشتید بلکه خدا آنان را کشت، و تو ای پیامبر، هنگامی که ریگ‌ها را به سوی آنان انداختی، در حقیقت تو نینداختی بلکه خدا انداخت. آری، چنین کرد تا آنچه را که باید، تحقّق بخشد و به مؤمنان از جانب خود نعمتی نیکو ارزانی دارد (هم دشمنانشان را سرکوب کند و هم از غنیمت‌های فراوان بهره مندشان سازد) چرا که خدا شنونده استغاثه آنان و به حالشان آگاه بود.'),
(1178, 8, 18, 'آری، حقیقت این بود، و آنچه مسلّم است این است که خداوند نیرنگ کافران را سست و بی اثر می‌کند.'),
(1179, 8, 19, 'ای مشرکان، اگر شما برای آشکار شدن حق خواهان داوری خدا میان خود و مؤمنان بودید بدین طریق که حق پویان را نصرت دهد، اینک با پیروزی مؤمنان این داوری صورت گرفته است. حال اگر از نیرنگ خود بر ضد خدا و پیامبر دست بردارید برای شما بهتر است، و اگر بار دیگر بازگردید و دست به نیرنگ بزنید ما نیز باز می‌گردیم و نیرنگتان را سست و بی اثر می‌کنیم، و گروه شماهر چند زیاد باشد هرگز برای شما کارساز نخواهد بود. این از آن روست که خداوند با مؤمنان است.'),
(1180, 8, 20, 'ای کسانی که ایمان آورده‌اید، از خدا و پیامبرش فرمان برید و اینک که می‌شنوید پیامبر شما را به کاری فرا می‌خواند یا از کاری باز می‌دارد از او روی برنتابید.'),
(1181, 8, 21, 'و مانند کسانی نباشید که به پیامبر گفتند سخن تو را شنیدیم، در صورتی که آن را به گوش دل نمی‌شنیدند.'),
(1182, 8, 22, 'آری، این کفرپیشگانی که از شنیدن سخن حق کرند و از گفتن حق لال، همانان که حق را درنمی یابند، نزد خدا بدترین جنبندگان به شمار می‌روند.'),
(1183, 8, 23, 'و اگر خدا در آنان خیری می‌یافت آنان را به شنیدن حق شنوا می‌ساخت، و اگر بی آن که خیری در آنان باشد حقیقت را به آنها می‌شناسانید، باز هم پشت می‌کردند و روی برمی تافتند.'),
(1184, 8, 24, 'ای کسانی که ایمان آورده‌اید، خدا را اجابت کنید و نیز پیامبر را آن گاه که شما را به اسلام که موجب حیات شماست فرا می‌خواند و بدانید که خدا میان آدمی و قلب او حایل می‌شود و بدانید که همگان به سوی او روانه می‌شوید و در حضور او گرد می‌آیید.'),
(1185, 8, 25, 'از فتنه‌ای بزرگ که در پیش است بترسید و در دفع آن بکوشید، زیرا آن به گونه‌ای است که نباید تنها به ستمکاران شما (فتنه انگیزان) برسد، بلکه دامنگیر همه شما خواهد شد، و بدانید که خدا سخت کیفر است.'),
(1186, 8, 26, 'برای این که بدانید خدا یاور مؤمنان است به یاد آورید زمانی را که در سرزمین مکّه گروهی اندک بودید و در آن جا ناتوان شمرده می‌شدید و همواره بیم آن داشتید که مردم شما را بربایند، سپس خدا شما را در مدینه جای داد و به یاری خود نیرومندتان ساخت تا در جنگ بدر پیروز شدید و شما را از غنایم آن جنگ که برایتان حلال کرده بود بهره مند ساخت، باشد که خدا را سپاسگزاری کنید.'),
(1187, 8, 27, 'ای کسانی که ایمان آورده‌اید، با افشای تصمیم‌های پیامبر در مورد کافران و آشکار ساختن اسرار حکومت اسلامی، به خدا و رسول او خیانت نکنید که خیانت به امانت‌های خود کرده‌اید، و این حقیقت را شما خود می‌دانید.'),
(1188, 8, 28, 'و بدانید که اموال و فرزندان شما وسیله آزمایش شما هستند، پس مبادا برای حفظ آنها به خدا و پیامبرش خیانت کنید، و بدانید آنان که امانت‌های خدا و پیامبرش را پاس دارند، نزد خدا پاداشی بزرگ خواهند داشت.'),
(1189, 8, 29, 'ای کسانی که ایمان آورده‌اید، اگر از خدا پروا کنید، برای شما نیرویی قرار می‌دهد که به وسیله آن حق را از باطل تشخیص دهید، و بدی هایتان را از شما می‌زداید و گناهانتان را می‌آمرزد، و خدا دارای فضلی بزرگ است.'),
(1190, 8, 30, 'ای پیامبر، برای این که بدانی خدا تو را یاری می‌کند، یاد کن زمانی را که کافران مکّه به مشورت نشستند و برای محو کردن دعوت تو چاره جویی می‌کردند، برخی بر آن بودند که تو را حبس کنند و برخی می‌خواستند تو را بکشند و برخی بر آن بودند که از مکّه بیرونت کنند. آنان همواره در پی چاره جویی اند و خدا نیز در برابر آنها کار تو را تدبیر می‌کند، و خدا بهترین تدبیرکنندگان است.'),
(1191, 8, 31, 'و هنگامی که آیات قرآن ـ که همه از جانب ماست ـ بر آنان تلاوت شود می‌گویند: شنیدیم و محتوای آن را دریافتیم. اگر ما هم می‌خواستیم، مانند این را می‌سرودیم، ولی ما اهل افسانه سرایی نیستیم. این چیزی جز افسانه‌هایی که پیشینیان ساخته‌اند نیست.'),
(1192, 8, 32, 'و یاد کن زمانی را که برخی از مسلمانان سرخورده گفتند: خداوندا، اگر این همان پیام حق است و از جانب توست ما آن را نمی‌پذیریم، پس سنگ‌هایی از آسمان بر ما بباران یا عذابی دردناک بر سر ما بیاور.'),
(1193, 8, 33, 'ولی بدانند که تا هنگامی که تو ای پیامبر در میان مسلمانان هستی خدا آنان را به عذاب نابود نمی‌کند، و تا هنگامی که از خدا طلب آمرزش می‌کنند، خدا به عذاب، نابودکننده آنان نیست.'),
(1194, 8, 34, 'چه چیز مشرکان مکّه را درخورِ آن ساخته است که خدا عذابشان نکند در صورتی که آنان مؤمنان را از ورود به مسجدالحرام بازمی دارند آنان متولّیان آن نیستند تا به هر که بخواهند اجازه ورود دهند و هر که را بخواهند از ورود به آن بازدارند. متولّیان آن جز تقواپیشگان نخواهند بود، ولی بیشترشان [این حقیقت را] نمی‌دانند.'),
(1195, 8, 35, 'نماز آنان در کنار آن خانه جز سوت کشیدن و کف زدن نیست. ای کافران، حال که چنین است، به سزای این که کفر ورزیده‌اید عذاب را بچشید.'),
(1196, 8, 36, 'کافران اموال خود را هزینه می‌کنند تا مردم را از راه خدا باز دارند. آری، به زودی کفرپیشگان مکه اموالشان را برای شعلهور ساختن جنگ هزینه می‌کنند، ولی به نتیجه دلخواه خود نمی‌رسند و اموال تلف شده آنان مایه حسرتشان می‌شود و سرانجام در فتح مکّه مغلوب می‌گردند. و کسانی از آنان که بر کفر اصرار ورزند به سوی دوزخ رانده خواهند شد.'),
(1197, 8, 37, 'کافران، به مقتضای کفرشان با دین خدا مخالفت می‌کنند و در این راه می‌کوشند، تا خداوند براساس سنّتی که در نظام آفرینش مقرّر داشته است ناپاکان را از پاکان جدا سازد. سپس برخی از ناپاکان را بر روی برخی دیگر نهد و همه را انباشته کند سپس آن را در دوزخ قرار دهد. اینانند که زیانکار خواهند بود.'),
(1198, 8, 38, 'به کافران بگو: اگر از ستیزه جویی با خدا و پیامبر دست بردارند و به کشتن و آزار مسلمانان پایان دهند، آنچه گذشته است بر آنان بخشوده می‌شود و از آنان انتقام گرفته نمی‌شود، و اگر بار دیگر به فتنه انگیزی و ستیزه جویی باز گردند خداوند نابودشان خواهد کرد و این سنت خداست که بر پیشینیان نیز گذشته است.'),
(1199, 8, 39, 'و با کافران بجنگید تا فتنه‌ای برجای نماند و دین (حاکمیّت) یکسره از آنِ خدا باشد. پس اگر از جنگ باز ایستادند و تن به سازش دادند، خدا حکم خود را درباره آنان اعلام خواهد کرد، چرا که او به آنچه می‌کنند بینا است.'),
(1200, 8, 40, 'و اگر روی برتافتند و باز هم سر جنگ داشتند، با آنان بجنگید و بدانید که خدا سرپرست و یاور شماست. و خوب سرپرست و نیکو یاوری است.'),
(1201, 8, 41, 'و بدانید که هر فایده‌ای به دست می‌آورید، خُمس آن برای خدا و پیامبر و برای خویشاوندان او، و یتیمان و بینوایان و درراه مانده از بستگان اوست، پس آن را به اهلش بپردازید اگر به خدا و آنچه در روز بدر بر بنده خود محمّد فرو فرستادیم ایمان دارید، همان روز که خدا با یاری کردن مؤمنان و شکست دادن مشرکان حق را از باطل مشخص ساخت، روزی که آن دو گروه مؤمن و مشرک برای نبرد با یکدیگر روبه رو شدند. و خدا بر انجام هر کاری تواناست.'),
(1202, 8, 42, 'هنگامی حق را از باطل مشخص ساخت که شما در نزدیک‌ترین کرانه آن وادی، در زمینی شنزار و دور از آب اردو زدید، و مشرکان در دورترین کرانه وادی، در کنارِ آب و در زمینی سخت پیاده شدند و کاروان تجاری قریش در فرو دست شما بودند و از ساحل دریا رهسپار مکّه شدند و از تیررس شما رهیدند. بدین ترتیب شما چاره‌ای جز نبرد با مشرکان نداشتید. اگر شما و آنان با یکدیگر وعده می‌کردید که این گونه باهم روبه رو شوید، قطعاً هر دو گروه وعده خود را خلاف می‌کردید، ولی آنچه را خدا پیش آورد برای این بود که کاری را که می‌بایست انجام شود به انجام رساند، تا در شرایطی که عوامل پیروزی برای مشرکان آماده بود، با پیروزی شما حق را از باطل مشخص سازد تا از آن پس هر که هلاک می‌شود، با دلیلی روشن هلاک گردد، و هر که حیات می‌یابد، با دلیلی آشکار حیات یابد. و نیز چون خدا شنونده استغاثه شما و آگاه به راز درونتان بود چنین تقدیر کرد.'),
(1203, 8, 43, 'ای پیامبر، یاد کن زمانی را که خداوند شمار مشرکان را در خواب تو، به تو اندک نشان می‌داد تا وقتی خواب خود را برای مؤمنان نقل می‌کنی، از برخورد با آنان بیمناک نباشند و تردیدی به خود راه ندهند. و اگر شمار آنان را به تو بسیار نشان می‌داد قطعاً سست و نگران می‌شدید و در کار جنگ، اختلاف می‌کردید، ولی خداوند بدین وسیله شما را از سستی و اختلاف در امان داشت، چرا که او به راز درون سینه‌ها داناست.'),
(1204, 8, 44, 'و یاد کن زمانی را که چون با مشرکان روبه رو شدید، آنان را در نظر شما اندک جلوه می‌داد و شما را نیز در نظر آنان اندک می‌نمایاند، تا خداوند کاری را که باید تحقّق می‌یافت (نبرد دو گروه) به انجام رساند، و بدانید که همه کارها به او بازگردانده می‌شود.'),
(1205, 8, 45, 'ای کسانی که ایمان آورده‌اید، هنگامی که در میدان نبرد با گروهی از کافران روبه رو گشتید ثابت قدم باشید و از برابر آنان مگریزید و خدا را با قلب و زبان خود بسیار یاد کنید، باشد که پیروز گردید.'),
(1206, 8, 46, 'و خدا و پیامبرش را در امر جهاد فرمان برید و با یکدیگر اختلاف نکنید که ناتوان می‌شوید و عزّت و شکوهتان از بین می‌رود، و بر سختی‌های جنگ و اطاعت از خدا و پیامبر شکیبا باشید که خداوند با شکیبایان است و آنان را یاری خواهد کرد.'),
(1207, 8, 47, 'و هنگامی که برای جهاد با دشمنان دین بیرون می‌روید، مانند کسانی نباشید که با سرمستی و برای خودنمایی به مردم از شهر و دیارشان برای جنگ بیرون رفتند و مردم را از راه خدا باز می‌داشتند. آنان گمان نکنند که از تحت سلطه خدا خارجند، بلکه خدا به آنچه می‌کنند احاطه دارد.'),
(1208, 8, 48, 'و هنگامی خدا حق و باطل را از یکدیگر مشخص و معلوم ساخت که شیطان کارهای مشرکان را در جنگ بدر برایشان آراست و به آنان گفت: امروز با این ساز و برگ نظامی وانبوه نفرات هیچ گروهی از مردم بر شما چیره نمی‌شود و من خود حمایتگر شما هستم، ولی هنگامی که آن دو گروه باهم روبه رو شدند و یکدیگر را دیدند به عقب بازگشت و گفت: من از شما بری هستم، من چیزی را می‌بینم که شما نمی‌بینید. می‌بینم فرشتگانی برای یاری مؤمنان و سرکوب شما آمده‌اند. من از عذاب خدا می‌ترسم، چرا که خدا سخت کیفر است.'),
(1209, 8, 49, 'آری، هنگامی خدا حق و باطل را از یکدیگر مشخص ساخت که منافقان و کسانی که در دل هایشان بیماری شکّ و تردید بود گفتند: اینان (مؤمنان) را دینشان فریفته است که با این شمار اندک و با کمترین تجهیزات به جنگِ سپاهی مجهّز آمده‌اند، ولی نمی‌دانستند که آنان بر خدا توکّل کرده‌اند. و هر کس بر خدا توکّل کند خدا او را بسنده است، زیرا خداوند مقتدری است شکست ناپذیر که کارهایش همه از روی حکمت است.'),
(1210, 8, 50, 'ای کاش می‌دیدی زمانی را که فرشتگان جان کافران را می‌گیرند در حالی که بر چهره و پشتشان می‌کوبند و به آنان می‌گویند: عذاب آتش سوزان را بچشید.'),
(1211, 8, 51, 'این کیفر به سزای دستاوردی است که خود پیش فرستاده‌اید و از آن روست که خدا به بندگانش ستم نمی‌کند [مجرمان و نیکوکاران را یکسان قرار نمی‌دهد و هر کدام را بر طبق کردارشان جزا می‌دهد].'),
(1212, 8, 52, 'شیوه این مشرکان همچون شیوه فرعونیان و کسانی است که پیش از آنان بودند که به آیات خدا کافر شدند، و خداوند آنان را به کیفر گناهانشان گرفت. همانا خدا نیرومند و سخت کیفر است.'),
(1213, 8, 53, 'این کیفر بدان سبب است که کافران مکه با خدا و رسولش به ستیزه برخاستند و بدین سبب شایستگی نعمت را از خود سلب کردند و خداوند نعمتی را که بر قومی ارزانی داشته است به محنت و عذاب تبدیل نمی‌کند تا این که آنان خود، آنچه را در دل دارند دگرگون سازند و با کفر و ناسپاسی خویش قابلیّت نعمت را از خود سلب نمایند، و نیز بدان سبب است که خدا شنونده فریادخواهی مسلمانان و آگاه به نیازهای آنان است.'),
(1214, 8, 54, 'آری، شیوه آنان همچون شیوه فرعونیان و کسانی است که پیش از آنان بودند، که آیات پروردگارشان را دروغ انگاشتند، پس ما آنان را به سزای گناهانشان هلاک نمودیم و فرعونیان را غرق کردیم و همه آنان مردمی ستمکار بودند.'),
(1215, 8, 55, 'بی تردید، بدترین جنبندگان در نزد خداوند یهودیان مدینه‌اند که کافر شدند و به حدّی کفر در دل هایشان رسوخ یافته است که هرگز ایمان نمی‌آورند.'),
(1216, 8, 56, 'آن گروهشان که بارها با آنان پیمان بستی ولی هر بار پیمان خود را شکستند و از خدا پروا نکردند و این شیوه آنان است.'),
(1217, 8, 57, 'پس اگر در جنگ بر آنان دست یافتی که خواهی یافت، چنان بر ایشان سخت گیر که مایه عبرت کسانی شوند که در پی آنانند و ترس و نگرانی بر آنان حاکم شود و دیگر برای نبرد با مؤمنان همداستان نشوند، باشد که بفهمند پیمان شکنی و ستیزه جویی با مؤمنان چه فرجام شومی دارد.'),
(1218, 8, 58, 'و اگر از قومی که با آنان پیمان بسته‌ای بیم خیانت در پیمان داری، پیمانشان را به سویشان بیفکن و لغو آن را به ایشان اعلام کن تا شما و آنان در آگاهی از لغو پیمان یکسان باشید، و بدان که اگر به آنان اعلام نکنی مرتکب خیانت شده‌ای و خداوند خیانتکاران را دوست نمی‌دارد.'),
(1219, 8, 59, 'کسانی که کافر شده‌اند مپندارند که با کفر و ستیزه جویی خویش بر خدا سبقت گرفته و بر او چیره گشته‌اند، آنان نمی‌توانند خدا را ناتوان کنند [چرا که کارهایشان از تحت اراده او خارج نیست].'),
(1220, 8, 60, 'شما مؤمنان باید برای مقابله با کافرانی که پیرامون شمایند، آنچه در توان دارید از نیروی رزمی و اسبان بسته (آماده نبرد) فراهم آورید تا بدین وسیله کسانی را که دشمن خدا و دشمن شمایند بترسانید [و اندیشه هجوم به مسلمانان را از آنان سلب کنید] و نیز باید برای مقابله با دشمنان دیگری که غیر از اینانند و شما از آنان بی خبرید و خدا به وجودشان آگاه است نیروی رزمی آماده کنید، و بدانید که هر چیزی را در راه خدا هزینه کنید، بی کم و کاست به شما بازگردانده می‌شود و بر شما ستم نخواهد شد.'),
(1221, 8, 61, 'و اگر به صلح تمایل داشتند تو نیز بدان روی آور، و بر خدا توکّل کن و بیم نیرنگشان را نداشته باش که او شنونده سخن بندگان و دانا به اندیشه‌های آنان است.'),
(1222, 8, 62, 'و اگر دشمنان بخواهند با پیشنهاد صلح تو را بفریبند تا در شرایط مناسب ضربه‌ای بر تو وارد کنند، خدا برای تو بس است، چرا که او بود که پیش‌تر تو را با یاری خود و به وسیله مؤمنان نیرومند ساخت.'),
(1223, 8, 63, 'و دل‌های مؤمنان را به هم پیوند داد، که اگر همه ثروت روی زمین را خرج می‌کردی نمی‌توانستی میان دل هایشان پیوند دهی، ولی خدا بود که میان آنان الفت انداخت، به یقین او شکست ناپذیر و حکیم است.'),
(1224, 8, 64, 'ای پیامبر، در میدان کارزار، خدا تو را بسنده است و نیز کسانی از مؤمنان که تو را پیروی کرده‌اند.'),
(1225, 8, 65, 'ای پیامبر، مؤمنان را به پیکار با کافران برانگیز. اگر از شما بیست تن شکیبا باشند، بر دویست تن از کافران چیره می‌شوند، و اگر از شما صد تن شکیبا باشند بر هزار تن از کفرپیشگان پیروز می‌شوند. این بدان سبب است که آنان مردمی هستند که نمی‌فهمند. بر این اساس، ما شما را مکلّف کرده بودیم حتی اگر دشمن ده برابر شما بودند باید مقاومت کنید و از میدان کارزار مگریزید.'),
(1226, 8, 66, 'اینک که خدا دانست در ایمان شما ضعفی پدید آمده است تکلیف شما را سبک ساخت، پس اگر از شما یکصد تن شکیبا باشند بر دویست تن چیره می‌شوند، و اگر از شما هزار تن باشند بر دو هزار تن پیروز می‌گردند. اینها همه به اذن خداست، و خدا با شکیبایان است.'),
(1227, 8, 67, 'چرا خودسرانه و بدون مشورت با پیامبر در جنگ بدر از دشمنان اسیر گرفتید تا در برابر آزادی آنان فدیه بگیرید؟ در صورتی که برای هیچ پیامبری سزاوار نبوده است که در میدان جنگ اسیرانی بگیرد. پیامبران باید در میدان جنگ چنان بر کشته‌ها بیفزایند تا عبرت دیگران شود. این شیوه تا زمانی است که دینشان در زمین استقرار نیافته باشد، امّا از آن پس برای آنان رواست که از دشمن اسیر بگیرند. شما خواهان متاع ناپایدار دنیا هستید و خداوند سرای آخرت را برای شما می‌خواهد، و خدا شکست ناپذیر و حکیم است.'),
(1228, 8, 68, 'اگر پیشتر از جانب خدا مقرر نشده بود که به عذاب گرفتار نگردید، قطعاً به سبب این که در جنگ بدر اسیر گرفتید، عذابی بزرگ به شما می‌رسید.'),
(1229, 8, 69, 'ولی اکنون از فدیه‌ها و آنچه به غنیمت گرفته‌اید بهره ببرید که برای شما حلال و پاکیزه است، و از خدا پروا کنید که خدا آمرزنده و مهربان است.'),
(1230, 8, 70, 'ای پیامبر، به اسیرانی که در دست شمایند و از آنان فدیه گرفته‌اید، بگو: اگر خداوند در دل‌های شما خیری (ایمان و حق باوری) سراغ داشته باشد، بهتر از آنچه از شما گرفته شده به شما می‌دهد و شما را می‌آمرزد، چرا که خداوند آمرزنده و مهربان است.'),
(1231, 8, 71, 'و اگر خواستند به تو خیانت کنند و بار دیگر آتش جنگ را بر ضدّ تو شعلهور سازند، خدا تو را بر آنان پیروز خواهد ساخت، چرا که آنان پیش‌تر نیز به سبب کفرشان، به خدا خیانت کردند و خدا تو را بر آنان چیره ساخت، و خدا به خیانت آنان داناست و براساس حکمتش، تو را بر آنان چیره می‌سازد.'),
(1232, 8, 72, 'کسانی که ایمان آورده و به مدینه هجرت نموده و با مال و جانشان در راه خدا جهاد کرده‌اند، و کسانی که پیامبر و مؤمنان را در مدینه جای داده و خدا و رسولش را یاری نموده‌اند، اینان بر یکدیگر ولایت دارند، و کسانی که ایمان آورده ولی هجرت نکرده‌اند، از ولایت آنان چیزی بر شما مقرّر نیست، تا آن که هجرت کنند، مگر ولایت یاری رسانی که اگر از شما در امر دین کمک خواستند بر شماست که آنان را یاری دهید، مگر این که بر ضدّ قومی باشد که میان شما و آنان پیمانی [مبنی بر عدم تعرّض] منعقد شده است، و خدا به آنچه می‌کنید بیناست.'),
(1233, 8, 73, 'کسانی که کفر ورزیده‌اند ولیّ یکدیگرند و خدا میان شما و آنان ولایتی مقرّر نکرده است. اگر قانون ولایت را که میان شما مؤمنان مقرّر شده است به کار نبندید و یا پیوند ولایت با کافران داشته باشید، در زمین فتنه و فسادی بزرگ روی خواهد داد.'),
(1234, 8, 74, 'و کسانی که ایمان آورده و هجرت نموده و در راه خدا جهاد کرده‌اند، و آنان که به پیامبر و مهاجران جای داده و خدا و رسولش را یاری کرده‌اند، اینان همان مؤمنان راستین اند که برایشان آمرزش و رزقی ارجمند است که در بهشت نصیبشان خواهد شد.'),
(1235, 8, 75, 'و کسانی که پس از این ایمان بیاورند و هجرت نمایند و همراه شما جهاد کنند، از شما خواهند بود و پیوند ولایت میان شما و آنان برقرار می‌گردد، و خویشاوندان در ارث بردن از یکدیگر بر دیگران مقدّم اند و این حکم در کتاب خدا ثبت شده است. بنابراین، از این پس هجرت و اخوّت ایمانی سبب ارث نخواهد بود. به یقین، خداوند به هر چیزی داناست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'از سوی خدا و پیامبرش به مشرکانی که با آنان پیمان بسته‌اید اعلام می‌شود که پیمان میان شما و آنان ملغی و بی اعتبار است.'),
(1237, 9, 2, 'بنابراین، ای مشرکان، چهار ماه در این سرزمین بگردید، این مدّت را به شما مهلت می‌دهیم و شما را از تعرّض مؤمنان در امان می‌داریم تا بیندیشید و برای سرنوشت خویش تصمیم بگیرید، یا از شرک دست بردارید و یا آماده نبرد شوید، و بدانید که شما نمی‌توانید خدا را درمانده کنید، و بدانید که خداوند کافران را خوار و ذلیل خواهد کرد.'),
(1238, 9, 3, 'و در روز حجّ اکبر (روز عید قربان) از سوی خدا و پیامبرش به مردم ابلاغ می‌شود که خدا پیمان مشرکان را ملغی کرده و پیامبرش نیز چنین کرده است. پس اگر از شرک باز گردید این برای شما بهتر است، و اگر از این دعوت روی برتابید، بدانید که شما نمی‌توانید خدا را درمانده کنید، و تو ای پیامبر، کافران را به عذابی دردناک نوید ده.'),
(1239, 9, 4, 'امّا مشرکانی که با آنان پیمان بسته‌اید و از پیمانشان چیزی نکاسته و کسی را بر ضدّ شما یاری نکرده‌اند، پیمانشان ملغی نیست، بنابراین پیمانشان را تا مدّتی که برای آنان مقرّر شده است به پایان رسانید که وفای به پیمان نشانگر تقواپیشگی است و خداوند تقوا پیشگان را دوست می‌دارد.'),
(1240, 9, 5, 'و چون آن مهلت چهار ماهه که جان و مال مشرکان در آن مصون شمرده شد، به پایان رسد، آنان را هر جا و هر وقت که یافتید بکشید، یا بگیریدشان و یا آنان را به محاصره درآورید و یا در هر کمینگاهی در کمینشان بنشینید، پس اگر از شرک بازگشتند و به توحید گراییدند و احکام اسلام را پذیرفتند پس نماز برپا داشتند و زکات پرداختند آزادشان بگذارید که خداوند توبه کنندگان را می‌آمرزد و آنان را مورد رحمت خود قرار می‌دهد.'),
(1241, 9, 6, 'و اگر یکی از آن مشرکان محارب از تو امان خواست تا نزد تو آید و از محتوای دعوتت با خبر شود، او را امان ده تا نزد تو آید و کلام خدا را بشنود و با آن آشنا شود، سپس او را به جای امنش برسان. این بدان سبب است که مشرکان ناآگاهند و لازم است این فرصت به کسانی که در پی تحقیق اند داده شود.'),
(1242, 9, 7, 'چگونه مشرکان می‌توانند نزد خدا و پیامبرش پیمانی داشته باشند پیمان همه آنان بی اعتبار است به جز کسانی از آنان که در کنار مسجدالحرام با ایشان پیمان بسته‌اید و تاکنون به پیمان خود وفادار بوده‌اند، پس تا هنگامی که به عهد خویش با شما پایدارند شما نیز به عهد خود با آنان پایدار بمانید، که پای بندی به پیمان، نشانگر تقواپیشگی است و خداوند تقواپیشگان را دوست می‌دارد.'),
(1243, 9, 8, 'چگونه پیمان مشرکان محترم شمرده شود، در صورتی که اگر بر شما چیره شوند نه حقّی از حقوق خویشاوندی را درباره شما رعایت می‌کنند و نه به هیچ پیمانی که با آنان امضا کرده‌اید وفادارند. شما را به زبان و با سخنان فریبنده خود خشنود می‌کنند ولی دل هایشان از آنچه بر زبان می‌آورند ابا دارد و بیشترشان از عهد و پیمان خود خارج شده‌اند.'),
(1244, 9, 9, 'آیات خدا را به کالای کم بهای دنیا فروخته‌اند و بدین کار مردم را از راه او باز داشته‌اند. راستی که بد کاری انجام می‌داده‌اند.'),
(1245, 9, 10, 'در حق هیچ مؤمنی هیچ یک از حقوق خویشاوندی را رعایت نمی‌کنند و به هیچ پیمانی که امضا کرده‌اند پای بند نیستند. اینانند که به پیمان‌های شما تجاوز کرده و آنها را نادیده گرفته‌اند.'),
(1246, 9, 11, 'پس اگر از شرک توبه کردند و احکام دین را پذیرفتند پس نماز به جای آوردند و زکات پرداختند، برادران دینی شما هستند و در تمام حقوق با سایر مؤمنان برابرند. ما آیات خود را به روشنی برای همه بیان می‌کنیم و تنها مردم دانا از آن بهره می‌برند.'),
(1247, 9, 12, 'و اگر گروهی از مشرکان پیمان‌های خود را که بر آنها سوگند یاد کرده‌اند پس از آن که بر آن عهد بستند، شکستند و درباره دین شما زبان به بدگویی گشودند آنان پیشوایان کفر به شمار می‌آیند و شما باید با پیشوایان کفر پیکار کنید، چرا که آنان را هیچ پیمانی نیست، باشد که از پیمان شکنی دست بردارند.'),
(1248, 9, 13, 'آیا با قومی که پیمان‌های خود را شکستند و قصد بیرون راندن پیامبر را داشتند و نخستین بار آغازگر جنگ با شما بودند، نمی‌جنگید؟ آیا از آنان می‌ترسید؟ اگر مؤمنید [باید برای شما آشکار باشد که] خدا سزاواتر است که از او بترسید.'),
(1249, 9, 14, 'با مشرکان پیکار کنید در این صورت خداوند آنان را به دست شما عذاب می‌کند و خوار و ذلیلشان می‌سازد و شما را بر آنان پیروز می‌گرداند و سینه [داغدار] گروهی از مؤمنان را شفا می‌بخشد.'),
(1250, 9, 15, 'و خشم دل هایشان را می‌برد، و این را هم بدانید که خداوند بر هر که بخواهد به رحمت خود باز می‌گردد [و او را از شرک می‌رهاند] و شما را از نبرد با وی باز می‌دارد، و خدا دانا و حکیم است.'),
(1251, 9, 16, 'آیا شما مدّعیان ایمان پنداشته‌اید که رها می‌شوید و مورد آزمایش قرار نمی‌گیرید، در صورتی که هنوز خداوند کسانی از شما را که در جهاد شرکت می‌کنند و جز خدا و رسول او و مؤمنان تکیه گاهی نگرفته‌اند، مشخص نکرده است؟ و خدا به آنچه می‌کنید آگاه است.'),
(1252, 9, 17, 'مشرکان حق ندارند در حالی که به زبان یا عمل، بر کفر خود گواهی می‌دهند، مساجد خدا از جمله مسجدالحرام را تعمیر کنند. آنان اعمالشان بی اثر است و برای همیشه در آتش دوزخ ماندگارند.'),
(1253, 9, 18, 'تنها کسانی حق دارند مساجد خدا را تعمیر کنند که به خدا و روز واپسین ایمان آورده و نماز برپاداشته و زکات داده و جز از خدا بیم نداشته‌اند (تنها او را پرستیده اند) که امید است اینان در زمره ره یافتگان باشند.'),
(1254, 9, 19, 'آیا آب دادن به حاجیان و تعمیر مسجدالحرام را که برخاسته از ایمان به خدا نبوده است ارج می‌نهید و چنین کسی را با آن کس که به خدا و روز واپسین ایمان آورده و در راه خدا جهاد کرده است یکسان می‌شمرید؟ نه، این دو گروه نزد خدا یکسان نیستند، زیرا خداوند مردم ستمکار (مشرکان) را به سوی خویش هدایت نمی‌کند.'),
(1255, 9, 20, 'کسانی که ایمان آورده و هجرت نموده و با مال و جانشان در راه خدا جهاد کرده‌اند، نزد خدا از همه بلندمرتبه ترند و اینانند که نیکبخت خواهند بود.'),
(1256, 9, 21, 'این گروه از مؤمنانند که پروردگارشان آنان را به رحمتی والا از جانب خویش و به خشنودی خود و بوستان‌هایی از بهشت که در آنها برایشان نعمتی پایان ناپذیر است مژده می‌دهد.'),
(1257, 9, 22, 'در آن جا برای همیشه ماندگارند. مبادا این وعده‌ها را بعید بدانید، چرا که خداست که نزد او پاداشی بزرگ است.'),
(1258, 9, 23, 'ای کسانی که ایمان آورده‌اید، اگر پدران و برادرانتان کفر را بر ایمان برگزیدند آنان را دوست خود و عهده دار کارهایتان قرار ندهید و کسانی از شما که آنان را دوست خود گیرند و کارهایشان را به آنان بسپرند ستمکار خواهند بود.'),
(1259, 9, 24, 'ای پیامبر، به آنان که ایمان آورده‌اند بگو: اگر پدران و پسران و برادران و همسران و خویشاوندانتان و اموالی که گرد آورده‌اید و تجارتی که از کسادش بیم دارید و خانه‌هایی که آنها را خوش می‌دارید، نزد شما از خدا و پیامبرش و جهاد در راه او محبوب‌تر است و اینها مانع رفتن شما به جهاد شده‌اند، منتظر باشید تا خدا فرمان خود را اجرا کند (مؤمنانی را جایگزین شما کند که خدا از هر چیزی برای آنان محبوب‌تر است و به بهترین وجه در راه او جهاد می‌کنند) و بدانید که خداوند مردمی را که از مرز بندگی او خارج شوند، به سوی پیامبر و یاری دین خدا هدایت نمی‌کند.'),
(1260, 9, 25, 'قطعاً خداوند شما را در معرکه‌های فراوانی یاری کرده است، و نیز در آن روز که در وادی (حُنَین) با مشرکان به کارزار پرداختید، آن گاه که کثرت افرادتان شما را شادمان ساخته بود به گونه‌ای که خدا را فراموش کردید و به فراوانی لشکر خود اعتماد نمودید، ولی آن سپاه عظیم کارساز نشد و هیچ خطری را از شما دفع نکرد و در محاصره دشمن قرار گرفتید و زمین با همه فراخی اش بر شما تنگ شد و سرانجام به دشمن پشت کردید و گریختید.'),
(1261, 9, 26, 'پس از آن، خداوند آرامشی را از جانب خود بر پیامبرش و بر مؤمنانی که در آن معرکه ثابت قدم ماندند فرو فرستاد و لشکریانی را از فرشتگان که شما آنها را ندیدید برای یاری شما نازل کرد و کسانی را که کافر شدند کیفر داد، و این است سزای کفرپیشگان.'),
(1262, 9, 27, 'سپس خداوند بعد از واقعه حنین به هر کس از کافران و مؤمنان خطاکار که می‌خواست روی کرد و به او توفیق توبه داد و توبه اش را پذیرفت و خدا راه توبه را برای همگان گشوده است، زیرا خدا آمرزنده و مهربان است.'),
(1263, 9, 28, 'ای کسانی که ایمان آورده‌اید، جز این نیست که مشرکان پلیدند، از این رو نباید پس از پایان امسال (سال نهم هجرت) به مسجدالحرام نزدیک شوند. و اگر می‌ترسید که با اجرای این حکم داد و ستدتان کساد شود و فقر به شما روی آورد، به زودی خدا، اگر بخواهد، از فضل خویش شما را توانگر می‌کند. به یقین، خداوند دانا و کارهایش همه از روی حکمت است.'),
(1264, 9, 29, 'با کسانی که به آنان کتاب آسمانی داده شده است (یهودیان، مسیحیان، مجوسیان و صابئی ها) ولی به خدا و روز قیامت آن گونه که باید ایمان ندارند و آنچه را خدا حرام کرده و پیامبرش محمّد تحریم نموده است بر خود حرام نمی‌دانند و دین حقّ را آیین خود نمی‌گیرند بجنگید تا با خضوع در برابر حکومت شما به قانون جزیه تن در دهند و آن را به دست خود به شما بپردازند.'),
(1265, 9, 30, 'یهودیان گفتند: عزیر پسر خداست. و نصارا گفتند: مسیح پسر خداست. این سخن آنان است که بر زبان می‌آورند، سخنی است بی پایه و عاری از حقیقت که به سخن آن کافران بت پرستی می‌ماند که پیش از ایشان بودند. خدا آنان را بکشد! تا کی از حق باز داشته و به سوی باطل کشانده می‌شوند.'),
(1266, 9, 31, 'یهودیان و مسیحیان از آن روی که بی هیچ قید و شرطی از عالمان و راهبان خود اطاعت کردند، آنها را به منزله پروردگار و معبود خویش شمردند و برای خدا ربوبیتی قائل نشدند. علاوه بر این، نصارا مسیح پسر مریم را نیز پروردگار خود گرفتند در حالی که آنان مأمور نبودند مگر این که یک خدا را که جز او معبودی درخور پرستش نیست عبادت کنند. او منزّه است از این که به او شرک ورزیده شود.‌'),
(1267, 9, 32, 'می‌خواهند نور خدا را که در آیین او جلوه کرده است با دهان خود خاموش کنند و خدا نخواهد گذاشت جز این شود که نور خود را به کمال رساند و سراسر گیتی را فراگیرد هر چند کافران از آن ناخشنود باشند.'),
(1268, 9, 33, 'اوست آن کسی که پیامبرش محمّد را با هدایت و دین حق به رسالت فرستاد تا آن را بر همه ادیان چیره کند هر چند مشرکان ناخشنود باشند.'),
(1269, 9, 34, 'ای کسانی که ایمان آورده‌اید، بسیاری از عالمان دینی یهود و راهبان مسیحی اموال مردم را به ناروا می‌خورند و آنان را از راه خدا باز می‌دارند، و کسانی را که زر و سیم می‌اندوزند و آن را در راه خدا خرج نمی‌کنند به عذابی دردناک نوید ده.'),
(1270, 9, 35, 'این عذاب در روزی است که آن گنج‌های طلا و نقره را در آتش دوزخ بگدازند و پیشانی و پهلو و پشتشان را با آنها داغ کنند و به آنان گفته شود: این است آنچه برای خود اندوختید که اینک به صورت عذاب برای شما جلوه گر شده است، پس بچشید آنچه را که می‌اندوختید.'),
(1271, 9, 36, 'شماره ماه‌های سال نزد خدا دوازده ماه است. این تعداد را خداوند از همان روزی که آسمان‌ها و زمین را آفرید در جهان هستی مقرّر کرد. از این دوازده ماه، چهار ماه (رجب، ذی القعده، ذی الحجّه و محرّم) ماه‌های حرام اند که جنگ در آنها ممنوع است. این است آن دینی که مصالح بندگان را تأمین و امور زندگی آنان را اداره می‌کند، پس مبادا در این ماه‌های حرام با ارتکاب گناه، به ویژه با برپایی جنگ بر خود ستم کنید. و با همه مشرکان بجنگید، چنان که آنان نیز با همه شما می‌جنگند، و در جنگ‌ها تقوای الهی را رعایت کنید و حدود او را محترم شمرید و بدانید که خدا با تقواپیشگان است و آنان را پیروز می‌گرداند.'),
(1272, 9, 37, 'جز این نیست که تأخیر انداختن ماه‌های حرام از زمان خودشان تغییر دادن حکم خداست و کفر است و مشرکان کفرپیشه با این کار بر کفر خویش می‌افزایند. با این کار نادرست سایر کافران نیز به گمراهی کشیده می‌شوند. کارشان بدین گونه است که ماه حرام را یک سال حلال می‌شمرند و حرمتش را برای ماه بعد قرار می‌دهند و سال دیگر حرمت آن را باز می‌گردانند و بدین طریق حرام و حلال را جابه جا می‌کنند تا تعداد آنچه حرام می‌شمرند با تعداد آنچه خدا حرام کرده است یکسان باشد، از این رو آنچه را خدا حرام کرده است حلال می‌شمرند، هر چند تعداد آن را حفظ می‌کنند. کارهای ناروایشان برای آنان آراسته شده است و خداوند به کافران توفیق هدایت و دستیابی به سعادت را نخواهد داد.'),
(1273, 9, 38, 'ای کسانی که ایمان آورده‌اید، شما را چه شده است که وقتی به شما می‌گویند در راه خدا برای جهاد حرکت کنید، کندی و سنگینی می‌کنید و به زمین می‌چسبید؟ آیا به زندگی دنیا بسنده کرده و آن را پسندیده و از زندگی آخرت چشم پوشیده‌اید؟ پس بدانید که بهره زندگی دنیا در برابر آخرت جز بهره‌ای ناچیز نیست.'),
(1274, 9, 39, 'اگر برای جهاد حرکت نکنید، خداوند شما را به عذابی دردناک کیفر می‌دهد و به جای شما مردمی دیگر می‌آورد که فرمان جهاد را نادیده نمی‌انگارند و در آن سستی نمیورزند، و اگر خدا شما را از میان ببرد هیچ زیانی به او نمی‌رسانید بلکه خود زیانکار خواهید شد که خداوند بر هر کاری تواناست.'),
(1275, 9, 40, 'اگر پیامبر را یاری نکنید او بی یاور نخواهد ماند، چنان که پیش‌تر خدا او را یاری کرد، آن گاه که کافران مکّه او را بیرون کردند (درصدد کشتنش برآمدند و او به ناچار از آن شهر بیرون رفت) و جز یک تن کسی همراهش نبود، زمانی که آن دو در آن غار (غار ثَوْر) بودند، وقتی که به همراهش می‌گفت: اندوه مدار و بدان که قطعاً خدا با ماست. آری، در چنین موقعیتی خدا پیامبرش را یاری کرد و آرامشی از جانب خود بر او فرو فرستاد و او را با سپاهیانی که آنها را نمی‌دیدید نیرو بخشید، و سخن کافران و تصمیم آنان بر قتل پیامبر و برچیدن رسالت را پست‌ترین سخنان قرار داد و آن را بی نتیجه ساخت، و سخن خدا که همانا یاری پیامبر و سیطره دین اوست، برتر و فایق بر سخن و تصمیم کافران است و خداوند شکست ناپذیر و کارهایش همه از روی حکمت است.'),
(1276, 9, 41, 'گرفتاری‌های زندگی و کارهای روزمرّه عذری موجه برای ترک جهاد نیست، بنابراین وقتی به جهاد فرا خوانده می‌شوید، خواه سبکبار باشید یا گرانبار، باید روانه شوید و با مال و جانتان در راه خدا پیکار کنید. این برای شما بهتر است اگر بدانید.'),
(1277, 9, 42, 'اگر چشم انداز جهادی که مردم را بدان فرا خواندی غنیمتی نزدیک و در دسترس و سفری آسان و کوتاه بود منافقان در پی تو می‌آمدند، ولی راه تا معرکه تبوک بر آنان دور و دراز آمد و آن را دشوار دیدند و در آن شرکت نکردند و به زودی وقتی از این غزوه بازگشتید به خدا سوگند خواهند خورد که اگر می‌توانستیم با شما خارج می‌شدیم. آنان با این شیوه‌ای که در پیش گرفته‌اند، خود را نابود می‌کنند و خدا می‌داند که آنان قطعاً دروغگویند.'),
(1278, 9, 43, 'خدا بر تو ببخشاید، چرا به آنان رخصت دادی که در جهاد شرکت نکنند؟ اگر می‌خواستی رخصت نمی‌دادی و سزاوارتر آن بود که چنین می‌کردی تا بدین وسیله آنان را می‌آزمودی تا کسانی که راست گفته‌اند برای تو معلوم شوند و دروغگویان را بشناسی.'),
(1279, 9, 44, 'کسانی که واقعاً به خدا و روز واپسین ایمان آورده‌اند، از تو برای ترک جهاد با مال و جان خود رخصت نمی‌طلبند، زیرا آنان اهل تقوا هستند و به ضرورت جهاد آگاهی دارند و خدا هم به تقواپیشگان داناست.'),
(1280, 9, 45, 'تنها کسانی که به خدا و روز واپسین ایمان ندارند و دل هایشان به شک آلوده است و همواره در شک خود سرگردانند، از تو اجازه می‌خواهند که رهسپار میدان جهاد نشوند.'),
(1281, 9, 46, 'آنان خواستار جهاد نیستند، اگر خواستار آن بودند ساز و برگی برای آن فراهم می‌آوردند، و خدا هم بسیج آنان را خوش نداشت و به کیفر نفاقشان رغبت به جهاد را از آنان سلب کرد و آنها را از این کار بازداشت و مؤمنان را از خطر آنان رهانید و از درونشان به آنان ندا داده شد که با خانه نشینان همنشین باشید.'),
(1282, 9, 47, 'اگر در میان شما رهسپار میدان جهاد می‌شدند، جز بر تباهی و تشویش خاطر شما نمی‌افزودند و به سرعت میان شما فساد و آشوب به پا می‌کردند و تفرقه و اختلاف شما را می‌خواستند، و در میان شما کسانی هستند که به سخنان آنها گوش می‌سپارند و خواسته هایشان را اجابت می‌کنند، و خداوند به ستمکاران داناست.'),
(1283, 9, 48, 'آنان پیش از این هم در پی فساد و ایجاد اختلاف در میان مؤمنان بودند و کارها را برای تو وارونه می‌کردند، تا آن که حق پدیدار شد و امر خدا (دین او) پیروز گشت در حالی که از آن ناخشنود بودند.'),
(1284, 9, 49, 'و از میان آنان کسی است که وقتی او را به جهاد فرا می‌خوانی می‌گوید: به من اجازه ماندن ده و مرا در فتنه میفکن و گرفتار بلا مکن. بدانید که آنان بر اثر کفر و نفاق، خود در فتنه فرو افتاده‌اند و سرانجام دوزخ کافران را فراخواهد گرفت.'),
(1285, 9, 50, 'اگر در این جنگ که به سوی آن رهسپاری پیروز شوی و خیری به تو رسد، آنان را بدحال می‌کند، و اگر گزندی به تو رسد و مغلوب گردی، می‌گویند: ما پیش از این کار خود را به دست گرفتیم و از حوادث در امان ماندیم، آن گاه روی برمی گردانند و شادمانه می‌روند.'),
(1286, 9, 51, 'به آنان بگو: جز آنچه خدا برای ما مقرّر کرده است چیزی به ما نمی‌رسد. او سرپرست ماست و چیزی در اختیار ما نیست و در هر حال به فرمان او عمل می‌کنیم و بر او توکّل داریم که مؤمنان باید فقط بر او توکّل کنند (امور خود را به او وانهند و به فرمان و تقدیر او گردن نهند.)'),
(1287, 9, 52, 'به آنان بگو: آیا برای ما جز یکی از این دو نیکی را انتظار می‌برید که یا پیروز شویم و غنایمی به دست آوریم و یا مغلوب گردیم و در راه خدا کشته شویم؟ ما نیز برای شما انتظار می‌بریم که خداوند از جانب خودش یا به دست ما گرفتار عذابتان کند، پس منتظر باشید که ما نیز با شما در انتظاریم.'),
(1288, 9, 53, 'بگو: چه از روی رغبت انفاق کنید یا از روی بی میلی، هرگز از شما پذیرفته نمی‌شود، زیرا شما مردمی تبهکارید و خدا انفاق را از تبهکاران نمی‌پذیرد.'),
(1289, 9, 54, 'و هیچ دلیلی برای پذیرفته نشدن انفاق هایشان نیست جز این که به خدا و پیامبرش کافر شدند، و نماز را جز با حال سستی به جای نمی‌آورند و جز از روی اکراه انفاق نمی‌کنند.'),
(1290, 9, 55, 'ای پیامبر، مبادا فراوانی اموال و فرزندان منافقان تو را مجذوب کند، جز این نیست که خدا می‌خواهد به وسیله اینها در زندگی دنیا عذابشان کند و در حالی که کافرند جانشان برآید.'),
(1291, 9, 56, 'آنان به خدا سوگند می‌خورند که از شما مؤمنانند، در حالی که ایمان ندارند و از شما نیستند، بلکه مردمی هستند که می‌ترسند و پریشان خاطرند.'),
(1292, 9, 57, 'اگر پناهگاه یا نهانگاه‌هایی یا راهی باریک برای گریز بجویند، [از شما] روی برتافته شتابان به سوی آن می‌روند و هیچ چیز آنان را باز نمی‌گرداند.'),
(1293, 9, 58, 'برخی از این منافقان کسانی اند که [از روی خودخواهی] درباره تقسیم صدقات بر تو ایراد می‌گیرند. پس اگر از صدقات به آنان داده شود راضی می‌شوند، و اگر از آن به ایشان داده نشود ناگهان به خشم می‌آیند.'),
(1294, 9, 59, 'ای کاش آنچه را خدا به آنان داد و پیامبرش به آنان پرداخت با رضایت می‌گرفتند و می‌گفتند: خدا ما را بس است، ما امید آن داریم که خدا از فضل خود به ما دهد و پیامبرش نیز به فرمان او به ما عطا کند، چرا که ما به فضل خدا دل بسته‌ایم.'),
(1295, 9, 60, 'بر همه مسلمانان روا نیست که از زکات واجب بهره مند شوند تا اگر به عدّه‌ای داده نشد شکایت کنند، زکات تنها به مستمندان و درماندگان و عاملانِ گمارده شده بر آن و مخالفانی که با هدف گرایش به اسلام یا یاری دادن مسلمانان از آنان دلجویی می‌شود اختصاص دارد، و نیز در راه آزادی بردگان و پرداخت وامِ کسانی که از پرداخت آن ناتوانند و در راه خدا و [تأمین توشه و مرکب] مسافری که در راه مانده است هزینه می‌شود. این موارد هشتگانه برای مصرف زکات از جانب خدا مقرّر شده و قابل تغییر نیست، و خدا دانا و کارهایش همه از روی حکمت است.'),
(1296, 9, 61, 'و برخی از منافقان کسانی اند که پیامبر را آزار می‌دهند و می‌گویند: او گوش است و به آنچه به وی می‌گویند گوش فرا می‌دهد. بگو: گوش فرا دادن او به سخن شما و این که شما را راستگو می‌داند برای شما بهتر است. آنچه را خدا می‌گوید باور می‌کند و آنچه را مؤمنان می‌گویند، به سود آنان می‌پذیرد و آنان را در سخنانشان دروغگو نمی‌شمرد و برای کسانی از شما مسلمانان که حقیقتاً ایمان آورده‌اند مایه رحمت است، و کسانی که پیامبر خدا را آزار می‌رسانند برای آنان عذابی دردناک خواهد بود.'),
(1297, 9, 62, 'منافقان برای شما به خدا سوگند می‌خورند تا شما را از خود خشنود سازند، در صورتی که اگر مؤمن باشند سزاوارتر است که خدا و پیامبرش را با اطاعت و ترک خصومت خشنود کنند.'),
(1298, 9, 63, 'آیا ندانسته‌اند که هر کس با خدا و پیامبر او مخالفت و دشمنی کند آتش دوزخ برای اوست که در آن جاودانه می‌ماند؟ این است خواری بزرگ.'),
(1299, 9, 64, 'منافقان از این بیم دارند که سوره‌ای از قرآن بر ضدّ آنان نازل شود و آنان را از آنچه در دل دارند (کفر و سوءقصد به تو) خبر دهد. به آنان بگو: همچنان در نفاق خود بمانید و با این شیوه مؤمنان را به مسخره گیرید، قطعاً خداوند آنچه را که از فاش شدنش بیم دارید آشکار خواهد کرد.'),
(1300, 9, 65, 'اگر از سوء قصدی که درباره تو داشتند بپرسی، می‌گویند: ما چنین قصدی نداشتیم، ما فقط کارهای بیهوده می‌کردیم و به بازی و سرگرمی مشغول بودیم. به آنان بگو: آیا برای بازی و سرگرمی، خدا و آیات او و پیامبرش را به مسخره می‌گرفتید؟'),
(1301, 9, 66, 'برای آنچه کرده‌اید عذر نیاورید که بی نتیجه است، زیرا شما پس از آن که ایمان آورده بودید، با سوء قصدی که درباره پیامبر داشتید، کافر شدید، از این رو همه درخورِ کیفرید، و اگر ما گروهی از شما را به اقتضای مصلحتی ببخشیم و مجازات نکنیم گروهی دیگر را بدان سبب که گنهکارند کیفر خواهیم داد.'),
(1302, 9, 67, 'مردان و زنان منافق همه در داشتن صفات ناپسند و ارتکاب کارهای ناروا مانند یکدیگرند. آنان به کار ناروا فرمان می‌دهند و از کار پسندیده بازمی دارند و از انفاق در راه خدا دست نگاه می‌دارند آری آنان خدا را فراموش کرده‌اند چرا که از بندگی خدا خارج شده‌اند و خدا هم آنان را به فراموشی سپرده است.'),
(1303, 9, 68, 'خداوند به مردان و زنان منافق و همه کفرپیشگان آتش دوزخ را وعده داده است که جاودانه در آن خواهند بود. آن آتش برایشان بسنده است و خداوند آنان را از رحمت خود دور ساخته و عذابی پایدار خواهند داشت.'),
(1304, 9, 69, 'شما منافقان همچون کافران و منافقانی هستید که پیش از شما بودند. آنان از شما نیرومندتر بودند و اموال و فرزندان بیشتری داشتند و به دنیا روی آوردند و از آنچه نصیبشان شد بهره بردند. شما نیز همچون منافقان و کافرانی که پیش از شما بودند و از نصیب خود کامیاب گشتند، از نصیب خود بهره بردید و در یاوه سرایی درباره آیات خدا فرورفتید، آن گونه که آنان فرورفتند. آنان اعمالشان در دنیا و آخرت تباه گردید و زیانکار شدند، شما نیز در زیانکاری و تباهی اعمال همچون آنانید، از این رو ثروت و مکنت برای شما نیز کارساز نخواهد بود.'),
(1305, 9, 70, 'آیا خبر کسانی که پیش از آنان بودند [و به پیامبران کافر شدند]، خبر قوم نوح و عاد و ثمود و قوم ابراهیم و مردم شهر مدیَن و آن شهرهای زیر و رو شده قوم لوط به ایشان نرسیده است؟ داستانشان این بود که پیامبرانشان معجزات و دلایلی روشن برای آنان آوردند ولی آنها آن را دروغ انگاشتند و در نتیجه به هلاکت رسیدند، پس خدا آن گاه که عذابشان کرد بر آنان ستم نکرد و زیبنده او هم نیست که ستم کند. آنان خود با روی آوردن به دنیا و تکذیب پیامبران، خویشتن را سزاوار کیفر نمودند و بدین طریق بر خود ستم روا داشتند.'),
(1306, 9, 71, 'امّا مردان و زنان با ایمان بر یکدیگر ولایت دارند، از این رو یکدیگر را به کار شایسته فرمان می‌دهند و از کار ناپسند نهی می‌کنند و نماز را برپا می‌دارند و زکات می‌پردازند و از خدا و پیامبرش اطاعت می‌کنند. اینانند که به زودی خداوند رحمت خود را شامل حالشان می‌کند و هیچ کس نمی‌تواند مانع آن شود، چرا که خدا مقتدری شکست ناپذیر و کارهایش همه از روی حکمت است.'),
(1307, 9, 72, 'خداوند به مردان و زنان با ایمان بوستان‌هایی پردرخت که از زیر آنها نهرها روان است و در آن جا جاودانه‌اند وعده داده است، و نیز سراهایی خوش در بوستان‌هایی دایمی علاوه بر اینها او از آنان خشنود است، و خشنودی خدا از همه موهبت‌های بهشتی برتر است. این است نیکبختی بزرگ.'),
(1308, 9, 73, 'ای پیامبر، با کافران و منافقان جهاد کن و بر آنان سخت گیر. سرایشان دوزخ است و آن بد جایگاهی است.'),
(1309, 9, 74, 'منافقان به خدا سوگند می‌خورند که سخنی کفرآمیز نگفته‌اند، در صورتی که حتماً آن را گفته و به طور جدّی هم گفته‌اند و بدین سبب پس از آن که اسلام آورده بودند کافر شدند و در گردنه راه تبوک آهنگ جان پیامبر را کردند و بدین کار دست نیافتند، و علّت خشمشان بر پیامبر چیزی جز این نبود که خداوند آنان را به فضل خویش توانگر کرد و پیامبرش نیز به وسیله غنایم جنگی بی نیازشان ساخت. پس اگر توبه کنند برای آنان بهتر است، چرا که توبه آنان آمرزش گناهان و ورود به بهشت را به دنبال خواهد داشت، و اگر از توبه رویگردان شوند، خداوند در دنیا و آخرت به عذابی دردناک عذابشان خواهد کرد و در روی زمین هیچ سرپرست و یاوری که آنان را از عذاب رهایی دهد، نخواهند داشت.'),
(1310, 9, 75, 'برخی از منافقان کسانی اند که با خدا پیمان بستند که اگر از فضل خود به ما ثروتی عطا کند قطعاً صدقه خواهیم داد و از کسانی خواهیم شد که کارهای شایسته انجام می‌دهند.'),
(1311, 9, 76, 'پس هنگامی که خداوند از فضل خود به آنان بخشید، بدان بخل ورزیدند و [به پیمانشان] پشت کردند و روی برتافتند.'),
(1312, 9, 77, 'سپس این بخل در دل هایشان نفاقی بر جای نهاد که تا روزی که مرگ را دیدار کنند از آنان جدا نخواهد شد. این بدان سبب بود که وعده‌ای را که به خدا دادند وفا نکردند و همواره دروغ می‌گفتند.'),
(1313, 9, 78, 'آیا ندانسته‌اند که خداوند کارها و سخنان پنهانی آنان را می‌داند و خداوند از همه نهان‌ها به خوبی آگاه است؟'),
(1314, 9, 79, 'کسانی که به مؤمنان توانگری که از روی رغبت صدقه می‌دهند طعنه می‌زنند و نیز بر کسانی از اهل ایمان که چیزی برای پرداخت صدقه نمی‌یابند مگر آن که آخرین تلاش خود را به کار بندند، خرده می‌گیرند، و هر دو گروه را به خاطر پرداخت صدقه مسخره می‌کنند، خدا آنان را به مسخره خواهد گرفت و برای آنان عذابی دردناک خواهد بود.'),
(1315, 9, 80, 'چه برای آنان از خدا آمرزش بخواهی یا آمرزش نخواهی یکسان است، اگر هفتاد بار هم برایشان آمرزش بخواهی هرگز خدا آنان را نمی‌آمرزد، زیرا آنان به خدا و رسول او کفر ورزیدند و از بندگی خدا خارج شدند، و خدا کسانی را که از بندگی او خارج شوند به قرب خویش نمی‌رساند.'),
(1316, 9, 81, 'واپس نهاده شدگان از این که به خاطر مخالفت با رسول خدا بر جای خود نشستند و رهسپار غزوه تبوک نشدند، شادمان شدند، و ناخشنود بودند از این که با مال و جانشان در راه خدا پیکار کنند و به دیگران نیز می‌گفتند: شما هم در این هوای گرم و سوزان به سوی جنگ نروید. بگو: آتش دوزخ سوزان‌تر است ای کاش می‌دانستند.'),
(1317, 9, 82, 'پس باید خنده و شادمانی آنان کم باشد و گریه و اندوهشان بسیار چرا که شادمانی آنان تنها در این دنیای زودگذر است و گریه و اندوهشان در سرای آخرت است که آتشی جاودانه دارد. این جزایی است در برابر کارهایی که می‌کردند.'),
(1318, 9, 83, 'پس اگر خداوند تو را از این جنگ به سوی گروهی از این منافقان بازگرداند، و آنان برای بسیج شدن از تو رخصت خواستند، به آنان بگو: از این پس هرگز با من رهسپار نمی‌شوید و همراه من با هیچ دشمنی کارزار نمی‌کنید، زیرا شما نخستین بار به باز نشستن از جهاد خشنود بودید، پس اکنون هم با کسانی که در خانه‌ها مانده‌اند (زنان، کودکان، بیماران) بنشینید.'),
(1319, 9, 84, 'و هرگز بر اَحَدی از آنان (منافقان) که در گذشته است نماز مخوان و بر گورش به دعا نایست، زیرا آنان به خدا و رسول او کافر شدند و در حالی مُردند که از بندگی خدا خارج بودند.'),
(1320, 9, 85, 'مبادا فراوانی مال و فرزندانشان تو را مجذوب کند، جز این نیست که خدا می‌خواهد به وسیله اینها در زندگی دنیا عذابشان کند و در حالی که کافرند جانشان برآید.'),
(1321, 9, 86, 'و چون سوره‌ای از قرآن نازل شود و در آن به مردم فرمان داده شود که به خدا ایمان بیاورید و همراه پیامبرش جهاد کنید، توانگرانشان از تو رخصت می‌طلبند و می‌گویند: ما را واگذار که با نشستگان باشیم.'),
(1322, 9, 87, 'آنان از این خشنود شدند که رهسپار جهاد نشوند و با خانه نشینان باشند، و بر دل هایشان مهر نهاده شد، ازاین رو درک نمی‌کنند.'),
(1323, 9, 88, 'امّا پیامبر و کسانی که ایمان آورده‌اند و با او هستند، با مال و جانشان جهاد کردند، و اینانند که همه خوبی‌ها برای آنهاست و اینانند که نیکبخت‌اند.'),
(1324, 9, 89, 'خداوند برای آنان بوستان‌هایی با درختانی انبوه آماده کرده است که از زیر آنها نهرها روان است و در آن جاودانه‌اند. این است کامیابی بزرگ.'),
(1325, 9, 90, 'پس از اعلام جهاد، بادیه نشینانی که از حضور در میدان جنگ معذور بودند آمدند تا به آنان اجازه جهاد داده شود، امّا آنان که ادعای ایمان می‌کردند و به خدا و رسول او دروغ گفتند بر جای نشستند. به زودی به کسانی از آنان که کافر شدند عذابی دردناک خواهد رسید.'),
(1326, 9, 91, 'بر ناتوانان و بیماران و تهیدستانی که هزینه سفر جنگ را ندارند جهاد مقرّر نشده است تا بر اثر آن دچار رنج و سختی شوند از این رو سزاوار کیفر و نکوهش نیستند، به شرط آن که برای خدا و پیامبرش خیرخواهی کنند و از خیانت به خدا و رسول بپرهیزند و کارها را بر ضدّ پیامبر و مؤمنان واژگونه نکنند. اینان از نیکوکارانند و هیچ راهی برای سرزنش نیکوکاران نیست، و خدا بسیار آمرزنده و مهربان است.'),
(1327, 9, 92, 'و نیز جهاد مقرّر نشده است بر کسانی که وقتی نزد تو می‌آیند تا مَرکبی به آنان بدهی و برای جهاد آماده شان کنی، می‌گویی: من مرکبی نمی‌یابم که شما را به آن مجهّز کنم و آنان باز می‌گردند در حالی که دیدگانشان از اندوهِ این که چیزی نمی‌یابند که در راه خدا هزینه کنند، اشکبار است.'),
(1328, 9, 93, 'راه سرزنش فقط بر روی کسانی گشوده است که با این که توانگرند از تو اجازه ترک جهاد می‌خواهند. آنان به این خشنودند که با خانه نشینان باشند، و خدا بر دل هایشان مهر نهاده است، از این روست که نمی‌دانند.'),
(1329, 9, 94, 'هنگامی که از جنگ به سوی این منافقان بازگردید برای شما عذر می‌آورند. ای پیامبر، به آنان بگو: عذر نیاورید، ما هرگز سخن شما را باور نمی‌کنیم، زیرا خدا ما را از خبرهای شما تا آن جا که نفاق و دروغگویی تان را آشکار کند، آگاه ساخته است و قطعاً خدا حقیقت عمل شما را می‌بیند و پیامبرش نیز آن را مشاهده می‌کند. سپس روز قیامت به سوی خدای دانای نهان و آشکار بازگردانده می‌شوید و آن گاه شما را نیز به حقیقت آنچه می‌کردید آگاه می‌کند.'),
(1330, 9, 95, 'وقتی به سوی آنان باز گردید، برای شما به خدا سوگند می‌خورند تا از آنها درگذرید و ملامتشان نکنید. شما از آنان روی برتابید، زیرا آنها پلیدند و جایگاهشان دوزخ است. این جزایی است در برابر کارهایی که می‌کردند.'),
(1331, 9, 96, 'برای شما سوگند یاد می‌کنند تا از آنان راضی شوید. اگر شما هم از آنان راضی شوید خدا از آنان خشنود نمی‌شود، زیرا خدا از مردمی که از بندگی او خارج شده‌اند راضی نمی‌شود.'),
(1332, 9, 97, 'بادیه نشینان از آن روی که از فرهنگ و تمدن بدورند، در کفر و نفاق از دیگران سخت ترند، و سزاوارترند به این که از چند و چون معارف و احکامی که خداوند بر پیامبرش فرو فرستاده است آگاه نباشند، و خداوند بسیار دانا و حکیم است.'),
(1333, 9, 98, 'و از بادیه نشینان کسانی اند که آنچه را به عنوان زکات می‌پردازند خسارت می‌شمرند، و در انتظارند که پیشامدهای ناگوار برای شما رخ دهد تا از تحت تسلّط شما خارج شوند و به آداب و رسوم جاهلی خویش باز گردند. بر آنان باد پیشامد ناگوار! و خداوند شنونده سخنان همه و آگاه از راز دل هاست.'),
(1334, 9, 99, 'و از بادیه نشینان کسانی اند که به خدای یگانه و روز واپسین ایمان دارند و آنچه را به عنوان زکات می‌پردازند و نیز دعاهایی را که پیامبر پس از ادای زکات در حق آنان می‌کند، باعث تقرّب به خدا می‌دانند. آگاه باشید که هم زکاتشان و هم دعای پیامبر وسیله تقرّب آنهاست و به زودی خدا آنان را به رحمت خویش درمی آرد، چرا که خداوند آمرزنده گناهان و رحمت آورنده بر مؤمنان است.'),
(1335, 9, 100, 'و از میان مهاجران و انصار پیشگامان نخستینشان که در ایمان و هجرت و یاری پیامبر بر دیگران سبقت گرفتند، و نیز کسانی که پس از آنان به جرگه مؤمنان پیوستند و با پیروی نیکویی از آنان پیروی کردند، خدا از آنان خشنود شده و آنان نیز از او خشنودند، و خدا برای آنان بوستان‌هایی با درختانی انبوه که زیر آنها نهرها روان است آماده کرده است و برای همیشه در آن جا ماندگارند. این است سعادت بزرگ.'),
(1336, 9, 101, 'و برخی از بادیه نشینانی که پیرامون شمایند منافقند، و از اهل مدینه نیز گروهی بر نفاق خو گرفته‌اند. ای پیامبر، تو آنان را نمی‌شناسی، ما آنان را می‌شناسیم. به زودی آنان را دو بار (یک بار در دنیا و بار دیگر در عالم برزخ) کیفر خواهیم کرد، سپس در سرای آخرت به سوی عذابی بزرگ رانده خواهند شد.'),
(1337, 9, 102, 'و برخی دیگر از بادیه نشینان منافق نیستند، بلکه گنهکارند و به گناهان خود اعتراف کرده و کار شایسته را با کارهای دیگر که ناشایسته است به هم آمیخته‌اند (هم کارهای پسندیده کرده‌اند و هم کارهای ناپسند) که امید است خداوند با آمرزش خود به آنان روی کند و گناهانشان را ببخشاید، زیرا خداوند بسیار آمرزنده و مهربان است.'),
(1338, 9, 103, 'ای پیامبر، از اموال مردم زکات بگیر، که با گرفتن زکات از آنان پاکشان می‌سازی و آنان را رشدی نیکو می‌دهی، و پس از دریافت زکات دعا کن که خدا خیر و برکت به آنان عطا کند، همانا دعای تو باعث آرامش آنهاست، و خداوند شنونده دعاها و دانا به همه نیازها است.'),
(1339, 9, 104, 'آیا ندانسته‌اند که خداست که توبه بندگانش را می‌پذیرد و اوست که زکات‌ها را دریافت می‌کند؟ و آیا ندانسته‌اند که خداست که بسیار توبه پذیر و مهربان است؟'),
(1340, 9, 105, 'و به مردم بگو: عمل کنید و بدانید که خدا امروز حقیقت اعمال نیک و بد شما را می‌نگرد و پیامبرش نیز آنها را مشاهده می‌کند و آن گروه از مؤمنان هم که شاهد اعمال شمایند آنها را می‌نگرند و دیری نمی‌گذرد که در قیامت به سوی خدای دانای نهان و آشکار باز گردانده می‌شوید و او شما را نیز به حقیقت آنچه می‌کردید آگاه می‌کند.'),
(1341, 9, 106, 'و از بادیه نشینان مسلمان گروهی هستند که نه موجب قطعی برای عذابشان هست نه سببی برای آمرزش آنان، فرجام کارشان موقوف به فرمان خداست، یا آنان را عذاب می‌کند و یا با آمرزش خود به آنان روی می‌آورد و آنان را می‌بخشد، و خدا دانا و حکیم است و براساس علم و حکمتش با آنان رفتار خواهد کرد.'),
(1342, 9, 107, 'و برخی از منافقان کسانی اند که برای بی رونق کردن مسجد قُبا و شکستن حرمت آن مسجدی ساختند و بر آن بودند که کفر به خدا و پیامبر را در آن رواج دهند و میان مؤمنانی که در مسجد قبا اجتماع می‌کردند جدایی بیفکنند و برای کسی که پیش‌تر با خدا و رسول او به جنگ برخاسته بود پایگاهی بسازند (تا به رهبری او و کمک سپاهیان روم به مدینه یورش برند و پیامبر و مؤمنان را از آن جا بیرون کنند). و [چون بازخواست شوند] سوگند می‌خورند که از این کار جز بهترین هدف را که احداث مساجد گوناگون و تسهیل در امر نماز است در نظر نداشته‌اند، ولی خدا گواهی می‌دهد که آنان دروغ می‌گویند.'),
(1343, 9, 108, 'ای پیامبر، هرگز در آن مسجد به نماز نایست، همانا مسجدی که از روز نخست براساس تقوا بنا نهاده شده سزاوارتر است که در آن به نماز بایستی. در آن مردانی هستند که دوست دارند از آلودگی‌ها پاک شوند، و خدا کسانی را که از گناهان و پلیدی‌ها دوری می‌کنند، دوست می‌دارد.'),
(1344, 9, 109, 'پس آیا کسی که اساس دین و روش زندگی خود را بر تقوای الهی و دستیابی به خشنودی خدا نهاده بهتر است یا کسی که بنیان دین و روش زندگی اش را بر لبه بی ثبات رودی نهاده است و با آن بنا در آتش دوزخ می‌افتد؟ و خداوند مردم ستمکار رابه قرب خویش نمی‌رساند.'),
(1345, 9, 110, 'این بنایی که آنان برای دین خود ساخته‌اند همواره در دل هایشان مایه تردید است و هرگز این تردید از بین نمی‌رود مگر آن که دل هایشان پاره پاره شود، و خداوند دانا و حکیم است.'),
(1346, 9, 111, 'همانا خدا از مؤمنان، جان و مالشان را در برابر این که بهشت برای آنان باشد خریده است، بدین صورت که در راه خدا پیکار کنند و بکشند و کشته شوند. این را خداوند به عنوان وعده‌ای راست و درست بر خویش واجب کرده و در تورات و انجیل و قرآن آن را به عنوان سند معامله ثبت کرده است. و کیست که به پیمان خویش از خدا وفادارتر باشد؟ پس به این داد و ستدی که به آن دست یافته‌اید شادمان باشید، و این است آن سعادت بزرگ.'),
(1347, 9, 112, 'مؤمنان کسانی اند که از غیر خدا روی برتافته و به سوی او بازگشته‌اند و او را عبادت می‌کنند و زبانشان به ستایش او گویاست و پیوسته برای عبادت از مسجدی به مسجد دیگر در رفت و آمدند و رکوع می‌کنند و به درگاه خدا سجده می‌برند، علاوه بر اینها به وظایف اجتماعی خود عمل می‌کنند و مردم را به کار پسندیده فرا می‌خوانند و از کار ناپسند بازمی دارند و در هر حال پاسدار مقرّرات الهی‌اند. اینان را ـ ای پیامبر ـ به بهشت و سعادتی بزرگ نوید ده.'),
(1348, 9, 113, 'پیامبر و کسانی که ایمان آورده‌اند حق ندارند برای مشرکان ـ پس از آن که برایشان آشکار شده است که آنان اهل دوزخند ـ از خدا آمرزش بخواهند، هر چند خویشاوندشان باشند.'),
(1349, 9, 114, 'امّا این که ابراهیم برای پدرش آمرزش خواست، تنها به خاطر وعده‌ای بود که به او داده بود تا بلکه به یکتاپرستی روی آوَرَد، امّا همین که برای ابراهیم روشن شد که وی دشمن خداست از او تبرّی جست. به راستی ابراهیم از خوف خدا بسیار نالان بود و به همین سبب آمرزش پدرش را از خدا می‌خواست، چنان که بردبار نیز بود و جفاهای او را تحمّل می‌کرد.'),
(1350, 9, 115, 'خداوند چنین نیست که مردمی را پس از آن که هدایتشان کرده است به بیراهه افکند تا این که آنچه را باید از آن پرهیز کنند برایشان بیان کند، و پرهیز از طلب آمرزش برای مشرکان از جمله آنهاست. قطعاً خدا به هر چیزی داناست.'),
(1351, 9, 116, 'فرمانرواییِ آسمان‌ها و زمین تنها از آنِ خداست. اوست که حیات می‌بخشد و می‌میراند، و برای شما هیچ سرپرست و یاوری جز خدا نیست، پس از دوستی با مشرکان حتی به این که برای آنان آمرزش بخواهید برحذر باشید.'),
(1352, 9, 117, 'همانا خدا با رحمت خود، بر پیامبر و مهاجران و انصار که در آن لحظات سختِ رهسپار شدن به سوی جنگ تبوک از پیامبر پیروی کردند، روی آورد، پس از آن که نزدیک بود دل‌های گروهی از آنان در اثر سختی‌ها دچار لغزش شود و باز گردند، ولی خداوند با رحمت خود به آنان روی کرد و راه درست را به آنها نشان داد، زیرا خداوند با مؤمنان رئوف است و رحمت خود را از آنان دریغ نمی‌دارد.'),
(1353, 9, 118, 'و بر آن سه تن نیز که واپس نهاده شدند و با پیامبر به جنگ تبوک نرفتند با رحمت خود روی کرد، چنین بود که به سبب این گناهشان اَحَدی حتی کسانشان با آنان معاشرت نکردند تا این که زمین با همه فراخی اش بر آنان تنگ شد و از شدّت اندوه جان هایشان بر آنان تنگی کرد و دریافتند که از جانب خدا پناهی جز این ندارند که به او روی آورند، سپس خدا با رحمت خود به آنان روی کرد تا از گناه خویش توبه کنند، به یقین خداوند پذیرنده توبه بندگان است و رحمت خود را از مؤمنان دریغ نمی‌کند.'),
(1354, 9, 119, 'ای کسانی که ایمان آورده‌اید، از خدا پروا کنید و با کسانی باشید که در گفتار و عقیده راستگویند.'),
(1355, 9, 120, 'مردم مدینه و بادیه نشینان پیرامونشان حق ندارند از همگامی با رسول خدا باز بمانند و نباید به خود بپردازند و در خوشی به سر برند و او را در خطرها واگذارند. این تکلیف در برابر این است که هیچ تشنگی و رنج و گرسنگی در راه خدا به آنان نمی‌رسد و در هیچ سرزمینی که کافران را به خشم می‌آورد گام نمی‌نهند و از هیچ دشمنی غنیمتی به دست نمی‌آورند مگر این که برای آنان در برابر هر یک از آنها عملی شایسته نوشته می‌شود و پاداش آن را می‌بینند، چرا که آنان نیکوکارند و خدا پاداش نیکوکاران را تباه نمی‌کند.'),
(1356, 9, 121, 'هیچ هزینه‌ای را ـ کم یا زیاد ـ در این راه متحمل نمی‌شوند و هیچ وادیی را نمی‌پیمایند مگر این که برای آنان نوشته می‌شود، تا خدا اعمال نیکشان را بر پایه بهترین عملی که کرده‌اند به آنان پاداش دهد.'),
(1357, 9, 122, 'مؤمنان وظیفه ندارند که همه رهسپار میدان‌های نبرد شوند و از دستیابی به دانش باز مانند پس چرا از هر فرقه‌ای گروهی نزد پیامبر نمی‌روند تا دانش دین بیاموزند و هنگامی که به سوی قومشان بازگشتند آنان را از مخالفت با احکام الهی هشدار دهند، باشد که آنان بترسند؟'),
(1358, 9, 123, 'ای کسانی که ایمان آورده‌اید، با کافرانی که همجوار شما هستند پیکار کنید، و باید در شما صلابت و سرسختی بیابند، و بدانید که خدا با تقواپیشگان است و آنان را یاری می‌کند.'),
(1359, 9, 124, 'و چون سوره‌ای از قرآن نازل شود، برخی از کسانی که دلشان آلوده به نفاق است و قرآن در آن تأثیر ندارد می‌گویند: این سوره بر ایمان کدام یک از شما افزود؟ اگر چنین تأثیری ندارد در حقانیّت آن تردید است. ولی منافقان باید بدانند آنان که ایمان آورده‌اند و شک و نفاق در دلشان راه نیافته است هر سوره‌ای که نازل شود بر ایمان آنان می‌افزاید و از آن شادمان می‌شوند.'),
(1360, 9, 125, 'امّا کسانی که در دل هایشان بیماری شکّ و نفاق است، هر سوره‌ای نازل شود پلیدی و گمراهی بر پلیدی و گمراهی پیشینشان می‌افزاید و در حال کفر خواهند مرد.'),
(1361, 9, 126, 'آیا نمی‌بینند که هر سال یک یا دو بار به بلایی آزموده می‌شوند، ولی از عهده آزمایش برنمی آیند و خدا را نافرمانی می‌کنند، سپس نه توبه می‌کنند و نه متذکّر می‌شوند؟'),
(1362, 9, 127, 'و هنگامی که سوره‌ای نازل شود و آنان نزد پیامبر باشند، نگران و مضطرب به یکدیگر می‌نگرند و گویی از یکدیگر می‌پرسند: آیا کسی شما را می‌بیند و نگرانی شما را درک می‌کند؟ سپس از نزد پیامبر باز می‌گردند. خدا دل هایشان را همواره از حق برگرداند، زیرا آنان مردمی هستند که حقیقت را درک نمی‌کنند.'),
(1363, 9, 128, 'همانا پیامبری از جنس خودتان به رسالت به سوی شما آمده است که تباهی و زیان شما بر او دشوار است و سخت به شما علاقه دارد و با مؤمنان رئوف و مهربان است.'),
(1364, 9, 129, 'پس اگر روی برتافتند و رسالت تو را نپذیرفتند بگو: خدا مرا بس است، زیرا معبودی شایسته پرستش جز او نیست، فقط بر او توکّل کرده‌ام، و اوست خداوندگار آن عرش بزرگ.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الف، لام، را. این آیات بلند مرتبه که بر تو وحی می‌شود، آیات قرآن، این کتاب حکمت آمیز است.'),
(1366, 10, 2, 'آیا برای این مردم شگفت آور است که به مردی از خودشان وحی کردیم که مردم را از فرجام کفر و گناه بترسان و به کسانی که ایمان آورده‌اند نوید ده که برای آنان نزد پروردگارشان مقام و منزلتی راستین است؟ آنان که رسالت پیامبر را انکار کردند، گفتند: بی تردید، این مرد جادوگر است و جادوگری اش به خوبی آشکار است.'),
(1367, 10, 3, 'پروردگار شما که امورتان را تدبیر می‌کند خداوند است که آسمان‌ها و زمین را در شش مرحله آفرید، آن گاه بر تخت فرمانروایی استیلا یافت و به تدبیر کار جهان پرداخت. در ساماندهی امور هیچ واسطه و سببی جز پس از اذن او نیست. این است خدا که پروردگار شماست، پس او را بپرستید. چرا با وجود این همه نشانه‌ها حقیقت را درنمی یابید؟'),
(1368, 10, 4, 'بازگشت همه شما به سوی اوست. خدا این را وعده داده است، وعده‌ای راست و حقیقتی بایسته هستی. او آفریدگان را نخست پدید می‌آورَد، سپس آنها را با پایان یافتن سرآمدشان به سوی خود باز می‌گرداند تا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند به عدالت سزا دهد، امّا کسانی که کافر شدند، برای آنان به سزای آن که کفر میورزیدند نوشابه‌ای از آب جوشان و عذابی دردناک خواهد بود.'),
(1369, 10, 5, 'اوست آن که خورشید را درخشان و ماه را تابان قرار داده و برای ماه منزل‌هایی بیست و هشتگانه مقرّر داشته تا شمار سال‌ها و حساب اوقات را بدانید. خدا آنها را جز به حق نیافریده است. نشانه‌های ربوبیّت خود را برای مردمی که اهل دانشند به خوبی آشکار می‌سازد.'),
(1370, 10, 6, 'به یقین در کوتاه و بلند شدن شب و روز و آنچه خدا در آسمان‌ها و زمین آفریده است، برای تقواپیشگان نشانه‌هایی بر ربوبیّت خداست.'),
(1371, 10, 7, 'همانا کسانی که رستاخیز را انکار می‌کنند و در نتیجه به دیدار ما امیدی ندارند و به زندگی دنیا دل خوش کرده و از تلاش برای حیات اخروی باز ایستاده‌اند، و کسانی که از نشانه‌های ما بی خبرند،'),
(1372, 10, 8, 'اینان جایگاهشان به سزای کارهایی که می‌کرده‌اند آتش دوزخ است.'),
(1373, 10, 9, 'امّا کسانی که ایمان آورده و کارهای شایسته کرده‌اند، پروردگارشان آنان را در اثر ایمانشان به قرب خود رهنمون می‌شود. در بوستان‌های پر نعمت بهشت از زیر قصرهایشان نهرها روان است.'),
(1374, 10, 10, 'هنگام ورود به بهشت، زبان به تسبیح خدا می‌گشایند و نخستین ندایشان در آن جا این است که: خداوندا، تو از هر عیب و نقصی پیراسته‌ای، و درودشان در آن جا سلام است. و آخرین دعایشان این است که ستایش، همه از آنِ خدا، پروردگار جهان هاست.'),
(1375, 10, 11, 'اگر خدا در رساندن عذاب به مردم کفرپیشه، آن گونه که خواهان شتاب ما در خیر و خوبی هستند، شتاب می‌کرد، مهلتشان فرا می‌رسید و حکم عذاب در حقشان صادر می‌شد، ولی به آنان در این دنیا مهلت می‌دهد، از این رو کسانی را که رستاخیز را دروغ انگاشته‌اند و در نتیجه به دیدار ما امیدی ندارند رها می‌کنیم که در طغیانگریِ خود پیوسته سرگردان بمانند.'),
(1376, 10, 12, 'و چون به انسان رنجی رسد، برای برطرف کردن آن، ما را در هر حال ـ به پهلو افتاده یا نشسته یا ایستاده ـ می‌خواند، و چون رنجش را برطرف کنیم چنان می‌گذرد و ما را از یاد می‌برد که گویی برای رنجی که به او رسیده بود ما را نخوانده است. این گونه برای کسانی که در بهرهوری از لذّت‌های دنیا زیاده روی می‌کنند، اعمالشان آراسته شده است.'),
(1377, 10, 13, 'به یقین ما امّت‌هایی را که پیش از شما بودند، هنگامی به هلاکت رساندیم که با دروغ انگاشتن رستاخیز ستم پیشه نمودند، در حالی که پیامبرانشان با دلایل روشن و روشنگر به سویشان آمدند ولی آنان درخور آن نبودند که ایمان بیاورند. این گونه، مردم گنهکار را جزا می‌دهیم.'),
(1378, 10, 14, 'سپس شما را بعد از آنان در زمین جانشین قرار دادیم تا بنگریم که چگونه عمل می‌کنید.'),
(1379, 10, 15, 'و هنگامی که آیات ما که الهی بودن آنها به خوبی روشن است بر مشرکان تلاوت شود، آنان که رستاخیز را دروغ پنداشته‌اند و به دیدار ما امیدی ندارند به پیامبر می‌گویند: قرآنی غیر از این بیاور که آیاتش برای ما قابل قبول باشد، یا تغییر و تحولی در آن پدید آور که با آرای ما سازگار باشد. بگو: من حقّ ندارم آن را از پیش خود دگرگون کنم. این سخن خداست که به من وحی کرده است و من جز آنچه را که به من وحی می‌شود پیروی نمی‌کنم، زیرا اگر پروردگارم را نافرمانی کنم از عذاب روزی بزرگ بیمناکم.'),
(1380, 10, 16, 'بگو: اگر خدا می‌خواست قرآنی دیگر نازل کند، من این قرآن را بر شما تلاوت نمی‌کردم و او شما را از آن آگاه نمی‌کرد. بی گمان من پیش از نزول قرآن عمری را در میان شما گذراندم و از وحی و قرآن چیزی به شما نگفتم، چرا نمی‌اندیشید تا دریابید که آوردن قرآن در اختیار من نیست؟'),
(1381, 10, 17, 'من اگر تغییری در این قرآن پدید آورم به خدا دروغ بسته‌ام، و کیست ستمکارتر از آن کس که بر خدا دروغ بندد؟ و اگر قرآنی جز این بیاورم آیات خدا را دروغ شمرده‌ام، و کیست ستمکارتر از آن کس که آیات خدا را دروغ انگارد؟ و در هر دو صورت بزرگ‌ترین گناه را مرتکب شده‌ام و حقیقت این است که گنهکاران نیکبخت نمی‌شوند.'),
(1382, 10, 18, 'مشرکان به جای خدا چیزهایی را می‌پرستند که نه زیانی به آنان می‌رساند و نه سودی عایدشان می‌کند، و در توجیه آن می‌گویند: آنها شفیعان ما نزد خدایند. به آنان بگو: آیا می‌خواهید خدا را به چیزی خبر دهید که در آسمان‌ها و زمین از آن آگاهی ندارد با این که خدا از همه چیز آگاه است؟ او منزّه و برتر از این است که به او شرک ورزند.'),
(1383, 10, 19, 'و مردم در آغاز جز امّتی یگانه نبودند و همگان بر آیین توحید می‌زیستند. سپس اختلاف کردند، گروهی بر توحید پایدار ماندند و گروهی به شرک گرایش یافتند. و اگر پیش‌تر از جانب پروردگارت سخنی نرفته بود که آدمیان تا مدّتی در زمین مستقر باشند، قطعاً میان آنان درباره آنچه بر سرش اختلاف می‌کردند داوری می‌شد و مشرکان به هلاکت می‌رسیدند.'),
(1384, 10, 20, 'مشرکان قرآن را نشانه رسالت پیامبر نمی‌دانند و می‌گویند: چرا از جانب پروردگارش معجزه‌ای بر او فرستاده نمی‌شود؟ بگو: معجزات از امور غیبی هستند، و غیب در اختیار خدا است نه در اختیار من، پس منتظر باشید که من نیز با شما از منتظرانم.'),
(1385, 10, 21, 'و هنگامی که به این مردم شرک پیشه پس از گزندی که به آنان رسیده است رحمتی بچشانیم، بی آن که انتظار رود، در نشانه‌هایی که بیانگر یکتایی ماست نیرنگ به کار می‌برند تا روشنگری آنها را مخدوش کنند. بگو: نیرنگ خدا با شما سریع‌تر است، چرا که فرستادگان ما از میان فرشتگان هر نیرنگی را به کار برید ثبت می‌کنند و ما از آن آگاهیم.'),
(1386, 10, 22, 'اوست آن که شما را در خشکی و دریا سیر می‌دهد، تا آن گاه که در کشتی‌ها استقرار یابید و کشتی‌ها به کمک بادی ملایم سرنشینان را به حرکت درآورند و آنان بدان شادمان گردند، ناگاه تند بادی بر کشتی‌ها بوزد و از هر سو موجی به سویشان روی آورَد و دریابند که از هر طرف در محاصره قرار گرفته و در معرض هلاکتند، آن گاه خدا را در حالی که اطاعت و پرستش را ویژه او می‌کنند می‌خوانند که: خدایا، اگر ما را از این گرفتاری نجات بخشی، قطعاً از سپاسگزاران خواهیم بود.'),
(1387, 10, 23, 'پس هنگامی که خداوند نجاتشان دهد بی آن که انتظار رود، به ناحق در زمین ستم می‌کنند. ای مردم، ستمکاری شما بر ضد خودتان است، چرا که شما را از ما دور می‌کند و گناهش در کارنامه شما ثبت می‌شود. در زندگی دنیا بهره ای می‌برید، سپس بازگشتتان به سوی ماست، آن گاه شما را به حقیقت آنچه می‌کردید آگاه خواهیم کرد.'),
(1388, 10, 24, 'جز این نیست که داستان زندگانی دنیا بسان آبی است که از آسمان فرو ریختیم، پس روییدنی‌های زمین ـ از آنچه مردم و دام‌ها می‌خورند ـ با آن درآمیخت، تا چون زمین با رویش گیاهان آرایش و شادابی خود را برگرفت و اهل آن پنداشتند که زمین را با قدرت تمام در اختیار دارند، شبی یا روزی فرمان ما دررسیدش پس آن را چنان درو شده گرداندیم که گویی دیروز در آن جا چیزی نبوده است. این گونه، آیات خود را برای مردمی که می‌اندیشند به روشنی بیان می‌کنیم.'),
(1389, 10, 25, 'و خداوند [همگان را] به سرای سلامت، همان بهشتی که هیچ گزند و آفتی در آن نیست فرا می‌خوانَد، و هر که را بخواهد به راهی راست می‌رساند.'),
(1390, 10, 26, 'برای کسانی که کارهای نیکو کرده‌اند، بهترین پاداش و پاداشی افزون بر آن خواهد بود. بر چهره هایشان هیچ غبار سیاهی نمی‌نشیند و هیچ ذلّتی آنها را فرا نمی‌گیرد. آنان اهل بهشتند و در آن جاودانه‌اند.'),
(1391, 10, 27, 'و کسانی که مرتکب بدی‌ها شده‌اند، سزایشان در برابر هر بدی عذابی مانند آن است و غبار ذلّت آنان را فرا می‌گیرد. در برابر کیفر الهی هیچ بازدارنده‌ای برایشان نیست. چهره هایشان چنان سیاه می‌گردد که گویی با پاره‌هایی از شب تار پوشانده شده است. آنان همدم آتش دوزخند و در آن جاودانه‌اند.'),
(1392, 10, 28, 'و یاد کن روزی را که آنان (مؤمنان و مشرکان و معبودانشان) را یکجا برمی انگیزیم و گرد می‌آوریم، سپس به کسانی که شرک ورزیده اند می‌گوییم: شما و معبودانتان که آنها را شریک خدا می‌پنداشتید، در جای خود بمانید، آن گاه با آشکار ساختن این حقیقت که رابطه عبادت میان آنان بر اساس وهم و خیال بوده است، میانشان جدایی می‌افکنیم، و شریکانی که برای خدا تصور می‌کردند می‌گویند: شما ما را نمی‌پرستیدید، بلکه شریکان خدا را پرستش می‌کردید و ما شریک خدا نیستیم چرا که خدا را هیچ شریکی نیست.'),
(1393, 10, 29, 'پس خدا میان ما و شما گواه است و گواهی او کافی است، قطعاً ما از پرستش شما بی خبر بودیم.'),
(1394, 10, 30, 'آن جاست که هر کسی هر عملی را که پیش فرستاده است می‌آزماید و حقیقت کارهای خود را آشکارا می‌بیند، و همگان به سوی خداوند که سرپرست واقعی آنهاست بازگردانده می‌شوند، و آنچه به دروغ بافته بودند از فکرشان زدوده می‌شود.'),
(1395, 10, 31, 'ای پیامبر، برای اثبات ربوبیّت خدا از مشرکان بپرس: کیست که با فرو ریختن باران از آسمان و رویاندن گیاهان از زمین به شما روزی می‌دهد؟ یا کیست که گوش‌ها و چشم‌های شما را در اختیار دارد و به آنها شنوایی و بینایی می‌دهد؟ و کیست که زنده را از مرده و مرده را از زنده بیرون می‌آورد؟ و کیست که کار جهان هستی را تدبیر می‌کند؟ خواهند گفت: همه کارها به خدا باز می‌گردد و سر رشته هر چیزی به دست اوست. پس بگو: چرا از خدا پروا نمی‌کنید و غیر او را می‌پرستید؟'),
(1396, 10, 32, 'این است خدا که پروردگار راستین شماست. بنابراین پس از حقّ که هدایت را در پی دارد، چیست جز باطل که پیامدش گمراهی است. تا کی شما را از حق باز می‌گردانند و به سوی باطل روانه می‌کنند؟'),
(1397, 10, 33, 'این گونه، قضای پروردگارت بر کسانی که از اطاعت خدا خارج شدند محقق شده است که آنان ایمان نیاورند.'),
(1398, 10, 34, 'ای پیامبر، به مشرکان بگو: آیا از میان معبودانتان که آنها را شریک خدا می‌پندارید کسی هست که آفریدگان را نخست پدید آورَد و آن گاه با پایان یافتن سرآمدشان آنها را به سوی خود باز گرداند و هر کسی را به سزای کردارش برساند؟ خود در پاسخ بگو: فقط خداست که آفریدگان را نخست پدید می‌آورد و سپس آنها را به سوی خود باز می‌گرداند، لذا او پروردگار شماست و باید او را پرستش کنید. پس تا کی شما را از حق باز می‌دارند و به سوی باطل روانه می‌کنند؟'),
(1399, 10, 35, 'به آنان بگو: آیا از میان معبودانتان که آنها را شریک خدا قرار داده‌اید کسی هست که پدیده‌های جهان هستی از جمله آدمیان را به سوی حق (هدف آفرینش آنها) راه نماید؟ خود در پاسخ بگو: فقط خداست که آفریده‌ها را به سوی حق رهنمون می‌شود پس آیا کسی که پدیده‌های جهان هستی را به اهدافشان راه می‌نماید سزاوارتر است که از او پیروی شود یا آن کسی که خود راه درست را نمی‌یابد مگر این که او را راه نمایند؟ شما را چه شده است؟ چگونه حکم می‌کنید؟'),
(1400, 10, 36, 'و بیشترشان جز از پی گمان نمی‌روند، در صورتی که گمان برای رسیدن به حق هیچ بسنده نیست. قطعاً خدا آنچه را براساس پندار عمل می‌کنند می‌داند.'),
(1401, 10, 37, 'نشاید که این قرآن از جانب غیر خدا و به دروغ به او نسبت داده شده باشد، بلکه تصدیق کننده معارف و احکام تورات و انجیل است که پیشاروی آن قرار دارند و بیان کننده حقایق کتاب‌های آسمانی دیگر است. در این قرآن هیچ گونه تردیدی نیست چرا که از جانب پروردگار جهان هاست.'),
(1402, 10, 38, 'آیا می‌گویند: محمّد خود آن را ساخته و به خدا نسبت داده است؟ بگو: اگر در این ادّعا راست می‌گویید، شما هم باید سوره‌ای مانند آن بیاورید و جز خدا هر که را می‌توانید به یاری فراخوانید.'),
(1403, 10, 39, '[آنان قرآن را نه از آن روی که ساخته بشر انگاشتند تکذیب کردند،] بلکه چون به علم آن احاطه نیافتند و هنوز حقیقت و خاستگاه آن که در آخرت آشکار می‌شود به ایشان نرسیده است آن را دروغ شمردند، و درست به همین دلیل کسانی هم که پیش از آنان بودند پیام رسولانشان را دروغ انگاشتند و ستمکار شدند. پس بنگر که فرجام آن ستمکاران چگونه بود تا بدانی این ستمکاران چه فرجامی خواهند داشت.'),
(1404, 10, 40, 'و برخی از اینان کسانی اند که به قرآن ایمان می‌آورند و برخی هم به آن ایمان نمی‌آورند، زیرا راه فساد در پیش گرفته‌اند، و پروردگارت از هر کسی به فسادگران داناتر است.'),
(1405, 10, 41, 'و اگر تو را تکذیب کردند، بگو: عمل من از آنِ من، و عمل شما از آنِ شماست. شما از آنچه من می‌کنم برکنارید، و من نیز از آنچه شما می‌کنید برکنارم.'),
(1406, 10, 42, 'و برخی از مشرکان به سخنان تو گوش فرا می‌دهند، در حالی که دل هایشان از درک آن در حجاب است. آیا تو می‌توانی به کران سخنی بشنوانی، هر چند نیندیشند و توانِ درک آن را نداشته باشند؟'),
(1407, 10, 43, 'از مشرکان کسانی اند که به تو می‌نگرند ولی کوردلند، آیا تو می‌توانی کوردلان را هر چند از دیدن حق ناتوان باشند راه بنمایی؟'),
(1408, 10, 44, 'قطعاً خدا بر مردم هیچ ستمی روا نمی‌دارد، بلکه این مردمند که با کر و کور ساختن خود، بر خود ستم می‌کنند.'),
(1409, 10, 45, 'و روزی که خدا آنان را محشور کند در حالی که احساس می‌کنند که گویی جز به اندازه ساعتی از روز در دنیا درنگ نکرده‌اند، و یکدیگر را می‌شناسند، آن روز کسانی که دیدار خدا و قیامت را دروغ انگاشته‌اند خواهند دانست که زیان کرده و رهیافته نبوده‌اند.'),
(1410, 10, 46, 'ای پیامبر، بدان که وعده‌های ما در حقّ کفرپیشگان تحقق خواهد یافت. اگر ما بخشی از کیفرهایی را که به آنان وعده می‌دهیم به تو بنمایانیم یا پیش از آن، جان تو را بستانیم، در هر صورت بازگشتشان به سوی ما و فرجامشان در اختیار ماست. علاوه بر این، خدا بر آنچه می‌کنند گواه است.'),
(1411, 10, 47, 'و هر امّتی پیامبری دارد که رسالت خود را به آنان ابلاغ می‌کند، پس وقتی پیامبرشان به سویشان بیاید و پیام خود را به آنان برساند، گروهی به او ایمان می‌آورند و گروهی به وی کفر میورزند، آن گاه خدا میان آنان به عدالت داوری می‌کند و مورد ستم قرار نمی‌گیرند.'),
(1412, 10, 48, 'و مشرکان به تمسخر [از تو و پیروانت] می‌پرسند: اگر راست می‌گویید، این وعده عذاب کی تحقق می‌یابد؟'),
(1413, 10, 49, 'بگو: من قدرت ندارم نه زیانی را از خود دور سازم و نه سودی را به سوی خود جلب کنم، مگر آنچه را که خدا بخواهد و مرا بر آن توانا سازد، لذا نمی‌توانم عذابی بر شما فرود آورم، اما بدانید که هر امّتی مهلتی دارد که هرگاه مهلتشان به پایان رسد، نه لحظه‌ای از آن باز می‌مانند و نه لحظه‌ای پیش می‌افتند.'),
(1414, 10, 50, 'بگو: به من خبر دهید، اگر عذاب الهی در شبی یا روزی بر شما در رسد که هرگز از شما بازداشته نمی‌شود، گناه پیشگان خواهانِ شتاب در چه بخشی از آنند؟'),
(1415, 10, 51, 'وانگهی، آیا هنگامی که عذاب دررسید، به قرآن ایمان می‌آورید؟ آیا اکنون ایمان می‌آورید تا عذاب از شما برداشته شود، در حالی که پیش از آن می‌خواستید هرچه زودتر به سراغتان آید؟'),
(1416, 10, 52, 'آن گاه پس از پایان مهلت هر امّتی، به کسانی که ستمکار بوده‌اند گفته می‌شود: بچشید عذاب جاودانه را، آیا جز به گناهانی که دست آورد خودتان است سزا داده می‌شوید؟'),
(1417, 10, 53, 'از تو می‌پرسند که آیا این عذاب موعود حق است؟ بگو: آری، به پروردگارم سوگند که قطعاً آن حق است و شما نمی‌توانید آن را از خود باز دارید و از آن بگریزید.'),
(1418, 10, 54, 'و اگر آنچه در زمین است از آنِ کسی باشد که ستم کرده است، قطعاً آن را برای رهایی خود از عذاب خواهد داد. و چون ستمکاران عذاب را ببینند از کرده خود پشیمان می‌شوند، ولی از بیم سرزنش پشیمانی خود را نهان می‌دارند، و میانشان به عدالت داوری می‌شود و بر آنان ستمی نمی‌رود.'),
(1419, 10, 55, 'بدانید که آنچه در آسمان‌ها و زمین است از آنِ خداست و هرگونه تصرّفی در آنها بخواهد می‌کند. بدین سبب وعده خدا به آمدن عذاب حق است و کسی نمی‌تواند آن را بازدارد، ولی بیشتر مردم چون مالکیت خدا را به خوبی درنیافته اند نمی‌دانند که تحقّق وعده‌های او تردیدناپذیر است.'),
(1420, 10, 56, 'اوست که به شما حیات می‌بخشد و جان شما را می‌ستاند و به سوی او بازگردانده می‌شوید، پس چگونه می‌توانید در اختیار او نباشید؟'),
(1421, 10, 57, 'ای مردم، برای شما از جانب پروردگارتان پندی هشیارکننده و درمانی برای آنچه از صفات نکوهیده در سینه هاست، و رهنمود و رحمتی برای مؤمنان، آمده است.'),
(1422, 10, 58, 'ای پیامبر، بگو: به فضل و رحمت خدا، آری به این دو، که مایه سعادت آنان است شادمان باشند نه به ثروت و امکانات. این از هر ثروتی که فراهم می‌آورند بهتر است.'),
(1423, 10, 59, 'بگو: به من خبر دهید، رزقی را که خدا برای شما فرو فرستاده و شما بخشی از آن را حرام و بخشی را حلال دانسته‌اید، بگو: آیا خدا به شما اجازه داده است که این احکام را صادر کنید یا با این کار بر خدا دروغ می‌بندید؟'),
(1424, 10, 60, 'کسانی که بر خدا دروغ می‌بندند و نعمت‌های او را ناسپاسی می‌کنند، به فرجام خود در روز قیامت چه گمان می‌برند؟ بی تردید، خداوند از فضل خود به مردم عطا می‌کند ولی بیشترشان ناسپاسی می‌کنند و با حرام شمردن بخشی از آنها شکر نعمت‌های خدا را پاس نمی‌دارند.'),
(1425, 10, 61, 'ای پیامبر، در هیچ کاری نمی‌باشی و هیچ بخشی از قرآن را که از سوی خدا بر تو نازل می‌شود تلاوت نمی‌کنی، و تو و دیگران هیچ کاری را انجام نمی‌دهید مگر این که ما شما را می‌بینیم و بر کارتان آن گاه که بدان می‌پردازید گواهیم، و هموزن ذرّه‌ای نه در زمین و نه در آسمان از پروردگار تو پنهان نمی‌ماند، و چیزی کوچک‌تر از ذرّه و بزرگ‌تر از آن نیست مگر این که در کتابی روشن محفوظ است.'),
(1426, 10, 62, 'آگاه باشید که دوستان خدا را در دنیا و آخرت هیچ ترسی فرا نمی‌گیرد و اندوهگین نمی‌شوند.'),
(1427, 10, 63, 'آنان کسانی اند که به خدا و پیامبر ایمان آورده و در برابر خدا تسلیم اند و پیش از این نیز تقواپیشه بوده‌اند.'),
(1428, 10, 64, 'برای آنان در زندگی دنیا و در آخرت بشارت است. این سخن و تقدیر الهی است و سخنان و تقدیرات خدا را دگرگونی نیست. این است سعادت بزرگ.'),
(1429, 10, 65, 'ای پیامبر، مبادا سخنان ناروای مشرکان درباره خدا و دینِ او تو را اندوهگین کند، چرا که عزّت به تمامی از آنِ خداست و مشرکان و معبودانشان هیچ نصیبی از عزّت ندارند، و اوست که سخنان آنان را می‌شنود و به حال تو و آنان آگاه است و هرگاه بخواهد آنان را به سزای کردارشان می‌رساند.'),
(1430, 10, 66, 'بدانید که هر که در آسمان‌ها و هر که در زمین است از آنِ خداست و کارش به دست اوست و کسانی که غیر خدا را شریک او می‌خوانند، در حقیقت از آن شریکان پیروی نمی‌کنند، زیرا آنها واقعیّتی ندارند. آنان جز پیرو گمان و پندار خود نیستند و جز به حدس سخن نمی‌گویند.'),
(1431, 10, 67, 'اوست آن که شب را برای شما پدید آورْد تا در آن آرام گیرید و روز را روشن آفرید. به راستی در اینها نشانه‌هایی بر یکتایی خداست، و تنها کسانی که گوش شنوا دارند سخن حق را شنیده و نشانه‌ها را درک می‌کنند.'),
(1432, 10, 68, 'گفتند: خدا فرزندی برای خود برگرفته است. او پاک و منزّه است از این که نیازمند فرزندی باشد. او از همه چیز بی نیاز است. آنچه در آسمان‌ها و آنچه در زمین است ملک اوست. شما هیچ برهانی بر ادّعای خویش ندارید. آیا چیزی را که نمی‌دانید، به دروغ به خدا نسبت می‌دهید؟'),
(1433, 10, 69, 'بگو: قطعاً کسانی که بر خدا دروغ می‌بندند نیکبخت نمی‌شوند.'),
(1434, 10, 70, 'در دنیا اندک بهره‌ای دارند، سپس بازگشتشان به سوی ماست، آن گاه به سزای این که کفر ورزیدند عذاب سخت را به ایشان می‌چشانیم.'),
(1435, 10, 71, 'و برای عبرت گرفتن کسانی که تو را تکذیب می‌کنند داستان نوح را برای آنان بخوان، آن گاه که به تنهایی در برابر قوم خود قرار گرفت و آنان را به مبارزه فرا خواند و گفت: ای قوم من، اگر قیام من برای دعوت شما به توحید و این که آیات خدا را برای شما یادآوری می‌کنم بر شما گران است، من بر خدا توکّل کرده‌ام، پس شما با معبودانتان که آنها را شریک خدا و یاور خود می‌پندارید، عزم خود را در کار خویش جزم کنید، سپس همه نیروهایتان را فراهم آورید تا در صورت شکست، کارتان برای شما مایه اندوه نشود، آن گاه کارم را یکسره کنید و مهلتم ندهید.'),
(1436, 10, 72, 'و اگر از دعوت من روی برتافتید، زیانی متوجّه من نمی‌شود، زیرا من مزدی از شما نخواسته‌ام. پاداش من جز برعهده خدا نیست. من فرمان یافته‌ام که از جمله کسانی باشم که در برابر خدا تسلیم اند و کارهای خود را به او سپرده‌اند.'),
(1437, 10, 73, 'با این وصف، او را تکذیب کردند، پس ما او و کسانی را که در کشتی با وی بودند، نجات بخشیدیم و آنان را در زمین جایگزین پیشینیان قرار دادیم، و کسانی را که آیات ما را دروغ انگاشتند، در سیلابی غرق کردیم. پس بنگر که فرجام هشدار داده شدگان چگونه بود.'),
(1438, 10, 74, 'آن گاه پس از او پیامبرانی را به سوی قومشان به رسالت برانگیختیم، ولی آنان را تکذیب کردند، سپس پیامبرانشان معجزاتی را که آنان پیشنهاد می‌کردند برایشان آوردند، اما آنان درخورِ این نبودند که به چیزی که قبلاً آن را دروغ شمرده بودند ایمان بیاورند، زیرا ما بر دل هایشان مُهر نهاده بودیم، و این گونه بر دل‌های تجاوزکاران مهر می‌نهیم.'),
(1439, 10, 75, 'سپس موسی و هارون را بعد از آن پیامبران با معجزاتی که نشان دهنده قدرت ما بود، به سوی فرعون و مهتران قومش (قِبطیان) فرستادیم، ولی آنان در برابر حق گردنفرازی کردند، زیرا مردمی گناه پیشه بودند.'),
(1440, 10, 76, 'چنین بود که وقتی آن معجزه حق از جانب ما برایشان آمد گفتند: بی تردید این جادویی است آشکار.'),
(1441, 10, 77, 'موسی گفت: آیا وقتی این معجزه راستین برای شما آمد آن را افسون می‌خوانید؟ آیا به راستی این افسون است؟ و حال آن که افسونگران نیکبخت نمی‌شوند.'),
(1442, 10, 78, 'گفتند: آیا با ادّعای پیامبری نزد ما آمده‌ای تا ما را از آداب و رسومی که پدرانمان را بر آن یافته‌ایم باز داری و با این شیوه به ریاست برسی و بزرگی و پیشوایی در این سرزمین از آنِ شما دو تن باشد؟ ما هرگز به شما ایمان نمی‌آوریم و شما را در هدف خود ناکام خواهیم گذاشت.'),
(1443, 10, 79, 'فرعون به کارگزارانش گفت: هر جادوگر ماهری را نزد من آورید.'),
(1444, 10, 80, 'هنگامی که جادوگران به میدان آمدند و با موسی رو به رو شدند، موسی به آنان گفت: هر چه را می‌خواهید بیفکنید، بیفکنید و جادوی خود را نشان دهید.'),
(1445, 10, 81, 'پس وقتی ریسمان‌ها و عصاهای خود را افکندند، موسی به آنان گفت: آنچه آورده‌اید همه جادوست و به زودی خدا آن را باطل می‌کند، چرا که خداوند کار مفسدان را به سامان نمی‌آورد.'),
(1446, 10, 82, 'و خداوند به حکم سنّت و مقدّرات خود، حق را به اثبات می‌رساند، هر چند گناه پیشگان آن را خوش نداشته باشند.'),
(1447, 10, 83, 'پس از بعثت موسی و سرباز زدن فرعون از پذیرش رسالت او، جز توده‌هایی از قوم موسی آن هم با بیم از فرعون و مهتران قوم خود به او ایمان نیاوردند. آنها می‌ترسیدند که فرعون به خاطر ایمانشان آنان را شکنجه کند، و جای این ترس هم بود، زیرا فرعون در آن سرزمین زمامداری چیره دست بود و در ستمکاری حدّ و مرزی نمی‌شناخت.'),
(1448, 10, 84, 'موسی به قوم خویش گفت: اگر همان گونه که حال و کارتان پیداست به خدا ایمان آورده‌اید و اگر امور خود را به او تسلیم کرده‌اید که سزاوار است چنین باشید، باید بر او توکّل کنید و آنچه را که برای شما خواسته است بپذیرید.'),
(1449, 10, 85, 'گفتند: ما فقط بر خدا توکّل کرده‌ایم. پس به درگاه خدا دعا کردند که: پروردگارا، ناتوانی و ذلّت را از ما بردار تا وسیله آزمون این مردم ستمکار نباشیم،'),
(1450, 10, 86, 'و ما را به رحمت خویش از چنگال این مردم کفرپیشه رهایی بخش.'),
(1451, 10, 87, 'و به موسی و برادرش وحی کردیم که برای قوم خود در مصر خانه‌هایی فراهم آورید، زیرا آنان باید چادرنشینی و پراکندگی را رها کنند، و شما و آنان خانه هایتان را رو به روی هم قرار دهید و نماز برپا دارید، و به موسی گفتیم: به مؤمنانِ قوم خود نوید ده که خداوند دعایشان را اجابت کرده و آنان را از ذلّت و زبونی و آزار فرعونیان رهایی خواهد بخشید.'),
(1452, 10, 88, 'موسی گفت: پروردگارا، تو به فرعون و مهتران قومش در زندگی دنیا زیور و اموالی داده‌ای تا پروردگارا، پیروانشان را از راه تو منحرف سازند. پروردگارا، اموالشان را روی به نیستی کشان و آن نعمت‌ها را برای آنان به بلا تبدیل کن و دل هایشان را چنان سخت گردان که ایمان نیاورند تا وقتی که آن عذاب دردناک را ببینند.'),
(1453, 10, 89, 'خدا به موسی و هارون وحی کرد که دعای شما دو تن درباره فرعونیان اجابت شد، پس بر فراخواندن مردم به سوی خدا پایداری کنید و راه و روش نادانان را در انجام کارهای جاهلانه‌ای که از شما می‌خواهند دنبال نکنید.'),
(1454, 10, 90, 'و بنی اسرائیل را از آن دریا پس از شکافتن آب عبور دادیم. پس فرعون و سپاهیانش از روی ستم و دشمنی آنان را دنبال کردند و وارد شکاف دریا شدند. تا آن گاه که فرعون را غرق شدن دررسید، گفت: ایمان آوردم که معبودی شایسته پرستش جز آن کسی که بنی اسرائیل به او ایمان آورده‌اند، نیست، و من از کسانی هستم که تسلیم او شده‌اند.'),
(1455, 10, 91, 'به او گفته شد: آیا اینک به خدا ایمان آوردی، در صورتی که پیش‌تر او را نافرمانی کردی و از فسادگران بودی؟'),
(1456, 10, 92, 'پس امروز تو را با بیرون انداختن پیکرت از دریا، نجات می‌دهیم تا برای کسانی که پس از تو می‌آیند نشانه‌ای باشی، و قطعاً بسیاری از مردم از نشانه‌های ما بی خبرند.'),
(1457, 10, 93, 'و همانا ما بنی اسرائیل را در سرزمینی مناسب که دارای شرایط یک زندگی نیکو بود سکونت دادیم و از چیزهای پاکیزه به آنان روزی دادیم، ولی پس از چندی راه تفرقه و اختلاف را دنبال کردند، و اختلافشان جز پس از فراهم آمدن علم و دانش برایشان نبود، از این رو در پیشگاه خدا عذری نخواهند داشت. بی تردید پروردگار تو در روز قیامت میان آنان درباره آنچه بر سرش اختلاف می‌کردند داوری خواهد کرد.'),
(1458, 10, 94, 'اگر درباره حقایقی که در این سوره بر تو فرو فرستاده‌ایم تردید داری، از کسانی که پیش از تو کتاب آسمانی می‌خوانده‌اند بپرس. به یقین که از جانب پروردگارت چیزی به تو رسیده است که سراسر حق است، پس مبادا از تردیدکنندگان باشی.'),
(1459, 10, 95, 'و از کسانی مباش که آیات خدا را دروغ انگاشتند، که در این صورت سرمایه ایمان را از دست خواهی داد و از زیانکاران خواهی شد.'),
(1460, 10, 96, 'به یقین، کسانی که سخن پروردگارت (وعده عذاب) درباره آنان تحقق یافته است ایمان نمی‌آورند.'),
(1461, 10, 97, 'گرچه هرگونه نشانه و معجزه‌ای برایشان بیاید، تا این که آن عذاب دردناک را ببینند، که دیگر در آن حال ایمانشان سودی برایشان نخواهد داشت.'),
(1462, 10, 98, 'پس چرا در میان شهرهایی که مردمش پیامبران را تکذیب کردند و عذاب بر آنان مقرر شد، مردم هیچ شهری پیش از نزول عذاب ایمان نیاوردند که ایمانشان سودشان دهد؟ تنها قوم یونس بودند که پیش از نزول عذاب ایمان آوردند و آن گاه که ایمان آوردند ما عذاب خواری را در زندگی دنیا از آنان برداشتیم و تا مدّتی که عمر طبیعی آنان بود از زندگی بهره مندشان ساختیم.'),
(1463, 10, 99, 'و اگر پروردگار تو می‌خواست، قطعاً همه کسانی که در این جهانند یکسره ایمان می‌آوردند، ولی خدا چنین نخواسته است. اینک آیا تو می‌خواهی مردم را بی آن که خود خواسته باشند وادار کنی تا ایمان بیاورند؟ نه تو بر این کار توانایی و نه این گونه ایمان کارساز است.'),
(1464, 10, 100, 'هیچ کس نمی‌تواند جز به اذن خدا ایمان بیاورد، و خداوند بر کسانی که تعقل نمی‌کنند و آیات خدا را از روی لجاجت دروغ می‌شمرند، پلیدیِ شک و تردید را قرار می‌دهد، از این رو درخورِ آن نیستند که به آنان رخصتِ ایمان دهد.'),
(1465, 10, 101, 'بگو: به آنچه در آسمان‌ها و زمین است بنگرید، همه آنها نشانه‌های قدرت خدایند که شما را به ایمان فرا می‌خوانند، ولی نشانه‌ها و هشدارها در مردمی که نمی‌خواهند ایمان بیاورند چه سودی خواهد داشت؟'),
(1466, 10, 102, 'آیا برای ایمان آوردن خود جز فرا رسیدن روزهایی مانند روزهای کسانی را که پیش از آنان بودند و به عذاب هلاک شدند انتظار می‌برند؟ ولی بدانند که در آن هنگام ایمانشان برایشان سودی ندارد. به آنان بگو: پس در انتظار آن روزها باشید که من نیز با شما از منتظرانم.'),
(1467, 10, 103, 'بر آن امّت‌ها عذاب می‌فرستادیم آنگاه پیامبران خود و کسانی را که ایمان آورده بودند نجات می‌دادیم. بدین سان برعهده ماست که مؤمنان امّت تو را نیز از عذاب رهایی بخشیم.'),
(1468, 10, 104, 'بگو: ای مردم، اگر در پایداری من بر آیین خودم تردید دارید و امید بسته‌اید که از آن دست بردارم، بدانید که من کسانی را که شما آنها را به جای خدا می‌پرستید نمی‌پرستم، بلکه فقط خدایی را می‌پرستم که جان هایتان را می‌ستاند، و به من فرمان داده شده است که در زمره مؤمنان باشم تا از عذاب تکذیب کنندگان دین در امان بمانم.'),
(1469, 10, 105, 'و نیز به من فرمان داده شده است که: روی خود را بدون انحراف به چپ و راست به سوی اسلام کن و هرگز از آن غفلت مکن و از مشرکان مباش.'),
(1470, 10, 106, 'و به جای خدا، چیزهایی را که به تو سود و زیانی نمی‌رسانند، مخوان، که اگر چنین کنی، قطعاً تو در آن صورت از ستمکارانی.'),
(1471, 10, 107, 'و اگر خدا به تو گزندی برساند، هیچ کس جز او آن را برطرف نخواهد کرد، و اگر برای تو خیری بخواهد، هیچ کس نمی‌تواند بخشش او را باز دارد. آن را به هر کس از بندگانش که بخواهد می‌رساند، و اوست که گناهان بندگانش را می‌بخشد و بر آنان رحمت می‌آورد.'),
(1472, 10, 108, 'بگو: ای مردم، این قرآن که سراسر حق است از جانب پروردگارتان برای شما آمده است، پس هر که بدان هدایت یابد، تنها به سود خود هدایت یافته است، و هر که گمراه گردد، تنها به زیان خود گمراه شده است. و بگو: من بر شما گمارده نشده‌ام و امورتان در اختیار من نیست تا بتوانم رهنمودهای این کتاب را بر شما تحمیل کنم.'),
(1473, 10, 109, 'ای پیامبر، از آنچه به تو وحی می‌شود پیروی کن و بر سختی‌هایی که در این راه به تو می‌رسد شکیبا باش تا خدا میان تو و مردم داوری کند، که او بهترین داوران است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الف. لام. را. این قرآن کتابی است از جانب حکیمی آگاه که آیات آن حقیقتی واحد دارد و هدف واحدی را دنبال می‌کند و در عین حال شرح داده شده و به شاخه‌ها و فصل‌های گوناگون تقسیم گردیده است.'),
(1475, 11, 2, 'آن حقیقت این است که: جز خدا را نپرستید. به راستی من از جانب او برای شما هشداردهنده و بشارتگرم.'),
(1476, 11, 3, 'و این که از پروردگارتان آمرزش گناهان خود را بخواهید، سپس با انجام کارهای شایسته به سوی او باز گردید که در آن صورت خداوند شما را تا مدّتی معیّن که سرآمد عمرتان است از بهره‌ای نیکو برخوردار می‌سازد و به هر کس که بر دیگران فضیلتی دارد و درخور پاداشی بیشتر است، پاداش فضیلت او را عطا می‌کند، و اگر از پرستش خدا روی برتابید، من از عذاب روزی بزرگ بر شما بیمناکم.'),
(1477, 11, 4, 'چرا که بازگشت شما به سوی خداست و او بر هر کاری تواناست.'),
(1478, 11, 5, 'بدانید که مشرکان آن گاه که پیامبر قرآن را بر مردم تلاوت می‌کند، سینه‌های خود را به عقب برمی گردانند و سرهایشان را به زیر می‌افکنند تا خود را پنهان سازند از این که آیات قرآن را شنیده‌اند و حجت‌های الهی را دریافت کرده‌اند باید بدانند که خداوند حتی هنگامی که آنان جامه‌های خوابشان را بر سر می‌افکنند، آنچه را نهان می‌دارند و آنچه را آشکار می‌کنند می‌داند، چرا که او به راز سینه‌ها داناست. پس چگونه می‌توانند بنمایانند که آیات الهی را نشنیده‌اند.'),
(1479, 11, 6, 'چگونه خداوند از نهان و آشکار امور و از راز دل‌ها آگاه نباشد در صورتی که هیچ جنبنده‌ای در زمین نیست مگر این که روزی اش تنها برعهده خداست و قرارگاه و گردشگاه آن را می‌داند. همه اینها در کتابی روشن ثبت است.'),
(1480, 11, 7, 'و اوست آن کسی که آسمان‌ها و زمین را در شش مرحله آفرید و عرش او در آن هنگام بر آب مستقر بود (همه هستی آب بود و او بر آب فرمانروایی می‌کرد) پس آسمان‌ها و زمین را پدید آورْد تا شما را بیازماید که کدام یک نیکوکارترید. و اگر بگویی: شما پس از مرگ برانگیخته می‌شوید، قطعاً کسانی که کافر شده‌اند خواهند گفت: این سخن جز افسونی آشکار نیست.'),
(1481, 11, 8, 'و اگر عذابی را که بدان تهدید شده‌اند تا مدّتی معدود از آنان به تأخیر افکنیم، حتماً از روی مسخره خواهند گفت: چه چیز آن را از ما باز می‌دارد؟ بدانند روزی که عذاب به سراغشان بیاید، از آنان باز گردانده نمی‌شود و آن چیزی که به مسخره می‌گرفتند آنان را فرو خواهد گرفت.'),
(1482, 11, 9, 'و اگر از جانب خود رحمتی به انسان بچشانیم و نعمتی در اختیارش نهیم، سپس آن را از او باز گیریم، به یقین او از این که بار دیگر به نعمت‌هایی دست یابد ناامید می‌شود و نعمت‌هایی که داشته ناسپاسی می‌کند و آنها را از جانب خدا نمی‌شمرد.'),
(1483, 11, 10, 'و اگر پس از گزندی که به او رسیده است نعمتی به او بچشانیم، حتماً خواهد گفت: همه دشواری‌ها از من دور شد، و می‌پندارد که دیگر رنجی به او نمی‌رسد، چرا که او بسیار شادمان و به خود بالنده است.'),
(1484, 11, 11, 'اما کسانی که شکیبایی ورزیده و کارهای شایسته انجام داده‌اند، نه هنگام سختی‌ها مأیوس و ناسپاس می‌شوند و نه هنگام راحتی شادمانی می‌کنند و به خود می‌بالند. اینانند که آمرزش و پاداشی بزرگ خواهند داشت.'),
(1485, 11, 12, 'ای پیامبر، رسالت تو جای هیچ گونه تردید و انکار ندارد، پس چگونه است که گروهی آن را دروغ انگاشته‌اند؟ آیا برخی از آنچه را به تو وحی می‌شود وامی نهی و به مردم نمی‌رسانی و سینه ات از آن به تنگ می‌آید که مبادا بگویند: چرا گنجی بر او فرو فرستاده نشده یا فرشته‌ای با او نیامده است؟ این چه انتظار بی جایی است که از تو دارند، تو فقط هشداردهنده‌ای و خدا کارساز و تدبیرکننده امور است.'),
(1486, 11, 13, 'یا می‌گویند: محمّد خود قرآن را بافته و آن را کلام خدا می‌نامد؟ اگر این ادّعا را دارند، بگو: چنانچه راست می‌گویید، ده سوره ساختگی مانند آن بیاورید و جز خدا هر که را می‌توانید به یاری فرا خوانید.'),
(1487, 11, 14, 'پس اگر دعوت شدگان به شما پاسخ ندادند و نتوانستند شما را برای آوردن همانند قرآن توانا سازند، بدانید که قرآن تنها به علم خدا نازل شده است و معبودی شایسته پرستش جز او نیست، چرا که اگر می‌بود، شما را در آوردن مثل این قرآن یاری می‌کرد. اینک آیا با وجود این دلیلِ روشن به فرمان خدا گردن می‌نهید؟'),
(1488, 11, 15, 'هر کس زندگی دنیا و آراستگی آن را می‌خواهد و برای آن تلاش می‌کند، ما در دنیا نتیجه اعمالشان را به طور کامل به آنان می‌دهیم و آنان در دنیا کاستی نخواهند دید.'),
(1489, 11, 16, 'اینان کسانی اند که در سرای آخرت بهره‌ای جز آتش ندارند، و آنچه در دنیا انجام داده‌اند بی ثمر است و هر عملی که کرده‌اند پوچ و تباه خواهد بود.'),
(1490, 11, 17, 'آیا کسی چون پیامبر که بر بینشی از جانب خدا اتکا دارد و بدین وسیله آسمانی بودن قرآن را دریافته است و شاهدی از خود وی به دنبال اوست که بینشی الهی به حقانیت وی و قرآن نازل شده بر او گواهی می‌دهد و کتاب موسی نیز که پیشوا و مایه رحمت است پیش از او نازل شده و دربردارنده حقایقی از قرآن است، مانند کسی است که از این امور بی بهره است؟ چنین کسانی به قرآن ایمان دارند، و هر کس از گروه‌ها به آن کفر ورزد آتش وعده گاه اوست. پس تو ـ ای پیامبر ـ چون از این ویژگی‌ها برخورداری، در حقّانیّت قرآن تردید مکن، همانا آن سراسر حق است و از جانب پروردگار توست، ولی بیشتر مردم به این حقیقت ایمان نمی‌آورند.'),
(1491, 11, 18, 'و کیست ستمکارتر از آن کس که بر خدا دروغ بسته است؟ آنان در روز قیامت بر پروردگارشان عرضه می‌شوند و گواهان می‌گویند: اینان کسانی اند که بر پروردگارشان دروغ بستند. بدانید که لعنت خدا بر ستمکاران است،'),
(1492, 11, 19, 'همانان که از راه خدا باز می‌مانند و کج آن راه را می‌خواهند و آخرت را انکار می‌کنند.'),
(1493, 11, 20, 'آنان با کفر ورزیدن خود، خدا را در این جهان ناتوان نکرده و از سیطره او خارج نشده‌اند و جز خدا هیچ سرپرستی که کارشان را تدبیر کند و آنها را به شرک و کفر گرایش دهد نداشته‌اند، بلکه خداوند به سزای گناهانشان آنان را به بیراهه برده است. عذاب برای آنان دو چندان خواهد شد، چرا که راه فسق و فجور را برگزیدند و بر آن پافشاری کردند. آنان از شنیدن اخطارها و نویدهای الهی ناتوان بودند و نشانه‌های خدا را نمی‌دیدند.'),
(1494, 11, 21, 'آنان کسانی اند که به خود زیان زده و خویشتن را به هلاکت افکنده‌اند، و روز رستاخیز با آشکار شدن حقایق، آنچه به دروغ بافته بودند از آنان گم می‌شود و از افکارشان محو می‌گردد.'),
(1495, 11, 22, 'حقّاً که آنان در سرای آخرت زیانکارترینِ مردمند، چرا که راه نجاتی از آتش دوزخ ندارند.'),
(1496, 11, 23, 'به یقین، کسانی که ایمان آورده و کارهای شایسته کرده‌اند و به پروردگارشان اطمینان داشته و در ایمانشان به او تزلزل نداشته‌اند اهل بهشتند و جاودانه در آن خواهند بود.'),
(1497, 11, 24, 'مثَل این دو گروه مؤمن و کافر، مانند کور و کر در مقایسه با بینا و شنواست، آیا این دو در مثل یکسانند؟ پس آیا توجّه نمی‌کنید و درنمی یابید؟'),
(1498, 11, 25, 'به راستی ما نوح را به سوی قومش به رسالت فرستادیم، پس به آنان گفت: من پیامبری هستم که به شما هشدار می‌دهم و آنچه را که در امر هدایت بدان نیاز دارید برای شما به روشنی بیان می‌کنم.'),
(1499, 11, 26, 'و این پیام را برای شما دارم که جز خدا را نپرستید، زیرا من از عذاب روزی دردناک بر شما بیمناکم.'),
(1500, 11, 27, 'مهتران قومش که کافر بودند، بی آن که بیندیشند گفتند: ما تو را جز بشری مانند خود نمی‌بینیم، و نمی‌بینیم که جز فرومایگان ما که فرومایگی آنان با یک نظر معلوم می‌شود کسی از تو پیروی کرده باشد، و نمی‌بینیم که شما از نظر ثروت و مقام و آگاهی از غیب امتیازی بر ما داشته باشید. ما نه تنها دلیلی بر لزوم پیروی از شما نداریم، بلکه می‌پنداریم شما دروغگویید.'),
(1501, 11, 28, 'نوح در پاسخ آنان گفت: ای قوم من، به من بگویید، اگر من بر معجزه‌ای از جانب پروردگارم که رسالت مرا تأیید کند، تکیه داشته باشم و خدا از نزد خود رحمتی (کتاب و دانشی) به من داده باشد که شما را به وسیله آن راه نمایم، ولی آن به دلیل تکبر و لجاجت شما بر شما پوشیده مانده باشد، آیا شما را به پذیرش آن رحمت در حالی که از آن کراهت دارید اجبار کنیم؟'),
(1502, 11, 29, 'ای قومِ من، من برای ابلاغ رسالت خود هیچ مالی از شما نمی‌خواهم، مزد من فقط برعهده خداست. پس تصور نکنید که من مدّعی رسالت شده‌ام تا مال و ثروتی به چنگ آورم. و من هرگز کسانی را که ایمان آورده‌اند از خود طرد نمی‌کنم، هر چند شما آنان را فرومایه پندارید. آنان پروردگارشان را دیدار خواهند کرد و حساب کارهایشان با اوست. روز حساب حقیقتی است تردیدناپذیر، ولی من شما را مردمی می‌بینم که از آن بی خبرید.'),
(1503, 11, 30, 'ای قوم من، اگر آنان را از خود طرد کنم، در حقشان ستم کرده و درخور عذاب الهی خواهم شد، آن گاه چه کسی مرا یاری می‌دهد و از کیفر خدا می‌رهاند؟ آیا توجه ندارید که این ستمکاری است و خداوند ستمکاران را رها نخواهد کرد.'),
(1504, 11, 31, 'من به شما نمی‌گویم که گنجینه نعمت‌های الهی نزد من است و می‌توانم تهیدستان را بی نیاز کنم، و غیب هم نمی‌دانم تا به هر خیری روی آورم و از هر بلایی بگریزم، و نمی‌گویم که من فرشته‌ام و نیازی به خوردن و آشامیدن ندارم، و درباره کسانی که چشمان ظاهربین شما آنان را حقیر می‌بیند، نمی‌گویم که خدا به آنان خیری عطا نمی‌کند. خدا به آنچه در دل آنان است داناتر است. اگر من درباره آنان چیزی را بگویم که شما می‌پندارید، از ستمکاران خواهم بود.'),
(1505, 11, 32, 'سران قوم نوح گفتند: ای نوح، با ما به جدال پرداختی و بسیار هم جدال کردی. اگر از راستگویانی، عذابی را که پیوسته به ما وعده می‌دهی و ما را بدان تهدید می‌کنی برای ما بیاور.'),
(1506, 11, 33, 'نوح گفت: من آورنده عذاب برای شما نیستم. این خداست که اگر بخواهد آن را برای شما می‌آورَد، و شما نمی‌توانید خدا را از آوردن آن ناتوان سازید.'),
(1507, 11, 34, 'و اگر بخواهم شما را اندرز دهم، چنانچه خدا بخواهد شما را به سزای گناهانتان به بیراهه کشد و عذابتان کند، اندرزهای من به حال شما سودی ندارد. او پروردگار شماست و شما به سوی او بازگردانده می‌شوید و کارتان در اختیار اوست.'),
(1508, 11, 35, 'ای پیامبر، آیا مشرکان قوم تو می‌گویند: محمّد خود این داستان را ساخته است و آن را کلام خدا می‌شمرد؟ بگو: اگر من آن را ساخته باشم گناهکارم و گناه من برعهده خود من است، و من از گناهی که شما مرتکب می‌شوید و این حقایق را انکار می‌کنید برکنارم.'),
(1509, 11, 36, 'و به نوح وحی شد که از قوم تو جز کسانی که ایمان آورده‌اند دیگر کسی ایمان نمی‌آورَد، پس به آنچه می‌کنند اندوهگین مباش.'),
(1510, 11, 37, 'و زیر نظر ما و براساس وحی ما کشتی را بساز، و درباره نجات کسانی که ستم کرده‌اند با من سخن مگوی، زیرا آنان غرق خواهند شد و این مقدر شده است.'),
(1511, 11, 38, 'و نوح کشتی را می‌ساخت، در حالی که هرگاه گروهی از اشراف قومش بر او می‌گذشتند، او را به خاطر ساختن کشتی مسخره می‌کردند. نوح در پاسخ آنها می‌گفت: اگر اینک ما را مسخره می‌کنید، ما نیز شما را همان گونه که مسخره می‌کنید، مسخره خواهیم کرد.'),
(1512, 11, 39, 'به زودی خواهید دانست چه کسی را در این دنیا عذابی در می‌رسد که او را خوار و رسوا می‌کند و فردای قیامت نیز عذابی جاودانه بر او فرود می‌آید.'),
(1513, 11, 40, 'کار نوح و قومش بدین ترتیب می‌گذشت، تا آن گاه که فرمان ما در رسید و از آن تنور به نشانه فرارسیدن عذاب، آب فوران کرد، به نوح گفتیم: از هر حیوانی دو تا، یک نر و یک ماده، در آن کشتی حمل کن و نیز خانواده ات ـ مگر آن کسی که پیش‌تر وعده ما مبنی بر غرق شدن او به اثبات رسیده است ـ و نیز کسانی را که ایمان آورده‌اند در کشتی سوار کن و جز عده‌ای اندک با او به خدا ایمان نیاورده بودند.'),
(1514, 11, 41, 'و نوح به افراد خانواده خود و به سایر مؤمنان گفت: در آن سوار شوید که روان شدن و لنگر انداختنش فقط با نام خداست، و خداوند سرنشینان آن را از غرق شدن نجات می‌دهد، چرا که پروردگارم آمرزنده و مهربان است.'),
(1515, 11, 42, 'و کشتی آنان را در میان امواجی کوه پیکر به پیش می‌برد، و نوح پسرش را که در کناری دور از کشتی بود ندا داد که: ای پسرک من، با ما به کشتی سوار شو و با کافران مباش که غرق خواهی شد.'),
(1516, 11, 43, 'پسر گفت: به زودی بر کوهی که مرا از این آب در امان دارد جای می‌گیرم. نوح گفت: امروز برای مردم هیچ نگهدارنده‌ای از عذاب خدا نیست، جز برای کسانی که خدا بر آنان رحمت آورده و به آنان توفیق ایمان داده و با ما در کشتی سوار شده‌اند. و موج میان آن دو حایل شد و پسر از غرق شدگان گردید.'),
(1517, 11, 44, 'و گفته شد: ای زمین، آب خود فرو بر، و ای آسمان، از باریدن باز ایست، و آب فرو نشانده شد و کار به انجام رسید و فرمان خدا درباره قوم نوح اجرا شد و کشتی بر کوه جودی قرار گرفت و گفته شد: دوری از رحمت خدا برای مردم ستمکار باد.'),
(1518, 11, 45, 'و هنگامی که موج میان نوح و پسرش حایل شد، نوح پروردگارش را ندا داد و گفت: پروردگارا، پسرم از خانواده من است و قطعاً وعده تو مبنی بر نجات خانواده من حق است، با این حال داوری با توست، که تو بهترین داورانی.'),
(1519, 11, 46, 'خدا فرمود: ای نوح، او از کسان تو که وعده نجات آنان به تو داده شده، نیست، خانواده تو به شرط این که صالح باشند نجات می‌یابند. پسر تو زشت کردار است، پس مبادا آنچه را بدان علم نداری از من بخواهی. من تو را پند می‌دهم که مبادا از جاهلان گردی.'),
(1520, 11, 47, 'نوح گفت: پروردگارا، من به تو پناه می‌برم از این که چیزی از تو بخواهم که بدان علم ندارم. اگر مرا نبخشایی و از خطاها مصونم نداری و بر من رحمت نیاوری از زیانکاران خواهم بود.'),
(1521, 11, 48, 'گفته شد: ای نوح، با سلامت و امنیتی که از جانب ماست و نیز با نعمت‌هایی پربرکت که بر تو و همراهانت و بر امّت‌های شایسته‌ای که از همراهان تو پدید می‌آیند نازل می‌شود، از کشتی فرود آی و از کسانی که همراه تواند امّت‌هایی ناشایسته پدید می‌آیند که ما آنان را از متاع دنیا بهره مند می‌کنیم، سپس عذابی دردناک از جانب ما به آنان خواهد رسید.'),
(1522, 11, 49, 'ای پیامبر، این داستان‌ها از خبرهای غیبی است که آنها را به تو وحی می‌کنیم. پیش از این نه تو از آن آگاه بودی و نه قوم تو. اکنون که دانستی نوح و پیروانش نجات یافتند و کافران به هلاکت رسیدند، بر آنچه حق است شکیبایی کن که فرجام نیک از آنِ تقواپیشگان است.'),
(1523, 11, 50, 'و به سوی قوم عاد، برادرشان هود را به رسالت فرستادیم، گفت: ای قوم من، خدا را بپرستید، زیرا شما را هیچ معبودی شایسته پرستش جز او نیست. شما با شرک ورزیدن خود جز افترازنندگانی بیش نیستید.'),
(1524, 11, 51, 'ای قوم من، من برای رساندن پیام الهی هیچ مزدی از شما درخواست نمی‌کنم، پس تصوّر نکنید که برای دستیابی به مال و ثروت مدّعی رسالت شده‌ام، مزد من جز برعهده کسی که مرا پدید آورده است، نیست. پس چرا سخنانم را درنمی یابید تا حقیقت برایتان آشکار شود؟'),
(1525, 11, 52, 'و ای قوم من، از پروردگارتان آمرزش گناهانتان را بخواهید، سپس با انجام کارهای شایسته به سوی او بازگردید که در این صورت ابرهایی پرباران به سوی شما خواهد فرستاد تا باران‌هایی سودمند بر شما فرو ریزند. و نیرویی بر نیرویتان خواهد افزود. پس مبادا با حال شرک و گناه از دعوت من روی برتابید.'),
(1526, 11, 53, 'گفتند: ای هود، برای ما حجّت و دلیلی روشن نیاورده‌ای، و ما برای سخنان تو معبودان خود را رها نمی‌کنیم و هرگز به تو ایمان نمی‌آوریم.'),
(1527, 11, 54, 'ما درباره تو چیزی جز این نمی‌گوییم که برخی از معبودان ما به تو گزندی رسانده و خِرد را از تو گرفته‌اند. هود گفت: من خدا را گواه می‌گیرم و شما نیز گواه باشید که من بری هستم از آنچه شما آن را شریک او می‌پندارید،'),
(1528, 11, 55, 'که آن غیر خداست، پس همگی برای نابودی من نیرنگ کنید و به من مهلت ندهید.'),
(1529, 11, 56, 'ولی بدانید که کاری از شما ساخته نیست، زیرا من بر خدا که پروردگار خودم و پروردگار شماست توکّل کرده‌ام. هیچ جنبنده‌ای نیست مگر این که او موی پیشانی اش را گرفته و بر آن تسلط کامل دارد. به یقین پروردگار من بر راهی راست قرار دارد و به شیوه‌ای درست عمل می‌کند. او مرا از نیرنگ شما مصون می‌دارد و بدین وسیله معجزه رسالت مرا به شما نشان می‌دهد.'),
(1530, 11, 57, 'پس اگر از پیروی من روی برتابید گرفتار عذاب می‌شوید و من مسئول آن نیستم، زیرا آنچه را که به منظور آن به سوی شما فرستاده شده‌ام به شما ابلاغ کردم، و پروردگارم شما را هلاک می‌کند و قومی جز شما را جایگزین شما می‌سازد و شما نمی‌توانید هیچ زیانی به او برسانید، چرا که پروردگارم بر هر چیزی نگهبان است، نه چیزی از او پنهان می‌ماند و نه از سلطه او راه گریزی هست.'),
(1531, 11, 58, 'و هنگامی که فرمان ما صادر شد و عذاب در رسید، هود و کسانی را که با او ایمان آورده بودند، به رحمتی از جانب خود نجات دادیم و آنان را از عذابی سخت رهایی بخشیدیم.'),
(1532, 11, 59, 'و این قوم عاد بودند که نشانه‌های پروردگارشان را انکار کردند و با نافرمانی از هود همه فرستادگان خدا را نافرمانی نمودند و فرمان هر ستمکار حق ناپذیری را پیروی کردند.'),
(1533, 11, 60, 'و در این دنیا لعنتی به دنبال دارند و روز رستاخیز نیز چنین خواهد بود. آن گاه ندایی آسمانی برخاست که: آگاه باشید، قوم عاد به پروردگارشان کافر شدند. هان، عادیان: قوم هود از رحمت خدا دور باشند.'),
(1534, 11, 61, 'و به سوی قوم ثمود، برادرشان صالح را به رسالت فرستادیم. گفت: ای قوم من، خدا را بپرستید چرا که شما را معبودی شایسته پرستش جز او نیست، اوست که شما را از زمین پدید آورده و در آن رشدتان داده و از شما خواسته است که در آن آبادانی کنید. پس آمرزش گناهانتان را از او بخواهید، سپس با انجام کارهای شایسته به سوی او باز گردید که پروردگار من نزدیک است و دعاها را اجابت می‌کند.'),
(1535, 11, 62, 'گفتند: ای صالح، پیش از این که ما را به توحید فراخوانی، در میان ما مایه امید بودی و دل بسته بودیم که ما را به راه ترقی درآوری ولی اینک نومید شده‌ایم. آیا ما را از پرستش آنچه پدرانمان می‌پرستند باز می‌داری؟ ما در آنچه تو ما را بدان فرا می‌خوانی تردید داریم و دعوت تو مایه بدگمانی ما شده است.'),
(1536, 11, 63, 'صالح گفت: ای قوم من، اگر من بر معجزه‌ای از جانب پروردگارم که رسالت مرا تأیید کند، تکیه داشته باشم و او از جانب خود رحمتی (رسالتی) به من داده باشد و مرا به ابلاغ آن فرمان داده باشد، به من بگویید، چنانچه او را نافرمانی کنم و پیامش را ابلاغ نکنم چه کسی مرا یاری می‌کند و از کیفر خدا می‌رهاند؟ من اگر به خواسته شما عمل کنم، جز این که به من زیان می‌رسانید چیزی بر من نخواهید افزود.'),
(1537, 11, 64, 'و ای قوم من، این ناقه خداست که او آن را از دل کوه برای شما پدید آورده است تا نشانه‌ای بر درستی رسالت من باشد، پس آن را آزاد بگذارید تا در زمین خدا بچرد و آسیبی به آن نرسانید که عذابی زودرس شما را فروخواهد گرفت.'),
(1538, 11, 65, 'پس آن را کشتند، و صالح گفت: سه روز در دیار خود بهره برید که [پس از این مهلت عذاب دامنگیرتان خواهد شد]، این وعده‌ای است که دروغ در آن راه ندارد.'),
(1539, 11, 66, 'پس هنگامی که فرمان ما در رسید، صالح و کسانی را که با او ایمان آورده بودند به رحمتی از جانب خود، از عذاب نجات دادیم و آنان را از خواری آن روز رهانیدیم، چرا که پروردگار توست که نیرومند و شکست ناپذیر است.'),
(1540, 11, 67, 'و کسانی را که ستم کرده بودند صیحه فرو گرفت و در خانه هایشان به زانو درآمدند و با چهره بر زمین افتادند و هلاک شدند،'),
(1541, 11, 68, 'چنان که گویی در آن جا سکونت نداشتند، آن گاه ندایی آسمانی برخاست که: آگاه باشید! قوم ثمود به پروردگارشان کافر شدند. هان، قوم ثمود از رحمت خدا دور باشند.'),
(1542, 11, 69, 'و در حقیقت فرستادگان ما (فرشتگان) با بشارت و نوید به حضور ابراهیم آمدند و سلام کردند. او پاسخ داد که: بر شما سلام باد. پس بی درنگ گوساله‌ای بریان شده برایشان آورد.'),
(1543, 11, 70, 'اما وقتی که دید دستشان به سوی آن نمی‌رود، ناخوشایندشان دانست و پنداشت که سوء قصدی دارند، ازاین رو در دلش از آنان احساس ترس کرد. فرشتگان گفتند: نترس. ما به سوی قوم لوط فرستاده شده‌ایم.'),
(1544, 11, 71, 'در این هنگام همسر ابراهیم که آن جا ایستاده بود و این ماجرا را نظاره می‌کرد، عادت ماهانه دید، پس او را به اسحاق و از پی اسحاق به فرزند وی یعقوب نوید دادیم.'),
(1545, 11, 72, 'همسر ابراهیم گفت: ای وای بر من! آیا من فرزند می‌آورم با این که زنی سالخورده‌ام و این هم شوهر من است که کهنسال است؟ به راستی این چیزی شگفت است!'),
(1546, 11, 73, 'فرشتگان به او گفتند: آیا از کار خدا در شگفتی؟ خداست که هر چه بخواهد انجام می‌دهد، وانگهی رحمت و برکات خاص او همواره بر شما خاندان نازل شده است. چرا که همه زیبایی‌ها و کارهای پسندیده و هر جود و کَرَمی از اوست.'),
(1547, 11, 74, 'هنگامی که ابراهیم دریافت که مهمانانش فرشته‌اند، و دلهره از او دور شد و آن مژده به وی رسید، با ما درباره قوم لوط به چون و چرا پرداخت و از آنان شفاعت کرد،'),
(1548, 11, 75, 'زیرا ابراهیم بسیار بردبار بود و در کیفر گنهکاران شتاب نمی‌کرد، و بسیار دلسوز بود و از گمراهی و هلاکت مردم سخت آزرده می‌شد، و برای انجام کارها روی به درگاه خدا می‌آورْد.'),
(1549, 11, 76, 'فرشتگان به او گفتند: ای ابراهیم، از این خواسته ات روی بگردان که فرمان پروردگارت در رسیده است و قطعاً عذابی بازگشت ناپذیر به سراغ آنان خواهد آمد.'),
(1550, 11, 77, 'و چون فرستادگان ما نزد لوط آمدند، از آن روی که به صورت جوانانی زیبا آمده بودند، حضورشان نزد لوط سبب ناراحتی او شد و دستش از یاری آنان کوتاه گردید، زیرا نمی‌توانست در برابر قومش از آنان دفاع کند، و گفت: این روزی دشوار است و شر و بلا سخت بدان آمیخته است.'),
(1551, 11, 78, 'قوم لوط که از ورود آن زیبا رویان با خبر شدند، شتابان به سوی او آمدند، و از پیش کارهای زشت مرتکب می‌شدند. لوط گفت: ای قوم من، اینان دختران منند. آنان را به همسری برگیرید که برای شما پاکیزه ترند. پس از خدا بترسید و مرا در مورد مهمانانم خوار و رسوا مکنید. آیا از میان شما مردی رهیافته نیست که از انسانیّت بهره مند باشد و مرا یاری کند؟'),
(1552, 11, 79, 'گفتند: تو خوب می‌دانی که رسم ما دوری از زنان و روی آوردن به همجنسان است، پس در دختران تو حقی نداریم و تو خوب می‌دانی که ما چه می‌خواهیم.'),
(1553, 11, 80, 'لوط گفت: ای کاش به وسیله شما نیرویی می‌داشتم که مرا یاری کند، یا به تکیه گاهی استوار (قبیله‌ای نیرومند و مدافع) روی می‌آوردم.'),
(1554, 11, 81, 'در این هنگام فرشتگان گفتند: ای لوط، ما فرستادگان پروردگار توییم. آنان هرگز به تو دست نمی‌یابند و نمی‌توانند گزندی به تو برسانند. پس، در پاسی از این شب با خانواده ات از این دیار کوچ کن، و هیچ یک از شما نباید روی بگرداند و به پشت سرش بنگرد. اما همسرت را با خود مبر، زیرا عذابی که به دیگران می‌رسد به او نیز خواهد رسید. قطعاً موعد هلاکتشان همین بامداد است. آیا بامداد نزدیک نیست؟'),
(1555, 11, 82, 'پس چون فرمان ما مبنی بر عذاب قوم لوط دررسید، دیارشان را زیر و زبر کردیم و سنگ‌هایی از (سجّیل) که لایه‌هایی منظّم داشت بر آن باراندیم.'),
(1556, 11, 83, 'سنگ‌هایی که نزد پروردگار تو نشاندار بود و هرگز از هدف خطا نمی‌رفت و آنها از ستمگران دور نیست.'),
(1557, 11, 84, 'و به سوی مردم مدین برادرشان شعیب را به رسالت فرستادیم. گفت: ای قوم من، خدا را بپرستید، که شما را معبودی شایسته پرستش جز او نیست، و از پیمانه و ترازو نکاهید. به راستی من شما را در نعمت فراوان می‌بینم، چه نیازی به اموالِ حرام دارید؟ من بر شما از عذاب روزی فراگیر که هیچ کس را توانِ گریز از آن نیست می‌ترسم.'),
(1558, 11, 85, 'و ای قوم من، پیمانه و ترازو را به طور کامل و با عدل و انصاف بسنجید، و هنگام وزن کردن و پیمانه نمودنِ کالاهای مردم، از آنها نکاهید، و در زمین تبهکارانه به فساد برنخیزید.'),
(1559, 11, 86, 'اگر مؤمن باشید، سودی که از داد و ستد برای شما می‌ماند و خدا شما را بدان راه نموده، برای شما بهتر از مالی است که از راه ناروا به دست می‌آورید. من بر شما نگهبان نیستم تا بتوانم به خیر و صلاح وادارتان کنم و شما را از شر و فساد باز دارم.'),
(1560, 11, 87, 'گفتند: ای شعیب، آیا نماز تو به تو فرمان می‌دهد که ما آنچه را پدرانمان می‌پرستند رها کنیم، یا در اموال خود آن گونه که می‌خواهیم تصرف نکنیم؟ راستی که تو بردبار و رهیافته‌ای و برای تو زشت است که آزادی را از ما سلب کنی.'),
(1561, 11, 88, 'شعیب گفت: ای قوم من، به من خبر دهید، اگر بر معجزه‌ای که از جانب پروردگار من است و رسالتم را تأیید می‌کند اتکا داشته باشم و او از جانب خود به من رزقی نیکو (وحی معارف و احکام دین) که شما را به وسیله آن راه نمایم، عطا کرده باشد، آیا می‌پندارید کاری نابخردانه می‌کنم که شما را به خدا و پرستش او فرا می‌خوانم و از داد و ستدهای نادرست باز می‌دارم؟ من نمی‌خواهم با شما مخالفت کنم و خود به آنچه شما را از آن باز می‌دارم، روی آورم. من جز اصلاح امور مردم، تا آن جا که در توان دارم، چیزی نمی‌خواهم، و توفیق من جز به مدد خدا نیست. فقط بر او توکّل کرده‌ام، و برای انجام کارها فقط به سوی او باز می‌گردم و از او یاری می‌طلبم.'),
(1562, 11, 89, 'و ای قوم من، مبادا ستیز شما با من سبب شود که مانند آن عذابی که به قوم نوح یا قوم هود یا قوم صالح رسید به شما نیز برسد، و قوم لوط از شما دور نیست.'),
(1563, 11, 90, 'و از پروردگارتان آمرزش گناهانتان را بخواهید، سپس با ایمان و کارهای شایسته به سوی او بازگردید که پروردگار من بر آمرزش طلبان رحمت می‌آورد و آنان را دوست می‌دارد.'),
(1564, 11, 91, 'گفتند: ای شعیب، ما بسیاری از آنچه را که تو می‌گویی به روشنی نمی‌فهمیم و قطعاً تو را در میان خود ناتوان می‌بینیم، و اگر آن چند تن از خویشاوندان تو که نزد ما محترمند نبودند، حتماً تو را سنگسار کرده بودیم، و تو بر ما قدرتی نداری.'),
(1565, 11, 92, 'شعیب گفت: ای قوم من، آیا آن چند تن از خویشاوندان من نزد شما از خدا گرامی ترند که شما او را پشت سر افکنده و به فراموشی سپرده‌اید؟ در حالی که پروردگارم به آنچه می‌کنید احاطه دارد.'),
(1566, 11, 93, 'و ای قوم من، هر کاری که قدرت دارید انجام دهید، من نیز [به آنچه وظیفه دارم] عمل می‌کنم. به زودی خواهید دانست که چه کسی عذابی به سراغش می‌آید که او را خوار و رسوا می‌سازد و چه کسی دروغگوست، و چشم به راه باشید که من نیز با شما چشم به راهم و از شما جدا نمی‌شوم.'),
(1567, 11, 94, 'و هنگامی که فرمان ما در رسید، شعیب و کسانی را که با او ایمان آورده بودند به رحمتی از جانب خود از عذاب نجات دادیم، و کسانی را که ستم کرده بودند صیحه فرو گرفت، پس در خانه هایشان به زانو درآمدند و با چهره بر زمین افتادند و هلاک شدند.'),
(1568, 11, 95, 'چنان که گویی در آن جا سکونت نداشتند. آن گاه ندایی از آسمان برخاست که: هان، اهل مدین از رحمت خدا دور باشند، چنان که قوم ثمود از رحمت خدا دور شدند.'),
(1569, 11, 96, 'و به راستی موسی را با معجزات خود و با قدرتی آشکار که فرعون را از دسترسی به او باز می‌داشت فرستادیم.'),
(1570, 11, 97, 'او را به سوی فرعون و مهتران قومش به رسالت گسیل داشتیم، ولی آنان از فرمان فرعون پیروی کردند، و فرمان فرعون صواب نبود که آنان را به راه راست رهنمون شود.'),
(1571, 11, 98, 'روز قیامت پیشاپیش قومش می‌رود سپس آنان را به آتش درمی آورَد، و بد آبشخوری است آتشی که بدان وارد می‌شوند.'),
(1572, 11, 99, 'و در این دنیا لعنتی بدرقه آنان می‌شود، و بد عطایی است آن آتش که روز رستاخیز نصیب آنان می‌گردد.'),
(1573, 11, 100, 'این بخشی از اخبار آن شهرهاست که برای تو حکایت می‌کنیم، برخی از آنها هنوز پابر جا هستند و آثارشان باقی است، و برخی درو شده و به کلّی بر باد رفته‌اند.'),
(1574, 11, 101, 'ما به آنان ستم نکردیم که عذاب بر سرشان فرو فرستادیم، بلکه آنان خود با شرک ورزیدنشان به خویشتن ستم کردند و سزاوار کیفر شدند. پس هنگامی که فرمان پروردگارت در رسید، معبودانشان که به جای خدا می‌خواندند آنان را هیچ کفایت نکردند و جز تباهی بر آنان نیفزودند.'),
(1575, 11, 102, 'و پروردگارت چون بخواهد شهرهایی را که مردمش ستمکارند به کیفر گیرد، همانگونه گیرد که پیشینیان را گرفت. قطعاً به کیفر گرفتن پروردگار تو دردناک و سخت است.'),
(1576, 11, 103, 'به راستی در این رویدادها برای کسی که از عذاب آخرت می‌ترسد نشانه‌ای است بر این که خداوند در روز قیامت مجرمان را کیفر خواهد داد. آن روز روزی است که همه مردم برای آن گرد آوری می‌شوند و روزی است که همگان (آدمیان، جنّیان و فرشتگان) آن را مشاهده خواهند کرد.'),
(1577, 11, 104, 'و ما آن را جز تا سرآمد مدتی که در حال شمارش است به تأخیر نمی‌افکنیم.'),
(1578, 11, 105, 'روزی که آن سرآمد فرا رسد، هیچ کس لب به سخن نمی‌گشاید، مگر کسی که خدا به او اجازه دهد. در آن روز بعضی از مردم، تیره بخت و بعضی نیکبخت خواهند بود.'),
(1579, 11, 106, 'امّا کسانی که تیره بخت اند در آتش خواهند بود و در آن جا ناله و فریادی خواهند داشت.'),
(1580, 11, 107, 'تا آسمان‌ها و زمین پابرجاست در آن جا ماندگارند، مگر این که پروردگارت بخواهد آنان رهایی یابند، چرا که پروردگارت آنچه بخواهد انجام می‌دهد.'),
(1581, 11, 108, 'و امّا کسانی که نیکبخت‌اند، تا آسمان‌ها و زمین برقرار است جاودانه در بهشت خواهند بود، مگر این که پروردگارت جز این را بخواهد، که البته نخواهد خواست، زیرا بهشت را به عنوان عطایی پایان ناپذیر مقرّر کرده است.'),
(1582, 11, 109, 'ای پیامبر، اکنون که سرگذشت پیشینیان را بر تو حکایت کردیم و دانستی که آنان بر اثر شرک هلاک شدند، مبادا در عبادت این مردم تردید کنی و گمان بری که شاید حجّتی دارند. آنان جز همان گونه که پیش‌تر پدرانشان (امت‌های گذشته) بی هیچ حجتی غیر خدا را می‌پرستیدند چیزی نمی‌پرستند، و قطعاً ما سهم آنان را از عذاب بی کم و کاست به آنان خواهیم داد.'),
(1583, 11, 110, 'اختلاف و تردید مردم در قرآن بر تو گران نیاید، اختلاف و تردید در کتاب‌های آسمانی چیز تازه‌ای نیست، ما به موسی نیز کتاب آسمانی دادیم ولی در آن اختلاف شد، و اگر از جانب پروردگارت سخنی صادر نشده بود که برای انسان‌ها در زمین سرآمدی هست، قطعاً میان کسانی که در آن کتاب اختلاف کردند داوری شده و هر کس به سزای کردارش رسیده بود. آری قوم موسی درباره کتاب او سخت در شک و تردید بودند.'),
(1584, 11, 111, 'و قطعاً پروردگار تو به همه کسانی که در کتاب موسی اختلاف کردند سزای اعمالشان را به طور کامل خواهد داد، زیرا او به آنچه می‌کنند آگاه است.'),
(1585, 11, 112, 'پس همان گونه که فرمان یافته‌ای بر این دین پایدار باش و کسانی هم که از شرک توبه کرده و همراه تو ایمان آورده‌اند باید پایداری کنند، و مبادا طغیان کنید و از بندگی خدا که خدا در فطرت شما نهاده است منحرف شوید، که او به آنچه می‌کنید بیناست و در صورت نافرمانی شما را مؤاخذه خواهد کرد.'),
(1586, 11, 113, 'و به کسانی که ستم کرده‌اند (دین خدا را نپذیرفته اند) اعتماد نکنید که آتش دوزخ به شما می‌رسد و در سرای آخرت جز خدا برای شما دوستانی یاری رسان نیست و آن گاه است که از سوی هیچ کس یاری نمی‌شوید.'),
(1587, 11, 114, 'در دو طرف روز (صبح و عصر) و نیز در ساعاتی از آغاز شب برای مردم نماز برپادار، زیرا نماز از کارهای نیک است و کارهای نیک گناهان را از بین می‌برد. این حقیقت که نیکی زداینده بدی است تذکاری است برای کسانی که به یاد خدا هستند.'),
(1588, 11, 115, 'و بر دشواری‌های راه رسالت و آزار کافران صبر کن که خداوند پاداش نیکوکاران را تباه نمی‌سازد.'),
(1589, 11, 116, 'شگفت است که چرا از امّت‌هایی که پیش از شما می‌زیستند، گروهی نبودند که قومشان را از فسادگری در زمین باز دارند و از عذاب برَهانند. آری عدّه‌ای اندک باقی ماندند، همانان که چون عذاب فرا رسید نجاتشان دادیم، و کسانی که ستم کردند، از پی لذت‌ها و نعمت‌هایی رفتند که در آن غوطهور بودند، در حالی که گنهکار بودند.'),
(1590, 11, 117, 'و پروردگار تو بر آن نبوده است که شهرهایی را که مردم آن اهل صلاحند به ستم هلاک کند، زیرا او هرگز به بندگانش ستم روا نمی‌دارد.'),
(1591, 11, 118, 'و اگر پروردگار تو می‌خواست، مردم را امّتی یگانه قرار می‌داد که همه بر یک آیین باشند، ولی چنین نخواست، از این رو آنان پیوسته بر اثر هواهای نفسانی در دین اختلاف می‌کنند و در نتیجه به بیراهه می‌روند،'),
(1592, 11, 119, 'مگر کسانی که پروردگارت بر آنان رحمت آورَد و به راه راست هدایتشان کند که خدا آنان را برای همین آفریده است، ولی چون اختلاف کرده و حقّ را گم نموده‌اند، وعده پروردگارت درباره آنان تحقق یافته است که: دوزخ را از تمامی جنّیان و آدمیان گمراه پُر خواهم کرد.'),
(1593, 11, 120, 'ای پیامبر، هر سرگذشتی را برای تو حکایت می‌کنیم داستان‌های پیامبران را، آنچه دلت را بدان استوار بسازیم، بر تو می‌خوانیم، و در این سوره آنچه سراسر حقّ است برای تو آمده، و در آن برای مؤمنان پند و تذکّری است.'),
(1594, 11, 121, 'و به کسانی که ایمان نمی‌آورند بگو: براساس همان وضعی که دارید و خیر خود را در آن می‌پندارید عمل کنید که ما نیز [به آنچه وظیفه خود می‌دانیم] عمل خواهیم کرد.'),
(1595, 11, 122, 'و در انتظار فرجام کار خود باشید که ما نیز در انتظار خواهیم بود.'),
(1596, 11, 123, 'غیب آسمان‌ها و زمین که فرجام کار تو و کافران را دربردارد و حاکی از پیروزی تو بر آنان است، در اختیار خداست و کارها همه به او بازگشت می‌یابد و به دست او رقم می‌خورد، پس او را عبادت کن و بر او توکّل نمای و [بدان که] پروردگارت از آنچه می‌کنید بی خبر نیست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الف، لام، را. این آیه‌های بلند مرتبه که اینک به تو وحی می‌کنیم و آیاتی که پیش‌تر نازل کرده‌ایم، آیات این کتاب روشن و روشنگر است.'),
(1598, 12, 2, 'ما آن را به گونه‌ای که خوانده شود و به زبان تازی فرو فرستادیم، باشد که در آن بیندیشید و حقایق آن را درک کنید.'),
(1599, 12, 3, 'ما با وحی کردنِ این قرآن به تو، داستان یوسف را که نیکوترین داستان است بر تو حکایت می‌کنیم، و قطعاً تو پیش از این نسبت به آن از بی خبران بودی.'),
(1600, 12, 4, 'یاد کن هنگامی را که یوسف به پدرش یعقوب گفت: ای پدر، من در خواب یازده ستاره و نیز خورشید و ماه را دیدم، آنها را دیدم که برای من سجده می‌کنند.'),
(1601, 12, 5, 'یعقوب گفت: ای پسرک من، خوابت را برای برادرانت حکایت مکن، زیرا آنان بر تو حسد می‌برند و قطعاً برای تو نیرنگی می‌اندیشند و شیطان آنان را تحریک خواهد کرد که نیرنگ خود را به کار بندند، چرا که شیطان برای آدمی دشمنی آشکار است.'),
(1602, 12, 6, 'و همان گونه که در خواب دیدی، پروردگارت تو را برمی گزیند و از بندگان خالص خود قرار می‌دهد و از تعبیر خواب‌ها و تحلیل درست رخدادها به تو می‌آموزد و نعمتش را بر تو تمام می‌کند و به واسطه تو نعمت خود را بر خاندان یعقوب نیز تمام می‌کند، چنان که پیش‌تر آن را بر پدرانت ابراهیم و اسحاق تمام کرد. به یقین، پروردگار تو به حال بندگانش آگاه و کارهایش همه از روی حکمت است.'),
(1603, 12, 7, 'ای پیامبر، از تو سرگذشت یوسف را می‌پرسند، قطعاً در داستان یوسف و برادرانش برای جویندگان حقیقت نشانه‌هایی است بر این که خدا یکتاست و عهده دار امور بندگان خالص خویش است.'),
(1604, 12, 8, 'یاد کن هنگامی را که برادران یوسف درصدد توطئه برآمدند و با یکدیگر گفتند: همه می‌دانیم که یوسف و برادرش بنیامین نزد پدرمان از ما محبوب ترند، در صورتی که ما گروهی نیرومندیم و همه کارهای پدر به دست ما انجام می‌شود. قطعاً پدرمان در برتری دادن یوسف و برادرش بر ما در خطایی آشکار است.'),
(1605, 12, 9, 'برخی از آنان گفتند: یوسف را بکشید، و برخی دیگر گفتند او را در سرزمینی دور دست بیفکنید که نتواند بازگردد، آن گاه توجّه پدرتان به شما معطوف می‌شود، بعد از او توبه می‌کنید و مردمی شایسته می‌شوید.'),
(1606, 12, 10, 'گوینده‌ای از آنان گفت: یوسف را نکشید، و کاری که به هلاکت او می‌انجامد نکنید. اگر می‌خواهید کاری کنید او را در قعر چاه بیفکنید، تا برخی از کاروانیان او را برگیرند و با خود به سرزمینی دور دست ببرند.'),
(1607, 12, 11, 'پس همه بر این رأی اتفاق کردند و نزد یعقوب رفتند و گفتند: ای پدر، تو را چه دلیلی است که ما را بر یوسف امین نمی‌دانی، در حالی که ما قطعاً خیرخواه او هستیم؟'),
(1608, 12, 12, 'فردا او را با ما بفرست، که اگر با ما بیاید، در چمنزار گردش می‌کند و به بازی و تفریح می‌پردازد و ما حتماً مراقب او هستیم.'),
(1609, 12, 13, 'یعقوب در پاسخ فرزندانش گفت: این که یوسف را ببرید مرا اندوهگین می‌کند، و می‌ترسم وقتی از او غافلید گرگ او را بخورد.'),
(1610, 12, 14, 'گفتند: اگر گرگ او را بخورد با این که ما گروهی نیرومندیم، قطعاً زیانکار خواهیم بود.'),
(1611, 12, 15, 'سرانجام برادران یوسف رضایت پدرشان را جلب کردند، و هنگامی که او را با خود بردند و همگی بر آن شدند تا او را در قعر چاه بگذارند، گذشت بر یوسف آنچه گذشت. و ما به او وحی کردیم که حتماً آنان را از این کارشان که زمینه عزّت و شکوه توست با خبر خواهی کرد، ولی آنان این حقیقت را درک نمی‌کردند.'),
(1612, 12, 16, 'و عصرگاهان، گریه کنان نزد پدرشان باز آمدند.'),
(1613, 12, 17, 'گفتند: ای پدر، ما رفتیم مسابقه دهیم، و یوسف را پیش اثاثیه خود گذاشتیم و گرگ او را خورد، ولی تو ما را در آنچه می‌گوییم تصدیق نخواهی کرد هر چند در سخن خود راستگو باشیم.'),
(1614, 12, 18, 'و بر پیراهنش خونی دروغین که آشکارا نشانگر دروغ بودن آن بود آوردند. یعقوب گفت: نه، این گونه نیست، بلکه نفس شما کاری را برای شما آراسته و شما را به آن وسوسه کرده است. پس من شکیبایی می‌کنم که در دشواری‌ها شکیبایی زیباست، و تنها خداست که بر آنچه وصف می‌کنید از او یاری خواسته می‌شود.'),
(1615, 12, 19, 'یوسف در چاه بود که کاروانی دررسید. آنان آب آور خود را فرستادند. او دلو خود را در چاه انداخت. وقتی دلو را بیرون آورْد و یوسف را بر طناب دلو آویخته دید گفت: مژده باد! این یک پسر بچه است، و او را به عنوان کالایی برای فروش پنهان ساختند، و خدا از آنچه می‌کردند آگاه بود.'),
(1616, 12, 20, 'کاروانیان او را به بهایی ناچیز ـ به چند درهم ـ فروختند، و چون نگران کشف ماجرا بودند به او رغبتی نشان نمی‌دادند.'),
(1617, 12, 21, 'آن مردی که او را خرید و از اهالی مصر بود، چون در سیمای یوسف نشانه عظمت دید، به همسرش گفت: تو خود امور او را برعهده بگیر و منزلتش را نیکو بدار، امید است برای ما در کارهای مهم سودمند باشد یا او را به فرزندی اختیار کنیم. بدین سان یوسف را در آن سرزمین جای دادیم و به او قدرت و مکنت بخشیدیم تا کارهایی را که مورد نظر ما بود به انجام رسانیم و به او تعبیر رؤیاها و تحلیل رخدادها را بیاموزیم، و خدا بر کار خود چیره است، ولی بیشتر مردم این حقیقت را نمی‌دانند.'),
(1618, 12, 22, 'و چون یوسف به توانایی‌های خویش دست یافت، به او رأیی درست و دانشی ناآمیخته با جهل عطا کردیم، و نیکوکاران را این گونه سزا می‌دهیم.'),
(1619, 12, 23, 'و آن زنی که یوسف در خانه او بود از یوسف خواست که خود را در اختیار او نهد، و درها را محکم بست و به یوسف گفت: بیا که برای تو آماده شده‌ام. یوسف گفت: پناه بر خدا! او پروردگار من است که جایگاه مرا نیکو قرار داده است. کاری که مرا بدان فرا می‌خوانی ستمکاری است و ستمکاران رستگار نمی‌شوند.'),
(1620, 12, 24, 'آن زن آهنگ یوسف کرد تا از او کام بگیرد، و یوسف نیز اگر برهان پروردگارش را ندیده بود، آهنگ آن زن می‌کرد. بدین سان برهان خود را به او نمایاندیم تا گناه و زشتکاری را از او بازگردانیم، زیرا او از بندگان ما بود که برای ما خالص گشته بود.'),
(1621, 12, 25, '[یوسف که دریافت آن زن آهنگ کامجویی دارد، گریخت و او نیز در پی یوسف شتافت] وهر دو به سوی در از یکدیگر سبقت گرفتند. یوسف پیش دوید تا در را بگشاید و بگریزد، ولی آن زن به پیراهن یوسف چنگ انداخت و او را چنان به سوی خود کشید که پیراهنش را از پشت درید. در این هنگام شوهر آن زن را در آستانه در یافتند. زن به شوهر خود گفت: کیفر کسی که بخواهد به همسر تو بدی کند چیست، جز این که به زندان افکنده شود یا عذابی دردناک به او بچشانند؟'),
(1622, 12, 26, 'یوسف بی هیچ دلهره‌ای گفت: من گناهی مرتکب نشده‌ام، او از من خواست که خود را در اختیار او بگذارم. و شاهدی از کسان آن زن گواهی داد که اگر پیراهن یوسف از جلو چاک خورده، زن راست می‌گوید و او از دروغگویان است. (۲۶'),
(1623, 12, 27, 'و اگر پیراهن او از پشت دریده شده، زن دروغ می‌گوید و او از راستگویان است.'),
(1624, 12, 28, 'پس چون عزیز مصر به پیراهن یوسف نگریست و دید از پشت چاک خورده، به همسرش گفت: این از نیرنگ شما زنان است، به راستی نیرنگ شما بزرگ است.'),
(1625, 12, 29, 'ای یوسف، از این ماجرا درگذر و آن را به کسی مگوی، و ای زن، تو هم از گناه خودت پوزش بخواه که قطعاً از خطاکاران بوده‌ای.'),
(1626, 12, 30, 'و زنانی در آن شهر (از همسرانِ کارگزاران و سران)، در محافل خود به ملامت می‌گفتند: همسر عزیز مصر به اصرار از جوان زرخریدش می‌خواهد که خود را در اختیار او نهد. عشق وی در دلش جای گرفته و سخت شیفته او شده است. به راستی ما او را در گمراهی آشکاری می‌بینیم.'),
(1627, 12, 31, 'پس هنگامی که همسر عزیز عیبجوییِ مکرآمیز زنان را درباره خود شنید، نزد آنان فرستاد و آنان را به خانه خود دعوت کرد و برایشان تکیه گاهی آماده ساخت، و به هر یک از آنان کاردی داد تا میوه بخورند، و به یوسف که در اندرون بود گفت: بر آنان در آی. وقتی یوسف وارد شد و زنان او را دیدند، چنان در زیبایی بزرگش یافتند که از خود بی خود شدند و دست‌های خود را بی آن که بفهمند بریدند و گفتند: منزّه است خدا! این بشر نیست، این جز فرشته‌ای بزرگوار نیست.'),
(1628, 12, 32, 'همسر عزیز گفت: این همان جوانی است که مرا در [عشق] او سرزنش کردید، و اینک در حضور شما اعتراف می‌کنم که من از او خواستم که خودش را در اختیار من نهد و به وصال من درآید، ولی او سخت امتناع ورزید. و سوگند یاد می‌کنم که اگر آنچه را به او فرمان می‌دهم انجام ندهد قطعاً زندانی خواهد شد و از زمره حقیران خواهد گشت.'),
(1629, 12, 33, 'یوسف گفت: پروردگارا، زندان برای من از چیزی که مرا به آن فرا می‌خوانند محبوب‌تر است، و اگر نیرنگشان را از من نگردانی به آنان گرایش می‌یابم و از نادانان خواهم شد.'),
(1630, 12, 34, 'پس پروردگارش دعای او را اجابت کرد و نیرنگ همسر عزیز و سایر زنان را از او بگردانید. آری، او شنونده سخن بندگان خویش است و به حالشان داناست.'),
(1631, 12, 35, 'پس از چندی برای عزیز مصر و دیگر دولتمردان این رأی پدید آمد که یوسف را با وجود این که نشانه‌های بسیاری از پاکدامنی او دیده بودند برای مدّتی روانه زندان کنند.'),
(1632, 12, 36, 'یوسف به زندان رفت و دو جوان از بردگان پادشاه مصر نیز با او وارد زندان شدند. مدّتی گذشت. یکی از آن دو زندانی به یوسف گفت: من خود را در خواب دیدم که انگور می‌فشارم تا شراب بسازم، و دیگری گفت: من خود را در خواب دیدم که بر روی سرم نان می‌برم و پرندگان از آن می‌خورند. ما را به تعبیر خوابی که هر یک دیده‌ایم آگاه کن که ما تو را از نیکوکاران و بر تعبیر خواب توانا می‌بینیم.'),
(1633, 12, 37, 'یوسف گفت: هیچ غذایی که روزیِ شما کنند به شما نمی‌رسد مگر این که حقیقت آن را به شما خبر می‌دهم پیش از آن که به شما برسد. این دانشی نیست که بتوان آن را تحصیل کرد، این از جمله علومی است که پروردگارم به من آموخته است، زیرا من آیین مردمی را که به خدا ایمان ندارند و آخرت را منکرند رها کرده‌ام.'),
(1634, 12, 38, 'و آیین پدرانم ابراهیم و اسحاق و یعقوب را پیروی کرده‌ام. ما را نرسد که چیزی را شریک خدا سازیم. این از فضل خدا بر ما خاندان نبوّت است، و پایداری ما بر توحید از فضل خدا بر مردم است، زیرا به وسیله ما هدایت می‌شوند، ولی بیشتر مردم نعمت وجود پیامبران را سپاسگزاری نمی‌کنند.'),
(1635, 12, 39, 'ای دو همدم زندان، آیا خدایان پراکنده شایسته پرستش اند یا خداوند یکتا که بر همه موجودات چیره است؟'),
(1636, 12, 40, 'شما به جای خدا جز نام‌هایی را که خود و پدرانتان آنها را نامگذاری کرده‌اید و حقیقتی ندارند نمی‌پرستید. خداوند بر این که آنها از جایگاه شفاعت برخوردارند یا از خود در هستی تأثیری دارند، هیچ برهانی فرو نفرستاده است. حکومت در جهان هستی جز از آنِ خدا نیست. او فرمان داده است که جز وی را نپرستید. این است آن آیینی که می‌تواند جوامع بشری را بر پای دارد و از نیکبختی برخوردار سازد، ولی بیشتر مردم این حقیقت را درک نمی‌کنند.'),
(1637, 12, 41, 'ای دو همدم زندان، امّا یکی از شما دو تن از زندان آزاد می‌شود و ساقیِ دربار می‌گردد و به سرورش شراب می‌دهد، و امّا آن دیگری به دار کشیده می‌شود و پرندگان از سرش می‌خورند. امری که درباره آن از من نظر می‌خواستید حتمی شده است.'),
(1638, 12, 42, 'یوسف به آن کس از آن دو تن که دریافت نجات می‌یابد، گفت: مرا نزد سرورت یاد کن، باشد که وسیله آزادی مرا از زندان فراهم کند، ولی شیطان از خاطر او برد که یوسف را نزد سرور خود یاد کند، از این رو یوسف چند سالی در زندان ماند.'),
(1639, 12, 43, 'و پادشاه به مهتران دربارش گفت: من در خواب هفت گاو فربه را دیدم که هفت گاو لاغر آنها را می‌خورند، و هفت خوشه سبز و خوشه‌های دیگری که خشک بودند نیز در خواب دیدم. ای مهتران، اگر تعبیر خواب می‌کنید، درباره خوابم به من نظر دهید.'),
(1640, 12, 44, 'گفتند: رؤیای تو چند رشته خوابِ درهم پیچیده است و ما تعبیر این گونه خواب‌ها را نمی‌دانیم.'),
(1641, 12, 45, 'و آن کس از آن دو زندانی که نجات یافته بود و پس از مدّتی یوسف را به خاطر آورد، گفت: من شما را از تعبیر رؤیای پادشاه خبر می‌دهم، پس مرا به زندان نزد یوسف بفرستید.'),
(1642, 12, 46, 'او نزد یوسف آمد و گفت: یوسف، ای مرد بسیار راستگوی، درباره دیدنِ هفت گاو فربه در خواب که هفت گاو لاغر آنها را می‌خورند و هفت خوشه سبز و خوشه‌های دیگری که خشک بودند به ما نظر ده، امیدوارم با دانستن تعبیر این خواب به سوی مردم بازگردم، باشد که آنان از حقیقت آن آگاه شوند.'),
(1643, 12, 47, 'یوسف گفت: خواب پادشاه پیامی است بدین مضمون: هفت سال پی درپی کشت می‌کنید، و آنچه درو خواهید کرد ـ جز اندکی را که در این سال‌ها می‌خورید ـ در خوشه اش واگذارید تا فاسد نشود.'),
(1644, 12, 48, 'آن گاه پس از آن، هفت سال سخت می‌آید که آنچه را برای آن هفت سال ذخیره کرده‌اید ـ جز اندکی را که برای بذر نگاه می‌دارید ـ خواهند خورد.'),
(1645, 12, 49, 'آن گاه پس از آن، سالی فرا می‌رسد که در آن سال مردم از باران برخوردار می‌شوند و گیاهان می‌رویند و مردم در آن سال از دام‌های خود شیر فراوان می‌دوشند.'),
(1646, 12, 50, 'فرستاده دربار بازگشت و تعبیر خواب پادشاه را گزارش کرد. پادشاه فرمان آزادی یوسف را صادر نمود و گفت: او را نزد من آورید. هنگامی که فرستاده پادشاه نزد یوسف آمد، یوسف همراه او نرفت و گفت: نزد سرورت باز گرد و از او بپرس وضع آن زنانی که دست خود را بریدند چگونه بود؟ البتّه پروردگار من به نیرنگشان آگاه است.'),
(1647, 12, 51, 'پادشاه، زنان را احضار کرد و گفت: داستان شما آن گاه که به اصرار از یوسف خواستید خودش را در اختیار شما نهد چه بود؟ گفتند: منزّه است خدا! ما هیچ گناهی از او سراغ نداریم. همسر عزیز گفت: اکنون حقیقت آشکار شد. من به اصرار از او خواستم که خودش را در اختیار من بگذارد و او چنین نکرد، و بی تردید او از راستگویان است.'),
(1648, 12, 52, 'یوسف در پی این ماجرا گفت: من از آن روی فرستاده پادشاه را بازگرداندم و بازجویی از زنان را درخواست کردم که عزیز مصر بداند من در نهان به او خیانت نکردم و بداند که خدا نیرنگ خیانتکاران را به نتیجه نمی‌رساند.'),
(1649, 12, 53, 'و افزود: من نفس خود را از گرایش به بدی‌ها تبرئه نمی‌کنم، چرا که نفس آدمی بسیار به بدی فرمان می‌دهد، مگر کسی را که پروردگار من بر او رحم کند، به راستی پروردگار من آمرزنده و مهربان است.'),
(1650, 12, 54, 'پس از آن که پاکدامنی یوسف ثابت شد، پادشاه گفت: یوسف را نزد من آورید که او را از خواص خود قرار خواهم داد. هنگامی که یوسف را نزد پادشاه آوردند و پادشاه با او سخن گفت و عظمت وی نزد پادشاه بیشتر آشکار شد، او را به صدارت منصوب کرد و گفت: تو امروز نزد ما مقامی بلند داری و در همه کارهای ما امین هستی.'),
(1651, 12, 55, 'یوسف گفت: مرا بر خزانه‌های این سرزمین بگمار تا آذوقه مردم را تأمین کنم و در دوران قحطی میانشان توزیع نمایم، که من نگهبان امور و به اداره کارها دانایم.'),
(1652, 12, 56, 'و این گونه به یوسف در آن سرزمین قدرت دادیم که هر جای آن می‌خواست منزل می‌گزید. هر که را بخواهیم از رحمت خود برخوردار می‌سازیم و پاداش نیکوکاران را تباه نمی‌کنیم.'),
(1653, 12, 57, 'و قطعاً پاداش آخرت برای کسانی که ایمان آورده و به درجات بالای ایمان رسیده و پیش از آن تقوا پیشه بوده‌اند، بهتر است.'),
(1654, 12, 58, 'سال‌های قحطی فرا رسید و برادران یوسف برای تهیّه آذوقه به مصر آمدند و بر یوسف وارد شدند. یوسف آنان را شناخت ولی آنان او را نشناختند.'),
(1655, 12, 59, 'و چون برادرانش را به خوار و بارشان مجهّز کرد، گفت: آن برادر پدریِ خود را که از او سخن گفتید، در نوبت بعدی نزد من آورید. آیا نمی‌بینید که من پیمانه را به طور کامل می‌دهم و به کسی ستم روا نمی‌دارم و بهترین میزبانانم؟'),
(1656, 12, 60, 'اگر او را نزد من نیاورید، هیچ سهمیّه‌ای نزد من نخواهید داشت و نباید برای خرید آذوقه به این سرزمین وارد شوید و نزد من بیایید.'),
(1657, 12, 61, 'گفتند: از پدرش به اصرار خواهیم خواست که او را به ما بسپرد تا همراه ما نزد تو آید و قطعاً این کار را خواهیم کرد.'),
(1658, 12, 62, 'یوسف به خدمتگزاران خود گفت: سرمایه آنان را که در برابر آذوقه پرداخته‌اند در اثاثیه شان بگذارید، شاید وقتی به سوی کسانشان بازگشتند به آن پی ببرند، امید که آنان با برادرشان به سوی ما باز آیند.'),
(1659, 12, 63, 'پس هنگامی که نزد پدرشان بازگشتند، گفتند: ای پدر، بدون همراهی برادرمان بنیامین آذوقه از ما منع شده است، پس برادرمان را با ما بفرست که اگر او بیاید آذوقه خواهیم گرفت و قطعاً ما نگهبان او خواهیم بود.'),
(1660, 12, 64, 'یعقوب به فرزندانش گفت: آیا جز همان گونه که شما را پیش‌تر درباره برادرش یوسف امین شمردم و به شما اطمینان کردم و آن حوادث روی داد، درباره او نیز شما را امین بشمارم و به شما اطمینان کنم؟ پس باید به خدا و نگهبانی او دل بست که خدا بهترین نگهبان و مهربانترین مهربانان است.'),
(1661, 12, 65, 'در این هنگام فرزندان یعقوب به سراغ کالاهای خود رفتند و وقتی آنها را گشودند، دیدند که سرمایه شان به آنان باز گردانده شده است. نزد پدر آمدند و گفتند: ای پدر، ما چه می‌خواهیم؟ این سرمایه ماست که به ما بازگردانده شده است. ما در سفر به مصر نه تنها از گزند عزیز در امانیم بلکه از کَرَم او نیز بهره مند خواهیم بود و برای خاندان خود آذوقه می‌آوریم و از برادرمان مراقبت می‌کنیم و بار شتری بر سهم خود می‌افزاییم که دستیابی به این سهمیه آسان است.'),
(1662, 12, 66, 'یعقوب گفت: هرگز بنیامین را با شما نمی‌فرستم تا این که وثیقه‌ای الهی به من بسپارید که او را حتماً به من بازگردانید، مگر این که از هر سو محاصره شوید و ناتوان گردید. پس هنگامی که وثیقه خود را به یعقوب سپردند، او گفت: خدا بر آنچه گفتیم ناظر و نگهبان باشد.'),
(1663, 12, 67, 'هنگامی که فرزندان یعقوب عازم سفر شدند پدرشان به آنان گفت: ای پسران من، همه باهم از یک دروازه وارد نشوید، بلکه از دروازه‌های گوناگون وارد شوید. و بدانید که اگر گزندی برای شما مقدّر شده باشد، من با این سفارش خود نمی‌توانم اندکی از آنچه را که خدا مقدّر کرده است از شما باز دارم، زیرا فرمانروایی فقط از آنِ خداست و من بر او توکّل کرده‌ام، و توکّل کنندگان فقط باید بر او توکّل کنند.'),
(1664, 12, 68, 'و چون از همان جایی که پدرشان به آنان فرمان داده بود وارد مصر شدند تدبیر یعقوب، بلایی را که از جانب خدا مقدّر شده بود برطرف نساخت، لیکن خدا حاجتی را که در دل یعقوب بود برآورد، و قطعاً او بدان سبب که ما به وی آموزش داده بودیم، از دانشی خاص بهره مند بود، ولی بیشتر مردم آن را نمی‌دانند و از آن بهره‌ای ندارند.'),
(1665, 12, 69, 'و هنگامی که بر یوسف وارد شدند، یوسف برادرش بنیامین را نزد خود جای داد و به او گفت: بی گمان من برادر توام و چاره‌ای اندیشیده‌ام تا تو را نزد خود نگه دارم، لذا بر آنچه مأموران انجام می‌دهند اندوهگین مباش.'),
(1666, 12, 70, 'پس هنگامی که یوسف برادرانش را به خواروبارشان مجهّز کرد، جام پادشاه را در بار برادرش نهاد. پس از حرکتِ آنان، نداکننده‌ای از کارگزارانِ یوسف بانگ برآورد: ای کاروانیان، قطعاً شما دزد هستید.'),
(1667, 12, 71, 'برادران یوسف روی به آنان کردند و گفتند: چه گم کرده‌اید؟'),
(1668, 12, 72, 'گفتند: جام پادشاه را گم کرده‌ایم، و یوسف گفت: هرکه آن را بیابد و نزد ما بیاورد، مزدش یک بارِ شتر آذوقه است و من خود ضامن آنم.'),
(1669, 12, 73, 'گفتند: به خدا سوگند، شما قبلاً از کار ما جویا شدید و دانستید که ما به این سرزمین نیامده‌ایم برای این که تباهی کنیم و ما هرگز دزد نبوده‌ایم.'),
(1670, 12, 74, 'گفتند: اگر شما دروغ گفته باشید و دزد در میان شما باشد، کیفر او چیست؟'),
(1671, 12, 75, 'گفتند: کیفر سرقت این است که هر کس جام پادشاه در بار او پیدا شود، خودش کیفر آن است که به بردگی صاحب کالا درمی آید. ما بدین سان ستمکاران (سارقان) را کیفر می‌دهیم.'),
(1672, 12, 76, 'پس از تعیین کیفر سرقت، [یوسف] بازرسی بارهای آنان را پیش از بازرسی بار برادرش آغاز کرد، آن گاه جام را از ظرف برادرش بنیامین درآورْد. این گونه برای یوسف تدبیر کردیم که برادرش را نزد خود نگه دارد، زیرا در آیین پادشاه، یوسف حق نداشت برادرش را، هر چند دزدی کرده باشد، بگیرد و نگه دارد، مگر این که مجرم بخواهد براساس آیین خودش کیفر داده شود، و خدا چنین خواست که برادران یوسف خود کیفر سارق را تعیین کنند. ما هر که را بخواهیم به درجاتی بالا می‌بریم، همان گونه که یوسف را به درجاتی بر برادرانش برتری دادیم، و برتر از هر صاحب دانشی دانشمندی هست.'),
(1673, 12, 77, 'برادران یوسف گفتند: اگر او دزدی می‌کند دور از انتظار نیست، زیرا او برادری داشت که پیش‌تر دزدی کرده بود. یوسف این اتّهام را در دل خود نهان داشت و آن را برایشان آشکار نکرد، فقط گفت: شما جایگاهی بدتر دارید و خداوند به آنچه وصف می‌کنید داناتر است.'),
(1674, 12, 78, 'گفتند: ای عزیز، او پدری پیر و کهنسال دارد، یکی از ما را به جای او بگیر و او را آزاد کن، که ما تو را از نیکوکاران می‌بینیم.'),
(1675, 12, 79, 'یوسف گفت: پناه به خدا از این که جز آن کسی را که کالای خود را نزد او یافته‌ایم بگیریم، زیرا در آن صورت قطعاً ستمکار خواهیم بود.'),
(1676, 12, 80, 'پس هنگامی که از او در مورد بنیامین ناامید شدند، به کناری رفتند و محرمانه درباره تصمیمات خود به گفتوگو پرداختند. بزرگشان گفت: آیا ندانسته‌اید و فراموش کرده‌اید که پدرتان از شما پیمانی الهی گرفت که فرزندش را به او بازگردانید؟ و پیش‌تر هم درباره یوسف کوتاهی کردید و گفتید از او مراقبت می‌کنیم، ولی او را در چاه انداختید. من هرگز از این سرزمین دور نمی‌شوم مگر این که پدرم از آن پیمانی که بستیم صرف نظر کند و به من اجازه بازگشت دهد، یا خدا در حقّ من حکم کند و راه نجاتی برایم قرار دهد که او بهترین حکم کنندگان است.'),
(1677, 12, 81, 'نزد پدرتان بازگردید و به او بگویید: ای پدر، پسرت بنیامین دزدی کرد، و ما درباره کیفر سرقت جز آنچه می‌دانستیم گواهی ندادیم و از غیب هم آگاه نبودیم و نمی‌دانستیم که او سرقت کرده است، وگرنه کیفر سرقت را به آنان نمی‌گفتیم و به آن رضایت نمی‌دادیم.'),
(1678, 12, 82, 'و برای این که در سخن شما تردید نکند به او بگویید: از آن سرزمینی که ما در آن بودیم و از کاروانی که با آن بدین جا روی آوردیم و شاهد ماجرا بودند بپرس، و قطعاً ما راست می‌گوییم.'),
(1679, 12, 83, 'یعقوب پس از شنیدن این سخنان گفت: نه، چنین نیست، بلکه نفس شما کاری را برای شما آراسته و شما را به آن وسوسه کرده است. من صبر می‌کنم که در دشواری‌ها شکیبایی نیکوست، امید است خدا هر سه نفرشان را به من بازگرداند. قطعاً او به بندگانش داناست و کارهایش براساس حکمت است.'),
(1680, 12, 84, 'سپس از آنان روی برتافت و گفت: افسوس بر یوسف! و هر دو چشمش از اندوه سفید گشت، چرا که خشمش را در خود نگاه می‌داشت و اندوهش را فرو می‌خورد.'),
(1681, 12, 85, 'فرزندانش به او گفتند: به خدا سوگند تو پیوسته یوسف را به خاطر می‌آوری و بر او اندوه می‌خوری تا سرانجام در آستانه تباهی قرار گیری یا هلاک شوی.'),
(1682, 12, 86, 'یعقوب گفت: من درد و اندوه خود را با شما نمی‌گویم و آن را فقط با خدا در میان می‌گذارم و از او چاره می‌جویم، و از صفات خدا چیزهایی می‌دانم که شما نمی‌دانید و به همین سبب به رحمت او دل بسته‌ام.'),
(1683, 12, 87, 'سپس گفت: ای پسران من، بروید و از یوسف و برادرش جست و جو کنید و از رحمت خدا و آسایشی که پس از سختی‌ها عطا می‌کند ناامید نشوید، زیرا حقیقت این است که جز کسانی که باور ندارند خدا بر زدودن رنج‌ها تواناست، کسی از رحمت خدا مأیوس نمی‌شود.'),
(1684, 12, 88, 'پسران یعقوب رهسپار مصر شدند. هنگامی که بر یوسف درآمدند، گفتند: ای عزیز، به ما و خانواده ما آسیب رسیده و در تنگنا قرار گرفته‌ایم. این سرمایه‌ای که برای خرید آورده‌ایم ناچیز و کمتر از بهای آذوقه‌ای است که بدان نیازمندیم، تو پیمانه را به طور کامل به ما عطا کن و برادرمان را به ما ببخش که خداوند بخشندگان را پاداش می‌دهد.'),
(1685, 12, 89, 'یوسف گفت: آیا دانستید آن گاه که نادان بودید با یوسف و برادرش چه کردید؟'),
(1686, 12, 90, 'گفتند: بی تردید، تو خود، یوسفی. چنین نیست؟ گفت: چرا، من یوسفم و این برادر من است. قطعاً خداوند نعمتی بزرگ به ما ارزانی داشت. زیرا حقیقت این است که هر کس تقوا پیشه کند و شکیبایی ورزد نیکوکار است و خدا پاداش نیکوکاران را تباه نمی‌کند.'),
(1687, 12, 91, 'برادران یوسف گفتند: به خدا سوگند که خدا تو را برگزیده و بر ما برتری داده است و قطعاً ما در اشتباه بوده‌ایم.'),
(1688, 12, 92, 'یوسف گفت: امروز بر شما هیچ سرزنشی نیست. خدا شما را می‌آمرزد و مهربانترین مهربانان است.'),
(1689, 12, 93, 'سپس به برادرانش فرمود: این پیراهن مرا ببرید و آن را بر چهره پدرم بیفکنید، او بدین سبب بینا می‌شود، و همه کسان خود را نزد من آورید.'),
(1690, 12, 94, 'و هنگامی که کاروان به راه افتاد و از مصر فاصله گرفت، پدرشان به تنی چند از پسرانش که نزد او بودند گفت: اگر مرا کم خرد نشمارید، بی شک من بوی یوسف را می‌شنوم.'),
(1691, 12, 95, 'گفتند: به خدا سوگند تو در همان اشتباه گذشته خود هستی که یوسف را این گونه دوست می‌داری.'),
(1692, 12, 96, 'و هنگامی که مژده رسان آمد، پیراهن را بر چهره یعقوب افکند و او بینا شد. او به فرزندانش گفت: مگر به شما نگفتم که من از صفات خدا چیزهایی می‌دانم که شما نمی‌دانید؟'),
(1693, 12, 97, 'گفتند: ای پدر، از خدا برای ما آمرزش گناهانمان را بخواه که ما در اشتباه بوده‌ایم.'),
(1694, 12, 98, 'یعقوب گفت: به زودی از پروردگارم برای شما آمرزش می‌طلبم. قطعاً اوست که بسیار آمرزنده و مهربان است.'),
(1695, 12, 99, 'یعقوب با کسان خود رهسپار مصر شد و یوسف در بیرون شهر به استقبال آنان رفت. هنگامی که بر یوسف وارد شدند، یوسف پدر و مادرش را در آغوش گرفت و گفت: همه وارد مصر شوید که به خواست خدا آن جا در امن و امان خواهید بود.'),
(1696, 12, 100, 'و پدر و مادرش را بر تخت نشانید، و همه در برابر یوسف بر زمین افتادند و سجده کردند، و یوسف گفت: ای پدر، این است تعبیر آن خوابی که پیش‌تر دیدم، پروردگارم اکنون به آن تحقّق بخشیده است، به راستی خدا به من نیکی کرد آن گاه که مرا از زندان خارج ساخت و شما را از صحرا به این جا آورْد، پس از آن که شیطان میان من و برادرانم فتنه ایجاد کرد. به راستی پروردگارم آنچه بخواهد به لطف و کَرَمش انجام می‌دهد، چرا که او به بندگانش داناست و کارهایش از روی حکمت است.'),
(1697, 12, 101, 'آن گاه با تمام وجود به خدا روی آورد و گفت: پروردگارا، تو از سلطنت بهره‌ای به من دادی و از تعبیر خواب‌ها و تحلیل رویدادها به من آموختی. ای پدیدآورنده آسمان‌ها و زمین، تنها تویی که در دنیا و آخرت سرپرست منی، مرا تا پایان عمر تسلیم خود گردان و مرا به شایستگان ملحق کن.'),
(1698, 12, 102, 'ای پیامبر، این سرگذشت از خبرهای غیب است که آن را به تو وحی می‌کنیم، و تو آن گاه که برادران یوسف همگی بر کار خود مصمم شدند و درباره یوسف بداندیشی می‌کردند نزد آنان نبودی که از اخبارشان آگاه شوی.'),
(1699, 12, 103, 'و بیشتر مردم ایمان نمی‌آورند، هر چند تو خواستار ایمان آنان باشی و در این راه سخت بکوشی.'),
(1700, 12, 104, 'این در حالی است که تو از آنان برای ابلاغ قرآن هیچ مزدی نمی‌طلبی تا به بهانه غرامت ایمان نیاورند. قرآن برای جهانیان یادآور آیات خدا و زداینده غفلت هاست، نه کالایی که به وسیله آن مقام و ثروت فراهم گردد.'),
(1701, 12, 105, 'و چه بسیار نشانه‌هایی بر یکتایی خدا در آسمان‌ها و زمین است که مردم بر آن می‌گذرند ولی از آنها روی می‌گردانند و آنها را درک نمی‌کنند.'),
(1702, 12, 106, 'و بیشتر کسانی که به خدا ایمان دارند، در همان حال مشرکند و ایمانشان به شرک آلوده است.'),
(1703, 12, 107, 'آیا کسانی که ایمان نمی‌آورند ایمنند از این که عذابی فراگیر از جانب خدا آنان را احاطه کند، یا قیامت ـ در حالی که بی خبرند ـ ناگهان بر آنان در رسد؟'),
(1704, 12, 108, 'ای پیامبر، بگو: این است راه من که از روی بصیرت، مردم را به خدای یکتا فرا می‌خوانم. این است راه من و کسانی که مرا پیروی کرده‌اند. آری، منزّه است خدا از این که شریک داشته باشد، و من از مشرکان نیستم.'),
(1705, 12, 109, 'ای پیامبر، تو نخستین بشر نیستی که از میان مردم به سوی آنان مبعوث شده‌ای، پیش از تو نیز به رسالت نفرستادیم مگر مردانی را که به آنان وحی می‌کردیم، مردانی از اهالی شهرهایی که به سویشان فرستاده شدند. آیا قوم تو در زمین سیر نکرده‌اند تا بنگرند فرجام کسانی که پیش از آنان می‌زیستند و پیامبران و آیات خدا را انکار کردند، چگونه بوده است؟ حقّا سرای آخرت برای کسانی که تقوا پیشه نموده‌اند بهتر است، آیا درنمی یابید؟'),
(1706, 12, 110, 'پیامبران مردم را به یکتاپرستی فرا خواندند و آنان را به عذاب الهی هشدار دادند، تا آن گاه که از ایمان مردم ناامید شدند و مردم پنداشتند که به پیامبران در مورد عذاب کافران دروغ گفته شده است یاری ما به آنان رسید، پس هر که را خواستیم نجات داده شد، و عذاب ما از مردم گنهکار بازگردانده نمی‌شود.'),
(1707, 12, 111, 'به یقین در سرگذشت یوسف و برادرانش، برای خردمندان درس عبرتی است. این داستان داستانی بافته شده نیست، بلکه تصدیق کننده تورات است که پیش از قرآن بوده، و روشنگر همه امور دینی مردم است و رهنمود و رحمتی است برای مردمی که ایمان می‌آورند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'الف، لام، میم، را. این پدیده‌های جهان هستی همه نشانه‌هایی بر یکتایی خداوندند، و کتابی که از جانب پروردگارت به سوی تو نازل شده سراسر حق است، ولی بیشتر مردم به آن نشانه‌ها و به این کتاب آسمانی ایمان نمی‌آورند.'),
(1709, 13, 2, 'خداست آن که آسمان‌ها را بدون ستونهایی که آنها را ببینید برافراشت، سپس بر عرش فرمانروایی تسلط یافت و خورشید و ماه را رام ساخت. هر کدام تا سر آمدی معیّن در حرکتند. او کار جهان را تدبیر می‌کند و موجودات را که نشانه‌های یکتایی اویند از هم جدا می‌سازد تا بدانید که مجرمان و نیکوکاران را نیز از هم جدا خواهد کرد، باشد که به دیدار خدا و برپایی قیامت یقین کنید.'),
(1710, 13, 3, 'و اوست آن که زمین را گسترانید تا درخورِ رویش گیاهان و پدید آمدن جانداران باشد، و در آن کوه‌هایی استوار برای ذخیره شدن آب‌ها و جویبارهایی برای روان ساختن آب به کشتزارها قرار داد، و از هر میوه‌ای که رویش آن در زمین امکان داشت، انواع گوناگونی در آن آفرید. پیوسته تاریکی شب را بر روز می‌پوشانَد تا زمینه آرامش مردم فراهم شود. به راستی در اینها برای مردمی که می‌اندیشند نشانه‌هایی بر یکتایی خداست.'),
(1711, 13, 4, 'و در زمین قطعاتی است با خاکی همسان که مجاور یکدیگرند، و در آن باغ‌هایی از انگور و کشتزار و درختان خرماست که برخی بر یک ریشه روییده‌اند و برخی جدا جدا بر ریشه‌هایی مختلف قرار دارند و همه با یک آب سیراب می‌شوند ولی مختلفند و ما بعضی از آنها را از جهت میوه بر بعضی دیگر برتری می‌دهیم. به یقین، در اینها برای مردمی که خرد ورزند نشانه‌هایی بر یکتایی خداست.'),
(1712, 13, 5, 'و اگر تعجب می‌کنی، از سخن منکران رستاخیز تعجّب کن که می‌گویند: آیا وقتی خاک شدیم، واقعاً در آفرینشی جدید قرار می‌گیریم؟ اینان کسانی اند که به پروردگارشان کافر شده‌اند. اینانند که بر گردن هایشان زنجیرهاست، و اینان همدم آتشند و در آن جاودانه خواهند بود.'),
(1713, 13, 6, 'کافران پیش از درخواست رحمت و سلامت، از تو می‌خواهند که هر چه زودتر عذاب الهی را بر آنان فرود آوری، در حالی که پیش از آنان عقوبت‌هایی عبرت انگیز بر منکران رسالت پیامبران رفته است و آنان از آن آگاهند. به یقین، پروردگار تو برای مردم ـ با وجود ستمکاری شان ـ صاحب مغفرت است و البته پروردگار تو سخت کیفر نیز هست.'),
(1714, 13, 7, 'کافران قرآن را نشانه رسالت تو نمی‌شمرند، لذا می‌گویند: چرا از جانب خدا معجزه‌ای همچون معجزات موسی و عیسی بر او فرستاده نشده است؟ ای پیامبر، آوردن معجزه در اختیار تو نیست، تو فقط بیم دهنده و هدایت کننده‌ای، و هر قومی هدایت کننده‌ای دارد.'),
(1715, 13, 8, 'خدا می‌داند که هر ماده‌ای چه در رحم دارد، و نیز از خون‌هایی که رحم‌ها در دوران بارداری جذب می‌کنند و به مصرف غذای جنین می‌رسانند و خون‌هایی که زیاد آورده به مصرف نمی‌رسانند و دفع می‌کنند آگاه است. چگونه آگاه نباشد، در صورتی که هر چیزی نزد او اندازه‌ای معین دارد.'),
(1716, 13, 9, 'دانای نهان و آشکار، و بزرگ و بلند مرتبه است.'),
(1717, 13, 10, 'نزد او یکسان است آن کس از شما که سخنش را نهان دارد و کسی که آن را آشکار کند، و آن کس که در شب خود را پنهان سازد و کسی که در روز به طور آشکار راه پیماید.'),
(1718, 13, 11, 'آدمی فرشتگانی در پی دارد که همواره از پیش روی و از پشت سرش او را احاطه کرده‌اند و به فرمان خدا او را از گزندهایی که برخاسته از فرمان خداست حفظ می‌کنند. این موهبت همچنان ادامه دارد، چرا که خداوند نعمتی را که به مردمی داده است به بلا و محنت تبدیل نمی‌کند تا این که آنان خود، خویشتن را دگرگون سازند و به کفر و ناسپاسی روی بیاورند. و هنگامی که خدا محنت و بلایی را برای مردمی بخواهد بازگشتی برای آن نیست و جز خدا هیچ سرپرستی در برابر آن محنت و بلا نخواهند داشت.'),
(1719, 13, 12, 'اوست آن که برق را در آسمان به شما می‌نمایاند تا در گروهی بیم و در گروهی امید به وجود آورَد، و اوست که ابرهای گرانبار را پدید می‌آورد.'),
(1720, 13, 13, 'رعد، همراه با ستایش خدا، و فرشتگان از هیبت او تسبیح او می‌گویند، و اوست که صاعقه‌ها را فرو می‌فرستد آنها را به هر که بخواهد می‌رساند. با این حال، مشرکان درباره ربوبیّت خدا مجادله می‌کنند و آن را ویژه معبودان خود می‌دانند، و حال آن که خدا در مجادله با آنان و رسوا ساختنشان سخت تواناست.'),
(1721, 13, 14, 'دعای راستین ویژه خداست، و کسانی که مشرکان آنها را به جای خدا می‌خوانند، هیچ پاسخی به آنان نمی‌دهند و اندکی از خواسته هایشان را هم تأمین نمی‌کنند، مگر مانند تشنه‌ای که دو دستش را از دور به سوی آب می‌گشاید تا آب به دهانش برسد ولی آب به دهانش نمی‌رسد. آری، دعای کافران جز در بیراهه نیست و قطعاً کارگشا نخواهد بود.'),
(1722, 13, 15, 'همه کسانی که در آسمان‌ها و زمینند، خواه ناخواه در برابر خدا خاضعند و برای او سجده می‌کنند، و نیز سایه هایشان در هر بامداد و عصرگاه.'),
(1723, 13, 16, 'به مشرکان بگو: پروردگار آسمان‌ها و زمین و تدبیرکننده آنها کیست؟ بگو: خداست. بگو: با این حال، آیا غیر او را سرپرستانی برای خود برگرفته‌اید که مالک هیچ سود و زیانی برای خود نیستند تا چه رسد به شما؟ بگو: آیا کافران که از دیدن حقایق کورند با مؤمنان که چشم بصیرت دارند برابرند؟ آیا تاریکی‌های کفر با نور ایمان یکسان است؟ آیا آنان برای خدا همتایانی پنداشته‌اند که مانند آفریده‌های خدا را آفریده‌اند و این دو دسته آفریده بر آنان مشتبه شده است؟ بگو: خدا آفریننده هر چیزی است، زیرا او یگانه و بر همه موجودات چیره است.'),
(1724, 13, 17, 'خداوند از آسمان آبی خالص فرو فرستاده است، و رودها هر کدام به اندازه گنجایش خود آب برگرفته و جاری شده‌اند، آن گاه سیل، کفی برآمده، بر روی خود برداشته است، و از آنچه مردم بر آن آتش می‌افروزند تا با ذوب کردن آنها زیور یا کالایی به دست آورند، کفی مانند کف سیلاب برمی آید. خداوند، حق و باطل را این گونه مَثَل می‌زند، حق مانند آب و فلز دارای ثبات، و باطل همچون کف سیلاب و کف فلزّ محو شدنی است. آری، کف به کناری می‌رود و نابود می‌شود، ولی آنچه به مردم سود می‌رساند در زمین باقی می‌ماند. خداوند مَثَل‌ها را این گونه تبیین می‌کند.'),
(1725, 13, 18, 'برای کسانی که دعوت پروردگارشان را اجابت کرده‌اند نیکوترین فرجام خواهد بود، و کسانی که دعوت او را نپذیرفته‌اند، اگر همه آنچه در زمین است همراه با برابر آن مال ایشان باشد، آن را برای نجات خود فدیه خواهند داد. آنان حساب بدی خواهند داشت و جایگاهشان دوزخ است و آن بد بستری است.'),
(1726, 13, 19, 'آیا کسی که بیناست و می‌داند آنچه از جانب پروردگارت بر تو نازل شده حق است، مانند کسی است که کور است و درنمی یابد؟ آنان برابر نیستند، زیرا حق بینان خردمندند و تنها خردمندان متذکّر می‌شوند.'),
(1727, 13, 20, 'همانان که به عهدشان با خدا وفا می‌کنند (به توحید و لوازم آن پای بندند) و آن پیمان را نمی‌شکنند.'),
(1728, 13, 21, 'و کسانی که آنچه را خدا به پیوستنش فرمان داده است می‌پیوندند و از کیفر پروردگارشان می‌ترسند و از سختیِ حساب اعمالشان بیم دارند.'),
(1729, 13, 22, 'و آنان که برای طلب خشنودی پروردگارشان، بر دشواری‌ها، فرمانبرداری از خدا و پرهیز از گناهان صبر کرده و نماز را برپا داشته و از آنچه روزیشان کرده‌ایم، در نهان و آشکار انفاق کرده‌اند، و بدی را با نیکی می‌زدایند. اینانند که فرجام نیکوی این سرا را خواهند داشت.'),
(1730, 13, 23, 'همان بوستان‌های دایمی بهشت که خردمندان همراه با پدران و همسران و فرزندانشان که شایسته آن هستند وارد آن می‌شوند و فرشتگان از هر دری بر آنان درمی آیند.'),
(1731, 13, 24, 'و به آنان می‌گویند: سلام بر شما به پاداش این که صبر کردید. فرجام این سرای برای کسانی که بر کردار شایسته پایدار بوده‌اند فرجامی نیکوست.'),
(1732, 13, 25, 'و کسانی که عهدشان را با خدا پس از بستن آن می‌شکنند و آنچه را خدا به پیوستن آن فرمان داده است می‌گسلند، و با انجام کارهای ناشایسته در زمین فساد می‌کنند، برای آنان دوری از رحمت خدا خواهد بود و ناگواری این سرا را خواهند داشت.'),
(1733, 13, 26, 'خداست که روزی را برای هر که بخواهد وسعت می‌بخشد و بر هر که بخواهد تنگ می‌گیرد، به همین سبب بهشت را برای مؤمنان، و ناگواری و دوری از رحمت خود را برای کافران و تبهکاران قرار می‌دهد. آنان به زندگی دنیا شادمان شدند و به آن اکتفا کردند، در حالی که زندگی دنیا در برابر آخرت جز بهره‌ای اندک نیست که سرانجام از میان خواهد رفت.'),
(1734, 13, 27, 'کفرپیشگان، قرآن را معجزه رسالت پیامبر نمی‌شمرند و می‌گویند: چرا از جانب پروردگارش معجزه‌ای بر او نازل نشده است تا ما بدین وسیله هدایت شویم؟ بگو: هدایت انسان‌ها به اراده خداست نه دیدن معجزه. خدا هر که را بخواهد گمراه می‌کند و برای کسانی چنین می‌خواهد که به او باز نیایند، و هر که به سوی او بازگردد، او را به سوی خود هدایت می‌کند.'),
(1735, 13, 28, 'بازآمدگان به سوی خدا کسانی اند که به او ایمان آورده‌اند و دل هایشان به یاد خدا آرام می‌گیرد. بدانید که تنها با یاد خدا دل‌ها آرامش می‌یابد.'),
(1736, 13, 29, 'کسانی که ایمان آورده و کارهای شایسته کرده‌اند، چون دلشان به یاد خدا آرامش یافته است، خوش‌ترین زندگی را خواهند داشت و برای آنان فرجامی نیکو خواهد بود.'),
(1737, 13, 30, 'همان گونه که سنّت ما تاکنون بر این جاری بوده است که مردم را به توحید فراخوانیم، تو را نیز در میان امّتی که پیش از آن امّت‌هایی گذشته‌اند به رسالت فرستادیم تا آنچه را به تو وحی کرده‌ایم بر آنان بخوانی، ولی آنان خدای رحمان را انکار می‌کنند. بگو: او پروردگار من است. معبودی شایسته پرستش جز او نیست. فقط بر او توکّل کرده‌ام و برای نیازهای خود به او رجوع می‌کنم.'),
(1738, 13, 31, 'و اگر قرآنی بود که کوه‌ها به وسیله آن از جای برکنده و روانه می‌شد، یا زمین به آن شکافته و قطعه قطعه می‌گشت، یا مردگان به وسیله آن زنده می‌شدند و درباره رستاخیز با آنان سخن گفته می‌شد. باز هم کافران هدایت نمی‌شدند، مگر این که خدا می‌خواست. آری، تنها معجزه سبب هدایت مردم نیست، بلکه کارها همه در اختیار خداست. با این وصف آیا مؤمنان ندانسته‌اند که خدا نمی‌خواهد این کفرپیشگان هدایت شوند و اگر خدا بخواهد همه مردم را هدایت می‌کند؟ و آیا مؤمنان از هدایت یافتن این کافران ناامید نشده‌اند که می‌خواهند معجزه‌ای جز قرآن برای آنان آورده شود تا ایمان بیاورند؟ و کسانی که رسالت تو را انکار کرده‌اند، پیوسته به سبب کفرشان به خدای رحمان، حادثه‌ای کوبنده به آنان می‌رسد، یا آن حادثه نزدیک خانه هایشان فرود می‌آید و آثار آن دامنگیرشان می‌شود. این امر همچنان ادامه دارد تا این که وعده خدا فرا رسد و نابودشان کند. به یقین، خداوند وعده خود را خلاف نمی‌کند.'),
(1739, 13, 32, 'سرنوشت پیشینیان گواه این وعده است، چرا که پیش از تو نیز پیامبرانی مورد استهزا قرار گرفتند و من به کسانی که کافر شده بودند مهلت دادم، سپس آنان را به عذاب گرفتم، پس کیفر من چگونه بود؟ آیا کافران توانستند از آن بگریزند؟'),
(1740, 13, 33, 'پس آیا خدا که بر هر شخصی چیره است و همه امور را او تدبیر می‌کند شریک دارد؟ ولی کافران برای خدا شریکانی پنداشته‌اند. بگو: آنها را وصف کنید تا برای شما روشن شود که درخورِ پرستش نیستند. آیا خدا را به شریک‌هایی خبر می‌دهید که او از وجودشان در زمین آگاه نیست، یا سخن شما سطحی و بی محتواست؟ کافران هیچ دلیلی بر شرک خود ندارند، بلکه برای آنان نیرنگشان آراسته شده و از راه درست بازداشته شده‌اند. و هر که را خدا به بیراهه کشد، هدایت کننده‌ای برای او نخواهد بود.'),
(1741, 13, 34, 'برای آنان در زندگی دنیا عذابی است، و قطعاً عذاب آخرت دشوارتر است، و به جای خدا نگهدارنده‌ای از عذاب نخواهند داشت.'),
(1742, 13, 35, 'وصف آن بهشتی که به تقواپیشگان وعده داده شده این است که از زیر درختان آن نهرها جاری است. میوه آن همیشگی و سایه اش پایدار است. این فرجام کسانی است که تقواپیشه بوده‌اند و فرجام کفرپیشگان آتش است.'),
(1743, 13, 36, 'ای پیامبر، کسانی که به آنان کتاب آسمانی داده‌ایم، از آنچه به سوی تو فرو فرستاده شده است شادمان می‌شوند، و برخی از احزابشان کسانی هستند که بخشی از آنچه را بر تو نازل کرده‌ایم، چون با باورهایشان ناسازگار است، انکار می‌کنند. بگو: جز این نیست که من فرمان یافته‌ام خدا را بپرستم و چیزی را شریک او قرار ندهم. من مردم را فقط به سوی او می‌خوانم و در کارهایم فقط به او رجوع می‌کنم.'),
(1744, 13, 37, 'و همان گونه که بر موسی و عیسی کتاب آسمانی نازل کردیم، بر تو نیز این قرآن را که دربردارنده حکم و به زبان عربی است فرو فرستادیم. و اگر پس از دانشی که به تو رسیده است، از خواسته‌های اهل کتاب پیروی کنی و به بخشی از احکام منسوخ یا تحریف شده آنان تمایل نشان دهی، یا در پاسخ به پیشنهادشان معجزه‌ای جز قرآن بخواهی، قطعاً خدا تو را مؤاخذه خواهد کرد، آن گاه نه به جای او یاوری خواهی داشت و نه کسی که تو را از کیفر او حفظ کند.'),
(1745, 13, 38, 'به یقین، ما پیش از تو نیز پیامبرانی را به رسالت فرستادیم و برای آنان همسران و فرزندانی قرار دادیم، و هیچ پیامبری را توانِ این نبوده است که جز به اذن خدا معجزه‌ای بیاورد، و خدا نیز جز بر طبق حکمت و مصلحت فرمانِ تحقق معجزه‌ای را نمی‌دهد، زیرا برای هر زمانی چیزی مقرر شده است که در آن زمان جز همان چیز تحقق نمی‌یابد.'),
(1746, 13, 39, 'خداست که در هر زمانی آنچه را بخواهد می‌زداید و آنچه را بخواهد استوار می‌دارد. البتّه زدودن و استوار داشتن امور از سوی خدا بی برنامه نیست، بلکه اساس آن (امّ الکتاب) است و (امّ الکتاب) فقط نزد اوست.'),
(1747, 13, 40, 'پس اگر بخشی از کیفرهایی را که به آنان وعده می‌دهیم به تو بنمایانیم، یا پیش از آن جان تو را بستانیم، تو در انتظار تحقّق آن وعده‌ها مباش، چرا که وظیفه تو فقط ابلاغ پیام است و حسابرسی آنان فقط برعهده ماست.'),
(1748, 13, 41, 'آیا ندیده‌اند که ما به سراغ زمین می‌آییم و از اطرافش آن را می‌کاهیم (آدمیان را جان می‌ستانیم و به هلاکت می‌افکنیم)؟ و خداست که حکم می‌کند و هیچ کس نمی‌تواند حکم او را دنبال کند و مانع آن شود، و او کارها را به سرعت همان گاه که انجام می‌شود حسابرسی می‌کند.'),
(1749, 13, 42, 'قطعاً کسانی هم که پیش از آنان بودند مکر ورزیدند، ولی مکرشان ما را از هلاک ساختن آنان بازنداشت، زیرا تدبیرها همه از آنِ خداست. اوست که می‌داند هر کسی چه فراهم می‌آورَد. و به زودی کافران خواهند دانست که فرجام نیکوی این سرای از آنِ کیست؟'),
(1750, 13, 43, 'ای پیامبر، کسانی که قرآن را انکار کرده و کافر شده اند می‌گویند: تو فرستاده خدا نیستی. بگو: برای روشن شدن حقیقت کافی است که خدا میان من و شما گواه باشد، و او به رسالت من گواهی داده است، و نیز کافی است آن کسی که دانش این کتاب (قرآن) نزد اوست به رسالت من گواهی دهد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الف. لام. را. این قرآن کتابی است که ما آن را به سوی تو فرستادیم تا مردم را به اذن پروردگارشان از تاریکی‌های گمراهی بیرون آوری و به نور هدایت، به راه آن مقتدر شکست ناپذیر ستوده روانه کنی.'),
(1752, 14, 2, 'خدایی که آنچه در آسمان‌ها و آنچه در زمین است از آنِ اوست، و وای بر کافران از عذابی سخت که دامنگیرشان خواهد شد.'),
(1753, 14, 3, 'همانان که زندگی دنیا را با شیفتگی برمی گزینند و آن را بر آخرت ترجیح می‌دهند و از راه خدا (آیینی که برای مردم مقرّر کرده است) باز می‌مانند و کج آن راه را می‌طلبند. اینان در بیراهه‌ای هستند که با حق فاصله‌ای دور و دراز دارد.'),
(1754, 14, 4, 'و ما هیچ پیامبری را جز به زبان قومش به رسالت نفرستادیم، تا پیام الهی را برای آنان به روشنی بیان کند. پس از آن، خداوند هر که را بخواهد به بیراهه می‌افکند و هر کس را بخواهد هدایت می‌کند و اوست مقتدری شکست ناپذیر که کارهایش همه از روی حکمت است.'),
(1755, 14, 5, 'ای پیامبر، ما تو را به رسالت فرستاده‌ایم، چنان که یقیناً موسی را با نشانه‌های خود (معجزاتی که به او دادیم) به رسالت فرستادیم و به او گفتیم: قوم خود را از تاریکی‌های گمراهی برون آور و به سوی نورِ هدایت روانه ساز و روزهای الهی را که حاکمیّت خدا بر همه اسباب و عوامل آشکار شد به آنان یادآوری کن. قطعاً در هر یک از آنها، برای هر کسی که در بلاها بسیار شکیبا و در برابر نعمت‌های الهی بسیار سپاسگزار باشد، نشانه‌هایی بر یکتایی خداست.'),
(1756, 14, 6, 'و یاد کن زمانی را که موسی به قومش گفت: آن نعمتی که خدا به شما ارزانی داشت به خاطر آورید، آن گاه که شما را از فرعونیان رهایی بخشید. آنان پیوسته عذاب ناگوار به شما می‌چشاندند، و برای نابود کردن نسل شما پسرانتان را سر می‌بریدند و زنانتان را برای کنیزی و خدمت زنده می‌گذاشتند، و در این امر برای شما از جانب پروردگارتان آزمونی بزرگ بود.'),
(1757, 14, 7, 'و یاد کن زمانی را که پروردگارتان اعلام کرد که اگر سپاسگزاری کنید، قطعاً بر نعمت‌های شما می‌افزایم، و اگر ناسپاسی کنید و به کفر و گناه روی آورید، بی تردید عذاب من سخت است.'),
(1758, 14, 8, 'و موسی به قومش گفت: اگر شما و کسانی که در زمینند یکسره ناسپاسی کنید و کافر شوید، به خدا زیانی نمی‌رسد، زیرا خداوند بی نیاز و ستوده است.'),
(1759, 14, 9, 'آیا داستان هلاکت کسانی که پیش از شما بودند: قوم نوح و عاد و ثمود و کسانی که بعد از آنان بودند به شما نرسیده است؟ هیچ کس جز خدا از حقیقت حالشان و تاریخ حیاتشان آگاه نیست. پیامبرانشان با دلایلی روشنگر به سویشان آمدند، ولی آنان دست پیامبران را به دهانشان برگرداندند (آنان را به سکوت واداشتند) و گفتند: ما به آیینی که شما برای آن فرستاده شده‌اید کافریم و در درستیِ آنچه ما را بدان فرا می‌خوانید تردید داریم و دعوت شما به یکتاپرستی مایه بدگمانی ما شده است.'),
(1760, 14, 10, 'پیامبرانشان گفتند: آیا در یکتایی خدا که پدیدآورنده آسمان‌ها و زمین و سامان دهنده آنهاست تردیدی هست؟ او شما را دعوت می‌کند تا پاره‌ای از گناهان شما را بر شما ببخشاید و تا سرآمدی معیّن به شما مهلت دهد، زیرا اگر بخشوده نشوید، پیش از مهلت مقرّر، به سزای گناهانتان به هلاکت خواهید رسید. گفتند: شما جز بشری مانند ما نیستید، اگر از جانب خدا پیامی به شما می‌رسید به ما نیز رسیده بود. شما می‌خواهید با این ادّعا ما را از آنچه پدرانمان می‌پرستیدند بازدارید. اگر از جانب خدا فرستاده شده‌اید برای ما معجزه‌ای آشکار بیاورید.'),
(1761, 14, 11, 'پیامبرانشان در پاسخ آنان گفتند: آری، ما جز بشری مانند شما نیستیم، ولی آدمیان در بهره مندی از نعمت‌های خدا یکسان نیستند، بلکه خدا به هر یک از بندگانش که بخواهد، با اعطای مقام نبوّت، نعمتی سنگین به او ارزانی می‌دارد. امّا این که از ما درخواست معجزه می‌کنید بدانید که ما نمی‌توانیم بی اذن خدا معجزه‌ای برای شما بیاوریم. پس کسانی که به خدا ایمان دارند باید تنها بر او توکّل کنند.'),
(1762, 14, 12, 'و ما چه عذری داریم که بر خدا توکّل نکنیم و حال آن که او راه‌هایی را که برای ما شایسته است به ما نشان داده است؟ به همین جهت بر آزارهایی که برای ابلاغ توحید به ما رسانده‌اید صبر می‌کنیم. پس اهل توکّل باید فقط بر خدا توکّل کنند.'),
(1763, 14, 13, 'و کسانی که کافر شدند، به پیامبرانشان گفتند: بی تردید، شما و پیروانتان را از سرزمین خود بیرون می‌کنیم مگر این که به آیین ما درآیید. پس پروردگارشان به آنان وحی کرد که حتماً ستمکاران را نابود خواهیم کرد.'),
(1764, 14, 14, 'و شما را پس از آنان در همان سرزمین سکونت خواهیم داد. این وعده برای کسی است که از مقام من بیم داشته و از تهدید من ترسیده است.'),
(1765, 14, 15, 'و پیامبران چنان از ستم کافران به ستوه آمدند که از خدا طلب یاری کردند و خدا هم آنان را یاری کرد و هر زورگوی ستیزه گری از نجات خود نومید شد و به هلاکت رسید.'),
(1766, 14, 16, 'دوزخ از پی اوست و به او خونابه خواهند نوشانید.'),
(1767, 14, 17, 'آن را جرعه جرعه می‌نوشد و می‌رود که نتواند آن را فرو برد، و مرگ از هر سو به سراغش می‌آید ولی هرگز نخواهد مرد، و عذابی سخت به دنبال خواهد داشت.'),
(1768, 14, 18, 'کسانی که به پروردگارشان کافر شده‌اند، اعمالشان در مَثَل به خاکستری می‌ماند که در روزی طوفانی تندبادی بر آن بوزَد که بر اندکی هم از آنچه فراهم آورده‌اند دست نمی‌توانند یافت. این است آن گمراهیی که از حق فاصله‌ای دور و دراز دارد.'),
(1769, 14, 19, 'آیا ندانسته‌ای که خداوند آسمان‌ها و زمین را به حق آفریده است (برای آن هدفی مقرر داشته است)؟ اگر بخواهد شما را از میان می‌بَرد و آفریدگانی جدید پدید می‌آورَد.'),
(1770, 14, 20, 'و این کار بر خدا دشوار نیست.'),
(1771, 14, 21, 'در قیامت پرده‌ها از روی چشم‌ها برداشته می‌شود و همه مردم درمی یابند که برای خدا آشکارند، آن گاه ناتوانانی که پیرو مستکبران کفرپیشه بودند، به آن گردنفرازان می‌گویند: ما در دنیا پیرو بی چون و چرای شما بودیم، آیا امروز چیزی از عذاب خدا را از ما باز می‌دارید؟ مستکبران در پاسخ می‌گویند: اگر خدا ما را به رهایی از این عذاب ره نموده بود، ما نیز شما را به رهایی از آن راهنمایی می‌کردیم، ولی بدانید چه بی تابی کنیم و چه شکیبایی ورزیم برای ما یکسان است، ما را هیچ گریزگاهی نیست.'),
(1772, 14, 22, 'و چون کار به انجام رسد، شیطان (ابلیس) به کافران و گنهکاران می‌گوید: خدا به شما وعده داد، وعده‌ای راست و درست، و به آن وفا کرد، و من هم به شما وعده دادم ولی با شما خلاف کردم، و من بر شما هیچ تسلّطی نداشتم، نه بر خودتان چیره بودم و نه بر خِردهایتان، فقط شما را به کفر و نافرمانی خدا فراخواندم و شما دعوتم را پذیرفتید، پس مرا سرزنش نکنید و خود را سرزنش کنید که به اختیار خود از من پیروی کردید، نه من فریادرس شما هستم و نه شما فریادرس من. من منکر آن بودم که مرا پیش از این، در حیات دنیا شریک خدا گیرید، به یقین ستمکاران عذابی دردناک دارند.'),
(1773, 14, 23, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند به بوستان‌هایی آکنده از درختان که از زیر آنها نهرها روان است درآورده می‌شوند، در حالی که به اذن پروردگارشان در آن جا جاودانه خواهند بود، و درودشان در آن جا سلام است.'),
(1774, 14, 24, 'آیا ندانسته‌ای که خداوند گواهی به توحید را چگونه مَثَل زده است؟ او سخن پاک (گواهی به توحید) را به درختی پاک که ریشه اش در زمین استوار و شاخه هایش در آسمان است تشبیه کرده است.'),
(1775, 14, 25, 'درختی که در هر زمان میوه اش را به اذن پروردگارش می‌دهد. و خداوند برای مردم مَثَل‌ها می‌زند، باشد که دریابند که نیکبختی آنان در گرو توحید و خداپرستی است.'),
(1776, 14, 26, 'و سخن پلید (شرک) در مَثَل همچون درختی بی ریشه و پلید است که از روی زمین برکنده شده و ثبات ندارد و جز شر و بدی ثمری به بار نمی‌آورَد.'),
(1777, 14, 27, 'خداوند کسانی را که ایمان آورده‌اند، در زندگی دنیا و در آخرت با سخنی استوار که آن را پذیرفته‌اند بر ایمانشان پایدار می‌دارد و ستمکاران (کفرپیشگان) را در گمراهی می‌افکند و آنان را از دستیابی به حیات پاک دنیا و آخرت محروم می‌کند. و خدا هر کاری که بخواهد انجام می‌دهد.'),
(1778, 14, 28, 'آیا به سران کفر ننگریسته‌ای که سپاس نعمت خدا را به ناسپاسی تبدیل کردند و قوم خود را در پی خویش به سرای هلاکت درآوردند؟'),
(1779, 14, 29, 'به دوزخ که به آن در می‌آیند و به آتشش می‌سوزند و آن بد قرارگاهی است.'),
(1780, 14, 30, 'آنان برای خدا همتایانی قرار دادند تا مردم را از راه او گمراه سازند و بدین وسیله از آنان بهره برند. ای پیامبر، به آنان بگو: از دنیا بهره برید ولی بدانید که بازگشت شما به سوی آتش است.'),
(1781, 14, 31, 'ای پیامبر، به آن بندگان من که ایمان آورده‌اند بگو نماز را برپا دارند و بخشی از آنچه را که روزی آنان کرده‌ایم، در نهان و آشکار انفاق کنند، پیش از آن که روزی فرا رسد که در آن نه داد و ستدی هست و نه رفاقتی، اگر بگویی، مؤمنان راستین همان کنند که تو فرمانشان می‌دهی.'),
(1782, 14, 32, 'چگونه خدا همتایانی داشته باشد، در حالی که اوست که آسمان‌ها و زمین را پدید آورده و از آسمان آبی فرو فرستاده و به وسیله آن از میوه‌ها برای شما روزی برآورده و کشتی‌ها را برای شما مسخّر ساخته تا به فرمان او در دریا روان شوند، و جویبارها را برای شما رام کرده است.'),
(1783, 14, 33, 'و خورشید و ماه را که پیوسته در حرکتند، برای شما رام ساخته و شب و روز را برای شما مسخّر کرده است.'),
(1784, 14, 34, 'و از هر آنچه درخواست کرده‌اید به شما داده است. و اگر نعمت‌های خدا را شماره کنید، نمی‌توانید آنها را به شمار آورید. بی تردید، انسان به خود بسیار ستم می‌کند و خویشتن را به هلاکت می‌افکند، چرا که در برابر نعمت‌های خدا بسیار ناسپاس است.'),
(1785, 14, 35, 'و یاد کن زمانی را که ابراهیم گفت: پروردگارا، این شهر (مکّه) را تحت مقرّراتی ویژه ایمن گردان و مرا و فرزندانم را از پرستش بت‌ها دور دار.'),
(1786, 14, 36, 'پروردگارا، آنها سبب گمراهی بسیاری از مردم شده‌اند. پس هر که از من پیروی کند و به آیینی که آورده‌ام پای بند باشد، او از من است، و هر که مرا نافرمانی کند از من نیست، با این حال تو آمرزنده و مهربانی.'),
(1787, 14, 37, 'پروردگارا، من برخی از فرزندانم (اسماعیل و نسل او) را در سرزمینی بی کشت و زرع، در کنار خانه حرمت یافته تو سکونت دادم، پروردگارا، تا نماز را برپا دارند، پس دل‌های گروهی از مردم را به آنان گرایش ده و آنان را از میوه‌ها و محصولات روزی عطا کن، باشد که سپاسگزاری کنند.'),
(1788, 14, 38, 'پروردگارا، به یقین، تو آنچه را که نهان می‌داریم و آنچه آشکار می‌کنیم، می‌دانی، و هیچ چیز نه در زمین و نه در آسمان بر خدا پنهان نیست.'),
(1789, 14, 39, 'ستایش خدای راست که دعای مرا اجابت کرد و با وجود سالخوردگی من، اسماعیل و اسحاق را به من عطا فرمود. قطعاً پروردگار من شنونده دعا و اجابت کننده آن است.'),
(1790, 14, 40, 'پروردگارا، مرا برپادارنده نماز گردان و از نسل من نیز نمازگزارانی قرار ده. پروردگارا، و دعای مرا بپذیر.'),
(1791, 14, 41, 'پروردگارا، مرا و پدر و مادرم و مؤمنان را روزی که حساب برپا می‌شود بیامرز.'),
(1792, 14, 42, 'و تو ای انسان مپندار که خدا از آنچه ستمکارانِ غرق در ناز و نعمت انجام می‌دهند بی خبر است، جز این نیست که کیفر آنان را تا روزی که چشم‌ها از ترس خیره می‌شود به تأخیر می‌افکند.'),
(1793, 14, 43, 'در حالی که گردن هایشان را کشیده و سرهایشان را بالا آورده‌اند، و بر اثر ترس پلک هایشان بر هم نمی‌آید و دل هایشان از هر چاره و تدبیری تهی است.'),
(1794, 14, 44, 'ای پیامبر، مردم را از روزی که عذاب به سراغشان می‌آید و هلاکشان می‌کند بیم ده. در آن روز کسانی که ستم کرده‌اند (شرک ورزیده و کافر شده اند) می‌گویند: پروردگارا، مرگ ما را تا زمانی نزدیک به تأخیر انداز. در این صورت دعوت تو را می‌پذیریم و از پیامبران پیروی می‌کنیم. به آنان بگو: آیا پیش‌تر سوگند یاد نمی‌کردید که امّتی نیرومندید و شما را فنا و انقراضی نیست؟'),
(1795, 14, 45, 'و آیا در دیار کسانی که بر خود ستم نمودند سکونت نکردید؟ و حال آن که برای شما آشکار شد که با آنان چگونه رفتار کردیم، و برای شما مَثَل‌ها زدیم و از امّت‌های هلاک شده نمونه‌های بسیاری بیان کردیم.'),
(1796, 14, 46, 'ما بر سر آنان عذابی فرود آوردیم و آنان هم نیرنگ خود را به کار بستند، ولی نیرنگشان در اختیار خدا بود و در نزد او آشکار. پس هرگز برای آنان چاره ساز نشد، هر چند مکرشان چنان بود که کوه‌ها به وسیله آن از جای کنده می‌شد.'),
(1797, 14, 47, 'پس هرگز مپندار که خداوند در وعده خود با پیامبرانش تخلّف می‌کند و به آنان یاری نمی‌رساند و دشمنانشان را کیفر نمی‌دهد، قطعاً خداوند شکست ناپذیر و انتقام گیرنده است.'),
(1798, 14, 48, 'روزی انتقام خواهد گرفت که قیامت رخ دهد و زمین به غیر این زمین و آسمان‌ها نیز به غیر این آسمان‌ها مبدل شوند و پرده‌ها همه کنار رود و همگان دریابند که در پیشگاه خداوند یگانه که بر همه موجودات چیره است، آشکارند.'),
(1799, 14, 49, 'آن روز گنهکاران را می‌بینی که در زنجیرها به هم بسته شده‌اند.'),
(1800, 14, 50, 'جامه هایشان از قَطِران (ماده‌ای سیاه و بدبو) است و آتش چهره آنان را می‌پوشاند.'),
(1801, 14, 51, '[آری، قیامت روی می‌دهد] تا خداوند به هر کسی همان را که فراهم آورده است سزا دهد و در آن هیچ تأخیری نیست زیرا خدا به سرعت اعمال بندگانش را حسابرسی می‌کند.'),
(1802, 14, 52, 'اینها همه ابلاغی است برای مردم تا حقایق روشن شود و مردم به وسیله آنها بیم داده شوند و بدانند که خدا معبودی یگانه است و صاحبان خرد نیز متذکر گردند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الف. لام. را. اینها که به سوی تو فرو فرستاده می‌شود آیات این کتاب آسمانی و آیات قرآنی است که حق و باطل را به روشنی از هم مشخص می‌کند.'),
(1804, 15, 2, 'چه بسا کسانی که به قرآن کافر شده‌اند در جهان دیگر آرزو کنند که‌ای کاش در برابر خدا تسلیم شده و به کتاب او ایمان آورده بودند.'),
(1805, 15, 3, 'با آنان درباره حق به گفت و گو منشین که جز به دنیا نمی‌اندیشند، رهایشان کن تا بخورند و بهره برگیرند و آرزوهایشان آنان را به بیهودگی بکشاند، به زودی خواهند دانست.'),
(1806, 15, 4, 'ما مردم هیچ شهری را نابود نکردیم مگر این که برای آنان کتابی بود که سرآمدشان در آن ثبت شده و ما به آن آگاه بودیم.'),
(1807, 15, 5, 'هیچ امّتی بر سرآمد معیّن خود سبقت نمی‌گیرد که زودتر از آن منقرض شود و از آن هم به تأخیر نمی‌افتد که دیرتر از آن از میان برود.'),
(1808, 15, 6, 'کافران به پیامبر گفتند: ای کسی که قرآن بر تو فرو فرستاده شده و آن را یادآور می‌خوانی، قطعاً تو دچار جنون شده‌ای.'),
(1809, 15, 7, 'اگر از راستگویانی چرا فرشتگان را نزد ما نمی‌آوری تا بر رسالت تو گواهی دهند؟'),
(1810, 15, 8, 'باید بدانند که ما فرشتگان را تنها هنگامی فرو می‌فرستیم که حقایق بتواند در آن به طور کامل آشکار شود، و این در دنیا برای مردم امکان پذیر نیست و اگر فرشتگان بر آنان فرود آیند مهلت نخواهند یافت و مرگشان فراخواهد رسید.'),
(1811, 15, 9, 'بی تردید، ما خود، این قرآن را که یاد خداست فرو فرستاده‌ایم و قطعاً ما خود، آن را از این که به فراموشی سپرده شود و نیز از هرگونه تحریف مصون می‌داریم.'),
(1812, 15, 10, 'و قطعاً، پیش از تو نیز پیامبرانی را در میان گروه‌های نخستین به رسالت فرستادیم.'),
(1813, 15, 11, 'و هیچ پیامبری برایشان نیامد مگر این که او را به مسخره می‌گرفتند.'),
(1814, 15, 12, 'همان گونه که ما پیام‌های خود را برای امّت‌های گذشته فرو فرستادیم و مورد تکذیب قرار گرفت، این قرآن را نیز به گونه‌ای در دل‌های این مجرمان راه می‌دهیم که سرانجام مورد انکار آنان قرار گیرد.'),
(1815, 15, 13, 'آنان به قرآن ایمان نمی‌آورند، و راه و روش مردم نخستین نیز بر این رفته است.'),
(1816, 15, 14, 'و اگر دری از آسمان بر آنان می‌گشودیم که پیوسته از آن بالا می‌رفتند و اسرار غیب را مشاهده می‌کردند،'),
(1817, 15, 15, 'باز هم می‌گفتند: جز این نیست که ما چشم بندی شده‌ایم، بلکه ما مردمی هستیم که به وسیله محمّد جادو شده‌ایم.'),
(1818, 15, 16, 'و به راستی ما در آسمان منزل‌هایی برای خورشید و ماه قرار دادیم و آسمان را با آفرینش ستارگان، برای نظاره گران آراستیم.'),
(1819, 15, 17, 'و آن را از نفوذ هر شیطانِ رانده شده­ای که برای دست­یابی به اسرار غیب به آن برآید، محفوظ داشتیم.'),
(1820, 15, 18, 'مگر این که کسی از آنان بخواهد استراق سمع کند و رخدادهای آینده را از فرشتگان دریابد، که شهابی آشکار او را دنبال خواهد کرد.'),
(1821, 15, 19, 'و زمین را به صورت دشت‌هایی قابل کِشت گستراندیم و کوه‌هایی استوار که لرزه‌های زمین را بگیرند در آن افکندیم و از هر چیزی که دارای وزن است در آن رویاندیم.'),
(1822, 15, 20, 'و برای شما و کسانی که شما روزی دهنده آنان نیستید، اسباب زندگی را در آن فراهم آوردیم.'),
(1823, 15, 21, 'و هیچ چیز نیست مگر این که خزانه­های آن نزد ماست، و ما آن را جز به اندازه معیّن فرو نمی­فرستیم'),
(1824, 15, 22, 'و بادها را که بارورکننده گیاهانند فرستادیم، پس آبی از آسمان فرو فرستادیم و آن را برای نوشیدن در اختیار شما نهادیم و شما هرگز ذخیره کننده آن در خزانه‌های زمین نیستید.'),
(1825, 15, 23, 'و به یقین، ماییم که زندگی می‌بخشیم و می‌میرانیم، و ماییم که پس از شما هر چه هست به ارث می‌بریم.'),
(1826, 15, 24, 'و قطعاً از شما مردم کسانی را که پیش‌تر بودند شناخته‌ایم و کسانی را که پس از این خواهند بود، نیز شناخته‌ایم.'),
(1827, 15, 25, 'و بی تردید پروردگار توست که آنان را در صحنه قیامت محشور می‌کند، قطعاً کارهای او همه از روی حکمت است و او به همه چیز داناست.'),
(1828, 15, 26, 'و به راستی ما انسان را از گِلی خشک که از گِلی سیاه و بدبو و دگرگون­شده پدید­آمده­بود آفریدیم.'),
(1829, 15, 27, 'و جن را پیش از آفرینش انسان، از آتشی پدید آمده از بادی سوزان خلق کردیم.'),
(1830, 15, 28, 'و یاد کن زمانی را که پروردگار تو به فرشتگان گفت: من از گِلی خشکیده که آن از گِلی سیاه و بدبو و دگرگون شده است بشری خواهم آفرید.'),
(1831, 15, 29, 'پس وقتی او را درست اندام ساختم و از روح خود در او دمیدم، به خاک بیفتید و او را سجده کنید.'),
(1832, 15, 30, 'پس فرشتگان همگی سجده کردند،'),
(1833, 15, 31, 'مگر ابلیس که خودداری کرد از این که با سجده کنندگان باشد.'),
(1834, 15, 32, 'پروردگارت گفت: ای ابلیس، تو را چه سودی است در این که با سجده کنندگان نیستی؟'),
(1835, 15, 33, 'گفت: من آن نبوده‌ام که برای بشری که او را از گِل خشکی پدید آمده از گِلی سیاه و بدبو و دگرگون شده آفریده‌ای سجده کنم.'),
(1836, 15, 34, 'خدا گفت: پس، از این جایگاه که منزلت مقربان است بیرون شو که تو رانده شده‌ای.'),
(1837, 15, 35, 'و قطعاً تا روز جزا از رحمت من دور خواهی بود.'),
(1838, 15, 36, 'ابلیس گفت: پروردگارا، پس مرا تا روزی که آدمیان برانگیخته می‌شوند مهلت ده.'),
(1839, 15, 37, 'خدا گفت: تو از مهلت یافتگان خواهی بود،'),
(1840, 15, 38, 'البته نه تا روز رستاخیز، بلکه تا آن زمان معیّن که مردم به کفر و گناه می‌پردازند.'),
(1841, 15, 39, 'ابلیس گفت: پروردگارا، بدان سبب که مرا به گمراهی افکندی، من هم اعمال و عقاید نادرست را در زمین برای مردم می‌آرایم و قطعاً همه آنان را به بیراهه می‌افکنم،'),
(1842, 15, 40, 'مگر آن بندگانت از میان آنان که برای تو خالص شده‌اند به گونه‌ای که برای من و دیگران در آنها نصیبی نیست.'),
(1843, 15, 41, 'خدا گفت: این که گروهی به وسیله تو گمراه شوند و گروهی از اغوای تو در امان بمانند، راهی استوار است که برپایی آن برعهده من است و جز به رخصت من تحقّق نخواهد یافت.'),
(1844, 15, 42, 'بی تردید، تو را بر بندگان من هیچ تسلّطی نیست، مگر بر کسانی از آنان که از تو پیروی می‌کنند، همانان که گمراهند.'),
(1845, 15, 43, 'و به یقین وعده گاه همه آنان دوزخ است.'),
(1846, 15, 44, 'برای دوزخ هفت طبقه و هفت نوع عذاب است و هر طبقه‌ای به گروهی از پیروان شیطان اختصاص دارد و به بخش‌هایی تقسیم شده است.'),
(1847, 15, 45, 'امّا تقواپیشگان در باغ‌هایی از بهشت و در کنار چشمه سارانی خواهند بود.'),
(1848, 15, 46, 'به آنان می‌گویند: با سلامت کامل به این جا درآیید در حالی که از هر گزندی در امان خواهید بود.'),
(1849, 15, 47, 'و هر دشمنی و کینه‌ای که در سینه هایشان است برمی کَنیم و برادرانه روبه روی هم بر تخت‌هایی می‌نشینند.'),
(1850, 15, 48, 'در آن جا هیچ خستگی و رنجی به آنان نمی‌رسد و از آن جا بیرون رانده نمی‌شوند.'),
(1851, 15, 49, 'ای پیامبر، به بندگانم خبر ده که منم بسیار آمرزنده و مهربان،'),
(1852, 15, 50, 'و خبر ده که عذاب من عذاب دردناک است.'),
(1853, 15, 51, 'و از مهمانان ابراهیم به آنان خبر ده تا به نمونه‌ای از رحمت و عذاب من آگاه شوند.'),
(1854, 15, 52, 'آن گاه که مهمانان بر او وارد شدند و سلام کردند. ابراهیم که دید آنان از خوردن غذا خودداری می‌کنند، به آنان گفت: ما از شما بیمناکیم.'),
(1855, 15, 53, 'گفتند: نترس، که ما فرستادگان پروردگار توییم و تو را به پسری دانا مژده می‌دهیم.'),
(1856, 15, 54, 'ابراهیم گفت: آیا با وجود این که پیری به سراغ من آمده و ناتوان شده‌ام به من مژده می‌دهید؟ راستی به چه چیز مژده می‌دهید؟'),
(1857, 15, 55, 'گفتند: ما به تو مژده‌ای درست دادیم و این تحقق خواهد یافت، پس از نومیدان مباش.'),
(1858, 15, 56, 'گفت: من از روی نومیدی در سخن شما تردید نکردم، آیا جز گمراهان چه کسی از رحمت پروردگارش نومید می‌شود؟'),
(1859, 15, 57, 'ابراهیم پرسید: پس کار مهم شما ـ ای فرستادگان ـ چیست؟'),
(1860, 15, 58, 'گفتند: ما به سوی مردمی تبهکار فرستاده شده‌ایم تا آنان را به هلاکت افکنیم.'),
(1861, 15, 59, 'ولی خاندان لوط را عذاب نمی‌کنیم و همه آنان را قطعاً نجات می‌دهیم،'),
(1862, 15, 60, 'مگر همسرش را که مقدر کرده‌ایم از ماندگارانِ در عذاب باشد.'),
(1863, 15, 61, 'پس هنگامی که آن فرستادگان نزد خاندان لوط آمدند،'),
(1864, 15, 62, 'لوط به آنان گفت: شما مردمی ناشناس هستید.'),
(1865, 15, 63, 'گفتند: نه، از ما نگران مباش، ما با آن عذابی که قوم تو در آن تردید داشتند نزد تو آمده‌ایم.'),
(1866, 15, 64, 'و آنچه را که سراسر حق است برای تو آورده‌ایم و قطعاً ما راست می‌گوییم.'),
(1867, 15, 65, 'پس، در پاسی از این شب، با خانواده خود از این سرزمین کوچ کن و خود از پی آنان روانه شو و حرکت آنان را زیر نظر داشته باش، و هیچ یک از شما نباید به پشت سر خود بنگرد، و هر جا که به شما فرمان داده می‌شود، بروید.'),
(1868, 15, 66, 'و این امر را مقرر داشته و از طریق وحی به لوط اعلام کردیم که بنیاد اینان هنگامی که به صبح درمی آیند برکنده خواهد شد.'),
(1869, 15, 67, 'و پیش از آن که لوط را از این امر با خبر کنیم، مردم شهر، در حالی که از ورود جوانانی زیبا چهره شادی می‌کردند، به سوی خانه لوط آمدند.'),
(1870, 15, 68, 'لوط به آنان گفت: اینها مهمانان منند، مرا رسوا مکنید.'),
(1871, 15, 69, 'از خدا بترسید و مرا خوار و شرمسار نسازید.'),
(1872, 15, 70, 'گفتند: مگر ما تو را از پذیرش مردم و جای دادن و مهمان نمودن آنان منع نکرده بودیم؟ پس حق ماست که حرمت مهمانان تو را پاس نداریم.'),
(1873, 15, 71, 'لوط به مهاجمان گفت: اینان دختران منند، اگر کاری می‌کنید آنان را به همسری خود برگیرید.'),
(1874, 15, 72, 'ای پیامبر، به جان تو سوگند که قوم لوط در سرمستی خود سرگردان بودند.'),
(1875, 15, 73, 'پس هنگامی که به روشنی بامداد درآمدند، بانگی مهیب آنان را فرو گرفت.'),
(1876, 15, 74, 'و ما دیارشان را زیر و زبر کردیم و بر آنان سنگ‌هایی از (سجّیل) باراندیم.'),
(1877, 15, 75, 'به راستی در دیار ویران شده قوم لوط برای اثرشناسان نشانه‌هایی از وقوع عذاب الهی است.'),
(1878, 15, 76, 'و آن آثار در راهی دایر (راه مدینه به شام) قرار دارد.'),
(1879, 15, 77, 'بی تردید، در این ماجرا برای مؤمنان نشانه‌ای بر حقّانیّت هشدارها و دعوت‌های خداوند است.'),
(1880, 15, 78, 'و قوم شعیب، آن جنگل نشینان نیز به راستی ستمکار بودند.'),
(1881, 15, 79, 'و ما از آنان انتقام گرفتیم، و این دو سرزمین (سرزمین قوم لوط و قوم شعیب) در راهی آشکار (راه مدینه به شام) قرار دارند.'),
(1882, 15, 80, 'و اهل (حِجر) هم پیامبران را تکذیب کردند.'),
(1883, 15, 81, 'و ما نشانه‌های خود را در اختیارشان نهادیم ولی از آنها روی برتافتند.'),
(1884, 15, 82, 'آنان از کوه‌ها خانه‌هایی برای خود می‌تراشیدند و به گمان خویش از گزندها در امان بودند.'),
(1885, 15, 83, 'ولی آن گاه که به بامدادی درآمدند، بانگی مهیب آنان را فرو گرفت و هلاکشان کرد.'),
(1886, 15, 84, 'و آنچه فراهم آورده بودند به کارشان نیامد و آنان را نیکبخت نساخت.'),
(1887, 15, 85, 'و ما آسمان‌ها و زمین و آنچه را که میان آن دو است جز به حق نیافریده‌ایم، و قیامت قطعاً آمدنی است، پس تو ـ ای پیامبر ـ کفر و تمسخر کافران را نادیده انگار و به نیکی چشم پوشی کن.'),
(1888, 15, 86, 'چرا که پروردگار توست که همگان را آفریده و به حالشان داناست.'),
(1889, 15, 87, 'و به راستی ما از میان آیات قرآن که باهم پیوند دارند سوره‌ای را با هفت آیه (سوره حمد) به تو عطا کردیم و این قرآن با عظمت را به تو دادیم.'),
(1890, 15, 88, '[پس] به مال و ثروتی که گروه‌هایی از کافران را از آن بهره مند ساخته‌ایم چشم مدوز، و بر کفر ورزیدن آنان اندوه مخور، و بال و پر خود را برای مؤمنان فرو گستر و آنان را پیرامون خود گرد آور و با آنان معاشرت داشته باش و با مهربانی و بردباری به تربیت آنان همت گمار.'),
(1891, 15, 89, 'و بگو: مسئولیت من این است که شما را به عذاب‌های الهی هشدار دهم و آنچه را که در دین خود بدان نیاز دارید به روشنی بیان کنم،'),
(1892, 15, 90, 'شما را به عذابی همانند عذابی که بر تقسیم کنندگان قرآن فرو فرستادیم هشدار می‌دهم.'),
(1893, 15, 91, 'همانان که قرآن را بخش بخش کردند، بخشی را سحر و بخش دیگر را افسانه‌های پیشینیان و بخشی را ساخته و پرداخته پیامبر شمردند.'),
(1894, 15, 92, 'سوگند به پروردگارت که از همه آنان سؤال خواهیم کرد،'),
(1895, 15, 93, 'از آنچه برای بازداشتن مردم از گرایش به سوی خدا، انجام می‌دادند.'),
(1896, 15, 94, 'اینک آنچه را بدان فرمان یافته‌ای اعلان کن و دعوتت را آشکار ساز و از مشرکان روی بگردان.'),
(1897, 15, 95, 'زیرا ما با فرو فرستادن عذاب، شرّ استهزاکنندگان را از تو بازمی داریم.'),
(1898, 15, 96, 'همانان که با وجود خدا معبود دیگری برای خود قرار می‌دهند. پس به زودی حقیقت را درخواهند یافت.'),
(1899, 15, 97, 'ما خوب می‌دانیم که تو از آنچه مشرکان از روی استهزا می‌گویند دلتنگ می‌شوی.'),
(1900, 15, 98, 'پس پروردگارت را همراه با ستایش او تسبیح گوی و از نمازگزاران باش تا اندوه تو برطرف گردد.'),
(1901, 15, 99, 'و پروردگارت را تا زمانی که مرگ به سراغت می‌آید و جهان غیب برای تو مشهود می‌شود بندگی کن.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'به زودی وعده خدا در مورد یاری مؤمنان و سرکوب کافران تحقق خواهد یافت، پس خواهان شتاب در تحقق این وعده نباشید، منزّه است خدا و بلند مرتبه‌تر از این است که چیزی را شریک او سازند.'),
(1903, 16, 2, 'فرشتگان را با روح ـ که از عالم امر است ـ بر هر کس از بندگانش که بخواهد فرو می‌فرستد، با این پیام که به مردم هشدار دهید که جز من معبودی شایسته پرستش نیست، پس از من پروا کنید و راه اطاعت را در پیش گیرید.'),
(1904, 16, 3, 'آسمان‌ها و زمین را به حق (هدفمند) آفریده است. او والاتر از این است که چیزی را شریک او سازند.'),
(1905, 16, 4, 'انسان را از نطفه‌ای پست آفریده است، ولی او بی­آن­که انتظاررود آشکارا با پروردگارش ستیزه­جویی می­کند.'),
(1906, 16, 5, 'و دام‌ها را که برای شما در آنها وسیله گرما و سودهایی دیگر است آفرید و از گوشت آنها می‌خورید.'),
(1907, 16, 6, 'و عصرگاهان که دام‌ها را از صحرا به آغل باز می‌گردانید و بامدادان که آنها را به چراگاه می‌برید، در آنها برای شما منظره‌ای زیباست.'),
(1908, 16, 7, 'و بعضی از دام‌ها بارهای سنگین شما را به شهری می‌برند که شما جز با تحمّل مشقّت نمی‌توانستید به آن جا برسید. به یقین پروردگار شما رئوف و مهربان است.'),
(1909, 16, 8, 'و اسبان و استران و خران را برای شما آفرید تا بر آنها سوار شوید و مایه زیبایی و تجمّل زندگی شما باشند، و چیزهایی را به سود شما می‌آفریند که از آنها آگاه نیستید.'),
(1910, 16, 9, 'آفرینش راه درست که رهروان آن را به نیکبختی برساند و نیز نشان دادن آن راه برعهده خداست، و برخی راه‌ها منحرفند و رهروان خود را به مقصد نمی‌رسانند، و اگر خدا می‌خواست همه شما را به راه درست رهنمون می‌شد.'),
(1911, 16, 10, 'اوست آن که از آسمان آبی فرو فرستاده است که بخشی از آن نوشیدنی شماست، و گیاهانی که دام‌ها را در آن می‌چرانید از آن برمی آید.'),
(1912, 16, 11, 'به وسیله آن برای شما زراعت و درختان زیتون و خرما و انگور و از سایر میوه‌ها درختان آنها را می‌رویاند. به راستی در اینها برای مردمی که می‌اندیشند نشانه‌ای بر یکتایی خدا در تدبیر جهان است.'),
(1913, 16, 12, 'و برای شما شب و روز و خورشید و ماه را رام ساخت، و ستارگان به فرمان او رام شده‌اند. به راستی در اینها نیز برای مردمی که تعقل می‌کنند نشانه‌هایی بر یگانگی خدا در تدبیر هستی است.'),
(1914, 16, 13, 'و آنچه در زمین برای شما با رنگ‌ها و انواع گوناگون آفرید، همه را مسخّر شما کرد. به راستی در این امر برای مردمی که به نظام و قوانین هستی توجه دارند نشانه‌ای بر یکتایی خدا در تدبیر جهان است.'),
(1915, 16, 14, 'و اوست آن که دریا را برای شما رام ساخته است تا از آن گوشتی تازه بخورید و زیوری که آن را می‌پوشید از آن بیرون آورید، و کشتی‌ها را در آن می‌بینی که آب را می‌شکافند و به پیش می‌روند، تا شما بهره‌ها ببرید و از فضل خدا روزی خویش را بجویید و شاید خدا را بر این نعمت‌ها سپاسگزاری کنید.'),
(1916, 16, 15, 'و در زمین کوه‌هایی استوار افکنده است تا زمین شما را نلرزاند، و جویبارهایی در آن پدید آورده تا آب به کشتزارها و بوستان‌ها برسد و راه‌هایی را در آن قرار داده است تا شما به مقصد خود راه یابید.'),
(1917, 16, 16, 'و نشانه‌هایی نیز پدید آورده است تا چیزهایی را که نمی‌دانید کشف کنید، و مردم [در تاریکی‌های خشکی و دریا] به وسیله ستارگان راه خود را می‌یابند.'),
(1918, 16, 17, 'پس آیا کسی که موجودات را می‌آفریند مانند کسی است که هیچ چیزی نمی‌آفریند؟ آیا توجّه نمی‌کنید که چنین آفریدگاری تدبیرکننده جهان هستی و شایسته پرستش است؟'),
(1919, 16, 18, 'اگر نعمت‌های خدا را شماره کنید نمی‌توانید آنها را به شماره درآورید، چرا که جهان هستی سراسر نعمت است و این از آن روست که خدا به مغفرتش پوشاننده نقص‌ها و به رحمتش بخشاینده کمال و تأمین کننده نیازهاست.'),
(1920, 16, 19, 'و خداست که آنچه را نهان می‌دارید و آنچه را آشکار می‌کنید می­داند; بنابراین او درخورِ پرستش است.'),
(1921, 16, 20, 'ولی معبودانی که مشرکان آنها را به جای خدا می­خوانند هیچ چیزی نمی­آفرینند و خود آفریده می­شوند.'),
(1922, 16, 21, 'آنها مردگانند و حیاتی ندارند و درنمی یابند که پرستش کنندگانشان چه زمانی برانگیخته می‌شوند.'),
(1923, 16, 22, 'آری، معبود شما معبودی است یگانه، پس کسانی که به جهان آخرت ایمان ندارند دل هایشان منکر حق است و آنان متکبّر و گردنکش‌اند.'),
(1924, 16, 23, 'بی تردید، خداوند آنچه را نهان می‌دارند و آنچه را آشکار می‌کنند می‌داند و آنان را براساس اعمالشان کیفر می‌کند. قطعاً او کسانی را که در برابر حق تکبّر ورزیده‌اند دوست نمی‌دارد.'),
(1925, 16, 24, 'و چون به آنان گفته شود: پروردگارتان چه چیزی فرو فرستاده است؟ می‌گویند: آنچه محمّد آن را کتاب آسمانی می‌نامد از جانب خدا نیست، آن افسانه‌هایی است که مردمان نخستین بافته‌اند.'),
(1926, 16, 25, 'آری آنان چنین می‌گویند تا سرانجام روز قیامت بار گناهان خود را به تمامی و بخشی از بار گناهان کسانی را که به نادانی آنان را به گمراهی می‌افکنند بر دوش گیرند. هان، بد است وزر و وبالی که بر دوش می‌گیرند.'),
(1927, 16, 26, 'کسانی هم که پیش از اینان بودند نیرنگ زدند، پس فرمان خدا بر این که تزویرشان از پایه‌های آن فرو ریزد در رسید و سقف آن نیز از بالا بر سرشان فرو ریخت و نیرنگشان بی ثمر شد، و عذاب از جایی که گمان نمی‌کردند به سراغشان آمد.'),
(1928, 16, 27, 'سپس روز قیامت خدا آنان را خوار و رسوا می‌کند، به آنان می‌گوید: آن شریکان من که به خاطر آنها با یکتاپرستان به ستیزه برخاستید کجایند؟ کسانی که به آنان دانش عطا شده است می‌گویند: بی تردید امروز خواری و ناگواری برای کافران است.'),
(1929, 16, 28, 'همانان که فرشتگان جانشان را می‌گیرند درحالی که بر خود ستم کرده‌اند، پس سر تسلیم فرود می‌آورند و می‌گویند: ما هیچ کار بدی نکرده‌ایم. پاسخ می‌شنوند: چرا، کارهای ناروای بسیاری کرده‌اید. قطعاً خدا به آنچه انجام داده‌اید داناست.'),
(1930, 16, 29, 'پس به طبقات دوزخ درآیید که جاودانه در آن خواهید ماند، و راستی جایگاه متکبّران بد است.'),
(1931, 16, 30, 'و به کسانی که تقواپیشه بودند گفته شد: پروردگارتان چه چیزی نازل کرده است؟ گفتند: او بهترین چیز را فرو فرستاده است. برای کسانی که در این دنیا کارهای شایسته کنند پاداشی نیکوست، و پاداش آنان در سرای آخرت بهتر است، و قطعاً سرای تقواپیشگان نیکوست،'),
(1932, 16, 31, 'باغ‌هایی همیشگی با درختانی انبوه که وارد آن می‌شوند. از زیر آنها نهرها روان است. در آن جا هر چه بخواهند برایشان موجود است. بدین سان خداوند تقواپیشگان را پاداش می‌دهد.'),
(1933, 16, 32, 'همانان که فرشتگان جانشان را می‌گیرند در حالی که از شرک و گناه پیراسته‌اند. فرشتگان به آنان می‌گویند: درود بر شما! به سزای کارهایی که می‌کردید به بهشت درآیید.'),
(1934, 16, 33, 'آیا مشرکان جز این انتظار می‌برند که فرشتگان به سراغشان بیایند و عذاب الهی را بر آنان فرود آورند، یا فرمان پروردگارت در رسد و قیامت برپا شود؟ کسانی که پیش از آنان بودند نیز همین گونه رفتار کردند (پیام‌های الهی را دروغ انگاشتند و آنها را به مسخره گرفتند) و خدا به آنان ستم نکرد، بلکه خود بر خویشتن ستم می‌کردند.'),
(1935, 16, 34, 'پس بدی‌های کردارشان (گناهانی که مرتکب شدند) به صورت عذاب بر آنان در رسید و همان چیزی را که به مسخره می‌گرفتند آنان را فراگرفت.'),
(1936, 16, 35, 'و کسانی که شرک ورزیدند گفتند: اگر خدا می‌خواست، نمی‌توانستیم نه ما و نه پدرانمان هیچ معبودی را جز او بپرستیم، و نیز اگر خدا می‌خواست، نمی‌توانستیم چیزی را جز آنچه او حرام کرده است حرام بشمریم، پس همه اینها به اراده خداست. کسانی هم که پیش از آنان بودند همین گونه رفتار کردند و با همین توجیه با پیامبران به مخالفت برخاستند. با این حال، آیا بر پیامبران وظیفه‌ای جز ابلاغ رسالت به طور آشکار هست؟'),
(1937, 16, 36, 'و قطعاً، در میان هر امّتی پیامبری برانگیختیم با این پیام که: خدا را بپرستید و از هر معبودی جز او دوری گزینید. پس از میان هر امّتی کسانی بودند که خدا آنان را هدایت کرد و از میان آنان کسانی نیز بودند که گمراهی بر ایشان ثابت گشت. پس ای مشرکان، در زمین گردش کنید و بنگرید فرجام کسانی که پیامبران را دروغگو شمردند چگونه بوده است؟'),
(1938, 16, 37, 'ای پیامبر، اگر هم بر هدایت آنان حرص ورزی بی ثمر است، زیرا هدایت به دست خداست و خدا کسانی را که گمراه کرده است هدایت نمی‌کند و آنان برای رهایی از گمراهی خود یاورانی نخواهند داشت.'),
(1939, 16, 38, 'آنان با سخت‌ترین سوگندهایشان به خدا سوگند خوردند که خدا کسانی را که می‌میرند برنمی انگیزد. چنین نیست، این سخنی نادرست است. خداوند رستاخیز را وعده داده و برپایی آن را بر خود لازم شمرده است، ولی بیشتر مردم این حقیقت را نمی‌دانند.'),
(1940, 16, 39, 'آری، خداوند رستاخیز را برپا می‌دارد تا اعمال و عقایدی را که مردم در آن اختلاف داشتند برایشان آشکار سازد و کافران بدانند که دروغ می‌گفتند.'),
(1941, 16, 40, 'جز این نیست که وقتی ما چیزی را اراده می‌کنیم، سخن ما در مورد آن این است که به آن می‌گوییم: باش، و آن موجود می‌شود.'),
(1942, 16, 41, 'و کسانی که پس از آن که در راه خدا مورد ستم قرار گیرند برای جلب خشنودی خدا هجرت کنند تا در جامعه‌ای خداپرست و عدالتخواه درآیند یا خود بر پایه عدل و داد جامعه‌ای خداپرست تشکیل دهند، قطعاً آنان را در دنیا در سرزمینی نیکو جای­می­دهیم و بی­گمان پاداش آخرت بزرگ­تر است. ای کاش می‌دانستند.'),
(1943, 16, 42, 'همان مهاجرانی که بر دشواری‌های راه خدا شکیبایند و بر پروردگارشان توکّل می‌کنند.'),
(1944, 16, 43, 'و پیش از تو نیز جز مردانی را که به آنان وحی می‌کردیم به رسالت نفرستادیم. اگر نمی‌دانید، از پژوهشگران کتاب‌های آسمانی بپرسید تا دریابید که پیامبران مبعوث نشده‌اند که نظام هستی را دگرگون سازند و اختیار را از انسان‌ها سلب کنند.'),
(1945, 16, 44, 'ما پیامبران را با دلایل آشکار و کتاب‌های آسمانی فرستادیم، و این قرآن را که وسیله یادآوری است به سوی تو نازل کردیم، تا برای مردم آنچه را که به سوی آنان نازل شده است بیان کنی، و باشد که درباره تو و محیط زندگی تو بیندیشند و دریابند که قرآن ساخته و پرداخته تو نیست.'),
(1946, 16, 45, 'آیا با وجود این همه دلایل روشن که بر حقّانیّت رسالت پیامبران وجود دارد، باز هم کسانی که حیله کنان مرتکب کارهای ناروا می‌شوند ایمنند از این که خداوند زمین را بشکافد و آنان را در آن فرو بَرد، یا عذاب از جایی که تصوّرش را نمی‌کنند به سراغشان آید؟'),
(1947, 16, 46, 'یا خدا آنان را در حال برخورداری از نعمت‌های گوناگون به عذاب فرو گیرد؟ چرا که نمی‌توانند خدا را ناتوان کنند و از حکم او بگریزند؟'),
(1948, 16, 47, 'یا آنان را دچار عذابی کند که از آن بترسند و به توبه روی آورند؟ به یقین، پروردگار شما رئوف واست.'),
(1949, 16, 48, 'آیا به چیزهایی که خدا آفریده است ننگریسته‌اند که سایه‌های آنها از جهت راست و از جهات چپ پدیدار می‌گردند و می‌چرخند، در حالی که خدا را سجده می‌کنند و در برابر او خاضعند؟'),
(1950, 16, 49, 'و همه جنبندگانی که در آسمان‌ها و زمین اند فقط در برابر خدا خاضعند و برای او سجده می‌کنند و فرشتگان نیز به درگاه او سجده می‌برند و در برابر او خاضعند و هرگز گردنکشی نمی‌کنند.'),
(1951, 16, 50, 'آنان از مقام پروردگارشان می‌ترسند، زیرا به خوبی دریافته‌اند که او بر آنان چیره است، از این رو آنچه فرمان می‌یابند انجام می‌دهند.'),
(1952, 16, 51, 'و خدا فرموده است: دو معبود برای خود مگیرید که یکی آفریدگار هستی باشد و دیگری تدبیرکننده امور، جز این نیست که او خدایی یگانه است که تدبیر و آفرینش، هر دو از آنِ اوست. پس تنها از من بترسید و تنها مرا پرستش کنید.'),
(1953, 16, 52, 'و آنچه در آسمان‌ها و زمین است از آنِ اوست و دین نیز پیوسته از آنِ اوست و هیچ کس حق ندارد دینی بیاورد. پس آیا از غیر او پروا می‌کنید و جز او را می‌پرستید؟'),
(1954, 16, 53, 'هر نعمتی که دارید از جانب خداست، زیرا آفرینش کار اوست و شما این را به خوبی می‌دانید، وانگهی چون گزندی به شما رسد، فقط به او روی می‌آورید و به درگاه او تضرّع می‌کنید.'),
(1955, 16, 54, 'و چون آن گزند را از شما برطرف کند، گروهی از شما، بی آن که انتظار رود، برای پروردگارشان شریکانی می‌پندارند.'),
(1956, 16, 55, 'آری، آنان شرک میورزند تا آنچه را به ایشان داده‌ایم ناسپاسی کنند. اینک ای مشرکان از نعمت‌های ما بهره برید و ناسپاسی کنید، به زودی فرجام شوم این کار را خواهید دانست.'),
(1957, 16, 56, 'آنان می‌پندارند اسباب و علل طبیعی ـ که حقیقت آنها را درنیافته‌اند ـ در آنچه ما به ایشان ارزانی داشته‌ایم از خود نقشی دارند، به خدا سوگند در برابر دروغبافی خود بازخواست خواهید شد.'),
(1958, 16, 57, 'و برای خدا که از داشتن فرزند منزّه است دختران را می‌انگارند و آنچه را می‌پسندند برای خود قرار می‌دهند.'),
(1959, 16, 58, 'این در حالی است که وقتی به یکی از آنان مژده دختر دهند، چهره اش از خشم سیاه می‌شود و خشم خود را فرو می‌برد.'),
(1960, 16, 59, 'از ناگواریِ مژده‌ای که به او داده‌اند از مردم پنهان می‌شود، و می‌اندیشد که آیا او را با شرمساری نگاه دارد یا در زیر خاک پنهانش کند. بدانند این که خدا را صاحب دختر می‌دانند و خود از داشتن آن بیزارند بد قضاوتی است.'),
(1961, 16, 60, 'وصفِ ناروا برای کسانی است که به آخرت ایمان ندارند، و بهترین وصف تنها از آنِ خداست، چرا که اوست عزتمند و حکیم.'),
(1962, 16, 61, 'و اگر خدا مردم را به سزای ستمشان مؤاخذه می‌کرد، هیچ انسانی را که بر روی زمین حرکت می‌کند باقی نمی‌گذاشت. ولی خداوند در کیفر مردم شتاب نمی‌کند، بلکه آنان را تا سرآمدی معیّن به تأخیر می‌افکند، و هنگامی که سرآمدشان فرا رسید، نه لحظه‌ای از آن باز می‌مانند و نه لحظه‌ای از آن پیش می‌افتند.'),
(1963, 16, 62, 'و آنچه را خوش نمی‌دارند برای خدا می‌پندارند و زبانشان به دروغ خبر می‌دهد که نیکوترین فرجام را خواهند داشت. به یقین، آتش دوزخ برای آنان است و آنان را پیش از دیگران به سوی آتش خواهند فرستاد.'),
(1964, 16, 63, 'به خدا سوگند که به سوی امّت‌های پیش از تو نیز پیامبرانی فرستادیم، امّا شیطان کارهای زشتشان را برای آنان بیاراست و در نتیجه از او پیروی کردند و از پیامبران روی برتافتند و اینک او عهده دار امور آنان است و روز قیامت عذابی دردناک خواهند داشت.'),
(1965, 16, 64, 'و ما این کتاب را بر تو فرو نفرستادیم مگر برای این که عقاید درست و اعمال نیکو را که مشرکان در آن اختلاف دارند برای آنان بیان کنی و برای مردمی که ایمان می‌آورند رهنمود و رحمتی باشد.'),
(1966, 16, 65, 'و خداست که از آسمان آبی فرو فرستاده و به وسیله آن زمین را پس از آن که خشک و مرده بوده با رویش گیاهان زنده­کرده­است. به یقین، در اینها برای مردمی که گوش شنوا دارند نشانه­ای بر وجود رستاخیز است.'),
(1967, 16, 66, 'و در دام‌ها نیز قطعاً برای شما عبرت و سبب دانشی بر قدرت خداوند است، از آنچه در شکم آنهاست، از میان خوراک هضم شده و خون (جایگاهی مجاور این دو)، شیری خالص که برای نوشندگان گواراست در اختیار شما می‌گذاریم تا آن را بیاشامید. پس بدانید که خدا بر گردآوری اجزای مردگان و زنده کردن آنها تواناست.'),
(1968, 16, 67, 'و از میوه‌های درختان خرما و انگور برای شما چیزی آفرید که از آنها هم باده برمی گیرید و هم رزقی نیکو تهیّه می‌کنید. به یقین، در این نیز برای مردمی که خرد را به کار می‌گیرند نشانه‌ای بر یکتایی خداست.'),
(1969, 16, 68, 'و پروردگارت به زنبورهای عسل الهام کرد که از کوه‌ها، درختان و جایگاه‌هایی که مردم برای تهیّه عسل برپا می‌کنند، خانه‌هایی برگیرید.'),
(1970, 16, 69, 'سپس از انواع میوه‌ها بخورید، آن گاه راه‌هایی را که پروردگارتان معیّن کرده است رام و فرمانبردار بپویید. از شکمِ آنها آشامیدنی رنگارنگی بیرون می‌آید که در آن درمان بیماری مردم است. به یقین، در اینها برای مردمی که می‌اندیشند نشانه‌ای بر یکتایی خداست.'),
(1971, 16, 70, 'و خدا شما را آفرید، سپس چون به عمری متوسّط رسیدید شما را می‌میراند، و برخی از شما کسانی اند که به فروترین دوران عمر رسانده می‌شوند و چنان فرتوت می‌گردند تا پس از دانستن، چیزی ندانند. قطعاً خدا دانا و تواناست.'),
(1972, 16, 71, 'و خدا برخی از شما را در بهره بری از روزی، بر برخی دیگر برتری بخشیده است، پس کسانی که برتری داده شده‌اند، حاضر نیستند روزیِ خود و امکان بهره برداریِ آزادانه از آن را در اختیار بردگان خود قرار دهند تا در آن باهم برابر باشند. این برتری، نعمتی الهی است، آیا نعمت خدا را انکار می‌کنند؟'),
(1973, 16, 72, 'و خدا برای شما از جنس خودتان همسرانی آفرید، و برای شما از همسرانتان پسران و نوادگانی که کمک شمایند پدید آورْد. و از چیزهای پاکیزه به شما روزی داد پس آیا به باطل ایمان می‌آورند و به نعمت خدا کفر میورزند؟'),
(1974, 16, 73, 'مشرکان به جای خدا معبودانی را می‌پرستند که برای آنان هیچ رزقی بلکه هیچ چیزی از آسمان‌ها و زمین را در اختیار ندارند و نمی‌توانند آن را در اختیار داشته باشند.'),
(1975, 16, 74, 'پس خدا را با پدیده‌ها نسنجید و همانند نسازید و او را به صفات آنها وصف نکنید، که خدا خود آگاه است ولی شما از ذات او بی خبرید.'),
(1976, 16, 75, 'خداوند برده زرخریدی را مَثَل زده که نه مالک خویشتن است و نه از دنیا مال و ثروتی دارد و نه می‌تواند در مالی تصرّف کند، در برابر کسی که ما به او از جانب خود رزقی نیکو داده‌ایم و او آزادانه در نهان و آشکار از آن انفاق می‌کند، آیا آنها برابرند؟ پس چگونه خدا را با معبودانشان برابر می‌دانند؟ ستایش همه از آنِ خداست، چرا که خوبی‌ها همه از جانب اوست ولی بیشتر مردم این حقیقت را نمی‌دانند.'),
(1977, 16, 76, 'و خداوند دو مرد را نیز مثل زده است که یکی از آن دو گنگ است و قدرت تفهیم و تفهّم ندارد و سربار مولای خویش است، او را به هر جا روانه کند سودی به دست نمی‌آورد، آیا او با کسی که به عدالت فرمان می‌دهد و خود نیز بر راه راست قرار دارد، برابر است؟ پس خدا هم که هدایتگر مردم است با بت‌ها که از خود چیزی ندارند برابر نیست.'),
(1978, 16, 77, 'نهانِ آسمان‌ها و زمین، از جمله رستاخیز، از آنِ خداست و آشکار ساختن آن، بر او دشوار نیست. برای او کار رستاخیز در آسانی و سرعت جز مانند یک نگاه نیست، بلکه از آن هم آسان‌تر و سریع‌تر است. به یقین، خدا بر انجام هر کاری تواناست.'),
(1979, 16, 78, 'و خداست که شما را از شکم مادرانتان بیرون آورد در حالی که چیزی نمی‌دانستید، و برای شما گوش و چشم و دل قرار داد تا حقایق را درک کنید و باشد که سپاس بگزارید.'),
(1980, 16, 79, 'آیا به پرندگان در حالی که در فضای آسمان مسخّر خدایند ننگریسته‌اند؟ هیچ کس جز خدا آنها را در آن حال نگاه نمی‌دارد. قطعاً در این برای مردمی که ایمان دارند نشانه‌هایی بر یکتایی خداست.'),
(1981, 16, 80, 'خداوند برای شما از خانه هایتان محل سکونت و استراحت قرار داده و برای شما از پوست دام‌ها خیمه‌ها پدید آورده که روز کوچ کردن و روز اقامت خود آنها را سبک می‌یابید، و از پشم‌ها و کرک‌ها و موهای آنها برای شما اثاث منزل و کالایی که تا مدّتی از آن بهره می‌برید قرار داده است.'),
(1982, 16, 81, 'و خدا برای شما از آنچه آفریده است سایه‌هایی قرار داده و از کوه‌ها برای شما نهانگاه‌ها پدید آورده و برای شما تن پوش‌هایی قرار داده است که شما را از گرما حفظ می‌کند، و نیز زره‌هایی که شما را در نبردتان از گزند تیرها و نیزه‌ها مصون می‌دارد. بدین سان نعمتش را بر شما تمام می‌کند، باشد که تسلیم خواسته او شوید و فرمانش را گردن نهید.'),
(1983, 16, 82, 'پس اگر با وجود این همه نعمت که به آنان ارزانی داشته‌ایم روی برتابند و ایمان نیاورند، آنان را به عذاب خواهیم گرفت و برعهده تو چیزی جز ابلاغ پیام به طور آشکار، نیست.'),
(1984, 16, 83, 'آنان نعمت‌های خدا را می‌شناسند و به الهی بودن آنها اذعان دارند، ولی هنگامی که باید به پاس آنها به خدا ایمان بیاورند آنها را نادیده می‌گیرند و بیشترشان علاوه بر این با پافشاری بر کفر و بازداری مردم از راه خدا همچنان بر کفر خویش می‌افزایند.'),
(1985, 16, 84, 'یاد کن روزی را که از هر امتی گواهی برمی انگیزیم تا بر کارهای آنان گواهی دهد، آن گاه به کسانی که کافر شده‌اند نه اجازه داده می‌شود که سخن بگویند و عذری برای کفر خویش بیاورند و نه از آنان خواسته می‌شود که با کارهای پسندیده گذشته خود را جبران کنند و خشنودی خدا را فراهم سازند، چرا که قیامت، روز جزاست نه روز عمل.'),
(1986, 16, 85, 'و چون ستمکاران (کافران و گنه پیشگان) عذاب دوزخ را بنگرند، نه از عذاب آنان کاسته می‌شود و نه به آنان مهلت می‌دهند.'),
(1987, 16, 86, 'و چون مشرکانْ شریکانی را که برای خدا پنداشته‌اند ببینند، گویند: پروردگارا، اینانند شریکانی که برای تو قراردادیم و آن­ها را به جای تو می­خواندیم و می­پرستیدیم. پس آن­ها پاسخشان­دهند که قطعاً شما دروغ­می­گویید.'),
(1988, 16, 87, 'آن روز که رستاخیز برپا شود مشرکان سر تسلیم به درگاه خدا فرود می‌آورند و آنچه به دروغ بافته‌اند، از خاطرشان محو می‌گردد.'),
(1989, 16, 88, 'اما سران کفر که هم خود کافر شدند و هم مردم را از راه خدا بازداشتند، به سزای این که با فسادگری خود مانع از تشکیل جامعه‌ای شایسته می‌شدند، عذابی بر عذاب کفرورزی و ستمگریشان می‌افزاییم.'),
(1990, 16, 89, 'و یاد کن روزی را که در میان هر امّتی گواهی از خودشان برمی انگیزیم تا بر کارهای آنان گواهی دهد، و تو را ـ ای پیامبر ـ شاهد می‌آوریم تا به منزلت والای آن گواهان شهادت دهی، این در حالی است که ما قرآن را که بیانگر هر چیز و هدایت و رحمت و مژده‌ای برای تسلیم شدگان در برابر حق بود بر تو نازل کردیم تا بر ستم ستمگران و اسلام تسلیم شدگان گواه باشی.'),
(1991, 16, 90, 'خداوند به شما فرمان می‌دهد تا برای پدید آمدن جامعه‌ای صالح، با مردم به عدل و داد رفتار کنید و به آنان نیکی کنید و حق خویشاوند را به او بپردازید، و شما را از کار زشت و ناپسند و از ظلم و سرکشی باز می‌دارد. خدا به شما پند می‌دهد، باشد که دریابید نیکبختی شما در به کار بستن این اندرزهاست.'),
(1992, 16, 91, 'و چون با خدا پیمان بستید به پیمان خود وفا کنید و سوگندهای خود را پس از استوار کردن آنها مشکنید، در حالی که خدا را در وفا کردن به پیمان‌ها بر خود ضامن قرار داده‌اید، به یقین خدا می‌داند که شما چه می‌کنید.'),
(1993, 16, 92, 'و در سوگندهای خود مانند آن زن نباشید که بافته اش را پس از آن که محکم می‌کرد از هم می‌گسست و رشته رشته می‌کرد. سوگندهای خود را دستاویز خدعه در میان خویش قرار می‌دهید تا به این وسیله گروهی مال و ثروت بیشتری از گروه مقابل به دست آورد. جز این نیست که خدا شما را بدین وسیله می‌آزماید و قطعاً روز قیامت آنچه را که در آن اختلاف می‌کردید آشکار می‌سازد و باطل گرایان و هدایت یافتگان را مشخص می‌کند.'),
(1994, 16, 93, 'اگر خدا می‌خواست شما را امّتی یگانه قرار می‌داد و همه را به راه راست می‌برد، ولی چنین نخواسته است، از این رو کسانی را که راه مخالفت را در پیش گیرند به مشیت خود به گمراهی می‌کشاند و کسانی را که راه فطرت را دنبال کنند به خواست خود هدایت می‌کند، و قطعاً شما از کارهایی که انجام داده‌اید بازپرسی خواهید شد.'),
(1995, 16, 94, 'و سوگندهای خود را دستاویز نیرنگ در میان خود قرار ندهید که گامی که بر ارزش‌های الهی و انسانی استوار بوده است پس از استواری اش می‌لغزد و شما بدین سبب که از راه خدا بازمانده‌اید در دنیا ناگواری می‌چشید و تیره بخت می‌گردید و در آخرت نیز عذابی بزرگ خواهید داشت.'),
(1996, 16, 95, 'و پیمان خدا را به بهایی اندک مفروشید. قطعاً آنچه نزد خداست ـ اگر بدانید ـ برای شما بهتر از مال و منال دنیاست.'),
(1997, 16, 96, 'زیرا آنچه از دنیا در اختیار شماست از بین می‌رود، ولی پاداش‌هایی که نزد خداست ماندگار است، و ما قطعاً پاداش کسانی را که در وفاداری به پیمان خدا شکیبا بوده‌اند، بر پایه بهترین کارهایی که انجام داده‌اند عطا می‌کنیم.'),
(1998, 16, 97, 'هر کس ـ از مرد یا زن ـ کاری شایسته کند و با ایمان باشد، قطعاً او را به حیاتی پاک زنده خواهیم داشت و به چنین کسانی بر پایه بهترین کاری که انجام می‌دادند پاداش خواهیم داد.'),
(1999, 16, 98, 'پس هرگاه قرآن تلاوت می­کنی از خدا بخواه که تو را از اغواگری‌های شیطانِ رانده شده پناه دهد،'),
(2000, 16, 99, 'زیرا او بر کسانی که ایمان آورده‌اند و بر پروردگارشان توکّل می‌کنند، هیچ تسلّطی نخواهد داشت.'),
(2001, 16, 100, 'تسلّط شیطان فقط بر کسانی است که او را سرپرست خود می‌گیرند، و بر کسانی است که با پیروی نمودن از او، وی را شریک خدا قرار می‌دهند.'),
(2002, 16, 101, 'و هرگاه در مقام نسخ، آیه‌ای از قرآن را به جای آیه‌ای دیگر بیاوریم ـ در حالی که خدا به آنچه نازل می‌کند داناتر است ـ مشرکان به تو می‌گویند: جز این نیست که تو دروغپردازی. چنین نیست، تو از آنچه می‌گویند مبرّایی، ولی بیشتر آنان حکمت این تغییرها را نمی‌دانند.'),
(2003, 16, 102, 'بگو: این آیات جایگزین را (روح القدس) از جانب پروردگارت به حق فرو آورده است، تا کسانی را که ایمان آورده‌اند بر ایمانشان ثابت قدم بدارد و برای کسانی که تسلیم فرمان خدایند، هدایت و نویدی باشد.'),
(2004, 16, 103, 'ما به خوبی می‌دانیم که مشرکان می‌گویند قرآن از سوی خدا نیست، بلکه انسانی آن را به محمّد می‌آموزد. چنین نیست که می‌گویند، زبان آن کسی که قرآن را ساخته او می‌دانند نارساست و این قرآن به زبان عربی شیوا و گویاست.'),
(2005, 16, 104, 'کسانی که به آیات خدا ایمان نمی‌آورند، خدا آنان را هدایت نمی‌کند و برای آنان عذابی دردناک خواهد بود، ولی محمّد به آیات خدا ایمان دارد و هدایت یافته است، پس چگونه می‌تواند بر خدا دروغ بندد؟'),
(2006, 16, 105, 'تنها کسانی دروغ­می­بافند و آن را به خدا نسبت می­دهند که به آیات خدا ایمان­ندارند، و اینانند که دروغ­گویند.'),
(2007, 16, 106, 'کسانی که پس از ایمان آوردنشان، به خدا کافر شوند، نه آن کسی که به گفتن سخنی کفرآمیز مجبور گردد ولی دلش به ایمان آرامش یافته است، بلکه کسانی که سینه را برای کفر گشوده و آن را به دل پذیرفته‌اند، خشمی از جانب خدا بر آنان خواهد بود و عذابی بزرگ خواهند داشت.'),
(2008, 16, 107, 'این بدان سبب است که آنان با شیفتگی زندگی دنیا را برگزیده و آن را بر آخرت ترجیح داده‌اند و بدان سبب است که خدا مردم کفرپیشه را هدایت نمی‌کند.'),
(2009, 16, 108, 'اینان کسانی اند که خدا بر دل‌ها و گوش‌ها و دیدگانشان مهر نهاده است، از این رو نه بُرهانی را درک می‌کنند و نه اندرزی را می‌شنوند و نه نگاهی عبرت آمیز دارند. اینانند که از سرای آخرت بی خبرند.'),
(2010, 16, 109, 'بی تردید، آنان در آخرت زیانکار خواهند بود.'),
(2011, 16, 110, 'آری، پروردگار تو کسانی را که پس از شکنجه شدن به دست مشرکان و جاری کردن سخنی کفرآمیز بر زبان، در راه خدا هجرت نمودند و سپس جهاد کردند و شکیبایی ورزیدند، قطعاً پروردگار تو پس از این مراحل آنان را می‌آمرزد و بر آنان رحمت می‌آورَد.'),
(2012, 16, 111, 'روزی که هر کسی در پیشگاه پروردگارش حضور می‌یابد در حالی که به دفاع از خویشتن می‌پردازد. در آن روز دفاع بی نتیجه است، زیرا به هر کسی آنچه را انجام داده است به طور کامل خواهند داد و به آنان ستم نخواهد شد.'),
(2013, 16, 112, 'و خداوند مثلی زده است: شهری که از آسیب‌ها در امان بود و مردمش در آن آرامش داشتند و روزی اش از هر سو به خوبی و فراوانی به آن می‌رسید، ولی نعمت‌های خدا را ناسپاسی کردند، و خدا هم به سزای کارهایی که می‌کردند فراگیری گرسنگی و ترس را به آنان چشانید.'),
(2014, 16, 113, 'قطعاً، پیامبری از خودشان به سویشان آمد، امّا او را دروغگو شمردند، سرانجام در حالی که ستمکار بودند، عذاب آنان را فرا گرفت.'),
(2015, 16, 114, 'پس، از آنچه خدا روزیِ شما کرده که حلال و پاکیزه است بخورید و نعمت‌های خدا را سپاسگزار باشید اگر فقط او را می‌پرستید.'),
(2016, 16, 115, 'خداوند، تنها مردار و خون و گوشت خوک و آنچه را که هنگام ذبح، نام غیر خدا بر آن برده شده، بر شما حرام کرده است. پس هر کس به خوردن اینها ناچار شود، به شرط آن که ناچاری اش بر اثر ستمکاری و سرکشی نباشد، گناهی بر او نیست، چرا که خداوند بسیار آمرزنده و مهربان است.'),
(2017, 16, 116, 'و به سبب این که زبانتان دروغ می‌بافد نگویید این چیز حلال است و آن دیگری حرام، تا بر خدا دروغ بندید. قطعاً کسانی که بر خدا دروغ می‌بندند نیکبخت نخواهند شد.'),
(2018, 16, 117, '[آنچه از دنیا در اختیار دارند] بهره‌ای اندک است و برای آنان عذابی دردناک خواهد بود.'),
(2019, 16, 118, 'آری بر یهودیان چیزهای پاکیزه‌ای را حرام نمودیم که پیش از این آنها را بر تو حکایت کردیم، ما با این کار بر آنان ستم نکردیم، بلکه آنان با نافرمانی خدا بر خود ستم می‌کردند و سزاوار کیفر شدند و ما به کیفر گناهشان آن چیزها را بر آنان تحریم نمودیم.'),
(2020, 16, 119, 'با این حال پروردگار تو کسانی را که از روی نادانی مرتکب گناه شده، سپس بعد از آن توبه نموده و کار شایسته کرده‌اند، آری پروردگار تو آنان را پس از توبه شان می‌آمرزد و بر آنان رحمت می‌آورَد.'),
(2021, 16, 120, 'به یقین، ابراهیم به تنهایی یک امّت بود. پرستشگر خدا و فرمانبردار او، و حقگرا و از هرگونه انحرافی به دور بود و هرگز از مشرکان نبود.'),
(2022, 16, 121, 'نعمت‌های خدا را سپاسگزار بود و خدا او را برگزید و برای خویش خالص گردانید و او را به راهی راست هدایت کرد.'),
(2023, 16, 122, 'و ما در دنیا زندگی نیکویی به او دادیم و قطعاً او در آخرت از شایستگان خواهد بود.'),
(2024, 16, 123, 'سپس به تو وحی کردیم که از آیین ابراهیم که حقگرا و از انحراف پیراسته بود و هرگز از مشرکان نبود پیروی کن.'),
(2025, 16, 124, 'تعطیلی کار در روز شنبه به عنوان تکلیف و آزمونی بر یهود مقرّر گردید تا دست از کار بکشند و به عبادت خدا بپردازند، ولی آنان پس از ابلاغ این حکم در آن اختلاف کردند (گروهی آن را پذیرفتند و گروهی با آن مخالفت کردند و گروهی نیز با نیرنگ حرمت آن را شکستند) و قطعاً پروردگار تو روز قیامت میان آنان درباره آنچه بر سرش اختلاف می‌کردند، داوری خواهد کرد.'),
(2026, 16, 125, 'مردم را با برهان و اندرز نیکو به راه پروردگارت فرا خوان و در این رهگذر با بهترین شیوه با آنان مجادله کن. قطعاً پروردگار تو به کسی که از راه او گم گشته داناتر است و او به هدایت یافتگان نیز آگاه‌تر است.'),
(2027, 16, 126, 'و اگر می‌خواهید کافران را به سزای عقوبت‌هایی که به شما رسانده‌اند مجازات کنید، مانند همان عقوبتی که به شما رسیده است آنان را عقوبت کنید نه بیشتر از آن، و اگر شکیبایی کنید و از عقوبتِ آنان درگذرید، قطعاً چشم پوشی برای صبرپیشگان بهتر از انتقام است.'),
(2028, 16, 127, 'ای پیامبر، شکیبایی کن، و شکیبایی تو جز به توفیق خدا نیست، و بر کفر ورزیدن کافران اندوه مخور و از نیرنگ‌هایی که می‌کنند دلتنگ مباش.'),
(2029, 16, 128, 'زیرا خدا با کسانی است که تقوا پیشه کرده‌اند و با کسانی است که نیکوکارند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'منزّه است آن خدای که بنده اش محمّد را در یک شب از مسجدالحرام به سوی مسجدالاقصی (بیت المقدس) که پیرامون آن را برکت دادیم، سیر داد. آری، ما او را بدان جا بردیم تا بخشی از نشانه‌های خود را به او بنمایانیم. به یقین، خداوند در خواست بندگانش را می‌شنود و به حال آنان بیناست، از این روی در پیامبر شایستگی این کرامت را دید و آن را به او عطا کرد.'),
(2031, 17, 2, 'و به موسی تورات را ارزانی داشتیم و آن را برای بنی اسرائیل مایه هدایت قرار دادیم با این پیام که: در امر دین و دنیا جز من کارسازی مگیرید که مرا در تدبیر جهان شریکی نیست.'),
(2032, 17, 3, 'آن کتاب هدایت را از آن روی به بنی اسرائیل دادیم که آنان از نسل کسانی بودند که با نوح بر آن کشتی سوارشان کردیم، و پیش‌تر وعده داده بودیم که همراهان نوح را با انواع برکت‌ها مدد می‌رسانیم، زیرا نوح بنده‌ای بسیار سپاسگزار بود.'),
(2033, 17, 4, 'و در آن کتاب به بنی اسرائیل اعلام کردیم که قطعاً دو بار در سرزمین فلسطین فساد به پا خواهید کرد و هر بار به سرکشی بزرگی دست خواهید زد.'),
(2034, 17, 5, 'ما نیز هر دو بار از شما انتقام خواهیم گرفت. پس چون زمان تحقق وعده انتقام ما به سزای نخستین بار از آن دو سرکشی فرا رسد، بندگانی از خود را که سخت جنگاورند به سوی شما بسیج می‌کنیم. آنان در میان خانه‌های شما درمی آیند و شما را سرکوب می‌کنند، و این وعده‌ای انجام شدنی است.'),
(2035, 17, 6, 'و پس از چندی چیرگی بر دشمنانتان را به شما باز می‌گردانیم و شما را با اموال و پسرانی فراوان یاری می‌دهیم و نفرات شما را بیشتر می‌گردانیم.'),
(2036, 17, 7, 'این شکست و پیروزی نتیجه عملکرد خودتان است. اگر نیکی کنید به خود نیکی کرده‌اید و اگر بدی کنید به خود بدی کرده‌اید. و چون زمان تحقق وعده انتقام ما به سزای سرکشی بعدی شما فرا رسد، باز هم بندگانی از خود را بسیج می‌کنیم تا چهره‌های شما را اندوهگین کنند و به مسجدالاقصی درآیند و حرمت آن را بشکنند و آن را ویران کنند، چنان که نخستین بار بدان جا وارد شدند و بر هر چه چیره می‌شوند، به کلّی آن را تباه سازند.'),
(2037, 17, 8, 'پس اگر بعد از آن توبه کنید و به اطاعت خدا بازآیید، امید است پروردگارتان بر شما رحمت آورَد و شما را نجات بخشد، و اگر دیگر بار به فساد و طغیان روی آورید ما نیز به عقوبت شما باز می‌گردیم، و بدانید که ما دوزخ را برای کافران تنگنایی قرار داده‌ایم که نتوانند از آن بگریزند.'),
(2038, 17, 9, 'به راستی این قرآن به آیینی که از همه آیین‌ها استوارتر و جامع‌تر است هدایت می‌کند، و به آن مؤمنانی که کارهای شایسته می‌کنند نوید می‌دهد که برای آنان پاداشی بزرگ خواهد بود،'),
(2039, 17, 10, 'و این که برای کسانی که به جهان آخرت ایمان ندارند عذابی دردناک آماده ساخته‌ایم.'),
(2040, 17, 11, 'و انسان همان گونه که خیر و نیکی را می‌طلبد و برای آن تلاش می‌کند شر و بدی را نیز می‌طلبد، چرا که او شتابزده است و بی آن که بیندیشد و خیر و شرّ خویش را بشناسد، شتابان در پی خواسته‌های نفسانی خود می‌رود.'),
(2041, 17, 12, 'ما شب و روز را دو نشانه توحید قرار داده‌ایم، شب را که خود نشانه‌ای است تاریک ساخته و روز را که نشانه‌ای دیگر است روشن و روشنی بخش گردانده‌ایم تا شما در پرتو آن روزی خود را که تفضّلی از جانب پروردگارتان است بجویید و شماره سال‌ها و حساب اوقات را بدانید، و هر چیزی را در آفرینش به خوبی مشخّص ساخته‌ایم.'),
(2042, 17, 13, 'و کارهای نیک و بد هر انسانی را که تعیین کننده سرنوشت اوست برای همیشه به گردن وی آویخته‌ایم و روز قیامت برای او کارنامه‌ای بیرون می‌آوریم که آن را به صورت گشوده دیدار می‌کند.'),
(2043, 17, 14, 'به او می‌گویند: کارنامه ات را بخوان، همین بس که امروز خود حسابرس کارهای خویش باشی.'),
(2044, 17, 15, 'هر که هدایت یافت تنها به سود خود هدایت یافته و هر که گمراه شد تنها به زیان خود گمراه شده است، و هیچ گنهکاری بار گناه دیگری را بر دوش نمی‌گیرد، و ما هیچ امّتی را پیش از آن که پیامبری به سویشان بفرستیم به عذاب‌های دنیوی عذاب نکرده‌ایم.'),
(2045, 17, 16, 'و چون زمان آن فرا رسد که دیاری را نابود کنیم، بر نعمت توانگران و مرفّهان آن می‌افزاییم و آنان با سرمستی بندگی خدا را رها می‌کنند و در آن دیار به فسق و فجور می‌پردازند، در نتیجه آن سخن (وعده عذاب) درباره آنان تحقق می‌یابد و آن جا را به کلّی نابود می‌کنیم.'),
(2046, 17, 17, 'چه بسیار امّت‌هایی را که پس از نوح هلاک کردیم، و همین بس که پروردگارت به گناهان بندگانش آگاه و بیناست.'),
(2047, 17, 18, 'هر کس بهره زندگی دنیای زودگذر را بخواهد و از سرای آخرت چشم بپوشد، در این دنیا به هر کس که بخواهیم و هر اندازه بخواهیم بی هیچ تأخیری عطا می‌کنیم، آن گاه دوزخ را برای او مقرّر می‌داریم تا ملامت شده و مطرود از رحمت خدا به آن درآید و به آتش آن بسوزد.'),
(2048, 17, 19, 'و هر کس زندگی آخرت را بخواهد و برای آن تلاشی درخورِ آن کند و به خدا و پیامبران ایمان داشته باشد، اینانند که تلاششان سپاسگزاری شده و مورد پذیرش خدا قرار خواهد گرفت.'),
(2049, 17, 20, 'هر دو گروه ـ هم دنیاطلبان و هم آخرت طلبان ـ را از عطای پروردگارت مدد می‌رسانیم، و برای عطای پروردگارت حد و مرزی نیست و از کسی باز داشته نمی‌شود و هر کس به قدر استحقاق خود آن را دریافت می‌کند.'),
(2050, 17, 21, 'بنگر که چگونه در دنیا برخی از مردم را در بهره مندی از موهبت‌ها بر برخی دیگر برتری داده‌ایم، بدین سان در آخرت نیز آنان را یکسان قرار نمی‌دهیم، بلکه سرای آخرت درجاتی بالاتر دارد و برتریِ مقامات آن بیشتر است.'),
(2051, 17, 22, 'با وجود خدا معبود دیگری برای خود قرار مده که نکوهیده و بی یاور از تقرّب به خدا باز خواهی ماند.'),
(2052, 17, 23, 'و پروردگار تو مقرّر داشته است که جز او را نپرستید و به پدر و مادر خود نیکی کنید. اگر یکی از آن دو یا هر دو، در کنار تو به کهنسالی رسیدند به آنان کلمه‌ای ناخوشایند مگوی، و با آنان پرخاشگری مکن، و با ادب و احترام با آنان سخن بگوی.'),
(2053, 17, 24, 'و از سر مهربانی بال فروتنی را برای آن دو بگستر و بگو: پروردگارا، بر آنان رحمت آور، چنان که مرا در کودکی پروردند.'),
(2054, 17, 25, 'پروردگارتان به آنچه در دل‌های شماست از شما داناتر است. اگر واقعاً شایسته باشید، چنانچه در حق پدر و مادرتان کوتاهی نمودید و پس از آن توبه کردید، قطعاً خدا توبه کنندگان را می‌آمرزد.'),
(2055, 17, 26, 'و حق خویشاوند و بینوا و در راه مانده را به او بده و هرگز به اسراف ریخت و پاش مکن.'),
(2056, 17, 27, 'چرا که ریخت و پاش کنندگان اسرافکار برادران (همدم و همگام) شیاطین‌اند، و ابلیس آن شیطان بزرگ همواره نسبت به نعمت‌های پروردگارش ناسپاس بوده است.'),
(2057, 17, 28, 'اگر از تهیدستان به علّت آن که مالی در اختیار نداری روی می‌گردانی، و گشایشی را از پروردگارت می‌طلبی که امید آن را داری، با آنان به نرمی سخن بگوی و آنان را با مهربانی بازگردان.'),
(2058, 17, 29, 'و دست خویش بر گردن مبند و بخل مکن و بسیار هم آن را مگشای که هر چه داری ببخشی و در نتیجه نکوهیده و برهنه بر جای بنشینی.'),
(2059, 17, 30, 'این سنّت پروردگار توست که روزی را برای هر که بخواهد گشاده می‌سازد و بر هر که بخواهد تنگ می‌گیرد. به یقین او به بندگانش آگاه و بیناست.'),
(2060, 17, 31, 'و فرزندانتان را از بیم این که مبادا تهیدست شوید و آنان به گدایی بیفتند یا با کسانی که همردیف شما نیستند ازدواج کنند نکُشید، ماییم که به آنان و شما روزی می‌دهیم. راستی کشتن آنان خطایی است بزرگ.'),
(2061, 17, 32, 'و به زنا نزدیک نشوید که آن کاری زشت و راهی بد و نادرست است.'),
(2062, 17, 33, 'و انسان را ـ که خدا کشتن او را حرام کرده است ـ جز به حق نکُشید، و هر کس به ستم کشته شود، برای ولیّ او سلطه‌ای بر قاتل قرار داده‌ایم، پس نباید در کشتن زیاده روی کند و چند تن را در برابر یک نفر بکشد یا کسی را جز قاتل به قتل رساند. بی تردید، ولیّ مقتول با حکمی که مقرر داشته‌ایم یاری شده است.'),
(2063, 17, 34, 'و به مال یتیم ـ جز به بهترین وجهی که برای او سودآورتر است ـ نزدیک نشوید تا به توانایی خود برسد، و به پیمان‌ها وفا کنید که قطعاً پیمان‌ها مورد سؤال قرار خواهند گرفت.'),
(2064, 17, 35, 'و چون پیمانه می‌کنید، پیمانه را پر سازید و اجناس را با ترازوی درست وزن کنید که این بهتر است و فرجامی نیکوتر دارد.'),
(2065, 17, 36, 'و از باورها، رفتارها و گفتارهایی که بدانها علم نداری پیروی مکن، زیرا گوش و چشم و دل، از همه اینها سؤال می‌شود که آیا آنچه را دریافته بودند علم بود یا شک و تردیدی به همراه داشت.'),
(2066, 17, 37, 'و در زمین از روی سرمستی و خودبینی گام برمدار، که تو هرگز نمی‌توانی زمین را بشکافی و در بزرگی هرگز به بلندای کوه‌ها نخواهی رسید.'),
(2067, 17, 38, 'همه آن تکلیف‌ها که برشمردیم، بَدش نزد پروردگار تو زشت و ناپسند است.'),
(2068, 17, 39, 'این سفارش‌ها از جمله حکمت‌هایی است که پروردگارت به تو وحی کرده است و از همه مهم‌تر توحید است، پس با وجود خداوند معبود دیگری برای خود قرار مده که نکوهیده و مطرود از رحمت خدا در دوزخ افکنده خواهی شد.'),
(2069, 17, 40, 'آیا پروردگارتان شما را با دادن پسران گرامی داشته و خود از فرشتگان دخترانی برگرفته است؟ راستی سخنی بزرگ بر زبان می‌رانید که پیامدهایی ناگوار دارد.'),
(2070, 17, 41, 'ما در این قرآن توحید را با دلایل گوناگون بیان کرده‌ایم تا آن را به خوبی دریابند، ولی چیزی جز گریز از حقیقت بر آنان نمی‌افزاید.'),
(2071, 17, 42, 'بگو: اگر ـ چنان که مشرکان می‌گویند ـ با وجود خدا معبودان دیگری نیز وجود داشت، در این صورت آنها درصدد یافتن راهی به سوی خدای صاحب عرش برمی آمدند تا خود فرمانروایی را در اختیار گیرند.'),
(2072, 17, 43, 'منزّه است خدا از این که کسی بر او چیره شود، و بسیار برتر و بالاتر است از آنچه درباره او می‌گویند.'),
(2073, 17, 44, 'آسمان‌های هفتگانه و زمین و هر که در آنهاست او را تسبیح می‌گویند، و هیچ چیزی نیست مگر این که خدا را همراه با ستایش او تسبیح می‌گوید، ولی شما تسبیح آنها را درنمی یابید. به راستی که خدا در کیفر منکران بردبار و توبه کنندگان را آمرزگار است.'),
(2074, 17, 45, 'و زمانی که قرآن می‌خوانی، میان تو و کسانی که به آخرت ایمان ندارند پرده‌ای قرار می‌دهیم که از دیدگانشان مستور است، از این رو حقایق آن را درنمی یابند و به توحید و معاد ایمان نمی‌آورند و رسالت تو را تصدیق نمی‌کنند.'),
(2075, 17, 46, 'و بر دل هایشان پوشش‌هایی نهاده‌ایم تا قرآن را چنان که باید درنیابند، و در گوش هایشان سنگینی قرار داده‌ایم تا آن را چنان که درخور آن است نشنوند. و هنگامی که پروردگارت را در قرآن به یگانگی یاد کنی پشت می‌کنند و می‌رمند.'),
(2076, 17, 47, 'ما به گوش هایشان آن گاه که به تو گوش می‌سپارند داناتریم و از دل هایشان آن گاه که درباره تو به نجوا می‌پردازند آگاه تریم، آن گاه که ستمکارانشان می‌گویند: شما جز از مردی افسون شده پیروی نمی‌کنید.'),
(2077, 17, 48, 'بنگر که چگونه برای تو وصف‌ها گفتند و به همین دلیل گمراه شدند، از این رو نمی‌توانند راهی به سوی ایمان بیابند.'),
(2078, 17, 49, 'و گفتند: آیا هنگامی که استخوان‌هایی شدیم و پوسیده گشتیم، به راستی با آفرینشی جدید برانگیخته می‌شویم؟'),
(2079, 17, 50, 'ای پیامبر، در پاسخ آنان بگو: سنگ شوید یا آهن،'),
(2080, 17, 51, 'یا به آفریده‌ای دیگر از آنچه در خاطرتان بزرگ می‌نماید و پدید آمدن دوباره انسان را از آن دشوار می‌بینید، باز هم برانگیخته خواهید شد. پس خواهند گفت: چه کسی ما را به صورت کنونی باز می‌گرداند؟ بگو: همان کس که بار نخست شما را پدید آورْد. پس سرهایشان را از روی استهزا به طرف تو حرکت می‌دهند و می‌گویند: آن چه زمانی خواهد بود؟ بگو: ممکن است آن روز نزدیک باشد.'),
(2081, 17, 52, 'روزی حیات دوباره خواهید یافت که خدا شما را برای جزا و پاداش فرا می‌خواند و شما ستایش کنان او را اجابت می‌کنید و به پا می‌خیزید، و آن روز گمان می‌برید که جز اندک زمانی در گورها درنگ نکرده‌اید.'),
(2082, 17, 53, 'و به بندگان مؤمن من بگو که در گفت و گو با کافران سخنی را که نیکوتر است بگویند و از سخن ناشایسته بپرهیزند که مؤمنان فرمان تو را اطاعت خواهند کرد. بی تردید، شیطان با همین سخنان ناشایسته میان مردم فتنه انگیزی می‌کند، زیرا او برای انسان دشمنی آشکار است.'),
(2083, 17, 54, 'پروردگارتان به حال شما داناتر است، پس درباره فرجام کار مردم داوری مکنید و آن را به خدا واگذارید، اگر او بخواهد بر شما رحمت می‌آورَد یا اگر بخواهد شما را کیفر می‌کند. و ما تو را ـ ای پیامبر ـ به عنوان کارگزاری برای آنان نفرستاده‌ایم تا هر که را بخواهی نجات بخشی و هر که را بخواهی محروم کنی.'),
(2084, 17, 55, 'و پروردگارت به کسانی که در آسمان‌ها و زمینند داناتر است، پس چگونه از حال شما آگاه نباشد؟ و به راستی ما برخی از پیامبران را بر برخی دیگر برتری دادیم و به داود زبور عطا کردیم.'),
(2085, 17, 56, 'به مشرکان بگو: فرشتگان و جنّیان و آدمیانی را که به جای خدا معبود خود پنداشته‌اید بخوانید. آنها نه می‌توانند گزند و محنت را از شما بزدایند و نه آن را دگرگون سازند.'),
(2086, 17, 57, 'کسانی که مشرکان آنها را می‌پرستند، خود واسطه ای می‌جویند که به وسیله آن به پروردگارشان نزدیک شوند و در پی آنند که ببینند کدامین واسطه به خدا نزدیک‌تر است تا راه او را دنبال کنند، آنان خود به رحمت الهی امیدوارند و از عذاب او می‌هراسند، چرا که عذاب پروردگارت درخورِ پرهیز است.'),
(2087, 17, 58, 'و هیچ شهری نیست مگر این که ما آن را پیش از روز قیامت به هلاکت می‌رسانیم یا آن را به عذابی سخت عذاب می‌کنیم. این در لوح محفوظ رقم خورده و حتمی شده است.'),
(2088, 17, 59, 'و چیزی ما را از فرستادن معجزاتِ پیشنهادی مشرکان باز نداشت جز این که پیشینیان این گونه معجزات را نیز دروغ انگاشتند و ما آنان را هلاک کردیم. ما به قوم ثمود آن ماده شتر را که آیتی روشن بود عطا کردیم ولی با دروغ شمردنش به خود ستم کردند و ما معجزات را جز برای بیم دادن مردم نمی‌فرستیم.'),
(2089, 17, 60, 'و یاد کن زمانی را که به تو گفتیم: قطعاً پروردگارت بر همه مردم احاطه دارد و راهی را که برمی گزینند می‌داند. و ما آن رؤیایی را که در خواب به تو نمایاندیم، و نیز آن گروهی را که چون درخت ریشه کرده‌اند و ما آنان را در قرآن لعنت کرده‌ایم، جز برای آزمایش مردم قرار نداده‌ایم، و ما مردم را بیم می‌دهیم ولی هشدار ما بر آنان جز طغیانی بزرگ نمی‌افزاید.'),
(2090, 17, 61, 'و برای این که بدانی چگونه مردم به راه خطا می‌روند یاد کن زمانی را که به فرشتگان گفتیم: برای آدم سجده کنید. پس همه جز ابلیس سجده کردند. او به خداوند گفت: آیا برای کسی که او را از گِل آفریده‌ای سجده کنم؟'),
(2091, 17, 62, 'سپس گفت: آیا دیدی این کسی را که بر من برتری دادی؟ اگر مهلتم دهی و مرگ مرا تا روز قیامت به تأخیر افکنی قطعاً نسل او ـ جز اندکی از آنان ـ را لجام خواهم کرد و در پی خویش خواهم برد.'),
(2092, 17, 63, 'خدا گفت: برو، ولی هر کس از آنان از تو پیروی کند، دوزخ که کیفری بی کم و کاست است سزای شما خواهد بود.'),
(2093, 17, 64, 'و از آنان هر که را توانستی با صدای خود به نافرمانی خدا برانگیز و با سواران و پیادگانت بر آنان بانگ بزن تا به گناه روی آورند، و با آنان در اموال و اولادشان شریک شو و از آنها در همان جهتی که می‌خواهی بهره بگیر و به آنان وعده و نوید ده، ولی باید بدانند که شیطان جز وعده‌ای فریبنده به آنان وعده ای نمی‌دهد.'),
(2094, 17, 65, 'بی تردید تو را بر بندگان من هیچ تسلّطی نیست، و پروردگارت برای کارسازی امور آنان بسنده است.'),
(2095, 17, 66, 'ای مردم، پروردگار شما کسی است که کشتی‌ها را در دریا برای شما روان می‌سازد تا از فضل او برای خود روزی طلب کنید، چرا که با شما مهربان است.'),
(2096, 17, 67, 'و هنگامی که در دریا آسیبی به شما می‌رسد و در معرض غرق شدن قرار می‌گیرید، جز خداوند هر که را برای نیازهای خود می‌خوانید به دل و اندیشه شما راه نمی‌یابد، ولی هنگامی که شما را رهایی بخشید و به خشکی بازگردانید از او روی برمی تابید. و انسان بسیار ناسپاس است.'),
(2097, 17, 68, 'با این وصف، آیا ایمنید از این که خدا گوشه‌ای از خشکی را بشکافد و شما را در آن نهان سازد، یا طوفانی از شن بر شما فرستد و شما را هلاک سازد، آن گاه هیچ کارسازی که بلا را از شما بگرداند برای خود نیابید؟'),
(2098, 17, 69, 'آیا چون از غرق شدن نجات یافته‌اید، ایمن شده‌اید از این که بار دیگر خدا شما را به دریا بازگرداند و به علت این که کفر ورزیده‌اید تندبادی کشتی شکن بر شما فرو فرستد و شما را غرق کند، آن گاه کسی را برای خود نیابید که ما را به این کار مؤاخذه کند؟'),
(2099, 17, 70, 'به راستی ما فرزندان آدم را به موهبت عقل و خرد گرامی داشته و آنان را در دریا بر کشتی‌ها و در خشکی بر مرکب‌ها سوار نموده و از خوراکی‌های مطبوع به آنان روزی داده و آنان را بر بسیاری از کسانی که آفریده‌ایم چنان که باید برتری بخشیده‌ایم.'),
(2100, 17, 71, 'یاد کن روزی را که هر گروهی را با پیشوایی که برگزیده و از او پیروی کرده است فرا می‌خوانیم. پس کسانی که کارنامه آنان به دست راستشان داده شود، کارنامه خود را می‌خوانند و به آنان به اندازه رشته هسته خرمایی ستم نمی‌شود و پاداششان به طور کامل به آنان عطا می‌گردد.'),
(2101, 17, 72, 'و هر کس در این دنیا کوردل باشد و از پیشوای به حق پیروی نکند در آخرت نیز کوردل است و به سعادت نمی‌رسد و گمراه‌تر است و در نتیجه به آمرزش و رحمت الهی دست نمی‌یابد.'),
(2102, 17, 73, 'ای پیامبر، نزدیک بود مشرکان تو را از آنچه به تو وحی کرده‌ایم بلغزانند و منحرف کنند تا چیزی را غیر از آنچه به تو وحی کرده‌ایم به ما نسبت دهی و در آن صورت تو را به دوستی می‌گرفتند.'),
(2103, 17, 74, 'و اگر ما تو را ثابت قدم نمی‌داشتیم نزدیک بود اندکی به آنان متمایل شوی.'),
(2104, 17, 75, 'در آن صورت، از عذابی که در زندگی دنیا برای مجرمان مقرّر داشته‌ایم دو چندان و از عذاب پس از مرگ نیز دو چندان به تو می‌چشاندیم، سپس کسی را در برابر ما نمی‌یابی که تو را یاری دهد و از عذاب رهایی بخشد.'),
(2105, 17, 76, 'و نزدیک بود مشرکان تو را از سرزمین مکّه برکَنند تا از آن جا بیرونت کنند، در آن صورت پس از تو جز اندک زمانی باقی نمی‌مانند و به حکم سنّت ما نابود می‌شوند.'),
(2106, 17, 77, 'همان سنّتی که برای همه پیامبرانی که پیش از تو به رسالت فرستادیم مقرّر داشتیم، و هرگز برای سنّت ما دگرگونی نخواهی یافت.'),
(2107, 17, 78, 'نماز را از هنگامی که خورشید از وسط آسمان می‌گذرد تا آن گاه که تاریکی شب به نهایت می‌رسد برپادار، و نماز صبح را نیز هنگام طلوع فجر به جای آور که قطعاً نماز صبح مشهود فرشتگان شب و روز است.'),
(2108, 17, 79, 'و پاره‌ای از شب را به نماز بیدار باش که این برای تو افزون بر نمازهای واجب است، باشد که پروردگارت به تو مقامی عطا کند که همه آن را بستایند و تو را با آن مقام (مقام شفاعت) در قیامت برانگیزد.'),
(2109, 17, 80, 'و بگو: پروردگارا، مرا در هر کاری وارد می‌کنی با راستی و درستی وارد کن و از هر کاری بیرون می‌آوری با راستی و درستی بیرون بیاور، و برای من از جانب خود تسلطی یاری بخش مقرر دار تا بدین وسیله از عهده انجام وظایف خود برآیم.'),
(2110, 17, 81, 'و بگو: حق پدیدار شد و باطل نابود گشت. آری، باطل نابود شدنی است.'),
(2111, 17, 82, 'و ما قرآن را که برای مؤمنان مایه شفا و رحمت است فرو می‌فرستیم، و ستمکاران (کافران و مشرکان) را چیزی جز زیان نمی‌افزاید.'),
(2112, 17, 83, 'و هنگامی که به انسان نعمت دهیم، به آن سرگرم می‌شود و از ما روی برمی تابد و با حرکت به سمت و سویی که برای خود برگزیده است از ما دور می‌شود، و چون گزندی به او رسد از آن روی که خدا را فراموش کرده است نومید می‌شود.'),
(2113, 17, 84, 'بگو: هر کسی براساس ساختار روانی خود عمل می‌کند، پس پروردگار شما بهتر می‌داند که چه کسی راه یافته‌تر است.'),
(2114, 17, 85, 'و از تو ـ ای پیامبر ـ درباره (روح) می‌پرسند، بگو: روح از عالَم امر پروردگار من است و به شما از دانش جز اندکی نداده‌اند.'),
(2115, 17, 86, 'و اگر بخواهیم، روح آسمانی را که به سوی تو فرستاده‌ایم تا قرآن را بر تو القا کند، از میان می‌بریم، آن گاه کسی را نمی‌یابی که در برابر ما کارساز تو باشد و او را به تو بازگرداند.'),
(2116, 17, 87, 'پس آنچه به تو ارزانی شده تنها در پرتو رحمتی است که از جانب پروردگارت به تو رسیده است، چرا که فضل او بر تو فراوان بوده است.'),
(2117, 17, 88, 'بگو: اگر انس و جن گرد هم آیند بر این که مانند این قرآن را بیاورند، مانند آن را نخواهند آورْد، هر چند یکدیگر را یاری دهند.'),
(2118, 17, 89, 'به راستی ما در این قرآن برای مردم از هرگونه مَثَلی ـ که حق را آشکار کند و آنان را به ایمان و سپاس الهی گرایش دهد ـ با بیان‌های گوناگون آوردیم، ولی بیشتر مردم نپذیرفتند و به چیزی جز کفر و ناسپاسی روی نیاوردند.'),
(2119, 17, 90, 'و مشرکان به پیامبر گفتند: ما هرگز به تو ایمان نمی‌آوریم، مگر این که از زمین مکّه برای ما چشمه‌ای همیشه جاری بجوشانی.'),
(2120, 17, 91, 'یا به صورت معجزه، برای تو بوستانی از درختان خرما و انگور باشد، آن گاه در لابه لای آنها نهرهای فراوان روان سازی.'),
(2121, 17, 92, 'یا آسمان را ـ چنان که پنداشته‌ای و ما را بدان تهدید می‌کنی ـ پاره پاره بر سر ما فرو افکنی، یا خدا و فرشتگان را رویاروی ما بیاوری تا آنها را بنگریم.'),
(2122, 17, 93, 'یا خانه‌ای از طلا داشته باشی، یا به آسمان بالا روی، و بالا رفتن تو را باور نخواهیم کرد مگر این که کتابی بر ما فرود آوری که آن را بخوانیم. ای پیامبر، در پاسخ آنان بگو: منزّه است پروردگار من از این که چنین قدرتی به کسی بدهد. آیا من جز بشری هستم که به رسالت فرستاده شده‌ام؟'),
(2123, 17, 94, 'و چیزی مردم را ـ آن گاه که هدایت برایشان آمد ـ از ایمان به رسالت تو باز نداشت مگر این که گفتند: آیا خدا بشری را به رسالت برانگیخته است؟'),
(2124, 17, 95, 'بگو: بر خداست که اهل زمین را هدایت کند، حتی اگر در زمین فرشتگانی بودند که به آرامی در آن راه می‌رفتند (براساس قوانین مادی می‌زیستند) قطعاً برای هدایت آنها از آسمان فرشته‌ای که پیام آور وحی ما باشد بر آنان نازل می‌کردیم.'),
(2125, 17, 96, 'بگو: همین بس که خدا میان من و شما گواه باشد. قطعاً او به بندگان خویش آگاه و بیناست.'),
(2126, 17, 97, 'هر که را خدا هدایت کند او رهیافته است، و کسانی را که به گمراهی بیفکند، برای آنان هرگز جز او کارسازانی نخواهی یافت، و روز قیامت آنان را در حالی که بر چهره هایشان افتاده‌اند کور و کر و لال محشور می‌کنیم. جایگاهشان دوزخ است. هرگاه آتش آن فرو نشیند، شعله‌ای سوزان برایشان می‌افزاییم.'),
(2127, 17, 98, 'این است سزایشان، چرا که آیات ما را انکار کردند و گفتند: آیا آن گاه که استخوان‌هایی شدیم و پوسیده گشتیم به راستی با آفرینشی جدید برانگیخته می‌شویم؟'),
(2128, 17, 99, 'آیا ندانسته‌اند که خدایی که آسمان‌ها و زمین را آفریده است می‌تواند مانند آنان را بیافریند؟ چگونه بی باکانه با آیات خدا می‌ستیزند و حال آن که خداست که برای عمرشان سرآمدی قرار داده است که در آن هیچ تردیدی نیست سپس از آنان انتقام خواهد گرفت؟ ولی ستمکاران حق را نپذیرفته و جز ناسپاسی پیشه نکرده‌اند.'),
(2129, 17, 100, 'بگو: اگر شما مالک گنجینه‌های رحمت پروردگارم بودید، در آن صورت قطعاً از بیم تنگدستی امساک می‌کردید و به مردم چیزی نمی‌دادید، چرا که انسان سخت بخیل است.'),
(2130, 17, 101, 'و به یقین ما به موسی نُه معجزه روشن عطا کردیم. پس، از بنی اسرائیل درباره زمانی که موسی نزدشان آمد بپرس. فرعون [رسالت موسی را نپذیرفت و] به او گفت: ای موسی، من تو را جادو شده و عقل از دست داده می‌پندارم.'),
(2131, 17, 102, 'موسی گفت: قطعاً تو دانسته‌ای که این معجزات را که مایه بصیرت است کسی جز پروردگار آسمان‌ها و زمین نازل نکرده است، و من تو را ـ ای فرعون ـ سرانجام هلاک شده می‌پندارم.'),
(2132, 17, 103, 'پس فرعون بر آن شد که موسی و پیروانش را از آن سرزمین بر کَنَد، ولی او و همه کسانی را که با وی بودند، در دریا غرق کردیم.'),
(2133, 17, 104, 'و پس از هلاکت فرعون به بنی اسرائیل گفتیم: در سرزمین قدس ساکن شوید، و هنگامی که زمان تحقق آخرین وعده ما در مورد کیفر شما فرا رسد، شما را یکجا از آن سرزمین بیرون کرده به سرزمینی دیگر می‌آوریم.'),
(2134, 17, 105, 'ما قرآن را به حق نازل کرده‌ایم و به حق نازل شده و باطلی در آن راه نیافته است. و تو را ـ ای پیامبر ـ تنها نویددهنده و اخطارکننده فرستادیم، پس تو را نسزد چیزی از آن بکاهی یا چیزی بر آن بیفزایی.'),
(2135, 17, 106, 'آری، قرآن را به مقتضای نیازها و رویدادها بخش بخش و به صورتی که خوانده شود فرو فرستادیم تا آن را با درنگ بر مردم تلاوت کنی، و آن را به تدریج فرو فرستادیم.'),
(2136, 17, 107, 'بگو: به آن ایمان بیاورید یا ایمان نیاورید، در حقانیت آن تأثیری ندارد. قطعاً کسانی که پیش از نزول قرآن از معارف الهی برخوردار شده‌اند، هنگامی که قرآن بر آنان تلاوت شود، سجده کنان، بر چانه‌های خود بر زمین می‌افتند.'),
(2137, 17, 108, 'و می‌گویند: منزّه است پروردگار ما. او قیامت را برپا خواهد کرد، زیرا وعده پروردگار ما قطعاً انجام شدنی است.'),
(2138, 17, 109, 'آری، گریه کنان بر چانه‌های خود به رویْ می‌افتند و قرآن آنان را فروتنی می‌افزاید.'),
(2139, 17, 110, 'بگو: خدا را با نام (اللّه) بخوانید یا با نام (رحمان) تفاوت نمی‌کند، هر کدام را بخوانید از نیکوترین نام هاست و نیکوترین نام‌ها همه از آن اوست. و نماز خود را با صدای بلند مخوان و آن را بسیار آهسته نیز مخوان، بلکه میان این دو راهی بجوی.'),
(2140, 17, 111, 'و بگو: ستایش همه از آنِ خداوند است، همان کسی که فرزندی برای خود نگرفته و در فرمانروایی شریکی ندارد و در تدبیر جهان هستی درمانده نیست تا نیاز به یاوری داشته باشد. خدا را سپاس گوی و او را چنان که شایسته است به بزرگی یاد کن.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'ستایش همه از آنِ خداوند است، همان کسی که این کتاب آسمانی را بر بنده اش محمّد فرو فرستاد و در آن هیچ کژی و انحرافی قرار نداد.'),
(2142, 18, 2, 'کتابی که مایه برپایی جوامع انسانی است، تا کافران و گنهکاران را به عذابی سخت که از جانب خداست بیم دهد و به مؤمنانی که کارهای شایسته می‌کنند نوید دهد که برای آنان پاداشی نیکو (بهشت) خواهد بود.'),
(2143, 18, 3, 'و برای همیشه در آن ماندگارند.'),
(2144, 18, 4, 'و تا کسانی را که گفتند: خدا فرزندی برگرفته است، هشدار دهد.'),
(2145, 18, 5, 'نه آنان بدین سخن علمی دارند و نه پدرانشان که مقتدای آنانند بدان علمی داشتند. چه بزرگ سخنی است که از دهانشان بیرون می‌آید! آنان جز دروغ نمی‌گویند.'),
(2146, 18, 6, 'بیم آن می‌رود که اگر مشرکان به این قرآن ایمان نیاورند تو در پیِ رویگردانی آنان خود را از شدت اندوه به هلاکت بیفکنی.'),
(2147, 18, 7, 'ولی جای اندوه نیست، ما آنچه را که روی زمین است زینتی برای آن قرار داده‌ایم تا بدین وسیله مردم را بیازماییم که کدام یک از آنان کردارش نیکوتر است.'),
(2148, 18, 8, 'و هنگامی که آزمون به پایان رسد، آنچه را که بر روی زمین است از بین می‌بریم و آن را به صورت خاکی بی گیاه و جایی که مورد رغبت نباشد درمی آوریم.'),
(2149, 18, 9, 'آیا پنداشته‌ای که اصحاب کهف و رقیم از نشانه‌های بسیار شگفت انگیز [و کم نظیر] ما بوده‌اند؟ ولی بدان آنچه بر آدمیان می‌گذرد نظیر واقعه‌ای است که بر اصحاب کهف گذشت.'),
(2150, 18, 10, 'یاد کن زمانی را که آن جوانان در غار جای گرفتند و گفتند: پروردگارا، از جانب خود رحمتی به ما ببخش و راه درست را به ما بنمای و در کاری که در پیش گرفته‌ایم راه نجاتی برای ما فراهم کن.'),
(2151, 18, 11, 'پس چند سالی در آن غار به آرامی بر گوش هایشان نواختیم و آنان را به خواب فرو بردیم.'),
(2152, 18, 12, 'سپس از خواب بیدارشان کردیم تا مشخص کنیم کدام یک از آن دو گروه مدت درنگشان را برشمرده‌اند.'),
(2153, 18, 13, 'ما داستان آنها را به دور از هر باطلی بر تو حکایت می‌کنیم. آنها جوانانی بودند که به پروردگارشان ایمان آوردند و ما آنان را هدایت، افزودیم.'),
(2154, 18, 14, 'و دل هایشان را استوار کردیم آن گاه که برخاستند و به سردمداران مشرک خود گفتند: پروردگار ما همان پروردگار آسمان‌ها و زمین است و ما هرگز به جای او معبودی را نمی‌پرستیم، زیرا اگر چنین کنیم، قطعاً سخنی گزاف و ناروا گفته‌ایم.'),
(2155, 18, 15, 'اینها قوم ما هستند که به جای او معبودانی را برگزیده و به پرستش آنها پرداخته‌اند. چرا بر شایستگی آنها برای پرستش دلیلی آشکار نمی‌آورند؟ این پرستش بی دلیل، افترا بر خداست، و کیست ستمکارتر از کسی که بر خدا دروغ بسته است.'),
(2156, 18, 16, 'سپس از مشرکان کناره گرفتند و به یکدیگر گفتند: اکنون که از آنان و از آنچه به جای خدا می‌پرستند کناره گرفتید و روی به خدای یکتا آوردید، در آن غار جای گیرید که پروردگارتان رحمتی از رحمت خود را بر شما می‌گستراند و برای شما در کارتان گشایشی فراهم می‌آورد.'),
(2157, 18, 17, 'و اگر خود را میان آنان فرض کنی، می‌بینی که چون خورشید برمی آید، از غارشان به سمت راست می‌گراید و بر آن جانب پرتو می‌افکند. و چون غروب می‌کند، از آنان می‌گذرد در حالی که نورش بر سمت چپ غار می‌تابد، این در حالی است که آنان در جایی فراخ از آن که آفتاب بر آن نمی‌تابد قرار گرفته‌اند. این از آیات خداست. هر که را خدا هدایت کند او رهیافته است، و هر که را به گمراهی بیفکند، هرگز برای او کارگزاری هدایتگر نخواهی یافت.'),
(2158, 18, 18, 'و دیدگانشان را گشوده می‌بینی و آنان را بیدار می‌انگاری، در حالی که خفته‌اند، و ما آنها را از پهلوی چپ به راست و از پهلوی راست به چپ می‌گردانیم تا لباس و اندامشان در اثر تماس با زمین فرسوده نگردد، و سگشان بر آستانه غار دو ساعدش را گسترده است. اگر بر آنها سر می‌کشیدی، با حالت گریز از آنان روی می‌گرداندی و از مشاهده آنها آکنده از ترس می‌شدی.'),
(2159, 18, 19, 'و همان گونه که خواب را بر آنان مسلط کردیم، بیدارشان نمودیم تا در میان خود از یکدیگر پرسش کنند. گوینده‌ای از آنان گفت: چقدر در خواب مانده‌اید؟ چند تن از آنان گفتند: یک روز یا پاره‌ای از روز را مانده‌ایم، و برخی دیگر گفتند: پروردگارتان به مقدار ماندنتان داناتر است، اینک یکی از خودتان را با این درهم هایتان به شهر بفرستید تا بنگرد که غذای کدامین فروشنده پاکیزه‌تر است، و از همان برای شما خوردنی بیاورد، و باید با مردم در داد و ستد و رفت و آمدش نرمی کند، مبادا کشمکشی روی دهد و کسی را از حال شما با خبر سازد.'),
(2160, 18, 20, 'قطعاً اگر آنان بر شما دست یابند سنگسارتان می‌کنند یا شما را به آیین خود باز می‌گردانند و در آن صورت هرگز سعادتمند نخواهید شد.'),
(2161, 18, 21, 'و بدینسان مردم را از حالشان باخبر ساختیم تا همگان (مؤمنان و کافران) بدانند که وعده خدا حق است و در رستاخیز هیچ تردیدی نیست. آری آن گاه اصحاب کهف را از خوابشان بیدار کردیم و مردم را از حالشان باخبر ساختیم که آنان درباره حقانیت معاد با یکدیگر کشمکش می‌کردند. ولی منکران معاد با این نشانه بزرگ به حقیقت راه نیافتند، از این رو گفتند: بنایی (دیواری) بر آنان بسازید و رهایشان کنید، پروردگارشان به حالشان آگاه‌تر است، امّا کسانی که در آنچه بدان باور داشتند (حقانیت معاد) پیروز شدند، گفتند: ما حتماً بر آنان مسجدی بنا می‌کنیم تا یادشان زنده بماند.'),
(2162, 18, 22, 'به زودی مردم در شمارِ اصحاب کهف اختلاف خواهند کرد، برخی خواهند گفت: آنان سه تن بودند و چهارمین آنها سگشان بود، و برخی دیگر خواهند گفت: آنان پنج تن بودند و ششمین آنها سگشان بود ـ آنان تیر در تاریکی می‌افکنند و از روی جهل سخن می‌گویند ـ و برخی نیز خواهند گفت: آنان هفت تن بودند و هشتمین آنها سگشان بود. بگو: پروردگارم به شمار آنها داناتر است، جز اندکی از مردم کسی شمار آنها را نمی‌داند. پس ای پیامبر، با کسانی که درباره آنها سخن می‌گویند جز به شیوه‌ای آشکار که آیات قرآن بر آن دلالت دارد جدال مکن و از هیچ یک از آنان درباره اصحاب کهف نظرخواهی مکن.'),
(2163, 18, 23, 'و هرگز در مورد کاری که آهنگ آن را داری مگو: قطعاً من فردا آن را انجام می‌دهم.'),
(2164, 18, 24, 'مگر این که به همراه آن بگویی: اگر خدا بخواهد. و چنانچه آن را فراموش کردی، پروردگارت را با این وصف که همه امور به دست اوست و جز به اراده او کاری انجام نمی‌گیرد یاد کن و بگو: امید است که پروردگارم مرا به چیزی که از یاد او پس از فراموشی، به هدایت نزدیک‌تر است (به ذکر همیشگی خود) هدایت کند.'),
(2165, 18, 25, 'و اصحاب کهف سیصد سال در غارشان به خواب ماندند و نُه سال نیز بر آن افزودند.'),
(2166, 18, 26, 'بگو: خداست که به مدت درنگشان در غار از همه داناتر است، زیرا غیب آسمان‌ها و زمین تنها از آنِ اوست. چه بینا و شنواست! برای آنان کارگزاری جز او نیست و هیچ کس را در فرمانروایی خود شریک نمی‌گیرد.'),
(2167, 18, 27, 'آنچه از لوح محفوظ که نگارش یافته پروردگار توست بر تو وحی می‌شود تلاوت کن. سخنان او را دگرگون کننده‌ای نیست، و جز او هرگز پناهگاهی برای خود نخواهی یافت.'),
(2168, 18, 28, 'ای پیامبر، به کافران مرفّه که در بند ایمان نیستند دل مبند، بلکه خود را پای بند و همراه کسانی که هر صبح و شام پروردگارشان را می‌خوانند و خشنودی او را می‌طلبند قرار ده و دیدگانت از آنان نگردد و به کفرپیشگان مرفّه نگراید که آرایش زندگی این دنیا را بخواهی، و از آن کس که ما دلش را از یاد خود غافل ساخته‌ایم و از پی هوای خویش رفته و کارش نادیده گرفتن حق بوده است پیروی مکن.'),
(2169, 18, 29, 'و به آنان بگو: حق از جانب پروردگار شماست. پس هر که بخواهد ایمان بیاورد و هر که بخواهد کافر شود، ولی بدانند که ما برای ستمکاران آتشی آماده کرده‌ایم که سراپرده اش آنان را احاطه کرده است، و اگر یاری بطلبند، با آبی مانند مس ذوب شده که چهره‌ها را می‌گدازد به فریادشان می‌رسند. بد نوشیدنی ای است آن آب و بد تکیه گاهی است آن آتش.'),
(2170, 18, 30, 'به یقین، کسانی که ایمان آورده و کارهای شایسته کرده‌اند، پاداششان را به طور کامل می‌دهیم، زیرا ما پاداش کسی را که کار نیکو کند تباه نمی‌کنیم.'),
(2171, 18, 31, 'اینانند که برایشان بوستان‌هایی دایمی است، از زیر قصرهایشان نهرها روان است. در آن جا با دستواره‌هایی زرّین آراسته می‌گردند و جامه‌هایی سبز از حریر نازک و دیبای ستبر بر تن می‌کنند، در آن جا بر تخت‌ها تکیه می‌زنند. نیکو پاداشی است آن بوستان‌ها و نیکو تکیه گاهی است آن تخت‌ها،'),
(2172, 18, 32, 'برای آنان که به دنیا دل بسته و از یاد خدا غافلند حکایتی را بیان کن، حکایت دو مردی که به یکی از آنها دو باغ انگور دادیم و گرداگرد آن دو باغ را با درختان خرما پوشاندیم و میان آن دو کشتزاری قرار دادیم.'),
(2173, 18, 33, 'درختان هر دو باغ میوه خود را می‌دادند و چیزی از آن نمی‌کاستند، و میان آن دو باغ نهری جوشان روان ساخته بودیم.'),
(2174, 18, 34, 'علاوه بر اینها او ثروتی فراوان داشت، پس به رفیقش در حالی که با او گفتوگو می‌کرد، گفت: دارایی من از تو بیشتر است و نفراتم (فرزندان و خدمتکارانم) از تو نیرومندترند.'),
(2175, 18, 35, 'آن مرد در حالی که بر اثر فراموشی خدا و غرور و تکبر بر خود ستم کرده بود وارد باغ خود شد و به رفیقش گفت: گمان نمی‌کنم این باغ هرگز تباه شود، زیرا اسباب خرمی آن همچنان فراهم است.'),
(2176, 18, 36, 'و گمان نمی‌کنم که رستاخیز برپا شود، و اگر هم رستاخیزی در کار باشد و من به سوی پروردگارم بازگردانده شوم قطعاً بازگشتی بهتر از این خواهم یافت، چرا که شایستگی من در دنیا نشانگر لیاقت من در آخرت است.'),
(2177, 18, 37, 'رفیقش ـ در حالی که با او گفتوگو می‌کرد ـ در پاسخ وی گفت: آیا به آن کسی که تو را از خاکی و سپس از نطفه‌ای آفرید و آن گاه تو را به صورت مردی اعتدال بخشید، کافر شده‌ای که اراده او را در آفرینش بی اثر می‌دانی؟ (۳۷'),
(2178, 18, 38, 'امّا من، حقیقت این است که خدا پروردگار من است و هیچ کس را با پروردگارم در تدبیر امور هستی شریک نمی‌سازم.'),
(2179, 18, 39, 'و چرا وقتی وارد باغ خود شدی نگفتی: امر آن است که خدا خواسته است و هیچ قدرتی جز در پرتو قدرت خدا نیست؟ و چون فرجام کارها در اختیار اوست، اگر می‌بینی مال و فرزندان من از تو کمتر است،'),
(2180, 18, 40, 'ممکن است پروردگارم بهتر از باغ تو به من عطا کند، و بر باغ تو شراره‌هایی از آسمان فرو فرستد، در نتیجه آن به صورت زمینی صاف و بی گیاه درآید.'),
(2181, 18, 41, 'یا آب آن به زمین فرو رود و هرگز نتوانی آن را به دست آوری.'),
(2182, 18, 42, 'و سرانجام، محصولش را تباهی فرا گرفت و او در حالی که باغش بر داربست‌های آن فرو ریخته بود برای آنچه در آن هزینه کرده بود، دست هایش را از پشیمانی به هم می‌مالید و می‌گفت: ای کاش کسی را با پروردگارم شریک نمی‌ساختم و به غیر او اعتماد نکرده بودم.'),
(2183, 18, 43, 'او که به خدمتکاران و فرزندان خود می‌بالید، در آن هنگام هیچ گروهی را جز خدا نداشت که به او یاری رسانند و خود نیز توانِ آن را نداشت که عذاب را از خویشتن بگرداند.'),
(2184, 18, 44, 'آری، آن گاه که علل و اسباب بی اثر شود، آشکار می‌گردد که فرمانروایی فقط در اختیار خداست، زیرا او حق است و بی عنایت او هیچ چیزی کارساز نیست. پاداش او از همه بهتر و به فرجام نیز از همه نیکوتر است.'),
(2185, 18, 45, 'برای آن رفاه طلبانی که به تجمّلات دنیا روی آورده و خدا را از یاد برده‌اند زندگی دنیا را مَثَل بزن که مانند آبی است که آن را از آسمان فرو فرستادیم، پس گیاه زمین با آن درآمیخت و سبز و خرّم شد، آن گاه پس از چندی خشک و خرد گشت به گونه‌ای که بادها آن را از سویی به سوی دیگر پراکنده می‌سازند، و خدا بر هر چیزی تواناست.'),
(2186, 18, 46, 'مال و پسران زیور زندگی دنیا و زودگذرند، ولی کارهای شایسته که ماندگار است نزد پروردگارت پاداشی بهتر دارد و برای دستیابی به رحمت او امیدبخش‌تر است.'),
(2187, 18, 47, 'یاد کن روزی را که کوه‌ها را از جای برکنده و روان می‌سازیم در نتیجه زمین را صاف و آشکار می‌بینی، و مشرکان را محشور کرده و هیچ یک از آنان را فرو نگذاشته‌ایم.'),
(2188, 18, 48, 'و در یک صف بر پروردگارت عرضه شده‌اند و به آنان گفته‌ایم: همان گونه که نخستین بار شما را آفریدیم و چیزی از زیورهای دنیا را با خود نداشتید اینک نزد ما آمده‌اید. تنها زیورهای دنیا بازدارنده شما از کارهای نیک نبود، بلکه می‌پنداشتید که هرگز برای شما وعده گاهی مقرّر نخواهیم کرد و قیامتی در کار نخواهد بود.'),
(2189, 18, 49, 'و نامه اعمال برای داوری در میان نهاده شده است، آن گاه مشرکان و گنهکاران را می‌بینی که از آنچه در آن است بیمناکند و می‌گویند: ای وای بر ما، این چه نامه عملی است که هیچ خرد و کلانی را فروگذار نکرده مگر این که آن را به شمار آورده است! و آنچه انجام داده‌اند حاضر یافته‌اند، و پروردگارت به هیچ کس ستم نمی‌کند.'),
(2190, 18, 50, 'و یاد کن زمانی را که به فرشتگان گفتیم: برای آدم سجده کنید. پس همه سجده کردند، جز ابلیس که از جنّیان بود و از فرمان پروردگارش سرپیچی کرد. با این وصف آیا او و نسلش را به جای من کارساز خود گرفته‌اید، در صورتی که آنها دشمن شمایند؟ بد جایگزینی است که ستمکاران به جای خدا دارند.'),
(2191, 18, 51, 'من ابلیس و نسل او را نه گواه بر آفرینش آسمان‌ها و زمین گرفتم و نه گواه بر آفرینش خودشان، پس چگونه می‌توانند کار جهان هستی را تدبیر کنند یا امور خود را سامان دهند؟ علاوه بر این، آنها عناصری گمراه گرند و من هرگز گمراه کنندگان را مددکار خود نگرفته‌ام.'),
(2192, 18, 52, 'و یاد کن روزی را که خدا به مشرکان می‌گوید: کسانی را که شریکان من می‌پنداشتید ندا دهید. پس آنها را می‌خوانند ولی آنان اجابتشان نمی‌کنند، و ما میان آنها فاصله‌ای قرار داده‌ایم که روابط آنها را با یکدیگر قطع کند.'),
(2193, 18, 53, 'و گنهکاران آتش دوزخ را دیده و دریافته‌اند که در آن فرو می‌افتند و آتش آنان را فرا می‌گیرد و گریزگاهی از آن برای خود نخواهند یافت.'),
(2194, 18, 54, 'و به راستی در این قرآن هرگونه مَثَلی که حق را برای مردم آشکار کند و آنان را به ایمان سوق دهد با بیان‌های گوناگون آورده‌ایم، ولی انسان از هر چیزی جدال پیشه‌تر است.'),
(2195, 18, 55, 'این مردم، گویی با کفر ورزیدن خود عذاب الهی را می‌طلبند، زیرا هنگامی که هدایت برایشان آمد، چیزی آنان را از ایمان آوردن و طلب آمرزش از پروردگارشان باز نداشت جز این که درخورِ آن گردند که سنّتی که درباره گذشتگان اجرا شد و آنان را نابود ساخت درباره اینان نیز اجرا شود، یا عذاب رویارویشان بیاید و با مشاهده آن به ایمان ناگزیر شوند.'),
(2196, 18, 56, 'ای پیامبر، مبادا کفر کافران تو را اندوهگین کند، زیرا ما پیامبران را جز بشارت دهنده و اخطارکننده نفرستاده‌ایم، و کسانی که کافر شده‌اند به وسیله باطل به جدال می‌پردازند تا حق را بدان پایمال کنند، و آنان آیات من و آنچه را که بدان بیم داده شده‌اند به مسخره گرفته‌اند.'),
(2197, 18, 57, 'و کیست ستمکارتر از آن کس که آیات پروردگارش به او یادآوری شده و از آنها روی برتافته و این دستاورد را که پیش فرستاده فراموش کرده است؟ ما بر دل‌های آنان چندین پوشش گذاشته‌ایم تا حقیقت را به اندیشه خود درنیابند، و در گوش هایشان سنگینی نهاده‌ایم تا سخن حق را چنان که باید نشنوند. از این رو، اگر آنان را به هدایت فراخوانی، هرگز هدایت نخواهند شد.'),
(2198, 18, 58, 'و پروردگار تو بسیار آمرزنده و صاحب رحمت است، لذا در عذابشان شتاب نمی‌کند. اگر به سزای آنچه فراهم آورده‌اند، آنان را مؤاخذه می‌کرد، قطعاً عذاب را هر چه زودتر بر سرشان فرود می‌آورد. آنان تصور نکنند که برای همیشه رهایی یافته‌اند، بلکه برای عذابشان موعدی است که در برابر آن هرگز پناهگاهی نخواهند یافت.'),
(2199, 18, 59, 'و این شهرهاست که چون مردمش ستم کردند، آنها را هلاک کردیم، ولی در هلاکتشان شتاب نکردیم بلکه برای آن موعدی مقرّر داشتیم.'),
(2200, 18, 60, 'و یاد کن زمانی را که موسی به جوانی که پیوسته همراهش بود گفت: همچنان خواهم رفت تا به محل برخورد آن دو دریا برسم یا زمانی دراز راه پویم.'),
(2201, 18, 61, 'پس به راه خود ادامه دادند تا وقتی که به محل برخورد آن دو دریا رسیدند و بر صخره‌ای آرمیدند، از ماهی خود که آن را در کناری نهاده بودند غافل شدند و ماهی راه خود را در دریا که همچون جاده‌ای بود در پیش گرفت و رفت.'),
(2202, 18, 62, 'و هنگامی که از آن جا گذشتند، موسی به جوان همراهش گفت: غذایمان را به ما بده که ما از این سفرمان رنج و خستگی دیدیم.'),
(2203, 18, 63, 'جوان گفت: دیدی [چه شد]؟ وقتی کنار آن صخره جای گرفتیم، من فراموش کردم جریان ماهی را برای تو بازگویم، و کسی جز شیطان یاد آن را از خاطرم نبرد، و ماهی به گونه‌ای شگفت انگیز راه خود را در دریا پیش گرفت و رفت.'),
(2204, 18, 64, 'موسی گفت: این رویداد همان چیزی است که ما در پی آن بودیم و این نشانه میعاد ماست، پس بی درنگ ردّ پای خود را دنبال کردند و بازگشتند.'),
(2205, 18, 65, 'چون بدان جا رسیدند، بنده‌ای از بندگان ما (خِضْر) را یافتند که از جانب خود رحمتی به او عطا کرده و از نزد خود دانشی به او آموخته بودیم.'),
(2206, 18, 66, 'موسی به او گفت: آیا اجازه می‌دهی به دنبال تو بیایم، با این قرار که بخشی از آنچه را که به تو آموخته‌اند به من بیاموزی تا به وسیله آن به راه درست رهنمون شوم؟'),
(2207, 18, 67, 'آن عالم (خضر) گفت: تو هرگز نمی‌توانی به همراه من باشی و بر روش آموزش من شکیبایی کنی.'),
(2208, 18, 68, 'و چگونه بر چیزی که دانشت بدان احاطه ندارد شکیبایی می‌کنی؟'),
(2209, 18, 69, 'موسی گفت: اگر خدا بخواهد مرا شکیبا خواهی یافت و در هیچ کاری تو را نافرمانی نخواهم کرد.'),
(2210, 18, 70, 'خضر گفت: اگر دنبال من آمدی، از من درباره کاری که انجام می‌دهم چیزی مپرس تا خود از آن برای تو سخنی آغاز کنم.'),
(2211, 18, 71, 'پس هر دو به راه افتادند، تا وقتی که سوار کشتی شدند، خضر آن را سوراخ کرد. موسی گفت: آیا آن را سوراخ کردی تا سرنشینانش را غرق کنی؟ به راستی کاری بس ناگوار کردی.'),
(2212, 18, 72, 'خضر گفت: آیا به تو نگفتم که هرگز نمی‌توانی به همراه من باشی و بر روش آموزش من شکیبایی ورزی؟'),
(2213, 18, 73, 'موسی گفت: مرا به فراموشی و غفلتم بازخواست مکن و به سبب کارم مرا در تنگنا قرار مده.'),
(2214, 18, 74, 'سپس از کشتی پیاده شدند و به راه افتادند، تا وقتی که به پسری نوجوان رسیدند و خضر او را کشت، موسی گفت: آیا شخص بی گناهی را بدون آن که کسی را کشته باشد کشتی؟ به راستی کاری ناپسند مرتکب شدی.'),
(2215, 18, 75, 'خضر گفت: آیا به تو نگفتم که هرگز نمی‌توانی به همراه من باشی و بر روش آموزش من شکیبایی کنی؟'),
(2216, 18, 76, 'موسی گفت: اگر پس از این درباره چیزی از تو پرسیدم با من همراهی مکن که قطعاً از جانب من به عذری رسیده‌ای.'),
(2217, 18, 77, 'پس به راه افتادند تا وقتی که به مردم دیاری رسیدند و از اهل آن جا غذا خواستند و آنان از مهمان نمودن و غذا دادن به آن دو خودداری کردند و در آن جا دیواری را یافتند که می‌خواست فرو ریزد و خضر آن را استوار ساخت، موسی گفت: اگر می‌خواستی بر آن دستمزدی می‌گرفتی تا به وسیله آن غذایی تهیه کنیم.'),
(2218, 18, 78, 'خضر گفت: این سخن تو باعث جدایی میان من و توست. به زودی دلیل آنچه را که نتوانستی بر آن شکیبایی کنی به تو خبر می‌دهم.'),
(2219, 18, 79, 'امّا آن کشتی، از آنِ بینوایانی بود که در دریا کار می‌کردند و معاش خود را از این طریق تأمین می‌نمودند. من خواستم آن را معیوب کنم، زیرا در کمین آنان پادشاهی بود که هر کشتی سالمی را به زور می‌گرفت.'),
(2220, 18, 80, 'امّا آن نوجوان، پدر و مادرش مؤمن بودند و ما ترسیدیم او با کفر و سرکشی خود، آن دو را در تنگنا قرار دهد و بیازارد.'),
(2221, 18, 81, 'پس خواستیم که پروردگارشان برای آنان فرزندی را که در ایمان و نیکوکاری از او بهتر و به رعایت حق خویشاوندی نزدیک‌تر باشد، جایگزین او کند.'),
(2222, 18, 82, 'و امّا آن دیوار، از آنِ دو پسر یتیم در شهر بود، و زیر آن گنجی وجود داشت که مال آن دو بود، و پدرشان مردی نیکوکار بود، پس پروردگارت خواست که آن دو به توانایی خویش رسند و گنجِ خود را از آن جا بیرون آورند. این به سبب رحمتی بود که از جانب پروردگارت شامل حالشان شده بود. من آنچه انجام دادم از پیش خود نکردم، بلکه فرمان خدا را به جای آوردم. این بود دلیل و خاستگاه آنچه که نتوانستی بر آن شکیبایی کنی.'),
(2223, 18, 83, 'و از تو ای پیامبر درباره ذوالقرنین می‌پرسند. بگو: به زودی درباره او از قرآن برای شما خواهم خواند.'),
(2224, 18, 84, 'ما به او برای حکومت در زمین توانایی بخشیدیم و از هر چیزی که او را به اهدافش برساند وسیله‌ای به او عطا کردیم.'),
(2225, 18, 85, 'پس او وسیله‌ای فراهم کرد.'),
(2226, 18, 86, 'و سپس به راه افتاد، تا وقتی که به غروبگاه خورشید، آخرین معموره‌های مغرب زمین رسید، از ساحل دریا چنین یافت که گویی خورشید در دریایی سیاه و گِل آلود فرو می‌رود، و در کنار دریا قومی را یافت و آنان را تحت سلطه خود درآورد. گفتیم: ای ذوالقرنین، با آنان چه خواهی کرد؟ آیا آنان را کیفر می‌دهی یا در میانشان به نیکی رفتار می‌کنی؟'),
(2227, 18, 87, 'گفت: امّا آن کس که ستم کرده است، به زودی او را کیفر می‌کنیم، سپس به سوی پروردگارش بازگردانده می‌شود و خداوند او را به عذابی که تصوّر آن را نمی‌کرده است معذَّب می‌دارد.'),
(2228, 18, 88, 'و امّا آن کس که ایمان آورده و کاری شایسته کرده است نیکوترین پاداش را خواهد داشت و به زودی او را به تکالیف سهل و ساده‌ای مکلّف خواهیم ساخت.'),
(2229, 18, 89, 'سپس وسیله دیگری برای حرکت به سوی مشرق فراهم نمود.'),
(2230, 18, 90, 'و به راه افتاد، تا وقتی که به جایگاه برآمدنِ خورشید رسید، خورشید را یافت که بر مردمی طلوع می‌کند که برای آنان در برابر تابش آن پوششی قرار نداده بودیم.'),
(2231, 18, 91, 'ذوالقرنین این گونه عمل می‌کرد، در حالی که ما به آنچه در اختیار داشت و به آنچه بر او می‌گذشت به خوبی آگاه بودیم و با هدایت ما اهداف خود را پیش می‌برد.'),
(2232, 18, 92, 'آن گاه وسیله دیگری برای حرکت به سوی شمال فراهم ساخت.'),
(2233, 18, 93, 'و به راه افتاد، تا وقتی که به درّه‌ای میان آن دو کوه (از کوه‌های قفقاز) رسید، در مجاورت آن دو کوه، قومی عقب مانده را یافت که به آسانی مفهوم هیچ سخنی را درک نمی‌کردند.'),
(2234, 18, 94, 'گفتند: ای ذوالقرنین، قبیله (یأجوج) و (مأجوج) فسادگرند. آنان به این سرزمین هجوم می‌آورند و به قتل و غارت ما می‌پردازند. آیا برای تو از اموال خود مزدی مقرّر کنیم که میان ما و آنان سدّی بنا کنی؟'),
(2235, 18, 95, 'ذوالقرنین گفت: من به اموال شما نیازی ندارم. آنچه پروردگارم به من در آن توانایی بخشیده بهتر از دستمزدی است که شما به من می‌پردازید. اگر خواهانِ بنای سدّ هستید، مرا با نیروی انسانی و تهیه مصالح سدّ یاری دهید که من میان شما و آنان سدّی استوار بنا خواهم کرد.'),
(2236, 18, 96, 'سپس گفت: پاره‌های آهن به من بدهید. پس از آن که پاره‌های آهن را آوردند، آنها را روی هم چید، و وقتی میان آن دو کوه را با پاره‌های آهن و مواد لازم پر کرد و آن دو را برابر ساخت، گفت: در دم‌های آهنگری بدمید. هنگامی که آنها را بسان آتش گداخته کرد گفت: مسِ ذوب شده به من دهید تا بر شکاف‌های آن فروریزم.'),
(2237, 18, 97, 'بدین ترتیب سدّی بلند و استوار ساخت. از آن پس قبیله یأجوج و مأجوج نه توانستند از آن بالا روند و نه توانستند در آن رخنه‌ای پدید آورند.'),
(2238, 18, 98, 'ذو القرنین گفت: این سد رحمتی از جانب پروردگار من است که بدین وسیله امّت‌هایی از گزند فسادگران مصون می‌مانند، و این سدّ تا فرا رسیدن وعده الهی پایدار است، و هنگامی که وعده پروردگارم فرا رسد آن را با خاک یکسان می‌کند، و وعده پروردگارم راست و درست است.'),
(2239, 18, 99, 'آن روز که چنین شود، مردم را به حال خود وا می‌نهیم تا چون دریایی طوفان زده آشفته گردند و مانند امواج به هم درآمیزند، و در صور دمیده می‌شود تا مردگان زنده شوند، آن گاه همه آنان را گرد می‌آوریم.'),
(2240, 18, 100, 'و آن روز دوزخ را برای کافران چنان که باید آشکار می‌سازیم.'),
(2241, 18, 101, 'همانان که چشمانشان از دیدن نشانه‌های من که باعث یادکرد من است در پرده بود و توانایی شنیدن حکمت‌ها و اندرزها را نداشتند.'),
(2242, 18, 102, 'آیا کسانی که یکتایی خدا را منکر شده‌اند پنداشته‌اند که بندگان مرا به جای من کارسازانی می‌گیرند که آنان را یاری دهند و کیفر مرا از آنان بازدارند؟ چنین نیست، قطعاً ما دوزخ را برای پیش پذیرایی از کافران آماده کرده‌ایم.'),
(2243, 18, 103, 'ای پیامبر، به مشرکان بگو: آیا شما را از کسانی که در همه کارهایشان زیانکارترین مردمند آگاه کنیم؟'),
(2244, 18, 104, 'آنان کسانی اند که تلاششان در زندگی دنیا به بیراهه می‌رود و خود می‌پندارند که کار نیک انجام می‌دهند.'),
(2245, 18, 105, 'آنان کسانی اند که نشانه‌های پروردگارشان و لقای او را انکار کرده‌اند، در نتیجه اعمالشان تباه شده است، از این رو ما در روز قیامت میزانی برای آنان برپا نمی‌کنیم و بی درنگ راهیِ دوزخ خواهند شد.'),
(2246, 18, 106, 'این است حقیقت امرشان. سزایشان از آن روی که کافر شدند و نشانه‌ها و پیامبران مرا به مسخره گرفتند، دوزخ است.'),
(2247, 18, 107, 'کسانی که ایمان آورده و کارهای شایسته کرده‌اند، باغ‌های فردوس پیش پذیرایی آنان است.'),
(2248, 18, 108, 'در آن جاودانه‌اند و انتقال از آن جا را نمی‌خواهند.'),
(2249, 18, 109, 'بگو: اگر دریا برای نگارش کلمات (نشانه‌های روشن) پروردگارم مرکّب شود، پیش از آن که نگارش کلمات پروردگارم پایان پذیرد دریا پایان خواهد یافت، هر چند مانند آن دریا را نیز به مدد آوریم.'),
(2250, 18, 110, 'بگو: جز این نیست که من هم بشری مانند شما هستم، با این تفاوت که به من وحی می‌شود که معبود شما معبودی یگانه است. پس هر کس به لقای پروردگار خود امید دارد باید کاری شایسته کند و هیچ کس را در پرستش پروردگارش شریک نسازد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کاف.‌ها. یا. عین. صاد.'),
(2252, 19, 2, 'این گزارشی است از رحمت بخشی پروردگارت بر بنده اش زکریّا،'),
(2253, 19, 3, 'آن گاه که در خلوتی نهان از مردم پروردگارش را ندا داد.'),
(2254, 19, 4, 'گفت: پروردگارا، من استخوانم سست گردیده و قدرتم کم شده و پیری در سرم شعله گونه برافروخته و فراگیر شده است و تاکنون ـ پروردگارا ـ هرگاه تو را خوانده‌ام از اجابت دعا محروم نبوده‌ام.'),
(2255, 19, 5, 'و من از رفتار بستگانم که پس از من وارثانم خواهند بود بیمناکم و همسرم نازا بوده و اکنون هم سالخورده است، پس از جانب خود وارثی به من عطا کن،'),
(2256, 19, 6, 'فرزندی که از من و از خاندان یعقوب ارث برَد، و او را ـ ای پروردگار من ـ در عقیده و عمل پسندیده گردان.'),
(2257, 19, 7, 'ما دعای او را اجابت کردیم و به وسیله فرشتگان به او ابلاغ نمودیم که: ای زکریّا، ما تو را به پسری که نامش یحیی است و پیش‌تر همنامی برای او قرار نداده‌ایم، مژده می‌دهیم.'),
(2258, 19, 8, 'زکریّا گفت: پروردگارا، چگونه مرا پسری باشد و حال آن که همسرم نازا بوده و اکنون هم سالخورده است و خود نیز از کهنسالی به ناتوانی رسیده‌ام.'),
(2259, 19, 9, 'خدا فرمود: حقیقت چنان است که به تو نوید دادم. پروردگار تو گفته که این کار برای من آسان است و من تو را پیش‌تر در حالی که چیزی نبودی آفریدم.'),
(2260, 19, 10, 'زکریّا گفت: پروردگارا، نشانه‌ای برای من قرار ده تا بدانم که آنچه شنیده‌ام از جانب توست. خدا فرمود: نشانه تو این است که سه شبانه روز، با این که سالمی، نمی‌توانی با مردم سخن بگویی.'),
(2261, 19, 11, 'پس از محراب خارج شد و به سوی قومش رفت و با اشاره به آنان فهماند که خدا را منزّه شمرید و بامدادان و عصرگاهان او را تسبیح گویید.'),
(2262, 19, 12, 'پس از چندی یحیی را به او عطا کردیم; آن گاه به یحیی گفتیم: ای یحیی، این کتاب (تورات) را به جِدّ و جهد بگیر (آن را نیک بیاموز و به احکام آن عمل کن) و ما به او در کودکی دانش معارف الهی را ارزانی کردیم.'),
(2263, 19, 13, 'و نیز از جانب خود، عشق و شوری الهی به او عطا کردیم و در پرتو آن روحش را نیکو پرورش دادیم، و او تقواپیشه بود.'),
(2264, 19, 14, 'و در حق پدر و مادرش نیکوکار بود و در برابر مردم تکبّر نورزید و زورگویی نکرد و سرکشی از خود نشان نداد.'),
(2265, 19, 15, 'و از روزی که زاده شد، قرین سلامت و از آفت‌ها پیراسته بود، و مقرّر کردیم که به هنگام مرگش و در عالم برزخ نیز گزندی نبیند، و روزی که زنده برانگیخته می‌شود نیز حیاتی پاک داشته باشد و هیچ رنجی به او نرسد.'),
(2266, 19, 16, 'و در این کتاب (قرآن) سرگذشت مریم را یاد کن، آن گاه که برای عبادت از کسان خود کناره گرفت و به نقطه‌ای در شرق مسجد رفت.'),
(2267, 19, 17, 'و در برابر آنان پرده‌ای افکند. در این هنگام، ما روح خود، جبرئیل را به سوی او فرستادیم و جبرئیل در نظر او به صورت انسانی با اندام معتدل نمایان شد.'),
(2268, 19, 18, 'مریم دهشت زده گفت: من از تو به خداوند رحمان پناه می‌برم اگر پرهیزگار باشی.'),
(2269, 19, 19, 'جبرئیل گفت: من بشر نیستم، بلکه فرستاده پروردگار توام; آمده‌ام تا پسری به تو ببخشم که از رشدی شایسته برخوردار باشد.'),
(2270, 19, 20, 'مریم گفت: چگونه مرا پسری باشد در حالی که هیچ بشری با من تماس نداشته است و بدکاره نیز نبوده‌ام؟'),
(2271, 19, 21, 'جبرئیل گفت: واقعیّت همان است که به تو گفتم. پروردگارت فرموده است که این کار بر من آسان است. ما این گونه به تو فرزندی می‌دهیم تا آنچه باید انجام گیرد و تا او را برای مردم نشانه‌ای قرار دهیم و از جانب خود او را مایه رحمتی گردانیم، و آفرینش او بدین صورت کاری حتمی است.'),
(2272, 19, 22, 'پس مریم به عیسی باردار شد و با حمل خود از خانواده اش کناره گرفت و به مکانی دور رهسپار گشت.'),
(2273, 19, 23, 'تا این که درد زایمان او را به سوی تنه خشکیده درخت خرمایی که در آنجا بود کشانید. گفت: ای کاش، پیش از این مرده بودم و به قدری ناچیز بودم که مرا به فراموشی می‌سپردند.'),
(2274, 19, 24, 'پس عیسی مادرش را از زیر پای او ندا داد که: اندوهگین مباش; همانا پروردگار تو زیر پایت جوی آبی پدید آورده است.'),
(2275, 19, 25, 'ساقه درخت خرما را به طرف خود تکان ده که خرمایی تازه بر تو فرو می‌ریزاند.'),
(2276, 19, 26, 'پس، از آن خرمای تازه بخور و از این جوی آب بنوش و بر موهبتی که خدا به تو داده است شادمان باش، و اگر از آدمیان کسی را دیدی که بر تو ایراد می‌گیرد، من پاسخ او را خواهم داد، تو قصد روزه کن و بگو: من برای خدای رحمان روزه سکوت نذر کرده‌ام، از این رو امروز با هیچ انسانی سخن نمی‌گویم.'),
(2277, 19, 27, 'آن گاه مریم کودک خود را در حالی که او را حمل می‌کرد نزد قومش آورْد. قوم او گفتند: ای مریم، به راستی گناهی بزرگ که از تو بعید می‌نمود، انجام داده‌ای.'),
(2278, 19, 28, 'ای خواهر هارون، پدرت مرد بدی نبود و مادرت نیز بدکاره نبود.'),
(2279, 19, 29, 'مریم به کودکش اشاره کرد که از او سؤال کنید. آنان گفتند: چگونه با کسی که کودکی در گهواره است سخن بگوییم؟'),
(2280, 19, 30, 'عیسی گفت: من بنده خدایم. خدا به من کتاب آسمانی داده و مرا پیامبر گردانیده است.'),
(2281, 19, 31, 'و مرا هر جا که باشم مایه برکت قرار داده، و تا زنده‌ام مرا به نماز و زکات سفارش کرده است.'),
(2282, 19, 32, 'و مقرّر داشته است که به مادرم نیکوکار باشم و مرا زورگو و پندناپذیر قرار نداده است.'),
(2283, 19, 33, 'و از روزی که زاده شدم، قرین سلامت و از آفت‌ها پیراسته بودم، و به هنگام مرگم و در عالم برزخ نیز گزندی نخواهم دید، و روزی که زنده برانگیخته می‌شوم نیز رنجی به من نخواهد رسید.'),
(2284, 19, 34, '[ای پیامبر،] این است عیسی پسر مریم و این هم ماجرای اوست. من سخن به راستی و درستی می‌گویم. این است حقیقت آن کسی که درباره وی تردید می‌کنند.'),
(2285, 19, 35, 'عیسی فرزند خدا نیست، چرا که خدا را نسزد که فرزندی برگیرد. او از این که نیازمند فرزند باشد منزّه است. چگونه به فرزند نیاز داشته باشد و حال آن که چون کاری را مقرّر کند، جز این نیست که به آن می‌گوید: باش، و آن پدید می‌آید.'),
(2286, 19, 36, 'آن گاه عیسی در ادامه سخنانش گفت: به یقین، خدا پروردگار من و پروردگار شماست، پس او را بپرستید. این است راه راست و درست.'),
(2287, 19, 37, 'پس کسانی که عیسی به سوی آنان فرستاده شد، به چند گروه تقسیم شدند و درباره او اختلاف کردند; گروهی به او کفر ورزیدند و گروهی او را خدا خواندند ـ این دو گروه همه کافر بودند ـ و گروهی راه اعتدال را پیمودند. پس وای بر کافران از دیدار روزی بزرگ!'),
(2288, 19, 38, 'روزی که آنان نزد ما می‌آیند چقدر شنوا و بینایند، ولی آن روز ستمکاران در گمراهی آشکاری قرار دارند و ظهور حقیقت برای آنان هدایتی را در پی ندارد و آنان را به سعادت نمی‌رساند.'),
(2289, 19, 39, 'و کافران را از روز حسرت بیم ده از آن گاه که کار به انجام می‌رسد و به سوی ما باز می‌آیند. آنان از آن روی که اکنون در غفلت اند و به یکتایی خدا ایمان نمی‌آورند، در آن روز سعادت خود را از کف داده‌اند و حسرت زده خواهند بود.'),
(2290, 19, 40, 'به یقین، ما زمین و هر که را بر روی آن است به میراث می‌بریم و همه به سوی ما باز گردانده می‌شوند.'),
(2291, 19, 41, 'و در این کتاب (قرآن) سرگذشت ابراهیم را یاد کن، که او بسیار راستگو و پیامبر بود.'),
(2292, 19, 42, 'یاد کن هنگامی را که به پدرش گفت: ای پدر، مگر نه این است که معبود باید از عبادت پرستشگر خود آگاه باشد و امور او را تدبیر کند، پس چرا چیزهایی را می‌پرستی که نه می‌شنوند و نه می‌بینند و نه چیزی را از تو کفایت می‌کنند؟'),
(2293, 19, 43, 'ای پدر، حقیقت این است که دانشی برای من فراهم آمده که برای تو فراهم نیامده است، پس مرا پیروی کن که من تو را به راهی راست هدایت می‌کنم.'),
(2294, 19, 44, 'ای پدر، از شیطان پیروی مکن که او تو را به پرستش غیر خدا می‌خواند، زیرا شیطان خدای رحمان را عصیانگر است و تو را جز به نافرمانی خدا فرا نمی‌خواند.'),
(2295, 19, 45, 'ای پدر، من می‌ترسم از جانب خدای رحمان عذاب خِذلان به سراغت آید و او تو را به حال خودت واگذارد و در نتیجه دوستدار شیطان گردی و شیطان کار تو را در اختیار گیرد.'),
(2296, 19, 46, 'گفت: ای ابراهیم، آیا تو از معبودان من رویگردانی؟ سوگند یاد می‌کنم که اگر از رویگردانی خود بازنایستی تو را سنگسار خواهم کرد، و اینک برای مدّتی طولانی مرا ترک کن و کناره گیری کن!'),
(2297, 19, 47, 'ابراهیم گفت: سلام بر تو باد، به زودی از پروردگارم برای تو آمرزش می‌طلبم، امید است تو را بیامرزد، زیرا او با من مهربان بوده دعاهای مرا مستجاب کرده است.'),
(2298, 19, 48, 'من از شما و از آنچه به جای خدا می‌خوانید کناره می‌گیرم و پروردگارم را می‌خوانم و امید آن دارم که در خواندن پروردگارم از اجابت او محروم نباشم.'),
(2299, 19, 49, 'پس هنگامی که از آنان و از آنچه به جای خدا می‌پرستیدند کناره گرفت، اسحاق و یعقوب را به او عطا کردیم و همه را پیامبر گردانیدیم.'),
(2300, 19, 50, 'و از رحمت خود به آنان بخشیدیم و برایشان در میان مردم ستایشی راستین و والا قرار دادیم.'),
(2301, 19, 51, 'و در این کتاب (قرآن) داستان موسی را یاد کن، که او انسانی بود خالص شده برای خدا به گونه‌ای که هیچ کس و هیچ چیز در او نصیبی نداشت. او فرستاده خدا، و پیامبر بود.'),
(2302, 19, 52, 'و از جانب راست کوه طور به او ندا دادیم، و در حالی که با وی به راز سخن گفتیم او را به خود نزدیک ساختیم.'),
(2303, 19, 53, 'و از رحمت خویش برادرش هارون را که به پیامبری برگزیدیم، به او عطا کردیم.'),
(2304, 19, 54, 'و در این کتاب (قرآن) اسماعیل را یاد کن، که او به وعده خود سخت وفادار و فرستاده خدا، و پیامبر بود.'),
(2305, 19, 55, 'و کسان خود را همواره به نماز و زکات فرمان می‌داد و نزد پروردگارش پسندیده بود.'),
(2306, 19, 56, 'و در این کتاب (قرآن) ادریس را یاد کن، که او بسیار راستگو و پیامبر بود.'),
(2307, 19, 57, 'و ما او را به مقامی بلند بالا بردیم.'),
(2308, 19, 58, 'این بلندمرتبگان که خدا آنان را از نعمت برخوردار ساخته بود ـ چه پیامبرانی که از نسل آدم و از نسل کسانی که آنان را با نوح بر کشتی نشاندیم و از نسل ابراهیم و اسرائیل بودند، و چه آنان که از پیامبران نبودند ولی آنان را هدایت کردیم و برگزیدیم ـ همه آنان هنگامی که آیات خدای رحمان بر آنان تلاوت می‌شد، سجده کنان و گریان بر زمین می‌افتادند.'),
(2309, 19, 59, 'آن گاه پس از آنان مردمی ناشایسته به جای ایشان آمدند که نماز را تباه ساختند واز خواهش‌های نفسانی پیروی کردند، و به زودی گمراهی خود را خواهند دید.'),
(2310, 19, 60, 'مگر کسانی که توبه کردند و ایمان آوردند و کار شایسته انجام دادند، که آنان به بهشت درمی آیند و هیچ ستمی بر آنان نخواهد رفت.'),
(2311, 19, 61, 'آنان به بوستان‌هایی جاودان که خدای رحمان آنها را به بندگان خود وعده داده و از درک آنان نهان است درمی آیند، و به یقین وعده او انجام شدنی است.'),
(2312, 19, 62, 'در آن جا هیچ سخن بیهوده ای نمی‌شنوند; فقط سخنی که از سلامت و عافیت حکایت دارد به گوششان می‌رسد، و در آن جا هر بامداد و عصرگاه رزق خود را خواهند داشت.'),
(2313, 19, 63, 'این همان بهشتی است که آن را به هر کس از بندگانمان که تقواپیشه است به میراث می‌دهیم.'),
(2314, 19, 64, 'و ما فرشتگان برای رساندن وحی جز به فرمان پروردگار تو ـ ای پیامبر ـ فرود نمی‌آییم، زیرا اعمال و آثار ما که پیش روی ماست، و اسباب و عللی که ما براساس آنها وجود یافته‌ایم، و وجود ما که میان این دو مرحله است همه از آنِ اوست و او از هر جهت بر ما احاطه دارد، و پروردگارت هرگز فراموشکار نبوده است; پس اگر می‌بینی ما در آوردن وحی تأخیر کرده‌ایم این برخاسته از مشیت خداست نه از روی غفلت و فراموشی او.'),
(2315, 19, 65, 'چگونه ما در اختیار خدا نباشیم و چگونه او فراموشکار باشد، با این که اوست پروردگار آسمان‌ها و زمین و آنچه میان آن دو است. پس فقط او را بپرست و بر پرستش او شکیبا باش. آیا برای او همنامی که تدبیرکننده جهان هستی و درخور پرستش باشد می‌شناسی؟'),
(2316, 19, 66, 'و انسان، بی آن که از او انتظار رود، می‌گوید: آیا چون مُردم، حتماً زنده از گور برانگیخته می‌شوم؟'),
(2317, 19, 67, 'آیا انسان به یاد نمی‌آورَد که ما او را پیش‌تر آفریدیم در حالی که چیزی نبود؟ پس چگونه آفرینش دوباره خود را بعید می‌داند؟'),
(2318, 19, 68, 'پس به پروردگارت سوگند که ما پس از بیرون آوردن کافران از گورها آنان را با شیاطین گرد می‌آوریم، سپس در حالی که روی هم انباشته شده‌اند، آنان را برای عذاب بر گرد دوزخ احضار می‌کنیم.'),
(2319, 19, 69, 'آن گاه از هر گروهِ هم مسلک هر کدام از آنان را که خدای را نافرمان‌تر است بیرون می‌کشیم.'),
(2320, 19, 70, 'وانگهی، ما به کسانی که برای ورود به دوزخ و سوختن در آتش آن سزاوارترند داناتریم و بهتر از هر کسی می‌دانیم که هر کدام درخورِ چه مرتبه‌ای از عذابند.'),
(2321, 19, 71, 'و هیچ یک از شما مردم ـ چه کافران و چه مؤمنان ـ نیست مگر این که گرداگرد دوزخ می‌آید. این کار بر پروردگارت حتمی و بایسته است.'),
(2322, 19, 72, 'سپس کسانی را که تقواپیشه بوده‌اند رهایی می‌بخشیم و ستمکاران (کافران و گنهکاران) را در حالی که به زانو در افتاده‌اند، در آن جا رها می‌کنیم.'),
(2323, 19, 73, 'و هنگامی که آیات روشن و روشنگر ما بر مردم خوانده شود، کافران به کسانی که ایمان آورده اند می‌گویند: کدام یک از ما دو گروه جایگاهی بهتر و محفلی نیکوتر دارد؟ همو نیکبخت و برحق است و آن هم جز ما کسی نیست.'),
(2324, 19, 74, 'راستی اینان چگونه برخورداری از دنیا را نشانه برحق بودن و نیکبختی خود می‌دانند و حال آن که چه بسیار نسل‌ها را که پیش از آنان بودند و اثاثیه‌هایی بهتر و منظره‌هایی نیکوتر داشتند، نابود کردیم.'),
(2325, 19, 75, 'ای پیامبر، به مردم بگو: کسانی که در گمراهی به سر می‌برند، خدای رحمان باید آنان را بر گمراهی شان یاری دهد تا همچنان راه انحراف بپویند، تا وقتی که آنچه را به آنان وعده داده‌اند بنگرند، و آن یا عذاب دنیاست و یا برپایی قیامت، به زودی خواهند دانست که چه کسی جایگاهش بدتر و سپاهش ناتوان‌تر است.'),
(2326, 19, 76, 'و خداوند کسانی را که هدایت یافته‌اند بر هدایتشان می‌افزاید، و کارهای شایسته که ماندگار است، نزد پروردگارت پاداشی بهتر و فرجامی نیکوتر دارد.'),
(2327, 19, 77, 'آیا دیدی آن کسی را که به آیات ما کفر ورزید و گفت: قطعاً به علت کفر ورزیدنم مال و فرزند بسیار به من داده خواهد شد؟!'),
(2328, 19, 78, 'آیا او بر غیب آگاهی یافته است که این گونه سخن می‌گوید، یا در پیشگاه خدای رحمان بر این سخنش پیمانی گرفته است؟!'),
(2329, 19, 79, 'چنین نیست که برخورداری از مال و فرزند در اثر کفر باشد; قطعاً ما آنچه را می‌گوید محفوظ می‌داریم و بر عذاب او آن گونه که باید خواهیم افزود.'),
(2330, 19, 80, 'و سخنی را که می‌گوید به ارث می‌بریم و آن را نزد خود نگاه می‌داریم تا او را در برابر آن مجازات کنیم، و او تنها و بدون هیچ یاوری نزد ما خواهد آمد.'),
(2331, 19, 81, 'مشرکان به جای خدا معبودانی را به پرستش گرفته‌اند تا برای آنان مایه عزّت باشند.'),
(2332, 19, 82, 'چنین نیست، به زودی معبودانشان پرستش آنان را انکار می‌کنند و با آنان به مخالفت برمی خیزند.'),
(2333, 19, 83, 'آیا ندیده‌ای و ندانسته‌ای که ما شیاطین را که از معبودان اهل شرک به شمار می‌روند به سوی آنان گسیل کرده‌ایم که آنان را به شرّ و تباهی برمی انگیزند. با این حال چگونه معبودانشان مایه عزّت آنان خواهند بود؟'),
(2334, 19, 84, 'پس برای نابودی کفرپیشگان شتاب مکن، زیرا ما کارهایشان را در مدّت عمرشان برای آنان برمی شمریم تا بدین وسیله عذابشان کنیم.'),
(2335, 19, 85, 'روزی [را یاد کن] که تقواپیشگان را گرد آورده و آنان را سواره به سوی بهشت که جایگاه مقربان خدای رحمان است روانه می‌کنیم.'),
(2336, 19, 86, 'و گنهکاران را در حالی که تشنه‌اند به سوی جهنّم می‌رانیم.'),
(2337, 19, 87, 'خدایان اهل شرک توانِ شفاعت کردن از کسی را ندارند; تنها کسانی می‌توانند شفاعت کنند که از خدای رحمان بر این امر پیمانی گرفته باشند.'),
(2338, 19, 88, 'مشرکان گفتند: خدای رحمان فرزندی برگرفته است.'),
(2339, 19, 89, 'راستی با این گفتارتان چیزی بس زشت و ناپسند آورده‌اید. (۸۹'),
(2340, 19, 90, 'نزدیک است آسمان‌ها از این سخن پاره پاره شوند و زمین بشکافد و کوه‌ها به شدت فرو ریزند و نابود شوند،'),
(2341, 19, 91, 'از این که برای خدای رحمان فرزندی خواندند;'),
(2342, 19, 92, 'در حالی که خدای رحمان را نسزد که فرزندی برگیرد.'),
(2343, 19, 93, 'هیچ کس در آسمان‌ها و زمین نیست جز این که روی به سوی خدا دارد و بنده‌ای در حضور او است.'),
(2344, 19, 94, 'و قطعاً آنان را محاسبه نموده و به دقت شماره کرده است. (مهر بندگی بر آنها نهاده است)'),
(2345, 19, 95, 'و روز قیامت همه آنان تنها و تهیدست به حضور او می‌آیند; پس چگونه ممکن است برخی از آنان وصف خدایی داشته باشند و فرزند خدا به شمار آیند؟'),
(2346, 19, 96, 'همانا کسانی که ایمان آورده و کارهای شایسته کرده‌اند، به زودی خدای رحمان برای آنان در دل‌ها محبّتی قرار می‌دهد.'),
(2347, 19, 97, 'جز این نیست که ما با فرو فرستادن این قرآن به زبان تو خواندن و فهمیدن آن را آسان ساختیم تا تقواپیشگان را به وسیله آن نوید، و مردمی را که دشمنی می‌کنند بدان بیم دهی.'),
(2348, 19, 98, 'و چه بسیار نسل‌هایی را که پیش از اینان بودند هلاک کردیم; آیا کسی از آنان را می‌یابی یا صدایی از ایشان می‌شنوی؟');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طا،‌ها.'),
(2350, 20, 2, 'ای پیامبر، ما این قرآن را بر تو فرو نفرستادیم تا برای ابلاغ آن به مردم به رنج بیفتی.'),
(2351, 20, 3, 'بلکه آن را فرو فرستادیم تا برای کسی که از فرجام غفلت بیم دارد تذکّری باشد.'),
(2352, 20, 4, 'این قرآن از جانب کسی نازل شده است که زمین و آسمان‌های بالاتر را آفریده است.'),
(2353, 20, 5, 'خدای رحمان بر تخت فرمانروایی عالم استیلا دارد و امور جهان هستی را تدبیر می‌کند و سامان می‌دهد.'),
(2354, 20, 6, 'آنچه در آسمان هاست و آنچه در زمین و آنچه میان آن دو و آنچه زیر خاک و در درون زمین است از آنِ اوست.'),
(2355, 20, 7, 'و اگر سخن را آشکارا بگویی، از آن آگاه است، زیرا او نهان تو را و نهان‌تر از نهانت را که حتی خود نیز از آن بی خبری می‌داند.'),
(2356, 20, 8, 'خداست که معبودی شایسته پرستش جز او نیست، زیرا نیکوترین نام‌ها همه از آنِ اوست.'),
(2357, 20, 9, 'آیا داستان موسی به تو رسیده است؟'),
(2358, 20, 10, 'آن گاه که راهش را گم کرده بود و آتشی دید، پس به خانواده اش گفت: این جا بمانید که من آتشی دیدم، می‌روم شاید شعله‌ای از آن برای شما بیاورم تا خود را بدان گرم کنید یا بر گرد آتش راهنمایی بیابم و راه مصر را از او جویا شوم.'),
(2359, 20, 11, 'پس هنگامی که نزدیک آتش رسید، ندا داده شد که: ای موسی،'),
(2360, 20, 12, 'همانا من پروردگار تواَم و تو در حضور منی، پس شرط ادب به جای آور و کفش هایت را بیرون کن که تو اینک در وادیِ مقدّس (طوی) هستی.'),
(2361, 20, 13, 'و من اینک تو را به رسالت برگزیدم، پس به آنچه به تو وحی می‌شود گوش بسپار.'),
(2362, 20, 14, 'همانا منم خداوند که معبودی جز من شایسته پرستش نیست، پس مرا عبادت کن و برای آن که به یاد من باشی نماز را برپا دار.'),
(2363, 20, 15, 'رستاخیز حتماً آمدنی است. نزدیک است آن را چنان نهان کنم که هیچ خبری از آن ندهم. آری، رستاخیز برپا خواهد شد، تا هر کسی در برابر هر کار نیک و بدی که برای آن تلاش می‌کند سزا داده شود.'),
(2364, 20, 16, 'پس مبادا آن کس که به سبب پیروی از هوای نفس خود به قیامت ایمان نیاورده است، تو را از ایمان به آن و از یاد کردن آن باز دارد که تباه می‌شوی.'),
(2365, 20, 17, 'و ای موسی: آنچه در دست راست توست چیست؟'),
(2366, 20, 18, 'موسی گفت: این عصای من است که بر آن تکیه می‌کنم و به وسیله آن برای گوسفندانم برگ درختان را فرو می‌ریزم، و نیازهای دیگری نیز به آن دارم.'),
(2367, 20, 19, 'خدا گفت: ای موسی، آن را بینداز.'),
(2368, 20, 20, 'پس موسی عصایش را بیفکنْد که ناگهان آن عصا ماری شد که به سرعت می‌خزید.'),
(2369, 20, 21, 'خدا گفت: آن را بگیر و مترس، به زودی آن را به حالت نخستینش باز می‌گردانیم.'),
(2370, 20, 22, 'و دستت را زیر بغل خود ببر، وقتی بیرون می‌آید، بی هیچ بیماریی سفید و درخشان خواهد بود، که این معجزه دیگری بر رسالت توست.'),
(2371, 20, 23, 'هدف این است که پاره‌ای از نشانه‌ها و معجزات بزرگ خویش را به تو نشان دهیم.'),
(2372, 20, 24, 'به سوی فرعون برو که او سرکشی کرده است.'),
(2373, 20, 25, 'موسی گفت: پروردگارا، سینه‌ام را برایم گشاده ساز تا بتوانم کلام تو را به خوبی دریافت کنم و دشواری‌های رسالت را تحمّل نمایم.'),
(2374, 20, 26, 'و کارم را برای من آسان گردان.'),
(2375, 20, 27, 'و گره از زبانم بگشای تا رسا سخن بگویم.'),
(2376, 20, 28, 'که در این صورت سخنم را درمی یابند.'),
(2377, 20, 29, 'و از خانواده‌ام یاوری برایم قرار ده،'),
(2378, 20, 30, 'هارون برادرم را.'),
(2379, 20, 31, 'پشتم را به حمایت او استوار کن.'),
(2380, 20, 32, 'و او را در ابلاغ وحی و رسالت شریک من گردان،'),
(2381, 20, 33, 'تا تو را بسیار (در هر مجلسی) تسبیح گوییم،'),
(2382, 20, 34, 'و تو را بسیار (در هر مجتمعی) به یگانگی یاد کنیم.'),
(2383, 20, 35, 'قطعاً تو به حال ما بینایی، و می‌دانی که ما همواره پرستشگر و تسبیح کننده تو بوده‌ایم.'),
(2384, 20, 36, 'خدا گفت: ای موسی، خواسته ات به تو داده شد.'),
(2385, 20, 37, 'و همانا پیش از این نیز باری دیگر به تو نعمتی بزرگ عطا کردیم.'),
(2386, 20, 38, 'آن گاه که به مادرت آنچه را که باید الهام شود الهام کردیم،'),
(2387, 20, 39, 'که فرزندت را در صندوق بگذار و آن را به دریا (رود نیل) بیفکن و چنین مقرر کرده‌ایم که دریا باید او را به ساحل افکند آن گاه فرعون که دشمن من و دشمن اوست وی را از کرانه دریا برمی گیرد. ای موسی، باز هم نعمتی بزرگ به تو ارزانی داشتم، آن گاه که محبّتی از خودم بر تو افکندم تا مجذوب تو گردند و زیر نظر من به تو احسان شود.'),
(2388, 20, 40, 'آن گاه که خواهرت می‌رفت تا از تو خبر گیرد. متوجّه شد که فرعونیان می‌خواهند برای تو دایه‌ای انتخاب کنند. به آنان گفت: آیا شما را به کسی که سرپرستی او را برعهده گیرد راهنمایی کنم؟ بدین ترتیب تو را به مادرت باز گرداندیم تا چشمش روشن گردد و اندوه نخورد. علاوه بر این، در مرحله دیگر نیز نعمتی بزرگ به تو ارزانی داشتیم، آن گاه که کسی از قِبْطیان را کشتی و بیم آن داشتی که تو را قصاص کنند; پس تو را از اندوه آن نجات بخشیدیم و همواره تو را آزمودیم، پس چندین سال در میان مردم مدین ماندی، تا این که ـ ای موسی ـ با مقدار فراوانی از علم و عمل که برای خود فراهم آوردی، آمدی.'),
(2389, 20, 41, 'و باز هم نعمتی بزرگ ارزانی ات کردیم آن گاه که تو را برای خودم خالص گردانیدم.'),
(2390, 20, 42, 'تو و برادرت با معجزه‌ها و نشانه‌های من روانه شوید و در یاد کردن من و فراخواندن مردم به توحید سستی مکنید.'),
(2391, 20, 43, 'به سوی فرعون بروید که او سرکشی کرده است.'),
(2392, 20, 44, 'و با او به نرمی سخن بگویید، شاید بر اثر یادآوریِ شما حقیقت را دریابد و ایمان بیاورد، یا بترسد و به حق نزدیک شود.'),
(2393, 20, 45, 'گفتند: پروردگارا، می‌ترسیم او بر ما سبقت گیرد و پیش از آن که پیام تو را ابلاغ کنیم ما را کیفر کند یا سر به طغیان بردارد و بیش از پیش بر بنی اسرائیل ستم کند و بر تو آشفته گوید.'),
(2394, 20, 46, 'خداوند در پاسخ آن دو گفت: نترسید، حتماً من با شما هستم، آنچه گفته شود می‌شنوم و آنچه انجام گیرد می‌بینم.'),
(2395, 20, 47, 'پس بی هیچ هراسی نزد او بروید و بگویید: ما فرستادگان پروردگار تو هستیم، رسالت ما را بپذیر و به پروردگارت ایمان بیاور و بنی اسرائیل را آزاد کن تا با من رهسپار شوند و آنان را شکنجه مکن، ما برای تو از جانب پروردگارت نشانه‌ای روشن بر حقّانیت رسالت خویش آورده‌ایم، و عافیت و سلامت بر کسی باد که از پی هدایت رفته و راه درست پیموده است.'),
(2396, 20, 48, 'زیرا به ما وحی شده است که عذاب بر کسی است که آیات خدا را دروغ انگاشت و از آنها روی برتافت.'),
(2397, 20, 49, 'موسی و هارون پیام خدا را به فرعون ابلاغ کردند. فرعون گفت: ای موسی، پروردگار شما دو تن کیست؟'),
(2398, 20, 50, 'موسی گفت: پروردگار ما همان کسی است که هستیِ درخورِ هر چیزی را به آن داده، سپس آن را به راه کمال هدایت کرده است.'),
(2399, 20, 51, 'فرعون گفت: پس حال مردمان نخستین چگونه است و آنها چگونه سزا داده می‌شوند، در حالی که نه از خودشان و نه از کارهایشان کسی آگاه نیست؟'),
(2400, 20, 52, 'موسی گفت: علم آن نزد پروردگار من است. این علمی است که در کتابی گسترده و فراگیر ثبت شده است. پروردگار من نه در علم خود خطا می‌کند و نه چیزی را از یاد می‌برد.'),
(2401, 20, 53, 'خداوند همان کسی است که زمین را برای شما چون گهواره‌ای قرار داد تا در آن رشد یابید، و در آن راه‌هایی برایتان پدید آورْد تا با پیمودن آنها به خواسته‌های خود دست یابید، و از آسمان آبی فرو فرستاد، پس به وسیله آن انواعی همسان از گیاهان گوناگون برآوردیم.'),
(2402, 20, 54, 'از آنها بخورید و دام هایتان را در آنها بچرانید; قطعاً در این مجموعه برای صاحبان خرد نشانه‌هایی بر یکتایی خدا در تدبیر جهان است.'),
(2403, 20, 55, 'ما شما را از زمین آفریده‌ایم و شما را در آن بازمی گردانیم و بار دیگر شما را زنده می‌کنیم و از آن بیرون می‌آوریم.'),
(2404, 20, 56, 'به یقین، ما همه معجزات خود را که به موسی دادیم به فرعون نمایاندیم، ولی او آنها را دروغ انگاشت و روی برتافت.'),
(2405, 20, 57, 'گفت: ای موسی، آیا به سراغ ما آمده‌ای تا ما را با سحر خود از سرزمینمان بیرون کنی؟'),
(2406, 20, 58, 'قطعاً ما هم با سحری مانند سحر خودت به سراغت خواهیم آمد و دلیل تو را باطل خواهیم ساخت، پس میان ما و خودت موعدی بگذار که نه ما آن را خلاف کنیم و نه تو، آن هم در مکانی هموار.'),
(2407, 20, 59, 'موسی گفت: موعد شما روز عید، روز جشن و آزین باشد، با این شرط که مردم ـ آن گاه که آفتاب گسترش می‌یابد ـ آنان را روانه وعده گاه گردانند.'),
(2408, 20, 60, 'فرعون موسی را ترک کرد و نیرنگ بازان خود (ساحران و کارگزارانش) را گرد آورد و سپس به وعده گاه آمد.'),
(2409, 20, 61, 'موسی به آنان گفت: وای بر شما! با شرک ورزیدن خود، بر خدا دروغ مبندید که شما را به عذابی سخت نابود خواهد کرد. بی تردید هر که بر خدا دروغ بست، از دستیابی به خواسته‌های خود ناامید شد.'),
(2410, 20, 62, 'پند موسی در ساحران اثر کرد و میان آنان درباره کارشان نزاع و کشمکش رخ داد. پس فرعون و کارگزارانش $برای رفع اختلاف و واداشتن ساحران به مبارزه با موسی، در نهان به نجوا پرداختند.'),
(2411, 20, 63, 'به ساحران گفتند: قطعاً این دو تن جادوگرند و می‌خواهند شما را با سحر خود از سرزمینتان بیرون کنند، و آیین و سنّت ملّی شما را که درست‌ترین سنّت هاست، از بین ببرند.'),
(2412, 20, 64, 'پس هر ترفندی که دارید فراهم آورید، آن گاه استوار و هماهنگ به میدان مبارزه آیید. امروز هر که برتری یابد پیروز و خوشبخت می‌شود.'),
(2413, 20, 65, 'روز وعده فرا رسید و همگان حاضر شدند. جادوگران با اطمینان به پیروزی خود، گفتند: ای موسی یا تو اوّل عصای خویش می‌افکنی یا ما نخستین کسی باشیم که ابزار سحر خود می‌افکند.'),
(2414, 20, 66, 'موسی گفت: نه، بلکه شما آنچه دارید بیفکنید. پس ناگهان ریسمان‌ها و چوبدستی هایشان بر اثر سحرشان چنین می‌نمود که آنها مارهایی هستند که به سرعت می‌خزند.'),
(2415, 20, 67, 'موسی با مشاهده جادوی بزرگ آنان اندک ترسی احساس کرد، ترسی که تنها در دلش بود، نه در چهره اش آشکار شد و نه در کردارش اثر گذاشت.'),
(2416, 20, 68, 'به او گفتیم: مترس که تو قطعاً از هر جهت از آنان برتری.'),
(2417, 20, 69, 'و گفتیم: آنچه در دست راست داری بیفکن; چون بیفکنی اژدهایی می‌شود که ساخته‌های جادوگران را می‌بلعد، زیرا آنچه آنان ساخته‌اند ترفند جادوگر است، و جادوگر هر جا درآید پیروز و نیکبخت نخواهد شد.'),
(2418, 20, 70, 'پس موسی عصایش را به زمین افکند. عصا اژدها شد و سحر ساحران را یکسره در کام خود فرو برد. ساحران چنان تحت تأثیر حق قرار گرفتند که بی اختیار به سجده افتادند و گفتند: ما به پروردگار هارون و موسی ایمان آوردیم.'),
(2419, 20, 71, 'فرعون گفت: پیش از آن که به شما اجازه دهم به او گرویدید؟ قطعاً او بزرگ شماست که با این هدف به شما سحر آموخته است که چون از سوی ما به مبارزه با او دعوت شوید صحنه سازی کنید و او را پیروزِ این میدان گردانید. سوگند یاد می‌کنم که به سزای این خیانت، دست و پای شما را یکی از راست و دیگری از چپ خواهم بُرید و شما را بر تنه‌های درختان خرما به صلیب می‌کشم و قطعاً خواهید دانست که عذاب کدام یک از ما ـ من یا خدای موسی ـ سخت‌تر و پایدارتر است.'),
(2420, 20, 72, 'آنان گفتند: ما هرگز تو و مال و جاه تو را بر این معجزات روشن و روشنگر که به ما رسیده است و بر آن کسی که ما را آفریده است برتری نمی‌دهیم و بر نمی‌گزینیم; پس هر کاری می‌خواهی بکن، تو فقط می‌توانی مدت این زندگی دنیا را بر ما تمام کنی.'),
(2421, 20, 73, 'ما به پروردگارمان ایمان آوردیم تا گناهان ما و گناه این سحری که ما را به آن وادار کردی بیامرزد، و بدان که خدا از هر کسی بهتر و پاینده‌تر است.'),
(2422, 20, 74, 'حقیقت این است که ما از آن روی خواهان آمرزش گناهان خود هستیم که هر کس گنهکار به حضور پروردگارش رسد، قطعاً جهنّم برای او خواهد بود، که در آن جا نه می‌میرد تا عذاب نبیند و نه می‌رَهَد تا به حیاتی آسوده رسد.'),
(2423, 20, 75, 'و کسانی که با ایمان به نزد پروردگارشان آیند در حالی که کارهای شایسته کرده باشند آنان بالاترین درجات را خواهند داشت;'),
(2424, 20, 76, 'همان بوستان‌های پاینده بهشتی با درختان انبوه که از زیر آنها نهرها روان است. در آن جا جاودانه خواهند بود، و این است پاداش کسی که در اثر ایمان و کار شایسته از پلیدی‌ها پاک شده است.'),
(2425, 20, 77, 'و همانا به موسی وحی کردیم که همراه بندگانم (بنی اسرائیل) شبانه از مصر بیرون رو، آن گاه راهی خشک در دریا برای آنان باز کن که نه از فرا رسیدن فرعونیان خواهی ترسید و نه [از غرق شدن] بیمناک خواهی شد.'),
(2426, 20, 78, 'پس موسی بنی اسرائیل را از شکاف دریا عبور داد و فرعون نیز با سپاهیانِ خود آنان را دنبال کرد، ولی فرعونیان را از دریا فرا گرفت آنچه فرا گرفت.'),
(2427, 20, 79, 'و فرعون قومش را به بیراهه برد و آنان را هدایت نکرد.'),
(2428, 20, 80, 'ای بنی اسرائیل، این گونه شما را از چنگال دشمنتان فرعون رهایی بخشیدیم و با شما در جانب راست کوه طور وعده نهادیم و بر شما (منّ) و (سلوی) فرو فرستادیم.'),
(2429, 20, 81, 'از آنچه روزی شما کرده‌ایم ـ که پاک و پاکیزه است ـ بخورید و در بهره برداری از روزی‌ها با ناسپاسی و کفران نعمت تجاوزگری نکنید که خشم من بر شما لازم خواهد شد، و هر که خشم من بر او لازم شود، قطعاً سقوط می‌کند و تباه می‌گردد.'),
(2430, 20, 82, 'و قطعاً من بسی آمرزنده کسی هستم که از شرک توبه کند و به یکتایی من ایمان آورَد و با اطاعت از دستورات من کار شایسته کند و با پیروی از پیامبران و پذیرش ولایت آنان راه درست را بیابد.'),
(2431, 20, 83, 'و ای موسی، چه چیز سبب شد که شتاب کنی و پیش از آمدن آن گروه از قومت که آنان را برای همراهی برگزیدی، به وعده گاه بیایی؟'),
(2432, 20, 84, 'موسی گفت: آنان در پی من هستند، و من ـ پروردگارا ـ با شتاب به سوی تو آمدم تا خشنود شوی.'),
(2433, 20, 85, 'خدا گفت: به راستی، ما قوم تو را پس از آمدنت به وعده گاه آزمودیم و سامری آنان را به بیراهه افکند.'),
(2434, 20, 86, 'موسی بی درنگ با حالت خشم و اندوه به سوی قومش بازگشت و گفت: ای قوم من، آیا پروردگارتان به شما وعده‌ای نیکو نداد؟ آیا زمان حضور من در وعده گاه از نظر شما طولانی شد و از بازگشت من ناامید شدید، یا خواستید خشم پروردگارتان بر شما لازم آید که به کفر گرایش یافتید و وعده‌ای را که با من کردید خلاف نمودید؟'),
(2435, 20, 87, 'گفتند: ما برای ساختن آن گوساله، از اموال خود صرف نکردیم تا عمداً با وعده تو مخالفت کرده باشیم، ولی از زیورآلات فرعونیان بارهایی گران بر دوش داشتیم و آنها را دور انداختیم و سامری این چنین کرد که آنها و آنچه را با خود داشت در آتش افکند.'),
(2436, 20, 88, 'پس برای آنان تندیس گوساله‌ای را که صدای گوساله داشت پدید آورد، سپس او و یارانش گفتند: این معبود شما و معبود موسی است که موسی آن را فراموش کرده و در کوه طور به جستوجویش پرداخته است.'),
(2437, 20, 89, 'آیا نمی‌بینند که آن تندیس هیچ پاسخی به سخن آنان نمی‌دهد، و زیانی را در اختیار ندارد که آن را از ایشان باز دارد و مالک سودی نیست که آن را به سوی ایشان جلب کند، پس چگونه او را خدای خود گرفته‌اند؟'),
(2438, 20, 90, 'آنان نه خود به این حقیقت روشن توجه کردند و نه به تذکر هارون گوش فرا دادند، هارون پیش از آمدن موسی به آنان گفت: ای قومِ من، جز این نیست که شما به وسیله این تندیس گوساله مورد آزمایش قرار گرفته‌اید و قطعاً پروردگار شما خدای رحمان است، پس از من پیروی کنید و فرمان مرا اطاعت نمایید.'),
(2439, 20, 91, 'آنان در پاسخ هارون گفتند: ما هرگز از پرستش آن دست برنمی داریم تا این که موسی به سوی ما بازگردد و تکلیف ما را درباره آن مشخص کند.'),
(2440, 20, 92, 'موسی روی به هارون کرد و گفت: ای هارون، هنگامی که دیدی آنان به بیراهه می‌روند چه چیز تو را از اطاعت فرمان من باز داشت و تو را بر آن داشت،'),
(2441, 20, 93, 'که از روش من پیروی نکنی؟ آیا از فرمان من که به تو گفته بودم به روش من عمل کن و راه فسادگران را پیروی مکن، سرپیچی کردی؟'),
(2442, 20, 94, 'هارون در حالی که سر و ریشش در چنگ موسی بود، به او گفت: ای پسر مادرم، نه ریش مرا بگیر و نه سرم را; من ترسیدم که در صورت مقاومت در برابر گمراهان، کار به جنگ و خونریزی بینجامد و تو بگویی میان بنی اسرائیل تفرقه افکندی و سخنم را در مورد اصلاح امور پاس نداشتی.'),
(2443, 20, 95, 'موسی به سامری روی کرد و گفت: ای سامری، این فتنه بزرگی که برپا کردی چیست و چه چیز تو را به آن وادار کرد؟'),
(2444, 20, 96, 'گفت: من چیزی را دریافتم که مردم آن را درنیافته بودند، و مشتی از آنچه آن رسول بر جای نهاده بود برگرفتم و در آتش افکندم و آن را به صورت گوساله‌ای درآوردم و نَفْس من این گونه آن کار را در نظرم بیاراست و مرا به آن وسوسه کرد.'),
(2445, 20, 97, 'موسی گفت: پس به سزای این کار از میان مردم بیرون رو، و با هیچ کس معاشرت مکن، زیرا بر تو مقرّر شده است که در زندگی خود همواره بگویی: به من نزدیک نشوید و با من تماس نگیرید، و برای تو موعدی خواهد بود که در آن هلاک خواهی گشت و هرگز در مورد آن با تو تخلف نخواهد شد، و به معبودت که بر پرستش آن پایدار بودی بنگر که قطعاً آن را به براده تبدیل خواهیم کرد و ذرّاتش را در دریا پراکنده خواهیم ساخت.'),
(2446, 20, 98, 'ای سامری و ای فرزندان اسرائیل، معبود شما تنها خداست که جز او معبودی شایسته پرستش نیست، و دانش او هر چیزی را فراگرفته است.'),
(2447, 20, 99, 'ای پیامبر! بدین سان که داستان موسی را بر تو حکایت کردیم، خبر برخی از امت‌ها و رویدادهایی را که گذشته است بر تو حکایت می‌کنیم، و همانا ما از جانب خود کتابی به تو داده‌ایم که مایه یاد آوردن خداست.'),
(2448, 20, 100, 'هر کس از آن روی برتابد، روز قیامت بار سنگینی از گناه بر دوش خواهد کشید.'),
(2449, 20, 101, 'و در وزر و وبال آن جاودانه خواهند ماند و روز قیامت بد باری خواهند داشت;'),
(2450, 20, 102, 'همان روز که در صور دمیده می‌شود و همگان را به صحنه قیامت می‌آورند. آن روز که چنین شود گنهکاران را با چشمانی سبز رنگ که قدرت بینایی ندارد محشور می‌کنیم.'),
(2451, 20, 103, 'آنان از ترس، آهسته به یکدیگر می‌گویند: شما در دنیا بیش از ده روز نمانده‌اید.'),
(2452, 20, 104, 'ما از همه داناتریم به آنچه می‌گویند، آن گاه که بهترینشان در بیان حقیقت، می‌گوید: بیش از یک روز در دنیا درنگ نکرده‌اید.'),
(2453, 20, 105, 'ای پیامبر، از تو در باره کوه‌ها می‌پرسند که به گاهِ قیامت چه خواهند شد؟ بگو: (پروردگارم آنها را ریز ریز و پراکنده خواهد ساخت،'),
(2454, 20, 106, 'و بدین طریق زمین را هموار و صاف وامی گذارد،'),
(2455, 20, 107, 'به گونه‌ای که در آن هیچ پستی و بلندی نمی‌بینی.'),
(2456, 20, 108, 'آن روز که چنین شود، همگان دعوتِ آن دعوت کننده را بدون هیچ گونه سرپیچی پیروی می‌کنند، و صداها در برابر خدای رحمان خاشع می‌شود و جز صدایی آهسته نمی‌شنوی.'),
(2457, 20, 109, 'آن روز که چنین شود، هیچ شفاعت کننده‌ای که شفاعتش سود بخشد، وجود نخواهد داشت، مگر کسی که خدای رحمان به او اجازه شفاعت داده و سخنش را پسندیده باشد.'),
(2458, 20, 110, 'خداوند از موقعیت پیش روی مجرمان در صحنه قیامت و موقعیت پشت سرشان پیش از برپایی قیامت آگاه است، ولی علم آنان به خدا احاطه ندارد.'),
(2459, 20, 111, 'و چهره‌ها در برابر خدای زنده و برپا دارنده، خوار و درمانده می‌شوند، و هر کس در اثر ارتکاب گناه'),
(2460, 20, 112, 'و هر کس بخشی از کارهای شایسته را انجام دهد و با ایمان باشد، نه از تباه شدن حق خود می‌ترسد و نه از کاسته شدن پاداشش.'),
(2461, 20, 113, 'و قرآن را این گونه (با بیانی معجزه آسا) فرو فرستادیم در حالی که آن کتابی خوانده شده و به زبان عربی است و برخی از هشدارها را به صورت‌های گوناگون در آن بیان کردیم، باشد که بترسند و از دشمنی با حق بپرهیزند یا این که یاد حق را در آنان به وجود آورد، پس آن را باور کنند و به آن ایمان آورند.'),
(2462, 20, 114, 'پس بلندمرتبه است خدا، آن مالک و فرمانروای جهان هستی که همواره پایدار است و دگرگونی در او راه ندارد. ای پیامبر، در خواندن قرآن، پیش از آن که وحی اش بر تو به پایان رسد، شتاب مکن و بگو: پروردگارا، مرا دانش بیفزای.'),
(2463, 20, 115, 'به یقین، ما پیش‌تر به آدم سفارش کردیم که از آن درخت نخورد، ولی او سفارش ما را به فراموشی سپرد و ما در او عزمی استوار در حفظ آن نیافتیم.'),
(2464, 20, 116, 'و یاد کن زمانی را که به فرشتگان گفتیم: برای آدم سجده کنید. پس همه سجده کردند جز ابلیس که سرباز زد.'),
(2465, 20, 117, 'از این رو گفتیم: ای آدم، قطعاً این ابلیس دشمن تو و دشمن همسر توست، مبادا شما را فریب دهد و از این بهشت بیرونتان کند، که برای دستیابی به گذرانِ زندگی به رنج خواهی افتاد.'),
(2466, 20, 118, 'قطعاً تو در این جا گرسنگی نخواهی داشت و برهنه نخواهی شد.'),
(2467, 20, 119, 'و در این جا نه تشنه خواهی شد و نه آفتاب بر تو خواهد تابید.'),
(2468, 20, 120, 'پس شیطان او را وسوسه کرد و گفت: ای آدم، آیا تو را به درختی که میوه اش حیاتی جاودان و سلطنتی زوال ناپذیر می‌بخشد، راهنمایی کنم؟'),
(2469, 20, 121, 'آن گاه آدم و همسرش از آن درخت خوردند، در نتیجه شرمگاه هایشان برایشان نمایان شد و شروع کردند از برگ درختان بهشت بر خود بچسبانند. و آدم پروردگارش را نافرمانی کرد و حقیقت را نیافت.'),
(2470, 20, 122, 'سپس پروردگارش او را برای خود برگزید، آن گاه به رحمت خویش به او بازگشت و او را به سوی خود هدایت کرد.'),
(2471, 20, 123, 'خدا گفت: هر دو از بهشت به زمین فرود آیید در حالی که شما آدمیان و شیاطین دشمن یکدیگرید; پس اگر از جانب من رهنمودی برای شما آمد ـ که خواهد آمد ـ هر کس از رهنمود من پیروی کند نه گمراه می‌شود و نه تیره بخت می‌گردد.'),
(2472, 20, 124, 'و هر کس از یاد من روی برتابد، قطعاً زندگی تنگ و دشواری خواهد داشت، و روز قیامت او را نابینا محشور می‌کنیم تا به بهشت راه نیابد.‌'),
(2473, 20, 125, 'می‌گوید: پروردگارا، چرا مرا نابینا محشور کردی با آن که در دنیا بینا بودم؟'),
(2474, 20, 126, 'خدا می‌گوید: ما امروز تو را نابینا محشور کردیم همان گونه که آیات ما در دنیا به تو رسید و تو آنها را نادیده انگاشتی، و همان گونه که در دنیا به وسیله آیات ما هدایت نیافتی امروز فراموش می‌شوی و به راه نجات رهنمون نمی‌شوی.'),
(2475, 20, 127, 'بدین سان کسی را که از مرز بندگی تجاوز کرده و به نشانه‌های پروردگارش ایمان نیاورده است سزا می‌دهیم، و قطعاً عذاب آخرت سخت‌تر و پایدارتر از عذاب دنیاست.'),
(2476, 20, 128, 'آیا این واقعیت، که چه بسیار نسل‌ها را پیش از این کفرپیشگان به عذاب هلاک کردیم در حالی که اینان هم اکنون در خانه‌های آنان رفت و آمد می‌کنند و آثار عذاب را می‌نگرند، راه عبرت گرفتن و ایمان آوردن به آیات الهی را برایشان آشکار نساخته است؟ قطعاً برای خردمندان در هلاکت پیشینیان نشانه‌هایی است بر این که تکذیب کنندگان آیات الهی به عذاب گرفتار خواهند شد.'),
(2477, 20, 129, 'و اگر از جانب پروردگارت از پیش سخنی نرفته بود که آدمیان تا مدتی در زمین می‌مانند، و اگر سرآمد معیّنی برایشان مقدر نشده بود، قطعاً به سبب کفر و طغیانشان هلاکت بر آنان لازم می‌آمد.'),
(2478, 20, 130, 'ای پیامبر، اکنون که از تقدیر الهی آگاه شدی، بر تلخی آنچه می‌گویند شکیبا باش و پیش از طلوع خورشید و پیش از غروب آن، همراه با ستایش پروردگارت او را تسبیح گوی، و در بخشی از ساعات شب و اطراف روز نیز به تسبیح او بپرداز، باشد که در اثر مداومت بر ستایش و تسبیح خدا به مقام رضا برسی و از مقدّرات الهی خشنود باشی.'),
(2479, 20, 131, 'و به سوی آنچه گروه‌هایی از کافران را بدان بهره مند ساخته‌ایم چشم مدوز. ما زینت زندگانی دنیا را برای کافران مقرر کرده‌ایم تا آنان را در آنچه داده‌ایم بیازماییم، و بدان که روزیِ پروردگارت که در آخرت به تو ارزانی می‌دارد، بهتر و پاینده‌تر است.'),
(2480, 20, 132, 'و کسانت را به نماز فرمان ده و [خود نیز] بر آن شکیبا باش. ما از تو روزی نمی‌طلبیم، بلکه به تو روزی می‌دهیم، و فرجام نیک برای تقواپیشگی است.'),
(2481, 20, 133, 'مشرکان مکّه گفتند: چرا محمّد از جانب پروردگارش معجزه‌ای برای ما نمی‌آورد که رسالتش را تأیید کند؟ راستی مگر قرآن به عنوان شاهدی که حقایق کتاب‌های آسمانی نخستین را به روشنی بیان می‌کند به آنان نرسیده است؟'),
(2482, 20, 134, 'آنان پیش از آمدن قرآن به خاطر کفر و طغیانگری سزاوار عذاب بودند و اگر ما آنان را پیش از نزول قرآن به عذابی هلاک می‌کردیم، می‌گفتند: پروردگارا، چرا پیامبری به سوی ما نفرستادی؟ اگر پیامبری فرستاده بودی، ما پیش از آن که بر اثر عذاب خوار و رسوا شویم از آیات تو پیروی می‌کردیم.'),
(2483, 20, 135, 'ای پیامبر، به آنان بگو: ما و شما در انتظاریم; ما منتظر پیروزی دین خدا و شما منتظر نابودی ما; پس منتظر باشید که به زودی خواهید دانست رهروان راه راست چه کسانی اند و هدایت یافتگان کیانند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'وقت حسابرسی اعمال مردم به آنان نزدیک شده است و آنان همچنان در غفلت اند و با دل بستن به دنیا و وانهادن توبه و ایمان، از آن رویگردانند.'),
(2485, 21, 2, 'هیچ وحی جدیدی که یادآور خدا باشد، از جانب پروردگارشان برایشان نمی‌آید مگر این که آن را می‌شنوند و به بازی می‌پردازند.'),
(2486, 21, 3, 'و دل هایشان به بیهوده مشغول است و در جلسه‌ای کاملا سرّی درباره دعوت پیامبر به گفتوگو نشستند، البته نه همه آنان بلکه ستمکارانشان و گفتند: آیا این شخص (محمّد) جز بشری مانند شماست؟ اگر از جانب خدا پیامی به او رسیده بود قطعاً به شما نیز می‌رسید، پس قرآنش افسونی بیش نیست. اکنون آیا شما با این که از بصیرت برخوردارید به افسون می‌گرایید؟'),
(2487, 21, 4, 'پیامبر به آنان گفت: پروردگارم هر سخنی را، خواه در آسمان گفته شود و خواه در زمین، می‌داند، و اوست که به همه گفته‌ها شنوا و به همه کارها داناست.'),
(2488, 21, 5, 'مشرکان قرآن را نخست افسون خواندند، آن گاه گفتند: نه، بلکه رؤیاهایی آشفته است که محمّد آنها را وحی الهی پنداشته و خود را پیامبر تصوّر کرده است، بلکه آن را خود بافته و کلام خدا قلمداد کرده است، بلکه او شاعر است و قرآنش برخاسته از خیالات شاعرانه اوست. اگر به راستی پیامبر است باید برای ما معجزه‌ای بیاورد همان گونه که پیامبرانِ نخستین همراه با معجزه فرستاده شدند. اگر معجزه‌ای بیاورد ما به او ایمان می‌آوریم.'),
(2489, 21, 6, 'پیش از اینان نیز مردم هیچ شهر و دیاری که از پیامبرانشان درخواست معجزه کردند، با مشاهده آن معجزات ایمان نیاوردند; ازاین رو ما آنان را هلاک کردیم. اکنون آیا این مشرکان با دیدن معجزه ایمان می‌آورند با این که مانند پیشینیان تجاوزکارند؟ پس بدانید که برآوردن خواسته آنان مایه ایمانشان نخواهد شد بلکه پی آمدش تنها هلاکت آنان است.'),
(2490, 21, 7, 'و پیش از تو نیز جز مردانی را که به آنان وحی می‌کردیم به رسالت نفرستادیم. اگر نمی‌دانید از پژوهندگان کتاب‌های آسمانی بپرسید تا دریابید که پیامبران از میان انسان‌ها برخاسته‌اند و اگر به همه انسان‌ها وحی نمی‌شود برای آن است که وحی موهبتی است که خدا آن را به هر که بخواهد عطا می‌کند.'),
(2491, 21, 8, 'ما از پیامبرانمان ویژگی‌های بشری را سلب نکرده و آنان را کالبدهایی که غذا نخورند قرار نداده‌ایم و در دنیا جاودانه هم نبوده‌اند.'),
(2492, 21, 9, 'ما آنان را فرستادیم و وعده‌ای را که به آنان داده بودیم تحقق بخشیدیم، پس آنان و هر که را خواستیم نجات دادیم و تجاوزگران را نابود ساختیم.'),
(2493, 21, 10, 'به راستی، ما کتابی به سوی شما فرو فرستادیم که در آن ارجمندترین معارف و پایدارترین احکام برای شما یادآوری شده است. آیا درنمی یابید؟'),
(2494, 21, 11, 'چه بسیار شهرهایی را که مردمانشان ستمکار (کافر و مشرک) بودند در هم شکستیم، و بعد از آنها مردمانی دیگر پدید آوردیم.'),
(2495, 21, 12, 'پس آن گاه که عذاب ما را احساس کردند، ناگهان از دیارشان گریختند.'),
(2496, 21, 13, 'از روی سرزنش به آنان گفته شد: فرار نکنید، و به سوی نعمت‌ها و لذت‌هایی که در آنها غوطهور بودید و به سوی خانه هایتان بازگردید، باشد که تهیدستان از شما چیزی درخواست کنند و شما در برابر آنان تکبّر کنید و بر خود ببالید.'),
(2497, 21, 14, 'آنان از گذشته خود پشیمان شدند و گفتند: ای وای بر ما، که ما واقعاً ستمکار بودیم.'),
(2498, 21, 15, 'این سخن همچنان بر زبانشان جاری بود تا این که آنان را چون کشتزاری درو شده گرداندیم و خاموششان کردیم.'),
(2499, 21, 16, 'ما آسمان و زمین و آنچه را که میان آن دو است نیافریده‌ایم که بازی کنندگان باشیم، پس مردم گمان نکنند که به سوی ما باز نمی‌گردند و پاداش و کیفری در کار نیست.'),
(2500, 21, 17, 'اگر می‌خواستیم سرگرمی و بازیچه‌ای برای خود انتخاب کنیم، آن را از میان آفریدگان انتخاب نمی‌کردیم تا نیازمند آنها باشیم; اگر سرگرمی و بازیچه‌ای اراده می‌کردیم آن را از ذات خویش برمی گرفتیم و ما از آن پیراسته‌ایم.'),
(2501, 21, 18, 'ما نه تنها هستی را به بازی نیافریده‌ایم، بلکه سنّت ما بر این است که حق را بر باطل می‌افکنیم آن گاه حق باطل را می‌شکند و ناگهان باطل نابود می‌شود. وای بر شما از این وصفی که درباره جهان دارید که نه رستاخیزی برای آن قائلید و نه پیام و پیامبری را از جانب خدا باور دارید.'),
(2502, 21, 19, 'هیچ کس را توانِ گریز از حسابرسی اعمالش نیست، زیرا هر که در آسمان‌ها و زمین است از آنِ اوست، و کسانی که مقرّب درگاه اویند تکبّر نمیورزند و از پرستش او سر باز نمی‌زنند و خسته نمی‌شوند.'),
(2503, 21, 20, 'آنان شب و روز تسبیح خدا می‌گویند و سستی نمی‌کنند.'),
(2504, 21, 21, 'آیا کسانی که پیام‌های الهی را نمی‌پذیرند، خدایانی از زمین برگزیده‌اند که آنها رستاخیز را برپا می‌دارند و اعمال مردم را حسابرسی می‌کنند؟'),
(2505, 21, 22, 'اگر در آسمان‌ها و زمین جز خدای یکتا خدایانی وجود می‌داشت، قطعاً آسمان‌ها و زمین در اثر تدبیرهای متضاد تباه می‌شد; پس تداوم هستی نشانه تدبیر واحد و یکتاییِ مدبّر آن است. منزّه است خدا، فرمانروای جهان هستی، از آنچه او را به ناروا وصف می‌کنند که برای او شریک می‌پندارند.'),
(2506, 21, 23, 'او چون مالک همه چیز است، از آنچه می‌کند باز خواست نمی‌شود، ولی آنان (مشرکان و معبودانشان) بازخواست خواهند شد.'),
(2507, 21, 24, 'آیا جز خدای یکتا خدایانی برای خود گرفته‌اند؟ بگو: برهانتان را بیاورید. این قرآن برای همراهان من مایه یادآوری است و کتاب‌های آسمانی گذشته نیز برای امّت‌های پیشین مایه یادآوری بوده است; همه آنها از یکتایی خدا یاد کرده و تنها او را شایسته پرستش دانسته‌اند، بلکه بیشتر کافران حق را نمی‌شناسند و به همین سبب از آن رویگردانند.'),
(2508, 21, 25, 'و ما پیش از تو هیچ پیامبری را به رسالت نفرستادیم مگر این که به او وحی می‌کردیم که: معبودی شایسته پرستش جز من نیست، پس شما و امّت هایتان مرا بپرستید.'),
(2509, 21, 26, 'مشرکان گفتند: خدای رحمان فرشتگان را به فرزندی برگرفته است. منزّه است خدا از داشتن فرزند; بلکه فرشتگان بندگانی اند که خدا آنان را گرامی داشته است.'),
(2510, 21, 27, 'آنان در سخن گفتن بر او پیشی نمی‌گیرند و تنها به فرمان او کار می‌کنند.'),
(2511, 21, 28, 'خدا از رفتار و گفتار فرشتگان که در پیش روی دارند و از عوامل پیدایش آنان که پشت سر نهاده‌اند با خبر است; ازاین رو آنان را شایسته بندگی خویش دانسته و به آن گرامیشان داشته است. آنان جز برای کسی که خدا دینش را پسندیده باشد، شفاعت نمی‌کنند و خود از ترس عذاب او هراسانند; زیرا می‌دانند که خدا بر بردن پاکی و عصمتشان تواناست.'),
(2512, 21, 29, 'و هر کس از آنان بگوید که من معبودی شایسته پرستشم نه خداوند، دوزخ را به عنوان کیفر او مقرّر می‌داریم و همه ستمکاران را بدین سان سزا می‌دهیم.'),
(2513, 21, 30, 'آیا کسانی که ربوبیّت خدا را منکر شده‌اند ندانسته‌اند که آسمان‌ها و زمین به هم پیوسته بودند و ما آنها را از هم گشودیم، و هر چیز زنده‌ای را از آب آفریدیم؟ با این وصف آیا باور نمی‌کنند که تدبیر امور آسمان‌ها و زمین همچون آفرینش آنها کار ماست؟'),
(2514, 21, 31, 'و در زمین کوه‌هایی استوار پدید آوردیم تا زمین مردم را نلرزاند، و در میان کوه‌ها راه‌هایی فراخ پدید آوردیم، باشد که آنان به مقصد خود راه یابند.'),
(2515, 21, 32, 'و آسمان را سقفی محفوظ از نفوذ شیاطین قرار دادیم. در آن نشانه‌هایی است بر یکتایی ما، ولی مردم از نشانه‌های موجود در آن رویگردانند.'),
(2516, 21, 33, 'اوست آن کسی که شب و روز و خورشید و ماه را پدید آورده است. هر یک از آنها در مداری شناورند.'),
(2517, 21, 34, 'مشرکان در انتظار مرگ تو نشسته‌اند تا از دعوت تو آسوده شوند. البته ما برای هیچ بشری پیش از تو عمر جاودانه قرار ندادیم، تو نیز خواهی مرد، امّا آیا اگر تو بمیری آنان برای همیشه می‌مانند؟'),
(2518, 21, 35, 'هر انسانی مرگ را خواهد چشید، و ما شما را در مدت عمرتان، چنان که باید، به بد و نیک می‌آزماییم، و سرانجام به سوی ما بازگردانده می‌شوید.'),
(2519, 21, 36, 'و آنان که به یکتایی خدا کافر شده‌اند، وقتی تو را می‌بینند فقط به مسخره ات می‌گیرند، آنان به یکدیگر می‌گویند: آیا این است همان کسی که معبودان شما را به ناتوانی یاد می‌کند؟ این در حالی است که آنان به این که خدا به رحمت گستر و بخشاینده نعمت‌ها وصف شود، کافرند.'),
(2520, 21, 37, 'آدمی چنان شتابزده است که گویی از شتاب آفریده شده است. شما کافران با کفرورزی و استهزای پیامبر شتاب در عذاب را می‌طلبید. از من خواهان شتاب در عذاب نباشید، که به زودی نشانه‌های خود (عذاب‌های دوزخ) را به شما ارائه خواهم کرد.'),
(2521, 21, 38, 'آنان می‌گویند: اگر تو و پیروانت راست می‌گویید که رستاخیز و عذابی در کار است، این وعده کی خواهد بود؟'),
(2522, 21, 39, 'ای کاش آنان که کافر شده اند می‌دانستند که زمانی را در پیش دارند که نه خود می‌توانند عذاب را از چهره شان و از پشتشان باز دارند و نه بر زدودن آن یاری می‌شوند.'),
(2523, 21, 40, 'بلکه ناگهان به سراغشان می‌آید و حیرانشان می‌کند، ازاین رو نه می‌توانند آن را باز گردانند و نه مهلت می‌یابند که در دفع آن بیندیشند.'),
(2524, 21, 41, 'ای پیامبر، به راستی رسولانی که پیش از تو بودند به سبب هشدارهایشان مورد استهزا قرار گرفتند; پس بر کسانی که پیامبران را مسخره می‌کردند، همان عذابی که آن را به استهزا می‌گرفتند فرود آمد.'),
(2525, 21, 42, 'بگو: چه کسی شما را شب و روز در برابر خدای رحمان ـ اگر بخواهد شما را عذاب کند ـ حفظ می‌کند؟ پندها در آنان کارساز نیست، بلکه آنان از این قرآن که باعث یاد آوردن پروردگارشان است رویگردانند.'),
(2526, 21, 43, 'مگر بجز ما خدایانی دارند که بتوانند آنان را از ما باز دارند؟ خدایانی که آنان می‌پندارند، نه می‌توانند یکدیگر را یاری کنند و نه می‌توانند خود را در برابر ما پناه دهد و حفظ نمایند، پس چگونه پرستشگران خود را یاری می‌دهند یا آنان را در پناه خود از عذاب ما حفظ می‌کنند؟'),
(2527, 21, 44, 'بلکه اینان (مشرکان مکّه) و پدرانشان را از انواع نعمت‌ها بهره مند ساختیم تا این که روزگارشان به درازا کشید و یکتاپرستی را که آیین پدرشان ابراهیم بود به فراموشی سپردند و به پرستش بت‌ها روی آوردند. آیا نمی‌بینند که ما به سراغ زمین می‌آییم و از اطراف آن می‌کاهیم (امّتی را در پی امّتی دیگر نابود می‌کنیم)؟ با این حال، آیا آنان بر ما چیره‌اند؟'),
(2528, 21, 45, 'بگو: جز این نیست که من شما را به وسیله وحی الهی که در هدایت آفرینی آن هیچ تردیدی نیست هشدار می‌دهم، ولی گوش هایتان ناشنواست، و ناشنوایان هنگامی که هشدارشان دهند، ندایی نمی‌شنوند.'),
(2529, 21, 46, 'فقط عذاب آنان را با حقیقت آشنا می‌کند. به یقین اگر دمی از عذاب پروردگارت به آنان برسد، می‌گویند: ای وای بر ما، که ما در اثر کفر و شرک راه ستم در پیش گرفته بودیم.'),
(2530, 21, 47, 'و روز قیامت ترازوهای عدالت را [برای سنجش باورها و اعمال مردمان] در میان می‌نهیم و به هیچ کس اندک ستمی نخواهد شد. و اگر عملی که سنجیده می‌شود هموزن دانه خردلی باشد آن را به حساب می‌آوریم و همین بس که ما حسابرس باشیم.'),
(2531, 21, 48, 'و همانا ما به موسی و هارون تورات را که جداکننده حق از باطل و برای تقواپیشگان روشنگر راه سعادت و مایه یادآوری خدا بود، عطا کردیم.'),
(2532, 21, 49, 'تقواپیشگان همان کسانی اند که از کیفر پروردگارشان با این که از دیدشان نهان است می‌ترسند و خود از برپایی قیامت هراسانند.'),
(2533, 21, 50, 'و این قرآن یادآوری است پرخیر و برکت که ما آن را فرو فرستاده‌ایم; آیا باز هم شما آن را انکار می‌کنید؟'),
(2534, 21, 51, 'و همانا ما، پیش از موسی و هارون به ابراهیم رهنمودی در خور استعدادش عطا کردیم که حقایق و معارف الهی را به وسیله آن درک کند، و ما به شایستگی‌های او آگاه بودیم.'),
(2535, 21, 52, 'آن گاه که به پدر و قوم خود ـ در نخستین باری که با جامعه شرک آلود روبرو شد ـ گفت: این تندیس‌ها چیست که شما بدانها روی آورده و بزرگشان داشته و به خدمت آنها ایستاده‌اید؟'),
(2536, 21, 53, 'گفتند: پدران خود را پرستش کننده آنها یافتیم.'),
(2537, 21, 54, 'گفت: قطعاً شما و پدرانتان در گمراهی آشکاری بوده‌اید.'),
(2538, 21, 55, 'گفتند: آیا حق را برای ما آورده‌ای و بجد سخن می‌گویی یا از بازی کنندگانی و این سخنان را به شوخی بر زبان می‌آوری؟'),
(2539, 21, 56, 'گفت: این معبودانی که برگرفته‌اید پروردگار شما نیستند، بلکه پروردگار شما پروردگار آسمان‌ها و زمین است، همان کسی که آنها را پدید آورده است، و من بر این حقیقت از گواهانم و خود آن را باور کرده‌ام، پس چگونه ممکن است سخن بجد نگویم و از بازیگران باشم؟'),
(2540, 21, 57, 'و با خود گفت: به خدا سوگند، پس از آن که پشت کردید و از این شهر بیرون رفتید، برای بت‌های شما چاره‌ای خواهم کرد.'),
(2541, 21, 58, 'پس از آن که از شهر خارج شدند، ابراهیم بت‌ها را خرد کرد، ولی بزرگشان را باقی گذاشت، شاید به آن روی آورند و آن را شکننده دیگر بت‌ها به شمار آورند.'),
(2542, 21, 59, 'هنگامی که قومش باز آمدند و بت‌ها را شکسته دیدند، از روی تأسف گفتند: چه کسی با معبودان ما چنین کرده است؟ قطعاً او از ستمکاران است.'),
(2543, 21, 60, 'گفتند: شنیده‌ایم جوانی که به او ابراهیم گفته می‌شود، از آنها به بدی یاد می‌کند.'),
(2544, 21, 61, 'گفتند: او را در نقطه‌ای نزدیک بتخانه در برابر چشم مردم احضار کنید، باشد که آنان گواهی دهند که او از معبودان به بدی یاد کرده است.'),
(2545, 21, 62, 'ابراهیم را احضار کردند و به او گفتند: ای ابراهیم، آیا تو با معبودان ما چنین کرده‌ای؟'),
(2546, 21, 63, 'ابراهیم گفت: بلکه شواهد نشان می‌دهد که این بزرگشان چنین کرده است; اگر قدرت سخن گفتن دارند، از خودشان بپرسید که چه کسی آنها را خرد کرده است.'),
(2547, 21, 64, 'بت پرستان به خود آمدند و دریافتند که معبودانشان فاقد شعورند و بر هیچ کاری قدرت ندارند; با خود گفتند: تردیدی نیست که شما خود ستمکارید که این گونه موجودات را می‌پرستید، نه ابراهیم که از آنها تبرّی می‌جوید.'),
(2548, 21, 65, 'ولی چیزی نگذشت که به جایگاه نخست خویش بازگشتند (خود را برحق و ابراهیم را ستمگر شمردند) و گفتند: ای ابراهیم، تو خوب می‌دانی که اینها سخن نمی‌گویند; پس تو آنها را خرد کرده‌ای.'),
(2549, 21, 66, 'ابراهیم گفت: اینک آیا به جای خدا چیزهایی را می‌پرستید که اندک سودی به شما نمی‌بخشند، و اگر عبادتشان را ترک کنید کمترین گزندی به شما نمی‌رسانند؟'),
(2550, 21, 67, 'اف بر شما و بر آنچه به جای خدا می‌پرستید. آیا تعقل نمی‌کنید؟'),
(2551, 21, 68, 'گفتند: اگر کاری می‌کنید، ابراهیم را بسوزانید و معبودانتان را یاری کنید.'),
(2552, 21, 69, 'پس او را به آتش افکندند و ما به آتش گفتیم: ای آتش، بر ابراهیم سرد و سلامت باش.'),
(2553, 21, 70, 'و خواستند درباره او مکری کنند و نورش را خاموش سازند، ولی ما او را رهایی بخشیدیم و آنان را زیانکارترین مردم قرار دادیم.'),
(2554, 21, 71, 'و او و لوط را از چنگال بت پرستان رهانیدیم و آن دو را به سرزمین شام که در آن برای همگان برکت قرار داده‌ایم رسانده‌ایم.'),
(2555, 21, 72, 'و اسحاق و یعقوب را به عنوان عطیّه‌ای به او بخشیدیم و همه را شایسته گردانیدیم.'),
(2556, 21, 73, 'و آنان را پیشوایانی قرار دادیم که به فرمان ما مردم را به راه سعادت آورند و به مقصد برسانند، و آنان را با نیرویی الهی که در درونشان نهاده و استوارشان ساخته‌ایم فراخوانده و به انجام کارهای نیک و برپایی نماز و پرداخت زکات سوقشان داده‌ایم و آنان پیش از این نیز پرستشگران ما بوده‌اند.'),
(2557, 21, 74, 'و به لوط حکمت و دانش عطا کردیم و او را از دیاری که مردمش آن کارهای پلید را می‌کردند، رهایی بخشیدیم. به راستی آنان بد مردمی بودند و از مرز فطرت خود تجاوز می‌کردند.'),
(2558, 21, 75, 'و او را به رحمت خود درآوردیم و تحت سرپرستی خویش قرار دادیم، زیرا او از شایستگان بود.'),
(2559, 21, 76, 'و نوح را یاد کن، آن گاه که پیش از ابراهیم ما را ندا داد و از ما یاری طلبید، ما هم دعای او را اجابت کردیم و وی و خاندانش را از آن اندوه بزرگ رهایی بخشیدیم.'),
(2560, 21, 77, 'و او را بر مردمی که آیات ما را دروغ می‌انگاشتند، پیروز کردیم و از شرّ آنان رهایی اش بخشیدیم. آنان چون بد مردمی بودند همگی شان را غرق کردیم.'),
(2561, 21, 78, 'و داود و سلیمان را یاد کن، آن گاه که درباره حکم کشتزاری که شبانه گوسفندانِ مردم در آن چریده و تباهش کرده بودند، به مشورت پرداختند، و ما همواره بر داوری پیامبران گواه بوده‌ایم.'),
(2562, 21, 79, 'پس کیفیت اجرای آن حکم را به سلیمان فهماندیم و به هر یک از آن دو، داوریِ درست و دانشی ژرف عطا کردیم، و کوه‌ها و پرندگان را رام ساختیم که در تسبیح خدا با داود هم آوا می‌شدند و پیش‌تر نیز ما عطاکننده این مواهب بوده‌ایم.'),
(2563, 21, 80, 'و به او فنّ زره سازی را که به سود شماست آموختیم، تا شما را در جنگ هایتان از ضربات سخت سلاح‌ها حفظ کند. پس آیا شما سپاسگزار نعمت‌های ما خواهید بود؟'),
(2564, 21, 81, 'و برای سلیمان حتی تندباد را نیز رام ساختیم که به فرمان او به سوی سرزمینی که در آن برکت نهاده بودیم (سرزمین شام) روان می‌شد، و ما به هر چیزی داناییم.'),
(2565, 21, 82, 'و از جنّیان شرور کسانی بودند که به دریا فرو می‌رفتند تا برای او جواهرات استخراج کنند، و غیر از این، کارهای دیگری نیز برای او می‌کردند، و ما آنها را در خدمت او نگاه داشته بودیم و از گزند رساندن به ملک و مملکت او بازمی داشتیم.'),
(2566, 21, 83, 'و ایّوب را یاد کن، آن گاه که پروردگارش را ندا داد که به من آسیب رسیده است و تو مهربانترین مهربانانی.'),
(2567, 21, 84, 'پس دعای او را اجابت کردیم و آسیب‌هایی که بر او وارد شده بود برطرف ساختیم، و کسانش را که از دست داده بود به او بازگرداندیم و همانندشان را نیز به وی عطا کردیم (آنان را به دو برابر رساندیم) تا برای او از جانب ما رحمتی و برای پرستشگران مایه تذکّر و توجهی باشد.'),
(2568, 21, 85, 'و اسماعیل و ادریس و ذوالکفل را یاد کن که همه آنان از صبرپیشگان بودند.'),
(2569, 21, 86, 'و ما آنان را به رحمت خود درآوردیم و در سرپرستی خویش قرار دادیم، زیرا آنان از شایستگان بودند.'),
(2570, 21, 87, 'و یونس، همدم آن ماهی را یاد کن، هنگامی که از میان قومش بیرون رفت، در حالی که بر آنان به سبب کفرشان خشم گرفته بود، و گمان کرد که ما هرگز بر او تنگ نخواهیم گرفت، ولی ما او را به کام نهنگ فرو بردیم و او در آن تاریکی‌ها ندا داد که جز تو خدایی شایسته پرستش نیست که بتوان به او پناه برد، و تو از این که مورد اعتراض باشی منزّهی، به یقین من از ستمکاران بودم.'),
(2571, 21, 88, 'پس دعای او را اجابت کردیم و از شکم ماهی نجاتش دادیم و از آن اندوه رهایی اش بخشیدیم و مؤمنان را نیز این گونه از دشواری‌ها نجات می‌دهیم.'),
(2572, 21, 89, 'و زکریّا را یاد کن، آن گاه که پروردگارش را خواند که پروردگارا، مرا تنها مگذار و فرزندی به من ببخش که تو بهترین وارثانی.'),
(2573, 21, 90, 'پس دعای او را اجابت نمودیم و یحیی را بدو بخشیدیم و همسرش را به رغم این که سالخورده و نازا بود شایسته حمل کردیم. این نعمت را از آن روی به خاندان زکریّا بخشیدیم که آنان در کارهای نیک شتاب می‌کردند و ما را به امید رحمت و ترس از کیفر می‌خواندند و در برابر ما فروتن بودند.'),
(2574, 21, 91, 'و یاد کن از آن بانویی که دامن خود را پاک نگاه داشت و ما از روح خویش در او دمیدیم (او بدون علل طبیعی به فرزندی باردار شد) و او و پسرش مسیح را برای جهانیان نشانه‌ای قرار دادیم.'),
(2575, 21, 92, 'ای مردم به یقین این است نوع شما که همه یک امت هستید و یک هدف را دنبال می‌کنید پس بیش از یک پروردگار ندارید و پروردگار شما منم و امورتان در اختیار من است; پس مرا بپرستید.'),
(2576, 21, 93, 'آری، انسان‌ها همه امّتی یگانه‌اند که باید با پیروی از پیامبران خدا یک دین می‌داشتند و همه بر آیین توحید بودند، ولی آنان دین را میان خود قطعه قطعه کردند و هر کدام بخشی از آن را گرفته و بخش‌هایی را وانهادند، امّا بدانند که همگان برای حسابرسی اعمالشان به سوی ما باز می‌گردند.'),
(2577, 21, 94, 'پس هر کس برخی از کارهای شایسته را انجام دهد در حالی که به خدا و پیامبران او ایمان داشته باشد، تلاشش پوشیده و بی سپاس نمی‌ماند و ما آن را برای او ثبت می‌کنیم.'),
(2578, 21, 95, 'و بازگشتِ مردم دیاری که آن را نابود کرده‌ایم، به دنیا ناشدنی است. آنان هرگز نمی‌توانند بازگردند تا آنچه را از دست داده‌اند با ایمان و عمل شایسته به دست آورند.'),
(2579, 21, 96, 'این سنّت همچنان ادامه دارد تا این که راهِ بسته یأجوج و مأجوج گشوده شود و آنان از هر بلندی شتابان به سوی سایر مردم روانه شوند.'),
(2580, 21, 97, 'در این هنگام آن وعده راست و درست نزدیک شود، و به ناگاه دیدگان کسانی که کفر ورزیده‌اند، خیره گردد و گویند: ای وای بر ما! به راستی ما از این رویداد بی خبر بودیم و کسی ما را بدان آگاه نکرد، نه بلکه ما خود ستمکار بودیم که به دنیا دل بستیم و این حقیقت را به فراموشی سپردیم.'),
(2581, 21, 98, 'شما کفرپیشگان و آنچه جز خدا می‌پرستید، هیزم دوزخید، شما در آن وارد خواهید شد.'),
(2582, 21, 99, 'اگر اینها خدایانی شایسته پرستش بودند وارد دوزخ نمی‌شدند معبودان و پرستشگرانشان همگی در آن جا ماندگارند.'),
(2583, 21, 100, 'آنان در آن جا نعره و فریادی دارند و در آن جا ناشنوا خواهند بود.'),
(2584, 21, 101, 'امّا کسانی که پیش‌تر از جانب ما آن بهترین وعده (ورود در بهشت) به آنان داده شده است، اینانند که از آتش دوزخ دور نگاه داشته می‌شوند.'),
(2585, 21, 102, 'صدای آن را نمی‌شنوند، و آنان در آنچه دلخواهشان باشد جاودانه‌اند.'),
(2586, 21, 103, 'آن هراس بزرگ که با دمیدنِ در صور پدید می‌آید آنان را غمگین نمی‌کند، و فرشتگان با این نوید به دیدارشان می‌آیند که: این همان روزی است که آن را به شما وعده می‌دادند.'),
(2587, 21, 104, 'روزی که آسمان را درهم می‌پیچیم و آن را از دیده‌ها نهان می‌سازیم همچون صفحه‌ای که با طومار شدنش نوشته‌های درونش را نهان می‌سازد همان گونه که بارِ نخست آفریدگان را به سهولت پدید آوردیم، دوباره آنها را باز می‌گردانیم. ما این را وعده داده و وفای به آن را بر خود لازم دانسته‌ایم و قطعاً انجام دهنده آن خواهیم بود.'),
(2588, 21, 105, 'و به یقین، در زبور، کتاب آسمانی داود، پس از تورات، آن یادواره الهی نوشتیم که زمین را از شرک و گناه پاک خواهیم ساخت و بندگان شایسته من آن را به ارث خواهند برد.'),
(2589, 21, 106, 'به راستی، در حقایقی که در این سوره بیان شده است، برای پرستشگران خدا وسیله‌ای برای وصول به هدف خواهد بود.'),
(2590, 21, 107, 'و ما تو را جز رحمتی برای همه گروه‌های بشری نفرستادیم.'),
(2591, 21, 108, 'بگو: جز این نیست که به من وحی می‌شود که معبود شما معبودی یگانه است; پس آیا شما سر تسلیم فرود می‌آورید؟'),
(2592, 21, 109, 'پس اگر از گرایش به توحید روی برتافتند و تسلیم خدا نشدند بگو: من به همه شما به طور یکسان خطر شرک را اعلام کردم و نمی‌دانم آیا آنچه بدان تهدید شده‌اید نزدیک است یا دور.'),
(2593, 21, 110, 'به یقین او سخنی را که آشکارا در استهزای به دین بر زبان می‌رانید می‌داند و از نیرنگ‌هایی که بر ضد دین تدارک می‌بینید و آنها را نهان می‌دارید، باخبر است. پس اوست که به نتیجه اعمال شما و نزدیکی و دوریِ کیفرتان آگاه است.'),
(2594, 21, 111, 'و من نمی‌دانم، شاید آن تهدید وسیله آزمونی برای شما باشد تا آنچه را درباره دین در نهان خویش دارید آشکار گردد و نیز مهلتی باشد که تا چندی از متاع دنیا بهره مند شوید و بدان خام گردید.'),
(2595, 21, 112, 'پیامبر به خدا عرضه داشت: پروردگارا، میان من و آنان به حق داوری کن، و به مشرکان گفت: پروردگار ما همان خدای رحمان است که بر آنچه به ناروا درباره دینش می‌گویید، از او یاری خواسته می‌شود.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'ای مردم، از پروردگارتان پروا بدارید و از شرک و گناه دوری کنید; قطعاً زلزله‌ای که در آستانه قیامت روی می‌دهد پدیده‌ای بس بزرگ است.'),
(2597, 22, 2, 'روزی که آن را ببینید، هر شیردهنده‌ای پستان از دهان شیرخواره اش می‌گیرد و وحشت زده از او دور می‌شود، و هر بارداری از ترس، وضع حمل می‌کند، و مردم را مست می‌بینی در حالی که آنان مست شراب نیستند، ولی عذاب خدا سخت است که هوش از آدمیان می‌بَرد.'),
(2598, 22, 3, 'و از مردم کسانی اند که بدون هیچ دانشی، درباره خدا و صفات و افعال او به جدال می‌پردازند و از هر شیطان پلیدی پیروی می‌کنند.'),
(2599, 22, 4, 'بر شیطان چنین مقرّر شده است که هر کس ولایت او را بپذیرد و خود را تحت نفوذ او قرار دهد، وی را به بیراهه خواهد کشید و او را به عذابِ آتش برافروخته راه خواهد نمود.'),
(2600, 22, 5, 'ای مردم، اگر در رستاخیز و زنده شدن مردگان تردید دارید، به آفرینش خود بنگرید، ما شما را از خاکی بی جان آفریدیم، آن گاه از نطفه و سپس از خونی بسته شده و پس از آن از گوشتی [که گویی] جویده شده با آفرینشی تمام و ناتمام، تا برای شما آشکار کنیم که زنده شدن مردگان امکان پذیر است; و هر جنینی را که بخواهیم تا سرآمدی معین در رحم‌ها نگاه می‌داریم، سپس شما را در حالی که کودکی هستید بیرون می‌آوریم، آن گاه [شما را رشد می‌دهیم] تا به توانایی خود برسید; و از شما کسانی اند که پیش از رسیدن به کهنسالی جانشان را می‌ستانند، و از شما کسانی اند که به فروترین دوران عمر رسانده می‌شوند تا چندان فرتوت گردند که بعد از دانستن، چیزی ندانند. و زمین را نیز خاموش و مرده می‌بینی، پس چون آب بر آن بفرستیم، سخت به جنبش درآید و بر دمد و از هر نوع گیاه نیکو و شاداب برویاند.'),
(2601, 22, 6, 'این آفرینش و تدبیر که سرانجام به رستاخیز منتهی می‌شود بدان سبب است که خداوند حق مطلق است و باطل از او سر نمی‌زند، در حالی که آفرینش بدون رستاخیز بیهوده است، و نیز بدان سبب است که خدا پیوسته مردگان را حیات می‌بخشد پس برپا کردن رستاخیز بر او دشوار نیست و نیز این که او بر انجام هر کاری تواناست.'),
(2602, 22, 7, 'و نیز با توجه به این حقیقت است که قیامت آمدنی است و هیچ شکی در آن نیست و این که خدا همه کسانی را که در گورهایند برمی انگیزد.'),
(2603, 22, 8, 'امّا از میان مردم کسی است که درباره خدا و صفات او مجادله می‌کند بی آن که به دانشی برخاسته از دلیل عقلی متّکی باشد یا با هدایت و الهامی از جانب خدا دلش به نور معرفت روشن شده باشد و یا به کتابی آسمانی که روشنگر است تمسّک نموده باشد.'),
(2604, 22, 9, 'این در حالی است که او از سر تکبّر پهلوی خود را می‌گرداند و روی برمی تابد تا مردم را از راه خدا به گمراهی بیفکند. برای او در این جهان خواری و رسوایی است و روز قیامت عذاب آن آتش برافروخته را به او خواهیم چشانید.'),
(2605, 22, 10, '[به او گفته می‌شود:] این کیفر به سزای دستاوردی است که خود پیش فرستاده‌ای، و نیز از آن روست که خدا به بندگانش ستم نمی‌کند، بلکه به هر کسی آنچه را که درخور اوست می‌دهد.'),
(2606, 22, 11, 'و از میان مردم کسی است که خدا را یکسویه و در برخی شرایط عبادت می‌کند; پس اگر خیری به او رسد دلش به آن آرام می‌گیرد و به عبادت خدا و دین او دل خوش می‌کند، و اگر رنج و محنتی به او رسد روی می‌گرداند و از عبادت خدا باز می‌ایستد. او در دنیا و آخرت زیان دیده است و زیانکاری آشکار همین است.'),
(2607, 22, 12, 'به جای خدا چیزی را می‌پرستد که نه به او زیانی می‌رساند و نه به او سودی می‌بخشد. این است آن گمراهی که با حق فاصله‌ای دور و دراز دارد.'),
(2608, 22, 13, 'روز قیامت درباره معبودش می‌گوید: کسی که زیان او از سودش نزدیک‌تر است قطعاً بد سرپرست و بد همدمی است.'),
(2609, 22, 14, 'قطعاً خداوند کسانی را که ایمان آورده و کارهای شایسته کرده‌اند به بوستان‌هایی پردرخت که از زیر آنها نهرها روان است درمی آورَد. به یقین خدا آنچه را بخواهد انجام می‌دهد.'),
(2610, 22, 15, 'هر کس می‌پنداشت که خدا هرگز پیامبرش را در دنیا یاری نمی‌کند و دین او را رونق نمی‌بخشد و در آخرت نیز او را یاری نمی‌کند و بر او و پیروانش رحمت نمی‌آورَد، اکنون که شوکت او و کثرت پیروانش را می‌بیند و به خشم می‌آید باید ریسمانی به آسمان کشد، سپس خود را بدان حلق آویز کند، آن گاه بنگرد که آیا این کار خشم او را از بین می‌برد.'),
(2611, 22, 16, 'ما قرآن را که دارای آیاتی روشن و روشنگر است بدین سان که در این سوره آمده است فرو فرستاده‌ایم، ولی بدانید که قرآن بدون اراده خدا سبب هدایت نمی‌شود زیرا حقیقت این است که خدا هر که را بخواهد هدایت می‌کند.'),
(2612, 22, 17, 'کسانی که به محمّد ایمان آورده و کسانی که یهودی اند و صابئان و مسیحیان و زرتشتیان و کسانی که شرک ورزیده‌اند، قطعاً خدا روز قیامت میان آنان در آنچه اختلاف کرده‌اند داوری می‌کند و حقگرایانشان را از باطل گرایانشان جدا می‌سازد. به یقین، خدا بر هر چیزی گواه است.'),
(2613, 22, 18, 'آیا ندانسته‌ای که هر که در آسمان‌ها و هر که در زمین است، و نیز خورشید و ماه و ستارگان و کوه‌ها و درختان و جنبندگان در برابر عظمت و شکوه خدا خضوع می‌کنند؟ و آیا ندانسته‌ای که بسیاری از مردم پیشانی به خاک می‌سایند و برای او سجده می‌کنند و بسیاری هم از سجده به درگاه او خودداری میورزند و به همین سبب عذاب بر آنان حتمی شده است؟ اینانند که خدا خوار و حقیرشان می‌دارد، و هر که را خدا خوار دارد گرامی دارنده‌ای برای او نخواهد بود. قطعاً خدا هر چه بخواهد انجام می‌دهد.'),
(2614, 22, 19, 'اینان دو گروه متخاصمند که درباره پروردگارشان با یکدیگر در ستیز بودند; پس کسانی که یکتایی پروردگارشان را باور نکرده و به پیامبران او کفر ورزیده‌اند، جامه‌هایی آتشین برای آنان بریده شده و از بالای سرشان آب جوشان بر آنان ریخته می‌شود.'),
(2615, 22, 20, 'آنچه در درونشان است و پوستشان به وسیله آن پخته و ذوب می‌گردد.'),
(2616, 22, 21, 'و برای آنان گرزهایی آهنین است.'),
(2617, 22, 22, 'هرگاه بخواهند از آتش بیرون روند و از اندوه رهایی یابند در آن باز گردانده می‌شوند و [به آنان گفته می‌شود:] بچشید عذاب سوزان آتش را!'),
(2618, 22, 23, 'امّا کسانی که یکتایی پروردگارشان را باور کرده و به پیامبران او ایمان آورده و کارهای شایسته انجام داده‌اند، خدا آنان را به بوستان‌هایی پردرخت که از زیر آنها نهرها روان است درمی آورَد. در آن جا به دستواره‌هایی زرّین و به مروارید درشت آراسته می‌شوند و جامه شان در آن جا دیبای خالص است.'),
(2619, 22, 24, 'در آن جا به گفتنِ سخن پاک راهنمایی می‌شوند و به راه خداوندِ ستوده هدایت می‌گردند.'),
(2620, 22, 25, 'کسانی که به پیامبر کافر شده‌اند و پیوسته مردم را از راه خدا و مؤمنان را از ورود به مسجدالحرام ـ که ما آن را عبادتگاهی برای همه مردم قرار داده‌ایم به گونه‌ای که ساکنان آن جا و کسانی که از مناطق دیگر می‌آیند در این بهرهوری حقی یکسان دارند ـ باز می‌دارند، برای آنان عذابی دردناک خواهد بود، زیرا هر که در آن جا از روی ستم مردم را منحرف بخواهد، از عذابی دردناک به او خواهیم چشانید.'),
(2621, 22, 26, 'یاد کن زمانی را که جایگاه آن خانه (کعبه) را با وحیی که به ابراهیم کردیم محل عبادت او مقرر داشتیم، او را گفتیم: در پرستش من و انجام مراسم حج چیزی را شریک من مساز، و آن خانه را که جایگاه پرستش من است برای طواف کنندگان و کسانی که به نماز و عبادت می‌ایستند و برای رکوع کنندگان و سجده کنندگان پاک گردان.'),
(2622, 22, 27, 'و به او گفتیم: در میان مردم بانگ برآور تا برای انجام مراسم حج در مسجدالحرام حضور یابند. اگر آنان را فراخوانی، پیاده و سوار بر شترانی لاغرمیان که از هر راه دوری فرا می‌رسند نزد تو می‌آیند،'),
(2623, 22, 28, 'تا منافع مادّی و معنوی را که برایشان فراهم است بنگرند و نام خدا را در روزهایی معیّن (ایّام تَشریق) به هنگام قربانی بر دام‌هایی که خدا روزی آنان کرده است ببرند. پس، از گوشت آنها بخورید و از آن به نیازمندان و تهیدستان نیز غذا دهید.'),
(2624, 22, 29, 'سپس حاجیان باید آلودگی‌های خود را بزدایند و از ناخن‌ها و موهای خود بگیرند تا از احرام خارج شوند، و باید به نذرهایی که کرده‌اند وفا کنند و بر گِرد آن خانه کهن (کعبه) طواف نمایند.'),
(2625, 22, 30, 'اینهاست مناسک حج که برای ابراهیم و کسانی که پس از او می‌آیند تشریع شده است. و هر کس محرّمات الهی را بزرگ بشمارد و از ارتکاب آنها پرهیز کند، این نزد پروردگارش برای او بهتر است. دام‌ها ـ جز آنچه بر شما خوانده می‌شود ـ برای شما حلال شده است. پس از بت‌ها که پلیدند دوری کنید و از دام‌هایی که به نام آنها سربریده می‌شود اجتناب نمایید و از گفتار باطل بپرهیزید;'),
(2626, 22, 31, 'در حالی که به سوی خدا گرایش داشته و کسی را شریک او قرار ندهید. و هر کس برای خدا شریک قرار دهد، از مرتبه انسانیّت ساقط می‌شود و شیطان او را شکار خویش می‌کند. او در مَثَل مانند این است که از آسمان فرو افتد و پرندگان شکاری او را بربایند، یا باد او را به مکانی دوردست بیفکند.'),
(2627, 22, 32, 'حقیقت آن است که گفتیم، و هر کس شعائر الهی را بزرگ بشمارد پس حرمت شترانی را که برای قربانی علامت گذاری شده‌اند پاس دارد. دلی تقواپیشه دارد زیرا بزرگ شمردن شعایر الهی برخاسته از تقواپیشگی دل هاست.'),
(2628, 22, 33, '[حفظ حرمت شتران قربانی در ترک بهره برداری از آنها نیست، بلکه] برای شما در آنها سودهایی است که می‌توانید تا مدّتی که برای قربانی کردن آنها تعیین شده است از آنها استفاده برید; و زمان قربانی کردن آنها هنگامی است که به آن خانه کهن (کعبه) برسند.'),
(2629, 22, 34, 'نه تنها برای ابراهیم و امّت او، بلکه برای هر امّتی قربانیی مقرر داشتیم تا بر دام‌هایی که خدا روزیِ آنها کرده است، هنگام ذبح، نام او را ببرند. پس معبود شما و آنان معبودی یگانه است و آن خداست; از این رو با اخلاص در انجام مناسک حج و تقدیم قربانی‌ها، تسلیم فرمان او باشید. و کسانی را که به خدا آرامش یافته‌اند و در برابر او خاشعند نوید ده;'),
(2630, 22, 35, 'همانان که چون خدا نزدشان یاد شود دل هایشان می‌ترسد و بر ناگواری‌هایی که به آنان رسیده است شکیبایند و نماز را برپا می‌دارند و از آنچه روزیشان کرده‌ایم انفاق می‌کنند.'),
(2631, 22, 36, 'و شتران فربهی را که برای قربانی حج آماده کرده‌اید، برای شما از شعائر الهی قرار داده‌ایم. برای شما در آنها خیری است. پس در حالی که بر دست‌های خود ایستاده‌اند نام خدا را بر آنها ببرید و نحرشان کنید و چون به پهلو درافتادند و جان دادند، خود از آنها بخورید و به تهیدستِ قناعت پیشه و مستمندی که به انتظار بخشش نزد شما آید از آن غذا دهید. این گونه، شتران را برای شما رام ساختیم، باشد که سپاسگزاری کنید.'),
(2632, 22, 37, 'هرگز گوشت و خون آنها به خدا نمی‌رسد، زیرا او از همه چیز بی نیاز است، امّا تقوای شما به او می‌رسد، چرا که پرهیزکاران به درگاه او تقرّب می‌یابند. بدین سان خدا آنها را برای شما رام ساخت تا شما را با تقدیم قربانی‌ها به اطاعت و تقرب خویش رهنمون شود و شما باید او را به پاس این که راهتان نمود به بزرگی یاد کنید. و به نیکوکاران نوید ده.'),
(2633, 22, 38, 'قطعاً خداوند شرّ مشرکان را از کسانی که ایمان آورده‌اند دور می‌کند، زیرا آنان را دوست می‌دارد، ولی مشرکان را چون به امانت الهی (فطرت خداجویی خویش) خیانت کرده و نعمت‌های او را ناسپاسی نموده‌اند دوست نمی‌دارد، که او هیچ خیانتکار ناسپاسی را دوست نمی‌دارد.'),
(2634, 22, 39, 'اینک به آن مؤمنانی که مورد هجوم قرار می‌گیرند، از جانب خدا رخصت داده شده است که به جهاد اقدام کنند. این بدان سبب است که آنان مورد ستم قرار گرفته‌اند، و قطعاً خدا بر یاری آنان تواناست و پیروزشان خواهد کرد.'),
(2635, 22, 40, 'همانان که به ناحق از دیارشان بیرون رانده شدند; اخراجشان سببی جز این نداشت که می‌گفتند پروردگار ما خداست. و اگر خدا با حکم جهاد، برخی از مردم را به وسیله برخی دیگر دفع نمی‌کرد، صومعه‌های راهبان و کلیساهای مسیحیان و معابد یهودیان و مساجد مسلمانان که در آنها نام خدا بسیار برده می‌شود ویران گشته بود، و قطعاً خدا کسانی را که او را یاری می‌دهند یاری خواهد کرد، چرا که خدا نیرومند و شکست ناپذیر است.'),
(2636, 22, 41, 'همانان که اگر در زمین به ایشان قدرت دهیم تا بتوانند روش زندگی اجتماعی خود را برگزینند، جامعه‌ای تشکیل می‌دهند که همگان در آن نماز را برپا می‌دارند و زکات می‌پردازند و به آنچه شایسته است فرمان می‌دهند و از آنچه ناشایسته است نهی می‌کنند. و قطعاً خدا مؤمنان را یاری می‌کند و آنان را بر کفرپیشگان پیروز می‌گرداند چرا که فرجام امور از آن خداست.'),
(2637, 22, 42, 'و اگر تو را ـ ای پیامبر ـ دروغ پرداز می‌شمرند غمگین مباش، چرا که تکذیب پیامبران پیشینه‌ای دور و دراز دارد; قوم نوح و عادیان و ثمودیان نیز پیامبرانشان را دروغگو شمردند،'),
(2638, 22, 43, 'و همچنین قوم ابراهیم و قوم لوط،'),
(2639, 22, 44, 'و نیز اهل مدین، و موسی هم از سوی فرعونیان تکذیب شد; پس به کافران مهلت دادم، سپس آنها را به عذاب گرفتم. پس انکار من در برابر کفر و تکذیب آنان چگونه بود و چه عذابی بر سرشان فرود آوردم.'),
(2640, 22, 45, 'چه بسیار شهرهایی را که مردمش ستمکار بودند نابود کردیم، و اینک دیوار خانه هایشان بر سقف‌های آن فرو ریخته است، و چه بسیار چاه‌هایی که با نابودی صاحبانش متروک شده و چه بسیار قصرهای گچکاری شده که ساکنانش به هلاکت رسیده‌اند.'),
(2641, 22, 46, 'پس آیا کسانی که تو را تکذیب می‌کنند در زمین گردش نکرده و با دیده عبرت به این ویرانه‌ها ننگریسته‌اند تا برایشان دل‌هایی پدید آید که با آنها در فرجام کفر و شرک بیندیشند یا گوش‌هایی که با آنها حقایق را بشنوند و ایمان بیاورند؟ به یقین دیده‌ها نابینا نیست ولی دل‌هایی که در سینه هاست کور است.'),
(2642, 22, 47, 'و از تو به مسخره می‌خواهند که در آوردن عذابی که بدان تهدید شده‌اند شتاب کنی. بدانند که خدا هرگز وعده اش را در مورد عذاب آنان خلاف نمی‌کند و در این کار شتاب نمیورزد، زیرا یک روز با هزار سالی که شما برمی شمرید نزد پروردگارت همسان و برابر است.'),
(2643, 22, 48, 'و چه بسیار مردم شهرهایی که ستمکار بودند مهلتشان دادم و سپس آنان را به عذاب گرفتم. اینک از چه رو در عذاب اینان شتاب کنم در حالی که بازگشت همگان فقط به سوی من است و من به حسابشان رسیدگی خواهم کرد.'),
(2644, 22, 49, 'بگو: ای مردم، جز این نیست که من برای شما پیامبری هشداردهنده‌ام و آنچه نیکبختی شما را موجب می‌شود و هر آنچه تیره بختی شما را در پی دارد به روشنی برای شما بیان می‌کنم.'),
(2645, 22, 50, 'پس کسانی که ایمان آورده و کارهای شایسته کرده‌اند آمرزش و رزقی ارجمند خواهند داشت که در بهشت نصیبشان خواهد شد.'),
(2646, 22, 51, 'و کسانی که در نابودی آیات ما نهایت تلاش خود را به کار بسته‌اند و به گمان خود می‌خواهند ما را سخت درمانده کنند، آنان اهل دوزخ خواهند بود.'),
(2647, 22, 52, 'و پیش از تو هیچ رسول و پیامبری را به سوی مردم نفرستادیم مگر این که وقتی برنامه هایش را برای پیشرفت رسالت خود آماده می‌ساخت و طرح‌هایی را برای تأمین اهدافش پی ریزی می‌کرد، شیطان درصدد برمی آمد تا با تحریک مردم اهداف او را به تباهی کشد، ولی خدا آنچه را شیطان القا می‌کرد محو می‌نمود، سپس با پیروز ساختن پیامبر یا رسول خود، آیات خویش را استوار می‌کرد، چرا که خداوند دانا و حکیم است.'),
(2648, 22, 53, 'خدا این فرصت را به شیطان داده است که آنچه را او بر ضد پیامبران القا می‌کند، برای کسانی که در دل هایشان بیماری شک و تردید است و نیز برای سنگدلان وسیله آزمونی قرار دهد، و قطعاً ستمکاران در مسیری خلاف هستند و با حق فاصله‌ای دور و دراز دارند.'),
(2649, 22, 54, 'آری، خدا ترفندهای شیطان را می‌زداید و آیات خود را استوار می‌سازد تا کسانی که از دانش برخوردار شده‌اند دریابند که آنچه پیامبران دنبال می‌کنند حق است و از جانب پروردگار توست، و آن را باور کنند و دل هایشان در برابر آن نرم و خاشع گردد، و قطعاً خداوند کسانی را که ایمان آورده‌اند به راهی راست هدایت می‌کند.'),
(2650, 22, 55, 'و کسانی که کفر ورزیده‌اند (سران کفرپیشه قریش) همواره از حقانیت قرآن در شکّند، تا این که به ناگاه قیامت بر آنان در رسد یا عذاب روزی که فردایی ندارد به سراغشان بیاید.'),
(2651, 22, 56, 'روزی که قیامت فرا رسد، آشکار شود که مالکیّت و فرمانروایی جهان هستی از آنِ خداست. او میان مردم داوری می‌کند; پس کسانی که ایمان آورده و کارهای شایسته کرده‌اند، در بوستان‌هایی پرنعمت خواهند بود.'),
(2652, 22, 57, 'و کسانی که کافر شده و آیات ما را دروغ انگاشته‌اند، اینانند که برایشان عذابی خوارکننده خواهد بود.'),
(2653, 22, 58, 'و کسانی که در راه خدا هجرت کرده، آن گاه در این مسیر کشته شده یا مرده‌اند، قطعاً خداوند به آنان رزقی نیکو عطا می‌کند، و بی گمان خداست که بهترین روزی دهندگان است.'),
(2654, 22, 59, 'بی تردید آنان را به جایگاهی که آن را می‌پسندند درمی آورَد، و به راستی خدا به آنچه مهاجران راه خدا می‌پسندند آگاه و در کیفر دشمنانشان بردبار است.'),
(2655, 22, 60, 'حقیقت همین است که گفته شد، و هر کس مانند عقوبتی که بر او رفته است عقوبت کند، به شرط این که به ناروا عقوبت شده باشد، خدا به او رخصت مقابله به مثل می‌دهد و او را بدین وسیله یاری می‌کند، چرا که خدا بسیار بخشاینده و آمرزنده است.'),
(2656, 22, 61, 'این قانون مقابله به مثل برخاسته از سنّت خدا در جهان آفرینش است; چنان که خدا شب را در روز فرو می‌برد و بر آن چیره می‌کند و روز را در شب فرو می‌برد و بر آن چیره می‌سازد، و نیز بدان سبب است که خداوند به گفتار و کردار ستمدیدگان شنوا و بیناست.'),
(2657, 22, 62, 'این تصرفاتی که از جانب خدا در تکوین و تشریع صورت می‌پذیرد بدان سبب است که خدا پایدار و از هر باطلی پیراسته است و آنچه جز او می‌خوانند باطل و بی ثبات است، و بدان سبب است که خدا بلند مرتبه و بزرگ است.'),
(2658, 22, 63, 'آیا ندیده‌ای که خدا از آسمان آبی فرو فرستاده است، آن گاه زمین به وسیله آن سبز و خرّم می‌شود؟ آری، علم خدا بر ژرفای هر چیزی احاطه دارد و از کُنه موجودات با خبر است.'),
(2659, 22, 64, 'آنچه در آسمان‌ها و آنچه در زمین است از آنِ اوست، و به راستی خدا از آنچه مردم انجام می‌دهند بی نیاز است و کارهایش همه زیباست و بر آن ستایش می‌شود.'),
(2660, 22, 65, 'نشانه‌های قدرت خدا در سراسر جهان هستی آشکار است; مگر ندیده‌ای که خدا آنچه را در زمین است و نیز کشتی‌ها را که به فرمان او در دریا روانند، برای شما رام ساخته و آسمان و آنچه را در آن است (صاعقه‌ها و سنگ‌های آسمانی و...) نگاه می‌دارد از این که بر زمین افتند، مگر این که خود اذن دهد. قطعاً خدا به مردم رئوف است و بر آنان ترحم می‌کند.'),
(2661, 22, 66, 'اوست آن که به شما حیات بخشیده است، سپس شما را می‌میراند، آن گاه در قیامت به شما حیاتی دوباره می‌بخشد. اینها برای شما نعمت‌هایی ارجمندند ولی انسان در برابر این نعمت‌ها بسیار ناسپاس است.'),
(2662, 22, 67, 'ما برای هر یک از امّت‌های گذشته عبادتی فراخور فهم و درک آنان قرار دادیم که همان را انجام می‌دادند، و برای تو و امّتت نیز عباداتی خاص قرار داده‌ایم; پس نباید کافران با تو در این امر کشمکش کنند و بگویند بدعت آورده‌ای. تو به مخالفت آنان توجّه مکن و مردم را به سوی پروردگارت فراخوان که قطعاً بر راهی راست که بدان هدایت شده‌ای قرار داری.'),
(2663, 22, 68, 'و اگر باز هم با تو به جدال پرداختند با آنان کشمکش مکن و بگو: خدا به آنچه می‌کنید داناتر است.'),
(2664, 22, 69, 'خدا در روز قیامت میان شما درباره آنچه با پیامبر بر سر آن مخالفت می‌کردید داوری می‌کند.'),
(2665, 22, 70, 'آیا ندانسته‌ای که خدا آنچه را در آسمان‌ها و زمین است می‌داند و از کارهای شما نیز آگاه است؟ قطعاً آنچه را خدا می‌داند با همه گستردگی اش در کتابی ثبت گردیده و یقیناً ثبت آنها بر خدا آسان است.'),
(2666, 22, 71, 'و مشرکان جز خدا چیزهایی را می‌پرستند که خدا برهانی با آنها فرو نفرستاده است و چیزهایی که خود به آنها علمی ندارند. آنان ستمکارند و ستمکاران هیچ یاوری نخواهند داشت.'),
(2667, 22, 72, 'و چون آیات ما که دلالت آنها [بر توحید] به خوبی روشن است، بر آنان تلاوت شود، در چهره کسانی که کافر شده‌اند، آثار انکار و ناخوشایندی را می‌بینی. نزدیک است به کسانی که آیات ما را بر آنان تلاوت می‌کنند، حمله برند. به آنان بگو: آیا شما را به بدتر از این آگاه کنم؟ این همان آتش دوزخ است که خدا آن را به کسانی که کافر شده‌اند وعده داده و بد سرانجامی است.'),
(2668, 22, 73, 'ای مردم مَثَلی زده شده است، بدان گوش فرا دهید: کسانی که شما آنها را به جای خدا می‌خوانید هرگز نمی‌توانند مگسی بیافرینند، هر چند برای آفریدن آن همداستان شوند; و اگر مگس چیزی از آنها برباید نمی‌توانند آن را بازپس گیرند. هم خدایانی که در پی آن مگس برآیند ناتوانند و هم مگسی که در پی آنند ناتوان است.'),
(2669, 22, 74, 'خداست که تدبیرکننده جهان هستی است و تنها او شایسته پرستش است، ولی مشرکان خدا را آن گونه که درخور منزلت اوست ارج ننهادند و به غیر او روی آوردند. بی گمان، خدا نیرومند و شکست ناپذیر است.'),
(2670, 22, 75, 'خدا از میان فرشتگان فرستادگانی شایسته را برمی گزیند تا وحی الهی را به پیامبران برسانند، و از میان مردم نیز شایستگان رسالت را انتخاب می‌کند تا پیام خدا را به مردم ابلاغ کنند چرا که خداوند درخواست مردم را که در پی کمالند می‌شنود و نیازشان به رهنمودها را می‌بیند.'),
(2671, 22, 76, 'خدا آنچه را که پیش روی فرشتگان و پیامبران است و آنچه را که پشت سرشان است می‌داند. چگونه ممکن است کارهای آنان بر خدا پوشیده بماند در صورتی که همه امور به او باز گردانده می‌شود.'),
(2672, 22, 77, 'ای کسانی که ایمان آورده‌اید، رکوع و سجده کنید و پروردگارتان را بپرستید و کار خیر انجام دهید، باشد که نیکبخت شوید.'),
(2673, 22, 78, 'ای کسانی که ایمان آورده‌اید، در آنچه مربوط به خداست ـ چنان که درخورِ تلاش برای اوست ـ تلاش کنید. او شما را برگزیده و در دینتان که همان آیین پدرتان ابراهیم است، بر شما هیچ دشواری و تنگنایی قرار نداده است. خدا شما را پیش از نزول قرآن و در این قرآن اهل تسلیم نامیده و اسلام شما را پذیرفته است تا هم پیامبر بر شما گواه باشد و هم شما بر اعمال مردم گواه باشید. پس نماز را برپا دارید و زکات بدهید و در همه حال به خدا تمسّک جویید و او را اطاعت کنید که او سرپرست شماست، او خوب سرپرست و نیکو یاوری است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'به راستی که مؤمنان به سعادت دست یافتند،'),
(2675, 23, 2, 'آنان که در نمازشان فروتن‌اند،'),
(2676, 23, 3, 'و آنان که از کار بیهوده رویگردانند،'),
(2677, 23, 4, 'و آنان که اموال خود را به وسیله انفاق پاک می‌سازند،'),
(2678, 23, 5, 'و آنان که دامن خود را حفظ می‌کنند،'),
(2679, 23, 6, 'مگر از همسرانشان یا کنیزانی که مالک آنها شده‌اند، که آنان نکوهش نمی‌شوند;'),
(2680, 23, 7, 'پس کسانی که راه دیگری جز آنچه مقرّر شد طلب کنند، آنان از مرز الهی گذشته‌اند;'),
(2681, 23, 8, 'و مؤمنان کسانی اند که امانت‌های یکدیگر را پاس می‌دارند و به پیمان خود وفادارند،'),
(2682, 23, 9, 'و آنان که همواره بر نمازهای واجب خود مواظبت می‌کنند،'),
(2683, 23, 10, 'اینانند میراث بران،'),
(2684, 23, 11, 'همانان که بهشت برین را به ارث می‌برند و در آن جاودانه‌اند.'),
(2685, 23, 12, 'همانا ما انسان را نخستین بار از عصاره گِلی آفریدیم.'),
(2686, 23, 13, 'سپس نسل او را از نطفه مقرر داشتیم; آن نطفه را در جایگاهی استوار قرار دادیم.'),
(2687, 23, 14, 'سپس آن نطفه را لخته خونی ساختیم، آن گاه آن لخته خون را به صورت پاره گوشتی [که گویی] جویده شده درآوردیم; پس آن قطعه گوشت را استخوان‌هایی چند ساختیم، آن گاه استخوان‌ها را با گوشت پوشاندیم، سپس آن را آفریده‌ای دیگر ساختیم (به او حقیقت انسانی بخشیدیم). پس خداوند که بهترین آفرینندگان است پرخیر و برکت است.'),
(2688, 23, 15, 'سپس شما بعد از این، قطعاً خواهید مرد.'),
(2689, 23, 16, 'و آن گاه به یقین شما در روز قیامت برانگیخته خواهید شد.'),
(2690, 23, 17, 'و به راستی ما بر فراز شما هفت آسمان که راه‌های نزول فرمان خدا، فرود و صعود فرشتگان و بالا رفتن اعمال بندگان است، پدید آوردیم و ما از آفریدگان بی خبر نیستیم.'),
(2691, 23, 18, 'و از آسمان آبی به اندازه فرو فرستادیم و آن را در دل زمین جای دادیم و قطعاً ما بر بُردن آن تواناییم.'),
(2692, 23, 19, 'پس برای شما به وسیله آن بوستان‌هایی از درختان خرما و انگور پدید آوردیم که در آنها برای شما میوه‌های فراوان است و از آنها می‌خورید.'),
(2693, 23, 20, '۲۰ و نیز به وسیله آن درخت زیتون را پدید آوردیم که از کوه سینا برمی آید و میوه ای می‌رویاند که دارای روغن است و خورشی است برای خورندگان.'),
(2694, 23, 21, 'به یقین برای شما در دام‌ها (شتر، گاو، گوسفند) نشانه‌ای است بر این که امور بندگان را ما تدبیر می‌کنیم و به آنان مهربانیم; از شیری که در شکم آنهاست برای نوشیدن در اختیار شما می‌نهیم، و نیز برای شما در آن دام‌ها سودهای بسیار است; از آنها بهره می‌برید و از گوشتشان می‌خورید.'),
(2695, 23, 22, 'و در خشکی بر شتران و در دریا بر کشتی‌ها سوارتان می‌کنند.'),
(2696, 23, 23, 'همانا نوح را به سوی مردم زمانش به رسالت فرستادیم. او بی درنگ به سراغ آنان رفت و گفت: ای قوم من، خداوند یکتا را بپرستید. شما را جز او معبودی شایسته پرستش نیست. آیا از کیفر او پروا نمی‌کنید که به یکتایی او کفر میورزید.'),
(2697, 23, 24, 'مهتران قومش که کافر بودند گفتند: ای مردم، این جز بشری مانند شما نیست. می‌خواهد بدین وسیله بر شما برتری جوید. اگر از غیب پیامی به او می‌رسید، می‌بایست به شما نیز که بشری مانند او هستید برسد، و اگر خدا می‌خواست پیامی به ما برساند، قطعاً فرشتگانی را فرو می‌فرستاد. علاوه بر این، آیین او سابقه‌ای ندارد و ما در میان پدران نخستین خود نشنیده‌ایم که این آیین را برگزیده باشند.'),
(2698, 23, 25, 'او مردی است که جنون دامنگیرش شده است، پس تا مدّتی کوتاه در انتظارش باشید، شاید بهبود یابد یا مرگ به سراغش آید.'),
(2699, 23, 26, 'نوح گفت: پروردگارا، من مردم را به عذاب هشدار دادم، ولی آنان عذاب را دروغ شمردند، اینک تو مرا به وسیله همان عذابی که چون بدان هشدارشان دادم دروغگویم پنداشتند، یاری ده.'),
(2700, 23, 27, 'پس هنگامی که خواستیم عذاب بفرستیم، به او وحی کردیم که آن کشتی را زیر نظر ما و بر طبق آنچه به تو وحی می‌کنیم، بساز، و چون حکم ما در مورد نابودی کفرپیشگان در رسید، و به نشانه عذاب، آب از آن تنور جوشید، از هر حیوانی، دو تا، یک نر و یک ماده، با کسانت (مؤمنان و خانواده ات) جز کسانی از آنان که پیش‌تر وعده عذاب در حقشان ثابت شده است، در آن کشتی سوار کن، و با من درباره نجات کسانی که ستم کرده‌اند سخن مگوی، چرا که آنان غرق خواهند شد و این مقدر شده است.'),
(2701, 23, 28, 'پس هنگامی که تو و کسانی که همراه تواَند بر کشتی نشستید، بگو: ستایش خدای را که ما را از این مردم ستم پیشه رهایی بخشید.'),
(2702, 23, 29, 'و بگو: پروردگارا، مرا از طوفان نجات ده و بر خشکی فرود آور، فرودی که قرین خیر و برکت باشد، که تو بهترین نجات دهندگان و نیکوترین فرودآورندگانی.'),
(2703, 23, 30, 'به یقین در این داستان نشانه‌هایی [بر یکتایی خدا و ربوبیّت او] است. و دعوت نوح و پیامدهای آن آزمونی از جانب ما بود و ما قطعاً آزمایش کننده بودیم.'),
(2704, 23, 31, 'سپس از پی آنان نسلی دیگر (قوم ثمود) را پدید آوردیم.'),
(2705, 23, 32, 'و در میانشان پیامبری از خودشان به رسالت فرستادیم با این پیام که: خداوند یکتا را بپرستید که شما را جز او معبودی شایسته پرستش نیست; آیا از کیفر او پروا نمی‌کنید که به یکتایی او کفر میورزید؟'),
(2706, 23, 33, 'و مهتران قومش که به خدا کافر شده و دیدار آخرت را دروغ شمرده بودند، و ما در زندگی دنیا آنان را از نعمت فراوان برخوردار ساخته بودیم گفتند: این مرد جز بشری مانند شما نیست; از آنچه شما می‌خورید او نیز می‌خورد و از آنچه شما می‌آشامید او نیز می‌آشامد. اگر پیامی از غیب به او رسیده بود به شما نیز می‌رسید;'),
(2707, 23, 34, 'و سوگند یاد می‌کنیم که اگر از بشری مثل خودتان که هیچ امتیازی بر شما ندارد پیروی کنید، قطعاً در آن صورت زیانکار خواهید بود.'),
(2708, 23, 35, 'آیا او به شما وعده می‌دهد که وقتی مردید و خاک گشتید و استخوانی چند شدید، حیاتی دوباره می‌یابید و از گورها برای حساب و جزا بیرون آورده می‌شوید؟'),
(2709, 23, 36, 'دور است دور آنچه شما را به آن وعده می‌دهند.'),
(2710, 23, 37, '[بدانید که] زندگی جز این زندگی ما در دنیا نیست که پیوسته نسلی از ما می‌میرد و نسلی دیگر پدید می‌آید، و ما برانگیخته نمی‌شویم.'),
(2711, 23, 38, 'او از جانب خدا رسالتی ندارد بلکه مردی است که سخن دروغینی را که خود ساخته به خدا بسته است، و $ما به او ایمان نخواهیم آورد.'),
(2712, 23, 39, 'پیامبرشان گفت: پروردگارا، من مردم را به عذاب هشدار دادم و آنان عذاب را دروغ شمردند، اینک تو مرا به وسیله همان عذابی که چون بدان انذارشان کردم دروغگویم پنداشتند، یاری ده.'),
(2713, 23, 40, 'خدا در پاسخ او فرمود: قطعاً پس از اندک زمانی با مشاهده عذاب پشیمان خواهند شد.'),
(2714, 23, 41, 'آن گاه صیحه‌ای مهیب آنان را به حق فرو گرفت، و آنها را همچون خاشاکِ سیلاب‌ها قرار دادیم. پس دوری از رحمت خدا بر ستم پیشگان باد.'),
(2715, 23, 42, 'سپس از پی آنان نسل‌های دیگری پدید آوردیم.'),
(2716, 23, 43, 'هیچ امّتی بر سرآمدی که برای آن مقدّر شده است سبقت نمی‌گیرد که زودتر از آن منقرض شود و از آن هم به تأخیر نمی‌افتد که دیرتر از آن از میان برود.'),
(2717, 23, 44, 'آری، امّت‌هایی را پس از نوح پدید آوردیم و فرستادگان خود را پیاپی به رسالت فرستادیم. هرگاه برای راهنمایی امّتی پیامبر آنان آمد، او را دروغگو شمردند; ما نیز آن امت‌ها را یکی پس از دیگری آورده و به وسیله عذاب هلاکشان ساختیم و از آنان چیزی باقی نگذاشتیم جز این که آنها را داستان‌هایی بر سر زبان‌ها نهادیم. پس دوری از رحمت خدا بر مردمی باد که ایمان نمی‌آورند.'),
(2718, 23, 45, 'سپس موسی و برادرش هارون را با معجزاتی که به آنان دادیم و با برهانی روشن به رسالت فرستادیم.'),
(2719, 23, 46, 'آن دو را به سوی فرعون و مهتران قومش گسیل داشتیم، ولی آنان گردنکشی کردند و رسالت آنها را نپذیرفتند و مردمی بودند که بر دیگران سلطه یافته بودند.'),
(2720, 23, 47, 'از این رو گفتند: آیا به دو بشر که مثل خود ما هستند و قومشان ما را بندگی می‌کنند ایمان بیاوریم؟'),
(2721, 23, 48, 'در نتیجه، موسی و هارون را تکذیب کردند، پس از زمره هلاک شدگان گشتند.'),
(2722, 23, 49, 'و همانا به موسی و هارون کتاب آسمانی تورات را دادیم، باشد که قومشان بدین وسیله هدایت یابند.'),
(2723, 23, 50, 'و عیسی بن مریم و مادرش را نشانه‌ای بر ربوبیّت خویش قرار دادیم و آن دو را در مکانی مرتفع و وسیع که جای زندگی و دارای آبی جاری بود سکونت دادیم.'),
(2724, 23, 51, 'ما به فرستادگان خود گفتیم: ای پیامبران، از چیزهای پاکیزه بخورید و استفاده برید و به شکرانه نعمت‌های الهی کاری شایسته کنید; قطعاً من به آنچه می‌کنید به خوبی آگاهم، مبادا از فرمان من روی برتابید.'),
(2725, 23, 52, 'و ای مردم به یقین این است نوع شما که همه یک امت هستید و یک هدف را دنبال می‌کنید پس بیش از یک پروردگار ندارید و منم پروردگار شما، پس همه از من پروا کنید.'),
(2726, 23, 53, 'ولی مردم دین و کتابی را که به وسیله پیامبران به آنان عرضه شده بود قطعه قطعه کردند و آن را میان خود تقسیم نمودند و هر کدام کتاب و دینی را برگزیدند و هر گروهی به آنچه در اختیار داشت شادمان بود.'),
(2727, 23, 54, 'پس ای پیامبر، آنان را تا هنگامی که عذاب الهی در رسد، در ورطه جهلشان رها کن.'),
(2728, 23, 55, 'آیا این کفرپیشگان مرفّه می‌پندارند این مال و اولادی که به آنان می‌دهیم و بدین وسیله به آنان مدد می‌رسانیم،'),
(2729, 23, 56, 'از آن روست که آنان را بزرگ می‌شمریم و می‌خواهیم در دادنِ نعمت‌ها به آنان شتاب کنیم؟! نه، این گونه نیست، آنان درک نمی‌کنند که حقیقت چیست. ما بدین وسیله آنان را سرگرم می‌کنیم تا همچنان در طغیان خود باقی بمانند.'),
(2730, 23, 57, 'آری، مال و اولاد کافران وسیله خیری برای آنان نیست. قطعاً کسانی که بیم آن دارند که از سعادت محروم شوند، چون از خشم پروردگارشان می‌هراسند،'),
(2731, 23, 58, 'و آنان که به آیات پروردگارشان ایمان می‌آورند،'),
(2732, 23, 59, 'و کسانی که به پروردگارشان شرک نمیورزند،'),
(2733, 23, 60, 'و آنان که آنچه را انفاق می‌کنند، در حالی انفاق می‌کنند که دل هایشان ترسان است از این که به سوی پروردگارشان بازمی گردند،'),
(2734, 23, 61, 'اینانند که در انجام کارهای نیک شتاب می‌کنند و به سوی آنها بر یکدیگر پیشی می‌گیرند.'),
(2735, 23, 62, 'و ما هیچ کس را جز به اندازه توانش تکلیف نمی‌کنیم، و نزد ما کتابی است که اعمال همگان را بی کم و کاست و بدون هیچ گونه اشتباه آشکار می‌سازد و براساس آن داوری می‌شود; از این رو در دادن سزا، ستمی به آنان نخواهد شد.'),
(2736, 23, 63, 'بلکه دل‌های کافران از این اوصافی که برای مؤمنان گفتیم سخت در غفلت است، و آنان کارهایی ناروا جز اینها دارند که به آنها می‌پردازند، و همین کارهاست که آنان را از آن صفات و اعمال پسندیده باز می‌دارد.'),
(2737, 23, 64, 'آنان روزگارشان را این گونه سپری می‌کنند، تا وقتی که مرفهانشان را به عذاب گرفتار ساختیم، ناگهان ناله و فریاد سر می‌دهند و درخواست کمک می‌کنند.'),
(2738, 23, 65, 'به آنان خطاب می‌شود: امروز ناله و فریاد سر ندهید که قطعاً از سوی ما یاری نخواهید شد.'),
(2739, 23, 66, 'زیرا آیات من پیوسته بر شما تلاوت می‌شد ولی شما از آن روی می‌گرداندید و به عقب باز می‌گشتید.'),
(2740, 23, 67, 'در حالی که با این کارتان کبر و بزرگ بینی خویش را نمایان می‌ساختید و شب هنگام [در بزم‌های خود] درباره قرآن یاوه سرایی می‌کردید.'),
(2741, 23, 68, 'آیا در این قرآن، نیندیشیده‌اند تا بدانند آن از جانب خداست؟ مگر برای آنان پیامی آمده که برای پدران نخستینشان نیامده است تا بگویند اگر این پیام حق است و از سوی خداست چرا برای پیشینیان فرستاده نشد؟'),
(2742, 23, 69, 'مگر پیامبر خود محمّد را که عمری با آنان زیسته است نشناخته‌اند و از درستکاری و راستگویی او بی خبرند که رسالتش را انکار می‌کنند؟!'),
(2743, 23, 70, 'آیا می‌گویند جنون دامنگیر او شده است؟ نه این گونه نیست، بلکه آنچه او آورده است حق است و بیشتر آنان حق را چون با هواهای نفسانی خود سازگار نمی‌بینند خوش نمی‌دارند.'),
(2744, 23, 71, 'اگر حق پیرو خواهش‌های آنان بود، قطعاً جهان هستی دگرگون می‌شد و آسمان‌ها و زمین و کسانی که در آنها بودند، تباه می‌شدند. ما هرگز حق را پیرو خواسته‌های آنان قرار نداده‌ایم، بلکه به آنان کتابی داده‌ایم که یادآور دینی درخور آنان است و باورها و رفتارهای درست را به آنان گوشزد می‌کند، ولی آنان با گریز از این کتاب، از دین واقعی خود رویگردانند.'),
(2745, 23, 72, 'مگر از آنان خراجی می‌خواهی [که به تو ایمان نمی‌آورند]؟ پاداش تو بر عهده خداست و قطعاً پاداش پروردگارت بهتر است و او بهترین روزی دهندگان است.'),
(2746, 23, 73, 'به یقین، تو آنان را به راهی راست و دینی درست فرا می‌خوانی.'),
(2747, 23, 74, 'و آنان که آخرت را باور ندارند، قطعاً از این راه منحرفند و به این دین پشت می‌کنند.'),
(2748, 23, 75, 'و اگر کافران را مورد رحمت قرار می‌دادیم و رنجی را که به عنوان عذاب دامنگیرشان شده است برطرف می‌ساختیم، باز هم در راه حق گام نمی‌نهادند، بلکه بر سرکشی خود پای می‌فشردند و همچنان در طغیان خویش سرگردان بودند. آری، نه رحمت الهی آنان را به راه می‌آورد و نه عذاب هشیارشان می‌کند.'),
(2749, 23, 76, 'به یقین ما آنان را به رنج قحطی و تنگی معیشت گرفتار ساختیم، ولی نه در برابر پروردگارشان فروتنی کردند، نه هرگز در پیشگاه او خاضع می‌شوند.'),
(2750, 23, 77, 'همواره با حق در ستیزند، تا وقتی که آنان را بمیرانیم و دری از عذابی سخت به رویشان بگشاییم، در آن جا ناگهان از دستیابی به هر خیری ناامید می‌شوند،'),
(2751, 23, 78, 'خداست که برای شما گوش‌ها و چشم‌ها پدید آورد تا بشنوید و ببینید، و دل‌ها پدید آورد تا بفهمید و دریابید، ولی شما اندک سپاسگزاری می‌کنید.'),
(2752, 23, 79, 'اوست که شما را در زمین پدید آورد; سپس چون بمیرید دوباره زنده می‌شوید و به سوی او روانه می‌گردید.'),
(2753, 23, 80, 'اوست که زنده می‌کند و می‌میراند، و رفت و آمد شب و روز و گذشت زمان در اختیار اوست; پس چرا درنمی یابید؟'),
(2754, 23, 81, 'کافران نشانه‌های ربوبیّت الهی را درک نکردند، بلکه از سرِ تقلید مثل آن سخنی را که مردمان نخستین گفتند بر زبان آوردند.'),
(2755, 23, 82, 'گفتند: آیا چون مردیم و خاک گشتیم و استخوانی چند شدیم، به راستی برانگیخته می‌شویم؟'),
(2756, 23, 83, 'این رستاخیز را که به ما وعده داده‌اند، پیش‌تر نیز به پدرانمان وعده دادند، ولی هرگز تحقق نیافت. اینها چیزی جز افسانه‌هایی که مردمان نخستین ساخته‌اند نیست.'),
(2757, 23, 84, 'ای پیامبر، به منکران رستاخیز بگو: اگر اهل دانشید، بگویید زمین و کسانی که در آنند از آنِ چه کسی است و هستیِ آنها وابسته به کیست؟'),
(2758, 23, 85, 'خواهند گفت: از آنِ خداست. بگو: پس چرا متوجّه نمی‌شوید که چنین خدایی می‌تواند هرگونه بخواهد در ملک خود تصرّف کند و انسان‌ها را بمیراند و حیاتی دوباره بخشد؟'),
(2759, 23, 86, 'و نیز به آنان بگو: پروردگار آسمان‌های هفتگانه و پروردگار عرش بزرگ که مرکز فرمانروایی هستی است، کیست؟'),
(2760, 23, 87, 'خواهند گفت: همه از آنِ خداست. بگو: پس چرا از خشم او که مالک آسمان‌ها و عرش بزرگ است نمی‌هراسید که رستاخیز را انکار می‌کنید؟'),
(2761, 23, 88, 'به آنان بگو: اگر دانا هستید، بگویید: آفرینش جهان هستی و فرمانروایی هر چیز به دست کیست، و حال آن که او هر که را به وی پناه بَرَد حمایت می‌کند و هیچ کس قدرت آن را ندارد که در برابر خدا از کسی حمایت کند و مانع خواسته او گردد؟'),
(2762, 23, 89, 'خواهند گفت: همه از آنِ خداست. بگو: پس تا کی دستخوش خیالات باطل می‌شوید و حق (توحید و رستاخیز) را باور نمی‌کنید؟'),
(2763, 23, 90, 'آری، وعده قیامت و حساب و جزا دروغ نیست، بلکه ما با این وعده حق را برایشان آورده‌ایم، و قطعاً منکران قیامت دروغ می‌گویند که رستاخیز را ناممکن می‌دانند.'),
(2764, 23, 91, 'خدا فرزندی اختیار نکرده است تا او نیز خدا باشد، و هرگز با وجود خدا هیچ معبود و خداوندگاری نبوده است، زیرا اگر خدایانی وجود می‌داشت، هر کدام با آنچه آفریده بود به سویی می‌رفت و در نتیجه نظام هستی از هم گسسته می‌شد، و علاوه بر این بعضی از آن خدایان بر بعضی دیگر برتری می‌یافتند. منزّه است خدا از آنچه مردم او را بدان وصف می‌کنند;'),
(2765, 23, 92, 'همان خدایی که دانای نهان و آشکار است و می‌داند که خداوندگاری جز او نیست. پس او والاتر از آن است که شریکی برای وی پندارند.'),
(2766, 23, 93, 'ای پیامبر، خدایت را بخوان و بگو: پروردگارا، اگر می‌خواهی عذابی را که به مشرکان وعده داده می‌شود در این دنیا به من نشان دهی،'),
(2767, 23, 94, 'پروردگارا، مرا از میان این ستم پیشگان بیرون بر تا عذاب موعود دامنگیر من نشود.'),
(2768, 23, 95, 'و ما قطعاً تواناییم بر این که آنچه را به مشرکان وعده می‌دهیم به تو نشان دهیم.'),
(2769, 23, 96, 'اما اکنون که در میانشان هستی، باید رفتار بدشان را با نیکوترین خصلت‌ها دفع کنی. ما به سخنان ناشایسته‌ای که مشرکان ما را بدان وصف می‌کنند داناتریم.'),
(2770, 23, 97, 'و بگو: پروردگارا، از وسوسه‌ها و اغواگری‌های شیاطین به تو پناه می‌برم.'),
(2771, 23, 98, 'پروردگارا، و از این که نزد من حاضر شوند به تو پناه می‌برم.'),
(2772, 23, 99, 'این کفرپیشگان همچنان به کفر خود ادامه می‌دهند و به ثروت خود مغرورند; تا آن گاه که مرگ به سراغ یکی از آنان بیاید، در آن حال به درگاه خدا استغاثه می‌کند و ندای (یا ربّ) سر می‌دهد و به فرشتگان می‌گوید: مرا به دنیا بازگردانید;'),
(2773, 23, 100, 'اگر باز گردم، شاید در ثروتی که از خود بر جای نهاده‌ام کاری شایسته کنم و آنها را برای خشنودی خدا انفاق نمایم. چنین نیست، درخواستِ بازگشت بی اثر است، او فقط این سخن را بر زبان می‌آورد، و فراروی آدمیان پس از مرگ، جهانی است میان دنیا و آخرت تا روزی که برانگیخته شوند.'),
(2774, 23, 101, 'پس هنگامی که در صور دمیده شود و مردگان زنده شوند، در آن روز میانشان خویشاوندی نمی‌ماند و هیچ کس به کار دیگری نخواهد آمد و از حال و کار یکدیگر نخواهند پرسید.'),
(2775, 23, 102, 'آن روز اعمال انسان را می‌سنجند; پس کسانی که اعمالشان وزین است، آنان سعادتمندند.'),
(2776, 23, 103, 'و کسانی که اعمالشان سبک است آنان کسانی اند که هستی خود را باخته‌اند و در دوزخ جاودانه خواهند بود.'),
(2777, 23, 104, 'شعله آتش صورت هایشان را می‌گدازد و آنان در آن جا با لب‌های سوخته و جمع شده و دندان‌های آشکار، به سر می‌برند.'),
(2778, 23, 105, 'به آنان گفته می‌شود: مگر آیات من پیوسته بر شما تلاوت نمی‌شد و شما آنها را دروغ می‌شمردید؟‌'),
(2779, 23, 106, 'می‌گویند: پروردگارا، شقاوت و تیره بختی ما بر ما چیره شد و ما مردمی گمراه بودیم.'),
(2780, 23, 107, 'پروردگارا، ما را از این جا بیرون بر و به دنیا بازگردان; اگر دوباره به شرک و گناه روی آوردیم، قطعاً ستم پیشه خواهیم بود.'),
(2781, 23, 108, 'خداوند به آنان می‌فرماید: در دوزخ گم شوید و دور باشید و با من سخن نگویید.'),
(2782, 23, 109, 'حقیقت این است که گروهی از بندگان من در دنیا می‌گفتند: پروردگارا، ایمان آوردیم، پس ما را ببخشای و بر ما رحمت آور که تو بهترین رحمت آورندگانی.'),
(2783, 23, 110, 'ولی شما آنان را به تمسخر گرفتید، تا آن جا که تمسخرتان از مؤمنان شما را از یادِ من به فراموشی انداخت، و شما همواره به آنان می‌خندیدید.'),
(2784, 23, 111, 'من هم امروز که روز جزاست، مؤمنان را به پاس این که در برابر ریشخند شما شکیبایی کردند و بر یاد من استوار بودند، پاداش دادم که تنها آنان سعادتمند باشند.'),
(2785, 23, 112, '[سپس] خداوند به کفرپیشگان می‌گوید: شما در گورهایتان به شمار سال‌ها چه مدت ماندید؟‌'),
(2786, 23, 113, 'می‌گویند: روزی یا پاره‌ای از روز را در آن جا به سر بردیم; درست مدت آن را نمی‌دانیم، از فرشتگانِ شمارش کننده بپرس.'),
(2787, 23, 114, 'خداوند می‌گوید: آری، شما جز اندک زمانی در گورهایتان درنگ نکردید. کاش آن روز که در دنیا بودید می‌دانستید که از مرگ تا برپایی رستاخیز فاصله زیادی نیست.'),
(2788, 23, 115, 'آیا پنداشتید که شما را بیهوده آفریده‌ایم و شما به سوی ما بازگردانده نمی‌شوید؟'),
(2789, 23, 116, 'خداوند که فرمانروای هستی است و هیچ امری را جز به حق مقرّر نمی‌کند، والاتر از آن است که شما را بیهوده بیافریند. هیچ معبودی شایسته پرستش جز او نیست. اوست پروردگار آن عرش ارجمند و زمام همه امور به دست اوست.'),
(2790, 23, 117, 'و هر کس با وجود خدا معبود دیگری بخواند که برای آن هیچ برهانی ندارد، حسابش فقط با پروردگارش خواهد بود. حقیقت این است که کافران به سعادت نمی‌رسند.'),
(2791, 23, 118, 'و بگو: پروردگارا، ببخشای و رحمت آور که تو بهترین رحمت آورندگانی.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'این سوره‌ای است که آن را فرو فرستادیم و عمل به احکامی را که در آن بیان شده است واجب کردیم و در آن آیاتی فرو فرستادیم که بیانگر معارف الهی است; باشد که شما آنها را فراگیرید و به خاطر داشته باشید.'),
(2793, 24, 2, 'زن و مرد زناکننده را، به هر یک از آنها صد تازیانه بزنید، و اگر به خدا و روز آخرت ایمان دارید نباید مهربانی و دلسوزی به آن دو شما را فراگیرد و در کیفرشان سستی به خرج دهید، که این سهل انگاری در دین و بی اعتنایی به قانون خداست، و باید گروهی از مؤمنان در صحنه کیفر حضور داشته باشند و عقوبت آنان را نظاره کنند.'),
(2794, 24, 3, 'مرد زناکاری که حد بر او جاری شده و توبه نکرده است، جز با زنی زناکار یا مشرک ازدواج نمی‌کند، و زن زناکاری که حد خورده و توبه نکرده است، او را جز مردی زناکار یا مشرک به همسری نمی‌گیرد، و این ازدواج بر مؤمنان حرام گردیده است.'),
(2795, 24, 4, 'و کسانی که زنان پاکدامن را به زنا متهم می‌کنند، سپس چهار مرد را بر درستی گفته خویش شاهد نمی‌آورند، هشتاد تازیانه به آنان بزنید و هرگز شهادتشان را در هیچ امری نپذیرید که اینان فاسقند.'),
(2796, 24, 5, 'مگر کسانی که پس از اجرای حد توبه کنند و کارهای خود را اصلاح نمایند، که اینان دیگر فاسق به شمار نمی‌آیند و شهادتشان پذیرفته می‌شود، چرا که خداوند آمرزنده گناهان و رحمت آورنده بر توبه کنندگان است.'),
(2797, 24, 6, 'و مردانی که همسران خود را به زنا متهم می‌کنند، و بر درستی دعوایشان گواهانی جز خویشتن ندارند، شهادت هر یک از آنان چهار مرتبه است که در هر مرتبه خدا را گواه می‌گیرد که در نسبتی که به همسرش می‌دهد از راستگویان است.'),
(2798, 24, 7, 'و پنجمین گواهی او این است که لعنت خدا بر او باد اگر از دروغگویان باشد.'),
(2799, 24, 8, 'بدین طریق اتهام شوهر به همسر خود ثابت می‌شود. و کیفر زنا بر او مقرر می‌گردد; و این کیفر را از او برمی دارد این که چهار مرتبه خدا را گواه گیرد که شوهرش از دروغگویان است،'),
(2800, 24, 9, 'و پنجمین بار گواهی دهد که خشم خدا بر او باد اگر شوهرش از راستگویان باشد.'),
(2801, 24, 10, 'و اگر نه این بود که فضل و رحمت خدا در پرتو دین شامل حال شماست و خداوند توبه پذیر و کارهایش براساس حکمت است، قطعاً تیره بختی شما را فرو می‌گرفت و گناه، شما را تباه می‌ساخت و نظام زندگی تان از هم می‌گسست.'),
(2802, 24, 11, 'قطعاً کسانی که آن بهتان را مطرح کردند، دسته‌ای توطئه گر از شما بودند. آنان با متهم ساختن یکی از زنان پیامبر خواستند خانواده او را آلوده نشان دهند و بر او طعنه زنند. شما آن بهتان را شرّی برای خود مپندارید، بلکه آن در نهایت برای شما خیر است، زیرا برای هر یک از آنان، آثار ناهنجار گناهی را که مرتکب شده است خواهد بود. و شما بدین وسیله منحرفان جامعه خود را می‌شناسید، و آن کس از ایشان که سهم عمده‌ای در گسترش آن بهتان را بر عهده داشته است، عذابی بزرگ خواهد داشت.'),
(2803, 24, 12, 'چرا وقتی آن بهتان را درباره همکیش خود شنیدید، مردان و زنان مؤمن در حق او که از خودشان به شمار می‌رفت گمان نیک نبردند و نگفتند: این بهتانی آشکار است؟'),
(2804, 24, 13, 'چرا مدّعیان بر آن بهتانی که زدند چهار مرد را گواه نیاوردند؟ پس چون گواهان را نیاورده‌اند، آنان نزد خدا قطعاً دروغگو به شمار می‌روند.'),
(2805, 24, 14, 'و اگر فضل و رحمت خدا در دنیا و آخرت بر شما نبود، قطعاً به خاطر بهتانی که در آن وارد شدید، در هر دو جهان عذابی بزرگ دامنگیرتان می‌شد.'),
(2806, 24, 15, 'آری، هنگامی شما وارد آن بهتان شدید که بی هیچ اندیشه‌ای در درستی یا نادرستی آن، زبان به زبان آن را می‌گرفتید و چیزی را می‌گفتید که از آن هیچ گونه آگاهی نداشتید و گفت و شنود آن را آسان می‌پنداشتید در حالی که نزد خدا امری بزرگ بود.'),
(2807, 24, 16, 'و چرا وقتی آن را شنیدید نگفتید: ما حق نداریم این شایعه بی دلیل را بر زبان آوریم ـ خداوندا، تو منزّهی و خانواده پیامبرت از هر آلودگی پیراسته‌اند ـ این سخن، افترایی بزرگ و بُهت آور است.'),
(2808, 24, 17, 'خدا به شما پند می‌دهد که اگر ایمان دارید، هرگز مثل چنین کاری را تکرار نکنید و به سخنان بی پایه نپردازید.'),
(2809, 24, 18, 'خداوند احکام دین خود را که از آیات او به شمار می‌رود برای شما بیان می‌کند، و خدا به مصلحت شما داناست و آنچه را برایتان مقرر می‌دارد حکیمانه مقرر می‌دارد.'),
(2810, 24, 19, 'کسانی که دوست دارند کارِ زشت در میان مؤمنان شایع شود، برای آنان در دنیا و آخرت عذابی دردناک خواهد بود، و خدا می‌داند که چه چیزی خشم او را در پی دارد و شما نمی‌دانید.'),
(2811, 24, 20, 'و اگر فضل و رحمت خدا شامل حال شما نبود و این که خدا رئوف و مهربان است، قطعاً به سزای آنچه کردید شما را کیفر می‌داد.'),
(2812, 24, 21, 'ای کسانی که ایمان آورده‌اید، از پی گام‌های شیطان نروید، که هر کس از پی گام‌های شیطان برود، به کارهای زشت و ناروا گرایش می‌یابد، زیرا شیطان به کار زشت و ناپسند فرمان می‌دهد. و اگر فضل و رحمت خدا بر شما نبود، هرگز اَحدی از شما پیراسته و پاک نمی‌شد، ولی خدا هر که را بخواهد (او را شایسته پاکی بداند)، پاک می‌سازد، و خدا شنونده درخواست‌های مردم و دانا به حال آنان است.'),
(2813, 24, 22, 'و از میان شما، آنان که ثروتی بیش از مخارج خویش دارند و از گشایش روزی برخوردارند، نباید از انفاق به خویشاوندان و مستمندان و هجرت کنندگانِ در راه خدا کوتاهی کنند; هر چند آنان دچار خطاها و لغزش‌هایی شده باشند. توانگران باید خطای آنان را ببخشند و از لغزششان چشم پوشی کنند. آیا دوست ندارید که خدا گناهانتان را بیامرزد؟ و خداوند آمرزنده و مهربان است.'),
(2814, 24, 23, 'به یقین، کسانی که زنان پاکدامنِ بی خبر مؤمن را به فحشا متهم می‌کنند، در دنیا و آخرت لعنت شده و از رحمت خدا دور گردیده‌اند، و برای آنان عذابی بزرگ خواهد بود.'),
(2815, 24, 24, 'آن عذاب در روزی است که زبان‌ها و دست‌ها و پاهایشان بر ضدّ آنان به آنچه می‌کردند گواهی می‌دهند.'),
(2816, 24, 25, 'در آن روز خداوند جزای بایسته آنان را به تمام و کمال به آنان می‌دهد، و آن روز خواهند دانست که خداست حق روشن و آشکار.'),
(2817, 24, 26, 'زنان پلید از آنِ مردان پلیدند و مردان پلید از آنِ زنان پلید. و زنان پاک از آنِ مردان پاکند و مردان پاک از آنِ زنان پاک. اینان به خاطر پاکی و ایمانشان، از تهمت‌هایی که درباره شان مطرح می‌کنند برکنارند. برای آنان آمرزش و روزی ارجمند (زندگی پاک در دنیا و پاداش نیکو در آخرت) خواهد بود.'),
(2818, 24, 27, 'ای کسانی که ایمان آورده‌اید، به خانه‌هایی که خانه‌های شما نیست وارد نشوید تا به ساکنانش آشنایی دهید و ورود خود را بر آنان، اعلام نمایید و بر اهل آن سلام کنید. این برای شما بهتر است، باشد که در اثر پای بندی به این شیوه پسندیده همواره وظایف اجتماعی خود را به خاطر داشته باشید.'),
(2819, 24, 28, 'پس اگر کسی را در آن خانه‌ها نیافتید، وارد آنها نشوید، تا به شما اجازه ورود داده شود; و اگر اجازه نیافتید و به شما گفته شد: بازگردید. بازگردید، که این برای شما پاک‌تر و بالنده‌تر است، و خدا به آنچه می‌کنید داناست.'),
(2820, 24, 29, 'بر شما گناهی نیست که به خانه‌های غیر مسکونی (از قبیل تجارتخانه‌ها، کاروانسراها، حمّام‌ها و دیگر اماکن عمومی) که استفاده‌ای برای شما در آنهاست، بدون اجازه وارد شوید، و بدانید که خدا آنچه را آشکار می‌کنید و آنچه را پنهان می‌دارید می‌داند.'),
(2821, 24, 30, 'ای پیامبر، به مردان مؤمن بگو چشمان خود را از شرمگاه‌های دیگران فرو بندند و شرمگاه‌های خود را نیز از دید دیگران بپوشانند، که اگر فرمانشان دهی چنین کنند. این شیوه برای آنان پاکیزه‌تر و بالنده‌تر است. قطعاً خدا به آنچه می‌کنند آگاه است.'),
(2822, 24, 31, 'و به زنان مؤمن نیز بگو چشمان خود را از شرمگاه‌های دیگران فروبندند و شرمگاه‌های خود را نیز از دید دیگران بپوشانند و زیبایی هایشان را جز آنچه خود نمایان است آشکار نسازند و اطراف مقنعه خود را بر سینه خویش بیندازند تا سینه شان را بپوشاند اگر به آنان بگویی قطعاً چنین خواهند کرد. و زیبایی‌های خود را آشکار نسازند جز برای شوهرانشان یا پدرانشان یا پدران شوهرانشان یا پسرانشان یا پسران شوهرانشان یا برادرانشان یا پسران برادرانشان یا پسران خواهرانشان یا زنان همکیش خود یا بردگانی که مالک آنها شده‌اند یا مردان سفیهی که سرپرستی دارند و شهوت جنسی ندارند یا کودکان نابالغی که بر اندام خاص زنان چیرگی نیافته‌اند، و زنان نباید پاهایشان را بر زمین بکوبند تا زیورهایی که پنهان می‌دارند معلوم شود. ای مؤمنان، همگی با پیمودن راه خدا به سوی او باز گردید، باشد که سعادتمند شوید.'),
(2823, 24, 32, 'ای مؤمنان، به مردان و زنانِ بی همسری که از [همکیشان] شما هستند و به آن بندگان و کنیزانتان که شایستگی ازدواج را دارند، همسر دهید. اگر تهیدست‌اند، خداوند آنان را از فضل خود توانگر خواهد ساخت، که خداوند دارای بخششی گسترده است و به حال کسی که صلاح او در گشایش است داناست.'),
(2824, 24, 33, 'و باید کسانی که هزینه ازدواج و مخارج زندگی را نمی‌یابند پاکدامنی پیشه کنند و از کارهای زشت بپرهیزند تا خداوند آنان را از فضل خود توانگر کند. و از میان بردگانتان، آنان که می‌خواهند با قراردادی مکتوب ثمن خود را بپردازند و آزادی خود را باز یابند، اگر شایستگی این کار را در آنان سراغ دارید، درخواستشان را بپذیرید و با آنان قرارداد بنویسید، و از مالی که خدا به شما ارزانی کرده است به عنوان زکات به ایشان بدهید [تا بتوانند بهای خود را بپردازند.] و اگر کنیزانتان خواهان عفّت‌اند، برای دستیابی به متاع ناپایدار دنیا آنان را به زنا وادار نکنید، و هر کس آنان را به این کار وادار کند [کیفر خواهد دید و] البته خداوند گناه کنیزان را پس از آن که به این کار زشت مجبور شده اند می‌آمرزد و رحمت خود را از آنان دریغ نمی‌دارد.'),
(2825, 24, 34, 'همانا ما آیاتی روشنگر که راه سعادت را به شما نشان می‌دهد و شرحی از آنان که پیش از شما درگذشتند ـ چه نیکانشان و چه تبهکارانشان ـ به سوی شما نازل کردیم تا کارهای شایسته و ناشایسته را بشناسید، و برای تقواپیشگان شما نیز اندرزی فرو فرستادیم.'),
(2826, 24, 35, 'خداوند نوری است که آسمان‌ها و زمین به سبب آن پدید آمده‌اند و برای او نوری ویژه مؤمنان است. این نور در مَثَل همچون چراغدانی است که در آن چراغی باشد و آن چراغ در بلوری، که وزش باد شعله اش را نلرزاند و از فروغش نکاهد. آن بلور چنان بدرخشد که گویی اختری تابان است. آن چراغ با روغنی که از درخت پربرکت زیتون فراهم آمده است افروخته می‌شود; درختی که نه در جانب شرق است و نه در جانب غرب که تنها یک سوی آن از تابش خورشید بهره بَرَد; نزدیک است روغن آن از شدّت صافی روشنی بخشد، هرچند آتشی به آن نرسیده و شعلهورش نکرده باشد; نوری است شدید بر روی نوری شدید. خدا هر که را بخواهد (شایسته بداند)، به نورِ هدایتش راه می‌نماید، و خدا این مَثَل‌ها را برای مردم می‌آورَد و خدا به هر چیزی داناست.'),
(2827, 24, 36, 'آن چراغ پرفروغ در خانه‌هایی است که خدا رخصت داده است که قدر و منزلت آنها رفعت یابد چرا که رخصت داده است که نام خدا در آنها برده شود. در آن خانه‌ها هر بامداد و عصرگاه مردانی او را تسبیح می‌گویند;'),
(2828, 24, 37, 'مردانی در آن خانه‌ها تسبیح خدا می‌گویند که نه هیچ تجارت و نه هیچ خرید و فروشی آنان را از یاد خدا و برپا داشتن نماز و پرداخت زکات بازمی دارد. آنان از روزی می‌هراسند که دل‌ها و دیده‌های همگان (مؤمنان و کافران) در آن دگرگون می‌شود. (دل‌ها و دیدگان مؤمنان به نور خدا بینا می‌شود و کرامت‌های الهی را می‌نگرند، و دل‌ها و دیدگان کافران کور می‌گردد و چیزی جز رنج نمی‌بینند).'),
(2829, 24, 38, 'آری، مؤمنان به کارهای شایسته می‌پردازند تا خدا هنگام دادن پاداش به آنان، تمام اعمالشان را هرچند کاستی‌هایی داشته باشد، بهترین عمل به شمار آورد و همه را بر پایه آن سزا دهد و از فضل خود نیز بر پاداششان بیفزاید، و خداوند به هر کس بخواهد، بی حساب; بدون آن که از اعمال او عوضی بخواهد، روزی و پاداش افزون خواهد داد.'),
(2830, 24, 39, 'این است وصف مؤمنان، اما کسانی که کفر ورزیده‌اند، اعمالشان همچون سرابی است در زمینی هموار که تشنه آن را آب می‌پندارد و به سراغ آن می‌رود، ولی هنگامی که به آن می‌رسد آن را چیزی نمی‌یابد. آری، کافران به گمان رستگاری بت می‌پرستند، اما وقتی مرگ یکی از آنان فرا رسید و به صحنه قیامت درآمد، آن جاست که اعمال خود را بی ثمر می‌بیند و خدا را نزد خویش حاضر می‌یابد و خدا هم حساب او را بررسی می‌کند و سزایش را به تمام و کمال به او می‌دهد، و خداوند اعمال بندگان را به سرعت رسیدگی می‌کند.'),
(2831, 24, 40, 'یا اعمالشان همچون تاریکی‌هایی است در دریایی موّاج که هماره آن دریا را موجی می‌پوشاند و بر فراز آن موجی دیگر است، و بالای آن ابرهایی است که جلو نورهای آسمانی را می‌گیرد. اینها تاریکی‌هایی است متراکم که بعضی بر روی بعضی قرار گرفته است، و کسی که در این دریاست، وقتی دست خود را بیرون می‌آورد، نزدیک است که آن را از شدت تاریکی نبیند. اعمال کافران نیز حجاب‌هایی متراکم اند که مانع از آنند که نور ایمان و معرفت بر دل هایشان بتابد و آنان را به راه نجات رهنمون شود. آری، کسی که خدا نوری برای او قرار نداده است هیچ نوری نخواهد داشت.'),
(2832, 24, 41, 'آیا ندانسته‌ای که هر که در آسمان‌ها و زمین است و پرندگانِ گشوده بال، خدا را با هستیِ خویش تسبیح می‌گویند و وجود نیازمندشان بی نیازی او را اعلام می‌کند؟ این حقیقتی است بس روشن و آشکار. همه موجودات راه عرضه نیاز خود را به درگاه خدا می‌دانند و از تسبیح گفتن خود آگاهند، و خدا به آنچه می‌کنند داناست.'),
(2833, 24, 42, 'فرمانرواییِ آسمان‌ها و زمین تنها از آنِ خداست و بازگشت همه امور نیز به سوی خداست.'),
(2834, 24, 43, 'آیا ندیده‌ای که خدا ابرهایی پراکنده را روانه می‌کند، آن گاه میان آنها پیوند می‌دهد، سپس آنها را متراکم می‌سازد، پس قطره‌های باران را می‌بینی که از لابه لای ابرها بیرون می‌آید؟ و آیا ندیده‌ای که خدا از آسمان، از تگرگ‌های متراکم موجود در آن که همچون کوه هاست فرو می‌فرستد و هر که را بخواهد با آن هدف قرار می‌دهد تا به مال و جان او آسیب برساند، و از هر که بخواهد آن را باز می‌دارد؟ نزدیک است روشنی برقش دیده‌ها را از بین ببرد.'),
(2835, 24, 44, 'خداوند شب و روز را به گردش درمی آورد و هر کدام را جایگزین دیگری می‌کند. به راستی در این جابه جایی برای اهل بصیرت نشانه و عبرتی است بر این که تنظیم امور هستی به دست اوست.'),
(2836, 24, 45, 'خداست که همه جنبندگان را از آبی که دارای یک طبیعت است آفرید; با وجود این، آنها دارای گونه‌ها و انواع بسیارند; برخی از آنها بر شکمشان می‌خزند و برخی از آنها بر روی دو پا راه می‌روند و برخی از آنها بر روی چهار پا راه می‌روند. خدا هر چه بخواهد می‌آفریند، زیرا او بر هر چیزی تواناست.'),
(2837, 24, 46, 'همانا ما آیاتی که بیانگر صفات خدا و روشنگر راه ایمان و کفر و توحید و شرک است فرو فرستادیم، و خداوند هر که را بخواهد به راهی راست هدایت می‌کند.'),
(2838, 24, 47, 'منافقان می‌گویند: به خدا و پیامبر ایمان آوردیم و به فرمانشان گردن نهادیم. ولی گروهی از آنان پس از این اقرار روی می‌گردانند و سرپیچی می‌کنند. ولی هیچ یک از این دو گروه ایمان نیاورده‌اند.'),
(2839, 24, 48, 'و هنگامی که نزاعی درگیرد و آنان به قانون خدا و داوری پیامبر فراخوانده شوند، بی آن که انتظار رود، گروهی از آنان رویگردان می‌شوند.'),
(2840, 24, 49, 'در صورتی که اگر داوری پیامبر ـ که همیشه حق است ـ به سود آنان باشد، با حال اطاعت و تسلیم به سوی او می‌آیند.'),
(2841, 24, 50, 'آیا در دل هایشان مرضی هست، یا در حقانیّت دینشان تردید دارند، یا می‌ترسند خداوند با حکمی ناحق به آنان ستم کند و پیامبرش با داوری نابحق ستم روا دارد؟ نه، اینها دلیل رویگردانی شان نیست، بلکه از آن روست که آنان ستمکارند، زیرا اظهار ایمان کردند و در دل تسلیم نشدند.'),
(2842, 24, 51, 'ولی مؤمنانِ حقیقی هنگامی که نزاعی میانشان رخ دهد و به قانون خدا و داوری پیامبر فراخوانده شوند تا میانشان داوری کند، سخنشان جز این نیست که می‌گویند: دعوت خدا را پذیرفتیم و بر اطاعت از خدا و پیامبرش گردن نهادیم. اینانند نیکبختان.'),
(2843, 24, 52, 'زیرا هر کس از خدا و پیامبرش فرمان برد و از خدا بترسد و از او پروا کند، تنها چنین کسانی به سعادت نایل می‌گردند.'),
(2844, 24, 53, 'منافقان با سوگندهای مؤکّد خود به خدا قسم خوردند که اگر آنان را به جهاد در راه خدا فرمان دهی، قطعاً به سوی میدان نبرد بیرون می‌روند. به آنان بگو: سوگند یاد نکنید، زیرا جهاد در راه خدا طاعتی است که ضرورت آن در دین شناخته شده است و وجوب آن در گرو سوگند نیست، و اگر سوگندتان برای جلب رضایت خدا و پیامبر است، بدانید که خدا به آنچه می‌کنید آگاه است و سوگندهای شما او را فریب نمی‌دهد.'),
(2845, 24, 54, 'بگو: خدا را اطاعت کنید و از فرستاده اش فرمان برید. پس اگر روی گرداندید و پیامبر را فرمان نبردید، به وی زیانی نمی‌رسد، چرا که فقط برعهده اوست آنچه بدان تکلیف شده است و برعهده شماست آنچه بدان تکلیف شده‌اید. و اگر از پیامبر فرمان برید، هدایت خواهید یافت، و بدانید که بر رسول خدا جز ابلاغ آشکار پیام وظیفه‌ای نیست.'),
(2846, 24, 55, 'خداوند به کسانی از شما که ایمان آورده و کارهای شایسته کرده‌اند وعده داده است که آنان را در زمین جانشین امّت‌های شایسته گذشته کند; چنان که شایستگان امت‌های پیشین را در زمین جانشین دیگران قرار داد، و نیز نوید داده است که آیین آنان را که بر پایه فطرت استوار است و خدا آن را برایشان پسندیده است، پابرجا سازد، به گونه‌ای که جامعه براساس آن شکل یابد، و وعده داده است که آنان را پس از این که پیوسته از سوی دشمنان داخلی و خارجی بیمناک بودند، ایمنی بخشد. در حالی به امنیت می‌رسند که مرا می‌پرستند و چیزی را شریک من قرار نمی‌دهند، و هر کس پس از تحقق این وعده، به وسیله کفر و نفاق و ارتکاب گناهان ناسپاسی کند، اینانند که از راه خدا بیرون رفته‌اند.'),
(2847, 24, 56, 'و نماز را بر پا دارید و زکات بدهید و حکم پیامبر را اطاعت کنید، امید است که مورد رحمت خدا قرار گیرید و وعده جانشین شدن در زمین و تشکیل جامعه‌ای بر پایه دین اسلام بی ترس از دشمنان، برای شما تحقق یابد.'),
(2848, 24, 57, 'هرگز مپندار که کفرپیشگان می‌توانند خدا را در زمین ناتوان کنند و او را از وفا به وعده خود بازدارند. آنان سرانجام شکست خواهند خورد، و جایگاهشان آتش دوزخ است و بی گمان، آن بدجایگاهی است.'),
(2849, 24, 58, 'ای کسانی که ایمان آورده‌اید، باید بردگانتان و نیز نوجوانانتان که به حد بلوغ نرسیده‌اند، هنگام ورود به خانه‌های شما، در هر شبانه روز سه مرتبه از شما اجازه بگیرند: پیش از نماز صبح، و در نیمروز آن گاه که لباس‌های خود را از تن بیرون می‌آورید، و پس از نماز عشا. این سه زمان، زمان‌هایی هستند که شما دوست نمی‌دارید دیگران از وضع شما در آنها آگاه شوند، و شایسته است که بر دیگران پوشیده بمانید. ولی پس از این سه زمان، نه بر شما گناهی است که آنان را به اجازه خواستن فرمان ندهید، و نه بر آنان گناهی است که اجازه در چندین آیه دیگر نگیرند، چرا که رفت و آمدشان بر گرد شما بسیار است و همه بر گرد یکدیگر می‌چرخید، و اجازه خواستن پی در پی ملال آور است. خداوند احکام دین خود را که از نشانه‌های اوست این گونه برای شما بیان می‌کند، و خدا به احکامی که مورد نیاز شماست داناست و آنچه برای شما مقرّر می‌دارد براساس حکمت اوست.'),
(2850, 24, 59, 'و هنگامی که کودکان شما به حدّ بلوغ رسیدند، باید برای ورود به خانه شما اجازه بگیرند; چنان که مردان و زنانی که پیش از آنان بالغ شدند، اجازه می‌خواستند. خداوند احکام دین خود را که از نشانه‌های اوست، این گونه برای شما به روشنی بیان می‌کند. و خداوند به احکامی که مورد نیاز شماست داناست، و آنچه برای شما مقرّر می‌دارد براساس حکمت اوست.'),
(2851, 24, 60, 'و بر زنانی که در اثر سالخوردگی از زناشویی بازنشسته و امیدی به ازدواج ندارند گناهی نیست که لباس‌های خود را فرو گذارند بی آن که زیبایی‌های خود را نمایان سازند; و اینکه پاکدامنی پیشه سازند و حجاب خویش را رعایت کنند بهتر و شایسته‌تر است. و خدا شنوا و داناست.'),
(2852, 24, 61, 'بر نابینا و بر لنگ و بر بیمار و بر شما گناهی نیست که از خانه‌های خودتان یا از خانه‌های پدرانتان یا خانه‌های مادرانتان یا خانه‌های برادرانتان یا خانه‌های خواهرانتان یا خانه‌های عموهایتان یا خانه‌های عمّه هایتان یا خانه‌های دایی هایتان یا خانه‌های خاله هایتان یا خانه‌هایی که مخازن خوراکی آنها را در اختیار دارید یا خانه دوستتان غذایی بخورید. بر شما و آنان گناهی نیست که دسته جمعی یا به تنهایی و جدا جدا غذا بخورید. شما باید آداب ورود به خانه دیگران را رعایت کنید و هنگامی که به خانه‌هایی درآمدید، به یکدیگر سلام کنید که سلام درودی است مبارک و خوشایند که از جانب خدا مقرر شده است. خداوند احکام دین خود را که از نشانه‌های اوست، این گونه برای شما به روشنی بیان می‌کند، شاید دانشِ دین بیابید و آن را به کار بندید.'),
(2853, 24, 62, 'مؤمنان راستین تنها کسانی اند که به خدا و پیامبرش ایمان آورده‌اند، و هنگامی که با پیامبر بر سر کاری که مربوط به جامعه اسلامی است گرد آمده‌اند، پیش از آن که از او اجازه بگیرند، از نزد وی بیرون نمی‌روند. کسانی که از تو اجازه می‌گیرند، اینانند که به خدا و پیامبرش ایمان دارند. پس اگر برای برخی از کارهای خود از تو اجازه خواستند، به هر کس از آنان که خواهی اجازه ده و برایشان از خدا آمرزش بخواه که خدا آمرزنده و مهربان است.'),
(2854, 24, 63, 'فراخوانی پیامبر را در میان خود مانند فراخوانی یکدیگر قرار ندهید; اگر پیامبر شما را برای کاری فراخواند، حاضر شوید، و بی اجازه او از نزدش خارج نشوید. قطعاً خداوند کسانی از شما را که آهسته آهسته و در پناه دیگران از حضور پیامبر بیرون می‌روند، می‌شناسد. پس باید کسانی که از فرمان پیامبر سرپیچی می‌کنند بترسند از این که بلایی به آنان رسد یا عذابی دردناک به سراغشان آید.'),
(2855, 24, 64, 'آگاه باشید که آنچه در آسمان‌ها و زمین است از آنِ خداست و او به ملک خویش آگاه است و قطعاً می‌داند که شما در چه حالی هستید و چه نیازی دارید و چه آیینی برای شما ضروری است، پس همان را برایتان مقرر کرده است; و نیز خبر دارد از روزی که همگان به سوی او بازگردانده می‌شوند و آنان را به حقیقت آنچه کرده‌اند آگاه می‌کند، و خدا به هر چیزی داناست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'پرخیر و برکت است آن کسی که قرآن را که جداکننده حق از باطل است بر بنده خود محمد فرو فرستاد تا جهانیان را بیم دهنده باشد.'),
(2857, 25, 2, 'همان کسی که فرمانروایی آسمان‌ها و زمین تنها از آنِ اوست، فرزندی نگرفته است و در فرمانروایی هیچ شریکی نداشته است، و هر چیزی را او آفریده و چنان که باید برای آن اندازه معین کرده است.'),
(2858, 25, 3, 'مشرکان به جای خدا معبودانی اختیار کرده‌اند شده‌اند، و برای خود نه زیانی را در اختیار دارند تا آن را از خود دور سازند و نه سودی را مالکند تا آن را برای خود فراهم آورند; چه رسد به این که بر دفع زیان از پرستشگران خود و رساندن سود به آنان توانا باشند. آنها نه مرگی را در اختیار دارند تا آن را از کسی دور سازند و نه حیاتی را مالکند تا آن را به کسی ارزانی دارند و نه بر رستاخیز قدرت دارند تا مردمان را پاداش دهند یا مجازات کنند.'),
(2859, 25, 4, 'کفرپیشگان عرب گفتند: این قرآن جز سخنی دروغین که محمد آن را بافته و کلام خدا می‌نامد چیزی نیست، و گروهی از اهل کتاب او را بر ساختن آن کمک کرده‌اند. راستی آنان با این سخن ستم کردند و دروغ گفتند.'),
(2860, 25, 5, 'و گفتند: این قرآن همان خرافه‌هایی است که مردمان نخستین نگاشته‌اند و محمد به کمک یارانش آنها را نوشته و هر صبح و عصر بر او خوانده می‌شود تا آن را فراگیرد و بر مردم بخواند.'),
(2861, 25, 6, 'بگو: چنین نیست، این قرآن را کسی فروفرستاده است که رازهای نهفته در آسمان‌ها و زمین را می‌داند و به نیازهای شما آگاه است. او با نازل کردن این قرآن می‌خواهد شما هدایت یابید تا از آمرزش و رحمت وی برخوردار شوید، زیرا او آمرزنده و مهربان است.'),
(2862, 25, 7, 'و گفتند: می‌بایست از جانب خدا فرشته‌ای به رسالت مبعوث شود، زیرا بشر نمی‌تواند با غیب سر و کار داشته باشد; این چه پیامبری است که غذا می‌خورد و برای کسب معاش در بازارها در حرکت است؟! چرا دست کم فرشته‌ای به سوی او فرستاده نشده است تا با او به هشدار مردم بپردازد؟'),
(2863, 25, 8, 'حال که فرشته‌ای با او نیامده است، چرا دست کم گنجی از آسمان به سوی او افکنده نمی‌شود تا نیازمند کسب معاش در کوچه و بازار نباشد یا بوستانی ندارد که از محصول آن بخورد و روزی خود را آماده داشته باشد؟ آن ستمگران به پیروان رسول خدا گفتند: شما جز از مردی که جادو شده و می‌پندارد که فرستاده خداست، پیروی نمی‌کنید.'),
(2864, 25, 9, 'بنگر که چگونه برای تو وصف‌ها آوردند و در نتیجه به بیراهه‌ای افتادند که نمی‌توانند به راه حق بازگردند.'),
(2865, 25, 10, 'پرخیر و برکت است آن خدایی که اگر بخواهد، بهتر از آنچه کافران پیشنهاد کردند، برای تو پدید می‌آورد; بوستان‌هایی با درختانی انبوه که نهرها از زیر آنها روان باشد، و اگر بخواهد کاخ‌هایی برای تو قرار می‌دهد که توصیفگران را درمانده کند.'),
(2866, 25, 11, 'غذا خوردن و راه رفتن تو در بازارها و نبودن فرشته‌ای با تو و نداشتن گنج و بوستان سبب اصلی انکار رسالت تو نیست، بلکه سبب اصلی این است که آنان قیامت را دروغ شمردند، و ما برای کسانی که قیامت را دروغ شمرند آتشی فروزان آماده کرده‌ایم.'),
(2867, 25, 12, 'چون آتش دوزخ کافران را از مکانی دور بنگرد، خشم گیرد و فروزنده‌تر شود و آنان فریاد خشم و خروش آن را می‌شنوند.'),
(2868, 25, 13, 'و هنگامی که آنان ـ در حالی که با غل و زنجیر به یکدیگر بسته شده‌اند ـ به تنگنایی از آتش دوزخ افکنده شوند، در آن جا فریاد واویلا سر می‌دهند و هلاکت خویش را می‌خواهند.'),
(2869, 25, 14, 'به آنان گفته می‌شود: امروز یک بار هلاکت خود را مخواهید، بلکه بارها و بارها هلاکت خود را بخواهید. امروز روز جزاست و فریاد واویلا ـ چه کم باشد یا زیاد ـ سودی به حال شما ندارد.'),
(2870, 25, 15, 'بگو: آیا این آتش فروزان بهتر است یا آن بهشت جاودان که به تقواپیشگان وعده داده شده است؟ بهشت پاداش تقوای آنان است و فرجامی است که بدان بازمی گردند.'),
(2871, 25, 16, 'آنان در آن جا، جاودانه‌اند هر چه بخواهند دارند. این وعده‌ای است برعهده پروردگارت که وفای به آن بر او حتمی است و از او درخواست شده است.'),
(2872, 25, 17, 'یاد کن روزی را که خداوند کافران را با آنچه جز خدا می‌پرستیدند برمی انگیزد و آنان را گرد می‌آورد، سپس به معبودانشان می‌گوید: آیا شما این بندگان مرا به بیراهه کشاندید یا آنها خود راه راست را گم کردند؟'),
(2873, 25, 18, 'معبودان می‌گویند: منزّهی تو; ما را نشاید که جز تو یاورانی بگیریم و آنان را به پرستش خود بخوانیم، ولی تو اینان و پدرانشان را از نعمت‌های دنیا برخوردار ساختی تا آن که تعلیمات پیامبران را که یادآور یکتایی تو بود فراموش کردند و بر اثر علاقه فراوانی که به دنیا داشتند مردمی تیره بخت و هلاک گشته بودند.'),
(2874, 25, 19, 'خدا به مشرکان می‌گوید: پس مطمئن باشید که معبودانتان آنچه را که شما درباره آنها می‌گفتید و معتقد بودید که آنها رنج‌ها و بلاها را از شما می‌گردانند و به شما یاری می‌رسانند، دروغ شمردند. بنابراین شما نه می‌توانید با پرستش اینها عذاب را از خود دور سازید و نه می‌توانید با یاری آنها از عذاب رهایی یابید. و هر کس از شما با ارتکاب گناهان ستم روا دارد، او را عذابی بزرگ می‌چشانیم.'),
(2875, 25, 20, 'پیامبر شدن بشر پدیده‌ای نوظهور نیست، ما پیش از تو هم ـ ای پیامبر ـ هیچ رسولی از رسولان خود را نفرستادیم مگر این که آنان نیز غذا می‌خوردند و برای کسب معاش در بازارها راه می‌رفتند. ما برخی از شما آدمیان را وسیله آزمون برخی دیگر قرار دادیم (پیامبران را نیز وسیله آزمون امّت‌ها قرار دادیم) تا بنگریم که آیا راه خدا را می‌پویید و بر مشکلات آن شکیبایید، یا پیرو هوای خویشید و ناگواری‌های پذیرش حق را تحمل نمی‌کنید. و پروردگار تو به راه صحیح تکامل انسان‌ها بیناست.'),
(2876, 25, 21, 'و آنان که منکر رستاخیزند و به لقای ما در آن جا امیدی ندارند، رسالت محمد را انکار کردند و گفتند: اگر بشر درخور این است که فرشته‌ای برای او وحی بیاورد و یا خدا خود با او سخن بگوید، پس چرا فرشتگان بر ما نازل نشده‌اند یا خدا را که به گمان محمد پروردگار ماست نمی‌بینیم تا با ما سخن بگوید؟ سوگند یاد می‌کنم که اینان به ناحق برای خود بزرگی و عظمت خواستند و به طغیانی بزرگ دست یازیدند.'),
(2877, 25, 22, 'ولی روزی که وارد عالَم برزخ می‌شوند و فرشتگان را دیدار می‌کنند، آن روز هیچ مژده و خبر خوشی برای تبهکاران نخواهد بود و آنان به فرشتگان که آماده کیفر ایشانند می‌گویند: بر شما حرام و ممنوع است که ما را عذاب کنید.'),
(2878, 25, 23, 'و در آن روز به هر کاری که کرده‌اند روی می‌آوریم و آن را همچون غبار پراکنده ای می‌سازیم.'),
(2879, 25, 24, 'بهشتیان در آن روز جایگاهشان بهتر و آسایشگاهشان برای استراحتِ نیمروز نیکوتر است.'),
(2880, 25, 25, 'و یاد کنید روزی را که آسمانِ پوشیده از ابر شکافته شود و فرشتگان چنان که باید به سوی زمین فرو فرستاده شوند.'),
(2881, 25, 26, 'در آن روز آشکار شود که فرمانروایی به تمام و کمال حق و حقیقت است و از آنِ خداوند رحمان است، و آن روز بر کافران روزی دشوار خواهد بود.'),
(2882, 25, 27, 'و روزی را که آن کسی که از هدایت پیامبر بی بهره بوده و بر خود ستم کرده است، دست‌های خود را از پشیمانی به دندان می‌گزد و می‌گوید: ای کاش، با رسول خدا راهی به سوی هدایت برگرفته بودم.'),
(2883, 25, 28, 'ای وای بر من! کاش فلانی را دوستِ خود نگرفته بودم.'),
(2884, 25, 29, 'بی گمان او مرا از این قرآن که یادآور خداست، پس از آن که به من رسید، دور ساخت و به بیراهه کشانید. آری، شیطان انسانی را که به یاری او دل بسته است یاری نمی‌کند.'),
(2885, 25, 30, 'و روزی را که محمد ـ فرستاده خدا، لب به شکایت می‌گشاید و می‌گوید: پروردگارا، کافران و گنهکاران قوم من این قرآن را وانهادند.'),
(2886, 25, 31, 'آری، همان گونه که برای تو دشمنانی قرار دادیم، برای هر پیامبری نیز دشمنانی از تبهکاران قرار دادیم. با این حال، بیمی به خود راه مده از این که مبادا امر دین و هدایت مردم با دشمنی آنان به سامان نرسد، چرا که همین بس که پروردگار تو هدایت کننده و یاریگر است. او هر که را درخور هدایت بداند هدایت می‌کند و تو و دین تو را یاری خواهد کرد.'),
(2887, 25, 32, 'و آنان که به پیام الهی کافر شدند، گفتند: چرا قرآن یکجا بر محمد نازل نشده است؟ این پراکندگی آیات قرآن نشانه این است که او خود، آنها را به مناسبتِ رویدادها می‌بافد و به خدا نسبت می‌دهد. آری، ما آیات قرآن را این گونه به تدریج و به مناسبتِ حوادث نازل می‌کنیم تا آن را به شکل بهتری در دل تو جای دهیم و بدین وسیله قلب تو را به تعالیم قرآن سخت استوار سازیم. با این حال، بخشی از آن را در پی بخشی دیگر می‌آوریم تا پیوند و ارتباط آنها با یکدیگر حفظ شود.'),
(2888, 25, 33, 'از نتایج نزول تدریجی قرآن این است که مردم هیچ وصف نادرستی را که از تحریف حقیقتی حکایت دارد، برای تو مطرح نمی‌کنند، جز این که ما با نزول آیاتی از قرآن، حق را با بهترین تفسیر برای تو می‌آوریم، تا حق استوار بماند و تحریف‌ها از بین برود.'),
(2889, 25, 34, 'کفرپیشگان با انکار آسمانی بودن قرآن از تو به بدی یاد کردند، ولی باید بدانند کسانی که در قیامت به صورت دسته جمعی و نگون بر چهره هایشان به سوی دوزخ رانده می‌شوند، بدترین جایگاه را دارند و از همه گمراه ترند.‌'),
(2890, 25, 35, 'می‌گویند: بشر درخورِ دریافت وحی نیست; بی گمان ما به موسی کتاب آسمانی عطا کردیم و [هیچ فرشته‌ای را دستیار او قرار ندادیم، بلکه] برادرش هارون را با وی همراه کردیم و او را پشتیبان وی قرار دادیم.'),
(2891, 25, 36, 'و به آن دو گفتیم: به سوی این مردم که نشانه‌های یکتایی ما را دروغ شمرده‌اند بروید و آنان را به توحید فراخوانید. ولی آنان رسالت موسی و هارون را نپذیرفتند و ما هم آنان را چنان که باید نابود ساختیم.'),
(2892, 25, 37, 'و قوم نوح را نیز آن گاه که پیامبران را تکذیب کردند غرق ساختیم و آنان را برای مردم نسلشان نشانه‌ای [بر حقانیّت پیامبران] قرار دادیم، و برای ستمکاران عذابی دردناک آماده کرده‌ایم.'),
(2893, 25, 38, 'و قوم عاد و ثمود و اصحاب رسّ و نسل‌های بسیاری را که پس از قوم نوح تا قوم فرعون پدید آمدند، همه را نابود کردیم.'),
(2894, 25, 39, 'و برای همه آنان مثل‌هایی آموزنده آوردیم و چون نپذیرفتند، همه آنان را درهم شکستیم و نابود ساختیم.'),
(2895, 25, 40, 'و قطعاً این کافران (کافران حجاز) بر شهر قوم لوط که آن باران شوم به صورت سنگپاره‌ها بر آن بارانده شد، گذر کرده‌اند; آیا آن جا را ندیده‌اند تا از انکار قرآن و رسالت پیامبر باز ایستند؟ چرا دیده‌اند، ولی رستاخیز را باور ندارند از این رو دعوت پیامبر را نمی‌پذیرند.'),
(2896, 25, 41, 'و هنگامی که تو را می‌بینند، فقط مسخره ات می‌کنند و می‌گویند: آیا این است آن کسی که خدا او را به رسالت برانگیخته است؟'),
(2897, 25, 42, 'اگر ما بر پرستش معبودان خود پایدار نبودیم، نزدیک بود او ما را از معبودانمان باز دارد. و به زودی ـ هنگامی که عذاب را بنگرند ـ خواهند دانست چه کسی گمراه‌تر است.'),
(2898, 25, 43, 'آیا دیدی آن کسی را که معبود خود را هوای نفس خویش قرار داده است؟ مگر تو بر او گماشته شده‌ای تا بتوانی او را هدایت کنی؟'),
(2899, 25, 44, 'آیا می‌پنداری که بیشتر آنان که پیرو هوای نفس اند گوش شنوا دارند و یا تعقّل می‌کنند؟ آنان جز مانند دام‌ها نیستند که از سخن جز آوایی نمی‌شنوند و از اندیشه بهره‌ای ندارند، بلکه چون از درک و فهم خود بهره نبرده‌اند از دام‌ها نیز گمراه ترند.'),
(2900, 25, 45, 'آیا به کار پروردگارت در آفرینش نظر نکرده‌ای که چگونه پس از زوالِ ظهر بر امتداد سایه افزوده و آن را گسترده است؟ و اگر می‌خواست آن را ساکن قرار می‌داد. سپس خورشید را نشانه وجود سایه قرار دادیم.'),
(2901, 25, 46, 'آن گاه پس از طلوع خورشید، سایه را به سوی خود می‌کشانیم و آن را به تدریج محو می‌کنیم، و این کار را به آسانی انجام می‌دهیم.'),
(2902, 25, 47, 'و اوست آن که شب را چون لباس، پوششی برای شما ساخت و خواب را باعث تعطیل قوا و آرامش اندامتان قرار داد، و روز را زمان پراکنده شدن برای به دست آوردن روزی مقرّر داشت.'),
(2903, 25, 48, 'اوست آن که بادها را پیشاپیش باران فرستاده است تا نویدبخش فرود آمدن رحمتش باشد، و از آسمان، آبی پاک و پاک کننده فرو فرستادیم.'),
(2904, 25, 49, 'تا به وسیله آن با رویش گیاهان سرزمینی مرده را زنده کنیم و نیز آن را برای نوشیدن در اختیار دام‌ها و مردمان بسیاری که آفریده‌ایم، قرار دهیم.'),
(2905, 25, 50, 'به یقین باران را میان مردم در مناطق گوناگون گردانده‌ایم (آن را پیوسته از جایی به جایی منتقل کرده ایم) تا توجه پیدا کنند و شکر نعمت‌ها را به جای آورند، ولی بیشتر مردم تنها از ناسپاسی ابا ندارند.'),
(2906, 25, 51, 'ای محمد، ما تو را به دلیل عظمت مقام و مرتبه ات به سوی همه انسان‌ها برانگیختیم، و اگر می‌خواستیم در هر شهری پیامبری هشداردهنده مبعوث می‌کردیم.'),
(2907, 25, 52, 'پس، از کافران فرمان مبر، و در برابر آنان با بیان حقایق قرآن به مبارزه و تلاشی بزرگ برخیز و رسالت خود را ابلاغ کن.'),
(2908, 25, 53, 'و اوست آن که دو دریا را کنار یکدیگر روان ساخت; این یکی خوشگوار و شیرین است و این یکی ناگوار و سخت شور است، و میان آن دو حایلی استوار پدید آورد و آنها را باز داشت از این که به یکدیگر بیامیزند.'),
(2909, 25, 54, 'اوست آن که از آب بشری آفرید و او را خویشاوند نسبی و سببی قرار داد. و پروردگارت تواناست.'),
(2910, 25, 55, 'مشرکان به جای خدا چیزهایی را می‌پرستند که نه به خاطر پرستش آنها سودی برایشان دارند و نه در صورت ترک پرستش آنها زیانی به آنان می‌رسانند; و کافر همیشه بر ضدّ پروردگارش پشتیبان شیطان است.'),
(2911, 25, 56, 'ای پیامبر، ما تو را جز نویددهنده و هشداردهنده نفرستادیم; بنابراین مسئول کفر مردم نیستی.'),
(2912, 25, 57, 'بگو: من برای رساندن پیام آسمانی خود هیچ مزدی از شما نمی‌خواهم، جز این که کسی بخواهد راهی به سوی پروردگارش برگیرد. آری، مزد من همین است که شما هدایت شوید.'),
(2913, 25, 58, 'و بر آن خدای زنده که نمی‌میرد توکّل کن و او را همراه با ستایش وی، از ناتوانی و بی دانشی و دیگر کاستی‌ها منزّه شمار، و همین بس که او به گناهان بندگانش آگاه است.'),
(2914, 25, 59, 'بر آن کسی توکل نما که آسمان‌ها و زمین و آنچه را که میان آن دو هست، در شش مرحله آفرید; سپس بر عرش فرمانروایی عالم استیلا یافت. اوست خداوند رحمان. پس به او روی بیاور، و از وی که آگاه است بپرس، که از هرچه خبر دهد حقیقت همان است.'),
(2915, 25, 60, 'و هنگامی که به کافران گفته شود: خداوند رحمان را سجده کنید، می‌گویند: و رحمان چیست؟ آیا چیزی را سجده کنیم که تو ما را به سجده برای آن فرمان می‌دهی؟ آنان نه تنها به این دعوت پاسخ نمی‌دهند، بلکه این دعوت بر گریزشان می‌افزاید.'),
(2916, 25, 61, 'پر خیر و برکت است آن خدایی که منازلی را در آسمان برای خورشید و ماه مقرّر کرد و چراغی فروزان و ماهی تابان در آن پدید آورد.'),
(2917, 25, 62, 'اوست آن که شب و روز را در پی یکدیگر آورده است برای کسی که بخواهد با بازگشت به فطرت خویش معارف الهی را دریابد یا با پرستش خدا و انجام کارهای شایسته او را سپاسگزاری کند.'),
(2918, 25, 63, 'بندگان واقعی خدای رحمان کسانی اند که روی زمین با خشوع گام برمی دارند (در معاشرت با مردم فروتنی می‌کنند و در برابر خدا خاضعند) و هنگامی که نادانان با سخنی جاهلانه آنان را خطاب می‌کنند، در پاسخ، سخنی مسالمت آمیز بر زبان می‌آورند.'),
(2919, 25, 64, 'آنان کسانی اند که شب را در عبادت خدا به سر می‌برند; گاه سر به سجده پروردگار دارند و گاه در برابر او به پا ایستاده‌اند.'),
(2920, 25, 65, 'و کسانی اند که می‌گویند: پروردگارا، عذاب دوزخ را از ما بگردان که عذاب آن شکنجه‌ای دائمی و پیوسته است.'),
(2921, 25, 66, 'به یقین، دوزخ بد جایگاه و بد اقامتگاهی است.'),
(2922, 25, 67, 'و کسانی اند که چون مالی را هزینه کنند ـ چه برای خود و چه برای دیگران ـ نه اسراف می‌کنند و نه تنگ می‌گیرند، بلکه هزینه کردن آنان در حد اعتدال و میان این دو حالت است.'),
(2923, 25, 68, 'و بندگان واقعی خدای رحمان کسانی اند که با وجود خدا معبود دیگری نمی‌خوانند و انسان را که خدا کشتن او را حرام کرده است نمی‌کشند، مگر این که قتل او به حق باشد، و زنا نمی‌کنند، و هر کس این کارها را انجام دهد با وِزر و وبال گناهانش رو به رو خواهد شد.'),
(2924, 25, 69, 'عذابش در روز قیامت دو چندان می‌شود و برای همیشه با خفّت و خواری در آن عذاب می‌ماند.'),
(2925, 25, 70, 'مگر کسانی که از شرک توبه کنند و به یکتایی خدا ایمان بیاورند و کار شایسته کنند، که اینان را خدا بدی‌های برخاسته از گناهانشان را از دل و جانشان می‌زداید و آنها را به آثاری نیکو تبدیل می‌کند، چرا که خداوند آمرزنده و مهربان است.'),
(2926, 25, 71, 'هر کس از گناهانش توبه کند و کار شایسته انجام دهد، قطعاً او چنان که بایسته است به سوی خدا باز آمده و درخور این است که خداوند بدی‌های او را به نیکی‌ها مبدّل سازد.'),
(2927, 25, 72, 'و بندگان واقعی خدای رحمان کسانی اند که در مجلس لهو و آوازه خوانی حاضر نمی‌شوند، و چون بر مردمی که به گناه و کار بیهوده مشغولند، بگذرند، از آنان روی می‌گردانند و می‌گذرند و خود را گرامی‌تر از آن می‌دانند که در محفلشان حضور یابند و با آنان همنشین شوند.'),
(2928, 25, 73, 'و کسانی اند که چون آیات پروردگارشان به آنان یادآوری شود، چشم و گوش بسته با آنها رو به رو نمی‌شوند، بلکه در آنها می‌اندیشند و آنها را از روی بصیرت باور می‌کنند.'),
(2929, 25, 74, 'و کسانی اند که می‌گویند: پروردگارا، از همسران و فرزندانمان نسلی پرهیزگار به ما ببخش که مایه خرسندی و روشنی چشم ما باشد، و چنان کن که در کارهای پسندیده و دستیابی به رحمت تو از همگان سبقت گیریم و پیشوا و مقتدای تقواپیشگان باشیم.'),
(2930, 25, 75, 'اینانند که به پاس آن که بر اطاعت خدا و دوری از گناهان صبر کردند، درجه رفیع بهشت را به آنها پاداش می‌دهند و در آن جا همواره با اموری مسرت بخش و سلامت آمیز روبه رویشان می‌سازند.'),
(2931, 25, 76, 'در بهشت که جایگاه و اقامتگاهی نیکوست، جاودانه‌اند.'),
(2932, 25, 77, 'بگو: خدا به شما کافران اعتنایی نمی‌کند، چرا که پیام او را دروغ شمرده و پیامبرش را تکذیب کرده‌اید. اگر لازم نبود که خدا برای اتمام حجّت، شما را به ایمان فراخواند، این اندازه هم به شما توجه نمی‌کرد. پس زود است که این تکذیبِ شما دامنگیرتان شود و به عذابی دائمی گرفتار شوید.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طا، سین، میم.'),
(2934, 26, 2, 'این آیاتی که در این سوره نازل خواهد شد، و آیاتی که پیش‌تر نازل شده است، همه آیات این کتابند که آسمانی بودن آن به خوبی آشکار است.'),
(2935, 26, 3, 'ای پیامبر، بیم آن می‌رود که تو جان خود را از غم این که مشرکانِ قوم تو به قرآن ایمان نمی‌آورند از دست بدهی!'),
(2936, 26, 4, 'اگر بخواهیم، آیتی مقهورکننده از آسمان بر آنان فرو می‌فرستیم; در نتیجه چنان در برابر آن خاضع می‌شوند که گردن هایشان فرو می‌افتد.'),
(2937, 26, 5, 'آنان چنانند که هیچ تذکّر جدیدی از سوی خدای رحمان برایشان نمی‌آید مگر این که از آن رویگردانند.'),
(2938, 26, 6, 'بی گمان آنان آیات الهی را دروغ شمرده‌اند، و به زودی اخبار آنچه به مسخره اش می‌گرفتند، به آنان خواهد رسید و به سزای تکذیب آیات الهی، عذاب‌هایی را در دنیا و کیفرهایی را در آخرت خواهند چشید.'),
(2939, 26, 7, 'چرا بر تکذیب آیات الهی و اعراض از آنها اصرار میورزند؟ مگر به زمین نظر نکرده‌اند که چه بسیار گیاهانی که همه جفت‌هایی زیبا هستند در آن رویانده‌ایم؟'),
(2940, 26, 8, 'قطعاً در این که خدا گیاهان را جفت آفریده تا به هدفی که دارند برسند، نشانه‌ای است بر این که آدمیان را به حال خود رها نمی‌کند; آنان را [با فرستادن پیامبران و کتاب‌های آسمانی] به راه سعادتشان هدایت می‌کند، ولی بیشترشان ایمان نمی‌آورند.'),
(2941, 26, 9, 'به یقین پروردگار توست آن مقتدر شکست ناپذیری که تکذیب کنندگان را مجازات می‌کند، و اوست آن رحمتگری که به حکم رحمتش قرآن را فرو می‌فرستد تا مردم را هدایت کند و هدایت یافتگان را نجات بخشد.'),
(2942, 26, 10, 'و یاد کن هنگامی را که پروردگارت موسی را ندا در داد که به سوی آن قوم ستم پیشه برو، قومی که با شرکورزی و شکنجه بنی اسرائیل ستم روا داشتند.'),
(2943, 26, 11, 'به سوی قوم فرعون برو، و به آنان بگو که آیا پروا نمی‌کنند و از ستمکاری باز نمی‌ایستند؟'),
(2944, 26, 12, 'موسی گفت: پروردگارا، می‌ترسم مرا تکذیب کنند و پیامم را دروغ شمرند.'),
(2945, 26, 13, 'و سینه‌ام [بر اثر تکذیبشان] تنگ می‌شود، و زبانم باز نمی‌شود تا بتوانم به خوبی از رسالت خود دفاع کنم، پس فرشته وحی را به سوی هارون بفرست و او را یاور من گردان.'),
(2946, 26, 14, 'و آنان بر من به سبب کشتن آن مردِ قبطی ادعای جرم دارند و می‌ترسم مرا به قصاص او بکشند.'),
(2947, 26, 15, 'خدا گفت: نه، تو را نخواهند کشت. پس هارون را به یاری موسی فرستاد و فرمود: هر دو با معجزاتی که به شما داده‌ام به سوی فرعونیان بروید، که ما نزد شما و آنان حاضریم و سخنانتان را می‌شنویم.'),
(2948, 26, 16, 'پس بر فرعون درآیید و بگویید: ما فرستاده پروردگار جهان هاییم.'),
(2949, 26, 17, 'و این پیام را برای تو آورده‌ایم که بنی اسرائیل را آزاد کن تا همراه ما به سرزمین قدس رهسپار شوند.'),
(2950, 26, 18, 'فرعون گفت: آیا تو را آن گاه که کودکی خردسال بودی در میان خود تربیت نکردیم؟ و تو چند سالی از عُمرت را در جمع ما بسر بردی.'),
(2951, 26, 19, 'و سرانجام کار خودت را که کاری بس نادرست بود کردی، و کسی را از ما کشتی و نعمت‌های مرا کفران کردی.'),
(2952, 26, 20, 'موسی در پاسخ فرعون گفت: آری، در آن زمان من برای دفاع از مردی اسرائیلی در برابر آن مرد قبطی به پا خاستم و آن کار را کردم، ولی نمی‌دانستم که به کشته شدن او می‌انجامد و چه فرجام ناگواری را برای من در پی دارد.'),
(2953, 26, 21, 'و وقتی شنیدم که بر کشتن من تصمیم گرفته‌اید، از شما ترسیدم و از دستتان گریختم و پروردگارم به من دانش و حکمت بخشید و مرا از پیامبران قرار داد.'),
(2954, 26, 22, 'آیا این که بر بنی اسرائیل ستم کرده و آنان را برده خود ساخته‌ای، نعمتی است که آن را بر من منّت می‌نهی؟'),
(2955, 26, 23, 'فرعون گفت: پروردگار جهان‌ها که تو را به رسالت فرستاده است چیست؟'),
(2956, 26, 24, 'موسی گفت: همان کسی که پروردگار آسمان‌ها و زمین و همه چیزهایی است که میان آن دو قرار دارد. شما اگر اهل یقین باشید، از تدبیری که بر جهان حاکم است درمی یابید که خدایی یگانه جهان را اداره می‌کند.'),
(2957, 26, 25, 'فرعون به کسانی که پیرامونش بودند از روی تعجب گفت: آیا نمی‌شنوید که موسی چه پاسخی می‌دهد؟'),
(2958, 26, 26, 'موسی گفت: پروردگار جهانیان، همان پروردگار شما و پروردگار پدران نخستین شماست.'),
(2959, 26, 27, 'فرعون به اطرافیان خود گفت: بی تردید، این پیامبرتان که به سوی شما فرستاده شده، دیوانه است.'),
(2960, 26, 28, 'موسی در ادامه سخنانش گفت: پروردگار جهانیان، همان پروردگار مشرق و مغرب و همه موجوداتی است که میان آن دو قرار دارد. اگر عقل خود را به کار گیرید درمی یابید که نظامی واحد بر آنها حاکم است و خدایی یکتا آنها را سامان می‌دهد.'),
(2961, 26, 29, 'فرعون گفت: ای موسی، سوگند یاد می‌کنم که اگر غیر از من معبودی اختیار کنی تو را در زمره آن زندانیان که از وضعشان خبر داری قرار می‌دهم.'),
(2962, 26, 30, 'موسی گفت: آیا اگر نشانه‌ای برای تو بیاورم که درستی رسالت مرا آشکار کند، باز هم مرا به زندان می‌افکنی؟'),
(2963, 26, 31, 'فرعون گفت: اگر راست می‌گویی که نشانه و معجزه‌ای داری آن را بیاور و نشان ده.'),
(2964, 26, 32, 'پس موسی عصای خود را افکند که ناگاه آن عصا اژدهایی آشکار شد.'),
(2965, 26, 33, 'و دستش را از زیر بغل بیرون کشید که ناگاه آن دست برای تماشاگران سفید و درخشان پدیدار شد.'),
(2966, 26, 34, 'فرعون وقتی با این حقیقتِ آشکار رو به رو گردید، چاره‌ای جز اتّهام ندید و به بزرگانی که پیرامونش بودند گفت: قطعاً این مرد ساحری زبردست و داناست.‌'),
(2967, 26, 35, 'می‌خواهد با سحر خود، شما را از سرزمینتان بیرون کند. با این وصف درباره او چه برنامه‌ای دارید و چه دستوری می‌دهید؟'),
(2968, 26, 36, 'مهتران قوم گفتند: در مجازات او و برادرش هارون شتاب مکن و کارشان را به تأخیر انداز و گروهی را به شهرها بفرست تا ساحرانی ماهر را گرد آورند و روانه دربار کنند.'),
(2969, 26, 37, 'آنان هر جادوگر زبردستِ دانایی را نزد تو می‌آورند.'),
(2970, 26, 38, 'پس جادوگران در وقتی که میان موسی و فرعونیان مقرّر شد و آن روزی معیّن بود گردآوری شدند.'),
(2971, 26, 39, 'و به مردم گفته شد: آیا شما هم در آن روز در میدان مبارزه اجتماع می‌کنید؟'),
(2972, 26, 40, 'اگر شما در آن روز گرد آیید، چنانچه جادوگران بر موسی پیروز شدند، امید است ما هم از آنان پیروی کنیم و به کیش آنان باشیم.'),
(2973, 26, 41, 'پس هنگامی که ساحران نزد فرعون آمدند، به او گفتند: اگر ما بر موسی پیروز شدیم، آیا حتماً مزدی برای ما خواهد بود؟'),
(2974, 26, 42, 'فرعون گفت: آری، و علاوه بر آن، قطعاً شما از مقرّبان خواهید شد.'),
(2975, 26, 43, 'موسی به ساحران گفت: افسون‌هایی را که تدارک دیده‌اید و می‌خواهید بیفکنید، همه را بیفکنید.'),
(2976, 26, 44, 'جادوگران ریسمان‌ها و عصاهایشان را افکندند و چنان وانمود کردند که آنها مارهایی پر جنب و جوش‌اند، و گفتند: به عزّت فرعون سوگند که ماییم پیروز این صحنه، و موسی شکست خورده است.'),
(2977, 26, 45, 'پس موسی عصایش را انداخت و ناگهان عصا آنچه را که ساحران به افسون ساخته بودند، به سرعت در کام خود فرو برد.'),
(2978, 26, 46, 'ساحران حقیقت را دریافتند و بی اختیار به درگاه خدا به سجده افتادند.'),
(2979, 26, 47, 'گفتند: ما به پروردگار جهان‌ها ایمان آوردیم،'),
(2980, 26, 48, 'همان پروردگار موسی و هارون.'),
(2981, 26, 49, 'فرعون به ساحران گفت: آیا پیش از آن که به شما اجازه دهم به او ایمان آوردید؟ و چاره‌ای جز این ندید که بگوید: قطعاً موسی بزرگ شماست که سحر را به شما آموخته است. به زودی خواهید دانست که با شما چه می‌کنم! حتماً یک دست و یک پای شما را به عکس یکدیگر خواهم برید و همه شما را به صلیب خواهم کشید.'),
(2982, 26, 50, 'آنان در پاسخ فرعون گفتند: این شکنجه برای ما زیانی نیست، زیرا بدین وسیله به سوی پروردگارمان باز می‌گردیم و به لقای او نایل می‌شویم.'),
(2983, 26, 51, 'و امید آن داریم که پروردگارمان گناهانمان را بر ما ببخشاید، چون ما نخستین کسانی بودیم که به فرستادگان او، موسی و هارون، ایمان آوردیم.'),
(2984, 26, 52, 'و به موسی وحی کردیم که بندگان من (بنی اسرائیل) را شبانه از مصر کوچ ده تا مورد تعقیب فرعونیان قرار گیرید، زیرا نجات شما در این خواهد بود.'),
(2985, 26, 53, 'بنی اسرائیل به همراه موسی شبانه از مصر حرکت کردند. چون فرعون از این ماجرا آگاه شد، گروهی را به شهرها فرستاد تا مردم را برای تعقیب بنی اسرائیل گرد آورند و روانه پایتخت کنند.'),
(2986, 26, 54, 'آنان به مردم می‌گفتند: این اسرائیلیان بازماندگانی بس اندک‌اند،'),
(2987, 26, 55, 'و آنان ما را با کردارشان به خشم آورده‌اند.'),
(2988, 26, 56, 'و ما مجموعه‌ای هستیم مصمم، هماهنگ و مراقب حیله دشمن.'),
(2989, 26, 57, 'بدین گونه ما فرعون و لشکریانش را در پی بنی اسرائیل روانه کردیم و آنان را از بوستان‌های پردرخت و چشمه سارهایشان بیرون راندیم.'),
(2990, 26, 58, 'و از گنج‌ها و جایگاهی نیکو که کاخ‌ها و خانه‌های مجلّل در آن داشتند خارج ساختیم.'),
(2991, 26, 59, 'آری، ماجرا این بود، و پس از هلاکت فرعونیان، آن بوستان‌ها و کاخ‌ها و گنجینه‌ها را به بنی اسرائیل به میراث دادیم.'),
(2992, 26, 60, 'پس از آن که فرعونیان بنی اسرائیل را تعقیب کردند، هنگام طلوع آفتاب به آنان رسیدند.'),
(2993, 26, 61, 'پس چون آن دو گروه به یکدیگر نزدیک شدند به گونه‌ای که همدیگر را می‌دیدند، همراهان موسی ترسان و لرزان گفتند: حتماً ما به چنگ لشکریان فرعون خواهیم افتاد.'),
(2994, 26, 62, 'موسی گفت: هرگز چنین نخواهد شد، زیرا پروردگارم با من است و مرا یاری می‌کند و به زودی مرا به راهی که موجب نجات ماست رهنمون می‌شود.'),
(2995, 26, 63, 'پس به موسی وحی کردیم: با عصایت به این دریا بزن. او عصایش را به دریا زد، بی درنگ دریا به صورت دو بخش شکافته شد و هر بخشی چون پاره کوهی بزرگ گشت.'),
(2996, 26, 64, 'پس ما موسی و همراهانش را وارد شکافِ دریا کردیم و دیگران (فرعون و لشکریانش) را به آن جا نزدیک ساختیم تا وارد آن شوند.'),
(2997, 26, 65, 'و موسی و کسانی را که با او بودند، همه را از دریا عبور دادیم و نجات بخشیدیم.'),
(2998, 26, 66, 'سپس دیگران را با بر هم آوردن آب دریا غرق کردیم.'),
(2999, 26, 67, 'همانا در این داستان نشانه‌ای است بر یکتایی خدا در تدبیر امور جهان و حقانیّت رسالت پیامبران، ولی بیشتر مردم آن عصر نه به یگانگی خدا ایمان آوردند و نه به رسالت پیامبران.'),
(3000, 26, 68, 'به یقین پروردگار توست آن مقتدر شکست ناپذیری که تکذیب کنندگان را مجازات می‌کند، و اوست آن مهربانی که به حکم رحمتش قرآن را فرو می‌فرستد تا مردم را هدایت کند و هدایت یافتگان را نجات بخشد.'),
(3001, 26, 69, 'و بر این مشرکان داستان ابراهیم را بخوان.'),
(3002, 26, 70, 'آن گاه که به پدر خود و قومش گفت: چه می‌پرستید؟'),
(3003, 26, 71, 'گفتند: بت‌هایی را می‌پرستیم و همواره به خاطر آنها بر آستانشان حضور داریم.'),
(3004, 26, 72, 'ابراهیم گفت: آیا وقتی که آنها را می‌خوانید، دعای شما را می‌شنوند؟'),
(3005, 26, 73, 'یا وقتی که آنها را پرستش می‌کنید سودی به شما می‌رسانند، و یا اگر دست از پرستش آنها بردارید زیانی به شما وارد می‌کنند؟'),
(3006, 26, 74, 'گفتند: از این درگذر ما پدران خود را یافتیم که چنین می‌کردند.'),
(3007, 26, 75, 'ابراهیم گفت: پس آیا دیدید چیزهایی را که می‌پرستیدید،'),
(3008, 26, 76, 'شما و پدران پیشترینتان؟'),
(3009, 26, 77, 'حال که برای پرستش هلاکت بار بت‌ها جز تقلید برهانی ندارید، بدانید که این بُت‌ها ـ از آن روی که دلیلی بر درستی عبادتشان نیست ـ دشمنان منند، ولی پروردگار جهانیان چون عبادتش بر برهان استوار است این گونه نیست.'),
(3010, 26, 78, 'او دشمن انسان نیست، بلکه پروردگاری مهربان است; او مرا آفریده و همو مرا به نعمت‌های این جهان و جهان دیگر هدایت می‌کند،'),
(3011, 26, 79, 'و اوست که به من خوراک و نوشیدنی می‌دهد،'),
(3012, 26, 80, 'و هنگامی که بیمار شوم تنها او مرا شفا می‌دهد،'),
(3013, 26, 81, 'و اوست آن که مرا می‌میراند و سپس زنده ام می‌کند،'),
(3014, 26, 82, 'و آن که امید دارم در روز جزا گناهم را بر من ببخشاید.'),
(3015, 26, 83, 'پروردگارا، دانش معارف دین و شناخت کردار درست را به من عطا کن و مرا به صالحان ملحق ساز.'),
(3016, 26, 84, 'پروردگارا، در میان نسل‌های آینده زبان راستگویی که مردم را به آیین من، آیین توحید دعوت کند پدید آور، زبانی که گویی زبان من است.'),
(3017, 26, 85, 'و مرا از میراث برانِ بهشت پرنعمت قرار ده.'),
(3018, 26, 86, 'و پدرم را بیامرز که او از گمراهان است.'),
(3019, 26, 87, 'و روزی که مردم برانگیخته می‌شوند دست از یاریم بر مدار که به یاری تو دل بسته‌ام.'),
(3020, 26, 88, 'روزی که نه مال برای انسان سودمند است و نه پسرانی که داشته است خیری به او می‌رسانند.'),
(3021, 26, 89, 'اما کسی که با دلی پاک از شرک و گناه نزد خدا آمده است، قطعاً از آن پاکی بهره خواهد برد.'),
(3022, 26, 90, 'آن روز بهشت را برای تقواپیشگان نزدیک می‌آورند.'),
(3023, 26, 91, 'و دوزخ را برای گمراهان آشکار می‌گردانند.'),
(3024, 26, 92, 'و به آنان گفته می‌شود: کجایند چیزهایی که به جای خدا می‌پرستیدید؟'),
(3025, 26, 93, 'آیا شما را یاری می‌دهند و عذاب را از شما می‌گردانند یا خود از کسی یاری می‌جویند تا عذاب را از خویشتن دور سازند؟'),
(3026, 26, 94, 'آن گاه بت‌ها و گمراهان همه با صورت به دوزخ افکنده می‌شوند.'),
(3027, 26, 95, 'و لشکریان ابلیس نیز همگی به دوزخ سرنگون می‌گردند.'),
(3028, 26, 96, 'آنها در حالی که در دوزخ با یکدیگر در ستیزند، به معبودانشان می‌گویند:'),
(3029, 26, 97, 'به خدا سوگند که ما در گمراهیِ آشکاری بودیم،'),
(3030, 26, 98, 'آن گاه که شما را با پروردگار جهانیان برابر می‌دانستیم،'),
(3031, 26, 99, 'و جز مجرمان که ما پیروشان بودیم، کسی ما را گمراه نکرد.'),
(3032, 26, 100, 'اینک از میان شفاعتگران، شفاعت کننده‌ای نداریم.'),
(3033, 26, 101, 'و برای ما هیچ دوست نزدیک و دلسوزی نیست.'),
(3034, 26, 102, 'ای کاش ما را به دنیا بازگشتی بود در این صورت از زمره مؤمنان می‌شدیم.'),
(3035, 26, 103, 'به راستی در داستان ابراهیم که بت‌ها را طرد کرد و بر حقّانیت توحید استدلال نمود، نشانه‌ای است بر این که فطرت انسان فطرت توحیدی است، ولی بیشتر قوم ابراهیم ایمان نیاوردند.'),
(3036, 26, 104, 'به یقین، پروردگار توست آن مقتدر شکست ناپذیری که تکذیب کنندگان را مجازات می‌کند، و اوست آن مهربانی که به حکم رحمتش قرآن را فرو می‌فرستد تا مردم هدایت شوند و هدایت یافتگان را نجات بخشد.'),
(3037, 26, 105, 'قوم نوح نیز پیامبران را تکذیب کردند،'),
(3038, 26, 106, 'آن گاه که برادرشان نوح به آنان گفت: آیا از عذاب الهی پروا نمی‌کنید؟'),
(3039, 26, 107, 'من برای هدایت شما فرستاده شده‌ام و در ابلاغ پیام خدا امین و درستکارم.'),
(3040, 26, 108, 'پس، از خدا بترسید و مرا فرمان برید.'),
(3041, 26, 109, 'من از شما هیچ مزدی بر ابلاغ رسالت خود نمی‌خواهم; مزد من جز برعهده پروردگار جهان‌ها نیست.'),
(3042, 26, 110, 'پس، از خدا بترسید و مرا فرمان برید.'),
(3043, 26, 111, 'اشراف قوم او در پاسخ گفتند: آیا به تو ایمان بیاوریم و حال آن که فرومایه‌ترین مردم از تو پیروی کرده‌اند؟'),
(3044, 26, 112, 'نوح گفت: من چه می‌دانم که اینان پیش از ایمان آوردنشان چه می‌کرده‌اند!'),
(3045, 26, 113, 'حساب آنان برعهده من نیست تا از اعمالشان جویا شوم. اگر اهل درک باشید، درمی یابید که حسابشان تنها بر پروردگار من است، زیرا او درخور سزا دادن است.'),
(3046, 26, 114, 'و من هرگز مؤمنان را از خود نمی‌رانم;'),
(3047, 26, 115, 'زیرا من جز پیامبری که وظیفه اش هشدار دادن آشکار به مردم است، نیستم.'),
(3048, 26, 116, 'گفتند: ای نوح، سوگند یاد می‌کنیم که اگر از دعوت خود دست برنداری، قطعاً از زمره سنگسارشدگان خواهی شد.'),
(3049, 26, 117, 'نوح از ایمان آوردن قومش مأیوس شد و گفت: (پروردگارا، قومِ من، مرا تکذیب کردند;'),
(3050, 26, 118, 'پس با فرستادن عذاب، میان من و آنان سخت جدایی بیفکن و مرا و همه مؤمنانی را که با منند، از آن عذاب رهایی بخش.'),
(3051, 26, 119, 'پس بر قوم نوح عذاب نازل کردیم، و او و کسانی را که با وی در آن کشتیِ انباشته از انسان‌ها و حیوانات بودند، نجات بخشیدیم.'),
(3052, 26, 120, 'آن گاه پس از نجات نوح و یارانش، بقیّه را غرق کردیم.'),
(3053, 26, 121, 'به راستی در این داستان نشانه‌ای است بر یکتایی خدا در تدبیر هستی و حقانیّت رسالتِ پیامبران، ولی بیشتر مردم آن عصر نه به یگانگی خدا ایمان آوردند و نه به رسالت پیامبران.'),
(3054, 26, 122, 'و قطعاً پروردگار توست آن مقتدر شکست ناپذیری که تکذیب کنندگان را مجازات می‌کند، و اوست آن مهربانی که به حکم مهربانی اش قرآن را فرو می‌فرستد تا مردم هدایت شوند و هدایت یافتگان را نجات بخشد.'),
(3055, 26, 123, 'قوم عاد نیز پیامبران را تکذیب کردند،'),
(3056, 26, 124, 'آن گاه که برادرشان هود به آنان گفت: آیا از عذاب الهی پروا نمی‌کنید؟'),
(3057, 26, 125, 'من برای هدایت شما فرستاده شده‌ام و در ابلاغ پیام خدا امین و درستکارم.'),
(3058, 26, 126, 'پس، از خدا بترسید و مرا فرمان برید.'),
(3059, 26, 127, 'من از شما هیچ مزدی بر ابلاغ رسالت خود نمی‌خواهم; مزد من جز برعهده پروردگار جهان‌ها نیست.'),
(3060, 26, 128, 'آیا به عبث، بی آن که شما را نیازی باشد، بر هر بلندی خانه‌ای رفیع که همچون علامتی نمایان است بنا می‌کنید؟'),
(3061, 26, 129, 'و کاخ‌های رفیع و استوار برای خود می‌سازید بدان امید که جاودانه بمانید؟'),
(3062, 26, 130, 'و هنگامی که شدّت عمل به خرج می‌دهید، همچون گردنکشان زیاده روی می‌کنید؟'),
(3063, 26, 131, 'پس، از خدا بترسید و مرا فرمان برید.'),
(3064, 26, 132, 'و از آن کس که شما را به نعمت‌هایی که می‌دانید مدد کرده است، پروا بدارید;'),
(3065, 26, 133, 'همو که شما را به دادنِ دام‌های فراوان و پسرانی چند مدد کرده است.'),
(3066, 26, 134, 'و شما را به بوستان‌ها و چشمه سارانی بسیار، یاری رسانده است.'),
(3067, 26, 135, 'آری، از او پروا کنید، زیرا من بر شما به سبب این کفر و ناسپاسی از عذاب روزی بزرگ بیمناکم.'),
(3068, 26, 136, 'عادیان گفتند: چه ما را پند دهی چه از پند دهندگان نباشی، برای ما یکسان است; بدان که ما به تو ایمان نخواهیم آورد.'),
(3069, 26, 137, 'این چیزهایی که تو ما را بدان فرا می‌خوانی، جز شیوه مردمان نخستین که اهل خرافات بودند چیزی نیست.'),
(3070, 26, 138, 'و ما هرگز عذاب نخواهیم شد و رستاخیزی در کار نیست.'),
(3071, 26, 139, 'پس هود را تکذیب کردند و ما هم آنان را به عذاب هلاک کردیم. به راستی در این داستان نشانه‌ای بر یکتایی خدا و حقانیت رسالت پیامبران است، ولی بیشتر مردم آن عصر نه به یگانگی خدا ایمان آوردند و نه به رسالت پیامبران.'),
(3072, 26, 140, 'و همانا پروردگار توست آن مقتدر شکست ناپذیری که تکذیب کنندگان را مجازات می‌کند، و اوست آن مهربانی که به حکم رحمتش قرآن را فرو می‌فرستد تا مردم هدایت شوند و هدایت یافتگان را نجات بخشد.'),
(3073, 26, 141, 'قوم ثمود نیز پیامبران را تکذیب کردند،'),
(3074, 26, 142, 'آن گاه که برادرشان صالح به آنان گفت: آیا از عذاب الهی پروا نمی‌کنید؟'),
(3075, 26, 143, 'همانا من برای هدایت شما فرستاده شده‌ام و در ابلاغ پیام خدا امین و درستکارم.'),
(3076, 26, 144, 'پس، از خدا بترسید و مرا فرمان برید.'),
(3077, 26, 145, 'من از شما هیچ مزدی بر ابلاغ رسالت خود نمی‌خواهم; مزد من جز برعهده پروردگار جهان‌ها نیست.'),
(3078, 26, 146, 'آیا گمان می‌کنید که در این نعمت‌هایی که شما را در این سرزمین فراگرفته است، ایمن از هر کیفری واگذارده می‌شوید؟'),
(3079, 26, 147, 'و شما را در بوستان‌ها و چشمه سارانی [فراوان]،'),
(3080, 26, 148, 'و در کشتزارها و درختان خرما که شکوفه‌هایی بهم تنیده و برهم نشسته دارند، به حال خود رها می‌کنند و حساب و کتابی در کار نیست؟'),
(3081, 26, 149, 'آیا کوه‌ها را می‌تراشید و ماهرانه از آنها خانه‌هایی [استوار] می‌سازید و گمان می‌کنید که خداوند به سراغ شما نخواهد آمد؟'),
(3082, 26, 150, 'پس، از خدا بترسید و مرا فرمان برید.'),
(3083, 26, 151, 'و از سرانِ اسرافکار خود فرمان مبرید و پیرو راه و رسم آنان مباشید;'),
(3084, 26, 152, 'همانان که در زمین تبهکاری می‌کنند و در پی اصلاح امور نیستند.'),
(3085, 26, 153, 'ثمودیان گفتند: ای صالح، جز این نیست که تو از کسانی هستی که سخت جادو شده‌اند و بر اثر آن عقل خود را از دست داده‌اند.'),
(3086, 26, 154, 'تو جز بشری مانند ما نیستی و اگر بشر درخور وحی بود می‌بایست به ما نیز وحی شود. اگر از راستگویانی و واقعاً از جانب خدا آمده‌ای، معجزه‌ای برای ما بیاور.'),
(3087, 26, 155, 'صالح به درخواست قوم خود، از دل کوه شتری ماده بیرون آورد و گفت: این ماده شتری است که سهمی از آب این سرزمین حق اوست و سهم روزی معیّن نیز حق شماست.'),
(3088, 26, 156, '[او را از حقّش محروم نکنید] و هیچ گزندی به او نرسانید که عذابِ روزی بزرگ شما را فرو می‌گیرد.'),
(3089, 26, 157, 'امّا ثمودیان شتر را پی کردند و کشتند و دیری نگذشت که با دیدنِ نشانه‌های عذاب پشیمان شدند. (۱۵۷'),
(3090, 26, 158, 'پس آن عذاب موعود آنان را فرو گرفت. به راستی در این داستان نشانه‌ای است بر یکتایی خدا و حقانیّت رسالت پیامبران، ولی بیشتر مردم آن عصر نه به یگانگی خدا ایمان آوردند و نه به رسالت پیامبران.'),
(3091, 26, 159, 'و همانا پروردگار توست آن مقتدر شکست ناپذیری که تکذیب کنندگان را مجازات می‌کند، و اوست آن مهربانی که به حکم رحمتش قرآن را فرو می‌فرستد تا مردم هدایت شوند و هدایت یافتگان را نجات بخشد.'),
(3092, 26, 160, 'قوم لوط نیز پیامبران را تکذیب کردند،'),
(3093, 26, 161, 'آن گاه که برادرشان لوط به آنان گفت: آیا از عذاب الهی پروا نمی‌کنید؟'),
(3094, 26, 162, 'من برای هدایت شما فرستاده شده‌ام و در ابلاغ پیام خدا امین و درستکارم.'),
(3095, 26, 163, 'پس از خدا بترسید و مرا فرمان برید.'),
(3096, 26, 164, 'من از شما هیچ مزدی بر ابلاغ رسالت خود نمی‌خواهم; مزد من جز برعهده پروردگار جهان‌ها نیست.'),
(3097, 26, 165, 'آیا از میان مردم جهان، شما به سراغ مردان می‌روید و با آنان درمی آمیزید؟'),
(3098, 26, 166, 'و اندامی از زنان که جفت‌های شما هستند و پروردگارتان آن را برایتان آفریده و به وسیله ازدواج برای شما حلال کرده است وامی گذارید؟ شما مردمی هستید که از مرز فطرت خود خارج شده‌اید.'),
(3099, 26, 167, 'گفتند: ای لوط، سوگند یاد می‌کنیم که اگر از سخن خود دست برنداری، قطعاً از اخراج شدگانِ از این شهر خواهی شد.'),
(3100, 26, 168, 'لوط با بی اعتنایی به تهدید آنان گفت: من از دشمنان این عمل زشت شما هستم.'),
(3101, 26, 169, 'پروردگارا، مرا و خانواده‌ام را که تنها گروندگان به منند از وزر و وبال آنچه این مردم انجام می‌دهند رهایی بخش.'),
(3102, 26, 170, 'پس او و خانواده اش را که همه مؤمن بودند، نجات بخشیدیم.'),
(3103, 26, 171, 'بجز همسر پیرش را که از کافران و باقی ماندگان در عذاب بود.'),
(3104, 26, 172, 'سپس دیگران را (همانان که لوط به سویشان فرستاده شد) هلاک کردیم.'),
(3105, 26, 173, 'و بر آنان بارانی از سنگپاره‌های آسمانی باراندیم، شوم بود باران کسانی که به آنان هشدار داده شد.'),
(3106, 26, 174, 'همانا در این داستان نشانه‌ای است بر یکتایی خدا و حقانیّت رسالت پیامبران، ولی قوم لوط بیشترشان نه به ربوبیّت خدا ایمان آوردند و نه به رسالت پیامبران.'),
(3107, 26, 175, 'و به یقین پروردگار توست آن مقتدر شکست ناپذیری که تکذیب کنندگان را مجازات می‌کند، و اوست آن $مهربانی که به حکم رحمتش قرآن را فرو می‌فرستد تا مردم هدایت شوند و هدایت یافتگان را نجات بخشد.'),
(3108, 26, 176, 'اهل (اَیْکه) (بیشه نزدیک مَدیَن) نیز پیامبران را تکذیب کردند،'),
(3109, 26, 177, 'آن گاه که شعیب به آنان گفت: آیا از کیفر خدا پروا نمی‌کنید؟'),
(3110, 26, 178, 'من برای هدایت شما فرستاده شده‌ام و در ابلاغ پیام خدا امین و درستکارم.'),
(3111, 26, 179, 'پس، از خدا بترسید و مرا فرمان برید.'),
(3112, 26, 180, 'من از شما هیچ مزدی بر ابلاغ رسالت خود نمی‌خواهم; مزد من جز برعهده پروردگار جهان‌ها نیست.'),
(3113, 26, 181, 'در خرید و فروش، پیمانه را پر کنید و از کسانی نباشید که با کاستن از پیمانه، به مردم زیان می‌رسانند.'),
(3114, 26, 182, 'و با ترازوی درست کالاها را وزن کنید.'),
(3115, 26, 183, 'و هنگام پیمانه یا وزن کردن، از کالاهای مردم نکاهید و در زمین فسادگرانه به تبهکاری نپردازید.'),
(3116, 26, 184, 'و از خدا پروا کنید همان که شما و مردمان نخستین را آفریده و تنفّر از زشتی‌ها را در سرشت آنان قرار داد.'),
(3117, 26, 185, 'اهل (اَیْکه) گفتند: ای شعیب، تو از کسانی هستی که سخت جادو شده و عقل خود را از دست داده‌اند.'),
(3118, 26, 186, 'و تو جز بشری مانند ما نیستی، و اگر بشر درخور وحی بود می‌بایست به ما نیز وحی شود، و قطعاً تو را از دروغگویان می‌پنداریم.'),
(3119, 26, 187, 'آن گاه از سر استهزا گفتند: اگر در ادّعای رسالت خود از راستگویانی، پاره‌هایی از آسمان بر ما بیفکن.'),
(3120, 26, 188, 'شعیب در پاسخ گفت: من اختیار چیزی را ندارم; پروردگارم به پیامد کارتان داناتر است.'),
(3121, 26, 189, 'پس او را تکذیب کردند در نتیجه عذابِ آن روز که از پاره ابری سایه افکن پدید آمد، آنان را فرو گرفت. به راستی آن عذاب، عذابِ روزی بزرگ بود.'),
(3122, 26, 190, 'همانا در این داستان نشانه‌ای است بر یکتایی خدا و حقانیّت رسالت پیامبران، ولی بیشتر آنان نه به ربوبیت خدا ایمان آوردند و نه به رسالت پیامبران.'),
(3123, 26, 191, 'و همانا پروردگار توست آن مقتدر شکست ناپذیری که تکذیب کنندگان را مجازات می‌کند، و اوست آن مهربانی که به حکم رحمتش قرآن را فرو می‌فرستد تا مردم هدایت شوند و هدایت یافتگان را نجات بخشد.'),
(3124, 26, 192, 'به راستی این قرآن از جانب پروردگار جهان‌ها نازل شده است.'),
(3125, 26, 193, '(روح الامین) آن را بر دلت فرود آورده است،'),
(3126, 26, 194, 'تا از کسانی باشی که مردم را هشدار می‌دهند.'),
(3127, 26, 195, 'جبریل آن را به زبان عربی که اهدافش را به روشنی بیان می‌کند، فرود آورده است.'),
(3128, 26, 196, 'و قطعاً خبر نزول آن در کتاب‌های پیامبران گذشته ثبت شده است.'),
(3129, 26, 197, 'آیا این مطلب که عالمان بنی اسرائیل نزول قرآن بر محمد را باور دارند، برای مشرکان نشانه‌ای بر حقانیت رسالت او نیست؟'),
(3130, 26, 198, 'اگر ما این قرآن را به زبان غیر عربی بر فردی غیر عرب فرو فرستاده بودیم،'),
(3131, 26, 199, 'و او آن را بر عرب زبانان می‌خواند، آنان به بهانه این که زبان آن را نمی‌فهمند، به آن ایمان نمی‌آوردند.'),
(3132, 26, 200, 'ما قرآن را در دل مشرکان مکه بدین گونه که از آن رویگردان شوند وارد ساختیم; چنان که با دیگر مجرمان نیز چنین خواهیم کرد، زیرا این سزای کفرپیشگی و جرم آنان است.'),
(3133, 26, 201, 'آری، مجرمان به قرآن ایمان نمی‌آورند تا این که آن عذاب دردناک را که هنگام مرگ به سراغشان می‌آید، مشاهده کنند.'),
(3134, 26, 202, 'عذابی که به طور ناگهانی و در حالی که از آن بی خبرند به سراغشان می‌آید.'),
(3135, 26, 203, 'و آن گاه درخواست مهلت می‌کنند و می‌گویند: آیا ما مهلتی خواهیم یافت؟'),
(3136, 26, 204, 'با این وصف، آیا باز هم می‌خواهند عذاب ما هرچه زودتر به سراغشان بیاید؟'),
(3137, 26, 205, 'آیا دانسته‌ای که اگر به آنان مهلت دهیم و چند سالی دیگر از متاع‌های دنیا بهره مندشان سازیم،'),
(3138, 26, 206, 'سپس عذابی که همواره به آنان وعده داده شده است، به سراغشان بیاید،'),
(3139, 26, 207, 'مهلت یافتن آنان و بهره بردنِ چند سالِ دیگر از دنیا، عذاب را از آنان دور نخواهد ساخت؟'),
(3140, 26, 208, 'ما هیچ شهری را به عذاب ویران نکردیم و مردم آن را هلاک نساختیم مگر این که برای آنان پیامبرانی هشداردهنده بود;'),
(3141, 26, 209, 'پیامبرانی که به آنان تذکر می‌دادند; و ما چنان نبوده‌ایم که پیش از فرستادن پیامبران، مردم را گرفتار عذاب کنیم، چرا که ما ستمکار نبوده‌ایم.'),
(3142, 26, 210, 'قرآن کلام خداست و شیاطین (جنّیان شرور) آن را فرود نیاورده‌اند.'),
(3143, 26, 211, 'آنان درخور این کار نیستند، زیرا موجوداتی فسادخواه و باطل گرا هستند و قرآن سراسر حق و پیراسته از باطل است، و آنها بر فرود آوردن قرآن ناتوانند،'),
(3144, 26, 212, 'چرا که از شنیدن اخبار آسمانی برکنار شده‌اند و هرگاه به عالم برتر نزدیک شوند تا خبری از غیب دریافت کنند، با شهاب‌های آسمانی رانده می‌شوند.'),
(3145, 26, 213, 'حال که قرآن کلام خداست و همگان را از شرک برحذر می‌دارد، مبادا با وجود خدا به پرستش معبودی دیگر روی بیاوری که در این صورت از عذاب شدگان خواهی شد.'),
(3146, 26, 214, 'و هم اکنون وقت آن است که خویشاوندان نزدیک‌تر خود را به کیفر شرک و بت پرستی هشدار دهی.'),
(3147, 26, 215, 'و برای کسانی که به تو ایمان آورده و از تو پیروی کرده‌اند بال و پر خویش فرو گستر و آنان را پیرامون خود گرد آور و با آنان مهربان باش و با بردباری به تربیتشان همّت گمار.'),
(3148, 26, 216, 'پس اگر خویشاوندانت از پذیرش دعوت تو سرپیچی کردند، به آنان بگو: من از کردار شما دوری می‌گزینم.'),
(3149, 26, 217, 'و در کار مؤمنان و کافران بر آن خداوند مقتدر و مهربانی توکل کن که با اقتدارش طغیانگران را کیفر و به رحمتش مؤمنان را رهایی می‌دهد;'),
(3150, 26, 218, 'همان کسی که تو را هنگامی که به نماز می‌ایستی می‌بیند،'),
(3151, 26, 219, 'و نیز حرکت تو را در میان سجده کنندگان.'),
(3152, 26, 220, 'آری، به خدا توکّل کن که همو شنوا و داناست.'),
(3153, 26, 221, 'ای مشرکان، شیطان قرآن را بر پیامبر القا نکرده است; می‌خواهید آیا به شما خبر دهم که شیاطین بر چه کسانی فرود می‌آیند و سخنانی را به آنان القا می‌کنند؟'),
(3154, 26, 222, 'آنها بر هر دروغ پرداز زشتکاری فرود می‌آیند، و پیامبر نه دروغی بر زبان آورده و نه هرگز کاری ناپسند کرده است.'),
(3155, 26, 223, 'آنها اخباری را که دزدانه از عالم برتر شنیده‌اند، به هر کس که بر او فرود آیند القا می‌کنند و بیشترشان دروغگو هستند و اخبار نادرست را در اختیار آن گروه از آدمیان قرار می‌دهند.'),
(3156, 26, 224, 'مشرکان می‌گویند: تو شاعری و قرآن شعر است; در صورتی که شاعران چون دروغ می‌بافند، گمراهان از آنان پیروی می‌کنند، ولی پیروان تو همه در پی حقیقت‌اند.'),
(3157, 26, 225, 'مگر ندیده‌ای که شاعران در هر وادیی گام می‌نهند و برای سخنان خود هیچ مرزی نمی‌شناسند؟'),
(3158, 26, 226, 'و چیزهایی می‌گویند که خود انجام نمی‌دهند.'),
(3159, 26, 227, 'جز آن شاعرانی که ایمان آورده و کارهای شایسته کرده و خدا را بسیار به یاد آورده و پس از آن که به وسیله هجو مشرکان مورد ستم قرار گرفته‌اند، با زبان شعر انتقام خود را از آنان گرفته‌اند. و کسانی که ستم کرده‌اند به زودی خواهند دانست که به چه جایگاهی باز خواهند گشت.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طا، سین. اینها که بر تو نازل می‌شود، آیات قرآن و آیات کتابی بلندمرتبه است که مقصود خود را به روشنی بیان می‌کند.'),
(3161, 27, 2, 'کتابی که هدایت کننده مؤمنان است و آنان را به رستگاری و ورود به بهشت نوید می‌دهد.'),
(3162, 27, 3, 'همانان که همواره نماز را برپا می‌دارند و زکات اموالشان را می‌پردازند و آنان به آخرت یقین دارند.'),
(3163, 27, 4, 'کسانی که سرای آخرت را باور ندارند، ما کارهایشان را برای آنان زیبا جلوه داده‌ایم، و چون منکر آخرتند، زندگی دنیا را بی هدف می‌دانند، از این رو همیشه سرگردانند.'),
(3164, 27, 5, 'آنان کسانی اند که در دنیا و آخرت بد عذابی خواهند داشت و در آخرت از همه زیانکارترند، زیرا کارهای پسندیده شان تباه شده است.'),
(3165, 27, 6, 'و حقّاً ـ ای پیامبر ـ قرآن از سوی حکیم و دانای بلندمرتبه بر تو تلقین می‌شود; از این رو هیچ کس نمی‌تواند آن را سست کند و در گزارش هایش ناراستی و در حکمش خطایی بیابد.'),
(3166, 27, 7, 'یاد کن زمانی را که موسی در شبی سرد، راه را گم کرد، به خانواده اش گفت: من آتشی دیدم به سراغ آن می‌روم و به زودی با یافتن راهنمایی از پیرامون آن، خبری از راه برای شما می‌آورم و گرنه شعله‌ای از آتش برخواهم گرفت و نزد شما خواهم آورد، باشد که آتشی برافروزید و گرم شوید.'),
(3167, 27, 8, 'پس هنگامی که نزد آتش آمد، آوایی به او رسید که: مبارک است آن کسی که با کلام خود، در این آتش بر تو تجلّی کرده و آن کسی که پیرامون این آتش است، و منزّه است خدا، پروردگار جهانیان، از این که مکانی او را احاطه کند.'),
(3168, 27, 9, 'ای موسی، حقیقت این است که منم خدای مقتدر شکست ناپذیر و حکیم که با تو سخن می‌گویم.'),
(3169, 27, 10, '[و سپس این صدا را شنید که:] عصایت را بیفکن. موسی عصایش را انداخت. عصا اژدهایی شد که همچون ماری کوچک به سرعت حرکت می‌کرد و در جنب و جوش بود. موسی که عصایش را این گونه دید، $هراسان روی گرداند و فرار کرد و باز نیامد. خدا به او گفت: ای موسی، نترس که تو رسول منی، و رسولان هنگامی که در پیشگاه من حضور دارند در امن و امانند و از هیچ چیز نمی‌ترسند.'),
(3170, 27, 11, 'ولی کسی که با ارتکاب گناه بر خود ستم کرده است ایمن نخواهد بود; مگر کسی که پس از گناه با توبه به درگاه خدا، نیکی را جایگزین بدی کرده باشد او نیز [تا وقتی که در پیشگاه من است] در امن و امان است و نباید از چیزی بهراسد. چرا که من آمرزنده و مهربانم.'),
(3171, 27, 12, 'و دستت را از گریبان زیر بغل خود کن آنگاه که برآید بی هیچ عیبی سپید و درخشان بیرون آید. این دو معجزه از جمله معجزات نُه گانه‌ای است که به سوی فرعون و قوم او می‌بری و به آنان ارائه می‌کنی; بی تردید آنان مردمی هستند که از مرز فطرت خارج شده‌اند.'),
(3172, 27, 13, 'پس هنگامی که معجزات ما که همه به روشنی حکایت از حقّانیت رسالت موسی داشت، به آنان رسید گفتند: (این جادویی آشکار است).'),
(3173, 27, 14, 'فرعون و اطرافیانش با این که دل هایشان به درستی معجزات ما یقین داشت آنها را به ستم و از سر تکبّر انکار کردند. پس بنگر فرجام فسادگران چگونه شد.'),
(3174, 27, 15, 'و سوگند یاد می‌کنم که ما به داود و فرزندش سلیمان، دانشی والا عطا کردیم، و آن دو گفتند: ستایش خدای را که با این نعمت دانش و دیگر نعمت‌هایی که به ما داده، ما را بر بسیاری از بندگان با ایمانش برتری بخشیده است.'),
(3175, 27, 16, 'و پس از داود، سلیمان دارایی‌ها و سلطنت او را به ارث برد و گفت: ای مردم، به من و پدرم زبان پرندگان را آموخته‌اند و از هر نعمتی، فراوان به ما داده شده است. به راستی این است فضل و عنایت آشکار که خدا به ما ارزانی داشته است.'),
(3176, 27, 17, 'و برای سلیمان سپاهیانش از جنّ و انس و پرندگان، همه گردآوری شدند و هر گروهی از آنها در جایگاه مخصوص خود مستقر می‌گردیدند و از پراکندگی و به هم آمیختگی بازداشته می‌شدند.'),
(3177, 27, 18, 'سلیمان و سپاهیانش حرکت کردند تا بر درّه مورچگان درآمدند. در آن میان مورچه‌ای گفت: ای مورچگان، به خانه هایتان درآیید، مبادا سلیمان و سپاهیانش که از وجود شما در این جا ناآگاهند، پایمالتان کنند.'),
(3178, 27, 19, 'سلیمان از سخن آن مورچه لب به خنده گشود و با تبسّمی، شادمانی خود را از نعمت‌های الهی آشکار کرد و گفت: پروردگارا، در دلم جوششی پدید آور تا نعمت تو را که به من و پدر و مادرم ارزانی داشته‌ای سپاس بگزارم و در من شوری به پا کن تا کاری را که تو می‌پسندی انجام دهم، و مرا فردی شایسته گردان و در میان بندگان شایسته ات درآور.'),
(3179, 27, 20, 'و حال آن پرندگان را که ملازم رکابش بودند جویا شد; هدهد در میان آنها نبود. گفت: مرا چه شده است که هدهد را نمی‌بینم؟ آیا او از آنان است که غیبت کرده‌اند [و از حضور در میان لشکر باز ایستاده اند]؟'),
(3180, 27, 21, 'بی گمان او را به عذابی سخت عذاب می‌کنم و یا سرش را می‌بُرم یا این که بر غیبتِ خود دلیلی روشن برای من بیاورد.'),
(3181, 27, 22, 'هدهد پس از درنگی نه چندان طولانی به حضور سلیمان رسید. سلیمان سبب غیبتش را پرسید. هدهد گفت: به چیزی آگاهی یافتم که تو از آن بی خبری; برای تو از دیار (سبا) خبری مهم و صحیح آورده‌ام.'),
(3182, 27, 23, 'من زنی را یافتم که بر مردم سبا حکومت می‌کرد و از تمام آنچه که پادشاهان برای اداره کشور خود به آن نیازمندند به او داده شده بود و تختی بزرگ داشت.'),
(3183, 27, 24, 'او و قومش را یافتم که به جای این که برای خدا سجده کنند، برای خورشید سجده می‌کردند، و شیطان کارهای ناروایشان را برای آنان آراسته و در نتیجه آنان را از راه خدا و پرستش او باز داشته بود، از این رو به حق راه نمی‌یابند.'),
(3184, 27, 25, 'آری، شیطان برای آنان چنین آراسته بود که برای خدا سجده نکنند; همان خدایی که موجودات را از عدم به درآورده و در آسمان‌ها و زمین به آنها وجود بخشیده و آنچه را که پنهان می‌کنید و آنچه را که آشکار می‌سازید همه را می‌داند.'),
(3185, 27, 26, 'خداست که هیچ معبودی جز او شایسته پرستش نیست و خداوندگار عرش بزرگ است.'),
(3186, 27, 27, 'سلیمان گفت: خواهیم دید که آیا راست گفتی یا از دروغگویانی.'),
(3187, 27, 28, 'پس نامه‌ای نگاشت و به هدهد گفت: این نامه مرا ببر و به سوی ملکه سبا و بزرگان دربارش بیفکن، سپس از جمع آنان به کناری برو، و در جایی که آنان را می‌بینی قرار گیر، آن گاه ببین درباره این نامه چه سخنانی را ردّ و بدل می‌کنند.'),
(3188, 27, 29, 'هدهد نامه سلیمان را به ملکه سبا رسانید. وقتی ملکه از مضمون نامه آگاه شد، به بزرگان دربارش گفت: ای مهتران، نامه‌ای گرانقدر به سوی من افکنده شده است;'),
(3189, 27, 30, 'زیرا آن نامه از سلیمان است و با نام خدا آغاز شده و مَطلع آن این است: به نام خداوند گسترده مهرِ مهربان.'),
(3190, 27, 31, 'و مضمون نامه این است: بر من برتری مجویید و به حالت تسلیم نزد من آیید.'),
(3191, 27, 32, 'پس به منظور مشورت گفت: ای مهتران، در مورد کاری که برای من پیش آمده است رأی خود را باز گویید، که من در اداره امور هرگز خودرأی نبوده و هیچ گاه بدون حضور شما در کاری تصمیم نگرفته‌ام.'),
(3192, 27, 33, 'سران گفتند: اندوه مدار، که ما نیرومندیم و لشکری دلاور و آماده نبرد داریم، فرمان از آنِ توست و ما $فرمانبرداریم، پس بنگر چه فرمان می‌دهی.'),
(3193, 27, 34, 'ملکه گفت: اکنون عزم نبرد ندارم، زیرا پادشاهان وقتی در جنگ پیروز می‌شوند و دیاری را فتح می‌کنند و بدان وارد می‌شوند، آن را ویران می‌کنند و عزیزانش را با قتل و اسارت و زورگویی، خوار می‌سازند و همواره چنین می‌کنند.'),
(3194, 27, 35, 'من پیش از آن که به جنگ اقدام کنم، خِبرگانی را با هدیه‌ای به سوی آنان می‌فرستم، پس می‌نگرم که آن فرستادگان با چه خبری باز می‌گردند و قدرت و مُکنت سلیمان را چگونه ارزیابی می‌کنند.'),
(3195, 27, 36, 'چون سرپرست فرستادگان نزد سلیمان آمد و هدیه ملکه را تقدیم سلیمان کرد، سلیمان گفت: آیا مرا به مالی ناچیز یاری می‌دهید؟ این نبوت و سلطنتی که خدا به من عطا کرده بهتر است از آنچه به شما داده است. من این کار شما را ناپسند می‌دانم و ناپسندتر این که شما به هدیه خود شادمانید و آن را بزرگ می‌شمرید.'),
(3196, 27, 37, 'به سوی آنان باز گرد که قطعاً با سپاهیانی که قوم سبا را در همان دیارشان در برابر آن هیچ تاب و توانی نباشد به سویشان خواهیم آمد و آنان را با ذلت و خواری از آن جا بیرون خواهیم کرد.'),
(3197, 27, 38, 'سلیمان پس از بازگرداندنِ فرستادگان ملکه به بزرگان دربارش گفت: ای مهتران، کدام یک از شما تختِ سلطنتِ آن زن را ـ پیش از آن که با حالت تسلیم نزد من آیند ـ برای من می‌آورَد؟'),
(3198, 27, 39, 'پلیدی مطرود که از جنّیان بود گفت: من آن را پیش از آن که از جایگاهت برخیزی [و مجلس خود را ترک گویی] برای تو می‌آورم و قطعاً من بر این کار توانا و امینم.'),
(3199, 27, 40, 'و آن کس که دانشی فراوان از کتاب الهی نزد خود داشت به سلیمان گفت: من آن را پیش از آن که نگاهت به تو بازگردد برای تو می‌آورم. سلیمان به او رخصت داد و ناگهان تخت پدیدار شد. وقتی سلیمان دید تخت نزد او قرار گرفته است گفت: این از فضل پروردگار من است; آن را به من ارزانی داشته است تا مرا بیازماید که آیا نعمتش را سپاس می‌گزارم یا ناسپاسی می‌کنم. و هر کس سپاسگزاری کند، تنها به سود خود سپاسگزاری می‌کند، و هر کس ناسپاسی کند [بر خود ستم کرده است که] پروردگار من بی نیاز و کریم است.'),
(3200, 27, 41, 'سلیمان پس از آن که دریافت که ملکه به سوی او رهسپار شده است، گفت: تخت او را [با دگرگونی‌هایی که در آن پدید می‌آورید] برای او ناشناخته سازید تا بنگریم آیا بر شناختن تخت خود راه می‌یابد یا از کسانی است که به آن پی نمی‌برند. ما بدین وسیله درک و خرد او را می‌آزماییم.'),
(3201, 27, 42, 'پس هنگامی که ملکه به حضور سلیمان رسید، به او گفته شد: آیا تخت تو همین گونه است؟ پاسخ داد: گویی این همان است. و دریافت که سلیمان با این کار دارای قدرتی برتر است; از این رو گفت: ما پیش از این به قدرت شگفت آور سلیمان آگاهی یافته و تسلیم او شده بودیم.'),
(3202, 27, 43, '[اما او هنوز به یکتایی خدا و رُبوبیّت او ایمان نیاورده بود] و آنچه جز خدا می‌پرستید، وی را از این که تسلیم خدا شود و به او ایمان بیاورد بازداشته بود، چرا که او از مردمی به شمار می‌رفت که کفرپیشه بودند.'),
(3203, 27, 44, 'به او گفته شد: به کاخ سلیمان درآی. او روانه کاخ شد. وقتی آن جا را دید، گمان کرد برکه آبی است، ازاین رو جامه از ساق هایش برگرفت. سلیمان به او گفت: این برکه نیست. کاخی است صاف و صیقلی شده از شیشه‌ها [ی بلورین]. ملکه دریافت که این نشانه‌ای بر نبوت اوست، زیرا بشر عادی قدرت چنین کاری ندارد، ازاین رو گفت: پروردگارا، من با پرستش خورشید بر خود ستم کردم و اکنون با سلیمان و همچون او در برابر خدا، پروردگار جهان‌ها، تسلیم شدم.'),
(3204, 27, 45, 'و همانا به سوی قوم ثمود برادرشان صالح را به رسالت فرستادیم با این پیام که خدا را بپرستید و از پرستش غیر او دست بدارید. ولی آنان بی آن که انتظار رود دو گروه شدند; گروهی مؤمن و گروهی کافر که با یکدیگر به جدال و ستیزه می‌پرداختند.'),
(3205, 27, 46, 'کافرانِ قومش پس از آن که آن ناقه را که معجزه صالح بود کشتند، به تمسخر سراغ عذاب موعود را می‌گرفتند. صالح به آنان گفت: ای قوم من، چرا پیش از آن که در سایه ایمان و کردار شایسته، خوبی را برای خود بخواهید، شتاب در بدی و عذاب را می‌طلبید؟ چرا از شرک توبه نمی‌کنید و از خدا نمی‌خواهید که شما را بیامرزد، شاید مورد رحمت او قرار گیرید و عذاب را از شما بگرداند؟'),
(3206, 27, 47, 'گفتند: از آن زمان که سخن از توحید گفته‌ای و عدّه‌ای به تو گرویده‌اند، ما دچار رنج‌های فراوان شده‌ایم; ازاین رو ما به تو و کسانی که با تو هستند و ایمان آورده‌اند، فال بد می‌زنیم. صالح گفت: بلا و محنت شما نزد خداست; اوست که به مقتضای اعمالتان آن را برای شما مقرّر کرده است و من در آن نقشی ندارم، بلکه شما مردمی هستید که مورد آزمایش قرار گرفته‌اید و این بلاها و رنج‌ها وسیله آزمون شماست.'),
(3207, 27, 48, 'و در آن شهر، نُه گروه [از سران طوایف] بودند که در زمین به فساد و تبهکاری می‌پرداختند و در صدد اصلاح امور نبودند.'),
(3208, 27, 49, 'آنان همقسم شده به خدا سوگند یاد کردند و گفتند: حتماً به او و خانواده اش شبیخون می‌زنیم و آنان را می‌کُشیم، سپس به ولیّ او خواهیم گفت: ما شاهد کشتار او و خانواده اش نبوده‌ایم و تأکید می‌کنیم که ما راست می‌گوییم.'),
(3209, 27, 50, 'آنان نیرنگ به کار بردند و ما هم به سزای نیرنگشان با آنان مکر کردیم، در حالی که از مکر ما بی خبر بودند.'),
(3210, 27, 51, 'پس بنگر که فرجام نیرنگشان چگونه بود; ما آنان [= سران طوایف] و قومشان را به تمامی نابود کردیم.'),
(3211, 27, 52, 'و اینها خانه‌های آنهاست که به سبب آن که ستم کردند از سکنه خالی شده است. به راستی در داستان صالح و قوم او برای مردمی که اهل دانشند آیتی است [بر یکتایی خدا و حقّانیت رسالت پیامبران].'),
(3212, 27, 53, 'آری، کافران را هلاک کردیم و کسانی را که ایمان آورده و تقوا پیشه کرده بودند، نجات بخشیدیم.'),
(3213, 27, 54, 'و لوط را یاد کن، آن گاه که به قومش گفت: آیا به آن کار زشت می‌پردازید در حالی که یکدیگر را نظاره می‌کنید؟'),
(3214, 27, 55, 'آیا شما از روی شهوت به جای زنان به سراغ مردان می‌روید؟ شما سزاوار ملامتید بلکه ملامت و سرزنشِ شما بی فایده است; زیرا شما مردمی هستید که نادانی می‌کنید.'),
(3215, 27, 56, 'ولی پاسخ قومش جز این نبود که گفتند: خاندان لوط را از شهر و دیارتان بیرون کنید. و به تمسخر گفتند: اینها مردمی هستند که خواهان پاکی اند و نمی‌خواهند دامنشان آلوده شود!'),
(3216, 27, 57, 'پس او و خانواده اش را نجات دادیم، جز همسرش را که مقدّر کردیم از باقی ماندگان در عذاب باشد.'),
(3217, 27, 58, 'و بر قومش بارانی از سنگپاره‌ها باراندیم. شوم بود باران کسانی که به آنان هشدار داده شد.'),
(3218, 27, 59, 'ای پیامبر، بگو: ستایش همه از آنِ خداست. و سلام بر بندگانی از او که آنها را برگزیده است. آیا خدا بهتر است یا آنچه شریک او قرار می‌دهند; همانها که از رساندن کمترین خیری به پرستشگران خود ناتوانند؟'),
(3219, 27, 60, 'آیا خدایان اهل شرک بهترند یا آن کس که آسمان‌ها و زمین را آفرید و از آسمان برای شما آبی فرو فرستاد و به وسیله آن باغ‌هایی شاداب رویانیدیم که شما را توان آن نبود که درختانش را برویانید; آیا با وجود خدا معبودی هست بلکه مشرکان مردمی هستند که از راه درست منحرف می‌شوند و از خدا به غیر او روی می‌کنند.'),
(3220, 27, 61, 'آیا آن کس که زمین را به گونه‌ای مستقر ساخته است که شما را نلرزاند و در دل آن نهرهایی پدید آورده و برای آن کوه‌هایی استوار قرار داده و میان دو دریا [= دریای شور و شیرین] حایلی نهاده است تا به هم نیامیزند، آیا او بهتر است یا معبودان اهل شرک؟ آیا با وجود خدا معبودی شایسته پرستش، هست؟ تلاش برای هدایت مشرکان بی فایده است، زیرا بیشترشان دانشی ندارند.'),
(3221, 27, 62, 'آیا آن کس که خواسته هر درمانده‌ای را ـ آن گاه که او را بخواند ـ اجابت می‌کند و گرفتاری اش را برطرف می‌سازد و شما آدمیان را در زمین جانشین قرار می‌دهد، بهتر است یا خدایان اهل شرک؟ آیا با وجود خدا معبودی شایسته پرستش، هست؟ شما کفرپیشگان چه اندک درمی یابید و متذکّر می‌شوید.'),
(3222, 27, 63, 'آیا آن کس که شب هنگام شما را در تاریکی‌های خشکی و دریا راه می‌نماید و آن کس که بادها را به عنوان نویدبخش نزول رحمتش پیشاپیش باران می‌فرستد، بهتر است یا خدایان اهل شرک که سودی برای پرستشگران خود ندارند؟ آیا با وجود خدا معبودی شایسته پرستش، هست؟ پس خداوند والاتر از آن است که شریکی برای او بپندارند.'),
(3223, 27, 64, 'آیا آن کس که آفریدگان را بار نخست پدید می‌آورد و سپس آنها را با برانگیختن مجدد به سوی خود باز می‌گرداند، و آن کس که امورتان را تدبیر می‌کند و از آسمان و زمین به شما روزی می‌دهد، او بهتر است یا معبودان اهل شرک که هیچ کاری از آنها ساخته نیست؟ آیا با وجود خدا معبودی شایسته پرستش، هست؟ بگو: اگر راست می‌گویید که اینها شایسته پرستش اند برهان خود را بیاورید.'),
(3224, 27, 65, 'بگو: از فرشتگان و جنّیان و پارسایان بشر که در آسمان‌ها و زمینند و شما آنها را می‌پرستید، هیچ کدام از غیب آگاه نیستند; تنها خداست که از غیب آگاه است. آنها درنمی یابند که چه زمانی قیامت برپا می‌شود و کی برانگیخته می‌شوند.'),
(3225, 27, 66, 'بلکه دانش منکران معاد درباره آخرت به پایان رسیده است زیرا همه دانش خود را در امور دنیا به کار برده و درباره آخرت اطّلاعی کسب نکرده‌اند و چنانند که گویی آن را نشنیده‌اند، نه، بلکه خبرش به گوش آنان رسیده ولی در شک و تردید به سر می‌برند. بلکه آنان کوردل اند و از درک آن ناتوانند.'),
(3226, 27, 67, 'و آنان که کافر شدند گفتند: آیا هنگامی که ما و پدرانمان مُردیم و خاک شدیم، حتماً زنده از گورها بیرون آورده می‌شویم؟ این چگونه ممکن است؟'),
(3227, 27, 68, 'به یقین این را به ما وعده داده‌اند و پیش‌تر نیز آن را به پدرانمان وعده داده بودند، ولی چنین چیزی تاکنون تحقّق نیافته است. این جز افسانه‌هایی که مردمان نخستین بافته‌اند چیزی نیست.'),
(3228, 27, 69, 'ای پیامبر، به آنان بگو: در زمین سیر و سفر کنید و بنگرید فرجام آن مجرمانی که رستاخیز را دروغ شمردند چگونه بوده است.'),
(3229, 27, 70, 'و بر آنان اندوه مخور که چرا بر کفرشان اصرار میورزند، و از این که نیرنگ می‌زنند تا دعوت تو را خاموش کنند دلتنگ مباش، زیرا کارشان برعهده ماست.'),
(3230, 27, 71, 'منکران رستاخیز، به تمسخر، از پیامبر و مؤمنان می‌پرسند: اگر راست می‌گویید که کافران در دنیا و آخرت گرفتار عذاب می‌شوند، این وعده کی تحقق خواهد یافت؟'),
(3231, 27, 72, 'بگو: من امید آن دارم که بخشی از عذابی که شتاب در آن را می‌طلبید در پی شما باشد.'),
(3232, 27, 73, 'و به راستی پروردگارت از فضل خود به مردم نعمت و رحمت بخشیده و عذاب را از آنان به تأخیر افکنده است، ولی بیشترشان بر این نعمت سپاسگزاری نمی‌کنند، بلکه به تمسخر شتاب در عذاب را می‌خواهند.'),
(3233, 27, 74, 'آنان گمان نکنند که تأخیر عذاب به علّت ناآگاه بودن خدا از کفرشان است، زیرا پروردگارت آنچه را که سینه هایشان نهان می‌دارد و آنچه را که خود آشکار می‌کنند، قطعاً می‌داند.'),
(3234, 27, 75, 'و در آسمان و زمین هیچ نهانی نیست جز این که در کتابی که روشنگر حقایق است ثبت شده است.'),
(3235, 27, 76, 'همانا این قرآن بیشتر احکام و معارفی را که بنی اسرائیل درباره آنها اختلاف دارند، بر آنان حکایت می‌کند و درست و نادرست آنها را آشکار می‌سازد.'),
(3236, 27, 77, 'و قطعاً این قرآن مؤمنان را به حق رهنمون می‌شود و برای آنان مایه رحمت است.'),
(3237, 27, 78, 'به یقین، پروردگار تو با حکم خود میان بنی اسرائیل داوری خواهد کرد، و اوست آن مقتدر دانا که در اراده خود مغلوب نمی‌شود و در حکمش خطا نمی‌کند.'),
(3238, 27, 79, 'پس بر خدا توکّل کن و کار مشرکان و کافران بنی اسرائیل را به او واگذار و بدان که تو قطعاً بر حقّی آشکار قرار داری.'),
(3239, 27, 80, 'آری، کارشان را به خدا واگذار، زیرا آنان مردگانند و تو نمی‌توانی سخن خود را به مردگان بشنوانی و آنان را هدایت کنی. آنان کرانی هستند که تو از شنواندن و رساندن دعوتت به آنان ناتوانی، به ویژه آن گاه که از تو روی برمی تابند و پشت می‌کنند و گر نه امکان داشت که با اشاره سخن خود را به آنان بفهمانی و هدایتشان کنی.'),
(3240, 27, 81, 'ای پیامبر. اینان کورند و به بیراهه می‌روند، و تو نمی‌توانی کوران را راه بنمایی و آنان را از گمراهی باز داری; تو فقط می‌توانی سخنت را به کسانی بشنوانی و آنان را هدایت کنی که در آیات ما ـ که همه بیانگر توحید است ـ بیندیشند و تصدیق کنند. اینانند که در برابر ما تسلیم می‌شوند و ایمان می‌آورند.'),
(3241, 27, 82, 'و هنگامی که بر کافران زمانِ آن فرا رسد که آیتی خارق العاده به آنان ارائه شود تا آنان را به اعتراف به حق وادار کند، جنبنده‌ای را که با آنان سخن خواهد گفت، از زمین برای آنان پدید می‌آوریم; زیرا در آن هنگام مردم بر اثر از کف نهادن استعداد ایمان به نشانه‌های روشن ما یقین نمی‌کنند.'),
(3242, 27, 83, 'و یاد کن روزی را که از هر امّتی، گروهی از کسانی را که آیات ما را دروغ انگاشتند بر می‌انگیزیم و برانگیخته شدگان از این که پراکنده شوند بازداشته می‌شوند تا همه آنان یکجا گردآوری شوند.'),
(3243, 27, 84, 'تا چون در جایگاهی که با آنان سخن گفته می‌شود حاضر شوند، خدا به آنان می‌گوید: آیا نشانه‌ها و آیات مرا دروغ انگاشتید و با بی توجهی در آنها اندیشه نکردید و آنها را ندانستید؟ شما جز تکذیب آیات من چه می‌کردید؟'),
(3244, 27, 85, 'و چون با تکذیب آیات خدا بر خود ستم روا داشتند، این سخن که خدا ستمکاران را هدایت نمی‌کند، در مورد آنان به وقوع پیوست; از این رو آنان دیگر عذری برای خود نمی‌یابند و هیچ سخنی نمی‌گویند.'),
(3245, 27, 86, 'آیا ندیده‌اند که ما شب را پدید آورده‌ایم تا در آن آرامش بیابند، و روز را روشن قرار داده‌ایم تا در پرتو آن آیات الهی را بنگرند؟ به راستی در این امور برای مردمی که ایمان دارند نشانه‌هایی بر یکتایی خدا هست.'),
(3246, 27, 87, 'و روزی که در صور دمیده شود، پس با نخستین نفخه، هر که در آسمان‌ها و هر که در زمین است به وحشت افتد و بمیرد، مگر آن کس که خدا بخواهد، و با نفخه دوم همگان ـ چه آنان که از دَهشت مرده و چه آنان که استثنا شده‌اند ـ به حضور خدا آیند.'),
(3247, 27, 88, 'تو این کوه‌ها را که اینک بی حرکت می‌پنداری آن روز که در صور دمیده شود خواهی دید که بسان ابرها به حرکت در می‌آیند و این جهان فرو می‌ریزد و با فرو ریختنش آخرت برپا می‌شود و هستی به کمال می‌رسد، زیرا خدا آن را ساخته است; همو که هر چیزی را استوار و متقن پدید آورده است. به یقین، او در آن روز به جزئیات آنچه شما می‌کردید آگاه است.'),
(3248, 27, 89, 'کسانی که فقط کار نیک آورند، آنان را پاداشی بهتر از آنچه کرده‌اند خواهد بود و در آن روز، آنان از ترس و دَهشت ایمنند.'),
(3249, 27, 90, 'و کسانی که فقط کار ناشایسته آورند، در آن روز بر صورت هایشان در آتش افکنده شوند [و به آنان گویند] آیا جز آنچه می‌کردید سزا داده می‌شوید؟ آری این کیفر، نمودِ همان اعمال شماست.'),
(3250, 27, 91, 'بگو: جز این نیست که فرمان یافته‌ام خداوندگار این شهر مکّه را پرستش کنم; همان که آن را حرمت بخشید و همه چیز از آنِ اوست، و نیز فرمان یافته‌ام از کسانی باشم که تسلیمِ خواستِ خداوندند.'),
(3251, 27, 92, 'و نیز به من امر شده است که قرآن را بر مردم تلاوت کنم. پس آن که با این قرآن هدایت یابد، خود بهره هدایت خویش بَرَد و سودش به من نمی‌رسد، و آن که هدایت قرآن نپذیرد و گمراه شود، وزر و وبالش بر خود اوست و به من نمی‌رسد. ای پیامبر، بگو: این بدان جهت است که من انذارگری از انذارگرانم، نه اختیاردار گمراهان و نه مسئول رفتار آنان.'),
(3252, 27, 93, 'و بگو: ستایش از آنِ خداوند است. او در قیامت و نیز در آستانه برپایی آن ـ که دیری نمی‌پاید ـ نشانه‌ها و آیات خود که همگان را به پذیرش حق وامی دارد به شما نشان خواهد داد و شما بی درنگ آنها را خواهید شناخت و حق را باور خواهید کرد. و پروردگارت از آنچه می‌کنید غافل و بی خبر نیست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طا، سین، میم.'),
(3254, 28, 2, 'این آیاتی که در این سوره خواهد آمد، و نیز آیاتی که پیش‌تر نازل شد، همه، آیات این کتاب است که نزول آن از جانب خداوند، روشن و آشکار است.'),
(3255, 28, 3, 'ای پیامبر، بخشی از داستان موسی و فرعون را بر تو می‌خوانیم ـ داستانی راست و درست ـ تا آنان که به آیات ما ایمان دارند و پیرو تو هستند در آن بیندیشند و بدانند که آن خداوندی که بنی اسرائیل را از چنگ فرعونیان رهایی بخشید، همو آنان را از سرکشان قریش نجات می‌دهد و حاکمیّت می‌بخشد.'),
(3256, 28, 4, 'همانا فرعون در سرزمین مصر به سرکشی و استکبار پرداخت و میان ساکنان آن دیار اختلاف برانگیخت و از آنان چندین گروه مختلف و متفرق بساخت. او در این میان طایفه بنی اسرائیل را به استضعاف کشید تا آن جا که پسرانشان را سر می‌برید و زنانشان را برای خدمت باقی می‌گذاشت، چرا که او از فسادگران بود.'),
(3257, 28, 5, 'آری، فرعون برای انقراض بنی اسرائیل تلاش می‌کرد، در حالی که ما می‌خواستیم کسانی را که در زمین به استضعاف کشیده شده بودند نعمتی گرانسنگ عطا کنیم; آنان را پیشوایانی قرار دهیم که از ایشان پیروی شود و مواهبی را که در اختیار فرعونیان بود از آنِ ایشان کنیم.'),
(3258, 28, 6, 'و می‌خواستیم آنان را مالک آن سرزمین گردانیم و در آن مستقرشان سازیم و از ناحیه همین مستضعفان، به فرعون پادشاه مصر و هامان وزیر او، و لشکریان این دو، همان را بنمایانیم که هماره از آن هراس داشتند.'),
(3259, 28, 7, 'در همان زمانی که پسران بنی اسرائیل را می‌کشتند، مادر موسی به موسی حامله شد و او را بزاد. ما به مادر موسی الهام کردیم: تا آن گاه که بر جان او نترسیده‌ای او را شیر ده، ولی آن گاه که ترسیدی او را در آن رود بزرگ (رود نیل) بیفکن و نترس، که کشته نمی‌شود و از دوری وی اندوهگین مباش که ما او را به تو باز می‌گردانیم و از پیامبرانش قرار می‌دهیم و او را به سوی خاندان فرعون و بنی اسرائیل به رسالت می‌فرستیم.'),
(3260, 28, 8, '[مادر موسی او را به نیل انداخت] فرعونیان او را یافتند و از آن رود برگرفتند و نزد فرعون آوردند، تا سرانجام دشمن آنان شود و مایه حزن و اندوهشان گردد. آری، فرعون و هامان و لشکریان آن دو، در کشتن پسران بنی اسرائیل و رها کردن موسی به راه خطا رفتند; خواستند آن کس را که نابودی آنان به دست او بود بکشند ولی ندانسته در نگهداری و تربیت وی کوشیدند.'),
(3261, 28, 9, 'آن هنگام که موسی را نزد فرعون آوردند، همسر فرعون به شفاعت او برخاست و به شوهرش گفت: این نور چشم من و تو و مایه سرور ماست، مبادا بکشیدش. امید می‌رود که به ما سود رساند و یا او را به فرزندی بگیریم. این چنین کردند ولی نمی‌فهمیدند حقیقت ماجرا چیست و فرجام کارشان چه خواهد شد.'),
(3262, 28, 10, 'با الهامی که به مادر موسی شد، دلش از ترس و اندوه پاک و تهی گردید، که اگر قلبش را با آن الهام محکم نکرده بودیم، نزدیک بود بر اثر ناشکیبایی راز خود را فاش کند. آری، به او الهام کردیم تا به حراستِ ما از موسی اطمینان یابد و بدان باور داشته باشد.'),
(3263, 28, 11, 'مادر موسی، موسی را به رود نیل انداخت و به خواهر وی گفت: او را دنبال کن تا ببینی فرجامش چه می‌شود. او در پی موسی از دور نظاره اش می‌کرد; دید خدمتکاران فرعون او را از آب گرفتند. ولی آنان متوجه نشدند که خواهر این کودک در پی اوست و او را نگهبانی می‌کند.'),
(3264, 28, 12, 'و ما موسی را - پیش از حضور خواهرش در نزد فرعونیان - از پذیرش دایگان باز داشتیم; پس شیر هیچ دایه‌ای را ننوشید. خواهرش حکایت او دریافت; پس نزد فرعونیان شتافت و گفت: آیا می‌خواهید شما را به خانواده‌ای که سرپرستی و دایگی او را برایتان به عهده گیرند و خیرخواه او باشند، راهنمایی کنم؟'),
(3265, 28, 13, 'فرعونیان پیشنهادش را پذیرفتند. او آنان را به مادر موسی راهنمایی کرد، و آنان کودک را به مادرش تسلیم کردند. بدین ترتیب، موسی را به مادرش بازگرداندیم تا دیده اش روشن شود و محزون نباشد و تا به چشم خود ببیند که وعده‌های خدا حق است، ولی بیشتر مردم راستی و درستیِ وعده‌های الهی را باور نمی‌کنند و در آن تردید روا می‌دارند.'),
(3266, 28, 14, 'پس هنگامی که موسی به توانایی اش رسید و برومند شد و اعتدال و برپایی یافت، به او دانشی عطا کردیم که براساس آن در معارف الهی و امور اخلاقی و آداب اجتماعی درست قضاوت می‌کرد، و به او علمی آموختیم که با هیچ گونه جهل و نادانی آمیخته نبود. اینها پاداش نیکوکاری او بود و ما بدین سان نیکوکاران را سزا می‌دهیم.'),
(3267, 28, 15, 'موسی آن هنگام که مردم مصر به خانه هایشان رفته و از کوچه و بازار بی خبر بودند، از قصر فرعون به درآمد و به آن شهر وارد شد. در آن جا دو مرد را یافت که به کشمکش و ستیز با یکدیگر برخاسته بودند. یکی از آن دو، اسرائیلی بود و همکیش موسی، و دیگری قِبطی و از دشمنان وی و طایفه بنی اسرائیل. آن مرد اسرائیلی که همکیش موسی بود، موسی را در برابر دشمن قبطی خود به یاری طلبید. موسی با مشتی گره کرده بر آن قبطی کوبید و او را هلاک کرد. موسی بر آنچه گذشت پشیمان شد و آن را زمینه گرفتاری خود به خطری بزرگ دانست و گفت: ستیزه گری آن دو مرد که به مرگ قبطی انجامید کاری شیطانی بود; بی تردید شیطان دشمنی گمراه کننده است که عداوت و گمراه گری او آشکار است.'),
(3268, 28, 16, 'موسی گفت: پروردگارا، من با کشتن آن قبطی، خود را در خطر هلاکت افکندم و به خویشتن ستم کردم; از تو می‌خواهم آن را بر من بپوشانی و پیامدش را از من بگردانی و مرا از شرّ فرعونیان برهانی. پس خداوند چنین کرد، زیرا او بسی پرده پوش و مهربان است.'),
(3269, 28, 17, 'گفت: پروردگارا، به شکرانه نعمتی که به من عطا کردی با تو پیمان می‌بندم که هرگز گنهکاران را در ارتکاب گناهشان یاری نکنم.'),
(3270, 28, 18, 'موسی پس از آن، دیگر به قصر فرعون باز نگشت و شب را هراسان در شهر بماند و به انتظار شرّی از ناحیه فرعون به سر می‌برد، که ناگهان همان مرد اسرائیلی که روز گذشته موسی را به یاری طلبیده بود، با فریادی بلند، از وی بر ضدّ قبطیِ دیگری کمک خواست. موسی از سر توبیخ به آن اسرائیلی گفت: تو راه درست را نمی‌پویی و آشکارا بیراهه می‌روی.'),
(3271, 28, 19, 'پس هنگامی که موسی خواست بر آن قبطی که دشمن هر دوی آنان بود بتازد، آن مرد اسرائیلی که به خاطرعتاب موسی گمان می‌کرد موسی آهنگ وی کرده است و می‌خواهد بر او حمله برد، گفت: ای موسی، آیا می‌خواهی مرا به قتل برسانی همان گونه که دیروز فردی را کشتی؟ تو جز این نمی‌خواهی که در زمین ستمگری کنی و نمی‌خواهی از مصلحان باشی.'),
(3272, 28, 20, 'مردی از دورترین نقطه شهر مصر، از همان جا که قصر فرعون قرار داشت، دوان دوان آمد و گفت: ای موسی، سران فرعونی درباره تو به مشورت نشسته‌اند تا تو را به قتل برسانند; پس از این شهر بیرون شو که من از خیرخواهان توام.'),
(3273, 28, 21, 'موسی بی درنگ از مصر بیرون رفت، در حالی که از فرعونیان ترسان بود و در انتظار آن بود که گرفتار آید. او به مناجات با خدا پرداخت و گفت: پروردگارا، مرا از این مردم ستمکار رهایی بخش.'),
(3274, 28, 22, 'و آن گاه که موسی از مصر خارج شد و به سوی مَدیَن روی آورد، راهش را نمی‌شناخت; گفت: امید است پروردگارم مرا راهنمایی کند و راه مستقیم آن دیار را به من بنمایاند.'),
(3275, 28, 23, '[خداوند دعایش را مستجاب کرد، و او راه مستقیم مدین را در پیش گرفت] و آن گاه که بر سَرِ آب مدین رسید، گروهی از مردم را دید که گوسفندان خود را آب می‌دهند، و در نزدیکی آنان دو زن را یافت که گوسفندانشان را از ورود به آبشخور باز می‌دارند. به آن دو گفت: کارتان چیست و چه می‌کنید؟ گفتند: روش ما این است که گوسفندان را آب نمی‌دهیم تا چوپانان از آبشخور درآیند و گوسفندان خود را از آن خارج سازند. پدرمان پیری سالخورده است و خود نمی‌تواند گوسفندانمان را به این جا آورد.'),
(3276, 28, 24, 'آن گاه موسی برای آن دو، گوسفندانشان را آب داد، سپس از آبشخور به سوی سایه رفت و گفت: پروردگارا، به این نیرویی که به من عطا کرده‌ای و خیرِ من در آن است، نیازمندم و برای حفظ و بقای آن به طعامی محتاجم.'),
(3277, 28, 25, 'یکی از آن دو زن در حالی که با نهایت حیا و عفت گام برمی داشت، نزد موسی آمد و به وی گفت: پدرم تو را فرا می‌خواند تا مزد آب دادنت به گوسفندان را، به تو بپردازد. موسی نزد شعیب رفت. او از حال و کارش پرسید. هنگامی که وی داستان خود را حکایت کرد، شعیب گفت: هراسی به خود راه مده که از دسترس آن مردم ستم پیشه دور شده و نجات یافته‌ای.'),
(3278, 28, 26, 'یکی از دختران شعیب گفت: ای پدر، او را برای کارهایت استخدام کن، که هم توانا است و هم درستکار; و بهترین کسی که استخدام می‌کنی، آن کس است که نیرومند و امین باشد.'),
(3279, 28, 27, 'شعیب به موسی گفت: (من می‌خواهم یکی از این دو دخترم را به تو تزویج کنم به شرط آن که در برابر آن هشت سال اجیر من باشی، و اگر این مدت را به ده سال برسانی اختیار با توست; از ناحیه من الزامی بر تو نیست، و بدان که نمی‌خواهم بر تو سخت بگیرم، و هرگز کار مشقت باری برعهده تو نخواهم گذاشت، و به زودی ـ اگر خدا بخواهد ـ مرا از درست کرداران و شایستگان خواهی یافت.'),
(3280, 28, 28, 'موسی در پاسخ شعیب گفت: آنچه شرط کردی و تعهّد نمودی، میان من و تو برقرار باشد; نه من و نه تو اختیار مخالفت نداریم، و هر کدام از آن دو مدت را که من اختیار کنم نباید به من ستمی روا داشته شود; اگر هشت سال را برگزینم، بیش از آن را از من مخواه، و اگر ده سال را انتخاب کردم، مرا به کمتر از آن مجبور مکن. خداوند بر آنچه گفتیم و شرط کردیم شاهد است، و در صورت اختلاف، او میان ما داوری خواهد کرد.'),
(3281, 28, 29, 'پس آن گاه که موسی مدت قرار داد را به پایان رسانید و با خانواده اش روانه مصر شد، در کنار کوه طور آتشی دید; به خانواده اش گفت: این جا بمانید که من آتشی دیدم; شاید پیرامون آن راهنمایی بیابم و از راه برایتان خبری بگیرم، یا پاره‌ای از آن آتش بیاورم، باشد که آتشی برافروزید و گرم شوید.'),
(3282, 28, 30, 'هنگامی که موسی به سراغ آن آمد، از جانب راست درّه، در قطعه زمینی که با سخن گفتن خداوند مبارک شد، از درختی که آن جا قرار داشت ندایی به او رسید که: ای موسی، همانا من خداوندم که پروردگار همه جهان‌ها هستم.'),
(3283, 28, 31, 'و نیز ندا شد که: ای موسی، عصایت را به زمین بیفکن. موسی عصای خویش انداخت. عصا اژدهایی شد که همچون ماری کوچک به شدّت می‌خزید. وقتی موسی عصایش را این گونه دید، هراسان روی گرداند و فرار کرد و باز نیامد. به او گفته شد: ای موسی، پیش آی و نترس، تو از کسانی هستی که ایمنند.'),
(3284, 28, 32, 'ای موسی، دستت را در گریبان خویش ببر، آن گاه که خارج شود، بی هیچ بیماری و عارضه‌ای، سپید و درخشان خواهد بود. و از خوف خدا بازویت را به خود بچسبان و فروتن و خاشع باش و همچون متکبران بازوان خود را مگشای. این دو آیت، حجت‌های روشنی از جانب پروردگار تواَند. با این دو برهان به سوی فرعون و اشراف قوم او برو که آنان مردمی هستند که از راه خدا بیرون رفته‌اند.'),
(3285, 28, 33, 'موسی گفت: پروردگارا، من کسی از این قِبطیان را کشته‌ام، می‌ترسم مرا به قصاص بکُشند.'),
(3286, 28, 34, 'و برادرم هارون از من زبانی گویاتر دارد، او را همراه من به یاری بفرست. او مرا تصدیق می‌کند و پیامم را به روشنی می‌رساند. من بیم آن دارم که مرا تکذیب کنند و من نتوانم درستیِ مدعای خویش را بیان کنم.'),
(3287, 28, 35, 'خداوند به موسی گفت: ما به زودی به وسیله برادرت هارون بازویت را محکم و استوار می‌کنیم و تو را نیرومند و برتر می‌سازیم و شما دو تن را به وسیله آیات و معجزات خویش بر آنها چیره می‌سازیم، به گونه‌ای که هرگز بر شما دست نخواهند یافت. تو و هارون و هر کسی که از شما دو تن پیروی کند، پیروز خواهید بود.'),
(3288, 28, 36, 'پس هنگامی که موسی با معجزات و نشانه‌های روشن و روشنگر ما نزد فرعونیان آمد، گفتند: آنچه موسی آورده است جز افسونی که به دروغ آیات الهی می‌نامدش نیست. آیینی که او مدّعی آن است سابقه‌ای ندارد و از پدران نخستین ما به ما نرسیده است و نشنیده‌ایم که این آیین را برگزیده باشند.'),
(3289, 28, 37, 'موسی در پاسخ فرعونیان گفت: پروردگارم از شما به کسی که هدایت را از جانب او آورده آگاه‌تر است و بهتر می‌داند که فرجام نیک دنیا و آخرت از آنِ کیست. بدانید که شما نیکبخت نمی‌شوید، زیرا حقیقت این است که ستمکاران به نیکبختی نخواهند رسید.'),
(3290, 28, 38, 'و فرعون گفت: ای بزرگان، درستیِ آنچه موسی مرا بدان فرا می‌خواند برای من روشن نشده است. من جز خودم معبودی برای شما سراغ نداشته‌ام. ای هامان، برای من بر گِل آتش بیفروز و آجر تهیه کن و با آن کاخی مرتفع بساز تا بر فراز آن برآیم، شاید از وجود خدای موسی آگاهی بیابم. من می‌پندارم که او از دروغگویان است.'),
(3291, 28, 39, 'فرعون و لشکریانش به ناحق در سرزمین مصر بر مردمان چیره شدند و وانمود کردند که سرای آخرت امری مشکوک است، و گمان بردند که به سوی خداوند باز گردانده نمی‌شوند.'),
(3292, 28, 40, 'بدین سبب او و لشکریانش را گرفتیم و آنان را به دریا انداختیم. پس بنگر که فرجام ستمکاران چگونه است!'),
(3293, 28, 41, 'و آنان را پیشوایانی قرار دادیم که در کفرورزی و ارتکاب گناه مقتدا باشند و مردمان را به کفر و گناه که درپی دارنده آتش است فراخوانند و در قیامت شفاعت هیچ کس به آنان نمی‌رسد و یاری نمی‌شوند.'),
(3294, 28, 42, 'و چون پیوسته گروهی از مردم به پیروی از آنان گمراه شده‌اند، در این دنیا هماره لعنتی را بدرقه آنان کرده‌ایم و از آن رو که در قیامت از سوی کسی یاری نمی‌شوند مورد تنفر مردمند و از زشت رویان خواهند بود.'),
(3295, 28, 43, 'پس از آن که امت‌های پیشین را نابود ساختیم، سزاوار بود که دعوت و اخطار تجدید شود; ازاین رو کتاب آسمانی تورات را که دربردارنده دلایلی روشن و برای مردم مایه بینش و هدایت و رحمت بود به موسی عطا کردیم، باشد که دریابند و عبرت گیرند و عقاید و رفتارهای بایسته را بفهمند.'),
(3296, 28, 44, 'ای پیامبر، تو در جانب غربی آن درّه حضور نداشتی آن گاه که تورات را بر موسی نازل کردیم و به وسیله آن عهدی را که با او داشتیم استوار ساختیم آری تو از حاضران در آن صحنه و شاهدان آن ماجرا نبودی.'),
(3297, 28, 45, 'لیکن ما پس از آن که امت‌هایی را بعد از او پدید آوردیم و زمانی طولانی بر آنان گذشت، داستان موسی را بر تو خواندیم. و نیز تو در میان اهل مدین (شعیب و مردمش) اقامت نداشتی و شاهد ماجراهای موسی در آن دیار نبودی تا بتوانی آن را باز گویی و آیات ما را که بیانگر سرگذشت موسی است، بر این مشرکان بخوانی. آری، تو نبودی و خبر نداشتی ولی ما تو را به سوی قومت به رسالت فرستادیم و آن اخبار را به تو وحی کردیم.'),
(3298, 28, 46, 'و ای پیامبر، تو در کنار کوه طور، آن گاه که موسی را از آن درخت ندا دادیم و او را به رسالت برگزیدیم، حضور نداشتی تا خود، آن داستان را گزارش دهی، ولی به خاطر رحمت و لطفی که از جانب پروردگارت بود، تو را از آن داستان باخبر ساختیم تا مردمی را که پیش از تو هشداردهنده‌ای برایشان نیامده بود هشدار دهی، باشد که متوجه شوند و دریابند.'),
(3299, 28, 47, 'و اگر نبود این که وقتی به سزای گناهانی که به دست خود پیش فرستاده‌اند عذاب به آنان می‌رسید و نداشتن پیامبر را بهانه ساخته و بر ما احتجاج می‌کردند، ما هرگز پیامبری به سوی آنان نمی‌فرستادیم; آری اگر پیامبری به رسالت نیایدشان می‌گویند: پروردگارا، چرا پیامبری به سوی ما نفرستادی اگر می‌فرستادی از آیات تو پیروی می‌کردیم و از مؤمنان می‌شدیم.'),
(3300, 28, 48, 'پس این پیامبر را به رسالت فرستادیم و قرآن را بر او نازل کردیم ولی هنگامی که قرآن ـ آن کتاب راست و درست ـ از جانب ما برایشان آمد، گفتند: چرا قرآن یکجا به او داده نشد، آن گونه که تورات یکجا به موسی داده شد؟ آیا این مشرکان، پیش‌تر (آن گاه که اهل کتاب قرآن را تصدیق کردند) به توراتی که به موسی داده شد کافر نشدند؟ [آن گاه] که گفتند: تورات و قرآن هر دو سحر و افسونند و یکدیگر را تأیید می‌کنند. و نیز گفتند: ما هر رسالتی را که از جانب خدا ادعا شود منکریم.'),
(3301, 28, 49, 'ای پیامبر، به آنان بگو: اگر راست می‌گویید که تورات و قرآن سحر و افسونند، شما از جانب خداوند کتابی بیاورید که از این دو کتاب هدایت آفرین‌تر باشد در این صورت من نیز از آن پیروی می‌کنم.'),
(3302, 28, 50, 'پس اگر به پیشنهاد تو پاسخ ندادند و همچنان بر افسون خواندن آنها اصرار ورزیدند، بدان که آنان در پی حق نیستند، بلکه پیرو هواهای خویشند. و کیست گمراه‌تر از آن کس که بدون رهنمودی از جانب خدا از هوای خویش پیروی کند؟ اینان گمراهند، زیرا کسی که از هوای خویش پیروی کند، از حق رویگردان شده و ستمکار است و خداوند ستمکاران را هدایت نمی‌کند.'),
(3303, 28, 51, 'همانا این سخن را پیاپی، آیه‌ای پس از آیه دیگر و سوره‌ای پس از سوره دیگر به آنان رساندیم شاید متوجه شوند و دریابند.'),
(3304, 28, 52, 'گروهی از کسانی که پیش از نزول قرآن کتاب آسمانی به آنان دادیم، به قرآن ایمان می‌آورند.'),
(3305, 28, 53, 'و هنگامی که قرآن بر آنان تلاوت شود می‌گویند: ما به این قرآن ایمان داریم. این حق است و از سوی پروردگارمان آمده است. ما پیش از آن که قرآن نازل شود، در برابر آن تسلیم بودیم.'),
(3306, 28, 54, 'به اینان دو بار مزدشان داده خواهد شد، به این سبب که در دو نوبت ایمان آوردند و هر بار بر مرارت‌های راه ایمان شکیبایی کردند; و آنان با خلق و خوی نیکو و مدارا با مردم، آزار و اذیّت آنان را از خود دور می‌سازند و از آنچه روزیِ آنان کرده‌ایم برای خدا و در راه او انفاق می‌کنند.'),
(3307, 28, 55, 'و آن گاه که سخن بیهوده‌ای چون دشنام و درشتی بشنوند از آن دوری کرده پاسخ نمی‌دهند، بلکه برای ترک مشاجره می‌گویند: سود و زیان کردارهای ما از آنِ ماست و به شما نمی‌رسد و سود و زیان کردارهای شما نیز از آنِ شما است و به ما نمی‌رسد. سپس وداع کرده و می‌گویند: شما از سوی ما در امن و امانید، و در دل می‌گویند: ما خواهان همنشینی با جاهلان نیستیم.'),
(3308, 28, 56, 'ای پیامبر، کار تو نیست که ایمان را بر قلب مردمی که دوستشان داری بنشانی و آنان را به سر منزل هدایت برسانی، بلکه خداست که بر دل هر کس که بخواهد ـ چنان که برای گروهی از اهل کتاب خواست ـ ایمان را افاضه می‌کند و او داناتر است به کسانی که هدایت پذیرند.'),
(3309, 28, 57, 'گروهی از مشرکان مکه، بهانه‌ای دیگر ساز کرده و گفتند: اگر از قرآن، این کتاب هدایت، پیروی کنیم و همراه تو باشیم، بی درنگ ما را از دیارمان می‌ربایند و با کشتن ما و غارت اموالمان، سرزمین مکه را از وجود ما تهی می‌کنند. آیا آنان را در حرمی امن، جای ندادیم؟ حرمی که میوه‌ها و محصولات فراوانی به سوی آن آورده می‌شود و در آن جمع می‌گردد; محصولاتی که از جانب ما روزیِ آنان شده است; ولی بیشترشان نمی‌دانند که ما از آنان حراست می‌کنیم و امنیّتشان را برقرار می‌سازیم و به آنان روزی می‌دهیم.'),
(3310, 28, 58, 'و این حقیقت را نیز بدانند که این گونه نیست که اگر مردم آنان را از میان نبرند بقایشان تضمین شده باشد; چه بسیار شهرها که مردمش مُتنعِّم بودند و در زندگی سرمستی کردند و ما آنان را نابود ساختیم. ای پیامبر، این ویرانه‌ها که در معرض دیدِ آنهاست، خانه‌های آنهاست که پس از هلاکتشان جز شماری از آنها مسکونی نشد. اینک ماییم که آن خانه‌ها را از آنان به ارث برده‌ایم.'),
(3311, 28, 59, 'هرگز پروردگار تو شهرها را نابود نمی‌کند مگر این که در مرکز آنها پیامبری بفرستد که آیات ما را بر مردمش تلاوت کند; و ما آبادی‌ها را به عذاب ویران نمی‌کنیم مگر این که مردمانِ آنها با تکذیب پیامبر و انکار آیاتِ ما ستمکار شوند.'),
(3312, 28, 60, 'و تمام نعمت‌های دنیوی که به شما داده شده، ساز و برگ زندگی دنیا و مایه آرایش و جمال آن است; دنیایی که فانی و نابود شدنی است، و پاداشی که در سرای آخرت نزد خداست بهتر و پایدارتر است; پس آیا درنمی یابید که شایسته است پاداش اخروی را بر متاع و زیور دنیا ترجیح دهید؟'),
(3313, 28, 61, 'آیا کسی که او را به آمرزش و بهشت که وعده‌ای نیکوست وعده داده‌ایم و او قطعاً به آن خواهد رسید، با آن کس که وی را از متاع زندگی دنیا بهره مند ساخته‌ایم و او تنها به بهرهوری از آن دل خوش کرده و از آن وعده نیکو محروم شده و در قیامت از کسانی است که برای کیفر احضار می‌شوند، برابر است؟'),
(3314, 28, 62, 'و روزی که خداوند ندایشان می‌دهد و می‌گوید: کجایند شریکان من، که شما آنها را در عبادت و تدبیر امور شریک من می‌پنداشتید؟'),
(3315, 28, 63, 'معبودان اهل شرک که آن سخن (وعده عذاب) بر آنان حتمی شده است گویند: پروردگارا، این مشرکان را ما به بیراهه افکندیم; ما چون خود گمراه بودیم آنان را گمراه ساختیم، ولی اجباری از ناحیه ما بر آنان نبود، همان گونه که ما خود نیز ـ بدون اجبار ـ گمراهی را برگزیدیم. ما از اینان به درگاه تو تبری می‌جوییم. آنان به اجبار ما، ما را نمی‌پرستیدند.'),
(3316, 28, 64, 'و به مشرکان گفته می‌شود: معبودهایتان را که شریک خدا می‌پنداشتید فراخوانید تا شما را از عذاب برهانند. آنان معبودانشان را می‌خوانند، ولی آنها به پرستشگران خود پاسخی نمی‌دهند و در نتیجه عذاب را در برابر خود می‌بینند. ای کاش پیش از این هدایت شده بودند.'),
(3317, 28, 65, 'و یاد کن روزی را که خداوند مشرکان را ندا می‌دهد و می‌گوید: در پاسخ فرستادگان من که شما را به ایمان و کار شایسته می‌خواندند، چه گفتید؟'),
(3318, 28, 66, 'مشرکان در آن روز دستاویزی نخواهند یافت که چرا دعوت پیامبران را نپذیرفته‌اند و آنان را تکذیب کرده‌اند; چرا که خبرها بر آنان پوشیده می‌ماند و نمی‌توانند پاسخی که مایه رهایی آنان از عذاب است بیابند، و از یکدیگر نیز پرسش نمی‌کنند تا بتوانند بهانه‌ای برای تکذیب پیامبران پیدا کنند.'),
(3319, 28, 67, 'این است سرنوشت کسی که کفر ورزیده و به سوی خدا باز نیامده است; اما آن کس که به سوی خدا باز آمده و ایمان آورده و کار شایسته کرده است امید است که از زمره سعادتمندان باشد.'),
(3320, 28, 68, 'و پروردگار تو هر چه را بخواهد می‌آفریند و هر شریعت و قانونی را که برای انسان‌ها اراده کند مقرر می‌دارد. آنان در برابر قوانین و احکامی که خداوند مقرر کرده است هیچ حق انتخاب ندارند. خداوند منزّه و برتر از شرکورزیِ آنان است.'),
(3321, 28, 69, 'پروردگارت آنچه را که سینه هایشان پنهان می‌دارد و آنچه را خود آشکار می‌کنند، همه را می‌داند.'),
(3322, 28, 70, 'تنها پروردگار تو دارای مقام اُلوهیت است. جز او معبودی شایسته پرستش نیست; در دنیا و آخرت همه ستایش‌ها به او اختصاص دارد، حکمرانی ـ چه در تکوین و چه در تشریع ـ از آنِ اوست و برای پاداش و جزا به سوی او بازگردانده می‌شوید.'),
(3323, 28, 71, 'بگو: به من خبر دهید، اگر خدا تا روز قیامت شب را بر شما پایدار گرداند، جز خداوند کدامین معبود می‌تواند حکم او را نقض کند و نوری پدید آورد که بتوانید در پرتو آن در طلب معاش برآیید؟ آیا نمی‌شنوید و درنمی یابید؟'),
(3324, 28, 72, 'بگو: به من خبر دهید، اگر خدا تا روز قیامت روز را بر شما پایدار گرداند، جز خداوند کدامین معبود می‌تواند حکم او را بشکند و شبی را برای شما پدید بیاورد که در آن آرامش بیابید؟ آیا نمی‌بینید و درنمی یابید؟'),
(3325, 28, 73, 'خداوند از پرتو رحمتش شب را برای شما قرار داد تا در آن آرامش یابید و روز را قرار داد تا در آن از پی روزی که فضل و بخشش اوست بروید و باشد که خدا را سپاس بگزارید.'),
(3326, 28, 74, 'و یاد کن روزی را که خداوند ندایشان می‌دهد و می‌گوید: کجایند آن شریکان من که شما آنها را در عبادت و تدبیر امور شریک من می‌پنداشتید؟'),
(3327, 28, 75, 'و در آن روز از میان هر امتی، گواهی که ناظر اعمال آنان بوده است بیرون می‌آوریم، آن گاه به آن امت‌ها خواهیم گفت: برهان خود را بر این پندار که خداوند شریکانی دارد، ارائه کنید، ولی آن روز که روز آشکار شدن حقیقت است خواهند دانست که خداوند حق است و اُلوهیَّت ویژه اوست، و آنچه به دروغ می‌بافتند از آنان پنهان می‌شود و از ذهن‌ها زایل می‌گردد.'),
(3328, 28, 76, 'همانا قارون از قوم موسی و از طایفه بنی اسرائیل بود که به ناحق سلطه جویی می‌کرد و در پی بیدادگری علیه آنان بود، و ما از گنج‌ها چندان به او دادیم که حمل کلیدهای آنها حتی بر گروهی نیرومند، گران بود; آن گاه که مردم طایفه اش به او گفتند: به این دارایی‌ها مسرور و سرمست مباش که خداوند شادمانانِ سرمست را دوست ندارد.'),
(3329, 28, 77, 'با این ثروتی که خداوند به تو ارزانی کرده است، در پیِ دستیابی به سرای آخرت باش و برای آبادانی آن بکوش، و بدان که بهره واقعی انسان از دنیا آن چیزی است که برای آخرت او سودمند باشد; پس مبادا با ترک انفاق بهره واقعی خود را از ثروت دنیا از کف بدهی! از سرِ احسان و نیکوکاری انفاق کن; همان گونه که خداوند به تو نیکی کرده و بی آن که بر او حقی داشته باشی به تو عطا فرموده است. هرگز این ثروت و شوکت را وسیله فسادجویی در زمین قرار مده، چرا که خداوند فسادگران را دوست ندارد.'),
(3330, 28, 78, 'قارون در پاسخ مردمش گفت: ثروتی که به من داده شده در نتیجه دانشی است که خود دارم. من راه‌های به دست آوردن ثروت را به خوبی می‌دانم; پس دارایی‌های من حق مسلّم من است، نه آن که بخششی از سوی خدا باشد. آیا قارون نمی‌دانست که خداوند از میان نسل‌هایی که پیش از او بودند کسانی را بر اثر گناهانشان نابود کرد که از او قوی‌تر بودند و اندوخته‌هایی افزون‌تر داشتند؟ سنت الهی بر این است که از مجرمان درباره گناهانشان پرسش نمی‌شود و برای عذرخواهی مهلت نمی‌یابند.'),
(3331, 28, 79, 'قارون غرق در زینت و زیور خود در میان قومش حضور یافت. کسانی که خواهان زندگانی دنیا بودند و از آخرت و پاداش‌های آن بی خبر بودند، گفتند: ای کاش ما نیز مانند آنچه به قارون داده شده است می‌داشتیم; راستی او از خوشبختی بهره‌ای فراوان دارد.'),
(3332, 28, 80, 'و کسانی که از دانش برخوردار شده بودند، به جاهلان گفتند: وای بر شما; پاداش الهی برای کسی که ایمان آورده و کار شایسته کرده بهتر از همه ثروت و مکنتی است که او دارد. این حقیقت را جز به شکیبایان نمی‌توان فهماند.'),
(3333, 28, 81, 'سرانجام، طغیان او سبب شد که ما زمین را بشکافیم و او و خانه اش را در آن نهان سازیم. در آن حال، او جز خدا هیچ گروهی نداشت که بتوانند یاریش دهند و از عذاب رهایی اش بخشند، و خود نیز از کسانی نبود که بتواند از خویشتن دفاع کند. او دریافت که نمی‌تواند از خدا بی نیاز باشد، و برای دستیابی به خیرها و دفع ناگواری‌ها، نیرو و نبوغ علمی کافی نیست.'),
(3334, 28, 82, 'و آنان که روز گذشته آرزوی موقعیت قارون را داشتند، فردای آن روز با پشیمانی می‌گفتند: وای! گویی این اندیشه خطا بود که دستیابی به مال و ثروت فضل خداوندی نیست و علم و درایت آدمی برای آن بسنده است. حقیقت این است که خدا روزی دهنده است. او روزی را برای هر کس از بندگانش که بخواهد فراخ می‌گرداند و بر هر که بخواهد تنگ می‌گیرد. اگر خداوند نعمتش را از ما دریغ کرده بود، قطعاً ما را نیز در زمین فرو برده بود. آنان پندار روز گذشته خود را پنداری کفرآمیز خواندند و با پشیمانی می‌گفتند: وای گویی حقیقت این است که کافران به نیکبختی نمی‌رسند.'),
(3335, 28, 83, 'آن خانه آخرت (بهشت برین) را ویژه کسانی قرار می‌دهیم که خواهان زورگویی در زمین و تسلّط بر بندگان خدا نباشند و نخواهند فسادانگیزی کنند و مرتکب نافرمانی خدا شوند. و فرجام نیکو (سرای آخرت) از آنِ تقواپیشگان است.'),
(3336, 28, 84, 'آنان که کار شایسته آورند پاداشی بهتر از آن خواهند داشت، و آنان که کار ناشایسته آورند، کسانی از ایشان که گناهان بسیاری مرتکب شده‌اند جز آنچه کرده‌اند سزا داده نمی‌شوند.'),
(3337, 28, 85, 'ای پیامبر، آن کس که بر تو واجب کرد قرآن را بر مردم بخوانی و آن را به ایشان ابلاغ کنی، قطعاً تو را پیروزمند و سرفراز به مکه باز می‌گرداند. در پاسخ تکذیب کنندگان رسالت و آنان که آیات الهی را افسون می‌خوانند بگو: پروردگارم از شما به آن کسی که هدایت را آورده آگاه‌تر است و او بهتر می‌داند که چه کسی در گمراهی آشکاری است.'),
(3338, 28, 86, 'تو امید نداشتی که کتاب آسمانی بر تو القا گردد، لیکن رحمتی بود از جانب پروردگارت که تو را فرا گرفت. پس باید این نعمت را پاس بداری و به شکرانه آن و نیز برای پیشبرد دعوتت پشتیبان کافران نباشی و آنان را در کفرشان یاری نکنی.'),
(3339, 28, 87, 'مبادا پس از آن که آیات خدا بر تو نازل شد، کافران با ترفندهایشان تو را از آنها باز دارند! مردم را به سوی پروردگارت فراخوان و هرگز از مشرکان مباش.'),
(3340, 28, 88, 'با وجود خداوند، معبود دیگری مخوان، زیرا جز او معبودی که شایسته پرستش باشد وجود ندارد; چرا که هر چه جز خداست نابود می‌شود و حاکمیت و تدبیر جهان هستی در اختیار اوست و شما به او بازگردانده می‌شوید.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الف. لام. میم.'),
(3342, 29, 2, 'آیا مردم پنداشته‌اند به صِرف این که گفتند: ایمان آوردیم; به حال خود رها می‌شوند و با سختی‌هایی که درستی و نادرستی ایمانشان با آنها آشکار می‌شود آزموده نمی‌شوند؟'),
(3343, 29, 3, 'به یقین ما تمام کسانی را که پیش از اینان بودند و ادعای ایمان می‌کردند با مشکلات آزمودیم. قطعاً خدا کسانی را که راست گفته‌اند و نیز مدعیان دروغگو را با آشکار شدن آثار صدق و کذبشان معلوم و مشخص خواهد ساخت.'),
(3344, 29, 4, 'آیا این مشرکان که مرتکب بدی‌ها می‌شوند و با ایجاد رنج‌ها و مشکلات فراوان برای مؤمنان، آنان را از ایمان باز می‌دارند، گمان کرده‌اند که بر ما پیشی گرفته و ما را ناتوان ساخته‌اند؟! آنان بد داوری می‌کنند.'),
(3345, 29, 5, 'هر کس به امید لقای خدا ایمان آورده است بر ایمان خود استوار بماند، چرا که آن سرآمد الهی که خداوند برای ملاقات خود با مؤمنان معین کرده است یقیناً فرا می‌رسد، و اوست شنوای گفتار آدمیان و دانا به احوال آنان.'),
(3346, 29, 6, 'و هر کس در راه خدا تلاش کند و بر ایمان پایدار بماند، نتیجه تلاشی که می‌کند از آنِ خود اوست و سودی به خدا نمی‌رسد، چرا که خداوند از همه جهانیان بی نیاز است.'),
(3347, 29, 7, 'و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، قطعاً گناهانشان را از آنان می‌زداییم و به هنگام دادنِ پاداش، بهترین عملشان را میزان قرار داده و براساس آن، جزایشان می‌دهیم.'),
(3348, 29, 8, 'ما به انسان درباره پدر و مادرش سفارشی نیکو کردیم; به او فرمان دادیم که به آنان احسان کند. اما اگر تلاش کردند چیزی را که چون نیست، بدان علم نداری شریک من سازی، از آنان اطاعت مکن، و سرانجام به سوی من باز خواهید گشت، آن گاه شما را از حقیقت اعمالی که انجام می‌دادید، باخبر خواهم ساخت.'),
(3349, 29, 9, 'و کسانی که به رغم تلاش پی گیر پدر و مادرشان ایمان آورده و شریکی برای خدا قرار نداده و کارهای شایسته کرده‌اند، از جدایی پدر و مادر خود باکی نداشته باشند، چرا که آنان را در سرای آخرت در میان بندگان شایسته که از نعمت‌های بهشت برخوردارند وارد خواهیم ساخت.'),
(3350, 29, 10, 'در میان مردم کسانی اند که می‌گویند: ایمان آورده‌ایم، ولی ایمانشان تا زمانی است که عافیت و سلامتشان برقرار باشد، اما آن گاه که به خاطر ایمانشان آزار شوند تحمل نمی‌کنند و شکنجه آدمیان را که ناچیز و پایان پذیر است بسان عذاب الهی که همیشگی است، می‌انگارند و به شرک روی می‌آورند. و قطعاً اگر از جانب پروردگارت یاری برسد و گشایشی پدید آید، خواهند گفت: ما هم با شما مؤمنان بودیم. این گونه نیست. آیا خداوند به آنچه در سینه آدمیان است داناتر نیست؟'),
(3351, 29, 11, 'در عین حال، خداوند با پیش آوردن سختی‌ها و امتحان‌ها، مؤمنان را از منافقان مشخص می‌سازد.'),
(3352, 29, 12, 'مشرکان مکه، همانان که در آغاز دعوت پیامبر کفر ورزیدند، به کسانی که ایمان آوردند گفتند: راه و روش ما را دنبال کنید و به شرک باز گردید، در آن صورت وظیفه خود می‌دانیم که گناهان شما را به گردن بگیریم. مشرکان حتی اندکی از گناه آنان را برعهده نمی‌گیرند. به راستی آنان دروغ می‌گویند.'),
(3353, 29, 13, 'و قطعاً این گمراهگران، گناهان سنگین خود را به دوش می‌کشند، و نیز همسان گناهانی را که گمراه شدگان بر دوش گرفته‌اند، با گناهان خود، بر دوش خواهند کشید و روز قیامت از دروغی که می‌بافتند و می‌گفتند گناهِ دیگران را می‌توان برعهده گرفت و این که خدا شریک دارد، بازخواست خواهند شد.'),
(3354, 29, 14, 'و همانا نوح را به سوی قومش به رسالت فرستادیم. او هزار سال پنجاه سال کم، در میان مردم درنگ کرد و به هدایت آنان پرداخت; سپس طوفان، آنان را فرو گرفت، چرا که آنان ستمکار بودند.'),
(3355, 29, 15, 'پس [ستمکاران را هلاک کردیم و] نوح و سرنشینان کشتی او را نجات دادیم و آن رخداد را برای همه گروه‌های بشری نشانه و آیتی قرار دادیم.'),
(3356, 29, 16, 'و ما ابراهیم را به سوی قومش به رسالت فرستادیم آن گاه که به مردمش گفت: فقط خدا را بپرستید و از او پروا داشته باشید، زیرا عبادت خدا و پرهیزگاری برای شما بهتر است اگر بدانید.'),
(3357, 29, 17, 'جز این نیست که شما به جای خدا بت‌هایی را می‌پرستید که از اُلوهیَّت بهره‌ای ندارند. شما در این که آنها را خدا می‌نامید و به پرستش آنها می‌پردازید دروغ می‌بافید، زیرا آنهایی که به جای خدا عبادتشان می‌کنید تا سودی به شما برسانند اختیاردار روزیِ شما نیستند; روزیِ شما نزد خداست، از او روزی طلب کنید و او را بپرستید و در برابر نعمت‌هایی که به شما داده است شکرگزار او باشید; چرا که برای حسابرسی فقط به سوی او بازگردانده می‌شوید.'),
(3358, 29, 18, 'اگر مرا دروغگو بشمارید و توحید و قیامت را منکر شوید، خلاف انتظار من نیست، زیرا امت‌هایی هم که پیش از شما بودند، فرستادگان الهی را دروغگو شمردند. و بر فرستاده خدا وظیفه‌ای جز ابلاغ پیامِ روشن نیست.'),
(3359, 29, 19, 'آیا منکران روز قیامت درنیافته‌اند که چگونه خداوند آفریدگان را نخست پدید می‌آورد، سپس آنها را با آفرینشی مجدد به سوی خویش باز می‌گرداند؟ به یقین این کار برای خداوند آسان است.'),
(3360, 29, 20, 'ای پیامبر، بگو: در زمین سیر کنید و بنگرید که چگونه خداوند آفرینش آدمیان را آغاز کرد. آن گاه [درمی یابید که] خداوند نشئه آخرت را پدید می‌آورد. کیفیت آفریده‌ها نشانه آن است که قدرت خداوند بی انتهاست و او بر هر چیزی تواناست.'),
(3361, 29, 21, 'و بگو: آنگاه که نشأه آخرت پدید آید، خداوند هر که را بخواهد عذاب می‌کند و هر که را بخواهد به رحمت خود درمی آورد، و شما به سوی خداوند بازگردانده می‌شوید.'),
(3362, 29, 22, 'شما هرگز نمی‌توانید نه در زمین خدا را ناتوان سازید و از او بگریزید و نه در آسمان; نه خود به تنهایی می‌توانید بر او غالب شوید و نه جز خدا سرپرستی دارید که بتواند شما را بر او چیره کند و از عذاب برهاند و نه یاوری دارید که بتوانید با کمک او از عهده این کار برآیید.'),
(3363, 29, 23, 'کسانی که آیات الهی را انکار کرده و به معاد و لقای خداوند کفر ورزیده‌اند، از رحمت من نومید شده‌اند و اینانند که عذاب دردناک قیامت را خواهند داشت.'),
(3364, 29, 24, 'ابراهیم قوم خود را به عبادت خدا و پرهیز از بت‌ها فرا خواند، ولی پاسخ آنان جز این نبود که [او را از میان بردارید]. گروهی گفتند: بکشیدش، و گروهی گفتند: او را به آتش بسوزانید. سپس بر سوزاندنش اتفاق کردند و او را در آتشی که فراهم آورده بودند افکندند، ولی خداوند وی را از آتش رهایی بخشید. همانا در این ماجرا برای مردمی که حق باورند نشانه‌هایی است.'),
(3365, 29, 25, 'ابراهیم به آنان گفت: جز این نیست که شما بت‌هایی را به جای خدا معبود خود قرار داده‌اید تا به این وسیله در زندگی دنیا، میان خودتان دوستی برقرار سازید، ولی بدانید که روز قیامت همین خدایان از شما تبری می‌جویند و سرانِ شما نیز از شما اعلان بیزاری می‌کنند و هر گروهی گروه دیگر را لعنت خواهد کرد و جایگاهتان آتش است و یاورانی نخواهید داشت که شما را از عذاب رهایی بخشند.'),
(3366, 29, 26, 'آن گاه لوط به وی ایمان آورد و ابراهیم گفت: من وطن خود را ترک می‌گویم و از میان مردم مشرک به سوی پروردگارم رهسپار می‌شوم و به جایی می‌روم که بتوانم آزادانه عبادت کنم. همانا اوست آن مقتدری که هر که را یاری کند خوار نمی‌گردد، و اوست آن حکیمی که هر که را حراست کند تباه نمی‌شود.'),
(3367, 29, 27, 'و اسحاق و یعقوب را به او عطا کردیم و در نسلش پیامبری و کتاب آسمانی را قرار دادیم و در دنیا پاداش او را به وی ارزانی داشتیم و قطعاً در آخرت نیز از زمره شایستگان خواهد بود.'),
(3368, 29, 28, 'و از لوط یاد کن آن گاه که به مردمش گفت: کار زشتی را مرتکب می‌شوید که به لواط و همجنس بازی می‌پردازید! هیچ امّتی از امّت‌های جهان در این کار بر شما پیشی نگرفته و این کار در میان آنها شایع نبوده است.'),
(3369, 29, 29, 'آیا شما به سراغ همجنسان خود می‌روید و با ترک ازدواج با زنان، راه تداوم نسل بشر را رها می‌کنید و در محفل‌های خود در حضور مردم آن کار زشت و ناپسند را انجام می‌دهید؟ از عذاب الهی بهراسید! ولی قوم لوط پاسخی جز این نداشتند که از سرِ استهزا گفتند: ای لوط، اگر راست می‌گویی که این کارها کیفر خداوند را در پی دارد، عذاب خدا را برای ما بیاور.'),
(3370, 29, 30, 'و لوط گفت: پروردگارا، مرا بر این مردمی که با آمیزش با مردان، زمین را به فساد می‌کشند و نسل بشر را به نابودی تهدید می‌کنند، یاری ده.'),
(3371, 29, 31, 'و آن گاه که فرستادگان ما (فرشتگان) بر ابراهیم وارد شدند و او و همسرش را به این که صاحب فرزند خواهند شد، بشارت دادند، به ابراهیم گفتند: ما آمده‌ایم تا اهل این آبادی (دیار سَدوم) را نابود کنیم، چرا که ساکنان آن ستم پیشه‌اند.'),
(3372, 29, 32, 'ابراهیم گفت: لوط در این دیار است چگونه می‌خواهید اهل آن را نابود سازید؟! فرشتگان در پاسخ او گفتند: ما بهتر می‌دانیم چه کسانی در آن جا به سر می‌برند; نه تنها لوط بلکه خانواده او را نیز نجات می‌دهیم بجز همسرش که او از باقی ماندگان است و عذاب او را فراخواهد گرفت.'),
(3373, 29, 33, 'هنگامی که فرستادگان ما [پس از ملاقات با ابراهیم] نزد لوط آمدند، او از حضورشان در خانه خود، که به صورت جوان‌هایی خوش سیما بودند دلتنگ و ملول شد و از این که بتواند در برابر هجوم تبهکاران، از میهمانانش دفاع کند خود را ناتوان دید. فرشتگان به لوط گفتند: نترس! خطری در پیش نیست و غمگین مباش که پیشامد ناگواری روی نداده است. ما تو و خانواده ات را نجات می‌دهیم، بجز همسرت که او از کسانی است که خواهد ماند و به عذاب گرفتار خواهد شد.'),
(3374, 29, 34, 'ما بر ساکنان این دیار، به سزای این که از راه خدا بیرون رفته‌اند، عذابی از آسمان فرود می‌آوریم.'),
(3375, 29, 35, 'و عذابی فرو فرستادیم و آن دیار را ویران کردیم، و از آن ویرانه برای مردمی که می‌فهمند نشانه‌ای آشکار برجای نهادیم تا عبرت گیرند و از خدا پروا کنند.'),
(3376, 29, 36, 'و به سوی مردم مدین برادرشان شعیب را به رسالت فرستادیم. شعیب به آنان گفت: ای قوم من، فقط خدا را بپرستید، و روز قیامت و پاداش‌های آن را که برای مؤمنان آماده شده است باور کنید و بدانها امیدوار باشید و در زمین تبهکارانه فسادگری نکنید.'),
(3377, 29, 37, 'مردم مدین شعیب را تکذیب کردند و رسالتش را دروغ پنداشتند، در نتیجه زمین لرزه‌ای شدید آنان را فرا گرفت، به گونه‌ای که قلب‌ها و اندام هایشان سخت بلرزید، سپس در دیارشان با زانو بر زمین افتادند و هلاک گشتند.'),
(3378, 29, 38, 'و قوم عاد و ثمود را یاد کن که به عذاب گرفتارشان ساختیم. نشانه فرجام کارشان از خانه هایشان به خوبی برای شما نمایان است. شیطان کارهای ناروایشان را در نظرشان بیاراست و آنان را از راه خدا باز داشت، در حالی که پیش‌تر بینشی درست داشتند، یگانگی خدا را باور داشتند و به عبادت خدا می‌پرداختند.'),
(3379, 29, 39, 'و نیز قارون و فرعون و هامان را یاد کن [که سرنوشتشان چگونه بود]. موسی آن معجزات و دلایل روشن را برای آنان آورد، ولی آنان به دلیل این که در زمین تکبر ورزیدند، حق را نپذیرفتند; اما هرگز بر ما پیشی نگرفتند و پیروز نشدند.'),
(3380, 29, 40, 'در نتیجه هر یک از آن امت‌ها را به سزای گناهش به عذابی گرفتار کردیم. از اینان مردمی بودند که سنگبارانشان کردیم، و امتی بود که غرّش مهیب آنان را فرا گرفت، و کسی بود که او را در زمین فرو بردیم، و نیز کسانی بودند که در آب غرقشان کردیم. خداوند با این کیفرها بر آنان ستم نکرد و نشاید که او ستم کند، بلکه آنان خود با گناهی که مرتکب شدند بر خویشتن ستم کردند و سزاوار کیفر شدند.'),
(3381, 29, 41, 'حکایت کسانی که جز خدا معبودانی را به سرپرستی و تدبیر امور خود برگزیده‌اند، حکایت عنکبوت است که خانه‌ای برای خود ساخته است و قطعاً سست‌ترین خانه‌ها خانه عنکبوت است. اگر مشرکان می‌دانستند که حکایتشان با آن خدایان حکایت عنکبوت و خانه اوست هرگز آنها را سرپرست خود نمی‌گرفتند.'),
(3382, 29, 42, 'قطعاً خدا واقعیت هر چیزی را که مشرکان به جای او می‌خوانند، می‌داند; از این رو آنها را به خانه عنکبوت مثل زده است. او می‌داند که آنچه را مشرکان به خدایی می‌خوانند، هیچ گونه سرپرستی و تدبیری را برعهده ندارند. و خداست که شکست ناپذیر و حکیم است;'),
(3383, 29, 43, 'ما این مثَل‌ها را برای همه مردم بیان می‌کنیم، ولی حقیقت آنها را جز عالمانی که در حقایق امور می‌اندیشند، درنمی یابند.'),
(3384, 29, 44, 'خداوند آسمان‌ها و زمین را به حق آفرید; آفرینشی که بازی و بیهودگی در آن راه ندارد. قطعاً در این آفرینش، آیت و نشانی است بر این که تدبیر امور تنها در اختیار خداوند است، گرچه تنها اهل ایمان آیت بودن آن را درمی یابند.'),
(3385, 29, 45, 'ای پیامبر، از داستان امت‌های گذشته و فرجام شرک و گناهشان باخبر شدی، اینک برای آن که گرفتار این سرنوشت نشوی، باید این کتاب آسمانی را که به تو وحی شده است تلاوت کنی و نماز را برپا داری، زیرا نماز آدمی را از کارهای زشت و ناپسند باز می‌دارد و مهم‌تر آن که نماز یاد خداست و یاد خدا بزرگترین عمل است. و خدا هر کار خوب و بدی را که می‌کنید می‌داند.'),
(3386, 29, 46, 'و با اهل کتاب (یهود و نصارا) جز با نیکوترین روش، بحث نکنید. به آنان، بگویید: ما به کتابی که بر خودمان نازل شده و نیز به کتابی که برای شما فرستاده شده است ایمان آورده‌ایم; معبود ما و معبود شما یکی است و ما تسلیم اوییم. البته با آن دسته از اهل کتاب که در بحث و مجادله ستم می‌کنند و رفق و مدارای شما را نشانه ضعف شما می‌پندارند این گونه بحث نکنید که کارساز نخواهد بود.'),
(3387, 29, 47, 'و ما قرآن را با این ویژگی که مردم را به تسلیم شدن در برابر خدا و ایمان آوردن به پیامبران و کتاب‌های آسمانی فرا می‌خواند، بر تو نازل کردیم، از این رو کسانی که به آنان کتاب آسمانی داده‌ایم به قرآن ایمان می‌آورند، و حتی برخی از این مشرکان نیز به آن ایمان خواهند آورد. از اهل کتاب و مشرکان فقط کافرانِ حق پوش آیات ما را انکار می‌کنند.'),
(3388, 29, 48, 'و تو پیش از نزول قرآن کتابی نمی‌خواندی و با دست خود چیزی نمی‌نوشتی، که اگر چنین بود، آنان که نادرستی قرآن را ادعا می‌کنند خود نیز در صحت آن و الهی بودنش تردید می‌کردند.'),
(3389, 29, 49, 'آری، قرآن کتابی جمع آوری شده و نگارش یافته نیست، بلکه آیاتی روشن و روشنگر است که در سینه کسانی که به آنان دانش عطا شده است نهفته است و جز کسانی که در حق آیات ما ستم می‌کنند و از روی دشمنی و لجاجت از پذیرش آنها خودداری میورزند کسی آیات ما را انکار نمی‌کند.'),
(3390, 29, 50, 'آن حق پوشان ستم پیشه با این ادعای ناروا که قرآن را نمی‌توان معجزه نامید، گفتند: اگر او پیامبر است، چرا از جانب پروردگارش معجزاتی مانند معجزات موسی و عیسی بر او فرستاده نمی‌شود؟ ای پیامبر، به آنان بگو: جز این نیست که آیات و معجزات نزد خداست، هرگاه بخواهد و هرگونه اراده کند، آنها را فرو می‌فرستد. آوردن معجزه برعهده من نیست، من فقط اخطارکننده‌ام، و وظیفه‌ام این است که رسالتم را به روشنی ابلاغ کنم.'),
(3391, 29, 51, 'و آیا معجزه بودن این کتابی که آن را بر تو نازل کرده‌ایم و پیوسته بر آنان خوانده می‌شود، برای آنان کافی نبوده است [که خواهان معجزه‌ای دیگرند]؟ به راستی قرآن برای مردمی که ایمان می‌آورند سرشار از رحمت و تذکار است.'),
(3392, 29, 52, 'ای پیامبر، در پاسخ منکران رسالت بگو: خداوند در کتاب خود به رسالت من گواهی داده است و کافی است که خداوند در این موضوعی که میان من و شما مورد نزاع است، گواه باشد، چرا که او هر آنچه را در آسمان‌ها و زمین است، می‌داند و کسانی که با انکار کتاب الهی به خدا کافر شده و به باطل گرویده‌اند، اینانند که در گرایش‌های خود زیانکارند.'),
(3393, 29, 53, 'از سرِ استهزا و ناباوری، از تو می‌خواهند در آوردنِ عذابی که آنان را بدان اخطار کرده‌ای، شتاب کنی. اگر برای آنان تعیین نشده بود که تا سرآمدی معین در زمین به سر برند، قطعاً عذاب به سراغشان می‌آمد و بی تردید، عذاب آنان را بی آن که متوجه شوند غافلگیر می‌کند.'),
(3394, 29, 54, 'چقدر نادانند که از تو می‌خواهند در آوردنِ عذاب برای آنان، شتاب کنی! قطعاً دوزخ کافران را احاطه خواهد کرد;'),
(3395, 29, 55, 'آن روز که عذاب از بالای سرشان و از زیر پاهایشان، آنان را دربرمی گیرد و خداوند به آنان می‌گوید: [این، همان اعمالی است که می‌کردید، پس] بچشید آنچه را که می‌کردید و بر آن اصرار میورزیدید.'),
(3396, 29, 56, 'ای بندگان من که ایمان آورده‌اید، این زمینِ من، گسترده و پهناور است; اگر نتوانستید در نقطه‌ای از آن مرا به یکتایی پرستش کنید، از آن جا هجرت کنید و در نقطه دیگر مرا بپرستید و از عبادتِ غیر من بپرهیزید.'),
(3397, 29, 57, 'زندگی دنیا چند روزی بیش نیست و سرانجام، هر کسی طعم مرگ را خواهد چشید; سپس در پی آن برای حساب و کتاب به سوی ما بازگردانده می‌شوید.'),
(3398, 29, 58, 'پس مبادا زندگی فانی دنیا شما را از ایمان و کار شایسته باز دارد، زیرا کسانی که ایمان آورده و کارهای شایسته کرده‌اند، هنگامی که به سوی ما بازگشتند، آنان را در غرفه‌هایی از بهشت که از زیر آنها نهرها جاری است، جای می‌دهیم. آنان در بهشت برای همیشه ماندگار خواهند بود. نیکوست پاداش عمل کنندگان.'),
(3399, 29, 59, 'همان کسانی که بر آزارها و مصیبت‌هایی که در وطن خود و در حال هجرت، در راه خدا دیدند شکیبایی ورزیدند و در همه حال بر پروردگار خویش توکل می‌کنند.'),
(3400, 29, 60, 'و چه بسیارند جنبندگانی که روزیِ خود را برنمی دارند و ذخیره نمی‌کنند و خداست که به آنها و شما روزی می‌دهد; پس مبادا از ترس گرسنگی و نیافتن اسباب زندگی، از هجرت سرباز زنید. زیرا اوست که شنونده دعاها و به نیازها داناست.'),
(3401, 29, 61, 'و اگر از این مشرکان بپرسی چه کسی آسمان‌ها و زمین را آفریده و خورشید و ماه را رام ساخته است، قطعاً خواهند گفت: خدا چنین کرده است. پس چگونه اینان از پرستش او باز داشته می‌شوند و به پرستش غیر او کشانده می‌شوند؟'),
(3402, 29, 62, 'خداست که روزی را برای هر کس از بندگانش که بخواهد فراخ می‌گرداند و بر هر کس که بخواهد تنگ می‌گیرد، و جز براساس مصلحت اراده نمی‌کند، زیرا خداوند، به هر چیزی داناست.'),
(3403, 29, 63, 'و اگر از این مشرکان بپرسی چه کسی از آسمان باران فرو فرستاد و به وسیله آن، زمین را پس از آن که خشک و مرده بود با رویش گیاهان زنده کرد؟ قطعاً خواهند گفت: خدا چنین کرده است. بگو: همه ستایش‌ها از آنِ خداست، با این حال بیشتر آنان تعقل نمی‌کنند و در آیات و نشانه‌های ما به درستی نمی‌اندیشند.'),
(3404, 29, 64, 'این زندگی دنیا جز مایه سرگرمی که آدمیان را از حیات برتر اخروی باز می‌دارد و جز بازیچه‌ای که به سرعت پایان می‌پذیرد، نیست، و به یقین زندگی سرای آخرت زندگی واقعی است. اگر اهل دانش بودند درمی یافتند که حقیقت همین است که گفته شد.'),
(3405, 29, 65, 'از آن جا که مشرکان درست نمی‌اندیشند و در آراء خود گرفتار تناقض‌اند، وقتی سوار کشتی می‌شوند خدا را در حالی که عبادت و اطاعت خود را ویژه او می‌کنند، می‌خوانند، ولی هنگامی که خدا آنان را به خشکی می‌رساند و از ورطه‌های هلاکت نجاتشان می‌دهد، همان لحظه به شرک می‌گرایند و دیگری را پروردگار خود می‌پندارند.'),
(3406, 29, 66, 'بگذار هر آنچه را به آنان داده‌ایم ناسپاسی کنند و از هر آنچه در اختیار دارند بهره برند، ولی به زودی فرجام شوم کار خود را خواهند دانست.'),
(3407, 29, 67, 'آیا این مشرکان ندیده‌اند که ما شهر مکه و حومه آن را حرمی امن قرار دادیم با این که مردم از اطراف آنان ربوده می‌شوند؟ با این حال، آیا این مشرکان به بت‌ها که پوچ و بی اساس اند ایمان می‌آورند و نعمت‌های خدا را ناسپاسی می‌کنند؟'),
(3408, 29, 68, 'کیست ستمکارتر از آن کس که بر خدا دروغ بسته و برای خدا شریک قرار داده است؟ و کیست ستمکارتر از آن کس که وقتی حق به سویش آمد آن را تکذیب کرد؟ اینان کفرپیشگانند; آیا در سرای آخرت جایگاه کافران دوزخ نخواهد بود؟'),
(3409, 29, 69, 'و کسانی که در آنچه مربوط به ماست، با تمام وجود تلاش کرده و هیچ گاه از ایمان و اطاعت باز نمانده‌اند، قطعاً آنان را به راه‌هایی که به ما منتهی می‌شود، راهنمایی می‌کنیم، و به راستی که خداوند با نیکوکاران است و رحمت و عنایت خود را بر آنان می‌گستراند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الف. لام. میم.'),
(3411, 30, 2, 'رومیان در نبردشان با ایرانیان شکست خوردند;'),
(3412, 30, 3, 'میدان این نبرد و شکست در نزدیک‌ترین موضع به سرزمین حجاز بود، و [بدانید که] رومیان پس از این شکستشان پیروز خواهند شد.'),
(3413, 30, 4, 'زمانِ این پیروزی در ظرف چند سال آینده ـ که از نُه سال تجاوز نمی‌کند و زودتر از سه سال نیز نخواهد بود ـ رخ می‌دهد. همه امور ـ چه پیش از شکست رومیان و چه پس از پیروزی آنان ـ در اختیار خداوند است، و در آن روز که رومیان پیروز شوند، مؤمنان شادمان می‌گردند.'),
(3414, 30, 5, 'شادمانی مؤمنان از آن روست که خداوند رومیان را یاری خواهد کرد. خداوند هر که را بخواهد یاری می‌کند و تنها او عزیز و مهربان است پس هر که را بخواهد عزّت می‌بخشد، و هر که را بخواهد از رحمت خود برخوردار می‌سازد.'),
(3415, 30, 6, 'غلبه رومیان در آینده‌ای نزدیک و شادمانی مؤمنان در آن روز را خدا وعده کرده است و او وعده خود را به انجام می‌رساند و از آن تخلف نمی‌کند، ولی بیشتر مردم از شئون خدا ناآگاه‌اند و به تحقق وعده‌های او اطمینان ندارند.'),
(3416, 30, 7, 'عدم اعتقاد مردم به تحقق وعده‌های الهی از آن روست که آنان ظاهری از حیات دنیا را می‌شناسند و از حیات اخروی که ورای آن است، بی خبرند.'),
(3417, 30, 8, 'آیا این بی خبران، از سرگرمی به امور دنیا به خود باز نیامده‌اند تا با فراغت و حضور ذهن بیندیشند که خداوند آسمان‌ها و زمین و آنچه را که میان آنهاست جز به حق نیافریده و آنها را بی هدف قرار نداده است و اینها را جز تا سرآمدی معین باقی نمی‌گذارد و سپس از میانشان می‌برد؟ آیا نیندیشیده‌اند که اگر آخرتی در کار نباشد این جهان که فرجامش نابودی است بیهوده و بی هدف خواهد بود؟ شگفت است که بسیاری از مردم لقای پروردگارشان و بازگشت به سوی او را انکار می‌کنند.'),
(3418, 30, 9, 'آیا اینان، در زمین سیر و سفر نکرده‌اند که به دیده عبرت بنگرند که فرجام امت‌های کفرپیشه‌ای که پیش از آنان می‌زیستند چگونه شد؟ آن امت‌ها بسی نیرومندتر از اینان بودند و زمین را برای آبادانی و زراعت کَند و کاو می‌کردند و بیشتر از آنچه این مردم آن را آباد کردند به عمران آن دست یافتند. پیامبرانشان با دلایلی روشن و روشنگر به سراغشان آمدند; پس خداوند با این کیفرها به آنان هیچ ستمی نکرد و نشاید که او ستم روا دارد. آنان خود با تکذیب پیامبران و ارتکاب گناهان [سزاوار عذاب شدند و] به خویشتن ستم کردند.'),
(3419, 30, 10, 'آری، فرجام کسانی که بد کردند چیزی جز بدی و عذاب ناگوار نبود، چرا که آیات خدا را تکذیب کردند و آنها را به استهزا می‌گرفتند.'),
(3420, 30, 11, 'خداوند، آفریدگان را نخست پدید می‌آورد، سپس با پایان یافتن اجلشان آفرینش آنها را تجدید می‌کند و آن گاه به او بازگردانده می‌شوید.'),
(3421, 30, 12, 'و آن روز که قیامت برپا می‌شود و مردم برای حساب و جزا به سوی خدا باز می‌گردند، گناهکاران از رحمت او نومید می‌شوند.'),
(3422, 30, 13, 'و از معبودانشان که آنها را شریکان خدا می‌پنداشتند شفاعت کننده‌ای ندارند و در آن روز پرستش خدایان خود را پنهان می‌دارند.'),
(3423, 30, 14, 'و آن روز که قیامت برپا می‌شود، در آن هنگام مؤمنانِ نیکوکار از مجرمان جدا می‌شوند.'),
(3424, 30, 15, 'اما کسانی که ایمان آورده و کارهای شایسته کرده‌اند، در بوستانی که در نهایت زیبایی و دلنشینی است جای می‌گیرند، در حالی که مسرورند و شادی از چهره‌های آنان نمایان است.'),
(3425, 30, 16, 'و اما کسانی که کفر ورزیده و آیات ما را تکذیب کرده و دیدار آخرت را دروغ شمرده‌اند، اینان در عذاب احضار خواهند شد.'),
(3426, 30, 17, 'خداوند در همه حال از کارهای ناروا و آنچه مشرکان درباره او می‌پندارند منزّه است. پس تسبیح و تنزیه خدای را آن گاه که به شب درمی آیید و آن گاه که وارد بامداد می‌شوید.'),
(3427, 30, 18, 'ستایش خدای را در آسمان‌ها و زمین، و تسبیح و تنزیه او را به هنگام عصر و آن گاه که به نیمروز درمی آیید.'),
(3428, 30, 19, 'موجود زنده را از مرده پدید می‌آورَد و از موجود زنده مرده برمی آورَد (او پیوسته از خاکِ مرده موجود زنده می‌آفریند و هماره موجودات زنده را به خاک بی جان مبدل می‌سازد) و زمین را پس از آن که خشک و بی جان شد، با رویش گیاه زنده می‌کند. شما آدمیان نیز بسان گیاهان، پس از مردن، حیاتی دوباره می‌یابید و از گورها برانگیخته می‌شوید.'),
(3429, 30, 20, 'و از نشانه‌های ربوبیت خدا آن است که شما را از خاک مرده و بی جان پدید آورْد و ناگهان انسان‌هایی شدید که برای تنظیم امور زندگی در همه جا پراکنده می‌شوید.'),
(3430, 30, 21, 'و از جمله نشانه‌های او این است که برای شما از جنس خودتان همسرانی آفرید تا در کنارشان آرامش یابید و میان شما الفت و رحمت برقرار ساخت تا نسل بشر را تداوم بخشد. به یقین در آنچه یاد شد، برای مردمی که می‌اندیشند نشانه‌هایی است بر این که تدبیر امور انسان‌ها در اختیار خداوند است.'),
(3431, 30, 22, 'و از نشانه‌های او آفرینش آسمان‌ها و زمین و اختلاف زبان‌ها و رنگ‌های شماست. قطعاً در آنچه یاد شد، برای دانشوران نشانه‌هایی است بر این که خداوند تنظیم کننده امور آدمیان است.'),
(3432, 30, 23, 'و از نشانه‌های ربوبیت او خواب شما در شب برای آرامش یافتن و تلاشتان در روز برای دستیابی به رزق خداست. به راستی در آنچه یاد شد، برای کسانی که شنوای حقیقت اند نشانه‌هایی است.'),
(3433, 30, 24, 'و از نشانه‌های او این است که برق آسمانی را به شما می‌نمایاند تا هراس از صاعقه و امید به بارش باران در شما پدید آید، و از آسمان باران می‌فرستد، سپس به وسیله آن، زمین را که خشک و مرده بود با رویش گیاهان زنده می‌کند. به یقین در آنچه بیان شد، برای کسانی که خرد را به کار می‌گیرند، نشانه‌هایی است بر این که تدبیر امور هستی در اختیار خداوند است.'),
(3434, 30, 25, 'و از نشانه‌های ربوبیت و یکتایی خدا این است که آسمان‌ها و زمین به فرمان او برپا و استوارند; سپس هنگامی که خداوند شما آدمیان را با یک ندا فرا خواند که از زمین برآیید! ناگهان از گورها بیرون می‌آیید.'),
(3435, 30, 26, 'و همه کسانی که در آسمان‌ها و زمینند مملوک اویند; پس حق اوست که آنان را از سرای دنیا به سرای آخرت کوچ دهد. همه کسانی که در آسمان‌ها و زمینند (فرشتگان، جنیان و آدمیان) خاضعانه به فرمان او گردن نهاده‌اند.'),
(3436, 30, 27, 'و اوست آن که موجودات را نخست پدید می‌آورد، سپس آنها را با آفرینشی جدید به سوی خود باز می‌گرداند. تجدید حیات و بازگرداندن آنها برای او در نهایت آسانی است، زیرا هر کمالی که در آسمان‌ها و زمین هست، خداوند برتر از آن را که مطلق و نامتناهی است داراست، چرا که او عزیز است و ذلت و کاستی در او راه ندارد. و حکیم است و در کارش هرگز سستی راه نمی‌یابد.'),
(3437, 30, 28, 'خداوند برای شما از خودتان مَثَلی زده و آن این است: آیا شما از میان بردگان زرخرید خود که مالک آنهایید، در آنچه روزی شما کرده‌ایم شریکانی دارید به گونه‌ای که شما و آنان در آن روزی‌ها حقی برابر داشته باشید و از این که بدون رخصتشان در آن اموال، تصرّف کنید، بهراسید همان گونه که از شریکان همسان خود که آزادند می‌هراسید؟ هرگز چنین نیست که بردگانتان شریک شما باشند و تصرف شما در اموالتان با رخصت آنان باشد. پس چگونه ممکن است آفریده‌های خداوند که همگی مملوک اویند شریک وی و شایسته پرستش باشند؟! خداوند این گونه، آیات خود را برای مردمی که به عقل خویش درمی یابند به روشنی بیان می‌کند.'),
(3438, 30, 29, 'ولی مشرکان باورهایشان را براساس علم و خِرد استوار نساخته‌اند، بلکه این ستمکاران از هواهای نفسانی خود پیروی کرده‌اند. پس کسانی را که خداوند به خاطر ستم هایشان به گمراهی افکنده است چه کسی هدایت می‌کند؟ آنان هدایت کننده‌ای ندارند و یاورانی نخواهند داشت که آنان را یاری دهند و نجاتشان بخشند.'),
(3439, 30, 30, 'حال که روشن شد آفرینش و تدبیر در اختیار خداوند است و او هیچ گونه شریکی ندارد و کسانی که از وی روی گردانده‌اند در قیامت راه نجاتی ندارند، پس روی خود را به سوی این دین [تسلیم خدا بودن] بگردان و $بی آن که به راست یا چپ گرایش یابی، تنها به آن چشم بدوز. بر این دین پایدار باش; دینی که فطرت خدادادیت تو را به آن گرایش می‌دهد; همان فطرتی که خداوند همه انسان‌ها را بر آن آفریده است. آفرینش خدا دگرگونی ندارد و با اختلاف افراد و اختلاف زمان‌ها و مکان‌ها، تغییر و تبدیل نمی‌پذیرد. این است دین استوار ولی بیشتر مردم نمی‌دانند.'),
(3440, 30, 31, 'تو و پیروانت بی هیچ انحرافی به این دین روی آورید و به درگاه خدا بازآیید و از او پروا کنید و نماز را برپا دارید و از مشرکان مباشید.'),
(3441, 30, 32, 'از کسانی نباشید که براساس هواهای نفسانی خود در دینشان اختلاف کردند و یکپارچگی آن را از بین بردند و به گروه‌های پراکنده‌ای که هر یک پیرو اندیشه‌ای است تقسیم شدند و هر گروهی به آیینی که برگزیده است شادمان است.'),
(3442, 30, 33, 'و آن گاه که مردم را مختصر زیانی برسد، پروردگار خود را در حالی که به سوی او باز آمده اند می‌خوانند تا رنج و محنت را از آنان بگرداند، ولی هنگامی که خدا از سوی خود اندک رحمتی به آنان چشانید، گروهی از آنان بی درنگ به شرک می‌گرایند و دیگران را شریک پروردگار خویش می‌گیرند.'),
(3443, 30, 34, 'بگذار آنچه را به آنان داده‌ایم ناسپاسی کنند! ای مشرکان، کامیاب شوید و از هر چه در اختیار دارید بهره گیرید، زود است که فرجام شوم کار خود را بدانید.'),
(3444, 30, 35, 'آیا ما دلیل و برهانی در اختیار آنها نهاده‌ایم که چیزی را بیان می‌کند که مشرکان به سبب آن برای خدا شریک قرار داده‌اند. [که این گونه بر شرک پایدارند]؟'),
(3445, 30, 36, 'و آن گاه که مردم را رحمتی بچشانیم بدان شادمان می‌گردند و ما را از یاد می‌برند، و اگر بر اثر کارهای ناروایی که دستاورد خودشان است مصیبت و رنجی به آنان برسد، بی درنگ نومید می‌شوند.'),
(3446, 30, 37, 'اینان در اشتباهند; نه نعمت همیشگی است که شادمانی داشته باشد و نه رنج و درد دائمی است که مایه یأس گردد. و مشیت الهی تعیین کننده است; آیا ندیده‌اند و درنیافته‌اند که خداوند روزی را برای هر که بخواهد فراخ می‌گرداند و بر هر که بخواهد تنگ می‌گیرد؟ به راستی در این امر، برای کسانی که ایمان می‌آورند نشانه‌هایی بر ربوبیت خدا و یکتایی او در تدبیر جهان است.'),
(3447, 30, 38, 'ای پیامبر، حق خویشاوندت را بپرداز و همچنین نصیب بینوا و در راه مانده را. شما مردم نیز حق خویشاوند پیامبر را بدهید و نیز نصیب بینوا و در راه مانده را، رعایت این امر برای کسانی که خواهان تقرب به درگاه اویند و خشنودی او را می‌طلبند بهتر است و آنان قطعاً سعادتمندند.'),
(3448, 30, 39, 'هر مالی از اموال خود را به رسم ربا داده‌اید تا در میان اموال مردم افزون شود (بهره‌ای حاصل کند) نزد خدا فزونی نمی‌گیرد، ولی هر مالی را به عنوان زکات پرداخته‌اید و خواهان تقرب به درگاه خداوندید و خشنودی و پاداش او را می‌طلبید، آنان اموالشان دو چندان افزون می‌گردد.'),
(3449, 30, 40, 'خدا همان کسی است که شما را آفرید، آن گاه به شما روزی داد، سپس شما را می‌میراند و بعد از آن به شما حیاتی دوباره می‌بخشد. اینها ویژگی‌های ربوبیت و شایستگی پرستش است. آیا از میان معبودانی که آنها را شریک خدا پنداشته‌اید کسی هست که حتی بخش اندکی از این کارها را انجام دهد؟ می‌دانید که آنها نمی‌توانند. و او منزه و برتر از این است که به او شرک ورزند.'),
(3450, 30, 41, 'بر اثر شرک ورزیدن مردم و گناهانی که مرتکب می‌شوند، فساد و تباهی، خشکی و دریا را فراگرفت. تا خداوند وزر و وبال برخی از رفتارها و باورهای نادرست مردم را به آنان بچشاند، باشد که از شرک و گناه بازگردند و به توحید و فرمانبرداری از او گردن نهند.'),
(3451, 30, 42, 'اگر تردید دارند که گناه و شرک سبب ویرانی و فساد است و باور ندارند که آدمیان کیفر اعمال خود را خواهند دید، به آنان بگو: در زمین سیر کنید و ببینید فرجام کسانی که پیش‌تر در زمین می‌زیستند چگونه شد. بیشتر آنان مشرک بودند که خداوند، سزای بخشی از اعمالشان را به آنان چشانید.'),
(3452, 30, 43, 'حال که فرجام کفر و شرک را دانستی، پیش از آن که روز قیامت فرا رسد، روی خود را به این دین (یکتاپرستی) که آیینی استوار است بگردان و تنها متوجه آن باش. روز قیامت حتمی است و هیچ چیز از جانب خدا مانع آن نخواهد شد. در آن روز مردم به دو گروه تقسیم می‌شوند، گروهی به بهشت می‌روند و گروهی به دوزخ درمی آیند.'),
(3453, 30, 44, 'کسانی که کفر میورزند، آن روز عقوبت کفرشان دامنگیرشان می‌شود، و کسانی که کار شایسته می‌کنند، بساط زندگی جاوید را برای خود می‌گسترانند.'),
(3454, 30, 45, '[خداوند روز قیامت را مقرر فرمود] تا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند از فضل خود سزا دهد. خداوند از آن رو مؤمنان شایسته کار را از فضل خود بهره مند می‌کند که دوستدار آنان است، و او کافران را دوست نمی‌دارد.'),
(3455, 30, 46, 'از نشانه‌های خدا این است که بادها را می‌فرستد تا نویدبخش باران باشند و بدین وسیله از رحمت خود به شما بچشاند و تا کشتی‌ها به فرمان او بر آب‌ها روان شوند و شما در طلب رزق و بخشش الهی برآیید. این نعمت‌ها را به شما ارزانی داشته است باشد که سپاسگزار او باشید و او را پرستش کنید.'),
(3456, 30, 47, 'و همانا پیش از تو پیامبرانی را به سوی قومشان به رسالت فرستادیم. آنان برای مردم خود دلایل روشن و روشنگر آوردند، ولی تنها گروهی از مردم ایمان آوردند و دیگران به مخالفت برخاستند. ما هم مؤمنان را یاری کردیم و از کسانی که مرتکب گناه شدند انتقام گرفتیم. آری، یاری مؤمنان و نجاتشان از عذاب حقی است بر عهده ما که خود آن را بر عهده گرفته‌ایم.'),
(3457, 30, 48, 'خدا همان کسی است که بادها را می‌فرستد که ابری را به حرکت درمی آورند; سپس آن گونه که خدا می‌خواهد آن را در پهنه آسمان می‌گستراند و از آن پاره ابرهایی فشرده و به هم آمیخته می‌سازد; آن گاه می‌بینی قطرات باران از لا به لای آنها بیرون می‌آید. پس هنگامی که خداوند باران را به هر کس از بندگانش که خواست، رسانید، ناگهان آنان شادمان می‌شوند.'),
(3458, 30, 49, 'و قطعاً پیش از این که باران بر آنان فرو ریزد، آری لحظه‌ای پیش از آن مأیوس و ناامید بودند.'),
(3459, 30, 50, 'پس به آثار رحمت الهی و نتایج باران بنگر و ببین چگونه خداوند زمین را پس از آن که خشک و مرده بود، با رویش گیاهان و درختان زنده کرد. به یقین، همان خدای رحمت آفرین مردگان را دوباره جان می‌بخشد، و او بر هر کاری تواناست.'),
(3460, 30, 51, 'و به یقین اگر بادی سرد بفرستیم که باغ و کشتزارشان را زرد کند و آنان بنگرند که باغ و زراعتشان زرد شده و خشکیده است، بی درنگ نعمت‌های الهی را انکار می‌کنند.'),
(3461, 30, 52, 'ای پیامبر، به این گونه مردم مشرک دل مبند و بر آنان اندوهگین مباش، چرا که آنان مُردگانند و تو نمی‌توانی سخن خود را به مردگان بشنوانی و آنان را هدایت کنی. اینان کرانند و تو بر شنواندن دعوتت به آنان ناتوانی. این در حالی است که آنان روی می‌گردانند و پشت می‌کنند، و گرنه امکان داشت حتی با اشاره سخنت را به آنان بفهمانی.'),
(3462, 30, 53, 'ای پیامبر، این مشرکان کورند و بی راهه می‌روند و تو نمی‌توانی کوران را از گمراهی باز داری و هدایتشان کنی; تو نمی‌توانی سخنت را جز به کسانی بشنوانی و هدایتشان کنی که آیات ما را ـ که همه نشانگر توحید است ـ تصدیق کنند. اینانند که در برابر ما تسلیم می‌شوند و ایمان می‌آورند.'),
(3463, 30, 54, 'خداوند کسی است که شما را به هنگام نوزادی ناتوان آفرید، آن گاه پس از ناتوانی نیرو و توان به وجود آورد و شما را به دوران جوانی رسانید، آن گاه پس از توانایی ضعف و سستی پدید آورد و شما را به پیری رسانید. آری، او هر چه بخواهد می‌آفریند. اوست که هم داناست و هم توانا.'),
(3464, 30, 55, 'و روزی که قیامت برپا شود، مجرمان سوگند یاد می‌کنند که جز زمانی کوتاه در قبرهای خویش نمانده و دوباره به حیات دنیوی بازگشته‌اند. اینان هماره چنین اند که از حقیقت باز داشته می‌شوند و به باطل کشیده می‌شوند.'),
(3465, 30, 56, 'اما کسانی که دانش و ایمان به آنان داده شده بود در پاسخ کافران تبهکار می‌گویند: شما پس از مردن، تا روز قیامت درنگ کرده و در قبرهایتان مانده‌اید. این حقیقت را قرآن بیان کرد که: پس از مردن تا روز قیامت فاصله و برزخی است. این مدت که مرده بودید همان فاصله و برزخ است. پس امروز روز رستاخیز است، ولی شما این روز را نشناختید و آن را باور نکردید.'),
(3466, 30, 57, 'در آن روز، نه عذرخواهی آنان که ستم کرده‌اند به حالشان سودبخش است و نه از آنان خواسته می‌شود که با انجام کارهای شایسته و تدارک گناهان خود درصدد برآیند توبیخ و سرزنش را از خود بگردانند.'),
(3467, 30, 58, 'همانا در این قرآن برای مردم از هر مثَل و توصیفی که حق را به دل‌ها نزدیک کند و بطلان راه کفر را آشکار سازد، نمونه‌ای بیان کرده‌ایم، و اگر نشانه و معجزه‌ای برای آنان بیاوری، قطعاً کسانی که کفر ورزیده‌اند خواهند گفت: شما جز باطل چیز دیگری نیاورده‌اید.'),
(3468, 30, 59, 'این گونه خداوند بر قلب کسانی که خدا و آیات او را نمی‌شناسند و قیامت را باور ندارند مهر می‌زند که نه مثل‌های قرآن را بفهمند و نه نشانه‌ها و معجزه‌ها را باور کنند.'),
(3469, 30, 60, 'پس در برابر برخوردهای ناپسند کافران که حق را باطل می‌شمرند شکیبا باش; خدا وعده پیروزی به تو داده است. وعده خدا حق است و به آن وفا می‌کند. مبادا کسانی که به راستی و درستی وعده‌های الهی اطمینان ندارند تو را به تردید افکنند و باورت را سست کنند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الف. لام. میم.'),
(3471, 31, 2, 'این آیه‌های بلند مرتبه که در این سوره خواهد آمد و آیاتی که پیش‌تر نازل شده، همه آیات این کتاب است; کتابی که دربردارنده حکمت است و هیچ سخن بیهوده و باطلی در آن راه ندارد.'),
(3472, 31, 3, 'کتابی که هدایتگر نیکوکاران و رحمت آفرین برای آنان است.'),
(3473, 31, 4, 'نیکوکاران کسانی اند که نماز را هماره به پا می‌دارند و زکات می‌پردازند و به سرای آخرت یقین دارند.'),
(3474, 31, 5, 'اینان بر هدایتی که از جانب پروردگارشان به آنان ارزانی شده است، استوارند و اینانند که به راستی نیکبختند.'),
(3475, 31, 6, 'از مردم کسی است که سخن‌ها و داستان‌های بیهوده را که باز دارنده از حق و گرایش دهنده به باطل است، می‌خرد تا با انتشار آنها، مردم را از راه خدا باز دارد و به بیراهه برد بی راهه‌ای تهی از هر گونه دانش و قرآن و حقایق آن را به استهزا گیرد. برای آنان (گمراه گران و گمراه شدگان) عذابی خوارکننده و ذلت بار خواهدبود.'),
(3476, 31, 7, 'و هنگامی که آیات ما بر او خوانده شود، متکبّرانه روی می‌گرداند، چنان که گویی آنها را نشنیده است; گویی در هر دو گوشش سنگینی افتاده و کر شده است; پس او را به عذابی دردناک بشارت ده.'),
(3477, 31, 8, 'اما نیکوکاران، همانان که ایمان آورده و کارهای شایسته کرده‌اند، برای آنان بهشتِ پرنعمت است.'),
(3478, 31, 9, 'در آن جا برای همیشه ماندگارند. خدا این وعده را داده است; وعده‌ای راست و درست که تخلف پذیر نیست. او عزیزی است که ذلت و شکست برای او نیست و حکیمی است که در گفتارش بیهوده و باطل راه ندارد.'),
(3479, 31, 10, 'خدا آسمان‌ها را بدون پایه‌هایی که آنها را ببینید آفرید و در زمین کوه‌هایی استوار افکند تا زمین، شما را نلرزاند، و در پهنه گیتی از هر نوع جنبنده‌ای پراکنده ساخت; و از آسمان بارانی فرو فرستادیم. آن گاه از هر گیاه ارجمندی نر و ماده اش را در زمین رویاندیم.'),
(3480, 31, 11, 'ای پیامبر، به مشرکان بگو: این مجموعه، آفریده‌های خداست; پس او شایسته عبادت است. اگر شما می‌پندارید که خدایانتان شایسته پرستش‌اند، به من نشان دهید که آنهایی که غیر خدایند چه آفریده‌اند؟ همه می‌دانند که آنها چیزی نیافریده‌اند پس تدبیری را در اختیار ندارند، ولی ستمکاران (مشرکان) در گمراهی آشکارند که این حقیقت روشن را در نمی‌یابند.'),
(3481, 31, 12, 'و قطعاً به لقمان حکمت عطا کردیم: به او فرمان دادیم که خدا را سپاس بگزار، و هر کس پیوسته خدا را سپاس بگزارد، جز این نیست که سپاسش به سود خودش خواهد بود، و هر کس کفران کند زیانی به خدا نمی‌رسد، چرا که خدا از همه چیز بی نیاز است و هماره مورد ستایش است.'),
(3482, 31, 13, 'و یاد کن هنگامی را که لقمان به پسرش ـ در حالی که او را پند می‌داد ـ گفت: ای پسرکِ من، برای خدا شریکی قرار مده، چرا که شرک ستمی بزرگ است.'),
(3483, 31, 14, 'ما به انسان در مورد پدر و مادرش سفارش کردیم; مادرش به هنگام بارداری، بسیار رنج و محنت کشید و پیوسته بر ضعف و سستی او افزوده می‌شد. سرانجام او را بزاد و در طول دو سال شیر داد; سپس او را از شیر باز گرفت. سفارش ما به انسان این بود که به وی فرمان دادیم که پدر و مادرت را سپاسگزار باش، همان گونه که فرمانش دادیم که مرا شکرگزار باش; مبادا فرمان نبری و سپاسگزاری نکنی، که بازگشت همگان به سوی من است.'),
(3484, 31, 15, 'و اگر پدر و مادرت تلاش کردند که چیزی را که بدان علم نداری ـ از آن روی که وجود ندارد ـ شریک من سازی، از آنان پیروی مکن. البته در کارهای دنیا با آنان به نیکی معاشرت کن و در حقشان ستم و خشونت روا مدار; اما در مورد دین خدا، اگر آنان توبه کرده و به سوی من بازگشته‌اند، راهشان را در پیش گیر و از آنان پیروی کن، و گرنه از آنان اطاعت مکن، بلکه راه کسی را پی گیر که به سوی من باز آمده است. سپس روز قیامت به سوی من باز خواهید گشت، آن گاه شما را به حقیقتِ آنچه کرده‌اید آگاه خواهم ساخت و براساس آن به شما پاداش یا کیفر می‌دهم.'),
(3485, 31, 16, 'لقمان پس از توجه دادن فرزندش به توحید، به یادآوری معاد پرداخت و گفت: ای پسرک من، اگر کار شایسته یا ناشایسته تو هموزن یک دانه خردل باشد و آن هم در دل صخره‌ای یا در آسمان‌ها و یا در دل زمین نهان باشد، خدا آن را برای حسابرسی حاضر خواهد کرد، چرا که خداوند علمش در ژرفای هر چیزی نفوذ دارد و به کنه موجودات آگاه است.'),
(3486, 31, 17, 'ای پسرک من، نماز را بر پا دار و به کار شایسته ـ که دین و خرد آن را به نیکی می‌شناسند ـ فرمان ده و از کار ناشایست نهی کن و بر مصیبت و رنجی که به تو رسیده است شکیبا باش [و بدان] که صبر و شکیبایی برخاسته از اراده جدّی و تصمیم قاطع در کارهاست.'),
(3487, 31, 18, 'و به تکبّر، از مردم رخ برمتاب و در زمین همچون سرمستان سبک مغز گام برمدار، چرا که خداوند هیچ خیالباف متکبر و فخرفروشی را دوست نمی‌دارد.'),
(3488, 31, 19, 'و در راه رفتن خود میانه رو باش، و صدایت را کوتاه کن، چرا که صدای خران که بلندآوازند زشت‌ترین بانگ هاست.'),
(3489, 31, 20, 'ای پیامبر، به مشرکان بگو: آیا ندیده‌اید که خداوند آنچه را که در آسمان‌ها و آنچه را که در زمین است به نفع شما رام ساخته و نعمت‌های آشکار و نهانش را به وفور بر شما ارزانی داشته است؟ با این حال، گروهی از مردم درباره یکتایی خداوند جدال می‌کنند بی آن که به دانشی برخاسته از دلیل عقلی و یا به هدایت و الهامی از جانب خداوند و یا به کتابی آسمانی که روشنگر است تمسّک کنند.'),
(3490, 31, 21, 'و هنگامی که به آنان گفته شود: از آیینی که خدا نازل کرده است پیروی کنید، خواهند گفت: چنین نمی‌کنیم، بلکه پیرو سنتی خواهیم بود که پدرانمان را بر آن یافته‌ایم. آیا بر پیروی از پدرانشان اصرار دارند هرچند شیطان آنان را بدین پیروی به سوی عذاب آتش فراخوانده باشد؟'),
(3491, 31, 22, 'و هر کس تسلیم خدا شود و با تمام وجود به پرستش او روی آورد قطعاً به محکم‌ترین دستاویز چنگ زده است و فرجام کارش نجات و رستگاری است، چرا که فرجام کارها به سوی خداوند و در اختیار اوست.'),
(3492, 31, 23, 'و کسانی که کفر ورزیده‌اند، نباید کفرشان مایه اندوه تو گردد. بازگشت آنان به سوی ماست و ما آنان را به حقیقت آنچه کرده‌اند آگاه خواهیم ساخت و پیامدهای اعمالشان را که آتش است به آنان نشان خواهیم داد. همانا خداوند به اندیشه‌ها و نیّت‌های نهفته در سینه‌ها، به خوبی داناست.'),
(3493, 31, 24, 'کافران مپندارند که در این جهان از تحت قدرت ما خارج شده‌اند; ما به آنان اندک بهره ای می‌دهیم سپس آنان را به اجبار به سوی عذابی سخت خواهیم برد.'),
(3494, 31, 25, 'اگر از این مشرکان بپرسی: چه کسی آسمان‌ها و زمین را آفریده است؟ قطعاً خواهند گفت خدا آفریدگار هستی است. ای پیامبر، تو به خاطر اعتراف مشرکان که در حقیقت اعتراف به توحید است، خدا را ستایش کن; ولی بیشتر آنان نمی‌دانند که تدبیر امور هستی از آفرینش آنها جدا نیست و اعتراف به آفریدگاری خدا اعتراف به یکتایی و نفی شریک از اوست. آری، گروه اندکی از مشرکان این حقیقت را درمی یابند ولی آن را انکار می‌کنند.'),
(3495, 31, 26, 'آنچه در آسمان‌ها و زمین وجود دارد فقط از آنِ خداست، زیرا تنها خداست که بی نیاز است و بر کارهایش که همه زیباست، ستایش می‌شود.'),
(3496, 31, 27, 'و اگر هر درختی که در پهنه زمین است قلم باشد، و هرچه دریا هست مرکّب گردد و هفت دریای دیگر (دریاهای بسیار) به مدد آید و بر آن افزوده شود، آن گاه با آن قلم‌ها و مرکّب‌ها آفریده‌های خداوند نگارش یابد، دریاها تمام می‌شود و نگارش آفریده‌های خدا به پایان نمی‌رسد، به راستی او عزیز است و چیزی بر او غالب نمی‌آید و حکیم است و تدبیر امور هستی را به هیچ کس وانمی گذارد.'),
(3497, 31, 28, 'مپندارید که حسابرسی اعمالِ این همه خلایق امکان پذیر نیست، زیرا نزد خدا، آفرینش همه شما و برانگیختنتان، مانند آفرینش و برانگیختن یک تن است، چرا که خداوند خود شنوا و بینا به هر گفتار و کرداری است.'),
(3498, 31, 29, 'آیا ندیده‌ای که خداوند پیوسته شب را در بخشی از روز فرو می‌برد و با کاستن از ساعات روز بر شب می‌افزاید و همواره روز را در بخشی از شب فرو می‌برد و با کاستن از شب ساعات روز را افزایش می‌دهد، و این که خداوند خورشید و ماه را رام ساخته و نظام آنها را تدبیر کرده است و هر کدام تا سرآمدی مشخص در حرکتند؟ و آیا درنیافته‌اید که خدا به آنچه می‌کنید داناست؟'),
(3499, 31, 30, 'آری، آفرینش هستی و تدبیر امورِ آن تنها در اختیار خداست و این بدان جهت است که خداوند ثابت و پایدار است و هیچ گونه بطلانی در او راه ندارد و آنچه را که به جای او می‌خوانند و آن را معبود می‌نامند باطل است و ثبوت و قراری ندارد و نیز بدان جهت است که خداوند بلند مرتبه و منزّه از هر کاستی است، و بزرگ است و دارنده همه کمالات است.'),
(3500, 31, 31, 'آیا ندیده‌ای که کشتی‌ها در دریا به وسیله بادها و دیگر عوامل که همه از نعمت‌های خدایند روان می‌شوند؟ خداوند این گونه مقرر کرده است تا نشانه‌های خود را به شما بنمایاند. به راستی در وزش بادها و حرکت کشتی‌ها بر روی دریا، برای کسانی که صبرپیشه و سپاسگزارند نشانه‌هایی بر یکتایی خداوند است.'),
(3501, 31, 32, 'و هنگامی که در میان دریا موج‌هایی بسان پاره‌های ابر آنان را احاطه کند به درگاه خدا روی می‌آورند و او را برای نجات خویش می‌خوانند در حالی که عبادت و اطاعت خود را ویژه او می‌کنند، ولی هنگامی که آنان را به خشکی برسانَد و از نابودی رهایی بخشد، تنها تعداد اندکی از آنان به راه راست قدم می‌نهند و بر توحید پایداری می‌کنند و بیشترشان به شرک می‌گرایند و آیات الهی را نادیده می‌انگارند. آیات ما را جز عهدشکنان ناسپاس انکار نمی‌کنند.'),
(3502, 31, 33, 'ای مردم، اکنون که دلیل یکتایی خدا را دانستید و پندهای شفابخش الهی را شنیدید، از پروردگار خود پروا کنید و آیات او را انکار نکنید و بترسید از روزی که هیچ پدری برای فرزندش چیزی را کفایت نمی‌کند. و هیچ فرزندی نیز کفایت کننده چیزی از پدرش نیست. به یقین وعده خدا در مورد برانگیختن مردگان حق است; پس مبادا زندگی دنیا شما را بفریبد و مبادا شیطان با وعده آمرزش خدا، شما را فریب دهد.'),
(3503, 31, 34, 'همانا آگاهی از زمان برپایی قیامت تنها نزد خداست و اوست که باران را فرو می‌فرستد و [زمان، مکان و مقدار] آن را می‌داند، و نیز به جنین‌هایی که در رحم‌ها هستند آگاه است، و هیچ کس نمی‌داند فردا چه دستاوردی خواهد داشت، و هیچ کس درنمی یابد که در چه سرزمینی خواهد مرد. به راستی خداوند دانا و خبیر است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الف. لام. میم.'),
(3505, 32, 2, 'این است آن کتابِ نازل شده که هیچ تردیدی در حقانیت آن نیست، زیرا از جانب پروردگار جهانیان است.'),
(3506, 32, 3, 'امّا مشرکان می‌گویند: محمّد خود قرآن را بربافته و آن را کتابی الهی می‌شمرد. نه، قرآن سراسر حق است و از جانب پروردگار توست تا مردمی را که پیش از تو پیامبری هشداردهنده برایشان نیامده است هشدار دهی باشد که راه یابند و هدایت شوند.'),
(3507, 32, 4, 'خداوند کسی است که آسمان‌ها و زمین و آنچه را که میان آن دو قرار دارد، در شش مرحله آفرید، آن گاه بر تخت فرمانروایی جهان استیلا یافت و به تدبیر امور پرداخت. جز او هیچ سرپرست و شفیعی که امور شما را تدبیر کند ندارید; پس آیا متوجه نمی‌شوید که اختیاردار جهان هستی خداست و تنها او شایسته پرستش است؟'),
(3508, 32, 5, 'شئون هستی را خدا تدبیر می‌کند و از آسمان ـ که مبدأ مدیریت این جهان است ـ امور زمین را به زمین فرو می‌فرستد; سپس آن امور در روزی که مقدارش برابر با هزار سال از سال‌هایی است که شما آدمیان برمی شمرید، به سوی او بالا می‌رود.'),
(3509, 32, 6, 'اوست خداوند دانا به نهان و آشکار; شکست ناپذیر و مهربان.'),
(3510, 32, 7, 'خدایی که آنچه را آفرید زیبا و نیکویش ساخت، و آفرینش آدمی را از گِل آغاز کرد.'),
(3511, 32, 8, 'پس از آن، نسل او را از راه ولادت، از چکیده‌ای که آبی پست و بی مقدار بود آفرید.'),
(3512, 32, 9, 'و در پی خلقتِ نخستین انسان، به او شکل آدمی بخشید و از روحی بلندمرتبه که جلوه‌ای الهی دارد در او دمید، و به شما گوش‌های شنوا و چشم‌های بینا و دل‌های اندیشه گر عطا فرمود، ولی سپاسگزاری شما در برابر نعمت‌های الهی بسی اندک است.'),
(3513, 32, 10, 'منکران رستاخیز گفتند: آیا هنگامی که ما مُردیم و در زمین متلاشی و ناپدید شدیم، در آفرینشی جدید قرار خواهیم گرفت و دوباره زنده خواهیم شد؟ اینان با این سخن نمی‌خواهند قدرت خدا را انکار کنند، بلکه آنان دیدار پروردگارشان را نپذیرفته و بدان کافر شده‌اند.'),
(3514, 32, 11, 'ای پیامبر، در پاسخ آنان بگو: شما در زمین ناپدید نمی‌شوید، بلکه فرشته مرگ که بر شما گماشته شده است، به هنگام مرگ، روح شما را که حقیقت شماست به تمام و کمال از اندامتان می‌ستاند و سپس در روز رستاخیز به سوی پروردگارتان بازگردانده می‌شوید.'),
(3515, 32, 12, 'ای کاش می‌دیدی زمانی را که مجرمان، سرهای خود را از شرمساری و پشیمانی در پیشگاه خداوندی که دیدارش را باور نداشتند به زیر افکنده‌اند و می‌گویند: پروردگارا! ما با مشاهده حقیقت بینا شدیم و از سرِ طاعت فرمان تو را شنیدیم، پس ما را به دنیا بازگردان، که اگر باز گردیم کار شایسته می‌کنیم، زیرا ما اهل یقین شده‌ایم.'),
(3516, 32, 13, '[ولی باید بدانند که] اگر ما می‌خواستیم همه را هدایت کنیم، هر فردی را به تناسب حال و به اندازه استعدادش هدایت می‌کردیم [تا هیچ کس از جن و انس به دوزخ درنیفتد]، ولی این سخن از جانب من تثبیت شده است که قطعاً دوزخ را از همه جنّیان و آدمیانی که شیطان را پیروی کنند، پر خواهم کرد.'),
(3517, 32, 14, 'پس شما کافران از آن رو که دیدار امروزتان را فراموش کردید و بدان بی اعتنا بودید، عذاب آتش را بچشید; ما نیز شما را به فراموشی سپرده بودیم و به نجات و سعادت شما که هم اکنون خواهان آنید، بی اعتنا بودیم. آری، به آنچه می‌کردید عذاب جاودانه را بچشید.'),
(3518, 32, 15, 'تنها کسانی به نشانه‌های ربوبیّت ما ایمان دارند که وقتی آن نشانه‌ها را به آنان یادآوری کنند، برای سجده به درگاه خدا بر زمین می‌افتند و پروردگارشان را همراه با ثنایی شایسته و نیکو از هر بدی و کاستی پیراسته می‌شمرند بی آن که ذره‌ای تکبّر ورزند.'),
(3519, 32, 16, 'پهلوهایشان از بسترها جدا می‌شود و از خوابِ شبانه برمی خیزند و پروردگار خود را از روی بیم و امید می‌خوانند و از آنچه روزیشان کرده‌ایم در راه خدا انفاق می‌کنند.'),
(3520, 32, 17, 'به سزای کارهایی که می‌کردند، پاداشی فراتر از تصور آدمی به آنان داده می‌شود و هیچ کس نمی‌داند چه چیزهایی که مایه سرور و چشم روشنی هر بیننده‌ای است، برای آنان نهان شده و ذخیره گشته است.'),
(3521, 32, 18, 'پس آیا کسانی که مؤمنند، همچون کسانی اند که فاسق و سرکش اند؟ نه اینان برابر نیستند.'),
(3522, 32, 19, 'و امّا کسانی که ایمان آورده و کارهای شایسته کرده‌اند، برای آنان باغ‌هایی است که در آنها سکونت می‌کنند. این عطایی است در برابر آنچه در دنیا انجام می‌دادند.'),
(3523, 32, 20, 'و امّا کسانی که از فرمان خدا و پرستش او سربرتافته و رستاخیز را منکر شده‌اند، سزایشان آتش است. هرگاه بخواهند از آن بیرون بیایند، به آن بازگردانده می‌شوند و به آنان گفته می‌شود: بچشید عذاب آتش را، همان عذابی که پیوسته آن را دروغ می‌شمردید.'),
(3524, 32, 21, 'قطعاً آنان را پیش از آن که در روز قیامت به عذاب بزرگ‌تر گرفتار سازیم، از رنج‌ها و مشقت‌های دنیوی که عذاب نزدیک‌تر است می‌چشانیم، شاید به خداپرستی بازگردند و از انکار آخرت بازایستند.'),
(3525, 32, 22, 'و کیست ستمکارتر از آن کس که آیات پروردگارش به او تذکر داده شود آن گاه از آن روی برتابد؟ اینان مجرم اند و ما قطعاً در آخرت از مجرمان انتقام خواهیم گرفت.'),
(3526, 32, 23, 'همانا به موسی تورات را که نزول قرآن را بر تو تأیید می‌کند عطا کردیم; پس مبادا درباره رستاخیز که روز لقای خداست و قرآن بیانگر آن است در تردید باشی; و ما تورات را وسیله هدایت بنی اسرائیل قرار دادیم.'),
(3527, 32, 24, 'و از میان آنان گروهی را چون در امر دین شکیبایی ورزیدند و پیش از آن به آیات ما یقین داشتند، به امامت و رهبری گماردیم تا مردم را به فرمان ما به سرمنزل هدایت برسانند.'),
(3528, 32, 25, 'به یقین، پروردگارت روز قیامت خود میان بنی اسرائیل درباره آنچه بر سرش اختلاف می‌کردند داوری خواهد کرد. و حق گرایان و باطل اندیشان را از هم جدا خواهد ساخت.'),
(3529, 32, 26, 'آیا این واقعیت که بسیاری از امت‌ها را هلاک ساختیم آنان را رهنمون نشده است؟ همان امت‌هایی که پیش از اینان بودند و هم اکنون اینان در خانه‌های آنان راه می‌روند. به یقین در آنچه یاد شد، آیات و نشانه‌هایی است; پس آیا پندها را نمی‌شنوند و حق را نمی‌پذیرند؟'),
(3530, 32, 27, 'آیا ندیده‌اند که ما با ابرهای باران زا آب را به سوی زمین خشک که گیاهی در آن نروییده است می‌رانیم، و به وسیله آن زراعتی از زمین برمی آوریم که دام هایشان و خودشان از آن می‌خورند؟ آیا چشم نمی‌گشایند و نمی‌بینند؟'),
(3531, 32, 28, 'و مشرکان می‌گویند: اگر شما مؤمنان راست می‌گویید، این فتح و پیروزی که شما را به آن نوید داده‌اند چه وقت تحقق می‌یابد؟'),
(3532, 32, 29, 'ای پیامبر، بگو: روز فتح فرا می‌رسد، ولی در آن روز ایمانِ کسانی که کفر ورزیده‌اند سودی برایشان ندارد و عذاب آنان را فرا می‌گیرد و هیچ مهلتی به آنان داده نمی‌شود.'),
(3533, 32, 30, 'پس روی از آنان برتاب و در انتظار آن باش که خداوند حق را بر باطل چیره سازد و تو را پیروز گرداند; آنان در انتظارند که با مردن و یا کشته شدن تو، دعوتت پایان پذیرد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'ای پیامبر، کافران با پشتیبانی منافقان از تو می‌خواهند که با آنان و معبودانشان مخالفت نکنی تا آنان نیز متعرّض تو و خدایت نشوند; از خدا پروا داشته باش و از کافران و منافقان فرمان مبر، چرا که خداوند دانا و حکیم است.'),
(3535, 33, 2, 'تو باید آنچه را از جانب پروردگارت به تو وحی می‌شود پیروی کنی و با کفرپیشگان و منافقان طبق فرمان خدا رفتار نمایی; به یقین خداوند به آنچه می‌کنید آگاه است.'),
(3536, 33, 3, 'مخالفت با خواسته کافران و منافقان برای تو دشوار می‌نماید، ولی بر خدا توکّل کن که خداوند برای کارسازی بندگانش بسنده است.'),
(3537, 33, 4, 'هیچ کس نمی‌تواند به دو امر متضاد اعتقاد داشته باشد، زیرا خداوند در درون هیچ کسی دو قلب قرار نداده است. پس تو نمی‌توانی هم پیرو آرای کفرپیشگان و منافقان باشی و هم پیرو وحی الهی. خداوند، همسرانتان را که با (ظِهار) بسان مادران خویش می‌شمرید، مادران شما قرار نداده و آنان را بر شما حرام نساخته است; و نیز پسرخوانده‌های شما را پسرانتان نشمرده است تا حکم پسران واقعی شما را داشته باشند و از شما ارث ببرند و همسرانشان مَحرم شما شوند. این گفتار شماست که بر زبان می‌رانید. خداوند به محتوای آن حکم نکرده است و خدا همواره حق می‌گوید و اوست که به راه درست هدایت می‌کند.'),
(3538, 33, 5, 'پسرخوانده‌های خود را آن گاه که نام می‌برید، به نام پدرانشان بخوانید که این نزد خدا منصفانه‌تر است; و اگر پدرانشان را نمی‌شناسید، آنان برادران و دوستان دینی شمایند، پس آنان را با نام برادر و دوست بخوانید. و اگر از روی خطا یا فراموشی آنان را به نام غیر پدرانشان نام برده‌اید، بر شما گناهی نیست، ولی در آنچه دل هایتان قصد کرده است و از روی عمد بوده گناه کرده‌اید. آری کار ناروایی که از روی خطا یا فراموشی انجام شده است آمرزیده می‌شود، چرا که خداوند آمرزنده و مهربان است.'),
(3539, 33, 6, 'پیامبر در امر دین و دنیای مؤمنان از آنان نسبت به خودشان سزاوارتر است، و همسران او به منزله مادران ایشانند. و در کتاب خدا ثبت است که خویشاوندان در ارث بردن از یکدیگر، بر همه مؤمنان و حتی مهاجران مقدّم‌اند، و از این پس هجرت و اُخوّت ایمانی سبب ارث نخواهد بود. آری می‌توانید در حق برادران ایمانیِ خود کار نیکی کنید و با وصیّت، بخشی از دارایی‌های خود را به آنان برسانید. این حکم در کتاب الهی نگاشته شده است.'),
(3540, 33, 7, 'و یاد کن هنگامی را که از همه پیامبران پیمان گرفتیم که یک دین را ابلاغ کنند، و نیز از تو و از نوح، و از ابراهیم و موسی و عیسی پسر مریم; آری از همه آنان پیمانی استوار گرفتیم.'),
(3541, 33, 8, 'خداوند این پیمان‌ها را برعهده آنان نهاد و ابلاغ دین واحد را از آنان خواست تا از راستی پیشگان بخواهد راستی و درستیِ خود را در این دنیا با کردار و گفتار درست، آشکار سازند و آن را در مرحله عمل نشان دهند، و او برای کفرپیشگان عذابی دردناک آماده کرده است.'),
(3542, 33, 9, 'ای کسانی که ایمان آورده‌اید، نعمت خدا را که در غزوه خندق بر شما نازل شد، به خاطر داشته باشید، آن هنگام که سپاهیانی از قبایل مختلف هم پیمان شدند و آهنگ شما کردند، و ما برای شکست آنان در شب‌هایی زمستانی بادی سرد بر آنان فرستادیم و لشکریانی از فرشتگان که شما آنها را نمی‌دیدید به سویشان گسیل داشتیم، و خدا به آنچه می‌کنید بیناست.'),
(3543, 33, 10, 'هنگامی خداوند نعمت خود را به شما ارزانی داشت که لشکریانی از سمت بالای شما (از شرق مدینه) و لشکریانی از فرودست شما (غرب مدینه) بر شما هجوم آوردند; و هنگامی که از شدّت ترس و وحشت دیدگان به یک سو غلطید و جان‌ها به حنجره‌ها رسید و درباره خدا گمان‌ها بردید.'),
(3544, 33, 11, 'آن گاه که لشکر احزاب از شرق و غرب بر مسلمانان تاختند و سختی به نهایت رسید، مؤمنان آزموده شدند و از ترس به شدت مضطرب گشتند.'),
(3545, 33, 12, 'و همان دم که منافقان و کسانی که در دل هایشان بیماری ریشه کرده بود و سست ایمان بودند، می‌گفتند: خداوند با نوید پیروزی ما را خام کرد و ما را جز به فریب وعده نداد و پیامبرش نیز چنین کرد.'),
(3546, 33, 13, 'و آن دم که گروهی از منافقان و کسانی که سست ایمان بودند گفتند: ای مردم یثرب، شما را در برابر مشرکان توانِ ایستادگی نیست، به مدینه بازگردید. و گروهی دیگر از آنان از پیامبر اجازه می‌خواستند که برگردند، و می‌گفتند: خانه‌های ما بی حفاظ است و در آن رخنه‌هایی است که از دزدان و دشمنان در امان نیست. در حالی که خانه هایشان بی حفاظ نبود. آنان با این بهانه فقط در پی آن بودند که از میدان نبرد بگریزند.'),
(3547, 33, 14, 'و اگر زمانی که منافقان و سُست عقیده‌ها در خانه‌های خود به سر می‌بردند، از اطراف و اکناف بر آنان وارد می‌شدند، سپس از آنان خواسته می‌شد که از دین بازگردند، قطعاً خواسته آنان را می‌پذیرفتند و در پذیرش آن جز زمانی کوتاه درنگ نمی‌کردند.'),
(3548, 33, 15, 'همانا این منافقان و بیماردلانِ سست ایمان پیش‌تر با خدا عهد بسته بودند که در میدان نبرد پشت به دشمن نکنند و از کارزار نگریزند، و پیمان خدا مورد سؤال قرار خواهد گرفت.'),
(3549, 33, 16, 'بگو: اگر از مرگ یا کشته شدن فرار می‌کنید، هرگز فرار برای شما سودی ندارد، زیرا هر کسی سرآمدی دارد که تقدیم و تأخیری در آن نیست. و گیرم که گریز از میدان جنگ موجب تأخیر مرگ شود در این صورت نیز جز زمانی اندک از متاع دنیا بهره مند نخواهید شد، چرا که این زمان پایان پذیر است.'),
(3550, 33, 17, 'بگو: هر آسایش و گزندی در گرو اراده خداست; اگر او رنجی برای شما بخواهد، یا رحمتی برایتان اراده کند کیست آن که شما را از خواسته او مانع شود؟ آنان برای خود هیچ سرپرست و یاوری جز خدا نخواهند یافت.'),
(3551, 33, 18, 'همانا خداوند به کسانی از شما که مردم را از حضور در جنگ بازمی دارند و نیز به کسانی که به برادران هم مسلک خود می‌گویند: میدان نبرد را رها کنید و به سوی ما بیایید، آگاه است. آنان خود، جز زمانی کوتاه در کارزار حضور نمی‌یابند.'),
(3552, 33, 19, 'از جانِ خود برای شما دریغ می‌کنند و بدان بخل میورزند، و هنگامی که با بروز نشانه‌های نبرد، دهشت و ترس پدیدار شود، آنان را می‌بینی که به تو می‌نگرند و چشمانشان بر اثر ترس، بسان چشمان کسی که سایه‌ای از مرگ بر او افکنده شده است، در حدقه‌ها می‌چرخد. ولی آن گاه که با خاموش شدن آتش جنگ، ترس و وحشت زایل شود، با زبان‌هایی تیز شما را نیش می‌زنند و به شدت سرزنش می‌کنند و بر مال و منالی که بدان دست یافته‌اید بخل میورزند. اینان اگرچه اظهار ایمان می‌کنند، ولی ایمان نیاورده‌اند; از این رو خداوند اعمالشان را تباه ساخته، و این کار بر خدا آسان است.'),
(3553, 33, 20, 'منافقان و بیماردلانِ سست ایمان از شدت ترس می‌پندارند لشکر احزاب از میدان کارزار نرفته‌اند، [در حالی که آنان گریخته بودند و غزوه خندق به پایان رسیده بود.] و اگر دوباره لشکر احزاب بازآیند، اینان دوست دارند که از مدینه به صحراها روند و در میان اعراب بادیه نشین به سر برند و در آن جا از خبرهای مربوط به شما پرسوجو کنند. [این را بدانید که] اگر آنان در میان شما باشند چندان ثمربخش نیست، زیرا جز اندک زمانی پیکار نمی‌کنند.'),
(3554, 33, 21, 'قطعاً در گفتار و رفتار رسول خدا اقتدای نیکویی برای شماست. اقتدا به رسول خدا از عهده همه مدعیان ایمان برنمی آید، بلکه آن کسی پیرو اوست که به خدا و روز واپسین امید دارد و خدا را بسیار یاد می‌کند.'),
(3555, 33, 22, 'برخورد منافقان و بیماردلانِ سست ایمان در رویارویی با دشمن این گونه بود. امّا مؤمنانِ راستین هنگامی که لشکر احزاب را مشاهده کردند و مدینه را در محاصره آنان دیدند، گفتند: این همان است که خدا به ما وعده داد و او راست گفت و این است وعده‌ای که رسول خدا به ما داد و او راست گفت. و مشاهده لشکر احزاب سبب نشد که آنان گمانی ناروا برند و یا سخنی ناشایسته گویند، بلکه آنان را جز ایمان و تسلیم در برابر خدا نیفزود.'),
(3556, 33, 23, 'مؤمنان با خدا عهد بستند که در برخورد با دشمنان از صحنه کارزار نگریزند. از میان آنان مردانی بودند که به آنچه با خدا بر سر آن پیمان بستند صادقانه وفا کردند. پس برخی از آنان با مردن یا کشته شدن در راه خدا، اجل خود را به پایان بردند و برخی از آنان این را انتظار می‌برند و در پیمان و مواضع خود هیچ تغییر و تبدیلی ایجاد نکرده‌اند.'),
(3557, 33, 24, '[پدید آمدن اسباب ترس و اضطراب با هجوم لشکر احزاب] بدان هدف بود که خداوند مؤمنانی را که در پیمان خود صادق بودند به خاطر راستی شان سزا دهد، و منافقان را اگر بخواهد ـ در صورتی که توبه نکنند ـ عذاب کند و چنانچه توبه کنند توبه شان را بپذیرد و عذابشان نکند، چرا که خداوند آمرزنده و مهربان است.'),
(3558, 33, 25, 'و خدا کافرانی را که جنگ احزاب را سامان دادند خشمگین و اندوهبار باز گرداند، بی آن که به هیچ نتیجه‌ای که آرزوی آن را داشتند و آن را خیر می‌پنداشتند نایل شده باشند. آری، خدا جنگ را از مؤمنان باز داشت پس میان آنان و دشمنانشان پیکاری رخ نداد. و او نیرومند و شکست ناپذیر است.'),
(3559, 33, 26, 'او کسانی از اهل کتاب را که از مشرکان پشتیبانی کردند، از دژها و بُرج هایشان به زیر آورد و در دل هایشان وحشت افکند [و آنان را تسلیم شما ساخت]، گروهی را می‌کشتید و گروهی را اسیر می‌کردید.'),
(3560, 33, 27, 'و سرزمینشان و خانه‌ها و ثروتشان و نیز سرزمین خیبر را که قدم در آن ننهاده بودید همه را بعد از ایشان از آنِ شما قرار داد، و خدا بر هر چیزی تواناست.'),
(3561, 33, 28, 'ای پیامبر به همسرانت بگو: اگر به لذت‌ها و بهره‌های دنیوی دل بسته‌اید و خواهان زندگی دنیا و زیورهای آنید، با من نمی‌توانید به سر برید، بیایید تا مالی که از آن بهره برید به شما بدهم و شما را طلاق گویم; طلاقی نیکو که در آن هیچ گونه کشمکش و نزاعی نباشد.'),
(3562, 33, 29, 'و اگر در پی اطاعت از خدا و رسول او هستید و نیکبختی سرای آخرت را می‌طلبید و زندگی با پیامبر را بی آن که خواهان رفاه و زیور دنیا باشید، برتری می‌دهید، همچنان با من به سر برید که بی تردید خدا برای نیکوکاران شما پاداشی بزرگ آماده کرده است.'),
(3563, 33, 30, 'ای همسران پیامبر، هر کس از شما کار ناشایسته و گناه بزرگِ آشکاری مرتکب شود عذابش دو چندان می‌شود و این کار بر خدا آسان است.'),
(3564, 33, 31, 'و هر کس از شما، خاضعانه خدا و رسولش را فرمان بَرَد و کاری شایسته کند، پاداشش را دو چندان به او می‌دهیم و بهشت را که روزیِ ارزشمندی است برای او آماده ساخته‌ایم.'),
(3565, 33, 32, 'ای همسران پیامبر، اگر شما تقوا پیشه کنید، مانند هیچ یک از زنان دیگر نیستید، زیرا شما منزلتی والاتر دارید; پس مبادا در حضور مردان به ناز و نرمی سخن گویید که آن کس که در دلش بیماری است و ایمانش سست است، طمع میورزد; باید با لحنی متعارف که فسادانگیز نیست سخن بگویید.'),
(3566, 33, 33, 'و در خانه‌های خویش بمانید و آن گونه که زنان در روزگار جاهلیتِ نخست نمایان می‌شدند، در میان مردم نمایان نشوید و نماز را به پا دارید و زکات بدهید و از خدا و رسولش اطاعت کنید. جز این نیست که خدا می‌خواهد پلیدی را از شما اهل بیت بزداید و شما را از آثار آن به طور کامل پاک دارد.'),
(3567, 33, 34, 'ای همسران پیامبر، آنچه از آیات خدا و معارف الهی که در خانه‌های شما تلاوت می‌شود، به خاطر بسپارید و از آنها غفلت نکنید، که علم و قدرت خدا نافذ است و او به هر چیزی آگاهی دارد.'),
(3568, 33, 35, 'به یقین، مردان و زنان مسلمان که در عمل به احکام دین پای بندند، و مردان و زنانی که دین را باور کرده‌اند، و مردان و زنانی که خدا و رسولش را از روی خضوع اطاعت می‌کنند، و مردان و زنان راستگو و مردان و زنانی که در مصیبت‌ها و بر طاعت و دوری از گناه شکیبایند، و مردان و زنان فروتن و مردان و زنانی که در راه خدا انفاق می‌کنند، و مردان و زنانی که روزه می‌دارند، و مردان و زنانی که شرمگاه‌های خود را از آنچه خدا حرام کرده است نگاه می‌دارند، و مردان و زنانی که با زبان و در دل بسیار یاد خدا می‌کنند، خداوند برای همه آنان آمرزش و پاداشی بزرگ فراهم کرده است.'),
(3569, 33, 36, 'و هنگامی که خدا و رسولش درباره امری از امور مؤمنان فرمانی صادر کردند، هیچ مرد و زن مؤمنی در کارشان اختیاری ندارند و آنان را نرسد که آنچه خود می‌خواند برگزینند. و هر کس خدا و رسول او را نافرمانی کند قطعاً به گونه‌ای آشکار گمراه گشته است.'),
(3570, 33, 37, 'ای پیامبر، یاد کن زمانی را که به پسرخوانده ات زید که خدا بر او نعمت ارزانی داشته و هدایتش کرده بود و تو نیز به او نعمت بخشیده و از بردگی رهایش ساخته بودی می‌گفتی: همسرت را نزد خود نگاه دار و از خدا بترس و او را طلاق مده; و در نفس خود حقیقتی را پنهان می‌داشتی که خدا آشکار شدنش را اراده کرده بود; او زینب همسر زید را در شمار همسران تو مقرّر داشته بود و تو از مردم بیم داشتی که مبادا موافقت با طلاق زینب و سپس به همسری گرفتن او در ایمان مردم اثری نامطلوب داشته باشد; در حالی که خدا سزاوارتر بود که از او پروا کنی [و درصدد انجام فرمان او باشی]. پس هنگامی که زید از همسرش خواسته خود را تأمین نمود، او را به همسری تو درآوردیم. آری چنین کردیم تا بر مؤمنان ازدواج با همسرانِ پسرخواندگانشان دشوار نباشد [حتی] پس از آن که پسرخواندگان خواسته خود را از همسرانشان تأمین نموده باشند، و کار خداوند انجام شدنی است.'),
(3571, 33, 38, 'بر پیامبر در آنچه خدا برای او مباح شمرده است هیچ گونه تنگنایی نیست. خداوند این سُنّت را درباره پیامبران پیشین نیز مقرر کرده بود که در کارهای مباح در تنگنا قرار نگیرند، و کار خدا دارای اندازه و حساب است و برای هر کسی آنچه را که مناسب اوست مقرر می‌کند.'),
(3572, 33, 39, 'همانان که پیام‌های خدا را ابلاغ می‌کردند و از خدا می‌ترسیدند و از هیچ کس جز خدا بیم نداشتند، و حسابرس بودن خدا بس است; پس بایسته است که تنها از او پروا شود.'),
(3573, 33, 40, 'محمّد پدر هیچ یک از مردان شما ـ نه زید و نه دیگران ـ نیست تا همسران طلاق داده شده آنان بر او حرام باشند، ولی رسول خدا و خاتم پیامبران است. و خدا به هر چیزی داناست.'),
(3574, 33, 41, 'ای کسانی که ایمان آورده‌اید، خدا را بسیار یاد کنید و ذکر او گویید،'),
(3575, 33, 42, 'و بامدادان و عصرگاهان که شاهد تحول و دگرگونی هستید خدا را تسبیح کنید و او را از تغییر و تحول و از هر عیب و نقصی پاک بشمارید.'),
(3576, 33, 43, 'زیرا اوست که رحمت خود را بر شما مؤمنان فرو می‌فرستد و فرشتگانش برای شما آمرزش می‌طلبند. تا خدا شما را از تاریکی‌های غفلت به درآورد و به روشنایی یاد خویش هدایت کند، و او به مؤمنان مهربان است.'),
(3577, 33, 44, 'روزی که آنان به لقای خدا می‌رسند، درودی که از جانب خدا و فرشتگانش نثار آنان می‌شود (سلام) است که نویددهنده سلامت و امنیت است، و خداوند برای آنان پاداشی ارزشمند و فراوان آماده کرده است.'),
(3578, 33, 45, 'ای پیامبر، ما تو را فرستادیم تا بر رفتار و کردار امّت گواه باشی، و مؤمنان را به بهشت و پاداش‌های الهی مژده دهی، و کافران و گنهکاران را از عذاب دوزخ بترسانی.'),
(3579, 33, 46, 'تو را فرستادیم تا مردم را به اذن خدا به سوی او فراخوانی و در راهنمایی آنان به سوی سعادت چراغی تابان باشی.'),
(3580, 33, 47, 'و به مؤمنان بشارت ده که برای آنان از جانب خداوند بخششی بزرگ است.'),
(3581, 33, 48, 'و از کافران و منافقان فرمان مبر، و به آزارشان بی اعتنا باش، و برای دفع آن به خدا توکّل کن که خداوند برای این که کارها به او واگذار شود کافی است.'),
(3582, 33, 49, 'ای کسانی که ایمان آورده‌اید، هرگاه زنان مؤمن را به نکاح خود درآوردید، سپس پیش از آن که با آنان همبستر شوید طلاقشان دادید، برای شما برعهده آنان عده‌ای نیست که بخواهید روزهای آن را شماره کنید. ولی باید ـ چنانچه مهریه‌ای برای آنان معیّن نکرده‌اید ـ مالی که از آن بهره برند به آنان بدهید و به خوبی، بی هیچ نزاع و خصومتی، آنان را رها سازید.'),
(3583, 33, 50, 'ای پیامبر، ما برای تو آن همسرانت را که مهرشان را داده‌ای حلال کردیم، و نیز کنیزانی را که خدا از غنایم جنگی به تو ارزانی کرده و تو مالک آنها شده‌ای، و همچنین دختران عمو و دختران عمه‌ها و دختران دایی و دختران خاله هایت را با این شرط که همراه تو هجرت کرده باشند. و اگر زنی مؤمن خود را بدون تقاضای مهریه، به پیامبر ببخشد و پیامبر بخواهد او را به همسری بگیرد، ما وی را برای پیامبر حلال کرده‌ایم. البته این قانون مخصوص توست نه سایر مؤمنان; و ما دانسته‌ایم که درباره زنانی که سایر مؤمنان به همسری می‌گیرند و درباره کنیزانی که مالک آنها می‌شوند چه احکامی مقرّر کرده‌ایم; [آنچه مقرر داشته‌ایم از روی علم بوده و مؤمنان به رعایت آن موظفند.] این هفت گروه از زنان را برای تو حلال کردیم تا هیچ مشقت و تنگنایی بر تو نباشد، و خداوند بسیار آمرزنده و مهربان است.'),
(3584, 33, 51, 'بر تو رواست که نوبت همبستری با هر یک از همسرانت را که بخواهی به تأخیر افکنی و هر کدام را که بخواهی به سوی خود فراخوانی، و نیز رواست که از آنان کناره گیری کنی و نوبت همبستری مقرّر نداری. و بر تو گناهی نیست که از آنان که کناره گیری کرده‌ای هر کدام را خواستی به سوی خود فراخوانی و برای او نوبت همبستری مقرر داری. این شیوه نزدیک‌تر است به این که آنان شادمان شوند و اندوهگین نشوند و همگیشان به آنچه به آنان داده‌ای خشنود گردند. و خداوند به آنچه در قلب‌های شماست آگاهی دارد، و خداوند مصلحت بندگان را می‌داند و در کیفر تبهکاران شتاب نمی‌کند.'),
(3585, 33, 52, 'بعد از این زنان که اکنون همسر تواَند و خدا و رسولش را بر دنیا و زیورهای آن برگزیده‌اند، زنان دیگر بر تو حلال نیستند [از این پس نباید همسری برگزینی]. و نیز بر تو روا نیست که با طلاق دادن آنان زنان دیگری را به جای آنان به همسری بگیری، هر چند زیبایی شان تو را به شگفت آورد، مگر این که بخواهی کنیزی را به مِلک خود درآوری که این بر تو حلال است. از مخالفت خدا باید برحذر بود، که خدا بر هر چیزی نگهبان است.'),
(3586, 33, 53, 'ای کسانی که ایمان آورده‌اید، داخل اتاق‌های پیامبر نشوید، مگر این که به شما اجازه داده شود که برای خوردن طعامی به آن جا روید; ولی پیش از موعد به خانه او داخل نشوید تا در انتظار وقت غذا، ماندنِ شما به درازا کشد، بلکه همان زمان که دعوت شده‌اید به خانه او درآیید، و وقتی غذا خوردید پراکنده شوید و به هیچ سخنی دل نسپارید و سرگرم نشوید، زیرا درنگ شما او را آزار می‌دهد و از شما شرم می‌کند که بگوید بیرون بروید، ولی خدا از گفتن حق شرم ندارد. و چون از همسران پیامبر کالایی خواستید و ناچار شدید با آنان سخن بگویید، از پشت حجابی از آنان بخواهید و از پس پرده با آنان سخن بگویید. این شیوه برای دل‌های شما و دل‌های آنان پاکیزه‌تر است. و شما حق ندارید پیامبر خدا را آزار دهید، و پس از او نباید هیچ گاه با همسرانش ازدواج کنید، که این کار نزد خدا گناهی بزرگ است.'),
(3587, 33, 54, 'اگر چیزی را آشکار کنید یا آن را نهان دارید خدا آن را می‌داند، زیرا خدا به هر آشکار و نهانی داناست.'),
(3588, 33, 55, 'بر همسران پیامبر گناهی نیست که در برابر پدرانشان و پسرانشان و برادرانشان و پسرانِ برادرانشان و پسرانِ خواهرانشان و زنان همکیششان و نیز بردگان و کنیزانشان حجاب نداشته باشند. ای همسران پیامبر، از خدا پروا داشته باشید و احکام او را رعایت کنید، به یقین خدا بر هر چیزی شاهد و ناظر است.'),
(3589, 33, 56, 'همانا خداوند رحمتش را بر پیامبر افاضه می‌کند و فرشتگانش برای او آمرزش می‌طلبند و او را به پاکی یاد می‌کنند; پس ای کسانی که ایمان آورده‌اید، شما نیز از خدا بخواهید تا او را از رحمت خود برخوردار سازد; و بر او سلام گویید، سلامی شایسته.'),
(3590, 33, 57, 'کسانی که خدا و رسولش را آزار می‌رسانند، قطعاً خدا آنان را در دنیا و آخرت لعنت می‌کند; در دنیا از رحمت هدایت و در آخرت از مقام قُرب خویش محرومشان می‌سازد، و برای آنان عذابی خوارکننده آماده کرده است.'),
(3591, 33, 58, 'کسانی که مردان و زنان مؤمن را بی آن که مرتکب گناهی شده باشند آزار می‌رسانند، قطعاً به آنان تهمت زده تا آزارشان را روا بشمارند پس بار بهتان و گناه آشکاری بر دوش می‌کشند.'),
(3592, 33, 59, 'ای پیامبر، به همسران و دخترانت و به زنان مؤمنان بگو بخشی از مقنعه‌های خویش را به خود نزدیک کنند تا گردن و سینه آنان آشکار نشود. این پوشش نزدیک‌ترین راه است برای این که آنان به عفت و حجاب شناخته شوند و در نتیجه مورد آزار فاسقان قرار نگیرند، و خداوند بسیار آمرزنده و مهربان است.'),
(3593, 33, 60, 'اگر منافقان و کسانی که در دل هایشان بیماری است و شایعه افکنانِ در مدینه، از کارهایشان باز نایستند، قطعاً تو را بر ضد آنان می‌شورانیم تا از مدینه بیرونشان کنی، آن گاه جز زمانی کوتاه در مجاورت تو در این شهر نخواهند زیست.'),
(3594, 33, 61, 'این در حالی است که آنان لعنت شده‌اند; هر کجا یافت شوند باید دستگیر گردند و همگی به سختی کشته شوند.'),
(3595, 33, 62, 'تبعید و کشتن منافقان و هم مسلکان آنان، که طغیانگری را از حد می‌گذرانند سنّتی است که خدا آن را در میان کسانی که پیش از شما درگذشته‌اند جاری ساخته است، و هرگز در سنت خدا تغییر و دگرگونی نخواهی یافت.'),
(3596, 33, 63, 'مردم از تو درباره زمان برپایی قیامت و دوری و نزدیکی آن می‌پرسند; بگو: علم آن فقط در نزد خداست. چه چیزی تو را از آن آگاه می‌کند و تو از هنگام آن چه می‌دانی؟ شاید زمان برپایی قیامت نزدیک باشد.'),
(3597, 33, 64, 'خداوند کافران را از رحمت خود دور ساخته و برای آنان آتشی فروزان آماده کرده است.'),
(3598, 33, 65, 'برای همیشه در آن ماندگارند، در حالی که نه سرپرستی می‌یابند که نجاتشان را برعهده گیرد، و نه یاوری که در رهایی از آتش به آنان کمک کند.'),
(3599, 33, 66, 'آن روز که صورت هایشان در آتش زیر و رو می‌شود، می‌گویند: ای کاش خدا را فرمان می‌بردیم و از پیامبرش اطاعت می‌کردیم.'),
(3600, 33, 67, 'و خواهند گفت: پروردگارا، ما از سران و کهنسالان خود پیروی کردیم و آنان ما را از راه راست به گمراهی کشاندند.'),
(3601, 33, 68, 'پروردگارا، آنان را از این عذاب، دو چندان ده، چرا که هم خود گمراه بودند و هم ما را به بیراهه بردند، و آنان را لعنت کن، لعنتی بزرگ، که ذرّه‌ای از رحمت تو نصیبشان نشود.'),
(3602, 33, 69, 'ای کسانی که ایمان آورده‌اید، پیامبرتان را با سخنانی که شایسته مقام نبوت نیست آزار ندهید و مانند آن کسانی از بنی اسرائیل نباشید که موسی را با تهمت و افترا آزار رساندند و خدا او را تبرئه کرد و ساحتش را از آنچه گفتند پاک ساخت، و او نزد خدا آبرومند بود.'),
(3603, 33, 70, 'ای کسانی که ایمان آورده‌اید، از خدا پروا کنید و سخن راست و استوار گویید، و از بیهوده گویی و سخنان فسادانگیز بپرهیزید.'),
(3604, 33, 71, 'اگر چنین باشید، خداوند اعمال و رفتار شما را برای شما به صلاح می‌آورد و برای شما گناهانتان را می‌بخشد. و هر کس از خدا و پیامبرش فرمان برد، قطعاً به سعادتی بزرگ نایل شده است.'),
(3605, 33, 72, 'به راستی ما امانت خود را بر آسمان‌ها و زمین و کوه‌ها عرضه کردیم، ولی آنها سرباز زدند و از این که آن بار سنگین را برگیرند، امتناع کردند و از آن هراسناک شدند، اما انسان آن را برعهده گرفت، چرا که او ستمکار و نادان بود.'),
(3606, 33, 73, 'فرجامِ برعهده گرفتن این امانت آن است که خداوند مردان و زنان منافق و مردان و زنان مشرک را عذاب می‌کند و با افاضه رحمت بر مردان و زنان مؤمن به آنان روی می‌آورد، چرا که خداوند بسیار آمرزنده و مهربان است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'ستایش در این جهان از آنِ خداست; آن که هر چه در آسمان‌ها و هر چه در زمین است مِلک اوست; و ستایش در جهان آخرت نیز از آنِ اوست، و او حکیم است که سرای آخرت را مقرر داشته، و او به جزئیات امور آگاه است.'),
(3608, 34, 2, 'آنچه در زمین فرو می‌رود و آنچه از زمین بیرون می‌آید و آنچه از آسمان نازل می‌شود و آنچه در آسمان بالا می‌رود همه را می‌داند (به تمام حرکات و کارها آگاهی دارد) و سزا و پاداش هر عملی را می‌داند; او مهربان و آمرزنده است و مؤمنان را از رحمت و آمرزش خود برخوردار می‌سازد.'),
(3609, 34, 3, 'با این حال، کسانی که کافر شدند، گفتند: قیامت به سراغ ما نخواهد آمد. بگو: آری خواهد آمد; به پروردگارم که نهان را می‌داند سوگند که به سراغ شما خواهد آمد. شما پراکنده شدن اجزای مردگان و تبدیل شدن آنها به خاک را دلیل انکار معاد انگاشته‌اید، ولی بدانید که هموزن ذرّه‌ای، نه در آسمان‌ها و نه در زمین، از دید پروردگار من دور نمی‌ماند، و کوچک‌تر از آن و بزرگ‌تر از آن چیزی نیست مگر این که در کتابی روشنگر ثبت شده است.'),
(3610, 34, 4, 'قیامت برپا خواهد شد، تا خدا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند سزا دهد. اینانند که آمرزش و نعمت‌های بهشت که روزیِ ارزشمندی است، برای آنان خواهد بود.'),
(3611, 34, 5, 'و کسانی که برای از بین بردن آیات ما تلاش کرده‌اند تا ما را [به خیال خود] سخت درمانده کنند، عذابی دردناک خواهند داشت; عذابی که از کردار ناپسندشان پدید می‌آید.'),
(3612, 34, 6, 'ولی کسانی که به آنان شناخت خدا و آیات او داده شده است، به خوبی می‌دانند کتابی که از جانب پروردگارت بر تو نازل شده و از آمدن قیامت خبر می‌دهد، سراسر حق است و به راه آن مقتدری که شکست ندارد و آن ستوده‌ای که همه کارهایش زیباست، هدایت می‌کند.'),
(3613, 34, 7, 'آنان که کفر ورزیده‌اند، از روی استهزا به همکیشان خود گفتند: آیا مردی را به شما نشان دهیم که شما را خبر می‌دهد که چون مردید و بدن هایتان کاملاً متلاشی و پراکنده گشت، قطعاً در آفرینشی جدید قرار می‌گیرید و حیاتی دوباره می‌یابید؟‌'),
(3614, 34, 8, 'نمی‌دانیم آیا این مرد عاقل است و آگاهانه سخن دروغینی را که خود ساخته است به خدا نسبت می‌دهد و یا نوعی جنون دامنگیرش شده و آنچه می‌گوید خیالبافیِ اوست. نه هیچ کدام نیست، ولی آنان که به حیات بازپسین ایمان ندارند در عذاب گرفتارند، از این رو به بیراهه می‌روند; بیراهه‌ای که با حق فاصله‌ای دور دارد.'),
(3615, 34, 9, 'آیا به آسمان و زمین که پیش روی آنان و پشت سرشان است نظر نیفکنده‌اند؟ اگر بخواهیم زمین را می‌شکافیم و آنان را در زمین فرو می‌بریم و یا پاره‌هایی از آسمان بر آنان می‌افکنیم و هلاکشان می‌کنیم. همانا در این اندرز و اخطار برای هر بنده‌ای که از نافرمانی خدا به اطاعت او روی آورد نشانه‌ای است.'),
(3616, 34, 10, 'همانا ما به داود از جانب خود عطیه‌ای دادیم، آن گاه که کوه‌ها و مرغان هوا را برای او رام ساختیم و گفتیم: ای کوه‌ها و ای پرندگان، در تسبیح خدا با او هم آواز شوید و خدای را پیاپی تسبیح گویید، و نیز آهن را برای او نرم ساختیم.'),
(3617, 34, 11, 'و گفتیم: زره‌های فراخ بساز و در بافتن حَلقه‌های آن اندازه نگهدار و آنها را متناسب با یکدیگر بساز، و به او و مردمش سفارش کردیم که کاری شایسته کنید که من آنچه را انجام می‌دهید می‌بینم.'),
(3618, 34, 12, 'و باد را به تسخیر سلیمان درآوردیم که سیر بامداد آن (از ابتدای روز تا هنگام ظهر) سیر یک ماه، و سیر عصرگاهش (از ظهر تا هنگام غروب) سیر یک ماه راه بود، و مس را [در معدنش] برای او ذوب کردیم که بسان چشمه‌ای روان بود، و از جن کسانی بودند که به فرمان پروردگارشان در برابر او کار می‌کردند، و [چنین مقرّر کردیم که] هر کس از آنها از دستور ما سرپیچی کند و به فرمان سلیمان نباشد، از عذاب آتش فروزان به او بچشانیم.'),
(3619, 34, 13, 'جنیان برای او هر چه می‌خواست: از عبادتگاه‌ها، مجسمه‌ها، ظروف غذا که در بزرگی به حوضچه‌ها می‌ماندند، و دیگ‌های بزرگ که در یک نقطه ثابت بودند، می‌ساختند. ای خاندان داود، برای سپاسگزاری به درگاه خدا کارهای شایسته کنید، و بدانید که سپاسگزاران بسی بلند مرتبه‌اند، و اندکی از بندگان من سپاسگزارند.'),
(3620, 34, 14, 'سلیمان بر عصای خود تکیه کرده بود که ما به مرگ وی حکم کردیم. وقتی او را میراندیم، چیزی جز موریانه جن و انس را به مرگ او رهنمون نشد، [به این طریق که] آن جنبنده خاکی عصای سلیمان را می‌خورد، تا عصا شکست و او بر زمین افتاد، آن گاه که فرو افتاد جنیان دریافتند که اگر غیب می‌دانستند، قبلاً از مرگ سلیمان آگاه می‌شدند و در آن فاصله در عذاب خوارکننده نمی‌ماندند.'),
(3621, 34, 15, 'به یقین قوم سبا را در محل سکونتشان نشانه‌ای [از رحمت و نعمت خدا] بود: دو باغستان که یکی سمت راست و دیگری بر سمت چپ دیارشان قرار داشت. به آنان گفتیم: از رزق پروردگارتان که در این بوستان‌ها پدید آورده است تناول کنید و او را بر نعمتش شکرگزار باشید. شهری است نیکو و دلپسند و پروردگاری است بس آمرزنده.'),
(3622, 34, 16, 'ولی آنان روی گرداندند و سپاس خدا را به جا نیاوردند; پس به سزای کردارشان سیلاب آن سدّ را که در دیارشان بود بر آنان روانه کردیم. سیل، دیار قوم سبا را درنوردید و هر دو بوستانشان را نابود کرد، و ما باغ‌های پرنعمتشان را به دو باغی که دارای میوه‌ای تلخ و درخت شوره گز و اندکی درخت کُنار بود، تبدیل کردیم.'),
(3623, 34, 17, 'این سزا را بدان سبب بر قوم سبا روا داشتیم که کفر ورزیدند و شکر نعمت نکردند، و آیا جز کسی را که بسیار ناسپاس است کیفر می‌دهیم؟'),
(3624, 34, 18, 'از نعمت‌هایی که به قوم سبا ارزانی داشته بودیم این بود که میان آنان و شهرهایی که در آنها برکت نهاده بودیم، آبادی‌هایی نزدیک به هم قرار داده بودیم; آبادی‌هایی آشکار که هر کدام از آنها از آبادی مجاورش قابل رؤیت بود، و نیز فاصله آنها را به یک اندازه مقرر کرده بودیم، و به آنان گفتیم: در این آبادی‌ها سیر و سفر کنید; خواه روزها حرکت کنید و خواه شب‌ها، هماره در امن و امانید و خطری در پیش ندارید.'),
(3625, 34, 19, 'ولی قوم سبا آن نعمت‌ها را ناسپاسی کردند و گفتند: پروردگارا، میان سفرهای ما فاصله‌های دور و دراز قرار ده، که این همه شهر و دیار در طول سفر ملال آور است. آنان با ارتکاب گناهان بر خویشتن ستم کردند; ما هم آنان را از میان بردیم، به گونه‌ای که هیچ اثری از ایشان باقی نگذاشتیم، جز این که آنها را به صورت قصه‌هایی بر سر زبان‌ها نهادیم. آری آنان را سخت پراکنده ساختیم به گونه‌ای که هیچ گروهی با نام و نشان قوم سبا یافت نمی‌شد. به راستی که در سرگذشت آنان برای هر کسی که بسیار شکیبا و سپاسگزار باشد، نشانه‌هایی است بر لزوم پرستش خدا و تحقق روز جزا و پاداش.'),
(3626, 34, 20, 'به یقین ابلیس گمان خود را درباره آدمیان (گمراه ساختن آنان) به ثبوت رسانید، از این رو همگان جز گروهی که سرای آخرت را باور داشتند، از او پیروی کردند.'),
(3627, 34, 21, 'و ابلیس بر آدمیان هیچ گونه تسلطی ندارد که به اجبار آنان را به بیراهه برد، مگر سلطه‌ای که آدمیان خود بخواهند، و این مقدار سلطه را از آن رو به ابلیس دادیم تا کسی را که به آخرت ایمان دارد از کسی که در آن شک دارد مشخص کنیم. و پروردگارت بر هر چیزی نگهبان است و هیچ چیز از علم او محو نمی‌شود.'),
(3628, 34, 22, 'ای پیامبر، به مشرکان بگو: کسانی را که به جای خدا معبود خود پنداشته‌اید بخوانید و نیازهایتان را از آنها بخواهید; آنها هموزن ذرّه‌ای نه در آسمان‌ها و نه در زمین مالک نیستند و در ملکیت آسمان‌ها و زمین هیچ شرکتی با خدا ندارند و هیچ یک از آنها در تدبیر امور هستی یاور و پشتیبان خدا نیست، پس چگونه می‌توانند نیازی را برآورده سازند تا سزاوار پرستش باشند؟'),
(3629, 34, 23, 'مشرکان می‌پندارند معبودانشان شفاعتگر آنانند و امورشان در دنیا به واسطه آنها تدبیر می‌شود، در حالی که شفاعت هیچ کس، حتی شفاعت فرشتگان ـ که خدا آنان را در تدبیر امور هستی واسطه قرار داده است ـ در پیشگاه خدا سودی نمی‌بخشد، مگر برای کسی که او رخصت دهد و فرشتگان را امر کند که شفیع او شوند و کارش را سامان بخشند. فرشتگان خاضعانه در انتظار فرمان الهی‌اند، و در این فاصله ترسان و نگرانند، تا چون فرمان الهی در رسد و تدبیر برخی امور به آنان واگذار شود، آرام گیرند و ترس از دل هایشان زدوده شود. در این هنگام فرشتگان زیردست از مهتران خود می‌پرسند: پروردگارتان چه فرمان داد؟ می‌گویند: آنچه فرمود حق است که نه دگرگون می‌شود و نه باطل می‌گردد، و اوست بلند مرتبه‌ای که هر چیزی در برابر او بی مقدار است، و اوست بزرگی که جهان هستی در برابر او کوچک است.'),
(3630, 34, 24, 'ای پیامبر، از مشرکان بپرس: چه کسی از آسمان‌ها و زمین به شما روزی می‌دهد؟ آنان هرچند دریافته‌اند که معبودانشان توانِ آفرینش ندارند تا روزی دهنده باشند، ولی به این حقیقت اعتراف نمی‌کنند; پس تو خود در پاسخ بگو که خدا روزی دهنده شماست نه معبودانتان. و بگو: ما و شما در دو جبهه مخالف قرار داریم; یا ما بر طریق هدایتیم و شما در گمراهی آشکار، و یا شما بر طریق هدایتید و ما در گمراهی آشکار; اینک بیندیشید و ره یافته را از گمراه باز شناسید.'),
(3631, 34, 25, 'بگو: نه از شما درباره گناهی که ما مرتکب شده‌ایم پرسش می‌شود و نه از ما درباره آنچه شما می‌کنید سؤال خواهد شد.'),
(3632, 34, 26, 'بگو: پروردگارمان ما و شما را در سرای آخرت یکجا گرد می‌آورد; سپس میان ما و شما به حق جدایی می‌اندازد و ما را از یکدیگر متمایز می‌سازد، تا هر کسی به سزای خود برسد. اوست که همه چیزها را مشخص می‌سازد و نیکوکاران را از بدکاران جدا می‌کند و به هر چیزی داناست.'),
(3633, 34, 27, 'بگو: کسانی را که به خدا ملحق نموده و همتایان او پنداشته‌اید به من نشان دهید تا بنگرم آیا در آنها ویژگی‌هایی از خدا وجود دارد که شایسته پرستش باشند؟ هرگز چنین نیست. آنها از آن رو که یا فاقد علم و قدرت و حیاتند و یا این ویژگی‌ها را خدا به آنها عطا کرده است خدا نیستند تا سزاوار پرستش باشند. آن که شایسته پرستش است خداوندی است که عزیز است پس کسی به الوهیت و ربوبیت او راهی ندارد، و حکیم است پس خود نیز کسی را از آن بهره ای نمی‌دهد که کاری گزاف است.'),
(3634, 34, 28, 'ما تو را به رسالت نفرستادیم مگر برای این که مردم را از گناه بازداری و بشارت دهنده و اخطارکننده باشی، ولی بیشتر مردم نمی‌دانند که اگر جز خدا پروردگاری داشتند، حتماً از جانب او پیامبری اخطارکننده و بشارت دهنده برای آنان می‌آمد.'),
(3635, 34, 29, 'کفرپیشگان به مسخره از پیامبر و مؤمنان می‌پرسند: اگر راست می‌گویید که قیامتی در کار است، این وعده کی تحقق خواهد یافت؟'),
(3636, 34, 30, 'بگو: برای شما وعده روزی خواهد بود که نه اندک زمانی از آن باز می‌مانید که دیرتر از وقتش بدان رسید و نه از آن سبقت می‌گیرید که زودتر از هنگامش به آن درآیید.'),
(3637, 34, 31, 'آنان که به رسالت پیامبران کافر شدند گفتند: ما هرگز نه به این قرآن ایمان می‌آوریم و نه به تورات و انجیل که پیش از آن بوده‌اند. ای پیامبر، ای کاش می‌دیدی زمانی را که این ستمکاران در پیشگاه خدا برای محاسبه و مجازات بازداشت شده‌اند و به بحث و جدال می‌پردازند و هر دسته‌ای سخن دسته دیگر را مردود می‌شمرد. در آن روز کافرانی که به دست مستکبران کفرپیشه به ناتوانی کشیده شده بودند به آنان می‌گویند: اگر شما نبودید و ما را به کفر مجبور نمی‌کردید قطعاً ما مؤمن می‌شدیم.'),
(3638, 34, 32, 'سران مستکبر به پیروان مستضعف خود خواهند گفت: آیا پس از آن که هدایت به سراغتان آمد و به شما ابلاغ شد، چگونه می‌توانید ادّعا کنید که ما شما را به اجبار از هدایت بازداشتیم؟ این گونه نیست، شما خود مجرم و گنهکار بودید.'),
(3639, 34, 33, 'و مستضعفان در پاسخ به سران مستکبر می‌گویند: [اگرچه ما را به اجبار از ایمان بازنداشتید، ولی] در گرایش ما به کفر، شما نیز دخالت داشتید. شما بودید که شب و روز فریبکاری کردید و پیوسته توطئه‌ها به کار بردید تا ما را به اطاعتِ خود واداشتید و ما هم آن گاه که خواستید به خدای یگانه کافر شویم و برای او همتایانی قرار دهیم خود را به پیروی از شما ناچار دیدیم. آنان وقتی عذاب دوزخ را می‌بینند پشیمان می‌شوند امّا پشیمانی خود را پنهان می‌دارند، و ما در گردن کسانی که کفر ورزیده‌اند زنجیرها می‌نهیم. آیا جز به آنچه می‌کردند سزا داده می‌شوند؟ این زنجیرها که در عذابشان داشته همان اعمال آنان است.'),
(3640, 34, 34, 'و ما در هیچ آبادیی پیامبری که مردم را از عذاب بیم دهد نفرستادیم جز این که مرفهان آن به پیامبران گفتند: ما به آنچه بدان رسالت یافته و به ابلاغ آن مأمور شده‌اید، کافریم.'),
(3641, 34, 35, 'و نیز گفتند: ما دارایی‌ها و فرزندانمان بیش از دیگران است و این نشانه کرامت ما نزد خداست، پس ما هرگز گرفتار عذاب او نخواهیم شد.'),
(3642, 34, 36, 'در پاسخ آنان بگو: پروردگار من روزی را برای هر کس که بخواهد وسعت می‌بخشد و بر هر کس که بخواهد تنگ می‌گیرد. این نه گواه کرامت و سعادت است و نه نشانه پستی و اهانت، ولی بیشتر مردم نمی‌دانند که فراوانی نعمت و تنگی معیشت به دست خداوند است.'),
(3643, 34, 37, 'ای مردم، دارایی‌های شما و فرزندانتان چیزی نیست که شما را نزد ما مقرّب سازد; تنها کسانی که ایمان آورده و با انفاق اموالشان در راه خدا و گسترش ایمان و عمل صالح در میان فرزندان خویش، کار شایسته کرده باشند، اینانند که در برابر آنچه کرده‌اند، پاداشی مضاعف خواهند داشت و در غرفه‌های رفیع بهشت از هر گزند و عذابی ایمنند.'),
(3644, 34, 38, 'کسانی که برای از بین بردن آیات ما تلاش می‌کنند تا ما را [به خیال خود] سخت درمانده سازند، در عذاب احضار می‌شوند; هرچند اموال و فرزندانشان بسیار باشد.'),
(3645, 34, 39, 'بگو: پروردگار من روزی را برای هر کس از بندگانش که بخواهد وسعت می‌بخشد و بر هر کس که بخواهد تنگ می‌گیرد; پس نه انفاق موجب کم شدن روزی است و نه خودداری از انفاق مایه فزونی آن. و هر چه را در راه خیر انفاق کنید ـ خواه کم باشد یا زیاد ـ خدا آن را در دنیا یا آخرت عوض می‌دهد و او بهترین روزی دهندگان است که از سرِ جود می‌بخشد نه به امید عوض.'),
(3646, 34, 40, 'یاد کن روزی را که خداوند همه آنان (فرشتگانی که پرستش می‌شدند و پرستشگران آنان) را گرد می‌آورد، سپس به فرشتگان می‌گوید: آیا از این که اینان شما را می‌پرستیدند، خرسند بودید.'),
(3647, 34, 41, 'فرشتگان می‌گویند: خداوندا، ما تو را از هر کاستی و از این که تو را شریکی باشد، منزّه می‌شمریم; تو ولیّ ما هستی نه آنان; ما هیچ گاه از این که آنان ما را عبادت می‌کردند، خشنود نبوده‌ایم. آنان جنیان را می‌پرستیدند و جنیان به این پرستش شادمان بودند. آنان بیشترشان به جنیان ایمان داشتند.'),
(3648, 34, 42, 'پس امروز برای شما آشکار شد که هیچ کدام برای دیگری نه سودی را در اختیار دارید تا آن را فراهم آورید و نه مالک زیانی هستید تا آن را دفع کنید; و امروز به کسانی که با شرک ورزیدن خود ستم روا داشتند، می‌گوییم: عذاب آتش را بچشید; همان آتشی که پیوسته آن را دروغ می‌شمردید.'),
(3649, 34, 43, 'و هنگامی که آیات روشن و تردیدناپذیر ما بر آنان تلاوت شود، سران شرک به پیروان خود می‌گویند: محمّد پیامبر نیست، او مردی است که می‌خواهد شما را از آنچه پدرانتان از دیرباز می‌پرستیدند بازدارد. و نیز می‌گویند: این چیزهایی که آنها را کلام خدا می‌نامد، جز سخنی دروغ که بر خدا بافته شده چیزی نیست. و کسانی که کافر شدند، درباره این قرآنِ سراسر حق ـ آن گاه که به ایشان رسید ـ گفتند: این جز افسونی آشکار نیست.'),
(3650, 34, 44, 'این مشرکان، حقِ آشکار را به پیروی از هوای خویش افسون می‌خوانند و بر ادّعای خود برهانی ندارند، زیرا ما به آنان کتاب‌هایی آسمانی نداده‌ایم که آنها را فراگیرند و بتوانند حق را از باطل تشخیص دهند; و پیش از تو پیامبری که بیمشان دهد به سویشان نفرستاده‌ایم تا حقایق را بیان کند و آنان به استناد تعالیم او درست را از نادرست بازشناسند.'),
(3651, 34, 45, 'کسانی هم که پیش از این مشرکان بودند آیات ما را دروغ شمردند، در حالی که اینان به یک دهم امکاناتی که به آنان داده بودیم نرسیده‌اند. آنان رسولان ما را تکذیب کردند و گرفتار عذاب شدند; پس [بنگرند که] عقوبت من چگونه بود; آن گاه دریابند که کیفر دادن اینان به راستی آسان است.'),
(3652, 34, 46, 'به مشرکان بگو: من شما را فقط یک پند می‌دهم و بدان توصیه می‌کنم، و آن این که به خاطر خدا، دو تا دو تا، یا یکی یکی به پا خیزید، آن گاه درباره پیامبر بیندیشید. هیچ نوع جنونی دامنگیر او نیست. شما او را خوب می‌شناسید; سال‌ها همراه و همنشین شما بوده است. او جز پیامبری نیست که پیش از فرارسیدن عذابی سخت، شما را از آن بیم می‌دهد.'),
(3653, 34, 47, 'بگو: هر پاداشی که در برابر رسالت خود از شما خواسته‌ام مال خودتان باشد. من مزدی از شما نخواسته‌ام و نمی‌خواهم. پاداش من جز برعهده خدا نیست. او بر هر چیزی شاهد و ناظر است و کار مرا نیز خود نظاره می‌کند.'),
(3654, 34, 48, 'بگو: همانا پروردگارم حق را می‌افکند و به وسیله آن باطل را نابود می‌کند. او به همه نهانها بسی داناست.'),
(3655, 34, 49, 'بگو: با نزول قرآن، حق به صحنه آمد و بعد از این دیگر باطل نه چیز جدیدی به میدان می‌آورد و نه چیزی را که گذشته است احیا می‌کند.'),
(3656, 34, 50, 'بگو: اگر گمراه شوم، جز این نیست که خود به بیراهه رفته‌ام و آن بر عهده خودم است; و اگر هدایت یابم، در اثر آن حقایقی است که پروردگارم به من وحی می‌کند، چراکه او شنونده دعاهاست و به بندگانش نزدیک است.'),
(3657, 34, 51, 'ای کاش می‌دیدی زمانی را که مشرکان قریش و هم مسلکانشان در آستانه مرگ در ترس و وحشت قرار می‌گیرند و نمی‌توانند از سیطره خدا دور بمانند، و از مکانی نزدیک گرفته می‌شوند، چرا که میان آنان و خدا فاصله‌ای نیست.'),
(3658, 34, 52, 'آنان در آخرت خواهند گفت: به قرآن ایمان آوردیم. اما چگونه از جهان آخرت که از سرای عمل دور است، دست یافتن به ایمانی کارساز برای آنان میسر است؟!'),
(3659, 34, 53, 'در آن سرا، ایمانشان به قرآن بی اثر است، چرا که قبلاً بدان کافر شدند و از دنیا، آن سرای دور از آخرت، جهان دیگر را که از دیدشان نهان بود، هدف تیرهای انکار که برخاسته از گمان بود قرار دادند.'),
(3660, 34, 54, 'با مرگ، میان آنان و لذّت‌هایی که به آنها رغبت داشتند فاصله افتاد; همان گونه که پیش‌تر با امثال آنان از امت‌های گذشته نیز چنین رفتار شد، چرا که آنان نیز درباره آخرت سخت در تردید بودند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'ستایش از آنِ خداوند است که پدیدآورنده آسمان‌ها و زمین است و فرشتگان را رسولانی قرار داده است تا در عالم تکوین و تشریع اجراکننده فرمانش باشند; برخی از آنها دو بال و برخی سه بال و برخی چهار بال دارند. او هر چه بخواهد در آفرینش می‌افزاید; همانا خداوند بر هر چیزی تواناست.'),
(3662, 35, 2, 'هر نعمتی را که خدا بر مردم بگشاید که همه برخاسته از رحمت اوست، هیچ بازدارنده‌ای برای آن نخواهد بود و آنچه را بازدارد، هیچ بخشنده‌ای بعد از او نیست که آن را گسیل دارد، و اوست شکست ناپذیری که هر چه خواهد همان شود، و حکیمی است که هر چه کند بر طبق حکمت و مصلحت کند.'),
(3663, 35, 3, 'ای مردم، نعمت‌های خدا را که به شما ارزانی داشته شده است به خاطر آورید; آیا آفریدگاری جز خدا از آسمان و زمین به شما روزی می‌دهد؟ پس معبودی سزاوار پرستش جز او نیست. شما تا کی از حقّ بازداشته می‌شوید و به شرک روی می‌آورید؟'),
(3664, 35, 4, 'و اگر مشرکان پس از شنیدن دلایل روشن، تو را تکذیب کردند و پیامت را دروغ شمردند، اندوهگین مشو، زیرا چیز تازه‌ای رخ نداده است، و پیش از تو نیز رسولانی [بسیار]، تکذیب شدند. آری غمگین مباش، چرا که همه کارها [از جمله کار آنان] به خدا باز گردانده می‌شود و او به سزای ستیزشان با حق، آنان را کیفر می‌دهد.'),
(3665, 35, 5, 'ای مردم، وعده خدا قطعاً تحقق خواهد یافت و رستاخیز برپا خواهد شد; پس مبادا زندگی دنیا با زیورها و لذت هایش شما را بفریبد و روز حساب را از یادتان ببرد و شیطان آن موجود حیله گر با نشان دادن جلوه‌های عفو و رحمت خدا، شما را اغفال کند!'),
(3666, 35, 6, 'چرا که شیطان دشمن شماست، پس شما نیز او را دشمن خود بدانید و از پیروی او بپرهیزید. جز این نیست که او گروه خویش را فرا می‌خواند تا سرانجام اهل آتش فروزان دوزخ شوند.'),
(3667, 35, 7, 'کسانی که کافر شده‌اند، آنان را به فراخور کفرشان عذابی سخت است، و کسانی که ایمان آورده و کارهای شایسته کرده‌اند آنان را نیز به فراخور ایمان و عملشان آمرزش و پاداشی بزرگ خواهد بود.'),
(3668, 35, 8, 'پس آیا آن کس که عمل ناپسندش برای او آراسته شده و آن را زیبا می‌بیند همچون کسی است که کردار ناپسند را بد می‌داند؟! هرگز این دو یکسان نیستند. خدا هر که را بخواهد گمراه می‌سازد، و هر که را بخواهد هدایت می‌کند. اکنون که دریافتی امر هدایت و ضلالت به دست خداست، مبادا به خاطر اندوه‌ها و حسرت‌هایی که بر کفرورزی آنان می‌خوری جانت تباه شود. خداوند به آنچه می‌کنند داناست. اوست که می‌داند چه کسی با کرداری که دارد شایسته هدایت است و چه کسی سزاوار ضلالت.'),
(3669, 35, 9, 'و خداوند آن کسی است که بادها را فرستاد و آنها ابرهایی را به حرکت آوردند و منتشر ساختند آن گاه ابرها را به سوی سرزمینی مرده راندیم و بدین وسیله زمین را پس از آن که مرده بود، با رویش گیاهان و درختان زنده ساختیم. زنده ساختن انسان‌ها و برانگیختن آنان در روز قیامت نیز همین گونه است.'),
(3670, 35, 10, 'هر کس عزت می‌طلبد باید آن را از خدا و در بندگیِ خالصانه او بجوید، چرا که عزت به تمامی از آنِ خداست. سخنان پاک که حاوی باورها و اعتقادات درست است به سوی خدا بالا می‌رود و به قرب الهی می‌رسد، و کردار شایسته آنها را صعود می‌دهد; اما کسانی که مکرهای ناپسند می‌کنند تا عزّتی به چنگ آورند برای آنان عذابی سخت خواهد بود و مکر آنان است که تباه و بی اثر می‌شود.'),
(3671, 35, 11, 'و خداست که شما را از خاکی و سپس از نطفه‌ای آفرید، آن گاه شما را جفت ـ گروهی را مرد و گروهی را زن ـ قرار داد; و هیچ مادینه‌ای حمل برنمی گیرد و وضع حمل نمی‌کند مگر به علم خدا، و به هیچ سالمندی عمر دراز داده نمی‌شود و از عمر هیچ کس کاسته نمی‌گردد مگر این که هم مدّت عمر او و هم سبب دراز یا کوتاه شدن آن در کتابی ثبت است. به یقین این تدبیر دقیق که حاکم بر جهان است بر خدا آسان است.'),
(3672, 35, 12, 'دو دریا برابر نیستند; این یکی شیرین است و تشنگی را فرومی نشاند و نوشیدنش گواراست و این یکی ناگوار و سخت شور است، ولی از هر دو، گوشتی تازه می‌خورید و از آنها زیوری که آن را می‌پوشید بیرون می‌آورید; و کشتی‌ها را در دریا می‌بینی که آبها را می‌شکافند و به پیش می‌روند، تا شما روزی خود را که فضل خداست بجویید، و شاید شما بر این نعمت‌ها شکرگزاری کنید.'),
(3673, 35, 13, 'خداوند پیوسته شب را در روز فرو می‌برد و با کاستن از روز بر شب می‌افزاید و همواره روز را در شب فرو می‌برد و با کاستن از شب بر روز می‌افزاید، و خورشید و ماه را رام ساخته و نظام آنها را تدبیر کرده است. هر یک تا سرآمدی معین روانند. ای انسان‌ها، این است خداوند، پروردگار شما که امورتان را تدبیر می‌کند. پادشاهی تنها از آنِ اوست، امّا کسانی که شما آنها را به جای او می‌خوانید مالک پوستِ هسته خرمایی هم نیستند.'),
(3674, 35, 14, 'آنها را بخوانید تا باور کنید که مالک چیزی نیستند. اگر آنها را بخوانید، دعا و درخواست شما را نمی‌شنوند، و اگر بشنوند شما را اجابت نمی‌کنند، و روز قیامت شرک شما را انکار می‌کنند و از شما تبری می‌جویند; و ای پیامبر، هیچ خبردهنده‌ای چون خداوندِ آگاه، تو را از حقیقت امر آگاه نمی‌کند.'),
(3675, 35, 15, 'ای مردم، شمایید که به خدا نیازمندید، و خداست که بی نیاز است و کارهایش همه پسندیده و زیباست.'),
(3676, 35, 16, 'اگر بخواهد، شما را هلاک می‌سازد و از میان می‌برد، زیرا به شما نیازی ندارد، و آن گاه آفریدگانی جدید می‌آورد.'),
(3677, 35, 17, 'و این کار بر خداوند دشوار نیست.'),
(3678, 35, 18, 'و هیچ کس بار گناه دیگری را بر دوش نمی‌گیرد. پس اگر گرانباری که بار گناهش بر او سنگینی می‌کند، دیگری را به سوی بار خویش فراخوانَد تا بار گناهش را او برگیرد پاسخی نمی‌شنود و چیزی از آن برداشته نمی‌شود، هرچند خویشاوندش را فراخوانده باشد. این تکذیب کنندگان رسالت [که خدا هدایتشان را نخواسته است]، از انذارها و هشدارهای تو سودی نمی‌برند. آنان چنانند که گویا اصلاً هشدارشان نداده‌ای. تو فقط کسانی را هشدار می‌دهی و هشدار تو برایشان مفید است که در پی آن از خدا می‌هراسند و از عذاب و کیفر او که از دیدشان نهان است می‌ترسند و نماز را به پا می‌دارند. اینان پاک و پاکیزه‌اند، و هر که پاک شود جز این نیست که به سود خود پاک می‌شود و نفع آن به خدا نمی‌رسد، چرا که بازگشت همگان [پاکان و ناپاکان] به سوی خداست.'),
(3679, 35, 19, 'کافران کورند و مؤمنان بینا، و کور و بینا برابر نیستند.'),
(3680, 35, 20, 'کافران در تاریکی‌ها هستند و مؤمنان در روشنایی، و هرگز تاریکی‌ها و نور یکسان نیستند.'),
(3681, 35, 21, 'مؤمنان سرانجام در سایه دلپذیر بهشت درمی آیند و کافران در حرارتِ سوزان دوزخ، و سایه و حرارتِ سوزان همسان نیستند.'),
(3682, 35, 22, 'مؤمنان زنده دلانند و کافران دل مردگان، و هیچ گاه زندگان و مردگان برابر نیستند. خداست که حق را به هر که بخواهد می‌شنواند و او را هدایت می‌کند، و تو ای پیامبر، نمی‌توانی به کسانی که چون مردگان در قبرها مدفونند حقیقت را بشنوانی و آنان را هدایت کنی.'),
(3683, 35, 23, 'تو فقط اخطارکننده‌ای و باید مردم را از عذاب الهی بترسانی.'),
(3684, 35, 24, 'همانا ما تو را به حق فرستادیم تا مؤمنان را مژده رسان و کافران را هشداردهنده باشی. و این پدیده‌ای نو و بی سابقه نیست; هیچ امتی نبوده مگر این که پیامبری بیم دهنده در میان آنان بوده و درگذشته است.'),
(3685, 35, 25, 'اگر مشرکان تو را تکذیب می‌کنند اندوهگین مباش، کسانی هم که پیش از اینان بودند پیامبران را تکذیب کردند، در حالی که رسولانشان برای آنان دلایل و معجزات روشن آوردند و نوشته‌هایی که مشتمل بر ذکر خدا و بیان کننده حکمت و موعظه بود و نیز کتاب آسمانیِ روشنگر که شرایع الهی را دربرداشت، به آنان ارائه کردند.'),
(3686, 35, 26, 'آن گاه کسانی را که کافر شدند به عذاب گرفتم; پس [بنگرند که] عقوبت من چگونه بود.'),
(3687, 35, 27, 'آیا ندیده‌ای که خدا از آسمان آبی نازل کرد، و به وسیله آن میوه‌هایی رنگارنگ برآوردیم؟ و آیا ندیده‌ای که برخی کوه‌ها رشته‌هایی راه مانند با رنگ‌های مختلف; سفید، سرخ و سیاهِ پررنگِ مشکی هستند؟'),
(3688, 35, 28, 'و [آیا ندیده‌ای که برخی] از مردمان و جانوران و دام‌ها بسان میوه جات و رشته کوه‌ها دارای رنگ‌های گوناگونند; برخی سفید و برخی سرخ و برخی سیاه رنگ اند؟ از میان بندگان خدا تنها عالمانند که از خدا می‌هراسند و در برابر او خشوع میورزند، زیرا [آنان دریافته‌اند که] خدا قاهری است شکست ناپذیر که باید از او بیم داشت، و همو بسیار آمرزنده است و باید مشتاق لقای او بود.'),
(3689, 35, 29, 'همانا کسانی که کتاب خدا، قرآن را تلاوت می‌کنند و نماز را با مداومت بر آن، برپا داشته‌اند و از آنچه روزی آنان کرده‌ایم، گاه در نهان برای دور ماندن از ریا و گاه آشکارا برای ترغیب دیگران، انفاق کرده‌اند، اینان به تجارتی امید بسته‌اند که هرگز کسادی ندارد و تباه نمی‌شود.'),
(3690, 35, 30, 'آنچه کرده‌اند بدین منظور است که خدا پاداششان را به تمام و کمال به آنان عطا کند و از فضل خود به آنان افزون دهد. قطعاً خدا لغزش هایشان را می‌بخشد; چرا که او بسیار آمرزنده است، و پاداش دستاوردشان را به آنان عطا می‌کند و از فضل خود بر آن می‌افزاید که او بسیار سپاسگزار است.'),
(3691, 35, 31, 'آنچه از این کتاب آسمانی به تو وحی کرده‌ایم همه حق است و کتاب‌های آسمانی پیش از خود را تأیید می‌کند. قطعاً خدا به بندگانش آگاه و بیناست.'),
(3692, 35, 32, 'سپس این کتاب آسمانی را به کسانی از بندگانمان که آنان را برگزیدیم سپردیم تا امر آن را بر عهده گیرند و از آن بهره برند; پس گروهی از آنان با ارتکاب گناهان بر خود ستم کردند، و گروهی از آنان راه میانه را در پیش گرفتند و در راه هدایت گام نهادند، و گروهی از آنان در انجام کارهای نیک به اذن خدا بر دیگران پیشی گرفتند و به درجات قرب الهی نایل آمدند. [بدانید] این امر [سپردن قرآن به بندگان برگزیده] تفضّلی بزرگ از جانب خداوند بر آنان است.'),
(3693, 35, 33, '[نتیجه آن] بهشت‌های همیشگی است که وارثان قرآن بدان درمی آیند و در آن جا به دستواره‌هایی از طلا و به مروارید درشت آراسته می‌شوند و جامه شان در آن جا ابریشم خالص است.'),
(3694, 35, 34, 'گنهکارانشان می‌گویند: سپاس خدای را که با جای دادن ما در بهشت، رنج و اندوه عالم برزخ را از ما زدود، همانا پروردگارمان بسیار آمرزنده و سپاسگزار است.'),
(3695, 35, 35, 'همان که ما را به فضل خود، بی آن که درخور آن باشیم، به سرای جاودان درآورد; سرایی که هیچ رنجی در آن به ما نمی‌رسد و برای دستیابی به خواسته هایمان هیچ گونه ملال و خستگی به ما راه نمی‌یابد و هرچه می‌طلبیم در اختیار ماست.'),
(3696, 35, 36, 'و کسانی که کافر شدند، آتش دوزخ برای آنان خواهد بود. حکم مرگشان صادر نمی‌شود تا بمیرند، و عذاب آتش نیز از آنان کاسته نمی‌شود. ما هر کفران پیشه‌ای را این گونه سزا می‌دهیم.'),
(3697, 35, 37, 'آنان در آتش دوزخ برای کمک خواهی فریاد برمی آورند و می‌گویند: پروردگارا، ما را از این جا به در آور، که اگر چنین کنی کار شایسته خواهیم کرد، غیر از آنچه پیش‌تر می‌کردیم که ناروا بود. در پاسخ آنان گفته می‌شود: هرگز چنین نخواهد شد; مگر به شما عمری ندادیم که هر کس عبرت آموز بود در آن عبرت می‌گرفت؟ و پیامبری بیم دهنده به سوی شما آمد و شما را از عذاب هشدار داد، ولی شما عبرت نگرفتید و ایمان نیاوردید; پس این عذاب را بچشید که هیچ یاوری برای ستمگران نیست تا آنان را از عذاب رهایی بخشد.'),
(3698, 35, 38, 'همانا خداوند به آنچه در آسمان‌ها و زمین نهان است داناست. به یقین او به اندیشه‌ها و نیّت‌های نهفته در سینه‌ها آگاه است.'),
(3699, 35, 39, 'اوست آن که شما را در زمین جانشین قرار داد و هر نسلی از شما آدمیان را جایگزین نسل پیشین کرد. پس تنها او خداوندگار شماست; و هر کس به یکتایی او در ربوبیت و تدبیر کافر گردد، کفرش تنها به زیان خود اوست، و کافران را کفرشان جز دشمنی شدید نزد پروردگارشان نمی‌افزاید، و کافران را کفرشان جز خسارتی بزرگ افزون نمی‌کند.'),
(3700, 35, 40, 'به مشرکان بگو: از معبودانتان که شریک خدا قرار داده‌اید و آنها را به جای خدا می‌خوانید و می‌پرستید به من خبر دهید. به من بنمایید که آنها چه چیزی از زمین و پدیده‌های آن را آفریده‌اند تا در زمین شریک خدا باشند، بلکه چه چیزی از آسمان‌ها را خلق کرده‌اند تا در آسمان‌ها شرکتی با خدا داشته باشند و در نتیجه از رُبوبیّت و تدبیر عالم بهره‌ای نصیب آنها شود؟ آیا کتابی به مشرکان داده‌ایم که شرکت معبودانشان با خدا و شایستگی آنها برای پرستش، در آن نوشته شده باشد تا از جانب خدا برای شرک ورزیدن خود، بر دلیلی روشن متّکی باشند؟ چنین نیست، بلکه شرک ورزیدن آنان ریشه در فریبکاری دارد و این مشرکان ستمکار چیزی جز فریب به یکدیگر وعده نمی‌دهند.'),
(3701, 35, 41, 'به یقین خداست که آسمان‌ها و زمین را نگاه می‌دارد و وجودشان را ادامه می‌دهد تا از میان نروند; و به یقین که اگر آن دو از میان بروند هیچ کس بعد از او نمی‌تواند آنها را نگهدارد و هستی آنها را برقرار دارد. همانا او بردبار است و در نابود ساختن آسمان‌ها و زمین تعجیل نمی‌کند، و ساتر است و راه نیستی را بر آنها پوشیده می‌دارد و بدین سبب آسمان‌ها و زمین تا مدتی معین برپا می‌مانند.'),
(3702, 35, 42, 'این مشرکان پیش از بعثت تو، با سخت‌ترین سوگندهای خود به نام خدا سوگند خوردند که اگر پیامبری بیم دهنده برای آنان بیاید، و در داشتن پیامبر، با یهودیان و مسیحیان یکسان شوند، قطعاً از هر یک از آن امّت‌ها هدایت یافته‌تر خواهند شد; ولی هنگامی که پیامبری برایشان آمد و آنان را به عذاب الهی هشدار داد، جز دوری و گریز بر آنان نیفزود.'),
(3703, 35, 43, 'برتری جویی آنان در روی زمین و نیرنگِ زشتشان ریشه در گریز آنان از پیامبر و تکذیب آیات الهی دارد. [بدانند که] نیرنگِ ناروا جز دامن صاحبش را نمی‌گیرد و اثر سوء آن جز به خود او باز نمی‌گردد. پس آیا جز همان سنّتی را که در میان امّت‌های حق ستیز نخستین جاری شد و آنان را هلاک ساخت، انتظار می‌برند؟ هرگز برای سنت خدا دگرگونی نمی‌یابی و هرگز سنّت خدا را قابل انتقال نخواهی یافت.'),
(3704, 35, 44, 'آیا این حق گریزان در زمین سیر نکرده‌اند تا سنت الهی را به دیده عبرت دریابند و بدانند که فرجام امت‌هایی که پیش از ایشان می‌زیستند و بسیار نیرومندتر از اینان بودند چگونه شد؟! [بدانند که] هیچ چیزی نه در آسمان‌ها و نه در زمین، خدا را ناتوان نمی‌سازد; نه مَکرِ مکّاران او را عاجز می‌کند و نه نیروی قدرتمندان; چرا که او دانای مطلق است و نیرنگ‌ها غافلگیرش نمی‌کند، و توانای مطلق است و هیچ چیزی در برابر او مقاومت ندارد.'),
(3705, 35, 45, 'و مقدر نیست که همه گنهکاران در دنیا کیفر شوند; اگر خدا همه مردم را به سزای گناهانی که انجام داده‌اند مؤاخذه می‌کرد، بر روی زمین هیچ جنبنده‌ای باقی نمی‌گذاشت ولی کیفر آنان را تا سرآمدی معین به تأخیر می‌اندازد و به آنان مهلت می‌دهد، و وقتی اجلشان فرا رسید، آنان را به سزای گناهانشان مؤاخذه می‌کند، چرا که خدا به بندگان خود بینا و به رفتارشان داناست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یا، سین.'),
(3707, 36, 2, 'سوگند به قرآنِ دربردارنده حکمت،'),
(3708, 36, 3, 'که تو یقیناً از جمله کسانی هستی که به رسالت فرستاده شده‌اند.'),
(3709, 36, 4, 'به قرآن سوگند که تو بر راهی راست قرار داری; راهی که به خدا منتهی می‌شود و تو را به نیکبختی می‌رساند.'),
(3710, 36, 5, 'قرآن از جانب خدای شکست ناپذیر رحمت گستر، نازل شده است; پس او بر منکران آن پیروز و بر باوردارندگان آن رحمت آورنده است.'),
(3711, 36, 6, 'تو را به رسالت برانگیخت و قرآن را فرو فرستاد تا مردمی را که پدرانشان به وسیله هیچ پیامبری بیم داده نشدند و از این رو در غفلت و بی خبری به سر می‌بردند، از عذاب الهی بیم دهی.'),
(3712, 36, 7, 'قطعاً آن سخن (وعده عذاب دوزخ) درباره بیشترشان تحقق یافته است; از این رو این مشرکان ایمان نخواهند آورد;'),
(3713, 36, 8, 'چرا که بر گردن هایشان غُل‌هایی نهاده‌ایم و دست هایشان را با آن غل‌ها بر گردن هایشان بسته‌ایم. آن غل‌ها تا چانه هایشان کشیده شده و تمام گردنشان را پوشانده و سرهایشان رو به بالا قرار گرفته است، بدین سبب پیش روی خود را نمی‌بینند و راه راست را نمی‌یابند.'),
(3714, 36, 9, 'و ما از پیش روی آنان حجابی و از پشت سرشان حجابی قرار داده‌ایم، به گونه‌ای که اطرافشان را پوشانده‌ایم، ازاین رو نمی‌بینند و هدایت نمی‌شوند.'),
(3715, 36, 10, 'آری، بر آنان یکسان است، چه بیمشان بدهی یا بیمشان ندهی ایمان نمی‌آورند.'),
(3716, 36, 11, 'اینان که غل‌ها بر گردن دارند و در حجابند، از هشدارهای تو بهره ای نمی‌برند و چنانند که گویی هشدارشان نداده‌ای. تو فقط کسی را هشدار می‌دهی و هشدار تو برای او سودمند است که قرآن را تصدیق کند و به سمت و سوی آن گراید و از خدای رحمان که برای او در حجاب است، بهراسد و در عین حال به رحمتش امیدوار باشد. چنین کسی را به آمرزش گناهان و بهشت که پاداشی ارجمند است نوید ده.'),
(3717, 36, 12, 'ما خود، مردگان را برای کیفر و پاداش زنده می‌کنیم و کارهای نیک و بدی را که در دوران زندگی پیش فرستاده‌اند و نیز سنت‌های شایسته و ناشایستی را که از خود در میان مردم به جای گذاشته‌اند، همه را در کتابی ثبت می‌کنیم، و ما هر چیزی را در لوح محفوظ که پیشوای خلایق است و مقدّرات حتمی آنها را به روشنی دربردارد، برشمرده و ثبت کرده‌ایم.'),
(3718, 36, 13, 'داستان مردم انطاکیه روشنگر بشارت‌ها و هشدارهای ماست. ای پیامبر، اهالی آن آبادی را آن گاه که فرستادگان ما بدان جا آمدند، برای مردم مَثَل بزن;'),
(3719, 36, 14, 'آن گاه که دو تن از رسولان خود را به سوی آنان فرستادیم [و آنان مردم را به پرستش خدای یکتا و باور داشتن روز جزا فراخواندند]، ولی بی درنگ آن دو را تکذیب کردند; ما نیز با گسیل داشتن سوّمین فرستاده خود، آن دو تن را نیرومند ساختیم، پس هر سه نفر به مردم آن دیار گفتند: به یقین ما از جانب خدا به سوی شما به رسالت فرستاده شده‌ایم.'),
(3720, 36, 15, 'مردم در پاسخ گفتند: شما جز بشری مانند ما نیستید و خداوند رحمان هیچ وحیی بر انسان فرو نفرستاده است، چرا که بشر شایسته نبوت و دریافت وحی نیست; ما نیز بشریم و هرگز از جانب خدا وحیی به ما نرسیده است، پس شما جز دروغ نمی‌گویید.'),
(3721, 36, 16, 'رسولان گفتند: بی تردید ما به سوی شما به رسالت فرستاده شده‌ایم و همین بس که پروردگارمان این را می‌داند; لذا نه از تکذیب شما باکی داریم و نه نیازمند تصدیقتان هستیم.'),
(3722, 36, 17, 'و بر ما جز ابلاغ آشکار پیام الهی، تکلیف دیگری نیست.'),
(3723, 36, 18, 'مردم آن دیار گفتند: ما [امر] شما را به فال بد گرفته‌ایم; به یقین اگر از این کار دست برندارید سنگسارتان می‌کنیم و عذابی دردناک از ما به شما خواهد رسید.'),
(3724, 36, 19, 'فرستادگان ما گفتند: شومی شما با خود شماست. آن حق گریزی و باطل گرایی که در درونتان ریشه دارد، مایه شومی شماست. آیا اگر حق را برای شما یادآوری کنند آن را انکار می‌کنید و آن را به فال بد می‌گیرید و تهدید می‌کنید؟! شما مردمی هستید که بی اندازه مرتکب گناه شده‌اید، و همین موجب حق ناپذیری شما شده است.'),
(3725, 36, 20, 'در این میان مردی از دورترین نقطه شهر شتابان آمد و گفت: ای قومِ من، از این رسولان پیروی کنید.'),
(3726, 36, 21, 'از کسانی که برای رسالت خود، مزدی از شما نمی‌طلبند و خود هدایت یافته‌اند پیروی کنید و دعوتشان را بپذیرید.'),
(3727, 36, 22, 'و بدانید که آفریننده آدمیان، تنها معبودی است که شایسته پرستش است; پس چیست مرا که نپرستم آن کسی را که مرا پدید آورده و شما همه به سوی او بازگردانده می‌شوید و به سزای کارهایتان می‌رسید؟!'),
(3728, 36, 23, 'آیا جز خدا معبودانی برگزینم که اگر خدای رحمان زیانی برای من بخواهد، شفاعت آنها چیزی از آن زیان را از من دور نمی‌سازد و [خود نیز] نمی‌توانند مرا نجات بخشند؟!'),
(3729, 36, 24, 'اگر جز خدا معبودانی را پرستش کنم، در آن صورت قطعاً در گمراهی آشکاری خواهم بود.'),
(3730, 36, 25, 'ای فرستادگان خدا، همانا من به پروردگارتان ایمان آورده‌ام; پس گواهی مرا بر یگانگی خدا بشنوید و گواه باشید.'),
(3731, 36, 26, 'ولی مردم انطاکیه آن مرد مؤمن با اخلاص را به قتل رساندند، و بی درنگ از جانب خدا به او ندا رسید که: به بهشت در آی. آن مرد در آن سرای گفت: ای کاش، قوم من می‌دانستند،'),
(3732, 36, 27, 'که پروردگارم گناهانم را بخشید و مرا از بزرگواران درگاهش قرار داد.'),
(3733, 36, 28, 'و پس از شهادت آن مرد هیچ لشکری از آسمان بر قوم او نفرستادیم تا از آنان انتقام گیرند، و پیش‌تر نیز برای نابود کردن تکذیب کنندگان لشکری فرو نفرستادیم.'),
(3734, 36, 29, 'وسیله نابودی آن قوم، تنها یک صیحه بود، که ناگهان همگی خاموش شدند.'),
(3735, 36, 30, 'دریغا بر این بندگان، که هیچ پیامبری برایشان نیامد مگر این که پیوسته او را مسخره می‌کردند.'),
(3736, 36, 31, 'آیا درنیافته‌اند که چه بسیار نسل‌ها را پیش از آنان هلاک کردیم؟ آیا ندانسته‌اند اکنون گرفتار کیفر الهی اند و نمی‌توانند به سوی اینان بازگردند؟'),
(3737, 36, 32, 'و جز این نیست که همه بندگان در پیشگاه ما گردآوری می‌شوند و در روز قیامت برای حساب و جزا احضار می‌گردند.'),
(3738, 36, 33, 'و همین زمینِ مرده برای آنان آیت و نشانه‌ای بر رُبوبیّت خداست که آن را با رویش گیاهان زنده کردیم و از آن دانه‌ای بر آوردیم که از آن می‌خورند.'),
(3739, 36, 34, 'و بوستان‌هایی از درختان خرما و انگور در آن قرار دادیم و چشمه‌ها در آن برشکافتیم;'),
(3740, 36, 35, 'تا از میوه آن بوستان‌ها که خدا پدید آورده و دست هایشان آن را به عمل نیاورده است بخورند; پس چرا خدا را با اقرار به ربوبیت او و اظهار بندگی در برابرش، سپاس نمی‌گویند؟!'),
(3741, 36, 36, 'منزّه است آن که همه جفت‌ها را آفرید; از گیاهان و حیواناتی که زمین آنها را می‌رویاند و پدید می‌آورد، و از آدمیان و آفریدگانی که بدانها آگاهی ندارند; او همه را به صورت نر و ماده آفرید.'),
(3742, 36, 37, 'و برای آدمیان نشانه‌ای دیگر بر ربوبیت و یکتایی خدا در تدبیر امر آسمان‌ها همین شب است که روز را از آن به در می‌آوریم و ناگهان آنان دوباره در تاریکی فرو می‌روند.'),
(3743, 36, 38, 'و خورشید آیت و نشانه‌ای دیگر است که پیوسته روان است تا آن گاه که با فرا رسیدن اجلش به استقرار و سکونی که برای آن تعیین شده است برسد. این، اندازه گیری خداوندی است که هیچ کس و هیچ چیز بر او فایق نمی‌آید و به همه مصلحت‌ها داناست.'),
(3744, 36, 39, 'و برای حرکت ماه منزل‌هایی بیست و هشتگانه را به اندازه معین کردیم. ماه این منزل‌ها را می‌پیماید تا به جایگاه نخستِ خود باز گردد و بسان چوب خوشه نخل که دیری از آن گذشته و منحنی شده است [به صورت هلال] دیده شود.'),
(3745, 36, 40, 'نه خورشید را می‌سزد که ماه را دریابد و نه شب بر روز پیشی گیرنده است; هر یک از اینها و دیگر ستارگان چون ماهیان دریا در مداری شناورند.'),
(3746, 36, 41, 'و برای آنان نشانه‌ای دیگر از ربوبیت ما این است که فرزندانشان را در کشتیِ انباشته از بار و مسافر حمل کرده و آنان را از غرق شدن حفظ نموده‌ایم.'),
(3747, 36, 42, 'و برای آنان چیزهای دیگری که مانند کشتی بر آن سوار شوند آفریده‌ایم.'),
(3748, 36, 43, 'تدبیر کشتی و سرنشینان آن با ماست; اگر بخواهیم آنان را در دریا فرو می‌بریم، آن گاه نه فریادرسی خواهند داشت و نه از غرق شدن نجات داده می‌شوند.'),
(3749, 36, 44, 'آری، هیچ وسیله‌ای برای نجاتشان نیست، مگر رحمتی از جانب ما به آنان برسد و زنده بمانند و تا فرارسیدن سرآمدی که برایشان معین کرده‌ایم، از نعمت‌های دنیا بهره ببرند.'),
(3750, 36, 45, 'و هنگامی که به مشرکان گفته می‌شود: این آیات و نشانه‌ها همه بیانگر این است که خدا پروردگار شماست، بنابراین از این شرک و بت پرستی که پیش رو دارید، و از آن عذاب و کیفری که پشت سر دارید بپرهیزید، باشد که مورد رحمت الهی قرار گیرید، رویگردان می‌شوند.'),
(3751, 36, 46, 'و هیچ نشانه‌ای از نشانه‌های پروردگارشان برایشان نمی‌آید مگر این که از آن رویگردان‌اند.'),
(3752, 36, 47, 'و هنگامی که به آنان گفته می‌شود: از آنچه خدا به شما روزی داده است به مستمندان انفاق کنید و با آنان مهربان باشید آنان که کافر شده‌اند، از روی مغالطه، به کسانی که ایمان آورده اند می‌گویند: آیا به کسی خوراک دهیم که اگر خدا می‌خواست او را اطعام می‌کرد و بی نیازش می‌ساخت؟! شما که مردم را به دستگیری از مستمندان فرا می‌خوانید جز در گمراهیِ آشکاری نیستید.'),
(3753, 36, 48, 'و به مسخره، از پیامبر و مؤمنان می‌پرسند: اگر راست می‌گویید که روز قیامتی هست و خدا همگان را در آن گرد می‌آورد، این وعده کی خواهد بود؟'),
(3754, 36, 49, 'برپایی قیامت و گردآوری آنان در آن صحنه بر خدا آسان است. آنان جز یک بانگ را که با دمیدن در صور ایجاد می‌شود، انتظار نمی‌کشند. آن بانگ به ناگاه آنان را در حالی که با یکدیگر کشمکش می‌کنند، فرو می‌گیرد و در دم هلاکشان می‌سازد.'),
(3755, 36, 50, 'آن گاه نه می‌توانند به دیگران وصیّتی کنند و نه فرصت می‌یابند به سوی کسان خود بازگردند.'),
(3756, 36, 51, 'و بار دیگر در صور دمیده می‌شود و به ناگاه آنان از گورهایشان برخاسته به سوی پروردگارشان می‌شتابند.‌'),
(3757, 36, 52, 'می‌گویند: ای وای بر ما که هلاک گشتیم! چه کسی ما را از خوابگاهمان برانگیخت؟ و سخن پیامبران را به یاد می‌آورند و می‌گویند: این رستاخیز همان است که خداوند رحمان وعده داده بود و فرستادگان خدا راست گفتند.'),
(3758, 36, 53, 'آن دمیدن در صور جز یک بانگ نیست که در پی آن به ناگاه آنان گردآوری می‌شوند و برای داوری نزد ما احضار می‌گردند.'),
(3759, 36, 54, 'پس امروز به هیچ کس اندک ستمی نمی‌شود، زیرا جز آنچه انجام می‌دادید، سزا داده نمی‌شوید.'),
(3760, 36, 55, 'همانا بهشتیان امروز سرگرم کاروبار خویشند و گفتگوهایی خوش دارند.'),
(3761, 36, 56, 'آنان و همسرانشان، در زیر سایه‌ها بر بالش‌ها تکیه زده‌اند.'),
(3762, 36, 57, 'برای آنان در آن جا میوه هاست و هر چه آرزو کنند و بخواهند، برایشان خواهد بود.'),
(3763, 36, 58, 'سلام بر آنان باد. این سلام از جانب پروردگاری رحمت آفرین به آنان گفته می‌شود.'),
(3764, 36, 59, 'آن روز به کفرپیشگان می‌گوییم: ای گنهکاران، امروز از بهشتیان جدا شوید.'),
(3765, 36, 60, 'ای پسران آدم، آیا به شما سفارش نکردم که با پیروی از شیطان او را پرستش نکنید، زیرا او دشمن آشکار شماست؟!'),
(3766, 36, 61, 'و آیا سفارش نکردم که مرا بپرستید و از من اطاعت کنید که این، راهی است راست و شما را به نیکبختی می‌رساند؟!'),
(3767, 36, 62, 'و به راستی شیطان گروه انبوهی از شما آدمیان را به گمراهی کشانید، پس چرا خردورزی نمی‌کردید و درنمی یافتید؟'),
(3768, 36, 63, 'دوزخ را در برابرشان حاضر می‌سازیم و می‌گوییم: این است آن دوزخی که همواره بدان تهدید می‌شدید.'),
(3769, 36, 64, 'امروز به سزای این که کفر میورزیدید در آن ماندگار باشید.'),
(3770, 36, 65, 'آن روز بر دهان هایشان مهر خموشی می‌زنیم و اعضایشان را به سخن درمی آوریم; دست هایشان با ما سخن می‌گویند و از گناهانی که به وسیله آنها انجام شده است به ما خبر می‌دهند، و پاهایشان نیز به گناهانی که فراهم آورده‌اند، گواهی می‌دهند.'),
(3771, 36, 66, 'و اگر می‌خواستیم، چشمانشان را محو و بی فروغ می‌کردیم; آن گاه می‌خواستند به سوی راه درست که آنان را به مقصد برساند، بر دیگران سبقت گیرند ولی با این حال چگونه می‌دیدند؟'),
(3772, 36, 67, 'و اگر می‌خواستیم، آنان را بر سر جایشان مسخ می‌کردیم و شکلشان را زشت و قبیح می‌ساختیم، آن گاه نه می‌توانستند در آن عذاب حرکتی کنند و نه به حالت نخستِ خویش بازگردند.'),
(3773, 36, 68, 'و [شما می‌بینید که] به هر کس عمر دراز دهیم، او را در آفرینش واژگونه می‌کنیم و نیرویش را به ناتوانی و دانشش را به جهل و هوشیاریش را به فراموشی مبدّل می‌سازیم; پس چرا خرد را به کار نمی‌گیرند و درنمی یابند که ما می‌توانیم چشمانشان را نابینا سازیم و آنان را مسخ کنیم؟!'),
(3774, 36, 69, 'ما به پیامبر شعر گفتن نیاموختیم و شایسته او هم نیست. قرآن شعر نیست، بلکه مایه تذکر است و کتابی است خوانده شده از جانب خدا و این حقیقتی روشن است.'),
(3775, 36, 70, 'ما قرآن را بر او نازل کردیم تا به کسانی که زنده‌اند و حق را درمی یابند هشدار دهد و آن سخن ما که گفته‌ایم دوزخ را از شیطان و همه پیروانش پر می‌کنیم، درباره کافران محقق شود.'),
(3776, 36, 71, 'آیا انسان‌ها برای پی بردن به یکتایی خدا و ربوبیت او در جهان هستی نیندیشیده و ندانسته‌اند که ما دام‌هایی را که دست هایمان آنها را ساخته و در خلقتشان شریکی نداشته‌ایم برای آنان آفریده و در اختیارشان نهاده‌ایم و اینک آنان مالک آنها هستند؟!'),
(3777, 36, 72, 'و دام‌ها را مسخّر آنان کردیم، پس از آنهاست وسیله سواری آنان و از گوشت آنها می‌خورند.'),
(3778, 36, 73, 'و برای آنان در آن دام‌ها سودها و نوشیدنی هاست; پس چرا به سپاسِ این نعمت‌ها خدا را پرستش نمی‌کنند؟'),
(3779, 36, 74, 'دلایل توحید آشکار است، ولی مشرکان به جای خدا معبودانی چون بتان، شیاطین و سران ستمگر را به خداوندگاری برگزیده و به پرستش آنها پرداخته‌اند، به این امید که از سوی آنها یاری شوند.'),
(3780, 36, 75, 'آنها نمی‌توانند پرستشگران خود را یاری کنند و مشرکان سپاه معبودان خود و دنباله رو آنهایند و در قیامت همراه معبودانشان برای کیفر احضار خواهند شد.'),
(3781, 36, 76, 'با روشن شدن این حقیقت که مشرکان دل به یاری ناتوانان بسته‌اند و در قیامت کیفر خواهند دید، نباید گفتار کفرآلودشان تو را غمگین کند. آنان نمی‌توانند بر ما غلبه کنند، زیرا ما آنچه را که از گفته‌ها پنهان می‌دارند و آنچه را آشکار می‌سازند می‌دانیم.'),
(3782, 36, 77, 'آیا انسان به روشنی ندانسته است که ما او را از نطفه‌ای ناچیز آفریدیم پس ناگهان او ستیزه گری شده است که آشکارا با ما به کشمکش می‌پردازد؟!'),
(3783, 36, 78, 'او از آن روی که آفرینش خود را فراموش کرده بود، استخوان‌های پوسیده را برای ما مَثَل آورد، تا ناممکن بودن رستاخیز را به اثبات رساند. گفت: چه کسی این استخوان‌ها را در حالی که پوسیده‌اند، حیاتی دوباره خواهد بخشید؟!'),
(3784, 36, 79, 'ای پیامبر، در پاسخ او بگو: همان توانمندی که نخستین بار آن استخوان‌ها را پدید آورد، آنها را زنده می‌کند. او به هر آفریده‌ای بسی داناست. ویژگی‌های همه خلایق را چه پیش از مرگشان و چه پس از مرگشان می‌داند، پس زنده کردن مردگان برای او سهل است.'),
(3785, 36, 80, 'همان توانمندی که برای شما از درخت سبز (مَرْخ و عَفار) که تروتازه است آتشی پدیدار کرد که شما بی درنگ از آن، آتش می‌افروزید.'),
(3786, 36, 81, 'آیا آن کسی که آسمان‌ها و زمین را آفریده است توانا نیست که با آفرینش دوباره انسان‌ها همانندشان را بیافریند؟! چرا، تواناست و اوست آفریننده‌ای که همواره می‌آفریند و به همه خلایق داناست.'),
(3787, 36, 82, 'وقتی ایجاد چیزی را بخواهد، کارش جز این نیست که به آن می‌گوید: باش. پس آن موجود می‌شود.'),
(3788, 36, 83, 'پس، از ناتوانی منزّه بدان آن کسی را که هستی هر چیزی به دست اوست و همگان به سوی او بازگردانده می‌شوید.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'سوگند به آن فرشتگان صف کشیده، که در مسیر وحی به صف درآمده‌اند،'),
(3790, 37, 2, 'پس به آن فرشتگان بازدارنده، که شیاطین را از تصرف در وحی بازمی دارند،'),
(3791, 37, 3, 'پس به آن فرشتگان تلاوت کننده که قرآن، آن یادآور الهی را بر پیامبر می‌خوانند،'),
(3792, 37, 4, 'به تمامی اینها سوگند که معبود همه شما قطعاً یکی است.'),
(3793, 37, 5, 'چرا که اوست پروردگار آسمان‌ها و زمین و آنچه میان آن دو است، و اوست پروردگار همه خاورها.'),
(3794, 37, 6, 'هر آینه ما نزدیک‌ترین آسمان را، به این ستارگانِ زینت بخش آراستیم.'),
(3795, 37, 7, 'و آن را از نفوذ هر جن شرور و پلیدی نگاه داشتیم.'),
(3796, 37, 8, 'آنان از گوش فرا دادن به مجمع فرشتگانِ بزرگوار که ساکنان آسمان‌های بالا هستند و از خبرهای غیبی جهان گفتوگو دارند، بازداشته شده‌اند، و از هر سو به آنان تیر افکنده می‌شود،'),
(3797, 37, 9, 'تا طرد شوند و به عالم فرشتگان راه نیابند، و برای آنان عذابی است پیوسته و دائم.'),
(3798, 37, 10, 'مگر کسی که دزدانه گوش فرادهد و خبری برباید، که شهابی شکافنده که هدف را خطا نمی‌کند، در پی او خواهد افتاد.'),
(3799, 37, 11, 'حال از مشرکان نظر بخواه که آیا آنان در خلقت استوارترند یا آسمان‌ها و زمین و فرشتگانی که پدید آورده‌ایم؟ ما انسان‌ها را از گِلی چسبنده آفریده‌ایم; از این رو آنها ضعیف ترند و هرگز بر ما چیره نخواهند شد.'),
(3800, 37, 12, 'بلکه‌ای محمد، از این که پیام به حق تو را دروغ می‌شمرند تعجّب کرده‌ای، و آنان رسالت و دعوت تو را به مسخره می‌گیرند.'),
(3801, 37, 13, 'و چون نشانه‌های توحید و دین حق را به آنان یادآور شوند، آگاهی نمی‌یابند.'),
(3802, 37, 14, 'این مشرکان وقتی معجزه‌ای (چون قرآن و شکافته شدن ماه را) ببینند، آن را به ریشخند می‌گیرند.'),
(3803, 37, 15, 'و می‌گویند: این چیزی که محمد آن را معجزه می‌خواند، جز افسونی آشکار نیست.‌'),
(3804, 37, 16, 'می‌گویند: آیا چون مُردیم و خاک گشتیم و استخوان‌هایی [پوسیده] شدیم، آیا به راستی برانگیخته می‌شویم؟!'),
(3805, 37, 17, 'و آیا پدرانِ نخستین ما که نابود شده‌اند برانگیخته می‌شوند؟!'),
(3806, 37, 18, 'ای پیامبر، بگو: آری، شما و پدرانتان زنده خواهید شد، در حالی که خوار و ذلیل هستید.'),
(3807, 37, 19, 'چرا که آن حیاتِ دوباره تنها [در پی] یک بانگ است که آنان را از گورها به صحنه قیامت می‌آورد و ناگهان مبهوت و متحیّر می‌نگرند.'),
(3808, 37, 20, 'و می‌گویند: ای وای بر ما که هلاک شدیم. این، روزِ جزاست.'),
(3809, 37, 21, 'آری، این است روز جدایی، که با داوری خداوند حق از باطل و مجرمان از مؤمنان جدا می‌شوند; همان روزی که پیوسته آن را دروغ می‌شمردید.'),
(3810, 37, 22, 'پس به فرشتگان می‌گوییم: کسانی را که ستم کرده‌اند، به همراه شیاطینی که دمسازشان بودند از جایگاهشان به در آورید و نیز آنچه را عبادت می‌کردند،'),
(3811, 37, 23, 'همان معبودانی که به جای خدا می‌پرستیدند، سپس آنها را یکجا به راه دوزخ برانید.'),
(3812, 37, 24, 'و بر صراط دوزخ نگاهشان دارید، که آنان مورد بازخواست قرار می‌گیرند.'),
(3813, 37, 25, 'از آنان می‌پرسند: از چه رو در برابر حق گردنکشی می‌کردید، و اینک شما را چه شده است که در ستیز با حق، یکدیگر را یاری نمی‌کنید؟'),
(3814, 37, 26, 'نه، بلکه، آنان امروز تسلیم‌اند.'),
(3815, 37, 27, 'برخی از آنان روی به برخی دیگر می‌آورند و پرخاشگرانه از یکدیگر پرس و جو می‌کنند.'),
(3816, 37, 28, 'پیروان به سران خود می‌گویند: شما بودید که فریبکارانه از درِ راستی و خیرخواهی بر ما وارد می‌شدید و با این شیوه ما را از راه به در می‌کردید و ما را به این سرنوشت شوم افکندید.'),
(3817, 37, 29, 'سران کفر می‌گویند: ما شما را از ایمان باز نداشتیم تا مسئول فرجام هلاکت بار شما باشیم، بلکه شما اصلاً ایمان نداشتید.'),
(3818, 37, 30, 'ما بر شما هیچ گونه تسلطی نداشتیم تا شما را از ایمان به سوی کفر سوق دهیم، بلکه خودتان مردمی سرکش بودید; چنان که ما نیز گردنکشی می‌کردیم.'),
(3819, 37, 31, 'پس سخن خداوند بر همه ما تثبیت شد که ما قطعاً چشندگان عذاب دوزخیم.'),
(3820, 37, 32, 'در نتیجه ما شما را به بیراهه افکندیم، چرا که ما خود گمراه بودیم و گمراهان جز گمراهی به بار نمی‌آورند.'),
(3821, 37, 33, 'پس در آن روز، پیشوایان شرک و کفر و پیروانشان، همه در عذاب دوزخ شریک یکدیگرند.'),
(3822, 37, 34, 'به یقین، ما با همه مشرکان گناه پیشه همین گونه رفتار خواهیم کرد.'),
(3823, 37, 35, 'زیرا هنگامی که به آنان گفته می‌شد: معبودی شایسته پرستش جز خدا نیست، بر گردنکشی خود اصرار میورزیدند.'),
(3824, 37, 36, 'و می‌گفتند: آیا به راستی ما برای شاعری مجنون معبودانمان را رها سازیم؟!'),
(3825, 37, 37, 'نه! محمد شاعر و مجنون نیست; او قرآن را که سراسر حق است آورده و پیامبران پیشین را تصدیق کرده است.'),
(3826, 37, 38, 'قطعاً شما به سزای گردنکشی و حق ناباوری خود عذاب دردناک دوزخ را خواهید چشید.'),
(3827, 37, 39, 'و جز آنچه انجام می‌دادید سزا داده نمی‌شوید.'),
(3828, 37, 40, 'لیکن آنان که بندگی خدا کرده و برای او خالص شدند پس به غیر او دل نبستند، در آن جهان علاوه بر پاداش کردارشان پاداشی دیگر خواهند یافت.'),
(3829, 37, 41, 'برای آنان در بهشت رزقی معلوم (ویژه و ممتاز) است.'),
(3830, 37, 42, 'میوه‌هایی [گوناگون]، در حالی که از کرامتی خاص نیز برخوردارند.'),
(3831, 37, 43, 'در بهشت‌هایی پرنعمت اقامت دارند.'),
(3832, 37, 44, 'بر تخت‌هایی ویژه رو به روی یکدیگر می‌نشینند.'),
(3833, 37, 45, 'بر گرد آنان جام شرابی از باده زلال می‌گردانند;'),
(3834, 37, 46, 'باده‌ای سفید و درخشان که برای نوشندگانش بسی خوشگوار است.'),
(3835, 37, 47, 'نه زیانی دارد و نه ایشان از آن به مستی می‌افتند.'),
(3836, 37, 48, 'و نزدشان حوریانی است چشم فرو هشته با نگاهی پرناز و کرشمه و چشمانی درشت و زیبا.'),
(3837, 37, 49, 'آنها [به قدری سفید و شفّافند که] گویی تخم پرندگانند که پنهانشان کرده‌اند.'),
(3838, 37, 50, 'بهشتیان، آن گاه که بر سریرها نشسته‌اند، برخی به برخی دیگر روی می‌آورند و از کاروبار یکدیگر پرسوجو می‌کنند.'),
(3839, 37, 51, 'گوینده‌ای از میانشان می‌گوید: همانا مرا در دنیا همنشینی بود;'),
(3840, 37, 52, 'که می‌گفت: آیا تو واقعاً از کسانی هستی که رستاخیز را باور دارند؟!‌'),
(3841, 37, 53, 'می‌گفت: آیا وقتی مردیم و خاک گشتیم و استخوان‌هایی [پوسیده] شدیم، به راستی زنده می‌شویم و سزا می‌بینیم؟!'),
(3842, 37, 54, 'سپس آن مرد بهشتی به یاران خود می‌گوید: آیا شما [هم] به دوزخ سر می‌کشید تا همنشین مرا ببینید و فرجامش را بنگرید؟'),
(3843, 37, 55, 'پس او به آتش سر می‌کشد و همنشینش را در میانه دوزخ می‌بیند.'),
(3844, 37, 56, 'به او می‌گوید: به خدا سوگند، نزدیک بود مرا به دوزخ افکنی و به هلاکتم اندازی.'),
(3845, 37, 57, 'و اگر نعمت هدایت و توفیق پروردگارم نبود، قطعاً من نیز از کسانی بودم که برای عذاب احضار می‌شدم.'),
(3846, 37, 58, 'آن گاه آن مرد بهشتی روی به یاران خود می‌کند و با شادی بسیار می‌گوید: ای یاران، آیا ما دیگر نخواهیم مُرد؟'),
(3847, 37, 59, 'آیا جز همان مرگ نخستینمان، مردنِ دیگری نخواهیم داشت؟ آیا ما گرفتار عذاب نمی‌شویم؟'),
(3848, 37, 60, 'آری، ما جاودانه‌ایم و رنجی نخواهیم دید. به راستی این همان رستگاری و سعادت بزرگ است.'),
(3849, 37, 61, 'پس اهل عمل باید در دنیا برای دستیابی به چنین پاداشی بکوشند.'),
(3850, 37, 62, 'آیا آن روزی نیکوی بهشتیان، برای پذیرایی خوب است یا درخت زقّوم که برای دوزخیان آماده شده است؟'),
(3851, 37, 63, 'به یقین، ما آن درخت را رنج و عذابی برای ستمکاران قرار دادیم.'),
(3852, 37, 64, 'همانا زقّوم، درختی است که در قعر دوزخ می‌روید.'),
(3853, 37, 65, 'خوشه اش از نهایت زشتی، گویی سرهای شیاطین است.'),
(3854, 37, 66, 'آن ستمکارانِ دوزخی سخت گرسنه‌اند و قطعاً از آن خواهند خورد و شکم‌های خود را از آن پر خواهند کرد. (۶'),
(3855, 37, 67, 'سپس برای آنان علاوه بر خوراک زقّوم آب جوشانی است که با آنچه خورده‌اند آمیخته می‌گردد.'),
(3856, 37, 68, 'پس از نوشیدن آب جوشان، قطعاً به سوی دوزخ باز خواهند گشت.'),
(3857, 37, 69, 'این کیفرها از آن روست که آنان پدرانشان را گمراه یافتند،'),
(3858, 37, 70, 'ولی در پیروی از ایشان درنگ نکردند و شتابان در پی آنان رفتند.'),
(3859, 37, 71, 'به یقین پیش از آنها بیشتر پیشینیان نیز گمراه شدند.'),
(3860, 37, 72, 'و به راستی ما در میان مردمانِ نخستین، پیامبرانی فرستادیم تا هشدارشان دهند.'),
(3861, 37, 73, 'پس بنگر فرجام هشدار داده شدگان چگونه شد; همگان به عذاب الهی گرفتار شدند و هلاک گشتند.'),
(3862, 37, 74, 'فقط آنان که بندگی خدا کردند و برای او خالص شدند و دل به غیر او نبستند، نجات یافتند.'),
(3863, 37, 75, 'و همانا نوح ما را ندا داد و در برابر قوم ستم پیشه اش از ما یاری طلبید و ما خواسته او را اجابت کردیم و نیک اجابت کننده‌ایم.'),
(3864, 37, 76, 'ما آنان را به طوفان گرفتار ساختیم و نوح و کسانش [خانواده و مؤمنان قومش] را از آن اندوه بزرگ رهانیدیم.'),
(3865, 37, 77, 'و پس از آنان، تنها نسل او را باقی گذاشتیم و دیگر نسل‌ها را از میان بردیم.'),
(3866, 37, 78, 'و پیامش را در میان امّت‌هایی که پس از او آمدند زنده نگاه داشتیم و تا قیامت نیز زنده نگاه خواهیم داشت.'),
(3867, 37, 79, 'سلام بر نوح باد، سلامی از جانب همه امت‌های بشری تا روز رستاخیز'),
(3868, 37, 80, 'ما همه نیکوکاران را آن گونه که به نوح پاداش دادیم، جزا می‌دهیم.'),
(3869, 37, 81, 'او از بندگان حقیقی ما و از مؤمنان راستین بود.'),
(3870, 37, 82, 'اما دیگران را (= کافران قوم نوح را) به طوفان غرق کردیم.'),
(3871, 37, 83, 'و به راستی ابراهیم از جمله پیروان مکتب نوح بود.'),
(3872, 37, 84, 'آن گاه که با دلی پاک از هرگونه شرک و خوی ناپسند و کدورت گناه به سوی پروردگارش آمد و به او ایمان آورد.'),
(3873, 37, 85, '[و] آن گاه که به پدر و قومش گفت: چه چیزی را می‌پرستید؟'),
(3874, 37, 86, 'آیا جز خدا معبودانی را می‌طلبید که شایستگی آنها برای پرستش دروغ محض است؟'),
(3875, 37, 87, 'با این وصف پندارتان درباره پروردگار جهانیان چیست؟ [گمان می‌کنید با شما چه خواهد کرد؟].'),
(3876, 37, 88, 'روزی مردم شهر برای مراسم عید آماده شدند و ابراهیم را نیز فراخواندند. او نگاهی به ستارگان انداخت،'),
(3877, 37, 89, 'و گفت: من بیمارم و از حضور در این مراسم ناتوان.'),
(3878, 37, 90, 'پس مردم از او روی گرداندند و خود از شهر بیرون شدند.'),
(3879, 37, 91, 'ابراهیم به سوی معبودانشان رفت و با خشم گفت: چرا از این خوراکی‌ها که برای شما تهیّه دیده‌اند، نمی‌خورید؟'),
(3880, 37, 92, 'شما را چه شده است که سخن نمی‌گویید و پاسخ مرا نمی‌دهید؟'),
(3881, 37, 93, 'سپس بر آنها تاخت و با دست راستش بر آنها نواخت.'),
(3882, 37, 94, 'قوم ابراهیم از رخداد بُتکده آگاه شدند و شتابان به سوی او روی آوردند.'),
(3883, 37, 95, 'ابراهیم را گرفتند و در حضور مردم، درباره شکننده بت‌ها از او سؤال کردند. ابراهیم گفت: آیا چیزی را که خود می‌تراشید می‌پرستید؟'),
(3884, 37, 96, 'با این که خدا شما و آنچه را می‌سازید، آفریده است.'),
(3885, 37, 97, 'بت پرستان گفتند: بنایی برای او بسازید [و در آن آتش بیفروزید] سپس او را در آتش فروزان بیفکنید.'),
(3886, 37, 98, 'آری، خواستند برای نابودی ابراهیم چاره‌ای کنند، ولی ما با سرد کردن آتش و تأمین سلامت او، آنان را پست‌ترین مردم قرار دادیم.'),
(3887, 37, 99, 'و او به پدر و قوم خود گفت: همانا من به سوی پروردگارم روانه می‌شوم و به سرزمینی می‌روم که با آسودگی خاطر به عبادت او بپردازم و به درگاهش نیایش کنم; حتماً او مرا راهنمایی خواهد کرد.'),
(3888, 37, 100, 'و در دعایش گفت: پروردگارا، فرزندی از زمره درستکاران به من عطا فرما.'),
(3889, 37, 101, 'پس ما او را به پسری نوجوان و بردبار بشارت دادیم.'),
(3890, 37, 102, 'آن گاه اسماعیل تولد یافت و رشد کرد و هنگامی که به سنّ جوانی رسید و توانست پا به پای پدرش ابراهیم به کار و تلاش روزانه بپردازد، ابراهیم گفت: پسرجانم، من مکرّر در خواب می‌بینم که تو را سر می‌بُرم، پس در این امر بیندیش و ببین نظرت چیست؟ اسماعیل گفت: ای پدر، آنچه را بدان مأمور می‌شوی انجام ده; اگر خدا بخواهد، مرا از صابران خواهی یافت.'),
(3891, 37, 103, 'پس وقتی ابراهیم و فرزندش تسلیم فرمان خدا شدند و ابراهیم او را بر گونه اش به زمین انداخت، منظره‌ای سخت و ناگوار پدیدار شد.'),
(3892, 37, 104, 'و ما او را ندا دادیم که‌ای ابراهیم،'),
(3893, 37, 105, 'همانا آن رؤیا را حقیقت بخشیدی و آنچه را مأمور شدی انجام دادی; ما همان گونه که ابراهیم را آزمودیم و پاداش دادیم، نیکوکاران را می‌آزماییم و پاداش می‌دهیم.'),
(3894, 37, 106, 'به یقین این فرمان، همان آزمونِ روشن بود.'),
(3895, 37, 107, 'و فرزندش اسماعیل را در برابر قربانی بزرگی باز خریدیم.'),
(3896, 37, 108, 'و پیامش را در میان امت‌هایی که پس از او آمدند زنده نگاه داشتیم و تا قیامت نیز زنده نگاه خواهیم داشت.'),
(3897, 37, 109, 'سلام خدا بر ابراهیم!'),
(3898, 37, 110, 'همه نیکوکاران را آن گونه که ابراهیم را پاداش دادیم، پاداش می‌دهیم.'),
(3899, 37, 111, 'او از بندگان واقعی ما و از مؤمنان راستین بود.'),
(3900, 37, 112, 'و او را به ولادت فرزندی دیگر به نام اسحاق که پیامبری از زمره درستکاران بود، بشارت دادیم.'),
(3901, 37, 113, 'و در آنچه به او و اسحاق عطا کردیم، برکت نهادیم، و از نسل آن دو، گروهی نیکوکارند و گروهی با ارتکاب گناهانْ آشکارا بر خود ستم می‌کنند.'),
(3902, 37, 114, 'و به راستی به موسی و هارون نعمتی بزرگ عطا کردیم.'),
(3903, 37, 115, 'و آن دو و قومشان را از آن اندوه بزرگ که فرعونیان برای آنان به وجود آورده بودند، رهایی بخشیدیم.'),
(3904, 37, 116, 'و آنان را یاری کردیم، در نتیجه بر فرعونیان پیروز شدند.'),
(3905, 37, 117, 'و به موسی و هارون آن کتاب را ارزانی داشتیم کتابی که نیازهای مادی و معنوی مردم را برایشان آشکار می‌ساخت و آنها را بیان می‌کرد.'),
(3906, 37, 118, 'و آن دو را به راه راست که ضامن نیکبختی انسان هاست هدایت کردیم.'),
(3907, 37, 119, 'و پیامشان را در میان امت‌هایی که پس از آنان آمدند زنده نگاه داشتیم و تا قیامت نیز زنده نگاه خواهیم داشت.'),
(3908, 37, 120, 'سلام خدا بر موسی و هارون!'),
(3909, 37, 121, 'ما همه نیکوکاران را بدین سان که آن دو را پاداش دادیم، پاداش می‌دهیم. (۱۲۱'),
(3910, 37, 122, 'آنها از بندگان واقعی ما و از مؤمنان راستین بودند.'),
(3911, 37, 123, 'و الیاس نیز از فرستادگان ما بود.'),
(3912, 37, 124, 'یاد کن زمانی را که به قومش گفت: آیا [از عذاب خدا] نمی‌هراسید؟'),
(3913, 37, 125, 'آیا (بَعْل) را می‌خوانید و به پرستش آن می‌پردازید و عبادت بهترین آفرینندگان را وامی گذارید؟'),
(3914, 37, 126, 'عبادت خدا را که پروردگار شما و پروردگار پدران نخستین شماست؟'),
(3915, 37, 127, 'قوم الیاس بی درنگ او را تکذیب کردند، و قطعاً آنان در روز قیامت برای عذاب احضار خواهند شد.'),
(3916, 37, 128, 'لیکن از میان آنان، بندگانِ خالص شده برای خدا که بندگیِ او کردند و دل به غیر او نبستند، در آن روز عذابی نخواهند چشید.'),
(3917, 37, 129, 'ما پیام او را در میان امت‌هایی که پس از او آمدند زنده نگاه داشتیم و تا روز قیامت نیز زنده نگاه خواهیم داشت.'),
(3918, 37, 130, 'سلام خدا بر الیاس!'),
(3919, 37, 131, 'ما همه نیکوکاران را آن گونه که به الیاس پاداش دادیم، پاداش می‌دهیم.'),
(3920, 37, 132, 'او از بندگان واقعی ما و از مؤمنان راستین بود.'),
(3921, 37, 133, 'و همانا لوط از فرستادگان ما بود.'),
(3922, 37, 134, '[یاد کن] وقتی را که او و همه خانواده اش را از عذاب رهایی بخشیدیم،'),
(3923, 37, 135, 'بجز همسر پیرش که در زمره کسانی بود که در عذاب باقی ماندند و بدان هلاک گشتند.'),
(3924, 37, 136, 'سپس دیگران را، همانان که لوط به سویشان فرستاده شد، هلاک کردیم.'),
(3925, 37, 137, '(سرزمین آنان در مسیر حجاز و شام است) و شما مردم حجاز قطعاً بر ویرانه‌های دیارشان صبحگاهان عبور می‌کنید;'),
(3926, 37, 138, 'و نیز شامگاهان بر آن جا می‌گذرید; پس چرا خرد را به کار نمی‌گیرید و درنمی یابید؟'),
(3927, 37, 139, 'و به یقین، یونس از فرستادگان ما بود.'),
(3928, 37, 140, '[یاد کن] زمانی را که او گریخت و به سوی کشتیِ انباشته از سرنشین رفت.'),
(3929, 37, 141, 'کشتی در دریا حرکت کرد و ماهیِ بزرگی راه را بر کشتی بست; پس یونس با دیگر سرنشینان قرعه انداختند، قرعه به نام او درآمد و از شکست خوردگان شد.'),
(3930, 37, 142, 'یونس را بی درنگ به دریا انداختند و آن ماهی او را بلعید در حالی که نکوهیده بود.'),
(3931, 37, 143, 'و اگر از زمره تسبیح گویان خدا نبود،'),
(3932, 37, 144, 'قطعاً تا روزی که مردم از قبرها برانگیخته می‌شوند، در شکم ماهی می‌ماند و از آن جا برانگیخته می‌شد.'),
(3933, 37, 145, 'آری، چون یونس را تسبیح گوی خویش یافتیم، او را در حالی که بیمار بود، از شکم ماهی به زمینی بی گیاه که سایبانی در آن یافت نمی‌شد، بیرون انداختیم.'),
(3934, 37, 146, 'و بر فراز او درختی از نوع کدو رویاندیم تا آن را سایبانِ خود گیرد.'),
(3935, 37, 147, 'و او را به سوی قومش که یکصد هزار نفر بلکه افزون بر آن بودند، به رسالت فرستادیم.'),
(3936, 37, 148, 'پس به او ایمان آوردند، از این رو هلاکشان نساختیم و تا سرآمدی که برایشان مقرّر شده بود، آنان را از نعمت‌های دنیا برخوردار کردیم.'),
(3937, 37, 149, 'مشرکان می‌پندارند فرشتگان دختران خدایند; ای پیامبر، از آنان جویا شو که آیا دختران برای پروردگارت و پسران برای آنان است؟!'),
(3938, 37, 150, 'آیا فرشتگان را دختر آفریده‌ایم و ایشان آفرینش آنها را نظاره گر بودند؟!'),
(3939, 37, 151, 'بدانید که آنان بر اثر دروغ پردازی‌های خود می‌گویند:'),
(3940, 37, 152, 'خدا فرزند آورده است بی تردید آنان دروغگویانند.'),
(3941, 37, 153, 'آیا خدا در گرفتنِ فرزند، دختران را بر پسران برگزیده است؟!'),
(3942, 37, 154, 'شما را چه شده است؟! چگونه بی دلیل حکم می‌کنید؟!'),
(3943, 37, 155, 'آیا درنمی یابید که خداوند از داشتن فرزند منزّه است؟!'),
(3944, 37, 156, 'آیا شما را کتابی آسمانی هست که مدعای شما در آن ثبت شده باشد و شما از برهانی روشن برخوردار باشید؟!'),
(3945, 37, 157, 'اگر راست می‌گویید، کتابتان را بیاورید و آن را ارائه دهید.'),
(3946, 37, 158, 'و مشرکان میان خدا و جنیان نسبت خویشاوندی پنداشتند و آنها را فرزندان خدا شمردند، در حالی که جنّیان به خوبی می‌دانند که خودشان برای حساب و جزا احضار خواهند شد. آیا فرزندان خدا محاکمه می‌شوند؟!'),
(3947, 37, 159, 'منزّه است خدا از آنچه مشرکان او را بدان وصف می‌کنند و داشتن فرزند را به او نسبت می‌دهند.'),
(3948, 37, 160, 'لیکن بندگانِ خالص شده برای خدا، که او را پرستش می‌کنند و دل به غیر او نمی‌بندند، خدا را به اوصافی که درخور اوست می‌ستایند.'),
(3949, 37, 161, 'قطعاً شما مشرکان و آنچه شما آن را می‌پرستید،‌'),
(3950, 37, 162, 'نمی‌توانید هیچ کس را بر ضد خدا گمراه کنید،'),
(3951, 37, 163, 'مگر کسی را که خود از پی آتش دوزخ روانه است.'),
(3952, 37, 164, 'جبریل و سایر فرشتگان سخنشان این است که: هیچ یک از ما نیست مگر این که مقام و منزلتی معین دارد و نمی‌تواند از آن تخطی کند.'),
(3953, 37, 165, 'همانا ما به انتظار فرمان خدا در پیشگاه او صف کشیده‌ایم.'),
(3954, 37, 166, 'و ما تسبیح گویان خداییم و ساحتش را از آنچه شایسته او نیست، منزّه می‌شمریم.'),
(3955, 37, 167, 'به یقین، مشرکان قریش و هم مسلکانشان می‌گفتند:'),
(3956, 37, 168, 'اگر کتابی یادآور، از آن گونه که بر امّت‌های نخستین نازل شد، نزد ما بود،'),
(3957, 37, 169, 'قطعاً ما راه می‌یافتیم و بندگانی می‌شدیم خالص شده برای خدا.'),
(3958, 37, 170, 'دیری نگذشت که ما قرآن را برایشان فرو فرستادیم، ولی آنان به گفته خود وفا نکردند و به آن کافر شدند; پس زود است که وِزر و وبال کفرشان را بدانند.'),
(3959, 37, 171, 'همانا پیشتر سخن و قضای ما در حق آن بندگانی که آنان را به رسالت فرستادیم مقرّر و حتمی شد،'),
(3960, 37, 172, 'که: آنان قطعاً بر دشمنانشان پیروز خواهند شد.'),
(3961, 37, 173, 'و این که: گروه و لشکر ما، آن مؤمنانِ مجاهد، بر مخالفانشان چیره خواهندگردید.'),
(3962, 37, 174, 'پس ای پیامبر، تا زمانی نه چندان طولانی، از مشرکان قوم خود روی بگردان و آنان را مهلت ده.'),
(3963, 37, 175, 'در این مدت آنان را بنگر و انکار و دشمنی شان را نظاره کن; زود است که آنان نیز فرجام انکار و سرکشی خود را ببینند و بنگرند که چگونه به دست شما کشته و اسیر می‌گردند.'),
(3964, 37, 176, 'پس آیا مشرکان خواهان آنند که هرچه زودتر عذاب ما به سراغشان بیاید؟'),
(3965, 37, 177, '[بدانند] آن گاه که عذاب به درگاه خانه هایشان فرود آید و همگان را فراگیرد، بامداد کسانی که اخطار شده‌اند، بامداد بدی است.'),
(3966, 37, 178, 'و تا مدتی از آنان روی بگردان و تا فرارسیدن اجلشان به آنان مهلت ده.'),
(3967, 37, 179, 'و در این مدت، کفر و عصیان مردم را نظاره گر باش; دیری نمی‌گذرد که آنان فرجام شوم کفر و گناه خویش را که همان عذاب دوزخ است بنگرند.'),
(3968, 37, 180, 'منزّه است پروردگار تو ـ پروردگار عزتمند ـ از آنچه مشرکان او را بدان وصف می‌کنند.'),
(3969, 37, 181, 'و سلام خدا بر همه فرستادگان.'),
(3970, 37, 182, 'و سپاس خدای را که پروردگار جهانیان است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'ص، سوگند به قرآن، این کتابِ یادآور توحید و معارف الهی، که تو از پیامبران و هشداردهندگانی.'),
(3972, 38, 2, '[پافشاری کافران بر کفر خود، نشانه کوتاهی قرآن در بیان معارف نیست،] بلکه آنان که کافر شده‌اند، در حالت امتناع از پذیرش قرآن و مخالفت با آن قرار دارند.'),
(3973, 38, 3, 'چه بسیار نسل‌ها را که پیش از اینان بودند و پیامبرانشان را تکذیب کردند، هلاک ساختیم; پس خدا را به فریادرسی خواندند، ولی زمان، زمانِ تأخیر عذاب نبود.'),
(3974, 38, 4, 'و از این که پیامبری هشداردهنده از جنس خودشان برایشان آمده است در شگفت‌اند، و کافران گفتند: این مرد افسونگری دروغ پرداز است که آنچه را خود بافته است کلام خدا می‌نامد.'),
(3975, 38, 5, 'آیا محمّد این معبودان فراوان را یک معبود قرار داده، و پنداشته است که آنها شایسته پرستش نیستند و تنها خدا شایسته عبادت است؟! راستی این چیزی بسیار شگفت آور است.'),
(3976, 38, 6, 'مهتران قریش چون دریافتند که پیامبر از انکار خدایانشان دست برنمی دارد و به سازش با آنان گرایشی ندارد، مجلس او را ترک کردند و به یکدیگر گفتند: باید به راه خود بروید و بر پرستش معبودانتان ایستادگی کنید; قطعاً محمّد می‌خواهد بدین وسیله بر مردم ریاست کند; چیزی که هر بشری در پی آن است.'),
(3977, 38, 7, 'ما در آخرین آیین که مردم این عصر آن را پذیرفته‌اند سخن از توحید نشنیده‌ایم; این از افسانه‌های گذشتگان است و پسندیده این دوران نیست; این چیزی جز دروغبافی نیست.'),
(3978, 38, 8, 'آیا از میان ما تنها بر محمّد قرآن نازل شده است؟! او چه امتیازی بر ما دارد که شایسته وحی باشد؟ آنان این سخن را از روی اعتقاد نگفتند، بلکه در قرآن که یادآور من است شک و تردید دارند، ولی ریشه انکار آنها شک و تردید نیست، چرا که اگر بیندیشند درمی یابند که قرآن معجزه الهی است، بلکه آنان به سبب خودبزرگ بینی آن را نمی‌پذیرند گرچه به الهی بودن آن اطمینان یابند. آنان هنوز عذاب مرا نچشیده‌اند; وقتی که عذاب به سراغشان آمد، به حقّانیت قرآن اعتراف خواهند کرد.‌'),
(3979, 38, 9, 'می‌گویند: بر پیامبر وحی نشده است. مگر گنجینه‌های رحمت پروردگارت نزد آنان است که آن را از تو بازدارند؟ چنین نیست، زیرا خداوند آن مقتدری است که هیچ کس نمی‌تواند در کار او دخالت کند و آن بخشایشگری است که هیچ کس نمی‌تواند رحمت او را بازگرداند.'),
(3980, 38, 10, 'یا فرمانروایی آسمان‌ها و زمین و آنچه میان آن دو است، در اختیار آنان است؟ اگر چنین است پس بر نردبان آسمان‌ها برآیند و از فرود آمدن وحی بر تو جلوگیری کنند.'),
(3981, 38, 11, 'این کافران که از جمله گروه‌های تشکّل یافته بر ضدّ پیامبرانند، لشکری ناچیز و ناتوانند و در آن جا (در آن دوردست ها)، بی یاور و شکست خورده‌اند.'),
(3982, 38, 12, 'پیش از اینان، قوم نوح و عاد و فرعونِ صاحب میخ‌ها نیز پیامبران را تکذیب کردند.'),
(3983, 38, 13, 'و ثمودیان و قوم لوط و قوم شعیب، آن جنگل نشینان، نیز پیام رسولان را دروغ شمردند. اینان همان گروه‌های تشکّل یافته بر ضدّ پیامبران بودند.'),
(3984, 38, 14, 'هیچ یک از آنان نبودند مگر این که پیامبران را تکذیب کردند، پس کیفر من بر آنان تثبیت شد.'),
(3985, 38, 15, 'و اینان هم که پیام تو را دروغ می‌شمرند، جز یک خروش آسمانی را انتظار نمی‌برند که در آن هیچ درنگی نیست و بازگشتی نخواهد داشت.'),
(3986, 38, 16, 'و کافران از روی تمسخر گفتند: پروردگارا، پیش از آن که روز حساب فرا رسد و مردم گرفتار عذاب شوند، بهره ما را از عذاب هر چه زودتر به ما برسان.'),
(3987, 38, 17, 'ای پیامبر، از برخوردهای ناهنجار آنان سستی به خود راه مده و بر آنچه می‌گویند شکیبا باش و بدین منظور از بنده ما داود که در تسبیح خدا و در دانش و فرمانروایی و نبرد با کافران، توانا بود، یاد کن; آری او همواره به ما رجوع می‌کرد.'),
(3988, 38, 18, 'ما کوه‌ها را رام ساختیم که او را همراهی کنند، آنها شامگاهان و بامدادان هم آوا با داود، خدا را تسبیح می‌گفتند.'),
(3989, 38, 19, 'و پرندگان را نیز گرد آمده در کنار او، رام ساختیم تا هم آوا با او تسبیح گویند. آری، کوه‌ها و پرندگان، همه تسبیح خدا می‌گفتند و با تسبیح خود همواره به خدا رجوع می‌کردند.'),
(3990, 38, 20, 'و فرمانروایی او را استوار ساختیم و به او حکمت و توانِ فصل خصومت دادیم.'),
(3991, 38, 21, 'آیا خبرِ آن ستیزه گران، آن گاه که از دیوارِ بلند خانه داود بالا رفتند، به تو رسیده است؟'),
(3992, 38, 22, 'وقتی که بر داود وارد شدند و او از آنان بیمناک شد. گفتند: نترس، ما دو گروهیم که با یکدیگر نزاع داریم; برخی از ما بر برخی دیگر ستم کرده‌اند; میان ما به حق داوری کن و ستم روا مدار و ما را به راه عدل راه نمای.'),
(3993, 38, 23, 'یکی از آنان گفت: این برادر من است که نَوَد و نُه میش دارد و من یک میش دارم; او گفته است که باید آن را به من واگذاری، و در گفتار بر من چیره شده است.'),
(3994, 38, 24, 'داود گفت: به یقین برادرت با درخواست میش تو برای افزودن آن به میش‌های خود بر تو ستم کرده است، و بسیاری از شریکان که اموال خود را به هم می‌آمیزند به یکدیگر ستم می‌کنند، مگر کسانی که ایمان آورده و کارهای شایسته کرده‌اند و اینان هم بسیار اندکند. [داود این گونه داوری کرد،] ولی دریافت که ما او را بدین وسیله آزموده‌ایم و او به سبب شتاب در قضاوت دچار لغزش شده است، پس از پروردگارش آمرزش خواست و به رکوع درآمد و به درگاه خدا توبه کرد.'),
(3995, 38, 25, 'پس ما آن لغزش را بر او بخشیدیم، و قطعاً او را نزد ما مرتبه‌ای بلند است و بازگشتی نیکو خواهد داشت.'),
(3996, 38, 26, 'آری، او را بخشیدیم و به وی گفتیم: ای داود، ما تو را در زمین جانشین خود قرار دادیم; باید خُلق و خوی تو همچون خُلق و خوی کسی باشد که تو جانشین او هستی. پس در میان مردم به حق داوری کن و در قضاوت پیرو هوای نفس مباش که تو را از راه خدا منحرف می‌کند. قطعاً کسانی که از راه خدا منحرف می‌شوند، قیامت را که روز حسابرسی است فراموش کرده‌اند و برای آنان به سزای از یاد بردن روز حساب، عذابی سخت خواهد بود.'),
(3997, 38, 27, 'ما آسمان و زمین و آنچه را که میان آن دو است بیهوده نیافریده‌ایم; این پندار کسانی است که کافر شده‌اند، پس وای بر کافران از آتش دوزخ!‌'),
(3998, 38, 28, 'می‌گویند: حساب و کتابی در کار نیست. مگر ما آفرینش را بر عدل و حکمت استوار نکرده‌ایم؟! با این وصف آیا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند مانند کسانی قرار می‌دهیم که با باورهای نادرست و کارهای ناروا در زمین تبهکاری می‌کنند؟! آیا تقواپیشگان را مانند بزهکاران قرار می‌دهیم؟!'),
(3999, 38, 29, 'این قرآن کتابی گرانقدر و پر خیر و برکت است که آن را به سوی تو فرستادیم تا همگان در آیات آن بیندیشند، پس هدایت پذیران راهیاب شوند و عنادپیشگان عذری بر گمراهی خویش نیابند و تا صاحب خردان به دلایل حق توجه کنند و به سوی آن هدایت گردند.'),
(4000, 38, 30, 'و سلیمان را به داود عطا کردیم. سلیمان بنده‌ای نیکو بود; او همواره به ما رجوع می‌کرد.'),
(4001, 38, 31, 'آن گاه که عصرگاهان اسب‌های تندرو و آماده برای جهاد، بر او عرضه شد.'),
(4002, 38, 32, 'پس گفت: من شیفته اسب‌ها شدم و علاقه به آنها مرا از خواندن نماز و یاد کردن پروردگارم باز داشت تا این که خورشید در پرده افق پنهان گشت و نماز از من فوت شد;'),
(4003, 38, 33, 'بار دیگر آنها را نزد من بازآورید. اسب‌ها را برای بار دوم بر او عرضه کردند، پس شروع کرد که بر ساق و گردن آنها دستی کشد.'),
(4004, 38, 34, 'و همانا سلیمان را آزمودیم و کالبد بی جان فرزندش را بر تخت او افکندیم; سپس او از این که به فرزندش امید بسته بود به درگاه ما توبه کرد.'),
(4005, 38, 35, 'گفت: پروردگارا، خطایم را بر من ببخشای و سلطنتی به من ارزانی دار که پس از من شایسته هیچ کس نباشد; همانا تویی که بسیار بخشنده‌ای.'),
(4006, 38, 36, 'پس باد را مسخّر وی ساختیم که به فرمان سلیمان به هر جایی که او می‌خواست روان می‌شد و به آسانی او را فرمانبری می‌کرد.'),
(4007, 38, 37, 'و شیطان‌ها، آن جنّیانِ شرور را برای او رام ساختیم; هر بنّایی از آنها را که به کار سازندگی بپردازد و هر غوّاصی را که از دریا برای او مروارید و مرجان استخراج کند.'),
(4008, 38, 38, 'و شیطان‌های دیگری از جنّیان را که در غل‌ها و زنجیرها به یکدیگر بسته شده بودند نیز برای او رام ساختیم.'),
(4009, 38, 39, 'ای سلیمان، این سلطنت با این همه نعمت، موهبت ما به توست که از فزونی قابل شمارش نیست و با بخشش به دیگران پایان نمی‌پذیرد; پس آن را ببخش یا نگهدار، که آن تمام شدنی نیست.'),
(4010, 38, 40, 'و قطعاً او را در پیشگاه ما مرتبه‌ای بلند است و بازگشتی نیکو خواهد داشت.'),
(4011, 38, 41, 'ای پیامبر، بنده ما ایّوب را یاد کن آن گاه که پروردگارش را ندا داد که: شیطان مرا رنج داده و به من گزند رسانده است.'),
(4012, 38, 42, 'پس دعایش را اجابت کردیم و چشمه آبی به او نمایاندیم و گفتیم: به سرعت با پاهای خود حرکت کن و آنها را در حال حرکت، استوار بر زمین بگذار; این چشمه جای شستوشو است; سرد است و نوشیدنی; به آن درآی و از آن بنوش. ایّوب چنین کرد و بیماری از او رخت بر بست.'),
(4013, 38, 43, 'و خانواده اش را که از دست داده بود به او بخشیدیم و آنها را به دو برابر رساندیم، تا از جانب ما رحمتی بر او و تذکّری برای خردمندان باشد.'),
(4014, 38, 44, 'و به او گفتیم: سوگند خود را مشکن و یک دسته شاخه گیاه، به تعداد تازیانه‌هایی که سوگند یاد کردی به همسرت بزنی، در دست گیر و آنها را یکجا بر او بزن. به راستی ما ایّوب را بر آن دردها و رنج‌هایی که بر او وارد ساختیم، شکیبا یافتیم; او خوب بنده‌ای بود; چرا که همواره به ما رجوع می‌کرد.'),
(4015, 38, 45, 'و نیز بندگان ما ابراهیم و اسحاق و یعقوب را یاد کن; آنان در اطاعت از خدا و رساندن خیر به مردمان نیرومند و در راهیابی به باورهای درست و کارهای شایسته تیزبین بودند;'),
(4016, 38, 46, 'زیرا ما آنان را به سبب برخورداری از خصلتی پاک که یاد سرای آخرت بود، برای خود خالص ساخته بودیم، به گونه‌ای که نه شیطان از آنان نصیبی داشت و نه هوای نفس.'),
(4017, 38, 47, 'آنان نزد ما از برگزیدگان به شمار می‌رفتند و از نیکان بودند.'),
(4018, 38, 48, 'و از دیگر پیامبران ما، اسماعیل و یَسع و ذوالکِفل را یاد کن و [بدان که] همه آنان از بهترین انسان‌ها بودند.'),
(4019, 38, 49, 'این قرآن یادآور توحید و معارف الهی است و قطعاً تقواپیشگان در جهان دیگر بازگشتی نیکو خواهند داشت.'),
(4020, 38, 50, 'برای آنان بهشت‌هایی است همیشگی که درهای نعمت آن برایشان گشوده است.'),
(4021, 38, 51, 'در آن جا عزّتمندانه [بر تخت ها] تکیه می‌زنند و میوه‌هایی را که در آن جا بسیار است و نیز نوشیدنی را به سوی خود فرا می‌خوانند.'),
(4022, 38, 52, 'در کنارشان همسرانی هستند که فقط چشم به شوهران خود دارند و دیده از غیر آنان فرو بسته‌اند و همسال و همسان شوهران خویش‌اند.'),
(4023, 38, 53, 'این بهشت همان چیزی است که برای روز حساب به شما تقواپیشگان وعده داده می‌شود.'),
(4024, 38, 54, 'آری، این است روزیِ ما به شما که هرگز آن را فنایی نیست.'),
(4025, 38, 55, 'این است فرجام تقواپیشگان و قطعاً سرکشان در آن سرا، بد بازگشتگاهی خواهند داشت.'),
(4026, 38, 56, 'بازگشتگاه آنان دوزخ است که به آن درمی آیند و از حرارتش به رنج می‌افتند; بد بستری برای دوزخیان آماده شده است.'),
(4027, 38, 57, 'این آبی است جوشان و چرکین و بدبو، که باید آن را بچشند و چاره‌ای جز این ندارند.'),
(4028, 38, 58, 'و این هم چیز دیگری است از جنس همان آبِ داغ و مایع چرکین با انواع گوناگون آن، که آنها را نیز باید بچشند.'),
(4029, 38, 59, 'به سران شرک و کفر گفته می‌شود: این گروهی که از شما پیروی کردند، به اجبار در میان شما درمی آیند و در دوزخ با شما خواهند بود. سران می‌گویند: خوشامد مبادشان، چرا که به آتش درآمدگان و از حرارت آن به رنج افتادگانند.'),
(4030, 38, 60, 'پیروان در پاسخ می‌گویند: بلکه شما را خوشامد مباد! شما بودید که این را برای ما پیش آوردید، و شوم است ماندن در آتش.'),
(4031, 38, 61, 'آن گاه می‌گویند: پروردگارا، هر کس این عذاب را برای ما پیش آورده است او را در آتش، عذابی دو چندان بیفزای.'),
(4032, 38, 62, 'و دوزخیان مؤمنان را در آتش نمی‌یابند، از این رو می‌گویند: ما را چه شده است که مردانی را که از زمره اشرار می‌شمردیم در این جا نمی‌بینیم؟'),
(4033, 38, 63, 'آیا آنان نجات یافته‌اند و ما به ناحق آنها را به تمسخر می‌گرفتیم یا آنان نیز در آتشند ولی چشمان ما از آنها منحرف شده است که آنان را نمی‌بینیم؟'),
(4034, 38, 64, 'این مشاجره اهل آتش که میان سران شرک و پیروانشان روی می‌دهد، قطعاً ثابت و محقّق است.'),
(4035, 38, 65, 'ای پیامبر، بگو: من در پی ثروت و مقام نیستم، بلکه پیامبری بیم دهنده‌ام، و جز خدای یگانه که بر هر چیزی غالب است معبود شایسته پرستشی نیست.'),
(4036, 38, 66, 'اوست پروردگار آسمان‌ها و زمین و آنچه میان آن دو است; همان خدای مقتدری که بسیار آمرزنده است.'),
(4037, 38, 67, 'بگو: این قرآن خبری است بس بزرگ،'),
(4038, 38, 68, 'که شما از آن رویگردانید.'),
(4039, 38, 69, 'من درباره فرشتگان، آن برترین ارجمندان، آن گاه که در مورد آدم بحث و جدال می‌کردند آگاهی نداشتم، ولی خدا از طریق وحی مرا از آن آگاه ساخت.'),
(4040, 38, 70, 'چیزی جز این به من وحی نمی‌شود که من پیامبری هشداردهنده‌ام و پیامم را به روشنی ابلاغ می‌کنم.'),
(4041, 38, 71, 'یاد کن زمانی را که پروردگارت به فرشتگان گفت: من از گِل، بشری خواهم آفرید;'),
(4042, 38, 72, 'پس هنگامی که او را به صورت انسانی کامل درآوردم و از روحی والا که از من است در او دمیدم و به او حیات انسانی بخشیدم، بر زمین افتید و او را سجده کنید.'),
(4043, 38, 73, 'پس همه فرشتگان او را سجده کردند،'),
(4044, 38, 74, 'جز ابلیس که تکبّر ورزید و پیش‌تر هم از کافران بود.'),
(4045, 38, 75, 'پروردگارت گفت: ای ابلیس، چه چیز تو را بازداشت از این که برای کسی سجده کنی که من او را با دو دست خودم آفریدم و در آفرینش او عنایتی خاص داشتم؟ آیا خود را برتر شمردی یا از بلندمرتبگانی بودی که به سجده کردن فرمان نیافته بودند؟'),
(4046, 38, 76, 'ابلیس گفت: من از او بهترم، چون مرا از آتش آفریدی و او را از گِل پدید آوردی. من این فرمان تو را نادرست می‌دانم، از این رو آن را اطاعت نخواهم کرد.'),
(4047, 38, 77, 'پروردگارت به او گفت: پس، از منزلتی که داری به درآی، زیرا تو رانده شده درگاه منی.'),
(4048, 38, 78, 'من تو را از رحمت خود دور ساختم و تا روز جزا لعنت من بر تو خواهد بود.'),
(4049, 38, 79, 'ابلیس گفت: پروردگارا، پس مرا تا روزی که مردم برای رستاخیز برانگیخته می‌شوند مهلت ده و هلاکم مکن.'),
(4050, 38, 80, 'پروردگارت به او گفت: قطعاً تو از مهلت یافتگانی، البته نه تا روز رستاخیز،'),
(4051, 38, 81, 'بلکه تا آن روز معیّن که مردم به کفر و گناه می‌پردازند.'),
(4052, 38, 82, 'ابلیس گفت: به عزّت تو سوگند که قطعاً همه انسان‌ها را به بیراهه می‌کشانم;'),
(4053, 38, 83, 'مگر از میان ایشان آن بندگانت که برای تو خالص شده‌اند به گونه‌ای که نه من در آن بهره‌ای دارم نه غیر من.'),
(4054, 38, 84, 'پروردگارت به ابلیس گفت: پس به حق سوگند ـ و فقط حق می‌گویم ـ'),
(4055, 38, 85, 'که قطعاً دوزخ را از تو و نسل تو و کسانی که تو را پیروی کنند، از همگی، پر خواهم کرد.'),
(4056, 38, 86, 'بگو: من از شما برای رساندن پیام قرآن هیچ مزدی نمی‌خواهم، و من از کسانی نیستم که خود را به چیزی آراسته سازم که در من نیست.'),
(4057, 38, 87, 'قرآن وسیله کسب مال نیست، بلکه تنها تذکّری برای جهانیان است.'),
(4058, 38, 88, 'و قطعاً پس از مدتی، آنچه را که قرآن از آن خبر می‌دهد خواهید دانست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'این است آن کتابِ فرو فرستاده شده از جانب خداوند شکست ناپذیر حکیم.'),
(4060, 39, 2, 'ما این کتاب را که سراسر حق است به سوی تو فرو فرستادیم و در آن به یکتاپرستی فرمان دادیم; پس خدا را در حالی که عبادت خود را ویژه او ساخته‌ای پرستش کن.'),
(4061, 39, 3, 'ای مردم، بدانید که عبادت خالص و پیراسته از شرک، از آنِ خداوند است که جز این گونه عبادت را نمی‌پذیرد. و کسانی که به جای خدا معبودانی را برگرفته و به عبادت آنها پرداخته و می‌گویند: ما آنها را جز برای این که ما را به خدا نزدیک کنند و به منزلتی نزد او برسانند، نمی‌پرستیم، قطعاً خداوند میان اینان و کسانی که خالصانه او را می‌پرستند، در آنچه بر سرش اختلاف دارند، داوری خواهد کرد، و بی تردید خداوند کسی را که دروغگو و بسیار ناسپاس است به فرجام نیک نمی‌رساند.‌'),
(4062, 39, 4, 'می‌گویند: خدا فرزند گرفته است. اگر خدا می‌خواست برای خود فرزندی بگیرد، از میان آنچه می‌آفریند، هر که را می‌خواست به فرزندی می‌گرفت، ولی او کار ناشدنی را اراده نمی‌کند. منزّه است او، اوست خدای یگانه که بر هر چیز و هر کس چیره است; پس چگونه ممکن است فرزند داشته باشد؟'),
(4063, 39, 5, 'تدبیر امور جهان هستی فقط در اختیار اوست، زیرا او آسمان‌ها و زمین را به حق آفریده است. پیوسته شب را بر روز می‌افکند و روز را بر شب می‌افکند، (رفت و آمدِ روز و شب را هماره برقرار می‌دارد) و خورشید و ماه را رام ساخته و نظام آنها را تدبیر کرده است. هر یک تا سرآمدی معیّن در حرکتند. آگاه باشید که اوست آن عزّتمندِ آمرزگار.'),
(4064, 39, 6, 'خداوند شما را از یک تن آفرید، و همسرش را از جنس او قرار داد، و برای شما از دام‌ها هشت تای پدید آورد (شتر، گاو، میش و بز; نر و ماده آنها) شما را در شکم مادرانتان آفرینشی پس از آفرینش دیگر، در تاریکی‌های سه گانه (شکم، رحم و مَشیمه) خلق می‌کند. این است خدا که پروردگار شماست. اوست که کارهای شما را به سامان می‌رساند، و فرمانروایی فقط از آنِ اوست. جز او معبودی شایسته پرستش نیست، پس چگونه از عبادت او باز گردانده و به پرستش غیر او برده می‌شوید؟'),
(4065, 39, 7, 'اگر به خدا کفر ورزید، زیانی به او نمی‌رسانید; زیرا او به شما نیازی ندارد. او از آن روی شما را به ایمان فرا می‌خواند که شما بندگان او هستید و او برای بندگانش ناسپاسی را نمی‌پسندد; و اگر با پرستش خالصانه او را شکرگزاری کنید، آن را برای شما می‌پسندد، و هیچ کس که گناهی بر دوش دارد بار گناه دیگری را بر نمی‌گیرد. بدانید که بازگشت شما به سوی پروردگارتان است، آن گاه شما را به حقیقت آنچه می‌کردید آگاه می‌سازد و شما را بر طبق آنچه در دل دارید بازخواست می‌کند. قطعاً او اندیشه‌ها و رازهای نهفته در سینه‌ها را به خوبی می‌داند.'),
(4066, 39, 8, 'و هنگامی به انسان گزندی برسد، به سوی خدا باز می‌گردد و به ربوبیّت او اعتراف می‌کند و برای برطرف شدن آن گزند پروردگار خود را می‌خواند، سپس وقتی خدا از جانب خود نعمتی به او بخشید، به آن سرگرم می‌شود و گزندی را که پیش‌تر برطرف شدن آن را از خدا می‌طلبید فراموش می‌کند و برای خدا همتایانی قرار می‌دهد تا مردم را از راه خدا باز دارد. بگو: از کفر خودت اندکی بهره مند شو، ولی بدان که این بهره مندی جاودانه نیست و تو از اهل دوزخ خواهی بود.'),
(4067, 39, 9, 'آیا این کفرپیشه که اهل دوزخ است بهتر است یا آن کسی که در ساعات شب، خاضعانه فرمان خدا را اطاعت می‌کند و به نماز برمی خیزد و در نماز خود به سجده و قیام مشغول است و از عذاب آخرت می‌ترسد و به رحمت پروردگارش امید دارد؟ بگو: آیا کسانی که خدا را می‌شناسند با کسانی که خدا را نمی‌شناسند یکسانند؟ تنها خردمندانند که حقایق را درمی یابند.'),
(4068, 39, 10, 'بگو: ای بندگان من که به یگانگی خدا ایمان آورده‌اید، از پروردگارتان پروا کنید. برای کسانی که در این جهان به کارهای نیک پرداخته‌اند، در دنیا نیکبختی و در آخرت سعادتمندی است، و زمینِ خدا گسترده است، اگر در سرزمینی نتوانستید به وظایف دینی خود عمل کنید، از آن جا هجرت کنید و بر ناگواری‌های راه ایمان شکیبا باشید; جز این نیست که پاداش شکیبایان به تمام و کمال و بدون محاسبه به آنها پرداخت خواهد شد.'),
(4069, 39, 11, 'بگو: من نیز چون شما فرمان یافته‌ام که خدا را ـ در حالی که عبادتم را ویژه او ساخته‌ام ـ بپرستم.'),
(4070, 39, 12, 'و نیز فرمان یافته‌ام که نخستین کسی باشم که فرمان خدا را گردن نهاده است.'),
(4071, 39, 13, 'بگو: من اگر با ترک عبادت خالصانه، پروردگارم را نافرمانی کنم، از عذاب روز قیامت که روزی بزرگ است می‌ترسم.'),
(4072, 39, 14, 'بگو: من فقط خدا را می‌پرستم، در حالی که عبادتم را ویژه او ساخته‌ام.'),
(4073, 39, 15, 'پس شما مشرکان هر چه را غیر از او می‌خواهید بپرستید، که نه تنها سودی نمی‌برید، بلکه وزر و وبال آن دامنگیرتان می‌شود. بگو: زیانکاران کسانی اند که با شرک ورزیدن خود در دنیا، خویشتن را در قیامت باخته و به هلاکت انداخته و آن همسران و خدمتگزاران را که برای آنان آماده شده است از دست داده‌اند. آگاه باشید، این است آن خسارتِ آشکار که جبران پذیر نیست.'),
(4074, 39, 16, 'برای آنان از فرازشان پوشش‌هایی از آتش و از زیرشان نیز پوشش‌هایی از آتش خواهد بود. این است آنچه خداوند بندگانش را به آن بیم می‌دهد. پس ای بندگان من، از کیفر من بترسید.'),
(4075, 39, 17, 'امّا کسانی که از پرستش بُت‌ها و دیگر معبودهای طغیانگر دوری گزیده و به سوی خدا بازگشته‌اند آنان را مژده باد! پس بشارت ده به بندگان من،'),
(4076, 39, 18, 'آنان که برای دستیابی به حقیقت، به سخن گوش می‌سپارند و بهترینِ آن را پیروی می‌کنند. اینانند که خدا آنان را راه نموده است و اینانند خردمندان.'),
(4077, 39, 19, 'پس آیا کسی که به سبب کفرش حکم عذاب بر او ثابت شده است، از عذاب رهایی خواهد یافت؟ آیا تو کسی را که در آتش است رهایی می‌بخشی؟!'),
(4078, 39, 20, 'لیکن کسانی که از پروردگارشان پروا داشته‌اند، برای آنان غرفه‌هایی والاست که بر فراز آنها غرفه‌هایی دیگر ساخته شده است، و در زیر آنها نهرها روان است. خدا این را وعده داده است و خدا از وعده اش تخلّف نمی‌کند.'),
(4079, 39, 21, 'آیا ندیده‌ای که خدا از آسمان آبی فرو فرستاد، پس آن را در چشمه‌هایی که در درون زمین هستند راه داد، سپس بهوسیله آن کشتزاری را که رنگ‌های آن گوناگون است برمی آورد، سپس آن کشتزار خشک می‌گردد، آن گاه آن را می‌بینی که زرد شده است، سپس خُردش می‌کند؟ قطعاً در این دگرگونی‌ها، برای صاحبان خرد تذکّری بر یکتایی خداست.'),
(4080, 39, 22, 'پس آیا کسی که خدا سینه اش را برای تسلیم شدن در برابر حق گشاده است در نتیجه او بر نوری از جانب خدا قرار دارد و حقایق را به وسیله آن می‌بیند، همچون کسی است که سختدل است و نوری برای شناخت ندارد؟ پس وای بر کسانی که دل هایشان از این که یاد خدا کند سخت شده است اینان در گمراهی آشکارند.'),
(4081, 39, 23, 'خدا قرآن را که بهترین سخن است فرو فرستاده است; کتابی که بخش‌هایی از آن شبیه یکدیگر است و در معنی به یکدیگر باز می‌گردند. (برخی از آنها برخی دیگر را تفسیر می‌کند) کتابی که با شنیدن آیات آن، پوست کسانی که از پروردگارشان می‌هراسند، بر اندامشان جمع می‌شود، سپس پوستشان و دلشان به یاد خدا نرم می‌گردد و بدان آرامش می‌یابد. این است هدایت خدا که هر کس را بخواهد بدان راه می‌نماید، و هر که را خدا به گمراهی کشد، او را هیچ هدایت کننده‌ای نخواهد بود.'),
(4082, 39, 24, 'پس آیا کسی که روز قیامت دست هایش در غل و زنجیر است و به ناچار صورتش را سپر آن عذاب سخت قرار می‌دهد، با کسی که از عذاب ایمن است، یکسان است؟ آن روز به این ستمکاران گفته می‌شود: آتش دوزخ را که خود فراهم می‌آوردید بچشید.'),
(4083, 39, 25, 'کسانی هم که پیش از آنان بودند، [پیامبران و آیات الهی را] تکذیب کردند; از این رو عذاب از آن جا که تصوّرش را نمی‌کردند به سراغشان آمد.'),
(4084, 39, 26, 'پس خدا ذلّت و حقارت را در زندگی دنیا به آنان چشانید و قطعاً عذاب آخرت بزرگ‌تر است; ای کاش می‌دانستند.'),
(4085, 39, 27, 'و همانا در این قرآن از هر مَثَلی برای مردم آورده‌ایم، باشد که آنان آگاه شوند و عبرت گیرند.'),
(4086, 39, 28, 'قرآنی به زبان عربی، بدون هیچ گونه انحراف و کژی، نازل کردیم، باشد که راه تقوا در پیش گیرند.'),
(4087, 39, 29, 'خداوند مَثَلی زده است مردی را که چند مالک در او شریکند; مالکانی بدخو و ناسازگار که در کار او به نزاع و کشمکش می‌پردازند، و مردی است که تنها در اختیار یک نفر و تسلیم اوست. آن مردِ نخست، مَثَلِ فردی است مشرک که خدایانی چند را که طبعاً باهم ناسازگارند می‌پرستد، یکی او را به کاری فرمان می‌دهد و دیگری او را از آن باز می‌دارد، و آن مرد دوم، مَثَلِ فردی است یکتاپرست که خدایی یگانه او را امر و نهی می‌کند. آیا این دو مرد یکسانند و حالتی برابر دارند؟ ستایش، همه از آنِ خداست، ولی بیش‌تر مردم نمی‌دانند.'),
(4088, 39, 30, 'قطعاً تو خواهی مُرد و آنان نیز خواهند مُرد;'),
(4089, 39, 31, 'سپس روز قیامت در پیشگاه پروردگارتان حضور یافته و به جدال خواهید پرداخت.'),
(4090, 39, 32, 'پس کیست ستمکارتر از آن کس که با قرار دادن شریک برای خدا، بر خدا دروغ بست و آن سخن راست (دین الهی) را وقتی برای او آمد، دروغ شمرد؟ آیا برای کافران در دوزخ جایگاهی نیست؟'),
(4091, 39, 33, 'کسانی که آن سخن راست را آوردند و آن را با ایمان و عملِ خود تصدیق کردند، آنانند تقواپیشگان.'),
(4092, 39, 34, 'آنان در حالی که نزد پروردگار خویشند هرچه بخواهند از جانب او در اختیارشان قرار خواهد گرفت این است سزای نیکوکاران.'),
(4093, 39, 35, 'آری، آنان آن سخن راست را باور کردند تا خدا گناهان بزرگشان را که بدترین کردارهاست از آنان بزداید و پاداش اعمالشان را بر پایه نیکوترین عملی که می‌کردند، به آنان بدهد.'),
(4094, 39, 36, 'مگر خدا کفایت کننده بنده اش نیست؟ با این حال آنان تو را از کسانی که جز اویند (معبودانشان) می‌ترسانند. آنان گمراهند و هر که را خدا گمراه کند، هیچ هدایتگری برای او نخواهد بود که او را به راه آورد.'),
(4095, 39, 37, 'و هر که را خدا هدایت کند گمراه کننده‌ای نخواهد داشت. مگر خدا شکست ناپذیر و انتقام گیرنده نیست؟'),
(4096, 39, 38, 'و اگر از مشرکان بپرسی: چه کسی آسمان‌ها و زمین را آفریده؟ قطعاً خواهند گفت: خدا آنها را آفریده است به آنان بگو: پس به من خبر دهید، اگر خدا گزندی برای من بخواهد، آیا آنچه به جای او می‌خوانید، می‌توانند گزندی را که او خواسته است برطرف کنند؟ یا اگر خدا رحمتی برای من بخواهد، آیا آنها می‌توانند رحمت او را بازدارند؟ بگو: چون آفرینش و تدبیر از آنِ خداست، پس او مرا بسنده است که کار خود را به او واگذارم، زیرا همه آنان که اهل توکّل اند تنها بر او توکّل می‌کنند.'),
(4097, 39, 39, 'بگو: ای قومِ من، شما برحسب موقعیت خود عمل کنید و بر کفر و لجاجت و بازداشتن از راه خدا ادامه دهید، من نیز به آنچه بدان فرمان یافته‌ام عمل می‌کنم، پس زود است که بدانید،'),
(4098, 39, 40, 'چه کسی را در دنیا عذابی خواهد آمد که خوار و حقیرش می‌کند، و بر چه کسی در آخرت عذابی پایدار فرود می‌آید.'),
(4099, 39, 41, 'ما این کتابِ سراسر حق را برای مردم بر تو فرو فرستادیم; پس هر که بدان هدایت یابد به سود خود اوست، و هر که گمراه گردد جز این نیست که به زیان خود گمراه می‌شود; و [بدان که] تو از جانب ما بر آنان وکیل نیستی تا رهنمودهای این کتاب را در دل هایشان وارد کنی.'),
(4100, 39, 42, 'خداست که جان مردم را هنگام مرگشان به تمام و کمال می‌گیرد و جان کسانی را که نمرده‌اند، در وقت خوابشان می‌ستاند; پس آنهایی را که حکم مرگ بر آنان گذرانده است، نگاه می‌دارد و دیگران را تا سرآمدی معیّن به بدن‌ها باز می‌فرستد. همانا در این امر برای مردمی که می‌اندیشند نشانه‌هایی است بر این که تدبیر امور آدمیان در اختیار خداست.'),
(4101, 39, 43, 'بلکه آیا مشرکان جز خدا شفاعتگران و کارسازانی برای خود برگرفته‌اند. بگو: آیا بُت‌ها را شفاعتگر خود می‌پندارید، هر چند آنها مالک چیزی نباشند و از عقل و شعور بی بهره باشند؟'),
(4102, 39, 44, 'بگو: شفاعت در کارها و تدبیر امور، همه از آنِ خداست، چرا که تنها او مالک آسمان‌ها و زمین است و شما فقط به سوی او بازگردانده می‌شوید.'),
(4103, 39, 45, 'و چون خدا به تنهایی یاد شود و نامی از معبودان اهل شرک به میان نیاید، دل‌های کسانی که آخرت را باور ندارند، به تنگ می‌آید و می‌رمد، و چون کسانی که جز اویند یاد شوند، ناگهان آنان شادمان می‌گردند.'),
(4104, 39, 46, 'بگو: خداوندا، ای پدیدآورنده آسمان‌ها و زمین، ای دانای نهان و آشکار، تو میان بندگانت درباره آنچه بر سرش اختلاف دارند، داوری خواهی کرد.'),
(4105, 39, 47, 'و اگر آنچه در زمین است و همانند آن که به آن ضمیمه شود، همه از آنِ کسانی باشد که با انکار آخرت به خود ستم نموده‌اند، آن را برای رهایی خود از عذاب ناگوار روز قیامت خواهند داد. آن روز از جانب خدا برای آنان عذاب‌هایی پدیدار می‌شود که تصورش را نمی‌کردند.'),
(4106, 39, 48, 'و زشتی گناهانی که مرتکب شده‌اند، برایشان آشکار می‌شود، و عذاب‌های روز قیامت که به مسخره می‌گرفتند بر آنان فرود می‌آید.'),
(4107, 39, 49, 'آدمی از آن روی که به پیروی از هواهای نفسانی خوی گرفته و به نعمت‌ها و اسباب ظاهری دنیا فریب خورده است، چنان است که وقتی گزندی به او می‌رسد ما را خالصانه می‌خواند، ولی آن گاه که از جانب خود نعمتی به او می‌بخشیم می‌گوید: این ثروتی که به من داده شده است، تنها به سبب دانشی است که من دارم. نه! چنین نیست، بلکه این وسیله آزمونی است، ولی بیش‌تر مردم نمی‌دانند.'),
(4108, 39, 50, 'کسانی که پیش از آنان بودند و ثروت و امکاناتی در اختیار داشتند نیز این سخن را گفتند، ولی آنچه به دست آورده بودند آنان را کفایت نکرد و از عذاب الهی نجاتشان نداد.'),
(4109, 39, 51, 'پس کارهای ناروایی که انجام داده بودند، به صورت عذاب به آنان رسید، و از این مردمی که تو پیامبرشان هستی، نیز کسانی که با ارتکاب گناهان ستم کرده‌اند، کارهای بدشان به صورت عذاب به آنان خواهد رسید و آنان هرگز نمی‌توانند خدا را ناتوان سازند.'),
(4110, 39, 52, 'آیا ندانسته‌اند که خداست که روزی را برای هر که بخواهد فراخ می‌گرداند و بر هر که بخواهد تنگ می‌گیرد؟ همانا در این امر برای مردمی که ایمان دارند، نشانه‌هایی از ربوبیّت خدا و یکتایی او در تدبیر جهان است.'),
(4111, 39, 53, 'بگو: ای بندگان من که با شرک یا ارتکاب گناهان دیگر بر خویشتن ستم کرده‌اید، مبادا از رحمت خدا ناامید شوید; چرا که خدا همه گناهان حتی شرک را در صورتی که توبه کنید می‌آمرزد. همانا اوست که بسیار آمرزنده و مهربان است.'),
(4112, 39, 54, 'و پیش از آن که عذابِ آخرت بر شما در رسد، به سوی پروردگارتان باز گردید و توبه کنید و تسلیم فرمان او شوید، زیرا هنگامی که عذاب در رسد، دیگر شفاعتی در کار نیست و از سوی کسی یاری نخواهید شد.'),
(4113, 39, 55, 'و پیش از آن که عذاب، به طور ناگهانی و بی آن که متوجه باشید، بر شما در رسد، از نیکوترین حقایقی که از جانب خدا به سوی شما نازل شده است پیروی کنید.'),
(4114, 39, 56, 'اینها را به شما توصیه می‌کنیم تا مبادا کسی در آن جهان بگوید: دریغا که در مورد حقوق خدا کوتاهی کردم; راستی من از مسخره کنندگان [دین خدا] بودم.'),
(4115, 39, 57, 'یا بگوید: اگر خدا مرا راهنمایی می‌کرد، من از تقواپیشگان بودم.'),
(4116, 39, 58, 'یا چون عذاب را در روز قیامت مشاهده کند، بگوید: ای کاش، من به دنیا بازگشتی داشتم در این صورت از نیکوکاران می‌شدم.'),
(4117, 39, 59, 'در پاسخ او می‌گویم: چنین نیست که تو را راه ننموده باشم; آیات من به تو رسید، ولی آنها را دروغ انگاشتی و گردنکشی کردی و از کافران بودی.'),
(4118, 39, 60, 'و روز رستاخیز کسانی را که با افتراهای گوناگون بر خدا دروغ بستند روسیاه می‌بینی: آنان در برابر آیات خدا گردنکشی کردند. آیا برای گردنکشان در دوزخ جایگاهی نیست؟'),
(4119, 39, 61, 'و خداوند کسانی را که تقوا پیشه کرده و از شرک و گناه برحذر بوده‌اند، از آن روی که نیکبخت شده‌اند نجات می‌بخشد، به گونه‌ای که ناگواری به آنان نمی‌رسد و اندوهگین نمی‌شوند.'),
(4120, 39, 62, 'خدا آفریننده هر چیزی است، و اوست کارگزار و تدبیرکننده همه چیز.'),
(4121, 39, 63, 'گنجینه‌های آسمان‌ها و زمین و کلیدهای آنها فقط از آنِ اوست، و کسانی که به آیات الهی کافر شده و با وجود دلایل روشن، ربوبیّت خدا و یکتایی او را انکار نموده‌اند، آنانند زیانکاران.'),
(4122, 39, 64, 'بگو: پس ای بی خردان، آیا با وجود این که پرستش تنها شایسته اوست، از من می‌خواهید غیر خدا را بپرستم؟'),
(4123, 39, 65, 'قطعاً به تو وحی شده است که اگر برای خدا شریکی قرار دهی اعمالت تباه می‌شود و از زیانکاران خواهی شد، و نیز به پیامبرانی که پیش از تو بودند این حقیقت وحی شد.'),
(4124, 39, 66, 'پس تنها خداوند یکتا را پرستش کن و از سپاسگزاران باش.'),
(4125, 39, 67, 'مشرکان از آن روی که بازگشت همه چیز را به خدا انکار کردند، منزلت خدا را آن گونه که باید نشناختند، و حال آن که روز قیامت زمین و آنچه در آن است همه در قبضه قدرت اوست، و آسمان‌ها در آن روز به دست خدا برچیده می‌شوند و آشکار می‌گردد که هر چه بود زمامش در اختیار خدا بود پس منزّه است او، و برتر از این که شریکی برای او بپندارند.'),
(4126, 39, 68, 'و در صور دمیده می‌شود، پس هر که در آسمان‌ها و هر که در زمین است می‌میرد، مگر کسی که خدا بخواهد; سپس بار دیگر در صور دمیده می‌شود و به ناگاه آنان که مرده‌اند حیاتی دوباره می‌یابند و به پا می‌ایستند و حیرت زده می‌نگرند.'),
(4127, 39, 69, 'و زمین به نور پروردگارش روشن می‌شود، و خوبی‌ها و بدی‌ها برای همگان آشکار می‌گردد، و آن کتاب (لوح محفوظ) در میان نهاده می‌شود و پیامبران احضار می‌گردند و از آنان درباره ابلاغ رسالت سؤال می‌شود، و شاهدانِ اعمال فراخوانده می‌شوند تا شهادت خود را ادا کنند، و میان مردم به حق داوری می‌شود و مورد ستم قرار نمی‌گیرند.'),
(4128, 39, 70, 'و با داوری خدا به هر کسی آنچه کرده است به تمام و کمال داده می‌شود، و خدا به آنچه می‌کنند از همه داناتر است.'),
(4129, 39, 71, 'و آنان که کفر ورزیده‌اند، گروه گروه به سوی جهنّم رانده می‌شوند، تا چون بدان جا رسند، درهای آن به رویشان گشوده می‌شود و نگهبانانش از آنان می‌پرسند: آیا پیامبرانی از خودتان برای شما نیامدند که آیات پروردگارتان را بر شما بخوانند و شما را از دیدار امروزتان بیم دهند؟ می‌گویند: چرا، آمدند، ولی ما آنان را دروغگو شمردیم و کافر شدیم و وعده عذاب درباره کافران تحقق یافت.'),
(4130, 39, 72, 'به آنان گفته می‌شود: به درهای دوزخ درآیید که در آن جاودانه می‌مانید. شما در برابر پیامبران گردنکشی کردید، و جایگاه گردنکشان بد جایگاهی است.'),
(4131, 39, 73, 'و آنان که از پروردگارشان پروا داشته‌اند، گروه گروه به سوی بهشت برده می‌شوند، تا چون بدان جا رسند ـ و پیش از آمدنشان، درهای بهشت به رویشان گشوده شده است ـ سرایی زیبا و نعمت‌هایی وصف ناپذیر می‌بینند، و نگهبانانش که به استقبال آنان آمده اند می‌گویند: سلام بر شما; جز آنچه می‌پسندید چیزی به شما روی نمی‌کند، چرا که از آلودگی‌ها پاک بودید، پس به بهشت درآیید که در آن جاودانه می‌مانید.'),
(4132, 39, 74, 'آنان به بهشت درآیند و گویند: ستایش خدای را که در وعده اش به ما راست گفت و سرزمین بهشت را میراث ما قرار داد; در هر جای آن که بخواهیم جای می‌گزینیم. پس خوب است پاداش کسانی که برای خدا کار کرده‌اند.'),
(4133, 39, 75, 'و در آن روز فرشتگان را می‌بینی که برای اجرای فرمان خدا پیرامون عرش الهی در گردش اند و همراه با ستایش پروردگارشان پیوسته او را تسبیح می‌گویند. در آن روز محکمه داوری برپا می‌شود تا میان آدمیان به حق داوری گردد، و گفته می‌شود: ستایش، همه از آنِ خداست که پروردگار همه جهان هاست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حا، میم.'),
(4135, 40, 2, 'این کتاب از جانب خدای مقتدر شکست ناپذیر و دانا نازل شده است.'),
(4136, 40, 3, 'همو که بخشنده گناه و توبه پذیر و سخت کیفر و بخشنده نعمت‌های مستمرّ است. جز او هیچ معبودی شایسته پرستش نیست و بازگشت همگان فقط به سوی اوست.'),
(4137, 40, 4, 'جز کسانی که کافر شده‌اند، کسی در آیات ما جدال نمی‌کند; پس [اقتدار و دانش پروردگارت را در نظر داشته باش تا] مبادا گردش آنان در شهرها (فرورفتن آنان در نعمت و رفاه و بهره مندی شان از جلوه‌های دنیا) تو را بفریبد و تصور کنی که آنان می‌توانند از کیفر خدا بگریزند.'),
(4138, 40, 5, 'پیش از اینان نیز قوم نوح و گروه‌هایی که پس از آنان بودند، پیامبرانشان را تکذیب کردند; و هر امّتی آهنگ پیامبر خویش کردند تا او را بگیرند و بکُشند و یا تبعیدش کنند، و به وسیله باطل به بحث و جدال پرداختند تا بدان حق را تباه سازند; من هم آنان را به عذاب گرفتم; پس بنگر که عقوبت من چگونه بود.'),
(4139, 40, 6, 'آری، همان گونه که کافران امّت‌های گذشته به عذاب‌های دنیوی گرفتار شدند، سخن پروردگارت در حق همه کافران، از جمله کافران امّت تو تحقق یافت که آنان همدم آتش دوزخ خواهند شد.'),
(4140, 40, 7, 'فرشتگانی که عرش الهی را حمل می‌کنند و کسانی که بر گرد آن هستند، پیوسته با ستایش پروردگارشان او را تسبیح می‌گویند و به یکتایی او در تدبیر جهان ایمان دارند، و برای کسانی که یگانگی خدا را باور کرده‌اند، آمرزش می‌طلبند، می‌گویند: پروردگارا، رحمت و دانش تو همه چیز را فراگرفته است; پس کسانی را که به سوی تو بازگشته و راه تو را پیموده‌اند بیامرز و آنان را از عذاب دوزخ نگاه دار.'),
(4141, 40, 8, 'پروردگارا، و آنان را به بهشت‌هایی همیشگی که وعده شان داده‌ای درآور، و از پدران و همسران و نسل هایشان کسانی را که شایسته ورود به بهشت اند به آنان ملحق کن; همانا تو مقتدر و شکست ناپذیری و کارهایت همه براساس حکمت است.'),
(4142, 40, 9, 'و آنان را روز قیامت از ناگواری‌ها حفظ کن، که هر که را در آن روز از ناگواری‌ها حفظ کنی قطعاً او را مشمول رحمت خود ساخته‌ای، و این است کامیابی بزرگ.'),
(4143, 40, 10, 'به یقین، کسانی که کافر شده‌اند، آن گاه که عذاب قیامت را می‌چشند، از سوی خدا به آنان ندا داده می‌شود که: قطعاً دشمنی خدا با شما سخت‌تر از دشمنی شما با خودتان است. شما آن گاه با خود دشمنی کردید و این عذاب را برای خود فراهم آوردید که به ایمان فرا خوانده می‌شدید ولی کفر میورزیدید.'),
(4144, 40, 11, 'آنان در دوزخ می‌گویند: پروردگارا، ما را دو بار میراندی و دو بار زنده کردی، (نخست در دنیا میراندی و حیات برزخی بخشیدی، و بار دیگر جان ما را در برزخ گرفتی و حیات اُخروی بخشیدی) اکنون معاد را باور کردیم و به گناهان خود اعتراف نمودیم; آیا برای بیرون شدن از این جا راهی هست؟'),
(4145, 40, 12, 'به آنان گفته می‌شود: ای کفرپیشگان، این کیفر به سزای آن است که چون خداوند به یکتایی خوانده می‌شد انکار می‌کردید، و اگر چیزی شریک او قرار داده می‌شد آن را باور می‌نمودید. اینک داوری از آنِ خداوند بلند مرتبه و بزرگ است که در حق شما هر چه بخواهد حکم می‌کند.'),
(4146, 40, 13, 'اوست آن که نشانه‌های خود را در ربوبیت و تدبیر هستی به شما نشان می‌دهد و از آسمان برای شما روزی فرو می‌فرستد، ولی جز کسی که به خدا روی آورده است این حقیقت را درنمی یابد.'),
(4147, 40, 14, 'پس ای مؤمنان، خدا را در حالی که عبادت خود را ویژه او ساخته‌اید بپرستید و در راهی که او برای شما ترسیم کرده است گام بردارید، هر چند کافران خوش نداشته باشند.'),
(4148, 40, 15, 'اوست آن که مرتبه‌های فرمانروایی اش بسی بلند است و صاحب عرش عظیم است. روح نبوت را که از امر اوست بر هر کس از بندگانش که بخواهد القا می‌کند و او را به رسالت برمی انگیزد تا مردم را از روز دیدارشان با خدا بیم دهد;'),
(4149, 40, 16, 'از روزی که آنان با برکنار شدن حجاب‌ها درمی یابند که در پیشگاه خدا آشکارند و هیچ چیزی از آنان بر خدا پنهان نیست. آن زمان از جانب خدا ندا می‌رسد که: امروز فرمانروایی از آنِ کیست؟ و خود پاسخ می‌گوید که: از آنِ خداوندِ یگانه است که بر همه موجودات چیره است.'),
(4150, 40, 17, 'امروز هر کسی بدانچه کرده است سزا داده می‌شود. امروز هیچ ستمی در کار نخواهد بود، چرا که خداوند اعمال بندگان را به سرعت حسابرسی می‌کند.'),
(4151, 40, 18, 'آنان را از روزِ قیامت، آن روزِ نزدیک، بترسان، آن گاه که دل‌ها از شدّت ترس از جای کنده شده و به حنجره‌ها رسیده است، در حالی که آنان سخت اندوهناک‌اند. آن روز ستمکاران نه خویشاوندی دارند که از آنان حمایت کند و نه شفاعت کننده‌ای که شفاعتش در حق آنان پذیرفته شود.'),
(4152, 40, 19, 'خداوند خیانتِ چشم‌هایی را که دزدانه می‌نگرند، می‌داند و به کفر و نفاق و گناهانی که سینه‌ها نهان می‌دارند، آگاه است.'),
(4153, 40, 20, 'و خداست که در دنیا و آخرت به حق حکم می‌کند (امور هستی را تدبیر می‌کند و برای هدایت مردم دین و آیین مقرر می‌دارد) ولی کسانی را که مشرکان به جای خدا می‌خوانند، هیچ حکمی ندارند (نه در آفرینش تدبیری دارند و نه آیینی را تشریع می‌کنند.) زیرا تنها خدا شنوا و بیناست.'),
(4154, 40, 21, 'آیا در زمین گردش نکرده‌اند تا به دیده عبرت بنگرند که فرجام کسانی که پیش از آنان می‌زیستند و پیامبران را تکذیب کردند چگونه شد؟ آنان بسیار نیرومندتر از اینان بودند و بناهایی که آنان در زمین بر جای گذاشتند از بناهایی که اینان دارند، بسیار استوارتر بود، اما خدا آنان را به گناهانشان گرفت و هلاکشان ساخت و هیچ کس نبود که آنان را از عذاب خدا نگاه دارد.'),
(4155, 40, 22, 'این فرجام شوم از آن روی بود که پیامبرانشان با نشانه‌هایی روشن به سوی آنان آمدند ولی آنان کفر ورزیدند و خدا هم آنان را گرفتار عذاب ساخت. به راستی خدا نیرومند و سخت کیفر است.'),
(4156, 40, 23, 'و همانا موسی را با معجزاتی که همه از نشانه‌های ما بود و نیز با سلطه و نیرویی آشکار که فرعون را از دسترسی به او باز می‌داشت، فرستادیم.'),
(4157, 40, 24, 'او را به سوی فرعون و هامان و قارون به رسالت فرستادیم، ولی آنان گفتند: موسی افسونگری دروغ پرداز است.'),
(4158, 40, 25, 'پس هنگامی که موسی حق را که از جانب ما بود برایشان آورد، گفتند: پسرانِ کسانی را که ایمان آورده و او را همراهی و پشتیبانی کرده‌اند، بکشید، و زنانشان را برای خدمت زنده نگاه دارید. آنان برای نابودی مؤمنان نیرنگ کردند ولی نیرنگ کافران جز در بیراهه نبود و به موسی و مؤمنان کارگر نشد.'),
(4159, 40, 26, 'و فرعون به بزرگان قومش گفت: مرا واگذارید اگر مرا به خود وانهید موسی را می‌کشم و بر اوست که پروردگارش را بخواند تا اگر توانست از دست من نجاتش دهد. من از این می‌ترسم که موسی دین شما را دگرگون سازد و شما را از پرستش خدایانتان به پرستش خدای خودش سوق دهد، یا این که شوکت یابد و به مبارزه برخیزد و در زمین تباهی پدید آورد.'),
(4160, 40, 27, 'موسی به فرعونیان گفت: من از شرّ هر گردنکشی که به روز قیامت که روز حسابرسی است ایمان ندارد، به خدای یکتا که پروردگار من و پروردگار شماست پناه برده‌ام.'),
(4161, 40, 28, 'در این میان مردی با ایمان که از خاندان فرعون بود و ایمانش را از آنان پوشیده می‌داشت، گفت: آیا مردی را به خاطر این که می‌گوید: فقط خدا پروردگار من است، می‌کشید؟ در حالی که او برای شما از جانب پروردگارتان دلایلی روشن بر مدعای خویش آورده است. اگر او دروغگو باشد دروغش به زیان او خواهد بود; و اگر راستگو باشد، دست کم برخی از آنچه به شما وعده می‌دهد به شما خواهد رسید. قطعاً خدا کسی را که تجاوزکار و دروغ پرداز باشد، هدایت نمی‌کند.'),
(4162, 40, 29, 'ای قومِ من، امروز فرمانروایی از آنِ شماست، در حالی که در این سرزمین بر بنی اسرائیل که زیردستان شما هستند چیره گشته‌اید، ولی چه کسی ما را در برابر عذاب خدا ـ اگر به سراغمان آمد ـ یاری می‌رساند؟ فرعون [سخن او را قطع کرد و] گفت: من جز آنچه را می‌بینم و به درستی آن اطمینان دارم به شما نمی‌نمایم، و شما را جز به راه درست راهنمایی نمی‌کنم.'),
(4163, 40, 30, 'و آن کس که ایمان آورده بود [در ادامه سخنانش] گفت: ای قومِ من، به راستی من بر شما از روزی بسانِ روز امّت‌های گذشته که در آن به عذاب الهی گرفتار شدند، می‌ترسم.'),
(4164, 40, 31, 'من از مثل آن سنّتی که بر قوم نوح و عاد و ثمود و کسانی که پس از آنان بودند، جاری شد بیم دارم، و خدا هیچ ستمی برای بندگان خود نمی‌خواهد.'),
(4165, 40, 32, 'و ای قومِ من، به راستی من بر شما از روز قیامت، روزی که ستمکاران یکدیگر را ندا می‌دهند که وای بر ما! می‌ترسم;'),
(4166, 40, 33, 'روزی که پشت کرده از آتش دوزخ می‌گریزید; در آن روز از جانب خدا هیچ کس نیست که شما را از عذاب نگاه دارد، زیرا خدا دوزخیان را در بیراهه افکنده است، و هر که را خدا در بیراهه افکند راهنمایی که او را به راه درست آورد نخواهد داشت.'),
(4167, 40, 34, 'و به راستی یوسف پیش از این، آن نشانه‌های روشن را که بر درستی رسالتش دلالت داشت برای شما آورد. پس تا زنده بود همچنان در حقّانیّت آنچه برای شما آورده بود تردید داشتید، تا وقتی که درگذشت، گفتید: خدا بعد از او هرگز پیامبری برنمی انگیزد. این گونه، خداوند کسانی را که از حدّ بگذرند و در شک و تردید به سر برند، به بیراهه می‌افکند.'),
(4168, 40, 35, 'همان کسانی که در آیات خدا ـ بی آن که برایشان برهانی آمده باشد ـ مجادله می‌کنند. این کار نزد خدا و نزد کسانی که ایمان آورده‌اند، مایه دشمنیِ بزرگی است. این گونه، خداوند بر دل هر گردنکشِ زورگویی مُهر می‌زند تا به حق راه نیابد.'),
(4169, 40, 36, 'فرعون [بار دیگر سخن آن مرد را قطع کرد و] گفت: ای هامان، برای من بنایی مرتفع بساز تا برفراز آن بروم، شاید به اسباب و وسایلی دست بیابم;'),
(4170, 40, 37, 'وسایلی آسمانی، تا بدین طریق از خدای موسی اطلاع یابم و البته من او را دروغگو می‌پندارم. بدین سان برای فرعون کردار زشتش آراسته گشت و از راهِ درست باز داشته شد. به همین جهت برای مبارزه با حق به نیرنگ‌هایی دست زد، ولی نیرنگ‌های او جز در تباهی نبود و او را به هدف نرساند.'),
(4171, 40, 38, 'و آن کس که ایمان آورده بود، سخن خود را ادامه داد و گفت: (ای قومِ من، مرا پیروی کنید که من شما را به راه درست که با پیمودن آن به سعادت می‌رسید راه می‌نمایم.'),
(4172, 40, 39, 'ای قومِ من، جز این نیست که این زندگی دنیا بهره‌ای برای سرای آخرت است، و سرای آخرت است که سرای پایندگی است.'),
(4173, 40, 40, 'هر که در این جهان کار بد کند، جز همانند آن در آخرت سزا داده نمی‌شود، و کسانی که کار شایسته کنند ـ مرد باشند یا زن ـ بدین شرط که با ایمان باشند، به بهشت درمی آیند و در آن جا بی حساب (بی آن که عوضی از آنان خواسته شود) روزی داده می‌شوند.'),
(4174, 40, 41, 'و ای قومِ من، چه شده است که من شما را به رهایی از آتش فرا می‌خوانم و شما مرا به ورود در آتش فرا می‌خوانید؟'),
(4175, 40, 42, 'مرا فرا می‌خوانید تا به خداوند کافر شوم و چیزی را که بدان دانشی ندارم و دلیلی بر وجودش نیست، شریک او سازم و حال آن که من شما را به سوی خدای شکست ناپذیر بسیار آمرزنده فرا می‌خوانم.'),
(4176, 40, 43, 'بی تردید، معبودی که مرا به [پرستش] آن فرا می‌خوانید و آن را شریک خدا می‌پندارید، نه در دنیا دعوتی دارد، زیرا پیامبری نفرستاده است که مردم را به پرستش خود ترغیب کند و نه در آخرت او را دعوتی است زیرا کسی به سوی او باز نمی‌گردد; اما در این تردیدی نیست که بازگشت ما به سوی خداست و تجاوزکارانی که از عبادت او سرباز زده‌اند، اهل آتش‌اند.'),
(4177, 40, 44, 'پس به زودی ـ آن گاه که عذاب را به چشم خود دیدید ـ آنچه را به شما می‌گویم و به آن هشدارتان می‌دهم یاد خواهید کرد و درخواهید یافت که من خیرخواه شما بودم، و من کار خود را به خدا وامی گذارم، زیرا خداوند به بندگانش بیناست.'),
(4178, 40, 45, 'پس فرعونیان آهنگ او کردند، اما چون او کار خود را به خدا واگذارده بود خدا وی را از بدی‌های نیرنگ آنان نگاه داشت و عذابی ناگوار بر خاندان فرعون فرود آمد.'),
(4179, 40, 46, 'آن عذاب، آتش است که بامدادان و عصرگاهان بر آن عرضه می‌شوند; و روزی که قیامت برپا گردد، فرمان رسد که فرعونیان را به سخت‌ترین عذاب درآورید.'),
(4180, 40, 47, 'و یاد کن زمانی را که آنان در آتش دوزخ به جدال می‌پردازند; پس ناتوانانشان به کسانی که گردنکشی کرده اند می‌گویند: ما در دنیا پیرو شما بودیم و انتظار داشتیم که ما را در سختی‌ها یاری رسانید، اینک آیا بخشی از این آتش را از ما باز می‌دارید؟'),
(4181, 40, 48, 'گردنکشان در پاسخ می‌گویند: ما و شما همگی در آتشیم و همگان ناتوان زیرا خداوند میان بندگانش داوری کرده تأثیر اسباب و علل را تباه ساخته است; ازاین رو ما نیز چون شما از این که حتّی بخشی از عذاب را از شما دور سازیم ناتوانیم.'),
(4182, 40, 49, 'آنان که در آتش دوزخ اند ـ چه فرعونیان و چه دیگران ـ به نگهبانان جهنّم می‌گویند: پروردگارتان را بخوانید تا بر ما آسان گیرد و این عذاب را اندک زمانی از ما بردارد، پروردگارتان دعای شما را اجابت می‌کند.'),
(4183, 40, 50, 'نگهبانان دوزخ در پاسخ می‌گویند: آیا پیامبرانتان برای شما دلایلی روشن بر درستی رسالت خویش نیاوردند؟ می‌گویند: چرا. نگهبانان می‌گویند: پس خود دعا کنید، ولی بدانید که دعایتان اجابت نخواهد شد، زیرا شما کافرید و دعای کافران جز در تباهی نیست.'),
(4184, 40, 51, 'ما قطعاً فرستادگان خود و کسانی را که ایمان آورده‌اند، هم در زندگی دنیا و هم روزی که گواهانِ اعمال برای ادای شهادت به پا می‌خیزند، یاری می‌کنیم;'),
(4185, 40, 52, 'روزی که که ستمکاران را عذرخواهی شان سود نمی‌دهد، و دوری از رحمت خدا برای آنان است و دوزخ نیز که سرایی ناگوار است برای آنان خواهد بود.'),
(4186, 40, 53, 'و همانا به موسی آن آیین هدایت کننده را ارزانی داشتیم و به بنی اسرائیل تورات را به میراث دادیم;'),
(4187, 40, 54, 'کتابی که برای عموم بنی اسرائیل راهنما و برای خردمندانشان مایه تذکار بود.'),
(4188, 40, 55, 'ای پیامبر، اکنون که دانستی فرجام ستمکاران به کجا می‌انجامد، بر آزار و مخالفت مشرکان شکیبا باش، زیرا وعده خدا راست است و به آن وفا می‌کند، و برای گناهت آمرزش بخواه و همراه با ستایش پروردگارت، در هر عصر و بامداد او را تسبیح گوی.'),
(4189, 40, 56, 'کسانی که در آیات خدا ـ بی آن که برهانی برایشان آمده باشد ـ به جدال می‌پردازند، در پیِ یافتن حق نیستند، و در سینه هایشان جز خود بزرگ بینی نیست. آنان با این جدال هرگز به مرادشان نمی‌رسند; پس تو از آنان به خدا پناه ببر، که او شنونده دعای بندگان و بینا به حال آنان است.'),
(4190, 40, 57, 'قطعاً آفرینش آسمان‌ها و زمین از آفرینش این مردم بزرگ‌تر است و جهان هستی با تمام عظمتش در اختیار خداست، تا چه رسد به انسان که جزئی از این هستی است، ولی بیشتر مردم نمی‌دانند که با جدال و نیرنگ هرگز نمی‌توان خدا را عاجز کرد.'),
(4191, 40, 58, 'نابینا و بینا یکسان نیستند، و کسانی که ایمان آورده و کارهای شایسته کرده‌اند نیز با گنهکاران یکسان نیستند; چه اندک توجه پیدا می‌کنید!'),
(4192, 40, 59, 'رستاخیز قطعاً آمدنی است و در آن هیچ تردیدی نیست، ولی بیشتر مردم به این حقیقت ایمان نمی‌آورند.'),
(4193, 40, 60, 'و پروردگارتان گفت: مرا بخوانید و بدانید که خواندن من عبادت است و اگر بخوانیدم دعایتان را اجابت می‌کنم; قطعاً کسانی که از روی تکبّر از عبادت من سرباز می‌زنند به زودی خوار و ذلیل وارد دوزخ خواهند شد.'),
(4194, 40, 61, 'خداست آن که شب را برای شما تاریک قرار داد تا در آن بیارامید، و روز را روشن کرد تا در پرتو آن در طلب روزی برآیید. همانا خداوند از فضل خود بر مردم نعمت‌ها بخشیده است ولی بیشتر مردم عبادت او را وامی گذارند و شکرگزاری نمی‌کنند.'),
(4195, 40, 62, 'این است خداوند که پروردگار شماست و امورتان را تدبیر می‌کند. آفریننده هر چیزی است و معبودی شایسته پرستش جز او نیست; پس چگونه از پرستش او باز داشته و به عبادت غیر او کشانده می‌شوید؟'),
(4196, 40, 63, 'کسانی که آیات خدا را انکار کرده‌اند، این گونه، بی هیچ دلیلی، از پذیرش معارف آنها باز گردانده می‌شوند.'),
(4197, 40, 64, 'خداست آن که زمین را برای شما قرارگاه و آسمان را بنایی قرار داد و به شما صورت بخشید و صورت هایتان (اندام و نیروهایتان) را نیکو ساخت و از چیزهای پاکیزه به شما روزی داد. این است خدا که پروردگار شماست; پس پر خیر و برکت است خدا که پروردگار جهانیان است.'),
(4198, 40, 65, 'اوست که زنده است و معبودی جز او نیست; پس او را در حالی که عبادت خود را ویژه او ساخته‌اید بخوانید و بپرستید. ستایش، همه از آنِ خدا پروردگار جهان هاست.'),
(4199, 40, 66, 'بگو: من نهی شده‌ام از این که کسانی را که شما به جای خدا می‌خوانید بپرستم، آن گاه که از جانب پروردگارم دلایل روشن [بر یکتایی او] آمد، و به من فرمان رسیده است که تسلیم پروردگار جهانیان باشم.'),
(4200, 40, 67, 'اوست آن که شما را از خاکی سپس از نطفه‌ای سپس از خون بسته‌ای آفرید. پس از آن شما را از شکم مادرانتان به صورت کودکی بیرون می‌آورَد. سپس شما را رشد می‌دهد تا به توانایی خود برسید و برومند شوید; آن گاه پس از مدتی سالمند شوید; و برخی از شما پیش از طی کردن این مراحل جانش ستانده می‌شود. آری، شما را آفرید تا سرانجام به پایان عمری که برایتان مقرّر شده است برسید و باشد که از روی خِرد حق را دریابید.'),
(4201, 40, 68, 'اوست آن که زنده می‌کند و می‌میراند، و چون تحقق چیزی را مقرر کند، تنها به آن می‌گوید: باش و آن چیز موجود می‌شود.'),
(4202, 40, 69, 'آیا کسانی را که در آیات خدا جدال می‌کنند، ندیده‌ای و از کارشان به شگفت نیامده‌ای که چگونه از راه راست باز گردانده شده و به بیراهه برده می‌شوند؟'),
(4203, 40, 70, 'کسانی اند که کتاب خدا (قرآن) را دروغ شمرده و آنچه فرستادگان خود را به ابلاغ آن گسیل داشته‌ایم تکذیب نموده‌اند، پس به زودی خواهند دانست که کارشان به کجا می‌انجامد;'),
(4204, 40, 71, 'آن گاه که غل‌ها و زنجیرها در گردن هایشان نهاده می‌شود و آنان را می‌کشانند;'),
(4205, 40, 72, 'در آب جوشان و آن گاه در آتش شعلهور می‌شوند.'),
(4206, 40, 73, 'سپس به آنان گفته می‌شود: چیزهایی که شریک خدا می‌ساختید کجایند تا شما را از آتش دوزخ برهانند و یا از شما شفاعت کنند،'),
(4207, 40, 74, 'همان‌هایی که به جای خدا می‌پرستیدید؟ می‌گویند: از دست ما رفته‌اند; آن گاه پوچی معبودان خود را به یاد آورده می‌گویند: بلکه ما پیش‌تر چیزی را به خدایی نخوانده و نپرستیده‌ایم. خداوند کافران را این گونه در بیراهه می‌اندازد تا باطل را حق تلقی کنند.'),
(4208, 40, 75, 'ای دوزخیان، این عذاب به سزای آن است که در زمین به ناروا شادمانی می‌کردید و بدان سبب است که سرمستی می‌نمودید.'),
(4209, 40, 76, 'اکنون به درهای دوزخ (دَرَکات و طبقات آن) درآیید در حالی که جاودانه در آن خواهید ماند. پس بد است جایگاه متکبّران.'),
(4210, 40, 77, 'پس ای پیامبر، بر آزار کافران شکیبا باش که وعده خدا راست است. و اگر بخشی از کیفرهایشان را که به آنان وعده می‌دهیم، به تو بنمایانیم، یا پیش از آن جانِ تو را بستانیم، در هر صورت به سوی ما باز گردانده می‌شوند و نمی‌توانند از عذابی که به آنان وعده داده‌ایم خود را برهانند.'),
(4211, 40, 78, 'و همانا پیش از تو پیامبرانی را به رسالت فرستادیم. از آنان کسانی اند که سرگذشتشان را برای تو حکایت کرده‌ایم و از آنان کسانی اند که داستانشان را برای تو حکایت نکرده‌ایم، و هیچ پیامبری را نرسد که جز به اذن خدا آیتی بیاورد که امّت کفرپیشه خود را با آن عذاب کند و حق را چیره سازد، ولی چون امر خدا به عذاب در رسد، میان پیامبران و امّت آنها به حق داوری می‌شود، و آن جاست که باطل گرایان زیان می‌بینند.'),
(4212, 40, 79, 'خداست آن که دام‌ها (شتر، گاو و گوسفند) را برای شما آفرید تا بر برخی از آنها سوار شوید. و گوشت برخی از آنها را می‌خورید.'),
(4213, 40, 80, 'و برای شما در آنها سودهایی است (از پشم، مو، کرک و پوستشان استفاده می‌کنید و از شیرشان بهره می‌برید) آنها را آفرید تا شما با سوار شدن بر آنها به مقصودی که در نظر دارید برسید، و شما را بر آنها و کشتی‌ها سوار می‌کنند.'),
(4214, 40, 81, 'و خدا آیات و نشانه‌های یکتایی خود را، هم در آفرینش و هم به واسطه پیامبرانش، به شما می‌نمایاند; پس کدام یک از آیات خدا را انکار می‌کنید؟'),
(4215, 40, 82, 'آیا اینان که یگانگی خدا را باور ندارند در زمین گردش نکرده‌اند تا به دیده عبرت بنگرند که فرجام کسانی که پیش از آنان بودند چگونه شد؟ آنان، هم تعدادشان بیشتر از اینان بود و هم نیرومندتر بودند، و بناهایی که در زمین بر جای گذاشتند از بناهایی که اینان دارند بسیار استوارتر بود، ولی آنچه به دست می‌آوردند به حالشان سودی نبخشید و عذاب خدا را از آنان بازنداشت.'),
(4216, 40, 83, 'و هنگامی که پیامبرانشان آن معارف روشن را برایشان آوردند، به دانشی که خود داشتند دل خوش کردند و پیام رسولان را به تمسخر گرفتند، ولی سرانجام همان چیزی که به استهزا می‌گرفتند بر آنان فرود آمد.'),
(4217, 40, 84, 'پس وقتی عذاب سخت ما را دیدند، گفتند: خدا را به یکتایی باور داریم و به آنچه به سبب آن مشرک شدیم کفر میورزیم.'),
(4218, 40, 85, 'ولی هنگامی که عذاب سخت ما را مشاهده کردند، دیگر ایمانشان برایشان سودی نداشت; چرا که از روی ناچاری ایمان آوردند. خدا این سنت را مقرّر کرده و همواره در میان بندگانش جاری بوده است که توبه مردم بعد از آن که عذاب را مشاهده کردند پذیرفته نشود، در آن جا کافران زیان می‌کنند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حا، میم.'),
(4220, 41, 2, 'این کتاب از جانب خداوند گسترده مهرِ مهربان فرو فرستاده شده است; کتابی که امور دنیا و آخرت آدمیان را اصلاح می‌کند;'),
(4221, 41, 3, 'کتابی که آیاتش به طور مشروح بیان شده است. قرآنی است به زبان عربی برای مردمی که اهل دانشند.'),
(4222, 41, 4, 'کتابی که نویدبخش و هشداردهنده است، ولی بیشتر آنان (مردم مکّه) روی برتافتند; پس آنان نمی‌شنوند و حق را نمی‌پذیرند.'),
(4223, 41, 5, 'و به پیامبر گفتند: دل هایمان از آنچه ما را بدان می‌خوانی در پوشش هاست و سخن تو را درک نمی‌کند، و در گوش‌های ما سنگینی است و گفتار تو را نمی‌شنود، و میان ما و تو حجابی ستبر است. که راه تفاهم را از میان برده است. پس تو بر ضدّ ما بکوش که ما نیز بر ضدّ تو تلاش خواهیم کرد.'),
(4224, 41, 6, 'ای پیامبر، در پاسخ آنان بگو: جز این نیست که من بشری چون شمایم، با این تفاوت که به من وحی می‌شود که معبود شما معبودی یگانه است; پس در پرتو توحید، راست قامتان به سوی او روی آورید و آمرزشِ شرکورزی‌ها و گناهانتان را از او بخواهید، که وای بر حال مشرکان!'),
(4225, 41, 7, 'همانان که در راه خدا انفاق نمی‌کنند و آخرت را باور ندارند.'),
(4226, 41, 8, 'اما کسانی که [به یکتایی خدا و جهان آخرت] ایمان آورده و کارهای شایسته کرده‌اند، قطعاً برای آنان پاداشی بی منّت خواهد بود.'),
(4227, 41, 9, 'بگو: آیا واقعاً شما به آن کسی که زمین را در دو مرحله آفرید کفر میورزید و برای او همتایانی قرار می‌دهید؟ این جای شگفتی است. آن بلند مرتبه، پروردگار همه جهان هاست.'),
(4228, 41, 10, 'او زمین را آفرید و در آن از فرازش کوه‌هایی استوار پدید آورد و در آن خیر و برکت فراوان نهاد، و خوراکی‌های آن را در چهار فصل، تقدیر فرمود، در حالی که آنها با آنچه موجودات با زبان نیازشان خواهان آنند برابر است.'),
(4229, 41, 11, 'سپس به آفرینش آسمان پرداخت، و آن توده‌ای از دود بود. پس به آسمان و زمین فرمود: خواه یا ناخواه باید به صحنه وجود بیایید. گفتند: آمدیم و همه به دلخواه مطیع فرمان توییم.'),
(4230, 41, 12, 'پس کار آسمان‌ها را بعد از آن که توده‌ای از دود درهم آمیخته بود در دو مرحله به هیئت هفت آسمان فیصله داد و در هر آسمانی فرمان مربوط به آن را به فرشتگان آن وحی کرد، و نزدیک‌ترین آسمان به زمین را با ستارگانی که همچون چراغ‌هایی هستند آراستیم و آن را از نفوذ شیاطین محفوظ داشتیم. این است اندازه آفرینی آن خدایی که هیچ کس و هیچ چیز بر او چیره نمی‌شود و به هر چیزی داناست.'),
(4231, 41, 13, 'پس اگر [با وجود این دلایل روشن] از پذیرش دعوت تو روی برتافتند به آنان بگو: من شما را به صاعقه‌ای همچون صاعقه قوم عاد و ثمود هشدار می‌دهم.'),
(4232, 41, 14, 'آن گاه که پیامبرانشان از پیش رو و از پشت سرشان به سویشان آمدند (با روش‌های گوناگون آنان را فراخواندند) که جز خدا را نپرستید. آنان در پاسخ گفتند: اگر پروردگار ما می‌خواست پیامبری بفرستد، قطعاً فرشتگانی فرو می‌فرستاد، اکنون که فرشته‌ای به رسالت نیامده است، ما به آنچه شما بدان فرستاده شده‌اید، کافریم.'),
(4233, 41, 15, 'امّا قوم عاد، به ناروا در زمین گردنکشی کردند و گفتند: چه کسی از ما نیرومندتر است؟ آیا ندانسته‌اند که خداوندی که خلقشان کرده از آنان نیرومندتر است؟ [آنان تکبر میورزیدند] و نشانه‌های ما را انکار می‌کردند.'),
(4234, 41, 16, 'پس در روزهایی شوم تندبادی سوزان بر آنان فرستادیم تا در زندگی دنیا عذاب ذلّت و خواری را به آنان بچشانیم و قطعاً عذاب آخرت خوارکننده‌تر است و آنان از سوی هیچ کس یاری نخواهند شد.'),
(4235, 41, 17, 'و اما ثمودیان، پس ما آنان راه نمودیم ولی آنان کوردلی را بر هدایت ترجیح دادند، پس به سزای گناهانی که مرتکب می‌شدند صاعقه‌ای آسمانی که عذاب ذلّت را در پی داشت، آنان را فرو گرفت.'),
(4236, 41, 18, 'و از میان قوم عاد و ثمود کسانی را که ایمان آورده و تقوا پیشه کرده بودند از عذاب رهانیدیم.'),
(4237, 41, 19, 'و یاد کن روزی را که دشمنان خدا گردآوری شده و به سوی آتش رانده شوند; پس در این کوچ، گروه‌های اول از حرکت باز داشته می‌شوند تا گروه‌های بعدی به آنان برسند و پراکنده نباشند.'),
(4238, 41, 20, 'تا چون به آتش رسند، گوش و چشم و پوستشان به آنچه انجام می‌دادند، بر ضدّشان گواهی می‌دهند.'),
(4239, 41, 21, 'و آنان به پوست خود می‌گویند: چرا بر ضدّ ما گواهی دادید؟ می‌گویند: همان خدایی که همه چیز را ناطق ساخته ما را به سخن درآورده است. و خداوند به کارهای شما داناست، چرا که او نخستین بار شما را آفرید و سرانجام به سوی او بازگردانده می‌شوید.'),
(4240, 41, 22, 'و شما آن گاه که به گناه روی می‌آوردید، از این که گوش و چشم و پوستتان ناظر شما بوده و بر ضد شما گواه باشند هراسی نداشتید و خود را از آن‌ها پنهان نمی‌کردید، شما اگر چه می‌پنداشتید اندام هایتان به کارهای شما آگاهی ندارند بلکه گمان داشتید که خدا بسیاری از کارهایتان را نمی‌داند.'),
(4241, 41, 23, 'آری، این است گمان بیجایی که درباره پروردگارتان بردید این گمان شما را به کفر و گناه کشانید و به هلاکت انداخت و در نتیجه از زیانکاران شدید.'),
(4242, 41, 24, 'پس اگر شکیبایی کنند [و دم برنیاورند] جایگاهشان آتش است و اگر عذرخواهی کنند و رضایت طلبند، از کسانی نیستند که عذرشان پذیرفته شود و مورد رضایت قرار گیرند.'),
(4243, 41, 25, 'و برای آنان به جای هدایتگران همنشینانی از شیاطین قرار دادیم; پس همه لذّت‌های مادّی را که پیشاروی آنان بود (در دسترسشان قرار داشت) و نیز آنچه در پی آنان بود (در آرزوی رسیدن به آن بودند) همه را در نظرشان آراستند [تا بدانها دل خوش کنند و از حق روی برتابند] و آن سخن (وعده عذاب دوزخ)، در حق آنان تثبیت شد، در حالی که در میان امّت‌هایی از جنّ و انس هستند که پیش از آنان [بر اثر کفر، به عذاب] درگذشتند. راستی که آنان زیانکارند.'),
(4244, 41, 26, 'و آنان که کافر شدند گفتند: به این قرآن گوش فرا ندهید و هنگامی که خوانده می‌شود، سخن لغو و بیهوده در آن بیفکنید، باشد که ندای آن به گوش‌ها نرسد و شما چیره شوید.'),
(4245, 41, 27, 'آری، ما قطعاً به کسانی که کفر ورزیده‌اند عذابی سخت می‌چشانیم و حتماً آنان را به کردار بدی که انجام می‌دادند سزا می‌دهیم.'),
(4246, 41, 28, 'این است سزای دشمنان خدا، همان آتش که آنان را در آن سرای جاودانه است; سزایی به کیفر این که آیات ما را انکار می‌کردند.'),
(4247, 41, 29, 'و کسانی که کافر شدند می‌گویند: پروردگارا، آن دو گروه از جنّ و انس را که گمراهمان کردند به ما نشان ده تا آنان را زیر گام هایمان نهیم تا از پست ترین‌ها شوند.'),
(4248, 41, 30, 'همانا کسانی که گفتند: پروردگارِ ما خداست; سپس بی هیچ انحرافی بر این سخن ثابت قدم ماندند، فرشتگان با این پیام بر آنان فرود می‌آیند که: نترسید و اندوه مدارید و مژده باد شما را به بهشتی که آن را به شما وعده می‌دادند.'),
(4249, 41, 31, 'ما فرشتگان در زندگی دنیا عهده دار کار شما بودیم پس در سرای آخرت نیز کارتان را برعهده می‌گیریم، و آنچه دل هایتان بخواهد در آن جا برای شما خواهد بود، و هر چه درخواست کنید در آن جا برای شما فراهم است.'),
(4250, 41, 32, 'اینها عطیّه‌ای است از جانب آمرزنده‌ای مهربان.'),
(4251, 41, 33, 'و کیست نیکو گفتارتر از آن کس که به سوی خدا دعوت کرد و کاری شایسته انجام داد و گفت: من از کسانی ام که در برابر خدا تسلیم‌اند.'),
(4252, 41, 34, 'ای پیامبر، خوی نیکو در تأثیرگذاری با خوی زشت برابر نیست; پس با نیکوترین خصلت بدی‌های آنان را دفع کن که ناگاه آن کسی که میان تو و او دشمنی است گویی دوستی دلسوز است.'),
(4253, 41, 35, 'این خصلت را جز بر دل کسانی که شکیبا بوده اند نمی‌اندازند و آن را جز بر کسی که از کمال بهره‌ای عظیم دارد نمی‌افکنند.'),
(4254, 41, 36, 'و اگر از جانب شیطان به تو شورشی رسد (مخالفان تو را بر ضدّ تو برانگیزد) و چنین خواهد کرد، به خدا پناه ببر، که او سخن تو را می‌شنود و به احوال تو داناست.'),
(4255, 41, 37, 'از نشانه‌های ربوبیّت او شب و روز و خورشید و ماه است; پس تنها او شایسته پرستش است و اگر شما او را می‌پرستید، نه برای خورشید سجده کنید و نه برای ماه، بلکه برای آن خدایی که همه آنها را آفریده است سجده کنید.'),
(4256, 41, 38, 'پس اگر گردنکشی کردند و از این که تنها برای خدا سجده کنند امتناع ورزیدند، بدانند که عبادت خدا از بین نمی‌رود، زیرا آنان که نزد پروردگار تواَند، شب و روز او را تسبیح می‌گویند و خسته و ملول نمی‌شوند.'),
(4257, 41, 39, 'و از نشانه‌های ربوبیّت او این است که تو زمین را افتاده و رام می‌بینی، پس هنگامی که آب بر آن فرو فرستیم، با رویش گیاهان به جنبش درمی آید و با رشد آنها فراز می‌آید. آری، همان کسی که زمین را با رویاندن گیاهان زنده کرد، مردگان را حیاتی دوباره می‌بخشد. به راستی او بر هر چیزی تواناست.'),
(4258, 41, 40, 'به یقین، کسانی که در پدیده‌های هستی که همه نشانه‌های ربوبیّت و الوهیّت ماست به انحراف می‌گرایند و آنها را می‌پرستند، و نیز در آیاتِ وَحْیانی ما کجروی می‌کنند و آنها را دروغ می‌شمرند، بر ما پنهان نمی‌مانند و به آتش دوزخ درمی آیند. اینک آیا کسی که در آتش افکنده می‌شود بهتر است یا کسی که روز قیامت ایمن از هر گزندی می‌آید؟ هر کاری خواستید بکنید، بی گمان او به آنچه انجام می‌دهید بیناست.'),
(4259, 41, 41, 'آنان که به این قرآن ـ چون برایشان آمد ـ کافر شدند، قطعاً در آتش دوزخ افکنده می‌شوند، و به راستی این کتابی است که هیچ چیز بر آن چیره نمی‌شود.'),
(4260, 41, 42, 'نه اکنون که عصر نزول است و نه در آینده، تا برپایی قیامت، باطل در آن راه نخواهد یافت و حقایق آن را دگرگون نخواهد ساخت، چرا که آن از جانب حکیمی ستوده که کارهایش همه استوار و نیکوست نازل شده است.'),
(4261, 41, 43, 'ای پیامبر، از جانب کافران جز آنچه به پیامبرانِ پیش از تو گفته شد چیز دیگری گفته نمی‌شود. همانا پروردگار تو، هم صاحب آمرزش است و هم صاحب عقوبتی دردناک; پس بنگرند کدام یک از این دو را سزاوارند.'),
(4262, 41, 44, 'و اگر این کتاب را قرآنی قرار می‌دادیم که مطالب آن نارسا بود، کافرانِ قوم تو می‌گفتند: چرا آیاتش به روشنی بیان نشده است؟ آیا کتابی نارسا و قومی دارای فصاحت و بلاغت؟ بگو: این کتاب برای کسانی که ایمان آورده‌اند رهنمودی است و بیماری شک و تردید آنان را شفا می‌بخشد. و کسانی که ایمان نمی‌آورند در گوش هایشان سنگینی است و قرآن برای آنان مایه کوردلی است. آنان نه پندی را می‌شنوند و نه حجّتی را به خرد درمی یابند; گویی از جایگاهی دور که توانِ شنیدن و دیدن ندارند، ندا داده می‌شوند.'),
(4263, 41, 45, '[انکار مشرکان بر تو دشوار نیاید،] همانا به موسی هم کتاب دادیم و در آن اختلاف شد. و اگر از جانب پروردگارت سخنی نرفته بود که شما آدمیان را در زمین تا هنگامی معین جایگاه و معیشتی است، قطعاً میان آنان داوری شده بود و هر کس به سزای کردارش رسیده بود. بی گمان، قوم موسی درباره کتاب او سخت در شک و تردیدند.'),
(4264, 41, 46, 'هر که کاری شایسته کند به سود خود اوست، و هر که بدی کند به زیان خود اوست، و پروردگارت هرگز به بندگان خود ستم نمی‌کند.'),
(4265, 41, 47, 'آگاهی از زمان برپایی قیامت فقط به خدا باز می‌گردد و جز او کسی از آن آگاه نیست، و میوه‌ها از غلافشان بیرون نمی‌آیند و هیچ ماده‌ای باردار نمی‌شود و بار فرو نمی‌نهد مگر این که او از آن آگاه است. و روزی که مشرکان را ندا می‌دهد که شریکانی که برای من تصوّر می‌کردید کجایند؟ می‌گویند: به تو اعلام کرده‌ایم که از میان ما هیچ کس گواهی نمی‌دهد که برای تو شریکی هست.'),
(4266, 41, 48, 'و آنچه پیش‌تر در دنیا می‌خواندند، از دیدشان پنهان می‌شود و درمی یابند که از عذاب قیامت هیچ گریزگاهی ندارند.'),
(4267, 41, 49, 'انسان از خواستنِ خوبی‌ها خسته نمی‌شود، و اگر شرّی به او رسد بسیار مأیوس و ناامید می‌گردد.'),
(4268, 41, 50, 'و قطعاً اگر پس از رنجی که به او رسیده است، از جانب خود رحمتی به او بچشانیم، خواهد گفت: این از آنِ من است و خود اختیار آن دارم و گمان نمی‌برم که قیامت برپا شود و محاسبه‌ای در کار باشد، و اگر به سوی پروردگارم بازگردانده شوم حتماً در پیشگاه او فرجامی نیکوتر خواهم داشت. ولی ما قطعاً کسانی را که کافر شده‌اند، به حقیقت آنچه کرده‌اند آگاه خواهیم ساخت و از عذابی سخت به آنان خواهیم چشانید.'),
(4269, 41, 51, 'و چون به انسان نعمت بخشیم، از ما روی برمی تابد و خود را به کناری می‌کشد و تکبّر میورزد، و چون شرّی به او رسد به دعا روی می‌آورد و برای برطرف شدن آن ما را بسیار می‌خواند.'),
(4270, 41, 52, 'بگو: به من خبر دهید، اگر این قرآن از نزد خدا آمده باشد، آن گاه شما به آن کافر شوید، کیست گمراه‌تر از آن کسی که از در مخالفت با آن درآید، آن هم مخالفتی که نشانی از وفاق در آن نیست؟'),
(4271, 41, 53, 'به زودی به مشرکان مکّه و پیروانشان آیات خود را که در اطراف جهان و درباره خودشان پدید می‌آید نشان خواهیم داد، تا برایشان آشکار شود که قرآن حق است و توحید که فراخوان قرآن است نیز حق است. آیا چیره بودن پروردگارت بر هر چیز و نیازمندی آنها به او و در نتیجه آشکار بودن او برای همه پدیده‌ها، در اثبات یگانگی او کفایت نمی‌کند؟'),
(4272, 41, 54, 'آگاه باشید که آنان در لقای پروردگارشان تردید دارند. آگاه باشید که او بر هر چیزی احاطه دارد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حا، میم.'),
(4274, 42, 2, 'عین، سین، قاف.'),
(4275, 42, 3, 'خداوند آن مقتدر حکیم بدین سان که این سوره را وحی کرد، به تو وحی می‌فرستد، و به کسانی از پیامبران که پیش از تو بودند نیز این گونه وحی می‌کرد.'),
(4276, 42, 4, 'آنچه در آسمان‌ها و آنچه در زمین است از آنِ اوست، پس حق اوست که از طریق وحی مردم را به بندگی خود فراخواند، و اوست آن بلندمرتبه بزرگ که درخورِ پرستش است.'),
(4277, 42, 5, 'نزدیک است آسمان‌ها در اثر عبور وحی، از فرازشان یکی پس از دیگری بشکافند، و فرشتگان در حالی که به ستایش پروردگارشان مشغولند او را تسبیح می‌گویند و از او می‌خواهند که برای زمینیان آیینی مقرر کند و کسانی که آن را بپذیرند بیامرزد. بدانید که خداست که آمرزنده و مهربان است.'),
(4278, 42, 6, 'و کسانی که غیر او را سرپرستان خود قرار داده و به پرستش آنها پرداخته‌اند، خدا از آنان بی خبر نیست. او بر شرک و دیگر گناهانشان نگهبان است و آنان را بدانها کیفر خواهد داد. پس تو ای پیامبر، وکیل آنان نیستی و اعمالشان در اختیار تو نیست تا بتوانی آنان را به سرمنزل هدایت برسانی; پس مسئول رفتارشان نیز نخواهی بود.'),
(4279, 42, 7, 'و این گونه، قرآن را که به زبان تازی است به تو وحی کردیم تا مردم مکّه را که مرکز این شهرهاست و نیز کسانی را که پیرامون آنند بیم دهی، و آنان را از روز قیامت که همگان در آن روز گردآوری می‌شوند و تردیدی در آن نیست، بترسانی. آن روز مردم دو گروه‌اند; گروهی به بهشت درمی آیند و گروهی وارد آتش فروزان دوزخ می‌شوند.'),
(4280, 42, 8, 'و اگر خدا می‌خواست، مردم را یک امّت قرار می‌داد و همه را به بهشت یا دوزخ درمی آورد، ولی سنّت خدا بر جدا ساختن آنان جاری شده است; هر که را بخواهد یاری می‌دهد و به رحمت خود درمی آورد، و اینان کسانی اند که ستمکار نیستند; اما ستمکاران را از یاری و رحمت خود محروم می‌سازد تا وارد دوزخ شوند. در آن هنگام آنان سرپرست و یاوری ندارند که از آتش نجاتشان دهد.'),
(4281, 42, 9, 'آیا جز خداسرپرستانی گرفته و به پرستش آنها پرداخته‌اند؟ تنها خداست که سرپرست آنان است و امورشان را برعهده دارد، و تنها اوست که مردگان را حیاتی دوباره می‌بخشد و اوست که بر هر چیزی تواناست.'),
(4282, 42, 10, 'و درباره هر چیزی اختلاف کردید، حکمش به خدا ارجاع می‌شود. ای مردم! عهده دار کارها خداوندی است که پروردگار من است. تنها بر او توکل کرده‌ام و در همه کارها به او رجوع می‌کنم.'),
(4283, 42, 11, 'او پَدیدآورنده آسمان‌ها و زمین است. از جنس خودتان برای شما همسرانی قرار داد، و از دام‌ها نیز جفت‌هایی برای آنها آفرید. او با این آفرینش تعداد شما آدمیان و دام‌ها را فراوان می‌سازد. هیچ چیزی مانند او نیست، و اوست که دعای بندگان را می‌شنود و کردارشان را می‌بیند.'),
(4284, 42, 12, 'کلیدهای آسمان‌ها و زمین از آنِ اوست. روزی را برای هر که بخواهد گشاده می‌سازد و بر هر که بخواهد تنگ می‌گیرد. به یقین او به هر چیزی داناست; پس نیاز موجودات را می‌داند و براساس آن روزی شان می‌دهد.'),
(4285, 42, 13, 'خداوند حقایقی از دین را که به نوح سفارش کرد و همچنین احکام و معارفی را که به تو وحی کردیم و نیز آنچه را که به ابراهیم و موسی و عیسی سفارش نمودیم، همه را برای شما امت اسلام بیان نمود و جزو شریعت شما قرار داد و شما را به پیروی از آن فرا خواند. فرمان خدا این است که تمامی احکام و معارف این آیین را برپا دارید و در آن اختلاف نیفکنید به گونه‌ای که بخشی را بپذیرید و بخشی را واگذارید. بر مشرکان آنچه آنان را بدان فرا می‌خوانی گران آمده و پذیرفتن آن برایشان دشوار شده است. خداست که هر که را بخواهد به این آیین متمایل می‌سازد و هر کس به سوی خدا باز آید به سوی آن آیین هدایتش می‌کند.'),
(4286, 42, 14, 'امّت‌های پیامبران هنگامی پراکنده شدند و به اختلافات دینی روی آورند که علم برایشان حاصل شده بود و حق را شناخته بودند. آنان به سبب برتری جویی و ستمی که در میانشان بود به اختلافات روی آوردند و در دین خدا اختلاف افکندند. و اگر از جانب پروردگارت سخنی نرفته بود مبنی بر این که امّت‌ها تا سرآمدی معین در زمین می‌مانند، قطعاً میانشان داوری شده بود و به سزای کردارشان هلاک شده بودند. و کسانی که پس از آن اختلاف کنندگان وارث کتاب آسمانی شدند درباره آن سخت در شک و تردید افتادند.'),
(4287, 42, 15, 'پس بدین سبب که خدا دینی برای تو و امّتت تشریع کرده که جامع ادیان گذشته است به دعوت همگان بپرداز و آن گونه که فرمان یافته‌ای بر راه راست پایدار باش و از هواهای نفسانی آنان پیروی مکن و بگو: به هر کتابی که خدا فرو فرستاده است ایمان آورده‌ام و فرمان یافته‌ام که میان شما به عدالت رفتار کنم، چرا که پروردگار ما و شما خداست و در آیین او همه باهم برابریم. اعمال ما از آنِ ما و اعمال شما از آنِ شماست. هیچ کس در برابر اعمال دیگران نه مؤاخذه می‌شود و نه پاداش می‌بیند. در این که میان ما و شما تفاوتی و امتیازی نیست نباید تردید کنیم زیرا هیچ حجت و برهانی در میان نیست. خدا در روز قیامت ما و شما را گرد می‌آورد و بازگشت همگان به سوی اوست.'),
(4288, 42, 16, 'و کسانی که درباره ربوبیّت خدا، پس از آن که از سوی فطرت سلیم پذیرفته شد، به محاجّه می‌پردازند تا سرانجام آن را انکار کنند، حجّتشان نزد خدا تباه است و بر آنان از جانب خدا خشمی خواهد بود و عذابی سخت خواهند داشت.'),
(4289, 42, 17, 'خدا همان کسی است که کتابِ وحی را به حق بی آن که باطلی در آن راه داشته باشد، و نیز دین را که معیار درست یا نادرست بودن باورها و کردارهاست، فرو فرستاده است. چه چیز تو را از قیامت آگاه می‌کند و تو از وقت آن چه می‌دانی؟ شاید قیامت نزدیک باشد.'),
(4290, 42, 18, 'کسانی که قیامت را باور ندارند، از روی استهزا شتاب در آمدنِ آن را می‌خواهند، و کسانی که ایمان آورده‌اند، در عین تمایل به برپایی آن، از آن بیمناکند و می‌دانند که آن حق است. بدانید کسانی که درباره قیامت جدال می‌کنند و در پی انکار آن هستند، در بیراهه‌ای هستند که با حق فاصله‌ای دور و دراز دارد.'),
(4291, 42, 19, 'خداوند به همه خصوصیات بندگانش آگاه است و اراده اش را درباره آنان به سهولت تحقق می‌بخشد; هر که را بخواهد روزی می‌دهد و هیچ کس نمی‌تواند مانع او شود، چرا که او نیرومند و شکست ناپذیر است.'),
(4292, 42, 20, 'هر کس کِشتِ آخرت بخواهد ما به کشت او می‌افزاییم و پاداشش را افزون می‌کنیم، و هر کس کشت دنیا بخواهد، از دنیا ـ کم یا زیاد ـ به او می‌دهیم ولی در آخرت هیچ بهره‌ای نخواهد داشت.'),
(4293, 42, 21, 'مگر مشرکان معبودانی دارند که از دین آنچه را خدا بدان رخصت نداده است برای آنان تشریع کرده باشند؟ اگر آن سخنِ فیصله دهنده مبنی بر این که باید مردم تا سرآمدی معیّن در زمین بمانند مقرّر نشده بود، قطعاً میانشان داوری می‌شد و به هلاکت می‌رسیدند. آری، در دنیا به آنان مهلت داده می‌شود ولی در آخرت ستمکاران عذابی دردناک خواهند داشت.'),
(4294, 42, 22, 'در قیامت ستمکاران را می‌بینی که از آنچه به دست آورده‌اند هراسناک اند و دستاوردشان به آنان می‌رسد; و کسانی که ایمان آورده و کارهای شایسته کرده‌اند، در آن روز در بوستان‌های پر درخت که زمینی آکنده از گیاه دارد، به سر می‌برند. آنان در آن جا هر چه بخواهند، از جانب پروردگارشان در اختیارشان قرار خواهد گرفت. این است آن فزون بخشی بزرگ.'),
(4295, 42, 23, 'این است آنچه خداوند بندگانش را که ایمان آورده و کارهای شایسته کرده‌اند بدان نوید می‌دهد. بگو: من بر ابلاغ رسالتم که شما را به چنین جایگاهی می‌رساند، هیچ مزدی جز دوستی با خویشانم از شما نمی‌خواهم، و هر کس کاری نیکو کند (به خویشان پیامبر مودّت داشته باشد) برای او در کار نیکویش پاداش نیک می‌افزاییم و کمبودهای آن را جبران می‌کنیم، چرا که خداوند بسیار آمرزنده و سپاسگزار است.'),
(4296, 42, 24, 'آیا منافقان و همفکرانشان می‌گویند: محمد بر خدا دروغ بسته است که مزد رسالت را مودّت خویشاوندانش قرار داده است؟ این وحی الهی است نه ساخته تو، چرا که کارها به دست خداست. او اگر بخواهد بر دلت مهر می‌نهد و وحی را از تو باز می‌دارد، و خداوند با سخنانش باطل را نابود می‌سازد و حق را تثبیت می‌کند، زیرا او به آنچه در سینه‌ها نهفته است به خوبی داناست.'),
(4297, 42, 25, 'و اوست کسی که توبه را از بندگانش می‌پذیرد و از گناهان درمی گذرد و آنچه می‌کنید می‌داند.'),
(4298, 42, 26, 'و اوست آن که عبادت کسانی را که ایمان آورده و کارهای شایسته کرده اند می‌پذیرد و از فضل خود برای آنان می‌افزاید و کافران را عذابی سخت خواهد بود.'),
(4299, 42, 27, 'و اگر خدا برای همه بندگانش روزی را گسترده می‌ساخت در زمین ستمگری می‌کردند و سرکشی می‌نمودند، ولی از روزی آنان آنچه را بخواهد به اندازه معین فرو می‌فرستد، زیرا او به بندگانش آگاه و بیناست.'),
(4300, 42, 28, 'و اوست آن که باران سودمند را پس از آن که مردم ناامید شده‌اند فرود می‌آورد و رحمت خود را با رویش گیاهان و پدید آوردن محصولات، میانشان می‌گستراند، و اوست که سرپرستی همگان را برعهده دارد و بر کارهایش هماره ستایش می‌شود.'),
(4301, 42, 29, 'و از نشانه‌های یکتایی او آفرینش آسمان‌ها و زمین و جنبندگانی است که در آن دو پراکنده ساخته است، و او بر گردآوردنشان ـ هرگاه بخواهد ـ تواناست.'),
(4302, 42, 30, 'و هر بلا و مصیبتی به شما برسد به سبب گناهانی است که مرتکب شده‌اید، و خدا از بسیاری از گناهانتان درمی گذرد و شما را به سبب آنها گرفتار نمی‌سازد.'),
(4303, 42, 31, 'و شما هرگز نمی‌توانید در زمین خدا را درمانده کنید و از دسترس او خارج شوید; پس ناگزیر در اثر گناهانتان بلاهایی را خواهید دید، و شما جز خدا سرپرستی ندارید که کارهایتان را تدبیر کند و یاوری ندارید که کمک کار شما باشد.'),
(4304, 42, 32, 'از نشانه‌های یکتایی او در تدبیر هستی کشتی هایِ کوه آساست که در دریا روانند.'),
(4305, 42, 33, 'اگر بخواهد باد را از حرکت باز می‌دارد، آن گاه کشتی‌ها بر روی دریا متوقف می‌شوند. قطعاً در اینها برای هر صبرپیشه سپاسگزاری نشانه‌هایی است بر یکتایی خدا.'),
(4306, 42, 34, 'یا کشتی‌ها را اگر بخواهد در اثر گناهانی که کشتی نشینان مرتکب شده‌اند در دریا نابود می‌کند، گرچه از بسیاری از گناهان آن گاه که بخواهد درمی گذرد.'),
(4307, 42, 35, 'آری، اگر بخواهیم چنین می‌کنیم، تا قدرت خویش را بنمایانیم و تا کسانی که در آیات ما مجادله می‌کنند بدانند که در برابر عذاب ما گریزگاهی ندارند.'),
(4308, 42, 36, 'پس آنچه به شما داده شده بهره‌ای از زندگی دنیاست که فقط چند روزی از آن برخوردار می‌شوید، ولی پاداش‌هایی که نزد خداست از آن رو که با رنج‌ها آمیخته نیست و زوالی ندارد بهتر و پاینده‌تر است. این پاداش از آنِ کسانی است که ایمان آورده و بر پروردگارشان توکّل می‌کنند;'),
(4309, 42, 37, 'و کسانی که از گناهان بزرگ و کارهای زشت دوری می‌کنند و چون بر کسی خشم گیرند، از او درمی گذرند;'),
(4310, 42, 38, 'و کسانی که خواسته پروردگارشان را اجابت کرده و نماز برپا داشته‌اند و در کاری که بر انجام آن تصمیم گرفته‌اند با یکدیگر مشورت می‌کنند و از آنچه روزیشان کرده‌ایم، برای خشنودی خدا انفاق می‌کنند;'),
(4311, 42, 39, 'و کسانی که چون بر آنان ستم رود، از دیگران یاری می‌طلبند و همگی برای رفع ستم می‌کوشند.'),
(4312, 42, 40, 'و سزای بدی بدی ای مانند آن است، ولی هر که درگذرد و کار خود را میان خود و خدای خویش به صلاح آورد پاداش او برعهده خداست. به یقین خدا ستمکاران را دوست نمی‌دارد.'),
(4313, 42, 41, 'سفارش به گذشت، حق انتقام و مقاومت در برابر ستمکار را سلب نمی‌کند; از این رو هر کس پس از ستم دیدنش، در برابر ستمکار بایستد و از او انتقام بگیرد، به حکم دین خدا در امن و امان است و راهی برای بازخواست او نیست.'),
(4314, 42, 42, 'راه بازخواست و انتقام گرفتن تنها بر ضد کسانی گشوده است که بر مردم ستم روا می‌دارند و در زمین به ناحق سرکشی می‌کنند. آنان عذابی دردناک خواهند داشت.'),
(4315, 42, 43, 'با این حال، هر که شکیبایی کند و از ستمی که بر او رفته است درگذرد، قطعاً به فضیلتی برتر دست یافته است، چرا که صبر و گذشت، نشانگر تصمیم جدّی در کارهاست.'),
(4316, 42, 44, 'اینهاست ویژگی‌های هدایت یافتگان و پاداششان نزد خدا، ولی کسی که خدا او را به خاطر ستمکاری اش به بیراهه برَد و از نعمت‌های آخرت محرومش سازد، پس از خدا هیچ کس کارش را برعهده نخواهد گرفت. و روز قیامت ستمگران را می‌بینی که چون عذاب را ببینند، می‌گویند: آیا راهی برای بازگشت به دنیا هست تا کار شایسته کنیم؟'),
(4317, 42, 45, 'و روز قیامت گمراهان را می‌بینی که بر آتش عرضه می‌شوند در حالی که از ذلّت و خواری سرافکنده‌اند و بر اثر ترس زیر چشمی عذاب‌ها را می‌نگرند. و کسانی که ایمان آورده‌اند، در آن صحنه می‌گویند: به یقین زیانکارانِ واقعی کسانی اند که روز قیامت به خود زیان رسانده و به هلاکت انداخته‌اند و همسران و خدمتکاران بهشتی خود را از کف داده‌اند. آری، ستمکاران در عذابی همیشگی خواهند بود.'),
(4318, 42, 46, 'و سرپرستانی ندارند که به جای خدا آنان را یاری کنند. و هر که را خدا به بیراهه بَرَد، هیچ راهی برای رهایی از عذاب نخواهد داشت.'),
(4319, 42, 47, 'پیش از آن که روزی فرا رسد که از جانب خدا بازگشتی برای آن نیست، فرمان پروردگارتان را اجابت کنید. آن روز در برابر عذاب خدا هیچ پناهگاهی نخواهید داشت و شما را راهی برای انکار آنچه کرده‌اید نخواهد بود.'),
(4320, 42, 48, 'پس اگر از هشدارهای تو روی برتافتند غمگین مباش، چرا که ما تو را نفرستاده‌ایم که بر آنان نگهبان باشی. برعهده تو چیزی جز ابلاغ پیام نیست. و ما چون آدمی را از جانب خود نعمتی بچشانیم، بدان شادمان می‌شود و ما را از یاد می‌برد. و اگر آنان را به سبب گناهانی که از پیش مرتکب شده‌اند بدی و رنجی رسد، ناسپاسی می‌کنند، چرا که انسان چون به دنیا روی آوَرَد، ناسپاس می‌گردد.'),
(4321, 42, 49, 'فرمانروایی آسمان‌ها و زمین تنها از آنِ خداست; هر چه بخواهد می‌آفریند; به هر که بخواهد دخترانی می‌دهد و پسران را به هر که بخواهد عطا می‌کند.'),
(4322, 42, 50, 'یا میان هر دو صنف پسر و دختر [برای هر که بخواهد] جمع می‌کند، و هر که را بخواهد عقیم می‌سازد. به راستی او دانا و تواناست.'),
(4323, 42, 51, 'و هیچ بشری را نسزد که خدا با او سخن بگوید جز به صورت وحی یا از پشت حجاب، یا این که فرستاده‌ای گسیل کند و او به اذن خدا آنچه را خدا بخواهد به وی وحی می‌کند. همانا او بلندمرتبه‌ای است که سخن گفتنش همچون سخن گفتن بشر نیست، و حکیمی است که مردم را به حال خود وانمی گذارد، بلکه سخنش را از طریق وحی به آنان ابلاغ می‌کند.'),
(4324, 42, 52, 'و بدین سان ای پیامبر، روحی را که از عالَم امر ما و حامل قرآن است، به سوی تو فرستادیم. تو پیش از آن نمی‌دانستی کتاب آسمانی چیست و چه معارفی را دربردارد، و باورها و کارهای شایسته را نیز به تفصیل نمی‌شناختی که کدامند و به همه آنها ملتزم نبودی، ولی ما آن را نوری قرار دادیم که هر یک از بندگان خود را بخواهیم به وسیله آن هدایت می‌کنیم، و قطعاً تو نیز مردم را به راه راست رهنمون می‌شوی.'),
(4325, 42, 53, 'راهِ همان خدایی که آنچه در آسمان‌ها و آنچه در زمین است تنها از آنِ اوست. پس بدانید که همه کارها فقط به او باز می‌گردد و به دست او سامان می‌یابد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حا، میم.'),
(4327, 43, 2, 'سوگند به این کتابِ روشنگر،'),
(4328, 43, 3, 'که ما آن را قرآنی به زبان تازی قرار دادیم، باشد که در آن بیندیشید.'),
(4329, 43, 4, 'این کتاب که نزد ما و در لوح محفوظ است، جایگاهی بس والا دارد که اندیشه آدمی بدان راه نخواهد یافت. در آن جا سربسته و تفصیل نایافته است.'),
(4330, 43, 5, 'آیا چون شما مردمی تجاوزکارید این کتاب را که یادآور خداست از شما دریغ می‌داریم تا از شما روی برتافته باشیم؟ نه، چنین نخواهیم کرد.'),
(4331, 43, 6, 'و چه بسیار پیامبرانی که در میان مردمان نخستین به رسالت فرستادیم.'),
(4332, 43, 7, 'و هیچ پیامبری برایشان نیامد مگر این که او را به استهزا می‌گرفتند.'),
(4333, 43, 8, 'ما هم آن تکذیب کنندگان را که از اینان (امّت تو) نیرومندتر بودند به هلاکت رساندیم; و داستان مردمان نخستین در سوره‌هایی که پیش‌تر به تو وحی کردیم، گذشت.'),
(4334, 43, 9, 'و اگر از آنان بپرسی: آسمان‌ها و زمین را چه کسی آفریده است؟ قطعاً خواهند گفت: آنها را خدای مقتدر و دانا آفریده است.'),
(4335, 43, 10, 'همان کسی که زمین را برای شما چون گهواره‌ای قرار داد تا در آن رشد کنید، و در آن برای شما راه‌هایی پدید آورد تا با پیمودن آنها به مقصد خود راه یابید.'),
(4336, 43, 11, 'و همان کسی که از آسمان آبی به اندازه فرو فرستاد، و به وسیله آن سرزمینی را که مرده بود با رویش گیاهان زنده ساختیم. شما نیز این گونه زنده از گورها بیرون آورده می‌شوید.'),
(4337, 43, 12, 'و همان کسی که انواع موجودات را آفرید، و برای شما کشتی‌ها و شترانی که بر آنها سوار می‌شوید پدید آورد،'),
(4338, 43, 13, 'تا بر پشتِ آنها قرار گیرید، سپس نعمت پروردگارتان را ـ آن گاه که بر مرکب‌ها نشستید ـ یاد کنید و بگویید: پاک و منزّه می‌دانیم کسی را که این مرکب را به تسخیر ما در آورد و گرنه ما را یارای آن نبود که این را در اختیار خویش گیریم.'),
(4339, 43, 14, 'و قطعاً ما به سوی پروردگارمان بازخواهیم گشت.'),
(4340, 43, 15, 'مشرکان خدای یکتا را وجودی مرکّب انگاشتند آن گاه که با قائل شدن فرزند برای او، بعضی از بندگانش را جزئی برای او قرار دادند. به راستی که انسان بسیار ناسپاس است و ناسپاسی او آشکار است.'),
(4341, 43, 16, 'آیا از آنچه می‌آفریند دخترانی را به فرزندی برگرفته و پسران را به شما اختصاص داده است؟'),
(4342, 43, 17, 'آنان چنین می‌پندارند، در صورتی که وقتی یکی از آنان را به آنچه همسان خدای رحمان انگاشته و دختر او شمرده است مژده دهند، چهره اش از شدت اندوه کبود می‌شود و آکنده از خشم می‌گردد.'),
(4343, 43, 18, 'آیا زنان را که در زر و زیور پرورش داده می‌شوند و هنگام مجادله بیانی نارسا دارند فرزند خدا می‌انگارند؟'),
(4344, 43, 19, 'و آنان فرشتگان را که بندگان خداوند رحمانند دختر پنداشتند; آیا هنگام آفرینش آنها حضور داشتند که به دختر بودنشان گواهی می‌دهند؟ بی گمان گواهی آنان ثبت می‌شود و روز قیامت مورد بازخواست قرار خواهند گرفت.'),
(4345, 43, 20, 'و [به گمان خود بر درستیِ پرستش فرشتگان برهان آوردند و] گفتند: اگر خدای رحمان می‌خواست، ما آنها را نمی‌پرستیدیم، زیرا هر چه را که او نخواهد، تحقق نمی‌پذیرد. آنان به این سخن هیچ علمی ندارند و نمی‌دانند که مشیّت الهی چیست و چگونه است، و جز به حدس و گمان سخن نمی‌گویند.'),
(4346, 43, 21, 'آیا پیش از قرآن کتابی آسمانی به آنان داده و پرستش فرشتگان را در آن روا شمرده‌ایم تا بدان تمسّک جویند و بر کردار خویش حجّت آورند؟'),
(4347, 43, 22, 'نه، آنان هیچ دلیلی ندارند، بلکه گفتند: ما پدران خود را بر آیینی یافتیم و راه و رسم آنان را دنبال نموده و رهیافته‌ایم.'),
(4348, 43, 23, 'و بدین گونه پیش از تو در هیچ شهری پیامبری هشداردهنده نفرستادیم مگر این که مرفّهانش گفتند: ما پدرانمان را بر آیینی یافته‌ایم و قطعاً از پی راه و رسم آنان خواهیم رفت.'),
(4349, 43, 24, 'پیامبرشان در پاسخ گفت: آیا راه پدرانتان را می‌پویید هر چند برای شما آیینی آورده باشم که از آنچه پدرانتان را بر آن یافته‌اید هدایت کننده‌تر باشد؟ گفتند: ما به آنچه شما برای ابلاغ آن فرستاده شده‌اید کافریم.'),
(4350, 43, 25, 'در نتیجه ما از آنان انتقام گرفتیم و هلاکشان کردیم، پس بنگر که فرجام آنان که پیامبران را تکذیب نمودند چگونه شد.'),
(4351, 43, 26, 'تقلید از پدران را می‌توان رها کرد; تو برای آنان داستان ابراهیم را بیان کن، آن گاه که ابراهیم به پدرش و قومش گفت: من از آنچه شما می‌پرستید سخت دوری می‌جویم;'),
(4352, 43, 27, 'ولی خدای را که مرا آفریده است می‌پرستم، زیرا یقین دارم که او چون آفریدگار است، مرا به سوی حق هدایت می‌کند.'),
(4353, 43, 28, 'و خداوند این سخن ابراهیم را در میان نسلش جاودان ساخت، بدان امید که مشرکان نسل او با راهنمایی یکتاپرستانشان به سوی توحید باز گردند.'),
(4354, 43, 29, 'ولی آن شرک پیشگان به سوی توحید بازنگشتند [و ما هم هلاکشان نکردیم،] بلکه آنان و پدرانشان را از نعمت‌های خود بهره مند ساختم تا آن که قرآن، این کتابِ سراسر حق، و پیامبری که درستیِ نبوّتش آشکار است برای آنان آمد.'),
(4355, 43, 30, 'و هنگامی که حق برای آنان آمد گفتند: این افسون است و ما آن را نخواهیم پذیرفت.'),
(4356, 43, 31, 'و گفتند: چرا این قرآن بر مردی بزرگ از این دو شهر (مکّه یا طائف) فرو فرستاده نشده است؟'),
(4357, 43, 32, 'شگفتا! آیا آنان رحمت پروردگارت را تقسیم می‌کنند تا نبوّت را به هر که بخواهند عطا کنند؟ ماییم که حتی وسیله گذران آنان را در زندگی دنیا میانشان تقسیم کرده، و برخی از آنان را بر برخی دیگر به درجاتی برتری داده‌ایم تا یکدیگر را به خدمت گیرند و زندگی اجتماعی خود را سامان بخشند. و نبوّت که رحمت پروردگار توست بهتر از مال و ثروتی است که آنان گرد می‌آورند. پس چگونه ممکن است اعطای این مقام را برعهده آنان بگذاریم؟'),
(4358, 43, 33, 'دارایی‌ها و زر و زیور دنیا نزد خدا بی ارزش است، و لذا اگر تقدیر الهی بر این نبود که همه مردم ـ چه مؤمن و چه کافر ـ در دستیابی به وسایل زندگی یکسان باشند، برای خانه‌های کسانی که به خدای رحمان کفر میورزند سقف‌هایی از نقره قرار می‌دادیم و نیز پله‌هایی که بر آنها آشکار شوند. (بر آنها برآیند و خود را به دیگران بنمایانند.)'),
(4359, 43, 34, 'و برای خانه هایشان درهایی عالی، و تخت‌هایی مجلل که بر آن تکیه زنند، قرار می‌دادیم;'),
(4360, 43, 35, 'و نیز زیوری در نهایت زیبایی، و اینها چیزی جز متاع زندگی این دنیای ناپایدار نیست، و سرای آخرت به حکم پروردگارت از آنِ تقواپیشگان است.'),
(4361, 43, 36, 'و هر کس خود را به کوری زند و از یاد خدای رحمان روی برتابد، شیطانی را بر او می‌گماریم و آن همواره دمساز و همنشین او خواهد بود.'),
(4362, 43, 37, 'و قطعاً شیطان‌ها آنان را از راه خدا باز می‌دارند و آنان خود را هدایت یافته می‌پندارند.'),
(4363, 43, 38, 'شیطان‌ها به اغواگری و اغوا شدگان به خیالِ حق پویی روزگار به سر می‌برند، تا آن گاه که با فرا رسیدن قیامت، هر یک از آنان همراه شیطانِ دمسازش نزد ما آید، به همنشین خود گوید: ای کاش میان من و تو فاصله‌ای به دوری مشرق و مغرب بود، که تو همنشین بدی هستی.'),
(4364, 43, 39, 'ای اغواشدگان و ای شیطان‌های اغواگر، شما از آن روی که به یکدیگر ستم کردید، امروز شریک بودنتان در این عذاب به شما سودی نمی‌بخشد و مایه تسلّی خاطرتان نمی‌شود.'),
(4365, 43, 40, 'ای پیامبر، گمراه شدگانی که خود را ره یافته می‌انگارند، کر و کورند; آیا تو می‌توانی به کران سخنی بشنوانی یا کوران را راه بنمایی؟ و آیا می‌توانی کسی را که در بیراهه‌ای آشکار قرار گرفته است رهنمون باشی؟'),
(4366, 43, 41, 'پس اگر پیش از آن که آنان را به عذاب گرفتار کنیم تو را از این جهان ببریم، قطعاً بعد از تو از آنان انتقام خواهیم گرفت.'),
(4367, 43, 42, 'یا اگر بخواهیم در این جهان آنان را عذاب کنیم و آنچه آنان را بدان تهدید کرده‌ایم به تو بنمایانیم، به یقین ما بر آنان تواناییم.'),
(4368, 43, 43, 'پس به آنچه بر تو وحی شده است چنگ بزن و به آن پای بند باش که تو بر راهی راست قرار داری.'),
(4369, 43, 44, 'و همانا این قرآن برای تو و امّتت وسیله یادآوری خداست، و دیری نخواهد گذشت که درباره آن از شما سؤال خواهد شد.'),
(4370, 43, 45, 'و از پیامبران ما که پیش از تو به رسالت فرستادیم بپرس که آیا جز خدای رحمان معبودانی قرار داده‌ایم که مورد پرستش قرار گیرند؟'),
(4371, 43, 46, 'و همانا موسی را با نشانه‌های خود به سوی فرعون و مهتران قومش به رسالت فرستادیم. پس موسی به آنان گفت: من فرستاده پروردگار همه جهان هایم.'),
(4372, 43, 47, 'پس وقتی آیات ما را که نشانگر حقّانیت او بود، به آنان ارائه داد، بی آن که انتظار رود به آنها خندیدند.'),
(4373, 43, 48, 'و ما هیچ معجزه‌ای به آنان ارائه ندادیم مگر این که آن از همتایش (معجزه دیگر) بزرگ‌تر بود; پس چون آنها را به مسخره گرفتند، به عذاب گرفتارشان کردیم، تا شاید دست از گردنکشی بدارند و رسالت موسی را بپذیرند.'),
(4374, 43, 49, 'و آن گاه که به ستوه آمدند، به موسی گفتند: ای افسونگر، پروردگارت را به عهدی که نزد تو دارد برای ما بخوان تا این عذاب را از ما بردارد که قطعاً رهنمود تو را خواهیم پذیرفت.'),
(4375, 43, 50, 'پس هنگامی که عذاب را از آنان برداشتیم، بی آن که انتظار رود عهد خویش شکستند.'),
(4376, 43, 51, 'و فرعون در میان قومش بانگ برآورد و گفت: ای قومِ من، آیا پادشاهی مصر و این جویبارهای رود نیل که از زیر کاخ من روان است، از آنِ من نیست؟ آیا نمی‌بینید؟'),
(4377, 43, 52, 'آیا موسی بهتر است یا من؟ آیا من از این کسی که بی مقدار است و نزدیک است که نتواند مقصود خود را بیان کند، بهتر نیستم؟'),
(4378, 43, 53, 'اگر او پیامبر است و از دیگران برتر، چرا دستبندهایی زرّین بر او افکنده نشده است تا از بزرگی و سروری نشانی داشته باشد، یا چرا فرشتگانی با او نیامده‌اند تا همدوش یکدیگر رسالتش را تصدیق کنند؟'),
(4379, 43, 54, 'پس فرعون با این سخن قومش را سبک مغز ساخت در نتیجه آنان هم از او فرمان بردند، چرا که مردمی فاسق بودند.'),
(4380, 43, 55, 'پس هنگامی که به سببِ فسقشان ما را به خشم آوردند، از آنان انتقام گرفتیم و همه آنان را غرق کردیم.'),
(4381, 43, 56, 'و آنان را در ورود به آتش دوزخ پیشقدم ساختیم و وسیله عبرت دیگران قرار دادیم.'),
(4382, 43, 57, 'و هنگامی که وصف عیسی پسر مریم در قرآن بیان شد، ناگهان قوم تو از شنیدن آن غوغا سر دادند و از روی تمسخر خندیدند.'),
(4383, 43, 58, 'و گفتند: آیا خدایان ما برای پرستش بهترند یا او؟ آنان این سخن را جز برای جدال با تو، نگفتند، بلکه آنان مردمی ستیزه گرند.'),
(4384, 43, 59, 'پسر مریم نبود جز بنده‌ای که آداب بندگی به جای آورد و ما نعمت پیامبری به او ارزانی داشتیم و او را آیتی قرار دادیم تا حق را برای بنی اسرائیل بیان کنیم.'),
(4385, 43, 60, 'در شگفت نباشید از این که ما بشری را با کمالات فرشتگان آفریدیم; اگر بخواهیم از میان شما نیز کسانی را در زمین قرار می‌دهیم که کار فرشتگان کنند و جایگزین یکدیگر شوند.'),
(4386, 43, 61, 'و به راستی عیسی پسر مریم از آن روی که بدون پدر آفریده شد و مردگان را زنده می‌کرد، نشانه‌ای برای امکان تحقق رستاخیز است; پس در آن تردید مکنید و مرا پیروی کنید که راه راست این است.'),
(4387, 43, 62, 'و مبادا شیطان شما را از راه به در کند، که قطعاً او برای شما دشمنی آشکار است.'),
(4388, 43, 63, 'و هنگامی که عیسی آن معجزات روشن را آورد، گفت: همانا من برای شما حکمت آورده‌ام، و آمده‌ام تا حکم برخی از چیزهایی را که در آن اختلاف می‌کنید برای شما بیان کنم; پس از عذاب خدا پروا کنید و از من که فرستاده او هستم فرمان برید.'),
(4389, 43, 64, 'قطعاً خداست که پروردگار من و پروردگار شماست; پس او را بپرستید که راه راست این است.'),
(4390, 43, 65, 'امّا گروه‌هایی که در میان امّتِ عیسی پدید آمدند، درباره او اختلاف کردند; گروهی او را انکار نمودند و گروهی درباره او غلو کرده خدایش خواندند و گروهی بر راه درست استوار ماندند. غالیان و منکران ستمکارند و وای بر ستمکاران از عذابِ روزی دردناک!'),
(4391, 43, 66, 'آیا جز این انتظار می‌برند که رستاخیز ـ در حالی که از آن غافلند ـ ناگهان بر آنان در رسد؟'),
(4392, 43, 67, 'در آن روز، دوستان و یاران ـ به جز تقواپیشگان ـ همه دشمن یکدیگرند.'),
(4393, 43, 68, 'به تقواپیشگان خطاب می‌شود: ای بندگان من، امروز نه هیچ ترسی بر شما است و نه اندوهگین می‌شوید.'),
(4394, 43, 69, 'ای کسانی که به آیات ما ایمان آوردید و تسلیم فرمان ما بودید،'),
(4395, 43, 70, 'شما و همسران با ایمانتان، شادمان و خندان به بهشت درآیید.'),
(4396, 43, 71, 'کاسه‌هایی زرّین و جام‌هایی بر آنان می‌گردانند تا از خوراکی‌ها و نوشیدنی‌ها بهره برند، و در آن جا هر چه دل‌ها بدان رغبت کند و چشم‌ها از دیدنش لذّت برد وجود خواهد داشت و شما در آن جاودان خواهید بود.'),
(4397, 43, 72, 'و این است آن بهشتی که در برابر آنچه انجام می‌دادید به شما داده شده است.'),
(4398, 43, 73, 'در این جا برای شما میوه‌های فراوان خواهد بود که تنها بخشی از آنها را می‌خورید; چرا که پایان ناپذیرند.'),
(4399, 43, 74, 'به یقین گنهکاران در عذاب دوزخ جاودانند.'),
(4400, 43, 75, 'عذاب از آنان کاسته نمی‌شود و در آن جا از رحمت خدا ناامیدند.'),
(4401, 43, 76, 'و ما به آنان ستم نکردیم، بلکه آنان به خود ستم کردند که با کارهای ناشایسته خود این عذاب را برای خویش فراهم آوردند.'),
(4402, 43, 77, 'و دوزخیان بانگ برمی آورند: ای نگهبانِ دوزخ، از پروردگارت بخواه که بر مرگ ما حکم کند و نابودمان سازد. نگهبان دوزخ می‌گوید: شما در این عذاب دردناک ماندگار خواهید بود.'),
(4403, 43, 78, 'ما فرشتگان برای شما آدمیان حق را آوردیم، ولی بیشترتان حق را خوش نداشتید.'),
(4404, 43, 79, 'آنان نه تنها حق را خوش نداشتند، بلکه بر ضدّ پیامبر نیرنگی سخت به کار بردند، و این ماییم که بر ضدّ آنان استوارکننده کید هستیم.'),
(4405, 43, 80, 'آیا می‌پندارند که ما آنچه را در درون خویش با خود می‌گویند و آنچه را با یکدیگر به راز می‌گویند نمی‌شنویم؟ چرا، همه آنها را می‌شنویم، و فرستادگان ما که نزد آنان برای ثبت اعمالشان حضور دارند، آنها را می‌نویسند.'),
(4406, 43, 81, 'ای پیامبر، به آنان که خدا را صاحب فرزند می‌انگارند، بگو: اگر خدای رحمان را فرزندی باشد من نخستین پرستنده او خواهم بود.'),
(4407, 43, 82, 'منزّه است پروردگار آسمان‌ها و زمین، خداوندگار عرش، از آنچه او را بدان وصف می‌کنند.'),
(4408, 43, 83, 'پس رهایشان کن، آنان در ناراستی‌ها فرو می‌روند و در دنیا به بازی و سرگرمی می‌پردازند تا آن روزشان را که به آنان وعده داده می‌شد دیدار کنند.'),
(4409, 43, 84, 'و اوست که در آسمان خدایی است شایسته عبادت و در زمین نیز خدایی است سزاوار پرستش، و اوست که حکیم و داناست.'),
(4410, 43, 85, 'و پر خیر و برکت است آن که فرمانروایی آسمان‌ها و زمین و آنچه میان آنهاست از آنِ اوست و علم به زمانِ برپایی قیامت نزد اوست و به سوی او باز گردانده می‌شوید.'),
(4411, 43, 86, 'و کسانی را که مشرکان به جای او می‌پرستند اختیار شفاعت ندارند; فقط کسانی می‌توانند شفاعت کنند که به یکتایی خدا، آن باورِ درست، اعتراف کرده باشند و حال و کار کسی را که در حق او شفاعت می‌کنند، بدانند.'),
(4412, 43, 87, 'و اگر از مشرکان بپرسی چه کسی آنان را آفریده است؟ قطعاً خواهند گفت: (خدا). پس تا کی از حق بازداشته و به سوی باطل کشانده می‌شوند؟'),
(4413, 43, 88, 'و خداوند به سخن پیامبر آگاه است که گفت: پروردگارا، اینان مردمی هستند که ایمان نمی‌آورند.'),
(4414, 43, 89, 'پس تو ای پیامبر، از آنان روی بگردان و با مسالمت با آنان وداع کن و ترکشان نمای، که به زودی فرجام کار خود را خواهند دانست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حا، میم.'),
(4416, 44, 2, 'سوگند به این کتاب روشنگر،'),
(4417, 44, 3, 'که ما آن را در شبی پربرکت (شب قدر) فرو فرستادیم، چرا که ما همواره با فرستادن وحی به سوی پیامبران، مردم را هشدار داده‌ایم.'),
(4418, 44, 4, 'در آن شب هر امر سربسته و مجملی که حدود آن نامشخص است باز و مشخص می‌گردد و قرآن نیز در چنین شبی از مرحله اجمال به مرحله تفصیل درآمده است.'),
(4419, 44, 5, 'آری، قرآن به فرمانی از جانب ما فرود آمد، چرا که سنّت ما بر فرستادن پیامبران و ابلاغ وحی به آنان بوده است.'),
(4420, 44, 6, 'قرآن نازل شد تا از جانب پروردگارت رحمتی برای مردم باشد; به یقین اوست که شنونده درخواست بندگان و دانا به نیازهای آنان است.'),
(4421, 44, 7, 'همان پروردگار آسمان‌ها و زمین و آنچه میان آنهاست. اهل یقین این حقیقت را دریافته‌اند. شما هم اگر اهل یقین باشید آن را درخواهید یافت.'),
(4422, 44, 8, 'هیچ معبودی جز او نیست. همواره حیات می‌بخشد و می‌میراند. پروردگار شما و پروردگار پدران نخستین شماست.'),
(4423, 44, 9, 'کفرپیشگان قریش، به قرآن و رسالت پیامبر ایمان نمی‌آورند، بلکه از آن در شک و تردید به سر می‌برند و با پرداختن به امور دنیا به بازی سرگرمند.'),
(4424, 44, 10, 'پس در انتظار روزی باش که آسمان دودی آشکار برآورَد;'),
(4425, 44, 11, 'دودی که این مردم را فرا می‌گیرد و مردم در آن روز می‌گویند: این عذابی است دردناک.'),
(4426, 44, 12, 'پروردگارا، این عذاب را از ما بگردان که ما ایمان می‌آوریم.'),
(4427, 44, 13, 'از کجا آنان متذکّر می‌شوند و به حق اعتراف می‌کنند، در حالی که پیامبری به سویشان آمد که درستیِ رسالتش آشکار بود.'),
(4428, 44, 14, 'ولی از او روی برتافتند و گفتند: او آموزش دیده و او عقل خود را از دست داده است.'),
(4429, 44, 15, 'ما این عذاب را برای زمانی کوتاه از شما برمی داریم ولی در آخرت به سوی عذاب باز خواهید گشت،'),
(4430, 44, 16, 'در روزی که آنان را با آن گرفتنِ بزرگ بگیریم. ما قطعاً از آنان انتقام خواهیم گرفت.'),
(4431, 44, 17, 'و به یقین، پیش از آنان قوم فرعون را آزمودیم، بدین طریق که پیامبری بزرگوار برایشان آمد،'),
(4432, 44, 18, 'با این پیام که: بنی اسرائیل را که بندگان خدایند به من بسپارید، زیرا من برای شما فرستاده‌ای امینم و نمی‌خواهم به کمک آنان بر شما شورش کنم.'),
(4433, 44, 19, 'و با دروغ انگاشتن رسالت من، بر خدا سرکشی مکنید که من برای شما حجّتی آشکار آورده‌ام.'),
(4434, 44, 20, 'و من به خدا که پروردگار من و پروردگار شماست پناه برده‌ام از این که سنگسارم کنید; پس شما را یارای این کار نیست.'),
(4435, 44, 21, 'و اگر به من ایمان نمی‌آورید، از من کناره گیری کنید و کاری به من نداشته باشید.'),
(4436, 44, 22, '[فرعونیان رسالت موسی را نپذیرفتند.] پس موسی پروردگار خود را خواند که: اینان مردمی تبهکار و درخور هلاکت‌اند.'),
(4437, 44, 23, '[خدا به او فرمود:] بندگانم (بنی اسرائیل) را شبانه به سوی دریا کوچ ده که شما قطعاً تعقیب خواهید شد.'),
(4438, 44, 24, 'و چون دریا را با عصایت گشودی و از آن گذشتید، آن را همچنان باز بگذار تا فرعونیان به آن درآیند، که آنان لشکری هستند که قطعاً در آن غرق خواهند شد.'),
(4439, 44, 25, 'چه بوستان‌های پر درخت و چشمه سارهایی که از خود برجای نهادند،'),
(4440, 44, 26, 'و کشتزارها و جایگاه‌هایی نیکو که در آنها کاخ‌ها و خانه‌های زیبا ساخته بودند،'),
(4441, 44, 27, 'و نعمت‌هایی که در آن بسر می‌بردند و گفتگوهایی خوش داشتند.'),
(4442, 44, 28, 'آری، ماجرا این گونه بود، و هنگامی که ما آنان را هلاک کردیم، همه آن نعمت‌ها را به مردمی دیگر (بنی اسرائیل) به میراث دادیم.'),
(4443, 44, 29, 'و نه آسمان بر آنان گریه کرد و نه زمین (هلاکتشان در جهان هستی تأثیری نگذاشت) و چون فرمان خدا به نابودی آنان رفته بود مهلت نیافتند و بی درنگ نابود شدند.'),
(4444, 44, 30, 'و به راستی بنی اسرائیل را از آن عذاب خفّت آور رهانیدیم;'),
(4445, 44, 31, 'از اسارت فرعون که متکبّر و از تجاوزکاران بود.'),
(4446, 44, 32, 'و قطعاً از روی آگاهی بنی اسرائیل را برگزیدیم و بر جهانیان برتری بخشیدیم.'),
(4447, 44, 33, 'و به آنان از معجزات چیزهایی دادیم که در آنها آزمونی آشکار برای ایشان بود.'),
(4448, 44, 34, 'همانا اینان (مشرکان مکّه و دیگر بت پرستان عرب) می‌گویند:'),
(4449, 44, 35, 'فرجام حیات ما جز همین مرگِ نخستین که با آن نابود می‌شویم چیزی نیست و ما قطعاً برانگیخته نخواهیم شد.'),
(4450, 44, 36, 'اگر راست می‌گویید، پدران ما را زنده کنید و آنان را باز آورید.'),
(4451, 44, 37, 'آیا اینان بهترند یا قوم (تُبّع) پادشاه یمن، و کسانی که پیش از آنان بودند؟ ما قوم تبّع و امّت‌هایی را که پیش از آنان بودند هلاک ساختیم، زیرا آنها مردمی تبهکار بودند.'),
(4452, 44, 38, '[چگونه مرگ نابودی است؟ در صورتی که] ما آسمان‌ها و زمین و آنچه را که میان آن دو است نیافریدیم که بازی کرده باشیم.'),
(4453, 44, 39, 'ما آنها را جز به حق (هدفدار) نیافریده‌ایم. این برای ضرورت برپایی رستاخیز برهانی روشن است ولی بیشترشان نمی‌دانند.'),
(4454, 44, 40, 'به یقین روز قیامت که روز جداسازی حق از باطل و تمایز تقواپیشگان از مجرمان است، میعاد همه آدمیان است.'),
(4455, 44, 41, 'روزی که هیچ مهتری امرِ زیردست خویش را کفایت نمی‌کند و ذرّه‌ای از گرفتاری او را برطرف نمی‌سازد و آنان از سوی هیچ کس یاری نمی‌شوند.'),
(4456, 44, 42, 'مگر کسی که خدا بر او رحمت آورَد و شفاعتِ شفاعت کننده‌ای را در حق او بپذیرد. همانا خداوند مقتدری شکست ناپذیر است و هر که را بخواهد مورد رحمت خود قرار می‌دهد.'),
(4457, 44, 43, 'به راستی درخت زقّوم،'),
(4458, 44, 44, 'خوراک گناه پیشه است.'),
(4459, 44, 45, 'همانند فلز ذوب شده است و در شکم‌ها می‌جوشد;'),
(4460, 44, 46, 'همچون جوشیدن آب جوشان.'),
(4461, 44, 47, 'ای فرشتگانِ مأمور دوزخ، آن گناه پیشه را بگیرید و به میانه دوزخ سوقش دهید.'),
(4462, 44, 48, 'سپس بر فراز سرش از آب جوشانی که بدان عذاب شود، فرو ریزید.'),
(4463, 44, 49, 'و به او گویید: بچش! همانا تویی آن که خود را برای همیشه مقتدر و ارجمند پنداشتی.'),
(4464, 44, 50, 'ای کفرپیشگان، این است آنچه در آن تردید روا می‌داشتید.'),
(4465, 44, 51, 'به یقین تقواپیشگان در جایگاهی امن قرار دارند;'),
(4466, 44, 52, 'در بوستان‌ها و در کنار چشمه سارانی.'),
(4467, 44, 53, 'آنان جامه‌هایی از حریرِ نازک و دیبای ضخیم بر تن می‌کنند و با اُنس و اُلفت رو به روی هم می‌نشینند.'),
(4468, 44, 54, 'آری، این گونه است; و آنان را با حوریانی که چشمانی سیاه و درشت دارند قرین می‌سازیم.'),
(4469, 44, 55, 'در آن جا هر نوع میوه ای می‌طلبند و از زیانش در امانند.'),
(4470, 44, 56, 'در آن جا [تلخی] مرگ را نمی‌چشند و برای همیشه زنده و جاویدند. مرگشان همان مرگِ نخستین بود که حیات دنیا را بدان سپری کردند، و هرگز تیره بخت نمی‌شوند، زیرا خدا آنان را از عذاب دوزخ مصون داشته است.'),
(4471, 44, 57, 'تا از جانب پروردگارت بر تقواپیشگان تفضّلی باشد، و این است سعادت بزرگ.'),
(4472, 44, 58, 'جز این نیست که ما فهم مطالب قرآن را با نزول آن به زبان تو (زبان عربی) آسان ساختیم، باشد که قومت آنها را دریابند و متذکّر شوند.'),
(4473, 44, 59, 'ولی آنان متذکّر نشدند; پس منتظر عذابی برای تکذیب کنندگان باش که آنان نیز در انتظار خواهند بود.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حا، میم.'),
(4475, 45, 2, 'این است آن کتاب فرو فرستاده شده از جانب خداوند شکست ناپذیر و حکیم.'),
(4476, 45, 3, 'به راستی در آفرینش آسمان‌ها و زمین، برای مؤمنان نشانه‌هایی بر یکتایی خداست.'),
(4477, 45, 4, 'و در آفرینش شما آدمیان و جنبندگانی که در سراسر گیتی پراکنده می‌سازد نیز برای مردمی که اهل یقین اند نشانه‌هایی بر وجود خدا و تدبیر اوست.'),
(4478, 45, 5, 'و در کاستی و فزونی ساعات شب و روز و در بارانی که روزی شما را در پی دارد و خدا آن را از آسمان فرو فرستاده و زمین را پس از مردنش بدان حیات بخشیده است و نیز در فرستادن بادها از سویی به سوی دیگر، برای مردمی که با خردِ خویش حق را از باطل تشخیص می‌دهند نشانه‌هایی از یکتایی خدا و تدبیر او وجود دارد.'),
(4479, 45, 6, 'این آیات قرآن را که به حق بر تو تلاوت می‌کنیم، خدا آنها را فرو فرستاده است. پس بعد از خدا که این گونه آیات را فرو فرستاده و بعد از آیات او که بر تو تلاوت می‌شود، به کدامین سخن ایمان می‌آورند؟'),
(4480, 45, 7, 'هلاکت باد بر هر کسی که دروغ پرداز و گناه پیشه است;'),
(4481, 45, 8, 'همان که آیات خدا را که بر او تلاوت می‌شود می‌شنود، آن گاه از روی تکبّر بر کفر خویش اصرار میورزد; چنان که گویی آنها را نشنیده است; پس او را به عذابی دردناک بشارت ده.'),
(4482, 45, 9, 'و هنگامی که به بخشی از آیات ما آگاهی یابد، آنها را به مسخره می‌گیرد. برای این دروغ پردازانِ گناه پیشه که در برابر حق گردنکشی می‌کنند و آیات ما را به تمسخر می‌گیرند، عذابی خفّت آور خواهد بود.'),
(4483, 45, 10, 'دوزخ را پشت سردارند. نه مال و منالی که به دست آورده‌اند و نه سرپرستانی که به جای خدا گرفته‌اند، هیچ یک برای آنان اندک چیزی را کفایت نمی‌کند و عذابی بزرگ خواهند داشت.'),
(4484, 45, 11, 'این قرآن مایه هدایت است، و برای کسانی که به آیات پروردگارشان کفر ورزیده‌اند عذابی دردناک، از #معانی'),
(4485, 45, 12, 'خداست که دریا را به تسخیر شما درآورد تا کشتی‌ها به فرمان او در آن روان شوند و با سوار شدن بر آنها روزیِ الهی را که بخشش اوست بجویید و باشد که خدا را در برابر این نعمت سپاسگزاری کنید.'),
(4486, 45, 13, 'و آنچه در آسمان‌ها و آنچه در زمین است همه را برای شما رام ساخت. اینها از جانب خداست. به راستی در این امور برای مردمی که می‌اندیشند نشانه‌هایی بر یکتایی خدا و تدبیر اوست.'),
(4487, 45, 14, 'ای پیامبر، به کسانی که ایمان آورده‌اند بگو: از بدرفتاری‌های گردنکشان که به روزهای خدا امیدی ندارند، درگذرند و متعرّض آنان نشوند، اگر به آنان بگویی همان کنند که تو می‌گویی. خدا این فرمان را صادر کرده است تا خود در یکی از آن روزها مردمی کفرپیشه را به سزای گناهانی که فراهم آورده‌اند کیفر دهد.'),
(4488, 45, 15, 'آری، دستاوردِ مردمان به هدر نمی‌رود; هر کس کار شایسته کند به سود خویش کرده، و هر کس بدی کند به زیان خویش مرتکب شده است; سپس به سوی پروردگارتان باز گردانده می‌شوید و براساس کردارتان سزا خواهید دید.'),
(4489, 45, 16, 'و از این روست که ما برای آدمیان دین را مقرّر داشتیم و به بنی اسرائیل تورات و تکالیف مقرر شده در کتاب الهی و نبوّت عطا کردیم و از چیزهای پاکیزه (همچون (مَنّ) و (سَلوی)) به آنان روزی دادیم و آنان را بر جهانیان برتری بخشیدیم.'),
(4490, 45, 17, 'و به آنان دلایلی روشن که راه خدا را می‌نمود و هر شبهه‌ای را در امر دین می‌زدود عطا کردیم، ولی آنان راه تفرقه را پیمودند و در امر دین دستخوش اختلاف شدند، آن هم پس از آن که دانش دین برایشان حاصل شده بود، ولی به خاطر برتری جویی و ستمی که میانشان بود به اختلاف و تفرقه روی آوردند. به یقین پروردگارت روز قیامت میانشان درباره آنچه بر سرش اختلاف می‌کردند داوری خواهد کرد.'),
(4491, 45, 18, 'سپس تو را ـ ای پیامبر ـ در امر دین بر طریقه خاصی که آیین اسلام است، نهادیم. بنابراین از آن پیروی کن و از خواسته‌های کسانی که نمی‌دانند پیروی مکن.'),
(4492, 45, 19, 'زیرا آنان هرگز تو را از نیازی که به خدا داری ذره‌ای کفایت نمی‌کنند. کسانی که پیرو هوای خویشند و دین گریزند ستمکارند و ستمکاران سرپرستان یکدیگرند و خدا عهده دار امورشان نیست، خدا تنها سرپرستی تقواپیشگان را که از آیین او پیروی می‌کنند برعهده می‌گیرد.'),
(4493, 45, 20, 'این آیینی که برای تو مقرّر کردیم مشتمل بر احکامی است که برای مردم مایه بینش‌ها و برای کسانی که ایمان دارند و راه یقین را می‌پویند مایه هدایت و رحمت است.'),
(4494, 45, 21, 'آیا کسانی که مرتکب کارهای ناروا شده‌اند پنداشته‌اند که آنان را مانند کسانی قرار می‌دهیم که ایمان آورده و کارهای شایسته کرده‌اند، به گونه‌ای که زندگی و مرگشان با زندگی و مرگ آنان یکسان باشد؟ بد داوری می‌کنند.'),
(4495, 45, 22, 'و خداوند آسمان‌ها و زمین را به حق (نه بیهوده و بی هدف) آفریده، و آنها را براساس عدالت استوار ساخته است تا هر کسی در برابر آنچه به دست آورده است سزا داده شود، و بر آنان ستم نمی‌رود.'),
(4496, 45, 23, 'پس آیا دیدی آن کسی را که معبود خویش را هوای خود قرار داده و با این که حق را شناخته است خدا او را در بیراهه گذارده و بر گوش و دلش مهر نهاده و بر چشمش پرده افکنده است؟ با این وصف، پس از خدا چه کسی او را به راه خواهد آورد؟ آیا درباره این گونه افراد نمی‌اندیشید تا پند گیرید؟'),
(4497, 45, 24, 'و مشرکان گفتند: برای ما انسان‌ها حیاتی جز همین زندگانی ما در دنیا نیست. هماره دسته‌ای از ما می‌میرند و دسته‌ای دیگر حیات می‌یابند. جز گذشت زمانه چیزی ما را فرسوده و نابود نمی‌کند. آنان به این سخن هیچ علمی ندارند و جز از روی حدس و گمان سخن نمی‌گویند.'),
(4498, 45, 25, 'و چون آیات ما که مشتمل بر دلایل روشن بر اثبات معاد است بر آنان تلاوت شود، دلیلشان جز این نیست که می‌گویند: اگر راست می‌گویید که مردگان زنده می‌شوند، پدران ما را زنده کنید و باز آورید.'),
(4499, 45, 26, 'در پاسخ آنان بگو: خداست که شما را حیات می‌بخشد، سپس می‌میراند و آن گاه برای روز قیامت که در آن هیچ شکّی نیست شما را گرد می‌آورد، ولی بیشتر مردم این حقیقتِ تردیدناپذیر را نمی‌دانند.'),
(4500, 45, 27, 'این در حالی است که فرمانروایی آسمان‌ها و زمین از آنِ اوست، پس می‌تواند حکم زنده شدن مردگان را صادر کند. آن روز که قیامت برپا می‌شود، آری آن روز باطل گرایان و یاوه سرایان زیان خواهند دید.'),
(4501, 45, 28, 'در آن روز هر امّتی را زانوزده و ترسان و فروتن می‌بینی. هر امّتی به سوی کارنامه اعمالش فراخوانده می‌شود تا براساس آن مورد حساب قرار گیرد، و به آنان گفته می‌شود: آنچه انجام می‌دادید، امروز همان را سزای شما خواهند نمود.'),
(4502, 45, 29, 'این کارنامه شما که با لوح محفوظ مطابق است نوشته ماست و به حق بر ضدّ شما شهادت می‌دهد، زیرا ما اعمال شما را از لوح محفوظ نسخه برداری می‌کردیم.'),
(4503, 45, 30, 'پس کسانی که ایمان آورده و کارهای شایسته کرده‌اند، پروردگارشان آنان را به رحمت خویش درمی آورَد. این است آن سعادت آشکار.'),
(4504, 45, 31, 'اما کسانی که کافر شده‌اند، به آنان گفته می‌شود: آیا حجّت‌ها و آیات من هماره بر شما تلاوت نمی‌شد، ولی شما از پذیرفتن آنها تکبّر ورزیدید و مردمی گنهکار بودید؟'),
(4505, 45, 32, 'و چون گفته می‌شد که قطعاً وعده خدا حق است و در وقوع رستاخیز شکّی نیست می‌گفتید: ما نمی‌دانیم رستاخیز چیست، و در مورد آن جز گمانی غیر قابل اعتماد نداریم و به یقین نرسیده‌ایم.'),
(4506, 45, 33, 'و منکران قیامت گناهانی که کرده‌اند آن روز برایشان آشکار می‌شود و عذاب‌هایی که به مسخره می‌گرفتند بر آنان فرود می‌آید.'),
(4507, 45, 34, 'و به آنان گفته می‌شود: از آن روی که شما دیدار امروزتان را به فراموشی سپردید و برای آن آماده نشدید، ما نیز امروز شما را به فراموشی می‌سپریم و در ناگواری‌های آن رهایتان می‌کنیم و جایگاهتان آتش است و شما را یاری کنندگانی نیست.'),
(4508, 45, 35, 'این عذاب بدان سبب است که آیات خدا را به مسخره گرفتید و زندگی دنیا شما را فریب داد. پس امروز نه از آتش به درآورده می‌شوند و نه از آنان می‌خواهند که رضایت خدا را فراهم کنند.'),
(4509, 45, 36, 'پس ستایش، همه از آنِ خداست که پروردگار آسمان‌ها و پروردگار زمین، پروردگار همه جهانیان است.'),
(4510, 45, 37, 'در آسمان‌ها و زمین و در تمام هستی، بزرگی تنها از آنِ اوست، و اوست آن که در آفرینش و تدبیر مقتدر و کارهایش همه استوار و حکیمانه است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حا، میم.'),
(4512, 46, 2, 'این است آن کتاب فرو فرستاده شده از جانب خداوند شکست ناپذیرِ حکیم.'),
(4513, 46, 3, 'ما آسمان‌ها و زمین و آنچه را که میان آن دو است جز به حق و برای سرآمدی معیّن نیافریدیم، پس با فروریختنشان به سوی هدفی که برای آنهاست، خواهند رفت. ولی کسانی که رستاخیز را انکار کرده‌اند، از آنچه بدان هشدار داده شده‌اند رویگردانند.'),
(4514, 46, 4, 'ای پیامبر، به مشرکان بگو: به من خبر دهید، آنچه را که به جای خدا می‌خوانید و می‌پرستید، به من خبر دهید که چه چیزی از زمین را آفریده‌اند تا در زمین شریک خدا باشند؟ آیا بخشی از آسمان‌ها را پدید آورده‌اند تا در آنها با خدا شریک گردند و در نتیجه تدبیر جهان هستی را عهده دار و شایسته پرستش باشند؟ اگر راست می‌گویید که اینها شریک خدایند، کتابی آسمانی که پیش از قرآن نازل شده باشد و بیانگر این ادّعا باشد و یا دانشی که به دست شما رسیده و سند مدّعای شماست، برای من بیاورید.'),
(4515, 46, 5, 'کیست گمراه‌تر از آن کس که به جای خدا کسانی را می‌خواند که تا روز قیامت او را اجابت نمی‌کنند و از دعای پرستندگان خویش غافل و بی خبرند؟'),
(4516, 46, 6, 'و چون مردم از گورها برانگیخته شوند و به سوی محشر روانه گردند، معبودانِ اهل شرک دشمنِ پرستشگران خود شوند و با تبرّی از آنان به عبادتشان کافر گردند.'),
(4517, 46, 7, 'و چون آیات روشن و تردیدناپذیر ما بر آنان تلاوت شود، کسانی که کافر شده‌اند، درباره آن آیاتِ سراسر حق ـ آن گاه که به ایشان رسد ـ می‌گویند: این افسونی است آشکار.'),
(4518, 46, 8, 'بلکه می‌گویند: محمّد خود قرآن را ساخته و آن را کلام خدا می‌شمرد. بگو: اگر من آن را ساخته باشم، خداوند مرا گرفتار عذاب می‌کند و شما را یارای آن نیست که چیزی از عذابِ او را از من بگردانید. او به یاوه سرایی شما درباره قرآن داناتر است. در اثبات الهی بودن قرآن همین بس که خدا میان من و شما گواهی دهد. او بسیار آمرزنده و مهربان است که پیامبران را برای هدایت مردم برمی انگیزد.'),
(4519, 46, 9, 'به آنان بگو: من در رفتار و کردارم راه تازه‌ای برخلاف پیامبرانِ پیشین برنگزیده‌ام و نمی‌دانم با من و شما چه خواهد شد و چه پیشامدی برای ما رخ خواهد داد، مگر این که در این باره به من وحی شود، و من جز از آنچه به سویم وحی می‌شود، پیروی نمی‌کنم و جز پیامبری هشداردهنده که پیامش را به روشنی بیان می‌کند، نیستم.'),
(4520, 46, 10, 'به مشرکان بگو: به من خبر دهید، اگر این قرآن از جانب خدا باشد در حالی که شما بدان کافر شده‌اید، و نیز اگر شاهدی از بنی اسرائیل بر کتابی که معارف آن مانند قرآن است شهادت دهد و از پی آن به قرآن ایمان بیاورد و شما از پذیرش آن تکبّر ورزید، آیا با این وصف شما گمراه نیستید؟ به یقین خداوند ستمکاران را به سوی حق رهنمون نمی‌شود.'),
(4521, 46, 11, 'و کسانی که کافر شدند، درباره مؤمنان گفتند: اگر ایمان به قرآن پسندیده بود، آنان که فرومایه‌اند در ایمان به آن بر ما سبقت نمی‌گرفتند. و چون بدان هدایت نیافته‌اند خواهند گفت: این قرآن دروغی کهنه و قدیمی است.'),
(4522, 46, 12, 'آنان قرآن را دروغ می‌شمرند در صورتی که پیش از آن، کتابِ موسی که پیشوای بنی اسرائیل و مایه رحمت آنان بود وجود داشته است، و این قرآن کتابی است به زبان عربی که تصدیق کننده آن است و با این هدف نازل شده است که ستمکاران را هشدار دهد و برای نیکوکاران بشارتی باشد.'),
(4523, 46, 13, 'همانا کسانی که گفتند: پروردگار ما خداست، سپس بی هیچ انحرافی بر این سخن ثابت قدم ماندند، نه ترسی بر آنان خواهد بود و نه اندوهگین می‌شوند.'),
(4524, 46, 14, 'اینان اهل بهشتند و جاودانه در آن خواهند بود; این پاداش را در برابر کارهای شایسته‌ای که می‌کردند، به آنان می‌دهند.'),
(4525, 46, 15, 'دهم، و در میان نسل من صلاح و شایستگی مقرّر دار تا برای من نسلی نیکو باشند، زیرا من به سوی تو بازگشته‌ام و از کسانی هستم که تسلیم فرمان تواَند.'),
(4526, 46, 16, 'اینان کسانی اند که بهترین کارهایی را که کرده‌اند (واجبات و مستحباتی که انجام داده اند) از آنان می‌پذیریم و در جمع دیگر بهشتیانی که آنان را بخشوده‌ایم از گناهانشان می‌گذریم. آری، خداوند آن وعده راستی را که همواره به آنان داده می‌شد، این گونه وفا می‌کند.'),
(4527, 46, 17, 'و آن کس که به پدر و مادر خود، آن گاه که او را به پذیرش رستاخیز فراخواندند، گفت: اف بر شما، آیا به من وعده می‌دهید که برای حسابرسی زنده و از خاک بیرون آورده می‌شوم و حال آن که امّت‌هایی که پیش از من زیستند و درگذشتند هیچ یک از آنها زنده نشدند؟! و پدر و مادرش از خدا یاری می‌جویند که فرزندشان را به ایمان متمایل سازد، و به وی می‌گویند: وای بر تو! به خدا و رسولش ایمان بیاور که وعده خدا حق است و رستاخیز خواهد آمد. و او در پاسخ می‌گوید: این جز افسانه‌هایی که مردمان نخستین بافته‌اند چیزی نیست.'),
(4528, 46, 18, 'اینان کسانی اند که آن سخن (وعده عذاب) درباره آنان به حقیقت پیوست، در حالی که در میان امّت‌هایی از جنّیان و آدمیان هستند که پیش از آنان درگذشتند. قطعاً اینان زیانکارند.'),
(4529, 46, 19, 'و برای هر یک از این دو طایفه (مؤمنان نیکوکار و کافران تبهکار) مراتبی است که از آنچه به جای آورده‌اند برخاسته است. برای آن است که خداوند آنچه را که باید انجام شود انجام دهد و سزای اعمالشان را به تمام و کمال به آنان بدهد، و به آنان ستم نمی‌شود.'),
(4530, 46, 20, 'و روزی که کافران بر آتش عرضه شوند، به آنان گفته می‌شود: چیزهایی را که خوش می‌داشتید و برای شما لذّت بخش بود در زندگانیِ دنیایتان مصرف کردید و از آنها بهره بردید، اینک برای شما چیزی که از آن لذّت برید باقی نمانده است; پس امروز به سزای این که در زمین به ناروا تکبّر می‌کردید و از حق روی برمی تافتید، و نیز به سزای این که از فرمان خدا خارج می‌شدید، به عذابی که همراه با خفّت و خواری است کیفر می‌شوید.'),
(4531, 46, 21, 'و هود برادر عادیان را یاد کن، آن گاه که قومش را در سرزمین احقاف به عذاب‌های الهی هشدار داد، در حالی که در عصر او و پیش از او نیز پیامبرانی هشداردهنده، گذشتند. هود با این پیام نزد قومش آمد که: جز خدا را نپرستید، زیرا من بر شما از عذاب روزی بزرگ می‌ترسم.'),
(4532, 46, 22, 'قوم او در پاسخ گفتند: آیا نزد ما آمده‌ای تا ما را با دروغ‌هایی که بر هم بافته‌ای از معبودانمان رویگردان سازی؟ اگر از راستگویانی، آن عذابی را که ما را بدان وعده می‌دهی بر سر ما بیاور.'),
(4533, 46, 23, 'هود گفت: آگاهی از فرود آمدن عذاب فقط نزد خداست، و من آنچه را بدان فرستاده شده‌ام به شما ابلاغ می‌کنم. من نه وقت آن عذاب را می‌دانم و نه چگونگی آن را، ولی می‌بینم شما مردمی هستید که نادانی می‌کنید.'),
(4534, 46, 24, 'سرانجام عذابِ موعود همچون ابری در گستره افق پدیدار شد. آنان چون دیدند ابری در پهنه افق است که رو به سوی درّه هایشان دارد، شادمانه گفتند: این ابری است که بر ما باران فرو می‌ریزد! و خدا به آنان گفت: نه، این ابرْ باران زا نیست; این همان عذابی است که شتاب در آن را می‌خواستید. این بادی است که در آن عذابی دردناک است.'),
(4535, 46, 25, 'بر هر کس و هر چیز بگذرد آن را به امر پروردگارش نابود می‌کند. آری، عذاب بر آنان فرود آمد و چنان شدند که جز خانه هایشان چیز دیگری دیده نمی‌شد. ما این گونه مردم گنهکار را سزا می‌دهیم.'),
(4536, 46, 26, 'و قطعاً ما به آنان (قوم عاد) در چیزهایی توانایی داده بودیم که به شما کفرپیشگانِ مکّه در آن‌ها چنان توانایی نداده‌ایم. و برای آنان گوش‌ها و چشم‌ها و قلب‌ها قرار داده بودیم تا بدانها چاره اندیشی کنند و شرّ و بلا را از خود بگردانند، ولی گوش‌ها و چشم‌ها و دل هایشان آن گاه که آیات ما را انکار کردند هیچ به کارشان نیامد و عذابی که مسخره اش می‌کردند بر آنان فرود آمد. اینک شما کفرپیشگان را که قدرتی کمتر از آنان دارید چه چیز از عذاب خدا ایمنی بخشیده است؟'),
(4537, 46, 27, 'و بی گمان، ما مردم آبادی‌هایی را که پیرامون شما بودند هلاک کردیم و آیات و نشانه‌ها را گوناگون گردانیدیم، باشد که از عبادت غیر خدا روی برتابند و به عبادت او روی بیاورند.'),
(4538, 46, 28, 'پس چرا آن کسانی را که به جای خدا معبود خود گرفتند و به وسیله آنها به خدا تقرّب می‌جستند، آنان را در رهایی از عذاب، یاری نکردند بلکه از نظرشان ناپدید شدند؟ آری، آن فرجام شوم، حقیقت پندار دروغینشان و نیز حقیقت گمانی بود که می‌بافتند.'),
(4539, 46, 29, 'و یاد کن زمانی را که گروهی از جنّیان را به سوی تو متوجّه ساختیم، آنان به قرآن گوش می‌سپردند; پس چون بر تلاوت قرآن حضور یافتند به یکدیگر گفتند: ساکت باشید تا گوش فرا دهیم. پس هنگامی که تلاوت قرآن پایان یافت، به سوی قوم خود بازگشتند و به هشدار آنان همّت گماشتند.'),
(4540, 46, 30, 'گفتند: ای هم تباران، همانا ما [آیاتی از [یک کتاب آسمانی را که پس از موسی نازل شده و کتاب‌های آسمانی پیش از خود را تصدیق می‌کند شنیدیم; کتابی که هر کس از آن پیروی کند او را به سوی حق و به راهی راست رهنمون می‌شود.'),
(4541, 46, 31, 'ای هم تباران، این پیامبر را که به سوی خدا دعوت می‌کند اجابت کنید و به او ایمان بیاورید، که اگر ایمان بیاورید خدا گناهان گذشته شما را بر شما می‌بخشد و شما را از عذابی دردناک پناه می‌دهد.'),
(4542, 46, 32, 'و کسانی که دعوت کننده به سوی خدا را اجابت نکنند و به او ایمان نیاورند، [بدانند که [هرگز در پهنه زمین از سلطه خدا خارج نمی‌شوند و جز خدا سرپرستانی ندارند که آنان را یاری دهند. اینان در گمراهی آشکاری فرو رفته‌اند.'),
(4543, 46, 33, 'آیا منکران رستاخیز به روشنی ندانسته‌اند که آن خدایی که آسمان‌ها و زمین را پدید آورد و در آفرینش آنها درمانده نشد، قادر است که همه مردگان را زنده کند؟ آری، او بر این کار قدرت دارد، زیرا او به هر چیزی تواناست.'),
(4544, 46, 34, 'و روزی که منکران رستاخیز بر آتش عرضه می‌شوند، به آنان گفته می‌شود: آیا این آتش حق نیست؟ می‌گویند: چرا، سوگند به پروردگارمان که حق است. خدا به آنان می‌گوید: پس به سزای این که کفر می­ورزیدید این عذاب را بچشید!'),
(4545, 46, 35, 'پس ای پیامبر، همان گونه که پیامبرانِ صاحب شریعت صبر کردند تو نیز صبر کن و شتاب در عذاب آنان را مخواه، که زمان عذابشان چندان دور نیست. روزی که آنچه را وعده داده می‌شوند بنگرند، گویی جز بخشی از یک روز را در زمین به سر نبرده‌اند. این قرآن ابلاغی است از جانب خدا. پس آیا جز مردم نافرمان را به هلاکت می‌رسانند؟');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'کسانی که کافر شده و مردم را از راه خدا بازداشته‌اند، خدا کارهایشان را که برای از بین بردن حق انجام داده‌اند تباه و بی نتیجه ساخته است.'),
(4547, 47, 2, 'و آنان که ایمان آورده و کارهای شایسته کرده‌اند و به آنچه بر محمّد فرو فرستاده شده ـ که سراسر حقّ و از جانب پروردگارشان است ـ ایمان آورده‌اند، گناهانشان را از آنان زدوده و حال و کارشان را در دنیا و آخرت اصلاح کرده است.'),
(4548, 47, 3, 'این بدان سبب است که کافران از باطل پیروی کرده و مؤمنان از حق ـ که از ناحیه پروردگارشان است ـ پیروی نموده‌اند. خداوند این گونه برای مردم اوصافشان را بیان می‌کند.'),
(4549, 47, 4, 'ای مؤمنان، اکنون که دریافتید شما بر حقّید و کافران بر باطل، هنگامی که در میدان کارزار با کافران رو به رو شدید، گردن هایشان را بزنید، تا آن گاه که از آنان بسیار کُشتید و بر ایشان چیره گشتید، آنان را به اسارت بگیرید و بندها را محکم کنید. پس از آن یا بر آنان منّت نهید و رهایشان سازید و یا با گرفتن فدیه آزادشان کنید. [شما باید به کارزار ادامه دهید [تا آن گاه که جنگ پایان پذیرد و جنگجویان سلاح‌های خود را بر زمین نهند. این است حکم خدا. اگر خدا می‌خواست خود از آنان انتقام می‌گرفت و هلاکشان می‌ساخت، ولی فرمان جهاد داد تا شما را به وسیله یکدیگر بیازماید. و کسانی که در راه خدا کشته شده‌اند، خدا هرگز کارهای نیکشان را تباه و بی اثر نخواهد کرد.'),
(4550, 47, 5, 'به زودی آنان را به راه سعادت رهنمون می‌شود و با آمرزش گناهانشان حالشان را نیکو می‌کند تا شایسته بهشت و حضور در پیشگاه خدا گردند.'),
(4551, 47, 6, 'پس آنان را به بهشتی که در کتاب‌های آسمانی برایشان وصف کرده است درمی آورد.'),
(4552, 47, 7, 'ای کسانی که ایمان آورده‌اید، اگر خدا را یاری کنید و برای خشنودی او با کافران بجنگید و از دینش حمایت کنید، او نیز با فراهم آوردن اسباب پیروزی، شما را یاری می‌کند و گام هایتان را در میدان نبرد استوار می‌سازد.'),
(4553, 47, 8, 'و کسانی که کافر شده‌اند، نگونساری بر آنان باد، و خدا کارهایشان را بی اثر کند و به مقصد نرساند.'),
(4554, 47, 9, 'این بدان سبب است که آنان آنچه را خدا نازل کرده است خوش نداشتند، و خدا نیز کارهایشان را نابود کرد.'),
(4555, 47, 10, 'آیا در زمین سیر نکرده‌اند تا به دیده عبرت بنگرند که فرجام کسانی که پیش از آنان بودند چگونه شد؟ خداوند آنان را با هر چه به ایشان وابسته بود نابود ساخت. ای پیامبر، نظیر این عقوبت‌ها برای کسانی که به تو کافر شدند نیز خواهد بود.'),
(4556, 47, 11, 'این (یاری دادن مؤمنان و کیفر کردن کافران) بدان جهت است که خدا ولیّ و سرپرست کسانی است که ایمان آورده‌اند، ولی کافران را هیچ کمک کار و سرپرستی نیست.'),
(4557, 47, 12, 'همانا خداوند کسانی را که ایمان آورده و کارهای شایسته کرده‌اند به بهشت‌هایی آکنده از درختان که در زیر آنها نهرها روان است وارد می‌کند. امّا کسانی که کافر شده‌اند، خوی انسانی را رها می‌کنند و زندگی دنیا را به کامرانی می‌گذرانند و بی هیچ هدفی، بدانسان که چهارپایان می‌خورند، می‌خورند، و آتشِ دوزخ جایگاهی برای آنان خواهد بود.'),
(4558, 47, 13, 'و چه بسیار مردم شهرها که از مردم شهر تو که تو را از آن بیرون کردند نیرومندتر بودند و ما آنان را هلاک کردیم و برای آنها هیچ یار و یاوری نبود که به آنان کمک کند.'),
(4559, 47, 14, 'آیا کسانی که بر دلیل روشنی از جانب پروردگارشان تکیه کرده‌اند، مانند کسانی اند که کارهای زشتشان برایشان جلوه داده شده است و از هواهای نفسانی خود پیروی کرده‌اند؟'),
(4560, 47, 15, 'وصف آن بهشتی که به تقواپیشگان وعده داده شده چنین است: در آن نهرهایی است از آبی تغییرناپذیر و نهرهایی از شیر که مزه اش دگرگون نشده و نهرهایی از باده که برای نوشندگان لذیذ است، و نهرهایی از عسلِ خالص شده. و برای آنان در آن جا از هرگونه میوه‌ای فراهم است، و از جانب پروردگارشان آمرزشی خواهند داشت که گناه و ناگواری را از آنان خواهد زدود. آیا کسانی که در این بهشت درمی آیند همچون کسانی اند که در آتش دوزخ جاودانند و به آنان آبی داغ می‌نوشانده‌اند که روده هایشان را پاره پاره کرده است؟'),
(4561, 47, 16, 'و از این مردم کسانی اند که به سخنان تو گوش فرا می‌دهند، ولی هنگامی که از نزد تو بیرون می‌روند، به یارانت که از دانش (معرفت الهی) بهره مند شده اند می‌گویند: او اندکی پیش چه گفت؟ اینان کسانی اند که خدا بر دل هایشان مهر نهاده است و از هواهای نفسانی خود پیروی کرده‌اند.'),
(4562, 47, 17, 'آنان که هدایت یافته و تسلیم فطرت پاک خویش شده و از پی حق رفته‌اند، خدا بر هدایتشان افزوده و تقوای درخورِ آنان را به آنان داده است تا از گناهان پرهیز کنند.'),
(4563, 47, 18, 'آیا کفرپیشگان جز این انتظار می‌برند که قیامت ـ که ناگهان می‌آید ـ بر آنان در رسد؟ به راستی نشانه‌های آن هم اکنون پدید آمده است و آن گاه که فرا رسد یادآور می‌شوند، ولی در آن هنگام که دوران عمل گذشته است چگونه این یادآوری برای آنان سودمند است؟'),
(4564, 47, 19, 'حقیقت این است که هیچ معبودی جز خدا نیست. تو این را می‌دانی; پس بر طبق دانش خود عمل کن و برای گناه خویش و برای مردان و زنان با ایمان آمرزش بخواه، تا از کسانی نباشی که خدا بر دل هایشان مهر می‌نهد، که خدا به احوال شما آگاه است و جنب و جوش و آرام و قرارتان را می‌داند.'),
(4565, 47, 20, 'و کسانی که ایمان آورده اند می‌گویند: چرا سوره‌ای که بیانگر مطالبی جدید است فرو فرستاده نمی‌شود؟ امّا آن گاه که سوره‌ای صریح و روشن نازل می‌شود و در آن از جنگ و جهاد یاد می‌گردد و مؤمنان به نبرد فراخوانده می‌شوند، بیماردلانِ سُست ایمان را می‌بینی که از شدّت ترس، همچون کسی که مرگ بر او سایه افکنده است، به تو می‌نگرند، پس همان مرگ برای آنان سزاوارتر است.'),
(4566, 47, 21, 'ایمانشان اطاعت امری است که بر آن پیمان بستند و اظهار آن سخنی پسندیده است، پس آن گاه که فرمان حتمی شد و به مرحله اجرا رسید، اگر با خدا راست می‌گفتند و فرمانبری می‌کردند، قطعاً برایشان بهتر بود.'),
(4567, 47, 22, 'پس ای سُست ایمانانِ بازمانده از جهاد، اگر از کتاب خدا و احکام آن روی برتابید، آیا این انتظار از شما دور است که با خونریزی و غارتگری، در زمین تباهی پدید آورید و با خویشاوندانتان پیمان و حرمت خویشاوندی را بشکنید؟'),
(4568, 47, 23, 'اینان کسانی اند که خداوند لعنتشان کرده و شنواییشان را از بین برده و دیدگانشان را نابینا ساخته است.'),
(4569, 47, 24, 'آیا در قرآن تدبّر نمی‌کنند؟ بلکه بر دل‌های آنان و هم مسلکانشان قفل‌های مخصوص دل‌ها قرار گرفته است.'),
(4570, 47, 25, 'کسانی که پس از آن که راه هدایت برایشان آشکار شد به عقب بازگشتند و به کفر گراییدند، شیطان کارهای ناپسند را برایشان آراست و آنان را در راه باطل مدد کرد.'),
(4571, 47, 26, 'این (حاکمیت شیطان) بدان جهت است که آنان به کسانی که از قرآن و احکام آن ناخشنود بودند وعده همکاری دادند و گفتند: در پاره‌ای از امور ـ تا آن جا که راز ما فاش نشود ـ از شما پیروی خواهیم کرد [غافل از آن که [خدا نهانکاری آنان را می‌داند.'),
(4572, 47, 27, 'امروز در پشت نقابِ نفاق نوید همکاری به کافران می‌دهند، ولی آن گاه که فرشتگان جانشان را بستانند در حالی که بر صورت و پشتشان می‌کوبند، حال و روزشان چگونه خواهد بود؟'),
(4573, 47, 28, 'این بدان جهت است که آنان از آنچه خدا را به خشم آورده است پیروی کردند و رضای او را خوش نداشتند، و خدا هم کارهای نیکشان را تباه و بی اثر ساخت.'),
(4574, 47, 29, 'بلکه بیماردلانِ سُست ایمان که سرانجام کافر شدند و در زمره منافقان درآمدند، گمان کردند که خداوند هیچ گاه کینه هایشان به دین و دین باوران را آشکار نخواهد ساخت.'),
(4575, 47, 30, 'و اگر می‌خواستیم، منافقان را به تو نشان می‌دادیم و تو آنان را با علامتی که ویژه آنهاست می‌شناختی، و اینک تو ایشان را از نحوه گفتارشان که مشتمل بر گوشه و کنایه است می‌شناسی، و خداوند به حقیقت کارهای شما آگاه است و می‌داند که از آنها چه هدفی را پی می‌گیرید.'),
(4576, 47, 31, 'و قطعاً شما را می‌آزماییم تا از میان شما کسانی را که در راه خدا جهاد می‌کنند و بر دشواری‌های اوامر الهی شکیبایند مشخص کنیم، و کارهای شما را که گزارش‌هایی از شماست، می‌آزماییم.'),
(4577, 47, 32, 'کسانی که پس از آن که راه هدایت برایشان آشکار شد کفر ورزیدند و مردم را از راه خدا بازداشتند و با پیامبر خدا دشمنی کردند و با او به مخالفت برخاستند، هرگز اندک زیانی به خدا نمی‌رسانند و به زودی خدا کارهایشان را که برای از بین بردن اساس دین سامان داده‌اند، تباه و بی اثر خواهد کرد.'),
(4578, 47, 33, 'ای کسانی که ایمان آورده‌اید، خدا را اطاعت کنید و از پیامبرش فرمان برید و به جنگ با دشمنان دین برخیزید و کارهای پسندیده خود را با کوتاهی در امر جهاد تباه مکنید.'),
(4579, 47, 34, 'اگر از خدا و پیامبرش فرمان نبرید و جهاد را ترک کنید، خداوند شما را در زمره کافران به شمار می‌آورد، و قطعاً کسانی که کافر شده و از راه خدا روی برتافته و در حال کفر مرده‌اند، خدا هرگز آنان را نخواهد آمرزید.'),
(4580, 47, 35, 'پس مبادا در جهاد کوتاهی کنید و کافران را به صلح و سازش فراخوانید، که شما برترید و بر آنان پیروز خواهید شد، چرا که نصرت خدا با شماست و از پاداش کارهایتان نمی‌کاهد.'),
(4581, 47, 36, 'جز این نیست که زندگی دنیا بازیچه و سرگرمی است و آدمی را از آخرت باز می‌دارد. شما اگر ایمان بیاورید و تقوا پیشه کنید، خدا مزدتان را به شما خواهد داد و در عوضِ آن همه اموالتان را نمی‌خواهد.'),
(4582, 47, 37, 'اگر خدا همه اموال شما را بخواهد و در پرداخت آنها به شما اصرار ورزد بخل می‌کنید و خدا کینه‌های شما را پدیدار می‌سازد.'),
(4583, 47, 38, 'هان شمایید همان کسانی که برای انفاق بخشی از اموالتان در راه خدا، فراخوانده می‌شوید، ولی گروهی از شما بخل میورزند. و هر کس از انفاق در راه خدا بخل ورزد، جز این نیست که خیر و نیکی را از خود دور ساخته است، زیرا خدا از دارایی‌های شما سودی نمی‌برد، چرا که خداوند بی نیاز است و شما نیازمندید. و اگر از ایمان و تقوا روی برتافتید و از انفاق در راه خدا امتناع کردید، خدا مردمی غیر از شما را جایگزین شما می‌کند، سپس آنان همچون شما نخواهند بود، بلکه ایمان می‌آورند و از اموال خود در راه خدا انفاق می‌کنند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'همانا ما با صلح (حُدَیبیّه) پیروزی درخشانی را برای تو فراهم آوردیم.'),
(4585, 48, 2, 'تا سرانجام با شکستن جاه و جلال مشرکان پیامدهای دعوت تو را که بر آنان ناگوار آمد ـ چه آنها که پیش از هجرت گذشت و چه آنها که پس از آن روی داد ـ و تو را مسئول آنها می‌دانستند و درصدد انتقام بودند، از تو بزداید و نعمت خود را با فراهم آوردن زمینه‌های فتح مکّه بر تو تمام کند و تو را به راهی راست که به گسترش دین و حاکمیّت اسلام می‌انجامد رهنمون شود،'),
(4586, 48, 3, 'و تا خدا تو را به یک پیروزی بی مانند برساند.'),
(4587, 48, 4, 'اوست آن که آرامش را در دل‌های مؤمنان پدید آورد تا ایمانی بر ایمانشان بیفزایند، و خدا را رسد که چنین کند، زیرا اسباب و علل جاری در آسمان‌ها و زمین در اختیار خداست و همه لشکریان او هستند، و خدا دانا و حکیم است.'),
(4588, 48, 5, 'آری آرامش را ویژه مؤمنان ساخت تا مردان و زنان با ایمان را به بوستان‌هایی با درختانی انبوه که زیر آنها نهرها جاری است و در آنها جاودانه‌اند، درآورَد و گناهانشان را از آنان بزداید; و این فرجام نزد خدا سعادتی بزرگ است،'),
(4589, 48, 6, 'و تا مردان و زنان منافق و مردان و زنان مشرک را که به خدا گمان بد می‌بردند عذاب کند. ناگواری بر آنان باد. و خدا بر آنان خشم گرفته و لعنتشان کرده و دوزخ را برای آنان آماده ساخته و آن بد جایگاهی است.'),
(4590, 48, 7, 'و خدا را رسد که با آنان چنین کند، زیرا از آنِ اوست اسباب و علل جاری در آسمان‌ها و زمین، و همه لشکریان او هستند و خدا شکست ناپذیر و حکیم است.'),
(4591, 48, 8, 'ای پیامبر، ما تو را به رسالت فرستادیم تا بر کارهای مردم گواه باشی و مؤمنان تقواپیشه را به پاداشی بزرگ بشارت و کافران را به عذابی دردناک هشدار دهی.'),
(4592, 48, 9, 'آری، ما پیامبر را به رسالت فرستادیم تا شما به خدا و رسولش ایمان بیاورید و با تلاش برای پیشبرد دین، خدا را یاری کنید و او را گرامی بدارید و هر صبح و عصر او را تسبیح گویید.'),
(4593, 48, 10, 'به راستی، کسانی که با تو بیعت می‌کنند، جز این نیست که با خدا بیعت می‌کنند. دست تو به منزله دست خداست که برای بیعت بالای دست‌های آنان قرار دارد. پس هر کس بیعتش را با تو بشکند، بیعت با خدا را شکسته است و خدا از آن زیانی نمی‌بیند، بلکه او به خود زیان رسانده است; و هر کس به آنچه با خدا بر آن پیمان بسته است وفا کند، به زودی خدا پاداشی بزرگ به او خواهد بخشید.'),
(4594, 48, 11, 'ای پیامبر، به زودی اعراب بادیه نشین که از سفر عُمره حدیبیّه تخلّف کردند و همراه تو نیامدند، عذر می‌آورند که اموال و کسانمان ما را به خود مشغول ساخت، برای ما از خدا آمرزش بخواه. آنان دروغ می‌گویند; نه اموال و کسانشان مانع آنان بود و نه از روی حقیقت آمرزش می‌طلبند. آنان به زبان چیزی می‌گویند که در دل هایشان نیست. به آنان بگو: اگر خدا برای شما زیانی بخواهد یا برای شما سودی اراده کند، کیست که از جانب خدا مالک چیزی باشد که آن را در جهت سود یا زیان شما به کار گیرد؟ بلکه خداست که به آنچه می‌کنید آگاه است.'),
(4595, 48, 12, 'نه، مال و کسانتان مانع شما نبود، بلکه بدان سبب پیامبر را در آن سفر همراهی نکردید که پنداشتید رسول خدا و مؤمنان هرگز به سوی خانواده هایشان باز نمی‌گردند و همه به دست مشرکان کشته می‌شوند، و این پندار در دل‌های شما آراسته گشت و گمان بردید که خدا پیامبرش را وامی گذارد، و این گمانی ناروا بود، و شما مردمی فاسد بودید.'),
(4596, 48, 13, 'شما با تخلّف از دستور پیامبر، هم به او و هم به خدا کافر شدید، و هر کس به خدا و رسولش ایمان نیاورد به آتشی افروخته درخواهد آمد، زیرا ما برای کافران آتشی افروخته آماده کرده‌ایم.'),
(4597, 48, 14, 'فرمانروایی آسمان‌ها و زمین تنها از آنِ خداست; هر که را بخواهد می‌آمرزد و هر که را بخواهد گرفتار عذاب می‌کند، و خدا آمرزنده و مهربان است.'),
(4598, 48, 15, 'به زودی خیبر را فتح خواهید کرد، و چون به سوی غنیمت‌های آن روانه شوید تا آنها را به چنگ آورید، مسلمانانی که از سفرِ عُمره تخلّف کردند و همراه پیامبر نرفتند خواهند گفت: ما را واگذارید (مانع ما نشوید) تا از پی شما بیاییم. این در حالی است که خدا غنائم خیبر را فقط به شما اختصاص داده است و متخلّفان می‌خواهند سخن خدا را دگرگون سازند. به آنان بگو: شما هرگز از پی ما نخواهید آمد، زیرا خدا از پیش چنین فرموده است. ولی آنان در پاسخ خواهند گفت: خدا چنین نفرموده است، بلکه شما بر ما حسد میورزید. چنین نیست، بلکه آنان جز اندکی نفهمیده‌اند.'),
(4599, 48, 16, 'ای پیامبر، به بادیه نشینانی که از سفر عُمره حدیبیّه تخلّف کردند بگو: [فرصتی برای جبران در پیش است. [به زودی به نبرد با قومی سخت نیرومند فراخوانده می‌شوید و شما با آنان می‌جنگید یا آنان تسلیم می‌شوند و اسلام می‌آورند. پس اگر فرمان برید و برای جهاد به سوی آنان حرکت کنید، خدا به شما مزدی نیکو عطا می‌کند، و اگر روی برتابید، چنان که پیش‌تر از سفر عمره حدیبیّه نیز روی برتافتید، به عذابی دردناک عذابتان خواهد کرد.'),
(4600, 48, 17, 'ولی بر نابینا گناهی نیست که در آن کارزار شرکت نکند و بر لنگ نیز گناهی نیست و بر بیمار نیز گناهی نیست; و هر کس از خدا و رسولش اطاعت کند، او را در بوستان‌هایی آکنده از درخت که از زیر آنها نهرها روان است در می‌آورد و هر کس از فرمان خدا و رسولش روی برتابد، او را به عذابی دردناک عذاب خواهد کرد.'),
(4601, 48, 18, 'ای پیامبر، قطعاً خدا از مؤمنان آن گاه که با تو در حدیبیّه زیر آن درخت بیعت می‌کردند خشنود شد و نیّت‌های صادقانه آنان را دانست; از این رو آرامش و اطمینان را در آنان پدید آورد، و دیری نمی‌گذرد که فتح خیبر را به آنان پاداش خواهد داد.'),
(4602, 48, 19, 'و غنیمت‌های فراوانی را که در آن نبرد به چنگ می‌آورند نیز نصیبشان خواهد کرد، و خدا شکست ناپذیر و حکیم است.'),
(4603, 48, 20, 'خدا به شما غنیمت‌های فراوانی وعده داده است که آنها را به دست خواهید آورد; از این رو پس از بازگشت از سفر حدیبیّه به زودی اینها (غنایم خیبر) را به شما ارزانی می‌کند و دست مردم [کفرپیشه [را از تعرّض به شما بازمی دارد. خداوند این وعده را به شما داد تا برای همه مؤمنان آیت و نشانه‌ای باشد و از این رهگذر حقانیت خود و درستی وعده پروردگارشان را دریابند، و تا شما مؤمنان را به راهی راست که به برتری حق و گسترش دین می‌انجامد راهنمایی کند.'),
(4604, 48, 21, 'و غنیمت‌های دیگری نیز هست که شما نتوانستید بر آنها دست یابید و خدا بر آنها احاطه و قدرت دارد، و خدا بر هر چیزی تواناست.'),
(4605, 48, 22, 'و اگر کسانی که کافر شده‌اند با شما به کارزار آیند پشت می‌کنند و می‌گریزند، آن گاه نه کسی را می‌یابند که کارشان را برعهده گیرد و نه کسی که آنان را یاری کند.'),
(4606, 48, 23, 'خدا مقرّر داشته است که پیامبران و مؤمنانِ واقعی بر دشمنانشان پیروز شوند. این سنّت خداست که از پیش جاری بوده است و هرگز در سنّت خدا تغییری نخواهی یافت.'),
(4607, 48, 24, 'اوست آن که در وادی مکّه (سرزمین حُدَیْبیّه) شما را بر مشرکان فایق آورد، پس از آن دست‌های آنان را از تعرّض به شما و دست‌های شما را از تعرّض به آنان بازداشت تا کار شما به صلح انجامید، و خدا به آنچه می‌کنید بیناست.'),
(4608, 48, 25, 'آنان همان کسانی اند که کافر شدند و شما را از ورود به مسجدالحرام [برای انجام عُمرِه [بازداشتند و نگذاشتند قربانی‌های شما ـ که باز داشته شده بودند ـ به جایگاهشان برسند. و اگر مردان و زنانی با ایمان که شما آنان را نمی‌شناختید در مکّه نبودند و با هجوم به مکّه آنان را از سر ناآگاهی پایمال می‌کردید و بر اثر کشتن آنان رویدادی ناخوشایند برای شما پیش می‌آمد، شما را از هجوم به مکّه و جنگ با مردم آن باز نمی‌داشتیم، ولی از این کار بازداشته شُدید تا خدا هر که را بخواهد به رحمت خود درآورد. و اگر مؤمنان و مشرکان مکّه مشخّص بودند، قطعاً کافران را با هجوم شما بر آنان به عذابی دردناک عذاب کرده بودیم.'),
(4609, 48, 26, 'مشرکان از آن روی شما را از مسجدالحرام بازداشتند که دل هایشان را از خشم شما پر کرده بودند، آن هم خشم جاهلانه‌ای که به مردم نادان اختصاص دارد. در مقابل، خدا هم آرامشی از جانب خود بر پیامبرش و بر مؤمنان فرو فرستاد تا حرکت و سکونشان همراه با وقار و دور از برخوردهای جاهلانه باشد، و روح ایمان را که به تقوا فرمان می‌دهد ملازم آنان ساخت. آری، مؤمنان به برخورداری از روح ایمان سزاوارتر از دیگران بودند و آنان بودند که شایستگی آن را داشتند، و خدا به هر چیزی کاملاً داناست.'),
(4610, 48, 27, 'سوگند یاد می‌کنم که خدا آن رؤیای درست را که به پیامبر نمایاند، حقیقت را آن گونه که بود برای او آشکار ساخت و به او نشان داد که ـ اگر خدا بخواهد ـ شما ایمن از شرّ مشرکان وارد مسجدالحرام می‌شوید و سرهای خود را می‌تراشید و موهایتان را کوتاه می‌کنید [= عمره به جای می‌آورید [و از آنان نمی‌هراسید، ولی خداوند آن را در سال جاری مقرّر نکرد، زیرا به مصلحتی آگاه بود که شما از آن آگاه نبودید; از این رو برای تحقق کاملِ آن رؤیا، صلح حدیبیّه را که خود فتح نزدیکی بود، پیش از ورودتان به مسجدالحرام پیش آورد.'),
(4611, 48, 28, 'اوست آن که پیامبرش محمّد را همراه با هدایت و دین حقّ به رسالت فرستاد، تا آن دین را بر همه ادیان فایق آورد، و همین بس که خدا به درستیِ رسالت محمّد (ص) و وعده‌های خود گواه باشد.'),
(4612, 48, 29, 'محمّد فرستاده خداست، و کسانی که با اویند، در برابر کافران سرسخت و در میان خود مهربانند. آنان را می‌بینی که در حال رکوع و سجودند (نماز را برپا می‌دارند) و در زندگی دنیا در پی دستیابی به پاداشی از جانب خدا هستند و خشنودی او را می‌جویند. علامت خشوع آنان در برابر خدا که پیامد سجده‌های آنهاست در رخسارشان نمایان است. این صفتِ آنان در تورات و صفتشان در انجیل است. آنان همچون گیاهانی هستند که جوانه‌های خود را پیرامون خویش می‌رویانند و آنها را نیرو می‌بخشند تا سِتَبر شوند و خود بر ساقه‌های خویش استوار بمانند. رشد نیکوی آن زراعت، کشاورزان را خرسند می‌سازد. خداوند مؤمنان را بدین سان بر تعدادشان می‌افزاید و آنان را نیرومند می‌سازد، تا به وسیله آن کافران را به خشم آورَد. خدا به کسانی از یاران پیامبر که همچنان بر ایمان و عمل خویش پایبند مانده و کارهای شایسته کرده‌اند، وعده آمرزش و پاداشی بزرگ داده است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'ای کسانی که ایمان آورده‌اید، به هیچ کاری اقدام نکنید تا حکم آن را از خدا و رسولش دریافت کنید، و هیچ حکمی را پیش از آن که از خدا و رسولش بشنوید، به زبان نیاورید. در پیروی از خدا ـ چه در گفتار و چه در رفتار ـ تقوای الهی را رعایت کنید، چرا که خدا سخن شما را می‌شنود و نهان و آشکارتان را می‌داند.'),
(4614, 49, 2, 'ای کسانی که ایمان آورده‌اید، در گفتوگو با پیامبر صدای خود را از صدای پیامبر بلندتر مکنید، و همچنان که برخی از شما با برخی دیگر بلند سخن می‌گویید با او بلند سخن مگویید، مبادا اعمالتان بدین سبب تباه شود و شما از تباه شدن آن بی خبر باشید.'),
(4615, 49, 3, 'آنان که نزد رسول خدا صدایشان را کوتاه می‌کنند همان کسانی اند که خدا دل هایشان را برای [رسیدن به [تقوا آزموده است; برای آنان آمرزش و اجری بزرگ خواهد بود.'),
(4616, 49, 4, 'کسانی که تو را از پشت اتاق هایت صدا می‌زنند و ادب را رعایت نمی‌کنند بیشترشان نمی‌فهمند.'),
(4617, 49, 5, 'و اگر آنان صبر می‌کردند و تو را از پشت اتاق‌ها صدا نمی‌زدند تا خود به سویشان بیرون روی و با آنان دیدار کنی، برایشان بهتر بود; زیرا با این روش، مقام رسالت را پاس داشته و درخور آمرزش و رحمت الهی می‌شدند، و خدا آمرزنده و مهربان است.'),
(4618, 49, 6, 'ای کسانی که ایمان آورده‌اید، اگر فاسقی برای شما خبری مهم آورد، درباره آن تحقیق کنید، تا مبادا از روی نادانی به قومی آسیب برسانید، آن گاه از آنچه بر سرشان آورده‌اید پشیمان شوید.'),
(4619, 49, 7, 'و بدانید که رسول خدا در میان شماست و شما باید این نعمت را پاس بدارید. اگر او در بسیاری از امور از آرای شما پیروی کند دچار زحمت می‌شوید و به هلاکت می‌افتید و خدا نخواسته است که چنین شود، بلکه ایمان را برای شما دوست داشتنی قرار داد و آن را در دل‌های شما بیاراست و کفر و فسق و گناه را در نظرتان ناخوشایند ساخت تا رشد یابید; و آنان که ایمان را دوست دارند و از کفر بیزارند رشدیافتگان‌اند.'),
(4620, 49, 8, 'آری، این موهبت‌ها به مؤمنان داده شد تا برای آنان از جانب خدا فضل و نعمتی باشد، و این عنایت‌ها بی مورد و گزاف نیست، بلکه خدا به شایستگانِ فضل و نعمت آگاه و کارهای او از روی حکمت است.'),
(4621, 49, 9, 'و اگر دو گروه از مؤمنان با یکدیگر پیکار کنند، میان آنان صلح و سازش برقرار سازید; پس اگر یکی از آن دو گروه بر دیگری ستم کرد و تعدی نمود، با آن گروهی که ستم می‌کند بجنگید تا به فرمان خدا بازگردد و حکم او را گردن نهد. پس اگر بازگشت و حکم خدا را گردن نهاد، میان آن دو گروه صلحی عادلانه برقرار کنید; بدین طریق که اگر گروه تجاوزگر خونی ریخته یا حقی را پایمال کرده حکم خدا را درباره اش اجرا کنید و همواره به عدالت رفتار نمایید، چرا که خدا دادگران را دوست می‌دارد.'),
(4622, 49, 10, 'همه مؤمنان ـ چه آنان که با یکدیگر در ستیزند و چه غیر آنان ـ جز این نیستند که با یکدیگر برادرند، پس بر شما فرض است که میان آن دو گروه از برادرانتان که با یکدیگر در ستیزند صلح و سازش برقرار کنید و همگان تقوای الهی را رعایت کنید، باشد که مورد رحمت قرار گیرید.'),
(4623, 49, 11, 'ای کسانی که ایمان آورده‌اید، نباید مردانی مردانِ دیگر را به مسخره گیرند، شاید آنان نزد خدا از اینان بهتر باشند، و نباید زنانی زنانِ دیگر را به مسخره گیرند، شاید آنان نزد خدا از اینان بهتر باشند، و عیوب یکدیگر را فاش نسازید و یکدیگر را با لقب‌ها و عنوان‌های بد یاد مکنید. یاد کردن مردم به بدی پس از ایمان آوردن آنان نارواست، و کسانی که از این گناهان توبه نکنند آنان ستمکارند.'),
(4624, 49, 12, 'ای کسانی که ایمان آورده‌اید، از گمان‌های بد درباره دیگران ـ که بسیار هم هست ـ بپرهیزید و به آنها ترتیب اثر ندهید، چرا که پاره‌ای از گمان‌ها گناه است، و درصدد کشف عیوب مردم برنیایید، و یکدیگر را غیبت نکنید که غیبت مؤمن بسان این است که کسی گوشت مرده برادر خود را بخورد; آیا یکی از شما دوست دارد گوشت برادرش را که مرده است بخورد؟ شما قطعاً از این کار کراهت دارید; پس غیبت را نیز ناخوشایند بدانید و از خدا بترسید و به درگاه او از این گناهان توبه کنید که خداوند بسیار توبه پذیر و مهربان است.'),
(4625, 49, 13, 'ای مردم، ما شما را از یک مرد و یک زن (آدم و حوّا) آفریدیم; بنابراین هیچ تیره و قبیله‌ای در آفرینش بر دیگری برتری ندارد; ما شما را به صورت تیره‌های گوناگون و قبیله‌های مختلف قرار دادیم تا یکدیگر را بشناسید نه آن که به نژادپرستی روی آورید و به نژاد و تیره خود افتخار کنید و هر ملّتی خود را از دیگر ملّت‌ها گرامی‌تر بداند; به یقین گرامی‌ترین شما نزد خدا با تقواترین شماست. خداست که معیار برتری انسان‌ها را معین می‌کند، چرا که او به حقایق امور دانا و آگاه است.'),
(4626, 49, 14, 'دسته‌ای از این اعراب بادیه نشین گفتند ایمان آوردیم. ای پیامبر، به آنان بگو: شما ایمان نیاورده‌اید، بلکه بگویید: اسلام آوردیم. هنوز ایمان در دل‌های شما راه نیافته است; ولی اگر خدا و پیامبرش را فرمان برید، از پاداش اعمالتان چیزی نمی‌کاهد، زیرا خداوند بسیار آمرزنده و مهربان است.'),
(4627, 49, 15, 'مؤمنان کسانی اند که به یکتایی خدا ایمان آورده و رسالت فرستاده او را باور کرده و در آنچه بدان گرویده‌اند تردید نکرده‌اند، و با مال و جانشان در راه خدا تلاش نموده‌اند (برای رضای خدا وظایف مالی و بدنی خود را انجام داده اند). تنها اینانند که در ادّعای ایمان راست می‌گویند.'),
(4628, 49, 16, 'به این بادیه نشینان بگو: آیا خدا را از ایمان و دینداری خود خبر می‌دهید، در حالی که خدا آنچه را در آسمان‌ها و آنچه را در زمین است می‌داند، و خدا به هر چیزی داناست.'),
(4629, 49, 17, 'بر تو منّت می‌نهند که اسلام آورده‌اند. به آنان بگو: بر من منّت نگذارید که اسلام آورده‌اید، بلکه خدا بر شما منّت دارد که شما را به ایمان هدایت کرده است اگر در ادّعای ایمان راستگو باشید.'),
(4630, 49, 18, 'همانا خداست که نهان آسمان‌ها و زمین را می‌داند، و خدا به آنچه می‌کنید بیناست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'قاف. سوگند به این قرآن ارجمند که ما تو را به رسالت فرستادیم تا مردم را به رستاخیز هشدار دهی.'),
(4632, 50, 2, 'آنان نه تنها به تو ایمان نیاوردند، بلکه از این که هشداردهنده‌ای از تبار خودشان به سویشان آمده است در شگفت شدند و کافران گفتند: این رستاخیز که محمّد می‌گوید چیزی شگفت و باور نکردنی است.'),
(4633, 50, 3, 'آیا چون مُردیم و خاک شدیم زنده می‌شویم و به صورت کنونی باز می‌گردیم؟ این بازگشتی است که خِرَد آن را بعید می‌داند و از پذیرش آن اِبا دارد.'),
(4634, 50, 4, 'ولی این پنداری نادرست است; چگونه بازگشتشان بعید شمرده شود، در حالی که ما از هر جزئی از بدن مردگانشان که زمین آن را در کام خود فرو می‌برد به خوبی آگاهیم. علاوه بر این نزد ما کتابی است که همه رویدادها در آن ثبت شده و از تغییر و تحریف مصون است.'),
(4635, 50, 5, 'حقیقت این است که منکرانِ معاد علم خدا را انکار نمی‌کنند، بلکه حق را ـ هنگامی که به سویشان آمد ـ از روی لجاجت دروغ انگاشتند; از این رو آنان در فضایی آشفته و سردرگم به سر می‌برند.'),
(4636, 50, 6, 'آیا به آسمان که بالای سرشان است ننگریسته‌اند که ما چگونه آن را ساخته و با ستارگانی درخشان زینتش داده‌ایم و برای آن هیچ گونه خلل و شکافی نیست؟'),
(4637, 50, 7, 'و زمین را برای زندگی آدمیان گستردیم و در آن کوه‌هایی ثابت و استوار نهادیم و از هرگونه گیاه زیبا و دل آرا در آن رویاندیم.'),
(4638, 50, 8, 'اینها را پدید آوردیم و سامان بخشیدیم تا برای هر بنده‌ای که به ما رجوع کننده است، مایه روشنگری و یادآوری باشد.'),
(4639, 50, 9, 'و از آسمان بارانی پربرکت فرود آوردیم و به وسیله آن باغ‌هایی انبوه از درختان و دانه‌هایی که درو می‌شوند رویاندیم.'),
(4640, 50, 10, 'و نیز درختان بلند خرما که دارای شکوفه‌های بر هم نشسته است.'),
(4641, 50, 11, 'اینها را آفریدیم تا برای بندگان رزق و روزی باشد; و نیز با آن آب که فرود آوردیم دیاری مرده را زنده کردیم، و بدین سان که گیاهان سر از خاک برمی آورند، مردگان نیز زنده می‌شوند و از گورها بیرون می‌آیند.'),
(4642, 50, 12, 'پیش از این مشرکان، قوم نوح و اصحاب رسّ و قوم ثمود پیامبران را تکذیب کردند،'),
(4643, 50, 13, 'و نیز قوم عاد و فرعون و قوم لوط، که برادران او به شمار می‌آمدند،'),
(4644, 50, 14, 'و قوم شعیب ـ آن جنگل نشینان ـ و قوم تبّع همه این اقوام پیامبران را تکذیب کردند; پس تهدید من مبنی بر هلاکت آنان محقّق شد.'),
(4645, 50, 15, 'آیا ما از آفرینش نخست، (آفرینش آسمان‌ها و زمین و هر چه در آنهاست) ناتوان بودیم که برای آفرینش مجدّد آنها درمانده باشیم؟! نه، ما ناتوان نیستیم، بلکه آنان از آفرینش مجدّد موجودات در شکّ و تردیدند.'),
(4646, 50, 16, 'به یقین ما انسان را آفریدیم، از این رو بر او تواناییم و آنچه را که نفْسش او را بدان وسوسه می‌کند می‌دانیم و ما از رگ ریسمان گونه‌ای که در سراسر اندام هایش گسترده است به او نزدیک تریم.'),
(4647, 50, 17, 'یاد کن زمانی را که آن دو فرشته دریافت کننده، اعمال آدمی را دریافت می‌کنند. یکی از آنها بر جانب راست و دیگری بر جانب چپ او نشسته‌اند.'),
(4648, 50, 18, 'او به هیچ سخنی زبان نمی‌گشاید مگر این که نگهبانی آماده ثبت و ضبط نزد وی حضور دارد.'),
(4649, 50, 19, 'و سرانجام، بی هوشیِ مرگ به حق سر می‌رسد [و گفته می‌شود: [ای انسان، این همان است که پیوسته از آن می‌گریختی.'),
(4650, 50, 20, 'و در صور دمیده می‌شود و در پی آن قیامت برپا می‌گردد. [به آدمی گفته می‌شود: [این است روزِ تهدید، همان روزی که گنهکاران به سزای کردارشان می‌رسند.'),
(4651, 50, 21, 'و هر کسی به سوی خدا می‌آید تا خدا درباره او داوری کند. این در حالی است که با او سوق دهنده‌ای است که او را به پیش می‌راند و شاهدی است که بر کارهایش گواهی می‌دهد.'),
(4652, 50, 22, 'خدا در آن صحنه به او می‌گوید: به راستی تو از این حقیقت که امروز به عیان آن را می‌بینی غافل بودی و ما پرده‌ای را که حقیقت را بر تو پوشانده بود کنار زدیم و امروز دیده تو تیزبین است.'),
(4653, 50, 23, 'و همدمش، آن فرشته گمارده شده بر او می‌گوید: این کسی که نزد من است، [برای بازپرسی اعمالش [مهیّاست.'),
(4654, 50, 24, 'و خدا به آن دو فرشته می‌گوید: هر کسی را که بر کفر اصرار میورزید و از روی لجاجت، با حق به ستیزه برمی خاست به دوزخ درافکنید;'),
(4655, 50, 25, 'هر کسی را که [مردم را [از نیکی باز می‌داشت و از پرستش خدا سرکشی می‌کرد و مردم را در دینشان به شکّ و تردید می‌انداخت;'),
(4656, 50, 26, 'همان کسی که با وجود خدا معبود دیگری می‌پنداشت، آری او را در عذاب شدید بیفکنید.'),
(4657, 50, 27, 'آن کفرپیشه می‌گوید: پروردگارا، شیطان که دمساز من بود مرا به سرکشی واداشت. و دمسازش می‌گوید: پروردگارا، من طغیانگری را بر او تحمیل نکردم، بلکه او خود در بیراهه‌ای دور از حق بود.'),
(4658, 50, 28, 'خدا به هر دو دسته می‌گوید: در حضور من با یکدیگر ستیزه نکنید که شما را سودی نمی‌بخشد، چرا که پیش از این وعده عذاب را به شما گوشزد کردم و به فرجامِ کفر و شرک هشدارتان دادم.'),
(4659, 50, 29, 'امروز عفو و بخششی در کار نیست، زیرا آن سخنِ (وعده عذاب برای کافران)، نزد من تغییر نخواهد یافت و من هرگز به بندگانم ستم روا نخواهم داشت.'),
(4660, 50, 30, 'روزی که به دوزخ می‌گوییم: آیا [از کافران و مجرمان [پر شدی؟ و او می‌گوید: آیا من جایی افزون بر آنچه در خود جای داده‌ام دارم؟'),
(4661, 50, 31, 'و بهشت را برای تقواپیشگان نزدیک می‌آورند، و آن در مکانی دور نیست که ورود به آن برایشان دشوار باشد.'),
(4662, 50, 32, '[به آنان می‌گویند: [این همان پاداشی است که آن را به شما وعده می‌دادند. این برای هر کسی است که در دنیا با اطاعت از خدا به او روی آورد و به آنچه خدا برعهده او نهاده بود همواره پای بند بود.'),
(4663, 50, 33, 'همان کسی که از عذاب و کیفر خدای رحمان ـ که از دید او نهان بود ـ می‌ترسید و به رحمتش امیدوار بود و اکنون با قلبی سرشار از توجه به خدا نزد او آمده است.'),
(4664, 50, 34, 'به آنان گفته می‌شود: با سلامت و امنیت به بهشت درآیید که این روز روزِ جاودانگی است.'),
(4665, 50, 35, 'آنچه در آن جا بخواهند برای آنان فراهم است و نزد ما افزون‌تر از آن نیز خواهد بود.'),
(4666, 50, 36, 'و چه بسیار نسل‌هایی را که پیش از ایشان نابود کردیم که نیرومندتر از اینان بودند و با قوّت و قدرت به شهرها می‌تاختند. ولی سرانجام گرفتار عذاب الهی شدند. آیا گریزگاهی که از عذاب خدا بدان پناه برده شود هست؟'),
(4667, 50, 37, 'همانا در این حقایقی که از روز رستاخیز و سرگذشت امّت‌های گذشته برشمردیم، برای کسی که دلی اندیشمند دارد یا کسی که به سخن حق گوش فرا می‌دهد و در این فضا حضور دارد، تذکر و عبرتی است.'),
(4668, 50, 38, 'و به یقین آسمان‌ها و زمین و آنچه را که میان آن دو است در شش مرحله آفریدیم و هیچ خستگی و رنجی به ما نرسید.'),
(4669, 50, 39, 'حال که دریافتی تکذیب کنندگان تو فرجامی ناگوار دارند، در برابر سخنان ناروای آنان صبر پیشه ساز، و پیش از طلوع خورشید و قبل از غروب، همراه با ستایش پروردگارت او را تسبیح گوی.'),
(4670, 50, 40, 'و در بخشی از شب و پس از سجده‌ها نیز او را تسبیح گوی.'),
(4671, 50, 41, 'و گوش به زنگِ روزی باش که منادی از جایگاهی نزدیک با دمیدن در صور بانگ برمی آورد.'),
(4672, 50, 42, 'روزی که همگان آن بانگِ به حق را می‌شنوند. آن روز، روزِ زنده شدن مردگان و بیرون آمدن از گورهاست.'),
(4673, 50, 43, 'به یقین ماییم که حیات می‌بخشیم و جان می‌ستانیم و بازگشت همگان فقط به سوی ماست.'),
(4674, 50, 44, 'روزی که زمین با شکافتنش از مردگان جدا می‌شود و مردگان از آن بیرون می‌آیند و به سوی کسی که آنان را فراخوانده است شتابان حرکت می‌کنند. این حشری است که بر ما آسان خواهد بود.'),
(4675, 50, 45, 'آری، تو شکیبا باش، ما آنچه را که آنان به گزاف درباره تو می‌گویند بهتر می‌دانیم و آنان را به سزای کردارشان خواهیم رسانید. تو بر آنان چیره نیستی تا به اجبار، به ایمان وادارشان کنی; پس به وسیله قرآن به هر کسی که از تهدید من می‌ترسد تذکّر بده.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'سوگند به آن بادهایی که [ابرها و [خاک‌ها را به شدّت می‌پراکنند،'),
(4677, 51, 2, 'پس به آن ابرهایی که باری گران از آب را با خود حمل می‌کنند،'),
(4678, 51, 3, 'پس به آن کشتی‌هایی که در دریاها به آسانی روانند،'),
(4679, 51, 4, 'پس به آن فرشتگانی که فرمان خدا را در امور جهان میان خود تقسیم می‌کنند،'),
(4680, 51, 5, 'سوگند به همه اینها که آنچه به شما وعده داده می‌شود راست است.'),
(4681, 51, 6, 'و قطعاً جزا و پاداش تحقق خواهد یافت.'),
(4682, 51, 7, 'سوگند به آسمانِ دارای راه‌ها،'),
(4683, 51, 8, 'که شما کفرپیشگان درباره قرآن سخنان گوناگون و ضدّ و نقیض می‌گویید.'),
(4684, 51, 9, 'از قرآن گردانیده می‌شود آن کس که گردانیده شده است.'),
(4685, 51, 10, 'مرگ بر آنان که بی هیچ دلیلی، تنها از روی حدس و گمان درباره قیامت سخن می‌گویند.'),
(4686, 51, 11, 'کسانی که در نادانی فرو رفته و از حقیقت آنچه به آنان خبر می‌دهند ناآگاهند.'),
(4687, 51, 12, 'همواره می‌پرسند: روز جزا کی خواهد بود؟'),
(4688, 51, 13, 'روز جزا روزی است که آنان بر آتش سوخته می‌شوند.'),
(4689, 51, 14, 'به آنان گفته می‌شود: عذاب خود را بچشید; این است همان عذابی که شتاب در آمدنِ آن را می‌خواستید.'),
(4690, 51, 15, 'همانا تقواپیشگان در بوستان‌ها و در کنار چشمه سارانی وصف ناپذیر خواهند بود.'),
(4691, 51, 16, 'آنچه را پروردگارشان به آنان عطا فرموده است با خرسندی می‌گیرند، زیرا آنان پیش از این در دنیا نیکوکار بودند،'),
(4692, 51, 17, 'اندکی از شب را می‌خوابیدند و در بیشتر ساعات آن به نماز می‌پرداختند.'),
(4693, 51, 18, 'و در سحرگاه‌ها آمرزش گناهانشان را از خدا می‌خواستند.'),
(4694, 51, 19, 'و می‌دانستند که در اموالشان برای نیازمندِ سائل و بینوایی که از درخواست اِبا دارد، حقّی هست.'),
(4695, 51, 20, 'و در زمین برای اهل یقین نشانه‌هایی بر یگانگی خدا در تدبیر جهان است.'),
(4696, 51, 21, 'و در خود شما نیز نشانه‌های روشنی بر یکتایی خدا هست; پس چرا آنها را نمی‌بینید و درنمی یابید؟'),
(4697, 51, 22, 'و باران که منشأ رزق شماست و خدا آن را فرو می‌ریزد و بهشت و نعمت‌های آن که به شما وعده داده می‌شود در آسمان است.'),
(4698, 51, 23, 'پس سوگند به پروردگار آسمان و زمین که رزق شما و بهشت و نعمت‌های آن امری ثابت و حتمی است; همان گونه که شما سخن می‌گویید و در سخن گفتن خود تردید ندارید.'),
(4699, 51, 24, 'آیا داستان میهمانان گرامی ابراهیم، آن فرشتگان ارجمند، به تو رسیده است؟'),
(4700, 51, 25, 'آن گاه که بر او وارد شدند و سلام گفتند. ابراهیم نیز به آنان سلام داد و پیش خود گفت: اینان مردمی ناآشنا و بیگانه‌اند.'),
(4701, 51, 26, 'پس بی درنگ و به طور پنهانی نزد خانواده اش رفت و طولی نکشید که گوساله‌ای فربه و بریان شده آورد.'),
(4702, 51, 27, 'و آن را نزدیکشان برد و به گمان این که آنها بشرند، گفت: چرا نمی‌خورید؟'),
(4703, 51, 28, 'پس وقتی دید دست به سوی آن نمی‌برند، در دل خود از آنان احساس ترسی کرد. فرشتگان که چنین دیدند، گفتند: مترس. و او را به پسری نوجوان و دانا مژده دادند.'),
(4704, 51, 29, 'همسرش چون این نوید را شنید، فریادکنان پیش آمد و سخت بر چهره خود زد و گفت: من پیرزنی نازا هستم، چگونه می‌توانم فرزندی بیاورم؟'),
(4705, 51, 30, 'فرشتگان به وی گفتند: پروردگار تو همان گونه فرموده است که ما به تو مژده داده‌ایم. به یقین او کارهایش از روی حکمت است و به حال تو و همسرت داناست.'),
(4706, 51, 31, 'ابراهیم گفت: ای فرستادگان، معلوم است شما برای امری مهم آمده‌اید، کارتان چیست؟'),
(4707, 51, 32, 'گفتند: ما به سوی قوم لوط که مردمی تبهکارند فرستاده شده‌ایم.'),
(4708, 51, 33, 'تا بر آنان سنگ‌هایی از گِل فرستیم;'),
(4709, 51, 34, 'سنگ‌هایی که نزد پروردگارت برای آن تجاوزکاران علامت گذاری شده است.'),
(4710, 51, 35, 'سپس فرشتگان را به سوی قوم لوط روانه ساختیم و هر کس از مؤمنان را که در آن شهر بود از آن جا بیرون کردیم.'),
(4711, 51, 36, 'ولی در آن جا جز یک خانه که افرادش تسلیم حق بودند. و آن هم خانواده لوط بود، نیافتیم.'),
(4712, 51, 37, 'سپس آن دیار را زیر و زبر کردیم و در آن جا برای کسانی که از عذاب دردناک می‌ترسند، نشانه‌ای از یگانگی در ربوبیت خود برجای نهادیم.'),
(4713, 51, 38, 'و در داستان موسی نیز نشانه‌ای بر ربوبیتِ بی همتای ماست، آن گاه که او را با دلایلی روشن به سوی فرعون فرستادیم.'),
(4714, 51, 39, 'ولی او با سپاهیانش روی برتافت و گفت: موسی جادوگر است و بار دیگر گفت: او دیوانه است.'),
(4715, 51, 40, 'پس او و سپاهیانش را گرفتیم و آنان را به دریا افکندیم در حالی که او در اثر این کفر و سرکشی درخور سرزنش بود.'),
(4716, 51, 41, 'و در قوم عاد نیز نشانه‌ای بر ربوبیت بی همتای ماست، آن گاه که آن بادِ بی ثمر و تباه کننده را بر آنان فرستادیم.'),
(4717, 51, 42, 'بر هرچه میوزید آن را وانمی گذاشت مگر این که آن را همچون چیزی می‌ساخت که پوسیده و خرد شده است.'),
(4718, 51, 43, 'و در ماجرای قوم ثمود نیز نشانه‌ای بر ربوبیت بی همتای ماست، آن گاه که [آن شتر را پی کردند پس [به آنان گفته شد تا چندی از نعمت‌های دنیا بهره ببرید و در این مهلتِ چند روزه به خود آیید و به فرمان خدا گردن نهید.'),
(4719, 51, 44, 'ولی آنان از فرمان پروردگارشان سرکشی کردند، پس در حالی که می‌نگریستند، صاعقه آنان را فرا گرفت.'),
(4720, 51, 45, 'صاعقه چنان سرعت داشت که آنان نه توانستند برخیزند و از عذاب خدا بگریزند و نه از کسی طلب یاری کنند.'),
(4721, 51, 46, 'و قوم نوح را نیز پیش از قوم عاد و ثمود و فرعونیان نابود کردیم، زیرا آنان مردمی بودند که از فرمان ما سرپیچی می‌کردند.'),
(4722, 51, 47, 'و ما آسمان را با قدرتی وصف ناپذیر بنا کردیم و قطعاً ما را قدرتی گسترده است.'),
(4723, 51, 48, 'و زمین را گستراندیم و ما خوب گستراننده‌ای هستیم.'),
(4724, 51, 49, 'و از هر چیزی دو صنف، آفریدیم، باشد که شما دریابید که آفریدگار آنها از داشتنِ همتا منزّه است.'),
(4725, 51, 50, 'پس ای مردم، با این همه دلایل روشن که حکایت از یگانگی خدا دارد، با شتاب به سوی خدا حرکت کنید و تنها او را بپرستید، همانا من از جانب او پیامبری هشداردهنده‌ام و شما را از عذاب وی برحذر می‌دارم و آنچه را که بدان نیاز دارید به روشنی بیان می‌کنم.'),
(4726, 51, 51, 'و با وجود خدا معبود دیگری قرار ندهید که من از جانب او پیامبری هشداردهنده‌ام و شما را از عذاب وی برحذر می‌دارم و آنچه را که بدان نیاز دارید به روشنی بیان می‌کنم.'),
(4727, 51, 52, 'بدین سان هیچ فرستاده‌ای از جانب خدا به سوی کسانی که پیش از اینان بودند نیامد، مگر این که گاه گفتند: او جادوگر است و گاه گفتند: او دیوانه است.'),
(4728, 51, 53, 'آیا یکدیگر را به گفتن این سخن سفارش می‌کردند؟ نه! چنین نبوده است، بلکه همه کفرپیشگان مردمی سرکش بودند و مخالفت با پیامبران پیامد سرکشی است.'),
(4729, 51, 54, 'ای پیامبر، حال که این کافران بر کفر و سرکشی خود پای می‌فشارند از آنان روی برتاب و با آنان درباره حق بحث و جدال مکن که بر تو ملامتی نیست، زیرا تو حجّت را بر آنان تمام کرده‌ای.'),
(4730, 51, 55, 'ولی یادآوری کن و پند ده و آن را پی گیر باش، چرا که تذکر و اندرز برای مؤمنان سودمند است.'),
(4731, 51, 56, 'من جنیّان و آدمیان را جز برای این که مرا بپرستند نیافریدم.'),
(4732, 51, 57, 'نه از آنان رزقی می‌طلبم و نه می‌خواهم مرا غذا دهند.'),
(4733, 51, 58, 'چرا که خدا خود بسیار روزی دهنده و مقتدر و نیرومند است.'),
(4734, 51, 59, 'آری، آنان را آفریدم تا مرا عبادت کنند، ولی گروهی از آنان کفر ورزیدند و ستمکار شدند، و قطعاً برای کسانی که ستم کرده‌اند بهره‌ای از عذاب، مانند بهره هم مسلکانشان از امّت‌های گذشته خواهد بود; پس شتاب در آمدنِ عذاب را از من نخواهند.'),
(4735, 51, 60, 'پس وای بر کسانی که کافر شده‌اند از روزشان همان روزی که به آنان وعده داده می‌شود.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'سوگند به کوه طور که خدا در آن با موسی سخن گفت،'),
(4737, 52, 2, 'و سوگند به تورات، آن کتابی که نگاشته شد،'),
(4738, 52, 3, 'که در صفحه‌ای برای تلاوت منتشر شد،'),
(4739, 52, 4, 'و سوگند به آن خانه آباد،'),
(4740, 52, 5, 'و به آسمان، آن بامِ برافراشته،'),
(4741, 52, 6, 'و به آن دریای شعلهور،'),
(4742, 52, 7, 'سوگند به همه اینها که عذاب روز قیامت که پروردگارت مقرّر کرده است تحقّق خواهد یافت.'),
(4743, 52, 8, 'هیچ بازدارنده‌ای برای آن نیست.'),
(4744, 52, 9, 'آن عذاب روزی پدید می‌آید که آسمان با جنبشی سخت، نابود می‌شود،'),
(4745, 52, 10, 'و کوه‌ها با لرزش زمین به شدّت حرکت می‌کنند.'),
(4746, 52, 11, 'وای بر تکذیب کنندگانِ قیامت در آن روز که این پدیده‌ها رخ دهد.'),
(4747, 52, 12, 'همانان که در باطل فرو رفته‌اند و با بحث و جدال در آیات خدا به بازی می‌پردازند.'),
(4748, 52, 13, 'روزی که به سوی آتش دوزخ سخت رانده می‌شوند.'),
(4749, 52, 14, 'به آنان می‌گویند: این همان آتشی است که آن را دروغ می‌انگاشتید.'),
(4750, 52, 15, 'آیا این آتش که پیش روی شماست افسون است، یا هنوز هم آن را توهّم و خیال می‌انگارید و آن را نمی‌بینید؟'),
(4751, 52, 16, 'در آن بسوزید! اینک چه بر آن شکیبایی کنید و چه شکیبایی نکنید برای شما یکسان است; نه شکیبایی شما آن را از شما دور می‌کند و نه زاری و بی تابی شما از شدّت آن می‌کاهد، زیرا به آنچه انجام داده‌اید مجازات می‌شوید.'),
(4752, 52, 17, 'این است فرجام کسانی که رستاخیز و رسالت پیامبران را دروغ می‌انگارند; امّا تقوا پیشگان قطعاً در بوستان‌های بهشتی سکونت خواهند یافت و در ناز و نعمت فراوان خواهند بود.'),
(4753, 52, 18, 'میوه‌هایی را که پروردگارشان به آنان ارزانی می‌دارد دریافت می‌کنند [در حالی که [پروردگارشان آنان را از عذاب دوزخ نگاه داشته است.'),
(4754, 52, 19, 'به آنان می‌گویند: به پاداش کارهای پسندیده‌ای که انجام می‌دادید، گوارا بخورید و بیاشامید و از هر نعمتی در کمال عافیت بهره برید.'),
(4755, 52, 20, 'این در حالی است که آنان بر تخت‌های ردیف شده‌ای، نشسته و بر بالش‌ها تکیه زده‌اند، و حوریانی را که چشمانی سیاه و درشت دارند، قرین آنان ساخته‌ایم.'),
(4756, 52, 21, 'و کسانی که ایمان آورده و فرزندانشان در ایمان از آنان پیروی کرده‌اند، فرزندانشان را هر چند ایمانشان کمتر باشد، در درجات بهشت به آنان ملحق می‌کنیم تا مایه سرورشان گردد، و هرگز از پاداش پدران نمی‌کاهیم، زیرا هر کسی در گروِ کاری است که انجام داده است; پس باید پاداش آن را دریافت کند و چیزی از آن کاسته نشود.'),
(4757, 52, 22, 'و پیوسته به بهشتیان هر نوع میوه و گوشتی که بخواهند و بدان تمایل داشته باشند ارزانی می‌داریم.'),
(4758, 52, 23, 'در آن جا برای نوشیدن شراب گرد می‌آیند و جام شراب را از یکدیگر می‌ستانند; شرابی که در نوشیدنش نه یاوه گویی است و نه ارتکاب گناه.'),
(4759, 52, 24, 'و نوجوانانی ویژه بهشتیان که همچون مرواریدهای نهفته در صدف، زیبا و شادابند، برای خدمت بر گردشان می‌گردند.'),
(4760, 52, 25, 'بهشتیان برخی روی به برخی دیگر کرده و از کار و بار یکدیگر در دنیا و از آنچه آنان را به بهشت رهنمون شده است می‌پرسند.‌'),
(4761, 52, 26, 'می‌گویند: ما پیش از این به خانواده خود علاقه مند بودیم و به سعادت آنها می‌اندیشیدیم و همواره آنان را به حق فرا می‌خواندیم.'),
(4762, 52, 27, 'پس خدا بر ما منّت نهاده و ما را به رحمت خود به بهشت درآورد و از عذاب سوزان نگاهمان داشت;'),
(4763, 52, 28, 'چرا که ما پیش از این او را به یکتایی می‌خواندیم و اوست احسان کننده و مهربان.'),
(4764, 52, 29, 'ای پیامبر، اکنون که این حقایق را برای تو یادآوری کردیم، مردم را به فرجام کارشان تذکّر ده که تو به برکت نعمتی که پروردگارت به تو ارزانی داشته است کاهن و مجنون نیستی و نخواهی شد.'),
(4765, 52, 30, 'بلکه می‌گویند: محمّد شاعر است، ما اضطراب مرگ را بر او انتظار می‌بریم.'),
(4766, 52, 31, 'بگو: منتظر باشید که رویدادی درخور خویش در پیش خواهید داشت; من نیز مانند شما در انتظار آنم که عذابی از جانب خدا شما را فرا گیرد.'),
(4767, 52, 32, 'آیا خِردهایشان آنان را به اظهار این گونه سخنان فرمان می‌دهد؟ نه، هرگز خردها چنین فرمان نمی‌دهند، بلکه آنان مردمی طغیانگرند و این پندارها و گفتارها پیامد طغیانگری و سرکشی است.'),
(4768, 52, 33, 'بلکه می‌گویند: قرآن را محمّد خود بافته است. نه، او چنین قدرتی ندارد; بلکه آنان ایمان نمی‌آورند; از این رو به چنین تهمتی روی می‌آورند.'),
(4769, 52, 34, 'اگر راست می‌گویند که او قرآن را ساخته است باید سخنی مانند آن بیاورند، زیرا آنان هم مثل او بشرند و اهل سخن.'),
(4770, 52, 35, 'مگر آنان از چیزی جز آنچه دیگران از آن پدید آمده‌اند، آفریده شده‌اند که نباید پیامبری به سویشان گسیل شود و پاداش و کیفری داشته باشند، یا این که خود آفریننده خویشند و خدا حق تدبیر و تربیت آنان را به وسیله پیامبران ندارد؟'),
(4771, 52, 36, 'یا این که آسمان‌ها و زمین را آفریده‌اند و بالاتر از آنند که به بندگی فراخوانده شوند؟ نه، آنان مردمی هستند که حق را باور ندارند.'),
(4772, 52, 37, 'یا مگر گنجینه‌های پروردگار تو نزد آنان است تا نبوّت را به هر که بخواهند عطا کنند و از هر که بخواهند بازدارند؟ یا این که آنان بر خدا چیره‌اند و می‌توانند آنچه را خدا به تو داده است از تو سلب کنند؟'),
(4773, 52, 38, 'یا مگر آنان را نردبانی است که به وسیله آن بر آسمان برآیند و سخنان خدا را بشنوند و آنچه را خود دریافت می‌کنند، بپذیرند و جز آن را دروغ شمرند؟ اگر این گونه است باید از آنان کسی که مدّعی شنیدن وحی است، حجّتی روشن بر ادّعای خویش بیاورد.'),
(4774, 52, 39, 'یا مگر دختران از آنِ خدایند و پسران از آنِ شما؟'),
(4775, 52, 40, 'یا مگر از آنان برای ابلاغ رسالت مزدی می‌طلبی و در نتیجه از زیانی که می‌بینند گرانبار می‌شوند و به رنج می‌افتند؟ [که رسالت تو را نمی‌پذیرند [.'),
(4776, 52, 41, 'بلکه آیا لوح محفوظ که نهان است و حقایق در آن ثبت است در اختیار آنان است و از آن نسخه برداری می‌کنند تا آنچه درباره تو می‌گویند حقایقی انکارناپذیر باشد؟!'),
(4777, 52, 42, 'بلکه آیا آنان می‌خواهند با طرح این گفتارها و پندارهای باطل بر ضدّ تو مکر کنند تا دعوت تو را خاموش کنند؟ ولی آنان که کافر شده‌اند خود گرفتار نیرنگ خویش شده‌اند چرا که خویشتن را از سعادت محروم می‌کنند.'),
(4778, 52, 43, 'آیا آنان جز خدا معبودی دارند که سزاوار پرستش باشد؟ منزّه است خدا از این که برای او شریکی قرار دهند.'),
(4779, 52, 44, 'آنان در انکار رسالت تو بدان جا رسیده‌اند که اگر به عیان ببینند از آسمان پاره‌ای از عذاب بر آنان فرو می‌افتد، می‌گویند: این ابری متراکم است.'),
(4780, 52, 45, 'ای پیامبر، اینک که بهانه‌های انکار رسالت تو را با حجّت زدودیم و دریافتی که آنان هر چند آیتی روشن بر حقانیت تو ببینند، نمی‌پذیرند، آنان را واگذار تا آن روزشان را که در آن با نفخه صور جانشان را می‌ستانند، دیدار کنند.'),
(4781, 52, 46, 'روزی که چاره اندیشی آنان سودی به حالشان ندارد و از سوی هیچ کس یاری نمی‌شوند [تا از عذاب قیامت رهایی یابند [.'),
(4782, 52, 47, 'و به یقین برای کسانی که ستم کرده‌اند، پیش از آن عذابی خواهد بود که در عالم برزخ دامنگیرشان می‌شود، ولی بیشترشان از آن بی خبرند.'),
(4783, 52, 48, 'بر حکمی که پروردگارت مقرّر داشته است شکیبا باش; تو قطعاً زیر نظر مایی و کارت بر ما پوشیده نیست; پس مبادا از فرمان ما روی برتابی; و آن گاه که به پا می‌خیزی همراه با ستایش پروردگارت او را تسبیح گوی.'),
(4784, 52, 49, 'و در پاره‌ای از شب با خواندن نماز شب و نیز آن هنگام که ستارگان در صبحدم پنهان شدند با خواندن نافله صبح، خدا را تسبیح گوی.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'سوگند به ستاره آن گاه که در افق فرو شود،'),
(4786, 53, 2, 'که همنشین شما محمّد نه از راه راست منحرف شده و نه در تشخیص حقیقت دچار خطا گردیده است.'),
(4787, 53, 3, 'او به رأی خویش چیزی به نام قرآن برای شما نمی‌خواند.'),
(4788, 53, 4, 'آن نیست مگر وحیی که از جانب خدا به او می‌رسد.'),
(4789, 53, 5, 'جبریل، آن فرشته برخوردار از نیروهای سخت و پایدار، قرآن را به او آموخت.'),
(4790, 53, 6, 'همو که از خرد و اندیشه‌ای استوار برخوردار است، او در چهره واقعی خود بر پیامبر ظاهر شد و ایستاد.'),
(4791, 53, 7, 'آنگاه که در بالاترین کناره آسمان بود.'),
(4792, 53, 8, 'سپس به پیامبر نزدیک شد، پس خود را به او رسانید تا او را بگیرد و به آسمان‌ها ببرد.'),
(4793, 53, 9, 'چنان نزدیک شد که فاصله آن دو به اندازه دو کمان یا نزدیک‌تر بود.'),
(4794, 53, 10, 'پس به پیامبر که بنده خدا بود، وحی کرد آنچه را وحی کرد.'),
(4795, 53, 11, 'پیامبر آن را در دل دید و دریافت و دلش آنچه را دید به خطا ندید.'),
(4796, 53, 12, 'آیا با او درباره آنچه می‌بیند مجادله می‌کنید، و اصرار دارید او خلاف آنچه را به عیان می‌بیند ادعا کند؟'),
(4797, 53, 13, 'او جبرئیل را در فرودی دیگر نیز در چهره واقعی اش مشاهده کرد.'),
(4798, 53, 14, 'نزدیک آن درخت کُنار که در انتهای آسمان هاست;'),
(4799, 53, 15, 'که بهشت، جایگاه همیشگی مؤمنان نزدیک آن درخت است;'),
(4800, 53, 16, 'آن گاه که درخت کُنار را فراگرفت آنچه آن را فراگرفت.'),
(4801, 53, 17, 'دیده پیامبر نه منحرف شد که حقیقت را دگرگون بیند و نه از حد گذشت تا سراب را حقیقت انگارد.'),
(4802, 53, 18, 'سوگند یاد می‌کنم که او بخشی از بزرگ‌ترین نشانه‌های پروردگارش را مشاهده کرد.'),
(4803, 53, 19, 'ای مشرکان، اینک با روشن شدن حقّانیت پیامبر، از بُتان خود، لات و عزّی به من خبر دهید;'),
(4804, 53, 20, 'و از مَنات، آن دیگری که سوّمین آنهاست،'),
(4805, 53, 21, 'آیا اینها را تندیس دختران خدا می‌پندارید؟ آیا پسر از آنِ شما و دختر از آنِ اوست؟'),
(4806, 53, 22, 'این تقسیم، بدین صورت، تقسیمی ستمکارانه و نابرابر است.'),
(4807, 53, 23, 'اینها جز نام‌هایی بی اساس نیستند که شما و پدرانتان بر آنها نهاده‌اید (یکی را خدای آسمان‌ها و دیگری را خدای زمین و آن دیگری را خدای باران نامیده‌اید.) خداوند برهانی که بتوان با آن بر ربوبیّت و الوهیّت آنها استدلال کرد، فرو نفرستاده است. مشرکان در مورد معبودانشان جز از پندار خود و آنچه هوای نفسشان بدان تمایل دارد، پیروی نمی‌کنند، در صورتی که قرآن که به حق راهنمای خلق است، از سوی پروردگارشان برای آنان آمده است.'),
(4808, 53, 24, 'مگر آدمی هر چه را آرزو کند ـ به صِرف آرزو کردن ـ به دست خواهد آورد که اینان با آرزوی شفاعت فرشتگان یا خدا بودن معبودانشان، به شفاعت آنها دل بسته و خدایی آنها را باور کرده‌اند؟'),
(4809, 53, 25, 'جهان آخرت و سرای دنیا تنها از آنِ خداست و او را در مُلکش شریکی نیست، پس چگونه ممکن است آرزوی مشرکان تحقق یابد؟'),
(4810, 53, 26, 'و چه بسیار فرشتگانی که در آسمان‌ها [و درمقام قرب الهی] هستند و شفاعتشان هیچ سودی ندارد، و هیچ کاری از آنان ساخته نیست، مگر پس از آن که خدا به کسی از آنان که بخواهد و بپسندد رخصت دهد.'),
(4811, 53, 27, 'این مشرکان که سرای آخرت را باور ندارند، فرشتگان را دختران خدا می‌شمرند و آنها را با نام‌هایی که ویژه زنان است، نامگذاری می‌کنند.'),
(4812, 53, 28, 'آنان به این اعتقادشان هیچ دانشی ندارند و تنها از وهم و خیال خود پیروی می‌کنند، و قطعاً گمان ـ تا چه رسد به وهم و خیال ـ برای دستیابی به حق هرگز بسنده نیست.'),
(4813, 53, 29, 'اینک، چون از یاد ما روی برتافتند و جز زندگی دنیا را نخواستند از آنان روی بگردان و کارشان را به خدا واگذار.'),
(4814, 53, 30, 'این منتهای دانش آنان است که تنها درباره دنیا می‌اندیشند. قطعاً پروردگار تو به کسی که از راه او دور گشته و به بیراهه افتاده داناتر است، و او به آن که هدایت یافته و راه او را پیموده نیز آگاه‌تر است.'),
(4815, 53, 31, 'و هر چه در آسمان‌ها و هر چه در زمین است از آنِ خداست; پس او را سزد که بشر را بیافریند و امرشان را تدبیر کند، تا کسانی را که بد کرده‌اند به آنچه کرده‌اند کیفر کند و کسانی را که نیکی نموده‌اند به نیکوترین پاداش سزا دهد.'),
(4816, 53, 32, 'نیکوکاران کسانی اند که از گناهان بزرگ و از کارهای قبیح خودداری می‌کنند، مگر گناهی که هر از گاهی بدان مبتلا شوند. قطعاً پروردگار را آمرزشی گسترده است و سزد که گنهکاران به درگاه او توبه کنند. او آن گاه که شما را از عناصر زمینی پدید آورد و پرورش داد تا به صورت نطفه در رحم‌ها قرار گرفتید، و نیز آن گاه که شما به صورت جنین در شکم مادرانتان بودید، از هر کس به شما آگاه‌تر بود. پس خویشتن را به پاکی و پیراستگی نستایید. او به آن کسی که تقوا پیشه نموده است داناتر است.'),
(4817, 53, 33, 'اینک به من خبر ده، از آن کس که از انفاق روی گردانید،'),
(4818, 53, 34, 'و اندکی از مال خود را در راه خدا داد و سپس از آن هم دریغ ورزید،'),
(4819, 53, 35, 'آیا علم غیب نزد اوست و آینده خود را می‌بیند که اگر انفاق کند تهیدست می‌شود؟'),
(4820, 53, 36, 'مگر او را به آن پندها و حکمت‌ها که در نوشته‌های نازل شده بر موسی (تورات) است، آگاه نکرده‌اند؟'),
(4821, 53, 37, 'آیا او را به آنچه در کتاب‌های نازل شده بر ابراهیم است، آگاه نکرده‌اند؟ همان که به وظیفه اش به خوبی وفا کرد.'),
(4822, 53, 38, 'آیا در آنها این حقیقت نیامده است که هیچ گنهکاری بار گناه دیگری را بر دوش نمی‌گیرد؟ پس چگونه سخن آن کس را که گفت: تو انفاق مکن، من گناهانت را برعهده می‌گیرم باور کرده است؟'),
(4823, 53, 39, 'مگر در کتاب‌های ابراهیم و موسی این حقیقت بیان نشده است که برای انسان جز آنچه برای به دست آوردنش تلاش کرده است چیزی نیست؟'),
(4824, 53, 40, 'و این که تلاشش در قیامت ـ که دیری نمی‌گذرد ـ مشاهده خواهد شد.'),
(4825, 53, 41, 'سپس در برابر عملش کاملترین سزا را به او خواهند داد.'),
(4826, 53, 42, 'و این که همه چیز به پروردگارت منتهی می‌شود.'),
(4827, 53, 43, 'و این که تنها اوست که خندیدن و گریستن را در آدمیان پدید آورده است.'),
(4828, 53, 44, 'و این که تنها اوست که میرانده و حیات بخشیده است.'),
(4829, 53, 45, 'و این که خداست که دو گونه نر و ماده را آفریده است،'),
(4830, 53, 46, 'از نطفه، آن گاه که در رحم ریخته می‌شود.'),
(4831, 53, 47, 'و این که آفرینشِ دیگر (پدید آوردن سرای آخرت) برعهده اوست.'),
(4832, 53, 48, 'و این که اوست که توانگری داده و دولت و مکنت بخشیده است.'),
(4833, 53, 49, 'و این که اوست پروردگار ستاره (شِعری).'),
(4834, 53, 50, 'و این که اوست که قوم هود، عادِ نخستین را به عذاب هلاک ساخت.'),
(4835, 53, 51, 'و نیز قوم ثمود را، و هیچ یک از کافرانِ آن قوم را باقی نگذاشت.'),
(4836, 53, 52, 'و قوم نوح را نیز پیش از عادیان و ثمودیان نابود کرد. آنان از قوم عاد و ثمود ستمکارتر و سرکش‌تر بودند.'),
(4837, 53, 53, 'و شهرهای زیر و زبر شده قوم لوط را در هم فرو ریخت.'),
(4838, 53, 54, 'و بر آنها پوشانید عذابی را که پوشانید.'),
(4839, 53, 55, 'پس ای انسان، در کدام یک از نعمت‌های پروردگارت تردید روا می‌داری؟'),
(4840, 53, 56, 'این قرآن هشداری است از جمله هشدارهای نخستین.'),
(4841, 53, 57, '[هشدار می‌دهد که [رستاخیز نزدیک شده است.'),
(4842, 53, 58, 'و جز خدا هیچ کس قادر نیست عذاب آن را برطرف کند.'),
(4843, 53, 59, 'پس چرا از این سخن که شما را به نجات از عذاب رستاخیز فرا می‌خواند در شگفت شده‌اید و آن را انکار می‌کنید؟'),
(4844, 53, 60, 'چرا آن را به استهزا گرفته‌اید و به آن می‌خندید و به حال خود نمی‌گریید؟'),
(4845, 53, 61, 'و به بیهودگی روزگار می‌گذرانید؟'),
(4846, 53, 62, 'آری، از آن روی که خدا پروردگار شماست و رستاخیز در انتظارتان، پس خدا را سجده کنید و او را بپرستید تا عذاب آن روز را از شما بگرداند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'قیامت بسیار نزدیک گردید و ماه شکافت و دو پاره شد.'),
(4848, 54, 2, 'و اگر این مشرکانِ لجوج آیت و معجزه‌ای ببینند روی برتافته و می‌گویند: افسونی است در پی افسونی دیگر.'),
(4849, 54, 3, 'آنان پیامبر و نشانه‌های رسالتش را تکذیب کردند و از پی هواهای نفسانی خود رفتند، در صورتی که هر امری سرانجام در جایگاه خود قرار خواهد گرفت و درستی یا نادرستی آن آشکار خواهد شد.'),
(4850, 54, 4, 'قطعاً از اخبار امّت‌های گذشته که به عذاب الهی هلاک شدند و از اخبار عذاب روز رستاخیز، آنچه در آن پند و عبرت است به آنان رسیده است.'),
(4851, 54, 5, 'قرآن حکمتی رسا و کامل است، ولی آن را دروغ انگاشتند و از پی هوای نفس خود رفتند; از این رو اخطارها به حالشان سود نمی‌دهد.'),
(4852, 54, 6, 'پس تو ـ ای پیامبر ـ از آنان روی بگردان و بر اخطارشان اصرار مکن و یاد کن روزی را که آن دعوت کننده آنان را به سوی چیزی سخت و دشوار که آن را تصوّر نمی‌کردند، فرا می‌خواند.'),
(4853, 54, 7, 'آنان در حالی که چشمانشان در اثر ذلّت فرو افتاده از گورها بیرون می‌آیند و آن چنان درهم و برهم می‌شوند که گویی ملخ‌هایی پراکنده‌اند.'),
(4854, 54, 8, 'شتابان به سوی آن دعوت کننده می‌روند و دعوتش را اجابت می‌کنند. کافران می‌گویند: امروز روزی سخت و دشوار است.'),
(4855, 54, 9, 'پیش از قوم تو، قوم نوح نیز رسالت پیامبران را دروغ انگاشتند; از این رو بنده ما نوح را تکذیب کردند و گفتند: او دیوانه است و از سوی جنّیان به گفتن این سخنان وادار می‌شود.'),
(4856, 54, 10, 'پس نوح پروردگارش را خواند که: پروردگارا، من شکست خورده‌ام، انتقام مرا بستان.'),
(4857, 54, 11, '[ما هم دعایش را اجابت کردیم،] پس درهای آسمان را با آبی ریزان گشودیم و بارانی شدید و پی درپی فرو ریختیم.'),
(4858, 54, 12, 'و زمین را به صورت چشمه‌های آب شکافتیم; پس آب‌های فرود آمده از آسمان با آب‌های جوشیده از زمین به همان اندازه که برای طوفان مقدّر شده بود به هم پیوست.'),
(4859, 54, 13, 'و نوح را بر کشتیِ ساخته شده از الوارها و میخ‌ها برنشاندیم.'),
(4860, 54, 14, 'آن کشتی با مراقبت و حراست همه جانبه ما بر روی آب روان بود. چنین تدبیر کردیم تا نوح را که به او کافر شدند و رسالتش را انکار کردند پاداش دهیم.'),
(4861, 54, 15, 'و به راستی آن کشتی را باقی گذاشتیم تا نشانه و دلیلی باشد برای هر کسی که درس عبرت می‌آموزد. اینک آیا کسی هست که متذکّر شود و درستیِ پیام رسولان را دریابد؟'),
(4862, 54, 16, 'حال که از سرگذشت قوم نوح آگاه شدید، آیا دانستید که عذاب من و هشدارهایم چگونه بود؟ آیا عذاب من سخت نبود؟ آیا هشدارهایم راست و درست نبود؟'),
(4863, 54, 17, 'همانا ما معارف قرآن را برای فراگیری همگان آسان کردیم; پس آیا کسی هست که متذکّر شود و خدا را بشناسد و به صفات و افعال او پی ببرد؟'),
(4864, 54, 18, 'قوم عاد پیامبرشان را تکذیب کردند و به عذاب گرفتار آمدند; پس عذاب من و هشدارهای من چگونه بود؟'),
(4865, 54, 19, 'ما در برهه‌ای از زمان که پیاپی برای آنان شوم بود، تندبادی سوزان بر آنان فرستادیم،'),
(4866, 54, 20, 'که مردم را از جا می‌کنْد [و وقتی آنها را بر زمین می‌افکند] گویی تنه‌های نخلی بودند که از ریشه برآمده بودند.'),
(4867, 54, 21, 'پس بنگر که عذاب من و هشدارهایم چگونه بود. آیا عذاب من سخت نبود؟ آیا هشدارهایم راست و درست نبود؟'),
(4868, 54, 22, 'همانا ما معارف قرآن را برای فراگیری همگان آسان کردیم; پس آیا کسی هست که متذکّر شود و خدا را بشناسد و به صفات و افعال او پی ببرد؟'),
(4869, 54, 23, 'قوم ثمود نیز هشدارها را دروغ انگاشتند.'),
(4870, 54, 24, 'و گفتند: آیا از بشری که از جنس خود ما و یکّه و تنهاست و هیچ عِدّه و عُدّه‌ای ندارد پیروی کنیم؟ در این صورت ما قطعاً در گمراهی و جنونی شگفت خواهیم بود.'),
(4871, 54, 25, 'آیا از میان ما وحی فقط بر او القا شده است؟ او بر ما هیچ امتیازی ندارد، بلکه بسیار دروغ پرداز و متکبّر است و می‌خواهد بدین وسیله بر ما بزرگی کند.'),
(4872, 54, 26, 'فردا به زودی خواهند دانست که دروغ پرداز و متکبّر کیست.'),
(4873, 54, 27, 'به پیامبرشان صالح گفتیم: همانا ما به شیوه اعجاز، آن شتری را که می‌خواهند برایشان می‌فرستیم تا آنان را بیازماییم; پس در انتظار [عملکرد] آنان باش و بر آزارشان شکیبایی کن.'),
(4874, 54, 28, 'و به او گفتیم: به آنان اعلام کن که آب چشمه میان آنان و شتر سهمیه بندی شده است و برای هر سهمی از شُرب صاحب آن حاضر می‌شود.'),
(4875, 54, 29, 'پس [از چندی] رفیقشان را صدا کردند که شتر را از پای درآورد; او هم پی کردن و کشتن شتر را برعهده گرفت و آن را پی کرد و کشت.'),
(4876, 54, 30, 'اینک بنگرید که عذاب من و هشدارهایم چگونه بود؟'),
(4877, 54, 31, 'ما بانگی مهیب بر آنان فرستادیم و آنان همچون خار و خاشاکی که آغل داران برای دام هایشان جمع آوری می‌کنند، خشک و بی رمق شدند.'),
(4878, 54, 32, 'همانا ما قرآن را برای فراگیری همگان آسان کردیم; پس آیا کسی هست که متذکّر شود و خدا را بشناسد و به صفات و افعال او پی ببرد؟'),
(4879, 54, 33, 'قوم لوط نیز هشدارها را دروغ انگاشتند.'),
(4880, 54, 34, 'ما هم بر آنان بادی که سنگبارانشان می‌کرد فرستادیم و تنها خاندان لوط بودند که آنان را در سحرگاهی [با بیرون بردن از شهر] نجات بخشیدیم.'),
(4881, 54, 35, 'تا بدین وسیله به آنان از جانب خود نعمتی بزرگ داده باشیم، زیرا آنان از سپاسگزاران بودند و ما هر که را سپاسگزار باشد همین گونه پاداش می‌دهیم.'),
(4882, 54, 36, 'بی گمان لوط قومش را از عذابی که ایشان را بدان گرفتیم، بیم داده بود، ولی آنان در هشدار و اخطار او تشکیک کرده به بحث و جدال پرداختند.'),
(4883, 54, 37, 'و از او خواستند تا مهمانانش را (فرشتگانی که به صورت انسان‌ها نزد او بودند) در اختیارشان نهد; ما هم چشمانشان را محو کردیم و فروغ از دیدگانشان گرفتیم، پس [گفتیم:] عذاب مرا و آنچه شما را بدان هشدار دادم بچشید!'),
(4884, 54, 38, 'و همانا در سپیده دمی، عذابی تخلّف ناپذیر به سراغشان آمد.'),
(4885, 54, 39, 'پس [به آنان گفتیم:] عذاب مرا و آنچه شما را بدان هشدار دادم بچشید!'),
(4886, 54, 40, 'همانا ما قرآن را برای فراگیری همگان آسان کردیم; پس آیا کسی هست که متذکّر شود و خدا را بشناسد و به صفات و افعال او پی ببرد؟'),
(4887, 54, 41, 'و قطعاً هشدارها به فرعونیان نیز رسید.'),
(4888, 54, 42, 'ولی آنان همه آیات ما را دروغ انگاشتند; ما هم آنان را به عذاب گرفتیم، گرفتنِ شکست ناپذیری مقتدر.'),
(4889, 54, 43, 'ای امّت محمّد، آیا کافران شما، از این امّت‌هایی که فرجام شومشان را یاد کردیم، در برخورداری از امکانات زندگی بهترند تا از کیفر ما در امان باشند، یا در کتاب‌های آسمانی مصونیّت شما از عذاب الهی ثبت شده است اگرچه کفرپیشه و گنهکار باشید؟'),
(4890, 54, 44, 'نه، چنین نیست. آیا کفرپیشگان شما می‌گویند: ما امّتی متحد و یکپارچه‌ایم و هر کس بخواهد به ما گزندی برساند، از او انتقام می‌گیریم؟'),
(4891, 54, 45, 'به زودی آن امّت متحد در جنگ بدر مغلوب می‌شوند و به میدان کارزار پشت می‌کنند و می‌گریزند.'),
(4892, 54, 46, 'و این پایان کیفرشان نیست، بلکه وعده گاهشان رستاخیز است، و رستاخیز از هر بلایی سخت‌تر و از هر ناگواری تلخ‌تر است.'),
(4893, 54, 47, 'رستاخیز برای آنان سخت خواهد بود، زیرا آنان مجرمند و مجرمان بهشت را نخواهند یافت و وارد آتشی برافروخته خواهند شد;'),
(4894, 54, 48, 'روزی که بر چهره هایشان در آتش کشیده می‌شوند. آن روز به آنان گفته می‌شود: بچشید گزندی را که از دوزخ به شما می‌رسد!'),
(4895, 54, 49, 'این بدان جهت است که ما هر چیزی را به اندازه آفریده‌ایم و برای مجرمان چنین مقرر کرده‌ایم که از بهشت محروم باشند و به دوزخ درآیند.'),
(4896, 54, 50, 'و فرمان ما برای تحقّق یافتن هر پدیده‌ای جز یک فرمان نیست و نیاز به تکرار و گذشت زمان ندارد و همچون دیدنِ با چشم، بی درنگ پدید می‌آید.'),
(4897, 54, 51, 'و به یقین که هم مسلکان شما را به عذاب هلاک کردیم; پس آیا کسی هست که متذکّر شود و دریابد [که فرجام تکذیب پیامبران عذاب است]؟'),
(4898, 54, 52, 'و هر چه به جای آورده‌اند در نامه اعمالشان ثبت است.'),
(4899, 54, 53, 'و هر کار خرد و کلانی که انجام داده‌اند در آن نوشته شده است.'),
(4900, 54, 54, 'به یقین، تقواپیشگان در بوستان‌هایی بهشتی و در کنار جویبارهایی بزرگ خواهند بود.'),
(4901, 54, 55, 'در جایگاهی که همه نعمت‌های آن حقیقی است; در حضور خدا که فرمانروایی مقتدر است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'خدای رحمان'),
(4903, 55, 2, 'به رحمت گسترده اش قرآن را به جنّ و انس آموخت.'),
(4904, 55, 3, 'انسان را آفرید.'),
(4905, 55, 4, 'به او سخن گفتن را تعلیم داد تا اندیشه و احساس خود را آشکار سازد.'),
(4906, 55, 5, 'خورشید و ماه بر طبق حسابی که از سوی او مقرّر شده است، پیوسته در حرکت‌اند.'),
(4907, 55, 6, 'و بوته و درخت فرمانبردار اویند و به این طریق او را سجده می‌کنند. (ریشه‌های خود را در خاک فرو می‌برند تا نیازشان را آشکار سازند).'),
(4908, 55, 7, 'و آسمان را برافراشت و هرگونه ابزار سنجش را در میان نهاد (تا باورها، گفتارها، کارها و دیگر چیزها با آن سنجیده شود).'),
(4909, 55, 8, 'پس نباید در وزن کردن اشیاء از اندازه‌ای که ترازو نشان می‌دهد درگذرید.'),
(4910, 55, 9, 'و باید سنجش اشیاء را به عدل و داد برپا دارید و از ترازو نکاهید.'),
(4911, 55, 10, 'و زمین را برای مردم نهاد.'),
(4912, 55, 11, 'در آن، میوه و درختان خرما با خوشه‌های غلاف دار است;'),
(4913, 55, 12, 'و نیز دانه سبوس دار و گیاه خوشبو.'),
(4914, 55, 13, 'پس ای جنّیان و انسیان، کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4915, 55, 14, 'خدای رحمان انسان را از گِلی خشکیده که همچون سفال بود آفرید.'),
(4916, 55, 15, 'و جن را از زبانه‌ای خالص، از آتشی [ویژه] پدید آورد.'),
(4917, 55, 16, 'پس ای جن و انس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4918, 55, 17, 'خدای رحمان پروردگار دو خاور و پروردگار دو باختر است، (خاور و باختر خورشید در زمستان و تابستان).'),
(4919, 55, 18, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4920, 55, 19, 'آن دو دریای شور و شیرین را که پیوسته باهم برخورد می‌کنند در هم آمیخت.'),
(4921, 55, 20, 'میان آن دو حایلی است که از حدّ خود درنمی گذرند و هیچ یک بر دیگری غالب نمی‌شود.'),
(4922, 55, 21, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4923, 55, 22, 'از آن دو دریا مروارید درشت و ریز بیرون می‌آید.'),
(4924, 55, 23, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4925, 55, 24, 'کشتی‌های ساخته و پرداخته شده دست بشر که در دریا روانند و در عظمت و بلندی همچون کوه‌ها هستند از آنِ خدایند.'),
(4926, 55, 25, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4927, 55, 26, 'هر که روی زمین است ـ از جنّ و انس ـ فانی خواهد شد.'),
(4928, 55, 27, 'و پروردگارت که شکوهمند و گرامی است با همه صفات ارجمند و زیبایش باقی می‌ماند.'),
(4929, 55, 28, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4930, 55, 29, 'هر که در آسمان‌ها و زمین است همواره حاجت خود را از او می‌طلبد، زیرا از هر جهت به وی نیازمند است. هر روز (و هر زمان) خداوند در کاری بدیع است غیر از کار گذشته و آینده.'),
(4931, 55, 30, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4932, 55, 31, 'ای جنّیان و انسیان، ای دو مخلوق وزین آفرینش به زودی نظام این جهان را برمی اندازیم و جهانی دیگر پدید می‌آوریم و در آن جا به کار شما خواهیم پرداخت.'),
(4933, 55, 32, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4934, 55, 33, 'در آن جا به شما می‌گوییم: ای گروه جنّ و انس، اگر می‌توانید از کرانه‌های آسمان‌ها و زمین بگذرید و از تحت قدرت خدا خارج شوید و خود را از کیفر او برهانید، بگذرید، ولی هرگز نمی‌توانید بگذرید، جز با نیرویی عظیم که آن هم در اختیار شما نیست.'),
(4935, 55, 34, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4936, 55, 35, 'در آن روز بر سرتان شعله آتش و دودی فرو فرستاده می‌شود ولی یکدیگر را یاری نمی‌کنید و از بلا نمی‌رهانید.'),
(4937, 55, 36, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4938, 55, 37, 'پس آن گاه که آسمان شکافته شود و همچون چرم سرخ، گلگون گردد [رستاخیز فرا می‌رسد].'),
(4939, 55, 38, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4940, 55, 39, 'آن روز که قیامت برپا می‌شود، هیچ جنّ و انسی را از گناهش نمی‌پرسند.'),
(4941, 55, 40, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4942, 55, 41, 'آری، از آنان نمی‌پرسند که چه کرده‌اند، بلکه مجرمان با علامتی که در چهره دارند شناخته می‌شوند; آن گاه موهای پیشانی و پاهایشان را می‌گیرند و آنان را به دوزخ می‌افکنند.'),
(4943, 55, 42, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4944, 55, 43, 'سپس گفته می‌شود: این است دوزخی که مجرمان آن را انکار می‌کردند.'),
(4945, 55, 44, 'آنان میان آتش و آبی داغ و بس جوشان می‌گردند.'),
(4946, 55, 45, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4947, 55, 46, 'و برای کسی که از مقام پروردگارش بترسد (خود را در برابر او بنده بداند) دو بهشت است: بهشتی به سزای اعمالش و بهشتی براساس فزون بخشی خدا.'),
(4948, 55, 47, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4949, 55, 48, 'دو بهشتی که دارای انواع میوه‌ها و محصولات‌اند.'),
(4950, 55, 49, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4951, 55, 50, 'در آنها دو چشمه روان است که در وصف نیاید.'),
(4952, 55, 51, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4953, 55, 52, 'در آن دو بهشت از هر میوه‌ای دو گونه است.'),
(4954, 55, 53, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4955, 55, 54, 'این در حالی است که آنان بر بالش‌ها تکیه زده و بر فرش‌هایی با آسترهایی از دیبای ستبر نشسته‌اند، و میوه‌های چیدنیِ آن دو بهشت، نزدیک و در دسترس است.'),
(4956, 55, 55, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4957, 55, 56, 'در آن جا همسرانی است که چشم بر شوهران خود دوخته و دیده از غیر آنها فرو بسته‌اند و پیش از آنان هیچ انس و جنّی با آنها آمیزش نکرده است.'),
(4958, 55, 57, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4959, 55, 58, 'آنها در لطافتِ رنگ، درخشندگی و زیبایی گویی یاقوت و مرجانند.'),
(4960, 55, 59, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4961, 55, 60, 'این است احسان خدا به کسانی که از مقام او می‌ترسند; مگر سزای نیکی جز نیکی است؟'),
(4962, 55, 61, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4963, 55, 62, 'و در مرتبه‌ای فروتر از آن دو بهشت، دو بهشت دیگر نیز هست.'),
(4964, 55, 63, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4965, 55, 64, 'چنان سرسبز و با طراوت است که از شدّت سبزی سیه گون می‌نماید.'),
(4966, 55, 65, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4967, 55, 66, 'در آن جا دو چشمه‌ای که می‌جوشد و فواره می‌زند جاری است.'),
(4968, 55, 67, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4969, 55, 68, 'در آن دو بهشت درختان میوه و خرما و انار است.'),
(4970, 55, 69, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4971, 55, 70, 'در آن جا زنانی نیکوخوی و زیباروی هستند.'),
(4972, 55, 71, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4973, 55, 72, 'حوریانی با چشم‌هایی درشت و سیاه که به همسران خود اختصاص دارند و در خیمه‌ها از نگاه دیگران مصونند.'),
(4974, 55, 73, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4975, 55, 74, 'پیش از آنان هیچ انس و جنّی با آنها آمیزش نکرده است.'),
(4976, 55, 75, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4977, 55, 76, 'بهشتیان حالی که تکیه زده بر روی بالش‌های سبز رنگ و فرش‌های فاخر نشسته‌اند، از مواهب بهشت بهره می‌برند.'),
(4978, 55, 77, 'پس کدام یک از نعمت‌های پروردگارتان را انکار می‌کنید؟'),
(4979, 55, 78, 'خجسته و پر برکت است نام پروردگارت همو که شکوهمند و گرامی است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'آن گاه که حادثه قیامت رخ دهد، مؤمنان کامیاب و کافران زیانکار گردند.'),
(4981, 56, 2, 'در وقوع این حادثه هیچ دروغ و خلاف گویی نیست.'),
(4982, 56, 3, 'آن رویداد، نظام کنونی دنیا را زیر و رو می‌کند.'),
(4983, 56, 4, 'آن گاه که زمین با لرزشی سخت لرزانده شود،'),
(4984, 56, 5, 'و کوه‌ها به شدّت متلاشی و ذرّه ذرّه شوند،'),
(4985, 56, 6, 'و غباری پراکنده گردند،'),
(4986, 56, 7, 'و شما مردم سه گروه شوید.'),
(4987, 56, 8, 'آن سه گروه عبارتند از: نیکبختان، نیکبختان کیانند!'),
(4988, 56, 9, 'و تیره بختان، تیره بختان کیانند!'),
(4989, 56, 10, 'و سبقت گیرندگان در کارهای نیک که به سوی آمرزش الهی و بهشت برین سبقت خواهند گرفت.'),
(4990, 56, 11, 'آنان مقرّبان درگاه خدایند.'),
(4991, 56, 12, 'که در بهشت‌های پرنعمت جای خواهند داشت.'),
(4992, 56, 13, 'مقرّبان درگاه خدا گروهی انبوه از امّت‌های پیامبران پیشین‌اند.'),
(4993, 56, 14, 'و گروهی اندک از امّت اسلام، این آخرین امّت.'),
(4994, 56, 15, 'آنان بر تخت‌هایی بافته شده قرار دارند.'),
(4995, 56, 16, 'و رو به روی یکدیگر با کمال اُلفت بر آنها تکیه زده‌اند.'),
(4996, 56, 17, 'در آن جا پسرانی هماره نوجوان و شاداب، به خدمت بر گردشان می‌گردند.'),
(4997, 56, 18, 'با جام‌هایی از نوشیدنی‌ها، و با آبریزها و جامی لبریز از باده زلال و روان.'),
(4998, 56, 19, 'نه از نوشیدن آن سر درد گیرند و نه از آن مست و بی خرد شوند.'),
(4999, 56, 20, 'آن پسرانِ خدمتگزار با هرگونه میوه که مقرّبان برگزینند،'),
(5000, 56, 21, 'و با هر گوشت پرنده‌ای که بدان رغبت کنند، بر گردشان می‌گردند.'),
(5001, 56, 22, 'مقرّبان را حوریانی است که چشمانی سیاه و درشت دارند.'),
(5002, 56, 23, 'حوریانی همچون مرواریدِ نهفته در صدف، درخشان و دور از دست.'),
(5003, 56, 24, 'این نعمت‌ها را به آنان ارزانی می‌داریم تا سزایی باشد برای کارهای پسندیده‌ای که می‌کردند.'),
(5004, 56, 25, 'در آن جا نه سخن بیهوده ای می‌شنوند و نه به ارتکاب گناهی متّهم می‌شوند.'),
(5005, 56, 26, 'آنان جز سلام که در میانشان رایج است سخنی نمی‌شنوند.'),
(5006, 56, 27, 'امّا اصحاب دست راست، همان نیکبختان، کیانند اصحاب دست راست؟!'),
(5007, 56, 28, 'آنان زیر درختانِ کُنار بی خار جای می‌گیرند;'),
(5008, 56, 29, 'زیر درختان موز که میوه آنها بر هم چیده شده است;'),
(5009, 56, 30, 'زیر سایه‌ای همواره گسترده;'),
(5010, 56, 31, 'و در کنار آبی ریزان و پیوسته روان;'),
(5011, 56, 32, 'و در میان میوه‌هایی فراوان،'),
(5012, 56, 33, 'که نه پایان می‌پذیرد و نه منعی پدید می‌آید.'),
(5013, 56, 34, 'در کنار همسرانی بلندمرتبه و زیبا.'),
(5014, 56, 35, 'به یقین ما آنان را پدید آورده و به گونه‌ای پرورانده‌ایم که شادابی و زیبایی شان دگرگون نمی‌شود.'),
(5015, 56, 36, 'و آنان را هماره دوشیزه گردانیده‌ایم.'),
(5016, 56, 37, 'و دلباخته همسرانشان و همسن و سال آنان ساخته‌ایم.'),
(5017, 56, 38, 'اینها همه برای اصحاب دست راست است.'),
(5018, 56, 39, 'آنان جمعیّتی انبوه از امّت‌های پیامبران گذشته‌اند.'),
(5019, 56, 40, 'و نیز جمعیّتی انبوه از امّت اسلام، این آخرین امّت.'),
(5020, 56, 41, 'و امّا اصحاب دست چپ، همان نامبارکانِ تیره بخت، و کیانند اصحاب دست چپ؟!'),
(5021, 56, 42, 'آنان در میان حرارتی سوزان و آبی بس داغ و جوشان به سر می‌برند.'),
(5022, 56, 43, 'و در سایه‌ای از دود سیاه;'),
(5023, 56, 44, 'سایه‌ای که نه خنک است و نه خوش.'),
(5024, 56, 45, 'این بدان سبب است که آنان پیش از این در دنیا، در اثر نعمت‌های خدا سرمست شده و بخشنده نعمت را از یاد برده بودند.'),
(5025, 56, 46, 'و بر شکستن آن پیمان بزرگ (پرستش خدای یکتا) اصرار میورزیدند و بندگی او را گردن نمی‌نهادند.'),
(5026, 56, 47, 'و می‌گفتند: آیا چون مُردیم و خاک گشتیم و چند استخوانی [پوسیده] شدیم، آیا به راستی دوباره برانگیخته می‌شویم؟'),
(5027, 56, 48, 'و آیا پدران نخستین ما که نابود شده‌اند، آنها هم برانگیخته می‌شوند؟'),
(5028, 56, 49, 'بگو: قطعاً مردم نخستین و آیندگان،'),
(5029, 56, 50, 'برای موعدی معیّن که نزد خدا روزی معلوم است، گردآوری خواهند شد.'),
(5030, 56, 51, 'پس شما ای کسانی که راه حق را گم کرده‌اید و آن را دروغ می‌انگارید،'),
(5031, 56, 52, 'قطعاً از درخت زقّوم خواهید خورد.'),
(5032, 56, 53, 'و شکم هایتان را از آن پر خواهید کرد.'),
(5033, 56, 54, 'آن گاه روی آن از آب جوش خواهید نوشید;'),
(5034, 56, 55, 'آن را همچون نوشیدن شتران تشنه، می‌آشامید.'),
(5035, 56, 56, 'این است آنچه در روز جزا بدان پذیرایی می‌شوند.'),
(5036, 56, 57, 'ای منکران رستاخیز، ماییم که شما را آفریده‌ایم، پس چرا سخن ما را درباره رستاخیز باور نمی‌کنید؟'),
(5037, 56, 58, 'به من بگویید، این نطفه که در رحم‌ها می‌ریزید،'),
(5038, 56, 59, 'آیا شمایید که آن را به صورت انسان درمی آورید یا ماییم که آن را به صورت انسان درمی آوریم؟'),
(5039, 56, 60, 'ماییم که مرگ را میان شما مقدّر کرده‌ایم و ما در پدید آمدن مرگ، مغلوب اسباب ویرانگر حیات نیستیم.'),
(5040, 56, 61, 'مرگ را میان شما مقدّر کردیم تا امثال شما را جایگزین شما سازیم و شما آدمیان را در آفرینش دیگری که از آن آگاهی ندارید درآوریم.'),
(5041, 56, 62, 'به یقین، شما آفرینش نخست را که آفرینش این جهان است شناخته‌اید، پس چرا از این رهگذر امکان تحقق جهان دیگر بلکه ضرورت آن را درنمی یابید؟'),
(5042, 56, 63, 'به من بگویید، آنچه را کِشت می‌کنید،'),
(5043, 56, 64, 'آیا شما آن را می‌رویانید و به ثمر می‌نشانید یا ما رویاننده و به ثمر نشاننده آنیم؟'),
(5044, 56, 65, 'اگر بخواهیم آن را به صورت خاشاک درمی آوریم، آن گاه شما از آنچه بر کِشتتان رسیده است شگفت زده درباره آن گفتوگو می‌کنید،'),
(5045, 56, 66, 'که به راستی که ما زیاندیده‌ایم،'),
(5046, 56, 67, 'بلکه ما از رزق خود محروم شده‌ایم.'),
(5047, 56, 68, 'به من بگویید، این آبی که می‌آشامید،'),
(5048, 56, 69, 'آیا شما آن را از ابر فرود آورده‌اید یا ما فرودآورنده‌ایم؟'),
(5049, 56, 70, 'اگر بخواهیم آن را شور می‌گردانیم; پس چرا سپاس خدا نمی‌گزارید؟'),
(5050, 56, 71, 'به من بگویید، این آتشی که شما با کشیدن شاخه دو درخت (مَرْخ و عَفار) به یکدیگر، می‌افروزید،'),
(5051, 56, 72, 'آیا شما درخت آن را آفریده و پرورش داده‌اید یا ما آفریننده و پرورش دهنده آنیم؟'),
(5052, 56, 73, 'ما این آتش را مایه تذکّر و وسیله استفاده برای کسانی که در بیابان فرود می‌آیند قرار داده‌ایم.'),
(5053, 56, 74, 'پس نام پروردگارت را که بزرگ است به پاکی و پیراستگی از هر عیب و کاستی یاد کن.'),
(5054, 56, 75, 'سوگند یاد می‌کنم به جایگاه‌های ستارگان در آسمان،'),
(5055, 56, 76, 'و بی تردید این سوگند ـ اگر بدانید ـ سوگندی است بزرگ!'),
(5056, 56, 77, 'که این کتاب، قرآنی گرانمایه و ارجمند است.'),
(5057, 56, 78, 'و در لوحی که از هرگونه تغییر و دگرگونی مصون است قرار دارد.'),
(5058, 56, 79, 'و جز پیراستگان از گناه هیچ کس به علم آن دسترسی نخواهد داشت.'),
(5059, 56, 80, 'آن از نزد پروردگار جهانیان فرو فرستاده شده است.'),
(5060, 56, 81, 'آیا این سخن را که از جانب خداست سبک می‌شمرید و به آن توجه نمی‌کنید.'),
(5061, 56, 82, 'و نصیب خود را از آن، تنها این قرار می‌دهید که آن را دروغ بشمارید؟'),
(5062, 56, 83, 'اگر مرگ به تقدیر خدا نیست، چرا آن گاه که جانِ محتضر به گلو می‌رسد،'),
(5063, 56, 84, 'و شما هنگام جان سپردنش او را می‌بینید،'),
(5064, 56, 85, 'و ما از شما به او نزدیک تریم ولی شما ما و فرشتگان ما را نمی‌بینید،'),
(5065, 56, 86, 'اگر رستاخیز حقیقت ندارد و برای شما در برابر اعمالتان جزا و پاداشی نیست،'),
(5066, 56, 87, 'اگر راست می‌گویید که چنین روزی نیست چرا جانِ آن محتضر را به او باز نمی‌گردانید؟'),
(5067, 56, 88, 'ما جان او را می‌ستانیم; پس اگر از مقرّبان باشد،'),
(5068, 56, 89, 'پاداش او راحتی و رهایی از هر درد و اندوه، و بهشتی پر نعمت است.'),
(5069, 56, 90, 'و اگر از اصحاب دست راست [آن نیکبختان و خجستگان] باشد،'),
(5070, 56, 91, 'از سوی دوستان و همدمان تو که همه از اصحاب دست راستند، برای تو جز خیر و سلامت نیست.'),
(5071, 56, 92, 'و امّا اگر آن کسی که جانش را می‌ستانیم از کسانی باشد که حق را دروغ انگاشته و گمراه بوده‌اند،'),
(5072, 56, 93, 'با آبی جوشان پذیرایی خواهد شد.'),
(5073, 56, 94, 'و به آتش سوزان دوزخ درخواهد آمد.'),
(5074, 56, 95, 'قطعاً این سخن که درباره گروه‌های سه گانه یادآور شدیم، راست و درست است و شکّ و تردیدی در آن نیست.'),
(5075, 56, 96, 'پس نام پروردگار بزرگ خویش را به پاکی و پیراستگی از هر عیب و نقصی یاد کن.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'آنچه در آسمان‌ها و زمین است، خدا را به پاکی و پیراستگی از هر عیب و نقصی یاد کرده‌اند و اوست مقتدر شکست ناپذیری که کارهایش همه متقن و استوار است.'),
(5077, 57, 2, 'فرمانروایی آسمان‌ها و زمین از آنِ اوست; حیات می‌بخشد و می‌میراند و او بر انجام هر کاری تواناست.'),
(5078, 57, 3, 'تنها اوست اوّل و آخر و ظاهر و باطن، و او به هر چیزی داناست.'),
(5079, 57, 4, 'اوست آن که آسمان‌ها و زمین را در شش مرحله آفرید، آن گاه بر تخت فرمانروایی استیلا یافت و به تدبیر امور هستی پرداخت. آنچه در زمین نفوذ می‌کند و آنچه از زمین برمی آید و آنچه از آسمان فرود می‌آید و آنچه در آن بالا می‌رود، همه را می‌داند، و هر کجا باشید او با شماست، و خدا به آنچه می‌کنید بیناست.'),
(5080, 57, 5, 'فرمانروایی آسمان‌ها و زمین تنها از آنِ اوست و کارها همه به او باز گردانده می‌شود و به دست او رقم می‌خورد.'),
(5081, 57, 6, 'شب را در روز فرو می‌برد و بر ساعات شب می‌افزاید و روز را در شب فرو می‌برد و بر ساعات روز می‌افزاید، و او به اندیشه‌های نهفته در سینه‌ها آگاه است.'),
(5082, 57, 7, 'ای مؤمنان، ایمان به خدا و رسول را در خود تقویت کنید و از اموالتان که خدا شما را در آنها جانشین دیگران قرار داده است در راه خدا انفاق کنید. پس کسانی از شما که ایمان آورده و در راه خدا انفاق کرده‌اند، پاداشی بزرگ خواهند داشت.'),
(5083, 57, 8, 'شما را چه شده است که آن گونه که باید به خدا ایمان نمی‌آورید و از او در هر امری اطاعت نمی‌کنید، در حالی که پیامبر شما را فرا می‌خواند تا به پروردگارتان چنان که بایسته است ایمان بیاورید؟ اگر مؤمنید، قطعاً خدا آن روز که به یکتایی او و رسالت پیامبرش گواهی دادید، از شما پیمان گرفت که فرمانبردار او باشید.'),
(5084, 57, 9, 'اوست آن که بر بنده اش محمّد آیاتی روشن از قرآن را که بیانگر احکام دین است، فرو می‌فرستد تا شما را از تاریکی‌ها بیرون آورد و به سوی نور روانه سازد، و قطعاً خداوند به شما مهربان است و شما را از رحمت خود بهره مند می‌کند.'),
(5085, 57, 10, 'و شما را چه شده است که در راه خدا انفاق نمی‌کنید با این که آسمان‌ها و زمین و آنچه از این دو در اختیار شماست برایتان باقی نمی‌ماند و به ارث به خدا می‌رسد؟ کسانی از شما که پیش از پیروزی، برای نبرد با کافران از اموال خود هزینه کردند و جنگیدند، با دیگران یکسان نیستند. آنان از کسانی که پس از پیروزی از دارایی‌های خود برای نبرد هزینه کردند و جنگیدند، مرتبه‌ای والاتر دارند. با این حال، خداوند به هر دو گروه پاداشی نیکو وعده داده است و خدا به آنچه انجام می‌دهید آگاه است.'),
(5086, 57, 11, 'کیست آن که با انفاق مال خود، به خدا وامی نیکو دهد؟ که خداوند هنگام پرداخت وام، آن را دو چندان به او عطا می‌کند و در آخرت پاداشی نفیس و گرانمایه خواهد داشت.'),
(5087, 57, 12, 'آن پاداش نفیس روزی به انفاق کنندگان داده می‌شود که مردان و زنان مؤمن را می‌بینی که نورشان پیشاپیش آنان و در طرف راستشان به سرعت می‌رود، چرا که خود به سوی درجات بهشت و مقام قرب الهی با شتاب در حرکت‌اند. فرشتگان به آنان می‌گویند: امروز مژدگانیِ شما باغ‌هایی آکنده از درختان است که از زیر آنها جویبارها روان است; جاودانه در آن جا خواهید بود. این است سعادت بزرگ.'),
(5088, 57, 13, 'روزی که مردان و زنان منافق که در تاریکی‌ها فرو رفته‌اند، به مؤمنان می‌گویند: در انتظار ما باشید تا به شما برسیم و از نور شما برگیریم. در پاسخ آنان گفته می‌شود: به دنیا که پشت سر نهادید باز گردید و با ایمان و کردار شایسته نوری بجویید که روشنایِی امروز برخاسته از ایمان و عمل شایسته دیروز است. آن گاه میان مؤمنان و منافقان دیواری کشیده می‌شود که مؤمنان را احاطه می‌کند و دری دارد; درونِ آن رحمت و بُرونش از جانب آن عذاب است.'),
(5089, 57, 14, 'منافقان، اهل ایمان را ندا در می‌دهند که مگر ما با شما بر یک آیین نبودیم؟ مؤمنان می‌گویند: چرا، ولی شما خود را به بلا و هلاکت افکندید و در انتظار آن بودید که برای دین و دینداران سختی‌ها پدید آید، و در شکّ و تردید به سر بردید و آرزوها شما را فریب داد، تا آن که فرمان خدا آمد و مرگْ شما را درگرفت، و شیطانِ حیله گر با نشان دادن جلوه‌های عفو و رحمت خدا شما را اغفال کرد.'),
(5090, 57, 15, 'پس امروز نه از شما منافقان و نه از کسانی که کافر شدند، فدیه‌ای که خود را به وسیله آن از آتش دوزخ برهانید، گرفته نمی‌شود; جایگاهتان آتش است و آن تنها یاور شماست و بد بازگشتگاهی است.'),
(5091, 57, 16, 'آیا برای کسانی که ایمان آورده‌اند هنگامِ آن نرسیده است که دل هایشان به یاد خدا و آن کتابِ سراسر حقی که از جانب خدا نازل شده است خاشع شود و مانند کسانی نباشند که قبلاً به آنان کتاب آسمانی داده شد و زمان بر آنان به درازا کشید، پس دل هایشان سخت شد و بسیاری از آنان عبادت خدا را رها کردند و به گناهان روی آوردند و فاسق شدند؟'),
(5092, 57, 17, 'بدانید که خدا زمین را پس از مردنش، با رویش گیاهان زنده می‌کند، و هم او می‌تواند دل‌های سخت را نرم و خاشع گرداند; شما نیز زمینه خشوع را در دل هایتان فراهم سازید. به راستی ما آیات و نشانه‌ها را برای شما به روشنی بیان کرده‌ایم، باشد که به خرد دریابید.'),
(5093, 57, 18, 'همانا مردان و زنانی که صدقه داده‌اند و کسانی که با انفاقشان در راه خدا به خداوند وامی نیکو داده‌اند، آنچه پرداخته‌اند برای آنان دو چندان می‌شود و در آخرت پاداشی نفیس و گرانمایه خواهند داشت.'),
(5094, 57, 19, 'و کسانی که به خدا و پیامبرانش ایمانی راستین آوردند، نزد خدا به منزله راستی پیشگان و گواهان اعمالند، و پاداش و نوری همچون پاداش و نور آنان خواهند داشت. و کسانی که [به ما و پیامبرانمان] کافر شدند و آیات و نشانه‌های ما را دروغ انگاشتند آنان همدم دوزخ اند و همواره در آن به سر خواهند برد.'),
(5095, 57, 20, 'بدانید که زندگی دنیا جز بازی و سرگرمی و آرایش و فخرفروشی به یکدیگر و سبقت گرفتن بر یکدیگر در جمع آوری مال و افزودن فرزندان، نیست، و اینها هیچ یک باعث کمال آدمی نخواهد بود. دنیا و زیورهای آن همچون بارانی است که کِشتِ برآمده از آن، کشاورزان را خشنود کند و به شعف آورد، سپس در مسیر خود حرکت می‌کند تا به رشد ممکن برسد، آن گاه آن را می‌بینی که زرد شده است، سپس خرد و خاشاک می‌گردد و در برابر باد پراکنده می‌شود. این است آنچه در دنیاست، اما در آخرت یک سوی آن عذابی سخت و یک سوی دیگرش آمرزشی بزرگ از جانب خدا و خشنودی او خواهد بود، و زندگی دنیا جز کالایی که بهره گیری از آن مایه فریب است چیزی نیست.'),
(5096, 57, 21, 'به سوی آمرزشی از پروردگارتان و بهشتی که وسعت آن همچون وسعت آسمان و زمین است، بشتابید و برای به دست آوردن آن بر یکدیگر سبقت گیرید. آن بهشت برای کسانی که به خدا و پیامبرانش ایمان آورده‌اند، آماده شده است. این فضل و بخشش خداست که آن را به هر که بخواهد عطا می‌کند (و چنین خواسته است که آن را به مؤمنان عطا کند) و خدا دارای فضل و بخشش بزرگ است.'),
(5097, 57, 22, 'هیچ بلا و مصیبتی نه در زمین و نه در مورد خودتان رخ نمی‌دهد مگر این که پیش از آن که آن را پدید آوریم، در کتابی ثبت شده است به یقین ثبت رویدادها پیش از وقوع آنها، بر خدا آسان است.'),
(5098, 57, 23, 'این حقیقت را یادآور شدیم تا بر نعمت‌هایی که از دست داده‌اید، اندوه مخورید و از آنچه خدا به شما ارزانی کرده است شادمان نباشید، که خدا هیچ متکبّر فخرفروشی را دوست ندارد.'),
(5099, 57, 24, 'همانان که بخل میورزند و از انفاق در راه خدا دریغ می‌کنند و مردم را نیز به بخل و ترک انفاق فرمان می‌دهند. و هر کس پند خدا نپذیرد و از انفاق روی بگرداند و حقایقی را که خداوند درباره دنیا و تقدیر امور بیان کرد نپذیرد، [بداند که] خدا از انفاق او بی نیاز است و بر همه کارها و تقدیرهایش ستایش می‌شود.'),
(5100, 57, 25, 'همانا ما پیامبران خود را با دلایل قاطع و معجزاتی که حقانیت رسالت آنان را آشکار می‌ساخت فرستادیم و با آنها کتاب آسمانی و دین را که معیار عقاید و اعمال انسان هاست فرود آوردیم، تا مردم همواره به عدل و داد برپا باشند و آهن را که در آن نیرویی سخت برای رزم و دفاع است و منافع دیگری نیز برای مردم دارد آفریدیم. آری چنین کردیم تا اموری که باید، تحقق یابد و تا خدا آن کسی را که در نهان از دید پیامبران، خدا و پیامبرانش را یاری می‌کند از دیگران مشخص سازد. به یقین خدا نیرومند و شکست ناپذیر است.'),
(5101, 57, 26, 'و همانا ما نوح و ابراهیم را به رسالت فرستادیم و در میان نسل آن دو، نبوّت و کتاب را قرار دادیم; پس گروهی از آنان هدایت یافتند و بسیاری از آنان از حد و مرز الهی بیرون رفتند.'),
(5102, 57, 27, 'سپس پیامبران دیگر خود را نیز بر طریقه آنان پی در پی فرستادیم، و عیسی پسر مریم را از پی آنان آوردیم و به او انجیل را عطا کردیم، و در دل‌های کسانی که از او پیروی کردند مهربانی و رحمت قرار دادیم. آنان رهبانیّت (عزلت برای عبادت) را از پیش خود درآوردند، ما آن را در آیینشان مقرر نکردیم ولی به آن روی آوردند تا خشنودی خدا را بجویند، اما حدّ و مرز آن را چنان که باید رعایت نکردند; پس به کسانی از آنان که ایمان آوردند اجرشان را عطا کردیم و بسیاری از آنان از مرز دین خارج شدند.'),
(5103, 57, 28, 'ای کسانی که ایمان آورده‌اید، از خدا پروا کنید و به پیامبرش محمّد ایمانی راستین بیاورید (همه دستورات او را اطاعت کنید) که خداوند به ازای ایمان و اطاعت شما دو بهره از رحمت خود را به شما ارزانی خواهد کرد و برایتان نوری قرار خواهد داد که در دنیا در پرتو آن حرکت کنید و نیک و بد را بشناسید و در آخرت راه بهشت را بیابید و آن را بپیمایید، و گناهان شما را خواهد بخشید، و خدا آمرزنده و مهربان است.'),
(5104, 57, 29, 'آری، ما مؤمنان را به درجات بالاتر ایمان دعوت کردیم و دو بهره از رحمت خود را به آنان وعده دادیم تا اهل کتاب [یهود و نصارا] باور نکنند که مؤمنان نمی‌توانند به چیزی از فضل خدا دست یابند. این وعده برای آن است که فضل و بخشش به دست خداست، آن را به هر که بخواهد می‌دهد، و خدا دارای فضل و بخشش بزرگ است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'ای پیامبر، خداوند درخواست آن زنی را که با تو درباره شوهرش جدال می‌کند، و از اندوه خود به خدا شکایت می‌برد شنید و اجابت کرد و خدا گفتوگوی شما دو تن را می‌شنود، زیرا خدا شنوا و بیناست.'),
(5106, 58, 2, 'کسانی از شما که همسرانشان را با (ظِهار) چون مادران خویش می‌شمرند، آنان مادرانشان نیستند. مادرانشان تنها کسانی اند که آنان را زاده‌اند، و اینان با ظهار سخنی زشت و باطل می‌گویند و مرتکب گناه می‌شوند، و البته خداوند از لغزش آنان می‌گذرد و گناهشان را می‌آمرزد.'),
(5107, 58, 3, 'و کسانی که زنان خود را ظهار می‌کنند، سپس بخواهند به آنچه گفته‌اند باز گردند تا آن را بشکنند، باید برده‌ای آزاد کنند البته پیش از آن که آن دو (ظهارکننده و همسرش) آمیزش کنند. این حکم به شما سفارش می‌شود و خدا به آنچه انجام می‌دهید آگاه است.'),
(5108, 58, 4, 'و آن کس که برده‌ای برای آزاد کردن نیافت باید پیش از آن که با یکدیگر آمیزش کنند دو ماه پیاپی روزه بدارد، و آن کس که نتواند دو ماه پیاپی روزه بدارد، باید شصت مسکین را طعام دهد. این کفّاره‌ای که به سزای روی آوردنتان به سنّت جاهلی مقرّر داشتیم، بدان جهت است که به خدا و رسولش ایمان بیاورید، و اینها حدود خداست و کسانی که آنها را نپذیرند کافرند و برای کافران عذابی دردناک خواهد بود.'),
(5109, 58, 5, 'بی تردید، کسانی که با خدا و رسولش مخالفت می‌کنند خوار و ذلیل خواهند شد; چنان که کسانی که پیش از اینان بودند خوار و ذلیل شدند به یقین ما آیاتی روشن که بر صدق پیامبر ما دلالت دارد فرو فرستادیم، و کسانی که به آیات ما کافر شوند، عذابی خوارکننده خواهند داشت.'),
(5110, 58, 6, 'این عذاب در روزی است که خدا همه آنان را از گورها برمی انگیزد و آنان را به آنچه کرده‌اند آگاه می‌سازد، چرا که خدا بر شمارش آنچه به جای آورده‌اند احاطه دارد و آنان آن را فراموش کرده‌اند، و خداوند بر هر چیزی چیره است و برای همه چیز آشکار است.'),
(5111, 58, 7, 'آیا ندانسته‌ای که خدا آنچه را در آسمان‌ها و آنچه را در زمین است می‌داند؟ هیچ رازی میان سه تن روی نمی‌دهد مگر این که او چهارمین آنهاست، و نه میان پنج تن مگر این که او ششمین آنهاست، و نه کمتر از این تعداد و نه بیشتر از آن، مگر این که هر کجا باشند او با آنها و از رازشان آگاه است. سپس روز قیامت آنان را به حقیقت آنچه کرده‌اند آگاه می‌کند. به یقین خدا به هر چیزی به خوبی داناست.'),
(5112, 58, 8, 'آیا به کسانی ننگریسته‌ای که از نجوا کردن نهی شده‌اند ولی هنوز از این کار باز نایستاده‌اند و پیوسته به آنچه از آن نهی شده‌اند باز می‌گردند؟ آنان درباره گناه و تجاوز به حقوق مردم و سرپیچی از فرمان‌های پیامبر با یکدیگر نجوا می‌کنند، و هنگامی که نزد تو می‌آیند با چیزی بر تو سلام می‌کنند که خدا با آن بر تو سلام نکرده است، و در آن حال با خود می‌گویند: اگر او پیامبری بر حق است چرا خدا ما را به سبب آنچه به او می‌گوییم و این گونه اهانتش می‌کنیم عذاب نمی‌کند؟ بدانند که دوزخ برای آنان بس است که به آن درمی آیند و به آتشش می‌سوزند و آن بد بازگشتگاهی است.'),
(5113, 58, 9, 'اما شما ای کسانی که ایمان آورده‌اید، هنگامی که با یکدیگر نجوا می‌کنید، از روی آوردن به گناه و تجاوز به حقوق مردم و سرپیچی از دستورات رسول خدا سخن مگویید، بلکه در نجواهایتان از گرایش به کارهای پسندیده و رعایت تقوا سخن به میان آورید و از خدا که به سوی او محشور می‌شوید پروا بدارید.'),
(5114, 58, 10, 'جز این نیست که نجوای منافقان و بیماردلان برخاسته از وسوسه‌های شیطان است. او آنان را به نجوا وامی دارد تا کسانی را که ایمان آورده‌اند اندوهگین کند و افکارشان را به گمان این که حادثه ناگواری رخ داده است مضطرب و پریشان سازد. ولی مؤمنان بدانند که کارها به دست خداست و شیطان و دسیسه‌های او زیانی به آنان نمی‌رساند، مگر آن که خدا رخصت دهد. پس مؤمنان باید تنها بر خدا توکّل کنند.'),
(5115, 58, 11, 'ای کسانی که ایمان آورده‌اید، هنگامی که در هر مجلسی به شما گفته شد برای دیگران جای بگشایید، برای آنان جای بگشایید در این صورت خدا به پاداش آن بهشت را برای شما فراخ می‌سازد، و هنگامی که به شما گفته شد برخیزید تا کسانی که در علم و تقوا از شما برترند بنشینند، برخیزید. اگر چنین کنید، خداوند کسانی از شما را که ایمان آورده‌اند، یک درجه و کسانی را که علاوه بر ایمان، دانش نیز به آنان داده شده است چندین درجه بالا می‌برد، و خدا به آنچه می‌کنید آگاه است.'),
(5116, 58, 12, 'ای کسانی که ایمان آورده‌اید، هنگامی که می‌خواهید با پیامبر نجوا کنید، پیش از نجوایتان به تهیدستی صدقه‌ای پیشکش کنید. این کار برای شما بهتر و برای دل هایتان پاکیزه‌تر است; و اگر چیزی نیافتید که صدقه دهید (خود تهیدست هستید) بر شما تکلیفی نیست، خدا از شما درمی گذرد، زیرا خدا آمرزنده و مهربان است.'),
(5117, 58, 13, 'آیا ترسیدید از این که پیش از نجوای خود با رسول خدا صدقاتی پیشکش کنید؟ اکنون که چنین نکردید و خدا هم این گناه را بر شما بخشود و آن تکلیف را از شما برداشت، بر دیگر تکالیف خود پایدار باشید، نماز را برپا دارید و زکات مال خویش بپردازید و خدا و پیامبرش را فرمان برید که خدا به آنچه می‌کنید آگاه است.'),
(5118, 58, 14, 'آیا به آنان که با قوم یهود ـ که خدا بر آنان خشم گرفته است ـ طرح دوستی افکندند نظر نیندوخته‌ای؟ آنان نه در زمره شما مؤمنانند و نه در زمره یهودیان، و به دروغ سوگند یاد می‌کنند که از شمایند، و خود نیز می‌دانند که دروغ می‌گویند.'),
(5119, 58, 15, 'خداوند برای آنان عذابی سخت فراهم کرده است; زیرا کارهایی که کردند و بر آن اصرار ورزیدند ناروا بود.'),
(5120, 58, 16, 'سوگندهای خود را برای دفع تهمت از خویش سپر قرار دادند و با نفاقشان خود را از راه خدا محروم کردند و مردم را نیز از راه خدا بازداشتند; از این رو برای آنان عذابی خوارکننده خواهد بود.'),
(5121, 58, 17, 'آنان برای دستیابی به زندگی دنیا از راه خدا رویگردان شدند، ولی بدانند که اموال و فرزندانشان هرگز چیزی از عذاب ابدی را از آنان کفایت نمی‌کند و نیازشان را به یاری خدا برطرف نمی‌سازد و آنان اهل آتشند و در آن ماندگار خواهند بود.'),
(5122, 58, 18, 'این در روزی است که خدا آنان را یکجا از گورها برمی انگیزد، و آنان همان گونه که امروز برای شما سوگند می‌خورند برای خدا سوگند یاد می‌کنند که مؤمن بوده‌اند، و می‌پندارند که بر جایگاهی قرار دارند که می‌توانند حقیقت را کتمان کنند. آگاه باشید که آنان قطعاً دروغگویند.'),
(5123, 58, 19, 'شیطان بر آنان چیره شده و در نتیجه یاد خدا را از خاطر آنان برده است. آنان حزب شیطانند. ای پیامبر، بدان که یقیناً حزب شیطان زیانکارند.'),
(5124, 58, 20, 'چرا که آنان با خدا و پیامبر سر ستیز دارند و قطعاً کسانی که با خدا و پیامبرش می‌ستیزند، در زمره پست ترین‌ها خواهند بود.'),
(5125, 58, 21, 'خدا مقرّر کرده است که: من حتماً بر تکذیب کنندگان پیروز خواهم بود و نیز پیامبرانم بر آنان پیروز خواهند شد، زیرا خدا نیرومند و مقتدری شکست ناپذیر است.'),
(5126, 58, 22, 'قومی را نخواهی یافت که به خدا و روز رستاخیز ایمان داشته باشند و با این حال کسانی را که با خدا و رسولش مخالفت کرده و در ستیز بوده‌اند دوست بدارند; هر چند پدران یا پسران یا برادران یا خاندانشان باشند. اینانند که خدا ایمان را در دل هایشان تثبیت کرده و آنان را به روحی که حیاتی ویژه به آنان بخشیده، نیرومند ساخته است و روز قیامت آنان را به بوستان‌های پردرخت که نهرها از زیر آنها روان است درمی آورَد در حالی که در آن جا جاودانه‌اند; چرا که خدا از آنان خشنود بود و آنان نیز از خدا خشنود بودند. آنان حزب خدایند. بدان که تنها حزب خدا سعادتمندند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'آنچه در آسمان‌ها و آنچه در زمین است خدا را به پاکی و پیراستگی از هر عیب و نقصی یاد کرده‌اند و اوست آن مقتدر شکست ناپذیری که کارهایش همه استوار و متقن است.'),
(5128, 59, 2, 'اوست آن که کافران اهل کتاب (یهودیان بنی نضیر) را در نخستین مرحله‌ای که کوچشان داد، از خانه و دیارشان بیرون کرد. شما مؤمنان گمان نمی‌کردید که آنان با این نیرو و قدرتی که دارند از دیارشان بیرون روند و خود نیز می‌پنداشتند که دژهایشان مانع تسلّط خدا بر آنان خواهد بود، امّا خدا از جایی که تصوّر نمی‌کردند بر آنان درآمد و در دل هایشان بیم افکند، تا آن جا که به دست خود و به دست مؤمنان خانه‌های خود را ویران می‌کردند. پس ای دیدوران از کارهای خدا که آنها را می‌نگرید عبرت گیرید.'),
(5129, 59, 3, 'و اگر خدا این جلای وطن را بر آنان مقرر نکرده بود، قطعاً آنان را در دنیا عذاب می‌کرد، و [به هر حال] برای آنان در آخرت عذاب آتش خواهد بود.'),
(5130, 59, 4, 'این کیفر (کوچ اجباری بنی نضیر و عذاب اخروی آنها) برای آن است که آنان از سر عناد با خدا و پیامبرش مخالفت کردند و هر کس عناد ورزد و با خدا مخالفت کند به عذابی سخت گرفتار می‌شود، چرا که خدا سخت کیفر است.'),
(5131, 59, 5, 'هر نخل سرسبز و خرّمی را که شما مؤمنان به هنگام محاصره دژهایشان بریدید یا آن را بر ساقه اش بر جای نهادید، به اذن خدا بود. او به شما چنین رخصتی داد تا اهداف خود را عملی کند و آن فاسقان را خوار و ذلیل گرداند.'),
(5132, 59, 6, 'و آن اموالی را که خداوند از بنی نضیر به پیامبرش باز گردانید و در اختیار او نهاد، شما بر آنها اسب و شتری نتاختید تا در آنها حقی داشته باشید، ولی خدا پیامبرانش را بر هر که بخواهد چیره می‌کند، و او پیامبر را بر بنی نضیر پیروز ساخت تا اموالشان به او اختصاص یابد، و خدا بر هر کاری تواناست.'),
(5133, 59, 7, 'آنچه خدا از اموالِ مردم آن آبادی‌ها، بدون پیکار مجاهدان، به پیامبرش بازگردانید، سهمی از آن به خدا اختصاص دارد که باید در راه او هزینه شود، و سهمی از آن برای پیامبر و سهمی برای خویشاوندان او و یتیمان و مستمندان و در راه ماندگان است. این تقسیم بدان جهت است که این اموال میان توانگرانِ شما دست به دست نگردد. آنچه را که رسول خدا به شما پرداخت آن را بگیرید و آنچه شما را از آن بازداشت از آن دست بدارید و از خدا پروا کنید و با پیامبر او مخالفت نکنید که خدا سخت کیفر است.'),
(5134, 59, 8, 'سهم خدا که باید در راه او هزینه شود از آنِ مهاجرانِ تهیدستی است که از خانه‌ها و اموالشان بیرون رانده شده و به مدینه هجرت کردند و اینک در جستوجوی رزقی از جانب خدا و در پی خشنودی او هستند و خدا و پیامبرش را با جان و دل یاری می‌کنند. اینانند راستگویان.'),
(5135, 59, 9, 'و نیز از آنِ انصار تهیدستی است که پیش از آمدنِ مهاجران به مدینه، آن شهر را به صورت مرکزی دینی که مؤمنان بتوانند در آن سکونت کنند درآوردند و ایمان را چنان بنا نهادند که مؤمنان بتوانند به مقتضای ایمانشان عمل کنند. آنان کسانی را که از مکّه هجرت کرده و به دیار آنان روی آورده‌اند دوست می‌دارند و از آنچه به مهاجران داده شده است در خود احساس نیاز نمی‌کنند تا از بهره مندی آنان از غنایم و محرومیت خود اندوهگین شوند، بلکه در استفاده از اموالشان، مهاجران را بر خود مقدم می‌دارند، گرچه خودشان سخت نیازمند باشند. و هر کس از بخل و آز نفس خود مصون بماند و از بخشش اموال خود و از این که مالی در اختیار دیگران نهاده شود، دلتنگ نشود، آنانند سعادتمندان.'),
(5136, 59, 10, 'و نیز سهم خدا از (فییء) از آنِ مسلمانان مستمندی است که پس از مهاجران و انصار (بعد از فتح مکّه) به اسلام روی می‌آورند; آنان که می‌گویند: پروردگارا، ما و برادرانمان را که در ایمان بر ما پیشی گرفتند ببخشای و در دل‌های ما دشمنی کسانی را که ایمان آورده‌اند قرار مده. پروردگارا، به یقین تو رئوف و مهربانی.'),
(5137, 59, 11, 'آیا به منافقان ننگریسته‌ای که به برادران خود از اهل کتاب (یهودیان بنی نضیر) که کفر ورزیده اند می‌گویند: سوگند یاد می‌کنیم که اگر شما از دیارتان بیرون رانده شدید ما نیز همراه شما خارج می‌شویم و هرگز از کسی درباره جدا شدن از شما فرمان نمی‌بریم و اگر با شما پیکار شد حتماً شما را یاری می‌کنیم. و خدا گواهی می‌دهد که منافقان دروغ می‌گویند و هرگز به وعده خود وفا نمی‌کنند.'),
(5138, 59, 12, 'بی گمان اگر یهودیان بنی نضیر از دیارشان بیرون رانده شوند منافقان همراه آنان بیرون نمی‌روند و اگر با آنان پیکار شود، قطعاً آنان را یاری نمی‌کنند، و اگر یاریشان کنند ـ که نخواهند کرد ـ پشت کرده از صحنه کارزار می‌گریزند، آن گاه از سوی هیچ کس یاری نمی‌شوند و به هلاکت می‌رسند.'),
(5139, 59, 13, 'منافقان از آن روی در برابر شما تاب مقاومت ندارند که از شما می‌هراسند. هراسی که آنان از شما در دل خود دارند بیش از هراسشان از خداست، و این بدان سبب است که آنان مردمی هستند که نمی‌فهمند که کارها به دست خداست.'),
(5140, 59, 14, 'هیچ کدام از یهودیان بنی نضیر و منافقانِ هم پیمانشان جز در شهرهایی برخوردار از استحکامات یا از پشت دیوارها با شما نمی‌جنگند. زورمندی آنان میان خودشان سخت است، اما بر اثر ترسی که خدا در دل هایشان نهاده است با شما رو در رو به کارزار نمی‌آیند. می‌پنداری گروهی متحد و یکپارچه‌اند، در حالی که دل هایشان پراکنده است و اُلفتی میانشان نیست. این بدان سبب است که آنان مردمی هستند که خردورزی نمی‌کنند.'),
(5141, 59, 15, 'حکایت آنان با منافقان همچون حکایت یهودیان بنی قَیْنُقاع با آنان است که اندک زمانی پیش از اینان در مدینه بودند. آنان نیز عهد خود با پیامبر را شکستند و به وعده منافقان دل بستند و فرجام شوم کار خود را در دنیا چشیدند و در آخرت نیز عذابی دردناک خواهند داشت.'),
(5142, 59, 16, 'داستان منافقان در فریب دادن بنی نضیر همچون داستان شیطان است که انسان را با وعده‌های خود فریب می‌دهد و از او می‌خواهد که کافر شود; هنگامی که انسان کافر شد و با فرجام شوم کفر خود مواجه گردید، شیطان او را رها می‌کند و می‌گوید: من از تو تبری می‌جویم، و به تمسخر می‌گوید من از خدا که پروردگار جهان هاست می‌ترسم.'),
(5143, 59, 17, 'پس فرجام شیطان فریبگر و انسان فریب خورده آن شد که هر دو به آتش درآیند و برای همیشه در آن بمانند، و این است سزای ستمگران.'),
(5144, 59, 18, 'پروا داشتن از خدا سبب مصون ماندن از فریب شیطان است; پس ای کسانی که ایمان آورده‌اید از خدا پروا کنید و به تکالیف الهی خود عمل نمایید، و هر کسی از شما باید بنگرد که برای فردای آخرتش چه پیش فرستاده است. از خدا بترسید و به حساب کارهایی که انجام داده‌اید برسید و نیک و بدش را دریابید که خدا به آنچه می‌کنید آگاه است.'),
(5145, 59, 19, 'و مانند کسانی نباشید که خدا را از یاد بردند و خدا هم حقیقت خودشان را از یادشان برد تا آن که ندانستند که کیستند و چه باید بکنند. اینان از مسیر بندگی خدا خارج شده‌اند.'),
(5146, 59, 20, 'دوزخیان با بهشتیان یکسان نیستند; بهشتیانند که سعادتمندند.'),
(5147, 59, 21, 'اگر ما این قرآن را بر کوهی فرو فرستاده بودیم، آن را با همه صلابت و عظمتش، در برابر خدا خاشع و از ترس او از هم پاشیده می‌دیدی. ما این مَثَل‌ها را برای مردم می‌زنیم، باشد که در قرآن بیندیشند.'),
(5148, 59, 22, 'اوست خدایی که جز او معبودی شایسته پرستش نیست و به نهان و آشکار جهان داناست. اوست که گسترده مهر و مهربان است.'),
(5149, 59, 23, 'اوست خدایی که جز او معبودی شایسته پرستش نیست; همان فرمانروایی که تدبیر امور بندگان در اختیار اوست و در کمال پاکی و طهارت است. با بندگانش برخوردی سالم و درست دارد. ایمنی بخش، برتری چیره، شکست ناپذیر، جبران کننده کاستی‌ها و بزرگ منش و با عظمت است. منزّه است خدا از این که به او شرک ورزند.'),
(5150, 59, 24, 'اوست خدا که آفریننده، پدیدآورنده و صورت آفرین است; نیکوترین نام‌ها تنها از آنِ اوست. آنچه در آسمان‌ها و زمین است او را به پاکی و پیراستگی از هر عیب و نقصی یاد کرده‌اند، و اوست آن مقتدر شکست ناپذیری که کارهایش همه استوار و متقن است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'ای کسانی که ایمان آورده‌اید، دشمنان من و دشمنان خودتان را دوست مگیرید. شما با آنان طرح دوستی می‌افکنید در صورتی که آنان قرآن و آیینی را که برای شما آمده و سراسر حق است انکار کردند، در حالی که پیامبر و شما را بدین سبب که به خدا، پروردگارتان ایمان آوردید از مکه بیرون راندند. پس اگر برای جهاد در راه من و دستیابی به خشنودی من هجرت کردید، با آن مشرکانِ حق ستیز دست دوستی ندهید. شما در نهان به آنان ابراز مودّت می‌کنید، در صورتی که من به آنچه شما نهان داشته و به آنچه آشکار کرده‌اید داناترم. و هر کس از شما به این کار اقدام کند به یقین راه راست را گم کرده است.'),
(5152, 60, 2, 'اگر بر شما دست یابند، با شما دشمنی می‌کنند، هر چند دست دوستی به آنان داده باشید. آنان در این صورت دست و زبانشان را به بدی به سوی شما می‌گشایند (شما را می‌کُشند یا اسیر می‌کنند و شکنجه می‌دهند و به شما ناسزا می‌گویند) و دوست دارند که شما از این رهگذر کافر شوید.'),
(5153, 60, 3, 'هرگز خویشاوندان و فرزندانتان ـ که به خاطر آنان با مشرکان طرح دوستی افکندید و سزاوار عذاب الهی شدید ـ روز قیامت به شما سودی نمی‌رسانند. آن روز خداوند پیوندها را می‌بُرد و میان شما و آنان جدایی می‌افکند، و خدا به آنچه می‌کنید بیناست.'),
(5154, 60, 4, 'قطعاً در گفتار و رفتار ابراهیم و کسانی که با او بودند برای شما اقتدای نیکویی است; آن گاه که به قوم خود گفتند: ما از شما و آنچه به جای خدا می‌پرستید دوری می‌جوییم و با شرک ورزیدن شما مخالفت می‌کنیم و میان ما و شما برای همیشه دشمنی و کینه پدید آمده است، مگر این که به خدای یکتا ایمان بیاورید. آری، سخنان ابراهیم و همگامان او تبری از کافران بود، جز این سخن ابراهیم که به پدرش گفت: حتماً برای تو از خدا آمرزش می‌طلبم، ولی من در برابر خدا مالک چیزی نیستم و نمی‌توانم آنچه را می‌طلبم برای تو محقّق سازم. ابراهیم و همگامانش در پی تبری از قومشان گفتند: پروردگارا، ما تنها بر تو توکّل کرده و به سوی تو باز آمده‌ایم و بازگشت هر چیزی به سوی توست.'),
(5155, 60, 5, 'پروردگارا، ما را مایه آزمون کافران قرار مده (آنان را بر ما چیره مساز تا بدین وسیله ایشان را بیازمایی)، پروردگارا، ما را ببخشای که تویی مقتدر و شکست ناپذیر و تویی که کارهایت همه استوار و حکیمانه است.'),
(5156, 60, 6, 'به یقین در توکل و نیایش ابراهیم و همگامانش به درگاه خدا برای شما اقتدای نیکویی است، البته این از عهده کسانی ساخته است که به خدا و روز واپسین امید دارند (خدا را باور کرده و به او دل بسته‌اند و به امید دستیابی به پاداش آخرت کارهای شایسته می‌کنند) و هر کس از فرمان خدا روی برتابد و با کافران دوستی کند خود زیان کرده است، زیرا خداوند به اطاعت او نیازی ندارد و خداست که در مورد دستورات خود که همه مصلحت آمیز است، ستوده است.'),
(5157, 60, 7, 'امید است که خداوند به کافرانِ مکّه این توفیق را بدهد که مسلمان شوند; آن گاه میان شما و کسانی از آنان که به فرمان خدا با آنان دشمنی کرده‌اید محبتی برقرار سازد، و خدا بر این کار تواناست. و او هرگاه بندگانش توبه کنند و اسلام آورند گناهانشان را می‌بخشد و آنان را از رحمت خود برخوردار می‌سازد.'),
(5158, 60, 8, 'خداوند شما را از دوستی با مشرکان مکّه بازداشته است ولی او شما را از کسانی که در امر دین با شما به کارزار برنخاسته و شما را از دیارتان بیرون نکرده‌اند باز نمی‌دارد که با آنها نیکی کنید و با آنان به عدل و داد رفتار نمایید، چرا که خداوند دادگران را دوست می‌دارد.'),
(5159, 60, 9, 'خداوند شما را فقط از دوستی با کسانی نهی می‌کند که در امر دین با شما پیکار کرده و شما را از دیارتان بیرون رانده و بر بیرون راندنتان یکدیگر را یاری نموده‌اند. و کسانی که با آنان طرح دوستی بریزند قطعاً ستمکارند.'),
(5160, 60, 10, 'ای کسانی که ایمان آورده‌اید، هرگاه زنان با ایمان به مدینه هجرت کردند و نزد شما آمدند، آنان را در مورد هجرت و ایمانشان بیازمایید و [به نتیجه آزمون اکتفا کنید که] خداوند به ایمانشان داناتر است. پس اگر با ایمانشان یافتید، آنان را به کافران باز مگردانید; نه زنان با ایمان برای مردان کافر حلالند و نه کافران برای زنان با ایمان، چرا که پیوند ازدواج میان آنان گسسته است; و باید به شوهرانشان آنچه را که به عنوان مهریه پرداخته‌اند بدهید. و بر شما گناهی نیست که با این زنان ازدواج کنید به شرط آن که مَهرشان را به آنان بدهید. شما بعد از آن که اسلام آوردید نباید زنان کافر خود را در همسری نگه دارید. و اگر همسر شما به کافران پیوست، از آنان مهریه‌ای را که به همسرانتان داده‌اید مطالبه کنید، و کافران نیز حق دارند که اگر همسرانشان به شما پیوستند، مهریه آنها را از شما بخواهند. این است حکمِ خدا که میان شما داوری می‌کند و خدا دانا و حکیم است.'),
(5161, 60, 11, 'و اگر با پیوستن همسرانتان به کفار، مهریه‌ای که از همسران شماست از دستتان رفت (پس از مطالبه آن را به شما ندادند)، آن گاه در جنگ غنیمتی از آنان به دست آوردید، باید به کسانی که همسرانشان به کافران پیوسته‌اند، از آن غنیمت مانند آنچه به عنوان مَهر هزینه کرده‌اند بدهید، و از خدا که به او ایمان دارید پروا داشته باشید [و احکام و حدود او را رعایت کنید].'),
(5162, 60, 12, 'ای پیامبر، هرگاه زنانی که ایمان آورده‌اند نزد تو آیند که با این شرایط با تو بیعت کنند که هیچ چیزی نه بُت و نه غیر آن را شریک خدا قرار ندهند، و از مال هیچ کس نه مال همسرانشان و نه مال دیگران دزدی نکنند، و آشکارا و نهانی مرتکب زنا نشوند و به هیچ وجه فرزندانشان را نکشند و فرزند دیگران را که میان دست و پای خود بر زمین می‌نهند، به دروغ از شوهران خود نخوانند، و در هیچ کار پسندیده‌ای که به آن فرمان می‌دهی، تو را نافرمانی نکنند، با آنان بیعت کن. و آمرزش گناهانشان را از خدا بخواه که خداوند آمرزنده و مهربان است.'),
(5163, 60, 13, 'ای کسانی که ایمان آورده‌اید، با یهودیان، همان قومی که خداوند بر آنان خشم گرفته است طرح دوستی نیفکنید; آنان از دستیابی به پاداش آخرت ناامید شده‌اند; همان گونه که منکران رستاخیز از [زنده شدن] مردگانِ دفن شده در گورها، ناامیدند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'آنچه در آسمان‌ها و زمین است خدا را به پاکی و پیراستگی از هر عیب و نقصی یاد کرده‌اند، و اوست آن مقتدر شکست ناپذیری که کارهایش همه استوار و متقن است.'),
(5165, 61, 2, 'ای کسانی که ایمان آورده‌اید، چرا چیزی می‌گویید که آن را انجام نمی‌دهید؟ شما پایداری در جهاد را وعده می‌دهید ولی به میدان نمی‌آیید و یا از صحنه کارزار می‌گریزید.'),
(5166, 61, 3, 'نزد خدا سخت مورد خشم است که چیزی را بگویید که انجام نمی‌دهید.'),
(5167, 61, 4, 'قطعاً خدا کسانی را که صف بسته در راه او پیکار می‌کنند و در این راه استقامت میورزند چنان که گویی بنایی پرداخته شده از سرب اند دوست می‌دارد.'),
(5168, 61, 5, 'ای مؤمنان، از گرفتار آمدن به فرجامی چون فرجام امّت موسی بیم داشته باشید و به پیامبرتان آزار نرسانید و یاد کنید هنگامی را که موسی به قوم خود گفت: ای قومِ من، چرا با این که می‌دانید من فرستاده خدا به سوی شما هستم مرا آزار می‌دهید؟ پس چون با آزار موسی به باطل گرایش یافتند، خدا هم دل هایشان را از گرایش به حق بازداشت و توفیق هدایت را از آنان سلب کرد. آری، کسانی که پیامبران را آزار دهند فاسقند و خداوند فاسقان را هدایت نمی‌کند.'),
(5169, 61, 6, 'و یاد کنید زمانی را که عیسی پسر مریم گفت: ای بنی اسرائیل، من فرستاده خدا به سوی شمایم و تورات را که پیش از من بوده است تصدیق می‌کنم و شما را به پیامبری که پس از من می‌آید و نام او احمد است نوید می‌دهم. پس هنگامی که او (احمد) با دلایل و معجزات روشن به سویشان آمد رسالتش را تکذیب کردند و گفتند: این جادویی آشکار است.'),
(5170, 61, 7, 'آنان با تکذیب رسالت پیامبر در حقیقت بر خدا افترا بستند، و چه کسی ستمکارتر است از آن کس که بر خدا دروغ بسته با وجود این که به تسلیم شدن در برابر خدا فرا خوانده شده است؟ و خدا مردم ستمکار را به راه درست نمی‌رساند.'),
(5171, 61, 8, 'آنان می‌خواهند نور خدا را با دهان خود خاموش کنند، در صورتی که خدا نور خود را به کمال می‌رساند، هر چند کافران آن را خوش نداشته باشند.'),
(5172, 61, 9, 'اوست آن که پیامبرش محمّد را با رهنمود و دین حق به رسالت فرستاد تا آن را بر همه ادیان فایق گرداند، گرچه مشرکان آن را خوش نداشته باشند.'),
(5173, 61, 10, 'ای کسانی که ایمان آورده‌اید، آیا شما را به تجارتی سودمند که شما را از عذابی دردناک می‌رهاند راه نمایم؟'),
(5174, 61, 11, 'به خدا و رسولش ایمانی راستین می‌آورید و در راه خدا با مال و جانتان جهاد می‌کنید. این ایمان و جهاد ـ اگر بدانید ـ برای شما بهتر است.'),
(5175, 61, 12, 'اگر ایمان بیاورید و جهاد کنید، خدا گناهانتان را بر شما می‌بخشاید و شما را در بوستان‌های پردرخت که از زیر آنها نهرها روان است و در خانه‌هایی خوش که در باغ‌های بهشتِ جاودان است درمی آورد. این است سعادت بزرگ.'),
(5176, 61, 13, 'و نعمتی دیگر نیز نصیب شما خواهد شد، که آن را دوست می‌دارید; آن نصرتی از جانب خدا و فتحی نزدیک است. و تو ای پیامبر، مؤمنان را به بهشت و پیروزی بر کافران بشارت ده.'),
(5177, 61, 14, 'ای کسانی که ایمان آورده‌اید، خدا را با ایمان و جهاد در راه او، و پیمودن راهی که پیامبرش در آن گام نهاده است یاری کنید; چنان که عیسی بن مریم به حواریّون گفت: چه کسانی یاران منند و مرا در سیر به سوی خدا یاری می‌دهند؟ حواریّون گفتند: ماییم یاران خدا و کمک کاران تو. پس طایفه‌ای از بنی اسرائیل ایمان آوردند و طایفه‌ای از آنان کافر شدند، و ما کسانی را که ایمان آورده بودند بر دشمنانشان قدرت دادیم و در نتیجه بر آنان پیروز شدند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'آنچه در آسمان‌ها و آنچه در زمین است، خدا را تسبیح می‌گویند و او را به پاکی از هر عیب و نقصی یاد می‌کنند; همو که فرمانروای هستی و پیراسته از نیازمندی و مقتدرِ شکست ناپذیری است که کارهایش همه از روی حکمت است.'),
(5179, 62, 2, 'اوست که در میان مردم عرب که خواندن و نوشتن نمی‌دانستند، پیامبری از خودشان برانگیخت; پیامبری که آیات خدا را بر آنان تلاوت می‌کند و آنان را به وسیله کارهای شایسته و اخلاق نیکو رشد می‌دهد و قرآن و معارف آن را به ایشان می‌آموزد، و آنان قطعاً پیش از این در گمراهی آشکاری فرو رفته بودند.'),
(5180, 62, 3, 'و نیز برای جمعیت دیگری از آن مردم که هنوز به ایشان نپیوسته‌اند آن پیامبر را به رسالت برانگیخت. و خداست که اراده اش غالب و کارهایش همه براساس حکمت است.'),
(5181, 62, 4, 'این بعثت فضل خداست که آن را به هر که بخواهد عطا می‌کند، و او خواسته است که آن را به محمّد ارزانی دارد، و خدا دارای تفضّلی بزرگ است.'),
(5182, 62, 5, 'داستان کسانی که احکام و معارف تورات به آنان آموخته و بر عهده آنان نهاده شد ولی به آن عمل نکردند و به مقصد نرساندند، همچون داستان الاغ است که کتاب­هایی را بر پشت خود می­بَرَد و از حمل کردن آنها جز خستگی نصیبی ندارد. بد است داستان مردمی که آیات خدا را دروغ شمردند. آنان ستمکارند و خدا مردم ستمکار را هدایت نمی‌کند.'),
(5183, 62, 6, 'بگو: ای یهودیان، اگر تصوّر می‌کنید که شما دوستان خدایید نه مردم دیگر، اگر راست می‌گویید تمنّای مرگ کنید تا به دیدار خدا نایل شوید، چرا که دوست شیفته دیدار دوست خویش است.'),
(5184, 62, 7, 'ولی به سبب گناهانی که پیش فرستاده و ستم‌هایی که کرده‌اند هرگز تمنّای مرگ نخواهند کرد، و خدا به حال ستمکاران به خوبی داناست.'),
(5185, 62, 8, 'بگو: مرگی که از آن می‌گریزید قطعاً به دیدار شما خواهد آمد، سپس به سوی آن دانای نهان و آشکار باز گردانده می‌شوید، آن گاه شما را به حقیقت و پی آمدهای ناگوار آنچه می‌کردید، آگاه خواهد ساخت.'),
(5186, 62, 9, 'ای کسانی که ایمان آورده‌اید، چون در روز جمعه برای نماز جمعه بانگ اذان داده شد، به سوی نماز که ذکر خداست بشتابید و داد و ستد و دیگر کارها را واگذارید. این برای شما بهتر است اگر بدانید.'),
(5187, 62, 10, 'پس وقتی نماز جمعه گزارده شد، رواست که در زمین پراکنده شوید و از فضل خدا بجویید (در پی دستیابی به عطایای دنیوی و اخروی خدا برآیید) و خدا را بسیار یاد کنید، باشد که سعادتمند شوید.'),
(5188, 62, 11, 'ای پیامبر، قدرناشناسانِ معارف دینی چون داد و ستدی یا طبل و دهلی را ببینند، به سوی آن روی می‌آورند و پراکنده می‌شوند و تو را در حالی که به قرائت خطبه نماز جمعه ایستاده‌ای رها می‌کنند. بگو: پاداشی که نزد خداست از آن طبل و دهل و از آن داد و ستد بهتر است، و خداوند بهترین روزی دهندگان است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'ای پیامبر، چون منافقان نزد تو آیند می‌گویند: گواهی می‌دهیم که تو قطعاً فرستاده خدایی. و خدا می‌داند که تو قطعاً فرستاده او هستی، و خدا گواهی می­دهد که منافقان قطعاً دروغ می­گویند و شهادتی را که اظهار می­دارند باور ندارند.'),
(5190, 63, 2, '[برای مصون ماندن از تعرض مؤمنان] سوگندهای دروغ خود را سپر خویش ساخته و با توطئه‌های گوناگون مردم را از راه خدا باز داشته‌اند. راستی که آنان آنچه به جای آورده‌اند بد بوده است.'),
(5191, 63, 3, 'این بدان سبب است که آنان ایمان آوردند و سپس کافر شدند [و کفر خود را نهان داشتند]، پس بر دل هایشان مهر زده شد، از این رو حق را درک نمی‌کنند.'),
(5192, 63, 4, 'و چون آنان را ببینی، از آن جا که آراسته و خوش سیما و دارای اندامی متناسبند، هیکل هایشان تو را به شگفت می‌آورد، و اگر زبان بگشایند، چون خوش سخن و چرب زبانند به سخنشان گوش می‌سپاری، ولی در واقع همچون چوب‌هایی تکیه داده بر دیوار شبح‌هایی بی روح اند که هیچ خیری از آنان تراوش نمی‌کند; همواره در ترس و هراس به سر می‌برند که مبادا نفاقشان آشکار شود; از این رو هر بانگی را به زیان خویش می‌پندارند. آنان دشمنان تواند که دشمنی را به نهایت رسانده‌اند; از آنان برحذر باش; خدا بکشدشان; چگونه از حق باز داشته و به سوی باطل رانده می‌شوند؟'),
(5193, 63, 5, 'و هنگامی که بخشی از خیانت‌ها و کجروی هایشان آشکار گردد و به آنان گفته شود: بیایید تا پیامبر خدا برای شما از خدا آمرزش بخواهد، سرهایشان را برمی گردانند، و آنان را می‌بینی که از گوینده آن سخن روی برمی تابند و تکبّرکنان از پذیرفتن سخن او خودداری می‌کنند.'),
(5194, 63, 6, 'برای آنان یکسان است، چه برایشان آمرزش بخواهی یا برایشان آمرزش نخواهی، خدا هرگز آنان را نخواهد آمرزید، زیرا آنان فاسقند و خدا مردم فاسق را هدایت نمی‌کند و به راه سعادت و بهشت نمی‌برد.'),
(5195, 63, 7, 'اینان همان کسانی اند که [به قوم و قبیله خود] می‌گویند: به مؤمنانی که نزد رسول خدایند و او را یاری می‌کنند انفاق نکنید تا از اطراف او پراکنده شوند و او نتواند بر ما حکمرانی کند. آنان چنین می‌گویند درحالی که گنجینه‌های آسمان‌ها و زمین از آنِ خداست و هرگاه بخواهد، یاران پیامبر را توانگر می‌کند، ولی منافقان این حقیقت را درنمی یابند.‌'),
(5196, 63, 8, 'می‌گویند: اگر به مدینه باز گردیم، قطعاً آن که عزیزتر است آن را که زبون‌تر است از آن جا بیرون خواهد کرد، و حال آن که عزّت، همه از آنِ خدا و پیامبرش و مؤمنان است، امّا منافقان نمی‌دانند.'),
(5197, 63, 9, 'ای کسانی که ایمان آورده‌اید، مبادا اموال و فرزندانتان شما را از یاد خدا باز دارند که به ورطه نفاق در خواهید افتاد، و کسانی که چنین کنند، آنانند زیانکاران.'),
(5198, 63, 10, 'و بخشی از آنچه را که به شما ارزانی داشته‌ایم انفاق کنید، پیش از آن که یکی از شما را مرگ در رسد و بگوید: پروردگارا، چرا مرگ مرا اندکی به تأخیر نینداختی تا از اموال خود صدقه دهم و از شایستگان باشم؟'),
(5199, 63, 11, 'این درخواست پذیرفته نمی‌شود، زیرا هنگامی که اجل کسی فرا رسید، هرگز خدا او را به تأخیر نمی‌افکند، و خدا به آنچه می‌کنید آگاه است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'آنچه در آسمان‌ها و آنچه در زمین است خدا را تسبیح می‌گویند و او را به پاکی و پیراستگی از هر عیب و نقصی یاد می‌کنند. فرمانروایی جهان هستی از آنِ اوست و ستایش نیز همه از آنِ اوست و او بر هر کاری تواناست.'),
(5201, 64, 2, 'اوست آن که شما را آفرید، پس شما دو گروه شدید، گروهی از شما کافر و گروهی از شما مؤمنند، و خدا به آنچه انجام می‌دهید بیناست.'),
(5202, 64, 3, 'آسمان‌ها و زمین را به حق (برای هدفی پایدار) آفرید و به شما صورت بخشید و اندام و قوای شما را متناسب با هدف آفرینش نیکو بیاراست، و بازگشت همگان به سوی اوست.'),
(5203, 64, 4, 'از آنچه در آسمان‌ها و زمین است (از جمله اجزای پراکنده مردگان) با خبر است، و آنچه را پنهان می‌دارید و آنچه را آشکار می‌سازید همه را می‌داند، و خدا به رازهای نهفته در سینه‌ها به خوبی داناست.'),
(5204, 64, 5, 'آیا خبر کسانی که پیش از این کفر ورزیدند و وزر و وبال کارشان را در همین جهان چشیدند به شما مشرکان، نرسیده است؟ آنان در آخرت نیز عذابی دردناک خواهند داشت.'),
(5205, 64, 6, 'این فرجام شوم بدان سبب بود که پیامبرانشان با دلیل‌هایی روشن به سویشان می‌آمدند ولی آنان می‌گفتند: آیا آدمیانی که هیچ امتیازی بر ما ندارند ما را هدایت می‌کنند؟ پس کافر شدند و روی برتافتند و گمان کردند که خدا به وجودشان نیازمند است و آنان را هلاک نمی‌کند، در صورتی که خدا از آنان بی نیاز بود، و خدا از همه چیز بی نیاز است و بر کارهایش که همه زیباست ستایش می‌شود.'),
(5206, 64, 7, 'کسانی که کافر شدند، پنداشتند که هرگز از گورها برانگیخته نمی‌شوند. بگو: چرا، به پروردگارم سوگند که قطعاً برانگیخته خواهید شد، سپس شما را به حقیقت و پی آمدهای ناگوار آنچه کرده‌اید آگاه خواهند ساخت، و این بر خدا آسان است.'),
(5207, 64, 8, 'پس به خدا و فرستاده اش محمّد و به قرآنِ روشنی بخش که ما آن را فرو فرستاده‌ایم ایمان بیاورید، و خدا به آنچه می‌کنید آگاه است و شما را به آن سزا می‌دهد.'),
(5208, 64, 9, 'در روزی برانگیخته می‌شوید که خدا شما را در آن روز که روزِ جمع شدن همگان است، برای داوری گرد می‌آورد. آن روز روزِ آشکار شدن زیان‌ها و نیرنگ‌هایی است که مردمان به یکدیگر زده‌اند. و هر کس به خدا ایمان بیاورد و کاری شایسته کند، خدا بدی‌های او را از او می‌زداید و او را به بوستان‌های پردرخت که نهرها از زیر آنها روان است درمی آورَد، در حالی که برای همیشه در آن جاودانه‌اند. این است سعادت بزرگ.'),
(5209, 64, 10, 'و کسانی که کافر شدند و آیات ما را دروغ انگاشتند اهل آتشند و در آن ماندگار خواهند بود، و آن بد بازگشتگاهی است.'),
(5210, 64, 11, 'هیچ گزندی جز به اذن خدا به کسی نمی‌رسد; و هر کس به خدا ایمان بیاورد، خدا دل او را به درک ربوبیت عام الهی راه می‌نماید، و خدا به هر چیزی به خوبی داناست.'),
(5211, 64, 12, 'و خدا را فرمان برید و به آیینی که برای شما مقرّر کرده است پای بند باشید و به دستورات پیامبر گردن نهید; پس اگر روی برتافتید، پیامبر شما را به انجام فرمان الهی و دستورات خود وادار نخواهد کرد، چرا که برعهده فرستاده ما جز ابلاغ آشکار پیام چیزی نیست.'),
(5212, 64, 13, 'خداست که معبودی شایسته پرستش جز او نیست; پس غیر او را فرمان نبرید که فرمانبرداری خود نوعی پرستش است، و مؤمنان باید در امور خود تنها بر خدا توکل کنند.'),
(5213, 64, 14, 'ای کسانی که ایمان آورده‌اید، قطعاً برخی از همسران و فرزندان شما دشمن شما هستند و می‌خواهند شما را از پای بندی به ایمان و کارهای شایسته بازدارند; از آنان برحذر باشید. و اگر از خطایشان درگذرید و بدون هیچ سرزنشی از آنان چشم پوشی کنید و آنان را ببخشایید، خدا شما را می‌آمرزد و از رحمت خود برخوردار می‌سازد، چرا که خداوند آمرزنده و مهربان است.'),
(5214, 64, 15, 'جز این نیست که اموال و فرزندان شما وسیله آزمایش شما هستند، پس مبادا آنها شما را به خود مشغول کنند و از طاعت خدا بازدارند که پاداش بزرگ تنها نزد خداست.'),
(5215, 64, 16, 'پس تا می‌توانید از خدا پروا کنید و سخنش را بشنوید و او را فرمان برید و از اموالتان انفاق کرده و خیری برای خود پیش فرستید. و هر کس از بخل و آز نفس خود مصون بماند و از بخشش اموال خود به دیگران دلتنگ نشود، آنانند سعادتمندان.'),
(5216, 64, 17, 'اگر با انفاق خود، به خدا وامی نیکو دهید، خدا هنگام پرداخت آن، آن را دوچندان به شما بازمی گرداند و شما را می‌آمرزد، و خدا بسیار سپاسگزار و بردبار است.'),
(5217, 64, 18, 'او دانای نهان و آشکار و شکست ناپذیر و حکیم است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'ای پیامبر و ای کسانی که پیرو پیامبرید، هنگامی که خواستید زنان را طلاق دهید، در زمانی که می‌تواند عدّه آنان آغاز شود طلاقشان دهید و شمار عدّه را نگه دارید تا حقوق و احکامی که بر آن مترتب است ضایع نشود، و از خدا که پروردگار شماست پروا کنید. آنان را در دوران عدّه از خانه‌هایی که در آن سکونت داشته‌اند بیرون مکنید و خودشان نیز بیرون نروند، مگر این که مرتکب کار زشت آشکاری شوند. اینها حدود خداست، و هر کس از حدود خدا تجاوز کند، بر خود ستم کرده است. تو نمی‌دانی، شاید بعد از این [که مطلّقه مدّتی در خانه شوهر به سر بُرد] خدا کاری را پدید آورَد که از طلاق پشیمانی حاصل شود و زندگی به حالت نخست باز گردد.'),
(5219, 65, 2, 'و هنگامی که زنان مُطَلَّقه به پایان عدّه خود نزدیک شدند، یا به آنان رجوع کنید با این هدف که با آنان معاشرت نیکو داشته باشید و حقوقشان را رعایت کنید و یا بگذارید عدّه شان به سر آید و به خوبی و با رعایت حقوقشان از آنان جدا شوید، و در صورت جدایی، دو مرد از خودتان (از مؤمنان) را که عادلند بر طلاق گواه گیرید، و گواهی را برای خدا برپا دارید. اینها احکامی هستند که هر کس به خدا و روز قیامت ایمان دارد بدانها پند داده می‌شود، و هر کس از خدا پروا بدارد و حدود الهی را رعایت کند، خدا برای بیرون شدن او از تنگناها راهی پدید می‌آورد.'),
(5220, 65, 3, 'و از جایی که تصوّر نمی‌کند، به او روزی می‌رساند و نیازهای زندگی اش را برطرف می‌سازد. و هر کس بر خدا توکل کند، خدا برای تأمین سعادت او بس است; چرا که خدا کار خود را به انجام می‌رساند. و خدا برای هر چیزی اندازه‌ای قرار داده است.'),
(5221, 65, 4, 'و از زنان شما آنان که از دیدنِ خون حیض نومیدند، اگر شک دارید که آیا به سنّ یائسگی رسیده‌اند یا به سبب عارضه‌ای حایض نمی‌شوند، عدّه آنان سه ماه است، و عدّه زنانی که در سال‌های عادت به سر می‌برند ولی خون ندیده‌اند، نیز سه ماه است، و سرآمد عدّه زنان باردار این است که وضع حمل کنند. و هر کس از خدا پروا کند (به احکام و مقرّرات الهی پای بند باشد)، خدا برای او در کارش آسانی پدید می‌آورد.'),
(5222, 65, 5, 'این فرمان خداست که آن را به سوی شما فرو فرستاده است; و هر کس از خدا پروا کند و به فرمان او گردن نهد، خدا گناهان صغیره او را می‌بخشد و مزدی را که از آن اوست بزرگ و پربار می‌گرداند.'),
(5223, 65, 6, 'شما باید زنان مطلّقه خود را در دوران عدّه در همان جا که خود سکونت دارید و به اندازه توانتان آن را فراهم نموده‌اید جای دهید، و نباید با کاستن از نفقه یا جایگاه سکونتشان به آنان زیان برسانید تا عرصه را برایشان تنگ و آنان را دچار مشقّت کنید. و اگر باردارند، باید تا هنگامی که وضع حمل کنند خرجشان را بدهید. و اگر برای شما فرزندتان را شیر دهند، مزد شیر دادنشان را به آنان بدهید و در امر فرزندتان با یکدیگر مشورت کنید و بر آنچه متعارف است توافق نمایید. و اگر هر یک از شما چیزی را از دیگری خواست که بر او دشوار است و در نتیجه به توافق نرسیدید، هر چه زودتر زن دیگری کودک را برای پدر [به خواست و هزینه او] شیر می‌دهد.'),
(5224, 65, 7, 'باید آن که توانگر است، نفقه همسر مطلّقه اش را که به فرزند او شیر می‌دهد توسعه بخشد، و آن که روزی بر او تنگ شده است، باید در حدّی که خدا به او نعمت داده است نفقه دهد. خدا هیچ کس را جز به قدر آنچه به او داده است تکلیف نمی‌کند. دیری نخواهد گذشت که خدا پس از سختی آسانی پدید می‌آورد.'),
(5225, 65, 8, 'و چه بسیار شهرها که مردم آن از فرمان پروردگار خود و پیامبران او سرپیچیدند و ما در حسابرسی بر آنان سخت گرفتیم و در این جهان با عذابی بی سابقه آنان را کیفر دادیم.'),
(5226, 65, 9, 'پس وِزر و وَبال کار خود را چشیدند و فرجام کارشان نیز زیانکاری است.'),
(5227, 65, 10, 'چرا که خداوند در سرای آخرت برای آنان عذابی سخت آماده کرده است. پس ای خردمندانی که ایمان آورده‌اید، در فرجام سرپیچی از فرمان خدا و پیامبر بیندیشید و از خدا پروا کنید که همانا خدا پیامبری که سود و زیانتان را به شما یادآوری می‌کند به سوی شما برانگیخته است;'),
(5228, 65, 11, 'پیامبری که آیات روشنگر خدا را بر شما تلاوت می‌کند تا کسانی را که ایمان آورده و کارهای شایسته کرده‌اند از تاریکی‌ها در آورد و به سوی نور ببرد، و هر کس به خدا ایمان آورد و کاری شایسته کند خدا او را به بوستان‌هایی آکنده از درختان که از زیر آنها نهرها روان است در می‌آورَد، در حالی که برای همیشه در آن جاودانه‌اند. به راستی خدا برای او رزقی نیکو فراهم آورده است (در دنیا ایمان و عمل شایسته را روزی او ساخته و در آخرت بهشت را به او ارزانی می‌دارد).'),
(5229, 65, 12, 'خدا همان کسی است که هفت آسمان را آفرید و از زمین نیز مانند آنها را پدید آورْد. فرمان از جانب او صادر می‌شود و از میان آسمان‌ها یکی پس از دیگری فرود می‌آید تا به زمین می‌رسد. آری، چنین مقرر شده است تا بدانید که خدا بر انجام هر کاری تواناست و علم او بر هر چیزی احاطه دارد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'ای پیامبر، چرا سوگند یاد می‌کنی و چیزی را که خدا بر تو حلال کرده است، بر خود حرام می‌کنی و بدان رضایت همسرانت را می‌جویی؟ در حالی که خدا آمرزنده و مهربان است،'),
(5231, 66, 2, 'خدا گشودنِ سوگندهایتان را برای شما مقرّر داشته و آن را با پرداخت کفّاره بر شما روا دانسته است، و خدا سرپرست شماست و امورتان را با قوانین و مقررات خود تدبیر می‌کند و او دانا و حکیم است.'),
(5232, 66, 3, 'و زمانی [را یاد کنید] که پیامبر به یکی از همسرانش (حَفصَه) سخنی به راز گفت و از او خواست که آن را نهان دارد ولی او نافرمانی کرد. پس چون آن راز را (با عایشه) باز گفت و خدا پیامبر را از آشکار شدن رازش آگاه ساخت، پیامبر بخشی از آن را اظهار کرد و از اظهار بخشی دیگر صرف نظر نمود. پس هنگامی که پیامبر همسرش (حفصه) را از آشکار شدن آن راز خبر داد، او گفت: چه کسی این را به تو خبر داده است؟ پیامبر گفت: خدای دانا به آشکار و نهان و آگاه از افکار و اندیشه‌ها، مرا از آن آگاه ساخته است.'),
(5233, 66, 4, 'شما دو زن با این عملی که مرتکب شده‌اید دل هایتان به انحراف کشیده شده و لازم است توبه کنید; پس اگر به سوی خدا باز گردید و از آزار پیامبر دست بردارید [کاری شایسته کرده اید]، و اگر بر ضدّ او همداستان شوید، خداوند خود یاور اوست و جبریل و صالحی از مؤمنان نیز یاور اویند، و علاوه بر اینان، فرشتگان هم پشتیبان او خواهند بود.'),
(5234, 66, 5, 'اگر پیامبر همه شما را طلاق دهد، امید است پروردگارش همسرانی بهتر از شما به او عوض دهد; زنانی که تسلیم فرمان خدا و با ایمان و فرمانبردار فروتن و توبه کننده و پرستشگر خدا و اهل روزه باشند، بیوه و دوشیزه.'),
(5235, 66, 6, 'ای کسانی که ایمان آورده‌اید، خود و خانواده هایتان را از آتشی که هیزمش مردم و سنگ‌ها خواهند بود، نگه دارید. بر آن آتش فرشتگانی سختگیر و نیرومند گمارده شده‌اند، که از آنچه خدا به آنان فرمان داده است سرپیچی نمی‌کنند و آنچه را که بدان مأمور شده‌اند انجام می‌دهند.'),
(5236, 66, 7, '[به کافران می‌گویند:] ای کسانی که کفر ورزیده‌اید، امروز هیچ عذر و حجتی بر بی گناهی خود نیاورید زیرا جز این نیست که کارهایی که می‌کردید سزای شما قرار داده می‌شود.'),
(5237, 66, 8, 'ای کسانی که ایمان آورده‌اید، به خدا باز گردید و توبه کنید، توبه‌ای که شما را از روی آوردن به گناه بازدارد; امید است پروردگارتان گناهانتان را از شما بزداید و شما را در بوستان‌هایی با درختانی انبوه که از زیر آنها نهرها روان است درآورَد. این موهبت در روزی به شما داده می‌شود که خدا در آن روز پیامبرش را با خلف وعده سرافکنده نمی‌کند، و کسانی که ایمان آورده‌اند در آن روز با وی خواهند بود. نورشان پیشاپیش آنان و در طرف راستشان به سرعت پیش می‌رود; چرا که خود شتابان به سوی درجات قرب الهی در حرکتند; می‌گویند: پروردگارا، نور ما را برای ما کامل گردان و گناهانمان را بر ما ببخشای که تو بر هر کاری توانایی.'),
(5238, 66, 9, 'ای پیامبر، با کافران و منافقان جهاد کن و بر آنان سخت گیر; جایگاهشان دوزخ است و آن بد بازگشتگاهی است.'),
(5239, 66, 10, 'خداوند برای کسانی که کفر ورزیده‌اند، همسر نوح و همسر لوط را مثَل آورده که هر دو در زوجیّتِ دو بنده شایسته از بندگان ما بودند ولی به آنان خیانت کردند و پیوندشان با آن شایستگان هیچ سودی به حالشان نکرد و از یاری خدا ذره‌ای بی نیازشان نساخت و به آن دو زن گفته شد: با کسانی که به آتش درمی آیند، به آتش درآیید.'),
(5240, 66, 11, 'و خداوند برای کسانی که ایمان آورده‌اند، همسر فرعون را مثل آورده است، آن گاه که گفت: پروردگارا، برای من در جوار قُرب خودت در بهشت خانه‌ای بنا کن و مرا از فرعون و کردارش رهایی بخش، و از این مردم ستمگر نجاتم ده.'),
(5241, 66, 12, 'و برای آنان مریم دختر عمران را نیز مثل آورده است، همان کسی که دامن خود را از آلودگی پاک نگه داشت و به این سبب ما از روح خود در آن دمیدیم و او سخنان پروردگارش و کتاب‌های او را تصدیق کرد و از کسانی بود که با فروتنی خدا را اطاعت می‌کردند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'پر خیر و برکت است خداوندی که فرمانروایی به دست اوست و او بر هر کاری تواناست.'),
(5243, 67, 2, 'همان که مرگ و زندگی را آفرید تا شما را بیازماید و مشخص کند که کدامتان نیکوکارترید; و اوست آن مقتدرِ شکست ناپذیری که آمرزنده گناهان شماست.'),
(5244, 67, 3, 'همان که هفت آسمان را به صورت طبقه طبقه آفرید. تو ای کسی که چشم بینا داری، در آفریده‌های خدایِ رحمان هیچ اختلاف و تفاوتی نمی‌بینی، چرا که همه روی به سوی هدفی دارند که برایشان مقرر شده است و برای رسیدن به مقصد خود از شرایط لازم برخوردارند. باز چشم به سوی آفریده‌ها برگردان و با دقت در آنها بنگر، آیا خلل و نقصانی در آنها می‌بینی؟'),
(5245, 67, 4, 'سپس باز هم چشم به سوی آنها برگردان و بارها به دقت در آنها بنگر، سرانجام دیده ات فرو مانده به سوی تو باز می‌گردد، در حالی که خسته است و هیچ خلل و نقصانی در آفرینش ندیده است.'),
(5246, 67, 5, 'و به راستی ما نزدیک‌ترین آسمان به زمین را با ستارگانی همچون چراغ بیاراستیم، و از آن ستارگان تیرهایی برای راندن شیاطین (جنیّان شرور) پدید آوردیم و برای آنان عذاب آتش افروخته آماده کرده‌ایم.'),
(5247, 67, 6, 'و برای کسانی که منکر ربوبیت پروردگارشان شدند عذاب دوزخ خواهد بود و آن بد بازگشتگاهی است.'),
(5248, 67, 7, 'هنگامی که در دوزخ افکنده شوند نعره دَمِش آن را می‌شنوند و بدین طریق آنان را در خود فرو می‌برد و پیوسته می‌جوشد و آنان را بالا و پایین می‌برد.'),
(5249, 67, 8, 'نزدیک است از شدّت خشم پاره پاره شود. هرگاه گروهی از کافران در آن افکنده شوند، فرشتگانِ نگهبان آن، سرزنش کنان از آنان می‌پرسند: مگر برای شما پیامبری هشداردهنده نیامد؟‌'),
(5250, 67, 9, 'می‌گویند: چرا، پیامبری هشداردهنده برای ما آمد ولی او را تکذیب کردیم و گفتیم: خداوند هیچ چیزی فرو نفرستاده است و شما مدّعیان نبوت جز در گمراهی بزرگی نیستید.'),
(5251, 67, 10, 'و می‌گویند: اگر سخن پیامبران را می‌شنیدیم و می‌پذیرفتیم یا حق را آن گاه که بیان کردند با خردورزی درمی یافتیم امروز در زمره همدمان این آتشِ افروخته نبودیم.'),
(5252, 67, 11, 'و به گناه خود اعتراف می‌کنند، پس ذلّت و نابودی بر اهل این آتشِ افروخته باد.'),
(5253, 67, 12, 'امّا مؤمنان، همانان که از پروردگارشان که از دیدشان نهان است می‌هراسند، آمرزش و پاداشی بزرگ خواهند داشت.'),
(5254, 67, 13, 'و برای خدا یکسان است، چه سخن خود را به راز گویید یا آن را با صدای بلند آشکار سازید، همه را می‌داند; زیرا او به افکار و اندیشه‌های نهفته در سینه‌ها به خوبی آگاه است; پس مبادا تصور کنید که رستاخیز و بررسی اعمال انسان‌ها امکان پذیر نیست.'),
(5255, 67, 14, 'آیا آن کسی که بشر را آفریده است، از گفتار و کردار و اندیشه‌های او بی خبر است؟ هرگز، او در درون هر چیزی نفوذ دارد و از تار و پود آن با خبر است.'),
(5256, 67, 15, 'اوست آن که زمین را همچون چهارپایی رام در اختیار شما قرار داد، پس در اطراف و اکناف آن رهسپار شوید و از رزقی که خدا برای شما مقرّر داشته و شما آن را با تلاش خود به دست می‌آورید بهره ببرید، و [بدانید که بازگشت شما به سوی خداست و] زنده کردن مردگان تنها به حکم اوست.'),
(5257, 67, 16, 'آیا شما که ربوبیت خدا را بر جهان هستی انکار می‌کنید از آن فرشتگانی که در آسمانند ایمنید که زمین را بشکافند و شما را در آن فرو برند، پس ناگهان زمین به لرزه درآید؟'),
(5258, 67, 17, 'آیا از آنان که در آسمانند ایمنید که بر شما تندبادی بفرستند که سنگبارانتان کند؟ به زودی خواهید دانست که هشدار من چگونه است.'),
(5259, 67, 18, 'اگر باور نمی‌کنند، در سرگذشت امّت‌های پیشین بیندیشند; همانا کسانی که پیش از اینان بودند، ربوبیّت مرا انکار و پیامبرانم را تکذیب نمودند و به عذاب گرفتار شدند; پس [بنگرند] که عقوبت من چگونه بود.'),
(5260, 67, 19, 'آیا به پرندگان که بالای سرشان در حال پروازند و بال‌های خود را می‌گشایند و جمع می‌کنند، نظر ندوخته‌اند؟ جز خدای رحمان هیچ کس آنها را در آسمان نگاه نمی‌دارد. قطعاً او به هر چیزی بیناست.'),
(5261, 67, 20, 'بلکه کیست آن کس که گفته می‌شود به جای خدای رحمان سپاه شماست و شما را در برابر عذاب الهی ـ اگر عذابی برای شما بخواهدـ یاری می‌دهد؟ کافران جز در فریب فرو نرفته‌اند; چرا که می‌پندارند معبودانشان یاور آنانند.'),
(5262, 67, 21, 'بلکه کیست آن کس که گفته می‌شود اگر خدا رزقش را از شما باز دارد او به شما روزی می‌دهد؟ آنان خود می‌دانند که چنین کسی وجود ندارد، ولی همچنان در سرکشی و گریز از حق فرو رفته‌اند.'),
(5263, 67, 22, 'آیا آن کس که بر چهره خویش در افتاده و واژگونه سیر می‌کند در نتیجه پیش روی خود را نمی‌بیند راهیاب‌تر است یا آن کس که استوار بر راهی راست حرکت می‌کند و راه و مقصد خود را نظاره گر است؟ کافران حق گریز و مؤمنان حق جو در مَثَل این گونه‌اند.'),
(5264, 67, 23, 'بگو: خداست آن کس که شما را پدید آورده و برای شما گوش‌ها و دیده‌ها و دل‌ها قرار داده است و شما باید خدا را بر این نعمت‌ها سپاس گویید ولی اندک سپاسگزاری می‌کنید.'),
(5265, 67, 24, 'بگو: اوست که شما را در زمین آفرید، و پس از مرگتان زنده می‌شوید و شما را دسته جمعی به سوی او روانه می‌کنند.'),
(5266, 67, 25, 'آنان می‌گویند: اگر راست می‌گویید که رستاخیزی در کار است، این وعده کی تحقق خواهد یافت؟'),
(5267, 67, 26, 'بگو: علم آن تنها نزد خداست و من هم فقط پیامبری اخطارگرم که پیامم را به روشنی به شما ابلاغ می‌کنم.'),
(5268, 67, 27, 'و هنگامی که آن وعده را نزدیک ببینند، چهره کسانی که آن را انکار می‌کردند اندوهبار می‌شود و آثار ناکامی و نومیدی در آنها آشکار می‌گردد و به آنان گفته می‌شود: این است همان چیزی که پیوسته آن را می‌طلبیدید و می‌گفتید: این وعده کی فرا می‌رسد؟'),
(5269, 67, 28, 'ای پیامبر، به آنان که همواره حوادث ناگوار را برای تو و پیروانت آرزو می‌کنند بگو: به من خبر دهید، اگر خدا مرا و هر که را با من است نابود کند یا ما را مورد رحمت قرار دهد، چه کسی شما کافران را پناه می‌دهد و از عذابی دردناک می‌رهاند؟'),
(5270, 67, 29, 'بگو: آن کسی که من شما را به بی همتا دانستن او فرا می‌خوانم و شما از او می‌خواهید که مرا هلاک کند، همان خدای رحمان است که ما به او ایمان آورده و فقط بر او توکل کرده‌ایم. پس به زودی خواهید دانست که از ما و شما چه کسی در گمراهی آشکاری قرار داشته است.'),
(5271, 67, 30, 'بگو: به من خبر دهید، اگر آب [مورد نیاز] شما در زمین فرو رود، چه کسی آبی روان برای شما خواهد آورد؟');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'نون. سوگند به قلم و آنچه با قلم می‌نویسند،'),
(5273, 68, 2, 'که تو در پرتو نعمتِ رسالتی که پروردگارت به تو عطا کرده است، به جنون مبتلا نیستی و نخواهی شد.'),
(5274, 68, 3, 'و قطعاً برای تو پاداشی پایان ناپذیر خواهد بود.'),
(5275, 68, 4, 'و راستی تو خلق و خویی نیکو و والا داری.'),
(5276, 68, 5, 'به زودی تو می‌بینی و آنان نیز خواهند دید،'),
(5277, 68, 6, 'که کدام یک از شما بی خرد و گرفتار جنونند.'),
(5278, 68, 7, 'همانا پروردگارت به کسی که از راه او منحرف شده داناتر است، و همو به کسانی که ره یافته‌اند آگاه‌تر است.'),
(5279, 68, 8, 'پس ای پیامبر، از آنان که تو را دروغ پرداز شمردند پیروی مکن.'),
(5280, 68, 9, 'آنان دوست دارند که تو در کارِ دین خود کوتاهی کنی و اندک گرایشی به آیینشان نشان دهی در این صورت آنان هم در دین خود مسامحه می‌کنند و به دین تو اندک گرایشی نشان می‌دهند.'),
(5281, 68, 10, 'تو نباید پیروی کنی از هر کسی که بسیار قسم می‌خورد، کم خرد و کوته اندیش است،'),
(5282, 68, 11, 'بسیار عیبجو و سخن چین است و حرفِ این و آن را برای فسادگری به دیگران می‌رساند;'),
(5283, 68, 12, 'بازدارنده مردم از کار خیر است و تجاوزکار و گناه پیشه است;'),
(5284, 68, 13, 'علاوه بر این صفات زشت، درشت خوی و بی ریشه است.'),
(5285, 68, 14, 'او به سبب این که مال دارد و از پسرانی چند برخوردار است، گرفتار این صفات زشت شده است.'),
(5286, 68, 15, 'هنگامی که آیات ما بر او تلاوت شود می‌گوید: اینها افسانه‌هایی است که مردم نخستین بافته‌اند.'),
(5287, 68, 16, 'به زودی در آخرت بر بینیِ چنین کسی نشانه آشکار ذلّت و حقارت می‌نهیم.'),
(5288, 68, 17, 'ما تکذیب کنندگان رسالت را گرفتار عذاب کردیم; چنان که صاحبان آن بوستان را دچار بلا ساختیم، آن گاه که سوگند یاد کردند که بامدادان میوه‌های باغشان را برای خود بچینند،'),
(5289, 68, 18, 'و سهمی برای تهیدستان استثنا نکنند.'),
(5290, 68, 19, 'پس همان شب، در حالی که خفته بودند، بلایی از جانب پروردگارت بر گرد آن بوستان به گردش درآمد.'),
(5291, 68, 20, 'و آن باغ بسوخت و چون شبِ تار، سیاه گشت.'),
(5292, 68, 21, 'و صبحگاهان بی خبر از آنچه روی داده، یکدیگر را ندا دردادند،'),
(5293, 68, 22, 'که اگر قصد چیدن میوه‌ها را دارید، در این پگاه به مزرعه خود درآیید.'),
(5294, 68, 23, 'پس به راه افتادند در حالی که آهسته باهم می‌گفتند،'),
(5295, 68, 24, 'که امروز نباید هیچ بینوایی در بوستانتان بر شما در آید.'),
(5296, 68, 25, 'پس صبحگاهان با این تصمیم که بینوایان را باز دارند، به باغ خود روی آوردند.'),
(5297, 68, 26, 'وقتی دیدند باغشان سوخته است، گفتند: قطعاً ما از راه صواب منحرف شده‌ایم;'),
(5298, 68, 27, 'بلکه از مزرعه خود نیز محروم گشته‌ایم.'),
(5299, 68, 28, 'بهترینشان گفت: آیا به شما نگفتم: چرا خدا را به پاکی یاد نمی‌کنید و از این تصمیمِ نادرست به درگاه او توبه نمی‌کنید؟'),
(5300, 68, 29, 'گفتند: پروردگارِ ما از هر عیب و نقصی پیراسته است، به یقین ما ستمکار بودیم.'),
(5301, 68, 30, 'پس روی به یکدیگر آوردند و زبان به ملامتِ هم گشودند.'),
(5302, 68, 31, 'گفتند: ای وای بر ما که سرکشی کردیم، ما از مرز بندگی خدا خارج شدیم،'),
(5303, 68, 32, 'امید است پروردگارمان بهتر از این باغ را به ما عوض دهد، زیرا که ما از غیر او روی برتافته و با رغبت به پروردگار خویش گرویده‌ایم.'),
(5304, 68, 33, 'عذاب این گونه است و عذاب آخرت قطعاً بزرگ‌تر است. ای کاش می‌دانستند.'),
(5305, 68, 34, 'همانا برای تقواپیشگان نزد پروردگارشان بوستان‌هایی بهشتی سرشار از نعمت است که به هیچ رنج و محنتی آمیخته نیست.'),
(5306, 68, 35, 'با این وصف آیا شما مشرکان می‌پندارید که ما فرمانبرداران را همچون گناهکاران قرار می‌دهیم و به هر دو گروه در آخرت ناز و نعمت می‌بخشیم؟'),
(5307, 68, 36, 'شما را چه شده است؟ چگونه داوری می‌کنید؟'),
(5308, 68, 37, 'یا مگر شما کتابی آسمانی دارید که در آن چنین می‌خوانید،'),
(5309, 68, 38, 'که برای شما در آخرت همان چیزی خواهد بود که آن را برمی گزینید و آن چیز بهشت است؟'),
(5310, 68, 39, 'یا مگر شما تا روز قیامت پیمان‌هایی بی نهایت استوار برعهده ما دارید که هر چه شما حکم می‌کنید همان حقِ شما باشد؟'),
(5311, 68, 40, 'ای پیامبر، از مشرکان بپرس کدام یک از آنان بر عهده می‌گیرد که روز قیامت میان فرمانبرداران و گناهکاران برابری برقرار کند؟'),
(5312, 68, 41, 'یا مگر مجرمان را خدایانی است شریک خداوند که به شفاعتشان خداوند آنان را با فرمانبرداران برابر می‌کند؟ اگر راست می‌گویند، شریکانی را که برای خدا پنداشته‌اند بیاورند و معرفی کنند.'),
(5313, 68, 42, 'ای پیامبر، یاد کن روزی را که کار به شدت دشوار می‌شود، آن روز چیزی جز سجده و بندگی خدا کارساز نیست و مشرکان به سجده در برابر خدا فراخوانده می‌شوند ولی نمی‌توانند سجده کنند.'),
(5314, 68, 43, 'این در حالی است که دیدگانشان فرو افتاده و خفّت و خواری بر تمام وجودشان چیره شده است. آنان در آن روز توانِ سجده کردن ندارند، زیرا در دنیا به سجده برای خدا فراخوانده می‌شدند و تندرست هم بودند ولی از سر تکبّر امتناع میورزیدند.'),
(5315, 68, 44, 'پس مرا با کسانی که این سخن (قرآن) را دروغ می‌انگارند واگذار که به تدریج آنان را از جایی که درنمی یابند، به ورطه هلاکت فرو خواهیم افکند.'),
(5316, 68, 45, 'و به آنان مهلت می‌دهم تا هر چه بخواهند نافرمانی کنند، و این تدبیر من درباره آنهاست، قطعاً تدبیر من استوار است.'),
(5317, 68, 46, 'یا مگر از آنان برای تبلیغ رسالتت مزدی می‌طلبی که در نتیجه زیان می‌بینند و بر اثر آن گرانبار می‌شوند و برای رهایی از آن، حقیقت را نمی‌پذیرند و می‌گویند: فرمانبرداران و مجرمان در روز رستاخیز باهم برابرند؟'),
(5318, 68, 47, 'یا مگر جهان غیب که خاستگاه پدیده‌ها و مقدرات است در اختیار آنان است تا آنچه را می‌پسندند تثبیت کنند و مقدر سازند؟'),
(5319, 68, 48, 'پس در برابر حکم پروردگارت که به آنان مهلت می‌دهد و به تدریج آنان را به سقوط می‌کشاند، شکیبا باش و در عذابشان شتاب مخواه و مانند یونس آن همدم ماهی مباش، آن گاه که در شکم ماهی خدا را ندا داد و به تقصیر خود اعتراف کرد در حالی که آکنده از غم و اندوه بود.'),
(5320, 68, 49, 'اگر لطفی از جانب پروردگارش شامل حال او نمی‌شد و توبه اش پذیرفته نمی‌گشت، از شکم ماهی، نکوهش شده به زمینی بی گیاه افکنده می‌شد.'),
(5321, 68, 50, 'ولی با پذیرفته شدن توبه اش، دیگر درخور نکوهش نبود، بلکه پروردگارش او را برگزید و از شایستگانش گردانید.'),
(5322, 68, 51, 'و آنان که کافر شده‌اند، هنگامی که آیات قرآن را که کتابی یادآور است شنیدند، نزدیک بود تو را چشم بزنند، با این حال می‌گویند: قطعاً او گرفتار جنون، و قرآنش الهام شیاطین است;'),
(5323, 68, 52, 'در صورتی که آن جز تذکاری برای جهانیان نیست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'آن حقیقت ثابت!'),
(5325, 69, 2, 'چیست آن حقیقت؟'),
(5326, 69, 3, 'و چه چیز تو را آگاه ساخت که آن حقیقت چیست؟'),
(5327, 69, 4, 'آن، قیامتِ در هم کوبنده است که قوم ثمود و عاد آن درهم کوبنده را دروغ شمردند.'),
(5328, 69, 5, 'امّا ثمودیان با آن صاعقه طغیانگر به هلاکت رسیدند.'),
(5329, 69, 6, 'و امّا عادیان با تندبادی سرد و سرکش نابود شدند.'),
(5330, 69, 7, 'خدا آن را هفت شب و هشت روزِ پیاپی بر آنان مسلّط کرد، در آن مدّت آن مردم را بر زمین افتاده می‌بینی که گویی تنه‌های نخل‌هایی فرو افتاده و میان تهی‌اند.'),
(5331, 69, 8, 'اینک آیا از آنان کسی را بر جای مانده می‌بینی؟'),
(5332, 69, 9, 'فرعون و کسانی که پیش از او بودند و پیامبران را تکذیب کردند و نیز قوم لوط، مردم آن شهرهای زیر و رو شده راه پرستش را به خطا پیمودند.'),
(5333, 69, 10, 'و فرستاده پروردگارشان را نافرمانی کردند و خدا هم آنان را به کیفری سخت گرفت.'),
(5334, 69, 11, 'به راستی ما ـ آن گاه که در عصر نوح آب طغیان کرد ـ پیشینیان شما را بر آن کشتی سوار کردیم،'),
(5335, 69, 12, 'تا آن را برای شما مایه تذکر و عبرتی قرار دهیم و گوش‌هایی که شنوایند آن را در دل‌ها جای دهند. تفسیر'),
(5336, 69, 13, 'پس آن گاه که برای زنده شدن مردگان، تنها یک بار در صور دمیده شود،'),
(5337, 69, 14, 'و زمین و کوه‌ها از جای برداشته و یکباره خرد و ذرّه ذرّه شوند،'),
(5338, 69, 15, 'آن روز است که رستاخیز به وقوع خواهد پیوست.'),
(5339, 69, 16, 'و آسمان از هم می‌شکافد و آن روز است که آسمان سست خواهد بود.'),
(5340, 69, 17, 'و فرشتگان در اطراف آن مستقرند و عرش پروردگارت را در آن روز هشت تن بر فراز آنان حمل می‌کنند. تفسیر'),
(5341, 69, 18, 'در آن روز شما با عقاید و اعمالتان بر خدا عرضه می‌شوید، آن روز هیچ نهانی از باورها و رفتارهای شما، پوشیده نمی‌ماند.'),
(5342, 69, 19, '[و کارنامه اعمالتان به شما داده می‌شود،] امّا کسی که کارنامه اش به دست راستش داده شود، می‌گوید: ای فرشتگان، کارنامه مرا بگیرید و بخوانید;'),
(5343, 69, 20, 'من در دنیا باور داشتم که با حساب اعمال خود رو به رو می‌شوم (از این رو ایمان آوردم و کار شایسته انجام دادم).'),
(5344, 69, 21, 'پس او در یک زندگی دلخواه و موردپسند خواهد بود;'),
(5345, 69, 22, 'در بهشتی بلندمرتبه،'),
(5346, 69, 23, 'که میوه‌های رسیده اش نزدیک و در دسترس است.'),
(5347, 69, 24, 'به بهشتیان گفته می‌شود: بخورید و بیاشامید که بر شما گواراست. اینها پاداش ایمان و کارهای شایسته شماست که در آن دورانی که سپری شد برای خود پیش فرستادید.'),
(5348, 69, 25, 'و امّا کسی که کارنامه اش به دست چپش داده شود، می‌گوید: ای کاش کارنامه‌ام به من داده نمی‌شد،'),
(5349, 69, 26, 'و نمی‌دانستم که حسابم چیست.'),
(5350, 69, 27, 'ای کاش آن مرگِ نخستین نابودم کرده بود،'),
(5351, 69, 28, 'دریغا! ثروتم به من سودی نبخشید و گزندی را دور نساخت.'),
(5352, 69, 29, 'و قدرتم از دستم رفت.'),
(5353, 69, 30, 'آن گاه خدا به فرشتگان می‌گوید: بگیرید او را و در غُل کشید.'),
(5354, 69, 31, 'سپس وی را به آتش دوزخ درافکنید.'),
(5355, 69, 32, 'آن گاه او را در زنجیری که درازیِ آن هفتاد ذراع است در بند کشید.'),
(5356, 69, 33, 'چرا که او به خداوند بزرگ ایمان نمی‌آورْد،'),
(5357, 69, 34, 'و کسی را به اطعام بینوا تشویق نمی‌کرد.'),
(5358, 69, 35, 'پس امروز در این جا برای او هیچ دوستِ دلسوزی نیست تا از وی شفاعت کند.'),
(5359, 69, 36, 'و طعامی جز از چرکاب دوزخیان نخواهد داشت، زیرا به ادای حق بینوایان همت نمی‌گماشت. تفسیر'),
(5360, 69, 37, 'آن طعام را جز گنه پیشگان نخواهند خورد.'),
(5361, 69, 38, 'سوگند یاد می‌کنم به آنچه می‌بینید،'),
(5362, 69, 39, 'و آنچه نمی‌بینید،'),
(5363, 69, 40, 'که این قرآن سخن فرستاده‌ای بزرگوار است.'),
(5364, 69, 41, 'و سخن شاعری نیست [که برخاسته از خیال باشد]، ولی اندکی از شما ایمان می‌آورید.'),
(5365, 69, 42, 'و سخن یک کاهن نیز نیست که جنّیان آن را القا کرده باشند، ولی اندکی از شما متذکّر می‌شوید و پند می‌پذیرید.'),
(5366, 69, 43, 'این قرآن از سوی پروردگار جهانیان نازل شده است و ساخته پیامبر نیست.'),
(5367, 69, 44, 'و اگر او بعضی از سخنان را بر ما بسته بود، تفسیر'),
(5368, 69, 45, 'قطعاً ما دست راستش را می‌گرفتیم،'),
(5369, 69, 46, 'سپس رگ جگرش را می‌بریدیم و نابودش می‌ساختیم.'),
(5370, 69, 47, 'و هیچ کدامتان نمی‌توانستید او را از ما پنهان سازید و از کیفر ما رهایی بخشد. تفسیر'),
(5371, 69, 48, 'به راستی این قرآن ارزش تقوا و معارف الهی را برای تقواپیشگان یادآوری می‌کند.'),
(5372, 69, 49, 'و ما به خوبی می‌دانیم که برخی از شما آن را دروغ می‌انگارید. تفسیر'),
(5373, 69, 50, 'و قطعاً آن برای کافران در روز رستاخیز مایه حسرت خواهد بود.'),
(5374, 69, 51, 'همانا این قرآن سخنی است راست و درست که هیچ شک و تردیدی در آن نیست.'),
(5375, 69, 52, 'پس نام پروردگارت را که بزرگ است به پاکی و پیراستگی از هر عیب و نقصی یاد کن.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'درخواست کننده‌ای از کافران، عذابی را درخواست کرد که فرودش بر آنان حتمی است.'),
(5377, 70, 2, 'عذابی که ویژه کافران است و هیچ چیز آن را باز نخواهد داشت.'),
(5378, 70, 3, 'و از جانب خداوندی است که دارای درجات است و هر فرشته‌ای به تناسب مقام قربش به سوی آن درجات عروج می‌کند.'),
(5379, 70, 4, 'فرشتگان و روح در روز قیامت، روزی که مقدار آن پنجاه هزار سال از سال‌های دنیاست به سوی او بالا می‌روند.'),
(5380, 70, 5, 'پس صبری نیکو پیشه کن که در آن هیچ بی تابی نباشد.'),
(5381, 70, 6, 'کافران برپایی رستاخیز را بعید می‌شمرند،'),
(5382, 70, 7, 'و ما به آن علم داریم و آن را نزدیک می‌بینیم.'),
(5383, 70, 8, 'آری، آن عذاب در روزی واقع خواهد شد که آسمان همچون فلزِّ مذاب می‌شود،'),
(5384, 70, 9, 'و کوه‌ها مانند پشم می‌گردند،'),
(5385, 70, 10, 'و هیچ خویشاوند دلسوزی از حال خویشاوند خود نمی‌پرسد.'),
(5386, 70, 11, 'خویشاوندان را روز قیامت به یکدیگر نشان می‌دهند، ولی از حال دیگری جویا نمی‌شوند، گناهکار دوست دارد و آرزو می‌کند که در برابر عذاب آن روز پسرانش را فدیه دهد،'),
(5387, 70, 12, 'و نیز همسر و برادرش را،'),
(5388, 70, 13, 'و خویشاوندان نزدیکش را همانان که امروز او را در کنار خود می‌گیرند و پناه می‌دهند،'),
(5389, 70, 14, 'و همه کسانی را که در روی زمینند، سپس این فدیه او را از عذاب برهاند.'),
(5390, 70, 15, 'نه! هرگز فدیه اثری نمی‌بخشد، چرا که آن آتش آتشی شعلهور است و خاموش نمی‌گردد;'),
(5391, 70, 16, 'دست‌ها و پاها را می‌سوزاند و از جای برمی کَند.'),
(5392, 70, 17, 'و به سوی خود فرا می‌خواند آن کسی را که به ایمان پشت کرده و از پرستش خدا روی برتافته،'),
(5393, 70, 18, 'و ثروتی گرد آورده و آن را در جایی نگه داشته و به مستمندان کمک نکرده است.'),
(5394, 70, 19, 'به راستی انسان آزمند و حریص آفریده شده است.'),
(5395, 70, 20, 'چون گزندی به او برسد بی تابی می‌کند و ناله و اندوه سر می‌دهد.'),
(5396, 70, 21, 'و چون خیری به او روی بیاورد، آن را به شدت از دیگران باز می‌دارد.'),
(5397, 70, 22, 'مگر نمازگزاران،'),
(5398, 70, 23, 'همانان که بر نماز خود مداومت می‌کنند،'),
(5399, 70, 24, 'و آنان که می‌دانند در اموالشان حقی است معلوم و معین،'),
(5400, 70, 25, 'برای نیازمندی که سؤال می‌کند و بینوایی که از درخواست کردن اِبا دارد;'),
(5401, 70, 26, 'و آنان که روز جزا را تصدیق کرده و تصدیق خود را در عمل نشان می‌دهند.'),
(5402, 70, 27, 'و آنان که از عذاب پروردگارشان بیمناکند.'),
(5403, 70, 28, 'چرا که [می دانند] کسی را از عذاب پروردگارشان ایمنی نداده‌اند.'),
(5404, 70, 29, 'و آنان که دامن خود را حفظ می‌کنند،'),
(5405, 70, 30, 'مگر از همسرانشان یا کنیزانی که مالک آنها شده‌اند که اینان نکوهش نمی‌شوند.'),
(5406, 70, 31, 'پس کسانی که راهی جز آنچه مقرر شده است طلب کنند، قطعاً از مرز الهی گذشته‌اند.'),
(5407, 70, 32, 'و آنان که امانت‌های یکدیگر را پاس می‌دارند و بر پیمان خود، وفادارند.'),
(5408, 70, 33, 'و آنان که شهادت‌های خود را برپا می‌دارند (در تحمل و ادای آن هیچ گونه کوتاهی نمی‌کنند و در آن تغییری نمی‌دهند).'),
(5409, 70, 34, 'و آنان که بر نماز خود مراقبت دارند و آداب کمال آن را رعایت می‌کنند.'),
(5410, 70, 35, 'اینان در بوستان‌های بهشتی خواهند بود و [در آن جا] مورد احترام قرار خواهند گرفت.'),
(5411, 70, 36, 'با این وصف که کفر موجب ورود به دوزخ، و ایمان موجب ورود به بهشت است، کسانی که اظهار ایمان کرده و در حقیقت کافرند، چه مقصدی دارند که در کنار تو قرار می‌گیرند و دیده از تو باز نمی‌گیرند (همواره ملازم تو هستند)؟'),
(5412, 70, 37, 'از جانب راست و چپ و گروه گروه؟'),
(5413, 70, 38, 'آیا هر یک از آنان طمع دارد که با این حضورش، به بهشتِ سرشار از نعمت درآورده شود؟'),
(5414, 70, 39, 'چنین نیست! ما آنان را از چیزی (نطفه) که خود می‌دانند آفریده‌ایم.'),
(5415, 70, 40, 'سوگند یاد می‌کنم به پروردگار مشرق‌ها و مغرب‌های خورشید که قطعاً ما تواناییم،'),
(5416, 70, 41, 'که این نفاق پیشگان را از بین ببریم و کسانی را که بهتر از آنانند و به خدا ایمان دارند و منکر حقایق نیستند جایگزین آنان کنیم و هیچ کس و هیچ چیزی بر ما سبقت نخواهد گرفت و ما مغلوب نخواهیم شد.'),
(5417, 70, 42, 'پس آنان را واگذار تا در بیهودگی‌هایی که بدانها پرداخته‌اند، فرو روند و به کارهایشان که بازیچه و بی هدف است سرگرم باشند تا آن روزشان را که به آنان وعده داده می‌شود، دیدار کنند.'),
(5418, 70, 43, 'روزی که از گورهایشان شتابان برمی آیند; گویی آنان [گم گشتگانی هستند که] به سوی نشانه‌ای که برای راهیابی نهاده شده است، می‌شتابند.'),
(5419, 70, 44, 'این در حالی است که چشمانشان فرو افتاده و خفّت و خواری بر آنان چیره شده است. این است همان روزی که به آنان وعده داده می‌شد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'همانا ما نوح را به رسالت به سوی قومش فرستادیم، به او وحی کردیم که قوم خود را پیش از آن که عذابی دردناک به سراغشان آید از فرجام گناه و شرک هشدار ده.'),
(5421, 71, 2, 'نوح گفت: ای قومِ من، من پیامبری هستم که شما را از عذاب الهی هشدار می‌دهم و آنچه را که در این زمینه مورد نیاز است برای شما به روشنی بیان می‌کنم.'),
(5422, 71, 3, 'پیام این است که خدا را بپرستید و از پرستش غیر او بر حذر باشید و از گناهان بپرهیزید و رسالت مرا بپذیرید و از من پیروی کنید.'),
(5423, 71, 4, 'در این صورت خداوند گناهانتان را که پیش‌تر مرتکب شده اید می‌آمرزد و مرگ شما را تا سرآمدی معیّن به تأخیر می‌افکند، و گرنه سرآمدِ دیگری مقرّر می‌دارد و شما را پیش از آن سرآمدِ معیّن به هلاکت می‌رساند; و هر سرآمدی که خدا مقدر نموده است چون فرا رسد، واپس افکنده نخواهد شد. اگر آنچه را به شما یادآوری کردم می‌دانستید، پیام مرا می‌پذیرفتید.'),
(5424, 71, 5, 'سرانجام نوح [از قوم خود به خدا شکایت برد و] گفت: پروردگارا، من قوم خود را شب و روز به پرستش تو و رعایت تقوا فراخواندم،'),
(5425, 71, 6, 'ولی دعوت من جز بر گریزشان نیفزود،'),
(5426, 71, 7, 'و من هرگاه آنان را فراخواندم که ایمان بیاورند تا گناهانشان را بیامرزی، انگشتان خود را در گوش هایشان کردند و جامه هایشان را بر سر و روی خویش کشیدند تا مرا نبینند و سخنم را نشنوند، و بر حق ناپذیری پافشاری کردند و سخت تکبّر ورزیدند.'),
(5427, 71, 8, 'من هم با فریادی رسا آنان را دعوت کردم،'),
(5428, 71, 9, 'و فراتر این که پیام خود را، هم آشکارا و هم در نهان به آنان ابلاغ نمودم.'),
(5429, 71, 10, 'و گفتم: از پروردگارتان آمرزش بخواهید که او بسیار آمرزنده است.'),
(5430, 71, 11, 'اگر از خدا آمرزش بخواهید، ابرهای پر باران برای شما می‌فرستد تا باران‌های سودمند بر شما فرو ریزند.'),
(5431, 71, 12, 'و شما را با اموال و پسرانی یاری می‌کند و بوستان‌هایی انبوه از درختان برای شما پدید می‌آورد و جویبارهایی برایتان قرار می‌دهد.'),
(5432, 71, 13, 'شما را چه شده است که مأیوس شده‌اید از این که خدا بر جایگاه و مرتبت ربوبیت باشد و عبادت ویژه او گردد؟'),
(5433, 71, 14, 'در حالی که (تدبیر امور شما در اختیار اوست، زیرا) اوست که شما را مرحله به مرحله خلق کرده است.'),
(5434, 71, 15, 'آیا ندیده‌اید که خدا چگونه هفت آسمان را که بر فراز یکدیگرند پدید آورده است؟'),
(5435, 71, 16, 'و ماه را در آنها تابان ساخته و خورشید را چون چراغی درخشان قرار داده است؟'),
(5436, 71, 17, 'و خدا شما را از زمین همچون رویاندن گیاه رویانده است.'),
(5437, 71, 18, 'سپس شما را بعد از مرگ به آن باز می‌گرداند و روز قیامت شما را از آن زنده بیرون می‌آورد.'),
(5438, 71, 19, 'و خدا زمین را برای شما چون بستری گسترده قرار داده است،'),
(5439, 71, 20, 'تا راه‌هایی فراخ از آن برگیرید و در آنها گام بسپرید.'),
(5440, 71, 21, 'نوح [در ادامه شکایت از قومش] گفت: پروردگارا، آنان مرا نافرمانی کردند و از کسانی پیروی نمودند که مال و فرزندشان جز زیان بر آنان نیفزودند.'),
(5441, 71, 22, 'و دست به نیرنگی بس بزرگ زدند.'),
(5442, 71, 23, 'و گفتند: ای مردم، مبادا معبودانتان را واگذارید; به ویژه (ودّ) و (سواع) و (یغوث) و (یعوق) و (نسر) را هرگز رها نکنید و از پرستش آنها دست برندارید.'),
(5443, 71, 24, 'و [بدین گونه] بسیاری از مردم را گمراه کردند. خداوندا، این ستمکاران را جز گمراهی میفزای.'),
(5444, 71, 25, 'سرانجام آنان به سبب گناهانشان در طوفان آب فرو برده شدند و بی درنگ به آتشی سوزان درآورده شدند، و جز خدا یاورانی برای خود نیافتند که آنان را از عذاب برهانند [و خدا هم مقرر داشته بود که آنان را یاری نکند].'),
(5445, 71, 26, 'و نوح گفت: پروردگارا، از کافران هیچ کس را بر روی زمین باقی مگذار،'),
(5446, 71, 27, 'زیرا اگر آنان را باقی گذاری، بندگان با ایمان تو را گمراه می‌کنند و خود نیز جز نسلی گنهکارِ زشت کردار و کفرپیشه به بار نمی‌آورند.'),
(5447, 71, 28, 'پروردگارا، مرا و پدر و مادرم و از مردم این عصر هر کسی را که به خانه‌ام درآمده و با ایمان باشد بیامرز، و همه مردان و زنان با ایمان را در هر عصری که باشند ببخشای، و این ستمکاران را در دنیا و آخرت جز تباهی و هلاکت میفزای.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'ای پیامبر، بگو: به من وحی شده است که گروهی از جنیّان به قرآن گوش فرا داده و آن گاه به سوی قوم خود بازگشته و به آنان گفته‌اند: ما کتابی خوانده شده و شگفت آور شنیده‌ایم،'),
(5449, 72, 2, 'که به عقاید و اعمال درست رهنمون می‌شود و مایه نیکبختی است; از این رو به آن ایمان آورده‌ایم و هرگز کسی را شریک پروردگارمان نخواهیم گرفت.'),
(5450, 72, 3, 'و حقیقت این است که پروردگارمان از نظر عظمت بسیار بلند مرتبه است; او نه همسری گرفته و نه فرزندی اختیار کرده است.'),
(5451, 72, 4, 'و این که بی خردانِ ما که برای خدا شریک قائل شده‌اند، سخنی دور از صواب درباره او گفته‌اند.'),
(5452, 72, 5, 'و ما در گذشته می‌پنداشتیم که انس و جن هرگز بر خدا دروغ نمی‌بندند، ولی اینک که دریافتیم خدا را شریکی نیست دانستیم که آنان که برای خدا شریکی پنداشته‌اند بر او دروغ بسته‌اند.'),
(5453, 72, 6, 'و این گونه بود که پیوسته مردانی از انس به مردانی از جنّ پناه می‌بردند و از آنها یاری می‌طلبیدند و بر سلطه گری و سرکشی جنّیان می‌افزودند.'),
(5454, 72, 7, 'و آن مردم نیز آن گونه پنداشتند که شما جنّیان می‌پنداشتید، آنان گمان کردند که خدا کسی را به پیامبری برنمی انگیزد.'),
(5455, 72, 8, 'و ما به آسمان صعود کردیم و به آن نزدیک شدیم و آن را آکنده از نگهبانانی توانا و شهاب سنگ‌هایی یافتیم.'),
(5456, 72, 9, 'و پیش از نزول قرآن، ما در آسمان برای شنیدن سخنان فرشتگان در مکان‌هایی می‌نشستیم، ولی اکنون هر کس در آن جا دزدانه گوش بسپارد، شهابی را برای خود در کمین می‌یابد.'),
(5457, 72, 10, 'و ما نمی‌دانیم که آیا در این رویداد برای کسانی که در زمینند گزندی اراده شده یا پروردگارشان برایشان چیزی را که مایه نیکبختی آنان است اراده کرده است.'),
(5458, 72, 11, 'و از میان ما گروهی نیکوکارند و گروهی جز این هستند، و ما بر راه و روش‌های گوناگون قرار داریم.'),
(5459, 72, 12, 'و ما باور کرده‌ایم که هرگز نمی‌توانیم خدا را در آنچه برای زمین مقرّر کرده است ناتوان سازیم، و هرگز نمی‌توانیم از دست او بگریزیم و او را به ستوه آوریم.'),
(5460, 72, 13, 'و ما هنگامی که آیات این قرآن هدایتگر را شنیدیم به آن ایمان آوردیم، چرا که هر کس به آن ایمان بیاورد، به پروردگارش ایمان آورده و هر کس به پروردگارش ایمان بیاورد، دیگر باکی ندارد از این که خیری از او کاسته شود یا امری ناخوشایند بر او چیره گردد.'),
(5461, 72, 14, 'و از ما جنّیان گروهی تسلیم خدایند و گروهی از فرمان خدا رویگردان شده به باطل گراییده‌اند; اما کسانی که تسلیم خدا شدند، آنانند که آهنگ حقیقت کرده و در پی حق برآمده‌اند.'),
(5462, 72, 15, 'و امّا باطل گرایان هیزم دوزخند و با سوختن در آتش خود، کیفر خواهند دید.'),
(5463, 72, 16, 'ای پیامبر، بگو: به من وحی شده است که اگر جن و انس در آن راه (تسلیم خدا بودن) پایداری می‌کردند، ما آبی فراوان که بنوشند در اختیارشان می‌گذاشتیم (نعمتی فراوان روزی آنان می‌کردیم).'),
(5464, 72, 17, 'تا آنان را در آنچه به ایشان داده‌ایم بیازماییم. و هر کس از یاد پروردگارش روی برتابد، خدا او را به عذابی که فراز آید و بر او چیره شود، درخواهد آورد.'),
(5465, 72, 18, 'و به من وحی شده است که اعضای هفتگانه سجده از آنِ خداست; پس با آنها تنها او را عبادت کنید و با وجود خدا کسی را نپرستید (برای هیچ کس جز او سجده نکنید).'),
(5466, 72, 19, 'و این گونه بود که وقتی محمّد، بنده خدا، به نماز می‌ایستاد و خدا را عبادت می‌کرد، می‌رفت که انبوهی از مشرکان بر او گرد آیند و به تمسخرش گیرند.'),
(5467, 72, 20, 'بگو: من با این کار هدفی جز این ندارم که پروردگارم را عبادت کنم و کسی را با او شریک نسازم و این کاری درخور سرزنش یا شگفتی نیست.'),
(5468, 72, 21, 'بگو: من نه می‌توانم به شما زیانی برسانم و نه می‌توانم با رساندن شما به حقیقت سودی عایدتان کنم شما اختیار زیانی را ندارم و هدایت شدن شما نیز در اختیار من نیست.'),
(5469, 72, 22, 'بگو: هرگز کسی نمی‌تواند مرا در برابر خدا از گزندی که او برای من خواسته است پناه دهد، و من هرگز پناهگاهی جز او نمی‌یابم.'),
(5470, 72, 23, 'آری، من جز ابلاغ پیام خدا و انجام رسالت‌های او پناهی ندارم. و هر کس از خدا و رسول او فرمان نبرد، قطعاً آتش دوزخ برای اوست و برای همیشه در آن ماندگار خواهد بود.'),
(5471, 72, 24, 'کافران، تا آن گاه که آتش دوزخ را که به آنان وعده داده می‌شود ببینند پیامبر را ناتوان و کم یاور می‌پندارند، از آن پس درمی یابند که چه کسی یاورش ناتوان‌تر و شمار سپاهش کمتر است.'),
(5472, 72, 25, 'بگو: من نمی‌دانم آیا آن عذابی که به شما وعده داده می‌شود نزدیک است یا پروردگارم برای آن سرآمدی دور مقرر می‌کند.'),
(5473, 72, 26, 'تنها خداست که دانای غیب و نهان است و هیچ کس را بر غیب خود آگاه نمی‌کند،'),
(5474, 72, 27, 'مگر کسانی را که از آنان خشنود باشد که آنان پیامبرانند، که خداوند پیشاروی آنان (از هنگامی که وحی را دریافت می‌کنند تا وقتی که آن را به مردم می‌رسانند) و نیز از پشت سرشان (از زمان صدور وحی تا هنگامی که به ایشان برسد) نگهبانانی را برای حراست از وحی گسیل می‌دارد.'),
(5475, 72, 28, 'تا معلوم دارد که آنان پیام‌های پروردگارشان را چنان که بوده است ابلاغ کرده‌اند، و علم خدا به وحیی که در درونشان جای گرفته است احاطه دارد و خدا هر چیزی را شماره کرده و به عدد آن آگاه است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'ای جامه خواب به خود پیچیده.'),
(5477, 73, 2, 'شب را ـ جز اندکی ـ برای نماز به پا خیز.'),
(5478, 73, 3, 'نیمی از شب را یا اگر خواستی اندکی از نیمه بکاه.'),
(5479, 73, 4, 'یا اندکی بر نیمه بیفزای و قرآن را پی در پی و شمرده در نمازت بخوان.'),
(5480, 73, 5, 'که به زودی ما گفتاری گرانبار را که دریافت حقایق آن دشوار و پایداری بر دستوراتش سخت است بر تو القا می‌کنیم.'),
(5481, 73, 6, 'قطعاً پدیده شب برای پیراستنِ نفس پابرجاتر از روز و سخن در آن ثابت‌تر و درست‌تر است.'),
(5482, 73, 7, 'تو در روز کارهایی داری که به خود مشغولت می‌کند و زمان درازی را از تو سپری می‌کند از این رو نمی‌توانی آن گونه که باید به پروردگارت روی بیاوری، پس شب را دریاب و در آن به عبادت خدا بپرداز.'),
(5483, 73, 8, 'و زبان به ذکر نام پروردگارت بگشای و به سوی او دست به دعا بردار و به درگاه او تضرع کن.'),
(5484, 73, 9, 'اوست پروردگار خاور و باختر، معبودی شایسته پرستش جز او نیست، پس او را در همه امورت ـ امور تکوینی و تشریعی ـ کارساز خویش برگیر.'),
(5485, 73, 10, 'و بر آنچه مشرکان درباره تو می‌گویند شکیبا باش و با آنان مقابله به مثل مکن، بلکه با خویی پسندیده آنان را به حق فراخوان.'),
(5486, 73, 11, 'و مرا با تکذیب کنندگان [رسالتت] که صاحبان نعمت اند و سر کردگانِ کفر، واگذار، تا خود از آنان انتقام گیرم، و به آنان اندکی مهلت ده تا زندگی چند روزه دنیا را سپری کنند.'),
(5487, 73, 12, 'به یقین نزد ما بندها و آتشی برافروخته است،'),
(5488, 73, 13, 'و طعامی گلوگیر و عذابی دردناک!'),
(5489, 73, 14, 'این کیفر در روزی است که زمین و کوه‌ها سخت بلرزند و کوه‌ها به صورت شن‌هایی روان درآیند.'),
(5490, 73, 15, 'ما به سوی شما پیامبری را که بر اعمالتان گواه است فرستاده‌ایم، همان گونه که به سوی فرعون نیز پیامبری فرستادیم.'),
(5491, 73, 16, 'ولی فرعون آن پیامبر را نافرمانی کرد، از این رو ما او را به کیفری سخت فرو گرفتیم.'),
(5492, 73, 17, 'پس اگر شما [به رسالت پیامبر] کافر شوید، چگونه از عذاب روزی که کودکان را پیر می‌کند، پرهیز توانید کرد؟'),
(5493, 73, 18, 'آن روز آسمان شکافته می‌شود، و وعده خدا انجام شدنی است.'),
(5494, 73, 19, 'قطعاً این سفارش (برخاستن در شب برای نماز) پند و تذکاری است، پس هر که خواهد راهی به سوی پروردگارش برگیرد و به قُرب او نایل آید، این راه را برمی گزیند.'),
(5495, 73, 20, 'همانا پروردگارت می‌داند که تو نزدیک به دو سوم شب و گاه نیمی از آن و گاه یک سوم آن را به نماز برمی خیزی، و گروهی از کسانی که با تواَند، نیز این گونه‌اند. چگونه خدا بی خبر باشد، در حالی که اوست که اندازه شب و روز را مقرّر می‌کند؟ او دانست که همه شما نمی‌توانید زمانِ تعیین شده برای عبادت شبانه را به شمار آورید; از این رو با رحمت خویش به شما روی کرد و تکلیف شما را کاهش داد. اکنون لازم نیست دو سوم یا نیمی از شب یا یک سوم آن را به نماز برخیزید، ولی هر چه از قرآن برای شما آسان است بخوانید. او همچنین دانست که برخی از شما بیمار خواهند شد و عدّه‌ای دیگر برای به دست آوردن روزی و برخورداری از فضل خدا سفر می‌کنند، و گروهی دیگر در راه خدا پیکار می‌نمایند و بر آنان دشوار است که حدود نیمی از شب را به نماز برخیزند. بنابراین هر چه از قرآن برای شما آسان است بخوانید و نماز را برپا دارید و زکات اموالتان را بپردازید و با انفاق در راه خدا به خدا وامی نیکو دهید. و هر کار خیری برای آخرت خود پیش می‌فرستید، آن را نزد خدا نیکوتر از آنچه کرده‌اید و با پاداشی بزرگ‌تر خواهید یافت. و از خدا آمرزش بخواهید که خدا بسیار آمرزنده و مهربان است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'ای جامه خواب بر خود کشیده.'),
(5497, 74, 2, 'برخیز و هشدار ده.'),
(5498, 74, 3, 'و پروردگارت را به بزرگی یاد کن.'),
(5499, 74, 4, 'و جامه ات را از آلودگی‌ها پاک دار.'),
(5500, 74, 5, 'و با ترک گناهان از کیفر دوری گزین.'),
(5501, 74, 6, 'و این کارها را فراوان مپندار و به خاطر انجام آنها بر خدا منّت مگذار.'),
(5502, 74, 7, 'و به خاطر پروردگارت بر مشکلات رسالت و سختی‌های اطاعت خدا صبر کن.'),
(5503, 74, 8, 'پس چون نواخته شود در چیزی که در آن می‌نوازند و مردگان برانگیخته شوند،'),
(5504, 74, 9, 'آن روز که هنگام بازگشت آفریدگان به سوی خداست، روز دشواری است،'),
(5505, 74, 10, 'بر کافران آسان نیست.'),
(5506, 74, 11, 'مرا وا گذار با آن کسی که من به تنهایی او را آفریدم (وَلید پسر مُغیرَه).'),
(5507, 74, 12, 'و برایش مالی فراوان قرار دادم.'),
(5508, 74, 13, 'و پسرانی به او بخشیدم که نزدش حضور دارند و در یاری وی آماده‌اند.'),
(5509, 74, 14, 'و برای او جاه و مقام آماده ساختم و امورش را سامان بخشیدم.'),
(5510, 74, 15, 'باز هم طمع دارد که بر مال و جاه و فرزندانش بیفزایم.'),
(5511, 74, 16, 'نه! هرگز نخواهم افزود، زیرا او با آیات ما به ستیز برخاست و به دارایی اش می‌بالید.'),
(5512, 74, 17, 'به زودی کیفری سخت را که همچون بالا رفتن بر گردنه‌ای صَعْب العبور است بر او تحمیل خواهم کرد.'),
(5513, 74, 18, 'آری، او برای مبارزه با قرآن اندیشید و جوانب کار را سنجید و طرحی افکند.'),
(5514, 74, 19, 'مرگ بر او باد، چگونه سنجید و طرحی افکند.'),
(5515, 74, 20, 'باز هم مرگ بر او باد، چگونه سنجید و طرحی افکند.'),
(5516, 74, 21, 'پس از آن نگریست تا رأی خود را باز گوید.'),
(5517, 74, 22, 'آن گاه اخم کرد و بر چهره اش ناخرسندی نمود.'),
(5518, 74, 23, 'سپس روی [از حق] گردانید و تکبّر ورزید.'),
(5519, 74, 24, 'و گفت: این قرآن چیزی جز افسونی برگرفته از جادوگران نیست.'),
(5520, 74, 25, 'این چیزی جز سخن بشر نیست.'),
(5521, 74, 26, 'به زودی او را به دوزخ در خواهم آورد.'),
(5522, 74, 27, 'و چه چیز تو را از حقیقت دوزخ آگاه کرد؟'),
(5523, 74, 28, 'بر هر که دست یابد او را می‌سوزاند و هر کسی را که در آن افتد رها نمی‌کند.'),
(5524, 74, 29, 'پوست‌ها را سخت [می گدازد و] سیاه می‌کند.'),
(5525, 74, 30, 'نوزده تن بر آن گمارده شده‌اند.'),
(5526, 74, 31, '۳۱ و ما نگهبانان دوزخ را جز از فرشتگان قرار ندادیم و شماره آنها را جز برای آزمایش کافران یادآوری نکرده‌ایم، تا اهل کتاب بدانند که این خبر با آنچه در کتاب هایشان آمده است مطابق است و در پی آن، کسانی هم که [به قرآن] ایمان آورده‌اند بر ایمانشان بیفزایند و آنان که کتاب آسمانی به ایشان داده شده و نیز مؤمنان در حقّانیت قرآن تردید نکنند، و سرانجام کسانی که در دل هایشان بیماری شک و نفاق است و کفرپیشگان بگویند: خدا از توصیف نگهبانان دوزخ که نوزده فرشته‌اند چه چیزی اراده کرده است; این تعداد اندک از فرشتگان چگونه می‌توانند آن همه دوزخیان را کیفر دهند؟! بدین گونه خدا هر که را بخواهد به بیراهه می‌کشاند و هر که را بخواهد به راه راست رهنمون می‌شود. و شمارِ سپاهیان پروردگارت را جز او کسی نمی‌داند، و یادآوری تعدادشان جز تذکاری برای بشر نیست.'),
(5527, 74, 32, 'نه! قرآن سحر نیست; سوگند به ماه.'),
(5528, 74, 33, 'و سوگند به شب آن گاه که پشت کرد و رفت.'),
(5529, 74, 34, 'و سوگند به بامداد آن گاه که روشن و آشکار شود.'),
(5530, 74, 35, 'که قطعاً قرآن یکی از نشانه‌های بزرگ الهی است،'),
(5531, 74, 36, 'در راستای هشدار دادن به بشر.'),
(5532, 74, 37, 'برای هر کس از شما که بخواهد سبقت گیرد و راه ایمان و اطاعت پوید یا عقب بماند و راه کفر و عصیان در پیش گیرد.'),
(5533, 74, 38, 'هر کسی نزد خدا در گروِ دستاورد خویش است;'),
(5534, 74, 39, 'مگر اهل دست راست که حق بندگی خدا ادا کردند و خود را از رهن به درآوردند و از گرفتاری رها ساختند.'),
(5535, 74, 40, 'آنان در بوستان‌های بهشتی قرار دارند، و می‌پرسند،'),
(5536, 74, 41, 'از گنهکاران:'),
(5537, 74, 42, 'چه چیزی شما را به دوزخ درآورده است؟'),
(5538, 74, 43, 'در پاسخ می‌گویند: ما از نمازگزاران نبودیم.'),
(5539, 74, 44, 'و بینوا را اطعام نمی‌کردیم.'),
(5540, 74, 45, 'و با باطل گرایان در بیهودگی فرو رفته و در گفتار و رفتار باطل به سر می‌بردیم.'),
(5541, 74, 46, 'و روز جزا را دروغ می‌شمردیم.'),
(5542, 74, 47, 'تا این که مرگمان که حقیقتی تردیدناپذیر بود دررسید.'),
(5543, 74, 48, 'از این رو شفاعتِ شفاعت کنندگان به حال آنان سودی نمی‌بخشد.'),
(5544, 74, 49, 'با این وصف، کافران را چه شده است که از تذکار قرآن رویگردان اند؟'),
(5545, 74, 50, 'چنان روی برتافته‌اند که گویی گورخرانی رمیده‌اند،'),
(5546, 74, 51, 'که از شیری گریخته‌اند.'),
(5547, 74, 52, 'چنین نیست که رویگردانی آنان از تذکار قرآن تنها به سبب گریزشان از حقیقت باشد، بلکه هر یک از آنان از سر تکبّر می‌خواهد نامه‌هایی گشوده از سوی خدا که شامل حقایق قرآن است به او داده شود.'),
(5548, 74, 53, 'نه، هرگز کتاب آسمانی به آنان داده نمی‌شود، و اصولاً سبب اصلی رویگردانی آنان از قرآن این نیست که چرا کتابی آسمانی از سوی خدا بر آنان نازل نمی‌شود، این دستاویزی بیش نیست بلکه آنان از سرای آخرت بیم ندارند.'),
(5549, 74, 54, 'نه! هرگز کتاب آسمانی به هر یک از آنان داده نمی‌شود. آنچه برعهده ماست اندرز است و قرآن برای آنان پندی بسنده است.'),
(5550, 74, 55, 'پس هر که خواست از آن پند گیرد.'),
(5551, 74, 56, 'و جز به خواست خدا پند نخواهند گرفت. او سزاوار این است که از او پروا کنند و سزاوار این است که تقواپیشگان را بیامرزد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'سوگند یاد می‌کنم به روز رستاخیز.'),
(5553, 75, 2, 'و سوگند یاد می‌کنم به نفس ملامتگر آدمی بر گناه، که مردگان برانگیخته خواهند شد.'),
(5554, 75, 3, 'آیا انسان می‌پندارد که هرگز استخوان هایش را گردآوری نمی‌کنیم و به او حیات دیگری نمی‌بخشیم؟'),
(5555, 75, 4, 'چرا، این کار را خواهیم کرد، در حالی که بر مرتب ساختن سرانگشتانش نیز تواناییم.'),
(5556, 75, 5, 'نه آدمی چنین نمی‌پندارد که او را حیاتی دوباره نمی‌بخشیم، بلکه او می‌خواهد آینده عمرش را به فسق و گناه بگذراند، این است که رستاخیز را دروغ می‌شمرد.'),
(5557, 75, 6, 'و از روی مسخره می‌پرسد: روز قیامت کی خواهد بود؟'),
(5558, 75, 7, 'باید بداند، هنگامی که چشم از شدت وحشت خیره شود.'),
(5559, 75, 8, 'و ماه بی نور گردد.'),
(5560, 75, 9, 'و خورشید و ماه گرد آیند [و به همدیگر رسند]،'),
(5561, 75, 10, 'آن روز انسان می‌گوید: گریزگاه کجاست؟'),
(5562, 75, 11, 'نه! گریزگاه مجویید، هیچ پناهگاهی وجود ندارد.'),
(5563, 75, 12, 'آن روز بازگشت همگان فقط به سوی پروردگار تو و قرارگاه تنها در حضور اوست.'),
(5564, 75, 13, 'در آن روز انسان را از آنچه پیش فرستاده (کارهای نیک یا بدی که خود کرده) و از آنچه بعد آورده (سنّت‌های پسندیده یا ناپسندی که بر جای نهاده است) آگاه می‌کنند.'),
(5565, 75, 14, 'نه، نیازی نیست که او را از کارهایش با خبر کنند، بلکه آدمی به نفس خویش بیناست و می‌داند چه کرده است،'),
(5566, 75, 15, 'هر چند عذرهایش را مطرح کند تا عذاب را از خود بگرداند.'),
(5567, 75, 16, 'ای پیامبر، هنگامی که قرآن به تو وحی می‌شود سکوت کن و زبانت را به تلاوت آن حرکت مده تا در خواندن آن شتاب کنی و آنچه را نخوانده‌ایم پیش از ما بخوانی.'),
(5568, 75, 17, 'چرا که برعهده ماست که وحی قرآنی را گرد آوریم و آن را بر تو بخوانیم.'),
(5569, 75, 18, 'پس هنگامی که قرائت آن را به پایان رساندیم تو از خواندن آن پیروی کن.'),
(5570, 75, 19, 'وانگهی بیان آن نیز برعهده ماست.'),
(5571, 75, 20, 'چنین نیست که قیامتی در کار نباشد; شما بر این عقیده خود هیچ برهانی ندارید، بلکه از این رو رستاخیز را باور ندارید که سرای زودگذر دنیا را دوست می‌دارید.'),
(5572, 75, 21, 'و سرای جاوید آخرت را رها می‌کنید.'),
(5573, 75, 22, 'آن روز که قیامت برپا شود، چهره‌هایی خرسند و شادابند،'),
(5574, 75, 23, 'از همه چیز دل بریده‌اند و تنها به پروردگارشان نگریسته و به او دل بسته‌اند.'),
(5575, 75, 24, 'آن روز چهره‌هایی عبوس و درهم کشیده‌اند.‌'),
(5576, 75, 25, 'می‌دانند که عذابی کمرشکن بر آنها تحمیل خواهد شد.'),
(5577, 75, 26, 'نه! به دنیا دل مبندید که مرگ به سراغتان خواهد آمد، و چون هنگام مرگ جان به گلوگاه رسد،'),
(5578, 75, 27, 'و اطرافیان حاضر شوند و گفته شود: کیست که او را از مرگ رهایی دهد؟'),
(5579, 75, 28, 'ولی آن محتضر دریافته است که این زمانِ جدایی است،'),
(5580, 75, 29, 'و ساق پاهایش به یکدیگر پیچد (پی در پی تا روز قیامت به او رنج و سختی رسد)،'),
(5581, 75, 30, 'آن روز، روزِ رانده شدن به سوی پروردگار توست.'),
(5582, 75, 31, 'آن کس که رستاخیز را انکار کرد نه دعوت قرآن را تصدیق کرد و نه نماز گزارد.'),
(5583, 75, 32, 'بلکه آن را دروغ انگاشت و از آن روی برتافت،'),
(5584, 75, 33, 'سپس به سوی کسانش رفت و بر خود می‌بالید.'),
(5585, 75, 34, 'ای منکر حق، این موقعیتی که در آن قرار داری برای تو سزاوارتر است و سزاوارتر.'),
(5586, 75, 35, 'باز هم این موقعیت برای تو سزاوارتر است و سزاوارتر.'),
(5587, 75, 36, 'آیا انسان می‌پندارد که به حال خود رها می‌شود و پس از مرگ زنده نمی‌گردد و کیفر و پاداشی در کار نیست؟'),
(5588, 75, 37, 'آیا او نطفه‌ای از منی که در رحم ریخته می‌شود نبود؟'),
(5589, 75, 38, 'سپس به صورت خون بسته درآمد، آن گاه خدا او را به اندازه آفرید و کامل و موزونش ساخت.'),
(5590, 75, 39, 'و از او دو گونه: مرد و زن پدید آورد.'),
(5591, 75, 40, 'آیا آن خدای بزرگ که آدمیان را نخستین بار آفرید، نمی‌تواند مردگان را زنده کند؟');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'قطعاً بر انسان روزگاری گذشته که او در آن روزگار چیزی نبوده که نام انسان بر وی نهاده شود; آیا چنین نبوده است؟'),
(5593, 76, 2, 'ما انسان را از نطفه‌ای که دارای اجزایی به هم آمیخته است آفریدیم و او را از صورتی به صورت دیگر و از مرحله‌ای به مرحله دیگر درآوردیم و او را شنوا و بینا ساختیم تا دعوت خدا را بشنود و نشانه‌های او را در آفرینش ببیند.'),
(5594, 76, 3, 'همانا ما راه درست را به او نشان دادیم و اوست که یا سپاس می‌گزارد و یا ناسپاسی پیشه می‌کند.'),
(5595, 76, 4, 'امّا بداند که ما برای کفرپیشگان زنجیرها و غل‌ها و آتشی فروزان آماده کرده‌ایم.'),
(5596, 76, 5, 'نیکوکاران از جامی می‌نوشند که آمیزه اش کافور است.'),
(5597, 76, 6, 'شرابشان از چشمه‌ای است که بندگان خالص خدا از آن می‌نوشند; چشمه‌ای که آن را خود در بهشت می‌شکافند و جاریش می‌سازند.'),
(5598, 76, 7, '[بخشی از سرگذشت آن نیکان بدین قرار است:] آنان به نذر خود وفا کردند و از روز قیامت که شرّ آن فراگیر و عذابش بی نهایت است، ترسیدند.'),
(5599, 76, 8, 'و غذایی را که از شدّت احتیاج، آن را دوست می‌داشتند، به مسکین و یتیم و اسیر عطا کردند.'),
(5600, 76, 9, 'و گفتند: جز این نیست که شما را برای خشنودی خدا اطعام می‌کنیم، نه از شما عِوضی می‌خواهیم و نه انتظار سپاس و ثنایی داریم.'),
(5601, 76, 10, 'زیرا ما از روز قیامت که از سوی پروردگارمان تحقق خواهد یافت می‌ترسیم; روزی که در اثر سختی هایش چهره‌هایی در هم کشیده می‌شود و روزی که بی نهایت دشوار است.'),
(5602, 76, 11, 'پس خداوند مقرّر داشت که آنان را از شرّ و رنج آن روز در امان دارد و شادابی و خرسندی را برایشان به ارمغان آرد.'),
(5603, 76, 12, 'و بهشت و پرنیان را به پاداش این که بر اطاعت خدا و در برابر گزندها و مصیبت‌ها شکیبایی ورزیدند پاداششان دهد.'),
(5604, 76, 13, 'در بهشت بر پشتی‌ها تکیه می‌زنند. در آن جا نه خورشیدی می‌بینند تا از گرمای آن آزرده شوند و نه سرمایی تا از سردی اش به ستوه آیند.'),
(5605, 76, 14, 'سایه هایش نزدیک و بر آنها گسترده است و میوه هایش در اختیار آنان نهاده شده است، به گونه‌ای که هر نوع میوه‌ای بخواهند، بی هیچ رنجی از درخت می‌چینند.'),
(5606, 76, 15, 'جام‌هایی سیمین و تنگ‌هایی بلورین بر آنان می‌گردانند.'),
(5607, 76, 16, 'بلورهایی شفّاف و روشن که گویی از نقره ساخته‌اند. خدمتکاران، آنها را در اندازه‌هایی مطابق میل آنان آورده و به قدر خواسته ایشان در آنها نوشیدنی دلخواه آنان ریخته‌اند.'),
(5608, 76, 17, 'در آن جا باده‌ای به آنان می‌نوشانند که آمیزه اش زنجبیل است،'),
(5609, 76, 18, 'از چشمه‌ای در بهشت که آن را (سلسبیل) می‌نامند.'),
(5610, 76, 19, 'و برای خدمت به آنان پسرانی هماره شاداب و نوجوان بر گردشان در گردش‌اند. آن پسران چنان نورانی و با طراوت اند که چون آنان را بنگری می‌پنداری مرواریدی پراکنده‌اند.'),
(5611, 76, 20, 'و چون در آن جا به نظاره بنشینی، نعمتی بی حدّ و سلطنتی بزرگ می‌بینی.'),
(5612, 76, 21, 'بر اندام بهشتیان جامه‌هایی سبز رنگ از حریر نازک و دیبای سِتَبر است و به دستواره‌هایی از نقره آراسته شده‌اند و پروردگارشان به آنان شرابی می‌نوشاند که هر پلیدی را از آنان می‌زداید.'),
(5613, 76, 22, 'به آنان گفته می‌شود: این برای شما جزایی است بزرگ، و سعیتان مورد سپاس قرار گرفته است.'),
(5614, 76, 23, 'ای پیامبر، ما خود، قرآن را چنان که باید بر تو فرو فرستادیم.'),
(5615, 76, 24, 'پس بر حکم پروردگارت که قرآن بیانگر آن است شکیبا باش، و از مردم، هیچ گنهکار یا کفرپیشه‌ای را فرمان مبر.'),
(5616, 76, 25, 'و هر بامداد و عصرگاه با خواندن نماز، نام پروردگارت را یاد کن.'),
(5617, 76, 26, 'و پاسی از شب را نماز بگزار و در آن پروردگارت را سجده کن، و شب هنگام که زمانی طولانی است، او را تسبیح گوی و به پاکی یاد کن.'),
(5618, 76, 27, 'اینان که گنهکار و کفرپیشه‌اند سرای زودگذر دنیا را دوست دارند و جز به آن نمی‌اندیشند و روزی گران را که پیش روی خود دارند وامی گذارند و از آن غفلت میورزند.'),
(5619, 76, 28, 'آنان گمان نکنند که اگر با دل بستن به دنیا با خدا مخالفت می‌کنند بر او چیره شده‌اند، زیرا ماییم که آنان را آفریده و پیوند مفاصل و اندامشان را استوار ساخته‌ایم، و هرگاه بخواهیم آنان را از بین می‌بریم و امثالشان را چنان که باید جایگزینشان می‌کنیم.'),
(5620, 76, 29, 'اینها همه مایه یادآوری است، پس هر که بخواهد راهی به سوی پروردگارش برگیرد و به قرب او نایل آید، این راه را برمی گزیند.'),
(5621, 76, 30, 'ولی بدانید که شما چیزی را اراده نمی‌کنید مگر این که خدا آن را اراده کند، همانا خدا دانا و حکیم است.'),
(5622, 76, 31, 'از این رو هر که را بخواهد به رحمت خود درمی آورد، و این را تنها برای مؤمنان و تقواپیشگان خواسته است; اما برای ستمکاران (کافران و گنه پیشگان) عذابی دردناک آماده کرده است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'سوگند به فرشتگان وحی که پی در پی فرستاده می‌شوند،'),
(5624, 77, 2, 'و آن گاه که فرستاده شوند همچون بادی توفنده راه می‌پویند،'),
(5625, 77, 3, 'و سوگند به آن فرشتگانی که صحیفه‌های وحی را برای پیامبر می‌گشایند،'),
(5626, 77, 4, 'و آن گاه که بگشایند، جدا ساختن حق را از باطل و حلال را از حرام آغاز می‌کنند،'),
(5627, 77, 5, 'و سپس صحیفه‌های وحی را به صورت قرآن که یادآور است بر پیامبر می‌افکنند،'),
(5628, 77, 6, 'تا اتمام حُجّتی باشد یا اخطار و هشداری;'),
(5629, 77, 7, 'به اینها سوگند که آنچه به شما وعده داده می‌شود (رستاخیز و جزا و پاداش) قطعاً تحقّق خواهد یافت.'),
(5630, 77, 8, 'پس آن گاه که ستارگان تاریک شوند و محو گردند،'),
(5631, 77, 9, 'و آن گاه که آسمان بشکافد و دو پاره گردد،'),
(5632, 77, 10, 'و آن گاه که کوه‌ها از جای برکنده شوند و روان گردند،'),
(5633, 77, 11, 'و آن گاه که برای پیامبران وقتی تعیین شود تا برای ادای شهادت حضور یابند، بر اثر شگفتی و هراس آن هنگام خواهند پرسید،'),
(5634, 77, 12, 'این رخدادها برای چه روزی به تأخیر افتاده است؟'),
(5635, 77, 13, 'برای روز داوری.'),
(5636, 77, 14, 'و چه چیز تو را آگاه کرده است که روز داوری چیست؟'),
(5637, 77, 15, 'آن روز وای بر تکذیب کنندگان!'),
(5638, 77, 16, 'آیا اقوام نخستین را به سبب این که رستاخیز را دروغ انگاشتند هلاک نکردیم؟'),
(5639, 77, 17, 'سپس امّت‌های بعدی را در پی آنان نابود می‌کنیم.'),
(5640, 77, 18, 'زیرا ما با همه مجرمان این گونه رفتار می‌کنیم.'),
(5641, 77, 19, 'آن روز وای بر تکذیب کنندگان!'),
(5642, 77, 20, 'مگر شما را از نطفه که آبی ناچیز است نیافریدیم؟'),
(5643, 77, 21, 'پس آن را در رحم که برای نگهداری از نطفه جایگاهی استوار است قرار دادیم،'),
(5644, 77, 22, 'تا زمانی معلوم که پایان مدّت بارداری است.'),
(5645, 77, 23, 'آری، شما را آفریدیم و امورتان را تقدیر کردیم، پس ما نیکو تقدیرکنندگانیم.'),
(5646, 77, 24, 'آن روز وای بر تکذیب کنندگان!'),
(5647, 77, 25, 'مگر ما زمین را گردآورنده بندگان نساختیم،'),
(5648, 77, 26, 'که زندگان و مردگان را در خود گرد می‌آورد؟'),
(5649, 77, 27, 'و در آن کوه‌هایی استوار و بلند پدید آوردیم و آبی شیرین و گوارا که از آنها سرچشمه می‌گیرد در اختیارتان نهادیم. آیا باز هم می‌پندارید تدبیر امور شما جز در اختیار ماست؟'),
(5650, 77, 28, 'آن روز وای بر تکذیب کنندگان!'),
(5651, 77, 29, 'خدا به آنان می‌گوید: به سوی آتشی که آن را دروغ می‌شمردید روانه شوید.'),
(5652, 77, 30, 'روانه شوید به سوی سایه‌ای از دود که دارای سه شاخه است.'),
(5653, 77, 31, 'سایه‌ای که نه مانع از حرارت است و نه شعله آتش را باز می‌دارد.'),
(5654, 77, 32, 'آتش دوزخ شراره‌هایی به بزرگی کاخ می‌پراند.'),
(5655, 77, 33, 'گویی آنها شترانی زرد رنگ‌اند.'),
(5656, 77, 34, 'آن روز وای بر تکذیب کنندگان!'),
(5657, 77, 35, 'این روزِ داوری روزی است که منکران قیامت در آن سخن نمی‌گویند.'),
(5658, 77, 36, 'و به آنان اجازه سخن گفتن نمی‌دهند تا بتوانند برای گناهان خود عذری بیاورند.'),
(5659, 77, 37, 'آن روز وای بر تکذیب کنندگان!'),
(5660, 77, 38, 'این است روز جدایی میان حق جویان و باطل گرایان، که شما و تکذیب کنندگان امّت‌های نخستین را در آن گرد آورده‌ایم.'),
(5661, 77, 39, 'پس اگر نیرنگی دارید، با من نیرنگ کنید و عذاب دوزخ را از خود بگردانید.'),
(5662, 77, 40, 'آن روز وای بر تکذیب کنندگان!'),
(5663, 77, 41, 'امّا تقواپیشگان در زیر سایه‌ها و کنار چشمه ساران،'),
(5664, 77, 42, 'و میوه‌هایی که دلخواهشان است به سر می‌برند.'),
(5665, 77, 43, 'به آنان گفته می‌شود: به پاداش آنچه انجام می‌دادید بخورید و بیاشامید که بر شما گواراست.'),
(5666, 77, 44, 'همانا ما نیکوکاران را این گونه پاداش می‌دهیم.'),
(5667, 77, 45, 'آن روز وای بر تکذیب کنندگان!'),
(5668, 77, 46, 'ای منکران رستاخیز، در این سرای دنیا بخورید و اندک زمانی بهره برید، ولی بدانید که این مایه نیکبختی شما نیست، زیرا شما مجرمید و سزای مجرمان آتش دوزخ است.'),
(5669, 77, 47, 'آن روز وای بر تکذیب کنندگان.'),
(5670, 77, 48, 'و چون به آنان گفته شود: نماز گزارده رکوع نمایید، نماز نمی‌گزارند و رکوع نمی‌کنند.'),
(5671, 77, 49, 'آن روز وای بر تکذیب کنندگان!'),
(5672, 77, 50, 'اگر این قرآن را که معجزه‌ای الهی است باور نمی‌کنند، به کدامین سخن بعد از آن ایمان می‌آورند؟');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'مشرکان درباره کدامین خبر از یکدیگر می‌پرسند؟'),
(5674, 78, 2, 'از رستاخیز، آن خبر بزرگ!'),
(5675, 78, 3, 'همان خبری که در آن اختلاف دارند. (گروهی آن را بعید و گروهی آن را ناممکن می‌شمرند و برخی نیز در آن تردید دارند و گروهی از سر عناد آن را انکار می‌کنند.)'),
(5676, 78, 4, 'درباره آن پرسش و اختلاف نکنند، دیری نمی‌گذرد که پدیدار می‌گردد پس آن را به عیان خواهند دید و از آن آگاه خواهند شد.'),
(5677, 78, 5, 'آری، درباره آن پرسش و اختلاف نکنند، به زودی پدیدار می‌شود پس آن را به عیان خواهند و از آن آگاه خواهند شد.'),
(5678, 78, 6, 'جهان هستی بی هدف آفریده نشده است و این خود دلیل وجود رستاخیز است; آیا این زمین را بستری آرام برای شما نساختیم؟'),
(5679, 78, 7, 'و آیا کوه‌ها را همچون میخ‌هایی قرار ندادیم؟'),
(5680, 78, 8, 'و شما را جفت آفریدیم تا نسلتان باقی بماند.'),
(5681, 78, 9, 'و خوابتان را مایه آسایش و استراحت گردانیدیم.'),
(5682, 78, 10, 'و شب را همچون پوششی بر پدیده‌ها قرار دادیم تا آرامش خویش باز یابید.'),
(5683, 78, 11, 'و روز را برای گذران زندگی مقرّر داشتیم تا در آن روزی به دست آورید.'),
(5684, 78, 12, 'و بر فرازتان هفت آسمانِ سخت و استوار ساختیم.'),
(5685, 78, 13, 'و خورشید را که چراغی گرم و پر فروغ است پدید آوردیم.'),
(5686, 78, 14, 'و از ابرهای باران دار، آبی ریزان فرو فرستادیم،'),
(5687, 78, 15, 'تا به وسیله آن از زمین دانه و گیاه برآوریم،'),
(5688, 78, 16, 'و نیز بوستان‌هایی انبوه با درختانی درهم پیچیده.'),
(5689, 78, 17, 'به یقین، روز داوری، پایانِ زمانی است که برای این جهان معیّن شده است.'),
(5690, 78, 18, 'روزی که در صور دمیده می‌شود، آن گاه شما گروه گروه [به صحنه قیامت] می‌آیید.'),
(5691, 78, 19, 'و آسمان گشوده می‌شود و درهایی می‌گردد و جهان آدمیان به جهان فرشتگان می‌پیوندد.'),
(5692, 78, 20, 'و کوه‌ها روان می‌شوند و نابود می‌گردند و از آنها چیزی جز سراب باقی نمی‌ماند.'),
(5693, 78, 21, 'همانا دوزخ کمینگاهی است،'),
(5694, 78, 22, 'برای سرکشان بازگشتگاهی.'),
(5695, 78, 23, 'روزگارانی دراز در آن خواهند ماند.'),
(5696, 78, 24, 'در آن جا نه خنَکی می‌چشند و نه نوشیدنی.'),
(5697, 78, 25, 'جز آبی جوشان و خونابه‌ای از دوزخیان.'),
(5698, 78, 26, 'آنان کیفری در خورِ آنچه می‌کردند خواهند دید.'),
(5699, 78, 27, 'چرا که به حساب روز قیامت امید نداشتند.'),
(5700, 78, 28, 'و سخت آیات ما را دروغ می‌انگاشتند.'),
(5701, 78, 29, 'و ما هر چیزی، از جمله اعمال آنان را در کتابی ارجمند ثبت کرده‌ایم.'),
(5702, 78, 30, 'پس ای منکران رستاخیز، بچشید که ما جز عذاب بر شما نمی‌افزاییم.'),
(5703, 78, 31, 'قطعاً تقواپیشگان را رستگاری و سرای نیکبختی خواهد بود.'),
(5704, 78, 32, 'باغ‌ها و تاکستان‌ها،'),
(5705, 78, 33, 'و زنانی نار پستان و همسال،'),
(5706, 78, 34, 'و جام‌هایی لبریز از شراب.'),
(5707, 78, 35, 'در آن جا نه سخن بیهوده به گوششان می‌رسد و نه تکذیب یکدیگر را می‌شنوند.'),
(5708, 78, 36, 'اینها به تقواپیشگان داده می‌شود در حالی که سزایی است از جانب پروردگار تو، و بخششی است از روی حساب.'),
(5709, 78, 37, 'همان پروردگار آسمان‌ها و زمین و آنچه میان آن دو است; خدایی که رحمتش فراگیر است و هیچ کس درباره آنچه او داوری کرده است از جانب وی قدرت سخن گفتن و میانجیگری ندارد،'),
(5710, 78, 38, 'آن روز از سخن گفتن ناتوانند که روح و فرشتگان به صف می‌ایستند. از حاضران در صحنه قیامت ـ چه آدمیان و چه جنّیان و چه فرشتگان ـ جز آن کسی که خدای رحمان به او اجازه دهد و سخن به صواب گوید، لب به سخن نمی‌گشاید.'),
(5711, 78, 39, 'آن روز حتماً به وقوع خواهد پیوست; پس هر که بخواهد راه بازگشتی به سوی خدا جوید، تا پاداش تقواپیشگان گیرد، کوتاهی نورزد.'),
(5712, 78, 40, 'به یقین، ما شما را از عذابی نزدیک بیم داده‌ایم; روزی که آدمی جزای دستاوردهایش را انتظار می‌کشد، و در این میان کافر می‌گوید: ای کاش خاک بودم.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'سوگند به آن فرشتگانی که از پایگاه‌های خود برای انجام مأموریت الهی، مجدانه از جا برمی خیزند،'),
(5714, 79, 2, 'و سوگند به آنان، که چون از جایگاه خود بیرون روند به آسانی خارج می‌شوند،'),
(5715, 79, 3, 'و سوگند به آنان، که چون روند شتاب میورزند،'),
(5716, 79, 4, 'و به سوی آنچه خدا اراده کرده است چنان که باید سبقت می‌گیرند،'),
(5717, 79, 5, 'و کار جهان را تدبیر می‌کنند; به همه اینها سوگند که شما برانگیخته خواهید شد;'),
(5718, 79, 6, 'در آن روز که صیحه‌ای بزرگ رخ دهد.'),
(5719, 79, 7, 'و از پی آن بانگی دیگر برخیزد.'),
(5720, 79, 8, 'آن روز که چنین شود، دل‌هایی بلرزند.'),
(5721, 79, 9, 'و دیدگانشان فرو افتد.'),
(5722, 79, 10, 'آنان در دنیا می‌گفتند: آیا پس از مرگ به حالت اول باز گردانده می‌شویم؟'),
(5723, 79, 11, 'آیا آن گاه که چند استخوان پوسیده و خرد گشتیم دوباره زنده می‌شویم؟'),
(5724, 79, 12, 'و به ریشخند گفتند: در این صورت، این بازگشتی زیانبار است.'),
(5725, 79, 13, 'اما زنده کردن مردگان بر ما دشوار نیست; آن تنها یک بانگ است که برمی آید و آنها را می‌راند.'),
(5726, 79, 14, 'و ناگهان زنده از دل خاک بر آمده روی زمین هموار قرار می‌گیرند.'),
(5727, 79, 15, 'ای پیامبر، آیا سرگذشت موسی به تو رسیده است؟'),
(5728, 79, 16, 'آن گاه که پروردگارش او را در وادی مقدّس (طُوی) به رسالت برگزید و به وی ندا در داد:'),
(5729, 79, 17, 'به سوی فرعون برو که او طغیان کرده و بندگی خدا را وانهاده است.'),
(5730, 79, 18, 'و به او بگو: آیا دوست داری که دل از طغیان بشویی و به پاکی رسی؟'),
(5731, 79, 19, 'و آیا سرِ آن داری که تو را به شناخت پروردگارت راه نمایم تا از او بیم داشته باشی و بندگی اش کنی؟'),
(5732, 79, 20, 'موسی به سراغ فرعون رفت و او را به بندگی خدا فرا خواند و بزرگترین آیت (اژدها شدنِ عصایش) را به او نشان داد.'),
(5733, 79, 21, 'فرعون رسالت موسی را دروغ انگاشت و او را نافرمانی کرد.'),
(5734, 79, 22, 'سپس پشت کرد و برای مبارزه با رسالت موسی به تلاش برخاست.'),
(5735, 79, 23, 'آن گاه مردم را گرد آورد و ندا در داد.'),
(5736, 79, 24, 'و گفت: منم برترین پروردگار شما.'),
(5737, 79, 25, 'پس خدا او را به عذاب آخرت و دنیا گرفتار ساخت.'),
(5738, 79, 26, 'به یقین، در این داستان برای کسی که فطرتی سالم دارد و از عذاب خدا می‌ترسد، پند و عبرتی است.'),
(5739, 79, 27, 'ای منکران رستاخیز، آیا آفرینش شما سخت‌تر است یا آفرینش آسمان که خدا آن را بنا کرده است.'),
(5740, 79, 28, 'سقفش را برافراشت و آن را موزون ساخت.'),
(5741, 79, 29, 'و شبش را تاریک کرد و روزش را آشکار نمود،'),
(5742, 79, 30, 'و زمین را پس از آفرینش آسمان و سامان دادن آن گسترانید،'),
(5743, 79, 31, 'آبِ آن را با شکافتن چشمه‌ها، از آن درآورْد و جاری ساخت و گیاهش را رویانید،'),
(5744, 79, 32, 'و کوه‌ها را بر زمین استوار کرد.'),
(5745, 79, 33, 'تا شما و دام هایتان را بهره مند سازد.'),
(5746, 79, 34, 'این جهان سرانجام به آخرت خواهد پیوست. پس وقتی که آن بزرگ‌ترین هنگامه سهمگین رخ دهد،'),
(5747, 79, 35, 'روزی که انسان آنچه را که در پی آن کوشیده است به یاد آوَرد،'),
(5748, 79, 36, 'و دوزخ برای کسی که دیده‌ای دارد که بدان ببیند نمایانده شود، آن گاه آدمیان دو دسته شوند.'),
(5749, 79, 37, 'امّا آن کس که سرکشی کرده و از مرز بندگی خدا خارج شده،'),
(5750, 79, 38, 'و زندگی دنیا را برگزیده و آخرت را فراموش کرده است،'),
(5751, 79, 39, 'به یقین دوزخ جایگاه اوست.'),
(5752, 79, 40, 'و امّا آن کس که از مقام و مرتبه پروردگارش ترسیده و نفس خود را از هوا و هوس باز داشته است،'),
(5753, 79, 41, 'به یقین بهشت جایگاه اوست.'),
(5754, 79, 42, 'مشرکان به تمسخر از تو می‌پرسند: قیامت چه زمانی برپا می‌شود؟'),
(5755, 79, 43, 'ای پیامبر، در خاطره تو از حقیقت رستاخیز چیست و از آن چه می‌دانی تا زمان وقوع آن را بدانی؟'),
(5756, 79, 44, 'امر قیامت تنها به پروردگار تو باز می‌گردد و اوست که حقیقت آن و هنگام وقوعش را می‌داند.'),
(5757, 79, 45, 'پس خبر دادن از زمان قیامت برعهده تو نیست، تو فقط هشداردهنده‌ای و کسی را هشدار می‌دهی که درخور ترس از رستاخیز باشد.'),
(5758, 79, 46, 'آنان بدانند که رستاخیز نزدیک است. روزی که آن را ببینند، گویی در قبرهایشان جز به اندازه عصرگاهِ روزی یا چاشتِ آن بیشتر نمانده‌اند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'چهره در هم کشید و روی برتافت.'),
(5760, 80, 2, 'بدان سبب که آن نابینا نزد او آمد.'),
(5761, 80, 3, 'تو چنین کردی در حالی که نمی‌دانی، شاید او پند گیرد و کار شایسته کند و پاک گردد،'),
(5762, 80, 4, 'یا هشیار شود و تذکر پیامبر برای او سودمند باشد و ایمان بیاورد.'),
(5763, 80, 5, 'امّا آن کس که توانگر است،'),
(5764, 80, 6, 'تو به او روی می‌آوری و به او می‌پردازی،'),
(5765, 80, 7, 'در حالی که بر تو گناهی نیست که او ایمان نیاوَرد و پاک نگردد.'),
(5766, 80, 8, 'و امّا آن کس که شتابان نزد تو آمد تا معارف دین را فرا گیرد و پاک گردد،'),
(5767, 80, 9, 'در حالی که از خدا می‌ترسید،'),
(5768, 80, 10, 'تو از او روی می‌گردانی و به دیگری می‌پردازی.'),
(5769, 80, 11, 'نه، مبادا چنین کنی، این قرآن پندی است.'),
(5770, 80, 12, 'پس هر که بخواهد، آن را یاد کند و پند گیرد.'),
(5771, 80, 13, 'قرآن در صحیفه‌هایی ارجمند نوشته شده است;'),
(5772, 80, 14, 'صحیفه‌هایی بلندمرتبه که از هر باطل و بیهوده و تناقضی پاک گشته است.'),
(5773, 80, 15, 'با دست فرشتگانی به سوی پیامبر فرستاده شده است،'),
(5774, 80, 16, 'فرشتگانی که بزرگوار و نیکوکارند.'),
(5775, 80, 17, 'مرگ بر این انسان کفرپیشه که چقدر حق پوش است و با این که آثار تدبیر خدا را در خود و جهان هستی می‌نگرد باز هم ربوبیّت او را انکار می‌کند.'),
(5776, 80, 18, 'او را از چه چیزی آفریده است که سزاوار طغیان باشد؟'),
(5777, 80, 19, 'از نطفه‌ای ناچیز او را آفرید، سپس او و کارهایش را تقدیر کرد.'),
(5778, 80, 20, 'آن گاه راه درست را برای او آسان ساخت.'),
(5779, 80, 21, 'سپس او را بمیرانْد و در گورش نهاد.'),
(5780, 80, 22, 'آن گاه چون بخواهد، او را حیات می‌بخشد و برمی انگیزد.'),
(5781, 80, 23, 'آیا او با دیدن این همه آثار قدرت الهی، ربوبیت خدا را باور کرده است؟ هرگز، او هنوز آنچه را خدا به وی فرمان داده به جای نیاورده است.'),
(5782, 80, 24, 'پس انسان باید برای درک ربوبیّت خدا به خوراکی اش بنگرد و در آن دقّت کند،'),
(5783, 80, 25, 'که ما آب را به صورت باران چنان که باید فرو ریختیم،'),
(5784, 80, 26, 'سپس زمین را با رویش گیاهان به خوبی شکافتیم،'),
(5785, 80, 27, 'و دانه در آن رویاندیم،'),
(5786, 80, 28, 'و درخت انگور و سبزی،'),
(5787, 80, 29, 'و درخت زیتون و درخت خرما،'),
(5788, 80, 30, 'و باغ‌هایی انبوه از درختان بزرگ،'),
(5789, 80, 31, 'و میوه و چراگاه.'),
(5790, 80, 32, 'همه خوراکی‌ها را رویاندیم تا شما و دام هایتان را بهره مند سازیم.'),
(5791, 80, 33, 'این جهان سرانجام به آخرت خواهد پیوست; پس هنگامی که در صور دمیده شود و آن بانگ شدید کرکننده برآید،'),
(5792, 80, 34, 'روزی که آدمی از برادرش می‌گریزد،'),
(5793, 80, 35, 'و از مادر و پدرش،'),
(5794, 80, 36, 'و از همسر و پسرانش;'),
(5795, 80, 37, 'زیرا هر کسی از ایشان آن روز کاری دارد که او را کفایت می‌کند و از پرداختن به دیگران باز می‌دارد،'),
(5796, 80, 38, 'آری، چون آن بانگ برآید و قیامت برپا گردد، در آن روز چهره‌هایی تابانند،'),
(5797, 80, 39, ''),
(5798, 80, 40, 'و چهره‌هایی در آن روز بر آنها غبار غم و اندوه نشسته است.'),
(5799, 80, 41, 'و سیاهی و تیرگی آنها را فرا می‌گیرد.'),
(5800, 80, 42, 'اینان همان کافرانِ بدکارِ گناه پیشه‌اند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'آن گاه که خورشید را سراسر تاریک کنند،'),
(5802, 81, 2, 'و آن گاه که ستارگان فرو ریزند،'),
(5803, 81, 3, 'و آن گاه که کوه‌ها را روان سازند،'),
(5804, 81, 4, 'و آن گاه که شتران آبستن وانهاده شوند،'),
(5805, 81, 5, 'و آن گاه که حیوانات وحشی محشور شوند،'),
(5806, 81, 6, 'و آن گاه که دریاها را شعلهور کنند،'),
(5807, 81, 7, 'و آن گاه که همگان را جفت و قرین کنند; نیکان را با حوریان بهشتی و مجرمان را با شیاطین،'),
(5808, 81, 8, 'و آن گاه که از دخترِ زنده به گور شده پرسند،'),
(5809, 81, 9, 'به کدامین گناه کشته شد؟'),
(5810, 81, 10, 'و آن گاه که نامه‌های اعمال برای حسابرسی گشوده شود،'),
(5811, 81, 11, 'و آن گاه که آسمان برکنده شود،'),
(5812, 81, 12, 'و آن گاه که دوزخ را برافروخته گردانند،'),
(5813, 81, 13, 'و آن گاه که بهشت برای ورود بهشتیان نزدیک آورده شود،'),
(5814, 81, 14, 'آن گاه که این حوادث روی دهد، هر کسی آنچه کرده است حاضر می‌یابد و از آن آگاه می‌شود.'),
(5815, 81, 15, 'سوگند یاد می‌کنم به آن ستارگانی که باز می‌گردند و نهان می‌شوند;'),
(5816, 81, 16, 'و با شتاب و نظم حرکت می‌کنند و از حرکت باز می‌ایستند چنان که گویی در آشیانه خود قرار گرفته‌اند،'),
(5817, 81, 17, 'و سوگند به شب آن گاه که پشت می‌کند و می‌رود،'),
(5818, 81, 18, 'و سوگند به بامداد آن گاه که برمی دمد و روشنی اش در افق پدیدار می‌شود،'),
(5819, 81, 19, 'که قرآن سخن فرستاده‌ای ارجمند (جبرئیل) است.'),
(5820, 81, 20, 'همو که نیرومند و نزد خدایِ صاحب عرش مقرّب و دارای مقام و منزلت است.'),
(5821, 81, 21, 'در آن جا فرشتگان دیگر از او فرمان می‌برند و در ابلاغ آنچه خدا به او فرمان می‌دهد امین است.'),
(5822, 81, 22, 'و سوگند به همه اینها که محمّد مبتلا به جنون نیست; او سال‌ها همراه و همنشین شما بوده است.'),
(5823, 81, 23, 'او قطعاً جبریل را در آن کرانه روشن مشاهده کرد.'),
(5824, 81, 24, 'و او در ابلاغ پیام‌های غیبی به شما، بخل نمیورزد.'),
(5825, 81, 25, 'و این قرآن گفتار شیطان آن موجود رانده شده نیست.'),
(5826, 81, 26, 'پس شما به کجا می‌روید؟'),
(5827, 81, 27, 'این قرآن جز تذکاری برای جهانیان نیست.'),
(5828, 81, 28, 'ولی تنها کسانی از شما از آن بهره می‌برند که بخواهند بر عبادت و اطاعت خدا پایدار باشند.'),
(5829, 81, 29, 'و شما چیزی را اراده نمی‌کنید مگر این که خدا که پروردگار جهان هاست آن را اراده کند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'آن گاه که آسمان شکافته شود،'),
(5831, 82, 2, 'و آن گاه که ستارگان پراکنده شوند،'),
(5832, 82, 3, 'و آن گاه که حایلِ میان دریاها شکافته شود و آب‌های شور و شیرین به هم آمیخته شوند،'),
(5833, 82, 4, 'و آن گاه که گورها را برای خارج شدن مردگان زیر و زَبَر کنند،'),
(5834, 82, 5, 'هر کسی از کارهایی که انجام داده و پیش فرستاده و سنّت‌هایی که بر جای نهاده است آگاه شود.'),
(5835, 82, 6, 'ای انسان، ای آن که قیامت را انکار می‌کنی، چه چیز تو را به واسطه پروردگار بزرگوار و کریمت فریب داد و خام کرد؟'),
(5836, 82, 7, 'آن خدایی که تو را آفرید، سپس، اندام هایت را سامان داد و با توازن دادن آنها تو را تعادل بخشید.'),
(5837, 82, 8, 'و در هر شکلی که خواست تو را ترکیب کرد.'),
(5838, 82, 9, 'نه، جای آن نیست که به لطف و کرم خدا فریفته شوی; بلکه شما آدمیان، روز جزا را دروغ می‌انگارید که کفر میورزید و عصیان می‌کنید.'),
(5839, 82, 10, 'و قطعاً فرشتگانی بر شما گمارده شده‌اند که نگهبان اعمالتان هستند;'),
(5840, 82, 11, 'فرشتگانی بزرگوار که اعمال شما را می‌نگارند،'),
(5841, 82, 12, 'آنچه را انجام می‌دهید می‌دانند.'),
(5842, 82, 13, 'به یقین، نیکان در [بهشت پر] نعمت‌اند.'),
(5843, 82, 14, 'و قطعاً گنهکارانِ کفرپیشه به دوزخ اندرند.'),
(5844, 82, 15, 'روز جزا در آن جا درآیند و همواره در آن بمانند.'),
(5845, 82, 16, 'و هرگز از آن جا بیرون نشوند و ناپدید نگردند.'),
(5846, 82, 17, 'چه چیز تو را آگاه کرده است که حقیقت روز جزا چیست؟'),
(5847, 82, 18, 'باز هم چه چیزی تو را آگاه کرده است که حقیقت روز جزا چیست؟'),
(5848, 82, 19, 'یاد کن روزی را که هیچ کس به سود کسی قدرت هیچ کاری ندارد، و روزی که رستاخیز رخ دهد، آشکار شود که فرمان از آنِ خداست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'وای بر کسانی که از پیمانه و ترازو می‌کاهند;'),
(5850, 83, 2, 'همانان که چون به پیمانه از مردم چیزی می‌گیرند، تمام می‌ستانند،'),
(5851, 83, 3, 'و چون به پیمانه یا ترازو به آنان چیزی می‌دهند، کم می‌دهند و زیان می‌رسانند.'),
(5852, 83, 4, 'شگفتا! آیا آنان گمان نمی‌برند که برانگیخته می‌شوند؟'),
(5853, 83, 5, 'به هنگام روزی بزرگ!'),
(5854, 83, 6, 'روزی که مردم برای داوری پروردگار جهانیان از دل خاک به پا می‌خیزند.'),
(5855, 83, 7, 'کم فروشی نکنند و از روز حساب غفلت نورزند، قطعاً سرنوشت فاجران که رستاخیز را دروغ می‌شمرند در سجّین مقرّر شده است.'),
(5856, 83, 8, 'و چه چیز تو را آگاه کرده است که سجّین چیست؟'),
(5857, 83, 9, 'سرنوشتی است حتمی که خدا برای گنهکاران مقرّر کرده است و برای همیشه در آن محبوسند.'),
(5858, 83, 10, 'وای بر تکذیب کنندگان، آن گاه که آنچه برایشان مقدر شده است تحقّق یابد!'),
(5859, 83, 11, 'همانان که روز جزا را دروغ می‌شمرند.'),
(5860, 83, 12, 'و جز هر تجاوزکار گناه پیشه‌ای آن را دروغ نمی‌شمرد.'),
(5861, 83, 13, 'هنگامی که آیات ما بر او تلاوت شود، می‌گوید: اینها افسانه‌هایی است که مردمان نخستین ساخته و پرداخته‌اند.'),
(5862, 83, 14, 'نه، آیات قرآن افسانه نیست، بلکه گناهانی که همواره مرتکب می‌شوند بر دل هایشان زنگار شده است، ازاین رو حق را درنمی یابند.'),
(5863, 83, 15, 'مبادا مرتکب گناه گردند که قطعاً گنهکاران در روز جزا از کرامت و قُرب پروردگارشان محرومند.'),
(5864, 83, 16, 'وانگهی، آنان حتماً به دوزخ درمی آیند و در آن جا ماندگار می‌شوند.'),
(5865, 83, 17, 'آن گاه به آنان گفته می‌شود: این است همان چیزی که آن را دروغ می‌شمردید.'),
(5866, 83, 18, 'آری، کم فروشی نکنند و از روز حساب غفلت نورزند که قطعاً سرنوشت نیکان در علیّین (مرتبه‌های والا و درجات قرب به خدا) است.'),
(5867, 83, 19, 'و چه چیز تو را آگاه کرده است که علیّین چیست؟'),
(5868, 83, 20, 'فرجامی است حتمی و مشخص،'),
(5869, 83, 21, 'که مقرّبان درگاه خدا آن را می‌نگرند.'),
(5870, 83, 22, 'همانا نیکان در نعمتی فراوان به سر می‌برند.'),
(5871, 83, 23, 'آنان در حجله‌ها بر تخت‌ها نشسته‌اند و بهشت و نعمت‌های آن را نظاره می‌کنند.'),
(5872, 83, 24, 'در چهره هایشان طراوات نعمت را درمی یابی.'),
(5873, 83, 25, 'از شرابی زلال و خالص که مُهر شده است در اختیارشان می‌گذارند.'),
(5874, 83, 26, 'شرابی که مهرش از مُشک است; پس سبقت گیرندگان در دستیابی به مواهب بهشتی، به ویژه این شراب، باید بکوشند و بر یکدیگر سبقت گیرند.'),
(5875, 83, 27, 'آمیزه آن از تسنیم است:'),
(5876, 83, 28, 'آن چشمه‌ای که مقرّبان درگاه خدا از آن می‌آشامند.'),
(5877, 83, 29, 'همانا کافران بدان سبب که گنهکار بودند، به نیکان به خاطر ایمانشان می‌خندیدند.'),
(5878, 83, 30, 'و چون بر آنان می‌گذشتند، با اشاره چشم و ابرو آنان را مسخره می‌کردند.'),
(5879, 83, 31, 'و چون نزد کسانشان باز می‌گشتند، شادمان از آنچه کرده‌اند باز می‌گشتند.'),
(5880, 83, 32, 'و چون مؤمنان را می‌دیدند، می‌گفتند: بی تردید اینان گمراهند.'),
(5881, 83, 33, 'و حال آن که آنان برای نگهبانی مؤمنان فرستاده نشده بودند که درباره آنان داوری کنند و گمراهشان بنامند.'),
(5882, 83, 34, 'پس امروز که روز جزاست، مؤمنان به کافران می‌خندند.'),
(5883, 83, 35, 'مؤمنان در حجله‌ها روی تخت‌ها نشسته و می‌نگرند،'),
(5884, 83, 36, 'که آیا کافرانِ گنه پیشه را به آنچه می‌کردند سزا داده‌اند؟');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'آن گاه که آسمان از هم بشکافد،'),
(5886, 84, 2, 'و پروردگارش را فرمان برد و سزد که چنین کند،'),
(5887, 84, 3, 'و آن گاه که زمین کشیده و گسترده شود،'),
(5888, 84, 4, 'و مردگانی را که در دل دارد بیرون افکند و از آنها تهی گردد،'),
(5889, 84, 5, 'و پروردگارش را فرمان برد و سزد که چنین کند، آن گاه که این حوادث رخ دهد، رستاخیز برپا می‌شود و همگان به دیدار پروردگار خود خواهند شتافت و سزای کردارشان را خواهند دید.'),
(5890, 84, 6, 'ای انسان، تو با رنج و تلاشی وصف ناپذیر به سوی پروردگارت روانه‌ای، و سرانجام به لقاء و داوری او خواهی رسید.'),
(5891, 84, 7, 'امّا آن کس که کارنامه اش به دست راستش داده شود،'),
(5892, 84, 8, 'به زودی با حسابی آسان کارهای او محاسبه می‌شود.'),
(5893, 84, 9, 'و شادمان به سوی کسانش (حوریان و خدمتکاران بهشتی) باز می‌گردد.'),
(5894, 84, 10, 'و امّا آن کس که کارنامه اش از پشت سرش به او داده شود،'),
(5895, 84, 11, 'دیری نمی‌گذرد که بر خود نفرین می‌کند و هلاکت خود را می‌خواهد.'),
(5896, 84, 12, 'و به آتش فروزان در می‌آید.'),
(5897, 84, 13, 'چرا که او در دنیا در میان خانواده خود سرمستی و شادمانی می‌کرد.'),
(5898, 84, 14, 'او می‌پنداشت که هرگز برای حسابرسی کارهایش به سوی خدا باز نمی‌گردد.'),
(5899, 84, 15, 'چرا، بازگشت او به سوی خدا حتمی است، و قطعاً پروردگارش به کارهای او بیناست.'),
(5900, 84, 16, 'سوگند یاد می‌کنم به سرخی و زردی و سفیدیِ افق هنگام غروب،'),
(5901, 84, 17, 'و به شب و آنچه در خود گرد می‌آورد،'),
(5902, 84, 18, 'و به ماه چون فروغش کامل شود و بدر تمام گردد،'),
(5903, 84, 19, 'که شما انسان‌ها در حرکت خود به سوی خدا مراحلی را یکی پس از دیگری خواهید پیمود تا سرانجام به حساب اعمال و جزای خویش برسید.'),
(5904, 84, 20, 'شگفتا! این انسان‌ها را چه شده است که ایمان نمی‌آورند؟'),
(5905, 84, 21, 'و هنگامی که قرآن بر آنان تلاوت شود سجده نمی‌کنند؟'),
(5906, 84, 22, 'قرآن نه در بیان حقایق نارساست و نه از برهان بی بهره، بلکه کسانی که کافر شده‌اند، بر کفرورزی و تکذیب [حق] خوی گرفته‌اند از این روست که حقایق قرآن را دروغ می‌انگارند.'),
(5907, 84, 23, 'و خدا به آنچه در دل‌های خود نهان می‌دارند داناتر است.'),
(5908, 84, 24, 'پس آنان را به عذابی دردناک نوید ده.'),
(5909, 84, 25, 'ولی کسانی که ایمان آورده و کارهای شایسته کرده‌اند، برای آنان پاداشی بی منّت خواهد بود.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'سوگند به آسمانِ دارای برج‌ها، جایگاه ستارگان که سنگر دفع شیاطین است،'),
(5911, 85, 2, 'و سوگند به روز رستاخیز، روزی که وعده داوری در آن داده شده است،'),
(5912, 85, 3, 'و سوگند به پیامبر آن که ناظر و شاهد اعمال امّت است و سوگند به پاداش مؤمنانِ ستمدیده و کیفر ستمکاران، پاداش و کیفری که به عیان مشاهده خواهد شد; به همه اینها سوگند که خدا مؤمنان را از نیرنگ کافران نگاه خواهد داشت و آنان را در ایمان، شکیبا و استوار خواهد ساخت.'),
(5913, 85, 4, 'لعنت و نفرین بر کسانی که آن خندق را برای سوزاندن مؤمنان حفر کردند;'),
(5914, 85, 5, 'همانان که آتشی شعلهور با هیزم فراوان برافروختند;'),
(5915, 85, 6, 'آن گاه که بر کنار خندقِ آتش نشستند،'),
(5916, 85, 7, 'و آنچه را که بر سر مؤمنان می‌آوردند تماشا می‌کردند،'),
(5917, 85, 8, 'و چیزی را از آنان ناخوشایند نمی‌دانستند جز این که به خدا، آن مقتدر شکست ناپذیر و ستوده ایمان آورده بودند.'),
(5918, 85, 9, 'همان خدایی که فرمانروایی آسمان‌ها و زمین از آنِ اوست; و خداوند بر هر چیزی ناظر است و هیچ کاری بر او پنهان نمی‌ماند.'),
(5919, 85, 10, 'به یقین، کسانی که مردان و زنان با ایمان را شکنجه کرده و آزار داده و سپس توبه نکرده‌اند، آنان را انواع عذاب دوزخ هست و عذاب آتش خواهند داشت.'),
(5920, 85, 11, 'کسانی که ایمان آورده و کارهای شایسته کرده‌اند، برای آنان بوستان‌هایی با درختانی انبوه خواهد بود که از زیر آنها جویبارها جاری است. این است سعادت بزرگ.'),
(5921, 85, 12, 'قطعاً گرفتنِ پروردگارت گرفتنی سخت است.'),
(5922, 85, 13, 'اوست که موجودات را پدید می‌آورد و آنها را پس از نابودی و تباهی به حالت نخستشان برمی گرداند و بازسازی می‌کند.'),
(5923, 85, 14, 'و اوست که آمرزنده مؤمنان و دوستدار آنان است.'),
(5924, 85, 15, 'صاحب عرش و ارجمند و با عظمت است.'),
(5925, 85, 16, 'هر کاری را بخواهد به طور کامل انجام می‌دهد.'),
(5926, 85, 17, 'آیا داستان آن سپاهیان به تو رسیده است؟'),
(5927, 85, 18, 'داستان فرعون و قوم ثمود؟ خدا آنان را نابود ساخت و پیامبرشان را یاری کرد.'),
(5928, 85, 19, 'ای پیامبر، کافران قوم تو از این داستان‌ها پند نمی‌گیرند و ایمان نمی‌آورند، بلکه همچنان بر دروغ انگاشتن قرآن اصرار میورزند.'),
(5929, 85, 20, 'و خدا از هر سو بر آنان احاطه دارد، پس نمی‌توانند از تحت قدرت او خارج شوند.'),
(5930, 85, 21, 'قرآن ـ چنان که آنان می‌پندارند ـ ساخته بشر یا القای شیاطین نیست، بلکه کتابی است خوانده شده و ارجمند و با عظمت،'),
(5931, 85, 22, 'که در لوحی مصون از هر کذب و باطلی قرار دارد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'سوگند به آسمان و آن آشکار شونده در شب،'),
(5933, 86, 2, 'و چه چیز تو را آگاه کرده است که آن آشکار شونده در شب چیست؟'),
(5934, 86, 3, 'آن ستاره درخشان است،'),
(5935, 86, 4, 'سوگند به اینها که هیچ کس نیست مگر این که نگهبانی بر او گمارده شده است که اعمال نیک و بد او را ثبت می‌کند.'),
(5936, 86, 5, 'انسان برای این که رستاخیز را باور کند باید بنگرد که از چه چیز آفریده شده است.'),
(5937, 86, 6, 'او از آبی جهنده آفریده شده است.'),
(5938, 86, 7, 'که از میان پشت و استخوان‌های سینه بیرون می‌آید.'),
(5939, 86, 8, 'قطعاً خداوندی که او را از این مادّه آفریده است، می‌تواند او را پس از مرگ به صورت نخست بازگرداند،'),
(5940, 86, 9, 'در روزی که نهان‌ها (عقاید و باورها و آثار کردارها) سنجیده شود و خالص از ناخالص جدا گردد.'),
(5941, 86, 10, 'پس برای او هیچ نیرویی نیست که عذاب را از خود دور کند و یاوری نیز ندارد که عذاب را از او بگرداند.'),
(5942, 86, 11, 'سوگند به آسمان که با طلوع و غروب ستارگان باز می‌گردد،'),
(5943, 86, 12, 'و سوگند به زمین که با رویش گیاهان می‌شکافد،'),
(5944, 86, 13, 'که قرآن سخنی است که حق را از باطل جدا می‌سازد.'),
(5945, 86, 14, 'و آن سخنی شوخی نیست.'),
(5946, 86, 15, 'کافران سخت نیرنگ می‌کنند تا بدین وسیله نور خدا را خاموش کنند،'),
(5947, 86, 16, 'و من نیز با مهلت دادن به آنان، خامشان می‌کنم تا در گمراهی بمانند و به کیفر کردارشان گرفتار آیند.'),
(5948, 86, 17, 'حال که چنین است کافران را مهلت ده; اندک زمانی به آنان فرصت ده.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'منزّه شمار نام پروردگارت را که از همگان برتر است;'),
(5950, 87, 2, 'همان که موجودات را آفرید و اجزای آنها را هماهنگ ساخت.'),
(5951, 87, 3, 'و آن که برای آفریدگان حدّی معیّن کرد و آنها را به آنچه برایشان تقدیر کرده است هدایت نمود.'),
(5952, 87, 4, 'و آن که علوفه چراگاه‌ها را رویانید،'),
(5953, 87, 5, 'سپس آن را خشک و تیره گون ساخت.'),
(5954, 87, 6, 'به زودی تو را بر خواندن قرآن و دانش آن توانا خواهیم ساخت به گونه‌ای که آن را فراموش نمی‌کنی.'),
(5955, 87, 7, 'ولی بدان که این وعده سلب قدرت از خدا نمی‌کند و اگر خدا بخواهد ـ که البته نخواهد خواست ـ آن را فراموش می‌کنی. به یقین او آشکار و آنچه را نهان می‌شود می‌داند.'),
(5956, 87, 8, 'و تو را چنان استعدادی می‌دهیم که آسان‌ترین راه را برای فراخوانیِ مردم برگزینی.'),
(5957, 87, 9, 'پس اگر تذکر و اندرز برای کسانی سود بخشد، آنان را یادآوری کن و اندرز ده.'),
(5958, 87, 10, 'به زودی آن کس که از خدا بیم دارد متذکّر خواهد شد.'),
(5959, 87, 11, 'و آن کس که تیره بخت‌ترین است و در دل از خدا بیمی ندارد، از تذکار قرآن دوری خواهد گزید،'),
(5960, 87, 12, 'همو که به بزرگترین آتش درمی آید و در آن ماندگار می‌شود.'),
(5961, 87, 13, 'آن گاه نه در آن می‌میرد تا عذاب نبیند و نه از آن رهایی می‌یابد تا به حیاتی آسوده رسد.'),
(5962, 87, 14, 'قطعاً نیکبخت شد کسی که خود را از پلیدی‌ها و وابستگی‌های مادّی پاک ساخت،'),
(5963, 87, 15, 'و نام پروردگارش را یاد کرد و نماز گزارد.'),
(5964, 87, 16, '[شما به آخرت دل نمی‌بندید و به پاکی نمی‌گرایید] بلکه زندگی دنیا را برمی گزینید،'),
(5965, 87, 17, 'در صورتی که سرای آخرت بهتر و پاینده‌تر است.'),
(5966, 87, 18, 'قطعاً این حقایق در کتاب‌های آسمانی نخستین وجود دارد،'),
(5967, 87, 19, 'در کتاب‌های ابراهیم و موسی.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'آیا خبر آن رویداد فراگیر به تو رسیده است؟'),
(5969, 88, 2, 'روزی که آن حادثه رخ دهد، گروهی خوار و زبونند و آثار خواری در چهره هایشان نمایان است.'),
(5970, 88, 3, 'آنان در دنیا تلاش بیهوده کرده و رنج و محنت کشیده‌اند.'),
(5971, 88, 4, 'و آن روز به آتش سوزان در می‌آیند و در آن ماندگارند.'),
(5972, 88, 5, 'از چشمه‌ای داغ به آنان می‌نوشانند،'),
(5973, 88, 6, 'و غذایی جز خارِ خشکیده ندارند،'),
(5974, 88, 7, 'غذایی که نه فربه می‌سازد و نه گرسنگی را چاره می‌کند.'),
(5975, 88, 8, 'آن روز گروهی نیز خرّم و شادابند،'),
(5976, 88, 9, 'و از تلاشی که در دنیا کرده‌اند خشنودند.'),
(5977, 88, 10, 'در بهشتی برین جای خواهند گرفت.'),
(5978, 88, 11, 'در آن جا سخن بیهوده ای نمی‌شنوند.'),
(5979, 88, 12, 'در آن جا چشمه‌هایی است روان.'),
(5980, 88, 13, 'در آن جاست تخت‌هایی بلند.'),
(5981, 88, 14, 'و تنگ‌هایی نهاده شده،'),
(5982, 88, 15, 'و بالش‌هایی کنار هم چیده،'),
(5983, 88, 16, 'و فرش‌هایی فاخر و گسترده.'),
(5984, 88, 17, 'آیا برای این که به تدبیر خدا پی ببرند، به شتران نمی‌نگرند که چگونه آفریده شده‌اند؟'),
(5985, 88, 18, 'و به آسمان نمی‌نگرند که چگونه برافراشته شده است؟'),
(5986, 88, 19, 'و به کوه‌ها نمی‌نگرند که چگونه روی زمین قرار داده شده‌اند؟'),
(5987, 88, 20, 'و به زمین نمی‌نگرند که چگونه گسترده و هموار شده است؟'),
(5988, 88, 21, 'پس ربوبیّت بی همتای خدا و روز جزا را به مردم تذکّر ده که تو فقط تذکّردهنده‌ای.'),
(5989, 88, 22, 'تو بر آنان چیره نیستی.'),
(5990, 88, 23, 'آری، پیوسته همگان را تذکّر ده، مگر کسی را که از تذکّر تو روی برتابد و کفر ورزد،'),
(5991, 88, 24, 'که خدا او را به بزرگترین عذاب کیفر خواهد داد.'),
(5992, 88, 25, 'همانا بازگشت مردم به سوی ماست.'),
(5993, 88, 26, 'آن گاه حساب اعمالشان برعهده ما خواهد بود.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'سوگند به سپیده دم،'),
(5995, 89, 2, 'و به شب‌های ده گانه ماه ذی حجّه،'),
(5996, 89, 3, 'و به آن جفت و طاق (روز تَرویه و عَرَفه)،'),
(5997, 89, 4, 'و به شب آن گاه که در حال گذر است، به همه اینها سوگند که طغیانگران دچار عذاب خواهند شد و خداپرستان پاداش خواهند گرفت.'),
(5998, 89, 5, 'آیا در اینها که یاد شد، برای خردمندی که حق را از باطل تشخیص دهد، سوگندی رسا هست؟'),
(5999, 89, 6, 'آیا ندانسته‌ای که پروردگارت با قوم عاد چه کرد؟'),
(6000, 89, 7, 'با ارم، آن شهری که پایه‌ها و ستون‌ها (قصرها و بناهای عالی) داشت،'),
(6001, 89, 8, 'که مانند آن در هیچ یک از شهرها ساخته نشده بود.'),
(6002, 89, 9, 'آیا ندانسته‌ای که خدا با قوم ثمود چه کرد؟ همانان که در آن درّه، صخره‌ها را می‌بریدند و در کوه‌ها خانه می‌ساختند.'),
(6003, 89, 10, 'آیا ندانسته‌ای که خدا با فرعون که صاحب میخ‌ها بود چه کرد؟'),
(6004, 89, 11, 'همان کسانی که در شهرها سر به طغیان برداشتند.'),
(6005, 89, 12, 'و در آنها تباهی بسیار به بار آوردند.'),
(6006, 89, 13, 'پس پروردگارت تازیانه عذاب را بر آنها فرود آورد.'),
(6007, 89, 14, 'قطعاً پروردگار تو در کمین طغیانگران است و آنان را سزا خواهد داد.'),
(6008, 89, 15, 'آری، آدمی این گونه است که وقتی پروردگارش او را می‌آزماید بدین طریق که عزیزش می‌دارد و نعمتش می‌بخشد، می‌گوید: پروردگارم مرا عزیز داشته و انجام هر کاری را بر من روا دانسته است.'),
(6009, 89, 16, 'و امّا وقتی او را می‌آزماید بدین طریق که روزی را بر او تنگ می‌گیرد، می‌گوید: پروردگارم مرا خوار شمرده است.'),
(6010, 89, 17, 'نه، چنین نیست که خواری در بینوایی و عزّت در توانگری باشد، اینها وسیله آزمایش شما هستند. چگونه ثروت برای شما مایه عزّت است در حالی که شما یتیم را گرامی نمی‌دارید.'),
(6011, 89, 18, 'و یکدیگر را بر اطعام بینوا ترغیب نمی‌کنید.'),
(6012, 89, 19, 'و میراث یتیمان را با حق خود یکجا می‌خورید.'),
(6013, 89, 20, 'و مال را بسیار دوست می‌دارید.'),
(6014, 89, 21, 'آری، هرگز توانگری نشانه عزّت و بینوایی نشانه خواری نزد خدا نیست، آن گاه که زمین را خرد و ذرّه ذرّه گردانند،'),
(6015, 89, 22, 'و فرمان پروردگارت در رسد و فرشتگان صف در صف بیایند،'),
(6016, 89, 23, 'و دوزخ در آن روز آورده شود (پدیدار گردد)، آن روز انسان متوجه می‌شود که نعمت‌ها و محنت‌ها همه وسیله آزمون الهی بوده است، ولی این توجه کجا برای او سودمند است؟'),
(6017, 89, 24, 'آن روز انسان کفرپیشه می‌گوید: ای کاش برای زندگی آخرتم که حیات واقعی است کار شایسته‌ای پیش فرستاده بودم.'),
(6018, 89, 25, 'پس در آن روز هیچ کس به سختیِ عذاب او عذاب نکند.'),
(6019, 89, 26, 'و هیچ کس به سختیِ در بند کشیدن او در بند نکشد.'),
(6020, 89, 27, 'و به آن کسی که خدا را پرستش نموده و دلش را به یاد او آرام داشته است خطاب می‌شود: ای نفس مطمَئنّه،'),
(6021, 89, 28, 'به سوی پروردگارت بازگرد که تو به پیشامدهایی که او برایت مقدّر کرد و به احکام و آیینی که برای تو مقرّر داشت خشنود بودی و خدا نیز از تو خشنود بود.'),
(6022, 89, 29, 'تو بدین شیوه مرا بندگی کردی پس در میان بندگان راستین من درآی.'),
(6023, 89, 30, 'و به بهشت من وارد شو.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'ای پیامبر، سوگند یاد می‌کنم به مکّه این شهر شکوهمند،'),
(6025, 90, 2, 'حالی که تو در این شهر جای داری،'),
(6026, 90, 3, 'و سوگند به ابراهیم آن پدر فرزانه و زاده اش اسماعیل آن فرزند مبارک،'),
(6027, 90, 4, 'که ما انسان را آفریده و حیاتش را با رنج و محنت آمیخته‌ایم.'),
(6028, 90, 5, 'آیا او با وجود این که خود را مغلوب ناگواری‌ها می‌بیند، گمان می‌کند که هرگز کسی بر او دست نخواهد یافت.‌'),
(6029, 90, 6, 'می‌گوید: از وقتی که مسلمان شده‌ام مال فراوانی تباه کرده‌ام!'),
(6030, 90, 7, 'آیا می‌پندارد که هیچ کس او را ندیده و از انفاق وی خبر ندارد؟'),
(6031, 90, 8, 'مگر ما برای او دو چشم قرار نداده‌ایم و دیدنی‌ها را به او نشان نداده‌ایم؟ پس چگونه تصوّر می‌کند که ما آنچه را او می‌بیند نمی‌بینیم؟'),
(6032, 90, 9, 'مگر برای او یک زبان و دو لب نیافریده‌ایم که با آنها سخن گوید و آنچه را در دل دارد بیان کند؟ با این حال چگونه می‌پندارد که ما از درون او بی خبریم؟'),
(6033, 90, 10, 'ما راه خیر و شرّ را به او نشان داده‌ایم، چگونه ممکن است خود خیر و شرّ را نشناسیم؟'),
(6034, 90, 11, 'او با این انفاق‌های ناچیز هرگز به مقام خجستگان نخواهد رسید، چرا که هنوز بر آن گردنه صعب العبور گام ننهاده است.'),
(6035, 90, 12, 'و چه چیز تو را آگاه کرده است که آن گردنه صعب العبور چیست؟'),
(6036, 90, 13, 'آن گردنه، آزاد کردن بنده‌ای است،'),
(6037, 90, 14, 'یا در روز قحطی طعام دادن،'),
(6038, 90, 15, 'به یتیمی خویشاوند،'),
(6039, 90, 16, 'یا به تهیدستی خاک نشین که سخت بینواست.'),
(6040, 90, 17, 'او علاوه بر این که بر آن گردنه گام ننهاده است، از کسانی نیست که ایمان آورده و یکدیگر را به صبر در راه خدا سفارش کرده و به بخشش به یتیمان و تهیدستان توصیه نموده‌اند.'),
(6041, 90, 18, 'کسانی که این گونه‌اند خجستگانند.'),
(6042, 90, 19, 'و آنان که به آیات ما که نشانگر یکتایی در ربوبیّت است کافر شده‌اند تیره بختان ناخجسته‌اند.'),
(6043, 90, 20, 'که آتشی سربسته بر آنان احاطه خواهد داشت.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سوگند به خورشید و تابش آن،'),
(6045, 91, 2, 'و سوگند به ماه چون از پی خورشید رَود،'),
(6046, 91, 3, 'و سوگند به روز چون زمین را روشن کند،'),
(6047, 91, 4, 'و سوگند به شب چون زمین را فرا گیرد،'),
(6048, 91, 5, 'و سوگند به آسمان و به آن مقتدری که آن را بنا کرد،'),
(6049, 91, 6, 'و سوگند به زمین و به آن قدرتی که آن را بگسترد،'),
(6050, 91, 7, 'و سوگند به نفس آدمی و آن قدرتمند حکیمی که آن را موزون ساخت و سامان بخشید،'),
(6051, 91, 8, 'و گنهکاری و تقواپیشگی اش را به آن الهام کرد،'),
(6052, 91, 9, 'به همه اینها سوگند که هر کس نفس خود را به وسیله تقوا رشد داد به سعادت رسید.'),
(6053, 91, 10, 'و هر کس آن را با گناه پرورید ناکام ماند.'),
(6054, 91, 11, 'به فرجام ثمودیان بنگرید تا عاقبت گناه را دریابید. قوم ثمود در اثر طغیانشان پیام الهی را دروغ انگاشتند.'),
(6055, 91, 12, 'آن گاه که تیره بخت ترینشان برانگیخته شد تا ناقه الهی را پی کند.'),
(6056, 91, 13, 'پس صالح فرستاده خدا به آنان ابلاغ کرد که ناقه خدا و آب خوردنش را واگذارید و مانع آن نشوید.'),
(6057, 91, 14, 'ولی قومش او را تکذیب کردند و ناقه را کشتند; پس پروردگارشان آنان را به سزای گناهشان به عذابی فرو گرفت و با خاک یکسانشان ساخت.'),
(6058, 91, 15, 'و از فرجام کاری که کرد باکی ندارد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'سوگند به شب آن گاه که روز را بپوشانَد،'),
(6060, 92, 2, 'و سوگند به روز آن گاه که پدیدار گردد،'),
(6061, 92, 3, 'و سوگند به آن قدرتی که نر و ماده را آفرید،'),
(6062, 92, 4, 'که تلاش‌های شما گوناگون است.'),
(6063, 92, 5, 'امّا آن کس که برای رضای خدا از مال خود بخشید و تقوا پیشه کرد،'),
(6064, 92, 6, 'و نیکوترین وعده خدا را در مورد انفاق کنندگان راست شمرد،'),
(6065, 92, 7, 'به زودی او را در اثر کارهای شایسته‌ای که انجام می‌دهد برای ورود به بهشت آماده خواهیم ساخت و به آسایش و زندگی آسوده می‌رسانیم.'),
(6066, 92, 8, 'و امّا آن کس که از بخشش مال خود بخل ورزید و با گردآوری ثروت در پی توانگری بود،'),
(6067, 92, 9, 'و نیکوترین وعده خدا را دروغ شمرد،'),
(6068, 92, 10, 'به زودی او را برای دشواری و عذاب آماده خواهیم ساخت.'),
(6069, 92, 11, 'و آن گاه که بمیرد و به هلاکت افتد، ثروتش برای او کارساز نیست.'),
(6070, 92, 12, 'ما این حقایق را برای راهنمایی مردم بیان می‌کنیم، چرا که راهنمایی بندگان تنها برعهده ماست.'),
(6071, 92, 13, 'و همانا جهان آخرت و سرای نخستین از آنِ ماست.'),
(6072, 92, 14, 'بدین سبب شما را از آتشی که زبانه می‌کشد، بیم دادم.'),
(6073, 92, 15, 'جز آن کسی که تیره بخت‌ترین مردم است، جاودانه در آن درنیاید;'),
(6074, 92, 16, 'همان کسی که حق را تکذیب کرد و از آن روی برتافت.'),
(6075, 92, 17, 'و آن کس که پرواکننده‌ترین مردم است، قطعاً از آن آتش دور داشته می‌شود;'),
(6076, 92, 18, 'همان کسی که دارایی اش را می‌بخشد و به وسیله آن رشدی شایسته می‌جوید.'),
(6077, 92, 19, 'و هدفش از کمک به نیازمندان جبران احسان آنان نیست، زیرا هیچ کس از آنان بر گردن او حق نعمتی ندارد که جبران آن لازم باشد.'),
(6078, 92, 20, 'بلکه انفاق او تنها برای دستیابی به خشنودی پروردگار بلندمرتبه اش هست.'),
(6079, 92, 21, 'و به زودی با دریافت پاداشی نیکو از جانب پروردگارش، خشنود خواهد شد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'سوگند به آن دم که آفتاب بگسترد،'),
(6081, 93, 2, 'و سوگند به شب آن گاه که تاریکی اش فراگیر شود و آرام یابد،'),
(6082, 93, 3, 'که پروردگارت ـ برخلاف آنچه دشمنان می‌گویند ـ نه رهایت کرده و نه تو را دشمن داشته است.'),
(6083, 93, 4, 'تو در این دنیا مورد رحمت مایی، و سرای آخرت قطعاً برای تو بهتر از این سرای خواهد بود.'),
(6084, 93, 5, 'و به زودی پروردگارت در آن سرای [چنان مقامی] به تو عطا کند که خرسند شوی.'),
(6085, 93, 6, 'مگر تو را کودکی یتیم نیافت پس به تو پناه داد؟'),
(6086, 93, 7, 'و تو را راه نایافته دید پس هدایتت کرد.'),
(6087, 93, 8, 'و تو را تهیدست یافت پس توانگرت ساخت.'),
(6088, 93, 9, 'پس تو نیز یتیم را خوار و مغلوب مکن که خود درد یتیمی را احساس کرده‌ای.'),
(6089, 93, 10, 'و سؤال کننده را که از تو رهنمود می‌خواهد یا مالی می‌طلبد، از خود مران و بر او بانگ مزن که خود تلخی تنگدستی را چشیده‌ای و نیاز به رهنمود را دانسته‌ای.'),
(6090, 93, 11, 'تو می‌دانی که همه این نعمت‌ها از جانب خداست، پس نعمت پروردگارت را بازگوی و شکرش را به جای آور (در گفتار و کردارت نشان ده که نعمت‌ها داده خداست).');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'آیا به تو شرح صدر ندادیم تا بتوانی وحی را دریافت کنی و بر گزندها و مشکلات راه رسالت شکیبا باشی؟'),
(6092, 94, 2, 'ما پیام تو را به مردم رساندیم و آنان را به پذیرش آن برانگیختیم و بدین وسیله سنگینی بار رسالت را از دوش تو برداشتیم.'),
(6093, 94, 3, 'آن بار سنگینی که پشت تو را شکسته بود.'),
(6094, 94, 4, 'و آوازه ات را بلند گردانیدیم تا آن جا که نام تو را با نام خود قرین ساختیم.'),
(6095, 94, 5, 'آری، رنج‌ها را از دوش تو برداشتیم، زیرا سنّت ما بر این جاری است که در پی دشواری آسانی باشد.'),
(6096, 94, 6, 'به یقین، در پی دشواری آسانی خواهد بود.'),
(6097, 94, 7, 'اکنون که دانستی سنّت ما چیست، هرگاه از کار رسالت فارغ شدی، به عبادت بکوش و رنج آن را بر خود هموار ساز.'),
(6098, 94, 8, 'و بدین وسیله به پروردگارت رغبت کن تا در پی این رنج، آسایشی نصیب تو سازد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'سوگند به آن کوه انجیر و زیتون (سرزمین دمشق و بیت المقدس) آن جا که خاستگاه پیامبرانی بسیار بود،'),
(6100, 95, 2, 'و سوگند به طور سینا، آن جا که خدا با موسی سخن گفت،'),
(6101, 95, 3, 'و سوگند به این شهر امن (مکّه)،'),
(6102, 95, 4, 'که ما انسان را در نیکوترین قوام آفریدیم تا بتواند در نزد خدایش به نیکبختیِ جاودانه دست یابد.'),
(6103, 95, 5, 'سپس او را چون از مسیر فطرت خارج شد، به جایگاهی پست که از جایگاه همه فرومایگانِ اهل عذاب پست‌تر بود بازگرداندیم.'),
(6104, 95, 6, 'مگر کسانی را که ایمان آورده و کارهای شایسته کرده‌اند، که برای آنان پاداشی بی پایان خواهد بود.'),
(6105, 95, 7, 'اینک ای انسان، پس از دانستن این حقیقت چه چیز تو را وامی دارد که روز جزا را دروغ شمری؟'),
(6106, 95, 8, 'مگر خداوند در داوری اش از همه داوران برتر نیست؟ پس چگونه می‌پنداری که روز جزایی مقرّر نکرده است تا هر کس به سزای کردار خویش رسد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'ای پیامبر، به نام پروردگارت دریافت قرآن را آغاز کن; همان کسی که انسان را آفرید.'),
(6108, 96, 2, 'انسان را از خونی بسته آفرید.'),
(6109, 96, 3, 'آری، دریافت قرآن را آغاز کن، و بدان که پروردگارت از همگان کریم‌تر و عطایش از عطای همه عطاکنندگان برتر است.'),
(6110, 96, 4, 'همو که خواندن و نوشتن را به وسیله قلم به آدمی آموخت.'),
(6111, 96, 5, 'و به انسان آنچه را نمی‌دانست یاد داد.'),
(6112, 96, 6, 'آیا گمان می‌کنی که او شکر این نعمت را به جای می‌آورَد؟ هرگز! انسان مرز خود را نمی‌شناسد و طغیان می‌کند،'),
(6113, 96, 7, 'چرا که خود را از پروردگار خویش بی نیاز می‌بیند.'),
(6114, 96, 8, 'به یقین، بازگشت آدمیان پس از مرگ به سوی پروردگار توست.'),
(6115, 96, 9, 'به من خبر ده، آیا آن کس که باز می‌دارد،'),
(6116, 96, 10, 'بنده‌ای را آن گاه که نماز می‌گزارد، به من خبر ده که کارش به کجا خواهد کشید؟'),
(6117, 96, 11, 'به من خبر ده، اگر آن نمازگزار بر طریق هدایت باشد،'),
(6118, 96, 12, 'یا به پرهیزگاری فرمان دهد، فرجام آن بازدارنده چه می‌شود؟'),
(6119, 96, 13, 'به من خبر ده، اگر آن بازدارنده، حق را دروغ انگارد و از آن روی برتابد، آیا جز در خورِ عذاب خواهد بود؟'),
(6120, 96, 14, 'مگر نمی‌داند که خدا کارهایش را می‌بیند؟'),
(6121, 96, 15, 'او حق ندارد چنین کند! اگر دست از این کار برندارد، به خواری اش می‌افکنیم و موی پیشانی اش را می‌گیریم و به دوزخش می‌کشانیم.'),
(6122, 96, 16, 'موی پیشانی دروغگوی خطاکار را.'),
(6123, 96, 17, 'آن گاه اهل محفل خود را به یاری بخواند.'),
(6124, 96, 18, 'ما نیز به زودی گماشتگان بر آتش [دوزخ] را فرا می‌خوانیم.'),
(6125, 96, 19, 'او حق ندارد تو را از نماز باز دارد! تو از او فرمان مبر و نماز را رها مکن، و سجده کن و به پروردگارت تقرّب جوی.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'ما قرآن را در شب قدر که امور جهان هستی در آن مقدر می‌گردد، بر تو فرو فرستادیم.'),
(6127, 97, 2, 'و چه چیز تو را آگاه کرده است که شب قدر چیست و چه عظمتی دارد؟'),
(6128, 97, 3, 'شب قدر برای عبادت خدا از هزار ماه بهتر است.'),
(6129, 97, 4, 'در آن شب، فرشتگان و روح به اذن پروردگارشان فرود می‌آیند تا هر امری را تدبیر کنند و آن را سامان دهند.'),
(6130, 97, 5, 'آن شب تا دمیدن سپیده برای بندگانی که به خدا روی می‌آورند شب سلامت و دریافت رحمت اوست.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'کسانی از اهل کتاب و مشرکان که به اسلام کافر شدند از سنّت خدا (هدایت آدمیان) جدا نبودند تا این که آن دلیل آشکار برایشان آمد و حجّت بر آنان تمام شد.'),
(6132, 98, 2, 'آن دلیل آشکار، فرستاده‌ای است از جانب خدا که صحیفه‌های پاک قرآنی را بر آنان تلاوت می‌کند.'),
(6133, 98, 3, 'در آن صحیفه‌ها نوشته‌هایی است که احکام و معارف الهی را که برپا دارنده امور جامعه انسانی است بیان می‌کند.'),
(6134, 98, 4, 'و کسانی که شریعت و کتاب آسمانی به آنان داده شد، دچار پراکندگی در دین خود نشدند و به مذاهب گوناگون روی نیاوردند مگر پس از آن که دلیل روشن برایشان آمد.'),
(6135, 98, 5, 'آنان به چیزی جز این فرمان نیافته بودند که خدا را بپرستند و اطاعت و عبادت را ویژه او کنند و بی آن که از راه اعتدال منحرف شوند، در راه او گام بسپرند و نماز را برپا دارند و زکات بپردازند. این است آن دین الهی که در قرآن و دیگر کتاب‌های آسمانی بیان شده و آن است برپادارنده مصالح جامعه انسانی.'),
(6136, 98, 6, 'کافران (اهل کتاب و مشرکان) قطعاً در آتش دوزخ خواهند بود و در آن جا ماندگارند. اینانند که بدترین آفریدگانند.'),
(6137, 98, 7, 'همانا کسانی که ایمان آورده و کارهای شایسته کرده‌اند، آنانند که بهترین آفریدگانند.'),
(6138, 98, 8, 'سزای آنان نزد پروردگارشان بوستان‌های جاوید بهشتی است با درختانی انبوه که از زیر آنها نهرها روان است. در آن جا برای همیشه ماندگارند. خدا از آنان خشنود است و آنان نیز از خدا خشنودند. این پاداش کسی است که از پروردگارش بترسد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'آن گاه که زمین به آن لرزش شدید و هولناکش بلرزد،'),
(6140, 99, 2, 'و زمین مردگان را که بار گرانی برآنند، از دل خود بیرون افکند،'),
(6141, 99, 3, 'و آدمی شگفت زده گوید: زمین را چه شده که این گونه به لرزه درآمده است؟'),
(6142, 99, 4, 'آن روز زمین خبرهای خود را بازگوید و بر اعمال انسان‌ها گواهی دهد;'),
(6143, 99, 5, 'بدان سبب که پروردگارت به آن وحی کرده است که چنین کند.'),
(6144, 99, 6, 'آن روز مردم از جایگاه خود در صحنه قیامت پراکنده و به سوی بهشت یا دوزخ روانه می‌شوند تا سزای کارهایشان به آنان نشان داده شود.'),
(6145, 99, 7, 'پس هر که هموزن ذرّه‌ای نیکی کند، آن را خواهد دید.'),
(6146, 99, 8, 'و هر که هموزن ذرّه‌ای بدی کند، آن را خواهد دید.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'سوگند به آن اسب‌ها که نفس زنان به سوی میدان جهاد تاختند،'),
(6148, 100, 2, 'و سوگند به آنها که با نواختن و تاختن بر سنگلاخ‌ها به وسیله سم هایشان شراره آتش برآوردند،'),
(6149, 100, 3, 'و سوگند به آنها که بامدادان بر دشمن به ناگهان یورش بردند،'),
(6150, 100, 4, 'و با آن یورش غباری برانگیختند،'),
(6151, 100, 5, 'و بدین وسیله میان سپاه دشمن درآمدند،'),
(6152, 100, 6, 'به آنها سوگند که آدمی در برابر نعمت‌های پروردگارش بسیار ناسپاس است.'),
(6153, 100, 7, 'و او قطعاً بر ناسپاسی خود گواه است.'),
(6154, 100, 8, 'و چون شیفته مال است بسیار بخیل است.'),
(6155, 100, 9, 'آیا نمی‌داند که ناسپاسی خدا پیامدی ناگوار دارد؟ آن روز که دفن شدگانِ در گورها برانگیخته می‌شوند.'),
(6156, 100, 10, 'و آنچه در سینه هاست همه فاش می‌گردد.'),
(6157, 100, 11, 'به یقین، آن روز پروردگارشان به آنان آگاه است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'آن در هم کوبنده!'),
(6159, 101, 2, 'چیست آن در هم کوبنده؟'),
(6160, 101, 3, 'و چه چیز تو را آگاه کرده است که آن در هم کوبنده چیست؟'),
(6161, 101, 4, 'روزی پدید خواهد آمد که آدمیان همچون ملخ‌های پراکنده از گورها به هر سو روی می‌آورند.'),
(6162, 101, 5, 'و کوه‌ها مانند پشم رنگارنگ زده شده متلاشی می‌گردند.'),
(6163, 101, 6, '[در آن روز اعمال مردم سنجیده می‌شود،] پس کسی که اعمالش وزین و شایسته است،'),
(6164, 101, 7, 'او در زندگیِ خوش و پسندیده‌ای خواهد بود.'),
(6165, 101, 8, 'و کسی که اعمالش سبک وزن و نارواست،'),
(6166, 101, 9, 'جایگاهش که او را در بر می‌گیرد هاویه است.'),
(6167, 101, 10, 'و چه چیز تو را آگاه کرده است که آن چیست؟'),
(6168, 101, 11, 'آتشی است بس سوزان.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'مسابقه در ثروت اندوزی و جاه طلبی شما را سرگرم کرد و از مقصد بازداشت،'),
(6170, 102, 2, 'تا آن گاه که مرگتان فرا رسید و گورهای خود را دیدید.'),
(6171, 102, 3, 'چنین نکنید! به زودی خواهید دانست که فرجام شوم این سرگرمی چیست.'),
(6172, 102, 4, 'آری، چنین نکنید! به زودی خواهید دانست که فرجام شوم این سرگرمی چیست.'),
(6173, 102, 5, 'هرگز چنین نکنید، که اگر به علم یقینی دست بیابید،'),
(6174, 102, 6, 'بی گمان دوزخ را در این جهان به چشم دل خواهید دید.'),
(6175, 102, 7, 'سپس آن را قطعاً روز قیامت به مشاهده یقینی خواهید دید.'),
(6176, 102, 8, 'سپس آن روز که دوزخ را بنگرید، درباره هر نعمتی که به شما ارزانی شده است از شما سؤال خواهد شد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'سوگند به عصر پیامبر، عصر پیدایش اسلام،'),
(6178, 103, 2, 'که انسان در زیانکاری و سرمایه عمرش رو به تباهی است;'),
(6179, 103, 3, 'مگر کسانی که به خدا و پیامبران و روز قیامت ایمان آورده و کارهای شایسته انجام داده و یکدیگر را به پیروی از حق سفارش کرده و به شکیبایی در اطاعت از خدا و دوری جستن از گناهان و تحمّل ناگواری‌ها توصیه نموده‌اند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'وای بر هر عیبجوی غیبت کننده‌ای،'),
(6181, 104, 2, 'که مالی فراهم آورده و آن را پی در پی شمرده است;'),
(6182, 104, 3, 'چون می‌پندارد که ثروتش مرگ را از او دور کرده و جاودانه اش ساخته است.'),
(6183, 104, 4, 'چنین نیست، مرگ او فرا خواهد رسید و قطعاً در آن آتش شکننده افکنده خواهد شد.'),
(6184, 104, 5, 'و چه چیزی تو را آگاه کرده است که آن آتش شکننده چیست؟'),
(6185, 104, 6, 'آتشِ برافروخته خداست،'),
(6186, 104, 7, 'که بر دل‌ها چیره می‌شود و جان آدمیان را می‌سوزاند.'),
(6187, 104, 8, 'آن آتش بر آنان در بسته خواهد بود و راه خلاصی از آن نخواهند داشت.'),
(6188, 104, 9, 'این در حالی است که آنان در ستون‌هایی بسیار دراز به بند کشیده شده‌اند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'مگر ندانسته‌ای که پروردگارت با آن فیل سواران که برای ویران ساختن کعبه لشکرکشی نمودند چه کرد؟'),
(6190, 105, 2, 'آیا ترفندشان را در بیراهه قرار نداد و بی اثر نساخت؟'),
(6191, 105, 3, 'و بر بالای سرشان فوج فوج پرندگانی فرستاد.'),
(6192, 105, 4, 'پرندگانی که بر آنان سنگ‌هایی از سجّیل می‌افکندند.'),
(6193, 105, 5, 'بدین گونه، خداوند جانشان را گرفت و آنان را همچون پوسته‌ای ساخت که مغزش خورده شده باشد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'از آن روی که خداوند قریش را با کوچ نمودن مأنوس ساخت،'),
(6195, 106, 2, 'آنان را با کوچ زمستانی و تابستانی مأنوس ساخت تا با امنیّت سفر کنند و معیشت خود را فراهم آورند،'),
(6196, 106, 3, 'به شکرانه اش باید خداوندگار این خانه (کعبه) را بپرستند;'),
(6197, 106, 4, 'همان کسی که آنان را در سرزمینی که اسباب معیشت فراهم نبود، از گرسنگی رهایی بخشید و طعامشان داد، و آنان را از ترس و دلهره ایمن ساخت.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'آیا دیدی آن کسی را که جزا و پاداش را دروغ می‌انگارد و رستاخیز را باور نمی‌کند؟'),
(6199, 107, 2, 'او همان کسی است که یتیم را به شدت و ستم از خود می‌راند،'),
(6200, 107, 3, 'و دیگران را بر اطعام بینوا ترغیب نمی‌کند.'),
(6201, 107, 4, 'با این حال تظاهر به ایمان می‌کند و نماز می‌گزارد. پس وای بر این نمازگزاران!'),
(6202, 107, 5, 'که از نمازشان غافلند و بدان اهتمام نمیورزند.'),
(6203, 107, 6, 'همانان که در عبادت هایشان ریا می‌کنند.'),
(6204, 107, 7, 'و از اموال خویش آنچه نیاز دیگران را برمی آورند دریغ میورزند.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'همانا ما به تو خیر فراوان دادیم (مقرر داشتیم که نسل تو فراوان گردد).'),
(6206, 108, 2, 'پس به شکرانه آن برای پروردگارت نماز بگزار و هنگام تکبیر، دست هایت را تا مقابل گلو بالا بر.'),
(6207, 108, 3, 'قطعاً نسل تو منقطع نمی‌شود، بلکه دشمن توست که بی نسل است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'بگو: ای کافران قریش، پیشنهاد شما که من بت هایتان را بپرستم تا شما هم خدای مرا بپرستید، هرگز قابل قبول نیست.'),
(6209, 109, 2, 'نه من آنچه را شما می‌پرستید خواهم پرستید،'),
(6210, 109, 3, 'و نه شما پرستنده کسی هستید که من او را می‌پرستم،'),
(6211, 109, 4, 'و نه من پرستنده چیزی هستم که شما آن را می‌پرستید،'),
(6212, 109, 5, 'و نه شما پرستنده کسی هستید که من او را می‌پرستم.'),
(6213, 109, 6, 'دین شما برای خودتان، و دین من هم برای خودم.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'هنگامی که یاری خدا و فتح مکّه فرارسد و مشرکان قریش مغلوب شوند،'),
(6215, 110, 2, 'و ببینی که مردم گروه گروه به دین خدا درمی آیند،'),
(6216, 110, 3, 'پروردگارت را همراه با ستایش او تسبیح گوی و از وی آمرزش بخواه که او بسیار توبه پذیر است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'دو دست ابولهب ناکارآمد باد، و تلاش هایش بر ضدّ پیامبر بی اثر، و خودش نیز برای همیشه تباه و از سعادت محروم باد!'),
(6218, 111, 2, 'دارایی و دستاوردش او را از تباهی دور نساخت.'),
(6219, 111, 3, 'به زودی به آتشی زبانه دار درمی آید و برای همیشه در آن ماندگار خواهد بود.'),
(6220, 111, 4, 'و همسرش نیز به آن آتش درخواهد آمد در حالی که هیزم کش دوزخ است.'),
(6221, 111, 5, 'و بر گردنش طنابی از پوسته درخت است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'بگو: حقیقت این است که خدا یگانه است.'),
(6223, 112, 2, 'خداست که هر چیزی به او نیازمند است و خود از همه چیز بی نیاز است.'),
(6224, 112, 3, 'از این روی نه فرزندی آورده و نه زاده کسی است.'),
(6225, 112, 4, 'و هیچ کس همتا و همانند او نبوده است.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'بگو: پناه می‌برم به خداوندگارِ سپیده دم که تاریکی را می‌شکافد،'),
(6227, 113, 2, 'از شرِّ هر صاحب شرّی که آفریده است،'),
(6228, 113, 3, 'و از شرِّ شب آن گاه که با تاریکی اش درآید،'),
(6229, 113, 4, 'و از شرِّ زنان جادوگر که هنگام افسون کردن مردمان، در گره‌های جادو سخت می‌دمند،'),
(6230, 113, 5, 'و از شرّ هر حسود آن گاه که حسد ورزد.');
INSERT INTO `fa_safavi` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'بگو: پناه می‌برم به پروردگار مردم،'),
(6232, 114, 2, 'پادشاه مردم،'),
(6233, 114, 3, 'معبود به حق مردم،'),
(6234, 114, 4, 'از شرِّ آن وسوسه گری که چون خدا از یاد رود بازآید و وسوسه کند، و چون خدا یاد شود باز رود و نهان گردد.'),
(6235, 114, 5, 'همان که در سینه‌های مردم وسوسه می‌کند،'),
(6236, 114, 6, 'چه از جنّیان و چه از آدمیان.');
