INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'In de naam van God, de erbarmer, de barmhartige.'),
(2, 1, 2, 'Lof zij God, de Heer van de wereldbewoners,'),
(3, 1, 3, 'de erbarmer, de barmhartige,'),
(4, 1, 4, 'de heerser op de oordeelsdag.'),
(5, 1, 5, 'U dienen wij en U vragen wij om hulp.'),
(6, 1, 6, 'Leid ons op de juiste weg,'),
(7, 1, 7, 'de weg van hen aan wie U genade geschonken hebt, op wie geen toorn rust en die niet dwalen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'A[lif] Lfaam] M[iem].'),
(9, 2, 2, 'Dat is het boek, waaraan geen twijfel is, een leidraad voor de godvrezenden,'),
(10, 2, 3, 'die geloven in het verborgene, de salaat verrichten en bijdragen geven van wat Wij hun voor hun levensonderhoud gegeven hebben'),
(11, 2, 4, 'en die geloven in wat naar jou neergezonden is en in wat voor jouw tijd is neergezonden; van het hiernamaals zijn zij vast overtuigd.'),
(12, 2, 5, 'Zij volgen de leidraad van hun Heer en zij zijn het die het welgaat.'),
(13, 2, 6, 'Zij die ongelovig zijn, voor hen maakt het niet uit of je hen waarschuwt of niet; zij geloven niet.'),
(14, 2, 7, 'God heeft hun harten en hun gehoor verzegeld en over hun ogen is een sluier; voor hen is er een geweldige bestraffing.'),
(15, 2, 8, 'Er zijn mensen die zeggen: "Wij geloven in God en de laatste dag", maar zij zijn geen gelovigen.'),
(16, 2, 9, 'Zij denken God en hen die geloven te misleiden, maar zij misleiden slechts zichzelf zonder het te beseffen.'),
(17, 2, 10, 'In hun harten is een ziekte en God heeft hun ziekte verergerd; voor de leugens die zij spraken krijgen zij een pijnlijke bestraffing.'),
(18, 2, 11, 'Als men tot hen zegt: "Brengt geen verderf op de aarde", zeggen zij: "Wij scheppen slechts orde."'),
(19, 2, 12, 'Toch zijn zij het die verderf brengen, maar zij beseffen het niet.'),
(20, 2, 13, 'Als men tot hen zegt: "Gelooft zoals de mensen geloven", zeggen zij: "Zullen wij dan geloven zoals de dwazen geloven?" Toch zijn zij de dwazen, maar zij weten het niet.'),
(21, 2, 14, 'Als zij hen die geloven ontmoeten zeggen zij: "Wij geloven", maar als zij met hun satanische makkers alleen zijn zeggen zij: "Wij horen bij jullie. Wij waren enkel aan het spotten."'),
(22, 2, 15, 'God drijft de spot met hen en laat hen in hun onbeschaamdheid doorgaan met dwalen.'),
(23, 2, 16, 'Zij zijn het die de dwaalweg ten koste van de leidraad gekocht hebben, maar hun handel zal hun niets opleveren en zij volgen het goede pad niet.'),
(24, 2, 17, 'Zij lijken bijvoorbeeld op iemand die een vuur aangestoken heeft. Als het dan alles rondom hem verlicht, neemt God hun licht weg en laat hen achter in de duisternis, zodat zij niets zien.'),
(25, 2, 18, 'Doof, stom en blind zijn zij. Zij keren dan ook niet terug.'),
(26, 2, 19, 'Of als een wolkbreuk uit de hemel waarin duisternis, donder en bliksem is. Uit doodsangst steken zij voor de donderslagen hun vingers in de oren, maar God heeft de ongelovigen omsingeld.'),
(27, 2, 20, 'Bijna ontneemt de bliksem hun het gezichtsvermogen. Telkens als hij hun licht geeft lopen zij erin, maar als het weer donker is, staan zij stil. Als God het wilde ontnam Hij hun gehoor en gezichtsvermogen. God is almachtig.'),
(28, 2, 21, 'O mensen, dient jullie Heer die jullie en hen die er voor jullie waren geschapen heeft -- misschien zullen jullie godvrezend worden --'),
(29, 2, 22, 'die voor jullie de aarde tot een rustbed heeft gemaakt en de hemel tot een gebouw en die uit de hemel water heeft laten neerdalen en daarmee vruchten heeft voortgebracht voor jullie levensonderhoud. Schrijft aan God geen gelijken toe, terwijl jullie beter weten.'),
(30, 2, 23, 'Als jullie in twijfel verkeren over wat Wij tot Onze dienaar hebben neergezonden, komt dan met een overeenkomstige soera en roept buiten God om jullie getuigen op, als jullie gelijk hebben.'),
(31, 2, 24, 'Als jullie dat niet doen -- en jullie zullen het niet doen -- vreest dan het vuur waarvan mensen en stenen de brandstof zijn en dat voor de ongelovigen is klaargemaakt.'),
(32, 2, 25, 'Verkondig het goede nieuws aan hen die geloven en de deugdelijke daden doen dat er voor hen tuinen zijn, waar de rivieren onderdoor stromen. Telkens als zij daaruit met vruchten gevoed worden zeggen zij: "Dit kregen wij vroeger ook al als levensonderhoud." Het wordt hun zo gegeven dat het er precies op lijkt. Voor hen zijn er daar reingemaakte echtgenotes en zij zullen daar altijd blijven.'),
(33, 2, 26, 'God schaamt zich niet een voorbeeld te geven met behulp van een mug of wat daarbovenuit gaat. Zij die geloven weten, dat het de waarheid is die van hun Heer komt. Zij die ongelovig zijn zeggen echter: "Wat bedoelt God toch met een dergelijk voorbeeld?" Hij misleidt daarmee velen en Hij wijst daarmee velen de goede richting. Hij misleidt slechts de verdorvenen,'),
(34, 2, 27, 'die de verbintenis met God, nadat zij is overeengekomen, verbreken, wat God geboden heeft samen te voegen scheiden en verderf op de aarde brengen. Dat zijn zij, de verliezers.'),
(35, 2, 28, 'Hoe zouden jullie niet in God geloven? Jullie waren toch dood en Hij heeft jullie leven gegeven. Hij zal jullie laten sterven en vervolgens weer tot leven brengen, waarop jullie tot Hem terug zullen keren.'),
(36, 2, 29, 'Hij is het die voor jullie al wat er op de aarde is geschapen heeft. Toen wendde Hij zich tot de hemel en fatsoeneerde hem tot zeven hemelen. Hij is alwetend.'),
(37, 2, 30, 'Toen jouw Heer tot de engelen zei: "Ik ga op aarde een plaatsvervanger aanstellen", zeiden zij: "Gaat U daar iemand aanstellen die er verderf brengt en bloed vergiet, terwijl wij U lofprijzen en Uw heiligheid eren?" Hij zei: "Ik weet wat jullie niet weten."'),
(38, 2, 31, 'En Hij onderwees Adam alle namen. Toen legde Hij hen aan de engelen voor en zei: "Deelt mij de namen van dezen maar mee, als jullie gelijk hebben."'),
(39, 2, 32, 'Zij zeiden: "U zij geprezen! Wij weten slechts wat U ons hebt onderwezen. U bent de wetende, de wijze'),
(40, 2, 33, 'Hij zei: "Adam, deel hun hun namen mee." Toen hij hun dan hun namen meedeelde zei Hij: "Had Ik jullie niet gezegd dat Ik het onzichtbare van de hemelen en de aarde ken en weet, wat jullie openlijk en in het verborgene doen."'),
(41, 2, 34, 'Toen Wij tot de engelen zeiden: "Buigt eerbiedig neer voor Adam", bogen zij zich eerbiedig neer, behalve Iblies; hij weigerde en was hoogmoedig, want hij was een van de ongelovigen.'),
(42, 2, 35, 'En Wij zeiden: "Adam, jij en je echtgenote mogen de tuin bewonen en jullie mogen ervan in overvloed eten waar jullie maar willen, maar jullie mogen deze boom niet benaderen, want dan behoren jullie tot de onrechtplegers."'),
(43, 2, 36, 'Toen liet de satan hen daaruit afdwalen. Zo bracht hij hen uit de plaats waarin zij waren naar buiten en Wij zeiden: "Daalt af, elkaar tot vijand; en tijdelijk is er voor jullie op de aarde een verblijfplaats en vruchtgebruik."'),
(44, 2, 37, 'Toen nam Adam van zijn Heer woorden in ontvangst en Hij wendde zich genadig tot hem; Hij is de genadegever, de barmhartige.'),
(45, 2, 38, 'Wij zeiden: "Daalt allen eruit af. Als er dan van Mij een leidraad tot jullie komt, dan hebben zij die Mijn leidraad volgen niets te vrezen, noch zullen zij bedroefd zijn.'),
(46, 2, 39, 'Maar zij die ongelovig zijn en Onze tekenen loochenen, zij zijn het die in het vuur thuishoren, zij zullen daarin altijd blijven."'),
(47, 2, 40, 'O Israëlieten, denkt aan Mijn genade die Ik jullie geschonken heb en komt het verbond met Mij na, dan zal Ik het verbond met jullie nakomen; voor Mij moeten jullie dus beducht zijn.'),
(48, 2, 41, 'En gelooft in wat Ik heb neergezonden ter bevestiging van wat jullie al hebben, weest niet de eersten die er niet in geloven en verkwanselt Mijn tekenen niet; Mij moeten jullie dus vrezen.'),
(49, 2, 42, 'Omkleedt de waarheid niet met onzin en verbergt haar niet; jullie weten wel beter.'),
(50, 2, 43, 'Verricht de salaat en geeft de zakaat en buigt met de buigenden. *'),
(51, 2, 44, 'Kunnen jullie dan de mensen aansporen tot vroomheid en jezelf vergeten? Jullie lezen het boek toch voor? Hebben jullie dan geen verstand?'),
(52, 2, 45, 'Neemt jullie toevlucht tot geduld en de salaat. Dat is veel, maar niet voor de deemoedigen,'),
(53, 2, 46, 'die menen dat zij hun Heer ontmoeten en dat zij tot Hem terugkeren.'),
(54, 2, 47, 'O Israëlieten, denkt aan Mijn genade die Ik jullie geschonken heb en daaraan dat Ik jullie boven de wereldbewoners heb verkozen.'),
(55, 2, 48, 'En vreest een dag waarop niemand iets van een ander op zich kan nemen, waarop voor hem geen voorspraak aanvaard, noch losprijs aangenomen wordt en waarop zij geen hulp zullen krijgen.'),
(56, 2, 49, 'Toen Wij jullie van Fir\'auns mensen redden die jullie een vreselijk leed berokkenden doordat zij jullie zonen afslachtten en alleen jullie vrouwen in leven lieten. Daarin was een geweldige beproeving van jullie Heer.'),
(57, 2, 50, 'En toen Wij voor jullie de zee spleten en jullie zo redden, maar Fir\'auns mensen lieten verdrinken, terwijl jullie toekeken.'),
(58, 2, 51, 'En toen wij met Moesa veertig nachten afgesproken hadden. Jullie namen toen in onrechtmatigheid het kalf aan, nadat hij weg was gegaan.'),
(59, 2, 52, 'Daarop vergaven Wij jullie nadat dat gebeurd was; misschien zullen jullie dank betuigen.'),
(60, 2, 53, 'En toen Wij Moesa het boek en het reddend onderscheidingsmiddel gaven. Misschien zullen jullie je de goede richting laten wijzen.'),
(61, 2, 54, 'En toen Moesa tot zijn volk zei: "Mijn volk, jullie hebben jezelf onrecht aangedaan doordat jullie het kalf hebben aangenomen. Wendt jullie berouwvol tot jullie maker en doodt dan jezelf. Dat is beter voor jullie bij jullie maker. Dan zal Hij zich genadig tot jullie wenden; Hij is de genadegever, de barmhartige."'),
(62, 2, 55, 'En toen jullie zeiden: "Moesa wij zullen jou niet geloven totdat wij God in alle openheid zien", waarop de donderslag jullie greep, terwijl jullie toekeken.'),
(63, 2, 56, 'Toen lieten Wij na jullie dood jullie weer opstaan; misschien zullen jullie dank betuigen.'),
(64, 2, 57, 'Toen lieten Wij jullie door de wolken overschaduwen en zonden Wij het manna en de kwartels op jullie neer: "Eet van de goede dingen die Wij jullie voor jullie levensonderhoud gegeven hebben." Zij deden Ons geen onrecht aan, maar zij deden zichzelf onrecht aan.'),
(65, 2, 58, 'En toen Wij zeiden: "Gaat deze stad binnen en jullie mogen ervan in overvloed eten waar jullie maar willen. Gaat eerbiedig neerbuigend de poort binnen en zegt: \'Vergiffenis!?, dan zullen Wij jullie je fouten vergeven en Wij zullen hun die goed doen nog meer doen toekomen."'),
(66, 2, 59, 'Maar zij die onrecht pleegden verwisselden het gezegde met iets anders dan wat tot hen gezegd was. Toen lieten Wij op hen die onrecht pleegden een plaag uit de hemel neerkomen voor het schandelijke dat zij deden.'),
(67, 2, 60, 'En toen Moesa voor zijn volk om water vroeg en Wij zeiden: "Sla met je stok op de rots", toen ontsproten daaruit twaalf bronnen, waarvan elke groep mensen wist waar ze moesten drinken. Eet en drinkt van de levensbehoeften die God schenkt en veroorzaakt geen ellende door op de aarde verderf te zaaien.'),
(68, 2, 61, 'En toen jullie zeiden: "Moesa, wij kunnen het met één soort voedsel niet uithouden. Bid voor ons tot jouw Heer dat Hij ons het een en ander levert van wat de aarde voortbrengt: groente, komkommers, knoflook, linzen en uien." Hij zei: "Willen jullie iets minderwaardigs hebben in plaats van iets beters? Gaat naar Egypte. Daar is wat jullie vragen!" Zij werden met vernedering en onderwerping getroffen en haalden zich Gods toorn op de hals. Dat was omdat zij ongelovig waren aan Gods tekenen en de profeten zonder enig recht doodden. Dat was omdat zij opstandig en vijandig bleven.'),
(69, 2, 62, 'Zij die geloven, zij die het jodendom aanhangen, de christenen en de Sabiërs die in God en de laatste dag geloven en die deugdelijk handelen, voor hen is hun loon bij hun Heer en zij hebben niets te vrezen noch zullen zij bedroefd zijn.'),
(70, 2, 63, 'En toen Wij het verdrag met jullie aangingen en de Berg boven jullie verhieven: "Houdt aan wat Wij jullie gegeven hebben stevig vast en gedenkt wat erin staat; misschien zullen jullie godvrezend worden."'),
(71, 2, 64, 'Toen keerden jullie je daarna af en zonder Gods goedgunstigheid jegens jullie en Zijn barmhartigheid zouden jullie tot de verliezers behoren.'),
(72, 2, 65, 'Jullie hebben toch weet van hen die bij jullie op de sabbat in overtreding kwamen. Wij zeiden immers tegen hen: "Weest weggejaagde apen."'),
(73, 2, 66, 'Zo maakten Wij het tot een afschrikwekkend voorbeeld voor hun eigen tijd en later en tot een aansporing voor de godvrezenden.'),
(74, 2, 67, 'En toen Moesa tot zijn volk zei: "God beveelt jullie een koe te slachten" zeiden zij: "Drijf jij de spot met ons?" Hij zei: "God bescherme mij ervoor dat ik zo dom zou zijn."'),
(75, 2, 68, 'Zij zeiden: "Bid voor ons tot jouw Heer dat Hij ons duidelijk maakt wat zij moet zijn." Hij zei: "Dit zegt Hij, dat zij niet een te oude koe moet zijn en ook niet een die nog niet gekalfd heeft: ertussenin. Doet dus wat jullie bevolen is."'),
(76, 2, 69, 'Zij zeiden: "Bid voor ons tot jouw Heer dat Hij ons duidelijk maakt wat haar kleur moet zijn." Hij zei: "Dit zegt Hij, dat zij een gele koe moet zijn, van een heldere kleur, die de kijkers verheugt."'),
(77, 2, 70, 'Zij zeiden: "Bid voor ons tot jouw Heer dat Hij ons duidelijk maakt wat zij moet zijn -- voor ons lijken de koeien namelijk op elkaar -- dan zal ons, als God het wil, de goede richting gewezen zijn."'),
(78, 2, 71, 'Hij zei: "Dit zegt Hij: zij moet geen gedweeë koe zijn die de aarde omploegt en die de akker bevloeit; een gave zonder vlek." Zij zeiden: "Nu ben jij met de waarheid gekomen." Daarop slachtten zij haar, maar bijna hadden zij het niet gedaan.'),
(79, 2, 72, 'En toen jullie een mens doodden en probeerden elkaar de schuld ervan te geven. God brengt echter tevoorschijn wat jullie verborgen hielden.'),
(80, 2, 73, 'Wij zeiden toen: "Sla hem met een stuk ervan." Zo maakt God de doden weer levend en laat Hij jullie Zijn tekenen zien. Misschien zullen jullie verstandig worden.'),
(81, 2, 74, 'Toen verhardden daarna jullie harten zich en zij waren als steen of nog harder. Er bestaan immers stenen waaruit rivieren ontspringen, er bestaan er die splijten zodat er water uit komt en er bestaan er die neervallen uit vrees voor God. God let goed op wat jullie doen.'),
(82, 2, 75, 'Begeren jullie dan dat zij geloof aan jullie hechten? En dat terwijl er onder hen een groep is die Gods woord gehoord had. Toen verdraaiden zij het, nadat zij het begrepen hadden en terwijl zij beter wisten.'),
(83, 2, 76, 'Wanneer zij hen die geloven ontmoeten, zeggen zij: "Wij geloven", maar wanneer zij onder elkaar zijn zeggen zij: "Zullen jullie hun vertellen wat God aan jullie openbaar gemaakt heeft, zodat zij het bij hun Heer als argument tegen jullie kunnen gebruiken? Hebben jullie dan geen verstand?"'),
(84, 2, 77, 'Of weten zij niet dat God weet wat zij in het geheim en wat zij openlijk doen?'),
(85, 2, 78, 'Onder hen zijn er ongeletterden die het boek op verzinsels na niet kennen en die alleen maar vermoedens hebben.'),
(86, 2, 79, 'Wee hen die het boek eigenhandig schrijven en dan zeggen: "Dit komt van God" om het voor een lage prijs te versjacheren. Wee hen dus om wat hun handen hebben geschreven en wee hen om wat zij eraan hebben verdiend.'),
(87, 2, 80, 'En zij zeggen: "Het vuur zal ons slechts een beperkt aantal dagen treffen." Zeg: "Hebben jullie met God een verbond gesloten -- dan zal God Zijn verbond niet verbreken -- of zeggen jullie over God iets wat jullie niet weten?"'),
(88, 2, 81, 'Ja zeker! Wie slechtheid begaat en wie door zijn fout omgeven wordt, zij zijn het dus die in het vuur thuishoren; zij zullen daarin altijd blijven.'),
(89, 2, 82, 'Maar zij die geloven en de deugdelijke daden doen, zij zijn het die in de tuin thuishoren; zij zullen daarin altijd blijven.'),
(90, 2, 83, 'En toen Wij het verdrag met de Israëlieten aangingen: "Jullie zult alleen God dienen. Weest goed voor de ouders, de verwant, de wezen en de behoeftigen. Spreekt tot de mensen ten goede. Verricht de salaat en geeft de zakaat." Toen onttrokken jullie je er in jullie afkerigheid aan, op enkelen na.'),
(91, 2, 84, 'En toen Wij het verdrag met jullie aangingen: "Jullie zult elkaars bloed niet vergieten en elkaar niet uit jullie woningen verdrijven." Toen bevestigden jullie het met jullie getuigenis.'),
(92, 2, 85, 'Daarop waren jullie het die elkaar doodden en die een groep van jullie uit hun woningen verdreven terwijl jullie elkaar bijstonden in vergrijp en overtreding. Maar indien zij als gevangenen tot jullie komen, kopen jullie hen wel vrij. Toch is het verboden voor jullie om hen te verdrijven. Of geloven jullie in een gedeelte van het boek en in een ander gedeelte niet? Wie van jullie dat doet verdient niet anders dan schande in het tegenwoordige leven en op de opstandingsdag zullen zij tot de zwaarste bestraffing veroordeeld worden. God let goed op wat jullie doen.'),
(93, 2, 86, 'Zij zijn het die het tegenwoordige leven ten koste van het latere gekocht hebben. Dus zal voor hen de bestraffing niet verlicht worden, noch zullen zij hulp krijgen.'),
(94, 2, 87, 'Wij hebben aan Moesa het boek gegeven en Wij hebben na hem de gezanten laten navolgen en Wij hebben \'Isa, de zoon van Marjam de duidelijke bewijzen gegeven en hem gesterkt met de heilige geest. Maar telkens als er een gezant tot jullie komt met iets wat jullie niet zint, zijn jullie dan niet hoogmoedig? Dan betichten jullie sommigen van leugens en anderen doden jullie.'),
(95, 2, 88, 'En zij zeggen: "Onze harten zijn onbesneden." Welnee, God heeft hen om hun ongeloof vervloekt. Hoe weinig is het dus wat zij geloven.'),
(96, 2, 89, 'En toen tot hen een boek van God kwam dat bevestigde wat zij hadden -- voordien hadden zij om bijstand gevraagd tegen hen die ongelovig zijn -- toen dan dat wat zij al wisten tot hen kwam, geloofden zij er niet aan. Gods vloek kome dus over de ongelovigen.'),
(97, 2, 90, 'Waarvoor zij zich verkocht hebben dat is pas slecht; dat zij niet geloven aan wat God neergezonden heeft, uit nijd dat God iets van Zijn genade neerzendt op wie van Zijn dienaren Hij wil. Het levert hun dus toorn op toorn op. En voor de ongelovigen is er een vernederende bestraffing.'),
(98, 2, 91, 'En als er tot hen gezegd wordt: "Gelooft in wat God heeft neergezonden" zeggen zij: "Wij geloven in wat tot ons is neergezonden." En zij geloven niet in wat erna gekomen is, terwijl dat juist de waarheid is, ter bevestiging van wat zij hebben. Zeg: "Waarom hebben jullie dan vroeger Gods profeten gedood, als jullie gelovig zijn?" *'),
(99, 2, 92, 'Moesa was toch tot jullie gekomen met de duidelijke bewijzen. Toen namen jullie in onrechtmatigheid het kalf aan, nadat hij weg was gegaan.'),
(100, 2, 93, 'En toen Wij het verdrag met jullie aangingen en de Berg boven jullie verhieven: "Houdt aan wat Wij jullie gegeven hebben stevig vast en hoort", zeiden zij: "Wij hebben gehoord en zijn opstandig geworden." En in hun harten waren zij door hun ongeloof doordrenkt van het kalf. Zeg: "Het is echt slecht wat jullie geloof jullie beveelt, als jullie gelovig zijn."'),
(101, 2, 94, 'Zeg: "Als de latere woning bij God alleen voor jullie en niet voor de andere mensen is, wenst dan de dood, als jullie gelijk hebt."'),
(102, 2, 95, 'Maar dat zullen zij nooit wensen wegens wat hun handen eerder gedaan hebben. God kent de onrechtplegers.'),
(103, 2, 96, 'En jij zult merken dat van de mensen zij het leven het meest begeren, meer nog dan de aanhangers van het veelgodendom. Eén van hen zou wel duizend jaar willen leven, maar door lang te leven wordt hij van de bestraffing niet gevrijwaard. God doorziet wel wat zij doen.'),
(104, 2, 97, 'Zeg: "Als iemand een vijand van Djibriel is -- hij heeft het toch met Gods toestemming in jouw hart neergezonden als bevestiging van wat er voordien al was en als leidraad en goed nieuws voor de gelovigen --,'),
(105, 2, 98, 'als iemand een vijand van God, Zijn engelen, Zijn gezanten, Djibriel en Mikaal is? God is ook een vijand van de ongelovigen."'),
(106, 2, 99, 'Wij hebben toch duidelijke tekenen naar jou neergezonden. Alleen de verdorvenen geloven er niet in.'),
(107, 2, 100, 'Heeft dan niet telkens als zij een verbintenis aangingen een groep van hen het veronachtzaamd. Jazelfs, de meesten van hen geloven niet.'),
(108, 2, 101, 'En toen tot hen een gezant van God kwam die bevestigde wat zij hadden, veronachtzaamde een groep van hen aan wie het boek gegeven was het boek Gods achterbaks alsof zij van niets wisten.'),
(109, 2, 102, 'En zij volgden wat de satanische makkers over de heerschappij van Soelaimaan voorlazen en Soelaimaan was niet ongelovig, maar de satanische makkers wel, doordat zij de mensen onderwijs gaven in toverij en in wat neergezonden was op de twee engelen in Babel, Haroet en Maroet. Zij onderwijzen niemand zonder te zeggen: "Wij zijn slechts een verzoeking. Word niet ongelovig." Zo leerden zij van die twee hoe verschil te maken tussen een man en zijn echtgenote. Toch berokkenen zij daarmee niemand schade zonder Gods toestemming. Zij leren wat hen schaadt en niet nut, terwijl ze wel weten dat wie zoiets koopt geen aandeel aan het hiernamaals heeft. Echt slecht is het waarvoor zij zich verkocht hebben. Als zij het maar wisten.'),
(110, 2, 103, 'Als zij gelovig en godvrezend zouden zijn dan was een beloning van de kant van God beter. Als zij het maar wisten.'),
(111, 2, 104, 'Jullie die geloven! Zegt niet: "Eerbiedig ons", maar zegt: "Let op ons en hoort." En voor de ongelovigen is er een pijnlijke bestraffing.'),
(112, 2, 105, 'Die mensen van het boek die ongelovig zijn, en de veelgodendienaars willen liever niet dat op jullie iets goeds van jullie Heer wordt neergezonden. Maar God bevoorrecht door Zijn barmhartigheid wie Hij wil. God is vol van geweldige goedgunstigheid.'),
(113, 2, 106, 'Welk teken Wij ook afschaffen of doen vergeten, Wij komen met iets beters of overeenkomstigs. Weet jij niet dat God almachtig is?'),
(114, 2, 107, 'Weet jij niet dat God de heerschappij over de hemelen en de aarde heeft? Buiten God hebben jullie geen enkele beschermer noch bondgenoot.'),
(115, 2, 108, 'Of willen jullie je gezant ondervragen zoals men vroeger Moesa ondervroeg? Wie geloof voor ongeloof inruilt die dwaalt af van de correcte weg.'),
(116, 2, 109, 'Veel van de mensen van het boek zouden, nadat jullie tot geloof gekomen zijn, jullie graag weer ongelovig willen maken, omdat ze afgunstig geworden zijn, nadat voor hen de waarheid duidelijk was geworden. Rekent het maar niet aan en scheldt het kwijt totdat God met Zijn beschikking komt. God is almachtig.'),
(117, 2, 110, 'Verricht de salaat en geeft de zakaat. En wat jullie vooraf voor jullie zelf aan goeds gedaan hebben, dat zullen jullie bij God vinden. Wat jullie doen doorziet God wel.'),
(118, 2, 111, 'En zij zeiden: "Alleen de joden en de christenen zullen de tuin binnengaan." Dat zijn hun wensen! Zeg: "Levert jullie bewijs maar, als jullie gelijk hebben."'),
(119, 2, 112, 'Welnee! Wie zich geheel aan God overgeeft en goed doet, die ontvangt zijn beloning bij zijn Heer. Zij hebben niets te vrezen noch zullen zij bedroefd zijn.'),
(120, 2, 113, 'De joden zeggen: "De christenen baseren zich op niets" en de christenen zeggen: "De joden baseren zich op niets." En toch lezen zij het boek voor. Zo zeggen ook zij die niet weten hetzelfde als zij. God zal dus op de opstandingsdag tussen hen oordelen over dat waarover zij het oneens waren.'),
(121, 2, 114, 'En wie zijn er zondiger dan zij die verhinderen dat in de bedehuizen Gods naam wordt vermeld en die proberen ze te verwoesten. Hun betaamde het slechts er bang binnen te gaan. Voor hen is er in het tegenwoordige leven schande en in het hiernamaals een geweldige bestraffing.'),
(122, 2, 115, 'Van God is het oosten en het westen. Waarheen jullie je dus ook wenden daar is Gods aangezicht. God is werkelijk alomvattend en wetend.'),
(123, 2, 116, 'En zij zeggen: "God heeft zich een kind genomen." Geprezen zij Hij! Integendeel, van Hem is wat er in de hemelen en wat er op de aarde is. Allen zijn Hem onderdanig,'),
(124, 2, 117, 'de schepper van de hemelen en de aarde. Wanneer Hij iets beslist dan zegt Hij er slechts tegen: "Wees!" en het is.'),
(125, 2, 118, 'Zij die niet weten zeggen: "Had God niet tot ons kunnen spreken of had niet een teken tot ons kunnen komen?" Zo spraken ook zij die er voor hun tijd waren dergelijke woorden. Hun harten lijken op elkaar. Wij hebben de tekenen duidelijk gemaakt voor mensen die overtuigd zijn.'),
(126, 2, 119, 'Wij hebben jou met de waarheid gezonden als verkondiger van goed nieuws en als waarschuwer. Jij hoeft geen verantwoording af te leggen over hen die in de hel thuishoren.'),
(127, 2, 120, 'Maar de joden noch de christenen zullen met jou ingenomen zijn zolang je niet hun geloof volgt. Zeg: "De leidraad van God, dat is de leidraad." Als jij hun neigingen volgt na de kennis die er tot jou gekomen is, dan heb je tegen God geen beschermer en geen bondgenoot.'),
(128, 2, 121, 'Zij aan wie Wij het boek hebben gegeven lezen het op de juiste manier voor; dat zijn zij die erin geloven. Wie er niet in geloven, dat zijn zij die de verliezers zijn.'),
(129, 2, 122, 'O Israëlieten, denkt aan Mijn genade die Ik jullie geschonken heb en daaraan dat Ik jullie boven de wereldbewoners heb verkozen.'),
(130, 2, 123, 'En vreest een dag waarop niemand iets van een ander op zich kan nemen, waarop voor hem geen losprijs aanvaard wordt, voorspraak voor hem geen nut heeft en waarop zij geen hulp zullen krijgen.'),
(131, 2, 124, 'En toen Ibrahiem door zijn Heer op de proef gesteld werd met opdrachten die hij uitvoerde, zei Hij: "Ik maak jou tot voorganger voor de mensen." Deze zei: "Sommigen uit mijn nageslacht ook?" Hij zei: "Mijn verbond zal zich niet uitstrekken tot de onrechtplegers."'),
(132, 2, 125, 'En toen Wij het huis maakten tot een plaats van samenkomst voor de mensen en een vrijplaats -- en beschouwt de standplaats van Ibrahiem als een gebedsplaats -- en Wij Ibrahiem en Isma\'iel opdroegen: "Reinigt Mijn huis voor hen die de omgang verrichten, die erin vertoeven en buigen en die zich eerbiedig neerbuigen."'),
(133, 2, 126, 'En toen Ibrahiem zei: "Mijn Heer, maak dit tot een veilige plaats en voorzie met vruchten in de levensbehoeften van zijn bewoners die geloven in God en de laatste dag", zei Hij: "Wie ongelovig is, hem zal Ik nog kort laten genieten, maar dan zal Ik hem naar de bestraffing van het vuur drijven." Dat is pas een slechte bestemming!'),
(134, 2, 127, 'En toen Ibrahiem de fundamenten van het huis optrok samen met Isma\'iel: "Onze Heer, aanvaard het van ons. U bent de horende, de wetende.'),
(135, 2, 128, 'Onze Heer, en maak dat wij ons beiden aan U overgeven en maak van ons nageslacht een gemeenschap die zich aan U overgeeft en toon ons onze riten en wendt U genadig tot ons. U bent de genadegever, de barmhartige.'),
(136, 2, 129, 'Onze Heer, en zend tot hen een gezant uit hun midden die hun Uw tekenen voordraagt, hun het boek en de wijsheid onderwijst en hen loutert. U bent de machtige, de wijze."'),
(137, 2, 130, 'En wie anders wendt zich af van het geloof van Ibrahiem dan wie zichzelf laat verdwazen? Zeker, Wij hebben hem in het tegenwoordige leven uitverkoren en in het hiernamaals behoort hij tot de rechtschapenen.'),
(138, 2, 131, 'Toen zijn Heer tot hem zei: "Geef je over." zei hij: "Ik geef mij over aan de Heer van de wereldbewoners."'),
(139, 2, 132, 'En dat droeg Ibrahiem aan zijn kinderen op; zo ook Ja\'koeb: "O mijn kinderen, God heeft voor jullie de godsdienst gekozen. Jullie mogen dus alleen maar sterven als mensen die zich [aan God] hebben overgegeven."'),
(140, 2, 133, 'Of waren jullie getuigen toen Ja\'koeb de dood nabij was? Toen hij tot zijn zoons zei: "Wat zullen jullie dienen na mijn heengaan?" zeiden zij: "Wij zullen jouw god en de god van jouw vaderen Ibrahiem, Isma\'iel en Ishaak dienen als één god en aan Hem hebben wij ons overgegeven."'),
(141, 2, 134, 'Dat is een gemeenschap die voorbijgegaan is. Haar komt toe wat zij verdiend heeft en jullie komt toe wat jullie verdiend hebben. Jullie hoeven geen verantwoording af te leggen over wat zij gedaan hebben.'),
(142, 2, 135, 'Zij zeiden: "Weest joden of christenen, dan laten jullie je op het goede pad brengen." Zeg: "Welnee, het geloof van Ibrahiem die het zuivere geloof aanhing; hij behoorde niet bij de veelgodendienaars."'),
(143, 2, 136, 'Zeg: "Wij geloven in God, in wat naar ons is neergezonden en in wat naar Ibrahiem, Isma\'iel, Ishaak, Ja\'koeb en de stammen is neergezonden en in wat aan Moesa en \'Isa is gegeven en in wat aan de profeten door hun Heer gegeven is. Wij maken geen verschil tussen één van hen en wij hebben ons aan Hem overgegeven."'),
(144, 2, 137, 'Als zij dan in hetzelfde geloven als waarin jullie geloven dan hebben zij zich op het goede pad laten brengen. Maar als zij zich afkeren dan zijn zij het oneens. God zal voor jullie dan genoeg zijn tegen hen. Hij is de horende, de wetende.'),
(145, 2, 138, 'De geloofsvorm van God! En wie heeft een betere geloofsvorm dan God? Wij dienen Hem.'),
(146, 2, 139, 'Zeg: "Twisten jullie met ons over God, terwijl Hij onze Heer is en jullie Heer. Wij hebben onze daden en jullie hebben jullie daden. Wij zijn Hem toegewijd.'),
(147, 2, 140, 'Of zeggen jullie dat Ibrahiem, Isma\'iel, Ishaak, Ja\'koeb en de stammen joden waren of christenen?" Zeg: "Weten jullie het beter of God?" En wie is zondiger dan hij die een getuigenis van God, dat hij heeft, verbergt? God let goed op wat jullie doen.'),
(148, 2, 141, 'Dat is een gemeenschap die voorbijgegaan is. Haar komt toe wat zij verdiend heeft en jullie komt toe wat jullie verdiend hebben. Jullie hoeven geen verantwoording af te leggen over wat zij gedaan hebben.'),
(149, 2, 142, 'De dwazen onder de mensen zullen zeggen: "Wat heeft hen afgewend van hun gebedsrichting waaraan zij gewoon waren?" Zeg: "Van God is het oosten en het westen; Hij wijst aan wie Hij wil de goede richting naar een juiste weg."'),
(150, 2, 143, 'Zo hebben Wij jullie gemaakt tot een evenwichtige gemeenschap opdat jullie getuigen zullen zijn over de mensen en opdat de gezant getuige zal zijn over jullie. Wij hebben de gebedsrichting waaraan jullie gewoon waren slechts ingesteld opdat Wij zouden weten wie van hen die zich op hun hielen omdraaien de gezant volgen. Het was inderdaad moeilijk, behalve voor hen aan wie God de goede richting wijst. God is niet zo dat Hij jullie geloof verloren laat gaan. God is voor de mensen vol mededogen en een erbarmer.'),
(151, 2, 144, 'Wij zien wel dat jouw gezicht de hemel rond zoekt. Dus zullen Wij je tot een gebedsrichting wenden die je bevalt. Wend dus je aangezicht in de richting van de heilige moskee. Waar jullie ook zijn, wendt jullie aangezicht in die richting. Zij aan wie het boek gegeven is weten dat het de waarheid is van hun Heer. God let goed op wat zij doen.'),
(152, 2, 145, 'Wat voor teken jij ook brengt aan hen aan wie het boek gegeven is, zij volgen jouw gebedsrichting toch niet en jij volgt hun gebedsrichting niet, noch volgen zij elkaars gebedsrichting. Als jij nu hun neigingen volgt, nadat de kennis tot jou is gekomen, dan behoor je zeker tot de onrechtplegers.'),
(153, 2, 146, 'Zij aan wie Wij het boek gegeven hebben kennen het zoals zij hun zonen kennen. Een groep van hen verbergt echter de waarheid willens en wetens.'),
(154, 2, 147, 'De waarheid komt van jouw Heer; wees dus niet een van hen die het in twijfel trekken.'),
(155, 2, 148, 'Ieder heeft een richting waarheen hij zich wendt. Wedijvert dan met elkaar in goede daden. Waar jullie ook zijn, God zal jullie tezamen brengen. God is almachtig.'),
(156, 2, 149, 'Waar je ook vandaan komt, wend je aangezicht in de richting van de heilige moskee. Het is de waarheid die van jouw Heer komt. God let goed op wat jullie doen.'),
(157, 2, 150, 'Waar jullie ook zijn, wendt jullie aangezicht in de richting van de heilige moskee, opdat de mensen geen argument tegen jullie hebben, behalve diegenen onder hen die onrecht plegen. Vreest hen niet, maar vreest Mij; ook opdat Ik Mijn gunst aan jullie vervolmaak. Misschien zullen jullie je de goede richting laten wijzen.'),
(158, 2, 151, 'Wij hebben immers ook tot jullie een gezant uit jullie midden gezonden die jullie Onze tekenen voorleest, die jullie loutert en die jullie het boek en de wijsheid onderwijst en die jullie onderwijst wat jullie niet wisten.'),
(159, 2, 152, 'Gedenkt Mij, dan gedenk Ik jullie, en betuigt Mij dank en weest niet ongelovig aan Mij.'),
(160, 2, 153, 'Jullie die geloven! Neemt jullie toevlucht tot geduld en de salaat. God is met hen die geduldig volharden.'),
(161, 2, 154, 'En zegt niet van hen die op Gods weg gedood worden dat zij dood zijn; zij zijn juist levend, maar jullie beseffen het niet.'),
(162, 2, 155, 'Wij zullen jullie op de proef stellen met iets van vrees, honger en tekort aan bezittingen, levens en vruchten, maar verkondig het goede nieuws aan hen die geduldig volharden,'),
(163, 2, 156, 'die, als onheil hen treft, zeggen: "Wij behoren aan God toe en tot Hem zullen wij terugkeren."'),
(164, 2, 157, 'Zij zijn het met wie hun Heer mededogen heeft en erbarmen; zij zijn het die het goede pad volgen.'),
(165, 2, 158, 'Zeker, as-Safa en al-Marwa behoren tot Gods gewijde symbolen. Als iemand dus een bedevaart maakt naar het huis, of er een bezoek aan brengt, dan is het voor hem geen overtreding als hij om beide de omgang verricht. En als iemand uit zichzelf iets goeds doet, dan is God dankbaar en wetend.'),
(166, 2, 159, 'Zij die de duidelijke bewijzen en de leidraad die Wij hebben neergezonden verbergen, nadat Wij ze aan de mensen in het boek hebben duidelijk gemaakt, dat zijn zij die door God en door de vervloekers worden vervloekt.'),
(167, 2, 160, 'Behalve zij die berouwvol zijn, zich beteren en het duidelijk maken. Dan zijn zij het tot wie Ik mij genadig wend. Ik ben de genadegever, de barmhartige.'),
(168, 2, 161, 'Zij die ongelovig zijn en als ongelovigen sterven, op hen rust Gods vloek en die van de engelen en de mensen tezamen.'),
(169, 2, 162, 'Zij zullen daarin altijd blijven; zij krijgen geen strafverlichting en ook geen uitstel.'),
(170, 2, 163, 'Jullie god is één god. Er is geen god dan Hij, de erbarmer, de barmhartige.'),
(171, 2, 164, 'In de schepping van de hemelen en de aarde, in het verschil van nacht en dag, in de schepen die op zee varen met wat nuttig is voor de mensen, in het water dat God uit de hemel laat neerdalen om daarmee de aarde te doen herleven nadat zij dood was, in dat Hij allerlei dieren erop heeft verspreid, in het besturen van de winden en in de wolken die voortgedreven worden tussen hemel en aarde zijn tekenen voor mensen die verstandig zijn.'),
(172, 2, 165, 'Er zijn mensen die aannemen dat er naast God nog gelijken zijn die zij even liefhebben als God, maar zij die geloven hebben God meer lief. Als zij die onrecht plegen maar zouden zien, wanneer zij de bestraffing bemerken, dat de kracht geheel aan God toebehoort en dat God streng is in de bestraffing.'),
(173, 2, 166, 'Wanneer zij die nagevolgd werden hen die hen volgden afwijzen en zij de bestraffing zien en hun banden zijn doorgesneden.'),
(174, 2, 167, 'En zij die volgden zeggen: "Als er een terugkeer voor ons was dan zouden wij hen afwijzen zoals zij ons afgewezen hebben." Zo laat God hun hun daden zien als [reden tot] wroeging voor hen. En zij kunnen niet meer uit het vuur komen.'),
(175, 2, 168, 'Mensen, eet van wat er toegestaan en goed is op de aarde en volgt niet de voetstappen van de satan; hij is een verklaarde vijand van jullie.'),
(176, 2, 169, 'Hij legt jullie slechts kwaad en gruwelijkheid op en dat jullie over God dingen moeten zeggen waar jullie geen weet van hebben.'),
(177, 2, 170, 'En als tot hen gezegd wordt: "Volgt wat God heeft neergezonden na" zeggen zij: "Welnee, wij volgen dat na waarvan wij merken dat onze vaderen er zich aan hielden." Ook dan soms als hun vaderen helemaal niet verstandig waren en zich niet de goede richting hadden laten wijzen?'),
(178, 2, 171, 'Zij die ongelovig zijn lijken bijvoorbeeld op iemand die schreeuwt tot iets wat alleen maar de roep en de kreet hoort. Doof, stom en blind zijn zij. Zij worden dan ook niet verstandig.'),
(179, 2, 172, 'Jullie die geloven! Eet van de goede dingen die Wij jullie voor jullie levensonderhoud gegeven hebben en betuigt dank aan God, als Hij het is die jullie dienen.'),
(180, 2, 173, 'Hij heeft voor jullie slechts verboden wat vanzelf is doodgegaan, bloed, varkensvlees en vlees van iets waarover iets anders dan God is aangeroepen. Maar wie ertoe gedwongen wordt, niet uit begeerte of om te overtreden, voor hem is het geen vergrijp. God is vergevend en barmhartig.'),
(181, 2, 174, 'Zij die verbergen wat God van het boek heeft neergezonden en het verkwanselen, zij zullen in hun buiken slechts vuur te verteren krijgen en God zal op de opstandingsdag niet tot hen spreken, noch zal Hij hen louteren. Voor hen is er een pijnlijke bestraffing.'),
(182, 2, 175, 'Zij zijn het die de dwaalweg ten koste van de leidraad gekocht hebben en de bestraffing ten koste van de vergeving. Hoe kunnen zij het in het vuur uithouden?'),
(183, 2, 176, 'Dat [gebeurt], omdat God het boek met de waarheid heeft neergezonden. Zij die over het boek van mening verschillen zijn het vergaand oneens.'),
(184, 2, 177, 'Vroomheid is niet dat jullie je gezichten naar het oosten en het westen wendt, maar vroom is wie gelooft in God, in de laatste dag, in de engelen, in het boek en in de profeten en wie zijn bezit, hoe lief hij dat ook heeft, geeft aan de verwanten, de wezen, de behoeftigen, aan hem die onderweg is, aan de bedelaars en voor [de vrijkoop van] de slaven, en wie de salaat verricht en de zakaat geeft en wie hun verbintenis nakomen, als zij een verbintenis zijn aangegaan en wie volhardend zijn in tegenspoed en rampspoed en ten tijde van strijd. Zij zijn het die oprecht zijn en dat zijn de godvrezenden.'),
(185, 2, 178, 'Jullie die geloven! Aan jullie is voorgeschreven te vergelden bij doodslag: een vrije voor een vrije, een slaaf voor een slaaf en een vrouw voor een vrouw. Als iemand iets wordt kwijtgescholden door zijn broeder dan moet de invordering in redelijkheid en de afdoening aan hem op juiste wijze gebeuren. Dat is een verlichting van jullie Heer en barmhartigheid. Wie daarna overtredingen begaat, voor hem is er een pijnlijke bestraffing.'),
(186, 2, 179, 'Voor jullie is er in de vergelding leven, o verstandigen; misschien worden jullie godvrezend.'),
(187, 2, 180, 'Aan jullie is voorgeschreven, wanneer een van jullie de dood nabij is, als hij bezit heeft na te laten, om in redelijkheid een [testamentaire] beschikking te maken voor de ouders en de verwanten, als een verplichting voor de godvrezenden.'),
(188, 2, 181, 'Als iemand het dan verandert nadat hij het gehoord heeft, dan wordt de schuld voor het vergrijp hun die het veranderen aangerekend; God is wetend en horend.'),
(189, 2, 182, 'Als iemand van een erflater een onrechtmatigheid of een vergrijp vreest en het tussen hen beiden in orde brengt, dan wordt hem geen vergrijp aangerekend; God is vergevend en barmhartig.'),
(190, 2, 183, 'Jullie die geloven! Aan jullie is voorgeschreven te vasten, zoals het was voorgeschreven aan hen die er voor jullie waren -- misschien zullen jullie godvrezend zijn --'),
(191, 2, 184, 'voor een bepaald aantal dagen. Maar als iemand van jullie ziek is of op reis, dan een aantal andere dagen. En zij die er wel toe in staat zijn [maar het niet doen] hebben als vervangende plicht een behoeftige te spijzigen, maar als iemand uit zichzelf iets goeds doet, is dat beter voor hem en dat jullie vasten is ook beter voor jullie, als jullie dat maar weten.'),
(192, 2, 185, 'De maand Ramadaan is het waarin de Koran werd neergezonden als een leidraad voor de mensen en als duidelijke bewijzen van de leidraad en het reddend onderscheidingsmiddel. Wie van jullie aanwezig is in de maand die moet erin vasten en als iemand ziek is of op reis, dan een aantal andere dagen. God wenst het jullie gemakkelijk te maken en niet moeilijk. Maakt het aantal dus vol en verheerlijkt God. Misschien zullen jullie dank betuigen.'),
(193, 2, 186, 'Wanneer Mijn dienaren jou naar Mij vragen: Ik ben nabij. Ik verhoor het gebed van iemand die bidt, wanneer hij Mij aanroept. Zij moeten Mij dus gehoor geven en in Mij geloven. Misschien zullen zij de goede weg opgaan.'),
(194, 2, 187, 'Het is jullie toegestaan in de nacht van de vasten omgang met jullie vrouwen te hebben; zij zijn bekleding voor jullie en jullie bekleding voor haar. God weet dat jullie jezelf bedriegen, dus heeft Hij zich genadig tot jullie gewend en jullie vergeven. Hebt nu dus gemeenschap met haar en streeft naar wat God jullie heeft voorgeschreven. Eet en drinkt totdat voor jullie in de morgenschemering de witte draad van de zwarte draad te onderscheiden is. Vast dan volledig tot aan de nacht. En hebt geen gemeenschap met haar, terwijl jullie in de moskeeën verblijven. Dat zijn Gods beperkingen. Komt die niet te na. Zo maakt God Zijn tekenen aan de mensen duidelijk. Misschien zullen jullie godvrezend worden.'),
(195, 2, 188, 'Verteert niet onderling door bedrog elkaars bezittingen en gebruikt ze niet om ermee de rechters te beïnvloeden, om een deel van de bezittingen van de mensen willens en wetens in zonde te verteren.'),
(196, 2, 189, 'Zij vragen jou naar de nieuwe manen. Zeg: "Zij zijn tijdsaanduidingen voor de mensen en de bedevaart." Vroomheid is niet dat jullie de huizen aan de achterkant ingaat, maar vroom is wie godvrezend is. Gaat dus door de deuren de huizen in en vreest God; misschien dat het jullie welgaat.'),
(197, 2, 190, 'En bestrijdt op Gods weg hen die jullie bestrijden, maar begaat geen overtredingen; God bemint de overtreders niet.'),
(198, 2, 191, 'Doodt hen waar jullie hen aantreffen en verdrijft hen waarvandaan zij jullie verdreven hebben. Verzoeking is erger dan te doden. Strijdt niet tegen hen bij de heilige moskee, zolang zij daarin niet tegen jullie strijden. Als zij tegen jullie strijden, strijdt dan tegen hen; zo is de vergelding voor de ongelovigen.'),
(199, 2, 192, 'Maar als zij ophouden, dan is God vergevend en barmhartig.'),
(200, 2, 193, 'Strijdt tegen hen tot er geen verzoeking meer is en de godsdienst alleen God toebehoort. Als zij ophouden, dan geen vergelding meer, behalve tegen de onrechtplegers.'),
(201, 2, 194, 'De heilige maand in plaats van de heilige maand: vergelding is van toepassing op de heilige dingen. Als iemand dus tegen jullie in overtreding is, mogen jullie tegen hem zo optreden als hij tegen jullie optrad. En vreest God en weet dat God met de godvrezenden is.'),
(202, 2, 195, 'Geeft bijdragen op Gods weg en stort jullie niet eigenhandig in de ondergang, en doet goed. God bemint hen die goed doen.'),
(203, 2, 196, 'En volbrengt voor God de bedevaart en het bezoek. Als jullie verhinderd zijn, brengt dan zoveel offergave als gemakkelijk is en scheert jullie hoofden pas als de offergave zijn bestemming [om geslacht te worden] heeft bereikt, maar als iemand van jullie ziek is of een letsel aan zijn hoofd heeft, dan is er een compensatie die bestaat uit vasten of een aalmoes of een offerdier. Als jullie vrij van vrees zijn, dan moet wie van het bezoek tot aan de bedevaart profiteert zoveel offergave brengen als gemakkelijk is. Als iemand niets te geven vindt, dan een vasten van drie dagen tijdens de bedevaart en zeven wanneer jullie teruggekeerd zijn; dat zijn er in totaal tien. Dat is voor diegene van wie de familie niet bij de heilige moskee woonachtig is. Vreest God en weet dat God streng is in de afstraffing.'),
(204, 2, 197, 'De bedevaart is in de bekende maanden. Wie zich daarin de verplichting van de bedevaart oplegt, moet zich tijdens de bedevaart van geslachtsverkeer, schandelijkheid en twist onthouden. Wat voor goeds jullie doen dat weet God. En neemt proviand mee, maar de beste proviand is godvrezendheid. Vreest Mij, o verstandigen.'),
(205, 2, 198, 'Het is voor jullie geen overtreding als jullie naar een gunst van jullie Heer streven. Wanneer jullie je van \'Arafaat wegspoeden gedenkt God dan bij het heilige baken en gedenkt Hem, hoe Hij jullie op het goede pad heeft gebracht, ook al behoorden jullie voordien tot hen die dwalen.'),
(206, 2, 199, 'Spoedt jullie dan weg waar de mensen zich vandaan spoeden en vraagt God om vergeving. God is vergevend en barmhartig.'),
(207, 2, 200, 'Als jullie je riten beëindigd hebben, gedenkt dan God zoals jullie je voorvaderen gedenken of nog intenser. Er zijn mensen die zeggen: "Onze Heer, geef ons in het tegenwoordige leven"; in het hiernamaals hebben zij geen aandeel.'),
(208, 2, 201, 'Er zijn er die zeggen: "Onze Heer, geef ons in het tegenwoordige leven wat goed is en ook in het hiernamaals wat goed is en bescherm ons voor de bestraffing van het vuur."'),
(209, 2, 202, 'Zij zijn het die een aandeel zullen ontvangen van wat zij hebben verdiend. God is snel met de afrekening.'),
(210, 2, 203, 'Gedenkt God op een bepaald aantal dagen. Als iemand het haastig in twee dagen doet, dan is dat voor hem geen vergrijp en als iemand het langzamer doet, dan is dat voor hem ook geen vergrijp, als hij maar godvrezend is. En vreest God en weet dat jullie tot Hem verzameld zullen worden.'),
(211, 2, 204, 'Onder de mensen is er hij wiens uitspraken over het tegenwoordige leven jou bevallen. Hij roept God als getuige aan voor wat er in zijn hart is. Toch is hij erg fel in zijn tegenstand.'),
(212, 2, 205, 'En wanneer hij weggaat trekt hij eropuit om op de aarde verderf te zaaien en het gewas en het jongvee te vernielen. Maar God bemint het verderf niet.'),
(213, 2, 206, 'En wanneer men tot hem zegt: "Vrees God", dan krijgt de trots over de zonde hem te pakken. De hel is dus goed genoeg voor hem. Dat is pas echt een slechte rustplaats!'),
(214, 2, 207, 'Onder de mensen is er ook hij die zichzelf verkoopt in zijn streven naar Gods tevredenheid. En God is vol mededogen voor de dienaren.'),
(215, 2, 208, 'Jullie die geloven! Treedt allen tezamen toe tot de overgave [aan God] en volgt de voetstappen van de satan niet, want hij is een verklaarde vijand van jullie.'),
(216, 2, 209, 'Als jullie echter een misstap maken, nadat de duidelijke bewijzen tot jullie zijn gekomen, weet dan dat God machtig is en wijs.'),
(217, 2, 210, 'Kunnen zij dan iets anders verwachten dan dat God in de schaduwen van de wolken tot hen komt en ook de engelen? De zaak is dan beslist. En aan God worden alle zaken voorgelegd.'),
(218, 2, 211, 'Vraagt de Israëlieten hoeveel duidelijke tekenen Wij hun gegeven hebben. Maar als iemand Gods genade verandert, nadat die tot hem is gekomen, dan is God streng in de afstraffing.'),
(219, 2, 212, 'Het tegenwoordige leven is voor hen die ongelovig zijn aantrekkelijk gemaakt en zij maken hen die geloven belachelijk. Maar zij die godvrezend zijn staan op de opstandingsdag boven hen. God geeft levensonderhoud aan wie Hij wil, zonder afrekening.'),
(220, 2, 213, 'De mensen waren [oorspronkelijk] één gemeenschap. Vervolgens zond God de profeten als verkondigers van de blijde boodschap en als waarschuwers. [Telkens] zond Hij met hen het boek met de waarheid neer om tussen de mensen te oordelen over de dingen waarover zij het oneens waren. Maar degenen aan wie het gegeven was werden het er slechts uit onderlinge nijd over oneens, nadat de duidelijke bewijzen tot hen gekomen waren. Daarop heeft God in Zijn goedheid hen die geloven geleid tot de waarheid, waarover zij het oneens waren. God leidt wie Hij wil op een juiste weg.'),
(221, 2, 214, 'Of rekenden jullie erop de tuin binnen te gaan, terwijl jullie nog niet iets dergelijks is overkomen als hun die voor jullie tijd zijn heengegaan? Tegenspoed en rampspoed trof hen en zij werden zozeer geschokt dat de gezant en zij die samen met hem geloofden zeiden: "Wanneer komt Gods hulp?" Zeker, Gods hulp is nabij.'),
(222, 2, 215, 'Zij vragen jou wat zij als bijdragen zullen geven. Zeg: "Laat het goede dat jullie als bijdragen geven zijn voor de ouders, de verwanten, de wezen, de behoeftigen en voor hem die onderweg is. Het goede dat jullie doen daarover is God wetend.'),
(223, 2, 216, 'Aan jullie is voorgeschreven te strijden, hoezeer het jullie ook tegenstaat. Maar misschien staat jullie iets tegen wat toch goed voor jullie is en misschien hebben jullie iets lief wat toch slecht is voor jullie. God weet en jullie weten niet.'),
(224, 2, 217, 'Zij vragen jou over de heilige maand, over het strijden erin. Zeg: "Erin te strijden is een ernstige zaak, maar het versperren van Gods weg, het niet in Hem en de heilige moskee geloven en het verdrijven van de mensen die erbij horen gelden bij God als ernstiger. Verzoeking is ernstiger dan te doden. Zij zullen pas ophouden tegen jullie te strijden wanneer zij jullie van jullie godsdienst hebben afgebracht; als zij dat zouden kunnen. Wie van jullie zich van hun godsdienst afkeren en dan als ongelovigen sterven, dat zijn zij wier daden in het tegenwoordige leven en in het hiernamaals vruchteloos zijn. Zij zijn het die in het vuur thuis horen; zij zullen daarin altijd blijven.'),
(225, 2, 218, 'Zij die geloven en zij die uitgeweken zijn en zich op Gods weg inspannen, zij zijn het die op Gods barmhartigheid hopen. God is vergevend en barmhartig. *'),
(226, 2, 219, 'Zij vragen jou naar de wijn en het kansspel. Zeg: "In beide is grote zonde en veel nuttigheid voor de mensen, maar hun zonde is groter dan hun nut." Zij vragen jou ook wat zij als bijdragen zullen geven. Zeg: "Wat over is." Zo maakt God aan jullie de tekenen duidelijk. Misschien zullen jullie nadenken'),
(227, 2, 220, 'over het tegenwoordige leven en het hiernamaals. Zij vragen jou over de wezen. Zeg: "Voor hen orde op zaken stellen is het beste." En als jullie met hen omgaan?, zij zijn toch jullie broeders! God onderscheidt de verderfbrenger van hem die herstel brengt. Als God wilde had Hij jullie in moeilijkheden gebracht. God is machtig en wijs.'),
(228, 2, 221, 'Trouwt niet met veelgodendienaressen, zolang zij niet gelovig zijn geworden; een gelovige slavin is echt beter dan een veelgodendienares, ook al bevalt zij jullie. En laat [jullie vrouwen] niet trouwen met veelgodendienaars, zolang zij niet gelovig zijn geworden; een gelovige slaaf is echt beter dan een veelgodendienaar, ook al bevalt hij jullie. Zij zijn het die oproepen tot het vuur, maar God roept op tot de tuin en de vergeving met Zijn toestemming. Hij maakt Zijn tekenen duidelijk aan de mensen, misschien zullen jullie het overwegen.'),
(229, 2, 222, 'En zij vragen jou naar de menstruatie. Zeg: "Dat is een ongemak. Onthoudt jullie dus van de vrouwen tijdens de menstruatie en benadert haar niet totdat zij weer rein zijn. Wanneer zij zich gereinigd hebben, komt dan tot haar zoals God jullie bevolen heeft. God bemint de berouwvollen en Hij bemint hen die zich reinigen.'),
(230, 2, 223, 'Jullie vrouwen zijn een akker voor jullie. Komt dan tot jullie akker hoe jullie willen. En laat er iets aan voorafgaan voor jullie zelf. Vreest God en weet dat jullie Hem zult ontmoeten. En verkondig het goede nieuws aan de gelovigen.'),
(231, 2, 224, 'Maakt God niet tot een beletsel bij jullie eden om vroomheid te betrachten, godvrezend te zijn en vrede te stichten tussen de mensen. God is horend en wetend.'),
(232, 2, 225, 'God rekent jullie onnadenkende uitspraken bij jullie eden niet aan, maar Hij rekent jullie aan wat jullie harten bedoelen. God is vergevend en zachtmoedig.'),
(233, 2, 226, 'Voor hen die zweren zich van hun vrouwen te onthouden is er een wachttijd van vier maanden, maar als zij erop terugkomen, dan is God vergevend en barmhartig.'),
(234, 2, 227, 'En als zij vastbesloten zijn de scheiding te geven, dan is God horend en wetend.'),
(235, 2, 228, 'De gescheiden vrouwen moeten voor zichzelf een wachttijd van drie menstruatieperioden aanhouden. Het is haar niet toegestaan te verbergen wat God in hun schoot geschapen heeft, als zij geloven in God en de laatste dag. Haar echtgenoten zijn gerechtigd haar in deze tijd terug te nemen als zij het graag weer goed willen maken. Zij hebben recht op hetzelfde als waartoe zij in redelijkheid verplicht zijn, maar de mannen hebben een positie boven haar. God is machtig en wijs.'),
(236, 2, 229, 'De scheiding geven mag twee keer, daarna moeten zij in redelijkheid teruggenomen of op een goede manier weggezonden worden. Het is jullie niet toegestaan iets te nemen van wat jullie haar gegeven hebben, behalve als beiden bang zijn Gods bepalingen niet te kunnen nakomen. Dan geldt dat waarmee zij zich loskoopt niet als overtreding voor hen beiden. Dat zijn Gods bepalingen; overtreedt ze niet. Wie Gods bepalingen overtreden, dat zijn de onrechtplegers.'),
(237, 2, 230, 'Als hij haar dan de scheiding geeft dan is zij voor hem daarna niet meer toegestaan, zolang zij niet met een andere echtgenoot trouwt. Als deze haar de scheiding geeft dan is het voor hen beiden geen overtreding tot elkaar terug te keren als zij denken Gods bepalingen te kunnen nakomen. Dat zijn Gods bepalingen die Hij verduidelijkt voor mensen die het weten.'),
(238, 2, 231, 'Als jullie vrouwen de scheiding geven en zij bereiken hun termijn, neemt haar dan in redelijkheid terug of zendt haar in redelijkheid weg, maar neemt haar niet terug uit treiterij, om te overtreden. Wie dat doet die doet zichzelf onrecht. Drijft niet de spot met Gods tekenen en denkt aan Gods genade aan jullie en aan wat Hij van het boek en de wijsheid op jullie neergezonden heeft om jullie ermee te vermanen. En vreest God en weet dat God met alles bekend is.'),
(239, 2, 232, 'Als jullie vrouwen de scheiding geven en zij bereiken hun termijn, verhindert haar dan niet te trouwen met haar echtgenoten wanneer zij in redelijkheid overeenstemming bereikt hebben. Daartoe wordt wie van jullie gelooft in God en de laatste dag aangespoord. Dat is zuiverder voor jullie en reiner. En God weet en jullie weten niet.'),
(240, 2, 233, 'Moeders zullen hun kinderen twee volle jaren zogen. Dat geldt voor wie tot het einde toe wil zogen. Het is de plicht van de vader in redelijkheid in haar onderhoud en kleding te voorzien. Niemand wordt meer opgelegd dan hij kan dragen. Een moeder zal geen schade lijden om haar kind, noch een vader om zijn kind. De erfgenaam heeft dezelfde plicht. Als beiden echter met wederzijds goedvinden en in gezamenlijk overleg met het zogen willen stoppen, dan is dat voor geen van beiden een overtreding. En als jullie je kinderen door anderen willen laten zogen dan is dat voor jullie ook geen overtreding wanneer jullie overhandigen wat jullie in redelijkheid te geven hebben. En vreest God en weet dat God doorziet wat jullie doen.'),
(241, 2, 234, 'Als er uit jullie midden zijn die weggenomen worden en echtgenotes achterlaten, dan moeten zij zich aan een wachttijd van vier maanden en tien [dagen] houden. Wanneer zij dan haar termijn bereiken dan is wat zij in redelijkheid doen voor jullie geen overtreding. God is over wat jullie doen welingelicht.'),
(242, 2, 235, 'Het is ook geen overtreding voor jullie als jullie toespelingen op een huwelijksaanzoek maken of [de gedachte eraan] in jullie binnenste koesteren. God weet dat jullie aan haar denken. Maar spreekt niets in het geheim met haar af, behalve als jullie behoorlijke dingen zeggen. Besluit niet het huwelijk te sluiten zolang niet de voorgeschreven termijn is bereikt. Weet dat God weet wat in jullie binnenste is. Weest voor Hem dus op jullie hoede en weet dat God vergevend en zachtmoedig is.'),
(243, 2, 236, 'Het is geen overtreding voor jullie als jullie vrouwen de scheiding geven zolang jullie haar niet aangeraakt of een verplichte bruidsgift voor haar vastgesteld hebben, maar treft wel in redelijkheid een voorziening -- de welgestelde naar zijn vermogen en de armlastige naar zijn vermogen -- voor haar levensbehoeften. Het is een verplichting voor hen die goed doen.'),
(244, 2, 237, 'En als jullie haar de scheiding geven voordat jullie haar hebben aangeraakt, maar voor haar al wel een verplichte bruidsgift hebt vastgesteld, dan wordt het de helft van wat jullie hebben vastgesteld, behalve als zij het kwijtscheldt of hij het kwijtscheldt onder wiens gezag de huwelijkssluiting valt. Het kwijtschelden is dichter bij godvrezendheid. En vergeet niet iets voor elkaar over te hebben. God doorziet wel wat jullie doen.'),
(245, 2, 238, 'Houdt jullie aan de salaats en vooral aan de middelste salaat en staat onderdanig voor God.'),
(246, 2, 239, 'Als jullie dan bang voor iets zijn doet het dan lopend of rijdend. Als jullie in veiligheid zijn, gedenkt dan God, zoals Hij jullie heeft onderwezen wat jullie niet wisten.'),
(247, 2, 240, 'Als er uit jullie midden zijn die weggenomen worden en echtgenotes achterlaten, dan moeten zij ten behoeve van hun echtgenotes een [testamentaire] beschikking treffen voor haar levensonderhoud gedurende een jaar, zonder dat zij uitgezet worden. Als zijzelf uittrekken dan geldt het redelijke dat zij met betrekking tot zichzelf doen niet als overtreding voor jullie. God is machtig en wijs.'),
(248, 2, 241, 'Gescheiden vrouwen hebben in redelijkheid recht op levensonderhoud. Het is een verplichting voor de godvrezenden.'),
(249, 2, 242, 'Zo maakt God aan jullie Zijn tekenen duidelijk. Misschien zullen jullie verstandig worden.'),
(250, 2, 243, 'Heb jij niet gezien naar hen die bij duizenden in doodsangst uit hun woningen trokken? Toen sprak God tot hen: "Sterft" en vervolgens maakte Hij hen weer levend. God is vol van goedgunstigheid jegens de mensen, maar de meeste mensen betuigen geen dank.'),
(251, 2, 244, 'Strijdt op Gods weg en weet dat God horend en wetend is.'),
(252, 2, 245, 'Wie is het die aan God een goede lening geeft? Hij zal het voor hem dan veelvoudig verdubbelen. God schenkt weinig en ook ruimschoots en tot Hem worden jullie teruggebracht.'),
(253, 2, 246, 'Heb jij niet gezien naar de voornaamsten van de Israëlieten na Moesa toen zij tot een profeet van hen zeiden: "Zend ons een koning, dan zullen wij op Gods weg strijden." Hij zei: "Zou het kunnen gebeuren, als het jullie voorgeschreven is te strijden, dat jullie dan niet strijden?" Zij zeiden: "Waarom zouden wij niet op Gods weg strijden, terwijl wij toch ver van onze woningen en zonen verdreven zijn?" Maar toen het hun voorgeschreven werd te strijden onttrokken zij zich eraan, op enkelen na. God kent de onrechtplegers.'),
(254, 2, 247, 'Toen zei hun profeet tot hen: "God heeft Taloet als koning tot jullie gezonden." Maar zij zeiden: "Hoe zou hij het koningschap over ons kunnen uitoefenen, terwijl wij meer recht op het koningschap hebben dan hij en hem ook geen overvloed van bezit gegeven is?" Hij zei: "God heeft hem boven jullie uitverkoren en Hij heeft hem zijn kennis en zijn lichaam in ruime mate vergroot. En God geeft Zijn koningschap aan wie Hij wil. God is alomvattend en wetend."'),
(255, 2, 248, 'Hun profeet zei tot hen: "Het teken van zijn koningschap is dat de ark tot jullie komt waarin een goddelijke rust van jullie Heer is en een overblijfsel van wat de mensen van Moesa en de mensen van Haroen hebben nagelaten, en die de engelen dragen. Daarin is een teken voor jullie, als jullie gelovig zijn."'),
(256, 2, 249, 'Toen dan Taloet met zijn troepen uittrok, zei hij: "God zal jullie op de proef stellen met een rivier. Wie eruit drinkt, hoort niet bij mij en wie het niet proeft, die hoort bij mij, behalve als iemand een handvol opschept." Toen dronken zij eruit op enkelen na. Toen hij daarop met hen die samen met hem gelovig waren de rivier overstak zeiden zij: "Vandaag hebben wij tegen Djaloet en zijn troepen geen kracht." Zij die meenden dat zij God zouden ontmoeten zeiden: "Vaak heeft een kleine troepenmacht met Gods toestemming een grote troepenmacht overwonnen! God is met hen die geduldig volharden."'),
(257, 2, 250, 'En toen zij optrokken tegen Djaloet en zijn troepen zeiden zij: "Onze Heer, verleen ons volharding, maak onze voeten stevig en help ons tegen de ongelovige mensen."'),
(258, 2, 251, 'Toen versloegen zij hen met Gods toestemming en Dawoed doodde Djaloet en God gaf hem het koningschap en de wijsheid en Hij onderwees hem [veel] van wat Hij wilde. Als God niet sommige mensen door anderen had laten verdrijven dan zou de aarde volledig verdorven zijn, maar God is vol van goedgunstigheid jegens de wereldbewoners.'),
(259, 2, 252, 'Dat zijn Gods tekenen. Wij lezen ze aan jou in waarheid voor. En jij bent zeker een van de gezondenen.'),
(260, 2, 253, 'Dat zijn de gezanten. Wij hebben sommigen boven anderen verkozen. Tot enkelen van hen heeft God gesproken en sommigen heeft Hij hogere rangen gegeven. Wij hebben \'Isa, de zoon van Marjam, de duidelijke bewijzen gegeven en hem gesterkt met de heilige geest. Als God het gewild had, hadden zij die na hen kwamen elkaar niet bestreden, nadat de duidelijke bewijzen tot hen gekomen waren. Maar zij waren het oneens. Sommigen van hen geloofden dus, maar anderen waren ongelovig. Als God het gewild had, hadden zij elkaar niet bestreden, maar God doet wat Hij wenst.'),
(261, 2, 254, 'Jullie die geloven! Geeft ook bijdragen van wat Wij jullie voor jullie levensonderhoud gegeven hebben voordat er een dag komt waarop er geen handel, geen vriendschap en geen voorspraak is. De ongelovigen zijn de onrechtplegers.'),
(262, 2, 255, 'God, er is geen god dan Hij, de levende, de standvastige. Sluimer noch slaap overmant Hem. Hem behoort wat in de hemelen en wat op de aarde is. Wie is het die zonder Zijn toestemming bij Hem zou kunnen bemiddelen? Hij weet wat vóór hen is en wat achter hen is en zij bevatten niets van Zijn kennis, behalve dan wat Hij wil. Zijn troon strekt zich uit over de hemelen en de aarde en het valt Hem niet zwaar beide in stand te houden. Hij is de verhevene, de geweldige.'),
(263, 2, 256, 'In de godsdienst is geen dwang. Redelijk inzicht is duidelijk onderscheiden van verdorvenheid. Wie dan geen geloof hecht aan de Taghoet maar gelooft in God, die houdt de stevigste handgreep vast, die niet afbreekt. En God is horend en wetend.'),
(264, 2, 257, 'God is de beschermer van hen die geloven: Hij brengt hen uit de duisternis naar het licht. Maar zij die ongelovig zijn, hun bondgenoten zijn de Taghoet: zij brengen hen uit het licht naar de duisternis. Zij zijn het die in het vuur thuishoren; zij zullen daarin altijd blijven.'),
(265, 2, 258, 'Heb jij niet gezien naar hem die met Ibrahiem redetwistte over zijn Heer, of God hem het koningschap gegeven had? Toen Ibrahiem zei: "Mijn Heer is het die leven geeft en die laat sterven." Hij zei: "Ik geef leven en laat sterven." Ibrahiem zei: "God laat de zon uit het oosten komen, laat jij haar uit het westen komen." Toen was hij die ongelovig was verbijsterd. God wijst de mensen die onrecht plegen de goede richting niet.'),
(266, 2, 259, 'Of zoals hij, die aan een stad voorbijkwam die verlaten was en in ruïnes. Hij zei: "Hoe kan God haar na haar dood weer tot leven brengen." Toen liet God hem honderd jaar dood zijn en liet hem vervolgens weer opstaan. Hij zei: "Hoe lang heeft het voor jou geduurd?" Hij zei: "Het heeft een dag of een deel van een dag geduurd." Hij zei: "Welnee, het heeft voor jou honderd jaar geduurd en kijk eens naar je eten en je drinken; zij zijn niet bedorven en kijk eens naar je ezel. Zo maken Wij jou tot een teken voor de mensen. En kijk eens naar de beenderen, hoe Wij ze samenvoegen en dan bekleden met vlees." Toen het hem duidelijk werd zei hij: "Ik weet dat God almachtig is."'),
(267, 2, 260, 'En toen Ibrahiem zei: "Mijn Heer, toon mij hoe U de doden weer levend maakt." Hij zei: "Geloof jij dan niet?" Hij zei: "Jawel, maar het is opdat mijn hart gerustgesteld wordt." Hij zei: "Neem dan vier stuks gevogelte en snijd ze naar jou toe in stukken. Leg er dan op elke rots een stuk van. Roep ze dan en ze zullen op je toe komen rennen. En weet dat God machtig is en wijs."'),
(268, 2, 261, 'Zij die hun bezittingen als bijdrage op Gods weg geven, lijken bijvoorbeeld op een zaadkorrel die zeven aren laat groeien met honderd korrels in elke aar. God geeft het dubbele aan wie Hij wil en God is alomvattend en wetend.'),
(269, 2, 262, 'Zij die hun bezittingen als bijdrage op Gods weg geven en dan het geven van hun bijdrage niet laten volgen door gepoch en ergernis, voor hen is hun loon bij hun Heer. Zij hebben niets te vrezen en zij zullen niet bedroefd zijn. *'),
(270, 2, 263, 'Vriendelijke woorden en vergeving zijn beter dan een aalmoes gevolgd door ergernis. God is behoefteloos en zachtmoedig.'),
(271, 2, 264, 'Jullie die geloven! Maakt jullie aalmoezen niet waardeloos door gepoch en ergernis zoals hij die zijn bezit weggeeft om door de mensen gezien te worden maar zonder te geloven in God en de laatste dag. Hij lijkt bijvoorbeeld op een rots met aarde erop. Dan treft hem een stortregen en die laat hem kaal achter. Zij hebben geen macht over iets wat zij verworven hebben. En God wijst de ongelovige mensen de goede richting niet.'),
(272, 2, 265, 'Zij die hun bezittingen als bijdrage geven in hun streven naar Gods tevredenheid en om hun zielen te sterken, lijken bijvoorbeeld op een tuin op een heuvel. Dan treft hem een stortregen en hij brengt het dubbele aan voedsel op. En als hem geen stortregen treft dan wel dauw. En God doorziet wel wat jullie doen.'),
(273, 2, 266, 'Zou één van jullie graag een tuin van palmen en wijnstokken willen hebben waar de rivieren onderdoor stromen en waarin hij allerlei vruchten heeft? Maar de ouderdom treft hem, terwijl hij nog weerloze nakomelingen heeft en de tuin wordt getroffen door een wervelstorm met vuur erin zodat hij wordt verbrand. Zo maakt God aan jullie de tekenen duidelijk. Misschien zullen jullie nadenken.'),
(274, 2, 267, 'Jullie die geloven! Geeft ook bijdragen van de goede dingen die jullie verworven hebben en ook van wat Wij voor jullie uit de aarde hebben voortgebracht. Weest er niet op uit om wat er slecht van is als bijdrage te geven; jullie zouden het zelf alleen maar nemen als jullie de ogen ervoor zouden sluiten. En weet dat God behoefteloos is en lofwaardig.'),
(275, 2, 268, 'De satan zegt jullie armoede toe en beveelt jullie gruwelijkheid, maar God zegt jullie van Zijn kant vergeving toe en genade. God is alomvattend en wetend.'),
(276, 2, 269, 'Hij geeft de wijsheid aan wie Hij wil. En aan wie de wijsheid gegeven is, aan hem is veel goeds gegeven. Maar alleen de verstandigen laten zich vermanen.'),
(277, 2, 270, 'Welke bijdrage jullie geven en welke gelofte jullie doen, God weet het. De onrechtplegers hebben geen meehelpers.'),
(278, 2, 271, 'Als jullie openlijk aalmoezen geven, dan is dat mooi, maar als jullie dat in het verborgene doen en ze aan de armen geven dan is dat beter voor jullie en het verzoent iets van jullie slechte daden. God is over wat jullie doen welingelicht. *'),
(279, 2, 272, 'Het is niet jouw taak om hen op het goede pad te brengen, maar God wijst de goede richting aan wie Hij wil. En het goede dat jullie als bijdrage geven is in jullie eigen voordeel, ook al geven jullie de bijdragen in jullie verlangen naar Gods aangezicht. En het goede dat jullie als bijdrage geven, zal jullie worden vergoed en jullie zal geen onrecht worden aangedaan.'),
(280, 2, 273, 'Het is ten bate van de armen die op Gods weg tekort gekomen zijn, zodat zij niet in staat zijn rond te trekken op de aarde. Wegens hun bescheidenheid denkt de domme dat zij rijk zijn, maar jij kent hen aan hun kenmerk; zij vragen de mensen niet opdringerig. En het goede dat jullie als bijdrage geven, daarvan weet God.'),
(281, 2, 274, 'Zij die hun bezittingen weggeven, \'s nachts en overdag, in het geheim en in het openbaar, zij hebben hun loon bij hun Heer en zij hebben niets te vrezen noch zullen zij bedroefd zijn.'),
(282, 2, 275, 'Zij die de woeker verteren zullen alleen maar opstaan zoals hij opstaat die door de aanraking van de satan in elkaar geslagen wordt. Dat komt omdat zij zeiden: "Handel is gelijk aan woeker." Maar God heeft de handel toegestaan en de woeker verboden. En hij tot wie een aansporing van zijn Heer komt en er dan mee ophoudt, mag houden wat hij al heeft verworven. Zijn zaak wordt naar God verwezen. Maar wie het weer doen, zij zijn het die in het vuur thuishoren; zij zullen daarin altijd blijven.'),
(283, 2, 276, 'God doet de woeker teniet, maar vermeerdert de opbrengst van de aalmoezen. En God bemint geen enkele zondige ongelovige.'),
(284, 2, 277, 'Zij die geloven en de deugdelijke daden doen, de salaat verrichten en de zakaat geven, zij hebben hun loon bij hun Heer en zij hebben niets te vrezen noch zullen zij bedroefd zijn.'),
(285, 2, 278, 'Jullie die geloven! Vreest God en ziet af van de woeker die nog overblijft, als jullie gelovig zijn.'),
(286, 2, 279, 'Als jullie het niet doen, voorziet dan oorlog van God en Zijn gezant, maar als jullie berouw hebben, dan mogen jullie je kapitaal behouden. Doet niemand onrecht aan en onderga geen onrecht.'),
(287, 2, 280, 'En als er iemand [vanwege zijn schuldeisers] in moeilijkheden is, [geeft] dan uitstel tot er betere omstandigheden zijn. Maar dat jullie het [hem] als aalmoes schenken is beter voor jullie als jullie dat maar wisten.'),
(288, 2, 281, 'En weest op jullie hoede voor een dag waarop jullie tot God teruggebracht zullen worden. Dan zal aan iedereen vergoed worden wat hij verdiend heeft. En hun zal geen onrecht worden gedaan.'),
(289, 2, 282, 'Jullie die geloven! Wanneer jullie met elkaar schuldverbintenissen aangaan tot een vastgestelde termijn, schrijft die dan op. Een schrijver moet het in jullie bijzijn correct opschrijven. Een schrijver mag niet weigeren te schrijven zoals God hem geleerd heeft; hij moet dus schrijven en hij die de verplichting aangaat moet dicteren. Hij moet God zijn Heer vrezen en niets eraan te kort doen. Als hij die de verplichting aangaat zwak van geest of lichaam is of niet in staat is zelf te dicteren, dan moet zijn zaakwaarnemer correct dicteren. En roept twee getuigen op uit het midden van jullie mannen. En als er geen twee mannen zijn, dan een man en twee vrouwen uit hen die jullie als getuigen aanvaarden, zodat als één van haar beiden zich vergist, de andere haar eraan kan herinneren. De getuigen moeten niet weigeren als zij worden opgeroepen. Verafschuwt niet het op te schrijven, of het klein is of groot, met zijn termijn. Dat is rechtmatiger bij God, juister voor het getuigenis en het bevordert dat jullie niet twijfelen. Alleen als het om aanwezige koopwaar gaat, die jullie onder elkaar uitwisselen, dan is het geen overtreding voor jullie als jullie het niet opschrijven. En neemt getuigen wanneer jullie met elkaar een koop afsluiten, maar laat schrijver noch getuige schade lijden. Als jullie dat doen dan is dat voor jullie een schande. Vreest God; God onderwijst jullie en God is alwetend.'),
(290, 2, 283, 'En als jullie op reis zijn en geen schrijver vinden, dan moet een onderpand genomen worden. Maar als jullie elkaar vertrouwen, dan moet hij in wie vertrouwen gesteld wordt het vertrouwen in hem beantwoorden en God zijn Heer vrezen. Jullie moeten het getuigenis niet achterhouden. Wie het achterhoudt diens hart is zondig. God weet wel wat jullie doen.'),
(291, 2, 284, 'Van God is wat er in de hemelen en wat er op de aarde is. Of jullie openlijk laten blijken wat in jullie binnenste is of het verbergen, God rekent met jullie daarover af en dan vergeeft Hij wie Hij wil en straft wie Hij wil. God is almachtig.'),
(292, 2, 285, 'De gezant gelooft in wat van zijn Heer vandaan naar hem is neergezonden en de gelovigen ook; allen geloven in God, in Zijn engelen, in Zijn boeken en in Zijn gezanten. Wij maken geen enkel onderscheid tussen Zijn gezanten. Zij zeggen: "Wij horen en gehoorzamen. Schenk ons Uw vergeving, onze Heer. Bij U is de bestemming."'),
(293, 2, 286, 'God legt niemand meer op dan hij kan dragen. Hem komt toe wat hij verdiend heeft en van hem wordt gevorderd wat hij heeft begaan. Onze Heer, reken het ons niet aan als wij vergeten of fouten maken. Onze Heer, leg ons niet een zware last op zoals U die aan hen hebt opgelegd die er voor onze tijd waren. Onze Heer, leg ons niet iets op waarvoor wij geen kracht hebben en schenk ons vergiffenis, vergeef ons en heb erbarmen met ons. U bent onze beschermheer, help ons dan tegen de ongelovige mensen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'A[lif] L[aam] M[iem].'),
(295, 3, 2, 'God, er is geen god dan Hij, de levende, de standvastige.'),
(296, 3, 3, 'Hij heeft het boek met de waarheid tot jou neergezonden ter bevestiging van wat er voordien al was en Hij heeft ook de Taura en de Indjiel neergezonden,'),
(297, 3, 4, 'vroeger al, als leidraad voor de mensen en Hij heeft het reddend onderscheidingsmiddel neergezonden. Zij die ongelovig zijn aan Gods tekenen, voor hen is er een zware bestraffing. God is machtig en wraakgierig.'),
(298, 3, 5, 'Voor God is niets verborgen, niet op de aarde, noch in de hemel.'),
(299, 3, 6, 'Hij is het die jullie in de moederschoot gevormd heeft, zoals Hij wil. Er is geen god dan Hij, de machtige, de wijze.'),
(300, 3, 7, 'Hij is het die tot jou het boek heeft neergezonden; een deel ervan bestaat uit eenduidige tekenen -- zij zijn de grondslag van het boek -- en een ander deel uit meerduidige. Zij nu die in hun hart een neiging naar het verkeerde hebben volgen bij hun streven naar verzoeking en bij hun streven naar [eigenzinnige] verklaring dat deel ervan dat meerduidig is. Maar de verklaring ervan kent niemand behalve God. En zij die een diepgewortelde kennis hebben zeggen: "Wij geloven erin. Het komt alles van onze Heer." Maar alleen de verstandigen laten zich vermanen.'),
(301, 3, 8, 'Onze Heer, laat onze harten niet naar het verkeerde neigen, nadat U ons de goede richting hebt gewezen en schenk ons van Uw kant barmhartigheid. U bent de vrijgevige.'),
(302, 3, 9, 'Onze Heer, U zult de mensen bijeenbrengen voor een dag waaraan geen twijfel is. God zal de afspraak niet afzeggen.'),
(303, 3, 10, 'Zij die ongelovig zijn, hun bezittingen en hun kinderen zullen hun bij God volstrekt niet baten. Zij zijn brandstof voor het vuur.'),
(304, 3, 11, 'Zoals dat het geval was met de mensen van Fir\'aun en hen die voor hun tijd leefden. Zij loochenden Onze tekenen en dus greep God hen voor hun zonden. God is streng in de afstraffing.'),
(305, 3, 12, 'Zeg tegen hen die ongelovig zijn: "Jullie zullen overwonnen en in de hel verzameld worden. Dat is pas een slechte rustplaats!"'),
(306, 3, 13, 'Er was een teken voor jullie in twee troepenmachten die elkaar ontmoetten. Een troepenmacht die streed op Gods weg en een andere die ongelovig was. Zij zagen met eigen ogen dat zij tweemaal zo talrijk waren als zijzelf. Maar God steunt met Zijn hulp wie Hij wil. Daarin is een les voor hen die inzicht hebben.'),
(307, 3, 14, 'Voor de mensen is de liefde tot de begeerlijkheden aantrekkelijk gemaakt, zoals vrouwen, zonen, opgehoopte fortuinen van goud en zilver, gemerkte paarden, vee en bouwland. Dat is het genot van het tegenwoordige leven; maar God, bij Hem is de goede terugkomst. *'),
(308, 3, 15, 'Zeg: "Zal ik jullie iets beters dan dat meedelen? Voor hen die godvrezend zijn, zijn er bij hun Heer tuinen waar de rivieren onderdoor stromen, waarin zij altijd zullen blijven, en ook reingemaakte echtgenotes en Gods welgevallen." God doorziet de dienaren'),
(309, 3, 16, 'die zeggen: "Onze Heer, wij geloven. Vergeef ons onze zonden en behoed ons voor de bestraffing van het vuur",'),
(310, 3, 17, 'die geduldig volharden, die waarheidslievend zijn, die onderdanig zijn, die bijdragen geven en die in de morgenschemering om vergeving vragen.'),
(311, 3, 18, 'God getuigt dat er geen god is dan Hij -- zo ook de engelen en zij die kennis bezitten. Hij is handhaver van de gerechtigheid. Er is geen god dan Hij, de machtige, de wijze.'),
(312, 3, 19, 'De godsdienst bij God is de Islaam [de overgave aan God]. Zij aan wie het boek gegeven is zijn het uit onderlinge nijd pas oneens geworden, nadat de kennis tot hen was gekomen. En als iemand niet aan Gods tekenen gelooft, dan is God snel met de afrekening.'),
(313, 3, 20, 'En als zij met jou redetwisten zeg dan: "Ik geef mij geheel over aan God, en wie mij volgen ook" en zeg tot hen aan wie het boek is gegeven en tot de ongeletterden: "Hebben jullie je overgegeven?" Als zij zich overgeven dan volgen zij het goede pad, maar als zij zich afkeren dan heb jij slechts de plicht van de verkondiging. God doorziet de dienaren.'),
(314, 3, 21, 'Zij die ongelovig zijn aan Gods tekenen, zonder enig recht de profeten doden en hen doden die tot rechtvaardigheid oproepen, zeg hun een pijnlijke bestraffing aan.'),
(315, 3, 22, 'Zij zijn het wier daden in het tegenwoordige leven en in het hiernamaals vruchteloos zijn en zij zullen geen helpers hebben.'),
(316, 3, 23, 'Heb jij niet gezien naar hen aan wie een aandeel aan het boek gegeven is? Dat zij werden opgeroepen tot Gods boek, opdat het tussen hen zou oordelen? Toen onttrok een groep van hen zich, afkerig als zij ervan waren.'),
(317, 3, 24, 'Dat is omdat zij zeggen: "Het vuur zal ons slechts een beperkt aantal dagen treffen." In hun godsdienst werden zij misleid door wat zij altijd weer verzonnen.'),
(318, 3, 25, 'En hoe zal het zijn wanneer Wij hen bijeenbrengen voor een dag waaraan geen twijfel is en wanneer aan elke ziel vergoed zal worden wat zij verdiend heeft. En aan hen zal geen onrecht gedaan worden.'),
(319, 3, 26, 'Zeg: "O God, heerser over het koningschap, U geeft het koningschap aan wie U wilt en U ontneemt het koningschap aan wie U wilt. U maakt machtig wie U wilt en U vernedert wie U wilt. Het goede is in Uw hand. U bent almachtig.'),
(320, 3, 27, 'U laat de nacht overgaan in de dag en de dag laat U overgaan in de nacht. U brengt het levende uit het dode voort en uit het levende brengt U het dode voort. U geeft levensonderhoud aan wie U wilt, zonder afrekening."'),
(321, 3, 28, 'De gelovigen moeten de ongelovigen niet in plaats van de gelovigen als medestander nemen. Wie dat doen behoren in niets tot God, behalve als jullie uit vrees voor hen op jullie hoede zijn. God waarschuwt jullie voor Hemzelf; bij God is de bestemming.'),
(322, 3, 29, 'Zeg: "Of jullie verbergen wat in jullie binnenste is of het openlijk laten blijken, God weet het en Hij weet wat in de hemelen en op de aarde is. God is almachtig."'),
(323, 3, 30, 'Op de dag waarop elke ziel het goede dat zij gedaan heeft voorgelegd zal vinden en ook het slechte dat zij gedaan heeft, zou zij wel willen dat er tussen haar en Hem een verre afstand was. God waarschuwt jullie voor Hemzelf. Maar God is vol mededogen voor de dienaren.'),
(324, 3, 31, 'Zeg: "Als jullie God liefhebben, volgt mij dan en God zal jullie liefhebben en jullie je zonden vergeven." God is vergevend en barmhartig.'),
(325, 3, 32, 'Zeg: "Gehoorzaamt God en de gezant, maar als jullie je afkeren? God heeft de ongelovigen niet lief."'),
(326, 3, 33, 'God heeft Adam, Noeh, de mensen van Ibrahiem en de mensen van \'Imraan uitverkoren boven de wereldbewoners,'),
(327, 3, 34, 'als afstammelingen van elkaar. God is horend en wetend.'),
(328, 3, 35, 'Toen de vrouw van \'Imraan zei: "Mijn Heer, ik wijd bij gelofte aan U wat in mijn buik is; neem het van mij aan. U bent de horende, de wetende."'),
(329, 3, 36, 'Toen zij haar gebaard had, zei zij: "Mijn Heer, ik heb een meisje gebaard." -- God wist het best wat zij gebaard had; het mannelijke is niet als het vrouwelijke -- "Ik heb haar Marjam genoemd en ik bid U haar en haar nageslacht te beschermen tegen de vervloekte satan."'),
(330, 3, 37, 'Toen aanvaardde haar Heer haar vriendelijk en zorgde ervoor dat zij goed opgroeide en Hij vertrouwde de zorg voor haar aan Zakarijja toe. Telkens als Zakarijja bij haar in het heiligdom binnenkwam vond hij proviand bij haar. Hij zei: "Marjam, waar heb jij dit vandaan?" Zij zei: "Het komt van God. God geeft levensonderhoud aan wie Hij wil, zonder afrekening."'),
(331, 3, 38, 'Daar riep Zakarijja zijn Heer aan; hij zei: "Mijn Heer, schenk mij van Uw kant een goed nageslacht. U bent de verhoorder van het gebed."'),
(332, 3, 39, 'Toen riepen de engelen tot hem terwijl hij in het heiligdom standvastig de salaat bad: "God kondigt jou Jahja aan, bevestiger van een woord van God, leidsman, asceet en profeet; een van de rechtschapenen."'),
(333, 3, 40, 'Hij zei: "Mijn Heer, hoe kan ik nog een jongen krijgen, terwijl ik al oud ben geworden en mijn vrouw onvruchtbaar is?" Hij zei: "Zo is het. God doet wat Hij wil."'),
(334, 3, 41, 'Hij zei: "Mijn Heer, geef mij een teken." Hij zei: "Jouw teken is dat je drie dagen niet tot de mensen zult spreken, behalve door middel van gebaren. En gedenk jouw Heer veel en lofprijs in de avond en \'s ochtends vroeg."'),
(335, 3, 42, 'En toen de engelen zeiden: "O Marjam, God heeft jou uitverkoren en jou rein gemaakt en Hij heeft jou uitverkoren boven de vrouwen van de wereldbewoners.'),
(336, 3, 43, 'O Marjam, wees jouw Heer onderdanig en buig je eerbiedig voor Hem neer en buig met de buigenden."'),
(337, 3, 44, 'Dat zijn enige mededelingen over het verborgene die Wij aan jou openbaren. Jij was immers niet bij hen toen zij hun rietpennen wierpen om [door loting] uit te maken wie van hen de zorg voor Marjam op zich zou nemen en jij was ook niet bij hen toen zij twistten.'),
(338, 3, 45, 'Toen de engelen zeiden: "O Marjam, God kondigt jou een woord van Hem aan, wiens naam zal zijn de masieh, \'Isa, de zoon van Marjam. Hij zal in het tegenwoordige leven en het hiernamaals in hoog aanzien staan en behoren tot hen die in de nabijheid [van God] zijn.'),
(339, 3, 46, 'In de wieg en als volwassene zal hij tot de mensen spreken en hij zal een van de rechtschapenen zijn."'),
(340, 3, 47, 'Zij zei: "Mijn Heer, hoe zou ik een kind krijgen, terwijl geen mens mij aangeraakt heeft" Hij zei: "Zo is het. God schept wat Hij wil. Wanneer Hij iets beslist, dan zegt Hij er slechts tegen: \'Wees!? en het is."'),
(341, 3, 48, 'En Hij zal hem het boek, de wijsheid, de Taura en de Indjiel onderwijzen.'),
(342, 3, 49, 'En [God zal hem] als een gezant tot de Israëlieten [zenden, om te zeggen]: "Ik ben tot jullie gekomen met een teken van jullie Heer: Dat ik voor jullie uit klei iets als de vorm van een vogel zal scheppen, er dan in zal blazen en dat het dan met Gods toestemming een vogel zal zijn. Dat ik blindgeborenen en melaatsen zal genezen en doden levend maak, met Gods toestemming. En dat ik jullie meedeel wat jullie eten en wat jullie in jullie huizen opslaan. Daarin is een teken voor jullie als jullie gelovig zijn.'),
(343, 3, 50, 'En als bevestiger van wat er van de Taura voor mijn tijd al was en om aan jullie iets van wat jullie verboden was toe te staan. Ik ben tot jullie gekomen met een teken van jullie Heer. Vreest dus God en gehoorzaamt mij.'),
(344, 3, 51, 'God is mijn Heer en jullie Heer; dient Hem dus. Dit is een juiste weg." *'),
(345, 3, 52, 'Toen \'Isa ongeloof bij hen gewaarwerd zei hij: "Wie zijn mijn helpers [op de weg] tot God." De discipelen zeiden: "Wij zijn Gods helpers. Wij geloven in God. Getuig dat wij ons [aan God] hebben onderworpen.'),
(346, 3, 53, 'Onze Heer, wij geloven in wat U hebt neergezonden en wij volgen de gezant. Schrijf ons dus op bij hen die getuigen."'),
(347, 3, 54, 'Zij maakten plannen en God maakte plannen, maar God is de beste plannenmaker.'),
(348, 3, 55, 'Toen God zei: "O \'Isa, Ik laat jou sterven en Ik zal jou tot Mij opheffen en jou reinigen van hen die ongelovig zijn en Ik zal tot de opstandingsdag hen die jou volgen stellen boven hen die ongelovig zijn. Dan zal jullie terugkeer tot Mij zijn en zal Ik tussen jullie oordelen over dat waarover jullie het oneens waren.'),
(349, 3, 56, 'Wat hen dus betreft die ongelovig zijn, hen zal Ik in het tegenwoordige leven en het hiernamaals met een strenge bestraffing straffen. Zij zullen geen helpers hebben.'),
(350, 3, 57, 'Maar wat hen betreft die geloven en de deugdelijke daden doen, hun zal Hij hun volle loon geven. God bemint de onrechtplegers niet."'),
(351, 3, 58, 'Dat is wat Wij jou van de tekenen en de wijze vermaning voorlezen.'),
(352, 3, 59, 'Bij God lijkt \'Isa bijvoorbeeld op Adam die Hij uit aarde geschapen heeft. Toen sprak Hij tot hem: "Wees!" en hij was.'),
(353, 3, 60, 'De waarheid komt van jouw Heer; behoor dus niet tot hen die twijfelen.'),
(354, 3, 61, 'Als dan iemand met jou over hem redetwist na wat er aan kennis tot jou gekomen is, zeg dan: "Komt, laten we onze zonen en jullie zonen, onze vrouwen en jullie vrouwen en onszelf en julliezelf bijeenroepen. Laten we dan plechtig tot God bidden en Gods vloek afroepen over hen die liegen."'),
(355, 3, 62, 'Dit is het juiste verhaal en er is geen god dan God. God is echt de machtige, de wijze.'),
(356, 3, 63, 'Als zij zich dan afkeren, dan is God welingelicht over de verderfbrengers.'),
(357, 3, 64, 'Zeg: "Mensen van het boek! Komt tot een uitspraak die voor jullie en voor ons gezamenlijk juist is. Dat wij alleen God dienen, dat wij niets aan Hem als metgezel toevoegen en dat wij elkaar niet tot heren naast God nemen." Als zij zich echter afkeren, zeg dan: "Getuigt dat wij [aan God] onderworpenen zijn."'),
(358, 3, 65, 'Mensen van het boek! Waarom twisten jullie over Ibrahiem. De Taura en de Indjiel waren toch pas na hem neergezonden! Hebben jullie dan geen verstand?'),
(359, 3, 66, 'Zo zijn jullie nu. Jullie hebben daarover getwist waar jullie kennis van hebben, maar waarom twisten jullie over iets waar jullie geen kennis van hebben?'),
(360, 3, 67, 'Ibrahiem was niet jood, noch christen, maar hij was een aanhanger van het zuivere geloof, die zich [aan God] overgaf en hij behoorde niet tot de veelgodendienaars.'),
(361, 3, 68, 'De mensen die Ibrahiem het meest waardig zijn zijn zeker zij die hem en deze profeet navolgen en die geloven. En God is de beschermer van de gelovigen.'),
(362, 3, 69, 'Een groep van de mensen van het boek zou jullie graag tot dwaling willen brengen, maar zij brengen slechts zichzelf tot dwaling zonder het te beseffen.'),
(363, 3, 70, 'Mensen van het boek! Waarom geloven jullie niet in de tekenen van God terwijl jullie toch getuigen zijn?'),
(364, 3, 71, 'Mensen van het boek! Waarom omkleden jullie de waarheid met onzin en verbergen jullie de waarheid willens en wetens?'),
(365, 3, 72, 'Een groep van de mensen van het boek zegt: "Gelooft aan de aanvang van de dag in wat op hen die geloven is neergezonden, maar weest aan het einde ervan ongelovig; misschien zullen zij terugkeren.'),
(366, 3, 73, 'En gelooft slechts hen die jullie godsdienst volgen." -- Zeg: "De leidraad is Gods leidraad" -- "[en gelooft niet] dat er aan iemand hetzelfde gegeven is als aan jullie of dat zij tegen jullie bij jullie Heer twisten." Zeg: "De goedgunstigheid is in Gods hand. Hij geeft haar aan wie Hij wil. God is alomvattend en wetend.'),
(367, 3, 74, 'Hij bevoorrecht door Zijn barmhartigheid wie Hij wil. God is vol van geweldige goedgunstigheid."'),
(368, 3, 75, 'En onder de mensen van het boek is er hij die, als je hem een fortuin toevertrouwt, het aan jou teruggeeft en onder hen is er hij die, als je hem een dinar toevertrouwt, hem alleen maar aan jou zal teruggeven als jij erop blijft staan. Dat komt omdat zij zeggen: "Men kan van ons geen rekenschap over de ongeletterden vragen." En zij zeggen over God willens en wetens een leugen.'),
(369, 3, 76, 'Ja zeker, als iemand zijn verbintenis nakomt en godvrezend is, dan bemint God de godvrezenden.'),
(370, 3, 77, 'Zij die Gods verbond en hun eden verkwanselen, dat zijn zij die geen aandeel aan het hiernamaals hebben. God zal niet tot hen spreken, noch naar hen omzien op de opstandingsdag. Hij zal hen niet louteren, maar voor hen is er een pijnlijke bestraffing.'),
(371, 3, 78, 'En onder hen is er een groep die hun tongen verdraaien bij [het voorlezen van] het boek, zodat jullie zullen denken dat het uit het boek is, terwijl het niet uit het boek is. En zij zeggen: "Het komt van God" terwijl het niet van God komt. Zij zeggen over God willens en wetens een leugen.'),
(372, 3, 79, 'Het past geen mens dat God hem het boek, de oordeelskracht en het profeetschap geeft en dat hij dan tot de mensen zal zeggen: "Weest dienaren van mij in plaats van van God." Maar wel: "Weest godgeleerden, doordat jullie het boek onderwijzen en doordat jullie het bestuderen."'),
(373, 3, 80, 'En ook niet dat hij jullie beveelt de engelen en de profeten tot heren te nemen. Zou hij jullie bevelen ongelovig te zijn, nadat jullie je [aan God] hebben overgegeven?'),
(374, 3, 81, 'En toen God een overeenkomst aanging met de profeten: "Wat voor boek en wijsheid Ik jullie ook geef, als er daarna een gezant komt die bevestigt wat jullie hebben, gelooft dan in hem en helpt hem." Hij zei: "Stemmen jullie ermee in en nemen jullie Mijn verbintenis daartoe aan?" Zij zeiden: "Wij stemmen ermee in." Hij zei: "Getuigt dan en Ik behoor met jullie tot hen die getuigen."'),
(375, 3, 82, 'Wie zich daarna afkeren, dat zijn de verdorvenen.'),
(376, 3, 83, 'Wensen zij een andere dan Gods godsdienst, terwijl wie er in de hemelen en op de aarde zijn zich goedschiks en kwaadschiks aan Hem hebben overgegeven en tot Hem zullen worden teruggebracht?'),
(377, 3, 84, 'Zeg: "Wij geloven in God en in wat op ons is neergezonden en in wat op Ibrahiem, Isma\'iel, Ishaak, Ja\'koeb en de stammen is neergezonden en in wat aan Moesa, \'Isa en de profeten is gegeven van de kant van hun Heer. Wij maken geen enkel verschil tussen hen en aan Hem hebben wij ons overgegeven."'),
(378, 3, 85, 'Wie iets anders dan de Islaam [de overgave aan God] als godsdienst wenst, van hem zal het dan niet worden aanvaard. Hij behoort in het hiernamaals tot de verliezers.'),
(379, 3, 86, 'Hoe zou God de goede richting [nog] wijzen aan mensen die ongelovig zijn geworden na geloofd te hebben, die getuigd hadden dat de gezant waarachtig is en tot wie de duidelijke bewijzen gekomen waren. God wijst de juiste richting niet aan de mensen die onrecht plegen.'),
(380, 3, 87, 'Zij zijn het wier vergelding is dat op hen de vloek van God, de engelen en de mensen tezamen rust.'),
(381, 3, 88, 'Zij zullen daarin altijd blijven; zij krijgen geen strafverlichting en ook geen uitstel.'),
(382, 3, 89, 'Behalve zij die daarna berouw tonen en het weer goedmaken. God is vergevend en barmhartig.'),
(383, 3, 90, 'Zij die ongelovig zijn geworden na geloofd te hebben en dan hun ongeloof nog vermeerderen, hun berouw zal niet aanvaard worden. Zij zijn het die dwalen.'),
(384, 3, 91, 'Zij die ongelovig zijn en sterven terwijl zij ongelovig zijn, van geen van hen zou de aarde vol goud aanvaard worden ook al zou hij zich ermee willen loskopen. Zij zijn het voor wie er een pijnlijke bestraffing is en zij zullen geen helpers hebben.'),
(385, 3, 92, 'Jullie zullen de vroomheid niet bereiken totdat jullie van wat jullie liefhebben bijdragen geven. En wat jullie ook aan bijdragen geven, God weet ervan. *'),
(386, 3, 93, 'Alle voedsel was aan de Israëlieten toegestaan, behalve wat Israël zichzelf als verbod oplegde, voordat de Taura werd neergezonden. Zeg: "Brengt de Taura dan en leest haar voor als jullie waarheid spreken."'),
(387, 3, 94, 'Wie dan daarna over God bedrog verzinnen, dat zijn de onrechtplegers.'),
(388, 3, 95, 'Zeg: "God spreekt de waarheid; volgt dus het geloof van Ibrahiem die het zuivere geloof aanhing; hij behoorde niet tot de veelgodendienaars."'),
(389, 3, 96, 'Het eerste huis dat voor de mensen werd neergezet is dat in Bakka [dwz. Mekka]; gezegend is het en een leidraad voor de wereldbewoners.'),
(390, 3, 97, 'Erin zijn duidelijke tekenen; het is de standplaats van Ibrahiem. Wie er binnentreedt is veilig. Het is voor de mensen een plicht jegens God om op bedevaart naar het huis te gaan; voor wie in staat is daarheen op weg te gaan. En wie ongelovig is? God heeft de wereldbewoners niet nodig.'),
(391, 3, 98, 'Zeg: "Mensen van het boek! Waarom geloven jullie niet in de tekenen van God terwijl God toch getuige is van wat jullie doen."'),
(392, 3, 99, 'Zeg: "Mensen van het boek! Waarom versperren jullie Gods weg voor wie geloven, in jullie verlangen dat hij een kronkelweg is, terwijl jullie toch getuigen zijn. God let goed op wat jullie doen."'),
(393, 3, 100, 'Jullie die geloven! Als jullie gehoorzamen aan een groep van hen aan wie het boek gegeven is, dan zullen zij nadat jullie tot geloof gekomen zijn jullie weer ongelovig maken.'),
(394, 3, 101, 'Maar hoe zouden jullie ongelovig kunnen zijn, terwijl Gods tekenen aan jullie worden voorgelezen en Zijn gezant in jullie midden is. Wie zich aan God vasthoudt die wordt naar een juiste weg geleid.'),
(395, 3, 102, 'Jullie die geloven! Vreest God zoals het Hem toekomt; jullie mogen dus alleen maar sterven als [mensen] die zich [aan God] hebben overgegeven.'),
(396, 3, 103, 'En houdt samen vast aan Gods band en splitst jullie niet op in groepen. Denkt aan Gods genade aan jullie toen jullie vijanden waren en Hij jullie harten tot elkaar bracht en jullie door Zijn genade broeders werden; toen jullie op de rand van de groeve van het vuur waren en Hij jullie ervan redde. Zo maakt God voor jullie Zijn tekenen duidelijk. Misschien zullen jullie je de goede richting laten wijzen.'),
(397, 3, 104, 'Laat er uit jullie een gemeenschap voortkomen [van mensen] die oproepen tot het goede, het behoorlijke gebieden en het verwerpelijke verbieden. Zij zijn het die het welgaat.'),
(398, 3, 105, 'Weest niet zoals zij die zich in groepen opsplitsten en van mening verschilden nadat de duidelijke bewijzen tot hen waren gekomen. Zij zijn het voor wie er een geweldige bestraffing is,'),
(399, 3, 106, 'op de dag dat sommige gezichten wit en andere gezichten zwart zullen worden. Wat hen dus betreft van wie de gezichten zwart worden: "Zijn jullie ongelovig geworden na geloofd te hebben? Proeft dan de bestraffing ervoor dat jullie ongelovig waren."'),
(400, 3, 107, 'En wat hen betreft van wie de gezichten wit zijn geworden, in Gods barmhartigheid zullen zij zijn; zij zullen daarin altijd blijven.'),
(401, 3, 108, 'Dat zijn Gods tekenen; Wij lezen ze jou in waarheid voor. God wenst geen onrecht voor de wereldbewoners.'),
(402, 3, 109, 'Van God is wat er in de hemelen en wat er op de aarde is en aan God worden alle zaken voorgelegd.'),
(403, 3, 110, 'Jullie zijn de beste gemeenschap die er voor de mensen is voortgebracht; jullie gebieden het behoorlijke, verbieden het verwerpelijke en geloven in God. Als de mensen van het boek zouden geloven dan was dat beter voor hen. Onder hen zijn er gelovigen, maar de meesten van hen zijn de verdorvenen.'),
(404, 3, 111, 'Zij zullen jullie geen schade berokkenen, alleen maar ongemak en als zij tegen jullie strijden zullen zij omkeren om te vluchten. Dan zullen zij geen hulp krijgen.'),
(405, 3, 112, 'Zij worden getroffen met vernedering waar men hen ook aantreft, behalve als zij een band met God of een band met de mensen hebben en zij halen zich Gods toorn op de hals en zij worden getroffen met onderwerping. Dat is omdat zij ongelovig waren aan Gods tekenen en de profeten zonder enig recht doodden. Dat is omdat zij opstandigen vijandig waren. *'),
(406, 3, 113, 'Zij zijn niet [allen] gelijk. Onder de mensen van het boek is er een gemeenschap die standvastig Gods tekenen gedurende de nacht voorleest, terwijl zij zich eerbiedig neerbuigen.'),
(407, 3, 114, 'Zij geloven in God en de laatste dag, zij gebieden het behoorlijke, verbieden het verwerpelijke en wedijveren in goede daden. Zij zijn het die tot de rechtschapenen behoren.'),
(408, 3, 115, 'Het goede dat zij doen, daarvoor zal hun geen ondankbaarheid betoond worden. God kent de godvrezenden.'),
(409, 3, 116, 'Hun die ongelovig zijn zullen hun bezittingen en hun kinderen bij God volstrekt niet baten. Zij zijn het die in het vuur thuishoren; zij zullen daarin altijd blijven.'),
(410, 3, 117, 'Wat zij in het tegenwoordige leven weggeven lijkt bijvoorbeeld op ijzige wind die het gewas trof van mensen die zichzelf onrecht aangedaan hadden en het vernielden. God doet hun geen onrecht aan, maar zij doen zichzelf onrecht aan.'),
(411, 3, 118, 'Jullie die geloven! Neemt geen vertrouwelingen buiten jullie kring; zij zullen niet nalaten jullie verderfelijke schade te berokkenen. Zij zouden voor jullie graag onheil willen. De haat blijkt openlijk uit hun monden, maar wat hun binnenste verbergt is erger. Wij hebben jullie de tekenen duidelijk gemaakt als jullie verstand hebben.'),
(412, 3, 119, 'Zo zijn jullie nu. Jullie beminnen hen, maar zij beminnen jullie niet. Jullie geloven in het hele boek. Wanneer zij jullie ontmoeten zeggen zij: "Wij geloven." Maar wanneer zij alleen zijn, bijten zij uit woede over jullie op hun vingertoppen. Zeg: "Sterft in jullie woede." God weet wat er binnen in de harten is.'),
(413, 3, 120, 'Als jullie iets goeds overkomt dan ergeren zij zich en als jullie iets ergs treft dan verheugen zij zich erover. Maar als jullie geduldig volharden en godvrezend zijn zal hun list jullie geen enkele schade berokkenen. God omvat wat zij doen.'),
(414, 3, 121, 'En toen jij \'s morgens vroeg jouw huisgenoten verliet om de gelovigen plaatsen voor de strijd aan te wijzen. God is horend en wetend.'),
(415, 3, 122, 'Toen twee groepen van jullie van plan waren om op te geven, terwijl God hun beschermer was. Op God moeten de gelovigen hun vertrouwen stellen.'),
(416, 3, 123, 'God had jullie al in Badr geholpen, toen jullie zwakker waren. Vreest God, misschien zullen jullie dank betuigen.'),
(417, 3, 124, 'Toen jij tot de gelovigen zei: "Is het dan niet genoeg voor jullie dat jullie Heer jullie met drieduizend neergezonden engelen versterkt?'),
(418, 3, 125, 'Ja zeker, als jullie geduldig volharden en godvrezend zijn en zij komen op jullie afstormen, dan zal God jullie versterken met vijfduizend aanstormende engelen."'),
(419, 3, 126, 'God heeft het alleen maar gedaan om het voor jullie goed nieuws te laten zijn en opdat jullie harten erdoor gerustgesteld zouden worden -- de overwinning komt alleen maar van God, de machtige, de wijze --,'),
(420, 3, 127, 'om een deel van hen die ongelovig zijn te vernietigen of te schande te maken, zodat zij teleurgesteld omkeren'),
(421, 3, 128, '? jij hebt daarin geen zeggenschap --, of opdat Hij zich genadig tot hen wendt of hen bestraft, want zij zijn onrechtplegers.'),
(422, 3, 129, 'Van God is wat er in de hemelen is en wat er op de aarde is. Hij vergeeft wie Hij wil en bestraft wie Hij wil. God is vergevend en barmhartig.'),
(423, 3, 130, 'Jullie die geloven! Verteert de woeker niet met veelvoudige verdubbeling. En vreest God; misschien zal het jullie welgaan.'),
(424, 3, 131, 'En vreest het vuur dat voor de ongelovigen is klaargemaakt.'),
(425, 3, 132, 'En gehoorzaamt God en de gezant; misschien zal aan jullie barmhartigheid bewezen worden. *'),
(426, 3, 133, 'Streeft naar vergeving van jullie Heer en naar een tuin zo breed als de hemelen en de aarde die klaargemaakt is voor de godvrezenden,'),
(427, 3, 134, 'die bijdragen geven in voorspoed en tegenspoed en die hun woede inhouden en de mensen vergeving schenken -- God bemint hen die goed doen --'),
(428, 3, 135, 'en die, wanneer zij iets gruwelijks doen of zichzelf onrecht aandoen, God gedenken en dan om vergeving voor hun zonden vragen -- en wie behalve God vergeeft de zonden? -- en niet volharden in wat zij willens en wetens deden.'),
(429, 3, 136, 'Zij zijn het wier beloning de vergeving van hun Heer is en tuinen waar de rivieren onderdoor stromen. Zij zullen daarin altijd zijn. Dat is pas een goed loon voor hen die [goed] doen!'),
(430, 3, 137, 'Al voor jullie tijd is de gebruikelijke behandeling meermaals toegepast. Reist dus op de aarde rond en kijkt hoe het einde was van de loochenaars.'),
(431, 3, 138, 'Dit is een uiteenzetting voor de mensen en een leidraad en een aansporing voor de godvrezenden.'),
(432, 3, 139, 'Versaagt niet en treurt niet. Jullie hebben de overhand als jullie gelovig zijn.'),
(433, 3, 140, 'Jullie mag dan een slag toegebracht zijn, hun was al een dergelijke slag toegebracht; in tijden van oorlog geven Wij afwisselend geluk aan de mensen. En [dit gebeurde] ook opdat God hen kent, die gelovig zijn en opdat Hij uit jullie midden getuigen kiest -- God bemint de onrechtplegers niet --'),
(434, 3, 141, 'en ook opdat God hen die geloven loutert en de ongelovigen vernietigt.'),
(435, 3, 142, 'Of rekenden jullie erop de tuin binnen te gaan, voordat God hen kent die zich van jullie inzetten en hen kent die geduldig volharden.'),
(436, 3, 143, 'Jullie hadden de dood gewenst voordat jullie hem tegenkwamen. Nu hebben jullie hem gezien, terwijl jullie toekeken.'),
(437, 3, 144, 'Mohammed is slechts een gezant; voor zijn tijd reeds waren de [andere] gezanten heengegaan. Wanneer hij dan zou sterven of gedood zou worden zouden jullie je dan op jullie hielen omdraaien? Maar wie zich op zijn hielen omdraait die brengt aan God geen enkele schade toe. God zal hen die dank betuigen belonen.'),
(438, 3, 145, 'Iemand kan alleen maar met Gods toestemming sterven volgens een vastgelegde voorbeschikking. Wie de beloning van het tegenwoordige leven wenst, aan hem zullen Wij er iets van geven. En wie de beloning van het hiernamaals wenst, aan hem zullen Wij er iets van geven. En Wij zullen hen die dank betuigen belonen.'),
(439, 3, 146, 'Hoeveel profeten zijn er niet geweest met wie grote menigten medestreden? Zij versaagden niet vanwege wat hen op Gods weg trof, zij verzwakten niet en zij gaven zich niet over. God bemint hen die geduldig volharden.'),
(440, 3, 147, 'Zij zeiden enkel maar: "Onze Heer, vergeef ons onze zonden en dat wij onze bevoegdheid overschreden hebben en maak onze voeten stevig en help ons tegen de ongelovige mensen."'),
(441, 3, 148, 'God gaf hun dan de beloning van het tegenwoordige leven en de goede beloning van het hiernamaals. God bemint hen die goed doen.'),
(442, 3, 149, 'Jullie die geloven! Als jullie hun die ongelovig zijn gehoorzamen dan zullen zij maken dat jullie je op je hielen omkeren en jullie zullen dan als verliezers omdraaien.'),
(443, 3, 150, 'Maar nee, God is jullie beschermheer; Hij is de beste helper.'),
(444, 3, 151, 'Wij zullen de harten van hen die ongelovig zijn schrik aanjagen, omdat zij aan God metgezellen toevoegen; iets waarvoor Hij geen machtiging had neergezonden. Hun verblijfplaats is het vuur. Dat is pas een slechte verblijfplaats voor de onrechtplegers!'),
(445, 3, 152, 'God heeft Zijn toezegging waargemaakt toen jullie hen met Zijn toestemming vernietigend versloegen, totdat jullie opgaven, met elkaar erover twistten en ongehoorzaam waren geworden, nadat Hij jullie had laten zien wat jullie liefhebben. Onder jullie zijn er die het tegenwoordige leven wensen en onder jullie zijn er die het hiernamaals wensen. Toen leidde Hij jullie van hen af om jullie op de proef te stellen, maar Hij had jullie al vergiffenis geschonken. God is vol van goedgunstigheid jegens de gelovigen. *'),
(446, 3, 153, 'Toen jullie er bergopwaarts vandoor gingen zonder je voor iemand om te draaien, hoewel de gezant jullie van achteren toeriep. Toen beloonde Hij jullie met smart voor smart opdat jullie niet bedroefd zouden zijn over wat jullie ontkomen was, noch over wat jullie getroffen had. God is welingelicht over wat jullie doen.'),
(447, 3, 154, 'Toen liet Hij na de smart, ter bescherming slaap neerdalen over jullie waardoor een groep van jullie bevangen werd, maar een andere groep dacht in hun zelfzucht op onterechte manier over God, zoals men in de tijd van onwetendheid dacht. Zij zeiden: "Hebben wij ook enige zeggenschap?" Zeg: "God heeft de gehele zeggenschap." Zij verbergen in hun binnenste wat zij jou niet laten blijken; zij, zeggen: "Als wij ook enige zeggenschap hadden gehad, zouden wij hier niet gesneuveld zijn." Zeg: "Al waren jullie in je huizen gebleven, dan zouden toch zij voor wie het was bepaald dat zij zouden sneuvelen naar hun rustplaatsen zijn opgetrokken." [Dit gebeurt] opdat God wat in jullie binnenste is op de proef stelt en wat in jullie harten is loutert. God weet wat er binnen in de harten is.'),
(448, 3, 155, 'Zij die zich van jullie afkeerden op de dag dat de beide troepenmachten tot een treffen kwamen, zij werden slechts door de satan tot een misstap gebracht wegens iets wat zij begaan hadden. God is vergevend en zachtmoedig.'),
(449, 3, 156, 'Jullie die geloven! Weest niet zoals zij die ongelovig zijn en die, wanneer zij op de aarde rondtrekken of op veldtocht zijn, van hun broeders zeggen: "Als zij bij ons waren gebleven, dan waren zij niet gestorven en niet gedood." [Zij zeggen het] opdat God dat tot [een reden voor] wroeging in hun harten zou maken. God geeft leven en laat sterven. God doorziet wat jullie doen.'),
(450, 3, 157, 'Als jullie op Gods weg sneuvelen of sterven, dan is vergeving van God en barmhartigheid beter dan wat zij bijeenbrengen.'),
(451, 3, 158, 'Als jullie sterven of sneuvelen dan zullen jullie tot God verzameld worden.'),
(452, 3, 159, 'Vanwege barmhartigheid van God was jij soepel voor hen, maar als jij streng en hardvochtig was geweest dan waren zij rondom jou uiteengegaan. Reken het hun dus maar niet aan en vraag vergeving voor hen en raadpleeg hen over het beleid. Wanneer je dan een besluit genomen hebt vertrouw dan op God. God bemint hen die vertrouwen hebben.'),
(453, 3, 160, 'Als God jullie helpt, kan niemand jullie verslaan, maar als Hij jullie in de steek laat, wie is er die jullie daarna nog helpen kan? Op God is het dat de gelovigen hun vertrouwen moeten stellen.'),
(454, 3, 161, 'Het komt een profeet niet toe dat hij [iets van de buit] ontvreemdt. Wie iets ontvreemdt zal wat hij heeft ontvreemd meebrengen op de opstandingsdag. Dan zal aan iedereen vergoed worden wat hij verdiend heeft en hun zal geen onrecht worden aangedaan.'),
(455, 3, 162, 'Is dan iemand die Gods welgevallen navolgt als iemand die zich gramschap van God op de hals haalt? Hun verblijfplaats is de hel. Dat is pas een slechte bestemming!'),
(456, 3, 163, 'Zij zijn verschillend in rang bij God. God doorziet wel wat zij doen.'),
(457, 3, 164, 'God heeft de gelovigen een gunst bewezen toen Hij bij hen een gezant uit hun midden liet opstaan, die hun Zijn tekenen voorleest, hen loutert en hun het boek en de wijsheid onderwijst, ook al verkeerden zij vroeger in duidelijke dwaling.'),
(458, 3, 165, 'Of toen jullie onheil trof dat jullie zelf al dubbel hadden toegebracht. Jullie zeiden: "Hoe komt dit?" Zeg: "Het komt van jullie zelf." God is almachtig.'),
(459, 3, 166, 'Wat jullie trof op de dag dat de beide troepenmachten tot een treffen kwamen was met Gods toestemming en [het gebeurde] opdat Hij de gelovigen kent'),
(460, 3, 167, 'en opdat Hij de huichelaars kent. Tot hen werd gezegd: "Komt en strijdt op Gods weg of verdrijft [de vijand]." Zij zeiden: "Als wij van de strijd geweten hadden dan waren wij jullie zeker gevolgd." Op die dag waren zij dichter bij het ongeloof dan bij het geloof; met hun monden zeiden zij wat in hun harten niet was. God weet het best wat zij verbergen.'),
(461, 3, 168, 'Zij die van hun broeders, terwijl zijzelf thuisbleven, zeiden: "Als zij naar ons geluisterd hadden, waren zij niet gedood." Zeg: "Weert dan de dood van jullie zelf af, als jullie gelijk hebben."'),
(462, 3, 169, 'En denk van hen die op Gods weg gedood worden niet dat zij dood zijn; zij zijn juist levend bij hun Heer, waar in hun onderhoud wordt voorzien,'),
(463, 3, 170, 'terwijl zij zich verheugen over wat God hun van Zijn genade geeft en zich verblijden dat de achterblijvers die zich nog niet bij hen gevoegd hebben niets te vrezen hebben noch bedroefd zullen zijn. *'),
(464, 3, 171, 'Zij verblijden zich over een genade en een gunst van God en dat God het loon van de gelovigen niet verloren laat gaan,'),
(465, 3, 172, 'die God en Zijn gezant gehoor geven na de slag die hen getroffen heeft. Voor diegenen onder hen die goed doen en die godvrezend zijn is er een geweldig loon.'),
(466, 3, 173, 'Tot wie de mensen zeiden: "De mensen hebben zich tegen jullie verzameld; vreest hen dus." Maar dat deed hun geloof alleen maar toenemen en zij zeiden: "God is voor ons goed genoeg; Hij is een voortreffelijke voogd."'),
(467, 3, 174, 'Dus keerden zij weerom met genade en gunst van God. Geen kwaad overkwam hen en zij volgden Gods welgevallen na. God is vol van geweldige goedgunstigheid.'),
(468, 3, 175, 'Dat was slechts de satan. Hij boezemt [jullie] vrees in voor zijn aanhangers. Vreest hen niet, maar vreest Mij als jullie gelovig zijn.'),
(469, 3, 176, 'Laat je niet bedroefd maken door hen die wedijveren in ongeloof; zij brengen God geen enkele schade toe. God wenst voor hen geen aandeel in het hiernamaals tot stand te brengen. Voor hen is er een geweldige bestraffing.'),
(470, 3, 177, 'Zij die voor het geloof ongeloof gekocht hebben; zij brengen God geen enkele schade toe. Voor hen is er een pijnlijke bestraffing.'),
(471, 3, 178, 'Zij die ongelovig zijn moeten er niet op rekenen dat het uitstel dat Wij hun verlenen voor hen beter is; Wij verlenen hun slechts uitstel opdat zij in zonde toenemen. Voor hen is er een vernederende bestraffing.'),
(472, 3, 179, 'God is niet zo dat Hij de gelovigen in de toestand laat waarin jullie nu zijn totdat Hij de slechte van de goede onderscheidt. God is niet zo dat Hij jullie op de hoogte stelt van het onzichtbare, maar God verkiest uit Zijn gezanten wie Hij wil. Gelooft dan in God en Zijn gezanten. En als jullie geloven en godvrezend zijn dan is er voor jullie een geweldig loon.'),
(473, 3, 180, 'Zij die gierig zijn met wat God hun van Zijn goedgunstigheid gegeven heeft, moeten niet denken dat het goed voor hen is. Welnee, het is slecht voor hen; op de opstandingsdag zal dat waarmee zij gierig waren hen om de nek worden gehangen. Aan God behoort de erfenis toe van de hemelen en de aarde. God is welingelicht over wat jullie doen.'),
(474, 3, 181, 'God heeft de uitspraak wel gehoord van hen die zeiden: "God is arm en wij zijn rijk." Wij zullen opschrijven wat zij zeiden en ook dat zij de profeten zonder enig recht doodden en Wij zullen zeggen: "Proeft de straf van de verbranding."'),
(475, 3, 182, 'Dat is voor wat jullie handen vroeger gedaan hebben en omdat God de dienaren niet onrechtvaardig behandelt.'),
(476, 3, 183, 'Zij die zeiden: "God heeft ons opgedragen geen gezant te geloven totdat hij ons een offer zou brengen dat door het vuur wordt verteerd." Zeg: "Tot jullie zijn al voor mijn tijd gezanten gekomen met de duidelijke bewijzen en met dat wat jullie zeggen. Waarom hebben jullie hen dan gedood, als jullie gelijk hebben."'),
(477, 3, 184, 'Als zij jou dan van leugens betichten; al voor jouw tijd zijn er gezanten van leugens beticht, die kwamen met de duidelijke bewijzen, met de Zoeboer en het verlichtende boek.'),
(478, 3, 185, 'Ieder zal de dood proeven, maar jullie volle loon zal jullie op de opstandingsdag gegeven worden. Wie dan van het vuur gevrijwaard wordt en in de tuin binnengebracht, die heeft gezegevierd. Het tegenwoordige leven is slechts het genot van de begoocheling. *'),
(479, 3, 186, 'Jullie zullen zeker op de proef gesteld worden in jullie bezittingen en in jullie zelf en jullie zullen zeker van hen aan wie voor jullie tijd het boek gegeven was en van de aanhangers van het veelgodendom veel ergerlijks horen. Als jullie geduldig volharden en godvrezend zijn, dan is dat een zaak van vastbeslotenheid.'),
(480, 3, 187, 'En toen God met hen aan wie het boek gegeven is het verdrag aanging: "Jullie moeten het aan de mensen duidelijk maken en het niet verbergen." Toen veronachtzaamden zij het achterbaks en verkwanselden het, maar wat zij ervoor gekocht hebben dat is pas slecht.'),
(481, 3, 188, 'Denk niet dat zij die zich verheugen over wat zij bereikt hebben en die graag geprezen worden voor wat zij niet gedaan hebben, denk toch niet dat zij voor de straf in veiligheid zijn. Voor hen is er een pijnlijke bestraffing.'),
(482, 3, 189, 'God heeft de heerschappij over de hemelen en de aarde en God is almachtig.'),
(483, 3, 190, 'In de schepping van de hemelen en de aarde en het verschil van nacht en dag zijn tekenen voor mensen die verstand hebben,'),
(484, 3, 191, 'die God staande, zittende en op hun zij liggende gedenken en die over de schepping van de hemelen en de aarde nadenken: "Onze Heer, U hebt dit niet voor niets geschapen -- U zij geprezen! -- en behoed ons voor de bestraffing van het vuur.'),
(485, 3, 192, 'Onze Heer, wie door U het vuur wordt binnengevoerd, die wordt door U te schande gemaakt. De onrechtplegers zullen geen helpers hebben.'),
(486, 3, 193, 'Onze Heer, wij hebben iemand die opriep tot het geloof gehoord: \'Gelooft in jullie Heer!? Dus geloven wij. Onze Heer, vergeef ons onze zonden en wis onze slechte daden uit en neem ons met de vromen weg.'),
(487, 3, 194, 'Onze Heer, en geef ons wat U ons door Uw gezanten hebt toegezegd en maak ons niet te schande op de opstandingsdag. U zult de afspraak niet afzeggen."'),
(488, 3, 195, 'Toen verhoorde hun Heer hen: "Ik laat het werk van iemand van jullie die [goed] doet niet verloren gaan, of het nu een man is of een vrouw; jullie horen bij elkaar. Zij dus die uitgeweken zijn, uit hun woningen verdreven zijn, op Mijn weg tegenspoed geleden hebben, gestreden hebben en gesneuveld zijn, van hen zal Ik de slechte daden kwijtschelden en Ik zal hen tuinen binnen laten gaan waar de rivieren onderdoor stromen; als beloning van God. En God, bij Hem is de goede beloning.'),
(489, 3, 196, 'Laat je niet begoochelen doordat zij die ongelovig zijn in het land rondtrekken.'),
(490, 3, 197, 'Een weinig genot! Dan zal hun verblijfplaats de hel zijn. Dat is pas een slechte rustplaats!'),
(491, 3, 198, 'Maar zij die hun Heer vrezen, voor hen zijn er tuinen waar de rivieren onderdoor stromen; zij zullen daarin altijd blijven: een gastverblijf van God. Wat bij God is, is het beste voor de vromen.'),
(492, 3, 199, 'Onder de mensen van het boek zijn er die in God geloven, in wat naar jullie is neergezonden en in wat tot hen is neergezonden, terwijl zij zich deemoedig aan God onderwerpen. Zij verkwanselen Gods tekenen niet. Zij zijn het voor wie hun loon bij hun Heer is. God is snel met de afrekening.'),
(493, 3, 200, 'Jullie die geloven! Weest geduldig, volhardend, standvastig en vreest God. Misschien zal het jullie welgaan.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'O mensen, vreest jullie Heer die jullie uit één wezen geschapen heeft, die uit hem zijn echtgenote schiep en die uit hen beiden vele mannen en vrouwen heeft voortgebracht en [over de aarde] verspreid. Vreest God uit wiens naam jullie elkaar iets vragen en respecteert de verwantschapsbanden. God is opziener over jullie.'),
(495, 4, 2, 'Geeft de wezen hun bezittingen, ruilt het onbetamelijke niet in voor het goede en verteert hun bezittingen niet samen met jullie eigen bezittingen; dat is een grote zonde.'),
(496, 4, 3, 'En als jullie vrezen ten aanzien van de wezen niet juist te handelen, trouwt dan met zoveel vrouwen als jullie goeddunkt, twee, drie of vier. Maar als jullie vrezen [haar] niet rechtvaardig te kunnen behandelen, dan met één of met slavinnen waarover jullie beschikken. Dat is het meest voor de hand liggend om onrechtvaardigheid te voorkomen.'),
(497, 4, 4, 'Geeft de vrouwen haar bruidsgiften als een schenking, maar als zij jullie uit eigen beweging iets ervan toestaan, gebruikt het dan met genoegen en voldoening.'),
(498, 4, 5, 'Geeft jullie bezittingen, die God jullie als middelen van bestaan verschaft heeft, niet aan de dwazen maar voorziet ermee in hun onderhoud en kleedt hen en spreekt tot hen op een vriendelijke manier.'),
(499, 4, 6, 'En toetst de wezen totdat zij de leeftijd om te trouwen bereikt hebben en als jullie bij hen dan redelijk inzicht bemerken, overhandigt hun dan hun bezittingen. En verteert ze niet verkwistend en overijld voordat zij groot geworden zijn. Wie rijk is moet er afblijven en wie arm is moet er in redelijkheid van gebruiken. En als jullie hun dan hun bezittingen overhandigen laat er dan getuigen bij aanwezig zijn. God is goed genoeg om af te rekenen.'),
(500, 4, 7, 'De mannen hebben een aandeel in wat de ouders en de verwanten nalaten en de vrouwen hebben een aandeel in wat de ouders en de verwanten nalaten of het nu weinig is of veel: een vastgesteld aandeel.'),
(501, 4, 8, 'En wanneer bij de verdeling de verwanten, de wezen en de behoeftigen aanwezig zijn, geeft hun daarvan dan iets voor hun levensonderhoud en spreekt tot hen op een vriendelijke manier.'),
(502, 4, 9, 'Zij die bevreesd zouden zijn, als zij zelf weerloze nakomelingen zouden achterlaten, moeten oppassen. Zij moeten God vrezen en passende woorden spreken.'),
(503, 4, 10, 'Zij die de bezittingen van de wezen onrechtmatig verteren, verteren slechts vuur in hun buik en zij zullen in een vuurgloed braden.'),
(504, 4, 11, 'God draagt jullie met betrekking tot jullie kinderen op: voor een mannelijk [kind] evenveel als het aandeel van twee vrouwelijke. Als er echter alleen maar vrouwen zijn en wel meer dan twee, dan is tweederde van wat hij nalaat voor haar. Maar als er maar één is dan is de helft voor haar. Voor de beide ouders: elk van beiden een zesde van wat hij nalaat, als hij kinderen heeft. Als hij geen kinderen heeft en zijn ouders erven van hem dan is een derde voor zijn moeder, maar als hij broers heeft dan is een zesde voor zijn moeder. [Dit geldt] na[dat rekening gehouden is met] een [testamentaire] beschikking die hij heeft gemaakt of een schuld. Jullie vaders en jullie zonen; jullie weten niet wie van hen jullie in nuttigheid het meest nastaat. [Dit is] een verplichting van God. God is wetend en wijs. *'),
(505, 4, 12, 'Voor jullie is de helft van wat jullie echtgenotes nalaten als zij geen kinderen hebben. Als zij kinderen hebben dan is een kwart van wat zij nalaten voor jullie. [Dit geldt] na[dat rekening gehouden is met] een [testamentaire] beschikking die zij hebben gemaakt of een schuld. Voor haar is een kwart van wat jullie nalaten als jullie geen kinderen hebben. Als jullie kinderen hebben dan is een achtste van wat jullie nalaten voor haar. [Dit geldt] na[dat rekening gehouden is met] een [testamentaire] beschikking die jullie hebben gemaakt of een schuld. Als er van een man of een vrouw in de zijlinie geërfd wordt terwijl hij een broer of een zuster heeft dan is er voor elk van beiden een zesde, maar als het er meer zijn, dan delen zij samen in een derde. [Dit geldt] na[dat rekening gehouden is met] een [testamentaire] beschikking die gemaakt is of een schuld. Er moet niet benadeeld worden! [Dit geldt] als een beschikking van God. God is wetend en zachtmoedig.'),
(506, 4, 13, 'Dat zijn Gods bepalingen. Wie aan God en Zijn gezant gehoorzaamt die zal Hij binnenvoeren in tuinen waar de rivieren onderdoor stromen, daarin zullen zij altijd blijven. Dat is de geweldige triomf!'),
(507, 4, 14, 'En wie ongehoorzaam is aan God en Zijn gezant en Zijn bepalingen overtreedt die zal Hij binnenvoeren in een vuur waarin hij altijd zal blijven. Voor hem is er een vernederende bestraffing.'),
(508, 4, 15, 'Als er onder jullie vrouwen iemand is die een gruweldaad begaat, roept dan uit jullie midden vier getuigen op tegen haar. Als zij dan getuigenis geven, geeft haar dan huisarrest, totdat de dood haar wegneemt of God haar een uitweg biedt.'),
(509, 4, 16, 'En de twee uit jullie midden die dat begaan, moeten jullie beiden straffen. Maar als zij berouw tonen en zich beteren, laat hen dan met rust. God is een genadegever en barmhartig.'),
(510, 4, 17, 'Het is Gods taak om zich genadig tot hen te wenden die het verkeerde uit onwetendheid doen en dan spoedig berouw tonen. Zij zijn het tot wie God zich genadig wendt. God is wetend en wijs.'),
(511, 4, 18, 'Maar de genadevolle aandacht geldt niet voor hen die verkeerde dingen blijven doen totdat een van hen, als de dood nabij is, zegt: "Nu heb ik berouw" en ook niet voor hen die als ongelovigen sterven. Zij zijn het voor wie Wij een pijnlijke bestraffing hebben klaargemaakt.'),
(512, 4, 19, 'Jullie die geloven! Het is jullie niet toegestaan vrouwen tegen haar wil te erven en houdt haar niet tegen om iets van wat jullie haar gegeven hebben mee te nemen, behalve als zij een overduidelijke gruweldaad hebben begaan. En gaat vriendelijk met haar om. Als jullie een afkeer van haar hebben, dan zijn jullie misschien wel afkerig van iets waar God toch veel goeds in heeft gelegd.'),
(513, 4, 20, 'En als jullie in plaats van een echtgenote een andere wensen te nemen en als jullie aan één van haar een fortuin gegeven hebben, neemt daarvan dan niets. Willen jullie het soms in lasterlijke slechtheid en duidelijke zonde nemen?'),
(514, 4, 21, 'Hoe kunnen jullie het nemen terwijl jullie al tot elkaar gekomen zijn en zij met jullie een solide overeenkomst zijn aangegaan?'),
(515, 4, 22, 'En trouwt niet met vrouwen met wie jullie vaders getrouwd geweest zijn, behalve als het al gebeurd is. Dat is iets gruwelijks en afschuwelijks en een slechte manier van doen.'),
(516, 4, 23, 'Verboden [om mee te trouwen] zijn voor jullie je moeders, dochters, zusters, tantes van vaders- en moederskant, dochters van broers en zusters, zoogmoeders en zoogzusters, de moeders van jullie vrouwen en jullie stiefdochters die onder jullie hoede staan en die geboren zijn uit jullie vrouwen met wie jullie daadwerkelijk gemeenschap hebben gehad -- als jullie nog geen gemeenschap met haar hadden gehad, dan is het voor jullie geen vergrijp -- en de echtgenotes van jullie lijfelijke zonen en dat jullie twee zusters samen als vrouw hebben, behalve als het al is gebeurd. God is barmhartig en vergevend. *'),
(517, 4, 24, 'En ook [verboden om mee te trouwen zijn] de eerbaar getrouwde vrouwen, behalve slavinnen waarover jullie beschikken. Het is Gods voorschrift voor jullie. Daarnaast is het jullie toegestaan er met jullie bezittingen in eerbaarheid en niet in ontucht naar te streven. Voor het genot dat jullie van haar ontvangen, geeft haar daarvoor haar loon; een verplichting is het. Het geldt voor jullie niet als overtreding als jullie daarboven in onderlinge overeenstemming iets regelen nadat de verplichting is vastgelegd. God is wetend en wijs.'),
(518, 4, 25, 'Als iemand van jullie het niet kan opbrengen te trouwen met eerbare gelovige vrouwen, dan kan hij trouwen met gelovige meisjes waarover jullie als slavinnen beschikken. God kent jullie geloof het best. Jullie horen bij elkaar. Trouwt dus met haar met toestemming van haar mensen en geeft haar in redelijkheid haar loon als eerbaar getrouwde vrouwen, die geen ontucht bedrijven en zich geen minnaars nemen. Wanneer men dan eerbaar met haar getrouwd is en als zij dan een gruweldaad begaan, dan geldt voor haar de helft van de bestraffing die voor de eerbaar getrouwde vrouwen geldt. Dat is voor wie uit jullie midden bang zijn zich te bezondigen. Maar dat jullie geduldig volharden is beter voor jullie. God is vergevend en barmhartig.'),
(519, 4, 26, 'God wil jullie duidelijkheid verschaffen, jullie op het goede pad brengen volgens de gewoonten van hen die er voor jullie tijd waren en zich genadig tot jullie wenden. God is wetend en wijs.'),
(520, 4, 27, 'En God wenst zich genadig tot jullie te wenden maar zij die de begeerten najagen wensen dat jullie geweldig gaan afwijken.'),
(521, 4, 28, 'God wenst het voor jullie gemakkelijk te maken; de mens is immers zwak geschapen.'),
(522, 4, 29, 'Jullie die geloven! Verteert niet onderling elkaars bezittingen door bedrog, behalve als het om handel met wederzijdse instemming gaat, en doodt elkaar niet. God is voor jullie barmhartig.'),
(523, 4, 30, 'Wie het in overtreding en onrechtmatigheid toch doet, die zullen Wij in een vuur laten braden; dat is voor God gemakkelijk.'),
(524, 4, 31, 'Als jullie de ernstige vergrijpen vermijden die jullie verboden zijn, dan zullen Wij de slechte daden van jullie kwijtschelden en jullie een voortreffelijke binnenkomst bezorgen.'),
(525, 4, 32, 'Begeert niet iets waarmee God een van jullie boven een ander heeft bevoorrecht. De mannen krijgen een aandeel van wat zij hebben verdiend en de vrouwen krijgen een aandeel van wat zij hebben verdiend. En vraagt God om iets van Zijn goedgunstigheid. God is alwetend.'),
(526, 4, 33, 'Wij hebben gemaakt dat er voor iedereen rechthebbenden zijn op wat de ouders en de verwanten nalaten. En zij met wie jullie door eden verbonden zijn, geeft hun hun aandeel. God is van alles getuige.'),
(527, 4, 34, 'De mannen zijn zaakwaarnemers voor de vrouwen, omdat God de een boven de ander heeft bevoorrecht en omdat zij van hun bezittingen uitgegeven hebben. De deugdzame vrouwen zijn dus onderdanig en zij waken over wat verborgen is, omdat God erover waakt. Maar zij van wie jullie ongezeglijkheid vrezen, vermaant haar, laat haar alleen in de rustplaatsen en slaat haar. Als zij jullie dan gehoorzamen, dan moeten jullie niet proberen haar nog iets aan te doen. God is verheven en groot.'),
(528, 4, 35, 'En als jullie onmin tussen beiden vrezen, zendt dan een scheidsrechter van zijn mensen en een scheidsrechter van haar mensen. Als zij beiden het weer goed willen maken, dan zal God hen met elkaar verzoenen. God is wetend en welingelicht.'),
(529, 4, 36, 'Dient God en voegt aan Hem niets als metgezel toe. En weest goed voor de ouders en ook voor de verwant, de wezen, de behoeftigen, de verwante buur, de niet-verwante buur, de niet-verwante medeburger, hij die onderweg is en slaven waarover jullie beschikken. God bemint niet wie ingebeeld en verwaand zijn,'),
(530, 4, 37, 'die gierig zijn, die de mensen opdragen gierig te zijn, die verbergen wat God hun van Zijn goedgunstigheid gegeven heeft -- voor de ongelovigen hebben Wij een vernederende bestraffing klaargemaakt --'),
(531, 4, 38, 'die hun bezittingen weggeven om door de mensen gezien te worden en die niet in God noch in de laatste dag geloven. En voor wie de satan een kameraad is; een slechte kameraad is dat!'),
(532, 4, 39, 'Wat zou hun kunnen overkomen als zij in God en in de laatste dag zouden geloven en ook iets van wat God hun voor hun levensonderhoud gegeven heeft als bijdragen geven. God is over hen wetend.'),
(533, 4, 40, 'God doet geen greintje onrecht aan en als het een goede daad is zal Hij die verdubbelen en van Zijn kant een geweldig loon geven.'),
(534, 4, 41, 'Hoe zal het dan zijn wanneer Wij uit elke gemeenschap een getuige laten komen en Wij jou als getuige tegen dezen hier laten komen?'),
(535, 4, 42, 'Op die dag zullen zij die ongelovig zijn en ongehoorzaam aan de gezant graag willen dat de aarde met hen gelijkgemaakt wordt. En zij kunnen voor God geen gesprek verbergen.'),
(536, 4, 43, 'Jullie die geloven! Nadert niet tot de salaat, terwijl jullie dronken zijn, zolang jullie niet weten wat jullie zeggen, en ook niet terwijl jullie onrein zijn -- behalve wanneer jullie onderweg voorbijkomen -- zolang jullie je niet eerst wassen. En als jullie ziek zijn of op reis of als iemand van het toilet komt of met vrouwen omgang heeft gehad en jullie vinden geen water, zoekt dan goede kale grond en wrijft jullie gezichten en handen. God is lankmoedig en vergevend.'),
(537, 4, 44, 'Heb jij niet gezien naar hen aan wie een aandeel aan het boek gegeven is, dat zij dwaling kopen en wensen dat jullie van de weg afdwalen?'),
(538, 4, 45, 'God kent jullie vijanden het best. God is goed genoeg als beschermer en God is goed genoeg als helper.'),
(539, 4, 46, 'Onder hen die het jodendom aanhangen zijn er die de woorden verdraaien [door ze] uit hun verband [te halen] en die zeggen: "Wij hebben gehoord en zijn opstandig geworden) en: "Hoor" zonder dat het hoorbaar is en: "Eerbiedig ons" met verdraaiing van hun tongen en belastering van de godsdienst. Als zij maar gezegd hadden: "Wij horen en gehoorzamen" en "Hoor en let op ons" dan was dat beter voor hen en juister, maar God heeft hen om hun ongeloof vervloekt; zij geloven maar weinig.'),
(540, 4, 47, 'Jullie aan wie het boek gegeven is! Gelooft in wat Wij hebben neergezonden ter bevestiging van wat jullie al hebben, voordat Wij gezichten weghalen en aan hun achterkant weer aanbrengen of hen vervloeken, zoals Wij hen van de sabbat vervloekt hebben. Gods beschikking wordt uitgevoerd.'),
(541, 4, 48, 'God vergeeft het niet als men aan Hem metgezellen toevoegt, maar afgezien daarvan vergeeft Hij aan wie Hij wil. Wie aan God metgezellen toevoegt, die heeft een geweldige zonde verzonnen.'),
(542, 4, 49, 'Heb jij niet gezien naar hen die van zichzelf zeggen dat zij gelouterd zijn? Nee toch, God zal louteren wie Hij wil en hun wordt nog geen vezeltje onrecht aangedaan.'),
(543, 4, 50, 'Kijk hoe zij over God leugens verzinnen; dat is genoeg als duidelijke zonde.'),
(544, 4, 51, 'Heb jij niet gezien naar hen aan wie een aandeel aan het boek gegeven is, dat zij geloven aan de Djibt en de Taghoet en over hen die ongelovig zijn zeggen: "Dezen volgen een betere weg dan zij die geloven."'),
(545, 4, 52, 'Zij zijn het die God vervloekt heeft en als God iemand vervloekt, dan zul je voor hem geen helper vinden.'),
(546, 4, 53, 'Of hebben zij een aandeel in de heerschappij? Nu dan! Zij zouden de mensen nog niet de holte in een dadelpit geven.'),
(547, 4, 54, 'Of zijn zij afgunstig op de mensen om wat God hun van Zijn goedgunstigheid gegeven heeft? Maar Wij hebben toch aan de mensen van Ibrahiem het boek en de wijsheid gegeven en Wij hebben hun een geweldige heerschappij gegeven.'),
(548, 4, 55, 'Onder hen zijn er, die erin geloven en onder hen zijn er, die zich ervan afkeren. De hel is als vuurgloed genoeg.'),
(549, 4, 56, 'Zij die ongelovig zijn in Onze tekenen, hen zullen Wij in een vuur laten braden. Telkens als hun huid gaar gebakken is, vervangen Wij die door andere huid, opdat zij de bestraffing proeven. God is machtig en wijs.'),
(550, 4, 57, 'Maar zij die gelovig zijn en de deugdelijke daden doen, hen zullen Wij tuinen laten binnengaan waar de rivieren onderdoor stromen, daarin zullen zij voor immer en altijd blijven. Voor hen zijn er daarin reingemaakte echtgenotes en Wij zullen hen in een beschuttende schaduw laten binnengaan.'),
(551, 4, 58, 'God beveelt jullie in bewaring gegeven goederen aan de rechthebbenden te overhandigen en, wanneer jullie tussen de mensen oordelen, dat jullie rechtvaardig oordelen. Hoe goed is het toch, waarmee God jullie aanspoort. God is horend en doorziend.'),
(552, 4, 59, 'Jullie die geloven! Gehoorzaamt God en gehoorzaamt de gezant en de gezagdragers uit jullie midden. Als jullie met elkaar twisten, legt het dan voor aan God en de gezant, als jullie in God en de laatste dag geloven; dat is beter voor jullie en een mooiere afsluiting.'),
(553, 4, 60, 'Heb jij niet gezien naar hen die beweren te geloven in wat naar jou is neergezonden en in wat er al voor jouw tijd is neergezonden, dat zij voor een uitspraak bij de Taghoet in beroep wensen te gaan, hoewel hun bevolen was daarin niet te geloven. De satan wenst hen ver te laten afdwalen.'),
(554, 4, 61, 'En wanneer tot hen gezegd wordt: "Komt tot wat God heeft neergezonden en tot de gezant" dan zie jij de huichelaars zich duidelijk van jou afkeren.'),
(555, 4, 62, 'En hoe is het dan als hen onheil treft voor wat hun handen tevoren gedaan hebben en als zij dan tot jou komen, terwijl zij bij God zweren: "Wij wensten alleen maar goed te doen en gunstig te stemmen"?'),
(556, 4, 63, 'Dat zijn zij van wie God weet wat er in hun harten is. Wend je van hen af en vermaan hen en zeg doeltreffende woorden tot hen over henzelf.'),
(557, 4, 64, 'Wij hebben de gezanten slechts gezonden om, met Gods toestemming, gehoorzaamd te worden. En als zij, omdat zij zichzelf onrecht aangedaan hebben, tot jou zouden komen en dan God om vergeving zouden vragen en als de gezant voor hen om vergeving zou vragen dan zouden zij merken dat God een genadegever is en barmhartig.'),
(558, 4, 65, 'Maar nee, bij jouw Heer, zij geloven pas [echt] als zij jou tot scheidsrechter maken over wat bij hen omstreden is en als zij dan bij zichzelf geen moeite hebben met wat jij geoordeeld hebt en het volledig aanvaarden.'),
(559, 4, 66, 'En als Wij aan hen voorgeschreven hadden: "Doodt elkaar" of "Vertrekt uit jullie woningen", dan zouden zij het op enkelen na niet doen. Maar als zij zouden doen waartoe zij worden aangespoord dan zou dat beter zijn en een stevigere bevestiging.'),
(560, 4, 67, 'En dan zouden Wij hun van Onze kant een geweldig loon geven.'),
(561, 4, 68, 'En Wij zouden hen op een juiste weg leiden.'),
(562, 4, 69, 'Wie God en de gezant gehoorzamen, dat zijn zij die bij hen behoren aan wie God genade geschonken heeft: de profeten, de oprechten, de [geloofs]getuigen en de rechtschapenen. Dat zijn goede kameraden!'),
(563, 4, 70, 'Dat is de goedgunstigheid van God. God is goed genoeg als wetende.'),
(564, 4, 71, 'Jullie die geloven! Neemt jullie voorzorgsmaatregelen en rukt uit in groepen of rukt gezamenlijk uit.'),
(565, 4, 72, 'Onder jullie zijn er die treuzelen en als jullie dan onheil treft zegt zo iemand: "God heeft mij genade geschonken, omdat ik niet met hen daarbij tegenwoordig was."'),
(566, 4, 73, 'Maar als jullie een gunst van God treft dan zegt hij, alsof er tussen jullie en hem geen vriendschap was: "Ach was ik maar met hen erbij geweest, dan had ik een geweldige triomf behaald." *'),
(567, 4, 74, 'Laten zij die het tegenwoordige leven verkopen voor het hiernamaals dan strijden op Gods weg en wie op Gods weg strijdt en gedood wordt of de overwinning behaalt, hem zullen Wij een geweldig loon geven.'),
(568, 4, 75, 'Wat hebben jullie dat jullie niet op Gods weg strijden en ook niet voor die onderdrukte mannen, vrouwen en kinderen die zeggen: "Onze Heer, breng ons uit deze stad waarvan de inwoners onrecht plegen en breng ons van Uw kant een beschermer en breng ons van Uw kant een helper."'),
(569, 4, 76, 'Zij die geloven strijden op Gods weg en zij die ongelovig zijn strijden op de weg van de Taghoet. Bestrijdt de aanhangers van de satan. De list van de satan is maar zwak!'),
(570, 4, 77, 'Heb jij niet gezien naar hen tot wie gezegd is: "Houdt jullie handen in bedwang, verricht de salaat en geeft de zakaat"? Toen hun dan voorgeschreven werd te strijden, toen was er opeens een groep onder hen die de mensen evenzeer als God vreesde, of nog meer, en die zeiden: "Onze Heer, waarom hebt U ons voorgeschreven te strijden? Had U ons niet nog een kort uitstel kunnen verlenen?" Zeg: "Het genot van het tegenwoordige leven is gering, maar het hiernamaals is beter voor wie godvrezend is en jullie wordt nog geen vezeltje onrecht aangedaan.'),
(571, 4, 78, 'Waar jullie ook zijn, de dood zal jullie bereiken ook al zijn jullie in hooggebouwde burchten." En als hen iets goeds treft zeggen zij: "Dit komt van God." En als hen iets slechts treft zeggen zij: "Dit komt van jou." Zeg: "Alles komt van God." Wat is er met deze mensen aan de hand dat zij een bericht nauwelijks begrijpen?'),
(572, 4, 79, 'Het goede dat jou treft, dat komt van God en het slechte dat jou treft, dat komt van jezelf. Wij hebben jou als gezant tot de mensen gezonden. God is goed genoeg als getuige.'),
(573, 4, 80, 'Wie de gezant gehoorzaamt, die gehoorzaamt God en wie zich afkeert? Wij hebben jou niet gezonden als iemand die over hen waakt.'),
(574, 4, 81, 'En zij zeggen:"Gehoorzaamheid!" Maar als zij bij jou wegtrekken dan besluit een groep van hen heimelijk iets anders dan zij zeggen, maar God schrijft op wat zij heimelijk besluiten. Wend je dus van hen af en stel je vertrouwen op God. God is goed genoeg als voogd.'),
(575, 4, 82, 'Overpeinzen zij de Koran dan niet? Als hij van een ander dan God [gekomen] was dan zouden zij er veel tegenstrijdigs in vinden.'),
(576, 4, 83, 'En wanneer tot hen een bericht over veiligheid of vrees komt, dan verspreiden zij het. Als ze het echter naar de gezant en naar de gezagdragers onder hen verwezen hadden dan zouden zij uit hun midden die gevolgtrekkingen kunnen maken, er kennis van hebben kunnen nemen. En zonder Gods goedgunstigheid jegens jullie en Zijn barmhartigheid zouden jullie op enkelen na de satan gevolgd hebben.'),
(577, 4, 84, 'Strijd dus op Gods weg -- jij wordt slechts voor jezelf aansprakelijk gesteld -- en spoor de gelovigen aan. Misschien dat God het geweld van hen die ongelovig zijn zal afwenden. Gods geweld is groter en Zijn strafvoltrekking is strenger.'),
(578, 4, 85, 'Wie met een goede voorspraak bemiddelt, krijgt zijn aandeel daarin en wie met een slechte voorspraak bemiddelt krijgt zijn portie daarvan. God houdt op alles toezicht.'),
(579, 4, 86, 'En wanneer men jullie met een groet begroet, groet dan op een betere manier terug of beantwoordt de groet. God rekent over alles af.'),
(580, 4, 87, 'God, er is geen god dan Hij. Hij zal jullie bijeenbrengen voor de opstandingsdag, waaraan geen twijfel is. En wie is er oprechter met berichten dan God?'),
(581, 4, 88, 'Hoe komt het dan dat er bij jullie met betrekking tot de huichelaars twee groepen zijn, terwijl God hen toch heeft laten terugvallen voor wat zij begaan hebben. Willen jullie dan hen die God tot dwaling gebracht heeft op het goede pad brengen? Wie door God tot dwaling gebracht wordt, voor hem vind je geen weg.'),
(582, 4, 89, 'Zij zouden graag willen dat jullie ongelovig werden, zoals zij dat zijn; dan zouden jullie gelijk zijn. Neemt van hen dus niemand als medestander zolang zij niet uitwijken op Gods weg. Als zij zich afkeren, grijpt hen dan en doodt hen waar jullie hen vinden. Neemt van hen niemand als medestander of als helper.'),
(583, 4, 90, 'Behalve hen die zich aansluiten bij mensen met wie jullie een verdrag hebben of die met beklemd gemoed tot jullie komen omdat zij tegen jullie zouden moeten strijden of tegen hun eigen mensen strijden. Als God gewild had, dan had Hij hun macht over jullie gegeven en dan hadden zij zeker tegen jullie gestreden. Als zij zich van jullie afzijdig houden, niet tegen jullie strijden en jullie vrede aanbieden dan verschaft God jullie geen weg om tegen hen op te treden.'),
(584, 4, 91, 'Jullie zullen anderen vinden die voor jullie veilig wensen te zijn en evenzo voor hun eigen mensen. Telkens als zij aan de beproeving worden blootgesteld worden zij daardoor misleid. Als zij zich dan niet van jullie afzijdig houden, jullie geen vrede aanbieden, noch hun handen in bedwang houden, doodt hen dan waar jullie hen aantreffen. Zij zijn het over wie Wij jullie een duidelijk gezag hebben verleend.'),
(585, 4, 92, 'Het komt een gelovige niet toe een gelovige te doden, tenzij per abuis. En als iemand per abuis een gelovige doodt dan geldt de vrijlating van een gelovige slaaf en bloedgeld dat wordt overhandigd aan zijn familie, behalve als het door hen als aalmoes wordt geschonken. Als hij tot mensen behoorde die vijanden van jullie zijn, terwijl hij wel gelovig was, dan geldt als vergelding de vrijlating van een gelovige slaaf, maar als hij tot mensen behoorde met wie jullie een verdrag hebben, dan bloedgeld dat wordt overhandigd aan zijn familie en de vrijlating van een gelovige slaaf. Als iemand niets te geven vindt, dan een vasten van twee maanden achter elkaar als genadegave van God. God is wetend en wijs.'),
(586, 4, 93, 'En wie een gelovige opzettelijk doodt, zijn vergelding is de hel, waarin hij altijd blijft. God is vertoornd op hem en vervloekt hem en maakt een geweldige bestraffing voor hem klaar.'),
(587, 4, 94, 'Jullie die geloven! Wanneer jullie op Gods weg rondtrekken, zorgt dan dat jullie duidelijke inlichtingen inwinnen en zegt niet tot iemand die jullie de vredesgroet aanbiedt: "Jij bent geen gelovig" in jullie streven naar het stoffelijk gewin van het tegenwoordige leven. Bij God zijn toch vele mogelijkheden tot buit. Zo waren jullie vroeger, maar God heeft jullie een gunst bewezen. Wint dus duidelijke inlichtingen in. God is over wat jullie doen welingelicht.'),
(588, 4, 95, 'De thuisblijvers onder de gelovigen -- afgezien van hen die gebreken hebben -- en zij die zich inspannen op Gods weg met hun bezittingen en hun eigen persoon zijn niet gelijk. God bevoorrecht hen die zich op Gods weg met hun bezittingen en hun eigen persoon inspannen met een rang boven de thuisblijvers. God heeft allen het beste toegezegd, maar zij die zich met hun bezittingen en hun eigen persoon inspannen worden door God meer dan de thuisblijvers bevoorrecht met een geweldig loon,'),
(589, 4, 96, 'met rangen van Hem afkomstig, met vergeving en met barmhartigheid. God is vergevend en barmhartig.'),
(590, 4, 97, 'Tot hen die door de engelen worden weggenomen, terwijl zij zichzelf onrecht hebben aangedaan, zeggen zij: "In wat voor toestand verkeerden jullie?" Zij zeggen dan: "Wij waren onderdrukten op de aarde." Zij zeggen: "Was Gods aarde niet zo ruim dat jullie daarin konden uitwijken?" Zij zijn het van wie de verblijfplaats de hel is; dat is een slechte bestemming.'),
(591, 4, 98, 'Maar niet voor die onderdrukte mannen, vrouwen en kinderen die tot geen list in staat zijn en die geen goede weg gewezen werd.'),
(592, 4, 99, 'Zij zijn het aan wie God misschien vergiffenis zal schenken. God is lankmoedig en vergevend. *'),
(593, 4, 100, 'Wie op Gods weg uitwijkt vindt op de aarde vele uitwijkmogelijkheden en ruimte. Wie zijn huis uitgaat om uit te wijken naar God en Zijn gezant en wie dan de dood bereikt, diens beloning is Gods taak. God is vergevend en barmhartig.'),
(594, 4, 101, 'Wanneer jullie op de aarde rondtrekken dan is het voor jullie geen vergrijp als jullie de salaat verkorten als jullie vrezen dat zij die ongelovig zijn jullie in verzoeking brengen. De ongelovigen zijn voor jullie een duidelijke vijand.'),
(595, 4, 102, 'En wanneer jij bij hen bent en voor hen [als voorganger] de salaat verricht, dan moet een groep van hen met jou de salaat verrichten en zij moeten hun wapens bij zich houden. Wanneer zij zich eerbiedig neerbuigen moeten zij achter jou zijn. Dan moet de andere groep komen die de salaat niet gebeden heeft. Zij moeten dan met jou de salaat bidden en zij moeten hun voorzorgsmaatregelen treffen en hun wapens bij zich houden. Zij die ongelovig zijn zouden graag willen dat jullie niet op jullie wapens en goederen zouden letten, zodat zij in één keer een uitval tegen jullie konden doen. Het is geen vergrijp voor jullie als jullie last van de regen hebben of ziek zijn om dan de wapens af te leggen. Maar treft voorzorgsmaatregelen. God heeft voor de ongelovigen een vernederende bestraffing klaargemaakt.'),
(596, 4, 103, 'En wanneer jullie de salaat beëindigd hebben, gedenkt dan God staand, zittend en op jullie zij liggend. Wanneer jullie dan weer rust hebben verricht dan de salaat. De salaat is voor de gelovigen een voorschrift voor bepaalde tijden.'),
(597, 4, 104, 'Versaagt niet in de achtervolging van de mensen. Als jullie pijn lijden dan lijden zij net zoals jullie pijn, maar jullie hopen van God wat zij niet kunnen hopen. God is wetend en wijs.'),
(598, 4, 105, 'Wij hebben het boek met de waarheid naar jou neergezonden, opdat jij tussen de mensen oordeelt met wat God jou heeft getoond. En wees geen voorvechter voor de verraders.'),
(599, 4, 106, 'En vraag God om vergeving. God is vergevend en barmhartig.'),
(600, 4, 107, 'Pleit niet ten gunste van hen die aan zichzelf verraad plegen. God bemint niet wie een verrader en een zondaar is.'),
(601, 4, 108, 'Zij verbergen zich voor de mensen, maar zij kunnen zich niet voor God verbergen, want Hij is bij hen wanneer zij heimelijk besluiten iets te zeggen wat Hem niet welgevallig is. God omvat wat zij doen.'),
(602, 4, 109, 'Zo is dat met jullie; zij zijn het voor wie jullie in het tegenwoordige leven gepleit hebben, maar wie zal op de opstandingsdag voor hen bij God pleiten of wie zal voor hen garant staan?'),
(603, 4, 110, 'Wie iets slechts doet of zichzelf onrecht aandoet en dan God om vergeving vraagt zal merken dat God vergevend en barmhartig is.'),
(604, 4, 111, 'En wie een zonde begaat, begaat die slechts ten koste van zichzelf. God is wetend en wijs.'),
(605, 4, 112, 'En wie een fout of een zonde begaat en er dan iemand die onschuldig is van beschuldigt, die heeft een lasterlijke slechtheid en duidelijke zonde op zich geladen.'),
(606, 4, 113, 'En zonder Gods goedgunstigheid jegens jou en Zijn barmhartigheid zou een groep van hen van zins geweest zijn jou tot dwaling te brengen, maar zij brengen slechts zichzelf tot dwaling en zij berokkenen jou geen enkele schade. En God heeft tot jou het boek en de wijsheid neergezonden en Hij heeft jou wat jij niet wist onderwezen. Gods goedgunstigheid jegens jou is geweldig. *'),
(607, 4, 114, 'Er is niets goeds in veel van hun vertrouwelijke gesprekken, behalve als iemand aanmaant tot een aalmoes, tot iets redelijks of om tussen de mensen vrede te stichten. Als iemand dat doet in het streven naar Gods tevredenheid, dan zal God hem een geweldig loon geven.'),
(608, 4, 115, 'Als iemand de gezant tegenwerkt nadat de leidraad hem duidelijk geworden is en hij toch niet de weg van de gelovigen volgt, dan zullen Wij hem daarheen brengen waarheen hij zich heeft afgekeerd; Wij zullen hem in de hel laten braden en dat is pas een slechte bestemming!'),
(609, 4, 116, 'God vergeeft het niet als men aan Hem metgezellen toevoegt, maar afgezien daarvan vergeeft Hij aan wie Hij wil. Wie aan Hem metgezellen toevoegt, die is ver afgedwaald.'),
(610, 4, 117, 'Zij roepen buiten Hem slechts vrouwelijke wezens aan; zij roepen slechts een opstandige satan aan.'),
(611, 4, 118, 'God heeft hem vervloekt, maar hij zei: "Ik zal van Uw dienaren een vastgesteld aandeel nemen,'),
(612, 4, 119, 'ik zal hen tot dwaling brengen, ik zal hun begeerten opwekken, ik zal hun bevelen en zij zullen de oren van hun vee afsnijden, ik zal hun bevelen en zij zullen Gods schepping veranderen." Wie zich de satan in plaats van God als beschermer neemt, die zal een duidelijk verlies lijden.'),
(613, 4, 120, 'Hij doet hun toezeggingen en wekt hun begeerten op, maar wat de satan hun toezegt is slechts begoocheling.'),
(614, 4, 121, 'Zij zijn het van wie de verblijfplaats de hel is en zij vinden daaruit geen ontsnapping.'),
(615, 4, 122, 'Maar zij die gelovig zijn en de deugdelijke daden doen, hen zullen Wij tuinen laten binnengaan waar de rivieren onderdoor stromen, daarin zullen zij voor immer en altijd blijven. Dat is in waarheid Gods toezegging. En wie is oprechter dan God in uitspraken?'),
(616, 4, 123, 'Het gebeurt niet volgens jullie verlangens, noch volgens de verlangens van de mensen van het boek. Wie iets slechts doet, aan hem wordt het vergolden en hij vindt in plaats van God beschermer noch helper voor zich.'),
(617, 4, 124, 'Maar wie -- hetzij man of vrouw -- deugdelijke daden doet als gelovige, zij zullen de tuin binnengaan en jullie wordt nog niet zoveel als de holte in een dadelpit onrecht aangedaan.'),
(618, 4, 125, 'Wie heeft er een betere godsdienst dan wie zich geheel aan God overgeeft en goed doet en het geloof volgt van Ibrahiem die het zuivere geloof aanhing? God heeft zich Ibrahiem als boezemvriend genomen.'),
(619, 4, 126, 'Van God is wat in de hemelen en wat op de aarde is en God omvat alle dingen.'),
(620, 4, 127, 'Zij vragen jou om uitsluitsel over de vrouwen. Zeg: "God geeft jullie uitsluitsel over haar en eveneens wat jullie in het boek voorgelezen wordt over de vrouwelijke wezen aan wie jullie niet geven wat haar volgens voorschrift toekomt en over dat jullie begeren met haar te trouwen en over die kinderen die weerloos zijn en dat jullie voor wezen de gerechtigheid moeten handhaven. Het goede dat jullie doen, daarvan weet God."'),
(621, 4, 128, 'En als een vrouw van haar echtgenoot slechte behandeling of afkerigheid vreest dan is het voor beiden geen vergrijp als zij zich met elkaar verzoenen; verzoening is beter, maar mensen geven zich gemakkelijk over aan hebzucht. Als jullie goed doen, dan is God welingelicht over wat jullie doen.'),
(622, 4, 129, 'Jullie zullen niet in staat zijn de vrouwen gelijk te behandelen, hoe graag jullie het ook zouden willen, maar geeft dan ook niet volledig toe aan jullie afkeer, zodat jullie haar alleen laten als een ongehuwde. En als jullie het weer goed maken en godvrezend zijn, dan is God vergevend en barmhartig.'),
(623, 4, 130, 'En als zij beiden scheiden dan zal God aan allebei voldoende van Zijn overvloed geven. God is alomvattend en wijs.'),
(624, 4, 131, 'Van God is wat er in de hemelen en wat er op de aarde is. Wij hebben aan hen die voor jullie tijd het boek gekregen hebben en aan jullie opgedragen God te vrezen. En als jullie ongelovig zijn, welnu, van God is wat er in de hemelen en wat er op de aarde is. God is behoefteloos en lofwaardig.'),
(625, 4, 132, 'Van God is wat er in de hemelen en wat er op de aarde is en God is goed genoeg als voogd.'),
(626, 4, 133, 'Als Hij wil, vaagt Hij jullie, o mensen, weg en brengt anderen. God heeft daarover macht.'),
(627, 4, 134, 'Wie de beloning van het tegenwoordige leven wenst? bij God is dus de beloning van het tegenwoordige leven en het hiernamaals. God is horend en doorziend.'),
(628, 4, 135, 'Jullie die geloven! Weest standvastig in de gerechtigheid als getuigen voor God, al is het tegen jullie zelf of de ouders of de verwanten. Of het nu om een rijke of om een arme gaat, God staat hen beiden zeer na. Volgt dus niet je geneigdheid om niet rechtvaardig te zijn. Maar als jullie verdraaien of jullie afwenden, dan is God welingelicht over wat jullie doen.'),
(629, 4, 136, 'Jullie die geloven! Gelooft in God, in Zijn gezant, in het boek dat Hij heeft neergezonden tot Zijn gezant en in het boek dat Hij vroeger al heeft neergezonden. Maar wie geen geloof hecht aan God, Zijn engelen, Zijn boeken, Zijn gezanten en de laatste dag, die is ver afgedwaald.'),
(630, 4, 137, 'Zij die geloven en dan ongelovig worden, dan weer gelovig worden en dan weer ongelovig en dan hun ongeloof nog vermeerderen? God is niet zo dat Hij hun vergeeft noch brengt Hij hen op het goede pad.'),
(631, 4, 138, 'Zeg de huichelaars aan dat er een pijnlijke bestraffing voor hen is,'),
(632, 4, 139, 'die zich de ongelovigen in plaats van de gelovigen als medestanders nemen. Zoeken zij bij hen de macht? Welnee, de macht behoort geheel aan God toe.'),
(633, 4, 140, 'Hij heeft tot jullie in het boek neergezonden dat jullie, wanneer jullie horen dat men aan Gods tekenen geen geloof hecht of ermee de spot drijft, niet bij hen gaat zitten totdat zij op een ander gesprek overgaan. Anders zijn jullie gelijk aan hen. God brengt de huichelaars en de ongelovigen bijeen in de hel, allen'),
(634, 4, 141, 'die tegenover jullie een afwachtende houding aannemen. Als jullie door God een succes behalen, dan zeggen zij: "Waren wij niet met jullie?" maar als de ongelovigen geluk hebben zeggen zij: "Hebben wij jullie niet overmeesterd maar toch de gelovigen van jullie afgehouden?" God zal op de opstandingsdag tussen jullie oordelen en God zal de ongelovigen geen weg verschaffen om tegen jullie op te treden.'),
(635, 4, 142, 'De huichelaars proberen God te misleiden, maar Hij misleidt hen. Wanneer zij zich opstellen voor de salaat, staan zij daar achteloos om door de mensen gezien te worden en zij gedenken God slechts weinig,'),
(636, 4, 143, 'terwijl zij heen en weer geslingerd worden, niet naar dezen en niet naar die. Wie door God misleid wordt voor hem vind je geen weg.'),
(637, 4, 144, 'Jullie die geloven! Neemt de ongelovigen niet in plaats van de gelovigen als medestander. Wensen jullie aan God een duidelijke machtiging tegen jullie te geven?'),
(638, 4, 145, 'De huichelaars komen in de laagste verdieping van het vuur en jij zult voor hen geen helper vinden,'),
(639, 4, 146, 'behalve voor hen die berouw tonen, zich beteren, zich aan God vasthouden en hun godsdienst geheel aan God wijden; zij zijn het die bij de gelovigen horen en God zal de gelovigen een geweldig loon geven.'),
(640, 4, 147, 'Wat zal God met jullie bestraffing doen als jullie dankbaar zijn en geloven? God is dankbaar en wetend.'),
(641, 4, 148, 'God houdt er niet van dat openlijk over het slechte gesproken wordt, behalve als aan iemand onrecht is aangedaan. God is horend en wetend.'),
(642, 4, 149, 'Als jullie openlijk goed doen of het verbergen of iets slechts vergeven, God is lankmoedig en vrij machtig.'),
(643, 4, 150, 'Zij die geen geloof hechten aan God en Zijn gezanten en die tussen God en Zijn gezanten onderscheid willen maken en zeggen: "Wij geloven in sommigen maar in anderen niet" en die een tussenweg willen nemen,'),
(644, 4, 151, 'dat zijn zij die in waarheid ongelovig zijn. Voor de ongelovigen hebben Wij een vernederende bestraffing klaargemaakt.'),
(645, 4, 152, 'Zij die geloven in God en Zijn gezanten en tussen hen geen enkel onderscheid maken; dat zijn zij aan wie Hij hun loon geeft. God is vergevend en barmhartig.'),
(646, 4, 153, 'De mensen van het boek vragen jou dat jij tot hen een boek uit de hemel neerzendt. Zij hebben Moesa iets nog ergers gevraagd. Zij zeiden namelijk: "Laat ons God in alle openbaarheid zien." Toen greep de donderslag hen wegens hun onrechtmatigheid. Vervolgens namen zij, nadat de duidelijke bewijzen tot hen gekomen waren, het kalf aan; maar dat vergaven Wij hen en Wij gaven Moesa een duidelijke machtiging.'),
(647, 4, 154, 'Wij verhieven de berg boven hen wegens het verdrag met hen, Wij zeiden tot hen: "Gaat eerbiedig neerbuigend de poort binnen", Wij zeiden: "Begaat op de sabbat geen overtredingen" en Wij gingen met hen een solide verdrag aan.'),
(648, 4, 155, 'Maar, vanwege het verbreken van hun verdrag, hun ongeloof aan Gods tekenen, het zonder enig recht doden van de profeten door hen en hun zeggen: "Onze harten zijn onbesneden" -- welnee, God heeft ze verzegeld om hun ongeloof; zij geloven maar weinig --'),
(649, 4, 156, 'en wegens hun ongeloof, hun geweldige kwaadsprekerij over Marjam,'),
(650, 4, 157, 'hun zeggen: "Wij hebben de masieh \'Isa, de zoon van Marjam, Gods gezant gedood." -- Zij hebben hem niet gedood en zij hebben hem niet gekruisigd, maar het werd hun gesuggereerd. Zij die het daarover oneens zijn, verkeren erover in twijfel. Zij hebben er behalve het afgaan op vermoedens geen kennis van; zij hebben hem vast en zeker niet gedood.'),
(651, 4, 158, 'Echter, God heeft hem tot Zich omhooggebracht. God is machtig en wijs.'),
(652, 4, 159, 'Er is niemand van de mensen van het boek die niet voor zijn dood in hem zal geloven en op de opstandingsdag zal hij over hen getuige zijn. --'),
(653, 4, 160, 'Wegens de ongerechtigheid dus van hen die het jodendom aanhangen, hebben Wij hun goede dingen verboden die hun waren toegestaan en ook wegens het versperren van Gods weg voor velen,'),
(654, 4, 161, 'wegens het nemen van woeker, hoewel het hun verboden was, en het door bedrog verteren van de bezittingen van de mensen: Wij hebben voor de ongelovigen onder hen een pijnlijke bestraffing klaargemaakt.'),
(655, 4, 162, 'Maar zij onder hen die een diepgewortelde kennis hebben en de gelovigen, omdat zij geloven in wat naar jou is neergezonden en in wat voor jouw tijd is neergezonden, zij namelijk die de salaat verrichten, die de zakaat geven en die in God en de laatste dag geloven, zij zijn het die Wij een geweldig loon zullen geven.'),
(656, 4, 163, 'Wij hebben aan jou geopenbaard zoals Wij aan Noeh en de profeten na hem geopenbaard hebben. En Wij hebben geopenbaard aan Ibrahiem, Isma\'iel, Ishaak, Ja\'koeb en de stammen, \'Isa, Ajjoeb, Joenoes, Haroen, Soelaimaan -- en Wij hebben aan Dawoed een Zaboer gegeven --'),
(657, 4, 164, 'aan gezanten over wie Wij jou vroeger al verteld hebben en aan gezanten over wie Wij jou niet verteld hebben, -- en God heeft tot Moesa echt gesproken --'),
(658, 4, 165, 'gezanten als verkondigers van goed nieuws en als waarschuwers, opdat de mensen tegen God geen argument zouden hebben na [het optreden van] de gezanten. God is machtig en wijs.'),
(659, 4, 166, 'Maar God getuigt over wat Hij naar jou heeft neergezonden; Hij heeft het neergezonden met Zijn wetenschap. Ook de engelen getuigen; maar God is goed genoeg als getuige.'),
(660, 4, 167, 'Zij die ongelovig zijn en de weg van God versperren zijn ver afgedwaald.'),
(661, 4, 168, 'Zij die ongelovig zijn en onrecht plegen, het komt God niet toe om hun te vergeven, noch om hen op een weg te leiden,'),
(662, 4, 169, 'behalve dan de weg naar de hel, daarin zullen zij voor immer en altijd blijven; dat is voor God gemakkelijk.'),
(663, 4, 170, 'O mensen, de gezant is tot jullie gekomen met de waarheid van jullie Heer. Gelooft dan, het is beter voor jullie. Maar als jullie ongelovig zijn -- van God is wat er in de hemelen en wat er op de aarde is. God is wetend en wijs.'),
(664, 4, 171, 'Mensen van het boek! Gaat niet te ver in jullie godsdienst en zegt over God alleen maar de waarheid. De masieh \'Isa, de zoon van Marjam is Gods gezant en Zijn woord dat Hij richtte tot Marjam en een geest bij Hem vandaan. Gelooft dan in God en Zijn gezanten en zegt niet: "Drie." Houdt daarmee op, het is beter voor jullie. Immers, God is één god. Geprezen zij Hij! Dat Hij een kind zou hebben! Van Hem is wat er in de hemelen en wat er op de aarde is. God is goed genoeg als voogd.'),
(665, 4, 172, 'De masieh versmaadt het niet een dienaar van God te zijn, ook de engelen niet die in [Zijn] nabijheid gebracht zijn. Wie het versmaden Hem te dienen en hoogmoedig zijn, die zal Hij allen tot zich verzamelen.'),
(666, 4, 173, 'Wat hen betreft die geloven en de deugdelijke daden doen, hun zal Hij hun volle loon geven en Hij zal hun van Zijn goedgunstigheid nog meer geven. Maar wat hen betreft die versmaden en hoogmoedig zijn, hen zal Hij straffen met een pijnlijke bestraffing en zij zullen buiten God geen beschermer en geen helper voor zich vinden.'),
(667, 4, 174, 'O mensen! Een bewijs van jullie Heer is tot jullie gekomen en Wij hebben een duidelijk licht naar jullie neergezonden.'),
(668, 4, 175, 'Wat hen betreft die in God geloven en zich aan Hem vasthouden, hen zal Hij in Zijn barmhartigheid en genade binnenvoeren en langs een juiste weg tot Hem leiden.'),
(669, 4, 176, 'Zij vragen jou om uitsluitsel. Zeg: "God geeft jullie uitsluitsel over het erven in de zijlinie." Als iemand aan zijn einde komt zonder dat hij kinderen heeft, maar hij heeft een zuster, dan is voor haar de helft van wat hij nalaat. Hij erft ook van haar als zij geen kinderen heeft. Als zij met twee zusters zijn dan is voor haar tweederde van wat hij nalaat. En als er verscheidene broers en zusters zijn, dan komt de man het aandeel van twee vrouwen toe. God maakt het voor jullie duidelijk, opdat jullie niet dwalen. God is alwetend.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'Jullie die geloven! Komt de verplichtingen na. Voor jullie zijn dieren van het vee toegestaan, behalve wat jullie [hierbij] voorgelezen wordt, mits jullie jachtbuit niet toegestaan achten als jullie in gewijde staat zijn. God bepaalt wat Hij wenst.'),
(671, 5, 2, 'Jullie die geloven! Ontwijdt Gods gewijde symbolen niet, noch de heilige maand, noch de offergave, noch de halsomhangsels, noch hen die zich naar het heilige huis begeven om naar goedgunstigheid van hun Heer en welgevallen te streven. Wanneer jullie niet [meer] in gewijde staat zijn, dan mogen jullie op jacht gaan. En laat de afkeer van bepaalde mensen, omdat zij jullie de weg naar de heilige moskee versperren, jullie er niet toe brengen overtredingen te begaan, maar staat elkaar bij in vroomheid en godvrezendheid en staat elkaar niet bij in zonde en overtreding, maar vreest God. God is streng in de afstraffing.'),
(672, 5, 3, 'Verboden is voor jullie wat vanzelf is doodgegaan, bloed, varkensvlees, vlees van iets waarover iets anders dan God is aangeroepen, het verstikte, het doodgeslagene, het doodgevallene, het doodgestotene, wat wilde dieren hebben aangevreten -- behalve wat jullie geslacht hebben -- en wat geofferd is op de offerstenen en ook dat jullie met pijlen verloten. Dat is een schande. Heden wanhopen zij die aan jullie godsdienst geen geloof hechten. Vreest hen niet, maar vreest Mij. Heden heb Ik jullie godsdienst voor jullie voltooid, Mijn genade aan jullie volledig bewezen en de Islaam [de overgave aan God] als godsdienst voor jullie goedgevonden. Maar als iemand door honger gedwongen wordt zonder tot zonde geneigd te zijn, dan is God vergevend en barmhartig.'),
(673, 5, 4, 'Zij vragen jou wat hun toegestaan is. Zeg: "Toegestaan aan jullie zijn de goede dingen en [de vangst van] die roofdieren die jullie afgericht hebben door ze iets te leren van wat God jullie geleerd heeft." Eet dus van wat zij voor jullie vangen en vermeldt Gods naam erover. En vreest God; God is snel met de afrekening.'),
(674, 5, 5, 'Heden zijn aan jullie de goede dingen toegestaan. Het voedsel van hen aan wie het boek gegeven is, is aan jullie toegestaan en jullie voedsel is aan hen toegestaan en ook de eerbare vrouwen onder de gelovigen en de eerbare vrouwen van hen aan wie voor jullie tijd het boek gegeven is, als jullie haar in eerbaarheid en niet in ontucht haar loon geeft en zonder minnaressen te nemen. Maar wie het geloof verzaakt, diens werk blijft vruchteloos en in het hiernamaals behoort hij tot de verliezers.'),
(675, 5, 6, 'Jullie die geloven! Wanneer jullie je voor de salaat opstellen, wast dan jullie gezichten en jullie handen tot aan de ellebogen en wrijft over jullie hoofden en [wast] jullie voeten tot de enkels. En als jullie onrein zijn, reinigt jullie dan. En als jullie ziek zijn of op reis of als iemand van het toilet komt of met vrouwen omgang heeft gehad en jullie vinden geen water, zoekt dan goede kale grond en wrijft jullie gezichten en handen ermee. God wenst jullie niet iets hinderlijks op te leggen, maar Hij wenst jullie slechts rein te maken en Zijn genade aan jullie volledig te bewijzen. Misschien zullen jullie dank betuigen.'),
(676, 5, 7, 'En gedenkt Gods genade aan jullie en het verdrag dat Hij met jullie aangegaan is toen jullie zeiden: "Wij horen en gehoorzamen." En vreest God. God weet wat er binnen in de harten is.'),
(677, 5, 8, 'Jullie die geloven! Weest standvastig voor God als getuigen van de rechtvaardigheid. En laat de afkeer van bepaalde mensen jullie er niet toe brengen niet rechtvaardig te zijn. Weest rechtvaardig, dat is dichter bij godvrezendheid. En vreest God. God is welingelicht over wat jullie doen.'),
(678, 5, 9, 'God heeft hun die geloven en de deugdelijke daden doen toegezegd dat er voor hen vergeving is en een geweldig loon.'),
(679, 5, 10, 'En zij die ongelovig zijn en Onze tekenen loochenen, zij zijn het die in het hellevuur thuishoren.'),
(680, 5, 11, 'Jullie die geloven! Gedenkt Gods genade aan jullie toen bepaalde mensen erop zonnen hun handen naar jullie uit te strekken, waarop Hij hun handen van jullie afhield. En vreest God. Op God moeten de gelovigen hun vertrouwen stellen.'),
(681, 5, 12, 'God was een verdrag met de Israëlieten aangegaan en Wij riepen uit hun midden twaalf aanvoerders op. En God zei: "Ik ben met jullie. Als jullie de salaat verrichten, de zakaat geven, in Mijn gezanten geloven, hen bijstaan en God een mooie lening geven, dan zal Ik jullie je slechte daden kwijtschelden en jullie binnenvoeren in tuinen waar de rivieren onderdoor stromen. Wie van jullie daarna ongelovig is, die dwaalt af van de correcte weg."'),
(682, 5, 13, 'Maar, vanwege het verbreken van hun verdrag hebben Wij hen vervloekt en hun harten hard gemaakt. Zij verdraaien de woorden [door ze] uit hun verband [te halen] en zij zijn een deel vergeten van dat waartoe zij aangemaand waren. En jij zult steeds weer verraad van hen bespeuren, op enkelen van hen na. Reken het hun maar niet aan en scheld het kwijt. God bemint hen die goed doen.'),
(683, 5, 14, 'En met hen die zeggen: "Wij zijn christenen" zijn Wij een verdrag aangegaan. Maar zij zijn een deel vergeten van dat waartoe zij aangemaand waren. Dus hebben Wij tussen hen vijandschap en haat tot aan de opstandingsdag laten ontstaan. God zal hun meedelen wat zij aan het doen waren.'),
(684, 5, 15, 'Mensen van het boek! Onze gezant is tot jullie gekomen om jullie veel van de dingen die jullie verborgen hielden duidelijk te maken en om veel kwijt te schelden. Tot jullie is van God een licht en een duidelijk boek gekomen.'),
(685, 5, 16, 'God leidt daarmee wie Zijn welgevallen navolgen op de wegen van de vrede, brengt hen met Zijn toestemming uit de duisternis naar het licht en leidt hen op een juiste weg.'),
(686, 5, 17, 'Ongelovig zijn zeker zij die zeggen: "God is de masieh \'Isa, de zoon van Marjam." Zeg: "Wie zou tegen God ook maar iets kunnen uitrichten, als Hij zou wensen dat de masieh \'Isa, de zoon van Marjam met zijn moeder en wie er op de aarde zijn allen zouden omkomen?" Van God is de heerschappij over de hemelen en de aarde en wat er tussen beide is. Hij schept wat Hij wil. God is almachtig.'),
(687, 5, 18, 'De joden en de christenen zeggen: "Wij zijn de kinderen Gods en Zijn beminden." Zeg: "Waarom straft Hij jullie dan voor jullie zonden? Maar nee, jullie zijn mensen die behoren tot hen die Hij geschapen heeft! Hij vergeeft aan wie Hij wil en Hij straft wie Hij wil." Van God is de heerschappij over de hemelen en de aarde en wat tussen beide is. Bij Hem is de bestemming.'),
(688, 5, 19, 'Mensen van het boek! Onze gezant is tot jullie gekomen om jullie na een onderbreking in de gezanten duidelijkheid te brengen opdat jullie niet zouden zeggen: "Tot ons is geen verkondiger van goed nieuws, noch een waarschuwer gekomen." Maar er is een verkondiger van goed nieuws en een waarschuwer tot jullie gekomen! God is almachtig.'),
(689, 5, 20, 'En toen Moesa tot zijn volk zei: "Mijn volk! Gedenkt Gods genade aan jullie toen Hij bij jullie profeten aanstelde en jullie tot koningen maakte en jullie gaf wat Hij niemand van de wereldbewoners gegeven had.'),
(690, 5, 21, 'Mijn volk! Gaat het heilige land binnen dat God jullie voorgeschreven heeft en keert het niet jullie ruggen toe; jullie zullen dan als verliezers omdraaien."'),
(691, 5, 22, 'Zij zeiden: "O Moesa, daarin zijn reusachtig sterke mensen. Wij zullen er niet binnengaan, zolang zij er niet uitgaan. Als zij er uitgaan, dan gaan wij binnen."'),
(692, 5, 23, 'Twee mannen van hen die godvrezend waren, aan wie God genade geschonken had zeiden: "Gaat, hen tegemoet, door de poort naar binnen. Wanneer jullie haar binnengaan dan overwinnen jullie. Op God moeten jullie je vertrouwen stellen als jullie gelovigen zijn."'),
(693, 5, 24, 'Zij zeiden: "O Moesa, wij zullen er nooit binnengaan zolang zij erin zijn. Ga jij maar en jouw Heer en strijdt. Wij blijven wel hier."'),
(694, 5, 25, 'Hij zei: "Mijn Heer, ik heb alleen maar macht over mijzelf en mijn broeder. Zonder ons af van de verdorven mensen."'),
(695, 5, 26, 'Hij zei: "Dan is het voor hen verboden, veertig jaar lang, waarin zij op de aarde zullen rondzwerven. Bekommer je maar niet om de verdorven mensen."'),
(696, 5, 27, 'En lees hun de mededeling over de twee zonen van Adam naar waarheid voor. Toen zij een offer brachten en het van een van beiden werd aangenomen. En het werd van de ander niet aangenomen. Die zei: "Ik sla jou dood!" Hij zei: "God neemt slechts van de godvrezenden aan.'),
(697, 5, 28, 'Ook al strek jij je hand naar mij uit om mij te doden, ik zal mijn hand niet naar jou uitstrekken om jou te doden. Ik vrees God, de Heer van de wereldbewoners.'),
(698, 5, 29, 'Ik wens dat jij de zonde aan mij en jouw zonde over je brengt en dan tot de bewoners van het vuur zult behoren. Dat is de vergelding voor de onrechtplegers."'),
(699, 5, 30, 'Toen zette hij zich ertoe aan om zijn broer te doden en hij doodde hem en zo ging hij tot de verliezers behoren.'),
(700, 5, 31, 'God zond toen een raaf die in de aarde scharrelde om hem te tonen hoe hij het lijk van zijn broer kon bedekken. Hij zei: "Wee mij! Ben ik niet in staat om zoals deze raaf te zijn en het lijk van mijn broer te bedekken?" Zo ging hij behoren tot hen die wroeging hebben.'),
(701, 5, 32, 'Derhalve hebben Wij aan de Israëlieten voorgeschreven dat wie iemand doodt, anders dan voor doodslag of wegens verderf zaaien op de aarde, is alsof hij de mensen gezamenlijk heeft gedood en dat wie iemand laat leven is alsof hij de mensen gezamenlijk heeft laten leven. Onze gezanten zijn tot hen met de duidelijke bewijzen gekomen, maar velen van hen waren daarna op de aarde toch onmatig.'),
(702, 5, 33, 'De vergelding van hen die tegen God en Zijn gezant oorlog voeren en erop uit trekken om op de aarde verderf te zaaien zal zijn, dat zij ter dood gebracht zullen worden, of gekruisigd, of dat hun handen en hun voeten aan tegenovergestelde kanten worden afgehouwen, of dat zij uit het land verbannen worden. Dat is voor hen een schande in het tegenwoordige leven en in het hiernamaals is er voor hen een geweldige bestraffing.'),
(703, 5, 34, 'Afgezien van hen die berouw tonen voordat jullie hen overmeesteren. Weet dan dat God vergevend en barmhartig is.'),
(704, 5, 35, 'Jullie die geloven! Vreest God en zoekt naar het middel om nader tot Hem te komen en zet jullie in op Zijn weg. Misschien dat het jullie welgaat.'),
(705, 5, 36, 'Ook al hadden zij die ongelovig zijn alles wat er op de aarde is en nog eens evenveel om zich ermee van de straf van de opstandingsdag vrij te kopen, het zou van hen niet worden aangenomen. Voor hen is er een pijnlijke bestraffing.'),
(706, 5, 37, 'Zij zullen uit het vuur wensen te ontkomen, maar zij kunnen er niet uit ontkomen. Voor hen is er een blijvende bestraffing.'),
(707, 5, 38, 'En de dief en de dievegge, houwt hun de hand af ter vergelding voor wat zij begaan hebben, als een afschrikwekkend voorbeeld van God. God is machtig en wijs.'),
(708, 5, 39, 'Toont echter iemand na zijn misdrijf berouw en betert hij zich, dan zal God zich genadig tot hem wenden. God is vergevend en barmhartig.'),
(709, 5, 40, 'Weet jij niet dat God de heerschappij over de hemelen en de aarde heeft? Hij bestraft wie Hij wil en Hij vergeeft aan wie Hij wil. God is almachtig.'),
(710, 5, 41, 'O gezant, laat je niet bedroefd maken door hen die in ongeloof wedijveren en die behoren bij hen die met hun monden zeggen: "Wij geloven", hoewel hun harten niet geloven. En bij hen die het jodendom aanhangen zijn er die gehoor geven aan leugen. Zij geven gehoor aan andere mensen die niet tot jou gekomen zijn. Zij verdraaien de woorden door ze uit hun verband te halen en zeggen: "Als dit aan jullie gegeven wordt, neemt het dan aan en als het niet aan jullie gegeven wordt weest dan op je hoede." En als God de verzoeking van iemand wenst dan zul jij voor hem niets tegen God kunnen uitrichten. Zij zijn het van wie God de harten niet rein wenst te maken. Voor hen is er in het tegenwoordige leven schande en voor hen is er in het hiernamaals een geweldige bestraffing.'),
(711, 5, 42, 'Zij geven gehoor aan leugen, zij verteren het oneerlijk verkregene! Als zij tot jou komen, oordeel dan tussen hen of wend je van hen af. Als jij je van hen afwendt dan zullen zij jou geen enkele schade berokkenen en als jij oordeelt, oordeel dan tussen hen met rechtvaardigheid. God bemint hen die rechtvaardig handelen.'),
(712, 5, 43, 'Hoe komen zij ertoe jou tot scheidsrechter te maken, terwijl zij toch de Taura bij zich hebben, waarin Gods oordeel staat? Dan zullen zij zich daarna toch afkeren. Zij zijn geen gelovigen!'),
(713, 5, 44, 'Wij hebben de Taura neergezonden met een leidraad erin en een licht, waarmee de profeten die zich [aan God] overgeven oordeel vellen voor hen die het jodendom aanhangen. Zo ook de rabbijnen en de schriftgeleerden, naar wat hun van Gods boek was toevertrouwd en waarvan zij getuigen waren. Vreest dan de mensen niet maar vreest Mij en verkwanselt Mijn tekenen niet. En wie niet oordeel vellen volgens wat God heeft neergezonden, dat zijn de ongelovigen.'),
(714, 5, 45, 'Wij hebben hun daarin voorgeschreven: leven om leven, oog om oog, neus om neus, oor om oor en tand om tand; ook voor verwondingen is er vergelding. Als iemand het dan als aalmoes kwijtscheldt dan geldt dat voor hem als verzoening. En wie niet oordeel vellen volgens wat God heeft neergezonden, dat zijn de onrechtplegers.'),
(715, 5, 46, 'En Wij hebben \'Isa, de zoon van Marjam in hun spoor laten volgen als bevestiger van wat er van de Taura voor zijn tijd al was. Wij gaven hem de Indjiel met een leidraad erin en een licht ter bevestiging van wat er van de Taura voor zijn tijd al was en als een leidraad en aansporing voor de godvrezenden.'),
(716, 5, 47, 'En laten de mensen van de Indjiel oordeel vellen volgens wat God daarin heeft neergezonden. En wie niet oordeel vellen volgens wat God heeft neergezonden, dat zijn de verdorvenen.'),
(717, 5, 48, 'En Wij hebben het boek met de waarheid naar jou neergezonden ter bevestiging van wat er voordien van het boek al was en om erover te waken. Oordeel dan tussen hen volgens wat God heeft neergezonden en volg hun neigingen niet in afwijking van wat van de waarheid tot jou gekomen is. Voor een ieder van jullie hebben Wij een norm en een weg bepaald. En als God het gewild had, zou Hij jullie tot één gemeenschap gemaakt hebben, maar Hij heeft jullie in wat jullie gegeven is op de proef willen stellen. Wedijvert dus in goede daden. Tot God is jullie terugkeer, gezamenlijk. Hij zal jullie dan dat meedelen waarover jullie het oneens waren.'),
(718, 5, 49, 'En oordeel tussen hen volgens wat God heeft neergezonden en volg hun neigingen niet en wees voor hen op je hoede dat zij je niet weglokken van een deel van wat God tot jou heeft neergezonden. Als zij zich afkeren, weet dan dat God hen wenst te treffen voor sommige van hun zonden. En veel van de mensen zijn echt verdorven.'),
(719, 5, 50, 'Is het de rechtspraktijk uit de tijd van onwetendheid die zij nastreven? Wie heeft een betere rechtspraktijk dan God voor mensen die overtuigd zijn?'),
(720, 5, 51, 'Jullie die geloven! Neemt de joden en de christenen niet als medestanders. Zij zijn onderling medestanders. Wie van jullie zich als medestander bij hen aansluit, die behoort bij hen. God wijst de mensen die onrecht plegen de goede richting niet.'),
(721, 5, 52, 'Jij ziet toch dat zij die in hun harten een ziekte hebben zich naar hen toe haasten; zij zeggen: "Wij vrezen dat een wending [van het lot] ons zal treffen." Misschien dat God succes brengt of een beschikking van Zijn kant. Dan zullen zij wroeging krijgen over wat zij in zichzelf geheim houden.'),
(722, 5, 53, 'En zij die geloven zullen zeggen: "Zijn zij dat die bij God dure eden zwoeren dat zij met jullie zouden zijn? Hun daden waren vruchteloos en zij zijn verliezers geworden."'),
(723, 5, 54, 'Jullie die geloven! Als iemand van jullie zich van zijn godsdienst afkeert, dan zal God wel met mensen komen die Hij bemint en die Hem beminnen, die inschikkelijk zijn voor de gelovigen en streng tegen de ongelovigen, die zich inspannen op Gods weg en die niemands verwijt vrezen. Dat is Gods goedgunstigheid die Hij geeft aan wie Hij wil. God is alomvattend en wetend.'),
(724, 5, 55, 'Jullie medestander is God en Zijn gezant en ook zij die geloven, de salaat verrichten en de zakaat geven terwijl zij buigen.'),
(725, 5, 56, 'Wie zich aansluit bij God, Zijn gezant en hen die geloven -- Gods partij -- zij zijn de overwinnaars!'),
(726, 5, 57, 'Jullie die geloven! Neemt niet hen, die jullie godsdienst met spot en scherts bejegenen, uit het midden van hen aan wie het boek voor jullie tijd gegeven was, noch de ongelovigen als medestanders. En vreest God als jullie gelovig zijn.'),
(727, 5, 58, 'Wanneer jullie oproepen tot de salaat, bejegenen zij dat met spot en scherts. Dat komt omdat zij mensen zijn die geen verstand hebben.'),
(728, 5, 59, 'Zeg: "Mensen van het boek! Koesteren jullie alleen maar wrok tegen ons omdat wij geloven in God, in wat naar ons is neergezonden en in wat vroeger is neergezonden en omdat de meesten van jullie verdorven zijn?"'),
(729, 5, 60, 'Zeg: "Zal ik jullie iets meedelen wat nog slechter is, als vergelding bij God? Wie door God is vervloekt en op wie Hij vertoornd is en van wie Hij er tot apen en varkens gemaakt heeft en die de Taghoet dienen; zij zijn het die de slechtste plaats hebben en die het verst afgedwaald zijn van de correcte weg."'),
(730, 5, 61, 'En wanneer zij tot jullie komen zeggen zij: "Wij geloven", maar zij komen in ongeloof binnen en zo gaan zij naar buiten. God weet het best wat zij verborgen hielden.'),
(731, 5, 62, 'Jij ziet velen van hen in zonde en overtreding wedijveren en in het verteren van het oneerlijk verkregene.'),
(732, 5, 63, 'Als de rabbijnen en de schriftgeleerden hun maar zouden verbieden zondige dingen te zeggen en het oneerlijk verkregene te verteren. Wat zij aan het doen waren was pas echt slecht!'),
(733, 5, 64, 'En de joden zeggen: "Gods hand is gebonden." Hun handen zijn gebonden en zij worden vervloekt om wat zij zeggen! Welnee, Gods handen zijn beide wijd opengespreid; Hij schenkt weg hoe Hij wil. Velen nemen door wat van jouw Heer naar jou is neergezonden nog toe in onbeschaamdheid en ongeloof. En Wij brachten tussen hen vijandschap en haat teweeg tot aan de opstandingsdag. Telkens als zij een vuur ontsteken dat tot oorlog leidt, dan dooft God het. En zij trekken eropuit om op de aarde verderf te zaaien. God bemint de verderfzaaiers niet.'),
(734, 5, 65, 'Als de mensen van het boek zouden geloven en godvrezend zouden zijn, zouden Wij hun slechte daden uitwissen en hen de tuinen van de gelukzaligheid binnenvoeren.'),
(735, 5, 66, 'En als zij zich aan de Taura en de Indjiel en wat van hun Heer naar jou is neergezonden zouden houden, zouden zij kunnen eten van wat boven hen is en van wat onder hun voeten is. Onder hen is er een gematigde gemeenschap, maar voor velen van hen geldt: slecht is het wat zij doen.'),
(736, 5, 67, 'O gezant, verkondig wat van jouw Heer tot jou is neergezonden en als jij dat niet doet dan heb jij Zijn zendingsopdracht niet verkondigd. God beschut jou voor de mensen. God wijst de ongelovigen de goede richting niet.'),
(737, 5, 68, 'Zeg: "Mensen van het boek! Jullie baseren jullie op niets zolang jullie je niet houden aan de Taura en de Indjiel en aan wat van jullie Heer naar jullie is neergezonden." Velen nemen door wat van jouw Heer naar jou is neergezonden nog toe in onbeschaamdheid en ongeloof. Bekommer je maar niet om de ongelovige mensen.'),
(738, 5, 69, 'Voorwaar diegenen die geloven en de Joden en de Sabiers en de Christenen en wie er in God en de oordeelsdag geloven en de goede werken verrichten, voor hen zal er geen angst zijn, zij zullen niet treuren.'),
(739, 5, 70, 'Wij zijn een verdrag aangegaan met de Israëlieten en Wij hebben tot hen gezanten gezonden. Telkens als er een gezant tot hen kwam met iets wat hun niet zon betichtten zij sommigen van leugens en anderen doodden zij.'),
(740, 5, 71, 'En zij dachten dat er geen verzoeking zou zijn; zij waren blind en doof. Toen wendde God zich genadig tot hen, maar velen van hen werden weer blind en doof. God doorziet wel wat zij doen.'),
(741, 5, 72, 'Ongelovig zijn zij die zeggen: "God is de masieh, de zoon van Marjam." Maar de masieh heeft gezegd: "O Israëlieten, dient God, mijn Heer en jullie Heer." Als iemand aan God metgezellen toevoegt, dan ontzegt God hem de tuin; zijn verblijfplaats is het vuur. De overtreders zullen geen helpers hebben.'),
(742, 5, 73, 'Ongelovig zijn zij die zeggen dat God een derde van drie is. Maar er is geen andere god dan één god. En als zij niet ophouden met wat zij zeggen, dan zal een pijnlijke bestraffing diegenen van hen treffen die ongelovig zijn.'),
(743, 5, 74, 'Zullen zij zich dan niet berouwvol tot God wenden en Hem om vergeving vragen? God is vergevend en barmhartig.'),
(744, 5, 75, 'De masieh, de zoon van Marjam, is alleen maar een gezant aan wie de andere gezanten zijn voorafgegaan en zijn moeder was een oprechte vrouw; beiden aten zij voedsel. Kijk hoe Wij voor hen de tekenen duidelijk maken en kijk dan hoe zij worden afgeleid.'),
(745, 5, 76, 'Zeg: "Zullen jullie in plaats van God iets dienen wat voor jullie niets schadelijks en niets nuttigs kan uitrichten." God is de horende, de wetende.'),
(746, 5, 77, 'Zeg: "Mensen van het boek! Gaat in jullie godsdienst niet te ver aan de waarheid voorbij! En volgt niet de neigingen van mensen die vroeger al dwaalden en die velen hebben misleid en die van de correcte weg afgedwaald zijn."'),
(747, 5, 78, 'Die Israëlieten die ongelovig waren zijn vervloekt bij monde van Dawoed en \'Isa, de zoon van Marjam. Dat was omdat zij opstandig en vijandig waren.'),
(748, 5, 79, 'Zij hielden elkaar niet af van het verwerpelijke dat zij deden. Wat zij deden was pas echt slecht.'),
(749, 5, 80, 'Jij ziet velen van hen bijstand verlenen aan hen die ongelovig zijn. Wat zijzelf tevoren deden was pas echt slecht, zodat God vergramd op hen is. En in de bestraffing zullen zij altijd blijven.'),
(750, 5, 81, 'Als zij geloofd hadden in God en de profeet en in wat naar hem is neergezonden, dan hadden zij hen niet als medestanders genomen, maar velen van hen zijn verdorvenen. *'),
(751, 5, 82, 'Jij zult merken dat de vijandigste mensen jegens hen die geloven de joden en de aanhangers van het veelgodendom zijn en je zult merken dat zij in genegenheid het dichtst staan bij hen die geloven die zeggen: "Wij zijn christenen." Dat komt omdat onder hen priesters en monniken zijn en omdat zij niet hoogmoedig zijn.'),
(752, 5, 83, 'Wanneer zij wat naar de gezant is neergezonden horen dan zie jij hun ogen van tranen overstromen wegens de waarheid die zij herkennen, terwijl zij zeggen: "Onze Heer, wij geloven. Schrijf ons dus op bij hen die getuigen.'),
(753, 5, 84, 'Waarom zouden wij niet in God geloven en in wat er van de waarheid tot ons is gekomen en begeren dat onze Heer ons samen met de rechtschapen mensen binnenvoert?"'),
(754, 5, 85, 'God beloont hen voor wat zij zeggen met tuinen waar de rivieren onderdoor stromen, daarin zullen zij altijd blijven. Dat is de beloning voor hen die goed doen.'),
(755, 5, 86, 'Maar zij die ongelovig zijn en Onze tekenen loochenen, zij zijn het die in het hellevuur thuishoren.'),
(756, 5, 87, 'Jullie die geloven! Zegt niet dat de goede dingen die God jullie heeft toegestaan verboden zijn en begaat geen overtredingen; God bemint de overtreders niet.'),
(757, 5, 88, 'En eet van wat God jullie als levensonderhoud gegeven heeft, als iets wat toegestaan en goed is. En vreest God in Wie jullie geloven.'),
(758, 5, 89, 'God rekent jullie de onnadenkende uitspraken bij jullie eden niet aan, maar Hij rekent jullie aan waartoe jullie je in eden verbinden. De verzoening ervoor is aan tien behoeftigen voedsel te geven zoals jullie gemiddeld aan je huisgenoten voedsel geven of het kleden van hen of de vrijlating van een slaaf. Als iemand niets te geven vindt, dan een vasten van drie dagen. Dat is een verzoening voor jullie eden, wanneer jullie zweren. En houdt jullie aan je eden. Zo maakt God Zijn tekenen aan jullie duidelijk; misschien zullen jullie dank betuigen.'),
(759, 5, 90, 'Jullie die geloven! De wijn, het kansspel, de offerstenen en de verlotingspijlen zijn een gruwel van satans makelij. Vermijdt die dus; misschien zal het jullie welgaan.'),
(760, 5, 91, 'De satan wenst slechts vijandschap en haat tussen jullie te veroorzaken door de wijn en het kansspel en door jullie van het gedenken Gods en van de salaat af te houden. Zullen jullie dan ophouden?'),
(761, 5, 92, 'En gehoorzaamt God en gehoorzaamt de gezant en als jullie je afkeren, weet dan dat Onze gezant alleen maar de plicht van de duidelijke verkondiging heeft.'),
(762, 5, 93, 'Voor hen die geloven en de deugdelijke daden doen is er geen vergrijp ten aanzien van het voedsel dat zij nemen, als zij maar godvrezend zijn en geloven en de deugdelijke daden doen en dan voorts godvrezend zijn en geloven en voorts godvrezend zijn en goed doen. God bemint hen die goed doen.'),
(763, 5, 94, 'Jullie die geloven! God zal jullie op de proef stellen met iets van de jachtbuit waarvan jullie handen en jullie speren zich meester maken, opdat God weet wie Hem in het verborgene vreest. Als iemand daarna overtredingen begaat, dan is er voor hem een pijnlijke bestraffing.'),
(764, 5, 95, 'Jullie die geloven! Doodt geen jachtwild als jullie in gewijde staat zijn. Als iemand van jullie het opzettelijk doodt dan geldt als vergelding een stuks vee gelijk aan wat hij gedood heeft, ter beoordeling van twee rechtvaardigen uit jullie midden, als een offergave die de Ka\'ba moet bereiken, of als verzoening het spijzigen van behoeftigen of een vasten die daarmee overeenkomt, zodat hij het walgelijke van zijn gedrag proeft. God scheldt wat er vroeger gebeurd is kwijt, maar als iemand het weer doet, dan neemt God wraak op hem. God is machtig en wraakgierig.'),
(765, 5, 96, 'Toegestaan voor jullie is wat op zee gevangen wordt en voedsel uit haar als levensmiddelen voor jullie en voor de reizigers, maar verboden is voor jullie wat op het land gejaagd wordt zolang jullie in gewijde staat zijn. En vreest God tot Wie jullie verzameld zullen worden.'),
(766, 5, 97, 'God heeft de Ka\'ba, het heilige huis, gemaakt tot een ondersteuning voor de mensen en evenzo de heilige maand, de offergave en de halsomhangsels. Dat is opdat jullie weten dat God weet wat er in de hemelen en wat er op de aarde is en dat God alwetend is.'),
(767, 5, 98, 'Weet dat God streng in de afstraffing is en dat God vergevend en barmhartig is.'),
(768, 5, 99, 'De gezant heeft alleen maar de plicht van de verkondiging, maar God weet wat jullie openlijk en in het verborgene doen.'),
(769, 5, 100, 'Zeg: "Het onbetamelijke en het goede zijn niet gelijk, ook al bevalt jullie de veelheid van het onbetamelijke. Vreest God dus, o verstandigen; misschien zal het jullie welgaan."'),
(770, 5, 101, 'Jullie die geloven! Vraagt niet naar dingen die jullie zullen kwellen als ze jullie bekend worden. Maar als jullie ernaar vragen, wanneer de Koran wordt neergezonden, dan worden ze jullie bekend. God heeft ze kwijtgescholden. God is vergevend en zachtmoedig.'),
(771, 5, 102, 'Mensen van voor jullie tijd hebben het gevraagd, maar toen schonken zij er toch geen geloof aan.'),
(772, 5, 103, 'God heeft niets ingesteld over de kameelmerrie met gespleten oren, noch over de losgelaten kameelmerrie, noch over de ooi die met een ram een tweeling vormt, noch over de niet meer te belasten kameelhengst. Maar zij die ongelovig zijn verzinnen leugens over God en de meesten van hen zijn niet verstandig.'),
(773, 5, 104, 'En wanneer tot hen gezegd wordt: "Komt tot wat God heeft neergezonden en tot de gezant", zeggen zij: "Dat waarvan wij gemerkt hebben dat onze vaderen eraan gewoon waren is goed genoeg voor ons." En als hun vaderen nu eens niets wisten en het goede pad niet volgden?'),
(774, 5, 105, 'Jullie die geloven! Let op jezelf. Iemand die dwaalt kan jullie geen schade berokkenen wanneer jullie het goede pad volgen. Tot God is jullie terugkeer, gezamenlijk. Hij zal jullie dan meedelen wat jullie aan het doen waren.'),
(775, 5, 106, 'Jullie die geloven! Bij jullie moet het getuigenis, ten tijde van de [testamentaire] beschikking, wanneer iemand van jullie de dood nabij is bestaan uit twee rechtvaardigen uit jullie midden of, als jullie op de aarde rondtrekken en jullie het onheil van de dood treft, twee anderen die niet uit jullie midden zijn. Jullie moeten hen na de salaat vasthouden en zij zullen dan, als jullie twijfelen, bij God zweren: "Wij verkopen het voor geen prijs, zelfs al zou het een verwant betreffen en wij zullen Gods getuigenis niet verbergen; dan zullen wij bij de zondaars behoren."'),
(776, 5, 107, 'Maar als uitkomt dat beiden zich aan een vergrijp schuldig gemaakt hebben, dan moeten er twee anderen in hun plaats komen uit het midden van hen tegen wie die twee zich vergrepen hadden; zij moeten dan bij God zweren: "Ons getuigenis is echt oprechter dan het getuigenis van hen beiden. Wij begaan geen overtredingen; als wij dat doen dan zullen wij bij de onrechtplegers behoren."'),
(777, 5, 108, 'Dat bevordert het best dat zij overeenkomstig de werkelijkheid getuigenis geven. Of zij moeten vrezen dat na hun eden nog weer andere eden worden gegeven. En vreest God en hoort. God wijst de verdorven mensen de goede richting niet.'),
(778, 5, 109, 'Op de dag dat God de gezanten bijeenbrengt en zegt: "Wat heeft men jullie geantwoord?" Dan zeggen zij: "Wij hebben er geen weet van; U bent de kenner van de verborgenheden."'),
(779, 5, 110, 'Toen God zei: "O \'Isa, zoon van Marjam, denk aan Mijn genade aan jou en jouw moeder toen Ik jou sterkte met de heilige geest, zodat jij in de wieg en als volwassene tot de mensen sprak en toen Ik jou het boek onderwees en de wijsheid, de Taura en de Indjiel en toen jij met Mijn toestemming uit klei iets als de vorm van een vogel schiep en er toen in blies en het met Mijn toestemming een vogel was en toen jij blindgeborenen en melaatsen met Mijn toestemming genas en toen jij doden met Mijn toestemming [uit het graf] tevoorschijn liet komen en toen ik de Israëlieten van je afhield toen jij met de duidelijke bewijzen tot hen kwam, waarop zij onder hen die ongelovig waren zeiden dat dit duidelijk slechts toverij was."'),
(780, 5, 111, 'En toen Ik aan de discipelen openbaarde: "Gelooft in Mij en Mijn gezant." Zij zeiden: "Wij geloven. Getuig dat wij ons [aan God] hebben overgegeven."'),
(781, 5, 112, 'Toen de discipelen zeiden: "O \'Isa, zoon van Marjam, kan jouw Heer tot ons een tafel uit de hemel laten neerdalen?" Hij zei: "Vreest God als jullie gelovigen zijn."'),
(782, 5, 113, 'Zij zeiden: "Wij wensen ervan te eten en dat onze harten gerustgesteld worden en dat wij weten dat jij ons de waarheid hebt gezegd en dat wij tot hen behoren die er getuigen van zijn."'),
(783, 5, 114, '\'Isa, de zoon van Marjam zei: "O God, onze Heer, laat tot ons uit de hemel een tafel neerdalen die voor ons een feest zal zijn, voor de eerste van ons en de laatste van ons en een teken van U. En voorzie in ons levensonderhoud; U bent de beste voorziener."'),
(784, 5, 115, 'God zei: "Ik laat haar tot jullie neerdalen. Wie daarna van jullie nog ongelovig is die zal Ik straffen met een bestraffing zoals Ik niemand van de wereldbewoners straf."'),
(785, 5, 116, 'En toen God zei: "O \'Isa, zoon van Marjam, heb jij tot de mensen gezegd: \'Neemt mij en mijn moeder tot goden naast God?" Hij zei: "U zij geprezen! Het past mij niet iets te zeggen waartoe ik geen recht heb. Als ik het gezegd zou hebben dan zou U het geweten hebben. U weet wat in mijn binnenste is, maar ik weet niet wat in Uw binnenste is. U bent de kenner van de verborgenheden.'),
(786, 5, 117, 'Ik heb tot hen alleen maar gezegd wat U mij bevolen hebt: \'Dient God, mijn Heer en jullie Heer? en ik was getuige van hen zolang ik bij hen was. En toen U mij weggenomen had was U de opziener over hen; U bent van alles getuige.'),
(787, 5, 118, 'Als U hen bestraft, dan zijn zij Uw dienaren en als U hun vergeeft dan bent U de machtige, de wijze."'),
(788, 5, 119, 'God zei: "Dit is de dag waarop de oprechten baat hebben van hun oprechtheid. Voor hen zijn er tuinen, waar de rivieren onderdoor stromen, daarin zullen zij voor immer en altijd blijven." God is met hen ingenomen en zij zijn met Hem ingenomen. Dat is de geweldige triomf!'),
(789, 5, 120, 'God heeft de heerschappij over de hemelen en de aarde en wat erin is en Hij is almachtig.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Lof zij God die de hemelen en de aarde geschapen en de duisternis en het licht gemaakt heeft. Toch zeggen zij die ongelovig zijn dat hun Heer gelijken heeft.'),
(791, 6, 2, 'Hij is het die jullie uit klei geschapen en dan een termijn bepaald heeft. Een vastgestelde termijn is er bij Hem. Toch twijfelen jullie.'),
(792, 6, 3, 'En Hij is God in de hemelen en op de aarde. Hij weet wat jullie geheim houden en wat jullie openbaar maken en Hij weet wat jullie ten uitvoer brengen.'),
(793, 6, 4, 'En er komt geen enkele van de tekenen van hun Heer tot hen of zij keren zich ervan af.'),
(794, 6, 5, 'Zij hebben immers de waarheid geloochend toen die tot hen kwam. Maar de mededelingen over dat, waarmee zij de spot dreven zullen tot hen komen.'),
(795, 6, 6, 'Hebben zij niet gezien hoeveel generaties Wij al voor hun tijd vernietigd hebben aan wie Wij op de aarde zoveel macht gegeven hadden, als Wij aan jullie niet hebben gegeven? En Wij hadden de hemel in overvloed over hen laten regenen en Wij hadden rivieren onder hen door laten stromen. Maar toen vernietigden Wij hen om hun zonden en Wij lieten na hen een andere generatie ontstaan.'),
(796, 6, 7, 'Als Wij tot jou een boek in geschreven vorm hadden neergezonden en zij zouden dat dan met hun handen betasten, dan zouden zij die ongelovig zijn zeggen: "Dit is duidelijk slechts toverij."'),
(797, 6, 8, 'En zij zouden zeggen: "Had dan niet een engel tot hem neergezonden kunnen worden?" Maar als Wij een engel zouden neerzenden, zou de beslissing zijn gevallen en dan zouden zij geen uitstel meer gekregen hebben.'),
(798, 6, 9, 'En al hadden Wij hem een engel gemaakt dan hadden Wij hem toch als een man gemaakt; Wij hadden voor hen verhuld wat zijzelf verhullen.'),
(799, 6, 10, 'Er werd al voor jouw tijd met gezanten de spot gedreven, maar zij die hen belachelijk maakten werden door dat waarmee zij de spot dreven ingesloten.'),
(800, 6, 11, 'Zeg: "Reist op de aarde rond en kijkt hoe het einde was van de loochenaars."'),
(801, 6, 12, 'Zeg: "Van wie is wat er in de hemelen en op de aarde is?" Zeg: "Van God." Hij heeft zichzelf barmhartigheid voorgeschreven. Hij zal jullie bijeenbrengen voor de opstandingsdag waaraan geen twijfel is. Zij die zichzelf verloren hebben geloven dus niet. *'),
(802, 6, 13, 'Van Hem is wat er in de nacht en op de dag rust. Hij is de horende, de wetende.'),
(803, 6, 14, 'Zeg: "Zal ik mij iemand anders dan God als beschermer nemen? De grondlegger van de hemelen en de aarde, Hij die voedsel geeft maar niet wordt gevoed!" Zeg: "Mij is bevolen de eerste te zijn van hen die zich [aan God] overgeven"; jij moet niet bij de veelgodendienaars behoren.'),
(804, 6, 15, 'Zeg: "Ik vrees, als ik ongehoorzaam aan mijn Heer ben, de bestraffing op een geweldige dag."'),
(805, 6, 16, 'Van wie het op die dag afgewend wordt, met hem heeft Hij erbarmen en dat is de duidelijke triomf.'),
(806, 6, 17, 'En als God je met tegenspoed treft dan is er niemand die dat kan wegnemen, behalve Hij. En als Hij je met iets goeds treft, Hij is almachtig.'),
(807, 6, 18, 'En Hij is de beheerser die boven Zijn dienaren staat en Hij is de wijze, de welingelichte.'),
(808, 6, 19, 'Zeg: "Wat geldt als het grootste getuigenis?" Zeg: "God is getuige tussen mij en jullie. Deze Koran is aan mij geopenbaard, opdat ik jullie en wie ermee bereikt wordt daarmee waarschuw. Willen jullie werkelijk getuigen dat er samen met God nog andere goden zijn?" Zeg: "Ik getuig het niet." Zeg: "Hij is slechts één god en ik heb niets te maken met de veelgodendienst die jullie bedrijven."'),
(809, 6, 20, 'Zij aan wie Wij het boek gegeven hebben kennen hem zoals zij hun zonen kennen. Zij die zichzelf verloren hebben geloven dus niet.'),
(810, 6, 21, 'En wie is er zondiger dan wie over God bedrog verzint of Zijn tekenen loochent? Het zal de onrechtplegers zeker niet welgaan.'),
(811, 6, 22, 'En op de dag waarop Wij hen allen verzamelen. Dan zullen Wij tot de aanhangers van het veelgodendom zeggen: "Waar zijn dan jullie [zogenaamd goddelijke] metgezellen waarvan jullie het bestaan beweerden?"'),
(812, 6, 23, 'Hun beproeving zal dan slechts zijn dat zij zeggen: "Bij God, onze Heer, wij waren geen veelgodendienaars."'),
(813, 6, 24, 'Kijk hoe zij zichzelf voorliegen en hoe zij kwijt zijn wat zij verzonnen hebben.'),
(814, 6, 25, 'Onder hen zijn er die naar jou luisteren, maar over wier harten Wij hoezen gelegd hebben, zodat zij het niet begrijpen en in hun oren hardhorendheid. En al zien zij elk teken, zij geloven er niet in. Zozeer, dat wanneer zij tot jou komen om met je te twisten, zij die ongelovig zijn zeggen: "Dit zijn slechts fabels van hen die er eertijds waren."'),
(815, 6, 26, 'En zij verbieden het en wenden zich ervan af. Maar zij vernietigen slechts zichzelf en zij beseffen het niet.'),
(816, 6, 27, 'Kon je hen maar zien wanneer zij voor het vuur worden opgesteld en dan zeggen: "Ach werden we maar teruggebracht, dan zouden wij de tekenen van onze Heer niet loochenen en dan zouden wij bij de gelovigen behoren."'),
(817, 6, 28, 'Welnee, voor hen is wat zij vroeger verborgen hielden openlijk zichtbaar. Ook al werden zij teruggebracht dan zouden zij toch terugkeren naar wat hun verboden was. Zij zijn toch leugenaars.'),
(818, 6, 29, 'En zij zeggen: "Er is alleen maar ons tegenwoordige leven en wij worden niet opgewekt."'),
(819, 6, 30, 'Kon je hen maar zien wanneer zij voor hun Heer worden opgesteld, hoe Hij zegt: "Is dit niet de waarheid?" Zij zeggen: "Ja zeker, bij onze Heer." Hij zegt: "Proeft dan de bestraffing ervoor dat jullie ongelovig waren."'),
(820, 6, 31, 'Zij die de ontmoeting met God loochenen zijn verloren. Zozeer, dat wanneer het uur onverwachts tot hen komt, zij zeggen: "Wat hebben wij een spijt van wat wij [in ons leven] veronachtzaamd hebben" terwijl zij hun lasten op hun ruggen dragen. Is het niet slecht wat zij te torsen hebben?'),
(821, 6, 32, 'Het tegenwoordige leven is slechts spel en tijdverdrijf, maar de laatste woning is voor hen die godvrezend zijn werkelijk beter. Hebben jullie dan geen verstand?'),
(822, 6, 33, 'Wij weten dat wat zij zeggen jou bedroeft; jou betichten zij niet van leugens, maar het zijn Gods tekenen die de onrechtplegers ontkennen.'),
(823, 6, 34, 'Al voor jouw tijd zijn er gezanten van leugens beticht geweest, maar de leugens waarvan men hen betichtte en het leed dat hun werd aangedaan verdroegen zij geduldig, totdat Onze hulp tot hen kwam. Er is niemand die Gods woorden veranderen kan; tot jou zijn immers al mededelingen over de gezondenen gekomen.'),
(824, 6, 35, 'En als hun afkerigheid voor jou te erg is en als jij dan in staat bent om een hol in de aarde of een ladder in de hemel te zoeken en hun dan een teken te brengen? Als God het had gewild, had Hij hen op het goede pad bijeengebracht. Wees dus niet een van de dwazen. *'),
(825, 6, 36, 'Alleen zij die horen kunnen gehoor geven. De doden zal God opwekken en dan zullen zij tot Hem worden teruggebracht.'),
(826, 6, 37, 'En zij zeiden: "Had er dan geen teken van zijn Heer tot hem neergezonden kunnen worden?" Zeg: "God heeft de macht om een teken neer te zenden, maar de meesten van hen weten het niet"'),
(827, 6, 38, 'Er is geen dier op de aarde en geen vogel die met zijn vleugels vliegt of zij behoren zoals jullie tot gemeenschappen. Wij hebben in het boek niets veronachtzaamd. Dan zullen zij tot hun Heer worden verzameld.'),
(828, 6, 39, 'Zij die Onze tekenen loochenen zijn doof en stom in de duisternis. God brengt tot dwaling wie Hij wil en Hij brengt wie Hij wil op een juiste weg.'),
(829, 6, 40, 'Zeg: "Hoe lijkt het jullie? Als Gods bestraffing tot jullie komt of het uur tot jullie komt, zullen jullie dan anderen dan God aanroepen, als jullie gelijk hebben?"'),
(830, 6, 41, 'Welnee, Hem zullen jullie aanroepen. Hij zal dan dat waarom jullie tot Hem roepen wegnemen, als Hij het wil, en wat jullie als metgezellen [aan Hem] hebben toegevoegd zullen jullie vergeten.'),
(831, 6, 42, 'Wij hebben tot gemeenschappen voor jouw tijd gezanten gezonden en hun tegenspoed en rampspoed opgelegd opdat zij zich misschien zouden vernederen.'),
(832, 6, 43, 'Hadden zij zich dan niet, toen Ons geweld tot hen kwam, kunnen vernederen. Maar hun harten verhardden zich en de satan maakte wat zij deden aantrekkelijk voor hen.'),
(833, 6, 44, 'En toen zij vergeten waren waartoe zij aangemaand waren, openden Wij voor hen de deuren tot alle dingen, totdat Wij hen, toen zij verheugd waren over wat hun gegeven was, onverwachts grepen; toen waren zij opeens in wanhoop terneergeslagen.'),
(834, 6, 45, 'Zo werden de mensen die onrecht pleegden tot de laatste toe geveld; lof zij God, de Heer van de wereldbewoners.'),
(835, 6, 46, 'Zeg: "Hoe zien jullie het? Als God jullie gehoor en jullie gezichtsvermogen wegneemt en jullie harten verzegelt, welke god is er dan, anders dan God, die het jullie terug kan geven?" Kijk, hoe Wij de tekenen afwisselen. Toch wenden zij zich af.'),
(836, 6, 47, 'Zeg: "Hoe lijkt het jullie? Als Gods bestraffing onverwachts tot jullie komt of in alle openheid, zullen dan anderen dan de mensen die onrecht plegen vernietigd worden?"'),
(837, 6, 48, 'Wij zenden de gezondenen slechts als verkondigers van goed nieuws en als waarschuwers. En wie geloven en zich beteren, zij hebben niets te vrezen noch zullen zij bedroefd zijn.'),
(838, 6, 49, 'Maar zij die Onze tekenen loochenen, hen treft de bestraffing voor het schandelijke dat zij deden.'),
(839, 6, 50, 'Zeg: "Ik zeg niet tot jullie dat ik Gods schatkamers bezit en ook ken ik het verborgene niet, noch zeg ik tot jullie dat ik een engel ben. Ik volg slechts wat aan mij geopenbaard wordt. Zijn de blinde en de ziende gelijk? Denken jullie dan niet na?"'),
(840, 6, 51, 'En waarschuw daarmee hen die vrezen tot hun Heer verzameld te worden; voor hen is er buiten Hem geen helper en geen bemiddelaar, misschien zullen zij godvrezend worden.'),
(841, 6, 52, 'En jaag hen niet weg die hun Heer in de morgen en de avond aanroepen terwijl zij Zijn aangezicht zoeken. Jij hoeft geen enkele rekenschap van hen te vragen en zij hoeven geen enkele rekenschap van jou te vragen zodat jij hen zou wegjagen; dan zou jij tot de onrechtplegers behoren.'),
(842, 6, 53, 'Zo hebben Wij door sommigen anderen in verzoeking gebracht, zodat zij zouden zeggen: "Zijn zij het aan wie God uit ons midden een gunst moest bewijzen?" Weet God niet het best wie de dankbaren zijn?'),
(843, 6, 54, 'En wanneer zij die in Onze tekenen geloven tot jou komen, zeg dan: "Vrede zij met jullie, jullie Heer heeft Zichzelf barmhartigheid voorgeschreven: als iemand van jullie uit onwetendheid het verkeerde doet en dan later berouw toont en het weer goedmaakt, dan is Hij vergevend en barmhartig."'),
(844, 6, 55, 'En zo zetten Wij de tekenen uiteen; ook opdat de weg van de boosdoeners duidelijk wordt.'),
(845, 6, 56, 'Zeg: "Het is mij verboden hen die jullie in plaats van God aanroepen te dienen." Zeg: "Ik volg jullie neigingen niet; dan zou ik dwalen en niet tot hen behoren die op het goede pad gaan."'),
(846, 6, 57, 'Zeg: "Ik steun op een duidelijk bewijs van mijn Heer en jullie loochenen het. Ik heb geen macht over dat wat jullie zouden willen verhaasten. Het oordeel komt alleen God toe. Hij vertelt de waarheid en Hij is de beste van de beslissers."'),
(847, 6, 58, 'Zeg: "Als ik macht had over dat wat jullie zouden willen verhaasten dan zou de beslissing tussen mij en jullie al zijn gevallen; God kent de onrechtplegers het best. *'),
(848, 6, 59, 'Bij Hem zijn de sleutels van het verborgene, alleen Hij kent ze. Hij weet wat er op het vasteland en wat er in de zee is. Er valt geen blad of Hij weet het en er is geen zaadkorrel in het duister van de aarde, niets vochtigs en niets droogs of het staat in een duidelijk boek.'),
(849, 6, 60, 'Hij is het die jullie \'s nachts zal wegnemen en die weet wat jullie overdag begaan hebben. Dan wekt Hij jullie daarin op, opdat een vastgestelde termijn wordt voltooid. Dan is jullie terugkeer tot Hem. Hij zal jullie dan meedelen wat jullie aan het doen waren.'),
(850, 6, 61, 'En Hij is de beheerser die boven Zijn dienaren staat en Hij zendt bewaarders voor jullie, zodat, wanneer de dood tot een van jullie komt, Onze gezanten hem wegnemen; zij veronachtzamen niets.'),
(851, 6, 62, 'Dan worden zij tot God hun ware beschermheer teruggebracht; komt Hem het oordeel niet toe? Hij is de snelste van hen die afrekenen."'),
(852, 6, 63, 'Zeg: "Wie zal jullie uit de duisternis van het vasteland en de zee redden wanneer jullie in deemoed en in het verborgene tot Hem bidden: \'Als U ons hieruit redt dan behoren wij tot hen die dank betuigen.?"'),
(853, 6, 64, 'Zeg: "God zal jullie eruit redden en uit elke benardheid, maar toch blijven jullie veelgodendienaars."'),
(854, 6, 65, 'Zeg: "Hij is het die de macht heeft om van boven vandaan of van onder jullie voeten vandaan een bestraffing tot jullie te zenden of jullie in [verschillende] groeperingen in de war te brengen en elkaars geweld te laten proeven." Kijk hoe Wij de tekenen afwisselen. Misschien zullen zij begrijpen.'),
(855, 6, 66, 'Jouw volk loochent het, maar het is de waarheid. Zeg: "Ik ben geen voogd over jullie."'),
(856, 6, 67, 'Voor elke mededeling is er een moment van bevestiging en jullie zullen het te weten komen.'),
(857, 6, 68, 'Wanneer jij hen ziet die over Onze tekenen kletsen, wend je dan van hen af totdat zij op een ander gesprek overgaan en als de satan maakt dat jij vergeet, blijf dan na het je herinnerd te hebben niet bij de mensen die onrecht plegen zitten.'),
(858, 6, 69, 'Zij die godvrezend zijn hoeven van hen geen enkele rekenschap te vragen. Het is echter een vermaning; misschien zullen zij godvrezend worden.'),
(859, 6, 70, 'En laat hen maar die hun godsdienst als spel en tijdverdrijf beschouwen. Het tegenwoordige leven begoochelt hen. En maan hiermee aan opdat niemand weggevaagd wordt voor wat hij heeft begaan; voor hem is er immers buiten God geen helper en geen bemiddelaar en al zou hij elke [mogelijke] losprijs aanbieden, het zou toch niet van hem aangenomen worden. Zij zijn het die weggevaagd worden voor wat zij begaan hebben. Voor hen is er een drank van gloeiend water en een pijnlijke bestraffing omdat zij ongelovig waren.'),
(860, 6, 71, 'Zeg: "Zullen wij in plaats van God iets aanroepen wat ons niet nut en niet schaadt en ons op onze hielen omdraaien, nadat God ons de goede richting heeft gewezen, als iemand die de satans op de aarde in verbijstering weggelokt hebben, hoewel hij vrienden heeft die hem tot de leidraad oproepen: \'Kom tot ons.?" Zeg: "Gods leidraad dat is de leidraad en ons is bevolen ons aan de Heer van de wereldwezens over te geven.'),
(861, 6, 72, 'En dat wij de salaat moeten verrichten en Hem vrezen. En Hij is het tot wie wij verzameld worden."'),
(862, 6, 73, 'En Hij is het die de hemelen en de aarde in waarheid geschapen heeft. En op de dag dat Hij zegt: "Wees" en het is, is wat Hij zegt de waarheid. Hij heeft de heerschappij op de dag dat op de bazuin geblazen wordt; de kenner van het verborgene en het waarneembare. En Hij is de wijze, de welingelichte.'),
(863, 6, 74, 'En toen Ibrahiem tot zijn vader Azar zei: "Houd jij afgodsbeelden voor goden? Ik zie dat jij en jouw volk in duidelijke dwaling verkeren."'),
(864, 6, 75, 'En zo toonden Wij Ibrahiem het rijk van de hemelen en de aarde, opdat hij ging behoren tot hen die vast overtuigd zijn.'),
(865, 6, 76, 'En toen de nacht hem omhulde zag hij een ster. Hij zei: "Dit is mijn heer." Maar toen zij onderging zei hij: "Ik houd niet van dingen die ondergaan."'),
(866, 6, 77, 'En toen hij de maan zag opkomen zei hij: "Dit is mijn heer." Maar toen zij onderging zei hij: "Als mijn heer mij niet de goede richting wijst zal ik zeker tot de mensen behoren die dwalen."'),
(867, 6, 78, 'En toen hij de zon zag opkomen zei hij: "Dit is mijn heer, deze is groter." Maar toen zij onderging zei hij: "O mensen, ik heb niets te maken met de veelgodendienst die jullie bedrijven.'),
(868, 6, 79, 'Ik wend mijn aangezicht tot Hem die de hemelen en de aarde aangelegd heeft, als een aanhanger van het zuivere geloof en ik behoor niet tot de veelgodendienaars."'),
(869, 6, 80, 'Zijn volk redetwistte met hem. Hij zei: "Willen jullie met mij over God redetwisten, terwijl Hij mij op het goede pad gebracht heeft? Ik vrees wat jullie aan Hem als metgezellen toevoegen niet, behalve als mijn Heer iets wil. Mijn Heer omvat alles met Zijn kennis. Zullen jullie je dan niet laten vermanen?'),
(870, 6, 81, 'En hoe zou ik wat jullie [aan Hem] als metgezellen toevoegen vrezen, als jullie niet vrezen aan God metgezellen toe te voegen, waarvoor Hij geen machtiging heeft neergezonden? Welke van de beide groepen heeft meer recht op veiligheid? Als jullie dat maar wisten?'),
(871, 6, 82, 'Zij die geloven en hun geloof niet door onrecht te plegen verhullen, voor hen is de veiligheid en zij volgen de goede richting."'),
(872, 6, 83, 'Dat was Ons argument dat Wij Ibrahiem tegen zijn volk gegeven hebben. Wij geven hogere rangen aan wie Wij willen. Jouw Heer is wijs en wetend!'),
(873, 6, 84, 'En Wij schonken hem Ishaak en Ja\'koeb; ieder [van hen] hebben Wij de goede richting gewezen en Noeh hadden Wij al eerder de goede richting gewezen en uit zijn nakomelingen Dawoed, Soelaimaan, Ajjoeb, Joesoef, Moesa en Haroen -- en zo belonen wij hen die goed doen --,'),
(874, 6, 85, 'en Zakarijja, Jahja, \'Isa en Iljaas -- ieder behorend tot de rechtschapenen --,'),
(875, 6, 86, 'en Isma\'iel, al-Jasa?, Joenoes en Loet; ieder [van hen] hebben Wij boven de wereldbewoners verkozen.'),
(876, 6, 87, 'En ook sommigen van hun vaderen, hun nakomelingen en hun broeders; Wij hebben hen verkozen en hen naar een juiste weg geleid.'),
(877, 6, 88, 'Dat is Gods leidraad waarmee Hij wie van Zijn dienaren Hij wil de goede weg wijst, maar als zij geloofd hadden dat God metgezellen had dan zou voor hen wat zij deden vruchteloos geworden zijn.'),
(878, 6, 89, 'Zij zijn het aan wie Wij het boek, de oordeelskracht en het profeetschap gegeven hebben, maar als dezen hier er niet in geloven, dan vertrouwen Wij het aan mensen toe die daaraan niet ongelovig zijn.'),
(879, 6, 90, 'Zij zijn het die God op het goede pad geleid heeft; volg dus hun leidraad. Zeg: "Ik vraag jullie er geen loon voor. Het is slechts een vermaning voor de wereldbewoners."'),
(880, 6, 91, 'Zij hebben God niet op Zijn juiste waarde geschat toen zij zeiden: "God heeft niets tot een mens neergezonden." Zeg: "Wie heeft het boek neergezonden dat Moesa als licht en leidraad voor de mensen bracht? Jullie tonen het in geschreven vorm openlijk, maar jullie houden ook veel verborgen. Aan jullie is onderwezen wat jullie niet wisten, jullie niet en jullie vaderen niet." Zeg: "God." En laat hen dan in hun geklets maar schertsen.'),
(881, 6, 92, 'Dit is een gezegend boek dat Wij hebben neergezonden, ter bevestiging van wat er al eerder was en opdat jij de hoofdstad [Mekka] en wie er omheen wonen ermee waarschuwt. Zij die geloven in het hiernamaals geloven erin en zij zijn degenen die hun salaat nakomen.'),
(882, 6, 93, 'En wie is zondiger dan wie over God bedrog verzint of wie zegt: "Aan mij is geopenbaard", terwijl aan hem niets is geopenbaard of wie zegt: "Ik zal iets neerzenden wat gelijk is aan wat God heeft neergezonden." Als jij het maar kon zien wanneer de onrechtplegers in doodsstrijd verkeren terwijl de engelen hun handen naar hen uitstrekken: "Laat jullie zielen eruit! Vandaag krijgen jullie de bestraffing van de schande als vergelding voor het onware dat jullie altijd over God gezegd hebben en omdat jullie Zijn tekenen hoogmoedig afgewezen hebben."'),
(883, 6, 94, 'En nu zijn jullie tot Ons gekomen, ieder afzonderlijk zoals Wij jullie de eerste maal geschapen hebben en jullie hebben wat Wij jullie verleend hadden achter jullie moeten laten. Ook zien Wij jullie bemiddelaars niet bij jullie, van wie jullie beweerden dat zij bij jullie golden als metgezellen [van God]; de band tussen jullie is doorgesneden en dat waarvan jullie het bestaan beweerden zijn jullie kwijt.'),
(884, 6, 95, 'God is het die de zaadkorrels en de pitten laat kiemen; Hij laat het levende uit het dode voortkomen en Hij laat het dode uit het levende voortkomen. Dat is nu God en hoe worden jullie zo afgeleid?'),
(885, 6, 96, 'Hij is het die de dageraad laat aanbreken en Hij heeft de nacht tot een rusttijd gemaakt en de zon en de maan om [de tijd] te berekenen. Dat is de verordening van de machtige, de wetende.'),
(886, 6, 97, 'Hij is het die voor jullie de sterren gemaakt heeft, opdat jullie daarmee de goede richting vinden in de duisternis van het vasteland en de zee; Wij hebben de tekenen uiteengezet voor mensen die weten.'),
(887, 6, 98, 'En Hij is het die jullie uit één wezen heeft laten ontstaan en daarna is er een verblijfplaats en een bewaarplaats. Wij hebben de tekenen uiteengezet voor mensen die begrijpen.'),
(888, 6, 99, 'En Hij is het die vanuit de hemel water laat neerdalen. Wij brengen daarmee plantengroei van allerlei soort voort -- Wij hebben daardoor frisse groene planten voortgebracht waaruit Wij opeengepakte zaadkorrels voortbrengen en uit de palmen, uit de bloeikolf ervan, laaghangende dadeltrossen -- en ook tuinen met wijnstokken, olijf- en granaatappelbomen die deels wel en deels niet op elkaar lijken. Kijk naar de vruchten ervan, wanneer zij vrucht dragen, en naar het rijp worden ervan; daarin zijn zeker tekenen voor mensen die geloven.'),
(889, 6, 100, 'Maar zij hebben de djinn tot metgezellen van God gemaakt, terwijl Hij hen geschapen heeft en zij hebben Hem zonen en dochters toegedicht, zonder enige kennis. Hij zij geprezen en Hij is verheven boven wat zij toeschrijven.'),
(890, 6, 101, 'De schepper van de hemelen en de aarde; hoe zou Hij een kind kunnen hebben, terwijl Hij niet eens een metgezellin heeft en ook alles geschapen heeft? Hij is alwetend.'),
(891, 6, 102, 'Dat is God jullie Heer; er is geen god dan Hij, de schepper van alle dingen. Dient Hem dus; Hij is over alles voogd.'),
(892, 6, 103, 'Hem bereiken de blikken niet, maar Hij bereikt de blikken wel. En Hij is de welwillende, de welingelichte.'),
(893, 6, 104, '"Tot jou zijn al inzichtelijke bewijzen van jouw Heer gekomen. Als iemand dus inzicht heeft, dan is het in zijn eigen voordeel. En als iemand blind is dan is het in zijn eigen nadeel. En ik ben niet iemand die over jullie waakt."'),
(894, 6, 105, 'Zo zetten Wij de tekenen uiteen, ook opdat zij zeggen: "Jij hebt bestudeerd" en opdat Wij het duidelijk maken aan mensen die weten.'),
(895, 6, 106, 'Volg wat jou door je Heer is geopenbaard. Er is geen god dan Hij. En wend je af van de veelgodendienaars.'),
(896, 6, 107, 'Als God het gewild had waren zij geen veelgodendienaars geworden. En jou hebben Wij niet gemaakt tot iemand die over hen waakt en jij bent ook niet voogd over hen.'),
(897, 6, 108, 'En hoont niet hen die zij in plaats van God aanroepen, zodat zij God niet uit vijandigheid zonder kennis gaan honen. Zo hebben Wij voor iedere gemeenschap hun werk mooi laten lijken. Maar hun terugkeer zal tot hun Heer zijn en Hij zal hun dan meedelen wat zij aan het doen waren.'),
(898, 6, 109, 'Zij hebben bij God dure eden gezworen dat zij, als er tot hen een teken zou komen, er zeker aan zouden geloven. Zeg: "De tekenen zijn uitsluitend bij God." En wat zal jullie tot het besef brengen dat zij, wanneer ze komen, toch niet zullen geloven?'),
(899, 6, 110, 'En Wij zullen hun harten en hun ogen omdraaien, zoals zij ook de eerste keer niet geloofd hebben en Wij laten hen in hun onbeschaamdheid doorgaan met dwalen. *'),
(900, 6, 111, 'Ook al zouden Wij de engelen naar hen neerzenden en al zouden de doden tot hen spreken en al zouden Wij voor hen alles in groepen verzamelen dan zouden zij nog alleen maar geloven als God het wilde. Maar de meesten van hen zijn onwetend.'),
(901, 6, 112, 'Zo hebben Wij voor iedere profeet een vijand gemaakt; satans van de mensen en de djinn van wie de een de ander mooilijkende praat influistert om te begoochelen -- en als jouw Heer gewild had, hadden zij het niet gedaan; laat hen toch met wat zij verzinnen --'),
(902, 6, 113, 'en opdat de harten van hen die niet in het hiernamaals geloven naar hem overhellen en opdat zij daarin behagen scheppen en opdat zij begaan wat zij begaan.'),
(903, 6, 114, '"Zal ik mij dan een andere scheidsrechter dan God zoeken, terwijl Hij het is die het boek duidelijk uiteengezet naar jullie heeft neergezonden?" En zij aan wie Wij het boek gegeven hebben weten dat het van jouw Heer vandaan met de waarheid is neergezonden. Behoor dus niet tot hen die twijfelen.'),
(904, 6, 115, 'Zo werd het woord van jouw Heer vervuld in waarheidsgetrouwheid en rechtvaardigheid. Er is niemand die Zijn woorden veranderen kan en Hij is de horende, de wetende.'),
(905, 6, 116, 'En als jij de meerderheid van wie er op de aarde zijn gehoorzaamt dan zullen zij jou van Gods weg laten afdwalen. Zij volgen alleen maar vermoedens en zij gissen slechts.'),
(906, 6, 117, 'Jouw Heer kent wie van Zijn weg afdwaalt het best en Hij kent hen die het goede pad volgen het best.'),
(907, 6, 118, 'En eet van dat waarover Gods naam is vermeld, als jullie in Zijn tekenen geloven.'),
(908, 6, 119, 'Wat hebben jullie dat jullie niet eten van datgene waarover Gods naam is vermeld? Hij heeft voor jullie toch uiteengezet wat Hij jullie heeft verboden, behalve dat waartoe jullie gedwongen worden? Maar velen brengen door hun neigingen, zonder kennis [anderen] tot dwaling. Echter jouw Heer kent de overtreders het best.'),
(909, 6, 120, 'Laat het uiterlijke en het innerlijke van de zonde achterwege; aan hen die zonde begaan zal vergolden worden wat zij begaan hebben.'),
(910, 6, 121, 'En eet niet van iets waarover Gods naam niet is vermeld; dat is een schande. De satans fluisteren hun vrienden in met jullie te twisten en als jullie hun gehoorzamen dan zijn jullie zeker veelgodendienaars.'),
(911, 6, 122, 'Is dan iemand die dood was en die Wij tot leven gebracht hebben en voor wie Wij een licht gemaakt hebben waarin hij onder de mensen wandelt als iemand die bijvoorbeeld in de duisternis is en er niet uit kan komen? Zo hebben Wij voor de ongelovigen wat zij aan het doen waren mooi laten lijken.'),
(912, 6, 123, 'En zo hebben Wij in iedere stad de grootste boosdoeners bestemd om er boze listen te beramen. Zij beramen echter slechts boze listen voor zichzelf, maar zij beseffen het niet.'),
(913, 6, 124, 'En wanneer tot hen een teken komt zeggen zij: "Wij zullen pas geloven wanneer ons hetzelfde gegeven wordt als wat aan Gods gezanten gegeven is." God weet het best waar Hij Zijn zendingsopdracht brengt. Bij God zal hen die boosdoeners zijn kleinering en bestraffing treffen voor wat zij aan boze listen beraamden.'),
(914, 6, 125, 'Maar als God iemand op het goede pad wenst te brengen dan stelt Hij diens hart open voor de overgave [aan God] en als Hij iemand tot dwaling wil brengen dan maakt Hij diens borst zo nauw en beklemd dat het is alsof hij moeizaam naar de hemel opklimt. Zo legt God de gruwelijke straf op aan hen die niet geloven.'),
(915, 6, 126, 'En dit is de weg van jouw Heer, een juiste; Wij hebben de tekenen uiteengezet voor mensen die zich laten vermanen. *'),
(916, 6, 127, 'Voor hen is de woning van de vrede bij hun Heer en Hij is hun beschermer wegens de dingen die zij gewoonlijk deden.'),
(917, 6, 128, 'En op de dag dat Hij hen allen verzamelt: "O djinn, hier bijeenvergaderd! Jullie hebben je veel mensen geworven." En hun aanhangers onder de mensen zeggen: "Onze Heer, wij hebben van elkaar nut gehad en nu hebben wij onze termijn bereikt die U ons hebt gesteld." Dan zegt Hij: "Het vuur is jullie verblijfplaats om er altijd in te blijven, behalve als God het anders wil." Jouw Heer is wijs en wetend.'),
(918, 6, 129, 'En zo maken Wij de onrechtplegers tot helpers voor elkaar om wat zij hebben begaan.'),
(919, 6, 130, '"O djinn en mensen, hier bijeenvergaderd! Zijn er niet gezanten uit jullie midden tot jullie gekomen om jullie van Mijn tekenen te vertellen en om jullie te waarschuwen voor de ontmoeting van jullie op deze dag?" Zij zeggen: "Wij getuigen tegen onszelf." Het tegenwoordige leven heeft hen begoocheld en zij getuigen tegen zichzelf dat zij ongelovigen waren.'),
(920, 6, 131, 'Dat is omdat jouw Heer de steden niet onrechtvaardig vernietigt terwijl hun inwoners onoplettend zijn.'),
(921, 6, 132, 'Voor ieder zijn er rangen voor wat zij gedaan hebben. Jouw Heer let goed op wat zij doen.'),
(922, 6, 133, 'En jouw Heer is de behoefteloze die vol barmhartigheid is. Als Hij wil, vaagt Hij jullie weg en laat na jullie komen wat Hij wil; zoals Hij jullie uit de nakomelingen van andere mensen liet ontstaan.'),
(923, 6, 134, 'Wat jullie aangezegd is komt zeker en jullie zullen er niets tegen kunnen doen.'),
(924, 6, 135, 'Zeg: "O mijn volk handelt naar jullie vermogen; ik doe het ook. En dan zullen jullie wel te weten komen voor wie de uiteindelijke woning is." Maar de onrechtplegers zal het niet welgaan.'),
(925, 6, 136, 'Zij bestemmen voor God een aandeel in het gewas en het vee dat Hij geschapen heeft en zij zeggen: "Dit is voor God -- beweren zij -- en dit is voor de door ons vereerde metgezellen [van God]." Maar wat voor hun [zogenaamd goddelijke] metgezellen bestemd is dat bereikt God niet en wat voor God bestemd is, dat bereikt hun [zogenaamd goddelijke] metgezellen. Slecht is het wat zij oordelen.'),
(926, 6, 137, 'Zo hebben hun [zogenaamd goddelijke] metgezellen voor velen van de veelgodendienaars het doden van hun kinderen aantrekkelijk gemaakt om hen in het verderf te storten en om hun godsdienst voor hen te verhullen. Als God gewild had, hadden zij het niet gedaan; laat hen toch met wat zij verzinnen.'),
(927, 6, 138, 'En zij zeggen: "Dit is onschendbaar vee en gewas, alleen als wij het willen mag iemand dat als voedsel nemen -- beweren zij -- en vee waarvan de ruggen [voor gebruik] verboden zijn en vee waarover zij Gods naam niet vermelden." Het is een verzinsel over Hem; Hij zal aan hen vergelden voor wat zij verzonnen.'),
(928, 6, 139, 'En zij zeggen: "Wat in de buiken van dit vee is, is voorbehouden aan onze mannen en verboden voor onze echtgenotes." Maar als het al dood is dan zijn zij allen deelgenoten. Hij zal aan hen vergelden voor hun toedichting; Hij is wijs en wetend.'),
(929, 6, 140, 'Verloren zijn zij die hun kinderen in verdwazing, zonder kennis doden en die, als verzinsel over God, verbieden wat God hun voor hun levensonderhoud geeft. Zij dwalen en volgen het goede pad niet.'),
(930, 6, 141, 'En Hij is het die tuinen heeft laten ontstaan, met en zonder latwerk om te stutten, en palmen en landbouwgewassen met verschillende vruchtopbrengst en olijf- en granaatappelbomen die deels wel en deels niet op elkaar lijken. Eet van de vruchten ervan, wanneer zij vrucht dragen en geeft wat ervan verplicht is op de dag van hun oogst maar weest niet verkwistend, want Hij bemint de verkwisters niet.'),
(931, 6, 142, 'Onder het vee zijn er dieren voor de last en voor de vacht. Eet van wat God jullie voor je levensonderhoud geeft en volgt de voetstappen van de satan niet; hij is voor jullie een duidelijke vijand.'),
(932, 6, 143, 'Acht paarsgewijze voorkomende dieren: van de schapen twee en van de geiten twee. Zeg: "Zijn het de twee mannetjes die Hij verboden heeft of de twee vrouwtjes of wat de buiken van de twee vrouwtjes bevatten? Deel het mij met kennis mee, als jullie oprecht zijn."'),
(933, 6, 144, 'En van de kamelen twee en van de koeien twee. Zeg: "Zijn het de twee mannetjes die Hij verboden heeft of de twee vrouwtjes of wat de buiken van de twee vrouwtjes bevatten? Of waren jullie er getuigen van toen God jullie dit opdroeg?" En wie is er onrechtvaardiger dan wie over God bedrog verzint om zonder kennis de mensen tot dwaling te brengen? God wijst de mensen die onrecht plegen de goede richting niet.'),
(934, 6, 145, 'Zeg: "Ik vind in wat mij is geopenbaard niets wat voor een eter die zich ermee voedt verboden is, behalve wat vanzelf dood gegaan is of uitgestroomd bloed of varkensvlees -- dat is een gruwel -- of iets schandelijks waarover iets anders dan Gods naam is uitgeroepen, maar als iemand ertoe gedwongen wordt, niet uit begeerte om te overtreden, dan is jouw Heer vergevend en barmhartig."'),
(935, 6, 146, 'Aan hen die het jodendom aanhangen hebben Wij alles met klauwen verboden en van de koeien en het kleinvee hebben Wij hun het vet verboden behalve wat aan hun ruggen of ingewanden vastzit of wat met bot vergroeid is. Dat hebben Wij hun opgelegd als vergelding voor hun begerigheid; Wij spreken toch de waarheid.'),
(936, 6, 147, 'Als zij jou dan van leugens betichten zeg dan: "Jullie Heer is vol alomvattende barmhartigheid maar Zijn geweld kan niet worden afgewend van de misdadige mensen."'),
(937, 6, 148, 'De aanhangers van het veelgodendom zullen zeggen: "Als God gewild had hadden wij geen metgezellen [aan Hem] toegevoegd en onze vaderen ook niet en dan hadden wij ook niets verboden." Zo hebben zij hen die er voor jouw tijd waren ook van leugens beticht totdat zij Ons geweld proefden. Zeg: "Is er bij jullie kennis? Komt er dan voor ons mee voor de dag. Jullie volgen alleen maar vermoedens en jullie gissen slechts."'),
(938, 6, 149, 'Zeg: "God heeft het doeltreffende argument; als Hij gewild had, dan had Hij jullie allen tezamen op het goede pad gebracht."'),
(939, 6, 150, 'Zeg: "Komt op met jullie getuigen die kunnen getuigen dat God dit verboden heeft." Maar als zij getuigenis afleggen, getuig dan niet met hen en volg de neigingen niet van hen die Onze tekenen loochenen en niet van hen die niet in het hiernamaals geloven en zeggen dat hun Heer gelijken heeft.'),
(940, 6, 151, 'Zeg: "Komt, dan zal ik jullie voorlezen wat jullie Heer jullie verboden heeft: Dat jullie aan Hem niets als metgezel mogen toevoegen, -- en om goed te zijn voor de ouders -- dat jullie je kinderen niet mogen doden uit vrees voor armoe," -- Wij voorzien in jullie en hun levensonderhoud -- "dat jullie geen gruwelijkheden mogen benaderen, noch uiterlijk noch innerlijk en dat jullie niemand mogen doden -- wat God verboden heeft -- behalve volgens het recht. Dat draagt Hij jullie op; misschien zullen jullie verstandig worden.'),
(941, 6, 152, 'En dat jullie niet aan het bezit van de wees mogen komen, tenzij dat op de beste manier gebeurt, totdat hij volgroeid is en dat jullie volle maat en gewicht in billijkheid moeten geven." -- Wij leggen niemand meer op dan hij kan dragen -- "En als jullie een uitspraak doen weest dan rechtvaardig zelfs al zou het een verwant betreffen. En dat jullie de verbintenis met God na moeten komen, dat draagt Hij jullie op -- misschien laten jullie je vermanen.'),
(942, 6, 153, 'En dat dit Mijn weg is, een juiste. Volgt die dus en volgt niet de verschillende andere wegen die jullie van Zijn weg afvoeren. Dat draagt Hij jullie op; misschien zullen jullie godvrezend worden."'),
(943, 6, 154, 'Toen gaven Wij aan Moesa het boek om [Onze genade] aan hem die goed gedaan heeft volledig te maken en om alles uiteen te zetten en als leidraad en barmhartigheid; misschien zullen zij in de ontmoeting met hun Heer geloven.'),
(944, 6, 155, 'En dit is een gezegend boek dat Wij neergezonden hebben. Volgt het dus en weest godvrezend; misschien zal aan jullie barmhartigheid bewezen worden.'),
(945, 6, 156, 'Dat jullie niet zeggen: "Het boek is op twee groepen voor onze tijd neergezonden en wij letten er niet op dat zij het bestudeerden."'),
(946, 6, 157, 'Of dat jullie niet zeggen: "Als het boek tot ons was neergezonden dan hadden wij ons beter de weg laten wijzen dan zij." Maar nu is er tot jullie een duidelijk bewijs van jullie Heer gekomen en een leidraad en barmhartigheid. En wie is er zondiger dan wie Gods tekenen loochent en zich daarvan afwendt? Wij zullen hen die zich van Onze tekenen afwenden vergelden met de vreselijke bestraffing voor dat waarvan zij zich afwenden.'),
(947, 6, 158, 'Kunnen zij dan iets anders verwachten dan dat de engelen tot hen komen of dat jouw Heer komt of dat er enige tekenen van jouw Heer komen? Op de dag dat er enige tekenen van jouw Heer komen heeft niemand nut van zijn geloof als hij niet al voor die tijd geloofd had of in zijn geloof iets goeds verworven had. Zeg: "Wachten jullie maar af; wij wachten ook af."'),
(948, 6, 159, 'Zij die hun godsdienst opsplitsen en tot sekten zijn geworden, met hen heb jij niets gemeen. Hun zaak wordt naar God verwezen; Hij zal hun dan meedelen wat zij deden.'),
(949, 6, 160, 'Als iemand met een goede daad komt dan is er voor hem tien maal zoveel en als iemand met een slechte daad komt dan wordt hem slechts dienovereenkomstig vergolden en hun zal geen onrecht worden aangedaan.'),
(950, 6, 161, 'Zeg: "Mijn Heer heeft mij op een juiste weg gebracht: een juiste godsdienst; het geloof van Ibrahiem die het zuivere geloof aanhing. Hij behoorde niet tot de veelgodendienaars."'),
(951, 6, 162, 'Zeg: "Mijn salaat en mijn godsdienstoefening, mijn leven en mijn sterven behoren God toe, de Heer van de wereldbewoners.'),
(952, 6, 163, 'Geen metgezel heeft Hij. Dat is aan mij bevolen en ik ben de eerste van hen die zich [aan God] overgeven."'),
(953, 6, 164, 'Zeg: "Zal ik een ander dan God als Heer begeren, terwijl Hij toch de Heer van alles is?" Iedere ziel begaat [het slechte] slechts ten koste van zichzelf. Niemand is belast met de last van een ander. Daarna is jullie terugkeer tot jullie Heer. Hij zal jullie dan dat waarover jullie het oneens waren meedelen.'),
(954, 6, 165, 'Hij is het die jullie op de aarde tot elkaar opvolgende geslachten gemaakt heeft en die sommigen van jullie hogere rangen dan anderen gegeven heeft om jullie op de proef te stellen in wat Hij jullie gegeven heeft. Jouw Heer is zeker snel met de afstraffing en Hij is werkelijk vergevend en barmhartig.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'A[lif] L[aam] M[iem] S[aad].'),
(956, 7, 2, 'Een boek dat naar jou is neergezonden -- er moet daarover geen benauwdheid in jouw hart zijn -- opdat jij ermee waarschuwt en als een vermaning voor de gelovigen.'),
(957, 7, 3, 'Volgt wat van jullie Heer naar jullie is neergezonden en volgt in plaats van Hem geen andere beschermers; hoe weinig laten jullie je vermanen!'),
(958, 7, 4, 'En hoeveel steden hebben Wij al niet vernietigd. Dan kwam Ons geweld over hen, \'s nachts of tijdens hun middagslaap.'),
(959, 7, 5, 'En zij konden, wanneer Ons geweld over hen kwam, slechts roepen: "Wij hebben onrecht gedaan."'),
(960, 7, 6, 'En Wij zullen hen tot wie gezanten gezonden zijn ondervragen en Wij zullen de gezondenen ondervragen.'),
(961, 7, 7, 'Wij zullen op grond van kennis aan hen vertellen; Wij waren immers niet afwezig.'),
(962, 7, 8, 'De afweging op die dag is de waarheid en zij van wie de weegschalen zwaar wegen, zij zijn het die het welgaat.'),
(963, 7, 9, 'Maar zij van wie de weegschalen licht zijn, zij zijn het die zichzelf verloren hebben omdat zij Onze tekenen onrecht aandeden.'),
(964, 7, 10, 'Wij hebben aan jullie macht op de aarde gegeven en Wij hebben op haar levensbenodigdheden voor jullie gemaakt; hoe gering is de dank die jullie betuigen.'),
(965, 7, 11, 'Wij hebben jullie geschapen en vervolgens gevormd. Toen zeiden Wij tot de engelen: "Buigt eerbiedig neer voor Adam." En zij bogen zich eerbiedig neer, behalve Iblies, die niet behoorde bij hen die zich eerbiedig neerbogen.'),
(966, 7, 12, 'Hij zei: "Wat weerhoudt jou, dat jij je niet eerbiedig neerboog toen Ik je dat beval?" Hij zei: "Ik ben beter dan hij, U hebt mij uit vuur geschapen en hem hebt U uit klei geschapen."'),
(967, 7, 13, 'Hij zei: "Daal hieruit af. Het past jou niet je hoogmoedig te gedragen. Ga dus hieruit weg. Jij behoort tot de gekleineerden."'),
(968, 7, 14, 'Hij zei: "Verleen mij uitstel tot de dag waarop zij opgewekt worden."'),
(969, 7, 15, 'Hij zei: "Jij behoort bij hen die uitstel hebben gekregen."'),
(970, 7, 16, 'Hij zei: "Maar omdat U mij misleid hebt zal ik voor hen op de loer liggen op Uw juiste weg.'),
(971, 7, 17, 'Dan zal ik op hen afkomen, van voren en van achteren en van rechts en van links. En U zult merken dat de meesten van hen geen dank betuigen."'),
(972, 7, 18, 'Hij zei: "Ga hieruit weg, veracht en verstoten. En als iemand van hen jou volgt? Ik zal de hel met jullie allen tezamen vullen."'),
(973, 7, 19, 'En: "O Adam, bewoon jij met je echtgenote de tuin en eet waarvan jullie willen, maar jullie mogen deze boom niet benaderen, want dan behoren jullie tot de overtreders."'),
(974, 7, 20, 'Toen fluisterde de satan hun in om hun openlijk te tonen wat er van hun schaamte verborgen was en hij zei: "Jullie Heer heeft jullie alleen maar van deze boom afgehouden opdat jullie geen engelen zouden worden of zouden gaan behoren tot hen die altijd blijven bestaan."'),
(975, 7, 21, 'En hij bezwoer hun: "Ik ben voor jullie echt een goede raadgever."'),
(976, 7, 22, 'Zo misleidde hij hen door begoocheling. Toen zij dan van de boom geproefd hadden werd hun schaamte zichtbaar voor hen en begonnen zij zich te bedekken met aaneengehechte bladeren uit de tuin. En hun Heer riep tot hen: "Had Ik jullie deze boom niet verboden en jullie niet gezegd dat de satan een verklaarde vijand van jullie is?"'),
(977, 7, 23, 'Zij zeiden: "Onze Heer, wij hebben onszelf onrecht aangedaan en als U ons niet vergeeft en erbarmen met ons hebt dan behoren wij bij de verliezers."'),
(978, 7, 24, 'Hij zei: "Daalt af -- elkaar tot vijand -- jullie hebben namelijk op de aarde tijdelijk een verblijfplaats en vruchtgebruik."'),
(979, 7, 25, 'Hij zei: "Op haar zullen jullie leven, op haar zullen jullie sterven en uit haar zullen jullie tevoorschijn gebracht worden."'),
(980, 7, 26, 'Kinderen van Adam! Wij hebben voor jullie kleding gemaakt om jullie schaamte te bedekken en kleding voor de sier, maar de kleding van de godvrezendheid is beter. Dat behoort tot Gods tekenen; misschien zullen zij zich laten vermanen.'),
(981, 7, 27, 'Kinderen van Adam! Laat de satan jullie niet in verzoeking brengen, zoals hij jullie beide voorouders uit de tuin verdreven heeft door hun hun kleding te ontnemen om hun hun schaamte te laten zien. Hij en zijn consorten zien jullie vanwaar jullie hen niet zien, Wij hebben de satans tot beschermers gemaakt van hen die niet geloven.'),
(982, 7, 28, 'En wanneer zij iets gruwelijks doen zeggen zij: "Wij hebben bevonden dat onze vaderen er zich aan hielden en God heeft het ons bevolen." Zeg: "God beveelt geen gruwelijkheid. Zeggen jullie over God soms iets wat jullie niet weten?"'),
(983, 7, 29, 'Zeg: "Mijn Heer heeft bevolen rechtvaardig te zijn en dat jullie bij elke moskee jullie aangezicht [tot Hem] richten en Hem aanroepen waarbij jullie de godsdienst geheel aan Hem wijden. Zoals Hij jullie bij het begin gemaakt heeft zo zullen jullie terugkeren."'),
(984, 7, 30, 'Een gedeelte heeft Hij op het goede pad gebracht en voor een gedeelte is de dwaling een feit geworden, want zij hebben zich de satans in plaats van God als beschermers genomen en zij denken dat hun de goede richting gewezen is.'),
(985, 7, 31, 'Kinderen van Adam! Doe jullie mooie kleding aan bij elke moskee en eet en drinkt, maar weest niet verkwistend; Hij bemint de verkwisters niet.'),
(986, 7, 32, 'Zeg: "Wie heeft de mooie [kleding] van God die Hij voor Zijn dienaren heeft voortgebracht en de goede dingen van de levensbehoeften verboden?" Zeg: "Zij zijn in het tegenwoordige leven bestemd voor hen die geloven en op de opstandingsdag hun voorbehouden." Zo zetten Wij de tekenen uiteen aan mensen die weten.'),
(987, 7, 33, 'Zeg: "Mijn Heer heeft slechts de gruwelijkheden verboden, de uiterlijke en de innerlijke, en de zonde en de onterechte aanmatiging en dat jullie aan God metgezellen toevoegen, iets waarvoor Hij geen machtiging heeft neergezonden, en dat jullie over God zeggen waar jullie geen weet van hebben."'),
(988, 7, 34, 'Voor iedere gemeenschap is er een termijn; wanneer hun termijn dan komt zullen zij geen uur te laat ontboden worden, noch te vroeg.'),
(989, 7, 35, 'Kinderen van Adam! Als tot jullie gezanten uit jullie midden komen die jullie Mijn tekenen vertellen dan hebben zij die godvrezend zijn en zich beteren niets te vrezen noch zullen zij bedroefd zijn.'),
(990, 7, 36, 'Maar zij die Onze tekenen loochenen en hoogmoedig afwijzen, zij zijn het die in het vuur thuishoren; zij zullen daarin altijd blijven.'),
(991, 7, 37, 'En wie is er zondiger dan wie over God bedrog verzint of Zijn tekenen loochent? Zij zijn het die hun aandeel in het boek zullen krijgen totdat Onze gezanten, wanneer zij tot hen komen om hen weg te nemen, zeggen: "Waar zijn zij dan die jullie in plaats van God aanroepen?" Zij zeggen: "Wij zijn hen kwijtgeraakt." En zij getuigen tegen zichzelf dat zij ongelovig waren.'),
(992, 7, 38, 'Hij zegt: "Gaat binnen in het vuur te midden van gemeenschappen van de djinn en de mensen die al voor jullie tijd voorbijgegaan waren." Telkens als een gemeenschap binnengaat vervloekt zij een andere en ten slotte, wanneer zij allen bij elkaar gekomen zijn, zegt de laatste van hen tot de eerste van hen: "Onze Heer, dezen hebben ons tot dwaling gebracht. Geef hun dus een dubbele bestraffing van het vuur." Hij zegt: "Voor ieder dubbel!" Maar jullie weten het niet.'),
(993, 7, 39, 'En de eerste van hen zegt tot de laatste van hen: "Dan hebben jullie toch geen voordeel boven ons. Proeft dus de bestraffing voor wat jullie begaan hebben."'),
(994, 7, 40, 'Voor hen die Onze tekenen loochenen en hoogmoedig afwijzen zullen de poorten van de hemel niet worden geopend, noch zullen zij de tuin binnengaan, zolang niet een kameel door het oog van een naald gaat. Zo vergelden Wij aan de boosdoeners.'),
(995, 7, 41, 'Voor hen is de hel een rustplaats en boven hen hebben zij bedekkingen. Zo vergelden Wij aan de onrechtplegers.'),
(996, 7, 42, 'Maar zij die geloven en de deugdelijke daden doen -- aan niemand wordt meer dan hij kan dragen opgelegd -- zij zijn het die in de tuin thuishoren; zij zullen daarin altijd blijven.'),
(997, 7, 43, 'En Wij nemen wat er in hun harten aan wrok was weg. Onder hen stromen rivieren en zij zeggen: "Lof zij God die ons tot hiertoe de goede richting gewezen heeft. Wij zouden ons de goede richting niet hebben laten wijzen als God ons die niet gewezen had. De gezanten van onze Heer zijn werkelijk met de waarheid gekomen." En hun wordt toegeroepen: "Dat is nu de tuin [voor jullie]. Jullie hebben die als erfdeel gekregen voor wat jullie gedaan hebben."'),
(998, 7, 44, 'De bewoners van de tuin roepen de bewoners van het vuur toe: "Wij hebben gemerkt dat wat onze Heer toegezegd had waar is. Hebben jullie ook gemerkt dat wat jullie Heer toegezegd had waar is?" Zij zeggen: "Ja." Dan roept een aankondiger onder hen uit: "Gods vloek rust op de onrechtplegers,'),
(999, 7, 45, 'die Gods weg versperren en verlangen dat het een kronkelweg is terwijl zij aan het hiernamaals ongelovig zijn."'),
(1000, 7, 46, 'Tussen beiden is er een afscheiding en op de kantelen zijn er mannen die iedereen aan hun kentekenen kennen en zij roepen hun die in de tuin thuishoren toe: "Vrede zij met jullie!" Zij zijn niet binnengegaan al begeren zij het. *'),
(1001, 7, 47, 'En wanneer hun blikken zich naar hen die in het vuur thuishoren toewenden zeggen zij: "Onze Heer, zet ons niet bij de mensen die onrecht plegen."'),
(1002, 7, 48, 'En zij die op de kantelen zijn roepen naar mannen die zij aan hun kentekenen kennen. Zij zeggen: "Het bijeenvergaren heeft jullie niet gebaat, noch dat jullie hoogmoedig waren."'),
(1003, 7, 49, 'Zijn dezen het van wie jullie gezworen hadden dat God hun Zijn barmhartigheid niet zou betonen? -- Gaat de tuin binnen. Jullie hebben niets te vrezen noch zullen jullie bedroefd zijn."'),
(1004, 7, 50, 'Zij die in het vuur thuishoren roepen naar hen die in de tuin thuishoren: "Schenkt ons water of iets van dat waarmee God jullie verzorgt." Zij zeggen: "God heeft beide verboden voor de ongelovigen,'),
(1005, 7, 51, 'die hun godsdienst als spel en tijdverdrijf beschouwden. Het tegenwoordige leven begoochelde hen." Vandaag vergeten Wij hen zoals zij hun ontmoeting op deze dag vergaten en Onze tekenen ontkenden.'),
(1006, 7, 52, 'Wij hebben hun een boek gebracht dat Wij op grond van kennis, als leidraad en barmhartigheid voor mensen die geloven, uiteengezet hebben.'),
(1007, 7, 53, 'Wachten zij op iets anders dan de uitwerking ervan? Op de dag dat de uitwerking ervan komt zullen zij die het eerder vergeten hadden zeggen: "De gezanten van onze Heer zijn werkelijk met de waarheid gekomen. Zijn er voor ons bemiddelaars om voor ons te bemiddelen? Of zullen wij teruggebracht worden om iets anders te doen dan wij gewoon waren te doen?" Zij hebben zichzelf verloren en wat zij verzonnen hebben zijn zij kwijtgeraakt.'),
(1008, 7, 54, 'Jullie Heer is God die de hemelen en de aarde in zes dagen geschapen heeft. Toen vestigde Hij zich op de troon. Hij laat de nacht de dag bedekken terwijl hij hem haastig najaagt. En de zon, de maan en de sterren zijn onderworpen aan Zijn zeggenschap. Behoren Hem de schepping en de ordening niet toe? Gezegend zij God, de Heer van de wereldbewoners.'),
(1009, 7, 55, 'Bidt in deemoed en in het verborgene tot jullie Heer; Hij bemint de overtreders niet.'),
(1010, 7, 56, 'Brengt geen verderf op de aarde, nadat zij in orde gemaakt is en roept Hem aan in vrees en begeerte. Gods barmhartigheid is dicht bij hen die goed doen.'),
(1011, 7, 57, 'En Hij is het die de winden als verkondigers van goed nieuws voor Zijn barmhartigheid uit zendt, totdat Wij ze, wanneer zij zware wolken opgestuwd hebben, naar een dode streek drijven en ermee het water laten neerdalen. En Wij brengen daarmee allerlei vruchten voort. Zo brengen Wij ook de doden tevoorschijn; misschien laten jullie je vermanen.'),
(1012, 7, 58, 'En het goede land, daarvan komen de planten met Gods toestemming op, maar van dat wat slecht is komt maar moeizaam iets op. Zo zetten Wij de tekenen uiteen voor mensen die dank betuigen.'),
(1013, 7, 59, 'Wij hebben Noeh tot zijn volk gezonden en hij zei: "Mijn volk! Dient God; jullie hebben geen andere god dan Hem. Ik vrees voor jullie de bestraffing op een geweldige dag."'),
(1014, 7, 60, 'De voornaamsten van zijn volk zeiden: "Wij zien dat jij in duidelijke dwaling verkeert."'),
(1015, 7, 61, 'Hij zei: "Mijn volk! Er is in mij geen dwaling, maar ik ben een gezant van de Heer van de wereldbewoners.'),
(1016, 7, 62, 'Ik verkondig jullie de zendingsopdrachten van mijn Heer en ik geef jullie goede raad en van God weet ik wat jullie niet weten.'),
(1017, 7, 63, 'Of zijn jullie verbaasd dat er tot een man uit jullie midden een vermaning van jullie Heer komt opdat hij jullie waarschuwt en opdat jullie godvrezend worden; misschien zal aan jullie barmhartigheid bewezen worden."'),
(1018, 7, 64, 'Maar zij betichtten hem van leugens. Toen redden Wij hem en hen die met hem waren in het schip en lieten Wij hen die Onze tekenen loochenden verdrinken; zij waren een blind volk.'),
(1019, 7, 65, 'En tot de \'Aad [was] hun broeder Hoed [gezonden]. Hij zei: "Mijn volk! Dient God; jullie hebben geen andere god dan Hem. Zullen jullie niet godvrezend worden?"'),
(1020, 7, 66, 'De voornaamsten, zij die uit zijn volk ongelovig waren, zeiden: "Wij zien dat jij in dwaasheid verkeert en wij menen dat jij tot de leugenaars behoort."'),
(1021, 7, 67, 'Hij zei: "Mijn volk! Er is in mij geen dwaasheid, maar ik ben een gezant van de Heer van de wereldbewoners.'),
(1022, 7, 68, 'Ik verkondig jullie de zendingsopdrachten van mijn Heer en ik ben voor jullie een betrouwbare raadgever.'),
(1023, 7, 69, 'Of zijn jullie verbaasd dat er tot een man uit jullie midden een vermaning van jullie Heer komt om jullie te waarschuwen? Herinnert jullie, toen Hij jullie na het volk van Noeh tot opvolgers gemaakt had en jullie een beduidend grotere gestalte gegeven had. En gedenkt de weldaden van jullie Heer; misschien zal het jullie welgaan."'),
(1024, 7, 70, 'Zij zeiden: "Ben jij tot ons gekomen, opdat wij God alleen zullen dienen en wat onze vaderen dienden verlaten? Breng wat jij ons aanzegt als jij tot hen die gelijk hebben behoort."'),
(1025, 7, 71, 'Hij zei: "Er zal jullie gruwel en toorn van jullie Heer overvallen. Willen jullie dan met mij twisten over namen die jullie en jullie vaderen gegeven hebben, waarvoor God geen enkele machtiging had neergezonden? Wacht maar af; ik zal met jullie afwachten."'),
(1026, 7, 72, 'Toen redden Wij hem en hen die met hem waren door barmhartigheid van Ons en tot de laatste toe velden Wij hen die Onze tekenen loochenden en die niet gelovig waren.'),
(1027, 7, 73, 'En tot de Thamoed [was] hun broeder Salih [gezonden]. Hij zei: "Mijn volk! Dient God; jullie hebben geen andere god dan Hem. Tot jullie is een duidelijk bewijs van jullie Heer gekomen. Dit is Gods kameelmerrie; voor jullie een teken. Laat haar eten op Gods aarde en treft haar niet met kwaad, want dan zal een pijnlijke bestraffing jullie grijpen.'),
(1028, 7, 74, 'En herinnert jullie, toen Hij jullie tot opvolgers maakte na de \'Aad. En Hij heeft jullie plaatsen aangewezen op de aarde om op haar vlakten jullie kastelen in te nemen en om de bergen tot huizen te behouwen. En gedenkt de weldaden van jullie Heer en veroorzaakt geen ellende door op de aarde verderf te zaaien."'),
(1029, 7, 75, 'De voornaamsten, zij die uit zijn volk hoogmoedig waren, zeiden tot hen die onderdrukt werden, tot hen die uit hun midden gelovig waren: "Weten jullie dat Salih een gezondene van zijn Heer is?" Zij zeiden: "Wij geloven zeker wel in dat waarmee hij gezonden is."'),
(1030, 7, 76, 'Zij die hoogmoedig waren zeiden: "Wij hechten geen geloof aan wat jullie geloven."'),
(1031, 7, 77, 'Zij sneden toen de hielpezen van de kameelmerrie door en minachtten het bevel van hun Heer en zij zeiden: "O Salih, breng wat jij ons aanzegt, als jij tot de gezondenen behoort."'),
(1032, 7, 78, 'Toen greep de aardbeving hen en lagen zij \'s morgens in hun woning dood op de grond.'),
(1033, 7, 79, 'Toen keerde hij zich van hen af en zei: "Mijn volk! Ik had jullie toch de zendingsopdracht van mijn Heer verkondigd en jullie goede raad gegeven, maar jullie beminnen de raadgevers niet."'),
(1034, 7, 80, 'En Loet, toen hij tot zijn volk zei: "Zullen jullie een gruweldaad begaan die nog niemand van de wereldbewoners vóór jullie heeft begaan?'),
(1035, 7, 81, 'Jullie komen vol begeerte tot mannen in plaats van tot vrouwen. Ja, jullie zijn overmatige mensen."'),
(1036, 7, 82, 'Het antwoord van zijn volk was slechts dat zij zeiden: "Verdrijf hen uit jullie stad, want zij zijn mensen die zich rein houden."'),
(1037, 7, 83, 'Toen redden Wij hem en zijn familie, behalve zijn vrouw; zij behoorde tot hen die achterbleven.'),
(1038, 7, 84, 'En Wij lieten regen op hen vallen. Kijk dan hoe het einde was van de boosdoeners.'),
(1039, 7, 85, 'En tot de Madjan [was] hun broeder Sjoe\'aib [gezonden]. Hij zei: "Mijn volk! Dient God; jullie hebben geen andere god dan Hem. Tot jullie is een duidelijk bewijs van jullie Heer gekomen. Geeft volle maat en gewicht en doe de mensen niet te kort in de dingen die van hen zijn en brengt geen verderf op de aarde, nadat zij in orde gemaakt is. Dat is beter voor jullie als jullie gelovig zijn.'),
(1040, 7, 86, 'Ligt niet op elke weg op de loer om te dreigen en om Gods weg voor hen die in Hem geloven te versperren en te verlangen dat het een kronkelweg is. En herinnert jullie, toen jullie weinig waren en Hij maakte dat jullie veel werden en kijkt hoe het einde was van de verderfbrengers.'),
(1041, 7, 87, 'En als een groep van jullie gelooft in dat waarmee ik gezonden ben en een groep niet gelooft, weest dan geduldig totdat God tussen ons oordeelt, want Hij is de beste van hen die oordelen." *'),
(1042, 7, 88, 'De voornaamsten, zij die uit zijn volk hoogmoedig waren, zeiden: "Wij zullen jou, Sjoe\'aib en hen die met jou geloven uit onze stad verdrijven, of je moet tot ons geloof terugkeren." Hij zei: "Ook dan, als dat ons tegenstaat?'),
(1043, 7, 89, 'Wij zouden toch over God bedrog verzinnen als wij tot jullie geloof zouden terugkeren, nadat God ons daaruit heeft gered. Het past ons niet ernaar terug te keren, behalve als God onze Heer het wil. Onze Heer omvat alles met Zijn kennis. Op God stellen wij ons vertrouwen. Onze Heer, doe naar waarheid uitspraak tussen ons en ons volk; U bent de beste van hen die uitspraak doen."'),
(1044, 7, 90, 'En de voornaamsten uit zijn volk, zij die ongelovig waren, zeiden: "Als jullie Sjoe\'aib volgen behoren jullie tot de verliezers."'),
(1045, 7, 91, 'Toen greep de aardbeving hen en lagen zij \'s morgens in hun woning dood op de grond.'),
(1046, 7, 92, 'Zij die Sjoe\'aib van leugens betichtten waren alsof zij er niet lang woonachtig waren geweest. Zij die Sjoe\'aib van leugens betichtten waren zelf de verliezers.'),
(1047, 7, 93, 'Toen keerde hij zich van hen af en zei: "Mijn volk! Ik had jullie toch de zendingsopdrachten van mijn Heer verkondigd en jullie goede raad gegeven. Hoe kan ik dan treurig zijn over ongelovige mensen?"'),
(1048, 7, 94, 'Wij hebben geen profeet naar een stad gezonden zonder dat Wij de inwoners ervan tegenspoed en rampspoed opgelegd hebben, opdat zij zich misschien zouden vernederen.'),
(1049, 7, 95, 'Dan vervingen Wij het slechte door het goede tot zij zich vermeerderden en zeiden: "Onze vaderen heeft ook rampspoed en voorspoed getroffen." Toen grepen Wij hen onverwachts zonder dat zij het beseften.'),
(1050, 7, 96, 'Maar als de inwoners van de steden geloofd hadden en godvrezend geweest waren dan hadden Wij voor hen de zegeningen van de hemel en de aarde geopend. Maar zij betichtten van leugens en dus grepen Wij hen voor wat zij begaan hadden.'),
(1051, 7, 97, 'Wanen de inwoners van de steden zich er veilig voor dat Ons geweld \'s nachts over hen komt terwijl zij slapen?'),
(1052, 7, 98, 'En wanen de inwoners van de steden zich er veilig voor dat Ons geweld op klaarlichte dag over hen komt terwijl zij schertsen?'),
(1053, 7, 99, 'En wanen de inwoners van de steden zich dan veilig voor de plannen van God? Maar alleen de mensen die verliezers zijn wanen zich veilig voor de plannen van God.'),
(1054, 7, 100, 'Is het dan aan hen die de aarde na de [vorige] bewoners beërven niet duidelijk dat Wij hen voor hun zonden getroffen hadden, als Wij gewild hadden? Maar Wij verzegelen hun harten zodat zij niet horen.'),
(1055, 7, 101, 'Die steden, daar vertellen Wij jou berichten over. Hun gezanten kwamen met de duidelijke bewijzen tot hen, maar zij waren niet zo dat zij geloofden in wat zij tevoren geloochend hadden. Zo verzegelt God de harten van de ongelovigen.'),
(1056, 7, 102, 'Wij hebben gemerkt dat de meesten van hen geen verbintenissen kennen en Wij hebben gemerkt dat de meesten van hen verdorven zijn.'),
(1057, 7, 103, 'Toen zonden Wij na hen Moesa met Onze tekenen naar Fir\'aun en zijn raad van voornaamsten, maar zij zondigden ertegen. Kijk dan hoe het einde was van de verderfbrengers.'),
(1058, 7, 104, 'En Moesa zei: "O Fir\'aun, ik ben een gezant van de Heer der wereldbewoners,'),
(1059, 7, 105, 'die begerig is om over God alleen maar de waarheid te zeggen. Ik ben tot jullie gekomen met een duidelijk bewijs van jullie Heer. Zend dus de Israëlieten met mij weg."'),
(1060, 7, 106, 'Hij zei: "Als jij echt met een teken gekomen bent, breng het dan tevoorschijn als jij behoort tot hen die gelijk hebben."'),
(1061, 7, 107, 'Toen wierp hij zijn staf en die was opeens duidelijk een slang.'),
(1062, 7, 108, 'En hij strekte zijn hand uit en zij was opeens wit voor de toeschouwers.'),
(1063, 7, 109, 'De voornaamsten uit het volk van Fir\'aun zeiden: "Zie dit is een kundig tovenaar.'),
(1064, 7, 110, 'Hij wil jullie uit jullie land verdrijven. Wat bevelen jullie dan aan?"'),
(1065, 7, 111, 'Zij zeiden: "Houd hem en zijn broer nog wat tegen en zend bijeenroepers in de steden uit'),
(1066, 7, 112, 'om elke kundige tovenaar tot jou te brengen."'),
(1067, 7, 113, 'En de tovenaars van Fir\'aun kwamen en zeiden: "Voor ons is er toch wel een beloning als wij overwinnen?"'),
(1068, 7, 114, 'Hij zei: "Ja, jullie zullen dan behoren tot hen die in [mijn] nabijheid zijn toegelaten."'),
(1069, 7, 115, 'Zij zeiden: "O Moesa, of jij werpt of wij zijn het die zullen werpen."'),
(1070, 7, 116, 'Hij zei: "Werpt." En toen zij wierpen betoverden zij de ogen van de mensen en boezemden hun angst in en zij bedreven geweldige tovenarij. *'),
(1071, 7, 117, 'Maar Wij openbaarden aan Moesa: "Werp jouw staf." Die verslond toen wat zij de mensen voortoverden.'),
(1072, 7, 118, 'Toen kwam de waarheid aan het licht en wat zij deden bleek vals te zijn.'),
(1073, 7, 119, 'Zij werden daar dus overwonnen en keerden om als gekleineerden.'),
(1074, 7, 120, 'En de tovenaars werden neergeworpen om zich eerbiedig neer te buigen.'),
(1075, 7, 121, 'Zij zeiden: "Wij geloven in de Heer van de wereldbewoners,'),
(1076, 7, 122, 'de Heer van Moesa en Haroen."'),
(1077, 7, 123, 'Fir\'aun zei: "Jullie geloven in Hem voordat ik jullie toestemming gegeven heb. Dat is een list die jullie in de stad beraamd hebben om haar inwoners eruit te verdrijven. Dat zullen jullie dan weten.'),
(1078, 7, 124, 'Ik zal jullie de handen en de voeten aan tegenovergestelde kanten afhouwen en dan zal ik jullie allen tezamen kruisigen."'),
(1079, 7, 125, 'Zij zeiden: "Wij zullen tot onze Heer omkeren.'),
(1080, 7, 126, 'Jij koestert alleen maar wrok tegen ons omdat wij in de tekenen van onze Heer geloofden toen zij tot ons kwamen. Onze Heer, verleen ons volharding en neem ons weg als [mensen] die zich [aan God] hebben overgegeven."'),
(1081, 7, 127, 'De voornaamsten uit het volk van Fir\'aun zeiden: "Zul jij toelaten dat Moesa en zijn volk op de aarde verderf zaaien en dat hij jou en jouw goden verlaat?" Hij zei: "Wij zullen hun zonen doden en hun vrouwen in leven laten. Wij zijn toch de overheersers over hen."'),
(1082, 7, 128, 'Moesa zei tot zijn volk: "Vraagt God om hulp en weest geduldig. De aarde behoort God toe; Hij laat haar beërven door wie van Zijn dienaren Hij wil en het [goede] uiteinde komt de godvrezenden toe."'),
(1083, 7, 129, 'Zij zeiden: "Ons is leed aangedaan voordat jij tot ons kwam en nadat jij tot ons gekomen bent." Hij zei: "Misschien dat jullie Heer jullie vijanden vernietigt en dat Hij jullie dan hun opvolgers op de aarde laat zijn. Dan zal Hij kijken hoe jullie het doen."'),
(1084, 7, 130, 'En Wij grepen de mensen van Fir\'aun met hongerjaren en tekort aan vruchten, opdat zij zich misschien zouden laten vermanen.'),
(1085, 7, 131, 'Wanneer dan iets goeds tot hen kwam zeiden zij: "Dit komt ons toe." Maar als hen iets slechts trof, zagen zij een slecht voorteken in Moesa en hen die met hem waren. Toch was hun slechte voorteken alleen maar bij God, maar de meesten van hen weten het niet.'),
(1086, 7, 132, 'En zij zeiden: "Wat jij ons ook voor teken brengt om ons ermee te betoveren, wij zullen jou toch niet geloven."'),
(1087, 7, 133, 'En Wij zonden over hen de overstroming, de sprinkhanen, de vlooien, de kikkers en het bloed als duidelijk te onderscheiden tekenen. Maar zij bleven hoogmoedige en misdadige mensen.'),
(1088, 7, 134, 'En toen de plaag hen overviel zeiden zij: "O Moesa, bid voor ons tot jouw Heer op grond van wat Hij jou opgedragen heeft. Als jij de plaag voor ons opheft dan zullen wij aan jou geloof hechten en de Israëlieten met jou mee wegzenden."'),
(1089, 7, 135, 'Maar toen Wij de plaag voor hen ophieven tot aan een termijn die zij moesten bereiken, toen braken zij meteen hun woord.'),
(1090, 7, 136, 'Dus namen Wij wraak op hen en Wij verdronken hen in de zee, omdat zij Onze tekenen geloochend hadden en er niet op gelet hadden.'),
(1091, 7, 137, 'En Wij lieten het volk dat onderdrukt was geweest het oosten en het westen beërven van het land dat Wij gezegend hadden. Zo werd het mooiste woord van jouw Heer vervuld aan de Israëlieten, omdat zij geduldig volhardden, maar Wij vernietigden wat Fir\'aun en zijn volk gemaakt hadden en wat zij [als gebouwen] hadden opgetrokken.'),
(1092, 7, 138, 'En Wij lieten de Israëlieten de zee oversteken en zij kwamen bij een volk dat afgoden die zij hadden bleef dienen. Zij zeiden: "O Moesa, maak voor ons een god zoals zij goden hebben." Hij zei: "Jullie zijn mensen die niets weten.'),
(1093, 7, 139, 'Waar dezen hier zich mee bezighouden, dat wordt vernietigd en wat zij gedaan hebben wordt tenietgedaan."'),
(1094, 7, 140, 'Hij zei: "Zal ik dan voor jullie een andere god begeren dan God, terwijl Hij jullie boven de wereldbewoners heeft verkozen?"'),
(1095, 7, 141, 'Toen Wij jullie van Fir\'auns mensen redden die jullie een vreselijk leed berokkenden doordat zij jullie zonen doodden en alleen jullie vrouwen in leven lieten. Daarin was een geweldige beproeving van jullie Heer.'),
(1096, 7, 142, 'En Wij spraken met Moesa dertig nachten af en Wij sloten ze af met nog tien. Zo duurde de afgesproken tijd met zijn Heer volle veertig nachten. En Moesa zei tot zijn broer Haroen: "Wees mijn opvolger bij mijn volk en stel orde op zaken en volg niet de weg van de verderfbrengers."'),
(1097, 7, 143, 'Toen Moesa op de met Ons afgesproken tijd kwam en zijn Heer tot hem sprak zei hij: "Mijn Heer, maak dat ik zo zie dat ik U aanschouw." Hij zei: "Jij zult Mij niet zien, maar kijk naar de berg. Als hij vast op zijn plaats blijft staan, dan zul jij Mij zien." Maar toen Zijn Heer Zijn glorie aan de berg openbaarde maakte Hij hem tot gruis en Moesa viel wezenloos neer. En toen hij weer tot bewustzijn kwam zei hij: "U zij geprezen, ik wend mij berouwvol tot U en ik ben de eerste van de gelovigen."'),
(1098, 7, 144, 'Hij zei: "O Moesa, Ik heb jou met Mijn zendingsopdrachten en Mijn woord boven de mensen uitverkoren. Houd dan wat Ik jou gegeven heb vast en behoor tot hen die dank betuigen."'),
(1099, 7, 145, 'En Wij schreven voor hem op de tafelen over alle dingen, als een aansporing en een uiteenzetting van alle dingen; "Houd die stevig vast en beveel jouw volk zich aan het mooiste ervan te houden. Ik zal jullie de woning van de verdorvenen laten zien."'),
(1100, 7, 146, 'Ik zal van Mijn tekenen hen afwenden die zich onterecht op de aarde hoogmoedig gedragen; ook al zien zij elk teken, zij geloven er niet in en al zien zij de weg van redelijk inzicht, zij zullen die niet als weg aannemen. Maar als zij de weg van de verdorvenheid zien dan nemen zij die aan als weg. Dat komt omdat zij Onze tekenen geloochend hebben en er niet op gelet hebben.'),
(1101, 7, 147, 'Zij die Onze tekenen en de ontmoeting van het hiernamaals loochenen, hun daden zullen vruchteloos zijn. Zal aan hen soms iets anders vergolden worden dan wat zij deden?'),
(1102, 7, 148, 'En het volk van Moesa maakte zich na zijn vertrek van hun sieraden een kalf, als een lichaam met geloei. Zagen zij dan niet dat het niet tot hen kon spreken en hun niet de weg kon wijzen? Zij namen het aan en pleegden onrecht.'),
(1103, 7, 149, 'En toen berouw hen overviel en zij zagen dat zij gedwaald hadden zeiden zij: "Als onze Heer geen erbarmen met ons heeft en ons vergeeft dan behoren wij tot de verliezers."'),
(1104, 7, 150, 'Toen Moesa toornig en vol spijt tot zijn volk terugkeerde zei hij: "Wat jullie na mijn weggaan gedaan hebben, dat is pas slecht! Wilden jullie de beschikking van jullie Heer voor zijn?" En hij wierp de tafelen neer en greep zijn broer bij het hoofd en trok hem naar zich toe. Hij zei: "Zoon van mijn moeder! Het volk heeft mij onder druk gezet en zij hadden mij bijna gedood. Maak niet dat de vijanden over mij leedvermaak hebben en zet mij niet bij de mensen die onrecht plegen."'),
(1105, 7, 151, 'Hij zei: "Mijn Heer, vergeef mij en mijn broer en laat ons Uw barmhartigheid binnengaan; U bent de barmhartigste van de barmhartigen."'),
(1106, 7, 152, 'Zij die het kalf aannamen, over hen zal toorn van hun Heer en vernedering in het tegenwoordige leven komen; zo vergelden Wij aan hen die bedenksels verzinnen.'),
(1107, 7, 153, 'Maar zij die slechte daden begaan en dan later berouw tonen en geloven; zeker, jouw Heer is daarna vergevend en barmhartig.'),
(1108, 7, 154, 'En toen de toorn van Moesa bedaard was nam hij de tafelen en in het inschrift erop was een leidraad en barmhartigheid voor hen die voor hun Heer ontzag hebben.'),
(1109, 7, 155, 'En Moesa koos uit zijn volk zeventig mannen voor de afgesproken tijd met Ons. Toen dan de aardbeving hen greep zei hij: "Mijn Heer, als U gewild had, had U hen al eerder kunnen vernietigen en mij ook. Zult U ons vernietigen voor wat de dwazen onder ons gedaan hebben? Het is toch Uw verzoeking waarmee U tot dwaling brengt wie U wilt en op het goede pad brengt wie U wilt. U bent onze helper, vergeef ons dan en heb erbarmen met ons; U bent de beste van hen die vergeven. *'),
(1110, 7, 156, 'En noteer voor ons in de tegenwoordige wereld wat goed is en ook in het hiernamaals, want wij hebben ons tot U gewend." Hij zei: "Met Mijn bestraffing tref Ik wie Ik wil en Mijn barmhartigheid omvat alles. Ik zal het noteren voor hen die godvrezend zijn en de zakaat brengen en voor hen die in Onze tekenen geloven,'),
(1111, 7, 157, 'die de gezant, de ongeletterde profeet volgen, die zij bij zich in de Taura en de Indjiel beschreven vinden. Hij gebiedt hun het behoorlijke en verbiedt hun het verwerpelijke en hij staat hun de goede dingen toe en verbiedt hun de onbetamelijke dingen. Hij neemt hun de last en de boeien af die op hen rustten. Zij nu die in hem geloven, hem bijstaan, hem helpen en het licht volgen dat met hem is neergezonden, zij zijn het die het welgaat."'),
(1112, 7, 158, 'Zeg: "O mensen, ik ben de gezant van God tot jullie allen, van Hem die de heerschappij over de hemelen en de aarde heeft. Er is geen god dan Hij. Hij geeft leven en laat sterven. Gelooft dan in God en Zijn gezant, de ongeletterde profeet die in God en Zijn woorden gelooft en volgt hem; misschien zullen jullie je de goede richting laten wijzen."'),
(1113, 7, 159, 'En onder de mensen van Moesa is er een gemeenschap [van hen] die de weg naar de waarheid wijst en die rechtvaardig ernaar handelt.'),
(1114, 7, 160, 'En Wij splitsten hen op in twaalf stammen, als gemeenschappen. En Wij openbaarden aan Moesa, toen zijn volk hem om water vroeg: "Sla met je staf op de rots." Toen ontsprongen daaruit twaalf bronnen, waarvan elke groep mensen wist waar ze moesten drinken. En Wij lieten hen door de wolken overschaduwen en zonden het manna en de kwartels tot hen neer: "Eet van de goede dingen waarmee Wij in jullie levensonderhoud voorzien." En zij deden Ons geen onrecht aan, maar zij deden zichzelf onrecht aan.'),
(1115, 7, 161, 'En toen tot hen gezegd werd: "Bewoont deze stad en eet ervan in overvloed, waar jullie maar willen en zegt: \'Vergiffenis!? en gaat eerbiedig neerbuigend de poort binnen, dan zullen Wij jullie je fouten vergeven en Wij zullen hun die goed doen nog meer doen toekomen."'),
(1116, 7, 162, 'Maar zij die onrecht pleegden verwisselden het gezegde voor iets anders dan wat tot hen gezegd was. Toen zonden Wij een plaag uit de hemel over hen voor het onrecht dat zij gepleegd hadden.'),
(1117, 7, 163, 'En vraag hun naar de stad die bij de zee lag; toen zij op de sabbat in overtreding waren, hoe hun vissen op hun sabbatdag tot hen kwamen terwijl ze vanzelf omhooggezwommen waren, maar op de dag dat zij de sabbat niet hielden kwamen ze niet. Zo stelden Wij hen op de proef voor het schandelijke dat zij deden.'),
(1118, 7, 164, 'En toen een gemeenschap van hen zei: "Waarom vermanen jullie mensen die God zal vernietigen of met een strenge bestraffing straffen?" Zij zeiden: "Als verontschuldiging tot jullie Heer en opdat zij misschien godvrezend zullen worden."'),
(1119, 7, 165, 'En toen zij vergeten waren waartoe zij gemaand waren, redden Wij hen die het slechte verboden en grepen Wij hen die onrecht pleegden met een erge bestraffing voor het schandelijke dat zij deden.'),
(1120, 7, 166, 'En toen zij wat hun verboden was minachtten, zeiden Wij tot hen: "Weest weggejaagde apen."'),
(1121, 7, 167, 'En toen jouw Heer aankondigde tegen hen tot aan de opstandingsdag mensen te zenden die hun een vreselijk leed zouden berokkenen. Jouw Heer is zeker snel met de afstraffing en Hij is werkelijk vergevend en barmhartig.'),
(1122, 7, 168, 'En Wij splitsten hen op de aarde in gemeenschappen op; sommigen van hen waren rechtschapen en anderen waren dat niet. En Wij stelden hen op de proef met goede en slechte dingen, opdat zij misschien terug zouden keren.'),
(1123, 7, 169, 'Toen werden zij daarna opgevolgd door opvolgers, die het boek erfden. Zij grijpen het stoffelijk gewin van deze tegenwoordige wereld aan en zeggen: "Het zal ons vergeven worden." En als een dergelijk stoffelijk gewin zich nog eens aan hen voordoet grijpen zij het aan. Is met hen niet het verdrag van het boek aangegaan dat zij over God alleen maar de waarheid zouden zeggen? En zij hebben wat erin staat bestudeerd. En de laatste woning is beter voor hen die godvrezend zijn. Hebben jullie dan geen verstand?'),
(1124, 7, 170, 'Maar zij die aan het boek vasthouden en de salaat verrichten? Wij zullen het loon van hen die herstel brengen niet verloren laten gaan. *'),
(1125, 7, 171, 'En toen Wij de berg boven hen ophieven alsof hij een stapelwolk was en zij meenden dat hij op hen zou vallen: "Houdt aan wat Wij jullie gegeven hebben stevig vast en gedenkt wat erin staat; misschien zullen jullie godvrezend worden."'),
(1126, 7, 172, 'En toen jouw Heer uit de kinderen van Adam, uit hun lendenen, hun nageslacht nam en hen over zichzelf liet getuigen: "Ben Ik niet jullie Heer?" Zij zeiden: "Ja zeker, wij getuigen." Zodat zij niet op de opstandingsdag zouden kunnen zeggen: "Hierop hadden wij niet gelet."'),
(1127, 7, 173, 'Of kunnen zeggen: "Onze voorvaderen waren vroeger veelgodendienaars en wij zijn slechts nageslacht na hen. Zult U ons dan vernietigen voor wat zij deden die zeggen dat het niet waar is?"'),
(1128, 7, 174, 'Zo zetten Wij de tekenen uiteen en misschien zullen zij terugkeren.'),
(1129, 7, 175, 'En lees hun de mededeling voor over hem aan wie Wij Onze tekenen gaven maar die er zich toen aan onttrok. Toen maakte de satan hem tot volgeling en behoorde hij tot de misleiden.'),
(1130, 7, 176, 'En als Wij gewild hadden dan hadden Wij hem daardoor verhoogd, maar zijn neigingen waren op de aarde gericht en hij volgde zijn geneigdheid. Hij lijkt bijvoorbeeld op een hond. Als je hem aanvalt dan laat hij zijn tong uit de bek hangen en laat je hem met rust, dan laat hij zijn tong uit de bek hangen. Daarop lijken de mensen die Onze tekenen loochenen. Vertel dus het verhaal; misschien zullen zij nadenken.'),
(1131, 7, 177, 'Een slecht voorbeeld zijn de mensen die Onze tekenen loochenden en die zichzelf onrecht aandeden.'),
(1132, 7, 178, 'Wie door God op het goede pad gebracht is die volgt het goede pad en wie Hij tot dwaling brengt, dat zijn de verliezers.'),
(1133, 7, 179, 'Wij hebben veel djinn en mensen voor de hel geschapen. Zij hebben harten waarmee zij niet begrijpen, zij hebben ogen waarmee zij niet zien en zij hebben oren waarmee zij niet horen. Zij zijn als het vee, nee zij dwalen nog erger. Zij zijn het die onoplettend zijn.'),
(1134, 7, 180, 'God komen de mooiste namen toe. Roept Hem daarmee aan en laat hen maar die Zijn namen misbruiken; aan hen zal worden vergolden wat zij deden.'),
(1135, 7, 181, 'En onder hen die Wij geschapen hebben is er een gemeenschap die de weg naar de waarheid wijst en die rechtvaardig ernaar handelt.'),
(1136, 7, 182, 'En zij die Onze tekenen loochenen, zullen Wij gaandeweg tot vernietiging brengen zonder dat zij het weten.'),
(1137, 7, 183, 'En Ik zal hun uitstel geven. Mijn list staat vast.'),
(1138, 7, 184, 'Hebben zij dan niet nagedacht? In hun medeburger is geen bezetenheid, hij is slechts een duidelijke waarschuwer.'),
(1139, 7, 185, 'Hebben zij niet het rijk van de hemelen en de aarde beschouwd en de dingen die God geschapen heeft en dat misschien hun termijn nabijgekomen is. Aan welk bericht zullen zij dan hierna nog geloven?'),
(1140, 7, 186, 'Als God iemand tot dwaling brengt dan is er voor hem niemand die de goede richting wijst. En Hij laat hen in hun onbeschaamdheid doorgaan met dwalen.'),
(1141, 7, 187, 'Zij vragen jou naar het uur, voor wanneer het is vastgesteld. Zeg: "De kennis ervan is slechts bij mijn Heer, alleen Hij zal het op de tijd ervan openbaar maken. Het is van groot gewicht in de hemelen en op de aarde. Het komt slechts onverwachts tot jullie." Zij vragen aan jou alsof jij ervan op de hoogte bent. Zeg: "De kennis ervan is slechts bij God, maar de meeste mensen weten het niet."'),
(1142, 7, 188, 'Zeg: "Ik heb geen macht om voor mijzelf tot nut of tot schade te zijn, afgezien van wat God wil. En als ik het verborgene zou kennen dan zou ik veel goeds verworven hebben en geen kwaad zou mij overkomen zijn. Ik ben slechts een waarschuwer en een verkondiger van goed nieuws voor mensen die geloven."'),
(1143, 7, 189, 'Hij is het die jullie uit één wezen geschapen heeft en die uit hem zijn echtgenote maakte opdat hij bij haar rust zou vinden. En toen hij met haar gemeenschap had gehad kreeg zij een lichte zwangerschapslast te dragen en zij bleef gewoon doorgaan, maar toen dat zwaar werd riepen zij God, hun beider Heer, aan: "Als U ons een gaaf kind geeft zullen wij zeker behoren tot hen die dank betuigen."'),
(1144, 7, 190, 'Toen Hij hun dan een gaaf kind gegeven had, kenden zij Hem metgezellen toe in wat Hij hun gegeven had. Maar God is verheven boven wat zij aan Hem als metgezellen toevoegen.'),
(1145, 7, 191, 'Voegen zij dan [aan God] iets als metgezellen toe wat niets schept, maar die zelf geschapen zijn?'),
(1146, 7, 192, 'Die zijn niet in staat hen te helpen, noch kunnen zij zichzelf helpen.'),
(1147, 7, 193, 'En als jullie hen tot de leidraad oproepen dan volgen zij jullie niet. Het maakt voor jullie niet uit of jullie hen oproepen of dat jullie zwijgen.'),
(1148, 7, 194, 'Zij, die jullie in plaats van God aanroepen, zijn dienaren net als jullie. Roept hen dan aan en laten zij jullie gehoor geven, als jullie gelijk hebben.'),
(1149, 7, 195, 'Hebben zij dan voeten waarmee zij lopen of hebben zij handen waarmee zij vastgrijpen of hebben zij ogen waarmee zij zien of hebben zij oren waarmee zij horen? Zeg: "Roept jullie [zogenaamd goddelijke] metgezellen aan, beraamt dan listen tegen mij en laat mij dan niet wachten.'),
(1150, 7, 196, 'Mijn beschermer is God die het boek heeft neergezonden en Hij verleent de rechtschapenen bijstand.'),
(1151, 7, 197, 'En zij die jullie in plaats van Hem aanroepen zijn niet in staat jullie te helpen, noch kunnen zij zichzelf helpen.'),
(1152, 7, 198, 'En als jullie hen tot de leidraad oproepen dan horen zij niet en jullie zien hen naar jullie kijken, maar zij zien niet."'),
(1153, 7, 199, 'Neem wat gemakkelijk gegeven kan worden, beveel het behoorlijke en wend je af van de dommen.'),
(1154, 7, 200, 'En als jij door de satan wordt opgehitst, vraag dan God om bescherming; Hij is horend en wetend.'),
(1155, 7, 201, 'Zij die godvrezend zijn zullen zich laten vermanen wanneer hun een opwelling van de satan overkomt en dan komen zij meteen tot inzicht.'),
(1156, 7, 202, 'Maar hun broeders versterken zij in verdorvenheid en dan houden zij er niet mee op.'),
(1157, 7, 203, 'En wanneer jij hun geen teken brengt zeggen zij: "Had jij er niet een kunnen uitkiezen?" Zeg: "Ik volg slechts wat door mijn Heer aan mij wordt geopenbaard. Dit zijn inzichtelijke bewijzen van jullie Heer en het is een leidraad en barmhartigheid voor mensen die geloven."'),
(1158, 7, 204, 'En wanneer de Koran wordt voorgelezen, luistert er dan naar en let in stilte op; misschien zal aan jullie barmhartigheid worden bewezen.'),
(1159, 7, 205, 'En denk in jezelf aan jouw Heer in deemoed en in vrees en zonder luid te spreken, in de morgenstond en in de avond. En behoor niet tot de onoplettenden.'),
(1160, 7, 206, 'Zij die bij jouw Heer zijn zijn niet te trots om Hem te dienen; zij prijzen Hem en buigen zich eerbiedig voor Hem neer.?');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Zij vragen jou naar de buit. Zeg: "De buit behoort aan God en Zijn gezant. Vreest dus God en stelt onderling orde op zaken en gehoorzaamt God en Zijn gezant als jullie gelovig zijn."'),
(1162, 8, 2, 'De gelovigen dat zijn slechts zij van wie de harten vol ontzag zijn wanneer Gods naam vermeld wordt en die, wanneer Zijn tekenen aan hen worden voorgelezen, erdoor in geloof toenemen en die op hun Heer hun vertrouwen stellen,'),
(1163, 8, 3, 'die de salaat verrichten en van wat Wij hun voor hun levensonderhoud gegeven hebben ook bijdragen geven.'),
(1164, 8, 4, 'Zij zijn het die echt geloven; voor hen zijn er verschillende rangen bij God en vergeving en een voortreffelijke voorziening.'),
(1165, 8, 5, 'Zoals jouw Heer jou met de waarheid jouw huis liet uitgaan en dat terwijl het een groep van de gelovigen tegenstond.'),
(1166, 8, 6, 'Zij twistten met jou over de waarheid, nadat die hun duidelijk was geworden, alsof zij met open ogen de dood in gedreven werden.'),
(1167, 8, 7, 'En toen God jullie toezegde dat één van de twee groepen voor jullie bestemd was en jullie liever hadden dat de weerloze voor jullie bestemd was. Maar God wenste de waarheid met Zijn woorden te bevestigen en de ongelovigen tot de laatste toe te vellen.'),
(1168, 8, 8, 'Om de waarheid te bevestigen en de onzin te ontkrachten ook al staat het de boosdoeners tegen.'),
(1169, 8, 9, 'Toen jullie je Heer om hulp vroegen en Hij jullie verhoorde: "Ik versterk jullie met duizend engelen die achter elkaar aan komen."'),
(1170, 8, 10, 'En God heeft het alleen maar gedaan om het goed nieuws te laten zijn en opdat jullie harten erdoor gerustgesteld zouden worden -- de overwinning komt alleen maar van God. God is machtig en wijs.'),
(1171, 8, 11, 'Toen Hij jullie als een bescherming van Hem door slaap bevangen liet worden en water uit de hemel over jullie liet neerdalen om jullie ermee rein te maken en de gruwel van de satan van jullie te verwijderen en om jullie harten standvastig en de voeten stevig te maken.'),
(1172, 8, 12, 'Toen jouw Heer aan de engelen openbaarde: "Ik ben met jullie, sterkt dus hen die geloven. Ik zal de harten van hen die ongelovig zijn schrik aanjagen. Houwt dan in op de nekken en houwt hen op al hun vingers."'),
(1173, 8, 13, 'Dat is omdat zij God en Zijn gezant tegenwerken. En als iemand God en Zijn gezant tegenwerkt, dan is God streng in de afstraffing.'),
(1174, 8, 14, 'Dat is het dan voor jullie; proeft het dan. En, voor de ongelovigen is er de bestraffing van het vuur.'),
(1175, 8, 15, 'Jullie die geloven! Wanneer jullie hen die ongelovig zijn tegenkomen wanneer zij met velen optrekken keert dan niet om, om te vluchten.'),
(1176, 8, 16, 'Wie zich op die dag omkeert -- behalve als krijgsmanoeuvre of om aansluiting te zoeken bij een andere troepenmacht -- die haalt zich de toorn van God op de hals en zijn verblijfplaats is de hel. Dat is pas een slechte bestemming!'),
(1177, 8, 17, 'Niet jullie hebben hen gedood, maar God heeft hen gedood en niet jij hebt geworpen toen jij wierp, maar God heeft geworpen en wel om de gelovigen daardoor met een goede beproeving op de proef te stellen. God is horend en wetend.'),
(1178, 8, 18, 'Dat is het dan voor jullie. En God verzwakt de list van de ongelovigen.'),
(1179, 8, 19, 'Als jullie om een goede afloop vragen dan is het succes al tot jullie gekomen, maar als jullie ophouden dan is het beter voor jullie. En als jullie terugkeren dan keren Wij [ook] terug. En jullie troepenmacht zal jullie in niets baten ook al is zij groot, maar God is met de gelovigen.'),
(1180, 8, 20, 'Jullie die geloven! Gehoorzaamt God en Zijn gezant en keert jullie niet van Hem af terwijl jullie toehoren.'),
(1181, 8, 21, 'En weest niet als zij die zeggen: "Wij horen", terwijl zij niet horen. *'),
(1182, 8, 22, 'De slechtste beesten zijn bij God de doven en de stommen die geen verstand hebben.'),
(1183, 8, 23, 'Als God bij hen van iets goeds geweten had, dan had Hij hen laten horen, en als Hij hen had laten horen dan hadden zij zich toch afgekeerd terwijl zij zich afwendden.'),
(1184, 8, 24, 'Jullie die geloven! Geeft gehoor aan God en de gezant, wanneer Hij jullie oproept tot wat jullie leven geeft en weet dat God tussen een man en zijn hart tussenbeide komt en dat jullie tot Hem verzameld zullen worden.'),
(1185, 8, 25, 'En weest op jullie hoede voor een verzoeking die niet speciaal hen uit jullie midden treft die onrecht plegen. En weet dat God streng is in de afstraffing.'),
(1186, 8, 26, 'En herinnert jullie toen jullie weinig waren en onderdrukt werden op de aarde, terwijl jullie bang waren dat de mensen jullie zouden wegrukken. Maar Hij gaf jullie toen een onderkomen en heeft jullie gesterkt met Zijn hulp en voorzag met goede dingen in jullie onderhoud; misschien zullen jullie dank betuigen.'),
(1187, 8, 27, 'Jullie die geloven! Weest niet ontrouw aan God en de gezant en weest niet ontrouw aan de dingen die jullie toevertrouwd zijn, terwijl jullie beter weten.'),
(1188, 8, 28, 'En weet dat jullie bezittingen en jullie kinderen een verzoeking zijn en dat er bij God een geweldig loon is.'),
(1189, 8, 29, 'Jullie die geloven! Als jullie God vrezen zal Hij voor jullie een reddend onderscheidingsmiddel brengen en jullie je slechte daden kwijtschelden en jullie vergeven; God is vol van geweldige goedgunstigheid.'),
(1190, 8, 30, 'En toen zij die ongelovig zijn plannen maakten om je vast te houden of je te doden of je te verdrijven. Zij maakten plannen en God maakte plannen, maar God is de beste plannenmaker.'),
(1191, 8, 31, 'En wanneer Gods tekenen aan hen worden voorgelezen zeggen zij: "Wij hebben het gehoord, als wij wilden dan zouden wij iets dergelijks kunnen zeggen. Dit zijn slechts fabels van hen die er eertijds waren."'),
(1192, 8, 32, 'En toen zij zeiden: "O God, als dit de waarheid is die van U komt, laat dan stenen uit de hemel op ons regenen of geef ons een pijnlijke bestraffing."'),
(1193, 8, 33, 'Maar God is niet zo dat Hij hen zou bestraffen terwijl jij nog onder hen verkeert, noch zou God hen bestraffen, terwijl zij om vergeving vragen.'),
(1194, 8, 34, 'Maar waarom zou God hen niet bestraffen als zij de weg naar de heilige moskee versperren en er niet de beschermers van zijn. De beschermers ervan zijn slechts de gelovigen, maar de meesten van hen weten het niet.'),
(1195, 8, 35, 'Hun salaat bij het huis is slechts gefluit en handgeklap. Proeft dan de bestraffing ervoor dat jullie ongelovig waren.'),
(1196, 8, 36, 'Zij die ongelovig zijn dragen hun bezittingen bij om Gods weg te versperren. Zij zullen ze als bijdragen geven en dat zal voor hen een [reden tot] wroeging worden. Dan zullen zij overwonnen worden en zij die ongelovig zijn zullen in de hel verzameld worden,'),
(1197, 8, 37, 'opdat God de slechten van de goeden onderscheidt en de slechten op elkaar zet en opstapelt en in de hel zet. Dat zijn de verliezers.'),
(1198, 8, 38, 'Spreekt tot hen die ongelovig zijn. Als zij ophouden, dan zal hun wat zij eerder deden vergeven worden, maar als zij het weer doen -- de gebruikelijke behandeling van hen die er eertijds waren is al eerder toegepast.'),
(1199, 8, 39, 'Strijdt tegen hen tot er geen verzoeking meer is en de gehele godsdienst alleen God toebehoort. En als zij dan ophouden doorziet God wel wat zij doen.'),
(1200, 8, 40, 'En als zij zich afkeren, weet dan dat God jullie beschermheer is; een voortreffelijke beschermheer en een voortreffelijke helper!'),
(1201, 8, 41, 'En weet, dat wanneer jullie iets buitmaken, dat dan aan God een vijfde deel toebehoort en aan de gezant, de verwanten, de wezen, de behoeftigen en hij die onderweg is, als jullie geloven in God en wat Wij tot Onze dienaar hebben neergezonden op de dag van het reddend onderscheidingsmiddel, de dag dat de beide troepenmachten tot een treffen kwamen; God is almachtig.'),
(1202, 8, 42, 'Toen jullie op de dichtbijgelegen helling waren en zij op de verafgelegen helling en de ruiterij beneden jullie was! Als jullie het met elkaar afgesproken hadden, dan waren jullie de afspraak zeker niet nagekomen. Maar het gebeurde opdat God een beschikking zou treffen die volvoerd moest worden en opdat zij die zijn omgekomen zouden omkomen op grond van een duidelijk bewijs en opdat zij die in leven bleven in leven zouden blijven op grond van een duidelijk bewijs. God is echt horend en wetend.'),
(1203, 8, 43, 'Toen God hen in jouw slaap weinig liet lijken! Als Hij hen talrijk had laten lijken, dan hadden jullie het opgegeven en met elkaar erover getwist, maar God heeft [jullie] bewaard. Hij weet wat er binnen in de harten is.'),
(1204, 8, 44, 'En toen Hij hen aan jullie, toen jullie tot een treffen kwamen, in jullie ogen weinig liet lijken en maakte dat jullie in hun ogen maar weinigen waren. Dat gebeurde opdat God een beschikking zou treffen die volvoerd moest worden. En aan God worden alle zaken voorgelegd.'),
(1205, 8, 45, 'Jullie die geloven! Wanneer jullie een troepenmacht tegenkomen houdt dan stand en gedenkt God veel; misschien zal het jullie welgaan.'),
(1206, 8, 46, 'Gehoorzaamt God en Zijn gezant en twist niet met elkaar zodat jullie opgeven en het jullie niet voor de wind gaat. En volhardt geduldig; God is met de geduldig volhardenden.'),
(1207, 8, 47, 'En weest niet als zij die in zelfgenoegzaamheid en om door de mensen gezien te worden uit hun woningen trokken en Gods weg versperden. God omvat wat zij doen.'),
(1208, 8, 48, 'En toen de satan voor hen hun daden aantrekkelijk maakte en zei: "Vandaag is er onder de mensen niemand die jullie overwint, want ik sta naast jullie." Maar toen de twee troepenmachten elkaar zagen lichtte hij zijn hielen en zei: "Ik heb met jullie niets te maken. Ik zie wat jullie niet zien. Ik ben bang voor God. En God is streng in de afstraffing."'),
(1209, 8, 49, 'Toen de huichelaars en zij die in hun harten een ziekte hebben zeiden: "Dezen zijn door hun godsdienst begoocheld." Maar als iemand zijn vertrouwen op God stelt, dan is God machtig en wijs.'),
(1210, 8, 50, 'Als jij maar kon zien wanneer de engelen hen die ongelovig zijn wegnemen, terwijl zij hen op de gezichten en hun ruggen slaan: "Proeft de straf van de verbranding."'),
(1211, 8, 51, 'Dat is voor wat jullie handen vroeger gedaan hebben en omdat God geen onrechtvaardige behandeling geeft aan de dienaren.'),
(1212, 8, 52, 'Het is net als met de mensen van Fir\'aun en hen die voor hun tijd leefden. Zij hechtten geen geloof aan Gods tekenen en dus greep God hen voor hun zonden. God is krachtig en streng in de afstraffing.'),
(1213, 8, 53, 'Dat komt omdat God niet zo is dat Hij genade die Hij aan mensen geschonken heeft verandert zolang zij niet veranderen wat in henzelf is en omdat God wetend en wijs is.'),
(1214, 8, 54, 'Het is net als met de mensen van Fir\'aun en hen die voor hun tijd leefden. Zij loochenden de tekenen van hun Heer en dus vernietigden Wij hen voor hun zonden en Wij verdronken de mensen van Fir\'aun. En het waren allen onrechtplegers.'),
(1215, 8, 55, 'De slechtste beesten zijn bij God zij die ongelovig zijn en die dus niet geloven,'),
(1216, 8, 56, 'met wie jij een verbintenis bent aangegaan en die dan elke keer hun verbintenis verbreken en die niet godvrezend zijn.'),
(1217, 8, 57, 'Als jij hen in de oorlog aantreft verstrooi dan via hen wie er achter hen staan; misschien laten zij zich vermanen.'),
(1218, 8, 58, 'En als jij van bepaalde mensen verraad vreest, zeg dan het verbond met hen klaar en duidelijk op. God bemint de verraders niet.'),
(1219, 8, 59, 'Zij die ongelovig zijn moeten er niet op rekenen [Ons] te vlug af te zijn; zij kunnen er niets tegen doen.'),
(1220, 8, 60, 'En maakt tegen hen zo goed als jullie kunnen de bewapening en de inzetbare paarden gereed om Gods vijand en jullie vijand daarmee vrees aan te jagen en afgezien van hen anderen die jullie niet kennen, maar die God kent. En wat jullie ook als bijdrage op Gods weg geven, het zal jullie worden vergoed en jullie zal geen onrecht worden aangedaan. *'),
(1221, 8, 61, 'En als zij geneigd zijn tot vrede, wees daar dan ook toe geneigd en stel je vertrouwen op God; Hij is de horende, de wetende.'),
(1222, 8, 62, 'En als zij jou wensen te misleiden, dan is God voor jou goed genoeg. Hij is het die jou met Zijn hulp en met de gelovigen sterkt.'),
(1223, 8, 63, 'En Hij heeft hun harten tot elkaar gebracht. Ook al zouden jullie alles wat er op de aarde is uitgeven, toch zouden jullie hun harten niet tot elkaar brengen, maar God heeft hun harten tot elkaar gebracht. Hij is machtig en wijs.'),
(1224, 8, 64, 'O profeet, God is goed genoeg voor jou en voor die gelovigen die jou volgen.'),
(1225, 8, 65, 'O profeet, spoor de gelovigen aan tot de strijd. Als er onder jullie twintig zijn die volhouden, dan zullen zij er tweehonderd overwinnen en als er onder jullie tweehonderd zijn, dan zullen zij duizend van hen die ongelovig zijn overwinnen, omdat dat mensen zijn die geen begrip hebben.'),
(1226, 8, 66, 'Nu heeft God het voor jullie gemakkelijk gemaakt; Hij weet dat er bij jullie zwakheid voorkomt. Dus, als er onder jullie honderd zijn die volhouden, dan zullen zij er tweehonderd overwinnen en als er onder jullie duizend zijn, dan zullen zij er met Gods toestemming tweeduizend overwinnen. God is met hen die geduldig volharden.'),
(1227, 8, 67, 'Het past een profeet niet gevangenen te hebben zolang hij niet in het land de overhand heeft. Jullie wensen het vergankelijke aanbod van de tegenwoordige wereld, maar God wenst het hiernamaals. God is machtig en wijs.'),
(1228, 8, 68, 'Als er geen eerdere schriftelijke vastlegging van God was geweest, dan had jullie voor wat jullie genomen hebben een geweldige bestraffing getroffen.'),
(1229, 8, 69, 'Eet dan van wat jullie buitgemaakt hebben als iets wat toegestaan en goed is en vreest God. God is vergevend en barmhartig.'),
(1230, 8, 70, 'O profeet, zeg tegen die gevangenen die in jouw handen zijn gevallen: "Als God weet dat er in jullie harten iets goeds is, dan zal Hij jullie iets beters geven dan wat er van jullie genomen is en Hij zal jullie vergeven; God is vergevend en barmhartige'),
(1231, 8, 71, 'En als zij jou wensen te verraden, welnu, zij hebben God al eerder verraden en Hij heeft toen over hen macht gegeven. En God is wetend en wijs.'),
(1232, 8, 72, 'Zij die geloven en uitgeweken zijn en zich met hun bezittingen en hun eigen persoon op Gods weg inspannen en zij die onderkomen en hulp gegeven hebben, dat zijn zij die elkaars medestanders zijn. En hun die geloven, maar nog niet zijn uitgeweken hoeven jullie geen enkele bescherming te geven zolang zij niet zijn uitgeweken. Maar als zij jullie om hulp in de godsdienst vragen dan moeten jullie hulp geven, behalve als het tegen mensen is met wie jullie een verbintenis hebben. God doorziet wel wat jullie doen.'),
(1233, 8, 73, 'En zij die ongelovig zijn zijn elkaars medestanders. En als jullie dit niet doen, dan zal er verzoeking in het land zijn en groot verderf.'),
(1234, 8, 74, 'Zij die geloven en uitgeweken zijn en zich op Gods weg inspannen en zij die onderkomen en hulp gegeven hebben, dat zijn zij die echt geloven; voor hen is er vergeving en een voortreffelijke voorziening.'),
(1235, 8, 75, 'En zij die later gelovig zijn geworden en uitgeweken zijn en die zich samen met jullie inspannen, die behoren bij jullie. Maar de bloedverwanten hebben volgens Gods boek de nauwste band met elkaar. God is alwetend.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Een opzegging door God en Zijn gezant aan hen onder de veelgodendienaars met wie jullie een verbond gesloten hebben.'),
(1237, 9, 2, 'Trekt dan vier maanden rond in het land en weet dat jullie niets tegen God kunnen doen en dat God de ongelovigen te schande maakt.'),
(1238, 9, 3, 'En een aankondiging door God en Zijn gezant aan de mensen op de dag van de grote bedevaart, dat God niets te maken heeft met de veelgodendienaars en Zijn gezant ook niet. Als jullie dan berouw tonen dan is dat beter voor jullie, maar als jullie je afkeren weet dan dat jullie niets tegen God kunnen doen. En zegt hun die ongelovig zijn een pijnlijke bestraffing aan,'),
(1239, 9, 4, 'behalve hun onder de veelgodendienaars met wie jullie een verbond gesloten hebben en die daarna jullie in niets tekort gedaan hebben en die niemand tegen jullie hebben geholpen. Voert dan het verbond met hen volledig uit tot aan de met hen vastgestelde tijd. God bemint de godvrezenden.'),
(1240, 9, 5, 'Als de heilige maanden zijn verstreken, doodt dan de veelgodendienaars waar jullie hen vinden, grijpt hen, belegert hen en wacht hen op in elke mogelijke hinderlaag. Maar als zij berouw tonen, de salaat verrichten en de zakaat geven, legt hun dan niets in de weg. God is vergevend en barmhartig.'),
(1241, 9, 6, 'En als een van de veelgodendienaars bij jou bescherming zoekt, geef hem dan bescherming totdat hij het woord van God hoort en laat hem daarna een plaats bereiken waar hij veilig is. Dat is omdat zij mensen zijn die niet weten.'),
(1242, 9, 7, 'Hoe kan er jegens de veelgodendienaars een verbondsverplichting bij God en bij Zijn gezant zijn, behalve dan jegens hen met wie jullie een verbintenis aangegaan zijn bij de heilige moskee. Zolang zij jegens jullie correct handelen, handelt jullie dan ook correct. God bemint de godvrezenden.'),
(1243, 9, 8, 'Hoe kan het anders, want als zij de overhand over jullie hebben dan houden zij zich aan familieband noch verbintenis. Zij stellen jullie tevreden met hun monden, maar hun harten zijn weigerachtig en de meesten van hen zijn verdorven.'),
(1244, 9, 9, 'Zij hebben Gods tekenen verkwanseld en Zijn weg versperd. Slecht is het wat zij aan het doen waren.'),
(1245, 9, 10, 'Zij houden zich met betrekking tot een gelovige aan familieband noch verbond. Dat zijn de overtreders.'),
(1246, 9, 11, 'Maar als zij berouw tonen, de salaat verrichten en de zakaat geven dan zijn zij jullie broeders in de godsdienst. En Wij zetten de tekenen uiteen aan mensen die weten.'),
(1247, 9, 12, 'En als zij hun eden breken nadat jullie met hen een verbond gesloten hebben en jullie godsdienst belasteren, bestrijdt dan de leiders van het ongeloof. Voor hen bestaan er geen eden. Misschien zullen zij ophouden.'),
(1248, 9, 13, 'Zullen jullie dan niet strijden tegen mensen die hun eden gebroken hebben en die van plan waren de gezant te verdrijven, terwijl zij het eerst tegen jullie begonnen? Vrezen jullie hen? God komt het met meer recht toe dat jullie Hem vrezen als jullie gelovig zijn.'),
(1249, 9, 14, 'Strijdt tegen hen, dan zal God hen door jullie handen bestraffen en te schande maken en Hij zal jullie tegen hen helpen en de harten van gelovige mensen genezen.'),
(1250, 9, 15, 'En Hij zal de woede die in hun harten is verwijderen en God zal zich genadig wenden tot wie Hij wil. God is wetend en wijs.'),
(1251, 9, 16, 'Of rekenden jullie erop met rust gelaten te worden en dat God nog niet zou weten wie zich uit jullie midden inspannen en zich buiten God, Zijn gezant en de gelovigen geen vertrouwelingen nemen? God is welingelicht over wat jullie doen.'),
(1252, 9, 17, 'Het komt de veelgodendienaars niet toe dienst te doen in Gods moskeeën terwijl zij over zichzelf van ongeloof getuigen; zij zijn het van wie de daden vruchteloos zijn en die altijd in het vuur zullen blijven.'),
(1253, 9, 18, 'Dienst doen in Gods moskeeën mogen slechts zij die in God en de laatste dag geloven en die de salaat verrichten en de zakaat geven en die alleen God vrezen; misschien zijn zij het die het goede pad volgen. *'),
(1254, 9, 19, 'Zouden jullie soms het te drinken geven aan de bedevaartgangers en het dienst doen in de heilige moskee gelijkstellen aan iemand die in God en de laatste dag gelooft en die zich inspant op Gods weg? Zij zijn bij God niet gelijk. God wijst de mensen die onrecht plegen de goede richting niet.'),
(1255, 9, 20, 'Zij die geloven en uitgeweken zijn en zich op Gods weg inspannen met hun bezittingen en hun eigen persoon zijn bij God hoger in rang; zij zijn het die triomferen.'),
(1256, 9, 21, 'Hun Heer verkondigt hun het goede nieuws van barmhartigheid van Hem en welbehagen en tuinen waarin voor hen een blijvende gelukzaligheid is,'),
(1257, 9, 22, 'waarin zij voor immer en altijd zullen blijven. Bij God is er een geweldig loon.'),
(1258, 9, 23, 'Jullie die geloven! Neemt jullie vaders en broeders niet als medestanders als zij het ongeloof liever hebben dan het geloof. Wie van jullie hen als medestanders hebben, dat zijn de onrechtplegers.'),
(1259, 9, 24, 'Zeg: "Als jullie vaders en jullie zonen en jullie broeders en jullie echtgenotes en jullie familie en de bezittingen die jullie je verworven hebben en koopwaar waarvan jullie onverkoopbaarheid vrezen en woonhuizen die jullie bevallen voor jullie dierbaarder zijn dan God en Zijn gezant en inspanning op Zijn weg, wacht dan maar af tot God met Zijn beschikking komt. God wijst de verdorven mensen de goede richting niet."'),
(1260, 9, 25, 'God heeft jullie toch op veel plaatsen geholpen en ook op de dag van Hoenain toen het jullie beviel dat jullie talrijk waren, maar het jullie niets baatte. En jullie kregen het benauwd op de aarde ondanks haar ruimte en keerden toen om voor de vlucht.'),
(1261, 9, 26, 'Toen liet God Zijn kalmte op Zijn gezant en op de gelovigen neerdalen en Hij liet troepen neerdalen die jullie niet zagen en Hij strafte hen die ongelovig zijn af. En dat is de vergelding voor de ongelovigen.'),
(1262, 9, 27, 'Dan wendt God zich daarna genadig tot wie Hij wil; God is vergevend en barmhartig.'),
(1263, 9, 28, 'Jullie die geloven! De veelgodendienaars zijn een verontreiniging; na dit jaar mogen zij de heilige moskee dan ook niet meer naderen. En als jullie bang zijn voor armoede, dan zal God jullie met Zijn goedgunstigheid rijk maken als Hij wil. God is wetend en wijs.'),
(1264, 9, 29, 'Strijdt tegen hen die niet in God geloven en niet in de laatste dag en die niet verbieden wat God en Zijn gezant verboden hebben en die niet de godsdienst van de waarheid aanvaarden uit het midden van hen aan wie het boek gegeven is, totdat zij naar vermogen onderdanig de schatting betalen.'),
(1265, 9, 30, 'En de joden zeggen: "\'Oezair is Gods zoon" en de christenen zeggen: "De masieh is Gods zoon." Dat is wat zij met hun monden zeggen. Zij benaderen zo wat zij die vroeger ongelovig waren zeiden. God bestrijde hen, hoe kunnen zij zo afwijken!'),
(1266, 9, 31, 'Zij namen hun schriftgeleerden en hun monniken tot heren in plaats van God en ook de masieh, de zoon van Marjam. En hun werd slechts bevolen één God te dienen. Er is geen god dan Hij. Hij zij geprezen, verheven als Hij is boven wat zij aan Hem als metgezellen toevoegen.'),
(1267, 9, 32, 'Zij wensen Gods licht met hun monden te doven, maar God weigert het Zijn licht niet te vervolmaken, ook al staat het de ongelovigen tegen.'),
(1268, 9, 33, 'Hij is het die Zijn gezant met de leidraad en de ware godsdienst gezonden heeft om hem te laten zegevieren over de gehele godsdienst, ook al staat het de veelgodendienaars tegen. *'),
(1269, 9, 34, 'Jullie die geloven! Veel van de schriftgeleerden en de monniken verteren de bezittingen der mensen door bedrog en versperren Gods weg. En hun die goud en zilver oppotten en geen bijdrage op Gods weg geven, zeg hun een pijnlijke afstraffing aan'),
(1270, 9, 35, 'op de dag dat zij in het vuur van de hel verhit zullen worden; dan zullen hun voorhoofden, hun zijden en hun ruggen verzengd worden. Dit is voor wat jullie voor jezelf opgepot hebben. Proeft dan wat jullie aan het oppotten waren.'),
(1271, 9, 36, 'Het aantal maanden is bij God twaalf maanden volgens de teboekstelling van God op de dag dat Hij de hemelen en de aarde schiep; daarvan zijn er vier heilig. Dat is de juiste godsdienst. Doet jullie jezelf daarin dus geen onrecht aan, maar bestrijdt de veelgodendienaars allen tezamen zoals zij jullie allen tezamen bestrijden. En weet dat God met de godvrezenden is.'),
(1272, 9, 37, 'Het invoegen van een schrikkelperiode is enkel maar meer ongeloof waarmee zij die ongelovig zijn tot dwaling gebracht worden. Zij staan dat het ene jaar toe en het andere jaar verbieden zij het om overeen te stemmen met het getal dat God heilig verklaard heeft. Zo verklaren zij ongewijd wat God heilig verklaard heeft. De slechtheid van hun daden is voor hen aantrekkelijk gemaakt. God wijst de ongelovige mensen de goede richting niet.'),
(1273, 9, 38, 'Jullie die geloven! Wat is er met jullie, wanneer tot jullie gezegd wordt: "Rukt uit op Gods weg", dat jullie dan bezwaard ter aarde zijgen. Bevalt het tegenwoordige leven jullie dan meer dan het hiernamaals? Maar het genot van het tegenwoordige leven is in verhouding tot het hiernamaals maar gering.'),
(1274, 9, 39, 'Als jullie niet uitrukken zal Hij jullie bestraffen met een pijnlijke bestraffing en andere mensen in jullie plaats zetten; en jullie brengen Hem geen enkele schade toe. God is almachtig.'),
(1275, 9, 40, 'Als jullie hem niet helpen? God heeft hem reeds geholpen toen zij die ongelovig zijn hem samen met een tweede verdreven, toen zij beiden in de grot waren. Toen zei hij tot zijn metgezel: "Wees niet bedroefd, God is met ons." Toen liet God Zijn kalmte op hem neerdalen en sterkte hem met troepen die jullie niet kunnen zien. En Hij gaf het woord van hen die ongelovig zijn de laagste en het woord van God de hoogste plaats. En God is machtig en wijs.'),
(1276, 9, 41, 'Rukt uit, of het jullie nu licht of zwaar valt en spant jullie met jullie bezittingen en jullie persoon in op Gods weg. Dat is beter voor jullie, als jullie dat maar wisten.'),
(1277, 9, 42, 'Als het om een gemakkelijk te behalen stoffelijk gewin en een eenvoudige reis zou gaan, dan zouden zij jou wel volgen, maar de afstand was te ver voor hen. En zij zullen bij God zweren: "Als wij konden zouden wij wel met jou uittrekken" waarbij zij zichzelf te gronde richten. God weet dat zij leugenaars zijn.'),
(1278, 9, 43, 'God vergeve je. Waarom heb jij hun vrijstelling gegeven voordat het jou duidelijk was wie oprecht waren en jij de leugenaars kende?'),
(1279, 9, 44, 'Zij die in God en de laatste dag geloven vragen jou geen ontheffing om met hun bezittingen en hun persoon ingezet te worden. God kent de godvrezenden.'),
(1280, 9, 45, 'Slechts zij die niet in God en de laatste dag geloven vragen jou om ontheffing. En hun harten verkeren in twijfel; dus aarzelen zij in hun twijfel. *'),
(1281, 9, 46, 'En als zij wensten uit te trekken, dan hadden zij voorbereidingen getroffen, maar het stond God tegen dat zij optrokken, dus hield Hij hen tegen. En er werd gezegd: "Blijft thuis met de thuisblijvers."'),
(1282, 9, 47, 'Als zij met jullie waren uitgetrokken zouden zij jullie alleen maar meer verderfelijke schade berokkenen. Zij zouden tussen jullie heen en weer gereden zijn in hun streven, jullie in verzoeking te brengen. En bij jullie zijn er die hun gehoor geven. God kent de onrechtplegers.'),
(1283, 9, 48, 'Zij hadden er vroeger al naar gestreefd jullie in verzoeking te brengen en zij stelden voor jou de zaken anders voor totdat de waarheid kwam en Gods ordening de overhand kreeg, hoewel het hun tegenstond.'),
(1284, 9, 49, 'En onder hen zijn er die zeggen: "Geef mij ontheffing en breng mij niet in verzoeking." Zij zijn toch al in de verzoeking terechtgekomen. Welnu, de hel zal de ongelovigen omvatten.'),
(1285, 9, 50, 'Als jou iets goeds treft ergeren zij zich en als jou onheil treft zeggen zij: "Wij hadden onze voorzorgsmaatregelen al genomen" en keren zich verheugd af.'),
(1286, 9, 51, 'Zeg: "Ons zal alleen maar treffen wat God voor ons te boek gesteld heeft. Hij is onze beschermheer. Op God moeten de gelovigen hun vertrouwen stellen."'),
(1287, 9, 52, 'Zeg: "Verwachten jullie voor ons iets anders dan een van de twee allermooiste dingen? Wij verwachten voor jullie dat God jullie treft met een bestraffing van Hem of door onze handen. Wachten jullie maar af; wij wachten met jullie af."'),
(1288, 9, 53, 'Zeg: "Jullie kunnen willens of onwillens bijdragen geven, het zal toch niet van jullie aangenomen worden. Jullie zijn verdorven mensen."'),
(1289, 9, 54, 'En dat hun bijdragen aangenomen werden, werd alleen maar verhinderd doordat zij geen geloof hechten aan God en Zijn gezant, alleen maar onachtzaam tot de salaat komen en slechts onwillig bijdragen geven.'),
(1290, 9, 55, 'Jij hoeft je niet te verwonderen over hun bezittingen noch over hun kinderen. God wenst hen daarmee in het tegenwoordige leven slechts te bestraffen en [wil] dat zijzelf als ongelovigen aan hun einde komen.'),
(1291, 9, 56, 'Zij zweren bij God dat zij bij jullie behoren. Toch behoren zij niet bij jullie, maar zij zijn mensen die angst hebben.'),
(1292, 9, 57, 'Als zij een toevluchtsoord of grotten of een onderaardse gang zouden vinden dan zouden zij zich daarin ijlings terugtrekken.'),
(1293, 9, 58, 'Onder hen zijn er ook die tegen jou aanmerkingen maken over de aalmoezen. Als hun ervan gegeven wordt zijn zij tevreden, maar als hun er niets van gegeven wordt dan zijn zij vergramd.'),
(1294, 9, 59, 'Als zij maar tevreden zouden zijn met wat God en Zijn gezant hun geven en zouden zeggen: "God is ons goed genoeg. God zal ons van Zijn goedgunstigheid geven en Zijn gezant zal dat ook; wij verlangen naar God." *'),
(1295, 9, 60, 'De aalmoezen zijn voor de armen en de behoeftigen, voor hen die ermee belast zijn en voor hen wier harten tot elkaar gebracht zijn, voor de vrijkoop van slaven en schuldenaren, om in te zetten op Gods weg en voor hem die onderweg is. [Dit is] een verplichting van God. God is wetend en wijs.'),
(1296, 9, 61, 'Onder hen zijn er ook die de profeet leed aandoen en zeggen: "Hij is een oor dat [alles gelooft wat het] hoort." Zeg: "Een oor dat hoort wat goed is voor jullie, dat gelooft in God en dat de gelovigen gelooft en dat een barmhartigheid is voor hen uit jullie midden die geloven. Voor hen die de gezant van God leed aandoen is er een pijnlijke bestraffing."'),
(1297, 9, 62, 'Zij bezweren jullie bij God, om jullie tevreden te stellen, maar God en Zijn gezant hebben er meer recht op door hen tevredengesteld te worden, als zij gelovigen zijn.'),
(1298, 9, 63, 'Weten zij dan niet dat er voor wie zich tegen God en Zijn gezant verzet het vuur van de hel is waarin hij altijd zal blijven? Dat is de geweldige schande.'),
(1299, 9, 64, 'De huichelaars zijn verontrust dat er tot hen een soera wordt neergezonden die hun zal meedelen wat er in hun harten is. Zeg: "Spot maar, God zal waar jullie verontrust over zijn tevoorschijn halen."'),
(1300, 9, 65, 'En als jullie zouden vragen zouden zij zeggen; "Wij kletsten maar wat en schertsten." Zeg: "Waren jullie met God, Zijn tekenen en Zijn gezant de spot aan het drijven?"'),
(1301, 9, 66, 'Verontschuldigt jullie niet. Jullie zijn ongelovig geworden na geloofd te hebben. Wij zullen een groep van jullie vergiffenis schenken en een andere groep bestraffen omdat zij boosdoeners waren.'),
(1302, 9, 67, 'De huichelaars en de huichelaarsters horen bij elkaar, zij gebieden het verwerpelijke, verbieden het behoorlijke en houden hun handen stijf op hun beurzen; zij vergeten God en dus vergeet Hij hen. De huichelaars, dat zijn de verdorvenen.'),
(1303, 9, 68, 'God heeft de huichelaars, de huichelaarsters en de ongelovigen het vuur van de hel toegezegd om daarin altijd te blijven. Dat is goed genoeg voor hen. God heeft hen vervloekt en voor hen is er een blijvende bestraffing.'),
(1304, 9, 69, 'Zoals het geval was met hen die er voor jullie tijd waren. Zij waren sterker dan jullie, hadden meer bezittingen en kinderen. Zij hebben dus het vruchtgebruik van hun aandeel gehad. Jullie hebben ook het vruchtgebruik van jullie aandeel gehad zoals zij die er voor jullie tijd waren het vruchtgebruik van hun aandeel hebben gehad en jullie hebben gekletst zoals zij gekletst hebben. Hun daden zijn vruchteloos in het tegenwoordige leven en het hiernamaals. Dat zijn de verliezers.'),
(1305, 9, 70, 'Is tot hen niet de mededeling gekomen over hen die er voor hun tijd waren, het volk van Noeh en de \'Aad en de Thamoed en het volk van Ibrahiem en de bewoners van Madjan en de ondersteboven gekeerde steden, tot wie hun gezanten met de duidelijke bewijzen kwamen. God was niet zo dat Hij hun onrecht aandeed, maar zij deden zichzelf onrecht aan.'),
(1306, 9, 71, 'Maar de gelovige mannen en vrouwen zijn elkaars medestanders, zij gebieden het behoorlijke, verbieden het verwerpelijke, verrichten de salaat, geven de zakaat en gehoorzamen God en Zijn gezant. Zij zijn het met wie God erbarmen zal hebben; God is machtig en wijs.'),
(1307, 9, 72, 'God heeft de gelovige mannen en vrouwen tuinen toegezegd waar de rivieren onderdoor stromen om daarin altijd te blijven en goede woningen in de tuinen van \'Adn. En Gods welgevallen is nog geweldiger. Dat is de geweldige triomf!'),
(1308, 9, 73, 'O profeet, stel je te weer tegen de ongelovigen en de huichelaars en pak hen stevig aan. Hun verblijfplaats is de hel; dat is pas een slechte bestemming!'),
(1309, 9, 74, 'Zij zweren bij God dat zij het niet gezegd hadden, terwijl zij het woord van ongeloof wel gesproken hadden. Zij zijn ongelovig geworden na de Islaam [de overgave aan God] aangenomen te hebben. Zij hadden iets in de zin wat zij niet bereikt hebben en zij koesteren alleen maar wrok omdat God en Zijn profeet hen door Zijn goedgunstigheid rijk hadden gemaakt. Als zij dan berouw tonen is het beter voor hen, maar als zij zich afkeren dan zal God hen met een pijnlijke bestraffing in het tegenwoordige leven en in het hiernamaals straffen. En zij hebben op de aarde geen beschermer en geen helper. *'),
(1310, 9, 75, 'Onder hen zijn er die met God een verbond gesloten hadden: "Als Hij ons van Zijn goedgunstigheid geeft zullen wij aalmoezen geven en behoren tot de rechtschapenen."'),
(1311, 9, 76, 'Maar toen Hij hun van Zijn goedgunstigheid gegeven had waren zij er gierig mee en keerden zich af terwijl zij zich afwendden.'),
(1312, 9, 77, 'En Hij liet als gevolg huichelarij in hun harten komen tot aan de dag waarop zij Hem zullen ontmoeten, omdat zij wat zij God toegezegd hadden niet zijn nagekomen en omdat zij logen.'),
(1313, 9, 78, 'Weten zij dan niet dat God hun geheimen en hun vertrouwelijke gesprekken kent en dat God de kenner is van de verborgenheden?'),
(1314, 9, 79, 'Zij die tegen de vrijwillige gevers onder de gelovigen aanmerkingen maken over de aalmoezen en ook tegen hen die alleen maar hun inspanning kunnen geven en hen dan belachelijk maken -- God maakt hen belachelijk en voor hen is er een pijnlijke bestraffing.'),
(1315, 9, 80, 'Vraag om vergeving voor hen of vraag niet om vergeving voor hen. Al vraag jij zeventig maal om vergeving voor hen, God zal hun niet vergeven. Dat komt omdat zij geen geloof hechten aan God en Zijn gezant. God wijst de verdorven mensen de goede richting niet.'),
(1316, 9, 81, 'Zij die achtergelaten zijn verheugen zich erover dat zij in tegenstelling tot de gezant van God thuisgebleven zijn en het staat hun tegen zich met hun bezittingen en hun persoon op Gods weg in te spannen en zij zeggen: "Rukt niet uit in de hitte." Zeg: "Het vuur van de hel is heter." Als zij dat maar begrijpen.'),
(1317, 9, 82, 'Dan zullen zij weinig lachen en veel huilen als vergelding voor wat zij begingen.'),
(1318, 9, 83, 'Als God jou dan terug laat keren tot een groep van hen en zij vragen jou toestemming om uit te trekken zeg dan: "Jullie zullen nooit met mij uittrekken en jullie zullen met mij tegen geen vijand strijden. Het beviel jullie de eerste maal thuis te blijven, blijft dan maar thuis met de achterblijvers."'),
(1319, 9, 84, 'Bid nooit de salaat voor een van hen die gestorven is en sta niet bij zijn graf. Zij hechtten geen geloof aan God en Zijn gezant en zij stierven als verdorvenen.'),
(1320, 9, 85, 'Jij hoeft je niet te verwonderen over hun bezittingen en hun kinderen. God wenst hen daarmee in het tegenwoordige leven slechts te bestraffen en [wil] dat zijzelf als ongelovigen aan hun einde komen.'),
(1321, 9, 86, 'En wanneer een soera wordt neergezonden dat zij in God moeten geloven en zich samen met Zijn gezant moeten inzetten, vragen de welgestelden onder hen ontheffing en zeggen: "Laat ons maar bij de thuisblijvers blijven."'),
(1322, 9, 87, 'Het bevalt hun bij de achterblijvers te zijn; hun harten zijn verzegeld, dus hebben zij geen begrip.'),
(1323, 9, 88, 'Maar de gezant en zij die met hem geloven zetten zich met hun bezittingen en hun persoon in. Zij zijn het voor wie de goede dingen zijn en zij zijn het die het welgaat.'),
(1324, 9, 89, 'God heeft voor hen tuinen klaargemaakt waar de rivieren onderdoor stromen, daarin zullen zij altijd blijven. Dat is de geweldige triomf!'),
(1325, 9, 90, 'En die bedoeïenen die verontschuldigingen hadden kwamen om vrijstelling te krijgen, maar zij die God en Zijn gezant bedriegen bleven thuis. Zij die onder hen ongelovig zijn zullen getroffen worden door een pijnlijke bestraffing.'),
(1326, 9, 91, 'De zwakken, de zieken en zij die over niets beschikken om als bijdrage te geven treft geen blaam, wanneer zij oprecht zijn tegenover God en Zijn gezant. Hun die goed doen kan niets verweten worden -- God is vergevend en barmhartig --'),
(1327, 9, 92, 'noch hun die, wanneer zij tot jou komen om van jou een rijdier te krijgen en jij moet zeggen: "Ik vind geen rijdier voor jou", zich dan omkeren terwijl hun ogen van tranen overstromen uit bedroefdheid dat zij over niets beschikken om als bijdrage te geven. *'),
(1328, 9, 93, 'Het verwijt treft slechts hen die ontheffing vragen terwijl zij rijk zijn. Het bevalt hun om bij de achterblijvers te zijn. God heeft hun harten verzegeld, dus hebben zij er geen weet van.'),
(1329, 9, 94, 'Zij verontschuldigen zich bij jou wanneer jij tot hen terugkeert. Zeg: "Verontschuldigt jullie niet, wij zullen jullie niet geloven. God heeft enkele berichten over jullie meegedeeld." God zal zien wat jullie doen en Zijn gezant ook en daarna zullen jullie tot de kenner van het verborgene en het waarneembare worden teruggebracht. Hij zal jullie dan meedelen wat jullie aan het doen waren.'),
(1330, 9, 95, 'Zij zullen jullie bij God bezweren, wanneer jullie tot hen weeromkeren, om hen met rust te laten. Laat hen maar met rust; zij zijn een gruwel en hun verblijfplaats is de hel als vergelding voor wat zij begaan hebben.'),
(1331, 9, 96, 'Zij bezweren jullie opdat zij jullie bevallen. Maar al bevallen zij jullie, de verdorven mensen bevallen God niet.'),
(1332, 9, 97, 'De bedoeïenen zijn het meest ongelovig en huichelachtig en het meest geëigend om de bepalingen niet te kennen van wat God tot Zijn gezant heeft neergezonden. God is wetend en wijs.'),
(1333, 9, 98, 'Onder de bedoeïenen zijn er die wat zij als bijdragen geven opvatten als boeteheffing en zij wachten op de wendingen van het lot voor jullie. Voor hen is de wending ten slechte! God is horend en wetend.'),
(1334, 9, 99, 'Onder de bedoeïenen zijn er ook die in God en de laatste dag geloven en die wat zij als bijdragen geven opvatten als middelen om nader tot God te komen en de gebeden van de gezant te verkrijgen; die zijn ook een middel voor hen om nader [tot God] te komen. God zal hen in Zijn barmhartigheid laten binnengaan. God is vergevend en barmhartig.'),
(1335, 9, 100, 'De allereersten van de uitgewekenen en van de meehelpers en zij die hen nagevolgd zijn in goed doen, God is met hen ingenomen en zij zijn met Hem ingenomen en Hij heeft voor hen tuinen klaargemaakt waar de rivieren onderdoor stromen, daarin zullen zij voor immer en altijd blijven. Dat is de geweldige triomf!'),
(1336, 9, 101, 'En onder de bedoeïenen uit jullie omgeving zijn er huichelaars en onder de mensen van Medina zijn er voor wie huichelarij gewoon geworden is. Jullie kennen hen niet maar Wij kennen hen. Wij zullen hen tweemaal bestraffen en dan zullen zij tot een geweldige bestraffing teruggebracht worden.'),
(1337, 9, 102, 'En anderen die hun zonden bekennen, zij vermengen een deugdelijke daad met een andere die slecht is; misschien dat God zich genadig tot hen wendt. God is vergevend en barmhartig.'),
(1338, 9, 103, 'Neem van hun bezittingen een aalmoes waarmee jij hen zult reinigen en louteren en bid de salaat voor hen; jouw gebeden zijn een geruststelling voor hen en God is horend en wetend.'),
(1339, 9, 104, 'Weten zij dan niet dat God het is die het berouw van Zijn dienaren aanvaardt en de aalmoezen aanneemt en dat God de genadegever, de barmhartige is.'),
(1340, 9, 105, 'En zeg: "Doet maar, God zal wel zien wat jullie doen en Zijn gezant en de gelovigen ook." En jullie zullen tot de kenner van het verborgene en het waarneembare worden teruggebracht. Hij zal jullie dan meedelen wat jullie aan het doen waren.'),
(1341, 9, 106, 'En anderen met betrekking tot wie wordt afgewacht tot Gods beschikking. Of Hij bestraft hen, of Hij wendt zich genadig tot hen. God is wetend en wijs.'),
(1342, 9, 107, 'Zij die zich om tegen te werken en uit ongeloof een moskee maakten, om scheuring tussen de gelovigen te veroorzaken en als steunpunt voor hen die al eerder tegen God en Zijn gezant streden, zij zweren: "Wij wensten alleen maar het beste." Maar God getuigt dat zij leugenaars zijn.'),
(1343, 9, 108, 'Stel je daarin nooit op. Een moskee die vanaf de eerste dag op godvrezendheid gegrondvest is heeft er meer recht op dat jij je erin opstelt; daarin zijn er mannen die het lief is zich te reinigen. En God bemint hen die zich reinigen.'),
(1344, 9, 109, 'Is dan iemand die zijn gebouw grondvest op vrees van God en welbehagen er beter aan toe of iemand die zijn gebouw grondvest op de rand van een afbrokkelende oever en er dan mee neerstort in het vuur van de hel? God wijst de mensen die onrecht plegen de goede richting niet.'),
(1345, 9, 110, 'Hun gebouw dat zij bouwden zal een oorzaak van twijfel in hun harten blijven tenzij hun harten breken. God is wetend en wijs.'),
(1346, 9, 111, 'God heeft van de gelovigen hun eigen persoon en hun bezittingen gekocht zodat de tuin voor hen is -- zij strijden op Gods weg, waarbij zij doden en worden gedood. Het is een toezegging waartoe Hij in waarheid verplicht is in de Taura, de Indjiel en de Koran. En wie komt zijn verbond getrouwer na dan God? Verblijdt jullie dus over de koop die jullie gesloten hebben. Dat is de geweldige triomf!'),
(1347, 9, 112, 'De berouwhebbenden, de dienenden, de lovenden, de vastenden, de buigenden, de zich eerbiedig neerbuigenden, de gebieders van het behoorlijke, de verbieders van het verwerpelijke en zij die zich aan Gods bepalingen houden? en verkondig het goede nieuws aan de gelovigen.'),
(1348, 9, 113, 'Het past de profeet en de gelovigen niet vergeving te vragen voor de veelgodendienaars ook al zijn zij verwanten, nadat het voor hen duidelijk geworden is dat zij de bewoners van het hellevuur zullen zijn.'),
(1349, 9, 114, 'Dat Ibrahiem voor zijn vader vergeving vroeg was alleen maar op grond van een toezegging die hij hem gedaan had, maar toen het hem duidelijk werd dat hij een vijand van God was brak hij met hem. Ibrahiem was vol meegevoel en zachtmoedig.'),
(1350, 9, 115, 'God is niet zo dat Hij mensen tot dwaling brengt nadat Hij hen op het goede pad gebracht heeft, zolang Hij hun niet heeft duidelijk gemaakt waarvoor zij zich moeten hoeden. God is alwetend.'),
(1351, 9, 116, 'God heeft de heerschappij over de hemelen en de aarde. Hij geeft leven en laat sterven. Jullie hebben buiten God geen beschermer en geen helper.'),
(1352, 9, 117, 'God heeft zich genadig gewend tot de profeet, de uitgewekenen en de meehelpers die hem gevolgd zijn in het uur van de tegenspoed, nadat de harten van een groep van hen bijna naar het verkeerde neigden. Toen wendde Hij zich genadig tot hen -- Hij is vol mededogen voor hen en barmhartig --'),
(1353, 9, 118, 'en ook tot de drie die achtergelaten waren totdat zij het op de aarde benauwd kregen ondanks haar ruimte en zij echt persoonlijk benauwd werden en meenden dat er geen toevluchtsoord veilig voor God was dan bij Hem. Toen wendde Hij zich genadig tot hen zodat zij berouw zouden tonen. God is de genadegever, de barmhartige.'),
(1354, 9, 119, 'Jullie die geloven! Vreest God en weest met de oprechten.'),
(1355, 9, 120, 'Het past de mensen van Medina en de bedoeïenen uit hun omgeving niet dat zij achterblijven bij de gezant van God of dat zij zichzelf meer ontzien dan hij. Dat is omdat zij door geen dorst, noch vermoeidheid, noch honger op Gods weg worden overvallen. Zij doen ook geen inval die de ongelovigen woedend maakt, noch maken zij een vijand iets afhandig zonder dat voor hen daarmee een deugdelijke daad opgeschreven wordt. God zal het loon van hen die goed doen niet verloren laten gaan.'),
(1356, 9, 121, 'En zij geven geen kleine bijdrage en geen grote en zij doorkruisen geen vallei zonder dat het opgeschreven wordt opdat God hen zal belonen voor het beste dat zij deden.'),
(1357, 9, 122, 'Het past de gelovigen niet allen tezamen uit te rukken. Van ieder onderdeel van hen zou een groep moeten uitrukken om begrip van de godsdienst te krijgen en om hun mensen te waarschuwen wanneer zij naar hen terugkeren; misschien zullen zij op hun hoede zijn.'),
(1358, 9, 123, 'Jullie die geloven! Strijdt tegen de ongelovigen die dicht bij jullie zijn en laat hen strengheid bij jullie vinden. Weet dat God met de godvrezenden is.'),
(1359, 9, 124, 'En wanneer een soera neergezonden wordt dan zijn er onder hen die zeggen: "Wie van jullie is hierdoor in zijn geloof gesterkt?" En zij die geloven worden er inderdaad in hun geloof door gesterkt terwijl zij zich verheugen.'),
(1360, 9, 125, 'En zij die in hun harten een ziekte hebben, zij worden erdoor tot een nog grotere gruwel dan zij al zijn en zij sterven als ongelovigen.'),
(1361, 9, 126, 'Zien zij dan niet dat zij in ieder jaar een of twee maal in verzoeking gebracht worden? Dan tonen zij geen berouw en laten zich niet vermanen.'),
(1362, 9, 127, 'En wanneer een soera neergezonden wordt dan kijken zij naar elkaar: "Is er iemand die jullie ziet?" En dan wenden zij zich af. God wendt hun harten af omdat zij mensen zijn die geen begrip hebben.'),
(1363, 9, 128, 'Tot jullie is een gezant uit jullie eigen midden gekomen. Iemand die het zwaar valt als jullie het moeilijk hebben, die met jullie begaan is, en jegens de gelovigen vol mededogen en barmhartig.'),
(1364, 9, 129, 'Maar als zij zich afkeren zeg dan: "God is mij goed genoeg. Er is geen god dan Hij. Op Hem stel ik mijn vertrouwen en Hij is de Heer van de geweldige troon."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'A[lif] L[aam] R[aa?]. Dat zijn de tekenen van het wijze boek.'),
(1366, 10, 2, 'Is het voor de mensen verbazingwekkend dat Wij aan een man uit hun midden geopenbaard hebben: "Waarschuw de mensen en verkondig het goede nieuws aan hen die geloven dat zij een betrouwbare voorrang bij hun Heer hebben"? De ongelovigen zeggen: "Dit is toch duidelijk een tovenaar."'),
(1367, 10, 3, 'Jullie Heer is God die de hemelen en de aarde in zes dagen geschapen heeft. Toen vestigde Hij zich op de troon om het bestuur te regelen. En er is geen bemiddelaar dan na Zijn toestemming. Dat is God jullie Heer, dient Hem dus. Zullen jullie je dan niet laten vermanen?'),
(1368, 10, 4, 'Tot Hem is jullie terugkeer, gezamenlijk. Dat is in waarheid Gods toezegging. Hij begint de schepping en Hij herhaalt haar dan om hen die geloven en de deugdelijke daden doen in rechtvaardigheid te belonen. Maar voor hen die ongelovig zijn is er een drank van gloeiend water en een pijnlijke bestraffing omdat zij ongelovig waren.'),
(1369, 10, 5, 'Hij is het die de zon gemaakt heeft als verlichting en de maan als een licht en die ervoor standen heeft verordend opdat jullie het aantal van de jaren en de berekening weten. God heeft dat slechts in waarheid geschapen. Hij zet de tekenen uiteen voor mensen die weten.'),
(1370, 10, 6, 'In het verschil van nacht en dag en in wat God geschapen heeft in de hemelen en de aarde zijn tekenen voor mensen die godvrezend zijn.'),
(1371, 10, 7, 'Zij die niet hopen op de ontmoeting met Ons, tevreden zijn met het tegenwoordige leven en er zich gerust in voelen en zij die niet op Onze tekenen letten,'),
(1372, 10, 8, 'zij zijn het voor wie de hel de verblijfplaats is voor wat zij begaan hebben.'),
(1373, 10, 9, 'Hen die geloven en de deugdelijke daden doen zal hun Heer wegens hun geloof op het goede pad voeren: onder hen stromen de rivieren in de tuinen van de gelukzaligheid.'),
(1374, 10, 10, 'Hun uitroep daarin zal zijn: "U zij geprezen, o God!", hun begroeting daarin zal zijn: "Vrede" en hun slotuitroep: "Lof zij God, de Heer van de wereldbewoners."'),
(1375, 10, 11, 'Als God het slechte voor de mensen zo zou verhaasten als zij het goede zouden willen verhaasten dan was voor hen hun termijn reeds ten einde. Maar Wij laten hen die niet hopen op de ontmoeting met Ons in hun onbeschaamdheid doorgaan met dwalen.'),
(1376, 10, 12, 'En wanneer de mens tegenspoed treft roept hij Ons aan, op zijn zij of zittend of staand, maar wanneer Wij zijn tegenspoed voor hem opgeheven hebben gaat hij verder alsof hij Ons niet had aangeroepen wegens tegenspoed die hem had getroffen. Zo is voor de onmatigen wat zij aan het doen waren aantrekkelijk gemaakt.'),
(1377, 10, 13, 'En Wij hebben de generaties die er voor jullie tijd waren vernietigd toen zij onrecht pleegden. Hun gezanten kwamen met de duidelijke bewijzen tot hen, maar zij waren niet zo dat zij geloofden. Zo vergelden Wij aan de misdadige mensen.'),
(1378, 10, 14, 'Toen hebben Wij jullie na hen als opvolgers op de aarde gezet om te kijken hoe jullie het zouden doen.'),
(1379, 10, 15, 'En wanneer Onze tekenen aan hen als duidelijke bewijzen worden voorgelezen, zeggen zij die niet op de ontmoeting met Ons hopen: "Kom met een andere Koran dan deze of verander hem." Zeg: "Het komt mij niet toe hem uit mijzelf te veranderen; ik volg alleen maar wat aan mij wordt geopenbaard. Ik vrees, als ik mijn Heer ongehoorzaam ben, voor een bestraffing op een geweldige dag."'),
(1380, 10, 16, 'Zeg: "Als God gewild had, zou ik hem jullie niet voorgelezen hebben en zou Hij hem jullie niet hebben laten weten. Ik heb toch voordien een leven lang bij jullie gewoond. Hebben jullie dan geen verstand?"'),
(1381, 10, 17, 'En wie is er zondiger dan hij die over God bedrog verzint of Zijn tekenen loochent? Het zal de boosdoeners zeker niet welgaan.'),
(1382, 10, 18, 'En zij dienen in plaats van God wat hen niet schaadt en niet nut en zij zeggen: "Dezen zijn onze bemiddelaars bij God." Zeg: "Willen jullie dan aan God iets meedelen wat Hij in de hemelen en op de aarde niet kent?" Hij zij geprezen, verheven als Hij is boven wat zij aan Hem als metgezellen toevoegen.'),
(1383, 10, 19, 'De mensen waren [oorspronkelijk] slechts één gemeenschap, maar toen werden zij het oneens. En als er al niet eerder een woord van jouw Heer gekomen was, dan was er tussen hen al een beslissing getroffen over dat waarover zij het oneens zijn.'),
(1384, 10, 20, 'En zij zeggen: "Had er dan niet een teken van zijn Heer tot hem neergezonden kunnen worden?" Zeg dan: "Het verborgene behoort God toe. Wachten jullie dus maar af; ik wacht met jullie ook af."'),
(1385, 10, 21, 'En wanneer Wij de mensen barmhartigheid laten proeven na een rampspoed die hen getroffen heeft dan hebben zij meteen iets te beramen tegen Onze tekenen. Zeg: "God is sneller in het beramen. Onze gezanten schrijven op wat jullie beramen."'),
(1386, 10, 22, 'Hij is het die jullie op het vasteland en op zee laat reizen totdat er, wanneer zij op de schepen zijn die hen met een goede wind meevoeren en zij zich erover verheugen, tot hen een stormwind komt en de golven van overal komen en zij denken dat zij omkomen. Dan roepen zij God aan waarbij zij de godsdienst geheel aan Hem wijden: "Als U ons hieruit redt dan behoren wij tot hen die dank betuigen."'),
(1387, 10, 23, 'Maar wanneer Hij hen gered heeft gedragen zij zich op de aarde meteen weer onrechtmatig. O mensen, jullie zelfzucht is ten koste van jullie zelf; het is slechts het genot van het tegenwoordige leven. Daarna is jullie terugkeer tot Ons. Wij zullen jullie dan meedelen wat jullie aan het doen waren.'),
(1388, 10, 24, 'Het tegenwoordige leven lijkt bijvoorbeeld op water dat Wij laten neerdalen uit de hemel. De planten van de aarde die door de mensen en het vee gegeten worden groeien daardoor in [bonte] mengeling totdat dan, wanneer de aarde de tooi ervan aangenomen heeft en ermee versierd is en haar bewoners denken dat zij er over kunnen beschikken, Onze beslissing \'s nachts of overdag tot ze komt. Wij maken het dan tot een stoppelveld alsof het niet nog gisteren gebloeid had. Zo zetten Wij de tekenen uiteen voor mensen die nadenken.'),
(1389, 10, 25, 'En God roept op tot de woning van de vrede en Hij voert wie Hij wil op een juiste weg.'),
(1390, 10, 26, 'Voor hen die goed doen is er het beste en nog meer; hun gezichten zullen niet met grauwheid of vernedering worden overdekt. Zij zijn het die in de tuin thuishoren; zij zullen daarin altijd blijven.'),
(1391, 10, 27, 'Maar voor hen die de slechte daden begaan is de vergelding voor een slechte daad iets overeenkomstigs; zij zullen met vernedering overdekt worden. Zij hebben tegen God geen beschermer. Het is alsof hun gezichten bedekt zijn met stukken van de duistere nacht. Zij zijn het die in het vuur thuishoren; daarin zullen zij altijd blijven.'),
(1392, 10, 28, 'En op de dag dat Wij hen allen verzamelen, dan zullen Wij tot de aanhangers van het veelgodendom zeggen: "Op jullie plaats, jullie en jullie [zogenaamd goddelijke] metgezellen!" En dan brengen Wij scheiding tussen hen aan. En hun [zogenaamd goddelijke] metgezellen zeggen: "Ons hebben jullie niet gediend.'),
(1393, 10, 29, 'God is goed genoeg als getuige tussen ons en jullie. Wij hebben niet op jullie eredienst gelet."'),
(1394, 10, 30, 'Daar zal iedere ziel ondervinden wat zij vroeger gedaan heeft. Dan worden zij tot God, hun ware beschermheer, teruggebracht en wat zij verzonnen hebben zijn zij kwijt.'),
(1395, 10, 31, 'Zeg: "Wie voorziet in jullie onderhoud vanuit de hemel en de aarde? Of wie heerst over horen en zien? En wie laat het levende uit het dode voortkomen en laat het dode uit het levende voortkomen? En wie regelt het bestuur?" Zij zullen zeggen: "God." Zeg: "Zullen jullie dan niet godvrezend worden?"'),
(1396, 10, 32, 'Dat is God jullie Heer. Wat is er dan na de waarheid nog, behalve dwaling? Hoe kunnen jullie dan zo afgeleid worden?'),
(1397, 10, 33, 'Zo werd het woord van jouw Heer bewaarheid tegen hen die verdorven zijn, omdat zij niet geloven.'),
(1398, 10, 34, 'Zeg: "Is er onder jullie [zogenaamd goddelijke] metgezellen iemand die de schepping begint en haar daarna herhaalt?" Zeg: "God begint de schepping en herhaalt haar daarna. Hoe kunnen jullie je zo laten verstrooien?"'),
(1399, 10, 35, 'Zeg: "Is er onder jullie [zogenaamd goddelijke] metgezellen iemand die tot de waarheid leidt?" Zeg: "God leidt tot de waarheid." Heeft dan iemand die tot de waarheid leidt er meer recht op gevolgd te worden of iemand die het goede pad niet volgt, maar zelf de goede richting gewezen moet worden? Wat is er dan met jullie? Hoe kunnen jullie oordelen?'),
(1400, 10, 36, 'De meesten van hen volgen slechts een vermoeden, maar vermoeden baat tegen de waarheid niets. God weet wat zij doen.'),
(1401, 10, 37, 'Het is niet zo dat deze Koran buiten God om verzonnen kan worden, maar het is een bevestiging van wat er voordien al was en de uiteenzetting door de Heer van de wereldbewoners van het boek waaraan geen twijfel is.'),
(1402, 10, 38, 'Of zeggen zij: "Hij heeft het verzonnen"? Zeg: "Komt dan met een overeenkomstige soera en roept wie jullie er buiten God om kunnen oproepen maar op, als jullie gelijk hebben."'),
(1403, 10, 39, 'Welnee, zij loochenen wat zij niet met hun kennis kunnen omvatten en waarvan de uitleg nog niet tot hen gekomen is. Zo hebben zij die er voor hun tijd waren ook van leugens beticht. Kijk dan hoe het einde was van de onrechtplegers.'),
(1404, 10, 40, 'En onder hen zijn er die erin geloven en onder hen zijn er die er niet in geloven, maar jouw Heer kent de verderfbrengers het best.'),
(1405, 10, 41, 'En als zij jou van leugens betichten, zeg dan: "Voor mij is wat ik doe en voor jullie wat jullie doen. Jullie hebben niets te maken met wat ik doe en ik heb niets te maken met wat jullie doen."'),
(1406, 10, 42, 'En onder hen zijn er die naar jou luisteren, maar ben jij het die de doven kunt laten horen, ook als zij geen verstand hebben?'),
(1407, 10, 43, 'En onder hen zijn er die naar jou kijken, maar ben jij het die de blinden kunt leiden, ook als zij geen inzicht hebben?'),
(1408, 10, 44, 'God doet de mensen geen enkel onrecht aan, maar de mensen doen zichzelf onrecht aan.'),
(1409, 10, 45, 'En op de dag dat Hij hen verzamelt, is het alsof het voor hen maar een uur van de dag geduurd heeft, terwijl zij elkaar herkennen. Zij die de ontmoeting met God loochenden zijn verloren; zij volgden het goede pad niet.'),
(1410, 10, 46, 'Of Wij jou iets van wat Wij hun toezeggen laten zien of dat Wij jou wegnemen, tot Ons is hun terugkeer. God is dan getuige over wat zij doen.'),
(1411, 10, 47, 'En voor elke gemeenschap is er een gezant. Wanneer dan hun gezant tot hen komt wordt tussen hen met rechtvaardigheid beslist en hun wordt geen onrecht aangedaan.'),
(1412, 10, 48, 'En zij zeggen: "Wanneer zal deze aanzegging zich voordoen, als jullie gelijk hebben?"'),
(1413, 10, 49, 'Zeg: "Ik geen heb macht om mezelf tot schade of tot nut te zijn, afgezien van wat God wil. Voor iedere gemeenschap is er een termijn; wanneer hun termijn komt dan zullen zij geen uur te laat ontboden worden noch te vroeg."'),
(1414, 10, 50, 'Zeg: "Hoe zien jullie het? Als Zijn bestraffing \'s nachts tot jullie komt of overdag -- wat zouden de boosdoeners er dan van willen verhaasten? --'),
(1415, 10, 51, 'zullen jullie dan wanneer het gebeurt erin geloven? Nu wel? En jullie hadden het nog wel willen verhaasten."'),
(1416, 10, 52, 'Dan wordt tegen hen die onrecht plegen gezegd: "Proeft de altijddurende bestraffing. Wordt aan jullie iets anders vergolden dan wat jullie begaan hebben?" *'),
(1417, 10, 53, 'En zij zullen jou vragen om mee te delen of het waar is. Zeg: "Ja, bij mijn Heer het is waar en jullie kunnen er niets tegen doen."'),
(1418, 10, 54, 'En als iedereen die onrecht pleegde zou bezitten wat er op de aarde is, dan zou hij zich ermee willen vrijkopen. Heimelijk hebben zij dan spijt wanneer zij de bestraffing zien. En er wordt tussen hen met rechtvaardigheid beslist en hun wordt geen onrecht aangedaan.'),
(1419, 10, 55, 'Zeker, van God is wat er in de hemelen en de aarde is. Gods toezegging is zeker waar, maar de meesten van hen weten het niet.'),
(1420, 10, 56, 'Hij geeft leven en Hij laat sterven en tot Hem zullen jullie terugkeren.'),
(1421, 10, 57, 'O mensen, tot jullie is een aansporing van jullie Heer gekomen en een genezing voor wat er in de harten is en een leidraad en barmhartigheid voor de gelovigen.'),
(1422, 10, 58, 'Zeg: "Over Gods goedgunstigheid en over Zijn barmhartigheid, daarover moeten zij zich verheugen. Dat is beter dan wat zij bijeenbrengen."'),
(1423, 10, 59, 'Zeg: "Hoe lijkt het jullie? Wat God voor jullie als levensonderhoud heeft laten neerdalen en waarvan jullie verboden en toegestane dingen hebben gemaakt?" Zeg: "Heeft God jullie toestemming gegeven of verzinnen jullie iets over God?"'),
(1424, 10, 60, 'En wat zal de mening van hen die over God bedrog verzinnen op de opstandingsdag zijn? God is vol van goedgunstigheid jegens de mensen, maar de meeste mensen zijn niet dankbaar.'),
(1425, 10, 61, 'Jij bent met geen zaak bezig, jij leest daarover geen Koran voor en jullie doen helemaal niets zonder dat Wij over jullie getuigen zijn wanneer jullie ermee aan de slag gaan. En aan jouw Heer ontgaat er geen greintje op de aarde noch in de hemel. En er is niets wat nog kleiner is, noch groter of het staat in een duidelijk boek.'),
(1426, 10, 62, 'Zeker, Gods helpers hebben niets te vrezen, noch zullen zij bedroefd zijn,'),
(1427, 10, 63, 'die geloofden en godvrezend waren.'),
(1428, 10, 64, 'Voor hen is het goede nieuws in het tegenwoordige leven en het hiernamaals. Gods woorden zijn niet te veranderen. Dat is de geweldige triomf.'),
(1429, 10, 65, 'Laat wat zij zeggen jou niet bedroefd maken. De macht behoort God geheel toe. Hij is de horende, de wetende.'),
(1430, 10, 66, 'Zeker, van God is wie in de hemelen en wie op de aarde is. Wat volgen zij die [zogenaamd goddelijke] metgezellen in plaats van God aanroepen? Zij volgen alleen maar vermoedens en zij gissen slechts.'),
(1431, 10, 67, 'Hij is het die voor jullie de nacht gemaakt heeft om erin te rusten en de dag om te zien. Daarin zijn tekenen voor mensen die horen.'),
(1432, 10, 68, 'Zij zeggen: "God heeft zich een kind genomen." Geprezen zij Hij. Hij is behoefteloos, van Hem is wat er in de hemelen en wat er op de aarde is. Jullie hebben hiervoor geen machtiging. Zeggen jullie over God waar jullie geen weet van hebben?'),
(1433, 10, 69, 'Zeg: "Het zal hun niet welgaan die over God bedrog verzinnen."'),
(1434, 10, 70, '[Zij hebben slechts] genot in het tegenwoordige leven. Daarna is hun terugkeer tot Ons. Dan zullen Wij hun de strenge bestraffing laten proeven, omdat zij ongelovig waren. *'),
(1435, 10, 71, 'En draag hun de mededeling over Noeh voor. Toen hij tot zijn volk zei: "O mensen, al is mijn optreden en mijn herinneren aan Gods tekenen jullie te veel, toch blijf ik op God mijn vertrouwen stellen. Wordt het samen met jullie [zogenaamd goddelijke] metgezellen eens over jullie zaak en laat dat voor jullie dan ook niet vaag zijn. Houdt je dan met mij bezig en laat mij dan niet wachten.'),
(1436, 10, 72, 'Als jullie je dan afkeren, ik heb jullie geen loon gevraagd. Slechts God is belast met mijn loon. En mij is bevolen tot hen te behoren die zich [aan God] overgeven."'),
(1437, 10, 73, 'Zij betichtten hem toen van leugens, maar Wij redden hem en wie er met hem in het schip waren en Wij maakten hen tot opvolgers. Maar Wij lieten hen die Onze tekenen loochenden verdrinken. Kijk dan hoe het einde was van de gewaarschuwden.'),
(1438, 10, 74, 'Toen zonden Wij na hem gezanten naar hun volk; zij brachten hun de duidelijke bewijzen. Maar zij waren niet zo dat zij geloofden in wat zij eerder geloochend hadden. Zo verzegelen Wij de harten van hen die overtredingen begaan.'),
(1439, 10, 75, 'Toen zonden Wij na hen Moesa en Haroen met Onze tekenen naar Fir\'aun en zijn raad van voornaamsten, maar zij waren hoogmoedige en misdadige mensen.'),
(1440, 10, 76, 'Toen dan de waarheid van Ons tot hen kwam zeiden zij: "Dit is duidelijk toverij."'),
(1441, 10, 77, 'Moesa zei: "Zeggen jullie [dat] tegen de waarheid wanneer die tot jullie komt? Is dit toverij? Tovenaars zal het niet welgaan."'),
(1442, 10, 78, 'Zij zeiden: "Ben jij tot ons gekomen om ons daarvan af te brengen waarvan wij gemerkt hebben dat onze vaderen eraan gewoon waren en opdat de hoogste macht in het land voor jullie beiden zal zijn? Maar wij hechten aan jullie geen geloof."'),
(1443, 10, 79, 'En Fir\'aun zei: "Breng mij elke kundige tovenaar."'),
(1444, 10, 80, 'En toen de tovenaars kwamen zei Moesa tot hen: "Werpt wat jullie te werpen hebt."'),
(1445, 10, 81, 'En toen zij geworpen hadden zei Moesa: "Wat jullie vertoond hebben is toverij! God zal het tenietdoen. God geeft geen goede afloop aan het werk van de verderfbrengers."'),
(1446, 10, 82, 'God bevestigt de waarheid met Zijn woorden ook al staat het de boosdoeners tegen.'),
(1447, 10, 83, 'Moesa werd slechts door enige jongeren van zijn volk geloofd ondanks hun vrees voor Fir\'aun en hun voornaamsten dat hij hen in verzoeking zou brengen. En Fir\'aun had de overhand in het land en hij behoorde tot de onmatigen.'),
(1448, 10, 84, 'En Moesa zei: "Mijn volk! Als jullie in God geloven, moeten jullie op Hem je vertrouwen stellen als jullie behoren tot hen die zich [aan God] overgeven."'),
(1449, 10, 85, 'Toen zeiden zij: "Op God stellen wij ons vertrouwen. Onze Heer, maak ons niet tot een verzoeking voor de mensen die onrecht plegen.'),
(1450, 10, 86, 'En red ons in Uw barmhartigheid van de ongelovige mensen."'),
(1451, 10, 87, 'En Wij openbaarden aan Moesa en zijn broeder: "Zoekt voor jullie volk in Egypte onderdak in huizen en brengt in jullie huizen een gebedsrichting aan en verricht de salaat en verkondigt het goede nieuws aan de gelovigen."'),
(1452, 10, 88, 'En Moesa zei: "Onze Heer, U hebt aan Fir\'aun en zijn raad van voornaamsten luister en bezittingen in het tegenwoordige leven gegeven, onze Heer, om [hen] van Uw weg te laten afdwalen. Onze Heer, verwijder hun bezittingen en verhard hun harten zodat zij niet geloven tot zij de pijnlijke bestraffing zien."'),
(1453, 10, 89, 'Hij zei: "Jullie beider gebed is verhoord. Handelt dan correct en volgt niet de weg van hen die niet weten." *'),
(1454, 10, 90, 'En Wij lieten de Israëlieten de zee oversteken, waarop Fir\'aun en zijn troepen hen uit opstandigheid en om te overtreden achtervolgden. Maar toen ten slotte de verdrinking hem verraste zei hij: "Ik geloof dat er geen god is dan Hij in wie de Israëlieten geloven en ik behoor tot hen die zich [aan God] overgeven."'),
(1455, 10, 91, 'Nu wel? Terwijl jij eerst ongehoorzaam was en tot de verderfbrengers behoorde!'),
(1456, 10, 92, 'Vandaag zullen Wij jou met je lichaam redden opdat jij voor wie na jou komen een teken zult zijn. Maar veel van de mensen letten niet op Onze tekenen.'),
(1457, 10, 93, 'En Wij gaven de Israëlieten onderdak in een betrouwbaar onderkomen en voorzagen met goede dingen in hun onderhoud en zij zijn het pas oneens geworden nadat tot hen de kennis gekomen was. Jouw Heer zal op de opstandingsdag tussen hen oordelen over dat waarover zij het oneens waren.'),
(1458, 10, 94, 'Maar als jij in twijfel verkeert over wat Wij naar jou hebben neergezonden vraag dan aan hen die het boek al van voor jouw tijd lezen. Tot jou is de waarheid van jouw Heer gekomen, wees dus niet een van hen die het in twijfel trekken.'),
(1459, 10, 95, 'En wees niet een van hen die Gods tekenen loochenen; dan zul jij tot de verliezers behoren.'),
(1460, 10, 96, 'Zij, tegen wie het woord van jouw Heer zich bewaarheid heeft, zullen niet geloven --'),
(1461, 10, 97, 'ook al komt elk teken tot hen -- totdat zij de pijnlijke bestraffing zien.'),
(1462, 10, 98, 'Waarom is er geen andere stad geweest die tot geloof kwam en die dan nut van haar geloof had dan het volk van Joenoes? Toen zij tot geloof kwamen hieven Wij de bestraffing van de schande in het tegenwoordige leven van hen op en Wij gaven hun een tijdelijk vruchtgebruik.'),
(1463, 10, 99, 'En als jouw Heer het had gewild, hadden wie er op de aarde zijn allen geloofd. Of kun jij de mensen dwingen gelovigen te worden?'),
(1464, 10, 100, 'Niemand kan geloven zonder Gods toestemming en Hij legt de gruwelijke straf op aan hen die niet verstandig zijn.'),
(1465, 10, 101, 'Zeg: "Kijkt naar wat er in de hemelen en op de aarde is." Maar de tekenen en de waarschuwingen baten de mensen niet die niet geloven.'),
(1466, 10, 102, 'Kunnen zij dan iets anders verwachten dan wat overeenkomt met de dagen van hen die voor jullie tijd zijn heengegaan? Zeg: "Wachten jullie maar af; ik behoor met jullie tot de afwachtenden."'),
(1467, 10, 103, 'Dan redden Wij onze gezanten en hen die geloven. Zo redden Wij, als een verplichting voor Ons, de gelovigen.'),
(1468, 10, 104, 'Zeg: "O mensen, als jullie in twijfel verkeren over mijn godsdienst: Ik dien hen die jullie in plaats van God dienen niet, maar ik dien God die jullie zal wegnemen. En mij is bevolen te behoren tot hen die geloven"'),
(1469, 10, 105, 'en: "Richt jouw aangezicht naar de godsdienst als een aanhanger van het zuivere geloof en wees niet een van de veelgodendienaars."'),
(1470, 10, 106, 'En roep in plaats van God niet iets aan wat jou niet nut en niet schaadt. Als jij dat doet, dan behoor jij zeker tot de onrechtplegers.'),
(1471, 10, 107, 'En als God je met tegenspoed treft dan is er niemand die dat kan wegnemen behalve Hij. En als Hij voor jou iets goeds wenst dan is er niemand die Zijn goedgunstigheid kan tegenhouden. Hij treft ermee wie van Zijn dienaren Hij wil. Hij is de vergevende, de barmhartige.'),
(1472, 10, 108, 'Zeg: "O mensen, tot jullie is de waarheid van jullie Heer gekomen. Wie zich de goede richting laat wijzen, die volgt het goede pad slechts tot zijn eigen voordeel en wie dwaalt, dwaalt slechts tot zijn eigen nadeel. En ik ben geen voogd over jullie'),
(1473, 10, 109, 'En volg wat aan jou wordt geopenbaard en wees geduldig totdat God oordeelt, want Hij is de beste van hen die oordelen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'A[lif] L[aam] R[aa?]. Een boek waarvan de tekenen eenduidig zijn vastgesteld en daarna van de kant van een Wijze en Welingelichte uiteengezet:'),
(1475, 11, 2, 'Jullie moeten alleen God dienen. Ik ben voor jullie van Zijn kant een waarschuwer en een verkondiger van goed nieuws.'),
(1476, 11, 3, 'En vraagt jullie Heer om vergeving en wendt jullie dan berouwvol tot Hem. Hij zal jullie tot een vastgestelde termijn van een goed vruchtgebruik laten genieten en Hij zal aan ieder die verdienstelijk is zijn verdienste geven. Maar als jullie je afkeren dan vrees ik voor jullie de bestraffing op een geweldige dag.'),
(1477, 11, 4, 'Tot God is jullie terugkeer en Hij is almachtig.'),
(1478, 11, 5, 'Toch vouwen zij hun harten dicht om zich voor Hem te verbergen. Maar wanneer zij zich met hun kleren bedekken weet Hij wat zij in het verborgene en wat zij openlijk doen. Hij weet wat er binnen in de harten is.'),
(1479, 11, 6, 'En er is geen dier op aarde of God zorgt voor zijn levensonderhoud en Hij kent zijn verblijfplaats en zijn bewaarplaats. Alles staat in een duidelijk boek.'),
(1480, 11, 7, 'Hij is het die de hemelen en de aarde in zes dagen geschapen heeft, terwijl Zijn troon boven het water was, om jullie op de proef te stellen wie van jullie het beste is bij wat hij doet. En als jij zegt: jullie zullen na de dood weer opgewekt worden" dan zeggen zij die ongelovig zijn: "Dit is duidelijk slechts toverij."'),
(1481, 11, 8, 'En als Wij de bestraffing voor hen tot een bepaalde tijd uitstellen zeggen zij: "Wat houdt haar tegen?" Toch kan zij op de dag dat zij over hen komt niet van hen afgewend worden en worden zij door dat waarmee zij de spot dreven ingesloten.'),
(1482, 11, 9, 'En als Wij van Onze kant de mens barmhartigheid laten proeven en dan weer van hem wegnemen dan is hij wanhopig en ondankbaar.'),
(1483, 11, 10, 'En als Wij hem dan weldadigheid laten proeven na rampspoed die hem getroffen heeft dan zegt hij: "Voor mij zijn de slechte tijden voorbij." En hij is verheugd en verwaand.'),
(1484, 11, 11, 'Maar zo zijn zij die geduldig zijn en de deugdelijke daden doen niet; zij zijn het voor wie vergeving is en een groot loon.'),
(1485, 11, 12, 'Misschien zou jij een deel van wat jou geopenbaard wordt willen weglaten en is jouw hart benauwd omdat zij zeggen: "Als er nu een schat tot hem was neergezonden of een engel met hem was meegekomen?" Maar jij bent slechts een waarschuwer en God is voogd over alles.'),
(1486, 11, 13, 'Of zeggen zij: "Hij verzint het." Zeg: "Komt dan met tien overeenkomstige soera\'s die verzonnen zijn en roept aan wie jullie in plaats van God maar kunnen, als jullie gelijk hebben.'),
(1487, 11, 14, 'Maar als zij jullie niet verhoren, weet dan dat het met Gods kennis is neergezonden en dat er geen god is dan Hij. Zullen jullie je dan [aan God] overgeven?"'),
(1488, 11, 15, 'Wie het tegenwoordige leven en zijn praal wensen, aan hen zullen Wij hun daden vergoeden en hun zal niet te kort gedaan worden.'),
(1489, 11, 16, 'Zij zijn het voor wie in het hiernamaals slechts het vuur is. Vruchteloos is wat zij erin gedaan hebben en wat zij deden wordt tenietgedaan.'),
(1490, 11, 17, 'En hij die op een duidelijk bewijs van zijn Heer steunt dan? En die gevolgd wordt door een getuige van Hem en voor wiens tijd er al het boek van Moesa was als voorbeeld en als barmhartigheid? Dat zijn zij die in Hem geloven. En wie van de partijen geen geloof aan Hem hecht, het vuur is de plaats die voor hem is aangewezen. Verkeer daarover dus niet in twijfel; het is de waarheid van jouw Heer. Maar de meeste mensen geloven niet.'),
(1491, 11, 18, 'En wie is er zondiger dan wie over God bedrog verzint? Zij zijn het die voor hun Heer worden voorgeleid en de getuigen zullen zeggen: "Dat zijn zij die over God gelogen hebben." Zeker, Gods vloek is over de onrechtplegers,'),
(1492, 11, 19, 'die Gods weg versperren en verlangen dat het een kronkelweg is, terwijl zij aan het hiernamaals geen geloof hechten.'),
(1493, 11, 20, 'Zij kunnen er op de aarde niets tegen doen en zij hebben buiten God geen beschermers. De bestraffing zal voor hen verdubbeld worden. Zij konden niet horen en zij zagen niet.'),
(1494, 11, 21, 'Zij zijn het die zichzelf verloren hebben en wat zij verzonnen hebben zijn zij kwijt.'),
(1495, 11, 22, 'Het staat vast dat zij in het hiernamaals de grootste verliezers zijn.'),
(1496, 11, 23, 'Zij die geloven, de deugdelijke daden doen en nederig op hun Heer vertrouwen, zij zijn het die in de tuin thuishoren; zij zullen altijd daarin blijven. *'),
(1497, 11, 24, 'De twee groepen lijken bijvoorbeeld op de blinde en dove en de ziende en horende. Zijn die twee bijvoorbeeld dan gelijk? Zullen jullie je dan niet laten vermanen?'),
(1498, 11, 25, 'Wij hebben Noeh tot zijn volk gezonden [met]: "Ik ben voor jullie een duidelijke waarschuwer.'),
(1499, 11, 26, 'Jullie moeten alleen God dienen. Ik vrees voor jullie de bestraffing op een pijnlijke dag."'),
(1500, 11, 27, 'En de voornaamsten, zij die van zijn volk ongelovig waren, zeiden toen: "Wij zien dat jij slechts een mens bent zoals wij en wij zien dat jij alleen maar ondoordacht gevolgd wordt door de allerverachtelijksten onder ons. Wij zien niet dat jullie op ons iets voor hebben. Integendeel, wij vermoeden dat jullie leugenaars zijn."'),
(1501, 11, 28, 'Hij zei: "Mijn volk! Hoe zien jullie het? Als ik op een duidelijk bewijs van mijn Heer steun en Hij mij van Zijn kant barmhartigheid gegeven heeft maar jullie er blind voor gemaakt zijn, zullen wij jullie er dan toe dwingen terwijl het jullie tegenstaat?'),
(1502, 11, 29, 'En, mijn volk, ik vraag jullie er geen betaling voor. Slechts God is belast met mijn loon. En ik jaag hen die geloven niet weg; zij zullen hun Heer ontmoeten. Maar ik zie dat jullie mensen zijn die niets weten.'),
(1503, 11, 30, 'Mijn volk! Wie zou mij tegen God kunnen helpen als ik hen weg zou jagen? Zullen jullie je dan niet laten vermanen?'),
(1504, 11, 31, 'En ik zeg niet tot jullie dat ik Gods schatkamers bezit, noch ken ik het verborgene, ook zeg ik niet dat ik een engel ben, noch zeg ik tegen hen die in jullie ogen verachtelijk zijn dat God hun niets goeds zal geven. God weet het best wat in jullie binnenste is, anders zou ik tot de onrechtplegers behoren."'),
(1505, 11, 32, 'Zij zeiden: "O Noeh, jij hebt met ons getwist, je hebt veel twist met ons gehad. Kom dan maar met wat je ons toegezegd hebt als je gelijk hebt."'),
(1506, 11, 33, 'Hij zei: "God zal ermee komen als Hij wil en jullie kunnen er niets tegen doen.'),
(1507, 11, 34, 'En mijn raad heeft voor jullie ook geen nut als ik jullie goede raad zou wensen te geven, wanneer God jullie wenst te misleiden. Hij is jullie Heer en tot Hem zullen jullie terugkeren."'),
(1508, 11, 35, 'Of zij zeggen: "Hij heeft het verzonnen." Zeg: "Als ik het verzonnen heb dan wordt mijn wandaad mij aangerekend, maar ik heb niets te maken met de wandaden die jullie bedrijven."'),
(1509, 11, 36, 'En aan Noeh is geopenbaard: "Niemand van jouw volk zal tot geloof komen, afgezien van hen die al geloofden. Wees dus niet bedroefd over wat zij aan het doen waren.'),
(1510, 11, 37, 'Bouw dan het schip onder Onze ogen en volgens Onze openbaring, maar spreek Ons niet aan over hen die onrecht plegen; zij zullen verdronken worden."'),
(1511, 11, 38, 'En hij bouwde het schip en telkens als er voornaamsten van zijn volk voorbijkwamen maakten zij hem belachelijk. Hij zei: "Jullie maken ons belachelijk, maar wij zullen jullie belachelijk maken, zoals jullie [ons nu] belachelijk maken.'),
(1512, 11, 39, 'En jullie zullen weten tot wie de bestraffing zal komen die hem te schande maakt en op wie een blijvende bestraffing zal neerkomen."'),
(1513, 11, 40, 'Toen dan Onze beschikking kwam en de oven overkookte, zeiden Wij: "Belaad het met van alles twee stuks, paarsgewijs, en met jouw familie behalve hem over wie al sprake geweest is, en met wie geloven." Maar slechts weinigen geloofden met hem. *'),
(1514, 11, 41, 'En hij zei: "Gaat aan boord. In Gods naam zal het afvaren en afmeren. Mijn Heer is vergevend en barmhartig."'),
(1515, 11, 42, 'En het [schip] voer met hen weg door golven als bergen en Noeh riep naar zijn zoon die apart stond: "Mijn zoon! Kom met ons aan boord en wees niet een van de ongelovigen."'),
(1516, 11, 43, 'Hij zei: "Ik zal wel een onderkomen op een berg vinden die mij tegen het water zal beschermen." Hij zei: "Tegen Gods beschikking is er vandaag geen beschermer behalve voor hen met wie Hij erbarmen heeft." En de golven kwamen tussen hen beiden in en zo werd hij een van de verdronkenen.'),
(1517, 11, 44, 'En er werd gezegd: "O aarde, slik je water in en o hemel, droog op" en het water nam af. De beslissing was gevallen. En het [schip] kwam op de Djoedi vast te zitten. En er werd gezegd: "Weg met de mensen die onrecht plegen."'),
(1518, 11, 45, 'En Noeh riep tot zijn Heer en zei: "Mijn Heer, mijn zoon behoort tot mijn familie; Uw toezegging is de waarheid en U bent de wijste van hen die oordelen."'),
(1519, 11, 46, 'Hij zei: "O Noeh, hij behoort niet tot jouw familie. Hij is [een toonbeeld van] ondeugdelijk handelen. Vraag Mij niet iets waarvan jij geen kennis hebt. Ik vermaan je niet tot de onwetenden te behoren."'),
(1520, 11, 47, 'Hij zei: "Mijn Heer, ik zoek bij U bescherming dat ik niets vraag waarvan ik geen kennis heb. En als U mij niet vergeeft en erbarmen met mij hebt, dan behoor ik tot de verliezers."'),
(1521, 11, 48, 'Er werd tot hem gezegd: "O Noeh, ga van boord in vrede die van Ons komt en met zegeningen voor jou en voor gemeenschappen van hen die bij jou zijn. Er zijn ook gemeenschappen die Wij laten genieten, maar dan treft hen een pijnlijke bestraffing van Ons."'),
(1522, 11, 49, 'Dat behoort tot de mededelingen over het verborgene die Wij aan jou openbaren en die jij noch jouw volk van tevoren kenden. Wees dan geduldig; het [goede] uiteinde komt de godvrezenden toe.'),
(1523, 11, 50, 'En tot de \'Aad [was] hun broeder Hoed [gezonden]. Hij zei: "Mijn volk! Dient God; jullie hebben geen andere god dan Hem. Jullie verzinnen alleen maar wat.'),
(1524, 11, 51, 'Ik vraag jullie er geen loon voor. Met mijn loon is slechts Hij belast die mij geschapen heeft. Hebben jullie dan geen verstand?'),
(1525, 11, 52, 'En, mijn volk, vraagt jullie Heer om vergeving en wendt je dan berouwvol tot Hem. Hij zal dan de hemel in overvloed over jullie laten regenen en jullie nog meer kracht geven. Maar keert jullie niet als boosdoeners af."'),
(1526, 11, 53, 'Zij zeiden: "O Hoed, jij hebt ons geen duidelijk bewijs gebracht en wij verlaten onze goden niet omdat jij het zegt; wij geloven jou niet.'),
(1527, 11, 54, 'Wij zeggen alleen maar dat een god van ons jou met iets slechts heeft getroffen." Hij zei: "Ik roep God als getuige aan en ook jullie moeten getuigen dat ik niets te maken heb met wat jullie [aan God] als metgezellen toevoegen,'),
(1528, 11, 55, 'buiten Hem om. Beraamt dan maar tezamen listen tegen mij en laat mij dan niet wachten.'),
(1529, 11, 56, 'Ik stel mijn vertrouwen op God, mijn Heer en jullie Heer. Er is geen dier op aarde of Hij houdt het bij zijn kuif vast. Mijn Heer is op een juiste weg.'),
(1530, 11, 57, 'En als jullie je afkeren, dan heb ik jullie dat verkondigd waarmee ik tot jullie gezonden ben. En mijn Heer zal maken dat een ander volk jullie opvolgt. En jullie brengen Hem geen enkele schade toe. Mijn Heer is het die over alles waakt."'),
(1531, 11, 58, 'En toen Onze beschikking kwam redden Wij Hoed en hen die met hem geloofden door barmhartigheid van Ons; Wij redden hen van een harde bestraffing.'),
(1532, 11, 59, 'Dat waren dan de \'Aad; zij verwierpen de tekenen van hun Heer en zij waren aan Zijn gezanten ongehoorzaam en zij volgden het bevel op van elke weerspannige geweldenaar.'),
(1533, 11, 60, 'En zij werden in het tegenwoordige leven en op de opstandingsdag door een vloek vervolgd. Zeker, de \'Aad hechtten geen geloof aan hun Heer. Weg dan met de \'Aad, het volk van Hoed.'),
(1534, 11, 61, 'En tot de Thamoed [was] hun broeder Salih [gezonden]. Hij zei: "Mijn volk! Dient God; jullie hebben geen andere god dan Hem. Hij heeft jullie uit de aarde laten ontstaan en je erop laten vestigen. Vraagt Hem dus om vergeving en wendt jullie dan berouwvol tot Hem. Mijn Heer is nabij en verhorende'),
(1535, 11, 62, 'Zij zeiden: "O Salih, jij was tot nu toe bij ons iemand van wie men iets kon verwachten. Zul jij ons verbieden te dienen wat onze vaderen dienden? Wij verkeren namelijk in hevige twijfel over dat waartoe jij ons oproept."'),
(1536, 11, 63, 'Hij zei: "Mijn volk! Hoe zien jullie het? Als ik op een duidelijk bewijs van mijn Heer steun en Hij mij barmhartigheid van Hem gegeven heeft, wie zou mij dan tegen God kunnen helpen als ik Hem ongehoorzaam zou zijn? Jullie brengen mij alleen maar meer verlies.'),
(1537, 11, 64, 'En, mijn volk, dit is Gods kameelmerrie; voor jullie een teken. Laat haar op Gods aarde eten en treft haar niet met kwaad, want dan zal een nabije bestraffing jullie grijpen."'),
(1538, 11, 65, 'Maar zij sneden haar hielpezen door. Toen zei hij: "Jullie kunnen nog drie dagen in jullie woning genieten. Dat is een ongelogen aanzegging."'),
(1539, 11, 66, 'En toen Onze beschikking kwam redden Wij Salih en hen die met hem geloofden door barmhartigheid van Ons en van de schande van die dag. Jouw Heer is de krachtige, de machtige.'),
(1540, 11, 67, 'Toen greep de schreeuw hen die onrecht deden en \'s morgens lagen zij in hun woningen dood op de grond,'),
(1541, 11, 68, 'alsof zij er niet lang woonachtig waren geweest. Zeker, de Thamoed hechtten geen geloof aan hun Heer. Weg dan met de Thamoed.'),
(1542, 11, 69, 'Ook waren Onze gezanten met het goede nieuws tot Ibrahiem gekomen. Zij zeiden: "Vrede!" Hij zei: "Vrede!" En het duurde niet lang of hij kwam met een geroosterd kalf.'),
(1543, 11, 70, 'En toen hij zag dat hun handen er niet aan kwamen kreeg hij argwaan tegen hen en werd door vrees voor hen bevangen. Zij zeiden: "Wees niet bang, wij zijn tot het volk van Loet gezonden."'),
(1544, 11, 71, 'En zijn vrouw stond erbij en lachte. Toen verkondigden Wij haar het goede nieuws van Ishaak en Ja\'koeb na hem.'),
(1545, 11, 72, 'Zij zei: "Wee mij! Zal ik een kind krijgen terwijl ik een oude vrouw ben en mijn echtgenoot hier een oude man? Dit is wel iets wonderlijks. "'),
(1546, 11, 73, 'Zij zeiden:"Verwonder jij je over Gods beschikking? Gods barmhartigheid en Zijn zegeningen zijn voor jullie, mensen van het huis. Hij is lofwaardig en glorierijke'),
(1547, 11, 74, 'Toen Ibrahiem van de schrik bekomen was en het goede nieuws tot hem gekomen was begon hij met Ons te twisten over het volk van Loet.'),
(1548, 11, 75, 'Ibrahiem was zachtmoedig, vol meegevoel en schuldbewust.'),
(1549, 11, 76, '"O Ibrahiem, houd hiermee op. De beschikking van jouw Heer is gekomen; over hen komt een onafwendbare bestraffing."'),
(1550, 11, 77, 'En toen Onze gezanten tot Loet kwamen was hij bezorgd over hen en maakte zich ongerust over hen. Hij zei: "Dit is een hachelijke dag."'),
(1551, 11, 78, 'En zijn volk kwam op hem toegesneld; zij hadden voordien al slechte daden begaan. Hij zei: "Mensen, hier zijn mijn dochters, zij zijn reiner voor jullie. Vreest dan God en maakt mij niet te schande om mijn gasten. Is er onder jullie geen verstandige man?"'),
(1552, 11, 79, 'Zij zeiden: "Jij weet wel dat wij op jouw dochters geen recht hebben en jij weet best wat wij wensen."'),
(1553, 11, 80, 'Hij zei: "Had ik maar macht over jullie of kon ik maar mijn toevlucht nemen tot een sterke lijfwacht."'),
(1554, 11, 81, 'Zij zeiden: "O Loet, wij zijn gezanten van jouw Heer. Zij zullen je niet kunnen bereiken. Vertrek met je familie in een deel van de nacht en niemand van jullie mag zich omdraaien. Uitgezonderd jouw vrouw, haar zal treffen wat hen treft. De morgen is het tijdstip dat voor hen aangewezen is. Is de morgen niet dichtbij?"'),
(1555, 11, 82, 'Toen Onze beschikking dan kwam keerden Wij haar ondersteboven en lieten er op elkaar volgende bakstenen op regenen,'),
(1556, 11, 83, 'die bij jouw Heer gemerkt zijn en die niet ver van de onrechtplegers weg zijn.'),
(1557, 11, 84, 'En tot de Madjan [was] hun broeder Sjoe\'aib [gezonden]. Hij zei: "Mijn volk! Dient God; jullie hebben geen andere god dan Hem. En doet niet tekort in maat en gewicht. Ik zie dat het jullie goed gaat, maar ik vrees voor jullie de bestraffing op een veelomvattende dag.'),
(1558, 11, 85, 'En, mijn volk, geeft volle maat en gewicht in billijkheid en doet de mensen niet tekort in de dingen die van hen zijn en veroorzaakt geen ellende op de aarde door verderf te zaaien.'),
(1559, 11, 86, 'Wat bij God overblijft is beter voor jullie als jullie gelovig zijn. En ik ben niet iemand die over jullie waakt."'),
(1560, 11, 87, 'Zij zeiden: "O Sjoe\'aib, dragen jouw gebeden jou op dat wij wat onze vaderen dienden verlaten of dat wij ophouden met onze bezittingen te doen wat wij willen? Jij bent toch de zachtmoedige, de verstandige."'),
(1561, 11, 88, 'Hij zei: "Mijn volk! Hoe zien jullie het? Als ik op een duidelijk bewijs van mijn Heer steun en Hij van Zijn kant goed in mijn levensonderhoud voorziet? Ik wil jullie ook niet tegenspreken over wat ik jullie verbied. Ik wens alleen maar zo goed als ik kan orde op zaken te stellen. Mijn welslagen hangt alleen van God af; op Hem stel ik mijn vertrouwen en tot Hem wend ik mij schuldbewust.'),
(1562, 11, 89, 'En, mijn volk, laat de onmin met mij jullie er niet toe brengen dat jullie hetzelfde treft dat het volk van Noeh of het volk van Hoed of het volk van Salih heeft getroffen. En het volk van Loet is niet ver van jullie.'),
(1563, 11, 90, 'Vraagt jullie Heer om vergeving en wendt jullie dan berouwvol tot Hem; mijn Heer is barmhartig en liefdevol."'),
(1564, 11, 91, 'Zij zeiden: "O Sjoe\'aib, wij begrijpen niet veel van wat jij zegt en wij zien dat jij bij ons weerloos bent. Als jouw aanhang er niet was, dan hadden we je gestenigd; jij hebt immers geen macht over ons."'),
(1565, 11, 92, 'Hij zei: "Mijn volk! Maakt mijn aanhang meer indruk op jullie dan God en hebben jullie Hem achtergesteld? Mijn Heer omvat wat jullie doen.'),
(1566, 11, 93, 'En, mijn volk, handelt naar jullie vermogen; ik doe dat ook. En dan zullen jullie wel weten tot wie de bestraffing komt die hem te schande maakt en wie er een leugenaar is. Wacht maar af; ik wacht met jullie af."'),
(1567, 11, 94, 'En toen Onze beschikking kwam redden Wij Sjoe\'aib en hen die met hem geloofden door barmhartigheid van Ons. Toen greep de schreeuw hen die onrecht deden en \'s morgens lagen zij in hun woningen dood op de grond,'),
(1568, 11, 95, 'alsof zij er niet lang woonachtig geweest waren. Weg dan met de Madjan zoals de Thamoed weg zijn.'),
(1569, 11, 96, 'En Wij hebben Moesa toch met Onze tekenen en een duidelijke machtiging gezonden'),
(1570, 11, 97, 'naar Fir\'aun en zijn raad van voornaamsten. Zij nu volgden het bevel op van Fir\'aun, maar het bevel van Fir\'aun was niet verstandig.'),
(1571, 11, 98, 'Hij zal op de opstandingsdag voor zijn volk uit gaan en hen naar beneden naar de drinkplaats bij het vuur brengen. Dat is pas een slechte drinkplaats om naartoe gebracht te worden!'),
(1572, 11, 99, 'En zij werden in dit [leven] en op de opstandingsdag door een vloek achtervolgd. Dat is pas een slechte gift wanneer die gegeven wordt.'),
(1573, 11, 100, 'Dat zijn enkele berichten over de steden die Wij jou vertellen; sommige staan nog overeind andere zijn weggemaaid.'),
(1574, 11, 101, 'Wij deden hun geen onrecht aan maar zij deden zichzelf onrecht aan en hun goden die zij in plaats van God aanriepen baatten hun niets toen de beschikking van jouw Heer kwam. Zij stortten hen alleen maar verder in het verderf.'),
(1575, 11, 102, 'Zo is de manier van aanpakken van jouw Heer wanneer Hij de steden aanpakt terwijl zij onrecht plegen; Zijn manier van aanpakken is pijnlijk en streng.'),
(1576, 11, 103, 'Daarin is een teken voor wie de bestraffing van het hiernamaals vreest. Dat is een dag waarvoor de mensen bijeengebracht worden en dat is een dag waarvan men getuige zal zijn.'),
(1577, 11, 104, 'Wij stellen haar slechts uit tot een vastgestelde termijn.'),
(1578, 11, 105, 'Op de dag dat zij komt zal iemand slechts met Zijn toestemming spreken. Onder hen zijn er ellendigen en gelukzaligen.'),
(1579, 11, 106, 'Zij die er ellendig aan toe zijn, zij zullen in het vuur zijn om erin te steunen en te kreunen.'),
(1580, 11, 107, 'Altijd zullen zij daarin blijven zolang de hemelen en de aarde bestaan -- behalve als jouw Heer het anders wil -- jouw Heer doet altijd wat Hij wenst. *'),
(1581, 11, 108, 'Zij die gelukzalig zijn, zij zullen in de tuin zijn. Altijd zullen zij daarin blijven zolang de hemelen en de aarde bestaan -- behalve als jouw Heer het anders wil -- als een ononderbroken gave.'),
(1582, 11, 109, 'Verkeer dus niet in twijfel over wat dezen dienen. Zij dienen slechts zoals hun vaderen vroeger dienden. Wij zullen hun volle aandeel onverkort aan hen vergoeden.'),
(1583, 11, 110, 'Wij hebben het boek aan Moesa gegeven, maar toen werd men het erover oneens. En als er al niet eerder een woord van jouw Heer gekomen was, dan was er tussen hen al een beslissing getroffen. Zij verkeren daarover in hevige twijfel.'),
(1584, 11, 111, 'Maar aan allen zal jouw Heer hun daden zeker vol vergoeden. Hij is welingelicht over wat zij doen.'),
(1585, 11, 112, 'Handel correct zoals aan jou bevolen is en aan wie met jou berouw tonen en wees niet onbeschaamd; Hij doorziet wel wat jullie doen.'),
(1586, 11, 113, 'En zoekt geen steun bij hen die onrecht plegen, dan zal het vuur jullie treffen. En jullie hebben buiten God geen beschermers; dan zullen jullie geen hulp krijgen.'),
(1587, 11, 114, 'En verricht de salaat op de beide uiteinden van de dag en in de vroege uren van de nacht. De goede daden verdrijven de slechte. Dat is een vermaning voor hen die gedenken.'),
(1588, 11, 115, 'En wees geduldig; God laat het loon van hen die goed doen niet verloren gaan.'),
(1589, 11, 116, 'Als er onder de generaties van voor jullie tijd maar mensen met bezonnenheid waren geweest die verboden op de aarde verderf te brengen; er waren er slechts enkelen, die Wij redden. Zij die onrecht deden joegen het hun verleende luxeleven na en zij waren boosdoeners.'),
(1590, 11, 117, 'Jouw Heer is niet zo dat Hij de steden ten onrechte vernietigt, terwijl hun inwoners zich beteren.'),
(1591, 11, 118, 'Als jouw Heer het had gewild, had Hij de mensen tot één gemeenschap gemaakt, maar zij blijven het oneens,'),
(1592, 11, 119, 'behalve zij met wie jouw Heer erbarmen heeft. Daartoe heeft Hij hen geschapen. Zo werd het woord van jouw Heer vervuld: "Ik zal de hel vullen met djinn en mensen tezamen."'),
(1593, 11, 120, 'Al de berichten over de gezanten die Wij jou vertellen zijn om jouw hart te versterken en de waarheid komt daarin tot jou en een aansporing en een vermaning voor de gelovigen.'),
(1594, 11, 121, 'En zeg tegen hen die niet geloven: "Handelt naar jullie vermogen, wij doen dat ook'),
(1595, 11, 122, 'en wacht af, wij wachten ook af."'),
(1596, 11, 123, 'En van God is het verborgene van de hemelen en de aarde. Hem komt de gehele zeggenschap toe. Dien Hem dus en stel je vertrouwen op Hem. Jouw Heer let goed op wat jullie doen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'A[lif] L[aam] R[aa?]. Dat zijn de tekenen van het duidelijke boek.'),
(1598, 12, 2, 'Wij hebben het als een Arabische Koran neergezonden; misschien zullen jullie verstandig worden.'),
(1599, 12, 3, 'Wij vertellen aan jou het mooiste verhaal doordat Wij aan jou deze Koran geopenbaard hebben ook al behoorde jij hiervoor tot de onoplettenden.'),
(1600, 12, 4, 'Toen Joesoef tot zijn vader zei: "Vader, ik heb elf sterren en de zon en de maan gezien; ik zag ze zich eerbiedig voor mij neerbuigend'),
(1601, 12, 5, 'Deze zei: "Mijn zoon, vertel je droom niet aan je broers; zij zouden een list tegen je kunnen beramen. De satan is namelijk een verklaarde vijand van de mens.'),
(1602, 12, 6, 'Zo zal jouw Heer jou uitkiezen, jou in de uitleg van droomverhalen onderwijzen en Zijn genade aan jou en aan het geslacht van Ja\'koeb volledig bewijzen, zoals Hij dat vroeger gedaan heeft aan jouw voorvaderen Ibrahiem en Ishaak. Jouw Heer is wetend en wijs."'),
(1603, 12, 7, 'Er waren in [het verhaal van] Joesoef en zijn broers tekenen voor hen die [erom] vragen.'),
(1604, 12, 8, 'Toen zij zeiden: "Joesoef en zijn broer zijn bij onze vader meer geliefd dan wij, terwijl wij toch een hele groep zijn. Onze vader verkeert in duidelijke dwaling.'),
(1605, 12, 9, 'Doodt Joesoef of verdrijft hem naar een [afgelegen] land dan zal het aangezicht van jullie vader niet van jullie worden afgeleid en dan zullen jullie daarna rechtschapen mensen zijn."'),
(1606, 12, 10, 'Iemand uit hun midden zei: "Doodt Joesoef niet maar werpt hem op de bodem van de waterput, dan zal een reisgezelschap hem wel oppikken, als jullie echt iets doen willen."'),
(1607, 12, 11, 'Zij zeiden: "O onze vader, waarom vertrouw jij ons niet met Joesoef, terwijl wij hem toch goed gezind zijn?'),
(1608, 12, 12, 'Stuur hem morgen met ons mee om goed te eten en om te spelen; wij zullen wel over hem waken."'),
(1609, 12, 13, 'Hij zei: "Ik word er bedroefd van als jullie met hem weggaan en ik vrees dat een wolf hem opeet terwijl jullie niet op hem letten."'),
(1610, 12, 14, 'Zij zeiden: "Als een wolf hem opeet terwijl wij toch met een hele groep zijn, dan zijn wij werkelijk verliezers."'),
(1611, 12, 15, 'Toen zij dan met hem weggingen kwamen zij overeen hem op de bodem van de waterput te zetten. En Wij openbaarden aan hem: "Jij zult hun over deze zaak van hen nog wel mededelingen doen, terwijl zij het niet beseffen."'),
(1612, 12, 16, 'En zij kwamen \'s avonds huilend bij hun vader.'),
(1613, 12, 17, 'Zij zeiden: "O onze vader, wij gingen weg om tegen elkaar te rennen en wij lieten Joesoef bij onze spullen achter en toen heeft een wolf hem opgegeten. Maar jij zult ons toch niet geloven, ook al spreken wij de waarheid."'),
(1614, 12, 18, 'Maar zij hadden wel vals bloed op zijn hemd aangebracht. Hij zei: "Jullie hebben jezelf iets wijsgemaakt! Dus maar mooi geduldig verdragen. En God is het wiens hulp gevraagd moet worden tegen wat jullie beschrijven."'),
(1615, 12, 19, 'En er kwam een reisgezelschap, en zij stuurden hun waterhaler, en hij liet zijn emmer neer. Hij zei: "Goed nieuws! Hier is een jongen." En zij verborgen hem als koopwaar. Maar God wist wat zij deden.'),
(1616, 12, 20, 'En zij verkochten hem onder de prijs, voor een paar dirhams; zij hechtten niet veel belang aan hem.'),
(1617, 12, 21, 'Hij die hem gekocht had -- uit Egypte was hij -- zei tot zijn vrouw: "Zorg dat hij goed wordt ondergebracht. Misschien dat hij ons tot nut is of dat wij hem als kind aannemen." Zo gaven Wij Joesoef een vaste positie in het land; ook om hem de uitleg van droomverhalen te onderwijzen. En God is meester van Zijn beschikking, maar de meeste mensen weten het niet.'),
(1618, 12, 22, 'Toen hij volgroeid was gaven Wij hem oordeelskracht en kennis. Zo belonen Wij hen die goed doen.'),
(1619, 12, 23, 'En zij in wier huis hij was probeerde hem te verleiden en zij sloot de deuren en zei: "Kom maar hier." Hij zei: "Dat verhoede God. Hij die mijn heer is heeft mij goed ondergebracht. De onrechtplegers zal het niet welgaan."'),
(1620, 12, 24, 'Maar zij begeerde hem en hij zou haar begeerd hebben als hij niet het bewijs van zijn Heer gezien had. Zulks, opdat Wij het kwaad en de gruwelijkheid van hem afwendden. Hij behoorde tot Onze uitverkoren dienaren.'),
(1621, 12, 25, 'En zij renden beiden naar de deur en zij scheurde zijn hemd van achteren en zij troffen haar meester bij de deur aan. Zij zei: "De vergelding voor iemand die jouw familie kwaad wil doen is toch dat hij gevangengezet wordt of een pijnlijke bestraffing?"'),
(1622, 12, 26, 'Hij zei: "Zij probeerde mij te verleiden." En een getuige uit haar familie getuigde: "Als zijn hemd van voren gescheurd is, dan is zij oprecht en behoort hij tot de leugenaars.'),
(1623, 12, 27, 'Maar als zijn hemd van achteren gescheurd is, dan liegt zij en behoort hij tot de oprechten."'),
(1624, 12, 28, 'En toen hij zijn hemd zag, dat het van achteren gescheurd was zei hij: "Dat is weer een list van jullie vrouwen; jullie listen zijn geweldig.'),
(1625, 12, 29, 'Joesoef, houd je daar niet mee bezig. En jij, vrouw, vraag om vergeving voor je zonde; jij bent wel een van hen die verkeerd gedaan hebben."'),
(1626, 12, 30, 'En de vrouwen in de stad zeiden: "De vrouw van de excellentie probeert haar slaaf te verleiden. Zij is verliefd op hem geworden. Wij zien dat zij in duidelijke dwaling verkeert."'),
(1627, 12, 31, 'Maar toen zij van hun geroddel hoorde ontbood zij hen en maakte voor hen een buffet klaar en zij gaf ieder van hen een mes. Toen zei zij: "Kom voor hen tevoorschijn." En toen zij hem zagen vonden zij hem geweldig. Zij sneden zich in hun handen en zeiden: "God beware! Dit is geen mens, dit is niets anders dan een voortreffelijke engel."'),
(1628, 12, 32, 'Zij zei: "En dat is hij nu over wie jullie mij verwijten gemaakt hebben. Ik heb inderdaad geprobeerd hem te verleiden en hij heeft weerstand geboden. Maar als hij niet doet wat ik van hem verlang dan zal hij gevangengezet worden en een van de gekleineerden zijn."'),
(1629, 12, 33, 'Hij zei: "Mijn Heer, de gevangenis is mij liever dan dat waartoe zij mij oproepen en als U hun list niet van mij afwendt dan zal ik mij tot hen aangetrokken voelen en tot de onwetenden behoren."'),
(1630, 12, 34, 'Daarop verhoorde zijn Heer hem en wendde hun list van hem af. Hij is de horende, de wetende.'),
(1631, 12, 35, 'Toen leek het hun, nadat zij de tekenen gezien hadden, goed toe om hem voor een tijd gevangen te zetten.'),
(1632, 12, 36, 'Twee slaven kwamen met hem de gevangenis in. Een van hen zei: "Ik zag mijzelf wijn persen." En de ander zei: "Ik zag mijzelf brood boven op mijn hoofd dragen waarvan de vogels aten. Deel ons de uitleg ervan mee. Wij zien dat jij behoort tot hen die goed doen."'),
(1633, 12, 37, 'Hij zei: "Er zal tot jullie geen voedsel komen waarmee jullie verzorgd worden of ik zal jullie de uitleg ervan meegedeeld hebben voordat het tot jullie komt. Dat behoort namelijk tot wat mijn Heer mij onderwezen heeft. Ik heb het geloof verlaten van mensen die niet in God geloven en die aan het hiernamaals geen geloof hechten.'),
(1634, 12, 38, 'Maar ik volg het geloof van mijn voorvaderen Ibrahiem, Ishaak en Ja\'koeb. Het past ons niet aan God ook maar iets als metgezel toe te voegen. Dat is een deel van Gods genade aan ons en aan de mensen. Maar de meeste mensen betuigen geen dank.'),
(1635, 12, 39, 'Jullie twee medegevangenen! Zijn verschillende heren beter of God, de ene, de albeheerser?'),
(1636, 12, 40, 'Wat jullie in plaats van God dienen zijn alleen maar namen die jullie en jullie vaderen gegeven hebben waarvoor God geen enkele machtiging had neergezonden. Het oordeel komt alleen God toe. Hij beveelt dat jullie alleen Hem dienen. Dat is de juiste godsdienst, maar de meeste mensen weten het niet.'),
(1637, 12, 41, 'Jullie twee medegevangenen! De een van jullie zal zijn heer wijn schenken en de ander zal gekruisigd worden en de vogels zullen van zijn hoofd eten. De zaak waarover jullie om uitsluitsel vroegen is al beslist."'),
(1638, 12, 42, 'En hij zei tot hem van de twee van wie hij vermoedde dat hij bevrijd zou worden: "Gedenk mij bij jouw heer." Maar de satan liet hem vergeten om [hem bij] zijn heer te gedenken en zo bleef hij nog een paar jaar in de gevangenis.'),
(1639, 12, 43, 'En de koning zei: "Ik zag zeven vette koeien die door zeven magere werden opgegeten en ook zeven groene aren en andere die droog waren. Raad van voornaamsten! Geeft mij uitsluitsel over mijn droom als jullie dromen kunnen verklaren."'),
(1640, 12, 44, 'Zij zeiden: "Een wirwar van dromen en wij weten geen dromen uit te leggen!"'),
(1641, 12, 45, 'En hij van de twee die bevrijd was en die zich na een tijd herinnerde zei: "Ik zal jullie de uitleg ervan meedelen, zendt mij er dus op uit."'),
(1642, 12, 46, '"Joesoef, jij waarheidslievende, geef ons uitsluitsel over zeven vette koeien die door zeven magere werden opgegeten en ook zeven groene aren en andere die droog waren. Misschien ga ik dan terug naar de mensen opdat zij het misschien weten."'),
(1643, 12, 47, 'Hij zei: "Jullie zullen zeven jaren volgens de gewoonte zaaien en wat jullie dan oogsten laat dat in de aren, op een klein deel na dat jullie opeten.'),
(1644, 12, 48, 'Dan zullen daarna zeven harde jaren komen, die wat jullie daartoe van tevoren hebben klaargelegd zullen opeten, op een klein deel na dat jullie kunnen opslaan.'),
(1645, 12, 49, 'Dan komt er daarna een jaar waarin de mensen regen zullen krijgen en waarin jullie sap zullen persen."'),
(1646, 12, 50, 'De koning zei: "Brengt hem bij mij." Maar toen de bode bij hem kwam zei hij: "Ga terug naar jouw heer en vraag hem hoe het gesteld is met de vrouwen die in hun handen sneden. Mijn Heer kent hun list."'),
(1647, 12, 51, 'Hij zei: "Wat was er met jullie toen jullie probeerden Joesoef te verleiden?" Zij zeiden: "God beware! Wij weten niets kwaads over hem." De vrouw van de excellentie zei: "Nu komt de waarheid aan het licht! Ik was het die hem probeerde te verleiden en hij behoort bij de oprechten."'),
(1648, 12, 52, '[Joesoef zei:] "Dat is, opdat hij weet dat ik hem niet in het verborgene heb verraden en dat God de list van de verraders niet goed laat aflopen. *'),
(1649, 12, 53, 'En ik pleit mijzelf niet vrij, want de ziel is toch iets wat tot het kwaad aanzet, behalve wanneer mijn Heer erbarmen heeft. Mijn Heer is vergevend en barmhartig."'),
(1650, 12, 54, 'De koning zei: "Brengt hem bij mij. Ik wil hem tot mijn vertrouweling maken." En toen hij tot hem sprak, zei hij: "Heden heb jij je positie bij ons gevestigd en geniet jij vertrouwen."'),
(1651, 12, 55, 'Hij zei: "Stel mij aan over de voorraadschuren van het land. Ik ben een kundig beheerder."'),
(1652, 12, 56, 'Zo gaven Wij Joesoef een machtige positie in het land om zich erin te vestigen waar hij wilde. Wij treffen met Onze barmhartigheid wie Wij willen. Wij laten het loon van hen die goed doen niet verloren gaan.'),
(1653, 12, 57, 'En het loon van het hiernamaals is beter voor hen die geloven en die godvrezend zijn.'),
(1654, 12, 58, 'En de broers van Joesoef kwamen en gingen bij hem binnen en hij herkende hen, maar zij herkenden hem niet.'),
(1655, 12, 59, 'En toen hij hen van hun proviand voorzien had zei hij: "Brengt een broer van jullie van vaderskant mee. Zien jullie niet dat ik een vol rantsoen geef en dat ik de beste ben van hen die een onderkomen geven?'),
(1656, 12, 60, 'En als jullie hem niet meebrengen, dan is er bij mij helemaal geen rantsoen voor jullie en dan hoeven jullie mij niet te benaderen."'),
(1657, 12, 61, 'Zij zeiden: "Wij zullen proberen zijn vader daartoe over te halen, dat zullen we zeker doen."'),
(1658, 12, 62, 'En hij zei tot zijn slaven: "Doet hun koopwaar in hun reiszakken, misschien zullen zij ze herkennen wanneer zij bij hun mensen weergekeerd zijn; misschien zullen zij terugkeren."'),
(1659, 12, 63, 'Toen zij dan bij hun vader teruggekeerd waren zeiden zij: "O onze vader, ons wordt het rantsoen ontzegd; stuur dus onze broer mee om een rantsoen te krijgen; wij zullen wel over hem waken."'),
(1660, 12, 64, 'Hij zei: "Kan ik hem jullie op een andere manier toevertrouwen dan zoals ik vroeger zijn broer aan jullie heb toevertrouwd? En God waakt het best en Hij is de barmhartigste van de barmhartigen."'),
(1661, 12, 65, 'Toen zij hun spullen openden vonden zij dat hun koopwaar was teruggegeven en zij zeiden: "O onze vader, wat wensen wij nog meer, onze koopwaar is aan ons teruggegeven. Wij zullen onze mensen voorzien, over onze broer waken en een kameellading meer rantsoen krijgen; dat is een gemakkelijk rantsoen!"'),
(1662, 12, 66, 'Hij zei: "Ik zal hem niet met jullie meesturen zolang jullie mij niet voor God een waarborg geven dat jullie hem weer naar mij brengen, behalve als jullie omkomen." Toen zij dan hun waarborg gegeven hadden zei hij: "God staat garant voor wat jullie zeggen."'),
(1663, 12, 67, 'En hij zei: "O mijn zonen, gaat niet door één poort naar binnen, maar gaat door verschillende poorten naar binnen. Maar tegen God baat ik jullie niets. Het oordeel komt alleen God toe. Op Hem stel ik mijn vertrouwen en op Hem moeten zij die vertrouwen hebben hun vertrouwen stellen.'),
(1664, 12, 68, 'En toen zij binnengegaan waren waarlangs hun vader hen bevolen had, baatte hun dat tegen God niets. Het was alleen maar een innerlijke behoefte van Ja\'koeb die hij bevredigde. En hij was zeker iemand die kennis had van wat Wij hem onderwezen hadden. Maar de meeste mensen weten het niet.'),
(1665, 12, 69, 'Toen zij bij Joesoef binnenkwamen haalde hij zijn broer bij zich. Hij zei: "Ik ben het, jouw broer; wees dus niet bedroefd over wat zij gedaan hebben."'),
(1666, 12, 70, 'Maar toen hij hen van hun proviand voorzien had, deed hij de drinkbeker in de reiszak van zijn broer. Daarop riep een omroeper uit: "Hé, jullie van de karavaan, jullie zijn dieven."'),
(1667, 12, 71, 'Zij zeiden terwijl zij op hem toekwamen: "Wat missen jullie dan?"'),
(1668, 12, 72, 'Zij zeiden: "Wij missen de bokaal van de koning. Wie hem terugbrengt die krijgt een kameellading. Daarvoor sta ik borg."'),
(1669, 12, 73, 'Zij zeiden: "Bij God! Jullie weten wel dat wij niet gekomen zijn om op aarde verderf te brengen en wij zijn geen dieven."'),
(1670, 12, 74, 'Zij zeiden: "Wat is dan de vergelding ervoor als jullie leugenaars zijn?"'),
(1671, 12, 75, 'Zij zeiden: "De vergelding ervoor is dat hij, in wiens reiszak hij gevonden wordt, het zelf moet ontgelden. Zo vergelden wij aan de onrechtplegers."'),
(1672, 12, 76, 'En hij begon met hun zakken vóór de zak van zijn broer. Toen haalde hij hem tevoorschijn uit de zak van zijn broer. Zo beraamden Wij een list voor Joesoef. Hij had volgens de wet van de koning zijn broer niet vast kunnen houden, maar [het gebeurde zo] omdat God het wilde. Wij geven hogere rangen aan wie Wij willen. En boven ieder die kennis bezit is er een wetende. *'),
(1673, 12, 77, 'Zij zeiden: "Als hij al steelt; een broer van hem heeft vroeger ook gestolen." Maar Joesoef hield het in zijn binnenste geheim en liet het aan hen niet blijken. Hij zei: "Jullie verkeren in een nog slechtere positie. En God weet het best wat jullie beschrijven."'),
(1674, 12, 78, 'Zij zeiden: "O excellentie, hij heeft een oude bejaarde vader; neem dus een van ons in zijn plaats. Wij zien dat u behoort tot hen die goed doen."'),
(1675, 12, 79, 'Hij zei: "Dat verhoede God, dat ik iemand anders zou nemen dan hem bij wie wij ons eigendom gevonden hebben. Anders zouden wij onrechtplegers zijn."'),
(1676, 12, 80, 'Toen zij dan voor hem geen hoop meer hadden gingen zij apart staan voor een vertrouwelijk gesprek. De oudste van hen zei: "Weten jullie niet van de waarborg die jullie je vader voor God hebben gegeven en wat jullie eerder met Joesoef niet zijn nagekomen? En ik zal het land niet verlaten totdat mijn vader het mij toestaat of God oordeel velt ten gunste van mij; Hij is de beste van hen die oordelen.'),
(1677, 12, 81, 'Keert terug naar jullie vader en zegt: \'O onze vader, jouw zoon heeft gestolen en wij geven alleen maar getuigenis van wat wij weten en over het verborgene waken wij niet.'),
(1678, 12, 82, 'En vraag de stad waarin wij waren en de karavaan waarmee wij gekomen zijn. Wij zijn echt oprecht."'),
(1679, 12, 83, 'Hij zei: "Jullie hebben jezelf iets wijsgemaakt! Dus maar mooi geduldig verdragen. Misschien dat God hen gezamenlijk bij mij zal brengen. Hij is de wetende, de wijze."'),
(1680, 12, 84, 'En hij wendde zich van hen af en zei: "Wat een verdriet om Joesoef." En zijn ogen werden wit van droefheid, maar hij hield zijn woede in.'),
(1681, 12, 85, 'Zij zeiden: "Bij God, jij houdt niet op aan Joesoef te denken, totdat jij ineenstort of bij hen behoort die te gronde gaan."'),
(1682, 12, 86, 'Hij zei: "Ik klaag mijn verdriet en droefheid alleen maar aan God. En ik weet van God wat jullie niet weten.'),
(1683, 12, 87, 'Mijn zonen, gaat heen om inlichtingen over Joesoef en zijn broer in te winnen. Aan Gods bemoediging wanhopen slechts de ongelovige mensen."'),
(1684, 12, 88, 'Toen zij dan bij hem binnenkwamen zeiden zij: "O excellentie, ons en onze familie heeft tegenspoed getroffen en wij komen met onbeduidende koopwaar, maar geef ons toch een vol rantsoen en geef het ons als aalmoes. God beloont hen die aalmoezen geven."'),
(1685, 12, 89, 'Hij zei: "Weten jullie wat jullie Joesoef en zijn broer aangedaan hebben toen jullie zo dom waren."'),
(1686, 12, 90, 'Zij zeiden: "Ben jij werkelijk Joesoef?" Hij zei: "Ik ben Joesoef en dit is mijn broer. God heeft ons een gunst bewezen. Als iemand godvrezend is en geduldig volhardt dan laat God het loon van hen die goed doen niet verloren gaan."'),
(1687, 12, 91, 'Zij zeiden: "Bij God! God heeft aan jou wel de voorkeur boven ons gegeven. En wij hebben echt verkeerd gedaan."'),
(1688, 12, 92, 'Hij zei: "Jullie wordt vandaag niets verweten. God zal jullie vergeven en Hij is de barmhartigste van de barmhartigen.'),
(1689, 12, 93, 'Gaat heen met dit hemd van mij en werpt het over het gezicht van mijn vader dan wordt hij ziende en brengt jullie familie allemaal naar mij."'),
(1690, 12, 94, 'En toen de karavaan afreisde zei hun vader: "Ik bespeur de geur van Joesoef. Als jullie nu maar niet gaan zeggen dat ik seniel word."'),
(1691, 12, 95, 'Zij zeiden: "Bij God! Jij verkeert weer in je oude dwaling."'),
(1692, 12, 96, 'Maar toen de verkondiger van het goede nieuws kwam wierp die het hemd over zijn gezicht en hij werd weer ziende. Hij zei: "Heb ik jullie niet gezegd dat ik van God weet wat jullie niet weten."'),
(1693, 12, 97, 'Zij zeiden: "O onze vader, vraag vergeving voor onze zonden; wij hebben verkeerd gedaan."'),
(1694, 12, 98, 'Hij zei: "Ik zal mijn Heer voor jullie om vergeving vragen; Hij is de vergevende, de barmhartige."'),
(1695, 12, 99, 'Toen zij dan bij Joesoef binnenkwamen haalde hij zijn ouders bij zich en zei: "Komt Egypte, zo God het wil, veilig binnen."'),
(1696, 12, 100, 'En hij verhief zijn ouders op de troon. En zij vielen eerbiedig buigend voor hem neer. En hij zei: "O mijn vader, dit is de uitleg van mijn droom van vroeger. Mijn Heer heeft hem waargemaakt en Hij heeft mij goed gedaan toen Hij mij uit de gevangenis heeft laten komen en Hij heeft jullie uit de steppe laten komen nadat de satan tussen mij en mijn broers tweedracht had gebracht. Mijn Heer is welwillend voor wie Hij wil; Hij is de wetende, de wijze. *'),
(1697, 12, 101, 'Mijn Heer, U hebt mij heerschappij gegeven en mij in de uitleg van droomverhalen onderwezen. Grondlegger van de hemelen en de aarde, U bent mijn beschermer in het tegenwoordige leven en in het hiernamaals, neem mij weg als iemand die zich [aan God] heeft overgegeven en voeg mij bij de rechtschapenen."'),
(1698, 12, 102, 'Dat zijn mededelingen over het verborgene die Wij aan jou openbaren. Jij was immers niet bij hen toen zij overeenkwamen plannen te beramen.'),
(1699, 12, 103, 'Maar de meeste mensen zijn, ook al zou jij het graag willen, niet gelovig.'),
(1700, 12, 104, 'En jij vraagt hun er geen loon voor; het is slechts een vermaning voor de wereldbewoners.'),
(1701, 12, 105, 'En hoe menig teken is er niet in de hemelen en op de aarde waaraan zij voorbijgaan terwijl zij zich ervan afwenden?'),
(1702, 12, 106, 'En de meesten van hen geloven niet in God zonder te geloven dat Hij [zogenaamd goddelijke] metgezellen heeft.'),
(1703, 12, 107, 'Wanen zij zich er veilig voor dat zij door Gods bestraffing overdekt worden of dat het uur onverwachts tot hen komt zonder dat zij het beseffen?'),
(1704, 12, 108, 'Zeg: "Dit is mijn weg, ik roep op tot God op grond van een inzichtelijk bewijs, ik en wie mij volgen. God zij geprezen, ik behoor niet tot de veelgodendienaars."'),
(1705, 12, 109, 'En Wij hebben voor jouw tijd slechts mannen uitgezonden uit de inwoners van de steden aan wie Wij een openbaring gegeven hadden. Hebben zij dan niet op de aarde rondgereisd zodat zij konden zien hoe het einde was van hen die er voor hen waren? En de woning van het hiernamaals is voor hen die godvrezend zijn werkelijk beter. Hebben jullie dan geen verstand?'),
(1706, 12, 110, 'En wanneer dan de gezanten geen hoop meer hadden en zij vermoedden dat er tegen hen gelogen was, dan kwam Onze hulp tot hen en dan werden wie Wij wilden gered. Maar Ons geweld kan niet worden afgewend van de misdadige mensen.'),
(1707, 12, 111, 'Zeker, in het verhaal over hen is een les voor de verstandigen; het is geen bericht dat verzonnen is, maar het is een bevestiging van wat er voordien al was en de uiteenzetting van alles en een leidraad en barmhartigheid voor mensen die geloven.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'A[lif] L[aam] M[iem] R[aa?]. Dat zijn de tekenen van het boek. En wat van jouw Heer naar jou is neergezonden is de waarheid, maar de meeste mensen geloven niet.'),
(1709, 13, 2, 'God die de hemelen zonder steun die jullie kunnen zien heeft opgericht. Toen vestigde Hij zich op de troon en maakte de zon en de maan dienstbaar; alles loopt tot een vastgestelde termijn. Hij regelt het bestuur terwijl Hij de tekenen uiteenzet; misschien zullen jullie van de ontmoeting met jullie Heer overtuigd zijn.'),
(1710, 13, 3, 'En Hij is het die de aarde uitgebreid heeft en er stevige bergen en rivieren op heeft gemaakt en die er van alle vruchten twee stuks, paarsgewijs, op heeft gemaakt. Hij laat de nacht de dag bedekken. Daarin zijn werkelijk tekenen voor mensen die nadenken.'),
(1711, 13, 4, 'En op de aarde zijn er bij elkaar in de buurt liggende stukken en tuinen met wijnstokken en landbouwgewassen en palmen met meervoudige en zonder meervoudige stammen die door een en hetzelfde water bevloeid worden. We laten sommige andere in vruchtopbrengst overtreffen. Daarin zijn tekenen voor mensen die verstandig zijn.'),
(1712, 13, 5, 'En als jij je al verwondert, dan is het zeker verwonderlijk dat zij zeggen: "Zullen wij wanneer wij stof geworden zijn, deel uitmaken van een nieuwe schepping?" Dat zijn zij die ongelovig zijn aan hun Heer en dat zijn zij die de halsketenen om hun nekken hebben en dat zijn zij, die in het vuur thuishoren; zij zullen daarin altijd blijven.'),
(1713, 13, 6, 'En zij willen van jou dat jij eerder het slechte dan het goede haastig brengt, hoewel er toch voor hun tijd al tot voorbeeld strekkende straffen zijn voltrokken. En jouw Heer is vol van vergeving voor de mensen, ondanks dat zij onrecht doen, maar jouw Heer is ook streng in de afstraffing.'),
(1714, 13, 7, 'Zij die ongelovig zijn zeggen: "Had er dan geen teken van zijn Heer tot hem neergezonden kunnen worden?" Maar jij bent slechts een waarschuwer; voor elke gemeenschap is er een gids die de goede richting wijst.'),
(1715, 13, 8, 'God weet wat iedere vrouw draagt en hoe de baarmoeders afnemen en hoe zij toenemen en alles heeft bij Hem een maat.'),
(1716, 13, 9, 'De kenner van het verborgene en het waarneembare, de grote, de verhevene.'),
(1717, 13, 10, 'Het maakt niet uit of iemand van jullie in het geheim spreekt of in het openbaar of zich \'s nachts verbergt of overdag openlijk rondloopt.'),
(1718, 13, 11, 'Hij heeft een gevolg voor en achter zich die hem op Gods bevel bewaken. God verandert niet wat er in mensen is zolang zij niet veranderen wat er in henzelf is en wanneer God het met mensen slecht voor heeft dan is dat onafwendbaar en zij hebben buiten God niemand die hen beschermt.'),
(1719, 13, 12, 'Hij is het die jullie in vrees en begeerte de bliksem laat zien en die de zware wolken laat ontstaan.'),
(1720, 13, 13, 'En de donder prijst Zijn lof; de engelen eveneens, uit vrees voor Hem. En Hij zendt de donderslagen en treft daarmee wie Hij wil, terwijl zij over God twisten. Hij is sterk in arglistigheid.'),
(1721, 13, 14, 'Tot Hem is het ware gebed en zij tot wie door hen in plaats van tot God wordt gebeden verhoren hen in niets; zij zijn slechts als iemand die zijn handen naar het water uitstrekt opdat het zijn mond bereikt, maar het bereikt hem niet. Het gebed van de ongelovigen mist steeds zijn doel.'),
(1722, 13, 15, 'Voor God buigen zich eerbiedig neer wie in de hemelen en op de aarde zijn, goedschiks of kwaadschiks, evenals hun schaduwen, in de morgenstond en in de avond. --'),
(1723, 13, 16, 'Zeg: "Wie is de Heer van de hemelen en de aarde?" Zeg: "God." Zeg: "Nemen jullie je dan in plaats van Hem beschermers die niet eens macht hebben om voor zichzelf tot nut of tot schade te zijn?" Zeg: "Zijn de blinde en de ziende gelijk of zijn de duisternis en het licht gelijk of hebben zij naast God metgezellen geplaatst die geschapen zouden hebben zoals Hij geschapen heeft zodat voor hen het scheppen gelijksoortig lijkt?" Zeg: "God is de schepper van alles en Hij is de éne, de albeheerser."'),
(1724, 13, 17, 'Hij laat uit de hemel water neerdalen zodat de beddingen naar hun omvang volstromen en de stroom er dan schuim bovenop heeft. Uit wat men in het vuur verhit om sieraden of nuttige dingen te vervaardigen komt een overeenkomstig schuim. Zo laat God dat op de waarheid en de onzin slaan. Wat het schuim betreft, dat gaat waardeloos verloren. Maar wat voor de mens nut heeft dat blijft in de aarde. Zo trekt God vergelijkingen.'),
(1725, 13, 18, 'Voor hen die aan hun Heer gehoor geven is er het beste. En zij die Hem geen gehoor geven, als zij alles wat er op de aarde is hadden en nog eens evenveel, dan zouden zij zich ermee willen vrijkopen. Voor hen is er de vreselijke afrekening, hun verblijfplaats is de hel. Dat is pas een slechte rustplaats! *'),
(1726, 13, 19, 'Is dan iemand die weet, dat wat van jouw Heer naar jou is neergezonden de waarheid is, gelijk aan iemand die blind is? Alleen de verstandigen laten zich vermanen.'),
(1727, 13, 20, 'Zij die Gods verbintenis nakomen en de overeenkomst niet verbreken'),
(1728, 13, 21, 'en zij die samenvoegen wat God geboden heeft samen te voegen, hun Heer vrezen en bang zijn voor de vreselijke afrekening'),
(1729, 13, 22, 'en zij die geduldig volharden in hun verlangen naar het aangezicht van hun Heer, de salaat verrichten, in het geheim en openbaar bijdragen geven van wat Wij hun voor hun levensonderhoud gegeven hebben en met het goede het slechte afweren, zij zijn het voor wie de uiteindelijke woning is:'),
(1730, 13, 23, 'de tuinen van \'Adn. Zij zullen ze binnengaan en ook die voorvaderen, echtgenotes en nakomelingen van hen die rechtschapen waren. En de engelen zullen bij hen door elke poort binnenkomen:'),
(1731, 13, 24, '"Vrede zij met jullie omdat jullie geduldig hebben volhard." Dat is pas een goede uiteindelijke woning!'),
(1732, 13, 25, 'Maar zij die de verbintenis met God verbreken nadat zij is bekrachtigd, scheiden wat God geboden heeft samen te voegen en verderf op aarde brengen, zij zijn het voor wie de vloek en voor wie de slechte woning is.'),
(1733, 13, 26, 'God voorziet ruimschoots in het levensonderhoud van wie Hij wil en ook met mate. En zij verheugen zich over het tegenwoordige leven; het tegenwoordige leven is in verhouding tot het hiernamaals slechts een gering genot.'),
(1734, 13, 27, 'En zij die ongelovig zijn zeggen: "Had er dan geen teken van zijn Heer tot hem neergezonden kunnen worden?" Zeg: "God brengt wie Hij wil tot dwaling en leidt tot Hem wie zich schuldbewust betoont."'),
(1735, 13, 28, 'Zij die geloven en wier harten gerustgesteld worden doordat zij God gedenken -- zeker, door God te gedenken worden de harten gerustgesteld --,'),
(1736, 13, 29, 'zij die geloven en de deugdelijke daden doen, voor hen is er zaligheid en een goede terugkomst.'),
(1737, 13, 30, 'Zo hebben Wij jou naar een gemeenschap gezonden, waaraan andere gemeenschappen zijn voorafgegaan, om aan hen voor te lezen wat Wij aan jou hebben geopenbaard, maar toch hechten zij geen geloof aan de Erbarmer. Zeg: "Hij is mijn Heer, er is geen god dan Hij; op Hem stel ik mijn vertrouwen en tot Hem wend ik mij berouwvol."'),
(1738, 13, 31, 'En ook al zou er een Koran zijn waarmee de bergen in beweging gezet werden of de aarde in stukken gesneden of de doden toegesproken? Welnee, aan God behoort de zeggenschap geheel toe. Is het voor hen die geloven dan niet duidelijk dat God, als Hij gewild had, de mensen allen tezamen op het goede pad gebracht had. Steeds weer zal hen die ongelovig zijn een catastrofe treffen voor wat zij gedaan hebben of die zal dicht bij hun woning plaatsvinden, totdat Gods aanzegging komt. God zal de afspraak niet afzeggen.'),
(1739, 13, 32, 'Er werd al voor jouw tijd met gezanten de spot gedreven, maar Ik heb aan hen die ongelovig zijn uitstel gegeven. Toen heb Ik hen gegrepen. En hoe was Mijn afstraffing dan?'),
(1740, 13, 33, 'Wie is dan Hij die van elke ziel nagaat wat zij heeft begaan? En toch kennen zij aan God metgezellen toe. Zeg: "Noemt hen. Of willen jullie Hem iets meedelen dat Hij op de aarde niet kent of iets wat alleen uit woorden blijkt?" Welnee, voor hen die ongelovig zijn is wat zij beramen aantrekkelijk gemaakt en zij worden van de weg afgehouden. En als God iemand tot dwaling brengt dan is er voor hem niemand die de goede richting wijst.'),
(1741, 13, 34, 'Voor hen is er een bestraffing in het tegenwoordige leven en de bestraffing van het hiernamaals is nog heviger en tegen God hebben zij geen beschermer. *'),
(1742, 13, 35, 'De tuin die de godvrezenden is toegezegd ziet er zo uit: Er stromen rivieren onderdoor en het voedsel en de schaduw ervan zijn duurzaam. Dat is de eindbestemming van hen die godvrezend zijn en de eindbestemming van de ongelovigen is het vuur.'),
(1743, 13, 36, 'Zij aan wie Wij het boek gegeven hebben verheugen zich over wat naar jou is neergezonden. En onder de partijen zijn er die een deel ervan verwerpen. Zeg: "Mij is slechts bevolen God te dienen en aan Hem geen metgezellen toe te voegen. Tot Hem roep ik op en naar Hem is mijn terugkomst."'),
(1744, 13, 37, 'En zo hebben Wij hem neergezonden als een Arabische beoordelingscode. Maar als jij na de kennis die er tot jou gekomen is hun neigingen volgt, dan heb jij tegen God geen helper en geen beschermer.'),
(1745, 13, 38, 'Wij hebben al voor jouw tijd gezanten gezonden en Wij hebben hun echtgenotes en nageslacht gegeven. Het komt een gezant niet toe zonder Gods toestemming met een teken te komen. En voor iedere termijn is er een boek.'),
(1746, 13, 39, 'God wist uit en bevestigt wat Hij wil. Bij Hem is het oorspronkelijke boek.'),
(1747, 13, 40, 'En of Wij jou iets laten zien van wat Wij hun toegezegd hebben of dat Wij jou wegnemen, jij hebt slechts de plicht van de verkondiging, maar Wij hebben de taak van de afrekening.'),
(1748, 13, 41, 'Hebben zij dan niet gezien dat Wij naar het land komen om de uiteinden ervan in te korten? God oordeelt en niemand kan Zijn oordeel ongedaan maken; Hij is snel met de afrekening.'),
(1749, 13, 42, 'Zij die er voor hun tijd waren beraamden al plannen maar het plannen beramen behoort geheel aan God toe; Hij weet wat elke ziel begaat. En de ongelovige zal weten voor wie de uiteindelijke woning is.'),
(1750, 13, 43, 'En zij die ongelovig zijn zeggen: "Jij bent geen gezondene." Zeg: "God is goed genoeg als getuige tussen jullie en mij en wie kennis van het boek hebben."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'A[lif] L[aam] R[aa?]. Een boek dat naar jou is neergezonden opdat jij de mensen met de toestemming van hun Heer uit de duisternis naar het licht brengt, naar de weg van de machtige, de lofwaardige.'),
(1752, 14, 2, 'God, van wie is wat er in de hemelen en wat er op de aarde is. En wee de ongelovigen wegens een strenge bestraffing.'),
(1753, 14, 3, 'Zij die het tegenwoordige leven meer liefhebben dan het hiernamaals en die de weg van God versperren en verlangen dat het een kronkelweg is, zij zijn het die in vergaande dwaling verkeren.'),
(1754, 14, 4, 'En Wij hebben geen gezant gezonden of het was in de taal van zijn volk om aan hen duidelijkheid te verschaffen. En God brengt tot dwaling wie Hij wil en Hij brengt op het goede pad wie Hij wil; Hij is de machtige, de wijze.'),
(1755, 14, 5, 'En Wij hadden Moesa met Onze tekenen gezonden: "Breng jouw volk uit de duisternis naar het licht en breng hun Gods dagen in herinnering; daarin zijn zeker tekenen voor ieder die geduldig volhardt en die dank betuigt."'),
(1756, 14, 6, 'En toen Moesa tot zijn volk zei: "Denkt aan Gods genade aan jullie toen Hij jullie van Fir\'auns mensen redde die jullie een vreselijk leed berokkenden en die jullie zonen afslachtten en alleen jullie vrouwen in leven lieten. Daarin was een geweldige beproeving van jullie Heer.'),
(1757, 14, 7, 'En toen jullie Heer aankondigde: \'Als jullie dankbaar zijn zal Ik jullie nog meer geven, maar als jullie in ondankbaarheid ongelovig zijn, dan is Mijn bestraffing streng."'),
(1758, 14, 8, 'En Moesa zei: "Al zijn jullie in ondankbaarheid ongelovig, jullie en wie er op de aarde zijn tezamen, dan nog is God behoefteloos en lofwaardig."'),
(1759, 14, 9, 'Is tot jullie niet de mededeling gekomen over hen die er voor jullie tijd waren, het volk van Noeh en de \'Aad en de Thamoed en hen die er na hen waren die alleen God kent. Tot hen kwamen hun gezanten met de duidelijke bewijzen, maar zij stopten hun handen in hun monden en zeiden: "Waarmee jullie gezonden zijn daaraan hechten wij geen geloof. Wij verkeren namelijk in hevige twijfel over dat waartoe jij ons oproept." *'),
(1760, 14, 10, 'Hun gezanten zeiden: "Valt er dan aan God te twijfelen, de grondlegger van de hemelen en de aarde? Hij roept jullie op, om jullie vergeving te schenken voor zonden van jullie en om jullie uitstel te verlenen tot een vastgestelde termijn." Zij zeiden: "Jullie zijn slechts mensen zoals wij. Jullie willen ons afbrengen van wat onze vaderen gewoon waren te dienen. Brengt ons dan een duidelijke machtiging."'),
(1761, 14, 11, 'Hun gezanten zeiden tot hen: "Wij zijn slechts mensen zoals jullie, maar God bewijst gunsten aan wie van Zijn dienaren Hij wil. En het komt ons niet toe jullie zonder Gods toestemming een machtiging te brengen. Op God moeten de gelovigen hun vertrouwen stellen.'),
(1762, 14, 12, 'Waarom zouden wij op God niet ons vertrouwen stellen? Hij heeft ons toch op onze wegen de goede richting gewezen. En wij zullen het leed dat jullie ons aandoen geduldig verdragen. Op God moeten de gelovigen hun vertrouwen stellen."'),
(1763, 14, 13, 'Zij die ongelovig waren zeiden tot hun gezanten: "Wij zullen jullie uit ons land verdrijven of jullie zullen tot ons geloof terugkeren." Toen openbaarde hun Heer aan hen: "Wij zullen de onrechtplegers vernietigen.'),
(1764, 14, 14, 'En Wij zullen jullie de aarde na hen laten bewonen. Dat is voor wie vreest om voor Mij te staan en voor wie Mijn dreigement vreest."'),
(1765, 14, 15, 'En zij vroegen om een goede afloop en elke weerspannige geweldenaar werd teleurgesteld.'),
(1766, 14, 16, 'Hem wacht de hel en hem wordt etterwater te drinken gegeven,'),
(1767, 14, 17, 'dat hij opslurpt maar bijna niet kan doorslikken. En de dood komt van alle kanten tot hem, maar hij sterft niet en hem wacht een harde bestraffing.'),
(1768, 14, 18, 'Met hen die aan hun Heer geen geloof hechten is het zo gesteld: hun daden zijn als as waarover op een stormdag de wind raast. Zij hebben geen macht over iets wat zij verworven hebben. Dat is de vergaande dwaling.'),
(1769, 14, 19, 'Zie jij dan niet dat God de hemelen en de aarde in waarheid geschapen heeft? Als Hij wil vaagt Hij jullie weg en brengt Hij een nieuwe schepping.'),
(1770, 14, 20, 'Dat is voor God niet lastig.'),
(1771, 14, 21, 'En zij zullen tezamen bij God vóórkomen. Dan zullen de zwakken tegen hen die hoogmoedig waren zeggen: "Wij waren toch volgelingen van jullie, baten jullie ons dan iets tegen Gods bestraffing?" Zij zeggen: "Als God ons de goede richting gewezen had, dan hadden wij jullie ook de goede richting gewezen. Voor ons maakt het niet uit of wij bezorgd weifelen of geduldig volharden, voor ons is er geen ontsnapping."'),
(1772, 14, 22, 'En de satan zegt, wanneer de beslissing is gevallen: "God heeft een waarachtige toezegging gedaan. Ik heb jullie toezeggingen gedaan, maar ik ben ze niet nagekomen. Ik had geen macht over jullie; ik riep jullie slechts op en jullie gaven aan mij gehoor. Verwijt mij dus niets maar verwijt het jullie zelf. Ik kan jullie geen hulp bieden en jullie kunnen mij geen hulp bieden. Ik hechtte er geen geloof aan toen jullie mij vroeger als metgezel van God vereerden." Voor de onrechtplegers is er een pijnlijke bestraffing.'),
(1773, 14, 23, 'Zij die geloven en de deugdelijke daden doen zullen in tuinen binnengebracht worden, waar de rivieren onderdoor stromen. Zij zullen met de toestemming van hun Heer daarin altijd blijven; hun begroeting daarin zal zijn: "Vrede."'),
(1774, 14, 24, 'Heb jij niet gezien hoe God een goed woord vergelijkt met een goede boom waarvan de wortel stevig staat en de takken in de hemel reiken?'),
(1775, 14, 25, 'Hij geeft te allen tijde met de toestemming van zijn Heer vruchtopbrengst. En God maakt deze vergelijkingen voor de mensen; misschien zullen zij zich laten vermanen!'),
(1776, 14, 26, 'En een slecht woord lijkt op een slechte boom die ontworteld is [zodat hij] boven op de aarde [staat] en die geen standplaats heeft.'),
(1777, 14, 27, 'God sterkt hen die geloven met de vaststaande uitspraak in het tegenwoordige leven en in het hiernamaals en God brengt de onrechtplegers tot dwaling; God doet wat Hij wil. *'),
(1778, 14, 28, 'Heb jij hen niet gezien die Gods genade voor ongeloof verwisseld hebben en hun volk in de woning van de ondergang terecht hebben laten komen,'),
(1779, 14, 29, 'in de hel waarin zij braden? Dat is pas een slechte standplaats!'),
(1780, 14, 30, 'En zij schrijven aan God gelijken toe om van Zijn weg te laten afdwalen. Zeg: "Geniet maar, jullie bestemming is in het vuur."'),
(1781, 14, 31, 'Zeg tot Mijn dienaren die geloven, dat zij de salaat verrichten en dat zij in het geheim en openbaar bijdragen geven van wat Wij hun voor hun levensonderhoud gegeven hebben voordat er een dag komt waarop er geen handel en geen vriendschap is.'),
(1782, 14, 32, 'God is het die de hemelen en de aarde geschapen heeft en die uit de hemel water heeft laten neerdalen en die daarmee vruchten heeft voortgebracht voor jullie levensonderhoud. En Hij maakte voor jullie de schepen dienstbaar om op Zijn bevel op zee te varen en Hij maakte voor jullie de rivieren dienstbaar.'),
(1783, 14, 33, 'Hij maakte voor jullie de zon en de maan dienstbaar in hun vaste loop en Hij maakte voor jullie de dag en de nacht dienstbaar.'),
(1784, 14, 34, 'Hij heeft jullie van alles wat jullie Hem gevraagd hebben gegeven en als jullie Gods genade willen tellen dan kunnen jullie het niet opsommen. De mens is werkelijk een zeer ondankbare zondaar.'),
(1785, 14, 35, 'Toen Ibrahiem zei: "Mijn Heer, maak deze plaats veilig en houd mij en mijn zoons ervan af de afgoden te dienen.'),
(1786, 14, 36, 'Mijn Heer, zij hebben veel van de mensen tot dwaling gebracht. Wie mij volgt, die behoort bij mij en als iemand aan mij ongehoorzaam is, dan bent U vergevend en barmhartig.'),
(1787, 14, 37, 'Onze Heer, ik heb enkelen van mijn nakomelingen in een vallei zonder gewas bij Uw heilige huis laten wonen, onze Heer, om de salaat te verrichten. Maak dan dat de harten van sommige mensen toeneiging tot hen krijgen en voorzie met vruchten in hun levensbehoeften; misschien zullen zij dank betuigen.'),
(1788, 14, 38, 'Onze Heer, U weet wat wij in het verborgene en wat wij openlijk doen; niets is op de aarde en in de hemel voor God verborgen.'),
(1789, 14, 39, 'Lof zij God die mij ondanks mijn ouderdom Isma\'iel en Ishaak geschonken heeft. Mijn Heer is werkelijk de hoorder van het gebed.'),
(1790, 14, 40, 'Mijn Heer, maak dat ik de salaat verricht en dat zij die tot mijn nageslacht behoren [het ook doen], onze Heer, en aanvaard mijn gebed.'),
(1791, 14, 41, 'Onze Heer, schenk aan mij, aan mijn ouders en aan de gelovigen vergeving op de dag dat de afrekening plaatsvindt."'),
(1792, 14, 42, 'En denk niet dat God niet goed let op wat de onrechtplegers doen. Hij geeft hun slechts uitstel tot een dag waarop de blikken verstarren,'),
(1793, 14, 43, 'terwijl zij zich voortspoeden met hun hoofden uitgestoken, hun oogleden die zich niet sluiten en hun harten leeg [van schrik].'),
(1794, 14, 44, 'En waarschuw de mensen voor een dag waarop de bestraffing tot hen komt. Dan zullen zij die onrecht pleegden zeggen: "Onze Heer, verleen ons nog een kort uitstel, dan zullen wij aan Uw oproep gehoor geven en de gezanten volgen." Hadden jullie vroeger niet ook al gezworen dat er voor jullie geen ondergang was?'),
(1795, 14, 45, 'En jullie bewoonden de woonhuizen van hen die zichzelf onrecht aandeden en het is jullie duidelijk geworden hoe Wij met hen gehandeld hebben. Wij hebben jullie toch voorbeelden ter vergelijking gegeven.'),
(1796, 14, 46, 'Zij hebben hun list beraamd, maar hun list is bij God bekend, ook al was hun list dat de bergen ermee zouden vergaan.'),
(1797, 14, 47, 'En denk niet dat God Zijn toezegging aan de gezanten niet nakomt. God is machtig en wraakgierig.'),
(1798, 14, 48, 'Op de dag dat de aarde vervangen zal worden door een andere aarde en de hemelen ook en waarop zij vóórkomen bij God, de ene, de albeheerser.'),
(1799, 14, 49, 'Jij ziet op die dag de boosdoeners in boeien aaneengeketend,'),
(1800, 14, 50, 'met hun kleren van pek en hun gezichten bedekt met het vuur,'),
(1801, 14, 51, 'opdat God aan elke ziel vergeldt wat zij begaan heeft. En God is snel met de afrekening.'),
(1802, 14, 52, 'Dit is een verkondiging voor de mensen, opdat zij erdoor gewaarschuwd worden, opdat zij weten dat Hij één god is en opdat de verstandigen zich laten vermanen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'A[lif] L[aam] R[aa?]. Dit zijn de tekenen van het boek en een duidelijke Koran.'),
(1804, 15, 2, 'Misschien zullen zij die ongelovig zijn graag willen dat zij mensen waren geweest die zich [aan God] overgegeven hadden.'),
(1805, 15, 3, 'Laat hen maar eten en genieten en door de hoop afgeleid worden; zij zullen het wel weten.'),
(1806, 15, 4, 'Wij hebben geen stad vernietigd zonder dat er voor haar een vastgestelde voorbeschikking was.'),
(1807, 15, 5, 'Geen gemeenschap kan te vroeg haar termijn bereiken en ook niet te laat.'),
(1808, 15, 6, 'En zij zeiden: "Hé jij tot wie de vermaning is neergezonden, jij bent bezeten!'),
(1809, 15, 7, 'Had jij niet met engelen kunnen komen, als jij gelijk hebt?"'),
(1810, 15, 8, 'Wij zenden de engelen slechts met de waarheid neer. Dan wordt hun geen uitstel meer verleend.'),
(1811, 15, 9, 'Wij hebben de vermaning neergezonden en Wij waken erover.'),
(1812, 15, 10, 'En Wij hebben al voor jouw tijd [gezanten] gezonden naar de groeperingen van hen die er eertijds waren.'),
(1813, 15, 11, 'En er kwam geen gezant tot hen of zij dreven de spot met hem.'),
(1814, 15, 12, 'Zo laten Wij dat in de harten van de boosdoeners gaan.'),
(1815, 15, 13, 'Zij geloven er niet aan, maar de gebruikelijke behandeling van hen die er eertijds waren heeft reeds eerder plaatsgevonden.'),
(1816, 15, 14, 'Al zouden Wij voor hen een poort in de hemel openen waardoor zij steeds maar konden opstijgen.'),
(1817, 15, 15, 'Dan zouden zij zeggen: "Onze ogen zijn slechts beneveld. Ja zeker, wij zijn mensen die men heeft betoverd."'),
(1818, 15, 16, 'En Wij hebben in de hemel sterrenbeelden gemaakt en ze voor de kijkers sierlijk gemaakt.'),
(1819, 15, 17, 'En Wij hebben ze behoed voor elke vervloekte satan,'),
(1820, 15, 18, 'behalve wie heimelijk afluistert; hij wordt dan achtervolgd dooreen duidelijke staartster.'),
(1821, 15, 19, 'En de aarde hebben Wij uitgebreid en er stevige bergen op aangebracht. En Wij hebben er allerlei dingen evenwichtig op laten groeien.'),
(1822, 15, 20, 'En Wij hebben er voor jullie levensbenodigdheden op gemaakt en ook voor hen voor wie jullie geen voorzieningen treffen.'),
(1823, 15, 21, 'Er bestaat niets of er zijn bij Ons voorraden van en Wij laten het slechts in vastgestelde mate neerdalen.'),
(1824, 15, 22, 'En Wij zenden de winden terwijl ze zwaarbeladen zijn en Wij laten uit de hemel water neerdalen en Wij geven jullie daarvan te drinken. En jullie hadden daarvan geen voorraden kunnen aanleggen.'),
(1825, 15, 23, 'Wij zijn het die leven geven en die laten sterven en Wij zijn het die beërven.'),
(1826, 15, 24, 'Wij kennen hen onder jullie die vroeg komen en Wij kennen hen die laat komen.'),
(1827, 15, 25, 'Jouw Heer, Hij is het die hen verzamelt. Hij is wijs en wetend.'),
(1828, 15, 26, 'Wij hebben de mens uit steenaarde, uit stinkende potklei geschapen.'),
(1829, 15, 27, 'En de djinn, die hebben Wij eerder uit het verzengende vuur geschapen.'),
(1830, 15, 28, 'En toen jouw Heer tot de engelen zei: "Ik ga een mens uit steenaarde, uit stinkende potklei scheppen.'),
(1831, 15, 29, 'En als Ik hem gevormd heb en hem iets van Mijn geest heb ingeblazen, valt dan in eerbiedige buiging voor hem neer."'),
(1832, 15, 30, 'En de engelen bogen zich allen tezamen eerbiedig voor hem neer.'),
(1833, 15, 31, 'Alleen Iblies niet, hij weigerde bij hen die zich eerbiedig neerbogen te behoren.'),
(1834, 15, 32, 'Hij zei: "O Iblies, wat heb je dat jij niet behoort bij hen die zich eerbiedig neerbuigen?"'),
(1835, 15, 33, 'Deze zei: "Ik ben niet zo dat ik mij eerbiedig neerbuig voor een mens die U uit steenaarde, uit stinkende potklei geschapen hebt."'),
(1836, 15, 34, 'Hij zei: "Ga hier weg, jij zult door steniging vervloekt zijn!'),
(1837, 15, 35, 'En de vloek zal tot de oordeelsdag op je rusten."'),
(1838, 15, 36, 'Hij zei: "Mijn Heer, verleen mij uitstel tot de dag waarop zij worden opgewekt."'),
(1839, 15, 37, 'Hij zei: "Jij behoort bij hen die uitstel hebben gekregen'),
(1840, 15, 38, 'tot de dag van de vastgestelde tijd."'),
(1841, 15, 39, 'Hij zei: "Mijn Heer, omdat U mij misleid hebt zal ik voor hen op de aarde [alles] schone schijn maken en ik zal hen zeker allen misleiden,'),
(1842, 15, 40, 'behalve Uw dienaren onder hen, die toegewijd zijn."'),
(1843, 15, 41, 'Hij zei: "Dit is voor mij een juiste weg,'),
(1844, 15, 42, 'want over Mijn dienaren heb jij geen gezag behalve over die misleiden die jou volgen."'),
(1845, 15, 43, 'En de hel is de plaats die voor hen allen tezamen is aangewezen.'),
(1846, 15, 44, 'Die heeft zeven poorten; elke poort ervan heeft een toegewezen deel.'),
(1847, 15, 45, 'Maar de godvrezenden zullen in tuinen en bij bronnen zijn:'),
(1848, 15, 46, '"Komt er in vrede en veiligheid binnen."'),
(1849, 15, 47, 'En Wij nemen weg wat er in hun harten aan wrok was, zodat zij als broeders op rustbanken tegenover elkaar zitten.'),
(1850, 15, 48, 'Zij worden daarin niet door vermoeidheid overvallen en zij zullen er niet uit verdreven worden.'),
(1851, 15, 49, 'Deel Mijn dienaren mee dat Ik de vergevende, de barmhartige ben.'),
(1852, 15, 50, 'En dat Mijn bestraffing de pijnlijke bestraffing is.'),
(1853, 15, 51, 'En bericht hun over de gasten van Ibrahiem.'),
(1854, 15, 52, 'Toen zij bij hem binnenkwamen en zeiden:"Vrede!" Hij zei: "Wij hebben ontzag voor jullie."'),
(1855, 15, 53, 'Zij zeiden: "Heb geen ontzag, wij verkondigen jou het goede nieuws dat jij een verstandige jongen krijgt."'),
(1856, 15, 54, 'Hij zei: "Verkondigen jullie mij goed nieuws nu de ouderdom mij al getroffen heeft? Wat voor goed nieuws verkondigen jullie dan?"'),
(1857, 15, 55, 'Zij zeiden: "Wij verkondigen jou de waarheid. Wees dus niet een van hen die de hoop opgeven."'),
(1858, 15, 56, 'Hij zei: "Wie zou er de hoop op de barmhartigheid van zijn Heer opgeven afgezien van hen die dwalen?"'),
(1859, 15, 57, 'Hij zei: "Waar komen jullie voor, o gezondenen?"'),
(1860, 15, 58, 'Zij zeiden: "Wij zijn gezonden naar misdadige mensen."'),
(1861, 15, 59, 'Uitgezonderd de familie van Loet, die zullen Wij allen tezamen redden,'),
(1862, 15, 60, 'behalve zijn vrouw; Wij hebben verordend dat zij behoort tot hen die achterblijven.'),
(1863, 15, 61, 'En toen de gezondenen bij de familie van Loet kwamen,'),
(1864, 15, 62, 'zei hij: "Jullie zijn onbekende mensen."'),
(1865, 15, 63, 'Zij zeiden: "Welnee, maar wij zijn tot jou gekomen met dat wat zij voortdurend in twijfel trokken.'),
(1866, 15, 64, 'En wij zijn tot jou gekomen met de waarheid en wij zijn echt oprecht.'),
(1867, 15, 65, 'Vertrek dus met je familie in een deel van de nacht en volg hen in de achterhoede en niemand van jullie mag zich omdraaien; ga daarheen waar jullie bevolen wordt."'),
(1868, 15, 66, 'En Wij kondigden hem de beslissing aan dat zij in de morgen tot de laatste toe geveld zouden zijn.'),
(1869, 15, 67, 'En de mensen van de stad kwamen om zich te vermaken.'),
(1870, 15, 68, 'Hij zei: "Dit zijn mijn gasten, onteert mij dus niet.'),
(1871, 15, 69, 'En vreest God en maakt mij niet te schande."'),
(1872, 15, 70, 'Zij zeiden: "Hebben wij je niet verboden je in te laten met de wereldbewoners?"'),
(1873, 15, 71, 'Hij zei: "Hier zijn mijn dochters, als jullie echt iets doen willen."'),
(1874, 15, 72, 'Bij jouw leven, zij gingen in hun roes door met dwalen!'),
(1875, 15, 73, 'Toen greep de schreeuw hen bij zonsopgang.'),
(1876, 15, 74, 'En Wij keerden haar ondersteboven en lieten er bakstenen op regenen.'),
(1877, 15, 75, 'Daarin zijn zeker tekenen voor hen die nauwkeurig beschouwen.'),
(1878, 15, 76, 'Zij ligt op een blijvende weg.'),
(1879, 15, 77, 'Daarin zijn zeker tekenen voor hen die geloven.'),
(1880, 15, 78, 'Ook de mensen van het kreupelbos waren onrechtplegers.'),
(1881, 15, 79, 'En Wij namen ook wraak op hen. Beide zijn zij een duidelijk voorbeeld.'),
(1882, 15, 80, 'En ook de mensen van al-Hidjr betichtten de gezondenen van leugens.'),
(1883, 15, 81, 'En Wij gaven hun Onze tekenen, maar zij wendden zich ervan af.'),
(1884, 15, 82, 'En zij hadden in de bergen veilige huizen uitgehouwen.'),
(1885, 15, 83, 'Toen greep de schreeuw hen in de morgen.'),
(1886, 15, 84, 'En dus baatte hun niet wat zij ten uitvoer hadden gebracht.'),
(1887, 15, 85, 'Wij hebben de hemelen en de aarde en wat er tussen beide is slechts in waarheid geschapen. En het uur komt zeker; weest dus op een vriendelijke manier toegevend.'),
(1888, 15, 86, 'Jouw Heer, Hij is de schepper, de wetende.'),
(1889, 15, 87, 'En Wij hebben jou zeven herhaalde gedeelten gegeven en de geweldige Koran.'),
(1890, 15, 88, 'Kijk niet met uitpuilende ogen naar wat Wij sommigen van hen als vruchtgebruik gegeven hebben en maak je niet bedroefd over hen en wees ontvankelijk voor de gelovigen.'),
(1891, 15, 89, 'En zeg: "Ik ben de duidelijke waarschuwer!"'),
(1892, 15, 90, 'Zoals [tot jou] hebben Wij [ook een boodschap] neergezonden tot hen die in stukken verdelen,'),
(1893, 15, 91, 'die de Koran in parten hebben opgedeeld.'),
(1894, 15, 92, 'Dus, bij jouw Heer, Wij zullen hen allemaal ondervragen'),
(1895, 15, 93, 'over wat zij aan het doen waren.'),
(1896, 15, 94, 'En verkondig luid wat jou bevolen is en wend je af van de veelgodendienaars.'),
(1897, 15, 95, 'Wij zijn voldoende voor jou tegen de spotters,'),
(1898, 15, 96, 'die naast God een andere god stellen; maar zij zullen het weten.'),
(1899, 15, 97, 'Wij weten dat jouw hart benauwd is om wat zij zeggen.'),
(1900, 15, 98, 'Maar prijs de lof van jouw Heer en wees met hen die zich eerbiedig neerbuigen.'),
(1901, 15, 99, 'En dien jouw Heer tot de zekerheid [van de dood] komt.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'Gods beschikking is gekomen, probeert dat dus niet te verhaasten. Hij zij geprezen, verheven als Hij is boven wat zij aan Hem als metgezellen toevoegen.'),
(1903, 16, 2, 'Hij zendt door Zijn beschikking de engelen met de geest neer tot wie van Zijn dienaren Hij wil: "Waarschuwt dat er geen god is dan Ik, vreest Mij dus."'),
(1904, 16, 3, 'Hij heeft de hemelen en de aarde in waarheid geschapen. Hij is verheven boven wat zij aan Hem als metgezellen toevoegen.'),
(1905, 16, 4, 'Hij heeft de mens uit een druppel geschapen en toch is hij dan een duidelijke tegenstander.'),
(1906, 16, 5, 'Ook het vee heeft Hij geschapen. Jullie hebben daarmee warmte en allerlei nuttigs en jullie eten ervan.'),
(1907, 16, 6, 'Daarmee is er voor jullie ook schoonheid wanneer jullie ze \'s avonds huiswaarts voeren en wanneer jullie ze \'s morgens naar de weiden brengen.'),
(1908, 16, 7, 'En het draagt jullie lasten naar een land dat jullie slechts met grote inspanning hadden kunnen bereiken. Jullie Heer is werkelijk vol mededogen en barmhartig.'),
(1909, 16, 8, 'En paarden, muildieren en ezels opdat jullie ze kunnen berijden en voor het mooi. En Hij schept wat jullie niet weten.'),
(1910, 16, 9, 'Het is Gods taak de goede weg aan te geven; sommige gaan namelijk de verkeerde kant op. En als Hij gewild had, dan had Hij jullie allen tezamen op het goede pad gebracht.'),
(1911, 16, 10, 'Hij is het die uit de hemel water laat neerdalen. Daardoor hebben jullie te drinken en daardoor is er geboomte waarin jullie kunnen laten weiden.'),
(1912, 16, 11, 'Hij laat voor jullie daarmee landbouwgewassen groeien en olijfbomen, palmen, wijnstokken en allerlei vruchten. Daarin is zeker een teken voor mensen die nadenken.'),
(1913, 16, 12, 'En Hij heeft voor jullie de dag en de nacht, de zon en de maan dienstbaar gemaakt en de sterren zijn onderworpen aan Zijn zeggenschap. Daarin zijn zeker tekenen voor mensen die verstandig zijn.'),
(1914, 16, 13, 'En ook in wat Hij op de aarde voor jullie in verschillende soorten geschapen heeft. Daarin is zeker een teken voor mensen die zich laten vermanen.'),
(1915, 16, 14, 'En Hij is het die voor jullie de zee dienstbaar heeft gemaakt om er vers vlees uit te eten en er sieraden uit te halen om je mee te tooien. En jullie zien de schepen haar doorklieven en [dat is] opdat jullie naar een gunst van Hem streven. En misschien zullen jullie dank betuigen.'),
(1916, 16, 15, 'En Hij heeft op de aarde stevige bergen aangebracht dat zij jullie niet aan het wankelen zou brengen en rivieren en wegen -- opdat jullie je misschien de goede richting zullen laten wijzen --'),
(1917, 16, 16, 'en wegmarkeringen. En met de sterren laten zij zich de goede richting wijzen.'),
(1918, 16, 17, 'Is Hij die schept dan zoals iemand die niet schept? Zullen jullie je dan niet laten vermanen?'),
(1919, 16, 18, 'En als jullie Gods genade willen tellen dan kunnen jullie het niet opsommen. God is werkelijk vergevend en barmhartig.'),
(1920, 16, 19, 'En God weet wat jullie in het geheim en wat jullie openlijk doen.'),
(1921, 16, 20, 'En zij die door hen in plaats van God aangeroepen worden scheppen niets, maar zij worden zelf geschapen.'),
(1922, 16, 21, 'Dood zijn zij en niet levend en zij beseffen niet wanneer zij worden opgewekt.'),
(1923, 16, 22, 'Jullie god is één god. En zij die niet in het hiernamaals geloven, hun harten ontkennen het in hun hoogmoed.'),
(1924, 16, 23, 'Het staat vast dat God weet wat zij in het geheim en wat zij openlijk doen. Hij bemint de hoogmoedigen niet.'),
(1925, 16, 24, 'En wanneer tot hen gezegd wordt: "Wat is het dat jullie Heer heeft neergezonden?" dan zeggen zij: "Fabels van hen die er eertijds waren."'),
(1926, 16, 25, 'Dat zij op de opstandingsdag hun volle lasten mogen dragen en ook nog iets van de lasten van hen die zij zonder kennis tot dwaling brengen. Is het niet slecht waarmee zij worden belast?'),
(1927, 16, 26, 'Zij die er voor hun tijd waren beraamden al plannen, maar God greep hun bouwwerk bij de fundamenten en het dak viel van bovenaf op hen en de bestraffing kwam tot hen vanwaar zij het niet beseften.'),
(1928, 16, 27, 'En dan, op de opstandingsdag zal Hij hen te schande maken en zeggen: "Waar zijn Mijn [zogenaamd goddelijke] metgezellen om wie jullie zo tegengewerkt hebben?" Zij aan wie de kennis gegeven is zeggen: "De schande en het kwaad komen vandaag over de ongelovigen,'),
(1929, 16, 28, 'die door de engelen worden weggenomen, terwijl zij zichzelf onrecht hebben aangedaan en die dan vrede aanbieden: \'Wij deden geen kwaad.?" "Integendeel, God weet wat jullie aan het doen waren.'),
(1930, 16, 29, 'Gaat de poorten van de hel binnen om er altijd in te blijven." Dat is pas echt een slechte verblijfplaats voor de hoogmoedigen. *'),
(1931, 16, 30, 'Ook tot hen die godvrezend zijn wordt gezegd: "Wat is het dat jullie Heer heeft neergezonden?" Dan zeggen zij: "Iets goeds." Voor hen die in dit tegenwoordige leven goed doen is er iets goeds, maar de woning van het hiernamaals is beter. Dat is pas een goede woning voor de godvrezenden!'),
(1932, 16, 31, 'De tuinen van \'Adn zullen zij binnengaan waar de rivieren onderdoor stromen. Zij hebben daarin wat zij willen. Zo beloont God de godvrezenden,'),
(1933, 16, 32, 'die door de engelen als goede mensen worden weggenomen. Zij zeggen: "Vrede zij met jullie, gaat de tuin binnen voor wat jullie gedaan hebben."'),
(1934, 16, 33, 'Kunnen zij dan iets anders verwachten dan dat de engelen tot hen komen of dat de beschikking van hun Heer komt? Zo handelden ook zij die er voor hun tijd waren. God deed hun geen onrecht aan, maar zij deden zichzelf onrecht aan.'),
(1935, 16, 34, 'De slechte daden die ze deden hebben hen dus getroffen en zij werden ingesloten door dat waarmee zij de spot dreven.'),
(1936, 16, 35, 'En de aanhangers van het veelgodendom zeggen: "Als God gewild had hadden wij niets in plaats van Hem gediend, wij noch onze vaderen en ook zouden wij buiten Hem om niets verboden hebben." Zo handelden ook zij die er voor hun tijd waren. Hebben de gezanten dan een andere plicht dan de duidelijke verkondiging?'),
(1937, 16, 36, 'En Wij hebben toch in elke gemeenschap een gezant laten opstaan: "Dient God en vermijdt de Taghoet." Een gedeelte van hen heeft God op het goede pad geleid en voor een gedeelte is de dwaling waar geworden. Reist dus op de aarde rond en kijkt hoe het einde was van de loochenaars.'),
(1938, 16, 37, 'Al zou jij hen nog zo graag op het goede pad willen brengen, God brengt hen, die Hij tot dwaling brengt, niet op het goede pad. En zij hebben ook geen helpers.'),
(1939, 16, 38, 'En zij zweren dure eden bij God: "God zal wie sterft niet opwekken!" Integendeel, het is een toezegging waartoe Hij in waarheid verplicht is -- maar de meeste mensen weten het niet --'),
(1940, 16, 39, 'opdat Hij aan hen dat waarover zij het oneens zijn duidelijk maakt en opdat zij die ongelovig zijn weten dat zij leugenaars waren.'),
(1941, 16, 40, 'Wat Wij tot iets zeggen wanneer Wij het wensen is dat Wij zeggen: "Wees!" en het is.'),
(1942, 16, 41, 'Zij die om God zijn uitgeweken, nadat hun onrecht was aangedaan, zullen Wij in het tegenwoordige leven zeker een goed onderdak geven, maar het loon van het hiernamaals is groter, als zij dat maar wisten,'),
(1943, 16, 42, 'zij die geduldig volharden en op hun Heer hun vertrouwen stellen.'),
(1944, 16, 43, 'En Wij hebben voor jouw tijd slechts mannen uitgezonden aan wie Wij een openbaring gegeven hadden -- vraagt de mensen van de vermaning maar, als jullie het niet weten --'),
(1945, 16, 44, 'met de duidelijke bewijzen en de Zoeboer. En Wij hebben tot jou de vermaning neergezonden opdat jij de mensen duidelijk maakt wat naar hen is neergezonden en misschien dat zij dan nadenken.'),
(1946, 16, 45, 'Wanen zij die de slechte daden beramen zich er veilig voor dat God hen met de aarde laat wegzinken of dat de bestraffing tot hen komt vanwaar zij het niet beseffen?'),
(1947, 16, 46, 'Of dat Hij hen grijpt terwijl zij rondtrekken zonder dat zij er iets aan kunnen doen?'),
(1948, 16, 47, 'Of dat Hij hen niet zal grijpen terwijl zij zich bang maken? Jullie Heer is vol mededogen en barmhartig.'),
(1949, 16, 48, 'Hebben zij dan niet gezien naar dingen die God geschapen heeft, die \'s avonds hun schaduw naar rechts en naar links werpen terwijl zij zich eerbiedig in onderdanigheid voor God neerbuigen.'),
(1950, 16, 49, 'Voor God buigt zich eerbiedig neer wat er in de hemelen en wat er op de aarde is, dieren en ook de engelen. En zij zijn niet hoogmoedig.'),
(1951, 16, 50, 'Zij vrezen hun Heer boven hen en zij doen wat hun bevolen wordt.?'),
(1952, 16, 51, 'En God heeft gezegd: "Neemt geen twee goden. Hij is slechts één god. Voor Mij moeten jullie dus beducht zijn."'),
(1953, 16, 52, 'En van Hem is wat er in de hemelen is en wat er op de aarde is en Hem behoort de godsdienst voortdurend toe. Zullen jullie dan iemand anders dan God vrezen?'),
(1954, 16, 53, 'En welke weldaad jullie ook toevalt, het komt van God. Wanneer jullie dan tegenspoed treft schreeuwen jullie tot Hem om hulp.'),
(1955, 16, 54, 'Maar wanneer Hij dan de tegenspoed voor jullie opheft dan voegt een groep van jullie metgezellen aan hun Heer toe'),
(1956, 16, 55, 'om in hun ongeloof ondankbaar te zijn voor wat Wij hun gegeven hebben. Geniet maar; jullie zullen het weten.'),
(1957, 16, 56, 'En zij bestemmen voor wat zij niet kennen een aandeel van wat Wij hun voor hun levensonderhoud gegeven hebben. Bij God, jullie zullen ter verantwoording geroepen worden over wat jullie aan het verzinnen waren.'),
(1958, 16, 57, 'En zij kennen aan God dochters toe! Geprezen zij Hij! En zij zelf hebben wat zij begeren.'),
(1959, 16, 58, 'En wanneer een van hen het goede nieuws van een meisje krijgt, betrekt zijn gezicht en hij is vol ingehouden woede.'),
(1960, 16, 59, 'Hij verbergt zich voor de mensen wegens het slechte nieuws dat hij gekregen heeft. Zal hij het ondanks de schande behouden of zal hij het in de grond verstoppen? Is het niet slecht wat zij van oordeel zijn?'),
(1961, 16, 60, 'Zij die niet in het hiernamaals geloven hebben het slechte voorbeeld, maar God komt het hoogste voorbeeld toe en Hij is de machtige, de wijze.'),
(1962, 16, 61, 'En als God de mensen het onrecht dat zij plegen zou aanrekenen, dan zou Hij op de [aarde] geen dier overlaten. Maar Hij geeft hun uitstel tot een vastgestelde termijn. En wanneer hun termijn dan komt, zullen zij geen uur te laat ontboden worden noch te vroeg.'),
(1963, 16, 62, 'Zij kennen aan God toe wat hun tegenstaat en hun tongen uiten de leugen dat er voor hen het mooiste is weggelegd. Het staat vast dat het vuur voor hen bestemd is en dat zij voortgedreven worden.'),
(1964, 16, 63, 'Bij God, Wij hebben tot gemeenschappen voor jouw tijd gezanten gezonden, maar de satan maakte voor hen hun daden aantrekkelijk. Hij is dus vandaag hun beschermer en voor hen is er een pijnlijke bestraffing.'),
(1965, 16, 64, 'En Wij hebben het boek slechts tot jou neergezonden opdat jij aan hen dat duidelijk maakt waarover zij het oneens waren en als een leidraad en barmhartigheid voor mensen die geloven.'),
(1966, 16, 65, 'God laat uit de hemel water neerdalen om daarmee de aarde te doen herleven nadat zij dood was. Daarin is werkelijk een teken voor mensen die horen.'),
(1967, 16, 66, 'In het vee is er voor jullie ook een les: van wat er in hun buiken tussen mest en bloed is, geven Wij jullie zuivere melk te drinken die de drinkers goed bekomt.'),
(1968, 16, 67, 'En van de vruchten van de palmen en de wijnstokken: jullie nemen ervan een bedwelmende drank en goede voeding. Daarin is zeker een teken voor mensen die verstandig zijn.'),
(1969, 16, 68, 'En jouw Heer heeft de bijen ingegeven: "Betrek behuizingen in de bergen, in de bomen en in wat zij [als dakconstructies] optrekken.'),
(1970, 16, 69, 'Eet dan van alle vruchten en ga met gemak langs de wegen van jouw Heer." Uit hun buiken komt een drank van verschillende kleuren waarin genezing voor de mensen is. Daarin is zeker een teken voor mensen die nadenken.'),
(1971, 16, 70, 'En God heeft jullie geschapen, daarna neemt Hij jullie weg. En er zijn er onder jullie die teruggebracht worden tot de meest vernederende leeftijd zodat zij na kennis gehad te hebben niets meer weten. God is wetend en vrijmachtig.'),
(1972, 16, 71, 'En God heeft sommigen van jullie boven anderen met levensbehoeften bevoorrecht, maar zij die bevoorrecht zijn geven hun levensbehoeften niet door aan slaven waarover zij beschikken zodat zij daarin dan gelijk zijn. Verwerpen zij dan Gods genade?'),
(1973, 16, 72, 'En God heeft voor jullie uit jullie eigen midden echtgenotes gemaakt en Hij heeft aan jullie uit jullie echtgenotes zonen en kleinzonen gegeven. En Hij heeft met goede dingen in jullie levensonderhoud voorzien. Geloven zij dan in de onzin terwijl zij aan Gods genade geen geloof hechten?'),
(1974, 16, 73, 'En zij dienen in plaats van God iets wat geen macht heeft om hen met iets uit de hemel en de aarde van levensbehoeften te voorzien. En zij zijn ook nergens toe in staat.'),
(1975, 16, 74, 'Maakt dus geen vergelijkingen met God. God weet en jullie weten niet. *'),
(1976, 16, 75, 'God geeft een voorbeeld van een slaaf over wie beschikt wordt, die nergens macht over heeft en iemand in wiens levensbehoeften Wij van Onze kant goed voorzien en die er dan ook in het geheim en openbaar bijdragen van geeft, zijn die dan gelijk? Lof zij God! Integendeel, maar de meesten van hen weten het niet.'),
(1977, 16, 76, 'Ook geeft God een voorbeeld van twee mannen. Een van beiden is stom en heeft nergens macht over en hij is een last voor zijn beschermheer, waar hij hem ook heen stuurt, hij komt met niets goeds. Is die dan gelijk aan iemand die gebiedt rechtvaardig te handelen en die op een juiste weg is?'),
(1978, 16, 77, 'En van God is het verborgene van de hemelen en de aarde. En de beschikking van het uur is slechts een oogwenk of het is nog korter. God is almachtig.'),
(1979, 16, 78, 'En God heeft jullie terwijl jullie niets wisten uit de buiken van jullie moeders voortgebracht. Ook heeft Hij aan jullie gehoor, gezichtsvermogen en harten gegeven; misschien zullen jullie dank betuigen.'),
(1980, 16, 79, 'Hebben zij niet gezien naar de vogels die in de lucht van de hemel voortgedreven worden. Alleen God houdt ze vast. Daarin zijn zeker tekenen voor mensen die geloven.'),
(1981, 16, 80, 'En God heeft voor jullie van jullie huizen een plaats om te rusten gemaakt en Hij heeft voor jullie van de huiden van het vee huizen gemaakt die jullie licht om te dragen vinden op de dag dat jullie je verplaatsen en op de dag dat jullie je vestigen, en van hun wol, hun vacht en hun haar huisraad en tijdelijk vruchtgebruik.'),
(1982, 16, 81, 'En God heeft voor jullie uit wat Hij geschapen heeft schaduwen gemaakt, Hij heeft voor jullie in de bergen onderkomens gemaakt en Hij heeft voor jullie kledingstukken gemaakt die jullie tegen de hitte beschermen en kledingstukken die jullie tegen jullie gewelddadigheid beschermen. Zo bewijst Hij zijn genade aan jullie volledig; misschien zullen jullie je [aan God] overgeven.'),
(1983, 16, 82, 'Maar als zij zich afkeren, dan heb jij slechts de plicht van de verkondiging.'),
(1984, 16, 83, 'Zij kennen Gods genade en dan ontkennen zij die; de meesten van hen zijn immers ongelovigen.'),
(1985, 16, 84, 'En op de dag dat Wij van iedere gemeenschap een getuige laten opstaan. Dan zal aan hen die ongelovig zijn geen gehoor gegeven worden en zij zullen geen kans meer krijgen om het goed te maken.'),
(1986, 16, 85, 'En wanneer zij die onrecht plegen de bestraffing zien, dan krijgen zij geen strafverlichting en ook geen uitstel.'),
(1987, 16, 86, 'En wanneer de aanhangers van het veelgodendom hun [zogenaamd goddelijke] metgezellen zien, dan zeggen zij: "Onze Heer dit zijn nu onze [zogenaamd goddelijke] metgezellen die wij in plaats van U aangeroepen hebben." Maar zij zullen tot hen terugzeggen: "Jullie zijn leugenaars."'),
(1988, 16, 87, 'En zij zullen op die dag God vrede aanbieden en wat ze verzonnen hebben zijn zij kwijtgeraakt.'),
(1989, 16, 88, 'Zij die ongelovig zijn en die Gods weg versperren, hun zullen Wij boven op de bestraffing nog strafverzwaring geven omdat zij verderf zaaiden.'),
(1990, 16, 89, 'En op de dag dat Wij in iedere gemeenschap uit hun eigen midden een getuige tegen hen laten opstaan. En jou laten Wij als getuige komen tegen dezen hier. En tot jou hebben Wij het boek neergezonden als een verduidelijking van alles en als leidraad en barmhartigheid en goed nieuws voor hen die zich [aan God] overgeven.'),
(1991, 16, 90, 'God gebiedt rechtvaardig te handelen, goed te doen en aan de verwanten giften te geven en Hij verbiedt wat gruwelijk, verwerpelijk en gewelddadig is. Hij spoort jullie aan; misschien laten jullie je vermanen.'),
(1992, 16, 91, 'Komt Gods verbintenis na wanneer jullie een verbintenis aangaan en verbreekt de eden niet na de bekrachtiging ervan, want jullie hebben toch God tot borg tegen jullie gemaakt. God weet wat jullie doen.'),
(1993, 16, 92, 'En weest niet als zij die haar garen na het stevig gesponnen te hebben weer opsplitst door jullie eden voor onderlinge arglistigheid te gebruiken omdat de ene gemeenschap talrijker is dan de andere gemeenschap. God stelt jullie daarmee slechts op de proef en op de opstandingsdag zal Hij jullie duidelijk maken waarover jullie het oneens waren.'),
(1994, 16, 93, 'En als God het gewild had dan zou Hij jullie tot één gemeenschap gemaakt hebben, maar Hij brengt tot dwaling wie Hij wil en Hij brengt op het goede pad wie Hij wil en jullie zullen ter verantwoording geroepen worden over wat jullie aan het doen waren.'),
(1995, 16, 94, 'En gebruik jullie eden niet voor onderlinge arglistigheid, want dan glijdt een voet na stevig gestaan te hebben uit en proeven jullie het kwade omdat jullie Gods weg versperden en is er voor jullie een pijnlijke bestraffing.'),
(1996, 16, 95, 'Verkwanselt Gods verbintenis niet; wat God heeft is beter voor jullie, als jullie dat maar wisten.'),
(1997, 16, 96, 'Wat jullie hebben vergaat, maar wat God heeft blijft. En Wij zullen hen die geduldig volharden met hun loon belonen voor het beste dat zij deden.'),
(1998, 16, 97, 'Wie -- hetzij man of vrouw -- deugdelijk handelt als gelovige, die zullen Wij een goed leven laten leiden en Wij zullen hen met hun loon belonen voor het beste dat zij deden.'),
(1999, 16, 98, 'En wanneer jij de Koran voorleest, vraag God dan om bescherming tegen de vervloekte satan.'),
(2000, 16, 99, 'Hij heeft geen gezag over hen die geloven en die op hun Heer hun vertrouwen stellen.'),
(2001, 16, 100, 'Gezag heeft hij over hen die hem als beschermheer nemen en die Hem metgezellen toeschrijven.'),
(2002, 16, 101, 'En wanneer Wij een teken vervangen [door het] in de plaats van een ander teken [te geven] -- God weet het best wat Hij neerzendt -- dan zeggen zij: "Jij verzint maar wat." Integendeel, maar de meesten van hen weten het niet.'),
(2003, 16, 102, 'Zeg: "De heilige geest zond het van jouw Heer vandaan met de waarheid neer om hen die geloven te sterken en als leidraad en goed nieuws voor hen die zich [aan God] overgeven."'),
(2004, 16, 103, 'Wij weten wel dat zij zeggen: "Het is slechts een mens die hem onderwijst." De taal van hem op wie zij abusievelijk doelen is vreemd, maar dit is duidelijke Arabische taal.'),
(2005, 16, 104, 'Zij die niet in Gods tekenen geloven zal God niet op het goede pad brengen. En voor hen is er een pijnlijke bestraffing.'),
(2006, 16, 105, 'Slechts leugen verzinnen zij die niet in Gods tekenen geloven; dat zijn de leugenaars.'),
(2007, 16, 106, 'Wie na geloofd te hebben aan God geen geloof meer hecht -- en dan niet wie gedwongen is, terwijl zijn hart in het geloof rust gevonden heeft, maar zij die hun hart voor het ongeloof openstellen -- op hen rust Gods toorn en voor hen is er een geweldige bestraffing.'),
(2008, 16, 107, 'Dat komt omdat zij het tegenwoordige leven meer liefhebben dan het hiernamaals en omdat God de ongelovige mensen niet op het goede pad brengt.'),
(2009, 16, 108, 'Zij zijn het van wie God de harten en het gehoor en de ogen verzegeld heeft. Zij zijn het die onoplettend zijn.'),
(2010, 16, 109, 'Het staat vast dat zij in het hiernamaals de verliezers zijn.'),
(2011, 16, 110, 'Voorts is jouw Heer voor hen die zijn uitgeweken nadat zij in verzoeking gebracht zijn en die zich dan te weer hebben gesteld en geduldig hebben volhard -- voor hen is jouw Heer tenslotte toch vergevend en barmhartig. *'),
(2012, 16, 111, 'Op de dag dat elke ziel komt om voor zichzelf op te komen en aan elke ziel vergoed wordt wat zij gedaan heeft. En hun zal geen onrecht worden aangedaan.'),
(2013, 16, 112, 'God heeft een voorbeeld gegeven met een stad die in veiligheid en gerustheid verkeerde. Haar levensonderhoud kwam van elke kant tot haar in overvloed, maar zij was ongelovig aan Gods gunsten. En God liet hen het kleed van honger en vrees voelen voor wat zij aan het doen waren.'),
(2014, 16, 113, 'En er kwam tot hen een gezant uit hun midden, maar zij betichtten hem van leugens. Dus greep de bestraffing hen terwijl zij onrecht pleegden.'),
(2015, 16, 114, 'Eet dan van wat God jullie als levensonderhoud gegeven heeft als iets wat toegestaan en goed is en betuigt dank voor Gods genade, als Hij het is die jullie dienen.'),
(2016, 16, 115, 'Hij heeft voor jullie slechts verboden wat vanzelf is doodgegaan, bloed, varkensvlees en vlees van iets waarover iets anders dan God is aangeroepen, maar wie ertoe gedwongen wordt, niet uit begeerte of om te overtreden, God is vergevend en barmhartig.'),
(2017, 16, 116, 'En zegt niet van iets waarover jullie tongen bedrog uiten: "Dit is toegestaan en dit is verboden" om over God bedrog te verzinnen. Zij die over God bedrog verzinnen, hun zal het niet welgaan.'),
(2018, 16, 117, 'En kort genot. En voor hen is er een pijnlijke bestraffing.'),
(2019, 16, 118, 'Aan hen die het jodendom aanhangen hebben Wij verboden wat Wij jou al eerder verteld hebben. Wij hebben hun geen onrecht aangedaan, maar zij hebben zichzelf onrecht aangedaan.'),
(2020, 16, 119, 'Voorts is jouw Heer voor hen die het verkeerde uit onwetendheid doen en dan daarna berouw tonen en het weer goedmaken, jouw Heer is daarna zeker vergevend en barmhartig.'),
(2021, 16, 120, 'Ibrahiem was een voorbeeld, onderdanig aan God, iemand die het zuivere geloof aanhing en hij behoorde niet tot de veelgodendienaars;'),
(2022, 16, 121, 'hij was dankbaar voor Zijn weldaden: Hij heeft hem verkozen en naar een juiste weg gebracht.'),
(2023, 16, 122, 'Wij hebben hem in het tegenwoordige leven gegeven wat goed is en in het hiernamaals hoort hij bij de rechtschapenen.'),
(2024, 16, 123, 'Toen openbaarden Wij aan jou: "Volg het geloof van Ibrahiem die het zuivere geloof aanhing; hij behoorde niet bij de veelgodendienaars."'),
(2025, 16, 124, 'De sabbat is opgelegd aan hen die het erover oneens waren. En jullie Heer zal op de opstandingsdag tussen hen oordelen over dat waarover zij het oneens waren.'),
(2026, 16, 125, 'Roep op tot de weg van jouw Heer met wijsheid en goede aansporing en twist met hen op de beste manier. Jouw Heer kent wie van Zijn weg afdwaalt het best en Hij kent hen die het goede pad volgen het best.'),
(2027, 16, 126, 'En als jullie bestraffen, bestraft dan evenals jullie zijn bestraft. Maar als jullie geduldig volharden, dan is dat beter voor hen die geduldig volharden.'),
(2028, 16, 127, 'Volhard geduldig. Jouw geduld komt slechts met Gods hulp. En wees niet bedroefd over hen en wees niet benauwd over wat zij aan listen beramen.'),
(2029, 16, 128, 'God is met hen die godvrezend zijn en hen die goed doen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Geprezen zij Hij die Zijn dienaar bij nacht een reis liet maken van de heilige moskee naar de verste moskee waarvan Wij de omgeving gezegend hebben om hem iets van Onze tekenen te tonen. Hij is de horende, de doorziende.'),
(2031, 17, 2, 'En Wij hebben Moesa het boek gegeven en Wij hebben het gemaakt tot een leidraad voor de Israëlieten: "Neemt in plaats van Mij geen andere voogd,'),
(2032, 17, 3, 'jullie die nakomelingen zijn van hen die Wij met Noeh gedragen hebben. Hij was een dienaar die dank betuigt."'),
(2033, 17, 4, 'En Wij hebben aan de Israëlieten in het boek aangekondigd: jullie zullen twee maal op de aarde verderf zaaien en jullie zullen je zeer hovaardig gedragen.'),
(2034, 17, 5, 'En wanneer de aanzegging van de eerste van beide uitkomt, dan zenden Wij tot jullie dienaren van Ons, mensen die hevig geweld uitoefenen. Zij dringen dan in de woningen door. Het is een aanzegging die wordt uitgevoerd.'),
(2035, 17, 6, 'Dan geven Wij jullie weer de overhand over hen en versterken Wij jullie met bezittingen en zonen en maken jullie tot een grotere troepenmacht.'),
(2036, 17, 7, 'Als jullie goed doen doen jullie goed in jullie eigen voordeel en als jullie verkeerd doen dan is het in jullie eigen nadeel. En wanneer de aanzegging van de tweede maal uitkomt, dan is het om jullie te kwellen en de moskee binnen te dringen zoals zij de eerste maal zijn binnengedrongen en om wat zij in hun macht krijgen geheel te vernietigen.'),
(2037, 17, 8, 'Misschien dat jullie Heer erbarmen met jullie heeft, maar als jullie het nog eens doen, doen Wij het nog eens. En Wij hebben de hel tot een gevangenis voor de ongelovigen gemaakt."'),
(2038, 17, 9, 'Deze Koran leidt tot dat wat juister is en verkondigt aan de gelovigen die de deugdelijke daden doen dat er voor hen een groot loon is'),
(2039, 17, 10, 'en dat Wij voor hen die niet geloven in het hiernamaals een pijnlijke bestraffing hebben klaargemaakt.'),
(2040, 17, 11, 'En de mens roept om het slechte evenals hij om het goede roept, want de mens is haastig.'),
(2041, 17, 12, 'Wij hebben de dag en de nacht tot twee tekenen gemaakt en het teken van de nacht hebben Wij toen uitgewist en het teken van de dag hebben Wij gemaakt om te zien en opdat jullie streven naar een gunst van jullie Heer en opdat jullie het aantal van de jaren en de berekening weten en alles hebben Wij duidelijk uiteengezet.'),
(2042, 17, 13, 'En bij iedere mens hebben Wij zijn lotsbestemming aan zijn nek vastgemaakt en Wij zullen voor hem op de opstandingsdag een boek tevoorschijn halen dat hij opengeslagen zal aantreffen:'),
(2043, 17, 14, '"Lees jouw boek, jij bent zelf vandaag goed genoeg om af te rekenen."'),
(2044, 17, 15, 'Wie zich de goede richting laat wijzen, die volgt het goede pad slechts in zijn eigen voordeel en wie dwaalt, dwaalt slechts tot zijn eigen nadeel en niemand is belast met de last van een ander. En Wij bestraffen niet voordat Wij een gezant hebben opgeroepen.'),
(2045, 17, 16, 'En wanneer Wij een stad wensten te vernietigen gaven Wij aan hen die daarin in luxe leven bevel. Dan gedroegen zij zich verderfelijk en dan heeft de uitspraak tegen haar zich bewaarheid en vernietigden Wij haar geheel.'),
(2046, 17, 17, 'En hoeveel generaties hebben Wij na Noeh reeds vernietigd! Jouw Heer is over de zonden van Zijn dienaren goed genoeg ingelicht en doorziet ze.'),
(2047, 17, 18, 'Wie het snel voorbijgaande leven wenst, die bezorgen Wij daarin snel wat Wij willen, voor wie Wij wensen. Dan maken Wij voor hem de hel om in te braden, verafschuwd en verstoten als hij is.'),
(2048, 17, 19, 'Wie het hiernamaals wenst en dat ijverig nastreeft terwijl hij gelovig is, zij zijn het aan wie voor hun streven dank betuigd wordt.'),
(2049, 17, 20, 'Allen sterken Wij, dezen en genen, door de gave van jouw Heer. De gave van jouw Heer wordt niet ontzegd.'),
(2050, 17, 21, 'Kijk hoe Wij sommigen van hen boven anderen hebben bevoorrecht en het hiernamaals heeft hogere rangen en grotere bevoorrechtingen.'),
(2051, 17, 22, 'Stel naast God geen andere god, want dan zul je verafschuwd en verlaten terneer zitten. *'),
(2052, 17, 23, 'En jouw Heer heeft bepaald dat jullie alleen Hem zullen dienen en dat men goed moet zijn voor de ouders; of nu een van tweeën of allebei bij jou de ouderdom bereiken, zeg dan niet: "Foei" tegen hen, bejegen hen niet onheus en spreek op een hoffelijke manier tot hen.'),
(2053, 17, 24, 'En wees uit barmhartigheid voor hen nederig en ontvankelijk en zeg: "Mijn Heer, erbarm U over hen, zoals zij mij grootbrachten toen ik klein was."'),
(2054, 17, 25, 'Jullie Heer weet het best wat in jullie binnenste is; als jullie rechtschapen zijn, dan is Hij voor de schuldbewusten vergevend.'),
(2055, 17, 26, 'En geef de verwant wat hem toekomt en ook de behoeftige en wie onderweg is en wees niet verspillend.'),
(2056, 17, 27, 'De verspillers zijn broeders van de satans en de satan is jegens zijn Heer ondankbaar.'),
(2057, 17, 28, 'En als jij je van hen afwendt in jouw streven naar barmhartigheid van jouw Heer waarop jij hoopt, spreek dan tot hen op een vriendelijke manier.'),
(2058, 17, 29, 'Houd je hand niet aan de hals gebonden en strek haar ook niet helemaal uit, want dan zul je met verwijten overladen beschaamd terneer zitten.'),
(2059, 17, 30, 'God voorziet ruimschoots in het levensonderhoud van wie Hij wil en ook met mate. Hij is over Zijn dienaren goed ingelicht en doorziet hen.'),
(2060, 17, 31, 'En jullie mogen jullie kinderen niet doden uit vrees voor armoe. Wij voorzien in hun en jullie levensonderhoud. Hen doden is een grote zonde.'),
(2061, 17, 32, 'En jullie mogen geen ontucht benaderen. Dat is iets gruwelijks en een slechte manier van doen.'),
(2062, 17, 33, 'En jullie mogen niemand doden -- wat God verboden heeft -- behalve volgens het recht. En als iemand onrechtmatig gedood is dan geven Wij zijn naaste verwant machtiging, maar hij moet in het doden niet te ver gaan; hij wordt immers geholpen.'),
(2063, 17, 34, 'En jullie mogen niet aan het bezit van de wees komen, tenzij dat op de beste manier gebeurt, totdat hij volgroeid is. En komt de verbintenis na. Over de verbintenis wordt verantwoording afgelegd.'),
(2064, 17, 35, 'En geeft volle maat als jullie afmeten en weegt met de juiste weegschaal. Dat is beter voor jullie en een mooiere afsluiting.'),
(2065, 17, 36, 'En ga niet achter iets aan waarvan jij geen kennis hebt. Het horen, het zien en het hart, over al dat wordt verantwoording afgelegd.'),
(2066, 17, 37, 'En loop niet verwaand op de aarde rond. Jij zult de aarde immers niet doorboren, noch de bergen in hoogte kunnen bereiken.'),
(2067, 17, 38, 'Van dat alles wordt het slechte bij jouw Heer verafschuwd.'),
(2068, 17, 39, 'Dat is iets wat jouw Heer van de wijsheid aan jou heeft geopenbaard. En stel naast God geen andere god, anders zul je in de hel worden geworpen, met verwijten overladen en verstoten.'),
(2069, 17, 40, 'Heeft jullie Heer dan voor jullie de zonen uitgekozen en zelf uit de engelen dochters genomen? Jullie zeggen werkelijk iets vreselijks!'),
(2070, 17, 41, 'In deze Koran hebben Wij uiteenzettingen gegeven opdat zij vermaand zouden worden, maar zij krijgen alleen maar meer afkeer.'),
(2071, 17, 42, 'Zeg: "Als er naast Hem andere goden zouden zijn, zoals zij zeggen, dan zouden ze zich een weg naar de Heer van de troon zoeken."'),
(2072, 17, 43, 'Hij zij geprezen en ver verheven is Hij boven wat zij zeggen.'),
(2073, 17, 44, 'Hem prijzen de zeven hemelen en de aarde en wie daarin zijn. Er is niets of het prijst Zijn lof, maar jullie begrijpen hun lofprijzing niet. Hij is zachtmoedig en vergevend.'),
(2074, 17, 45, 'En wanneer jij de Koran voorleest zetten Wij tussen jou en hen die niet geloven een verborgen afscheiding.'),
(2075, 17, 46, 'En over hun harten hebben Wij hoezen gelegd, zodat zij het niet begrijpen en in hun oren hardhorendheid. En wanneer jij alleen jouw Heer in de Koran noemt keren zij in afkeer hun ruggen toe.'),
(2076, 17, 47, 'Wij weten het best waarom zij luisteren wanneer zij naar jou luisteren en wanneer zij een vertrouwelijk gesprek hebben wanneer de onrechtplegers zeggen: "Jullie volgen slechts een man die betoverd is."'),
(2077, 17, 48, 'Kijk hoe zij voor jou vergelijkingen maken; zij dwalen en kunnen dus geen weg meer vinden.'),
(2078, 17, 49, 'En zij zeggen: "Wanneer wij dan beenderen en overblijfselen zijn zullen wij dan echt als een nieuwe schepping opgewekt worden?" *'),
(2079, 17, 50, 'Zeg: "Jullie kunnen stenen of ijzer zijn,'),
(2080, 17, 51, 'of iets anders geschapens wat jullie in je hart nog moeilijker lijkt!" Maar dan zullen zij zeggen: "Wie zal ons dan terugbrengen?" Zeg: "Hij die jullie de eerste maal geschapen heeft!" Dan zullen zij tegen jou hun hoofd schudden en zeggen: "Wanneer is dat dan?" Zeg: "Misschien dat het dichtbij is."'),
(2081, 17, 52, 'De dag dat Hij jullie zal oproepen! Dan zullen jullie Hem gehoor geven door Hem te loven en jullie zullen denken dat het voor jullie slechts kort geduurd heeft.'),
(2082, 17, 53, 'En zeg aan Mijn dienaren dat te zeggen wat het beste is, want de satan hitst op tot tweedracht onder hen; de satan is een verklaarde vijand van de mens.'),
(2083, 17, 54, 'Jullie Heer kent jullie het best. Als Hij wil, zal Hij erbarmen met jullie hebben of als Hij wil zal Hij jullie bestraffen. En Wij hebben jou niet als voogd over hen gezonden.'),
(2084, 17, 55, 'En jouw Heer kent wie er in de hemelen en op de aarde zijn het best. Wij hebben sommige profeten boven andere verkozen en aan Dawoed hebben Wij een Zaboer gegeven.'),
(2085, 17, 56, 'Zeg: "Roept hen maar aan van wie jullie beweren dat zij naast Hem bestaan. Zij hebben geen macht om van jullie iets schadelijks weg te nemen of te veranderen."'),
(2086, 17, 57, 'Zij die zij aanroepen zoeken zelf naar het middel om nader tot hun Heer te komen, zij die van hen het naast zijn, zij hopen op Zijn barmhartigheid en vrezen Zijn bestraffing. Voor de bestraffing van jouw Heer moet men op zijn hoede zijn.'),
(2087, 17, 58, 'En er is geen stad of Wij vernietigen haar voor de opstandingsdag of bestraffen haar met een strenge bestraffing. Dat is in het boek opgetekend.'),
(2088, 17, 59, 'En niets verhinderde Ons de tekenen te zenden dan dat zij die er eertijds waren ze loochenden. En Wij gaven de Thamoed de kameelmerrie als iets duidelijk zichtbaars, maar zij behandelden haar onrechtvaardig. En Wij zenden slechts tekenen om vrees aan te jagen.'),
(2089, 17, 60, 'En toen Wij tot jou zeiden dat jouw Heer de mensen omsingelde. En Wij hebben het visioen dat Wij jou hebben laten zien alleen maar tot een verzoeking voor de mens gemaakt en ook de in de Koran vervloekte boom; Wij jagen hun vrees aan, maar zij nemen slechts toe in grote onbeschaamdheid.'),
(2090, 17, 61, 'Toen Wij tot de engelen zeiden: "Buigt eerbiedig neer voor Adam", bogen zij zich eerbiedig neer, behalve Iblies. Deze zei: "Zal ik mij eerbiedig neerbuigen voor iemand die U van klei hebt geschapen!"'),
(2091, 17, 62, 'Hij zei: "Hoe lijkt het U? Dit is hij die U meer geëerd hebt dan mij. Als U mij uitstel verleent tot aan de opstandingsdag, dan zal ik zijn nageslacht op maar enkelen na in bedwang houden."'),
(2092, 17, 63, 'Hij zei: "Ga weg, en als dan iemand van hen jou volgt, dan is de hel jullie beloning, een rijkelijke beloning!'),
(2093, 17, 64, 'En schrik hen af met jouw stem, wie je maar kunt. En verzamel jouw paarden en jouw mannen tegen hen. En deel met hen in de bezittingen en de kinderen en doe hun toezeggingen" -- de satan zegt hun slechts begoocheling toe --'),
(2094, 17, 65, '"want over Mijn dienaren heb jij geen gezag." Jouw Heer is goed genoeg als voogd.'),
(2095, 17, 66, 'Jullie Heer is het die voor jullie de schepen op zee voortstuwt opdat jullie streven naar een gunst van Hem. Hij is voor jullie barmhartig.'),
(2096, 17, 67, 'En wanneer jullie op zee tegenspoed treft dan zijn zij hen die zij aanroepen kwijt, behalve Hem. Maar wanneer Hij jullie dan gered en aan land gebracht heeft, wenden jullie je af; de mens is ondankbaar.'),
(2097, 17, 68, 'Wanen jullie je er dan veilig voor dat Hij een stuk vasteland met jullie laat wegzinken of dat Hij tegen jullie een zandstorm stuurt? Dan vinden jullie niemand die voor jullie garant staat.'),
(2098, 17, 69, 'Of wanen jullie je er dan veilig voor dat Hij jullie een andere keer ernaar terug laat keren? En dat Hij dan tegen jullie een rukwind stuurt en jullie dan laat verdrinken om jullie ondankbare ongeloof? Dan vinden jullie niemand die Ons voor jullie zou kunnen vervolgen. *'),
(2099, 17, 70, 'Wij hebben de kinderen van Adam geëerd en Wij hebben hen op het vasteland en de zee gedragen en Wij hebben met goede dingen in hun onderhoud voorzien en Wij hebben hen duidelijk verkozen boven velen van hen die Wij geschapen hebben.'),
(2100, 17, 71, 'Op de dag dat Wij alle mensen met hun voorganger oproepen. Aan wie dan hun boek in hun rechterhand gegeven wordt, dat zijn zij die hun boek voorlezen en wie nog geen vezeltje onrecht wordt aangedaan.'),
(2101, 17, 72, 'En wie er hier blind is, die is in het hiernamaals blind en de weg nog meer kwijt.'),
(2102, 17, 73, 'En bijna hadden zij jou weggelokt van wat Wij aan jou geopenbaard hebben, zodat jij over Ons iets anders zou verzinnen; dan hadden zij jou tot vriend genomen.'),
(2103, 17, 74, 'Als Wij jou niet gesterkt hadden, dan zou jij bijna bij hen een beetje steun gezocht hebben.'),
(2104, 17, 75, 'Dan hadden Wij jou het leven dubbel en ook de dood dubbel laten proeven. Dan zou jij tegen Ons voor jou geen helper vinden.'),
(2105, 17, 76, 'En bijna hadden zij jou door afschrikking het land uitgejaagd om je er zo uit te verdrijven, maar dan zou het na jouw vertrek voor hen nog maar kort duren,'),
(2106, 17, 77, 'volgens de gebruikelijke behandeling van hen tot wie Wij voor jouw tijd Onze gezanten gezonden hebben. En jij zult in Onze gebruikelijke behandeling geen verandering vinden.'),
(2107, 17, 78, 'Verricht de salaat bij het neergaan van de zon tot het vallen van de nacht en ook de Koranlezing van de dageraad; van de Koranlezing van de dageraad moet men getuige zijn.'),
(2108, 17, 79, 'En ook \'s nachts, houd ermee nachtwake als een extra verplichting voor jou; misschien dat jouw Heer jou zal opwekken in een loffelijke positie.'),
(2109, 17, 80, 'En zeg: "Mijn Heer, laat mij op een waarachtige manier binnengaan en laat mij op een waarachtige manier naar buiten gaan en verschaf mij van Uw kant een machtiging die helpt."'),
(2110, 17, 81, 'En zeg: "De waarheid is gekomen en aan de onzin is een einde gekomen; aan de onzin komt echt een einde."'),
(2111, 17, 82, 'En Wij zenden van de Koran [gedeelten] neer die genezing en barmhartigheid zijn voor de gelovigen. De onrechtplegers lijden alleen maar meer verlies.'),
(2112, 17, 83, 'En wanneer Wij de mens genade schenken, wendt hij zich af en gaat opzij. Maar wanneer hem het kwaad treft, is hij wanhopig.'),
(2113, 17, 84, 'Zeg: "Ieder werkt naar zijn aard en jullie Heer weet het best wie het betere pad volgt."'),
(2114, 17, 85, 'Zij vragen jou over de geest. Zeg: "De geest komt door de beschikking van mijn Heer. En van de kennis is jullie slechts weinig gegeven."'),
(2115, 17, 86, 'En als Wij wilden zouden Wij wat Wij aan jou geopenbaard hebben wegnemen. Dan vind jij niemand die voor jou tegen Ons garant staat,'),
(2116, 17, 87, 'behalve als barmhartigheid van jouw Heer; Zijn goedgunstigheid aan jou is groot.'),
(2117, 17, 88, 'Zeg: "Als de mensen en de djinn zich zouden verenigen om met net zoiets als deze Koran te komen dan zouden ze toch niet met iets overeenkomstigs komen, ook al zouden zij elkaar tot hulp zijn."'),
(2118, 17, 89, 'In deze Koran hebben Wij voor de mensen uiteenzettingen gegeven van allerlei voorbeelden, maar de meeste mensen willen alleen maar ongelovig zijn.'),
(2119, 17, 90, 'En zij zeggen: "Wij zullen aan jou geen geloof hechten totdat jij voor ons een bron uit de aarde laat ontspringen,'),
(2120, 17, 91, 'of tot jij een tuin van palmen en wijnstokken hebt en er ruimschoots rivieren doorheen laat stromen,'),
(2121, 17, 92, 'of tot jij de hemel, zoals jij beweert, op ons in stukken laat neervallen of met God en de engelen in groepen komt,'),
(2122, 17, 93, 'of tot jij een huis van goud bezit of in de hemel opstijgt. En wij zullen niet geloven dat jij opgestegen bent als je niet een boek op ons neerzendt dat wij kunnen lezen." Zeg: "Geprezen zij mijn Heer! Ben ik dan iets anders dan een mens, een gezant?"'),
(2123, 17, 94, 'En wat de mensen verhinderde te geloven toen de leidraad tot hen kwam was alleen maar dat zij zeiden: "Heeft God dan een mens als gezant opgeroepen?"'),
(2124, 17, 95, 'Zeg: "Als er op de aarde engelen waren geweest die rustig rondwandelden, dan hadden Wij tot hen uit de hemel een engel als gezant neergezonden."'),
(2125, 17, 96, 'Zeg: "God is goed genoeg als getuige tussen mij en jullie. Hij is over Zijn dienaren goed ingelicht en doorziet ze."'),
(2126, 17, 97, 'Wie door God op het goede pad gebracht is, die volgt het goede pad en voor wie Hij tot dwaling brengt zul jij buiten Hem om geen beschermers vinden; Wij zullen hen op de opstandingsdag verzamelen: op hun gezichten [ter aarde geworpen], blind, stom en doof. Hun verblijfplaats is de hel; telkens als hij begint te doven wakkeren Wij voor hen de vuurgloed aan.'),
(2127, 17, 98, 'Zo wordt aan hen vergolden omdat zij aan Onze tekenen geen geloof hechtten en dat zij zeiden: "Wanneer wij dan beenderen en overblijfselen zijn zullen wij dan echt als een nieuwe schepping opgewekt worden?" *'),
(2128, 17, 99, 'Hebben zij dan niet gezien dat God die de hemelen en de aarde geschapen heeft de macht heeft iets te scheppen wat aan hen gelijk is? En Hij heeft voor hen een termijn gesteld waaraan geen twijfel is, maar de onrechtplegers willen alleen maar ongelovig zijn.'),
(2129, 17, 100, 'Zeg: "Als jullie over de schatkamers van de barmhartigheid van mijn Heer konden beschikken dan zouden jullie het uit vrees voor het weggeven bij je houden." De mens is gierig.'),
(2130, 17, 101, 'En Wij hebben aan Moesa negen duidelijke tekenen gegeven. Vraag de Israëlieten maar. Toen hij tot hen kwam en Fir\'aun tot hem zei: "Ik denk echt, o Moesa, dat jij betoverd bent."'),
(2131, 17, 102, 'Deze zei: "Jij weet heel goed dat alleen de Heer van de hemelen en de aarde deze dingen als inzichtelijke bewijzen heeft neergezonden en ik denk echt, o Fir\'aun dat jij ten ondergang gedoemd bent."'),
(2132, 17, 103, 'En hij wenste hen door afschrikking het land uit te jagen, maar Wij verdronken hem en wie er bij hem waren allemaal.'),
(2133, 17, 104, 'En Wij zeiden daarna tot de Israëlieten: "Bewoont het land en wanneer de toezegging van het hiernamaals uitkomt, zullen Wij jullie allen door elkaar bijeenbrengen."'),
(2134, 17, 105, 'Met de waarheid hebben Wij hem neergezonden en met de waarheid is hij neergedaald en jou hebben Wij slechts gezonden als verkondiger van goed nieuws en als waarschuwer.'),
(2135, 17, 106, 'En een Koran hebben Wij in gedeelten opgedeeld opdat jij hem bedachtzaam aan de mensen zou voorlezen; Wij hebben hem in gedeelten neergezonden.'),
(2136, 17, 107, 'Zeg: "Gelooft erin of gelooft niet. Zij aan wie vroeger al kennis gegeven was vallen op hun kinnen in eerbiedige buiging neer, wanneer hij aan hen wordt voorgelezen."'),
(2137, 17, 108, 'En zij zeggen: "Geprezen zij onze Heer. De toezegging van onze Heer is uitgevoerd."'),
(2138, 17, 109, 'En zij vallen op hun kinnen terwijl zij huilen en hun deemoed neemt erdoor toe."'),
(2139, 17, 110, 'Zeg: "Roept God of roept de Erbarmer aan. Waarmee jullie ook aanroepen, Hij heeft de mooiste namen. En spreek niet luid bij je salaat en fluister daarbij ook niet, maar zoek naar een manier ertussenin."'),
(2140, 17, 111, 'En zeg: "Lof zij God die zich geen kind genomen heeft en die geen metgezel in de heerschappij heeft en die geen helper tegen de vernedering behoeft. En verheerlijk Zijn grootheid!"');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Lof zij God die het boek tot Zijn dienaar heeft neergezonden en die heeft gemaakt dat het geen afwijking heeft,'),
(2142, 18, 2, '[een boek] dat juist is, om de mensen te waarschuwen voor een hevig geweld dat van Hem komt en om aan de gelovigen die de deugdelijke daden doen het goede nieuws te verkondigen dat er voor hen een goed loon is --'),
(2143, 18, 3, 'daarin zullen zij voor altijd verblijven --'),
(2144, 18, 4, 'en om hen te waarschuwen die zeggen: "God heeft zich een kind genomen."'),
(2145, 18, 5, 'Zij hebben daar geen kennis over en hun vaderen ook niet. Het is een groot woord dat uit hun monden komt. Zij spreken slechts leugen.'),
(2146, 18, 6, 'En misschien zul jij ten gevolge daarvan, als zij in dit bericht niet geloven, je zelf van smart nog ombrengen.'),
(2147, 18, 7, 'Wij hebben wat er op de aarde is tot een versiering voor haar gemaakt om hen op de proef te stellen wie van hen het beste is in wat hij doet.'),
(2148, 18, 8, 'En Wij zullen wat er op haar is tot dorre kale grond maken.'),
(2149, 18, 9, 'Of denk jij dan dat de mensen van de grot en ar-Rakiem iets verbazingwekkends onder Onze tekenen waren?'),
(2150, 18, 10, 'Toen de jonge mannen in de grot een onderkomen zochten en zeiden: "Onze Heer, geef ons van Uw kant barmhartigheid en bereid ons uit onze toestand een goede uitweg."'),
(2151, 18, 11, 'En Wij bedekten in de grot hun oren voor een aantal jaren.'),
(2152, 18, 12, 'Toen lieten Wij hen opstaan, opdat Wij zouden weten wie van beide partijen het nauwkeurigst zou berekenen hoe lang zij er gebleven waren.'),
(2153, 18, 13, 'Wij vertellen jou het bericht over hen naar waarheid; zij waren jonge mannen die in hun Heer geloofden en die Wij nog beter op het goede pad brachten.'),
(2154, 18, 14, 'En Wij versterkten hun harten toen zij opstonden en zeiden: "Onze Heer is de Heer van de hemelen en de aarde. Wij zullen in plaats van Hem geen god aanroepen; dan zouden wij iets afwijkends zeggen.'),
(2155, 18, 15, 'Onze mensen hier hebben zich in plaats van Hem goden genomen. Hadden zij dan niet daarvoor met een duidelijke machtiging kunnen komen? En wie is er zondiger dan wie over God bedrog verzint?'),
(2156, 18, 16, 'Maar daar jullie je van hen afgescheiden hebben en alleen God dienen, zoekt dan jullie onderkomen in de grot. Dan zal jullie Heer iets van Zijn barmhartigheid voor jullie uitspreiden en maken dat er uit jullie toestand voor jullie iets zinvols voortkomt." *'),
(2157, 18, 17, 'En jij zou zien dat de zon, wanneer zij opging, zich aan de rechterkant van hun grot wegboog en wanneer zij onderging, dat zij hen aan de linkerkant schampte, terwijl zij zich in een nis ervan bevonden. Dat behoort tot Gods tekenen. Wie door God op het goede pad gebracht is, die volgt het goede pad en wie Hij tot dwaling brengt, voor hem vind jij geen beschermer die de weg wijst.'),
(2158, 18, 18, 'En jij zou denken dat zij wakker waren, terwijl zij lagen te slapen. En Wij draaiden hen op hun rechterkant en op hun linkerkant terwijl hun hond zijn voorpoten op de drempel had uitgestrekt. Als jij hen te zien had gekregen, zou je bij hen weg zijn gevlucht en je zou voor hen van vrees vervuld zijn geweest.'),
(2159, 18, 19, 'Zo hebben Wij hen laten opstaan, opdat zij elkaar zouden ondervragen. Een van hen zei: "Hoe lang heeft het voor jullie geduurd?" Zij zeiden: "Het heeft voor ons een dag of een paar dagen geduurd." Zij zeiden: "Jullie Heer weet het best hoe lang het voor jullie geduurd heeft. Zendt dan een van jullie met dit zilverstuk van jullie naar de stad en dan moet hij maar kijken wat het beste voedsel is om jullie daarvan proviand te brengen. En hij moet zich vriendelijk gedragen en niemand van jullie iets laten merken.'),
(2160, 18, 20, 'Want als zij van jullie vernemen dan zullen zij jullie stenigen of jullie naar hun geloof terugbrengen en dan zal het jullie nooit welgaan."'),
(2161, 18, 21, 'Zo maakten Wij dat men hen ontdekte, opdat zij zouden weten dat Gods toezegging waar is en dat er aan het uur geen twijfel is. Toen zij met elkaar over hun geval twistten, zeiden zij: "Bouwt een gebouw over hen heen." Maar hun Heer kent hen het best. Zij die in hun geval overwonnen zeiden: "Wij zullen over hen heen een plaats van aanbidding inrichten."'),
(2162, 18, 22, 'Ze zullen zeggen: "Drie, en de vierde van hen is hun hond" en ze zeggen: "Vijf, en de zesde van hen is hun hond." Het is een gissing naar het verborgene. En ze zeggen: "Zeven, en de achtste van hen is hun hond." Zeg: "Mijn Heer kent hun aantal het best" En slechts weinigen kennen hen. Twist dus slechts op een oppervlakkige manier over hen en vraag van geen van hen uitsluitsel over hen.'),
(2163, 18, 23, 'En zeg niet van iets: "Ik zal dat morgen doen"'),
(2164, 18, 24, 'zonder: "als God wil." En gedenk jouw Heer wanneer je het vergeten hebt en zeg: "Misschien dat mijn Heer mij op het goede pad zal brengen, dat dichter bij de rechtschapenheid is dan dit"'),
(2165, 18, 25, 'Zij waren driehonderd jaar in de grot gebleven en nog negen jaar erbij.'),
(2166, 18, 26, 'Zeg: "God weet het best hoe lang het voor hen duurde. Van Hem is het verborgene van de hemelen en de aarde. Wat is Hij scherpziend en wat is Hij scherphorend! Zij hebben buiten hem geen beschermer en Hij maakt niemand deelgenoot van Zijn oordeel."'),
(2167, 18, 27, 'En lees voor wat Ik jou van het boek van jouw Heer heb geopenbaard. Er is niemand die Zijn woorden kan veranderen en buiten Hem zul jij geen schuilplaats vinden.'),
(2168, 18, 28, 'En volhard geduldig met hen die hun Heer in de morgen en de avond aanroepen terwijl zij Zijn aangezicht zoeken en laat het niet zo zijn dat jij voor hen geen oog hebt omdat jij de pracht van het tegenwoordige leven wenst. En gehoorzaam hem niet van wie Wij het hart te onoplettend hebben gemaakt om Ons te gedenken en die zijn eigen bevlieging volgt en die onmatig is.'),
(2169, 18, 29, 'En zeg: "De waarheid komt van jouw Heer vandaan." Wie het wil, die moet dan geloven en wie het wil, die moet maar ongelovig zijn. Wij hebben voor de onrechtplegers een vuur klaargemaakt dat hen van alle kanten omhult. En als zij om hulp vragen worden zij geholpen met water als gesmolten metaal dat de gezichten roostert. Dat is pas een slechte drank en het is een slechte rustplaats!'),
(2170, 18, 30, 'Zij die geloven en de deugdelijke daden doen? Wij zullen het loon van hen die goed werk doen niet verloren laten gaan.'),
(2171, 18, 31, 'Zij zijn het voor wie de tuinen van \'Adn zijn waarvan de rivieren onder hen door stromen. Zij tooien zich daarin met armbanden van goud en zij kleden zich met groene kleding van zijde en goudbrokaat, terwijl zij er op ligbanken achteroverleunen. Dat is pas een goede beloning en het is een goede rustplaats!'),
(2172, 18, 32, 'En geef hun een voorbeeld: Twee mannen, voor een van beiden maakten Wij twee tuinen met wijnstokken en Wij omringden die met palmen en plaatsten tussen beide landbouwgewassen.'),
(2173, 18, 33, 'Beide tuinen gaven hun vruchtopbrengst en schoten daarbij in niets tekort. En Wij lieten door beide een rivier stromen.'),
(2174, 18, 34, 'Hij had inderdaad opbrengst! Hij zei dus tot zijn metgezel met wie hij in gesprek was: "Ik heb meer bezit dan jij en beschik over meer mensen."'),
(2175, 18, 35, 'En hij ging zijn tuin in, terwijl hij zichzelf onrecht aandeed. Hij zei: "Ik denk niet dat dit hier ooit zal vergaan'),
(2176, 18, 36, 'en ik denk ook niet dat het uur aanstaande is. En als ik naar mijn Heer teruggebracht word zal ik nog iets beters dan dit hier als eindbestemming vinden."'),
(2177, 18, 37, 'Zijn metgezel met wie hij in gesprek was zei tot hem: "Hecht jij dan geen geloof aan Hem die jou uit aarde en dan uit een druppel schiep en je dan tot man gevormd heeft?'),
(2178, 18, 38, 'Maar voor mij is Hij God, mijn Heer, en ik voeg aan mijn Heer niemand als metgezel toe.'),
(2179, 18, 39, 'Had je maar gezegd toen jij je tuin inging: \'Zoals God het wil. Alleen in God is kracht!? Als jij van mij ziet, dat ik minder bezit en kinderen heb,'),
(2180, 18, 40, 'misschien dat God mij dan iets beters zal geven dan jouw tuin en er een afrekening uit de hemel naartoe zal zenden zodat het kale, glibberige grond wordt,'),
(2181, 18, 41, 'of dat het water er diep wegzakt zodat jij het niet meer kunt vinden."'),
(2182, 18, 42, 'En zijn vruchten werden volkomen verwoest. Toen begon hij zich de handen te wringen om wat hij ervoor had uitgegeven nu hij er zo geruïneerd bijlag. En hij zei: "Ach had ik aan mijn Heer maar niemand als metgezel toegevoegd."'),
(2183, 18, 43, 'En buiten God was er voor hem geen troepenmacht die hem kon helpen; hij kreeg dan ook geen hulp.'),
(2184, 18, 44, 'Als het zo ver is heeft alleen de ware God nog bescherming te bieden. Hij geeft de beste beloning en het beste uiteinde.'),
(2185, 18, 45, 'En maak voor hen een vergelijking: Het tegenwoordige leven is als water dat Wij uit de hemel laten neerdalen. De planten van de aarde groeien daardoor in bonte mengeling. Dan worden zij droge stengels die de wind wegblaast. God is over alles machthebber.'),
(2186, 18, 46, 'Bezit en zonen zijn de pracht van het tegenwoordige leven, maar de deugdelijke daden, die duurzaam zijn, hebben bij jouw Heer een betere beloning en een betere verwachting.'),
(2187, 18, 47, 'En op de dag dat Wij de bergen in beweging zetten en jij de aarde tevoorschijn ziet komen en Wij hen verzamelen, dan laten Wij niet één van hen achter.'),
(2188, 18, 48, 'En zij worden voor jouw Heer in een rij voorgeleid: "Jullie bent tot Ons gekomen zoals Wij jullie de eerste maal geschapen hebben. Toch beweerden jullie dat Wij voor jullie geen aangewezen tijdstip hadden vastgesteld."'),
(2189, 18, 49, 'En het boek wordt voorgelegd en dan zul jij de boosdoeners terug zien deinzen voor wat erin staat en zij zullen zeggen: "Wee ons, wat is er met dit boek dat niets kleins en niets groots onopgesomd laat?" En zij vinden dat wat zij deden aanwezig is. En jouw Heer doet niemand onrecht aan.'),
(2190, 18, 50, 'Toen Wij tot de engelen zeiden: "Buigt eerbiedig neer voor Adam", bogen zij zich eerbiedig neer, behalve Iblies die tot de djinn behoorde; hij zondigde dus tegen het bevel van zijn Heer. Zullen jullie dan hem en zijn nageslacht in plaats van mij als beschermers nemen, terwijl zij jullie tot vijand zijn. Dat is pas een slechte ruil voor de onrechtplegers. *'),
(2191, 18, 51, 'Ik liet hen niet getuige zijn van de schepping van de hemelen en de aarde en ook niet van de schepping van henzelf; Ik neem hen die tot dwaling brengen toch niet als steun.'),
(2192, 18, 52, 'En op de dag dat Hij zegt: "Roept Mijn [zogenaamd goddelijke] metgezellen van wie jullie het bestaan beweerden." Dan roepen zij hen aan, maar zij geven hun geen gehoor. En Wij maken tussen hen een afgrond.'),
(2193, 18, 53, 'En de boosdoeners zien het vuur en vermoeden al dat zij erin zullen vallen en zij merken dat er geen ontkomen aan is.'),
(2194, 18, 54, 'En Wij hebben in deze Koran voor de mensen toch allerlei voorbeelden uiteengezet, maar de mens is het meest van al twistziek.'),
(2195, 18, 55, 'En de mensen werden toen de leidraad tot hen kwam slechts gehinderd te geloven en hun Heer om vergeving te vragen, opdat de gebruikelijke behandeling van hen die er eertijds waren tot hen zou komen of de bestraffing in allerlei soorten tot hen zou komen.'),
(2196, 18, 56, 'En Wij zenden de gezondenen slechts als verkondigers van goed nieuws en als waarschuwers. En zij die ongelovig zijn redetwisten met behulp van onzin om daarmee de waarheid te weerleggen. En zij hebben met Mijn tekenen en dat waarmee zij gewaarschuwd werden de spot gedreven.'),
(2197, 18, 57, 'En wie is er zondiger dan hij die met de tekenen van zijn Heer vermaand is en er zich dan van afwendt en vergeet wat zijn handen tevoren deden? Wij hebben over hun harten hoezen gelegd zodat zij het niet begrijpen en in hun oren hardhorendheid. En als jij hen oproept tot de leidraad, dan zullen zij zich toch nooit in de goede richting laten leiden.'),
(2198, 18, 58, 'Maar jouw Heer is de vergevende die vol barmhartigheid is. Als Hij hun zou aanrekenen wat zij begaan hebben, dan zou Hij voor hen de bestraffing verhaasten. Toch ligt voor hen een aangewezen tijdstip vast waarvoor zij geen uitwijkplaats zullen vinden.'),
(2199, 18, 59, 'En dat zijn de steden die Wij vernietigd hebben toen zij onrecht pleegden. En Wij hebben voor hun vernietiging een aangewezen tijdstip vastgesteld.'),
(2200, 18, 60, 'En toen Moesa tot zijn knecht zei: "Ik zal niet ophouden tot ik de plaats bereik waar de beide zeeën samenkomen al zou ik tijden moeten doorgaan."'),
(2201, 18, 61, 'En toen zij de plaats waar beide samenkomen bereikt hadden, vergaten zij hun vis en die baande zich toen een weg de zee in waarbij hij verdween.'),
(2202, 18, 62, 'En toen zij verder gegaan waren zei hij tot zijn knecht: "Breng ons ons middagmaal, want door deze reis van ons hebben wij vermoeienis te verduren gehad."'),
(2203, 18, 63, 'Hij zei: "Heb je gezien? Toen wij ons naar de rots begaven vergat ik de vis. En niemand anders dan de satan heeft mij hem laten vergeten, zodat ik niet aan hem dacht en hij baande zich een weg de zee in op een verbazingwekkende manier."'),
(2204, 18, 64, 'Hij zei: "Dat was het wat wij zochten!" Toen draaiden zij om en keerden op hun schreden terug.'),
(2205, 18, 65, 'Zij vonden toen een van Onze dienaren die Wij barmhartigheid van Onze kant gegeven hadden en die Wij kennis die bij Ons vandaan kwam onderwezen hadden.'),
(2206, 18, 66, 'Moesa zei tot hem: "Mag ik jou volgen zodat jij mij onderwijst in wat jou aan redelijk inzicht onderwezen is?"'),
(2207, 18, 67, 'Hij zei: "Jij zult het met mij niet kunnen uithouden.'),
(2208, 18, 68, 'Hoe zul jij het kunnen uithouden bij iets wat jij met je kennis niet kunt omvatten?"'),
(2209, 18, 69, 'Hij zei: "Je zult vinden dat ik, zo God wil, volharden zal en ik zal jou in geen bevel ongehoorzaam zijn."'),
(2210, 18, 70, 'Hij zei: "Als jij mij dan volgt, vraag mij dan niets, zolang ik er zelf niet tot jou over spreek."'),
(2211, 18, 71, 'Zo gingen zij dan verder totdat hij, toen zij aan boord van het schip waren gegaan, er een gat in maakte. Hij zei: "Maak jij er een gat in om haar opvarenden te laten verdrinken? Daar heb je echt iets vreselijks begaan."'),
(2212, 18, 72, 'Hij zei: "Heb ik niet gezegd dat je het met mij niet zou kunnen uithouden?"'),
(2213, 18, 73, 'Hij zei: "Neem mij niet kwalijk dat ik het vergat en reken het mij niet te zwaar aan."'),
(2214, 18, 74, 'Zo gingen zij verder totdat zij een jonge man tegenkwamen. Die doodde hij toen. Hij zei: "Hoe kon jij een onschuldig mens doden en niet eens uit vergelding voor het leven van iemand anders? Daar heb je echt iets verwerpelijks begaan." *'),
(2215, 18, 75, 'Hij zei: "Heb ik niet gezegd dat je het met mij niet zou kunnen uithouden?"'),
(2216, 18, 76, 'Hij zei: "Als ik je hierna nog eens naar iets vraag, dan moetje mij niet verder met je mee laten gaan. Van mijn kant heb je al een verontschuldiging gekregen."'),
(2217, 18, 77, 'Zo gingen zij dan verder totdat zij, toen zij bij de mensen van de stad gekomen waren, hun om voedsel vroegen. Maar zij weigerden hun gastvrijheid te verlenen. Toen vonden zij daar een muur die dreigde in te storten, maar hij zette hem overeind. Hij zei: "Als je wilde, had je daarvoor loon kunnen krijgen."'),
(2218, 18, 78, 'Hij zei: "Dit is dan de scheiding tussen jou en mij. Ik zal je de uitleg meedelen van wat jij niet kon uithouden.'),
(2219, 18, 79, 'Wat het schip betreft, dat was van arme mensen die op zee werken en ik wenste het te beschadigen. Hun stond namelijk een koning te wachten die elk schip met geweld nam.'),
(2220, 18, 80, 'Wat de jonge man betreft, zijn ouders waren gelovige mensen en wij vreesden dat hij hen door onbeschaamdheid en ongeloof te zeer zou kwellen.'),
(2221, 18, 81, 'Wij wensten dus dat hun Heer hun iemand voor hem in de plaats zou geven die zuiverder en vriendelijker zou zijn dan hij.'),
(2222, 18, 82, 'En wat de muur betreft, die was van twee weesjongens in de stad en er was een schat onder die hun beiden toebehoorde en hun vader was een rechtschapen man geweest. Jouw Heer wenste dat zij volgroeid zouden zijn en hun schat tevoorschijn halen; het was barmhartigheid van jouw Heer. Ik deed het niet uit eigen beweging. Dat is de uitleg van wat jij niet kon uithouden."'),
(2223, 18, 83, 'En zij vragen jou naar de man met de twee horens. Zeg: "Ik zal jullie over hem een verslag voorlezen."'),
(2224, 18, 84, 'Wij hadden hem op de aarde macht gegeven en een toegangsweg tot alle dingen.'),
(2225, 18, 85, 'Toen volgde hij een toegangsweg'),
(2226, 18, 86, 'totdat hij, toen hij de plaats van de zonsondergang bereikte, vond dat zij onderging in een bron van modder en daarbij trof hij mensen aan. Wij zeiden: "Jij met de twee horens, of jij bestraft, of jij behandelt hen goed."'),
(2227, 18, 87, 'Hij zei: "Wat hem betreft die onrecht pleegt die zullen wij bestraffen, dan wordt hij tot zijn Heer teruggebracht en Hij zal hem met een kwalijke bestraffing straffen.'),
(2228, 18, 88, 'En wat hem betreft die gelooft en deugdelijk handelt, die zal als beloning het beste krijgen. En Wij zullen van Onze beschikking iets gemakkelijks tot hem zeggen."'),
(2229, 18, 89, 'Daarna volgde hij een toegangsweg,'),
(2230, 18, 90, 'totdat hij, toen hij de plaats van de zonsopgang bereikte, vond dat zij opging over mensen voor wie Wij geen bescherming ertegen gemaakt hadden.'),
(2231, 18, 91, 'Zo was het. En Wij omvatten met Onze kennis wat er bij hem was.'),
(2232, 18, 92, 'Daarna volgde hij een toegangsweg,'),
(2233, 18, 93, 'totdat hij, toen hij tussen de beide barrières aankwam, ernaast mensen aantrof die nauwelijks een woord begrepen.'),
(2234, 18, 94, 'Zij zeiden: "Jij met de twee horens, Jadjoedj en Madjoedj brengen verderf op de aarde. Zullen wij jou er dan een vergoeding voor toekennen dat jij tussen ons en hen een barrière maakt?"'),
(2235, 18, 95, 'Hij zei: "De macht die mijn Heer mij gegeven heeft is beter. Helpt mij maar krachtig, dan zal ik tussen jullie en hen een wal opwerpen.'),
(2236, 18, 96, 'Brengt mij brokken ijzer." En toen hij het dan tussen beide hellingen even hoog had opgestapeld zei hij: "Blaast!" En toen hij er dan een vuur van gemaakt had zei hij: "Brengt mij gesmolten koper om eroverheen te gieten."'),
(2237, 18, 97, 'En zij konden er niet overheen komen noch een bres erin slaan.'),
(2238, 18, 98, 'Hij zei: "Dit is een barmhartigheid van mijn Heer. Wanneer de toezegging van mijn Heer komt, dan maakt Hij hem met de grond gelijk. En de toezegging van mijn Heer is waar."'),
(2239, 18, 99, 'En Wij laten hen op die dag door elkaar heen krioelen. Dan wordt op de bazuin geblazen en verzamelen Wij hen bijeen.'),
(2240, 18, 100, 'En Wij confronteren op die dag de ongelovigen met de hel.'),
(2241, 18, 101, 'Van hen waren de ogen voor Mijn vermaning afgedekt en zij konden niet horen.'),
(2242, 18, 102, 'Of rekenen zij die ongelovig zijn erop dat zij Mijn dienaren in plaats van Mij als beschermers kunnen nemen? Wij hebben de hel voor de ongelovigen als gastverblijf klaargemaakt.'),
(2243, 18, 103, 'Zeg: "Zullen wij jullie meedelen wie door hun daden de grootste verliezers zijn,'),
(2244, 18, 104, 'van wie het streven in het tegenwoordige leven teloorgaat terwijl zij erop rekenen goed werk te doen?"'),
(2245, 18, 105, 'Dat zijn zij die geen geloof hechten aan de tekenen van hun Heer en aan de ontmoeting met Hem. Hun daden zullen dus vruchteloos zijn. Wij zullen hun op de opstandingsdag dan ook geen gewicht toekennen.'),
(2246, 18, 106, 'Dat is hun vergelding, de hel, omdat zij ongelovig waren en de spot dreven met Mijn tekenen en Mijn gezanten.'),
(2247, 18, 107, 'Maar zij die geloven en de deugdelijke daden doen, voor hen zijn er de tuinen van het paradijs als gastverblijf.'),
(2248, 18, 108, 'Zij zullen daarin altijd blijven zonder er ooit uit weg te willen gaan.'),
(2249, 18, 109, 'Zeg: "Als de zee inkt was voor de woorden van mijn Heer, dan zou de zee uitgeput raken voordat de woorden van mijn Heer uitgeput raakten, ook al brachten wij nog een keer zoveel."'),
(2250, 18, 110, 'Zeg: "Ik ben slechts een mens als jullie; aan mij wordt geopenbaard dat jullie god één god is. Wie dan hoopt op de ontmoeting met zijn Heer, die moet deugdelijk handelen en in de eredienst van zijn Heer niet een als metgezel [aan Hem] toevoegen."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'K[aaf] H[aa?] J[aa?] \'[Ain] S[aad].'),
(2252, 19, 2, 'De vermelding van de barmhartigheid van jouw Heer aan Zijn dienaar Zakarijja.'),
(2253, 19, 3, 'Toen hij tot zijn Heer in het verborgen riep,'),
(2254, 19, 4, 'zei hij: "Mijn Heer, mijn gebeente is zwak geworden in mij en mijn hoofd grijs van ouderdom. Bij het aanroepen van U, mijn Heer, ben ik nooit ongelukkig geweest.'),
(2255, 19, 5, 'Maar ik ben bang voor de verwanten die na mij komen, want mijn vrouw is onvruchtbaar. Schenk mij dan van Uw kant een naaste verwant,'),
(2256, 19, 6, 'die van mij zal erven en die van het geslacht van Ja\'koeb zal erven en maak, mijn Heer, dat hij aangenaam is."'),
(2257, 19, 7, '"O Zakarijja, Wij kondigen jou een jongen aan die de naam Jahja heeft, een naam die Wij nog niet eerder aan iemand gegeven hebben."'),
(2258, 19, 8, 'Deze zei: "Hoe kan ik nog een jongen krijgen terwijl mijn vrouw onvruchtbaar is en ik een hoge ouderdom heb bereikt?"'),
(2259, 19, 9, 'Hij zei: "Zo is het. Jouw Heer heeft gezegd: \'Het is voor Mij gemakkelijk. Ik heb jou vroeger immers ook geschapen toen jij nog niets was.?"'),
(2260, 19, 10, 'Hij zei: "Mijn Heer, geef mij een teken." Hij zei: "Jouw teken is dat jij drie nachten, hoewel je gezond en wel bent, toch niet tot de mensen zult spreken."'),
(2261, 19, 11, 'Toen ging hij het heiligdom uit naar zijn volk en gaf hun te kennen dat zij \'s ochtends en \'s avonds moesten lofprijzen.'),
(2262, 19, 12, '"O Jahja, houd het boek stevig vast." En Wij gaven hem de oordeelskracht al als kind'),
(2263, 19, 13, 'en mededogen van Onze kant en reinheid. Hij was godvrezend'),
(2264, 19, 14, 'en plichtsgetrouw jegens zijn ouders en hij was geen ongehoorzame geweldenaar.'),
(2265, 19, 15, 'En vrede zij met hem op de dag dat hij geboren werd, op de dag dat hij sterft en op de dag dat hij weer tot leven wordt opgewekt.'),
(2266, 19, 16, 'En vermeld in het boek Marjam. Toen zij zich van haar familie terugtrok naar een oostelijke plaats'),
(2267, 19, 17, 'en een afscherming tegen hen maakte. Toen zonden Wij Onze geest naar haar en hij deed zich aan haar voor als een goedgevormd mens.'),
(2268, 19, 18, 'Zij zei: "Ik zoek bij de Erbarmer bescherming tegen jou, als jij godvrezend bent."'),
(2269, 19, 19, 'Hij zei: "Maar ik ben de gezant van jouw Heer om jou een reine jongen te schenken."'),
(2270, 19, 20, 'Zij zei: "Hoe zou ik een jongen krijgen, terwijl geen mens mij aangeraakt heeft; en ik ben geen onkuise vrouw."'),
(2271, 19, 21, 'Hij zei: "Zo is het. Jouw Heer heeft gezegd: \'Het is voor Mij gemakkelijk. En het is opdat Wij hem tot een teken voor de mensen maken en uit barmhartigheid van Ons. En het is een beslissing die gevallen is.?" *'),
(2272, 19, 22, 'Dus werd zij zwanger van hem en trok zich met hem terug naar een afgelegen plaats.'),
(2273, 19, 23, 'Toen maakten de weeën dat zij naar de stam van de palm ging. Zij zei: "Ach was ik maar eerder gestorven en was ik maar volstrekt in vergetelheid geraakt."'),
(2274, 19, 24, 'Maar hij riep haar van onder haar vandaan: "Wees niet bedroefd, jouw Heer heeft onder jou een beekje geplaatst.'),
(2275, 19, 25, 'En schud de stam van de palm naar je toe dan zal zij verse rijpe dadels op je laten vallen.'),
(2276, 19, 26, 'Eet en drink dan en wees goedsmoeds en als jij iemand van de mensen ziet zeg dan: \'Ik heb aan de Erbarmer de gelofte gedaan mij te onthouden en dus zal ik vandaag met geen mens spreken.?"'),
(2277, 19, 27, 'Toen kwam zij met hem bij haar mensen, terwijl zij hem droeg. Zij zeiden: "O Marjam jij hebt echt iets ongehoords begaan.'),
(2278, 19, 28, 'Zuster van Haroen, jouw vader was geen slechte man en jouw moeder was geen onkuise vrouw!"'),
(2279, 19, 29, 'Maar zij wees naar hem. Zij zeiden: "Hoe kunnen wij spreken met iemand die nog een kind in de wieg is?"'),
(2280, 19, 30, 'Hij zei: "Ik ben Gods dienaar; Hij heeft mij het boek gegeven en mij tot profeet gemaakt.'),
(2281, 19, 31, 'En Hij heeft mij gezegend gemaakt waar ik ook ben en Hij heeft mij de salaat en de zakaat opgelegd zolang ik leef'),
(2282, 19, 32, 'en ook om plichtsgetrouw te zijn jegens mijn moeder en Hij heeft mij niet tot een ellendige geweldenaar gemaakt.'),
(2283, 19, 33, 'En vrede zij met mij op de dag dat ik geboren werd, op de dag dat ik sterf en op de dag dat ik weer tot leven word opgewekt."'),
(2284, 19, 34, 'Dat is Tsa, de zoon van Marjam, het woord van de waarheid waaraan zij twijfelen.'),
(2285, 19, 35, 'God is niet zo dat Hij zich een kind neemt. Geprezen zij Hij! Wanneer Hij iets beslist dan zegt Hij er slechts tegen: "Wees!" en het is.'),
(2286, 19, 36, '"God is mijn Heer en jullie Heer; dient Hem dus. Dit is een juiste weg."'),
(2287, 19, 37, 'Maar de partijen waren het onderling oneens. Wee dus hen die ongelovig zijn want zij zullen van een geweldige dag getuige zijn.'),
(2288, 19, 38, 'Wat zullen zij scherphorend en scherpziend zijn op de dag dat zij tot Ons komen! Maar de onrechtplegers verkeren vandaag in duidelijke dwaling.'),
(2289, 19, 39, 'En waarschuw hen voor de dag van wroeging wanneer de beslissing is gevallen, terwijl zij in onoplettendheid verkeren en niet geloven.'),
(2290, 19, 40, 'Wij zullen de aarde beërven en wie erop zijn en tot Ons zullen zij teruggebracht worden.'),
(2291, 19, 41, 'En vermeld in het boek Ibrahiem. Hij was een oprecht mens en een profeet.'),
(2292, 19, 42, 'Toen hij tot zijn vader zei: "O mijn vader, waarom dien jij wat niet kan horen, niet kan zien en wat jou niets baat?'),
(2293, 19, 43, 'O mijn vader, tot mij is kennis gekomen die tot jou niet gekomen is. Volg mij dus, dan zal ik jou op een effen weg leiden.'),
(2294, 19, 44, 'O mijn vader, dien de satan niet. De satan is weerspannig tegen de Erbarmer.'),
(2295, 19, 45, 'O mijn vader, ik ben bang dat een straf van de Erbarmer jou zal treffen en dat jij dan een volgeling van de satan wordt."'),
(2296, 19, 46, 'Deze zei: "Wil jij je van mijn goden afkeren, Ibrahiem? Als jij niet ophoudt, zal ik je stenigen; ga een lange tijd bij mij weg."'),
(2297, 19, 47, 'Hij zei: "Vrede zij met je. Ik zal mijn Heer voor jou om vergeving vragen; Hij is voor mij welwillend.'),
(2298, 19, 48, 'Ik ga mij van jullie afscheiden en ook van wat jullie in plaats van God aanroepen. Ik roep mijn Heer aan, misschien dat ik bij het aanroepen van mijn Heer niet ongelukkig ben."'),
(2299, 19, 49, 'En toen hij zich van hen afscheidde en van wat zij in plaats van God dienden schonken Wij hem Ishaak en Ja\'koeb en allen maakten Wij tot profeten.'),
(2300, 19, 50, 'En Wij schonken hun iets van Onze barmhartigheid en Wij verschaften hun een betrouwbare, hoogstaande reputatie.'),
(2301, 19, 51, 'En vermeld in het boek Moesa. Hij was uitverkoren en hij was een gezant en een profeet.'),
(2302, 19, 52, 'En Wij riepen hem van de rechterkant van de berg en brachten hem dichterbij voor een vertrouwelijk gesprek.'),
(2303, 19, 53, 'En Wij schonken hem uit Onze barmhartigheid zijn broeder Haroen als profeet.'),
(2304, 19, 54, 'En vermeld in het boek Isma\'iel. Hij hield zich aan zijn toezegging en hij was een gezant en een profeet.'),
(2305, 19, 55, 'En hij legde zijn mensen de salaat en de zakaat op en hij had bij zijn Heer welgevallen gevonden.'),
(2306, 19, 56, 'En vermeld in het boek Idries. Hij was een oprecht mens en een profeet.'),
(2307, 19, 57, 'En Wij hebben hem naar een hoge plaats verheven.'),
(2308, 19, 58, 'Zij zijn het aan wie God onder de profeten genade geschonken heeft, uit de nakomelingen van Adam, uit hen die Wij met Noeh gedragen hebben, uit de nakomelingen van Ibrahiem en Isra?iel en uit hen die Wij op het goede pad gebracht en uitgekozen hebben. Wanneer de tekenen van de Erbarmer aan hen worden voorgelezen vallen zij eerbiedig buigend neer en huilen. -- *'),
(2309, 19, 59, 'Toen volgden hen daarna opvolgers op die de salaat veronachtzaamden en de begeerten najoegen; zij gaan dus verderf tegemoet.'),
(2310, 19, 60, 'Behalve wie berouw toont, gelooft en deugdelijk handelt. Zij zijn het dus die de tuin binnengaan en hun wordt geen enkel onrecht aangedaan.'),
(2311, 19, 61, 'De tuinen van \'Adn die de Erbarmer aan Zijn dienaren in het onzichtbare heeft toegezegd; zijn toezegging komt uit.'),
(2312, 19, 62, 'Zij horen daarin geen onzin, maar alleen: Vrede! En voor hen is er \'s ochtends en \'s avonds hun levensonderhoud.'),
(2313, 19, 63, 'Dat is de tuin die Wij hun van Onze dienaren die godvrezend zijn laten beërven.'),
(2314, 19, 64, '"Wij laten ons alleen maar door de beschikking van jouw Heer neerzenden. Van Hem is wat vóór ons is en wat achter ons is en wat daartussen is. En jouw Heer is niet vergeetachtig.'),
(2315, 19, 65, 'De Heer van de hemelen en de aarde en wat er tussen beide is. Dient Hem dus en volhardt in Zijn eredienst. Ken jij van Hem een naamgenoot?"'),
(2316, 19, 66, 'De mens zegt: "Als ik sterf, zal ik dan levend tevoorschijn gebracht worden?"'),
(2317, 19, 67, 'Gedenkt de mens dan niet dat Wij hem vroeger geschapen hebben toen hij nog niets was?'),
(2318, 19, 68, 'Maar, bij jouw Heer, Wij zullen hen en de satans verzamelen en dan zullen Wij hen rondom de hel op de knieën voorgeleiden.'),
(2319, 19, 69, 'Dan zullen Wij bij iedere groepering hen eruit halen die het hevigst tegen de Erbarmer tekeergingen.'),
(2320, 19, 70, 'Dan kennen Wij hen die het meest verdienen te braden het best.'),
(2321, 19, 71, 'Er is niemand onder jullie die er niet naar zal afdalen. En het is voor jouw Heer een besluit dat gevallen is.'),
(2322, 19, 72, 'Dan redden Wij hen die godvrezend zijn en laten de onrechtplegers daarin op de knieën achter.'),
(2323, 19, 73, 'En wanneer aan hen Onze tekenen als duidelijke bewijzen worden voorgelezen zeggen zij die ongelovig zijn tot hen die geloven: "Welke van de beide groepen heeft een betere positie en verkeert in beter gezelschap?"'),
(2324, 19, 74, 'Hoeveel generaties hebben Wij reeds vóór hun tijd vernietigd die betere voorzieningen hadden en die een betere aanblik boden?'),
(2325, 19, 75, 'Zeg: "Als iemand in dwaling verkeert, dan moge de Erbarmer het voor hem nog verlengen. En wanneer zij ten slotte zien wat hun is aangezegd -- of het nu de bestraffing is of het uur? dan zullen zij weten wie de slechtste positie hebben en de zwakste troepenmacht."'),
(2326, 19, 76, 'En God brengt hen die zich op het goede pad hebben laten brengen nog beter op het goede pad. En de deugdelijke daden die duurzaam zijn hebben bij jouw Heer een betere beloning en verzekeren een betere terugkeer.'),
(2327, 19, 77, 'Heb jij hem gezien die geen geloof hecht aan Onze tekenen en die zegt: "Aan mij zullen zeker bezit en kinderen gegeven worden"?'),
(2328, 19, 78, 'Heeft hij dan het onzichtbare in ogenschouw genomen of heeft hij met God een verbond gesloten?'),
(2329, 19, 79, 'Welnee, Wij zullen opschrijven wat hij zegt en de bestraffing voor hem nog verlengen.'),
(2330, 19, 80, 'En Wij zullen dat wat hij zegt van hem erven en hij zal alleen tot ons komen.'),
(2331, 19, 81, 'En zij hebben zich in plaats van God andere goden genomen om door hen macht te krijgen.'),
(2332, 19, 82, 'Welnee, zij zullen voor hun eredienst niet erkentelijk zijn en tegenstanders voor hen zijn.'),
(2333, 19, 83, 'Heb jij niet gezien dat Wij de satans naar de ongelovigen gezonden hebben om hen op te stoken?'),
(2334, 19, 84, 'Wees niet te haastig tegen hen; Wij zijn voor hen al aan het aftellen.'),
(2335, 19, 85, 'Op de dag dat Wij de godvrezenden als een afvaardiging bij de Erbarmer zullen verzamelen'),
(2336, 19, 86, 'en de boosdoeners naar de hel drijven als een kudde naar de drinkplaats,'),
(2337, 19, 87, 'beschikken alleen zij over voorspraak die een verbond met de Erbarmer hebben gesloten.'),
(2338, 19, 88, 'En zij zeggen: "De Erbarmer heeft zich een kind genomen."'),
(2339, 19, 89, 'Jullie hebben echt iets afgrijselijks begaan.'),
(2340, 19, 90, 'Bijna zou de hemel ervan barsten, de aarde opensplijten en het gebergte ineenstorten,'),
(2341, 19, 91, 'dat zij aan de Erbarmer een kind toekennen.'),
(2342, 19, 92, 'Het past niet bij de Erbarmer dat Hij zich een kind neemt.'),
(2343, 19, 93, 'Ieder die in de hemelen en de aarde is komt slechts als dienaar tot de Erbarmer.'),
(2344, 19, 94, 'Hij heeft hen opgesomd en nauwkeurig geteld.'),
(2345, 19, 95, 'En een ieder van hen zal op de opstandingsdag alleen tot Hem komen.'),
(2346, 19, 96, 'Zij die geloven en de deugdelijke daden doen, aan hen zal de Erbarmer liefde doen toekomen.'),
(2347, 19, 97, 'Wij hebben hem dus in jouw taal gemakkelijk gemaakt, opdat jij het goede nieuws ermee zou verkondigen aan de godvrezenden en twistzieke mensen ermee zou waarschuwen.'),
(2348, 19, 98, 'En hoeveel generaties hebben Wij voor hun tijd reeds vernietigd! Word jij van een van hen nog iets gewaar of hoor jij van hen nog gefluister?');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'T[aa?] H[aa?].'),
(2350, 20, 2, 'Wij hebben de Koran niet tot jou neergezonden opdat jij ongelukkig zou zijn,'),
(2351, 20, 3, 'maar slechts als een vermaning voor wie [God] vreest,'),
(2352, 20, 4, 'als een neerzending van Hem die de aarde en de hoge hemelen geschapen heeft,'),
(2353, 20, 5, 'de Erbarmer; Hij heeft zich op de troon gevestigd.'),
(2354, 20, 6, 'Hem behoort wat er in de hemelen, wat er op de aarde, wat er tussen beide en wat er onder de grond is.'),
(2355, 20, 7, 'En al spreek jij met luide stem, Hij weet wat geheim is en wat nog verborgener is.'),
(2356, 20, 8, 'God, er is geen god dan Hij, Hem komen de mooiste namen toe.'),
(2357, 20, 9, 'Is het verhaal van Moesa tot jou gekomen?'),
(2358, 20, 10, 'Toen hij een vuur zag en tot zijn mensen zei: "Blijft staan, ik heb een vuur waargenomen, misschien zal ik jullie er een fakkel van brengen of bij het vuur een aanduiding van de goede richting vinden."'),
(2359, 20, 11, 'Toen hij er kwam werd hem toegeroepen: O Moesa,'),
(2360, 20, 12, 'Ik ben het, jouw Heer. Trek dus jouw schoenen uit. Jij bent in de geheiligde vallei Toewa.'),
(2361, 20, 13, 'En Ik heb jou uitgekozen; luister dus naar wat geopenbaard zal worden.'),
(2362, 20, 14, 'Ik ben God, er is geen god dan Ik. Dien Mij dus en verricht de salaat om Mij te gedenken.'),
(2363, 20, 15, 'Het uur komt -- Ik houd het nauwelijks nog verborgen -- opdat aan elke ziel vergolden wordt wat zij nastreeft.'),
(2364, 20, 16, 'Laat wie er niet aan gelooft en zijn geneigdheid volgt jou er niet van afhouden, zodat jij ten onder gaat.'),
(2365, 20, 17, 'En wat is dat daar in je rechterhand?"'),
(2366, 20, 18, 'Hij zei: "Dat is mijn staf waarop ik leun en waarmee ik voor mijn schapen bladeren afsla en ik heb er ook nog andere toepassingen voor."'),
(2367, 20, 19, 'Hij zei: "Werp hem neer, Moesa."'),
(2368, 20, 20, 'En hij wierp hem neer en het was opeens een slang die voortbewoog.'),
(2369, 20, 21, 'Hij zei: "Pak hem en wees niet bang; Wij laten hem weer net zo worden als hij eerst was.'),
(2370, 20, 22, 'En houd je hand onder je arm dan zal zij wit, maar zonder iets slechts, tevoorschijn komen als nog een teken'),
(2371, 20, 23, 'om jou iets van Onze grootse tekenen te laten zien.'),
(2372, 20, 24, 'Ga heen naar Fir\'aun; hij is onbeschaamd."'),
(2373, 20, 25, 'Hij zei: "Mijn Heer, stel bij mij mijn hart open,'),
(2374, 20, 26, 'vergemakkelijk mijn taak voor mij'),
(2375, 20, 27, 'en maak een knoop in mijn tong los,'),
(2376, 20, 28, 'zodat zij begrijpen wat ik zeg.'),
(2377, 20, 29, 'En stel voor mij een medewerker aan uit mijn familie,'),
(2378, 20, 30, 'Haroen, mijn broer.'),
(2379, 20, 31, 'Geef mij door hem meer kracht'),
(2380, 20, 32, 'en laat hem in mijn taak delen,'),
(2381, 20, 33, 'opdat wij U veelvuldig prijzen'),
(2382, 20, 34, 'en U veelvuldig gedenken.'),
(2383, 20, 35, 'U doorziet ons!"'),
(2384, 20, 36, 'Hij zei: "Jouw verzoek is ingewilligd, Moesa!'),
(2385, 20, 37, 'En Wij hebben jou ook al een andere keer een gunst bewezen,'),
(2386, 20, 38, 'toen Wij aan jouw moeder openbaarden wat geopenbaard is:'),
(2387, 20, 39, '\'Werp hem in de kist en werp hem in de zee, dan zal de zee hem op de kust werpen, zodat een vijand van Mij en een vijand van hem hem zal opnemen.? En Ik heb over jou liefde van Mij uitgestort en jij moest onder Mijn ogen grootgebracht worden.'),
(2388, 20, 40, 'Toen jouw zuster heenging en zei: \'Zal ik jullie iemand aanwijzen die voor hem zal zorgen?? Zo brachten Wij jou terug naar jouw moeder opdat jij goedsmoeds en niet bedroefd zou zijn. En jij hebt een mens gedood, maar Wij hebben jou uit de nood gered en jou aan verzoeking blootgesteld. Zo bleef jij jaren bij de mensen van Madjan. Toen kwam jij, Moesa, [hierheen] als beschikt.'),
(2389, 20, 41, 'En Ik heb jou voor Mijzelf uitgekozen.'),
(2390, 20, 42, 'Ga heen, jij en jouw broeder, met Mijn tekenen en wordt niet moe Mij te gedenken.'),
(2391, 20, 43, 'Gaat heen naar Fir\'aun; hij is onbeschaamd.'),
(2392, 20, 44, 'En spreekt zachtmoedig tot hem; misschien laat hij zich vermanen of vreest hij."'),
(2393, 20, 45, 'Zij zeiden: "Onze Heer, wij zijn bang dat hij zich aan ons vergrijpt of dat hij onbeschaamd zal zijn."'),
(2394, 20, 46, 'Hij zei: "Weest niet bang, Ik ben met jullie; Ik hoor en zie!'),
(2395, 20, 47, 'Gaat dus naar hem toe en zegt: \'Wij zijn gezanten van jouw Heer. Zend dan de Israëlieten met ons weg en bestraf hen niet. Wij zijn tot jou gekomen met een teken van jouw Heer. En vrede zij met wie de leidraad volgt.'),
(2396, 20, 48, 'Aan ons is immers geopenbaard dat de bestraffing komt over wie loochent en zich afkeert."'),
(2397, 20, 49, 'Hij zei: "Wie is dan jullie Heer, Moesa?"'),
(2398, 20, 50, 'Deze zei: "Onze Heer is het die aan alles zijn aard heeft gegeven en dan de goede richting heeft gewezen."'),
(2399, 20, 51, 'Hij zei: "Hoe is het gesteld met de eerdere generaties?"'),
(2400, 20, 52, 'Hij zei: "De kennis daarover is bij mijn Heer, in een boek. Mijn Heer dwaalt niet en vergeet niet."'),
(2401, 20, 53, 'Hij die voor jullie de aarde tot een wiegenbed gemaakt heeft en die voor jullie daarop wegen heeft getrokken en die uit de hemel water heeft laten neerdalen waarmee Wij dan velerlei soorten van plantengroei hebben voortgebracht.'),
(2402, 20, 54, 'Eet en weidt jullie vee. Daarin zijn tekenen voor de schranderen. *'),
(2403, 20, 55, 'Uit haar [nml. de aarde] hebben Wij jullie geschapen en in haar zullen Wij jullie terug laten keren en uit haar zullen Wij jullie een tweede maal tevoorschijn brengen.'),
(2404, 20, 56, 'En Wij hebben hem al Onze tekenen laten zien, maar hij loochende en weigerde.'),
(2405, 20, 57, 'Hij zei: "Ben jij tot ons gekomen om ons door jouw toverij uit ons land te verdrijven, Moesa?'),
(2406, 20, 58, 'Dan zullen wij wel met overeenkomstige toverij komen. Maak maar een afspraak tussen ons en jou, waaraan wij ons evenals jij moeten houden, op een effen plaats."'),
(2407, 20, 59, 'Deze zei: "De afspraak met jullie is op de feestdag en dat de mensen op de voormiddag verzameld worden."'),
(2408, 20, 60, 'Maar Fir\'aun keerde zich af om zijn plan te beramen en toen kwam hij terug.'),
(2409, 20, 61, 'Moesa zei tot hen: "Wee jullie, verzint over God geen bedrog, want dan verdelgt Hij jullie door een bestraffing. Wie verzinsels vertelt wordt teleurgesteld."'),
(2410, 20, 62, 'Toen twistten zij met elkaar over hun geval en hielden een vertrouwelijk gesprek.'),
(2411, 20, 63, 'Zij zeiden: "Deze twee zijn tovenaars. Zij willen jullie door hun toverij uit jullie land verdrijven en jullie voorbeeldige opvattingen afschaffen.'),
(2412, 20, 64, 'Beraamt dan jullie plan en komt dan in een rij. Heden zal het hem welgaan die de overhand krijgt."'),
(2413, 20, 65, 'Zij zeiden: "O Moesa, of jij werpt of wij zijn het die het eerst zullen werpen."'),
(2414, 20, 66, 'Hij zei: "Werpt maar." En plotseling werd hem door hun toverij de indruk gegeven dat hun touwen en staven voortbewogen.'),
(2415, 20, 67, 'Toen werd Moesa in zijn binnenste door vrees bevangen.'),
(2416, 20, 68, 'Wij zeiden: "Weest niet bang, jij houdt de overhand.'),
(2417, 20, 69, 'Werp wat in je rechterhand is, dan zal het wat zij gemaakt hebben verslinden. Wat zij gemaakt hebben is slechts een tovenaarslist en de tovenaar zal het niet welgaan waar hij ook komt."'),
(2418, 20, 70, 'En de tovenaars werden neergeworpen om zich eerbiedig neer te buigen en zij zeiden: "Wij geloven in de Heer van Haroen en Moesa."'),
(2419, 20, 71, 'Hij zei: "Jullie geloven aan hem voordat ik jullie toestemming gegeven heb. Hij is zeker jullie meester die jullie heeft leren toveren. Ik zal jullie de handen en de voeten aan tegenovergestelde kanten afhouwen en ik zal jullie aan palmstammen kruisigen. Dan zullen jullie weten wie van ons strenger in het bestraffen en onvergankelijker is."'),
(2420, 20, 72, 'Zij zeiden: "Wij verkiezen jou niet boven de duidelijke tekenen die tot ons gekomen zijn en boven Hem die ons geschapen heeft. Beslis dus maar wat je wilt; jij beslist slechts over het tegenwoordige leven.'),
(2421, 20, 73, 'Wij geloven in onze Heer opdat Hij ons onze fouten vergeven zal en de toverij waartoe jij ons gedwongen hebt. God is beter en onvergankelijker."'),
(2422, 20, 74, 'Hij die tot zijn Heer komt als boosdoener, voor hem is de hel waarin hij niet zal sterven en niet zal leven.'),
(2423, 20, 75, 'Maar wie tot Hem komen als gelovigen die de deugdelijke daden gedaan hebben, zij zijn het voor wie de hoogste rangen zijn:'),
(2424, 20, 76, 'de tuinen van \'Adn waar de rivieren onderdoor stromen; zij zullen altijd daarin blijven. Dat is de beloning voor wie gelouterd zijn.'),
(2425, 20, 77, 'En Wij hebben aan Moesa geopenbaard: "Vertrek \'s nachts met Mijn dienaren en baan voor hen een droge weg in de zee zonder bang te zijn ingehaald te worden en zonder te vrezen."'),
(2426, 20, 78, 'Toen volgde Fir\'aun hen met zijn troepen, maar zij werden door de zee verzwolgen.'),
(2427, 20, 79, 'Fir\'aun bracht zijn volk tot dwaling en wees hun niet de goede richting.'),
(2428, 20, 80, 'O Israëlieten, Wij hebben jullie van jullie vijand gered en Wij spraken met jullie aan de rechterkant van de berg af en Wij zonden het manna en de kwartels tot jullie neer.'),
(2429, 20, 81, 'Eet van de goede dingen waarmee Wij in jullie levensonderhoud voorzien, maar overdrijft daarbij niet zodat Mijn toorn over jullie losbarst; over wie Mijn toorn losbarst, die komt ten val.'),
(2430, 20, 82, 'Maar Ik ben een vergever voor wie berouw toont, gelooft en deugdelijk handelt en zich dan op het goede pad laat brengen.'),
(2431, 20, 83, '"En wat brengt jou zo haastig bij je volk vandaan, Moesa?"'),
(2432, 20, 84, 'Hij zei: "Zij zijn het die achter mij aan zijn en ik heb mij naar U gehaast, mijn Heer, opdat U tevreden zou zijn."'),
(2433, 20, 85, 'Hij zei: "Wij hebben jouw volk in verzoeking gebracht nadat jij weggegaan was en de Samiriet heeft hen tot dwaling gebracht."'),
(2434, 20, 86, 'Toen keerde Moesa toornig en vol spijt tot zijn volk terug. Hij zei: "Mijn volk! Heeft jullie Heer jullie niet een goede toezegging gedaan? Duurde de tijd jullie te lang of wensten jullie dat er toorn van jullie Heer over jullie los zou barsten, zodat jullie de afspraak met mij niet nakwamen?"'),
(2435, 20, 87, 'Zij zeiden: "Wij zijn niet uit eigen beweging de afspraak met jou niet nagekomen, maar wij werden overladen met hele ladingen sieraden van de mensen. Die hebben wij toen [in het vuur] gegooid en de Samiriet deed dat ook.'),
(2436, 20, 88, 'Hij bracht toen voor hen een kalf tevoorschijn, als een lichaam met geloei. En zij zeiden: "Dit is jullie god en de god van Moesa, maar hij was het vergeten."'),
(2437, 20, 89, 'Zien zij dan niet dat het niets tot hen terugzegt en dat het voor hen niets schadelijks en niets nuttigs kan uitrichten?'),
(2438, 20, 90, 'En Haroen had al van tevoren tot hen gezegd: "O mijn volk! Jullie worden daarmee in verzoeking gebracht. Jullie Heer is toch de Erbarmer! Volgt mij dus en gehoorzaamt mijn bevel."'),
(2439, 20, 91, 'Zij zeiden: "Wij zullen niet ophouden het eer te bewijzen zolang Moesa niet tot ons terugkomt."'),
(2440, 20, 92, 'Hij zei: "O Haroen, wat weerhield jou toen jij zag dat zij dwaalden,'),
(2441, 20, 93, 'dat jij mij niet gevolgd bent? Was jij ongehoorzaam aan mijn bevel?"'),
(2442, 20, 94, 'Hij zei: "Zoon van mijn moeder, grijp mij niet bij mijn baard en bij mijn hoofd. Ik vreesde dat jij zou zeggen: \'Jij hebt de Israëlieten opgesplitst en je niet gehouden aan wat ik zei.?"'),
(2443, 20, 95, 'Hij zei: "En hoe is het met jou, o Samiriet?"'),
(2444, 20, 96, 'Hij zei: "Ik had inzicht in iets waarin zij dat niet hadden en ik nam een handvol van het spoor van de gezant en heb het [over het kalf] geworpen. Dat had ik mijzelf zo wijsgemaakt."'),
(2445, 20, 97, 'Hij zei: "Ga weg, jouw lot in het leven zal zijn dat je moet zeggen: \'Niet aanraken!? En voor jou is er een afspraak waaraan jij je niet kunt onttrekken. En kijk dan naar jouw god, die jij eer bleef bewijzen. Wij zullen hem zeker verbranden en dan in de zee verstrooien.'),
(2446, 20, 98, 'Jullie god is God alleen, buiten wie er geen andere god is. Hij omvat alles met Zijn kennis."'),
(2447, 20, 99, 'Zo vertellen Wij jou berichten over wat vroeger gebeurde en Wij hebben tot jou van Onze kant een vermaning laten komen.'),
(2448, 20, 100, 'Wie zich daarvan afwenden, die zullen op de opstandingsdag een last te dragen hebben;'),
(2449, 20, 101, 'zij zullen er altijd in blijven en op de opstandingsdag zal het voor hen slecht te dragen zijn.'),
(2450, 20, 102, 'Op de dag dat op de bazuin geblazen wordt! Wij zullen op die dag de boosdoeners, verblind en verschrikt als zij zijn, verzamelen,'),
(2451, 20, 103, 'terwijl zij onder elkaar fluisteren: "Het heeft voor jullie slechts tien [dagen] geduurd."'),
(2452, 20, 104, 'Wij weten het best wat zij zeggen, wanneer de voorbeeldigste van hen in levenswandel zegt: "Het heeft voor jullie slechts een dag geduurd."'),
(2453, 20, 105, 'En zij vragen jou naar de bergen. Zeg: "Mijn Heer zal ze geheel verstrooien'),
(2454, 20, 106, 'en ze als een kale vlakte achterlaten,'),
(2455, 20, 107, 'waarin je geen kronkels en geen hobbels ziet."'),
(2456, 20, 108, 'Op die dag zullen zij de oproeper volgen tot wie er geen kronkelweg is en de stemmen zullen deemoedig tot de Erbarmer opklinken en je zult slechts geschuifel horen.'),
(2457, 20, 109, 'Op die dag heeft voorspraak alleen maar nut voor hem aan wie de Erbarmer het heeft toegestaan en wiens woorden Hem welgevallig zijn.'),
(2458, 20, 110, 'Hij weet wat vóór hen is en wat achter hen is terwijl zij Hem met hun kennis niet bevatten. *'),
(2459, 20, 111, 'En de gezichten zwichten voor de Levende, de Standvastige en teleurgesteld wordt hij wiens last onrecht is.'),
(2460, 20, 112, 'Maar wie deugdelijke daden doet, terwijl hij gelovig is, die zal voor onrecht niet bang zijn en ook niet dat hem tekort gedaan wordt.'),
(2461, 20, 113, 'En zo hebben Wij het neergezonden als een Arabische Koran en Wij hebben er uiteenzettingen van dreigementen in gegeven; misschien zullen zij godvrezend worden of brengt hij voor hen een nieuwe vermaning.'),
(2462, 20, 114, 'Hoogverheven is God, de ware koning. En haast je niet met de Koran voordat de openbaring ervan aan jou geheel is geschied en zeg: "Mijn Heer, geef mij meer kennis."'),
(2463, 20, 115, 'En Wij hadden vroeger aan Adam opdracht gegeven maar hij vergat het; Wij vonden bij hem geen vastbeslotenheid.'),
(2464, 20, 116, 'Toen Wij tot de engelen zeiden: "Buigt eerbiedig neer voor Adam", bogen zij eerbiedig neer, behalve Iblies; hij weigerde.'),
(2465, 20, 117, 'Wij zeiden dus tot Adam: "Dit is een vijand van jou en je echtgenote; laat hij jullie dus niet uit de tuin verdrijven want dan zul je ongelukkig zijn.'),
(2466, 20, 118, 'Het is jou gegund dat jij er geen honger hebt noch naakt bent'),
(2467, 20, 119, 'en dat jij er geen dorst hebt noch onder de hitte lijdt."'),
(2468, 20, 120, 'Maar de satan fluisterde hem in en zei: "O Adam zal ik jou de boom van het eeuwige leven wijzen en een heerschappij die niet vergaat?"'),
(2469, 20, 121, 'Toen aten zij er beiden van en hun schaamte werd voor hen zichtbaar en zij begonnen zich te bedekken met aaneengehechte bladeren uit de tuin. Adam was ongehoorzaam aan zijn Heer en misleid.'),
(2470, 20, 122, 'Daarna verkoos zijn Heer hem, wendde zich genadig tot hem en bracht hem op het goede pad.'),
(2471, 20, 123, 'Hij zei: "Daalt beiden er tezamen uit af -- elkaar tot vijand. Als er dan van Mij een leidraad komt, dan zal wie Mijn leidraad navolgt niet dwalen en niet ongelukkig zijn.'),
(2472, 20, 124, 'Maar wie zich van Mijn vermaning afwendt die zal een benauwd leven leiden en Wij zullen hem op de opstandingsdag blind ter verzameling opbrengen.'),
(2473, 20, 125, 'Hij zal zeggen: "Mijn Heer, waarom hebt U mij blind ter verzameling opgebracht, terwijl ik toch kon zien?"'),
(2474, 20, 126, 'Hij zegt: "Zo is het, Onze tekenen zijn tot jou gekomen, maar jij hebt ze vergeten, evenzo word jij vandaag dus vergeten."'),
(2475, 20, 127, 'Zo zullen Wij ook aan hem die onmatig is en die niet in de tekenen van zijn Heer gelooft vergelden. De bestraffing van het hiernamaals is strenger en onvergankelijker.'),
(2476, 20, 128, 'Is het hun dan niet duidelijk hoeveel generaties van wie in de woningen wandelen Wij al vóór hun tijd vernietigd hebben? Daarin zijn tekenen voor de schranderen.'),
(2477, 20, 129, 'En als er al niet eerder een woord van jouw Heer gekomen was en een vastgestelde termijn, dan was het onafwendbaar geweest.'),
(2478, 20, 130, 'Verdraag wat zij zeggen dus geduldig en prijs de lof van jouw Heer voor zonsopgang en voor haar ondergang en lofprijst gedurende de nacht en op de uiteinden van de dag; misschien ben jij dan tevreden.'),
(2479, 20, 131, 'En kijk niet met uitpuilende ogen naar wat Wij sommigen van hen als vruchtgebruik gegeven hebben, de schittering van het tegenwoordige leven, om hen daarmee in verzoeking te brengen. En het levensonderhoud van jouw Heer is beter en onvergankelijker.'),
(2480, 20, 132, 'En leg jouw mensen de salaat op. Volhard jij er ook in. Wij vragen van jou geen levensonderhoud. Wij zijn het die jou levensonderhoud geven en het [goede] uiteinde komt de godvrezendheid toe.'),
(2481, 20, 133, 'En zij zeiden: "Had hij ons geen teken van zijn Heer kunnen brengen?" Is tot hen dan niet als duidelijk bewijs gekomen wat er in de eerdere bladen staat?'),
(2482, 20, 134, 'En als Wij hen vernietigd hadden door een bestraffing vóór zijn komst, dan hadden zij gezegd: "Onze Heer, had U tot ons niet een gezant kunnen zenden, zodat wij Uw tekenen hadden kunnen volgen voordat wij vernederd en te schande gemaakt werden?"'),
(2483, 20, 135, 'Zeg: "Iedereen wacht af; wacht dus maar af. Dan zullen jullie weten wie de mensen van de effen weg zijn en wie het goede pad volgen."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'Voor de mensen is hun afrekening nabijgekomen terwijl zij zich in onoplettendheid afwendden.'),
(2485, 21, 2, 'Geen nieuwe vermaning komt er tot hen van hun Heer of zij luisteren ernaar terwijl zij schertsen'),
(2486, 21, 3, 'en terwijl hun harten verstrooiing zoeken. En zij die onrecht plegen zeggen in hun vertrouwelijke gesprekken: "Is deze iets anders dan een mens, zoals jullie? Zullen jullie je dan met toverij inlaten hoewel jullie het doorzien?"'),
(2487, 21, 4, 'Hij [Mohammed] zegt: "Mijn Heer weet wat er in de hemel en op de aarde gezegd wordt; Hij is de horende, de wetende."'),
(2488, 21, 5, 'Maar nee, zij zeggen: "Een wirwar van dromen. Hij heeft ze zeker verzonnen. Hij is zeker een dichter. Laat hij maar met een teken tot ons komen, zoals dat gezonden is aan hen die er eertijds waren."'),
(2489, 21, 6, 'Geen stad die Wij voor hun tijd vernietigd hebben heeft geloofd. Zullen zij dan geloven?'),
(2490, 21, 7, 'En Wij hebben vóór jouw tijd slechts mannen uitgezonden aan wie Wij een openbaring gegeven hadden -- vraagt de mensen van de vermaning maar, als jullie het niet weten.'),
(2491, 21, 8, 'En Wij hebben hen niet als lichamen gemaakt die geen voedsel eten en zij bleven ook niet altijd bestaan.'),
(2492, 21, 9, 'Toen hebben Wij voor hen de toezegging waargemaakt en hen en wie Wij wilden gered, maar de onmatigen vernietigd.'),
(2493, 21, 10, 'Wij hebben naar hen een boek neergezonden waarin de vermaning voor jullie staat. Hebben jullie dan geen verstand?'),
(2494, 21, 11, 'En hoeveel steden die onrecht pleegden hebben Wij al niet vernietigd waarna Wij een ander volk lieten ontstaan!'),
(2495, 21, 12, 'Wanneer zij dan Ons geweld voelden renden zij eruit weg.'),
(2496, 21, 13, '"Rent niet weg, maar keert terug naar het aan jullie verleende luxeleven en jullie woningen; misschien zullen jullie je moeten verantwoorden."'),
(2497, 21, 14, 'Zij zeiden: "Wee ons, wij hebben echt onrecht gepleegd."'),
(2498, 21, 15, 'Maar dat bleef hun geroep totdat Wij hen als een stoppelveld maakten, als uitgeblusten.'),
(2499, 21, 16, 'Wij hebben de hemel en de aarde en wat er tussen beide is niet als een spel geschapen.'),
(2500, 21, 17, 'Als Wij Ons iets voor tijdverdrijf hadden willen verschaffen dan hadden Wij het bij Ons vandaan genomen, als Wij het wilden doen.'),
(2501, 21, 18, 'Integendeel, Wij treffen de onzin met de waarheid waarmee hij dan verbrijzeld wordt en zo komt er een eind aan. En wee jullie voor wat jullie beschrijven.'),
(2502, 21, 19, 'Hem behoort wat er in de hemelen en op de aarde is. En wie er bij Hem zijn, zijn niet te trots om Hem te dienen noch worden zij er moe van.'),
(2503, 21, 20, 'Zij lofprijzen \'s nachts en overdag en zij verslappen niet.'),
(2504, 21, 21, 'Of hebben zij zich dan goden uit de aarde genomen die kunnen opwekken?'),
(2505, 21, 22, 'Als er in beide [de hemel en de aarde] andere goden waren dan God, dan zouden zij in slechte staat verkeren. God zij geprezen, de Heer van de troon verheven als Hij is boven wat zij Hem toeschrijven.'),
(2506, 21, 23, 'Hij hoeft zich niet te verantwoorden over wat Hij doet, maar zij moeten zich wel verantwoorden.'),
(2507, 21, 24, 'Of hebben zij zich in plaats van Hem goden genomen? Zeg: "Brengt jullie bewijs!" Dit is een vermaning van hen die met mij zijn en een vermaning van hen die er voor mij waren. Echter, de meesten van hen kennen de waarheid niet en wenden zich dus af.'),
(2508, 21, 25, 'En Wij hebben vóór jouw tijd geen gezant gezonden zonder dat Wij hem geopenbaard hebben dat er geen god is dan Ik, dient Mij dus.'),
(2509, 21, 26, 'En zij zeggen: "De Erbarmer heeft zich een kind genomen." Geprezen zij Hij! Integendeel, zij zijn geëerde dienaren.'),
(2510, 21, 27, 'Zij spreken niet eerder dan Hij en zij handelen op Zijn bevel.'),
(2511, 21, 28, 'Hij weet wat vóór hen is en wat achter hen is en zij bemiddelen slechts voor wie Hem welgevallig is, terwijl zij door de vrees voor Hem ontzag hebben. *'),
(2512, 21, 29, 'En als iemand van hen zegt: "Ik ben god naast Hem", dan vergelden Wij hem dat met de hel. Zo vergelden Wij aan de onrechtplegers.'),
(2513, 21, 30, 'Hebben zij die ongelovig zijn dan niet gezien dat de hemelen en de aarde een samenhangende massa waren? Wij hebben ze toen van elkaar gescheiden en Wij hebben uit water al het levende gemaakt. Zullen zij dan niet geloven?'),
(2514, 21, 31, 'En Wij hebben op de aarde stevige bergen gemaakt dat zij hen niet aan het wankelen zou brengen en Wij hebben er passen in gemaakt als wegen; misschien zullen zij zich de goede richting laten wijzen.'),
(2515, 21, 32, 'En Wij hebben de hemel tot een beschermd dak gemaakt. Maar zij wenden zich van Zijn tekenen af.'),
(2516, 21, 33, 'En Hij is het die de nacht en de dag, de zon en de maan geschapen heeft. Alle zweven zij in een hemelbaan.'),
(2517, 21, 34, 'En Wij hebben aan geen mens vóór jouw tijd onsterfelijkheid toegekend. Als jij sterft, zullen zij dan blijven voortbestaan?'),
(2518, 21, 35, 'Ieder levend wezen zal de dood proeven. En Wij stellen jullie op de proef met het slechte en het goede, als een verzoeking. En tot Ons zullen jullie teruggebracht worden.'),
(2519, 21, 36, 'En wanneer zij die ongelovig zijn jou zien, drijven zij slechts de spot met jou: "Is dit hem die het over jullie goden heeft?" Maar zij, zij hechten aan Gods vermaning geen geloof.'),
(2520, 21, 37, 'De mens is uit haast geschapen. Ik zal jullie Mijn tekenen wel tonen; vraagt Mij dus niet het te verhaasten.'),
(2521, 21, 38, 'En zij zeggen: "Wanneer zal deze aanzegging zich voordoen, als jullie gelijk hebben?"'),
(2522, 21, 39, 'Als zij die ongelovig zijn de tijd maar kenden wanneer zij het vuur van hun gezichten, noch van hun ruggen kunnen afhouden en zij geen hulp zullen krijgen!'),
(2523, 21, 40, 'Welnee, het zal onverwachts tot hen komen en hen verrassen. Dan kunnen zij het niet tegenhouden en zij krijgen ook geen uitstel.'),
(2524, 21, 41, 'Er werd al voor jouw tijd met gezanten de spot gedreven, maar zij die hen belachelijk maakten werden ingesloten door dat waarmee zij de spot dreven.'),
(2525, 21, 42, 'Zeg: "Wie zal jullie \'s nachts en overdag beschermen tegen de Erbarmer?" Maar nee, van de vermaning van hun Heer wenden zij zich af.'),
(2526, 21, 43, 'Of hebben zij goden die hen tegen Ons zullen verdedigen? Zij kunnen zichzelf niet eens helpen en hun wordt tegen Ons geen bijstand gegeven.'),
(2527, 21, 44, 'Maar Wij hebben dezen daar en hun vaderen nog laten genieten zolang hun leven duurde. Zien zij dan niet dat Wij naar het land komen om de uiteinden ervan in te korten? Zullen zij dan de overwinnaars zijn?'),
(2528, 21, 45, 'Zeg: "Ik waarschuw jullie alleen maar met de openbaring, maar de doven horen de oproep niet, wanneer zij gewaarschuwd worden."'),
(2529, 21, 46, 'Als hen zelfs maar een vleugje van de straf van jouw Heer treft dan zeggen zij al: "Wee ons, wij waren onrechtplegers."'),
(2530, 21, 47, 'En Wij zullen voor de opstandingsdag de eerlijke weegschalen opstellen en niemand zal in iets onrecht worden aangedaan. Al gaat het om het gewicht van een mosterdzaadje, Wij zullen het brengen; Wij zijn als afrekenaar goed genoeg.'),
(2531, 21, 48, 'Wij hebben toch aan Moesa en Haroen het reddend onderscheidingsmiddel gegeven en een verlichting en een vermaning voor de godvrezenden,'),
(2532, 21, 49, 'die hun Heer in het verborgene vrezen en ontzag hebben voor het uur.'),
(2533, 21, 50, 'En dit is een gezegende vermaning die Wij hebben neergezonden. Willen jullie haar dan verwerpen?'),
(2534, 21, 51, 'En Wij hebben vroeger al aan Ibrahiem zijn redelijk inzicht gegeven en Wij waren bekend met hem.'),
(2535, 21, 52, 'Toen hij tot zijn vader en zijn volk zei: "Wat zijn dat voor beelden waaraan jullie eer bewijzen?"'),
(2536, 21, 53, 'Zij zeiden: "Wij hebben gemerkt dat onze vaderen hen al dienden."'),
(2537, 21, 54, 'Hij zei: "Dan verkeerden jullie en jullie vaderen in duidelijke dwaling."'),
(2538, 21, 55, 'Zij zeiden: "Ben jij tot ons gekomen met de waarheid of behoor jij tot hen die schertsen?"'),
(2539, 21, 56, 'Hij zei: "Nee, jullie Heer is de Heer van de hemelen en de aarde die ze ook aangelegd heeft en ik behoor tot hen die jullie daarvan getuigen.'),
(2540, 21, 57, 'En bij God ik zal tegen jullie afgoden een list beramen, nadat jullie de rug hebben toegekeerd."'),
(2541, 21, 58, 'Toen sloeg hij ze aan gruizels, behalve een grote ervan; misschien zouden zij ernaar terugkeren.'),
(2542, 21, 59, 'Zij zeiden: "Wie heeft dit met onze goden gedaan? Die behoort zeker tot de onrechtplegers."'),
(2543, 21, 60, 'Zij zeiden: "Wij hebben een jonge man over hen horen spreken; Ibrahiem heet hij."'),
(2544, 21, 61, 'Zij zeiden: "Brengt hem dan onder de ogen van de mensen; misschien zullen zij getuigen."'),
(2545, 21, 62, 'Zij zeiden: "Heb jij dit met onze goden gedaan, Ibrahiem?"'),
(2546, 21, 63, 'Hij zei: "Welnee, dat heeft deze grote van hen gedaan. Vraagt hun maar als ze kunnen spreken."'),
(2547, 21, 64, 'En zij kwamen weer tot zichzelf en zeiden: "Jullie zijn het die de onrechtplegers zijn."'),
(2548, 21, 65, 'Toen kregen zij een terugval en zeiden: "Maar jij weet toch dat dezen niet kunnen spreken."'),
(2549, 21, 66, 'Hij zei: "Dienen jullie dan in plaats van God iets wat jullie niets nut en niet schaadt?'),
(2550, 21, 67, 'Foei jullie en wat jullie in plaats van God dienen. Hebben jullie dan geen verstand?"'),
(2551, 21, 68, 'Zij zeiden: "Verbrandt hem en helpt jullie goden, als jullie echt iets willen doen."'),
(2552, 21, 69, 'Wij zeiden: "O vuur wees koud en ongevaarlijk voor Ibrahiem."'),
(2553, 21, 70, 'Zij wilden een list tegen hem beramen, maar Wij maakten hen tot de grootste verliezers.'),
(2554, 21, 71, 'En Wij redden hem en Loet naar het land dat Wij gezegend hebben voor de wereldbewoners.'),
(2555, 21, 72, 'En Wij schonken hem Ishaak en Ja\'koeb nog bovendien en ieder [van hen] hebben Wij rechtschapen gemaakt.'),
(2556, 21, 73, 'En Wij hebben hen tot voorgangers gemaakt die op Ons bevel de goede richting wezen en Wij hebben aan hen geopenbaard de goede daden te doen, de salaat te verrichten en de zakaat te geven. En zij dienden Ons.'),
(2557, 21, 74, 'En aan Loet hebben Wij oordeelskracht en kennis gegeven en Wij redden hem uit de stad die onbetamelijke dingen deed; zij waren slechte, verdorven mensen.'),
(2558, 21, 75, 'En Wij hebben hem in onze barmhartigheid binnen laten gaan; hij behoorde tot de rechtschapenen.'),
(2559, 21, 76, 'En [denk] aan Noeh, toen hij -- vroeger -- riep. Daarop verhoorden Wij hem en Wij redden hem en zijn familie uit de geweldige benardheid.'),
(2560, 21, 77, 'Wij hielpen hem ook tegen de mensen die Onze tekenen loochenden. Dat waren slechte mensen. Dus lieten Wij hen allen tezamen verdrinken.'),
(2561, 21, 78, 'En aan Dawoed en Soelaimaan, toen zij over het gewas oordeelden toen het kleinvee van het volk \'s nachts was uitgebroken en erin gaan grazen. Wij waren getuige van hun oordeel.'),
(2562, 21, 79, 'En Wij maakten dat Soelaimaan het begreep en aan een ieder [van hen] gaven Wij oordeelskracht en kennis. En Wij maakten de bergen samen met Dawoed dienstbaar, zodat zij Ons prijzen en evenzo de vogels. Wij hebben dat gedaan!'),
(2563, 21, 80, 'En Wij hebben hem geleerd maliënhemden voor jullie te maken, opdat die jullie tegen jullie gewelddadigheid beschermen. Zijn jullie dan dankbaar?'),
(2564, 21, 81, 'En aan Soelaimaan [onderwierpen Wij] de wind bij storm zodat die zich op zijn bevel naar het land spoedt dat Wij gezegend hebben -- Wij zijn namelijk alwetend --'),
(2565, 21, 82, 'en satans die voor hem doken en daarnaast nog ander werk deden. En Wij bewaakten hen.'),
(2566, 21, 83, 'En aan Ajjoeb toen hij tot zijn Heer riep: "Mij heeft tegenspoed getroffen, maar U bent de barmhartigste van de barmhartigen."'),
(2567, 21, 84, 'Wij verhoorden hem toen en Wij hieven de tegenspoed op waarin hij verkeerde. En Wij gaven hem zijn familie terug en nog eens zoveel met hen uit barmhartigheid van Onze kant en als vermaning voor hen die [Ons] dienen.'),
(2568, 21, 85, 'En aan Isma\'iel, Idries en Dzoe-l-Kifl. Ieder [van hen] behoorde tot hen die geduldig volharden.'),
(2569, 21, 86, 'En Wij hebben hen in Onze barmhartigheid binnen laten gaan; zij behoorden tot de rechtschapenen.'),
(2570, 21, 87, 'En aan hem met de vis, toen hij kwaad wegging en meende dat Wij geen macht over hem hadden. En hij riep in de duisternis: "Er is geen god dan U. U zij geprezen! Ik was een van de onrechtplegers."'),
(2571, 21, 88, 'Toen verhoorden Wij hem en redden hem uit de nood. Zo redden Wij namelijk de gelovigen.'),
(2572, 21, 89, 'En aan Zakarijja toen hij tot zijn Heer riep: "Mijn Heer, laat mij niet alleen blijven, ook al bent U de beste van de erfgenamen."'),
(2573, 21, 90, 'Toen verhoorden Wij hem en Wij schonken hem Jahja; Wij maakten zijn echtgenote namelijk weer vruchtbaar. Zij wedijverden in goede daden en riepen Ons aan in verlangen en ontzag en zij onderwierpen zich deemoedig aan Ons.'),
(2574, 21, 91, 'En aan haar die haar eerbaarheid bewaarde. Toen bliezen Wij haar iets van Onze geest in en Wij maakten haar en haar zoon tot een teken voor de wereldbewoners.'),
(2575, 21, 92, '"Deze gemeenschap van jullie is één gemeenschap en Ik ben jullie Heer; dient Mij dus."'),
(2576, 21, 93, 'Maar zij splitsten zich onderling op; allen keren zij naar Ons terug.'),
(2577, 21, 94, 'Maar wie deugdelijke daden doet, terwijl hij gelovig is, hem valt geen ondankbaarheid voor zijn moeite ten deel; Wij zullen het voor hem bijschrijven.'),
(2578, 21, 95, 'En aan een stad die Wij hebben vernietigd is het niet toegestaan dat [de inwoners ervan] terugkeren,'),
(2579, 21, 96, 'zolang niet [de wal van] Jadjoedj en Madjoedj geopend wordt en zij van elke hoogte komen aansnellen'),
(2580, 21, 97, 'en de ware aanzegging nadert. Dan zullen de blikken van hen die ongelovig zijn verstarren: "Wee ons, wij hadden daarop niet gelet. Jazeker, wij waren onrechtplegers."'),
(2581, 21, 98, 'Jullie en wat jullie in plaats van God dienden zijn brandstof voor de hel; jullie zullen erin afdalen om te drinken.'),
(2582, 21, 99, 'Als dezen daar goden waren dan waren zij er niet in afgedaald. En allen zullen zij daarin altijd blijven.'),
(2583, 21, 100, 'Zij zullen daarin steunen maar er zelf niets horen.'),
(2584, 21, 101, 'Maar zij aan wie al eerder door Ons het mooiste gegeven is, zij zijn het die er ver van gehouden worden.'),
(2585, 21, 102, 'Zij horen niet het minste geluid ervan. Maar zij zullen altijd blijven in wat hun zielen begeren.'),
(2586, 21, 103, 'De grote schrik zal hen niet bedroefd maken en de engelen zullen hen ontvangen: "Dit is jullie dag die jullie toegezegd was."'),
(2587, 21, 104, 'De dag dat Wij de hemel oprollen zoals een rol voor de boeken opgerold wordt. Zoals Wij de eerste schepping begonnen zijn zullen Wij haar herhalen. Dat is een toezegging waartoe Wij verplicht zijn. Wij doen het zeker!'),
(2588, 21, 105, 'En Wij hebben in de Zaboer, na de vermaning, geschreven dat Mijn rechtschapen dienaren de aarde zullen beërven.'),
(2589, 21, 106, 'Hierin is een verkondiging voor mensen die [Ons] dienen.'),
(2590, 21, 107, 'En Wij hebben jou slechts als barmhartigheid voor de wereldbewoners gezonden.'),
(2591, 21, 108, 'Zeg: "Aan mij wordt slechts geopenbaard dat jullie god één god is. Zullen jullie dan [mensen] worden die zich [aan God] hebben overgegeven."'),
(2592, 21, 109, 'Als zij zich dan afkeren zeg dan: "Ik heb het jullie klaar en duidelijk verkondigd en ik weet niet of het dichtbij of veraf is wat jullie is aangezegd.'),
(2593, 21, 110, 'Hij weet wat hardop gezegd wordt en Hij weet wat jullie verbergen.'),
(2594, 21, 111, 'Maar ik weet niet of het misschien een verzoeking voor jullie is of een tijdelijk vruchtgebruik."'),
(2595, 21, 112, 'Hij zegt: "Mijn Heer, oordeel naar waarheid. En onze Heer is de Erbarmer wiens hulp gevraagd wordt tegen wat jullie beschrijven."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'O mensen, vreest jullie Heer; de beving van het uur is iets geweldigs!'),
(2597, 22, 2, 'Op de dag dat jullie het zien zal elke zogende vrouw wat zij zoogt veronachtzamen en elke zwangere zal wat zij draagt baren. En je zult de mensen dronken zien, al zijn zij niet dronken. Maar Gods straf is streng.'),
(2598, 22, 3, 'En er zijn mensen die zonder kennis over God twisten en die elke opstandige satan navolgen.'),
(2599, 22, 4, 'Er staat voor hem geschreven dat hij hem die zich als medestander bij hem aansluit tot dwaling zal brengen en naar de bestraffing van de vuurgloed zal leiden.'),
(2600, 22, 5, 'O mensen, als jullie in twijfel verkeren over de opwekking? Wij hebben jullie geschapen uit aarde, daarna uit een druppel, daarna uit een bloedklonter en daarna uit een klomp vlees geschapen en ongeschapen om het jullie duidelijk te maken. En Wij laten wat Wij willen voor een vastgestelde termijn in de moederschoot verblijven. Dan brengen Wij jullie als kind tevoorschijn. Dan moeten jullie volgroeid worden. En onder jullie zijn er die weggenomen worden en onder jullie zijn er die teruggebracht worden tot de meest vernederende leeftijd zodat zij, na kennis gehad te hebben, niets meer weten. En je ziet dat de aarde verdord is, maar wanneer Wij er dan water op laten neerdalen beweegt zij zich, zwelt op en laat allerlei kostelijke soorten groeien.'),
(2601, 22, 6, 'Dat is omdat God de waarheid is en omdat Hij de doden levend maakt en omdat Hij almachtig is,'),
(2602, 22, 7, 'en omdat het uur komt waaraan geen twijfel is en omdat God opwekt wie in de graven zijn.'),
(2603, 22, 8, 'En er zijn mensen die zonder kennis, zonder leidraad en zonder verlichtend boek over God twisten.'),
(2604, 22, 9, 'Zo iemand gaat trots opzij staan om [anderen] van Gods weg te laten afdwalen. Voor hem is er in het tegenwoordige leven schande en Wij zullen hem op de opstandingsdag de bestraffing van het vuur laten proeven.'),
(2605, 22, 10, '"Dat is voor wat jouw handen vroeger gedaan hebben en omdat God geen onrechtvaardige behandeling geeft aan de dienaren."'),
(2606, 22, 11, 'En er zijn mensen die God op het randje dienen. Als zo iemand iets goeds treft, voelt hij zich daarbij gerust, maar als hem een beproeving treft, draait hij zijn gezicht weer om. Hij verliest de tegenwoordige wereld en het hiernamaals; dat is het duidelijke verlies.'),
(2607, 22, 12, 'Hij roept in plaats van God iets aan wat hem niet schaadt en niet nut. Dat is de vergaande dwaling.'),
(2608, 22, 13, 'Hij roept tot iemand van wie de schade nog eerder komt dan het nut. Dat is pas een slechte beschermheer en dat is pas een slechte metgezel!'),
(2609, 22, 14, 'God laat hen die geloven en de deugdzame daden doen, tuinen binnengaan waar de rivieren onderdoor stromen. God doet wat Hij wenst.'),
(2610, 22, 15, 'Wie meent dat God hem in de tegenwoordige wereld en in het hiernamaals niet zal helpen die moet maar met een touw naar de hemel reiken en het dan afsnijden. En dan moet hij maar kijken of door zijn list verwijderd wordt wat hem woedend maakt.'),
(2611, 22, 16, 'En zo hebben Wij hem neergezonden als duidelijke tekenen. En het is zo dat God de goede richting wijst aan wie hij wenst.'),
(2612, 22, 17, 'Zij die geloven, zij die het jodendom aanhangen, de Sabiërs, de christenen, de magiërs en de aanhangers van het veelgodendom, God zal scheiding tussen hen aanbrengen op de opstandingsdag. God is van alles getuige.'),
(2613, 22, 18, 'Zie jij niet dat voor God zich eerbiedig neerbuigen wie er in de hemelen en wie er op de aarde zijn, de zon, de maan en de sterren, de bergen en de bomen, de dieren en veel van de mensen. Maar veel zijn er die de bestraffing terecht zullen krijgen. En wie door God vernederd wordt, voor hem is er niemand die hem eer bewijst. God doet wat Hij wil." --'),
(2614, 22, 19, 'Dit zijn twee tegenpartijen die met elkaar over hun Heer twisten: Voor hen die ongelovig zijn worden kleren geknipt uit vuur terwijl over hun hoofden gloeiend water wordt uitgegoten.'),
(2615, 22, 20, 'Wat in hun buiken en hun huis is smelt daardoor.'),
(2616, 22, 21, 'Voor hen zijn er knuppels van ijzer.'),
(2617, 22, 22, 'Telkens als zij van smart eruit wensen te gaan, worden zij erin teruggebracht en [wordt tot hen gezegd]: "Proeft de straf van de verbranding."'),
(2618, 22, 23, 'Maar God laat hen die geloven en de deugdzame daden doen tuinen binnengaan waar de rivieren onderdoor stromen. Zij tooien zich daarin met armbanden van goud en met parels en hun kleren zijn daar van zijde.'),
(2619, 22, 24, 'En zij worden geleid tot iets wat goed is om te zeggen en zij worden naar de weg van de lofwaardige geleid.'),
(2620, 22, 25, 'Zij die ongelovig zijn en de weg van God versperren en ook de weg naar de heilige moskee die Wij gemaakt hebben voor de mensen, of zij daar nu een vaste verblijfplaats hebben of [als bezoeker] vanuit de woestijn komen? en als iemand daar misbruik wenst te maken door onrecht te plegen, dan zullen Wij hem een pijnlijke bestraffing laten proeven.'),
(2621, 22, 26, 'En toen Wij aan Ibrahiem op de plaats van het huis onderdak gaven [en zeiden]: "Voeg niets als metgezel aan Mij toe en reinig Mijn huis voor hen die de omgang verrichten, die rechtopstaan en die buigen en zich eerbiedig neerbuigen.'),
(2622, 22, 27, 'En roep onder de mensen op tot de bedevaart zodat zij te voet tot je komen en op allerlei knokige kamelen die door elke diepe bergpas heen komen,'),
(2623, 22, 28, 'om getuige te zijn van dingen die voor hen nuttig zijn en om Gods naam op de bekende dagen te vermelden over de dieren van het vee waarmee Hij jullie voorzien heeft. Eet daar dan van en voedt ook de arme die noodlijdend is.'),
(2624, 22, 29, 'Dan moeten zij zich ontdoen van hun viezigheid en hun geloften vervullen en de omgang om het aloude huis verrichten."'),
(2625, 22, 30, 'Dat is het. En als iemand de heilige dingen in ere houdt dan is dat beter voor hem bij zijn Heer. En voor jullie is het vee toegestaan, behalve wat jullie voorgelezen wordt. Vermijdt dus de gruwel van de afgoden en vermijdt het de onwaarheid te zeggen,'),
(2626, 22, 31, 'als aanhangers van het zuivere geloof ten opzichte van God zonder metgezellen aan Hem toe te voegen. En als iemand aan God metgezellen toevoegt, dan is het alsof hij uit de hemel valt en dan door de vogels meegesleurd of door de wind naar een afgelegen plaats geblazen wordt.'),
(2627, 22, 32, 'Dat is het. En als iemand Gods gewijde symbolen in ere houdt, dan komt dat van de godvrezendheid van de harten.'),
(2628, 22, 33, 'Voor jullie zijn daarin allerlei nuttigheden tot een vastgestelde termijn en daarna vinden zij hun bestemming [om geslacht te worden] bij het aloude huis.'),
(2629, 22, 34, 'En voor elke gemeenschap hebben Wij een rite gemaakt om Gods naam te vermelden over de dieren van het vee waarmee Hij hen voorzien heeft. En jullie god is één god, geeft jullie dus over aan Hem en verkondigt het goede nieuws aan hen die nederig vertrouwen,'),
(2630, 22, 35, 'van wie, wanneer Gods naam vermeld wordt, de harten vol ontzag zijn, die geduldig volharden bij wat hen treft en die de salaat verrichten en die bijdragen geven van wat Wij hun voor hun levensonderhoud gegeven hebben.'),
(2631, 22, 36, 'En de offerkamelen hebben Wij voor jullie tot Gods gewijde symbolen laten behoren. Daarin is [allerlei] goeds voor jullie. En vermeldt Gods naam erover als ze gekluisterd klaar staan. En als ze op hun zij gevallen zijn, eet er dan van en voedt ook de arme die bescheiden is en die erom vraagt. Zo hebben Wij ze aan jullie dienstbaar gemaakt; misschien zullen jullie dank betuigen.'),
(2632, 22, 37, 'Het vlees noch het bloed ervan zal God bereiken, maar de godvrezendheid van jullie bereikt Hem. Zo hebben Wij ze aan jullie dienstbaar gemaakt opdat jullie de grootheid van God verkondigen omdat Hij jullie op het goede pad gebracht heeft. En verkondigt het goede nieuws aan hen die goed doen.'),
(2633, 22, 38, 'God zal hen die geloven verdedigen. God bemint geen enkele ondankbare verrader.'),
(2634, 22, 39, 'Aan hen die bestreden worden is [de strijd] toegestaan omdat hun onrecht is aangedaan; God heeft de macht hen te helpen,'),
(2635, 22, 40, 'die zonder recht uit hun woningen verdreven zijn, alleen maar omdat zij zeggen: "Onze Heer is God" -- en als God de mensen elkaar niet had laten weerhouden dan waren kluizenaarsverblijven, kerken, synagogen en moskeeën waarin Gods naam vaak genoemd wordt zeker verwoest. Maar God zal hen die Hem helpen zeker helpen; God is krachtig en machtig --'),
(2636, 22, 41, 'zij die, als Wij hun macht op de aarde geven, de salaat verrichten, de zakaat geven, het behoorlijke gebieden en het verwerpelijke verbieden. En de uiteindelijke beschikkingen berusten bij God.'),
(2637, 22, 42, 'Als zij jou van leugens betichten; al voor hun tijd had Noehs volk van leugens beticht en [evenzo] de \'Aad en de Thamoed'),
(2638, 22, 43, 'en het volk van Ibrahiem en het volk van Loet'),
(2639, 22, 44, 'en de bewoners van Madjan. En Moesa is van leugens beticht geweest, maar Ik heb aan hen die ongelovig zijn uitstel gegeven. Toen heb Ik hen gegrepen. En hoe was Mijn terechtwijzing dan?'),
(2640, 22, 45, 'En hoeveel steden hebben Wij al niet vernietigd, omdat zij onrecht pleegden, zodat zij verlaten en in ruïnes zijn? En bronnen die opgegeven zijn en hooggebouwde kastelen.'),
(2641, 22, 46, 'Hebben zij dan niet op de aarde rondgereisd zodat zij harten kregen om ermee te begrijpen of oren om ermee te horen? Toch zijn de ogen niet blind, maar de harten die in de borsten zijn, zijn blind.'),
(2642, 22, 47, 'En zij willen van jou dat de bestraffing wordt verhaast. God zal Zijn toezegging zeker nakomen. Maar een dag bij jouw Heer is als duizend jaar volgens jullie berekening.'),
(2643, 22, 48, 'En hoeveel steden heb Ik al niet uitstel gegeven, hoewel zij onrecht pleegden. Toen heb Ik haar gegrepen. En tot Mij is de bestemming.'),
(2644, 22, 49, 'Zeg: "Jullie mensen! Ik ben voor jullie een duidelijke waarschuwer!"'),
(2645, 22, 50, 'Zij dan die geloven en de deugdelijke daden doen, voor hen is er vergeving en een voortreffelijke voorziening.'),
(2646, 22, 51, 'Maar zij die tegen Onze tekenen proberen in te gaan om ze te ontkrachten, dat zullen de bewoners van het hellevuur zijn.'),
(2647, 22, 52, 'En Wij hebben voor jouw tijd geen gezant of profeet gezonden zonder dat de satan hem, wanneer hij iets wenste, iets [om] volgens zijn wens [voor te lezen] had ingegeven. Maar God schaft af wat de satan ingeeft en dan stelt God Zijn tekenen eenduidig vast. God is wijs en wetend.'),
(2648, 22, 53, 'Opdat Hij wat de satan ingeeft tot een verzoeking maakt voor hen die in hun harten een ziekte hebben en van wie de harten verhard zijn -- de onrechtplegers zijn het vergaand oneens --'),
(2649, 22, 54, 'en opdat zij aan wie de kennis is gegeven weten dat het de waarheid is van jouw Heer zodat zij erin geloven, en opdat hun harten nederig op Hem vertrouwen. God leidt hen die geloven op een juiste weg.'),
(2650, 22, 55, 'Zij die ongelovig zijn blijven erover in twijfel totdat het uur onverwachts tot hen komt of totdat de straf van een troosteloze dag tot hen komt.'),
(2651, 22, 56, 'De heerschappij op die dag heeft God [alleen]. Hij zal tussen hen oordelen. Dus zullen zij die geloven en de deugdelijke daden doen in de tuinen van de gelukzaligheid zijn.'),
(2652, 22, 57, 'En zij die ongelovig zijn en Onze tekenen loochenen, zij zijn het voor wie er een vernederende bestraffing is.'),
(2653, 22, 58, 'En zij die op Gods weg uitgeweken zijn en dan gedood worden of sterven, voor hen voorziet God goed in hun onderhoud. God is werkelijk de beste voorziener.'),
(2654, 22, 59, 'Hij zal hen zeker een plaats binnen laten gaan waar zij mee ingenomen zijn. God is wetend en zachtmoedig. *'),
(2655, 22, 60, 'Zo is dat. En wie overeenkomstig de bestraffing die hij gekregen heeft bestraft en dan onrechtmatig wordt behandeld, hem zal God zeker helpen. God is lankmoedig en vergevend.'),
(2656, 22, 61, 'Dat is zo omdat God de nacht laat overgaan in de dag en de dag laat overgaan in de nacht en omdat God horend en doorziend is.'),
(2657, 22, 62, 'Dat is zo omdat God de waarheid is en omdat wat zij buiten Hem aanroepen de onwaarheid is en omdat God de verhevene, de grote is.'),
(2658, 22, 63, 'Zie jij dan niet dat God uit de hemel water laat neerdalen zodat de aarde groen wordt? God is de welwillende, de welingelichte.'),
(2659, 22, 64, 'Van Hem is wat er in de hemelen en wat er op de aarde is. En God is de behoefteloze, de lofwaardige.'),
(2660, 22, 65, 'Zie je niet dat God voor jullie dienstbaar gemaakt heeft wat er op de aarde is en ook de schepen zodat zij op Zijn bevel op zee varen en dat Hij de hemel vasthoudt, zodat hij niet op de aarde valt, behalve met Zijn toestemming? God is voor de mensen vol mededogen en barmhartig.'),
(2661, 22, 66, 'En Hij is het die jullie leven heeft gegeven. Hij zal jullie laten sterven en vervolgens weer tot leven brengen. De mens is werkelijk ondankbaar.'),
(2662, 22, 67, 'Voor elke gemeenschap hebben Wij een ordening gemaakt waarnaar zij moeten handelen. Zij moeten dus met jou over de zaak niet twisten. En roep op tot jouw Heer. Jij volgt een juiste leidraad.'),
(2663, 22, 68, 'En als zij met jou twisten zeg dan: "God weet wat jullie doen.'),
(2664, 22, 69, 'God zal op de opstandingsdag tussen jullie over dat waarover jullie het oneens waren oordelen.'),
(2665, 22, 70, 'Weet jij dan niet dat God weet wat er in de hemel en op de aarde is? Dat staat in een boek; dat is voor God gemakkelijk."'),
(2666, 22, 71, 'En zij dienen in plaats van God iets waarvoor Hij geen machtiging had neergezonden en waarvan zij geen kennis hebben; de onrechtplegers hebben geen bondgenoot.'),
(2667, 22, 72, 'En wanneer hun Onze tekenen als duidelijke bewijzen worden voorgelezen, dan herken jij de afkeer op de gezichten van hen die ongelovig zijn, terwijl zij zich bijna storten op hen aan wie Onze tekenen worden voorgelezen. Zeg: "Zal ik jullie iets meedelen wat nog erger is dan dat? Het vuur! God heeft het hun die ongelovig zijn aangezegd." Dat is pas een slechte bestemming!'),
(2668, 22, 73, 'Jullie mensen! Een vergelijking wordt voorgehouden. Luistert er dan naar. Zij die jullie in plaats van God aanroepen kunnen niet eens een vlieg scheppen ook al zouden zij zich daartoe aaneensluiten. En als een vlieg iets van hen weggrist kunnen zij het niet van hem terugpakken. Zwak is hij die het najaagt en dat wat nagejaagd wordt.'),
(2669, 22, 74, 'Zij hebben God niet op de juiste waarde geschat. God is krachtig en machtig.'),
(2670, 22, 75, 'God kiest uit de engelen gezanten en ook uit de mensen. God is horend en doorziend.'),
(2671, 22, 76, 'Hij weet wat vóór hen is en wat achter hen is. En aan God worden alle zaken voorgelegd.'),
(2672, 22, 77, 'Jullie die geloven! Buigt en buigt jullie eerbiedig neer en dient jullie Heer en doet het goede; misschien zal het jullie goed gaan. --'),
(2673, 22, 78, 'En zet jullie voor God in met de inzet die Hem toekomt. Hij heeft jullie verkozen en Hij heeft jullie in de godsdienst niets hinderlijks opgelegd: het geloof van jullie vader Ibrahiem. Hij heeft jullie vroeger al [mensen] die zich [aan God] overgegeven hebben genoemd en [nu] ook hierin, opdat de gezant getuige voor jullie zou zijn en opdat jullie getuigen voor de mensen zouden zijn. Verricht dus de salaat en geeft de zakaat en houdt jullie aan God vast. Hij is jullie beschermheer; een voortreffelijke beschermheer en een voortreffelijke helper.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'Het zal de gelovigen welgaan,'),
(2675, 23, 2, 'die in hun salaat deemoedig zijn,'),
(2676, 23, 3, 'die geklets mijden,'),
(2677, 23, 4, 'die de zakaat opbrengen'),
(2678, 23, 5, 'en die hun schaamstreek kuis bewaren,'),
(2679, 23, 6, 'behalve bij hun echtgenotes of slavinnen waarover zij beschikken, dan valt hun niets te verwijten,'),
(2680, 23, 7, 'maar zij die meer dan dat begeren, zij zijn het die overtreden.'),
(2681, 23, 8, 'Die ook goed toezicht houden op hun onderpanden en hun verbintenis'),
(2682, 23, 9, 'en die zich aan hun salaats houden.'),
(2683, 23, 10, 'Zij zijn het die de erfgenamen zijn,'),
(2684, 23, 11, 'die het paradijs zullen beërven, zij zullen daarin altijd blijven.'),
(2685, 23, 12, 'Wij hebben de mens toch uit een extract van klei geschapen.'),
(2686, 23, 13, 'Daarna maakten Wij hem tot een druppel in een solide verblijfplaats.'),
(2687, 23, 14, 'Daarna schiepen Wij de druppel tot een bloedklonter, dan schiepen Wij de bloedklonter tot een vleesklomp, dan schiepen Wij de vleesklomp tot gebeente en dan bedekten Wij het gebeente met vlees. Daarna lieten Wij het als een nieuwe schepping ontstaan. Gezegend zij God, de beste schepper.'),
(2688, 23, 15, 'Daarna zullen jullie, nadat dat gebeurd is, sterven.'),
(2689, 23, 16, 'Daarna zullen jullie op de opstandingsdag opgewekt worden.'),
(2690, 23, 17, 'En boven jullie hebben Wij zeven lagen geschapen en Wij letten goed op de schepping.'),
(2691, 23, 18, 'En Wij hebben met mate water uit de hemel neer laten dalen en het in de aarde laten rusten -- Wij zijn ook in staat het weg te nemen --'),
(2692, 23, 19, 'en Wij hebben voor jullie daarmee tuinen van palmen en wijnstokken laten ontstaan waarin jullie veel vruchten hebben en waarvan jullie kunnen eten'),
(2693, 23, 20, 'en een boom die uit de berg Sinaa? voortkomt die olie voortbrengt en saus voor hen die eten.'),
(2694, 23, 21, 'In het vee is er voor jullie ook een les; van wat er in hun buiken is geven Wij te drinken. Jullie hebben daarvan ook veel nut en jullie eten ervan.'),
(2695, 23, 22, 'En op hen en op de schepen worden jullie voortgedragen.'),
(2696, 23, 23, 'Wij hebben Noeh tot zijn volk gezonden en hij zei: "Mijn volk! Dient God; jullie hebben geen andere god dan Hem. Zullen jullie niet godvrezend worden?"'),
(2697, 23, 24, 'En de raad van voornaamsten, zij van zijn volk die ongelovig waren, zei toen: "Dit is slechts een mens zoals jullie, die boven jullie wil uitblinken. Als God het namelijk gewild had, dan had Hij wel engelen neergezonden. Wij hebben hiervan bij onze vaderen die er eertijds waren niet gehoord.'),
(2698, 23, 25, 'Hij is slechts een man die last heeft van bezetenheid. Wacht maar enige tijd af."'),
(2699, 23, 26, 'Hij zei: "Mijn Heer, help mij want zij betichten mij van leugens."'),
(2700, 23, 27, 'Toen openbaarden Wij aan hem: "Bouw het schip onder Onze ogen en volgens Onze openbaring. Wanneer dan Onze beschikking komt en de oven overkookt, laat er dan van alles twee stuks, paarsgewijs in gaan en jouw familie, behalve hem uit hun midden over wie al sprake is geweest, maar spreek Ons niet aan over hen die onrecht plegen; zij zullen verdronken worden.'),
(2701, 23, 28, 'En wanneer jij met hen die bij jou zijn op de boot plaats genomen hebt, zeg dan: \'Lof zij God die ons van de mensen die onrecht plegen gered heeft.?'),
(2702, 23, 29, 'En zeg: \'Mijn Heer, geef mij een gezegend onderkomen; U bent de beste van hen die een onderkomen geven.?"'),
(2703, 23, 30, 'Daarin zijn zeker tekenen en Wij hebben'),
(2704, 23, 31, 'Toen lieten Wij na hen een andere generatie ontstaan.'),
(2705, 23, 32, 'En Wij zonden een gezant uit hun midden tot hen [met de oproep]: "Dient God; jullie hebben geen andere god dan Hem. Zullen jullie niet godvrezend worden?"'),
(2706, 23, 33, 'En de raad van voornaamsten van zijn volk, zij die ongelovig waren en de ontmoeting van het hiernamaals loochenden en die Wij in het tegenwoordige leven in luxe lieten leven, zei: "Dit is slechts een mens zoals jullie, die eet wat jullie eten en drinkt wat jullie drinken.'),
(2707, 23, 34, 'En als jullie een mens zoals jullie zelf gehoorzamen dan zijn jullie werkelijk verliezers!'),
(2708, 23, 35, 'Zegt hij jullie werkelijk aan dat jullie, wanneer jullie gestorven zijn en stof en gebeente geworden zijn, dat jullie dan weer tevoorschijn gebracht zullen worden? *'),
(2709, 23, 36, 'Ver, ver ernaast is wat jullie aangezegd wordt.'),
(2710, 23, 37, 'Er bestaat alleen ons tegenwoordige leven; wij sterven en wij leven en wij worden niet opgewekt.'),
(2711, 23, 38, 'Hij is slechts een man die over God een leugen verzonnen heeft en wij zullen aan hem geen geloof hechten."'),
(2712, 23, 39, 'Hij zei: "Mijn Heer, help mij want zij betichten mij van leugens."'),
(2713, 23, 40, 'Hij zei: "Binnenkort zullen zij wroeging gaan krijgen."'),
(2714, 23, 41, 'Toen greep de schreeuw hen met recht en Wij maakten hen tot rommel. Weg dus met de mensen die onrecht plegen.'),
(2715, 23, 42, 'Toen lieten Wij na hen andere generaties ontstaan.'),
(2716, 23, 43, 'Geen gemeenschap kan te vroeg haar termijn bereiken en ook niet te laat.'),
(2717, 23, 44, 'Toen zonden Wij Onze gezanten, de een na de ander. Telkens als tot een gemeenschap haar gezant kwam betichtten zij hem van leugens. Dus lieten Wij hen elkaar opvolgen en maakten hen tot [het onderwerp van] verhalen. Weg dus met mensen die niet geloven.'),
(2718, 23, 45, 'Toen zonden Wij Moesa en zijn broer Haroen met Onze tekenen en een duidelijke machtiging'),
(2719, 23, 46, 'naar Fir\'aun en zijn raad van voornaamsten, maar zij waren hoogmoedig; zij waren mensen die de overhand hadden.'),
(2720, 23, 47, 'En zij zeiden: "Zullen wij aan twee die mensen zijn zoals wij geloof hechten en dat terwijl hun volk aan ons onderhorig is?"'),
(2721, 23, 48, 'Dus betichtten zij hen van leugens. Zo behoorden zij tot hen die vernietigd werden.'),
(2722, 23, 49, 'En Wij hebben aan Moesa toch het boek gegeven; misschien zullen zij zich de goede richting laten wijzen.'),
(2723, 23, 50, 'En Wij hebben de zoon van Marjam en zijn moeder tot een teken gemaakt en Wij gaven hun een onderkomen op een heuvel met een stevige bodem en bronwater.'),
(2724, 23, 51, '"Jullie gezanten! Eet van de goede dingen en handelt deugdelijk. Ik weet wat jullie doen.'),
(2725, 23, 52, 'Deze gemeenschap van jullie is één gemeenschap en Ik ben jullie Heer; vreest Mij dus."'),
(2726, 23, 53, 'Maar zij splitsten zich onderling op in groepen; elke partij was blij over wat zij hadden.'),
(2727, 23, 54, 'Laat hen nog maar enige tijd in hun waan.'),
(2728, 23, 55, 'Rekenen zij er dan bij het bezit en de zonen die Wij hun verlenen op dat'),
(2729, 23, 56, 'Wij voor hen wedijveren in goede daden? Integendeel, maar zij beseffen het niet.'),
(2730, 23, 57, 'Zij die door de vrees voor hun Heer ontzag hebben,'),
(2731, 23, 58, 'zij die in de tekenen van hun Heer geloven,'),
(2732, 23, 59, 'zij die aan hun Heer geen metgezel toevoegen'),
(2733, 23, 60, 'en zij die geven wat zij geven terwijl hun harten vol ontzag zijn omdat zij tot hun Heer terug zullen keren,'),
(2734, 23, 61, 'zij wedijveren in goede daden en zij zullen daarbij de eersten zijn.'),
(2735, 23, 62, 'Maar Wij leggen niemand meer op dan hij kan dragen en Wij hebben een boek dat de waarheid spreekt; en hun zal geen onrecht worden gedaan.'),
(2736, 23, 63, 'Maar nee, hun harten verkeren wat dit aangaat in een waan en zij hebben afgezien daarvan nog andere daden die zij blijven doen.'),
(2737, 23, 64, 'Wanneer Wij dan hen die onder hen in luxe leven met de bestraffing te pakken nemen, dan schreeuwen zij opeens om hulp.'),
(2738, 23, 65, '"Schreeuwt vandaag maar niet om hulp; jullie zullen tegen Ons geen hulp krijgen.'),
(2739, 23, 66, 'Mijn tekenen zijn aan jullie voorgelezen, maar jullie hebben je hielen gelicht,'),
(2740, 23, 67, 'in jullie hoogmoed erover terwijl jullie \'s nachts onzin uitkraamden."'),
(2741, 23, 68, 'Overpeinzen zij dan niet wat gezegd is? Of is er tot hen iets gekomen, wat niet tot hun vaderen die er eertijds waren gekomen was?'),
(2742, 23, 69, 'Of erkennen zij hun gezant niet zodat zij hem verwerpen?'),
(2743, 23, 70, 'Of zeggen zij: "Hij heeft last van bezetenheid)? Welnee, hij is met de waarheid tot hen gekomen, maar de meesten van hen verafschuwen de waarheid.'),
(2744, 23, 71, 'Maar als de waarheid hun grillen zou volgen, dan zouden de hemelen, de aarde en wie zich daar bevinden verdorven raken. Nee, Wij zijn met hun vermaning tot hen gekomen, maar zij wenden zich van hun vermaning af.'),
(2745, 23, 72, 'Of vraag jij hun een vergoeding? De vergoeding van jouw Heer is beter; Hij is de beste voorziener.'),
(2746, 23, 73, 'En jij roept hen echt op tot een juiste weg.'),
(2747, 23, 74, 'Maar zij die in het hiernamaals niet geloven wijken van de weg af. *'),
(2748, 23, 75, 'En al zouden Wij erbarmen met hen hebben en de tegenspoed waarin zij verkeren opheffen, dan zouden zij in hun onbeschaamdheid toch doorgaan met dwalen.'),
(2749, 23, 76, 'En Wij hebben hen met de bestraffing gegrepen, maar zij hebben zich niet aan hun Heer overgegeven en zij vernederen zich niet.'),
(2750, 23, 77, 'Wanneer Wij dan voor hen een deur tot een strenge bestraffing openen, dan zijn zij opeens in wanhoop terneergeslagen.'),
(2751, 23, 78, 'Hij is het die voor jullie gehoor, gezichtsvermogen en harten heeft laten ontstaan; gering is de dank die jullie betuigen.'),
(2752, 23, 79, 'En Hij is het die jullie op de aarde geschapen heeft en tot Hem zullen jullie verzameld worden.'),
(2753, 23, 80, 'En Hij is het die leven geeft en laat sterven en van Hem is het verschil van nacht en dag; zullen jullie dan niet tot verstand komen?'),
(2754, 23, 81, 'Welnee, zij zeggen hetzelfde als zij die er eertijds waren.'),
(2755, 23, 82, 'Zij zeggen: "Zullen wij, wanneer wij gestorven zijn en stof en gebeente geworden zijn, dan weer opgewekt worden?'),
(2756, 23, 83, 'Dat was ons en onze vaderen vroeger ook al toegezegd, maar dit zijn slechts fabels van hen die er eertijds waren."'),
(2757, 23, 84, 'Zeg: "Van wie is de aarde en wat erop is, als jullie het weten?"'),
(2758, 23, 85, 'Zij zullen zeggen: "Van God." Zeg: "Zullen jullie je dan niet laten vermanen?"'),
(2759, 23, 86, 'Zeg: "Wie is de Heer van de zeven hemelen en de Heer van de geweldige troon?"'),
(2760, 23, 87, 'Zij zullen zeggen: "Van God." Zeg: "Zullen jullie dan niet godvrezend worden?"'),
(2761, 23, 88, 'Zeg: "In wiens hand is de heerschappij over alles zodat hij bescherming geeft terwijl er tegen hem geen bescherming gegeven kan worden, als jullie het weten?"'),
(2762, 23, 89, 'Zij zullen zeggen: "Van God." Zeg: "Hoe zijn jullie dan zo betoverd?"'),
(2763, 23, 90, 'Ja zeker, Wij zijn met de waarheid tot hen gekomen en zij zijn het die liegen.'),
(2764, 23, 91, 'God heeft zich geen kind genomen en er is naast Hem geen andere god; dan zou elke god met wat hij geschapen heeft weggaan en zou de een de ander overweldigen. God zij geprezen, verheven als Hij is boven wat zij [Hem] toeschrijven,'),
(2765, 23, 92, 'de kenner van het verborgene en het waarneembare; ver verheven is Hij boven wat zij aan Hem als metgezellen toeschrijven!'),
(2766, 23, 93, 'Zeg: "Mijn Heer, als U mij wilt laten zien wat hun aangezegd wordt,'),
(2767, 23, 94, 'mijn Heer, zet mij dan niet bij de mensen die onrecht plegen."'),
(2768, 23, 95, 'Wij hebben zeker de macht om jou te laten zien wat Wij hun aanzeggen.'),
(2769, 23, 96, 'Weer de slechte daad af met iets wat beter is. Wij weten het best wat zij beschrijven.'),
(2770, 23, 97, 'En zeg: "Mijn Heer, ik zoek bij U bescherming tegen de ophitsingen van de satans,'),
(2771, 23, 98, 'en ik zoek er bij U bescherming voor, mijn Heer, dat zij bij mij komen."'),
(2772, 23, 99, 'En wanneer dan tot een van hen de dood komt, zegt hij: "Mijn Heer, laat mij teruggaan,'),
(2773, 23, 100, 'opdat ik dan misschien wel deugdelijk zal handelen in wat ik [nu] achtergelaten heb." Nee! Dat is zomaar een woord dat hij zegt, maar achter hen is een versperring tot aan de dag waarop zij opgewekt worden.'),
(2774, 23, 101, 'Wanneer op de bazuin geblazen wordt, dan bestaan er op die dag tussen hen geen verwantschapsbetrekkingen meer en zullen zij elkaar niet ondervragen.'),
(2775, 23, 102, 'En zij van wie de weegschalen zwaar wegen, zij zijn het die het welgaat.'),
(2776, 23, 103, 'Maar zij van wie de weegschalen licht zijn, zij zijn het die zichzelf verloren hebben; in de hel zullen zij altijd blijven.'),
(2777, 23, 104, 'Het vuur zal hun gezichten verzengen waarbij zij hun mond vertrekken.'),
(2778, 23, 105, '"Zijn Mijn tekenen niet aan jullie voorgelezen en loochenden jullie ze dan niet?"'),
(2779, 23, 106, 'Zij zeggen: "Onze Heer, onze ellendigheid heeft ons overwonnen; wij waren mensen die dwaalden.'),
(2780, 23, 107, 'Onze Heer, breng ons hieruit en als wij dan terugvallen, dan plegen wij echt onrecht."'),
(2781, 23, 108, 'Hij zegt: "Weest weg, [blijft] erin en spreekt niet tot mij.'),
(2782, 23, 109, 'Er was een groep van Mijn dienaren die gewoonlijk zeiden: \'Onze Heer, wij geloven, vergeef ons dan en heb erbarmen met ons; U bent de beste van de barmhartigen.?'),
(2783, 23, 110, 'Maar jullie maakten hen belachelijk, totdat zij jullie daardoor lieten vergeten aan Mij te denken. En jullie lachten hen uit.'),
(2784, 23, 111, 'Ik beloon hen vandaag, omdat zij geduldig hebben volhard, zodat zij het zijn die triomferen."'),
(2785, 23, 112, 'Hij zegt: "Hoe lang heeft het voor jullie in de aarde geduurd, hoeveel jaar?"'),
(2786, 23, 113, 'Zij zeggen: "Het heeft voor ons een dag of een paar dagen geduurd, vraag maar aan hen die kunnen tellen."'),
(2787, 23, 114, 'Hij zegt: "Het heeft voor jullie inderdaad slechts kort geduurd. Als jullie het maar wisten.'),
(2788, 23, 115, 'Of dachten jullie dan dat Wij jullie als een spel geschapen hebben en dat jullie niet tot Ons terug worden gebracht?"'),
(2789, 23, 116, 'Hoogverheven is God, de ware koning. Er is geen god dan Hij, de Heer van de edele troon.'),
(2790, 23, 117, 'En als iemand naast God een andere god aanroept, waarvoor hij geen bewijs heeft, dan is zijn afrekening bij zijn Heer; het zal de ongelovigen niet welgaan.'),
(2791, 23, 118, 'En zeg: "Mijn Heer, vergeef mij en heb erbarmen; U bent de beste van de barmhartigen."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'Een soera die Wij hebben neergezonden, die Wij tot een verplichting gemaakt hebben en waarin Wij duidelijke tekenen hebben neergezonden; misschien laten jullie je vermanen.'),
(2793, 24, 2, 'De overspelige vrouw en de overspelige man, geselt elk van hen beiden met honderd geselslagen en krijgt in Gods godsdienst geen mededogen met hen, als jullie in God en de laatste dag geloven. Bij hun bestraffing moet een groep gelovigen aanwezig zijn.'),
(2794, 24, 3, 'Een overspelige man mag slechts met een overspelige vrouw trouwen of met een aanhangster van het veelgodendom. En met een overspelige vrouw mag slechts een overspelige man trouwen of een aanhanger van het veelgodendom. Dat is namelijk voor de gelovigen verboden.'),
(2795, 24, 4, 'Zij die eerbaar getrouwde vrouwen beschuldigen en dan niet met vier getuigen komen, geselt hen met tachtig geselslagen. Van hen zullen jullie nooit meer getuigenis aannemen; zij zijn de verdorvenen.'),
(2796, 24, 5, 'Behalve zij die daarna berouwvol zijn en zich beteren. God is vergevend en barmhartig.'),
(2797, 24, 6, 'Zij die hun echtgenotes beschuldigen en behalve zichzelf geen getuigen hebben, het getuigenis van een van hen zal zijn dat hij viermaal bij God getuigt dat hij iemand is die de waarheid zegt.'),
(2798, 24, 7, 'En de vijfde maal [moet hij uitspreken] dat Gods vloek op hem zal rusten als hij een leugenaar is.'),
(2799, 24, 8, 'En als zij viermaal bij God getuigt dat hij een leugenaar is, dan weert dat voor haar de bestraffing af.'),
(2800, 24, 9, 'En de vijfde maal [moet zij uitspreken] dat Gods toorn op haar zal rusten als hij iemand is die de waarheid zegt.'),
(2801, 24, 10, 'En zonder Gods goedgunstigheid jegens jullie en Zijn barmhartigheid en dat Hij een wijze genadegever is?'),
(2802, 24, 11, 'Zij die de laster hebben geuit zijn maar een groep uit jullie midden. Jullie moeten niet denken dat het iets slechts voor jullie is. Integendeel, het is iets goeds voor jullie. Ieder van hen wordt de zonde die hij heeft begaan aangerekend, maar hij die het meeste op zijn geweten heeft, hem wacht een geweldige bestraffing.'),
(2803, 24, 12, 'Waren de gelovige mannen en vrouwen, toen jullie het hoorden, maar weldenkend over zichzelf geweest en hadden zij maar gezegd: "Dit is duidelijke laster."'),
(2804, 24, 13, 'Waren zij maar met vier getuigen gekomen. Nu zij niet met getuigen zijn gekomen zijn zij het die bij God als leugenaars gelden.'),
(2805, 24, 14, 'En zonder Gods goedgunstigheid jegens jullie en Zijn barmhartigheid in het tegenwoordige leven en in het hiernamaals had jullie voor wat jullie zo overhaast gezegd hebben een geweldige bestraffing getroffen.'),
(2806, 24, 15, 'Toen jullie dat met jullie tongen overnamen, met jullie monden zeiden waarvan jullie geen kennis hebben en dachten dat het iets onbeduidends was, maar bij God was het afschuwelijk.'),
(2807, 24, 16, 'Hadden jullie, toen jullie het hoorden, maar gezegd: "Het is niet aan ons hierover te spreken. U zij geprezen; dit is geweldige kwaadsprekerij."'),
(2808, 24, 17, 'God spoort jullie aan nooit meer iets dergelijks te doen, als jullie gelovig zijn.'),
(2809, 24, 18, 'En God maakt jullie de tekenen duidelijk; God is wetend en wijs.'),
(2810, 24, 19, 'Zij die graag zouden willen dat gruweldaad zich onder hen die geloven verspreidt, voor hen is er een pijnlijke bestraffing in het tegenwoordige leven en in het hiernamaals. God weet en jullie weten niet.'),
(2811, 24, 20, 'En zonder Gods goedgunstigheid jegens jullie en Zijn barmhartigheid en dat God vol mededogen en barmhartig is?'),
(2812, 24, 21, 'Jullie die geloven! Volgt de voetstappen van de satan niet. En als iemand de voetstappen van de satan volgt?; hij legt jullie het gruwelijke en het verwerpelijke op. En zonder Gods goedgunstigheid jegens jullie en Zijn barmhartigheid zou niemand van jullie ooit gelouterd worden, maar God loutert wie Hij wil; God is horend en wetend.'),
(2813, 24, 22, 'Zij onder jullie die het goed en ruim hebben mogen niet zweren dat zij de verwanten, de behoeftigen en de uitgewekenen op Gods weg niets zullen geven. Zij moeten juist vergiffenis schenken en kwijtschelden. Willen jullie ook niet graag dat God jullie vergeeft? God is vergevend en barmhartig.'),
(2814, 24, 23, 'Zij die eerbaar getrouwde, niets vermoedende en gelovige vrouwen beschuldigen worden vervloekt in het tegenwoordige leven en in het hiernamaals en voor hen is er een geweldige bestraffing'),
(2815, 24, 24, 'op de dag dat hun tongen, hun handen en hun voeten tegen hen zullen getuigen over wat zij deden.'),
(2816, 24, 25, 'Op die dag zal God hun hun ware loon volledig geven en zullen zij weten dat God de duidelijke waarheid is.'),
(2817, 24, 26, 'Onbetamelijke vrouwen horen bij onbetamelijke mannen en onbetamelijke mannen horen bij onbetamelijke vrouwen. Goede vrouwen horen bij goede mannen en goede mannen horen bij goede vrouwen; zij zijn het die onschuldig worden verklaard aan wat zij zeggen. Voor hen is er vergeving en een voortreffelijke voorziening.'),
(2818, 24, 27, 'Jullie die geloven! Gaat andere huizen dan jullie eigen huizen pas binnen als jullie gevraagd hebben of het gelegen komt en hun bewoners gegroet hebben. Dat is beter voor jullie; misschien zullen jullie je laten vermanen.'),
(2819, 24, 28, 'En als jullie er niemand in vinden gaat er dan pas binnen als men jullie toestemming geeft, maar als men tot jullie zegt: "Ga terug" ga dan terug. Dat is zuiverder voor jullie; God weet wat jullie doen.'),
(2820, 24, 29, 'Het is voor jullie geen overtreding als jullie onbewoonde huizen binnengaan waarin er voor jullie iets is om te gebruiken. God weet wat jullie openlijk en in het verborgen doen.'),
(2821, 24, 30, 'Zeg tot de gelovige mannen dat zij hun ogen neerslaan en hun schaamstreek kuis bewaren. Dat is zuiverder voor hen; God is welingelicht over wat zij doen.'),
(2822, 24, 31, 'En zeg tot de gelovige vrouwen dat zij hun ogen neerslaan en hun schaamstreek kuis bewaren en dat zij hun sieraad niet openlijk tonen, behalve wat gewoon al zichtbaar is. En zij moeten sluiers over hun boezem dragen en hun sieraad niet openlijk tonen, behalve aan hun echtgenoten of hun vaders of de vaders van hun echtgenoten of hun zonen of de zonen van hun echtgenoten of hun broers of de zonen van hun broers of de zonen van hun zusters of hun vrouwen of slavinnen over wie zij beschikken of mannelijke volgelingen die geen geslachtsdrift meer hebben of de kinderen die nog niet op de schaamdelen van de vrouwen letten. En zij moeten niet met hun voeten stampen zodat men weet wat zij voor verborgen sieraad dragen. En wendt jullie berouwvol tot God, o gelovigen; misschien zal het jullie welgaan.'),
(2823, 24, 32, 'En trouwt met hen uit jullie midden die nog vrijgezel zijn en de rechtschapenen uit jullie slaven en slavinnen. Als zij arm zijn zal God hen door Zijn goedgunstigheid rijk maken; God is alomvattend en wetend.'),
(2824, 24, 33, 'En zij die geen manier vinden om te trouwen moeten kuis onthouding oefenen totdat God hen door Zijn goedgunstigheid rijk maakt. En voor hen onder de slaven waarover jullie beschikken, die de vrijbrief begeren, schrijft voor hen de vrijbrief als jullie iets goeds in hen vaststelt. En geeft hun iets van Gods bezit wat Hij aan jullie gegeven heeft. En dwingt jullie slavinnen niet tot onkuisheid, als zij eerbaar wensen te blijven, in jullie streven naar het stoffelijk gewin van het tegenwoordige leven. En als iemand hen dwingt dan is God nadat zij gedwongen zijn geweest vergevend en barmhartig.'),
(2825, 24, 34, 'En Wij hebben naar jou verduidelijkende tekenen gezonden en een voorbeeld van hen die voor jullie tijd zijn heengegaan en een aansporing voor de godvrezenden.'),
(2826, 24, 35, 'God is het licht van de hemelen en de aarde. Zijn licht lijkt bijvoorbeeld op een nis met een lamp erin. De lamp staat in een glas. Het glas is zo schitterend als een stralende ster. Zij brandt [op olie] van een gezegende boom, een olijfboom -- geen oostelijke en geen westelijke -- waarvan de olie bijna [uit zichzelf] licht geeft, ook al heeft geen vuur haar aangeraakt; licht boven licht. God leidt tot Zijn licht wie Hij wil -- God geeft vergelijkingen voor de mensen en God is alwetend --'),
(2827, 24, 36, 'in huizen waarvan God heeft toegestemd dat zij werden opgericht en waarin Zijn naam wordt vermeld. Hem prijzen daarin in de morgenstond en in de avond'),
(2828, 24, 37, 'mannen, die niet door koopwaar en handel afgeleid worden van het gedenken Gods, het verrichten van de salaat en het geven van de zakaat en die een dag vrezen waarop de harten en de ogen zich omdraaien,'),
(2829, 24, 38, 'opdat God hen zal belonen voor het beste dat zij gedaan hebben en hun van Zijn goedgunstigheid nog meer zal geven. God geeft levensonderhoud aan wie Hij wil, zonder afrekening.'),
(2830, 24, 39, 'Maar zij die ongelovig zijn, hun daden zijn als een luchtspiegeling op een vlakte. De dorstige denkt dat het water is en wanneer hij er dan gekomen is vindt hij niets. Maar daar vindt hij God, die hem dan zijn rekening volledig betaalt; God is snel met de afrekening.'),
(2831, 24, 40, 'Of [ze zijn] als duisternissen op een diepe zee die door golven bedekt wordt met nog golven erboven waarboven wolken zijn: duisternissen boven elkaar. Wanneer hij zijn hand uitsteekt kan hij hem bijna niet zien. En aan wie God geen licht geeft, voor hem is er geen licht.'),
(2832, 24, 41, 'Heb jij niet gezien dat God geprezen wordt door wie er in de hemelen en op de aarde zijn en ook door de vogels met uitgespreide vleugels? En van een ieder kent Hij zijn salaat en zijn lofprijzing; God weet wat zij doen.'),
(2833, 24, 42, 'God heeft de heerschappij over de hemelen en de aarde; bij God is de bestemming.'),
(2834, 24, 43, 'Heb jij niet gezien dat God de wolken voortstuwt, ze dan samenvoegt en dan tot een stapel maakt? Dan zie je de regen ertussenuit komen. En Hij laat uit de hemel bergen neerdalen waarin hagel is en Hij treft daarmee wie Hij wil en wendt het af van wie Hij wil. Bijna ontneemt de flits van de bliksem ervan hem het gezichtsvermogen.'),
(2835, 24, 44, 'God keert de nacht en de dag om. Daarin is een les voor hen die inzicht hebben.'),
(2836, 24, 45, 'God heeft ieder dier uit water geschapen; daaronder zijn er die op hun buik gaan, daaronder zijn er die op twee benen lopen en er zijn daaronder die op vier lopen. God schept wat Hij wil; God is almachtig.'),
(2837, 24, 46, 'Wij hebben verduidelijkende tekenen neergezonden. God wijst aan wie Hij wil de goede richting naar een juiste weg.'),
(2838, 24, 47, 'En zij zeggen: "Wij geloven in God en de gezant en wij gehoorzamen." Dan keert daarna een groep van hen zich af. Dat zijn geen gelovigen.'),
(2839, 24, 48, 'En wanneer zij tot God en Zijn gezant worden opgeroepen opdat hij tussen hen zal oordelen, dan wendt een groep van hen zich af.'),
(2840, 24, 49, 'Maar als het recht aan hun kant is, komen zij gewillig naar hem.'),
(2841, 24, 50, 'Is er in hun harten een ziekte of twijfelen zij of zijn zij bang dat God en Zijn profeet hun onrecht zullen aandoen? Welnee zij zijn het die onrecht plegen.'),
(2842, 24, 51, 'Maar wat de gelovigen zeggen wanneer zij tot God en Zijn gezant worden opgeroepen opdat hij tussen hen zal oordelen is: "Wij horen en gehoorzamen. Zij zijn het die het welgaat."'),
(2843, 24, 52, 'En wie God en Zijn gezant gehoorzaamt en voor God bevreesd is en Hem vreest, zij zijn het die triomferen.'),
(2844, 24, 53, 'Zij zweren bij God dure eden dat zij als jij hun beveelt zullen uittrekken. Zeg: "Zweert niet! Gehoorzaamheid is passend! God is welingelicht over wat jullie doen."'),
(2845, 24, 54, 'Zeg: "Gehoorzaamt God en gehoorzaamt de gezant, maar als jullie je afkeren, dan is hij alleen maar verplicht tot dat waarmee hij belast is. En jullie zijn verplicht tot dat waarmee jullie belast zijn. En als jullie hem gehoorzamen dan laten jullie je de goede richting wijzen. De gezant heeft alleen maar de plicht van de duidelijke verkondiging."'),
(2846, 24, 55, 'God heeft hun onder jullie die geloven en de deugdelijke daden doen toegezegd, dat Hij hen als opvolgers op de aarde laat komen zoals Hij hen die voor hen waren als opvolgers heeft laten komen en dat Hij voor hen hun godsdienst, die Hij voor hen goed bevonden heeft, een vaste plaats heeft gegeven en dat Hij hun in plaats van hun vrees veiligheid zal geven. Zij dienen Mij en zij voegen niets als metgezel aan Mij toe. Wie daarna nog ongelovig zijn, dat zijn de verdorvenen.'),
(2847, 24, 56, 'Verricht de salaat, geeft de zakaat en gehoorzaamt de gezant; misschien zal aan jullie barmhartigheid bewezen worden.'),
(2848, 24, 57, 'Jij mag niet denken dat zij die ongelovig zijn op de aarde iets kunnen uitrichten; hun verblijfplaats is het vuur en dat is pas een slechte bestemming!'),
(2849, 24, 58, 'Jullie die geloven! De slaven waarover jullie beschikken en zij uit jullie midden die nog niet de puberteit bereikt hebben, moeten aan jullie op drie tijden vragen of het gelegen komt: voor de salaat van de dageraad, wanneer jullie op de middag je kleren aflegt en na de avondsalaat, drie tijden namelijk waarop jullie ongekleed zijn. Het is voor jullie en voor hen geen overtreding als jullie daarna bij elkaar in en uit gaan. Zo verduidelijkt God voor jullie de tekenen; God is wetend en wijs.'),
(2850, 24, 59, 'En wanneer de kinderen uit jullie midden de puberteit bereikt hebben, dan moeten zij vragen of het gelegen komt zoals zij deden die hen voor waren. Zo verduidelijkt God voor jullie Zijn tekenen; God is wetend en wijs.'),
(2851, 24, 60, 'En voor de vrouwen die op leeftijd zijn en die niet meer verwachten te trouwen is het geen overtreding als zij hun kleren afleggen, maar dan zonder sieraad te vertonen. Maar als zij het nalaten is het beter voor hen. En God is horend en wetend.'),
(2852, 24, 61, 'De blinde treft geen blaam, de kreupele treft geen blaam en de zieke treft geen blaam noch jullie zelf als jullie in jullie [eigen] huizen eten of in de huizen van jullie vaders of in de huizen van jullie moeders of in de huizen van jullie broers of in de huizen van jullie zusters of in de huizen van jullie ooms van vaderskant of in de huizen van jullie tantes van vaderskant of in de huizen van jullie ooms van moederskant of in de huizen van jullie tantes van moederskant of waarvan jullie de sleutels bezitten of van een vriend van jullie. Het is geen overtreding voor jullie als jullie gezamenlijk eten of afzonderlijk. Wanneer jullie dus huizen binnengaan, groet dan elkaar met een gezegende en goede groet die van God komt. Zo verduidelijkt God voor jullie de tekenen; misschien zullen jullie verstandig worden.'),
(2853, 24, 62, 'De gelovigen zijn zij die in God en Zijn gezant geloven en die, wanneer zij met hem iets gezamenlijk hebben, niet weggaan voordat zij hem om toestemming gevraagd hebben. Zij die jou om toestemming vragen, dat zijn zij die in God en Zijn gezant geloven. Wanneer zij jou voor een eigen aangelegenheid om toestemming vragen, geef dan toestemming aan wie van hen jij wilt en vraag God voor hen om vergeving; God is vergevend en barmhartig.'),
(2854, 24, 63, 'Behandelt de oproep van de gezant in jullie midden niet zoals jullie oproep aan elkaar. God kent hen wel die heimelijk uit jullie midden wegsluipen. Zij die de gezant in zijn ordening tegenwerken moeten maar verontrust zijn dat hen een verzoeking zal treffen of dat hen een pijnlijke bestraffing zal treffen.'),
(2855, 24, 64, 'Zeker, van God is wat er in de hemelen en op de aarde is, Hij kent de toestand waarin jullie verkeren en op de dag dat zij tot Hem teruggebracht worden, dan zal Hij hun meedelen wat zij deden; God is alwetend.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Gezegend zij Hij die het reddend onderscheidingsmiddel tot Zijn dienaar heeft neergezonden, opdat hij voor de wereldbewoners een waarschuwer zou zijn,'),
(2857, 25, 2, 'Hij die de heerschappij over de hemelen en de aarde heeft, die zich geen kind genomen heeft, die geen metgezel in de heerschappij heeft en die alles geschapen en nauwkeurig geordend heeft.'),
(2858, 25, 3, 'Maar zij hebben zich in plaats van Hem goden genomen die zelf geschapen zijn, maar niets kunnen scheppen en die geen macht hebben om voor zichzelf tot nut of tot schade te zijn en die geen macht hebben over dood en leven, noch over herrijzenis.'),
(2859, 25, 4, 'Zij die ongelovig zijn zeggen: "Dit is slechts laster die hij verzonnen heeft en waarbij andere mensen hem geholpen hebben." Zo begaan zij onrecht en onwaarheid.'),
(2860, 25, 5, 'En zij zeggen: "Fabels van hen die er eertijds waren die hij zich heeft laten opschrijven; zij worden hem \'s ochtends en \'s avonds gedicteerd.'),
(2861, 25, 6, 'Zeg: "Hij die kent wat in de hemelen en op de aarde geheim is heeft het neergezonden; Hij is vergevend en barmhartig."'),
(2862, 25, 7, 'En zij zeggen: "Hoe komt het dat deze gezant voedsel eet en op de markten rondloopt? Had er dan niet een engel naar hem neergezonden kunnen worden om samen met hem een waarschuwer te zijn?'),
(2863, 25, 8, 'Of als er hem maar een schat toegeworpen was of als hij maar een tuin had gehad waarvan hij kon eten!" En de onrechtplegers zeggen: "Jullie volgen slechts een man die betoverd is."'),
(2864, 25, 9, 'Kijk hoe zij voor jou vergelijkingen maken; zij dwalen en kunnen dus geen weg meer vinden.'),
(2865, 25, 10, 'Gezegend zij Hij die, als Hij het wil, aan jou iets beters dan dit kan geven: tuinen waar de rivieren onderdoor stromen en kastelen kan Hij jou geven.'),
(2866, 25, 11, 'Ja zeker, zij loochenen het uur, maar Wij hebben voor hen die het uur loochenen een vuurgloed klaargemaakt.'),
(2867, 25, 12, 'Wanneer hij hen uit de verte te zien krijgt, horen zij al zijn gegrom en gesteun.'),
(2868, 25, 13, 'En wanneer zij er aaneengeketend uit geworpen worden naar een enge plaats dan roepen zij daar ach en wee.'),
(2869, 25, 14, '"Roept vandaag niet één keer ach en wee maar roept vaak ach en wee."'),
(2870, 25, 15, 'Zeg: "Is dat beter of de tuin van het eeuwige leven die aan de godvrezenden is toegezegd en die voor hen een beloning en een bestemming is?'),
(2871, 25, 16, 'Zij hebben daarin wat zij willen, terwijl zij er altijd blijven. Dat is een opeisbare toezegging waartoe jouw Heer verplicht is."'),
(2872, 25, 17, 'En op de dag dat Hij hen en wat zij in plaats van God dienen verzamelt en dan zegt: "Hebben jullie deze dienaren van Mij tot dwaling gebracht of zijn zij zelf van de weg afgedwaald?",'),
(2873, 25, 18, 'zeggen zij: "U zij geprezen! Het paste ons niet dat wij ons in plaats van U beschermheren hebben genomen, maar U hebt hen en hun vaderen laten genieten totdat zij de vermaning vergaten en verloren mensen geworden zijn."'),
(2874, 25, 19, 'Zij hebben jullie dus over wat jullie zeiden van leugens beticht. Jullie kunnen het niet afwenden en geen hulp krijgen en wie van jullie onrecht pleegt laten wij een grote straf proeven.'),
(2875, 25, 20, 'Wij hebben voor jouw tijd alleen maar gezondenen gezonden die voedsel aten en op de markten rondliepen. En Wij hebben sommigen van jullie tot een verzoeking voor anderen gemaakt [om te zien] of jullie geduldig kunnen volharden; jouw Heer is doorziend. *'),
(2876, 25, 21, 'En zij die niet hopen op de ontmoeting met Ons zeggen: "Hadden dan de engelen niet tot ons neergezonden kunnen worden? Of zagen wij onze Heer maar!" Zij zijn ingebeeld en hebben grote minachting.'),
(2877, 25, 22, 'Op de dag dat zij de engelen zien, op die dag is er geen goed nieuws voor de boosdoeners en zij zullen zeggen: "Dat is volstrekt ontoelaatbaar!"'),
(2878, 25, 23, 'En Wij wenden Ons tot de daden die zij gedaan hebben en maken ze tot verstrooid stof.'),
(2879, 25, 24, 'Zij die in de tuin thuishoren hebben op die dag een betere verblijfplaats en een mooiere rustplaats.'),
(2880, 25, 25, 'En op de dag dat de hemel in wolken uiteensplijt en de engelen werkelijk neergezonden worden,'),
(2881, 25, 26, 'op die dag heeft de Erbarmer de ware heerschappij; het is een dag die voor de ongelovigen moeilijk is.'),
(2882, 25, 27, 'En op de dag dat de onrechtvaardige op zijn handen bijt, terwijl hij zegt: "Was ik maar samen met de gezant op weg gegaan.'),
(2883, 25, 28, 'Wee mij, had ik die en die maar niet als vriend genomen.'),
(2884, 25, 29, 'Hij heeft mij van de vermaning laten afdwalen." De satan is namelijk iemand die de mens in de steek laat.'),
(2885, 25, 30, 'En de gezant zegt: "Mijn Heer, mijn volk houdt deze Koran voor iets wat gemeden moet worden."'),
(2886, 25, 31, 'En zo hebben Wij voor elke profeet een vijand uit de boosdoeners gemaakt, maar jouw Heer is goed genoeg als leider en helper.'),
(2887, 25, 32, 'En zij die ongelovig zijn zeggen: "Had de Koran niet als één geheel tot hem neergezonden kunnen worden?" Het is zoals het is, opdat Wij jouw hart versterken en Wij hebben hem achtereenvolgens voorgedragen.'),
(2888, 25, 33, 'En zij komen niet tot jou met een voorbeeld of Wij komen tot jou met de waarheid en een betere verklaring.'),
(2889, 25, 34, 'Zij die op hun gezichten [ter aarde geworpen] tot de hel verzameld zullen worden, zij zijn het die de slechtste plaats hebben en het verst afgedwaald zijn van de weg.'),
(2890, 25, 35, 'Wij hebben toch aan Moesa het boek gegeven en zijn broer Haroen met hem als medewerker aangesteld.'),
(2891, 25, 36, 'En Wij zeiden: "Gaat heen naar de mensen die Onze tekenen loochenen." En Wij vernietigden hen toen geheel.'),
(2892, 25, 37, 'En de mensen van Noeh hebben Wij laten verdrinken, toen zij de gezanten van leugens betichtten en Wij hebben hen voor de mensen tot een teken gemaakt. En Wij hebben voor de onrechtplegers een pijnlijke bestraffing klaargemaakt.'),
(2893, 25, 38, 'Ook de \'Aad en de Thamoed en de mensen van de bron en veel generaties daartussen.'),
(2894, 25, 39, 'En voor elk hebben Wij voorbeelden ter vergelijking gegeven en elk hebben Wij geheel vernietigd.'),
(2895, 25, 40, 'Zij zijn toch bij de stad voorbij gekomen waarop de slechte regen geregend had; hebben zij haar dan niet gezien? Welnee, zij verwachten geen herrijzenis.'),
(2896, 25, 41, 'En wanneer zij jou zien, drijven zij slechts de spot met jou: "Is dit hem die God als gezant heeft opgeroepen?'),
(2897, 25, 42, 'Bijna had hij ons van onze goden laten afdwalen, als wij niet aan hen vastgehouden hadden." Zij zullen, wanneer zij de bestraffing zien, wel weten wie er het verst afgedwaald is van de weg.'),
(2898, 25, 43, 'Heb jij hem gezien die zijn grillen tot zijn god maakt? Ben jij over hem voogd?'),
(2899, 25, 44, 'Of denk jij dat de meesten van hen horen of verstand hebben? Zij zijn slechts als het vee. Ja zeker, zij zijn het verst afgedwaald van de weg.'),
(2900, 25, 45, 'Heb jij niet gezien naar jouw Heer, hoe Hij de schaduw heeft uitgestrekt? En als Hij het gewild had, dan had Hij gemaakt dat hij stilstond. Vervolgens maken Wij de zon tot een aanwijzer ervoor.'),
(2901, 25, 46, 'Daarop trekken Wij hem langzamerhand naar Ons terug.'),
(2902, 25, 47, 'En Hij is het die voor jullie de nacht tot kleding en de slaap om uit te rusten gemaakt heeft en Hij heeft de dag gemaakt om op te staan.'),
(2903, 25, 48, 'En Hij is het die de winden als verkondigers van goed nieuws voor Zijn barmhartigheid uit zendt. En Wij laten uit de hemel rein water neerdalen'),
(2904, 25, 49, 'om daarmee een dode streek tot leven te brengen en om daarmee veel van wat Wij geschapen hebben, vee en mensen, te drinken te geven.'),
(2905, 25, 50, 'En Wij hebben het onder hen uiteengezet opdat zij vermaand zouden worden, maar de meeste mensen willen alleen maar ongelovig zijn.'),
(2906, 25, 51, 'En als Wij gewild hadden, dan hadden Wij in iedere stad een waarschuwer laten opstaan.'),
(2907, 25, 52, 'Gehoorzaam dan de ongelovigen niet, maar stel je tegen hen hevig te weer.'),
(2908, 25, 53, 'En Hij is het die de beide zeeën vrij heeft laten stromen, de een zoet en fris en de ander zout en pekelig en Hij heeft tussen beide een versperring gemaakt en een volstrekte ontoegankelijkheid.'),
(2909, 25, 54, 'En Hij is het die uit water een mens geschapen heeft en hem dan gemaakt heeft tot bloed- en aanverwant; jouw Heer is vrijmachtig.'),
(2910, 25, 55, 'En zij dienen in plaats van God iets wat hen niet nut en niet schaadt. En de ongelovige verleent hulp tegen zijn Heer.'),
(2911, 25, 56, 'Maar Wij hebben jou slechts als verkondiger en waarschuwer gezonden.'),
(2912, 25, 57, 'Zeg: "Ik vraag jullie daarvoor geen loon, behalve als iemand de weg tot God wil inslaan."'),
(2913, 25, 58, 'En stel je vertrouwen op de levende die niet sterft en prijs Zijn lof; Hij is goed genoeg als iemand die welingelicht is over de zonden van Zijn dienaren.'),
(2914, 25, 59, 'Hij die de hemelen en de aarde en wat er tussen beide is in zes dagen geschapen heeft en die zich toen op de troon vestigde, hij is de Erbarmer. Vraag maar aan iemand die welingelicht is.'),
(2915, 25, 60, 'Wanneer men tot hen zegt: "Buigt jullie eerbiedig neer voor de Erbarmer" dan zeggen zij: "Wat is dat, de Erbarmer? Zullen wij ons eerbiedig voor iets neerbuigen, alleen maar omdat jij het ons beveelt?" En het vermeerdert slechts hun afkerigheid."'),
(2916, 25, 61, 'Gezegend zij Hij die in de hemel sterrenbeelden heeft gemaakt en die daarin een heldere lamp en een verlichtende maan heeft gemaakt.'),
(2917, 25, 62, 'En hij is het die de dag en de nacht als elkaars aflossing gemaakt heeft voor wie zich wenst te laten vermanen of die dankbaar wenst te zijn.'),
(2918, 25, 63, 'En de dienaren van de Erbarmer zijn zij die bescheiden op de aarde rondwandelen en wanneer de onwetenden hen toespreken zeggen: "Vrede".'),
(2919, 25, 64, 'En zij die de nacht doorbrengen terwijl zij zich eerbiedig voor hun Heer neerbuigen en terwijl zij rechtop staan.'),
(2920, 25, 65, 'En zij die zeggen: "Onze Heer, wend van ons de bestraffing van de hel af; de bestraffing daarmee blijft voortdurend."'),
(2921, 25, 66, 'Het is een slechte verblijfplaats en standplaats.'),
(2922, 25, 67, 'En zij die niet overdrijven en niet te zuinig zijn wanneer zij bijdragen geven, maar daartussen het midden houden.'),
(2923, 25, 68, 'En zij die naast God geen andere god aanroepen en die niemand doden -- wat God verboden heeft -- behalve volgens het recht en die geen overspel plegen; wie dat doet zal moeten boeten.'),
(2924, 25, 69, 'Voor hem zal op de opstandingsdag de bestraffing verdubbeld worden en vernederd zal hij er altijd in blijven.'),
(2925, 25, 70, 'Behalve hij die berouw toont, gelooft en deugdelijk handelt; zij zijn het voor wie God hun slechte daden tegen goede inwisselt; God is vergevend en barmhartig.'),
(2926, 25, 71, 'En wie berouw heeft en deugdelijk handelt, die wendt zich dus werkelijk berouwvol tot God.'),
(2927, 25, 72, 'En zij die geen vals getuigenis geven en die wanneer zij bij geklets voorbijkomen er waardig aan voorbijgaan.'),
(2928, 25, 73, 'En zij die wanneer zij met de tekenen van hun Heer vermaand worden er niet doof en blind bij neervallen.'),
(2929, 25, 74, 'En zij die zeggen: "Onze Heer, schenk ons dat wij aan onze echtgenotes en nakomelingen vreugde beleven en maak ons tot een voorbeeld voor de godvrezenden."'),
(2930, 25, 75, 'Zij zijn het die met de [feest]zaal beloond worden omdat zij geduldig volhard hebben. En men zal hen daarin met een begroeting en met "vrede" tegemoet treden.'),
(2931, 25, 76, 'Zij zullen daarin altijd blijven; goed is zij als verblijfplaats en standplaats.'),
(2932, 25, 77, 'Zeg: "Mijn Heer zal zich niet om jullie bekommeren als jullie Hem niet aanroepen, maar jullie hebben het toch geloochend, dus zal het onafwendbaar zijn."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'T[aa?] S[ien] M[iem].'),
(2934, 26, 2, 'Dit zijn de tekenen van het duidelijke boek.'),
(2935, 26, 3, 'Misschien zul jij jezelf van smart nog ombrengen omdat zij niet gelovig zijn.'),
(2936, 26, 4, 'Als Wij wilden, hadden Wij uit de hemel tot hen een teken neergezonden, zodat hun nekken ervoor gebogen zouden blijven.'),
(2937, 26, 5, 'Geen nieuwe vermaning komt er tot hen van de Erbarmer of zij wenden zich ervan af.'),
(2938, 26, 6, 'Zij hebben ze immers geloochend, maar de mededelingen over dat waarmee zij de spot dreven zullen tot hen komen.'),
(2939, 26, 7, 'Of hebben zij niet naar de aarde gezien, hoeveel voortreffelijke soorten Wij erop hebben laten groeien?'),
(2940, 26, 8, 'Daarin is zeker een teken, maar de meesten van hen zijn niet gelovig.'),
(2941, 26, 9, 'Echter, jouw Heer is werkelijk de machtige, de barmhartige.'),
(2942, 26, 10, 'Toen jouw Heer Moesa riep: "Ga naar de mensen die onrecht plegen,'),
(2943, 26, 11, 'het volk van Fir\'aun [om te zien] of zij niet godvrezend zullen worden."'),
(2944, 26, 12, 'Hij zei: "Mijn Heer ik ben bang dat zij mij van leugens zullen betichten,'),
(2945, 26, 13, 'en mijn hart is benauwd en mijn tong komt niet los; ontbied Haroen dus maar.'),
(2946, 26, 14, 'Zij kunnen mij ook nog op een zonde aanspreken en ik ben dus bang dat zij mij zullen doden."'),
(2947, 26, 15, 'Hij zei: "Welnee, ga dus beiden met Onze tekenen heen. Wij zijn met jullie en luisteren."'),
(2948, 26, 16, 'Toen kwamen zij bij Fir\'aun en zeiden: "Wij zijn de gezant van de Heer van de wereldbewoners.'),
(2949, 26, 17, 'Laat de Israëlieten met ons gaan."'),
(2950, 26, 18, 'Hij zei: "Hebben wij jou niet als kind grootgebracht en heb jij niet jaren van jouw leven bij ons gewoond?'),
(2951, 26, 19, 'En jij hebt gedaan wat je gedaan hebt en bent nu een van de ondankbaren."'),
(2952, 26, 20, 'Hij zei: "Dan heb ik het gedaan toen ik nog een van de dwalenden was.'),
(2953, 26, 21, 'En ik ben van jullie weggevlucht toen ik bang voor jullie geworden was, maar mijn Heer heeft mij oordeelskracht geschonken en mij tot een van de gezondenen gemaakt.'),
(2954, 26, 22, 'Is dat dan genade die jij aan mij bewezen hebt, dat jij de Israëlieten geknecht hebt?"'),
(2955, 26, 23, 'Fir\'aun zei: "En wat is dan de Heer van de wereldbewoners?"'),
(2956, 26, 24, 'Hij zei: "De Heer van de hemelen en de aarde en wat er tussen beide is, als jullie ervan overtuigd zijn."'),
(2957, 26, 25, 'Hij zei tegen wie er om hem heen stonden: "Horen jullie het niet?"'),
(2958, 26, 26, 'Hij zei: "Hij is jullie Heer en de Heer van jullie voorvaderen."'),
(2959, 26, 27, 'Hij zei: "Jullie gezant die tot jullie gezonden is, is bezeten."'),
(2960, 26, 28, 'Hij zei: "De Heer van het oosten en het westen en wat er tussen beide is, als jullie verstand hebben."'),
(2961, 26, 29, 'Hij zei: "Als jij je een ander dan mij als god neemt dan zal ik je zeker tot een van de gevangenen maken."'),
(2962, 26, 30, 'Hij zei: "Maar wat dan als ik jou iets duidelijks breng?"'),
(2963, 26, 31, 'Hij zei: "Breng het maar tevoorschijn als jij behoort tot hen die gelijk hebben."'),
(2964, 26, 32, 'Toen wierp hij zijn staf en het was opeens duidelijk een slang.'),
(2965, 26, 33, 'En hij strekte zijn hand uit en zij was opeens wit voor de toeschouwers.'),
(2966, 26, 34, 'Hij zei tot de voornaamsten rondom hem: "Zie, dit is een kundig tovenaar.'),
(2967, 26, 35, 'Hij wil jullie met toverij uit jullie land verdrijven. Wat gebieden jullie dan?"'),
(2968, 26, 36, 'Zij zeiden: "Houd hem en zijn broer nog wat tegen en zend bijeenroepers in de steden uit,'),
(2969, 26, 37, 'om elke kundige tovenaar tot jou te brengen."'),
(2970, 26, 38, 'En de tovenaars verzamelden zich op de afgesproken tijd van een vastgestelde dag.'),
(2971, 26, 39, 'En men zei tot de mensen: "Zullen jullie ook bijeenkomen?'),
(2972, 26, 40, 'Misschien zullen wij de tovenaars volgen als zij de overwinnaars zijn."'),
(2973, 26, 41, 'Toen de tovenaars dan kwamen zeiden zij tot Fir\'aun: "Er is voor ons toch wel een beloning als wij overwinnen?"'),
(2974, 26, 42, 'Hij zei: "Ja, jullie zullen dan tot hen behoren die in [mijn] nabijheid zijn toegelaten."'),
(2975, 26, 43, 'Moesa zei tot hen: "Werpt wat jullie te werpen hebben."'),
(2976, 26, 44, 'Zij wierpen dus hun touwen en hun staven en zeiden: "Bij de macht van Fir\'aun, wij zijn werkelijk de overwinnaars."'),
(2977, 26, 45, 'Daarop wierp Moesa zijn staf. Die verslond toen wat zij de mensen voortoverden.'),
(2978, 26, 46, 'Toen werden de tovenaars neergeworpen om zich eerbiedig neer te buigen.'),
(2979, 26, 47, 'Zij zeiden: "Wij geloven in de Heer van de wereldbewoners,'),
(2980, 26, 48, 'de Heer van Moesa en Haroen."'),
(2981, 26, 49, 'Hij zei: "Jullie geloven aan hem voordat ik jullie toestemming gegeven heb. Hij is zeker jullie meester die jullie heeft leren toveren. Dan zullen jullie het weten; Ik zal jullie de handen en voeten aan tegenovergestelde kanten afhouwen en jullie allen tezamen kruisigen."'),
(2982, 26, 50, 'Zij zeiden: "Het deert [ons] niet, wij zullen tot onze Heer omkeren.'),
(2983, 26, 51, 'Wij begeren dat onze Heer ons onze fouten vergeeft, omdat wij [nu] de eersten van de gelovigen zijn."'),
(2984, 26, 52, 'En Wij openbaarden aan Moesa: "Vertrek \'s nachts met Mijn dienaren, want jullie zullen achtervolgd worden."'),
(2985, 26, 53, 'Toen zond Fir\'aun in de steden bijeenroepers uit:'),
(2986, 26, 54, '"Dezen zijn maar een klein groepje'),
(2987, 26, 55, 'en toch maken zij ons woedend.'),
(2988, 26, 56, 'Maar wij zijn allen op onze hoede."'),
(2989, 26, 57, 'Toen lieten Wij hen uit tuinen en bronnen wegtrekken,'),
(2990, 26, 58, 'bij schatten weg en uit een voortreffelijke positie.'),
(2991, 26, 59, 'Zo is het. En Wij lieten de Israëlieten het beërven.'),
(2992, 26, 60, 'Maar zij achtervolgden hen bij zonsopgang.'),
(2993, 26, 61, 'En toen de beide troepenmachten elkaar zagen zeiden de mensen van Moesa: "Wij worden ingehaald."'),
(2994, 26, 62, 'Hij zei: "Welnee, mijn Heer is met mij; Hij zal mij de goede richting wijzen."'),
(2995, 26, 63, 'En Wij openbaarden aan Moesa: "Sla met jouw staf op de zee." Toen spleet zij en elke kant was als een geweldige berg.'),
(2996, 26, 64, 'En Wij lieten de anderen daar dichtbij komen.'),
(2997, 26, 65, 'En Wij redden Moesa en wie er met hem waren, allen tezamen.'),
(2998, 26, 66, 'Toen lieten Wij de anderen verdrinken.'),
(2999, 26, 67, 'Daarin is zeker een teken, maar de meesten van hen zijn geen gelovigen.'),
(3000, 26, 68, 'Echter, jouw Heer is de machtige, de barmhartige.'),
(3001, 26, 69, 'En lees hun de mededeling over Ibrahiem voor,'),
(3002, 26, 70, 'toen hij tot zijn vader en zijn volk zei: "Wat dienen jullie?"'),
(3003, 26, 71, 'Zij zeiden: "Wij dienen afgoden en Wij zullen hen blijven dienen."'),
(3004, 26, 72, 'Hij zei: "Horen zij jullie als jullie roepen?'),
(3005, 26, 73, 'Of zijn zij van nut voor jullie of schaden zij?"'),
(3006, 26, 74, 'Zij zeiden: "Welnee, maar wij hebben gemerkt dat onze vaderen al zo deden."'),
(3007, 26, 75, 'Hij zei: "Hebben jullie dan wel gezien wat jullie aan het dienen waren?'),
(3008, 26, 76, 'Jullie en je verre voorvaders?'),
(3009, 26, 77, 'Zij zijn mij tot vijand, maar niet de Heer van de wereldbewoners'),
(3010, 26, 78, 'die mij geschapen heeft en mij op het goede pad brengt'),
(3011, 26, 79, 'en Hij is het die mij spijzigt en te drinken geeft'),
(3012, 26, 80, 'en wanneer ik ziek ben, dan geneest Hij mij'),
(3013, 26, 81, 'en Hij is het die mij laat sterven en dan [weer] tot leven brengt'),
(3014, 26, 82, 'en Hij is het van wie ik begeer dat Hij mij op de oordeelsdag mijn fout vergeeft.'),
(3015, 26, 83, 'Mijn Heer, schenk mij oordeelskracht en voeg mij bij de rechtschapenen.'),
(3016, 26, 84, 'En verschaf mij een betrouwbare reputatie bij het nageslacht.'),
(3017, 26, 85, 'En maak dat ik behoor tot hen die de tuin van de gelukzaligheid beërven.'),
(3018, 26, 86, 'En vergeef mijn vader, hij behoort tot hen die dwalen.'),
(3019, 26, 87, 'En maak mij niet te schande op de dag dat zij worden opgewekt.'),
(3020, 26, 88, 'Op de dag dat bezit niet baat, noch zonen.'),
(3021, 26, 89, 'Behalve wie tot God komt met een zuiver hart."'),
(3022, 26, 90, 'En de tuin wordt dicht bij de godvrezenden gebracht.'),
(3023, 26, 91, 'En het hellevuur wordt onthuld voor de misleiden.'),
(3024, 26, 92, 'En tot hen wordt gezegd: "Waar is het wat jullie gewoon waren te dienen'),
(3025, 26, 93, 'in plaats van God? Komen zij jullie helpen of zullen zij zichzelf helpen?"'),
(3026, 26, 94, 'En zij zullen erin gekieperd worden, zij en de misleiden'),
(3027, 26, 95, 'en de troepen van de duivel, allen tezamen.'),
(3028, 26, 96, 'Zij zeggen terwijl zij er met elkaar twisten:'),
(3029, 26, 97, '"Bij God, wij verkeerden wel in duidelijke dwaling'),
(3030, 26, 98, 'dat wij jullie vergeleken met de Heer van de wereldbewoners.'),
(3031, 26, 99, 'En het waren alleen maar de boosdoeners die ons tot dwaling gebracht hebben.'),
(3032, 26, 100, 'En nu hebben wij geen bemiddelaars,'),
(3033, 26, 101, 'noch een echte vriend.'),
(3034, 26, 102, 'Als er een terugkeer voor ons was dan zouden wij tot de gelovigen behoren."'),
(3035, 26, 103, 'Daarin is zeker een teken, maar de meesten van hen zijn niet gelovig.'),
(3036, 26, 104, 'Jouw Heer is echter werkelijk de machtige, de barmhartige.'),
(3037, 26, 105, 'Het volk van Noeh betichtte de gezondenen van leugens.'),
(3038, 26, 106, 'Toen hun broeder Noeh tot hen zei: "Willen jullie niet godvrezend zijn?'),
(3039, 26, 107, 'Ik ben voor jullie een betrouwbare gezant.'),
(3040, 26, 108, 'Vreest dan God en gehoorzaamt mij.'),
(3041, 26, 109, 'En ik vraag jullie daarvoor geen loon. Slechts de Heer van de wereldbewoners is belast met mijn loon.'),
(3042, 26, 110, 'Vreest dan God en gehoorzaamt mij." *'),
(3043, 26, 111, 'Zij zeiden: "Zullen wij jou geloven terwijl jij alleen maar gevolgd wordt door de allerverachtelijksten?"'),
(3044, 26, 112, 'Hij zei: "En hoe kan ik weten wat zij gedaan hebben?'),
(3045, 26, 113, 'Mijn Heer is belast met de afrekening met hen, als jullie het maar beseffen.'),
(3046, 26, 114, 'En ik jaag hen die geloven niet weg.'),
(3047, 26, 115, 'Ik ben slechts een duidelijke waarschuwere'),
(3048, 26, 116, 'Zij zeiden: "Als jij niet ophoudt, Noeh, dan behoor jij bij hen die gestenigd worden."'),
(3049, 26, 117, 'Hij zei: "Mijn Heer, mijn volk beticht mij van leugens.'),
(3050, 26, 118, 'Doe dus tussen mij en hen uitspraak en red mij en die gelovigen die met mij zijn."'),
(3051, 26, 119, 'Toen redden Wij hem en wie er met hem in het volbeladen schip waren.'),
(3052, 26, 120, 'Daarna lieten Wij toen de achtergeblevenen verdrinken.'),
(3053, 26, 121, 'Daarin is zeker een teken, maar de meesten van hen zijn niet gelovig.'),
(3054, 26, 122, 'Jouw Heer is echter werkelijk de machtige, de barmhartige.'),
(3055, 26, 123, 'De \'Aad betichtten de gezondenen van leugens.'),
(3056, 26, 124, 'Toen hun broeder Hoed tot hen zei: "Willen jullie niet godvrezend zijn?'),
(3057, 26, 125, 'Ik ben voor jullie een betrouwbare gezant.'),
(3058, 26, 126, 'Vreest dan God en gehoorzaamt mij.'),
(3059, 26, 127, 'En ik vraag jullie daarvoor geen loon. Slechts de Heer van de wereldbewoners is belast met mijn loon.'),
(3060, 26, 128, 'Willen jullie op elke heuvel voor de grap een teken bouwen'),
(3061, 26, 129, 'en bouwwerken maken alsof jullie er altijd zullen blijven?'),
(3062, 26, 130, 'En wanneer jullie toeslaan slaan jullie als geweldenaars toe.'),
(3063, 26, 131, 'Vreest dan God en gehoorzaamt mij.'),
(3064, 26, 132, 'En vreest Hem die jullie heeft versterkt met wat jullie weten.'),
(3065, 26, 133, 'Hij heeft jullie versterkt met vee en zonen'),
(3066, 26, 134, 'en met tuinen en bronnen.'),
(3067, 26, 135, 'Ik vrees voor jullie de bestraffing van een geweldige dag."'),
(3068, 26, 136, 'Zij zeiden: "Het maakt ons niet uit of jij ons aanspoort of niet behoort tot hen die aansporen.'),
(3069, 26, 137, 'Dit is slechts een gewoonte van hen die er eertijds waren.'),
(3070, 26, 138, 'En wij zijn het niet die gestraft worden."'),
(3071, 26, 139, 'Toen betichtten zij hem van leugens en vernietigden Wij hen. Daarin is zeker een teken, maar de meesten van hen zijn niet gelovig.'),
(3072, 26, 140, 'Jouw Heer is echter werkelijk de machtige, de barmhartige.'),
(3073, 26, 141, 'De Thamoed betichtten de gezondenen van leugens.'),
(3074, 26, 142, 'Toen hun broeder Salih tot hen zei: "Willen jullie niet godvrezend zijn?'),
(3075, 26, 143, 'Ik ben voor jullie een betrouwbare gezant.'),
(3076, 26, 144, 'Vreest dan God en gehoorzaamt mij.'),
(3077, 26, 145, 'En ik vraag jullie daarvoor geen loon. Slechts de Heer van de wereldbewoners is belast met mijn loon.'),
(3078, 26, 146, 'Zullen jullie dan in veiligheid gelaten worden in wat er hier is,'),
(3079, 26, 147, 'te midden van tuinen en bronnen'),
(3080, 26, 148, 'en landbouwgewassen en palmen waarvan de bloeiwijze zacht is?'),
(3081, 26, 149, 'En zullen jullie in de bergen vaardig huizen uithouwen?'),
(3082, 26, 150, 'Vreest dan God en gehoorzaamt mij.'),
(3083, 26, 151, 'En gehoorzaamt niet het bevel van onmatigen,'),
(3084, 26, 152, 'die Op de aarde verderf zaaien en die geen orde op zaken stellen."'),
(3085, 26, 153, 'Zij zeiden: "Maar jij behoort tot hen die betoverd zijn.'),
(3086, 26, 154, 'Jij bent slechts een mens zoals wij. Breng dan een teken, als jij behoort tot hen die gelijk hebben."'),
(3087, 26, 155, 'Hij zei: "Dit is een kameelmerrie die haar tijd om te drinken heeft en jullie hebben je tijd om te drinken op een vastgestelde dag.'),
(3088, 26, 156, 'En treft haar niet met kwaad, want dan zal de bestraffing van een geweldige dag jullie grijpen."'),
(3089, 26, 157, 'Maar zij sneden haar hielpezen door en kregen er \'s morgens spijt van.'),
(3090, 26, 158, 'Toen greep de bestraffing hen. Daarin is zeker een teken, maar de meesten van hen zijn niet gelovig.'),
(3091, 26, 159, 'Echter, jouw Heer is werkelijk de machtige, de barmhartige.'),
(3092, 26, 160, 'Het volk van Loet betichtte de gezondenen van leugens.'),
(3093, 26, 161, 'Toen hun broeder Loet tot hen zei: "Willen jullie niet godvrezend zijn?'),
(3094, 26, 162, 'Ik ben voor jullie een betrouwbare gezant.'),
(3095, 26, 163, 'Vreest dan God en gehoorzaamt mij.'),
(3096, 26, 164, 'En ik vraag jullie daarvoor geen loon. Slechts de Heer van de wereldbewoners is belast met mijn loon.'),
(3097, 26, 165, 'Zullen jullie tot de mannen onder de wereldbewoners gaan?'),
(3098, 26, 166, 'En echtgenotes die God voor jullie geschapen heeft verwaarlozen? Ja zeker, jullie zijn mensen die overtredingen begaan."'),
(3099, 26, 167, 'Zij zeiden: "Als jij niet ophoudt, Loet, dan behoor jij bij hen die verdreven worden."'),
(3100, 26, 168, 'Hij zei: "Ik behoor tot hen die jullie handelwijze verafschuwen.'),
(3101, 26, 169, 'Mijn Heer, red mij en mijn huisgenoten van wat zij doen."'),
(3102, 26, 170, 'En Wij redden hem en zijn huisgenoten, allen tezamen,'),
(3103, 26, 171, 'behalve een oude vrouw te midden van hen die achterbleven.'),
(3104, 26, 172, 'Toen vernietigden Wij de anderen.'),
(3105, 26, 173, 'En Wij lieten regen op hen vallen; slecht was die regen voor hen die gewaarschuwd waren.'),
(3106, 26, 174, 'Daarin is zeker een teken, maar de meesten van hen zijn niet gelovig.'),
(3107, 26, 175, 'Jouw Heer is echter werkelijk de machtige, de barmhartige.'),
(3108, 26, 176, 'De mensen van het kreupelbos betichtten de gezondenen van leugens.'),
(3109, 26, 177, 'Toen Sjoe\'aib tot hen zei: "Willen jullie niet godvrezend zijn?'),
(3110, 26, 178, 'Ik ben voor jullie een betrouwbare gezant.'),
(3111, 26, 179, 'Vreest dan God en gehoorzaamt mij.'),
(3112, 26, 180, 'En ik vraag jullie daarvoor geen loon. Slechts de Heer van de wereldbewoners is belast met mijn loon. *'),
(3113, 26, 181, 'En geeft volle maat en behoort niet tot hen die verlies veroorzaken.'),
(3114, 26, 182, 'En weegt met de juiste weegschaal.'),
(3115, 26, 183, 'En doet de mensen niet tekort in de dingen die van hen zijn en veroorzaakt geen ellende op de aarde door verderf te zaaien.'),
(3116, 26, 184, 'En vreest Hem die zowel jullie als de schepselen die er eertijds waren geschapen heeft."'),
(3117, 26, 185, 'Zij zeiden: "Maar jij behoort tot hen die betoverd zijn.'),
(3118, 26, 186, 'En jij bent slechts een mens zoals wij en wij denken dat jij behoort tot hen die liegen.'),
(3119, 26, 187, 'Laat dan stukken uit de hemel op ons neervallen als jij behoort tot hen die gelijk hebben."'),
(3120, 26, 188, 'Hij zei: "Mijn Heer weet het best wat jullie doen."'),
(3121, 26, 189, 'Maar zij betichtten hem van leugens. Toen greep hen de bestraffing van de dag van de stapelwolk. Dat was de bestraffing van een geweldige dag.'),
(3122, 26, 190, 'Daarin is zeker een teken, maar de meesten van hen zijn niet gelovig.'),
(3123, 26, 191, 'Jouw Heer is echter werkelijk de machtige, de barmhartige.'),
(3124, 26, 192, 'Dit is een neerzending van de Heer van de wereldbewoners --'),
(3125, 26, 193, 'daarmee is de betrouwbare geest neergedaald'),
(3126, 26, 194, 'tot jouw hart, opdat jij tot de waarschuwers zou behoren --'),
(3127, 26, 195, 'in duidelijke Arabische taal.'),
(3128, 26, 196, 'En hij staat in de Zoeboer van hen die er eertijds waren.'),
(3129, 26, 197, 'Is het dan geen teken voor hen dat de geleerden van de Israëlieten hem kennen?'),
(3130, 26, 198, 'Als Wij hem tot een vreemdtalige neergezonden hadden'),
(3131, 26, 199, 'en die hem aan hen had voorgelezen, dan hadden zij er niet in geloofd.'),
(3132, 26, 200, 'Zo hebben Wij hem in de harten van de boosdoeners laten gaan.'),
(3133, 26, 201, 'Zij geloven er niet in totdat zij de pijnlijke bestraffing zien'),
(3134, 26, 202, 'die onverwachts tot hen komt, zonder dat zij het beseffen.'),
(3135, 26, 203, 'Dan zeggen zij: "Wordt aan ons uitstel verleend?"'),
(3136, 26, 204, 'Willen zij dan Onze bestraffing verhaasten?'),
(3137, 26, 205, 'Hoe zie jij het dan als Wij hun jarenlang vruchtgebruik hebben gegeven,'),
(3138, 26, 206, 'dat dan tot hen komt wat hun was aangezegd?'),
(3139, 26, 207, 'Dan baat hun het vruchtgebruik niet dat zij genieten.'),
(3140, 26, 208, 'En Wij hebben geen enkele stad vernietigd zonder dat zij waarschuwers had gehad'),
(3141, 26, 209, 'ter vermaning; Wij hebben namelijk geen onrecht gepleegd.'),
(3142, 26, 210, 'De satans zijn er niet mee neergedaald.'),
(3143, 26, 211, 'Het past hun niet en zij kunnen het niet.'),
(3144, 26, 212, 'Zij zijn van het horen uitgesloten.'),
(3145, 26, 213, 'Roep dus naast God geen andere god aan, anders zul jij tot de bestraften behoren.'),
(3146, 26, 214, 'En waarschuw jouw naaste familieleden.'),
(3147, 26, 215, 'En wees ontvankelijk voor die gelovigen die jou volgen.'),
(3148, 26, 216, 'En als zij aan jou ongehoorzaam zijn zeg dan: "Ik heb niets te maken met wat jullie doen."'),
(3149, 26, 217, 'En stel je vertrouwen op de machtige, de barmhartige,'),
(3150, 26, 218, 'die jou ziet wanneer jij rechtop staat'),
(3151, 26, 219, 'en wanneer jij je heen en weer wendt met hen die zich eerbiedig neerbuigen.'),
(3152, 26, 220, 'Hij is de horende, de wetende.'),
(3153, 26, 221, 'Zal Ik jullie meedelen tot wie de satans neerdalen?'),
(3154, 26, 222, 'Zij dalen neer tot elke zondige lasteraar.'),
(3155, 26, 223, 'Dezen luisteren scherp en de meesten van hen zijn leugenaars.'),
(3156, 26, 224, 'En de dichters, de misleiden volgen hen.'),
(3157, 26, 225, 'Zie jij niet dat zij in elke vallei rondzwerven'),
(3158, 26, 226, 'en dat zij zeggen wat zij niet doen?'),
(3159, 26, 227, 'Maar niet zij die geloven, de deugdelijke daden doen en God veelvuldig gedenken en die zich verweren nadat hun onrecht is aangedaan. En zij die onrecht plegen zullen te weten komen wat voor omkeer zij zullen meemaken.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'T[aa?] S[ien]. Dit zijn de tekenen van de Koran en een duidelijk boek.'),
(3161, 27, 2, 'Een leidraad en goed nieuws voor de gelovigen,'),
(3162, 27, 3, 'die de salaat verrichten en de zakaat geven, terwijl zij van het hiernamaals vast overtuigd zijn.'),
(3163, 27, 4, 'Voor hen die niet in het hiernamaals geloven hebben Wij hun daden mooi laten lijken, zodat zij doorgaan met dwalen.'),
(3164, 27, 5, 'Zij zijn het voor wie de vreselijke bestraffing is en zij zijn in het hiernamaals de grootste verliezers.'),
(3165, 27, 6, 'En aan jou wordt de Koran overgebracht van de kant van een wijze en wetende.'),
(3166, 27, 7, 'Toen Moesa tot zijn mensen zei: "Ik heb een vuur waargenomen, ik zal jullie er bericht van brengen of ik breng jullie een brandende fakkel; misschien zullen jullie [erdoor] verwarmd worden."'),
(3167, 27, 8, 'Toen hij daar kwam werd hem toegeroepen: "Gezegend is Hij die in het vuur is en wie er rondom is en God zij geprezen, de Heer van de wereldbewoners.'),
(3168, 27, 9, 'O Moesa, Ik ben het, God, de machtige, de wijze."'),
(3169, 27, 10, 'En: "Werp je staf." Maar toen hij zag dat hij zich bewoog als een slang keerde hij hem de rug toe en draaide zich niet meer om. "O Moesa, wees niet bang. Bij Mij zijn de gezondenen niet bang,'),
(3170, 27, 11, 'behalve hij die onrecht pleegt. Maar als hij het na de slechte daad door iets goeds vervangt, dan ben Ik vergevend en barmhartig.'),
(3171, 27, 12, 'En steek je hand in je boezem, dan zal zij wit, maar zonder iets slechts tevoorschijn komen als een van negen tekenen voor Fir\'aun en zijn volk. Zij zijn verdorven mensen."'),
(3172, 27, 13, 'Toen dan Onze tekenen duidelijk zichtbaar tot hen kwamen zeiden zij: "Dit is duidelijk toverij."'),
(3173, 27, 14, 'En zij verwierpen ze uit onrechtmatigheid en hovaardij, hoewel zij zelf overtuigd waren. Kijk dan hoe het einde was van de verderfbrengers.'),
(3174, 27, 15, 'En Wij hebben aan Dawoed en Soelaimaan kennis gegeven en zij zeiden: "Lof zij God die ons boven velen van Zijn gelovige dienaren heeft verkozen."'),
(3175, 27, 16, 'En Soelaimaan werd Dawoeds erfgenaam en hij zei: "O mensen, aan ons is de spraak van de vogels onderwezen en aan ons is van alles gegeven; dit is een duidelijke goedgunstigheid."'),
(3176, 27, 17, 'En voor Soelaimaan werden zijn troepen verzameld -- djinn, mensen en vogels -- en zij werden in het gelid gezet.'),
(3177, 27, 18, 'Toen zij dan in de vallei van de mieren kwamen zei een mier: "O mieren, gaat jullie woningen binnen opdat Soelaimaan en zijn troepen jullie niet zonder het te merken vertrappen."'),
(3178, 27, 19, 'Toen glimlachte hij omdat hij om haar woorden moest lachen en zei: "Mijn Heer, spoor mij aan dat ik voor Uw genade die U mij en mijn ouders geschonken hebt dank betuig en dat ik iets deugdelijks doe dat U bevalt en laat mij met Uw rechtschapen dienaren in Uw barmhartigheid binnengaan."'),
(3179, 27, 20, 'En hij keek of er bij de vogels een miste en zei: "Hoe komt het dat ik de hop niet zie? Of is die bij de afwezigen?'),
(3180, 27, 21, 'Ik zal hem streng bestraffen of ik zal hem slachten of hij moet mij een duidelijke machtiging brengen."'),
(3181, 27, 22, 'Hij bleef toen niet lang meer weg en zei: "Ik omvat [met mijn kennis] wat jij niet omvat en ik ben uit Saba? naar jou gekomen met een vaststaand bericht.'),
(3182, 27, 23, 'Ik heb gemerkt dat een vrouw over hen heerst aan wie van alles is gegeven en die een geweldige troon heeft.'),
(3183, 27, 24, 'Ik heb gemerkt dat zij en haar volk zich eerbiedig voor de zon neerbuigen in plaats van voor God, dat de satan hun daden voor hen mooi heeft laten lijken en hun de weg versperd heeft; zij volgen dus het goede pad niet,'),
(3184, 27, 25, 'zodat zij zich niet eerbiedig voor God neerbuigen die het verborgene in de hemelen en de aarde tevoorschijn brengt en die weet wat jullie in het verborgen, en wat jullie openlijk doen.'),
(3185, 27, 26, 'God, er is geen god dan hij, de Heer van de geweldige troon." -- *'),
(3186, 27, 27, 'Hij zei: "We zullen zien of jij gelijk hebt of dat jij tot de leugenaars behoort.'),
(3187, 27, 28, 'Breng deze brief van mij weg en overhandig hem aan hen. Wend je dan van hen af en kijk wat zij terugzeggen."'),
(3188, 27, 29, 'Zij zei: "Raad van voornaamsten! Aan mij is een voortreffelijke brief overhandigd.'),
(3189, 27, 30, 'Hij is van Soelaimaan en hij luidt: In de naam van God, de erbarmer, de barmhartige.'),
(3190, 27, 31, 'Weest niet hovaardig tegenover mij en komt tot mij als [mensen] die zich [aan God] hebben overgegeven."'),
(3191, 27, 32, 'Zij zei: "Raad van voornaamsten! Geeft mij uitsluitsel over wat ik zal beschikken; ik tref geen beschikking zonder dat jullie er getuige van zijn."'),
(3192, 27, 33, 'Zij zeiden: "Wij bezitten een strijdmacht en kunnen hevig geweld uitoefenen, maar de beschikking is aan jou. Kijk maar wat je wilt bevelen."'),
(3193, 27, 34, 'Zij zei: "Wanneer koningen een stad binnentrekken brengen zij haar tot verderf en maken de machtigen onder haar mensen tot onderworpelingen. Zo doen zij.'),
(3194, 27, 35, 'Ik zal naar hen een geschenk zenden en kijken waarmee de gezondenen terugkeren."'),
(3195, 27, 36, 'Toen hij dan bij Soelaimaan kwam zei deze: "Willen jullie mij met bezit overladen? Wat God mij gegeven heeft is beter dan wat jullie mij geven. Jullie echter verheugen je over jullie geschenk.'),
(3196, 27, 37, 'Keer naar hen terug, wij zullen met troepen tot hen komen waartegen geen weerstand geboden kan worden en wij zullen hen er als onderworpelingen die onderdanig zijn uit verdrijven."'),
(3197, 27, 38, 'Hij zei: "Raad van voornaamsten! Wie van jullie brengt mij haar troon voordat zij tot mij komen als [mensen] die zich [aan God] hebben overgegeven?"'),
(3198, 27, 39, 'Een \'ifriet van de djinn zei: "Ik zal hem jou brengen voordat jij van jouw plaats opstaat. Ik heb er de kracht voor en ben betrouwbaar."'),
(3199, 27, 40, 'Hij die kennis van het boek had zei: "Ik breng hem je voordat jij met je oog hebt geknipperd." En toen hij hem daar bij zich zag staan zei hij: "Dit is een gunst van mijn Heer om mij op de proef te stellen of ik dank zal betuigen of ondankbaar ben. En wie dank betuigt doet dat in zijn eigen voordeel en wie ondankbaar is? mijn Heer is behoefteloos en edel."'),
(3200, 27, 41, 'Hij zei: "Maak haar troon voor haar onherkenbaar, dan zullen wij kijken of haar de goede richting gewezen is of dat zij behoort bij hen die het goede pad niet volgen."'),
(3201, 27, 42, 'Toen zij dan kwam zei men tot haar: "Is dit jouw troon?" Zij zei: "Het is alsof het hem is." [Soelaimaan zei:] "Aan ons was voor haar tijd al kennis gegeven en wij hadden ons [aan God] overgegeven."'),
(3202, 27, 43, 'Maar wat zij in plaats van God dienden had haar [van het goede pad] afgehouden. Zij behoorde tot ongelovige mensen.'),
(3203, 27, 44, 'Men zei tot haar: "Treed het paleis binnen." En toen zij het zag dacht zij dat het diep water was en zij ontblootte haar benen. Hij zei: "Het is een met glasplaten bekleed paleis." Zij zei: "Mijn Heer, ik heb mijzelf onrecht aangedaan en ik geef mij samen met Soelaimaan over aan God, de Heer van de wereldbewoners."'),
(3204, 27, 45, 'En Wij hebben tot de Thamoed hun broeder Salih gezonden: "Dient God!" Toen waren zij opeens twee groepen die met elkaar twistten.'),
(3205, 27, 46, 'Hij zei: "O mensen, waarom wensen jullie het slechte eerder dan het goede te verhaasten? Vroegen jullie God maar om vergeving; misschien zal aan jullie barmhartigheid bewezen worden."'),
(3206, 27, 47, 'Zij zeiden: "Wij zien in jou en wie met jou zijn een slecht voorteken." Hij zei: "Jullie slechte voorteken is bij God. Ja zeker, jullie zijn mensen die in verzoeking gebracht worden."'),
(3207, 27, 48, 'En er was in de stad een bende van negen die op de aarde verderf zaaiden en die geen orde op zaken stelden.'),
(3208, 27, 49, 'Zij zeiden: "Zweert met elkaar bij God dat wij hem en zijn huisgenoten \'s nachts zullen overvallen en dan zullen wij tegen zijn beschermer zeggen: Wij waren geen getuige van zijn vernietiging en wij spreken de waarheid."'),
(3209, 27, 50, 'Zij maakten plannen en Wij maakten plannen zonder dat zij het beseften.'),
(3210, 27, 51, 'Kijk dan hoe het einde was van hun plannenmakerij: Wij vernietigden hen en hun volk allen tezamen!'),
(3211, 27, 52, 'Dat zijn hun huizen, verlaten en vervallen omdat zij onrecht pleegden. Daarin is een teken voor mensen die weten.'),
(3212, 27, 53, 'Maar Wij redden hen die geloofden en godvrezend waren.'),
(3213, 27, 54, 'En Loet, toen hij tot zijn volk zei: "Zullen jullie een gruweldaad begaan hoewel jullie het doorzien?'),
(3214, 27, 55, 'Zullen jullie uit begeerte tot de mannen gaan in plaats van tot de vrouwen? Ja zeker, jullie zijn mensen die niets weten." *'),
(3215, 27, 56, 'En het antwoord van zijn volk was slechts dat zij zeiden: "Verdrijft de familie van Loet uit jullie stad, want zij zijn mensen die zich rein houden."'),
(3216, 27, 57, 'Toen redden Wij hem en zijn familie, behalve zijn vrouw; Wij hebben verordend dat zij tot de achterblijvers behoorde.'),
(3217, 27, 58, 'En Wij lieten regen op hen vallen; slecht was die regen voor hen die gewaarschuwd waren.'),
(3218, 27, 59, 'Zeg: "Lof zij God en vrede met Zijn dienaren die Hij heeft uitverkoren." Is God beter of wat zij als metgezellen aan Hem toeschrijven?'),
(3219, 27, 60, 'Of wie heeft de hemelen en de aarde geschapen en voor jullie water uit de hemel laten neerdalen? Wij hebben er toch kostelijke boomgaarden mee laten groeien! Jullie zouden de bomen er niet van kunnen laten groeien. Is er naast God nog een god? Welnee, zij zijn mensen die in de verkeerde richting gaan.'),
(3220, 27, 61, 'Of wie heeft de aarde tot een vaste grond gemaakt, er rivieren doorheen gemaakt, stevige bergen voor haar gemaakt en tussen beide zeeën een versperring gemaakt? Is er naast God nog een god? Welnee, de meesten van hen weten het niet.'),
(3221, 27, 62, 'Of wie verhoort de in nood gezetene, als hij tot Hem bidt en neemt het kwade weg en maakt jullie tot opvolgers op de aarde. Is er naast God nog een god? Hoe weinig laten jullie je vermanen!'),
(3222, 27, 63, 'Of wie leidt jullie in de duisternis van land en zee en wie zendt de winden als verkondigers van goed nieuws voor Zijn barmhartigheid uit? Is er naast God nog een god? God is verheven boven wat zij aan Hem als metgezellen toevoegen.'),
(3223, 27, 64, 'Wie begint met de schepping en herhaalt haar dan en wie voorziet in jullie onderhoud vanuit de hemel en de aarde? Is er naast God nog een god? Zeg: "Brengt jullie bewijs dan, als jullie gelijk hebben."'),
(3224, 27, 65, 'Zeg: "Niemand kent in de hemelen en de aarde het verborgene, behalve God en zij beseffen niet wanneer zij worden opgewekt.'),
(3225, 27, 66, 'Welnee, hun kennis over het hiernamaals is onzeker. Ja zeker, zij verkeren erover in twijfel. Ja zeker, zij zijn er blind voor."'),
(3226, 27, 67, 'Zij die ongelovig zijn zeggen: "Wanneer wij en onze vaderen stof geworden zijn, zullen wij dan echt tevoorschijn gebracht worden?'),
(3227, 27, 68, 'Dat is ons en onze vaderen vroeger toegezegd, maar dat zijn slechts fabels van hen die er eertijds waren."'),
(3228, 27, 69, 'Zeg: "Reist op de aarde rond en kijkt hoe het einde was van de boosdoeners.'),
(3229, 27, 70, 'En weest niet bedroefd over hen en weest niet benauwd over wat zij aan listen beramen."'),
(3230, 27, 71, 'Zij zeggen: "Wanneer zal deze aanzegging zich voordoen, als jullie gelijk hebben?"'),
(3231, 27, 72, 'Zeg: "Misschien dat een deel van wat jullie wensen te verhaasten jullie al op de hielen zit."'),
(3232, 27, 73, 'En jouw Heer is vol van goedgunstigheid jegens de mensen, maar de meesten van hen betuigen geen dank.'),
(3233, 27, 74, 'Jouw Heer weet wat hun harten koesteren en wat zij openlijk doen.'),
(3234, 27, 75, 'En er is niets verborgens in de hemel en de aarde of het staat in een duidelijk boek.'),
(3235, 27, 76, 'Deze Koran vertelt aan de Israëlieten het meeste van dat waarover zij het oneens zijn.'),
(3236, 27, 77, 'En hij is een leidraad en barmhartigheid voor de gelovigen.'),
(3237, 27, 78, 'Jouw Heer zal met Zijn oordeel tussen hen oordelen; Hij is de machtige, de wijze.'),
(3238, 27, 79, 'En stel je vertrouwen op God; jij steunt op de duidelijke waarheid.'),
(3239, 27, 80, 'Jij kunt de doden niet laten horen, noch kun jij de doven de oproep laten horen wanneer zij de rug toekeren.'),
(3240, 27, 81, 'En jij brengt de blinden niet van hun dwaalweg af. Jij kunt slechts hen die in Onze tekenen geloven doen horen; zij hebben zich [aan God] overgegeven. *'),
(3241, 27, 82, 'En wanneer de uitspraak over hen komt laten Wij voor hen een beest uit de aarde tevoorschijn komen dat tot hen spreekt, omdat de mensen van Onze tekenen niet overtuigd waren.'),
(3242, 27, 83, 'En de dag waarop Wij uit iedere gemeenschap een groep verzamelen van hen die Onze tekenen loochenen en die dan in het gelid worden gezet.'),
(3243, 27, 84, 'En wanneer zij dan komen, zegt Hij: "Hebben jullie Mijn tekenen geloochend, hoewel jullie ze niet met kennis konden omvatten? Of wat hebben jullie gedaan?"'),
(3244, 27, 85, 'En de uitspraak komt over hen omdat zij onrecht pleegden; zij zullen dus niet spreken.'),
(3245, 27, 86, 'Hebben zij niet gezien dat Wij de nacht gemaakt hebben, opdat zij erin rusten, en de dag om te zien? Daarin zijn zeker tekenen voor mensen die geloven.'),
(3246, 27, 87, 'En op de dag dat op de bazuin geblazen wordt worden wie er in de hemelen en wie er op de aarde zijn verschrikt, behalve zij van wie God het anders wil. En allen komen onderdanig tot Hem.'),
(3247, 27, 88, 'En de bergen waarvan je denkt dat ze vaststaan zie je voorbijgaan als wolken. Het is het werk van God die in alles bedreven is. Hij is welingelicht over wat jullie doen.'),
(3248, 27, 89, 'Voor hen die met een goede daad komen, is er iets beters dan dat; zij zijn op die dag veilig voor de schrik.'),
(3249, 27, 90, 'Zij die met een slechte daad komen worden halsoverkop in het vuur gestort: "Wordt aan jullie iets anders vergolden dan wat jullie gedaan hebben?"'),
(3250, 27, 91, 'Maar mij is bevolen de Heer van deze plaats te dienen die haar heilig verklaard heeft en aan wie alles behoort. En mij is bevolen tot hen die zich [aan God] overgeven te behoren'),
(3251, 27, 92, 'en de Koran voor te lezen. Wie zich dan de goede richting laat wijzen, die volgt het goede pad slechts in zijn eigen voordeel en als iemand dwaalt zeg dan: "Ik behoor slechts tot de waarschuwers."'),
(3252, 27, 93, 'En zeg: "Lof zij God, Hij zal jullie Zijn tekenen laten zien zodat jullie ze zullen kennen. En God let goed op wat jullie doen."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'T[aa?] S[ien] M[iem].'),
(3254, 28, 2, 'Dit zijn de tekenen van het duidelijke boek.'),
(3255, 28, 3, 'Wij lezen jou naar waarheid mededelingen voor over Moesa en Fir\'aun voor mensen die geloven.'),
(3256, 28, 4, 'Fir\'aun had de overhand in het land en maakte de mensen ervan tot groeperingen, waarvan hij een groep onderdrukte doordat hij hun zonen afslachtte en alleen hun vrouwen in leven liet; hij behoorde tot de verderfbrengers.'),
(3257, 28, 5, 'Maar Wij wensten hun die onderdrukt werden in het land een gunst te bewijzen, hen tot voorgangers te maken, hen tot erfgenamen te maken,'),
(3258, 28, 6, 'hun macht op de aarde te geven en Fir\'aun en Hamaan en hun troepen te tonen waar zij verontrust over waren.'),
(3259, 28, 7, 'En Wij openbaarden aan de moeder van Moesa: "Zoog hem en wanneer jij bang voor hem bent, werp hem dan in de zee en wees niet bang en niet bedroefd; Wij brengen hem naar jou terug en Wij maken hem tot een van de gezondenen."'),
(3260, 28, 8, 'Toen namen de mensen van Fir\'aun hem op, opdat hij voor hen een vijand en [reden tot] droefheid zou worden. Fir\'aun en Hamaan en hun legers waren immers zondaars.'),
(3261, 28, 9, 'En de vrouw van Fir\'aun zei: "Jij en ik zullen vreugde aan hem beleven. Dood hem niet. Misschien dat hij ons tot nut is of dat wij hem als kind aannemen." Maar zij beseften het niet.'),
(3262, 28, 10, 'En het hart van Moesa\'s moeder werd ontroostbaar. Bijna had zij het van hem bekend gemaakt, als Wij haar hart niet versterkt hadden, opdat zij tot de gelovigen behoorde.'),
(3263, 28, 11, 'En zij zei tot zijn zuster: "Ga achter hem aan." Zij hield dus van terzijde een oog op hem zonder dat zij het merkten. *'),
(3264, 28, 12, 'Wij hadden namelijk van tevoren minnen voor hem verboden. En zij zei: "Zal ik jullie een familie wijzen die hem voor jullie kan verzorgen en die over hem waakt?"'),
(3265, 28, 13, 'Zo brachten Wij hem naar zijn moeder terug opdat zij vreugde aan hem zou beleven en niet bedroefd zou zijn en opdat zij zou weten dat de toezegging van God waar is; maar de meesten van hen weten het niet.'),
(3266, 28, 14, 'Toen hij volgroeid en welgevormd was gaven Wij hem oordeelskracht en kennis. Zo belonen Wij hen die goed doen.'),
(3267, 28, 15, 'En hij kwam de stad binnen op een tijd dat haar mensen er niet op letten en hij vond er twee mannen die aan het vechten waren; de een was van zijn groepering en de ander van zijn vijand en hij die van zijn groepering was riep hem te hulp tegen hem die van zijn vijand was. Moesa sloeg hem en bracht hem om. Hij zei: "Dit is werk van de satan; hij is een verklaarde vijand die tot dwaling brengt."'),
(3268, 28, 16, 'Hij zei: "Mijn Heer, ik heb mijzelf onrecht aangedaan. Vergeef mij dus." En Hij vergaf hem; Hij is de vergevende, de barmhartige.'),
(3269, 28, 17, 'Hij zei: "Mijn Heer, omdat U mij genade geschonken hebt, zal ik de boosdoeners geen bijstand meer verlenen."'),
(3270, 28, 18, '\'s Morgens begon hij bang te worden en om zich heen te kijken. En daar was hij die hem de vorige dag om hulp had gevraagd die [weer] om hulp schreeuwde. Moesa zei tot hem: "Jij bent duidelijk misleid."'),
(3271, 28, 19, 'Maar toen hij wilde inslaan op hem die een vijand van hen beiden was zei hij: "O Moesa, wil jij mij ook doden zoals jij gisteren iemand gedood hebt? Jij wenst alleen maar een geweldenaar in het land te zijn en wenst niet bij hen die herstel brengen te behoren."'),
(3272, 28, 20, 'En er kwam een man uit het verste deel van de stad aanrennen die zei: "O Moesa, de voornaamsten beraden zich over jou om jou te doden. Ga dus weg. Ik ben iemand die jou goede raad geeft."'),
(3273, 28, 21, 'Hij ging er dus bang en om zich heen kijkend weg. Hij zei: "Mijn Heer, red mij van de mensen die onrecht plegen."'),
(3274, 28, 22, 'Toen hij in de richting van Madjan ging zei hij: "Misschien dat mijn Heer mij op de correcte weg zal brengen."'),
(3275, 28, 23, 'En toen hij bij het water van Madjan kwam vond hij daar een gemeenschap van mensen die [hun vee] te drinken gaven en hij vond naast hen twee vrouwen die [hun vee] terughielden. Hij zei: "Wat is er met jullie beiden?" Zij zeiden: "Wij kunnen [ons vee] pas te drinken geven als de herders weggaan. Onze vader is namelijk een zeer oude man."'),
(3276, 28, 24, 'Toen gaf hij [hun vee] te drinken en ging terug in de schaduw en zei: "Mijn Heer, ik heb behoefte aan het goede dat U tot mij neerzendt."'),
(3277, 28, 25, 'Toen kwam een van hen beiden bedeesd naar hem toe. Zij zei: "Mijn vader nodigt je uit om je ervoor te belonen dat jij ons [vee] te drinken gegeven hebt." Toen hij bij hem kwam en hem het verhaal vertelde zei deze: "Wees niet bang, je bent aan mensen die onrecht plegen ontkomen."'),
(3278, 28, 26, 'Een van de twee vrouwen zei: "Mijn vader, neem hem toch in dienst. Je kunt het best iemand in dienst nemen die sterk en betrouwbaar is."'),
(3279, 28, 27, 'Hij zei: "Ik wil je graag met een van mijn beide dochters hier laten trouwen op voorwaarde dat jij acht jaar bij mij in dienst komt. En als je er tien vol maakt dan is dat je eigen zaak; ik wil het jou niet te moeilijk maken. Als God het wil zul je merken dat ik tot de rechtschapenen behoor."'),
(3280, 28, 28, 'Hij zei: "Dat is dan een afspraak tussen mij en jou. Welke van de twee termijnen ik ook vol maak, ik kan er niet op aangesproken worden. En God staat garant voor wat wij zeggen."'),
(3281, 28, 29, 'Toen Moesa de termijn vol gemaakt had en met zijn huisgenoten wegtrok nam hij op de zijkant van de berg een vuur waar en hij zei tot zijn mensen: "Blijft staan, ik heb een vuur waargenomen, misschien zal ik jullie er een bericht van brengen of een toorts van vuur; misschien zullen jullie [erdoor] verwarmd worden."'),
(3282, 28, 30, 'Toen hij daar kwam werd hem van de rechterkant van de vallei op het door God gezegende veld vanuit de boom toegeroepen: "O Moesa, Ik ben het, God, de Heer van de wereldbewoners."'),
(3283, 28, 31, 'En: "Werp je staf." Maar toen hij zag dat die zich bewoog als een slang keerde hij hem de rug toe en draaide zich niet meer om. "O Moesa, kom dichterbij en wees niet bang; jij behoort bij hen die in veiligheid zijn.'),
(3284, 28, 32, 'Stop je hand in je boezem dan zal zij wit, maar zonder iets slechts, tevoorschijn komen en trek je van schrik uitgestrekte arm weer naar je toe. Het zijn namelijk twee bewijzen van jouw Heer bestemd voor Fir\'aun en zijn raad van voornaamsten. Zij zijn verdorven mensen."'),
(3285, 28, 33, 'Hij zei: "Mijn Heer, ik heb iemand van hen gedood en ben dus bang dat zij mij doden.'),
(3286, 28, 34, 'En mijn broer Haroen is welsprekender dan ik; zend hem als hulp met mij mee om wat ik zeg te bevestigen, want ik ben bang dat zij mij van leugens zullen betichten."'),
(3287, 28, 35, 'Hij zei: "Wij zullen jou door jouw broer kracht verlenen en Wij zullen jullie beiden machtiging geven. Zij zullen jullie dus niet te na kunnen komen. Met Onze tekenen zullen jullie beiden en zij die jullie volgen de overwinnaars zijn."'),
(3288, 28, 36, 'Toen Moesa met Onze duidelijke tekenen tot hen kwam zeiden zij: "Dit is slechts toverij die verzonnen is. Zo iets hebben wij niet gehoord bij onze vaderen die er eertijds waren."'),
(3289, 28, 37, 'Maar Moesa zei: "Mijn Heer weet het best wie er met de leidraad van Zijn kant komt en voor wie de uiteindelijke woning zal zijn. Het zal de onrechtplegers niet welgaan."'),
(3290, 28, 38, 'En Fir\'aun zei: "Raad van voornaamsten, ik ken voor jullie geen god buiten mij. Hamaan, stel dus leem bloot aan vuur en maak voor mij een toren; misschien kan ik dan opklimmen naar de god van Moesa. Maar ik vermoed dat hij tot de leugenaars behoort."'),
(3291, 28, 39, 'En hij en zijn troepen waren onterecht hoogmoedig op de aarde en zij dachten dat zij niet tot Ons teruggebracht zouden worden.'),
(3292, 28, 40, 'En wij grepen hem en zijn troepen en wierpen hen in de zee. Kijk dan hoe het einde was van de onrechtplegers.'),
(3293, 28, 41, 'En Wij maakten hen tot voorgangers die oproepen tot het vuur. En op de opstandingsdag zullen zij geen hulp krijgen.'),
(3294, 28, 42, 'En Wij hebben hen in dit tegenwoordige leven door een vloek laten achtervolgen en op de opstandingsdag zullen zij tot de verafschuwden behoren.'),
(3295, 28, 43, 'Wij hebben aan Moesa, nadat Wij de eerdere generaties vernietigd hadden, het boek gegeven als inzichtelijke bewijzen voor de mensen en als leidraad en als barmhartigheid. Misschien zullen zij zich laten vermanen.'),
(3296, 28, 44, 'En jij was niet op de westkant toen Wij de beschikking aan Moesa uitvaardigden en jij behoorde niet tot de getuigen.'),
(3297, 28, 45, 'Maar Wij hebben generaties laten ontstaan en hun leven duurde lang. En jij verbleef niet bij de mensen van Madjan om Onze tekenen aan hen voor te lezen, maar Wij waren het die [gezanten] uitzonden.'),
(3298, 28, 46, 'En jij was niet op de zijkant van de berg toen Wij riepen. Maar het is uit barmhartigheid van jouw Heer dat jij er bent om mensen te waarschuwen tot wie voor jouw tijd geen waarschuwer gekomen was; misschien zullen zij zich laten vermanen.'),
(3299, 28, 47, 'En het is alleen maar opdat zij, als hen onheil treft voor wat hun handen eerder gedaan hebben, niet kunnen zeggen: "Onze Heer, had U niet een gezant tot ons kunnen zenden, zodat wij Uw tekenen hadden kunnen volgen en bij de gelovigen hadden behoord?"'),
(3300, 28, 48, 'Toen dan de waarheid van Ons tot hen kwam zeiden zij: "Had hem niet hetzelfde gegeven kunnen worden als wat aan Moesa gegeven is?" Waren zij dan niet ongelovig aan wat vroeger aan Moesa gegeven was? Zij zeiden: "Twee tovenaars die elkaar bijstaan." En zij zeiden: "Wij hechten aan niets geloof."'),
(3301, 28, 49, 'Zeg: "Komt dan met een boek dat van God komt en dat beter de goede richting wijst dan die beiden. Dan zal ik het volgen, als jullie gelijk hebben."'),
(3302, 28, 50, 'Maar als zij jullie geen gehoor geven, weet dan dat zij slechts hun neigingen volgen. En wie dwaalt er meer dan hij die zonder een leidraad van God zijn neiging volgt; God brengt de mensen die onrecht plegen niet op het goede pad. *'),
(3303, 28, 51, 'En Wij hebben hun het woord in opeenvolging doen toekomen; misschien laten zij zich vermanen.'),
(3304, 28, 52, 'Zij aan wie Wij hiervoor het boek gegeven hebben geloven erin.'),
(3305, 28, 53, 'En als het aan hen wordt voorgelezen zeggen zij: "Wij geloven erin. Het is de waarheid van onze Heer; wij hadden ons hiervoor reeds [aan God] overgegeven."'),
(3306, 28, 54, 'Zij zijn het aan wie hun loon tweemaal gegeven zal worden, omdat zij geduldig hebben volhard. En met het goede weren zij het kwade af en van wat Wij hun voor hun levensonderhoud gegeven hebben geven zij bijdragen.'),
(3307, 28, 55, 'En wanneer zij geklets horen dan mijden zij het en zij zeggen: "Wij hebben onze daden en jullie hebben jullie daden. Vrede zij met jullie! Wij zoeken geen omgang met de onwetenden."'),
(3308, 28, 56, 'Jij kunt wie jij lief hebt niet op het goede pad brengen, maar God brengt wie Hij wil op het goede pad en Hij kent hen die het goede pad volgen het best.'),
(3309, 28, 57, 'En zij zeggen: "Als wij met jou de leidraad volgen dan worden wij uit ons land weggerukt." Hebben Wij hun dan niet over een heilige en veilige plaats macht gegeven waar van Onze kant allerlei vruchten als levensonderhoud bijeengebracht worden? Maar de meesten van hen weten het niet.'),
(3310, 28, 58, 'En hoeveel steden die prat gingen op hun manier van leven hebben Wij al niet vernietigd! Daar zijn dan hun woonhuizen, waarin na hen slechts weinigen wonen. En Wij waren het die erfden.'),
(3311, 28, 59, 'Maar jouw Heer heeft de steden niet vernietigd voordat Hij in hun hoofdstad een gezant opgeroepen had, die aan hen Onze tekenen voorlas. En Wij hebben de steden slechts vernietigd als hun bewoners onrechtplegers waren.'),
(3312, 28, 60, 'Wat aan jullie ook gegeven is, het is het genot van het tegenwoordige leven en de praal ervan, maar wat van God komt is beter en onvergankelijker. Zullen jullie dan niet tot verstand komen?'),
(3313, 28, 61, 'Is dan iemand aan wie Wij een goede toezegging gedaan hebben en die haar ook zal ontvangen, als iemand die Wij het vruchtgebruik van het tegenwoordige leven hebben laten genieten en die dan op de opstandingsdag bij de voorgeleiden behoort?'),
(3314, 28, 62, 'En op de dag dat Hij hen roept en zegt: "Waar zijn dan Mijn [zogenaamd goddelijke] metgezellen waarvan jullie het bestaan beweerden?"'),
(3315, 28, 63, 'zeggen zij tegen wie de uitspraak zich bewaarheid heeft: "Onze Heer, diegenen die wij misleid hebben, hebben wij misleid zoals wijzelf misleid waren. Wij wijzen [hen] ten overstaan van U af. Zij hebben niet ons gediend."'),
(3316, 28, 64, 'En er wordt gezegd: "Roept jullie [zogenaamd goddelijke] metgezellen aan." Dan roepen zij hen aan, maar zij worden door hen niet verhoord. En zij zien de bestraffing. Hadden zij het goede pad maar gevolgd.'),
(3317, 28, 65, 'En op de dag dat Hij hen roept en zegt: "Wat hebben jullie de gezondenen geantwoord?"'),
(3318, 28, 66, 'Dan zijn hun op die dag de mededelingen ontschoten en zij vragen elkaar er ook niet naar.'),
(3319, 28, 67, 'Maar wat hem betreft die berouw toont, gelooft en deugdelijk handelt, die zal misschien behoren tot hen die het welgaat.'),
(3320, 28, 68, 'En jouw Heer schept wat Hij wil en Hij kiest uit. De keuze komt hun niet toe. God zij geprezen! En verheven is Hij boven wat zij aan Hem als metgezellen toevoegen.'),
(3321, 28, 69, 'En jouw Heer weet wat hun harten verbergen en wat zij openlijk doen.'),
(3322, 28, 70, 'En Hij is God. Er is geen god dan Hij. Hem zij lof in het tegenwoordige bestaan en het hiernamaals. Hem komt het oordeel toe en tot Hem worden jullie teruggebracht.'),
(3323, 28, 71, 'Zeg: "Hoe zien jullie het? Als God voor jullie de nacht permanent zou maken tot aan de opstandingsdag, welke god is er dan, anders dan God, die jullie licht zal brengen? Horen jullie dan niet?"'),
(3324, 28, 72, 'Zeg: "Hoe zien jullie het? Als God voor jullie de dag permanent zou maken tot aan de opstandingsdag, welke god is er dan, anders dan God, die jullie een nacht zal brengen waarin jullie kunnen rusten? Hebben jullie dan geen inzicht?"'),
(3325, 28, 73, 'Door Zijn barmhartigheid heeft Hij voor jullie de nacht en de dag gemaakt om er in te rusten en om een gunst van Hem na te streven. En misschien zullen jullie dank betuigen.'),
(3326, 28, 74, 'En op de dag dat Hij hen roept en zegt: "Waar zijn dan Mijn [zogenaamd goddelijke] metgezellen waarvan jullie het bestaan beweerden?"'),
(3327, 28, 75, 'En Wij nemen uit iedere gemeenschap een getuige en dan zeggen Wij: "Levert jullie bewijs maar." Dan weten zij dat de waarheid God toebehoort en dat zij kwijt zijn geraakt wat zij verzonnen hebben.'),
(3328, 28, 76, 'Karoen behoorde tot het volk van Moesa en hij gedroeg zich onrechtmatig tegenover hen. En Wij hadden hem zoveel schatten gegeven dat zijn sleutels met moeite door een groep sterke mannen gedragen zouden kunnen worden. Toen zijn volk tot hem zei: "Verheug je niet zo. God bemint hen niet die zich [ongegrond] verheugen.'),
(3329, 28, 77, 'En streef met wat God jou gegeven heeft naar de laatste woning en vergeet jouw aandeel aan de tegenwoordige wereld niet. Doe goed zoals God aan jou goed heeft gedaan en streef niet naar verderf op de aarde. God bemint de verderfzaaiers niet."'),
(3330, 28, 78, 'Hij zei: "Het is mij gegeven op grond van kennis die ik heb." Wist hij dan niet dat God voor zijn tijd generaties heeft vernietigd die sterker waren dan hij en die meer bijeengebracht hadden? Maar de boosdoeners worden niet langdurig over hun zonden ondervraagd.'),
(3331, 28, 79, 'Hij kwam toen in zijn praalkleding naar buiten, naar zijn volk. Zij die het tegenwoordige leven wensten zeiden: "Ach hadden wij toch hetzelfde als wat aan Karoen gegeven is. Hij is wel iemand met geweldig geluk."'),
(3332, 28, 80, 'Maar zij aan wie kennis gegeven was zeiden: "Wee jullie! Gods beloning is beter voor wie gelooft en deugdelijk handelt. En het wordt slechts aan hen die geduldig volharden aangeboden."'),
(3333, 28, 81, 'Toen lieten Wij de aarde met hem en zijn huis wegzinken en er was voor hem geen groep [mensen] die hem in plaats van God konden helpen; hij behoorde niet tot hen aan wie hulp geboden werd.'),
(3334, 28, 82, 'En zij die de vorige dag nog zijn plaats gewenst hadden zeiden \'s morgens: "O wee, God voorziet ruimschoots in het levensonderhoud van wie van Zijn dienaren Hij wil en ook met mate. Als God ons niet een gunst had bewezen, dan had Hij ons laten wegzinken. O wee, het gaat de ongelovigen niet wel."'),
(3335, 28, 83, 'Dat is de laatste woning. Wij geven haar aan hen die geen hovaardij op de aarde wensen en geen verdorvenheid. En het [goede] uiteinde komt de godvrezenden toe.'),
(3336, 28, 84, 'Voor hem die met een goede daad komt is er iets beters dan dat. Voor hem die met een slechte daad komt? aan hen die slechte daden begaan hebben wordt slechts vergolden wat zij gedaan hebben.'),
(3337, 28, 85, 'Hij die deze Koran voor jou tot een verplichting gemaakt heeft brengt jou terug naar een plaats van terugkomst. Zeg: "Mijn Heer weet het best wie er met de leidraad komt en wie er in duidelijke dwaling verkeert."'),
(3338, 28, 86, 'Jij had niet verwacht dat het boek aan jou overgebracht zou worden. Het is enkel uit barmhartigheid van jouw Heer. Wees dus niet iemand die de ongelovigen bijstand verleent.'),
(3339, 28, 87, 'En zij moeten jou niet van Gods tekenen afhouden nadat zij naar jou zijn neergezonden. En roep op tot jouw Heer en behoor zeker niet tot de veelgodendienaars.'),
(3340, 28, 88, 'Roep dus naast God geen andere god aan. Er is geen god dan Hij. Alles gaat teniet, maar Zijn aangezicht niet. Hem komt het oordeel toe en tot Hem worden jullie teruggebracht.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'A[lif] L[aam] M[iem].'),
(3342, 29, 2, 'Rekenen de mensen er dan op dat zij met rust gelaten worden omdat zij zeggen: "Wij geloven" en dat zij niet aan verzoeking worden blootgesteld?'),
(3343, 29, 3, 'Wij hebben hen die er voor hen waren aan verzoeking blootgesteld. God zal dus zeker hen die de waarheid spreken kennen en Hij zal de leugenaars kennen.'),
(3344, 29, 4, 'Of rekenen zij die slechte daden begaan erop dat zij Ons te vlug af zullen zijn? Slecht is het wat zij oordelen.'),
(3345, 29, 5, 'Als iemand de ontmoeting met God verwacht? Gods termijn komt echt en Hij is de horende, de wetende.'),
(3346, 29, 6, 'En wie zich inzet, die zet zich tot zijn eigen voordeel in. God heeft de wereldbewoners niet nodig.'),
(3347, 29, 7, 'Zij die geloven en de deugdelijke daden doen, hun zullen Wij hun slechte daden kwijtschelden en Wij zullen hen belonen voor het beste dat zij deden.'),
(3348, 29, 8, 'En Wij hebben de mens opgedragen goed voor zijn ouders te zijn. Als zij er echter bij jou op aandringen aan Mij metgezellen toe te voegen, waarvan jij geen kennis hebt, gehoorzaam hun dan niet. Tot Mij is jullie terugkeer. Ik zal jullie dan meedelen wat jullie aan het doen waren.'),
(3349, 29, 9, 'Zij die geloven en de deugdelijke daden doen zullen Wij bij de rechtschapenen binnen laten gaan.'),
(3350, 29, 10, 'En onder de mensen zijn er die zeggen: "Wij geloven in God." Wanneer hun echter wegens [hun geloof in] God leed wordt aangedaan stellen zij de beproeving van de mensen gelijk aan Gods bestraffing. Maar als er van jouw Heer een overwinning komt dan zeggen zij: "Wij waren toch met jullie." Weet God dan niet het best wat er in de harten van de wereldbewoners is?'),
(3351, 29, 11, 'God zal zeker hen die geloven kennen en Hij zal de huichelaars kennen.'),
(3352, 29, 12, 'Zij die ongelovig zijn zullen tegen hen die geloven zeggen: "Volgt onze weg, dan zullen wij jullie fouten dragen." Zij zullen echter niets van hun fouten dragen; zij zijn leugenaars.'),
(3353, 29, 13, 'Zij zullen hun eigen lasten dragen en nog andere lasten bij hun eigen lasten. En zij zullen op de opstandingsdag verantwoording moeten afleggen over wat zij verzonnen hadden.'),
(3354, 29, 14, 'En Wij hebben Noeh naar zijn volk gezonden en hij verbleef duizend jaar in hun midden, op vijftig jaar na. Toen greep de overstroming hen terwijl zij onrecht pleegden.'),
(3355, 29, 15, 'Maar Wij redden hem en de opvarenden van het schip en Wij maakten het tot een teken voor de wereldbewoners.'),
(3356, 29, 16, 'En Ibrahiem. Toen hij tot zijn volk zei: "Dient God en vreest Hem. Dat is beter voor jullie, als jullie het maar wisten.'),
(3357, 29, 17, 'Maar jullie dienen in plaats van God afgoden en jullie brengen laster voort. Zij die jullie in plaats van God dienen hebben geen macht om jullie van levensbehoeften te voorzien. Zoekt de levensbehoeften dus bij God en dient Hem en betuigt Hem dank; tot Hem worden jullie teruggebracht.'),
(3358, 29, 18, 'En als jullie [mij] van leugens betichten, gemeenschappen voor jullie tijd hadden al van leugens beticht. De gezant heeft slechts de plicht van de duidelijke verkondiging."'),
(3359, 29, 19, 'Hebben zij dan niet gezien hoe God de schepping laat beginnen en haar dan herhaalt? Dat is voor God gemakkelijk.'),
(3360, 29, 20, 'Zeg: "Reist dus op de aarde rond en kijkt hoe Hij met de schepping is begonnen. Dan laat God de laatste totstandkoming ontstaan. God is almachtig.'),
(3361, 29, 21, 'Hij bestraft wie Hij wil en Hij erbarmt zich over wie Hij wil en tot Hem worden jullie weer teruggebracht.'),
(3362, 29, 22, 'Jullie kunnen er op de aarde niets tegen doen noch in de hemel en jullie hebben buiten God geen beschermer en geen helper."'),
(3363, 29, 23, 'Zij die ongelovig zijn aan Gods tekenen en aan de ontmoeting met Hem, zij zijn het die aan Mijn barmhartigheid wanhopen. Zij zijn het voor wie er een pijnlijke bestraffing is.'),
(3364, 29, 24, 'Het antwoord van zijn volk was slechts dat zij zeiden: "Doodt hem of verbrandt hem." Maar God redde hem van het vuur. Daarin zijn tekenen voor mensen die geloven.'),
(3365, 29, 25, 'En hij zei: "Jullie hebben je in plaats van God slechts afgoden genomen om jullie onderlinge liefde in het tegenwoordige leven [te bestendigen], maar op de opstandingsdag willen jullie elkaar niet meer kennen en vervloeken jullie elkaar; jullie verblijfplaats is het vuur en jullie zullen geen helpers hebbent *'),
(3366, 29, 26, 'Maar Loet geloofde hem en zei: "Ik wijk naar mijn Heer uit; Hij is de machtige, de wijze."'),
(3367, 29, 27, 'En Wij hebben hem Ishaak en Ja\'koeb geschonken en Wij hebben in zijn nageslacht het profeetschap en het boek tot stand gebracht. En Wij hebben hem zijn loon in het tegenwoordige leven gegeven en in het hiernamaals behoort hij tot de rechtschapenen.'),
(3368, 29, 28, 'En Loet. Toen hij tot zijn volk zei: "Jullie begaan een gruweldaad die nog niemand van de wereldbewoners vóór jullie heeft begaan.'),
(3369, 29, 29, 'Zullen jullie tot mannen komen, struikroverij bedrijven en in jullie samenkomst het verwerpelijke begaan?" Het antwoord van zijn volk was slechts dat zij zeiden: "Breng ons dan de bestraffing van God als jij tot hen die gelijk hebben behoort."'),
(3370, 29, 30, 'Hij zei: "Mijn Heer, help mij tegen de verderfbrengende mensen."'),
(3371, 29, 31, 'En toen Onze gezanten met het goede nieuws tot Ibrahiem kwamen zeiden zij: "Wij gaan de mensen van deze stad vernietigen; haar bewoners zijn onrechtplegers."'),
(3372, 29, 32, 'Hij zei: "Maar Loet is daar!" Zij zeiden: "Wij weten het best wie daar zijn. Wij zullen hem en zijn familie redden, behalve zijn vrouw; zij behoort tot hen die achterblijven."'),
(3373, 29, 33, 'En toen Onze gezanten tot Loet kwamen was hij bezorgd over hen en maakte zich ongerust over hen. Maar zij zeiden: "Wees niet bang en wees niet bedroefd; wij zullen jou en jouw familie redden, behalve jouw vrouw; zij behoort tot hen die achterblijven.'),
(3374, 29, 34, 'Wij zullen over de bewoners van deze stad een plaag uit de hemel laten neerdalen omdat zij verdorven waren."'),
(3375, 29, 35, 'En Wij lieten er een duidelijk teken van over voor mensen die verstandig zijn.'),
(3376, 29, 36, 'En tot Madjan hun broeder Sjoe\'aib. Hij zei: "Mijn volk! Dient God en verwacht de laatste dag en veroorzaakt geen ellende op de aarde door verderf te zaaien."'),
(3377, 29, 37, 'Maar zij betichtten hem van leugens en toen greep de aardbeving hen en lagen zij \'s morgens in hun woning dood op de grond.'),
(3378, 29, 38, 'En de \'Aad en de Thamoed! Dat is jullie duidelijk geworden uit hun woonhuizen. De satan had voor hen hun daden mooi laten lijken en hun de weg versperd, hoewel zij toch inzicht hadden.'),
(3379, 29, 39, 'En Karoen en Fir\'aun en Hamaan. Moesa was met de duidelijke bewijzen tot hen gekomen, maar zij waren hoogmoedig in het land en toch konden zij niet ontkomen.'),
(3380, 29, 40, 'Dus grepen Wij een ieder voor zijn zonde. Tegen sommigen stuurden Wij een zandstorm. Anderen greep de schreeuw. Weer anderen lieten Wij met de aarde wegzinken en er waren er die Wij lieten verdrinken. Toch was het niet zo dat God hun onrecht aandeed, maar zij deden zichzelf onrecht aan.'),
(3381, 29, 41, 'Zij die zich in plaats van God beschermers hebben genomen lijken bijvoorbeeld op de spin die zich een huis genomen heeft. En het brooste huis is toch het huis van de spin. Als zij dat maar wisten.'),
(3382, 29, 42, 'God kent alles wat zij in plaats van Hem aanroepen; Hij is de machtige, de wijze.'),
(3383, 29, 43, 'Dit zijn de vergelijkingen die Wij voor de mensen maken, maar slechts zij die kennis hebben begrijpen ze.'),
(3384, 29, 44, 'God heeft de hemelen en de aarde in waarheid geschapen. Daarin is een teken voor de gelovigen.'),
(3385, 29, 45, 'Lees voor wat aan jou van het boek is geopenbaard en verricht de salaat; de salaat verbiedt wat gruwelijk en verwerpelijk is. Maar het gedenken Gods is geweldiger. En God weet wat jullie doen. *'),
(3386, 29, 46, 'Twist met de mensen van het boek slechts op de beste manier, behalve met degenen onder hen die onrecht plegen, en zegt: "Wij geloven in wat naar ons is neergezonden en in wat naar jullie is neergezonden. Onze god en jullie god is één. En wij geven ons over aan Hem."'),
(3387, 29, 47, 'Zo hebben Wij het boek naar jou neergezonden en zij aan wie Wij het boek gegeven hebben geloven erin. En ook onder dezen hier zijn er die erin geloven. Slechts de ongelovigen verwerpen Onze tekenen.'),
(3388, 29, 48, 'Jij hebt tevoren geen boek voorgelezen, noch met je rechterhand opgeschreven; dan zouden zij twijfelen die zeggen dat het niet waar is.'),
(3389, 29, 49, 'Integendeel, het zijn duidelijke tekenen in de harten van hen aan wie de kennis gegeven is. Slechts de onrechtplegers verwerpen Onze tekenen.'),
(3390, 29, 50, 'En zij zeggen: "Had er dan geen teken van zijn Heer vandaan tot hem neergezonden kunnen worden?" Zeg: "De tekenen zijn slechts bij God en ik ben alleen maar een duidelijke waarschuwer."'),
(3391, 29, 51, 'Is het dan niet genoeg voor hen dat Wij tot jou het boek hebben neergezonden dat aan hen wordt voorgelezen? Daarin is barmhartigheid en een vermaning voor mensen die geloven.'),
(3392, 29, 52, 'Zeg: "God is goed genoeg als getuige tussen mij en jullie. Hij weet wat er in de hemelen en de aarde is. En zij die in het onware geloven en aan God geen geloof hechten, zij zijn het die de verliezers zijn."'),
(3393, 29, 53, 'En zij willen van jou dat de bestraffing wordt verhaast. En als er geen vastgestelde termijn was, dan was de bestraffing tot hen gekomen; dan was hij onverwachts tot hen gekomen terwijl zij het niet beseften.'),
(3394, 29, 54, 'Zij willen van jou dat de bestraffing wordt verhaast. Welnu, de hel zal de ongelovigen omvatten.'),
(3395, 29, 55, 'Op de dag dat de bestraffing hen van boven af en van onder hun voeten vandaan bedekt en Hij zegt: "Proeft wat jullie aan het doen waren."'),
(3396, 29, 56, 'Mijn dienaren die geloven! Mijn aarde is ruim, dient Mij dus!'),
(3397, 29, 57, 'Iedereen zal de dood proeven; dan zullen jullie tot Ons teruggebracht worden.'),
(3398, 29, 58, 'En aan hen die geloven en de deugdelijke daden doen zullen Wij [feest]zalen toewijzen in de tuin waar de rivieren onderdoor stromen; zij zullen altijd daarin blijven. Dat is pas een goed loon voor hen die [goed] doen,'),
(3399, 29, 59, 'die geduldig volharden en op hun Heer hun vertrouwen stellen.'),
(3400, 29, 60, 'En hoeveel dieren zijn er die hun levensbehoeften niet dragen. God voorziet in hun onderhoud en dat van jullie; Hij is de horende, de wetende.'),
(3401, 29, 61, 'Maar als jij hun vraagt wie de hemelen en de aarde geschapen en de zon en de maan dienstbaar gemaakt heeft, dan zeggen zij: "God." Hoe kunnen zij dan zo zijn afgeleid?'),
(3402, 29, 62, 'God voorziet ruimschoots in het levensonderhoud van wie Hij wil en ook met mate. God is alwetend.'),
(3403, 29, 63, 'En als jij hun vraagt wie uit de hemel water laat neerdalen om daarmee de aarde te doen herleven nadat zij dood was dan zeggen zij: "God." Zeg: "Lof zij God!" Maar de meesten van hen zijn niet verstandig.'),
(3404, 29, 64, 'Dit tegenwoordige leven is slechts tijdverdrijf en spel, maar de laatste woning, dat is het ware leven. Als zij het maar wisten.'),
(3405, 29, 65, 'En wanneer zij aan boord van een schip gaan roepen zij God aan terwijl zij de godsdienst geheel aan Hem wijden, maar wanneer Hij hen dan gered en aan land gebracht heeft, dan bedrijven zij veelgodendienst'),
(3406, 29, 66, 'om ondankbaar te zijn voor wat Wij hun gegeven hebben en om te genieten. Maar zij zullen het weten!'),
(3407, 29, 67, 'Hebben zij dan niet gezien dat Wij een heilige en veilige plaats gemaakt hebben, terwijl de mensen rondom weggerukt worden? Geloven zij dan in de onzin, terwijl zij aan Gods genade geen geloof hechten?'),
(3408, 29, 68, 'En wie is er zondiger dan wie over God bedrog verzint of de waarheid loochent wanneer zij tot hem komt? Is er in de hel niet een verblijfplaats voor de ongelovigen?'),
(3409, 29, 69, 'En hen die zich voor Ons inzetten zullen Wij op Onze wegen leiden. God is met hen die goed doen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'A[lif] L[aam] M[iem].'),
(3411, 30, 2, 'De Romeinen zijn overwonnen'),
(3412, 30, 3, 'in het dichtstbijzijnde land, maar na hun nederlaag zullen zij overwinnen,'),
(3413, 30, 4, 'over enkele jaren. God komt de beslissing toe, vroeger en later. En op die dag zullen de gelovigen zich verheugen'),
(3414, 30, 5, 'over Gods hulp. Hij helpt wie Hij wil; Hij is de machtige, de barmhartige.'),
(3415, 30, 6, 'Het is Gods toezegging. God zal Zijn toezegging zeker nakomen, maar de meeste mensen weten het niet.'),
(3416, 30, 7, 'Zij kennen slechts de buitenkant van het tegenwoordige leven, terwijl zij op het hiernamaals geen acht slaan.'),
(3417, 30, 8, 'Denken zij dan in zichzelf niet na? God heeft de hemelen en de aarde en wat er tussen beide is slechts in waarheid en voor een vastgestelde termijn geschapen. Maar velen van de mensen hechten geen geloof aan de ontmoeting met hun Heer.'),
(3418, 30, 9, 'Hebben zij dan niet op de aarde rondgereisd en ernaar gekeken hoe het einde van hen was die er voor hen waren? Die waren sterker dan zij en ploegden en bebouwden het land meer dan zij het bebouwden. En tot hen kwamen hun gezanten met de duidelijke bewijzen. God was niet zo dat Hij hun onrecht aandeed, maar zij deden zichzelf onrecht aan.'),
(3419, 30, 10, 'Toen was het einde van hen die verkeerd deden het allerergste, omdat zij Gods tekenen geloochend hadden en omdat zij er de spot mee dreven.'),
(3420, 30, 11, 'God begint met de schepping, dan herhaalt Hij haar en dan zullen jullie tot Hem teruggebracht worden.'),
(3421, 30, 12, 'En op de dag dat het uur aanbreekt zullen de boosdoeners in wanhoop terneergeslagen zijn.'),
(3422, 30, 13, 'En er zullen onder hun [zogenaamd goddelijke] metgezellen geen bemiddelaars zijn, maar zij zullen aan hun [zogenaamd goddelijke] metgezellen geen geloof hechten.'),
(3423, 30, 14, 'En op de dag dat het uur aanbreekt, op die dag zullen zij verspreid worden.'),
(3424, 30, 15, 'Maar wat hen betreft die geloven en de deugdelijke daden doen, zij zullen in een hof verblijd worden.'),
(3425, 30, 16, 'En wat hen betreft die ongelovig zijn en die Onze tekenen en de ontmoeting van het hiernamaals loochenen, zij zijn het die ter bestraffing worden voorgeleid.'),
(3426, 30, 17, 'God zij dus geprezen in de tijd van de avond en in de tijd van de morgen.'),
(3427, 30, 18, 'Hem zij de lof in de hemelen en op de aarde, ook \'s avonds en op de middag.'),
(3428, 30, 19, 'Hij brengt het levende uit het dode voort en Hij brengt het dode uit het levende voort en Hij doet de aarde herleven nadat zij dood was. En zo zullen jullie tevoorschijn gebracht worden.'),
(3429, 30, 20, 'Tot Zijn tekenen behoort dat Hij jullie uit aarde geschapen heeft; toen waren jullie dan mensen die zich verspreidden.'),
(3430, 30, 21, 'En tot Zijn tekenen behoort dat Hij voor jullie echtgenotes uit jullie eigen midden geschapen heeft om bij haar rust te vinden. En Hij heeft liefde en erbarmen tussen jullie gebracht. Daarin zijn tekenen voor mensen die nadenken.'),
(3431, 30, 22, 'En tot Zijn tekenen behoren de schepping van de hemelen en de aarde en het verschil in jullie talen en kleuren. Daarin zijn tekenen voor de wereldbewoners.'),
(3432, 30, 23, 'En tot Zijn tekenen behoort jullie slaap, \'s nachts en overdag en jullie streven naar een gunst van Hem. Daarin zijn tekenen voor mensen die horen.'),
(3433, 30, 24, 'En tot Zijn tekenen behoort dat Hij jullie in vrees en begeerte de bliksem laat zien en dat Hij uit de hemel water laat neerdalen en dat Hij daarmee de aarde laat herleven nadat zij dood was. Daarin zijn tekenen voor mensen die verstandig zijn.'),
(3434, 30, 25, 'En tot Zijn tekenen behoort dat de hemel en de aarde op Zijn bevel vast staan. Dan, wanneer Hij jullie luid uit de aarde oproept, dan komen jullie tevoorschijn.'),
(3435, 30, 26, 'En van Hem is wie er in de hemelen en op de aarde is. Allen zijn aan Hem onderdanig.'),
(3436, 30, 27, 'Hij is het die met de schepping begint en haar dan herhaalt. Dat is gemakkelijk voor Hem. En Hem komt het hoogste voorbeeld toe in de hemelen en op de aarde; Hij is de machtige, de wijze.'),
(3437, 30, 28, 'Hij maakt voor jullie voorbeelden uit jullie eigen midden. Hebben jullie dan onder de slaven waarover jullie beschikken deelgenoten in de levensbehoeften waarmee Wij jullie hebben voorzien zodat jullie daarin gelijk zijn? Zijn jullie bang voor hen zoals jullie bang voor jullie zelf zijn? Zo zetten Wij de tekenen uiteen voor mensen die verstandig zijn.'),
(3438, 30, 29, 'Maar nee, zij die onrecht plegen volgen zonder kennis slechts hun eigen neigingen. Wie kan er dan aan iemand die door God tot dwaling is gebracht de goede richting wijzen? Zij hebben geen helpers.'),
(3439, 30, 30, 'En richt je aangezicht naar de godsdienst als een aanhanger van het zuivere geloof, de van God afkomstige aanleg die Hij de mensen ingeschapen heeft. Gods schepping is niet te veranderen. Dat is de juiste godsdienst, maar de meeste mensen weten het niet.'),
(3440, 30, 31, 'Wendt jullie schuldbewust tot Hem en vreest Hem en verricht de salaat en behoort niet tot de veelgodendienaars,'),
(3441, 30, 32, 'tot hen die hun godsdienst opsplitsen en tot sekten zijn geworden; elke sekte is blij over wat zij hebben.'),
(3442, 30, 33, 'En wanneer de mensen tegenspoed treft roepen zij hun Heer aan, terwijl zij zich schuldbewust tot Hem wenden, maar daarna, wanneer Hij hun van Zijn kant barmhartigheid laat proeven, dan voegt een groep van hen metgezellen aan Hem toe,'),
(3443, 30, 34, 'om in hun ongeloof ondankbaar te zijn voor wat Wij hun gegeven hebben. Geniet maar; jullie zullen het weten.'),
(3444, 30, 35, 'Of hebben Wij tot hen een machtiging neergezonden waarin sprake zou zijn van de metgezellen die zij aan Hem toevoegen?'),
(3445, 30, 36, 'En wanneer Wij de mensen Onze barmhartigheid laten proeven, dan verheugen zij zich erover, maar als hen kwaad treft om wat hun handen eerder gedaan hebben, dan geven zij de hoop op.'),
(3446, 30, 37, 'Hebben zij dan niet gezien dat God ruimschoots in het levensonderhoud voorziet van wie Hij wil en ook met mate? Daarin zijn tekenen voor mensen die geloven.'),
(3447, 30, 38, 'Geef dus de verwant wat hem toekomt en ook de behoeftige en wie onderweg is. Dat is beter voor hen die het aangezicht van God zoeken en zij zijn het die het welgaat.'),
(3448, 30, 39, 'En de [lening op] woeker, die jullie geven om het ten koste van de bezittingen van de mensen te laten groeien, groeit bij God niet. Maar de zakaat die jullie geven terwijl jullie Gods aangezicht zoeken? zij zijn het die het laten verveelvoudigen.'),
(3449, 30, 40, 'God is het die jullie geschapen heeft en daarna in jullie onderhoud voorzien heeft. Dan laat Hij jullie sterven. Daarna brengt Hij jullie weer tot leven. Is er onder jullie [zogenaamd goddelijke] metgezellen iemand die zoiets doen kan? Hij zij geprezen, verheven als Hij is boven wat zij aan Hem als metgezellen toevoegen.'),
(3450, 30, 41, 'Het verderf is op het vasteland en op zee zichtbaar geworden door wat de handen van de mensen ten uitvoer brengen. [Dat is gebeurd] opdat Hij hun iets van wat zij gedaan hebben wil laten proeven; misschien zullen zij terugkeren.'),
(3451, 30, 42, 'Zeg: "Reist op de aarde rond en kijkt hoe het einde was van hen die er vroeger waren; de meesten van hen waren veelgodendienaars."'),
(3452, 30, 43, 'En richt je aangezicht naar de juiste godsdienst voordat er een dag komt die door God niet wordt afgewend. Op die dag zullen zij gesplitst worden.'),
(3453, 30, 44, 'Wie ongelovig is, diens ongeloof is in zijn nadeel en wie deugdelijk hebben gehandeld, die bereiden een plaats voor zichzelf,'),
(3454, 30, 45, 'opdat Hij hen die geloven en de deugdelijke daden doen door Zijn gunst beloont; Hij bemint de ongelovigen niet.'),
(3455, 30, 46, 'En tot Zijn tekenen behoort dat Hij de winden als verkondigers van goed nieuws uitzendt opdat Hij jullie van Zijn barmhartigheid laat proeven, opdat de schepen op Zijn bevel varen en opdat jullie streven naar een gunst van Hem; misschien zullen jullie dank betuigen.'),
(3456, 30, 47, 'En Wij hebben al voor jouw tijd gezanten naar hun volk gezonden en zij kwamen tot hen met de duidelijke bewijzen. Toen namen Wij wraak op hen die boosdoeners waren, maar het was voor Ons een verplichting de gelovigen te helpen.'),
(3457, 30, 48, 'God is het die de winden uitzendt die dan in de hemel wolken opdrijven. En Hij spreidt ze uit hoe Hij het wil en Hij verdeelt ze in stukken. Dan zie je de regen ertussenuit komen. En wanneer Hij van zijn dienaren ermee treft wie Hij wil, dan verblijden zij zich.'),
(3458, 30, 49, 'Ook al waren zij voorheen, voordat het tot hen was neergezonden, in wanhoop terneergeslagen.'),
(3459, 30, 50, 'Kijkt dan naar de sporen van Gods barmhartigheid, hoe Hij de aarde laat herleven nadat zij dood was. Dat is Hij die de doden laat herleven en Hij is almachtig.'),
(3460, 30, 51, 'En als Wij een wind uitzenden en zij hem dan geel zien worden blijven zij daarna toch ongelovig.'),
(3461, 30, 52, 'Maar jij kunt de doden niet doen horen, noch kun jij de doven de oproep doen horen wanneer zij de rug toekeren.'),
(3462, 30, 53, 'En jij brengt de blinden niet van hun dwaalweg af. Jij kunt slechts hen die in Onze tekenen geloven doen horen; zij hebben zich [aan God] overgegeven.'),
(3463, 30, 54, 'God is het die jullie eerst in zwakte schept, dan geeft Hij na de zwakte kracht en dan geeft Hij na de kracht zwakte en grijs haar. Hij schept wat Hij wil en Hij is de wetende, de vrijmachtige.'),
(3464, 30, 55, 'En op de dag dat het uur aanbreekt zullen de boosdoeners zweren dat het niet langer dan een uur geduurd heeft. Zo afgeleid waren zij.'),
(3465, 30, 56, 'Zij aan wie de kennis en het geloof gegeven is zeggen: "Het heeft voor jullie volgens Gods boek tot de dag van de opwekking geduurd. Dit is dus de dag van de opwekking! Maar jullie wisten het niet."'),
(3466, 30, 57, 'En op die dag zal hun die onrecht pleegden hun verontschuldiging niet baten en zij krijgen geen kans meer om het goed te maken.'),
(3467, 30, 58, 'Wij hebben in deze Koran voor de mensen allerlei voorbeelden gegeven, maar als jij met een teken tot hen komt zeggen zij die ongelovig zijn: "Jullie zijn slechts mensen die zeggen wat niet waar is."'),
(3468, 30, 59, 'Zo verzegelt God de harten van hen die niet weten.'),
(3469, 30, 60, 'Volhard dus geduldig; Gods toezegging is waar. En zij die geen vaste overtuiging hebben, kunnen jou niet aan het weifelen brengen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'A[lif] L[aam] M[iem].'),
(3471, 31, 2, 'Dit zijn de tekenen van het wijze boek;'),
(3472, 31, 3, 'een leidraad en barmhartigheid voor hen die goed doen,'),
(3473, 31, 4, 'die de salaat verrichten en de zakaat geven terwijl zij van het hiernamaals vast overtuigd zijn.'),
(3474, 31, 5, 'Zij volgen de leidraad van hun Heer en zij zijn het die het welgaat.'),
(3475, 31, 6, 'Onder de mensen zijn er die kletspraat verkopen om zonder kennis van Gods weg af te laten dwalen en er de spot mee te drijven. Zij zijn het voor wie er een vernederende bestraffing is.'),
(3476, 31, 7, 'En wanneer aan hem Onze tekenen worden voorgelezen dan keert hij zich hoogmoedig om alsof hij ze niet gehoord heeft, alsof in zijn oren hardhorendheid is. Verkondig hem dan een pijnlijke bestraffing.'),
(3477, 31, 8, 'Zij die geloven en de deugdelijke daden doen, voor hen zijn de tuinen van de gelukzaligheid,'),
(3478, 31, 9, 'waarin zij altijd zullen blijven. Dat is in waarheid Gods toezegging. Hij is de machtige, de wijze.'),
(3479, 31, 10, 'Hij heeft de hemelen zonder steun die je kunt zien geschapen en Hij heeft op de aarde stevige bergen aangebracht zodat zij jullie niet aan het wankelen zou brengen. En Hij heeft er allerlei dieren op verspreid. En Wij hebben uit de hemel water laten neerdalen en Wij hebben er allerlei voortreffelijke soorten op laten groeien.'),
(3480, 31, 11, 'Dit is Gods schepping. Laat mij dan zien wat zij geschapen hebben die er buiten Hem nog zouden zijn. Welnee, de onrechtplegers verkeren in duidelijke dwaling.'),
(3481, 31, 12, 'Wij hadden aan Loekmaan de wijsheid gegeven, dat hij aan God dank moest betuigen. En wie dank betuigt doet dat slechts in zijn eigen voordeel en wie ondankbaar is, God is behoefteloos en lofwaardig.'),
(3482, 31, 13, 'Toen Loekmaan zijn zoon vermanend toesprak: "Mijn zoon, bedrijf geen veelgodendienst, want veelgodendienst is een geweldig misdrijf."'),
(3483, 31, 14, 'En Wij hebben de mens met betrekking tot zijn ouders opgedragen -- zijn moeder heeft hem immers in grote zwakheid gedragen en totdat hij gespeend werd waren het nog twee jaar --: "Jij moet Mij en jouw ouders dank betuigen. Bij Mij is de bestemming.'),
(3484, 31, 15, 'Als zij er echter bij jou op aandringen aan Mij metgezellen toe te voegen, waarvan jij geen kennis hebt, gehoorzaam hun dan niet. En ga in het tegenwoordige leven behoorlijk met hen om en volg de weg van wie zich schuldbewust tot Mij wendt. Daarna zal tot Mij jullie terugkeer zijn. Ik zal jullie dan meedelen wat jullie aan het doen waren."'),
(3485, 31, 16, '"Mijn zoon, al gaat het om het gewicht van een mosterdzaadje en al was het in een rots of in de hemelen of op de aarde, God zal het brengen. God is welwillend en welingelicht.'),
(3486, 31, 17, 'Mijn zoon, verricht de salaat, gebied het behoorlijke en verbied het verwerpelijke. En verdraag geduldig wat je treft, dat is een zaak van vastbeslotenheid.'),
(3487, 31, 18, 'Wend je wang niet hoogmoedig van de mensen af en loop niet verwaand op de aarde rond. God bemint geen enkele ingebeelde en verwaande.'),
(3488, 31, 19, 'En wees gematigd in je lopen en spreek met zachte stem. De afschuwelijkste stem is immers de stem van ezels."'),
(3489, 31, 20, 'Hebben jullie niet gezien dat God voor jullie wat er in de hemelen en wat er op de aarde is dienstbaar heeft gemaakt en dat Hij Zijn weldaden aan jullie uiterlijk en innerlijk vervolmaakt? Er zijn mensen die zonder kennis, zonder leidraad en zonder verlichtend boek over God twisten.'),
(3490, 31, 21, 'En als tot hen gezegd wordt: "Volgt wat God heeft neergezonden na", zeggen zij: "Welnee, wij volgen dat na waarvan wij merken dat onze vaderen zich eraan hielden." Ook dan soms, als de satans hen tot de bestraffing van de vuurgloed oproepen?'),
(3491, 31, 22, 'En wie zich geheel aan God overgeeft en goed doet, die houdt de stevigste handgreep vast. En de uiteindelijke beschikkingen berusten bij God.'),
(3492, 31, 23, 'En als iemand ongelovig is, laat dan zijn ongeloof jou niet bedroefd maken. Tot Ons is hun terugkeer en Wij zullen hun meedelen wat zij deden. God weet wat er binnen in de harten is.'),
(3493, 31, 24, 'Wij zullen hen nog kort laten genieten, maar dan zullen Wij hen naar een harde bestraffing drijven.'),
(3494, 31, 25, 'En als jij hun vraagt wie de hemelen en de aarde geschapen heeft zeggen zij: "God." Zeg: "Lof zij God!" Maar de meesten van hen weten het niet.'),
(3495, 31, 26, 'Van God is wat er in de hemelen en op de aarde is. God is de behoefteloze, de lofwaardige.'),
(3496, 31, 27, 'Zelfs al waren de bomen die er op de aarde zijn pennen en al werd de zee [als inkt gebruikt en] daarna nog eens met zeven zeeën aangevuld, dan waren Gods woorden nog niet uitgeput. God is machtig en wijs.'),
(3497, 31, 28, 'Jullie schepping en jullie opwekking is niet anders dan als van één persoon. God is horend en doorziend.'),
(3498, 31, 29, 'Heb jij niet gezien dat God de nacht laat overgaan in de dag en de dag laat overgaan in de nacht, dat Hij de zon en de maan dienstbaar gemaakt heeft, dat alles tot een vastgestelde termijn loopt en dat God welingelicht is over wat jullie doen?'),
(3499, 31, 30, 'Dat is zo omdat God de waarheid is en omdat wat zij buiten Hem aanroepen de onwaarheid is en omdat God de verhevene, de grote is.'),
(3500, 31, 31, 'Heb jij niet gezien dat de schepen door Gods genade op zee varen om jullie iets van Zijn tekenen te tonen? In de schepen zijn zeker tekenen voor ieder die geduldig volhardt en die dank betuigt.'),
(3501, 31, 32, 'En als golven hen als stapelwolken overdekken roepen zij God aan, waarbij zij de godsdienst geheel aan Hem wijden. En wanneer Hij hen gered en aan land gebracht heeft, dan zijn er onder hen gematigden, maar Onze tekenen worden slechts verworpen door ieder die trouweloos en ondankbaar is.'),
(3502, 31, 33, 'Jullie mensen! Vreest jullie Heer en vreest een dag waarop geen ouder voor zijn kind kan betalen en geen kind ook maar iets voor zijn ouder kan betalen! Gods aanzegging is waar. Laat het tegenwoordige leven jullie dus niet begoochelen en laat de begoochelaar jullie niet met betrekking tot God begoochelen.'),
(3503, 31, 34, 'God, bij Hem is de kennis van het uur, Hij laat de regen neerdalen en Hij weet wat er in de moederschoten is. Niemand weet wat hij morgen ten uitvoer zal brengen en niemand weet in welk land hij zal sterven. God is wetend en welingelicht.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'A[lif] L[aam] M[iem].'),
(3505, 32, 2, 'De neerzending van het boek waaraan geen twijfel is, is gebeurd door de Heer van de wereldbewoners.'),
(3506, 32, 3, 'Of zeggen zij: "Hij heeft het verzonnen." Integendeel, het is de waarheid van jouw Heer vandaan, opdat jij mensen waarschuwt tot wie voor jouw tijd geen waarschuwer was gekomen; misschien zullen zij zich de goede richting laten wijzen.'),
(3507, 32, 4, 'God is het die de hemelen en de aarde en wat er tussen beide is in zes dagen geschapen heeft. Toen vestigde Hij zich op de troon. Buiten Hem hebben jullie geen beschermer, noch een bemiddelaar. Zullen jullie je dan niet laten vermanen?'),
(3508, 32, 5, 'Hij regelt het bestuur vanuit de hemel tot aan de aarde en dan komt het [weer] tot Hem op in een dag waarvan de maat volgens jullie berekening duizend jaren is.'),
(3509, 32, 6, 'Dat is de kenner van het verborgene en het waarneembare, de machtige, de wijze,'),
(3510, 32, 7, 'die alles wat Hij geschapen heeft goed gemaakt heeft en die de schepping van de mens uit klei aangevangen heeft.'),
(3511, 32, 8, 'Daarna maakte Hij zijn nageslacht uit een extract van verachtelijk water.'),
(3512, 32, 9, 'En dan vormde Hij hem en blies in hem iets van Zijn geest. En Hij heeft voor jullie gehoor, gezichtsvermogen en harten gemaakt; hoe weinig is de dank die jullie betuigen.'),
(3513, 32, 10, 'En zij zeggen: "Wanneer wij in de aarde zoek geraakt zijn, zullen wij dan werkelijk deel uitmaken van een nieuwe schepping?" Ja zeker, aan de ontmoeting met hun Heer hechten zij geen geloof. *'),
(3514, 32, 11, 'Zeg: "De doodsengel aan wie Wij jullie hebben toevertrouwd zal jullie wegnemen en dan zullen jullie tot jullie Heer teruggebracht worden."'),
(3515, 32, 12, 'Kon jij de boosdoeners maar zien wanneer zij bij hun Heer hun hoofd buigen: "Onze Heer, wij zijn tot inzicht gekomen en wij hebben geluisterd. Breng ons terug, dan zullen wij deugdelijk handelen. Wij zijn vast overtuigd."'),
(3516, 32, 13, 'En als Wij het gewild hadden hadden Wij aan iedere persoon gegeven dat hij op het goede pad gebracht werd, maar de uitspraak van Mij is bewaarheid: "Ik zal de hel met djinn en mensen gezamenlijk vullen."'),
(3517, 32, 14, 'Proeft het dus omdat jullie de ontmoeting op deze dag van jullie hebben vergeten; Wij hebben jullie ook vergeten! Proeft de altijd durende bestraffing voor wat jullie deden.'),
(3518, 32, 15, 'Slechts zij geloven aan Onze tekenen die, wanneer zij ermee vermaand worden, neervallen in eerbiedige buiging en de lof van hun Heer prijzen, terwijl zij niet hoogmoedig zijn. --'),
(3519, 32, 16, 'Zij houden zich er verre van op hun zijden op hun rustplaatsen te liggen, maar roepen hun Heer aan in vrees en begeerte en geven bijdragen van wat Wij hun voor hun levensonderhoud geven.'),
(3520, 32, 17, 'En niemand weet wat voor vreugde voor hem verborgen is als beloning voor wat zij gedaan hebben.'),
(3521, 32, 18, 'Is dan iemand die gelovig is als iemand die verdorven is? Zij zijn niet gelijk.'),
(3522, 32, 19, 'Wat hen betreft die geloven en de deugdelijke daden doen, voor hen zijn er de tuinen van de [hemelse] verblijfplaats als gastverblijf voor wat zij gedaan hebben.'),
(3523, 32, 20, 'Maar wat hen betreft die verdorven zijn, hun verblijfplaats is het vuur. Telkens als zij eruit wensen te gaan, worden zij erin teruggebracht en wordt tot hen gezegd: "Proeft de bestraffing van het vuur dat jullie loochenden."'),
(3524, 32, 21, 'Maar afgezien van de grootste bestraffing zullen Wij hun ook nog van de bestraffing in het tegenwoordige leven laten proeven; misschien zullen zij terugkeren.'),
(3525, 32, 22, 'En wie is er zondiger dan hij die met de tekenen van zijn Heer vermaand is en er zich dan van afwendt? Wij nemen wraak op de boosdoeners.'),
(3526, 32, 23, 'En Wij hebben Moesa het boek gegeven. Verkeer dus niet in twijfel over zijn ontmoeting [ermee]. En Wij hebben het tot een leidraad voor de Israëlieten gemaakt.'),
(3527, 32, 24, 'En Wij hebben sommigen van hen tot voorgangers gemaakt die volgens Ons bevel de goede richting wezen toen zij geduldig volhardden en van Onze tekenen vast overtuigd waren.'),
(3528, 32, 25, 'Jouw Heer zal dus op de opstandingsdag scheiding tussen hen aanbrengen wegens dat waarover zij het oneens waren.'),
(3529, 32, 26, 'Is het hun dan niet duidelijk hoeveel generaties Wij al vóór hun tijd vernietigd hebben, hoewel zij in hun woningen rondwandelen? Daarin zijn tekenen. Horen zij dan niet?'),
(3530, 32, 27, 'Hebben zij dan niet gezien dat Wij het water naar de kale aarde drijven en er dan landbouwgewassen mee voortbrengen waarvan hun vee en zijzelf eten? Hebben zij dan geen inzicht?'),
(3531, 32, 28, 'En zij zeggen: "Wanneer zal dat succes zich voordoen als jullie gelijk hebben?"'),
(3532, 32, 29, 'Zeg: "Op de dag van het succes hebben zij die ongelovig zijn geen nut van hun geloof en krijgen zij geen uitstel."'),
(3533, 32, 30, 'Wend je dus van hen af en wacht af. Zij wachten ook af.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'O profeet, vrees God en gehoorzaam de ongelovigen en de huichelaars niet; God is wetend en wijs.'),
(3535, 33, 2, 'En volg wat aan jou door jouw Heer wordt geopenbaard. God is welingelicht over wat jullie doen.'),
(3536, 33, 3, 'En stel je vertrouwen op God. God is als voogd goed genoeg.'),
(3537, 33, 4, 'God heeft voor een man niet twee harten in zijn binnenste gemaakt. En Hij heeft jullie echtgenotes van wie jullie je scheiden [door uit te spreken dat zij als jullie moeders zijn] niet [werkelijk] tot jullie moeders gemaakt. En Hij heeft jullie aangenomen zonen niet [werkelijk] tot jullie zonen gemaakt. Dat is wat jullie met jullie monden zeggen. Maar God zegt de waarheid en Hij leidt op de juiste weg.'),
(3538, 33, 5, 'Noemt hen naar hun vaders; dat is rechtmatiger bij God. Maar als jullie hun vaders niet kennen, dan zijn zij jullie broeders in de godsdienst en jullie beschermelingen. Wat jullie daarbij per ongeluk fout doen is voor jullie geen overtreding, maar wel wat jullie uit de grond van jullie harten en opzettelijk doen. En God is vergevend en barmhartig.'),
(3539, 33, 6, 'De profeet heeft een nauwere band met de gelovigen dan zij onderling hebben en zijn echtgenotes zijn hun moeders. En de bloedverwanten hebben volgens Gods boek een nauwere band met elkaar dan met de gelovigen en de uitgewekenen. Alleen moeten jullie je medestanders behoorlijk behandelen. Dat is in het boek opgetekend.'),
(3540, 33, 7, 'En toen Wij met de profeten de overeenkomst aangingen en ook met jou en met Noeh, Ibrahiem, Moesa en \'Isa, de zoon van Marjam -- Wij zijn namelijk een solide overeenkomst met hen aangegaan --'),
(3541, 33, 8, 'opdat Hij de oprechten naar hun oprechtheid zal vragen. En voor de ongelovigen heeft Hij een pijnlijke bestraffing klaargemaakt.'),
(3542, 33, 9, 'Jullie die geloven! Gedenkt Gods genade aan jullie toen er troepen tot jullie kwamen en Wij tegen hen een wind zonden en troepen die jullie niet zagen. God doorziet wat jullie doen.'),
(3543, 33, 10, 'Toen zij van boven jullie en van onder jullie vandaan tot jullie kwamen en toen de ogen scheel keken en de harten in de kelen klopten en jullie van alles over God dachten.'),
(3544, 33, 11, 'Daar werden de gelovigen beproefd en hevig door elkaar geschud.'),
(3545, 33, 12, 'En toen de huichelaars en zij die in hun harten een ziekte hebben zeiden: "God en Zijn gezant hebben Ons slechts begoocheling toegezegd."'),
(3546, 33, 13, 'En toen een groep van hen zei: "Mensen van Jathrib! Dit is geen plaats voor jullie om te blijven; keert terug!" En een deel van hen vroeg de profeet om ontheffing, waarbij zij zeiden: "Onze huizen zijn onbeschermd." Maar die waren niet onbeschermd; zij wensten slechts te vluchten.'),
(3547, 33, 14, 'En als men bij hen van de zijkanten was binnengedrongen en men van hen gevraagd had aan de verzoeking toe te geven dan zouden zij het gedaan hebben en er slechts even mee geaarzeld hebben.'),
(3548, 33, 15, 'Toch hadden zij van tevoren met God de verbintenis aangegaan dat zij zich niet zouden omkeren om te vluchten. Maar over de verbintenis met God wordt verantwoording afgelegd.'),
(3549, 33, 16, 'Zeg: "De vlucht zal jullie niet baten, of jullie nu op de vlucht slaan voor het sterven of het doden. En aan jullie zal slechts weinig te genieten gelaten worden."'),
(3550, 33, 17, 'Zeg: "Wie is het die jullie tegen God beschermt als Hij jullie kwaad wil berokkenen of jullie barmhartigheid wil bewijzen?" Zij vinden voor zich buiten God geen beschermer en geen helper. *'),
(3551, 33, 18, 'God kent de belemmeraars onder jullie en hen die tot hun broeders zeggen: "Komt naar ons!" Maar in het heetst van de strijd komen zij slechts weinig voor,'),
(3552, 33, 19, 'terwijl zij tegenover jullie gierig zijn. En wanneer de angst komt zie je hen naar jou kijken terwijl hun ogen in het rond draaien als bij iemand die voor de dood in onmacht valt. Maar wanneer de angst voorbij is bevitten zij jullie met scherpe tongen uit gierigheid naar het goede. Zij zijn het die niet geloven en God maakt hun daden vruchteloos; dat is voor God gemakkelijk.'),
(3553, 33, 20, 'Zij rekenen erop dat de partijen niet zouden weggaan, maar als de partijen [nog eens] zouden komen zouden zij het liefst in de woestijn bij de bedoeïenen willen zijn om daar naar nieuws over jullie te vragen. En als zij bij jullie zouden zijn zouden zij maar weinig strijden.'),
(3554, 33, 21, 'Jullie hebben toch in Gods gezant een goed voorbeeld voor wie op God en de laatste dag hopen en God veel gedenken.'),
(3555, 33, 22, 'En toen de gelovigen de partijen zagen zeiden zij: "Dit is wat God en Zijn gezant ons hadden aangezegd; God en Zijn gezant spreken de waarheid." En het deed hun geloof en onderwerping [aan God] alleen maar toenemen.'),
(3556, 33, 23, 'Onder de gelovigen zijn er mannen die de verbintenis die zij met God zijn aangegaan oprecht nagekomen zijn. Sommigen hebben hun levenstaak volbracht en anderen wachten nog, maar hebben niets gewijzigd.'),
(3557, 33, 24, '[Dat is zo] opdat God de oprechten voor hun oprechtheid beloont en de huichelaars bestraft, als Hij dat wil, of zich genadig tot hen wendt; God is vergevend en barmhartig.'),
(3558, 33, 25, 'Maar God heeft hen die ongelovig zijn in hun woede teruggewezen zonder dat zij iets goeds bereikt hadden; God heeft de gelovigen voor het strijden gevrijwaard; God is sterk en machtig.'),
(3559, 33, 26, 'En God liet degenen van de mensen van het boek die hen geholpen hadden uit hun verdedigingswerken afdalen en Hij heeft hun harten schrik aangejaagd zodat jullie een groep doodden en een groep gevangen namen.'),
(3560, 33, 27, 'En Hij liet jullie hun land, hun woningen en hun bezittingen beërven en ook land dat jullie [tot dan toe] niet betreden hadden; God is almachtig.'),
(3561, 33, 28, 'O profeet, zeg tot jouw echtgenotes: "Als jullie het tegenwoordige leven en zijn praal zouden wensen, komt dan, dan zal ik jullie gebruiksgoederen geven en jullie op een vriendelijke manier wegzenden.'),
(3562, 33, 29, 'Maar als jullie God, Zijn gezant en de laatste woning wensen, dan heeft God voor haar onder jullie die goed doen een geweldig loon klaargemaakt."'),
(3563, 33, 30, 'Vrouwen van de profeet! Wie van jullie een duidelijke gruweldaad begaat, voor haar zal de bestraffing verdubbeld worden. Dat is voor God gemakkelijk. *'),
(3564, 33, 31, 'En wie van jullie aan God en Zijn gezant onderdanig is en deugdelijk handelt, aan haar zullen Wij haar loon twee maal geven en Wij hebben voor haar een voortreffelijke voorziening klaargemaakt.'),
(3565, 33, 32, 'Vrouwen van de profeet! Jullie zijn als geen van de andere vrouwen. Als jullie godvrezend zijn weest dan niet bedeesd bij het spreken opdat niet iemand in wiens hart een ziekte is begeerte krijgt. En spreekt op een behoorlijke manier.'),
(3566, 33, 33, 'En blijft in jullie huizen en vertoont jullie niet opgesmukt als vroeger in de tijd van de onwetendheid. En verricht de salaat, geeft de zakaat en gehoorzaamt God en Zijn gezant. God wenst slechts van jullie, de huisgenoten, de gruwel te verwijderen en jullie geheel rein te maken.'),
(3567, 33, 34, 'En gedenkt wat van Gods tekenen en de wijsheid in jullie huizen wordt voorgelezen. God is welwillend en welingelicht.'),
(3568, 33, 35, 'De mannen en vrouwen die zich [aan God] hebben overgegeven, de gelovige mannen en vrouwen, de onderdanige mannen en vrouwen, de oprechte mannen en vrouwen, de geduldig volhardende mannen en vrouwen, de deemoedige mannen en vrouwen, de mannen en vrouwen die aalmoezen geven, de mannen en vrouwen die vasten, de mannen en vrouwen die hun schaamstreek kuis bewaren, de mannen en vrouwen die God veel gedenken, voor hen heeft God vergeving en een geweldig loon klaargemaakt.'),
(3569, 33, 36, 'Het past een gelovige man en een gelovige vrouw niet, wanneer God en Zijn gezant iets beslist hebben, nog de vrije keus te hebben in hun beschikking. En wie God en Zijn gezant trotseert, dwaalt duidelijk.'),
(3570, 33, 37, 'Toen jij tot hem, aan wie God weldaden geschonken heeft en aan wie jij weldaden geschonken hebt, zei: "Houd jouw vrouw bij je en vrees God" en jij in jezelf verborgen hield wat God toch openbaar zou maken en jij voor de mensen vreesde, hoewel het God met meer recht toekomt dat jij Hem vreest? Toen Zaid de omgang met haar had beëindigd, hebben Wij haar tot jouw echtgenote gemaakt, opdat er voor de gelovigen geen belemmering zou zijn met betrekking tot de echtgenotes van hun aangenomen zonen, als dezen de omgang met haar beëindigd hebben. Gods beschikking wordt uitgevoerd.'),
(3571, 33, 38, 'Voor de profeet is er niets hinderlijks in wat God voor hem als verplichting heeft vastgesteld, volgens Gods gebruikelijke behandeling van hen die voor jullie tijd zijn heengegaan -- en Gods beschikking is een vaststaand besluit --,'),
(3572, 33, 39, 'die Gods zendingsopdrachten verkondigden en die Hem vreesden, maar die behalve God niemand vreesden; God is om af te rekenen goed genoeg.'),
(3573, 33, 40, 'Mohammed is de vader van geen enkele man uit jullie midden, maar hij is Gods gezant en het zegel van de profeten. En God is alwetend.'),
(3574, 33, 41, 'Jullie die geloven! Gedenkt God veelvuldig'),
(3575, 33, 42, 'en prijst Hem \'s ochtends en \'s avonds.'),
(3576, 33, 43, 'Hij is het die jullie zegent, en Zijn engelen ook, om jullie uit de duisternis naar het licht te voeren; Hij is barmhartig voor de gelovigen.'),
(3577, 33, 44, 'Hun begroeting op de dag dat zij Hem ontmoeten zal zijn: "Vrede" en voor hen is een voortreffelijk loon klaargemaakt.'),
(3578, 33, 45, 'O profeet, Wij hebben jou als getuige, als verkondiger van goed nieuws en als waarschuwer gezonden'),
(3579, 33, 46, 'en als iemand die met Zijn toestemming tot God oproept en als een helder verlichtende lamp.'),
(3580, 33, 47, 'En verkondig het goede nieuws aan de gelovigen dat zij van God een grote gunst zullen krijgen.'),
(3581, 33, 48, 'En gehoorzaam de ongelovigen en de huichelaars niet, sla geen acht op het leed dat zij aandoen en stel je vertrouwen op God; God is goed genoeg als voogd.'),
(3582, 33, 49, 'Jullie die geloven! Wanneer jullie gelovige vrouwen getrouwd hebben en jullie geven hun de scheiding voordat jullie haar aangeraakt hebben dan kunnen jullie voor haar geen wachttijd berekenen, maar treft wel een voorziening en zendt haar op een vriendelijke manier weg.'),
(3583, 33, 50, 'O profeet, Wij hebben aan jou toegestaan [om mee te trouwen]: jouw echtgenotes aan wie jij haar loon gegeven hebt, de slavinnen waarover jij beschikt uit de buit die God jou gegeven heeft, de dochters van je oom van vaderszijde, de dochters van je tante van vaderszijde, de dochters van je oom van moederszijde en de dochters van je tante van moederszijde die met jou zijn uitgeweken en ook een gelovige vrouw die zich zelf aan de profeet geeft als de profeet met haar trouwen wil. Dit is alleen aan jou voorbehouden en niet aan de gelovigen -- Wij weten wel wat Wij hun met betrekking tot hun echtgenotes en de slavinnen waarover zij beschikken als verplichting hebben opgelegd -- opdat het voor jou niet iets hinderlijks zou zijn; God is vergevend en barmhartig. *'),
(3584, 33, 51, 'Jij kunt uitstel geven aan wie van haar jij wilt en jij kunt bij jou laten verblijven wie jij wilt. En als jij verlangt naar iemand van haar die jij uitgesloten had, dan is dat geen overtreding voor jou. Dat maakt eerder dat zij vreugde beleven en niet bedroefd zijn en allen tevreden zijn met wat jij hun gegeven hebt. En God weet wat in jullie harten is; God is wetend en zachtmoedig.'),
(3585, 33, 52, 'Verder zijn voor jou geen vrouwen toegestaan, ook niet dat jij haar in de plaats van andere echtgenotes neemt ook al bevalt haar schoonheid je, maar wel slavinnen waarover jij beschikt. En God is over alles opziener.'),
(3586, 33, 53, 'Jullie die geloven! Gaat de huizen van de profeet slechts binnen als aan jullie toestemming is gegeven om [mee] te eten maar zonder [van tevoren te gaan] wachten tot het klaar is. Maar wanneer jullie uitgenodigd worden, gaat dan binnen. En wanneer jullie gegeten hebben gaat dan weer uit elkaar zonder te blijven praten; daarmee vallen jullie de profeet lastig en dan schaamt hij zich voor jullie, maar God schaamt zich niet voor de waarheid. En als jullie haar [ de vrouwen van de profeet] iets om te gebruiken vraagt, vraagt haar dat dan van achter een afscheiding. Dat is reiner voor jullie harten en haar harten. Het past jullie niet Gods gezant lastig te vallen, noch dat jullie ooit na hem met zijn echtgenotes trouwt. Dat is bij God afschuwelijk.'),
(3587, 33, 54, 'Of jullie openlijk iets doen of in het verborgene, God is alwetend.'),
(3588, 33, 55, 'Maar het is voor haar geen overtreding [ongesluierd te zijn] in aanwezigheid van haar vaders, haar zonen, haar broers, de zonen van haar broers, de zonen van haar zusters, hun vrouwen en de slavinnen waarover zij beschikken. En vreest God. God is van alles getuige.'),
(3589, 33, 56, 'God en Zijn engelen zegenen de profeet. Jullie die geloven! Zegent hem en groet hem door hem vrede toe te wensen.'),
(3590, 33, 57, 'Zij die God en de profeet tot last zijn, moge God hen in het tegenwoordige leven en in het hiernamaals vervloeken en voor hen een vernederende bestraffing klaarmaken.'),
(3591, 33, 58, 'En zij die de gelovige mannen en de gelovige vrouwen lastigvallen voor iets wat zij niet begaan hebben, beladen zich met laster en een duidelijke zonde.'),
(3592, 33, 59, 'O profeet! Zeg tot jouw echtgenotes, jouw dochters en de vrouwen van de gelovigen iets van haar overkleding over zich heen naar beneden te laten hangen. Dat bevordert het best dat men haar herkent en niet lastigvalt. En God is vergevend en barmhartig.'),
(3593, 33, 60, 'Als de huichelaars, zij die in hun harten een ziekte hebben en de verspreiders van valse geruchten in de stad niet ophouden dan zullen Wij jou zeker tegen hen laten optreden; dan zullen zij slechts korte tijd daarin jouw buren blijven.'),
(3594, 33, 61, 'Vervloekt zijn zij; waar men hen ook aantreft worden zij gegrepen en onverbiddelijk gedood,'),
(3595, 33, 62, 'volgens Gods gebruikelijke behandeling van hen die voor jullie tijd zijn heengegaan en jij zult in Gods gebruikelijke behandeling geen wijziging vinden.'),
(3596, 33, 63, 'De mensen vragen jou naar het uur. Zeg: "De kennis daarover is slechts bij God. Hoe zul jij het te weten komen? Misschien dat het uur dichtbij is."'),
(3597, 33, 64, 'God heeft de ongelovigen vervloekt en voor hen een vuurgloed klaargemaakt,'),
(3598, 33, 65, 'waarin zij voor immer en altijd zullen blijven; en zij zullen beschermer noch helper vinden.'),
(3599, 33, 66, 'Op de dag dat hun gezichten in het vuur rondgedraaid worden zeggen zij: "Ach hadden wij God maar gehoorzaamd en hadden wij de gezant maar gehoorzaamd."'),
(3600, 33, 67, 'En zij zeggen: "Onze Heer, wij hebben onze leidslieden en onze meesters gehoorzaamd, maar zij hebben ons op de dwaalweg gebracht.'),
(3601, 33, 68, 'Onze Heer, geef hun het dubbele van de bestraffing en vervloek hen met een grote vervloeking."'),
(3602, 33, 69, 'Jullie die geloven! Weest niet zoals zij die Moesa lastigvielen. God heeft hem toen onschuldig verklaard aan wat zij zeiden; Hij was bij God in hoog aanzien.'),
(3603, 33, 70, 'Jullie die geloven! Vreest God en spreekt passende woorden,'),
(3604, 33, 71, 'dan zal Hij jullie daden verbeteren en jullie je zonden vergeven. En wie God en Zijn gezant gehoorzaamt heeft een geweldige triomf behaald.'),
(3605, 33, 72, 'Wij hebben de toevertrouwde taak aan de hemelen, de aarde en de bergen aangeboden, maar zij weigerden haar te dragen en schrokken ervoor terug. De mens droeg haar -- hij is een onwetende zondaar --'),
(3606, 33, 73, 'opdat God de huichelaars en de huichelaarsters, de veelgodendienaars en -dienaressen zal bestraffen en opdat God zich genadig tot de gelovige mannen en vrouwen wendt; God is vergevend en barmhartig.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Lof zij God van wie is wat er in de hemelen en wat er op de aarde is en Hem zij lof in het hiernamaals; Hij is de wijze, de welingelichte.'),
(3608, 34, 2, 'Hij kent wat er in de aarde gaat en wat eruit komt, wat er uit de hemel afdaalt en wat erin opstijgt; Hij is de barmhartige, de vergevende.'),
(3609, 34, 3, 'Zij die ongelovig zijn zeggen: "Het uur komt niet tot ons." Zeg: "Wel zeker, bij mijn Heer, de kenner van het verborgene, het komt tot jullie. Hem ontgaat geen greintje in de hemelen noch op de aarde. En er is niets wat nog kleiner is noch groter of het staat in een duidelijk boek,'),
(3610, 34, 4, 'opdat Hij hen die geloven en de deugdelijke daden doen zal belonen; zij zijn het voor wie er vergeving is en een voortreffelijke voorziening.'),
(3611, 34, 5, 'Maar zij die tegen Onze tekenen proberen in te gaan om ze te ontkrachten; zij zijn het voor wie er een bestraffing door een pijnlijke plaag is.'),
(3612, 34, 6, 'Zij aan wie de kennis gegeven is zien dat wat er van jouw Heer naar jou is neergezonden de waarheid is en dat het naar de weg van de machtige, de lofwaardige leidt.'),
(3613, 34, 7, 'En zij die ongelovig zijn zeggen: "Zullen wij jullie een man aanwijzen, die jullie meedeelt dat jullie, wanneer jullie geheel vergaan zijn, deel zullen uitmaken van een nieuwe schepping?'),
(3614, 34, 8, 'Heeft hij over God bedrog verzonnen of heeft hij last van bezetenheid?" Integendeel, zij die niet in het hiernamaals geloven komen in de bestraffing terecht en verkeren in verregaande dwaling.'),
(3615, 34, 9, 'Hebben zij dan niet gezien naar wat er van de hemel en de aarde vóór hen is en wat achter hen is? Als Wij willen laten Wij de aarde met hen wegzinken of Wij laten stukken uit de hemel op hen neervallen. Daarin is een teken voor iedere schuldbewuste dienaar.'),
(3616, 34, 10, 'En Wij gaven van Onze kant aan Dawoed een gunst: "O bergen, zingt berouwvol met hem lof en o vogels, jullie ook." En Wij maakten voor hem het ijzer buigzaam.'),
(3617, 34, 11, '"Maak pantserhemden en meet het vlechtwerk goed af en handel deugdelijk; Ik doorzie wel wat jullie doen."'),
(3618, 34, 12, 'En aan Soelaimaan [maakten Wij] de wind [dienstbaar] die \'s morgens een maandreis en \'s middags een maandreis aflegt. En Wij lieten voor hem de bron van het gesmolten koper stromen. En onder de djinn waren er die met de toestemming van zijn Heer voor hem werkten; wie van hen van Ons bevel afweek lieten Wij van de bestraffing van de vuurgloed proeven.'),
(3619, 34, 13, 'Zij maakten voor hem wat hij wilde; heiligdommen, standbeelden, schotels als waterbekkens en stevig staande ketels. "Werkt, mensen van Dawoed, in dankbaarheid. Weinig van Mijn dienaren zijn dankbaar."'),
(3620, 34, 14, 'Toen Wij dan voor hem de dood beschikt hadden was het pas de houtworm die hen op zijn dood opmerkzaam maakte omdat die zijn staf opvrat. En toen hij viel werd het de djinn duidelijk dat zij, hadden zij het verborgene gekend, niet zo lang in de vernederende bestraffing gebleven waren.'),
(3621, 34, 15, 'De Saba? hadden in hun woongebied een teken: twee tuinen, een aan de rechterkant en een aan de linkerkant. "Eet van het levensonderhoud van jullie Heer en betuigt Hem dank; het is een goede streek en Hij is een vergevend Heer."'),
(3622, 34, 16, 'Maar zij wendden zich af en dus zonden Wij de overstroming van de dam en Wij veranderden hun beide tuinen in twee tuinen met een bittere vruchtopbrengst, tamarisken en een paar lotusbomen.'),
(3623, 34, 17, 'Daarmee vergolden Wij hun omdat zij ondankbaar waren. Vergelden Wij dan iemand anders dan de ondankbare zo?'),
(3624, 34, 18, 'En tussen hen en de steden die Wij gezegend hebben plaatsten Wij [een reeks van onderling] zichtbare steden en Wij bepaalden de reis erdoor: "Reist er bij nacht en bij dag veilig doorheen."'),
(3625, 34, 19, 'Maar zij zeiden: "Onze Heer vergroot de reisafstanden naar ons" en deden zichzelf onrecht aan. Wij maakten hen dus tot [het onderwerp van] verhalen en Wij lieten hen geheel vergaan. Daarin zijn zeker tekenen voor ieder die geduldig volhardt en die dank betuigt.'),
(3626, 34, 20, 'En de mening van Iblies over hen werd bevestigd: zij volgden hem, behalve een groep gelovigen.'),
(3627, 34, 21, 'Hij had geen macht over hen, maar het gebeurde opdat Wij hen die in het hiernamaals geloven zouden onderscheiden van wie erover in twijfel verkeert. Jouw Heer is het die over alles waakt.'),
(3628, 34, 22, 'Zeg: "Roept hen maar aan van wie jullie beweren dat zij naast God bestaan. Zij hebben geen greintje heerschappij in de hemelen, noch op de aarde. Zij hebben in geen van beide een aandeel en Hij heeft onder hen geen enkele helper."'),
(3629, 34, 23, 'Bij Hem heeft de voorspraak ook geen nut, behalve voor wie Hij het toestaat. En wanneer dan de vrees uit hun harten is weggenomen zeggen zij: "Wat heeft jullie Heer gezegd?" En dan zeggen zij: "De waarheid." Hij is de verhevene, de grote.'),
(3630, 34, 24, 'Zeg: "Wie voorziet vanuit de hemel en de aarde in jullie onderhoud?" Zeg: "God. Wij volgen de leidraad en jullie verkeren in duidelijke dwaling of omgekeerde'),
(3631, 34, 25, 'Zeg: "Jullie hoeven geen verantwoording af te leggen over wat wij begaan en wij hoeven geen verantwoording af te leggen over wat jullie doen."'),
(3632, 34, 26, 'Zeg: "Onze Heer zal ons samenbrengen. Dan zal Hij naar waarheid tussen ons uitspraak doen; Hij is het die uitspraak doet, de wetende."'),
(3633, 34, 27, 'Zeg: "Laat mij hen zien die jullie aan Hem als [zogenaamd goddelijke] metgezellen hebben toegevoegd. Welnee, Hij is God de machtige, de wijze."'),
(3634, 34, 28, 'En Wij hebben jou naar alle mensen gezamenlijk slechts gezonden als verkondiger van goed nieuws en als waarschuwer, maar de meeste mensen weten het niet.'),
(3635, 34, 29, 'En zij zeggen: "Wanneer zal deze aanzegging zich voordoen als jullie gelijk hebben?"'),
(3636, 34, 30, 'Zeg: "Jullie hebben een afspraak voor een dag waarvoor jullie geen uur te laat ontboden zullen worden, noch te vroeg."'),
(3637, 34, 31, 'Zij die ongelovig zijn zeggen: "Wij zullen niet in deze Koran geloven, noch in wat eraan vooraf is gegaan." Kon jij het maar zien wanneer de onrechtplegers bij hun Heer staan opgesteld en elkaar verwijten maken. Zij die onderdrukt werden zullen tegen hen die hoogmoedig waren zeggen: "Als jullie er niet geweest waren, dan waren wij gelovig geweest."'),
(3638, 34, 32, 'Maar zij die hoogmoedig waren zeggen tegen hen die onderdrukt werden: "Hebben wij jullie dan van de leidraad afgehouden nadat hij tot jullie gekomen was? Welnee, jullie waren boosdoeners."'),
(3639, 34, 33, 'En zij die onderdrukt werden zeggen tegen hen die hoogmoedig waren: "Welnee, [het kwam door jullie] listen van \'s nachts en overdag; toen jullie ons bevalen aan God geen geloof te hechten en aan Hem gelijken toe te schrijven." Heimelijk hebben zij dan spijt wanneer zij de bestraffing zien. Maar Wij leggen de halsketenen om de nekken van hen die ongelovig zijn. Zal aan hen soms iets anders vergolden worden dan zij deden?'),
(3640, 34, 34, 'Wij hebben geen waarschuwer naar een stad gezonden zonder dat haar inwoners die een luxeleven leidden zeiden: "Waarmee jullie gezonden zijn daar hechten wij geen geloof aan."'),
(3641, 34, 35, 'En zij zeiden: "Wij hebben zeer veel bezittingen en kinderen; wij zijn het niet die gestraft worden."'),
(3642, 34, 36, 'Zeg: "Mijn Heer voorziet ruimschoots in het levensonderhoud van wie Hij wil en ook met mate, maar de meeste mensen weten het niet"'),
(3643, 34, 37, 'Jullie bezittingen, noch jullie kinderen zijn het die jullie nader tot Ons zullen brengen, behalve dan als iemand gelooft en deugdelijk handelt; zij zijn het voor wie er een dubbele beloning is voor wat zij gedaan hebben en zij zullen veilig in de [feest]zalen zijn.'),
(3644, 34, 38, 'Maar zij die tegen Onze tekenen proberen in te gaan om ze te ontkrachten; zij zijn het die ter bestraffing worden voorgeleid.'),
(3645, 34, 39, 'Zeg: "Mijn Heer voorziet ruimschoots in het levensonderhoud van wie van Zijn dienaren Hij wil en ook met mate. En als jullie iets als bijdrage schenken, dan zal Hij het vergoeden; Hij is de beste voorziener."'),
(3646, 34, 40, 'En op de dag dat Hij hen allen verzamelt en dan tot de engelen zegt: "Zijn dezen het die jullie hebben gediend?"'),
(3647, 34, 41, 'Zij zeggen dan: "U zij geprezen! U bent ons tot vriend en zij niet. Integendeel, zij dienden de djinn; de meesten van hen geloven in hen."'),
(3648, 34, 42, '"Vandaag kunnen zij niets tot nut of schade voor elkaar uitrichten^ Ook zeggen Wij tot hen die onrecht pleegden: "Proeft de bestraffing met het vuur dat jullie loochenden."'),
(3649, 34, 43, 'En als aan hen Onze tekenen als duidelijke bewijzen voorgelezen worden zeggen zij: "Dat is een man die jullie alleen maar wenst af te brengen van wat jullie vaderen gewoon waren te dienen." En zij zeggen: "Dit is slechts laster die verzonnen is." En zij die aan de waarheid geen geloof hechten wanneer zij tot hen komt zeggen: "Dit is duidelijk slechts toverij."'),
(3650, 34, 44, 'Wij hebben hun geen boeken gegeven die zij konden bestuderen, noch hebben Wij voor jouw tijd een waarschuwer tot hen gezonden.'),
(3651, 34, 45, 'Maar zij die er voor hun tijd waren, hebben [de gezanten ook] van leugens beticht, terwijl zij [nu] nog niet een tiende bereikt hebben van wat Wij aan hen gegeven hadden. Toch hadden zij ook Mijn gezanten van leugens beticht. En hoe was Mijn terechtwijzing dan? *'),
(3652, 34, 46, 'Zeg: "Ik roep jullie tot slechts één ding op; dat jullie je getweeën en afzonderlijk voor God opstellen en dan nadenken. In jullie medeburger is geen bezetenheid, hij is voor jullie slechts een waarschuwer voor een strenge bestraffing."'),
(3653, 34, 47, 'Zeg: "Ik vraag jullie geen loon; het is in jullie eigen voordeel. Slechts God is belast met mijn loon; Hij is van alles getuige."'),
(3654, 34, 48, 'Zeg: "Mijn Heer treft met de waarheid; Hij is de kenner van de verborgenheden."'),
(3655, 34, 49, 'Zeg: "De waarheid is gekomen. De onzin kan niets laten beginnen en niets herhalen."'),
(3656, 34, 50, 'Zeg: "Als ik dwaal, dan dwaal ik slechts ten koste van mijzelf, maar als ik het goede pad volg, dan komt dat door wat mijn Heer aan mij geopenbaard heeft; Hij is horend en nabij."'),
(3657, 34, 51, 'Kon jij het maar zien wanneer zij, zonder te kunnen ontkomen, van schrik bevangen zijn en vanuit een nabije plaats weggegrist worden'),
(3658, 34, 52, 'en zeggen: "Wij geloven erin", maar hoe zouden zij dat vanuit een verafgelegen plaats nog kunnen bereiken?'),
(3659, 34, 53, 'Zij hechtten er vroeger toch ook geen geloof aan en zij proberen vanuit een verafgelegen plaats het verborgene te treffen.'),
(3660, 34, 54, 'En wat zij begeren wordt voor hen onbereikbaar gemaakt zoals dat vroeger met hun soortgenoten gedaan was; zij verkeerden immers in hevige twijfel.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Lof zij God, de grondlegger van de hemelen en de aarde, die de engelen tot gezanten met vleugels gemaakt heeft, twee, drie of vier! Hij voegt aan de schepping toe wat Hij wil; God is almachtig.'),
(3662, 35, 2, 'De barmhartigheid die God voor de mensen openstelt kan niemand tegenhouden. En wat Hij tegenhoudt kan niemand na Hem nog wegzenden. Hij is de machtige, de wijze.'),
(3663, 35, 3, 'O mensen, gedenkt Gods genade aan jullie. Is er een andere schepper dan God, die vanuit de hemel en de aarde in jullie onderhoud voorziet? Er is geen god dan Hij! Hoe worden jullie zo afgeleid?'),
(3664, 35, 4, 'Maar als zij jou van leugens betichten; al voor jouw tijd zijn er gezanten van leugens beticht geweest. En aan God worden alle zaken voorgelegd.'),
(3665, 35, 5, 'O mensen, Gods toezegging is waar. Laat het tegenwoordige leven jullie dus niet begoochelen en laat de begoochelaar jullie dan niet begoochelen met betrekking tot God.'),
(3666, 35, 6, 'De satan is voor jullie een vijand. Neemt dus maar aan dat hij een vijand is. Hij roept zijn partij slechts op opdat zij in de vuurgloed zullen thuishoren.'),
(3667, 35, 7, 'Zij die ongelovig zijn, voor hen is er een strenge bestraffing, maar zij die gelovig zijn en de deugdelijke daden doen, voor hen is er vergeving en een groot loon.'),
(3668, 35, 8, 'En hij dan voor wie de slechtheid van zijn handelen aantrekkelijk gemaakt is en die het dan ook als goed beschouwt? Maar God brengt tot dwaling wie Hij wil en Hij brengt op het goede pad wie Hij wil. Je moet uit wroeging over hen niet buiten jezelf raken. God weet wat zij doen.'),
(3669, 35, 9, 'En God is het die de winden uitzendt die dan wolken opdrijven. Dan drijven Wij ze naar een dode streek en laten de aarde ermee herleven nadat zij dood was. Zo is het ook met de herrijzenis.'),
(3670, 35, 10, 'Als iemand de macht wenst? welnu de macht behoort God geheel toe. Tot Hem stijgen de goede woorden op en Hij verheft de deugdzame daad. Maar voor hen die de slechte daden beramen is er een strenge bestraffing; de list van hen gaat verloren.'),
(3671, 35, 11, 'God heeft jullie uit aarde en dan uit een druppel geschapen en dan heeft Hij jullie tot echtgenoten gemaakt. Geen vrouw is zwanger en baart zonder dat Hij het weet; niemands levensduur wordt verlengd en niemands levensduur wordt verkort of het staat in een boek. Dat is voor God gemakkelijk.'),
(3672, 35, 12, 'De beide zeeën zijn niet gelijk; de ene is zoet, fris en geschikt om te drinken en de andere zout en pekelig. En uit beide krijgen jullie vers vlees te eten en jullie halen er sieraden uit op om je mee te tooien. En jullie zien de schepen haar doorklieven [en dat is] opdat jullie streven naar een gunst van Hem; misschien zullen jullie dank betuigen.'),
(3673, 35, 13, 'God laat de nacht overgaan in de dag en Hij laat de dag overgaan in de nacht en Hij heeft de zon en de maan dienstbaar gemaakt. Alles loopt tot een vastgestelde termijn. Dat is God, jullie Heer; Hij heeft de heerschappij. Zij die jullie in plaats van Hem aanroepen hebben nog geen flintertje heerschappij.'),
(3674, 35, 14, 'Als jullie hen aanroepen horen zij jullie aanroep niet en als ze al horen dan geven ze aan jullie geen gehoor. En op de opstandingsdag zullen zij jullie veelgodendienst verloochenen. En niemand kan mededelingen doen als iemand die welingelicht is.'),
(3675, 35, 15, 'O mensen, jullie zijn het die behoefte aan God hebben, maar God, Hij is de behoefteloze, de lofwaardige.'),
(3676, 35, 16, 'Als Hij wil vaagt Hij jullie weg en brengt Hij een nieuwe schepping.'),
(3677, 35, 17, 'Dat is voor God niet lastig.'),
(3678, 35, 18, 'Niemand is belast met de last van een ander en als iemand die zwaar beladen is oproept om [mee] te dragen, dan zal toch niets van hem gedragen worden ook al betreft het een verwant. Jij kunt slechts hen waarschuwen die God in het verborgen vrezen en de salaat verrichten. En als iemand zich gelouterd heeft dan is hij in zijn eigen voordeel gelouterd; bij God is de bestemming.'),
(3679, 35, 19, 'De blinde en de ziende zijn niet gelijk,'),
(3680, 35, 20, 'noch de duisternissen en het licht,'),
(3681, 35, 21, 'noch de schaduw en de hitte;'),
(3682, 35, 22, 'ook de levenden en de doden zijn niet gelijk. God doet horen wie Hij wil, maar jij kunt wie er in de graven zijn niet doen horen.'),
(3683, 35, 23, 'Jij bent slechts een waarschuwer.'),
(3684, 35, 24, 'Wij hebben jou met de waarheid gezonden als verkondiger van goed nieuws en als waarschuwer. En er is geen gemeenschap of er is wel een waarschuwer geweest.'),
(3685, 35, 25, 'En als zij jou van leugens betichten, zij die er voor hun tijd waren hebben dat ook gedaan. Hun gezanten kwamen tot hen met de duidelijke bewijzen, met de Zoeboer en met het verlichtende boek.'),
(3686, 35, 26, 'Toen heb Ik hen die ongelovig waren gegrepen. En hoe was Mijn terechtwijzing dan?'),
(3687, 35, 27, 'Heb jij niet gezien dat God uit de hemel water laat neerdalen? Dan brengen Wij daarmee verschillende soorten vruchten voort. En bij de bergen zijn er witte en rode lagen, verschillend van kleur, en ook gitzwarte.'),
(3688, 35, 28, 'Ook bij de mensen en bij de dieren en het vee zijn er zo verschillende kleuren en soorten. God wordt slechts gevreesd door de geleerden onder Zijn dienaren. God is machtig en vergevend.'),
(3689, 35, 29, 'Zij die het boek van God voorlezen, de salaat verrichten en in het geheim en openbaar bijdragen geven van wat Wij hun voor hun levensonderhoud gegeven hebben, verwachten koopwaar die niet verloren gaat,'),
(3690, 35, 30, 'opdat Hij hun hun volle loon geeft en van Zijn goedgunstigheid nog meer zal geven; Hij is vergevend en dank betuigend.'),
(3691, 35, 31, 'En wat Wij aan jou van het boek geopenbaard hebben, dat is de waarheid, ter bevestiging van wat er voordien al was; God is over Zijn dienaren goed ingelicht en Hij doorziet hen.'),
(3692, 35, 32, 'Toen hebben Wij het boek als erfenis gegeven aan hen die Wij van Onze dienaren hadden uitverkoren. Onder hen zijn er die zichzelf onrecht aandoen, die gematigd zijn en die met Gods toestemming wedijveren in goede daden. Dit is de grote goedgunstigheid:'),
(3693, 35, 33, 'Zij zullen de tuinen van \'Adn binnengaan. Zij tooien zich daarin met armbanden van goud en met parels en hun kleren zijn daar van zijde.'),
(3694, 35, 34, 'En zij zeggen: "Lof zij God die van ons de droefheid heeft weggenomen. Vergevend en dank betuigend is onze Heer,'),
(3695, 35, 35, 'die ons door Zijn goedgunstigheid in de woning van het [eeuwige] verblijf heeft geplaatst. Wij worden daarin niet door vermoeidheid overvallen, noch worden wij daarin door matheid overvallen."'),
(3696, 35, 36, 'Maar zij die ongelovig zijn, voor hen is er het vuur van de hel; er zal voor hen geen eind aan gemaakt worden zodat zij sterven, noch zal de bestraffing ermee voor hen verlicht worden. Zo vergelden Wij aan iedere ondankbare ongelovige.'),
(3697, 35, 37, 'Zij zullen erin uitschreeuwen: "Onze Heer, haal ons hier uit, dan zullen wij deugdelijk handelen, anders dan wij altijd deden." "Hebben Wij jullie dan niet zo lang laten leven dat iemand zich zou kunnen laten vermanen als hij het wilde? En er was een waarschuwer tot jullie gekomen! Proeft het dus maar; de onrechtplegers hebben geen helper."'),
(3698, 35, 38, 'God is de kenner van het verborgene van de hemelen en de aarde; Hij weet wat er binnen in de harten is.'),
(3699, 35, 39, 'Hij is het die jullie tot elkaar opvolgende geslachten op de aarde gemaakt heeft. En wie ongelovig is, diens ongeloof is in zijn nadeel; voor de ongelovigen maakt hun ongeloof de afschuw bij hun Heer alleen maar erger en voor de ongelovigen brengt hun ongeloof hun alleen maar groter verlies.'),
(3700, 35, 40, 'Zeg: "Hoe zien jullie je [zogenaamd goddelijke] metgezellen die jullie in plaats van God aanroepen? Laat mij dan zien wat zij van de aarde geschapen hebben." Hebben zij een aandeel in de hemelen of hebben Wij hun een boek gegeven zodat zij op een duidelijk bewijs steunen? Welnee, de onrechtplegers beloven elkaar slechts begoocheling. *'),
(3701, 35, 41, 'God houdt de hemelen en de aarde vast, zodat zij niet vergaan; als zij zouden vergaan, dan zou niemand behalve Hij hen kunnen vasthouden. Hij is zachtmoedig en vergevend.'),
(3702, 35, 42, 'Zij hebben bij God dure eden gezworen dat zij, als tot hen een waarschuwer zou komen, zeker beter het goede pad op zouden gaan dan enige andere gemeenschap. Maar toen tot hen een waarschuwer kwam kregen zij alleen maar meer afkeer,'),
(3703, 35, 43, 'terwijl zij zich op de aarde hoogmoedig gedroegen en slechte plannen beraamden. Maar slechte plannen overmeesteren alleen maar de beramers ervan. Kunnen zij dan iets anders verwachten dan de gebruikelijke behandeling van hen die er eertijds waren? Jij zult dus in Gods gebruikelijke behandeling geen verandering vinden en je zult in Gods gebruikelijke behandeling geen wijziging vinden.'),
(3704, 35, 44, 'Hebben zij dan niet op de aarde rondgereisd, zodat zij konden zien hoe het einde was van hen die er voor hen waren en die sterker waren dan zij? En er is niets in de hemelen en op de aarde wat iets tegen God kan doen; Hij is wetend en vrijmachtig.'),
(3705, 35, 45, 'Als God de mensen zou aanrekenen wat zij begaan hebben zou Hij op het aardoppervlak geen dier overlaten. Maar Hij geeft hun uitstel tot een vastgestelde termijn. En wanneer hun termijn komt, dan doorziet God Zijn dienaren.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'J[aa?] S[ien].'),
(3707, 36, 2, 'Bij de wijze Koran!'),
(3708, 36, 3, 'Jij behoort tot de gezondenen,'),
(3709, 36, 4, 'op een juiste weg.'),
(3710, 36, 5, 'Het is de neerzending door de machtige, de barmhartige,'),
(3711, 36, 6, 'opdat jij mensen waarschuwt van wie de voorvaderen niet gewaarschuwd waren, zodat zij onoplettend zijn.'),
(3712, 36, 7, 'De uitspraak over de meesten van hen is bewaarheid; toch geloven zij niet.'),
(3713, 36, 8, 'Wij hebben de halsketenen om hun nekken gelegd en die komen tot hun kinnen zodat zij omhoog moeten kijken.'),
(3714, 36, 9, 'En Wij hebben vóór hen een barrière en achter hen een barrière gemaakt en hen met een sluier bedekt zodat zij dus niets kunnen zien.'),
(3715, 36, 10, 'En het maakt voor hen niet uit of jij hen waarschuwt of niet; zij geloven niet.'),
(3716, 36, 11, 'Jij kunt slechts hem waarschuwen die de vermaning volgt en de Erbarmer in het verborgen vreest. Verkondig hem dus vergeving en een voortreffelijk loon.'),
(3717, 36, 12, 'Wij maken de doden weer levend en Wij schrijven wat zij vroeger gedaan hebben op, ook de sporen die zij nalaten. Alles hebben Wij opgesomd in een duidelijk en voorbeeldig boek.'),
(3718, 36, 13, 'Maak voor hen een vergelijking met de bewoners van de stad toen de gezondenen tot hen kwamen.'),
(3719, 36, 14, 'Toen Wij er twee tot hen zonden en zij die beiden van leugens betichtten. Toen lieten Wij als versterking een derde komen. Zij zeiden dus: "Wij zijn tot jullie gezonden."'),
(3720, 36, 15, 'Zij zeiden: "Jullie zijn slechts mensen zoals wij. De Erbarmer heeft niets neergezonden; jullie liegen alleen maar."'),
(3721, 36, 16, 'Zij zeiden: "Onze Heer weet dat wij echt tot jullie zijn gezonden.'),
(3722, 36, 17, 'Wij hebben alleen maar de plicht van de duidelijke verkondiging."'),
(3723, 36, 18, 'Zij zeiden: "Wij zien in jullie een slecht voorteken. Als jullie niet ophouden zullen wij jullie stenigen en dan zal jullie van onze kant een pijnlijke bestraffing treffen."'),
(3724, 36, 19, 'Zij zeiden: "Jullie slechte voorteken hangt met jullie zelf samen. Hebben jullie je laten vermanen? Welnee, jullie zijn overmatige mensen."'),
(3725, 36, 20, 'En er kwam uit het verste deel van de stad een man aanrennen die tot hen zei: "Mijn volk! Volgt de gezondenen.'),
(3726, 36, 21, 'Volgt hen die jullie geen loon vragen en die het goede pad volgen.'),
(3727, 36, 22, 'En waarom zou ik niet Hem dienen die mij geschapen heeft en tot wie jullie teruggebracht zullen worden?'),
(3728, 36, 23, 'Zal ik mij in plaats van de Erbarmer andere goden nemen? Als de Erbarmer voor mij tegenspoed wenst baat hun voorspraak mij niets en kunnen zij mij ook niet redden.'),
(3729, 36, 24, 'Dan zou ik werkelijk in duidelijke dwaling verkeren.'),
(3730, 36, 25, 'Ik geloof in jullie Heer; luistert dus naar mij."'),
(3731, 36, 26, 'Tot hem werd gezegd: "Ga de tuin binnen!" Hij zei: "Ach had mijn volk maar geweten,'),
(3732, 36, 27, 'dat mijn Heer mij vergeven en tot een van de geëerden gemaakt heeft." *'),
(3733, 36, 28, 'Maar na zijn tijd zonden Wij geen troepenmacht tot zijn volk, Wij zonden ook niets neer.'),
(3734, 36, 29, 'Het was slechts één schreeuw en zij waren uitgeblust!'),
(3735, 36, 30, 'Wee de dienaren! Er komt geen gezant tot hen of zij drijven de spot met hem.'),
(3736, 36, 31, 'Hebben zij niet gezien hoeveel generaties Wij voor hun tijd vernietigd hebben en dat zij niet tot hen terugkeren?'),
(3737, 36, 32, 'En zij zullen allen tezamen bij Ons worden voorgeleid.'),
(3738, 36, 33, 'En een teken voor hen is de dode aarde die Wij tot leven brengen en waaruit Wij zaadkorrels voortbrengen zodat zij ervan eten.'),
(3739, 36, 34, 'En Wij hebben erop tuinen van palmen en wijnstokken gemaakt en bronnen laten ontspringen,'),
(3740, 36, 35, 'opdat zij van de vruchten daarvan en van wat hun handen maken kunnen eten. Zullen zij dan geen dank betuigen?'),
(3741, 36, 36, 'Geprezen zij Hij die alles geschapen heeft wat paarsgewijze voorkomt bij wat de aarde voortbrengt, bij hen zelf en bij wat zij niet weten.'),
(3742, 36, 37, 'En een teken voor hen is de nacht. Wij stropen de dag ervan af en dan zijn zij in het duister!'),
(3743, 36, 38, 'En de zon loopt tot haar verblijfplaats. Dat is de verordening van de machtige, de wetende.'),
(3744, 36, 39, 'En voor de maan hebben Wij standen verordend zodat zij er ten slotte uitziet als een oude verschrompelde dadelstengel.'),
(3745, 36, 40, 'Het past de zon niet de maan te bereiken en de nacht niet dat hij de dag inhaalt. Alle zweven zij in een hemelbaan.'),
(3746, 36, 41, 'En een teken is het voor hen dat Wij hun nakomelingen op het volbeladen schip gedragen hebben.'),
(3747, 36, 42, 'En Wij hebben voor hen iets overeenkomstigs geschapen waar zij op kunnen gaan.'),
(3748, 36, 43, 'En als Wij willen laten Wij hen verdrinken; dan helpt om hulp roepen niet en worden zij niet gered.'),
(3749, 36, 44, 'Behalve uit barmhartigheid van Ons en als een tijdelijk genot.'),
(3750, 36, 45, 'En wanneer tot hen gezegd wordt: "Vreest wat er vóór jullie is en wat er achter jullie is; misschien zal aan jullie barmhartigheid bewezen worden."'),
(3751, 36, 46, 'Maar er komt geen enkel teken van hun Heer tot hen of zij keren zich ervan af.'),
(3752, 36, 47, 'En wanneer tot hen gezegd wordt: "Geeft bijdragen van wat God jullie voor je levensonderhoud gegeven heeft" dan zeggen zij die ongelovig zijn tegen hen die gelovig zijn: "Zullen wij voedsel geven aan wie God het kan geven als Hij wil? Jullie verkeren in duidelijke dwaling."'),
(3753, 36, 48, 'En zij zeggen: "Wanneer zal deze aanzegging zich voordoen, als jullie gelijk hebben?"'),
(3754, 36, 49, 'Zij wachten slechts op één schreeuw die hen zal grijpen terwijl zij aan het twisten zijn.'),
(3755, 36, 50, 'Dan zullen zij geen testament maken en ook niet meer tot hun familie terugkeren.'),
(3756, 36, 51, 'Er wordt op de bazuin geblazen en dan komen zij uit de graven naar hun Heer aangesneld.'),
(3757, 36, 52, 'Zij zeggen: "Wee ons! Wie heeft ons uit deze rustplaats van ons laten opstaan? Dit is wat de Erbarmer ons aangezegd had; de gezondenen hadden gelijk."'),
(3758, 36, 53, 'Het is slechts één schreeuw en zij worden gezamenlijk bij Ons voorgeleid.'),
(3759, 36, 54, '"Vandaag zal niemand in iets onrecht worden aangedaan. En aan jullie wordt slechts vergolden voor wat jullie hebben gedaan."'),
(3760, 36, 55, 'Zij die in de tuin thuis zijn houden zich vandaag blij bezig.'),
(3761, 36, 56, 'Zij en hun echtgenotes zijn in een schaduwrijke omgeving terwijl zij er op ligbanken achteroverleunen.'),
(3762, 36, 57, 'Zij hebben er vruchten en wat zij maar verlangen.'),
(3763, 36, 58, '"Vrede!"is [voor hen] een [welkomst]woord dat van een barmhartige Heer komt.'),
(3764, 36, 59, '"Zondert jullie vandaag van hen af, jullie boosdoeners! *'),
(3765, 36, 60, 'Heb Ik jullie, kinderen van Adam, niet opgedragen dat jullie de satan niet moeten dienen -- hij is een verklaarde vijand voor jullie --'),
(3766, 36, 61, 'en dat jullie Mij moeten dienen? Dat is een juiste weg.'),
(3767, 36, 62, 'Hij heeft uit jullie midden vele schepselen tot dwaling gebracht. Hadden jullie dan geen verstand?'),
(3768, 36, 63, 'Dat is de hel die jullie was aangezegd.'),
(3769, 36, 64, 'Braadt er vandaag in omdat jullie ongelovig waren."'),
(3770, 36, 65, 'Vandaag verzegelen Wij hun monden, maar hun handen spreken tot Ons en hun voeten getuigen van wat zij begaan hebben.'),
(3771, 36, 66, 'En als Wij wilden, dan zouden Wij hun ogen het gezichtsvermogen ontnemen. Dan zouden zij naar de weg rennen, maar hoe zouden zij dan zien?'),
(3772, 36, 67, 'En als Wij wilden, dan zouden Wij hen ter plekke van gedaante veranderen. Dan zouden zij niet vooruit kunnen gaan noch terugkeren.'),
(3773, 36, 68, 'En als Wij iemand een lang leven geven, dan laten Wij zijn gestel slechter worden. Hebben zij dan geen verstand?'),
(3774, 36, 69, 'Wij hebben hem de dichtkunst niet geleerd, zij betaamt hem ook niet. Het is niets anders dan een vermaning en een duidelijke Koran'),
(3775, 36, 70, 'om wie er leven te waarschuwen en opdat de uitspraak aan de ongelovigen bewaarheid wordt.'),
(3776, 36, 71, 'Hebben zij dan niet gezien dat Wij als een van de dingen die Onze handen gemaakt hebben voor hen vee hebben geschapen, waarover zij dan de beschikking hebben?'),
(3777, 36, 72, 'En Wij hebben ze aan hen onderworpen: sommige kunnen zij berijden en andere, daar eten zij van.'),
(3778, 36, 73, 'Zij hebben daarmee veel nuttigheden en soorten drank. Zullen zij dan niet dank betuigen?'),
(3779, 36, 74, 'Maar zij hebben zich in plaats van God andere goden genomen opdat zij misschien hulp zullen krijgen.'),
(3780, 36, 75, 'Zij kunnen hen niet helpen, zij zijn voor hen een troepenmacht die zelf wordt voorgeleid.'),
(3781, 36, 76, 'Laat wat zij zeggen jou niet bedroefd maken. Wij weten wat zij in het geheim en wat zij openlijk doen.'),
(3782, 36, 77, 'Heeft de mens dan niet gezien dat Wij hem uit een druppel geschapen hebben? Toch is hij een duidelijke tegenstander.'),
(3783, 36, 78, 'Hij maakt een vergelijking met Ons en vergeet hoe hij geschapen is. Hij zegt: "Wie maakt de beenderen weer levend als zij gruis zijn?"'),
(3784, 36, 79, 'Zeg: "Hij die ze de eerste maal heeft laten ontstaan zal ze weer levend maken. En Hij kent alles wat geschapen is,'),
(3785, 36, 80, 'Hij die voor jullie uit groene bomen vuur gemaakt heeft zodat jullie er meteen een vuur mee kunnen ontsteken."'),
(3786, 36, 81, 'Is Hij die de hemelen en de aarde geschapen heeft niet bij machte iets te scheppen wat aan hen gelijk is? Ja zeker, Hij is de schepper, de wetende.'),
(3787, 36, 82, 'Wanneer Hij iets wenst, is zijn bevel dat Hij ertegen zegt: "Wees" en het is.'),
(3788, 36, 83, 'Geprezen zij dus Hij in wiens hand de heerschappij over alles is en tot wie jullie worden teruggebracht.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Bij de zich in rijen opstellenden,'),
(3790, 37, 2, 'de scheldend afschrikkenden'),
(3791, 37, 3, 'en een vermaning voorlezenden!'),
(3792, 37, 4, 'Voorwaar, jullie god is één,'),
(3793, 37, 5, 'de Heer van de hemelen, de aarde en wat er tussen beide is en de Heer van de plaatsen van opkomst.'),
(3794, 37, 6, 'Wij hebben de dichtstbijzijnde hemel met de pracht van de sterren opgesierd'),
(3795, 37, 7, 'om ook elke opstandige satan af te weren,'),
(3796, 37, 8, 'zodat zij niet naar de allerhoogste raad van voornaamsten kunnen luisteren. En er wordt van elke kant tegen hen aan gegooid'),
(3797, 37, 9, 'om hen te verjagen -- en voor hen is er een voortdurende bestraffing --'),
(3798, 37, 10, 'behalve als iemand toch iets opvangt; hij wordt dan achtervolgd door een brandende staartster.'),
(3799, 37, 11, 'Vraag hun maar om uitsluitsel of zij het moeilijkst te scheppen zijn of anderen die Wij geschapen hebben; hen hebben Wij van kleverige klei geschapen.'),
(3800, 37, 12, 'Nee, jij bent verbaasd, maar zij schimpen.'),
(3801, 37, 13, 'Wanneer zij vermaand worden gedenken zij niet.'),
(3802, 37, 14, 'Wanneer zij een teken zien lachen zij schamper'),
(3803, 37, 15, 'en zeggen: "Dit is duidelijk slechts toverij.'),
(3804, 37, 16, 'Wanneer wij gestorven zijn en stof en botten geworden, zullen wij dan opgewekt worden?'),
(3805, 37, 17, 'En onze vaderen dan, die er eertijds waren?"'),
(3806, 37, 18, 'Zeg: "Ja zeker, en jullie zullen onderdanig zijn."'),
(3807, 37, 19, 'Dan klinkt slechts één afschrikkende kreet en dan zullen zij [staan te] kijken'),
(3808, 37, 20, 'en zeggen: "Wee ons, dit is de oordeelsdag!"'),
(3809, 37, 21, '"Dit is de dag van de schifting die jullie hebben geloochend." *'),
(3810, 37, 22, '"Verzamelt hen die onrecht hebben gepleegd, hun echtgenoten en wat zij hebben gediend,'),
(3811, 37, 23, 'in plaats van God. Voert hen dan op de weg naar het hellevuur.'),
(3812, 37, 24, 'En laat hen zich opstellen, want zij moeten verantwoording afleggen."'),
(3813, 37, 25, '"Waarom hebben jullie elkaar niet geholpen?"'),
(3814, 37, 26, 'Maar nee, vandaag geven zij zich over.'),
(3815, 37, 27, 'Zij komen op elkaar toe om elkaar te ondervragen.'),
(3816, 37, 28, 'Zij zeggen: "Jullie kwamen altijd van rechts op ons toe."'),
(3817, 37, 29, 'Zij zeggen: "Welnee, wij waren geen gelovigen'),
(3818, 37, 30, 'en wij hadden geen macht over jullie, maar jullie waren onbeschaamde mensen.'),
(3819, 37, 31, 'En dus is de uitspraak van onze Heer tegen ons bewaarheid. Wij zullen het proeven.'),
(3820, 37, 32, 'Wij hebben jullie misleid, maar wij waren zelf misleid."'),
(3821, 37, 33, 'Op die dag zijn zij dus deelgenoten in de bestraffing.'),
(3822, 37, 34, 'Zo doen Wij met de boosdoeners.'),
(3823, 37, 35, 'Toen tot hen gezegd werd: "Er is geen god dan God" waren zij hoogmoedig'),
(3824, 37, 36, 'en zeiden: "Zullen wij onze goden voor een bezeten dichter verlaten?"'),
(3825, 37, 37, 'Welnee, hij is met de waarheid gekomen en bevestigt de gezondenen.'),
(3826, 37, 38, 'Maar jullie zullen de pijnlijke bestraffing proeven.'),
(3827, 37, 39, 'En aan jullie wordt slechts vergolden wat jullie gedaan hebben.'),
(3828, 37, 40, 'Maar [dat geldt] niet voor de toegewijde dienaren van God.'),
(3829, 37, 41, 'Zij zijn het voor wie er een vastgestelde voorziening is:'),
(3830, 37, 42, 'Vruchten; en zij worden geëerd'),
(3831, 37, 43, 'in de tuinen van de gelukzaligheid;'),
(3832, 37, 44, 'op rustbanken zitten zij tegenover elkaar,'),
(3833, 37, 45, 'terwijl een drinkbeker bij hen wordt rondgegeven [waarin een drank is] uit een bron,'),
(3834, 37, 46, 'die helderwit is en aangenaam voor de drinkers,'),
(3835, 37, 47, 'zonder iets schadelijks erin en waarvan zij niet beneveld raken.'),
(3836, 37, 48, 'En bij hen zijn gezellinnen met afgewende blikken en met grote ogen,'),
(3837, 37, 49, 'alsof zij goed bewaarde eieren zijn.'),
(3838, 37, 50, 'Zij komen op elkaar toe om elkaar te ondervragen.'),
(3839, 37, 51, 'Iemand uit hun midden zegt: "Ik had een kameraad,'),
(3840, 37, 52, 'die zei: \'Ben jij echt een van hen die denken dat het waar is?'),
(3841, 37, 53, 'Wanneer wij gestorven zijn en stof en botten geworden, zullen wij dan geoordeeld worden??"'),
(3842, 37, 54, 'Hij zegt: "Willen jullie naar beneden kijken?"'),
(3843, 37, 55, 'Dan kijkt hij naar beneden en ziet hem midden in het hellevuur.'),
(3844, 37, 56, 'Hij zegt: "Bij God, bijna had jij mij in het verderf gestort.'),
(3845, 37, 57, 'Zonder Gods genade zou ik een van hen geweest zijn die voorgeleid worden.'),
(3846, 37, 58, 'Maar wij zijn toch niet dood?'),
(3847, 37, 59, 'Alleen onze eerste dood hebben we gehad. En wij worden toch ook niet gestraft?'),
(3848, 37, 60, 'Dit is de geweldige triomf!'),
(3849, 37, 61, 'Iets dergelijks, daar moet men naar toewerken."'),
(3850, 37, 62, 'Is dat beter als gastverblijf of de zakkoemboom?'),
(3851, 37, 63, 'Wij hebben die tot een verzoeking gemaakt voor de onrechtplegers.'),
(3852, 37, 64, 'Het is een boom die uit de oorsprong van het hellevuur tevoorschijn komt,'),
(3853, 37, 65, 'waarvan de knoppen zijn als satanskoppen.'),
(3854, 37, 66, 'Daar eten zij van en vullen hun buiken ermee.'),
(3855, 37, 67, 'Daaroverheen krijgen zij dan een mengsel van gloeiend water.'),
(3856, 37, 68, 'Hun terugkeer is dan naar het hellevuur.'),
(3857, 37, 69, 'Zij hebben hun vaderen in dwaling aangetroffen'),
(3858, 37, 70, 'en zijn hen achterna gesneld.'),
(3859, 37, 71, 'Voor hun tijd verkeerden de meesten van hen die er eertijds waren al in dwaling,'),
(3860, 37, 72, 'hoewel Wij in hun midden waarschuwers hadden gezonden.'),
(3861, 37, 73, 'Kijk dan hoe het einde was van de gewaarschuwden!'),
(3862, 37, 74, 'Maar [dat geldt] niet voor de toegewijde dienaren van God.'),
(3863, 37, 75, 'Noeh had tot Ons geroepen; een voortreffelijk verhoorder [zijn Wij].'),
(3864, 37, 76, 'En Wij redden hem en zijn familie uit de geweldige benardheid'),
(3865, 37, 77, 'en maakten dat het zijn nakomelingen waren die overbleven.'),
(3866, 37, 78, 'En Wij lieten voor hem een goede naam bij het nageslacht na.'),
(3867, 37, 79, 'Vrede zij met Noeh onder de wereldbewoners!'),
(3868, 37, 80, 'Zo belonen Wij hen die goed doen.'),
(3869, 37, 81, 'Hij behoort tot Onze gelovige dienaren.'),
(3870, 37, 82, 'Toen lieten Wij de anderen verdrinken. *'),
(3871, 37, 83, 'Tot zijn groepering behoorde ook Ibrahiem.'),
(3872, 37, 84, 'Toen hij tot zijn Heer kwam met een zuiver hart.'),
(3873, 37, 85, 'Toen hij tot zijn vader en zijn volk kwam en zei: "Wat dienen jullie toch?'),
(3874, 37, 86, 'Wensen jullie iets lasterlijks: andere goden in plaats van God?'),
(3875, 37, 87, 'En wat is jullie mening over de Heer van de wereldbewoners?"'),
(3876, 37, 88, 'Toen keek hij opmerkzaam naar de sterren'),
(3877, 37, 89, 'en zei: "Ik ben ziek."'),
(3878, 37, 90, 'Dus keerden zij hem de rug toe.'),
(3879, 37, 91, 'Toen wendde hij zich tersluiks tot hun goden en zei: "Eten jullie dan niet?'),
(3880, 37, 92, 'Waarom spreken jullie niet?"'),
(3881, 37, 93, 'En hij begon ze met zijn rechterhand kapot te slaan.'),
(3882, 37, 94, 'Toen kwamen zij wel op hem toe rennen.'),
(3883, 37, 95, 'Hij zei: "Dienen jullie wat jullie zelf uitgehouwen hebben?'),
(3884, 37, 96, 'Maar God heeft jullie en wat jullie maken geschapen."'),
(3885, 37, 97, 'Zij zeiden: "Trekt voor hem een gebouw op en werpt hem dan in het helse vuur."'),
(3886, 37, 98, 'Zij wilden tegen hem graag een list beramen, maar Wij maakten hen tot de onderliggenden.'),
(3887, 37, 99, 'En hij zei: "Ik ga tot mijn Heer, Hij zal mij op het goede pad brengen.'),
(3888, 37, 100, 'Mijn Heer, schenk mij iemand die rechtschapen is."'),
(3889, 37, 101, 'Daarop verkondigden Wij hem het goede nieuws van een zachtmoedige jongen.'),
(3890, 37, 102, 'Toen die zover was dat hij met hem mee kon gaan zei hij: "Mijn zoon, ik heb in de slaap gezien dat ik je zal offeren. Zie eens wat jij ervan vindt." Hij zei: "Mijn vader, doe wat je bevolen is. Je zult merken dat ik, als God het wil, iemand ben die geduldig volhardt."'),
(3891, 37, 103, 'Toen zij zich beiden [aan Gods wil] overgegeven hadden en hij hem op zijn voorhoofd had neergelegd,'),
(3892, 37, 104, 'riepen Wij hem: "Ibrahiem!'),
(3893, 37, 105, 'Jij hebt de droom doen uitkomen. Zo belonen Wij hen die goed doen.'),
(3894, 37, 106, 'Dit was duidelijk een beproeving."'),
(3895, 37, 107, 'En Wij gaven voor hem een geweldig offer in de plaats.'),
(3896, 37, 108, 'En Wij lieten voor hem een goede naam bij het nageslacht na.'),
(3897, 37, 109, 'Vrede zij met Ibrahiem!'),
(3898, 37, 110, 'Zo belonen Wij hen die goed doen.'),
(3899, 37, 111, 'Hij behoort tot Onze gelovige dienaren.'),
(3900, 37, 112, 'En Wij verkondigden hem het goede nieuws van Ishaak die een profeet uit het midden van de rechtschapenen zou zijn.'),
(3901, 37, 113, 'Wij zegenden hem en Ishaak. En onder hun nageslacht zijn er die goed doen en die zich duidelijk onrecht aandoen.'),
(3902, 37, 114, 'Wij hebben aan Moesa en Haroen een gunst bewezen.'),
(3903, 37, 115, 'En Wij redden hen beiden en hun volk uit de geweldige benardheid.'),
(3904, 37, 116, 'Wij hielpen hen en dus waren zij de overwinnaars.'),
(3905, 37, 117, 'En Wij gaven hun beiden het overduidelijke boek'),
(3906, 37, 118, 'en leidden hen op de juiste weg.'),
(3907, 37, 119, 'En Wij lieten voor hen een goede naam bij het nageslacht na.'),
(3908, 37, 120, 'Vrede zij met Moesa en Haroen!'),
(3909, 37, 121, 'Zo belonen Wij hen die goed doen.'),
(3910, 37, 122, 'Zij behoren tot Onze gelovige dienaren.'),
(3911, 37, 123, 'Ook Iljaas behoorde tot de gezondenen.'),
(3912, 37, 124, 'Toen hij tot zijn volk zei: "Zullen jullie niet godvrezend worden?'),
(3913, 37, 125, 'Roepen jullie Baäl aan en verlaten jullie de beste van de scheppers,'),
(3914, 37, 126, 'God, jullie Heer en de Heer van jullie vaderen die er eertijds waren?"'),
(3915, 37, 127, 'Maar zij betichtten hem van leugens en dus worden zij zeker voorgeleid.'),
(3916, 37, 128, 'Maar [dat geldt] niet voor de toegewijde dienaren van God.'),
(3917, 37, 129, 'En Wij lieten voor hem een goede naam bij het nageslacht na.'),
(3918, 37, 130, 'Vrede zij met Il-jasien!'),
(3919, 37, 131, 'Zo belonen Wij hen die goed doen.'),
(3920, 37, 132, 'Hij behoort tot Onze gelovige dienaren.'),
(3921, 37, 133, 'Ook Loet behoorde tot de gezondenen.'),
(3922, 37, 134, 'Toen Wij hem en zijn familie allen tezamen redden,'),
(3923, 37, 135, 'behalve een oude vrouw [die] bij de achterblijvers [bleef].'),
(3924, 37, 136, 'Toen vernietigden Wij de anderen.'),
(3925, 37, 137, 'Jullie komen er immers nog voorbij, \'s morgens'),
(3926, 37, 138, 'en \'s nachts. Hebben jullie dan geen verstand?'),
(3927, 37, 139, 'Ook Joenoes behoorde tot de gezondenen.'),
(3928, 37, 140, 'Toen hij naar het volbeladen schip wegliep'),
(3929, 37, 141, 'en het lot wierp; maar hij was een van de verliezers.'),
(3930, 37, 142, 'Toen slokte de vis hem op, laakbaar als hij was.'),
(3931, 37, 143, 'En als hij niet tot hen die lofprijzen behoord had,'),
(3932, 37, 144, 'dan was hij in zijn buik gebleven tot de dag waarop men wordt opgewekt. *'),
(3933, 37, 145, 'Wij wierpen hem toen, ziek als hij was, op een onbegroeide plaats.'),
(3934, 37, 146, 'En Wij lieten boven hem een pompoenplant groeien.'),
(3935, 37, 147, 'En Wij zonden hem naar honderdduizend [mensen] -- of het waren er nog meer --'),
(3936, 37, 148, 'die toen geloofden. Hen lieten Wij toen nog een tijd genieten.'),
(3937, 37, 149, 'Vraag hun dan om uitsluitsel of jouw Heer de dochters heeft en zij de zonen.'),
(3938, 37, 150, 'Of hebben Wij de engelen als vrouwelijke wezens geschapen terwijl zij er getuige van waren?'),
(3939, 37, 151, 'Met hun lasterlijke leugen zeggen zij immers:'),
(3940, 37, 152, '"God heeft kinderen verwekt." Leugenaars zijn zij!'),
(3941, 37, 153, 'Heeft Hij de dochters boven de zonen verkozen?'),
(3942, 37, 154, 'Wat is er met jullie? Hoe kunnen jullie oordelen?'),
(3943, 37, 155, 'Laten jullie je dan niet vermanen?'),
(3944, 37, 156, 'Of hebben jullie een duidelijke machtiging?'),
(3945, 37, 157, 'Brengt dan jullie boek als jullie gelijk hebben.'),
(3946, 37, 158, 'En zij hebben verwantschap tussen Hem en de djinn gefabriceerd, maar de djinn weten dat zij zullen worden voorgeleid --'),
(3947, 37, 159, 'God zij geprezen, verheven als Hij is boven wat zij toeschrijven --'),
(3948, 37, 160, 'alleen niet de toegewijde dienaren van God.'),
(3949, 37, 161, '"En jullie dan, en wat jullie dienen?'),
(3950, 37, 162, 'Jullie kunnen niemand tegen Hem ophitsen,'),
(3951, 37, 163, 'behalve dan wie er braadt in het hellevuur.'),
(3952, 37, 164, 'Er is niemand van ons of hij heeft een vastgestelde positie.'),
(3953, 37, 165, 'Maar wij, wij zijn het die zich in rijen opstellen'),
(3954, 37, 166, 'en wij, wij zijn het die lofprijzen."'),
(3955, 37, 167, 'Zij zeiden steeds:'),
(3956, 37, 168, '"Als wij maar een vermaning hadden als van hen die er eertijds waren,'),
(3957, 37, 169, 'dan zouden wij Gods toegewijde dienaren zijn."'),
(3958, 37, 170, 'Toch hechtten zij er geen geloof aan, maar zij zullen het weten.'),
(3959, 37, 171, 'Ons woord was al eerder tot Onze dienaren, de gezondenen, gekomen.'),
(3960, 37, 172, 'Zij waren het aan wie hulp werd verleend.'),
(3961, 37, 173, 'En Onze troepenmacht, zij zijn de overwinnaars.'),
(3962, 37, 174, 'Keer je dus tijdelijk van hen af.'),
(3963, 37, 175, 'En kijk naar hen, zij zullen naar jou kijken.'),
(3964, 37, 176, 'Willen zij dan Onze bestraffing verhaasten?'),
(3965, 37, 177, 'En wanneer die in hun gebied neerkomt zal dat een slechte morgen voor de gewaarschuwden zijn.'),
(3966, 37, 178, 'Keer je tijdelijk van hen af.'),
(3967, 37, 179, 'En kijk, zij zullen naar jou kijken.'),
(3968, 37, 180, 'Geprezen zij jouw Heer, de Heer van de macht, verheven als Hij is boven wat zij toeschrijven.'),
(3969, 37, 181, 'En vrede zij met de gezondenen.'),
(3970, 37, 182, 'En lof zij God, de Heer van de wereldbewoners.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'S[aad]. Bij de Koran met de vermaning!'),
(3972, 38, 2, 'Jazeker, zij die ongelovig zijn zijn hoogmoedig en zij zijn het oneens.'),
(3973, 38, 3, 'Hoeveel generaties hebben Wij reeds vóór hun tijd vernietigd. Zij riepen dan, maar er was geen tijd meer om te ontkomen.'),
(3974, 38, 4, 'Zij verbazen zich dat er tot hen een waarschuwer uit hun midden gekomen is. De ongelovigen zeggen: "Dit is een leugenachtige tovenaar.'),
(3975, 38, 5, 'Wil hij de goden dan tot één god maken? Dit is wel iets wonderlijks."'),
(3976, 38, 6, 'En de voornaamsten onder hen gaan verder: "Gaat voort en houdt vast aan jullie goden; dat is iets wat gewenst moet worden.'),
(3977, 38, 7, 'Wij hebben dit in het laatste geloof niet eens gehoord; dit is slechts een uitvindsel.'),
(3978, 38, 8, 'Is uit ons midden tot hem de vermaning neergezonden?" Welnee, zij verkeren in twijfel over Mijn vermaning. Nee, zij hebben Mijn bestraffing nog niet geproefd.'),
(3979, 38, 9, 'Of zijn bij hen de schatkamers van de barmhartigheid van jouw Heer, de machtige, de vrijgevige?'),
(3980, 38, 10, 'Of hebben zij de heerschappij over de hemelen en de aarde en wat tussen beide is? Laten zij dan maar op de ladders omhoogklimmen.'),
(3981, 38, 11, 'Een troepenmacht van de partijen is het die daar verslagen is.'),
(3982, 38, 12, 'Voor hun tijd had het volk van Noeh van leugens beticht en ook de \'Aad en Fir\'aun van de tentpinnen,'),
(3983, 38, 13, 'en de Thamoed en het volk van Loet en de mensen van het kreupelbos; dat waren de partijen.'),
(3984, 38, 14, 'Zij allen hebben niet anders gedaan dan de gezanten van leugens betichten en dus is de afstraffing bewaarheid.'),
(3985, 38, 15, 'Dezen hebben niets anders te verwachten dan één schreeuw die geen onderbreking heeft.'),
(3986, 38, 16, 'En zij zeggen: "Onze Heer, geef ons snel ons aandeel, nog voor de dag van de afrekening."'),
(3987, 38, 17, 'Verdraag wat zij zeggen geduldig en denk aan Onze dienaar Dawoed, de solide; hij was schuldbewust.'),
(3988, 38, 18, 'Wij maakten de bergen samen met hem dienstbaar, zodat zij Ons in de avond en bij zonsopgang prijzen,'),
(3989, 38, 19, 'en ook de verzamelde vogels; alles wendde zich schuldbewust tot Hem.'),
(3990, 38, 20, 'En Wij versterkten zijn heerschappij en gaven hem wijsheid en onderscheidingsvermogen.'),
(3991, 38, 21, 'Is het bericht over het getwist tot jou gekomen toen zij over de muren van het paleisgebouw klommen?'),
(3992, 38, 22, 'Toen zij bij Dawoed binnenkwamen en hij van hen schrok, maar zij zeiden: "Wees niet bang. Wij zijn twee tegenstanders, een van ons heeft de ander onrechtvaardig behandeld. Oordeel dus naar waarheid tussen ons en wijk er niet van af en leid ons naar de correcte uitweg.'),
(3993, 38, 23, 'Dit is mijn broer, hij heeft negenennegentig schapen en ik heb maar één schaap en hij zei: \'Laat mij ervoor zorgen.? En hij heeft mij overreed."'),
(3994, 38, 24, 'En hij zei: "Hij heeft jou onrecht aangedaan door jouw schaap nog bij zijn schapen te vragen. Velen van hen die gemeenschappelijk eigendom hebben behandelen elkaar onrechtmatig. Alleen niet zij die geloven en de deugdelijke daden doen, maar met hoe weinigen zijn zij!" En Dawoed vermoedde wel dat Wij hem in verzoeking gebracht hadden en hij vroeg zijn Heer om vergeving, viel buigend neer en betoonde zich schuldbewust. --'),
(3995, 38, 25, 'En Wij vergaven hem dat; hij staat Ons na en heeft een goede terugkomst.'),
(3996, 38, 26, 'O Dawoed, Wij hebben jou tot opvolger op de aarde gemaakt. Oordeel dus naar waarheid tussen de mensen en volg [je eigen] neiging niet, want die zal je van Gods weg doen afdwalen; voor hen die van Gods weg afdwalen is er een strenge bestraffing omdat zij de dag van de afrekening hebben vergeten.'),
(3997, 38, 27, 'En Wij hebben de hemel en de aarde en wat er tussen beide is niet voor niets geschapen. Dat denken zij die ongelovig zijn. Wee hen die ongelovig zijn, wegens het vuur.'),
(3998, 38, 28, 'Of zullen Wij hen die geloven en de deugdelijke daden doen soms zo behandelen als hen die op de aarde verderf zaaien? Of behandelen Wij de godvrezenden als de overtreders?'),
(3999, 38, 29, '[De Koran] is een gezegend boek dat Wij naar jou hebben neergezonden, opdat zij de verzen ervan overdenken en opdat de verstandigen zich laten vermanen.'),
(4000, 38, 30, 'En Wij hebben aan Dawoed Soelaimaan geschonken, een voortreffelijk dienaar; hij was schuldbewust.'),
(4001, 38, 31, 'Toen hem \'s avonds de lichtvoetige renpaarden werden voorgeleid,'),
(4002, 38, 32, 'toen zei hij: "Ik heb bezit meer lief dan het gedenken van God" en toen zij uit het gezicht verdwenen:'),
(4003, 38, 33, '"Brengt ze naar mij terug." Toen begon hij ze over de benen en de halzen te strijken.'),
(4004, 38, 34, 'Wij hadden Soelaimaan werkelijk aan verzoeking blootgesteld. En Wij zetten op zijn troon [iets wat alleen maar] een lichaam [was]. Toen betoonde hij zich schuldbewust.'),
(4005, 38, 35, 'Hij zei: "Mijn Heer, vergeef mij en schenk mij een heerschappij die na mij voor niemand meer passend is; U bent de vrijgevige."'),
(4006, 38, 36, 'En Wij maakten de wind aan hem dienstbaar, zodat die zich zachtjes op zijn bevel spoedde naar waar hij hem heen stuurde.'),
(4007, 38, 37, 'En ook de satans, elke bouwer en duiker,'),
(4008, 38, 38, 'en anderen in boeien aaneengeketend.'),
(4009, 38, 39, '"Dit is Onze gave, geef overvloedig of terughoudend, zonder af te rekenen.'),
(4010, 38, 40, 'Hij staat Ons na en heeft een goede terugkomst."'),
(4011, 38, 41, 'En denk aan Onze dienaar Ajjoeb, toen hij tot zijn Heer riep: "De satan heeft mij met tegenspoed en kwelling getroffen."'),
(4012, 38, 42, '"Stamp met je voet, dit is water voor een koel bad en een dronk."'),
(4013, 38, 43, 'En Wij gaven hem zijn familie terug en nog eens zoveel met hen, uit barmhartigheid van Onze kant en als vermaning voor de verstandigen.'),
(4014, 38, 44, 'En "neem een bundel [twijgen] in je hand en sla ermee en breek je eed niet." Wij merkten dat hij geduldig had volhard, een voortreffelijk dienaar; hij was schuldbewust.'),
(4015, 38, 45, 'En denk aan Onze dienaren Ibrahiem, Ishaak en Ja\'koeb die kracht en inzicht hadden.'),
(4016, 38, 46, 'Wij hebben hen met iets bijzonders gekenmerkt: het denken aan de [laatste] woning.'),
(4017, 38, 47, 'Bij Ons behoren zij bij de uitverkorenen, de goeden.'),
(4018, 38, 48, 'En denk aan Isma\'iel, al-Jasa? en Dzoe-l-Kifl. Ieder [van hen] behoorde tot de goeden.'),
(4019, 38, 49, 'Dit is een vermaning. Voor de godvrezenden zal er een goede terugkomst zijn:'),
(4020, 38, 50, 'De tuinen van \'Adn, waarvan de poorten voor hen open staan.'),
(4021, 38, 51, 'Daarin leunen zij achterover terwijl zij er om veel vruchten en drank kunnen vragen. *'),
(4022, 38, 52, 'En bij hen zijn even oude gezellinnen met afgewende blikken.'),
(4023, 38, 53, 'Dit is wat jullie voor de dag van de afrekening wordt toegezegd.'),
(4024, 38, 54, 'Dit is Onze voorziening; zij is onvergankelijk.'),
(4025, 38, 55, 'Zo is het. Maar voor de onbeschaamden zal er een slechte terugkomst zijn:'),
(4026, 38, 56, 'De hel waarin zij zullen braden. Dat is pas een slechte rustplaats!'),
(4027, 38, 57, 'Zo is het. Laten zij het maar proeven: gloeiend water en etter,'),
(4028, 38, 58, 'en andere, gelijksoortige dingen.'),
(4029, 38, 59, 'Dit is een groep die zich samen met jullie naar binnen stort. "Zij zijn niet welkom! Zij zullen braden in het vuur."'),
(4030, 38, 60, 'Zij zeggen: "Nee hoor, jullie zijn niet welkom. Jullie hebben ons dit geleverd. Dat is pas een slechte standplaats!"'),
(4031, 38, 61, 'Zij zeggen: "Onze Heer, wie ons dit heeft geleverd, geef hem een dubbele bestraffing in het vuur."'),
(4032, 38, 62, 'En zij zeggen: "Waarom zien wij bepaalde mannen niet die wij tot de slechten rekenden?'),
(4033, 38, 63, 'Hebben wij hen [onterecht] belachelijk gemaakt of kijken onze ogen langs hen heen?"'),
(4034, 38, 64, 'Dat is waar. [Zo is] het getwist van de bewoners van het vuur.'),
(4035, 38, 65, 'Zeg: "Ik ben slechts een waarschuwer en er is geen andere god dan God, de ene, de albeheerser,'),
(4036, 38, 66, 'de Heer van de hemelen en de aarde en wat tussen beide is, de machtige, de vergever."'),
(4037, 38, 67, 'Zeg: "Het is een geweldige mededeling,'),
(4038, 38, 68, 'waarvan jullie je afwenden.'),
(4039, 38, 69, 'Ik had geen kennis van de allerhoogste raad van voornaamsten, toen zij met elkaar twistten.'),
(4040, 38, 70, 'Aan mij is slechts geopenbaard dat ik een duidelijke waarschuwer ben."'),
(4041, 38, 71, 'Toen jouw Heer tot de engelen zei: "Ik ga een mens uit klei scheppen.'),
(4042, 38, 72, 'En als Ik hem gevormd heb en hem iets van Mijn geest heb ingeblazen, valt dan in eerbiedige buiging voor hem neer."'),
(4043, 38, 73, 'En de engelen bogen zich allen tezamen eerbiedig voor hem neer.'),
(4044, 38, 74, 'Alleen Iblies niet, hij was hoogmoedig en behoorde tot de ongelovigen.'),
(4045, 38, 75, 'Hij zei: "O Iblies, wat verhindert jou je eerbiedig neer te buigen voor wat Ik eigenhandig geschapen heb? Ben jij hoogmoedig of behoor jij tot hen die de overhand hebben?"'),
(4046, 38, 76, 'Hij zei: "Ik ben beter dan hij, U hebt mij uit vuur geschapen en hem hebt U uit klei geschapen."'),
(4047, 38, 77, 'Hij zei: "Ga hier weg, jij zult door steniging vervloekt zijn!'),
(4048, 38, 78, 'En Mijn vloek zal tot de oordeelsdag op je rusten."'),
(4049, 38, 79, 'Hij zei: "Mijn Heer, verleen mij uitstel tot de dag waarop zij worden opgewekte'),
(4050, 38, 80, 'Hij zei: "Jij behoort bij hen die uitstel hebben gekregen'),
(4051, 38, 81, 'tot de dag van de vastgestelde tijd."'),
(4052, 38, 82, 'Hij zei: "Bij Uw macht, ik zal hen zeker allen misleiden,'),
(4053, 38, 83, 'behalve Uw toegewijde dienaren onder hen."'),
(4054, 38, 84, 'Hij zei: "De waarheid is -- en Ik spreek de waarheid --:'),
(4055, 38, 85, 'Ik zal de hel met jou en met hen die jou volgen vullen, allen tezamen."'),
(4056, 38, 86, 'Zeg: "Ik vraag jullie er geen loon voor en ik behoor niet tot hen die zichzelf [met zoiets] belasten.'),
(4057, 38, 87, 'Het is slechts een vermaning voor de wereldbewoners.'),
(4058, 38, 88, 'En jullie zullen over een tijd de mededeling daarover kennen."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'De neerzending van het boek is gebeurd door God, de machtige, de wijze.'),
(4060, 39, 2, 'Wij hebben het boek met de waarheid naar jou neergezonden. Dien God dus en wijd de godsdienst geheel aan Hem.'),
(4061, 39, 3, 'Behoort de zuivere godsdienst niet God toe? Zij die zich in plaats van Hem beschermers nemen [zeggen]: "Wij dienen hen slechts opdat zij ons nader tot God brengen." God zal tussen hen oordelen over dat waarover zij het oneens zijn. God wijst wie een ongelovige leugenaar is de goede richting niet.'),
(4062, 39, 4, 'Als God gewenst had zich een kind te nemen, dan had Hij uit wat Hij geschapen heeft uitgekozen wat Hij had gewild. Geprezen zij Hij! Hij is God, de ene, de albeheerser.'),
(4063, 39, 5, 'Hij heeft de hemelen en de aarde in waarheid geschapen. Hij legt de nacht over de dag en Hij legt de dag over de nacht en Hij heeft de zon en de maan dienstbaar gemaakt; alles loopt tot een vastgestelde termijn. Is Hij niet de machtige, de vergevende?'),
(4064, 39, 6, 'Hij heeft jullie uit één wezen geschapen en toen heeft Hij er zijn echtgenote uit gemaakt. En Hij heeft van het vee acht paarsgewijs voorkomende dieren naar jullie neergezonden. Hij schept jullie in de buiken van jullie moeders, de ene schepping na de andere in drievoudige duisternis. Dat is God, jullie Heer. Hij heeft de heerschappij. Er is geen god dan Hij. Hoe kunnen jullie je dan zo laten verstrooien?'),
(4065, 39, 7, 'Als jullie ongelovig zijn, God heeft jullie niet nodig. Hem bevalt het ongeloof bij zijn dienaren niet, maar als jullie dank betuigen dan bevalt Hem dat bij jullie. Niemand is belast met de last van een ander. En vervolgens is jullie terugkeer tot jullie Heer. Hij zal jullie dan meedelen wat jullie aan het doen waren. Hij weet wat er binnen in de harten is.'),
(4066, 39, 8, 'En wanneer de mens tegenspoed treft, roept hij zijn Heer aan, terwijl hij zich schuldbewust tot Hem wendt, maar daarna, wanneer Hij hem van Zijn kant genade schenkt, vergeet hij wat hij daarvoor tot Hem geroepen had. En hij schrijft aan God gelijken toe om [anderen] van Zijn weg af te laten dwalen. Zeg: "Geniet in jouw ongeloof nog maar een weinig; jij behoort tot hen die in het vuur thuishoren."'),
(4067, 39, 9, 'Is iemand die gedurende de nacht onderdanigheid toont, terwijl hij zich eerbiedig neerbuigt en opstaat, op zijn hoede is voor het hiernamaals en op de barmhartigheid van Zijn Heer hoopt?? Zeg: "Zijn zij die weten en zij die niet weten gelijk? Slechts de verstandigen laten zich vermanen."'),
(4068, 39, 10, 'Zeg: "Mijn dienaren, die gelovig zijn! Vreest jullie Heer. Voor hen die in dit tegenwoordige leven goed doen is er iets goeds en Gods aarde is wijd. Maar aan hen die geduldig volharden zal hun volle loon gegeven worden, zonder afrekening."'),
(4069, 39, 11, 'Zeg: "Mij is bevolen God te dienen door de godsdienst geheel aan Hem te wijden.'),
(4070, 39, 12, 'En mij is bevolen de eerste te zijn van hen die zich [aan God] overgeven."'),
(4071, 39, 13, 'Zeg: "Ik vrees voor een geweldige dag als ik aan mijn Heer ongehoorzaam ben."'),
(4072, 39, 14, 'Zeg: "God dien ik door mijn godsdienst geheel aan Hem te wijden.'),
(4073, 39, 15, 'En dient maar wat jullie willen in plaats van Hem." Zeg: "De verliezers zijn zij die zichzelf en hun families verliezen op de opstandingsdag. Is dat niet het duidelijke verlies?"'),
(4074, 39, 16, 'Zij hebben boven zich stapelwolken van vuur en onder zich stapelwolken. Daarmee jaagt God Zijn dienaren vrees aan. Mijn dienaren, weest godvrezend!'),
(4075, 39, 17, 'Voor hen die het vermijden de Taghoet te dienen en die zich schuldbewust tot God wenden is er goed nieuws. Verkondig het goede nieuws dus aan Mijn dienaren,'),
(4076, 39, 18, 'die het woord horen en dan het beste ervan volgen. Zij zijn het die God op het goede pad brengt en dat zijn zij, de verstandigen.'),
(4077, 39, 19, 'En als voor iemand het woord van de bestraffing is bewaarheid? kun jij dan iemand redden die in het vuur is?'),
(4078, 39, 20, 'Maar voor hen die hun Heer vrezen zijn er [feest]zalen waarboven [feest] zalen gebouwd zijn en waar de rivieren onderdoor stromen. Gods toezegging is het. God zal de afspraak niet afzeggen.'),
(4079, 39, 21, 'Zie jij dan niet dat God water uit de hemel laat neerdalen en dan naar bronnen in de aarde laat gaan? Dan brengt Hij er landbouwgewassen van allerlei soorten mee voort. Dan verdorren zij en jij ziet ze geel worden en dan maakt Hij ze tot gruis. Daarin is een vermaning voor de verstandigen.'),
(4080, 39, 22, 'Is dan iemand wiens hart God opengesteld heeft voor de overgave [aan God] en die zich in een licht dat van zijn Heer afkomstig is bevindt?? Wee dus hen van wie de harten hard zijn zodat zij God niet gedenken. Zij zijn het die in duidelijke dwaling verkeren.'),
(4081, 39, 23, 'God heeft het beste nieuws neergezonden, een boek met op elkaar lijkende herhaalde gedeelten waarvan bij hen die hun Heer vrezen de rillingen over hun huid lopen. Daarna ontspannen hun huid en hun harten zich om God te gedenken. Dat is Gods leidraad waarmee Hij leidt wie Hij wil. En als God iemand tot dwaling brengt, dan is er voor hem niemand die de goede richting wijst.'),
(4082, 39, 24, 'Is dan iemand die zich op de opstandingsdag met zijn gezicht voor de vreselijke bestraffing beschermt?? Tot de onrechtplegers wordt namelijk gezegd: "Proeft wat jullie begaan hebben."'),
(4083, 39, 25, 'Zij die er voor hun tijd waren hebben ook van leugens beticht, maar toen kwam de bestraffing tot hen vanwaar zij het niet beseften.'),
(4084, 39, 26, 'En God liet hen in het tegenwoordige leven de schande proeven, maar de bestraffing in het hiernamaals is nog erger. Als zij het maar wisten.'),
(4085, 39, 27, 'En Wij hebben in deze Koran allerlei voorbeelden voor de mensen gegeven; misschien laten zij zich vermanen.'),
(4086, 39, 28, 'Een Arabische Koran die geen afwijking heeft; misschien zullen zij godvrezend worden.'),
(4087, 39, 29, 'God geeft als voorbeeld een man die meerdere ruziënde bazen heeft en een man die volledig aan één man toebehoort. Zijn die twee bijvoorbeeld gelijk? Lof zij God! Maar de meesten van hen weten het niet.'),
(4088, 39, 30, 'Jij bent sterfelijk en zij zijn sterfelijk.'),
(4089, 39, 31, 'Dan zullen jullie op de opstandingsdag met elkaar twisten. *'),
(4090, 39, 32, 'En wie is er zondiger dan wie over God liegt en de betrouwbare waarheid loochent wanneer zij tot hem komt? Is er in de hel niet een verblijfplaats voor de ongelovigen?'),
(4091, 39, 33, 'En wie met de betrouwbare waarheid komen en geloof aan haar hechten, zij zijn het die godvrezend zijn.'),
(4092, 39, 34, 'Zij hebben bij hun Heer wat zij willen. Dat is het loon voor hen die goed doen,'),
(4093, 39, 35, 'opdat God hun het slechtste dat zij gedaan hebben kwijtscheldt en hen beloont met het loon voor het beste dat zij deden.'),
(4094, 39, 36, 'Is God niet goed genoeg voor Zijn dienaar? En zij willen jou bang maken met hen die er in plaats van Hem zouden zijn. En als God iemand tot dwaling brengt, dan is er voor hem niemand die de goede richting wijst.'),
(4095, 39, 37, 'En als God iemand de goede richting wijst dan is er voor hem niemand die tot dwaling brengt. Is God niet machtig en wraakgierig?'),
(4096, 39, 38, 'En als jij hun vraagt wie de hemelen en de aarde geschapen heeft zeggen zij: "God." Zeg: "En hebben jullie wel gezien wat jullie in plaats van God aanroepen? Als God voor mij tegenspoed wenst, kunnen zij dan Zijn tegenspoed voor mij wegnemen? Of als Hij voor mij barmhartigheid wenst, kunnen zij dan Zijn barmhartigheid tegenhouden?" Zeg: "Voor mij is God goed genoeg, op Hem moeten zij die vertrouwen hebben hun vertrouwen stellen."'),
(4097, 39, 39, 'Zeg: "Mijn volk! Handelt naar jullie vermogen; ik doe dat ook. Dan zullen jullie weten'),
(4098, 39, 40, 'tot wie er een bestraffing komt die hem te schande maakt en op wie een blijvende bestraffing zal neerkomen."'),
(4099, 39, 41, 'Wij hebben tot jou het boek voor de mensen met de waarheid neergezonden. Wie zich de goede richting laat wijzen volgt het goede pad slechts tot zijn eigen voordeel en wie dwaalt, dwaalt slechts ten koste van zichzelf. En jij bent geen voogd over hen.'),
(4100, 39, 42, 'God neemt de zielen weg op de tijd van hun dood en in hun slaap, als ze nog niet gestorven zijn. Dan houdt Hij die waarvoor de dood beslist is tegen en zendt de andere voor een bepaalde termijn weer weg. Daarin zijn tekenen voor mensen die nadenken.'),
(4101, 39, 43, 'Of hebben zij buiten God om bemiddelaars genomen? Zeg: "En als zij niets kunnen uitrichten en geen verstand hebben?"'),
(4102, 39, 44, 'Zeg: "Aan God komt de bemiddeling geheel toe. Hij heeft de heerschappij over de hemelen en de aarde. Voorts zullen jullie tot Hem worden teruggebracht."'),
(4103, 39, 45, 'En wanneer God als enige vermeld wordt krimpen de harten van hen die niet in het hiernamaals geloven ineen. En wanneer zij die er in plaats van Hem zouden zijn vermeld worden dan verblijden zij zich opeens.'),
(4104, 39, 46, 'Zeg: "O God, schepper van de hemelen en de aarde, kenner van het verborgene en het waarneembare, U oordeelt tussen Uw dienaren over dat waarover zij het oneens waren."'),
(4105, 39, 47, 'Als zij die onrecht plegen alles wat er op de aarde is hadden en nog eens evenveel, dan zouden zij zich ermee willen vrijkopen van de erge bestraffing op de opstandingsdag. Van de kant van God zal voor hen dan openlijk zichtbaar worden waarop zij niet gerekend hadden.'),
(4106, 39, 48, 'De slechte daden die zij begaan hebben worden dan namelijk voor hen zichtbaar en zij worden ingesloten door dat waarmee zij de spot dreven.'),
(4107, 39, 49, 'En wanneer de mens tegenspoed treft dan roept hij Ons aan, maar wanneer Wij hem met genade van Onze kant omgeven hebben zegt hij: "Het is mij gegeven op grond van kennis die ik heb." Welnee, het is een verzoeking. Maar de meesten van hen weten het niet.'),
(4108, 39, 50, 'Zij die er voor hen waren hadden dat ook al gezegd, maar hun baatte niet wat zij ten uitvoer hadden gebracht.'),
(4109, 39, 51, 'De slechte daden die zij begingen hebben hen dus getroffen. En ook zij die onrecht plegen uit het midden van dezen hier zullen door de slechte daden die zij hebben begaan getroffen worden zonder dat zij er iets aan kunnen doen.'),
(4110, 39, 52, 'Weten zij dan niet dat God ruimschoots in het levensonderhoud voorziet van wie Hij wil en ook met mate? Daarin zijn tekenen voor mensen die geloven.'),
(4111, 39, 53, 'Zeg: "Mijn dienaren! Als jullie tegen jezelf onmatig zijn geweest moeten jullie de hoop op de barmhartigheid van God niet opgeven. God vergeeft al de zonden; Hij is de vergevende, de barmhartige.'),
(4112, 39, 54, 'En wendt jullie schuldbewust tot jullie Heer en geeft jullie over aan Hem voordat de bestraffing tot jullie komt. Dan zullen jullie geen hulp krijgen.'),
(4113, 39, 55, 'En volgt het beste van wat tot jullie van de kant van jullie Heer is neergezonden voordat de bestraffing onverwachts tot jullie komt zonder dat jullie het beseffen."'),
(4114, 39, 56, 'Opdat niemand zal zeggen: "Wat heb ik er spijt van dat ik God veronachtzaamd heb! Ik behoorde immers tot de spotters."'),
(4115, 39, 57, 'Of dat iemand zal zeggen: "Als God mij op het goede pad gebracht had behoorde ik tot de godvrezenden."'),
(4116, 39, 58, 'Of dat iemand zal zeggen wanneer hij de bestraffing ziet: "Als er een terugkeer voor mij was zou ik tot hen die goed doen behoren."'),
(4117, 39, 59, '"Welnee, Mijn tekenen zijn toch tot jou gekomen, maar jij hebt ze geloochend, jij was hoogmoedig en behoorde tot de ongelovigen."'),
(4118, 39, 60, 'Op de opstandingsdag zul je hen die over God logen met zwarte gezichten zien. Is er in de hel niet een verblijfplaats voor de hoogmoedigen?'),
(4119, 39, 61, 'Maar God redt hen die godvrezend zijn om wat zij tot stand hebben gebracht. Hen treft geen kwaad noch zullen zij bedroefd zijn.'),
(4120, 39, 62, 'God is de schepper van alle dingen en Hij is over alles voogd.'),
(4121, 39, 63, 'Hij heeft de sleutels van de hemelen en de aarde. Zij die Gods tekenen loochenen, zij zijn het die de verliezers zijn.'),
(4122, 39, 64, 'Zeg: "Willen jullie mij dan bevelen iets anders dan God te dienen, jullie onwetenden?"'),
(4123, 39, 65, 'Aan jou en aan hen die er voor jouw tijd waren is geopenbaard: "Als jij veelgodendienst bedrijft dan zal wat jij doet vruchteloos zijn en zul jij tot de verliezers behoren.'),
(4124, 39, 66, 'Neen, jij moet God dienen en behoren tot hen die dank betuigen!"'),
(4125, 39, 67, 'Zij hebben God niet op Zijn juiste waarde geschat. De gehele aarde zal namelijk op de opstandingsdag in Zijn greep zijn en de hemelen zullen zijn samengevouwen in Zijn rechterhand. Hij zij geprezen, verheven als Hij is boven wat zij aan Hem als metgezellen toevoegen.'),
(4126, 39, 68, 'En er wordt op de bazuin geblazen en wie er in de hemelen en wie er op de aarde zijn vallen wezenloos neer, behalve zij van wie God het wil. Dan wordt er een tweede keer op geblazen en dan staan zij op om te kijken.'),
(4127, 39, 69, 'En de aarde straalt in het licht van haar Heer, het boek wordt voorgelegd en de profeten en getuigen worden gebracht. En er wordt tussen hen naar waarheid beslist en hun wordt geen onrecht aangedaan.'),
(4128, 39, 70, 'En aan iedereen wordt vergoed wat hij gedaan heeft; Hij kent wat zij doen het best.'),
(4129, 39, 71, 'En zij die ongelovig zijn worden in horden naar de hel gedreven en wanneer zij er komen worden haar poorten geopend en haar bewakers zeggen tot hen: "Zijn er tot jullie geen gezanten uit jullie eigen midden gekomen, die jullie de tekenen van jullie Heer voorlazen en die jullie waarschuwden voor de ontmoeting op deze dag van jullie?" Zij zeggen: "Ja zeker." Maar het woord van de bestraffing is voor de ongelovigen bewaarheid.'),
(4130, 39, 72, 'Er wordt gezegd: "Gaat de poorten van de hel binnen om er altijd te blijven." Dat is pas een slechte verblijfplaats voor de hoogmoedigen!'),
(4131, 39, 73, 'En zij die gelovig zijn worden in horden naar de tuin gedreven en wanneer zij er komen worden haar poorten geopend en haar bewakers zeggen tot hen: "Vrede zij met jullie! Jullie zijn goed geweest, gaat er dus binnen om er altijd te blijven."'),
(4132, 39, 74, 'En zij zeggen: "Lof zij God, die Zijn belofte aan ons heeft waargemaakt en die ons de aarde heeft laten beërven zodat wij ons in de tuin kunnen vestigen waar wij willen." Dat is pas een goed loon voor hen die [goed] doen.'),
(4133, 39, 75, 'En je ziet de engelen rondom de troon staan terwijl zij de lof van hun Heer prijzen. En er wordt tussen hen naar waarheid beslist en men zegt: "Lof zij God, de Heer van de wereldbewoners."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'H[aa?] M[iem].'),
(4135, 40, 2, 'De neerzending van het boek is gebeurd door God, de machtige, de wetende,'),
(4136, 40, 3, 'Hij die de zonde vergeeft en het berouw aanvaardt, die streng is in de afstraffing en vermogend. Er is geen god dan Hij. Bij Hem is de bestemming.'),
(4137, 40, 4, 'Over Gods tekenen twisten slechts zij die ongelovig zijn; laat je niet begoochelen doordat zij in het land rondtrekken.'),
(4138, 40, 5, 'Voor hun tijd had het volk van Noeh van leugens beticht en na hen ook de partijen. En iedere gemeenschap was van plan om hun gezant te grijpen en zij redetwistten met behulp van onzin om daarmee de waarheid te weerleggen, maar Ik greep hen. En hoe was Mijn afstraffing dan?'),
(4139, 40, 6, 'Zo werd het woord van jouw Heer bewaarheid tegen hen die ongelovig zijn, dat zij in het vuur thuishoren.'),
(4140, 40, 7, 'Zij die de troon dragen en eromheen staan prijzen de lof van hun Heer en geloven in Hem. En zij vragen om vergeving voor hen die geloven: "Onze Heer, U omvat alles in barmhartigheid en kennis. Vergeef dan hun die berouw tonen en Uw weg volgen en behoed hen voor de bestraffing van het hellevuur.'),
(4141, 40, 8, 'Onze Heer, en laat hen de tuinen van \'Adn binnengaan die U hun hebt toegezegd en ook hen van hun vaderen en echtgenotes en nageslacht die rechtschapen zijn. U bent de machtige, de wijze.'),
(4142, 40, 9, 'En behoed hen voor de slechte daden, want wie U op die dag voor de slechte daden beschermt, met hem hebt U erbarmen en dat is de geweldige triomf."'),
(4143, 40, 10, 'Aan hen die ongelovig zijn wordt toegeroepen: "Gods afschuw is groter dan jullie afschuw van jezelf wanneer jullie tot het geloof worden opgeroepen en jullie toch ongelovig blijven."'),
(4144, 40, 11, 'Zij zeggen: "Onze Heer, U hebt ons tweemaal laten sterven en tweemaal tot leven gebracht. Wij erkennen dus onze zonden. Is er nog een weg om hier uit te komen?"'),
(4145, 40, 12, 'Dat is zo omdat jullie ongelovig waren wanneer tot God alleen werd opgeroepen, en gelovig wanneer aan Hem metgezellen werden toegevoegd. Maar het oordeel komt God toe, de verhevene, de grote.'),
(4146, 40, 13, 'Hij is het die jullie Zijn tekenen laat zien en die voor jullie uit de hemel levensonderhoud laat neerdalen. Maar slechts wie schuldbewust is Iaat zich vermanen.'),
(4147, 40, 14, 'Roept dus God aan waarbij jullie de godsdienst geheel aan Hem moeten wijden, ook al verafschuwen de ongelovigen het.'),
(4148, 40, 15, 'De Verhevene van rang en de Heer van de troon verleent door Zijn beschikking de geest aan wie van Zijn dienaren Hij wil om voor de dag van de ontmoeting te waarschuwen,'),
(4149, 40, 16, 'de dag waarop zij vóórkomen, waarbij niets van hen voor God verborgen blijft. "Wie heeft vandaag de heerschappij?" "God, de ene, de albeheerser."'),
(4150, 40, 17, '"Vandaag wordt aan iedereen wat hij begaan heeft vergolden, vandaag is er geen onrecht." God is snel met de afrekening.'),
(4151, 40, 18, 'En waarschuw hen voor de dag van de aanstaande [opstanding] wanneer de harten bedrukt in de kelen kloppen. De onrechtplegers hebben dan geen echte vriend en geen bemiddelaar aan wie gehoor gegeven wordt.'),
(4152, 40, 19, 'Hij kent het verraad in de ogen en wat de harten verbergen.'),
(4153, 40, 20, 'En God beslist naar waarheid, maar zij die door hen in plaats van Hem aangeroepen worden beslissen niets. God is de horende, de doorziende. *'),
(4154, 40, 21, 'Hebben zij dan niet op de aarde rondgereisd en ernaar gekeken hoe het einde was van hen die er vóór hen waren? Zij waren sterker dan zij en lieten meer sporen achter op de aarde, maar God greep hen voor hun zonden; voor hen was er tegen God geen beschermer.'),
(4155, 40, 22, 'Dat was omdat hun gezanten met de duidelijke bewijzen tot hen waren gekomen en zij toch ongelovig waren. Dus greep God hen; Hij is sterk en streng in de afstraffing.'),
(4156, 40, 23, 'Wij hadden Moesa toch met Onze tekenen en een duidelijke machtiging gezonden'),
(4157, 40, 24, 'naar Fir\'aun, Hamaan en Karoen. Maar zij zeiden: "Een leugenachtige tovenaar!"'),
(4158, 40, 25, 'En toen hij met de waarheid van Ons bij hen kwam zeiden zij: "Doodt de zonen van hen die met hem geloven en laat hun vrouwen in leven." Maar de list van de ongelovigen mist steeds haar doel.'),
(4159, 40, 26, 'En Fir\'aun zei: "Laat mij Moesa doden en hij moet dan maar zijn Heer aanroepen. Ik ben bang dat hij [anders] jullie godsdienst zal veranderen of zichtbaar verderf op de aarde zal brengen."'),
(4160, 40, 27, 'En Moesa zei: "Ik zoek bescherming bij mijn Heer en jullie Heer tegen iedere hoogmoedige die niet in de dag van de afrekening gelooft."'),
(4161, 40, 28, 'Een gelovige man uit Fir\'auns mensen die zijn geloof verborgen hield zei: "Zullen jullie een man doden omdat hij zegt: \'Mijn Heer is God?, terwijl hij toch met de duidelijke bewijzen van zijn Heer tot jullie gekomen is? Als hij een leugenaar is zal zijn bedrog in zijn eigen nadeel zijn. Maar als hij gelijk heeft, dan zal jullie zeker iets van wat hij jullie heeft aangezegd treffen. God wijst de goede richting niet aan wie schaamteloos zijn en leugenaars.'),
(4162, 40, 29, 'Mijn volk! Heden hebben jullie de heerschappij, jullie hebben de overhand in het land, maar wie zal ons helpen tegen Gods geweld als dat tot ons komt?" Fir\'aun zei: "Ik maak jullie er slechts opmerkzaam op hoe ik het zie en ik wijs jullie slechts de weg van de rechtschapenheid."'),
(4163, 40, 30, 'Maar hij die geloofde zei: "Mijn volk! Ik vrees voor jullie iets dergelijks als de dag van de partijen,'),
(4164, 40, 31, 'iets dergelijks als het geval was met het volk van Noeh, de \'Aad en de Thamoed en hen die er na hen kwamen. Maar God wenst geen onrecht voor Zijn dienaren.'),
(4165, 40, 32, 'Mijn volk! Ik vrees voor jullie de dag van het elkaar toeroepen;'),
(4166, 40, 33, 'de dag waarop jullie de rug zullen toekeren, zullen jullie tegen God geen beschermer hebben. En als God iemand tot dwaling brengt is er voor hem niemand die de goede richting wijst.'),
(4167, 40, 34, 'Joesoef was toch met de duidelijke bewijzen tot jullie gekomen, maar jullie bleven in twijfel verkeren over wat hij jullie bracht. En toen hij dan aan zijn eind gekomen was zeiden jullie: \'God zal na hem wel geen andere gezant laten opstaan.? Zo brengt God tot dwaling wie schaamteloos zijn en twijfelaars,'),
(4168, 40, 35, 'zij die over Gods tekenen twisten zonder dat er een machtiging tot hen gekomen is; dat wekt grote afschuw op bij God en bij hen die geloven. Zo verzegelt God het hart van iedereen die hoogmoedig is en een geweldenaar."'),
(4169, 40, 36, 'En Fir\'aun zei: "Hamaan, bouw voor mij een toren, misschien kan ik dan de ladders bereiken,'),
(4170, 40, 37, 'de ladders van de hemelen, en kan ik omhoogklimmen naar de god van Moesa. Ik vermoed namelijk dat hij een leugenaar is." Zo werd voor Fir\'aun de slechtheid van zijn handelen aantrekkelijk gemaakt en werd hij van de weg afgehouden. En de list van Fir\'aun leidde slechts tot mislukking.'),
(4171, 40, 38, 'Hij die gelovig was zei: "Mijn volk! Volgt mij, dan zal ik jullie de weg van de rechtschapenheid wijzen.'),
(4172, 40, 39, 'Mijn volk! Dit tegenwoordige leven is niet meer dan vruchtgebruik, maar het hiernamaals, dat is de vaststaande woning.'),
(4173, 40, 40, 'Als iemand iets slechts doet, dan wordt hem slechts dienovereenkomstig vergolden. Maar wie van de mannen of de vrouwen deugdelijk handelen en gelovig zijn, dat zijn zij die de tuin binnengaan. Aan hen wordt daarin levensonderhoud gegeven, zonder afrekening. *'),
(4174, 40, 41, 'En, mijn volk, hoe komt het dat ik jullie tot de redding oproep en dat jullie mij tot het vuur oproepen?'),
(4175, 40, 42, 'Jullie roepen mij op aan God geen geloof te hechten en dat ik aan hem metgezellen toevoeg waarvan ik geen kennis heb, maar ik roep jullie op tot de machtige, de vergever.'),
(4176, 40, 43, 'Het staat vast dat er tot dat waartoe jullie mij oproepen helemaal niet opgeroepen kan worden, niet in de tegenwoordige wereld en niet in het hiernamaals, dat onze terugkeer tot God is en dat de onmatigen in het vuur thuishoren.'),
(4177, 40, 44, 'En wat ik tot jullie zeg zal jullie heugen, maar ik vertrouw mijn zaak aan God toe. God doorziet de dienaren."'),
(4178, 40, 45, 'Toen behoedde God hem voor de slechte daden die zij beraamden en de mensen van Fir\'aun werden ingesloten door de vreselijke bestraffing,'),
(4179, 40, 46, 'het vuur, waaraan zij \'s morgens en \'s avonds worden blootgesteld. En op de dag dat het uur aanbreekt [zal gezegd worden]: "Laat de mensen van Fir\'aun de zwaarste bestraffing binnengaan."'),
(4180, 40, 47, 'En wanneer zij in het vuur met elkaar redetwisten en de zwakken tegen hen die hoogmoedig waren zullen zeggen: "Wij waren toch volgelingen van jullie, baten jullie ons dan als bescherming tegen een deel van het vuur?"'),
(4181, 40, 48, 'Zij die hoogmoedig waren zeggen: "Wij zijn er toch allen in; God heeft tussen de dienaren geoordeeld."'),
(4182, 40, 49, 'En zij die in het vuur zijn zeggen tegen de bewakers van de hel: "Bidt tot jullie Heer dat Hij ons een dag verlichting van de bestraffing geeft."'),
(4183, 40, 50, 'Zij zeggen: "Waren tot jullie niet jullie gezanten met de duidelijke bewijzen gekomen?" Zij zeggen: "Ja zeker." Zij zeggen: "Bidt dan zelf." Maar het gebed van de ongelovigen mist steeds zijn doel.'),
(4184, 40, 51, 'Wij helpen Onze gezanten en hen die geloven in het tegenwoordige leven en op de dag dat de getuigen opstaan,'),
(4185, 40, 52, 'op de dag dat hun die onrecht pleegden hun verontschuldiging niet zal baten. Voor hen is de vloek en voor hen is de slechte woning.'),
(4186, 40, 53, 'En Wij hebben Moesa toch de leidraad gegeven en de Israëlieten hebben Wij het boek laten beërven,'),
(4187, 40, 54, 'als leidraad en vermaning voor de verstandigen.'),
(4188, 40, 55, 'Volhard dus geduldig, want Gods toezegging is waar, vraag om vergeving voor jouw zonde en prijs de lof van jouw Heer in de avond en \'s ochtends vroeg.'),
(4189, 40, 56, 'Zij die over Gods tekenen twisten zonder dat er een machtiging tot hen gekomen is hebben in hun harten slechts grootheidswaan; zij zullen hun doel niet bereiken. Vraag dus God om bescherming; Hij is de wetende, de doorziende.'),
(4190, 40, 57, 'De schepping van de hemelen en de aarde is groter dan de schepping van de mens, maar de meeste mensen weten het niet.'),
(4191, 40, 58, 'De blinde en de ziende zijn niet gelijk, noch zij die geloven en de deugdelijke daden doen en zij die verkeerd doen. Hoe weinig laten jullie je vermanen.'),
(4192, 40, 59, 'Het uur komt zeker, daaraan is geen twijfel, maar de meeste mensen geloven niet.'),
(4193, 40, 60, 'En jullie Heer zegt: "Roept Mij aan, dan zal Ik jullie verhoren. Zij die te hoogmoedig zijn om Mij te dienen zullen onderdanig de hel binnengaan."'),
(4194, 40, 61, 'God is het die voor jullie de nacht gemaakt heeft om te rusten en de dag om te zien. God is vol van goedgunstigheid jegens de mensen, maar de meeste mensen betuigen geen dank.'),
(4195, 40, 62, 'Dat is God, jullie Heer, de schepper van alle dingen; er is geen god dan Hij. Hoe kunnen jullie dan zo worden afgeleid?'),
(4196, 40, 63, 'Zij die Gods tekenen ontkennen worden zo afgeleid.'),
(4197, 40, 64, 'God is het die voor jullie de aarde tot een vaste grond heeft gemaakt en de hemel tot een gebouw, die jullie gevormd heeft -- een mooie vorm heeft Hij jullie gegeven -- en die met goede dingen in jullie onderhoud voorziet. Dat is God, jullie Heer. Gezegend zij dus God, de Heer van de wereldbewoners.'),
(4198, 40, 65, 'Hij is de levende, er is geen god dan Hij. Roept Hem dus aan terwijl jullie de godsdienst geheel aan Hem wijden. Lof zij God, de Heer van de wereldbewoners. *'),
(4199, 40, 66, 'Zeg: "Toen tot mij de duidelijke bewijzen van mijn Heer kwamen is mij verboden hen te dienen die jullie in plaats van God aanroepen en Mij is bevolen mij aan de Heer van de wereldbewoners over te geven.'),
(4200, 40, 67, 'Hij is het die jullie uit aarde geschapen heeft, daarna uit een druppel en daarna uit een bloedklonter. Dan brengt Hij jullie als kind tevoorschijn. Dan moeten jullie volgroeid worden. Dan moeten jullie oude mensen worden -- er zijn er onder jullie die Hij eerder wegneemt? en jullie moeten een vastgestelde termijn bereiken. En misschien zullen jullie verstandig worden.'),
(4201, 40, 68, 'Hij is het die leven geeft en laat sterven en wanneer Hij iets beslist zegt Hij er slechts tegen: \'Wees!? en het is."'),
(4202, 40, 69, 'Heb jij niet gezien naar hen die over Gods tekenen twisten hoe zij zich laten verstrooien?'),
(4203, 40, 70, 'Zij die het boek en dat waarmee Wij Onze gezanten gezonden hebben loochenen, zullen het weten.'),
(4204, 40, 71, 'Wanneer zij de halsketenen om hun nekken hebben en de kettingen, worden zij gesleurd'),
(4205, 40, 72, 'in het gloeiende water en daarna in het vuur verbrand.'),
(4206, 40, 73, 'Dan wordt er tot hen gezegd: "Waar zijn zij dan die jullie als [zogenaamd goddelijke] metgezellen vereren'),
(4207, 40, 74, 'in plaats van God?" Zij zeggen: "Wij zijn hen kwijt. Nee, wij hadden vroeger niets aangeroepen." Zo brengt God de ongelovigen tot dwaling.'),
(4208, 40, 75, 'Dat komt omdat jullie je op de aarde onterecht verheugden en omdat jullie verwaand waren.'),
(4209, 40, 76, '"Gaat de poorten van de hel binnen om er altijd in te blijven. Dat is pas een slechte verblijfplaats voor de hoogmoedigen!"'),
(4210, 40, 77, 'Volhard dus geduldig, want Gods toezegging is waar. En of Wij jou iets van wat wij hun aangezegd hebben laten zien of dat Wij jou wegnemen, tot Ons zullen zij teruggebracht worden.'),
(4211, 40, 78, 'En Wij hebben al voor jouw tijd gezanten gezonden. Onder hen zijn er over wie Wij jou verteld hebben en onder hen zijn er over wie Wij jou niet verteld hebben. Het komt een gezant niet toe een teken te brengen zonder Gods toestemming. Maar wanneer Gods beschikking komt wordt naar waarheid beslist. En zij die zeggen dat het niet waar is zullen verloren zijn.'),
(4212, 40, 79, 'God is het die voor jullie het vee gemaakt heeft om erop te rijden en om ervan te eten.'),
(4213, 40, 80, 'Jullie hebben daarmee allerlei nuttigheden. En ook kunnen jullie erop iets bereiken wat jullie na aan het hart ligt. Op hen en ook op de schepen worden jullie gedragen.'),
(4214, 40, 81, 'En Hij laat jullie Zijn tekenen zien. Welk teken van God verwerpen jullie dan?'),
(4215, 40, 82, 'Hebben zij dan niet op de aarde rondgereisd en ernaar gekeken hoe het einde was van hen die er vóór hen waren? Zij waren meer dan hen en sterker en lieten meer sporen achter op de aarde, maar wat zij ten uitvoer hadden gebracht baatte hun niet.'),
(4216, 40, 83, 'Toen dan Onze gezanten met de duidelijke bewijzen tot hen kwamen verheugden zij zich over wat zij aan kennis hadden, maar zij werden ingesloten door dat waarmee zij de spot dreven.'),
(4217, 40, 84, 'En toen zij Ons geweld zagen zeiden zij: "Wij geloven in God alleen en wij hechten geen geloof aan wat wij aan Hem als metgezellen toegevoegd hebben."'),
(4218, 40, 85, 'Maar zij hadden geen nut van hun geloof toen zij Ons geweld zagen. Het was Gods gebruikelijke behandeling die reeds eerder te midden van Zijn dienaren had plaatsgevonden. Daar hadden de ongelovigen dus verloren.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'H[aa?] M[iem].'),
(4220, 41, 2, 'Een neerzending door de Erbarmer, de barmhartige,'),
(4221, 41, 3, 'een boek waarvan de tekenen zijn uiteengezet als een Arabische Koran voor mensen die weten,'),
(4222, 41, 4, 'als een verkondiger van goed nieuws en een waarschuwer. Maar de meesten van hen wenden zich af; zij luisteren dus niet.'),
(4223, 41, 5, 'En zij zeggen: "Onze harten zijn in hoezen afgesloten van dat waartoe jij ons oproept, in onze oren is hardhorendheid en tussen jou en ons is een afscheiding. Handel dan maar, wij zullen het ook doen."'),
(4224, 41, 6, 'Zeg: "Ik ben slechts een mens als jullie; aan mij wordt geopenbaard dat jullie god één god is. Handelt dus correct jegens Hem en vraagt Hem om vergeving. Wee dus de veelgodendienaars,'),
(4225, 41, 7, 'die de zakaat niet geven en die aan het hiernamaals geen geloof hechten.'),
(4226, 41, 8, 'Zij die geloven en de deugdelijke daden doen zullen een ononderbroken loon hebben."'),
(4227, 41, 9, 'Zeg: "Hechten jullie echt geen geloof aan Hem die de aarde in twee dagen geschapen heeft en schrijven jullie aan Hem gelijken toe? Dat is de Heer van de wereldbewoners!"'),
(4228, 41, 10, 'Hij heeft erop stevige, er bovenuitstekende bergen gemaakt, haar gezegend en allerlei levensonderhoud voor haar verordend in vier dagen, gelijkmatig verdeeld voor hen die vragen.'),
(4229, 41, 11, 'Toen wendde Hij zich tot de hemel die rook was en Hij zei ertegen en tegen de aarde: "Komt, goedschiks of kwaadschiks." Zij zeiden beide: "Wij komen goedschiks."'),
(4230, 41, 12, 'Toen voltooide Hij ze in twee dagen als zeven hemelen en openbaarde in elke hemel wat de ordening ervan was. En Wij hebben de dichtstbijzijnde hemel met lampen opgesierd die ook als bescherming dienen. Dat is de verordening van de machtige, de wetende.'),
(4231, 41, 13, 'En als zij zich afwenden zeg dan: "Ik waarschuw jullie voor een donderslag als de donderslag van de \'Aad en de Thamoed."'),
(4232, 41, 14, 'Toen hun gezanten van voren en van achteren tot hen kwamen [en zeiden]: "Jullie moeten alleen God dienen", zeiden zij: "Als Onze Heer wilde dan zond Hij engelen neer, maar waarmee jullie gezonden zijn daaraan hechten wij geen geloof."'),
(4233, 41, 15, 'Wat de \'Aad betreft, zij waren onterecht hoogmoedig op de aarde en zij zeiden: "Wie is er sterker dan wij?" Hebben zij dan niet gezien dat God, die hen geschapen heeft sterker is dan zij? Maarzij ontkenden Onze tekenen.'),
(4234, 41, 16, 'Dus zonden Wij tegen hen een gierende wind op onheilspellende dagen om hun de bestraffing van de schande in het tegenwoordige leven te laten proeven, maar de bestraffing in het hiernamaals is nog schandelijker. En zij krijgen geen hulp.'),
(4235, 41, 17, 'En wat de Thamoed betreft, Wij wezen hun de goede richting, maar zij hadden blindheid liever dan de goede richting gewezen te worden. Toen greep de donderslag van de schandelijke bestraffing hen voor wat zij begaan hadden.'),
(4236, 41, 18, 'Maar Wij redden hen die geloofden en godvrezend waren.'),
(4237, 41, 19, 'Maar op de dag dat Gods vijanden in het vuur verzameld worden en dan in het gelid worden gezet!'),
(4238, 41, 20, 'En wanneer zij daar dan gekomen zijn zal hun gehoor, hun gezicht en hun huid tegen hen getuigen over wat zij deden.'),
(4239, 41, 21, 'En zij zeggen tegen hun huid: "Waarom getuigden jullie tegen ons?" En die zullen dan zeggen: "God die alles laat spreken heeft ons laten spreken en Hij heeft jullie de eerste maal geschapen en tot Hem worden jullie teruggebracht.'),
(4240, 41, 22, 'Jullie hadden je namelijk niet zo verborgen dat jullie gehoor, jullie gezicht en jullie huid niet tegen jullie konden getuigen. Jullie dachten echter dat God niet veel wist van wat jullie deden.'),
(4241, 41, 23, 'En die gedachte die jullie over jullie Heer hadden heeft jullie in het verderf gestort en jullie behoren nu tot de verliezers."'),
(4242, 41, 24, 'Als zij volharden dan is het vuur een verblijfplaats voor hen en als zij om een mildere behandeling vragen dan wordt hun die niet gegeven. *'),
(4243, 41, 25, 'En Wij hadden voor hen kameraden gemaakt die toen voor hen aantrekkelijk maakten wat er voor hen was en wat er achter hen was en het woord wordt tegen hen bewaarheid, te midden van gemeenschappen van de djinn en de mensen die al voor hun tijd voorbijgegaan waren; zij waren verliezers.'),
(4244, 41, 26, 'Zij die ongelovig zijn zeggen: "Luistert niet naar deze Koran en praat er zonder op te letten doorheen. Misschien zullen jullie overwinnen."'),
(4245, 41, 27, 'Maar hun die ongelovig zijn zullen Wij een strenge bestraffing laten proeven en Wij zullen aan hen het slechtste dat zij gedaan hebben vergelden.'),
(4246, 41, 28, 'Dat is het loon voor Gods vijanden: het vuur waarin zij een altijd bestaande woning hebben als vergelding dat zij Onze tekenen ontkenden.'),
(4247, 41, 29, 'En zij die ongelovig zijn zeggen: "Onze Heer, laat ons die mensen en djinn zien die ons tot dwaling hebben gebracht, dan zullen wij hen onder onze voeten zetten zodat zij de onderliggenden zijn."'),
(4248, 41, 30, 'Maarzij die zeggen: "Onze Heer is God" en die dan correct handelen, tot hen komen de engelen neer: "Vreest niet en weest niet bedroefd, maar verheugt jullie in de tuin die jullie is toegezegd.'),
(4249, 41, 31, 'Wij zijn jullie medestanders in het tegenwoordige leven en in het hiernamaals, daarin is voor jullie wat jullie zielen begeren en daarin is voor jullie wat jullie verlangen:'),
(4250, 41, 32, 'Een gastverblijf van een Vergevende en een Barmhartige."'),
(4251, 41, 33, 'En wie spreekt er beter dan wie tot God oproept, deugdelijk handelt en zegt: "Ik behoor tot hen die zich [aan God] overgeven."'),
(4252, 41, 34, 'En de goede daad en de slechte daad zijn niet gelijk; weer die af met iets wat beter is. Dan zal hij, tussen wie en jou vijandschap was, zijn als een boezemvriend.'),
(4253, 41, 35, 'Maar het wordt slechts aan hen die geduldig volharden aangeboden; het wordt slechts aangeboden aan iemand met geweldig geluk.'),
(4254, 41, 36, 'En als jij door de satan wordt opgehitst, vraag dan God om bescherming; Hij is de horende, de wetende.'),
(4255, 41, 37, 'Tot Zijn tekenen behoren de nacht en de dag en de zon en de maan. Buigt niet eerbiedig neer voor de zon en de maan, maar buigt eerbiedig neer voor God die hen geschapen heeft, als Hij het is die jullie dienen.'),
(4256, 41, 38, 'Als zij dan hoogmoedig zijn? zij die bij hun Heer zijn prijzen Hem in de nacht en overdag en zij verafschuwen het niet."'),
(4257, 41, 39, 'En tot Zijn tekenen behoort dat jullie zien dat de aarde roerloos is, maar wanneer Wij er dan water op laten neerdalen beweegt zij zich en zwelt op. Hij die haar laat herleven, laat ook de doden herleven; Hij is almachtig.'),
(4258, 41, 40, 'Zij die Onze tekenen misduiden zijn voor Ons niet verborgen. Is dan iemand die in het vuur geworpen wordt er beter aan toe of iemand die veilig op de opstandingsdag aankomt? Doet maar wat jullie willen; Hij doorziet wat jullie doen.'),
(4259, 41, 41, 'Zij die geen geloof hechten aan de vermaning wanneer die tot hen komt?; het is een machtig boek,'),
(4260, 41, 42, 'waar de onwaarheid van voren niet bij komt, noch van achteren, een neerzending door een Wijze en Lofwaardige.'),
(4261, 41, 43, 'Aan jou wordt slechts gezegd wat al aan de gezanten voor jouw tijd gezegd was; jouw Heer is vol vergeving maar geeft ook een pijnlijke afstraffing.'),
(4262, 41, 44, 'En als Wij hem als een vreemdtalige Koran hadden gemaakt dan hadden zij gezegd: "Waarom zijn de tekenen ervan niet uiteengezet? Vreemdtalig en toch een Arabier?" Zeg: "Hij is voor hen die geloven een leidraad en een genezing." Zij die niet geloven hebben in hun oren hardhorendheid en zij hebben er geen zicht op. Zij zijn het die van een verafgelegen plaats worden geroepen.'),
(4263, 41, 45, 'En Wij hadden aan Moesa het boek gegeven, maar toen werd men het erover oneens. En als er al niet eerder een woord van jouw Heer gekomen was, dan was er tussen hen al een beslissing getroffen. Zij verkeren daarover in hevige twijfel.'),
(4264, 41, 46, 'Als iemand deugdelijk handelt dan is het in zijn eigen voordeel, maar als iemand verkeerd doet dan is het in zijn nadeel; jouw Heer geeft geen onrechtvaardige behandeling aan de dienaren. *'),
(4265, 41, 47, 'De kennis over het uur gaat op Hem terug. Er komen geen vruchten uit hun doppen en geen vrouw is zwanger en baart zonder dat Hij het weet. En op de dag dat Hij hen toeroept: "Waar zijn dan Mijn [zogenaamd goddelijke] metgezellen", zeggen zij: "Wij delen U mee dat er bij ons geen getuige is."'),
(4266, 41, 48, 'Maar dat wat zij vroeger aanriepen zijn zij kwijt en zij dachten dat er voor hen geen ontsnapping was.'),
(4267, 41, 49, 'De mens verafschuwt het niet om voor het goede te bidden, maar als hem het kwaad treft is hij wanhopig en vertwijfeld.'),
(4268, 41, 50, 'En als Wij hem van Onze kant barmhartigheid laten proeven na rampspoed die hem getroffen heeft dan zegt hij: "Dit komt mij toe en ik denk niet dat het uur aanstaande is en als ik naar mijn Heer word teruggebracht krijg ik bij Hem zeker het beste." Maar Wij zullen hun die ongelovig zijn meedelen wat zij deden en Wij zullen hen van een harde bestraffing laten proeven.'),
(4269, 41, 51, 'En wanneer Wij de mens genade schenken, wendt hij zich af en gaat opzij. Maar wanneer hem het kwaad treft dan is hij breedsprakig in het gebed.'),
(4270, 41, 52, 'Zeg: "Hoe zien jullie het? Als het van God afkomstig is en jullie hechten er dan geen geloof aan? Wie dwaalt er meer dan wie het er vergaand mee oneens is?"'),
(4271, 41, 53, 'Wij zullen hun Onze tekenen aan de horizonten en in henzelf laten zien zodat het voor hen duidelijk wordt dat het de waarheid is. Is het dan niet genoeg dat jouw Heer over alles getuige is?'),
(4272, 41, 54, 'Toch verkeren zij in twijfel over de ontmoeting met hun Heer en toch omvat Hij alles.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'H[aa?] Mpem].'),
(4274, 42, 2, '\'[Ain] S[ien] K[aaf].'),
(4275, 42, 3, 'Zo openbaart God, de machtige, de wijze, aan jou en aan hen die er voor jouw tijd waren.'),
(4276, 42, 4, 'Van Hem is wat er in de hemelen en wat er op de aarde is en Hij is de verhevene, de geweldige.'),
(4277, 42, 5, 'Bijna zouden de hemelen boven hen barsten; de engelen prijzen de lof van hun Heer en zij vragen om vergeving voor wie er op de aarde zijn. Is God niet de vergevende, de barmhartige?'),
(4278, 42, 6, 'Van hen die zich in plaats van God beschermers genomen hebben is God een bewaker en jij bent niet voogd over hen.'),
(4279, 42, 7, 'En zo hebben Wij aan jou een Arabische Koran geopenbaard, opdat jij er de hoofdstad [Mekka] en wie er omheen wonen mee waarschuwt en opdat jij waarschuwt voor de dag van de bijeenzameling waaraan geen twijfel is: een groep in de tuin en een groep in de vuurgloed.'),
(4280, 42, 8, 'En als God het gewild had zou Hij hen tot één gemeenschap gemaakt hebben, maar Hij laat in Zijn barmhartigheid binnengaan wie Hij wil. En de onrechtplegers hebben geen beschermer en geen helper.'),
(4281, 42, 9, 'Of hebben zij zich in plaats van Hem beschermers genomen? Maar God is de beschermer en Hij maakt de doden levend en Hij is almachtig.'),
(4282, 42, 10, 'En waarover jullie het ook oneens zijn, het oordeel erover komt God toe. Dat is God, mijn Heer. Op Hem stel ik mijn vertrouwen en tot Hem wend ik mij schuldbewust.'),
(4283, 42, 11, 'De grondlegger van de hemelen en de aarde. Hij heeft uit jullie eigen midden echtgenotes voor jullie gemaakt en ook uit het vee paren; daarmee vermeerdert Hij jullie. Niets is aan Hem gelijk. Hij is de horende, de doorziende.'),
(4284, 42, 12, 'Hij heeft de sleutels van de hemelen en de aarde. Hij voorziet ruimschoots in het levensonderhoud van wie Hij wil en ook met mate. Hij is alwetend. *'),
(4285, 42, 13, 'Hij verordineert voor jullie van de godsdienst wat Hij aan Noeh had opgedragen en wat Wij aan jou geopenbaard hebben en wat Wij aan Ibrahiem, Moesa en \'Isa hadden opgedragen: Houdt de godsdienst in stand en splitst jullie daarin niet op in groepen. De veelgodendienaars vinden het erg waartoe jij hen oproept. God verkiest daartoe wie Hij wil en wijst wie schuldbewust is er de goede richting heen.'),
(4286, 42, 14, 'Zij splitsten zich uit onderlinge nijd pas nadat tot hen de kennis was gekomen in groepen op. En als er al niet eerder een woord van jouw Heer over een vastgestelde termijn gekomen was, dan was er tussen hen al een beslissing getroffen. Maar zij die na hen het boek als erfdeel hebben gekregen verkeren in hevige twijfel.'),
(4287, 42, 15, 'Doe daarom dus de oproep en handel correct zoals aan jou is bevolen en volg hun neigingen niet. En zeg: "Ik geloof in wat God als boek heeft neergezonden en aan mij is bevolen bij jullie rechtvaardig te handelen. God is onze Heer en jullie Heer; wij hebben onze daden en jullie hebben jullie daden. Er is tussen ons en jullie geen tegenspraak; God zal ons samenbrengen en bij Hem is de bestemming."'),
(4288, 42, 16, 'En van hen die over God redetwisten nadat aan Hem gehoor gegeven was, is de tegenspraak bij God weerlegd; op hen rust toorn en voor hen is er een strenge bestraffing.'),
(4289, 42, 17, 'God is het die het boek met de waarheid neergezonden heeft en ook de weegschaal. En hoe zul jij te weten komen of misschien het uur dichtbij is?'),
(4290, 42, 18, 'Zij die er niet aan geloven willen het verhaasten, maar zij die eraan geloven hebben er ontzag voor. Zij weten dat het werkelijkheid is. Zeker, zij die over het uur twisten zijn ver afgedwaald.'),
(4291, 42, 19, 'God is zachtmoedig voor Zijn dienaren, Hij voorziet wie Hij wil, en Hij is de sterke, de almachtige.'),
(4292, 42, 20, 'Wie de oogst van het hiernamaals wenst, diens oogst vermeerderen Wij en wie de oogst van het tegenwoordige leven wenst, die geven Wij ervan, maar een aandeel in het hiernamaals heeft hij niet.'),
(4293, 42, 21, 'Of hebben zij [zogenaamde] metgezellen [van God] die voor hen van de godsdienst dat verordineren wat God niet toegestaan heeft? En als er niet een beschikkingswoord geweest was dan was er tussen hen al een beslissing getroffen. Voor de onrechtplegers is er echt een pijnlijke bestraffing.'),
(4294, 42, 22, 'Jij zult dan de onrechtplegers terug zien deinzen voor wat zij begaan hebben, terwijl het hen overvalt. Maar zij die geloven en de deugdelijke daden doen zullen in de hoven van de tuinen zijn. Zij hebben bij hun Heer wat zij willen. Dat is de grote goedgunstigheid.'),
(4295, 42, 23, 'Dat is het goede nieuws dat God verkondigt aan Zijn dienaren die geloven en de deugdelijke daden doen. Zeg: "Ik vraag er geen loon voor, slechts de [normale] vriendelijkheid van verwanten." En als iemand een goede daad doet, dan zullen Wij het goede daarin voor hem vermeerderen. God is vergevend en dank betuigend.'),
(4296, 42, 24, 'Of zeggen zij: "Hij heeft over God een leugen verzonnen"? Als God wil verzegelt Hij jouw hart. God wist de onzin uit en bevestigt de waarheid met Zijn woorden. Hij weet wat er binnen in de harten is.'),
(4297, 42, 25, 'Hij is het die het berouw van Zijn dienaren aanneemt en de slechte daden niet aanrekent en Hij weet wat jullie doen.'),
(4298, 42, 26, 'En Hij verhoort hen die geloven en de deugdelijke daden doen en Hij zal hun van Zijn goedgunstigheid nog meer geven. Maar de ongelovigen, voor hen is er een strenge bestraffing.'),
(4299, 42, 27, 'En als God ruimschoots in het levensonderhoud van Zijn dienaren zou voorzien dan zouden zij zich op de aarde onrechtmatig gedragen, maar Hij laat wat Hij wil met mate neerdalen. God is over Zijn dienaren goed ingelicht en Hij doorziet hen.'),
(4300, 42, 28, 'Hij is het die de regen laat neerdalen nadat zij de hoop hadden opgegeven en Hij spreidt Zijn barmhartigheid uit; Hij is de beschermheer, de lofwaardige.'),
(4301, 42, 29, 'Tot Zijn tekenen behoort de schepping van de hemelen en de aarde en de dieren die Hij erop heeft verspreid. En als Hij wil is Hij bij machte om hen bijeen te brengen.'),
(4302, 42, 30, 'En het onheil dat jullie treft, het is voor wat jullie handen begaan hebben. Maar Hij scheldt veel kwijt.'),
(4303, 42, 31, 'En jullie kunnen er op aarde niets tegen doen en jullie hebben buiten God geen beschermer noch helper.'),
(4304, 42, 32, 'En tot Zijn tekenen behoren de schepen die als bergen op de zee varen.'),
(4305, 42, 33, 'Als Hij wil laat Hij de wind gaan liggen zodat zij bewegingloos op haar oppervlak blijven liggen. Daarin zijn zeker tekenen voor ieder die geduldig volhardt en die dank betuigt.'),
(4306, 42, 34, 'Of Hij laat ze ondergaan voor wat zij begaan hebben. Maar Hij scheldt veel kwijt.'),
(4307, 42, 35, 'Zij die over Zijn tekenen twisten moeten maar weten dat er voor hen geen ontsnapping is.'),
(4308, 42, 36, 'Wat jullie ook gegeven is, het is het genot van het tegenwoordige leven, maar wat van God komt is beter en onvergankelijker voor hen die geloven en hun vertrouwen op hun Heer stellen'),
(4309, 42, 37, 'en die de grote zonden en gruwelijkheden vermijden en wanneer zij boos worden toch vergevensgezind zijn'),
(4310, 42, 38, 'en die gehoor geven aan hun Heer en de salaat verrichten, wier beleid onderling beraad is en die van wat Wij hun voor hun levensonderhoud gegeven hebben bijdragen geven'),
(4311, 42, 39, 'en die weerstand bieden wanneer hen gewelddadigheid treft.'),
(4312, 42, 40, 'De vergelding voor een slechte daad is een overeenkomstige slechte daad. Maar als iemand kwijtscheldt en het weer goedmaakt, dan is zijn beloning Gods taak; Hij bemint de onrechtplegers niet.'),
(4313, 42, 41, 'Maar zij die weerstand bieden nadat hun onrecht is aangedaan, hun kan men niets verwijten.'),
(4314, 42, 42, 'Wel kan men hun iets verwijten die de mensen onrecht aandoen en die zich zonder enig recht op de aarde onrechtmatig gedragen; zij zijn het voor wie er een pijnlijke bestraffing is.'),
(4315, 42, 43, 'En als iemand geduldig volhardt en vergevensgezind blijft, dan is dat een zaak van vastbeslotenheid.'),
(4316, 42, 44, 'Wie door God tot dwaling gebracht wordt heeft daarna geen beschermer meer. En je ziet de onrechtplegers zeggen wanneer zij de bestraffing zien: "Is er nog een weg om terug te keren?"'),
(4317, 42, 45, 'En jij ziet dat zij, deemoedig door de vernedering, eraan worden blootgesteld, terwijl zij steels uit de ooghoeken kijken. Maar zij die geloven zeggen: "De verliezers zijn zij die zichzelf en hun families verliezen op de opstandingsdag." Zeker, de onrechtplegers bevinden zich in een blijvende bestraffing.'),
(4318, 42, 46, 'Zij hebben geen beschermers die hen in plaats van God kunnen helpen. Wie door God tot dwaling wordt gebracht, voor hem is er geen weg.'),
(4319, 42, 47, 'Geeft gehoor aan jullie Heer voordat er van God een dag komt waarvan geen terugkeer meer is. Voor jullie is er geen toevluchtsoord op die dag, noch een mogelijkheid te ontkennen.'),
(4320, 42, 48, 'Als zij zich afwenden? Wij hebben jou niet als bewaker tot hen gezonden. Jij hebt slechts de plicht van de verkondiging. En wanneer Wij van Onze kant de mens barmhartigheid laten proeven, dan verheugt hij zich erover, maar als hem kwaad treft om wat hun handen eerder gedaan hebben dan is de mens ondankbaar.'),
(4321, 42, 49, 'God heeft de heerschappij over de hemelen en de aarde. Hij schept wat Hij wil; hij schenkt aan wie Hij wil vrouwelijk en aan wie Hij wil mannelijk nageslacht.'),
(4322, 42, 50, 'Of Hij geeft beide, mannelijk en vrouwelijk. En Hij maakt kinderloos wie Hij wil; Hij is wetend en vrij machtig.'),
(4323, 42, 51, 'En het is een mens niet gegeven dat God tot hem spreekt, anders dan door openbaring of vanachter een afscheiding of doordat Hij een gezant zendt die met Zijn toestemming openbaart wat Hij wil. Hij is verheven en wijs.'),
(4324, 42, 52, 'Zo hebben Wij aan jou een geest door Onze beschikking geopenbaard. Jij wist niet wat het boek, noch wat het geloof was, maar Wij hebben het tot een licht gemaakt waarmee Wij van Onze dienaren wie Wij willen de goede richting wijzen. En jij, jij wijst de richting naar een juiste weg,'),
(4325, 42, 53, 'naar de weg van God, van wie is wat er in de hemelen en wat er op de aarde is. Zeker, aan God worden uiteindelijk alle zaken voorgelegd.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'H[aa?] M[iem].'),
(4327, 43, 2, 'Bij het duidelijke boek!'),
(4328, 43, 3, 'Wij hebben het tot een Arabische Koran gemaakt; misschien zullen jullie verstandig worden.'),
(4329, 43, 4, 'En het is in het oorspronkelijke boek bij Ons, verheven en wijs.'),
(4330, 43, 5, 'Zullen Wij de vermaning dan aan jullie voorbij laten gaan omdat jullie onmatig zijn?'),
(4331, 43, 6, 'Hoeveel profeten hebben Wij al niet gezonden onder hen die er eertijds waren.'),
(4332, 43, 7, 'En er kwam geen profeet tot hen of zij dreven de spot met hem.'),
(4333, 43, 8, 'Toch hebben Wij er die meer macht dan jullie bezaten vernietigd en het voorbeeld van hen die er eertijds waren is al eerder toegepast.'),
(4334, 43, 9, 'Als jij hun vraagt wie de hemelen en de aarde heeft geschapen zeggen zij: "De machtige, de wetende heeft ze geschapene'),
(4335, 43, 10, 'Hij is het die voor jullie de aarde tot een wiegenbed heeft gemaakt en die daarop wegen heeft gemaakt -- opdat jullie je misschien de goede richting zullen laten wijzen --'),
(4336, 43, 11, 'en die uit de hemel met mate water heeft laten neerdalen -- Wij wekken daarmee dan een dode streek weer tot leven. Zo zullen jullie tevoorschijn worden gebracht --'),
(4337, 43, 12, 'en die alle paren geschapen heeft en die voor jullie schepen gemaakt heeft en vee waarop jullie kunnen rijden,'),
(4338, 43, 13, 'opdat jullie er boven op plaats nemen. Gedenkt dan de genade van jullie Heer, wanneer jullie erop hebben plaats genomen en zeggen: "Geprezen zij Hij die dit voor ons dienstbaar heeft gemaakt; wij waren daartoe niet in staat.'),
(4339, 43, 14, 'Wij zullen zeker tot onze Heer omkeren."'),
(4340, 43, 15, 'Maar zij maakten sommigen van Zijn dienaren tot een deel van Hem. De mens is werkelijk duidelijk ondankbaar.'),
(4341, 43, 16, 'Of heeft Hij zich uit wat Hij schept dochters genomen en voor jullie de zonen uitgekozen?'),
(4342, 43, 17, 'Wanneer een van hen het goede nieuws krijgt van wat voor de Erbarmer kenmerkend zou zijn, dan betrekt zijn gezicht en is hij vol ingehouden woede.'),
(4343, 43, 18, 'Of zulken die in weelde worden grootgebracht en die zich in twistgesprekken niet duidelijk kunnen uitdrukken?'),
(4344, 43, 19, 'Zij maken de engelen die de dienaren van de Erbarmer zijn tot vrouwelijke wezens. Waren zij soms getuige van hun schepping? Hun getuigenis zal worden opgeschreven en zij zullen worden ondervraagd.'),
(4345, 43, 20, 'En zij zeggen: "Als de Erbarmer gewild had hadden wij hen niet gediend." Daar hebben zij geen weet van; zij gissen slechts.'),
(4346, 43, 21, 'Of hadden Wij hun vroeger al een boek gegeven waaraan zij kunnen vasthouden?'),
(4347, 43, 22, 'Welnee, maar zij zeggen: "Wij hebben gemerkt dat onze vaderen tot een [geloofs]gemeenschap behoorden en in hun spoor gaan wij in de goede richting."'),
(4348, 43, 23, 'En zo hebben Wij vóór jouw tijd geen waarschuwer naar een stad gezonden zonder dat haar inwoners die een luxeleven leidden zeiden: "Wij hebben gemerkt dat onze vaderen tot een [geloofs]gemeenschap behoorden en in hun spoor gaan wij verder." *'),
(4349, 43, 24, 'Hij zei: "En als ik dan tot jullie kom met een betere leidraad dan die waarvan jullie gemerkt hebben dat jullie vaderen zich eraan hielden?" Zij zeiden: "Waarmee jullie gezonden zijn, daaraan hechten wij geen geloof."'),
(4350, 43, 25, 'Dus namen Wij wraak op hen. En kijk dan hoe het einde was van de loochenaars!'),
(4351, 43, 26, 'En toen Ibrahiem tot zijn vader en zijn volk zei: "Ik heb niets te maken met wat jullie dienen,'),
(4352, 43, 27, 'maar alleen met Hem die mij geschapen heeft; Hij zal mij op het goede pad brengen."'),
(4353, 43, 28, 'Dat maakte hij tot een blijvende uitspraak in zijn nageslacht. Misschien zullen zij terugkeren.'),
(4354, 43, 29, 'Maar Ik heb dezen daar en hun vaderen nog laten genieten totdat de waarheid en een duidelijke gezant tot hen kwamen.'),
(4355, 43, 30, 'En toen de waarheid tot hen kwam zeiden zij: "Dit is toverij en wij hechten er geen geloof aan."'),
(4356, 43, 31, 'En zij zeiden: "Had deze Koran niet tot een aanzienlijk man uit de beide steden neergezonden kunnen worden?"'),
(4357, 43, 32, 'Verdelen zij soms de barmhartigheid van hun Heer? Wij zijn het die onder hen hun levensbehoeften in het tegenwoordige leven hebben verdeeld en wij hebben sommigen van hen hogere rangen dan anderen gegeven opdat de een de ander in dienst neemt. Maar de barmhartigheid van jouw Heer is beter dan wat zij bijeenbrengen.'),
(4358, 43, 33, 'En als de mensen niet één gemeenschap waren geweest, dan hadden Wij aan hen die aan de Erbarmer geen geloof hechten voor hun huizen daken van zilver gegeven en trappen om omhoog te gaan,'),
(4359, 43, 34, 'en deuren voor hun huizen en rustbanken waarop zij achteroverleunen,'),
(4360, 43, 35, 'en pracht en praal. Dat alles is slechts het vruchtgebruik van het tegenwoordige leven, maar het hiernamaals is bij jouw Heer voor de godvrezenden.'),
(4361, 43, 36, 'En wie zich voor de vermaning van de Erbarmer blind houdt, voor hem maken Wij een satan die dan een kameraad voor hem is --'),
(4362, 43, 37, 'die zullen hun dan de weg versperren, maar zij denken dat zij op het goede pad zijn --'),
(4363, 43, 38, 'en wanneer hij dan tot Ons komt, zegt hij: "Ach, was er tussen mij en U een afstand als tussen oost en west!" Dat is dus pas een slechte kameraad.'),
(4364, 43, 39, 'Het zal jullie vandaag, omdat jullie onrecht gepleegd hebben, niet baten dat jullie deelgenoten in de bestraffing zijn.'),
(4365, 43, 40, 'Kun jij dan de doven laten horen of de blinden en wie in duidelijke dwaling verkeren de goede richting wijzen?'),
(4366, 43, 41, 'Als Wij jou wegnemen, dan zullen Wij wraak op hen nemen,'),
(4367, 43, 42, 'of Wij zullen jou wat Wij hun hebben aangezegd laten zien, want Wij hebben de macht over hen.'),
(4368, 43, 43, 'Houd vast aan wat aan jou is geopenbaard; jij bent op een juiste weg.'),
(4369, 43, 44, 'Het is namelijk een vermaning voor jou en jouw volk en jullie zullen verantwoording moeten afleggen.'),
(4370, 43, 45, 'En vraag aan gezanten die Wij voor jouw tijd gezonden hebben of Wij in plaats van de Erbarmer goden gemaakt hebben die zij moeten dienen.'),
(4371, 43, 46, 'Wij hebben Moesa met Onze tekenen naar Fir\'aun en zijn raad van voornaamsten gezonden en hij zei: "Ik ben de gezant van de Heer van de wereldbewoners."'),
(4372, 43, 47, 'Maar toen hij met Onze tekenen tot hen kwam, toen begonnen zij er meteen om te lachen.'),
(4373, 43, 48, 'Toch lieten Wij hun alleen maar tekenen zien waarvan het ene nog groter was dan het andere. En Wij grepen hen met de bestraffing, opdat zij misschien terug zouden keren.'),
(4374, 43, 49, 'En zij zeiden: "O tovenaar, bid voor ons tot jouw Heer op grond van wat Hij jou opgedragen heeft, dan zullen wij het goede pad volgen."'),
(4375, 43, 50, 'Maar toen Wij de bestraffing voor hen ophieven, braken zij meteen hun woord.'),
(4376, 43, 51, 'En Fir\'aun liet onder zijn volk omroepen: "Mijn volk, heb ik niet de heerschappij over Egypte en deze rivieren die beneden mij langsstromen? Hebben jullie dan geen inzicht?'),
(4377, 43, 52, 'Is het niet zo dat ik beter ben dan deze hier die verachtelijk is en die zich nauwelijks kan uitdrukken?'),
(4378, 43, 53, 'Als hem nu armbanden van goud waren omgehangen of de engelen als begeleiding met hem waren meegekomen?"'),
(4379, 43, 54, 'Zo bracht hij zijn volk aan het weifelen en zij gehoorzaamden hem; zij waren verdorven mensen.'),
(4380, 43, 55, 'Toen zij Ons dan kwaad gemaakt hadden namen Wij wraak op hen en verdronken hen allen tezamen.'),
(4381, 43, 56, 'Zo maakten Wij hen tot voorlopers en voorbeeld voor de lateren.'),
(4382, 43, 57, 'En toen de zoon van Marjam als voorbeeld werd aangehaald, begon jouw volk daarover meteen te schreeuwen.'),
(4383, 43, 58, 'En zij zeiden: "Zijn onze goden beter of hij?" Maar zij haalden hem alleen maar als voorbeeld aan om te twisten. Ja zeker, zij zijn twistzieke mensen.'),
(4384, 43, 59, 'Hij is slechts een dienaar aan wie Wij genade geschonken hebben en die Wij tot een voorbeeld voor de Israëlieten hebben gemaakt.'),
(4385, 43, 60, 'En als Wij wilden hadden Wij uit jullie midden engelen gemaakt die jullie op de aarde hadden opgevolgd.'),
(4386, 43, 61, 'En hij is een kenteken voor het uur. Twijfelt er dus niet aan en volgt mij; dat is een juiste weg.'),
(4387, 43, 62, 'En laat de satan jullie er niet van afhouden; hij is voor jullie een verklaarde vijand.'),
(4388, 43, 63, 'Toen \'Isa met de duidelijke bewijzen kwam zei hij: "Ik ben met de wijsheid tot jullie gekomen en ik zal jullie enige van de dingen waarover jullie het oneens zijn duidelijk maken. En vreest God en gehoorzaamt mij.'),
(4389, 43, 64, 'God is mijn Heer en Hij is jullie Heer. Dient Hem dus, dat is een juiste weg."'),
(4390, 43, 65, 'Maar de partijen waren het onderling oneens. Wee dus hen die onrecht plegen wegens de bestraffing op een pijnlijke dag.'),
(4391, 43, 66, 'Verwachten zij dan iets anders dan dat het uur onverwachts tot hen komt zonder dat zij het beseffen?'),
(4392, 43, 67, 'Vrienden zullen op die dag elkaar tot vijand zijn, alleen de godvrezenden niet.'),
(4393, 43, 68, '"Mijn dienaren! Jullie hebben niets te vrezen noch zullen jullie bedroefd zijn.'),
(4394, 43, 69, 'Jullie die in Onze tekenen geloofden en zich [aan God] hadden overgegeven,'),
(4395, 43, 70, 'gaat de tuin binnen, jullie en jullie echtgenotes, om verblijd te worden."'),
(4396, 43, 71, 'Bij hen worden schotels van goud en bekers rondgegeven. En daarin is wat de zielen begeren en wat aangenaam voor de ogen is. "En jullie zullen daarin altijd blijven.'),
(4397, 43, 72, 'Dat is de tuin die jullie beërfd hebben voor wat jullie hebben gedaan.'),
(4398, 43, 73, 'Jullie hebben daarin veel vruchten waarvan jullie kunnen eten."'),
(4399, 43, 74, 'De boosdoeners zullen altijd in de bestraffing van de hel blijven.'),
(4400, 43, 75, 'Zij zal voor hen niet getemperd worden en zij zullen in wanhoop terneergeslagen zijn.'),
(4401, 43, 76, 'En Wij hebben hun geen onrecht aangedaan, maar zij waren het die onrecht pleegden.'),
(4402, 43, 77, 'En zij roepen: "O Malik, laat jouw Heer een einde aan ons maken." Hij zegt: "Jullie blijven hier."'),
(4403, 43, 78, 'Wij hebben jullie de waarheid gebracht, maar de meesten van jullie verafschuwen de waarheid.'),
(4404, 43, 79, 'Of hebben zij een of ander plan gemaakt? Wij kunnen ook plannen maken.'),
(4405, 43, 80, 'Of rekenen zij erop dat Wij hun geheime en vertrouwelijke gesprekken niet horen? Zeker wel, en Onze gezanten schrijven het bij hen op.'),
(4406, 43, 81, 'Zeg: "Als de Erbarmer een kind had zou ik de eerste van de aanbidders zijn.'),
(4407, 43, 82, 'Geprezen zij de Heer van de hemelen en de aarde, de Heer van de troon, en verheven is Hij boven wat zij toeschrijven."'),
(4408, 43, 83, 'Laat hen maar kletsen en schertsen totdat zij hun dag tegenkomen die hun wordt aangezegd.'),
(4409, 43, 84, 'En Hij is het die in de hemel god is en die op de aarde god is; Hij is de wijze, de wetende.'),
(4410, 43, 85, 'Gezegend zij Hij die de heerschappij heeft over de hemelen en de aarde en wat er tussen beide is. En bij Hem is de kennis over het uur en tot Hem worden jullie teruggebracht.'),
(4411, 43, 86, 'Zij die jullie in plaats van Hem aanroepen beschikken niet over voorspraak, behalve zij die van de waarheid getuigen en die kennis hebben.'),
(4412, 43, 87, 'En als jij hun vraagt wie hen geschapen heeft zeggen zij: "God." Hoe kunnen zij dan zo zijn afgeleid?'),
(4413, 43, 88, 'En [bij Hem is de kennis ] erover dat hij zegt: "Mijn Heer, dezen hier zijn mensen die niet geloven."'),
(4414, 43, 89, 'Schenk maar geen aandacht aan hen en zeg: "Vrede". Zij zullen het weten!');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'H[aa?] M[iem].'),
(4416, 44, 2, 'Bij het duidelijke boek!'),
(4417, 44, 3, 'Wij hebben het in een gezegende nacht neergezonden -- Wij hebben steeds gewaarschuwd --'),
(4418, 44, 4, 'waarin iedere wijze beschikking afzonderlijk wordt beslist,'),
(4419, 44, 5, 'als een beschikking van Onze kant. Wij hebben steeds [gezanten] gezonden --'),
(4420, 44, 6, 'als een barmhartigheid van jouw Heer. Hij is de horende, de wetende,'),
(4421, 44, 7, 'de Heer van de hemelen en de aarde en wat er tussen beide is, als jullie ervan overtuigd zijn.'),
(4422, 44, 8, 'Er is geen god dan Hij. Hij geeft leven en laat sterven, jullie Heer en de Heer van jullie vaderen die er eertijds waren.'),
(4423, 44, 9, 'Toch schertsen zij in hun twijfel.'),
(4424, 44, 10, 'Wacht dan de dag maar af waarop de hemel met duidelijke rook zal komen'),
(4425, 44, 11, 'die de mensen zal bedekken; dat is een pijnlijke bestraffing.'),
(4426, 44, 12, '"Onze Heer, hef de bestraffing voor ons op, want wij zijn gelovigen."'),
(4427, 44, 13, 'Hoe zou vermaning hun tot voordeel hebben kunnen zijn, terwijl er toch een duidelijke gezant tot hen gekomen is?'),
(4428, 44, 14, 'Toen keerden zij zich van hem af en zeiden: "Iemand die elders onderwezen is en die bezeten is."'),
(4429, 44, 15, 'Wij zullen de bestraffing een korte tijd opheffen, maar jullie zullen terugvallen.'),
(4430, 44, 16, 'Op de dag dat Wij met groot geweld toeslaan zullen Wij zeker wraaknemen.'),
(4431, 44, 17, 'Wij hebben vóór hun tijd het volk van Fir\'aun aan verzoeking blootgesteld. Tot hen kwam een voortreffelijk gezant:'),
(4432, 44, 18, '"Draagt de dienaren van God aan mij over. Ik ben voor jullie een betrouwbaar gezant.'),
(4433, 44, 19, 'En weest niet hovaardig tegenover God, want ik ben met een duidelijke machtiging tot jullie gekomen.'),
(4434, 44, 20, 'En ik zoek bescherming bij mijn Heer en jullie Heer dat jullie mij niet zullen stenigen.'),
(4435, 44, 21, 'En als jullie mij niet geloven laat mij dan alleen."'),
(4436, 44, 22, 'Toen riep hij zijn Heer aan: "Dezen zijn misdadige mensen."'),
(4437, 44, 23, '"Vertrek \'s nachts met Mijn dienaren, want jullie zullen achtervolgd worden.'),
(4438, 44, 24, 'En laat de zee kalm achter; zij zijn een verdronken troepenmacht."'),
(4439, 44, 25, 'Hoeveel lieten zij niet achter: tuinen en bronnen,'),
(4440, 44, 26, 'landbouwgewassen en een voortreffelijke positie'),
(4441, 44, 27, 'en een aangenaam leven waarover zij blij waren.'),
(4442, 44, 28, 'Zo was het. En Wij lieten andere mensen het beërven.'),
(4443, 44, 29, 'De hemel schreide niet over hen, noch de aarde. Aan hen werd geen uitstel meer verleend.'),
(4444, 44, 30, 'Maar Wij redden de Israëlieten van de vernederende bestraffing'),
(4445, 44, 31, 'van Fir\'aun. Hij had de overhand en behoorde tot de onmatigen.'),
(4446, 44, 32, 'Maar op grond van kennis hadden Wij hen boven de wereldbewoners uitgekozen.'),
(4447, 44, 33, 'En Wij gaven hun tekenen waarin een duidelijke beproeving was.'),
(4448, 44, 34, 'Dezen hier zeggen:'),
(4449, 44, 35, '"Er is alleen maar onze eerste dood en wij worden niet opgewekt.'),
(4450, 44, 36, 'Brengt onze vaderen toch, als jullie gelijk hebben."'),
(4451, 44, 37, 'Zijn zij beter of het volk van Toebba\' en zij die er voor hun tijd waren? Hen hebben Wij vernietigd; zij waren boosdoeners.'),
(4452, 44, 38, 'Wij hebben de hemelen en de aarde en wat er tussen beide is niet als een spel geschapen.'),
(4453, 44, 39, 'Wij hebben beide slechts in waarheid geschapen, maar de meesten van hen weten het niet.'),
(4454, 44, 40, 'De dag van de schifting is de afgesproken tijd voor hen allen tezamen.'),
(4455, 44, 41, 'Op de dag dat een beschermheer een beschermeling niets baat, terwijl zij ook geen hulp zullen krijgen,'),
(4456, 44, 42, 'behalve zij met wie God erbarmen heeft; Hij is de machtige, de barmhartige.'),
(4457, 44, 43, 'De zakkoemboom'),
(4458, 44, 44, 'is voedsel voor de zondaar.'),
(4459, 44, 45, 'Als gesmolten metaal kookt het in de buiken,'),
(4460, 44, 46, 'zoals gloeiend water kookt.'),
(4461, 44, 47, '"Grijpt hem en sleurt hem midden in het hellevuur.'),
(4462, 44, 48, 'En giet dan als bestraffing gloeiend water op zijn hoofd.'),
(4463, 44, 49, 'Proef dan, jij bent toch de machtige, de voortreffelijke."'),
(4464, 44, 50, 'Dit is het waarover jullie twijfelden.'),
(4465, 44, 51, 'De godvrezenden zullen op een betrouwbare plaats zijn,'),
(4466, 44, 52, 'te midden van tuinen en bronnen.'),
(4467, 44, 53, 'Zij kleden zich met zijde en brokaat en zij zitten tegenover elkaar.'),
(4468, 44, 54, 'Zo is het! En Wij geven hun gezellinnen met sprekende grote ogen ten huwelijk.'),
(4469, 44, 55, 'Zij kunnen daarin veilig om allerlei vruchten vragen.'),
(4470, 44, 56, 'Zij zullen daar, behalve de eerste dood, de dood niet proeven en Hij beschermt hen tegen de bestraffing van het hellevuur.'),
(4471, 44, 57, 'Het is goedgunstigheid van jouw Heer. Dat is de geweldige triomf!'),
(4472, 44, 58, 'Wij hebben hem jouw taal gemakkelijk gemaakt; misschien laten zij zich vermanen.'),
(4473, 44, 59, 'Wacht dus af; zij wachten ook af.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'H[aa?] M[iem].'),
(4475, 45, 2, 'De neerzending van het boek is gebeurd door God, de machtige, de wijze.'),
(4476, 45, 3, 'In de hemelen en de aarde zijn zeker tekenen voor de gelovigen.'),
(4477, 45, 4, 'Ook in jullie schepping en de dieren die Hij heeft verspreid zijn tekenen voor mensen die vast overtuigd zijn.'),
(4478, 45, 5, 'En in het verschil van nacht en dag en in wat God als levensonderhoud uit de hemel heeft laten neerdalen en waarmee Hij de aarde weer laat herleven nadat zij dood was en in het besturen van de winden zijn tekenen voor mensen die verstandig zijn.'),
(4479, 45, 6, 'Dat zijn Gods tekenen. Wij lezen ze aan jou in waarheid voor. Aan welk bericht zullen zij dan na [dat van] God en Zijn tekenen nog geloven?'),
(4480, 45, 7, 'Wee elke zondige lasteraar,'),
(4481, 45, 8, 'die Gods tekenen die Wij aan hem voorlezen hoort en dan in zijn hoogmoed volhardt alsof hij het niet gehoord had. Verkondig hem dus een pijnlijke bestraffing.'),
(4482, 45, 9, 'En wanneer hij iets van Onze tekenen weet dan drijft hij de spot ermee. Zij zijn het voor wie er een vernederende bestraffing is.'),
(4483, 45, 10, 'Achter hen is de hel en wat zij zich verworven hebben baat hun niets, noch wat zij zich in plaats van God als beschermers genomen hebben; voor hen is er een geweldige bestraffing.'),
(4484, 45, 11, 'Dit is een leidraad en voor hen die geen geloof hechten aan de tekenen van hun Heer is er een bestraffing door een pijnlijke plaag.'),
(4485, 45, 12, 'God is het die de zee aan jullie dienstbaar heeft gemaakt, opdat de schepen op Zijn bevel varen en opdat jullie naar een gunst van Hem streven; misschien zullen jullie dank betuigen.'),
(4486, 45, 13, 'En Hij heeft wat er in de hemelen en wat er op de aarde is aan jullie dienstbaar gemaakt. Het komt alles van Hem. Daarin zijn tekenen voor mensen die nadenken.'),
(4487, 45, 14, 'Zeg aan hen die geloven dat zij hun die Gods dagen niet verwachten moeten vergeven, opdat Hij aan mensen vergeldt voor wat zij begaan hebben.'),
(4488, 45, 15, 'Als iemand deugdelijk handelt is het in zijn eigen voordeel, maar als iemand verkeerd doet dan is het in zijn nadeel. Voorts zullen jullie tot jullie Heer worden teruggebracht.'),
(4489, 45, 16, 'Wij hebben aan de Israëlieten het boek, de oordeelskracht en het profeetschap gegeven, Wij hebben met goede dingen in hun onderhoud voorzien en Wij hebben hen boven de wereldbewoners verkozen.'),
(4490, 45, 17, 'En Wij hebben hun duidelijke bewijzen van de ordening gegeven. Zij werden het er slechts uit onderlinge nijd over oneens nadat de kennis tot hen gekomen was. Jouw Heer zal op de opstandingsdag tussen hen oordelen over dat waarover zij het oneens waren.'),
(4491, 45, 18, 'Daarna hebben Wij voor jou een normatieve richting van de ordening bepaald. Volg die dus en volg niet de grillen van hen die geen kennis hebben.'),
(4492, 45, 19, 'Zij zullen jou tegen God helemaal niet baten. De onrechtplegers zijn elkaars beschermers, maar God is de beschermer van de godvrezenden.'),
(4493, 45, 20, 'Dit zijn inzichtelijke bewijzen voor de mensen en een leidraad en barmhartigheid voor mensen die vast overtuigd zijn.'),
(4494, 45, 21, 'Of rekenen zij die misdaden begaan erop dat Wij hen in hun leven en hun sterven gelijk zullen behandelen als hen die geloven en de deugdelijke daden doen? Slecht is het wat zij oordelen.'),
(4495, 45, 22, 'God heeft de hemelen en de aarde in waarheid geschapen en aan iedereen zal vergolden worden wat hij begaan heeft en hun zal geen onrecht worden aangedaan.'),
(4496, 45, 23, 'Heb je gezien naar hem die zijn grillen tot zijn god maakt? God heeft hem bewust tot dwaling gebracht en zijn gehoor en zijn hart verzegeld en over zijn ogen is een sluier. Wie zou hem na God nog de weg kunnen wijzen? Zullen jullie je niet laten vermanen?'),
(4497, 45, 24, 'Zij zeggen: "Er is alleen maar ons tegenwoordige leven; wij sterven en wij leven en alleen door de tijd worden wij omgebracht." Zij hebben daarvan geen kennis. Zij hebben alleen maar vermoedens.'),
(4498, 45, 25, 'En wanneer Onze duidelijke tekenen aan hen worden voorgelezen, dan is hun argument slechts dat zij zeggen: "Brengt onze vaderen, als jullie gelijk hebben."'),
(4499, 45, 26, 'Zeg: "God geeft jullie leven. Dan laat Hij jullie sterven en dan brengt Hij jullie bijeen voor de opstandingsdag waaraan geen twijfel is, maar de meeste mensen weten het niet."'),
(4500, 45, 27, 'God heeft de heerschappij over de hemelen en de aarde en op de dag dat het uur aanbreekt, op die dag zullen zij die zeggen dat het niet waar is verloren zijn.'),
(4501, 45, 28, 'En dan zie jij iedere gemeenschap neergeknield. Iedere gemeenschap wordt tot haar boek geroepen: "Vandaag wordt aan jullie vergolden wat jullie gedaan hebben.'),
(4502, 45, 29, 'Dit is Ons boek, het spreekt de waarheid over jullie uit. Wij hebben wat jullie deden genoteerd."'),
(4503, 45, 30, 'Wat hen betreft die geloven en de deugdelijke daden doen, hen zal hun Heer in Zijn barmhartigheid laten binnengaan. Dat is de duidelijke triomf!'),
(4504, 45, 31, 'Maar wat hen betreft die ongelovig zijn: "Waren Mijn tekenen jullie niet voorgelezen? Maar jullie waren hoogmoedige en misdadige mensen."'),
(4505, 45, 32, 'En wanneer gezegd werd: "Gods aanzegging is waar en het uur, daaraan is geen twijfel", zeiden jullie: "Wij weten niet wat het uur is. Wij hebben alleen maar een vermoeden, maar wij zijn niet overtuigd."'),
(4506, 45, 33, 'De slechte daden die zij begaan hebben worden dan voor hen zichtbaar en zij worden door datgene waarmee zij de spot dreven ingesloten.'),
(4507, 45, 34, 'En er wordt gezegd: "Vandaag vergeten Wij jullie zoals jullie deze dag van jullie vergeten hebben, jullie verblijfplaats is het vuur en jullie hebben geen helpers.'),
(4508, 45, 35, 'Dat is zo omdat jullie met de tekenen van God de spot gedreven hebben en omdat het tegenwoordige leven jullie begoocheld heeft." Vandaag zullen zij er dus niet uitgehaald worden, noch zullen zij een kans krijgen om het goed te maken.'),
(4509, 45, 36, 'Maar lof zij God de Heer van de hemelen, de Heer van de aarde en de Heer van de wereldbewoners.'),
(4510, 45, 37, 'En Hij heeft de hoogste macht in de hemelen en op de aarde en Hij is de machtige, de wijze.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'H[aa?] M[iem].'),
(4512, 46, 2, 'De neerzending van het boek is gebeurd door God, de machtige, de wijze.'),
(4513, 46, 3, 'Wij hebben de hemelen en de aarde en wat er tussen beide is slechts in waarheid en voor een vastgestelde termijn geschapen, maar zij die ongelovig zijn wenden zich af van dat waarmee zij gewaarschuwd worden.'),
(4514, 46, 4, 'Zeg: "Hoe zien jullie wat jullie in plaats van God aanroepen? Laat mij dan zien wat zij van de aarde geschapen hebben. Hebben zij een aandeel in de hemelen? Brengt mij een boek dat er voor dit [boek] was of een spoor van kennis, als jullie gelijk hebben."'),
(4515, 46, 5, 'En wie dwaalt er meer dan hij die, in plaats van God, iemand aanroept die hem tot aan de opstandingsdag geen gehoor geeft? Zij schenken aan hun gebed geen aandacht'),
(4516, 46, 6, 'en wanneer de mensen verzameld worden zijn zij hun tot vijand en zij betonen zich ondankbaar over de dienst aan hen.'),
(4517, 46, 7, 'En wanneer Onze tekenen aan hen als duidelijke bewijzen worden voorgelezen zeggen zij die geen geloof hechten aan de waarheid, wanneer die tot hen komt: "Dit is duidelijk toverij."'),
(4518, 46, 8, 'Of zij zeggen: "Hij heeft het verzonnen." Zeg: "Als ik het verzonnen heb, dan kunnen jullie voor mij tegen God niets uitrichten. Hij weet het best waarmee jullie aan de slag gaan. Hij is als getuige tussen mij en jullie goed genoeg. Hij is de vergevende, de barmhartige."'),
(4519, 46, 9, 'Zeg: "Ik vertegenwoordig niet iets ongehoords onder de gezanten. Ik weet ook niet wat er met mij en met jullie zal gebeuren. Ik volg slechts wat aan mij geopenbaard wordt en ik ben alleen maar een duidelijke waarschuwer."'),
(4520, 46, 10, 'Zeg: "Hoe zien jullie het? Als het toch van God komt, terwijl jullie er geen geloof aan hechten, hoewel iemand van de Israëlieten getuigenis aflegt over dat wat eraan gelijk is en dus gelooft? Maar jullie zijn hoogmoedig. God wijst de mensen die onrecht plegen de goede richting niet."'),
(4521, 46, 11, 'Zij die ongelovig zijn zeggen tegen hen die geloven: "Als het iets goeds was hadden zij het niet eerder dan wij gekregen." Omdat zij zich er niet de goede richting door laten wijzen zullen zij dus zeggen: "Oude laster."'),
(4522, 46, 12, 'Eraan voorafgegaan is het boek van Moesa als voorbeeld en barmhartigheid. En dit is een boek dat in de Arabische taal een bevestiging geeft, om hen die onrecht plegen te waarschuwen en als goed nieuws voor hen die goed doen.'),
(4523, 46, 13, 'Zij die zeggen: "Onze Heer is God" en die dan correct handelen hebben niets te vrezen noch zullen zij bedroefd zijn.'),
(4524, 46, 14, 'Zij zijn het die in de tuin thuishoren, waarin zij altijd zullen blijven, als een beloning voor wat zij hebben gedaan.'),
(4525, 46, 15, 'En Wij hebben de mens opgedragen zijn ouders goed te behandelen -- zijn moeder heeft hem immers moeizaam gedragen en moeizaam gebaard. De zwangerschap en [de tijd erna] totdat hij gespeend wordt is dertig maanden. En wanneer hij dan volgroeid is en veertig jaar wordt zegt hij: "Mijn Heer, spoor mij aan dat ik voor Uw genade, die U mij en mijn ouders geschonken hebt, dank betuig en dat ik iets deugdelijks doe wat U bevalt. En maak voor mij dat mijn nageslacht deugdzaam is. Ik wend mij berouwvol tot U en ik behoor bij hen die zich [aan God] overgeven."'),
(4526, 46, 16, 'Zij zijn het van wie Wij het beste dat zij deden aannemen en van wie Wij de slechte daden overslaan; zij zijn bij hen die in de tuin thuishoren. Dat is de betrouwbare toezegging die aan hen gedaan is.'),
(4527, 46, 17, 'Maar hij die tegen zijn ouders zegt: "Foei jullie beiden, willen jullie mij aanzeggen dat ik tevoorschijn gebracht zal worden terwijl er voor mijn tijd al generaties zijn heengegaan?" En zij vragen God om hulp [en zeggen]: "Wee jou, geloof toch, de aanzegging van God is waar!" Maar hij zegt: "Dit zijn slechts fabels van hen die er eertijds waren."'),
(4528, 46, 18, 'Zij zijn het tegen wie het woord bewaarheid wordt, te midden van gemeenschappen van de djinn en de mensen die al voor hun tijd voorbijgegaan waren; zij waren verliezers.'),
(4529, 46, 19, 'Voor een ieder zijn er rangen voor wat zij gedaan hebben en opdat Hij hun hun daden volledig zal vergoeden; hun zal geen onrecht worden gedaan.'),
(4530, 46, 20, 'En op de dag dat zij die ongelovig zijn naar het vuur worden voorgeleid [wordt gezegd]: "Jullie hebben jullie goede dingen in jullie aardse leven verbruikt en genoten. Vandaag krijgen jullie de bestraffing van de schande als vergelding, omdat jullie onterecht hoogmoedig op de aarde waren en voor het schandelijke dat jullie deden."'),
(4531, 46, 21, 'En vermeld de broeder van de \'Aad toen hij zijn volk in de zandduinen waarschuwde -- waarschuwers zijn er voor hem en na hem geweest --: "Jullie moeten alleen God dienen. Ik vrees voor jullie de bestraffing van een geweldige dag."'),
(4532, 46, 22, 'Zij zeiden: "Ben jij tot ons gekomen om ons van onze goden af te brengen? Breng maar wat jij ons aanzegt, als jij gelijk hebt."'),
(4533, 46, 23, 'Hij zei: "De kennis [daarover] is bij God. Ik verkondig jullie dat waarmee ik gezonden ben, maar ik zie dat jullie mensen zijn die niets weten."'),
(4534, 46, 24, 'Toen zij het als een dikke bewolking zagen opkomen die hun valleien naderde zeiden zij: "Dat is een opkomende bewolking die ons regen brengt." "Welnee, het is wat jullie wilden verhaasten, een wind waarin een pijnlijke bestraffing is,'),
(4535, 46, 25, 'die alles op bevel van zijn Heer vernietigt." \'s Morgens was het zo dat slechts hun huizen gezien werden. Zo vergelden Wij aan de misdadige mensen.'),
(4536, 46, 26, 'Wij hadden hun zoveel macht gegeven als Wij aan jullie niet hebben gegeven. En Wij hadden hun gehoor, gezichtsvermogen en harten gegeven, maar hun gehoor, hun gezichtsvermogen en hun harten baatten hun niets toen zij Gods tekenen ontkenden en zij werden ingesloten door dat waarmee zij de spot dreven.'),
(4537, 46, 27, 'En Wij hebben de steden rondom jullie ook vernietigd. En Wij hebben de tekenen afgewisseld; misschien zullen zij terugkeren.'),
(4538, 46, 28, 'Als zij die zij zich als goden in plaats van God genomen hadden om nader tot Hem te komen hen maar geholpen hadden. Maar nee, hen waren zij kwijt; dat was hun laster en wat zij verzonnen hadden.'),
(4539, 46, 29, 'En toen Wij een groep djinn naar jou toe brachten om naar de Koran te luisteren. En toen zij gekomen waren zeiden zij: "Let in stilte op." En toen het beëindigd was, keerden zij naar hun volk terug om te waarschuwen.'),
(4540, 46, 30, 'Zij zeiden: "Volk van ons! Wij hebben een boek gehoord dat na Moesa is neergezonden om wat er voor was te bevestigen. Het leidt naar de waarheid en naar een juiste weg.'),
(4541, 46, 31, 'Volk van ons! Geeft gehoor aan hem die tot God oproept en gelooft in Hem, dan zal Hij jullie vergeving schenken voor zonden van jullie en jullie beschermen voor een pijnlijke bestraffing."'),
(4542, 46, 32, 'En als iemand geen gehoor geeft aan hem die tot God oproept, dan kan hij er op de aarde toch niets tegen doen en buiten God heeft hij geen beschermers. Zij zijn het die in duidelijke dwaling verkeren.'),
(4543, 46, 33, 'Hebben zij dan niet gezien dat God, die de hemelen en de aarde geschapen heeft zonder dat de schepping ervan Hem veel inspanning kostte, de macht heeft om de doden levend te maken? Ja zeker, Hij is almachtig.'),
(4544, 46, 34, 'En op de dag dat zij die ongelovig zijn naar het vuur worden voorgeleid: "Is dit niet de waarheid?" Zij zeggen: "Ja zeker, bij onze Heer!" Hij zegt: "Proeft dan de bestraffing ervoor dat jullie ongelovig waren."'),
(4545, 46, 35, 'Volhard dus geduldig zoals die profeten die vastbesloten waren geduldig volhardden en probeer niet het voor hen te verhaasten. Het zal voor hen op de dag dat zij zien wat hun toegezegd was zijn alsof het voor hen niet langer dan een uur van de dag heeft geduurd. [Dit is] een verkondiging. Zullen dan anderen dan de verdorven mensen vernietigd worden?');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'Van hen die ongelovig zijn en de weg van God versperren laat Hij de daden teloorgaan.'),
(4547, 47, 2, 'Maar van hen die geloven, de deugdelijke daden doen en geloven in wat tot Mohammed is neergezonden -- dat is de waarheid die van hun Heer komt -- zal Hij de slechte daden verzoenen en hun toestand in orde brengen.'),
(4548, 47, 3, 'Dat komt omdat zij die ongelovig zijn het onware volgen en omdat zij die gelovig zijn de waarheid volgen die van hun Heer afkomstig is. Zo geeft God zijn voorbeelden voor de mensen.'),
(4549, 47, 4, 'En wanneer jullie hen die ongelovig zijn [in de strijd] ontmoeten, slaat hen dan dood, maar wanneer jullie dan de overhand over hen verkregen hebben boeit hen dan stevig vast, hetzij om hen later als gunst vrij te laten hetzij om hen los te laten kopen, wanneer de lasten van de oorlog zijn afgelegd. Zo is het. En als God zou willen zou Hij zelf zich op hen wreken, maar Hij wil jullie met elkaar op de proef stellen. En zij die op Gods weg gedood zijn, hun daden zullen niet teloorgaan.'),
(4550, 47, 5, 'Hij zal het voor hen goed laten aflopen en hun toestand in orde brengen.'),
(4551, 47, 6, 'En Hij zal hen de tuin laten binnengaan die Hij aan hen bekend heeft gemaakt.'),
(4552, 47, 7, 'Jullie die geloven! Als jullie God helpen dan zal Hij jullie helpen en jullie voeten stevig maken.'),
(4553, 47, 8, 'Maar zij die ongelovig zijn, neer met hen! Hij laat hun daden teloorgaan.'),
(4554, 47, 9, 'Dat komt omdat zij verafschuwen wat God neergezonden heeft. Dus maakt Hij hun daden vruchteloos. *'),
(4555, 47, 10, 'Hebben zij dan niet op de aarde rondgereisd zodat zij konden zien hoe het einde was van hen die er voor hen waren? God roeide hen uit. En voor de ongelovigen zijn er overeenkomstige dingen.'),
(4556, 47, 11, 'Dat komt omdat God de beschermheer is van hen die geloven en omdat de ongelovigen geen beschermheer hebben.'),
(4557, 47, 12, 'God laat hen die geloven en de deugdelijke daden doen binnengaan in tuinen, waar de rivieren onderdoor stromen. Maar zij die ongelovig zijn genieten en eten zoals het vee eet en het vuur is een verblijfplaats voor hen.'),
(4558, 47, 13, 'En hoeveel steden die sterker waren dan jouw stad die jou verdreven heeft hebben Wij niet vernietigd; en zij hadden geen helper.'),
(4559, 47, 14, 'Is dan iemand die op een duidelijk bewijs van zijn Heer steunt gelijk aan iemand voor wie de slechtheid van zijn handelen aantrekkelijk gemaakt is? Zij volgen namelijk hun grillen.'),
(4560, 47, 15, 'De tuin die de godvrezenden is toegezegd ziet er zo uit: Er zijn rivieren van water dat niet brak is, rivieren van melk waarvan de smaak niet verandert, rivieren van wijn die aangenaam is voor de drinkers en rivieren van gezuiverde honing. En zij hebben daar allerlei vruchten en vergeving van hun Heer. [Zijn zij] als zij die altijd in het vuur blijven en gloeiend water te drinken krijgen dat hun ingewanden stuksnijdt?'),
(4561, 47, 16, 'Onder hen zijn er die naar jou luisteren, maar wanneer zij dan bij jou zijn weggegaan zeggen zij tot hen aan wie de kennis gegeven is: "Wat heeft hij zo-even gezegd?" Zij zijn het van wie God de harten verzegeld heeft en zij volgen hun grillen.'),
(4562, 47, 17, 'Zij die zich op het goede pad hebben laten brengen brengt God nog beter op het goede pad en Hij geeft hun hun godvrezendheid.'),
(4563, 47, 18, 'Verwachten zij dan iets anders dan dat het uur onverwachts tot hen komt? De voortekenen ervan zijn al gekomen. Hoe zou vermaning hun tot voordeel kunnen zijn wanneer hij komt?'),
(4564, 47, 19, 'Weet dan dat er geen god is dan God en vraag om vergeving voor jouw zonden en voor de gelovige mannen en vrouwen. En God kent jullie rondtrekken en jullie verblijfplaats.'),
(4565, 47, 20, 'En zij die geloven zeggen: "Was er maar een soera neergezonden." En als er dan een eenduidige soera wordt neergezonden waarin er van strijden sprake is, dan zie jij hen die een ziekte in hun harten hebben naar jou kijken met een blik als van iemand die voor de dood in onmacht valt. Zij kunnen het krijgen!'),
(4566, 47, 21, 'Gehoorzaamheid en redelijke woorden! Maar wanneer de zaak vaststaat dan zou het beter voor hen zijn als zij tegenover God oprecht waren geweest.'),
(4567, 47, 22, 'Of is het, als jullie je afwenden, misschien zo dat jullie verderf zaaien op de aarde en jullie verwantschapsbanden doorsnijden?'),
(4568, 47, 23, 'Zij zijn het die God vervloekt. Dus heeft Hij hen doof gemaakt en hun ogen verblind.'),
(4569, 47, 24, 'Overpeinzen zij de Koran dan niet? Ofwel, zijn er harten die op slot zijn?'),
(4570, 47, 25, 'Hun die hun ruggen toegekeerd hebben, nadat de leidraad hun duidelijk was geworden, heeft de satan iets wijsgemaakt en hij heeft hun uitstel beloofd.'),
(4571, 47, 26, 'Dat is zo, want zij zeggen tot hen die verafschuwen wat God heeft neergezonden: "Wij zullen jullie gedeeltelijk gehoorzamen." Maar God weet wat zij in het verborgen doen.'),
(4572, 47, 27, 'En hoe zal het dan zijn wanneer de engelen hen wegnemen terwijl ze op hun gezichten en ruggen slaan?'),
(4573, 47, 28, 'Dat gebeurt, want zij volgen wat God vergramd maakt en verafschuwen Zijn welbehagen. Dus maakt Hij hun daden vruchteloos.'),
(4574, 47, 29, 'Of rekenen zij die in hun harten een ziekte hebben erop dat God hun rancune niet aan het licht zal brengen?'),
(4575, 47, 30, 'En als Wij zouden willen zouden Wij hen aan jou tonen, zodat jij hun kenmerken zou herkennen. Maar jij zult hen zeker aan hun dubbelzinnige manier van spreken herkennen. En God kent hun daden.'),
(4576, 47, 31, 'Wij zullen jullie op de proef stellen zodat Wij weten wie van jullie zich inzetten en geduldig volharden en om de berichten over jullie te toetsen.'),
(4577, 47, 32, 'Zij die ongelovig zijn, Gods weg versperren en de gezant tegenwerken nadat de leidraad hun duidelijk geworden is kunnen God geen enkele schade toebrengen, maar Hij zal hun daden vruchteloos maken.'),
(4578, 47, 33, 'Jullie die geloven! Gehoorzaamt God en gehoorzaamt de gezant en maakt jullie daden niet waardeloos.'),
(4579, 47, 34, 'Zij die ongelovig zijn en Gods weg versperren en dan als ongelovigen sterven, aan hen zal God geen vergeving schenken.'),
(4580, 47, 35, 'Versaagt dus niet en roept niet op tot vrede wanneer jullie toch de overhand hebben. God is namelijk met jullie en Hij zal jullie de vergoeding voor jullie daden niet onthouden.'),
(4581, 47, 36, 'Het tegenwoordige leven is slechts spel en tijdverdrijf, maar als jullie geloven en godvrezend zijn zal Hij jullie je loon geven, terwijl Hij niet om jullie bezittingen vraagt.'),
(4582, 47, 37, 'Als Hij jullie erom zou vragen en erop zou aandringen, dan zouden jullie gierig zijn en zou Hij jullie rancune aan het licht brengen.'),
(4583, 47, 38, 'Dat zijn jullie dus die opgeroepen worden om bijdragen te geven op Gods weg, maar onder jullie zijn er die gierig zijn. Maar wie gierig is, die is slechts gierig ten opzichte van zichzelf. God is de behoefteloze en jullie zijn de armen. Als jullie je afkeren zal Hij andere mensen in jullie plaats zetten. Dan zullen zij niet zijn zoals jullie.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Wij hebben jou een duidelijk succes laten behalen,'),
(4585, 48, 2, 'opdat God jou je vroegere en je latere zonden vergeeft, Zijn genade aan jou volledig bewijst en jou op een juiste weg leidt'),
(4586, 48, 3, 'en opdat God jou op een geweldige manier hulp verleent.'),
(4587, 48, 4, 'Hij is het die de kalmte in de harten van de gelovigen heeft laten neerdalen opdat zij bij hun geloof nog in geloof toenemen -- van God zijn de troepen van de hemelen en de aarde en God is wetend en wijs --'),
(4588, 48, 5, 'om de gelovige mannen en vrouwen tuinen te laten binnengaan waar de rivieren onderdoor stromen terwijl zij daar altijd in blijven en om hun hun slechte daden kwijt te schelden -- dat is een geweldige triomf bij God --'),
(4589, 48, 6, 'en om de huichelaars en de huichelaarsters en de veelgodendienaars en -dienaressen die over God slechte gedachten hebben te straffen. Voor hen is de wending ten slechte, God is op hen vertoornd, Hij vervloekt hen en heeft voor hen de hel klaargemaakt; dat is een slechte bestemming.'),
(4590, 48, 7, 'Van God zijn de troepen van de hemelen en de aarde en God is wetend en wijs.'),
(4591, 48, 8, 'Wij hebben jou gezonden als getuige, als verkondiger van goed nieuws en als waarschuwer,'),
(4592, 48, 9, 'opdat jullie geloven in God en Zijn gezant, Hem bijstaan, Hem vereren en Hem \'s ochtends en \'s avonds prijzen.'),
(4593, 48, 10, 'Zij die jou trouw zweren, zweren God trouw. Gods hand is op hun handen gelegd. En wie [die eed] breekt, die breekt hem tot zijn eigen nadeel. Maar wie de verbintenis die hij met God gesloten heeft nakomt, die zal Hij een geweldig loon geven.'),
(4594, 48, 11, 'Zij van de bedoeïenen die achtergelaten zijn zullen tot jou zeggen: "Onze bezittingen en onze families hielden ons bezig. Vraag dus vergeving voor ons." Met hun tongen zeggen zij wat niet in hun harten is. Zeg: "Wie zou voor jullie tegen God ook maar iets kunnen uitrichten als Hij jullie schade wenst te berokkenen of als Hij jullie voordeel wenst te geven?" Integendeel, God is over wat jullie doen welingelicht.'),
(4595, 48, 12, 'Ja zeker, jullie dachten dat de gezant en de gelovigen nooit meer naar hun families zouden terugkeren. En in jullie harten leek dat aantrekkelijk. Jullie hadden slechte gedachten en jullie waren verloren mensen.'),
(4596, 48, 13, 'En wie niet in God en Zijn gezant gelooft? Wij hebben voor de ongelovigen een vuurgloed klaargemaakt.'),
(4597, 48, 14, 'En God heeft de heerschappij over de hemelen en de aarde. Hij vergeeft aan wie Hij wil en Hij bestraft wie Hij wil. En God is vergevend en barmhartig.'),
(4598, 48, 15, 'Wanneer jullie uittrekken om buit te behalen zullen de achtergelatenen zeggen: "Laat ons jullie volgen" in hun wens Gods woorden te veranderen. Zeg: "Jullie zullen ons niet volgen. Zo heeft God het van tevoren gezegd." En zij zullen zeggen: "Welnee, jullie misgunnen het ons." Integendeel, zij begrijpen slechts weinig.'),
(4599, 48, 16, 'Zeg tot de bedoeïenen die achtergelaten zijn: "Jullie zullen worden opgeroepen [tot de strijd] tegen mensen van geweldige sterkte. Jullie zullen tegen hen strijden of zij geven zich over [aan God]. En als jullie gehoorzamen zal God jullie een goed loon geven, maar als jullie je afkeren, zoals jullie je vroeger hebben afgekeerd, dan zal Hij jullie met een pijnlijke bestraffing straffen."'),
(4600, 48, 17, 'De blinde treft geen blaam, de kreupele treft geen blaam en de zieke treft geen blaam en wie God en Zijn gezant gehoorzaamt die zal Hij tuinen laten binnengaan waar de rivieren onderdoor stromen, maar wie zich afkeert, die zal Hij met een pijnlijke bestraffing straffen.'),
(4601, 48, 18, 'God was met de gelovigen ingenomen toen zij jou onder de boom trouw zwoeren en Hij weet wat er in hun harten is en Hij liet dus de kalmte op hen neerdalen en beloonde hen met een spoedig succes'),
(4602, 48, 19, 'en veel buit die zij zullen behalen. En God is machtig en wijs.'),
(4603, 48, 20, 'God heeft jullie veel buit toegezegd die jullie zullen behalen. Hij heeft dus een deel voor jullie verhaast en de handen van de mensen van jullie afgehouden. En wel opdat het een teken voor de gelovigen zou zijn en het hen op een juiste weg zou leiden.'),
(4604, 48, 21, 'En ook een ander deel, dat jullie nog niet hebben kunnen behalen, is door God reeds omsingeld; God is almachtig.'),
(4605, 48, 22, 'En als zij die ongelovig zijn tegen jullie gestreden hadden, dan zouden zij jullie de rug hebben toegekeerd. Dan zouden zij geen beschermer en geen helper gevonden hebben,'),
(4606, 48, 23, 'volgens Gods gebruikelijke behandeling van hen die voor jullie tijd zijn heengegaan en jij zult in Gods gebruikelijke behandeling geen wijziging vinden.'),
(4607, 48, 24, 'Hij is het namelijk die in het dal van Mekka hun handen van jullie en jullie handen van hen afgehouden heeft, nadat Hij jullie over hen had laten zegevieren. God doorziet wel wat jullie doen.'),
(4608, 48, 25, 'Zij zijn het die ongelovig zijn en die jullie de weg naar de heilige moskee versperren, terwijl de offergave verhinderd wordt zijn bestemming [om geslacht te worden] te bereiken. En als jullie gelovige mannen en vrouwen die er waren, maar die jullie niet kenden, niet onder de voet gelopen zouden hebben en jullie vanwege hen dus door iets afschuwelijks getroffen zouden worden? Het was opdat God wie Hij wil in Zijn barmhartigheid zou laten binnengaan. Als zij zich onderscheiden hadden, dan zouden Wij hen uit hun midden die ongelovig zijn met een pijnlijke bestraffing gestraft hebben.'),
(4609, 48, 26, 'Toen zij die ongelovig zijn de minachting -- de minachting van de tijd van onwetendheid -- in hun harten lieten ontvlammen en God Zijn kalmte op Zijn gezant en op de gelovigen liet neerdalen en maakte dat zij zich aan het woord van de godvrezendheid hielden; zij hadden daar het meeste recht op en waren het waardig. En God is alwetend.'),
(4610, 48, 27, 'God heeft voor Zijn gezant het visioen in werkelijkheid waargemaakt. Jullie zullen als God het wil de heilige moskee met geschoren hoofden en bijgeknipt veilig binnengaan zonder bang te zijn. Hij weet immers wat jullie niet weten en Hij verleende daarnaast een spoedig succes.'),
(4611, 48, 28, 'Hij is het die Zijn gezant met de leidraad en de ware godsdienst gezonden heeft om hem te laten zegevieren over de gehele godsdienst. En God is goed genoeg als getuige.'),
(4612, 48, 29, 'Mohammed is de gezant van God. En zij die met hem zijn zijn streng tegen de ongelovigen, maar onderling barmhartig. Jij ziet hen buigen en zich eerbiedig neerbuigen in hun streven naar goedgunstigheid van God en welgevallen. Hun kenteken is op hun gezicht als gevolg van het zich eerbiedig neerbuigen. Zo worden zij gekenschetst in de Taura en in de Indjiel worden zij gekenschetst als zaad dat zijn loten laat ontspruiten en dan sterker laat worden. Dan worden zij dikker en staan stevig op hun stengels zodat zij de zaaiers bevallen. Zo wil Hij door hen de ongelovigen woedend maken. God heeft degenen onder hen die geloven en de deugdelijke daden doen vergeving toegezegd en een geweldig loon.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'Jullie die geloven! Loopt niet vooruit op God en Zijn gezant en vreest God. God is horend en wetend.'),
(4614, 49, 2, 'Jullie die geloven! Verheft jullie stemmen niet boven de stem van de profeet uit en spreekt tot hem niet met zo?n luide stem als jullie onderling doen, opdat jullie daden niet vruchteloos worden zonder dat jullie het beseffen.'),
(4615, 49, 3, 'Zij die met zachte stem bij de gezant van God spreken die zijn het van wie God de harten op godvrezendheid onderzocht heeft. Voor hen is er vergeving en een geweldig loon.'),
(4616, 49, 4, 'Zij die van buiten naar jou roepen als jij in de binnenkamers bent, van hen zijn de meesten niet verstandig.'),
(4617, 49, 5, 'En als zij geduldig zouden zijn totdat jij tot hen naar buiten komt zou dat beter voor hen zijn. En God is vergevend en barmhartig.'),
(4618, 49, 6, 'Jullie die geloven! Als een verdorvene met een mededeling tot jullie komt zorgt dan dat jullie duidelijke inlichtingen inwinnen, opdat jullie niet in onwetendheid mensen treffen en wroeging krijgen over wat jullie gedaan hebben.'),
(4619, 49, 7, 'En weet dat de gezant van God in jullie midden is. Als hij jullie in veel van het beleid zou gehoorzamen dan zouden jullie het moeilijk krijgen. Maar God heeft het geloof bij jullie geliefd gemaakt en het in jullie harten aantrekkelijk gemaakt. En Hij heeft jullie het ongeloof, de schandelijkheid en de ongehoorzaamheid laten verafschuwen. Zij zijn het die zich op de goede weg bevinden,'),
(4620, 49, 8, 'als een gunst van God en genade. God is wetend en wijs.'),
(4621, 49, 9, 'En als twee groepen van de gelovigen met elkaar strijden, sticht dan vrede tussen hen. En als een van beide zich onrechtmatig tegenover de andere gedraagt strijdt dan tegen die groep die zich onrechtmatig gedraagt, totdat zij zich weer aan Gods bevel houdt. En als zij zich er dan weer aan houdt sticht dan op een billijke manier vrede tussen beide en handelt daarbij rechtvaardig. God bemint hen die rechtvaardig handelen.'),
(4622, 49, 10, 'De gelovigen zijn immers broeders. Sticht dus vrede tussen jullie beide broeders en vreest God; misschien zal aan jullie barmhartigheid bewezen worden.'),
(4623, 49, 11, 'Jullie die geloven! Mensen moeten elkaar niet belachelijk maken. Misschien zijn zij juist beter dan zij! En ook vrouwen moeten dat niet doen. Misschien zijn zij juist beter dan zij! En maakt geen aanmerkingen op elkaar en geeft elkaar geen scheldnamen. Schandelijkheid, dat is pas een slechte naam, nadat het geloof is aangenomen. En wie geen berouw tonen, dat zijn dan de onrechtplegers.'),
(4624, 49, 12, 'Jullie die geloven! Vermijdt vele vermoedens -- sommige vermoedens zijn zonde -- en spioneert niet en roddelt niet over elkaar. Zou een van jullie graag het vlees van zijn dode broeder eten? Dat zouden jullie toch verafschuwen. En vreest God; God is een genadegever en barmhartig.'),
(4625, 49, 13, 'O mensen, Wij hebben jullie uit een man en een vrouw geschapen en Wij hebben jullie tot volkeren en stammen gemaakt opdat jullie elkaar zouden kennen. De voortreffelijkste van jullie is bij God de godvrezendste. God is wetend en welingelicht. *'),
(4626, 49, 14, 'De bedoeïenen zeggen: "Wij geloven." Zeg: "Jullie geloven niet, maar zeg: \'Wij hebben ons [aan God] overgegeven.? Het geloof is nog niet in jullie harten binnengekomen, maar als jullie God en Zijn gezant gehoorzamen, dan zal Hij jullie voor je daden niets tekort laten komen. God is wetend en wijs."'),
(4627, 49, 15, 'De gelovigen, dat zijn zij die in God en Zijn gezant geloven en dan niet twijfelen en die zich met hun bezittingen en hun eigen persoon op Gods weg inspannen; zij zijn het die oprecht zijn.'),
(4628, 49, 16, 'Zeg: "Willen jullie dan God over jullie godsdienst beleren, terwijl God weet wat er in de hemelen en wat er op de aarde is?" God is alwetend.'),
(4629, 49, 17, 'Zij beschouwen het als een gunst aan jou dat zij zich [aan God] hebben overgegeven. Zeg: "Beschouwt jullie overgave niet als een gunst aan mij. Integendeel, God heeft jullie een gunst bewezen dat Hij jullie tot het geloof geleid heeft, als jullie oprecht zijn."'),
(4630, 49, 18, 'God kent het verborgene van de hemelen en de aarde en God doorziet wel wat jullie doen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'K[aaf]. Bij de glorierijke Koran!'),
(4632, 50, 2, 'Ja zeker, zij verbazen zich dat er tot hen een waarschuwer uit hun midden gekomen is en dus zeggen de ongelovigen: "Dit is wel iets wonderlijks.'),
(4633, 50, 3, 'Zullen wij dan als wij gestorven zijn en stof geworden zijn?? Dat is toch een vergezochte terugkeer!)'),
(4634, 50, 4, 'Wij weten wel wat de aarde van hen verteert; bij Ons is een boek dat [alles] bewaart.'),
(4635, 50, 5, 'Maar nee, zij hebben de waarheid geloochend toen die tot hen kwam. Zij verkeren dus in een verwarde toestand.'),
(4636, 50, 6, 'Kijken zij dan niet naar de hemel boven hen, hoe Wij die gebouwd en opgesierd hebben en dat er geen barsten in zijn?'),
(4637, 50, 7, 'En de aarde hebben Wij uitgebreid en erop stevige bergen aangebracht. En Wij hebben er allerlei kostelijke soorten op laten groeien,'),
(4638, 50, 8, 'als een verduidelijking en een vermaning voor iedere schuldbewuste dienaar.'),
(4639, 50, 9, 'En Wij hebben uit de hemel gezegend water laten neerdalen en daarmee tuinen en graan voor de oogst laten groeien'),
(4640, 50, 10, 'en palmen die hoog oprijzen en boven elkaar geplaatste bloeiwijzen hebben,'),
(4641, 50, 11, 'als levensonderhoud voor de dienaren. En Wij brengen daarmee een dode streek tot leven. Zo is ook het tevoorschijn komen [van de doden].'),
(4642, 50, 12, 'Voor hun tijd heeft het volk van Noeh van leugens beticht en ook de mensen van de bron en de Thamoed'),
(4643, 50, 13, 'en de \'Aad en Fir\'aun en de broeders van Loet'),
(4644, 50, 14, 'en de mensen van het kreupelbos en het volk van Toebba\', allen betichtten zij de gezanten van leugens, dus werd Mijn aanzegging bewaarheid.'),
(4645, 50, 15, 'Heeft de eerste schepping Ons dan inspanning gekost? Welnee, maar zij zijn in verwarring over een nieuwe schepping.'),
(4646, 50, 16, 'Wij hebben de mens toch geschapen en Wij weten wat hij zichzelf influistert. Wij zijn namelijk dichter bij hem dan de halsslagader.'),
(4647, 50, 17, 'Wanneer de beide ontvangers, aan de rechter- en de linkerkant zittend, [hem] zullen ontvangen'),
(4648, 50, 18, 'kan hij geen woord uitbrengen zonder dat er een bewaker klaarstaat.'),
(4649, 50, 19, 'En de roes van de dood brengt de waarheid; dat is het wat jullie ontweken.'),
(4650, 50, 20, 'En er wordt op de bazuin geblazen; dat is de dag van de aanzegging.'),
(4651, 50, 21, 'En elke persoon komt met een voortdrijver bij zich en een getuige.'),
(4652, 50, 22, '[En tot hem wordt gezegd:] "Jij hebt hierop niet gelet, maar wij hebben jouw bedekking van je afgenomen, zodat je blik vandaag scherp is."'),
(4653, 50, 23, 'En zijn kameraad zegt: "Dit is wat bij mij klaarstaat."'),
(4654, 50, 24, '[En tot die twee wordt gezegd:] "Jullie beiden moeten elke weerspannige ongelovige in de hel werpen,'),
(4655, 50, 25, 'die het goede tegenhoudt, overtredingen begaat, in twijfel verkeert,'),
(4656, 50, 26, 'en die naast God een andere god stelt. Werpt hem dus in de pijnlijke bestraffing." *'),
(4657, 50, 27, 'Zijn kameraad zegt: "Onze Heer, ik heb hem niet opstandig gemaakt; hij verkeerde zelf in verregaande dwaling."'),
(4658, 50, 28, 'Hij zegt: "Twist in Mijn bijzijn niet met elkaar. Ik heb jullie de aanzegging vroeger al gegeven.'),
(4659, 50, 29, 'De uitspraak wordt bij Mij niet veranderd en Ik geef geen onrechtvaardige behandeling aan de dienaren'),
(4660, 50, 30, 'op de dag dat Wij tot de hel zeggen: \'Ben je al vol?? en dat zij zegt: \'Komt er nog meer??"'),
(4661, 50, 31, 'En de tuin wordt dicht bij de godvrezenden gebracht, niet ver van hen vandaan.'),
(4662, 50, 32, '"Dit is wat aan jullie is toegezegd voor ieder die schuldbewust is en zich aan [de godsdienst] houdt,'),
(4663, 50, 33, 'die de Erbarmer in het verborgene vreest en met een schuldbewust hart komt.'),
(4664, 50, 34, 'Gaat in vrede binnen." Dat is de dag van de eeuwigheid.'),
(4665, 50, 35, 'Daarin hebben zij wat zij willen en bij Ons is er nog meer.'),
(4666, 50, 36, 'Hoeveel generaties hebben Wij al niet voor hun tijd vernietigd die meer macht bezaten dan zij. Toch zochten zij het land af of er geen ontsnapping was.'),
(4667, 50, 37, 'Daarin is een vermaning voor wie een hart heeft of wie scherp luistert als hij er getuige van is.'),
(4668, 50, 38, 'Wij hebben de hemelen en de aarde en wat er tussen beide is in zes dagen geschapen en Wij zijn niet door uitputting overvallen.'),
(4669, 50, 39, 'Verdraag wat zij zeggen dus geduldig en prijs de lof van jouw Heer voor zonsopgang en na zonsondergang'),
(4670, 50, 40, 'en prijs Hem \'s nachts en na afloop van de eerbiedige neerbuiging.'),
(4671, 50, 41, 'En luister op de dag dat de oproeper van een nabije plaats roept.'),
(4672, 50, 42, 'Op de dag dat zij de schreeuw in waarheid zullen horen; dat is de dag waarop [de doden] tevoorschijn komen.'),
(4673, 50, 43, 'Wij geven leven en Wij laten sterven en bij Ons is de bestemming.'),
(4674, 50, 44, 'Op de dag dat de aarde voor hen uiteensplijt zodat zij snel [tevoorschijn] komen. Dat is een gemakkelijk verzamelen voor Ons.'),
(4675, 50, 45, 'Wij weten het best wat zij zeggen en jij bent geen bewindvoerder over hen. Vermaan dan met de Koran wie Mijn aanzegging vreest.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Bij de heftig opjagenden,'),
(4677, 51, 2, 'bij de last dragenden,'),
(4678, 51, 3, 'bij de gemakkelijk voortsnellenden'),
(4679, 51, 4, 'en bij de bevel verdelenden!'),
(4680, 51, 5, 'Wat jullie is aangezegd is zeker waar'),
(4681, 51, 6, 'en het oordeel is zeker aanstaande.'),
(4682, 51, 7, 'Bij de hemel met zijn banen!'),
(4683, 51, 8, 'Jullie zijn het in wat jullie zeggen oneens.'),
(4684, 51, 9, 'Wie zich laat afleiden wordt van hem afgeleid.'),
(4685, 51, 10, 'Doodvallen kunnen zij die slechts gissen,'),
(4686, 51, 11, 'die in een waan blijven.'),
(4687, 51, 12, 'Zij vragen wanneer de oordeelsdag zal zijn.'),
(4688, 51, 13, 'Op de dag dat zij aan de beproeving van het vuur worden blootgesteld.'),
(4689, 51, 14, '"Proeft jullie beproeving, dit is het wat jullie wilden verhaasten."'),
(4690, 51, 15, 'Maar de godvrezenden zullen in tuinen en bij bronnen zijn.'),
(4691, 51, 16, 'Zij nemen wat hun Heer hun geeft; zij waren immers voordien mensen die goed deden.'),
(4692, 51, 17, '\'s Nachts sliepen zij slechts weinig'),
(4693, 51, 18, 'en in de morgenschemering vroegen zij om vergeving'),
(4694, 51, 19, 'en een rechtmatig aandeel in hun bezittingen was voor de bedelaar en de onbemiddelde.'),
(4695, 51, 20, 'Op de aarde zijn er tekenen voor hen die vast overtuigd zijn,'),
(4696, 51, 21, 'en in jullie zelf. Hebben jullie dan geen inzicht?'),
(4697, 51, 22, 'En in de hemel is jullie levensonderhoud en wat jullie wordt toegezegd.'),
(4698, 51, 23, 'Bij de Heer van de hemel en de aarde, het is zo waar als dat jullie kunnen spreken.'),
(4699, 51, 24, 'Is het verhaal van de geëerde gasten van Ibrahiem tot jullie gekomen?'),
(4700, 51, 25, 'Toen zij bij hem binnengingen en "Vrede" zeiden, zei hij: "Vrede, onbekende mensen."'),
(4701, 51, 26, 'Hij wendde zich toen heimelijk tot zijn huisgenoten en bracht een gemest kalf.'),
(4702, 51, 27, 'Dat zette hij hun toen voor. Hij zei: "Willen jullie niet eten?"'),
(4703, 51, 28, 'Toen werd hij door vrees voor hen bevangen. Zij zeiden: "Wees niet bang" en verkondigden hem het goede nieuws van een verstandige jongen.'),
(4704, 51, 29, 'Toen kwam zijn vrouw er schreeuwend aan. Zij sloeg zich in het gezicht en zei: "Een onvruchtbare oude vrouw!"'),
(4705, 51, 30, 'Zij zeiden: "Zo heeft jouw Heer het gezegd. Hij is de wijze, de wetende." *'),
(4706, 51, 31, 'Hij zei: "Waar komen jullie voor, o gezondenen?"'),
(4707, 51, 32, 'Zij zeiden: "Wij zijn gezonden naar misdadige mensen'),
(4708, 51, 33, 'om op hen stenen van klei neer te zenden,'),
(4709, 51, 34, 'die bij jouw Heer gemerkt zijn voor de onmatigen."'),
(4710, 51, 35, 'En Wij lieten hen die daar tot de gelovigen behoorden eruit gaan,'),
(4711, 51, 36, 'maar Wij vonden er slechts één huis van hen die zich [aan God] overgaven.'),
(4712, 51, 37, 'En Wij lieten daarin een teken achter voor hen die de pijnlijke bestraffing vrezen.'),
(4713, 51, 38, 'Ook in [het verhaal van] Moesa, toen Wij hem met een duidelijke machtiging naar Fir\'aun zonden.'),
(4714, 51, 39, 'Die keerde zich toen met zijn garde af en zei: "Een tovenaar of een bezetene."'),
(4715, 51, 40, 'En Wij grepen hem en zijn troepen en wierpen hen in de zee; laakbaar was hij.'),
(4716, 51, 41, 'Ook in [het verhaal van] de \'Aad, toen Wij de barre wind tegen hen zonden,'),
(4717, 51, 42, 'die waar hij overheen ging niets overliet, maar het als gruis maakte.'),
(4718, 51, 43, 'Ook in [het verhaal van] de Thamoed, toen Wij tot hen zeiden: "Jullie kunnen nog een tijd genieten."'),
(4719, 51, 44, 'Maar zij minachtten het bevel van hun Heer en toen greep de donderslag hen, terwijl zij keken.'),
(4720, 51, 45, 'Toen konden zij niet meer opstaan en konden niet meer geholpen worden.'),
(4721, 51, 46, 'Ook het volk van Noeh, vroeger al; dat waren verdorven mensen.'),
(4722, 51, 47, 'De hemel hebben Wij duurzaam gebouwd; Wij hebben het vermogen.'),
(4723, 51, 48, 'En de aarde hebben Wij uitgespreid; een voortreffelijke plaatsbereider zijn Wij.'),
(4724, 51, 49, 'En alles hebben Wij paarsgewijs geschapen; misschien zullen jullie je laten vermanen.'),
(4725, 51, 50, 'Vlucht dan tot God; ik ben voor jullie een duidelijke waarschuwer van Zijn kant.'),
(4726, 51, 51, 'En stelt naast God geen andere god; ik ben voor jullie een duidelijke waarschuwer van Zijn kant.'),
(4727, 51, 52, 'Zo is er ook tot hen die er voor hun tijd waren geen gezant gekomen zonder dat zij zeiden: "Een tovenaar of een bezetene."'),
(4728, 51, 53, 'Hebben zij het soms aan elkaar opgedragen? Welnee, zij zijn onbeschaamde mensen.'),
(4729, 51, 54, 'Keer je dus van hen af; jou treft dan geen blaam.'),
(4730, 51, 55, 'En vermaan, want de vermaning is nuttig voor de gelovigen.'),
(4731, 51, 56, 'Ik heb de mensen en de djinn slechts geschapen om Mij te dienen.'),
(4732, 51, 57, 'Ik wens door hen niet van levensonderhoud te worden voorzien en Ik wens niet dat zij mij voeden.'),
(4733, 51, 58, 'God is de voorziener die sterke kracht heeft.'),
(4734, 51, 59, 'Maar voor hen die onrecht plegen is er een portie die even groot is als de portie van hun metgezellen. Zij moeten Mij dan maar niet vragen het te verhaasten.'),
(4735, 51, 60, 'En wee hen die ongelovig zijn wegens hun dag die hun is aangezegd.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Bij de berg.'),
(4737, 52, 2, 'Bij een boek dat is opgetekend'),
(4738, 52, 3, 'op opengerold perkament.'),
(4739, 52, 4, 'Bij het in stand gehouden huis.'),
(4740, 52, 5, 'Bij het opgeheven dak.'),
(4741, 52, 6, 'Bij de kolkende zee.'),
(4742, 52, 7, 'De bestraffing van jouw Heer is aanstaande.'),
(4743, 52, 8, 'Niemand kan haar afweren'),
(4744, 52, 9, 'op de dag dat de hemel begint te trillen'),
(4745, 52, 10, 'en de bergen bewegen.'),
(4746, 52, 11, 'Wee op die dag de loochenaars,'),
(4747, 52, 12, 'zij die in geklets schertsen.'),
(4748, 52, 13, 'Op de dag dat zij in het vuur van de hel geduwd worden.'),
(4749, 52, 14, '"Dit is het vuur waarvan jullie het bestaan loochenden.'),
(4750, 52, 15, 'Is dit dan toverij of zien jullie het niet goed?'),
(4751, 52, 16, 'Braadt erin en of jullie het wel of niet kunnen verdragen maakt voor jullie niet uit. Aan jullie wordt vergolden wat jullie gedaan hebben."'),
(4752, 52, 17, 'Maar de godvrezenden zullen in tuinen en in gelukzaligheid zijn,'),
(4753, 52, 18, 'blij met wat hun Heer hun geeft. En hun Heer beschermt hen tegen de bestraffing van de hel.'),
(4754, 52, 19, '"Eet en drinkt met genoegen [als beloning] voor wat jullie gedaan hebbent'),
(4755, 52, 20, 'Achterovergeleund op in rijen gezette rustbedden. En Wij geven hun gezellinnen met sprekende grote ogen ten huwelijk.'),
(4756, 52, 21, 'En het nageslacht van hen die geloven en die in hun geloof door hun nageslacht gevolgd worden hebben Wij bij hen gevoegd. En Wij laten hen voor wat zij gedaan hebben in niets tekortkomen. Elke man is aansprakelijk voor wat hij begaan heeft.'),
(4757, 52, 22, 'En Wij overladen hen met vruchten en vlees, wat zij maar begeren,'),
(4758, 52, 23, 'terwijl zij er een beker aan elkaar doorgeven waarbij er onzinnig geklets noch verleiding tot zonde voorkomt. *'),
(4759, 52, 24, 'En bij hen gaan er jongelingen rond, die als goedbewaarde parels zijn, om hen te bedienen.'),
(4760, 52, 25, 'En zij komen naar elkaar toe om elkaar te ondervragen.'),
(4761, 52, 26, 'Zij zeggen: "Vroeger, te midden van onze familie, hadden wij ontzag [voor Gods woord].'),
(4762, 52, 27, 'Toen bewees God ons een gunst en beschermde ons voor de straf van de verzengende gloed.'),
(4763, 52, 28, 'Vroeger baden wij tot Hem; Hij is de welwillende, de barmhartige."'),
(4764, 52, 29, 'Vermaan dus, want jij bent door de genade van jouw Heer geen waarzegger en geen bezetene.'),
(4765, 52, 30, 'Of zeggen zij: "Een dichter. Wij wachten af wat het noodlot hem voor twijfelachtigs brengt."'),
(4766, 52, 31, 'Zeg: "Wachten jullie maar af, ik behoor tot hen die samen met jullie afwachten."'),
(4767, 52, 32, 'Of bevelen hun verstandelijke vermogens hun dat of zijn zij onbeschaamde mensen?'),
(4768, 52, 33, 'Of zeggen zij: "Hij heeft het bedacht."? Zeker niet, maar zij geloven niet.'),
(4769, 52, 34, 'Laten zij dan met een overeenkomstig bericht komen, als zij gelijk hebben.'),
(4770, 52, 35, 'Of zijn zij door niets geschapen? Of zijn zij zelf de scheppers?'),
(4771, 52, 36, 'Of hebben zij de hemelen en de aarde geschapen? Welnee, maar zij zijn niet overtuigd.'),
(4772, 52, 37, 'Of zijn bij hen de schatkamers van jouw Heer? Of zijn zij de opperheersers?'),
(4773, 52, 38, 'Of hebben zij een ladder waarop zij kunnen staan luisteren? Dan moet hun luisteraar maar een duidelijke machtiging brengen.'),
(4774, 52, 39, 'Of zou Hij dan dochters hebben en jullie zonen?'),
(4775, 52, 40, 'Of vraag jij hun loon zodat zij met betalingsverplichtingen belast zijn?'),
(4776, 52, 41, 'Of is het verborgene bij hen zodat zij het kunnen opschrijven?'),
(4777, 52, 42, 'Of wensen zij een list te beramen? Maar zij die ongelovig zijn worden door list belaagd.'),
(4778, 52, 43, 'Of hebben zij een andere god dan God? Geprezen zij Hij, verheven als Hij is boven wat zij aan Hem als metgezellen toevoegen.'),
(4779, 52, 44, 'En als zij een stuk uit de hemel zien vallen zeggen zij: "Een stapelwolk."'),
(4780, 52, 45, 'Laat hen maar totdat zij hun dag tegenkomen waarop zij wezenloos neervallen;'),
(4781, 52, 46, 'de dag waarop hun list hun niets baat en waarop zij geen hulp zullen krijgen.'),
(4782, 52, 47, 'Voor hen die onrecht plegen is er daarnaast nog een bestraffing, maar de meesten van hen weten het niet.'),
(4783, 52, 48, 'Volhard dus geduldig tot aan het oordeel van jouw Heer; Wij hebben jou voor ogen. En prijs de lof van jouw Heer wanneer je opstaat.'),
(4784, 52, 49, 'Lofprijs Hem ook in de nacht en als de sterren verbleken.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Bij de ster, wanneer zij valt.'),
(4786, 53, 2, 'Jullie medeburger dwaalt niet en heeft geen afwijking,'),
(4787, 53, 3, 'noch spreekt hij uit een bevlieging.'),
(4788, 53, 4, 'Dit is niet anders dan een ingegeven openbaring.'),
(4789, 53, 5, 'Hem onderwees een grootmachtige'),
(4790, 53, 6, 'en scherpzinnige. Evenwichtig'),
(4791, 53, 7, 'stond hij hoog aan de horizon.'),
(4792, 53, 8, 'Toen naderde hij, liet zich neder --'),
(4793, 53, 9, 'op twee booglengten afstand of nog nader --'),
(4794, 53, 10, 'en gaf Zijn dienaar die openbaring.'),
(4795, 53, 11, 'Het hart loog niet over wat hij zag.'),
(4796, 53, 12, 'Zullen jullie hem dan betwisten wat hij ziet?'),
(4797, 53, 13, 'Hij had hem reeds gezien bij een andere neerdaling,'),
(4798, 53, 14, 'bij de lotusboom van de eindbestemming,'),
(4799, 53, 15, 'bij de tuin van de [hemelse] verblijfplaats,'),
(4800, 53, 16, 'toen de lotusboom verhuld werd met wat hem verhulde.'),
(4801, 53, 17, 'Zijn blik week noch dwaalde:'),
(4802, 53, 18, 'Hij had een van de grootste tekenen van zijn Heer gezien.'),
(4803, 53, 19, 'Hoe zien jullie dan al-Laat en al-\'Oezza,'),
(4804, 53, 20, 'en Manaat, de derde, de andere?'),
(4805, 53, 21, 'Zouden jullie dan de mannelijke [kinderen] hebben en Hij de vrouwelijke?'),
(4806, 53, 22, 'Dat zou dan een onrechtvaardige verdeling zijn.'),
(4807, 53, 23, 'Het zijn slechts namen die jullie en jullie vaderen gegeven hebben en waarvoor God geen enkele machtiging had neergezonden. Jullie volgen slechts vermoedens en wat jullie zelf graag willen, hoewel van jullie Heer de leidraad is gekomen.'),
(4808, 53, 24, 'Of krijgt de mens alles wat hij wenst?'),
(4809, 53, 25, 'Van God is het hiernamaals en het tegenwoordige bestaan!'),
(4810, 53, 26, 'En hoeveel engelen zijn er niet in de hemelen wier voorspraak niets baat, behalve nadat God toestemming geeft voor wie Hij het wil en goedvindt.'),
(4811, 53, 27, 'Zij die niet in het hiernamaals geloven geven de engelen vrouwelijke namen.'),
(4812, 53, 28, 'Zij hebben daarover geen kennis. Zij volgen slechts vermoedens en vermoedens baten niets tegen de waarheid.'),
(4813, 53, 29, 'Wend je dan af van wie zich van Onze vermaning heeft afgekeerd en die slechts het tegenwoordige leven wenst.'),
(4814, 53, 30, 'Zover heeft hun kennis gereikt, maar jouw Heer kent wie van Zijn weg afdwaalt het best en Hij kent hem die het goede pad volgt het best.'),
(4815, 53, 31, 'En van God is wat er in de hemelen en wat er op de aarde is om aan hen die verkeerd doen te vergelden wat zij gedaan hebben en de allermooiste beloning te geven aan hen die goed doen,'),
(4816, 53, 32, 'die de grote zonden en gruwelijkheden vermijden, afgezien dan van kleine overtredingen; jouw Heer is alomvattend in Zijn vergeving. Hij kent jullie het best; toen Hij jullie uit de aarde liet ontstaan en toen jullie nog ongeboren in de buik van jullie moeders waren. Zeg dan niet van jullie zelf dat jullie gelouterd zijn; Hij weet het best wie godvrezend is.'),
(4817, 53, 33, 'Heb jij hem gezien die zich afkeert'),
(4818, 53, 34, 'en die een weinig geeft en dan niet meer?'),
(4819, 53, 35, 'Heeft hij de kennis van het onzichtbare, zodat hij het ziet?'),
(4820, 53, 36, 'Of is hem dan niet meegedeeld wat er staat in de bladen van Moesa'),
(4821, 53, 37, 'en Ibrahiem die [zijn plicht] vervulde?'),
(4822, 53, 38, 'Dat niemand belast is met de last van een ander.'),
(4823, 53, 39, 'Dat de mens slechts krijgt wat hij heeft nagejaagd.'),
(4824, 53, 40, 'En dat wat hij heeft nagejaagd zichtbaar zal worden.'),
(4825, 53, 41, 'Dat wordt hem dan volledig vergolden.'),
(4826, 53, 42, 'En dat bij jouw Heer de eindbestemming is.'),
(4827, 53, 43, 'En dat Hij het is die laat lachen en die laat huilen.'),
(4828, 53, 44, 'En die laat sterven en die leven geeft.'),
(4829, 53, 45, 'En dat Hij de beide geslachten, het mannelijke en het vrouwelijke, geschapen heeft'),
(4830, 53, 46, 'uit een druppel, wanneer die wordt uitgestort.'),
(4831, 53, 47, 'En dat de laatste totstandkoming Zijn taak is.'),
(4832, 53, 48, 'En dat Hij het is die rijk maakt en die vermogen geeft.'),
(4833, 53, 49, 'En dat Hij de Heer van Sirius is.'),
(4834, 53, 50, 'En dat Hij de \'Aad van eertijds heeft vernietigd'),
(4835, 53, 51, 'en ook de Thamoed -- en dat Hij toen niets overliet --'),
(4836, 53, 52, 'en het volk van Noeh daarvoor al. Zij waren uiterst zondig en onbeschaamd.'),
(4837, 53, 53, 'En de ondersteboven gekeerde [stad] stortte Hij naar beneden.'),
(4838, 53, 54, 'En die heeft Hij toen bedekt met de bedekking die Hij gaf.'),
(4839, 53, 55, 'Welke weldaden van jouw Heer wil jij dan betwijfelen?'),
(4840, 53, 56, 'Dit is een waarschuwer als de eerdere waarschuwers.'),
(4841, 53, 57, 'De aanstaande [opstanding] is nabij.'),
(4842, 53, 58, 'Niemand buiten God kan haar opheffen.'),
(4843, 53, 59, 'Zijn jullie dan verbaasd over dit bericht?'),
(4844, 53, 60, 'En lachen jullie en huilen jullie niet,'),
(4845, 53, 61, 'afgeleid als jullie zijn?'),
(4846, 53, 62, 'Buigt dan eerbiedig neer voor God en dient [Hem]."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Het uur is nabijgekomen en de maan is gespleten.'),
(4848, 54, 2, 'Maar als zij een teken zien wenden zij zich af en zeggen: "Voortdurende toverij."'),
(4849, 54, 3, 'En zij loochenen het en zij volgen hun grillen. En alles heeft zijn vaste tijd.'),
(4850, 54, 4, 'Tot hen zijn er berichten gekomen die afschrikwekkend zijn;'),
(4851, 54, 5, 'een doeltreffende wijsheid. Maar de waarschuwingen baten niet.'),
(4852, 54, 6, 'Keer je dus van hen af. Op de dag dat de oproeper tot iets vreselijks oproept'),
(4853, 54, 7, 'en zij met hun deemoedige blikken uit de graven tevoorschijn komen alsof zij uiteengejaagde sprinkhanen zijn,'),
(4854, 54, 8, 'terwijl zij zich voortspoeden naar de oproeper, dan zeggen de ongelovigen: "Dit is een moeilijke dag!"'),
(4855, 54, 9, 'Voor hun tijd had het volk van Noeh al gezegd dat het leugens waren; zij betichtten Onze dienaar namelijk van leugen. Zij zeiden: "Een bezetene." Hij werd door hen afgeschrikt.'),
(4856, 54, 10, 'En hij bad tot zijn Heer: "Ik ben verslagen, kom dus te hulp."'),
(4857, 54, 11, 'Toen openden Wij de poorten van de hemel met neergutsend water.'),
(4858, 54, 12, 'En Wij lieten de aarde in bronnen uitbarsten, zodat het water bij elkaar kwam volgens een vastgelegde beschikking.'),
(4859, 54, 13, 'Maar hem droegen Wij op het uit planken en pennen gemaakte [schip],'),
(4860, 54, 14, 'dat voor Onze ogen bleef varen, als beloning voor hem die ondankbaar behandeld was.'),
(4861, 54, 15, 'En Wij lieten het achter als een teken. Maar is er dan iemand die zich laat vermanen?'),
(4862, 54, 16, 'En hoe waren Mijn bestraffing en Mijn waarschuwingen dan?'),
(4863, 54, 17, 'Wij hebben de Koran toch gemakkelijk gemaakt om erdoor vermaand te worden; maar is er dan iemand die zich laat vermanen?'),
(4864, 54, 18, 'De \'Aad hadden gezegd dat het leugens waren. En hoe waren Mijn bestraffing en Mijn waarschuwingen dan?'),
(4865, 54, 19, 'Wij zonden tegen hen een gierende wind op een langdurige, onheilspellende dag'),
(4866, 54, 20, 'die de mensen wegrukte als ontwortelde palmstronken.'),
(4867, 54, 21, 'En hoe waren Mijn bestraffing en Mijn waarschuwingen dan?'),
(4868, 54, 22, 'Wij hebben de Koran toch gemakkelijk gemaakt om erdoor vermaand te worden; maar is er dan iemand die zich laat vermanen?'),
(4869, 54, 23, 'De Thamoed hadden gezegd dat de waarschuwingen leugens waren.'),
(4870, 54, 24, 'En zij zeiden: "Eén mens uit ons midden, zullen wij die volgen? Dan zouden wij toch wel in dwaling verkeren en waanzinnig zijn.'),
(4871, 54, 25, 'Zou uit ons midden de vermaning aan hem zijn opgelegd? Welnee, hij is een arrogante leugenaar."'),
(4872, 54, 26, 'Morgen zullen zij weten wie de arrogante leugenaar is.'),
(4873, 54, 27, 'Wij zullen de kameelmerrie als een beproeving voor hen zenden. Let dus op hen, wacht maar af en wees geduldig.'),
(4874, 54, 28, 'En deel hun mee dat het water tussen hen verdeeld moet worden. Elke portie om te drinken op zijn beurt.'),
(4875, 54, 29, 'Toen riepen zij hun medeburger. Hij sloeg toe en sneed de hielpezen door.'),
(4876, 54, 30, 'En hoe waren Mijn bestraffing en Mijn waarschuwingen dan?'),
(4877, 54, 31, 'Wij zonden slechts één schreeuw tegen hen en zij waren als de droge takken van iemand die een omheining maakt.'),
(4878, 54, 32, 'Wij hebben de Koran toch gemakkelijk gemaakt om erdoor vermaand te worden; maar is er dan iemand die zich laat vermanen?'),
(4879, 54, 33, 'Het volk van Loet had gezegd dat de waarschuwingen leugens waren.'),
(4880, 54, 34, 'Wij zonden over hen een regen van stenen, behalve op de familie van Loet die Wij in de morgenschemering redden,'),
(4881, 54, 35, 'als een daad van genade van Onze kant; zo belonen Wij wie dank betuigt.'),
(4882, 54, 36, 'Hij had hen voor Ons geweld gewaarschuwd, maar zij twijfelden aan de waarschuwingen.'),
(4883, 54, 37, 'Zij probeerden hem zijn gasten af te troggelen. Toen ontnamen Wij hun ogen het gezichtsvermogen: "Proeft dan Mijn bestraffing en Mijn waarschuwingen!"'),
(4884, 54, 38, 'En een blijvende bestraffing kwam \'s ochtends over hen:'),
(4885, 54, 39, '"Proeft dan Mijn bestraffing en Mijn waarschuwingen!"'),
(4886, 54, 40, 'Wij hebben de Koran toch gemakkelijk gemaakt om erdoor vermaand te worden; maar is er dan iemand die zich laat vermanen?'),
(4887, 54, 41, 'Ook tot de mensen van Fir\'aun kwamen de waarschuwingen.'),
(4888, 54, 42, 'Zij loochenden al Onze tekenen en dus grepen Wij hen zoals een krachtige machthebber dat doet.'),
(4889, 54, 43, 'Zijn de ongelovigen van jullie beter dan die? Of is er voor jullie een vrijbrief in de Zoeboer?'),
(4890, 54, 44, 'Of zeggen zij: "Wij zullen tezamen overwinnen."?'),
(4891, 54, 45, 'Gezamenlijk worden zij verslagen en zij zullen de rug toekeren.'),
(4892, 54, 46, 'Ja zeker, het uur is het voor hen aangewezen tijdstip en het uur is nog vreselijker en bitterder.'),
(4893, 54, 47, 'De boosdoeners verkeren in dwaling en zijn waanzinnig.'),
(4894, 54, 48, 'Op de dag dat zij op hun gezichten [ter aarde geworpen] in het vuur gesleurd worden: "Proeft dan de aanraking met de hellegloed."'),
(4895, 54, 49, 'Wij hebben alles op maat geschapen.'),
(4896, 54, 50, 'En Onze beschikking is slechts één [woord], als een oogwenk.'),
(4897, 54, 51, 'Wij hebben jullie soortgenoten vernietigd, maar is er dan iemand die zich laat vermanen?'),
(4898, 54, 52, 'En alles wat zij gedaan hebben staat in de Zoeboer.'),
(4899, 54, 53, 'En alles, klein en groot staat opgetekend.'),
(4900, 54, 54, 'De godvrezenden zullen in tuinen en bij rivieren zijn,'),
(4901, 54, 55, 'op een waarachtige zetel bij een machtige koning.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'De Erbarmer,'),
(4903, 55, 2, 'Hij onderwees de Koran,'),
(4904, 55, 3, 'Hij schiep de mens,'),
(4905, 55, 4, 'Hij onderwees hem de uiteenzetting.'),
(4906, 55, 5, 'De zon en de maan gaan volgens een berekening.'),
(4907, 55, 6, 'De ster en de bomen buigen zich eerbiedig neer.'),
(4908, 55, 7, 'De hemel heeft Hij opgeheven en Hij heeft de weegschaal opgesteld,'),
(4909, 55, 8, 'opdat jullie bij het wegen niet over de schreef gaan,'),
(4910, 55, 9, 'maar het gewicht rechtvaardig vaststellen en de weegschaal niet te weinig laten wegen.'),
(4911, 55, 10, 'En de aarde heeft Hij voor de schepselen klaargezet.'),
(4912, 55, 11, 'Daarop zijn vruchten en palmen met hulzen'),
(4913, 55, 12, 'en zaadkorrels aan halmen en welriekende planten.'),
(4914, 55, 13, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4915, 55, 14, 'Hij schiep de mens uit steenaarde zoals aardewerk.'),
(4916, 55, 15, 'En de djinn schiep Hij uit de gloed van het vuur.'),
(4917, 55, 16, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4918, 55, 17, 'De Heer van de beide oostkanten en de beide westkanten.'),
(4919, 55, 18, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4920, 55, 19, 'Hij heeft de beide zeeën vrij laten stromen zodat zij elkaar ontmoeten,'),
(4921, 55, 20, 'met tussen beide een versperring om niet te ver te gaan.'),
(4922, 55, 21, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4923, 55, 22, 'Daaruit komen parels en koraal.'),
(4924, 55, 23, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4925, 55, 24, 'En van Hem zijn de schepen die met volle zeilen als bergen op de zee varen.'),
(4926, 55, 25, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4927, 55, 26, 'Een ieder die er op de aarde is zal vergaan,'),
(4928, 55, 27, 'maar jouw Heers gelaat van majesteit en eer blijft bestaan.'),
(4929, 55, 28, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4930, 55, 29, 'Allen in de hemelen en op de aarde wenden zich met vragen tot Hem. Elke dag houdt Hij zich met iets bezig.'),
(4931, 55, 30, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4932, 55, 31, 'Wij zullen Ons bezighouden met jullie, die beiden zwaar wegen.'),
(4933, 55, 32, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4934, 55, 33, 'O djinn en mensen, hier bijeen! Als jullie uit de gebieden van de hemelen en de aarde weg kunnen komen, maakt dan dat jullie wegkomen! Maar jullie kunnen slechts met een machtiging wegkomen.'),
(4935, 55, 34, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4936, 55, 35, 'Laaiend vuur en gloeiend koper zal over jullie worden uitgestort en jullie zullen geen hulp vinden.'),
(4937, 55, 36, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4938, 55, 37, 'Als dan de hemel barst en rood wordt als scharlaken leer.'),
(4939, 55, 38, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4940, 55, 39, 'Op die dag zullen mensen noch djinn naar hun zonden worden gevraagd.'),
(4941, 55, 40, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4942, 55, 41, 'De boosdoeners zullen herkend worden aan hun kenmerk en men zal hen bij de kuif en de voeten grijpen.'),
(4943, 55, 42, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4944, 55, 43, 'Dit is de hel waarvan de boosdoeners het bestaan loochenden.'),
(4945, 55, 44, 'Zij lopen tussen haar en gloeiend heet water heen en weer.'),
(4946, 55, 45, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4947, 55, 46, 'Maar voor wie vreest om voor zijn Heer te staan zijn er twee tuinen.'),
(4948, 55, 47, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4949, 55, 48, 'Vol met allerlei zijtakken.'),
(4950, 55, 49, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4951, 55, 50, 'Daarin zijn twee bronnen die stromen.'),
(4952, 55, 51, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4953, 55, 52, 'Daarin zijn er van elke vrucht twee soorten.'),
(4954, 55, 53, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4955, 55, 54, 'Zij leunen achterover op rustbedden die aan de binnenkant met brokaat zijn gevoerd en de vruchten van de beide tuinen hangen dichtbij.'),
(4956, 55, 55, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4957, 55, 56, 'Daarbij zijn er gezellinnen met afgewende blikken die voor hun tijd door mensen noch djinn waren aangeraakt.'),
(4958, 55, 57, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4959, 55, 58, 'Alsof zij saffieren en koralen zijn.'),
(4960, 55, 59, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4961, 55, 60, 'Is de beloning voor goed doen iets anders dan goed doen?'),
(4962, 55, 61, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4963, 55, 62, 'En daarnaast zijn er nog twee tuinen.'),
(4964, 55, 63, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4965, 55, 64, 'Donkergroene.'),
(4966, 55, 65, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4967, 55, 66, 'Daarin zijn twee bruisende bronnen.'),
(4968, 55, 67, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4969, 55, 68, 'Daarin zijn vruchten, palmen en granaatappelen.'),
(4970, 55, 69, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4971, 55, 70, 'Daarbij zijn er goede en mooie vrouwen.'),
(4972, 55, 71, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4973, 55, 72, 'Met sprekende grote [ogen], in tenten afgezonderd.'),
(4974, 55, 73, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4975, 55, 74, 'Die voor hun tijd door mensen noch djinn waren aangeraakt.'),
(4976, 55, 75, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4977, 55, 76, 'Achterovergeleund op groene kussens en onvoorstelbaar mooie [tapijten].'),
(4978, 55, 77, 'Welke weldaden van jullie beider Heer loochenen jullie dan?'),
(4979, 55, 78, 'Gezegend zij de naam van jouw Heer vol majesteit en eer.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Wanneer het aanstaande komt,'),
(4981, 56, 2, 'waarvan niemand de komst kan loochenen,'),
(4982, 56, 3, 'vernedert en verhoogt het.'),
(4983, 56, 4, 'Wanneer de aarde hevig door elkaar wordt geschud'),
(4984, 56, 5, 'en de bergen geheel worden verbrijzeld'),
(4985, 56, 6, 'en dan verspreid stof worden,'),
(4986, 56, 7, 'dan zullen jullie er in drie groepen zijn:'),
(4987, 56, 8, 'Zij dan die aan de rechterkant staan -- wie zijn zij die aan de rechterkant staan?'),
(4988, 56, 9, 'En zij die aan de sinistere kant staan -- wie zijn zij die aan de sinistere kant staan?'),
(4989, 56, 10, 'Maar de eerstgekomenen zijn het eerst gekomen;'),
(4990, 56, 11, 'zij zijn het die in de nabijheid [van God] zijn gebracht,'),
(4991, 56, 12, 'in de tuinen van de gelukzaligheid:'),
(4992, 56, 13, 'een groep van hen die er eertijds waren'),
(4993, 56, 14, 'en weinig van hen die er later waren,'),
(4994, 56, 15, 'op rustbanken met inlegwerk,'),
(4995, 56, 16, 'waarop zij tegenover elkaar zittend achteroverleunen.'),
(4996, 56, 17, 'Bij hen gaan altijd jong blijvende jongelingen rond'),
(4997, 56, 18, 'met bekers en kruiken en een drinkbeker [waarin een drank is] uit een bron,'),
(4998, 56, 19, 'waarvan zij geen hoofdpijn krijgen en waarvan zij niet beneveld raken,'),
(4999, 56, 20, 'en vruchten die zij voor zich uitkiezen'),
(5000, 56, 21, 'en vlees van gevogelte, wat zij maar begeren.'),
(5001, 56, 22, 'En er zijn gezellinnen met sprekende grote ogen,'),
(5002, 56, 23, 'die als welbewaarde parels zijn.'),
(5003, 56, 24, 'Als beloning voor wat zij gedaan hebben.'),
(5004, 56, 25, 'Zij horen er geen onzinnig geklets noch verleiding tot zonde,'),
(5005, 56, 26, 'alleen maar het gezegde: Vrede, vrede!'),
(5006, 56, 27, 'En zij die rechts staan -- wie zijn zij die rechts staan?'),
(5007, 56, 28, 'Te midden van lotusbomen zonder doornen zijn zij,'),
(5008, 56, 29, 'en opeengepakte bananen,'),
(5009, 56, 30, 'uitgestrekte schaduw,'),
(5010, 56, 31, 'vrij stromend water'),
(5011, 56, 32, 'en veel vruchten,'),
(5012, 56, 33, 'zonder ophouden en niet onbereikbaar,'),
(5013, 56, 34, 'op verhoogde rustbedden.'),
(5014, 56, 35, 'Wij hebben haar laten ontstaan.'),
(5015, 56, 36, 'En Wij hebben haar tot maagden gemaakt,'),
(5016, 56, 37, 'als vurig beminnende even oude gezellinnen'),
(5017, 56, 38, 'voor hen die rechts staan:'),
(5018, 56, 39, 'Een groep van hen die er eertijds waren'),
(5019, 56, 40, 'en weinig van hen die er later waren.'),
(5020, 56, 41, 'En zij die links staan -- wie zijn zij die links staan?'),
(5021, 56, 42, 'In een verzengende gloed en in gloeiend water staan zij'),
(5022, 56, 43, 'en in de schaduw van zwarte rook,'),
(5023, 56, 44, 'die niet koud is noch weldadig.'),
(5024, 56, 45, 'Voordien leefden zij in luxe'),
(5025, 56, 46, 'en volhardden in de geweldige zonde.'),
(5026, 56, 47, 'Zij zeiden: "Wanneer wij gestorven zijn en stof en botten geworden, zullen wij dan opgewekt worden?'),
(5027, 56, 48, 'En onze vaderen dan, die er eertijds waren?"'),
(5028, 56, 49, 'Zeg: "Zij die er eertijds waren en zij die er later waren'),
(5029, 56, 50, 'zullen bijeengebracht worden op de afgesproken tijd van een vastgestelde dag.'),
(5030, 56, 51, 'Dan zullen jullie, o dwalers, die zeiden dat het leugens waren'),
(5031, 56, 52, 'eten van zakkoembomen'),
(5032, 56, 53, 'en daarvan de buiken vullen.'),
(5033, 56, 54, 'En dan zullen jullie daarbij gloeiend water drinken'),
(5034, 56, 55, 'en dan zullen jullie drinken als een verdorste kameel."'),
(5035, 56, 56, 'Dit is hun gastverblijf op de oordeelsdag.'),
(5036, 56, 57, 'Wij zijn het die jullie geschapen hebben. Waarom willen jullie het niet geloven?'),
(5037, 56, 58, 'Hoe zien jullie [het zaad] dat jullie uitstorten dan?'),
(5038, 56, 59, 'Zijn jullie het die het scheppen of zijn Wij de schepper?'),
(5039, 56, 60, 'Wij hebben voor jullie de dood verordend en niemand kan Ons voor zijn,'),
(5040, 56, 61, 'wanneer Wij [jullie] door gelijksoortigen willen vervangen en jullie opnieuw laten ontstaan in een vorm die jullie niet kennen.'),
(5041, 56, 62, 'Jullie kennen toch de eerste totstandkoming; waarom laten jullie je dan niet vermanen?'),
(5042, 56, 63, 'Hoe zien jullie het dan als jullie het land bewerken?'),
(5043, 56, 64, 'Zaaien jullie het in of zijn Wij het die zaaien?'),
(5044, 56, 65, 'Als Wij wilden maakten Wij het tot gruis. Dan zouden jullie verbijsterd staan te kijken:'),
(5045, 56, 66, '"Wij zijn met schulden beladen.'),
(5046, 56, 67, 'Nee, ons is alles ontroofd."'),
(5047, 56, 68, 'Hoe zien jullie het water dan dat jullie drinken?'),
(5048, 56, 69, 'Hebben jullie het uit de wolken neer laten komen of hebben Wij het neer laten dalen?'),
(5049, 56, 70, 'Als Wij wilden hadden Wij het pekelig gemaakt; waarom betuigen jullie dan geen dank?'),
(5050, 56, 71, 'Hoe zien jullie het vuur dan dat jullie ontsteken?'),
(5051, 56, 72, 'Hebben jullie de bomen ervoor laten ontstaan of hebben Wij ze laten ontstaan?'),
(5052, 56, 73, 'Wij hebben het als een vermaning gemaakt en voor de woestijnbewoners als iets om te gebruiken.'),
(5053, 56, 74, 'Prijs dan de geweldige naam van jouw Heer.'),
(5054, 56, 75, 'Niet dan, Ik zweer bij het neervallen van de sterren --'),
(5055, 56, 76, 'en dat is een geweldige eed, als jullie dat maar wisten --'),
(5056, 56, 77, 'dat het werkelijk een voortreffelijke Koran is'),
(5057, 56, 78, 'in een goedbewaard boek'),
(5058, 56, 79, 'dat slechts zij die rein gemaakt zijn zullen aanraken:'),
(5059, 56, 80, 'Een neerzending door de Heer van de wereldbewoners.'),
(5060, 56, 81, 'Is het dan over dit bericht dat jullie toegeeflijk zijn?'),
(5061, 56, 82, 'Maar jullie maken het tot jullie dagelijkse kost het te loochenen.'),
(5062, 56, 83, 'Waarom [brengen jullie de levensadem] wanneer hij de keel bereikt niet [terug],'),
(5063, 56, 84, 'terwijl jullie op die tijd toekijken?'),
(5064, 56, 85, 'Maar Wij zijn dichter bij hem dan jullie. Jullie zien het echter niet.'),
(5065, 56, 86, 'Waarom, als jullie niet geoordeeld zullen worden,'),
(5066, 56, 87, 'brengen jullie hem dan niet terug, als jullie gelijk hebben?'),
(5067, 56, 88, 'En als hij behoort tot hen die in de nabijheid van God zijn gebracht,'),
(5068, 56, 89, 'dan zijn er een koele bries, welriekende planten en een tuin van gelukzaligheid.'),
(5069, 56, 90, 'En als hij behoort tot hen die rechts staan,'),
(5070, 56, 91, 'dan is er "vrede zij met jou" van hen die rechts staan.'),
(5071, 56, 92, 'En als hij behoort tot de dwalende loochenaars,'),
(5072, 56, 93, 'dan is er een gastverblijf in gloeiend water'),
(5073, 56, 94, 'en gebraden worden in het hellevuur.'),
(5074, 56, 95, 'Dit is de vaststaande waarheid.'),
(5075, 56, 96, 'Prijs dan de geweldige naam van jouw Heer.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Wat er in de hemelen is en wat er op de aarde is prijst God en Hij is de machtige, de wijze.'),
(5077, 57, 2, 'Hij heeft de heerschappij over de hemelen en de aarde. Hij geeft leven en laat sterven en Hij is almachtig.'),
(5078, 57, 3, 'Hij is de eerste en de laatste, de zichtbare en de verborgene en Hij is alwetend.'),
(5079, 57, 4, 'Hij is het die de hemelen en de aarde in zes dagen geschapen heeft. Toen vestigde Hij zich op de troon. Hij kent wat in de aarde gaat en wat eruit komt, wat uit de hemel afdaalt en wat erin opstijgt en Hij is met jullie waar jullie zijn. God doorziet wel wat jullie doen.'),
(5080, 57, 5, 'Hij heeft de heerschappij over de hemelen en de aarde en aan God worden de zaken voorgelegd.'),
(5081, 57, 6, 'Hij laat de dag overgaan in de nacht en Hij weet wat er binnen in de harten is.'),
(5082, 57, 7, 'Gelooft in God en Zijn gezant en geeft bijdragen van datgene waarover Hij jullie als opvolgers heeft aangesteld. Zij dan uit jullie midden die geloven en bijdragen geven, voor hen is er een groot loon.'),
(5083, 57, 8, 'Wat hebben jullie toch dat jullie niet in God geloven, hoewel de gezant jullie oproept om in jullie Heer te geloven? Hij is toch een verdrag met jullie aangegaan als jullie gelovig zijn.'),
(5084, 57, 9, 'Hij is het die tot Zijn dienaar duidelijke tekenen neergezonden heeft om jullie uit de duisternis naar het licht te brengen. God is vol mededogen met jullie en barmhartig.'),
(5085, 57, 10, 'Wat hebben jullie toch dat jullie geen bijdragen op Gods weg geven? Aan God behoort immers de erfenis toe van de hemelen en de aarde. Niet gelijk [aan de anderen] zijn uit jullie midden zij die al voor het succes bijdragen gaven en streden. Zij zijn het die een hogere rang hebben dan zij die erna bijdragen zijn gaan geven en zijn gaan strijden. Maar aan allen heeft God het mooiste toegezegd. God is welingelicht over wat jullie doen.'),
(5086, 57, 11, 'Wie is het die aan God een goede lening geeft? Hij zal het dan voor hem verdubbelen en hij krijgt een voortreffelijk loon.'),
(5087, 57, 12, 'Op de dag dat jij de gelovige mannen en vrouwen ziet terwijl hun licht voor hen en rechts van hen schijnt: "Goed nieuws is er vandaag voor jullie: tuinen waar de rivieren onderdoor stromen, waarin jullie altijd zullen blijven." Dat is de geweldige triomf.'),
(5088, 57, 13, 'Op de dag dat de huichelaars en de huichelaarsters zeggen tot hen die geloven: "Wacht toch, dan kunnen wij van jullie licht [het onze] aansteken." Dan wordt er tot hen gezegd: "Gaat maar naar achteren terug en zoekt zelf maar licht." Dan wordt er tussen hen een muur opgetrokken, met een poort; aan de binnenkant ervan is barmhartigheid, maar aan de buitenkant, ervoor, is de bestraffing.'),
(5089, 57, 14, 'Zij roepen hen toe: "Waren wij niet met jullie?" Zij zeggen: "Zeker wel, maar jullie hebben jezelf in verzoeking gebracht, jullie hebben afgewacht en getwijfeld en jullie wensen hebben jullie begoocheld tot Gods beschikking kwam. De begoochelaar heeft jullie namelijk met betrekking tot God begoocheld.'),
(5090, 57, 15, 'Vandaag wordt er van jullie geen losprijs aangenomen en ook niet van hen die ongelovig zijn. Jullie verblijfplaats is het vuur; dat is jullie beschermer en dat is pas een slechte bestemming!"'),
(5091, 57, 16, 'Is het voor hen die geloven niet tijd dat hun harten zich verootmoedigen voor Gods vermaning en wat Hij van de waarheid heeft neergezonden? En weest niet zoals zij aan wie het boek vroeger al gegeven was. De tijd duurde hun toen te lang, hun harten verhardden zich en velen van hen werden verdorven.'),
(5092, 57, 17, 'Weet dat God de aarde na haar dood laat herleven. Wij hebben jullie de tekenen duidelijk gemaakt; misschien zullen jullie verstandig worden.'),
(5093, 57, 18, 'De mannen en vrouwen die aalmoezen geven en die aan God een goede lening geven; Hij zal het voor hen verdubbelen en zij krijgen een voortreffelijk loon.'),
(5094, 57, 19, 'Zij die in God en Zijn gezanten geloven, zij zijn het die de oprechten en de getuigen bij hun Heer zijn. Zij krijgen hun loon en hun licht. Maar zij die ongelovig zijn en Onze tekenen loochenen, zij zijn het die in het hellevuur thuishoren.'),
(5095, 57, 20, 'Weet dat het tegenwoordige leven slechts spel en tijdverdrijf, pracht en onderling gepraal en streven naar meer bezittingen en kinderen is. Het is bijvoorbeeld als met regen; de plantengroei die ervan komt bevalt de ongelovigen. Dan verdort het en jij ziet het geel worden en dan wordt het tot gruis. En in het hiernamaals is er een strenge bestraffing en barmhartigheid van God en welbehagen. Het tegenwoordige leven is slechts begoochelend genot.'),
(5096, 57, 21, 'Spoedt dus voort naar vergeving van jullie Heer en naar een tuin zo breed als de breedte van de hemel en de aarde, die klaargemaakt is voor hen die in God en Zijn gezanten geloven. Dat is Gods goedgunstigheid die Hij geeft aan wie Hij wil. God is vol van geweldige goedgunstigheid.'),
(5097, 57, 22, 'Geen onheil valt op de aarde of bij jullie zelf voor of het staat in een boek voordat Wij het laten gebeuren. Dat is voor God gemakkelijk.'),
(5098, 57, 23, 'Het is, opdat jullie niet bekommerd raken over wat jullie ontgaan is en je niet verheugen over wat tot jullie komt. En God bemint geen enkele ingebeelde en verwaande,'),
(5099, 57, 24, 'die gierig zijn en die de mensen opdragen gierig te zijn. En als iemand zich afkeert? God is de behoefteloze, de lofwaardige.'),
(5100, 57, 25, 'Wij hebben Onze gezanten met de duidelijke bewijzen gezonden en Wij hebben het boek en de weegschaal met hen neergezonden, opdat de mensen de rechtvaardigheid in stand houden. En Wij hebben het ijzer neergezonden. Daarin is geweldige kracht en veel nuttigheid voor de mensen, opdat God weet wie Hem en Zijn gezanten in het verborgen helpen. God is krachtig en machtig.'),
(5101, 57, 26, 'Wij hebben Noeh en Ibrahiem gezonden en Wij hebben in hun beider nageslacht het profeetschap en het boek tot stand gebracht. Sommigen volgden het goede pad, maar velen van hen waren verdorven.'),
(5102, 57, 27, 'Toen lieten Wij Onze gezanten in hun spoor volgen. Wij lieten \'Isa, de zoon van Marjam daarna volgen en gaven hem de Indjiel. En Wij legden mededogen en barmhartigheid in de harten van hen die hem volgden en ook monnikschap dat zij uitgevonden hebben. Wij hadden het hun niet voorgeschreven, maar het kwam voort uit hun streven naar Gods welbehagen. Echter zij leefden het niet op de juiste manier na. Dus gaven Wij hun uit hun midden die geloofden hun loon, maar velen van hen waren verdorven.'),
(5103, 57, 28, 'Jullie die geloven! Vreest God en gelooft in Zijn gezant zodat Hij jullie twee porties van Zijn barmhartigheid geeft, voor jullie een licht maakt waarin jullie kunnen wandelen en jullie vergeeft; God is vergevend en barmhartig.'),
(5104, 57, 29, 'De mensen van het boek moeten goed weten dat zij niet de beschikking hebben over iets van Gods goedgunstigheid en dat de goedgunstigheid in Gods hand is; Hij geeft die aan wie Hij wil en God is vol van geweldige goedgunstigheid.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'God heeft haar wel horen spreken die met jou over haar echtgenoot twist en tot God klaagt. God hoort namelijk jullie gesprek; God is horend en doorziend.'),
(5106, 58, 2, 'Zij die zich uit jullie midden van hun vrouwen scheiden [door uit te spreken dat zij als hun moeders zijn] -- zij zijn niet [werkelijk] hun moeders, want hun moeders zijn slechts zij die hen gebaard hebben en zij zeggen iets verwerpelijks en onwaars, maar God is lankmoedig en vergevend --'),
(5107, 58, 3, 'zij dus die zich van hun vrouwen scheiden [door uit te spreken dat zij als hun moeders zijn] en dan terugkomen op wat zij zeggen, [zijn verplicht tot] vrijlating van een slaaf voordat zij elkaar aanraken. Daartoe worden jullie aangespoord; God is welingelicht over wat jullie doen.'),
(5108, 58, 4, 'En als iemand [daarvoor] geen mogelijkheid vindt dan [is hij verplicht tot] een vasten van twee maanden achter elkaar voordat zij elkaar aanraken. En als iemand [daartoe] niet in staat is, dan [is hij verplicht] aan zestig behoeftigen voedsel te geven. Dat is opdat jullie in God en Zijn gezant geloven en dat zijn Gods bepalingen. En voor de ongelovigen is er een pijnlijke bestraffing.'),
(5109, 58, 5, 'Zij die zich tegen God en Zijn gezant verzetten worden te schande gemaakt zoals zij die er voor hen waren te schande zijn gemaakt. Wij hebben immers duidelijke tekenen neergezonden. En voor de ongelovigen is er een vernederende bestraffing,'),
(5110, 58, 6, 'op de dag dat God hen allen opwekt. Hij zal hun dan meedelen wat zij gedaan hebben. God heeft het opgesomd, maar zij hebben het vergeten. God is van alles getuige.'),
(5111, 58, 7, 'Heb jij dan niet gezien dat God weet wat er in de hemelen en wat er op de aarde is? Er is geen vertrouwelijk gesprek van drie of God is de vierde, en niet van vijf of Hij is de zesde en ook als het er minder zijn of meer, dan is Hij met hen waar zij ook zijn. Dan zal Hij hun op de opstandingsdag meedelen wat zij gedaan hebben. God is alwetend.'),
(5112, 58, 8, 'Heb jij dan niet gezien naar hen aan wie het verboden was vertrouwelijke gesprekken te voeren en die dan terugkeren tot wat hun verboden was en dat zij vertrouwelijke gesprekken houden over zonde, overtreding en ongehoorzaamheid aan de gezant? En als jij bij hen komt groeten zij jou zoals God jou niet groet en zij zeggen bij zichzelf: "Als God ons maar niet bestraft voor wat wij zeggen." Voor hen is de hel goed genoeg, waarin zij zullen braden en dat is pas een slechte bestemming!'),
(5113, 58, 9, 'Jullie die geloven! Wanneer jullie vertrouwelijk met elkaar spreken spreekt dan niet vertrouwelijk over zonde, overtreding en ongehoorzaamheid aan de gezant, maar spreekt vertrouwelijk over vroomheid en godvrezendheid. En vreest God tot wie jullie verzameld zullen worden.'),
(5114, 58, 10, 'Vertrouwelijke gesprekken zijn van de satan om hen die geloven bedroefd te maken. Maar hij kan hen in niets schaden als het niet met Gods toestemming is. En op God moeten de gelovigen dus hun vertrouwen stellen.'),
(5115, 58, 11, 'Jullie die geloven! Wanneer men tot jullie zegt: "Maakt plaats bij de samenkomsten" maakt dan plaats opdat God voor jullie plaats maakt. En wanneer men tot jullie zegt: "Staat op" staat dan op, opdat God aan hen die uit jullie midden geloven en aan wie de kennis is gegeven hogere rangen geeft. God is welingelicht over wat jullie doen.'),
(5116, 58, 12, 'Jullie die geloven! Wanneer jullie vertrouwelijk met de gezant willen spreken, brengt dan voorafgaand aan jullie vertrouwelijke gesprekken een aalmoes. Dat is beter voor jullie en reiner. Maar als jullie niets te geven vinden dan is God vergevend en barmhartig.'),
(5117, 58, 13, 'Schrikken jullie ervoor terug dat jullie voorafgaand aan jullie vertrouwelijke gesprekken aalmoezen moeten brengen? En wanneer jullie het niet doen en God zich genadig tot jullie wendt, verricht dan de salaat, geeft de zakaat en gehoorzaamt God en Zijn gezant. Maar God is welingelicht over wat jullie doen.'),
(5118, 58, 14, 'Heb jij dan niet gezien naar hen die mensen op wie God vertoornd is als medestanders nemen? Zij horen niet bij jullie en ook niet bij hen. Zij zweren willens en wetens meineden.'),
(5119, 58, 15, 'God heeft voor hen een strenge bestraffing klaargemaakt. Het is slecht wat zij aan het doen waren.'),
(5120, 58, 16, 'Zij hebben hun eden als bescherming gebruikt en zo de weg van God versperd. Voor hen is er dus een vernederende bestraffing.'),
(5121, 58, 17, 'Hun zullen hun bezittingen en hun kinderen bij God volstrekt niet baten. Zij zijn het die in het vuur thuishoren, waarin zij altijd zullen blijven,'),
(5122, 58, 18, 'op de dag dat God hen allen opwekt. Zij zullen dan tot Hem zweren zoals zij tot jullie zweren en zij gaan ervan uit dat het ergens op gebaseerd is. Toch zijn zij de leugenaars.'),
(5123, 58, 19, 'De satan heeft hen overmeesterd en hun Gods vermaning laten vergeten. Zij zijn de partij van de satan. En de partij van de satan, dat zijn de verliezers.'),
(5124, 58, 20, 'Zij die zich tegen God en Zijn gezant verzetten, zij zijn het die tot de diepst vernederden behoren.'),
(5125, 58, 21, 'God heeft opgeschreven: "Ik en Mijn gezant zullen zeker overwinnen." God is krachtig en machtig.'),
(5126, 58, 22, 'Jij zult geen mensen vinden die in God en de laatste dag geloven die vriendschappelijk omgaan met hen die zich tegen God en Zijn gezant verzetten, ook al zijn het hun vaders of hun zonen of hun broers of hun familieleden. Zij zijn het in wier harten Hij het geloof geschreven heeft en Hij versterkt hen met een geest bij Hem vandaan. En Hij laat hen tuinen binnengaan waar de rivieren onderdoor stromen; daarin zullen zij altijd blijven. God is met hen ingenomen en zij zijn met Hem ingenomen. Dat zijn zij, de partij van God. En de partij van God, dat zijn zij die het welgaat.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Wat er in de hemelen is en wat er op de aarde is prijst God en Hij is de machtige, de wijze.'),
(5128, 59, 2, 'Hij is het die de ongelovigen van de mensen van het boek bij de eerste verzameling uit hun woningen heeft verdreven. Jullie dachten niet dat zij zouden vertrekken en zij dachten dat hun versterkingen hen tegen God konden beschermen. Maar God kwam tot hen waar zij er niet op rekenden en Hij joeg hun harten schrik aan zodat hun huizen door hen eigenhandig en door de handen van de gelovigen verwoest werden. Leert dan de les daaruit, jullie die inzicht hebben.'),
(5129, 59, 3, 'En als God voor hen niet de verbanning had voorgeschreven dan had Hij hen in de tegenwoordige wereld bestraft, maar in het hiernamaals is er voor hen de bestraffing van het vuur.'),
(5130, 59, 4, 'Dat is omdat zij God en Zijn gezant tegenwerken. En als iemand God tegenwerkt, dan is God streng in de afstraffing.'),
(5131, 59, 5, 'Dat jullie palmen hebben omgehakt en op hun wortels hebben laten staan, gebeurde met Gods toestemming en opdat Hij de verdorvenen te schande maakte.'),
(5132, 59, 6, 'Wat God Zijn gezant aan van hen afkomstige buit gegeven heeft, dat hoefden jullie niet aan te sporen, paarden noch andere rijdieren. Maar God laat zijn gezant heersen over wie Hij wil; God is almachtig.'),
(5133, 59, 7, 'En wat God Zijn gezant aan van de bewoners van de steden afkomstige buit gegeven heeft behoort God en Zijn gezant toe en de verwanten, de wezen, de behoeftigen en hij die onderweg is, opdat het niet in omloop komt bij de rijken onder jullie. Wat de gezant jullie geeft, neemt dat, maar wat hij jullie ontzegt, blijft daarvan af. En vreest God, want God is streng in de afstraffing.'),
(5134, 59, 8, 'Het is ten bate van de arme uitgewekenen die uit hun woningen en van hun bezittingen verdreven zijn, want zij streven naar goedgunstigheid van God en welgevallen en zij helpen God en Zijn gezant; zij zijn de oprechten.'),
(5135, 59, 9, 'En zij die al eerder dan zij in de woning gevestigd en in het geloof bevestigd waren beminnen hen die naar hen uitgeweken zijn; zij ondervinden in hun binnenste geen behoefte aan wat hun gegeven wordt en zij verkiezen hen boven zichzelf ook al hebben zij zelf gebrek. En wie voor de eigen hebzucht behoed worden, zij zijn het die het welgaat.'),
(5136, 59, 10, 'En zij die na hen gekomen zijn zeggen: "Onze Heer, vergeef ons en onze broeders die ons in het geloof zijn voorgegaan en laat geen wrok in onze harten komen jegens hen die geloven. Onze Heer, U bent vol mededogen en barmhartig."'),
(5137, 59, 11, 'Heb jij niet gezien naar hen die huichelen? Zij zeggen tot hun ongelovige broeders onder de mensen van het boek: "Als jullie verdreven worden zullen wij met jullie wegtrekken en wij zullen in iets wat jullie aangaat nooit iemand anders gehoorzamen en als men tegen jullie strijdt zullen wij jullie helpen." Maar God getuigt dat zij leugenaars zijn.'),
(5138, 59, 12, 'Als zij verdreven worden zullen zij niet met hen wegtrekken en als men tegen hen strijdt, dan helpen zij hen niet en als zij al helpen, dan zullen zij zich omkeren om te vluchten. Dan zullen zij geen hulp krijgen.'),
(5139, 59, 13, 'Jullie boezemen hun nog meer angst in dan God. Dat komt omdat zij mensen zijn die geen begrip hebben.'),
(5140, 59, 14, 'Gezamenlijk bestrijden zij jullie slechts in versterkte steden of van achter muren. Hun onderling geweld is hevig; jij denkt dat zij gezamenlijk optreden, maar hun harten zijn verdeeld. Dat komt omdat zij mensen zijn die geen verstand hebben.'),
(5141, 59, 15, 'Zoals bijvoorbeeld zij die kort voor hen het walgelijke van hun gedrag geproefd hebben; voor hen is er een pijnlijke bestraffing.'),
(5142, 59, 16, 'Zoals bijvoorbeeld de satan, wanneer hij tot de mens zegt: "Wees ongelovig." En als hij ongelovig is geworden zegt hij: "Ik heb met jullie niets te maken, ik vrees God, de Heer van de wereldbewoners."'),
(5143, 59, 17, 'Maar hun beider einde is dat zij in het vuur terechtkomen waarin zij altijd zullen blijven. En dat is de vergelding voor de onrechtplegers.'),
(5144, 59, 18, 'Jullie die geloven! Vreest God en ieder moet bekijken wat hij voor de dag van morgen voorbereidt. En vreest God, want God is welingelicht over wat jullie doen.'),
(5145, 59, 19, 'En weest niet als zij die God vergeten hebben en die Hij dan zichzelf vergeten liet; zij zijn het die verdorven zijn.'),
(5146, 59, 20, 'Zij die in het vuur thuishoren zijn niet gelijk aan hen die in de tuin thuishoren, want zij zijn de triomferenden.'),
(5147, 59, 21, 'Als Wij deze Koran tot een berg hadden neergezonden, dan had jij hem zich zien verootmoedigen en uit vrees voor God zien barsten. Dit zijn de vergelijkingen die Wij voor de mensen maken; misschien zullen zij nadenken.'),
(5148, 59, 22, 'Hij is God; er is geen god dan Hij, de kenner van het verborgene en het waarneembare, de erbarmer, de barmhartige.'),
(5149, 59, 23, 'Hij is God; er is geen god dan Hij, de koning, de allerheiligste, de instandhouder, de veiligheidgever, de bewaker, de machtige, de geweldige, de trotse. Geprezen zij God, verheven als Hij is boven wat zij aan Hem als metgezellen toevoegen.'),
(5150, 59, 24, 'Hij is God, de schepper, de maker, de vormgever. Hem komen de mooiste namen toe. Hem prijst wat er in de hemelen is en wat er op de aarde is en Hij is de machtige, de wijze.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'Jullie die geloven! Neemt mijn en jullie vijanden niet als medestanders waarbij jullie hun blijken van genegenheid geven. Zij hechten geen geloof aan de waarheid die tot jullie gekomen is en zij verdrijven de gezant en jullie omdat jullie in God, jullie Heer geloven. Als jullie uittrekken om jullie op Mijn weg in te zetten en om naar Mijn welbehagen te streven, terwijl jullie hun in het geheim genegenheid tonen, dan weet Ik het best wat jullie in het verborgen en wat jullie openlijk doen. En wie van jullie het doet is van de correcte weg afgedwaald.'),
(5152, 60, 2, 'Als zij jullie aantreffen zijn zij vijanden voor jullie; zij strekken hun handen en hun tongen met slechte bedoelingen naar jullie uit en zouden graag willen dat jullie ongelovig werden.'),
(5153, 60, 3, 'Jullie verwantschapsbanden noch jullie kinderen zullen jullie van nut zijn; op de opstandingsdag zal Hij tussen jullie scheiding aanbrengen. En God doorziet wel wat jullie doen.'),
(5154, 60, 4, 'Jullie hebben toch een goed voorbeeld in Ibrahiem en hen die met hem waren, toen zij tot hun volk zeiden: "Wij hebben niets te maken met jullie en wat jullie in plaats van God dienen. Wij hechten geen geloof aan jullie. Tussen jullie en ons is de vijandschap en de haat voor altijd openlijk zichtbaar geworden, totdat jullie in God alleen geloven", behalve dan in wat Ibrahiem tot zijn vader zei: "Ik zal voor jou om vergeving vragen, maar ik kan ten gunste van jou niets tegen God uitrichten. Onze Heer, op U stellen wij ons vertrouwen en tot U wenden wij ons schuldbewust en bij U is de bestemming.'),
(5155, 60, 5, 'Onze Heer maak ons niet tot een verzoeking voor hen die ongelovig zijn en vergeef ons, onze Heer, want U bent de machtige, de wijze."'),
(5156, 60, 6, 'Inderdaad hebben jullie in hen een goed voorbeeld, voor wie op God en de laatste dag hopen, maar als iemand zich afkeert? God is de behoefteloze, de lofwaardige.'),
(5157, 60, 7, 'Misschien dat God tussen jullie en hen die jullie als vijand beschouwen genegenheid zal brengen; God is vrijmachtig en God is vergevend en barmhartig.'),
(5158, 60, 8, 'God verbiedt niet dat jullie hen die niet wegens de godsdienst tegen jullie gestreden hebben en die jullie niet uit jullie woningen verdreven hebben, met respect en rechtvaardig behandelen. God bemint hen die rechtvaardig handelen.'),
(5159, 60, 9, 'Maar God verbiedt dat jullie hun die wegens de godsdienst tegen jullie gestreden hebben, die jullie uit jullie huizen verdreven en die bij jullie verdrijving geholpen hebben, bijstand verlenen. En zij die hun bijstand verlenen, zij zijn de onrechtplegers.'),
(5160, 60, 10, 'Jullie die geloven! Wanneer gelovige vrouwen als uitgewekenen tot jullie komen, stelt haar dan op de proef. God kent haar geloof het best. En wanneer jullie weten dat zij gelovig zijn zendt haar dan niet terug naar de ongelovigen. Zij zijn aan hen niet toegestaan [om mee getrouwd te zijn] en zij zijn voor haar niet toegestaan. En geeft aan haar wat jullie als bijdragen geeft. En het is geen overtreding voor jullie als jullie met haar trouwen, wanneer jullie haar haar loon geven. En houdt niet vast aan de huwelijksbanden met de ongelovige vrouwen. En vraagt om de bijdrage die jullie gegeven hebben; zij vragen ook om de bijdrage die zij gegeven hebben. Dat is Gods oordeel; Hij oordeelt tussen jullie. God is wetend en wijs.'),
(5161, 60, 11, 'En als echtgenotes van jullie er naar de ongelovigen vandoor gaan en als jullie dan een strafexpeditie houden geeft dan aan hen bij wie hun echtgenotes zijn weggegaan zoveel als wat zij als bijdrage hebben gegeven. En vreest God in wie jullie geloven.'),
(5162, 60, 12, 'O profeet, wanneer de gelovige vrouwen tot jou komen om jou trouw te zweren met de verplichting niets als metgezel aan God toe te voegen, niet te stelen, geen overspel te plegen, hun kinderen niet te doden, niet met lasterlijke slechtheid aan te komen die zij eigenhandig bij de geboorte verzinnen en jou in het redelijke niet ongehoorzaam te zijn, neem haar eed van trouw dan aan en vraag God voor haar om vergeving; God is vergevend en barmhartig.'),
(5163, 60, 13, 'Jullie die geloven! Neemt geen medestanders op wie God vertoornd is. Zij hebben geen hoop voor het hiernamaals zoals de ongelovigen geen hoop hebben voor hen die in de graven zijn.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Wat er in de hemelen is en wat er op de aarde is prijst God en Hij is de machtige, de wijze.'),
(5165, 61, 2, 'Jullie die geloven! Waarom zeggen jullie wat jullie niet doen.'),
(5166, 61, 3, 'Het wekt bij God grote afschuw op als jullie zeggen wat jullie niet doen.'),
(5167, 61, 4, 'God bemint hen die in gesloten gelid op Zijn weg strijden alsof zij een hecht samengevoegd gebouw zijn.'),
(5168, 61, 5, 'Toen Moesa tot zijn volk zei: "Mijn volk! Waarom vallen jullie mij lastig. Jullie weten toch dat ik de gezant van God bij jullie ben." Maar toen zij naar het verkeerde neigden, liet God hun harten naar het verkeerde neigen. God wijst de verdorven mensen de goede richting niet.'),
(5169, 61, 6, 'Toen \'Isa, de zoon van Marjam zei: "O Israëlieten, ik ben de gezant van God bij jullie om te bevestigen wat er van de Taura voor mijn tijd al was en om het goede nieuws te verkondigen van een gezant die na mij zal komen en van wie de naam Ahmad zal zijn." Maar toen hij met de duidelijke bewijzen tot hen kwam zeiden zij: "Dit is duidelijk toverij."'),
(5170, 61, 7, 'En wie is er zondiger dan wie over God bedrog verzint, terwijl hij tot de overgave [aan God] is opgeroepen. God wijst de mensen die onrecht plegen de goede richting niet.'),
(5171, 61, 8, 'Zij wensen Gods licht met hun monden uit te doven, maar God vervolmaakt Zijn licht, ook al staat het de ongelovigen tegen.'),
(5172, 61, 9, 'Hij is het die Zijn gezant met de leidraad en de ware godsdienst gezonden heeft om hem te laten zegevieren over de gehele godsdienst, ook al staat het de veelgodendienaars tegen.'),
(5173, 61, 10, 'Jullie die geloven! Zal ik jullie op een handel wijzen die jullie van een pijnlijke bestraffing redt?'),
(5174, 61, 11, 'Jullie moeten in God en Zijn gezant geloven en jullie op Gods weg met jullie bezittingen en jullie eigen persoon inzetten. Dat is beter voor jullie, als jullie dat maar wisten!'),
(5175, 61, 12, 'Hij zal jullie je zonden vergeven en jullie in tuinen laten binnengaan waar de rivieren onderdoor stromen en in goede woningen in de tuinen van \'Adn. Dat is de geweldige triomf!'),
(5176, 61, 13, 'En nog iets anders wat jullie graag willen: hulp van God en een spoedig succes. Verkondig het goede nieuws aan de gelovigen.'),
(5177, 61, 14, 'Jullie die geloven! Weest Gods helpers. Zoals \'Isa, de zoon van Marjam, tot zijn discipelen zei: "Wie zijn mijn helpers [op de weg] tot God?" De discipelen zeiden: "Wij zijn Gods helpers." Een groep van de Israëlieten geloofde dus, maar een andere groep bleef ongelovig. En Wij ondersteunden hen die geloofden tegen hun vijanden en zij kregen dus de heerschappij.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Wat er in de hemelen is en wat er op de aarde is prijst God, de koning, de allerheiligste, de machtige en de wijze.'),
(5179, 62, 2, 'Hij is het die bij de ongeletterden een gezant uit hun midden heeft laten opstaan die aan hen Zijn tekenen voorleest, die hen loutert en die hun het boek en de wijsheid onderwijst, ook al verkeerden zij vroeger in duidelijke dwaling,'),
(5180, 62, 3, 'en ook anderen dan hen die zich nog niet bij hen gevoegd hebben. Hij is de machtige, de wijze.'),
(5181, 62, 4, 'Dat is Gods goedgunstigheid die Hij geeft aan wie Hij wil; God is vol van geweldige goedgunstigheid.'),
(5182, 62, 5, 'Zij aan wie de Taura is opgelegd en die het daarna toch niet konden dragen lijken bijvoorbeeld op een ezel die boeken draagt. Het voorbeeld van de mensen die Gods tekenen loochenen dat is pas slecht. God wijst de mensen die onrecht plegen de goede richting niet.'),
(5183, 62, 6, 'Zeg: "Jullie die het jodendom aanhangen! Als jullie beweren dat jullie Gods vrienden zijn en de andere mensen niet, wenst dan de dood als jullie gelijk hebben."'),
(5184, 62, 7, 'Maar dat zullen zij nooit wensen, wegens wat hun handen eerder hebben gedaan. God kent de onrechtplegers.'),
(5185, 62, 8, 'Zeg: "De dood waarvoor jullie vluchten zal jullie inhalen. Dan zullen jullie naar de Kenner van het verborgene en het waarneembare worden teruggebracht. Hij zal jullie dan meedelen wat jullie aan het doen waren."'),
(5186, 62, 9, 'Jullie die geloven! Wanneer jullie tot de salaat op de vrijdag [als de dag van de samenkomst] worden opgeroepen, haast jullie dan om God te gedenken en laat het zakendoen. Dat is beter voor jullie, als jullie dat maar wisten.'),
(5187, 62, 10, 'En wanneer de salaat beëindigd is, gaat dan weer uit elkaar het land in, streeft naar Gods gunst en gedenk God veel; misschien zal het jullie welgaan.'),
(5188, 62, 11, 'Maar wanneer zij koopwaar zien of een tijdverdrijf gaan zij erachteraan en zij laten jou staan. Zeg: "Wat bij God is, is beter dan tijdverdrijf en koopwaar en God is werkelijk de beste voorziener."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Wanneer de huichelaars tot jou komen zeggen zij: "Wij getuigen dat jij Gods gezant bent." God weet dat jij inderdaad Gods gezant bent en God getuigt dat de huichelaars leugenaars zijn.'),
(5190, 63, 2, 'Zij hebben hun eden als bescherming gebruikt en zo Gods weg versperd. Het is slecht wat zij aan het doen waren.'),
(5191, 63, 3, 'Dat komt omdat zij geloofd hebben en toen ongelovig werden; hun harten zijn verzegeld, dus hebben zij geen begrip. *'),
(5192, 63, 4, 'Maar wanneer jij hen ziet, bevallen jou hun lichamen en als zij spreken luister jij naar wat zij zeggen. Zij zijn als balken die gestut moeten worden. Zij denken dat elke schreeuw tegen hen gericht is. Zij zijn de vijand; wees voor hen op je hoede. God bestrijde hen, hoe kunnen zij zo afwijken!'),
(5193, 63, 5, 'En wanneer tot hen gezegd wordt: "Komt, dan zal Gods gezant voor jullie om vergeving vragen" dan wenden zij hun hoofden af en dan zie jij hen zich in hun hoogmoed afkeren.'),
(5194, 63, 6, 'Voor hen maakt het niet uit of jij om vergeving voor hen vraagt of dat jij niet om vergeving voor hen vraagt, God zal hun niet vergeven; God wijst de verdorven mensen de goede richting niet.'),
(5195, 63, 7, 'Zij zijn het die zeggen: "Geeft geen bijdragen aan wie er bij Gods gezant horen, voordat zij er vandoor gaan." Echter, van God zijn de schatkamers van de hemelen en de aarde, maar de huichelaars hebben geen begrip.'),
(5196, 63, 8, 'Zij zeggen: "Als wij terugkeren naar Medina, dan zullen de machtigen de onderworpelingen eruit verdrijven." Echter, van God is de macht en van Zijn gezant en de gelovigen, maar de huichelaars weten het niet.'),
(5197, 63, 9, 'Jullie die geloven! Jullie bezittingen en jullie kinderen moeten jullie er niet van afleiden God te gedenken. Wie dat doen, dat zijn dus de verliezers.'),
(5198, 63, 10, 'En geeft bijdragen van wat Wij jullie voor jullie levensonderhoud gegeven hebben, voordat de dood tot een van jullie komt en hij dan moet zeggen: "Mijn Heer, had U mij niet nog een kort uitstel kunnen verlenen, dan zou ik nog aalmoezen kunnen geven en bij de rechtschapenen behoren."'),
(5199, 63, 11, 'Maar God zal niemand uitstel geven, wanneer zijn termijn is gekomen; God is welingelicht over wat jullie doen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Wat er in de hemelen en op de aarde is prijst God. Hij heeft de heerschappij en Hem zij lof; Hij is almachtig.'),
(5201, 64, 2, 'Hij is het die jullie geschapen heeft, maar sommigen van jullie zijn ongelovig en anderen zijn gelovig; God doorziet wel wat jullie doen.'),
(5202, 64, 3, 'Hij heeft de hemelen en de aarde in waarheid geschapen en jullie gevormd; Hij heeft jullie een mooie vorm gegeven. En bij Hem is de bestemming.'),
(5203, 64, 4, 'Hij weet wat er in de hemelen en op de aarde is en Hij weet wat jullie in het geheim en wat jullie openlijk doen; God weet wat er binnen in de harten is.'),
(5204, 64, 5, 'Is tot jullie niet de mededeling gekomen over hen die vroeger ongelovig waren? Zij proefden het walgelijke van hun gedrag en voor hen was er een pijnlijke bestraffing.'),
(5205, 64, 6, 'Dat kwam omdat zij, hoewel hun gezanten met de duidelijke bewijzen tot hen gekomen waren, gezegd hadden: "Zullen mensen ons op het goede pad brengen?" Zij waren ongelovig en keerden zich af. Maar God kon het zonder hen stellen; God is behoefteloos en lofwaardig.'),
(5206, 64, 7, 'Zij die ongelovig zijn beweren dat zij niet zullen worden opgewekt. Zeg: "Integendeel, jullie zullen worden opgewekt en dan zal aan jullie worden meegedeeld wat jullie gedaan hebben. Dat is voor God gemakkelijk."'),
(5207, 64, 8, 'Gelooft dus in God, Zijn gezant en het licht dat Hij heeft neergezonden. God is welingelicht over wat jullie doen.'),
(5208, 64, 9, 'De dag dat Hij jullie bijeen zal brengen voor de dag van de bijeenbrenging. Dat is de dag van de zwendel. Wie in God gelooft en deugdelijk handelt verzoent Hij zijn slechte daden en laat hem in tuinen binnengaan waar de rivieren onderdoor stromen; zij zullen daarin altijd blijven. Dat is de grote triomf.'),
(5209, 64, 10, 'Maar zij die ongelovig zijn en Onze tekenen loochenen, zij zijn het die in het vuur thuishoren; zij zullen daarin altijd blijven. Dat is pas een slechte bestemming!'),
(5210, 64, 11, 'Er vindt geen onheil plaats zonder dat het met Gods toestemming is. En als iemand in God gelooft zal Hij zijn hart de goede weg wijzen. God is alwetend.'),
(5211, 64, 12, 'Gehoorzaamt God en gehoorzaamt de gezant, maar als jullie je afkeren, dan heeft Onze gezant alleen maar de plicht van de duidelijke verkondiging.'),
(5212, 64, 13, 'God, er is geen god dan Hij en op God moeten de gelovigen hun vertrouwen stellen.'),
(5213, 64, 14, 'Jullie die geloven! Onder jullie vrouwen en jullie kinderen zijn er die jullie tot vijand zijn. Hoedt jullie dus voor hen, maar als jullie het niet aanrekenen, het kwijtschelden en vergeven, dan is God vergevend en barmhartig.'),
(5214, 64, 15, 'Jullie bezittingen en jullie kinderen zijn een verzoeking voor jullie en God, bij Hem is een geweldig loon.'),
(5215, 64, 16, 'Vreest dus God zo goed als jullie kunnen en luistert, gehoorzaamt en geeft bijdragen; dat is beter voor jullie zelf. En wie voor de eigen hebzucht behoed worden, dat zijn zij die het welgaat.'),
(5216, 64, 17, 'En als jullie aan God een goede lening geven zal Hij het voor jullie verdubbelen en Hij zal jullie vergeven; God is dankbetuigend en zachtmoedig,'),
(5217, 64, 18, 'de kenner van het verborgene en het waarneembare, de machtige, de wijze.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'O profeet! Wanneer jullie je van vrouwen scheiden, doet dat dan met inachtneming van haar wachttijd en berekent de wachttijd precies. En vreest God, jullie Heer. Jullie mogen haar niet uit haar huizen zetten; zij hoeven er pas uit te gaan als zij een duidelijke gruweldaad begaan hebben. Dat zijn Gods bepalingen. Wie Gods bepalingen overtreedt doet zichzelf onrecht. Jij weet niet of God daarna misschien nog nieuwe omstandigheden brengt.'),
(5219, 65, 2, 'En als zij hun termijn bereiken, neemt haar dan in redelijkheid terug of scheidt in redelijkheid van haar. En laat twee rechtvaardigen uit jullie midden getuige zijn en houdt het getuigenis voor God staande. Daartoe wordt wie in God en de laatste dag gelooft aangespoord. En wie God vreest, die zal Hij een uitweg verschaffen.'),
(5220, 65, 3, 'En Hij zal in zijn levensonderhoud voorzien vanwaar hij er niet op rekent. En wie op God zijn vertrouwen stelt, voor die is dat goed genoeg. God bereikt Zijn doel. God heeft voor alles een maat vastgesteld.'),
(5221, 65, 4, 'En die vrouwen van jullie die niet meer op de menstruatie hopen hebben, als jullie twijfelen, een wachttijd van drie maanden en ook zij die nog niet gemenstrueerd hebben. En voor de zwangeren is haar termijn dat zij baren waarvan zij zwanger zijn. En voor hem die God vreest zal Hij de omstandigheden gemakkelijker maken.'),
(5222, 65, 5, 'Dat is de beschikking van God die Hij naar jullie heeft neergezonden. En als iemand God vreest, dan verzoent Hij zijn slechte daden en maakt zijn loon geweldig.'),
(5223, 65, 6, 'Laat haar wonen waar jullie wonen, naar jullie vermogen. Brengt haar geen schade toe om haar in het nauw te brengen. Als zij zwanger zijn geeft haar dan bijdragen totdat zij baren waarvan zij zwanger zijn. En als zij [jullie kinderen] voor jullie zogen, geeft haar dan haar loon. En doet elkaar redelijke aanbevelingen. Maar als jullie het moeilijk met elkaar eens worden, dan zal een andere vrouw [het kind] voor hem moeten zogen.'),
(5224, 65, 7, 'De welgestelde moet naar zijn welstand bijdragen geven en wie met mate levensonderhoud is toebedeeld moet bijdragen geven van wat God hem gegeven heeft. God legt niemand meer op dan Hij hem gegeven heeft. God zal het na de moeite gemakkelijk maken.'),
(5225, 65, 8, 'Hoeveel steden hebben niet het bevel van hun Heer en Zijn gezanten geminacht, waarmee Wij toen streng hebben afgerekend en die Wij met een kwalijke bestraffing hebben gestraft.'),
(5226, 65, 9, 'Zij proefden toen het walgelijke van hun gedrag en hun einde was een verloren zaak.'),
(5227, 65, 10, 'God heeft voor hen een strenge bestraffing klaargemaakt. Vreest God dus, o verstandigen die geloven! God heeft naar jullie een vermaning neergezonden,'),
(5228, 65, 11, 'een gezant die aan jullie Gods tekenen als duidelijke bewijzen voorleest om hen die geloven en de deugdelijke daden doen uit de duisternis naar het licht te brengen. En wie in God gelooft en deugdelijk handelt, die zal Hij tuinen laten binnengaan waar de rivieren onderdoor stromen; daarin zullen zij altijd en immer blijven. God heeft een goede voorziening voor hem getroffen.'),
(5229, 65, 12, 'God is het die zeven hemelen geschapen heeft en van de aarde evenveel, waarbij de ordening [van God] ertussen neerdaalde opdat jullie weten dat God almachtig is en dat God alles met Zijn kennis omvat.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'O profeet! Waarom verklaar jij verboden wat God heeft toegestaan om je vrouwen tevreden te stellen? God is vergevend en barmhartig.'),
(5231, 66, 2, 'God heeft voor jullie bepaald hoe jullie je eden moeten ontbinden. God is jullie beschermheer en Hij is de wijze, de wetende.'),
(5232, 66, 3, 'Toen de profeet aan een van zijn echtgenotes in vertrouwen een gebeurtenis meedeelde en zij het toch verder vertelde en God het hem openbaar maakte, maakte hij het deels bekend en liet het deels onvermeld. Toen hij het haar meedeelde zei zij: "Wie heeft dat aan jou meegedeeld?" Hij zei: "De wetende, de welingelichte heeft het mij meegedeeld."'),
(5233, 66, 4, 'Als jullie je beiden berouwvol tot God wenden, dan waren jullie harten afgedwaald. Maar als jullie beiden elkaar bijstaan tegen hem, dan is God zijn beschermheer en Djibriel, en verder zullen de rechtschapen gelovigen en de engelen ook bijstand verlenen.'),
(5234, 66, 5, 'Als hij zich van jullie scheidt zal zijn Heer hem misschien wel andere echtgenotes, die beter zijn dan jullie, voor jullie in de plaats geven, vrouwen die zich [aan God] overgeven, die gelovig, onderdanig, berouwhebbend, dienend en vastend zijn, die al eerder getrouwd geweest zijn of die nog maagd zijn.'),
(5235, 66, 6, 'Jullie die geloven! Beschermt jullie zelf en jullie huisgenoten voor een vuur waarvan mensen en stenen de brandstof zijn. Hardhandige en strenge engelen zijn ermee belast die God niet ongehoorzaam zijn in wat Hij hun beveelt en die doen wat hun bevolen is.'),
(5236, 66, 7, 'Jullie die ongelovig zijn! Verontschuldigt jullie vandaag niet. Aan jullie wordt vergolden wat jullie gedaan hebben.'),
(5237, 66, 8, 'Jullie die geloven! Wendt jullie met oprecht berouw tot God, misschien dat jullie Heer jullie slechte daden zal verzoenen en jullie tuinen zal laten binnengaan waar de rivieren onderdoor stromen, op de dag dat God de profeet en hen die met hem geloven niet te schande zal maken; hun licht schijnt voor hen en rechts van hen, terwijl zij zeggen: "Onze Heer vervolmaak ons licht voor ons en schenk ons vergeving. U bent almachtig."'),
(5238, 66, 9, 'O profeet, stel je te weer tegen de ongelovigen en de huichelaars en pak hen stevig aan. Hun verblijfplaats is de hel; dat is pas een slechte bestemming!'),
(5239, 66, 10, 'God heeft voor hen die ongelovig zijn de vrouw van Noeh en de vrouw van Loet als een voorbeeld gegeven. Zij stonden onder de hoede van twee van Onze rechtschapen dienaren, maar zij hebben hen verraden. Dus hadden zij tegen God geheel geen baat van hen en er werd gezegd: "Gaat beiden met de anderen het vuur binnen."'),
(5240, 66, 11, 'God heeft voor hen die gelovig zijn de vrouw van Fir\'aun als een voorbeeld gegeven toen zij zei: "Mijn Heer bouw voor mij bij U een huis in de tuin en red mij van Fir\'aun en wat hij doet en red mij van de mensen die onrecht plegen."'),
(5241, 66, 12, 'En Marjam, de dochter van \'Imraan die haar eerbaarheid bewaarde. Toen bliezen Wij er iets van Onze geest in en zij geloofde de woorden van haar Heer en Zijn boeken; zij behoorde tot de onderdanigen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Gezegend zij Hij in wiens hand de heerschappij is en Hij is almachtig.'),
(5243, 67, 2, 'Hij die de dood en het leven geschapen heeft om jullie op de proef te stellen wie van jullie het beste is bij wat hij doet; Hij is de machtige, de vergevende.'),
(5244, 67, 3, 'Hij die zeven hemelen in lagen heeft geschapen. Je ziet in de schepping van de erbarmer geen tekortkoming. Kijk dan eens om of jij onvolkomenheden ziet.'),
(5245, 67, 4, 'En kijk nog eens tweemaal om, dan zul je jouw ogen beschaamd neerslaan, vermoeid als zij zijn.'),
(5246, 67, 5, 'En Wij hebben de nabije hemel met lampen opgesierd en die gemaakt om de satans ermee te stenigen. En Wij hebben voor hen de bestraffing van de vuurgloed klaargemaakt.'),
(5247, 67, 6, 'En voor hen die aan hun Heer geen geloof hechten is er de bestraffing van de hel; dat is pas een slechte bestemming!'),
(5248, 67, 7, 'Wanneer zij erin geworpen worden horen zij het gekreun ervan terwijl zij overkookt.'),
(5249, 67, 8, 'Zij barst bijna van woede. Telkens als er een groep in wordt geworpen vragen haar bewakers: "Is er geen waarschuwer tot jullie gekomen?"'),
(5250, 67, 9, 'Dan zeggen zij: "Ja zeker, er is een waarschuwer tot ons gekomen, maar wij hebben'),
(5251, 67, 10, 'En zij zeggen: "Hadden wij maar geluisterd of ons verstand gebruikt, dan waren wij nu niet bij hen die in de vuurgloed thuishoren."'),
(5252, 67, 11, 'En zij erkennen hun zonden. Weg dus met hen die in de vuurgloed thuishoren!'),
(5253, 67, 12, 'Maar voor hen die hun Heer in het verborgen vrezen is er vergeving en een groot loon.'),
(5254, 67, 13, 'Of jullie nu in het geheim spreken of in het openbaar, Hij weet wat er binnen in de harten is.'),
(5255, 67, 14, 'Zou Hij hen die Hij geschapen heeft niet kennen; Hij is de welwillende, de welingelichte.'),
(5256, 67, 15, 'Hij is het die de aarde voor jullie handelbaar gemaakt heeft. Wandelt dus over haar rug en eet van het levensonderhoud dat Hij geeft. En de herrijzenis voert naar Hem.'),
(5257, 67, 16, 'Wanen jullie je er veilig voor dat Hij die in de hemel is de aarde met jullie laat wegzinken? Zij begint al te trillen!'),
(5258, 67, 17, 'Of wanen jullie je er veilig voor dat Hij die in de hemel is tegen jullie een zandstorm stuurt? Jullie zullen het weten hoe het met Mijn waarschuwing staat!'),
(5259, 67, 18, 'Zij die er voor hun tijd waren hebben ook van leugens beticht. En hoe was Mijn terechtwijzing dan?'),
(5260, 67, 19, 'Hebben zij niet gezien naar de vogels boven hen die hun vleugels uitspreiden en intrekken. Alleen de Erbarmer houdt ze vast. Hij doorziet alles.'),
(5261, 67, 20, 'Of wie is het die in plaats van de Erbarmer voor jullie een troepenmacht kan zijn die jullie helpt? De ongelovigen zijn slechts aan begoocheling [ten prooi gevallen].'),
(5262, 67, 21, 'Of wie is het die in jullie levensonderhoud zal voorzien als Hij Zijn voorzieningen tegenhoudt? Maar nee, zij volharden in minachting en afkeer.'),
(5263, 67, 22, 'Volgt dan iemand die wandelt terwijl hij op zijn gezicht valt het goede pad beter of iemand die rechtop een juiste weg bewandelt?'),
(5264, 67, 23, 'Zeg: "Hij is het die jullie heeft laten ontstaan en die voor jullie gehoor, gezichtsvermogen en harten heeft gemaakt; gering is de dank die jullie betuigen."'),
(5265, 67, 24, 'Zeg: "Hij is het die jullie op de aarde geschapen heeft en tot Hem zullen jullie verzameld worden."'),
(5266, 67, 25, 'En zij zeggen: "Wanneer zal deze aanzegging zich voordoen, als jullie gelijk hebben?"'),
(5267, 67, 26, 'Zeg: "De kennis [daarover] is bij God. Ik ben slechts een duidelijke waarschuwer."'),
(5268, 67, 27, 'Wanneer zij het dan van dichtbij zien vertrekken de gezichten van hen die ongelovig zijn. En er wordt gezegd: "Dit is het wat jullie hebben verlangd."'),
(5269, 67, 28, 'Zeg: "Hoe zien jullie het, als God mij en wie er met mij zijn vernietigt of met ons erbarmen heeft? Maar wie zal de ongelovigen voor een pijnlijke bestraffing beschermen?"'),
(5270, 67, 29, 'Zeg: "Hij is de Erbarmer. In Hem geloven wij en op Hem stellen wij ons vertrouwen. Jullie zullen wel te weten komen wie er in duidelijke dwaling verkeert."'),
(5271, 67, 30, 'Zeg: "Hoe zien jullie het, als jullie water diep zou wegzakken, wie zou jullie dan bronwater brengen?"');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'N[oen]. Bij de pen en wat zij neerschrijven.'),
(5273, 68, 2, 'Jij bent door Gods genade geen bezetene.'),
(5274, 68, 3, 'Er is voor jou een ononderbroken loon'),
(5275, 68, 4, 'en jij bent hoogstaand van karakter.'),
(5276, 68, 5, 'Jij zult inzien en zij zullen inzien'),
(5277, 68, 6, 'wie van jullie de verdwaasde is.'),
(5278, 68, 7, 'Jouw Heer kent wie van Zijn weg afdwaalt het best en Hij kent hen die het goede pad volgen het best.'),
(5279, 68, 8, 'Geef aan de loochenaars geen gehoor.'),
(5280, 68, 9, 'Zij zouden graag willen dat jij toegeeflijk was dan zouden zij ook toegeeflijk zijn.'),
(5281, 68, 10, 'Geef aan geen enkele verachtelijke edenzweerder gehoor,'),
(5282, 68, 11, 'een met roddel rondlopende lasteraar,'),
(5283, 68, 12, 'een zondige overtreder die het goede belet,'),
(5284, 68, 13, 'een bruut en bovendien een indringer,'),
(5285, 68, 14, 'ook al heeft hij bezit en zonen.'),
(5286, 68, 15, 'Wanneer hem Onze tekenen worden voorgelezen, zegt hij: "Fabels van hen die er eertijds waren."'),
(5287, 68, 16, 'Wij zullen hem brandmerken op zijn snoet.'),
(5288, 68, 17, 'Wij hebben hen op de proef gesteld zoals Wij de bezitters van de tuin op de proef gesteld hebben toen zij zwoeren: "In de morgen zullen wij hem afoogsten!"'),
(5289, 68, 18, 'zonder een voorbehoud te maken.'),
(5290, 68, 19, 'Toen ging er een nachtelijke bezoeking van jouw Heer rond, terwijl zij sliepen,'),
(5291, 68, 20, 'en \'s morgens was hij als afgeoogst.'),
(5292, 68, 21, 'Toen riepen zij \'s morgens tegen elkaar:'),
(5293, 68, 22, '"Gaat vroeg naar jullie akker als jullie willen afoogsten."'),
(5294, 68, 23, 'En zij gingen op weg terwijl zij onder elkaar fluisterden:'),
(5295, 68, 24, '"Vandaag zal er geen behoeftige bij jullie binnenkomen."'),
(5296, 68, 25, 'En zij waren vroeg weggegaan met de bedoeling daartoe in staat te zijn.'),
(5297, 68, 26, 'Maar toen zij het zagen zeiden zij: "Wij zijn verdwaald.'),
(5298, 68, 27, 'Nee, wij zijn beroofd!"'),
(5299, 68, 28, 'De evenwichtigste onder hen zei: "Had ik het jullie niet gezegd? Als jullie [God] maar zouden prijzen."'),
(5300, 68, 29, 'Zij zeiden: "Onze Heer zij geprezen, wij waren onrechtplegers."'),
(5301, 68, 30, 'Toen gingen zij elkaar verwijten maken.'),
(5302, 68, 31, 'Zij zeiden: "Wee ons, wij waren onbeschaamd.'),
(5303, 68, 32, 'Misschien dat onze Heer ons iets beters ervoor in de plaats zal geven. Wij verlangen naar onze Heer."'),
(5304, 68, 33, 'Zo was de bestraffing, maar de bestraffing in het hiernamaals is nog erger. Als zij het maar wisten!'),
(5305, 68, 34, 'Maar de godvrezenden hebben bij hun Heer de tuinen van de gelukzaligheid.'),
(5306, 68, 35, 'Zullen Wij dan hen die zich [aan God] hebben overgegeven als de boosdoeners behandelen?'),
(5307, 68, 36, 'Wat is er met jullie? Hoe kunnen jullie oordelen?'),
(5308, 68, 37, 'Of hebben jullie een boek, waarin jullie kunnen studeren?'),
(5309, 68, 38, 'Daarin is zeker wat jullie verkiezen.'),
(5310, 68, 39, 'Of zijn Wij door eden die tot de opstandingsdag reiken aan jullie verplicht dat jullie zullen hebben wat jullie beslissen?'),
(5311, 68, 40, 'Vraag hun wie van hen dat beweert.'),
(5312, 68, 41, 'Of hebben zij [zogenaamd goddelijke] metgezellen? Dan moeten zij maar met hun [zogenaamd goddelijke] metgezellen komen, als zij gelijk hebben.'),
(5313, 68, 42, 'Op de dag dat hun de benen ontbloot worden en zij opgeroepen worden zich eerbiedig neer te buigen, maar het dan niet kunnen,'),
(5314, 68, 43, 'terwijl zij met hun deemoedige blikken met vernedering overdekt worden. Toch waren zij opgeroepen zich eerbiedig neer te buigen toen zij nog in veiligheid waren.'),
(5315, 68, 44, 'Laat Mij dan maar met hen die dit bericht loochenen; Wij zullen hen gaandeweg tot vernietiging brengen zonder dat zij het weten.'),
(5316, 68, 45, 'En Ik zal hun uitstel geven. Mijn list staat vast.'),
(5317, 68, 46, 'Of vraag jij hun loon, zodat zij met betalingsverplichtingen belast zijn?'),
(5318, 68, 47, 'Of is het verborgene bij hen zodat zij het kunnen opschrijven?'),
(5319, 68, 48, 'Volhard dus geduldig tot aan het oordeel van jouw Heer. En wees niet als hij die in de vis was toen hij vol ingehouden woede tot zijn Heer riep.'),
(5320, 68, 49, 'Als hem geen genade van zijn Heer bereikt had was hij, verafschuwd als hij was, op een onbegroeide plaats uitgeworpen.'),
(5321, 68, 50, 'Maar zijn Heer verhoorde hem en maakte hem tot een van de rechtschapenen.'),
(5322, 68, 51, 'Zij die ongelovig zijn laten jou met hun blikken bijna uitglijden, wanneer zij de vermaning horen en zij zeggen: "Hij is bezeten."'),
(5323, 68, 52, 'Toch is het niets anders dan een vermaning voor de wereldbewoners.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'De verwerkelijking!'),
(5325, 69, 2, 'Wat is de verwerkelijking?'),
(5326, 69, 3, 'En hoe zul jij te weten komen wat de verwerkelijking is?'),
(5327, 69, 4, 'De Thamoed en de \'Aad loochenden de catastrofe.'),
(5328, 69, 5, 'De Thamoed nu, zij werden door het grote geweld vernietigd.'),
(5329, 69, 6, 'Maar de \'Aad, zij werden door een gierende en razende wind vernietigd'),
(5330, 69, 7, 'die Hij hen zeven nachten en acht dagen ononderbroken liet ondergaan. En je zou de mensen er hebben kunnen zien liggen alsof zij ontwortelde, holle palmstronken waren.'),
(5331, 69, 8, 'Zie jij nog overblijfselen van hen?'),
(5332, 69, 9, 'Ook Fir\'aun en wie er voor zijn tijd waren en de ondersteboven gekeerde steden begingen misstappen.'),
(5333, 69, 10, 'Zij waren ongehoorzaam aan de gezant van hun Heer, Toen greep Hij hen met een overweldigende greep.'),
(5334, 69, 11, 'Maar toen het water overstroomde droegen Wij jullie op het vaartuig'),
(5335, 69, 12, 'om het voor jullie tot een vermanende herinnering te maken en opdat een opmerkzaam oor het zou opmerken.'),
(5336, 69, 13, 'En wanneer er éénmaal op de bazuin geblazen wordt'),
(5337, 69, 14, 'en de aarde en de bergen opgepakt worden en in één klap vergruisd worden.'),
(5338, 69, 15, 'Op die dag komt het aanstaande'),
(5339, 69, 16, 'en barst de hemel, want op die dag is hij breekbaar.'),
(5340, 69, 17, 'En de engelen staan op zijn randen. En acht dragen op die dag de troon van jouw Heer boven zich.'),
(5341, 69, 18, 'Op die dag worden jullie voorgeleid en blijft niets van jullie verborgen.'),
(5342, 69, 19, 'Wie dan zijn boek in zijn rechterhand krijgt die zal zeggen: "Neemt en leest mijn boek maar voor.'),
(5343, 69, 20, 'Ik vermoedde immers dat ik mijn afrekening tegemoet kon zien."'),
(5344, 69, 21, 'Hij zal dan een tevreden leven leiden,'),
(5345, 69, 22, 'in een hooggelegen tuin,'),
(5346, 69, 23, 'met de af te plukken vruchten dichtbij.'),
(5347, 69, 24, '"Eet en drinkt met genoegen [als beloning] voor wat jullie vroeger, in de dagen die voorbij zijn, gedaan hebben."'),
(5348, 69, 25, 'Maar wie zijn boek in zijn linkerhand krijgt die zal zeggen: "Wee mij, had ik mijn boek maar niet gekregen.'),
(5349, 69, 26, 'En had ik maar niet van mijn afrekening afgeweten.'),
(5350, 69, 27, 'Wee mij, was het maar het einde.'),
(5351, 69, 28, 'Mijn bezit heeft mij niet gebaat.'),
(5352, 69, 29, 'Mijn gezag ben ik kwijt."'),
(5353, 69, 30, '"Grijpt hem en bindt hem.'),
(5354, 69, 31, 'En laat hem dan in het hellevuur braden.'),
(5355, 69, 32, 'Legt hem dan aan een ketting van zeventig ellen lang."'),
(5356, 69, 33, 'Hij geloofde immers niet in de geweldige God.'),
(5357, 69, 34, 'En hij drong er niet op aan de behoeftige voedsel te geven.'),
(5358, 69, 35, 'Vandaag heeft hij hier dus geen echte vriend'),
(5359, 69, 36, 'en ook geen voedsel, behalve pus'),
(5360, 69, 37, 'dat slechts door de zondaars gegeten wordt.'),
(5361, 69, 38, 'Niet dan? Ik zweer bij wat jullie doorzien'),
(5362, 69, 39, 'en wat jullie niet doorzien,'),
(5363, 69, 40, 'dat wat de gezant zegt voortreffelijk is.'),
(5364, 69, 41, 'Het zijn niet de woorden van een dichter. Hoe weinig geloven jullie!'),
(5365, 69, 42, 'Noch zijn het de woorden van een waarzegger. Hoe weinig laten jullie je vermanen!'),
(5366, 69, 43, 'Het is een neerzending van de Heer van de wereldbewoners.'),
(5367, 69, 44, 'En als hij over Ons enige uitspraken zelf bedacht had'),
(5368, 69, 45, 'hadden Wij hem bij de rechterarm gegrepen.'),
(5369, 69, 46, 'Dan hadden Wij hem de levensader doorgesneden.'),
(5370, 69, 47, 'Niet een van jullie had dat van hem kunnen afweren.'),
(5371, 69, 48, 'Het is een vermaning voor de godvrezenden.'),
(5372, 69, 49, 'Wij weten dat er onder jullie loochenaars zijn.'),
(5373, 69, 50, 'Het is een [reden tot] wroeging voor de ongelovigen.'),
(5374, 69, 51, 'En het is de vaststaande waarheid.'),
(5375, 69, 52, 'Prijs dan de geweldige naam van jouw Heer.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Iemand vraagt naar een aanstaande bestraffing.'),
(5377, 70, 2, 'De ongelovigen hebben daartegen geen afweer.'),
(5378, 70, 3, 'Zij komt van God die de trappen beheert'),
(5379, 70, 4, 'waarlangs de engelen en de geest tot Hem opstijgen, op een dag die vijftigduizend jaren lang is.'),
(5380, 70, 5, 'Wees dus maar mooi geduldig en volhardend.'),
(5381, 70, 6, 'Zij zullen haar van verre zien.'),
(5382, 70, 7, 'En Wij zullen haar van dichtbij zien'),
(5383, 70, 8, 'op de dag dat de hemel als gesmolten metaal zal zijn'),
(5384, 70, 9, 'en de bergen als wol.'),
(5385, 70, 10, 'En geen boezemvriend zal naar een boezemvriend vragen.'),
(5386, 70, 11, 'Zij zullen elkaar te zien krijgen. De boosdoener zou zich op die dag graag van de bestraffing willen loskopen, met zijn zonen,'),
(5387, 70, 12, 'met zijn gezellin, met zijn broers,'),
(5388, 70, 13, 'met zijn familie die hem toevlucht verschaft'),
(5389, 70, 14, 'en met allen die er op de aarde zijn. En dat zou hem dan redden?'),
(5390, 70, 15, 'Zeker niet! Het is een laaiend vuur,'),
(5391, 70, 16, 'dat de hoofdhuid wegschroeit,'),
(5392, 70, 17, 'dat hem oproept die de rug toewendde en zich afkeerde,'),
(5393, 70, 18, 'die bijeenbracht en oppotte.'),
(5394, 70, 19, 'De mens is vreesachtig geschapen.'),
(5395, 70, 20, 'Wanneer hem het kwaad treft is hij terneergeslagen.'),
(5396, 70, 21, 'En wanneer hem het goede treft houdt hij het voor zich.'),
(5397, 70, 22, 'Alleen niet zij die de salaat verrichten,'),
(5398, 70, 23, 'die zich voortdurend aan hun salaat houden'),
(5399, 70, 24, 'en van wie er een rechtmatig aandeel in hun bezittingen is'),
(5400, 70, 25, 'voor de bedelaar en de onbemiddelde,'),
(5401, 70, 26, 'die geloven dat de oordeelsdag waar is,'),
(5402, 70, 27, 'die voor de bestraffing door hun Heer terugdeinzen --'),
(5403, 70, 28, 'de bestraffing door hun Heer, daar is niemand veilig voor --'),
(5404, 70, 29, 'en die hun schaamstreek kuis bewaren --'),
(5405, 70, 30, 'behalve bij hun echtgenotes of slavinnen waarover zij beschikken, dan valt hun niets te verwijten.'),
(5406, 70, 31, 'Maar zij die meer begeren, zij zijn het die overtreden --'),
(5407, 70, 32, 'die ook goed toezicht houden op hun onderpanden en hun verbintenis,'),
(5408, 70, 33, 'die hun getuigenis afleggen'),
(5409, 70, 34, 'en die zich aan hun salaat houden.'),
(5410, 70, 35, 'Zij zijn het die in tuinen worden geëerd.'),
(5411, 70, 36, 'Wat hebben zij die ongelovig zijn toch dat zij op jou toe rennen,'),
(5412, 70, 37, 'van rechts en van links, in groepen?'),
(5413, 70, 38, 'Begeert dan iedere man van hen een tuin van gelukzaligheid binnen te gaan?'),
(5414, 70, 39, 'Welnee, zij weten toch waarvan Wij hen geschapen hebben.'),
(5415, 70, 40, 'Niet dan? Ik zweer bij de Heer van de oostkanten en de westkanten dat Wij in staat zijn'),
(5416, 70, 41, 'om hen door iets beters te vervangen en niemand kan Ons voor zijn.'),
(5417, 70, 42, 'Laat hen maar kletsen en schertsen totdat zij hun dag tegenkomen die hun wordt aangezegd,'),
(5418, 70, 43, 'de dag waarop zij snel uit de graven tevoorschijn komen alsof zij zich naar hun afgod haasten,'),
(5419, 70, 44, 'terwijl zij met hun deemoedige blikken met vernedering overdekt worden. Dat is de dag die hun was aangezegd.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Wij hebben Noeh naar zijn volk gezonden: "Waarschuw jouw volk voordat een pijnlijke bestraffing tot hen komt."'),
(5421, 71, 2, 'Hij zei: "Mijn volk! Ik ben voor jullie een duidelijke waarschuwer'),
(5422, 71, 3, 'dat jullie God moeten dienen, Hem moeten vrezen en mij moeten gehoorzamen.'),
(5423, 71, 4, 'Dan zal Hij voor zonden van jullie vergeving schenken en jullie uitstel verlenen tot een vastgestelde termijn. Maar Gods termijn wordt niet uitgesteld wanneer hij komt, als jullie dat maar wisten."'),
(5424, 71, 5, 'Hij zei: "Mijn Heer, ik heb mijn volk nacht en dag opgeroepen,'),
(5425, 71, 6, 'maar mijn oproep heeft hen alleen maar meer laten vluchten.'),
(5426, 71, 7, 'En telkens als ik hen opriep, opdat U hun zou vergeven, stopten zij hun vingers in hun oren, bedekten zich met hun kleren en bleven stijfkoppig en hoogmoedig.'),
(5427, 71, 8, 'Toen riep ik hen in het openbaar op.'),
(5428, 71, 9, 'Toen sprak ik openlijk en in het diepste geheim met hen.'),
(5429, 71, 10, 'En ik zei: \'Vraagt jullie Heer om vergeving; Hij is vergevend.'),
(5430, 71, 11, 'Hij zal dan de hemel in overvloed over jullie laten regenen,'),
(5431, 71, 12, 'jullie met bezittingen en zonen versterken, tuinen voor jullie maken en rivieren voor jullie maken.'),
(5432, 71, 13, 'Wat is er met jullie dat jullie van God geen waardigheid verwachten?'),
(5433, 71, 14, 'Hij heeft jullie toch in fasen geschapen.'),
(5434, 71, 15, 'Zien jullie dan niet hoe God zeven hemelen in lagen geschapen heeft?'),
(5435, 71, 16, 'En dat Hij daarin de maan tot een licht heeft gemaakt en dat Hij de zon tot een heldere lamp heeft gemaakt?'),
(5436, 71, 17, 'God heeft jullie toch uit de aarde laten ontstaan.'),
(5437, 71, 18, 'Daarna zal Hij jullie in haar terug laten keren en jullie dan opnieuw tevoorschijn brengen.'),
(5438, 71, 19, 'En God heeft de aarde voor jullie tot een uitgelegd tapijt gemaakt,'),
(5439, 71, 20, 'opdat jullie er wegen en passen begaan kunnen.?"'),
(5440, 71, 21, 'Noeh zei: "Mijn Heer, zij gehoorzamen mij niet, maar zij volgen iemand wiens bezit en kinderen hem alleen maar meer verlies laten lijden.'),
(5441, 71, 22, 'En zij hebben grote listen beraamd'),
(5442, 71, 23, 'en zij zeiden: \'Verlaat jullie goden niet. Verlaat Wadd niet, noch Soewaa\', noch Jaghoeth, Ja\'oek en Nasr.:'),
(5443, 71, 24, 'En zij hebben velen tot dwaling gebracht. Laat dan de onrechtplegers alleen maar meer dwalen."'),
(5444, 71, 25, 'Vanwege hun zonden werden zij verdronken en toen een vuur binnengebracht en zij vonden buiten God om voor zich geen helpers.'),
(5445, 71, 26, 'En Noeh zei: "Mijn Heer, laat niet een van de ongelovigen op de aarde blijven wonen.'),
(5446, 71, 27, 'Immers, als U hen laat zullen zij Uw dienaren tot dwaling brengen en alleen maar overtreders en ondankbaren voortbrengen.'),
(5447, 71, 28, 'Mijn Heer, vergeef mij en mijn ouders en wie mijn huis als gelovige binnenkomen, en de gelovige mannen en vrouwen. En laat de ongelovigen alleen maar meer vernietigd worden."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'Zeg: "Aan mij is geopenbaard dat er een groep djinn geluisterd heeft en dat zij toen zeiden: \'Wij hebben een wonderbaarlijke Koran gehoord'),
(5449, 72, 2, 'die naar de rechtschapenheid voert. Wij geloven er dus in en voegen niemand aan onze Heer als metgezel toe.?'),
(5450, 72, 3, 'En: \'Verheven is de majesteit van onze Heer. Hij heeft zich geen metgezellin noch een kind genomen.?'),
(5451, 72, 4, 'En: \'De dwaas onder ons zegt over God afwijkende dingen.?'),
(5452, 72, 5, 'En: \'Wij zijn van mening dat mensen noch djinn over God leugens zullen mogen zeggen.?'),
(5453, 72, 6, 'En: \'Er waren mannen van de mensen die hun toevlucht zochten bij mannen van de djinn en zo maakten zij hen kwaadaardiger.?'),
(5454, 72, 7, 'En: \'Zij dachten evenals jullie dat God niemand zou laten opstaan.?'),
(5455, 72, 8, 'En: \'Wij hebben de hemel afgezocht en gemerkt dat hij vol is met strenge wachters en staartsterren.?'),
(5456, 72, 9, 'En: \'Wij zaten er op allerlei plaatsen te luisteren, maar wie nu wil luisteren die vindt een staartster voor hem op de loer liggen.?'),
(5457, 72, 10, 'En: \'Wij weten niet of er iets slechts wordt beoogd met hen die er op de aarde zijn of dat hun Heer goede levenswandel voor hen beoogt.?'),
(5458, 72, 11, 'En: \'Sommigen van ons zijn rechtschapen, maar anderen zijn dat niet; wij gaan gescheiden wegen.?'),
(5459, 72, 12, 'En: \'Wij zijn van mening dat wij in de aarde God niet kunnen ontkomen en dat wij Hem ook niet door te vluchten kunnen ontkomen.?'),
(5460, 72, 13, 'En: \'Toen wij de leidraad hoorden geloofden wij erin; en wie in zijn Heer gelooft is voor geweld noch kwaadaardigheid bang.?'),
(5461, 72, 14, 'En: \'Sommigen van ons hebben zich [aan God] overgegeven, maar anderen zijn verdoold. Zij die zich [aan God] overgeven, zij zijn het die naar goede levenswandel streven,'),
(5462, 72, 15, 'maar de verdoolden, zij zijn brandhout voor de hel.?'),
(5463, 72, 16, 'En: \'Als zij op de juiste weg gebleven waren, dan hadden wij hun overvloedig water te drinken gegeven'),
(5464, 72, 17, 'om hen daarmee op de proef te stellen. En wie zich van de vermaning van zijn Heer afwendt, die zal Hij een ondraaglijke bestraffing laten doormaken.?'),
(5465, 72, 18, 'En: \'De moskeeën behoren God toe; roept dus niemand behalve God aan.?'),
(5466, 72, 19, 'En: \'Toen Gods dienaar opstond om Hem aan te roepen zaten zij bijna boven op hem.?"'),
(5467, 72, 20, 'Zeg: "Ik roep alleen maar mijn Heer aan en ik voeg aan Hem niemand als metgezel toe."'),
(5468, 72, 21, 'Zeg: "Ik heb geen macht om jullie schade te berokkenen of tot een goede levenswandel te brengen."'),
(5469, 72, 22, 'Zeg: "Niemand zal mij tegen God beschermen en buiten Hem zal ik geen schuilplaats vinden.'),
(5470, 72, 23, 'Het is slechts een verkondiging van God en van Zijn boodschap. En wie aan God en Zijn gezant ongehoorzaam is, voor hem is er het vuur van de hel. Daarin zullen zij altijd en immer blijven.'),
(5471, 72, 24, 'Als zij dan zien wat hun wordt aangezegd zullen zij weten wie de zwakste helper en het kleinste aantal heeft."'),
(5472, 72, 25, 'Zeg: "Ik weet niet of wat aangezegd wordt dichtbij is of dat het naar later verschoven is door mijn Heer,'),
(5473, 72, 26, 'die het verborgene kent en die Zijn verborgenheid aan niemand toont,'),
(5474, 72, 27, 'behalve aan een gezant aan wie Hij een welbehagen heeft. En Hij laat voor hem en achter hem bewakers op de loer liggen,'),
(5475, 72, 28, 'om te weten of zij de boodschap van hun Heer verkondigen. Hij omvat wat bij hen is en Hij heeft alles opgesomd en nauwkeurig geteld."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'O jij in kleren gehulde!'),
(5477, 73, 2, 'Blijf op een klein gedeelte na de hele nacht op'),
(5478, 73, 3, '? de helft of iets minder'),
(5479, 73, 4, 'of iets meer -- en draag de Koran achtereenvolgens voor.'),
(5480, 73, 5, 'Wij zullen jou zwaarwegende woorden opleggen.'),
(5481, 73, 6, 'De nachtwaak maakt meer indruk en laat juistere dingen zeggen.'),
(5482, 73, 7, 'Immers, overdag heb jij langdurige bezigheden.'),
(5483, 73, 8, 'Gedenk dus de naam van jouw Heer en zonder je geheel voor Hem af,'),
(5484, 73, 9, 'de Heer van het oosten en het westen. Er is geen god dan Hij; neem Hem dus als voogd.'),
(5485, 73, 10, 'En verdraag wat zij zeggen geduldig en ga minzaam bij hen weg.'),
(5486, 73, 11, 'En laat Mij maar met de loochenaars die een aangenaam leven leiden en geef hun nog enig uitstel.'),
(5487, 73, 12, 'Wij hebben boeien en een hellevuur bij Ons'),
(5488, 73, 13, 'en voedsel om in te stikken en een pijnlijke bestraffing'),
(5489, 73, 14, 'op de dag dat de aarde en de bergen beven en de bergen stuifduinen worden.'),
(5490, 73, 15, 'Wij hebben een gezant tot jullie gezonden om over jullie getuige te zijn, zoals Wij een gezant tot Fir\'aun gezonden hebben.'),
(5491, 73, 16, 'Maar Fir\'aun was ongehoorzaam aan de gezant en dus grepen Wij hem op een afschuwelijke manier.'),
(5492, 73, 17, 'Hoe zouden jullie je dan, als jullie ongelovig blijven, willen beschermen tegen een dag waarop de kinderen tot grijsaards worden,'),
(5493, 73, 18, 'waarop de hemel splijt? Zijn aanzegging wordt uitgevoerd.'),
(5494, 73, 19, 'Dit is een vermaning en wie wil, slaat de weg naar zijn Heer in.'),
(5495, 73, 20, 'Jouw Heer weet dat jij bijna tweederde van de nacht opblijft, of de helft, of een derde, samen met een groep van hen die bij jou horen. God bepaalt de maat van de nacht en de dag. Hij weet dat jullie het niet kunnen en heeft zich genadig tot jullie gewend. Leest dus uit de Koran een gemakkelijk gedeelte voor. Hij weet dat er onder jullie zieken zullen zijn en anderen die op de aarde rondtrekken om naar Gods gunst te streven en anderen die op Gods weg strijden. Leest er dus een gemakkelijk gedeelte uit voor, verricht de salaat, geeft de zakaat en geeft aan God een goede lening. En wat jullie eerder aan goeds gedaan hebben voor jullie zelf, dat zullen jullie bij God als nog beter en geweldiger beloond vinden. En vraagt God om vergeving; God is vergevend en barmhartig.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'O jij ommantelde!'),
(5497, 74, 2, 'Sta op en waarschuw.'),
(5498, 74, 3, 'Jouw Heer, verheerlijk Hem.'),
(5499, 74, 4, 'Jouw kleren, reinig ze.'),
(5500, 74, 5, 'De gruwel, vermijd die.'),
(5501, 74, 6, 'Doe niet wel om meer te krijgen.'),
(5502, 74, 7, 'En op jouw Heer, wacht geduldig.'),
(5503, 74, 8, 'Wanneer dan op de hoorn geblazen wordt,'),
(5504, 74, 9, 'dat is dan op die dag een moeilijke dag,'),
(5505, 74, 10, 'voor de ongelovigen niet gemakkelijk.'),
(5506, 74, 11, 'En laat Mij maar met hen die Ik alleen geschapen heb'),
(5507, 74, 12, 'en aan wie Ik uitgestrekt bezit gegeven heb'),
(5508, 74, 13, 'en aanwezige zonen.'),
(5509, 74, 14, 'En Ik had voor hem een goede plaats bereid,'),
(5510, 74, 15, 'maar dan verlangt hij van Mij nog meer!'),
(5511, 74, 16, 'Welnee, want hij was tegen Onze tekenen weerspannig.'),
(5512, 74, 17, 'Ik zal hem ondraaglijke kwelling opleggen.'),
(5513, 74, 18, 'Hij dacht en wikte.'),
(5514, 74, 19, 'Doodvallen kan hij, hoe hij wikte.'),
(5515, 74, 20, 'En nog eens doodvallen kan hij, hoe hij wikte.'),
(5516, 74, 21, 'Toen keek hij.'),
(5517, 74, 22, 'Toen fronste hij en grijnsde.'),
(5518, 74, 23, 'Toen keerde hij de rug toe en was hoogmoedig.'),
(5519, 74, 24, 'En hij zei: "Dit is slechts toverij die wordt doorverteld;'),
(5520, 74, 25, 'dit zijn slechts de woorden van een mens."'),
(5521, 74, 26, 'Ik zal hem in de hellehitte laten braden.'),
(5522, 74, 27, 'En hoe zul jij te weten komen wat de hellehitte is?'),
(5523, 74, 28, 'Zij laat niets over en zij laat niets achter.'),
(5524, 74, 29, 'Zij verzengt de huid.'),
(5525, 74, 30, 'Negentien hebben er opzicht over haar.'),
(5526, 74, 31, 'Wij hebben slechts engelen als vuurwachters aangesteld en Wij hebben hun aantal voor hen die ongelovig zijn alleen maar tot een verzoeking gemaakt. Ook opdat zij aan wie het boek gegeven is overtuigd worden, opdat zij die geloven hun geloof nog vermeerderen, opdat zij aan wie het boek gegeven is en zij die geloven niet twijfelen en opdat zij die in hun harten een ziekte hebben en de ongelovigen zullen zeggen: "Wat bedoelt God bijvoorbeeld hiermee?" Zo brengt God wie Hij wil tot dwaling en brengt Hij wie Hij wil op het goede pad. En slechts Hij kent de troepenmachten van jouw Heer. Het is slechts een vermaning voor de mensen.'),
(5527, 74, 32, 'Nee toch! Bij de maan!'),
(5528, 74, 33, 'Bij de nacht wanneer hij de rug toekeert!'),
(5529, 74, 34, 'Bij de morgen wanneer hij gloort!'),
(5530, 74, 35, 'Het is een van de grootste [rampen]!'),
(5531, 74, 36, 'Als een waarschuwing voor de mensen,'),
(5532, 74, 37, 'voor wie er uit jullie midden vooruit willen komen of achterblijven.'),
(5533, 74, 38, 'Iedere persoon is aansprakelijk voor wat hij begaan heeft,'),
(5534, 74, 39, 'behalve zij die rechts staan.'),
(5535, 74, 40, 'In tuinen doen zij bij elkaar navraag'),
(5536, 74, 41, 'naar de boosdoeners.'),
(5537, 74, 42, '"Wat heeft jullie de hellehitte binnengevoerd?"'),
(5538, 74, 43, 'Zij zullen zeggen: "Wij hebben niet bij hen behoord die de salaat baden,'),
(5539, 74, 44, 'wij hebben geen voedsel aan de behoeftige gegeven,'),
(5540, 74, 45, 'wij hebben meegekletst met hen die kletsen'),
(5541, 74, 46, 'en wij hebben de oordeelsdag geloochend'),
(5542, 74, 47, 'totdat de zekerheid [van de dood] tot ons kwam."'),
(5543, 74, 48, 'Maar de voorspraak van de bemiddelaars zal voor hen geen nut hebben.'),
(5544, 74, 49, 'Wat is er dan met hen dat zij zich van de vermaning afwenden?'),
(5545, 74, 50, 'Alsof zij opgeschrikte ezels zijn'),
(5546, 74, 51, 'die voor een leeuw vluchten.'),
(5547, 74, 52, 'Maar toch wil een ieder van hen dat hem opengeslagen bladen gegeven worden.'),
(5548, 74, 53, 'Ja zeker! Maar zij zijn niet bang voor het hiernamaals.'),
(5549, 74, 54, 'Ja zeker! Het is een vermaning.'),
(5550, 74, 55, 'En wie wil denkt daaraan.'),
(5551, 74, 56, 'Maar zij denken er slechts aan als God het wil. Hem komt het toe gevreesd te worden en Hem komt het toe te vergeven.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Niet dan? Ik zweer bij de opstandingsdag!'),
(5553, 75, 2, 'Niet dan? Ik zweer bij de ziel die zichzelf verwijten maakt!'),
(5554, 75, 3, 'Rekent de mens erop dat Wij zijn beenderen niet bijeen zullen brengen?'),
(5555, 75, 4, 'Zeker, Wij zijn in staat om zijn vingertoppen te vormen.'),
(5556, 75, 5, 'Maar de mens wenst er maar op los te leven.'),
(5557, 75, 6, 'Hij vraagt wanneer de opstandingsdag zal zijn.'),
(5558, 75, 7, 'Wanneer dan de ogen verblind worden'),
(5559, 75, 8, 'en de maan wordt verduisterd'),
(5560, 75, 9, 'en de zon en de maan worden samengevoegd,'),
(5561, 75, 10, 'op die dag zal de mens zeggen: "Waar is een toevluchtsoord?"'),
(5562, 75, 11, 'Welnee, er is geen schuilplaats.'),
(5563, 75, 12, 'Bij jouw Heer is op die dag de verblijfplaats.'),
(5564, 75, 13, 'Aan de mensen zal op die dag worden meegedeeld wat zij vroeger en later gedaan hebben.'),
(5565, 75, 14, 'Ja zeker, de mens zal een duidelijk bewijs tegen zichzelf zijn,'),
(5566, 75, 15, 'ook al draagt hij zijn verontschuldigingen aan.'),
(5567, 75, 16, 'Beweeg niet je tong ermee om je ermee te haasten.'),
(5568, 75, 17, 'Het is Onze taak hem te verzamelen en voor te lezen.'),
(5569, 75, 18, 'En als Wij hem voorlezen volg dan de voorlezing ervan.'),
(5570, 75, 19, 'En dan is het Onze taak hem uiteen te zetten.'),
(5571, 75, 20, 'Maar nee, jullie beminnen het snel voorbijgaande'),
(5572, 75, 21, 'en veronachtzamen het hiernamaals.'),
(5573, 75, 22, 'Stralende gezichten zijn er op die dag'),
(5574, 75, 23, 'die naar hun Heer kijken.'),
(5575, 75, 24, 'En grijnzende gezichten zijn er op die dag'),
(5576, 75, 25, 'die wel vermoeden dat hun iets ontzettends wordt aangedaan.'),
(5577, 75, 26, 'Ja zeker, wanneer [door de levensadem] het sleutelbeen bereikt wordt'),
(5578, 75, 27, 'en er gezegd wordt: "Wie kan bezweren?"'),
(5579, 75, 28, 'en [de betrokkene] vermoedt dat het het afscheid is'),
(5580, 75, 29, 'en de benen over elkaar gelegd zijn,'),
(5581, 75, 30, 'op die dag wordt men naar jouw Heer gedreven.'),
(5582, 75, 31, 'Hij dacht immers dat het niet waar was en bad de salaat niet!'),
(5583, 75, 32, 'Maar hij loochende het en keerde zich af.'),
(5584, 75, 33, 'Toen ging hij snoevend naar zijn familie.'),
(5585, 75, 34, 'Wacht maar jij, wacht maar,'),
(5586, 75, 35, 'En nog eens: Wacht maar jij, wacht maar.'),
(5587, 75, 36, 'Rekent de mens erop dat hij ongemoeid gelaten wordt?'),
(5588, 75, 37, 'Was hij niet een druppel zaad dat werd uitgestort?'),
(5589, 75, 38, 'Daarna was hij een bloedklonter. Toen schiep Hij, vormde'),
(5590, 75, 39, 'en maakte er de twee geslachten uit: het mannelijke en het vrouwelijke.'),
(5591, 75, 40, 'Is dat niet Hij die in staat is de doden levend te maken?');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Is er niet een tijdsperiode geweest dat de mens niets noemenswaards was?'),
(5593, 76, 2, 'Wij hebben de mens uit een druppel geschapen, uit een mengsel om hem te toetsen. En Wij hebben hem horend en ziend gemaakt.'),
(5594, 76, 3, 'Wij hebben hem de goede weg gewezen, of hij nu dankbaar of ondankbaar is.'),
(5595, 76, 4, 'Voor de ongelovigen hebben Wij kettingen, halsketenen en een vuurgloed klaargemaakt.'),
(5596, 76, 5, 'Maar de vromen zullen drinken uit een beker die met kamfer is bijgemengd'),
(5597, 76, 6, 'uit een bron waaruit Gods dienaren drinken en die zij rijkelijk uit de aarde laten ontspringen.'),
(5598, 76, 7, 'Zij vervullen hun geloften en zijn bang voor een dag waarvan het kwaad om zich heen grijpt.'),
(5599, 76, 8, 'En zij geven, hoe lief zij het ook hebben, voedsel aan behoeftige, wees en gevangene.'),
(5600, 76, 9, '"Wij geven jullie voedsel ter wille van God; Wij wensen van jullie geen loon of dank.'),
(5601, 76, 10, 'Wij vrezen van Onze Heer een angstaanjagende en vreeswekkende dag."'),
(5602, 76, 11, 'Maar God beschermt hen voor het kwaad van die dag en maakt hen stralend en blij.'),
(5603, 76, 12, 'En Hij beloont hen, omdat zij geduldig hebben volhard, met een tuin en met zijde.'),
(5604, 76, 13, 'Daarin liggen zij achterovergeleund op ligbanken terwijl zij er geen zon en geen kou zien.'),
(5605, 76, 14, 'De schaduwen zullen er dichtbij zijn en de vruchten hangen er voor het grijpen.'),
(5606, 76, 15, 'Een kelk van zilver wordt bij hen rondgegeven en bekers die van kristal zijn,'),
(5607, 76, 16, 'van zilverwit kristal, die zij precies vol hebben geschonken.'),
(5608, 76, 17, 'En daar krijgen zij uit een beker te drinken waarin gember is bijgemengd,'),
(5609, 76, 18, 'uit een bron daar, die Salsabiel heet. *'),
(5610, 76, 19, 'En bij hen gaan altijd jong blijvende jongelingen rond. Wanneer jij hen ziet denk je dat zij rondgestrooide parels zijn.'),
(5611, 76, 20, 'En wanneer jij het ziet dan zie jij gelukzaligheid en een grote heerschappij.'),
(5612, 76, 21, 'Zij hebben kleren van groene zijde en brokaat aangetrokken en dragen armbanden van zilver en hun Heer geeft hun zuivere drank te drinken.'),
(5613, 76, 22, '"Dit is als beloning voor jullie en jullie worden bedankt voor wat jullie hebben nagestreefd."'),
(5614, 76, 23, 'Wij hebben de Koran werkelijk tot jou neergezonden.'),
(5615, 76, 24, 'Wacht dus geduldig het oordeel van jouw Heer af en gehoorzaam van hen geen zondaar of een ongelovige.'),
(5616, 76, 25, 'En gedenk de naam van jouw Heer \'s ochtends en \'s avonds.'),
(5617, 76, 26, 'En een deel van de nacht, buig je dan eerbiedig voor Hem neer en prijs Hem de nacht lang.'),
(5618, 76, 27, 'Dezen hier beminnen het snel voorbijgaande en veronachtzamen een zware dag.'),
(5619, 76, 28, 'Wij hebben hen geschapen en hun kracht gegeven en wanneer Wij willen vervangen Wij hen door gelijksoortigen.'),
(5620, 76, 29, 'Dit is een vermaning en wie wil slaat de weg naar zijn Heer in.'),
(5621, 76, 30, 'Maar jullie willen het slechts als God het wil. God is wetend en wijs.'),
(5622, 76, 31, 'Hij laat wie Hij wil in Zijn barmhartigheid binnengaan. En de onrechtplegers, voor hen heeft Hij een pijnlijke bestraffing klaargemaakt.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Bij de achtereenvolgens losgelatenen!'),
(5624, 77, 2, 'En de er op los stormenden!'),
(5625, 77, 3, 'En de wijd verspreidenden!'),
(5626, 77, 4, 'En de duidelijk onderscheidenden!'),
(5627, 77, 5, 'En de vermaning brengenden,'),
(5628, 77, 6, 'ter verontschuldiging of ter waarschuwing!'),
(5629, 77, 7, 'Wat jullie is aangezegd gebeurt.'),
(5630, 77, 8, 'Wanneer dan de sterren worden uitgewist.'),
(5631, 77, 9, 'En wanneer de hemel wordt gesplitst.'),
(5632, 77, 10, 'En wanneer de bergen worden verstrooid.'),
(5633, 77, 11, 'En wanneer voor de gezanten de tijd is bepaald,'),
(5634, 77, 12, 'voor welke dag de termijn is vastgesteld,'),
(5635, 77, 13, 'voor de dag van de schifting.'),
(5636, 77, 14, 'En hoe zul jij te weten komen wat de dag van de schifting is?'),
(5637, 77, 15, 'Wee op die dag de loochenaars!'),
(5638, 77, 16, 'Hebben Wij hen die er eertijds waren niet vernietigd?'),
(5639, 77, 17, 'Maar dan laten Wij de lateren hen volgen.'),
(5640, 77, 18, 'Zo doen Wij met de boosdoeners.'),
(5641, 77, 19, 'Wee op die dag de loochenaars!'),
(5642, 77, 20, 'Hebben Wij uit verachtelijk water niet jullie geschapen,'),
(5643, 77, 21, 'die Wij toen in een solide verblijfplaats legden,'),
(5644, 77, 22, 'tot een vastgestelde duur?'),
(5645, 77, 23, 'Wij hebben die bepaald en een voortreffelijke bepaler zijn Wij.'),
(5646, 77, 24, 'Wee op die dag de loochenaars!'),
(5647, 77, 25, 'Hebben Wij de aarde niet gemaakt voor het opnemen'),
(5648, 77, 26, 'van levenden en doden?'),
(5649, 77, 27, 'En Wij hebben stevige hoog uitstekende bergen op haar gemaakt en jullie fris water te drinken gegeven.'),
(5650, 77, 28, 'Wee op die dag de loochenaars!'),
(5651, 77, 29, '"Gaat op weg naar wat jullie altijd loochenden.'),
(5652, 77, 30, 'Gaat op weg naar een schaduw van drie takken,'),
(5653, 77, 31, 'die geen schaduw geeft en die niet tegen de vuurgloed helpt."'),
(5654, 77, 32, 'Hij schiet vonken torenhoog,'),
(5655, 77, 33, 'alsof het gele kamelen waren.'),
(5656, 77, 34, 'Wee op die dag de loochenaars!'),
(5657, 77, 35, 'Dit is de dag waarop zij niet spreken.'),
(5658, 77, 36, 'En het wordt hun niet toegestaan zich te verontschuldigen.'),
(5659, 77, 37, 'Wee op die dag de loochenaars!'),
(5660, 77, 38, '"Dit is de dag van de schifting waarop wij jullie en hen die er eertijds waren bijeenbrengen.'),
(5661, 77, 39, 'En als jullie nog een list hebben, gebruikt die dan tegen Mij."'),
(5662, 77, 40, 'Wee op die dag de loochenaars!'),
(5663, 77, 41, 'Maar de godvrezenden zijn in schaduwen en bij bronnen'),
(5664, 77, 42, 'en bij vruchten die zij maar verlangen.'),
(5665, 77, 43, '"Eet en drinkt met genoegen [als beloning] voor wat jullie gedaan hebben."'),
(5666, 77, 44, 'Zo belonen Wij hen die goed doen.'),
(5667, 77, 45, 'Wee op die dag de loochenaars!'),
(5668, 77, 46, '"Eet en geniet nog even, want jullie zijn boosdoeners."'),
(5669, 77, 47, 'Wee op die dag de loochenaars!'),
(5670, 77, 48, 'En wanneer tot hen gezegd wordt: "Buigt" dan kunnen zij niet buigen.'),
(5671, 77, 49, 'Wee op die dag de loochenaars!'),
(5672, 77, 50, 'Aan welk bericht zullen zij dan hierna nog geloven?');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'Waarover ondervragen zij elkaar?'),
(5674, 78, 2, 'Over de geweldige mededeling'),
(5675, 78, 3, 'waarover zij het oneens zijn.'),
(5676, 78, 4, 'Nee toch, zij zullen het weten!'),
(5677, 78, 5, 'Nog eens, nee toch, zij zullen het weten!'),
(5678, 78, 6, 'Hebben Wij niet de aarde tot een wiegebed gemaakt'),
(5679, 78, 7, 'en de bergen tot tentpinnen?'),
(5680, 78, 8, 'Wij hebben jullie in paren geschapen.'),
(5681, 78, 9, 'Wij hebben jullie slaap gemaakt om uit te rusten.'),
(5682, 78, 10, 'Wij hebben de nacht gemaakt als een omhulling.'),
(5683, 78, 11, 'Wij hebben de dag gemaakt voor de levensbehoeften.'),
(5684, 78, 12, 'Wij hebben boven jullie zeven hechte [hemelen] gebouwd.'),
(5685, 78, 13, 'Wij hebben een schitterende lamp gemaakt.'),
(5686, 78, 14, 'En Wij hebben uit de regenwolken water in stromen laten neerdalen'),
(5687, 78, 15, 'om daarmee graan en planten voort te brengen'),
(5688, 78, 16, 'en dichtbegroeide tuinen.'),
(5689, 78, 17, 'De dag van de schifting is een afgesproken tijd,'),
(5690, 78, 18, 'de dag waarop op de bazuin geblazen wordt. Dan zullen jullie in groepen komen'),
(5691, 78, 19, 'en wordt de hemel geopend en hij is een en al poorten.'),
(5692, 78, 20, 'En de bergen worden in beweging gezet en zijn als een luchtspiegeling.'),
(5693, 78, 21, 'En de hel is een hinderlaag,'),
(5694, 78, 22, 'waar de onbeschaamden naar terugkeren,'),
(5695, 78, 23, 'waar zij voortdurend zullen blijven.'),
(5696, 78, 24, 'Zij zullen daarin noch koelte noch iets om te drinken proeven,'),
(5697, 78, 25, 'maar gloeiend water en etter,'),
(5698, 78, 26, 'als een passende vergelding.'),
(5699, 78, 27, 'Zij verwachtten geen afrekening'),
(5700, 78, 28, 'en zij loochenden Onze tekenen herhaaldelijk.'),
(5701, 78, 29, 'En alles hebben Wij opgesomd in een boek.'),
(5702, 78, 30, 'Proeft dan; jullie bestraffing wordt alleen maar erger.'),
(5703, 78, 31, 'Maar voor de godvrezenden is er een triomf,'),
(5704, 78, 32, 'boomgaarden en wijnstokken,'),
(5705, 78, 33, 'rondborstige gezellinnen die even oud zijn'),
(5706, 78, 34, 'en een vol gevulde beker.'),
(5707, 78, 35, 'Zij horen daar geen geklets en geen loochening.'),
(5708, 78, 36, 'Als beloning van jouw Heer, als gift en als afrekening'),
(5709, 78, 37, 'van de Heer van de hemelen en de aarde en wat er tussen beide is, de Erbarmer, die zij niet kunnen tegenspreken'),
(5710, 78, 38, 'op de dag dat de geest en de engelen in een rij staan. Spreken zullen slechts zij aan wie de Erbarmer het toestaat en die zeggen wat juist is.'),
(5711, 78, 39, 'Dat is de dag van de waarheid. Wie wil begeeft zich op de terugweg naar zijn Heer.'),
(5712, 78, 40, 'Wij hebben jullie gewaarschuwd voor een nabije bestraffing op de dag dat de mens kijkt naar wat zijn handen vroeger gedaan hebben en waarop de ongelovige zegt: "O wee, was ik maar stof!"');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Bij de heftige losrukkers!'),
(5714, 79, 2, 'Bij de vlugge uittrekkers!'),
(5715, 79, 3, 'Bij de zwevende zwevers!'),
(5716, 79, 4, 'En bij de snelle voortspoeders!'),
(5717, 79, 5, 'En bij hen die een bevel uitvoeren!'),
(5718, 79, 6, 'Op de dag dat de bevende zal beven,'),
(5719, 79, 7, 'waar de volgende direct achteraan komt;'),
(5720, 79, 8, 'harten zullen op die dag bonzen,'),
(5721, 79, 9, 'hun ogen zijn neergeslagen.'),
(5722, 79, 10, 'Zij zullen zeggen: "Worden wij echt in onze vroegere toestand teruggebracht?'),
(5723, 79, 11, 'Ook als wij vermolmde beenderen zijn geworden?"'),
(5724, 79, 12, 'Zij zeggen: "Dat is dan een terugkeer met verlies!"'),
(5725, 79, 13, 'Dan klinkt slechts één afschrikkende kreet'),
(5726, 79, 14, 'en dan staan zij al op het aardoppervlak.'),
(5727, 79, 15, 'Is het verhaal van Moesa tot jou gekomen?'),
(5728, 79, 16, 'Toen zijn Heer in de geheiligde vallei Toewa tot hem riep:'),
(5729, 79, 17, '"Ga heen naar Fir\'aun -- hij is onbeschaamd --'),
(5730, 79, 18, 'en zeg: \'Zou jij je niet willen louteren?'),
(5731, 79, 19, 'Dan zal ik jou naar mijn Heer leiden zodat jij [Hem] zult vrezen.?"'),
(5732, 79, 20, 'Toen liet hij hem het grootste teken zien.'),
(5733, 79, 21, 'Maar hij loochende het en was weerspannig.'),
(5734, 79, 22, 'Toen keerde hij het snel de rug toe.'),
(5735, 79, 23, 'En hij riep een bijeenkomst samen en deed een afkondiging;'),
(5736, 79, 24, 'hij zei: "Ik ben jullie hoogste heer."'),
(5737, 79, 25, 'Toen greep God hem door aan hem de straf van het hiernamaals en die van het tegenwoordige bestaan te voltrekken.'),
(5738, 79, 26, 'Daarin is een les voor wie vreest.'),
(5739, 79, 27, 'Zijn jullie moeilijker te scheppen of de hemel die Hij gebouwd heeft?'),
(5740, 79, 28, 'Hij heeft het dak ervan omhoog geheven en heeft hem gefatsoeneerd.'),
(5741, 79, 29, 'Hij heeft de nacht ervan donker gemaakt en het heldere daglicht ervan tevoorschijn gebracht.'),
(5742, 79, 30, 'En de aarde spreidde Hij daarna uit.'),
(5743, 79, 31, 'Hij bracht haar water en haar weiden tevoorschijn'),
(5744, 79, 32, 'en de bergen heeft hij stevig aangebracht,'),
(5745, 79, 33, 'als vruchtgebruik voor jullie en jullie vee.'),
(5746, 79, 34, 'En wanneer dan de overstelpende catastrofe komt,'),
(5747, 79, 35, 'op de dag dat de mens zich herinnert wat hij heeft nagejaagd,'),
(5748, 79, 36, 'en het hellevuur onthuld wordt voor wie zien kan,'),
(5749, 79, 37, 'dan zal voor wie onbeschaamd was'),
(5750, 79, 38, 'en het tegenwoordige leven verkoos'),
(5751, 79, 39, 'het hellevuur de verblijfplaats zijn.'),
(5752, 79, 40, 'Maar dan zal voor wie vreesde om voor zijn Heer te staan en zich zijn persoonlijke neigingen ontzegde'),
(5753, 79, 41, 'de tuin de verblijfplaats zijn.'),
(5754, 79, 42, 'Zij vragen jou naar het uur, voor wanneer het is vastgesteld.'),
(5755, 79, 43, 'Hoe kun jij dat nu noemen?'),
(5756, 79, 44, 'De uiteindelijke kennis berust bij jouw Heer.'),
(5757, 79, 45, 'Jij bent slechts een waarschuwer voor wie er bang voor zijn.'),
(5758, 79, 46, 'En op de dag dat zij het zien zal het zijn alsof het voor hen niet langer dan een avond of de morgen erna geduurd heeft.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Hij fronste en keerde zich af'),
(5760, 80, 2, 'toen de blinde man bij hem kwam.'),
(5761, 80, 3, 'Maar hoe kun jij het weten, misschien dat hij zich loutert'),
(5762, 80, 4, 'of zich Iaat vermanen, zodat de vermaning hem baat.'),
(5763, 80, 5, 'Maar de zelfgenoegzame rijke'),
(5764, 80, 6, 'aan hem wijd je alle tijd.'),
(5765, 80, 7, 'Toch deert het jou niet dat hij zich niet loutert.'),
(5766, 80, 8, 'Maar wie op jou toe komt snellen'),
(5767, 80, 9, 'en daarbij vol vrees is,'),
(5768, 80, 10, 'aan hem schenk jij geen aandacht.'),
(5769, 80, 11, 'Nee toch, zij zijn een vermaning!'),
(5770, 80, 12, 'Dus, wie wil denkt eraan,'),
(5771, 80, 13, '[geschreven als zij zijn] op vereerde bladen,'),
(5772, 80, 14, 'die verheven zijn en reingemaakt,'),
(5773, 80, 15, 'met de hand van schrijvers,'),
(5774, 80, 16, 'die edel zijnen vroom.'),
(5775, 80, 17, 'De mens kan doodvallen, ondankbaar als hij is.'),
(5776, 80, 18, 'Waaruit heeft Hij hem geschapen?'),
(5777, 80, 19, 'Uit een druppel heeft Hij hem geschapen en toen heeft Hij zijn maat bepaald.'),
(5778, 80, 20, 'Dan heeft Hij de weg voor hem gebaand.'),
(5779, 80, 21, 'Dan laat Hij hem sterven en begraaft hem.'),
(5780, 80, 22, 'Dan, wanneer Hij wil, wekt Hij hem op.'),
(5781, 80, 23, 'Welnee, hij heeft nog niets uitgevoerd van wat Hij geboden heeft.'),
(5782, 80, 24, 'De mens moet maar eens zijn voedsel bekijken.'),
(5783, 80, 25, 'Dat Wij het water in gutsen uitgieten,'),
(5784, 80, 26, 'dan de aarde in voren openbreken'),
(5785, 80, 27, 'en dan erin laten ontspruiten: graan,'),
(5786, 80, 28, 'wijnstokken en voedergewassen,'),
(5787, 80, 29, 'olijfbomen en palmen,'),
(5788, 80, 30, 'in dichtbegroeide boomgaarden,'),
(5789, 80, 31, 'vruchten en foerage,'),
(5790, 80, 32, 'als vruchtgebruik voor jullie en jullie vee.'),
(5791, 80, 33, 'En wanneer dan de overdonderende komt'),
(5792, 80, 34, 'op de dag dat de man voor zijn broer vlucht'),
(5793, 80, 35, 'en voor zijn vader en zijn moeder'),
(5794, 80, 36, 'en voor zijn metgezellin en zijn zonen,'),
(5795, 80, 37, 'op die dag heeft elk van hen een zaak die hem bezighoudt.'),
(5796, 80, 38, 'Gezichten zullen er op die dag stralend zijn,'),
(5797, 80, 39, 'lachend en verheugd.'),
(5798, 80, 40, 'En gezichten zullen er op die dag zijn met stof erop'),
(5799, 80, 41, 'en met grauwheid overdekt.'),
(5800, 80, 42, 'Dat zijn zij, de ongelovigen, de overtreders.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Wanneer de zon wordt omwonden.'),
(5802, 81, 2, 'En wanneer de sterren neerstorten.'),
(5803, 81, 3, 'En wanneer de bergen in beweging gezet worden.'),
(5804, 81, 4, 'En wanneer de tiendemaandse kamelen in de steek gelaten worden.'),
(5805, 81, 5, 'En wanneer de wilde dieren verzameld worden.'),
(5806, 81, 6, 'En wanneer de zee aan het kolken gemaakt wordt.'),
(5807, 81, 7, 'En wanneer de zielen verenigd worden.'),
(5808, 81, 8, 'En wanneer aan het in de grond gestopte meisje gevraagd wordt'),
(5809, 81, 9, 'voor welke zonde zij gedood werd.'),
(5810, 81, 10, 'En wanneer de bladen opengeslagen worden.'),
(5811, 81, 11, 'En wanneer de hemel afgestroopt wordt.'),
(5812, 81, 12, 'En wanneer de hel opgestookt wordt.'),
(5813, 81, 13, 'En wanneer de tuin dichtbij gebracht wordt.'),
(5814, 81, 14, 'Dan weet een ziel wat zij heeft teweeggebracht.'),
(5815, 81, 15, 'Nee toch! Ik zweer bij de achteruitgaande planeten,'),
(5816, 81, 16, 'die voortsnellen en zich verschuilen!'),
(5817, 81, 17, 'Bij de nacht wanneer hij opdoemt!'),
(5818, 81, 18, 'Bij de morgen wanneer hij gloort!'),
(5819, 81, 19, 'Dit is het woord van een voortreffelijk gezant,'),
(5820, 81, 20, 'machtig bij de Heer van de troon en standvastig,'),
(5821, 81, 21, 'gehoorzaamd en ook betrouwbaar.'),
(5822, 81, 22, 'Jullie medeburger is geen bezetene.'),
(5823, 81, 23, 'Hij heeft hem immers aan de duidelijke horizon gezien.'),
(5824, 81, 24, 'Hij is niet gierig met [mededelingen over] het verborgene'),
(5825, 81, 25, 'en het is ook geen uitspraak van een vervloekte satan.'),
(5826, 81, 26, 'Waarheen zullen jullie dan gaan?'),
(5827, 81, 27, 'Het is slechts een vermaning voor de wereldbewoners,'),
(5828, 81, 28, 'voor wie van jullie correct willen handelen.'),
(5829, 81, 29, 'Maar jullie zullen het slechts willen als God het wil, de Heer van de wereldbewoners.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Wanneer de hemel splijt.'),
(5831, 82, 2, 'En wanneer de sterren verstrooid worden.'),
(5832, 82, 3, 'En wanneer de zeeën opengebroken worden.'),
(5833, 82, 4, 'En wanneer de graven omgewoeld worden.'),
(5834, 82, 5, 'Dan weet een ziel wat zij vroeger en later gedaan heeft.'),
(5835, 82, 6, 'O mens, wat heeft jou begoocheld aangaande jouw voortreffelijke Heer,'),
(5836, 82, 7, 'die jou geschapen heeft, dan gevormd en dan evenwichtig gemaakt?'),
(5837, 82, 8, 'In welke vorm Hij het wilde heeft Hij jou samengesteld.'),
(5838, 82, 9, 'Maar nee, jullie loochenen toch het oordeel.'),
(5839, 82, 10, 'Maar er zijn bewaarders voor jullie,'),
(5840, 82, 11, 'voortreffelijke, die opschrijven,'),
(5841, 82, 12, 'die weten wat jullie doen.'),
(5842, 82, 13, 'De vromen verkeren in gelukzaligheid,'),
(5843, 82, 14, 'maar de overtreders zijn in het hellevuur.'),
(5844, 82, 15, 'Daarin braden zij op de oordeelsdag.'),
(5845, 82, 16, 'Zij kunnen het niet ontgaan.'),
(5846, 82, 17, 'En hoe kom jij te weten wat de oordeelsdag is?'),
(5847, 82, 18, 'Nog eens, hoe kom jij te weten wat de oordeelsdag is?'),
(5848, 82, 19, 'Op de dag waarop niemand iets voor een ander kan uitrichten. Op die dag heeft God het bevel.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Wee de knoeiers'),
(5850, 83, 2, 'die, wanneer zij zich door de mensen iets laten afmeten, de volle maat verlangen,'),
(5851, 83, 3, 'maar die zelf, wanneer zij afmeten of afwegen, te weinig geven.'),
(5852, 83, 4, 'Denken die mensen dan niet dat zij opgewekt worden'),
(5853, 83, 5, 'op een geweldige dag,'),
(5854, 83, 6, 'op de dag waarop de mensen voor de Heer der Werelden staan?'),
(5855, 83, 7, 'Welnee! Het boek van de overtreders is in Siddjien.'),
(5856, 83, 8, 'En hoe kom jij te weten wat dat is: Siddjien?'),
(5857, 83, 9, 'Een vol beschreven boek.'),
(5858, 83, 10, 'Wee op die dag de loochenaars'),
(5859, 83, 11, 'die de oordeelsdag loochenen.'),
(5860, 83, 12, 'Maar alleen elke zondige overtreder loochent hem.'),
(5861, 83, 13, 'Wanneer aan hem Onze tekenen voorgelezen worden zegt hij: "Fabels van hen die er eertijds waren."'),
(5862, 83, 14, 'Welnee! Maar wat zij begaan hebben is aan hun harten aangekoekt.'),
(5863, 83, 15, 'Welnee! Op die dag worden zij van hun Heer afgeschermd.'),
(5864, 83, 16, 'Dan zullen zij in het hellevuur braden.'),
(5865, 83, 17, 'En dan zal gezegd worden: "Dit is het wat jullie loochenden!"'),
(5866, 83, 18, 'Welnee! Het boek van de vromen is in \'Illijjoen.'),
(5867, 83, 19, 'En hoe kom jij te weten wat dat is: \'Illijjoen?'),
(5868, 83, 20, 'Een vol beschreven boek,'),
(5869, 83, 21, 'waarvan zij die in de nabijheid [van God] zijn gebracht getuige zijn.'),
(5870, 83, 22, 'De vromen verkeren in gelukzaligheid.'),
(5871, 83, 23, 'Op rustbanken kijken zij.'),
(5872, 83, 24, 'Je herkent in hun gezichten het stralende van de gelukzaligheid.'),
(5873, 83, 25, 'Hun wordt verzegelde edele wijn te drinken gegeven,'),
(5874, 83, 26, 'waarvan het zegel muskus is -- daarvoor zou men om het hardst willen lopen --'),
(5875, 83, 27, 'en die is bijgemengd uit Tasniem,'),
(5876, 83, 28, 'een bron waaruit zij die in de nabijheid [van God] zijn gebracht drinken.'),
(5877, 83, 29, 'Zij die boosdoeners waren lachten om hen die geloofden.'),
(5878, 83, 30, 'En wanneer zij hen voorbijkwamen knipoogden zij naar elkaar'),
(5879, 83, 31, 'en wanneer zij naar hun huisgenoten terugkeerden, kwamen zij spottend weerom.'),
(5880, 83, 32, 'En wanneer zij hen zagen zeiden zij: "Die daar verkeren in dwaling."'),
(5881, 83, 33, 'Toch waren zij niet als bewakers naar hen gezonden.'),
(5882, 83, 34, 'Maar vandaag lachen zij die geloven om de ongelovigen.'),
(5883, 83, 35, 'Op rustbanken kijken zij.'),
(5884, 83, 36, 'Zijn de ongelovigen beloond voor wat zij gedaan hebben?');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Wanneer de hemel barst'),
(5886, 84, 2, 'en naar zijn Heer luistert en zijn plicht nakomt.'),
(5887, 84, 3, 'En wanneer de aarde uitgebreid wordt'),
(5888, 84, 4, 'en uitwerpt wat er in haar is en zich leegt'),
(5889, 84, 5, 'en naar haar Heer luistert en haar plicht nakomt.'),
(5890, 84, 6, 'O mens, jij streeft met inspanning naar jouw Heer en jij zult Hem ontmoeten.'),
(5891, 84, 7, 'Wie dan zijn boek in zijn rechterhand krijgt,'),
(5892, 84, 8, 'met hem zal schappelijk worden afgerekend.'),
(5893, 84, 9, 'En hij zal blij naar zijn familie terugkeren.'),
(5894, 84, 10, 'Maar wie zijn boek achter zijn rug krijgt aangereikt,'),
(5895, 84, 11, 'zal ach en wee roepen'),
(5896, 84, 12, 'en in de vuurgloed braden.'),
(5897, 84, 13, 'Hij was blij bij zijn familie.'),
(5898, 84, 14, 'Hij dacht dat hij niet terug zou keren.'),
(5899, 84, 15, 'Maar nee, jouw Heer doorzag hem wel.'),
(5900, 84, 16, 'Nee toch! Ik zweer bij het avondrood,'),
(5901, 84, 17, 'bij de nacht en wat hij verhult'),
(5902, 84, 18, 'en bij de maan wanneer zij vol is.'),
(5903, 84, 19, 'Jullie zullen fase na fase doorlopen.'),
(5904, 84, 20, 'Wat hebben zij toch dat zij niet geloven'),
(5905, 84, 21, 'en wanneer de Koran aan hen wordt voorgelezen dat zij zich niet eerbiedig neerbuigen? --'),
(5906, 84, 22, 'Welnee, zij die ongelovig zijn loochenen [het].'),
(5907, 84, 23, 'Maar God weet het best wat zij in zich bergen.'),
(5908, 84, 24, 'Verkondig hun dan een pijnlijke bestraffing.'),
(5909, 84, 25, 'Maar niet aan hen die geloven en de deugdelijke daden doen; voor hen is er een ononderbroken loon.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Bij de hemel vol sterrenbeelden!'),
(5911, 85, 2, 'Bij de aangezegde dag!'),
(5912, 85, 3, 'Bij een getuige en wat getuigd wordt!'),
(5913, 85, 4, 'Doodvallen mogen de makers van de kuil,'),
(5914, 85, 5, 'van het vuur met veel brandstof.'),
(5915, 85, 6, 'Toen zij erbij zaten'),
(5916, 85, 7, 'en getuige waren van wat zij de gelovigen aandeden!'),
(5917, 85, 8, 'Zij koesterden alleen maar wrok tegen hen omdat zij geloofden in God, de machtige, de lofwaardige,'),
(5918, 85, 9, 'die de heerschappij over de hemelen en de aarde heeft. En God is van alles getuige.'),
(5919, 85, 10, 'Zij die gelovige mannen en vrouwen aan verzoeking blootstellen en dan geen berouw tonen, voor hen is de bestraffing van de hel, voor hen is de bestraffing met het vuur.'),
(5920, 85, 11, 'Zij die geloven en de deugdelijke daden doen, voor hen zijn er tuinen waar de rivieren onderdoor stromen. Dat is de grote triomf!'),
(5921, 85, 12, 'Het geweld van jouw Heer is hevig.'),
(5922, 85, 13, 'Hij is het die [de schepping] laat beginnen en die [haar] herhaalt.'),
(5923, 85, 14, 'Hij is de vergevende, de liefdevolle,'),
(5924, 85, 15, 'de Heer van de grote troon'),
(5925, 85, 16, 'die doet wat Hij wenst.'),
(5926, 85, 17, 'Is het verhaal van de troepenmachten tot jou gekomen?'),
(5927, 85, 18, 'Van Fir\'aun en de Thamoed?'),
(5928, 85, 19, 'Welnee, zij die ongelovig zijn blijven steeds loochenen.'),
(5929, 85, 20, 'Maar God omvat hen van achteren.'),
(5930, 85, 21, 'Ja zeker, het is een glorierijke Koran,'),
(5931, 85, 22, 'op een goedbewaard paneel.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Bij de hemel en de nachtster!'),
(5933, 86, 2, 'En hoe kom jij te weten wat de nachtster is?'),
(5934, 86, 3, 'De doordringende ster.'),
(5935, 86, 4, 'Er is niemand voor wie er geen bewaker is.'),
(5936, 86, 5, 'De mens moet maar eens kijken waaruit hij geschapen is.'),
(5937, 86, 6, 'Geschapen is hij uit gutsend vocht,'),
(5938, 86, 7, 'dat tussen de lendenen en de ribben tevoorschijn komt.'),
(5939, 86, 8, 'Om hem terug te brengen, daartoe heeft Hij de macht,'),
(5940, 86, 9, 'op de dag dat de geheimen worden getoetst.'),
(5941, 86, 10, 'Dan heeft hij geen kracht en geen helper.'),
(5942, 86, 11, 'Bij de hemel met zijn kringloop!'),
(5943, 86, 12, 'Bij de aarde die uitbot!'),
(5944, 86, 13, 'Het zijn beslissende woorden.'),
(5945, 86, 14, 'Het is geen scherts.'),
(5946, 86, 15, 'Zij beramen een list.'),
(5947, 86, 16, 'En Ik zal een list beramen.'),
(5948, 86, 17, 'Geef de ongelovigen dan maar uitstel, verleen hun enig uitstel.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Prijs de naam van jouw Heer, de hoogste,'),
(5950, 87, 2, 'die geschapen heeft en gevormd'),
(5951, 87, 3, 'en die geordend heeft en de goede richting gewezen'),
(5952, 87, 4, 'en die het weidegras laat opkomen'),
(5953, 87, 5, 'en het daarna dor en grauw maakt.'),
(5954, 87, 6, 'Wij zullen jou laten voorlezen en jij zult het niet vergeten,'),
(5955, 87, 7, 'behalve wat God wil -- Hij kent wat openbaar is en wat verborgen blijft.'),
(5956, 87, 8, 'Wij leggen jou namelijk een gemakkelijke taak op.'),
(5957, 87, 9, 'Vermaan dus, als de vermaning nut heeft.'),
(5958, 87, 10, 'Wie vreest zal zich laten vermanen.'),
(5959, 87, 11, 'Maar vermijden zal het de ellendeling'),
(5960, 87, 12, 'die in het grote vuur zal braden.'),
(5961, 87, 13, 'Hij zal daarin dan niet sterven en niet leven.'),
(5962, 87, 14, 'Maar welgaan zal het wie zich loutert,'),
(5963, 87, 15, 'de naam van zijn Heer noemt en de salaat bidt.'),
(5964, 87, 16, 'Maar nee, jullie verkiezen het tegenwoordige leven.'),
(5965, 87, 17, 'Maar het hiernamaals is beter en onvergankelijker.'),
(5966, 87, 18, 'Dit staat al in de eerdere bladen,'),
(5967, 87, 19, 'de bladen van Ibrahiem en Moesa.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'Is het verhaal van de bedekking tot jou gekomen?'),
(5969, 88, 2, 'Terneergeslagen gezichten zijn er op die dag,'),
(5970, 88, 3, 'die zwoegen en zich afsloven,'),
(5971, 88, 4, 'die in een heet vuur braden'),
(5972, 88, 5, 'en die te drinken krijgen uit een gloeiende bron.'),
(5973, 88, 6, 'Hun voedsel bestaat slechts uit doornstruiken.'),
(5974, 88, 7, 'Die maken niet vet en stillen de honger niet.'),
(5975, 88, 8, 'Zalige gezichten zijn er op die dag,'),
(5976, 88, 9, 'die met hun streven tevreden zijn,'),
(5977, 88, 10, 'in een hooggelegen tuin,'),
(5978, 88, 11, 'waarin je geen onzin hoort.'),
(5979, 88, 12, 'Daar is een stromende bron.'),
(5980, 88, 13, 'Daar zijn verhoogde rustbedden,'),
(5981, 88, 14, 'klaargezette bekers,'),
(5982, 88, 15, 'rijen van kussens'),
(5983, 88, 16, 'en uitgespreide tapijten.'),
(5984, 88, 17, 'Kijken zij dan niet naar de kamelen hoe zij zijn geschapen?'),
(5985, 88, 18, 'En naar de hemel hoe hij is opgeheven?'),
(5986, 88, 19, 'En naar de bergen hoe zij zijn opgericht?'),
(5987, 88, 20, 'En naar de aarde hoe zij is geëffend?'),
(5988, 88, 21, 'Vermaan dan; jij bent slechts een vermaner.'),
(5989, 88, 22, 'En jij bent geen heerser over hen.'),
(5990, 88, 23, 'Maar wie zich afkeert en ongelovig is,'),
(5991, 88, 24, 'hem zal God straffen met de grootste bestraffing.'),
(5992, 88, 25, 'Tot Ons is hun terugkeer.'),
(5993, 88, 26, 'Dan is bij Ons hun afrekening.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Bij de dageraad!'),
(5995, 89, 2, 'Bij tien nachten!'),
(5996, 89, 3, 'Bij even en oneven!'),
(5997, 89, 4, 'Bij de nacht wanneer hij afloopt!'),
(5998, 89, 5, 'Is dat zo geen eed voor wie verstand heeft?'),
(5999, 89, 6, 'Heb jij niet gezien hoe jouw Heer met de \'Aad gehandeld heeft?'),
(6000, 89, 7, 'Met Iram van de pilaren,'),
(6001, 89, 8, 'zoals er in het land nooit geschapen waren?'),
(6002, 89, 9, 'En de Thamoed die de rotsen in de vallei uithieuwen?'),
(6003, 89, 10, 'En met Fir\'aun van de tentpinnen?'),
(6004, 89, 11, 'Die allen onbeschaamd in het land optraden?'),
(6005, 89, 12, 'En die toen steeds meer verderf brachten?'),
(6006, 89, 13, 'Toen liet God de gesel van de bestraffing over hen heen gaan.'),
(6007, 89, 14, 'Jouw Heer ligt in de hinderlaag.'),
(6008, 89, 15, 'Maar de mens zegt, wanneer zijn Heer hem op de proef stelt en hem dan mild behandelt en een aangenaam leven geeft: "Mijn Heer heeft mij mild behandeld."'),
(6009, 89, 16, 'Maar als Hij hem op de proef stelt en hem dan zijn levensonderhoud beperkt dan zegt hij: "Mijn Heer heeft mij vernederd."'),
(6010, 89, 17, 'Welnee, maar jullie behandelen de wees niet mild.'),
(6011, 89, 18, 'En jullie sporen elkaar niet aan de behoeftige voedsel te geven.'),
(6012, 89, 19, 'Jullie verteren het erfdeel gretig.'),
(6013, 89, 20, 'Jullie zijn volkomen verzot op bezit.'),
(6014, 89, 21, 'Maar nee, wanneer de aarde tot gruis vergruisd wordt'),
(6015, 89, 22, 'en jouw Heer en de engelen komen, in rij na rij,'),
(6016, 89, 23, 'en op die dag de hel gebracht wordt, op die dag laat de mens zich vermanen. Maar wat moet hij dan met de vermaning?'),
(6017, 89, 24, 'Hij zegt: "Ach, had ik voor mijn leven maar voorzorgsmaatregelen genomen."'),
(6018, 89, 25, 'Maar op die dag is er niet een die bestraft zoals Hij'),
(6019, 89, 26, 'en niet een die in de boeien slaat zoals Hij.'),
(6020, 89, 27, '"O ziel die rust gevonden heeft,'),
(6021, 89, 28, 'keer tevreden en met welgevallen aanvaard terug naar jouw Heer.'),
(6022, 89, 29, 'En treed binnen te midden van Mijn dienaren'),
(6023, 89, 30, 'en treed binnen in Mijn tuin."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Nee toch! Ik zweer bij deze stad!'),
(6025, 90, 2, 'Bij deze stad waarin jij woonachtig bent!'),
(6026, 90, 3, 'Bij een vader en wat hij verwekt heeft!'),
(6027, 90, 4, 'Wij hebben de mens toch in benardheid geschapen.'),
(6028, 90, 5, 'Denkt hij dan dat niet één iets tegen hem vermag?'),
(6029, 90, 6, 'Hij zegt: "Ik heb een aanzienlijk vermogen verbruikt."'),
(6030, 90, 7, 'Denkt hij dan dat niet één hem zag?'),
(6031, 90, 8, 'Hebben Wij voor hem niet twee ogen gemaakt'),
(6032, 90, 9, 'en een tong en twee lippen?'),
(6033, 90, 10, 'En hebben Wij hem niet de twee wegen gewezen?'),
(6034, 90, 11, 'Toch is hij de steile weg niet ingeslagen.'),
(6035, 90, 12, 'En hoe kom jij te weten wat de steile weg is?'),
(6036, 90, 13, 'Vrijlating van een slaaf'),
(6037, 90, 14, 'of voedsel geven op een dag van hongersnood'),
(6038, 90, 15, 'aan een wees uit de verwantschap'),
(6039, 90, 16, 'of aan een arme behoeftige.'),
(6040, 90, 17, 'En verder, dat hij behoort tot hen die geloven, die elkaar tot volharding aanmanen en die elkaar tot barmhartigheid aanmanen.'),
(6041, 90, 18, 'Dat zijn zij die aan de rechterkant staan.'),
(6042, 90, 19, 'Maar zij die aan Onze tekenen geen geloof hechten, zij zijn het die aan de linkerkant staan,'),
(6043, 90, 20, 'zij worden door een vuur omsloten.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Bij de zon en haar morgenlicht!'),
(6045, 91, 2, 'Bij de maan, wanneer zij haar volgt!'),
(6046, 91, 3, 'Bij de dag, wanneer zij hem beschijnt!'),
(6047, 91, 4, 'Bij de nacht, wanneer hij haar bedekt!'),
(6048, 91, 5, 'Bij de hemel en wie hem heeft gebouwd!'),
(6049, 91, 6, 'Bij de aarde en wie haar heeft uitgespreid!'),
(6050, 91, 7, 'Bij een ziel en wie haar heeft gevormd!'),
(6051, 91, 8, 'En die haar toen haar zondigheid en haar godvrezendheid heeft ingegeven!'),
(6052, 91, 9, 'Wel gaat het wie haar loutert.'),
(6053, 91, 10, 'Maar teleurgesteld wordt wie haar laat verkommeren.'),
(6054, 91, 11, 'De Thamoed hebben het in hun onbeschaamdheid geloochend,'),
(6055, 91, 12, 'toen de ellendeling onder hen opstond.'),
(6056, 91, 13, 'Toen zei Gods gezant tot hen: "Pas op met Gods kameelmerrie en haar tijd om te drinken."'),
(6057, 91, 14, 'Maar zij betichtten hem van leugens en sneden haar hielpezen door. Toen vernietigde hun Heer hen om hun zonden en maakte hen met de grond gelijk'),
(6058, 91, 15, 'en Hij vreesde de gevolgen niet.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Bij de nacht wanneer hij toedekt!'),
(6060, 92, 2, 'Bij de dag wanneer hij verschijnt!'),
(6061, 92, 3, 'Bij Hem die het mannelijke en het vrouwelijke heeft geschapen!'),
(6062, 92, 4, 'Jullie streven is echt velerlei.'),
(6063, 92, 5, 'Hij dan die geeft en godvrezend is'),
(6064, 92, 6, 'en aan het allermooiste gelooft,'),
(6065, 92, 7, 'hem leggen Wij een gemakkelijke taak op.'),
(6066, 92, 8, 'Hij dan die gierig en zelfgenoegzaam is'),
(6067, 92, 9, 'en die het allermooiste loochent,'),
(6068, 92, 10, 'hem leggen Wij een moeilijke taak op.'),
(6069, 92, 11, 'Zijn bezit baat hem niet als hij in de afgrond stort.'),
(6070, 92, 12, 'Het is Onze taak de goede weg te wijzen.'),
(6071, 92, 13, 'En van Ons is het hiernamaals en het tegenwoordige bestaan.'),
(6072, 92, 14, 'Ik waarschuw jullie dus voor een vuur dat laait.'),
(6073, 92, 15, 'Daarin zal slechts de ellendeling braden'),
(6074, 92, 16, 'die het geloochend en zich afgekeerd heeft.'),
(6075, 92, 17, 'Maar de godvrezende zal daarvan ver gehouden worden'),
(6076, 92, 18, 'hij die zijn bezit gegeven heeft om zich te louteren'),
(6077, 92, 19, 'en niet om voor een weldaad door iemand beloond te worden,'),
(6078, 92, 20, 'maar uit zijn verlangen naar het aangezicht van zijn hoogste Heer.'),
(6079, 92, 21, 'En Hij zal tevreden zijn.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Bij het morgenlicht!'),
(6081, 93, 2, 'Bij de nacht wanneer het stil is!'),
(6082, 93, 3, 'Jouw Heer heeft je niet verlaten, noch gehaat.'),
(6083, 93, 4, 'En het hiernamaals is echt beter voor jou dan het tegenwoordige bestaan.'),
(6084, 93, 5, 'Jouw Heer zal jou zeker geven en dan ben jij voldaan.'),
(6085, 93, 6, 'Heeft Hij jou niet als wees gevonden en onderdak gegeven?'),
(6086, 93, 7, 'Jou dwalend gevonden en jou de weg gewezen?'),
(6087, 93, 8, 'Jou behoeftig gevonden en rijk gemaakt?'),
(6088, 93, 9, 'De wees dus, verdruk hem niet.'),
(6089, 93, 10, 'De bedelaar, verjaag hem niet.'),
(6090, 93, 11, 'En de genade van jouw Heer, vertel daarvan!');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Hebben Wij bij jou je hart niet opengesteld'),
(6092, 94, 2, 'en jou je last afgenomen,'),
(6093, 94, 3, 'die zwaar op jouw rug drukte?'),
(6094, 94, 4, 'En Wij hebben jouw aanzien vergroot.'),
(6095, 94, 5, 'Want met het moeilijke hangt het gemakkelijke samen.'),
(6096, 94, 6, 'Ja, met het moeilijke hangt het gemakkelijke samen!'),
(6097, 94, 7, 'En wanneer je klaar bent span je dan in'),
(6098, 94, 8, 'en richt je verlangen tot jouw Heer.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Bij de vijgenboom en de olijfboom!'),
(6100, 95, 2, 'Bij de berg Sinien!'),
(6101, 95, 3, 'Bij deze veilige stad!'),
(6102, 95, 4, 'Wij hebben de mens in de mooiste gestalte geschapen.'),
(6103, 95, 5, 'Daarna laten Wij hem weer diep zakken,'),
(6104, 95, 6, 'behalve hen die geloven en de deugdelijke daden doen. Voor hen is er immers een ononderbroken loon.'),
(6105, 95, 7, 'Wat kan jou over het oordeel nog van leugens betichten?'),
(6106, 95, 8, 'Is God niet de wijste van hen die oordelen?');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Lees voor in de naam van jouw Heer die heeft geschapen.'),
(6108, 96, 2, 'Geschapen heeft Hij de mens uit een bloedklonter.'),
(6109, 96, 3, 'Lees voor! Jouw Heer is de edelmoedigste,'),
(6110, 96, 4, 'die onderwezen heeft met de pen.'),
(6111, 96, 5, 'Hij heeft de mens onderwezen wat hij niet wist.'),
(6112, 96, 6, 'Welnee, de mens is onbeschaamd,'),
(6113, 96, 7, 'dat hij zich behoefteloos waant.'),
(6114, 96, 8, 'Maar tot zijn Heer is de terugkeer.'),
(6115, 96, 9, 'Heb jij hem gezien die een verbod oplegt'),
(6116, 96, 10, 'aan een dienaar wanneer hij bidt?'),
(6117, 96, 11, 'Meen jij dat hij op de goede weg is'),
(6118, 96, 12, 'of godvrezendheid gebiedt?'),
(6119, 96, 13, 'Meen jij dat hij loochent en zich afkeert?'),
(6120, 96, 14, 'Weet hij niet dat God ziet?'),
(6121, 96, 15, 'Welnee! Als hij niet ophoudt zullen Wij hem bij de kuif grijpen,'),
(6122, 96, 16, 'een zondige leugenachtige kuif.'),
(6123, 96, 17, 'Laat hij dan zijn bende maar roepen.'),
(6124, 96, 18, 'Wij zullen de hellewachters roepen.'),
(6125, 96, 19, 'Nee hoor, gehoorzaam hem niet, maar buig je eerbiedig neer en kom nader.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Wij hebben hem neergezonden in de nacht van de beslissing.'),
(6127, 97, 2, 'En hoe kom jij te weten wat de nacht van de beslissing is?'),
(6128, 97, 3, 'De nacht van de beslissing is beter dan duizend maanden.'),
(6129, 97, 4, 'De engelen en de geest dalen erin neer met toestemming van hun Heer voor elke beschikking.'),
(6130, 97, 5, 'Vol vrede is hij tot aan de opkomst van de dageraad.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Zij onder de mensen van het boek die ongelovig zijn en de veelgodendienaars zullen pas ophouden als het duidelijke bewijs tot hen komt:'),
(6132, 98, 2, 'Een gezant van God die reingemaakte bladen aan hen voorleest,'),
(6133, 98, 3, 'waarin juiste dingen geschreven staan.'),
(6134, 98, 4, 'Zij aan wie het boek gegeven is hebben zich pas nadat het duidelijke bewijs tot hen gekomen was opgesplitst.'),
(6135, 98, 5, 'Hun werd slechts bevolen God te dienen en daarbij als aanhangers van het zuivere geloof de godsdienst geheel aan Hem te wijden en de salaat te verrichten en de zakaat te geven. Dat is de juiste godsdienst.'),
(6136, 98, 6, 'Zij onder de mensen van het boek die ongelovig zijn en de veelgodendienaars zullen in het vuur van de hel zijn, waarin zij altijd zullen blijven; zij zijn het slechtst af van de schepping.'),
(6137, 98, 7, 'Maar zij die geloven en de deugdelijke daden doen, zij zijn het best af van de schepping.'),
(6138, 98, 8, 'Als hun loon zijn er bij hun Heer de tuinen van \'Adn waar de rivieren onderdoor stromen; daarin zullen zij altijd blijven. God heeft een welgevallen aan hen en zij hebben een welgevallen aan Hem. Dat is voor wie zijn Heer vreest.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Wanneer de aarde bevend dooreen wordt geschud'),
(6140, 99, 2, 'en haar lasten tevoorschijn brengt'),
(6141, 99, 3, 'en de mens zegt: "Wat heeft zij toch?"'),
(6142, 99, 4, 'Op die dag zal zij haar berichten meedelen'),
(6143, 99, 5, 'die jouw Heer haar heeft geopenbaard.'),
(6144, 99, 6, 'Op die dag zullen de mensen afzonderlijk voorkomen om hun daden getoond te krijgen.'),
(6145, 99, 7, 'En wie ook maar een greintje goeds heeft gedaan die zal het zien.'),
(6146, 99, 8, 'En wie ook maar een greintje kwaads heeft gedaan die zal het zien.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Bij de snuivend rennenden'),
(6148, 100, 2, 'en de vonken slaanden'),
(6149, 100, 3, 'en de \'s morgens aanstormenden,'),
(6150, 100, 4, 'die dan stof opwerpen'),
(6151, 100, 5, 'en dan midden in de slagorde doorbreken!'),
(6152, 100, 6, 'De mens is zijn Heer niet erkentelijk.'),
(6153, 100, 7, 'Hij is er zelf getuige van.'),
(6154, 100, 8, 'Het bezit heeft hij hevig lief. *'),
(6155, 100, 9, 'Weet hij het dan niet? Wanneer wat in de graven is wordt omgewoeld'),
(6156, 100, 10, 'en wat in het binnenste is tevoorschijn wordt gebracht,'),
(6157, 100, 11, 'dan is hun Heer op die dag over hen welingelicht.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'De catastrofe!'),
(6159, 101, 2, 'Wat is de catastrofe?'),
(6160, 101, 3, 'En hoe zul jij te weten komen wat de catastrofe is?'),
(6161, 101, 4, 'Op de dag dat de mensen als verspreide motten zijn'),
(6162, 101, 5, 'en de bergen als gekaarde wol.'),
(6163, 101, 6, 'En van wie de weegschalen dan zwaar wegen,'),
(6164, 101, 7, 'die zal dan een tevreden leven leiden.'),
(6165, 101, 8, 'En van wie de weegschalen dan licht wegen'),
(6166, 101, 9, 'diens moeder is de afgrond.'),
(6167, 101, 10, 'En hoe zul jij te weten komen wat zij is?'),
(6168, 101, 11, 'Een gloeiend vuur!');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'De wens naar meer houdt jullie zozeer bezig,'),
(6170, 102, 2, 'dat jullie zelfs de graven bezoeken.'),
(6171, 102, 3, 'Maar nee, jullie zullen het weten.'),
(6172, 102, 4, 'Nog eens, maar nee, jullie zullen het weten.'),
(6173, 102, 5, 'Maar nee, als jullie het maar zeker zouden weten.'),
(6174, 102, 6, 'Jullie zullen zeker het hellevuur zien.'),
(6175, 102, 7, 'Nog eens, jullie zullen het met een zeker oog zien.'),
(6176, 102, 8, 'Dan zullen jullie op die dag ondervraagd worden over het geluk.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Bij de namiddag!'),
(6178, 103, 2, 'Zeker, de mens lijdt echt verlies.'),
(6179, 103, 3, 'Alleen niet zij die geloven en de deugdelijke daden doen, die elkaar aanmanen tot de waarheid en die elkaar aanmanen tot volharding.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Wee elke lasteraar en roddelaar'),
(6181, 104, 2, 'die bezit bijeenbrengt en het telkens telt'),
(6182, 104, 3, 'en daarbij denkt dat zijn bezit hem onsterfelijk maakt.'),
(6183, 104, 4, 'Welnee, hij wordt in de verbrijzelaar gesmeten.'),
(6184, 104, 5, 'En hoe kom jij te weten wat de verbrijzelaar is?'),
(6185, 104, 6, 'Gods aangestoken vuur,'),
(6186, 104, 7, 'dat in de harten doordringt.'),
(6187, 104, 8, 'Het zal hen omsluiten,'),
(6188, 104, 9, 'in langgerekte zuilen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Heb jij niet gezien hoe jouw Heer met de mensen van de olifant heeft gehandeld?'),
(6190, 105, 2, 'Heeft Hij hun list niet op een dwaalspoor gebracht?'),
(6191, 105, 3, 'En Hij heeft tegen hen zwermen vogels gezonden'),
(6192, 105, 4, 'die bakstenen op hen wierpen.'),
(6193, 105, 5, 'En hij maakte hen als afgevreten halmen.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'Daarvoor dat de Koeraisj samenbrengen,'),
(6195, 106, 2, 'dat zij hun zomer- en winterkaravaan samenbrengen.'),
(6196, 106, 3, 'Laten zij daarvoor de Heer van dit huis dienen,'),
(6197, 106, 4, 'die hun voedsel tegen de honger geeft en die hen beveiligt voor de angst.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Heb jij hem gezien die de godsdienst loochent?'),
(6199, 107, 2, 'Dat is hij die de wees wegduwt'),
(6200, 107, 3, 'en die er niet op aandringt de behoeftige voedsel te geven.'),
(6201, 107, 4, 'En wee hen die de salaat bidden'),
(6202, 107, 5, 'die hun salaat veronachtzamen,'),
(6203, 107, 6, 'die vertoon willen maken'),
(6204, 107, 7, 'en die de hulpverlening weigeren.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Wij waren het die jou de overvloed gaven!'),
(6206, 108, 2, 'Bid dan tot jouw Heer en breng offers.'),
(6207, 108, 3, 'Jouw hater, hij is de alleenstaande.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'Zeg: "O ongelovigen.'),
(6209, 109, 2, 'Ik zal niet dienen wat jullie dienen.'),
(6210, 109, 3, 'En jullie dienen niet wat ik dien:'),
(6211, 109, 4, 'En ik dien niet wat jullie dienen.'),
(6212, 109, 5, 'En jullie dienen niet wat ik dien.'),
(6213, 109, 6, 'Jullie hebben jullie godsdienst en ik heb mijn godsdienst."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Wanneer Gods hulp komt en het succes'),
(6215, 110, 2, 'en jij de mensen in drommen in Gods godsdienst ziet binnengaan,'),
(6216, 110, 3, 'prijs dan de lof van jouw Heer en vraag Hem om vergeving; Hij is een genadegever.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'De handen van Aboe Lahab mogen kapotgaan en hij mag zelf kapotgaan.'),
(6218, 111, 2, 'Zijn bezit en wat hij verworven heeft baat hem niet.'),
(6219, 111, 3, 'Hij zal braden in een vuur vol vlammen.'),
(6220, 111, 4, 'En ook zijn vrouw, die het hout aandraagt'),
(6221, 111, 5, 'met om haar nek een koord van touw.');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'Zeg: "Hij is God als enige,'),
(6223, 112, 2, 'God de bestendige.'),
(6224, 112, 3, 'Hij heeft niet verwekt en is niet verwekt'),
(6225, 112, 4, 'en niet één is er aan Hem gelijkwaardig."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'Zeg: "Ik zoek bescherming bij de Heer van de ochtendschemering'),
(6227, 113, 2, 'tegen het kwaad dat Hij geschapen heeft'),
(6228, 113, 3, 'en tegen het kwaad van de donkere nacht wanneer hij aanbreekt'),
(6229, 113, 4, 'en tegen het kwaad van haar die op de knopen blazen'),
(6230, 113, 5, 'en tegen het kwaad van een jaloerse wanneer hij jaloers is."');
INSERT INTO `nl_leemhuis` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'Zeg: "Ik zoek bescherming bij de Heer van de mensen,'),
(6232, 114, 2, 'de koning van de mensen,'),
(6233, 114, 3, 'de god van de mensen,'),
(6234, 114, 4, 'tegen het kwaad van de stiekeme influisteraar'),
(6235, 114, 5, 'die de mensen in hun binnenste influistert,'),
(6236, 114, 6, 'of hij nu tot de djinn of tot de mensen behoort."');
