INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'In de naam van Allah, de Erbarmer, de Meest Barmhartige.'),
(2, 1, 2, 'Alle lof zij Allah, de Heer der Werelden.'),
(3, 1, 3, 'De Erbarmer, de Meest Barmhartige.'),
(4, 1, 4, 'De Heerser op de Dag des Oordeels.'),
(5, 1, 5, 'U alleen aanbidden wij en U alleen vragen wij om hulp.'),
(6, 1, 6, 'Leid ons op het rechte Pad.'),
(7, 1, 7, 'Het Pad van degenen aan wie U gunsten hebt geschonken, niet van degenen op wie de toorn rust en niet dat van de dwalenden.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'Alif Lâm Mîm.'),
(9, 2, 2, 'Dit is het Boek (de Koran) waaraan geen twijfel is, een leidraad voor de Moettaqôen.'),
(10, 2, 3, 'Degenen die in het onwaarneembare geloven en de shalât onderhouden en die bijdragen geven van waar Wij hun mee hebben voorzien.'),
(11, 2, 4, 'En degenen die geloven in wat aan jou (O Moehammad) is neergezonden (de Koran) en in wat vóór jou is neergezonden en die van het (bestaan van het) Hiernamaals overtuigd zijn.'),
(12, 2, 5, 'Zij zijn depenen die Leiding van hun Heer ontvangen en zij zijn degenen die welslagen.'),
(13, 2, 6, 'Voorwaar, voor degenen die niet geloven, maakt het geen verschil of jij hen waarschuwt of dat jij hen niet waarschuwt: zij zullen niet geloven.'),
(14, 2, 7, 'Allah heeft hun harten en hun gehoor verzegeld en over hun ogen ligt een bedekking en voor hen is er een geweldige bestraffing.'),
(15, 2, 8, 'En er zijn er onder de mensen die zeggen: "Wij geloven in Allah en in de Laatste Dag," terwijl zij geen gelovigen zijn.'),
(16, 2, 9, 'Zij trachten Allah en degenen die geloven te bedriegen, maar zij bedriegen niemand dan zichzelf, terwijl zij het niet beseffen.'),
(17, 2, 10, 'In hun hart is een ziekte (twijfel en huichelarij) en Allah heeft deze ziekte doen verergeren, en voor hen is er een pijnlijke bestratting vanwege wat zij plachten te loochenen.'),
(18, 2, 11, 'En als er tot hen wordt gezegd: "Zaait geen verderf op aarde," dan zeggen zij: "Voorwaar, wij zijn slechts verbeteraars." (van de juiste normen en waarden)'),
(19, 2, 12, 'Weet: voorwaar, zij zijn de verderfzaaiers, maar zij beseffen liet niet.'),
(20, 2, 13, 'En als er tot hen wordt gezegd: "Gelooft zoals de mensen gcloven," dan zeggen zij: "Zullen wij geloven zoals dc dwazen geloven?" Voorwaar, zij zkin dc dwazen, maar zij weten het niet.'),
(21, 2, 14, 'En als zij degenen die geloven ontmoeten, dan zeggen zij: "Wij geloven." Maar wanneer zij terugkeren naar hun Satans (die hun leiders zijn) dan zeggen zij: "Voorwaar, wij staan aan jullie kant. Voorwaar, wij zijn slechts spotters."'),
(22, 2, 15, 'Allah spot met hen en laat ben rusteloos in hun overtredingen verkeren.'),
(23, 2, 16, 'Zij zijn degenen die de Leiding voor de dwaling hebben verruild, daarom levert hun handel geen winst op, en zij zijn geen rechtgeleidenen.'),
(24, 2, 17, 'Hun gelijkenis is als de gelijkenis van degene die een vuur ontsteekt: wanneer het (vuur) zijn omgeving verlicht, dan neemt Allah hun licht weg en laat Hij hen in de duisternissen achter. Zij zien niet.'),
(25, 2, 18, '(Zij zijn) doof, stom, en blind (van hart), daarom keren zij niet terug.'),
(26, 2, 19, 'Of als (de gelijkenis, met) een regenstorm uit de hemel met daarin duisternissen, donder en bliksem. Zij stoppen hun vingers in hun oren voor de donderslagen, uit doodsangst. En Allah omvat de ongelovigen.'),
(27, 2, 20, 'Bijna rukt de bliksem hun gezicht weg. Telkens wanneer deze hen verlicht, dan lopen zij in (het licht), maar wanneer hij hen in de duisternis laat, dan blijven ze staan. En als Allah het had gewild, dan had Hij hun gehoor en hun gezicht weggenomen. Voorwaar. Allah is Almachtig over alle zaken.'),
(28, 2, 21, 'O mensen, aanbid jullie Heer, Degenen Die jullie heeft geschapen en degenen vóór jullie. Hopelijk zullen jullie (Allah) vrezen.'),
(29, 2, 22, 'Degene Die de aarde voor jullie heeft gemaakt tot een tapijt en de hemel tot een gewelf en Hij zendt water uit de hemel neer, waarmee Hij vervolgens vruchten voortbrengt als voorziening voor jullie. Ken daarom geen deelgenoten toe aan Allah, terwijl jullie (het) wegen.'),
(30, 2, 23, 'En als jullie in twijfel verkeren over wat Wij hebben neergezonden aan Onze dienaar (Moehammad), brengt dan een gelijkwaardige Soerah voort, en roept jullie getuigen buiten Allah op, als jullie waarachtigen zijn.'),
(31, 2, 24, 'Als jullie dan daartoe niet in staat zijn, en jullie zullen er nooit toe in staat zijn, vreest dan de Hel; haar brandstof bestaat uit mensen en stenen, (zij is) gereedgemaakt voor de ongelovigen.'),
(32, 2, 25, 'En geef (O Moehammad) goede tijdingen aan degenen die geloven en goede werken verrichten: dat er voor hen Tuinen (in het Paradijs) zijn waar onder door de rivieren stromen. Telkens wanneer hun daaruit een vrucht wordt gegeven als voorziening, zeggen zij: "Dit is waarmee wij vroeger zijn voorzien." en het soortgelijke zal hun gegeven worden en er zijn daarin reine echtgenoten voor hen, en zij zijn daarin eeuwig levenden.'),
(33, 2, 26, 'Voorwaar, Allah acht het niet beneden Zich om een mug tot gelijkenis te stellen, of iets dat nietiger is dan dat. Wat betreft degenen die geloven, zij weten dat het de Waarheid van hun Heer is. En wat degenen betreft die niet geloven, zij zeggen: "Wat bedoelt Allah met deze gelijkenis?" Hij (Allah) doet er velen mee dwalen en Hij leidt er velen mee, en Hij doet er niemand door dwalen dan de grote zondaren.'),
(34, 2, 27, '(Zij zijn) degenen die het verbond met Allah verbreken na de bekrachtiging ervan, en die verbreken wat Allah bevolen heeft om te verbinden en die verderf op aarde zaaien. Zij zijn degenen die de verliezers zijn.'),
(35, 2, 28, 'Hoe kunnen jullie niet in Allah geloven, terwijl jullie levenloos waren en Hij jullie toen tot leven bracht; waarop Hij jullie deed sterven, daarna doet Hij jullie herleven, en tenslotte zullen jullie tot hem terugkeren.'),
(36, 2, 29, 'Hij is het Degeme Doe vppr jullie alles wat op aarde is geschapen heeft, daarna wendde Hij Zich tot de hemel en vormde deze tot zeven hemelen. En Hij is Alwetend over alle zaken.'),
(37, 2, 30, 'En toen jouw Heer tot de Engelen zei: "Voorwaar, Ik zal op aarde een gevolmachtigde aanstellen," zeiden zij: "Gaat U daarop iemand aanstellen," zeiden zij: "Gaat U daarop iemand aanstellen die verderf zal zaaien en bloed zal vergieten, terwijl wij U roemen met de lofprijzing die U toekomt en wij U heiligen?" Hij zei: "Voorwaar, Ik weet wat jullie niet weten".'),
(38, 2, 31, 'En Hij onderwees Adam de namen van alle dingen en vervolgens toonde Hij deze aan de Engelen en zei: "Noem Mij de namen van deze (dingen), als jullie waarachtig zijn."'),
(39, 2, 32, 'Zij zeiden: "Heilig bent U, wij hebben geen kennis, behalve wat U ons onderwezen hebt: voorwaar, U bent de Alwetende, de Alwijze."'),
(40, 2, 33, 'Hij zei: "O Adam, noem hun de namen ervan." En toen hij hun de namen ervan had genoemd, zei Hij: "Zei Ik jullie niet, dat Ik het onwaarneembare van de hemelen en de aarde ken, en weet wat jullie openlijk doen en wat jullie plachten te verbergen?"'),
(41, 2, 34, 'En toen Wij tot de Engelen zeiden: "Buigt jullie voor Adam," toen bogen zij, bhalve Iblis (de Satan). Hij weigerde en was hooghartig en hij werd één van de ongelogigen.'),
(42, 2, 35, 'En Wij zeiden: "O Adam, verblijft in het Paradijs, jij en je vrouw, en eet daaruit overloedig, zoals jullie willen, maar nadert deze boom niet, anders zullen jullie tot de onrechtplegers behoren."'),
(43, 2, 36, 'Satan deed hen misstappen begaan, waardoor zij eruit geraakten en hij deed hen verdrijven van de plaats waar zij zich bevonden. En Wij zeiden: "Daalt af, een deel van jullie zal een vijand voor de ander zijn. En voor jullie zal een vijand voor de ander zijn. En voor jullie is er op de aarde een verblijfplaats en een genieting, tot een bepaalde tijdstip (de dood)."'),
(44, 2, 37, 'Toen ontving Adam van zijn Heer Woorden. Daarop aanvaardde Hij zijn berouw. Voorwaar, Hij is de Meest Berouwaanvaardende, de Meest Barmhartige.'),
(45, 2, 38, 'Wij zeiden: "Daalt allen af uit haar (het Paradijs). En Zodra er van Mij Leiding tot jullie komt; wie dan Mijn Leiding volgen: er zal geen vrees over hen komen en zij zullen niet treuren."'),
(46, 2, 39, 'Maar degenen die niet geloven en Onze Verzen loochenen, diegenen zijn de bewoners can de Hel, zij zijn daarin eeuwig levenden.'),
(47, 2, 40, 'O Kinderen van Israël! Gedenkt Mijn gunst die Ik jullie geschonken heb en houdt jullie aan het verbond met Mij, dan zal Ik Mij houden aan het verbond met jullie. En vreest daarom alleen Mij.'),
(48, 2, 41, 'En gelooft in wat Ik aan jullie heb neergezonden (de Koran), als bevestiging van wat bij jullie is (de Taurât en de Zabôer), en weest niet de eersten die daarin niet geloven. En verruilt Mijn Verzen niet voor een geringe prijs. En vreest daarom alleen Mij.'),
(49, 2, 42, 'En vermengt de Waarheid niet met de valsheid en verbergt de Waarheid niet terwijl jullie het weten.'),
(50, 2, 43, 'En onderhoudt de shalât en geeft de zakât en buigt tezamen met hen die buigen.'),
(51, 2, 44, 'Roepen jullie de mensen op tot vroomheid en vergeten jullie jezelf, terwijl jullie de Schrift (Taurât) voorlezen? Begrijpen jullie dan niet?'),
(52, 2, 45, 'En vraagt (Allah) om hulp door middel ven geduld en de Shalât. En voorwaar, dat is zwaar, behalve voor de ootmoedigen.'),
(53, 2, 46, '(Zij zijn) degenen die ervan overtuigd zijn dat zij hun Heer zullen ontmoeten en dat zij tot jem zullen terugkeren.'),
(54, 2, 47, 'O Kindern van Israël, gedenkt Mijn gunst die Ik jullie heb geschonken, en dat Ik jullie heb bevoorecht boven de (andere) volkeren.'),
(55, 2, 48, 'En vreest de Dag waarop geen ziel een andere ziel ergens mee kan bijstaan, en er geen voorspraak van haar aanvaard wordt en er geen losprijs van haar aangenomen wordt en zij niet geholpen zullen worden.'),
(56, 2, 49, 'En (gedenkt) toen Wij wullie van Fir\'aun\'s volgelingen redden, zij kwelden jullie met de zwaarste foltering; zij slachtten jullie zonen ad en lieten jullie dochters in leven. Daarin was een geweldige beproeving van jullie Heer.'),
(57, 2, 50, 'En (gedenkt) toen Wij voor jullie de zee kliefden waarmee Wij jullie redden en Fir\'aun\'s volgelingen deden verdrinken, terwijl jullie toekeken.'),
(58, 2, 51, 'En (gedenkt) toen Wij Môesa veertig nachten beloofden, toen namen jullie het kalf aan (ter aanbidding) na zijn vertrek, en jullie waren onrechtplegers.'),
(59, 2, 52, 'Vervolgens vergaven Wij jullie daarna, hopelijk zullen jullie dankbaar zijn.'),
(60, 2, 53, 'En (gedenkt) toen Wij Môesa de Schrift (de Taurât) en de Foerqân gaven, hopelijk zullen jullie de leiding volgen.'),
(61, 2, 54, 'En (gedenkt) toen Môesa tot zijn volk zei: "O mijn volk! Voorwaar, jullie hebben jezelf onrecht aangedaa doordat jullie het kalf (ter aanbidding) hebben genomen, wendt jullie daarom in berouw tot jullie Schepper en doodt dan julliezelf, dat is beter voor jullie bij jullie Schepper." Toen aanvaardde Hij jullie berouw. Voorwaar, Hij is de Meest Berouwaanvaardende, de Meest Barmhartige.'),
(62, 2, 55, 'En (gedenkt) toen jullie zeiden: "O Môesa, wij zullen jou niet geloven totdat wij Allah duidelijk (in Zijn ware gestalte) zien," waarop de bklisem jullie greep, terwijl jullie toekeken.'),
(63, 2, 56, 'Vervolgens wekken Wij jullie op na jullie dood, hoplijk zullen jullie dankbaar zijn.'),
(64, 2, 57, 'En wij gaven jullie schaduw door middel van de wolk en Wij deden Manna en kwartels voor jullie neerdalen. Eet van de goede dingen waarmee Wij jullie hebben voorzien. Zij deden Ons geen onrecht aan, maar zij deden zichzelf onrecht aan.'),
(65, 2, 58, 'En (gedenkt) toen Wij zeiden: "Gaat deze stad binnen en eet daarvan (het land) van de overvloed, zoveel als jullie willen." Gaat buigend de poort binnen en zegt: "Vergeving!" Wij zullen jullie jullie fouten vergeven, en Wij zullen vermeerden voor de weldoeners."'),
(66, 2, 59, 'Daarna verwisselden degenen die onrecht pleegden het Woord met iets anders dan wat tot hen gezegd was en daarom deden Wij op degenen die onrecht pleegden een plaag uit de hemel neerkomen, vanwege de grote zonden die zij plachten te bedrijven.'),
(67, 2, 60, 'En (gedenkt) toen Môesa water vroeg voor zijn volk, waarop Wij zeiden: "Sla met je staf op de rots." Toen ontsprongen daaruit twaalf bronnen. Waarlijk, elke stam kende zijn drinkplaats. Eet en drinkt can Allah\'s voorzieningen. En doet geen kwaad op aarde als verderfzaaiers.'),
(68, 2, 61, 'En (gedenkt) toen jullie zeiden: "O Môesa! Wij verdragen het niet om van één soort voedsel te leven, roep daarom voor ons jouw Heer aan, opdat Hij voor on voortbrengt van wat de aarde doet groeien van haar groenten, haar komkommers, haar knoflook, haar linzen en haar uien." Hij zei: "Willen jullie dat wat minderwaardig is, nemen in plaats van het betere? Gaat naar een ander land, en voorwaar, voor jullie zal er zijn wat jullie vroegen." En er werd over hen vernedering en ellende gebracht en zij keerden terug onder de toorn van Allah. Dat was omdat zij voortdurent Allah\'s Tekenen verwierpen en de Profeten dooden, zonder het recht te hebben. Dit was omdat zij opstandig waren en overtredingen plachten te begaan.'),
(69, 2, 62, 'Voorwaar, degenen die geloven, en degenen die het Jodendom belijden en de Christenen en de Sabiërs; (zij allen) geloven in Allah en in de Laatste Dag, en zij verrichten goede werken: voor hen is hun beloning bij hun Heer en geen vrees zal er over hen zijn noch zullen zij treuren.'),
(70, 2, 63, 'En (gedenkt) toen Wij jullie belofte aanvaardden en de (berg) Thôer boven jullie verhieven, (zeggend:) "Houdt stevig vast aan wat Wij aan jullie gegeven hebben en gedenkt wat er in staat, hopelijk zullen jullie (Allah) vrezen."'),
(71, 2, 64, 'Vervolgens wendden jullie je af, en als er niet de gunst van Allah over jullie en Zijn Barmhartigheid was geweest, dan zouden jullie zeker tot de verliezers behoren.'),
(72, 2, 65, 'En voorzeker, jullie kennen degenen die in overtreding waren onder jullie (volk) betreffende de Sabbat, Wij zeiden immers tot hen: "Weest verachte apen."'),
(73, 2, 66, 'Zo maakten Wij deze (bestraffing) tot een waarschuwing voor die tijd en de tijd erna en een vermaning voor de Moettaqôen.'),
(74, 2, 67, 'En (gedenkt) toen Môesa tot zijn volk zei: "Voorwaar, Allah beveelt jullie dat jullie een koe slachten." Zij zeiden: "Maak jij ons tot (onderwerp van) bespotting?" Hij antwoordde: "Ik zoek bescherming bij Allah dat ik tot de dommen zou behoren."'),
(75, 2, 68, 'Zij zeiden: "Roep voor ons jouw Heer aan, op dat Hij ons duidelijk maakt wat voor koe het moet zijn." Hij zei: "Voorwaar, Hij zegt: \'Het us een koe die niet oud is en niet jong, maar van een leeftijd daartussen\'. Doe dan wat jullie bevolen is."'),
(76, 2, 69, 'Zij zeiden: "Roep voor ons jouw Heer aan, opdat Hij ons duidelijk maakt wat haar kleur is.\' Hij zei: "Hij zeg dat het een koe met een heldere gele kleur is, die de toeschouwers verheugt."'),
(77, 2, 70, 'Zij zeiden: "Roep voor ons jouw Heer aan, opdat Hij ons duidelijk maakt wat voor koe het moet zijn. Voorwaar, voor ons lijken alle koeien op elkaar. En voorwaar, wij zullen, als Allah het wil, tot de rechtgeleiden behoren."'),
(78, 2, 71, 'Hij (Môesa) zei: "Hij zegt dat het een koe is die niet bestemd is om de aarde om te ploegen of de akkers te bevloeien, een gave, zonder vlek." Zij zeiden: "Nu ben je met de ware beschrijving gekomen."\' Daarop slachtten zij haar, maar bijna hadden zij het niet gedaan.'),
(79, 2, 72, 'En (gedenkt) toen jullie een mens doodden en met elkaar daarover redetwistten (over wie de moordenaar was), en Allah is de Onthuller van wat jullie plachten te verbergen.'),
(80, 2, 73, 'Toen zeiden Wij: "Slaat hem (de dode) met een deel van haar (de koe)." Zo doet Allah de doden herleven en toont Hij jullie Zijn Tekenen, hopelijk zullen jullie begrijpen.'),
(81, 2, 74, 'Toen verhardden jullie harten zich daarna, zodat zij als steen werden of zelfs harder dan dat, want voorwaar, er zijn stenen waaruit rivieren ontspringen en voorwaar, er zijn stenen die splijten zodat er water uitstroomt, en er zijn er die neervallen uit vrees voor Allah. En Allah is niet onachtzaam omtrent wat jullie doen.'),
(82, 2, 75, 'Hopen jullie dan nog dat zij (de Joden) in jullie (godsdienst) zullen geloven, terwijl er waarlijk een groep onder hen is, die het Woord van Allah heeft gehoord, en die het verdraaiden nadat zij het begrepen hadden, terwijl zij het weten?'),
(83, 2, 76, 'En wanneer zij degenen die geloven ontmoeten, zeggen zij: "Wij geloven," maar wanneer zij onder elkaar zijn, dan zeggen zij: "Zullen jullie hun vertellen wat Allah aan jullie openbaar heeft gemaakt, zodat zij het als argument tegen jullie zullen gebruiken bij jullie Heer?" Begrijpen jullie dan niet?'),
(84, 2, 77, 'En weten zij niet dat Allah weet wat zij verbergen en wat zij openlijk doen?'),
(85, 2, 78, 'En er zijn ongeletterden onder hen die de Shcrift (de Taurât) niet kennen, behalve door kletspraat en zij vermoeden slechts.'),
(86, 2, 79, 'Wee dan degenen die de Schrift met hun eigen handen schrijven en vervolgens zeggen: "Dit komt van Allah." Om het te verruilen voor iets van geringe waarde. Wee dan hen vanwege wat hun handen geschreven hebben en wee hen vanwege wat zij verrichtten.'),
(87, 2, 80, 'En zij zeiden: "De Hel zal ons niet aankraken, behalve een beperkt aantal dagen," Zeg (O Moehammad): "Hebben jullie een belofte van Allah ontvangen? Dan zal Allah Zijn belofte niet verbreken. Of zeggen jullie over Allah iets wat jullie niet weten?"'),
(88, 2, 81, 'Welzeker, degenen die slechte daden verrichten, en door hun zonden omsloten zijn, diegenen dan, zijn de bewoners van de Hel. Zij zijn daarin eeuwig levenden.'),
(89, 2, 82, 'Maar degenen die geloven en goede werken verrichten, zij zijn degenen die de bewoners van het Paradijs zijn. Zij zijn daarin eeuwig levenden.'),
(90, 2, 83, 'En (gedenkt) toen Wij het verbond van de Kinderen van Israël aanvardden (zeggent): "Aanbidt niets dan Allah, en betracht goedheid jegens de ouders, en de verwand, en de wees, en de behoeftige, en spreekt het goede tot de mensen en onderhoudt de shalât en geeft de zakât." Vervolgens ontrokken jullie je er aan, behalve een klein aantal van jullie, terwijl jullie je afwendden.'),
(91, 2, 84, 'En (gedenkt) toen Wij jullie verbond aanvaardden: "Vergiet elkaars bloed niet en verdrijft elkaar niet uit jullie woonplaatsen." Daarop bevestigden jullie (dat) en jullie getuigden (daarvan).'),
(92, 2, 85, 'Daarna zijn jullie (Joden) degenen geworden die elkaar doden en een deel van jullie eigen volk uit hun woonplaatsen verdrijven, jullie steunen elkaar in zondigheid en vijandschap. En wanneer zij (andere Joden) tot jullie komen als gevangenen, dan kopen jullie hen vrij, terwijl het jullie verboden is hen te verdrijven. Geloven jullie in een gedeelte van de Schrift en in een ander gedeelte niet? Er is geen beloning voor wie van jullie zo handelen, maar vernedering in het aardse leven en op de Dag der Opstanding zullen zij worden teruggevoerd tot de zwaarste bestraffing. En Allah is niet onachtzaam omtrent wat jullie doen.'),
(93, 2, 86, 'Zij zijn degenen die het aardse leven voor het Hiernamaals hebben verruild. De bestraffing zal voor hen niet verlichts worden zn zij zullen niet geholpen worden.'),
(94, 2, 87, 'En Wij hebben Môesa voorzeker het Boek gegeven en Wij deden na hem de Boodschappers volgen. Wn Wij gaven \'Îsa, de zoon van Maryam, de duidelijke bewijzen. En Wij versterkten hem met de heilige Geest (Djibrîl). Is het dan zo dat telkens wanneer er een Boodschapper tot jullie kwam met wat niet in overeenstemming was met jullie begeerten, jullie hooghartig werden en jullie een aantal van hen loochenden en anderen doodden?'),
(95, 2, 88, 'En zij zeggen: "Onze harten zijn bedekt." Nee! Allah heeft hen verloekts vanwege hun ongeloof. Weinigen zijn het daarom die geloven.'),
(96, 2, 89, 'En wanneer er een boek tot ben komt van Allah, bevestigend wat zich bij hen bevindt, terwijl zij daarvoor om hulp hadden gevraagd tegen degenen die niet geloven: toen dan tot hen kwam wat zij al wisten, geloofiden zij er niet in. De vloek van Allah vloek rust daarom op de ongelovigen.'),
(97, 2, 90, 'Slecht is het, waarvoor zij hun zielen verkocht hebben; dat zij niet geloven in wat Allah heeft neergezonden; uit afgunst, dat Allah zijn gunst neerzendt tot wie hij wil van zijn dienaren. Zo wender zij zich van toorn tot toorn. En voor de ongelovigen is er een vernederende bestraffing.'),
(98, 2, 91, 'en als er tot hen gezegd wordt: "Gelooft in wat Allah heeft neergezonden," zeggen zij: "Wij geloven in wat aan ons is neergezonden." En zij geloven niet in wat erna is (neergezonden), terwijl het de waarheid is, bevestigend wat zich bij hen bevindt. Zeg (O moehammad): "Waarom hebben jullie dan vroeger de profeten van Allah gedood, als jullie gelovigen zijn?"'),
(99, 2, 92, 'en voorzeker, Móesa is tot jullie gekomen met de duidelijk bewijzen, warrop jullie het half (ter aanbidding) namen nadat hij was weggeegaan. en jullie waren onrechtplegers.'),
(100, 2, 93, 'En (gedenkt) toen Wij jullie verbond aanvaardden den Wij de (berg) Thôer boven jullie verhieven, (zeggend:) "Houdt jullie stevig vast aan wat Wij jullie gegeven hebben en luistert". Zij zeiden: "Wij hebben geluisterd, maar wij gehoorzamen niet." En hun harten waren doordrenkt met (liefde voor) het kalf vanwege hun ongeloof. Zeg (O Moehammad): "Slecht is het wartoe jullie (vervalste) geloof jullie oproept, als jullie gelovigen zijn (in de Taurât geloven)."'),
(101, 2, 94, 'Zeg: "Als het Huis van het Hiernamaals zij Allah alleen maar voor jullie is, met uitzondering van de andere mensen: wenst dan de dood, als jullie waaractig zijn."'),
(102, 2, 95, 'Maar zij zullen hem (de dood) nooit wensen, vanwege wat hun handen (aan zonden) hebben voortgebracht. En Allah kent de onrechtplegers.'),
(103, 2, 96, 'En jij zult hen zeker bevinden als de mensen die het meest begerig zijn naar het (wereldse) leven, meer nog dan degenen die deelgenoten (aan Allah) toekennen. Elk van hen zou wel een leeftijd van duizend jaren gegeven willen worden. Maar hij zou daardoor niet (kunnen) vluchten voor de bestraffing, door de verlenging can zijn leeftijd. En Allah ziet toe op wat zij doen.'),
(104, 2, 97, 'Zeg (O Moehammad): "Wie een vijand van Djibrîl is: voorwar, hij heeft hem (de Koran) in jouw hart neergezonden, met toestemming van Allah, als een bevestiging can wat er vóór (geopenbaard) was, en als Leiding en als een verheugende tijding voor de gelovigen."'),
(105, 2, 98, 'En wie een vijand van allah is en van zijn Engelen en vam zijn boodschappers en van djibril en van MIKA IL: voorwaar, Allah is een vijand van de ongelovigen.'),
(106, 2, 99, 'En voorzeker, wij hebben duidelijke verzen tot jou neergezonden. en Alleen de zwaar-zondigen geloven er niet in.'),
(107, 2, 100, 'Is het niet zo dat telkens wanneer zij (de ongelovigen) een verbintenis aangingen, zij die (later) van zich afwierpen? Maar de mesesten van hen geloven niet.'),
(108, 2, 101, 'En toen er tot hen een boodschapper van allah was gekomen, bevestigend wat bij ben bevond, wierp een groep van degenen aan wie de Schrift(de taurat) gegeven was, het boek van Allah achter hun ruggen, alsof zij niet wisten.'),
(109, 2, 102, 'An zij volgden wat de satans voorlazen ten tijde van soelaiman \'s koninkrijk en soelaiman was niet ongelovig, maar de satans waren ongelovig, zij onderwezen de mensen tovenarij (Sihr) en wat was neergezonden te Babel aan de twee Engelen Haroet en Maroet. En geen van beiden gaven onderricht, zonder dat zij zeiden: "Voorwaar, wij zijn slechts een beproeving, weast daarom niet ongelovig." Zo onderwezen zij hen(trovenarij), waardoor zij een scheiding veroorzaakten tussen een mann en zijn echtgenote. En zij schaadden daarmee niemand, behalve met toestermming van Allah. En zij (de mensen) leerden wat hen schaadde en ben niet baatte. En voorzeker, zij wisten dat, wie dat(tovenarij) koopt, geen aandeel zal hebben in het Hiernamaals. En slecht is het waarvoor zij hun zielen verkochten, als zij het maar wisten.'),
(110, 2, 103, 'En als zij hadden geloofd en Allah hadden gevreesd, dan zou (hun) beloning van bij Allah beter geweest zijn. Als zij het maar wisten.'),
(111, 2, 104, 'O jullie die geloven, zegt niet: "ra\'inâ", maar zegt: "Oenzhoernâ", en luistert. En voor de ongelovigen is er een pijnlijke bestraffing.'),
(112, 2, 105, 'Degenen die ongelovig zijn onder de Lieden van de schrift (de Joden en de christenen) en de voeelgodenaanbidders, wensen niet dat er iets goeds aan jullie wordt neergezonden van jullie Heer. Maar Allah verkiest voor Zijn Barmbartigheid wie Hij en Allah is de bezitter van de Geweldige Gunst.'),
(113, 2, 106, 'Welk vers Wij ook afschaffen of doen vergeten; Wij brengen er iets beters voor in de plaats, of iets wat daaraan gelijk is. Weet jij niet dat Allah macht heeft over alle dingen?'),
(114, 2, 107, 'Weet jij njet dat aan Allah de heerschappij van de hemelen en de aarde behoort? En er is voor jullie buiten Allah geen helper en geen beschermer.'),
(115, 2, 108, 'Of willen jullie je Boodschapper ondervragen zoals Môesa vroeger (werd ondervraagd)? En wie ongeloof in ruil neemt voor geloof, die is afgadwaald van het juiste pad.'),
(116, 2, 109, 'Velen onder de lieden van de schrift wensen dat zij jullie, nadat jullie tot geloof zijn gekomen, weer tot ongelovigen zouden kunnen maken, uit afgunst die onder hen leeft, nadat voor hen de waarheid duidelijk was geworden. Maar vergeeft hen en laat hen maar begaan, totdat Allah komt met zijn bevel. voorwaar, Allah is Almachtig over alle zaken.'),
(117, 2, 110, 'En onderhoudt de shalât en geeft de zakât en wat jullie hebben vooruit gezonden aan goede daden voor jezelf; jullie zullen het bij Allah aantreffen. Voorwaar, Allah is Alziende over was jullie doen.'),
(118, 2, 111, 'En zij(de joden en de Christenen) zeiden: "Niemand zai het Paradijs binnengaan, behalve wie jood of christen was." Dat zijn hun eigen wensen. Zeg: " Brengt jullie bewijzen, als jullie waarachtigen zijn."'),
(119, 2, 112, 'Welzeker! Wie zich volledig in overgave wendt tot Allah en die een weldoener is, voor hem is zijn beloning bij zijn Heer. Green vrees zal over hen komen en zij zullen niet treuren.'),
(120, 2, 113, 'En de Joden zeiden: "De christenen hebben geen grondslag" (voor hun beweringen). En de Christenen zeiden: "De Joden hebben geen grondslag. "Terwijl zij de Schrift voorlezen. Degenen die niet weten (de veelgodenaanbidders), spreken het zelfde woord als zij. En Allah zal tussen hen oordelen op de Dag der Opstanding omtrent dat waarover zij van mening verschillen.'),
(121, 2, 114, 'En wie is er zondiger dan degene die verhindert dat in de Moskeeen van Allah Zijn Naam genoemd wordt en die zich beijvert om deze te verwoesten? Zij behoren deze niet binnen te gaan, behalve als vrezenden. Voor hen is er op de wereld een vernedering en in het Hiernamaals is er een geweldige bestraffing.'),
(122, 2, 115, 'En aan Allah behoren het Oosten en het Westen. Waarheen jullie je ook wenden, daar is het Aangezicht van Allah. Voorwar, Allah is Alomvattend en Alwetend.'),
(123, 2, 116, 'En zij (de Christenen) zeiden: "Allah heeft Zich een zoon genomen." Heilig is Hij! Nee! Aan Hem behoort wat er in de hemelen en (op) de aarde is. Allen gehoorzamen Hem ootmoedig.'),
(124, 2, 117, '(Hij is) de Voortbrenger van de hemelen en de aarde. En wanner Hij een besluit over een zaak heeft genomen, dan zegt Hij er slechts tegen: "Wees," en het is.'),
(125, 2, 118, 'En degenen die niet weten, zeiden: Waarom spreekt Allah niet tot ons en waarom komt er geen Teken tot ons?" Zo spraken ook degenen die voor hen waren. Hun harten lijken op elkaar. Waarlijk, Wij hebben de Tekenen duidelijk gemaakt aan een overtuigd volk.'),
(126, 2, 119, 'Voorwaar, Wij hebben jou (Moehammad) met de Waarheid gezonden, als een brenger van een verheugende tijding en als een waarschuwer, en jij zal niet worden ondervraagd over de bewoners van de Djahîm (de Hel).'),
(127, 2, 120, 'En de Joden en de Christenen zullen nooit behagen in jou vinden, totdat jij hun godsdienst volgt. Zeg (O Moehammad): "Voorwar, de Leiding van Allah: dat is Leiding." En als jij hun begeerten volgt, nadat de kennis tot jou is gekomen, dan zal er voor jou tegen Allah geen beschermer en geen helper zijn.'),
(128, 2, 121, 'Degenen aan wie Wij de Schrift hebben gegeven lezen het op de juiste manier voor, zij zijn degenen die erin geloven. En degenen die er niet in geloven: zij zijn de verliezers.'),
(129, 2, 122, 'O Kinderen van Israel, gedenkt Mijn gunst die Ik jullie heb geschonken en dat Ik jullie heb bevoorrecht boven de (andere) volken.'),
(130, 2, 123, 'En vreest een Dag waarop de ene ziel niets voor een andere ziel kan doen en er geen losprijs van haar aanvard zal worden, geen voorspraak zal haar baten. En zij zullen niet geholpen worden.'),
(131, 2, 124, 'En (gedenkt) toen Ibrâhîm door zijn Heer beproefd werd met enkele woorden (geboden en verboden) die hij daarop in acht nam. Hij (Allah) zei: "Voorwar, Ik zal jou voor de mensheid tot een leider maken." Hij (Ibrâhîm zei: "En ook van mijn nageslacht?" Hij (Allah) antwoordde: "Mijn verbond omvat de onrechtplegers niet."'),
(132, 2, 125, 'En (gedenkt) toen Wij het Huis (de Ka\'bah) tot een plaats van verzameling voor de mensheid maakten en een plaats van veiligheid. En neemt de standplaats van Ibrâhîm tot een plaats voor de shalât. En Wij legden de plicht op aan Ibrâhîm en Isma\'îl: "Reinigt Mijn Huis voor degenen die de ommegang (thawaf) maken, voor hen die er de I\'tikaf verrichten en voor hen die zich buigen en die knielen (de shalât).'),
(133, 2, 126, 'En (gedenkt) toen Ibrâhîm smeekte: "Mijn Heer, maak dit gebied tot een veilige plaats en voorzie haar bewoners met vruchten, degenen van hen die geloven in Allah en in het Hiernamaals." Hij (Allah) zei: "En (ook) degene die ongelovig is, zal ik genietingen schenken, voor een korte tijd, daarna zal Ik hen naar de bestraffing van de Hel drijven. En dat is de slechtste plaats van terugkeer.'),
(134, 2, 127, 'En (gedenkt) toen Ibrâhîm de grondvesten van het Huis legde, samen met Isma\'îl (biddend): "Onze Heer, aanvaard het van ons: voorwar, U bent de Alhorende, de Alwetende.'),
(135, 2, 128, 'Onze Heer, maak ons beiden tot mensen die zich overgeven aan U en (maak) onze nakomelingen tot een volk dat zich overgeeft aan U en onderwijs ons de gebruiken (van o.a. de Haddj) en aanvaard ons berouw, voorwaar, U bent de Meest Berouwaanvaardende, de Meest Barmhartige.'),
(136, 2, 129, 'Onze Heer! En zend tot hen een Boodschapper van hun eigen volk, die hen Uw Verzen voordraagt en die hen het Boek (de Koran) en de Wijsheid onderwijst en die hen reinigt. Voorwar, U bent de Almachtige, de Alwijze."'),
(137, 2, 130, 'En wie keert zich af van de godsdienst van Ibrâhîm, anders dan wie zichzelf voor de gek houdt? En voorzeker hebben Wij hem uitverkoren in de wereld, en voorwaar, hij behoort in het Hiernamaals tot de oprechten.'),
(138, 2, 131, 'En (gedenkt) toen zijn Heer tot hem zei: "Onderwerp jezelf (aan Mij)." Hij zei: "Ik onderwerp mij aan de Heer der Werelden."'),
(139, 2, 132, 'En Ibrâhîm droeg aan zijn kinderen en aan Ya\'qoeb op: "O mijn kinderen, voorwaar, Allah heeft de godsdienst voor jullie gekozen, sterft daarom niet, behalve als jullie overgegevenen zijn."'),
(140, 2, 133, 'Of waren jullie getuigen toen Ya\'qoeb de dood nabij was (en) hij tot zijn kinderen zei: "Wat zullen jullie aanbidden na mij?" Zij zeiden: "Wij zullen uw God aanbidden, de God van uw vaderen, Ibrâhîm, en Isma\'îl en Ishaq, als de Ene God, en wij heebn ons aan Hem overgegeven."'),
(141, 2, 134, 'Dat was een gemeenschap die waarlijk heen is gegaan. Voor haar is wat zij heeft verworven en voor jullie is wat jullie hebben verworven en jullie zullen niet worden ondervraagd over wat zij plachten te bedrijven.'),
(142, 2, 135, 'En zij zeiden: "Wordt Jood of Christen, dan volgen jullie de leiding." Zeg: "Nee! (Wij volgen) de godsdienst van Ibrâhîm, die Hanîf was en hij behoorde niet tot de veelgodenaanbidders."'),
(143, 2, 136, 'Zeg (O Moehammad): "Wij geloven in Allah en wat er aan ons is neergezonden en wat er is neergezonden aan Ibrâhîm en Ishma\'il en Ishaq en Ya\'qoeb en de kinderen van Ya\'qoeb en wat er is gegeven aan Môesa en Isa en wat er is gegeven van hun Heer aan de Profeten, wij maken geen onderscheid tussen één van hen en wij onderwerpen ons aan Hem."'),
(144, 2, 137, 'Als zij dan geloven in het gelijke van waarin jullie geloven, dan volgen zij waarlijk de Leiding. En als zij zich afwenden: voorwaar, dan zijn zij het die in vijandschap (jegens jullie) verkeren. Allah zal jou (O Moehammad) dan beschermen tegen hen. En Hij is de Alhorende, de Alwetende.'),
(145, 2, 138, '(Neemt) de Shibghah van Allah. En wie heeft er een betere Shibghah dan Allah? En wij zijn aanbidders van Hem.'),
(146, 2, 139, 'Zeg (O Moehammad): "Redetwisten jullie met ons over Allah? Terwijl Hij onze Heer en jullie Heer is? Voor ons onze werken, en voor jullie jullie weken. En Wij zijn Hem zuiver toegewijd."'),
(147, 2, 140, 'Of zeggen jullie (Joden en Christenen): "Voorwaar, Ibrâhîm en Isma\'îl en Ishaq en Ya\'qoeb en zijn kinderen waren Joden of Christenen?" Zeg: "Weten jullie beter of Allah? En wie is onrechtvaardiger dan hij die een getuigenis van Allah, die hij bezit, verbergt?" En Allah is niet onachtzaam omtrent wat jullie doen.'),
(148, 2, 141, 'Dat was een gemeenschap die waarlijk heen is gegaan. Voor haar is wat zij heeft verworven, en voor jullie is wat jullie hebben verworven en jullie zullen niet worden ondervraagd over wat zij plachten te bedrijven.'),
(149, 2, 142, 'De dwazen onder de mensen zullen zeggeh: "Wat heeft hen zich doen afwenden van hun Qiblah (gebedsrichting) die zij gewoonlijk in acht namen?" Zeg: Aan Allah behoort het Oosten en het Westen Hij leidt wie Hij wil op een recht Pad."'),
(150, 2, 143, 'Zo maakten Wij jullie tot een gematigd volk, opdat jullie getuigen zullen zijn voor de mensen en opdat de Boodschapper (Moehammed) een getuige zal zijn voor jullie. En Wij hebben de Qiblah die jullie gewend waren slechts aangewezen om degenen die de Boodschapper volgen onder degenen die zich op hun hielen omdraaien te beproeven. En zeker, dit (de verandering van de Qiblah) was zwaar, behalve voor degenen die Allah leiding gaf. En Allah is neit zo dat Hij jullie geloof (shalât) verloren zou doen gaan. Voorwaar, Allah is zeker genading, meest harmhartig voor de mensen.'),
(151, 2, 144, 'Waarlijk, Wij hebben gezien hoe jouw gezicht voortdurend tot de hemel wendde, daarom wenden Wij jou (nu) naar een Oiblah die jou welgevallig is. Wend jouw gezicht in de richting van de Masdjid al Harâm (de Gewijde Moskee te Mekkah). En waar jullie je ook bevinden (en de shalât gaan verrichten), wendt jullie gezichten in die richting. En voorwaar, degenen aan wie de Schrift is gegeven, weten zeker dat het de Waarheid van jullie heer is. En Allah is niet onachtzaam omtrent wat zij doen.'),
(152, 2, 145, 'En als jij aan degenen aan wie de Schrift is gegeven alle Tekenen brengt, dan nog zullen zij jouw Qiblah niet volgen. En jij zult hun Qiblah nooit volgen. En evenmin zal een gedeelte van hen ooit de Qiblah van anderen volgen. En als jij hun begeerten had gevolgd, nadat de kennis tot jou was gekomen, dan zou zij zeker tot de onrechtplegers behoren.'),
(153, 2, 146, 'Degenen aan wie Wij de Schrift hebben gegeven, kennen hem (Moehammad) zoals zij hun zonen kennen, en voorwaar, een groep onder hen verbergt zeker de Waarheid, terwijl zij (die) kennen.'),
(154, 2, 147, 'De Waarheid komt van jouw Heer, behoor daarom niet tot de twijlfelaars.'),
(155, 2, 148, 'En voor iedere gemeenschap is er een Qiblah. Wedijvert daarom met elkaar in goede daden. Waar jullie ook zijn, Allah zal jullie samen brengen. Voorwaar, Allah is Almachtig over alle dingen.'),
(156, 2, 149, 'En waar jij ook vandaan vertrekt, wend jouw gezicht in de richting van de Masdjid al Harâm (de Gewijde Moskee te Mekkah) en voorwaar, het is zeker de Waarheid van jouw Heer, en Allah is niet onachtzaam omtrent wat jullie doen.'),
(157, 2, 150, 'En waar jij ook vandaan vertrekt, wend jouw gezicht in de richting van de Masdjid al Harâm, en waar jullie je ook bevinden, wendt dan jullie gezichten in haar richting, zodat de mensen geen argument tegen jullie hebben, behalve de onrechtvaardigen onder hen, en vreest daarom niet hen, maar vreest Mij, opdat Ik Mijn gunst aan jullie zal vervolmaken. En hopelijk zullen jullie rechte Leiding volgen.'),
(158, 2, 151, 'Zoals Wij een Boodschapper uit jullie midden zonden, die aan jullie Onze Verzen voorleest, die jullie reinigt, die jullie het Boek (de Koran) en de Wijsheid onderwijst en die jullie onderwijst wat jullie niet weten.'),
(159, 2, 152, 'Gedenkt Mij daarom, dan zal Ik jullie gedenken en weest Mij dankbaar en weest Mij niet ondankbaar.'),
(160, 2, 153, 'O jullie die geloven, zoekt hulp door middel van geduld en de shalât. Voorwaar, Allah is met de geduldigen.'),
(161, 2, 154, 'En zegt niet over degenen die zijn gedood (gesneuveld) op de Weg van Allah, dat zij dood zijn. Nee, zij leven, maar jullie beseffen het niet.'),
(162, 2, 155, 'En Wij zullen jullie zeker beproeven met iets van vrees, honger, vermindering van bezittingen, levens en vruchten. Maar geeft verheugende tijdingen aan de geduldigen.'),
(163, 2, 156, 'Degenen die wanneer een ramp hen treft, zeggen: (Inna lillahi wa inna ilaihi radji\'oen) "Voorwar, aan Allah behoren Wij, en voorwaar, tot Hem zullen Wij terugkeren."'),
(164, 2, 157, 'Zij zijn degenen op wie de zegeningen van hun Heer neerdalen, en Barmhartigheid, en zij zijn degenen die de rechte Leiding ontvangen.'),
(165, 2, 158, 'Voorwaar, Shafa en Marwah behoren tot de aan Allah gewijde Tekenen. Wie dan de Haddj of de Oemrah verricht naar het Huis (de Ka\'bah): het is geen zonde als hij om beide (Shafâ en Marwah) loopt (tijdens de Sa\'î). En wie vrijwillig goede daden verricht: voorwaar, Allah is Waarderend en Alwetend.'),
(166, 2, 159, 'Voorwaar, zij die verbergen wat Wij hebben neergezonden van de duidelijke bewijzen en de Leiding, nadat Wij die aan de mensen hebben duidelijk gemaakt in de Schrift: zij zijn degenen die Allah vervloekt en die vervloekers vervloeken.'),
(167, 2, 160, 'Behalve degenen die berouw hebben getoond, zich gebeterd hebben en (de Waarheid) duidelijk hebben gemaakt. Diegenen zijn het van wie Ik berouw aanvaard. En Ik ben de Meest Berouwaanvaardende, de Meest Barmhartige.'),
(168, 2, 161, 'Voorwaar, degenen die ongelovig waren en stierven terwijl zij ongelovig waren, op hen rust de vloek van Allah en van de Engelen en de mensen tezamen.'),
(169, 2, 162, 'Eeuwig levenden zijn zij daarin (de Hel). En voor hen zal de bestraffing niet verlicht worden, noch zal hen uitstel gegeven worden.'),
(170, 2, 163, 'En jullie god is één God. Geen god is er dan Hij, de Erbarmer, de Meest Barmhartige.'),
(171, 2, 164, 'Voorwaar, in de schepping van de hemelen en de aarde en de afwisseling van de nacht en de dag en de schepen die over de zee varen met wat de mensen voordeel geeft, en het water dat Allah uit de hemel neerzendt, waarmee Hij de aarde tot leven brengt na haar dood, en dat hij daarop allerlei dieren verspreidde, en de besturing van de winden en de wolken die tussen de hemel en de aarde dienstbaar zijn gemaakt, zijn zeker Tekenen voor een volk dat verstandig is.'),
(172, 2, 165, 'En er zijn er onder de mensen die nasst Allah deelgenoten teokennen, die zij liefhebben met de liefde als (die) voor Allah, maar degenen die geloven zijn sterker in liefde voor Allah. En als degenen die onrecht pleegden zouden weten wanneer zij de bestraffing zien, (dan zouden zij weten) dat alle macht aan Allah behoort en dat Allah hard is in de bestraffing.'),
(173, 2, 166, 'Wanneer degenen die gevolgd werden zich los verklaren aan degenen die hen volgen: en zij zagen de bestraffing en (dat) de banden met hen verbroken waren.'),
(174, 2, 167, 'En degenen die volgden zeiden: "Was er voor ons nog maar één keer (de gelegenheid om naar de aarde terug te keren), dan zouden wij ons onschuldig verklaren aan hen, zoals zij zich aan ons onschuldig verklaarden." Zo laat Allah hen hun daden zien, als (een bron) van spijt voor hen. En zij zullen de Hel niet verlaten.'),
(175, 2, 168, 'O mensen, eet van wat op de aarde is het toegestane en het goede, en volgt niet in de voetstappen van de Satan. Voorwaar, hij is voor jullie een duidelijke vijand.'),
(176, 2, 169, 'Voorwaar, hij roept jullie op tot het kwade en zedeloosheid en (wil) dat jullie over Allah zeggen wat jullie niet weten.'),
(177, 2, 170, 'En wanneer tot hen gezegd wordt: "Volgt wat Allah heeft neergezonden," dan zeggen zij: "Maar wij volgen dat (pad van afgoderij) waarop wij onze vaderen aantroffen." Ook als hun vaderen niets begrepen en niet de rechte Leiding volgden?'),
(178, 2, 171, 'En de gelijkenis van degenen die ongelovig zijn, is als de gelijkenis met iemand (een herder) die roept naar iets wat niet luistert, behalve naar een roep of een schreeuw. Zij zijn doof, stom en blind (van hart), daarom begrijpen zij niet.'),
(179, 2, 172, 'O jullie die geloven, eet van de goede dingen waarmee Wij jullie hebben voorzien en weest Allah dankbaar als Hij alleen het is die jullie aanbidden.'),
(180, 2, 173, 'Voorwaar, Hij heeft voor jullie verboden het gestorvene (het niet ritueel geslachte), bloed, varkensvlees, en dat waarover (bij het slachten) een andere naam dan die van Allah is uitgesproken. Maar wie door nood gedwongen is, zonder dat hij het wenst en niet overdrijft, dan is het voor hem geen zonde. Voorwaar, Allah is Meest Vergevensgezind, Meest Barmhartig.'),
(181, 2, 174, 'Voorwaar, degenen die verbergen wat Allah heeft neergezonden van de Schrift en die het verruilen voor een geringe prijs, zij zijn degenen die in hun buiken niets dan vuur verteren. En Allah zal niet tot hen spreken op de Dag der Opstanding en Hij zal hen niet reinigen en voor hen is er een pijnlijke bestraffing.'),
(182, 2, 175, 'Zij zijn diegenen die de Leiding hebben verruild voor de dwaling en de vergiffenis voor de bestraffing. Hoe geduldig zijn zij met de Hel!'),
(183, 2, 176, 'Dat is zo omdat Allah de Schrift met de Waarheid heeft neergezonden. En voorwaar, degenen die van mening verschillen over de Schrift verkeren in vegaande verdeelheid.'),
(184, 2, 177, 'Het is geen vroomheid dat jullie je gezichten naar het Oosten en het Westen wenden, maar vroom is wie gelooft in Allah en het Hiernamaals en de Engelen en de Schrift en de Profeten en die het bezit dat hij liefheeft weggeeft aan de verwanten en de wezen en de behoeftigen en de reiziger (zonder proviand) en de bedelaars en (het gebruikt) voor het vrijkopen van slaven, en die de shalât onderhoudt, de zakât geeft. En die trouw zijn aan hun belofte wanneer zij een belofte hebben gedaan en de geduldigen in tegenspoed, in rampspoed en in oorlogstijd. Zij zijn diegenen die Moettaqôen zijn, en zij zijn het die de godvrezenden zijn.'),
(185, 2, 178, 'O jullie die geloven, de Qishâsh inzake doodslag is jullie verplicht: de vrije (mens) voor de vrije (mens), de slaaf voor de slaaf, de vrouw voor de vrouw. En degene die dan kwijtschelding zan zijn broeder ontvangt, laat het dan gevolgd worden door een redelijke (eis van de eiser) en genoegdoening voor hem op een goeded (manier, van de schuldige). Dat is een verlichting van jullie Heer en een Barmhartigheid en degene die dan daarna nog overtreedt, voor hem ie er dan een pijnlijke bestraffing.'),
(186, 2, 179, 'En voor jullie is er in de Qishâsh leven, O bezitters van verstand, hopelijk zullen jullie (Allah) vrezen.'),
(187, 2, 180, 'Het is jullie verplicht wanneer de dood één van jullie nabij is, als hij bezit nalaat, een testament te maken voor de ouders en de verwanten, volgens wat redelijk is (dit is) een plicht voor de Moetaqôen.'),
(188, 2, 181, 'Wie den het testament verandert nadat hij het heeft gehoord: voorwaar, dan rust de zonde op hen die het veranderd hebben. Voorwaar, Allah is Alhorend, Alwetend.'),
(189, 2, 182, 'Maar degene die dan van de erflater partijdigheid of zonde vreest, en daarna verzoening tussen hen teweeg bracht, dan rust er geen zonde op hem. Voorwaar, Allah is Meest Vergevensgezind, Meest Barmhartig.'),
(190, 2, 183, 'O jullie dei geloven, het vasten is jullie verplicht, zoals het ook verplicht was voor hen vóór jullie, hopelijk zullen jullie (Allah) vrezen.'),
(191, 2, 184, '(Vast) en vastgesteld aantal dagen. Maar degene die dan van jullie ziek is, of op reis, dan een aantal andere dagen. En op degenen (van hen) die het (slechts met grote moeite) kunnen volbrengen, rust de plicht van Fidyah: het voeden van een arme. Maar degene die vrijwillig meer (dan verplicht is) geeft: dat is beter voor hem. En dat jullie vasten is beter voor jullie, als jullie dat maar weten.'),
(192, 2, 185, 'De maand Ramadhân is het waarin de Koran is neergezonden, als Leiding voor de mensheid en als duidelijke bewijzen van de Leiding en de Foerqân. Wie van jullie aanwezig is in de maand, laat die dan vasten, maar wie ziek is of op reis, dan is er een aantal andere dagen (om de vasten in te halen). Allah wenst voor jullie het gemakkelijke en Hij wenst niet voor jullie het ongemak. En maakt het aantal (dagen) vol en prijst Allah\'s Grootheid omdat Hij jullie leiding schonk, hopelijk zullen jullie dankbaar zijn.'),
(193, 2, 186, 'En wanneer Mijn dienaren jou (O Moehammad) vragen stellen over Mij: voorwaar, Ik ben nabij, Ik verhoor de smeekbede van de smekende wanneer hij tot Mij smeek. Laten zij aan Mij gehoor geven en in Mij geloven. Hopelijk zullen zij de juiste Leiding volgen.'),
(194, 2, 187, 'Het is jullie in de nachten van het vasten toegestaan omgang te hebben met jullie vrouwen. Zij zijn (als) kleding voor jullie en jullie zijn (als) kleding voor hen. Allah wist dat jullie jezelf bedrogen, Hij aanvaardde jullie berouw en vergaf jullie. Nu mogen jullie dan omgang met hen hebben. En streeft naar wat Allah voor jullie bepaald heeft. En eet en drinkt tot de witte draad en de zwarte draad voor jullie te onderscheiden is, het is Fadjr (ochtendschemering). Maakt daarna het vasten vol tot zonsondergang. En hebt geen omgang met hen, terwijl jullie I\'tikâf in de moskeeen verrichten. Dat zijn de greenzen van Allah, nadert deze daarorn niet. Zo maakt Allah Zijn Tekenen duidelijk voor de mensen, hopelijk zullen zij (Allah) vrezen.'),
(195, 2, 188, 'En eet niet onderling van jullie bezittingen op onwettige wijze door deze (bezittingen op misleidend wijze) voor de rechters te brengen, zodart jullie op zondige wijze van een gedeelte van de bezittingen van de mensen kunnen eten, terwijil jullie het weten.'),
(196, 2, 189, 'Zij vragen jou (Moehammad) over de nieuwe manen. Zeg: "Zij zijn tijdsaanduidingen voor de mensen en (voor het vastellen van) de Haddj. En het is geen vroomheid dat jullie de huizen binnengaan aan de achterzijden, vroom zijn zij (Allah) vrezen en die de huizen binnengaan door hun deuren. En vreest Allah, hopelijk zullen jullie welslagen.'),
(197, 2, 190, 'En strijdt op de Weg van Allah tegen degenen die tegen jullie strijden en overtreedt niet. Voorwaar, Allah heeft de overtreders niet lief.'),
(198, 2, 191, 'En doodt hen waar jullie hen ook aantreffen en verdrijft hen zoals zij jullie hebben verdreven. En Fitnah is erger dan doodslag. En bestrijdt hen niet bij de Masdjid al Harâm (de Gewijde Moskee te Mekkah) totdat zij jullie daar bestrijden; als zij jullie dan bestrijden: doodt hen dan. Zo is de vergeliding voor de ongelovigen.'),
(199, 2, 192, 'Maar als zij ophouden, voorwaar, dan is Allah Meest Vergevensgezind, Meest Barmhartig.'),
(200, 2, 193, 'En bestridjt hen tot er geen Fitnah (meer) is en de godsdienst aan Allah behoort, maar zij dan ophouden, dan is er geen vijandschap, behalve tegen de onrechtplegers.'),
(201, 2, 194, 'De gewijde maand om de gewijde maand, (in) de gewijde (plaatesen en maanden) geldt de Qishâsh. Wie dan tegen jullie overtreedt, overtreedt dan tegen hem dan en dezelfde mate waarin hij tegen jullie overtreedt. En vreest Allah en weet dat Allah met de Moettaqôen is.'),
(202, 2, 195, 'En geeft bijdragen op de Weg van allah, en stort jullie niet door eigen toedoen in de ondergang, en doet goed. Voorwaar, Allah heeft de weldoeners lief.'),
(203, 2, 196, 'En verricht de Haddj en de \'Oemrah voor Allah. En indien iemand dan verhinderd is, laat die dan een offerdier (slachten) dat makkelijk te vinden is, en escheert jullie hoofden niet tot het offerdier zijn slachtplaats heeft bereikt. (Voor) wie van jullie ziek is of iets aan zijn hoofd heeft, dat hem last bezorgt (en waardoor scheren noodzakelijk is) is er Fidyah (een vervangende plicht): het vasten of het voeden (van armen) of het slachten van een offerdier. En wanneer jullie in veiligheid zijn en (het betreft) degene die de Haddj op de Tamattoe\'-wijze verricht, laat hem dan offeren wat makkelijk te vinden is. Maar degene die niets vindt, (voor hem geldt) dan het vasten: drie dagen gedurende de Haddj en zeven (dagen) wanneer jullie teruggekeerd, dat is tien bijelkaar. Dat geldt voor degene wiens gezinsleden niet bij de Masdjid al Harâm (de Gewijde Moskee te Mekkah) wonen. En vreest Allah en weet dat Allah hard is in de bestraffing.'),
(204, 2, 197, 'De Haddj is in de bekende maanden. Degene die zich dan tot het verrichten van de Haddj daarin heeft verplicht, (voor hem is er) geen geslachtsgemeenschap, geen zondigheid en geen twist tijdens de Haddj. En wat jullie aan goeds doen. Allah kent het. En neemt proviand mee, en de beste proviand is Taqwa, vreest Mij daarom, O jullie bezitters van verstand.'),
(205, 2, 198, 'Er rust op jullie geen zonde als jullie (tijdens de Haddj) een gunst van jullie Heer zoeken. Wanneer jullie dan \'Arafâh verlaten, gedenkt dan Allah bij het Gewijde Teken (te Moedzdalifah) en gedenkt Hem omdat Hij jullie geleid heeft, terwijl jullie daarvoor tot de dwalenden behoorden.'),
(206, 2, 199, 'Vertrekt daarna van waar de andere mensen vertrekken (\'Arafâh) en zoekt vergeving bij Allah. Voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(207, 2, 200, 'Wanneer jullie dan jullie (Haddj-)rituelen hebben voltooid, gedenkt dan Allah zoals jullie je vaderen gedenken, of nog intenser. Er zijn mensen die smeken: "Onze Heer, geef ons in de wereld." Maar er is voor hen in het Hiernamaals geen aandeel.'),
(208, 2, 201, 'En er zijn er onder hen die smeken: "Onze Heer, geef ons in de wereld wat goed is en het Hiernamaals wat goed is en bescherm ons tegen de bestraffing van de Hel."'),
(209, 2, 202, 'Zij zijn degenen voor wie er een beloning is voor wat zij hebben verricht. En Allah is snel in de afrekening.'),
(210, 2, 203, 'En gedenkt Allah gedurende een vastgesteld aantal dagen. Maar wie haast heeft om na twee dagen te vertrekken, en rust dan geen zonde op hem; en wie (het vertrek nog een dag) uitstelt, er rust dan (ook) geen zonde op hem, voor wie Allah vreest. En vreest Allah en weet dat jullie tot Hem verzameld zullen worden.'),
(211, 2, 204, 'En onder de mensen is er degene wiens woorden over het wereldse leven jullie verbazen en hij roept Allah om te getuigen over wat zich in zijn hart bevindt, terwijl hij de ergste opstandeling is.'),
(212, 2, 205, 'En wanneer hij zich afwendt, (dan) gaat hij op de aarde rond om er verderf te zaaien en het gewas en het vee te vernietigen. En Allah houdt niet van het verderf.'),
(213, 2, 206, 'En wanneer tot hem wordt gezegd: "Vreest Allah," dan maakt de trots op zijn zonde zich van hem meester. De Hel is voor hem voldoende, en dat is de slechtste verblijfplaats!'),
(214, 2, 207, 'En er is er een onder de mensen die zichzelf verkoopt, het welbehagen van Allah zoekend. En Allah is Meest Genadig voor de dieneren.'),
(215, 2, 208, 'O jullie die geloven, treedt de Islam binnen, volledig, en volgt niet de voetstappen van de Satan. Voorwaar, hij is voor jullie een duidelijke vijand.'),
(216, 2, 209, 'Wanneer jullie dan afwijken (van de Weg van Allah), nadat de duidelijke bewijzen tot jullie zijn gekomen, weet dan dat Allah Almachtig, Alwijs is.'),
(217, 2, 210, 'Wat zij afwachten, is niets anders dan dat (de bestraffing van) Allah tot hen komt, in de schaduwen van de wolken, en de Engelen, en dat de zaak (hun ondergang) wordt beslist. En tot Allah worden alle zaken teruggevoerd.'),
(218, 2, 211, 'Vraag aan de Kinderen van Israel hoeveel duidelijke bewijzen Wij hun hebben gegeven. En wie de genieting van Allah vervangt nadat deze tot hem is gekomen: voorwaar, Allah is hard in de bestraffing.'),
(219, 2, 212, 'Voor degenen die ongelovig zijn, is het wereldse leven schoonschijnend gemaakt en zij bespotten degenen die geloven. Maar degenen die (Allah) vrezen, zullen zich op de Dag der Opstanding boven hen bevinden. En Allah schenkt voorzieningen zonder berekening aan wie Hij wil.'),
(220, 2, 213, 'De mensheid was een godsdienst (toegedaan, maar er ontstond oneningheid) waarop Allah de Profeten zond als verkondigers van verheugende tijdingen en als waarschuwers. En Hij zond met hen de Schrift neer met de Waarheid om te oordelen tussen de mensen over hetgeen waarover zij van mening verschilden. En niemand verschilde van mening daarover dan degenen aan wie het gegeven was, nadat de duidelijke bewijzen tot hen waren gekomen, uit onderlinge afgunst. Allah leidde degenen die geloven met de Waarheid, met Zijn toestemming, (weg van degenen die) van mening verschilden. En Allah leidt wie Hij wil op een recht Pad.'),
(221, 2, 214, 'Denken jullie dat jullie het Paradijs zulen binnengen, terwijl het gelijke dat tot degenen kwam die voor jullie zijn heengegaan, nog niet tot jullie is gekomen? Rampen en tegenspoed troggen hen en zij werden zó geschokt dat de Boodschapper en degenen die met hem geloofden, zeiden: "Wanneer kompt de hulp van Allah?" Weet: voorwaar, de hulp van Allah is nabij.'),
(222, 2, 215, 'Zij vragen jou wat zij als bijdrage moeten geven. Zeg: "Wat jullie aan goeds geven als bijdrage, is besternd voor de ouders en de verwanten en de wezen en de armen en de reiziger zonder proviand." En wat jullie aan goeds doen: voorwaar, Allah is daarover Alwetend.'),
(223, 2, 216, 'De strijd is jullie verplicht, terwijl jullie er een afkeer van hebben. Maar het kan zijn dat jullie afkeer van iets hebben, terwijl het goed is voor jullie; en het kan zijn dat jullie van iets houden, terwijl het slecht is voor jullie. En Allah weet, terwijl jullie niet weten.'),
(224, 2, 217, 'Zij vragen jou over de strijd in de Gewijde maanden. Zeg (O Moehammad): "De strijd daarin is een grote zonde. En het afhouden van het Pad van Allah en ongeloof aan Hem en het versperren van de teogang tot de Masdjid al Harãm" (de Gewijde Moskee te Mekkah) en het verdrijven van de bewoners er omheen, (dit alles) is nog erger bij Allah. En Fitnah (hier: afgoderij) is erger dan het doden. En zij zullen niet ophouden jullie te bestrijden totdat zij jullie van jullie godsdienst hebben afgebracht en afvalligen hebben gamaakt, als zij dartoe in staat zouden zijn. En wie van julle afvallig is aan zijn godsdienst en dan sterft, terijl hij een ongelovige is, diegenen zijn het wier daden vruchteloos zijn, op aarde en het Hiernamaals, diegenen zijn de bewoners van de Hel. Zij zijn daarin eeuwig levenden.'),
(225, 2, 218, 'Voorwar, degenen die geloven en degenen die uitgeweken zijn en degenen die strijden op de Web van Allah, zij zijn degenen die hopen op de Barmhartigheid van Allah. En Allah is Vergevensgezind, Meest Barmhartig.'),
(226, 2, 219, 'Zij vragen jou over de wijn en het kansspel. Zeg: "In beide is grote zonde en nut voor de mensen, maar de zonde in beide is groter dan hun nuit." En zij vragen jou wat zij aan bijdragen moeten geven. Zeg: "Wat jullie kunnen missen." Zo maakt Hij voor jullie Zijn Tekenen duidelijk, hopelijk zullen jullie nadenken.'),
(227, 2, 220, 'Over de wereld en het Hiernamaals. En zij vragen jou over de wezen. Zeg: "Het juiste beheer van hun zaken is een goede daad, en als jullie je bezittingen met die can hen vermengen, dan zijn zij jullie broeders. An Allah weet de verderfzaaier van de goede beheerder te onderscheiden. En als Allah het wilde, zou Hij jullie in moeilijkheden gebracht hebben. Voorwaar, Allah is Almachtig, Alwetend.'),
(228, 2, 221, 'En huwt niet met de veelgodenaanbidsters totdat zij gelovigen zijn geworden, en een gelovige slavin is zeker beter dan een veelgodenaanbidster, ook al is zij aantrekkelijk voor jullie. En huwelijkt (de gelovige vrouwen) niet uit aan de veelgodenaanbidders, totdat zij gelovigen zijn geworden. En een gelovige slaaf is zeker beter dan een veelgodenaanbidder, ook al bevalt hij jullie. Zij nodigen uit tot de Hel, terwijl Allah tot het Paradijs en tot de vergeving uitnodigt, met Zijn verlof. Hij maakt Zijn Verzen duidelijk aan de mensen, hopelijk zullen zij (de vermaning) ter harte nemen.'),
(229, 2, 222, 'En zij vragen jou over de menstruatie (Haid). Zeg: "Dat is een onreinheid, vermijdt daarom (sexueel contact met) de vrouwen gedurende de ongesteldheid. En nadert hen niet totdat zij rein zijn. Wanneer zij zich dan gereinigd hebben, dan mogen jullie tot hen komen, zoals Allah jullie bevolen heeft. Voorwaar, Allah heeft de berouwvollen lief en Hij heeft hen lief die zich reinigen.'),
(230, 2, 223, 'Jullie vrouwen zijn (als) akkers voor jullie, komt dan tot jullie akkers zoals jullie wensen. En stuurt voor jullie zelf (goede werken) vooruit en vreest Allah en weet dat jullie Hem zeker zullen ontmoeten. En geeft verheugende tijdingen aan de gelovigen.'),
(231, 2, 224, 'En maakt (de Naam van) Allah niet tot een belemmering in jullie eden (zwerende) dat jullie geen goedheid zullen bedrijven en (Allah) niet zullen vrezen en geen verzoening tussen de mensen tot stand zullen brengen. En Allah is Alhorend, Alwetend.'),
(232, 2, 225, 'Allah rekent jullie onnadenkendheid in jullie eden niet aan. Maar Hij beoordeelt jullie naar wat jullie harten verworven hebben (door jullie intenties). En Allah is Vergevensgezind, Zachtmoedig.'),
(233, 2, 226, 'Voor degenen die zweren zich te zullen onthouden van hun vrouwen is een termijn van vier maanden vastgesteld. Als zij dan terugkeren (naar hun vrouwen): voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(234, 2, 227, 'Maar als zij vastbesloten zijn tot echtscheiding: voorwaar, Allah is Alhorend, Alwetend.'),
(235, 2, 228, 'En de gescheiden vrouwen moeten voor zichzelf een Wachttijd (\'Iddah) van drie maandelijkse perioden (maandstonden) in acht nemen. En het is hen niet toegestaan le verbergen wat Allah in hun schoten heeft geschapen, als jullie geloven in Allah en in de Laatste Dag. En hun echtgenoten hebben het recht om hen terug te nemen binnen die wachtijd, als zij verzoening wensen. En voor de vrouwen zijn er rechten overeenkomstig hun plichten, volgens wat redelijk is. Maar voor de mannen is er een rang boven hen (de vrouwen). En Allah is Almachtig Alwijs.'),
(236, 2, 229, 'De verstoting is twee maal (mogelijk). Daarna is er (de keus tussen) terugname volgens de voorschriften of scheiding op een goede manier. En het is jullie niet toegestaan om iets terug te nemen van wat jullie hen (de vrouwen gegeven hebben, behalve wanneer beiden vrezen dat ze niet de voorschriften van Allah in acht kunnen nemen. Als jullie dan vrezen dat zij beiden de voorschriften van Allah niet in acht nemen dan is het geen zonde voor wanneer zij zich ermee vrijkoopt. Dat zijn de voorschriften van Allah, overtreedt die daarom niet. En degenen de voorschriften van Allah over treden: diegenen zijn de onrechtplegers.'),
(237, 2, 230, 'En wanneer bij haar de scheiding heeft gegeven dan is zij hem daarna niet (als echtgenote) toegestaan, totdat zij niet een andere man gehuwd geweest is. En als hij dan van haar gescheiden is, dan rust er geen zonde op hen als zij weer bij elkaar terugkomen, wanneer zij menen dat zij de voorschriften van Allah in acht kunnen nemen. En dat zijn de voorschriften van Allah die Hij duidelijk maak, voor een volk dat weet.'),
(238, 2, 231, 'Wanneer de vrouwen scheiding (aankondigen) en zij hebben hun termijn (de wachttijd van drie maanden) bereikt, neemt hen dan terug volgens de voorschriften of scheidt volgens de voorschriften. En houdt hen niet vast met de bedoeling hen te kwellen waarmee jullie zouden overtreden. En degene die dat zou doen, die heeft voorzeker zichzelf onrecht aangedaan. En maakt de Verzen van Allah niet tot onderwerp van bespotting. En gedenkt de gunst van Allah aan jullie en wat Hij aan jullie heeft neergezonden van het Boek (de Koran) en de Wijsheid waarmee Hij jullie onderwijst. En vreest Allah en weet dat Allah over alle zaken Alwetend is.'),
(239, 2, 232, 'En wanneer jullie de vrouwen scheiding hebben gegeven en zij hun termijn hebben bereikt verhindert hen dan niet om te huwen met hun aanstaande echtgenoten als zij met elkaar overeenstemming hebben bereikt, volgens de voorschriften. Dat is waartoe jullie vermaand worden, (voor) wie van jullie in Allah gelooft en in de Laatste Dag. Dat is deugdzamer en reiner voor jullie. En Allah weet, terwijl jullie niet weten.'),
(240, 2, 233, 'De moeders dienen hun kinderen twee volle jaren te zogen, voor wie die de zogingsperiode wil volmaken. En op de vader rust de plicht van het voorzien in hun voedsel en hun kleding, volgens de voorschriften. Niemand wordt belast dan volgens zijn vermogen. Laat de moeders niet geschaad worden vanwege haar kind, noch de vader vanwege zijn kind. En op de erfgenamen rust dezelfde plicht. Maar wanneer beiden spening wensen met wederzijds goedvinden en overleg, dan rust er geen zonde op hen. En wanneer jullie je kinderen door zoogmoeders wilt laten zogen, dan rust er geen zonde op jullie, als jullie er een redelijke vergoeding voor geven. En vreest Allah en weet dat Allah Alziende is over wat jullie doen.'),
(241, 2, 234, 'En degenen van jullie die worden weggenomen (overlijden) en echtgenoten achterlaten, zij moeten dan voor zichzelf een termijn in acht nemen van vier maanden en tien dagen. En wanneer zij hun termijn hebben bereikt, dan rust er geen zonde op hen ten aanzien van wat zij met zichzelf doen, volgens de voorschriften. En Allah is Alwetend over wat jullie doen.'),
(242, 2, 235, 'En er rust geen zonde op jullie wanneer jullie de vrouwen indirect huwelijksaanzoeken doen, of jullie het verbergen in jullie harten. Allah weet dat jullie hen zuilen noemen, maar doet hen geen beloften in het geheim, behalve om een behoorlijk woord te spreken. En besluit niet tot de huwelijksvoltrekking totdat de voorgeschreven termijn is bereikt. En weet dat Allah weet wat zich in jullie harten bevindt. Hoedt jullie daarom voor Hem en weet dat Allah Vergevensgezind, Zachtmoedig is.'),
(243, 2, 236, 'Er rust geen zonde op jullie wanneer jullie de vrouwen scheiding geven voordat jullie hen hebben aangeraakt (geslachtsgemeenschap) of jullie voor hen een bruidschat hebben vastgesteld, En geeft hun een geschenk, de rijke volgens zijn vermogen en de arme volgens zijn vermogen. Een redelijk geschenk: als een plicht voor de weldoeners.'),
(244, 2, 237, 'En wanneer jullie hun scheiding geven voor-dat jullie hen hebben aangeraakt en terwijl jullie voor hen een bruidschat hebben vastgesteld: (geeft dan) de helft van wat jullie hebben vastgesteld behalve wanneer zij kwijtschelding geven of degene in wiens hand de huwelijksvoltrekking rust. En dat jullie kwijtschelding geven is dichter bij Taqwa. En vergeet niet elkaars goede eigenschappen. Voorwaar, Allah is Alziende over wat jullie doen.'),
(245, 2, 238, 'Waakt over de shalât en (in het bijzonder) over de middelste shalât (\'Ashr). En staat voor Allah in ootmoed.'),
(246, 2, 239, 'En als jullie (een aanval) vrezen, (verricht de shalât dan) lopende of rijdende en als jullie dan in veiligheid zijn, gedenkt dan Allah omdat Hij jullie heeft onderwezen wat jullie voorheen niet wisten.'),
(247, 2, 240, 'En degenen onder jullie die weggenomen worden en echtgenotes achterlaten, moeten een testament maken voor hun echtgenotes, een voorziening voor een jaar, zonder uitzelting, maar als zij (de huizen) uit vrije wil verlaten, dan rust er geen zonde op jullie ten aanzien van wat zij voor zichzelf doen, volgens de voorschriften. En Allah is Almachtig, Alwijs.'),
(248, 2, 241, 'En voor de gescheiden vrouwen is er een gift naar redelijkheid, als een plicht voor de Moettaqôen.'),
(249, 2, 242, 'Zo maakt Allah voor jullie zijn Verzen duidelijk hopelijk zullen jullie begrijpen.'),
(250, 2, 243, 'Heb jij degenen niet gezien die uit hun huizen vluchtten terwijl zij met duizenden waren, uit angst voor de dood? Allah zei toen tot hen: "Sterft!" Daarna bracht Hij hen weer tot leven. Voorwaar, Allah is de Bezitter van Gunsten voor de mensen, maar de meeste mensen zijn niet dankbaar.'),
(251, 2, 244, 'En strijdt op de Weg van Allah en weet dat Allah Alhorend, Alwetend is.'),
(252, 2, 245, 'Wie is degene die aan Allah een goede lening geeft? Hij zal die vele malen vermenigvuldigen. En Allah beperkt en verruimt (Zijn voorzieningen) en tot Hem worden jullie teruggebracht.'),
(253, 2, 246, 'Heb jij niet gezien, (hoe het einde was van) de vooraanstaanden van de Kinderen van Israe I na (het heengaan van) Môesa? Toen zij tot een Profeet van hen zeiden: "Wijs voor ons een koning aan, dan zullen wij strijden op de Weg van Allah." Hij zei: "Is het mogelijk dat als jullie de strijd wordt verplicht, jullie niet zullen strijden?" Zij zeiden: "Waarom zouden wij niet op de Weg van Allah strijden, terwijl wij uit onze woonplaatsen zijn verdreven en van onze zonen?" En toen dan hun de strijd werd verplicht, wendden zij zich af, met uitzondering van een klein aantal van hen. En Allah kent de onrechtplegers.'),
(254, 2, 247, 'En hun Profeet zei tot hen: "Voorwaar, Allah heeft voor jullie Thâlôet aangewezen als koning." Zij zeiden: "Hoe kan het zijn dat hem het koningschap over ons gegeven wordt, terwijl wij meer recht hebben op het koningschap dan hij, en hem geen overvloed aan bezittingen is gegeven?"\' Hij zei: "Voorwaar, Allah heeft hem boven jullie verkozen en hem rijkelijk voorzien van kennis en lichaamskracht. En Allah geeft het koningschap aan wie Hij wil en Allah is Allesomvattend. Alwetend,'),
(255, 2, 248, 'En hun Profeet zei tot hen: "Voorwaar, een teken van zijn koningschap is dat de Tabôet (ark) tot jullie zal komen waarin geruststelling van jullie Heer is, (en) waarin zich een nalatenschap bevindt van wat is nagelaten door de familie van Môesa en de familie van Hârôen. De Engelen zullen hem (de ark) dragen. Voorwaar, daarin zijn zeker Tekenen voor jullie, als jullie gelovigen zouden zijn.'),
(256, 2, 249, 'En toen Tâlôet met de legers was uitgetrokken, zei hij: "Voorwaar, Allah zal jullie zeker beproeven door middel van een rivier. Wie er dan van drinkt, die is niet een van mij en wie er niet (meer) van proeft, dan een slokje uit zijn hand, die is een van mij." Toen dronken zij ervan, met uitzondering van een klein aantal van hen. Toen hij en degenen die met hem geloofden (de rivier) waren overgestoken, zeiden zij: "Wij hebben vandaag geen kracht om Djâlôet (Goliat) en zijn legers te bevechten." Degenen die overtuigd waren dat zij Allah zeker zullen ontmoeten, zeiden: "Hoeveel kleine troepen hebben niet grote troepen overwonnen, met het verlof van Allah. En Allah is met de geduldigen."'),
(257, 2, 250, 'En toen wij optrokken tegen Djâlôet en zijn legers, zeiden zij: "Onze Heer, schenk ons geduld en maak onze voeten standvastig en sta ons bij tegen het ongelovige volk."'),
(258, 2, 251, 'Toen versloegen zij hen met het verlof van Allah en Dâwôed doodde Djâlôet en Allah gaf hem het koningschap en de Wijsheid (het Profectsehap) en Hij onderwees hem wat Hij wilde. En als Allah niet een deel van de mensen door een ander (deel) zou verstoten, din zou de aarde ten onder gaan. Maar Allah is de Bezitta van gunsten voor de werelden.'),
(259, 2, 252, 'Dit zijn Verzen van Allah die Wij in de Waarneid aan jullie voorlezen, En voorwaar, jij (O Moehammad) behoort tot de Gezondenen.'),
(260, 2, 253, 'Dat zijn de Boodschappers van wie Wij sommigen boven anderen bevoorrecht hebben, onder hen zijn er tot wie Allah gesproken heeft, en onder hen zijn er die Hij (enkele) graden verheven heeft, en Wij hebben aan \'Isa, de zoon van Maryam, duidelijke Tekenen gegeven en Wij hebben hem niet de Heilige Geest (Djibrîl) versterkt. Als Allah het gewild had, hadden degenen na lien niet met elkaar gevochten nadat de duidelijke Tekenen tot hen waren gekomen, maar zij twistten: sommigen geloofden en sommigen van hen waren ongelovig. En als Allah het gewild had, hadden zij niet met elkaar gevochten, maar Allah doet wat Hij wil.'),
(261, 2, 254, 'O jullie die geloven: geeft van dat waar Wij jullie mee voorzien hebben, voordat de Dag komt waarop er noch handel, noch voorspraak zal zijn. En de ongelovigen: zij zijn de onrechtvaardigen.'),
(262, 2, 255, 'Allah, er is geen god dan Hij, de Levende, de Zelfstandige, sluimer noch slaap kan Hem treffen, aan Hem behoort toe wat er in de hemelen en wat er op de aarde is. Wie is degene die van voorspraak is bij Hem zonder Zijn verlof? Hij kent wat er voor hen is en wat er achter hen is. En zij kunnen niets van Zijn Kennis omvatten, behalve wat Hij wil. En Zijn Zetel strekt zich uit over de Hemelen en de Aarde en het waken over beide vermoeit Hem niet. En Hij is de Verhevene, de Almachtige.'),
(263, 2, 256, 'Er is geen dwang in de godsdienst. Waarlijk, de rechte leiding is duidelijk onderscheiden van de dwaling, en hij die de Thaghôet verwerpt en in Allah gelooft: hij heeft zeker het stevigste houvast gegrepen, dat niet breken kan. En Allah is Alhorend, Alwetend.'),
(264, 2, 257, 'Allah is de Helper van degenen die geloven. Hij voert hen van de duisternis naar het licht. En degenen die ongelovig zijn: hun helpers zijn de Thaghôet, zij voeren hen van het licht naar de duisternis. Diegenen zijn de gezellen van de Hel, zij zullen daar eeuwig levenden zijn.'),
(265, 2, 258, 'Weet jij niet van degene die met Ibrâhîm over zijn Heer redetwistte, omdat Allah hem het koninkijk had gegeven? Toen Ibrâhîm zei: "Zijn Heer is Degene Die doet leven en doet sterven." Hij zei: "Ik doe leven en sterven." Ibrâhîm zei: "Maar voorwaar, het is Allah Die ervoor zorgt dat de zon in het Oosten opkomt, zorg jij er dan voor dat zij in het Westen opkomt." Toen zweeg degene die ongelovig was van verbazing. En Allah leidt lief onrechtvaardige volk niet.'),
(266, 2, 259, 'Of degene die, toen hij langs een dorp kwam dat verlaten was en in ruïnes lag, zei: "Hoe kan Allah het na haar dood weer doen herleven?" Toen deed Allah hem voor honderd jaar dood zijn, en wekte hem weer op. Hij zei: "Hoe lang verbleef jij hier?" Hij zei: "Ik verbleef hier een dag of een gedeelte van een dag." Hij (Allah) zei: "Nee, jij verbleef hier honderd jaar; kijk naar je voedsel en je drank, zij zijn niet bedorven; en kijk naar je ezel, zodat Wij jou tot een Teken voor de mens maken. En kijk naar hoe Wij de beenderen in elkaar zetten en met vlees bedekken." En toen hem dit duidelijk werd, zei hij: "Ik weet dat Allah Almachtig is over alle dingen."'),
(267, 2, 260, 'En toen Ibrâhîm zei: "Mijn Heer, toon mij hoe U de doden doet leven." Hij (Allah) zei: "Geloof jij dan niet?" Hij zei: "Jawel maar opdat mijn hart tot rust komt." Hij (Allah) zei: "Neem dan vier vogels en snijd ze voor je in stukken, leg dan van hen op iedere berg stukken; roep hen dan, zij zullen dan haastig tot je komen, en weet dat Allah Almachtig, Alwijs is."'),
(268, 2, 261, 'De gelijkenis van degenen die op de Weg van Allah uitgeven is als de gelijkenis van een graankorrel, (die) zeven aren voortbrengt, in iedere aar honderd korrels, en Allah vermenigvuldigt voor wie Hij wil. En Allah is Alomvattend, Alwetend.'),
(269, 2, 262, 'Degenen die hun eigendommen op de Weg van Allah uitgeven en dan bun vrijgevigheid noch met opscheppen, noch met kwetsen laten volgen, voor hen is hun beloning bij him Heer, er is voor hen geen angst en zij zullen niet treuren.'),
(270, 2, 263, '(Het uitspreken van,) vriendelijke woorden en vergeving is beter dan een liefdadigheid die door kwetsing gevolgd wordt. En Allah is Behoefteloos, Zachtmoedig.'),
(271, 2, 264, 'O jullie die geloven: maakt jullie liefdadigheid noch ongeldig door opscheppen, noch door kwetsen, zoals degene die, van zijn eigendom geeft om op te vallen bij de mensen, en (die) niet in Allah en de Laatste Dag gelooft. En de gelijkenis met hem is als met een gladde rots, bedekt met aarde, waarop zware regen valt die haar kaal achterlaat: zij verdienen niets voor wat zij gedaan hebben. En Allah leidt het ongelovige volk niet.'),
(272, 2, 265, 'En de gelijkenis van degenen die van hun eigendommen besteden omwille van het welbehagen van Allah en de versterking van hun ziel, is als met een tuin op een hoge en vruchtbare plaats waar zware regen op valt en (die) dan dubbel vrucht draagt. En als er geen zware regen valt, dan is lichte dauw (voldoende). En Allah is Alziende ever wat jullie doen.'),
(273, 2, 266, 'Wenst een van jullie een tuin met dadelpalmen en druivenranken waar de rivieren onderdoor stromen en waarin alle (soorten) fruit zijn, en de ouderdom treft hem terwijl zijn kinderen nog zwak zijn en zij (de tuin) wordt getroffen door een orkaan met vuur erin, zodat (deze) verbrandt? Zo maakt Allah aan jullie de Tekenen duidelijk. Hopelijk zullen jullie nadenken.'),
(274, 2, 267, 'O jullie die geloven: geeft van de goede dingen die jullie verworven hebben en (van wat) Wij voor jullie uit de aarde hebben voortgebracht en kiest niet van het slechte ervan om te geven, waarvan jullie slechts met gesloten ogen zouden nemen. En weet dat Allah Behoefteloos, Prijzenswaardig is.'),
(275, 2, 268, 'De Satan dreigt jullie met armoede en beveelt jullie gierigheid en Allah belooft jullie vergeving van Hem en gunst. En Allah is Alomvattend, Alwetend.'),
(276, 2, 269, 'Hij (Allah) geeft de Wijsheid aan wie Hij wijl en wie de Wijsheid geschonken wordt: aan hem wordt inderdaad veel goeds gegeven. En niemand laat zich vermanen dan de bezitters van verstand.'),
(277, 2, 270, 'En wat jullie ook in liefdadigheid uitgeven en welke eed jullie ook afleggen: voorwaar, Allah weet man en de onrechtplegers hebben geen helpers.'),
(278, 2, 271, 'Als jullie de liefdadigheid openlijk geven, dan is dat best, en als jullie haar heimelijk aan de armen geven is dat beter voor jullie en het wist jullie zonden uit. En Allah is Alwetend over wat jullie doen.'),
(279, 2, 272, 'Het is niet aan jou hen te leiden, maar Allah leidt wie Hij wil, en wat jullie aan goeds geven is voor jullie zelf. En jullie geven slechts liefdadigheid omwille van het welbehagen van Allah. En wat jullie ook van het goede geven zal jullie beloond worden en jullie zullen niet onrechtvaardig behandelt worden.'),
(280, 2, 273, '(De liefdadigheid is) voor de armen die, op de Weg van Allah, weerhouden zijn (te werken), zij zijn niet in staat op deze aarde te reizen (om te werken). Door hun bescheidenheid vermoedt de onwetende dat zij rijk zijn, je herkent hen aan hun tekens: zij vragen niet van de mensen op opdringerige wijze. En wat jullie ook van het goede uitgeven, voorwaar, Allah weet ervan.'),
(281, 2, 274, 'En degenen die \'s nachts en overdag van hun eigendommen uitgeven, heimlijk en openlijk, voor hen is hun beloning bij hun Heer, en voor hen zal er geen vrees zijn en zij zullen treuren.'),
(282, 2, 275, 'Degenen die van de rente eten zullen niet anders opstaan als degene die opstaat en door de Satan tot bezetenheid is geslagen. Dat is omdat zij zeggen: "De handel is te vergelijken met rente." Maar Allah heeft de handel toegestaan en de rente verboden. En wie nad it de vermaning van zijn Heer tot hem is gekomen stopt: voor hem is wat hij al heeft, zijn zaak is aan Allah, maar wie het herhaalt: zij zijn de bewoners van de Hel, zij zijn daarin eeuwig levenden.'),
(283, 2, 276, 'Allah zal de rente al zijn zegeningen ontnemen en Hij zal (de zegeningen) an de liefdadigheid vermeerderen en Hij houdt van geen enkele ondankbare zondaar.'),
(284, 2, 277, 'Voorwaar, degenen die geloven en goede daden verrichten en de shalât onderhouden en de zakât geven, voor hen zal de beloning bij hun Heer zijn. En zij zullen niet angstig zijn en zij zullen niet treuren.'),
(285, 2, 278, 'O jullie die geloven, vreest Allah en geeft op wat er van (vragen) van rente overblijft, als jullie gelovigen zijn.'),
(286, 2, 279, 'En wanneer jullie (dit) niet doen: weest op de hoogte van de oorlog van Allah en Zijn Boodschapper. Maar als jullie berouwvol zijn: dan blijft het oorspronkelijke bezit voor jullie. Jullie plegen (dan) geen onrechtvaardigheid en jullie worden niet onrechtvaardig behandeld.'),
(287, 2, 280, 'En wanneer degene die schuldig is in moeilijkheden verkeert, geeft dan uitsel tot een makkelijker tijd (voor hem), en wanneer jullie het (verschuldigde) als liefdadigheid beschouwen is dat beter voor jullie, als jullie het weten.'),
(288, 2, 281, 'En vreest de Dag waarop jullie tot Allah teruggevoerd zullen worden: dan zal iedere ziel beloond worden voor wat zij verdiend heeft, en zij zullen niet onrechtvaardig behandeld worden.'),
(289, 2, 282, 'O jullie die geloven, wanneer jullie een lening afsluiten tot een vastgestelde tijd, legt deze dan schriftelijk vast, en laat een schrijver van onder jullie op rechtvaardige wijze schrijven en laat de schrijver niet weigeren te schriijven zoals Allah hem onderwees. Laat hem dus schrijven en laat degene die de schuld aangaat dicteren en laat hem Allah zijn heer vrezen en laat hem niets (van wat hij schuldig is) in mindering brengen. En in het geval dat degene die de schuld aangaat dwaas of zwak is, of niet in staat om zelf te dicteren, laat dan zijn verzorger waarheidsgetrouw dicteren. En laat twee getuigen van onder jullie mannen getuigen, en als er niet twee mannen zijn, dan een man en twee vrouwen die jullie als getuigen goedkeuren, zodat als één van hen zich vergist, de andere haar kan verbeteren. En laat de getuigen niet weigeren wanneer zij opgeroepen worden. Veronachtzaamt niet het op te schrijven, klein of groot, tot zijn vastgestelde tijd, dat is rechtvaardiger bij Allah, en oprechter als bewijs en (het) vermindert de twijfels. Behalve wanneer het een contante transactie betreft die jullie met elkaar regelen, er treft jullie dan geen verwijt wanneer jullie het niet opscrijven. Maar neemt getulgen wanneer jullie handel drijven. Laat noch de schrijver, noch de getuige benadeeld worden, wanneer jullie het (toch) doen: dan zou dat een zware zonde van jullie zijn. En vreest Allah, en het is Allah Die jullie onderwijst, en Allah is Alwatende over alle zaken.'),
(290, 2, 283, 'En wanneer jullie op reis zijn, en jullie junnen geen schrijver vinden, dan kan een plechtige belofte met onderpand (hetzelfde doel dienen). En als jullie elkaar iets toevertrouwen, laat dan degene die iets toevertrouwd is, dit vertrouwen waard zijn en laat hem Allah zijn Heer vrezen. En verbergt geen bewijs. En wie het verbergt: voorwaar, zijn hart wordt zondig. En Allah is Alwetend over wat jullie doen.'),
(291, 2, 284, 'Aan Allah behoort alles wat er in de hemelen en op de aarde is, en of jullie nu openlijk laten blijken wat er in jullie zielen is, of jullie verbergen het: Allah zal jullie ervoor de rekening opmaken, en Hij vergeeft wie Hij wil, en Hij bestraft wie Hij wil, en Allah is Almachtig over alle zaken.'),
(292, 2, 285, 'De Boodschapper gelooft in wat hem van zijn Heer is geopenbaard, en (ook) de gelovigen, allen geloven in Allah, en Zijn Engelen en Zijn Boeken en Zijn Boodschappers. Wij maken geen onderscheid tussen Zijn Boodschappers. Zij zeiden: "Wij luisteren en wij gehoorzamen, vergeef ons, onze heer, en tot U is de terugkeer."'),
(293, 2, 286, 'Allah belast niemand dan volgens zijn vermogen. Voor hem is hetgeen (de beloning) dat hij doet en voor hem is hetgeen (de bestraffing) wat hij doet. (Zeg:) "Onze Heer, bestraf ons niet als wij vergeten of als wij fouten maken. Onze Heer, belast ons niet zoals U degenen vóór ons belast heeft. Onze Heer belast ons niet met wat wij niet kunnen dragen en scheld ons kwijt en vergeef ons en wees ons genadig. U bent onze Meester en help ons tegen het ongelovige volk."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'Alif - Lâm - Mîm.'),
(295, 3, 2, 'Allah, er is geen god dan Hij, de Levende, de Eeuwig Bestaande.'),
(296, 3, 3, 'Hij heeft aan jou het Boek met de Waarheid neergezonden, bevastigend wat daaraan voorafgegaan was, en Hij zond de Taurât en de Indjîl neer,'),
(297, 3, 4, 'Hiervoor, als Leiding voor de mensen, en Hij deed de Foerqân neerdalen. Voorwaar, degenen die niet in de Tekenen van Allah geloven, voor hen is er een strenge bestraffing. En Allah is Almachtig, Bezitter can de Vergelding.'),
(298, 3, 5, 'Voorwaar, voor Allah is niets verborgen op de aarde of in de hemel.'),
(299, 3, 6, 'Hij is Degene Die die jullie in de baarmoeder vorm geeft, hoe Hij wil. Er is geen god dan Hij, de Almachtige, de Alwaijze.'),
(300, 3, 7, 'Hij is Degene Die het boek aan jou heeft neergezonden, met daarin eenduidige Verzen, zij zijn de grondslag van het Boek, andere zijn voor meer uitleg vatbaar. Maar degenen die in hun harten een neiging (tot valsheid) hebben, misbruiken de (Verzen) met meerdere betekenissen om Fitnah te zaaien en de ware betekenis ervan te zoeken. En de uitleg ervan is bij niemand ervan bekend dan bij Allah. En degenen die stevig gegrondvest in kennis staan, zeggen: "Wij geloven er in, alles is van onze Heer," en zij laten zich niet vermanen, behalve de bezitters van verstand."'),
(301, 3, 8, '(Zij zeggen:) "Onze Heer, laat onze harten niet afwijken nadat U ons geleid heeft en schenk ons van Uw kant Barmhartigheid. Voorwaar. U bent de Schenker.'),
(302, 3, 9, 'Onze Heer, voorwaar, U bent Degene Die de mensen verzamelt op de Dag waaraan geen twijfel is." Voorwaar, Allah verbreekt de belofte niet.'),
(303, 3, 10, 'Voorwaar, degenen die ongelovig zijn: hun eigendommen, noch hun nakomelingen zullen hun baten tegen (de bestraffing) van Allah en zij zijn brandstof voor de Hel.'),
(304, 3, 11, 'Zoals dat het geval was met het familie van de Fir\'aun en degenen vóór hen, zij loochenden Onze Tekenen en Allah strafte hen vanwege hun zonden. En Allah is streng in de Bestraffing.'),
(305, 3, 12, 'Zeg (O Moehammad tot degenen die ongelovig zijn: "Jullie zullen verslagen worden en in de Hel verzameld worden, en (dat is) de slechtste verblijfplaats."'),
(306, 3, 13, 'Er is waarlijk reeds vóór jullie een Teken geweest in de twee groepen (legers) die elkaar entmoetten. Het ene vocht op de Weg van Allah, en het andere was een ongelovige (groep). Zij (de ongelovigen) zagen hen (de gelovigen) met hun ogen twee maal En Allah steunt met Zijn hulp wie Hij wil. Voorwaar, daarin is zeker een lering voor de bezitters van inzicht.'),
(307, 3, 14, 'Voor de mensen is de liefde voor begeerlijke (zaken) als vrouwen aantrekkelijk gemaakt, (evenals de liefde voor) zonen, omvangrijke gouden en zilveren bezittengen, gemerkte paarden en kudden dieren en akkers. Dat is de genieting van het wereldse leven. En Allah, bij Hem is de beste terugkeer.'),
(308, 3, 15, 'Zeg (O Moehammad): "Zal ik jullie over (iets) beters dan dat mededelen? Voor degenen die (Allah) vrezen zijn er tuinen (de Paradijs) bij hun Heer, waar onder door de rivieren stromen, daar zijn zij eeuwig levenden en (daar zijn) reine echtgenotes en het welbehagen van Allah." En Allah is Alziende over de dienaren.'),
(309, 3, 16, '(Zij zijn) degenen die zeggen: "Onze Heer, voorwaard, wij geloofden, vergeef ons onze zonden en bescherm ons tegen de bestraffing van de Hel."'),
(310, 3, 17, '(Zij zijn) de geduldigen en de waarachtigen en de gehoorzamen en degenen die (voor Allah) uitgeven en degenen die (Allah) om vergeving vragen op het laatst van de nacht.'),
(311, 3, 18, 'Allah getuigt dat er geen god is dan Hij en (ook) de Engelen en de bezitters van kennis, standvastig in de gerechtighied. Er is geen god dan Hij, de Almachtige, de Alwijze.'),
(312, 3, 19, 'Voorwaar, de (enige) Godsdienst bij Allah is de Islam en degenen die de Schrift gegeven was verschilden (hierover) nadat de kennis tot hen gekomen was niet over van mening dan door onderlinge jaloezie. En wie de Tekenen van Allah loochent: voorwaar, Allah is snel in de afrekening.'),
(313, 3, 20, 'En als zij met jou redetwisten, zeg dan: "Ik heb mijn aangezicht overgegeven aan Allah en (ook) wie mij volgt." En zeg tegen degenen die het Boek gegeven is en de ongeletterden: "Geven jullie je over (aan Allah)?" En als zij zich overgeven, dan volgen zij Leiding, maar als zij zich afrekeren, dan is er voor jou slechts de plicht tot verkondiging. En Allah is Alziende over de dienaren.'),
(314, 3, 21, 'Voorwaar, degenen die de Tekenen van Allah loochenen en de Profeten doden zonder het recht te hebben en zij doden degenen die tot gerechtighied onder de mensen oproepen: verkondig hen een pijnlijke bestraffing.'),
(315, 3, 22, 'Zij zijn degenen wiens werken op de aarde en (in) het Hiernamaals geen vrucht dragen en er zullen voor hen geen helpers zijn.'),
(316, 3, 23, 'Heb jij degenen niet gezien aan wie een deel van de Schrift gegeven was? Zij zijn tot het Boek van Allah uitgenodigd, opdate her tussen hen zou oordelen. Vervolgens scheidde een groep van hen zich af en zij wendden zich af.'),
(317, 3, 24, 'Dat is omdat zij zeggen: "De Hel zal ons niet aanraken, behalve een vastgesteld aantal dagen." Want zij werden in hun godsdienst bedrogen door wat zij plachten te verzinnen.'),
(318, 3, 25, 'En wat (zal er met hen gebeuren) wanneer Wij hen bij elkaar brengen op een Dag waaraan geen twijfel is, en iedere ziel vergolden zal worden voor wat zij bedreven heeft, terwijl zij zullen niet onrechtvaardig behandeld worden?'),
(319, 3, 26, 'Zeg: "O Allah, Koning van het Koninkrijk, U schenkt het Koninkrijk aan wie U wilt, en U ontzegt het Koninkrijk aan wie U wilt, en U eert wie U wilt, en U vernedert wie U wilt, in Uw hand is het Goede. Voorwaar, U bent Almachtig over alle zaken."'),
(320, 3, 27, 'U doet de dag de nacht bedekken en U doet de nacht de dag bedekken en U brengt de levenden voort uit de doden en u brengt de doden voort uit de levenden en U voorziet wie U wilt zonder afrekening."'),
(321, 3, 28, 'Laten de gelovigen niet de ongelovigen in plaats van de gelovigen als beschermers nemen, en degene die dat doet heeft niets meer met Allah te maken, behalve wanneer jullie hen (de ongerlovigen) angstig vrezen. En Allah waarschuwt jullie (voor) Zijn (bestraffing). En tot Allah is de terugkeer.'),
(322, 3, 29, 'Zeg (O Moehammad): "Of jullie verbergen wat in jullie harten is, of jullie laten het zien: Allah weet ervan. Hij kent wat er in de hemelen is en wat er op de aarde is. En Allah is Almachtig over alle zaken."'),
(323, 3, 30, 'Op de Dag dat iedere ziel aanwezig vindt wat zij van het goede verricht heeft en wat zij van het kwade verricht heeft, zal zij wensen dat er een grotere afstand tussen haar en haar (kwaad) zou zijn. En Allah waarschuwt jullie (voor) Zijn (bestraffing) en Allah is Genadig voor de dienaren.'),
(324, 3, 31, 'Zeg (O Moehammad): Als jullie van Allah houden, volg mij dan: Allah zal van jullie houden en jullie zonden vergeven. En Allah is Vergevensgezind, Meest Barmhartig."'),
(325, 3, 32, 'Zeg: "Gehoorzaamt Allah en de Boodschapper, maar als jullie je afwenden: voorwaar, Allah houdt niet van de ongelovigen."'),
(326, 3, 33, 'Voorwaar, Allah verkoos Adam en Noeh en de familie van Ibrâhîm en failie van Imran boven de (andere) wereldbewoners.'),
(327, 3, 34, 'Zij zijn afstammelingen van elkaar, en Allah is Alhorend, Alwetend.'),
(328, 3, 35, 'Teon de vrouw van \'Imran zei: "Mijn Heer, voorwaar, ik wijd bij belofte aan U wat er in mijn buik is, aanvaard het van mij. Voorwaar, U bent de Alhorende, de Alwetende."'),
(329, 3, 36, 'En toen zij haar gebaard had, zei zij: "Mijn Heer, ik heb haar gebaard, (het is) een meisje," - en Allah wist het best wat zij gebaard had - "en het mannelijke is niet als het vrouwelijke. En voorwaar, ik heb haar Maryam genoemd, en ik smeek U haar en haar nakomelingen te bescherment tegen de vervloekte Satan."'),
(330, 3, 37, 'Toen aanvaardde haar Heer haar vriendelijk en deed haar goed opgroeien. En Hij stelde haar onder de hoede van Zakariyya. Iedere keer dat Zakariyya in de (gebeds-) ruimte bij haar kwam vond hij levensvoorzieningen bij haar. Hij zei: "O maryam, van waar heb jij dat?" Zij zei: "Dat komt bij Allah vandaan, voorwaar, Allah voorziet wie Hij wil zonder afrekening."'),
(331, 3, 38, 'Daar smeekte Zakariyyâ zijn Heer, hij zei: "O, mijn Heer, schenk mij van U een goed nageslacht, voorwaar, U bent het die de smeekbede verhoot."'),
(332, 3, 39, 'Toen riepen de Engelen tot hem, toen hij in de gebedsruimte (Mihrâb) in gehed stond: "Voorwaar, Allah brengt jou een verheugende tijding: (de geboorte van) Yahya, als een bevstiging van een Woord van Allah, als een leider, een ingetogene, een Profeet, behorende tot de rechtschapenen."'),
(333, 3, 40, 'Hij zei: "O mijn Heer, hoe kan ik een jongen (verwekken), terwijl ik oud gewordden ben en mijn vrouw onvruchtbaar is?" Hij (Allah) zei: "Zo doet Allah wat Hij wil."'),
(334, 3, 41, 'Hij zei "O mijn Heer, geef mij een teken." Hij (Allah) zei: "Jouw teken is, dat je drie dragen niet met de mensen spreekt dan door gebaren, gedenkt jouw Heer veel en prijst (Zijn glorie) in de avond en in de ochtend."'),
(335, 3, 42, 'En toen de Engelen zeiden: "O Maryam, voorwaarm Allah heeft jou uitverkoren en jou gereinigd en jou boven de vrouwen van de werelden uitverkoren."'),
(336, 3, 43, 'O Maryam, gerhoorzaam jour Heer en kniel je voor Hem neer en buig je met de buigenden."'),
(337, 3, 44, 'Dat zijn berichten over het verborgene die wij aan jou (O Moehammad) mededelen. En jij was niet met hen toen zij door hun pennen te werpen verlotten wie can hen verantwoordelijk was voor de zorg voor Maryam, en jij was ook niet bij hen toen zij hierover redetwistten.'),
(338, 3, 45, '(Gedenkt) toen de Engelen zeiden: "O Maryam, voorwaar, Allah kondigt jou met een Woord van Hem een verheugende tijding aan: zjin naam is de Messîh, \'Îsa, zoon van Maria, in deze wereld en in het Hiernamaals is hij een man van eer en hij behoort tot degenen die dicht (bij Allah) staan.'),
(339, 3, 46, 'En hij spreekt tot de mensen vanuit de wie en als volwassene en hij behort tot de rechtschapenen."'),
(340, 3, 47, 'Zij zei: "O mijn Heer, hoe kan ik een kind krijgen terwijl geen man mij heeft aangeraakt?" Hij (Allah) zei: "Zo is het: Allah schept wat Hij wil, als Hij over een zaak bepaalt, dan zegt Hij er slechts tegen: \'Wees\', en het is."'),
(341, 3, 48, 'En Hij onderwijst hem de Schrift, en de Wijsheid en de Taurât en de Indjîl.'),
(342, 3, 49, 'En (hij is) als een Boodschapper voor de Kinderen van Israël, (die zegt:) "Voorwaar, ik ben tot jullie gekomen met een Teken van jullie Heer, en ik maak voor jullie (iets) uit klei, gelijkende op de vorm van een vogel en ik blaas erin en het zal met verlof van Allah een vogel zijn. En ik genees de blinden en de leprozen en ik doe de doden met het verlof van Allah tot leven komen. En ik vertel jullie wat jullie eten, en wat jullie in jullie huizen bewaren. Voorwaar, daarin is een Teken voor jullie als jullie gelovigen zijn.'),
(343, 3, 50, 'En als bevestiging van wat er voor mij is gekomen van de Taurât, opdat ik jullie een paar dingen wettig kan verklaren die voor jullie verboden waren, en ik ben tot jullie gekomen met een Teken van jullie Heer, vreest daarom Allah en gehoorzaamt mij.'),
(344, 3, 51, 'Voorwaar, Allah is mijn Heer en jullie Heer, dient Hem dus, dit is een recht Pad."'),
(345, 3, 52, 'En toen \'Îsa ongeloof bij hen ontdekte, zei hij: "Wie zijn mijn helpers (op het rechte Pad) naar Allah?" Zijn metgezellen (Hawâriyyôen) zeiden: "Wij zijn helpers van Allah, wij geloven in Allah en getuigen dat wij ons overgegeven hebben.'),
(346, 3, 53, 'Onze Heer, wij geloven in wat U neergezonden hebt en wij volgen de Boodschapper en schrijf ons daarom op bij de getuigen (van de Eenheid van Allah)."'),
(347, 3, 54, 'En zji (de ongelovigen) beraamden listen en Allah maakte plannen en Allah is de beste van hen die plannen maken.'),
(348, 3, 55, 'En toen Allah zei: "O \'Îsa, voorwaar, Ik zal jou (tot Mij) nemen en Ik zal jou (tot Mij) opheffen en jou reinigen van degenen die ongelovig zijn en Ik zal degenen die jou volgen boven degenen die ongelovig zijn stellen, tot de Dag der Opstanding. Daarna zal de terugkeer tot Mij zijn en daarna zal Ik onder jullie rechtspreken over dat waarover jullie plachten de redetwistten.'),
(349, 3, 56, 'En wat degenen die ongelovig zijn betreft: hen zal Ik op de wereld en (in) het Hiernamaals met een strenge bestraffing straffen en er zullen voor hen geen helpers zijn.'),
(350, 3, 57, 'En wat degenen die gelovig zijn en goede werken verrichten betreft; Allah zal hUn hun (volledige) beloning schenken, en Allah houdt niet van de onrechtvaardigen.'),
(351, 3, 58, 'Zo dragen Wij jou voor van de Tekenen en de wijze Vermaning (de Koran).'),
(352, 3, 59, 'Voorwaar, de gelijkenis (van de schepping) van \'Îsa is bij Allah als de gelijkenis (van de schepping) van Adam. Hij schiep hem uit aarde en zei vervolgens tot hem: \'Wees\', en hij was.'),
(353, 3, 60, 'Waarheid komt van jouw Heer en behoort niet tot degenen die twijfelen.'),
(354, 3, 61, 'Wie dan met jou (Mohammed) over (\'Îsa) redetwist, nadat de kennis tot jou is gekomen, zeg dan: "Laten wij onze zonen en jullie zonen en onze vruowen en jullie vrouwen en onszelf en julliezelf bijelkaar roepen en dan (gezamenlijk) Allah\'s vloek afroepen over hen die leigen."'),
(355, 3, 62, 'Voorwaar, dit is de ware geschiedenis, en er is geen god dan Allah en voorwaar, Allah is het Die de Almachtige, de Alwijze is.'),
(356, 3, 63, 'Als zij zich dan afwenden: voorwaar, dan is Allah op de hoogte van de verderfzaaiers.'),
(357, 3, 64, 'Zeg: "O Lieden van de Schrift, komt tot een gelijkluidend woord tussen ons en jullie: dat wij niemand dan Allah aanbidden en dat wij niets naast Hem tot delgenoot maken en dat wij elkaar niet als heren naast Allah plaatsen." Als zij zich dan afwenden, zegt dan: "Getuigt dat wij ons (aan Allah) overgegeven hebben."'),
(358, 3, 65, 'O Lieden van het Boek, waarom redetwisten jullie over Ibrâhîm? De Taurât en de Indjîl zijn toch pas na hem geopenbaard, begrijpen jullie dan niet?'),
(359, 3, 66, 'Zo, jullie zijn degenen die geredetwist hebben over iets waarover jullie kennis hadden, maar waarom redetwistten jullie over iets waarover jullie geen kennis hebben? En Allah weet, en jullie weten niet.'),
(360, 3, 67, 'Ibrâhîm was geen Jood, en geen Christen, maar hij was een Hanîf, die zich (aan Allah) overgegeven had, en hij behoorde niet tot de veelgodenaanbidders.'),
(361, 3, 68, 'Voorwaar, degenen die Ibrâhîm het meest nabij komen, zijn degenen die hem en deze Profeet (Moehammad) volgen en degenen die geloven. En Allah is de Beschermer van de gelovigen.'),
(362, 3, 69, 'Een groep van de Lieden van het Boek zou jullie willen doen dwalen en zij doen niemand dan zichzelf dwalen. En zijn beseffen het niet.'),
(363, 3, 70, 'O Lieden van de Schrift, waarom ontkennen jullie de Tekenen val Allah, terwijl jullie getuigen?'),
(364, 3, 71, 'O lieden van de Schrift, waarom vermengen jullie de Waarheid met valsheid en verbergen jullie de Waarheid, terwijl Jullie (het toch) weten?'),
(365, 3, 72, 'Een groep van de Lieden van de Schrift zei: "(Doe alsof je) gelooft in wat er aan degenen die geloven aan het begin van de dag geopenbaard is, maar gelooft niet aan het einde ervan. Hopelijk zullen zij terugkeren (naar de ongelovigen)."'),
(366, 3, 73, 'En gelooft slechts hen die jullie godsdienst volgen, zegt: "Voorwaar, de Lieding is de Leiding van Allah en (gelooft niet) dat er aan iemand hetzelfde is gegeven als aan jullie werd gegeven, of dat Zij tegen jullie bij jullie Heer twisten." Zeg: "Voorwaar, de gunst is van de Hand van Allah, die Hij geeft aan wie Hij wil; Allah is Alomvattend, Alwetend.'),
(367, 3, 74, 'Hij kiest voor Zijn Barmhartigheid wie Hij wil en Allah is de Bezitter van de Geweldige Gunst.'),
(368, 3, 75, 'En onder de Lieden van de Schrift is er degene die, als jij hem een schat toevertrouwt, jou deze zal teruggeven; en onder hen is er degene die als jij hem een dinar toevertrouwt, deze niet aan jou zal teruggeven, behalve als jij er op aandingt. Dat komt omdat zij zeggen: "Wij zijn niet verantwoordelijk voor de ongeletterden." En zij vertellen over Allah leugens terwijl zij het weten.'),
(369, 3, 76, 'Welzeker, hij die zijn belofte nakomt en (Allah) vreest: voorwaar, Allah houdt van de Moettaqôen.'),
(370, 3, 77, 'Voorwaar, degenen die hun belofte aan Allah en hun eden voor een geringe prijs verruilden, dat zijn degenen die geen aandeel hebben in het Hiernamaals. En Allah spreekt niet tot hen en beziet hen niet op de Dag der Opstanding. Hij zal lien niet reinigen en voor hen is er een pijnlijke bestraffing.'),
(371, 3, 78, 'En voorwaar, onder hen is er een groep die de Schrift verdraait met hun tongen, opdata jij denkt dat dit bij de Schrift hoort, terwijl het niet bij de Schrift hoort. En rij zeggen: "Het komt van Allah," terwijl het niet van Allah komt. En zij vertellen leugens over Allah, terwijl zij het weten.'),
(372, 3, 79, 'Het past de mens niet dat Allah hem de Schrift en de Wijsheid en het Profeetschap heeft gegeven en dan tot de mens zegt: "Weest aanbidders voor mij in plaats van (voor) Allah". Het is juist: "Weest godsgeleerden (Rabbâniyyin) doordat jullie de Schrift onderwijzen en doordat jullie die bestuderen.'),
(373, 3, 80, 'En Hij beveelt, jullie niet de Engelen en de Profeten als Heren te nemen. Zou Hij jullie opdragen ongelovig te zijn, nadat jullie je overgegeven hebben?'),
(374, 3, 81, 'En (gedenkt) toen Allah een overeenkomst aanging met de Profeten (en zei): "Wat ik jullie ook gegeven heb van de Schrift en de Wijsheid; en er komt daarna een Boodschapper tot jullie ter bevestiging van wat bij jullie is: jullie zullen zeker in hem geloven en hem zeker helpen." (Allah) zei: "Erkennen jullie dit en aanvaarden jullie Mijn verbond?" Zij zeiden: "Wij erkennen het." Hij (Allah) zei: "Getuigt dan en Ik behoor met jullie tot degenen die getuigen."'),
(375, 3, 82, 'En die zich daarna afwenden: dat zijn degenen die zware zonden begaan.'),
(376, 3, 83, 'Zouden zij een andere godsdient dan die van Allah zoeken, terwijl degenen die er in de hemelen en rop de aarde zijn zich gewillig en ongewillig aan Hem hebben overgegeven? En tot Hem worden zij teruggekeerd.'),
(377, 3, 84, 'Zeg: "Wij geloven in Allah en in wat er tot ons neergezonden is en in wat er neergezonden is aan Ibrâhîm en Isma\'îl, Ishaq, Ya\'qoeb en de kinderen van Ya\'qoeb (al Asbath) en in wat er aan Môesa, \'Isa en de Profeten van hun Heer werd gegeven. Wij maken geen onderscheid tussen wie van hen dan ook en wij hebben ons aan Hem overgegeven."'),
(378, 3, 85, 'En wie er een andere godsdient dan de Islam zoekt: het zal niet van hen aanvaard worden en hij behoort in het Hiernamaals tot de verlizers.'),
(379, 3, 86, 'Hoe zou Allah een volk leiden dat ongelovig geworden is nadat zij gelovig waren? En (die) getuigden dat de Boodschapper waar is en nadat de duidelijkheden tot hen gekomen waren? En Allah leidt het onrechtvaardige volk niet.'),
(380, 3, 87, 'Zij zijn degenen wiens beloning is dat op hen de vloek van Allah, de Engelen en de gehele mensheid rust.'),
(381, 3, 88, 'Zij zullen daarin eeuwig levenden zijn en de straf zal niet voor hen worden verlicht en er zal voor hen geen uitstel verleend worden.'),
(382, 3, 89, 'Behalve degenen die daarna berouw hebben en zich beteren. En voorwaar, Allah is Vergevensgezind Meest Barmhartig.'),
(383, 3, 90, 'Voorwaar, degenen die ongelovig zijn geworden na te hebben geloofd en dan hun ongeloof laten toenemen: hun berouw zal nooit aanvaard worden. Zij zijn degenen die dwalenden zijn.'),
(384, 3, 91, 'Voorwaar, degenen die ongelovig zijn en stefven terwijl zijn ongelovig zijn: van geen van hen zal een aarde vol goud aanvaard worden, ook al zou hij zich daarmee willen vrijkopen. Zij zjin degenen voor wie er een pijnlijke bestraffing is en voor hen zullen er geen helpers zijn.'),
(385, 3, 92, 'Jullie zullen de vroomheid niet bereiken totdat jullie van wat jullie liefhebben bijdragen geven. En wat jullie ook van iets bijdragen. voorwaar, Allah weet daarvan.'),
(386, 3, 93, 'Alle voedsel was de Kinderen van Israël toegestaan voordat de Taurât neergzeonden was, behalve wat Israel zichzelf verbood. Zeg: "Brengt de Taurât dan, en leest haar voor als jullie waarachtigen zijn."'),
(387, 3, 94, 'Degenen die dan daarna de leugens over Allah verzinnen: zij zijn degenen die onrechtplegers zijn.'),
(388, 3, 95, 'Zeg: "Allah spreekt de Waarheid, volgt dus de godsdienst van Ibrâhîm, Hanîf; en hij behoorde niet tot de veelgodenaanbidders."'),
(389, 3, 96, 'Voorwar, het eerste Huis dat voor de mensheid gestricht werd, is dat in het gezegende Bakka (Mekkah), als leiding voor de werelden.'),
(390, 3, 97, 'Er in zijn duidelijk Tekenen, (zoals) de standplaats van Ibrâhîm, en hij die er in binnengaat is veilig. En Allah heeft voor de mensen de bedevaart verplicht gesteld, (voor hen) die in staat zijn daarheen op weg te gaan. En wie ongerlovig is: Allah heeft geen behoeft aan de werelden.'),
(391, 3, 98, 'Zeg: "O Lieden van de Schrift, waarom geloven jullie niet in de Tekenen van Allah, terwijl Allah getuige is van wat jullie doen?"'),
(392, 3, 99, 'Zeg: "O Lieden van de Schrift, waarom houden jullie wie gelooft op de Weg can Allah tegen, hopend dat hij krom is, terwijl jullie toch getuigen zijn. En Allah is niet onachtzaam met betrekking tot wat jullie doen."'),
(393, 3, 100, 'O jullie die geloven, als jullie een groep volgen van degenen aan wie de Schrift gegeven is: zij zullen jullie tot ongeloof doen terugkeren nadat jullie geloofd hebben.'),
(394, 3, 101, 'En hoe zouden jullie ongelovig kunnen zijn, terwijl de Verzen van Allah aan jullie voorgedragen worden, en Zijn Boodschapper onder jullie is? En hij dit (de godsdienst) van Allah stevig vasthoudt wordt zeker naar een recht Pad geleid.'),
(395, 3, 102, 'O jullie die geloven, vreest Allah vol ware godsvrees voor Hem, en sterft niet anders dan als moslims.'),
(396, 3, 103, 'En houdt jullie allen stevig vast aan het trouw (de godsdienst) van Allah en weest niet verdeeld. Gedenkt de gunst die Allah jullie schonk toen jullie vijanden waren en Hij jullie harten tot elkaar bracht en jullie door Zijn gunst broeders werden, toen jullie op de rand van de afgrond van de Hel waren en Hij jullie ervan redde. Zo heeft Allah Zijn Tekenen voor jullie duidelijk gemaakt. Hopelijk zullen jullie leiding volgen.'),
(397, 3, 104, 'En laat er uit jullie een groep voortkomen die uitnodigt tot het goede en oproept tot deugdelijkheid en (dit) het verwerpelijke verbiedt, en zij zijn degenen die de welslagenden zijn.'),
(398, 3, 105, 'En weest niet als degenen die (onderling) verdeeld zijn en gaan redetwisten nadat de duidelijke Tekenen tot hen gekomen zijn. En zij zijn degenen voor wie er een geweldige bestraffing is.'),
(399, 3, 106, 'Op die Dag zullen er gezichten wit geworden zijn en zullen er gezichten zwart geworden zijn. En wat betreft degenen wiens gezichten zwart geworden zullen zijn, (hen zal gezegd worden:) "Zijn jullie tot ongeloof vervallen nadat jullie gelovig geworden waren? Proeft dan de bestraffing wegens wat jullie plachten niet te geloven."'),
(400, 3, 107, 'En wat betreft degenen wiens gezichten wit zullen zijn: zij zullen in de Barmhartigheid van Allah verkeren en zij zullen daarin eeuwig levenden zijn.'),
(401, 3, 108, 'Dit zjin de Verzen van Allah: Wij lezen ze jou volgens de Waarheid voor en Allah wil geen onrechtvaardigheid voor de werelden.'),
(402, 3, 109, 'En aan Allah behoort alles in de hemelen en op de aarde en alle zaken worden tot Allah teruggekeerd.'),
(403, 3, 110, 'Jullie zijn de beste gemeenschap die uit de mensen is voortgebracht, (zolang) jullie tot het goede oproepen en jullie het verwerpelijke verbieden, en jullie in Allah geloven. En als de Lieden van de Schrift zouden geloven, zou dat beter voor hen zijn, onder hen zijn er gelovigen, maar de meesten vam hen zijn begaan zware zonden.'),
(404, 3, 111, 'Zij zullen jullie geen kwaad berokkenen, behalve ergernis en als zij jullie bevechten zullen zij jullie de rug toekeren, daarna zullen zij niet geholpen worden.'),
(405, 3, 112, 'Zij werden met vernedering geslagen. waar zij zich ook bevonden, tenzij zij een verdrag met Allah en een verdrag met de mensen hadden. En zij keerden terug met de toorn van Allah en zij werden met vernedering geslagen. Dit was omdat zij de Tekenen van Allah verwierpen en de Profeten zonder recht te hebben doodden, dit was omdat zij ongehoorzaam waren en overtraden.'),
(406, 3, 113, 'Niet allen van de Lieden van de Schrift zijn hetzelfde: (er is) een groep onder hen die standvastig is en zij lezen de Verzen van Allah in een gedeelte van de nacht voor, terwijl zij zich neerbuigen (in hun Shalât).'),
(407, 3, 114, 'Zij geloven in Allah en de Laatste Dag en zij roepen op tot het goede en zij verbieden het verwerpelijke en zij haasten zich goede werken te verrichten, zij zijn degenen die tot de rechtschapenen behoren.'),
(408, 3, 115, 'Van al wat zij verrichten van het goede zal niets ooit verworpen worden en Allah is op de hoogte van de Moettaqoen.'),
(409, 3, 116, 'Voorwaar, degenen die ongelovig zijn zullen hun bezittingen en hun kinderen bij Allah op geen enkele manier baten. En zij zijn de bewoners van de Hel, zij zijn daarin eeuwig levenden.'),
(410, 3, 117, 'De gelijkenis van wat zij tijdens dit wereldse leven uitgeven is als die van de wind, die een hevige kou meenam en de oogst trof van een volk dat zichzelf onrecht aandeed en die (oogst) vernielde. En het is niet Allah die hen onrecht aandeed, maar zij deden zichzelf onrecht aan.'),
(411, 3, 118, 'O jullie die geloven, neemt geen boezemvrienden van buiten jullie kring; zij zullen jullie voortdurend schade berokkenen, zij wensen slechts dat jullie lijden. De vijandschap is duidelijk geworden door wat er uit hun monden voorkomt, wat hun harten verbergen is nog erger. Wij hebben de Tekenen voor jullie duidelijk gemaakt, als jullie begrijpen.'),
(412, 3, 119, 'Ah, jullie zijn degenen die van hen houden, maar zij houden niet van jullie en jullie geloven in het gehele Boek. En als zij jullie ontmoeten, zeggen zij \'Wij geloven,\' maar wanneer zij alleen zijn bijten zij de toppen van hun vingers af van woede. Zegt: "Sterft in jullie woede, Allah is op de hoogte van het binnenste van de harten."'),
(413, 3, 120, 'Als jullie het goede overkomt, zijn zij verdrietig; maar als jullie het slechte overkompt, zijn zij daar blij mee. Maar als jullie geduldig zijn en (Allah) vrezen, dan zal hun listigheid jullie geen schade berokkenen. Voorwaar, Allah omvat wat zij doen.'),
(414, 3, 121, '(Gedenk) toen jij (O Moehammad) jouw familie in de ochtend verliet om voor de gelovigen plaatsen voor de slag voor te bereiden. En Allah is de Alhorende, de Alwetende.'),
(415, 3, 122, '(Gedenk) toen twee groepen van jullie naar lafheid neigden, terwijl Allah hun beschermer was. En laat de gelovigen op Allah, vertrouwen.'),
(416, 3, 123, 'En voorzeker, Allah heeft jullie toch op (het slagveld van) Badr geholpen terwijl jullie zwakker waren, vreest daarom Allah, hopelijk zuilen jullie dankbaar zijn.'),
(417, 3, 124, '(Gedenk) toen jij de gelovigen zei: "Is het niet voldoende voor jullie dat jullie Heer jullie helpt met drieduizend neergezonden Engelen?"'),
(418, 3, 125, 'Jawel, als jullie geduldig zijn en (Allah) vrezen en zij komen op jullie afgestormd, dan zal Allah jullie helpen met vijfdduizend welonderscheid Engelen.'),
(419, 3, 126, 'En Allah heeft jullie hiermee alleen maar een verheugende boodschap willen geven en jullie harten daarmee gerust willen stellen, en er is geen hulp dan die van Allah, de Almachtige, de Alwijze.'),
(420, 3, 127, '(Dat is) om een gedeelte van degenen die ongelovig waren te vernietigen, of hen te vemederen. zodat zij onverrichterzake terugkeren.'),
(421, 3, 128, 'Het het is jouw zaak niet aan of Hij hun berouw aanvaardt of Hij hen bestraft, want voorwaar: zij zijn de onrechtplegers.'),
(422, 3, 129, 'En aan Allah behoort alles wat er in de hemelen en op de aarde is, Hij vergeeft wie Hij wil en Hij bestraft wie Hij wil. En Allah is Vergevensgezind, Meest Barmhartig.'),
(423, 3, 130, 'O jullie die geloven, eet niet van de rente met meervoudige verdubbeling en vreest Allah, hopelijk zullen jullie welslagen.'),
(424, 3, 131, 'Vreest de Hel die voor de ongelovigen id gereedgemaakt.'),
(425, 3, 132, 'En gehoorzaamt Allah en de Boodschapper, hopelijk zullen jullie begenadigd worden.'),
(426, 3, 133, 'En haast jullie naar vergeving van jullie Heer en (naar het) Paradijs, dat net zo wijd is als de hemelen en de aarde, gereedgemaakt voor de Moettaqôen.'),
(427, 3, 134, '(Zij zij het) die uitgeven in voorspoed en in tegenspoed, en die de woede inhouden en vergevers van de mensen zijn. En Allah houdt van de weldoeners.'),
(428, 3, 135, 'En degenen die, als zij een zedeloosheid begaan hebben, of zichzelf onrecht aangedaan hebben, daarna Allah gedenken en dan vergeving voor hun zonden vragen, en niemand vergeeft de zonden behalve Allah En zij volharden niet in wat zij deden, terwijl zij (het) weten.'),
(429, 3, 136, 'Zij zijn degenen wiens beloning vergeving van hun Heer is, en Tuinen (het Paradijs) waar onder door de rivieren stromen, zij zijn daarin eeuwig levenden en (het is) de beste deloning voor hen die (goed) doen.'),
(430, 3, 137, 'Er wijn er vóór julile geweest die op de (voor Allah) gebruikelijke wijze behandeld werden: reist dan rond op aarde en ziet hoe het einde was van de loochenaars.'),
(431, 3, 138, 'Dit is een verklaring (deze Koran) voor de mensen en leiding en onderricht voor de Moettaqôen.'),
(432, 3, 139, 'Voelt jullie niet vemederd en treurt niet; en jullie zijn de winnaars. indien jullie (ware) gelovigen zijn.'),
(433, 3, 140, 'Als jullie gewond raken: het is ook zo dat de mensen (vijanden) door een soortgelijke verwonding getroffen zijn. Dit soort dagen laten Wij onder de mensen wisselen, opdat Allah degenen die geloven toetst, en opdat Hij van onder jullie martelaren (lot Zich) neemt. En Allah houdt niet van de onrechtvaardigen.'),
(434, 3, 141, 'En opdat Allah degenen die geloven reinigt en de ongelovigen vemietigt.'),
(435, 3, 142, 'Dachten jullie dat jullie het Paradijs binnen zouden gaan zonder dat Allah degenen die vochten gekend doet worden en de geduldigen gekend doet worden?'),
(436, 3, 143, 'En voorzeker, jullie hadden de dood gewenst, voordat jullie hem (de dood) tegenkwamen en waarlijk, jullie hebben hem gezien en jullie waren getuigen.'),
(437, 3, 144, 'En Moehammad is niet meer dan een Boodschapper, vóór hem zijn de Boodschappers reeds heengegaan. Als hij dan zou sterven of gedood worden: waarom zouden jullie je dan op jullie hielen omdraaien (terugvallen in ongeloof)? En wie zich op zijn hielen zou omdraaien: het schaadt Allah niets. En Allah zal de dankbaren belonen.'),
(438, 3, 145, 'En geen ziel kan sterven, behalve met het verlof Allah, zoals vastgelegd. En wie de beloning van de wereld wil, Wij geven hem ervan; en wie de beloning van het Hiernamaals wil, Wij geven hem ervan. En Wij zullen de dankbaren belonen.'),
(439, 3, 146, 'En hoevelen van de Profeten vochten er niet, vergezeld van vele mensen en zij verloren de moed niet, wanneer zij op de Weg van Allah door rampspoed getroffen werden. En zij verzwakten niet en zij gaven zich niet over en Allah houdt van de geduldigen.'),
(440, 3, 147, 'En hun woorden waren niet anders dan dat zij zeiden: "Onze Heer, vergeef ons onze zonden en onze overdrijvingen met betrekking tot onze zaak en maak ons standvastig en help ons tegen het ongelovige volk."'),
(441, 3, 148, 'En Allah gaf hun (daarom) een beloning in de wereld en een goede beloning in het Hiernamaals. En Allah houdt van de weldoeners.'),
(442, 3, 149, 'O jullie die geloven, wanneer jullie degenen die niet geloven gehoorzamen, zullen zij jullie op jullie hielen doen omkeren (naar het ongeloof) en jullie zullen dan als verliezers omkeren.'),
(443, 3, 150, 'Maar Allah is jullie Beschermer en Hij is de Beste der Helpers.'),
(444, 3, 151, 'Wij zullen angst in de harten van de ongelovingen werpen wegens wat zij naast Allah aan deelgenoten toegekend hebben, waarvoor Hij geen bewijs neergedonden heeft. Hun verblijfplaats is de Hel. En het is de slechste plaats voor de onrechtplegers.'),
(445, 3, 152, 'En voorzekerm Allah heeft Zijn belofte aan jullie vervuld toen jullie met Zijn toestemming jullie vijand gingen doden, totdat jullie versaagden, en met elkaar redetwisten over de opracht en jullie gehoorzaamden (de Profeet) niet nadat hij jullie liet zien de wereld willen en onder jullie zijn er die het Hiernamaals willen. En toen leidde Hij julle van hen (de ongelovigen) af, om jullie vergeven en Allah is de Bezitter van de Gunsten voor de gelovigen.'),
(446, 3, 153, '(Gedenkt) toen jullie omhoog wegvluchtten, zonder een blik op iemand te werpen, en de Boodschapper jullie can achter jullie riep, toen vergold Hij jullie met ramp na ramp, opdat jullie niet treuren over wat voorbij is en niet (treuren) over wat jullie getroffen heeft. En Allah is Alziende over wat jullie doen.'),
(447, 3, 154, 'Toen liet Hij na de ramp een vreedzame slaap over jullie neerdalen waardoor een groep van jullie bevangen werd, en een groep maakte zichzelf bang door op onterechte wijze over Allah te denken, denkend volgens de onwetendheid, zeggend: "Hebben wij over de zaak iets te zeggen?" Zeg (O, Moehammad): "Voorwaar, de zaak behoort geheel aan Allah toe." Zij verbergen in hun zielen wat zij jou niet laten merken. Zij zeggen: "Hadden wij maar over de zaak iets te zeggen, dan zouden wij hier niet gedood worden." Zeg (O Moehammad): "Al waren jullie in jullie huizen gebleven: degenen voor wie de dood bepaald was, zouden naar hun rustplaatsen vertrokken zijn." (Dit is) opdat Allah dat wat in jullie binnensten is op de proef stelt, en opdat Hij reinigt wat in jullie harten is. En Allah is op de hoogte van het bimenste van de harten.'),
(448, 3, 155, 'Voorwaar, degenen van jullie, die omkeerden op de dag dat de mee legers elkaar (bij Oehoed) troffen: voorwaar, het was Satan die hen deed falen vanwege wat zij (aan ongerhoorzaamheid) verricht hadden. En voorzeker, Allah heeft hen zeker vergeven en Allah is Vergevensgezind, Zachtmoedig.'),
(449, 3, 156, 'O jullie die geloven! Weest niet als degenen die ongelovig zijn en (die) over hun broeders wanneer zij over de aarde reizen of in strijd verwikkeld zijn: "Wanneer zij bij ons gebleven waren, zouden zij niet gestorven of gedood zijn." Opdat Allah dat tot (een bron van) wroeging in hun harten maakt. En het is Allah Die doet leven en Die doet sterven. En Allah is ziende over wat jullie doen.'),
(450, 3, 157, 'En als jullie gedood worden op de Weg van Allah, of sterven: vergeving van Allah en (Zijn) Barmhartigheid zijn beter dan wat zij verzamelen.'),
(451, 3, 158, 'En als jullie sterven, of als jullie gedood worden: het is tot Allah dat jullie verzameld worden.'),
(452, 3, 159, 'En het was dankzij de Barmhartigheid van Allah dat jij zacht met hen was en als je streng en hardvochtig was geweest, dan waren zij rondom jou uiteengegaan. Vergeef hen dus (hun fouten) en vraag vergeving voor hen on raadpleeg hen bij de zaak. En wanneer je dan besloten hebt vertrouw dan op Allah. Voorwaar, Allah houdt van degenen die (op Allah) vertrouwen.'),
(453, 3, 160, 'Wanneer Allah jullie helpt is er geen overwinnaar over jullie. En indien Hij jullie in de steek laat, wie is degene die jullie daarna nog kan helpen? En laat daarom de gelovigen op Allah vertrouwen.'),
(454, 3, 161, 'En het past een Profeet niet, dat hij iets achterhoudt (van oorlogsbuit). En wie iets achterhoudt, zed wat hij achterhield op de Dag der Opstanding meenemen. Dan zal iedere ziel vergoed krijgen wat hij verricht heeft en zij zullen niet onrechtvaardig behandeld worden.'),
(455, 3, 162, 'Is dan iemand die het welgevallen van Allah volgt als iemand die de woede van Allah over zich afroept en wiens verblijfplaats in de Hel is? En dat is de slechtste eindbestemming!'),
(456, 3, 163, 'Zij (hebben) verschillende graden bij Allah en Allah is Alziende over wat zij doen.'),
(457, 3, 164, 'Voorzeker, Allah gaf een grote gunst aan de gelovigen, toen Hij tot hen een Boodschapper uit hun midden stuurde. Hij draagt hun Zijn Verzen voor. En hij reinigt hen (de gelovigen) on hij onderwijst hun het Boek (de Koran) en de Wijsheid, terwijl zij daarvoor zeker in duidelijke dwaling verkeerden.'),
(458, 3, 165, 'En toen een ramp jullie troF (bij Oehoed), hoewel jullie (JE vijanden) met om dubbele ramp getroffen hebben (bij Badr), (waarom) zeggen jullie dan: "Waar komt dat vandaan?" Zeg: "Dat komt van jullie zelf." En Allah is Almachtig over alle zaken.'),
(459, 3, 166, 'En wat jullie trof op de dag dat de twee legers elkaar ontmoetten, was met het verlof ven Allah, opdat Hij de gelovigen toetst.'),
(460, 3, 167, 'En opdat Hij degenen die huichelen gekend deed worden, en er werd hun gezegd: "Kom en vecht op de Weg van Allah, of verdedigt (jullie zelf tenminste)." Zij zeiden: "Wanneer wij geweten hadden dat er een gevecht zou zijn, zouden wij zeker jullie gevolgd hebben." Zij waren op die dag dichter hij ongeloof dan bij geloof en zij zeiden met hun lippen wat niet in hun harten was. En Allah weet beter wat zij verbergen.'),
(461, 3, 168, '(Zij zijn) degenen die tegen hun broeders zeiden (terwijl) zij zaten: "Hadden zij maar naar ons geluisterd, dan waren zij niet gedood." Zeg: "Houdt de dood dan van jullie af als jullie waarachtigen zijn."'),
(462, 3, 169, 'En denkt niet van degenen die op de Weg van Allah gedood zijn dat zij gestorven zijn. Zij leven zelfs bij hun Heer, zij worden voorzien."'),
(463, 3, 170, 'Zij verheugen zich met wat Allah hen van Zijn gunsten gaf, an zij verheugen zich over degenen die zich nog niet na hen (bij hen) gavoegd hebben (en) er is voor hen geen angst en zij treuren niet.'),
(464, 3, 171, 'Zij verheugen zich over de genieting van Allah en (Zijn) gunst. En voorwaar, Allah verwaarloost de beloning van de gelovigen niet.'),
(465, 3, 172, 'Degenen die aan de oproep van Allah en de Boodschapper nadat zij gewond waren (geraakt) gehoor gaven: voor degenen onder hen die good doen en (Allah) vrezen is er een geweldige beloning.'),
(466, 3, 173, 'Degenen tegen wie de mensen zeggen: "Voorwaar, de mensen hebben zich tegen jullie verzameld hen dus." Hun geloof werd erdoor versterkt en zij zeiden: "Allah is ons genoeg en Hij is de beste Beschermer."'),
(467, 3, 174, 'En zij keerden terug (van de oorlog) met de genieting van Allah en (Zijn) Gunst: het kwaad heeft hen niet aangeraakt, want zij volgden het welgevallen van Allah en Allah is de Bezitter van de Geweldige Gunst.'),
(468, 3, 175, 'Voorwaar, het was slechts de Satan en zijn volgelingen die jullie bang maakten, weest daarom niet bang voor Mhen, weest bang voor Mij, indien jullie (ware) gelovigen zijn.'),
(469, 3, 176, 'En laat je niet treurig maken (O Moehammed) door degenen die zich naar het ongeloof haasten. Voorwaar, zij zullen Allah niet schaden. Allah wil dat Hij hen geen aandeel in het Hiernamaals geeft en voor hen is er een geweldige bestraffing.'),
(470, 3, 177, 'Voorwaar, degenen die het geloof voor ongeloof verruild hebben. zij brengen Allah in niets schade tot en voor hen is er een pijnlijke bestraffing.'),
(471, 3, 178, 'Laat degenen die ongelovig zijn niet denken dat Ons uitstel geven beter voor hen is: Wij geven hun slechts uitstel opdat hun zonden zullen toenemen en voor hen is er een vemederende bestraffing.'),
(472, 3, 179, 'Allah zal de gelovigen niet in de toestand laten waarin jullie verkeren totdat Hij het slechte van het goede scheidt. En Allah brengt jullie niet op de hoogte van het onwaarneembare, maar Allah kiest uit Zijn Boodschappers wie Hij wil Gelooft dus in Allah en Zijn Boodschappers. En indien jullie geloven en (Allah) vrezen hebben, den is er voor jullie een geweldige beloning'),
(473, 3, 180, 'En laat degenen die gierig zijn (en achterhouden van) wat Allah hun gegeven heeft van Zijn gunst, niet denken dat het good voor hen is. Welnee, het is slecht voor hen, (het onderwerp van) hun gierigheid zal hen op de Dag der Opstanding om hun nek gehangen worden. En aan Allah behoort de erfenis van de hemelen en de aarde. En Allah is Alwetend over wat jullie doen.'),
(474, 3, 181, 'Voorzeker, Allah heeft de uitspraken gehoord van degenen die zeiden: "Voorwaar, Allah is arm en wij zijn rijk." Wij zullen opschrijven wat zij zeiden en ook het zonder recht doden van de Profeten en Wi zullen (tegen hen) zeggen: "Proeft de verbrandende bestraffing."'),
(475, 3, 182, 'Dat is vanwege hetgeen jullie handen deden en voorwaar, Allah is niet onrechtvaardig tegenover de dienaren.'),
(476, 3, 183, '(Zij zijn) degenen die zeiden: "Voorwaar, Allah heeft ons bevolen geen Boodschapper te geloven, totdat hij ons een offer liet zien dat door de Hel verteerd werd." Zeg (O Moehammad): "Er kwamen Boodschappers vóór mij tot jullie met de duidelijke (Tekenen) en met wat jullie zeiden. Waarom hebben jullie hen dan gedood, als jullie waarachtigen zijn?"'),
(477, 3, 184, 'En als zij jou loochenen: waarlijk geloochend werden de Boodschappers vóór jou, (die) met de duidelijke (Tekenen) en de Zoeboer en de verlichte Boeken kwamen.'),
(478, 3, 185, 'Iedere ziet zal de dood proeven, en voorwaar: pas op de Dag der Opstanding zullen jullie je beloning volledig ontvangen, en slechts degene die van de Het weggehouden wordt en het Paradijs binnengeleid wordt heeft waarlijk succes en het wereldse leven is slechts een misleidende genieting.'),
(479, 3, 186, 'Jullie zullen zeker op de proef gesteld worden in jullie eigendommen en in jullie zelf en jullie zullen zeker van degenen die de Schrift vóór jullie gegeven was en degenen die deelgenoten (aan Allah) toekenden veel pijnlijks horen. En als jullie geduldig zijn en (Allah) vrezen: voorwaar, dat behoort tot de aanbevolen daden.'),
(480, 3, 187, 'En (gedenkt) toen Allah een verbond met degenen die de Schrift gegeven waren sloot: "Opdat jullie hot (de Schrift) aan de mensen duidelijk zouden maken en opdat jullie het niet zouden verbergen." Toen wierpen zij het achter hum ruggen weg en zij ruilden het in voor een geringe prijs. En het was een slechte ruil die zij maakten.'),
(481, 3, 188, 'En denkt niet dat degenen die blij zijn met wat zij gedaan hebben en die er van houden geprezen te worden voor wat zij niet gedaan hebben; denkt niet dat zij aan de bestraffing kunnen ontsnappen. En voor ben is er een pijnlijke bestraffing.'),
(482, 3, 189, 'En aan Allah behoort het Koninkrijk van de hemelen en de aarde toe en Allah is Almachtig over alle dingen.'),
(483, 3, 190, 'Voorwaar, in de schepping van de hemelen en de aarde en in hot afwisselen van de nacht en de dag zijn zeker Tekenen voor bezitters van begrip.'),
(484, 3, 191, 'Degenen die Allah gedenken terwijl zij staan en zitten on op hun zij liggen en nadenken over de schepping van de hemelen en de aarde, (zeggend:) "Onze Heer, U heeft dit (alles) niet voor niets geschapen, glorie zij U, bescherm ons dus tegen de bestraffing van de Hel.'),
(485, 3, 192, 'Onze Heer, voorwaar, U bent degene die iemand de Hel binnenleidt, U heeft hem dan waarlijk vernederd. En voor de onrechtvaardigen zullen er geen helpers zijn.'),
(486, 3, 193, 'Onze Heer, voorwaar, wij hebben een oproeper gehoord die oproept tot geloof: \'Gelooft in jullie Heer,\' dus geloven wij. Onze Heer, vergeef ons onze zonden en wis onze fouten uit en neem ons leven met (dat van) de vromen.'),
(487, 3, 194, 'Onze Heer, schenk ons wat U aan Uw Boodschappers belooft hebt en verneder ons niet op de Dag der Opstanding. Voorwaar, U verbreekt de belofte niet."'),
(488, 3, 195, 'En hun Heer heeft hun (smeekbede) verhoort, (zeggend:) "Voorwaar, Ik doe het werk van de werkenden van jullie niet verloren gaan, of het nu man of vrouw is, jullie komen uit elkaar voort. Zij die uitgeweken zijn en uit hun huizen verdreven werden, en die leden op Mijn Weg, en zij (die) doodden en gedood werden: Ik zal hun fouten zeker uitwissen en hen in de Tuinen (het Paradijs) binnenleiden, waar onder door de rivieren stromen, als een beloning van bij Allah. En Allah, bij Hem is de goede beloning."'),
(489, 3, 196, 'Laat je niet verleiden door het (genietend) rondgaan van degenen die ongelovig zijn in het land.'),
(490, 3, 197, '(Het zijn slechts) kleine genietingen, en vervolgens is hun verblijfplaats de Hel, dat is de slechtste verblijfplaats.'),
(491, 3, 198, 'Maar degenen die hun Heer vrezen, voor hen zijn er de Tuinen (het Paradijs) waar onder door de rivieren stromen, zij zijn daarin eeuwig levenden, een ontvangst can Allah. En dat wat van Allah komt is beter voor de vromen.'),
(492, 3, 199, 'En voorwaar, er zijn er onder de Lieden van de Schrift die zeker in Allah geloven en in wat aan jullie geopenbaard is en in wat hen geopenbaard is, terwijl zij nederig tegenover Allah zijn, zij ruilen de Verzen van Allah niet in voor een geringr prijs: zij zijn degenen voor wi hun beloning bij hun Heer is. Voorwaar, Allah is snel met de afrekening.'),
(493, 3, 200, 'O jullie die geloven, weest geduldig, en weest standvastig, sluit de rijen en vreest Allah. Hopelijk zullen jullie welslagen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'O mens, vreest jullie Heer die jullie schiep uit één enkele ziel (en die) daaruit zijn echtgenote schiep en uit hen beiden vele mannen en vrouwen deed voortkomen. En vreest Allah in wiens Naam jullie elkaar (om hulp) vragen en (onderhoudt) de familiebanden. Voorwaar. Allah is de Waker over jullie.'),
(495, 4, 2, 'En geeft de wezen (wanneer zij volwassen worden) hun bezittingen en verruilt het slechte (van jullie niet voor goede (van hen) en eet niet van hun eigendommen (gemengd) niet jullie eigendommen. Voorwaar, dat is een grote zonde.'),
(496, 4, 3, 'En indien jullie vrezen (de vrouwen van) de wezen niet rechtvaardige te behandelen, trouwt dan met de vrouwen (niet de vrouwen van de wezen) die jullie aanstaan, twee, drie of vier. En als jullie vrezen hen niet rechtvaardig (te kunnen) verzorgen, dan één of wat jullie aan slavinnen bezitten. Dat is de beste wijze om niet onrechtvaardig te zijn.'),
(497, 4, 4, 'En geeft de vrouwen hun bruidschatten als een schenking, maar wanneer het hen belieft (van de bruidschat) terug te geven: eet er dan met plezier en welbehagen van.'),
(498, 4, 5, 'Geeft niet degenen (onder jullie hoede) die zwak van geest zijn jullie eigendommen, waarover Allah jullie als toezichthouder heeft aangesteld, maar onderhoudt hen daarvan en kleedt hen daarvan en spreekt tot hen met vriendelijke woorden.'),
(499, 4, 6, 'Onderzoekt (de lichamelijke en geestelijke vorderingen van) de wezen, totdat zij de leeftijd van het huwelijk bereiken en wanneer jullie bemerken dat zij (hiertoe) geschikt zijn, geeft hun dan hun eigendommen terug en eet er niet buitensporig en haastig van, voordat zij groot geworden zijn. Hij (de voogd) die voldoende heeft: laat hem het niet aanraken; en hij die arm is: laat hem er op redelijke wijze van gebruiken. En als jullie hun hun eigendommen teruggeven: laat er dan getuigen bij zijn. Er Allah is genoeg als Toezichthouder.'),
(500, 4, 7, 'Voor de mannen is er een aandeel in wat achtergelaten wordt door de ouders en de verwanten, en voor de vrouwen is er een aandeel in wat achtergelaten wordt door de ouders en de verwanten, of het weinig of veel is: een vastgesteld aandeel.'),
(501, 4, 8, 'En wanneer bij de verdeling verwanten (zonder erfrecht) en wezen en armen aanwezig zijn: voorziet hen ervan en spreekt tot hen met vriendelijke woorden.'),
(502, 4, 9, 'En laat degenen die een zwak nageslacht achterlaten bevreesd zijn en bezorgd zijn. Laat hen Allah vrezen en laat hen met de juiste woorden spreken.'),
(503, 4, 10, 'Voorwaar, degenen die van de eigendommen van de wezen op onrechtmatige wijze eten: voorwaar, zij verteren slechts vuur in hun buiken. En zij zullen een laaiend vuur (de Hel) binnengaan.'),
(504, 4, 11, 'Allah heeft met betrekking tot (de erfenis) aan jullie kinderen voorgeschreven: (een man) een gedeelte gelijk aan twee gedeelten van de vrouw. En als er (alleen) vrouwen zijn, twee of meer, dan is er voor hen twee-derde van wat hij nalaat en als er één (vrouw) is, dan is er voor haar de helft. En voor zijn beide ouders (is er voor) een ieder van hen één-zesde van wat hij nalaat, indien hij kind(-eren) had. En indien hij geen kind(-eren) had, en hij laat aan zijn ouders na: dan is er voor zijn moeder éénderde. En indien hij broeders had: dan is er voor zijn moeder één-zesde: na aftrek van een beschikking (ten gunste van degenen zonder erfrecht) die hij opmaakte of schulden. Jullie ouders of jullie zonen; jullie weten niet wie van hen jullie nader zijn in nut. Als een voorschrift van Allah, voorwaar. Allah is Alwetend, Alwijs.'),
(505, 4, 12, 'En voor jullie (mannen) is de helft van wat jullie vrouwen nalaten indien zij geen kind(-eren) hebben. En indien zij een kind (of meer van hen) hebben, dan is er voor jullie één-vierde van wat zij nalaten; na aftrek van een beschikking (ten gunste van degenen zonder erfrecht) die zij opmaken of schulden. En voor hen (vrouwen) is er één-vierde van wat jullie nalaten indien jullie geen kind(-eren) hebben; maar indien jullie kind(-eren) hebben, dan is er voor hen (vrouwen) één-achtste van wat jullie nalaten, na aftek van een beschikking (ten gunste van degenen zonder erfrecht) die jullie opmaken of schulden. En indien een man waarvan geërfd wordt Kalâlah is, of het is een vrouw (in dezelfde positie) en hij (of zij) heeft een broeder of een zuster, dan is er voor een ieder van hen één-zesde. En indien er nicer van hen zijn: dan delen zij in na aftrek van een beschikking (ten gunste van degenen zonder erfrecht) die opmaken of schulden, zonder benadeling. Als voorschriften van Allah en Allah is Alwetend, Zachtmoedig.'),
(506, 4, 13, 'Dit zijn de door Allah vastgestelde bepalingen en hij die Allah en Zijn Boodschapper gehoorzaamt: Hij (Allah) zal hem het Paradijs binnenleiden, waar onder door de rivieren stromen. Zij zijn eeuwig levenden daarin. En dat is de geweldige overwinning.'),
(507, 4, 14, 'En hij die Allah en Zijn Boodschapper ongehoorzaam is en Zijn bepalingen overtreedt, Hij (Allah) zal hem de Hel binnenleiden. Zij zijn eeuwig levenden daarin. En voor hem is er een vemedenende bestraffing.'),
(508, 4, 15, 'En degene die van jullie vrouwen ontucht pleegt: neemt de getuigenis van vier van jullie ever hen af en wanneer zij (alle vier belastend) getuigen, sluit hen dan in de huizen op totdat de dood hen neemt of Allah voor hen een weg geeft.'),
(509, 4, 16, 'En als twee personen van jullie daaraan schildig zijn: straft hen beiden: en als zij berouwvol zijn en zich beteren, laat hen dan met rust. Voorwaar, Allah is Berouwaanvaardend, Meest Barmhartig.'),
(510, 4, 17, 'Voorwaar, Allah aanvaardt slechts het berouw van degenen die het slechte uit onwetendheid bedrijven en vervolgens snel berouwvol zijn. Zij zijn degenen wan wie Allah het berouw aanvaardt en Allah is Alwetend, Alwijs.'),
(511, 4, 18, 'En er is geen (aanvaaring van het) berouw voor degenen die het slechte bedrijven totdat een van hen de dood nabij is, (en dan) zegt: "Voorwaar, nu heb ik berouw." En ook niet voor degenen die ongelovig sterven. Zij zijn degenen voor wie Wij een pijnlijke bestraffing voorbereid hebben.'),
(512, 4, 19, 'O jullie die geloven, het is jullie niet toegestaan vrouwen tegen hun wil te erven, noch te verhinderen om wat jullie aan hen gegeven hebben mee te nemen, behalve als zij duidelijk ontucht pleegden. En behandelt hen volgens de voorschriften. En wanneer jullie een afkeer van hen hebben, dan kan het zijn dat jullie een afkeer hebben van iets, terwijl Allah daarin veel goeds gelegd heeft.'),
(513, 4, 20, 'En als jullie een vrouw in de plaats van een andere (vrouw) willen nemen, terwijl jullie één can hen een schat gegeven hadden, neemt daar niets van terug: zouden jullie het door laster en duidelijke zonde terugnemen?'),
(514, 4, 21, 'En hoe zouden jullie ervan (kunnen) terugnemen, terwijl jullie al (als man on vrouw) tot elkaar gekomen zijn en "met jullie een plechtige overeenkomst gesloten hebben?'),
(515, 4, 22, 'En huwt niet van de vrouwen die jullie vaders huwden, tenzij dat al gobeurd is (voor deze openbaring). Voorwaar, het was een zedeloosheid en toomafroepend (gedrag) en een slechte weg.'),
(516, 4, 23, 'Voor jullie verboden (om te trouwen zijn): jullie moeders en jullie dochters en jullie zusters, en jullie vaders zusters en jullie moeders zusters en dochters van jullie broeders en dochters van jullie zusters en de zoogmoeders van jullie en jullie zusters van (dezelfde) zoogmoeder en de moeders van jullie vrouwen en jullie stiefdochters die onder jullie voogdij stun van vrouwen (de moeders van de stiefdochters) waarmee jullie geslachtsgemeenschap hadden. Wanneer jullie nog geen geslachtsgemeenschap met hen hadden is er geen overtreding (om met hen te trouwen), en de (voomalige) vrouwen van jullie zonen die uit jullie voortkomen en het huwen van twee zusters gezamenlijk, behalve als het reeds (voor de openbaring) gebeurd is. Voorwaar, Allah is Vergevensgezind, Meest Barmhatig.'),
(517, 4, 24, 'En (ook verboden zijn:) de getrouwde vrouwen, behalve de slavinnen onder jullie gezag. Als een beslissing van Allah voor jullie. Het is voor jullie teogestaan wat daar buiten valt. Opdat jullie naar (hen) streven met jullie bezittingen, op eerbare wijze, niet ontuchtige. En voor wat jullie van hen genieten: geeft hun hun bruidschat, als verplichting. En er is geen zonde in wat jullie overeenkomen na de bepaling (van de bruidschat). Voorwaar, Allah is Alwetend, Alwijs.'),
(518, 4, 25, 'En wie niet van jullie in staat is te huwen met vrije gelovige vrouwen: hij kan de gelovige vrouwen onder de slavinnen waar jullie over beschikken trouwen. En Allah kent jullie geloof hot best. Jullie komen uit elkaar voort. Trouwt hen dus met verlof van hun familie en geeft hun hun bruidschatten volgens de voorschriften, zij behoren eerbaar te zijn en niet ontuchtig en (behoren zich) pen vriend te nemen. En indien zij getrouwd zijn en indien zij (dan) overspel plegen: zij krijgen dan de halve bestraffing van de vrije getrouwde vrouw. Dit is voor degenen onder jullie die bang zijn overspel te plegen. En het is beter voor jullie, indien jullie geduld hebben. En Allah is Vergevensgezind, Meest Genadevol.'),
(519, 4, 26, 'Allah wil jullie duidelijkheid verschaffen en jullie leiden op de wijze van degenen die jullie vooraf gingen on jullie berouw aanvaarden, on Allah is Alwetend, Alwijs.'),
(520, 4, 27, 'En Allah wil jullie berouw aanvaarden, terwijl degenen die hun begeerten volgen, (willen) dat jullie je gewelding (van de Waarheid) afwenden.'),
(521, 4, 28, 'Allah wil (jullie lasten) voor jullie verlichten. En de mens was zwak geschapen.'),
(522, 4, 29, 'O jullie die geloven, eet niet van elkaars eigendommen op valse wijze, (eet) slechts door handel met wederzijdse overeenstemming. En doodt elkaar niet. Voorwaar, Allah is voor jullie Meest Barmhartig.'),
(523, 4, 30, 'En wie dat op vijandige en onrechtvaardige wijze doet, zullen Wij in de Het werpen. En dat is voor Allah gemakkelijk.'),
(524, 4, 31, 'Indien jullie grote zonden, die verboden zijn, vermijden zullen Wij jullie fouten uitwissen en zullen Wij jullie naar een eervolle plaats (het Paradijs) leiden.'),
(525, 4, 32, 'En verlangt hetgeen niet waarmee Allah somigen van jullie boven anderen bevoorrecht heeft: voor de mannen is er een aandeel in wat zij gedaan hebben, en voor de vrouwen is er een aandeel in wat zij gedaan hebben. En vraagt Allah om Zijn gunst Voorwaar, Allah i\'s Alwetend over alle zaken.'),
(526, 4, 33, 'En voor ieder hebben Wij gemaakt dat er Erfgenamen zijn voor wat de ouders en de verwanten nalaten. Degenen waarmee wie jullie een eed gezworen hebben: geeft hun hun aandeel. Voorwaar, Allah is Getuige van alle zaken.'),
(527, 4, 34, 'De mannen zijn de toezichthouders over de vrouwen omdat Allah de één boven de andere bevoorrecht heeft en omdat zij van hun eigendommen uitgeven (aan de vrouwen). En de oprechte vrouwen zijn de gehoorzame vrouwen en zij waken (over zichzelf en de eigendommen) in de afwezigheid (van haar man), zoals Allah ook waakt. En wat betreft hen (echtgenotes) wauvan jullie ongehoorzaamheid vrezen: vermaant hen, (als dat niet helpt) negeert hen (in bed) en (als dat niet helpt) slaat hen (licht). Indien zij jullie dan gehoorzamen: zoek den geen voorwendsel (om hen lastig te vallen). Voorwaar, Allah is Verheven, Groots.'),
(528, 4, 35, 'En als jullie een breuk tussen beiden vrezen: stuurt dan een berniddelaar van zijn familie en een bemiddelaar van haar familie, indien zij Een verzoening willen, zal Allah tussen hen beiden een verzoening bewerkstelligen. Voorwaar, Allah is Alwetend, Kennend.'),
(529, 4, 36, 'En aanbidt Allah en kent Hem in niets een deelgenoot toe, en weest goed voor de ouders en de verwanten on de wezen en de behoeftigen en de verwante buren en de niet-verwante buren en de goede vrienden en de reiziger en de slaven waarover jullie beschikken. Voorwaar, Allah houdt niet van trotse hoogmoedigen.'),
(530, 4, 37, '(Zij zijn) degenen die gierig zijn en de mensen gierigheid bevelen en die verbergen wat Allah hen van Zijn gunst gegeven heeft. En voor de ongelovigen hebben Wij een vemederende bestraffing voorbereid.'),
(531, 4, 38, 'En (ook voor) degenen die van bun bezittingen uitgeven om de mensen het te laten zien en die niet in Allah en de Laatste Dag geloven en degenen die de Satan tot vriend nemen, het is een slechte vriend!'),
(532, 4, 39, 'En wat zou het hen schaden indien zij in Allah en de Laatste Dag zouden geloven, en zij uitgeven van hetgeen waar Allah hen mee voorzien heeft. En Allah kent hen.'),
(533, 4, 40, 'Voorwaar, Allah behandelt niemand in de geringste mate onrechtvaardig, an als er iets goeds gedaan wordt, vermeerdert Hij het en geeft Hij van Zijn Zijde een geweldige beloning.'),
(534, 4, 41, 'En hoe dan, indien Wij uit iedere gemeenschap een getuige (een Profeet) naar voren brengen en Wij jou (O Moehammad) als getuige tegen diegenen (van jouw gemeenschap die zondigen) naar voren brengen?'),
(535, 4, 42, 'Op die Dag wensen degenen die ongelovig zijn on de Boodschapper ongehoorzaam zijn, dat zij met aarde gelijk gemaakt waren, maar zij zullen geen feit voor Allah kunnen verbergen.'),
(536, 4, 43, 'O jullie die geloven, nadert niet de shalât terwijl jullie dronken zijn, totdat jullie weten wat jullie zeggen; en (ook) niet onrein (Djoenoeb), behalve degenen die er onderweg doorheen komen, totdat jullie je (ritueel) gereinigd hebben. En in het geval dat jullie nek zijn, of op reis, of jullie van het toilet komen, of jullie de vrouwen aanraken en jullie geen water vinden: doet dan de Tayammoem met schone aarde en wrijft ennee langs jullie gezichten en handen. Voorwaar, Allah is Vergevend, Vergeverisgezind.'),
(537, 4, 44, 'Hebt jij degenen niet gezien aan wie een gedeelte van de Schrift is gegeven? Zij verruillen (de Waarheid) voor de dwaling on willen dat jullie van de Weg afdwalen.'),
(538, 4, 45, 'En Allah kent jullie vijanden beter en Allah is voldoende als een Beschermer en Allah is voldoende als een Helper.'),
(539, 4, 46, 'En onder de Joden, zijn or degenen die woorden (uit de Schrift) van hun juiste plaatsen verplaatsen, en zij zeggen: "Wij hebben gehoord en wij gehoorzamen niet" en (zij zeggen:) "Hoort," zonder dat hot hoorbaar is, en "Râ\'inâ" terwijl zij hun tongen verdraaien en de godsdienst belasteren. Als zij alleen maar gezegd hadden: "Wij horen en wij gehoorzamen," en "Hoor en kijk naar ons," dan zou het beter en passender voor ben geweest zijn, maar Allah heeft hen vanwege hun ongeloof vervloekt en zij geloven niet, behalve een beetje.'),
(540, 4, 47, 'O jullie die de Schrift is gegeven! Gelooft in wat Wij jullie hebben neergezonden, als een bevestiging van wat jullie reeds van vroeger hebben, voordat Wij gezichten verminken en op hun achterkant aanbrengen, of Wij hen vervloeken zoals Wij de (overtredende) deelnemers aan de Sabbath vervloekten. En de beschikking van Allah wordt uitgevoerd.'),
(541, 4, 48, 'Voorwaar, Allah vergeeft niet dat aan Hem deelgenoten toegekend worden, maar Hij vergeeft daarnaast alles, aan wie Hij wil. En wie aan Allah deelgenoten toekent: die heeft waarlijk een geweldige zonde verzonnen.'),
(542, 4, 49, 'Heb jij degenen niet gezien die (trots) aanspraak maken zichzelf te louteren (van zonde)? Welnee, het is Allah die loutert wie Hij wil en zij zullen in het geheel niet onrechtvaardig behandeld worden.'),
(543, 4, 50, 'Ziet hoe zij de leugen tegen Allah verzinnen, on (dat) is op zichzelf genoeg als een duidelijke zonde.'),
(544, 4, 51, 'Heb jij degenen niet gezien die een gedeelte van de Schrift gegeven was? Zij geloven in de Djibt en de Thaghôet en zij zeggen tegen degenen die ongelovig zijn (over zichzelf): "Zij zijn degenen die een betere Weg volgen dan degenen die geloven.'),
(545, 4, 52, 'Zij zijn degenen die Allah heeft vervloekt. En wie door Allah vervloekt is: voor hem vindt je nooit een helper.'),
(546, 4, 53, 'Is er voor hen een aandeel in het Koninkrijk (de goddelijke macht)? Nee, dan zouden zij de mensen (nog) niets geven.'),
(547, 4, 54, 'Of zijn zij jaloers op de mensen vanwege wat Allah hen van Zijn gunst heeft gegeven? Waarlijk, Wij gaven de familie van Ibrâhîm de Schrift en de Wijsheid en Wij gaven hem een geweldig koninkrijk.'),
(548, 4, 55, 'En onder hen (de jaloerse mensen) waren er die erin geloofden en onder hen waren er die (mensen van het geloof) afhielden. En de Hel is voldoende als laaiend vuur.'),
(549, 4, 56, 'Voorwaar, degenen die niet in Onze Tekenen geloven zullen Wij in het vuur (de Hel) leiden: telkens wanneer hun huiden gebraden zijn, zullen Wij die door andere huiden vervangen, opdat zij de bestraffing proeven. Voorwaar, Allah is Almachtig, Alwijs.'),
(550, 4, 57, 'En degenen die geloven en goede werken verrichten zullen Wij in de Tuinen (het Paradijs) binnenleiden, waar onder door de rivieren stromen, zij zijn eeuwig levenden daarin. Voor hen zijn daarin reine echtgenotes. En Wij zullen hen in de beschuttende schaduw leiden.'),
(551, 4, 58, 'Voorwaar, Allah gebiedt jullie de toevertrouwde (zaken) aan haar eigenaren te geven, en wanneer jullie tussen de mensen oordelen, oordeel dan met rechtvaardigheid. Voorwaar, Allah onderwijst jullie hiermee op de beste wijze. Voorwaar, Allah is Alhorend. Alziend.'),
(552, 4, 59, 'O jullie gelovigen, gehoorzaamt Allah en gehoorzaamt de Boodschapper en degenen onder jullie die met gezag bekleed zijn. Als jullie over iets van mening verschillen, legt het dan voor aan Allah en de Boodschapper, indien jullie in Allah en de Laatste Dag geloven. Dat is beter en een betere afsluiting.'),
(553, 4, 60, 'Heb jij degenen niet gezien die dachten dat zij geloofden in wat aan jou geopenbaard is en in wat er voor jou geopenbaard is? Zij willen volgens de Thaghôet berechten, hoewel hen toch bevolen was er niet in te geloven. En het is zo dat de Satan hen ver weg wil doen afdwalen.'),
(554, 4, 61, 'En wanneer tot hen gezegd wordt: "Komt naar wat Allah heeft neergezonden en naar de Beodschapper," zie je de huichelaars (de mensen) geheel van jou afhouden.'),
(555, 4, 62, 'En hoe is het dan wanneer een ramp hen trof door wat hun handen gedaan hebben? Dan komen zij naar jou toe, zij zweren bij Allah: "Wij wilden slechts goed doen en een goede verzoening!"'),
(556, 4, 63, 'Zij zijn diegenen waarvan Allah weet zich wat in hun harten bevindt. Wendt je van hun (zonden) af en onderricht hen en spreekt tot hen indrukwekkende woorden.'),
(557, 4, 64, 'En Wij hebben slechts een Boodschapper gestuurd om gehoorzaamd te worden, met het verlof van Allah, en waren zij maar, toen zij onrechtvaardig tegenover zichzelf waren, tot jou gekomen en hadden zij Allah maar om vergeving gevraagd, en de Boodschapper had (dan) vergeving voor hen gevraagd. Zij zouden zeker ondervonden hebben dat Allah Berouwaanvaardend, Meest Genadig is.'),
(558, 4, 65, 'Bij jouw Heer, zij geloven niet totdat zij jou laten oordelen over waar zij over van mening verschillen en dan in zichzelf geen weerstand vinden tegen wat jij orrdeelde, en zij aanvaarden (het dan) volledig.'),
(559, 4, 66, 'En indien Wij voor hen voorgeschreven hadden "Doodt elkaar" of "Verlaat jullie huizen", dan hadden slechts weinigen van hen dat gedaan. Maar indien zij werkelijk gedaan hadden wat hen gezegd werd, was dat beter voor hen geweest en (het) had hen steviger gevestigd (in het geloof).'),
(560, 4, 67, 'En Wij zouden hun van Onze Zijde een geweldige beloning gegeven hebben.'),
(561, 4, 68, 'En Wij zouden hen op een recht Pad geleid hetben.'),
(562, 4, 69, 'En wie Allah en de Boodschapper gehoorzaamt: zij zijn met degenen van de Profeten en de waarachtigen en de martelaren en de oprechten die Allah begunstigd heeft. Zij zijn de beste metge zellen!'),
(563, 4, 70, 'Dat is de gunst van Allah en het is voldoende dat Allah Alwetend is.'),
(564, 4, 71, 'O jullie die geloven, neemt jullie voorzorgsmaatregelen en gaat dus voort in groepen, of gaat gezamenlijk voort.'),
(565, 4, 72, 'En voorwaar, er zijn er onder jullie die zouden dralen. Als jullie door een ramp getroffen worden, zeggen zij: "Waarlijk, Allah heeft mij begunstigd, doordat ik niet aanwezig onder hen was."'),
(566, 4, 73, 'En als jullie een gunst van Allah ten deel valt, dan zegt hij, alsof er tussen jullie en hem geen vriendschap was: "O was ik maar met hen geweest, dan zou ik een geweldige overwinning behaald hebben."'),
(567, 4, 74, 'Laat hem vechten op de Weg van Allah, die het wereldse leven ruilt voor het Hiernamaals. En wie strijdt op de Weg van Allah, of hij gedood wordt of overwint: Wij zullen hem daarna een geweldige beloning geven.'),
(568, 4, 75, 'En waarom zouden jullie niet vechten op de Weg van Allah (ter verdediging) van de onderdrukten vAn de mannen en de vrouwen en de kinderen, degenen die zeggen: "Onze Heer, haal ons weg uit deze stad, waar de mensen onrecht plegen en breng ons van Uw Zijde een beschermer en breng ons van Uw Zijde een helper!"'),
(569, 4, 76, 'Degenen die geloven vechten op de Weg van Allah en degenen die ongelovig zijn vechten op de weg van de Thaghôet vecht dus tegen de helper van de Satan. Voorwaar, de listen van de Satan zijn zwak.'),
(570, 4, 77, 'Zie jij degenen niet tot wie gezegd werd: "Houdt jullie handen af (van de strijd), en onderhoudt de shalât en geeft de zakât"? Toen hen (op het laatst) de strijd bevolen was, was er een groep onder hen die de mensen net zo (veel) als Allah vreesden, of nog meer vreesden. Zij zeiden: "Onze Heer, waarom heeft U ons bevolen te vechten? Had U ons geen kort uitstel kunnen toestaan?" Zeg: "Het genot van de wereld is weinig en het Hiernamaals is beter voor wie (Allah) vreest en jullie worden in het geheel niet onrechtvaardig behandeld."'),
(571, 4, 78, 'Waar jullie je ook bevinden, de dood zal jullie bereiken, al waren jullie in versterkte kastelen. Indien hen (iets) goeds overkomt, zeggen zij: "Dit komt van Allah," maar indien hen (iets) slechts treft, zeggen zij: "Dit komt van jou (Moehammad)." Zeg (O Moehammad): "Alles komt van Allah." Zij zijn degenen van het volk die bijna niet in staat zijn een uitspraak te begrijpen.'),
(572, 4, 79, 'Wat jou van het goede overkomt, het is van Allah; en wat jou van het slechte overkomt, het is van jouzelf En Wij hebben jou als Boodschapper naar de mensen gezonden. En Allah is voldoende als Getuige.'),
(573, 4, 80, 'Wie de Boodschapper gehoorzaamt, bij gehoorzaamt waarlijk Allah. En wie zich afkeert: Wij hebben jou niet als toezichthouder naar ben gezonden.'),
(574, 4, 81, 'Zij zeggen \'gehoorzaamheid," maar wanneer zij bij jou wegtrekken, bekonkelt een groep van ben iets anders dan zij jou zeiden. En Allah schrijft op wat zij bekonkelen. Houd je dus verre van hen en stel je vertrouwen op Allah. En Allah is voldoende als Beschermer.'),
(575, 4, 82, 'Denken zij dan niet na over de Koran? En wanneer (die) niet van bij Allah geweest was, dan zouden zij daarin veel tegestrijdigs vinden.'),
(576, 4, 83, 'En wanneer zij met een zaak van veiligheid of vrees tot ben komen, dan verspreiden zij het. En indien zij het aan de Boodschapper voorgelegd hadden, of aan degenen van hen die met gezag bekleed zijn, dan zouden degenen onder hen die onderzoeksbekwaam zijn er kennis van kunnen nemen. Als het niet vanwege de gunst van Allah over jullie zou zijn, en Zijn Genade, dan zouden jullie de Satan volgen, op weinigen na.'),
(577, 4, 84, 'Strijd dan (O Moehammad) op de Weg van Allah, jij wordt slechts voor jezelf aansprakelijk gesteld. En moedigt de gelovigen (tot de strijd) aan. Hopelijk zal Allah het geweld van degenen die ongelovig zijn teniet doen. En Allah is harder in geweld en harder in bestraffing.'),
(578, 4, 85, 'En wie een goede voorspraak schenkt: hem komt een aandeel daarin toe. En wie een slechte voorspraak geeft: hij draagt de last daarvan. En Allah is Almachtig over alle zaken.'),
(579, 4, 86, 'En wanneerjullie met een groEt begroEt worden, groet dan met een betere dan deze (terug), of beantwoordt hem (op gelijke wijze). Voorwar, Allah stelt de afrekening over alle zaken op.'),
(580, 4, 87, 'Allah, er is geen god dan Hij! Hij zal jullie bijeenbrengen op de Dag der Opstanding, waaraan geen twijfel is. En wiens woorden zijn oprechter dan die van Allah?'),
(581, 4, 88, 'En hoe is het dat er bij jullie twee groepen zijn ten aanzien van de huichelaars, terwijl Allah hen omvergeworpen heeft vanwege wat zij deden? Willen jullie hen leiden die door Allah tot dwaling gebracht zijn? En wie door Allah tot dwaling gebracht is: voor hen vinden jullie nooit een weg.'),
(582, 4, 89, 'Zij verlangen ernaar dat jullie ongelovig worden, zoals zij ongelovig zijn, zodat jullie gelijk zijn. Neemt dus geen vrienden uit hun midden totdat zij uitwijken op de Weg van Allah. En als zij zich afkeren (van de Islam), grijpt hen dan en doodt hen waar jullie hen ook vinden. En neemt uit hun midden geen vrienden en geen helpers.'),
(583, 4, 90, 'Behalve degenen die zich aansluiten bij een volk, waarmee jullie een wederzijds verdrag hebben, of (zij) die tot jullie komen met een beklemd gemoed omdat zij tegen jullie zouden moeten strijden. Indien Allah dat gewild had, had Hij hen over jullie kunnen laten heersen, of jullie laten doden. Daarom, als zij zich van jullie afzijdig houden en niet tegen jullie strijden en jullie vrede aanbieden, dan heeft Allah voor jullie geen weg tegen hen geopend (om hen te doden).'),
(584, 4, 91, 'Jullie zullen anderen vinden, die wensen voor jullie veilig te zijn en voor hun volk: iedere keer dat zij weer op de proef gesteld worden, keren zij daarheen (veelgoderij) terug. Indien zij jullie niet met rust laten en (geen) vrede aanbieden en (niet) afhouden: grijpt hen dan en doodt hen waar jullie hen ook aantreffen. En zij zijn degenen ten aanzien van wie Wij jullie een duidelijk bewijk verleend hebben.'),
(585, 4, 92, 'En nooit mag een gelovige een (andere) gelovige te doden, behalve per vergissing. En wie per vergissing een gelovige gedood heeft: dan (is hij verplicht tot) de vrijlating van een gelovige slaaf en (de betaling van) bloedgeld aan zijn familie, tenzij zij het als een gift (beschouwen). En wanneer hij tot een jullie vijandig volk behoort en hij is een gelovige, dan (is hij verplicht tot) de vrijlating van een gelovige slaaf. En wanneer hij tot een volk behoort waarmee jullie een wederzijds verdrag sloten, dan (is hij verplicht tot) betaling van bloedgeld aan zijn (voor) wie niets (te geven) vindt, geldt een vasten van twee (aaneengesloten) maanden als berouw tegenover Allah. En Allah is Alwetend, Alwijs.'),
(586, 4, 93, 'En wie een gelovige opzettelijk doodt: zijn vergelding is de hel, hij is eeuwig levend daarin. En Allah is woedend op hem en Allah vervloekt hem en Hij bereidt voor hem een geweldige bestraffing voor.'),
(587, 4, 94, 'O jullie die geloven, als jullie je op de Weg van Allah rondtrekken (om te strijden), onderzoekt dan (nauwkeurig) en zegt niet tot degene die jullie Salâm geeft (groet): "Jij bent geen gelovige" verlangend naar de vergankelijke genieting van het wereldse leven, het is bij Allah dat veel mogelijkheden tot buit zijn. Zo waren jullie zelf (eens in de toestand van ongelovigen) en toon begunstigde Allah jullie, onderzoekt dus (nauwkeurig). Voorwaar, Allah is Alwetend over wat jullie doen.'),
(588, 4, 95, 'Niet gelijk zijn de (thuis-)zittenden van de gelovigen, behalve gabrekkigen, aan degenen die strijden op de Weg van Allah met hun eigendommen en hun zielen. Allah heeft de strijders met hun eigendommen en zielen een rang bevoorrecht boven de (thuis-)zitters. Aan allen heeft Allah het goede beloofd. En Allah heeft de strijders boven de (thuis,-)zitters bevoorrecht met een geweldige beloning.'),
(589, 4, 96, 'Met rangen van Hem en vergeving en Barmhartigheid, En Allah is Vergevensgezind, Meest Barmhartig.'),
(590, 4, 97, 'Voorwaar, (tot) degenen waarvan de zielen door de Engelen worden meegenomen, en die onrechtvaardig waren, zeggen zij: "In wat voor toestand waren jullie (toen jullie stierven)?" Zij zeggen: "Wij waren de onderdrukten op aarde." Zij (de Engelen) zeggen: "Was de aarde van Allah niet (zo) uitgestrekt dat jullie daarop hadden kunnen uitwijken?" Zij zijn degenen wiens verblijfplaats de Hel is. Het is de slechtste bestemming!'),
(591, 4, 98, 'Behalve de ondaedrukten van de mannen en de vrouwen en de kinderen die niet tot macht in staat zijn, en die geen weg kunnen vinden.'),
(592, 4, 99, 'Zij zijn het van wie Allah hopelijk (de fouten) zal uitwissen. En Allah is Vergevend, Vergevens- gezind.'),
(593, 4, 100, 'En wie op de Weg van Allah op de aarde uitwijkt, vindt vele toevluchtsoorden en overvloed. En wie zijn huis varlaat als een uitwijker naar Allah en Zijn Boodchapper, en de dood treft hem dan: waarlijk, zijn beloning is bij Allah. En Allah is Vergevensgezind, Meest Barmhartig.'),
(594, 4, 101, 'En als jullie op de aarde rondtrekken, dan is het voor jullie geen zonde de shalât te verkortan, indien jullie bang zijn dat de ongelovigen jullie aanvallen. Voorwaar, de ongelovigen zijn voor jullie een duidelijke vijand.'),
(595, 4, 102, 'En als jij bij hen bent (O Moehammad) en de shalât met hen verricht, laat dan een groep van hen met jou (in de shalât) staan, terwijl zij hun wapens dragen en als zij zich in Sadjdah neerbuigen, laat zij dan achter jullie zijn. En laat dan een andere groep komen (dus na de eerste Raka\'ah) die de shalât nog niet verricht heeft en laat lien de shalât met jou verrichten (dus de tweede Raka\'ah) en laat hen hun voorzorgsmaartregelen treffen en hun wapens dragen. De ongelovigen verlangen ernaar dat jullie niet op jullie wapens en goederen letten zodat zij jullie met één beweging wegvagen. Het is geen zonde voor jullie om, indien jullie last hebben van de regen of indien jullie ziek zijn, jullie wapens neer te leggen. En treft jullie voorzorgsmaatregelen. Voorwaar, Allah heeft voor de ongelovigen een vemederende bestraffing voorbereid.'),
(596, 4, 103, 'En als jullie dan de shalât verricht hebben, gedenkt dan Allah, staand en zittend en liggend. En wanneer jullie veilig zijn, verricht dan de shalât. Voorwaar, de shalât is de gelovigen op vaste tijden voorgeschreven.'),
(597, 4, 104, 'En versaagt niet in de vervolging van het (ongelovige) volk. Wanneer jullie pijn lijden dan, voorwaar, zij lijden pijn zoals jullie pijn lijden. En jullie hopen op (dat) van Allah waarop zij niet hopen. En Allah is Alwetend, Alwijs,'),
(598, 4, 105, 'Wij hebben jou liet Boek met de Waarheid neergezonden zodat jij kunt oordelen tussen de mensen, met wat Allah aan jou openbaarde. En wees geen verdediger voor de verraders.'),
(599, 4, 106, 'En vraag om vergeving bij Allah. Voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(600, 4, 107, 'En plait niet voor degenen die aan zichzelf verraad plegen. Voorwaar, Allah houdt niet van wie verraderlijk en zondig is.'),
(601, 4, 108, 'Zij verbergen zich voor de mensen, maar zij (kunnen) zich niet voor Allah verbergen. En Hij is bij hen wanneer zij in het verborgene besluiten woorden (te zeggen) die Hij afkeurt. En Allah omvat alles wat zij doen.'),
(602, 4, 109, 'Zo, zijn jullie degenen die voor hen pleitten tijdens wereldse leven? Maar wie zal voor hen pleiten bij Allah op de Dag der Opstanding, of wie zal een beschermer voor hen zijn?'),
(603, 4, 110, 'En wie slechte werken verricht of zichzelf onrecht aandoet, maar dan om vergeving bij Allah vraagt: hij zal vinden dat Allah Vergevensgezind, Meest Barmhartig is.'),
(604, 4, 111, 'En wie een zonde begaat: voorwaar, hij begaat die tegen zichzelf En Allah is Alwetend, Alwijs.'),
(605, 4, 112, 'En wie een fout of een zonde begaat en die dan toeschrijft aan degene die onschuldig is: voorzeker, hij laadt een lasterlijk verzinsel en een duidelijke zonde op zich.'),
(606, 4, 113, 'En zonder de gunst van Allah voor jou en Zijn Barmhartigheid, zou een groep van hen zeker willen samenzweren om jou te laten dwalen, maar zij deden slechts zichzeft dwalen, en zij kunnen jou geen enkel kwaad doen. En Allah heeft jou het Boek en de Wijsheid gezonden en jou geleerd wat jij niet wist. En de gunst van Allah voor jou is geweldig.'),
(607, 4, 114, 'Er is geen goed in veel van hun heimelijke gesprekken, behalve wie aanmaant tot liefdadigheid of wijsheid of verzoening tussen de mensen, wie dat doet om het welbehageb van Allah to bereiken: Wij zullen hem een geweldige beloning geven.'),
(608, 4, 115, 'En wie de Boodschapper tegenwerkt nadat de Leiding hem duidelijk is geworden en een andere weg volgt dan die van de gelovigen: Wij laten hem ( gaar naar) waarheen hij zich afgekeerd had en Wij zullen hem in de Hel binnenlieden. En dat is de slechtste bestemming!'),
(609, 4, 116, 'Voorwaar, Allah vergeeft niet dat aan Hem deelgenoten worden toegekend, maar Hij vergeeft daarbuiten aan wie Hij wil. En wie deelgenoten aan Allah toekent, hij heeft ver gedwaald.'),
(610, 4, 117, 'Zij aanbidden naast Hem slechts afgodsbeelden en zij aanbidden slechts de opstandige satan.'),
(611, 4, 118, 'Allah vervloekte hem (de satan) en hij (de Satan) zei: "Ik zal van Uw dienaren een bepaald gedeelte nemen.'),
(612, 4, 119, 'En ik zal hen doen dwalen en ik zal hun ijdelheid opwekken en ik zal hen bevelen de oren van het vee te snijden en ik zal hen bevelen de schepping van Allah te veranderen." En wie in plaats van Allah de Satan tot beschermer neemt, die zal waarlijk een duidelijk verlies lijden.'),
(613, 4, 120, 'Hij doet hen beloften en hij wekt hun ijdelheid op en wat de Satan hen belooft is slechts een misleiding,'),
(614, 4, 121, 'Zij zijn degenen wiens verblijfplaats, de Hel is, en zij vinden daaruit geen ontsnapping.'),
(615, 4, 122, 'Degenen die geloven en goede werken verrichten: Wij zullen hen in Tuinen (het Paradijs) binnenleiden, waar onder door de rivieren stromen, zij zijn daarin eeuwig levenden. En de belofte van Allah is waar. En wiens woord is meer waar dan dat van Allah?'),
(616, 4, 123, 'Het gebeurt niet door jullie ijdelheden en (ook) niet door de ijdelheden van de Lieden van de Schrift: wie slacht doet wordt er voor vergolden en hij zal zich buiten Allah geen beschermer en geen helper vinden.'),
(617, 4, 124, 'En wie goed deel, man of vrouw en hij (zij) is gelovig: zij zijn degenen die het Paradijs binnengaan en zij zullen in het geheel niet onrechtvaardig behandeld worden.'),
(618, 4, 125, 'En wie is beter in godsdienst dam degene die zich volledig aan Allah overgeeft en weldoet en de godsdienst van Ibrâhîm, Hanîf volgt. En Allah nam lbrahim als boezemvriend.'),
(619, 4, 126, 'En aan Allah behoort wat in de hemelen is en wat op de aarde is en Allah omvat alle zaken.'),
(620, 4, 127, 'En zij vragen jou om een uitspraak (Fatwa) over de vrouwen. Zeg: "Allah geeft uitspraken over hen en wat jullie in het Boek voorgelezen wordt over de vrouwelijke wezen, aan wie jullie niet de voor hen voorgeschreven rechten geven en (over) hen die jullie wensen te trouwen en over de zwakken onder de kinderen. En dat jullie moeten zorgen voor de rechtvaardigheid voor de wezen." Er is niets wat julli van het goede doen of, voorwaar, Allah weet erven.'),
(621, 4, 128, 'En indien een vrouw van haar echtgenoot slechtheid of afkeer vreest, dan is er geen zonde voor hen indien zij onderling tot een verzoening komen en de verzoening is beter, maar mensen worden gekenmerkt door gierigheid. En indien jullie het goede doen an (Allah) vrezen: voorwaar, Allah is Alwetend over wat jullie doen.'),
(622, 4, 129, 'En jullie zullen nooit in staat zijn rechtvaardig te zijn voor de vrouwen, hoezeer jullie ook willen, geeft niet geheel toe aan jullie neiging (tot één bepaalde vrouw) en laat haar (een andere vrouw) niet in het onzekere verkeren, on als jullie je beteren en (Allah) vrezen: voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(623, 4, 130, 'En indien zij beiden van elkaar gaan scheiden, dan zal Allah beiden van Zijn overvloed schenken er Allah is Alomvattend Alwijs.'),
(624, 4, 131, 'En aan Allah behoort wat in de hemelen en op de aarde is. En voorzeker, Wij hebben degenen die de Schrift vóór jullie gegeven was opgedragen, en ook jullie, om Allah te vrezen. En indien jullie ongelovig zijn, dan voorwaar: aan Allah behoort wat in de hemelen en op de aarde is. En Allah is Behoefteloos, Prijzenswaardig.'),
(625, 4, 132, 'En aan Allah behoort wat in de hemelen en op de aarde is. En Allah is voldoende als Beschermer.'),
(626, 4, 133, 'En indien Hij wilt, vernietigt Hij jullie, O mensen, en vervangt Hij jullie. En Allah heeft de macht om dat te doen.'),
(627, 4, 134, 'Wie een beloning van de wereld wil: het is bij Allah dat de beloning van de wereld en het Hiernamaals is. En Allah is Alhorend, Alziend.'),
(628, 4, 135, 'O jullie die geloven. Weest standvastigen ten aanzien van de gerechtigheid, als getuigen omwille van Allah. Zelfs tegenover jullie zelf of de kinderen en de verwanten, of het nu een rijke of een arme is (waartegen getuigd moet worden), want Allah kent hun belangen beter. Volgt niet de begeerte om niet rechtvaardig te zijn. En indien jullie verdraaien of je afwenden, dan voorwaar: Allah weet wat jullie doen.'),
(629, 4, 136, 'O jullie die geloven! Gelooft in Allah en Zijn Boodschapper en in wat Hij aan Zijn Boodschapper openbaarde en in de Schrift die Hij vroeger openbaarde. En wie ongelovig is aan Allah en Zijn Engelen en Zijn Boeken en Zijn Boodschappers on de Laatste Dag: hij is waarlijk ver weg gedwaald.'),
(630, 4, 137, 'Voorwaar, degenen die geloven en do ongelovig worden en dan geloven en dan (weer) ongelovig worden en dan het ongeloof vemeerderen: Allah vergeeft hen niet en Hij leidt hen niet op een (recht) Pad.'),
(631, 4, 138, 'Waarschuw de huichelaars dat or voor hen een pijnlijke bestraffing is.'),
(632, 4, 139, 'Degenen die de ongelovigen in plaats van de gelovigen als beschermers nemen: is het de eer, die zij bij hen zoeken? En voorwaar, alle eer is bij Allah.'),
(633, 4, 140, 'En Hij heeft jullie al in het Boek (de Koran) geopenbaard, dat als jullie de Verzen van Allah horen, en zij worden verworpen en bespot, dat jullie niet met hen (die dit doen) zitten, totdat zij op een ander gesprek overgaan. Anders zouden jullie als hen zijn. Voorwaar, Allah zal de huichelaars en de ongelovigen allen in de Hel verzamelen.'),
(634, 4, 141, '(Zij zijn) degenen die een afwachtende houding tegenover jullie aannemen on, als jullie een overwinning van Allah ten deel valt, zeggen: "Wij waren toch met jullie?" Maar als die de ongelovigen ten deel valt, zeggen zij: "Hebben wij jullie niet geholpen en hebben wij de gelovigen niet van jullie afgehouden?"\' Dan Allah zal onder hen recht spreken up de Dag der Opstanding. En Allah zal nooit aan de ongelovigen een weg tegen de gelovigen geven.'),
(635, 4, 142, 'Voorwaar, de huichelaars proberen Allah te misleiden, en Hij vargeldt hun (misleading). En wanneer zij in de shalât staan, staan zij or lui bij, om door de mensen gezien te worden. En zij gedenken Allah slechts weinig.'),
(636, 4, 143, 'Zij bewegen zich onzeker heen on weer, niet naar deze (kant) en niet naar die (kant). En wie door Allah tot dwalen gebracht wordt: voor hen zal jij nooit een weg vinden.'),
(637, 4, 144, 'O jullie de geloven! Neemt niet de ongelovigen tot beschermers in plaats van de gelovigen, willen jullie bij Allah een duidelijk bewijs tegen jullie zelf geven?'),
(638, 4, 145, 'Voorwaar, de huichelaars zullen in de laagste verdieping van de Hel zijn: jij zult nooit een helper voor hen vinden.'),
(639, 4, 146, 'Behalve degenen die berouw hebben en zich beteren on zich aan Allah vasthouden en hun godsdienst voor Allah zuiveren: zij zijn degenen bij de gelovigen. En Allah zal de gelovigen een geweldige beloning geven.'),
(640, 4, 147, 'Waarom zal Allah jullie bestraften, indien jullie dankbaar zijn en geloven? En Allah is Waarderend, Alwetend.'),
(641, 4, 148, 'Allah houdt er niet van dat er openlijk slechte woorden worden gesproken, behalve door wie onrecht aangedaan is. En Allah is Alhorend, Alwetend.'),
(642, 4, 149, 'Indien jullie openlijk het goede doen of het verborgen, of (iets) slechts vergeven: voorwaar, Allah is Vergevensgezind, Almachtig.'),
(643, 4, 150, 'Voorwaar, degenen die niet in Allah geloven en Zijn Boodschapper: zij willen onderscheid maken (in het geloven) tussen Allah en Zijn Boodschappers, en zeggen: "Wij geloven in sommigen on verwerpen anderen," En zij willen daartussen (tussen geloof en ongeloof) een weg vinden.'),
(644, 4, 151, 'Zij zijn degenen die waarlijk de ongelovigen zijn: en Wij hebben voor de ongelovigen een vernedmnde bestraffing voorbereid.'),
(645, 4, 152, 'En degenen die in Allah en Zijn Boodschappers geloven en geen onderscheid maken tussen één van hen: dat zijn degenen aan wie Hij hun beloningen zal geven. En Allah is Vergevensgezind, Meest Barmhartig.'),
(646, 4, 153, 'En de Lieden van de Schrift vragen jou om aan hen een Boek uit de Hemel neer te doen denden; en waarlijk, zij vroegen Môesa om een groter (wonder) dan dat, zij zeiden: "Laat ons Allah openlijk aanschouwen." Toen trof de bliksemslag hen vanwege hun onrecht En zij namen het kalf (ter aanbidding) nadat de duidelijke Tekenen tot hen gekomen waren en Wij vergaven lien dat en Wij gaven Môesa een duidelijk bewijs.'),
(647, 4, 154, 'En Wij verhieven (de berg) Thôer boven hen vanwege het verdrag met hen, en Wij zeiden tot hen: "Treedt de poort buigend binnen," en Wij zeiden tot hen: "Overtreedt (de wettelijke regels) van de Sabbat niet." En Wij sloten met hen een plechtig verbond.'),
(648, 4, 155, 'En (Wij vervloekten hen) wegens hun verbreken van het verbond en hun niet geloven in de Tekenen van Allah, en hun zonder recht doden van de Profeten. En (wegens) hun uitspraak "Onze harten worden bedekt," heeft Allah zelfs hun harten vergrendeld vanwege hun ongeloof En zij geloven niet behalve slechts weinigen.'),
(649, 4, 156, 'En (wegens) hun ongeloof en hun uitspraken over Maryam, als geweldige leugens!'),
(650, 4, 157, 'En (wegens) hun uitspraak "Wij hebben de Masîh \'Isa, zoon van Maryam, gedood." Maar zij doodden hem niet an zij kruisigden hem niet, maar iemand die voor hen op hem leek. En voorwaar, degenen die daar van mening over verschillen, twijfelen daar onderling over. Zij hebben daar geen kennis ever, zij volgen slechts vermoedens, en zij zijn er niet van overtuigd dat zij \'Îsa gedood hebben.'),
(651, 4, 158, 'Maar Allah heeft hem juist tot Zich opgeheven. En Allah is Almachtige, Alwijs.'),
(652, 4, 159, 'En er is niemand van de Lieden van de Schrift of hij moet voor zijn dood in hem (de Profeet \'Îsa) geloven en op de Dag der Opstanding zal hij een getuige voor hen zijn.'),
(653, 4, 160, 'En vanwege het onrecht van de Joden, hebben Wij voor hen (bepaalde) goede toegestane (soorten voedsel) verboden en vanwege hun veelvuldig afhouden van de Weg van Allah.'),
(654, 4, 161, 'En (vanwege) hun nemen van rente, hoewel dat hun verboden was en hun onrechtmatig eten van de bezittingen vam de mensen. En Wij hebben voor de ongelovigen onder hen een pijnlijke bestraffing voorbereid.'),
(655, 4, 162, 'Maar degenen onder hen die stevig gegrondvest zijn in kennis en (ook) de gelovigen, geloven in wat aan jou geopenbaard is en wat vóór jou geopenbaard is. Zij die de shalât verrichten; zij die de zakât geven en de gelovigen in Allah en de Laatste Dag: aan hen zullen Wij een geweldige beloning geven.'),
(656, 4, 163, 'Voorwaar, Wij hebben aan jou geopenbaard zoals Wij aan Nôeh en de Profeten na hem opendaarden. En Wij openbanden aan Ibrâhîm en Ismâ\'îl on Ishâq en Ya\'qôeb en de kinderen (van Ya\'qôeb) en \'Isa en Ayyôeb en Yôenoes en Hârôen en Soelaimân, En Wij gaven Dâwôed de Zabôer (Psalmen).'),
(657, 4, 164, 'En (Wij zonden) Boodschappers waarover Wij jou waarlijk reeds verhaald hebben en (Wij zonden) Boodschappers waarover Wij jou niet verhaald hebben. En Allah voerde een gesprek met Môesa.'),
(658, 4, 165, '(Wij zonden) Boodschappers als brengers van verheugende tijdingen en als waarschuwers opdat de mens geen excuus tegenover Allah zou hebben no de Boodschappers. En Allah is Almachtig, Alwijs.'),
(659, 4, 166, '(De ongelovigen getuigen niet over jouw Profeetschap) maar Allah getuigt (wel) over wat Hij aan jou gezonden heeft. Hij zond deze met Zijn Kennis neer. En de Engelen getuigen. En Allah is voldoende als Getuige.'),
(660, 4, 167, 'Voorwaar, degenen die ongelovig zijn en (die de mensen) van de Weg van Allah afhouden zijn waarlijk ver weg gedwaald.'),
(661, 4, 168, 'Voorwaar, degenen die ongelovig zijn en onrechtvaardig zijn: Allah zal hun niet vergeven en zal hen niet op een Pad leiden.'),
(662, 4, 169, 'Behalve het Pad naar de Hel, zij zijn daar eeuwig levenden. En dat is voor Allah gemakkelijk.'),
(663, 4, 170, 'O mensen, waarlijk, de Boodschapper is tot jullie gekomen met de Waarheid van jullie Heer. Gelooft dus, (dat is) beter voor jullie. En indien jullie niet geloven: voorwaar, aan Allah behoort wat er in de hemelen en op de aarde is, En Allah is Alwetend, Alwijs.'),
(664, 4, 171, 'o Lieden van de Schrift, overdrijft niet in jullie godsdienst en zeg niets over Allah dan de Waarheid. Voorwaar, de Masîh \'Isa, zoon van Maryam, is een Boodschapper van Allah en Zijn Woord, dat Hij aan Maryam zond en uit een Geest (Djibrîl) van Hem voortkomend. Gelooft dug in Allah en Zijn Boodschappers en zegt niet (dat Allah) \'drie\' is. Houdt (hiermee) op, dat is beter voor jullie Voorwaar, Allah is de Ene God. Verheven is Hij (boven de bewering dat) Hij een zoon heeft. Hem behoort wat in de hemelen en op de aarde is. En Allah is voldoende als Getuige.'),
(665, 4, 172, 'Hot is de Masih niet te min om een dienaar van Allah te zijn en de Engelen on de (Hem) nabijen (ook niet). Degene die de aanbidding van Hem te min is on arrogant zijn, Hij zal hen allen bij Zich verzamelen.'),
(666, 4, 173, 'Maar wat betreft degenen die geloven en goede daden verrichten: Hij zal hun hun beloningen geven en meer van Zijn gunst geven. Maar degenen die hoogmoedig en arrogant zijn zal Hij dan straffen met een pijnlijke bestraffing en zij zullen zich buiten Allah gaan beschermer en geen helper vinden.'),
(667, 4, 174, 'O mensen, er is voor jullie waarlijk een bewijs van jullie Heer gekomen en Wij hebben een duidelijk Licht (do Koran) over jullie doen neerdalen.'),
(668, 4, 175, 'En wat betreft degenen die in Allah geloven: houdt jullie aan Hem vast. Dan zal Hij hen in (de) Barmbartigheid van Hem en Zijn gunst (het Paradijs) binnenleiden. En Hij leidt hen naar een recht pad.'),
(669, 4, 176, 'Zij vragen jou, zeg (O Moehammad): "Allah geeft jullie een antwoord over de Kalâlah: indien een persoon overlijdt terwijl hij geen kind heeft en hij heeft (wel) een zuster: dan krijgt zij de helft van wat hij achterlaat. En hij erft (alles) in het geval dat zij (zijn zuster) geen kind heeft. En indien er twee (zusters) zijn, krijgen zij twee-derde van wat hij achterlaat. Maar indien er (van de overladen man) broeders en zusters (achterblijven), dan is er voor een man net zoveel als voor twee vrouwen." Allah heeft jullie dit duidelijk gemaakt, opdat jullie niet dwalen. En Allah is Alwetend over alle zaken.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'O jullie die geloven! Komt de beloften na. Toegestaan voor jullie zijn de dieren van het vee, behalve wat aan jullie voorgelezen is. Niet toegestaan is het jagen als jullie in de gewijde staat zijn (tijdens de bedevaart). Voorwaar, Allah oordeelt over wat Hij wil.'),
(671, 5, 2, 'O jullie die geloven! Ontheiligt niet de gewijde Tekenen van Allah en niet de gewijde Maand (van de Haddj) en niet de offerieren en niet de halsomhangsels en niet degenen die onderweg zijn naar het Gewijde Huis (de Ka\'bah) terwijl zij de gunst en het welgevallen van hun Heer zoeken. En wanneer jullie gewijde toestand voltooid is, dan (mogen) jullie jagen. En laat de haat van een volk dat jullie hindert (te gaan) naar de Masdjid al Harâm (de gewijde Moskee te Mekkah) jullie er niet toe brengen overtredingen te begaan, maar ondersteunt elkaar bij het goede en Taqwa. En steunt elkaar niet bij zonde en overtreding. En vreest Allah. Voorwaar, Allah is streng in de bestraffing.'),
(672, 5, 3, 'Verboden voor jullie zijn het kadaver, het bloed en het van de varkens en hetgeen waarover anders (dan de Naam) van Allah is uitgesproken, het gewurgde, het geslagene, het gevallene, het gestokene en dat waar de wilde dieren van gevreten hebben - behalve wat jullie geslachte hebben - on (verboden zijn) wat op de afgodsaltaren geslacht is en wat jullie met pijlen verloten. Dat is een zware zonde. Op deze dag wanhopen degenen die ongelovig zijn aan (de bestrijding van) jullie godsdienst. Vreest hen niet, maar vreest Mij. Vandaag heb Ik jullie godsdienst voor jullie vervolmaakt en heb Ik Mijn gunst voor jullie volledig gemaakt en heb Ik de Islam voor jullie als godsdienst gekozen. En wie door honger gedwongen is, zonder neiging tot zonde: Allah is dan Vergevensgezind, Meest Barmahartig.'),
(673, 5, 4, 'En zij vragen jou wat hun toegestaan is. Zeg: "Jullie toegestaan zijn alle goede (soorten voedsel) en wat jullie krijgen met behulp van roofdieren die jullie leren (vangen) zoals Allah jullie leerde." Eet van wat zij voor jullie gevangen hebben, en spreekt er de Nam van Allah over uit (bij het loslaten van de afgerichte dieren) En vreest Allah. Voorwaar, Allah is snel in het opmaken van de afrekening.'),
(674, 5, 5, 'Op deze dag zijn alle goede (zaken) voor jullie toegestaan gemaakt, en het voedsel (geslachte dieren) van de Lieden van de Schrift is jullie toegestam en jullie voedsel is hun toegestaan. (Toegestaan om mee te trouwen zijn:) de eerbare vrouwen onder de gelovige vrouwen en de eerbare vrouwen onder degenen die de Schrift vóór jullie gegeven is; indien jullie hun hun bruidschatten geven, in eerbaairheid handelend en niet in ontucht en neemt Seen minnaressen. En degene die het geloof verwerpt: zijn werk draagt waarlijk geen vrucht en hij behoort in het Hiernamaals tot de verliezers.'),
(675, 5, 6, 'O jullie die geloven! Wanneer jullie je voorbereiden op de shalât, wast dan jullie gezichten en jullie handen tot de ellebogen en wrijft over jullie hoofden en wast jullie voeten tot de enkels. Indien jullie Djoenoeb zijn, reinigt jullie dan. En indien jullie ziek zijn, of op reis, of als een van jullie van het toilet gekomen is, of jullie hebben jullie vrouwen aangeraakts en jullie vinden dan geen water: verricht dan Tayammoem met schone aarde. En wrijft ermee over jullie gezichten en jullie handen. Allah wil het jullie niet moeilijk maken, maar Hij wil jullie reinigen, zodat Hij Zijn genieting voor jullie vervolmaakt. Hopelijk zullen jullie dankbaar zijn.'),
(676, 5, 7, 'En gedenkt de genieting van Allah voor jullie en Zijn verbond dat Hij met jullie sloot toen jullie zeiden: "Wij hebben gehoord en gehoorzaamd." En vreest Allah. Voorwaar, Allah is Alwetend over wat zich in de harten bevindt.'),
(677, 5, 8, 'O jullie die geloven! Weest standvastig voor Allah als rechtvaardige getuigen. En laat de haat van een volk jullie er niet we brengen niet rechtvaardig te wezen. Weest rechtvaardig, dat is het dichtst bij Taqwa. En vreest Allah. Voorwaar, Allah weet wat jullie doen.'),
(678, 5, 9, 'En Allah beloofde degenen die geloofden en goede werken verrichtten dat er voor hen vergeving en een geweldige beloning is.'),
(679, 5, 10, 'En degenen die ongelovig waren en Onze Tekenen loochenden: zij zijn de bewoners van Djahîm (de Hel).'),
(680, 5, 11, 'O jullie die geloven! Gedenkt de genieting van Allah voor jullie toen een volk hun handen naar jullie wilde uitstrekken en toen (Allah) hun handen van jullie afhield. En vreest Allah. En laat de gelovigen hun vertrouwen op Allah stellen.'),
(681, 5, 12, 'En voorzeker, Allah heeft een verbond met de Kinderen van Israël gesloten. En Wij hebben onder hen twaalf stamhoofden aangesteld en Allah zei: "Voorwaar, Ik ben met jullie, indien jullie de shalât verrichten en de zakât betalen en jullie in Mijn Boodschappers geloven en hen helpen en een goede lening aan Allah verstrekken (bijdragen geven op de Weg van Allah). Dan zal Ik zeker jullie fouten voor jullie uitwissen en Ik zal jullie zeker Tuinen (het Paradijs) binnenleiden waar de rivieren onder door stromen. En wie van jullie hierna ongelovig is: hij dwaalt waarlijk van de rechte Weg."'),
(682, 5, 13, 'Maar omdat zij hun verbond verbraken, hebben Wij hen vervloekt en hebben Wij hun harten hard gemaakt. En zij verschoven de woorden (in de Schrift) van hun plaatsen en zij vergaten een gedeelte van hetgeen waarmee zij mee vermaand waren. En jij (O Moehammad), behalve van enkelen van hen. Vergeef hun en scheld (het) kwijt. Voorwaar, Allah houdt van de weldoeners.'),
(683, 5, 14, 'En van degenen die zeggen: "Voorwaar, wij zijn Christenen," (zeggen Wij:) Wij sloten een verbond met hen. Zij vergaten een gedeelte van hetgeen waar zij mee vermaand waren. Wij hebben daarop vijandschap en haat tussen hen doen ontstaan tot op de Dag der Opstanding. En Allah zal hun duidelijk maken wat zij plachten te doen.'),
(684, 5, 15, 'O Lieden van de Schrift! Waarlijk, Onze Boodschapper is tot jullie gekomen; hij heeft jullie veel duidelijk gemaakt van wat jullie in de Schrift verborgen, en hij heeft jullie veel kwijtgescholden. Waarlijk, er is van Allah een Licht (de Profeet) en een duidelijk Boek (de Koran) tot jullie gekomen.'),
(685, 5, 16, 'Allah leidt hen ermee die Zijn welbehagen zoeken naar wegen van vrede en Hij brengt hen vanuit de duisternissen naar het Licht, met Zijn verlof, en Hij leidt hen naar een recht Pad.'),
(686, 5, 17, 'Voorzeker, zij waren ongelovig, degenen die zeiden: "Voorwaar, Allah is de Masîh, zoon van Maryam." Zeg (O Moehammad): "Wie zou er enige macht hebben om Allah (af te houden) wanneer Hij zou wensen dat de Masîh, zoon van Maryam, en zijn moeder en wie er ook allemaal op de aarde zijn, vernietigd zou worden?" En aan Allah behoort het Koninkrijk van de hemelen en de aarde en wat er tussen hen is. Hij schept wat Hij wil. En Allah is Almachtig over alle zaken.'),
(687, 5, 18, 'En de Joden en de Christenen zeiden: "Wij zijn zonen van Allah en Zijn geliefden." Zeg (O Moehammed): "Waarom straft Hij jullie dan voor jullie zonden? Maar nee, jullie zijn (gewone) mensen, die Hij schiep, Hij vergeeft wie Hij wil en Hij straft wie Hij wil." En an Allah behoort het Koninkrijk van de hemelen en de aarde en wat er tussen hen is. En tot Hem is de terugkeer.'),
(688, 5, 19, 'O Lieden van de Schrift! Waarlijk, Onze Boodschapper is tot jullie gekomen, en hij heeft jullie na een onderbreking in (de rocks van) Boodschappers duidelijkheid gegeven, opdat jullie niet zouden zeggen: "Er is tot ons geen verkondiger van verheugende tijdingen en een waarschuwer gekomen." Waarlijk, er komt een verkondiger van verheugende tijdingen en een waarschuwer tot jullie. En Allah is Almachtig over alle zaken.'),
(689, 5, 20, 'En (gedenkt) toen Môesa tot zijn volk zei: "O mijn volk, gedenkt de genieting van Allah voorjullie, toen Hij Profeten uit jullie midden voortbracht en jullie koninkrijken gaf en aan jullie gaf wat aan niet één (volk) in de werelden gegeven was.'),
(690, 5, 21, 'O mijn volk, treedt het Heilige Land binnen dat Allah jullie toegewezen heeft en keert het niet jullie ruggen toe, want anders zullen jullie als verliezers terugkeren."'),
(691, 5, 22, 'Zij zeiden: "O Môesa, daarin is een heel sterk volk en wij zullen daarin nooit binnentreden totdat zu van daar vertrekken, en indien zij het verlaten, dan zullen wij (daar) binnentreden."'),
(692, 5, 23, 'Twee mannen van degenen die (Allah) vreesden en aan wie Allah van Zijn gunsten gegeven had, zeiden. "Val hen aan via de poort on waneer jullie dan binnengetreden zijn, dan zullen jullie waarlijk winnaars zijn. En stelt jullie vertrouwen op Allah, indien jullie gelovigen zijn."'),
(693, 5, 24, 'Zij zeiden; "O Môesa, wij zullen dau nooit binnentreden, den zolang zij daarbinnen zijn. Gaat u maar en uw Heer, en vecht met u tweeën, voonvaar, wij zullen hier blijven zitten."'),
(694, 5, 25, 'Hij (Môesa) zei: "O mijn Heer, ik heb alleen macht over mijzelf en mijn broeder, breng een scheiding aan tussen ons en het zwaar zondige volk."'),
(695, 5, 26, 'Hij (Allah) zei: "Het is daarom dat (het land) voor veertig jaren voor hen verboden wordt, (gedurende die periode) zullen zij rusteloos op aarde rondgaan. Treur daarom niet over het zwaar zondiee volk."'),
(696, 5, 27, 'Vertel hen de warheid over het verhaal over de twee zonen van Adam: toen zij een offer brachten, werd het van één van hen (Abel) aanvaard en van de ander (Kaïn) werd het niet aanvaard. Hij (Kaïn) zei: "Ik zal jou doden." Hij (Abel) zei: "Voorwaar, Allah aanvaardt alleen het otter van de Moettaqeen.'),
(697, 5, 28, 'Wanneer jij je hand naar mij uitstrekt om mij te doden: het is niet aan mij om mijn hand naar jou uit te strekken om jou te doden, waarlijk, ik vrees Allah, Heer der Werelden.'),
(698, 5, 29, 'Voorwaar, ik wil dat je mijn zonde en jouw zonde op je neemt en dat he dan tot de bewoners van de Hel wordt. En dat is de vergelding voor de onrechtvaardigen."'),
(699, 5, 30, 'Toen Zette hij zich ertoe aan om zijn broeder te doden en hij doodde hem, en zo werd hij een van de verliezers.'),
(700, 5, 31, 'Toen stuurde Allah een raaf, die over de grond kreste om hem te laten zien hoe hij het lichaam van zijn broeder kon bedekken. Hij zei: "Wee mij! Waarom ben ik zo zwak dat ik niet net zoals de raaf het lichaam van mijn broeder kan bedekken?" En zo werd hij een van hen die wroeging hebben.'),
(701, 5, 32, 'Daarom hebben Wij de Kinderem van Israel voorgeschreven dat voor wie een ziel doodt - niet (als vergelding) voor een ziel of het verderf zaaien op aarde - het is alsof hij alle mensen doodde en dat voor wie iemand laat leven, het is alsof hij alle mensen dead leven. En waarlijk, en Onze Boodschappers kwamen tot hen met duidelijke Tekenen, velen van hen (de Kinderen van Israel) waren daarna overtreders op de arde.'),
(702, 5, 33, 'Voorwaar, de vergelding van degenen die oorlog voeren tegen Allah en Zijn Boodschapper en (die) naar het zaaien van verderf op aarde streven, is dat zij gedood worden, of gekruisigd worden, of het afhouwen van handen ert voeten aan tegenovergestelde kanten, of dat zij uit het land verbannen worden. Dat is voor hen een vernedering op de wereld en voor hen is er in het Hiernamaals een geweldige bestraffing.'),
(703, 5, 34, 'Behalve voor degenen die berouw tonen, voordat jullie hen in jullie macht krijgen (en bestraffen). En weet dat Allah Vergevensgezind, Meest Barmhhartig is.'),
(704, 5, 35, 'O jullie die geloven! Vreest Allah en zoekt naar een middel om (zo dicht mogelijk) bij Hem (te komen), en streeft op Zijn Weg. Hopelijk zullen jullie welslagen.'),
(705, 5, 36, 'Voorwaar, indien degenen die ongelovig zijn alles op aarde zouden hebben en het gelijke daarvan bij hen zouden hebben om daarmee hun bestraffing op de Dag der Opstanding af te kopen: het zou niet van hen aanvaard worden. En er is voor hen een pijnlijke bestraffing.'),
(706, 5, 37, 'Zij zuilen uit de Hel willen ontsnappen, maar er is voor hen geen ontsnapping daaruit, en voor hen is een voortdurende bestraffing.'),
(707, 5, 38, 'En de dief en de dievegge: houwt hun handen af als een vergelding vanwege (de misdaad) die zij begingen, als een bestraffing van Allah. En Allah is Almachtig, Alwijs.'),
(708, 5, 39, 'En wit na zijn onrecht berouw toont en zich betert: Allah aanvaardt het berouw van hem. Voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(709, 5, 40, 'Weet jij dan niet dat het Koninkrijk van de hemelen en de aarde aan Allah toebehoort: Hij straft wie Hij wil en Hij vergeeft wie Hij wil. En Allah is Almachtig over alle zaken.'),
(710, 5, 41, 'O Boodschapper! Laat je niet bedroeven door degenen die met elkaar wedijveren in ongeloof en die behoren tot degenen die met hun monden zeggen: "Wij geloven,"\' maar wiens harten niet geloven. En er zijn er onder de Joden, die gehoor geven aan de leugen: zij geven gehoor aan een ander volk dat niet bij jou gekomen is (en jou niet kent). Zij verdraaien de woorden van hun plaatsen. Zij zeggen: "Indien jullie dat gegeven is, neemt het dan; en als jullie het niet gegeven is, let dan op." En als Allah Zijn beproeving voor iemand wil, dan ben jij niet bij machte dat bij Allah tegen te houden. Zij zijn degenen wiens harten Allah niet wil reinigen. Voor hen is er op de wereld een vernedering en voor hen is er in het Hiernamaals een geweldige bestraffing.'),
(711, 5, 42, 'Zij blijven gehoor geven aan de leugen en zij blijven eten van het verbodene en als zij tot jou komen: oordeel dan tussen hen of wend je van hen af en indien jij je van hen afwendt, dan kunnen zij jou geen enkele schade berokkenen. En iendien jij oordeelt, oordeel dan onder hen met rechtvaardigheid. Voorwaar, Allah houdt van de rechtvaardigen.'),
(712, 5, 43, 'Maar hoe komt het, dat zij zich door jou laten oordelen, terwijl zij de de Taurât bij zich hebben? Daarin is de wet van Allah en zelfs na dat zullen zij zich afkeren! En zij zijn geen gelovigen!'),
(713, 5, 44, 'Voorwaar, Wij hebben de Taurât neergezonden met daarin leiding en licht. De profeten, die zich (aan Allah) overgegeven hadden, oordeelden ermee over de Joden. En de rabbijnen en de schriftgeleerden (oordeelden ook), met behulp van hetgeen hun van de schrift van Allah was toevertrouwd en zij waren daar getuigen van. Vreest daarom niet de mensen maar vreest Mij en verruilt Mijn tekens niet voor een geringe prijs. En wie niet oordeelt met wat Allah geopenbaard heeft: zij zijn de ongelovigen!'),
(714, 5, 45, 'En Wij hebben daarin voor hen voorgeschreven: dat een ziel voor een ziel, een oog voor een oog, een neus voor een neus, een oor voor een oor, een tand voor een tand (vergolden wordt), en bij wonden geldt (ook) vergelding (qishâsh). En wie het kwijtscheldt: het is voor hem een uitwissing (van zijn zonden). En wie niet oordeelt met wat Allah neergezonden heeft: zij zijn het die de onrechtvaardigen zijn!'),
(715, 5, 46, 'En Wij lieten \'Isa, zoon van Maryam, in hun voetstappen volgen, ter bevestiging van wat er van de Taurat (reeds) vóór hem was. En Wij gaven hem de Indjîl met daarin Leiding on Licht en een bevestiging van wat er van de Taurât (reeds) vóór hem was: als een leiding en een onderricht voor de Moettaqôen.'),
(716, 5, 47, 'En laat de lieden van de Indjil oordelen met wat Allah daarin geopenbaard heeft. En wie niet oordeelt met wat Allah neergezonden heeft: zij zijn degenen die zware zondaren zijn.'),
(717, 5, 48, 'En Wij hebben aan jou het Boek (de Koran) met de Waarheid neergezonden, ter bevestiging van de Schrift die eraan vooraf ging en ter bescherming. Oordeel dus onder hen met wat Allah neergezonden heeft. En volg niet hun begeerten om van de Waarheid die tot jou gekomen is af te wijken. Voor een ieder onder jullie hebben Wij een Wet en een manier van leven bepaald. En als Allah gewild had, had Hij jullie (als behorend) tot een godsdienst gemaakt, maar (Hij doet dit niet omdat Hij) jullie op de proef stelt met wat Hij jullie gegeven heeft. Wedijvert dus (op het gebied) van de goede zaken. Tot Allah is de terugkeer van jullie allemaal, en Hij zal jullie hetgeen waarover jullie van mening verschillen vertellen.'),
(718, 5, 49, 'En oordeel (O Moehammad) onder hen met wat Allah neergezonden heeft, en volg niet hun begeerten en hoed je voor hen opdat zij jou niet weglokken van een deel van wat Allah jou neergezonden heeft. En als zij zich afwenden: weet dan dat Allah hen waarlijk wil treffen voor sommige van hun zonden. En voorwaar, veel van de mensen zijn zeket zwaar zondig.'),
(719, 5, 50, 'Zoeken zij dan de wet van de Djahiliyyah (onwetendheid)? En wie is er beter dan Allah in het oordelen over hot overtuigde volk?'),
(720, 5, 51, 'O jullie die geloven! Neemt niet de Joden en de Christenen als beschermers, zij beschermen elkaar. En wie van jullie hen als beschermers neemt: voorwaar, hij behoort tot hen. Voorwaar. Allab leidt het onrechtvaardige volk niet.'),
(721, 5, 52, 'En jij ziet toch dat zij in hun harten een ziekte hebben en zich naar hen (de vijanden van de moslims) toe haasten. Zij zeggen: "Wij vrezen dat een verandering (van het lot) ons zal treffen." Misschien dat Allah (jou) de overwinning zal geven, of een beschikking van Hem (in hun nadeel zal geven), dan zullen zij spijt hebben van wat zij in zichzelf geheim hielden.'),
(722, 5, 53, 'En degenen die geloven zeggen (tegen hun vijanden over de hypocrieten): "Zijn zij degenen die bij Allah zwoeren dat zij echt bij jullie zouden zijn?" Hun werken zullen vruchteloos zijn en zij zullen verliezers worden.'),
(723, 5, 54, 'O jullie die geloven! Wie van jullie zijn godsdienst afvallig is: Allah zal een volk nemen waar Hij van houdt en dat van Hem houdt, dat zachtmoedig is tegenover de gelovigen en streng tegenover de ongelovigen, dat streeft op de Weg van Allah en zij zijn niet bang voor degenen die verwijten maken. Dat is de gunst van Allah, die Hij geeft aan wie Hij wil en Allah is Allesomvattend, Alwetend.'),
(724, 5, 55, 'Voorwaar, jullie helpers zijn Allah en Zijn Boodschapper en degenen die geloven, die de shalât verrichten en de zakât geven, zij buigen zich nederig.'),
(725, 5, 56, 'En wie Allah tot Beschermer neemt en Zijn Boodschapper en degenen die gelovig zijn: voorwaar, de groep (volgelingen) van Allah is overwinnaar.'),
(726, 5, 57, 'O jultie die geloven! Neemt niet degenen die jullie godsdienst tot een (onderwerp van) bespotting en scherts maken van degenen die de Schrift vóór jullie gegeven waren en de ongelovigen als beschcrmcrs. En vreest Allah, indien jullie gelovigen zijn.'),
(727, 5, 58, 'En wanneer jullie oproepen tot dc shalât, nemen zij dat als (onderwerp van) bespotting on scherts. Dat is omdat zij een volk zijn dat niet begrijpt.'),
(728, 5, 59, 'Zeg (O Moehammad): "\'O Lieden van de Schrift! Wreken jullie je alleen maar op ons omdat wij in Allah geloven en (in) wat ons is neergczomden en (in) wat vroeger neergezonden is? En voorwaar, de meesten van jullie zijn zware zondaren."'),
(729, 5, 60, 'Zeg: "Zal ik jullie iets mededelen dat slechter is dan dat, als een vergelding van Allah? Wie door Allah vervloekt is en op wie Allah woedend is, en van wie Hij soinmigen tot apen an varkens gemaakt heeft, en die de Thaghôet dienen: zij zijn degenen die de slechtste plaats hebben en die hct verst afdwalen van de Weg!"'),
(730, 5, 61, 'En wanneer zij tot jullie kwamen, zeiden zij: "Wij geloven." Terwijl zij waarlijk (de Islam) als ongelovigen binnen gingen en zij gingen er waarlik (als ongelovigen) uit. En Allah weet beter wat zij plachten te verbergen.'),
(731, 5, 62, 'En jij ziet velen van hen terwijl zij zich haasten naar de zonde en de overtreding en hun eten van hel verbodene. Het is zeker slecht wat zij plachten te doen!'),
(732, 5, 63, 'Waarom verbieden de geleerden en de godsgdeerden (onder de Joden) hun hun zondige woorden niet en hun eten van het verbodene? Het is zeker slecht wat zij plachten te bedrijven!'),
(733, 5, 64, 'En de Joden zeiden: "De Hand van Allah is gebonden (gierig)." Hun handen zijn gebonden en vervloekt zijn zij vanwege wat zij zeiden! Welnee, Zijn handen zijn wijd uitgestrekt en Hij schenkt hoe Hij wil. (Do Koran,) die door jouw Heer aan jou is neergezonden, vermeerdert zeker bij velen van hen overtreding en ongeloof. En Wij wakkerden vijandschap en haat onder hen aan tot aan de Dag der Opstanding. Iedere keer dat zij een vuur ontsteken, dat tot de oorlog leidt, dooft Allah het. En zij zaaien verderf op de aarde. En Allah houdt niet van de verderfzaaiers.'),
(734, 5, 65, 'En als de Lieden van de Schrift maar hadden geloofd en (Allah) gevreesd, dan hadden Wij hen hun fouten uitgewist en hadden Wij hen gelukzali Tuinen (het Paradijs) binnengeleid.'),
(735, 5, 66, 'En als zij zich aan de Taurât vastgehouden hadden en aan de Indjil en aan wat hun van hun Heer neergezonden was, dan hadden zij gegeten van wat boven ben en onder hun voeten (aan voedsel) was. Onder hen is een gematigde gemeenschap, maar van velen van hen is het slecht wat zij doen.'),
(736, 5, 67, 'O Boodschapper! Verkondig wat jou van jouw Heer neergezonden is. En indien jij dat niet doet, dan heb jij Zijn Boodschap niet verkondigd. En Allah zal jou tegen de mensen beschermen. Voorwaar, Allah leidt het ongelovige volk niet.'),
(737, 5, 68, 'Zeg (O Moehammad): "O Lieden van de Schrift! Jullie zijn niet (op de juiste weg) dat jullie je niet vasthouden aan de TaurAt en de Indjil en aan wat jullie door jullie Heer neergezonden is." (De Koran) die door jouw Heer aan jou is neergezonden, vermeerdert zeker bij velen van hen overtreding en ongeloof. En treur niet om het ongelovige.'),
(738, 5, 69, 'Voorwaar, degenen die (in de Koran) geloven en de Joden en de Sabiërs en de Christenen en wie er in Allah en de Laatste Dag geloven en goede werk verrichten: voor hen zal er geen angst zijn en zij zullen niet treuren.'),
(739, 5, 70, 'En Wij sloten een verbond met de Kinderen van Israel en Wij zonden hen Boodschappers. Iedere keer dat er een Boodschapper tot hen kwam, met wat niet met hun eigen wens overeenkwam, loochenden zij sominigen en doodden zij sommigen.'),
(740, 5, 71, 'En zij dackten dat er geen beproeving zou zijn, zij werden daarop blind en doof. Vervolgens aanvaardde Allah hun berouw, maar velen van hen werden (weer) blind en doof. En Allah zict wat zij doen.'),
(741, 5, 72, 'Voorzeker, zij zijn ongelovig die zeggen: "Allah is de Masîh, zoon van Maryam." Hoewel de Masîh zei: "O Kinderen van Israël, aanbidt Allah, mijn Heer on jullie Heer." Voorwaar, hij die deelgenoten aan Allah toekent: Allah heeft hem waarlijk het Paradijs verboden. En zijn bestemming zal de Hel zijn. En voor de onrechtvaardigen zijn er geen helpers.'),
(742, 5, 73, 'Voorzeker zij zijn ongelovig die zeggen: "Allah is één derde van drie (goden).\'" Want er is geen god dan Allah, de Ene. En indien zij niet ophouden met wat zij zeggen: dan treft zeker een pijnlijke bestraffing degenen van hen die ongelovig zijn.'),
(743, 5, 74, 'Waarom tonen zij geen berouw aan Allah en vragen zij Hem niet am vergeving? En Allah is Vergevensgezind, Meest Barmhartig.'),
(744, 5, 75, 'De Masîh, zoon van Maryam, is niet anders dan een Boodschapper, hij werd waarlijk voorafgegaan door Boodschappers en zijn moeder was een oprechte vrouw. Zij plachten beiden voedsel te eten (net als andere mensen), Zie hoe Wij voor hen de Tekenen duidelijk maakten en zie dan hoe zij (de ongelovigen) zich afwendden.'),
(745, 5, 76, 'Zeg (O Moehammad): "Aanbidden jullie naast Allah wat geen schade voor jullie kan voorkomen en jullie geen voordeel ken brengen?" En Allah, Hij is de Alhorende, de Alwetende.'),
(746, 5, 77, 'Zeg: "O Lieden van de Schrift! Overdrijft niet zonder recht in jullie godsdienst en volgt niet de begeerten van een volk dat vroeger waarlijk dwaalde en velen deed dwalen. En zij dwaalden van het rechte Pad."'),
(747, 5, 78, 'Vervloekt waren degenen die ongelovig waren van de Kinderen van Israel, door de tong(en) van Dâwôed en \'Îsa, de zoon van Maryam. Dit was omdat zij ongehoorzaam waren en (de wet) plachten te overtreden.'),
(748, 5, 79, 'Zij verboden elkaar het verwerpelijke niet dat zij verrichtten. Slecht was het wat zij plachten te doen!'),
(749, 5, 80, 'Jij ziet velen ven hen degenen die ongelovig zijn als beschermers nemen. Slecht is het wat zij voor zichzelf gedaan hebben en Allah is vertoornd op hen. En zij zullen eeuwig levenden zijn in de bestraffing.'),
(750, 5, 81, 'Indien zij maar in Allah geloofd hadden en in de Profeet en in wat aan hem neergezonden was, dan hadden zij hen niet tot beschermers genomen, maar velen van hen zijn zware zondaren.'),
(751, 5, 82, 'Jij zult zeker vinden dat de mensen die het sterkst in vijandschap tegenover de gelovigen zijn, de Joden en de degenen die deelgenoten (aan Allah) toekennen zijn; en jij zult zeker vinden dat zij die het dichtst bij in liefde voor de gelovigen zijn, degenen zijn, die zeggen: "Voorwaar, wij zijn Christenen." Dat is omdat er onder hen priesters en monniken zijn en omdat zij niet hoogmoedig zijn.'),
(752, 5, 83, 'En wannccrzij horen wat zan de Boodschapper geopenbaard is, zie jij hun ogen van de tranen overvloeien, omdat zij de Waarheid herkennen. Zij zeggen: "Onze Heer, wij geloven, schrijf ons dus op bij de getuigen.'),
(753, 5, 84, 'En waarom zouden wij niet geloven in Allah en in wat tot ons is geckomen van de Waarheid en wij verlangen cr hevig naar dat onze Heer ons met het oprechte volk (het Paradijs) binnenleidt."'),
(754, 5, 85, 'Allah beloont hen dan voor wat zij zeiden met Tuinen (het Paradijs) waar onder door de rivieren stromen en zij zijn daarin eeuwig levenden. En dat is de beloning van de weldoeners.'),
(755, 5, 86, 'En degenen die ongelovig zijn en Onze Verzen loochenen: zij zijn degenen die de bewoners van Djahim (de Hel) zijin.'),
(756, 5, 87, 'O jullie die geloven! Maakt de goede zaken niet verboden die Allah jullie heeft toegestaan en overtreedt niet. Voorwaar, Allah houdt niet van de overtreders.'),
(757, 5, 88, 'En eet van de goede, toegestane zaken (Halâl) waarmee Allah jullie voorziet. En vreest Allah, Degene in Wie jullie geloven.'),
(758, 5, 89, 'Allah rekent jullie de onnadenkendheid bij jullie eden niet aan, maar Hij rekent jullie aan wat jullie in jullie eden (bewust) vastleggen. (Bij het verbreken van jullie eden) geldt Kaffârah hiervoor: het voeden van tien armen, zoals jullie gemiddeld jullie families voeden; of het hen kleden; of het vrijlaten van een slaaf. En wie dat niet vindt: het vasten van drie dagen. Dat is de Kaffârah voor (het verbreken van) jullie eden die jullie zwoeren. Maar weest jullie eden getrouw. Zo heeft Allah jullie Zijn Tekenen duidelijk gemaakt, hcpelijk zullen jullie dankbaar zijn!'),
(759, 5, 90, 'O, jullie die geloven! Voorwaar, de wijn en het gokken en de afgodsbeelden en pijlen om te verloten zijn oreinheden die tot het werk van de Satan behoren, vermijdt deze (zaken) dus. Hopefijk zullen jullie welslagen!'),
(760, 5, 91, 'Voorwaar, de Satan wil alleen maar vijandschap en haat onder jullie veroorzaken met behulp van wijn en gokken en door jullie af te houden van het gedenken van Allah en de shalât: houdt ermee op!'),
(761, 5, 92, 'En gehoorzamt Allah en gehoorzaamt de Boodschapper en weest op jullie hoede! En als jullie je afwenden, weet dan: voorwaar, aan Onze Boodshapper is slechts de duidelijke verkondiging.'),
(762, 5, 93, 'Er is voor degenen die geloven en goede werken verrichten geen zonde door wat zij (vroeger) aten, wanneer zij (Allah) vrezen en geloven en goede werken verrichten an weer (Allah) vrezen en geloven en weer (Allah) vrezen en good doen. En Allah houdt van de weldoeners.'),
(763, 5, 94, 'O jullie die geloven! Allah wil jullie alleen maar beproeven met wat ven de jachtbuit, die in het bereik ligt van jullie handen en jullie speren, opdat Allah degene die Hem in het verborgene vreest toetst. En wie daarna overtreedt: voor hem is er een pinlijke bestraffing.'),
(764, 5, 95, 'O jullie die geloven! Doodt geen wild indien jullie in de gewijde staat zijn. En wie van jullie het opzettelijk doodt: dan is de vergelding het slachten van het vergelijkbare aan vee zoals beoordeeld door twee rechtvaardigen van jullie, als een offer naar de Ka\'bah gebracht. Of, als Kaffârah (boetedooning), het voeden van de armen of een darmee overeenkomende (hoeveelheid dagen aan) vasten, opdat hij de straf voor zijn daad proeft. Allah scheldt wat vroeger gebeurd is kwijt; en als hij herhaal: dan zal Allah hem ervoor straffen. En Allah is Almachtig, Heer der Vergelding.'),
(765, 5, 96, 'Toegestaan voor jullie is de jacht op de waterdieren, wat daarvan eetbaar is, als genieting voor jullie en voor de reizigers. En verboden voor jullie is de jacht op dieren van het land zolang jullie in de gewijde toestand zijn. En vreest Allah tot Wie jullie verzameld zullen worden.'),
(766, 5, 97, 'Allah maakte de Ka\'bah, het Gewijde Huis, als een plaats voor de mensen om te staan, en de Heilige Maand en de offerdieren en de halsbanden. Dit opdat jullie weten dat Allah weet wat er in de hemelen en op de aarde is en dat Allah Alwetend is over alle zaken.'),
(767, 5, 98, 'Weet dat Allah streng is in de bestraffing en dat Allah Vergevensgezind, Meest Barmhartig is.'),
(768, 5, 99, 'Op de Boodschapper rust slechts (do plicht tot) de verkondiging, en Allah weet alles wat jullie openlijk doen wat jullie verbergen.'),
(769, 5, 100, 'Zeg (O Moehammad): "Het slechte en het goede zijn niet gelijk," hoewel de veelbeid van het slechte jou zal aantrekken. Vreest dus Allah, O bezitters van begrip, hopelijk zullen jullie welslagen."'),
(770, 5, 101, 'O jullie die geloven! Vraagt niet naar zaken die, indien (ze) jullie uitgclegd worden, jullie zullen verontrusten. En wanneer jullie daarover vragen wanneer de Koran geopenbaard wordt, worden zij jullie uitgelegd. Allah vergaf dat. En Allah is Vergevensgezind, Zachtmoedig.'),
(771, 5, 102, 'Waarlijk, een volk vóór jullie vroeg er naar en werd er daarna ongelovig door.'),
(772, 5, 103, 'Allah heeft (geen bijgeloof) ingesteld zoals Behîrah, Sâ\'ibah Washîlah an Hâm; maar het zijn degenen die ongelovig zijn, die over Allah leugens verzinnen en de meesten van hen begrijpen het niet.'),
(773, 5, 104, 'En toen tot hen werd gczcgd: "Komt tot wat Allah geopenbaard heeft en tot Zijn Boodschapper," zeiden zij: "Ons is voldoende wat wij (aan leafregels) bij onze vaderen vonden." En dat terwijl hun vaderen niets wisten en geen Leiding volgen?'),
(774, 5, 105, 'O jullie die geloven! Aan jullie (de hoede over) jullie zelf. Er kan jullie geen schade berokkend worden door degene die dwaalt, wanneer jullie de Leiding volgen. Tot Allah is de terugkeer van jullie allen en Hij zal jullie vertellen wat jullie plachten le doen.'),
(775, 5, 106, 'O jullie die geloven! Stelt getuigen onder jullie aan wanneer één van jullie de dood nadert en wanneer jullie het testament opmaken; (door) twee rechtvaardigen van onder jullie, of twee broeders van buiten jullie, indien jullie in het land rondreizen en de ramp van de dood jullie treft. Houdt hen na de shalât vast en laat hen dan (als volgt) bij Allah zweren, indien jullie twijfelen: "Wij zullen het (oprecht getuigen) voor geen prijs inruilen, zelfs als (de begunstigde) een verwant is en wij zullen de getuigenis bij Allah niet verbergen, voorwaar, anders zouden wij tot de zondaren behoren."'),
(776, 5, 107, 'Maar indien van deze twee ontdekt wordt dat zij schuldig zijn aan zonde (mijneed laat dan twee anderen in hun plaats staan van degenen die meer recht hebben en meer verwant zijn, en laat hen bij Allah zweren: "Voorwaar, onze getuigenis is meer waarheidsgetrouw dan hun beider getuigenis en wij overtreden niet, voorwaar, anders zouden wij tot de onrechtplegers behoren."'),
(777, 5, 108, 'Dat is passender: dat zij de getuigenis afleggen overeenkomstig haar ware inhoud of (het is passender) dat zij vrezen dat eden na hun eden afgenomen zullen worden. En vreest Allah en luistert (naar Hem). En Allah leidt het zwaar zondige volk niet.'),
(778, 5, 109, 'Op de Dag dat Allah de Boodschappers zal verzamelen en lien dan zal zeggen: "Wat was het antwoord (opjullie Boodschap)?" Zij zeggen: "Wij hebben er geen kennis van, voorwaar, U bent de Kenner van het verborgene."'),
(779, 5, 110, '(Godeaki) toen Allah zei: "O \'Isa, zoon van Maryam, gedenk Mijn gunst aan jou en aan jouw moeder toen ik jou versterkte met de Heilige Geest (Djibril) zodat jij met de mensen sprak toen jij in de wieg was en toen jij volwassen was. En toen Ik jou de Schrift en de Wijsheid en de Taurat en de Indjil onderwees. En toen jij uit klei de gelijkenis van een vogel schiep, met Mijn verlof; en jij blies erin en het werd een vogel, met Mijn verlof; en jij genas de blinden en de leprozen, rnet Mijn verlof. En toen jij de doden deed weerkeren, met Mijn verlof. En toen Ik de Kinderen van Israel van jou afhield toen jij met de duidelijke Bewijzen tot hen kwam." En degenen onder hen die ongelovig waren, zeiden: "Dat is niets dan duidelijke tovenarij."'),
(780, 5, 111, 'En toen Ik de metgezellen (van \'Isa) inspireerde om in Mij le geloven en in Mijn Boodschappers, zij zeiden: "Wij geloven, en getuig dat wij ons (aan U) overgegeven hebben."'),
(781, 5, 112, 'En toen de metgezellen zeiden: "O \'Isa, zoon van Maryam, ben jij in staat om jouw Heer te vragen om tot ons een Ma\'idah (gedekte tafel) te doen neerdalen uit de hemel?" Hij (\'Isa) zei: "Vreest Allah, indien jullie gelovigen zijn."'),
(782, 5, 113, 'Zij zeiden: "Wij willen (alleen maar) dat wij daarvan eten en onze harten tot rust gebracht worden en wij (willen) weten of jij ons waarlijk de Waarheid hebt verteld en dan behoren wij tot de getuigen dmvan."'),
(783, 5, 114, '\'Isa, zoon van Maryam, zei: "O Allah, onze Heer, doe een tafel uit de hemel neerdalen opdat er voor de eerste tot en met de laatste van ons een plechtig feest (\'Id) is, als een Teken van U. En geeft ons voorziening en U bent de Beste van de Voorzieners."'),
(784, 5, 115, 'Allah zei: "Ik zal hem tot jullie doen neerdalen, maar wie van jullie daarna ongelovig is: voorwaar, Ik zal hem straffen met een bestraffing waarmee Ik niemand in de werelden gestraft heb."'),
(785, 5, 116, 'En toen zei Allah: "O \'Isa, zoon van Maryam, heb jij tegen de mensen gezegd: "Neemt mij en mijn moeder tot twee goden naast Allah?" En hij (Isa) zei: "Heilig bent U! Nooit zou ik kunnen zeggen waarop ik geen recht beb. Indien ik dat gezegd had, zou U dat zeker geweten hebben. U weet wat er in mijn ziel is, en ik weet niet wat er in Uw Ziel is. Voorwaar, U bent de Kenner van het verborgene.'),
(786, 5, 117, 'Ik heb hen niet anders gezegd dan U mij heeft geboden te zeggen: \'Dient Allah, mijn Heer en jullie Heer.\' En ik was getuige van hen zolang ik onder hen was, on toen u mij tot U opnam was U de Waker over hen en U bent Getuige van alle zaken.'),
(787, 5, 118, 'Indien U hen straft: voorwaar, zij zijn Uw dienaren. En indien U hen vergeeft: dan voorwaar, U bent de Almachtige, de Alwijze."'),
(788, 5, 119, 'Allah zei: "Dit is een Dag waarop hun waarachtigheid de waarachtigen zal baten; er zijn voor hen Tuinen (het Paradijs) waar onder door de rivieren stromen, zij zijn eeuwig levenden daarin. Allah is welbehaagd met hen en zij zijn welbehaagd met Hem. Dat is de geweldige overwinning."'),
(789, 5, 120, 'Aan Allah behoort het Koninkrijk van de hemelen en de aarde en wat daarin is. En Hij is Almachtig over alle zaken.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Alle lof zij Allah, Die de hemelen en de aarde schiep en Die de duisternissen en het licht maakte. Maar vervolgens kennen degenen die ongelovig aan hun Heer zijn (deelgenoten) toe.'),
(791, 6, 2, 'Hij is Degene Die jullie uit klei schiep en Hij heeft vervolgens een bepaaldc tijd (voor de doden) vastgelegd. En een bepaalde tijd is er bij Hem (voor de opwekking). Toch twijfelen jullie.'),
(792, 6, 3, 'En Hij is Allah in de hemelen en op de aarde. Hij kent jullie geheim en (het) openlijke van jullie en Hij is Alwetend over wat jullie doen.'),
(793, 6, 4, 'En er komt geen Teken van de Tekenen van hun Heer tot hen of zij wenden zich daarvan af.'),
(794, 6, 5, 'Voorzeker. zij loochenden de Waarheid toen die tot hen kwam, maar spoedig zullen berichten hen bereiken over wat zij plachten te bespotten.'),
(795, 6, 6, 'Hebben zij niet gezien hoevelen Wij vernietigden van voor hen, gemeenschappen die Wij op aarde macht gegeven hadden, macht die Wij jullie niet gegeven hebben, en voor wie Wij uit de hemel harde regen gezonden hebben en die Wij rivieren die onder hen door stroomden brachten; maar Wij vernietigden hen vanwege hun zonden en Wij deden na hen andere gemeenschappen opstaan.'),
(796, 6, 7, 'En indien Wij aan jou (O Moehammad) een Boek op perkament hadden doen neerdalen, zodat zij het met hun handen hadden kunnen aanraken, zouden degenen die ongelovig zijn zeker gezegd hebben: "Dit is slechts duidelijke tovenarij."'),
(797, 6, 8, 'En zij (de ongelovigen) zeggen: "Waarom hebben zij geen Engel tot hem (Moehammad) doen neerdalen?" En indien Wij een Engel neergezonden hadden was de zaak zeker besloten en hadden zij geen uitstel meer gekregen.'),
(798, 6, 9, 'En als Wij een Engel gestuurd hadden, dan hadden Wij hem als een man gestuurd en Wij hadden hen laten twijfelen waarover zij reeds twijfelden.'),
(799, 6, 10, 'En de Boodschappers voor jou werden zeker bespot, maar degenen die hen belachelijk maakten werden door hetgeen waarmee zij de spot dreven (met straf) ornsingeld.'),
(800, 6, 11, 'Zeg (O Moehammad): "Reist op de aarde rond en zie dan hoe het einde was van de loochenaars."'),
(801, 6, 12, 'Zeg: "Aan wie behoort wat er in de hemelen en op de aarde is?" Zeg: "Aan Allah. Hij heeft Zichzelf de Barmhartigheid voorgeschreven, Hij zal jullie zeker bijeenbrengen op de Dag der Opstanding, waaraan geen twijfel is. Degenen die zichzelf verloren hebben: zij zijn het die niet geloven."'),
(802, 6, 13, 'En aan Hem behoort alles wat er in de nacht en de dag rust. En Hij is de Alhorende, de Alwetende.'),
(803, 6, 14, 'Zeg (O Moehammad): "Zal ik iemand anders dan Allah, de Schepper van de hemelen en de aarde, als Beschermer nemen? Hij is Degene Die voedt, maar Hij wordt niet gevoed." Zeg: "Voorwaar, ik ben geboden de eerste te zijn die zich (aan Allah) overgeeft." En behoor zeker niet tot de veelgodenaanbidders.'),
(804, 6, 15, 'Zeg: "Voorwaar, ik vrees, als ik mijn Heer ongehoorzaam ben, de bestrafring op de Geweldige Dag."'),
(805, 6, 16, 'Van wie op die Dag (de bestraffing) afgewend wordt, die heeft Hij dan waarlijk begenadigd. En dat is een duidelijke overwinning.'),
(806, 6, 17, 'Indien Allah jou met tegenspoed treft, dan kan niemand die weghalen, behalve Hij; en indien Hij jou met het goede treft: Hij is Almachtig over alle zaken.'),
(807, 6, 18, 'En Hij is de Meest Machtige boven Zijn dienaren en Hij is de Alwijze, de Alwetende.'),
(808, 6, 19, 'Zeg (O Moehammad): "Welke zaak vormt de grootste getuigenis?" Zeg: "Allah is Getuige over mij en jullie en deze Koran is aan mij geopenbaard om ermee jullie te vermanen en (ook) een ieder die erdoor bereikt wordt. Zouden jullie kunnen getuigen dat er naast Allah andere goden zijn?" Zeg: "Nee, (dat) getuig ik niet." Zeg: "Voorwaar, Hij is de Enige God; en voorwaar, ik ben onschuldig aan wat jullie aan deelgenoten toekennen."'),
(809, 6, 20, 'Degenen aan wie Wij de Schrift gegeven hebben, kennen hem (Moehmmad) zoals zij hun zonen kennen; degenen die zichzelf verloren hebben geloven niet.'),
(810, 6, 21, 'Wie is er onrechtvaardiger dan degene die een leugen tegen Allah verzonnen heeft of die Zijri Verzen loochende? Voorwaar, de onrechtplegers slagen niet.'),
(811, 6, 22, 'En op de Dag dat Wij hen allen bijeenbrengen, zullen Wij tegen degenen die deelgenoten toekennen zeggen: "Waar zijn de deelgenoten waarvan jullie (het bestaan) plachten te boweren?'),
(812, 6, 23, 'En dan zal er geen excuus voor hen zijn, dan te zeggen: "Bij Allah, onze Heer, wij waren geen veelgodenaanbidders."'),
(813, 6, 24, 'Zie hoe zij over zichzelf liegen en afgedwaald zijn van wat zij plachten te verzinnen.'),
(814, 6, 25, 'En onder hen zijn or die naar jou luisteren, maar Wij hebben over hun harten sluiers aangebracht zodat zij het (de Koran) niet begrijpen en in hun oren proppen. En indien zij ieder Teken zagen, zouden zij er niet in geloven, zelfs in die mate dat wanneer zij tot jou komen om met jou te redetwisten, degenen die ongelovig zijn zeggen: "Dit zijn slechts fabels van de ouden."'),
(815, 6, 26, 'En zij verbieden het en zij keren zich ervan af, maar zij vernietigen slechts zichzelf en zij beseffen het niet.'),
(816, 6, 27, 'En als jij (hen) kon zien wanneer zij bij de Hel geplaatst worden en zij dan zeggen: "Werden wij maar teruggebracht, dan zouden wij de Tekenen van onze Heer niet loochenen en zouden wij tot de gelovigen behoren."'),
(817, 6, 28, 'Zeker niet, er wordt voor hen zichtbaar wat zij vroeger plachten te verbergen. Als zij teruggebracht zouden worden, zouden zij weer herhalen wat hen verboden was en zij zijn zeker leugenaars.'),
(818, 6, 29, 'En zij zeggen: "Er is niets dan ons huidige leven en wij zullen niet opgewekt worden."'),
(819, 6, 30, 'En als jij (hen) kon zien wanneer zij voor hun Heer gebracht worden: Hij zal zeggen: "Is dit niet de Waarheid? zij zullen zeggen: "Zeker, bij onze Heer." Hij zal zeggen: "Proeft dan de bestraffing vanwege (wat) jullie plachten niet te geloven."'),
(820, 6, 31, 'Waarlijk verloren zijn degenen die de ontmoeting met Allah loochenen, totdat wanneer het Uur plotseling tot hen komt, zij zullen zeggen: "Wee ons voor wat wij in (onze levens) veronachtzaamden," en zij dragen hun zonden op hun ruggen. En weet: het is slecht wat zij dragen!'),
(821, 6, 32, 'En het wereldse leven is niets dan spel en vermaak. En het Huis van het Hiernamaals is beter voor degenen die (Allah) vrezen. Begrijpen jullie dan niet?'),
(822, 6, 33, 'Wij weten inderdaad (O Moehammad) dat jij treurt door degenen die (kwaad) spreken, en voorwaar, zij loochenen jou niet, maar het zijn de Verzen van Allah die de onrechtvaardigen ontkennen.'),
(823, 6, 34, 'En voorzeker, de Boodschappers van voor jou werden inderdaad geloochend en zij waren geduldig met het loochenen en de kwelling, totdat Onze hulp tot hen kwam. En er is niemand die de Woorden van Allah kan veranderen en voorzeker, er zijn al berichten over de Boodschappers tot jou gekomen.'),
(824, 6, 35, 'En als hun afkeer van jou zwaar voor jou is: als jij in staat zou zijn een grot in de aarde of een ladder naar de hemel te maken, zodat jij een Teken tot hen zou kunnen laten komen (doe het dan); en indien Allah het gewild zou hebben, zou Hij hen onder de Leiding verzamelen. Behoor daarom zeker niet tot de onwetenden.'),
(825, 6, 36, 'Voorwaar, alleen degenen die luisteren zullen gehoorzamen. En Allah zal de doden opwekken en vervolgens worden zij tot Hem teruggekeerd.'),
(826, 6, 37, 'En zij zeiden: "Waarom wordt er geen Teken (wonder) van zijn Heer tot hem gezonden?" Zeg (O Moehammad): "Allah is bij machte om een Teken neer te zenden, maar de meesten van hen begrijpen het niet."'),
(827, 6, 38, 'En er is geen levend wezen op aarde an geen vogel die met zijn vleugels vliegt, of het behoort tot gemeenschappen zoals die van jullie. En Wij hebben niets in het Boek veronachtzaamd en zij zullen allen bij hun Heer verzameld worden.'),
(828, 6, 39, 'Endegenen die Onze Tekenen loochenen zijn doof en stom in de duisternis: Allah laat dwalen wie Hij wil en Hij plaatst op de Rechte Weg wie Hij wil.'),
(829, 6, 40, 'Zeg (O Moehammad): "Wat vinden jullie, wanneer de bestraffing van Allah of het Uur tot jullie komt: zouden jullie je dan op iemand anders beroepen dan op Allah, indien jullie waarachtigen zijn?"'),
(830, 6, 41, 'Welnee, op Hem zouden jullie je beroepen en Hij zou hetgeen waarvoor jullie een beroep op Hem deden wegnemen, indien Hij zou willen. En jullie zouden vergeten wat jullie aan deelgenoten toegekend hebben.'),
(831, 6, 42, 'En voorzeker, Wij hebben inderdaad naar gemeenschappen van voot jou (Boodschappers) gestuurd en Wij hebben hen met tegenspoed en rampen getroffen. Hopelijk zullen zij nederig worden (tegenover Allah).'),
(832, 6, 43, 'En waarom zijn zij, toen Onze tegenspoed tot hen kwam, niet nederig geworden? Integendeel, hun harten werden zelfs hard en de Satan deed wat zij plachten te doen schoon schijnen.'),
(833, 6, 44, 'En toen zij vergaten waarmee zij gewaarschuwd waren, openden Wij voor hen vervolgens de poorten naar alle (aantrekkelijkc) zaken; zozeer dat, toen zij blij waren met wat hun geschonken was, Wij hen plotseling bestraften en zij daarop wanhopig werden.'),
(834, 6, 45, 'En vervolgens werd de laatste van het volk dat onrechtvaardig was vernietigd: alle lof zij Allah, Heer der Werelden.'),
(835, 6, 46, 'Zeg (O Moehammad): "Wat vinden jullie indien Allah jullie horen en jullie zien wegnam en jullie harten verzegelde: welke andere god dan Allah zou jullie die teruggeven? "En zie hoe Wij de Tekenen uitleggen en toch wenden zij zich af.'),
(836, 6, 47, 'Zeg: "Wat vinden jullie als de bestraffing van Allah tot jullie komt, plotseting of openlijk? Niemand dan het onrechtvaardige volk wordt vernietigd."'),
(837, 6, 48, 'Wij hebben de Boodschappers slechts als brengers van verheugende tijdingen gestuurd an (als) waarschuwers; dus wie gelooft en oprecht is: voor hem is er geen angst en zij zullen niet treuren.'),
(838, 6, 49, 'Maar degenen die de Tekenen van Ons loochenen: hen treft de bestraffing wegens de zware zonden die zij plachten te begaan.'),
(839, 6, 50, 'Zeg: "Ik zeg jullie niet dat de schatten ven Allah bij mij zijn en niet dat ik het verborgene ken, en ik zeg jullie niet dat ik een Engel ben: ik volg slechts wat aan mij geopenbaard wordt." Zeg: "Zijn de blinden en de zienden gelijk? Denken jullie dan niet na?"'),
(840, 6, 51, 'En waarschuw daarmee degenen dit vrezen dat zij tot hun Heer verzameld zullen worden: er is voor hen naast Hem geen Helper en geen Voorspreker. Hopelijk zullen zij (Allah) vrezen.'),
(841, 6, 52, 'En stuur degenen niet weg die in de ochtend en de avond hun Heer aanroepen en Zijn Aangezicht wensen. Jij bent in niets voor hen aansprakelijk en zij zijn in niets voor jou aansprakelijk, stuur jij hen dan weg: dan behoor jij tot de onrechtvaardigen.'),
(842, 6, 53, 'En zo hebben Wij sommigen van hen door anderen beproefd, opdat zij zouden zeggen: "Zijn zij dan, die Allah boven ons begunstigt?" Is het niet Allah die de dankbaren het beste kent?'),
(843, 6, 54, 'En wanneer degenen die in Onze Tekenen geloven tot jou komen, zeg dan: Salâmoen \'alaikoem (Vrede zij met jullie)." Jullie Heer heeft Zichzelf de Barmhartigheid voorgeschreven. Indien een van jullie uit onwetendheid slecht doet en dan daarna berouw heeft en zich betert: voorwaar, dan is Hij Vergeveinsgezind, Meest Barmhartig.'),
(844, 6, 55, 'En zo leggen Wij de Verzen uit. En opdat de weg van de zondaren duidelijk wordt.'),
(845, 6, 56, 'Zeg: "Voorwaar, ik ben verboden degenen te dienen die jullie buiten Allah aanbidden." Zeg: "Ik zal jullie begeerten niet volgen; ik zou dan dwalen en ik zou niet tot de rechtgeleiden behoren."'),
(846, 6, 57, 'Zeg (O Moehammad): "Voorwaar, ik berust mij op een duidelijk bewijs van mijn Heer en jullie loochenen bet, ik heb (geen macht) over wat jullie willen verhaasten. Voorwaar, het oordeel is alleen aan Allah, Hij verklaart de Waarheid en Hij is de Beste van de Oordelaars."'),
(847, 6, 58, 'Zeg. "Indien dat wat jullie verhaast willen binnen mijn macht lag, was de zaak tussen mij en jullie (al) beoordeeld, maar Allah kent de onrechtvaardigen beter."'),
(848, 6, 59, 'Hij bezit de schatten van het onwaarneembare en niemand kent die, behalve Hij. Hij weet wat er op de aarde is en in de zee; en er valt nog geen blad of Hij weet ervan; en er is geen graankorrel in de duisternissen van de aarde; en niets vers en niets droogs, of het is in een duidelijk Boek.'),
(849, 6, 60, 'Hij is degene die jullie (zielen) in de nacht wegneemt en Hij weet wat jullie gedurendede dag verricht hebben: Hij doet jullie or weer in ontwaken opdat een vastgestelde periode vervuld wordt, daarna is jullie terugkeer tot Hem. Hij zal jullie dan op de hoogte brengen van wat jullie plachten to doen.'),
(850, 6, 61, 'En Hij is de Krachtige, boven Zijn dienaren en hij stelt Wakers (Engelen) over jullie aan, zodat als een van jullie de dood bereikt de door ons gezondenen hem (zijn ziel) wegnemen en zij verzuimen niet.'),
(851, 6, 62, 'Dan worden zij tot Allah, hun ware Meester, teruggebracht Wed dat het Oordeel aan Hem is en Hij is het snelst van hen die afrekenen.'),
(852, 6, 63, 'Zeg (O Moehammad): "Wie redt jullie uit de duisternissen van het land en de zee terwijl jullie Hem in nederigheid en stilte auroepen: "Indien Hij ons van deze (gevaren) zou redden, zouden wij zeker tot de dankbaren behoren."'),
(853, 6, 64, 'Zeg: "Het is Allah Die jullie van deze (gevaren) redt en van alle moeilijkheden en toch kennen jullie deelgenoten toe (aan Allah)?"'),
(854, 6, 65, 'Zeg: "Hij is de Machthebber Die jullie bestraffingen zendt, van boven jullie vandaan of van onder jullie voeten, of Hij verwart jullie in (verschillende) groeperingen en laat sommigen van jullie de wraak van anderen proeven." Zie hoe Wij de Tekenen uitleggen. Hopelijk zullen zij het begrijpen.'),
(855, 6, 66, 'Maar jouw volk loochende ze, hoewel het de Waarheid is. Zeg: "Ik ben niet als verantwoordelijke over juille aangesteld."'),
(856, 6, 67, 'Voor ieder bericht is een bepaalde tijd en jullie zullen het spoedig weten.'),
(857, 6, 68, 'En wanneer jij degenen ziet die beledigend spreken over Onze Verzen, wend je dan van hen af, totdat zij over een ander onderwerp spreken. En indien de Satan het jou doet vergeten, zit dan niet, nadat de herinnering (tot jou komt), bij het onrechtvaardige volk.'),
(858, 6, 69, 'En degenen die (Allah) vrezen zijn in niets verantwoordelijk voor hun rekening, maar het is een vermaning. Hopelijk zullen zij (Allah) vrezen.'),
(859, 6, 70, 'En laat degenen met rust die hun godsdienst tot spel en vennaak nemen, het wereldse leven misleidt hen. Maar vermaant met (de Koran) opdat niemand weggevaagd wordt door wat zij verrichten: hij zal zich buiten Allah geen Helper en geen Voorspreker vinden. En al zou hij iedere losprijs aanbieden: het wordt niet van hem aanvaard. Zij zijn degenen die zichzelf wcgvagen door wat zij verrichten. Voor hen is er een drank van gloeiend water en een pijnlijke bestraffing: vanwege wat zij plachten niet te geloven.'),
(860, 6, 71, 'Zeg: "Roepen wij dat naast Allah aan dat ons geen goed kan doen en ons geen schade kan berokkenen, en keren wij op onze schreden terug nadat Allah ons geleid heeft? Zoals degene die door de Satans op aarde verdwaasd weggelokt is, terwijl hij metgezellen heeft die hem tot de Leiding oproepen: \'Kom tot ons!\'" Zeg: "Voorwaar, Leiding van Allah is de (enige) Leiding en wij zijn bevolen ons te onderwerpen aan de Heer der Werelden.'),
(861, 6, 72, 'En de shalât te onderhouden en Hem te vrezen en Hij is Degene tot Wie jullie verzameld worden."'),
(862, 6, 73, 'Hij is Degene Die de hemelen on de aarde schiep met de Waarheid. En op de dag waarop Hij zegt \'Wees,\' en het is: Zijn Woord is de Waarheid. Ham behoort de heerschappij op de Dag waarop op de bazuin geblazen wordt, Kenner van het onwaarneembare on het waarneembare en Hij is de Alwijze, dc Alwetende.'),
(863, 6, 74, 'En (gedenkt) toen lbrahim tot zijn vader Azar zei: "Neem jij afgodsbeelden tot goden? Voorwaar, ik zie dat jij en jouw volk duidelijk in dwaling verkeren."'),
(864, 6, 75, 'En zo lieten Wij lbrahim het koninkrijk der hemelen en der aarde zien opdat hij tot de overtuigden zou behoren.'),
(865, 6, 76, 'En toen de nacht hem omhulde zag hij een ster, hij zei: "Dit is mijn Meet." Maar toen hij onderging, zei hij: "Ik hou niet van degenen die ondergaan."'),
(866, 6, 77, 'En toen hij de maan zag opkomen, zei hij: "Dit is mijn Heer." Maar toen hij onderging, zei hij: "\'Tenzij mijn Heer mij leidt, zal ik zeker tot het dwelende volk behoren."'),
(867, 6, 78, 'Fn toen hij de zon zag opgaan zei hij: "Dit is mijin Heer, deze is groter." Maar toen zij onderging, zei hij: "O mijn volk: voorwaar, ik ben onschuldig aan wat jullie an deelgenoten (aan Allah) toekennen."'),
(868, 6, 79, '"Voorwaar, ik heb mijn aangezicht gewend naar Hem die de hemelen en de aarde schiep, als Hanîf, en ik behoor niet tot de veelgodenaanbidders."'),
(869, 6, 80, 'En zijn volk redetwistte met hem, hij zei: "Redetwisten jullie met mij over Allah, terwijl Hij mij geleid heeft? Ik vrees niet wat jullie Hem aan deelgenoten toekennen, behalve als mijn Heer iets wil. Mijn Heer omvat alles met Zijn kennis, trekken jullie (hier) dan geen lering uit?'),
(870, 6, 81, 'En hoe zou ik wat jullie aan deelgenoten toekennen (kunnen) vrezen, terwijl jullie niet vrezen Allah deelgenoten toe te kennen, waartoe Hij jullie geen bewijs gezonden heeft? Welke van de twee groepen is veiliger? Indien jullie dat wisten?"'),
(871, 6, 82, 'Degenen die geloven en niet hun geloof met onrecht (afgoderij) mengen: zij zijn degenen die veiligheid toekomt en zij zijn de rechtgelieden.'),
(872, 6, 83, 'En dat was Ons argument dat Wij Ibrâhîm tegen zijn volk gaven, Wij verheffen met graden wie Wij willen: voorwaar, jouw Heer is Alwijs, Alwetend.'),
(873, 6, 84, 'En Wij schonken hem Ishaq en Ya\'qoeb, allen leidden Wij, on ervoor leiden wij Noeh, en van zijn nageslacht Dawoed en Soelaiman en Ayyoeb en Yoesoef en Mocsa en Hârôen: en zo belonen Wij de weldoeners.'),
(874, 6, 85, 'En Zakariyya en Yahya en \'Isa en Ilyas: allen behoorden tot de oprechten.'),
(875, 6, 86, 'En Isma\'îl en Al Yasa\' en Yoenoes en Loeth: allen hebben Wij boven de werelden bevoorrecht.'),
(876, 6, 87, 'En van hun vaderen en hun nageslacht en hun broeders: Wij hebben hen verkozen en Wij leidden hen op een recht Pad.'),
(877, 6, 88, 'Dit is de Leiding van Allah, waarmee Hij van Zijn dienaren leidt wie Hij wil, maar als zij deelgenoten (aan Allah) toegekend hadden, was wat zij aan het doen waren vruchteloos geworden.'),
(878, 6, 89, 'Zij zijn degenen die Wij de Schrift en de Wijsheid en het Profeetschap gaven en indien zij er ongelovig aan zijn: waarlijk, Wij vertrouwen het toe aan een volk dat er niet ongelovig aan is.'),
(879, 6, 90, 'Zij zijn degenen die Allah Leiding gaf: volgt dus hun leiding. Zeg: "Ik vraag U er geen beloning voor, het is slechts een vermaning voor de werelden."'),
(880, 6, 91, 'En zij hebben Allah niet waarheidsgetrouw ingeschat, toen zij zeiden: "Allah heeft niets aan de mens doen neerdalen." Zeg (O Moehammad): "Wie zond de Schrift dan neer die tot Môesa kwam als een licht en als Leiding voor de mensen? Jullie maken er perkament van om te laten zien, maar jullie verbergen er veel (van). Er wordt jullie in geleerd wat jullie niet geleerd was, noch jullie vaderen." Zeg: "Allah (zond het neer)." En laat hen zich dan vermaken met hun ijdele praat.'),
(881, 6, 92, 'En dit is een Boek dat Wij neergezonden hebben, gezegend en bevestigend wat er (aan openbaringen) aan vooraf ging; om Oemmoelqoeri (Mekkah) mee te vermanen en om haar heen. En degenen die in het Hiernamaals geloven, geloven in (het Boek). En zij waken over hun shalât.'),
(882, 6, 93, 'En wie is er meer onrechtvaardig dan degene die een leugen tegen Allah heeft verzonnen, of zei: "Er is aan mij geopenbaard," terwijl er niets aan hem is geopenbaard, en wie zei: "Ik zal het gelijke laten neerdalen van wat Allah heeft laten nederdalen." En als jij zou kunnen zien hoe (het gaat met) de onrechtvaardigen in doodsstrijd en hoe de Engelen hun handen naar hen uitstrekken (terwijl zij zeggen): "Geeft jullie zielen op! Vandaag worden jullie beloont met de bestraffing van de schande vanwege wat jullie aan onwaarheid over Allah plachten te zeggen en vanwege wat jullie van Zijn Verzen hoogmoedig plachten te verwerpen."'),
(883, 6, 94, 'En voorzeker, jullie komen een voor een tot Ons, zoals Wij jullie de eerste keer schiepen, en jullie hebben wat Wij jullie schonken achter jullie ruggen gelaten. En Wij zien jullie voorsprekers, waarvan jullie veronderstelden dat zij deelgenoten (van Allah) waren niet bij jullie; een breuk tussen jullie (en hen) is er gemaakt, on weggedwaald van jullie is dat wat jullie plachten to beweren (te bestaan).'),
(884, 6, 95, 'Voorwaar, het is Allah Die de graankorrel en de dadelpit doet ontkiemen Hij doet het levende uit het dode voortkomen en Hij doet het dode uit het levende voortkomen. Het is waarlijk Allah: waarom laten jullie je afleiden (van de Waarheid)?'),
(885, 6, 96, '(Hij) doet de dag aanbreken en Hij maakte de nacht tot een rusttijd en de zon en de maan voor het berekenen (van de tijd): dat is de vaststelling van de Almachtige, de Alwetende.'),
(886, 6, 97, 'En Hij is Degene Die de sterren voor jullie maakte, opdat jullie door de duisternissen van de aarde en de zee geleid worden. Waarlijk, Wij hebben de Tekenen uitgelegd aan een volk dat weet.'),
(887, 6, 98, 'En Hij is Degene Die jullie dood voortkomen uit één enkele ziel en daarna is er ten vaste verblijfplaats en een bewaarplaats. Waarlijk, Wij hebben de Tekenen uitgelegd aan een volk dit begrijpt.'),
(888, 6, 99, 'Hij is Degene Die water uit de hemel doet neerdalen en Wij laten daarmee allerlei soorten gewas voortkomen, waaruit Wij groenten laten voortkomen (en) waaruit Wij dikgepakt graan laten voortkomen en uit de dadelpalmen, uit de kolf ervan, laaghangende dadeltrossen; en tuinen met druivenstruiken en olijfbomen en granaatappelbomen, gelijksoortig en niet gelijksoortig. Beziet hun vruchten wanneer zij vrucht dragen en (beziet) hun rijping. Voorwaar, daarin zijn Tekenen voor een volk dat gelooft.'),
(889, 6, 100, 'En zij maakten de Djinn\'s tot deelgenoten van Allah, hoewel Hij hen schiep en zij hebben bij Hem zonder kennis zonen en dochters verzonnen. Heilig is Hij en Verheven is Hij boven wat zij beschrijven.'),
(890, 6, 101, 'Schepper van de hemelen en de aarde: hoe kan Hij een zoon hebben indien Hij geen metgezellin heeft? En Hij schiep alle dingen en Hij is Alwetend omtrent alles.'),
(891, 6, 102, 'Dat is Allah, jullie Heer! Er is geen god dan Hij, Schepper van alles: aanbidt Hein dus. En Hij is over alle zaken Toezichthouder.'),
(892, 6, 103, 'Geen blik kan Hem bereiken, maar Hij bereikt de blik (van iedereen); en Hij is de Zachtmoedige, de Alwetende.'),
(893, 6, 104, 'Waarlijk, tot jullie zijn zichtbar bewijzen gekomen van jullie Heer. Wie goed ziet: er is (een voordeel) voor hemzelf, en wie blind is: er is (een nadeel) voor hem, en ik (Moehammad) ben geen bewaker over jullie.'),
(894, 6, 105, 'En zo leggen Wij de Tekenen uit opdat zij zeggen: "Jij hebt (dit van iemand) geleerd." En opdat Wij liet verduidelijken aan een volk dat weet.'),
(895, 6, 106, 'Volg (O Moehammad) hetgeen jou van jouw Heer geopenbaard wordt er is geen god dan Hij. En wend je af van de veelgodenaanbidders.'),
(896, 6, 107, 'En indien Allah gewild had, hadden zij (Allah) geen deelgenoten toegekend, en Wij hebben jou (O Moehammad) niet als bewaker over hen aangesteld en jij bent geen voogd over hen.'),
(897, 6, 108, 'En bespot niet degenen die naast Allah (goden) aanroepen zodat zij niet Allah vijandig zonder kennis bespotten. Op deze wijze hebben Wij aan iedere gemeenschap hun werk mooi doen toeschijnen. Hierna is hun terugkeer tot hun Heer en Hij zal hen vertellen wat zij plachten le doen.'),
(898, 6, 109, 'En zij hebben bij Allah dure eden gezworen dat indien er een Teken tot hen zou komen, zij er zeker door zouden geloven. Zeg: "Voorzeker, alle Tekenen zijn bij Allah. En wat zal jullie doen weten, dat als er eenmaal (een Teken tot hen) kwam, zij niet zouden geloven."'),
(899, 6, 110, 'En Wij zullen hun harten en hun blikken omkeren, zoals zij er de eerste maal al niet in geloofden an Wij laten hen in hun dwaling rusteloos rondgaan.'),
(900, 6, 111, 'En al zouden Wij Engelen tot hen neergezonden hebben en zouden de doden tot hen gesproken hebben en zouden Wij alle zaken (die Moehammad\'s Profeetschap bewijzen) vóór hen verzameld hebben, dan nog zouden zij niet geloven, tenzij Allah het wilde, maar de meesten van hen zijn onwetend.'),
(901, 6, 112, 'En zo hebben Wij voor iedere Profeet een vijand gemaakt; Satans van onder de mensen en de Djinn\'s, zij fluisteren elkaar fraaie woorden in om (de mensen) te misleiden. En als jouw Heer het gewild had, dan zouden zij het niet hebben gedaan, laat hen daarom en wat zij verzinnen.'),
(902, 6, 113, 'Zodat de hanen van degenen die niet in het Hiernamaals geloven ertoe neigen en zodat zij er welbehagen aan hebben, en zodat zij verrichten wat zij (de Satans) verrichten.'),
(903, 6, 114, '(Zeg, O Moehammad:) "Zal ik dan een andere Rechter dan Allah zoeken, terwijl Hij het is Die het Boek (de Koran) tot hen heeft neergezonden als een uiteenzetting?" En degenen aan wie Wij de Schrift hebben gegeven weten dat het een neerzending met de Waarheid van jouw Heer is. Behoor dus zeker niet tot de twijfelaars.'),
(904, 6, 115, 'En het Woord van jouw Heer is tot voltooiing gekomen in waarachtigheid en rechtvaardigheid. Niemand kan Zijn Woorden veranderen. En Hij is de Alhorende, de Alwetende.'),
(905, 6, 116, 'En als jij de meesten van hen die op aarde zijn volgt, dan zullen zij jou doen afdwalen van de Weg van Allah. Zij volgen slechts een vermoeden en zij vertellen slechts verzinsels.'),
(906, 6, 117, 'Voorwaar, jouw Heer, Hij weet het beste wie van Zijn Pad afdwaalt en Hij weet beter wie geleid worden.'),
(907, 6, 118, 'Eet dan van hetgeen waarover de Naam van Allah is uitgesproken (tijdens het slachten), als jullie in Zijn Verzen geloven.'),
(908, 6, 119, 'En wat is er met jullie, dat jullie niet eten van hetgeen waarover de Naam van Allah is uitgesproken, terwijl Hij jullie waarlijk heeft uiteengezet wat Hij jullie heeft verboden, behalve hetgeen waartoe jullie waren genoodzaakt. En voorwaar, de meesten doen (de mensen) zeker dwalen door hun begeerten, zonder kennis. Voorwaar, jouw Heer, Hij kent de overtreders beter.'),
(909, 6, 120, 'En laat de openlijke en de verborgen zonden achterwege. Voorwaar, degenen die zonden bepan zullen vergolden worden met hetgeen zij plachten te verrichten.'),
(910, 6, 121, 'En eet niet van hetgeen waarover (tijdens het slachten) de Naam van Allah niet is uitgesproken en voorwaar, dat is zeker een zware zonde. En voorwaar, de Satans fluisteren hun vrienden in om met jullie te redetwisten en als jullie hen gehoorzamen, dan zullen jullie zeker veelgodenaanbidders worden.'),
(911, 6, 122, 'En wie dood was, en die Wij daarna tot leven brachten en voor wie Wij een licht maakten, waarmee hij onder de mensen rondgaat, is hij te vergelijken met hem die in de duisternissen verkeert, waar bij nimmer uit kan komen? Zo wordt voor de gelovigen schoonschijnend gemaakt wat zij plachten w doen.'),
(912, 6, 123, 'En zo hebben Wij in elke stad haar grootste misdadigers geplaatst opdat zij er (hun kwade plannen) beramen. En zij beramen niet dan tegen zichzelf, terwijl zij het niet beseffen.'),
(913, 6, 124, 'En wanneer er een Teken tot hen komt, dan zeggen zei: "Wij zullen nooit geloven tot aan ons hetzelfde is gegeven als wat aan Allah\'s Boodschappers is gegeven." Allah weet beter waar Hij Zijn Boodschap brengt. Bij Allah zullen degenen die misdaden pleegden deden door kleinering en een strenge bestraffing getroffen worden vanwege wat zij aan kwaad plachten te beramen.'),
(914, 6, 125, 'En wie wil dat Allah hem leidt, diens borst verruimt Hij voor de Islam; en wie Hij wenst te doen dwalen, diens borst maakt hij nauw en beklemd alsof hij (op een hoge berg) naar de hemel klimt. Zo legt Allah de bestraffing op aan degenen die niet geloven.'),
(915, 6, 126, 'En dit is het Pad van jouw Heer, een recht (Pad). Waarlijk, Wij hebben Onze Verzen uiteengezet voor een volk dat zich laat vermanen.'),
(916, 6, 127, 'Voor hen is er Dâroessalâm (het Huis van de Vrede, het Paradijs) bij hun Heer. En Hij is hun Beschermer wegens wat zij plachten te doen.'),
(917, 6, 128, 'En op de Dag waarop Hij allen bijéén zal brengen (zal Hij zeggen:) "O Bemeenschap van Djinn\'s, jullie hebben velen van onder de mensen misleid." En hun bondgenoten van onder de mensen zullen zeggen: "Onze Heer, wij hebben van elkaars diensten gebruik gemaakt, en wij hebben de termijn bereikt die U voor ons had gesteld." Hij (Allah) zal zeggen: "De Hel is jullie bestemming, eeuwig levenden zijn jullie daarin, tenzij Allah (anders) wil." Voorwaar, jouw geer is Alwijs, Alwetend.'),
(918, 6, 129, 'Zo maken Wij de onrechtplegers tot elkaars bondgenoten wegens wat zij plachten te verrichten.'),
(919, 6, 130, '"O gemeenschap van djinn\'s en mensen. zijn er uit jullie midden geen gezanten tot jullie gekomen die jullie Mijn Verzen bekendmaakten en die jullie waarschuwden voor de ontmoeting met deze Dag van jullie?"\' Zij zullen zeggen: "Wij getuigen tegen onszelf." Het wereldse leven heeft hen misleid en zij getuigen tegen zichzelf dat zij ongelovigen plachten te zijn.'),
(920, 6, 131, 'Der is omdat jouw Heer nimmer de steden onrechtvaardig vernietigt en de bewoners ervan zijn achtelozen.'),
(921, 6, 132, 'En voor een ieder zijn er graden naar wat zij gedaan hebben. En jouw Heer is niet onachtzaam aangaande wat zij deden.'),
(922, 6, 133, 'En jouw Heer is de Behoefteloze, de Bezitter van Barmhartigheid. Als Hij wil, dan vaagt Hij jullie weg en stelt Hij na jullie als opvolgers aan wie Hij wil, zoals Hij jullie heeft voortgebracht uit het nageslacht van een ander volk.'),
(923, 6, 134, 'Voorwaar, wat jullie is aangezegd zal zeker komen, en jullie zullen het niet (kunnen) tegenhouden.'),
(924, 6, 135, 'Zeg (O Moehammad): "O mijn volk, werkt naar jullie vermogen. Voorwaar, ik ben een werker en later zullen jullie weten voor wie de uiteindelijka opbrengst (in het Hiernamaals gunstig) is. Voorwaar, de onrechtplegers zullen niet welslagen."'),
(925, 6, 136, 'En zij hebben Allah een deel toebedacht van wat Hij heeft voortgebracht aan gewassen en vee en zij zeiden: "Dit is voor Allah," volgens hun bewering, "en dit is voor onze afgoden." Wat dan voor hun afgoden is, dat bereikt niet Allah; en wat voor Allah is, dat bereikt wel hun afgoden. Slecht is het wat zij oordelen,'),
(926, 6, 137, 'En zo deden hun leiders het doden van hun kinderen voor velen van de veelgodenaanbidders mooi toeschijnen, om hen te vernietigen en om hun godsdienst (met valsheid) te mengen. En als Allah het gewild had, dan hadden zij het niet gedaan. Laat hen en wat zij verzinnen dus!'),
(927, 6, 138, 'En zij zeiden: "Dit vee an (deze) gewassen zijn voorbehouden, niemand eet er van, behalve wit wij willen," zo beweren zij, "en er is vee waarvan hun ruggen verboden zijn (om op te rijden), en vee waarover de Naam van Allah niet uitgesproken is," als een verzinsel over Hem. Hij zal hen vergelden vanwege wat zij plachten te verzinnen.'),
(928, 6, 139, 'En zij zeiden: "Wat zich in de buiken van dit vee bevindt is voorbehouden aan onze mannen en verboden voor onze vrouwen." En wanneer het doodgeboren is, dan zijn zij (do mannen en de vrouwen) en deelgenoten in. Hij (Allah) zal hun beschrijving vergelden. Voorwaar, Hij is Alwijs, Alwetend.'),
(929, 6, 140, 'Waarlijk, degenen die hun kinderen hebben gedood uit dwaasheid, zonder kennis, hebben een groot verlies geleden, en zij verklaarden voor verboden wat Allah hun aan voorzieningen heeft geschonken, als een verzinsel over Allah. Waarlijk, zij dwaalden en zij plachten niet geleid te worden.'),
(930, 6, 141, 'En Hij is Degene Die tuinen heeft voortgebracht, gestuttc en niet gestutte, en dadelpalmen en gewassen met verschillende vruchten, en olijfbomen en granaatappelbomen, gelijkend en niet gelijkend. Eet van hun vruchten wanneer zij vrucht geven, en geeft er het rechtmatige deal (de zakât) van op de dag van hun oogst. En overdrijft niet. Voorwaar, Hij houdt niet van de buitensporigen.'),
(931, 6, 142, 'En (Hij maakte) onder het vee rijdieren en slachtdiejren. Eet van dat waar Allah jullie mee voorzien heeft, en volgt niet de voetstappen van de Satan. Voorwaar, hij is voor jullie een duidelijke vijand.'),
(932, 6, 143, 'Acht, in paren: van de schapen een koppcl en van de geiten een koppel. Zeg (O Moehammad): "Heeft Hij de twee mannelijke dieren verboden of de twee vrouwelijke, of wat de schoten van de vrouwelijke dieren bevatten? Licht mij in op grond van kennis indien jullie waarachtigen zijn."'),
(933, 6, 144, 'En van de kamelen een koppel en van de koei en een koppel. Zeg: "Heeft Hij de twee mannelijke dieren verboden of de twee vrouwelijke, of wat de schoten van de vrouwelijke dieren bevatten? Of waren jullie getuigen toen Allah dit voor jullie bepaalde? Wie is er dan onrechtvaardiger dan degene die een leugen over Allah verzint om de mensen te doen dwalen, zonder kennis." Voorwaar, Allah leidt het onrechtplegende volk niet.'),
(934, 6, 145, 'Zeg: "Ik vind in wat aan mij is geopenbaard geen verbod dat een eter iets eet, behalve wanneer het een kadaver dier is, of stromend bloed of vakensvlees. Want voorwaar, het is onrein, of zwaar zondig waarover iets anders dan de Naam van Allah is uitgesproken. Wie dan door nood gedwongen is (daarvan te eten), zonder dat hij het wenst en zonder dat hij overdrijft: voorwaar, dan is jouw Heer Vergevensgezind, Meest Barmhartig.'),
(935, 6, 146, 'En voor de Joden verboden Wij alle (dieren) met (ongesplitste) hoeven. En van de koeien en de schapen verboden Wij voor hen hun vet, behalve wat hun ruggen dragen, het vet van de ingewanden en het vet dat aan het bot is vergroeid. Zo vergolden Wij hun vanwege hun begerigheid. En voorwaar, Wij zijn zeker waarachtig.'),
(936, 6, 147, 'Als zij jou dan loochenen, zeg dan: "Jullie Heer is de Bezitter van Alomvattende Barmhartigheid, maar Zijn geweld kan niet worden afgewend van het misdadige volk."'),
(937, 6, 148, 'Degenen die deelgenoten (aan Allah) toekenden, zullen zeggen: "Als Allah het had gewild, dan hadden wij geen deelgenoten (aan Allah) toegekend en evenmin onze vaderen, en hadden wij niet (dat wat toegestaan was) verboden verklaard." Zo loochenden zij ook degenen vóór hen (de Boodschappers), totdat zij Ons geweld proefden. Zeg: "Hebben jullie kennis? Brengt het dan naar buiten voor Ons. Jullie volgen slechts vermoedens en jullie liegen slechts."'),
(938, 6, 149, 'Zeg: "Maar Allah heeft het doeltreffende argument. Als Hij het dan had gewild, dan had Hij jullie allen geleid."'),
(939, 6, 150, 'Zeg (O Moehammad): "Komt met jullie getuigen die kunnen getuigen dat Allah dat verboden heeft verklaard." Als zij dan getuigen, getuig dan niet met hen. En volg niet de begeerten van degenen die Onze Verzen loochenen, en evenmin degenen die niet in het Hiernamaals geloven, terwijl zij deelgenoten toekennen aan hun Heer.'),
(940, 6, 151, 'Zeg: "Komt, ik zal voorlezen wat jullie Heer jullie verboden heeft verklaard: dat jullie iets als deelgenoot aan Hem toekennen. Weest goed voor jullie ouders, en doodt niet jullie kinderen uit (angst voor) armoede. Wij schenken voorzieningen aan jullie en aan hen. En nadert niet de zedeloosheid, de openlijke noch de verborgene, en doodt niet de ziel die Allah verboden heeft verklaard, tenzij volgens het recht. Dat is wat Hij jullie heeft opgedragen, hopelijk zullen jullie begrijpen.'),
(941, 6, 152, 'En nadert het bezit van de wees niet, tenzij op ten wijze die meet voordeel (aan de wees) geeft, totdat hij de volwassenheid bereikt. En geeft de volle maat en vult de weegschaal met het gelijke gewicht. Wij (Allah) belasten niemand dan volgens zijn vermogen. En wanneer jullie rechtspreken, weest dan rechtvaardig, ook al betreft het een verwant. En vervult het verbond met Allah. Dat is wat Hij jullie heeft opdragen, hopelijk zullen jullie je laten vermanen.'),
(942, 6, 153, 'En dat dit Mijn Pad is, een recht Pad, volgt het dan, en volgt geen (andere) paden, want die zullen jullie doen afsplitsen van Zijn Pad. Dat is wat Hij jullie heeft opgedragen, hopelijk zullen jullie (Allah) vrezen."'),
(943, 6, 154, 'Toen pven wij Môesa de Schrift (de Taurât), ter vervolmaking (van de gunst) voor degene die het goede verrichtte en als een uiteenzetting voor alle dingen en leiding en Barmhartigheid. Hopelijk zullen zij in de ontmoeting met hun Heer geloven.'),
(944, 6, 155, 'En dit is een Boek dat Wij hebben neergezonden als een zegen, volgt het daarom on vreest (Allah), hopelijk zullen jullie begenadigd worden.'),
(945, 6, 156, 'Opdat zij geen argument zullen hebben om te zeggen: "De Schrift is slechts neergezonden aan de twee groepen (de Joden en Christenen) vóór ons, en wij hebben zeker geen acht geslagen op hun leringen."'),
(946, 6, 157, 'Of dat zij zeggen: "Als de Schrift aan ons was neergezonden, dan zouden wij zeker rechter geleid zijn dan zij." En nu is er dan voorzeker een duidelijk bewijs van jullee Heer tot jullie gekomen, en leiding en Barmhartightid. En wie is er onrechtvaardiger dan wie de Verzen van Allah loochent en zich ervan ofkeert. Wij zullen degenen die zich van Onze Verzen afkeerden vergelden met de ergste bestraffing omdat zij zich plachten af to keren.'),
(947, 6, 158, 'Zij wachten slechts tot dat de Engelen tot hen komen of dat (de bestraffing) jouw Heer tot hen komt of dat een aantal van de Tekenen van jouw Heer komt. Op de Dag dat een deel van de Tekenen van jouw Heer komt, zal het geloof van iemand niets baten wanneer hij daarvóór niet geloofde of niets goeds verrichtte toen hij geloofde. Zeg (O Moehammad): "Wachten jullie, voorwaar, Wij zijn (ook) wachtenden."'),
(948, 6, 159, 'Voorwaar, degenen die hun godsdienst opsplitsten on tot partijen werden, jij (Moehammad) bent in niets verantwoordelijk voor hen, hun kwestie rust slechts bij Allah, Vervolgens zal Hij hun berichten over wat zij plachten te doen.'),
(949, 6, 160, 'Wie met een pede (daad) komt; voor hem is er (een beloning) als van tien daarvan; en wie met een slechte (daad) komt, dit wordt dan slechts vergolden met het gelijke, en hun zal geen onrecht worden amgedaan.'),
(950, 6, 161, 'Zeg (O Moehammad): "Voorwaar, Mijn Heer heeft mij op een reht Pad geleid, de ware godsdienst, de godsdienst van Ibrâhîm, de Hanîf, en hij behoorde niet tot de veelgodenaanbidders."'),
(951, 6, 162, 'Zeg: "Voorwaar, mijn shalât, mijn aanbidding, mijn leven en mijn sterven zijn opgedragen aan Allah, Heer der Werelden.'),
(952, 6, 163, 'Hij heeft geen deelgenoten, en dat is mij bevolen (te verkondigen). En ik ben de eerste van de Moslims."'),
(953, 6, 164, 'Zeg: "Zal ik een andere Heer dan Allah zoeken, terwijl Hij de Heer van alle zaken is?" En niemand bedrijft iets of het rust op hemzelf en geen enkele drager draagt de last van een ander. Daarna is jullie terugkeer tot jullie Heer en dan zal Hij jullie inlichten over hetgeen waarover jullie van mening plachten te verschillen."'),
(954, 6, 165, 'En Hij is het Die jullie heeft aangesteld als gevolmachtigden op de aarde, Hij heeft een deel van jullie in rangen verheven boven anderen, om jullie te beproeven met wat Hij jullie heeft gegeven. Voorwaar, jouw Heer is snel in de bestraffing, en voorwaar, Hij is zeker Vergevensgezind, Meest Bamhartig.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'Alif Lâm Mîm Shâd.'),
(956, 7, 2, '(Dit is) een Boek dat aan jou (Moehammad) neergezonden is, laat er daarom geen bedruktheid in jouw borst zijn vanwege deze (Koran). (Hij is neergezonden) om ermee te waarschuwen, en is een vemaning voor de gelovigen.'),
(957, 7, 3, 'Volgt dan hetgeen aan jullie is neergezonden van jullie Heer en volgt buiten Hem geen leiders. Weinig is het dat jullie je laten vermanen.'),
(958, 7, 4, 'En hoeveel steden hebben Wij niet vernietigd: Onze bestrafring kwam tot hen in de nacht of terwijl zij in de middag rustten.'),
(959, 7, 5, 'En hun smeekbede, toen Onze bestraffing tot hen kwam, was slechts dat zij zeiden: "Voorwaar, wij waren onrechtplegers."'),
(960, 7, 6, 'Wij zullen zeker degenen aan wie (Profeten) gezonden waren ondervragen, en Wij zullen zeker de gczondenen vragen.'),
(961, 7, 7, 'Wij zullen hun dan vertellen (wat zij hebben verricht), met kennis, en Wij waren niet afwezig.'),
(962, 7, 8, 'Op die Dag zal de weegschaal de Waarheid aangeven. Wiens schaal (met goede daden) dan zwaar is: zij zijn degenen die welslagen.'),
(963, 7, 9, 'En wiens schaal licht is: zij zijn degenen die zichzelf verlies hebben toegebracht omdat zij Onze Verzen plachten te ontkennen.'),
(964, 7, 10, 'En voorzeker hebben Wij jullie macht gegeven op aarde en Wij hebben op haar voor jullie levensvoorzieningen geplaatst. Weinig is het dat jullie dankbaar zijn.'),
(965, 7, 11, 'En voorzeker, Wij hebben jullie geschapen, vervolgens hebben Wij jullie vormgegeven en daarna zeiden Wij tot de Engelen: "Knielt jullie neer voor Adam," toen knielden zij, behalve Iblis, hij behoorde niet tot de knielenden.'),
(966, 7, 12, 'Hij (Allah) vroeg: "Wat belemmerde jou je neer te knielen, toen Ik het jou beval?" Hij zei: "Ik ben beter dan hem (Adam); U hebt mij uit vuur geschapen terwijl U hem uit aarde hebt geschapen."'),
(967, 7, 13, 'Hij (Allah) zei: "Daal af uit het (Paradijs), want het past jou niet dat jij je er hoogmoedig in gedraagt. vertrek daaroum. Voorwaar, jij behoort tot de vernederden."'),
(968, 7, 14, 'Hij zei:"Geef mij uitstel tot de Dag waarop zij opgewekt zullen worden."'),
(969, 7, 15, 'Hij (Allah) zei: "Voorwaar, jij behoort tot hen aan wie uitstel is gegeven."'),
(970, 7, 16, 'Hij (Iblis) zei: "Omdat U mij hebt doen dwalen, zal ik hen belemmeren op Uw recht Pad.'),
(971, 7, 17, 'Daarna zal ik zeker tot hen komen, van voor hen en ven achter hen en van hun rechterzijde en van hun linkerzijde, en U zult de meesten van hen niet als dankbaren aantreffen."'),
(972, 7, 18, 'Hij (Allah) zei: \'Ga er uit weg, veracht en verstoten. Wie van hen jou dan volgt: voorwaar, Ik zal de hel vullen met jullie allen."'),
(973, 7, 19, 'En: "O Adam, verblijft in het Paradijs, jij en je vrouw, en eet wat jullie willen, en nadert deze boom niet, want dan zullen jullie tot de onrechtplegers behoren."'),
(974, 7, 20, 'Toen fluisterde de Satan hen in om te onthullen wat er van hun schaamte bedekt was, en hij zei: "Jullie Heer houdt jullie slechts van deze boom af omdat jullie anders Engelen worden, of dat jullie tot de eeuwiglevenden zullen behoren."'),
(975, 7, 21, 'En hij bezwoer hun: "Voorwaar, ik behoor voor jullie zeker tot raadgevers,"'),
(976, 7, 22, 'Waarlijk, hij bedroog hen door misleiding, Toen zij dan van (de vruchten van) de boom hadden geproefd, werd hun schaamte zichtbaar en zij begonnen zich to bedekken met aaneenge-regen bladeren van het Paradijs. En hun Heer riep tot hen: "Heb ik jullie deze boom van jullie niet verboden en heb Ik niet tot jullie gezegd: "Voorwaar, de Satan is voor jullie een duidelijke vijand?"'),
(977, 7, 23, 'Zij zeiden: "Onze Heer, wij hebben onszelf onrecht aangedaan en wanneer U ons niet vergeeft en ons geen genade schenkt, dan zullen wij zeker tot de verliezers behoren."'),
(978, 7, 24, 'Hij (Allah) zei: "Daalt af, jullie zijn elkaars vijanden, en voor jullie is er op de aarde een verblijfplaats en een genieting tot een bepaalde tijd."'),
(979, 7, 25, 'Hij zei: "Daarop zullen jullie leven en daarop zullen jullie sterven en daaruit zullen jullie tevoorschijn worden gebracht."'),
(980, 7, 26, 'O Kinderen van Adam, voorzeker, Wij hebben voor jullie kleding neergezonden om jullie schaamte te bedekken en versierselen. En het kleed van het vrezen (van Allah), dat is het beste. Dat zijn een aantal van Allah\'s Tekenen. Hopelijk laten zij zich vermanen.'),
(981, 7, 27, '0OKinderen van Adam, laat de Satan jullie niet in verzoeking brengen, zoals hij jullie voorouders uit de tuin heeft verdreven; hij nam hun kleding van hen weg om hen hun schaamte te tonen. Hij en zijn aanhangers zien jullie van waar jullie hen niet zien. Voorwaar, Wij hebben de Satans tot leiders gemaakt voor degenen die niet geloven.'),
(982, 7, 28, 'En wanneer zij een gruweldaad bedrijven, dan zeggen zij: "Wij troffen dit bij onze vaderen aan en Allah heeft ons dit bevolen." Zeg (O Moehammad): "Voorwaar, Allah beveelt geen gruweldaden, zeggen jullie dat over Allah wat jullie niet weten?"'),
(983, 7, 29, 'Zeg: "Mijn Heer heeft rechtvaardigheid bevolen." En: "Richt jullie aangezicht (tot Hem) bij iedere knieling (in het gebed) en aanbidt Hem op een zuivere wijze van aanbidding. Zoals Hij jullie de eerste keer heeft geschapen, zo zullen jullie terugkeren."'),
(984, 7, 30, 'Een groep heeft Hij leiding gegeven en voor een andere groep is hun dwaling terecht. Voorwaar, zij namen de Satans als beschermers, naast Allah, en zij dachten dat zij waarlijk rechtgeleiden waren.'),
(985, 7, 31, 'O Kinderen van Adam, draagt jullie mooie kleding bij elke shalât, en eet en drinkt en overdrijft niet. Voorwaar, Hij houdt niet van de buitensporigen.'),
(986, 7, 32, 'Zeg (O Moehammad): "Wie heeft de mooie kleding die Allah voor Zijn dienaren heeft gebracht on de goede dingen van de voorzieningen verboden verklaard?" Zeg: "Dit is op de Dag der Opstanding uitsluitend voor degenen die geloofden tijdens het wereldse leven." Zo zetten Wij de Verzen uiteen aan een volk dat weet.'),
(987, 7, 33, 'Zeg: "Mijn Heer heeft slechts de zedeloosheden verboden, wat er openlijk van is en wat er verborgen van is; en de zonde; en de overtreding zonder recht; en dat jullie Allah deelgenoten toekennen, waarvoor Hij geen bewijs heeft neergezonden en dat jullie over Allah zeggen wat jullie niet weten."'),
(988, 7, 34, 'Voor iedere gemeenschap is er een vastgesteld tijdstip on wanneer haar tijdstip is gekomen, dan kunnen zij het geen moment uitstellen noch vervroegen.'),
(989, 7, 35, 'O Kinderen van Adam, wanneer er Boodschappers uit jullie midden tot jullie komen, die jullie Mijn Verzen vertellen: wie dan (Allah) vreest en zich betert; over hem zal geen angst komen. Noch zullen zij treuren.'),
(990, 7, 36, 'En degenen die Onze Verzen loochenen en er hoogmoedig tegenover staan, zij zijn degenen die de bewoners van de Hel zijn. Zij zijn daarin eeuwig levenden.'),
(991, 7, 37, 'Wie zijn dan onrechtvaardiger dan degenen die een leugen over Allah hebben verzonnen en die Zijn Verzen geloochend hebben? Zij zijn degenen die door hun aandeel in het Boek (Lauhoelmahfoezh) getroffen worden, totdat wanneer Onze gezanten (de Engelen) tot hen komen die hen wegnemen. Zij zeiden: "Waar is hetgeen dat jullie plachten to aanbidden naast Allah?" Zij zeiden: "Zij zijn van ons weggegaan," on zij getuigden over zichzelf dat zij ongelovigen waren.'),
(992, 7, 38, 'Hij (Allah) zegt: "Treedt de gemeenschappen van de Djinn\'s en de mensen binnen die jullie reeds zijn voorgegaan in de Hel. Telkens wanneer een gemeenschap (de Hel) binnengaat, vervloekt zij haar zuster(-gemeenschap), totdat, wanneer zij allen bijelkaarkomen, de laatste van hen over de eerste van hen zegt: "Onze Heer, deze heeft ons heeft doen dwalen, geef hun daarom een dubbele bestraffing van de Hel." Hij zei: "Voor een ieder is er een dubbele (bestraflring), maar jullie weten het niet."'),
(993, 7, 39, 'En de eerste van hen zal tot de laatste van hen zeggen: "Jullie zijn niet boven ons begunstigd, proeft daarom de bestraffing vanwege hetgeen jullie plachten verrichten.'),
(994, 7, 40, 'Voorwaar, degenen die Onze Verzen loochenen en die zich er hooghartig van afwenden: de poorten van de hemel zullen voor hen niet worden geopend, en zij zullen het Paradijs niet binnengaan, totdat de kameel door het oog van de naald gaat. En zo vergelden Wij de misdadigers.'),
(995, 7, 41, 'Voor hen is er een plaats van hellevuur en boven hen zijn er bedekkingen (van vuur). Zo vergelden Wij de onrechtplegers.'),
(996, 7, 42, 'En degenen die geloven en goede werken verrichten, Wij belasten niemand (van hen) dan naar zijn vermogen: zij zijn degenen die de bewoners van het Paradijs zijn. Zij zijn daarin eeuwig levenden.'),
(997, 7, 43, 'En Wij nemen uit hun harten weg wat er aan wrok is, onder (het Paradijs) door stromen de rivieren. En zij zeiden: "Alle lof zij Allah, Die ons hierheen heeft geleid en wij zouden geen leiding hebben gevonden als Allah ons geen Leiding had geschonken, Voorzeker, de gezanten van Onze Heer zijn in Waarheid gekomen. En tot hen wordt geroepen: "Dit is het Paradijs dat jullie hebben beerfd wegens watjullie plachten te doen."'),
(998, 7, 44, 'En de bewoners van het Paradijs roepen tot de bewoners van de Hel: "Wij hebben waarlijk aangetroffen wat onze Heer ons heeft beloofd, hebben jullie dan ook werkelijk aaagetroffen wat jullie Heer jullie heeft aangezegd?" Zij zeiden: "Ja". Dan verkondigt een omroeper onder hen dat de vloek van Allah op de onrechtplegers rust.'),
(999, 7, 45, 'Degenen die (de mensen) afhouden van de Weg van Allah en die wensen dat hij krom was. En zij geloven niet in het Hiernamaals.'),
(1000, 7, 46, 'En tussen hen is een afscheiding en op de A\'raf bevinden zich mannen, zij kennen allen door hun kenmerken; zij roepen tot de bowoners van het Paradijs: Salamoen \'alaikoem." (Vrede zij met jullie) Zij zijn haar nog niet binnengegaan terwijl zij (dat) begeren.'),
(1001, 7, 47, 'En wanneer zij hun blikken in de richting van de bewoners ven de Hel wenden, dan zeggen zij: "Onze Heer, plaats ons niet bij het onrechtplegende volk."'),
(1002, 7, 48, 'En zij die zich op de A\'râf bevinden, roepen tot mannen die zij bij hun kenmerken kennen, zij zeggen: "Jullie vergaren baat jullie niet, noch hetgeen waarop jullie trots plachten te zijn."'),
(1003, 7, 49, '(Zij dit op de A\'râf zitten zeggen:) "Zijn dezen (de gelovigen) degenen waarvan jullie hebben gezworen dat Allah hen niet met Barmhartigheid zou bereiken?" (Tot de gelovigen zal worden gezegd:) "Gaat het Paradijs binnen, er zal geen vrees over jullie komen, noch zullen jullie treuren."'),
(1004, 7, 50, 'En de bewoners van de Hel roepen tot de bewoners van het Paradijs: "Stort over ons uit water, of van dat waar Allah jullie mee voorzien heeft." Zij zeggen: "Voorwaar, Allah heeft dit verboden voot de ongelovigen."'),
(1005, 7, 51, '(Zij zijn) degenen die hun godsdienst als vermaak en spel beschouwden en degenen die bedrogen zijn door het wereldse leven. Op de Dag (der Opstanding) vergeten Wij hen zoals zij de ontmoeting met deze Dag van hen vergaten, on omdat zij Onze Tekenen plachten te ontkennen.'),
(1006, 7, 52, 'En voorzeker hebben Wij hun een Boek gebracht en het daarna uittengezet, op grond ven kennis, als Leiding en als Bamhartigheid voor een volk dat gelooft.'),
(1007, 7, 53, 'Zij wachten op niets anders dan het gevolg ervan. De Dag waarop het gevolg ervan komt, zeggen degenen die het (Boek) daarvoor hadden vergeten: "Waarlijk, de Boodschappers van onze Heer kwamen tot ons met de Waarheid: zijn er voor ons dan nog voorsprekers die ten gunste ven ons spreken? Of kunnen wij (naar de aarde) worden teruggebracht? Dan zullen wij anders handelen dan wij plachten te handelen." Waarlijk, zij hebben zichzelf verlies toegebracht en van hen weggegaan is wat zij plachten te verzinnen.'),
(1008, 7, 54, 'Voorwaar, jullie Heer is Allah, Degene Die de hemelen en de aarde in zes dagen (perioden) heeft geschapen. Vervolgens zetelde Hij zich op de Troon. Hij doet de nacht de dag bedekken, die hem haastig najaagt; en de zon, de maan en de sterren zijn aan Zijn bevel onderworpen. Weet, dat scheppen en bevelen aan Hem is voorbehouden. Gezegend zij Allah, de Heer der Werelden.'),
(1009, 7, 55, 'Roept jullie Heer aan in nederigheid en (met) zachtheid. Voorwaar, Hij houdt niet van de overtreders.'),
(1010, 7, 56, 'En zaait geen verderf op aarde na de verbetering ervan (door de aanwezigheid van de Profeet) en roept Hem aan, (Zijn bestraffing) vrezend en (Zijn Barmhartigheid) begerend. Voorwaar, Allah\'s Barmhartigheid is dicht bij de weldoeners.'),
(1011, 7, 57, 'En Hij is Degene Die de winden zendt als aankondigers van verheugende tijdingen, voorafgaand aan Zijn Barmhartigheid (regen). Zodat wanneer zij de zware wolken hebben opgeheven, Wij die naar een dorre streek drijven, waarna Wij daaruit water doen neerdalen, waarmee Wij dan allerlei soorten vruchten voortbrengen. Zo wekken Wij de doden op. Hopelijk laten jullie je vermanen.'),
(1012, 7, 58, 'En de vruchtbare streek brengt haar gewassen vooit met verlof van haar Heer. En de (streek) die onvruchtbaar is, brengt niets voort dan een schaats gewas. Zo leggen Wij de Tekenen uit aan een volk dat dankbaar is.'),
(1013, 7, 59, 'Voorzeker, Wij zonden Nôeh tot zijn volk en hij zei: "O mijn volk, aanbidt Allah, er is voor jullie geen god dan Hij: voorwaar, ik vrees voor jullie de bestraffing van een geweldige Dag."'),
(1014, 7, 60, 'De vooraanstaanden van zijn volk zeiden: "Voorwaar, wij zien jou zeker in duidelijke dwaling verkeren."'),
(1015, 7, 61, 'Hij (Nôeh) zei: "O mijn volk, er is bij mij geen dwaling, maar ik ben een Boodschapper van de Heer der Werelden.'),
(1016, 7, 62, 'Ik verkondig jullie Boodschappen van mijn Heer en ik geef jullie raad en ik weet over Allah wat jullie niet weten."'),
(1017, 7, 63, 'En verbaasde het jullie, dat er een vermaning van jullie Heer tot jullie is gekomen, door een man uit jullie midden? (Hij komt) opdat hij jullie waarschuwt, en opdat jullie (Allah) vrezen. En hopelijk zullen jullie begenadigd worden.'),
(1018, 7, 64, 'Toen loochenden zij hem (Nôeh), waarna Wij hem redden en degenen met hem in het schip. En Wij verdronken hen die Onze Tekenen loochenden. Voorwaar, zij waren een blind volk.'),
(1019, 7, 65, 'En tot de \'Âd (zonden Wij) hun broeder Hôed, Hij zei: "O mijn vok aanbidt Allah, er is voor jullie geen god dan Hij, zullen jullie dan niet (Allah) vrezen?"'),
(1020, 7, 66, 'De vooraanstaanden onder zijn volk, die ongelovig waren, zeiden: "Voorwaar, wij zien dat jij in dwaasheid verkeert; en voorwar, wij menen zeker dat jij tot de leugenaars behoort."'),
(1021, 7, 67, 'Hij (Hôed) zei: "O mijn volk, er is bij mij geen dwaasheid, maar ik ben een Boodschapper van de Heer der Werelden.'),
(1022, 7, 68, 'Ik verkondig jullie Boodschappen van mijn Heer en ik ben voor jullie een betrouwbare raadgever."\''),
(1023, 7, 69, 'En verhaasde het jullie, dat er tot jullie een vermaning van jullie Heer is gekomen, door een man uit jullie midden, om jullie te vermanen? En gedenkt toen Hij jullie tot opvolgers had aangesteld na (do ondergang van) het volk van Nôeh. En jullie gestalten maakten Wij groter (dan die van hen). Gedenkt daarom de gunsten van Allah. Hopelijk zullen jullie welslagen.'),
(1024, 7, 70, 'Zij zeiden: "Ben jij tot ons gekomen opdat Wij Allah ah Enige aanbidden en verlaten wat onze vaderen plachten te aanbidden? Laat dan komen, hetgeen wat jij ons aanzegt, als jij tot de waarachtigen behoort."'),
(1025, 7, 71, 'Hij zei: "Waarlijk, een bestraffing en toorn van jullie Heer is erover jullie neergekomen. Willen jullie met mij redetwisten over de namen die jullie en jullie vaderen gaven (aan jullie afgoden), waarvoor Allah geen bewijs heeft neergezonden? Wachten jullie maar: voorwaar, ik behoor met jullie tot de wachtenden."'),
(1026, 7, 72, 'Toen redden Wij hem (Hôed) en degenen met hem door Genade van Ons en Wij roeiden degenen die Onze Tekenen loochenden geheel uit. En zij plachten geen gelovigen te zijn.'),
(1027, 7, 73, 'En tot de Tsamôed (zonden Wij) hun broeder Shâlih. Hij zei: "O mijn volk, aanbidt Allah, er is voor jullie geen god dan Hij. Waarlijk, er is een duidelijk bewijs van jullie Heer tot jullie gekomen. Dat is de vrouwtjeskameel van Allah, als een Teken voor jullie. Laat haar daarom op de aarde van Allah eten en doe haar geen kwaad, anders zal een pijnlijke bestraffing jullie grijpen."'),
(1028, 7, 74, 'En (gedenkt) toen Hij jullie tot opvolgers aanstelde na (de ondergang van) de \'Ad en Hij jullie op aarde vestigde. Jullie namen van haar vlakten om er paleizen (op te bouwen) en jullie namen van de bergen om er huizen uit te houwen. Gedenkt daarom de gunsten van Allah en sticht geen onheil op de aarde, als verderfzaaiers.'),
(1029, 7, 75, 'De vooraannstaanden, van degenen die hoogmoedig waren onder zijn volk, zeiden tot de nederigen onder hen die geloofden: "Weten jullie dat Shâlat een door zijn Heer gezondene is?" Zij zeiden: "Voorwaar, wij geloven in hetgeen warmee hij is gezonden."'),
(1030, 7, 76, 'Degenen die hoogmoedig waren, zeiden: "Voorwaar, wij geloven niet in hetgeen waarin jullie geloven."'),
(1031, 7, 77, 'Zij slachtten de vrouwtjeskameel en zij minachtten het bevel van hun Heer en zij zeiden: "O Shâlih, laat het aangezegde (de bestraffing) tot ons komen, als jij tot de gezanten behoort!"'),
(1032, 7, 78, 'Toen greep de aardbeving hen, waardoor zij doden in hun huizen werden.'),
(1033, 7, 79, 'Toen wendde hij (Shâlih) zich van hen af en zei. "O mijn volk, voorzeker, ik heb jullie een Boodschap van mijn Heer vorkondigd en jullie raad gegeven, maar jullie houden niet van de raadgevers."'),
(1034, 7, 80, 'En (gedenkt) Lôeth, toen hij tot zijn volk zei: "Begaan jullie zedeloosheid die nog niemand van de bewoners van de werelden vóór jullie heeft begaan?'),
(1035, 7, 81, 'Voorwaar, jullie naderen zeker wellustig mannen in plaats van vrouwen. Jullie zijn zelfs ten overschrijdend volk."'),
(1036, 7, 82, 'En het antwoord van zijn volk was slechts dat zij zeiden: "Verdrijft hen (Lôeth en zijn volgelingen) uit jullie stad. Voorwaar, zij zijn mensen dit zich reinigen."'),
(1037, 7, 83, 'Toen redden Wij hen en zijn volgelingen, behalve zijn vrouw, zij behoorde tot de achterblijvers (die gestraft werden).'),
(1038, 7, 84, 'En Wij deden een regen (van stenen) op hen neerkomen. Zie dan hoe liet einde was vam de misdadigers.'),
(1039, 7, 85, 'En tot (de bewoners van) Madyan (zonden Wij) hun broeder Sjoe\'aib, Hij zei: "O mijn volk, unbidt Allah, er is voor jullie geen god dan Hij. Waarlijk, er is een duidelijk bewijs van jullie Heer tot jullie gekomen. Geeft dan de volle maat en het volle gewicht en benadeelt de mensen niet in hun zaken en zaait geen verderf op aarde na de verbetering op haar (door de komst van een Profeet). Dat is beter voor jullie, als jullie gelovigen zijn."'),
(1040, 7, 86, 'En zit niet op iedere weg, terwijl jullie degenen bedreigen en afhouden van het Pad van Allah die in Hem geloven, wensend dat het krom was. En gedenkt toen jullie met woinigen waren en Hij jullie talrijk deed worden. En zie hoe het einde was van de verderfzaaiers.'),
(1041, 7, 87, 'En als er een groep onder jullie is die gelooft in hetgeen waarmee ik (Sjoe\'aib) ben gezonden, en er een andere groep is die niet gelooft: weest dan geduldig tot Allah tussen ons oordeelt, en Hij is de Beste der Rechters.'),
(1042, 7, 88, 'De vooraanstaanden, van degenen die hoogmoedig waren van zijn volk, zeiden: "Wij zullen jou, O Sjoe\'aib, en degenen die met jou geloven zeker uit onze stad verdrijven, of jullie moeten terugkeren tot onze godsdienst." Hij zei: "En als wij er een afkeer van zouden hebben?"'),
(1043, 7, 89, 'Waarlijk, wij zouden een leugen ovet Allah verzinnen als wij zouden terugkeren tot jullie godsdienst, nadat Allah ons daaruit heeft gered. Het past ons niet dat wij erin terugkeren, behalve wanneer Allah, onze Heer, het wil. Onze Heer omvat alle dingen met Zijn kennis. Op Allah vertrouwen wij. Onze Heer, doe een uitspraak tussen ons en ons volk, naar de Waarheid en U bent de Beste van de Oordelaars.'),
(1044, 7, 90, 'En de vooraanstaanden van zijn volk, die ongelovig waren, zeiden. "Als jullie Sjoe\'aib gevolgd hebben: voorwaar, dan zullen jullie zeker de verliezers zijin."'),
(1045, 7, 91, 'Toen greep de aardbeving hen en zij werden doden in hun huizen.'),
(1046, 7, 92, 'Degenen die Sjoe\'aib loochenden werden alsof zij nooit in haar (de stad) hadden gewoond. Degenen die Sjoe\'aib loochenden, zij zijn de verliezers.'),
(1047, 7, 93, 'Hij (Sjoe\'ajb) wendde zich men van hen af, en hij zei: "O mijn volk, voorzeker, ik heb Boodschappen van mijn Hecr verkondigd, en jullie raad gegeven. Hoe kan ik dan bedroefd zijn over een volk dal ongelovig is?"'),
(1048, 7, 94, 'En Wij zonden geen Profeet naar een stad, of Wij troffen de inwoners ervan met kwclling en tegenspoed. Hopelijk zullen zij nederig worden.'),
(1049, 7, 95, 'Daarna vervingen Wij het slechte door het goede, totdat zij (in aantal en welvaart) toenamen, en zij zeiden: "Voorzeker, tegenspoed en voorspoed hebben ook onze vaderen getroffen." Toen grepen Wij hen onverwachts, terwijl zij er niet op bedacht waren.'),
(1050, 7, 96, 'En ais de inwoners van de steden hadden geloofd en (Allah) hadden gcvreesd, dan hadden Wij zeker voor hen zegeningen uit de hemel en de aarde geopend; maar zij loochenden, zodat Wij hen grepen wegens wat zij plachten te verrichten.'),
(1051, 7, 97, 'Voelen de inwoners van de steden zich er soms veilig voor, dat Onze bestraffing in de nacht tot hen komt, terwijl zij slapen?'),
(1052, 7, 98, 'Ofvoelen de inwoners van de steden zich er soms veilig voor, dat Onze bestrafting in de ochtend tot hen komt, terwijl zij spelen?'),
(1053, 7, 99, 'Voelen zij zich soms veilig voor het plan van Allah? Niemand voelt zich veilig voor het plan van Allah, behalve het verliezende volk.'),
(1054, 7, 100, 'Is het degenen die de aarde beërven na (de ondergang van) haar bewoners niet duidelijk geworden dat, als Wij zouden willen, Wij hen zouden treffen vanwcge hun zonden, en Wij hun harten zouden vergrendelen zodat zij niet zouden kunnen luisteren?'),
(1055, 7, 101, 'Over deze steden geven Wij jou (O Moehammad) berichten. En voorzeker, hun Profeten waren tot hen gekomen met de duidelijke bewijzen. Maar zij waren niet zo dat zij geloofden in wat zij voorheen loochenden. Zo vergrendelt Allah de beden van de ongelovigen.'),
(1056, 7, 102, 'En Wij troffen de meesten van hen aan als mensen die zich niet aan een verbond hielden. En de meesten van ben troffen Wij zeker aan als zwaar zondigen.'),
(1057, 7, 103, 'Vervolgens zonden Wij na hen Môesa met Onze Tekenen tot Fir\'aun en zijn vooraanstaande volgelingen, waarop zij deze in hun onrechtvaadigheid verwierpem. Aanschouw dan hoe het einde was van de verderfzaaiers.'),
(1058, 7, 104, 'En Môesa zei: "O Fir\'aun, voorwaar, ik ben een Boodschapper van de Heer der Werelden.'),
(1059, 7, 105, 'Het is mij verplicht dat ik over Allah niets dan de Waarheid zeg. Waarlijk, ik ben tot jullie gekomen met een duidelijk Teken van jullie Heer, stuur de Kinderen van breel daarom met mij mee."'),
(1060, 7, 106, 'Hij (Fir\'aun) zei: "Als jij met een Teken bent gekomen, kom er dan mee, als jij tot de waarahtigen behoort."'),
(1061, 7, 107, 'Toen wierp bij (Môesa) zijn staf, en toen werd deze een duidelijke slang.'),
(1062, 7, 108, 'En bij haalde zijn hand tevoorschijn, en toen werd deze witstralend voor de toeschouwers.'),
(1063, 7, 109, 'De vooraanstaanden van Fir\'aun\'s volk zeiden: "Voorwaar, dit is een kundige tovenaar!'),
(1064, 7, 110, 'Hij wil jullie uit jullie land verdrijven!"\' (Fir\'aun vroeg:) "Wat raden jullie aan?"\''),
(1065, 7, 111, 'Zij zeiden: "Geef hem en zijn broeder uitstel, en stuur verzamelaars naar de steden.'),
(1066, 7, 112, 'Opdat zij alle vaardige tovenaars tot u brengen.'),
(1067, 7, 113, 'En de tovenaars kwamen tot Fir\'aun, zij zeiden: "Voorwaar, is er voor ons zeker een beloning als wij de winnaars zijn."'),
(1068, 7, 114, 'Hij (Fir\'aun) zei: "Ja, en voorwaar, jullie zullen tot de nabijen behoren."'),
(1069, 7, 115, 'Zij zeiden: "O Môesa, wep jij (eerst) of werpen wij?"'),
(1070, 7, 116, 'Hij zei: "Werpt." Toen zij dan wierpen, betoverden zij de ogen van de mensen en joegen hen angst aan met geweldige tovenarij.'),
(1071, 7, 117, 'En Wij openbaarden aan Môesa: "Werp jouw staf!" En toen verslond deze wat zij met hun bedrog hadden gemaakt.'),
(1072, 7, 118, 'Toen werd de Wacheid duidelijk, en bleek wat zij (de tovenaars) plachten te doen valsheid te zijn.'),
(1073, 7, 119, 'Zij werden daar verslagen, en zij keerden vernederd terug.'),
(1074, 7, 120, 'En de (tot inkeer gekomen) tovenaars wierpen zich (als in de shalât) neer.'),
(1075, 7, 121, 'Zij zeiden: "Wij geloven in de Heer der Werelden.'),
(1076, 7, 122, 'De Heer van Môesa en Hârôen."'),
(1077, 7, 123, 'Fir\'aun zei: "Geloven jullie hem vóór dat ik jullie toestemming heb gegeven? Voorwaar, dit is zeker een list die jullie beraamd hebben in de stad om de inwoners ervan te verdrijven. Maar straks zullen jullie weten (wat de gevolgen van jullie daden zijn).'),
(1078, 7, 124, 'Ik zal zeker jullie handen en voeten kruiselings afhouwen en vervolgens zal ik jullie zeker allen kruisigen."'),
(1079, 7, 125, 'Zij (de voormalige tovenaars) zelden: "Voorwaar, tot onze Heer keren wij terug.'),
(1080, 7, 126, 'En jij neemt slechts wraak op ons, omdat wij in de Tekenen van onze Heer geloofden toen deze tot ons kwamen. Onze Heer, schenk ons geduld en doe ons sterven als mensen die zich (aan U) hebben overgegeven."'),
(1081, 7, 127, 'En de vooraanstaanden van het volk van Fir\'aun zeiden: "Laat u Môesa en zijn volk verderf op aarde zaaien en u en uw goden verlaten?" Hij zei: "Wij zullen hun zonen doden en hun dochters in leven laten: en voorwaar, wij zijn oppermachtig over hen."'),
(1082, 7, 128, 'Môesa zei tot zijn volk: "Smeekt Allah om hulp en weest geduldig. Voorwaar, de aarde behoort aan Allah, Hij doet haar erven aan wie Hij wil van zijn dienaren. En de (goede) einde behoort aan de Moettaqôen.'),
(1083, 7, 129, 'Zij zeiden: "Wij werden gekweld vóór dat jij (Môesa) tot ons kwam en nadat jij tot ons bent gekomen." Hij zei: "Hopelijk zal jullie Heer jullie vijanden vernietigen en jullie als opvolgers aanstellen op de aarde, opdat Hij zal aanschouwen hoe jullie handelen."'),
(1084, 7, 130, 'En voorzeker grepen Wij het volk van Fir\'aun met jaren (van hongersnood) en tekorten aan oogstopbrengsten. Hopelijk zullen zij zich laten vermanen.'),
(1085, 7, 131, 'En wanneer dan het goede tot Hen kwam, zeiden zij: "Voor ons is dat (vanwege onze inspaningen)," maar wanneer het slechte hen treft, dan wijzen zij Môesa en degenen met hem aan als brengers van het noodlot. Wet: hun noodlot is slechts bij Allah, maar de meesten van hen weten het niet.'),
(1086, 7, 132, 'Zij zeiden: "Met wat voor Tekenen jij ook tot ons komt om ons daarmee te betoveren, wij zullen daardoor niet in jou zeloven."'),
(1087, 7, 133, 'Daarop zonden Wij tot hen de overstroming, en sprinkhanen, en luizen, en kikkers en bloed, als duidelijke Tekenen, maar zij toonden zich hoogmoedig en zij waren een misdadig volk.'),
(1088, 7, 134, 'En toen de plaag ben overviel, zeiden zij: "O Môesa, roep jou Heer aan krachtens het verbond dat jij met Hem hebt. Als jij de plaag van ons wegneemt, dan zullen wij zeker in jou geloven en dan zullen wij de Kinderen van Israël zeker met jou sturen."'),
(1089, 7, 135, 'En toen Wij de plaag van hen hadden weggenomen, voor een vastgestelde termijn die zij bereikten, toen breken zij hun woord.'),
(1090, 7, 136, 'Wij vergolden hun toen doordat Wij hen verdronken in de zee omdat zij Onze Tekenen loochenden en zij ze niet in acht plachten te nemen.'),
(1091, 7, 137, 'En aan het volk dat onderdrukt was geweest, deden Wij gebieden ten Oosten en ten Westen van het land erven, die Wij zegenden. En het mooie Woord van jouw Heer werd bewaarheid voor de Kinderen van Israël omdat zij geduldig waren geweest. En Wij verwoestten wat Fir\'aun en zijn volk hadden gemaakt en wat zij plachten te bouwen.'),
(1092, 7, 138, 'En Wij brachten de Kinderen van Israël naar de overkant van de zee. En toen zij een volk ontmoetten dat opging in het vereren van hun afgodsbeelden, zeiden zij: "O Môesa, maak voor ons een god zoals hun goden." Hij zei: "Voorwaar, jullie zijn een onwetend volk."'),
(1093, 7, 139, 'Voorwaar, van dezen zal hetgeen waar zij in opgaan vernietigd worden, en wat zij plachten te doen bleek valsheid te zijn.'),
(1094, 7, 140, 'Hij zei: "Zou ik voor jullie een god naast Allah zoeken, terwijl Hij jullie heeft bevoorrecht boven de werelden?""'),
(1095, 7, 141, 'En (gedenkt) toen Wij ben redden van de volgelingen van Fir\'aun, zij kwelden jullie met de ergste bestraffing; zij doodden jullie zonen en lieten jullie dochters in leven. En daarin was een geweldige beproeving van jullie Heer.'),
(1096, 7, 142, 'En Wij beloofden Môesa (een periode van) dertig dagen en Wij vervolmaakten deze met tien (dagen), zo werd de afgesproken termijn met zijn Heer vervolmaakt: veertig nachten. En Môesa zei tot zijn broeder Hârôen: "Vervang mij bij mijn volk, en verbeter en volg niet het pad van de verderfzaaiers."'),
(1097, 7, 143, 'En toen Môesa op de met Ons afgesproken tijd was gekomen en zijn Heer tot hem had gesproken, zei hij: "Mijn Heer, Toon U aan mij." Hij zei: "Jij zult Mij nimmer (kunnen) zien, maar kijk naar de berg, als deze op zijn plaats blijft, dan zul je Mij zien." En toen zijn Heer zich aan de berg zichtbaar maakte, maakte Hij deze tot pulver, en Môesa viel bewusteloos ter aarde. En toen hij weer bij bewustzijn was gekomen, zei hij: "Heilig bent U, ik wend mij berouwvol tot U en ik ben de eerste van de gelovigen."'),
(1098, 7, 144, 'Hij (Allah) zei: "O Môesa. voorwaar, Ik heb jou uitverkoren beven de (andere) mensen, door middel van Mijn Boodschap aan jou en Mijn ij Woord. Neem dan wat Ik aan jou heb gegeven (de Taurát) en behoor tot de Dankbaren."'),
(1099, 7, 145, 'En Wij schreven hem voor in de Tafelen over alle zaken, als een vermaning en als een uiteenzetting over alle zaken (en Wij zeiden:) "Grijpt dit stevig vast en beveel jouw volk dat zij zich er op de beste manier aan houden, Ik zal jullie de woonplaatsen van de zwaar zondigen tonen."'),
(1100, 7, 146, 'Ik zal degenen die zich onterecht hoogmoedig op aarde gedragen van Mijn Tekeeien afwenden. En al zagen zij ieder Teken, dan nog zouden zij er niet in geloven, en al zagen zij het juiste Pad, dan zouden zij het niet als hun (pad) nemen. Maar als zij het pad van de dwaling zien, dan nemen zij het als (hun) pad. Dat is omdat zij Onze Tekenen loochenden en er geen acht op plachten te slaan.'),
(1101, 7, 147, 'En degenen die Onze Tekenen en de ontmoeting in het Hiernamaals loochenden: hun werken zijn vructhteloos. Zij worden niet vergolden, behalve voor wat zij plachten te doen.'),
(1102, 7, 148, 'En het volk van Môesa maakte, na zijn vertrek (naar de berg Thôer). van hun (gouden) sieraden (een afgodsbeeld met) het lichaam van een kalf dat een loeiend geluid maakte. Beseften zij niet dat het in werkelijkheid niet tot hen kon spreken en hen geen weg kon wijzen? Zij namen het (ter aanbidding) en zij waren onrechtplegers.'),
(1103, 7, 149, 'En toen spijt hen van voor en van achter overviel, en zij zagen dat zij waarlijk gedwaald hadden, zeiden zij: "Als onze Heer ons niet begenadigt en ons niet vergeeft, dan zullen wij zeker tot de verliezers behoren."'),
(1104, 7, 150, 'En toen Môesa tot zijn volk terugkeerde, boos en bedroefd, zei hij: "Slecht is wat jullie in mijn plaat tijdens mijn afwezigheid hebben gedaan, wilden jullie het bevel (tot bestraffing) van jullie Heer verhaasten?" En bij zette de Tafelen haastig neer en bij greep zijin broeder bij zijn baard en trok hem naar zich toe. Hij (Hârôen) zei: "Zoon van mijn moeder, voorwaar, het volk heeft mij overweldigd en bijna hadden zij mij gedood. Laat de vijanden geen leedvermaak over mij hebben en stel mij niet gelijk aan het volk van onrechtplegers."'),
(1105, 7, 151, 'Hij (Môesa) zei: "Mijn Heer, vergeef mij en mijn broeder en doe ons Uw Barmhartigheid binnengaan. En U bent de Barmhartigste der Erbarmers."'),
(1106, 7, 152, 'Voorwaar, degenen die het kalf (tot hun god) hebben genomen: de toorn van hun Heer zal hen treffen, alsmede vernedering in het wereldse leven. En zo vergelden Wij degenen die leugens verzinnen.'),
(1107, 7, 153, 'En degenen die slechte werken verrichtten en die daarna berouw toonden en geloofden: voorwaar, jouw Heer is dan daarna zeker Vergevensgezind, Meest Barmhartig.'),
(1108, 7, 154, 'En toen de toorn van Môesa bedaard was, nam hij de Tafelen op. En daarin staat Leiding en Barmhartigheid beschreven voor degenen die kun Heer vrezen.'),
(1109, 7, 155, 'En Môesa koos uit zijn volk zeventig mannen op de door Ons bepaalde tijd (om Allah om vergiffenis te smeken). En toen de aardbeving hen greep, zei hij: "Mijn Heer, als U wilde, dan had U hen en mij eerder vernietigd. Zult U ons vernietigen wegens wat de dwazen onder ons hebben gedaan? Dit is niets anders dan een beproeving van U. U doet daarmee dwalen wie U wil en U leidt wie U wil, U bent onze Beschermer, vergeef ons daarom en begenadig ons, U bent de Beste der Vergevensgezinden.'),
(1110, 7, 156, 'En schrijf ons in deze wereld het goede voor en (ook) in het Hiernamaals. Voorwaar, wij wonden ons in berouw tot U." Hij (Allah) zei: "Met Mijn bestraffing tref Ik wie Ik wil en Mijn Barmhartigheid omvat alle dingen. Ik zal haar voorschrijven aan degenen die (Mij) vrezen, die de zakât geven en die in Mijn Tekenen geloven."'),
(1111, 7, 157, '(Zij zijn) degenen die de Boodschapper van Allah volgen, de ongeletterde Profeet waarover bij hen, in de Taurât en in de Indjîl, geschreven is. Hij beveelt hun het behoorlijke en hij verbiedt hun het verwerpelijke, en hij staat hun de goede dingen toe en bij verbiedt hun de slechte dingen. En bij bevrijdt hun van kun lasten en van de boeien die op hen rustten. Degenen die hem geloven, hem bijstaan en hem helpen en die het Licht (de Koran) volgen dat met hem is neergezonden, zij zijn degenen die welslagen."'),
(1112, 7, 158, 'Zeg: "O mensen, voorwaar, ik ben de Boodschapper van Allah voor jullie allen. (Allah is) Degene aan Wie het Koninkrijk over de hemelen en de aarde behoort, geen god is er dan Hij. Hij doet leven en Hij doet sterven. Gelooft daarom in Allah en Zijn Boodschapper, de ongeletterde Profeet, die in Allah en Zijn Woorden gelooft, en volgt hem. Hopelijk zullen jullie Leiding volgen."'),
(1113, 7, 159, 'En onder het volk van Môesa, is er een gemeenschap die leiding geeft volgens de Waarheid, en die rechtvaardig handelen.'),
(1114, 7, 160, 'En Wij verdeelden hen in twaalf stammen als gemeenschappen en Wij openbaarden aan Môesa, toen zijn volk om water vroeg: Sla met jouw staf op de rots." Toen ontsprongen daaruit twaalf bronnen. Voorzeker, alle stammen kenden hun drinkplaatsen en Wij gaven hen schaduw met de wolk en Wij deden Manna en kwartels op ben neerdalen, (zeggend:) "Eet van de goede dingen waarvan Wij jullie hebben voorzien, en zij waren niet onrechtvaardig jegens Ons, maar zij waren onrechtvaardig jegens zichzelf.'),
(1115, 7, 161, 'En (gedenkt) toen er tot hen werd gezegd: "Bewoont deze stad en eet van haar (opbrengsten) wat jullie willen en zegt: \'Vergeving!\' En gaat de poorten binnen, jullie hoofden buigend, dat zullen Wij jullie je fouten vergeven. Wij zullen (de beloningen) van de weldoeners vergroten."'),
(1116, 7, 162, 'Daarna verruilden degenen onder hen die onrecht pleegden (het Woord van Allah) voor een ander woord dat niet tot hen was gesproken, waarop Wij een plaag op hen neerzonden uit de hemel wegens het onrecht dat zi plachten te bedrijven.'),
(1117, 7, 163, 'En vraag hen over de stad die dicht bij de zee lag, toen zij de Sabbat overtraden, toen hun vissen voor hen boven water verschenen op hun Sabbat, terwijl zij op (andere) dagen dan de Sabbat niet verschenen. Zo beprcoefden Wij hen wegens de zwam zonden die zij plachten te begaan.'),
(1118, 7, 164, 'En (gedenkt) toen eem gemeenschap onder hen zei: "Waarom vermanen jullie een volk dat Allah zal vernietigen of bestraffen met een harde bestraffing?" Zij zeiden: "Opdat wij niet beschuldigd zullen worden bij jullie Heer en hopelijk zullen zij (Allah) vrezen."'),
(1119, 7, 165, 'En toen zij vergaten waarmee zij vermaand waren, toen redden Wij degenen die het kwade verboden, en Wij grepen degenen die onrecht pleegden met een harde bestraffing, wegens de zware zonden die zij plachten te bedrijven.'),
(1120, 7, 166, 'En toen zij opstandig werden met betrekking tot wat hun was verboden, zeiden Wij tot hen: "Weest verachte apen."'),
(1121, 7, 167, 'En (gedenkt) toen jouw Heer bekend maakte dat Hij zeker (mensen) tegen hen zou zenden, tot am de Dag van de Opstanding, die hen zouden treffen met deergsle bestraffing. Voorwaar, jouw Heer is zeker snel in de bestraffing: en voorwaar, Hij is zeker Vergevensgezind, Meest Bannhartig.'),
(1122, 7, 168, 'En Wij verdeelden hen op aarde in gemeenschappen. Onder hen zijn rechtschapenen en onder hen zijn er die dat niet zijn, en Wij beproefden hen met de goede en de slechte dingen. Hopelijk zullen zij terugkeren.'),
(1123, 7, 169, 'En na hen kwamen er opvolgers die de Schrift (de Taurâh) erfden. Zij namen vergankelijke genietingen van dit (wereldse leven). En zij zeiden: "Wij zullen vergeven worden." En als dezelfde genie tingen tot hen komen, dan nemen zij die (weer) aan. Is er geen verbond van de Schirift met hen aangegaan dat ze over Allah niets dan de Waarheid zouden zeggen? En zij bestudeerden wat er in is. En het Huis van het Hiernamaals is beter voor degenen die (Allah) vrezen. Bwijpen jullie dan niet?'),
(1124, 7, 170, 'En degenen die zich vasthouden aan het Boek en de shalât onderhouden: voorwaar, de beloning van degenen die verbetering brengen zullen Wij niet verloren doen gaan.'),
(1125, 7, 171, 'En (gedenkt) toen Wij de berg boven hen verhieven alsof hij een gewelf van wolken was, en zij dachten dat hij op hen zou vallen, (en Wij zeiden:) "Houdt jullie stevig vast aan wat jullie is gegeven en gedenkt wat er in (de Schrift) staat. Hopelijk zullen jullie (Allah) vrezen."'),
(1126, 7, 172, 'En (gedenkt) toen jouw Heer het nageslacht van de Kinderen van Adam uit hun lendenen nam, en hen deed getuigen over zichzelf (en Hij zei:) "Ben Ik niet jullie Heer?" Zij zeiden: "Jazeker, dat getuigen wij." Opdat jullie op de Dag van de Opstanding niet zullen zeggen: "Wij waren hieromtrent achtelozen."'),
(1127, 7, 173, 'Of dat jullie niet zullen zeggen: "Onze vaderen kenden vroeger al deelgenoten (ma Allah) toe en wij zijn nakomelingen na hen; zult U ons vernietigen voor wat de volgers van de valsheid deden?"'),
(1128, 7, 174, 'En zo leggen Wij de Verzen uit. Hopelijk zullen zij terugkeren.'),
(1129, 7, 175, 'En lees hen de geschiedenis voor van degene aan wie Wij Onze Verzen gaven. Vervolgens maakte hij zich daarvan los, waarop de Satan hem achtervolgde zodat bij één van de dwalenden werd.'),
(1130, 7, 176, 'En als Wij het hadden gewild, dan zouden Wij hem daarmee (met Onze Verzen) hebben verheven, maar hij neigde naw de aarde en volgde zijn hegeerte. Zijn gelijkenis is als de gelijkenis van de hond; als je hem wegjaagt, dan hijgt bij, en als je hem met rust laat dan hijgt hij. Dat is de gelijkenis van het volk dat Onze Verzen loochent, vertel daarom de geschiedenissen. Hopelijk zullen zij nadenken.'),
(1131, 7, 177, 'Slecht is de gelijkenis van het volk dat Onze Verzen loochent en zij plachten zichzelf onrecht aan te doen.'),
(1132, 7, 178, 'Wie door Allah geleid wordt; die volgt de ware Leiding, en wie Hij doet dwalen: zij zijn degenen die de verliezers zijn.'),
(1133, 7, 179, 'En voorzeker, Wij hebben velen van de Djinn\'s en de mensen voor de Hel geschapen. Zij bezitten harten waarmee zij niet begrijpen en zij bezitten ogen waarmee zij niet zien en zij bezitten oren waarmee zij niet horen, zij zijn degenen die als het vee zijn. Zij dwalen zelfs nog erger. Zij zijn degenen die de achtelozen zijn.'),
(1134, 7, 180, 'En aan Allah behoren de Schone Namen, roept Hem daarmee aan en verlaat degenen die misbruik van Zijn Namen maken; zij zullen worden vergolden vooir wat zij plachten te doen.'),
(1135, 7, 181, 'En onder hen die Wij hebben geschapen bevindt zich een gemeenschap die leidt volgens de Waarheid en daarmee handelen zij rechtvaardig.'),
(1136, 7, 182, 'En degenen die Onze Verzen loochenden, die zullen Wij geleidelijk naar de vernietiging voeren, ze dat zij het niet weten.'),
(1137, 7, 183, 'En Ik geef hen uitstel, Voorwaar, Mijn plan is sterk.'),
(1138, 7, 184, 'En denken zij niet na? Hun stamgenoot (Moehammad) is niet bezeten. Hij is slechts een duidelijke waarschuwer.'),
(1139, 7, 185, 'En kijken zij niet naar het Koninkrijk van de hemelen en de aarde en naar alle dingen die Allah heeft geschapen? En mogelijk is hun termijn waarlijk reeds nabij. In welk woord buiten dit Woord zullen zij dan geloven?'),
(1140, 7, 186, 'En wie door Allah tot dwaling gebracht wordt: voor hem is er geen leider en Hij laat hen onrustig in hun overtreding verkeren.'),
(1141, 7, 187, 'Zij vragen jou (O Moehammad) over het Uur: "Wanneer zal het plaatsvinden?" zeg: "Voorwaar, de kennis daarover is slechts bij mijn Heer, niemand kan over de tijd ervan duidelijkheid geven dan Hij. Zwaar (is deze kwestie) in de hemelen en op de aarde. Het zal slechts plotseling tot jullie komen." Zij vragen jou als of jij daarvan op de hoogte bent. Zeg: "Voorwaar, de kennis daarover is slechts bij Allah, maar de meeste mensen weten het niet.'),
(1142, 7, 188, 'Zeg: "Ik heb geen inacht om voor mijzelf iets van nut te verwerven of schade af te wenden, behalve wat Allah wil. En als ik het onwaarneembare kende, dan zou ik het goede vermeerderd hebben en zou het kwade mij niet hebben getroffen. Ik ben niets dan een waarschuwer en een verkondiger van verheugende tijdingen voor een gelovig volk.'),
(1143, 7, 189, 'Hij is Degene Die jullie heeft geschapen uit één enkele ziel en Hij maakte daaruit zijn echtgenote, opdat bij bij haar rust zou vinden. En nadat hij bij haar gelegen had, droeg zij een lichte last waar zij toen met voortging. En toen zij zwaar werd, riepen zij Allah, hun Heer aan: "Als U ons een rechtschapene schenkt, dan zullen wij tot de dankbaren horen."'),
(1144, 7, 190, 'Maar toen Hij hen een rechtschapene had geschonken, kenden zij naast Hem deelgenoten toe in wat Hij hun had geschonken. Verheven is dan Allah boven de deelgenoten die zij (aan Allah) toekennen.'),
(1145, 7, 191, 'Maken zij (beelden tot) deelgenoten die niets (kunnen) scheppen en die (zelf) geschapen zijn?'),
(1146, 7, 192, 'En die hen niet kunnen helpen en die zichzelf niet (kunnen) helpen?'),
(1147, 7, 193, 'En als jullie hen aanroepen om leiding (te verkrijgen), dan verhoren zij jullie niet. Het is voor jullie hetzelfde, of jullie hen aanroepen of dat jullie zwijgen.'),
(1148, 7, 194, 'Voorwaar, degenen die jullie buiten Allah aanroepen zijn schepselen zoals jullie zelf. Roept hen dan aan en laten zij jullie dan verhoren, als jullie waarachtigen zijn.'),
(1149, 7, 195, 'Hebben zij voeten waarmee zij lopen? Of hebben zij handen waarmee zij grijpen? Of hebben zij ogen waarmee zij zien? Of hebben zij oren waarmee zij horen? Zeg: "Roept jullie afgoden aan en laat hen een list tegen mij uitvoeren, en geeft mij geen uitstel.'),
(1150, 7, 196, 'Voorwaar, mijn Beschermer is Allah, Die het Boek heeft neergezonden, en Hij beschermt de oprechten.'),
(1151, 7, 197, 'En zij (de afgoden) die zij buiten Hem aanroepen, zijn niet in staat jullie te helpen, noch (kunnen) zij zichzelf helpen."'),
(1152, 7, 198, 'En wanneer jullie ben aanroepen om leiding (te verkrijgen) dan horen zij niet. En jij ziet hen naar jou kijken, terwijl zij niet zien.'),
(1153, 7, 199, 'Aanvaard de verontschuldiging en roep op tot het behoorlijke en wend je af van de onwetenden.'),
(1154, 7, 200, 'En wanneer een influistering van de Satan jou ingefluisterd wordt, zoek dan je toevlucht bij Allah. Voorwaar, Hij is Alhorend, Alwetend.'),
(1155, 7, 201, 'Voorwaar, wanneer degenen die (Allah) vrezen door een influiskring van de Satan getroffen worden, gedenken zij (Allah). En dan zien zij (de Waarheid) in.'),
(1156, 7, 202, 'En hun (slechte) broeders doen hen nog verder afdwalen en zij houden daama niet op (te dwalen).'),
(1157, 7, 203, 'En wanneer jij niet met een Vers tot hen komt, dan zeggen zij: "Had je er zelf niet een kunnen maken?" Zeg: "Ik volg alleen dat wat aan Mij is geopenbaard van mijn Heer. Dit zijn de heldere bewijzen van jullie Heer en Leiding en Bamhartigheid voor een gelovig volk."'),
(1158, 7, 204, 'En als de Koran wordt voorgedragen, luistert er dan naar en zwijgt hopelijk zullen jullie begenadigd worden.'),
(1159, 7, 205, 'En noem (de Namen van) jouw Heer in jezelf met nederigheid en vrees, en zonder luidruchtigheid van woorden, in de ochtend en in de avond en behoor niet tot de achtelozen.'),
(1160, 7, 206, 'Voorwaar, degenen die bij jouw Heer zijn (de Engelen), zijn niet te hoogmoedig voor de aanbidding van Hem, en zij prijzen Zijn Glorie en voor Hem werpen zij zich neer.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Zij vragen jou (O Moehammad) over de oorlogsbuit, zeg: "De oorlogsbuit behoort aan Allah en de Boodschapper toe. Vreest dan Allah en sticht vrede onder jullie. En gehoorzaamt Allah en Zijn Boodschapper, als jullie gelovigen zijn."'),
(1162, 8, 2, 'Voorwaar, de gelovigen zijn slechts degenen wiens harten sidderen wanneer Allah genoemd wordt en wameer Zijn Verzen aan hen worden voorgedragen; hun geloof neemt dan toe, en op hun Heer hebben zij hun vertrouwen gesteld.'),
(1163, 8, 3, 'Zij die de shalât onderhouden en die bijdragen geven van de voorzieningen die Wij hun hebben geschonken.'),
(1164, 8, 4, 'Zij zijn degenen die de ware gelovigen zijn, voor hen zijn er rangen bij hun Heer, en vergeving en een edele voorziening.'),
(1165, 8, 5, 'Zoals jouw Heer jou met de Waarheid uit jouw huis heeft doen gaan, (zo verdeelt Hij ook met de Waarheid de oorlogsbuit). En voorwaar, een groep van de gelovigen heeft ef een afkeer van.'),
(1166, 8, 6, 'Zij redetwisten met jou over de Waarheid nadat deze is duidelijk geworden, alsof zij naar de dood gedreven worden terwijl zij toezien.'),
(1167, 8, 7, 'En (gedenkt) toen Allah jullie beloofde dat er één van de twee groepen (van jullie vijanden) zeker voor jullie zou zijn. En jullie wensten dat zij die geen wapens bij zich droegen voor jullie zouden zijn. Maar Allah wenst dat de Waarheid bewaarheid wordt door zijn Woorden en Hij roeit de ongelovigen uit.'),
(1168, 8, 8, 'Opdat de Waarheid bewaarheid zou worden en de valsheid als valsheid duidelijk zou worden, ook al hebben de misdadigers er een afkeer van.'),
(1169, 8, 9, 'Toen jullie je Heer (bij de Slag bij Badr) om hulp vroegen en Hij jullie verhoorde (en zei:) "Ik zal jullie (leger) aanvullen met duizend Engelen die elkaar (in strijdgroepen) opvolgen."'),
(1170, 8, 10, 'En Allah deed dat slechts om verheugende tij dingen te verkondigen en opdat jullie harten daardoor tot rust zouden komen. En er is geen hulp dan van bij Allah. Voorwaar, Allah is Almachtig, Alwijs.'),
(1171, 8, 11, '(Gedenkt) toen Hij jullie met slaap bedekte, als middel ter beveiliging van Hem en Hij deed uit de hemel water op jullie neerdalen om jullie daarmee te reinigen en om de plaag van de Satan van jullie weg te nemen en om jullie harten te versterken en om jullie hielen te verstevigen.'),
(1172, 8, 12, '(Gedenk) toen jouw Heer aan de Engelen openbaarde: "Voorwaar, Ik ben met jullie, versterkt daarom degenen die geloven. Ik zal angst werpen in de harten van degenen die ongelovig zijn. Staat dan hun hoofden af en slaat al hun vingers en tenen af."'),
(1173, 8, 13, 'Dat was omdat zij Allah en Zijn Boodschapper bestreden, en wie tegen Allah en zijn Boodschapper strijdt: Allah is hard in de bestraffing.'),
(1174, 8, 14, 'Zo, (is de straf), proeft die dan: en voorwaar, voor de ongelovigen is er de bestraffing van de Hel.'),
(1175, 8, 15, 'O jullie die geloven, wanneer jullie degenen die ongelovig zijn ontmoeten, die tegen jullie optrekken, draait dan hen niet dc rug toe (om te vluchten).'),
(1176, 8, 16, 'En wie op die dag hen de rug toedraait, anders dan om een wending te maken (als strategie), of om zich aan te sluiten bij een groep (medestrijders), waarlijk, die keert terug onder de toom van Allah en zijn verblijfplaats is de Hel. En dat is de slechtste bestemming.'),
(1177, 8, 17, 'Het waren niet jullie die hen doodden, maar het was Allah die hen doodde, en het was niet jij (O Moehammad) die wierp toen jij wierp, maar het was Allah die wierp. En om de gelovigen er mee te beproeven, als een goede beproeving. Voorwaar, Allah is Alhorend, Alwetend.'),
(1178, 8, 18, 'Zo is het. En voorwaar, Allah maakt de list van de ongelovigen zwak.'),
(1179, 8, 19, 'Als jullie (ongelovigen) om een (beslissende) overwinning vragen, waarlijk, de beslissing is reeds tot jullie gekomen en als jullie ophouden, dan is dat beter voor jullie. Maar als jullie (het strijden) hervatten, dan zullen Wij ook (Onze hulp aan de gelovigen) hervatten en jullie leger zal jullie niets baten, ook al zijn de manschappen talrijk. En waarlijk, Allah is met de gelovigen.'),
(1180, 8, 20, 'O jullie die geloven, gehoorzaamt Allah en Zijn Boodschapper en wendt jullie niet van hem af, terwijl jullie (de Koran) horen.'),
(1181, 8, 21, 'En weest niet als degenen die zeiden: "Wij hebben gehoord," terwijl zij niet hebben geluisterd.'),
(1182, 8, 22, 'Voorwaar, de slechtste schepselen bij Allah zijn de doven en de stommen (van hart): degenen die niet begrijpen.'),
(1183, 8, 23, 'En Allah weet dat er niets goeds in hen was, dus liet Hij hen niet luisteren. En als Hij hen had doen luisteren, dan zouden zij zich afwenden, terwijl zij (de Waarheid) afwijzen.'),
(1184, 8, 24, 'O julliel die geloven, geeft gehoor aan Allah en aan de Boodschapper wanneer hij jullie oproept tot wat jullie leven geeft en weet dat Allah een afscheiding maakt tussen een mens en zijn hart. En voorwaar, tot Hem zullen jullie worden verzameld."'),
(1185, 8, 25, 'En vreest een beproeving die niet uitsluitend degenen die onrecht plegen onder jullie zal treffen. En weet dat Allah hard is in de bestraffing.'),
(1186, 8, 26, 'En gedenkt toen jullie met weinigen waren en jullie onderdukt werden in het land. Jullie vreesden dat de mensen jullie zouden ontvoeren. Toen gaf Hij jullie een toevluchtsoord (de stad Medinah) en versterkte Hij jullie met Zijn hulp. En Hij voorzag jullie van de goede dingen. Hopelijk zullen jullie dankbaar zijn.'),
(1187, 8, 27, 'O jullie die geloven, pleegt geen verraad tegenover Allah en de Boodschapper en pleegt geen verraad tegenover het jullie toevertrouwde, terwijl jullie het weten.'),
(1188, 8, 28, 'En weet dat jullie bezittingen en jullie kinderen slechts een beproeving zijn en dat er bij Allah een geweldige beloning is.'),
(1189, 8, 29, 'O jullie die geloven, als jullie Allah vrezen, zal Hij jullie een onderscheidingsvermogen geven en jullie slechte daden uitwissen en jullie vergeven. En Allah is de Bezitter van de Geweldige Gunst.'),
(1190, 8, 30, 'En (gedenk) toen degenen die ongelovig waren, een list tegen jou beraamden om jou vast te binden of jou te doden of jou te verdrijven. En zij beraamden een list en Allah maakte een plam. En Allah is de Beste der Beramers.'),
(1191, 8, 31, 'En wanneer Onze Verzen aan ben worden voorgedragen, dan zeggen zij: "Wij hebben (iets dergelijks al eerder) gehoord, en als wij willen dan zullen wij zeker zoiets zeggen. Dit is niets dan een fabel van de voorafgaanden."'),
(1192, 8, 32, 'En (gedenkt) toen zij zeiden: "O Allah, als dit (de Koran) de Waarheid is, van U afkomstig, doe dan stenen op ons neer regenen uit de hemel. Of geef ons een pijnlijke bestraffing."'),
(1193, 8, 33, 'Maar Allah zal hen nooit bestraffen terwijl jij (Moehammad) je onder hen bevindt, en Allah zal hen nooit bestraffen terwijl zij om vergeving vragen.'),
(1194, 8, 34, 'En welk argument hebben zij, dat Allah hen niet zal bestraffen terwijl zij (de mensen) weghouden van de Masdjid al Harâm (de Gewijde Moskee te Mekkah) en zij niet de beheerders ervan zijn. De (rechtmatige) beheerders ervan zijn slechts de Moettaqôen, maar de meesten van hen weten het niet.'),
(1195, 8, 35, 'En hun sheidt bij het Huis van Allah (de Ka\'bah) was niets dan gefluit en handgeklap. Proeft dan de bestraffing wegens dat waaraan jullie ongelovig plachten te zijn.'),
(1196, 8, 36, 'Voorwaar, degenen die niet geloven besteden hun bezittingen om (de mensen) van het Pad van Allah af te houden. Zij zullen deze (bezittingen blijven) besteden, maar daarna zullen deze een bron van spijt voor hen worden en uiteindelijk zullen zij worden verslagen. En degenen die ongelovig zijn zullen in de Hel verzameld worden.'),
(1197, 8, 37, 'Opdat Allah het slechte van het goede zal scheiden en Hij de slechten op elkaar zal plaatsen en Hij alles tezamen zal opstapelen en de Hel in zal voeren. Zij zijn degenen die de verliezers zijn.'),
(1198, 8, 38, 'Zeg tot degenen die ongelovig zijn: "Als jullie ophouden, zullen jw]ie worden vergeven voor wat reeds voorbij is, maar als jullie in herhaling vervalken, dan (geldt) voor ben de handelwijze (van Allah), zoals die veedsgold voor de vroegeren.'),
(1199, 8, 39, 'En bevecht hen totdat er geen Fitnah meer is en de godsdienst geheel voor Allah is. Als zij ophouden, voorwaar, dan is Allah wat betreft hetgeen zij bedreven Alziende.'),
(1200, 8, 40, 'En ais zij zich afwenden, weet dan dat Allah waarlijk jullie Beschermer is. Hij is de Beste Berchermer en de Beste Helper.'),
(1201, 8, 41, 'En weet dat, wat jullie ook aan oorlogsbuit (Ghanîmah) hebben verkregen: één vijfde deel ervan is voor Allah en voor de Boodschapper en voor de verwanten (van de Boodschapper), en de wezen, en de annen en de reiziger (zonder proviand), als jullie in Allah geloven en in wat Wij aan Onze dienaar hebben neergezonden op de dag van het onderscheid, de dag dat de twee legers elkaar troffen. En Allah is Almachtig over alle dingen.'),
(1202, 8, 42, 'Toen jullie je op de nabijgelegen rand van de vallei bevonden en zij zich op de verste rand van de vallei, terwijl de karavaan zich onder jullie bevond. En als jullie een afspraak zouden maken, (over de dag van de veldslag), dan zouden jullie het met elkaar oneens zijn. (Maar Allah liet de veldslag plaatsvinden) zodat Allah een zaak zou volbrengen die plaats moest vinden, zodat zij die sneuvelden vanwege een duidelijk bewijs sneuvelden; en zodat zij die in leven bleven vanwege een duidelijk bewijs in leven bleven. En voorwaar, Allah is zeker Alhorend en Alwetend,'),
(1203, 8, 43, 'Toen Allah hen in jouw slaap gering in aantal toonde (was jij aangemoedigd). En als Hij hen groot in aantal had getoond, dan zouden jullie ontmoedigd zijn geraakt, en het met elkaar oneens zijn geworden over de kwestie, maar Allah bracht redding. Voorwaar, Hij weet wat zich in de harten bevindt.'),
(1204, 8, 44, 'Toen Hij hen aan jullie toonde, toen jullie met hen tot een treffen kwamen, deed Hij hen in jullie ogen als weinigen voorkomen. En Hij deed jullie als een klein aantal in hun ogen voorkomen, zodat Allah een vastgestelde zaak zou volbrengen. En tot Allah keren alle zaken terug.'),
(1205, 8, 45, 'O jullie die geloven, als jullie een leger treffen, weest dan standvastig en gedenkt Allah veelvuldig. Hopelijk zullen jullie welslagen.'),
(1206, 8, 46, 'En gehoorzaamt Allah en Zijn Boodschapper en twist niet onderling, waardoor jullie ontmoedigd raken en jullie kracht verdwijnt. En weest geduldig: voorwaar, Allah is met de geduldigen.'),
(1207, 8, 47, 'En weest niet zoals degenen die hun huizen verlieten, trots en vol ijdel vertoon voor de mensen, en die van het Pad van Allah afflielden. En Allah omvat (met Zijn kennis) wat zij doen.'),
(1208, 8, 48, 'En (gedenkt) toen de Satan hun daden voor hen schoonschijnend maakte, en zei: "Er is op deze dag geen mens die jullie kan verslaan: en voorwaar, Ik ben jullie naaste." Maar toen de twee legers elkaar zagen, draaide hij zich om op zijn hielen en zij: "Voorwaar, ik heb niets met jullie van doen. Voorwaar, ik zie wat jullie niet zien, voorwaar, ik vrees Allah." En Allah is hard in de bestraffing.'),
(1209, 8, 49, 'Toen de huichelaars en degenen in wiens harten een ziekte is, (tegen de gelovigen) zeiden: "Hun godsdienst heeft ben misleid." Maar (voor) wie Allah vertrouwt (is Allah hun Helper). Voorwaar, Allah is Almachtig, Aiwijs.'),
(1210, 8, 50, 'En als jij (het) zou kunnen zien, wanneer de Engelen (de zielen van) degenen die ongelovig zijn wegnemen, (en hoe) zij hun gezichten en hun ruggen slaan (en zeggen:) "Proeft de bestraffing van het branden!" (Dan zie je zeker een geweldige zaak.)'),
(1211, 8, 51, 'Dat is, vanwege wat hun handen hebben voortgebracht, en waarlijk, Allah is geen onrechtvaardige voor de dienaar.'),
(1212, 8, 52, 'Zoals Fir\'aun en zijn volgelingen cm degenen vóór hen gewoon waren: zij waren ongelovig aan de Tekenen van Allah, waarop Allah hen greep wegens hun zonden. Voorwaar, Allah is sterk, hard in de bestraffing.'),
(1213, 8, 53, 'Dat is omdat Allah nimmer een genieting die Hij aan een volk heeft geschonken verandert (in een bestraffing), tenzij zij veranderen wat zich bij hen bevindt: en voorwaar, Allah is Alhorend, Alwetend.'),
(1214, 8, 54, 'Zoals Fir\'aun en zijn volgelingen en degenen vóór hen; zij loochhenden de Tekenen van hun Heer, waarna Wij hen vernietigden wegens hun zonden. En Wij verdronken Fir\'aun en zijn volgelingen en zij allen waren onrechtplegers.'),
(1215, 8, 55, 'Voorwaar, de slechtste schepselen bij Allah zijn degenen die ongelovig zijn, omdat zij niet geloven.'),
(1216, 8, 56, '(Zij zijn) degenen met wie jij (O Moehammad) een verbond bent aangegaan, (en die) vervolgens hun verbond iedere keer schenden: en zij vrezen (Allah) niet.'),
(1217, 8, 57, 'Wanneer jullie hen dan treffen in de oorlog: doe degenen achter hen dan alle kanten uitvluchten, hopelijk zullen zij er lering uit trekken.'),
(1218, 8, 58, 'En als jij verraad vreest van een volk, hef het (verbond) dan met wederzijdse duidelijkheid op. Voorwaar, Allah houdt niet van de verraders.'),
(1219, 8, 59, 'En laten degenen die ongelovig zijn zeker niet denken dat zij (de bestraffing van Allah) kunnen tegenhouden. Voorwaar, zij kunnen (die) niet ontkrachten.'),
(1220, 8, 60, 'En brengt wat jullie kunnen aan macht bij elkaar, om hen mee te bevechten, waaronder strijdrossen om daarmee de vijanden van Allah en jullie vijanden angst aan te jagen, en ook anderen die jullie niet kennen (en) die Allah wel kent. En wat jullie ook aan bijdragen uitgeven op de Weg van Allah, Hij zal jullie volledig vergoeden. En jullie zal geen onrecht worden aangedaan.'),
(1221, 8, 61, 'En als zij geneigd zijn tot vrede, wees dan ook daartoe geneigd en stel jouw vertrouwen op Allah. Voorwaar, Hij is Alhorend, Alwetend.'),
(1222, 8, 62, 'En als zij joe willen bedriegen; den is Allah voorj jou voldoende. Hij is het Degene jou versterkte door Zijn hulp en door de gelovigen.'),
(1223, 8, 63, 'En Hij bracht hun harten tot elkaar. En al had jij alles wat er op de aarde is besteed, dan zou jij hun harten niet tot elkaar hebben kunnen brengen, maar Allah heeft hen tot elkaar gebracht. Voorwaar, Hij is Almachtig, Alwijs.'),
(1224, 8, 64, 'O Profeet, Allah is voor jou voldoende en voor de gelovigen die jou volgen.'),
(1225, 8, 65, 'O Profeet, spoor de gelovigen aan tot het gevecht. Als er onder jullie twintig zijn die geduldig zijn, dan zullen zij er tweehonderd verslaan. En als er onder jullie honderd zijn, dan zullen zij er duizend verslaan van degenen die ongelovig zijn, omdat zij een volk zijn dat niet begrijpt.'),
(1226, 8, 66, 'Nu heeft Allah jullie verlichting gegeven en Hij weet dat er onder jullie zwakken zijn. En als er onder jullie honderd zijn die geduldig zijn, dan zullen zij er tweehonderd verslaan. En als er duizend onder juillie zijn, dan zullen zij er tweeduizend verslaan, met Allah\'s verlof. En Allah is met de geduldigen.'),
(1227, 8, 67, 'Het past een Profeet niet dat hij krijgsgevangenen heeft, totdat hij (de vijanden) op aarde heeft onderworpen. Jullie wensen de wereldse vergankelijkheden, maar Allah wenst (voor jullie) het Hiernamaals. En Allah is Abmachtig, Alwijs.'),
(1228, 8, 68, 'Als er niet een vastgestelde beschikking van Allah aan voorafgegaan was, dan zou een geweldige bestraffing jullie zeker hebben getroffen, wegens dat wat jullie hebben genomen.'),
(1229, 8, 69, 'Eet dan van wat jullie aan oorlogsbuit hebben genomen, het is toegestaan en goed, en vreest Allah. Voorwaw, Allah is Vergeverisgezind, Meest Barmhartig.'),
(1230, 8, 70, 'O Profeet, zeg tot de gevangenen die zich in jouw handen bevinden: "Als Allah doet weten dat er iets van goedheid in jullie harten is, dan zal Hij jullie iets beters geven dan wat van jullie is afgenomen, en dan zal Hij jullie vergeven." En Allah is Vergevensgezind, Meest Barmhartig.'),
(1231, 8, 71, 'Maar als zij verraad willen plegen tegenover jou (O Moehammed), dan pleegden zij daarvoor verraad tegenover Allah, en Hij zal jou tot heerser over hen maken, en Allah is Alwetend, Alwijs.'),
(1232, 8, 72, 'Voorwaar, degenen die geloven en die zijn uitgeweken (naar Medinah) en die hebben gesteden met hun bezittingen en hun levens op de Weg van Allah, en degenen die onderdak boden en hulp verleenden: zij zijn het die elkaars bondgenoten zijn. En degenen die geloven" maar die nog niet zijn uitgeweken: jullie hebben geen enkele verbondenheid met hen, totdat zij zijn uitgeweken. En wanneer zij jullie hulp vragen bij de godsdienst, dan is het jullie plicht om hen te helpen, behalve tegen een volk waarmee jullie een verbond hebben. En Allah is Alziende over wat jullie doen.'),
(1233, 8, 73, 'En degenen die ongelovig zijn, zijn elkaars bondgenoten. Als jullie dat niet doen (elkaar steunen en beschermen), den zal er chaos en een groot verderf op aarde ontstaan.'),
(1234, 8, 74, 'En degenen die geloven en zijn uitgeweken en hebben gestreden op de Weg van Allah, en degenen die onderdak hebben gegeven en hulp hebben verleend: zij zijn het die de ware gelovigen zijn. Voor ben is er vergeving en een edele voorziening.'),
(1235, 8, 75, 'En degenen die daarna gelovig zijn geworden en zijn uitgeweken en samen rnet jullie hebben gestreden: zij zijn degenen die tot jullie behoren. En de bloedverwanten hebben voorrang boven de anderen in het Boek van Allah. Voorwaar, Allah is Alwetend over alle dingen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, '(Dit is een verklaring over) een verbreking van de banden (Barâ\'ah), van Allah en zijn Boodschapper, gericht tot de veelgodenaanbidders met wie jullie (gelovigen) een verbond hebben gesloten.'),
(1237, 9, 2, 'Trekt dan vier maanden in het land rond, en weet dat jullie het plan van Allah nimmer kunnen ontkrachten. En voorwmr, Allah vernedert de ongelovigen.'),
(1238, 9, 3, 'Ein (dit is) een bekendmaking van Allah en zijn Boodschapper aan de mensheid op de dag van de grote Bedevaart (al Haddj al Akbar) (inhoudende) dat Allah en Zijn Boodschapper niets te maken hebben met de veelgodenaanbidders. En als jullie (veelgodenaanbidders) berouw tonen, dan is dat beter voor jullie, maar als jullie afwenden, weet dan dat jullie (het plan van) Allah nimmer kunnen ontkrachten. En waarschuwt degenen die ongelovig zijn voor een pijnlijke bestraffing.'),
(1239, 9, 4, 'Behalve met de veelgodenaanbidders met wie jullie een verbond zijn aangepan, (en) die jullie daarna niets tekort hebben gedaan en die niemand tegen jullie hebben geholpen. Komt dan het verbond met hen na, tot het eind van de overeengekomen termijn. Voorwaar, Allah houdt van de Moettaqôen.'),
(1240, 9, 5, 'En wanneer de gewijde maanden voorbij zijn, doodt dan de veelgodenaanbidders waar jullie hen ook aantreffen en grijpt hen, en omsingelt hen en loert op ben vanuit elke hinderlaag. Als zij dan berouw tonen, en de shalât onderhouden de zakât geven, laat hen dan vrij. Voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(1241, 9, 6, 'En één wanneer van de veelgodenaanbidders bescherming bij jullie zoektt geeft hem dan bescherming, zodat hij het Woord van Allah hoort. En brengt hem daarna nou een plaats die veilig voor hem is. Dat is omdat zij een volk zijn dat niet weet.'),
(1242, 9, 7, 'Hoe kan er voor de veelgodenaanbidders een verbond zijn bij Allah en bij zijn Boodschapper, behalve voor degenen met wie jullie een verbond hebben gesloten bij de Masdjid al Harâm? Zolang zij het gestand blijven, blijven jullie het met hen gestand. Voorwaar, Allah houdt van de Moettaqôen.'),
(1243, 9, 8, 'Hoe kan het zijn, dat wanneer zij de overhand over jullie hebben, zij niet waken over de verwantschapsbanden met jullie, noch over de afspraken? Zij behagen jullie met hun monden, terwijl hun harten afkerig zijn. En de meesten van hen zijn verderfzaaiers.'),
(1244, 9, 9, 'Zij verruilden de Verzen van Allah voor een geringe prijs, waarna zij (de mensen) afhielden van Zijn Pad: voorwaar, slecht is het wat zij plachten te doen.'),
(1245, 9, 10, 'Zij waken met betrekking tot de gelovige niet over de verwantschapsbanden, noch over de afspraken. Zij Zijn degenen die de overtreders zijn.'),
(1246, 9, 11, 'Wanneer zij dan berouw tonen, en de shalât onderhouden en de zakât geven, dan zijn zij jullie broeders is de godsdienst. En Wij zeden de Verzen uiteen aan een dat volk dat weet.'),
(1247, 9, 12, 'En wanneer zij hun eden breken, nadat zij een verbond hebben gesloten en zij zich vijandig gedragen tegen over jullie godsdienst: doodt dan de leiders van de ongelovigen. Voorwaar, bij ben hebben eden geen waarde. Hopelijk zuilen zij ophouden.'),
(1248, 9, 13, 'Waarom bestrijden jullie een volk dat hun eden breekt niet? Terwijl zij van plan zijn om de Boodschapper te verdrijven, en zij jullie het eerst aanvallen? Vrezen jullie hen, terwijl Allah er meer recht op heeft dat jullie Hem vrezen, als jullie gelovigen zijn?'),
(1249, 9, 14, 'Doodt hen; Allah zal hen zeker bestraffen door middel van jullie handen en Hij zal hen vernederen en Hij zal jullie helpen tegen hen en Hij zal de heden van een gelovig volk genezen.'),
(1250, 9, 15, 'En Hij zal de woede van de heden doen verdwijnen en Allah aanvaardt het berouw van wie Hij wil. En Allah is Alwetend, Alwijs.'),
(1251, 9, 16, 'Of dachten jullie dat jullie onbeproefd gelaten zouden worden, terwijl Allah nog niet heeft doen weten, wie van jullie strijden en geen venrouwelingen nemen buiten Allah en zijn Boodschapper en de gelovigen? En Allah is Alwetend over wat jullie doen.'),
(1252, 9, 17, 'En het past de veelge de veelgodaanbidders niet dat zij de moskeeën van Allah verzorgen, terwijl zij over zichzelf getuigen dat zij ongelovig zijn. Zij zijn degenen wiens daden vruchteloos zijn en die in de Hel eeuwig levenden zijn.'),
(1253, 9, 18, 'Voorwaar, de moskeeën van Allah worden slechts verzorgd door wie in Allah en de Laatste Dag gelooft, en die de shalât onderhoudt en de zakât geeft en die niemand vreest dan Allah. Hopelijk zijn zij degenen die tot de rechtgeleiden zullen behoren.'),
(1254, 9, 19, 'Stellen jullie het geven van drinken aan de bedevaartgangers en het onderhouden van de Masdjid al Harâm (de gewijde Moskee In Mekkah) gefijk aan (de aanbidding door) degene die in Allah en het Hiernamaals gelooft en die strijdt op de Weg van Allah? Zij zijn niet gelijk bij Allah. En Allah leidt het onrechtplegende volk niet.'),
(1255, 9, 20, 'Degenen die geloven en die zijn uitgeweken en die strijden op de Weg van Allah met hun bezittingen en hun levens zijn hoger in rang bij Allah. En zij zijn degenen die de overwinnaren zijn.'),
(1256, 9, 21, 'Hun Heer verkondigt hun verheugende tijdingen over Barmhartigheid van Hem en welbehagen en Tuinen (het Paradijs) waarin voor hen een blijvende gelukzaligheid is.'),
(1257, 9, 22, 'Zij zijn daarin eeuwig levenden, voor altijd. Voorwaar, bij Allah bevindt zich een geweldige beloning.'),
(1258, 9, 23, 'O jullie die goloven, neemt jullie vaders en jullie broeders niet als leiders wanneer zij ongeloof verklezen boven geloof. En wie van jullie hen tot leiders maakt: zij zijn degenen die de onrechtplegers zijn.'),
(1259, 9, 24, 'Zeg: "Als jullie vaders en jullie zonen en jullie broeders en jullie echtgenotes en jullie familie en de bezittingen die jullie verworven hebben en de handel waarvan jullie verlies vrezen en de huizen die jullie behagen, jullie dierbaarder zijn den Allah en Zijn Boodschapper en het strijden op Zijn Weg, wacht dan tot Allah met Zijn beschikking komt. En Allah leidt het zwaar zondige volk niet,"'),
(1260, 9, 25, 'Voorzeker, Allah heeft jullie reeds in vele veldslagen geholpen en op de dag van Hoenain, toen jullie grote aantallen (manschappen) jullie trots hadden gemaakt, naar dat baate jullie niets en de aarde, met al haar wijdsheid, werd jullie nauw, Daarna wendden jullie vluchtend de rug toe.'),
(1261, 9, 26, 'Vervolgens deed Allah rust over zijn Boodschapper en over de gelovigen neerdalen en Hij zond een leger (van Engelen) dat jullie niet zagen, en Hij bestrafte degenen die ongelovig waren. En dat is de vergelding voor de ongelovigen.'),
(1262, 9, 27, 'Vervolgens aanvaardt Allah daarna het berouw van wie Hij wil. En Allah is Vergevensgezind, Meest Barmhartig.'),
(1263, 9, 28, 'O jullie die geloven: voorwaar, de veelgodenaanbidders zijn onrein. Laat hen daarom de Masdjid al Harâm (de gewijde moskee in Mekkah) niet naderen na dit jaar van hen. En als jullie armoede vrezen; Allah zal jullie rijk maken door Zijn gunst, als Hij het wil. Voorwaar, Allah is Alwetend, Alwijs.'),
(1264, 9, 29, 'Doodt hen dan die niet in Allah en het Hiernamaals geloven en die niet voor verboden houden wat Allah en zijn Boodschapper verboden hebben verklaard; en zij die de godsdienst van de Waarheid niet als godsdienst nemen, van hen aan wie de Schrift is gegeven, totdat zij het beschermgeld (Djizyah) betalen, naar vemogen, terwijl zij onderdanigen zijn.'),
(1265, 9, 30, 'En de Joden zeggen: "Oezair is de zoon van Allah," en de Christenen zeggen: "De Masîh (\'Isa) is de zoon van Allah." Dat zijn hun woorden uit ban monden. Zij doen soortgelijke uitspraken als degenen die voorheen ongelovig weren. Moge Allah hen vervloeken, Hoe kunnen zij zo afwijken?'),
(1266, 9, 31, 'Zij hebben hun schriftgeleerden en hun monniken tot heren naast Allah genomen en (ook) de Masîh de zoon van Marywn, terwijl hun niets is bevolen dan dat zij één God aaiibidden: er is geen god dan Hij. Heilig is Hij boven de deelgenoten die zij naast Hem toekennen.'),
(1267, 9, 32, 'Zij willen het Licht van Allah doven met hun monden, maar Allah wil slechts Zijn Licht volledig laten schijnen, ook al haten de ongelovigen het.'),
(1268, 9, 33, 'Hij is het Die Zijn Boodschapper heeft gezonden met de leiding en de ware godsdienst, om deze te doen zegevieren over alle godsdiensten, ook al hebben de veelgodenaanbidders er een afkeer van.'),
(1269, 9, 34, 'O jullie die geloven, voorwaar, de meesten van de schriftgeleerden en de monniken verteren de bezittingen van de mensen op onrechtmatige wijze en zij houden (hen) af van de Weg van Allah. En zij die het goud en het zilver oppotten dit niet besteden op de Weg van Allah; verkondig hen over een pijnlijke bestraffing.'),
(1270, 9, 35, 'De dag waarop dit (goud en zilver) verhit zal worden in het vuur van de Hel, dan zal ermee over hun voorhoofden, hun zijden en hun ruggen gestreken worden. (En er zal worden gezegd.) "Dat is, wat jullie voor jezelf hadden opgepot, proeft dan wat jullie plachten op te potten."'),
(1271, 9, 36, 'Voorwaar, het aantal maanden bij Allah is twaalf maanden, volgens de beschikking van Allah op de dag dat Hij de hemelen en de aarde schiep. Daarvan zij er vier gewijd. Dat is de rechte godsdienst. Doet jullie zelf dan daarin geen onrecht aan. Maar bevecht alle veelgodenaanbidders zoals zij jullie allen bevechten. en weet dat Allah met de Moettaqóen is.'),
(1272, 9, 37, 'Voorwaar, het uitstellen (van de gewijde maanden) is slechts oen toename van het ongeloof, degenen die ongelovig zijn, worden erdoor tot dwalen gebracht. Het éne jaar verklaren zij het toegestaan en het andere jaar verklaren zij het verboden, om het aantal kloppend te maken met wat Allah voor verboden heeft verklaard. Hun slechte daden werden voor hen schoonschijnend gemaakt. En Allah leidt het ongelovige volk niet.'),
(1273, 9, 38, 'O jullie die geloven, wat is er met jullie dat wanneer er tot jullie wordt gezegd: "Rukt uit op de Weg van Allah, "jullie bezwaard op de grond zakken? Hebben jullie dan meer behagen aan het wereldse leven dan aan het Hiernamaals? En de genieting van het wereldse leven is slechts kort in vergelijking tot het Hiernamaals.'),
(1274, 9, 39, 'Als jullie niet uitrukken, dan zal Hij jullie met een pijnlijke bestraffing bestraffen en een ander volk voor jullie in de plaats nemen en jullie kunnen Hem geen enkele schade toebrengen. En Allah is Almachtig over alle zaken.'),
(1275, 9, 40, 'Als jullie hem (de Profeet) niet helpen: waarlijk, Allah heeft hem reeds eerder geholpen, toen degenen die ongelovig waren hen (uit Mekkah) hadden verdreven, (en) hij de tweede van twee was toen zij zich in de grot bevonden, (en) toen hij tot zijn metgezet (Aboe Bakr) zei: "Treur niet, voorwaar, Allah is met ons." Waarna Allah Zijn rust op hen deed neerdalen en Hij hen bijstand gaf met een leger dat jullie niet zagen. En Hij verlaagde het woord van degenen die ongelovig waren tot het laagste en het Woord van Allah, dat is het hoogste. En Allah is Almachtig, Alwijs.'),
(1276, 9, 41, 'Rukt uit, licht of zwaar, en strijdt met jullie bezittingen en jullie levens op de Weg van Allah, dat is beter voor jullie, als jullie het weten.'),
(1277, 9, 42, 'Als er een nabije vergukelijke genieting en een gemakkelijk tocht zou zijn, dan zouden zij jou zeker volgen, maar de tocht lijkt hen zwaar. En zij zullen bij Allah zwefen: "Als wij ertoe in staat geweest zouden zijn, dan zouden wij met jullie zijn weggepan." Zij vernietigen zichzelf, en Allah weet dat zij zeker leugenaars zijn.'),
(1278, 9, 43, 'Moge Allah jou (Moehammad) vergeven, waarom heb jij vrijstelling gegeven (om achter te blijven), nog vóórdat jou was gebleken wie degenen waren die waarachtig waren en vóór jij wist wie de leugenaars waren?'),
(1279, 9, 44, 'Degenen die in Allah en de Laatste Dag geloven zullen jou niet om vrijstelling vragen om niet te boeven strijden met hun bezittingen en hun levens. En Allah kent de Moettaqòen.'),
(1280, 9, 45, 'Voorwaar, degenen die niet in Allah en de I.aatste Dag geloven vragen jou om vrijstelling en (zij zijn degenen) wiens harten twijfelen waardoor zij in hun twijfel heen en weer geslingerd worden.'),
(1281, 9, 46, 'En als zij (met jou ten strijde) zouden willen trokken, dan zouden zij daartoe zeker voorbereidingen hebben getroffen. Maar Allah keurde hun vertrek af, zodat Hij hen ontmoedigde. En er werd gezegd: "Blijf thuis met de thuisblijvers."'),
(1282, 9, 47, 'En als zij met jullie (ten strijde) zouden trekken, dan zouden zij voor jullie niets vermeerderd hebben dan wanorde, en zij zouden zeker tussen jullie heen en weer rennen om tussen jullie tweedracht te zaaien, en en onder jullie zijn er die geneigd zijn naar hen te luisteren. En Allah kent de onrechtplegers.'),
(1283, 9, 48, 'Voorzeker, voorheen probeerden zij reeds twee dracht te zaaien en zij verzonnen listen tegen jou, tot de Waarheid kwam en de verordening van Allah zegevierde, terwijl zij er een afkeer van hadden.'),
(1284, 9, 49, 'En onder hen zijn er die zeggen: "Geef mij vrijstelling en breng geen onheil over mij." Weet, dat zij zich (reeds) in het onheil hebben gestort, en voorwaar, de Hel onnineelt zeker de gelovigen.'),
(1285, 9, 50, 'Wanneer jou iets goeds overkomt, dan maakt het lien nijdig, en wanneer tegensspoed jou treft, dan zeggen zij: "Wij hebben van te voren reeds onze maatregelen getroffen." En zij wenden zich af, terwijl zij zich verheugen.'),
(1286, 9, 51, 'Zeg: "Niets zal ons treffen, behalve wat Allah voor ons heeft beschikt. Hij is onze Beschermer."\' En laten de gelovigen daarom op Allah hun vertrouwen stellen.'),
(1287, 9, 52, 'Zeg: "Jullie venuchten slechts voor ons één van de twee goede dingen. En wij verwachten dat Allah jullie met een bestraffing van Zijn kant zal treffen, of (met een bestraffing) door ons. Wachten jullie dan maar af, wij wachten daar met jullie op.'),
(1288, 9, 53, 'Zeg: "Geeft bijdragen, willig of onwillig, het zal nimmer van jullie worden aanvaard: voorwaar, jullie zijn een zwaar zondig volk."'),
(1289, 9, 54, 'En er is niets dat de aanvaarding van hun bijdragen tegenhoudt dan het feit dat zij waarlijk niet in Allah en zijn Boodschapper geloven, en dat zij niet tot de shalât komen, tenzij schoorvoetend, en dat zij geen bijdragen geven, behalve met weerzin.'),
(1290, 9, 55, 'Laat daarom hen bezittingen en hun kinderen geen (goede) indruk op jou maken. Allah wil ben slechts daarmee bestraffen in het wereldse leven en dat hun zielen weggaan terwijl zij ongelovigen zijn.'),
(1291, 9, 56, 'En zij (de huichelaars) zweren bij Allah dat zij zeker tot jullie behoren. Zij behoren niet tot jullie, want zij zijn een lafhartig volk.'),
(1292, 9, 57, 'Als zij een toevluchtsoord, of grotten of holen zouden vinden, dan zouden zij zich daarheen wenden, terwijl zij zich haasten.'),
(1293, 9, 58, 'En onder ben zijn er die aanmerkingen op jou maken over (de verdeling van) de zakât. Als hun dan ervan wordt gegeven, dan zijn zij tevreden, maar wanneer hun er niet van wordt gegeven, zie, dan zijn zij boos.'),
(1294, 9, 59, 'En als zij tevreden zouden zijn met wat Allah en zijn Boodschapper hun hebben gegeven en zij zeiden: "Allah is voor ons voldoende, Allah zal ons van zijn grote gunst geven, en (ook) zijn Boodschapper, voorwaar, op Allah vestigen wij onze hoop." (Dan zou dat beter voor hen zijn.)'),
(1295, 9, 60, 'Voorwaar, de zakât is slechts voor de armen en de behoeftigen en de werkenden (aan de inzameling ervan) en de Moe\'allaf en voor (het vrijkopen) van de slaven, en de schuldenaren en om (uit te geven) op de Weg van Allah en voor de reiziger (zonder proviand), als een plicht tegenover Allah. En Allah is Alwetend, Alwijs.'),
(1296, 9, 61, 'En onder ben zijn er die de Profeet kwetsen en zij zeggen: "Hij is een oor (dat naar iedereen luistert). Zeg: "Een oor dat goed is voor jullie, bij gelooft in Allah en bij gelooft de gelovigen en hij is een genade voor degenen onder jullie die geloven." En degenen die de Boodschapper van Allah kwetsen: voor hen is er een pijnlijke bestraffing.'),
(1297, 9, 62, 'Zij zweren jullie bij Allah om jullie te behagen, terwijl Allah en zijn Boodschapper er meer recht op hebben dat zij hen behagen, als zij gelovigen zijn.'),
(1298, 9, 63, 'Weten zi (de huichelaars) niet dat voor degene die Allah en zijn Boodschapper uitdagen het vuur van de Hel (terecht) is, zij zullen daarin eeuwig levenden zijn. Dat is de geweldige vernedering.'),
(1299, 9, 64, 'De huichelaars vrezen dat er over hen een Soerah (hoofdstuk) wordt neergezonden die (hen) bekend maakt wat in hun harten is. Zeg: "Spotten jullie maar: voorwaar, Allah zal dat waar jullie voor vrezen zeker bekend maken".'),
(1300, 9, 65, 'En als jij (O Moehammad) ven vraagt (over hun gespot), dan zullen zij zeker antwoorden: "Wij kletsten en schertsten maar wat." Zeg: "Plachten jullie de spot te drijven met Allah en Zijn Verzen en Zijn Boodschapper?"'),
(1301, 9, 66, 'Verontschuldigt jullie maar niet, jullie zijn ongelovig geworden nadat jullie geloofden. Als Wij een groep van jullie vergeven (vanwege hun berouw) dan zullen Wij een andere groep bestraffen omdat zij misdadigers waren.'),
(1302, 9, 67, 'De huichelaars en de huichelaarsters zijn eender, zij sporen aan tot het verwerpelijke en zij verbieden het behoorlijke. Zij houden hun handen dicht (uit gierigheid). Zij vergaten Allah, zodat Hij hen vergat. Voorwaar, de huichelaars zijn de zwaar zondigen.'),
(1303, 9, 68, 'Allah heeft de huichelaars, de huichetaarsters en de ongelovigen het vuur van de Hel aangezegd, zij zijn daarin eeuwig levenden. Het is voldoende voor hen. En Allah heeft hen vervloekt. En voor hen is er een blijvende bestraffing.'),
(1304, 9, 69, '(Zij zijn) zoals degenen vóór jullie, zij waren sterker in kracht dan jullie en rijker aan bezittingen en kinderen. Zij hebben genoten van hun deel en jullie hebben genoten van jullie deel, zoals degenen vóór jullie hebben genoten. En jullie zijn verdiept (in ijdele gesprekken) zoals zij verdiept waren. Zij zijin degenen wiens daden vruchteloos zijn, in dit leven en in het Hiernamaals. Zij zijn degenen die de verliezers zijn.'),
(1305, 9, 70, 'Heeft het bericht hun niet bereikt over degenen vóór hen; het volk van Noeh, en de \'Âd en de Tsamôed, en het volk van Ibrâhîm, en de bewoners van Madyan en de bewoners van de geruïneerde steden? Hun Boodschappers waren tot ben gekomen met de duidelijke bewijzen. En Allah heeft hen nimmer onrecht aangedaan, maar zij hebben zichzelf onrecht aangedaan.'),
(1306, 9, 71, 'En de gelovige mannen en de gelovige vrouwen zijn elkaars helpers, zij roepen op tot het behoorlijke en verbieden het verwerpelijke en zij onderhouden de shalât en geven de zakât en zij gehoorzamen Allah en zijn Boodschapper. Zij zijn degenen die Allah zal begenedigen. Voorwaar, Allah is Almachtig, Alwijs.'),
(1307, 9, 72, 'Allah heeft de gelovigen mannen en de gelovige vrouwen Tuinen (het Paradijs) beloofd waar onder door de rivieren stromen, zij zijn daarin eeuwig, levenden. En goede woonplaatsen in de Tuinen van \'Adn (Eden). En het welbehagen van Allah is groter. Dat is de geweldige overwinning.'),
(1308, 9, 73, 'O Profeet, bevecht de ongelovigen en de huichelaars en treed hard tegen hen op. En hun verblijfplaats is de Hel, en dat is de slechtste bestemming.'),
(1309, 9, 74, 'Zij zwoeren bij Allah wat zij zeiden, terwijl zij voorzeker het woord van ongeloof uitspraken en zij zijn ongelovig geworden na hun Islam, en zij verlangden wat zij niet konden uitvoeren. Fn zij verweten slechts (toen) Allah en zijn Boodschapper hen (de gelovigen) rijkdom schonken, van Zijn gunst. Als zij dan berouw tonen, dan zou dat beter voor hen zijn, maar als zij zich afwenden, dan zal Allah hen straffen met een pijnlijke bestraffing, in dit leven en in het Hiernamaals. En op de aarde is er voor hen geen bescherma en geen helper.'),
(1310, 9, 75, 'En onder hen zijn er die aan Allah beloofden: "Als Hij ons van Zijn gunst schenkt, dan zullen wij bijdragen geven en dan zullen wij zeker tot de rechtschapenen behoren."'),
(1311, 9, 76, 'Maar wanneer Hij hun dan van Zijn gunst heeft geschonken, dan zijn zij er gierig mee en wenden zij zich af (van hun belofte). En zij wenden zich af.'),
(1312, 9, 77, 'Daarom veroorzaakte (de gierigheid) huichelachtigheid in hun harten, tot de Dag waarop zij Hem ontmoeten, omdat zij de aan Allah gedane belofte hebben gebroken en omdat zij plachten te liegen.'),
(1313, 9, 78, 'Weten zij niet dat Allah hun geheim kent en hun vertrouwlijke gesprekken en dat Allah de Kenner van het onwaarneembare is?'),
(1314, 9, 79, 'Degenen die beledigende aanmerkingen maken over de vrijwillige gevers onder de gelovigen over de aalmoezen en over degenen die vanwege hun armoede niets kunnen vinden (om te geven), tenzij met de grootste moeite, en die dan de spot met hen drijven: Allah zal de spot op hen terugwerpen en voor hen is cr een pijnlijke bestraffing.'),
(1315, 9, 80, 'Of jij nu vergeving voor hen vraagt, of geen vergeving voor hen vraagt (het maakt geen verschil); ook al zou jij zeventig keer vergeving voor hen vragen; nooit zal Allah hen vergeven. Dat is omdat zij niet in Allah en zijn Boodschapper geloven. En Allah leidt het zwaar zondige volk niet.'),
(1316, 9, 81, 'De achterblijvers (in de tijd van de Slag bij Tahôek) verheugden zich er over dat zij achter de Boodschapper van Allah waren gebleven en zij hadden er een afkeer van om met hun bezittingen en hun levens te strijden op de Weg van Allah, en zij zeiden: "Rukt niet uit in de hitte!" Zeg: "De hitte van de Hel is erger." Als jullie dat toch begrepen!'),
(1317, 9, 82, 'Laten zij daarom weinig lachen en veel huilen, als een vergelding voor hetgeen zij plachten te bedrijven.'),
(1318, 9, 83, 'Als Allah jou (O Moehammad) dan (veilig) terugbrengt naar een groep van hen, dan vragen zij jou toestemming om (ten strijde) te trekken. Zeg dan: "Jullie zullen nooit met mij uittrekken en nimmer zullen jullie met mij een vijand bevechten, voorwaar, het behaagde jullie de eerste keer om te blijven zitten, blijft daarom zitten bij de thuisblijvers.'),
(1319, 9, 84, 'En verricht nooit een shalât over een dode van hen en sta niet bij zijn gra£: voorwaar, zij geloven niet in Allah en zijn Boodschapper en zij stierven, en zij waren zwaar zondig.'),
(1320, 9, 85, 'En laat hun bezittingen en hun kinderen op jou geen (goede) indruk maken; Allah wenst hen slechts daarmee te straffen in dit leven en dat hun zielen weggaan terwijl zij ongelovig zijn.'),
(1321, 9, 86, 'En wanneer er een Soerah (een hoofdstuk) wordt neergezonden (waarin wordt gemaand:) "Gelooft in Allah en strijdt met Zijn Boodschapper," dan vragen de welgestelden onder hen om toestemming, zij zeggen: "Laat ons maar achter bij de thuisblijvers."'),
(1322, 9, 87, 'Het behaagt hen om bij de thuisblijvers te behoren en hun harten zijn vergrendeld, omdat zij (het) niet begrijpen.'),
(1323, 9, 88, 'Maar de Boodschapper en degenen die met hem geloven, zij strijden met hun bezittingen en hun levens. En nj zijn degenen voor wie er de goede dingen zijn, en zij zijn degenen die de welslagenden zijn.'),
(1324, 9, 89, 'Allah heeft voor hen een Tuin (het Paradijs) bereid waar onder door de rivieren stromen, zij zijn daarin de eeuwig levenden. Dat is de geweldige overwinning.'),
(1325, 9, 90, 'De aanbieders van verontschuldigingen onder de bedoeïenen komen, opdat hun vrijstelling zal worden gegeven, en degenen die Allah en Zijn Boodschapper loochenen, gaan zitten. Een pijnlijke bestrafring zal de ongelovigen onder hen treffen.'),
(1326, 9, 91, 'Er rust geen zonde op de zwakken, de zieken, noch op degenen die niets kunnen vinden om te besteden, wanneer zij oprecht zijn tegenover Allah en zijn Boodschapper. Tegen de weldoeners is er geen weg (cm ben van zonde te beschuldigen). En Allah is Vergevensgezind, Meest Barmhartig.'),
(1327, 9, 92, 'En (ook) niet op degenen die, toon zij naar jou kwamen opdat jij lien zou vervoeren. (En) jij zei: "Ik kan niets vinden waarop jullie vervoerd kunnen worden," (en) zij draaiden zich om, terwijl hun ogen overstroomden van tranen, uit droefheid omdat zij niets konden vinden om als bijdrage te besteden.'),
(1328, 9, 93, 'Voorwaar, slechts tegen degenen die jou om vrijstelling vragen terwijl zij rijk zijn, is er een weg (om hen te beschuldigen). Het behaagde hen om tot de thiuisblijvers te behoren en Allah heeft hun harten vergrendeld zodat zij niet weten.'),
(1329, 9, 94, 'Zij zullen jullie verontschuldigen aanbieden wanneer jullie tot hen zijn teruggekeerd. Zeg: "Biedt geen verontschuldigingen aan, wij zullen jullie nooit geloven. Voorzeker, Allah heeft ons ingelicht over jullie (ware) houding. En Allah en Zijn Boodschapper zullen jullie daden zien. Daarna zullen jullie worden teruggevoerd tot de Kenner van het onwaarneembare en het waarneembare en dan zal Hij jullie inlichten over wat jullie plachten te doen."'),
(1330, 9, 95, 'Zij zullen tegenover jou bij Allah zweren, wanneer jullie tot hen zijn terugkeerd, opdat jullie je van hen afwenden. Wendt jullie dan van hen af. voorwaar, zij zijn onrein en hun verblijfplaats is de Hel, als vergelding voor wat zij plachten te verrichten.'),
(1331, 9, 96, 'Zij zweren tegenover jullie opdat jullie welbehagen aan hen zullen hebben, en als jullie welbehagen aan hen hebben: voorwaar, Allah heeft geen welbehagen aan het zwaar zondige volk.'),
(1332, 9, 97, 'De bedoeïenen zijn het ergst in ongeloof en huichelarij en het ligt ben het meest om de bepalingen die Allah heeft neergezonden aan Zijn Boodschapper niet te kennen. En Allah is Alwetend, Alwijs.'),
(1333, 9, 98, 'Onder de bedoeïenen zijn er die hun bijdragen als een boete beschouwen en zij wachten af totdat jullie getroffen worden door tegenslagen. Moge zij zelf door een tegenslag getroffen worden! En Allah is Alhorend, Alwetend.'),
(1334, 9, 99, 'En onder de bedoeïenen zijn er die geloven in Allah en de Laatste Dag en die hun bijdragen beschouwen als middelen om Allah dichter te naderen, en om de smeekbede van de Boodschappper (te verkrijgen). Weet, voorwaar, het is voor hen een middel tot toenadering (tot Allah). En Allah zal hen Zijn Barmbartigheid doen binnengaan. Voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(1335, 9, 100, 'De allereerste (Moslims) van de Ukgewekenen (Moehâdjirîn) en de Anshâr en degenen die hen volgden in goede daden. Allah heeft welbehagen aan hen en zij hebben welbehagen aan Hem. Hij heeft voor tien Tuinen (in het Pamdijs) bereid waar onder door de rivieren stromen, zij zijn daarin eeuwig levenden. Dat is de geweldige overwinning.'),
(1336, 9, 101, 'En onder de bedoeïenen in jouw omgeving bevinden zich huichelaars, en (ook) onder de bewoners van Medinah, zij volharden in hun huichelarij. Jij kent hen niet, maar Wij kennen hen wel. Wij zullen hen twee maal straffen, dan zullen zij worden teruggevoerd naar een geweldige bestraffing.'),
(1337, 9, 102, 'En anderen die hun zonden hebben bekend, zij mengen een goede daad met een andere slechte (daad). Hopelijk zal Allah hun berouw aanvaarden. Voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(1338, 9, 103, 'Neem van de bezittingen de zakkât, jij reinigt en zuivert hen daarmee (O Moehammad), en verricht een smeekbede, want jouw smeekbede is een geruststelling voor hen. En Allah is Alhorend, Alwetend.'),
(1339, 9, 104, 'Weten zij dan niet dat het Allah is Die het bereouw aanvaardt van Zijn dienaren en Die de zakât aanvaardt en dat Allah de Vergevensgezinde, de Meest Barmhartige is?'),
(1340, 9, 105, 'En zeg (O Moehammad): "Allah zal jullie werken zien en ook Zijn Boodschapper en de gelovigen. En jullie zullen worden teruggevoerd tot de Kenner van het onwaarneembare en het waarneembare, dan zal hij jullie inlichten omtrent hetgeen jullie plachten te doen.'),
(1341, 9, 106, 'En anderen worden in afwachting gesteld van het oordeel van Allah: of Hij hen straft of hun berouw aanvaardt. En Allah is Alwetend, Alwijs.'),
(1342, 9, 107, 'En degenen (de huichelaars) die een moskee hebben gebouwd om schade en ongeloof en splitsing onder de gelovigen te veroorzaken, en als een hinderlaag van degenen die eerder tegen Allah en Zijn Boodschapper vochten: en zij zullen zeker zweren: "Wij wensen niets den het goede." Maar Allah is er Getuige van dat zij zeker leugenaars zijn.'),
(1343, 9, 108, 'Verricht daarin nooit de shalât. Waarlijk, een moskee die vanaf de eerste dag is gebouwd op Taqwa, heeft er meer recht op dat jij daarin de shalât verricht. Daarin zijn mannen die ervan houden om zich te reinigen. En Allah houdt van bhn die zich reinigen.'),
(1344, 9, 109, 'Is hij, die zijn bouwwcrk op vrees voor Allah heeft gegrondvest en op Zijn welbehagen, dan niet beter? Of hij dan, die zijn bouwwerk heeft gegrondvest op de rand van een ravijn, dat dan met hem in het vuur van de Hel stort? Allah leidt het onrechtplegende volk niet.'),
(1345, 9, 110, 'Het bouwwerk dat zij hebben gebouwd zal een bron van onrust blijven in hun harten, tenzij hun harten in stukken zijn gebroken. En Allah is Alwetend, Alwijs.'),
(1346, 9, 111, 'Voorwaar, Allah heeft van de gelovigen hun levens en bezittingen gekocht omdat er voor hen het Paradijs is. Zij strijden op de Weg van Allah, zodat zij doden of gedood wordenm als een belofte waar Hij Zich aan heeft verbonden, een Waarheid (die staat verineld) in de Taurât, en de lndjîl en de Koran. En wie is zijn belofte meer trouw dan Allah? Verheugt jullie daarom over jullie koop die jullie met Hem hebben gestoten. En dat is de geweldige overwinning.'),
(1347, 9, 112, '(Zij zijn) de berouwvollen, en de dienenden, en de prijzenden, en de rondtrekkenden, en de buigenden, en de knielenden, en de oproepers tot het behoorlijke en de weerhouders van het verwerpelijke, de wakers over de bepalingen van Allah. En verkondig een verheugende tijding aan de gelovigen.'),
(1348, 9, 113, 'Het past de Profect en degenen die geloven niet dat zij de veelgodenaanbidders om vergeving vragen, ook al zijn zij verwanten, nadat het hen duidelijk is geworden dat zij de bewoners van de Hel zijn.'),
(1349, 9, 114, 'En Ibrâhîm\'s verzoek om vergeving voor zijn vader was slechts vanwege een belofte die hij aan hem had gedaan. Toen het hem dan duidelijk was geworden dat hij een vijand van Allah was, verbrak hij (de band) met hem. Voorwaar, Ibrâhîm was zeker nederig, zachtaardig.'),
(1350, 9, 115, 'En Allah doet nooit een volk dwalen nadat Hij hen heeft geleid, totdat Hij hun duidelijk heeft gemaakt waarvoor zij beducht moeten zijn. Voorwaar, Allah is Alwetend over alle dingen.'),
(1351, 9, 116, 'Voorwaar, aan Allah behoort de heerschappij van de hemelen en de aarde, Hij doet leven en Hij doet sterven, en er is voor jullie buiten Allah geen beschermer en geen helper.'),
(1352, 9, 117, 'Voorzeker, Allah heeft het berouw van de Profeet aanvaard en van de Uitgewekenen en de Helpers die hem volgden in het uur van de nood, nadat de herten van een groep van hen bijna geneigd was (zich af te wenden), daarna aanvaardde Hij hun berouw. Voorwaar, Hij is voor hen Meest Genadig, Meest Barmhartig.'),
(1353, 9, 118, 'En (ook) tegenover de drie die waren achtergebleven totdat de aardc mct (al) haar wijdsheid, voor hen te nauw werd en zij benauwdheid voelden en zij ervan overtuigd waren dat er geen toevluchtsoord was tegen de (bestraffing van) Allah, behalve bij Hem. Daarna aanvaardde Hij hun berouw opdat zij berouwvol zouden blijven. Voorwaar, Allah, Hij is Vergevensgezind, Meest Barmhartig.'),
(1354, 9, 119, 'O jullie die geloven, vreest Allah en weest met de waarachtigen.'),
(1355, 9, 120, 'En het past de bewoners van Medinah niet, noch ben die in de omgeving verblijven van de bedoevenen, dat zij achterblijven bij de Boodschapper van Allah, noch dat zij hun eigen leven verkiezen boven zijn (Moehammad\'s) leven. Dat is omdat noch dorst, noch vermoeienis, noch honger hen treft op de Weg van Allah. En zij betreden geen plaats waarmee zij de woede van de ongelovigen opwekken, noch nemen zij iets af van een vijand of er wordt voor hen daarmee een goede daad opgeschreven. Voorwaar, Allah doet de beloning van de weldoeners niet verloren gaan.'),
(1356, 9, 121, 'En zij geven geen bijdrage, klein of groot, en zij doorkruisen geen vallei, of het wordt voor hen opgeschreven, opdat Allah hen zal belonen naar het beste van wat zij plachten te doen.'),
(1357, 9, 122, 'En het past de gelovigen niet dat zij allen (ten strijde) uitrukken. Waarom rukt niet van elke groep een aantal uit, opdat zij (de achtergeblevenen) belzrip verkrijgen over de godsdienst, zodat zij hun volk zullen waarschuwen wanneer het tot hen is teruggekeerd. Hopelijk zullen zij zichzelf behoeden.'),
(1358, 9, 123, 'O jullie die geloven, bevecht de ongelovigen in jullie naaste omgeving en laten zij hardheid bij jullie aantreffen. En weet dat Allah met de Moettaqôen is.'),
(1359, 9, 124, 'En wanneer er een hoofdstuk is neergezonden dan zijn er onder hen (de huichelaars) die zeggen: "Bij wie van jullie is door dit (hoofdstuk) het geloof toegenomen?" Wat degenen die geloven dan betreft: hun geloof is toegenomen en zij verheugen zich.'),
(1360, 9, 125, 'En wat degenen in wiens hart een ziekte is betreft: bij hen stapelt dit (nieuw) ongeloof op hun ongeloof en zij sterven terwijl zij ongelovigen zijn.'),
(1361, 9, 126, 'En zien zij dan niet dat zij in elk jaar één of twee keer beproefd worden? Daarop tonen zij geen berouw en zij trekken er geen lering uit.'),
(1362, 9, 127, 'En wanneer een hoofdstuk wordt neergezonden, dan kijken zij elkaar aan (zeggende:) "Is er iemand (van de gelovigen) die jullie ziet?" Daarna wenden zij zich af. Allah heeft hun harten afgcwend omdat zij een volk zijn dat niet begrijpt.'),
(1363, 9, 128, 'Voorzeker, er is een Boodschapper tot jullie gekomen uit jullie eigen midden. Zwaar voor hem is jullie lijden, vurig wenst hij het goede voor jullie, voor de gelovigen is bij liefdevol en barmhartig.'),
(1364, 9, 129, 'Als zij zich dan afwenden, zeg dan: "Allah is mij voldoende, geen god is er dan Hij!, op Hem vertrouw ik, en Hij is de Heer van de Geweldige Troon."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Alif Lâm Râ. Dit zijn de Verzen van het wijze Boek.'),
(1366, 10, 2, 'Is het voor de mensen iets verwonderlijks dat Wij aan een man uit hun midden openbaarden: "Waschuw de mensen en verkondig verheugende tijdingen aan degenen die geloven: er is een eervolle plaats is bij hun Heer."\' De ongelovigen zeggen: "Voorwaar, dit is zeker een duidelijke tovenaar."'),
(1367, 10, 3, 'Voorwaar, jullie Heer is Allah, Die de hemelen en de aarde in zes dagen (perioden) heeft geschapen, en Hij zetelde zich op de Troon. Hij regelt het bestuur, en er is geen voorspreker, behalve na Zijn verlof Dat is Allah, jullie Heer, aanbidt daarom Hem. Laten jullie je niet vermanen?'),
(1368, 10, 4, 'Bij Hem is jullie plaats van terugkeer van jullie allen, als een ware belofte van Allah. Voorwaar, Hij begint de schepping en vervolgens herhaalt Hij deze, opdat Hij degenen die geloven en goede werken verrichten rechtvaardig zal belonen. En degenen die niet geloven, voor hen zijn er kokende dranken en een pijnlijke bestraffing wegens wat zij niet geloofden.'),
(1369, 10, 5, 'Hij is Degene Die de zon heeft gemaakt tot een (stralend) schijnsel en de maan tot een licht en Hij bepaalde haar standen opdat jullie de jaartelling zouden kennen en de (tijds-)berekening. Allah heeft dat slechts in Waarheid geschapen. Hij zet de Tekenen uiteen aan een volk dat weet.'),
(1370, 10, 6, 'Voorwaar, in de afwisseling van nacht en dag en (in) wat Allah heeft geschapen in de hemelen en op de aarde zijn zeker Tekenen voor een volk dat (Allah) vreest.'),
(1371, 10, 7, 'Voorwaar, degenen die de ontmoeting met Ons niet verwachten en die tevreden zijn met het wereldse leven en die zich er gerust in voelen en zij die achteloos tegenover Onze Tekenen staan.'),
(1372, 10, 8, 'Zij zijn degenen wiens verblijfplaats de Hel is, wegens wat zij plachten te verrichten.'),
(1373, 10, 9, 'Voorwaar, degenen die geloven en goede werken verrichten. hun Heer zal ben leiden vanwege hun geloof, (naar de plaats) waar onder door de rivieren stromen in de Tuinen van gelukzaligheid (het Paradijs).'),
(1374, 10, 10, 'Zij smeken daarin: "Soebhânaka Allahoemma," (Heilig bent U, O Allah) en hun begroeting daarin is: "Salâm!" (Vrede, veiligheid). En hun laatste smeekbede is: "Alhamdoe lillâhi Rabbil Âlamin!" (Alle lof zij Allah, de Heer der Werelden.)'),
(1375, 10, 11, 'En als Allah het kwade voor de mensen zou bespoedigen zoals zij om bcspoediging van het goede vragen, dan zou hun termijn zeker reeds zijn beëindigd. En Wij laten degenen die de ontmoeting met Ons niet verwachten rusteloos verkeren in hun buitensporigteid.'),
(1376, 10, 12, 'En wanneer tegenslag de mens treft, dan roept hij Ons aan, liggend, zittend of staande. En als Wij dan de tegenslag van hem hebben weggenomen, dan vervolgt hij (zijn slechte pad) alsof hij Ons nooit heeft aangeroepen wegens een tegenslag die hem trof. Zo is voor de buitensporigen schoonschijnend gemaakt wat zij plachten te doen.'),
(1377, 10, 13, 'En voorwaar, Wij hebben de generaties vóór hen vernietigd tom zij onrecht pleegden, terwijl hun Boodschappers tot hen waren gekomen met de duidelijke bewijzen, maar zij geloofden niet Zo vergolden Wij het boosaardige volk.'),
(1378, 10, 14, 'Vervolgens maakten Wij juli ie tot gevohnachtigden op aarde zodat Wij zouden zien hoe jullie handelen.'),
(1379, 10, 15, 'En wanneer Onze duidelijke Verzen aan hen worden voorgelezen, dan zeggen degenen die de ontmoeting met Ons niet verwachten: "Breng een andere Koren dan deze, of verander hem." Zeg: "Het past mij niet dat ik hem verander uit eigen wil, ik volg niets dan wat aan mij is geopenbaard. Voorwaar, ik vrees, wanneer ik mijn Heer ongehoorzaam ben, de bestraffing van een geweldige Dag."'),
(1380, 10, 16, 'Zeg: "Als Allah het had gewild, dan had ik het niet aan jullie voorgedragen en had Hij hem niet aan jullie bekend gemaakt. En voorzeker, ik heb een leeftijd lang vóór hem (de Koran) temidden van jullie geleefd. Denken jullie dan niet na?"'),
(1381, 10, 17, 'Wie is dan onrechtvaardiger dan degene die een leugen over Allah verzint of die liegt over Zijn Verzen. Voorwaar, de misdadigers zullen niet slagen.'),
(1382, 10, 18, 'En zij aanbidden naast Allah wat hen niet schaadt en niet baat, en zij zeggen: "Dezen zijn onze voorsprekers bij Allah." Zeg. "Willen jullie Allah inlichten over wat Hij niet kent in de hemelen en op de aarde?" Heilig is Hij en Verheven boven de deelgenoten die zij aan Hem toekennen.'),
(1383, 10, 19, 'En de mensheid behoorde slechts tot één godsdienst, maar zij waren het oneens met elkaar. En ware het niet dat er een Woord van jouw Heer was vooruitgegaan, dan zou er tussen hen zeker reeds zijn beslist over dat waarover zij het met elkaar oneens zijn.'),
(1384, 10, 20, 'En zij zeggen: "Waarom is er niet een Teken aan hem neergezonden van zijn Heer." Zeg dan: \'"Voorwaar, het onwaarneembare behoort slechts aan Allah. Wachten jullie dan, waarlijk, ik ben met jullie één van de wachtenden."'),
(1385, 10, 21, 'En als Wij de mensen Barmhartigheid deden proeven nadat tegenspoed hen had getroffen, dan is er bij ben een list tegen Onze Tekenen. Zeg: "Allah is sneller met een plan." Voorwaar, Onze gezanten (Engelen) schrijven op wat jullie beramen.'),
(1386, 10, 22, 'Hij is het die jullie in staat stelt om over land en over de zee te reizen, totdat wanneer jullie in de schepen zijn en deze met lien vooftvaren met een gunstige wind en zij zich daarover verheugen, en er dan een stormachtige wind tot hen komt. En wanneer de golven vaa alle kanten tot hen komen en zij ervan overtuigd zijn dat zij door hen zijn ingesloten, dan roepen zij Allah aan, Hem zuiver aanbiddend: "Als U ons hieruit redt, dan zullen wij zeker tot de dankbaren behoren."'),
(1387, 10, 23, 'Toen Hij hen dan gered had. handelden zij buitensporig op aarde, zonder recht. O mensen, jullie buitensporigheid is slechts ten nadele van jullie, (het is) als een genieting van het wereldse leven, Vervolgens is jullie terugkeer tot Ons, waarna Wij jullie zullen inlichten over wat jullie plachten te doen.'),
(1388, 10, 24, 'Voorwaar, de gelijkenis van het wereldse leven is als water dat Wij uit de hemel neerzenden, waardoor he de gewassen van de aarde in een rnengeling doet groeien, waarvan de mensen en het vee eten. Totdat, wanneer de aarde haar versierselen heeft aangenomen en zij mooi is geworden en haar bewoners menen dat zij machthebbers zijn over haar, Onze bestraffing tot hen komt, in de nacht of overdag, waarop Wij haar tot een afgemaaid veld maken, alsof er de dag ervoor niets groeide. Zo zetten Wij de Tekenen uiteen aan een volk dat nadenkt.'),
(1389, 10, 25, 'En Allah roept naar het Huis van de Vrede (Dâroessalâm, het Paradijs) en Hij leidt wie Hij wil naar een recht Pad.'),
(1390, 10, 26, 'Voor degenen die het goede verrichten is er het beste en meer. Grauwheid noch vernedering zal hun gezichten bedekken. Zij zijn degenen die de bewoners van het Paradijs zijn, zij zijn daarin eeuwig levenden.'),
(1391, 10, 27, 'En degenen die slechte daden verrichten: de vergelding van de slechte daad is met het daaraan gelijke, en vernedering, bedekt hen. Er zal voor hen tegen Allah geen verdediger zijn. Het is alsof hun gezichten zijn bedekt inet stukken vaji de nacht, als een duisternis. Zij zijn de bewoners van de Hel, zij zijn daarin eeuwig levenden.'),
(1392, 10, 28, 'En op die Dag verzamelen Wij hen allen, vervolgerns zeggen Wij tot degenen die (aan Allah) deelgenoten toekenden: "Op jullie plaats, jullie en jullie afgoden!" Dan zullen Wij onderscheid tussen hen maken. En hun afgoden zullen zeggen: "Jullie hebben ons niet aanbeden.'),
(1393, 10, 29, 'Allah is voldoende als getuige tussen ons en jullie. Voorwaar, Wij waren zeker onwetend omtrent jullie aanbidding."'),
(1394, 10, 30, 'Op die Dag zal iedere ziel worden beproefd met wat zij voorheen heeft gedaan en zij zullen worden teruggevoerd tot Allah, hun ware Heer, en wat zij plachten te verzinnen, zal van hen weggaan.'),
(1395, 10, 31, 'Zeg: "Wie schenkt jullie voorzieningen uit de hemel en de aarde," of: "Wie heeft macht over (het scheppen van) het horen en het zien en wie brengt het levende voort uit het dode en wie brengt het dode voort uit het levende, en wie verordent het bestuur?" Zij zullen zeggen: "Allah," Zeg dan: "Zullen jullie (Allah) dan niet vrezen?"'),
(1396, 10, 32, 'Dat is Allah, jullie ware Heer. En na de Waarheid, is er niets dan de dwaling. Hoe komt het dan dat jullie worden afgeleid?'),
(1397, 10, 33, 'Zo werd het Woord van jouw Heer tegenover degenen die verdorven handelden bewaarheid. Voorwaar. zij geloven niet.'),
(1398, 10, 34, 'Zeg (O Moehammad): "Zijn er onder jullie afgoden die de schepping beginnen en deze vervolgens herhalen?" Zeg: "Allah begint de schepping en herhaalt deze vervolgens, hoe komt het dan dat jullie worden belogen?"'),
(1399, 10, 35, 'Zeg: "Zijn er onder jullie afgoden die naar de Waarheid leiden? Zeg: "Allah leidt naar de Waarheid." Heeft Hij die naar de Waarheid leidt er meer recht op om gevolgd te worden, of hij, die geen leiding geeft maar zelf geleid moct worden? Wat is er dan met jullie? Hoe oordelen jullie?'),
(1400, 10, 36, 'En de meesten van hen volgen niets dan een vermoeden. Voorwaar, het onzekere heft de Waarheid niet op. Voorwaar, Allah weet wat zij doen.'),
(1401, 10, 37, 'En het is omnogelijk dat deze Koran is gemaakt door iemand anders dan Allah, maar (hij is er) als bevestiging van wat ervoor was en als uitleg van de Schrift. Daaraan is geen twijfel, (hij komt) van de Heer der Werelden.'),
(1402, 10, 38, 'Of zeggen zij: "Hij (Moehammed) heeft hem verzonnen." Zeg: "Komt dan met een hoofdstuk dat daaraan gelijkwaardig is en roept aan wie jullie kunnen, buiten Allah, als jullie waarachtigen zijn."'),
(1403, 10, 39, 'Nee, zij loochenden zelfs de kennis over hem (de Koran), die zij niet kunnen bevatten, en de uitleg ervan is nog niet tot hen gekomen. Zo loochenden ook degenen vóór hen. Zie dan hoe het einde was van de onrechtplegers.'),
(1404, 10, 40, 'En onder hen zijn er die erin geloven, en onder hen zijn er die er niet in geloven, en jouw Heer kent de verderfzaaiers beter.'),
(1405, 10, 41, 'En als zij jou loochenen, zeg dan: "Voor mij mijn daden en voor jullie jullie daden, jullie zijn niet aansprakelijk voor wat ik doe en ik ben niet aansprakelijk voor wat jullie doen."'),
(1406, 10, 42, 'En onder hen zijn er die naar jou luisteren. Kan jij dan de doven (van hart) doen horen, ook wanneer zij hun verstand niet gebruiken?'),
(1407, 10, 43, 'Voorwaar, en onder hen zijn er die naar jou kijken (en de Tekenen van jouw Profeetschap zien). Kan jij dan de blinden (van hart) leiden, ook wanneer zij niet zien?'),
(1408, 10, 44, 'Voorwaar, Allah doet de niensen geen enkel onrecht aan, maar de mensen doen zichzelf onrecht aan.'),
(1409, 10, 45, 'En (gedenkt) de Dag waarop Hij hen bijeenbrengt, (dan is het voor hen) alsof zij niet langer (op aarde) hebben vertoefd dan één moment van de dag. Zij kennen elkaar (op die Dag). Voorzeker, zij die de ontmoeting met Allah loochenen, zullen verlies lijden en zij waren geen rechtgeleiden.'),
(1410, 10, 46, 'Of Wij jou nu een gedeelte van wat Wij hen (van de bestraffing) hebben aangezegd laten zien, of dat Wij jou wegnemen: later is tot Ons hun terugkeer. Allah is Getuige van wat zij doen.'),
(1411, 10, 47, 'En voor iedere gemeenschap is er een Boodschapper. Wanneer dan hun Boodschapper is gekomen, dan wordt er onder hen rechtvaardig geoordeeld, en hun zal geen onrecht worden aangedaan.'),
(1412, 10, 48, 'En zij zeggen: "Wanneer is deze aanzegging. als jullie waarachtigen zijn?"'),
(1413, 10, 49, 'Zeg (O Moehammad): "Ik heb voor mijzelf geen macht om schade te voorkomen, noch om iets van nut te verkrijgen, behalve wat Allah wil." Voor elke gemeenschap is er een eindtijd. Wanneer hun eindtijd is gekomen, dan kunnen zij het geen moment uitstellen, noch kunnen zij het bespoedigen.'),
(1414, 10, 50, 'Zeg: "Wat denken jullie, wanneer Zijn bestraffing tot jullie komt, in de nacht of overdag?" Waarom bespoedigen de misdadigers die dan?"'),
(1415, 10, 51, 'Vervolgens, wanneer (de bestraffing) heeft plaatsgevonden, dan geloven jullie erin? Nu? Terwijl jullie waarlijk om bespoediging ervan plachten te vragen?'),
(1416, 10, 52, 'Daarna zal tot degenen die onrecht pleegden worden gezegd: "Proeft de eeuwige bestraffing, jullie worden vergolden voor niets anders dan wat jullie plachten te bedrijven."'),
(1417, 10, 53, 'En zij vragen jou: "Is het waar?" Ze.g: "Ja, bij mijn Heer: voorwaar, het is zeker de Waarheid, en jullie kunnen het niet ontvluchten."'),
(1418, 10, 54, 'En wanneer iedere onrecht plegende mens zou beschikken over alles wat er op de aarde is, dan zou bij zich ennee willen vrijkopen. En zij verbergen hun spijt wanneer zij de bestraffing zien. En er zal tussen hen rechtvaardig worden geoordeeld, en hun zal geen onrecht worden aangedaan.'),
(1419, 10, 55, 'Weet voorwaar, aan Allah behoort wat in de hemelen en op de aarde is. Weet: voorwaar, de belofte van Allah is waar, maar de meesten van hen weten het niet.'),
(1420, 10, 56, 'Hij doet leven en Hij doet sterven en tot Hem worden jullie teruggekeerd.'),
(1421, 10, 57, 'O mensen, voorzeker, er is cm vermaning van jullie Heer tot jullie gekomen en een genezing voor wat in jullie harten is, en Leiding en Barmhartigheid voor de gelovigen.'),
(1422, 10, 58, 'Zeg (O Moehammad): "Met de gunst van Allah en Zijn Barmhartigheid, laten zij zich dan daarmee verheugen. Hij (de gunst) is beter dan wat zij verzamelen."'),
(1423, 10, 59, 'Zeg: "Hebben jullie gezien wat Allah voor jullie heeft neergezonden aan voorzieningen, waarna jullie daarvan verboden en toegestane dingen maakten?" Zeg: "Heeft Allah jullie (darvoor) toestemming gegeven? Of verzinnen jullie leugens over Allah?"'),
(1424, 10, 60, 'En wat zullen degenen die over Allah leugens verzinnen, denken op de Dag der Opstanding? Voorwaar, Allah is de Bezitter van de gunst vom de mensen, maar de meesten van hen zijn niet dankbaar.'),
(1425, 10, 61, 'En jij houdt je niet met een zaak bezig, en niets draag jij daarover van de Koran voor, en jullie verrichten jullie geen werk, of Wij zijn over jullie Getuigen wanneer jullie daarin verdiept zijn. En jouw Heer ontgaat geen stofdeeltje op de aarde en (in) de hemel; en er is niets kleiners dan dat en niets groters, of het staat in een duidelijk Boek.'),
(1426, 10, 62, 'Weet: voorwaar, er zal geen vrees over de geliefden van Allah komen en zij zullen niet treuren.'),
(1427, 10, 63, 'Degenen die geloofden en voortdurend (Allah) vreesden.'),
(1428, 10, 64, 'Voor hen is er de verheugende tijding in het wereldse leven en in het Hiernamaals. De Woorden van Allah kennen geen verandering. Dat is de geweldige overwinning.'),
(1429, 10, 65, 'En laten hun woorden jou niet bedroeven: voorwaar, alle glorie behoort aan Allah, Hij is de Alhorende, de Alwetende.'),
(1430, 10, 66, 'Weet: voorwaar, aan Allah behoort wat er in de hemelen en op de aarde is. En degenen die deelgenoten naast Allah aanroepen volgen geen (overtuiging), zij volgen niets dan vermoedens en zij doen niets dan liegen.'),
(1431, 10, 67, 'Hij is Degene Die de nacht voor jullie heeft gemaakt opdat jullie er rust in vinden, en de dag om te zien. Voorwaar, daarin zijn zeker Tekenen voor een volk dat luistert.'),
(1432, 10, 68, 'Zij (de Joden en de Christenen) zeggen: "Allah heeft Zich een zoon genomen." Heilig is Hij, Hij is de Behoefteloze, aan Hem behoort wat in de hemelen en op de aarde is. Jullie hebben daarvoor geen bewijs. Zeggen jullie over Allah wat jullie niet weten?'),
(1433, 10, 69, 'Zeg: "Voorwaar, degenen die leugens over Allah verzinnen zullen niet welslagen."'),
(1434, 10, 70, '(Voor hen is er slechts) een genieting op de wereld, daarna is hun terugkeer tot Ons. Vervolgens doen Wij hen de harde bestraffing proeven wegens dat waaraan zij ongelovig plachten te zijn.'),
(1435, 10, 71, 'En lees ben de geschiedenis van Noeh voor, toen Hij tot zijn volk zei: "O mijn volk, als jullie bezwaar hebben tegen mijn plaats en mijn vermanen met Allah\'s Verzen: op Allah heb ik mijn vertrouwen gesteld. Neem dan jullie besluit, (samen met) jullie deelgenoten en laat jullie besluit vervolgens niet verborgen zijn voor jullie (onderling). Voert het vervolgens uit en geeft mij geen uitstel.'),
(1436, 10, 72, 'Als jullie je dan afwenden; ik heb jullie geen beloning gevraagd; mijn beloning rust slechts bij Allah, en mij is bevolen om te behoren tot hen die zich hebben overgegeven (aan Allah)."'),
(1437, 10, 73, 'Toen loochenden zij hem (Noeh) waarop Wij hem redden en degenen die met hem in het schip waren en Wij maakten hen tot opvolgers. En Wij verdronken degenen die Onze Tekenen loochenden. Zie dan hoe het einde van de gewaarschuwden was.'),
(1438, 10, 74, 'Daarna zonden Wij na hem Boodschappers tot hun volk zodat zij niet de duidelijke Bewijzen tot hen kwamen, zij wilden toen niet geloven vanwege wat zij voorheen hadden geloochend. Zo vergrendelden Wij de harten van dc overtreders.'),
(1439, 10, 75, 'Daarna zonden Wij na hen Môesa en Hârôen tot Fir\'aun en zijn vooraanstaanden met Onze Tekenen, waarop zij hoogmoedig werden. En zij waren een misdadig volk.'),
(1440, 10, 76, 'Toen dan de Waarheid van Onze Zijde tot hen gekomen was, zeiden zij: "Voorwaar, dit is zeker duidelijke tovenarij."'),
(1441, 10, 77, 'Môesa zei: "Spraken jullie de Waarheid toen die tot jullie kwam? Is dit tovenarij, terwijl de tovenaars niet winnen?"'),
(1442, 10, 78, 'Zij zeiden. "Ben jij tot ons gekomen om ons af te brengen van dat (pad van de afgoderij) waarop wij onze vaderen aantroffen en opdat de macht op aarde voor jullie (Môesa en Hârôen) zou zijn? Maar wij geloven jullie niet."'),
(1443, 10, 79, 'En Fir\'aun zei: "Brengt mij alle vakkundige tovenaars."'),
(1444, 10, 80, 'Toen dan alle tovenaars waren gekomen, zei Môesa tot hen: "Werpt dat wat jullie (willen) werpen."'),
(1445, 10, 81, 'Toen zij dan hadden geworpen, zei Môesa: "Wat jullie hebben gebracht is tovenarij, voorwaar, Allah zal het teniet doen. Voorwaar, Allah laat de werken van de verderfzaaiers niet voortbestaan."'),
(1446, 10, 82, 'En Allah vestigt de Waarheid met Zijn Woorden, ook al hebben de misdadigers er een afkeer van.'),
(1447, 10, 83, 'Toen gcloofde niemand Môesa, behalve (enkele) jongelingen van zijn volk, terwijl zij vreesden dat Fir\'aun en zijn vooraanstaanden hen zouden vervolgen. En voorwaar, Fir\'aun was zeker een onderdrukker in het land: en voorwaar, hij behoorde zeker tot de buitensporigen.'),
(1448, 10, 84, 'En Môesa zei: "O mijn volk, als jullie in Allah geloven, stelt dan jullie vertrouwen op Hem, als jullie je hebben overgegeven (aan Allah)."'),
(1449, 10, 85, 'Toen zeiden zij: "Op Allah hebben wij ons vertrouwen gesteld, Onze Hem, maak ons geen voorwerp van vervolging door het onreelhtplegende volk.'),
(1450, 10, 86, 'En red ons door Uw Barmhartigheid van het ongelovige volk."'),
(1451, 10, 87, 'En Wij openbaarden aan Môesa en zijn broeder: "Bouwt voor jullie volk huizen in Egypte en maakt van jullie woningen gebedsbuizen en onderhoudt de shalât en verkondigt een verheugende tijding aan de gelovigen."'),
(1452, 10, 88, 'En Môesa zei: "Onze Heer, voorwaar, U heeft Fir\'aun en zijn vooraanstaanden versieringen en bezittingen tijdens het wereldse leven gegeven. Onze Heef, daardoor hielden zij (de mensen) af van Uw Pad. O Heer, vaag hun bezittingen weg en verhardt hun harten zodat zij niet geloven totdat zij de pijnlijke bestraffing zien."'),
(1453, 10, 89, 'Hij (Allah) zei: "Voorzeker, Ik heb jullie smeekbede verhoord. Blijft daarom (op het rechte Pad) en volgt niet de weg van degenen die niet weten."'),
(1454, 10, 90, 'En Wij brachten de Kinderen van Israël over de zee, waarop Fir\'aun en zijn legefs hen volgden uit tirannie en vijandschap, totdat, toen de verdrinking hen bereikte, Fir"aun zei: "Ik geloofdat er geen god is dan Degene waarin de Kinderen van Israël geloven en ik behoor tot hen die zich hebben overgegeven (aan Allah)."'),
(1455, 10, 91, '(Allah zei:) "Nu? Terwijl jij voorheen opstandig was en tot de verderfzaaiers behoorde?"'),
(1456, 10, 92, 'Op deze dag dan redden Wij jouw lichaam opdat jij een Teken zult zijn voor hen die na jou komen. En voorwaar, velen van de mensen zijn achteloos tegenover Onze Tekenen.'),
(1457, 10, 93, 'En Wij plaatsten de Kinderen van Israël in voortreffelijke woonplaatsen en Wij gaven hun voorzieningen van de goede dingen, en zij verschildein niet van mening totdat de kennis tot hen was gekomen. Voorwaar, jouw Heer oordeelt tussen hen op de Dag der Opstanding over dat waar zij van mening over verschilden.'),
(1458, 10, 94, 'Als jij dan in twijfel verkeert over wat Wij aan jou hebben neergezonden, vraag dan aan hen die de Schrift lezen (die) vóór jou (is geopenbaard). Voorzeker, de Waarheid van jouw Heer is tot jou gekomen, behoor daarom niet tot de twijfelaars.'),
(1459, 10, 95, 'En behoor in geen geval tot degenen die de Verzen van Allah loochenen, anders dan zal jij tot de verliezers behoren.'),
(1460, 10, 96, 'Voorwaar, degenen over wie het Woord (van bestraffing) van jouw Heer tereclit is: zij geloven niet.'),
(1461, 10, 97, 'Ook al kwamen alle Tekenen tot hen, totdat zij de pijnlijke bestraffing zien.'),
(1462, 10, 98, 'Was er maar een stad geweest, naast die van het volk van Yôenoes, die geloofde, opdat haar gelool haar zou baten. Toen zij (het volk van Yôenoes") geloofden, namen Wij de bestraffing van de vernedering in het wereldse leven weg, en schonken Wij hen genietingen tot een bepaalde tijd.'),
(1463, 10, 99, 'En als jouw Heer het had gewild, dan zouden degenen die op aarde zijn, zeker allen tezamen hebben geloofd. Wil jij (O Moehammad) dan de mensen dwingen opdat zij gelovigen worden?'),
(1464, 10, 100, 'En geen ziel kan geloven, behalve met verlof van Allah. En Hij legt een bestraffing op aan degenen die hun verstand niet gebruiken.'),
(1465, 10, 101, 'Zeg (O Moehammad): "Aanschouwt wat er in de hemelen en op de aarde is. Maar de Tekenen en de waarschuwingen baten niet voor een volk dat niet gelooft."'),
(1466, 10, 102, 'Wachten zij dan alleen maar op rampen zoals van degenen die hen vooraf gingen? Zeg: "Wachten jullie dan maar. Voorwaar, ik behoor met jullie tot de wachtenden."'),
(1467, 10, 103, 'Daarna redden Wij Onze Boodschappers en degenen die geloofden. Zo stellen Wij het Ons tot een plicht om de gelovigen te redden.'),
(1468, 10, 104, 'Zeg: "O mensen, als jullie twijfelen aan mijn godsdienst: ik aanbid niet wat jullie naast Allah aanbidden, maar ik aanbid Allah die jullie wegneemt. En mij is bevolen om tot de gelovigen te behoren.'),
(1469, 10, 105, 'En om jouw aangezicht (O Moehammad) tot de godsdienst te wenden als een Hanîf, en behoor zeker niet tot de veelgodenaanbidders.'),
(1470, 10, 106, 'En roep buiten Allah niet aan wat jou niet kan baten en niet kan schaden. Als jij dat deed, voorwaar, dan zou jij tot de onrechtplegers behoren.'),
(1471, 10, 107, 'En wanneer Allah jou met een tegenslag treft, dan is er niemand die deze kan wegnemen, behalve Hij. En wanneer Hij voor jou iets goeds wenst, dan kan niemand Zijn gunst tegenhouden. Hij treft daarmee wie Hij wil van Zijn dienaren. En Hij is de Vergevensgezinde, de Meest Barmhartige.'),
(1472, 10, 108, 'Zeg: "O mensen, waarlijk, de Waarheid van jullie Heer is tot jullie gekomen. Wie dan de Leiding volgt: voorwaar, hij volgt die slechts in zijn eigen voordeel. En wie dwaalt: voorwaar, die dwaalt slechts ten nadele van zichzelf En ik ben geen voogd over jullie."'),
(1473, 10, 109, 'En volg wat ma jou is geopenbaard en wees geduldig totdat Allah oordeelt. En Hij is de Beste der Rechters.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'AIif Lim Râ. (Dit is) een Boek waarvan de Verzen hecht zijn geplaatst en die vervolgens zijn uiteengezet, van de Zijde van de Alwijze, de Alwetende.'),
(1475, 11, 2, 'Opdat jullie slechts Allah aanbidden. Voorwaar, ik ben voor jullie een waarschuwer en een verkondiger van een verheugende tijding.'),
(1476, 11, 3, 'En als jullie je Heer om vergeving vragen en jullie je vervolgens berouwvol tot Hem wenden, dan zal Hij jullie een goedegenieting schenken, tot een bepaalde tijd. En Hij beloont iedere bezitter van een verdienste met Zijn gunst. Als jullie afwenden: voorwaar, dan vrees ik voor jullie de bestraffing van de Grote Dag.'),
(1477, 11, 4, 'Tot Allah is jullie terugkeer, en Hij is Almachtig over alle dingen.'),
(1478, 11, 5, 'Weet zij (de huichelaars) wenden hun borsten af om zich voor hem (Moehammad) te verbergen. Zelfs wanneer zij zich met hun kleren bedekken, weet Hij wat zij verbergen en wat zij openlijk doen. Voorwaar, Hij is Alwetend over wat er in de harten is.'),
(1479, 11, 6, 'Er is geen levend wezen (Dâbbah) op aarde, of aan Allah is het onderhoud ervan. En Hij kent de verblijfplaats en de bewaarplaats ervan. Alles is vastgelegd in een duidelijk Boek.'),
(1480, 11, 7, 'En Hij is Degene die de hemelen en de aarde heeft geschapen in zes dagen (perioden) en Zijn Troon was op liet water, om te beproeven wie van jullie de beste daden verricht. En als jij zegt: "Voorwaar, jullie zullen worden opgewekt na de dood," dan zullen degenen die ongelovig zijn, zeker zeggen: "Dit is slechts duidelijke tovenarij."'),
(1481, 11, 8, 'En wanneer Wij de bestraffing van hen uitstellen tot een bepaald tijdstip, dan zullen zij zeker zeggen: "Wat houdt haar tegen?" Weet, op de dag waarop zij tot hen komt, kan zij van hen niet worden afgewend en worden zij omsingeld door hetgeen waarmee zij de spot plachten te drijven.'),
(1482, 11, 9, 'En wanneer Wij de mens Barmhartigheid van Ons doen proeven, (en) die vervolgens van hun wegnemen: voorwaar, hij wordt dan een wanhopige ondankbare.'),
(1483, 11, 10, 'En wanneer Wij hem genietingen doen proeven nadat tegenslagen hem troffen zal hij zeker zeggen: "Het kwaad heeft mij verlaten." Voorwaar, hij is zeker verheugd, hoogmoedig.'),
(1484, 11, 11, 'Behalve degenen die geduldig zijn en goede werken verrichten. Zij zijn degenen voor wie er vergeving en een grote beloning is.'),
(1485, 11, 12, 'Misschien zou jij een gedeelte van wat aan jou geopenbaard is willen weglaten, en is jouw hart benauwd, omdat zij zeggen: "Waarom is er geen schat naar hem neergezonden, of is er geen Engel met hem gekomen?" Voorwaar, jij bent slechts een waarschuwer en Allah is Bewaker van alle dingen.'),
(1486, 11, 13, 'Of zij zeggen: "Hij (Moehammad) heeft hem (de Koran) verzonnen." Zeg: "Brengt dan tien verzonnen hoofdstukken voort die daaraan gelijk zijn, en roept op wie jullie kunnen, buiten Allah, als jullie waarachtigen zijn."'),
(1487, 11, 14, 'En als zij geen gehoor geven aan jullie, weet dan dat hij met de Kennis van Allah neergezonden is, en er geen god is dan Hij. Zullen jullie je dan (aan Allah) overgeven?'),
(1488, 11, 15, '(Voor) wie het wereldse leven en haar versiering wensen zullen Wij hun daden daarin volledig vergoeden, en zij zullen daarin niet worden benadccld.'),
(1489, 11, 16, 'Zij zijn degenen voor wie er in het lliernamaals niets dan de Hel is, en vruchteloos is wat zij daarin (op aarde) verrichtten. En wat zij plachten te doen is verloren gegaan.'),
(1490, 11, 17, 'Is hij (niet beter) die op een duidelijk bewijs van zijn Heer steunt en die een getuige van Hem volgt? En hiervôôr was er de Schrift van Môesa, als Leiding en Barmhartigheid. Zij zijn degenen die erin geloven en wie er niet in gelooft van de bondgenoten: de Hel is zijn aanzegging. Twijfel daarom hier niet aan: voorwaar, het is de Waarheid van jouw Heer, maar de meeste mensen geloven het niet.'),
(1491, 11, 18, 'En wie is onrechtvaardiger dan degene die leugens over Allah verzonnen heeft? Zij zijn degenen die zullen worden voorgeleid bij hun Heer. En de getuigen zullen zeggen: "Zij zijn degenen die logen over hun Heer." Weet, Allah\'s vloek rust op de onrechtvaardigen.'),
(1492, 11, 19, 'Degenen die aflhouden van de Weg van Allah en die het krom wensen, en zij geloven niet in het Hiernamaals.'),
(1493, 11, 20, 'Zij zijn degenen die niet in staat zijm (om de bestraffing) in deze wereld te ontvluchten. En er zijn voor hen geen beschermers, en de bestraffing wordt voor hen vermeerderd. Zij waren niet in staat om te horen en zij waren niet in staat om te zien.'),
(1494, 11, 21, 'Zij zijn degenen die zichzelf verlies hebben toegebracht, en wat zij plachten te verzinnen is van heil weggegaan.'),
(1495, 11, 22, 'Het is zeker zo dat ziij in het Hiernamaals de verliezers zijn.'),
(1496, 11, 23, 'Voorwaar, degenen die geloven en goede werken verrichten, en die zich verootmoodigen bij hun Heer: zij zijn degenen die de bewoners van het Paradijs zijn, zij zijn daarin eeuwig levenden.'),
(1497, 11, 24, 'De gelijkenis tussen de twee groepen is als die tussen de blinden en de doven (van hart) en de zienden en de horenden: zijn zij gelijk? Trekken jullie er dan geen lering uit?'),
(1498, 11, 25, 'En voorzeker, Wij zonden Nôeh tot zijn volk. (Hij zei:) "Voorwaar, ik hen voor jullie een duidelijke waarschuwer.'),
(1499, 11, 26, 'Opdat jullie slechts Allah aanbidden. Voorwaar, ik vrees voor jullie de bestraffing op een pijnlijke Dag."'),
(1500, 11, 27, 'En de vooraanstaanden van zijn volk, die ongelovig waren, zeiden: "Wij zien dat jij slechts een mens zoals wij bent. En wij zien dat niemand jou volgt, behalve degenen die de laagsten zijn onder ons, die simpel zijn. En wij zien dat jullie geen voordeel boven ons hebben. Wij zijn er zelfs van overtuigd dat jullie leugenaars zijn."'),
(1501, 11, 28, 'Hij zei: "O mijn volk, wat denken jullie? Als ik steun op een duidelijk bewijs van mijn Heer en Hij heeft mij Barmhartigheid geschonken van Zijn Zijde, die voor jullie verborgen is: zouden wij het jullie opdringen, terwijl jullie er een afkeer van hebben?"'),
(1502, 11, 29, 'En: "O mijn volk, ik vraag jullie hiervoor geen rijkdom, mijn beloning is slechts bij Allah. En ik zal de ongerlovigen nimmer wegjagen. Voorwaar, zij zullen hun Heer zeker ontmoeten, maar ik zie dat jullie een onwetend volk zijn."'),
(1503, 11, 30, 'En: "O mijn volk, wie kan mij redden (van de bestraffing) van Allah, als ik hen zou wegjagen? Trekken jullie er dan geen lering uit?'),
(1504, 11, 31, 'En ik zeg niet tot jullie: \'Ik bezit de schatten van Allah.? En ik ken het onwaarneembare niet. En ik zeg niet: \'Voorwaar, ik ben een Engel.\' En ik zeg niet tot de-genen die verachtelijk zijn in jullie ogen: \'Allah zal jullie nimmer iets goeds geven.\' Allah weet beter wat er in hen is. Voorwaar, den zou ik zeker tot de onrechtplegers behoren."'),
(1505, 11, 32, 'Zij zeiden: "O Nôeh, je hebt met ons getwist, en je hebt lang met ons getwist, laat dan tot ons komen wat jij hebt aangezegd, als jij tot de waarachtigen behoort."'),
(1506, 11, 33, 'Hij zei: "Voorwaar, Allah doet het komen, als Hij wil. En jullie zullen er niet aan kunnen ontkomen.'),
(1507, 11, 34, 'En mijn raad baat jullie niet, als ik jullie raad zou willen geven. Als Allah jullie wil doen dwalen: Hij is jullie Heer en tot Hem worden jullie terugge keerd."'),
(1508, 11, 35, 'Of zij zeggen: "Hij heeft het verzonnen." Zeg: "Als ik het heb verzonnen, dan rust mijn misdaad op mij. En ik ben onschuldig aan de misdaden die jullie plegen."'),
(1509, 11, 36, 'En er werd aan Nôeh geopenbaard: "Voorwaar, van jouw volk zal nooit iemand geloven, behalve degenen die reeds geloofden, treur daarom niet over wat zij plachten te doen.'),
(1510, 11, 37, 'Bouw het schip onder Ons toezicht en Onze Openbaring en spreek Mij niet aan over degenen die onrecht pleegden. Voorwaar, zij worden verdronken."'),
(1511, 11, 38, 'En hij bouwde het schip en telkens wanneer de vooraanstaanden van zijn volk voor bij kwamen, bespotten zij hem. Hij zei: "Als jullie ons bespotten: voorwaar, dan zullen wij jullie later bespotten, zoals jullie ons (nu) bespotten."'),
(1512, 11, 39, 'Jullie zullen het weten komen, tot wie de bestraffing komt die hem vernedert. En een blijvende bestraffing komt op hen neer.'),
(1513, 11, 40, 'Totdat, toen Ons bevel (om hen te vernietigen) kwam, en de oven overkookte. En Wij zeiden: "Laad het (schip) met twee van elke soort, en jouw familie, behalve hen voor wie het woord (van bestraffing) reeds is voorafgegaan. En degene die gelooft. En niemand geloofde met ten, behalve enkelen.'),
(1514, 11, 41, 'En hij (Nôeh) zei: "Ga aan boord, (en zeg:) "Bismillahi madjréha wa moersahâ" (In de Naam van Allah vaart zij haar koers en gaat zij voor anker.) Voorwaar, mijn Heer is zeker Vergevensgezind, Meest Barmbartig\'.'),
(1515, 11, 42, 'En zij vaude met hen uit, op golven zo hoog als bergen, en Nôeh riep tot zijn zoon die alleen (op het land) stond: \'O mijn zoon, kom bij ons aan boord en behoor niet tot de ongelovigen."'),
(1516, 11, 43, 'Hij zei: "Ik zal een schuilplaats zoeken in de bergen die mij tegen het water zal beschernen." Hij (Nôeh) zei: "Op deze dag is er geen bescherming tegen het bevel van AlIah, behalve voor wie Hij begenadigt." En de golven scheidden hen en bij behoorde tot de verdronkenen.'),
(1517, 11, 44, 'En er werd gezegd: "O aarde, slikje water in," en: "O hemel, droog op." En het water trok zich terug. En het bevel was uitgevoerd. En het schip ging voor anker bij de berg Djôedy. En er werd gezegd: "Verdoemd is het onrechtplegende volk."'),
(1518, 11, 45, 'En Nôeh riep tot zijn Heer, hij zei: "O mijn Heer, voorwaar, mijn zoon behoort tot mijin familie, en voorwaar, Uw belofte is de Waarheid. En U bent de Rechtvaardigste der Rechters."'),
(1519, 11, 46, 'Hij (Allah) zei: "O Nôeh, voorwaar, hij behoort niet tot jouw familie, voorwaar, het is geen rechtschapen daad, vraag Mij daarom niet iets waarover jij geen kennis hebt. Voorwaar, Ik vermaan je, opdat jij niet tot de onwetenden zult behoren."'),
(1520, 11, 47, 'Hij (Nôeh) zei: "O mijn Heer, voorwaar, ik zoek mijin toevlucht bij U tegen dat ik iets zal vragen waarover ik geen kennis heb, en als U mij niet vergeeft en mij niet begenadig dan zal ik zeker tot de verliezers behoren."'),
(1521, 11, 48, 'Er werd gezegd: "O Nôeh, ga van boord met veiligheid en de zegening van Ons over jou en over de gemeenschappen met jou. En Wij schenken genietingen aan gemeenschappen, vervolgens treft hen een pijnlijke bestraffing van Ons."'),
(1522, 11, 49, 'Dit is één van de berichten uit het onwaaneembare die Wij aan jou openbaren. En hiervóór wist jij, noch jouw volk dit. Wees daarom geduldig. Voorwaar, het goede einde is voor de Moettaqôen.'),
(1523, 11, 50, 'En tot het volk van \'Âd (zonden Wij) hun broeder Hôed, hij zei: "O mijn volk, aanbidt Allah, er is voor jullie geen god den Hij, jullie zijn niets dan verzinners.'),
(1524, 11, 51, 'O mijn volk, ik vraag jullie hiervoor geen beloning, mijn beloning rust slechts bij Degene Die mij heeft geschapen, denken jullie dan niet na?"'),
(1525, 11, 52, 'En: "O mijn volk, vraagt vergeving aan jullie Heer, wendt jullie dan in berouw tot Hem; dan zal Hij de hemel overvloedige regen doen uitstorten over jullie en Hij zal voor jullie kracht toevoegen aan jullie kracht, en wendt jullie niet af als misdadigers."'),
(1526, 11, 53, 'Zij zeiden: "O Hôed, jij heb ons geen duidelijk Teken gebracht en wij verlaten onze goden niet vanwege jouw woord, en wij geloven jou niet."'),
(1527, 11, 54, 'Wij zeggen slechts: "En aantal van onze goden hebben jou met iets slechts getroffen." Hij zei: "Ik roep Allah op als Getuige. en getuig dat ik onschuldig ben aan wat jullie aan deelgenoten toekennen.'),
(1528, 11, 55, 'Naast Hem. Beraamt gezamenlijk een plan tegen mij en geeft mij vervolgens geen uitstel.'),
(1529, 11, 56, 'Voorwaar, ik verbouw op Allah, mijn Heer en jullie Heer. En er is geen levend wezen of Hij heeft het volledig in Zijn macht. Voorwaar, mijn Heer handelt op rechtvaardige wijze.'),
(1530, 11, 57, 'Als jullie je afwenden, dan heb ik jullie doorgegeven waar ik tot jullie mee gezonden ben. En mijn Heer zal jullie doen opvolgen door een ander volk en jullie kunnen Hem geen schade toebrengen. Voorwaar, mijn Heer is Waker over alle dingen."'),
(1531, 11, 58, 'En toen Ons bevel was gekomen, redden Wij Hôed en degenen die met hem geloofden, door Genade van Ons, en Wij redden ten van een zwam bestraffing.'),
(1532, 11, 59, 'En dat was (het einde van) het volk van \'Âd, zij verwierpen de Tekenen van hun Heer, zij gehoorzaamden de Profeten niet en zij volgden het bevel van iedere opstandige geweldenaar.'),
(1533, 11, 60, 'En zij worden in deze wereld en op de Dag der Opstanding achtervolgd door een vloek. Weet: voorwaar, de \'Âd waren ongelovig aan hun Heer. Weet: verdoemd is het volk van Hôed.'),
(1534, 11, 61, 'En tot de Tsamôed zonden Wij hun broeder Shâlih, hij zei: "O mijn volk, aanbidt Allah, er is voor jullie geen god dan Hij, Hij is Degene Die jullie uit de aairde heeft doen onstm en die jullie daarop heeft gevestigd, vraagt Hem daarom om vergeving en wendt jullie dan in berouw tot Hem. Voorwaar, mijn Heer is nabij, Verberend."'),
(1535, 11, 62, 'En zij (de Tsamôed) zeiden: "O Shâlih, hiervoor was jij temidden van ons iemand waarop wij onze hoop hadden gevestigd. Verbied jij ons dat wij aanbidden wat onze vaderen aanbaden? En wij verkeren in grote twijfel over hetgeen waartoe jij ons oproept."'),
(1536, 11, 63, 'Hij zei: "O mijn volk, wat denken jullie, als ik op een duidelijk bewijs van mijn Heer steun, en Hij mij Barmhartigheid van Hem heeft geschonken: wie kan mij dan tegen (de bestraffing van) Allah helpen als ik Hem ongehoorzaam ben. Daarom vermeerderen jullie niets voor mij dan verlies."'),
(1537, 11, 64, 'En: "O mijn volk, dit is een vrouwteskameel van Allah, als een Teken voor jullie, laat haar daarom eten van de aarde van Allah en doe haar geen kwaad, anders zal een nabije bestraffing jullie grijpen."'),
(1538, 11, 65, 'Toen slachtten zij haar, waarop bij (Shâlih) zei. "Blijft drie dagen in jullie huizen. Dat is een aanzegging die niet kan worden geloochend."'),
(1539, 11, 66, 'En toen Ons bevel kwam, redden Wij Shâlih en degenen die met hem geloofden, door Genade van Ons en (Wij redden hen) van een vernedering op die Dag. Voorwaar, jouw Heer is de Sterke, de Almachtige.'),
(1540, 11, 67, 'En de bliksemalag greep degenen die onrecht hadden gepleegd, zodat zij doden in hun huizen werden.'),
(1541, 11, 68, 'Alsof zij er nooit gewoond hadden. Weet, voorwaar, de Tsmôed waren ongelovig aan hun Heer, Weet: verdoemd zijn de Tsamôed.'),
(1542, 11, 69, 'En voorzeker, Onze gezanten (Engelen) kwamen tot Ibrâhîm met de verheugende tijding, zij zeiden: "Salâm" (Vrede). "Hij zei: "Salâm." Niet lang daarna bracht hij een geroosterd kalf.'),
(1543, 11, 70, 'Toen hij dan zag dat hun handen er niet naar reikten vond hij ben vreemd en werd hij bang van hen. Zij zeiden: "Vreest niet, Wij zijn gezonden tot het volk van Lôeth."'),
(1544, 11, 71, 'En zijn vrouw stond en zij lachte, daarna verkondigden wij haar een verheugende tijding over (de geboorte van) lshâq, en na Ishaq Ya\'qôeb.'),
(1545, 11, 72, 'Zij zei: "Wee mij, zal ik een kind baren, terwijl ik een oude vrouw ben, en deze echtgenoot van mij is een oude man. Voorwaar, dat is zeker iets verbazingwekkends."'),
(1546, 11, 73, 'Zij (de Engelen) zeiden: "Verbaas jij je over de beschikking van Allah? Het is Allah\'s Barmhartigheid en het zijn Zijn zegeningen over jullie, O bewoners van het huis. Voorwaar, Hij is Meest Prijzenswaardig, Meest Vrijgevig."'),
(1547, 11, 74, 'En toen de angst bij lbrâhîm was verdwenen, en de goede tijding tot hem was gekomen, redetwistte hij met Ons over het volk van Lôeth.'),
(1548, 11, 75, 'Voorwaar, Ibrâhîm is inderdaad zachtmoedig, teder, berouwvol.'),
(1549, 11, 76, 'O Ibráhîm, wend je hier van af! Voorwaar, de beschikking van jouw Heer is reeds gekomen. Voorwaar, een onafwendbare bestraffing zal tot hen komen.'),
(1550, 11, 77, 'En toen Onze gezanten (Engelen) tot Lôeth kwamen, voelde hij zich benard en machteloos benauwd, en hij zei: "Dit is een zware dag."'),
(1551, 11, 78, 'En zijn volk kwam tot hem, zich tot hem haastend. En voorheen plachten zij slechte daden te bedrijven. Hij zei: "O mijn volk, dit zijn mijn dochters, zij zijn reiner voor juillie. Vreest daarom Allah, en vernedert mij niet tegenover mijn gasten. Is er onder jullie geen man met gezond verstandt?"'),
(1552, 11, 79, 'Zij zeiden: "Voorzeker, je weet dat wij geen behoefte hebben aan jouw dochters, en voorwaar, jij weet wat wij wensen."'),
(1553, 11, 80, 'Hij zei: "Als ik toch macht tegen jullie had, of mijn toevlucht kon nemen bij een machtige familie... (dan zou ik tegen jullie op kunnen treden.)"'),
(1554, 11, 81, 'Zij (de Engelen) zeiden: "O Lôeth, voorwaar, wij zijn de gezanten van jouw Heer, zij zullen jou nimmer kunnen bereiken, vertrek dus met jouw familie in de nacht. En laat niemand van jullie achterblijven, behalve jouw vrouw. Voorwaar, wat haar zal treffen, is wat hen zal treffen. Voorwaar, wat hun is aangezegd, zal in de ochtend plaatsvinden. Is de ochtend niet nabij?"'),
(1555, 11, 82, 'Toen dan Ons bevel kwam, keerden Wij het (land) ondersteboven. En Wij deden er stenen van gebakken klei op neerregenen, in lagen.'),
(1556, 11, 83, 'Gekenmerkt bij jouw Heer. En deze (bestraffing) is voor de onrechtplegers niet ver.'),
(1557, 11, 84, 'En tot (de bewoners van) van Madyan (zonden Wij) hun broeder (Sjoe\'aib). Hij zei: "O mijn volk, aanbidt Allah, er is voor jullie geen god dan Hij, en vermindert niet de maat en de weegschaal. Voorwaar, ik zie dat jullie in goede doen zijn en voorwaar, ik vrees voor jullie de bestraffing op een allesomvattende Dag."'),
(1558, 11, 85, 'En: "O mijn volk, geeft de volle maat en vult de weegschaal tot het gelijke gewicht, en benadeelt de mensen niet in hun rechten, en bedrijft geen kwaad op aarde door verderf te zaaien.'),
(1559, 11, 86, 'Wat (aan voorziening na het eerlijk afwegen) overblijft van Allah is beter voor jullie, als jullie gelovigen zijn. En ik ben geen waker over jullie."'),
(1560, 11, 87, 'Zij zeiden: "\'O Sjoe\'aib, gebiedt jouw shalât dat wij verlaten wat onze voorvaderen aanbaden, of dat wij ophouden met onze bezittingen te doen wat wij willen? Voorwaar, je bent zeker zachtmoedig, verstandig"'),
(1561, 11, 88, 'Hij zei: "O mijn volk, wat denken jullie, als ik op een duidelijk bewijs van mijn Heer steun, en Hij voorziet mij van Zijn Zijde met een goede voorziening, (zou ik Hem ongehoorzaam zijn)? En ik wil mij niet tegenover jullie schuldig maken aan wat ik jullie verbied. Ik wens slechts verbetering volgens mijn vermogen, en er is voor mij geen goddelijke overeenstemming dan bij Allah. Op Hem heb ik mijn vertrouwen gesteld en tot Hem keer ik terug."'),
(1562, 11, 89, 'En: "O mijn volk, laat mijn onenigheid (met jullie) er niet toe leiden dat jullie hetzelfde treft als het volk van Noeh trof, of het volk van Hoed, of het volk van Shalih. En het volk van Loeth is niet ver van jullie.'),
(1563, 11, 90, 'En vraagt jullie Heer om vergeving en wendt jullie vervolgens in berouw tot Hem. Voorwaar, mijn Heer is Meest Bamhartig, Meest Liefdevol."'),
(1564, 11, 91, 'Zij zeiden: "O Sjoe\'aib, Wij begrijpen niet veel van wat jij zeg en voorwaar, wij zien dat jij onder ons een zwakke bent. En ware het niet vanwege jouw familie, dan hadden wij jou zeker gestenigd. Jij bent niet eerwaardiger dan wij."\''),
(1565, 11, 92, 'Hij zei: "O mijn volk, is mijn familie eerwaardiger bij jullie dan Allah?" Keren jullie Hem de rug toe? Voorwaar, mijn Heer omvat alles wat jullie doen."'),
(1566, 11, 93, 'En: "O mijn volk, werkt volgens jullie vermogen, ook ik werk, later zullen jullie weten tot wie een bestraffing komt die hem vernedert, en wie een leugenaar is. En wacht af, voorwaar, ik ben met jullie een wachtende."'),
(1567, 11, 94, 'En toen Ons bevel was gekomen, redden Wij Sjoe\'aib en degenen die met hem geloofden, door Genade van Ons. En de donderslag groep degenen die onrecht pleegden, waarna zij doden in hun huizen werden.'),
(1568, 11, 95, 'Alsof zij er nooit gewoond hadden. Weet, verdoemenis is er voor de bewoners van Madyan, zoals de Tsamôed werden verdoemd.'),
(1569, 11, 96, 'En voorzeker, wij hebben Môesa gezonden met Onze Tekenen en duidelijke bewijzen.'),
(1570, 11, 97, 'Tot Fir\'aun en zijn vooraanstaanden. Toen volgden zij (de vooraanstaanden) het bevel van Fir\'aun, terwijl het bevel van Fir\'aun een onrecht was.'),
(1571, 11, 98, 'Hij zal vóór zijn volk lopen op de Dag der Opstanding en hen naar de Hel voeren, De slechtste plaats is de plaats waarheen (dan gevoerd) wordt!'),
(1572, 11, 99, 'En zij worden in deze (wereld) gevolgd door een vervloeking en de Dag der Opstanding. De slechtste gave (vervloeking) is de gave die (dan gegeven) wordt!'),
(1573, 11, 100, 'Dat zijn enkele geschiedenissen over de steden die Wij aanjou (O Moehammad) vertellen, daarvan zijn er nog, en zijn er geruïneerd.'),
(1574, 11, 101, 'En Wij hebben hun geen onrecht aangedaan, maar zij hebben zichzelf onrecht aangedaan. En hun goden die zij naast Allah aanriepen, baatten hun niets tom het bevel van jouw Heer kwam. En zij vermeerderden voor hen niets dan vernietiging.'),
(1575, 11, 102, 'En zo is de ingreep van jullie Heer, wanneer Hij de steden grijpt die onrechtvaardig zijn. Voorwaar, zijn greep is pijnlijk, hard.'),
(1576, 11, 103, 'Voorwaar, daarin is zeker cm Teken voor wie de bestraffing van het Hiernamaals vreest. Dat is een Dag waarop de mensen verzameld worden. Dat in een Dag weuop getuigd zal worden.'),
(1577, 11, 104, 'En Wij zullen hem niet uitstellen, dan voor een vastgestelde termijn.'),
(1578, 11, 105, 'Op de Dag die komt zai geen ziel spreken, behalve met Zijn verlof. En onder hen er zijn en zijn ongerlukkigen en gelukkigen.'),
(1579, 11, 106, 'En wat degenen betreft die ongelukkig zijn, zij bevinden zich in de Hel. Voor hen is daarin gekreun en gesnik (als zij ademen).'),
(1580, 11, 107, 'Eeuwig levend verblijven zijn daarin, zolang de hemelen en de aarde voortbestaan, tenzij jouw Heer anders wil. Voorwaar, jouw Heer doet wat Hij wil.'),
(1581, 11, 108, 'En wat degenen betreft die gelukkig zijn, zij bevinden zich in het Paradijs, zij zijn daarin eeuwig levenden, zolang de hemelen en de aarde voortbestaan, tenzij jouw Heer anders wil, als een gift zonder onderbreking.'),
(1582, 11, 109, 'Verkeer daarom niet in twijfel over wat deze mensen aanbidden, zij aanbidden slechts zoals hun vaderen voorheen aanbeden. En voorwaar, wij zullen hen zeker hun deel volledig vergelden, zonder vermindering.'),
(1583, 11, 110, 'En voorzeker, wij hebben Môesa de Schrift (de Taurât) gegeven, waarna er onenigheid over bestond. En als er niet een Woord van jouw Heer vooruitgegaan was, dan zou er reeds Uwen hen zijn geoordeeld. En voorwaar, zij verkeren zeker in vergaande twijfel.'),
(1584, 11, 111, 'En voorwar, jouw Heer zal aan een ieder zijn daden volledig vergelden. Voorwaar, Hij is Alwetend over wat zij doen.'),
(1585, 11, 112, 'Wees standvastig, zoals jou en wie met jou berouw toont is bevolen, en overtreed niet. Voorwaar, Hij ziet wat jullie doen.'),
(1586, 11, 113, 'En neigt niet tot degenen die onrecht pleegden, anders zal de Hel jullie aanraken en is er voor jullie naast Allah geen beschermer. Dan zullen jullie niet worden geholpen.'),
(1587, 11, 114, 'En verricht de shalât aan het begin en het einde van dc dag en aan het begin van de avond, voorwaar, de goode daden wissen de slechte daden uit. Dat is een onderricht voor degenen die zich laten onderrichten.'),
(1588, 11, 115, 'En wees geduldig, went waarlijk, Allah zal de beloning van de weldoeners niet verloren doen gaan.'),
(1589, 11, 116, 'Waren er maar onder de generaties vóór jullie bezitters van inzicht geweest, die weerhielden van het verderf op aarde (maar deze waren niet), met uitzondering van enkelen van hen die Wij hebben gered. En degenen die onrecht pleegden, joegen de weelde na waarin zij leefden, eti zij waren misdadigers.'),
(1590, 11, 117, 'En jouw Heer was niet zo dat Hij de steden onrechtvaardig vernietigde, terwijl de bewoners ervan oprechten waren.'),
(1591, 11, 118, 'En als jouw Heer het had gewild, dan zou Hij de mensheid (als behorend tot) één godsdienst hebben gemaakt, maar zij bleven van mening verschillen.'),
(1592, 11, 119, 'Behalve wie jouw Heer begenadigd heeft. En daarom heeft Hij hen geschapen. En het Woord van jouw Heer is vastgesteld. "Ik zal de Hel vullen met Djinn\'s en mensen tezamen."'),
(1593, 11, 120, 'En Wij vertellen jou alle geschiedenissen van de Boodschappers, waarmee Wij jouw hart versterken. En daarin is tot jou de Waarheid gekomen, en een onderricht en een lering voor de gelovigen.'),
(1594, 11, 121, 'En zeg tot degenen die niet geloven: "Werkt volgens jullie vermogens. Voorwaar, (ook) wij werken.'),
(1595, 11, 122, 'En wacht af, Voorwaar, (ook) wij zijn wachtenden."'),
(1596, 11, 123, 'En aan Allah behoort het onwaarneembare van de hemelen en de aarde en tot Hem keren alle dingen terug. Aanbid daarom Hem en vertrouw op Hem en jouw Heer is niet onachtzaam omtrent wat jullie doen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'Alif Lim Râ. Dit zijn de Verzen van het duidelijke Boek.'),
(1598, 12, 2, 'Voorwaar, Wij hebben hem neergezonden, een Arabische Koran. Hopelijk zuilen jullie begrijpen.'),
(1599, 12, 3, 'Wij vertellen jou het beste verhaal doordat Wij aan jou deze Koran openbaarden, hoewel jij daarvoor tot de onwetenden behoorde.'),
(1600, 12, 4, 'En (gedenkt) toen Yôusef tot zijn vader zei: "O mijn vader, voorwaar, ik zag (in een droom) elf sterren en de zon en de maan, ik zag dat zij zich voor mij bogen."\''),
(1601, 12, 5, 'Hij zei: "O mijn zoon, vertel jouw droom niet aan jouw broeders, anders zullen zij tegen jou een plan beramen. Voorwaar, de Satan is voor de mens een duidelijke vijand."'),
(1602, 12, 6, 'Zo verkiest jouw Heer jou en onderwijst Hij jou de uitleg van de vertellingen (dromen). En Hij vervolmaakt Zijn genieting aan jou en de familie van Ya\'qôeb, zoals Hij het daarvoor heeft vervolmaakt aan jouw vaderen, Ibrâhîm en Ishaq. Voorwaar, jouw Heer is Alwetend, Alwijs.'),
(1603, 12, 7, 'Voorzeker, in (de geschiedenis van) Yôusef en zijn broeders waren Tekenen voor hen die vragen stelden.'),
(1604, 12, 8, 'Toen zij zeiden: "Yôesoef en zijn broeder zijn zeker geliefder bij onze vader dan wij, terwijl wij een hechte groep zijn. Voorwaar, onze vader verkeert zeker in duidelijke dwaling.'),
(1605, 12, 9, 'Doodt Yôesoef of zet hem het land uit, zodat het gezicht van jullie vader alleen voor jullie zal zijn en daarna worden jullie een rechtschapen volk."'),
(1606, 12, 10, 'Eén van hen zei: "Doodt Yôesoef niet, maar werpt hem op de bodem van de put, opdat enkele reizigers hem zullen vinden, als jullie iets willen ondernemen."'),
(1607, 12, 11, 'Zij zeiden: "O onze vader, waarom vertrouwt u Yôesoef niet aan ons toe. En voorwaar, wij zijn hem welgezind.'),
(1608, 12, 12, 'Laat hem morgen met ons mee gaan, zodat hij overvloedig zal eten en spelen. En voorwaar, wij zullen zeker wakers over hem zijn."'),
(1609, 12, 13, 'Hij (Ya\'qôeb) zei: "Voorwaar, het verdriet mij dat jullie hem meenemen en ik vrees dat de wolf hem zal verslinden, terwijl jullie niet op hem letten."'),
(1610, 12, 14, 'Zij zeiden: "Als de wolf hem verslindt, terwijl wij met een hechte groep zijn, dan zullen wij zeker de verliezers zijn."'),
(1611, 12, 15, 'Toen zij met hem weggingen en overeenkwamen om hem op de bodem van de put te werpen, openbaarden Wij aan hem: "Jij zal hen zeker inlichten over die zaak van hun, terwijl zij het niet beseffen."'),
(1612, 12, 16, 'En zij gingen in de avond huilend naar hun vader.'),
(1613, 12, 17, 'Zij zeiden: "O onze vader, voorwaar, wij gingen weg om een wedloop te houden en lieten Yôesoef achter bij onze goederen. Toen heeft de wolf hem verslonden, maar u zult ons niet geloven, ook al spreken wij de waarheid."'),
(1614, 12, 18, 'En zij kwamen met zijn hemd, met vals bloed (daarop). Hij zei: "Maar jullie hebben voor jezelf iets moois verzonnen. Daarom is geduld gewenst. En Allah is het Die om hulp wordt gevraagd bij wat jullie beschrijven."'),
(1615, 12, 19, 'En er kwam een groep reizigers, en zijstuurden hun waterdrager, en hij liet zijn waterzak neer. Hij zei: "O goed nieuws! Dit is een jongen." En Zij verborgen hem als koopwaar. En Allah is Alwetend over wat zij doen.'),
(1616, 12, 20, 'En zij verkochten hem voor een geringe prijs, een paar dirham, en zij wamn voor hem onverschilligen.'),
(1617, 12, 21, 'En degene uit Egypte die hem had gekocht, zei tot zijn vrouw: "Geef hem een eervol verblijf, misschien zal hij ons voordeel brengen, of zullen wij hem als een zoon aannemen." Zo verstevigden Wij de positie van Yôsoef op aarde, opdat Wij hem de uitleg van de vertellingen (dromen) zouden onderwijzen. En Allah is de Beheerser van Zijn zaken, maar de meeste mensen weten (het) niet.'),
(1618, 12, 22, 'En toen hij (Yôesoef) zijn volle kracht had bereikt, gaven Wij hem wijsheid en kennis. En zo belonen Wij de weldoeners.'),
(1619, 12, 23, 'En zij in wier huis bij verbleef, probeerde hem te verleiden, tegen zijn wil en zij sloot de deuren en zij zei: "Kom hier." Hij zei: "(ik zoek mijn) toe vlucht bij Allah. Voorwaar, Hij is mijn Heer, (Hij geeft mij) mijn beste plaats. Voorwaar, de onrechtplegers zullen niet slagen."'),
(1620, 12, 24, 'En voorzeker, zij begeerde hem. Als hij geen Teken van zijn Heer had gezien. zou hij haar hebben begeerd. Zo was het, opdat Wij het kwaad en de zedeloosheid zouden afwenden. En voorwaar, hij is één van Onze oprechte dienaren.'),
(1621, 12, 25, 'En heiden renden naar de deur en zij scheurde zijn hemd aan de achterkant en zij troffen haar man aan bij de deur. Zij zei: "Is er een vergelding voor hem die jouw familie kwaad wil doen, anders dan gevangenzetting, of een pijnlijke bestraffing"'),
(1622, 12, 26, 'Hij (Yôesoef) zei: "Zij verleidde mij tegen mijn wil," en een getuige van haar familie getuigde: "Als zijn hemd aan de voorkant is gescheurd, dan heeft zij gelijk en behoort hij tot de leugenaars.'),
(1623, 12, 27, 'Maar als zijn hemd aan de achterkant is gescheurd, dan heeft zij gelogen en behoort hij tot de waarachtigen."'),
(1624, 12, 28, 'En toen hij (de getuige) zag dat zijn hemd am de achterkant was gescheurd, zei hij: "Voorwaar, het was één van jullie listen. Voomaar, jullie listen zijn enorm".'),
(1625, 12, 29, 'Yôesoef, wend je hier van af En jij (O vrouw) vraag om vergeving voor jouw zonde. Voorwaar, jij behoort tot de zondaren.'),
(1626, 12, 30, 'En enkele vrouwen in de stad zelden: "De vrouw van Al \'Azîz verleidt haar slaaf tegen zijn wil, hij heeft haar hevig verliefd gemaakt. En voorwaar, wij zien haar in duidelijke dwaling verkeren."'),
(1627, 12, 31, 'Toen zij hoorde over hun kwaadsprekerij, liet zij hen komen en zij zette voor hen kussens gereed en zij voorzag een ieder van hen van een mes. Zij zei: Komt tevoorschijn voor hen." En toen zij hem zagen waren zij van hem onder de indruk en verwondden zij hun handen, en zij zeiden: "Heilig is Allah, dit is geen mens, dit is niets dan een nobele Engel!"'),
(1628, 12, 32, 'Zij zei: "Dit is degene vanwege wie juille mij verwijten. En voorzeker, ik heb geprobeerd hem te verleiden tegen zijn wil, waarop hij weigerde. Maar als hij niet doet wat ik beveel, dan zal hij zeker gevangen gezet worden en zal hij zeker tot de vernederden behoren."'),
(1629, 12, 33, 'Hij zei: "Mijn Heer, de gevangenschap is mij liever dan wat waar zij mij toe uitnodigt, als U hun list niet van mij afwendt, zal ik tot hen neigen, en zal ik tot de enwetenden behoren."'),
(1630, 12, 34, 'Toen verhoorde zijn Heer hem en wendde hun list van hem af. Voorwaar, Hij is de Alhorende, de Alwetende.'),
(1631, 12, 35, 'Vervolgens leek het hen, nadat zij de bewijzen hadden gezien (een goed plan) om hem voor een tijd gevangen te zetten.'),
(1632, 12, 36, 'Ein met hem kwamen twee jonge mannen in de gevangenis. Eén van hen zei: "Voorwaar, ik zag dat ik in een droom druiven perste." En de ander zei: "Ik zag dat ik in een droom brood op mijn hoofd droeg, waarvan de vogels aten. Vertel ons de uitleg ervan, wij beschouwen jou als behorende tot de kenners van droomuitleg."'),
(1633, 12, 37, 'Hij zei: "Er zal geen voedsel tot jullie komen ah levensvoorziening" of ik zal jullie vertellen over de uitleg ervan, vóórdat het jullie bereikt. Dat is wat mijn Heer mij heeft onderwezen. Voorwaar, ik heb de godsdienst verlaten van een volk dat niet in Allah gelooft, en zij geloven niet in het Hiernanaals.'),
(1634, 12, 38, 'En ik volgde de godsdienst van mijn vaderen: (Ibrâhîm, Ishâq en Ya\'qôeb, het pas ons niet om iets als deelgenoot am Allah toe te kennen. Dat is één van de gunsten van Allah aan ons en aan de mensen, maar de meeste mensen zijn niet dankbaar."'),
(1635, 12, 39, '(Yôesoef zei:) "O mijn medegevangenen, zijn verschillende heren beter, of Allah, de Ene, de Overweldiger?'),
(1636, 12, 40, 'Wat jullie naast Hem aanbidden zijn slechts namen die jullie en jullie vaderen hebben gegeven. Allah heeft hiervoor geen bewijs neergezonden. Het oordeel is slechts aan Allah. Hij beveeft dat jullie niets aanbidden behalve Hem: dat is de ware godsdieng maar de meeste mensen weten het niet."'),
(1637, 12, 41, 'O mijn medegevangenen, wat één van jullie betreft; hij zal voor zijn heer wijn inschenken. Wat de andere betret hij zal gekruisigd worden en de vogels zullen van zijn hoofd eten. De zaak waarover jullie vmgen, is reeds besloten."'),
(1638, 12, 42, 'En bij zei tegen de andere, van wie hij dacht dat hij gered zou worden: "Noem mij tegenover jouw heer." Maar de Satan maaktc dat hij vergat zijn naam te noemen voor zijn heer. Daarom verbleef hij een aantal jaren in de gevangenis.'),
(1639, 12, 43, 'En de koning zei: "Ik zag in een droom zeven vette koeien, die verslonden werden door zeven magere koeien, en zeven groene korenaren en (zeven) andere verdorde. O jullie vooraanstaanden, legt mijn droom uit, als jullie kenners van droomuitleg zijn."'),
(1640, 12, 44, 'Zij zeiden: "Dit is een verwarde mengeling van dromen, en wij zijn geen kenners van de dromenuitleg."'),
(1641, 12, 45, 'En degene die gered werd van hen, herinnerde het zich na een tijd, en zei: "Ik zal jullie de uitleg ervan vertellen, zendt mij daarom."'),
(1642, 12, 46, '(Hij zei:) "O Yôesoef, de zeer waarachtige, leg ons uit over de zeven vette koeien die verslonden werden door zeven magere koeien en over de groene korenaren en de andere (zeven) verdorde, opdat ik terug zal gaan naar de mensen. Hopelijk zullen zij (het) weten."'),
(1643, 12, 47, 'Hij (Yôesoef) zei: "Jullie zullen zeven jaren zaaien zoals gewoonlijk, en wat jullie oogsten, laat het in de aren, behalve een klein gedeelte dat jullie eten.'),
(1644, 12, 48, 'En daarna komen zeven moeilijke jaren die alles verteren wat jullie opgeslagen hebben, behalve wat jullie (veilig) bewaard hebben.'),
(1645, 12, 49, 'Vervolgens komt daarna een jeu waarin de mensen regen zullen krijgen en daarin zullen zij persen."'),
(1646, 12, 50, 'En hij (de koning) zei: "Brengt hein (Yôesoef) bij mij." En toen de gezant bij hem kwam, zei hij (Yôesoef): "Ga terug naar jouw heer en vraag hem hoe het is met de vrouwen die hun handen verwondden. Voorwaar, mijn Heer weet van hun list."'),
(1647, 12, 51, 'Hij (de koning) zei (tot de vrouwen): "Wat was er met jullie toen jullie probeerden hem te verleiden, tegen zijn wil? Zij zeiden: "Heilig is Allah, wij weten geen kwaad van hem." Zij (de vrouw van Al \'Azîz) zei: "De waarheid is gebleken, ik probeerde hem tegen zijn wil te verleiden. En voorwaar, bij behoort zeker tot de waarachtigen."'),
(1648, 12, 52, '(Yôesoof zei:) "Laat hem (Al \'Azîz) weten dat ik hem niet heb verraden tijdens zijn afwijzigheid. En voorwaar, Allah leidt de at van de verraders niet.'),
(1649, 12, 53, 'Ik verklaar dat ikzelf niet onschuldig ben. Voorwaar, de ziel spoort aan tot het kwade, behalve bij wie door mijn Heer benigd is. Voorwaar, mijn Heer is Vergevensgezind, Meest Barmhartig."'),
(1650, 12, 54, 'rui de koning zei: "Brengt hem bij mij, ik maak hem tot mijn vertrouweling. En toen hij tot hem sprak, zei hij: "Voorwaar, vandaag ben jij aan onze zijde een geëerde en betrouwbare."'),
(1651, 12, 55, 'Hij zei: "Maak mij (beheerder) over de schatten van het land. Voorwaar, ik ben een kundige beheerder."'),
(1652, 12, 56, 'En zo gaven Wij Yôesoef een machtige positie in het land, hij vedigde zich daarin zoals hij wilde. Wij geven onze Barmhartigheid aan wie Wij willen, Wij doen de beloning van de weldoeners niet verloren gaan.'),
(1653, 12, 57, 'En de beloning van het Hiernamaals is zeker beter voor degenen die geloofden en (Allah) vreesden.'),
(1654, 12, 58, 'En de broeders van Yôesoef kwamen en zij traden bij hem binnen. Toen herkende hij hen, terwijl zij hem niet herkenden.'),
(1655, 12, 59, 'En toen hij hen had voorzien van proviand, zei hij: "Brengt mij jullie broeder van jullie vaders kant (Benyamin). Zien jullie niet dat ik de volle maat geef, en dat ik de beste van de gastheren ben?'),
(1656, 12, 60, 'Als jullie hem niet bij mij brengen, krijgen jullie geen maat (graan) meer en benadert mij (dan) niet meer."'),
(1657, 12, 61, 'Zij zeiden: "Wij zullen proberen om zijn vader over te halen om hem mee te laten komen. Voorwaar, wij zullen het zeker doen."'),
(1658, 12, 62, 'Hij (Yôesoef) zei tegen zijn gezel: "Stop hun ruilmiddelen in hun proviandzakken, zodat zij het kunnen ontdekken als zij terugkeren tot hun familie. Hopelijk zullen zij terugkeren."'),
(1659, 12, 63, 'Toen zij bij hun vader terugkeerden, zeiden zij: "O onze vader, het graan wordt ons onthouden, laat onze broeder met ons mee gaan, zodat wij graan krijgen. En voorwaar, wij zullen zeker over hem waken."'),
(1660, 12, 64, 'Hij (Ya\'q-ôeb) zei: "Ik zal hem niet aan jullie toevertrouwen, zoals ik jullie eerder zijn broeder aan jullie toevertrouwde. Allah is de beste Waker, en Hij is de Meest Barmhartige der Erbarmers."\''),
(1661, 12, 65, 'Toen zij hun proviandzak openmaakten, vonden zij hun ruilmiddelen, en zij zeiden: "O onze vader, wat wensen wij nog meer? Dit zijn de onze goederen die aan ons teruggegeven zijn, en wij kunnen over onze broeder waken en wij kunnen een extra maat (graan) krijgen, zoveel als een kameel kan dragen. Dat is een makkelijke maat."\''),
(1662, 12, 66, 'Hij (Ya\'q-ôeb) zei: "Ik zal hem nooit met jullie mee laten gaan, vóórdat jullie een belofte afleggen in de Naam van Allah: dat jullie hem zeker bij mij terug zullen brengen, behalve als jullie omsingeld worden." Toen zij hun beloften afgelegd hadden, zei hij: "Allah is Gelitige van wat wij gezegd hebben."'),
(1663, 12, 67, 'Hij zei: "O mijn zonen, ga niet door één poort naar binnen, mar ga door verschillende poorten naar binnen. Ik ken niets voor jullie doen tegen (de wil van) Allah, het oordeel is slechts aan Allah. Op Hem heb ik mijn vertrouwen gesteld. En laten zij die vertrouwen hebben op Hem hun vertrouwen stellen."'),
(1664, 12, 68, 'En toen zij binnentraden zoals hun vader bevolen had, hielp hen dat niets tegen de beschikking van Allah. Het was niets dan een wens van Ya\'qôeb waartoe hij besloten had. Voorwaar, hij (Ya\'qôeb) is zeker de bezitter van kennis omdat Wij hem onderwezen hebben, maar de meeste mensen weten (het) niet."'),
(1665, 12, 69, 'Toen zij bij Yôesoef binnenkwamen, nam hij zijn broeder (Benyamin) met zich mee naar zijn plaats, en zei: "Voorwaar, ik ben jouw broeder, treurt daarom niet over wat zij plachten te doen."'),
(1666, 12, 70, 'Toen hij ben van proviand voorzien had, stopte Yôesoef zijn drinkbeker in de proviandzak van zijn broeder. Toen riep een oproeper: "O jullie van de karavaan! Voorwaar, jullie zijn zeker dieven!"'),
(1667, 12, 71, 'Zij zeiden, terwijl zij op hen toekwamen: "Wat missen jullie?"'),
(1668, 12, 72, 'Zij zeiden: "Wij missen de drinkbeker van de koning, en wie hem terugbrengt zal een kameellading graan doen toekomnen, dat garandeer ik."'),
(1669, 12, 73, 'Zij (de broeders van Yôesoef) zeiden: "Wij zweren bij Allah dat jullie zeker weten dat wij niet gekomen zijn om in het land verderf te zaaien en wij zijn ook geen dieven."'),
(1670, 12, 74, 'Zij zeiden: "Wat zal de vergelding zijn als jullie leugenaars zijn?"'),
(1671, 12, 75, 'Zij zeiden: "De bestraffing van degene bij wie de drinkbeker in zijn proviandzak gevonden wordt is dat hij zelf (als een slaaf) vastgehouden zal worden. Zo bestraffen wij de onrechtplegers."'),
(1672, 12, 76, 'Toen begon hij (Yôesoef) hun proviondzakken te onderzoeken, vóór de proviandzak van zijn broeder, vervolgens haalde hij haar (de drinkbeker) uit de proviandzak van zijn broeder (Benyamin). Zo maakten Wij het plan voor Yôesoef. Het pastte hem niet om zijn broeder te bestraffen volgens de wet van de koning, behalve als Allah dat wilde. Wij verhogen de rang van wie Wij willen. En boven iedere bezitter van kennis, is er iemand met nog meer kennis.'),
(1673, 12, 77, 'Zij zeiden: "Als hij steelt, voorzeker een broeder van hem heeft eerder gestolen." Yôesoef had dit geheim gehouden en niet aan hen verteld. Hij zei: "Jullie hebben een slechtere plaats hij Allah en Allah weet beter wat jullie beschrijven."'),
(1674, 12, 78, 'Zij zeiden, "O al \'Azîz, voorwaar, hij heeft een oude vader, en neem daarom een van ons in plaats van hem. Voorwaar, wij zien dat jij tot de weldoeners behoort."'),
(1675, 12, 79, 'Hij (Yôesoef) zei: "Ik zoek mijn toevlucht tot Allah, dat wij iemand zouden vastnemen, behalve degene hij wie wij onze goederen aangetroffen hebben. Anders zouden wij zeker tot de onrechtplegers behoren."'),
(1676, 12, 80, 'Toen zij wanhoopten aan de beslissing van hem (Yôesoef), overleg plegend in het geheim, zei de oudste van hen: "Weten jullie niet dat jullie vader een belofte van jullie heeft aanvaard in de Naam van Allah? Jullie waren eerder nalatig met Yôesoef Daarom zal ik het land nimmer verlaten totdat mijn vader mij toestemming geeft, of Allah aan mij de beslissing geeft. En Hij is de Beste der Rechters.'),
(1677, 12, 81, 'Keert terug naar jullie vader en zegt hem: \'O onze vader, voorwaar, uw zoon heeft gestolen en wij kunnen alleen getuigen zijn van wat wij weten. En wij zijn geen waker over het verborgene.'),
(1678, 12, 82, 'En vraagt aan (de bewoners van) het land waar wij woonden, en (hij) de karavaan die wij ontmoetten. En voorwaar, wij zijn zeker waarachtigen."'),
(1679, 12, 83, 'Hij (Ya\'qôcb) zei: "Welnee, jullie hebben voor jullie zelf iets moois verzonnen (Mijn) geduld is goed. Hopelijk brengt Allah hen allen terug hij mij. Voorwaar, Hij is de Alwetende, de Alwijze."'),
(1680, 12, 84, 'En hij (Ya\'qôeb) wendde zich af van hen en zei: "Ik heb medelijden met Yôesoef," en zijn ogen werden wit van verdriet en hij beheerste zijn woede.'),
(1681, 12, 85, 'Zij zeiden: "Bij Allah, jij zal aan Yôesoef blijven denken totdat je er ziek van wordt of jij behoort tot hen die te gronde gaan."'),
(1682, 12, 86, 'Hij zei: "Voorwaar, alleen bij Allah klaag ik over mijn ellende en verdriet, en ik weet van Allah wat jullie niet weten."'),
(1683, 12, 87, '"O mijn zonen, gaat heen om nieuws in te winnen over Yôesoef en zijn broeder en wanhoopt niet aan de Genade van Allah. Voorwaar, niemand wanhoopt aan de Genade van Allah, behalve het ongelovige volk."'),
(1684, 12, 88, 'Toen zij bij hem (Yôesoef) binnenkwamen, zeiden zij: "O al \'Azîz, wij en onze familie zijn getroffen door ellende, en wij komen goederen brengen die niet waardevol zijn, dus geef ons daarom de volle maat en schenk ons (een gift). Voorwaar, Allah beloont de schenkers."'),
(1685, 12, 89, 'Hij zei: "Beseften jullie wat jullie met Yôesoef en zijn broeder gedaan hebben, toen jullie onwetend waren?"'),
(1686, 12, 90, 'Zij zeiden: "Ben jij echt Yôesoef?" Hij antwoordde: "Ik ben Yôesoef en dit is mijn broeder. Allah heeft ons genade geschonken. Voorwaar, wie (Allah) vreest en geduldig is: voorwaar, Allah doet de beloning van de weldoeners niet verloren gaan."'),
(1687, 12, 91, 'Zij zeiden: "Bij Allah, voorzeker, Allah heeft jou boven ons verheven. Voorwaar, wij waren zeker zondaren."'),
(1688, 12, 92, 'Hij zei: "Er is voor jullie geen verwijt op deze dag. En hopelijk vergeeft Allah jullie, en Hij is de Meest Barmhartige der Erbarmers.'),
(1689, 12, 93, 'Neemt dit hemd van mij met jullie mee, legt het vervolgens op het gezicht van mijn vader, dan zal hij weer kunnen zien. En brengt al jullie familieleden bij mij."'),
(1690, 12, 94, 'En toen de karavaan het land uittrok, zei hun vader. "Voorwaar, ik ruik de geur van Yôesoef, als jullie mij niet als zwakzinnig zouden beschouwen (zou ik jullie vertellen dat Yôesoef nog leeft.)"'),
(1691, 12, 95, 'Zij zeiden: "Bij Allah, voorwaar, jij verkeert nog in je oude waanvoorstelling!"'),
(1692, 12, 96, 'En toen de verkondiger van de verheugende tijding kwam, legde hij het (hemd) op zijn gezicht, hij (Ya\'qôeb) kreeg vervolgens zijn gezichtsvermogen terug, en zei: "Voorwaar, heb ik jullie niet gezegd dat ik van Allah weet wat jullie niet weten?"'),
(1693, 12, 97, 'Zij zeiden: "O onze vader, vraag voor ons vergeving voor onze zonden. Voorwaar, wij waren zondaren."'),
(1694, 12, 98, 'Hij (Ya\'qôeb) zei: "Ik zal mijn Heer vergeving voor jullie vragen. Voorwaar, Hij is de Vergevens-gezinde, de Meest Barmhartige."'),
(1695, 12, 99, 'Toen zij binnenkwamen bij Yôesoef, omhelsde hij zijn onders, en hij zei: "Gaat Egypte binnen. Als Allah het wil, zullen jullie veilig zijn."'),
(1696, 12, 100, 'En hij bracht zijn ouders naar de slaapplaats. En zij wierpen zich ter aarde voor hem (Yôesoef). En hij zei: "O mijn vader, dit is de uitleg van mijn vroegere droom; waarlijk, mijn Heer heeft het werkelijkheid laten worden. En waarlijk, mijn Heer heeft goed voor mij gezorgd toen hij mij vrijgelaten heeft uit de gevangenis en toen hij jullie uit de woestijn bracht, nadat de Satan de relatie tussen mij en mijn broeders verbroken had. Voorwaar, mijn Heer is Zachtmoedig voor wie Hij wil. Voorwaar, Hij is de Alwetende, de Alwijze.'),
(1697, 12, 101, 'O mijn Heer, voorzeker, U heeft mij een gedeelte van het koninkrijk gegeven en U heeft mij de uitleg van de dromen onderwezen. Schepper van de hemelen en de aarde, U bent mijn Beschermer, op de wereld en in het Hiernamaals, doe rnij sterven als iemand die zich (aan Allah) overgegeven heeft en verenig mij met de oprechten."'),
(1698, 12, 102, 'Dit behoort tot de goschiedenissen over het onwaarneembare, die Wij aan jou (O Moehammad) openbaren. Jij was niet bij hen, toen zij bijeenkwamen voor hun zaak, terwijl zij een list beraamden.'),
(1699, 12, 103, 'En er zijn veel mensen (die niet goloven), ook al wens jij dat zij gelovigen worden.'),
(1700, 12, 104, 'En jij (O Moehammad) vraagt van hen daarvoor geen beloning, hij (de Koran) is slechts een Vermaning voor de werelden.'),
(1701, 12, 105, 'En er zijn veel Tekenen in de hemelen en op de aarde waaraan zij voorbijgaan, terwijl zij zich ervan afwenden.'),
(1702, 12, 106, 'En de meesten van hen geloven niet in Allah zonder deelgenoten aan Hem toe te kennen.'),
(1703, 12, 107, 'Denken zij dan dat zij veilig zijn voor de bestraffing van Allah die hen bedekt, of dat het Uur niet onverwacht tot hen zal komen, terwijl ze het niet vermoeden?'),
(1704, 12, 108, 'Zeg: "Dit is mijn Weg (godsdienst), ik en degenen die mij volgen roepen op tot Allah, op grond van een duidelijk bewijs. Heilig is Allah, en ik behoor niet tot de veelgodenaanbidders."'),
(1705, 12, 109, 'Wij hebben vóór jou niemand gezonden, behalve mannen van de bewoners van de steden aan wie Wij openbaarden. Reizen zij dan niet rond op de aarde, zodat zij zien boe het einde was van degenen vóór hen? En het Huis van het Hiernamaals is beter voor degenen die (Allah) vrezen. Denken jullie dan niet na?'),
(1706, 12, 110, 'Totdat de Boodschappers wanhoopten en dachten dat zij werden geloochend, toen kwam Onze hulp tot hen. En Wij redden wie Wij wilden. En Onze bestraffing kan niet tegengehouden worden van het misdadige volk.'),
(1707, 12, 111, 'Voorzeker, cr is een lering in hun geschiedenissen voor de bezitters van verstand. Hij (de Koran) is geen verzonnen verhaal, maar is er als bevestiging van wat ervoor was, als een verduidelijking van alle dingen, als Leiding en als een Barmhartigheid voor een volk dat gelooft.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Alif Lâm Mîm Râ. Dit zijnde Verzen van Het Boek. En wat am jou van jouw Heer neergezonden is, is de Waarheid; maar de meeste mensen geloven het niet.'),
(1709, 13, 2, 'Allah is Degene Die de hemelen heeft verbeven, zonder steunpilaren die jullie zien. Vervolgens zetelde Hij Zich op de Troon en maakte de zon en de maan dienstbaar. Ieder draait tot een vastgestelde tijd. Hij (Allah) regelt de zaak (en) legt de Tekenen uit. Hopelijk zullen jullie vo de ontmoeting met jullie Heer overtuigd zijn.'),
(1710, 13, 3, 'Hij is Degene Die die de aarde heeft uitgespreid en daarop stevige bergen en rivieren heeft gemaakt, en van alle soorten vruchten heeft Hij paren gemaakt. Hij doet de nacht de dag bedekken. Voorwaar, daarin zijn Tekenen voof een volk, dat nadenkt.'),
(1711, 13, 4, 'En op de aarde zijn streken naast elkaar, en druivenstruiken, en planten en dadelpalmen, sommige met takken, andere met enkelvoudige stammen, begoten met één soort water. Wij doen sommige van hen beter eetbaar zijn dan andere. Voorwaar, daarin zijn Tekenen voor een volk dat begrijpt.'),
(1712, 13, 5, 'En als jij je verbaast, nog verbazender zijn dan hun woorden: "Wanneer wij tot aarde geworden zijn, zullen wij dan zeker een nieuw schepsel worden?" Zij zijn degenen die niet in hun Heer geloven en zij zullen degenen met de ketenen om hun nekken zijn. En zij zijn degenen die de bewoners van de Hel zijn, zij zijn daarin eeuwig levenden.'),
(1713, 13, 6, 'En zij vragen aan jou (O Moehammad) om verhaasting van het slechte, vóór het goede. En de voorbeelden van de bestraffing zijn reeds voorafgegaan. Voorwaar, jouw Heer is de Bezitter van vergeving voor de mensen, ondanks hun onrechtvaardige daden. En voorwaar, jouw Heer is zeker streng in de bestraffing.'),
(1714, 13, 7, 'En degenen die ongelovig zijn, zeggen: "Waarom is er geen Teken van zijn Heer naar hem (Moehammad) neergezonden?" Voorwaar, jij bent slechts een waarschuwer en voor elk volk is er een leider (Profeet).'),
(1715, 13, 8, 'Allah weet wat iedere vrouw draagt en wat de baarmoeders verminderen of wat zij vermeerderen. Ein alle dingen hebben bij Hem een maatgeving.'),
(1716, 13, 9, '(Hij is) de Kenner van het onwaarneembare en het waarneembare, de Grootste, de Allerhoogste.'),
(1717, 13, 10, '(Voor Hem) is degene onder jullie die het woord verbergt gelijk aan degene die het hardop uitspreekt. En hij die verbergt in de nacht (is gelijk aan wie) overdag bekendmaakt.'),
(1718, 13, 11, '- En voor de mens zijn er bewakers (Engelen), voor hem en achter hem, zij waken over hem op bevel van Allah. Voorwaar, Allah verandert de (goede) toestand van een Yolk niet, totdat zij hun eigen toestand verarderen. En als Allah iets kwaads voor een volk wil, dan is er niemand die het kan afwenden en er is voor hen naast Hem geen Helper.'),
(1719, 13, 12, 'Hij is Degene die de bliksem aan jullie laat zien, om vrees en hoop (regen) te veroorzaken, en Hij doet de zware wolken ontstaan.'),
(1720, 13, 13, 'En de donder roemt Hem met Zijn loflprijzing en (ook) de Engelen. uit vrees voor Hem en Hij stuurt de bliksemschichten en treft ermee wie Hij wil. En zij redetwisten over Allah, en Hij is hard in het grijpen.'),
(1721, 13, 14, 'Aan Hem is de ware smeekbede gericht, en degenen die zij naast Hem aanbidden verhoren hen in niets, behalve zoals bij degene zijn beide handen uitstrekt naar water om het naar zijn mond te brengen, maar het bereikt zijn mond niet. En de smeekbeden van de ongelovigen zijn nutteloos.'),
(1722, 13, 15, 'En voor Allah werpt zich neer wat zich in de hemelen en op de aarde bevindt, gewillig of ongewillig, en ook hun schaduwen (onderwerpen zich) in de ochtend en in de avond.'),
(1723, 13, 16, 'Zeg: "Wie is de Heer van de hemelen en de aarde?" Zeg: "Allah." Zeg.- "Nemen jullie dan naast Hem beschermers, terwijl zij geen macht hebben om voor zichzelf nut (te verwerven) of schade (af te wenden). Zeg: "Zijn de blinden en de zienden gelijk, of zijn de duisternissen en het licht aan elkaar gelijk? Of kenden zij naast Allah deelgenoten toe. die iets geschapen zouden hebben, zoals Zijn schepping?" Zodat het scheppen voor hen hetzelfde is, zeg: "Allah is de Schepper van alles en Hij is de Ene, de Overweldiger."'),
(1724, 13, 17, 'Hij (Allah) deed water uit de hemel neerdalen, dat daarna in beddingen naar hun omvang stroomt en de stroom dragt rijzend schuim mee. En uit wat zij in het vuur verhitten om sieraden of gebruiksvoorwerpen (te maken) komt soortgelijk schuim. Zo geeft Allah een gelijkenis over de Waarheid en de valsheid. Wat het schuim betreft, het verdwijnt als iets nutteloos, en wat betreft hetgeen de mensen baat, dat blijft op de aarde. Zo geeft Allah de gelijkenissen.'),
(1725, 13, 18, 'Voor degenen die aan hun Heer gehoor geven, is er het goede (het Paradijs). En degenen die Hem geen gehoor geven: al hadden zij alles op aarde en nog eens zo veel daarbij; zij zouden zich daarmee willen vrijkopn. Zij zijn degenen voor wie er een slechte afrekening zal zijn. En hun verblijfplaats is de Hel, dat is de slechtste plaats!'),
(1726, 13, 19, 'Is iemand die weet dat wat een jou is neergezonden van jouw Heer de Waarheid is, gelijk aan degene die blind is? Het zijn slechts de bezitters van verstand die er lering uit trokken.'),
(1727, 13, 20, '(Zij zijn) degenen die het verbond met Allah nakomen en het verbond niet verbreken.'),
(1728, 13, 21, 'En degenen die onderhouden wat Allah heeft bevolen te onderhouden. En zij vrezen hun Heer en zij zijn bang voor de slechte aftekening.'),
(1729, 13, 22, 'En degenen die geduldig zijn bij het zoeken naar het welbehagen van hun Heer, en die de shalât onderhouden en die bijdragen geven van waar Wij hun mee voorzagen, in het verborgene en openlijk. En die door het goede het kwade opheffen. Zij zijn degenen voor wie er de goede eindbestemming is.'),
(1730, 13, 23, 'Zij treden de Tuinen van "Adn (het paradijs) binnen en (ook) degenen die oprecht waren van hun vaderen en hun echtgenoten en hun nakomelingen. En de Engelen treden bij hen binnen door iedere poort.'),
(1731, 13, 24, '(Zeggend:) "Salâmoen alaikoem bima shabartoem." (Vrede zij met jullie wegens jullie geduldig zijn) Het is de beste eindbestemming.'),
(1732, 13, 25, 'En degenen die het verbond met Allah na de bekrachtiging ervan schenden en die verbreken wat Allah bevolen heeft om te verbinden en die verderf zaaien op de aarde: zij zijn degenen voor wie er de vervloeking en de slechte verblijfplaats is.'),
(1733, 13, 26, 'Allah verruimt de levensvoorziening voor wie Hij wil en Hij beperkt. Zij verheugen zich over het wereldse leven, terwijl het wereldse leven in vergelijking met het Hiernamaals slechts een verganke lijke genieting is.'),
(1734, 13, 27, 'En degenen die ongelovig zijn, zeggen: "Waarom is er geen Teken aan hem (Moehammad) neergezonden van zijn Heer?" Zeg: "Voorwaar, Allah doet dwalen wie Hij wil, en Hij leidt naar Zich toe wie berouw toont."'),
(1735, 13, 28, '(Zij zijn) degenen die gelovigen zijn en wiens harten tot rust komen door het gedenken van Allah. Weet: door het gedenken van Allah komen de harten tot rust.'),
(1736, 13, 29, 'En degenen die geloven en het goede werken verrichten: voor hen is er een goed leven en een goede plaats van terugkeer.'),
(1737, 13, 30, 'En zo hebben Wij jou (O Moehammad) tot een gemeenschap gezonden, waaraan andere gemenschappen vooraf zijn gegaan, om aan hen (de Koran) voor te dragen. die Wij aan jou hebben geopenbaard, terwijl zij de Erbarmer niet geloven. Zeg: "Hij is mijn Heer. er is geen god dan Hij, op Hem heb ik mijn vertrouwen gesteld en tot Hem is de terugkeer."'),
(1738, 13, 31, 'En als er een oplezing (een geopenbaard boak) zou zijn, waardoor de bergen verplaatst worden of de aarde gespleten werd of de doden zouden kunnen spreken (dan zou dat deze Koran zijn). Maar bij Allah berusten alle zaken. Weten degenen die gelovigen niet, dat als Allah het zou willen, Hij zeker alle mensen geleid zou hebben? En tegenspoed blijft degenen die ongelovig zijn treffen wegens wat zij bedreven. Of er zal een ramp vlakbij hun huizen Plaatsvinden, totdat de belofte van Allah komt. Voorwaar, Allah verbreekt de belofte niet.'),
(1739, 13, 32, 'En voorzeker, de Boodschappers vôôr jou werden bespot, waarna Ik de ongelovigen uitstel gaf. Vervolgens greep Ik hen, hoe was dan Mijn bestraffing?'),
(1740, 13, 33, 'Is Hij die waakt over wat elke ziel heeft verworven (gelijk aan de afgodsbeelden)? Zij maakten afgoden naast Allah. Zeg: "Beschrijft hen." Of willen jullie Hem inlichten over iets op de aarde dat Hij zou niet kennen? Of zijn het holle woorden? Nee, de list van degenen die ongelovig zijn, is schoon schijnend gemaakt (door de Satan). En zij houden (de mensen) af van de Weg van Allah. En wie door Allah tot dwaling gebracht wordt: voor hem is er geen gids.'),
(1741, 13, 34, 'Voor hen is er een bestraffing tijdens het wereldse leven, maar de bestraffing van het Hiernamaals is harder. En voor hen is er tegen (de bestraffing van) Allah geen beschermer.'),
(1742, 13, 35, 'De beschrijving van het Paradijs dat aan de Moettaqôen beloofd is: er stromen rivieren onder door, de vruchten erin zijn onuitputtelijk en haar schaduw ook. Dat is de eindbestemming van degenen die (Allah) vrezen. En de eindbestemming van de ongelovigen is de Hel.'),
(1743, 13, 36, 'Degenen aan wie Wij de Schrift hebben gegeven, verheugden zich over wat aan jou (O Moehammad) is neergezonden. Onder de bondgenoten zijn er die een gedeelte ervan ontkennen. Zeg: "Het is mij bevolen om alleen Allah te aanbidden en ik ken Hem geen deelgenoten toe. Alleen Hem roep ik aan, en tot Hem is mijn terugkeer.'),
(1744, 13, 37, 'En zo hebben Wij hem (de Koran) neergezonden, als een wetgeving in de Arabische taal. Als jij hun begeerten had gevolgd, nadat de kennis tot jou was gekomen, dan zou er voor jou (tegen de bestraffing van) Allah geen helper zijn en geen beschermer.'),
(1745, 13, 38, 'En voorzeker, Wij hebben Boodschappers voor jou gezonden. En Wij maakten voor hen echtgenoten en nakomelingen. En het is niet aan de Boodschappers om een Vers te brengen, tenzij met verlof van Allah. Voor elke periode is er een Boek.'),
(1746, 13, 39, 'Allah wist uit wat Hij wil en vestigt (wat Hij wil) en bij Hem bevindt zich de Oemmoelkitab.'),
(1747, 13, 40, 'En als Wij jou een deel laten zien van wat Wij hen aangezegd hebben, of jou doen sterven: voorwaar, op jou rust slechts de verkondiging. En aan Ons is de afrekening.'),
(1748, 13, 41, 'En zien zij niet dat Wij tot het land komen en Wij het van buitenaf inperken? En Allah oordeelt en niemand kan Zijn oordeel afwenden. En Hij is snel in de afrekening,'),
(1749, 13, 42, 'En waarlijk, degenen voor hen beraamden (reeds) listen, maar Allah beheerst alle listen. Hij weet wat elke ziel doet. En de ongelovigen zullen te weten komen voor wie de (goede) eindbestemming is.'),
(1750, 13, 43, 'En degenen die ongelovig zijn zeggen: jij bent geen gezondene. Zeg: "Allah is genoeg als Getuige tussen mij en jullie en tussen de mensen die kennis hebben van de Schrift."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'Alif, Lam, Ra. (Dit is) een Boek dat Wij aan jou (O Moehammad) neergezonden hebben, opdat jij de mensen uit de duisternissen naar het licht zult voeren, met verlof van hun Heer, naar het Pad van de Almachtige, de Geprezene.'),
(1752, 14, 2, 'Allah is Degene aan Wie wat er in de hemelen en op de aarde is behoort. En wee de ongelovigen, een harde bestraffing (zal hen treffen).'),
(1753, 14, 3, 'Degenen die het wereldse leven verkiezen boven het Hiernamaals, en afhouden van de Weg van Allah en die wensen dat die krom wordt. Zij zijn degenen die in vergaande dwaling verkeren.'),
(1754, 14, 4, 'En Wij hebben geen Boodschapper gezonden, of (hij sprak) de taal van zijn volk, om hen een duidelijke uitleg te geven. Daarna doet Allah dwalen wie Hij wil, en leidt Hij wie Hij wil. En Hij is de Almachtige, de Alwijze.'),
(1755, 14, 5, 'Voorzeker, wij zonden Môesa met Onze Tekenen, (zeggend:) "Voer jouw volk vanuit de duisternissen naar het licht en herinner hen aan de dagen van Allah." Daarin zijn inderdaad Tekenen voor alle geduldige dankbaren.'),
(1756, 14, 6, 'En (gedenkt) toen Môesa tegen zijn volk zei: "Gedenkt de Genade van Allah aan jullie toen Hij jullie redde van Fir\'aun en zijn volgelingen, hij pijnigde jullie met zwaarste foltering: zij slachtten jullie zonen af en lieten jullie dochters in leven. Daarin was een geweldige beproeving van jullie Heer."'),
(1757, 14, 7, 'En (gedenkt) toen jullie Heer bekend maakte: "Als jullie dankbaar zijn, dan zullen Wij zeker jullie (genietingen) vermeerderen. En als jullie ondankbaar zijn: voorwaar, Mijn bestraffing is zeker hard."'),
(1758, 14, 8, 'En Môesa zei: "Als jallie ondankbaar zijn, jullie en allen die op de aarde zijn: voorwaar, Allah is zeker Behoefteloos, Geprezen."'),
(1759, 14, 9, 'Is de geschiedenis van degenen voor jullie nog niet tot jullie gekomen, (over) het volk van Nôeh, en de \'Ad en de Tsamoed en degenen na hen? Niemand kent hen behalve Allah. Hun Boodschappers kwamen tot hen met de duidelijke bewijzen, vervolgens bedekten zij met hun handen hun mond (vanwege hun verachting) en zeiden: "Voorwaar, wij geloven niet in dat, waarmee jullie gezonden zijn. En voorwaar, wij verkeren in ernstige twijfel over dat waar jullie ons toe oproepen."'),
(1760, 14, 10, 'Hun Boodschappers zeiden: "Is er twijfel over Allah, de Schepper van de hemel en de aarde? Hij roept jullie om jullie zonden te vergeven en Hij geeft jullie uitstel tot een vastgestelde tijd." Zij zeiden: "Jullie zijn slechts mensen als wij. Jullie willen ons afhouden van wat onze voorouders aanbaden. Geeft ons daarom een duidelijk bewijs!"'),
(1761, 14, 11, 'Hun Boodschappers zeiden tot hen: "Wij zijn slechts mensen als jullie, maar Allah geeft Zijn gunsten aan wie Hij wil van Zijn dienaren. En het is niet aan ons om jullie een bewijs te brengen, tenzij met verlof van Allah. En laten de gelovigen daarom hun vertrouwen stellen op Allah.'),
(1762, 14, 12, 'Waarom zouden wij niet op Allah ons vertrouwen stellen. terwijl Hij ons waarlijk heeft geleid op onze weg? En wij zullen zeker geduld hebben met de kwellingen die jullie ons aandoen. En laten daarom zij die vertrouwen hebben (hun) vertrouwen op Allah stellen."'),
(1763, 14, 13, 'En degenen die ongelovig waren, zeiden tot hun Boodschappers: "Wij zullen jullie zeker uit ons land verdrijven, of jullie moeten zeker terugkeren tot ons geloof." Toen openbaarde hun Heer aan hen: "Wij zullen de onrechtplegers zeker vernietigen.'),
(1764, 14, 14, 'En Wij zullen jullie na hen in het land vestigen. Dat is voor wie Mijn plaats (macht) vreest en Mijn aaazegging vreest."'),
(1765, 14, 15, 'En zij vroegen om een overwinning en iedere opstandige geweldenaar verloor.'),
(1766, 14, 16, 'Voor hen ligt de Hel. En hij zal worden begoten met ettervocht.'),
(1767, 14, 17, 'Hij drinkt het slokje voor slokje, en hij kan het bijna niet doorslikken. En de dood komt tot hem van alle kanten, maar hij sterft niet, en voor hem ligt nog een zware bestraffing.'),
(1768, 14, 18, 'De vergelijking met degenen die niet in hun Heer geloven, is alsof hun daden als as zijn, dat door een harde wind wordt weggeblazen op een stormachtige dag. Zij hebben geen enkele macht over wat zij hebben verworven. Dat is een vergaande dwaling.'),
(1769, 14, 19, 'Zie jij (O Moehammad) dan niet dat Allah de hemelen en de aarde met de Waarheid heeft geschapen? Wanneer Hij wil, doet Hij jullie verdwijnen en brengt Hij een nieuwe schepping.'),
(1770, 14, 20, 'En dat is voor Allah niet moeilijk.'),
(1771, 14, 21, 'En zij verschijnen allen voor Allah, en de zwakken zullen dan tot degenen die hoogmoedig waren zeggen: "Voorwaar, wij waren jullie volgelingen, kunnen jullie dan nu voor ons iets van de bestraffing van Allah afwenden?" Zij zeggen: "Als Allah ons geleid had, zouden wij jullie zeker geleid hebben; het is voor ons hetzelfde, of wij nu klagen. of dat wij geduldig zijn. Er is voor ons geen toevluchtsoord."'),
(1772, 14, 22, 'De Satan zei, nadat de zaak besloten was: "Voorwaar, Allah heeft jullie een ware belofte gedaan, en ik heb jullie een belofte gedaan, maar ik liet jullie daarna in de steek. Ik had geen macht over jullie, behalve dat ik jullie heb geroepen, waarop jullie mij gehoorzamnden, verwijt mij daarom niets! Verwijten jullie julliezelf maar. Ik kan jullie niet helpen en jullie kunnen mij niet helpen. Voorwaar. ik verwerp het, dat jullie mij voorheen als deelgenoot (aan Allah) toekenden." Voorwaar, voor de onrechtplegers is er een pijnlijke bestraffing.'),
(1773, 14, 23, 'En degenen die geloofden en goede daden verrichtten worden de Tuinen (het Paradijs) binnengeleid, waar onder door de rivieren stromen, daarin zullen zij eeuwig levenden zijn, met verlof van hun Heer. Hun begroeting daarin is "Salâm." (Vrede)'),
(1774, 14, 24, 'Zie jij niet boe Allah een vergelijking maakt met een goede uitspraak, die als een goede boom is, waarvan de wortel stevig staat en de takken naar de hemel reiken?'),
(1775, 14, 25, 'Hij geeft zijn vruchten in eik seizom, met verlof van zijn Heer. Allah maakt de vergelijkingen voor de mensen. Hopelijk zullen zij er lering uit trekken.'),
(1776, 14, 26, 'En de vergelijking met een slechte uitspraak is als die met een slechte boom, die ontworteld op de aarde staat, en die geen stevigheid heeft.'),
(1777, 14, 27, 'Allah venterkt (het geloof van) degenen die geloven met de staadvastige uitspraak tijdens het wereldse leven en in het Hienamaals; en Allah laat de onrechtplegers dwalen en Allah doet wal Hij wil.'),
(1778, 14, 28, 'Zie jij degenen niet die de genieting van Allah hebben verwisseld voor onteloof, en die hun volk naar het rijk van de ondergang hebben gebracht?'),
(1779, 14, 29, 'De Hel, waarin zij binnengaan, dat is de slechtste verblifpllaats!'),
(1780, 14, 30, 'Zij hebben am Allah deelgenoten toegekend, om (de mensen) te doen afdwalen van Zijn Weg. Zeg: "Genieten jullie maar! Voorwaar, jullie eindbestemming is de Hel."'),
(1781, 14, 31, 'Zeg (O Moehammad) tot mijn dienaren die geloven: "Onderhoudt de shalât en geeft bijdragen van waar Wij hun rnee voorzien hebben, in het verborgene of openlijk, voordat er een Dag komt, waarop er geen handel en geen boezemvriend zal zijn."'),
(1782, 14, 32, 'Allah is Degene Die de hemelen en de aarde heefl geschapen en Hij zendt uit de hemel water neer, waarmee Hij dan vruchten voortbrengt als een voorziening voor jullie. En Hij heeft het schip voor jullie dienstbaar gemaakt, opdat het op de zee zal varen op Zijn bevel. Hij maakte voor jullie de rivieren dienstbaar.'),
(1783, 14, 33, 'En Hij maakte voor jullie de zon en de maan dienstbaar, die onophoudelijk (draaien in hun baan). En Hij maakte voor jullie de nacht en de dag dienstbaar.'),
(1784, 14, 34, 'En Hij schonk jullie van alles wat jullie Hem vroegen. En als jullie de gunsten van Allah op willen sommen, dan kunnen jullie ze niet tellen. Voorwaar, de mensen zijn zeker onrechtvaardig, ondankbaar.'),
(1785, 14, 35, 'En toen Ibrâhîm zei: "Mijn Heer, maak deze stad veilig en houd mij en mijn zonen ervan af dat wij afgoden zouden aanbidden.'),
(1786, 14, 36, 'Mijn Heer, voorwaar, zij hebben vele mensen doen dwalen. Wie mij dan volgt: voorwaar, die behoort bij mij. En wie mij niet gehoorzaant: voorwaar, U bent Vergevensgezind, Meest Barmbartig.'),
(1787, 14, 37, 'Onze Heer! Voorwaar, ik heb mijn kinderen achtergelaten in een onbegroeide vallei bij Uw gewijde huis (de Ka\'bah). Onze Heer! (ik tiet hen achter) zodat zij de shalât zullen onderhouden, laat daarom de harten van de mensen tot hen neigen, en voorzie hen van vruchten. Hopelijk zullen zij dankbaar zijn.'),
(1788, 14, 38, 'Onze Heer! Voorwaar, U weet wat wij verbergen en wat wij openlijk doen en et is voor Allah niets verborgen op de aarde en niet in de hemel.'),
(1789, 14, 39, 'Alle lof zij Allah, die mij Isma\'îl en Ishaq geschonken heeft, ondanks mijn ouderdom. Voorwaar, mijn Heer verhoort zeker de smeekbeden.'),
(1790, 14, 40, 'Mijn Heer, mad mij en mijn nakomelingen onderhouders van de shalât. Onze Heer, verhoor mijn smeekbeden.'),
(1791, 14, 41, 'Mijn Heer, vergeef mij en mijn ouders en de gelovigen op de Dag waarop de afrekening plaats vindt."'),
(1792, 14, 42, 'En denk niet (O Moehammad) dat Allah achteloos is tegenover wat de onrechtplegers doen. Voorwaar, Hij geeft hun uitstel tot de Dag waarop de ogen verstard openstaan.'),
(1793, 14, 43, 'Zij hassten zich met opgeheven hoofd, teirwijl hun ogen niet knipperen en terwijl hun harten leeg zijn.'),
(1794, 14, 44, 'En waarschuw de mensen voor de Dag dat de bestraffing tot hen komt en waarop degenen die onrecht pleegden, zeggen: "Onze Heer, geef ons uitstel voor een korte tijd, zodat wij aan Uw oproep gehoor geven en de Boodschappers volgen." (Tot hen wordt gezegd:) "Jullie hebben vroeger toch gezworen, dat jullie nimmer zouden sterven?'),
(1795, 14, 45, 'En jullie verbleven in woonplaatsen van degenen die zichzelf onrecht aandeden, en het is voor jullie duidelijk geworden hoe Wij hen behandelen. En Wij hebben voor jullie vergelijkingen gemaakt."'),
(1796, 14, 46, 'En waarlij\'k, zij hebben hun list beraamd, maar hun list is (bekend) bij Allah. Ondanks dat bun list (zo geweldig was) dat de bergen er door zouden vergaan.'),
(1797, 14, 47, 'Denkt daarom niet dat Allah Zijn belofte zal verbreken aan Zijn Boodschappers. Voorwaar, Allah is de Verhevene en de Bezitter van de Vergelding.'),
(1798, 14, 48, 'Op de Dag dat de aarde wordt vervangen door een andere aarde, en zo ook de hemelen. En de mensen zullen verschijnen voor Allah, de Ene, de Overweldiger.'),
(1799, 14, 49, 'En jij zal op die Dag de misdadigers zien, bij eengebonden in ketenen.'),
(1800, 14, 50, 'Hun kleding zal van toer zijn en de Hel zal hun gezichten bedekken.'),
(1801, 14, 51, 'Zodat Allah elke ziel zal belonen voor wat zij heeft gedaan. Voorwaar, Allah is snel in de afrekening.'),
(1802, 14, 52, 'Dit is een Verkondiging voor de mensen, opdat zij daardoor gewaarschuwd zullen worden, en opdat zij weten dat Hij de Enige God is en opdat de bezitters van verstand de vermaningen ter harte zullen nemen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Alif Lam Ra. Dit zijn Verzen van het Boek, en een duidelijke Koran.'),
(1804, 15, 2, 'Misschien zullen degenen die ongelovig zijn wensen dat zij Moslims waren.'),
(1805, 15, 3, 'Lad hen eten en zich vermaken en worden afgeleid door de (valse) hoop, later ze zullen (het) weten.'),
(1806, 15, 4, 'En Wij hebben nooit een stad vernietigd, behalve op een vastgestelde tijdstip.'),
(1807, 15, 5, 'Er is geen gemeenschap die haar tijdstip kan verhaasten of vertragen.'),
(1808, 15, 6, 'Zij zeiden: "O jij, aan wie de vermaning (Koran) is neergezonden: voorwaar, jij bent zeker bezeten.'),
(1809, 15, 7, 'Waarom heb jij de Engelen niet naar ons gebracht, als jij tot de waarachtigen behoort?"\''),
(1810, 15, 8, 'Wij sturen de Engelen niet, behalve met de Waarheid. En zij zouden dan geen uitstel (van hun bestraffing) hebben gekregen.'),
(1811, 15, 9, 'Voorwaar, Wij zijn het Die de Vermaning (de Koran) hebben neergezonden. En voorwaar, Wij zijn daarover zeker de Wakers.'),
(1812, 15, 10, 'En voorzeker, Wij hebben (Boodschappers) voor jou gezonden naar de vroegere volkeren.'),
(1813, 15, 11, 'En er kwam nooit een Boodschapper tot hen of zij dreven de spot met hen.'),
(1814, 15, 12, 'Op deze wijze doen wij het (ongeloof) in de harten van de misdadigers binnengaan.'),
(1815, 15, 13, 'Zij geloven er niet in, hoewel de gebruikelijke handelwijze (de bestraffing van Allah) van de vroegeren wazlijk heeft plaatsgevonden.'),
(1816, 15, 14, 'En als Wij voor hen een poort van de hemel zouden openen, wardoor zij dan zouden kunnen blijven opstijgen.'),
(1817, 15, 15, '(Dan) zouden zij zeker zeggen: "Voorwaar, ons gezichtsvermogen is beneveld; wij zijn zelfs een betoverd volk."'),
(1818, 15, 16, 'En voorzeker, Wij hebben in de hemel sterrenstelsels aangebracht en Wij hebben haar versierd voor de aanschouwers.'),
(1819, 15, 17, 'En Wij hebben haar bewaakt tegen elke vervloekte Satan.'),
(1820, 15, 18, 'Behalve degene die (de Satan) heeft afgeluisterd, dan achtervolgd wordt door een heldere vlam.'),
(1821, 15, 19, 'En Wij hebben de aarde uitgestrekt en Wij hebben daarop bergen geplaatst en Wij hebben daarop van alles doen groeien volgens een evenwichte maat.'),
(1822, 15, 20, 'En Wij hebben voor jullie daar levensonderhoud gemaakt, (ook voor) degene voor wie jullie niet de voorzieners zijn.'),
(1823, 15, 21, 'Er is geen ding waarvan de schatten niet bij Ons zijn, en Wij zenden deze slechts volgens een vastgestelde maatgeving neer.'),
(1824, 15, 22, 'En Wij hebben de winden gezonden als bestuivers en Wij hebben regen neergezonden uit de hemel, waarmee wij jullie te drinken geven. En jullie zijn daar niet de bewaarders van.'),
(1825, 15, 23, 'Voorwaar, Wij zijn het Die doen leven en doen sterven. En Wij zijn de erfgenamen.'),
(1826, 15, 24, 'En voorzeker, Wij kennen de mensen die jullie zijn voorgegaan (in de dood). En voorzeker, Wij kennen de achterblijvers.'),
(1827, 15, 25, 'En voorwaar, jouw Heer is het Die hen verzamelt. Voorwaar, Hij is Alwijs, Alwetend.'),
(1828, 15, 26, 'En voorzeker, Wij hebben de mens (Adam) geschapen uit klei, van zwart slijk gevormd.'),
(1829, 15, 27, 'En Wij hebben daarvoor de Djinn\'s geschapen uit een gloeiend vuur.'),
(1830, 15, 28, '(Gedenkt) toen jouw Heer tot de Engelen zei: "Voorwaar, Ik zal een mens scheppen van klei, uit zwart slijk gevormd."'),
(1831, 15, 29, 'Toen Ik hem vervolmaakt had en Mijn (geschapen) Geest erin geblazen had, toen knielden zij (de Engelen) voor hem.'),
(1832, 15, 30, 'Toen knielden de Engelen allen gezamenlijk.'),
(1833, 15, 31, 'Behalve Iblis, bij weigerde te behoren tot de knielenden.'),
(1834, 15, 32, 'Hij (Allah) zei: "O Iblis, wat is er met jou dat jij niet bij de knielenden behoort?"'),
(1835, 15, 33, 'Hij (Iblis) zei: "Ik zal niet knielen voor een mens die U heeft geschapen uit klei, uit zwarte slijk gevormd."'),
(1836, 15, 34, 'Hij (Allah) zei: "Ga eruit (het Paradijs), voor- waar, jij bent een vervloekte!'),
(1837, 15, 35, 'En voorwaar, de vervloeking rust op jou tot aan de Dag des Oordeels."'),
(1838, 15, 36, 'Hij (Iblis) zei: "Mijn Heer, schenk mij dan uitstel tot de Dag waarop zij zullen worden opgewekt."'),
(1839, 15, 37, 'Hij (Allah) zei: "Voorwaar, jij behoort tot degenen die uitdel kregen.'),
(1840, 15, 38, 'Tot de Dag van het vastgestelde tijdstip."'),
(1841, 15, 39, 'Hij (Iblis) zei. "Mijn Heer, omdat U mij heeft doen dwalen, zal ik voor ben (hun slechte daden) zeker schoonschijnend maken op de aarde, en ik zal hen zeker allen doen dwalen.'),
(1842, 15, 40, 'Behalve Uw dienaren, onder hen die oprecht zijn."'),
(1843, 15, 41, 'Hij (Allah) zei: "Dit is een recht Pad, op Mij rust (het waken erover).'),
(1844, 15, 42, 'Voorwaar, jij hebt geen macht over Mijn dienaren, behalve (over) degene die jou volgt van de dwalenden."'),
(1845, 15, 43, 'En voorwaar, de Het is aan hen allen zeker toegezegd.'),
(1846, 15, 44, 'Zij heeft zeven poorten. Aan iedere poort is een deel van hen toegewezen.'),
(1847, 15, 45, 'Voorwaar, de Moettaqoen zullen in de Tuinen (het Paradijs) en bij bronnen vertoeven.'),
(1848, 15, 46, '(Tegen hen wordt gezegd:) "Treedt deze binnen in vrede en veiligheid."'),
(1849, 15, 47, 'En Wij nemen weg wat er in hun harten aan wrok is, (zij zijn daarin) als broeders, op rustbanken zitten zij tegenover elkaar.'),
(1850, 15, 48, 'Daarin raakt hen geen vermoeidheid en zij worden daaruit niet verdreven.'),
(1851, 15, 49, 'Bericht mijn dienaren (O Moehammad:) "Voorwaar, ik ben de Vergevensgezinde, de meest Barmhartige.'),
(1852, 15, 50, 'En dat Mijn bestraffing een pijnlijke bestraffing is."'),
(1853, 15, 51, 'En bericht hun over de gasten van Ibrâhîm.'),
(1854, 15, 52, 'Toen zij bij hem binnenkwamen, zeiden zij: "Salam." (Vrede) Ibrâhîm zei: "Voorwaar, wij zijn bang voor jullie."'),
(1855, 15, 53, 'Zij zeiden: "Wees niet bang. Voorwaar, wij geven jou een verheugende tijding over (de geboorte van) een jongen, die kennis bezit."'),
(1856, 15, 54, 'Hij (Ibrâhîm) zei: "Geven jullie mij een verheugende tijding, terwijl de ouderdom mij heeft bereikt? Waarover geven jullie mij dan een verheugende tijding?"'),
(1857, 15, 55, 'Zij zeiden: "Wij hebben jou in waarheid een verheugende tijding gegeven, behoor daarom niet tot de wanhopigen."'),
(1858, 15, 56, 'Hij (Ibrâhîm) zei: "Niemand wanhoopt aan de Barmhartigheid van zijn Heer dan de dwalenden."'),
(1859, 15, 57, 'Hij (Ibrâhîm) zei: "Wat is jullie zaak, O, gezanten?"'),
(1860, 15, 58, 'Zij (de Engelen) zeiden: "Voorwaar, wij zijn gezonden tot een misdadig volk.'),
(1861, 15, 59, 'Uitgezonderd de volgelingen van Loeth. Voorwaar, wij zullen hen (in opdracht van Allah) zeker allen redden.'),
(1862, 15, 60, 'Behalve zijn vrouw, wij hebben besloten dat zij tot de achterblijversi zal behoren."'),
(1863, 15, 61, 'En toen de gezanten tot de volgelingen van Loeth kwamen.'),
(1864, 15, 62, 'Hij (Loeth) zei: "Jullie zijn een onbekend volk."'),
(1865, 15, 63, 'De gezanten (de Engelen) zeiden: "Eigenlijk zijn wij tot jou gekomen, met dat waarover zij plachten te twijfelen (de bestraffing).'),
(1866, 15, 64, 'En wij zijn tot jou gekomen met de Waarheid. En voorwaar, wij zijn zeker waarachtigen.'),
(1867, 15, 65, 'Dus vertrek daarom met jouw familie in het laatste gedeelte van de nacht. En volg achter hen (jouw familie) en laat niemand van jullie omkijken en vervolg (de reis) zoals jullie bevolen is."'),
(1868, 15, 66, 'En Wij openbaarden aan hem (Loeth) die zaak: dat zij in de ochtend zullen worden uitgeroeid.'),
(1869, 15, 67, 'En de bewoners van de stad (Sodom) verheugden zich.'),
(1870, 15, 68, 'Hij (Loeth) zei: "Voorwaar, dit zijn mijn gasten; maakt mij dus niet te schande.'),
(1871, 15, 69, 'En vreest Allah en vernedert mij niet."'),
(1872, 15, 70, 'Zij zeiden: "Hebben wij jou niet verboden (over ons te praten) tegen de mensen?"'),
(1873, 15, 71, 'Ilij (Loeth) zei: "Dit zijn mijn dochters (vrouwen uit mijn volk), als jullie (iets op toegestane wijze willen) doen."'),
(1874, 15, 72, 'Bij jouw leven (O Moehammad): voorwaar, zij verkeren onrustig in hun dwaling.'),
(1875, 15, 73, 'Toen trof de donderslag hen bij zonsopgang.'),
(1876, 15, 74, 'Toen keerden Wij haar (de stad) ondersteboven en deden Wij op hen stenen van harde klei neerkomen.'),
(1877, 15, 75, 'Voorwaar, daarin zijn zeker Tekenen voor degenen die er lering uit trekken.'),
(1878, 15, 76, 'En voorwaar, zij (de stad) ligt aan een (nog) bestaande weg.'),
(1879, 15, 77, 'En voorwaar, daarin is zeker een Teken voor de gelovigen.'),
(1880, 15, 78, 'en voorwaar, de bewoners van Aikah waren zeker onrechtplegers.'),
(1881, 15, 79, 'Toen hebben Wij hen vernietigd. En voorwaar, de beide steden liggen aan een duidelijke weg.'),
(1882, 15, 80, 'En voorzeker, de bewoners van Hidjr loochenden de Boodschappers.'),
(1883, 15, 81, 'En Wij hebben ben Onze Tekenen gegeven, maar zij plachten zich daarvan af te wenden.'),
(1884, 15, 82, 'En zij hieuwen de rotsen uit tot veilige woningen.'),
(1885, 15, 83, 'Toen trof de donderslag hen in de ochtend.'),
(1886, 15, 84, 'Toen baatte wat zij plachten te verrichten hen niet.'),
(1887, 15, 85, 'En Wij hebben de hemelen en de aarde en wat er tussen is niet geschapen behalve met de Waarheid. En voorwaar, het Uur zal zeker komen, geeft daarom em passende kwijtschelding.'),
(1888, 15, 86, 'Voorwaar, jouw fleer is de Schepper, de Alwetende.'),
(1889, 15, 87, 'En voorzeker, Wij hebben jou de zeven vaak herhaalde (Verzen) gegeven en de geweldige Koran.'),
(1890, 15, 88, 'Kijk niet verlangend uit naar de genietingen die Wij aan een groep van hen (de ongelovigen) hebben gegeven. En treur niet over hen, en wees nederig tegenover de gelovigen.'),
(1891, 15, 89, 'En zeg (O Moehammad): "Voorwaar, ik ben de duidelijke waarschuwer."'),
(1892, 15, 90, 'Zoals Wij (de bestraffing) hebben neergezonden naar de verdelers.'),
(1893, 15, 91, 'Degenen die de Koran hebben opgedeeld.'),
(1894, 15, 92, 'Bij jouw Heer, Wij zullen hen zeker allen ondervragen.'),
(1895, 15, 93, 'Over wat zij plachten te doen.'),
(1896, 15, 94, 'Verkondig daarom wat bevolen is, en wend je af van de veelgodenaanbidders.'),
(1897, 15, 95, 'Voorwaar, Wij hebben jou beschermd (tegen het kwaad) van de spotters.'),
(1898, 15, 96, 'Degenen die naast Allah een andere god plaatsen, later zullen zij (het) te weten komen.'),
(1899, 15, 97, 'En voorzeker, Wij weten dat jouw borst benauwd is wegens wat zij zeggen.'),
(1900, 15, 98, 'Heilig daarom jouw Heer met een lofprijzing en behoor tot hen die zich neerknielen.'),
(1901, 15, 99, 'En aanbid jouw Heer totdat het zekere (de dood) tot jou komt.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'De beslissing van Allah komt (zeker) wens haar dus niet te verhaasten. Heilig is Allah en Verheven boven wat zij (Hem) aan deelgenoten toekennen.'),
(1903, 16, 2, 'Hij heeft de Engelen doen neerdalen met de Openbaring van Zijn bevel. naar wie Hij wil van Zijn dienaren, om te waarschuwen: "Er is geen God dan Ik, dus vreest Mij."'),
(1904, 16, 3, 'Hij heeft de hemelen en aarde in Waarheid geschapen. Verheven is Hij boven wat zij (Hem) aan deelgenoten toekennen.'),
(1905, 16, 4, 'Hij schiep de mens uit een druppel sperma, en dan wordt hij een duidelijke hetwister (van Allah).'),
(1906, 16, 5, 'En Hij schiep het vee voor jullie: warmte en nut zijn erin, en jullie eten ervan.'),
(1907, 16, 6, 'En voor jullie is er schoonheid in, wanneer jullie het vee \'s avonds naar de stal voeren en wanneer jullie het vee \'s morgens gaan weiden.'),
(1908, 16, 7, 'En Zij dragen lasten voor jullie naar landen die jullie niet zonder moeilijkheden voor julliezelf konden bereiken. Voorwaar, jullie Heer is zeker Meest Genadig, Meest Barmhartig. THIS TEXT HAS BEEN AUTOMATICALLY GENERATED (OCR) AND REQUIRES FIXING/CHECKING. USE THE CTRL+(Right/Left Arrow) KEYS TO MOVE ALONG THE WORDS AND CHECK WITH THE LEFT SIDE. PLEASE DELETE *THIS* NOTE IF YOU ARE READY TO CHECK AND APPROVE.'),
(1909, 16, 8, 'En het paard, de muilezel en de ezel, zodatiullie erop kunnen rijden en (zij zijin) als een sieraad. En Hij schept wat jullie niet weten.'),
(1910, 16, 9, 'En het is aan Allah om de rechte Weg te tonen en er zijn er die (ervan) afwijken. En als Hij het had gewild, had Hij zeker jullie allen gelei&'),
(1911, 16, 10, 'Hij is Degene Die water uit de hemel doet neerdalen, waarvan een deel voor jullie om te drinken is, en een deel voor de gewassen waaropjullie (vee) weiden.'),
(1912, 16, 11, 'Hij brengt er voorjuitie het graan en de olijfbomen en de palmbomen en de druivenstruiken mee voort en alle vruchten. Voorwaar, daarin zijn zeker Tekenen voor een volk dat nadenkt'),
(1913, 16, 12, 'En Hij maakte voor jullie de nacht en de dag en de zon en de maan dienstbaar. En de sterren zijn dienstbaar, met Zijn vwlof Voorwaar, daarin zijn zeker Tekenen voor een volk dat begrijpt'),
(1914, 16, 13, 'En (ook in) wat Hij voor jullie maakte op aarde, verschillend van soort Voorwaar, daarin zijn zeker Tekenen voor een veik dat zich laat vermanen.'),
(1915, 16, 14, 'En Hij is Degene Die de zee diemtbau heeft getnaakt, opdat jullie uit haar vers vlees kunnen eten. en jullie halen sieraden uit haar die jullie dragen, en jullie zien de schepen op haar varen. En opdat jullie Zijn gunst zoeken en hopelijk zullen jullie dankbaar zijn.'),
(1916, 16, 15, 'En Hij bracht op de aarde gebergten aan zodat jullie niet met haar beven, en rivieren en wegen. Hopelijk zullen jullie Leiding volgen.'),
(1917, 16, 16, 'En (Hij maakte) kerunerken, en door de sterren laten zij zich de weg wijzen.'),
(1918, 16, 17, 'Is Degene Die schept dan gelijk aan degene die niet schept? Zullen juilie je niet laten vemenen?'),
(1919, 16, 18, 'En als jullie de gunsten van Allah (zouden wiilen) optellen kunnen jullie ze niet opsommen. Voorwaar, Allah is zeker Vergevensgezind, Meest Barmhartig.'),
(1920, 16, 19, 'En Allah weet wat jullie verbergen en wat jullie tonen.'),
(1921, 16, 20, 'En degenen die zij naast Allah aanroepen scheppen niets, maar zij zijn (zelf) geschapen.'),
(1922, 16, 21, 'Doden en zijn zij zonder te leven. En zij weten niet wanneer zij worden opgewekt'),
(1923, 16, 22, 'Jullie god is één God. Wat betreft degenen die niet in het Hiernamaals Ooyen: hun harten zijn verderfelijk en zij zijn hoogmoedigen.'),
(1924, 16, 23, 'Zonder twijfel weet Allah wat zij verbergen en wat zij tonen. Voorwaar, Hij houdt niet van de hoogmoedigen.'),
(1925, 16, 24, 'En wanneer ken wordt gezegd: "Wat heeft jullie Heer doen neerdalen?" zeggen zij: "Fabels van de vroegeren!"'),
(1926, 16, 25, 'Zodat zij op de Dag der Opstanding hun volledige lasten dragen en (ook een gedeelte) van de lasten van degenen zonder kennis die zij deden dwalen. Weet: slecht is het wat zij dragen!'),
(1927, 16, 26, 'Waarlijk, degenen vôôr hen beraamden ook (tegen Allah), maar Allah greep hun bouwwerken bij de fundamenten en daarop viel het dak boven op hen neer. En de bestraffing trof hen van waar zij niet beseften.'),
(1928, 16, 27, 'Vervolgens zal Hij hen op de Dag der Opstanding vernederen, en zeggen: "Waar zijn Mijn (zogenaamde)deelgenoten waarover jullie (met de gelovigen) plachten te redetwisten?" Degenen die kennis gegeven is, zullen zeggen: "Voorwaar, op deze Dag is er vernedering en kwaad voor de ongelovigen.'),
(1929, 16, 28, '(Zij zijn) degenen die door de Engelen weggenomen werden terwijl zij onrecht tegen zichzelf begingen. Dan zullen zij zich overgeven (en zeggen:) "Wij plachten geen kwaad te bedrijven. (De Engelen zullen antwoorden:) "Nee, voorwaar, Allah weet het beste wat jullie plachten te doen."'),
(1930, 16, 29, 'Dus gaat de poorten van de Hel binnen om daarin eeuwig levenden te zijn. De verblijfplaats van de hoogmoedigen is zeker het slechtst!'),
(1931, 16, 30, 'En er zal tot degenen die (Allah) vreesden gezegd worden: "Wat is het dat jullie Heer heeft doen neerdalen?" Zij zullen zeggen: "Het goede." Voor degenen die het goede in deze wereld deden, is er (in het Hiernamaals) het goede. En het Huis van het Hiernamaals is zeker beter. En het Huis van de Moettaqôen is zeker het beste.'),
(1932, 16, 31, 'Zij zullen de Tuinen van \'Adn (het Paradijs) binnengaan, waar onder door de rivieren stromen. Voor hen is daarin wat zij willen. Zo beloont Allah de Moettaqôen.'),
(1933, 16, 32, '(Zij zijn) degenen die de Engelen als reinen wegnemen, terwijl zij zeggen: "Salâmoen \'Alaikoem (Vrede zij met jullie), treedt het Paradijs binnen, wegens wat jullie plachten te doen."'),
(1934, 16, 33, 'Zij (de ongelovigen) wachten slechts tot de Engelen hen wegnemen, of het bevel van jouw Heer hen treft. Zo deden degenen vôôr hen. En Allah heeft hun geen onrecht aangedaan, maar zij plachten zichzelf onrecht aan te doen.'),
(1935, 16, 34, 'En zij werden getroffen door het slechte van wat zij deden en zij werden omsingeld door wat zij plachten te bespotten.'),
(1936, 16, 35, 'En degenen die (Allah) deelgenoten toekenden, zeiden: "Indien Allah gewild had, hadden wij niets naast Hem aanbeden, wij noch onze vaderen, en wij zouden naast (de verboden) van Hem niets verboden hebben." Zo deden degenen vôôr hen. En de Boodschappers zijn tot niets anders verplicht dan de duidelijke verkondiging.'),
(1937, 16, 36, 'En voorzeker. Wij hebben aan iedere gemeenschap een Boodschapper gezonden (die zei:) "Aanbidt Allah en houdt afstand van de Thagoet." En er zijn er onder hen die Allah leidde en er zijn er die Hij tot de dwaling veroordeelde. Reist dus op de aarde rond en zie boe het einde was van de loochenaars.'),
(1938, 16, 37, 'Ook al verlang jij (O Moehammad) er hevig naar hen te leiden: voorwaar, Allah leidt niet degene die Hij doet dwalen. En er zijn voor hen geen helpers.'),
(1939, 16, 38, 'En zij zweren bij Allah hun duurste eden dat Allah degene die sterft niet zal opwekken: integendeel, het is als een belofte waar Hij Zich aan heeft verbonden, maar de meesten van hen weten het niet.'),
(1940, 16, 39, '(Hij zal hen opwekken) om hun dat waar zij over twistten duidelijk te mdwn en om degenen die ongelovig waren te doen weten dat zij leugenaars waren.'),
(1941, 16, 40, 'Voorwaar, Ons Woord tegen iets wat Wij willen. is dat Wij er slechts tegen zeggen: "Weese" en het is.'),
(1942, 16, 41, 'En degenen die uitweken omwille van Allah nadat ben onrecht was aangedaan zullen Wij in deze wereld een goede behuizing geven, maar de beloning van het Hiernamaals zal groter zijn. Indien zij dat maar wisten!'),
(1943, 16, 42, '(Zij zijn) degenen die geduldig waren en hun Heer vertrouwden.'),
(1944, 16, 43, 'En vóór jou (O Moehammad) zonden Wij slechts mannen aan wie Wij openbaarden; vraag het dan am de bezitters van de kennis, indien jullie het niet weten.'),
(1945, 16, 44, '(Wij zonden ben) met duidelijke (Tekenen) en de Zaboer. En Wij deden aan jou de Vermaning (de Koran) neerdalen om aan de mensen duidelijk te maken wat aan hen neergezonden is. En hopelijk zullen zij nadenken.'),
(1946, 16, 45, 'Voelen degenen die slechte daden beramen zich er dan veilig voor dat Allah hen niet in de aarde doet wegzinken, of dat de bestraffing hen treft van waar zij het niet beseffen?'),
(1947, 16, 46, 'Of dat Hij hen treft tijdens hun rondgaan, terwijl zij zich het niet van zich af kunnen houden?'),
(1948, 16, 47, 'Of dat Hij hen treft terwijl zij bang zijn? Voorwaar, jullie Heer is zeker Meest Genadig, Meest Barmhartig.'),
(1949, 16, 48, 'Zien zij dan niet de dingen die Allah geschapen heeft, hoe hun schaduwen zich naar rechts en links wenden, terwijl zij zich voor Allah neerwerpen en zij nederig zijn?'),
(1950, 16, 49, 'En voor Allah werpt alles van de levende wezens in de hemelen en alles op de aarde zich neer, en (ook) de Engelen, en zij zijn niet hoogmoedig.'),
(1951, 16, 50, 'Zij vrezen hun Heer boven hen en zij doen wat Hij beveelt.'),
(1952, 16, 51, 'En Allah zei: "Neemt geen twee goden. Voorwaar, Hij is een God. Vreest daarom Mij alleen."'),
(1953, 16, 52, 'En aan Hem behoort wat er in de hemelen en (op) de aarde is en gehoorzaamheid behoort altijd aan Hem te zijn. Zullen jullie dan iemand anders dan Allah vrezen?'),
(1954, 16, 53, 'En jullie hebben geen gunst of het komt van Allah. En wanneer jullie dan door tegenspoed geraakt worden, is Hij het tot Wie jullie je nederig om hulp wenden.'),
(1955, 16, 54, 'En als Hij de tegenspoed voor jullie opheft is er een groep onder jullie die aan hun Heer deelgenoten toekent.'),
(1956, 16, 55, 'Om ondankbaarheid te tonen voor wat Wij hun gegeven hebben. Geniet dan maar, spoedig zullen jullie weten (wat de gevolgen zijn).'),
(1957, 16, 56, 'En zij bestemmen een deel van waar Wij hun mee voorzien hebben voor waar zij niet over weten. Bij Allah, jullie zullen zeker ondervraagd worden over wat jullie plachten te verzinnen.'),
(1958, 16, 57, 'En zij kennen Allah dochten toe! Heitig is Hij! En voor zichzelf (kennen zij toe) wat zij verlangen.'),
(1959, 16, 58, 'En wanneer een van hen de verheugende tijding verkondigd wordt van (de geboorte van) een meisje wordt zijn gezicht somber en is hij vertoornd.'),
(1960, 16, 59, 'Hij verbergt zich voor de mensen wegens het slechte nieuws wat hij kreeg! Zal hij het in weerwil van de schande behouden of zal hij het in de grond verstoppen? Weet: slecht is het waar zij over oordelen!'),
(1961, 16, 60, 'Voor degenen die niet in het Hiernamaals geloven is er de vergelijking met het slechte; voor Allah zijn er de Verheven Eigenschappen. En Hij is de Geweldige, de Alwijze.'),
(1962, 16, 61, 'En als Allah de mensen voor hun onrechtvaardigheid zou straffen, zou Hij geen levend wezen op haar (de aarde) overlaten: maar Hij geeft hun uitstel voor een bepaalde tijd. En wanneer kun tijd komt, kunnen zij het geen moment uitstellen en zij kunnen het niet bespoedigen.'),
(1963, 16, 62, 'En zij kennen aan Allah toe waar zij zelf een afschuw van hebben en hun tongen beschrijven de leugen dat er voor hen het mooiste is. Het is zeker dat er voor hen de Hel is en dat zij zich (er naar toe) haasten.'),
(1964, 16, 63, 'Bij Allah, voorzeker, Wij zonden (Profeten) naar de gemeenschappen vóóoor jou (O Moehammad), maar de Satan deed hun werken schoon schijnen. En hij is op deze wereld hun beschermer. En voor hen is er een pijnlijke bestraffing.'),
(1965, 16, 64, 'En Wij hebben aan jou (O Moehammad) het Boek slechts doen neerdalen om hen duidelijk te maken waarin zij verschillen en als Leiding en Barmhartigheid voor een volk dat gelooft.'),
(1966, 16, 65, 'En Allah doet water uit de hemel neerdalen en geeft de aarde er leven mee na haar dood. Voorwaar, dat is zeker een Teken voor een volk dat luistert.'),
(1967, 16, 66, 'En voorwaar, voor jullie is er in het vee een lering: Wij geven jullie van wat er in hun buiken tussen mest en bloed is pure melk, gemakkelijk voor de drinkers.'),
(1968, 16, 67, 'En van de vruchten van de dadelpalm en de druivenstruiken maken jullie een bedwelmende drank en een goede voorziening. Voorwaar, daarin is zeker een Teken voor een volk dat begrijpt.'),
(1969, 16, 68, 'En jouw Heer inspireerde de bij: "Bouw huizen in de bergen en in de bomen en in wat zij (de mensen) bouwen.'),
(1970, 16, 69, 'Eet dan van alle vruchten en volg de wegen die jouw Heer makkelijk maakte."\' Er komt uit hun buiken een drank met verschillende kleuren, daarin is genezing voor de mensen. Voorwaar, daarin is zeker een Teken voof een volk dat nadenkt.'),
(1971, 16, 70, 'Het is Allah Die jullie schept en jullie vervolgens wegneemt. En onder jullie zijn er die teruggebracht worden naar een vernederende leeftijd, zodat hij niets meer weet na geweten te hebben. Voorwaar, Allah is Alwetend, Almachtig.'),
(1972, 16, 71, 'En Allah heeft sommigen van jullie boven anderen bevoorrecht met levensonderhoud. En degenen die meer bevoorrecht zijn geven hun levensonderhoud niet door aan jullie slaven, zodat zij daar gelijk in zouden zijn. Zullen zij de gunsten van Allah dan ontkennen?'),
(1973, 16, 72, 'En Allah heeft voor julliezelf uit julliezelf echtgenotes doen voortkomen en hij deed voor jullie uit jullie echtgenotes zonen en kleinkinderen voortkomen en voorzag jullie van goede dingen. Zullen zij in het valse geloven en ondankbaar zijn voor de gunst van Allah?'),
(1974, 16, 73, 'En zij aanbidden dat naast Allah wat hun niets van levensonderhoud kan verschaffen uit de hemelen en de aarde. En zij zijn nergens toe in staat.'),
(1975, 16, 74, 'Maakt dus geen gelijkenissen (afgoden) van Allah. Voorwaar, Allah weet en jullie weten niet'),
(1976, 16, 75, 'Allah maakt een vergelijking: een slaaf waarover beschikt wordt heeft geen macht en (de ander is) degene die Wij van Ons goed levensonderhoud voorzien hebben, en hij geeft er heimelijk en openlijk van uit. Zijn zij gelijk? Alle lof is voor Allah. Welnee, de meesten van hen weten (het) niet.'),
(1977, 16, 76, 'En Allah maakt een vergelijking met twee mannen: een van de twee is stom en hij heeft geen enkele macht en hij is een last voor zijn meester; waar hij hem ook heen stuurt: hij brengt niets goeds voort. Is hij gelijk aan degene die het oprechte gebiedt, en op het Rechte Pad is?'),
(1978, 16, 77, 'Aan Allah behoort het verborgene van de hemelen en de aarde. En de zaak van het Uur is slechts een oogwenk of het is sneller. Voorwaar, Allah is Almachtig over alle zaken.'),
(1979, 16, 78, 'En Allah bracht jullie uit de buiken van jullie moeders voort terwijl jullie niets wisten. En Hij gaf jullie het gehoor en het zien en de harten. Hopelijk zullen jullie dankbaarheid tonen.'),
(1980, 16, 79, 'Zien zij de vogels niet gemakkelijk vliegend in het midden van de hemel? Niemand dan Allah houdt hen vast. Voorwaar, daarin is zeker een Teken voor een volk dat gelooft.'),
(1981, 16, 80, 'En Allah maakte voor jullie jullie huizen tot plaatsen om uit te rusten en Hij maakte voor jullie de huiden van het vee tot tenten die licht zijn op de dag dat jullie reizen en op de dag dat jullie je vestigen (en Hij maakte) hun wol en hun vacht tot gebruikszaken en als een genieting voor een bepaalde tijd.'),
(1982, 16, 81, 'En Allah gaf jullie uit wat Hij schiep schaduwen en Hij maakte voor jullie in de bergen schuilplaatsen en Hij gaf jullie kleding die jullie tegen de hitte beschermt en kleding die jullie tegen jullie oorlog beschemt. Op deze wijze vervolmaakt Hij voor juille Zijn gunst. Hopelijk zullen jullie je (aan Hem) overgeven.'),
(1983, 16, 82, 'Maar als zij zich afwenden: jouw plicht (O Moehammad) is slechts de duidelijke verkondiging.'),
(1984, 16, 83, 'Zij herkennen de gunst van Allah en daarop ontkennen zij die en de meesten van hen zijn ondankbaren.'),
(1985, 16, 84, 'Op de Dag zullen Wij uit iedere gemeenschap een getuige doen opstaan, dan zullen degenen die ongelovig zijn geen gehoor gegeven worden en zij krijgen niet de gelegenheid zich te verbeteren.'),
(1986, 16, 85, 'En wanneer degenen die onrecht pleegden de bestraffing zien zal die voor hen niet lichter gemaakt worden en zij zullen geen uitstel krijgen.'),
(1987, 16, 86, 'En wanneer degenen die deelgenoten toekenden hun deelgenoten zullen zien, zullen zij zeggen: "Onze Heer! Zij zijn degenen die onze deelgenoten waren die wij naast U plachten aan te roepen." Maar zij (de deelgenoten) zullen hun woorden terugweirpen, en zeggen: "Voorwaar, jullie zijn zeker leugenaars!"'),
(1988, 16, 87, 'En op die Dag zullen zij Allah hun onderwerping tonen. En wat zij plachten te verzinnen zal van hen weggedwald zijn.'),
(1989, 16, 88, '(Zij zijn) degenen die ongelovig waren en (mensen) van de Weg van Allah afhielden. Wij zullen bestraffing op bestraffing stapelen vanwege wat zij am verderf zaaiden.'),
(1990, 16, 89, 'En op die dag zullen Wij uit het midden van iedere gemeenschap een getuige doen opstaan en Wij zullen jou als getuige nemen tegen deze (gemeenschap van jou). En Wij hebben jou het Boek neergezonden, als een uitleg van alle zaken en als Leiding en Barmhartigheid en een verheugende tijding voor de Moslims.'),
(1991, 16, 90, 'Allah beveelt rechtvaardigheid en het goede en het geven aan de verwanten en Hij verbiedt de zedeloosheid en het verwerpelijke en de opstandigheid. Hij onderricht jullie, hopelijk zullen jullie je laten vermanen.'),
(1992, 16, 91, 'En komt het verbond met Allah na wanneer jullie je verbonden hebben, en breekt niet jullie eden nadat jullie ze bevestigd hebben. En waarlijk, jullie hebben Allah als Getuige genomen. Voorwaar, Allah is Alwetend over wat jullie doen.'),
(1993, 16, 92, 'En weest niet als zij die haar garen weer op- splitst nadat zij het stevig gesponnen had, Mlie eden als een list tussen jullie gebruikend, omdat één gemeenschap talrijker is dan een (andere) gemeenschap. Voorwaar, Allah beproeft jullie er slechts mee. En Hij zal jullie op de Dag der Opstanding duidelijk maken waar jullie over plachten te redetwisten.'),
(1994, 16, 93, 'En als Allah het gewild had, had Hij jullie zeker (als behorend) tot één godsdienst gemaakt. Maar Hij doet dwalen wie Hij wil en Hij leidt wie Hij wil. En jullie zullen zeker ondervraagd worden over wat jullie plachten te doen.'),
(1995, 16, 94, 'En zweert juillie eden niet als een list tussen jullie, zodat een voet uitglijdt nadat die stevig neergezet was en jullie zullen het slechte moeten proeven vanwege jullie afhouden (van de mensen) van de Weg van Allah. En voor jullie (zondaars) is er een geweldige bestraffing.'),
(1996, 16, 95, 'En ruilt het verbond met Allah niet in voor een geringe prijs. Voorwaar, wat bij Allah is, is beter voor jullie als jullie (het) weten.'),
(1997, 16, 96, 'Wat bij jullie is, zal vergaan, maar wat bij Allah is, is blijvend. En Wij zullen degenen die geduldig waren zeker belonen met hun beloning, volgens het beste van wat zij plachten te doen.'),
(1998, 16, 97, 'Wie het goede doet, man of vrouw, en hij gelooft: voorwaar, aan hem geven Wij een goed leven. En Wij zullen hen zeker belonen met hun beloning, volgens het beste van wat zÜij plachten te doen. THIS TEXT HAS BEEN AUTOMATICALLY GENERATED (OCR) AND REQUIRES FIXING/CHECKING. USE THE CTRL+(Right/Left Arrow) KEYS TO MOVE ALONG THE WORDS AND CHECK WITH THE LEFT SIDE. PLEASE DELETE *THIS* NOTE IF YOU ARE READY TO CHECK AND APPROVE.'),
(1999, 16, 98, 'En wawmrjullie de Koran lezen: zoek den de bescherming van Allah tegen de vervloekte Satan.'),
(2000, 16, 99, 'Voorwaar, hij heeft geen gezag over degenen die geloven en kun Heer vertrouwen,'),
(2001, 16, 100, 'Voorwaar, bij heeft alleen gezag over degenen die hem als beschermer nemen en (over) degenen die deelgenoten am Hem toekennen.'),
(2002, 16, 101, 'En wanneer Wij een Vers door een ander Vers vervangen, en Allah weet heter wat Hij neerzendt, (dan) zeggen zij, "Voorww, jij (Moehammed) b" een verzinnef van leugens." De meesten van ben weten zelfs niets.'),
(2003, 16, 102, 'Zeg (O Moehammad): "De Heilige Geest (Djibrll) van jouw Heer deed hem met de Waarheid neerdalen om degenen die geloven te versterken, en als Leiding en een verheugende tijding voor de Moslinu."'),
(2004, 16, 103, 'En voorzeker, Wij weten dat zij zeggen: "Voorwaar, het is slechts een mens die hem onderwijst." De taal van degenen waar zli valselijk naar verwijzen is vreemd, maar dit is oen duidelijke Areibische taal.'),
(2005, 16, 104, 'Voorwaar, (wat betreft) degenen die niet in de Verzen van Allah geloven: Allah zal hen niet leiden en voor hen zal er een pijnlijke bestraffing zijn.'),
(2006, 16, 105, 'Voorwaar, degenen die de leugen verzinnen geloven niet in de Verzen van Allah. Zij zijn degenen die leugenaars zijn.'),
(2007, 16, 106, 'Wie aan Allah ongelovig is na geloofd te hebben, behalve wie gedwongen is terwijl zijn hw in het geloof tot rust gekomen wn maar (voor) wie die zijn hart voor het ongeloof openstelde: voor hem is er de toom van Ailah en voor hem is er een geweldige bestraffing.'),
(2008, 16, 107, 'Dat is omdat zij het wereldse leven verkiezen boven het Hiernamaals, en omdat Allah het ongelovige volk niet zal leiden.'),
(2009, 16, 108, 'Zij zijn degenen bij wie Allah hun h"n en ban gehoor en hun zien vergrendeld heeft; en zij zijn degenen die onachtzaam zijn.'),
(2010, 16, 109, 'Het is zeker dat zij in het N iernamals de verliezers zijn.'),
(2011, 16, 110, 'Voorwaar dan, jouw Heer is voor degenen die uitgeweken zijn nadat zij op de proef gesteld werden en daarna streden en geduldig waren; voorwaar, jouw Heer is daarna (voor hen) zeker Vergevensgezind. Meest Bambartig.'),
(2012, 16, 111, 'Op die Dag zal iedere ziel voor zichzelf pleiten en zal iedere ziet voor wat hij verrichtte volledig beloond worden en zij zullen niet onrechtvaardig behandeld worden.'),
(2013, 16, 112, 'En Allah geeft een voorbeeld: een veilige en rustige stad had haar voorzieningen overvloedig van iedere plaats gekregen en was toch ondankbaar voor de gunsten van Allah. Allah deed haar honger en angst proeven wegens wat zij plachten te bedrijven.'),
(2014, 16, 113, 'En voorzeker, er kwam m Boodschapper vanuit hun midden tot hen, maar zij loochenden hem waarop de bestraffing hen trof, en zij waren onrechtvaardigen.'),
(2015, 16, 114, 'Eet dus van het toegestane en het goede waar Allah jullie mee voorzien heeft en weest dankbaar voor de gunsten van Allah, als jullie alleen Hem aanbidden.'),
(2016, 16, 115, 'Voorwaar, Hij heeft jullie het kadaver en het bloed en het vlees van het varken en hetgeen waarover anders dan (de Nam van) Allah afgeroepen is verboden. En wie ertoe genoodzaakt is, zonder begeerte en zonder overdrijving: voorwaar, Allah is dan Vergevensgezind, Meest Barmhartig.'),
(2017, 16, 116, 'En zegt niet door de leugen die jullie tongen beschrijven: "Dit is toegestaan en dat is verboden," om over Allah een leugen te verzinnen. Voorwaar, degenen die over Allah de leugen verzinnen zullen niet welslagen.'),
(2018, 16, 117, '(Dat is) een kleine genieting en voor hen is er een pijnlijke bestraffing.'),
(2019, 16, 118, 'En de Joden verboden Wij wat Wij jou vroeger verhaalden. Wij deden hen geen onrecht aan maar zij waren onrechtvaardig voor zichzelf.'),
(2020, 16, 119, 'Voorwaar dan, jouw Heer is voor degenen die het slechte bedreven uit onwetendheid (en) daarna berouw toonden en zich beterden: voorwaar, jouw Heer is daarna zeker Vergevensgezind, Meest Barmhartig.'),
(2021, 16, 120, 'Voorwaair, Ibrâhîm was een Imam (voorbeeld), gehoorzaam aan zijn Heer, Hanîf, en hij behoorde niet tot de veelgodenaanbidders.'),
(2022, 16, 121, '(Hij was) dankbaar voor Zijn gunsten, Hij verkoos hem en leidde hem op een Recht Pad.'),
(2023, 16, 122, 'En Wij gaven hem op de wereld het goede en voorwaar, in het Hiernamaals behoort hij tot de oprechten.'),
(2024, 16, 123, 'Daarop openbaarden Wij aan jou (O Moehammad): "Volg de godsdienst van Ibrâhîm, Hanîf, en hij behoorde niet tot de veelgodenaanbidders."'),
(2025, 16, 124, 'De Sabbat was slechts gemaakt voor degenen die van mening verschilden over haar (dag). En voorwaar, jouw Heer zal zeker tussen hen op de Dag der Opstanding over dat waarover zij van mening plachten te verschillen oordelen.'),
(2026, 16, 125, 'Nodig uit tot de Weg van jouw Heer, met wijsheid en goed onderricht, en wissel met hen van gedachten op de beste wijze. Voorwaar, jouw Heer weet het beste wie van Zijn Weg afgedwaald is en Hij weet beter wie de rechtgeleiden zijn.'),
(2027, 16, 126, 'En wanneer jullie bestraffen, bestraft dan in overeenstemming met wat jullie aangedaan is. Maar als jullie geduldig zijn is dat zeker beter voor de geduldigen.'),
(2028, 16, 127, 'En wees geduldig (O Moehammad) en jij bent slechts geduldig door Allah. En treur niet over hen en wees niet benauwd voor wat zij beramen.'),
(2029, 16, 128, 'Voorwaar, Allah is niet degenen die (Hem) vrezen en met degenen die weldoeners zijn.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Heilig is Degene Die \'s nachts Zijn dienaar (Moehammad) van de Masdjid al Harâm (de Gewijde Moskee te Mekkah) naar de Masdjid al Aqsha heeft gebracht, waarvan Wij de omgeving hebben gezegend, opdat Wij hem van Onze Tekenen lieten zien. Voorwaar, Hij is de Alhorende, de Alziende.'),
(2031, 17, 2, 'En Wij hebben aan Môesa de Schrift (de Taurat) gegeven en Wij maakten deze als Leiding voor de Kinderen van Israël: "Neemt geen helper naast mij."'),
(2032, 17, 3, 'O nakomelingen van degenen die Wij met Nôeh (in de ark) gedragen hebben, voorwaar, hij (Noeh) was een dankbare dienaar!'),
(2033, 17, 4, 'En Wij hebben de Kinderen van Israel op de hoogte gebracht in de Schrift: "Jullie zullen zeker twee maal verderf zaaien op aarde en jullie zullen je zeker hoogmoedig gedragen, met grote hoogmoed."'),
(2034, 17, 5, 'En toen (de vervulling) van de eerste van de twee beloften kwam, stuurden Wij jullie dienaren van Ons, bezitters van verschrikkelijke macht, die daarop de huizen binnendirongen: het was een vervulde belofte.'),
(2035, 17, 6, 'Vervolgens gaven Wij jullie de overhand over hen en vemeerderden Wij voor jullie de bezittingen en zonen en maakten Wij jullie tot de grootste groep.'),
(2036, 17, 7, 'Als jullie goed deden, deden jullie goed voor julliezelf: en als jullie kwaad deden, dan was dat voor jullie. En toen de laatste belofte vervuld werd (stuurden Wij vijanden) om jullie gezichten te verminken en de gebedsruimte binnen te gaan, zoals zij daar de eerste keer binnengingen, en om volledig te vernietigen wat zij veroverd hadden.'),
(2037, 17, 8, 'Mog ejullie Heer jullie begenadigen. En als jullie terugkeren (naar jullie zonden) zullen Wij terugkeren (naar het bestraffen van jullie). En Wij maakten de Hel voor de ongelovigen tot een gevangenis.'),
(2038, 17, 9, 'Voorwaar, deze Koran leidt naar wat rechter is en brengt goede tijdingen aan de gelovigen die goede werken verrichten. Voorwaar, er is voor hen een grote beloning.'),
(2039, 17, 10, 'En voor degenen die niet in het Hiernamaals geloven, voor hen hebben Wij een pijnlijke bestraffing voorbereid.'),
(2040, 17, 11, 'En de mens smeekt om het kwade, net zoals hij om het goede smeekt. En de mens is haastig (van aard).'),
(2041, 17, 12, 'En Wij hebben de nacht en de dag tot twee Tekenen gemaakt. En het Teken van de nacht hebben Wij donker gemaakt en het Teken van de dag hebben Wij verlichtend gemaakt opdat jullie de gunst van jullie Heer zoeken en opdat jullie de jaartelling en de berekening ervan kennen. En alle zaken hebben Wij met een duidelijke uitleg uiteengezet.'),
(2042, 17, 13, 'En voor ieder mens hebben Wij zijn lotsbestemming aan zijn hals vastgemaakt. En Wij voorzien hem op de Dag der Opstanding van een boek dat bij opengeslagen aantreft.'),
(2043, 17, 14, '(Er wordt tegen hem gezegd:) "Lees jouw boek." Op deze Dag is jouw eigen ziel voldoende als berekenaar tegen jou.'),
(2044, 17, 15, 'Wie Leiding volgt, volgt de Leiding waarlijk voor zichzelf, en wie dwaalt, dwaalt waarlijk tegen zichzelf en niemand kan de zonde van iemand anders dragen. En Wij bestraffen niet voordat Wij een Boodschapper hebben gestuurd.'),
(2045, 17, 16, 'En als Wij een (volk in een) stad willen vernietigen, bevelen Wij hen die daarin in weelde leven (Allah te gehoorzamen); zij begaan daarin zware zonden, zodat het woord (van de bestraffing) terecht is, vervolgens vernietigen Wij hen volledig.'),
(2046, 17, 17, 'En hoeveel generaties hebben Wij niet na Nôeh ij vernietigd? En jouw Heer is voldoende om de zonden van Zijn dienaren te wetm en te zien.'),
(2047, 17, 18, 'En wie het vergankelijke (van de wereld) wenst: voor hem zullen Wij wat Wij wensen daarin verhaaasten, voor wie Wij willen. Vervolgens maken Wij voor hem de Hel, hij gaat daar binnen, vernederd en verjaagd.'),
(2048, 17, 19, 'En wie het Hiernamaals wenst en er met een redelijke inzet naar streeft, én hij is gelovig: hij (behoort) tot degenen van wie hun streven wordt beloond.'),
(2049, 17, 20, 'Beide groepen, voornoemde en laatstgenoemde, geven Wij ondersteuning van jouw Heer, en de ondersteuning van jouw Heer is onafwendbaar.'),
(2050, 17, 21, 'Zie hoe Wij sommigen van hen bevoorrecht hebben boven anderen. En het Hiernamaals is zeker hoger in rang en groter als voorrecht.'),
(2051, 17, 22, 'En neem geen andere god naast Allah, anders zit jij daar (O Moehammad) met verwijten (beladen) en verlaten.'),
(2052, 17, 23, 'En jullie Heer heeft bepaald dat jullie niets dan Hem alleen aanbidden, en goedheid betrachten tegenover de ouders. Als een van de twee of beiden de ouderdom bereiken in jouw aanwezigheid, zeg dan nooit "foei" tegen ben, snauw hen niet af en spreek tot hen een vriendelijk woord.'),
(2053, 17, 24, 'En wees zachtmoedig voor beiden, en nederig en liefdevol, en zeg: "O mijn Heer, schenk hun Genade, zoals zij mij opvoedden toen ik klein was."'),
(2054, 17, 25, 'Jullie Heer weet beter wat er injullie zielen is, als jullie oprechte mensen zijn. Voorwaar; dan is Hij voor de berouwvollen Vergevensge-zind.'),
(2055, 17, 26, 'En geef de verwanten hun recht en de armen en de reizigers (zonder proviand), en wees niet verkwistend.'),
(2056, 17, 27, 'Voorwaar. de verkwisters zijn broeders van de Satan en de Satan is ondankbaar jegens zijn Heer.'),
(2057, 17, 28, 'En als jullie je van hen afwenden omwille van de Barmhartigheid van jullie Heer die jullie hopen te krijgen: spreekt dan (toch) een zacht woord tot hen.'),
(2058, 17, 29, 'En maak je hand niet vastgebonden aan je nek, en strek hem niet uit met de hele uitstrekking, want dan wordt je (gierigheid of spilzucht) verweten en spijtig.'),
(2059, 17, 30, 'Voorwaar, jouw Heer verruimt de voorziening voor wie Hij wil en Hij beperkt. Voorwaar, Hij is Alwetend, Alziend over Zijn dienaren.'),
(2060, 17, 31, 'En doodt jullie kinderen niet uit vrees voor armoede: Wij voorzien hun en jullie van levensonderhoud. Voorwaar, hen doden is een grote zonde.'),
(2061, 17, 32, 'En nader niet de ontucht. Voorwaar, ontucht is een zedeloosheid en een slechte weg.'),
(2062, 17, 33, 'En doodt geen ziel waarvan Allah (het doden) verboden heeft verklaard, behalve volgens het recht en wie onrechtvaardig gedood wordt: Wij hebben zijn erfgenaam een bevoegdheid gegeven; maar overdrijft niet hij het doden. Voorwaar: hij wordt geholpen.'),
(2063, 17, 34, 'En benadert niet het bezit van de wees, behalve op de betere manier, totdat bij volwassen is: en komt de belofte na. Voorwaar, over de belofte worden jullie (op de Dag der Opstanding) ondervraagd'),
(2064, 17, 35, 'En geeft de volle maat wanneer jullie afwegen en weegt met de juiste weegschaal, dat is beter en een mooiere afsluiting.'),
(2065, 17, 36, 'En volg niet dat waarover je geen kennis hebt. Voorwaar, het gehoor en het gezichtsvermogen en de harten: die zullen allen erover ondervraagd worden.'),
(2066, 17, 37, 'En loop niet hoogmoedig op de aarde. Voorwaar, jij kan de aarde niet doen splijten en niet de hoogte van een berg bereiken.'),
(2067, 17, 38, 'Dat alles is slecht bij jouw Heer, verwerpelijk.'),
(2068, 17, 39, 'Dat is (een deel) van de Wijsheid die Allah aan jou openbaarde. En neem geen andere god naast Allah, anders wordt je in de Hel geworpen, vol verwijten en verworpen.'),
(2069, 17, 40, 'Heeft jullie Heer voor jullie zonen verkozen en voor Zichzelf vanonder de Engelen dochters genomen? Voorwaar, jullie spreken zeker geweldig (slechte) woorden!'),
(2070, 17, 41, 'En Wij hebben in deze Koran (zaken) uitgelegd opdat zij (de ongelovigen) vermaand worden, maar het versterkt bij hen slechts het afwenden.'),
(2071, 17, 42, 'Zeg (O Moehammad): "Als er goden bij Hem geweest zouden zijn, zoals zij zeggen, dan zouden zij een Weg naar de Eigenaar van de Troon (Allah) zoeken."'),
(2072, 17, 43, 'Heilig is Hij en Verheven boven wat zij in grote trots zeggen.'),
(2073, 17, 44, 'De zeven hemelen en de aarde en wie er in hen zijn, roepen Zijn Glorie uiten er is niets of het roept Zijn Glorie uit met Zijn lofprijzing en toch begrijpen jullie hun glorieroep niet. Voorwaar, Hij is Zachtmoedig, Vergevensgezind.'),
(2074, 17, 45, 'En wanneer jij de Koran voordraagt, brengen Wij tussen jou en degenen die niet in het Hiernamaals geloven een verhinderende afscheiding aan.'),
(2075, 17, 46, 'En Wi hebben bedekkingen over hun harten aangebracht, zodat zij hem niet begrijpen, en doofheid in hun oren. Wanneer jij jouw Enige Heer noemt tijdens de Koranrecitatie, dan keren zij hun ruggen toe, zich afwendend.'),
(2076, 17, 47, 'Wij weten beter hoe zij over hem horen, wanneer zij naar jou lijken te luisteren. En toen zij onderling fluisterden, zeiden de onrechtvaardigen: "Jullie volgen slechts een betoverde man!"'),
(2077, 17, 48, 'Kijk hoe zij vergelijkingen met jou maken: hierdoor dwalen zij en kunnen zij geen Weg vinden.'),
(2078, 17, 49, 'En zij zeiden: "Wanneer Wij botten en stof geworden zijn, worden wij dan zeker tot een nieuwe schepping opgewekt?"'),
(2079, 17, 50, 'Zeg: "Wordt steen of ijzer... (en jullie worden zelfs dan opgewekt.)'),
(2080, 17, 51, 'Of een schepping waarvan het in jullie harten het moeilijkst lijkt." Dan zullen zij zeggen: "Wie doet ons terugkeren?" Zeg: "Degene Die jullie de eerste keer schiep." Dan zullen zij hun hoofd naar jou schudden, en zeggen: "\'Wanneer is dat?" Zeg: "Het kan nabij zijn!"'),
(2081, 17, 52, 'Op de Dag waarop Hij jullie oproept en jullie dan (Zijn oproep) beantwoorden met een lofprijzing van Hem. En jullie zullen veronderstellen dat jullie slechts weinig (tijd op aarde) verbleven.'),
(2082, 17, 53, 'En zeg tegen Mijn dienaren (O Moehammad) dat zij wat beter is zeggen. Voorwaar, de Satan zaait verdeeldheid onder hen. Voorwaar, de Satan is voor de mensen een duidelijke vijand.'),
(2083, 17, 54, 'Jullie Heer kent jullie beter. Indien Hij wil, begenadigt Hij jullie; of, indien Hij wil, bestraft Hij jullie. En Wij hebben jou (O Moehammad) niet als voogd tot hen gezonden.'),
(2084, 17, 55, 'En jouw Heer weet beter over wie er in de hemelen en (op) de aarde is. En Wij hebben sommige Profeten bevoorrecht boven andere Profeten, en Wij gaven Dâwôed de Zabôer'),
(2085, 17, 56, 'Zeg (O Moehammad): "Roept degenen die jullie naast Allah als god veronderstellen maar aan, zij zijn niet bij machte het kwade van jullie weg te nemen en het te veranderen."'),
(2086, 17, 57, 'Zij (de veelgodenaanbidders) zijn degenen die aanroepen, (en zij die aangeroepen worden) zoeken naar een middel tot hun Heer. Wie van hen het dichtst bij (hun Heer) zijn en op Zijn Barmhartigheid hopen en Zijn bestraffing vrezen: voorwaar, een bestraffing van jouw Heer is te vrezen.'),
(2087, 17, 58, 'En er is geen land, of Wij hebben het vôôr de Dag van de Opstanding vernietigd, of haar bestraft met een harde bestraffing. Dat is (vastgelegd) in het geschreven Boek.'),
(2088, 17, 59, 'En niets verhinderde Ons de Tekenen neer te zenden dan dat de voorafgaaden hen loochenden. En Wij gaven de Tsamôed de vrouwtjeskameel, als een duidelijk zichtbaar (Teken) maar zij behandelden haar onrechtvaardig. En Wij zenden de Tekenen alleen maar om (hen) te doen vrezen.'),
(2089, 17, 60, 'En (gedenk) toen Wij zeiden dat jouw Heer de mensheid omvat. En Wij hebben jou de waarneming die wij jou lieten zien slechts als een beproeving voor mensen gegeven; en (ook) de vervloekte boom in de Koran. En Wij jagen hen vrees aan: maar het venneerdert bij hen niets dan een grote overtreding.'),
(2090, 17, 61, 'En toen Wij tot de Engelen zeiden: "Kniel jullie neer voor Adam," toen knielden zij allen, behalve Iblîs (Satan). Hij zei: "Moet ik (mij) neerknielen voor iemand die U uit aarde heeft geschapen?"'),
(2091, 17, 62, 'Hij (Iblîs) zei: "Ziet U? Dat is degene die U boven mij geëerd hebt. Indien U mij uitstel geeft tot de Dag der Opstanding zal ik zeker zijn nageslacht doen dwalen, op weinigen na."'),
(2092, 17, 63, 'Hij (Allah) zei: "Ga heen; wie van hen jou volgt: voorwaar: de Hel zal jullie beloning zijn, als een volledige beloning!'),
(2093, 17, 64, 'En rui van hen op wie jij kan met jouw stem, en val hen aan met je paarden en je voetvolk, en deel rijkdommem en kinderen met hen en doe hen beloften." En de Satan belooft ben niets dan bedrog.'),
(2094, 17, 65, 'Voorwaar, jij hebt geen macht over Mijn dienaren. En jouw Heer is voldoende als Beschemer."'),
(2095, 17, 66, 'Jullie Heer is Degene Die voor jullie de schepen op zee doet varen, opdat jullie van Zijn gunsten zoeken. Voorwaar, Hij is voor jullie Meest Barmhartig.'),
(2096, 17, 67, 'En als de tegenspoed jullie op zee getroffen heefk verdwijnt degene die jullie aanroepen, behalve Hij. Toen Hij jullie naar het kad redde, wendden jullie je af Ende mens is ondenkbaar!'),
(2097, 17, 68, 'Voelen jullie je er veilig voor, dat Hij jullie niet in de aarde weg doet zinken wanneer jullie op het land zijn, of stenen over jullie zendt? Vervolgens vinden jullie geen beschermer voor jullie.'),
(2098, 17, 69, 'Of voelen jullie je er veilig voor, dat Hij jullie nog een andere keer (naar de zee) doet terugkeren, en Hij dan een orkaan over jullie zendt, die jullie dan verdrinkt, omdat jullie ongelovig zijn en dat jullie dan daarin geen helper tegen Ons vinden?'),
(2099, 17, 70, 'En voorzeker, Wij hebben de kinderen van Adam geëerd. Wij brachten hen op het land en op de zee. Wij gaven hun levensonderhoud van het goede en Wij bevoorrechtten hen met een privilege boven vele van de andere schepsels die Wij hebben geschapen.'),
(2100, 17, 71, 'Op de dag waarop Wij alle mensen met hun boeken zullen oproepen: wie dan zijn boek (met zijn daden) in zijn echterhand gegeven wordt: zij zijn degenen die hun boek (met een gerust hart) lezen. En hen zal geen enkel onrecht aangedaan worden.'),
(2101, 17, 72, 'En wie blind (van had) is in deze wereld, zal fn het Hiernamaals ook blind wezen en het meest van de Weg gedwaald zijn.'),
(2102, 17, 73, 'En zij hadden jou je bijna doen afwenden van wat Wij aan jou geopenbaard hadden, zodat jij iets anders over Ons verzonnen had; dan hadden zij jou zeker als boezemvriend genomen!'),
(2103, 17, 74, 'En als Wij jou niet sterk zouden hebben gemaakt, dan zou jij je bijna een klein beetje tot hen geneigd hebben.'),
(2104, 17, 75, 'Dan zouden Wij jou m dubbel deel (van de bestraffing) van dit leven en een dubbel deel van de dood laten proeven. En jij vindt dan geen beschermer tegen Ons.'),
(2105, 17, 76, 'En bijna hadden zij jou vanuit het land opgejaagd, om jou eruit te verdrijven, maar dan zouden zij er na jou niet gebleven zijn, behalve weinigen.'),
(2106, 17, 77, '(Dit was) de handelwijze van de Boodschappers die Wij vóór jou gezonden hebben en jij zult geen verandering in Onze handelwijze vinden.'),
(2107, 17, 78, 'Onderhoud de shalât na het hoogtepunt van de zon tot donkerte van de nacht en het Fadjr-gebed. Voorwaar, van het Fadjr-gebed wordt getuigd (door Engelen).'),
(2108, 17, 79, 'En verricht in een gedeelte van de nacht gebed, als een extra (gebed) voor jou. Jouw Heer zal jou zeker tot een loffelijke positie verheffen.'),
(2109, 17, 80, 'Zeg (O Moehammad): "Laat mij binnentreden op oprechte wijze en laat mij verlaten op oprechte wijze en doe mij van Uw Zijde helpende macht toekomen."\''),
(2110, 17, 81, 'En zeg: "De Waarheid is gekomen en de valsheid is ten onder gegaan. Voorwaar, de valsheid gaat ten onder."'),
(2111, 17, 82, 'En Wij zonden (dat) van de Koran neer wat genezing en Barmhartigheid voor de gelovigen is. En het vermeerdert voor de onrechtvaardigen slechts verlies.'),
(2112, 17, 83, 'En wanneer Wij de mens begunstigen wendt hij zich af en keert zijn zijde toe, maar als het kwaad hem treft wordt hij wanhopig.'),
(2113, 17, 84, 'Zeg: "Een ieder handelt naar zijn geaardheid en jullie Heer weet beter wie er beter op de Weg geleid is."'),
(2114, 17, 85, 'En zij vragen jou over de ziel, zeg: "De ziel behoort tot de zaken van mijn Heer. En de kennis erover wordt jullie niet gegeven, behalve een weinig."'),
(2115, 17, 86, 'En indien Wij het gewild hadden, dan zouden Wij zeker dat kunnen laten verdwijnen wat Wij aan jou geopenbaard hebben, en dan zou jij niemand kunnen vinden die er bi Ons een beschermer tegen is.'),
(2116, 17, 87, 'Behalve als Barmhartigheid van jouw Heer, voorwaar: Zijn gunst voor jou is groot. Zeg (O Moehammad): "Als de mensen en de Djinn\'s zich zouden verzamelen om het gelijke van deze Koran te maken; dan kunnen zij niet het daaraan gelijke komen, zelfs al zouden zij elkur hulp zijn."'),
(2117, 17, 88, 'Zeg (O Moehammad): "Als de mensen en de Djinn\'s zich zouden verzamelen om het gelijke van deze Koran te maken; dan kunnen zij niet het daaraan gelijke komen, zelfs al zouden zij elkur hulp zijn."'),
(2118, 17, 89, 'En voorzeker, Wij hebben voor de mensen in deze Koran alle (soorten) vergelijkingen uitgelegd, maar de meeste mensen weigeren het, behalve in ondankbaarheid.'),
(2119, 17, 90, 'En zij zeiden (tegen Moehammad): "Wij zullen jou nooit geloven, totdat jij voor ons een bron uit de aarde doet opwellen.'),
(2120, 17, 91, 'Of jij een tuin met dadelpalmen en druivenstruiken hebt, en dan overvloedige rivieren uit hun midden doet ontspringen.'),
(2121, 17, 92, 'Of jij de hemel in stukken op ons neer doet vallen, zoals jij ons beweert, of jij Allah en de Engelen vóór ons brengt.'),
(2122, 17, 93, 'Of jij een huis van goud hebt, of naar de hemel opstijgt, en wij zullen jouw opstijging nooit geloven, totdat jij een boek ma ons neerzendt, dat wij kunnen lezen." Zeg (O Moehammad): "Heilig is mijn Heer, ik ben niets anders dan een menselijke Boodschapper."'),
(2123, 17, 94, 'En er was niets dat de mensen verhinderde te geloven tm de Leiding tot hen kwam, behalve dat zij zeiden: "Heeft Allah een menselijke Boodschapper neergezonden?"'),
(2124, 17, 95, 'Zeg: "Wanneer er op de aarde Engelen waren die rustig rondliepen, dan zouden Wij een Engel tot hen als Boodschapper neergezonden hebben."'),
(2125, 17, 96, 'Zeg: "Allah is voldoende als Getuige tussen mij en jullie, en voorwaar Hij is Alwetend, Alziend over Zijn dienaren."'),
(2126, 17, 97, 'En wie door Allah geleid wordt, hij is de welgeleide; maar wie (door Hem) tot dwaling gebracht is, voor hem zal jij nooit een beschermer naast Hem vinden. En Wij zullen hen op de Dag der Opstanding op hun gezichten verzamelen: blind en stom en doof Hun verblijfplaats is de Hel. Iedere keer dat hij dooft vermeerderen Wij voor ben het laaiend vuur.'),
(2127, 17, 98, 'Dat is hun beloning, omdat zij Onze Tekenen verwierpen, en zij zeiden: "Wanneer Wij beenderen en stof geworden zijn, zullen wij dan zeker tot een nieuwe schepping opgewekt worden?"'),
(2128, 17, 99, 'Zien zij niet in dat Allah, Degene Die de hemelen en de aarde schiep, bij machte is het daarmee vergelijkbare te scheppen? En dat Hij een tijdstip heeft vastgesteld voor hen, zonder dat daarover twijfel bestaat? Maar de onrechtvaardigen weigeren het, behalve in ongeloof.'),
(2129, 17, 100, 'Zeg: "Als jullie de schatten van Barmhartigheid van mijn Heer bezaten, dan zouden jullie hen vasthouden uit vrees voor de uitgave (ervan)." En de mens is gierig!'),
(2130, 17, 101, 'En voorzeker, Wij hebben Môesa negen duidelijke Tekenen gegeven, en vraag de kinderen van Israël (O Moehammad) toen hij (Môesa) tot hen kwam, en Fir\'aun tot hen zei: "Voorwaar, ik verondemel zeker dat jij, O Môesa, betoverd bent!"'),
(2131, 17, 102, 'Hij (Môesa) zei: "Jij weet dan niemand anders die (Tekenen) heeft neergezonden dan de Heer van de hemelen en de aarde, als een duidelijk bewijs. Voorwaar, ik veronderstel dat jij, O Fir\'aun, ten onder gaat."'),
(2132, 17, 103, 'Vervolgens wenste bij (Fir\'aun) ben uit het land te verdrijven, dus verdronken Wij hem en degenen die met hem waren allemaal.'),
(2133, 17, 104, 'En Wij zeiden na hem (Fir\'aun) tot de Kinderen van Israël: "Woont in het land, wanneer dan de laatste aanzegging (de Dag der Opstanding) komt zullen Wij jullie gemengd bijeenbrengen."'),
(2134, 17, 105, 'En met de Waarheid hebben Wij hem (de Koran) neergezonden en met de Waarheid is hij neergedaald. En Wij hebben jou niets anders dan als een verkondiger van goede tijdingen en als een waarschuwer gezonden.'),
(2135, 17, 106, 'En (de openbaring van) de Koran hebben Wij in gedeelten verdeeld, om hem aan de mensen met tussenpozen voor te dragen en Wij hebben hem els een neerzending neergezonden.'),
(2136, 17, 107, 'Zeg (O Moehammad): "Gelooft er in of gelooft niet." Voorwaar, degenen aan wie ervóór de kennis gegeven was, vallen op hun gezichten neer, zich neerknielend, wanneer hij hun voorgedragen wordt.'),
(2137, 17, 108, 'En zij zeggen: "Heilig is onze Heer, de belofte van onze Heer is zeker vervuld."'),
(2138, 17, 109, 'En zij vallen op hun gezichten neer, terwijl zij huilen, en het vermeerdert hun vrees (voor Allah)."'),
(2139, 17, 110, 'Zeg: "Roept Allah aan of roept de Barmhartige aan. Waarbij jullie Hem ook aanroepen: aan Hem behoren de Schone Namen. Fn verricht je shalât niet met luide stem, en spreekt er niet zacht bij, maar zoekt een weg daartussen."'),
(2140, 17, 111, 'En zeg: "Alle lof zij Allah. Degene die Zich geen kind neemt en Die voor Zich geen deelgenoot in het Koninkrijk heeft en Hij is niet vemederd, daarom heeft Hij geen helper (nodig). En verheerlijk Hem met een grote verkeerlijking."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Alle lof zij Allah, Degene Die aan zijn dienaar het Boek heeft gezonden, en Hij heeft daarin geen afwijkingen gemaakt.'),
(2142, 18, 2, 'Als rechte Leiding, om te waarschuwen voor een harde bestraffing van Zijn Zijde en om een verheugende tijding te brengen aan de gelovigen, degenen die goede daden verrichten: dat er voor hen een goede beloning is (het Paradijs).'),
(2143, 18, 3, 'Zij zullen daafin voor altijd blijven.'),
(2144, 18, 4, 'En om degenen te waarschuwen, die zeggen: "Allah heeft Zich een zoon genomen."'),
(2145, 18, 5, 'Zij hebben hier geen kennis over, noch hun vaderen. Slecht zijn de woorden die uit hun monden voortkomen: zij spreken slechts leugenachtig.'),
(2146, 18, 6, 'En misschien zou jij (O Moehammad) jezelf na hun afwenden vernietigen van verdriet, wanneer zij niet in dit bericht (de Koran) geloven.'),
(2147, 18, 7, 'Voorwaar, Wij hebben wat op de aarde is een versiering voor haar gemaakt om hen te beproeven, (om te zien) wie van hen het beste werk (verrichtte).'),
(2148, 18, 8, 'En voorwaar, Wij zullen zeker wat op haar (de aarde) is tot droge grond maken.'),
(2149, 18, 9, 'Of denk jij (O Moehammad) dat de Bewoners van de Grot (al-Kahf) en de Raqîm verbazingwekkend onder Onze Tekenen waren?'),
(2150, 18, 10, 'Toen de jongeren een schuilplaats in de Grot zochten, zeiden zij: "O onze Heer, schenk ons van Uw Zijde Barmhartigheid en bereid ons een rechte weg voor onze zaak."'),
(2151, 18, 11, 'En tom brachten Wij in de Grot een sluier over hen aan (zodat zij sliepen), voor een bepaald aantal jaren.'),
(2152, 18, 12, 'Vervolgens wekten Wij hen op, opdat Wij deden weten welke van de twee groepen het beste was in het berekenen van hoelang zij daar verbleven.'),
(2153, 18, 13, 'Wi berichten jou kun geschiedenis volgens de Waarheid: voorwaar, zij waren jongeren die in hun Heer geloofden en Wij versterkten voor ben de Leiding.'),
(2154, 18, 14, 'En Wij versterkten hun harten toen zij opstonden, en zeiden: "Onze Heer, Heer van de hemelen en de aarde, nooit zullen Wij een god naast Hem aanroepen: anders zouden wij een enorme leugen uitgesproken hebben!'),
(2155, 18, 15, 'Dit volk van ons heeft nmt Hem een god genomen, hadden zij daarvoor geen duidelijk bewijs naar voren kunnen brengen? En wie is er onrechtvaardiger dan degene die over Allah leugens verzint?'),
(2156, 18, 16, '"En als jullie je van ben (de ongelovigen) afwenden en van wat zij aanbidden, behalve Allah (zo zei één van de vervolgden tegen de anderen vervolgden), zoek dan bescherming in de Groten jullie Heer zal Zijn Barmhartigheid over jullie uitspreiden, en voor jullie in jullie zaak gemak brengen."'),
(2157, 18, 17, 'En jij zou zien dat de zon, wanneer zij opging, zich aan de rechterkant van hun grot wegboog; en wanneer zij onderging, zich aan de linkerkant wegboog, terwijl zij in de open ruimte ervan (lagen): dit behoort tot de Tekenen van Allah. Wie door Allah geleid wordt: hij volgt Leiding. En wie tot dwaling gebracht wordt: hij zal nooit een helper vinden die hem recht leidt.'),
(2158, 18, 18, 'En jij zou gedacht hebben dat zij wakker waren; maar zij waren slapend. En Wij draaiden hen op hun rechterkant en op hun linkerkant; en hun hond lag uitgestrekt met zijn voorpoten op de voorgrond. Als jij hen zou zien. had jij hen de rug toegekeerd, vluchtend, en jij zou met angst voor hen vervuld zijn.'),
(2159, 18, 19, 'En zo wekten Wij hen op, opdat zij elkaar zouden ondervragen. Eén van hen zei: "Hoe lang hebben jullie (hier) verbleven?" Zij zeiden: Wij verbleven (hier) een dag of een gedeelte van een dag." Zij zeiden: "Jullie Heer weet beter hoelang jullie (hier) verbleven. Stuurt den één van jullie met dit geld van jullie naar de stad en laat hem zien welk voedsel het beste is en laat hem daarvan levens voorziening voor jullie meenemen, en laat hem zachtmoedig zijn, en laat hem niet naar één van jullie informeren.'),
(2160, 18, 20, 'Voorwaar, indien zij over jullie zouden vernemen, dan zouden zij jullie stenigen, of jullie terug doen keren tot hun godsdienst en dan zouden jullie nooit welslagen."'),
(2161, 18, 21, 'Zo brachten Wij hen de hoogte, opdat zij weten dat de belofte van Allah Waarheid is en dat er gen twijfel aan het Uur is. Toen zij (de mensen in de stad) over hun geval twistten, zeiden zij: "Bouwt een gebouw over hen heen (als amdenken)." Hun Heer weet beter over hen. Degenen die de overhand kregen over hun geval, zeiden. "Wij zullen zeker over hen een gebedsruimte maken."'),
(2162, 18, 22, 'Zij zeggen: "(Zij waren met) drie, hun vierde was hun hond," en zij zeggen, "Vijf, hun zesde was hun hond," radend naar het onbekende. En zij zeggen: "Zeven, hun achtste was hun bond." Zeg: "Mijn Heer kent hun aantal beter, zij kennen het niet, behalve weinigen." Redetwist daarom niet over hen, behalve (met) een duidelijk twistgesprek. En ondervraagt niet één van hen over hen.'),
(2163, 18, 23, 'En zeg zeker nooit over iets: "Voorwaar, dat zal ik morgen doen."'),
(2164, 18, 24, '(Zeg) slechts: "Indien Allah het wenst" en herinner je jouw Heer indien jij, vergeet, en zeg: "Hopelijk zal mijn Heer mij leiden naar wat dichterbij is van deze Leiding."'),
(2165, 18, 25, 'En zij verbleven driehonderd jaar in hun grok en vermeerderd met negen.'),
(2166, 18, 26, 'Zeg (O Moehammad): "Allah weet beter hoelang zij er verbleven, aan Hem behoort het verborgene van de hemelen en de aarde, Hij ziet het beter en Hij hoort het beter. Er is voor hen naast Hem beschermer en Hij laat niet één deelgenoot in Zijn oordeel toe."'),
(2167, 18, 27, 'En draag voor van wat aan jou geopenbaard is van het Boek van jouw Heer. Niemand kan Zijn Woorden veranderen en jij zult naast Hem nooit een schuilplaats vinden.'),
(2168, 18, 28, 'En wees zelf geduldig met degenen die hun Heer in de morgen en de avond aanroepen, zij wensen Zijn Aangezicht. En wend jouw ogen niet af van hen omdat jij de versierselen van het wereldse leven wenst. En gehoorzaam niet degene wiens hart Wij Onze gedachtenis hebben doen veronachtzamen en (die) zijn begeerten volgt: en bij is in overtreding in zijn zaak.'),
(2169, 18, 29, 'En zeg: "De Waarheid is van jullie Heer: dus wie wil, laat hem geloven; en wie wil, laat hem ongelovig zijn." Voorwaar, Wij hebben voor de onrechtplegers het vuur voorbereid, waarvan de rook hen als een tent omhult. En als zij hulp (tegen dorst) vragen worden zij geholpen met water als gesmolten koper dat hun gezichten roostert. De slechtste drank en de shebtste verblijfplaats!'),
(2170, 18, 30, 'Voorwaar, degenen die geloven en goede werken verrichten: voorwaar, Wij zullen de beloning van wie een goed werk verricht niet verloren doen gaan.'),
(2171, 18, 31, 'Zij zijn degenen voor wie er de Tuinen van \'Adn (het Paradijs) zijn, waar onder door de rivieren stromen, zij zullen er in versierd worden met kettingen van goud en zij dragen groene gewaden van zijde en brokaat, er in leunend op kussens, De beste beloning en de beste verblijfplaats!'),
(2172, 18, 32, 'En geef hun de gelijkenis van de twee mannen: aan één van hen deden Wij twee tuinen met druivenstruiken toekornen en Wij omringden die met dadelpalmen (en) tussen hen in akkers.'),
(2173, 18, 33, 'Ieder van die twee tuinen bracht vructen voort en faalde daar in niets in. En in hun midden deden Wij een rivier ontspringen.'),
(2174, 18, 34, 'En er waren vruchten voor hem. Hij zei dus tot zijn (gelovige) gesprekspartner: "Ik ben jouw meerdere op het gebied van bezit en ik ben eervoller als persoon."'),
(2175, 18, 35, 'En hij ging zijn tuin binnen en hij deed zichzelf onrecht aan, hij zei: "Ik denk niet dat die (tuinen) ooit zullen vergaan.'),
(2176, 18, 36, 'En denk niet dat het Uur der Opstanding zal plaatsvinden, en zelfs al zal ik tot mijn Heer teruggebracht worden: ik zat ervoor zeker een betere plaats van terugkeer vinden."'),
(2177, 18, 37, 'Zijn (gelovige) gesprekspartner zei, toen hij met hem in gesprek was, tot hem: "Geloof jij niet in Degene Die jou van aarde geschapen heeft, vervolgens uit een druppel sperma, en jou ten slotte tot mens vormde?'),
(2178, 18, 38, 'Maar wat mij betreft: Allah is mijn Heer en ik ken mijn Heer niet één deelgenoot toe.'),
(2179, 18, 39, 'En had jij maar, toen jij je tuin binnentrad, gezegd: \'Mâ sjâ\'a Allah, lâ qoewwata illâbillah\' - (Wat Allah wil, er is geen kracht dan door Allah), indien jij van mij ziet, dat ik minder danjou ben, op het gebied van bezit en kinderen.'),
(2180, 18, 40, 'Moge mijn Heer mij iets beters geven dan jouw tuin en een ramp over haar neerzenden vanuit de hemel, zodat het glibberige aarde wordt!'),
(2181, 18, 41, 'Of haar water in de aarde wegvloeit, zodat jij het nooit meer zal kunnen vinden."'),
(2182, 18, 42, 'En zijn vruchten werden vernietigd. Toen begon hij zich in zijn handen te wringen, over wat hij ervoor uitgegeven had en dat zij (de tuinen) nu tot de bodem toe geruïneerd waren." En hij zei: "Wee mij, had ik maar mijn Heer niet één deelgenoot toegekend!"'),
(2183, 18, 43, 'En hij had geen groep die hem naast Allah hielp en hij was niet staat zichzelf te verdedigen.'),
(2184, 18, 44, 'Daar, de hulp is van Allah, de Ware. Hij is beter (als gever van) een beloning en beter (als gever van) een bestemming.'),
(2185, 18, 45, 'En maak voor hen de vergelijking met het wereldse leven: het is als water dat Wij uit de hemel deden neerdalen; het vermengde zich met de beplanting van de aarde. Vervolgens werd die tot droge stoppels en de wind verwaaide die. En Allah is Almachtig over alle zaken.'),
(2186, 18, 46, 'Het bezit on de zonen zijn de versieringen van het wereldse leven, maar de goede blijvende (daden) zijn beter bij jouw Heer, als beloning en betere hoop.'),
(2187, 18, 47, 'En op de Dag dat Wij de bergen verplaatsen, zal jij de aarde duidelijk zien. En Wij zullen hen verzamelen en Wij zullen niet één van hen achterlaten.'),
(2188, 18, 48, 'En zij zullen voor jouw Heer in rijen opgesteld worden (en Allah zal tot hen zeggen). "Voorzeker, jullie zijn tot Ons gekomen zoals Wij jullie de eerste keer schiepen. Jullie vermoedden zelfs dat Wij de afspraak met jullie nooit zouden maken."'),
(2189, 18, 49, 'En het boek (met hun daden) zal voor hen geplaatst worden en jij zult de zondaren in angst zien wegens wat daarin staat. En zij zullen zeggen: "Wee ons, wat is dat voor boek dat niets kleins weglaat en niets groots, zonder dat berekend te hebben!" En zij zullen wat zij deden vóór zich vinden. En jouw Heer doet niet één van hen onrecht aan.'),
(2190, 18, 50, 'En (gedenkt) toen Wij tot de Engelen zeiden: "Knielt jullie eerbiedig voor Adam neer," toen knielden zij, behalve Iblîs, hij behoorde tot de Dijinn\'s en hij schond het gebod van zijn Heer. Zouden jullie dan hem en zijn nageslacht als helpers naast Mij nemen, terwijl zij voor jullie vijnden zijn? Slecht is de ruil voor onrechtvaardigen!'),
(2191, 18, 51, 'Ik roep hen niet op om te getuigen van de schepping van de hemelen en de aarde en niet van de schepping van henzelf, en Ik neem niet degenen die doen dwalen als helpers.'),
(2192, 18, 52, 'En op de Dag waarop Hij zegt: "Roept de deelgenoten op waarvan jullie (het bestaan) vermoedden," zullen zij hen oproepen, maar zij zullen hen niet antwoorden en Wij zullen onder hen een (plaats van) verniettiging maken.'),
(2193, 18, 53, 'En de zondaren zullen de Hel zien en vermoeden dat zij er in moeten gaan, en zij vinden daaruit gew ontvluchting.'),
(2194, 18, 54, 'En Wij hebben in deze Koran voor de mensen allerlei vergelijkingen uitgelegd. Maar de mensen zijn het meest over ich twistziek.'),
(2195, 18, 55, 'En er is niets dat de mens verhindert om te geloven als de Leiding tot hen komt en om vergeving te vragen bij hun Heer, behalve de handelwijze die de vroegeren trof, of de bestraffing die zichtbaar over hen kwam.'),
(2196, 18, 56, 'En Wij zenden de Boodschappers slechts als verkondigers van goede tijdingen en waarschuwers. En degenen die ongelovig zijn twisten slechts op valse wijze om daarmee de Waarheid te vernietigen. En zij nemen Mijn Verzen en dat waarvoor gewaarschuwd werd, ter bespotting.'),
(2197, 18, 57, 'En wie is er onrechtvaardiger dan degene die met Verzen van zijn Heer vermaand wordt, maar zich daarvan afwendt en vergeet wat zijn handen gedaan hebben? Voorwaar, Wij hebben over hun harten een versluiering aangebracht, zodat zij het niet begrijpen, en in hun oren doofheid. En indien jij hen tot de Leiding oproept: zij zullen nooit en te nimmer Leiding volgen.'),
(2198, 18, 58, 'En jouw Heer is de Vergevensgezind, Bezitter van de Barmhartigheid. Indien Hij hen zou grijpen voor wat zij verricht hebben, dan zou bij zeker de bestraffing voor hen versneld hebben. Maar voor hen is er de afspraak (op de Dag van de Opstanding), nooit zullen zij buiten Hem een toevlucht vinden.'),
(2199, 18, 59, 'En dat zijn de steden die Wij vernietigd hebben toen zij onrechtvaardig waren; en Wij hebben voor hun vernietiging een tijdstip bepaald.'),
(2200, 18, 60, 'En (gedenkt) toein Môesa tot zijn gezel zei: "Ik zal niet opgeven voordat ik de samenvloeiing van de twee zeeën bereik, of ik zal altijd doorgaan."'),
(2201, 18, 61, 'En toen zij de samenvloeiing van belde bereikten, vergaten zij hun vis, die zich vrijelijk zijn weg in de zee zocht.'),
(2202, 18, 62, 'En toen zij voortgingen zei hij (Môesa) tot zijn gezel: "Breng ons ons eten (de vis). Voorzeker, wij zijn door deze reis van ons moe geworden."'),
(2203, 18, 63, 'Hij (de gezel) zei: "Zag jij (wat er gebeurde) toen wij ons naar de rots begaven\'? Voorwaar, toen vergat ik de vis, en ik ben hem niet vergelen te herinneren, behalve door de Satan. En bij zocht zijn weg in de zee op een verbazingwekkende manier."'),
(2204, 18, 64, 'Hij (Môesa) zei: "Dat was wat wij zochten," toen keerden zij in hun voetstappen terug.'),
(2205, 18, 65, 'Toen vonden zij een dienaar van onder Onze dienaren, aan wie Wij Barmhartigheid van Onze Zijde gegeven hadden en Wij hadden hem van Onze Zijde kennis bijgebracht.'),
(2206, 18, 66, 'Môesa zei tot hem: "Mag ik jou volgen, opdat jij mij onderwijst wat aan jou van de kennis geleerd is?"'),
(2207, 18, 67, 'Hij zei: "Voorwaar, jij zult nooit in staat zijn geduld met mij te hebben.'),
(2208, 18, 68, 'En hoe kan jij geduld hebben met wat jouw begrip niet omvat?"'),
(2209, 18, 69, 'Hij (Môesa) zei: "Jij zult vinden dat ik, indien Allah het wil, een geduldige ben en ik zal jou in geen opdracht ongehoorzaam zijn."'),
(2210, 18, 70, 'Hij zei: "Als jij mij volgt, stel mij dan geen vraag over iets vôôr ik jou er de uitleg van vertel."'),
(2211, 18, 71, 'Zo gingen zij verder tot zij aan boord van de boot gingen en hij er een gat in maakte. Hij (Môesa) zei: "Maakte jij er een gat in om de opvarenden ervan te doen verdrinken? Voorzeker, jij hebt iets raars gedaan!"'),
(2212, 18, 72, 'Hij zei: "Heb ik jou niet gezegd: \'Voorwaar, jij bent niet in staat om geduld met mij te hebben?"'),
(2213, 18, 73, 'Hij (Môesa) zei: "Neem mij niet kwalijk wat ik vergeten ben, en belast mij niet in mijn zaak met moeilijkheden."'),
(2214, 18, 74, 'Zo gingen zij verder totdat zij een jongeling ontmoetten en hij hem doodde. Hij (Môesa) zei: "Doodde jij een onschuldige ziel zonder recht? Voorzeker, jij hebt iets vreemds gedaan!"'),
(2215, 18, 75, 'Hij zei: "Heb ik jou dan niet gezegd dat jij nooit in staat zal zijn geduld met mij te hebben?"'),
(2216, 18, 76, 'Hij (Môesa) zei: "Als ik jou bierna over iets vraag, laat mij jou dan niet vergezellen, jij hebt van mijn kant waarlijk al een verontschuldiging gekregen."'),
(2217, 18, 77, 'Zo gingen zij verder totdat zij enkele bewoners uit een stad ontmoetten en haar bewoners om voedsel vroegen, maar zij weigerden hen gastvrijheid te verlenen. Toen vonden zij daar een muur die dreigde in te storten, maar hij zette die weer recht. Hij (Môesa) zei: "Als jij gewild had, zou jij hiervoor zeker een vergoeding kunnen aannemen."'),
(2218, 18, 78, 'Hij zei: "Dit is de scheiding tussen mij en jou. Ik zal jou op de hoogte brengen van de uitleg van hetgeen waarmee jij niet in staat was geduld te hebben.'),
(2219, 18, 79, 'Wat betreft de boot: zij was van arme mensen, die op zee werkten, en ik wilde haar onbruikbaar maken, want na hen kwarn een koning, die elke boot met geweld zou nemen.'),
(2220, 18, 80, 'Wat betreft de jongen: zijn ouders waren gelovigen. maar wij vreesden dat hij hen tot dwaling en ongeloof zou dwingen.'),
(2221, 18, 81, 'Daarom wilden wij dat hun Heer hem voor hen ruilde voor een betere zoon dan hem, reiner en rneer nabij in genegenheid.'),
(2222, 18, 82, 'En wat betreft de muur: die behoorde toe ann twee jongelingen die wees waren in de stad en eronder lag een schat die bestemd was voor ben, en hun vader was een oprechte man geweest. Daarom wenste jouw Heer dat zij hun volwassen leeftijd bereikten en (dan) hun schat er uit haalden, als Barmhartigheid van jouw Heer. En ik deed het niet uit mijn eigen wil; dat is de uitleg over hetgeen waamee jij niet in staat was geduld te hebben."'),
(2223, 18, 83, 'En zij vragen jou (O Moehammad) over Dzôelqarnain'),
(2224, 18, 84, 'zeg: "Ik zal een geschiedenis over hem aan jullie voordragen." Voorwaar, Wij versterkten zijn positie op aarde en Wij gaven hem voor alle zaken mogelijkheden.'),
(2225, 18, 85, 'Daarop volgde hij een weg.'),
(2226, 18, 86, 'Totdat, toen hij de plaats van de zonsondergag bereikte, hij haar onder vond gaan in een modderige bron, en hij trof daarbij een volk aan. Wij zeiden: "O Dzôelqarain: ôf jij geeft hun een bestraffing, ôf jij behandelt hen met goedheid."'),
(2227, 18, 87, 'Hij zei: "Wat betreft degene die onrechtvaardig is: wij zullen hem straffen, waarna hij tot zijn Hecir teruggezonden zal worden, en Hij zal hem dan straffen met een verschrikkelijke bestraffing.'),
(2228, 18, 88, 'Maar wat betreft degene, die gelooft en goede werken verricht: voor hem zal er een goede beloning zijn en wij zullen hem van ons bevel zeggen het gemakkelijke (te doen)."'),
(2229, 18, 89, 'Daarop volgde hij een weg.'),
(2230, 18, 90, 'Totdat, toen hij de plaats van zonsopgang bereikte, hij haar over een volk zag opgaan aan wie Wij geen bedekking hadden doen toekomen.'),
(2231, 18, 91, 'Zo was het (en hij liet hen met rust). En Wij omvatten waarlijk met Onze kennis wat bij hem was.'),
(2232, 18, 92, 'Daarop volgde hij een weg.'),
(2233, 18, 93, 'Totdat, toen hij tussen de twee bergen kwarn, hij vôôr die een volk aantrof dat nauwelijks een woord begreep.'),
(2234, 18, 94, 'Zij zeiden: "O Dzôelqarnain, (de volken van) Ya\'djôedj en Ma\'djôedj zijn verderfzaaiers op aarde. Zullen wij jou een vergoeding geven opdat jij tussen ons en hen een afscheiding maakt?"'),
(2235, 18, 95, 'Hij (Dzôelqarnain) zei: "(De macht) waarmee mijn Heer mij voorzien heeft is beter. Helpt mij daarorn met kracht, opdat ik een sterke muur tussen jullie en hen zal bouwen.'),
(2236, 18, 96, 'Brengt mij brokken ijzer." Totdat, toen hij (de ruimte) tussen de twee hellingen gevuld had, hij zei: "Blaast." Totdat, toen het roodgloeiend werd, hij zei: "Brengt mij gesmolten ijzer om het eroverheen te gieten."'),
(2237, 18, 97, 'En zij waren niet in staat om het te beklimmen en niet om er door heen te breken.'),
(2238, 18, 98, 'Hij (Dzôelqarnain) zei: "Dit is Barmhartigheid van mijn Heer. Maar als de belofte van mijn Heer komt, maakt Hij het tot stof. En de belofte van mijn Heer is Waarheid."'),
(2239, 18, 99, 'En op die Dag laten Wij hen, om hen met elkaar te mengen. En er wordt op de Bazuin geblazen, waarna Wij hen allen bij elkaar zullen brengen.'),
(2240, 18, 100, 'En op die Dag zullen Wij de Hel duidelijk zichtbaar aan de ongelovigen tonen.'),
(2241, 18, 101, 'Degenen voor wie de ogen versluierd waren voor het gedenken van Mij en (die) niet tot horen in staat waren.'),
(2242, 18, 102, 'Denken degenen die niet geloven, dat zij Mijn dienaren naast Mij tot beschermers kunnen nemen? Voorwaar, wij hebben de Hel klaargemaakt als een verblijfplaats voor de ongelovigen.'),
(2243, 18, 103, 'Zeg (O Moehammad): "Zullen wij jullie op de hoogte brengen van wie de grootste verliezers zijn door (hun) daden?"'),
(2244, 18, 104, '(Het zijn) degenen wiens daden vruchteloos waren in het wereldse leven. En zij dachten dat zij goed werk verrichtten.'),
(2245, 18, 105, 'Zij zijn degenen die niet geloven in de Tekenen van hun Heer en in de ontmoeting met Hem. Hun daden zijn vruchteloos en Wij kennen hun (daden) op de Dag der Opstanding geen gewicht toe.'),
(2246, 18, 106, 'Dat is hun vergelding: de Hel, omdat zij ongelovig waren en zij de spot dreven met Mijn Tekenen en Mijn Boodschappers.'),
(2247, 18, 107, 'Voorwaar, voor degenen die geloven en goede daden verrichten zijn er de Tuinen van Firdaus (het Paradijs) als een verblijfplaats.'),
(2248, 18, 108, 'Zij zullen daarin eeuwig levenden zijn. En zij zullen daar geen verandering in wensen.'),
(2249, 18, 109, 'Zeg (O Moehammad): "Al werd de zee inkt om de Woorden van mijn Heer (op te schrijven), zij zou opraken voordat de Woorden van mijn Heer opgeraakt zouden zijn, zelfs al voegden Wij een daaraan gelijke hoeveelheid toe."'),
(2250, 18, 110, 'Zeg: "Voorwaar, ik ben slechts een mens zoals jullie. Het is aan mij geopenbaard dat jullie god één God is. Wie daarom de ontmoeting met zijn Heer verwacht: laat hem goede daden verrichten en laat hem bij de aanbidding van zijn Heer niet één deelgenoot toekennen."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'Kâf Ha Ya \'Ain Shâd,'),
(2252, 19, 2, '(Dit is) een vermelding van Barmhartigheid van jouw Heer aan Zijn dienaar Zakariyyâ.'),
(2253, 19, 3, '(Gedenkt) toen hij zachtjes zijn smeekbeden tot zijn Heer richtte.'),
(2254, 19, 4, 'Hij (Zakariyyâ) zei: "O mijn Heer, voorwaar, mijn botten zijn zwak en mijn haar is grijs glanzend, nooit ben ik, O Heer, teleurgesteld geraakt in mijn smeekbeden tot U.'),
(2255, 19, 5, 'En ik bein bang voor (het ontbreken van) mijn nakomelingen na mij, want mijn vrouw is onvruchtbaar. Dus schenk mij van Uw Zijde een nakomeling.'),
(2256, 19, 6, 'Die van mij zal erven en van de familie van Ya\'qôeb. Mijn Heer, maak hem U welgevallig."'),
(2257, 19, 7, '(Allah zei:) "O Zakariyyâ, Wij brengen jou de verbeugende tijding van een jongen, Yahya genaamd. Wij gaven niemand eerder deze naam."'),
(2258, 19, 8, 'Hij (Zakariyyâ) zei: "O mijn Heer, voorwaar, hoe kan ik een jongen krijgen terwijl mijn vrouw onvruchtbaar is? En waarlijk, ik heb een hoge leeftijd bereikt."'),
(2259, 19, 9, 'Hij (Allah) zei: "Zo zal het zijn, jouw Heer zegt: het is makkelijk voor Mij. En voorwaar. Ik heb jou hiervoor geschapen toen jij niets was."'),
(2260, 19, 10, 'Hij zei: "O mijn Heer, geef mij een Teken." Hij (Allah) zei: "Jouw Teken is dat jij gedurende drie nachten niet zult spreken met de mensen, hoewel jij gezond bent."'),
(2261, 19, 11, 'Toen kwam hij van de plaats van gebed naar zijn volk, en gebaarde hen: "Looft (Allah\'s) glorie in de ochtend en de avond."'),
(2262, 19, 12, '(Allah zei:) "O Yahya, neem de Schrift stevig aan." En Wij gaven de Wijsheid aan hem, terwijl hij jong was.'),
(2263, 19, 13, 'Als genegenbeid van Onze Zijde en reiniging. En hij vreesde (Allah).'),
(2264, 19, 14, 'En hij was goed voor zijn ouders en hij was niet arrogant en ongehoorzaam.'),
(2265, 19, 15, 'Vrede zij met hem op de dag dat hij geboren werd en de dag dat hij sterft en op de dag dat hij tot leven wordt opgewekt.'),
(2266, 19, 16, 'En noem in het boek (de Koran) Maryam, toen zij zich terugtrok van haar familie naar een oostelijke plaats (in de tempel).'),
(2267, 19, 17, 'En zich van hen afzonderde achter een scherm. Vervolgens zonden Wij haar Onze Geest (Djibrîl) en hij verscheen aan haar als een volmaakt mens.'),
(2268, 19, 18, 'Zij zei: "Ik zoek mijn bescherming bij de Barmhartige tegen jou, als jij (Allah) vreest."'),
(2269, 19, 19, 'Hij zei: "Voorwaar, ik ben slechts een gezant van jouw Heer, om jou een reine jongen te schenken."'),
(2270, 19, 20, 'Zij zei: "Hoe kan ik een jongen krijgen terwijl geen man mij heeft aangeraakt, ik ben geen onzedelijke vrouw."'),
(2271, 19, 21, 'Hij zei: "Zo is het, jouw Heer heeft gezegd: \'Dit is makkelijk voor Mij. En opdat Wij hem tot een Teken voor de mensen maken en als Barmhartigheid van Ons. En het is een vastgestelde zaak."'),
(2272, 19, 22, 'En zo droeg zij hem en trok zich niet hem terug op een verre plek.'),
(2273, 19, 23, 'En de geboorteweeën dwongen haar naar de stam van een palmboom te gaan. Zij zei: "Was ik maar hiervoor gestorven en volledig vergeten geweest."'),
(2274, 19, 24, 'Toen riep hij (Djibrîl) haar van beneden de palmboom: "Treur niet, want jouw Heer heeft een boekje beneden jou verschaft.'),
(2275, 19, 25, 'En schud de stam van de palmboom naar jou toe, dan zullen er rijpe dadels op jou vallen.'),
(2276, 19, 26, 'Dus eet en drink en verkoel jouw ogen. Maar als jij iemand ziet, zeg dan: "Voorwaar, ik heb de Barmhartige belooft te vasten, dus zal ik vandaag tot geen mens spreken."'),
(2277, 19, 27, 'Toen ging zij naar haar volk, hem (\'Îsa) dragend. Zij zeiden: "O Maryam, jij hebt iets vreemds gedaan.'),
(2278, 19, 28, 'O zuster van Hârôen, jouw vader was geen slechte man en jouw moeder was geen onzedelijke vrouw."'),
(2279, 19, 29, 'Daarop wees zij naar hem. Zij zeiden: "Hoe kunnen wij spreken met een baby, die nog als kind in de wieg ligt?"'),
(2280, 19, 30, 'Hij (\'Îsa) zei: "Voorwaar, ik ben een dienaar van Allah. Hij heeft mij de Schrift gegeven en mij tot een Profeet gemaakt.'),
(2281, 19, 31, 'En Hij heeft mij gezegend waar ik ook ben en Hij heeft mij bevolen de shalât te verrichten en de zamt (te betalen), zolang ik leef.'),
(2282, 19, 32, 'En om goed te zijn voor mijn moeder. En Hij heeft mij niet als een arrogante ongehoorzame gemaakt.'),
(2283, 19, 33, 'Vrede zij met mij op de dag dat ik geboren werd en op de dag dat ik sterf en op de dag dat ik tot leven word opgewekt."'),
(2284, 19, 34, 'Dat is \'Îsa, zoon van Maryam, het Woord van Waarheid waaraan zij twijfelen.'),
(2285, 19, 35, 'Het is niet passend voor Allah om een zoon te hebben, Heilig is Hij, als Hij iets bepaalt, zegt Hij er slechts tegen: "Wees," en het is.'),
(2286, 19, 36, '(Zeg:) "En voorwaar, Allah is mijn Heer en jullie Heer: dus aanbidt Hem, dit is het rechte Pad."'),
(2287, 19, 37, 'Maar de groepen verschilden onderling van mening; dus wee hen die niet geloven getuige te zijn van de geweldige Dag.'),
(2288, 19, 38, 'Hoeveel beter zullen zij luisteren en hoeveel scherper zullen zij zien op de Dag dat zij tot Ons zullen komen! Maar de onrechtvaardigen verkeren in duidelijke dwaling.'),
(2289, 19, 39, 'En waarschuw hen voor de Dag van spijt, wanneer de zaak bepaald is; zij zijn onachtzaam en zij geloven niet.'),
(2290, 19, 40, 'Voorwaar, Wij erven de aarde en een ieder die zich daarop bevindt. En tot Ons zullen zij weerkeren.'),
(2291, 19, 41, 'En vertel (O Moehammad) in het Boek over lbrâhîm: hij was waarheidsgetrouw, een Profeet.'),
(2292, 19, 42, '(Gedenk) toen hij tot zijn vader zei: "O mijn vader, waarom aanbidt u iets dat niet hoort, niet ziet en u niets baat?'),
(2293, 19, 43, 'O mijn vader, er is waarlijk kennis tot mij gekomen, die niet tot u kwam. Dus volg mij, dan leid ik u op het rechte Pad.'),
(2294, 19, 44, 'O mijn vader, gehoorzaam de Satan niet: voorwaar, de Satan is opstandig tegen de Barmhartige.'),
(2295, 19, 45, 'O mijn vader, ik ben bang dat een bestraffing van de Barmhartige u zig treffen, dan zal u een helper van de Satan worden."'),
(2296, 19, 46, 'Hij zei: "Haatjij mijn poden. O (Ibrâhîm? Als jij niet ophoudt, zal ik jou zeker stenigen. Ga bij mij vandaan voor een lange tijd."'),
(2297, 19, 47, 'Hij (Ibrâhîm) zei: "Vrede zij met u, ik zal vergeving voor u vragen bij mijn Heer: voorwaar, Hij is altijd mild voor mij.'),
(2298, 19, 48, 'En ik zal weggaan van jullie en van wat jullie naast Allah aanbidden. En ik zal bij mijn Heer smeken, moge ik niet teleurgesteld zijn in mijn smeekbeden aan mijn Heer."'),
(2299, 19, 49, 'En toen hij van hen wegging en van wat zij naast Allah aanbaden, schonken Wij hem Ishâq en Ya\'qôeb en Wij maakten leder van hen een Profeet.'),
(2300, 19, 50, 'En Wij schonken hun van Onze Barmhartigheid en Wij maakten hen geëerd, verheven.'),
(2301, 19, 51, 'En vertel (O Moehammad) in het Boek over Môesa: hij was een uitverkorene en hij was een Boodschapper, een Profeet.'),
(2302, 19, 52, 'En Wij riepen hem van de rechterzijde van (de berg) Thôer en Wij brachten hem dichterbij Ons, zachtjes smekend.'),
(2303, 19, 53, 'En Wij schonken hem van Onze Barnhartigheid zijn broeder Hârôen, een Profeet.'),
(2304, 19, 54, 'En vertel in het Boek over Ismâ\'îl: hij was trouw aan zijn beloften en hij was een Boodschapper, een Profeet.'),
(2305, 19, 55, 'En hij placht zijn familie de shalât en de zakât te bevelen en hij vond welbehagen bij zijn Heer.'),
(2306, 19, 56, 'En vertel in het Boek over Isrîs: hij was een oprechte, een Profeet.'),
(2307, 19, 57, 'En Wij verhieven hem in een verheven positie.'),
(2308, 19, 58, 'Zij zijn degenen die Allah begenadigd heeft onder de Profeten van de nakomelingen van Adam en degenen die Wij met Nôeh (in de ark) droegen en onder de nakomelingen van Ibrâhîm en Isrâ\'îl en onder hen die Wij geleid en uitgekozen hebben. Wanneer aan ben de Verzen van de Barmhartige worden voorgedragen, buigen zij knielend neer en huilen.'),
(2309, 19, 59, 'Maar na ben volgden andere generaties, die de shalât achterwege lieten en de begeerten volgden. Daarom zullen zij verlies tegemoet zien.'),
(2310, 19, 60, 'Behalve wie berouw toont en gelooft en goede daden verricht. Zij zijn het die het Paradijs zullen binnentreden en hun zal geen onkel onrecht aangedaan worden.'),
(2311, 19, 61, 'Tuinen van \'Adn (het Paradijs) die de Barmhartige aan zijn dienaren in het onwaarneembare beloofde. Voorwaar, (de vervulling van) Zijn belofse vindt zeker plaats.'),
(2312, 19, 62, 'Zij zullen daarin geen onzinnige gesprekken horen, maar slechts \'Vrede\' en daarin is voor hen levensonderhoud, \'s ochtends en \'s avonds.'),
(2313, 19, 63, 'Dat is het Paradijs dat Wij Onze dienaren die (Allah) vrezen doen erven.'),
(2314, 19, 64, 'Wij (Engelen) dalen slechts op bevel van jouw Heer neer. Aan Hem behoort alles wat voor ons is en wat achter ons is en wat ertussen is. En jouw Heer is niet vergeetachtig.'),
(2315, 19, 65, '(Hij is de) Heer van de hemelen en de aarde en wat ertussen is: aanbid Hem daarom, en wees geduldig in het aanbidden van Hem. Ken jij iemand die am Hem gelijkwandig is?"'),
(2316, 19, 66, 'En de mens zegt: "Is het waar dat als ik sterf, ik zeker tot leven zal worden opgewekt?"'),
(2317, 19, 67, 'Herinnert de mens zich niet dat Wij hem eerder geschapen hebben, toen hij niets was?'),
(2318, 19, 68, 'Daarom, bij jouw Heer, Wij zullen hen en de Satans zeker verzamelen. Vervolgens zullen Wij ten zeker rondom de Het brengen, knielend.'),
(2319, 19, 69, 'Dan zullen Wij uit elke groep degenen plukken, die het meest in opstand kwamen tegen de Barmhartige.'),
(2320, 19, 70, 'Dan zullen Wij zeker degenen gekend doen worden, die het meest verdienen daarbinnen te gaan.'),
(2321, 19, 71, 'En er is niemand onder jullie of hij treedt daar binnen. Het is van jouw Heer een als onafwendbaar vastgesteld besluit.'),
(2322, 19, 72, 'Dan zuilen Wij hen redden die (Allah) vreesden, en Wij laten de onrechtplegers er in achter, knielend.'),
(2323, 19, 73, 'En wanneer Onze duidelijke Verzen aan hen worden voorgedragen, zeggen degenen die ongelovig zijn tegen degenen die gelovig zijn: "Welke van de twee groepen is op een betere plaats en in een beter gezelschap?"'),
(2324, 19, 74, 'En hoeveel generaties hebben Wij niet vóór hen vernietigd, die beter toegerust waren en (mooier in) verschijning.'),
(2325, 19, 75, 'Zeg (O Moehammad): "Wie in dwaling verkeert: de Barmhartige laat voor hem de termijn verlengen, tot wanneer zij zien wat hun aangezegd was: òf de bestraffing, òf het Uur; dan zullen zij weten wie er slecht of is wat betreft plaats, en zwakker is wat betreft een helper."'),
(2326, 19, 76, 'En Allah zal voor degenen die Leiding volgen dt Leiding vemeerderen; en de blijvende goede daden zijn heter bij jouw Heer wat betreft beloning en beter wat betreft de terugkeer.'),
(2327, 19, 77, 'Heb jij degene gezien, die niet in Onze Tekenen gelooft, en zegt: "Aan mij zullen zeker bezit en zonen gegeven worden"?'),
(2328, 19, 78, 'Heeft hij het onwaarneembare gezien of heeft hij een verbond gesloten met de Barmhartige?'),
(2329, 19, 79, 'Welnee, Wij zullen opschrijven wat hij zeg en Wij verlengen de duur van de bestraffing voor hem.'),
(2330, 19, 80, 'En Wij zullen erven wat hij opnoemt en hij zal alleen tol Ons komen.'),
(2331, 19, 81, 'En zij hebben goden naast Allah genomen, opdat zij voor hen een medestander zijn.'),
(2332, 19, 82, 'Zeker niet, zij zullen hun aanbidding verwerpen en tegenstanders van hun zijn.'),
(2333, 19, 83, 'Zie jij niet dat Wij de Satans gestuurd hebben naar de ongelovigen om hen tot wanorde op te stoken?'),
(2334, 19, 84, 'Vraag daarom niet (O Moehammad) om bespoediging voor hen: voorwaar, Wij stellen voor ben hun tijd nauwkeurig vast.'),
(2335, 19, 85, 'Op die Dag zullen Wij de Moeftaqôen voor de Barmhartige verzamelen als afgevaardigen.'),
(2336, 19, 86, 'En Wij zullen de misdadigers naar de Hel drijven, opgedreven (als vee).'),
(2337, 19, 87, 'Zij hebben geen macht tot voorspraak, behalve wie iuet de Barmhartige een verbond heeft gesloten.'),
(2338, 19, 88, 'En zij zeiden: "De Barmhartige heeft Zich een zoon genomen."'),
(2339, 19, 89, 'Voorzeker, jullie zijn met iets verwerpelijks gekomen.'),
(2340, 19, 90, 'De hemelen staan daardoor op het punt om open te barsten en de aarde om open te splijten en de bergen om uiteen te vallen!'),
(2341, 19, 91, 'Omdat zij een zoon toeschrijven aan de Barmhartige.'),
(2342, 19, 92, 'Het is niet passend voor de Barmhartige om Zich een zoon te nemen.'),
(2343, 19, 93, 'En er is niemand in de hemelen of (op) de aarde of bij zal als een dienaar naar de Barmhartige komen.'),
(2344, 19, 94, 'Voorzeker, Hij heeft hun aantal het beste berekend en Hij heeft hun aantal nauwkeurig vastgesteld.'),
(2345, 19, 95, 'leder van ben zal op de Dag der Opstanding alleen tot Hem komen.'),
(2346, 19, 96, 'Voorwaar, degenen die geloven en goede werken verrichten: de Barmhartige zal hen liefde schenken.'),
(2347, 19, 97, 'Voorwar, Wij hebben hem gemakkelijk in jouw taal (O Moehammad) gemaakt opdat jij er goede tijdingen mee brengt aan de Moettaqôen en liet twistende volk er mee waarschuwt.'),
(2348, 19, 98, 'En hoeveel generaties vóór hen hebben Wij niet vernietigd? Zie jij ook maar één van hen of hoor je hun zacht gefluister?');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'Thâ Hâ.'),
(2350, 20, 2, 'Wij hebben de Koran niet tot jou doen neerdalen om jou ongelukkig te maken.'),
(2351, 20, 3, 'Maar slechts als een waarschuwing voor wie (Allah) vreest.'),
(2352, 20, 4, 'Als een openbaring van Hem Die de aarde en de hoge hemelen geschapen heeft.'),
(2353, 20, 5, 'De Barmhartige, Die op de Troon zetelt.'),
(2354, 20, 6, 'Aan Hem behoort wat in de hemelen en op de aarde is en wat ertussen is en wat zich onder de grond bevindt.'),
(2355, 20, 7, 'Of jij niet luide stem spreekt (of niet): voorwaar, Hij kent het geheim en het meest verborgene.'),
(2356, 20, 8, 'Allah, er is geen god den Hij, de Schone Namen (Asmâoelhoesnâ)behoren Hem toe.'),
(2357, 20, 9, 'En heeft het verhaal van Môesa jou bereikt?'),
(2358, 20, 10, 'Toen hij een vuur zag, en tot zij familie zei "Blijft hier, want ik zie een vuur. Misschien zal ik daarvan een fakkel bij jullie brengen of zal ik bij het vuur Leiding vinden."'),
(2359, 20, 11, 'En toen hij daar aankwam, werd geroepen: "O Môesa!"'),
(2360, 20, 12, 'Voorwaar, Ik ben jouw Heer, dus trek jouw sandalen uit: vooirwau, jij bevindt je in de heilige vallei Thoewa.'),
(2361, 20, 13, 'En ik heb jou uitverkoren, dus luister naar wat geopenbaard wordt.'),
(2362, 20, 14, 'Voorwaarm, Ik ben Allah, er is geen god dan Ik. Aanbid mij daarom en onderhoud de shalât om Mij te gedenken.'),
(2363, 20, 15, 'Voorwaar, het Uur zal komen. Ik sta op het punt om Zelf te onthullen dat iedere ziel beloond zal worden voor wat zij nastreeft.'),
(2364, 20, 16, 'Laat je daarom er niet van affhouden door degene die er niet in gelooft en zijn begeerte volgt, zodat jij niet ten onder gaat.'),
(2365, 20, 17, 'Wat is dat daar in jouw rechterhand, O Môesa?"'),
(2366, 20, 18, 'Hij (Môesa) zei: "Dat is mijn staf, waarop ik leun en warmee ik bladeren afsla voor mijn schapen en die ik ook voor andere doelen gebruik."'),
(2367, 20, 19, 'Hij (Allah) zei: "Werp hem neer, O Môesa!"'),
(2368, 20, 20, 'Toen wierp hij hem neer en hij word een slang die zich snel voortbewoog.'),
(2369, 20, 21, 'Hij (Allah) zei: "Pak hem op en wees niet bang, Wij zullen hem weer in de oorspronkelijke toestand terugbrengen.'),
(2370, 20, 22, 'En stop jouw hand onderjouw kraag, hij zal wit tevoorschijn komen, zonder ziekte, als een ander Teken.'),
(2371, 20, 23, 'Zodat Wij jou iets te laten zien van Onze gmte Tekenen.'),
(2372, 20, 24, 'Ga naar Fir\'aun: voorwaar, hij overtrad."'),
(2373, 20, 25, 'Hij (Môesa) zei: "O Heer, verruim mijn borst.'),
(2374, 20, 26, 'En maak mijn taak makkelijk voor mij.'),
(2375, 20, 27, 'En verlos mij van het gebrek in mijn tong.'),
(2376, 20, 28, 'Zodat zij mijn woorden zullen begrijpen.'),
(2377, 20, 29, 'En stel voor mij een helper aan uit mijn familie.'),
(2378, 20, 30, 'Hârôen, mijn broeder.'),
(2379, 20, 31, 'Versterk met hem mijn kracht.'),
(2380, 20, 32, 'En laat hem delen in mijn taak.'),
(2381, 20, 33, 'Zodat wij U veel kunnen prijzen.'),
(2382, 20, 34, 'En U veel zullen gedenken.'),
(2383, 20, 35, 'Voorwaar, U bent Alziend over ons."'),
(2384, 20, 36, 'Hij (Allah) zei: "Waarlijk, jouw verzoek is ingewilligd, O Môesa.'),
(2385, 20, 37, 'En voorzeker, Wij hebben jou een andere keer begunstigd.'),
(2386, 20, 38, 'Toen Wi jouw moeder inspireerden met wat geinspireerd werd.'),
(2387, 20, 39, '(Wij zeiden haar:) \'Leg hem in de kist en werp hem in de zee, zodat de zee hem op de kust zal werpen; een vijand van Mij en een vijand van hem zal hem opnemen.\' En ik heb Mijn liefde over jou uitgestort opdat jij onder Mijn toezicht grootgebracht werd.'),
(2388, 20, 40, 'Toen jouw zuster heenging en (tegen de familie van Fir\'aun) zei: \'Zal ik hem naar iemand brengen, die voor hem kan zorgen?\' Zo brachten Wij jou bij jouw moeder terug, zodat haar ogen verblijdden en niet treurden. En jij hebt iemand gedood; daarop hebben Wij jou uit de moeilijkheden gered en Wij hebben jou aan veel beproevingen blootgesteld. Zo verbleef jij jaren onder het volk van Madyan, toen kwam jij, zoals bepaald, O Môesa.'),
(2389, 20, 41, 'En ik heb jou voor Mijzelf gekozen.'),
(2390, 20, 42, 'Gaat heen jij en je broeder en veronachtzaamt Mijn gedachtenis niet.'),
(2391, 20, 43, 'Gaat naar Fir\'aun: voorwaar, hij overtrad.'),
(2392, 20, 44, 'En spreekt mild tot hem, moge hij zich laten vermanen, of er bang van worden."'),
(2393, 20, 45, 'Zij zeiden: "Onze Heer, voorwaar, wij zijn bang dat hij gewelddadig zal zijn tegen ons, of zal overtreden."'),
(2394, 20, 46, 'Hij zei: "Weest niet bang. Voorwaar, Ik ben met jullie beiden, Ik hoor en Ik zie.'),
(2395, 20, 47, 'Gaat daarom naar hem toe. en zegt: \'Voorwaar, wij zijn Boodschappers van jouw Heer, stuur de Kinderen van Israël met ons en bestraft hen niet. Wij zijn waarlijk tot jou gekomen met een Teken van jouw Heer, en vrede is met degene die de Leiding volgde.'),
(2396, 20, 48, 'Voorwaar, het is aan ons geopenbaard, dat de bestraffing over hem zal komen die (Allah) loochent en zich afwendt."\''),
(2397, 20, 49, 'Hij (Fir\'aun) zei: "Wie is jullie Heer, O Môesa?"'),
(2398, 20, 50, 'Hij (Môesa) zei: "Onze Heer is Degene Die aan ieder ding van Zijn schepping vorm gegeven heeft en (het) daarna geleid heeft."'),
(2399, 20, 51, 'Hij (Fir\'aun) zei, "Hoe staat het dan met de vroegere generaties?"'),
(2400, 20, 52, 'Hij (Môesa) zei: "De kennis over hen is bij mijn Heer, in een Boek. Mijn Heer maakt geen fouten en vergeet niet."'),
(2401, 20, 53, 'Degene Die de aarde voor jullie uitspreidde en er wegen voor jullie op aanlegde, en water uit de hemel deed neerdalen. Waarmee Wij paren van verschillende soorten planten hebben voortgebracht.'),
(2402, 20, 54, 'Eet en laat jullie vee grazen: voorwaar, daarin zijn Tekenen voor de bezitters van verstand.'),
(2403, 20, 55, 'Uit haar hebben Wij jullie geschapen en daarin zullen Wij jullie terug doen keren en daaruit zullen Wij jullie een andere keer opwekken.'),
(2404, 20, 56, 'En voorzeker, Wij hebben hem (Fir\'aun) al Onze Tekenen laten zien, maar hij ontkende en weigerde.'),
(2405, 20, 57, '(Fir\'aun zei:) "Ben jij naar ons toegekomen om ons uit ons land te verdrijven met jouw tovenarij, O Môesa?'),
(2406, 20, 58, 'Dan zullen wij zeker met eenzelfde soort tovenarij voor jou komen, maak maar een afspraak tussen ons en jou, die wij niet verbreken en jij ook niet, op een plaats op gelijke afstand."'),
(2407, 20, 59, 'Hij (Môesa) zei: "Jullie afspraak zal op de feestdag zijn en laat de mensen in de ochtend verzameld worden."'),
(2408, 20, 60, 'Toen ging Fir\'aun weg om zijn plannen te beramen en daarna kwam hij terug.'),
(2409, 20, 61, 'Môesa zei tot hen: "Wee jullie, verzint geen leugens over Allah, want dan zal Hij jullie door een bestraffing vernietigen. En waarlijk, wie leugens verzint is degene die verlist."'),
(2410, 20, 62, 'Zij (de Lieden van Fir\'aun) twistten met elkaar over hun zaak en hielden een geheim overleg.'),
(2411, 20, 63, 'Zij zeiden: "Waarlijk, dit zijn zeker twee tovenaars, die jullie met hun tovenarij uit jullie land willen verdrijven en zij gaan jullie navolgenswaardige levenswijze doen verdwijnen.'),
(2412, 20, 64, 'Stelt daaarom jullie plan op en komt dan in rijen. En waarlijk, hij die vandaag Wint zal de overhand krijgen."'),
(2413, 20, 65, 'Zij zeiden: "O Môesa, of jij werpt, of zijn wij het die het eerst werpen?"'),
(2414, 20, 66, 'Hij zei: "Werpt maar." En toen scheen het hem toe dat hun touwen en hun staven zich door hun tovenarij voortbewogen.'),
(2415, 20, 67, 'Toen voelde Môesa vrees in zich opkomen.'),
(2416, 20, 68, 'Wij (Allah) zeiden: "Vrees niet! Voorwaar, jij zult de overhand krijgen.'),
(2417, 20, 69, 'Werp neer wat in jouw rechterhand is, het zal wat zij wrochtten verslinden. Voorwaar, wat zij wrochtten is slechts een list van een tovenaar. En de tovenaar wint niet, hoe hij het ook doet."'),
(2418, 20, 70, 'Toen werden de tovenaars op hun knieën neergeworpen, zij zeiden: "Wij geloven in de Heer van Hârôen en Môesa,"'),
(2419, 20, 71, 'Hij (Fir\'aun) zei: "Geloven jullie hem voordat ik jullie toestemming gegeven heb? Voorwaar, hij is zeker jullie meester die jullie tovenarij onderwezen heeft. Ik zal zeker jullie handen en voeten aan tegenovergestelde kanten afhakken en ik zal jullie zeker kruisigen aan de stammen van palmbomen! En jullie zullen zeker weten wie van ons strenger en blijvender is wat betreft bestraffing."'),
(2420, 20, 72, 'Zij zeiden: "Wij zullen jou nooit verkiezen boven de duidelijke Tekenen die tot ons gekomen zijn en Degene Die ons geschapen heeft. Besluit daarom wat je besluit is: voorwaar, wat jij ook besluit, dat is van het wereldse leven.'),
(2421, 20, 73, 'Voorwaar, wij geloven in onze Heer, opdat Hij onze zonden zal vergeven en (ook) de tovenarij, waartoe jij ons gedwongen hebt. En Allah is beter (in het belonen) en blijvender (in de bestraffinjg)."'),
(2422, 20, 74, 'Voorwaar, wie als een zondaar war zijn Heer komt, voor hem is zeker de Hel, waarin hij noch leeft noch sterft.'),
(2423, 20, 75, 'En zij die als gelovigen naar Hem komen en waarlijk goede daden verrichten: zij zijn degenen die de hoge rangen krijgen.'),
(2424, 20, 76, 'De Tuinen van \'Adn ( het Paradijs), waar onder door de rivieren stromen, zij zijn eeuwig levenden darin. Dat is de beloning voor wie zich reinigt.'),
(2425, 20, 77, 'En voorzeker, Wij hebben aan Môesa geopenbaard: "Reis in de nacht met Mijn dienaren en sla (met je staf) voor hen een droge weg door de zee. Wees niet bang om bereikt te worden en wees niet angstig."'),
(2426, 20, 78, 'Toen volgde Fir\'aun hen met zijn legers, daarop werden zij bedekt door de vloedgolf die over hen kwam.'),
(2427, 20, 79, 'En Fir\'aun deed zijn volk dwalen en leidde hen niet.'),
(2428, 20, 80, 'O Kinderen van Israël, Wij hebben jullie waarlijk van jullie vijand gered en Wij sloten een verbond met jullie bij de rechterzijde van (de berg) Thôer en Wij deden Manna en kwartels voor jullie neerdalen.'),
(2429, 20, 81, 'Eet van de gome dingen, waarmee Wij jullie hebben voorzien en overdrijft niet zodat Mijn woede jullie niet treft. En wie door Mijn woede getroffen wordt zal waarlijk ten onder gaan.'),
(2430, 20, 82, 'En voorwaar, Ik ben zeker een Vergevensgezinde voor degene die berouw toonde en geloofde en goede daden verrichtte en vervolgens Leiding volgde.'),
(2431, 20, 83, '(Allah zei:) "En wat heeft jou je van jouw volk doen weghaasten, O Môesa?"'),
(2432, 20, 84, 'Hij zei: "Zij volgen in mijn voetstappen en ik haastte mij naar U, O mijn Heer, om door U behaagd te worden."'),
(2433, 20, 85, 'Hij (Allah) zei: "Wij hebben jou op de proef gesteld nadat jij (vertrok) en de Sâmirî bracht hen tot dwaling."'),
(2434, 20, 86, 'Toen keerde Môesa terug naar zijn volk, woedend en vol spijt, en zei. "O mijn volk, heeft jullie Heer jullie geen goede belofte gedaan? Duurde (de vervulling van) de belofte te lang of wilden jullie dat de woede van jullie Heer jullie trof, zodat jullie mijn afspraak (met jullie) afzegden?"'),
(2435, 20, 87, 'Zij zeiden: "Wij hebben de afspraak met jou niet uit vrije wil afgezegd, maar wij werden belast met ladingen sieraden van het volk. Toen gooiden wij die (in het vuur), net zoals de Sâmirî (ze in het vuur) wierp.'),
(2436, 20, 88, 'Toen haalde de Sâmirî voor hen een beeld van een kalf (uit het vuur), met geloei. En zij zeiden: "Dit is jullie god en de god van Môesa, maar hij vergat (hem)."'),
(2437, 20, 89, 'Zien zij niet dat het geen woord aan hen terug gaf en het ook geen kracht had om voor hen schade (te voorkomen) of te baten?'),
(2438, 20, 90, 'En voorzeker, Hârôen had tevoren gezegd: "O mijn volk, jullie worden daarmee op de proef gesteld. En voorwaar, jullie Heer is de Barmhartige. Volgt mij daarom en gehoorzaamt mijn bevel."'),
(2439, 20, 91, 'Zij (de aanbidders van het kalf) zeiden: "Wij zullen nooit ophouden hein (te aanbidden), totdat Môesa tot ons terugkeert."'),
(2440, 20, 92, 'Hij (Môesa) zei: "O Hârôen, wat hield jou tegen toen jij ben zag dwalen?'),
(2441, 20, 93, 'Om mij te volgen? Ben jij dan ongehoor-zaam aan mijn bevel geweest?"'),
(2442, 20, 94, 'Hij (Hârôen) zei: "Zoon van mijn moeder, grijp mij niet bij mijn baard of bij mijn hoofd. Ik was bang dat jij zou zeggen: \'Jij hebt de Kinderen van Israël verdeeld en jij hebt mijn woorden niet in acht genomen."'),
(2443, 20, 95, 'Hij (Môesa) zei: "Wat was jouw geval, O sâmirî?"'),
(2444, 20, 96, 'Hij (de Sâmiri) zei: "Ik doorzag wat zij niet doorzagen en ik nam een handvol uit het spoor van de gezant en ik strooide het (over het kalt). En zo verblijdde ik mijzelf"'),
(2445, 20, 97, 'Hij (Môesa) zei: "Ga dan maar heen. Voorwaar, voor jou is er in het leven, dat jij slechts kan zeggen: \'Raak mij niet aan, \'en voorwaar, voor jou is er een afspraak (in het Hiernamaals), die nooit afgezegd kan worden. En kijk naar jouw god die jij voortdurend aan het aanbidden was: wij zullen hem zeker verbranden. En vervolgens werpen wij hem zeker als as in de zee!"'),
(2446, 20, 98, 'Voorwaar, jullie god is slechts Allah, er is geen god dan Hij. Hij omvat alle zaken met (Zijn) kennis.'),
(2447, 20, 99, 'Zo verhalen Wij jou van de geschiedenissen van wat voorafgegaan is. En waarlijk, Wij hebben jou van Onze Zijde de Vermaning gegeven.'),
(2448, 20, 100, 'Hij, die zich ervan afwendt: voorwaar, hij zal op de Dag der Opstanding een zonde dragen.'),
(2449, 20, 101, 'Zij zullen daarin eeuwig levenden zijn. Slecht zal de last voor hen zijn op de Dag der Opstanding!'),
(2450, 20, 102, 'Op de Dag, waarop op de bazuin wordt geblazen en Wij de zondaren verzamelen, met hun afschrikwekkende gezichten.'),
(2451, 20, 103, 'Fluisterend onder elkaar zeggen ze: "Jullie hebben slechts tien dagen op aarde verbleven."'),
(2452, 20, 104, 'Wij weten het beste wat zij zeggen wanneer degene die het meest voorbeeldig de Weg volgde onder hen zegt. "Jullie hebben er slechts één dag verbleven."'),
(2453, 20, 105, 'En zij vragen jou over de bergen. Zeg dan: "Mijn Heer zal ze als pulver doen uiteenvallen.'),
(2454, 20, 106, 'En Hij laat hen achter als een kale vlakte.'),
(2455, 20, 107, 'Waarop je geen lage en hoge (plaatsen) ziet."'),
(2456, 20, 108, 'Op die dag zullen zij de Oproeper volgen; er is geen (mogelijkeid) om aan hem te ontsnappen. En stemmen zullen zacht klinken tegenover de Barmhartige, zodat je niets dan gefluister hoort.'),
(2457, 20, 109, 'Op de Dag is bemiddeling niet van nut, behalve voor hem, aan wie de Barmhartige toestemming geeft en wiens woorden Hem welgevallen.'),
(2458, 20, 110, 'Hij weet wat vôôr hen is en wat achter hen is, en zij kunnen Hem met kennis niet omvatten.'),
(2459, 20, 111, 'En alle gezichten zullen eerbiedig neerbuigen voor de Levende, de Zelfstandige. En waarlijk, hij die onrecht pleegde zal teleurgesteld worden.'),
(2460, 20, 112, 'En hij die goede daden verrichtte en een gelovige was, hoeft niet bang te zijn voor onrecht of verlies.'),
(2461, 20, 113, 'En zo hebben Wij hem neergezonden als een Arabische Koran en Wij hebben daarin waarschuwingen uiteengezet. Hopelijk zullen zij (Allah) vrezen of zal hij (de Koran) hen lering brengen.'),
(2462, 20, 114, 'En Verheven is Allah, de Ware Koning. En haast je niet met de Koran (O Moehammad), voordat zijn openbaring an jou voltooid is. En zeg: "\'Mijn Heer, vermeerder voor mijn kennis."'),
(2463, 20, 115, 'En Wij hadden vroeger met Adam een verbond gesloten, maar hij vergat het. Wij vonden bij hem geen vastberadenheid.'),
(2464, 20, 116, 'En toen Wij tegen de Engelen Zeiden: "Werpt jullie neer voor Adam," wierpen zij zich neer, behalvc lblîs, hij weigerde.'),
(2465, 20, 117, 'Daarop zeiden Wij: "O Adam, voorwaar, dit is zeker een vijand van jou en jouw vrouw. Laat hem daarom jullie niet uit het Paradijs verdrijven, want dan zal jij zeker ongelukkig worden.'),
(2466, 20, 118, 'Voorwaar, daarin is voor jou geen honger en jij bent er niet naakt.'),
(2467, 20, 119, 'Jij hebt ef zeker geen dorst en jij wordt er niet blootgesteld aan hitte."'),
(2468, 20, 120, 'Maar de Satan fluisterde hem in, en zei: "O Adam, zal ik jou de eeuwige boom wijzen en een koninkrij\'k dat niet vergaat?"'),
(2469, 20, 121, 'Vervolgens aten zij ervan, zodat hun schaamte zichtbaar werd en zij begonnen zich te bedekken uwt bladeren van het Paradijs; en zo was Adam zijn Hecr ongehoorzaam en dwaalde hij.'),
(2470, 20, 122, 'Daarna koos zijn Heer hem uit en Hij aanvaardde zün berouw en leidde (hem).'),
(2471, 20, 123, 'Hij (Allah) zei: "Daalt hieruit af, tezamen, onder jullie zal de één de vijand zijn van de ander. Maar als van Mij Leiding tot jullie komt: wie Mijn Leiding volgt dwaalt niet en is niet ongelukkig.'),
(2472, 20, 124, 'En hij die zich afwendt van Mijn Vemaning: voorwaar, er zal dan voor hem een benauwd leven zijn. En Wij zullen hen verzamelen op de Dag der Opstanding, in blinde toestand.'),
(2473, 20, 125, 'Hij zal zeggen: "Mijn Heer, waarom verzamelt U mij in blinde toestand, terwijl ik vroeger in staat was om te zien?"'),
(2474, 20, 126, 'Hij (Allah) zai zeggen: "Zo is het. Onze Tekkenen zijn tot jou gekomen, maar jij verpt ze, en daaroin wordt jij vandaag vergeten."'),
(2475, 20, 127, 'En zo vergeiden Wij wie overschrijdt en niet in de Tekenen van zijn Heer gelooft. En de bestraffing in het Hiernamaals is zeker strenger en blijvender.'),
(2476, 20, 128, 'Is er voor hen den geen Leiding in hoeveel genenties vóór hen Wij vernietigd hebben, en over wiens (vernietigde) woningen zij rondwandeled? Voorwaar, daarin zijn zeker Tekenen voor de bezittem van verstand.'),
(2477, 20, 129, 'En als er geen Wood van jouw Heer voorafgegan was, en het tijdstip niet vastgesteld was, dan zou (de bestraffing) ben zeker treffen.'),
(2478, 20, 130, 'Wees daarom geduldig (O Moehammad) met wat zij zeggen en prijs de lof van jouw Heer vóór de zonsopgang en vóór haar ondergang en tijdens de nacht en prijs (Hem) op de uiteinden van de dag. Hopelijk ben jij tevreden.'),
(2479, 20, 131, 'En kijk niet met uitpuilende ogen naar wat Wij sommigen van hen aan genietingen hebben geschonken, (het is slechts) de versiering van het wereldse leven, om hen ermee op de proef te stellen. Ein de voorziening van jouw Heer is beter en blijvender.'),
(2480, 20, 132, 'En beveel jouw familie de shalât te verrichten en volhard daarin. Wij wagen van jou geen voorziening (te geven), Wij geven jou voorziening. Het (goede) einde is voor degene die (Allah) vreest.'),
(2481, 20, 133, 'En zij (de ongelovigen) zeggen: "Waarom is hij niet met een Teken van Zijn Heer tot ons gekomen?" Is er dan geen duidelijk Teken tot hen gekomen in de voorafgaande Geschriften?'),
(2482, 20, 134, 'En als Wij hen vernietigd hadden door een bestraffing voor hem (de Koran), dan zouden zij zeker zeggen: "Onze Heer, had U maar een Boodschapper naar ons gestuurd, dan zouden wij Uw Tekenen hebben gevolgd, voordat wij vemederd en te schande gemaakt werden!"\''),
(2483, 20, 135, 'Zeg: "Allen wachten af, wacht dus af. Jullie zullen te weten komen wie de volgers van het rechte Pad zijn en wie Leiding volgden."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'Dichter bij voor de mensen is hun afrekening gekomen, terwijl zij zich in onachtzaamheid afwenden.'),
(2485, 21, 2, 'En er komt geen nieuwe Vermaning van hun Heer tot hen, of zij luisteren ernaar terwijl zij er de spot mee drijven.'),
(2486, 21, 3, 'Achteloos zijn hun harken. En degenen die onrechtvaardig zijn verbergen (hun onrecht) in heimelijk overleg (en zeggen:) "Deze (Boodschapper) is niet anders dan een mens zoals jullie." Nemen jullie dan tovenarij aan, terwijl jullie het doorzien?'),
(2487, 21, 4, 'Hij (Moehammad) zei: "Mijn Heer weet wat er in de hemelen en op de aarde gesproken wordt, en Hij is de Alhorende, de Alwetende."'),
(2488, 21, 5, 'Zij (de ongelovigen) zeggen zelfs: "Het verwardste gedroom is (deze Koran), hij verzon hem zelfs, hij is zelfs een dichter! Laat hem dan een Teken tot ons brengen zoals aan de voorafgaanden gezonden werd!"\''),
(2489, 21, 6, 'Zij geloofden niet, degenen vóór hen uit de steden die Wü vernietigden: zullen zij dan geloven?'),
(2490, 21, 7, 'En Wij hebben (niemnd) vóór jou gezonden of zij waren slechts mannen aan wie Wij openbaarden. Vraagt dan de bezitters van kennis, indien jullie het Riet weten.'),
(2491, 21, 8, 'En Wij schiepen hen niet met lichamen die geen voedsel aten en zij leefden niet eeuwig.'),
(2492, 21, 9, 'Dawop vervulden Wij voor ben de belofte en Wij redden ben en wie Wij wensten. En Wij vemietigden de buitensporigen.'),
(2493, 21, 10, 'Voorzeker, Wij hebben aan jullie een Boek doen neerdalen met daarin jullie eer. Begrijpom jullie het niet?'),
(2494, 21, 11, 'En hoevelen uit de steden vernietigden Wij niet die onrecht pleegden, na wie Wij een ander volk deden opstaan?'),
(2495, 21, 12, 'En wanneef zij dan Onze besüafflng aan voelden komen, dan (probeerden) zij ervan weg te rennen.'),
(2496, 21, 13, 'Rent niet weg, maar keert terug naar wat jullie van de goede dingen van het leven gegeven was en jullie huizen, opdat jullie ondervraagd zullen worden.'),
(2497, 21, 14, 'Zij zeiden: "Wee ons: voorwaar, wij waren onrechtvaardigen!"'),
(2498, 21, 15, 'En die weeklacht van hen hield niet op voordat Wij hen als neergemaaid, uitgestorven maakten.'),
(2499, 21, 16, 'En Wij schiepen de hemelen en de aarde en wat tussen hen is niet als vermaak.'),
(2500, 21, 17, 'Indien Wij gewild zouden hebben het als vermaak te nemen, dan zouden Wij het van Onze Zijde gernomen hebben, als Wij (zoiets al) gedaan zouden hebben.'),
(2501, 21, 18, 'Welnee, Wij werpen de Waarheid tegen het valse, waarop het vernietigd wordt en dan verdwijnt het. En wee jullie voor wat jullie toeschrijven (aan Allah).'),
(2502, 21, 19, 'Aan Hem behoort wat in de hemelen en op de aarde is. En degenen die niet Hem zijn (de Engelen), zijn niet te hoogmoedig om Hem te dienen en zij worden er niet moe van.'),
(2503, 21, 20, 'Zij prijzen Zijn Glorie tijdens de nacht en de dag en versagen niet.'),
(2504, 21, 21, 'Of hebben zij (de ongelovigen) goden uit de aarde genomen die (de doden kunnen) opwekken?'),
(2505, 21, 22, 'Als er andere goden dan Allah in zouden zijn, dan zou zij (de hemelen en de aarde) zeker vergaan: maar Heilig is Allah, Heer van de Troon, boven wat zij Hem toeschrijven!'),
(2506, 21, 23, 'Hij kan niet over Zijn handelen ondervraagd worden, terwijt zij wel ondervraagd worden.'),
(2507, 21, 24, 'Of hebben zij naast Hem goden genomen? Zeg (O Moehammad): "Brengt jullie bewijs, dit is de Varmaning van degenen met mij en vóór mij." Maar de meesten van hen kennen de Waarheid niet, daarom keren zij zich af.'),
(2508, 21, 25, 'En Wij stuurden niet één van de Boodschappers vóór jou, of Wij openbaarden aan hem dat er geen andere god dan Ik is, aanbidt Mij daarom.'),
(2509, 21, 26, 'En zij zeiden: "De Bamhartige heeft Zich een zoon genomen." Heilig is Hij! Zij (de Engelen) zijn slechts geëerde dienaren!'),
(2510, 21, 27, 'Zij nemen het wooird niet vóór Hem en zij handelen op Zijn bevel.'),
(2511, 21, 28, 'Hij weet wat vóór hen is en wat achter hen is en zij zijn niet van voorspraak, behalve voor wie Hem welgevallig zijn. En uit ontzag voor Hem vrezen zij (Allah).'),
(2512, 21, 29, 'En wie van hen zegt: "Voorwaar, ik ben een god naast Hem," die vergelden Wij daarop niet de Het, zo vergelden Wij de onrechtplegers.'),
(2513, 21, 30, 'Weten degenen die ongelovig zijn niet dat de hemelen en de aarde als een gemengde massa waren en dat Wij hen beide daarop splitsten en dat Wij alle levende dingen uit het water maakten? Geloven zij niet?'),
(2514, 21, 31, 'En Wij maakten stevige bergen op de aarde, zodat zij niet met hen schudt. En Wij maakten daarin brede passen als wegen. Hopelijk zullen zij Leiding volgen.'),
(2515, 21, 32, 'En Wij maakten de hemel als een beschermende kap, maar zij wendden zich af van zijn Tekenen.'),
(2516, 21, 33, 'En Hij is Degene Die de nacht en de dag geschapen heeft, en de zon en de maan, allen bewegen in een baan.'),
(2517, 21, 34, 'En Wij hebben geen mens vóór jou onsterfelijkheid gegeven. Als jij zou sterven; zouden zij dan eeuwig leven?'),
(2518, 21, 35, 'Iedere ziel zal de dood ervaren en Wij stellen jullie op de proef met het slechte en het goede, als een beproeving, en tot Ons worden jullie teruggkeerd.'),
(2519, 21, 36, 'En als degenen die ongelovig zijn jou zien, zullen zij jou slechts spottend behandelen: "Is dit degene die jullie goden verwijten maakt?" En in het gedenken van de Barmhartige geloven zij niet.'),
(2520, 21, 37, 'De mens is haastig (van aard) geschapen. Spoedig zal ik jullie mijn Tekenen laten zien, vraagt daarom geen verhaasting (ervan).'),
(2521, 21, 38, 'Zij zeggen: "Wanneer vindt (de vervulling van) deze belofte plaats als jullie waarachtigen zijn?"'),
(2522, 21, 39, 'Als degenen die ongelovig zijn maar het moment gekend hadden waarop zij de Hel niet van hun gezichten kunnen afhouden, en niet van hun ruggen. En zij worden niet geholpen!'),
(2523, 21, 40, 'Integendeel, het zal hen onverwachts overvallen en hen verbijsteren. Daarom zijn zij niet in staat het tegen te houden. En hun (bestraffing) zal niet uitgesteld worden.'),
(2524, 21, 41, 'En voorzeker, er word vóór jou al de spot met de Boodschappers gedreven maar degenen die hen belachelijk maakten, werden omsingeld door hetgeen waarmee zij de spot plachten te drijven.'),
(2525, 21, 42, 'Zeg: "Wie kan jullie \'s nacht en overdag veiligheid bieden tegen de Barmhartige?" Nee, zij keren zich af van de Vermaning van kun Heer.'),
(2526, 21, 43, 'Of hebben zij goden, die hen verdedigen tegen Ons? Zij zijn niet in staat zichzelf te helpen en zij worden niet tegen Ons bijgestaan.'),
(2527, 21, 44, 'Maar Wij hebben hun en hun vaderen genietingen geschonken, totdat de leeftijden voor hen veriengd werden. Zien zij dan niet dat Wij het land (onder hun macht) doen verminderen vanaf haar buitengronzen? Zijn zij dan de overwinnaars?'),
(2528, 21, 45, 'Zeg: "Voorwaar, ik waarschuw jullie slechts met de Openbaring." Maar de doven luisteren niet naar de oproep, zelfs (niet) wanneer zij gewaarschuwd worden.'),
(2529, 21, 46, 'En als ook maar een adem van de bestraffing van jouw Heer ben trek zeggen zij zeker: "Wee ons! Voorwaar, wij waren onrechtvaardigen!"'),
(2530, 21, 47, 'En Wij zullen betrouwbare weegschalen opstellen op de Dag der Opstanding, zodat geen ziet iets van onrecht aangedaan wordt. En al gaat het om het gewicht van een mosterdzaadje: Wij zullen het naar voren brengen. En Wij zijn voldoende als Berekenaars.'),
(2531, 21, 48, 'En voorzeker hebben Wij aan Môesa en Hârôen een Foerqân gegeven, als een verheidering en een Vemaning voor de Moettaqôen.'),
(2532, 21, 49, 'Degenen die voor kun Heer vrezen in het verborgene. En zij zijn bang voor het Uur.'),
(2533, 21, 50, 'En dit is een gezegende Vermaning, die Wij neerzonden. Zullen jullie haar dan verwerpen?'),
(2534, 21, 51, 'En voorzeker gaven Wij vroeger Ibrâhîm zijn rechtgeleidheid en Wij waren bekend met hem.'),
(2535, 21, 52, '(Gedenkt) toen hij tegen zijn vader en zijn voik zei: "Wat zijn dat voor beelden, die jullie aanbidden?"'),
(2536, 21, 53, 'Zij zeiden: "Wij vonden dat onze vaderen hen aanbaden."'),
(2537, 21, 54, 'Hij zei: "Voorzeker, jullie en jullie vaderen verkeren in duidelijke dwaling."'),
(2538, 21, 55, 'Zij zeiden: "Ben jij naar ons gekomen met de Waarheid, of behoor jij tot hen die spotten?"'),
(2539, 21, 56, 'Hij zei "Integendeel, jullie Heer is de Heer van de hemelen en de aarde, die Hij geschapen heeft. En ik behoor tot degenen die daarvan getuigen.'),
(2540, 21, 57, 'Bij Allah, ik zal zeker een plan beramen tegen jullie afgoden, nadat jullie weggaan, jullie ruggen toekerend."'),
(2541, 21, 58, 'Toen sloeg hij hen allemaal in stukken, behalve de grootste van hen, misschien zouden zij tot bem terugkeren.'),
(2542, 21, 59, 'Zij zeiden: "Wie heeft dat met onze afgoden gedaan? Voorwaar, hij behoort tot de onrechtplegers!"'),
(2543, 21, 60, 'Zij zeiden: "Wij hebben een jongeman over hen boren spreken, Ibrâhîm wordt hij genoemd."'),
(2544, 21, 61, 'Zij zeiden: "Brengt hem dan onder de ogen van de mensen, hopelijk zullen zij getuigen."'),
(2545, 21, 62, 'Zij zeiden: "Heb jij dit met onze goden gedaan, O Ibrâhîm?"'),
(2546, 21, 63, 'Hij zei: "Nee, de grootste van hen heeft het gedaan. Dus ondervraagt hen maar, als zij kunnen spreken."'),
(2547, 21, 64, 'Toen kwamen zij tot zichzelf, en zeiden (tegen elkaar): "Voorwaar, jullie zijn zelf de onrechtplegers."'),
(2548, 21, 65, 'Toen bogen zij hun hoofden (en zeiden:) "Voorzeker, jij weet dat zij niet kunnen spreken."'),
(2549, 21, 66, 'Hij (Ibrâhîm) zei: "Aanbidden jullie dan (een god) naast Allah, die jullie in niets baat en niet schaadt?'),
(2550, 21, 67, 'Foei jullie een wat jullie naast Allah aanbidden. Begrijpen jullie dan niet?"'),
(2551, 21, 68, 'Zij (de ongelovigen) zeiden: "Verbrandt hem en helpt jullie goden, als jullie (iets willen) doen."'),
(2552, 21, 69, 'Wij (Allah) zeiden: "O vuur, wees koud en veilig voor Ibrâhîm.'),
(2553, 21, 70, 'En zij wilden een list tegen hem beramen, maar Wij maakten hen tot de grootste verliezers.'),
(2554, 21, 71, 'En Wij redden hem en Lôeth naar het land dat Wij gezegend hebben voor de wereldbewoners.'),
(2555, 21, 72, 'En Wij schonken hem Ishâq en Ya\'qôeb als een geschenk. En Wij maakten ieder van hen tot oprechten.'),
(2556, 21, 73, 'En Wij maakten hen tot leiders, die leiding gaven volgens Ons bevel. En Wij openbaarden aan hen goede daden te verrichten en de shalât te onderhouden en de zakât te geven. En zij waren aanbidders van Ons.'),
(2557, 21, 74, 'En aan Lôeth schonken Wij wijsheid en kennis en Wij redden hem uit de stad waarvan (de bevolking) vuiligheid placht te bedrijven. Voorwaar, zij waren een slecht volk, zwaar zondigen.'),
(2558, 21, 75, 'En Wij deden hem in Onze Barmhartigheid binnengaan: voorwaar, hij behoorde tot de oprechten.'),
(2559, 21, 76, 'En (gedenkt) Nôeh, toen bij Ons vroeger aanriep en Wij hem daarop verhoorden: Wij redden hem en zijn familie van een geweldige ramp.'),
(2560, 21, 77, 'En Wij hielpen hem tegen het volk dat Onze Tekorten loochende. Voorwaar, zij waren een slecht volk. Toen deden Wij hen allen verdrinken.'),
(2561, 21, 78, 'En (gedenkt) Dâwôcd en Soelaimân toen zij een oordeel gaven over het akkerland, waarop de schapen van het volk grazend rondgelopen hadden. En Wij waren getuigen van hun oordeel."\''),
(2562, 21, 79, 'En Wij deden Soelaimân (de zaak) begrijpen. En aan ieder van hen gaven Wij wijsheid en kennis. En Wij maakten Dâwôcd met de bergen en de vogels dienstbaar om (Allah\'s) Glorie te prijzen, Eii Wij waren het Die dat deden.'),
(2563, 21, 80, 'En Wij leerden hem kleding (maliënkolders) te maken om jullie te beschermen in jullie oorlog. Zullen jullie dan dankbaren zijn?'),
(2564, 21, 81, 'En aait Soelaimin (onderwierpen Wij) de stormachtige wind, die met Zijn verlof naar het land bewoog dat Wij gezegend hadden. En Wij zijn Alwetend over alle zaken.'),
(2565, 21, 82, 'En van de Satans doken er voor hem en zij verrichtten daarnaast ander werk. En Wij waren Wakers over hen.'),
(2566, 21, 83, 'En (gedenkt) Ayyôeb toen hij zijn Heer aanriep (en zei:) "Voorwaar, tegenspoed heeft mij getroffen en U bent de Bamhartigste der Barmhartigen."'),
(2567, 21, 84, 'Toen verhoorden Wij hem en hieven de tegenspoed voor hem op. En Wij gaven hem zijn familie en het daaraan gelijke aan hem (erbij), als een Barmhartigheid van Ons en als een vermaning voor de aanbidders.'),
(2568, 21, 85, 'En (gedenkt) Ismâ\'îl, Idrîs en Dzôelkifl: allen behoorden tot de geduldigen.'),
(2569, 21, 86, 'En Wij deden ben in Onze Barmhartigheid binnengaan. Voorwaar, zij behoorden tot de oprechten.'),
(2570, 21, 87, 'En (gedenkt) Dzôennôen toen hij kwaad wegging en meende dat Wij geen macht over hem hadden. Toen riep hij uit in de duisternissen: "Er is geen god dan U, Heilig bent U: voorwaar, ik behoorde tot de onrechtvaardigen."'),
(2571, 21, 88, 'Toen verhoorden Wij hem en Wij redden hem iuit de nood. En zo redden Wij de gelovigen.'),
(2572, 21, 89, 'En (gedenkt) Zakariyyâ, toen hij zijn Heer aanriep: "Mijn Heer, laat mij niet alleen (zonder nageslacht) en U bent de beste van de erfgenamen."'),
(2573, 21, 90, 'Toen verhoorden Wij hem en Wij schonken hem Yahya en Wij maakten zijn vrouw geschikt (om te baren). Voorwaar, zij wedijverden in goede daden en riepen Ons aan, verlangend (naar Onze Genade) en vol ontzag (voor Onze bestraffing). En zij waren nederig tegenover Ons.'),
(2574, 21, 91, 'En zij (Maryam) bewaarde haar eerbaarheid, toen bliezen Wij van Onze Geest in haar. En Wij maakten haar en haar zoon een Teken voor de werelden.'),
(2575, 21, 92, 'Voorwar, deze godsdienst (Islam) is jullie godsdienst, de enigste. Ik ben jullie Heer, aanbidt Mij daarom.'),
(2576, 21, 93, 'Maar zij raakten onderling verdeeld over hun zaak (van eenheid). Allen zulleit tot Ons terugkeren.'),
(2577, 21, 94, 'En wie goede daden verricht en een gelovige is: zijn streven zal niet ontkend worden. Voorwaar, Wij zullen het voor hem opschrijven.'),
(2578, 21, 95, 'En het is onmogelijk voor (de bewoners van) een stad die Wij vernietigd hebben dat zij terugkeren (cm zich te beteren).'),
(2579, 21, 96, 'Totdat voor Ya\'djôedj en Ma\'djôedj (de muur) geopend wordt en zij van iedere hoogte komen aansnellen.'),
(2580, 21, 97, 'En de ware belofte nabij komt. Dan zullen de blikken van degenen die ongelovig zijn verstarren: "Wee ons, wij verkeerden in onachtzaamheid daaromtrent, wij waren zelfs onrechtvaardigen!"'),
(2581, 21, 98, 'Voorwaar, jullie en wat jullie naast Allah aanbidden zal brandstof zijn voor de Hel, jullie zullen er binnengaan.'),
(2582, 21, 99, 'En als diegenen goden waren, dan zouden zij er niet binnengaan. En allen zullen daarin eeuwig levenden zijn.'),
(2583, 21, 100, 'Zij zullen het daarin uitgillen en zij horen daarin niets.'),
(2584, 21, 101, 'Voorwaar, degenen aan wie het goede van Ons voorafgegaan is: zij zijn degenen die daar ver van gehouden worden.'),
(2585, 21, 102, 'Zij zullen er geen geluid van horen. En in wat hun zielen verlangen zullen zij eeuwig levenden zijn.'),
(2586, 21, 103, 'De grote verschrikking bedroeft hen niet en de Engelen zullen hun ontvangen (en zeggen:) "Dit is jullie dag, die jullie beloofd was.'),
(2587, 21, 104, '(Gedenk) de Dag waarop Wij de hemelen oprollen, zoals het oprollen van het perkament om op te schrijven: net zoals Wij de eerste schepping begonnen zullen Wij haar herhalen, als een belofte die Wij op Ons namen. Voorwaar, Wij zullen het doen.'),
(2588, 21, 105, 'En voorzeker hebben Wij in de Zabôer geschreven, na de vermelding (in de Lauhoelmahfôezh), dat de aarde geërfd zal worden door Mijn rechtschapen dienaren.'),
(2589, 21, 106, 'Voorwaar, in deze (Koran) is zeker een Boodschap voor een volk van aanbidders.'),
(2590, 21, 107, 'En Wij hebben jou (O Moehammad) slechts gezonden als een barmhartigheid voor de werelden.'),
(2591, 21, 108, 'Zeg: "Voorwaar, wat aan mij geopenbaard is, is dat jullie god één God is, zullen jullie je dan aan Hem onderwerpen?"'),
(2592, 21, 109, 'Maar als zij zich afwenden, zeg dan: "Ik heb jullie opgeroepen tot hetzelfde en ik weet niet of wat jullie aangezegd is dichtbij of ver weg is.'),
(2593, 21, 110, 'Voorwaar, Hij weet aat openlijk besproken wordt en Hij weet wat jullie verbergen.'),
(2594, 21, 111, 'En ik wed niet of dit een beproeving voor jullie is en een tijdelijk genot."'),
(2595, 21, 112, 'Zeg: "Mijn Heer, oordeel naar de Waarheid. En onze Heer, de Barmhartige, is Degene wiens hulp gevraagd wordt tegen wat jullie (Hem) toeschrijven."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'O mensen, vreest jullie Heer. Voorwaar, de beving van het Uur zal een geweldige zaak zijn.'),
(2597, 22, 2, 'Op die Dag zullen jullie het zien: ieder zogende vrouw zal haar zuigeling veronachtzamen en iedere zwangere vrouw zal haair vrucht laten vallen. En jij zult de mensen als dronken zien, hoewel zij niet dronken zijn maar de bestraffing van Allah is hard'),
(2598, 22, 3, 'Er zijn er onder de mensen, die over Allah redetwisten, zonder kennis, en zij volgen elke iedere opstandige Satan.'),
(2599, 22, 4, 'Het is hem (de Satan) voorgeschreven dat wie hem volgt, door hem tot dwaling gebracht wordt, en hij leidt hem tot de bestraffing van de Sa\'îr (de Hel).'),
(2600, 22, 5, 'O mensen, als jullie in twijfel verkeren over de Opstanding: voorwaar, Wij schiepen jullie uit aarde, vervolgens uit sperma, daarop uiteen bloedklonter, toen uit een vleesklomp, gevormd en niet gevormd; om aan jullie (de schepping) duidelijk te maken. En Wij laten in de baarmoeder rusten wat Wij willen, tot een bepaald tijdstip, waarna Wij voor jullie een kind (de baarmoeder) doen verlaten Daarop (is er een tijd) om jullie je leefkijd van volledige kracht te doen bereiken. En er zijn er onder jullie die weggenomen worden en er zijn er onder jullie die teruggevoerd worden tot de meest vervallen der leeftijden, zodat hij niets meer weet, na kennis (gehad te hebben). En je ziet de aarde verdord en wanneer Wij dan het water op haar doen neerdalen, beweegt zij zich en zwelt op en brengt velerlei schitterende planten voort.'),
(2601, 22, 6, 'Dat is omdat Allah de Waarheid is en omdat Hij de doden tot leven brengt en omdat Hij Almachtig over alle zaken is.'),
(2602, 22, 7, 'En omdat het Uur komt, daaraan is geen twijfel, en omdat Allah degenen die er in de graven zijn zal opwekken.'),
(2603, 22, 8, 'En er zijn er onder de mensen die over Allah redetwisten, zonder kennis, zonder Leiding en zonder verlichtend Boek.'),
(2604, 22, 9, '(Trots) zijn zijde toekerend om af te doen dwalen van de Weg van Allah: voor hem is er op de wereld vermedering en Wij doen hem op de Dag der Opstanding een brandende bestraffing proeven.'),
(2605, 22, 10, '(Er wordt gezegd:) "Dat is vanwege wat jouw handen vooruitgestuurd hebben." En voorwaar, Allah is niet onrechtvardig voor de dienaren.'),
(2606, 22, 11, 'En er zijn eronder mensen die Allah op de rand aanbidden: als hem iets goeds overkomt is hij daar tevreden mee, maar als hem een beproeving ten deel valt, wendt hij zijn gezicht weer af: hij verliest de wereld en het Hiernamals. Dat is het duidelijke verlies!'),
(2607, 22, 12, 'Hij roept naast Allah aan wat voor hem geen schade tegenhoudt en hem ook van geen nut is: dat is een verpande dwaling!'),
(2608, 22, 13, 'Hij roept iets aan waarvan zijn schade dichter bij zijn nut is. Zeker, een slechte helper, en zeker een slechte metgezel!'),
(2609, 22, 14, 'Voorwaar, Allah doet degenen die geloven en goede daden verrichten de Tuinen (het Paradijs) binnengan, waar de rivieren onder door stromen. Voorwaar, Allah doet wat Hij wenst.'),
(2610, 22, 15, 'En wie denkt dat Allah hem (Moehammad) niet zal helpen in de wereld en het Hiernamaals, laat hem een koord naar de hemel spannen en laat hem (zichzelf) dan afsnijden. Laat hem dan zien of zijn list dat wat woedend maakte doet verdwijnen!'),
(2611, 22, 16, 'En zo hebben Wij hem (de Koran) neergezonden, als duidelijke Verzen. En voorwaar, Allah leidt wie Hij wenst.'),
(2612, 22, 17, 'Voorwaar, degenen die geloven en de Joden en de Sabiërs en de Christenen en de magiërs en degenen die deelgenoten (aan Allah) toekennen: voorwaar, Allah zal tussen hen oordelen op de Dag der Opstanding. Voorwaar, Allah is van alle zaken Getuige.'),
(2613, 22, 18, 'Zie jij dan niet dat alles zich voor Allah neerknielt wat er in de hemel en op de aarde is, en de zon en de maan en de sterren en de bergen en de bomen en de dieren en een groot deel van de mensen? Maar voor velen is de bestraffing verplicht. En wie er door Allah vemederd wordt: voor hem zijn erdan geen eerbewijzers. Voorwaar, Allah doet wat Hij wil.'),
(2614, 22, 19, 'Dit zijn twee tegenstanders die over hun Heer twisten. Voor degenen die niet geloven zulten daarom gewaden uit vuur gesneden worden, van boven hun hoofden zal kokend water worden uitgegoten.'),
(2615, 22, 20, 'Wat zich in hun buiken bevindt zal erdoor smelten en (ook) de huiden.'),
(2616, 22, 21, 'En voor ben zijn er knotsen van ijzer.'),
(2617, 22, 22, 'Telkens wanneer zij van ellende eruit willen gaan, worden zij erin teruggebracht (en wordt gezegd:) "Proeft de brandende bestraffing!"'),
(2618, 22, 23, 'Voorwaar, Allah zal degenen die geloven en goede daden verrichten de Tuinen (het Paradijs) binnen doen gaan, waar de rivieren onder doof stromen. Daarin zullen zij gesierd worden met gouden armbanden en parels en hun kleding er in is van zijde.'),
(2619, 22, 24, 'En zij werden geleid tot het goede van het gesprokene en zij werden geleid op het Pad van de Geprezene.'),
(2620, 22, 25, 'Voorwaar, degenen die niet geloven en van de Weg van Allah en de Masdjid al Harâm (de gewijde Moskee in Mekkah) afhouden, die Wij voof alle mensen gemaakt hebben; zowel de inwoner van daar als degene van buiten: en wie wenst daar het slechte te doen door onrechtpleging, hem zullen Wij een pijnlijke bestraffing doen proeven.'),
(2621, 22, 26, 'En toen Wij voor Ibrâhîm de plaats vastelden van het Huis (zeiden Wij:) "Ken Mij in niets deelgenoten toe en reinig Mijn Huis voor de rondgaanden en de buigenden en de knielenden.'),
(2622, 22, 27, 'En verkondig onder de mensen de Haddj, zij zullen te voet naar jou komen of op magere kamelen, van elke vergelegen plek.'),
(2623, 22, 28, 'Zodat zij getuigen zijn van de voordelen voor hen. En de Naam van Allah uitspreken op de bekende dagen over het slachtvee waarmee Hij hen voorzien heeft. Eet er daarom van en voedt de arme behoeftige.'),
(2624, 22, 29, 'Dan moeten zij (de omgeving van) haar onreinheid reinigen en kun geloften vervullen een rondgang maken om het Aloudc Huis." (al Bait al \'Atîq)'),
(2625, 22, 30, 'Dat is het, en wie de gewijde zaken van Allah eert: dat is beter voor hem bij zijn Heer. Aan jullie is het vee toegestaan, behalve wat is voorgelezen aan jullie. Dus vermijdt de onreinheid van de afgoden en vermijdt het spreken van het valse.'),
(2626, 22, 31, 'Als Hoenafâ tegenover Allah, zonder Hem deelgenoten toe te kennen, want wie aan Allah deelgenoten toekent is als iemand die uit de hemel valt, en dan dom de vogels wordt weggegrist of door de wind wordt geblazen naar een afgelegen plek.'),
(2627, 22, 32, 'Zo is het, en wie de gewijde Tekenen van Allah eer bewijst: voorwaar, dat is door het vrezen (van Allah) in de harten.'),
(2628, 22, 33, 'Voor jullie zijn daarin voordelen voor een vastgestelde periode; vervolgens is er de offerplaats hij het Aloude Huis.'),
(2629, 22, 34, 'En voor iedere gemeenschap hebben Wij godsdienstige gebruiken (om te offoren) vastgesteld, opdat zij de Nam van Allah zullen uitspreken over het vee, waarmee Hij hen voorzien heeft. En jullie god is de Ène God, aan Hem geven jullie je daarom over. En brengt verheugende tijdingen am ben die zich overgaven.'),
(2630, 22, 35, 'Degenen waarvan, als de Naam van Allah genoemd wordt de harten trillen en die geduldigen zijn met wat ben treft en die de shalât onderhouden en uitgeven van dat waar Wij hen mee voorzien hebben.'),
(2631, 22, 36, 'En de kamelen (om te offeren) hebben Wij voor jullie gemaakt als behorend tot de gewijde Tekenen van Allah, daarin is goeds, voor jullie. Spreekt daarom de Naam van Allah over hen uit als zij in rijen staan (om geslacht te worden). En wanneer zij op hun zij vallen, eet dan van hen en voedt wie daar nederig om vraagt en de bedelaar. Zo hebben Wij ben aan jullie dienstbaar gemaakt. Hopelijk zullen jullie dankbaar zijn.'),
(2632, 22, 37, 'Het is niet hun vlees en niet hun bloed dat Allah bereikt, maar wat Hem bereikt is de vrees (voor Hem). Zo heeft Hij hen aan jullie dienstbaar gemaakt, opdat jullie Allah verheerlijken omdat Hij jullie leidde. En breng verheugende tijdingen aan de weldoeners.'),
(2633, 22, 38, 'Voorwaar, Allah zal degenen die geloven verdedigen. Voorwaar, Allah houdt van geen enkele ongelovige verrader.'),
(2634, 22, 39, 'Toestemming (om te vechten) is gegeven aan degenen die bevochten worden, omdat zij met onrecht behandeld worden. En voorwaar, Allah is zeker bij machte hen te helpen.'),
(2635, 22, 40, '(Zij zijn) degenen die zonder recht zijn verdreven uit hun huizen, alleen maar omdat zij zeiden: "Onze Heer is Allah." En als Allah de mensen niet van elkaar weerhield, waren kloosters, kerken en synagogen en moskeeën waarin de Naam van Allah vaak genoemd wordt, zeker verwoest. En Allah zal zeker hen helpen die Item (Zijn godsdienst) helpen. Voorwaar, Allah is zeker Sterk, Geweldig.'),
(2636, 22, 41, '(Zij zijn) degenen die, als Wij hen macht geven op aarde, de shalât onderhouden en de zakât betalen en het goede bevelen en het verwerpelijke verbieden. En het einde van alle zaken rust bij Allah.'),
(2637, 22, 42, 'En wanneer zij jou loochenen (O Moehammad): waarlijk, het volk van Nôeh en de \'Âd en de Tsamôcd loochenden (hun Profeten) vóór hen.'),
(2638, 22, 43, 'En het volk van Ibrâhîm en het volk van Lôeth.'),
(2639, 22, 44, 'En de bewoners van Madyan. En Môesa werd geloochend. Toen gaf Ik uitstel aan de ongelovigen, vervolgens bestrafte Ik hen. En hoe was mijn afkeuring?'),
(2640, 22, 45, 'En hoeveel steden hebben Wij niet vernietigd omdat zij onrecht pleegden waarop zij tot de bodem toe geruïneerd zijn? En (hoeveel) bronnen die verwaarloosd zijn en hoge kastelen (zijn er niet verlaten)?'),
(2641, 22, 46, 'Hebben zij dan niet rondgereisd op de aarde, zodat zij harten kregen om te begrijpen of oren om mee te horen? Voorwaar, de ogen zijn niet blind, maar de harten in hun binnengen zijn blind.'),
(2642, 22, 47, 'En zij vragen jou om de bestraffing te bespoedigen, Allah zet zijn belofte nooit breken. En voorwaar, één dag bij jouw Heer is als duizend jaren, zoals jullie berekenen.'),
(2643, 22, 48, 'En hoeveel steden heb ik geen uitstel gegeven, terwijl zij onrecht pleegden, waarna Ik hen bestrafte. En tot Mij is de terugkeer.'),
(2644, 22, 49, 'Zeg (O Moehammad): "O mensen, voorwaar, ik ben slechts een duidelijke waarschuwer voor jullie."'),
(2645, 22, 50, 'Degenen die geloven en goede daden verrichten, voor hen is er vergeving en een weldadige voorziening.'),
(2646, 22, 51, 'En degenen die proberen Onze Verzen te ontkrachten: zij zullen de bewoners van Djahîm (de Hel) zijn."'),
(2647, 22, 52, 'En Wij hebben geen enkele Boodschapper of Profeet vóór jullie gestuurd zonder dat, wanneer hij (de Koran) voordroeg, de Satan iets in zijn voorlezing wierp, maar Allah heft wat de Satan erin wierp op. Vervolgens bevestigt Allah Zijn Verzen. En Allah is Alwetend, Alwijs.'),
(2648, 22, 53, 'Zodat Hij wat de Satan inwerpt als een beproeving maakt voor degenen in wiens harten er een ziekte is en wiens harten verhard zijn. En voorwaar, de onrerhtvaardigen zijn sterk vijandig.'),
(2649, 22, 54, 'En zodat degenen die kennis gegeven is, zullen weten dat het de Waarheid van jouw Heer is, zodat zij erin geloven en zij hun harten aan Hem onderwerpen. En voorwaar, Allah zal de gelovigen zeker leiden op een recht Pad.'),
(2650, 22, 55, 'Maar degenen die niet geloven zullen erover in twijfel blijven, totdat het Uur onverwachts tot hen komt of de bestraffing van een ellendige Dag hen treft.'),
(2651, 22, 56, 'De heerschappij op die Dag behoort aan Allah. Hij zal tussen hen oordelen. Daarop zullen degenen die geloven en gmde daden verrichten in de Tuinen van gelukzaligheid (het Paradijs) zijn.'),
(2652, 22, 57, 'En degenen die niet geloven en Onze Verzen loochenen: zij zijn degenen voor wie er een vernederende bestraffing is.'),
(2653, 22, 58, 'En degenen die op de Weg van Allah uitwijken en vervolgens gedood worden of sterven. Allah zal hen zeker voorzien van een goede voorziening. Voorwaar, Allah is de Beste van de Voorzieners.'),
(2654, 22, 59, 'Hij zal hen zeker een plaats laten binnengaan, die hen behaagt. Voorwaar, Allah is zeker Alwetend, Zachtmoedig.'),
(2655, 22, 60, 'Zo is het. En wie straft met het gelijke van dat waar hij mee gestraft is en dan opnieuw onrechtvaardig behandeld wordt: Allah zal hen zeker helpen. Voorwaar, Allah is zeker Vergevend, Vergevensgezind.'),
(2656, 22, 61, 'Dat is omdat Allah de nacht doet overgaan in de dag en de dag doet overgaan in de nacht. En omdat Allah zeker Alhorend, Alziend is.'),
(2657, 22, 62, 'Dat is omdat Allah de Waarheid is en omdat wat zij naast Hem aanroepen vals is en omdat Allah de Verhevene, de Grootste is.'),
(2658, 22, 63, 'Zie jij niet dat Allah water uit de hemelen doet neerdalen? Zo wordt de aarde groen. Voorwaar, Allah is zeker Zachtmoedig, Alwetend.'),
(2659, 22, 64, 'Aan Hem behoort wat er in de hemelen en op de aarde is. En voorwaar, Allah is zeker de Behoefteloze, de Geprezene.'),
(2660, 22, 65, 'Zie jij niet dat Allah alles op de aarde en de schepen die over de zeeën rondvaren op zijn bevel aan jullie dienstbaar heeft gemaakt? En dat Hij de hemelen weerhoudt op de aarde te vallen, alleen maar door Zijn toestemming? Voorwaar, Allah is voor de mensen zeker Zachtmoedig, Meest Bamhartig.'),
(2661, 22, 66, 'En Hij is het Die jullie doet leven en dan doet sterven en jullie vervolgens doet leven. Voorwaar, de mens is zeker ondankbaar.'),
(2662, 22, 67, 'Voor iedere gemeenschap hebben Wij godsdienstige gebruiken vastgesteld, die zij moeten volgen. Laat hen daarom niet met jou over de zaak twisten, en roep op tot jouw Heer: voorwaar, jij volgt zeker rechte Leiding.'),
(2663, 22, 68, 'En als zij met jou twisten zeg dan: "Allah weet beter wat jullie doen."'),
(2664, 22, 69, 'Allah zal op de Dag der Opstanding onder jullie oordelen over dat waarover jullie plachten te redetwistten.'),
(2665, 22, 70, 'Weet jij niet dat Allah weet wat er in de hemelen en op de aarde is? Voorwaar, dat is in een Boek (Lauhoelmahfôezh). Voorwaar, dat is voor Allah gemakkelijk.'),
(2666, 22, 71, 'En zij aanbidden naast Allah dat waarover Hij geen bewijs neergezonden heeft en waarover zij geen kennis hebben. En voor de onrechtplegers zal ef geen helper zijn.'),
(2667, 22, 72, 'En wanneer de duidelijke Verzen aan hen worden voorgedragen herken jij op de gezichten van de degenen die ongelovig zijn de afkeuring. Zij staan op het punt om degenen die Onze Verzen aan hen voordragen aan te vallen. Zeg (O Moehammad): "Zal ik jullie op de hoogte brengen van wat slechter dan dat is? De Hel! En Allah beloofde die aan degenen die ongelovig zijn, en dat is een slechte bestemming."'),
(2668, 22, 73, 'O mensen! Er wordt eren vergelijking gemaakt, luistert ernaar! Voorwaar, degenen die jullie naast Allah aanroepen zullen nooit een vlieg kunnen scheppen, al kwamen zij daarvoor allen bijelkaar! En als de vlieg iets van hen zou weggrissen, kunnen zij het niet van hem terugpakken. Zwak is hij die er naar zoekt en het gezochte!'),
(2669, 22, 74, 'Zij schatten Allah niet op de Zijn ware nucht in. Voorwaar, Allah is zeker Sterk, Machtig.'),
(2670, 22, 75, 'Allah kiest uit de Engelen gezanten en uit de mensen (Boodschappers). Voorwaar, Allah is Alhorend, Alziend.'),
(2671, 22, 76, 'Hij weet wat vóór hen is en wat achter hen is en tot Allah worden alle zaken teruggebracht.'),
(2672, 22, 77, 'O jullie die geloven: buigt en knielt en aanbidt jullie Heer en doet het goede. Hopelijk zullen jullie welslagen.'),
(2673, 22, 78, 'En streeft naar Allah volgens het streven waar Hij rechtop heeft. Hij heeft jullie uitgekozen en Hij heeft het jullie in de godsdienst niet moeilijk gemaakt: (volgt) de godsdienst van jullie vader Ibrâhîm. Hij is degene die jullie Moslims genoemd heeft, vroeger en hierin (de Koran): opdat de Boodschapper getuige is voor jullie en opdat jullie getuigen zijn voor de mensen. Onderhoudt dus de shalât en geeft de zakât en houdt jullie vast ma Allah, Hij is jullie Meester: de beste Meester en de beste Helper!');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'Waarlijk, de gelovigen slagen.'),
(2675, 23, 2, 'Degenen die nederig zijn in hun shalât.'),
(2676, 23, 3, 'En degenen die nutteloos gepraat vemijden.'),
(2677, 23, 4, 'En degenen die hun plicht tot zakât nakomen.'),
(2678, 23, 5, 'En degenen die hun kuisheid bewaken.'),
(2679, 23, 6, 'Behalve tegenover hun echtgenotes en hun slavinnen, dan worden zij niet verweten.'),
(2680, 23, 7, 'Maar wie meer dan dat wensen: zij zijn degenen die de overtreders zijn.'),
(2681, 23, 8, 'En degenen die goed zorgen voor wat hen is toevertrouwd en voor hun beloften.'),
(2682, 23, 9, 'En degenen die hun shalât onderhouden.'),
(2683, 23, 10, 'Zij zijn degenen die de erfgenamen zijn'),
(2684, 23, 11, 'Degenen die Firdaus (het Paradijs) zullen erven, zij zijn daarin eeuwig levenden.'),
(2685, 23, 12, 'En voorzeker, Wij hebben de mens uit een uittreksel van klei geschapen.'),
(2686, 23, 13, 'Vervolgens maakten Wij hem tot een druppel in een stevige bewaarplaats.'),
(2687, 23, 14, 'Vervolgens schiepen Wij de druppel tot een bloedklonter, toen vormden Wij de bloedklonter tot een vleesklomp, waarna Wij de vleesklomp voorzagen van beenderen, die Wij vervolgens met vlees bekleedden. Later scheppen Wij een andere schepping. Gezegend is daarom Allah, de Beste der Scheppers.'),
(2688, 23, 15, 'Vervolgens zullen jullie zeker daarna sterven.'),
(2689, 23, 16, 'Daarna zullen jullie op de Dag der Opstanding worden opgewekt.'),
(2690, 23, 17, 'En voorzeker, Wij schiepen boven jullie zeven hemelen en Wij veronachtzamen de schepping niet'),
(2691, 23, 18, 'En Wij stuurden een afgepaste hoeveelheid water neer waarna Wij het blijvend in de grond vasthouden. En voorwaar, Wij zijn in staat het weer weg te nemen.'),
(2692, 23, 19, 'Toen legden Wij daarmee voor jullie tuinen met palmen en druivenstruiken aan, met daaraan veel vruchten. En jullie eten daarvan.'),
(2693, 23, 20, 'En (van) een boom die groeit op de berg Sinaî. die olie en een gerecht voor hen die eten levert.'),
(2694, 23, 21, 'En voorwaar, in het vee is een lering voor jullie. Wij geven jullie te drinken uit hun buiken en voor jullie zijn daarin vele nuttige zaken en jullie eten daarvan.'),
(2695, 23, 22, 'Op hen en op de schepen worden jullie vervoerd.'),
(2696, 23, 23, 'En voorzeker, Wij hebben Nôeh tot zijn volk gezonden. Toen zei hij: "O mijn volk, aanbidt Allah, er is geen andere god voor jullie dan Hij. Waarom vrezen jullie (Allah) niet?"'),
(2697, 23, 24, 'Toen zeiden de vooraanstanden, die niet geloofden, van zijn volk: "Deze (man) is slechts een mens zoals jullie. Hij wenst uit te blinken boven jullie. En als Allah het gewild had, zou Hij Engelen hebben gestuurd. Wij hebben hierover van onze voorouders nog nooit gehoord.'),
(2698, 23, 25, 'Hij is slechts een man die bezeten is, wacht daarom op hem tot een bepaald moment."'),
(2699, 23, 26, 'Hij (Nôeh) zei: "O mijn Heer, help mij vanwege wat zij loochenen."'),
(2700, 23, 27, 'Toen openbaarden Wij aan hem: "Bouw onder Ons toezicht en (volgens) Onze aanwijzing een schip. En als Ons bevel komt en de oven overkookt, ga dan aan boord, met van ieder (dier) twee, paarsgewijs, en jouw familie, behalve degene van hen tegen wie het Woord (van bestraffing) er eerder was. En spreek Mij niet aan over degenen die onrecht pleegden. Voorwaar, zij zullen verdronken worden.'),
(2701, 23, 28, 'Dus wanneer jij en de mensen met jou aan boord van het schip zullen gaan, zeg dan: "Alle lof zij Allah, Die ons gered heeft van het onrechtvaardige volk."'),
(2702, 23, 29, 'En zeg: "O mijn Heer, plaats mij op een gezegende plaats. En U bent het die de beste plaatsen geeft."'),
(2703, 23, 30, 'Voorwar, daarin zijn zeker Tekenen en Wij hebben hen zeker beproefd.'),
(2704, 23, 31, 'Vervolgens brengen Wij na hen een andere generatie voort.'),
(2705, 23, 32, 'En Wij stuurden hun een Boodschapper van onder hen (die zei:) "Aanbidt Allah. Er is geen god voor jullie dan Hem, waarom vrezen jullie (Allah) niet?"'),
(2706, 23, 33, 'En de vooraanstanden, die niet geloofden, van zijn volk en die de ontmoeting in het Hiernamaals loochenden en die Wij de weelde van het wereldse leven gaven, zeiden: "Deze (man) is slechts een mens zoals jullie, hij eet van wat jullie eten en hij drinkt van wat jullie drinken.'),
(2707, 23, 34, 'En als jullie rnensen volgen die zoals jullie zijn: voorwaar, dan behoren jullie zeker tot de verliezers.'),
(2708, 23, 35, 'Belooft hij jullie dat wanneer jullie dood zijn en tot stof en beenderen zijn geworden, dat jullie dan opgewekt worden?'),
(2709, 23, 36, 'Ver, ver weg is wat aan jullie beloofd is.'),
(2710, 23, 37, 'Ons leven is slechts op de wereld, wij gaan dood en wij leven en wij zullen niet opgewekt worden.'),
(2711, 23, 38, 'Hij is slechts een man die leugens over Allah heeft vèrzonnen. En Wij geloven hem niet."'),
(2712, 23, 39, 'Hij (Hôcd) zei: "O mijn Heer, help mij tegen wat zij loochenen."'),
(2713, 23, 40, 'Hij (Allah) zei: "Binnenkort zullen zij zeker spijt krijgen."'),
(2714, 23, 41, 'Terecht trof de bliksemslag hen toen, en Wij maakten hen als schuim. Ten onder gaat het onrechtvaardige volk!'),
(2715, 23, 42, 'Vervolgens brachten Wij na hen andere generaties voort.'),
(2716, 23, 43, 'Geen gemeenschap is in staat om haar vastgestelde tijdstip te vervroegen of uit te stellen.'),
(2717, 23, 44, 'Vervolgens stuurden Wij Onze Boodschappers, elkaar opvolgend. Telkens wanneer hun Boodschapper naar een gemeenschap kwam, loochenden zij hem. Toen deden Wij hen elkaar opvolgen (in de ondcrgang) en Wij maakten hen tot onderwerp van verhalen. Ten onder gaat het volk dat niet gelooft!'),
(2718, 23, 45, 'Daarna zonden Wij Môesa en zijn broeder Hârôen met Onze Tekenen en een duidelijk Bewijs.'),
(2719, 23, 46, 'Naar Fir`aun en zijn vooraanstaanden. En zij waren hoogmoedig en zij waren een hooghartig volk.'),
(2720, 23, 47, 'En zij zeiden: "Zouden wij die twee mensen, die zijn zoals wij, geloven, terwijl hun volk voor ons dienaren is?"'),
(2721, 23, 48, 'Daarom loochenden zij hen beiden en behoorden toen tot de vernietigden.'),
(2722, 23, 49, 'En voorzeker, wij hebben aan Môesa de Schrift (de Taurât) gegeven. Hopelijk zullen zij Leiding volgen.'),
(2723, 23, 50, 'En Wij maakten de zoon van Maryam en zijn moeder tot een Teken en Wij gaven hun een onderkomen op een hoogplegen plaats, bewoonbaar met stromend water.'),
(2724, 23, 51, 'O Boodschappers, eet van de goede zaken en verricht goede daden. Voorwaar, Ik ben Alwetend over wat jullie doen.'),
(2725, 23, 52, 'En voorwaar, deze godsdienst (de Islam) is jullie godsdienst, de enigste. En Ik ben jullie Heer, vreest Mij daarom.'),
(2726, 23, 53, 'Toen raakten zij onderling verdeeld in verschilende groepen over hun zaak (van hun godsdienst). Elke groep was blij met wat zij hadden.'),
(2727, 23, 54, 'Laat ben daarom in hun dwaling tot een bepaald tijdstip.'),
(2728, 23, 55, 'Denken zij dat omdat Wij hen rijkdom en kinderen gegeven hebben.'),
(2729, 23, 56, 'Wij Om voor hen haasten in (het schenken van) de goede zaken? Nee, maar zij beseffen het niet.'),
(2730, 23, 57, 'Voorwaar, degenen van hen die bedachtzaam zijn vanwege hun ontzag voor Allah.'),
(2731, 23, 58, 'En degenen die in de Verzen van hun Heer geloven.'),
(2732, 23, 59, 'En degenen die hun Heer geen deelgenoten toekennen.'),
(2733, 23, 60, 'En degenen die hun giften gaven terwijl hun harten vol ontzag zijn omdat zij tot hun Heer zullen terugkeren.'),
(2734, 23, 61, 'Zij zijn degenen die zich haasten om goede daden te verrichten en zkj zijn daarbij de eersten.'),
(2735, 23, 62, 'Wij belasten niemand dan volgens zijn vermogen en Wij hebben een boek dat volgens de Waarheid spreekt. En zij worden niet onrechtvaardig behandeld.'),
(2736, 23, 63, 'Maar hun harten verkeren hierover in dwaling. Zij hebben daarnaast (slechte) werken die zij verrichten.'),
(2737, 23, 64, 'Totdat, wanneer Wij degenen onder hen die in weelde leven met de bestraffing treffen; zij (om liulp) schreeuwen.'),
(2738, 23, 65, 'Schreeuw die Dag niet (om hulp): voorwaar, er zal jullie geen hulp van Ons verleend worden.'),
(2739, 23, 66, 'Waarlijk, Mijn Verzen zijn al aan jullie voorgedragen, maar jullie keerden je op jullie hielen om.'),
(2740, 23, 67, 'Hoogmoedig tegen over hem (de Koran), kletsend in de nacht: jullie verlieten hem.'),
(2741, 23, 68, 'Denken zij niet na over het Woord (de Koran)? Of is er iets tot hen gekomen dat niet eerder tot hun voorouders gekomen was?'),
(2742, 23, 69, 'Of erkennen zij hun Boodschapper niet? Zodat zij het verwerpen?'),
(2743, 23, 70, 'Of zeggen zij., "Hij (Moehammad) is bezeten." Integendeel, hij kwam tot hm met de Waarheid, maar de meesten van hen haten de Waarheid.'),
(2744, 23, 71, 'Als de Waarheid hun begeerten zou volgen zouden de hemelen en de ard en alles wat er op hen is ten onder gaan. Wij hebben hun echter hun Eer (de Koran) gegeven, maar zij wenden zich van hun Eer af.'),
(2745, 23, 72, 'Of vraag jij (O Moehammad) een beloning van hen? De beloning van jouw Heer is beter, want Hij is de Beste van de Voorzieners,'),
(2746, 23, 73, 'Voorwaar, jij roept hen op tot een Recht Pad.'),
(2747, 23, 74, 'En voorwaar, degenen die niet geloven in het Hiernamaals zijn zeker van het Rechte Pad afgeweken.'),
(2748, 23, 75, 'En als Wij hen zouden begenadigen en Wij zouden opheffen wat er aan tegenspoed bij hen is: dan zouden zij blijven doorgaan met hun dwalingen.'),
(2749, 23, 76, 'En voorzeker, Wij hebben hen met de bestraffing getroffen, toch werden zij niet ootmoedig tegenover hun Heer en zij werden niet nederig.'),
(2750, 23, 77, 'Totdat, wanneer Wij voor hen de deur van een zware bestraffing openden, zij in wanhoop verkeerden.'),
(2751, 23, 78, 'En Hij is Degene Die voor jullie het gehoor en gezichtsvermogen en de harten geschapen heeft. Weinig dankbaarheid tonen jullie!'),
(2752, 23, 79, 'En Hij is Degene Die juuie deed groeien op de aarde en tot Hem worden jullie verzameld.'),
(2753, 23, 80, 'En Hij is Degene Die doet leven en doet sterven. En aan Hem behoort het afwissden van de dag en de nacht Begrijpen jullie dan niet?'),
(2754, 23, 81, 'Integendeel, zij zeggen hetzelfde als de vroegeren zeiden.'),
(2755, 23, 82, 'Zij zeiden: "Wanneer wij dood zijn en tot aarde en beenderen geworden zijn, zullen wij dan zeker worden opgewckt?'),
(2756, 23, 83, 'Voorzeker, dit werd vroeger aan ons en onze vaderen beloofd: dit zijn slechts fabels van de vroegeren."'),
(2757, 23, 84, 'Zeg: "Aan wie behoort de aarde en alles wat zich daarop bevindt, als jullie het weten?"'),
(2758, 23, 85, 'Zij zullen zeggen: "Aan Allah." Zeg: "Waarom laten jullie je dan niet vermanen?"'),
(2759, 23, 86, 'Zeg: "Wie is de Heer van de zeven hemelen en de Heer van de Geweldige Troon?'),
(2760, 23, 87, 'Zij zullen zeggen: "Aan Allah." Zeg: "Waarom vrezen jullie (Allah) dan niet?"'),
(2761, 23, 88, 'Zeg: "In Wiens handen is de heerschappij over alles? En Hij beschermt en Hij wordt niet beschermt, als jullie het weten."'),
(2762, 23, 89, 'Zij zullen zeggen: "Aan Allah." Zeg: "Waarom zijn jullie dan misleid?"'),
(2763, 23, 90, 'Maar Wij hebben de Waarheid gegeven, zij zijn zeker leugenaars.'),
(2764, 23, 91, 'Allah heeft zich geen kind genomen en er is geen god naast Hem! Dan zou iedere god weggaan met wat hij schiep en zouden zij elkaar overweldigen. Heilig is Allah boven wat zij (Hem) toeschrijven.'),
(2765, 23, 92, 'De Kenner van het onwaarneembare en het waarneembare: verheven is Hij boven wat zij (Hem) toekennen.'),
(2766, 23, 93, 'Zeg: "Mijn Heer, indien U mij zou laten zien wat hun aangezegd is.'),
(2767, 23, 94, 'Mijn Heer, breng mij niet onder het onrechtvaardige volk!"'),
(2768, 23, 95, 'En voorwaar, Wij zijn zeker in staat om jou te laten zien wat Wij hun toezeggen.'),
(2769, 23, 96, 'Weer het slechte af met het beste. Wij weten het beste wat zij toeschrijven.'),
(2770, 23, 97, 'En zeg: "Mijn Heer, ik zoek mijn toevlucht tot U tegen de influisteringen van de Satans.'),
(2771, 23, 98, 'En ik zoek mijn toevlucht tot U zodat zij niet bij mij komen."'),
(2772, 23, 99, 'Totdat, wanneer de dood tot een van hen komt, hij zal zeggen: "O mijn Heer, laat mij terugkeren.'),
(2773, 23, 100, 'Hopelijk kan ik goede werken verrichten voor wat ik nagelaten heb." Zeker niet! Voorwaar, dit zijn slechts woorden die hij spreekt en voor hen is een scheiding tot de Dag waarop zij opgewekt worden.'),
(2774, 23, 101, 'Wanneer er op de bazuin geblazen wordt, op die Dag is er geen verwantschap tussen hen en zij kunnen elkaar geen vragen stellen.'),
(2775, 23, 102, 'Degenen wiens weegschalen zwaar wegen: Zij zijn degenen die de welslagenden zijn.'),
(2776, 23, 103, 'Degene wiens weegschalen licht wegen: zij zijn degenen die zichzelf verloren hebben, zij zullen in de Hel eeuwig levenden zijn.'),
(2777, 23, 104, 'Het Vuur verbrandt hun gezichten terwijl zij dawin venminkt worden.'),
(2778, 23, 105, 'Zijn Mijn Verzen dan niet aan jullie voorgedragen? Toen plachten jullie ze te loochenen.'),
(2779, 23, 106, 'Zij zelden: "Onze Heer, wij zijn overwonnen door ons ongeluk en wij waren een dwalend volk.'),
(2780, 23, 107, 'Onze Heer, haal ons hieruit! Als wij het herhalen: voorwaar, dan zijn wij onrechtvaardigen."'),
(2781, 23, 108, 'Hij zei: "Blijft daarin en lijdt. En spreekt niet tot Mij."'),
(2782, 23, 109, 'Voorwaar, er is een groep onder Mijn dienaren die zegt: "Onze Heer, wij geloofden, vergeef ons daarom en schenk ons Barmhartigheid en U bent de Beste der Bamhartigen."'),
(2783, 23, 110, 'Toen maakten jullie hen tot een onderwerp van bespotting, totdat jullie vergaten Mij te gedenken. En jullie plachten ben uit te lachen.'),
(2784, 23, 111, 'Voorwaar, Ik gaf hun op die Dag een beloning omdat zij geduldig waren: en voorwaar, zij zijn de winnaars.'),
(2785, 23, 112, 'Hij (Allah) zei: "Hoeveel jaren verbleven jullie op de aarde?"'),
(2786, 23, 113, 'Zij zeiden: "Wij verbleven daar een dag of een gedeelte van een dag, vraag het aan de rekenaars."'),
(2787, 23, 114, 'Hij (Allah) zei: "Jullie verbleven er maar kort, als jullie het weten.'),
(2788, 23, 115, 'Dachten jullie dat Wij jullie zo maar geschapen hebben? En dat jullie niet tot Ons terugkeren?"'),
(2789, 23, 116, 'Verheven is Allah, de Ware Koning er is geen god dan Hij, Heer van de Edele Troon.'),
(2790, 23, 117, 'En wie een andere god aanroept naast Allah, waarvoor hij geen bewijs heeft: voorwaar, zijn afrekening is bij zijn Heer. Voorwaar, de ongelovigen zullen niet welslagen.'),
(2791, 23, 118, 'En zeg: "Vergeef en begenadig, en U bent de Beste van de Bamhartigen."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'Dit is een Soerah die Wij neergezonden hebben en die Wij verplicht hebben gesteld; en Wij hebben daarin duidelijke Verzen neergezezonden. Hopelijk zullen jullie je laten vermanen.'),
(2793, 24, 2, 'De ontuchtige vrouw en de ontuchtige man, slaat hen ieder met honderd slagen. En laat medelijden met hen jullie niet treffen in de godsdienst van Allah, indien jullie in Allah en de Laatste Dag geloven. En laat een groep gelovigen getuige zijn van hun bestraffing.'),
(2794, 24, 3, 'De ontuchtige man trouwt niet, behalve met een ontuchtige vrouw of een veelgodenaanbidster. En de ontuchtige vrouw wordt niet getrouwd, behalve door een ontuchtige man of een veelgodenaanbidder. En dat is verboden voor de gelovigen.'),
(2795, 24, 4, 'En degenen die eerzame vrouwen beschuldigen (van ontucht) en vervolgens geen vier getuigen brengen: slaat hen met tachtig slagen. En aanvaardt nooit getuigenissen van hen, went zij zijn degenen die zwam zonden begaan.'),
(2796, 24, 5, 'Behalve degenen die daarna berouw hebben en zich beteren. En voorwaar, Allah is dan Vergevensgezind, Meest Barmhartig. En degenen die hun echtgenotes beschuldigen terwijl zij er geen getuigen voor hebben, behalve'),
(2797, 24, 6, 'zichzelf: de getuigenis van een van hen bestaat uit vier maal bij Allah zweren dat hij tot de waarachtigen behoort.'),
(2798, 24, 7, 'En de vijfde getuigenis is dat de straf van Allah over hem komt als hij tot de leugenaars behoort.'),
(2799, 24, 8, 'En de bestraffing zal van haar afgewend worden als zij vier maal bij Allah zweert dat haar man zeker tot de leugenaars behoort.'),
(2800, 24, 9, 'En haar vijfde (getuigenis) is dat de toom van Allah haar treft als hij tot de waarachtigen behoort.'),
(2801, 24, 10, 'En als de gunst van Allah en Zijn Barmhartigheid er voor jullie niet was en als Hij niet de Vergever, de Alwijze was... (zouden jullie snel ten ondergaan).'),
(2802, 24, 11, 'Voorwaar, degenen die de laster naar voren brachten zijn een groep onder jullie. Denkt niet dat het slecht voor jullie is. Integendeel, het is goed voor jullie: een ieder van hen wordt belast voor de zonde. En degene van hen die het grootste aandeel had: voor hem is er een geweldige bestraffing.'),
(2803, 24, 12, 'Hadden, toen jullie het hoorden, de gelovige mannen en de gelovige vrouwen maar het goede bij zichzelf gedacht, en hadden zij maar gezegd: "Deze (beschuldiging) is duidelijke laster."'),
(2804, 24, 13, 'Hadden zij maar vier getuigen naar voren gebracht. Toen zij geen getuigen naar voren brachten, werden zij daarom degenen die bij Allah de leugenaars zijn.'),
(2805, 24, 14, 'En als de gunst van Allah voor jullie er niet geweest was en Zijn Barmhartigheid in de wereld en het Hiernamaals, dan zou een geweldige bestraffing jullie zeker treffen vanwege wat jullie gedaan hebben.'),
(2806, 24, 15, '(En gedenkt) toen jullie de (laster) met jullie tongen overnamen en met jullie monden dat zeiden waarover jullie gew kennis hadden. En jullie dachten dat het iets kleins was, maar het is bij Allah geweldig.'),
(2807, 24, 16, 'En hadden jullie maar, toen jullie het hoorden, gezegd: "Het is niet aan ons om hierover te spreken, Heilig bent U, dit is een geiveldig verzinsel."'),
(2808, 24, 17, 'Allah waarschuwt jullie zoiets nooit te herhalen, indien jullie gelovig zijn.'),
(2809, 24, 18, 'En Allah verduidelijkt jullie de Verzen. En Allah is Alwetend, Alwijs.'),
(2810, 24, 19, 'Voorwaar, degenen die er van houden dat de gruweldaad zich verspreidt onder degenen die geloven: voor hen is er een pijnlijke bestraffing op de wereld en in het Hiernamaals. En Allah weet, terwijl jullie niet weten.'),
(2811, 24, 20, 'En als de gunst van Allah voor jullie er niet geweest was en Zijn Barmhartigheid en Allah niet Vriendelijk en Meest Barmhartig was... (zouden jullie snel ten ondergaan.)'),
(2812, 24, 21, 'O jullie die geloven! Volgt niet in de voetstappen van de Satan. En wie de voetstappen van de Satan volgt: voorwaar, hij (Satan) beveelt de gruweldaden en het verwerpelijke. En als de gunst van Allah er voor jullie niet geweest was en Zijn Barmhartigheid, dan zou niet één van jullie zich ooit reinigen. Maar Allah reinigt wie Hij wil. En Allah is Alhorend en Alwetend.'),
(2813, 24, 22, 'En laat hen van jullie die bemiddeld en gefortuncerd zijn niet zweren niet uit te geven aan de verwanten en de amen en de uitwijkers op de Weg van Allah; laat hen vergeven en lankmoedig zijn: houden jullie er niet van dat Allah jullie vergeeft? En Allah is Vergevensgezind, Meest Barmhartig.'),
(2814, 24, 23, 'Voorwaar, degenen die de eerbare, reine, gelovige vrouwen beschuldigen (van ontucht) zijn vervloekt op de wereld en in het Hiernamaals. En voor hen is er een geweldige bestraffing.'),
(2815, 24, 24, 'Op die Dag zullen hun tongen, hun handen en hun voeten tegen hen getuigen over wat zijn plachten te doen.'),
(2816, 24, 25, 'Op die Dag zal Allah hun hun ware loon betalen en zij zullen weten dat Allah zeker de duidelijke Waarheid is.'),
(2817, 24, 26, 'Zondige vrouwen zijn er voor zondige mannen en zondige mannen zijn er voor zondige vrouwen; en goede vrouwen zijn er voor goede mannen en goede mannen zijn er voor goede vrouwen; zij zijn degenen die onschuldig zijn aan wat zij (de lasteraars) zeggen: voor hen is er vergeving en een weldadige voorziening.'),
(2818, 24, 27, 'O jullie die geloven! Gaat geen huizen binnen, behalve jullie huizen, totdat jullie toestemming hebben gevraagd en salâm hebben gegeven aan haar bewoners. Dat is beter voor jullie, hopelijk zuilen jullie je laten vermanen.'),
(2819, 24, 28, 'Maar als jullie niemand daar (thuis) in vinden: gaat er den niet binnen totdat jullie toestemming hebben gekregen. En als tegen jullie wordt gezegd: "Gaat terug," gaat dan terug, want dat is reiner voor jullie. En Allah is Alwetend over wat jullie doen.'),
(2820, 24, 29, 'Het is geen ovenmding voor jullie als jullie onbewooinde huizen binnengaan, waarin is wat jullie kunnen gebruiken. En Allah weet wat jullie openlijk doen en wat jullie verbergen.'),
(2821, 24, 30, 'Zeg (O Moehammad) tegen de gelovige mannen dat zij hun ogen neerslaan en hun kuisheid bewaken, dat is reiner voor hen. Voorwaar, Allah is Alwetend over wat zij bedrijven.'),
(2822, 24, 31, 'En zeg tegen de gelovige vrouwen, dat zij hun ogen neerslaan en hun kuisheid bewaken, en hun sieraad niet tonen, behalve wat daarvan zichtbaar is. En zij moeten hun sluiers over hun boezems dragen en hun schoonheid niet openlijk tonen, behalve aan hun echtgenoten, of hun vadem of de vaders van hun echtgenoten, of hun zonen, of de zonen van hun echtgenoten, of hun broeders, of de zonen van hun broeders, of de zonen van hun zusters, of hun vrouwen, of slavinnen waarover zij beschikken, of mannelijke helpers die geen begeerte meer hebben, of de kinderen die nog niet op de \'Aurât as van vrouwen letten. En laten zij niet met hun voeten stampen om hun sieraden die zij verbergen te laten kennen. En keert jullie allen berouwvol ten Allah, O gelovigen. Hopelijk zullen jullie welslagen.'),
(2823, 24, 32, 'En trouwt hen onder jullie die ongetrouwd zijn en de waarachtigen onder jullie slaven en slavinnen. Als zij arm zijn, zal Allah hen voorzien vanuit Zijn gunst. En Allah is Alomvattend, Alwetend.'),
(2824, 24, 33, 'En laat degenen die geen (mogelijkheid) vinden tot het huwelijk kuis blijven, tot Allah hen uit Zijn gunst voorziet. En degenen onder de slaven waarover jullie beschikken die een vrijbrief van jullie wensen: schrijft die voor hen, indien jullie iets goeds in hen kennen. En geeft hun van de rijkdom die Allah jullie gaf. En dwingt jullie slavinnen niet tot onkuisheid indien zij kuisheid wensen omdat jullie de vergangkelijkheden van de wereld begeren. Maar indien iemand hen dwingt: voorwaar, Allah is na hun dwang (voor deze vrouwen) Vergevelaggezind, Meest Barmhartig.'),
(2825, 24, 34, 'En Wij hebben zeker voor jullie duidelijke Verzen neergezonden en voorbeelden voor degenen die jullie vroeger voorafgegaan zijn en een onderricht voor de Moettaqôen.'),
(2826, 24, 35, 'Allah geeft het licht aan de hemelen en de aarde. De gelijkenis van Zijn licht is met een nis met een lamp erin: de lamp bevindt zich in een glas. Het glas is als een stralende ster, die brandt (van de olie) van een gezegende olijfbloom, die niet van het Westen en niet het Oosten is. Haar olie verlicht, hoewel zij (nog) niet door het vuur aangemakt is. Licht op licht. Allah leidt naar Zijn licht wie Hij wil. En Allah maakt gelijkenissen voor de mensen. En Allah is Alwetend over alle zaken.'),
(2827, 24, 36, '(Zo\'n licht brandt) in huizen waarvoor Allah gebood (Hem) erin te eren en Zijn Naam te noemen, zij prijzen Zijn Glorie daarin in de ochtenden en de avonden.'),
(2828, 24, 37, '(Door) mmen die niet door handel en niet door verkoop worden afgeleid van de gedachtenis van Allah en (ook niet van) het onderhouden van de shalât en het geven van de zakât en die bang zijn voet de Dag waarop de harten en de ogen sidderen.'),
(2829, 24, 38, 'Opdat Allah hen beloont volgens het beste van wat zij deden. En Hij vermeerdert voor hen Zijn gunst. En Allah voorziet wie Hij wil zonder rekening.'),
(2830, 24, 39, 'En degenen die ongelovig zijn: hun daden zijn als een luchtspiegeling op de woestijnvlakte. De dorstige denkt water (te vinden) totdat hij daar komt en er niets vindt Maar hij vindt Allah bij zich en Hij zal zijn rekening opmaken. En Allah is snel in de afrekening.'),
(2831, 24, 40, 'Of (de toestand van de ongelovige is) als de donkerten in de diepe zee, bedekt door golf op golf, waarop wolken zijn. donkerten bovenop elkaar. Wanneer iemand zijn hand uitstrekt kan hij die bijna niet zien. En aan wie Allah geen licht geeft: voor hem is er gew licht.'),
(2832, 24, 41, 'Zie jij niet dat alles in de hemelen en op de aarde Allah prijst en (ook) de vogels met uitgespreide vleugels? Ieder kent zijn shalât en zijn lofprijzing en Allah is Alwetend over wat zij doen.'),
(2833, 24, 42, 'En aan Allah behoort de heerschappij van de hemelen en de aarde en tot Allah is de terugkeer.'),
(2834, 24, 43, 'Zie jij niet dat Allah de wolken voortdrijft en hen dan bij elkaar voegt en hen daarop in stapels verzamelt? Hierna zie jij de regen uit hun midden komen. En Hij doet uit de hemelen bergen (wolken) neerdalen waarin hagel is. Hij treft daarme wie Hij wil en Hij wendt het af van wie Hij wil. De flits van de bliksem ontneemt bijna het gezichtsvermogen.'),
(2835, 24, 44, 'Allah wisselt de dag en de nacht af. Voorwaar, daarin is zeker een lering voor de bezitters van inzicht.'),
(2836, 24, 45, 'En Allah schiep ieder levend wezen uit water; onder hen er die zich op hun buiken voortbewegen en onder hen zijn er die op twee (voeten) lopen en onder hen zijn er die op vier (poten) lopen. Allah schept wat Hij wil. Voorwaar, Allah is Almachtig over alle zaken.'),
(2837, 24, 46, 'Wij hebben zeker duidelijke Verzen neergezonden en Allah leidt wie Hij wil naar een recht pad.'),
(2838, 24, 47, 'En zij (de huichelaars) zeggen: "Wij geloven in Allah en de Boodschapper en wij gehoorzamen." Vervolgens wendt een groep van hen zich daarna af. En zij zijn niet de gelovigen.'),
(2839, 24, 48, 'En wanneer zij tot Allah en Zijn Boodschapper opgeroepen worden, opdat hij onder hen oordeelt, dan is er een groep onder hen die zich afwendt.'),
(2840, 24, 49, 'Maar indien het recht aan hun kant is, begeven zij zich nederig naar hem.'),
(2841, 24, 50, 'Is er een ziekte in hun harten, of twijfelen zij, of vrezen zij dat Allah en Zijn Boodschapper onrechtvardig voor hen zijn? Integendeel, zij zijn degenen die de onrechtvaardigen zijn.'),
(2842, 24, 51, 'De woorden van de gelovigen, wanneer zij naar Allah en Zijn Boodschapper opgeroepen worden, opdat hij onder hen oordeelt, zijn slechts dat zij zeggen: "Wij horen en wij gehoorzamen." En zij zijn degenen die de welslagenden zijn.'),
(2843, 24, 52, 'En wie Allah en Zijn Boodschapper gehoorzaamt en bang voor Allah is en Hem vreest: zij zijn degenen die de overwinnaars zijn.'),
(2844, 24, 53, 'En zij zweren bij Allah met hun duurste eden, dat als jij hen zou bevelen, zij (hun huizen) zouden verlaten. Zeg: "Zweert niet! (Jullie) gehoorzaamheid is bekend: voorwaar, Allah is Alwetend over wat jullie doen.'),
(2845, 24, 54, 'Zeg: "Gehoorzaamt Allah en gehoorzaamt de Boodschapper!" En als jullie je afwenden: hij is slechts verantwoordelijk voorwaar hij mee belast is en jullie zijn verantwoordelijk voor waar jullie mee belast zijn. En indien jullie hem gehoorzamen, volgen jullie Leiding. En de Boodschapper is slechts verantwoordelijk voor de duidelijke verkondiging.'),
(2846, 24, 55, 'En Allah heeft degenen onder jullie die geloven en goede werken verrichten beloofd, dat Hij hen zeker op aarde als gevolmachtigden aanstelt, zoals Hij degenen vóór hen als gevolmachtigden aanstelde, en dat Hij hun godsdienst die Hem voor hen behaagde zeker bevestig en dat Hij voor hen na hun vrees (door) veiligheid vervangt. Zij aanbidden Mij en zij kennen Mij in niets deelgenoten toe. Maar wie daarna ongelovig zijn: zij zijn degenen die zwaar zondig zijn.'),
(2847, 24, 56, 'En onderhoudt de shalât en geeft de zakât en gehoorzaamt de Boodschapper. Hopelijk zullen jullie begenadigd worden.'),
(2848, 24, 57, 'Denkt niet dat degenen die ongelovig zijn op de aarde kunnen vluchten (voor hun bestraffing); en hun verblijfplaats is de Hel: en dat is zeker de slechtste bestemming!'),
(2849, 24, 58, 'O jullie die geloven! Laat de slaven waarover jullie beschikken en degenen onder jullie die de volwassenheid nog niet bereikt hebben, jullie bij drie gelegenheden toestemming vragen (om tot jullie te komen): voor de Fadjr-shalât; waneer jullie je gewaden afleggen voor de Zhoehr-shalât en na de \'Isjâ\'-shalât: de drie (gelegenheden) waarbij jullie je ontkleden. Het is geen overtreding voor jullie en voor hen als zij daarna rondgaan tot elkaar (om te bedienen). Zo maakt Allah jullie de Tekenen duidelijk. En Allah is Alwetend, Alwijs.'),
(2850, 24, 59, 'En wanneer de kinderen onder jullie de volwassenheid bereiken, laat hen dan toestemming vragen zoals degenen vóór hen om toestemming vroegen. Zo maakt Allah jullie Zijn Tekenen duidelijk. En Allah is Alwetend, Alwijs.'),
(2851, 24, 60, 'En de oudere vrouwen die niet meer hopen te trouwen, voor hen is het geen overtreding om hun (buitenste) gewaden af te leggen, maar zonder (hun) sieraad te vertonen. Maar het na te laten is beter voor hen. En Allah is Alhorend, Alwetend.'),
(2852, 24, 61, 'Het is niet een zonde voor de blinde, en niet voof de lamme. en niet voot de zieke, en niet voor julliezelf, dat jullie in jullie huizen eten, of in de huizen van jullie vaderen, of in de huizen van jullie moeders, of in de huizen van jullie broeders, of in de huizen van jullie zusters, of in de huizen van jullie vaders broeders, of in de huizen van jullie vaders zusters, of in de huizen van jullie moeders broedcrs, of in de huizen van jullie moeders zusters, of in de huizen waarvan jullie de sleutels bezitten, of (in de huizen van)jullie vrienden. Het is geen overtreding voor jullie om gezamenlijk te eten of apart. Wanneer jullie dan huizen binnentreden, geeft julliezelf dan salâm, een groet van bij Allah, gezegend en goed. Zo maakt Allah jullie de Tekenen duidelijk. Hopelijk zullen jullie begrijpen.'),
(2853, 24, 62, 'Voorwaar, de gelovigen zijn slechts degenen die in Allah en Zijn Boodschapper geloven. En wanneer zij met hem zijn bij een gezamenlijke zaak gaan zij niet weg voordat zij hem om toestemming hebben gevraagd. Voorwaar, degenen die jou (O Moehammad) om toestemming vragen: zij zijn degenen die in Allah en Zijn Boodschapper geloven. En als zij jou om toestemming vragen voor een of andere zaak: geef dan toestemming aan wie van hen jij wil en vraag Allah om hun vergeving. Voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(2854, 24, 63, 'Maakt de (manier van) aanspreken van de Boodschapper onder jullie niet als de aanspreking van jullie onder elkaar. Waarlijk, Allah kent degenen onder jullie die in het geheim wegsluipen. Laten degenen die zijn bevel ongehoorzaam zijn opletten dat een beproeving hen treft of een pijnlijke bestrafring hen treft.'),
(2855, 24, 64, 'Weet: voorwaar, aan Allah behoort wat in de hemelen is en op de aarde. Waarlijk, Hij kent de toestand waarin jullie je bevinden. En op de dag dat zij tot Hem teruggekeerd worden, zal Hij hen vertellen wat zij deden. En Allah is Alwetend over alle zaken.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Gezegend is Degene Die de Foerqân (de Koran) heeft neergezonden naar Zijn dienaar, opdat hij een waarschuwer voor de werelden zal zijn.'),
(2857, 25, 2, 'Hij is Degene aan wie het koninkrijk van de hemelen en de aarde toebehoort. En Hij neemt Zich nooit een zoon en Hij heeft geen deelgenoot in het koninkrijk. En Hij schiep alles en Hij bepaalt alles nauwkeurig.'),
(2858, 25, 3, 'Toch namen zij naast Hem goden die niets schiepen en die niet bij machte zijn zelf kwaad (af te wenden) en goed (te doen) en geen macht hebben over leven en niet over de dood en over het opwekken.'),
(2859, 25, 4, 'En degenen die ongelovig zijn, zeggen: "Dit is slechts een leugen die hij verzonnen heeft en waar een ander volk hem bij geholpen heeft." Waarlijk, zij kwamen met onrecht en leugens.'),
(2860, 25, 5, 'En zij zeiden: "(Dit zijn) fabels van de vroegeren die hij heeft laten opschrijven en zij worden hem \'s morgens en \'s avonds voorgelezen.'),
(2861, 25, 6, 'Zeg (O Moehammad): "Hij heeft hem (de Koran) neergezonden, Degene Die het geheim in de hemelen en op de aarde kent. Voorwaar, Hij is Vergevensgezind, Meest Barmhartig."'),
(2862, 25, 7, 'En zij zeiden: "Wat is dat voor Boodschapper, die voedsel eet en op de markten rondgaat, waarom is hem geen Engel gezonden, zodat hij niet hem een waarschuwer is?'),
(2863, 25, 8, 'Of is hem geen schat geschonken of heeft hij geen tuin, waar hij van kan eten?" En de onrechtvaardigen zeiden: "Jullie volgen slechts een bezeten man."'),
(2864, 25, 9, 'Zie hoe zij niet jou vergelijkingen maken; zij dwalen en zijn niet in staat een weg (te vinden).'),
(2865, 25, 10, 'Gezegend is Degene Die, als Hij dat wil, jou beter dan dat schenkt: tuinen waar onder door de rivieren stromen en Hij schenkt jou paleizen.'),
(2866, 25, 11, 'Maar nee, zij loochenen het Uur. En Wij hebben voor wie het Uur loochent een laaiend vuur (de Hel) gereedgemaakt.'),
(2867, 25, 12, 'Wanneer dat (vuur) hen van een verre plaats ziet horen zij haar gesteun en gekrijs.'),
(2868, 25, 13, 'En wanneer zij er gebonden uitgeworpen werden naar een enge plaats, dan smeken zij daar om vernietiging.'),
(2869, 25, 14, 'Smeekt op die Dag niet om één vernietiging, smeekt om vele vernietigingen!'),
(2870, 25, 15, 'Zeg: "Is dat beter of de eeuwige tuin (het Paradijs) die aan de Moettaqôen beloofd is? Het is voor hen een beloning en een eindbestemming."'),
(2871, 25, 16, 'Voor hen is daarin alles wat zij wensen; eeuwig levend (daarin): het is een belofte van jouw Heer, waarnaar gevraagd mag worden.'),
(2872, 25, 17, 'En op de Dag waarop Hij hen en wat zij naast Allah aanbidden zal verzamelen, zal Hij zeggen: jullie het, die Mijn dienaren deden dwalen, of zijn zij zelf van de Weg gedwaald?"'),
(2873, 25, 18, 'Zij zullen zeggen: "Heilig bent U, het past ons niet dat wij buiten U beschermers genomen hebben, maar U hebt hen en hun vaderen laten genieten totdat zij de vermaning vergaten en een vernietigd volk geworden waren."'),
(2874, 25, 19, '(Allah zal zeggen:) "Zij hebben jullie van leugens beticht over wat jullie zeiden. Jullie zijn niet in staat (de bestraffing) af te wenden en (er is) geen hulp. En wie van jullie onrecht pleegt: hem doen Wij de grootste bestraffing proeven."'),
(2875, 25, 20, 'En Wij zonden geen Boodschappers vóór jou, of zij aten voedsel en zij gingen op de markten rond. En Wij hebben sommigen van jullie tot een beproeving voor anderen gemaakt: zullen jullie geduld hebben? En jouw Heer is Alziende.'),
(2876, 25, 21, 'En degenen die niet op de ontmoeting met Ons hopen, zeiden: "Waren de Engelen maar tot ons neergezonden of zagen wij onze Heer maar!" Voorzeker, zij zijn arrogant over zichzelf en zij overtreden met een grote overtreding.'),
(2877, 25, 22, 'Op de Dag waarop zij de Engelen zullen zien, op die Dag zal er geen verheugende tijding voor de misdadigers zijn, en zij zullen zeggen: "(Weest) ver verwijderd!"'),
(2878, 25, 23, 'En Wij wenden Ons tot de daden die zij hebben verricht en Wij maken die tot verstrooid stof.'),
(2879, 25, 24, 'De bewoners van het Paradijs zullen op die Dag, een goede verblijfplaats hebben en een betere rustplaats.'),
(2880, 25, 25, 'En (gedenkt) de Dag waarop de hemel met de wolken uiteen zal splijten en de Engelen neerdalen.'),
(2881, 25, 26, 'De ware heerschappij zal op die Dag toebehoren aan de Barmhartige en het zal een moeilijke Dag zijn voor de ongelovigen.'),
(2882, 25, 27, 'En (gedenkt) de Dag waarop de onrechtvaardige op zijn handen bijt, terwijl hij zegt: "Had ik maar een Weg genomen met de Boodschapper!'),
(2883, 25, 28, 'Wee mij! Had ik maar niet zo\'n ongelovige als boezemvriend genomen.'),
(2884, 25, 29, 'Voorzeker, hij heeft mij doen afdwalen van de Vermaning nadat die tot mij gekomen was: en de Satan is de mensen ontrouw!"'),
(2885, 25, 30, 'En de Boodschapper zei: "O mijn Heer, voorwaar, mijn volk heefl deze Koran achtergelaten.'),
(2886, 25, 31, 'En zo hebben Wij voor iedere Profeet een vijand gemaakt onder de misdadigers. Maar jouw Heer is voldoende als Leider en Helper.'),
(2887, 25, 32, 'En degenen die ongelovig zijn, zeggen: "Was de Koran maar in één keer volledig aan hem neergezonden..," (Maar) zo hebben Wij daarmee jouw hart versterkt (O Moehammad), en Wij hebben hem regelmatig doen voordragen.'),
(2888, 25, 33, 'En zij komen niet met een rare vraag tot jou, of Wij brengen jou de waarheid en een mooiere uitleg.'),
(2889, 25, 34, 'Degenen die op hun gezichten bij de Hel verzameld zullen worden zijn degenen met de slechtste plaats en zijn het verst afgedwaald van de Weg.'),
(2890, 25, 35, 'Voorzeker, Wij hebben aan Môesa de Schrift gegeven. En Wij hebben zijn broeder Hârôen aangewezen als rechterhand.'),
(2891, 25, 36, 'Toen zelden Wij: "Gaat naar het volk dat Onze Tekenen loochent," en Wij vernietigden hen met een volledige vernietiging.'),
(2892, 25, 37, 'En Wij verdronken het volk van Nôeh toen zij de Boodschappers loochenden. En Wij maakten hen tot een teken voor de mensheid. En Wij hebben voor de onrechtplegers een pijnlijke bestraffing voorbereid.'),
(2893, 25, 38, 'En (Wij vernietigden de volken van de) \'Âd en Tsamôed en het volk van Rass en vele generaties daartussen.'),
(2894, 25, 39, 'En voor ieder maakten Wij de gelijkenissen en eik vernietigden Wij met een volledige vernietiging.'),
(2895, 25, 40, 'En voorzeker, zij (de ongelovigen) kwamen voorbij de stad, waarop een slechte regen geregend had. Hebben zij dat dan niet gezien? Maar nee, zij hoopten niet tot leven gebracht te worden.'),
(2896, 25, 41, 'En wanneer Zij jou zien (O Moehammad), nemen zij jou slechts tot onderwerp van bespotting: "Is die het die Allah als Boodschapper heeft gestuurd?'),
(2897, 25, 42, 'Hij had ons bijna van onze goden doen afdwalen, als wij niet geduldig met hen gebleven waren." Maar zij zullen weten, wanneer zij de bestraffing zien, wie het verst van de Weg afgedwaald is.'),
(2898, 25, 43, 'Heb jij degene gezien, die zijn begeerten als zijn god neemt? Zou jij een beschermer voor hem zijn?'),
(2899, 25, 44, 'Of denk jij dat de meesten van hen horen of begrijpen? Zij zijn slechts als vee, erger nog, zij zijn het verst afgedwaald van de Weg.'),
(2900, 25, 45, 'Zie jij niet hoe jouw Heer de schaduwen verlengt? En als Hij het had gewild, had Hij die zeker kunnen doen stilstaan. Toen hebben Wij de zon tot een wijzer gemaakt.'),
(2901, 25, 46, 'Vervolgens trokken Wij haar (rond het middaguur) naar Ons toe, een geleidelijke terugtrekking.'),
(2902, 25, 47, 'En Hij is Degene Die de nacht als een kledingstuk voor jullie heeft gemaakt en de slaap om uit te rusten. En Hij heeft de dag gemaakt om wakker te zijn (om te werken).'),
(2903, 25, 48, 'En Hij is Degene Die de winden, als brengers van verheugende tijdingen voor Zijn Barmhartigheid uit heeft gestuurd. En Wij doen puur water uit hemel neerdalen.'),
(2904, 25, 49, 'Opdat Wij hiermee droog land vruchtbaar maken en om water te geven aan wat Wij geschapen hebben: vee en veel mensen.'),
(2905, 25, 50, 'En voorzeker, Wij hebben dit (geven van water) onder hen afgewisseld, opdat zij er een lering uit trekken. Maar de meeste mensen weigeren, behalve ondankbaarheid.'),
(2906, 25, 51, 'En als Wij het gewild hadden, dan hadden Wij zeker naar iedere stad een waarschuwer gestuurd.'),
(2907, 25, 52, 'Gehoorzaarn daarom de ongelovigen niet en vecht tegen hen met een grote strijd.'),
(2908, 25, 53, 'Hij is Degem Die de twee zeeën naast elkaar doet stromen, de een zoet en fris van smaak, de ander zout en bitter. En Hij heeft een afscheiding tussen hen geplaatst, en (die is) als een onverbrugbare afscheiding.'),
(2909, 25, 54, 'En Hij is Degene Die de mens heeft geschapen uit water en vervolgens heeft Hij hem nageslacht en aangetrouwdheid gegeven. En jouw Heer is Almachtig.'),
(2910, 25, 55, 'En zij aanbidden dat naast Allah wat hen niet kan baten en niet kan schaden. En de ongelovige is een helper (van de Satan) tegen zijn Heer.'),
(2911, 25, 56, 'En Wij hebben jou slechts gestuurd als een brenger van verbeugende tijdingen en als een waarschuwer.'),
(2912, 25, 57, 'Zeg (O Moehammad): "Ik vraag van jullie hiervoor geen beloning anders dan dat men de Weg tot zijn Heer wil nemen.'),
(2913, 25, 58, 'En vertrouw op de Levende, Die niet sterft en prijs Zijn lof. En Hij is voldoende als Alwetende over de zonden van Zijn dieneren.'),
(2914, 25, 59, 'Diegene Die de hemel en de aarde en alles wat daartussen is geschapen heeft in zes dagen. Vervolgens zetelde Hij zich op de Troon, Hij is de Erbarmer, vraag over Hem aan degene die daarover het meeste weet.'),
(2915, 25, 60, 'En als er tot hun gezegd wordt: "Kniel voor de Erbarmer," zeggen zij: "Wie is de Erbarmer, zouden wij knielen voor wat jij ons beveelt?" En het doet hun afkeer toenemen.'),
(2916, 25, 61, 'Gezegend is Degene Die de sterrenstelsels in de hemel heeft gemaakt en daarin een lamp (de zon) en een verlichtende maan heeft geplaatst.'),
(2917, 25, 62, 'En Hij is Degene Die de nacht en de dag doet afwisselen voor wie er een lering uit wil trekken of dankbaar wil zijn.'),
(2918, 25, 63, 'En de dienaren van de Erbarmer zijn degenen die bescheiden op aarde rondgaan. En als onwetenden hen aanspreken, zeggen zij: "Salâm!" (Vrede)'),
(2919, 25, 64, 'En degenen die de nacht doorbrengen, terwijl zij voor hun Heer knielen en staan (in hun shalât).'),
(2920, 25, 65, 'En degenen die zeggen: "Onze Heer, wend de bestraffing van de Hel van ons af. Voorwaar, haar bestraffing is een voortdurende kwelling.'),
(2921, 25, 66, 'Voorwaar, het is een zeer slechte vestiging en verblijfplaats."'),
(2922, 25, 67, 'En degenen die, wanneer zij besteden, niet overdrijven en niet gierig zijn, maar het midden daartussen houden.'),
(2923, 25, 68, 'En degenen die niet naast Allah een andere god aanroepen en die niemand doden, waarvan (het doden) door Allah verboden is, behalve volgens het recht. En die geen ontucht plegen, want wie dat doet zal een bestraffing ontmoeten.'),
(2924, 25, 69, 'Voor hem zal de bestraffing vermenigvuldigd worden op de Dag der Opstanding en vernederd zal hij daarin eeuwig leven.'),
(2925, 25, 70, 'Behalve degene die berouw toont en gelooft en goede daden verricht. Voor diegenen wisselt Allah hun zonden in voor goede daden. En Allah is Vergevensgezind, Meest Barmhartig.'),
(2926, 25, 71, 'En wie berouw toont en goede daden verricht: voorwaar, hij wendt zich berouwvol tot Allah.'),
(2927, 25, 72, 'En (ook) degenen die geen valse getuigenis afleggen. En wanneer zij voorbijkomen aan onzinnig gepraat, gaan zij den waardig aan voorbij.'),
(2928, 25, 73, 'En (ook) degenen die, waaneer zij met de Verzen van hun Heer vermaand worden, daarbij niet doof en blind neervallen.'),
(2929, 25, 74, 'En (ook) degenen die zeggen: "Onze Heer, voor ons onze echtgenotes en onze nakomelingen een verkoeling voor de ogen en maak ons leiders voor de Moettaqôen.'),
(2930, 25, 75, 'Zij zijn degenen die beloond zullen worden met een ereplaats (in het Paradijs) en zij zullen daarin ontvangen worden met om begroeting en vrede.'),
(2931, 25, 76, 'Zij leven daarin eeuwig, een goede vestiging en verblijfplaats.'),
(2932, 25, 77, 'Zeg: "Mijn Heer zal zich niet om jullie bekommeren, indien jullie Hem niet aanroepen; waarlijk, jullie hebben (Hem) geloochend, dus is er spoedig de onvermijdbare (bestraffing)."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'Tha Sîn Mîm.'),
(2934, 26, 2, 'Dit zijn Verzen van het duidelijke Boek.'),
(2935, 26, 3, 'Misschien zou jij jezelf vernietigen van verdriet omdat zij geen gelovigen zijn.'),
(2936, 26, 4, 'Als Wij het gewenst hadden, hadden Wij een Teken uit de hemel tot hen doen neerdalen, zodat hun nekken ervoor gebogen bleven.'),
(2937, 26, 5, 'Er komt geen nieuwe Vemaning van de Erbarmer tot hen, of zij wenden zich eman af.'),
(2938, 26, 6, 'Voorzeker, zij loochenden, maar berichten over wat zij plachten te bespotten zullen tot hen komen.'),
(2939, 26, 7, 'Kijken zij dan aiet naar de aarde, hoeveel Wij er van allerlei rijke soorten grwassen op doen groeien?'),
(2940, 26, 8, 'Voorwaar, daarin is zeker een Teken, maar de meesten van hen zijn geen gelovigen.'),
(2941, 26, 9, 'En voorwaar, jouw Heer: Hij is zeker de Almachtige, Meest Barmhartige.'),
(2942, 26, 10, '(Gedenk) toen jouw Heer Môesa opriep: "Ga naar het volk van de onrechtvaardigen.'),
(2943, 26, 11, 'Het volk van Fir\'aun, vrezen zij (Allah) niet?'),
(2944, 26, 12, 'Hij (Môesa) zei: "Mijn Heer, ik ben bang dat zij mij loochenen.'),
(2945, 26, 13, 'En dat mijn borst zich zal vernauwen en dat ik niet vloeiend zal spreken, zend daarom (de Engel) naar Hârôen.'),
(2946, 26, 14, 'En zij hebben (een beschuldiging van) een misdaad tegen mij en ik ben bang dat zij mij zullen doden."'),
(2947, 26, 15, 'Hij (Allah) zei: "Nee, gaat dus beiden met Onze Tekenen: voorwaar, Wij zijn met jullie, luisterend.'),
(2948, 26, 16, 'Gaat daarom naar Fir\'aun en zegt: "Voorwaar, wij zijn de Boodschappers van de Heer der Werelden.'),
(2949, 26, 17, 'Zend de Kinderen van Israël met ons."'),
(2950, 26, 18, 'Hij (Fir\'aun) zei: "Hebben wij jou niet als een kind onder ons opgevoed en verbleef jij geen jaren van jouw leven onder ons?'),
(2951, 26, 19, 'En jij deed wat jij deed en jij behooft tot de ondankbaren."'),
(2952, 26, 20, 'Hij (Môesa) zei: "Ik heb dat gedaan toen ik tot de onnadenkenden behoorde.'),
(2953, 26, 21, 'Dus vluchtte ik weg toen ik bang voor jullie was. Daarop heeft mijn Heer aan mij Wijsheid ij gegeven en gemaakt dat ik tot de Boodschappers behoorde.'),
(2954, 26, 22, 'En dit is de gunst die jij mij bewees: dat jij de Kinderen van Israël tot slaven gemaakt hebt."'),
(2955, 26, 23, 'Fiir\'aun zei: "En wie is de Heer der Werelden?"'),
(2956, 26, 24, 'Hij (Môesa) zei: "De Heer van de hemelen en de aarde en wat tussen hen beide is, als jullie er maar van overtuigd waren."'),
(2957, 26, 25, 'Hij (Fir\'aun) zei tot hen die rondom hem waren: "Luisteren jullie niet?"'),
(2958, 26, 26, 'Hij (Môesa) zei: "Juitie Heer en de Heer van jullie voorvaderen."'),
(2959, 26, 27, 'Hij (Fir\'aun) zei: "Voorwaar, jullie Boodschapper die tot jullie gezonden is, is zeker bezeten."'),
(2960, 26, 28, 'Hij (Môesa) zei: "De Heer van het Oosten en het Westen en wat tussen hen beide is, als jullie begrijpen."'),
(2961, 26, 29, 'Hij (Fir\'aun) zei: "Als jij een andere god dan mij hebt aangenomen, dan zal ik jou zeker tot een van de gevangenen maken."'),
(2962, 26, 30, 'Hij (Môesa) zei: "Zelfs als ik jou iets duidelijks kan laten zien?"'),
(2963, 26, 31, 'Hij (Fir\'aun) zei: "Breng het maar, als jij tot de waarachtigen behoort."'),
(2964, 26, 32, 'Toen wierp hij zijn staf neer en daarop werd het een duidelijke slang.'),
(2965, 26, 33, 'En hij strekte zijn hand uit en die werd wit voor de toeschouwers.'),
(2966, 26, 34, 'Hij (Fir\'aun) zei tegen de vooraanstaanden rondom hem: "Voorwaar, dit is zeker een bekwame tovenaar.'),
(2967, 26, 35, 'Hij wil jullie uit jullie land verdrijven met zijn tovenarij. Dus wat adviseren jullie?"'),
(2968, 26, 36, 'Zij zeiden: "Stel (de zaak van) hem en zijn broeder uit en stuur bijeenroepers naar de steden.'),
(2969, 26, 37, 'Zij zullen elke bekwame tovenaar bij jou brengen.'),
(2970, 26, 38, 'Zo werden de tovenaars verzameld op een afgesprokem lijd op een aangewezen dag.'),
(2971, 26, 39, 'En tot de mensen werd gezegd: "Zijn jullie nu bijeengekomen?'),
(2972, 26, 40, 'Moge wij de tovenaars volgen als zij de overwinnaars zijn."'),
(2973, 26, 41, 'Toen de tovenaars kwamen, zeiden zij tot Fir\'aun: "Krijgen we zeker een beloning, als wij de overwinnaars zijn?"'),
(2974, 26, 42, 'Hij zei: "Ja, jullie zullen dan tot de (mij) nabijen behoren."'),
(2975, 26, 43, 'Môesa zei tot hen: "Werpt maar wat jullie te werpen hebben."'),
(2976, 26, 44, 'Toen wierpen zij hun touwen en staven neer, terwijl zij zeiden: "Bij de eer van Fir\'aun: voorwaar, wij zullen zeker de overwinnaars zijn."'),
(2977, 26, 45, 'Toen wierp Môesa zijn staf neer, en toen verslond zij wat zij met hun bedrog hadden gemaakt.'),
(2978, 26, 46, 'Toen wierpen de tovenaan zich neer, knielend.'),
(2979, 26, 47, 'Zij zeiden: "Wij geloven in de Heer der Werelden.'),
(2980, 26, 48, 'De Heer van Môesa en Hârôen."'),
(2981, 26, 49, 'Hij (Fir\'aun) zei: "Geloven jullie hem voordat ik jullie toestemming geef? Voorwaar, hij is zeker jullie meerdere die jullie de tovenarij onderwees. En spoedig zullen jullie het weten: ik zal jullie handen en jullie voeten aan tegenovergestelde kanten afhakken en ik zal jullie allen kruisigen."'),
(2982, 26, 50, 'Zij (de tovenaars) zeiden: "Het deert (ons) niet. Voorwaar, wij zullen naar onze Heer terugkeren.'),
(2983, 26, 51, 'Voorwaar, wij verlangen dat Hij onze fouten vergeeft, omdat wij de eersten van de gelovigen zijn."'),
(2984, 26, 52, 'En wij openbaarden aan Môesa: "Reis in de nacht met Mijn dienaren: voorwaar, jullie zullen achtervolgd worden."'),
(2985, 26, 53, 'Toen stuurde Fir\'aun bijeenroepers de steden in.'),
(2986, 26, 54, '"Diegenen zijn zeker een kleine groep.'),
(2987, 26, 55, 'En voorwaar, zij hebben ons woedend gemaakt.'),
(2988, 26, 56, 'En voorwaar, wij zijn zeker allen voorzichtig."'),
(2989, 26, 57, 'Toen verdreven Wij hen van de tuinen en bronnen.'),
(2990, 26, 58, 'En de schatten en eervolle plaatsen.'),
(2991, 26, 59, 'Zo was het; en Wij deden de Kinderen van Israël het erven.'),
(2992, 26, 60, 'Toen achtervolgden zij hen bij zonsopgang.'),
(2993, 26, 61, 'En toen de twee groepen elkaar zagen, zeiden de metgezellen van Môesa: "Voorwaar, wij worden zeker bereikt!"'),
(2994, 26, 62, 'Hij (Môesa) zei: "Zeker niet voorwaar, mijn Heer is met mij, Hij zal mij lieden."'),
(2995, 26, 63, 'Toen openbaarden Wij aan Môesa: "Sla de zee met jouw staf." Toen spleet de zee en elk gedeelte was als een geweldige berg.'),
(2996, 26, 64, 'En Wij deden de anderen daar dichtbij komen.'),
(2997, 26, 65, 'En wij redden Môesa en allen die bij hem waren.'),
(2998, 26, 66, 'Vervolgens verdronken Wij de anderen.'),
(2999, 26, 67, 'Voorwaar, daarin is zeker een Teken, maar de meesten van hen zijn geen gelovigen.'),
(3000, 26, 68, 'En voorwaar, jouw Heer (O Moehammad) is zeker Hij, de Almachtige, de Meest Barmhartige.'),
(3001, 26, 69, 'En lees hun de geschiedenis van Ibrâhîm voor.'),
(3002, 26, 70, '(Gedenk) toen hij tot zijn vader en zijn volk zei: "Wat aanbidden jullie?"'),
(3003, 26, 71, 'Zij zeiden: "Wij aanbidden afgoden en wij zullen hen blijven aanbidden."'),
(3004, 26, 72, 'Hij (Ibrâhîm) zei: "Horen zij jullie, wanneer jullie hen aanroepen?'),
(3005, 26, 73, 'Of brengen zij jullie voordeel of berokkenen zij jullie nadeel?'),
(3006, 26, 74, 'Zij zeiden. "Wij vonden dat zelfs onze vaderen zo deden."'),
(3007, 26, 75, 'Hij (Ibrâhîm) zei: "Hebben jullie dain gezien wat jullie plegen te aanbidden?'),
(3008, 26, 76, 'Jullie en jullie vaderen die voorafgingen?'),
(3009, 26, 77, 'Voorwaar, zij zijn een vijand voor mij, (ik aanbid niemand) behalve de Heer der Werelden.'),
(3010, 26, 78, 'Degene Die mij geschapen heeft, Hij leidt mij.'),
(3011, 26, 79, 'En Hij is Degene Die mij voedt en Die mij te drinken geeft.'),
(3012, 26, 80, 'En wanneer ik ziek ben, is Hij het Die mij geneest.'),
(3013, 26, 81, 'Degene Die mij doet sterven en mi vervolgens doet leven.'),
(3014, 26, 82, 'En Degene van Wie ik hevig verlang dat Hij mijn zonden zal vergeven op de Dag des Oordeels.'),
(3015, 26, 83, 'Mijn Heer, schenk mij wijsheid en verenig mij met de rechtschapenen.'),
(3016, 26, 84, 'En maak mijn naam vermaard onder de lateren.'),
(3017, 26, 85, 'En maak mij één van de erfgenamen van de Tuin van de gelukzaligheid (het Paradijs).'),
(3018, 26, 86, 'En vergeef mijn vader, waal bij behoorde tot de dwalenden.'),
(3019, 26, 87, 'En verneder mij niet op de Dag waarop er wordt opgewekt.'),
(3020, 26, 88, 'Op de Dag, waarop rijkdom en zonen niet zullen baten.'),
(3021, 26, 89, 'Alleen bij (zal gebaat zijn), die naar Allah komt met een zuiver hart'),
(3022, 26, 90, 'En de Tuin wordt dichtbij de Moettaqôen gebracht.'),
(3023, 26, 91, 'En Djahîm (de Hel) wordt tentoongesteld aan de dwalenden.'),
(3024, 26, 92, 'En tot hen wordt gezegd: "Waar is het, wat jullie plachten te aanbidden?'),
(3025, 26, 93, 'Naast Allah? Kunnen zij jullie helpen of zichzelf helpen?'),
(3026, 26, 94, 'Dan worden zij hals over kop daarin geslingerd, zij en de dwalenden.'),
(3027, 26, 95, 'En de troepen van Iblîs (de Satan), allemaal.'),
(3028, 26, 96, 'Zij zeggen, terwijl zij met elkaar redetwisten:'),
(3029, 26, 97, '"Bij Allah, wij verkeerden zeker in een duidelijke dwaling.'),
(3030, 26, 98, 'Dat wij jullie (de afgoden) gelijkstelden met de Heer der Werelden.'),
(3031, 26, 99, 'En alleen de misdadigers hebben ons doen afdwalen.'),
(3032, 26, 100, 'En wij hebben geen voorsprekers,'),
(3033, 26, 101, 'En geen boezemvriend.'),
(3034, 26, 102, 'Was er voor ons maar een weg terug, dan zouden wij tot de gelovigen behoren."'),
(3035, 26, 103, 'Voorwaar, daarin is zeker een Teken, maar de meesten van hen zijn ongelovigen.'),
(3036, 26, 104, 'En voorwaar, jouw Heer (O Moehammad) is zeker Hij, de Almachtige, de Meest Barmhartige.'),
(3037, 26, 105, 'Het volk van Nôeh loochende de Boodschappers.'),
(3038, 26, 106, '(Gedenk) toen hun broeder Nôeh tot hen zei: "Vrezen jullie (Allah) niet?'),
(3039, 26, 107, 'Voorwaar, ik ben voor jullie een betrouwbare Boodschapper.'),
(3040, 26, 108, 'Vreest daarom Allah en gehoorzaamt mij.'),
(3041, 26, 109, 'Ik vraag jullie er geen beloning voor, mijn beloning berust alleen bij de Heer der Werelden.'),
(3042, 26, 110, 'Vreest daarom Allah en gehoorzaamt mij.'),
(3043, 26, 111, 'Zij zeiden: "Zouden wij jou volgen, terwijl de meest nederigen jou volgen?"'),
(3044, 26, 112, 'Hij (Nôeh) zei: "En ik heb geen kennis over wat zij deden.'),
(3045, 26, 113, 'Hun afrekening is slechts bij mijn Heer, als jullie het maar zouden beseffen.'),
(3046, 26, 114, 'Ik zal de gelovigen zeker niet wegjagen.'),
(3047, 26, 115, 'Ik ben slechts een duidelijke waarschuwer."'),
(3048, 26, 116, 'Zij zeiden: "Als jij er niet mee ophoudt, O Nôeh, dan behoor jij tot degenen die gestenigd worden!"'),
(3049, 26, 117, 'Hij (Nôeh) zei: "Mijn Heer, voorwaar mijn volk loochent mij.'),
(3050, 26, 118, 'Spreek daarom een oordeel uit tussen mij en hen. En red mij en de gelovigen die met mij zijn."'),
(3051, 26, 119, 'Toen redden Wij hem en degenen die met hem in het beladen schip waren.'),
(3052, 26, 120, 'En vervolgens verdronken Wij degenen die achterbleven (in de zondvloed).'),
(3053, 26, 121, 'Voorwaar, daarin is zeker een Teken, maar de meesten van hen zijn geen gelovigen.'),
(3054, 26, 122, 'En voorwaar, jouw Heer (O Moehammad) is zeker Hij, de Almachtige, de Meest Barmhartige.'),
(3055, 26, 123, 'Het volk van de \'Âd loochende de Boodschappers.'),
(3056, 26, 124, '(Gedenk) toen hun broeder Hôed tot hen zei: "Vrezen jullie Allah niet?'),
(3057, 26, 125, 'Voorwaar, ik ben voor jullie een betrouwbare Boodschapper.'),
(3058, 26, 126, 'Vreest daarom Allah en gehoorzaamt mij.'),
(3059, 26, 127, 'En ik vraag jullie er geen beloning voor, want mijn beloning berust alleen bij de Heer der Werelden.'),
(3060, 26, 128, 'Zouden jullie op elke heuvel een gebouw bouwen om jullie te vermaken?'),
(3061, 26, 129, 'En bouwen jullie paleizen in de hoop dat jullie eeuwig leven?'),
(3062, 26, 130, 'En als jullie toeslaan, slaan jullie toe als geweldenaars.'),
(3063, 26, 131, 'Vreest daarom Allah en gehoorzaamt mij.'),
(3064, 26, 132, 'En vrom Hem Die jullie dat geschonken heeft waarover jullie weten.'),
(3065, 26, 133, 'En Hij Die jullie vee en zonen schenkt.'),
(3066, 26, 134, 'En tuinen en bronnen.'),
(3067, 26, 135, 'Voorwaar, ik vrees voor jullie een bestraffing op de geweldige Dag."'),
(3068, 26, 136, 'Zij zeiden: "Voor ons is het hetzelfde of jij ons waarschuwt of dat jij niet tot de waarschuwers behoort.'),
(3069, 26, 137, 'Dit is slechts een gewoonte van de vroegeren.'),
(3070, 26, 138, 'En wij zullen niet behoren tot hen die gestraft worden."'),
(3071, 26, 139, 'Maar zij loochenden hem, dus vernietigden Wij hen. Voorwaar, daarin is zeker een Teken, maar de meesten van hen waren gew gelovigen.'),
(3072, 26, 140, 'En voorwaar, jouw Heer (O Moehammad) is zeker Hij, de Almachtige, de Meest Barmhartige.'),
(3073, 26, 141, 'Het volk van de Tsamôed loochende de Boodschappers.'),
(3074, 26, 142, '(Gedenk) toen hun broeder Shâlih tot hen zei: "Vrezen jullie (Allah) niet?'),
(3075, 26, 143, 'Voorwaar, ik ben voor jullie een betrouwbare Boodschapper.'),
(3076, 26, 144, 'Vreest daarom Allah en gehoorzaamt mij.'),
(3077, 26, 145, 'En ik vraag jullie er geen beloning voor, mijn beloning berust alleen bij de Heer der Werelden.'),
(3078, 26, 146, 'Zullen jullie in veiligheid gelaten worden temidden van wat hier is?'),
(3079, 26, 147, 'Temidden van tuinen en bronnen.'),
(3080, 26, 148, 'En akkerland en dadelpalmen met tere trossen.'),
(3081, 26, 149, 'En jullie houwen vaardig huizen uit in de bergen.'),
(3082, 26, 150, 'Vreest dan Allah en gehoorzaamt mij.'),
(3083, 26, 151, 'En geeft geen gehoor aan het bevel van de buitensporigen.'),
(3084, 26, 152, 'Degenen die verderf zaaien op de aarde en zich niet beteren."'),
(3085, 26, 153, 'Zij zeiden: "Voorwaar, jij behoort tot de betoverden.'),
(3086, 26, 154, 'Jij bent slechts een mens zoals wij. Breng daarom een Teken als jij tot de waarachtigen behoort."'),
(3087, 26, 155, 'Hij (Shâlih) zei: "Dit is een vrouwtjeskameel, zij heeft recht om te drinken en jullie hebben recht om te drinken, (ieder) op een vastgestelde dag.'),
(3088, 26, 156, 'En treft haar niet met kwaad, want dan zal de straf van een Geweldige Dag jullie treffen.'),
(3089, 26, 157, 'Toen slachtten zij haar, daarna werden zij berouwvollen.'),
(3090, 26, 158, 'Toen trof de bestraffing hen. Voorwaar, daarin is zeker een Teken, maar de meesten van hen zijn geen gelovigen.'),
(3091, 26, 159, 'En voorwaar, jouw Heer (O Moehammad) is zeker Hij, de Almachtige, de Meest Barmhartige.'),
(3092, 26, 160, 'Het volk van Lôeth loochende de Boodschappers.'),
(3093, 26, 161, '(Gedenk) toen hun broeder Lôeth tot hen zei: "Vrezen jullie (Allah) niet?'),
(3094, 26, 162, 'Voorwaar, ik ben voor jullie een betrouwbare Boodschapper.'),
(3095, 26, 163, 'Vreest daarom Allah en gehoorzaamt mij.'),
(3096, 26, 164, 'En ik vraag jullie er geen beloning voor, want mijn beloning berust alleen bij de Heer der Werelden.'),
(3097, 26, 165, 'Waarom benaderen jullie van de wereldbcwoners de mannen?'),
(3098, 26, 166, 'En verlaten jullie hen die jullie Heer als echtgenotes geschapen heeft? Jullie zijn beslist een overtredend volk!"'),
(3099, 26, 167, 'Zij zeiden: "O Lôeth, als jij er niet mee ophoudt, behoor jij tot de verdrevenen."'),
(3100, 26, 168, 'Hij in zei: "Voorwaar, ik behoor tot hen die jullie daden verachten.'),
(3101, 26, 169, 'Mijn Heer, red mij en mijn familie van wat zij doen."'),
(3102, 26, 170, 'En Wij hebben hem en zijn familie allen gered.'),
(3103, 26, 171, 'Behalve een oude vrouw onder de achterblijvers.'),
(3104, 26, 172, 'Toen vernietigden Wij de anderen.'),
(3105, 26, 173, 'En Wij deden een (vulkanische) regen op hen neerstromen, hoe slecht was de regen voor de gewaarschuwden!'),
(3106, 26, 174, 'Voorwaar, daarin is zeker een Teken, maar de meesten van hen zijn geen gelovigen.'),
(3107, 26, 175, 'En voorwaar, jouw Heer (O Moehammad), is zeker Hij, de Almachtige, de Meest Bamhartige.'),
(3108, 26, 176, 'De bewoners van Aikah loochenden de Boodschappers.'),
(3109, 26, 177, '(Gedenk) toen Sjoe\'aib tot hen zei: "Vrezen jullie (Allah) niet?'),
(3110, 26, 178, 'Voorwaar, ik ben voor jullie een betrouwbare Boodschapper.'),
(3111, 26, 179, 'Vreest daarom Allah en gehoorzaamt mij.'),
(3112, 26, 180, 'En ik vraag jullie er geen beloning voor, mijn beloning berust slechts bij de Heer der Werelden.'),
(3113, 26, 181, 'En geeft de volle maat een behoort niet tot hen die tekort doen.'),
(3114, 26, 182, 'En weegt met juiste weegschalen.'),
(3115, 26, 183, 'En benadeelt niet de mensen in hun zaken en verricht geen kwaad op aarde, als verderfzaaiers.'),
(3116, 26, 184, 'En vreest Degene Die jullie en de vroegere generaties geschapen heeft."'),
(3117, 26, 185, 'Zij zeiden: "Voorwaar, jij behoort slechts tot de betoverden.'),
(3118, 26, 186, 'En jij bent slechts een mens als wij ein wij vinden dat jij zeker tot de leugenaars behoort.'),
(3119, 26, 187, 'Laat dan eens een stuk van de hemel op ons vallen, als jij tot de waarachtigen behoort."'),
(3120, 26, 188, 'Hij zei: "Mijn Heer weet het beste wat jullie doen."'),
(3121, 26, 189, 'Maar zij loochenden hem, waarop een bestraffing hen trofop een zwaarbewolkte dag. Voorwaar, het was een bestraffing van een geweldige dag.'),
(3122, 26, 190, 'Voorwaar, daarin is zeker een Teken, maar de meesten van ben zijn geen gelovigen.'),
(3123, 26, 191, 'En voorwaar, jouw Heer (O Moehammad) is zeker Hij, de Almachtige, de Meest Bamhartige.'),
(3124, 26, 192, 'En voorwaar, hij (de Koran) is zeker een neerzending van de Heer der Werelden.'),
(3125, 26, 193, 'Met hem (de Koran) daalde de getrouwe Geest (Djibrîl) neer.'),
(3126, 26, 194, 'Op jouw hart (O Moehammad), opdat jij tot de waarschuwers behoort.'),
(3127, 26, 195, 'In een duidelijke Arabische taal.'),
(3128, 26, 196, 'En voorwaar, hij (de Koran) is zeker (aangekondigd) in de Schriften van de vroegeren.'),
(3129, 26, 197, 'Is het voor hen dan geen teken dat de geleerden van de Kinderen van Israël hem kennen?'),
(3130, 26, 198, 'En als Wij hem am de niet-Arabieren hadden doen neerdalen.'),
(3131, 26, 199, '(En als) hij hem dan aan ben voorgedragen had, dan hadden zij er niet in geloofd.'),
(3132, 26, 200, 'Op deze wijze deden Wij hem binnendringen in de barten van de misdadigers.'),
(3133, 26, 201, 'Zij zullen er niet in geloven totdat zij de pijnlijke bestraffing zien.'),
(3134, 26, 202, 'Die plotseling tot ben zal komen, terwijl zij het niet beseffen.'),
(3135, 26, 203, 'Dan zeggen zij: "Krijgen wij uitstel?"'),
(3136, 26, 204, 'Vragen zij dan dat Onze bestraffing bespoedigd wordt?'),
(3137, 26, 205, 'Wat denk jij dan, als Wij hun (enige) jaren laten genieten?'),
(3138, 26, 206, 'En daarop tot hen komt wat beloofd was?'),
(3139, 26, 207, 'Het zal hun niet baten, wat hun aan genot gegeven was.'),
(3140, 26, 208, 'En Wij hebben geen stad vernietigd zonder dat er voor haar waarschuwers waren geweest.'),
(3141, 26, 209, 'Als een waarschuwing: en Wij weren geen onrechtvaardigen.'),
(3142, 26, 210, 'En hij (de Koran) is niet door de Satans neergedaald.'),
(3143, 26, 211, 'Het past hun niet en zij zijn er niet toe in staat.'),
(3144, 26, 212, 'Voorwaar, van het horen (ervan) zijn zij zeker buitengesloten.'),
(3145, 26, 213, 'Roept dus geen andere goden naast Allah aan, anders zal jij tot de bestraften behoren.'),
(3146, 26, 214, 'En waarschuw jouw naaste familieleden.'),
(3147, 26, 215, 'En wees bescheiden eva nederig tegenover de gelovigen die jou volgen.'),
(3148, 26, 216, 'En als zij jou dan ongehoorzam zijn, zeg dan: "Ik ben onschuldig aan wat jullie doen."'),
(3149, 26, 217, 'En vertrouw op de Almachtige, de Meest Barmhartige.'),
(3150, 26, 218, 'Degene Die jou ziet als jij staat (te bidden).'),
(3151, 26, 219, 'En jouw bewegingen (ziet) onder de knielenden.'),
(3152, 26, 220, 'Voorwaar, Hij is de Alhorende, de Alwetende.'),
(3153, 26, 221, 'Zal ik jou vertellen tot wie de Satans neerdalen?'),
(3154, 26, 222, 'Zij dalen neer tot elke zondige leugenaar.'),
(3155, 26, 223, 'Zij luisteren nam het gesprokene en de meesten van hen zijn leugenaars.'),
(3156, 26, 224, 'En de dichters; de dwalenden volgen hen.'),
(3157, 26, 225, 'Zie jij niet dat zij rusteloos ronddwalen in iedere vallei?'),
(3158, 26, 226, 'En dat zij zeker zeggen wat zij niet doen?'),
(3159, 26, 227, 'Behalve degenen die geloven en goede daden verrichten en Allah vaak gedenken. En zij overwinnen nadat hun onrecht is aangedaan. En degenen die onrecht pleegden zullen spoedig weten tot welke plaats van terugkeer zij zullen terugkeren!');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'Tha Sîn. Dit zijn de Verzen van de Koran en een duidelijk Boek.'),
(3161, 27, 2, 'Als Leiding en een verheugende tijding voor de gelovigen.'),
(3162, 27, 3, 'Degenen die de shalât verrichten en de zakât geven, en die overtuigd zijn van het Hiernamals.'),
(3163, 27, 4, 'Voorwaar, voor degenen die niet in het Hiernamaals geloven, doen Wij hun daden schoen toeschijnen, daarop verkeren zij rusteloos in hun dwaling.'),
(3164, 27, 5, 'Zij zijn degenen voor wie er de slechte bestraffing is en in het Hiernamaals zijn zij de grootste verliezers.'),
(3165, 27, 6, 'En voorwaar, jij ontvangt de Koran van de Zijde van de Alwijze, de Alwetende.'),
(3166, 27, 7, '(Gedenk) toen Môesa tot zijn familie zei: "Ik heb een vuur gezien. Ik zal jullie er snel over berichten, of ik breng jullie er een fakkel van, zodat jullie je kunnen verwarmen."'),
(3167, 27, 8, 'Maar toen hij er aankwam, werd bij geroepen: "Gezegend is hij (Môesa) die op de plaats van het vuur is en die er omheen zijn. En Heilig is Allah, de Heer der Werelden.'),
(3168, 27, 9, 'O Môesa, Ik ben het, Allah, de Almachtige, de Alwijze.'),
(3169, 27, 10, 'Werp jouw staf neer." Maar toen hij deze zag, bewoog zij alsof het een slang was, toen keerde hij haastig omen keek niet om. (Allah zei:) "O Môesa, wees niet bang, want voorwaar, bij Mij zijn de Boodschappen niet bang."'),
(3170, 27, 11, 'En wie onrecht pleegde en vervolgens een slechte daad voor een goede daad inruilde: dan ben Ik waarlijk Vergevensgezind, Meest Barmhartig.'),
(3171, 27, 12, '(Allah zei tot Môesa:) "En stop jouw hand onder jouw kraag en deze schijnt wit, zonder ziek te zijn: als één van de negen wonderen tegen Fir\'aun en zijn volk. Voorwaar, zij zijn een zwaw zondig volk."'),
(3172, 27, 13, 'Toen Onze Tekenen ter verduidelijking tot hen kwamen, zeiden zij: "Dit is duidelijke tovenarij."'),
(3173, 27, 14, 'En zij ontkenden ze, hoewel zij zelf ervan overtuigd waren, uit onrechtvaardigheid en hoogmoed. Zie dan hoe het einde van de verderfzaaiers was!'),
(3174, 27, 15, 'En voorzeker, Wij gaven kennis aan Dâwôed en Soelaimân, en beiden zeiden zij: "Alle lof zij Allah, Degene Die ons heeft bevoorrecht boven velen van Zijn gelovige dienaren."'),
(3175, 27, 16, 'En Soelaimân volgde Dâwôed op. En hij zei: "O mensen, aan ons is de taal van de vogels onderwezen, en ons is alles gegeven. Voorwaar, dit is zeker de duidelijke gunst."'),
(3176, 27, 17, 'En voor Soelaimân werden zijn troepen verzameld: de Djinn\'s en de mensen en de vogels, en zij werden in rijen opgesteld.'),
(3177, 27, 18, 'Totdat zij langs de vallei van de mieren kwamen, en een mier zei: "O jullie mieren, gaat jullie woningen binnen, anders zullen Soelaimân en zijn troepen jullie vertrappen, zonder dat zij het beseffen!"'),
(3178, 27, 19, 'Toen glimlachte hij (Soelaimân) om haar woorden, en zei: "Mijn Heer, maak mij dankbaar voor Uw gunst die U mij en mijn ouders schonk, en doe mij goede daden verrichten, waar U Uw welgevallen op doet rusten. En laat mij, door Uw Barmhartigheid, behoren tot Uw rechtschapen dienaren."\''),
(3179, 27, 20, 'En hij inspecteerde de vogels, toen zei hij: "Hoe komt het dat ik de hop niet zie? Of behoort hij tot de afwezigen?'),
(3180, 27, 21, 'Ik zal hem zeker een strenge bestraffing opleggen of ik zal hem zeker slachten, of hij zal bij mij moeten komen met een duidelijke reden."'),
(3181, 27, 22, 'Daarop bleef hij niet lang afwezig, en hij zei: "Ik weet iets waarvan jij nog niet weet. En ik breng jou uit Saba\' overtuigende berichten.'),
(3182, 27, 23, 'Voorwaar, ik heb gezien dat een vrouw over hen heerst en zij beschikt over alle zaken en zij heeft een geweldige troon.'),
(3183, 27, 24, 'Ik heb gezien dat zij en haar volk knielen voor de zon, naast Allah. En dat de Satan hun hun daden schoon doet toeschijnen en hen daarmee afleidt van de Weg. Zij volgen dus geen Leiding.'),
(3184, 27, 25, 'Zodat zij zich niet voor Allah neerknielen, Degene Die voortbrengt wat verborgen is in de hemelen en op de aarde, en Die weet wat jullie verbergen en wat jullie openlijk doen.'),
(3185, 27, 26, 'Allah, er is geen god dan Hij, de Heer van de Geweldige Troon."'),
(3186, 27, 27, 'Hij (Soelaimân) zei: "Wij zullen zien of jij de waarheid verteld hebt of dat jij tot de leugenaars behoort.'),
(3187, 27, 28, 'Ga heen met deze brief en geef hem aan hen; wend je dan van hen af en zie wat zij terugzeggen."'),
(3188, 27, 29, 'Zij zei: "O vooraanstanden, aai mij is een edele brief bezorgd.'),
(3189, 27, 30, 'Voorwaar, hij komt van Soelaimân en voorwaar, hij (luidt): "In de Naam van Allah, de Erbarmer, de Meest Barmhartige.'),
(3190, 27, 31, 'Verhef jezelf niet in hoogmoed boven mij en komt maar mij als overgegevenen."'),
(3191, 27, 32, 'Zij zei: "O vooraanstanden, geeft mij raad in mijn zaak. Ik nam geen besluit over een zaak voordat jullie er getuigen van waren."'),
(3192, 27, 33, 'Zij zeiden: "Wij beschikken over macht en wij beschikken over grote moed, maar de zaak berust bij U, zie daarom maar wat U beveelt."'),
(3193, 27, 34, 'Zei zij: "Voorwaar, wanneer de koningen een stad binnengingen, zaaiden zij daar verderf en maakten zij de edelen onder haar inwoners tot vernederden; en zo handelen zij.'),
(3194, 27, 35, 'En voorwaar, ik zal hun een geschenk sturen en zien waarmee de gezanten temgkeren."'),
(3195, 27, 36, 'Maar toen hij (de gezant) bij Soelaimân kwam, zei hij: "Zouden jullie mij met bezit steunen? Dat wat Allah mij gegeven heeft is beter dan wat Hij jullie gegeven heeft. Maar jullie verheugen je over jullie geschenk.'),
(3196, 27, 37, 'Keer naar Hen terug, wij komen zeker met troepen naar hen, waartegen zij geen verzet kunnen bieden. En Wij zullen hen zeker daaruit verdrijven, vemederd, terwijl zij onderworpenen zijn.'),
(3197, 27, 38, 'Hij zei: "O vooraanstanden, wie van jullie brengt mij haar troon, voordat zij naar mij komen als overgegevenen."'),
(3198, 27, 39, 'Een Ifrît van de Djinn\'s zei: "Ik zal hem naar jou brengen voordat jij van jouw plaats opstaat en voorwaar, ik ben zeker een betrouwbare kracht daartoe."'),
(3199, 27, 40, 'Degene met kennis van Schrift zei: "ik zal hein in een oogwenk naar jou toebrengen." En toen hij hem vóór zich geplaatst zag, zei hij: "Dit is een gunst van mijn Heer, om mij op de proef te stellen of ik dankbaar zal zijn of ik ondankbaar zal zijn. Maar hij die dankbaar is, is slechts dankbaar voor zichzelf. En wie ondankbaar is: voorwaar, mijn Heer is Behoefteloos. Edel."'),
(3200, 27, 41, 'Hij (Soelaimân) zei: "Maak haar troon onherkenbaar voor haar, zodat wij zien of zij herkent of dat zij behoort tot degenen die niet herkennen."'),
(3201, 27, 42, 'Toen zij aankwam, werd haar gezegd: "Es dit jouw troon?" Zij zei: "Het is alsof hij het is." (Soelaimân zei:) "Aan ons is vóór haar al kennis gegeven en wij gaven ons (aan Allah) over."'),
(3202, 27, 43, 'En zij werd verhinderd door wat zij naast Allah aanbad. Voorwaar, zij behoorde tot het ongelovige volk.'),
(3203, 27, 44, 'Er werd tegen haar gezegd: "Treed het paleis binnen." Toen zij het zag, dacht zij dat het een waterplas was en zij trok (haar gewaad) op van haar benen. Hij zei: "Het is een paleis dat betegeld is met glas. Zij zei: "Mijn Heer, voorwaar, ik heb mijzelf onrecht aangedaan en ik geef mij met Soelaimân over aan Allah, de Heer der Werelden."'),
(3204, 27, 45, 'En voorzeker, Wij zonden hun broeder Shâlih naar de Tsamôcd. (Hij zei:) "Aanbidt Allah!" Toen werden zij twee groepen die met elkaar redetwistten.'),
(3205, 27, 46, 'Hij zei: "O mijn volk, waarom zouden jullie het slechte vóór het goede bespoedigen? Hadden jullie maar om vergeving gevraagd. Hopelijk worden jullie begenadigd."'),
(3206, 27, 47, 'Zij zeiden: "Wij verwachtten van jou en degenen die met jou zijn een slecht lot." Hij zei: "Jullie lot is bij Allah. Jullie zijn beslist een volk dat op de proef gesteld wordt."'),
(3207, 27, 48, 'En in de stad was een groep van negen, die verderf zaaide op aarde en die zich niet beterde.'),
(3208, 27, 49, 'Zij zeiden: "Zweert onder elkaar bi Allah dat wij zeker een nachtelijke aanval doen tegen hem(Shâlih) en zijn familie; daarna zeggen wij zeker tot zijn verwant: "Wij waren geen getuigen van de uitroeiing van zijn familie, en voorwaar, wij zijn waarachtigen."'),
(3209, 27, 50, 'En zij beraamden een list en Wij beraamden een list, terwijl zij het niet beseften.'),
(3210, 27, 51, 'En zie hoe het einde vaa hun list was: dat Wij hen vernietigden en hun vo Ik, allemaal.'),
(3211, 27, 52, 'Dat zijn nu hun huizen, tot ruïnes geworden, omdat zij onrecht pleegden. Voorwaar, daarin is zeker een Teken voor een volk dat begrijpt.'),
(3212, 27, 53, 'En Wij redden hen die geloofden en zij plachten (Allah) te vrezen.'),
(3213, 27, 54, 'En (gedenkt) Lôeth, toen hij tot zijn volk zei: "Begaan jullie gruweldaden hoewel jullie het inzien?'),
(3214, 27, 55, 'Waarom benaderen jullie uit begeerte mannen in plaats van vrouwen? Jullie zijn beslist een onwetend volk."'),
(3215, 27, 56, 'Maar het antwoord van zijn volk was slechts dat zij zeiden: "Verdrijft de familie van Lôeth uit jullie stad: voorwaar, zij zijn mensen die zich reinigen."'),
(3216, 27, 57, 'Toen redden Wij hem en zijn familie, behalve zijn vrouw: Wij bepaalden dat zij tot de achterblijvers behoorde.'),
(3217, 27, 58, 'En Wij deden op hen een (vulkanische) regen neerdalen: en slecht was de regen voor de gewaarschuwden.'),
(3218, 27, 59, 'Zeg (O Moehammad): "Alle lof zij Allah en vrede voor Zijn dienaren en degenen die Hij verkoos." Is Allah beter, of wat zij (Hem) aan deelgenoten toekennen?'),
(3219, 27, 60, 'Of wie heeft de hemelen en de aarde geschapen en water uit de hemel neergezonden, waarmee Wij prachtige tuinen doen groeien? Jullie zijn niet in staat haar bomen te doen groeien. Is er een god naast Allah? Welnee, zij (die dit beweren) behoren tot een volk dat afwijkt (van de Waarheid).'),
(3220, 27, 61, 'Of wie heeft de aarde als een verblijfplaats gemaakt en daardoor rivieren getrokken en daarop stevige bergen geplaatst en een scheiding tussen de twee zeeën gemaakt? Is er een god naast Allah? Maar de meesten van hen weten het niet.'),
(3221, 27, 62, 'Of wie heeft de in nood verkerende verhoord, wanneer hij Hem aanroept, en het onheil van hem weggenomen en jullie gevolmachtigden gemaakt op de aarde? Is er een god naast Allah? En weinig is het dat jullie je laten vermanen!'),
(3222, 27, 63, 'Of wie heeft jullie geleid in de duisternissen op het land en op de zee en wie heeft de winden als dragers van verheugende tijdingen voor Zijn Barmhartigheid uitgestuurd? Is er een god naast Allah? Verheven is Allah boven wat zij Hem aan deelgenoten toekennen!'),
(3223, 27, 64, 'Of wie is de schepping begonnen en heeft haar vervolgens herhaald; en wie heeft in jullie onderhoud uit de hemel en de aarde voorzien? Is er een god naast Allah? Zeg (O Moehammad): "Brengt jullie bewijs, als jullie waarachtigen zijn."'),
(3224, 27, 65, 'Zeg: "Niemand kent het onwaarneembare in de hemelen en op dc aarde, behalve Allah." En zij weten niet waneer zij worden opgewekt.'),
(3225, 27, 66, 'Hun kennis omvat het Hiernamaals niet. Zij verkeren hierover zelfs in onzekerheid, zij zijn er zelfs blind voor.'),
(3226, 27, 67, 'En degenen die ongelovig zijn zeggen: "Is het waar, dat als wij en onze vaderen tot aarde zijn geworden, wij zeker worden opgewekt?'),
(3227, 27, 68, 'Voorzeker, dit is ons en onze vaderen vroeger beloofd. Dit zijn slechts fabels van de vroegeren."'),
(3228, 27, 69, 'Zeg (O Moehammad): "Reist rond op de aarde en ziet hoe het einde van de misdadigers was."'),
(3229, 27, 70, 'En treur niet om hen, en wees ook niet bezorgd over wat zij aan listen beramen.'),
(3230, 27, 71, 'En zij (de ongelovigen) zeggen: "Wanneer zal deze belofte (vervuld worden), als jullie warachtigen rijn?"'),
(3231, 27, 72, 'Zeg (O Moehammad): "Moge een deel van wat jullie wensen te bespoedigen bijna bij jullie zijn."'),
(3232, 27, 73, 'En voorwaar, jouw Heer bezit zeker gunsten voor de mensen, maar de meesten van en zijn niet dankbaar.'),
(3233, 27, 74, 'En voorwaar, jouw Heer weet zeker wat hun harten verbergen en wat zij openlijk doen.'),
(3234, 27, 75, 'En niets is onwaameembaar in de hemel en op de aarde, of het staat in een duidelijk Boek.'),
(3235, 27, 76, 'Voorwaar, deze Koran vertelt aan de Kinderen van Israël waarover zij het meeste redetwisten.'),
(3236, 27, 77, 'En voorwaar, hij is zeker Leiding en Barmhartigheid voor de gelovigen.'),
(3237, 27, 78, 'Voorwaar, jouw Heer zal tussen hen oordelen met Zijn Oordeel. En Hij is de Almachtige, de Alwetende.'),
(3238, 27, 79, 'Vertrouw daarom op Allah. Voorwaar, jij (O Moehammad) bent op (het Pad van) de duidelijke Waarheid.'),
(3239, 27, 80, 'Voorwaar, jij bent niet in staat de doden te doen horen en de doven de roep te doen horen wanneer zij de rug toekeren.'),
(3240, 27, 81, 'En jij kunt ook niet de blinden van hun dwaling wegleiden en jij kunt alleen hen doen luisteren die in Onze Verzen geloven, waarop zij zich (aan Allah) overgeven.'),
(3241, 27, 82, 'En wanneer het woord hen treft, dan brengen Wij voor hen een levend wezen uit de aarde tevoorschijn om tot hen te spreken: voorwaar, de mensheid was niet van Onze Tekenen overtuigd.'),
(3242, 27, 83, 'En (gedenk) op die Dag zullen Wij uit iedere gemeenschap een groep verzamelen van hen die Onze Tekenen loochenden, zij worden dan in rijen opgesteld.'),
(3243, 27, 84, 'Totdat, wanneer zij komen, Hij zal zeggen: "Hebben jullie Mijn Tekenen geloochend, die jullie niet met kennis omvatten? Of wat was het, dat jullie plachten te doen?"'),
(3244, 27, 85, 'En het woord zal hen treffen omdat zij onrecht pleegden, en zij spreken niet.'),
(3245, 27, 86, 'Hebben zij niet gezien dat Wij de nacht voor hen hebben gemaakt om erin uit te rusten en de dag om hen licht te geven? Voorwaar, daarin zijn zeker Tekenen voor een volk dat gelooft.'),
(3246, 27, 87, 'En (gedenkt) de Dag waarop op de Bazuin geblazen wordt: wie er dan in de hemelen en op de aarde zijn, zullen in angst verkeren, behalve voor wie Allah het (anders) wil. Eii allen zullen nederig tot Hem komen.'),
(3247, 27, 88, 'En jij ziet de bergen, waarvan jij denkt dat zij vaststaan, terwijl zij voorbijgaan als wolken. Een werk van Allah, Degene Die alle zaken nauwkeurig geregeld heeft. Voorwaar, Hij is Alwetend over wat jullie doen.'),
(3248, 27, 89, 'En wie met het goede kwam, zal beter dan dat krijgen en zij zullen op die Dag veilig zijn voor angst.'),
(3249, 27, 90, 'En wie met het slechte kwam, zij zullen met hun gezicht in de Hel geworpen worden. Jullie worden slechts beloond voor wat jullie plachten te doen.'),
(3250, 27, 91, '(Zeg, O Moehammad:) "Voorwaar, het is mij bevolen om de Heer van deze stad (Mekkah) te aanbidden, die Hij wijdde. En aan Hem behoren alle zaken, en het is mij bevolen om tot de Moslims te behoren.'),
(3251, 27, 92, 'En de Koren voor te dragen. Hij die dan de Leiding volgt, volgt die voor zichzelf." En wie dan dwaalt, zeg dan (tot hen): "Voorwaar, ik behoor slechts tot de waarschuwers."'),
(3252, 27, 93, 'En zeg: "Alle lof zij Allah. Hij zal jullie Zijn Tekenen laten zien en jullie zullen ze herkennen." En jouw Heer is niet onachtzaam omtrent wat jullie doen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'Tha Sîn Mîm.'),
(3254, 28, 2, 'Dit zijn de Verzen van het duidelijke Boek.'),
(3255, 28, 3, 'Wij dragen jou van de geschiedenissen van Môesa en Fir\'aun voor, volgens de Waarheid, voor een volk dat gelooft.'),
(3256, 28, 4, 'Voorwaar, Fir\'aun wag hoogmoedig in het land en hij verdeelde haar mensen in partijen. Hij onderdrukte een groep van hen: hij slachtte hun zonen en hij liet hun vrouwen leven. Voorwaar, hij behoorde tot de verderfzaaiers.'),
(3257, 28, 5, 'En Wij wilden de onderdrukten in het land een gunst schenken. En Wij maakten hen tot leiders en Wij maakten ben tot erfgenamen.'),
(3258, 28, 6, 'En Wij vestigden hen op aarde en Wij toonden Fir\'aun,. en Hâmân en hun troepen onder hen dat waar zij bevreesd voor waren.'),
(3259, 28, 7, 'En Wij inspireerden aan de moeder van Môesa: "Zoog hem", maar als jij voor hem vreest, werp hem dan in de rivier, en vrees niet en treur niet: voorwaar, Wij zullen hem bij jou terugbrengen en hem tot één van de Boodschappers maken.'),
(3260, 28, 8, 'Toen pikten de mensen van Fir\'aun hem op (uit de rivier), zodat hij een vijand van hen zou worden en een bron van droefenis. Voorwaar, Fir\'aun, Hâmân en hun legers waren zondaren.'),
(3261, 28, 9, 'En de vrouw van Fir\'aun zei: "Hij is een verkoeling voor mijn en jouw oog. Doodt hem niet, moge hij ons baten, of kunnen wij hem als zoon annemen," En zij beseften niet.'),
(3262, 28, 10, 'En het hart van de moeder van Môesa werd leeg, en zij had het (geheim) bijna onthuld, als Wij haar hart niet versterkt hadden, zodat zij tot de gelovigen behoorde.'),
(3263, 28, 11, 'En zij zei tegen zijn (Môesa) zuster: "Volg hem." En zo bekeek zij hem op een afstand, terwijl zij het niet beseften.'),
(3264, 28, 12, 'En Wij hebben hem daarvóór zoogmoeders verboden. En zij (zijn zuster) zei (tegen de familie van Fir\'aun): "Zal ik jullie een huisgezin wijzen dat hem voor jullie zal verzorgen en hem opvoeden?"'),
(3265, 28, 13, 'Toen deden Wij hem tot zijn moeder terugkeren, opdat haar oog verkoeld werd en zij niet bedroefd was, en opdat zij wist dat de belofte van Allah waar is. Maar de meesten van hen weten niet.'),
(3266, 28, 14, 'En toen hij zijn volle kracht had bereikt, en volgroeid was, gaven Wij hem wijsheid en kennis. En zo belonen Wij de weldoeners.'),
(3267, 28, 15, 'En hij kwam de stad binnen op een tijd dat haar mensen onachtzaam waren, en hij vond daar twee mannen die vochten: de één van zijn groep (de Kinderen van Israël) en de ander van zijn vijanden (de mensen van Fir\'aun). Daarop vroeg degene van zijn groep hem om hulp tegen degene van zijn vijand. Toen sloeg Môesa hem met zijn vuist en doodde hem (per ongeluk). Hij zei: "Dit behoort tot het werk van de Satan: voorwaar, hij is een vijand die duidelijk doet dwalen."'),
(3268, 28, 16, 'Hij zei: "Mijn Heer, voorwaar, ik heb mijzelf onrecht aangedaan, dus vergeef mij." Daarop vergaf Hij hem. Voorwaar, Hij is de Vergevensgezinde, de Meest Barmhartige.'),
(3269, 28, 17, 'Hij (Môesa) zei: "Mijn Heer, door de gunst die U mij geschonken heeft, zal ik nooit meer een helper zijn voor de misdadigers.'),
(3270, 28, 18, 'Toen werd hij bang in de stad en was op zijn hoede, toen daar degene was die de vorige dag om zijn hulp gevraagd had. Hij vroeg hem (opnieuw) om hulp. Môesa zei tot hem: "Voorwaar, jij bent zeker een duidelijk dwalende."'),
(3271, 28, 19, 'Maar toen hij (Môesa) degene wilde grijpen, die een vijand van hen beiden was, zei deze: "O Môesa, wil jij mij vermoorden, zoals jij gisteren een ziel gedood hebt? Jij wenst slechts een tiran te worden op de aarde en jij wenst niet tot de verzoeners te behoren."'),
(3272, 28, 20, 'En een man kwam aanrennen van het andere einde van de stad, terwijl hij riep: "De vooraanstaanden beramen een list om jou te vermoorden. Vertrek daarom: voorwaar, ik behoor tot de raadgevers voor jou."'),
(3273, 28, 21, 'Daarom vertrok hij van daar, vrezend en op zijn hoede. Hij zei: "Mijn Heer, red mij van het onrechtvaardige volk."'),
(3274, 28, 22, 'En toen hij in de richting van Madyan ging, zei hij: "Moge mijn Heer mij op de rechte Weg leiden."'),
(3275, 28, 23, 'En toen hij het water van Madyan bereikte, vond hij daar een groep mensen die hun vee te drinken gaf. En Hij trof naast hen twee vrouwen aan die hun vee tegenhielden. Hij zei. "Wat is er met jullie aan de hand?" Zij zeiden: "Wij laten het vee pas drinken als de herders weggaan en onze vader is een zeer oude man."'),
(3276, 28, 24, 'Toen gaf hij hun vee voor hen te drinken. Toen trok hij zich terug in de schaduw, en zei: "Mijn Heer, ik heb behoefte aan het goede dat U tot mij neerzendt."'),
(3277, 28, 25, 'Toen kwam een van de twee (vrouwen) verlegen naar heen toelopen, en zei: "Voorwaar, mijn vader roept jou om te komen, om jou te belonen, omdat jij ons vee te drinken hebt gegeven." En toen hij bij hem kwam en hem het verhaal vertelde, zei hij: "Wees niet bang, jij hebt je gered van het onrechtvaardige volk."'),
(3278, 28, 26, 'Een van de twee (vrouwen) zei: "O vader, neem hem toch in dienst. Voorwaar, de beste die je in dienst kan nemen is de betrouwbare sterke.'),
(3279, 28, 27, 'Hij zei: "Voorwaar, ik wens dat jij met een van deze dochters van mij trouwt, op voorwaarde dat jij ach tjaar bij mij in dienst komt. Maar als jij er tien vervult, dan is dat aan jou. Ik wil het jou niet moeilijk maken, jij zult bevinden dat ik, als Allah het wil, tot de oprechten behoor."'),
(3280, 28, 28, 'Hij (Môesa) zei: "Laat dit zo zijn tussen mij en jou. Welke van de twee termijnen ik ook vervul, laat er geen onvrede tegen mij zijn en Allah is Berschermer over wat wij zeggen."'),
(3281, 28, 29, 'En toen Môesa de termijn vervuld had, en niet zijn familie reisde, zag hij aan de zijkant van (de berg) Thôer vuur. Hij zei tot zijn familie: "Blijft, want ik heb een vuur gezien. Moge ik jullie daarover nieuws brengen of een fakkel van het vuur meenemen, hopelijk verwarmen jullie (je ermee)."'),
(3282, 28, 30, 'Maar toen hij daar ankwam, werd hij geroepen vanaf de rechterzijde van de vallei, op de gezegende plaats, van bij een boom: "O Môesa, voorwaar, Ik ben Allah, Heer der Werelden!'),
(3283, 28, 31, 'En werp jouw staf neer!" Toen hij deze zag, bewoog die alsof zij een slang was, toen keerde hij zich haastig om, en koek niet om. (Allah zei:) "O Môesa, kom naderbij, en wees niet bang: voorwaar, jij behoort tot hen die veilig zijn.'),
(3284, 28, 32, 'Stop jouw hand onder jouw kraag, en hij zal wit schijnen, zonder ziek te zijn. En hou jouw arm tegen jouw zijde tegen de angst. Dit zijn twee Tekenen van jouw Heer tegen Fir\'aun en zijn vooraanstanden. Voorwaar, zij zijn een zwaar zondig volk."\''),
(3285, 28, 33, 'Hij (Môesa) zei: "Mijn Heer, voorwaar, ik heb iemand van hen doodgeslagen en ik ben bang dat zij mij doden.'),
(3286, 28, 34, 'Mijn broeder Hârôen kan beter spreken dan ik. Stuur hem daarom met mij als een helper die mij bevestigt: voorwaar, ik ben bang dat zij mij loochenen."'),
(3287, 28, 35, 'Hij (Allah) zei: "Wij zallen jou versterken met jouw broeder en Wij zullen jullie beiden gezag geven, zodat zij jullie niet kunnen bereiken. (Vertrek) met Onze Tekenen, jullie beiden en degenen die jullie volgen zullen overwinnaars zijn."'),
(3288, 28, 36, 'En toen Môesa tot hen kwam met Onze duidelijke Tekenen, zeiden zei: "Dit is slechts verzonnen tovenarij, wij hebben dit niet gehoord van onze voorvaderen."'),
(3289, 28, 37, 'En Môesa zei: "Mijn Heer weet het beste wie met de Leiding van Zijn Zijde kwam en voor wie (het Paradijs) het einde van het huis (van deze wereld) is. Voorwaar, de onrechtvaardigen zullen niet welslagen."'),
(3290, 28, 38, 'En Fir\'aun zei: "O vooraanstaanden, ik weet geen andere god dan ikzelf voor jullie. Steek daarom voor mij, O Hâmân, een vuur aan (van een oven om) klei (te bakken voor de stenen van) een toren voor mij, moge ik opstijgen naar de god van Môesa. En voorwaar, ik veronderstel zeker dat hij tot de leugenaars behoort."'),
(3291, 28, 39, 'En hij en zijn troepen warm hoogmoedig op aarde, zonder het recht te hebben, en zij veronderstelden dat zij niet tot Ons zouden worden teruggekeerd.'),
(3292, 28, 40, 'Wij grepen daarom hem en zijn troepen, toen wierpen Wij hen in de zee. Zie hoe het einde was van de onrechtvaardigen.'),
(3293, 28, 41, 'En Wij maakten hein leiders, die oproepen tot de Hel. En op de Dag der Opstanding zullen zij niet geholpen worden.'),
(3294, 28, 42, 'En Wij vervolgden hen in dit leven met een vloek. En op de Dag der Opstanding zullen zij tot de verachten behoren.'),
(3295, 28, 43, 'En voorzeker, Wij gaven Môesa de Schrift (de Taurât), nadat Wij de eerdere generaties vernietigd hadden, als een verbeldering voor de mensen en als Leiding en Barmhartigheid. Hopelijk zullen zij zich taten vermanen.'),
(3296, 28, 44, 'En jij (O Moehammad) was niet op de westkant van (de berg) "Thôer, toen Wij aan Môesa het bevel openbaarden, en jij behoorde niet toT de gettuigen.'),
(3297, 28, 45, 'Maar Wij brachten nieuwe generaties voort en hun levens werden verlengd. En jij woonde niet bij de bewoners van Madyan om Onze Verzen aan hen voor te dragen. En Wij zijn het Die (Boodschappers) zouden.'),
(3298, 28, 46, 'En jij was niet op de zijkant van Thôer toen Wij riepen. Maar het is dank zij de Barmhartigheid van jouw Heer dat jij er bent om een volk, waartoe vóór jou geen waarschuwer kwam, te waarschuwen. Hopelijk zullen zij zich laten vermanen.'),
(3299, 28, 47, 'Opdat, wanneer een ramp door wat hun handen eerder verricht hebben hen treft, zij niet zouden zeggen: "Onze Heer, had U maar een Boodschapper gestuurd, dan zouden Wij Uw Tekenen gevolgd hebben en wij zouden tot de gelovigen behoren."'),
(3300, 28, 48, 'Toen dan de Waarheid van Onze Zijde tot hen kwam, zeiden zij: "Was hem (Moehammad) maar hetzelfde gegeven als wat Môesa gegeven was! Maar loochenden zij dan niet eerder wat Môesa gegeven was? Zij zeiden: "Dit zijn twee tovenaars, die elkaar helpen." En zij zeiden: "Wij geloven geen van allen!"'),
(3301, 28, 49, 'Zeg (O Moehammad): "Breng dan een Boek van Allah dat een betere Leiding geeft dan deze twee, dan zal ik het volgen, als jullie waarachtigen zijn!"'),
(3302, 28, 50, 'Als zij jou den geen antwoord geven, weet dan dat zij slechts hun begeerten volgen. En wie dwaalt er meer dan wie er zonder Leiding van Allah zijn begeerten volgt? Voorwaar, Allah leidt het onrechtvaardige volk niet.'),
(3303, 28, 51, 'En voorzeker, Wij openbaarden hun achtereenvolgens het Woord, hopelijk zullen zij zich laten vermanen.'),
(3304, 28, 52, 'Degenen die Wij daarvóór (vóór de Koran) de Schrift gegeven hebben: zij geloven erin.'),
(3305, 28, 53, 'En wanneer hij (de Koran) aan hen voorgedragen wordt, zeggen zij: "Wij geloven erin. Voorwaar, het is de Waarheid van onze Heer. Voorwaar, wij hadden ons ervóór al overgegeven (aan Allah)." En welke dingen jullie ook gegeven werden: zij zijn de genietingen van het wereldse leven en haar versiering. Maar wat van de Zijde van Allah komt is beter en blijvender. Begrijpen jullie dan niet?'),
(3306, 28, 54, 'Diegenen zal tweemaal hun beloning gegeven worden: omdat zij geduldig waren en zij met het goede het kwade afwendden; en omdat zij uitgeven van dat waar Wij hen mee voorzien hebben.'),
(3307, 28, 55, 'En wanneer zij nutteloos gepraat hoorden, wendden zij zich af, en zeiden: "Voor ons onze daden en voor jullie jullie daden, vrede zij met jullie, wij zoeken de onwetenden niet op."'),
(3308, 28, 56, 'Voorwaar, jij kunt degene die jij liefhebt geen leiding geven. Maar Allah leidt wie Hij wil, en Hij kent degenen die leiding volgen het best.'),
(3309, 28, 57, 'En zij zeiden: "Als wij de leiding met jou volgen, dan worden wij uit ons land gedreven." Hebben Wij hen dan niet op een gewijde en een veilige plaats doen vestigen, waaheen allerlei soorten vruchten worden gebracht, als een levensvoorziening van Onze zijde? Maar de meesten van hen weten het niet.'),
(3310, 28, 58, 'En hoeveel steden hebben Wij niet vernietigd die overdreven met hun genietingen? En dat zijn hun woningen, er wonen na hen slechts weinigen. En Wij waren de erfgenamen.'),
(3311, 28, 59, 'En jouw Heer was geen vernietiger van de steden vóórdat Hij tot hun hoofdstad een boodschapper had gestuurd die hen Onze verzen voordroeg. En Wij waren geen vernietiger van de stad, tenzij haar bewoners onrechtvaardig waren.'),
(3312, 28, 60, 'En welke dingen jullie ook gegeven werden: zij zijn de genietingen van het wereldse leven en haar versiering. Maar wat van de zijde van Allah komt is beter en blijvender. Begrijpen jullie dat dan niet?'),
(3313, 28, 61, 'Is iemand aait wie Wij een goede belofte hebben gedaan en die (de vervulling) ervan krijgt, als iemand die Wij hebben doen genieten van de genietingen van het wereldse leven en die dan op de Dag der Opstanding tot de voorgeleiden (voor de bestraffing) behoort?'),
(3314, 28, 62, 'En op de Dag dat Hij hen zal roepen, zal Hij zeggen: "Waar zijn Mijn deelgenoten, degenen die jullie veronderstelden (te bestaan)?"'),
(3315, 28, 63, 'Degenen voor wie het Woord bewaarheid wordt, zullen zeggen: "Onze Heer, zij zijn degenen die wij deden dwalen. Wij hebben hen doen dwalen zoals wij zelf dwaalden. Wij verontschuldigen ons bij U. Zij aanbaden ons niet."'),
(3316, 28, 64, 'En er wordt gezegd: "Roept jullie deelgenoten aan!" Zij zullen hen aanroepen, maar zij zullen geen gehoor aan hen geven en zij zullen de bestraffing zien. Hadden zij man Leiding gevolgd!'),
(3317, 28, 65, 'En op die Dag zal Hij hen roepen, terwijl Hij zegt: "Wat is het dat jullie de Boodschappers hebben geantwoord?"'),
(3318, 28, 66, 'Maar op die Dag zullen voor hen de argumenten duister blijven en zij zullen het niet aan elkaar (kunnen) vragen.'),
(3319, 28, 67, 'Maar wie berouw had en geloofde en goede daden verrichtte: moge hij dan tot de welslagenden behoren.'),
(3320, 28, 68, 'En jouw Heer schept wat Hij wil en Hij verkiest (wie Hij wil). Het is niet aan hen om te kiezen. Heilig is Allah en Verheven boven wat zij aan deelgenoten (aan Hem) toekennen.'),
(3321, 28, 69, 'En jouw Heer wed wat hun harten verbergen en wat zij openlijk doen.'),
(3322, 28, 70, 'En Hij is Allah, er is geen god dan Hij. Aan Hem behoort alle lof, op deze wereld en in het Hiernamaals, en aan Hem behoort het oordeel en tot Hem zullen jullie teruggekeerd worden.'),
(3323, 28, 71, 'Zeg (O Moehammad): "Wat dachten jullie: als Allah de nacht zou doen voortduren tot aan de Dag der Opstanding; welke god, anders dan Allah, zou er zijn die jullie licht brengt? Luisteren jullie dan niet?"'),
(3324, 28, 72, 'Zeg: "Wat dachten jullie: als Allah de dag zou doen voortduren tot de Dag der Opstanding, welke god, anders den Allah, zou er zijn die jullie een nacht geeft om in uit te rusten? Zien jullie dan niet?'),
(3325, 28, 73, 'En het behoort tot Zijn Barmhartigheid dat Hij voor jullie de nacht en de dag gemaakt heeft om in uit te rusten en Zijn gunsten te zoeken. Hopelijk zullen jullie dankbaar zijn.'),
(3326, 28, 74, 'En op die Dag mept Hij hen, en zegt: "Waar zijn Mijn deelgenoten, degenen die jullie veronderstelden (te bestaan)?"'),
(3327, 28, 75, 'En Wij brengen uit iedere gemeenschap een getuige (een Profeet) naar voren en Wij zeggen: "Brengt jullie bewijzen!" Dan zullen zij weten dat de Waarheid aan Allah toebehoort. En wat zij plachten te verzinnen is van hen verdwenen.'),
(3328, 28, 76, 'Voorwaar, Qârôen behoorde tot het volk van Môesa, maar Hij bedroog hen. En Wij schonken hem zoveel schatten dat hun sleutels met moeite door een groep sterke mannen gedragen zouden kunnen worden. (Gedenkt) toen zijn volk tot hem zei: "Wees niet hoogmoedig: voorwaar, Allah houdt niet van de hoogmoedigen.'),
(3329, 28, 77, 'En zoek met wat Allah jou gegeven heeft het Huis van het Hiernamaals en vergeet niet jouw deel in de wereld. En doe goed zoals Allah jou goed heeft gedaan en zoek geen verderf op de aarde. Voorwaar. Allah houdt niet van de verderfzaaiers."'),
(3330, 28, 78, 'Hij (Qârôen) zei: "Voorwaar, dat wat aan mij gegeven is, berust op kennis die ik bezit." Wist hij dan niet dat Allah vóór zijn tijd generaties heeft vernietigd die veel sterker waren dan hij en meer (rijkdom) verzameld hadden? En de misdadigers hoeven niet over hun zonden ondervraagd te werden (want Allah kent hun zonden al).'),
(3331, 28, 79, 'Toen begaf hij zich onder zijn volk, in vol ornaat. Degenen die het wereldse leven wensten, zeiden: "Hadden wij maar zoveel als Qârôen gegeven is. Voorwaar, hij is zeker een man die gewuldig geluk heeft."'),
(3332, 28, 80, 'En degenen aan wie de kennis gegeven was, zeiden: "Wee jullie, de beloning van Allah is beter voor wie gelooft en goede daden verricht. En zij wordt niet bereikt, behalve door de geduldigen."'),
(3333, 28, 81, 'Daarom deden wij hem en zijn woning in de aarde wegzinken. En voor hem was er geen groep die hem kon helpen, behalve Allah. En hij behoorde niet tot de weerbaren.'),
(3334, 28, 82, 'En degenen die de vorige dag zijn positie wensten gingen zeggen: "O wee, het is Allah Die het onderhoud verruimt en beperkt voor wie Hij wil van Zijn dienaren. Als Allah ons niet begunstigd had, had Hij ons zeker doen wegzinken. O wee, de ongelovigen welslagen niet."'),
(3335, 28, 83, 'Dat Huis van het Hiernamaals (het Paradijs) schenken Wij aan hen, die niet hoogmoedig wensen te zijn op aarde en geen verderf (zaaien) en het (goede) einde is voor de Moettaqôen.'),
(3336, 28, 84, 'Wie met het goede kwam, voor hem is er iets beters dan dat; en wie die met het slechte kwam: degenen die slechte daden verrichtten worden slechts vergolden voor wat zij plachten te doen.'),
(3337, 28, 85, 'Voorwaar, Degene Die de Koran voor jou tot een verplichting heeft gemaakt, zal jou zeker terugbrengen naar de plaats van de terugkeer. Zeg (O Moehammad): "Mijn Heer weet het beste wie met de Leiding is gekomen en wie degene is die in duidelijke dwaling verkeert."'),
(3338, 28, 86, 'En jij had niet gehoopt dat het Boek tot jou neergezonden zou worden, behalve als Barmhartigheid van jouw Heer. Wees dus zeker geen medestander van de ongelovigen.'),
(3339, 28, 87, 'En laat jou niet weghouden van de Verzen van Allah nadat zij tot jou neergezonden zijn en roep jouw Heer aan. En behoor zeker niet tot de veelgodenaanbidders.'),
(3340, 28, 88, 'En roep geen andere god aan naad Allah. Er is geen god dan Hij. Alle dingen zullen vergaan, behalve Allah. Aan Hem is het oordeel en tot Ham worden jullie teruggebracht.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'Alif Lâm Mîm.'),
(3342, 29, 2, 'Dachten de mensen dat zij met rust gelaten worden, als zij zeggen: "Wij geloven," en dat zij niet op de proef gesteld worden?'),
(3343, 29, 3, 'En voorzeker, Wij hebben degenen vóór hen op de proef gesteld. Allah kent zeker degenen die de oprecht spreken en Hij kent zeker de leugenaars.'),
(3344, 29, 4, 'Of dachten degenen die slechte daden verrichten dat zij aan Ons kunnen ontvluchten? Slecht is het wat zij veronderstellen!'),
(3345, 29, 5, 'Wie hoopt op de ontmoeting met Allah: voorwaar, het door Allah vastgestelde tijdstip komt zeker en Hij is de Alhorende, de Alwetende.'),
(3346, 29, 6, 'En wie (voor Allah) strijdt, strijdt slechts voor zichzelf. Voorwaar, Allah is zeker vrij van de behoeften van de werelden.'),
(3347, 29, 7, 'En degenen die geloven en goede daden verrichtten zullen Wij zeker hun slechte daden kwijtschelden en Wij zullen hun zeker belonen voor het beste van wat zij plachten te doen.'),
(3348, 29, 8, 'En Wij hebben de mens bevolen goed te zijn voor zijn ouders, maar indien zij jou dwingen om deelgenoten toe te kennen aan Mij, waarvan jij geen kennis hebt, gehoorzaam hen dan niet. Tot Mij is jullie terugkeer, daarna zal Ik jullie op de hoogte brengen van wat jullie plachten te doen.'),
(3349, 29, 9, 'En degenen die geloven en goede daden verrichtten zullen Wij zeker doen binnengaan in (het gezelschap) van de oprechten,'),
(3350, 29, 10, 'En er zijn er onder de mensen, die zeggen: "Wij geloven in Allah," en als zij dan door (te geloven in) Allah gekweld worden, beschouwen zij de beproeving van de mensen als een bestraffing van Allah; en als er een overwinning van jouw Heer komt, zeggen zij zeker: "Voorwaar, wij waren met Jullie!" En is het niet Allah, Die het beste weet wat er in de harten van de mensen is?'),
(3351, 29, 11, 'En Allah kent zeker degenen die geloven en Hij kent zeker de huichelaars.'),
(3352, 29, 12, 'En degenen die ongelovig zijn, zeggen tegen degenen die geloven: "Volgt onze weg, dan zullen wij zeker jullie zonden dragen." Maar zij dragen geen enkele zonde van hen. Voorwaar, zij zijn zeker leugenaars.'),
(3353, 29, 13, 'En zij zullen zeker hun (eigen) lasten dragen, en lasten naast hun lasten. En zij zullen op de Dag der Opstanding ondervraagd worden over wat zij plachten te verzinnen.'),
(3354, 29, 14, 'En voorzeker, Wij hebben Nôeh tot zijn volk gezonden en hij verbleef duizend jaar bij hen, op vijftig jaar na. Toen overviel de zondvloed hen, terwijl zij onrechtvaardigen waren.'),
(3355, 29, 15, 'Toen hebben Wij hem en de opvarenden van de ark gered. En Wij maakten haar tot een lering voor de werelden.'),
(3356, 29, 16, 'En (gedenkt) Ibrâhîm, toen hij tot zijn volk zei: "Aanbidt Allah en vreest Hem, dat is beter voor jullie, ah jullie het weten.'),
(3357, 29, 17, 'Voorwaar, wat jullie naast Allah aanbidden zijn slechts afgoden, en jullie verzinnen leugens. Voorwaar, degenen die jullie naast Allah aanbidden hebben geen macht om jullie van levensonderhoud te voorzien. Zoekt daarom de levensvoorziening bij Allah en aanbidt Hem, en weest Hem dankbaar, tot Hem worden jullie teruggebracht.'),
(3358, 29, 18, 'Maar als jullie loochenen: voorzeker, vele gemeenschappen vóór jullie loochenden. En de plicht van de Boodschapper is slechts de duidelijke verkondiging.'),
(3359, 29, 19, 'Zien zij niet in, hoe Allah de schepping schiep en haar vervolgens herhaalde? Voorwaar, dat is voor Allah gemakkelijk.'),
(3360, 29, 20, 'Zeg: "Reist op de aarde en ziet dan hoe Hij de schepping schiep; vervolgens wekt Hij een laatste schepping op. Voorwaar, Allah is Almachtig over alle zaken."'),
(3361, 29, 21, 'Hij bestraft wie Hij wil en Hij begenadigt wie Hij wil, en tot Hem zullen jullie teruggekeerd worden.'),
(3362, 29, 22, 'En er is voor jullie geen ontvluchten (aan Hem) op de aarde en niet in de hemel, en er is voor jullie naast Allah geen beschermer en geen helper.'),
(3363, 29, 23, 'En degenen die niet geloven in de Verzen van Allah en de ontmoeting met Hem: zij zijn degenen die wanhopen aan Mijn Barmhartigheid en zij zijn degenen voor wie er een pijnlijke bestraffing is.'),
(3364, 29, 24, 'Maar het antwoord van zijn volk was slechts dat zij zeiden: "Doodt hem," of "Verbrandt hem." Maar Allah redde hem van het vuur. Voorwaar, daarin zijin zeker Tekenen voor een volk dat gelooft.'),
(3365, 29, 25, 'En hij (Ibrâhîm) zei: "Voorwaar, wet jullie naast Allah hebben genomen zijn slechts afgoden om de onderlinge liefde tussen jullie te versterken in dit wereldse leven. Op de Dag van de Opstanding zullen jullie elkaar verwerpen en elkaar vervloeken. Maar jullie verblijfplaats is de Hel en er zullen geen helpers voor jullie zijn."'),
(3366, 29, 26, 'Maar Lôeth geloofde hem, en (Ibrârîm) zei: "ik ben een uitwijker naar mijn Heer: voorwaar, Hij is de Almachtige, de Alwijze."'),
(3367, 29, 27, 'En Wij schonken hem Ishâq en Ya\'qôeb. En Wij hebben ender zijn nakomelingen het Profèctschap en dc Schrift tot stand gebracht. En Wij schonken hem zijn beloning in de wereld. En voorwaar, in het Hiernamaals behoort hij zeker tot de oprechten.'),
(3368, 29, 28, 'En (gedenkt) Lôcth, toen hij tegen zijn volk zei. "Jullie begaan zeker gruweldaden, die niemand van de mensen ooit beging.'),
(3369, 29, 29, 'Is het dan inderdaad niet zo dat jullie de mensen benaderen en op de weg struikroverij bedrijven en in jullie samenkomsten het verwerpelijke verichten?" Het antwoord van zijn volk was slechts dat zij zeiden: "Geef ons dan de bcstraffing van Allah, als jij tot de waarachtigen behoort!"'),
(3370, 29, 30, 'Hij zei: "Mijn Heer, help mij tegen het volk van de verdedzaaiers! "'),
(3371, 29, 31, 'En toen Onze gezanten (Engelen) tot Ibrâhîm kwamen met de verheugende tijdingen, zeiden zij."Voorwaar, wij zullen de bewoners van deze stad vernietigen. Voorwaar, haar bewoners waren onrechtvaardigen."'),
(3372, 29, 32, 'Hij zei: "Lôeth is daar." Zij miden: "Wij weten beter wie daar zijn. Voorwaar, wij zullen hem en zijn familie redden, behalve zijn vrouw. Zij zal tot de achterblijvers behoren."'),
(3373, 29, 33, 'En toen Onze gezanten naar Lôeth kwamen, was hij bedroefd vanwege hen, en hij had geen macht om hen te beschermen. Maar zij zeiden: "Wees niet bang en treur niet: voorwaar, wij redden zeker jou en jouw familie, behalve jouw vrouw. Zij zal tot de achterblijvers behoren.'),
(3374, 29, 34, 'Voorwaar, wij zullen een zware bestraffing uit de hemel neerzenden op de bewoners van deze stad omdat zij zware zonden begingen."'),
(3375, 29, 35, 'En voorzeker, Wij hebben een duidelijk Teken achtergelaten voor een volk dat begrijpt.'),
(3376, 29, 36, 'En naar Madyan (stuurden Wij) hun broeder Sjoe\'aib. En hij zei: "O mijn volk, aanbidt Allah en hoopt op de Laatste Dag en verricht geen kwaad op aarde als verderfzaaiers."'),
(3377, 29, 37, 'Toen loochenden zij hem waarna een aardbeving hen trof en zij werden doden in hun huizen.'),
(3378, 29, 38, 'En (Wij vernietigden) de \'Âd en de Tsamôed. Dat is jullie waarlijk bekend geworden door (de ruïnes van) hun woningen. En de Satan deed voor hen hun daden schoon sehijnen, waarna hij hen afleidde van de Weg, hoewel zij over inzicht beschikten.'),
(3379, 29, 39, 'En (Wij vernietigden) Qârôen en Fir\'aun en Hâmân. En voorzeker is Môesa tot hen met duidelijke Tekenen gekomen, maar zij waren hoogmoedig op de aarde. En zij konden (Onze bestraffing) niet ontvluchten.'),
(3380, 29, 40, 'En ieder bestraften Wij voor zijn zonde. Daarom waren er onder hen over wie Wij een storm var hagelstenen zonden en waren er onder hen die door een bliksemslag gegrepen werden. En er waren er onder hen die Wij in de aarde deden wegzinken en er waren er onder hen Wij verdronken. Allah was niet onrechtvaardig voor hen, maar zij waren onrechtvaardig voor ziclizelf'),
(3381, 29, 41, 'De gelijkenis met degenen die helpers naast Allah nemen, is als die met een spin die een huis maakt. En voorwaar, het zwakste van de huizen is zeker het huis van een spin (al \'Ankabôet), als zij het wisten.'),
(3382, 29, 42, 'Voorwaar, Allah weet wat zij voor iets naast Hem aanroepen en Hij is de Almachtige, de Alwijze.'),
(3383, 29, 43, 'En deze vergelijkingen maken Wij voor de mensen, maar zij begrijpen ze niet, behalve degenen die over kennis beschikken.'),
(3384, 29, 44, 'Allah schiep de hemelen en de aarde in waarheid. Voorwaar, daarin is zeker een Teken voor de gelovigen.'),
(3385, 29, 45, 'Draag voor (O Moehammad) wat aan jou in het Boek geopenbaard is en onderhoud de shalât. Voorwaat, de shalât weerhoudt van de gruweldaad en het verwerpelijke. Zeker, het gedenken van Allah (de shalât) is groter en Allah weet wat jullie bedrijven.'),
(3386, 29, 46, 'En redetwist niet anders dan op de beste wijze met de lieden van de Schrift, behalve met de onrechtplegers onder hen. En zegt: "Wij geloven in wat aan ons is neergezonden en in wat aan jullie is neergezonden; en onze God en jullie God is Één, en wij hebben ons am Hem overgegeven.'),
(3387, 29, 47, 'En zo hebben Wij aan jou het Boek neergezonden. Degenen aan wie Wij de Schrift (de Taurât) hebben gegeven. zij geloven erin (in de Koran) en onder dezen (de bewoners van Mekkah) zijn er die erin geloven. En niemand verwerpt Onze Verzen dan de ongelovigen.'),
(3388, 29, 48, 'En daarvóór heb jij nooit een boek gelezen, en jij hebt nooit iets ervan met je rechterhand geschreven. Anders zouden de ontkenners zeker twijfelen.'),
(3389, 29, 49, 'Het zijn zelfs duidelijke Verzen in de harten van degenen aan wie de kennis is gegeven. En niemand verwerpt Onze Verzen dan de onrechtvaardigen.'),
(3390, 29, 50, 'En zij zeiden: "Was er maar een Teken (wonder) van zijn Heer aan hem gezonden?" Zeg: "Voorwaar, de Tekenen zijn slechts bij Allah. En voorwaar, ik ben slechts een duidelijke waarschuwer."'),
(3391, 29, 51, 'Is het voor hen niet voldoende dat Wij aan jou het Boek hebben neergezonden, dat aan hen wordt voorgedragen? Voomaar, daarin is zeker barmhartigheid en een vermaning voor een volk dat gelooft.'),
(3392, 29, 52, 'Zeg: "Allah is voldoende als Getuige tussen mij en jullie." Hij kent wat er in de hemelen en op de aarde is. En degenen die in de valsheid geloven en aan Allah ongelovig zijn: zij zijn de verliezers.'),
(3393, 29, 53, 'En zij vragen jou om de bestraffing te laten bespoedigen. En als er geen vastgestelde termijn was, dan zou de bestraffing zeker reeds tot hen zijn gekomen. En die zal zeker plotseling tot hen komen, terwijl zij het niet beseffen.'),
(3394, 29, 54, 'Zij vragen jou om de bestraffing te laten bespoedigen. En voorwaar, de Hel omsluit zeker de ongelovigen.'),
(3395, 29, 55, 'Op die Dag bedekt de bestraffing hen, van boven ben en van onder hun voeten. En Hij zal zeggen: "Proeft wat jullie plachten te doen."'),
(3396, 29, 56, 'O Mijn dienaren die geloven: voorwaar, Mijn aarde is wijd, aanbidt daarom slechts Mij.'),
(3397, 29, 57, 'Elke ziet zal de dood ervaren, Daarna zullen jullie tot Ons worden teruggekeerd.'),
(3398, 29, 58, 'En voor degenen die geloven en goede werken verrichten zullen Wij zeker in het Paradijs hoge verblijven plaatsen, waar onder door de rivieren stromen. Zij zijn daarin eeuwiglevenden. De beste beloning is voor hen die (goede) werken verrichten.'),
(3399, 29, 59, 'Degenen die geduld hebben en op hun Heer vertrouwen.'),
(3400, 29, 60, 'En hoeveel levende wezens zijn er niet, die niet over hun levensondeoud beschikken? Allah voorziet hen en jullie ook. En Hij is Alhorend, Alwetend.'),
(3401, 29, 61, 'En als je kun vraagt wie de hemelen en de aarde heeft geschapen en wie de zon en de maan heeft onderworpen, dan zullen zij zeker zeggen: "Allah." Hoe komt het dan, dat zij zo bedrogen worden?'),
(3402, 29, 62, 'Allah verruimt de voorzieningen voor wie Hij wil van Zijn dienaren, en Hij beperkt voor hen. Voorwaar, Allah is Alwetend over alle dingen.'),
(3403, 29, 63, 'En als jij hun vraagt wie uit de hemel water doet neerkomen en dan de aarde daarmee tot leven brengt na haar dood, dan zeggen zij zeker: "Allah." Zeg: "Alle lof zij Allah." Maar de meesten van hen begrijpen het niet.'),
(3404, 29, 64, 'En dit wereldse leven is niets dan vermaak en spel. En voorwaar, het Huis van het Hiernamaals is zeker het echte leven, als zij het wisten!'),
(3405, 29, 65, 'En als zij op de schepen varen, dan roepen zij Allah aan. Hem zuiver aanbiddend. Maar zodra Hij hen dan heeft gered (en) aan land heeft gebracht, dan kennen zij deelgenoten (aan Allah) toe.'),
(3406, 29, 66, 'Om hun ondankbaarheid te tonen voor wat Wij hun gegeven hebben en om te genieten. Spoedig zuilen zij het te weten.'),
(3407, 29, 67, 'En zim zij niet dat Wij, een gewijd veilig (land) hebben gemaakt, terwijl de mensen om hen heen worden verdreven? Geloven zij dan in de valsheid en ontkennen zij de gunst van Allah?'),
(3408, 29, 68, 'En wie is er onrechtvaardiger dan wie er een leugen over Allah verzint of de Waarheid loochent wanneer die tot hem is gekomen? Is de Hel niet de plaats voor de ongelovigen?'),
(3409, 29, 69, 'En degenen die omwille van Ons streven, die zullen Wij zeker Leiding schenken op Onze wegen. En voorwaar, Allah is zeker met de weldoeners.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'Alif Lâm Mîm.'),
(3411, 30, 2, 'Ik Romeinen zijn overwonnen.'),
(3412, 30, 3, 'In het nabijgelegen land, en na hun nederlaag zullen zij overwinnen.'),
(3413, 30, 4, 'In enkelen jaren. Aan Allah behoort het bevel, voordien en nadien. En op die dag zullen de gelovigen zich verheugen.'),
(3414, 30, 5, 'Dank zij de hulp van Allah. Hij helpt wie Hij wil. En Hij is de Almachtige, de Meest Bamhartige.'),
(3415, 30, 6, 'Als een belofte van Allah. Allah breekt Zijn belofte niet, maar de meeste mensen weten het niet.'),
(3416, 30, 7, 'Zij kennen het uiterlijke van het wereldse leven, terwijl zij met betrekking tot het Hiernamaals onachtzamen zijn.'),
(3417, 30, 8, 'En denken zij niet na over zichzelf? Allah heet de hemelen en de aarde en wat daartussen is, niet geschapen dan in Waarheid en voor een vastgestelde termijn. En voorwaar, de meeste mensen geloven zeker niet in de ontmoeting met hun Heer.'),
(3418, 30, 9, 'En reizen zij (ongelovigen) niet op de aarde, zodat zij zien hoe het einde was van degenen vóór hen? Zij waren sterker in kracht dan zij. En zij bewerkten de aarde en zij bebouwden haar meer dan zij (de ongelovigen) haar bebouwden. En hun Boodschappers waren tot hen gekomen met de duidelijke bewijzen. Het was Allah niet die hun onrecht aandeed, maar zij deden zichzelf onrecht aan.'),
(3419, 30, 10, 'Daarna was de ergste bestraffing het einde van degenen die slechte werken verrichtten, omdat zij de Verzen van Allah loochenden en zij er de spot mee plachten te drijven.'),
(3420, 30, 11, 'Allah schept de schepping, daarop wekt Hij haar op en tenslotte worden jullie tot Hem terruggekeerd.'),
(3421, 30, 12, 'En op de Dag waarop het Uur valt wanhopen de zondaren.'),
(3422, 30, 13, 'Er is voor hen onder hun afgoden geen voorspreker en zij zullen hun afgoden ontkennen.'),
(3423, 30, 14, 'Op de Dag waarop het Uur valt, op die Dag zullen zij gegroepeerd worden.'),
(3424, 30, 15, 'Wat degenen die geloven en goede daden verrichten betreft: zij zullen in een hof worden verblijd.'),
(3425, 30, 16, 'En wat degenen die ongelovig zijn en Onze Verzen en de ontmoeting van het Hiernamaals loochenen betreft: zij zijn degenen die voor de bestraffing voorgeleid zullen worden.'),
(3426, 30, 17, 'Prijst daarom de Glorie van Allah wanneer jullie (ergens) in de namiddag zijn en wanneer jullie (ergens) in de ochtend zijn.'),
(3427, 30, 18, 'En aan Hem is alle lof in de hemelen en op de aarde, en in de avond en wanneer jullie (ergens) in de voormiddag zijn.'),
(3428, 30, 19, 'Hij brengt het levende voort uit het dode en Hij brengt het dode voort uit het levende en Hij doet de aarde leven na haar dood. Zo worden jullie (na jullie dood) opgewekt.'),
(3429, 30, 20, 'En het behoort tot Zijn Tekenen dat Hij jullie uit aarde heeft geschapen, waarop jullie je toen als mensen verspreidden.'),
(3430, 30, 21, 'En het behoort tot Zijn Tekenen dat Hij van jullie eigen soort echtgenotes heeft geschapen, opdat jullie rust bij haar vinden en Hij bracht tussen jullie liefde en barmhartigheid. Voorwaar, daarin zijn zeker Tekenen voor een volk dat nadenkt.'),
(3431, 30, 22, 'En tot Zijn Tekenen behoort de schepping van de hemelen en de aardeen het verschillende van jullie talen en kleuren. Voorwaar, daarin zijn zeker Tekenen voor de bezitters van kennis.'),
(3432, 30, 23, 'En tot Zijn Tekenen behoort jullie slaap, \'s nachts en overdag en jullie zoeken naar Zijn gunst. Voorwaar, daarin zijn zeker Tekenen voor een volk dat luistert.'),
(3433, 30, 24, 'En tot Zijn Tekenen behoort dat Hij jullie de bliksem laat zien, om vrees en hoop op te wekken. En Hij doet water uit de hemel neerdalen, waarna Hij daarmee de aarde doet leven na haar dood. Voorwaar, daairin zijn zeker Tekenen voor een volk dat begrijpt.'),
(3434, 30, 25, 'En tot Zijn Tekenen behoort dat Hij de hemel en de aarde in stand houdt, met Zijn bevel. Daarna wanneer Hij jullie met een roep uit de aarde doet oproepen, dan zullen jullie tevoorschijn komen.'),
(3435, 30, 26, 'En aan Hem behoort wie er in de hemelen en op de arde zijn. Allen zijn Hem gehoorzaam.'),
(3436, 30, 27, 'En Hij is Degene Die de schepping schept en haar daarna herhaalt en dat is voor Hem nog gemakkelijker. En aan Hem behoren de meest verheven eigenschappen in de hemelen en op de aarde. En Hij is de Almachtige, de Alwijze.'),
(3437, 30, 28, 'Hij geeft jullie een vergelijking met julliezelf. Zijn er voor jullie die over slaven beschikken deelgenoten in de voorzieningen die Wij jullie hebben geschonken, zodat jullie daarin gelijk zijn? En vrezen jullie hen zoals jullie elkaar vrezen? Zo leggen Wij de Tekenen uit aan een volk dat begrijpt.'),
(3438, 30, 29, 'Maar degenen die onrecht pleegden volgden hun begeerten. zonder kennis. En wie kan dan hen leiden die Allah deed dwalen? En voor hen zijn er geen helpers.'),
(3439, 30, 30, 'Wend dan jouw aangezicht (O Moehammad) naar de godsdienst als een Hanîf. (Volg) de na tuurlijke aanleg, die Allah in de mens geschapen heeft. Er is geen verandering in de schepping van Allah Dat is de juiste godsdienst, maar de meeste mensen weten het niet.'),
(3440, 30, 31, '(Wendt jullie) als berouwvollen tot Hem, en vreest Hem en onderhoudt de shalât en behoort niet tot de veelgodenaanbidders.'),
(3441, 30, 32, 'Behorend tot degenen die hun godsdienst hebben opgesplitst en tot groepen zijn geworden. Iedere groep verbeugt zich in wat zij hebben.'),
(3442, 30, 33, 'En wanneer tegenspoed de mensen treft, dan roepen zij hun Heer aan, als berouwvollen tot Hem. Daarna, wanneer Hij hun Barmhartigheid van Hem doet proeven, dan kont een groep van hen deelgenoten aan hun Heer toe.'),
(3443, 30, 34, 'Om ondankbaarheid te tonen voor wat Wij hen hebben gegeven. Geniet maar, spoedig zuilen jullie het weten.'),
(3444, 30, 35, 'Hebben Wij een bewijs neergezonden dat spreekt over de deelgenoten die zij am Hem toekennen?'),
(3445, 30, 36, 'En wanneer Wij de mensen Barmhartigheid doen proeven, dan zijn zij daar blij mee, maar wanneer kwaad hen treft, wegens wat zij bedreven, dan wanhopen zij.'),
(3446, 30, 37, 'En zien zij niet dat Allah ck voorzieningen verruimt voor wie Hij wil, en dat Hij beperkt? Voorwaar, daarin zijn zeker Tekenen voor een volk dat gelooft.'),
(3447, 30, 38, 'Geeft dan de verwant zijn recht, en de behoeftige en de reiziger (zonder proviand). Dat is beter voor degenen die het welbehagen van Allah wensen. En zij zijn degenen die de welslagenden zijn.'),
(3448, 30, 39, 'Wat jullie van eigendommen van de mensen gegevon hebben als rente om te vermeerderen: het vermeerdert niets bij Allah. Maar wat jullie aan zakât hebben gegeven, het welbehagen van Allah wensend: zij zijn degenen voor wie (hun beloningen) verveelvuldigd worden.'),
(3449, 30, 40, 'Allah is Degene Die jullie heeft geschapen en jullie daarop voorzag. Vervolgens doet Hij jullie sterven en daarna doet Hij jullie weer leven. Is er één onder jullie deelgenoten die ook maar iets van deze deden kan verrichten? Heilig is Hij en Verheven boven de deelgenoten die zij (Hem) toekennen.'),
(3450, 30, 41, 'Het verderf is op het land en de zee zichtbaar door wat de mensen hebben verricht, zodat Hij hun een gedeelte van wat zij hebben verricht, doet proeven. Hopelijk zij zullen berouw tonen.'),
(3451, 30, 42, 'Zeg: "Reist op aarde en zie hoe het einde was van degenen vóór hen." De meegen van hen waren veelgoden aanbidders.'),
(3452, 30, 43, 'Wend daarom jouw aangezicht tot de juiste godsdienst vóórdat er van Allah een Dag komt die onafwendbaar is. Op die Dag worden zij gegroepeerd.'),
(3453, 30, 44, 'Wie niet gelooft is verantwoordelijk voor zijn ongeloof. En degenen die goede daden verrichten, zij hebben voor zichzelf voorbereidingen getroffen.'),
(3454, 30, 45, 'Opdat Hij degenen die geloven en goede werken verrichten zal belonen, van Zijn gunst. Voorwaar, Hij houdt niet van de ongelovigen.'),
(3455, 30, 46, 'En tot Zijn Tekenen behoort dat Hij de winden zendt als verkondigers van verheugende tijdingen en om jullie van Zijn Barmhartigheid te doen proeven. En opdat de schepen varen op Zijn bevel en opdat jullie Zijn gunst zoeken. Hopelijk zullen jullie dankbaar zijn.'),
(3456, 30, 47, 'En voorzeker, Wij hebben vóór jou Boodschappers tot kan volken gezonden, en zij kwamen tot hen met de duidelijke bewijzen, waarna Wij degenen die zonden begingen vergolden. En Wij hebben Onszelf verplicht de gelovigen te helpen.'),
(3457, 30, 48, 'Allah is Degene Die de winden zendt die dan wolken voortdrijven, waarna Hij hen in de hemel uitspreidt hoe Hij wil, en hen in stukken verdeelt. Dan zie jij de regen vanuit hun midden komen. Fn wanneer deze neerkomt op wie Hij wil van Zijn dienaren verheugen zij zich.'),
(3458, 30, 49, 'Hoewel zij, vóórdat deze op hen neer wordt gezonden, zeker wanhoopten.'),
(3459, 30, 50, 'Zie dan de sporen van de Barmhartigheid van Allah: hoe Hij de aarde doet leven na haar dood. Voorwaar, zo is Hij Die de doden zeker doet leven. En Hij is Almachtig over alle zaken.'),
(3460, 30, 51, 'Maar als Wij een wind zonden, waarna zij (hun gewassen) geel zien worden, dan blijven zij zeker daarna ongelovig.'),
(3461, 30, 52, 'Voorwaar, jij kunt de doden, noch de doven de oproep doen horen als zij zich afwenden en de rug toe keren.'),
(3462, 30, 53, 'En jij kunt nooit de blinden leiden uit hun dwaling. Jij kunt niemand doen horen, behalve wie in Onze Tekenen gelooft. Zij zijn Moslims.'),
(3463, 30, 54, 'Allah is Degene Die jullie heeft geschapen uit een zwak (vocht), daarna maakt Hij na zwakte sterkte. Waarop Hij van sterkte zwakte en ouderdom maakt. Hij schept wat Hij wil, en Hij is de Alwijze, de Almachtige.'),
(3464, 30, 55, 'En op de Dag waarop het Uur valt, zweren de misdadigers dat zij niet langer dan een uur (in hun graven verbleven). Zo zijn zij bedrogen.'),
(3465, 30, 56, 'En degenen aan wie de kennis en het geloof zijn gegeven, zullen zeggen: "Voorzeker, jullie verbleven er zoals is vastgelegd door Allah, tot de Dag der Opstanding. Dit is dan de Dag der Opkwekking, maar jullie wisten het niet."'),
(3466, 30, 57, 'Op die Dag zullen de verontschuldigingen van degenen die onrecht pleegden hun met baten. En hun zal geen gelegenheid worden gegeven om berouw te tonen.'),
(3467, 30, 58, 'En voorzeker, Wij hebben voor de mensen in deze Koran allerlei vergelijkingen gegeven; en als jij hen een Vers brengt, dan zeggen degenen die niet geloven zeker: "Jij bent niets dan een vervalser."'),
(3468, 30, 59, 'Zo vergrendelt Allah de harten van degenen die niet weten.'),
(3469, 30, 60, 'Wees daarom geduldig: voorwaar, de belofte van Allah is waar. En wordt niet verontrust door degenen die niet (door jou) overtuigd zijn.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'Alif Lâm Mîm.'),
(3471, 31, 2, 'Dit zijn de Verzen van het wijze Boek.'),
(3472, 31, 3, 'Als Leiding en Barmhartigheid voor de weldoeners.'),
(3473, 31, 4, 'Degenen die de shalât onderhouden en de zakât geven, en zij zijn overtuigd van het Hiernamaals.'),
(3474, 31, 5, 'Zij zijn het die de Leiding van hun Heer volgen en zij zijn de welslagenden.'),
(3475, 31, 6, 'En er zijn er onder de mensen die onzinnige praat kopen om te doen afdwalen van de Weg van Allah, zonder kennis, en die het (de Islam) bespotten. Zij zijn degenen voor wie er een vernederende bestraffing is.'),
(3476, 31, 7, 'En wanneer Onze Verzen aan hem worden voorgedragen, dan wendt hij zich er hoogmoedig van af, alsof hij ze niet hoort. Het is alsof er in zijn oren doofheid is. Verkondig hem daarom een pijnlijke bestraffing.'),
(3477, 31, 8, 'Voorwaar, degenen die geloven en goede werken verrichten: voor hen zijn er de Tuinen van gelukzaligheid (het Paradijs).'),
(3478, 31, 9, 'Zij Zijn eeuwig levenden daarin, als een belofte van Allah die waar is, en Hij is de Almachtige, de Alwijze.'),
(3479, 31, 10, 'Hij schiep de hemelen en de aarde, zonder pilaren die jullie kunnen zien, en Hij plaatste op de aarde bergen opdat zij jullie niet zou doen wankelen, en Hij verspreidde daarop allerlei soorten dieren. En Wij zenden water uit de hemel, waarna Wij er allerlei soorten rijke gewassen op doen groeien.'),
(3480, 31, 11, 'Dat is de schepping van Allah. Toont Mij wat degenen buiten Allah hebben geschapen. Maar de onrechtplegers verkeren in duidelijke dwaling.'),
(3481, 31, 12, 'En voorzeker, Wij hebben Loeqmân de wijsheid geschonken: wees Allah dankbaar. En wie dankbaar is, is slechts dankbaar voor zichzelf; en wie ondankbaar is: voorwaar, Allah is Behoefteloos, Geprezen.'),
(3482, 31, 13, 'En (gedenkt) toen Loeqmân tot zijn zoon zei, hem raad gevende: "O mijn zoon, ken Allah geen deelgenoten toe: voorwaar, het toekennen van deelgenoten (aan Allah) is zeker een geweldig onrecht."'),
(3483, 31, 14, 'En Wij bevolen de mens (goedheid) jegens zijn ouders. Zijn moeder droeg heen in zwakheid op zwakheid, en het zogen van hem duurde twee jaren. Wees daarom Mij en jouw ouders dankbaar. Tot Mij is de terugkeer.'),
(3484, 31, 15, 'En als zij jou dwingen dat jij iets aan Mij toekent, zonder dat jij er kennis over hebt: gehoorzam ham dan niet. En vergezel hen vriendelijk op de wereld. En volg de Weg van degenen die zich tot Mij hebben gewend. Daarna is tot Mij jullie terugkeer. Dan zal Ik jullie op de hoogte brengen van wat jullie plachten te doen.'),
(3485, 31, 16, '(Lôeqmân zei:) "O mijn zoon, ook al is er iets dat slechts, het gewicht van een mosterdzaadje heeft, dat zich in een rots bevindt, of in de hemelen, of in de aarde: Allah zal het tevoorschijn brengen. Voorwaar, Allah is Zachtmoedig, Alwetend.'),
(3486, 31, 17, 'O mijn zoon, onderhoud de shalât en roep op tot het goede en weerhoud van het verwerpelijke. En wees geduldig met wat jou treft. Voorwaar, dat behoort tot de aanbevolen daden.'),
(3487, 31, 18, 'En wend jouw gezicht niet af van de mensen en loop niet trots op aarde: voorwaar, Allah houdt van geen enkele verwaande opschepper.'),
(3488, 31, 19, 'En wees gematigd in jouw (manier van) lopen en spreek met een zachte stem: voorwaar, de memt verafschuwde van de stemmen is zeker de stem van de ezel."'),
(3489, 31, 20, 'Zien jullie niet dat Allah wat zich in de hemelen en op de aarde bevindt aan jullie dienstbaar heeft gemaakt? En Hij heeft Zijn gunsten voor jullie vervolmaakt, zichtbaar en onzichtbaar. En er zijn er onder de mensen die over Allah redetwisten zonder kennis, zonder Leiding en zonder verlichtend Boek.'),
(3490, 31, 21, 'En wanneer tot hen wordt gezegd: "Volgt wat Allah heeft neergezonden," dan zeggen zij: "Nee, wij volgen dat wat wij bij onze vaderen aantroffen." Zelfs als de Satan hen tot de bcstraffing van de Hel roept?'),
(3491, 31, 22, 'En wie zich geheel aan Allah overgeek terwijl hij een weldoener is: waarlijk, die heeft het stevige houvast gegrepen. En bij Allah is het einde van de dingen.'),
(3492, 31, 23, 'En wie niet gelooft, laat diens ongeloof jou niet bedroeven, tot Ons is hun terugkeer. Daarop zullen Wij hun op de hoogte brengen van wat zij bedreven. Voorwaar, Allah is Alwetend over wat zich in de hanen bevindt.'),
(3493, 31, 24, 'Wij doen hen een weinig genieten en vervolgens dwingen Wij hen naar een zware bestraffing,'),
(3494, 31, 25, 'En als jij hun vraagt wie de hemelen en de aardde geschapen heeft, dan zeggen zij zeker: "Allah." Zeg: "Alle lof zij Allah." Maar de meesten van hen weten het niet.'),
(3495, 31, 26, 'Aan Allah behoort wat zich in de hemelen en op de aarde bevindt. Voorwaar, Hij is de Behoefteloze, de Geprezene.'),
(3496, 31, 27, 'En als alle bomen op de aarde pennen waren en de zee (inkt), waarna er nog zeven zeeéën (met inkt) aan toegevoegd zouden worden, dan nog zouden de Woorden van Allah niet zijn uitgeput. Voorwaar, Allah is Almachtig, Alwijs.'),
(3497, 31, 28, 'De schepping van jullie en jullie opwekking is (voor Allah) slechts als die van één ziel. Voorwaar, Allah is Alhorend, Alziend.'),
(3498, 31, 29, 'Zie jij niet dat Allah de nacht in de dag doet overgaan en Hij de dag doet overgaan in de nacht en Hij de zon en de maan dienstbaar heeft gemaakt en dat allen tot een vastgesteld tijdstip bewegen? En voorwaar, Allah is Alwetend over wat jullie doen.'),
(3499, 31, 30, 'Dat is zo omdat Allah de Waarheid is en omdate wat zij naast Hem aanroepen de valsheid is. En omdat Allah de Verhevene, de Grootste is.'),
(3500, 31, 31, 'Zie jij niet dat de schepen op de zee varen door de gunst van Allah, opdat Hij jullie van Zijn Tekenen laat zien? Voorwaar, daarin zijn zeker Tekenen voor iedere goduwige en dankbare.'),
(3501, 31, 32, 'En wanneer golven als wolken hen bedekken roepen zij Allah aan, Hem zuiver aanbiddend. Wanneer Wij hen dan hebben gered en aan land hebben gebracht, dan zijn er onder hen die gematigd (tussen geloof en ongeloof) zijn. En nieniand anders ontkent Onze Tekenen dan iedere verrader en ondankbare.'),
(3502, 31, 33, 'O mensen, vreest jullie Heer en weest bevreesd voor een Dag waarop een vader zijn zoon niet van nut kan zijn, noch een zoon zijn vader in iets van nut kan zijn. Voorwaar, de belofte van Allah is waar, Laat daarom het wereldse leven jullie niet verleider en hat de verleider jullie niet van Allah wegleiden.'),
(3503, 31, 34, 'Voorwaar, Allah beschikt over de kennis van het Uur, en Hij zendt de regen neer, en Hij kent wat zich in de schoten bevindt. En niemand weet wat hij morgen zal doen. En niemand weet op welke grond hij zal sterven. Voorwaar, Allah is van alles op de hoogte, Alwetend.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'Alif Lâm Mîm.'),
(3505, 32, 2, 'De neerzending van het Boek waaraan geen twijfel is, is van de Heer der Werelden.'),
(3506, 32, 3, 'Zij zeggen zelfs: "Hij (Moehammad) heeft hem verzonnen." Nee! Het is de Waarheid van jouw Heer, zodat jij een volk waarschuwt tot wie vóór jou geen waarschuwer is gekomen. Hopelijk zulten zij Leiding volgen.'),
(3507, 32, 4, 'Allah is Degene Die de hemelen en de aarde en wat tussen hen is heeft geschapen, in zes dagen (perioden), en Hij zetelde Zich op de Troon. Er is voor jullie buiten Allah geen beschermer en geen voorspreker. Laten jullie je dan niet vermanen?'),
(3508, 32, 5, 'Hij regelt het bestuur vanuit de hemel naar de aarde, waarna die naar Hem opstijgt in een dag waarvan de tijdmaat duizend jaar is, zoals jullie rekenen.'),
(3509, 32, 6, 'Dat is de Kenner van het onwaarneembare en het waarneembare, de Almachtige, de Meest Barmhartige.'),
(3510, 32, 7, 'Degene Die alles wat Hij schiep op de beste manier heeft geschapen. En Hij begon de schepping van de mens uit klei.'),
(3511, 32, 8, 'Daarna maakte Hij zijn nageslacht van een uittreksel van nederig water (sperma).'),
(3512, 32, 9, 'Daarop voltooide Hij hem en blies in hem van de Ziel en Hij maakte voor jullie het gehoor en het gezichtsvermogen en de harten. Weinig dankbaarheid tonen jullie!'),
(3513, 32, 10, 'En zij zeggen: "Wanneer wij in de aarde zijn vergaan, zullen wij dan zeker tot een nieuwe schepping worden? Zij zijn zelfs ongelovig aan de ontmoeting met hun Heer.'),
(3514, 32, 11, 'Zeg (O Moehammad): "De Engel des doods die over jullie is aangesteld, zal jullie wegnemen. Vervolgens worden jullie tot jullie Heer teruggekeerd."'),
(3515, 32, 12, 'En als jij het kon zien wanneer de zondaren hun hoofden buigen voor hun Heer (en zeggen:) "Onze Heer, wij hebben gezien en gehoord, breng ons daarom terug, opdat wij goede werken verrichten. Voorwaar, wij zijn overtuigd."'),
(3516, 32, 13, 'En als Wij wilden, dan zouden Wij zeker iedere ziel haar Leiding geven, maar het Woord is door Mij bepaald: "Ik zal de Hel zeker vullen met Djinn\'s en mensen, allemaal"'),
(3517, 32, 14, 'Proeft dan (de bestraffing), omdat jullie ie de ontmoeting van deze Dag van jullie vergaten. Voorwaar, Wij hebben jullie vergeten. En proeft de eeuwige bestraffing vanwege wat jullie plachten te doen."'),
(3518, 32, 15, 'Voorwaar, gelovig aan Onze Verzen zijn slechts degenen die, wanneer zij ermee vemaand worden, zich neerbuigen, en die de Glorie van hun Heer prijzen met Zijn lofprijzing. En zij zijn niet hoogmoedig.'),
(3519, 32, 16, 'Hun zijden mijden de slaapplaatsen, zij roepen hun Heer aan, vrezend en hopend. En zij geven uit van dat waar Wij hun mee voorzagen.'),
(3520, 32, 17, 'En geen ziet weet welke verkoeling van de ogen voor hen verborgen wordt gehouden, als beloning voor wat zij plachten te doen.'),
(3521, 32, 18, 'Is wie gelovig is, gelijk aan een zwaar zondige? Zij zijn niet gelijk.'),
(3522, 32, 19, 'Wat betreft degenen die geloofden en goede werken verrichtten: voor hen zijn er Tuinen (het Paradijs) als verblijfplaats, als ontvangst voor wal zij plachten te doen.'),
(3523, 32, 20, 'En wat betreft degenen die zware zonden begingen: hun verblijfplaats is de Hel. Iedere keer dat zij eruit willen komen, worden zij erin terruggedreven, en wordt er tot hen gezegd: "Proeft de bestraffing van de Hel die jullie plachten te loochenen."'),
(3524, 32, 21, 'En Wij zullen hun zeker de bestraffing van het wereldse (leven) dom proeven, vóór de grotere bestraffing, Hopelijk zullen zij terugkeren.'),
(3525, 32, 22, 'En wie is er onrechtvaardiger dan degene die met de Verzen van zijn Heer wordt vermand en zich er vervolgens van afwendt? Voorwaar, Wij zullen de zondaren vergelden.'),
(3526, 32, 23, 'En voorzeker, Wij hebben Môesa het Boek (de Taurât) gegeven. Verkeer daarom (O Moehammad) niet in twijfel over de ontvangst ervan (de Koran). En Wij maakten kom (de Taurât) tot Leiding voor de Kinderen van Israël.'),
(3527, 32, 24, 'En Wij stelden vanwege kun geduld onder hen leiders aan, die leiding gaven volgens Ons bevel. En zij waren van Onze Tekenen oveirtuigd.'),
(3528, 32, 25, 'Voorwaar, jouw Heer oordeelt tussen hen op de Dag der Opstanding over dat waarover zij plachten te redetwisten.'),
(3529, 32, 26, 'Is er geen leiding voor hen in hoeveel generaties Wij vóór hen hebben vernietigd? Zij lopen in kun woonplaatsen rond. Voorwaar, daarin zijn zeker Tekenen. Luisteren zij dan niet?'),
(3530, 32, 27, 'En zien zij niet dat Wij het water (in wolken) naar het dorre land drijven, waarna Wij gewassen tevoorschijn doen komen, waarvan hun vee en zij zelf eten? Zien zij dan niet?'),
(3531, 32, 28, 'En zij zeggen: "Wanneer zal die overwinning plaatsvinden, als jullie waarachtigen zijn?"'),
(3532, 32, 29, 'Zeg: "Op de Dag van de overwinning zal het geloof van degenen die ongelovig waren niet meer baten, en hen zal geen uitstel worden gegeven."\''),
(3533, 32, 30, 'Wend je daarom van hen af en wacht af: voorwaar, zij zijn afwachtenden.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'O Profeet, vrees Allah en gehoorzaam niet de ongelovigen en de huichelaars: voorwaar, Allah is Alwetend, Alwijs.'),
(3535, 33, 2, 'En volg wat aan jou is geopenbaard van jouw Heer. Voorwaar, Allah, Hij is Alwetend over wat jullie doen.'),
(3536, 33, 3, 'En vertrouw op Allah, en Allah is voldoode als Beschermer.'),
(3537, 33, 4, 'Allah heeft voor geen mens twee harten in zijn binnenste gemaakt. En Hij heeft niet jullie echtgenotes, tot wie jullie de Zhihâr hebben uitgesproken, tot jullie moeders gemaakt. En Hij heeft niet jullie aangenomen zonen tot jullie (echte) zonen gemaakt. Dat zijn jullie woorden uit jullie monden. En Allah spreekt de Waarheid, en Hij leidt op de Weg.'),
(3538, 33, 5, 'Roept hen met de namen van hun vaders, dat is rechtvaardiger bij Allah. En als jullie hun vaders niet kennen, dan zijn ze jullie broeders in de godsdienst en jullie beschermelingen. En er is geen zonde voor jullie in de fouten die jullie daarmee (per ongeluk) hebben gemaakt, maar wet in wat jullie harten zich hebben voorgenomen. En Allah is Vergevensgezind, Meest Barmhartige.'),
(3539, 33, 6, 'De Profeet is de gelovigen meer nabij dan zij zichzelf. En zijn echtgenetes zijn hun moeders. En de bloedverwanten zijn meer nabij (in erfrecht) volgens het Boek van Allah dan de gelovigen en de uitgewekenen, behalve wanneer jullie voor juille broeders (in het geloof) een goede daad willen verrichten (in het testament). Dat staat in het Boek beschreven.'),
(3540, 33, 7, 'En (gedenk) toen Wij met de Profeten hun verbond aangingen en met jou (O Moehammad), en met Nôeh en lbrâhîm en Môesa en \' îsa, de zoon van Maryam. En Wij gingen met hen een plechtig verbond aan.'),
(3541, 33, 8, 'Opdat Hij de waarachtigen over hun waarachtigheid zal ondervragen. En Hij heeft voor de ongelovigen een pijnlijke bestraffing voorbereid.'),
(3542, 33, 9, 'Ojullie die geloven, gedenkt de genade van Allah aan jullie, toen een leger tot jullie was geko men. Wij zonden toen een (verwoestende) wind tegen hen en een leger (van Engelen) dat jullie niet zagen. En Allah is Alziende over wat jullie doen.'),
(3543, 33, 10, 'Toen zij tot jullie kwamen, van boven jullie en van onder jullie en toen de ogen zich afwendden en de harten in de kelen schoten, en jullie (het slechte) over Allah veronderstelden.'),
(3544, 33, 11, 'Daar werden de gelovigen beproefd en hevig geschokt.'),
(3545, 33, 12, 'En (gedenk) toen de huichelaars en degenen, in wier harten een ziekte is, zeiden: "Allah en Zijn Boodschapper hebben ons slechts een misleidende belofte gedaan.'),
(3546, 33, 13, 'En (gedenk) toen een groep van hen zei: "O bewoners van Yatsrib (Medinah), er is geen plaats voor jullie. gaat daarom terug." En een groep onder hen vroeg (de Profeet) ontheffing, zeggend: "Voorwaar, onze huizen zijn onbeschermd," terwijl deze niet onbeschermd waren, zij wilden alleen maar vluchten.'),
(3547, 33, 14, 'En als (Medinah) langs alle kanten binnengevallen zou worden, en zij gevraagd zouden worden de beproeving (te doorstaan), dan zouden zij zeker ingestemd hebben, en zij zouden slechts even hebben geaarzeld.'),
(3548, 33, 15, 'En voorzeker. zij hadden Allah daarvôôr een belofte gedaan: dat zij niet de rug zouden toekeren. En over de belofte aan Allah zal ondervraagd worden.'),
(3549, 33, 16, 'Zeg (O Moehammad): "Vluchten zal jullie niet baten. Als jullie zouden vluchten voor de dood of het doden, dan zouden jullie slechts even genieten."'),
(3550, 33, 17, 'Zeg. "Wie is het die jullie tegen Allah kan beschermen als Hij voor jullie de vernietiging wenst, of als Hij voor jullie genade wenst?" En zij (de huichelaars) zullen voor zichzelf naast Allah geen beschermer en geen helper vinden.'),
(3551, 33, 18, 'Waarlijk, Allah kent degenen onder jullie die belemmeren, en die tot hun broeders zeggen: "Komt bij ons:" en zij nemen niet deel aan de strijd, behalve even.'),
(3552, 33, 19, 'Zij (de huichelaars) zijn gicrig tegenover jullie. Wanneer den de angst (voor de slag) komt, dan zie jij hen naar jou kijken, terwijl hun ogen rollen als van degene die uit doodsangst flauw valt. En als de angst is verdwenen, dan kwetsen zij jullie met scherpe tongen, terwijl zij gierig zijn met het goede. Zij zijn degenen die niet geloven, daarom deed Allah hun daden vruchteloos worden. En dat is voor Allah gemakkelijk.'),
(3553, 33, 20, 'Zij dachten dat de bondgenoten nog niet waren weggetrokken. En als de bondgenoten zouden terugkomen, dan zouden zij wensen dat zij zich bij de bedoeïnen bevonden, vragend naar nieuws over jullie. En als zij zich onder jullie zouden bevinden, dan zouden zij niet strijden, behalve even.'),
(3554, 33, 21, 'Voorzeker, de Boodschapper van Allah is voor jullie een goed voorbeeld: voor wie op (de beloning van) Allah en het Hiernamaals hoopt, en voor wie Allah veelvuldig gedenkt.'),
(3555, 33, 22, 'En toen de gelovigen de bondgenoten zagen, zeiden zij: "Dit is wat Allah en Zijn Boodschapper ons hebben beloofd," en Allah en Zijn Boodschapper hebben gelijk. En het doet hun slechts toenemen in geloof en onderwerping.'),
(3556, 33, 23, 'Onder de gelovigen zijn er mannen die de belofte die zij aan Allah gedaan hebben trouw blijven. Onder hen zijn er wiens wens vervuld is (omwille van Allah gedood zijn) en anderen die (daarop) wachten. En zij hebben niets veranderd (in hun belofte).'),
(3557, 33, 24, 'Opdat Allah de waarachtigen zal belonen voor hun waarachtigheid en Hij de huichelaars zal bestraffen, als Hij wil, of hun berouw aanvaarden. Voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(3558, 33, 25, 'En Allah dreef de ongelovigen en hun woede (over hun verlies) terug; zij verkregen niets van het goede. En Allah hield (de ongelovigen) af van de gelovigen in de strijd. En Allah is Sterk, Almachtig'),
(3559, 33, 26, 'En Hij zond degenen die hen (de bondgenoten) steunden van de Lieden van de Schrift van hun forten naar beneden. En Hij wierp angst in hun harten. Jullie doodden een groep (mannen) en jullie namen een groep (vrouwen en kinderen) gevangen.'),
(3560, 33, 27, 'En Hij deed jullie hun land en hun huizen en hun bezittingen erven en een land dat jullie nog niet betreden hebben. En Allah is Almachtig over alle zaken.'),
(3561, 33, 28, 'O Profeet, zeg tot jouw echtgenotes: "Als jullie het wereldse leven wensen en de versieringen ervan, komt dan, dan zal ik jullie een scheidingsgift schonken en jullie op een eervolle manier laten gaan.'),
(3562, 33, 29, 'Maar als jullie (het welbehagen van) Allah en zijn Boodschapper wensen, en het Huis van het Hiernamaals: voorwaar, Allah heeft voor de weldoensters onder jullie een geweldige beloning bereid."'),
(3563, 33, 30, 'O Vrouwen van de Profeet, wie van jullie een duidelijke zedeloosheid begaat, die zal daarvoor een dubbele bestraffing ontvangen. En dat is voor Allah gemakkelijk.'),
(3564, 33, 31, 'En wie van jullie Allah en Zijn Booddschapper gehoorzaamt en goede werken verricht, die zullen Wij twee maal een beloning geven. Wij hebben voor haar een edele voorziening bereid.'),
(3565, 33, 32, 'O vrouwen van de Profeet, jullie zijn zoals geen van de andere vrouwen, als jullie (Allah) vrezen. Weest daarom niet minzaam in jullie manier van spreken waardoor degene in wiens hart een ziekte is, begeerte gaat voelen, en spreekt een juist woord.'),
(3566, 33, 33, 'En blijft in jullie huizen en vertoont jullie veirsieringen niet zoals dat in de vroegere tijd van de onwetendheid werd gedaan. En onderhoudt de shalât en geeft de zakât en gehoorzaamt Allah en Zijn Boodschapper. Voorwaar, Allah wenst slechts de onreinheid van jullie weg te nemen, O Lieden van het huis (Ahloel Bait), en jullie zo rein mogelijk te maken.'),
(3567, 33, 34, 'En herinnert jullie wat in jullie huizen wordt voorgedragen van de Verzen van Allah en van de wijsheid. Voorwaar, Allah is Zachtmoedig, Alwetend.'),
(3568, 33, 35, 'Voorwaar, de mannen die zich hebben overgegeven (aan Allalh) en de vrouwen die zich hebben overgegeven, en de gelovige mannen en de gelovige vrouwen, en de gehoorzame mannen en de gehoorzame vrouwen, en de waarachtige mannen en de waarachtige vrouwen, en de geduldige mannen en de geduldige vrouwen, en de ootmoedige mannen en de ootmoedige vrouwen, en de bijdragen gevende mannen en de bijdragen gevende vrouwen, en de vastende mannen en de vastende vrouwen en de mannen die over hun kuisheid waken en de vrouwen die (daarover) waken, en de mannen die Allah veelvuldig gedenken en de vrouwen die gedenken: Allah heeft voor hen vergeving bereid en een geweldige beloning.'),
(3569, 33, 36, 'En het past een gelovige man en een gelovige vrouw niet, wanneer Allah en Zijn Boodschapper een zaak hebben besloten, om een andere keuze te maken in hun zaak. En wie Allah en Zijn Boodschapper niet gehoorzaamt: waarlijk, hij verkeert in duidelijke dwaling.'),
(3570, 33, 37, 'En (gedenk) toen jij (O Moehammad) zei tot degene die door Allah begenadigd was en aan wie jij genade had gegeven: "Hou jouw echtgenote bij jou, en vrees Allah." Toen jij in je hart verborgen hield wat Allah openbaar wilde maken, en jij de mensen vreesde, terwijl Allah er meer recht op had dat je Hem vreesde. Toen Zaid geen behoefte meer aan haar had, toen hebben Wij jou met haar gehuwd, zodat er voor de gelovigen geen belemmering zou bestaan met betrekking tot de (voormalige) vrouwen van hun aangenomen kinderen, wanneer zij geen behoefte meer aan hen hebben. En het bevel van Allah wordt uitgevoerd.'),
(3571, 33, 38, 'Er is voor de Profeet geen probleem in wat Allah hem verplicht heeft. Dat is de handelwijze van Allah zoals die reeds gold voor de voorafgaanden (Profeten). En het bevel van Allah is een vaststaande beschikking.'),
(3572, 33, 39, '(Zij waren) degenen die de Boodschappen van Allah verkondigden en die Hem vreesden en die niemand vreesden dan Allah. En Allah is voldoende als Berekenaar.'),
(3573, 33, 40, 'Moehammad is niet de vader van één van jullie mannen, maar hij is de Boodschapper van Allah en de laatste van de Profeten. En Allah is Alwetend over alle zaken.'),
(3574, 33, 41, 'O jullie die geloven, gedenkt Allah veelvuldig!'),
(3575, 33, 42, 'En prijst Zijn Glorie in de ochtend en in de avond.'),
(3576, 33, 43, 'Hij is Degene Die jullie Barmhartigheid schenkt en Zijn Engelen (smeken om vergeving voor jullie), opdat Hij jullie uit de duisternissen naar het licht zal brengen, en Hij is voor de gelovigen Meest Barmhartig.'),
(3577, 33, 44, 'Hun begroeting op de Dag van de ontmoeting met Hem is: "Salâm!"\' (Vrede!) En Hij heeft voor hen een edele beloning bereid.'),
(3578, 33, 45, 'O Profeet, voorwaar, Wij hebben jou gezonden als een getuige en als een brenger van verheugende tijdingen en als een waarschuwer.'),
(3579, 33, 46, 'En als een oproeper tot Allah, met Zijn toestemming, en als een verlichtende lamp.'),
(3580, 33, 47, 'En verkondig de gelovigen de verheugende tijding: dat er voer hen een grote gunst is van Allah.'),
(3581, 33, 48, 'En gehoorzaam de ongelovigen en de huichelaars niet en schenk geen aandacht aan hun kwelling. En vertrouw op Allah. En Allah is voldoende als Beschermer.'),
(3582, 33, 49, 'O jullie die geloven, wanner jullie de gelovige vrouwen hebben gehuwd en daarna van hen scheiden, voordat jullie hen hebben aangerakt, dan is er voor jullie geen plicht om hen een wachtperiode in acht te laten nemen. Geeft hun dan een geschenk en laat hen op een eervolle wijze gaan.'),
(3583, 33, 50, 'O Profeet, voorwaar. Wij hebben jou jouw echtgenotes die jij hun bruidschat hebt gegeven toegstaan, en de slavinnen waar jij over beschikt van wat Allah voor jou heeft aangewezen; en de dochters van jouw oom en de dochters van jouw tantes van vaderszijde; en de dochters van jouw oom van moederszijde en de dochters van jouw tantes van moederszijde. En (ook) zij die met jou zijn uitgeweken, en de gelovige vrouw die zichzelf aan de Profeet heeft geschonken, als de Profeet haar wenst te huwen, als een uitzondering voor jouzelf, die niet geldt voor de gelovigen. Voorzeker, Wij weten wat Wij hen verplicht hebben met betrekking tot hun echtgenotes en waar hun rechterhand over beschikt, opdat er voor jou geen moeilijkheid zal zijn. En Allah is Vergevensgezind, Meest Barmhartig.'),
(3584, 33, 51, 'Jij mag uitstel geven aan wie van hen jij wenst en jij mag tot jou nemen wie jij wenst. En naar wie jouw hart uitgaat van hen van wie jij afstand hebt genomen; er is voor jou geen zonde in. Dat is de verkoeling der ogen meer nabij. Laten zij dan niet treuren en laten zij tevreden zijn met wat jij hen allen hebt gegeven. En Allah kent wat in jullie harten is. En Allah is Alwetend, Zachtmoedig.'),
(3585, 33, 52, 'Daarna (O Moehammad), zijn de (andere) vrouwen niet toegestaan en ook niet dat jij hen vervangt door (andere) echtgenotes, ook al wordt jij aangetrokken door hun schoonheid, behalve de slavinnen waar jij over beschikt. En Allah waakt over alle zaken.'),
(3586, 33, 53, 'O jullie die geloven, gaat de huizen van de Profeet niet binnen, behalve als jullie toestemming is gegeven om er te eten, zonder er de etenstijd af te wachten. Maar wanneer jullie worden uitgenodigd, gaat er dan naar binnen, en wanneer jullie dan hebben gegeten, verspreidt jullie dan. En blijft niet met elkaar zitten praten. Dat zou lastig voor de Profeet zijn en hij zou in verlegenheid gebracht worden. Allah schaamt zich niet voot de Waarheid. En wanneer jullie hun (de vrouwen van de Profeet) om iets vragen, vraagt het dan van achter de afscheiding. Dat is reiner voor jullie harten en voor hun harten. En jullie mogen de Boodschapper van Allah niet kwetsen en jullie mogen nooit na hem één van zijn echtgenotes huwen. Voorwaar, dat is bij Allah een grote zonde.'),
(3587, 33, 54, 'Of jullie iets openlijk doen, of het verbergen: voorwaar, Allah is Alwetend over alle zaken.'),
(3588, 33, 55, 'Het is geen zonde voor hen (vrouwen van de Profeet) om met hun vaders, hun zonen, hun broeders, de zonen van hun broeders, de zonen van hun zusters en hun vrouwen, en met de slavinnen waar zij over beschikken (om zonder gebruikmaking van een afscherming tot anderen te spreken). En vreest Allah. Voorwaar, Allah is Getuige over alle zaken.'),
(3589, 33, 56, 'Voorwaar, Allah geeft Barmhartigheid en Zijn Engelen smeken om vergeving voor de Profeet. O jullie die geloven, smeekt om genade voor hem en spreekt de vredewens over hem uit.'),
(3590, 33, 57, 'Voorwaar, degenen die Allah en Zijn Boodschapper beledigen: Allah zal hen vervloeken in deze wereld en in het Hiernamaals en Hij zai voor hen een vernederende bestraffing bereiden.'),
(3591, 33, 58, 'En degenen die de gelovige mannen en de gelovige vrouwen kwetsen, zonder dat zij iets (slechts) hebben verricht: voorzeker, zij hebben laster en een duidelijke zonde op zich geladen.'),
(3592, 33, 59, 'O Profeet, zeg tot jouw echtgenotes en tot jouw dochters en tot de vrouwen van de gelovigen dat zij hun overkleden (Djilbâb) over zich heen laten hangen. Op die manier is het gemakkelijker om hen te herkennen en worden zij niet lastig gevallen. En Allah is Vergevensgezind, Meest Barmhartig.'),
(3593, 33, 60, 'Als de huichelaars, en degenen in wier harten een ziekte is, en de lasteraars in Medinah, niet ophouden, dan zullen Wij jou tegen hen maatregelen laten nemen. Daarna zullen zij daar niet jouw buren zijn, behalve even.'),
(3594, 33, 61, 'Vervloekten zijn zij. Waar zij ook worden aangetroffen: grijpt hen en doodt hen.'),
(3595, 33, 62, 'Als de handelwijze van Allah met de voorafgaanden. En jij zal in de handelwijze van Allah nooit een verandering aantreffen.'),
(3596, 33, 63, 'De mensen vragen jou over het Uur. Zeg: "Voorwaar, kennis daarover berust slechts bij Allah." En hoe kan jij dat weten? Misschien is het Uur al nabij.'),
(3597, 33, 64, 'Voorwaar, Allah heeft de ongelovigen vervloekt en voor hen een laaiend vuur bereid.'),
(3598, 33, 65, 'Eeuwig levenden zijn zij daarik voor altijd. Zij zullen geen beschermers en geen helpers vinden.'),
(3599, 33, 66, 'Op de Dag waarop hun gezichten zullen worden rondgedraaid in de Hel zeggen zij: "Hadden wij Allah maar gehoorzaamd en hadden wij de Boodschapper maar gehoorzaamd."'),
(3600, 33, 67, 'En zij zeggen: "Onze Heer, voorwaar, wij gehoorzaamden onze leiders en onze vooraanstaanden, waarop zij ons van de Weg deden afdwalen.'),
(3601, 33, 68, 'Onze Heer, tref hen rnet het dubbele van de bestraffing en vervloek hen met een grote vervloeking."'),
(3602, 33, 69, 'O jullie die geloven, weest niet zoals degenen die Môesa kwetsten. Allah verklaarde hem onschuldig aan wat zij zeiden. En hij staat bij Allah in hoog aanzien.'),
(3603, 33, 70, 'O jullie die geloven, vreest Allah en spreekt het ware woord.'),
(3604, 33, 71, 'Hij (Allah) zal jullie goede daden aanvaarden en jullie je zonden vergeven. En wie Allah en Zijn Boodschapper gehoorzaamt: waarlijk, die heeft een geweldige triomf behaald.'),
(3605, 33, 72, 'Voorvaar, Wij hebben de Amânah (godsdienstige plichten) aan de hemelen en de aarde en de bergen aangeboden, maar zij weigerden deze te dragen en zij waren er beducht voor, maar de mens nam deze op zich. Voorwaar, hij is onrechtvaardig en onwetend.'),
(3606, 33, 73, '(De Amânah werd hen aangeboden) opdat Allah de huichelaars en de huichelaarsters en de veelgodenaanbidders en de veelgodenaanbidsters zal bestraffen. En Allah aanvaardt het berouw van de gelovige mannen en de gelovige vrouwen. En Allah is Vergevensgezind en Meest Barmhartig');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Alle lof zij Allah, Degene aan Wie wat er in de hemelen en op de aarde is behoort; en Hem zij alle lof in het Hiernamaals. En Hij is de Alwijze, de Alwetende.'),
(3608, 34, 2, 'Hij kent wat de aarde ingaat en wat er uit voortkomt en wat neerdaalt uit de hemel en wat erin opstijgt. En Hij is de Meest Barmhartige, de Vergevensgezinde.'),
(3609, 34, 3, 'En degenen die ongelovig zijn, zeggen: "Het Uur zal niet tot ons komen." Zeg: "Welzeker, bij mijn Heer, de Kenner van het onwaarneembare, het zal zeker tot jullie komen. Er is niets met het gewicht van een mosterdzaadje in de hemelen of op de aarde dat Hem ontgaat. En er is niets wat groter of kleiner is dan dat, of het staat vermeld in en duidelijk Boek.'),
(3610, 34, 4, '(Dat is) opdat Hij degenen die geloven en goede werken verrichten zal belonen, zij zijn degenen voor wie er vergeving is en een edele voorziening.'),
(3611, 34, 5, 'Maar degenen die ernaar streven om Onze Verzen te ontkrachten: zij zijn degenen voor wie er een bestraffing van een pijnlijke kastijding is.'),
(3612, 34, 6, 'En degenen ma wie kennis is gegeven, zien dat wat aan jou (O Moehammad) is neergezonden van jouw Heer (de Koran) de Waarheid is, en dat hij naar het Pad van de Almachtige, de Geprezene leidt.'),
(3613, 34, 7, 'En degenen die ongelovig zijn, zeggen: "Zullen wij jullie een man aanwijzen die jullie vertelt dat wanneer jullie geheel uiteen gevallen zijn, dat jullie dan zeker een nieuwe schepping worden?"'),
(3614, 34, 8, 'Heeft hij een leugen over Allah verzonnen, of is hij bezeten? Neel Degenen die niet in het Hiernamaals geloven, zullen bestraft worden en zij verkeren in vergaande dwaling.'),
(3615, 34, 9, 'Kijken zij dan niet naar wat voor hen en achter hen is van de hemel en de aarde? Als Wij zouden willen, dan zouden Wij hen in de aarde doen wegzinken, of een stuk uit de hemel op hen doen vallen. Voorwaar, daarin is zeker een Teken voor iedere berouwvolle dienaar.'),
(3616, 34, 10, 'En voorzeker, Wij hebben Dâwôed een gunst van Onze Zijde de geschonken, (zeggend:) "O bergen, herhaalt het prijzen van de lof van Allah met hem, en ook de vogels." En Wij maakten het ijzer zacht voor hem.'),
(3617, 34, 11, '(Allah zei:) "Maak maliënkolders en bepaal de maat van de ringen," En verricht goede werken. Voorwaar, Ik zie wat jullie doen.'),
(3618, 34, 12, 'En voor Soelaimân (onderwierpen Wij) de winden. Het waaien ervan in de ochtend duurt een maand en in de avond duurt het een maand. En Wij maakten het brons voor hem tot een vloeiende bron. En onder de Djinn\'s waren er die voor hem werkten, met toestemming van zijn Heer. En wie van hen afweek van Ons bevel, die doen Wij de bestraffing van het laaiende vuur (de Hel) proeven.'),
(3619, 34, 13, 'Zij maakten voor hem wat hij wilde; hoge gehouwen, beelden en schalen zo groot als vijvers en onverplaatsbare ketels. Werkt, O familie van Dâwôed, uit dankbaarheid. Maar, weinigen van Mijn dienaren zijn dankbaren.'),
(3620, 34, 14, 'Toen Wij dan voor hem (Soelaimân) de dood bedden beschikt, was er niets dat hen op zijn dood wees, behalve de diertjes van de aarde die zijn staf opaten. Toen hij (zijn lichaam) was gevallen, was het de Djinn\'s duidelijk (dat bij dood was). Hadden zij maar kennis gehad over het onwaarneembare, dan zouden zij niet in de vernederende bestraffing zijn gebleven.'),
(3621, 34, 15, 'En voorzeker, voor (het volk van) Saba\' is er in hun woonplaatsen een teken: twee tuinen, aan de rechterzijde en aan de linkerzijde (van de vallei). (Wij zeiden hen:) "Eet van de voorzieningen van jullie Heer en weest Hem dankbaar. (Het is) een welvarend land en een Vergevensgezinde Heer."'),
(3622, 34, 16, 'Daarna wendden zij zich af, waarop Wij een vernietigende overstroming over hen brachten. En Wij vervingen hun twee tuinen door twee tuinem met bittere vruchten en tamariskbomen en een klein aantal lotusbomen.'),
(3623, 34, 17, 'Zo vergolden wij hun omdat zij ongelovigen waren. En Wij vergelden niemand dan de ondankbaren.'),
(3624, 34, 18, 'En Wij maakten tussen hen en de steden die Wij hadden gezegend, duidelijk zichtbare steden en Wij maakten daarlangs een reis mogelijk. Reist daarlangs in veiligheid, dag en nacht.'),
(3625, 34, 19, 'En zij zeiden: "Onze Heer, maak onze reis lang." En zij deden zichzelf onrecht aan en Wij maakten hen tot onderwerp van gesprek. En Wij verspreidden hen geheel uit elkaar. Voorwaar, daarin zijn zeker tekenen voor iedere geduldige dankbare.'),
(3626, 34, 20, 'En voorzeker, de veronderstelling van Iblîs over hen werd bewaarheid, want zij volgden hem, met uitzondering van een groep gelovigen.'),
(3627, 34, 21, 'En hij (Iblîs) had geen macht over hen, (maar het was zo) opdat Wij degenen die in het Hiernamaals geloven zouden onderscheiden van degenen die daarover in twijfel verkeren. En jouw Heer is de Waker over alle zaken.'),
(3628, 34, 22, 'Zeg: "\'Roept degenen op, die jullie beweren (als goden) naast Allah te zijn." Zij hebben zelfs over het gewicht van een mosterdzaadje in de hemelen en op de aarde geen macht en zij hebben daarin geen aandeel. En Hij heeft onder hen geen helper.'),
(3629, 34, 23, 'En de voorspraak baat bij Hem niet, behalve aan wie Hij toestemming heeft gegeven. Totdat, wanneer de angst van hun harten is weggenomen, zij vragen: "Wat heeft jullie Heer gezegd?" Zij zeggen: "De Waarheid." En Hij is de Verhevene, de Grootste.'),
(3630, 34, 24, 'Zeg: "Wie schenkt jullie voorzieningen uit de hemelen en de aarde?" Zeg: "Allah! En voarwaar, wij zijn het of jullie zijn het die zeker de rechte Leiding volgen of in duidelijke dwaling verkeren."'),
(3631, 34, 25, 'Zeg: "Jullie zullen niet ondervraagd worden over wat wij misdaan hebben en wij zullen niet worden ondervraagd over wat jullie deden."'),
(3632, 34, 26, 'Zeg. "Onze Heer zal ons bijeenbrengen, daarna zal Hij tussen ons oordelen volgens de Waarheid." En Hij is de Oordelaar, de Kenner.'),
(3633, 34, 27, 'Zeg: "Toont mij degenen die jullie aan Hem toevoegen als deelgenoten." Nee, Hij is zelfs Allah, de Almachtige, de Alwijze.'),
(3634, 34, 28, 'En Wij hebben jou niet anders gezonden dan aan de gehele mensheid en als een verkondiger van verheugende tijdingen en als een waarschuwer. Maar de meeste mensen weten het niet.'),
(3635, 34, 29, 'En zij zeggen: "Wanneer zal de belofte vervuld worden, als jullie waarachtigen zijn?"'),
(3636, 34, 30, 'Zeg: "Voor jullie is er een belofte van een Dag, die jullie geen moment kunnen uitstellen of bespoedigen.'),
(3637, 34, 31, 'En degenen die ongelovig zijn, zeggen: "Wij zullen nooit in deze Koran geloven, en niet in wat ervóór was." En als jij (O Moehammad) de onrechtplegers zouden kunnen zien, wanneer Zij voorgeleid worden bij hun Heer. Zij zullen elkaar woorden verwijten. Degenen die volgelingen waren, zeggen tot degenen die hoogmoedig waren: "Als jullie ons niet hadden misleid, dan zouden wij zeker gelovigen zijn geweest"'),
(3638, 34, 32, 'Degenen die hoogmoedig waren, zullen tot degenen die volgelingen waren zeggen: "Waren wij het die jullie hebben afgehouden van de rechte Leiding, nadat deze tot jullie was gekomen? Nee! Jullie waren de misdadigers."'),
(3639, 34, 33, 'En degenen die volgengen waren, zuilen tot degenen die hoogmoedig weren zeggen: "Het was zelfs (jullie) list gedurende de nacht en de dag, toen jullie ons opriepen om niet in Allah te geloven en om naast Hem afgoden toe te kennen." En zij zullen de spijt verbergen wanneer zij de bestraffing zien en Wij ketenen om de nekken van degenen die ongelovig waren leggen. Zij worden slechts vergolden voor wat zij plachten te doen.'),
(3640, 34, 34, 'En Wij zonden geen waarschuwer naar een stad, of degenen die daarin in weelde leefden zeiden: "Voorwaar, wij geloven niet in wat jullie mee zijn gezonden."'),
(3641, 34, 35, 'En zij zeiden: "Wij hebben meer bezittingen en kinderen en wij zullen niet worden bestraft."'),
(3642, 34, 36, 'Zeg: "Voorwaar, mijn Heer verruimt de voorzieningen voor wie Hij wil en Hij beperkt, maar de meeste mensen weten het niet."'),
(3643, 34, 37, 'En noch jullie bezittingen, noch jullie kinderen zijn het die jullie dichter bij Ons brengen, maar wie gelooft en goede werken verricht: zij zijn degenen voor wie er een veelvoudige beloning is voor wat zij gedaan hebben, en zij zulten in de verblijfplaatsen veilig zijn.'),
(3644, 34, 38, 'En degenen die ernaar streven om Onze Tekenen te ondkrachten; zij zijn degenen die voor de bestraffing voorgeleid zullen worden.'),
(3645, 34, 39, 'Zeg: "Voorwaar, mijn Heer verruimt de voorzieningen voor wie Hij wil van Zijn dienaren en Hij beperkt voor hen. En wat jullie ook aan bijdragen uitgeven; Hij zal het vergoeden. En Hij is de Beste der Voorzieners."'),
(3646, 34, 40, 'En (gedenkt) de Dag dat Hij hen allen verzamelt en dan tot de Engelen zegt: "Waren zij degenen die jullie plachten te aanbidden?"'),
(3647, 34, 41, 'Zij zullen zeggen: "Heilig bent U, U bent onze Beschermer, niet zij. Zij plachten zelfs de Djinn\'s (de Satan) te aanbidden, de meesten van hen geloofden hem."'),
(3648, 34, 42, 'Op deze Dag zijn jullie niet bij machte om elkaar van nut te zijn en niet om elkaar schade te berokkenen, en Wij zullen zeggen tot degenen die onrecht pleegden: "Proeft de bestraffing van de Hel die jullie plachten te loochenen."'),
(3649, 34, 43, 'En als Onze duidelijke Verzen aan hen worden voorgedragen, zeggen zij: "Dit is niets anders dan een man die jullie wil affhouden van wat jullie vaderen plachten te aanbidden." En zij zeggen: "Dit is niets anders dan een verzonnen lcugen." En degenen die ongelovig zijn, zeiden over de Waarheid toen deze tot hen kwam: "Dit is niets anders dan duidelijke tovenarij."'),
(3650, 34, 44, 'En Wij hebben ben geen boeken gegeven die zij bestudeerden. En Wij hebben vóór jou geen waarschuwer tot hen gezonden.'),
(3651, 34, 45, 'En degenen vóór hen loochenden terwijl zij (de ongelovigen) nog geen tiende hadden bereikt van wat Wij hun (de nlachtige volkeren) hadden gegeven. En zij loochenden Mijn Boodschappers. En hoe (verschrikkelijk) was toen Mijn afschuw!'),
(3652, 34, 46, 'Zeg: "Voorwaar, ik vermaan jullie tot slechts één ding: dat jullie je gelweeën of alleen voor Allah opstellen en dan nadenken." Jullie metgezel (Moehammad) is niet bezeten; hij is slechts een waarschuwer voor jullie, voor een harde bestraffing.'),
(3653, 34, 47, 'Zeg: "Ik heb jullie niet om een beloning gevraagd; die is voor jullie zelf Mijn beloning is slechts bij Allah en Hij is Getuige over alle zaken."'),
(3654, 34, 48, 'Zeg: "Mijn Heer openbaart de Waarheid. Hij is de Kenner van het onwaarneembare."'),
(3655, 34, 49, 'Zeg: "De Waarheid is gekomen en de valsheid zal zich niet herhalen en niet terugkomen."'),
(3656, 34, 50, 'Zeg: "Als ik dwaal, dan dwaal ik slechts in het nadeel van mijzelf, en als ik de Leiding volg, dan is dat door wat mijn Heer aan mij heeft geopenbaard," Voorwaar, Hij is Alhorend, nabij.'),
(3657, 34, 51, 'En als jij hen zou zien wanneer zij beven van angst, er zal dan voor hen geen ontkomen Záin en zij zullen uit een nabije plaats genomen worden, (en dan zie jij de geweldige bestraffing).'),
(3658, 34, 52, 'En zij zeggen: "Wij geloven (nu) in hem," maar hoe zouden zij (het gelooft) nog kunnen bereiken, vanaf een verafgelegen plaats?'),
(3659, 34, 53, 'En terwijl zij liem daarvóór waarlijk verwierpen. En zij gissen naar het onwaarneembare vanaf een verafgelegen plaats.'),
(3660, 34, 54, 'En tussen hen en wat zij verlangen is een belemmering gemakt, zoals dat bij hun (soortgelijke) volkeren van vroeger is gedaan. Voorwaar, zij verkeerden in vergaande twijfel.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Alle tof zij Allah, de Schepper van de hemelen en de aarde. Hij heeft de Engelen tot gezanten gemaakt, met twee, drie of vier vleugels. Hij voegt aan de schepping toe wat Hij wil. Voorwaar, Allah is Almachtig over alle zaken.'),
(3662, 35, 2, 'Wat Allah voor de mensen schenkt aan Barmhartigheid, niemand kan het tegenhouden; en wat hij tegenhoudt, buiten Hem kan niemand het loslaten. En Hij is de Almachtige, de Alwijze.'),
(3663, 35, 3, 'O mensen, gedenkt de gunst van Allah voor jullie. Is er een andere schepper dan Allah, Die jullie uit de hemelen en de aarde voorzieningen schenkt? Geen god is er dan Hij. Hoe kunnen jullie dan belogen worden!'),
(3664, 35, 4, 'En als zij jou (O Moehammad) loochenen: voorzeker, de Boodschappers vóór jou werden ook geloochend. En tot Allah worden alle zaken teruggekeerd.'),
(3665, 35, 5, 'O mensen, voorwaar, de belofte van Allah is waar. Laat daarom het wereldse leven jullie niet verleiden en laat de verleider (de Satan) jullie niet van (de vergeving van) Allah weghouden.'),
(3666, 35, 6, 'Voorwaar, de Satan is voor jullie een vijand, beschouwt hem daarom als een vijand. Voorwaar, hij roept zijn volgelingen op om de bewoners van het laaiende vuur (de Het) te worden.'),
(3667, 35, 7, 'Degenen die ongelovig zijn, voor hen is er een harde bestraffing; en degenen die geloven en goede werken verrichten, voor hen is er vergeving en een grote beloning.'),
(3668, 35, 8, 'Is hij dan, wiens slechte daden (door de Satan) schoonschijnend zijn gemaakt en die ze dan ook als goed beschouwt (gelijk aan hem die dit afkeurt)? Maar Allah doet dwalen wie Hij wil en Hij leidt wie Hij wil. Laat je daarom (O Moehammad) niet ten gronde gaan uit wroeging over hen. Voorwaar, Allah is Alwetend over wat zij bedrijven.'),
(3669, 35, 9, 'En Allah is het Degene Die de winden heeft gezonden opdat deze wolken voortdrijven, waarop Wij die naareen dode Week voeren. Dan doen Wij de aarde ermee tot leven komen na haar dood. Zo is ook de opwekking.'),
(3670, 35, 10, 'Wie eer wenst: aan Allah behoort alle eer. Tot Hem stijgt het goede woord op, en de goede daad verheft dit (woord). En degenen die kwade plannen beramen: voor hen is er een harde bestraffing, en de list van diegenen is mislukt.'),
(3671, 35, 11, 'En Allah heeft jullie uit aarde geschapen, vervolgens uit een druppel en daarop maakten Wij jullie tot paren. En er is geen vrouw die zwanger is en bevalt, of het is met Zijn kennis. En de leeftijd van iemand wordt niet verlengd, en zijn leeftijd wordt niet verkort, of het staat in een Boek vemeld. Voorwaar, dat is voor Allah gemakkelijk.'),
(3672, 35, 12, 'Ein de twee zeeën zijn niet gelijk, de éne is zoet, fris en aangenaam om van te drinken, de andere is zout en bitter, maar uit beiden eten jullie vers vlees en halen jullie sieraden tevoorschijn, die jullie dragen. En jullie zien de schepen haar doorklieven opdat jullie Zijn gunst zullen zoeken. En hopelijk zullen jullie dankbaar zijn.'),
(3673, 35, 13, 'Hij doet de nacht overgaan in de dag en Hij doet de dag overgaan in de nacht en Hij heeft de zon en de maan dienstbaar gemaakt. Allen lopen een vastgestelde tijd. Dat is Allah, jullie Heer, aan Hem behoort de heerschappij. En degenen die jullie naast Hem aanroepen, hebben over een dadelvliesje nog geen macht.'),
(3674, 35, 14, 'Als jullie hen aanroepen, dan horen zij jullie aanroep niet, en als zij zouden horen, dan zouden zij jullie (smeekbede) voor jullie niet verhoren. En op de Dag der Opstanding zullen zij het verwerpen dat jullie deelgenoten naast Allah maakten. En niemand brengt jou op de hoogte zoals de Alwetende.'),
(3675, 35, 15, 'O mensen, jullie zijn het die behoefte aan Allah hebben, maar Allah, Hij is de Behoeftloze, de Geprezene.'),
(3676, 35, 16, 'Als Hij wilt, neemt Hij jullie weg en brengt Hij een nieuwe schepping.'),
(3677, 35, 17, 'En dat is voor Allah niet zwaar.'),
(3678, 35, 18, 'En geen enkele lastdrager zal de last (zonden) van een andere dragen. En als een zwaarbelaste ziel om hulp zou roepen om haar last te dragen, dan zal daarvan niets gedragen worden, ook al is hij een verwant. Voorwaar, jij kunt slechts degenen waarschuwen die kun Heer vrezen, hoewel zij Hem niet kunnen waarnemen, en die de shalât onderhouden. En wie zich reinigt: voorwaar, die reinigt zich slechts voor zichzelf, en tot Allah is de terugkeer.'),
(3679, 35, 19, 'En de blinde is niet gelijk aan de ziende.'),
(3680, 35, 20, 'En de duisternissen zijn niet (gelijk) aan het licht.'),
(3681, 35, 21, 'En de schaduw (van het Paradijs) is niet (gelijk) aan de hitte (van de Hel).'),
(3682, 35, 22, 'En de levenden zijn niet gelijk aan de doden. Voorwaar, Allah doet horen wie Hij wil, maar jij krant degenen die in de graven zijn niet doen horen.'),
(3683, 35, 23, 'Jij bent slechts een waarschuwer.'),
(3684, 35, 24, 'Voorwaar, Wij hebben jou met de Waarheid gezonden, als een verkondiger van verheugende tijdingen en als een waarschuwer. En er was geen volk, of er verkeerde onder hen een waarschuwer.'),
(3685, 35, 25, 'En als zij jou loochenen; zij loochenden ook degenen vóór hen: hun Boodschappers kwamen tot hen met duidelijke bewijzen, met de Schriften en met het verlichtende Boek.'),
(3686, 35, 26, 'Daarop greep Ik degenen die ongelovig waren. En hoe (verschrikkelijk) was Mijn afschuw?'),
(3687, 35, 27, 'Zie jij niet dat Allah uit de hemel water doet neerdalen waarmee Wij vruchten met verschillende kleuren voortbrengen? En dat er witgestreepte en roodgestreepte bergen met veerschillende kleuren en ook ravezwarte zijn?'),
(3688, 35, 28, 'En dat er ook onder de mensen, de dieren en het vee zijn die verschillende kleuren hebben? Voorwaar, het zijn slechts de bezitters van kennis onder Zijn dienaren die Allah vrezen. Voorwaar, Allah is Almachtig, Vergevensgezind.'),
(3689, 35, 29, 'Voorwaar, degenen die het Boek van Allah (de Koran) voordragen en de shalât onderhouden en bijdragen geven van waar Wij hun mee voorzien hebben, in het verborgene of openlijk: zij hopen op een handel die geen verlies zal geven.'),
(3690, 35, 30, 'Opdat Hij hun hun volle beloning zal geven en voor hen zal toevoegen van Zijn gunst. Voorwaar, Hij is Vergevensgezind, Ment Waarderend.'),
(3691, 35, 31, 'En wat Wij aan jou hebben geopenbaard van het Boek, dat is de Waarsheid, als een bevestiging van wat ervóór was. Voorwaar, Allah is zeker Alwetend over Zijn dienaren, Alziend.'),
(3692, 35, 32, 'Daarna deden Wij degenen die Wij verkozen van Onze dienaren het Boek erven. En onder hen zijn er die zichzelf onrecht aandoen, en onder hen zijn er die gematigd zijn, en onder hen zijn er die wedijveren in de goede werken, met toestemming van Allah. Dat is de grote gunst.'),
(3693, 35, 33, 'De Tuinen van \'Adn (het Paradijs) zullen zij binnengaan, waarin zij gesierd zullen worden met gouden armbanden en parels, en hun gewaden zijn daar van zijde.'),
(3694, 35, 34, 'En zij zullen zeggen: "Alle lof zij Allah Die onze treurnis heeft weggenomen: voorwaar, onze Heer is zeker Vergevensgezind, Meest Waarderend.'),
(3695, 35, 35, 'Degene Die ons door Zijn gunst het Blijvende Huis (het Paradijs) heeft geschonken, waarin vermoeidheid ons niet raakt en verveeldheid ons niet raakt."'),
(3696, 35, 36, 'Maar degenen die ongelovig zijn, voor hen is er het vuur van de Hel en er is geen beschiking (om dood te gaan) voor hen bepaald, zodat zij zouden sterven. En de straf ervan zal voor hen niet worden verlicht. Zo vergelden Wij iedere zeer ongelovige.'),
(3697, 35, 37, 'En zij zullen daarin schreeuwen: "Onze Heer, haat ons hier uit, dan zullen wij goede werken verrichten, wat wij niet plachten te doen." Hebben Wij jullie geen lang leven geschonken, zodat wie wilde de vemaningen te harten kom nemen? En de waarschuwer is tot jullie gekomen. Proeft daarom (de bestraffing). en voor de onrechtplegers is er geen helper.'),
(3698, 35, 38, 'Voorwaar, Allah is de Kenner van het onwaarneembare van de hemelen en de aarde. Voorwaar, Hij is Alwetend over wat zich in de harten bevindt.'),
(3699, 35, 39, 'Hij is Degene Die jullie heeft aangesteld als gevolmachtigden op aarde. Wie dan ongelovig is: zijn ongeloof is tegen hem. En het ongeloof van de ongelovigen doet bij hun Heer niets toenemen den toorn. En het ongeloof van de ongelovigen doet niets toenemen dan verlies.'),
(3700, 35, 40, 'Zeg (O Moehammad): "Hebben jullie jullie afgoden gezien die jullie naast Allah aanroepen? Toont mij wat zij van de aarde hebben geschapen, of hebben zij een aandeel in (de schepping) van de hemelen of hebben Wij hun een boek gegeven zodat zij door een bewijs daaruit gesteund worden?" Nee! De onrechtplegers beloven elkaar niets dan bedrog.'),
(3701, 35, 41, 'Voorwaar, Allah houdt de hemelen en de aarde vast, zodat zij niet vergaan; en zeker, wanneer zij (dreigen te) vergaan dan is er buiten Hem niemand die hen kan vasthouden. Voorwaar, Hij is Zachtmoedig, Vergevensgezind.'),
(3702, 35, 42, 'En zij zweren bij Allah de duurste eden: dat wanneer er een waarschuwer tot hen komt, zij zeker rechter geleid zullen zijn dan welk volk dan ook. Maar wanneer er dan cm waarschuwer tot hen komt noemt er niets voor hen toe dan afkeer.'),
(3703, 35, 43, 'Als hoogmoedigen op de aarde, en met een slechte list. Maar de slechte list treft niemand dan de beramers ervan. Zij wachten op nicts anders dan de manier waarop de woegeren werden bestraft. Jij zult in de handelwijze van Allah nooit een verandering aantreffen en jij zult in de handelwijze van Allah nooit een afwijking aantreffen.'),
(3704, 35, 44, 'En reizen zij niet op aarde en zien zij niet hoe het einde was van degenen vóór hen, terwijl zij machtiger waren dan zij? En er is niets in de hemelen en op de aarde dat Allah ontgaat. Voorwaar, Hij is Alwijs, Machtig.'),
(3705, 35, 45, 'En als Allah de mensen zou grijpen wegens wat zij bedreven hebben, dan zott Hij geen levend wezen op het aardoppervlak achterlaten. Maar Hij geeft hun uitstel tot een vastgeswide tijd. En wanneer hun tijd is gekomen (worden zij bestraft): voorwaar, Allah is Alziend over Zijn dienaren.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'Ya Sîn.'),
(3707, 36, 2, 'Bij de wijze Koran.'),
(3708, 36, 3, 'Voorwaar, jij (Mohammed) behoort zeker tot de gezanten.'),
(3709, 36, 4, 'Op het rechte Pad.'),
(3710, 36, 5, '(Deze Koran) als een neerzending van de Almachtige, de Meest Barmhartige.'),
(3711, 36, 6, 'Opdat jij een volk zal waarschuwen waarvan hun voorvaderen niet gewaarschuwd zijn, zodat zij achtelozen waren.'),
(3712, 36, 7, 'Voorzeker, het woord (van bestraffing) is tegen de meesten van hen bewaarheid omdat zij niet geloven.'),
(3713, 36, 8, 'Voorwaar, Wij hebben om hun nekken ketenen gelegd en die reiken tot de kinnen, zodat hun hoofden opgeheven blijven.'),
(3714, 36, 9, 'En Wij hebben vóór hen een hindernis geplaatst en achter hen een hindernis en Wij hebben hun ogen bedekt, zodat zij niet kunnen zien.'),
(3715, 36, 10, 'En het maakt geen verschil of jij hen waarschuwt of niet waarschuwt, zij geloven niet.'),
(3716, 36, 11, 'Voorwaar, jij kunt slechts waarschuwen wie de Vermaning volgt en de Erbarmer vreest, hoewel hij Hem niet kan waarnemen. Verkondig hem daarom de verheugende tijding van vergeving en een edele beloning.'),
(3717, 36, 12, 'Voorwaar, Wij zijn het Die de doden tot leven brengen en Wij schrijven op wat zij gedaan hebben en (ook) hun sporen. En alle zaken hebben Wij opgesomd in een duidelijk boek.'),
(3718, 36, 13, 'En geef hen een voorbeeld: de bewoners van de stad toen de gezanten tot haar kwamen.'),
(3719, 36, 14, 'Toen Wij er twee tot hen zonden, waarop zij hen loochenden, toen versterkten Wij (hen) met een derde. Zij zeiden toen: "Voorwaar, wij zijn gezanten voor jullie."'),
(3720, 36, 15, 'Zij zeiden: "Jullie zijn slechts mensen zoals wij en de Erbarmer heeft niets neergezonden, jullie doen niets anders dan liegen."'),
(3721, 36, 16, 'Zij zeiden: "Voorwaar, onze Heer weet dat wij zeker gezanten voor jullie zijn.'),
(3722, 36, 17, 'En onze taak is slechts het overbrengen van de duidelijke verkondiging.'),
(3723, 36, 18, 'Zij zeiden: "Voorwaar, wij zien een kwaad lot in jullie, als jullie dan niet ophouden, dan zullen wij jullie zeker stenigen en dan zal jullie zeker een pijnlijke bestraffing van ons treffen."'),
(3724, 36, 19, 'Zij zeiden: "Jullie kwade lot ligt bij jullie zelf, is het omdat jullie vermaand worden? Nee, jullie zijn een buitensporig volk."'),
(3725, 36, 20, 'En uit het verste gedeelte van de stad kwam een man aangesneld, die zei: "O mijn volk, volgt de gezanten.'),
(3726, 36, 21, 'Volgt hen die geen beloning van jullie vragen en rechtgeleiden zijn.'),
(3727, 36, 22, 'En waarom zou ik niet Hem aanbidden Die mij heeft geschapen? En tot Hem worden jullie teruggekeerd.'),
(3728, 36, 23, 'Hoe zou ik dan naast Hem goden kunnen nemen? Als de Barmhartige voor mij tegenspoed zou wensen, dan zou hun voorspraak mij niets baten en mij niet redden.'),
(3729, 36, 24, 'Voorwaar, dan zou ik zeker in duidelijke dwaling verkeren.'),
(3730, 36, 25, 'Voorwaar, ik geloof in jullie Heer, luistert daarom."'),
(3731, 36, 26, 'Er werd gezegd: "Treed het Paradijs binnen." Hij zei: "O wee, wist mijn volk maar.'),
(3732, 36, 27, 'Wat mijn Heer mij heeft vergeven en mij tot één van de geëerden heeft gemaakt!"'),
(3733, 36, 28, 'En Wij hebben na hem geen leger (van engelen) uit de hemel tot zijn volk gezonden, en Wij zonden hen niet.'),
(3734, 36, 29, 'Het was slechts één bliksemslag en toen waren zij dood.'),
(3735, 36, 30, 'Wat jammer voor de dienaren, er komt geen Boodschapper tot hen, of zij drijven de spot met hem.'),
(3736, 36, 31, 'Weten zij dan niet hoeveel generaties vóór hen Wij vernietigd hebben? Zij zullen niet tot hun (wereldse levens) terugkeren.'),
(3737, 36, 32, 'En zij zullen allen bijelkaar bij Ons voorgeleiden zijn.'),
(3738, 36, 33, 'En een teken voor hen is de dode aarde die Wij tot leven doen komen en waaruit Wij graan voortbrengen waarvan zij eten.'),
(3739, 36, 34, 'En Wij hebben daarin tuinen met dadelpalmen en druivenstruiken gemaakt en Wij hebben daarin bronnen doen ontspringen.'),
(3740, 36, 35, 'Opdat zij van haar vruchten eten en van wat hun handen hebben verricht. Zijn zij dan niet dankbaar?'),
(3741, 36, 36, 'Heilig is Degene Die al de soorten heeft geschapen die de aarde voortbrengt en uit henzelf en van wat zij niet weten.'),
(3742, 36, 37, 'En een Teken voor hen is de nacht waarvan Wij de dag onttrekken, dan bevinden zij zich in de duisternis.'),
(3743, 36, 38, 'En de zon loopt in haar vaste baan. Dat is de verordening van de Almachtige, de Alwijze.'),
(3744, 36, 39, 'En Wij hebben voor de maan standen bepaald, zodat zij terugkeert als een oud sikkeltje van een dadeltros.'),
(3745, 36, 40, 'Het is niet mogelijk dat de zon de maan bereikt en de nacht kan de dag niet inhalen. Allen bewegen in een kringloop.'),
(3746, 36, 41, 'En het is een Teken voor hen dat Wij hun nakomelingen dragen in een volgeladen schip.'),
(3747, 36, 42, 'En Wij hebben voor hen iets dat daarop lijkt geschapen, waarop zij rijden.'),
(3748, 36, 43, 'En als Wij willen, dan doen Wij hen verdrinken en dan zal er geen helper voor hen zijn, en zij zullen niet worden gered.'),
(3749, 36, 44, 'Behalve als een genade van Ons, en als een gunst voor een vastgestelde tijd.'),
(3750, 36, 45, 'En wanneer tot hen wordt gezegd: "Vreest wat vóór jullie en wat achter jullie is, hopelijk zullen jullie begenadigd worden, (toen keerden zij zich af.)"'),
(3751, 36, 46, 'En er komt geen Teken tot hen, van de Tekenen van hun Heer, of zij wenden zich er van af.'),
(3752, 36, 47, 'En wanneer tot hen wordt gezegd: "Geeft uit van waar Allah jullie mee voorzien heeft," dan zeggen degenen die ongelovig zijn tot degenen die gelovig zijn: "Zouden wij degenen voeden die Hij had kunnen voeden, als Allah het gewild had? Jullie verkeren slechts in duidelijke dwaling."'),
(3753, 36, 48, 'En zij zeggen: "Wanneer zal deze belofte vervuld worden, als jullie waarachtigen zijn?"'),
(3754, 36, 49, 'Zij wachten op niets anders dan één enkele bliksemslag die hen grijpt terwijl zij redetwisten.'),
(3755, 36, 50, 'Dan kunnen zij (elkaar) niet raadplegen en zij kunnen niet tot hun familie terugkeren.'),
(3756, 36, 51, 'En er wordt op de bazuin geblazen, daarop snellen zij uit de graven naar hun Heer.'),
(3757, 36, 52, 'Zij zeggen: "Wee ons! Wie heeft ons van onze rustplaatsen doen opstaan? Dat is wat de Barmhartige heeft aangezegd, en de Gezondenen hebben de waarheid gesproken."'),
(3758, 36, 53, 'Het zal slechts één enkele bliksemslag zijn en dan zullen zij allen bij Ons voorgeleid worden.'),
(3759, 36, 54, 'Dan zal op deze Dag niemand onrecht aangedaan worden en jullie zullen slechts worden beloond voor wat jullie plachten te doen.'),
(3760, 36, 55, 'Voorwaar, de bewoners van het Paradijs zullen op deze Dag bezig zijn te genieten.'),
(3761, 36, 56, 'Zij en hun echtgenotes zullen zich in schaduwen bevinden, op rustbanken leunend.'),
(3762, 36, 57, 'Voor hen zijn er vruchten en voor hen is er wat zij verlangen.'),
(3763, 36, 58, '"Salâm!" Een Woord van een Meest Barmhartige Heer.'),
(3764, 36, 59, 'Gaat op deze Dag (van de gelovigen) weg, O jullie misdadigers!'),
(3765, 36, 60, 'Heb ik jullie, O kinderen van Adam, niet opgedragen om de Satan niet te dienen? Voorwaar, hij is voor jullie een duidelijke vijand.'),
(3766, 36, 61, 'En aanbidt Mij: dat is het rechte Pad.'),
(3767, 36, 62, 'En voorzeker, hij heeft vele schepselen onder jullie doen dwalen. Gebruikten jullie je verstand dan niet?'),
(3768, 36, 63, 'Dit is de Hel waarvoor jullie gewaarschuwd plachen te worden.'),
(3769, 36, 64, 'Gaat haar op deze Dag binnen wegens wat jullie plachten niet te geloven.'),
(3770, 36, 65, 'Op die Dag plaatsen Wij een zegel op hun monden, en hun handen zullen tot Ons spreken en hun voeten zullen getuigen over wat zij plachten te verrichten.'),
(3771, 36, 66, 'En als Wij gewild hadden, dan hadden Wij zeker het gezichtvermogen uitgewist van hun ogen, waarna zij een wedloop naar het Pad zouden houden, maar hoe zouden zij (dat) kunnen zien?'),
(3772, 36, 67, 'En als Wij gewild hadden, dan hadden Wij hun gestalten veranderd op hun plaatsen (van zondigheid), waarna zij niet weg zouden kunnen gaan en niet terug zouden gaan.'),
(3773, 36, 68, 'En voor wie Wij de leeftijd verlengen doen Wij zijn lichaam verzwakken. Denken zij dan niet na?'),
(3774, 36, 69, 'En Wij hebben hem (Mohammed) het dichten niet onderwezen en het past hem niet. Het is niets dan een Vermaning en een duidelijke Koran.'),
(3775, 36, 70, 'Opdat hij de levenden zal waarschuwen en opdat het Woord (van bestraffing) voor de ongelovigen bewaarheid wordt.'),
(3776, 36, 71, 'Zien zij dan niet dat onder wat Onze Handen voor hen geschapen hebben het vee is, zodat zij daarvan bezitter zijn?'),
(3777, 36, 72, 'En Wij hebben het (vee) voor hen onderworpen, sommige berijden zij ervan en sommige eten zij.'),
(3778, 36, 73, 'En voor hen is er nut in en een (bron van) driken. Zijn zij dan niet dankbaar?'),
(3779, 36, 74, 'En zij nemen naast Allah goden in de hoop dat zij geholpen zullen worden.'),
(3780, 36, 75, 'Zij zijn niet in staat om hen te helpen, en zij (de veelgodenaanbidders) zijn voor hen een leger dat wordt voorgeleid.'),
(3781, 36, 76, 'Laten hun woorden jou daarom niet treurig maken: Voorwaar, Wij weten wat zij verbergen en wat zij openlijk doen.'),
(3782, 36, 77, 'Ziet de mens niet dat Wij hem uit een druppel hebben geschapen? Toch is hij duidelijk een redetwister.'),
(3783, 36, 78, 'En hij geeft Ons een voorbeeld, terwijl hij vergeet hoe hij zelf geschapen is. Hij zei: "Wie doet de beenderen tot leven komen, terwijl ze gruis zijn?"'),
(3784, 36, 79, 'Zeg: "Hij Die ze de eerste keer heeft doen ontstaan, Die zal ze doen leven. En Hij is de Kenner van de gehele schepping.'),
(3785, 36, 80, '(Hij is) Degene Die voor jullie van de groene boom vuur heeft gemaakt. Waarna jullie er een vuur mee ontsteken."'),
(3786, 36, 81, 'Is Degene Die de hemelen en de aarde heeft geschapen niet bij machte om het gelijke ervan te scheppen? Zeker wel! En Hij is de Schepper, de Alwetende.'),
(3787, 36, 82, 'Voorwaar, wanneer Hij iets wil (scheppen), dan zegt hij er slechts tegen: "Wees," en het is.'),
(3788, 36, 83, 'Heilig is Degene in Wiens Hand de heerschappij over alle zaken is en tot Hem worden jullie teruggekeerd.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Bij hen die in rijen staan (de Engelen).'),
(3790, 37, 2, 'Die de wolken voortdrijven.'),
(3791, 37, 3, 'Die de Vermaning (de Koran) voordragen.'),
(3792, 37, 4, 'Voorwaar, jullie God is zeker Eén.'),
(3793, 37, 5, 'De Heer van de hemelen en de aarde en wat er tussen is en de Heer van de plaatsen van zonsopgang.'),
(3794, 37, 6, 'Voorwaar, Wij hebben de nabije hemel gesierd met een veniering: de sterren.'),
(3795, 37, 7, 'En als bescherming tegen alle opstandige Satans.'),
(3796, 37, 8, 'Zij kunnen niet luisteren bij de hoogste groep (de Engelen). Er wordt naar hen geworpen vanuit alle kanten.'),
(3797, 37, 9, 'Ter verjaging. En voor hen is er een ononderbroken bestraffing.'),
(3798, 37, 10, 'Behalve wie afluisterend luistert: een gloeiende vlam achtervolgt hem.'),
(3799, 37, 11, 'Vraag hen: "Zijn zij (de mensen) moeilijker om te scheppen of dat (hemel en aarde en wat er tussen is) wat wij hebben geschapen?" Voorwaar, Wij hebben hen van kleverige klei geschapen.'),
(3800, 37, 12, 'Jij verbaastje zelfs omdat zij (de door jou gebracht boodschap) bespotten.'),
(3801, 37, 13, 'En wanneer zij vermaand worden, dan nemen zij de Vemaning niet tot zich.'),
(3802, 37, 14, 'En wanneer zij een Teken (een goddelijk wonder) zien, dan bespotten zij.'),
(3803, 37, 15, 'En zij zeggen: "Dit is niets dan duidelijke tovenarij."'),
(3804, 37, 16, 'Als wij al dood zijn en tot aarde en beenderen zijn geworden; zullen wij dan zeker opgewekt worden?'),
(3805, 37, 17, 'En ook onze voorvaderen?"'),
(3806, 37, 18, 'Zeg: "Ja, en jullie zullen vemederd zijn."'),
(3807, 37, 19, 'Het is dan slechts één bliksemslag, waarna zij om zich heen kijken.'),
(3808, 37, 20, 'En zij zullen zeggen: "Wee ons, dit is de Dag des Oordeels."'),
(3809, 37, 21, 'Dit is de Dag van de beslissing, die jullie plachten te loochenen.'),
(3810, 37, 22, '(Tot de Engelen wordt gezegd:) "Verzamelt degenen die onrecht pleegden en hun gelijken en wat zij plachten te aanbidden.'),
(3811, 37, 23, 'Naast Allah. Leidt ben dan naar de weg naar Djahîm (de Hel).'),
(3812, 37, 24, 'En houdt hen vast: voorwaar, zij zullen ondervraagd worden."'),
(3813, 37, 25, '(Er zal aan hen gevraagd worden:) "Wat is er met jullie, waarom helpen jullie elkaar niet?"'),
(3814, 37, 26, 'Op die Dag zullen zij zich zelfs overgeven.'),
(3815, 37, 27, 'En zij zullen zich tot elkaar wenden en elkaar ondervragen.'),
(3816, 37, 28, 'Zij (de volgelingen) zullen zeggen: "Voorwaar, jullie zijn van de rechterkant tot ons gekomen."'),
(3817, 37, 29, 'Zij (de leiders) zullen antwoorden: "Jullie was waren zelfs geen gelovigen.'),
(3818, 37, 30, 'Ea wij hadden geen macht over jullie. Jullie waren zelfs een overtredend volk.'),
(3819, 37, 31, 'Het Woord (van bestraffing) tot ons van Onze Heer zal daarom bewaarheid worden. Voorwaar, wij zullen het zeker proeven.'),
(3820, 37, 32, 'Wij misleidden jullie toen: voorwaar, wij waren misleiders."'),
(3821, 37, 33, 'Voorwaar, zij zullen dan op die Dag in de bestraffing bijelkaar zijn.'),
(3822, 37, 34, 'Voorwaar, zo behandelen Wij de misdadigers.'),
(3823, 37, 35, 'Voorwaar, toen er tot hen gezegd werd: "Er is geen god dan Allah," toen waren zij hoogmoedig.'),
(3824, 37, 36, 'En zij zeggen: "Zullen wij dan onze goden achterlaten vanwege een bezeten dichter?"'),
(3825, 37, 37, 'Nee! Hij (Moehammad) is met de Waarheid gekomen en hij heeft de Gezondenen (de Profeten vóór hem) bevestigd.'),
(3826, 37, 38, 'Voorwaar, jullie proeven zeker de pijnlijke bestraffing.'),
(3827, 37, 39, 'En jullie worden slechts vergolden voor wat jullie hebben gedaan.'),
(3828, 37, 40, 'Behalve de dienaren van Allah die zuiver in hun aanbidding zijn.'),
(3829, 37, 41, 'Zij zijn degenen voor wie er een bekende voorziening is (het Paradijs).'),
(3830, 37, 42, 'Vruchten. En zij zijn de geëerden.'),
(3831, 37, 43, 'In Tuinen van Gelukzaligheid (het Paradijs).'),
(3832, 37, 44, 'Op rustbanken tegenover elkaar.'),
(3833, 37, 45, 'Onder hen wordt rondgegaan met een beker met Ma\'in (van de bron van het Paradij).'),
(3834, 37, 46, 'Helder wit, smakelijk voor de drinkers.'),
(3835, 37, 47, 'Deze (drank) kent geen beneveling en zij worden er niet dronken van.'),
(3836, 37, 48, 'En bij hen zijn schonen met ingetogen blikken, met mooie ogen.'),
(3837, 37, 49, 'Als waren zij welbewaarde eieren.'),
(3838, 37, 50, 'Zij wenden zich dan tot elkaar en stellen elkaar vragen.'),
(3839, 37, 51, 'Een spreker onder hen zal zeggen: "Voorwaar, ik had een vriend.'),
(3840, 37, 52, 'Hij zei (vroeger tegen mij): "Voorwaar, behoor jij tot hen die (de Opstanding) bevestigen?'),
(3841, 37, 53, 'Als wij dan al dood zijn, en tot aarde en beenderen zijn geworden, zullen wij dan zeker worden beoordeld?"\''),
(3842, 37, 54, 'Hij zei (tegen de anderen in hct Paradijs): "Hebben jullie (dit) gezien?"'),
(3843, 37, 55, 'Toen keek hij en zag hem in het midden van Djahîm (de Hel).'),
(3844, 37, 56, 'Hij zei: "Bij Allah, jij hebt mij bijna in het ongeluk gestort.'),
(3845, 37, 57, 'En als er niet de genade van mijn Heer geweest was, dan zou ik zeker tot de voorgeleiden (voor de Hel) behoren.'),
(3846, 37, 58, 'Zullen wij dan niet sterven?'),
(3847, 37, 59, 'Naut ons eerste sterven? En zullen wij niet worden bestraft?"'),
(3848, 37, 60, 'Voorwaar, dat is zeker de geweldige overwinning.'),
(3849, 37, 61, 'Voor zoiets, laten de werkenden daarvoor werken.'),
(3850, 37, 62, 'Is die ontvangst beter, of de Zaqqôem-boom (in de Hel)?'),
(3851, 37, 63, 'Voorwaar, Wij hebben hem tot een beproeving voor de onrechtvaardigen gemaakt.'),
(3852, 37, 64, 'Voorwaar, het is een boom die voortkomt uit de bodem van Djahîm (de Hel).'),
(3853, 37, 65, 'De kolven ervan zijn als satanskoppen.'),
(3854, 37, 66, 'Voorwaar, dan zullen zij er van eten zodat zij er de buiken mee vullen.'),
(3855, 37, 67, 'Daarna is er voor hen een drank, gemengd met kokend water.'),
(3856, 37, 68, 'Tenslotte is hun terugkeer zeker naar Djahîm.'),
(3857, 37, 69, 'Voorwaar, zij troffen hun vaderen in dwaling verkerend aan.'),
(3858, 37, 70, 'Toen volgden zij hen haastig in hun voetsporen.'),
(3859, 37, 71, 'En voorzeker dwaalden vóór hen de meesten van de vroegeren.'),
(3860, 37, 72, 'En voorzeker hebben Wij uit hun midden waarschuwers gezonden,'),
(3861, 37, 73, 'Zie dan (O Moehammad) hoe het einde was van de gewaamshuwden.'),
(3862, 37, 74, 'Behalve (het einde van) de dienaren van Allah die, zuiver in hun aanbidding zijn.'),
(3863, 37, 75, 'En voorzeker, Nôeh riep Ons aan, en Wij zijn zeker de beste verhorenden.'),
(3864, 37, 76, 'En Wij redden hem en zijn volgelingen van de geweldige ramp.'),
(3865, 37, 77, 'En Wij maakte zijn nakomelingen tot voortlevenden.'),
(3866, 37, 78, 'En Wij maakten voor hem (zijn goede naam) blijvend onder de lateren.'),
(3867, 37, 79, 'Vrede zij met Nôeh in de werelden.'),
(3868, 37, 80, 'Voorwaar, zo belonen Wij de weldoeners.'),
(3869, 37, 81, 'Voorwaar, hij behoort tot Onze gelovip dienaren.'),
(3870, 37, 82, 'Wij verdonken toen de anderen.'),
(3871, 37, 83, 'En voorwaar, tot zijn groep behoorde zeker Ibrâhîm.'),
(3872, 37, 84, '(Gedenk) toen hij tot zijn Heer kwam met een zuiver hart.'),
(3873, 37, 85, 'Toen hij tot zijn vader en zijn volk zei: "Wat aanbidden jullie?'),
(3874, 37, 86, 'Wensen jullie als een verzinsel goden naast Allah?'),
(3875, 37, 87, 'Wat stellen jullie je voor over de Heer der Werelden?"'),
(3876, 37, 88, 'Hij keek toen een ogenblik naar de sterren.'),
(3877, 37, 89, 'Hij zei toen: "Voorwaar, ik ben ziek."'),
(3878, 37, 90, 'Toen wendden zij zich af, hem de rug toekerend.'),
(3879, 37, 91, 'Toen ging hij heimelijk naar hun goden en zei: "Eten jullie (dit voedsel) niet?'),
(3880, 37, 92, 'Wat is er met jullie dat jullie niet spreken?"'),
(3881, 37, 93, 'Toen liep hij op hen toe en sloeg (hen) met de rechterhand.'),
(3882, 37, 94, 'Daarop liepen zij (de veelgodenaanbidders) snel naar hem toe.'),
(3883, 37, 95, 'Hij zei: "Aanbidden jullie wat jullie hebben uitgehouwen?'),
(3884, 37, 96, 'Terwijl Allah jullie heeft geschapen en wat jullie maken."'),
(3885, 37, 97, 'Zij zeiden: "Bouwt voor hem een bouwwerk (brandstapel) en werpt hem in liet laaiende vuur."'),
(3886, 37, 98, 'Toen zij een list tegen hem wensten te beramen maakten Wij hen tot de allerlaagsten.'),
(3887, 37, 99, 'En hij zei (toen hun pogingen mislukt waren): "Ik wend mij tot mijn Heer, Hij zal mij leiden.'),
(3888, 37, 100, 'Mijn Heer, schenk mij (een zoon) van de rechtschapenen."'),
(3889, 37, 101, 'Toen verkondigden Wij hem de verheugende tijding van een zachtmoedige jongen (Ismâ\'îl).'),
(3890, 37, 102, 'Toen hij de leeftijd had bereikt waarop hij hem (Ibrâhîm) kon helpen, zei hij: "O mijn zoon, voorwaar, ik heb in een droom gezien dat ik jou zal offeren, zeg mij hoe jij daarover denkt," Hij zei: "O mijn vader, doe wat u is bevolen, U zult vinden dat ik, als Allah het wil, tot de geduldigen behoor."'),
(3891, 37, 103, 'Toen zij zich (aan Allah) hadden overgegeven en hij hem op zijn slaap had gelegd (om te offeren).'),
(3892, 37, 104, 'Toen riepen Wij tot hem: "O Ibrâhîm!'),
(3893, 37, 105, 'Waarlijk, jij hebt de droom in waarheid vervuld. Voorwaar, zo belonen Wij de weldoeners."'),
(3894, 37, 106, 'Voorwaar, dat is zeker de duidelijke beproeving.'),
(3895, 37, 107, 'En Wij gaven hem ter vervanging een groot offerdier.'),
(3896, 37, 108, 'En Wij maakten voor hem (zijn goede naam) blijvend onder de lateren.'),
(3897, 37, 109, 'Vrede zij met Ibrâhîm.'),
(3898, 37, 110, 'Zo belonen Wij de weldoeners.'),
(3899, 37, 111, 'Voorwaar, hij behoort tot Onze gelovige dienaren.'),
(3900, 37, 112, 'En Wij verkondigden hem de verheugende tijding over (de geboorte van) Ishâq, als een Profeet van de rechtschapenen.'),
(3901, 37, 113, 'En Wij zegenden hem en Ishâq. En onder kun nakomelingen zijn er die weldoener zijn en (ook) die duidelijk onrechtvaardig voor zichzelf zijn.'),
(3902, 37, 114, 'En voorzeker, Wij hebben Môesa en Hârôen begenadigd.'),
(3903, 37, 115, 'En Wij hebben hen beiden en hun volk gered van de geweldige ramp.'),
(3904, 37, 116, 'En Wij hielpen hen, waarop zij de overwinnaars werden.'),
(3905, 37, 117, 'En Wij gaven hun de verduidelijkende Schrift (de Taurât).'),
(3906, 37, 118, 'En Wij hebben Hen op het rechte Pad geleid.'),
(3907, 37, 119, 'En Wij maakten voor hen (hun goede naam) blijvend onder de lateren.'),
(3908, 37, 120, 'Vrede zij met Môcsa en Harôen.'),
(3909, 37, 121, 'Voorwaar, zo belonen Wij de weldoeners.'),
(3910, 37, 122, 'Voorwaar, zij behoren tot Onze gelovige dienaren.'),
(3911, 37, 123, 'En voorwaar. Ilyâs behoort zeker tot de Gezondenen.'),
(3912, 37, 124, '(Gedenkt) toen hij tot zijn volk zei: "Vrezen jullie (Allah) niet?'),
(3913, 37, 125, 'Aanbidden jullie Ba\'l (een afgod) en verlaten jullie de Beste der Scheppers?'),
(3914, 37, 126, 'Allah is jullie Heer en de Heer van jullie voorvaderen."'),
(3915, 37, 127, 'Toen loochenden zij hem, daarom worden zij zeker voorgeleiden (voor de bestraffing).'),
(3916, 37, 128, 'Behalve de dienaren van Allah die zuiver in hun aanbidding zijn.'),
(3917, 37, 129, 'En Wij maakten voor hem (zijn goede naam) blijvend onder de lateren.'),
(3918, 37, 130, 'Vrede zij met Ilyâs.'),
(3919, 37, 131, 'Voorwaar, zo belonen wij de weldoeners.'),
(3920, 37, 132, 'Voorwaar, hij behoort tot Onze gelovige dienaren.'),
(3921, 37, 133, 'En voorwaar, Lôeth behoort zeker tot de gezondenen.'),
(3922, 37, 134, '(Gedenk) toen Wij hem en zijn volgelingen allen hebben gered.'),
(3923, 37, 135, 'Behalve een vrouw (zijn echtgenote) die tot de achterblijvers behoorde.'),
(3924, 37, 136, 'Vervolgens vernietigden Wij de overigen.'),
(3925, 37, 137, 'En voorwaar, jullie gaan in de ochtend aan hen (de ruïnes van hun steden) voorbij.'),
(3926, 37, 138, 'En ook in de nacht, denken jullie dan niet na?'),
(3927, 37, 139, 'En voorwaar, Yôenoes behoort zeker tot de gezondenen.'),
(3928, 37, 140, '(Gedenkt) toen hij wegliep naar het volgeladen schip.'),
(3929, 37, 141, 'Toen lootte hij (om een plaats erop) en bij behoorde daarop tot de verliezers.'),
(3930, 37, 142, 'Toen slokte de vis hem op en hij verweet zichzelf.'),
(3931, 37, 143, 'En als hij niet tot degenen die de Glorie van Allah prezen behoord had.'),
(3932, 37, 144, 'Zou hij zeker in zijn buik zijn gebleven, tot de Dag waarop zij worden opgewekt.'),
(3933, 37, 145, 'Toen wierpen Wij hem eruit, op een kale vlakte, en hij was ziek.'),
(3934, 37, 146, 'En Wij deden over hem een boom groeien met veel bladeren.'),
(3935, 37, 147, 'En Wij zonden hem naar een honderdduizendtal (volgelingen) of meer.'),
(3936, 37, 148, 'Daarop geloofden zij en Wij schonken hun genietingen, voor een bepaalde tijd.'),
(3937, 37, 149, 'Vraag hen (de ongelovigen), of voor jouw Heer de dochters zijn en voor hen de zonen.'),
(3938, 37, 150, 'Hebben Wij de Engelen als vrouwen geschapen en waren zij getuigen?'),
(3939, 37, 151, 'Weet dat zij wegens hun verzonnen leugens zeker zullen zeggen:'),
(3940, 37, 152, '"Allah heeft kinderen verwekt." Voorwaar, zij zijn zeker leugenaars.'),
(3941, 37, 153, 'Heeft Hij dochters verkozen boven zonen?'),
(3942, 37, 154, 'Wat is er met jullie? Hoe beoordelen jullie?'),
(3943, 37, 155, 'Laten jullie je dan in iet vermanen?'),
(3944, 37, 156, 'Of beschikken jullie over een duidelijk bewijs?'),
(3945, 37, 157, 'Brengt dan jullie boek, als jullie waarachtigen zijn!'),
(3946, 37, 158, 'En zij verzinnen verwantschap tussen Hem en de Djinn\'s. En voorzeker, de Djinn\'s weten dat zij de voorgeleiden zullen zijn.'),
(3947, 37, 159, 'Heilig is Allah boven wat zij toeschrijven.'),
(3948, 37, 160, 'Behalve de dienaren van Allah die Hem zuiver aanbidden.'),
(3949, 37, 161, 'Voorwaar jullie en wat jullie aanbidden.'),
(3950, 37, 162, 'Jullie kunnen niemand tegen (het plan van) Hem te doen dwalen.'),
(3951, 37, 163, 'Behalve degene die Djahîm (de Hel) binnengaat.'),
(3952, 37, 164, '(De Engelen zeggen:) "En er is niemand van ons, of er is voor hem een bekende plaats.'),
(3953, 37, 165, 'En voorwaar, wij zijn degenen die in rijen staan.'),
(3954, 37, 166, 'En voorwaar, wij zijn zeker degenen die de Glorie van Allah prijzen."'),
(3955, 37, 167, 'En zij (de ongelovigen) zullen zeker zeggen:'),
(3956, 37, 168, '"Als wij over een Vermaning van de vruegeren hadden beschikt,'),
(3957, 37, 169, 'Dan zouden wij zeker tot de dienaren van Allah die Hem zuiver aanbidden hebben behoord."'),
(3958, 37, 170, 'Maar zij verwierpen hem (de Koran), daarom zullen zij het weten.'),
(3959, 37, 171, 'En voorzeker, Ons Woord is voorafgegaan aan Onze gezonden dienaren.'),
(3960, 37, 172, 'Voorwaar, zij zijn het die zeker geholpen zullen worden.'),
(3961, 37, 173, 'Voorwaar, zij zijn Onze legers die zeker de overwinnaars zullen zijn.'),
(3962, 37, 174, 'Wend je (O Moehammad) dan voor een bepaalde tijd van hen (de gelovigen) af.'),
(3963, 37, 175, 'En kijk naar hen, zij zullen spoedig (de gevolgen) zien.'),
(3964, 37, 176, 'Vragen zij dan Onze bestraffing te bespoedigen?'),
(3965, 37, 177, 'Als dan (de bestraffing) neerdaalt op hun erven, dat is dan de slechtste ochtend voor de gewaarschuwden.'),
(3966, 37, 178, 'En wend je van hen af voor een bepaalde tijd.'),
(3967, 37, 179, 'En kijk, spoedig zullen zij (de bestraffing) zien.'),
(3968, 37, 180, 'Heilig is jouw Heer, de Heer van de Almacht, boven wat zij toeschrijven.'),
(3969, 37, 181, 'En vrede zij met de gezondenen.'),
(3970, 37, 182, 'En alle lof zij Allah, de Heer der Werelden.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'Shâd. Bij de Koran, bezitter van eer.'),
(3972, 38, 2, 'Maar degenen die ongelovig zijn., verkeren in eigendunk en opstandigheid.'),
(3973, 38, 3, 'Hoeveelgeneraties vóór hen hebben Wij niet vernietigd, die toen (tot Ons) riepen. Die tijd was niet de tijd om te vluchten.'),
(3974, 38, 4, 'En zij verbaasden zich dat er om waarschuwer uit hun midden tot hen was gekomen. En de ongelovigen zeiden: "Dit is een liegende tovenaar!'),
(3975, 38, 5, 'Heeft hij de goden tot één God gernaakt? Voorwaar, dit is zeker een verbazingwekkend iets."'),
(3976, 38, 6, 'En de vooranstaanden onder hen gingen weg (zeggend:) "Ga door en wees geduldig met (de aanbidding van) jullie goden. Voorwaar, dat (van Moehammad) is zeker iets dat (tegen jullie) bedoeld is.'),
(3977, 38, 7, 'En wij hebben hierover niets gehoord in de laatste godsdienst. Dit is niets dan een verzinsel."'),
(3978, 38, 8, 'Is aan hem uit Ons midden de Vermaning neergezonden? Nee! Zij verkeren in twijfel over Mijn Vermaning. Nee! Zij hebben de bestraffing nog niet geproefd.'),
(3979, 38, 9, 'Of zijn bij ben de schatten van de Barmhartigheid van jouw Heer, de Almachtig, de Schenker?'),
(3980, 38, 10, 'Of behoort aan hèn het koninkrijk van de hemelen en de aarde en wat daartussen is? Laten zij dan langs de ladders omhoog klimmen!'),
(3981, 38, 11, 'Zij zijn daar niets anden dan het verslagen leger van de bondgenoten.'),
(3982, 38, 12, 'Vóór hen loochende het volk van Nôeh, en (het volk van) de \'Âd en Fir\'aun, de bezitter van de pinnen (macht).'),
(3983, 38, 13, 'En de Tsamôed, het volk van Lôeth en de bewoners van Aikah. Zij zijn de bondgenoten.'),
(3984, 38, 14, 'En er was niemand onder hen, of hij loochende de Boodschappers, zodat Mijn bestraffing bewaarheid word.'),
(3985, 38, 15, 'En zij wachtten slechts op één enkele bliksemslag, die geen onderbreking kent.'),
(3986, 38, 16, 'En zij zeggen: "Onze Heer, bespoedig voor ons ons deel (van de bestraffing) vóór de Dag des Oordeels."'),
(3987, 38, 17, 'Weest geduldig mt wat zij zij zeggen, en gedenk Onze dienaar Dâwôed, de bezitter van kracht. Voorwaar, hij is de meest berouwtonende.'),
(3988, 38, 18, 'En Wij maakten de bergen dienstbaar, die met hem de Glorie van Allah prezen in de avond en in de ochtend.'),
(3989, 38, 19, 'En (ook) de verzamelde vogels, allen wendden zich voortdurend tot Hem.'),
(3990, 38, 20, 'En Wij versterkten zi n koninkrijk en Wij gaven hem de wijsheid en de beslissende uitspraken.'),
(3991, 38, 21, 'Heeft jou het bericht over de onenigheid bereikt? Toen zij over de muur de gebedsruimte binnendrongen?'),
(3992, 38, 22, 'Toen zij bij Dâwôed binnenkwamen, schrok Hij van ten, zij zeiden: "Wees niet bang, wij zijn twee mannen die het met elkaar oneens zijn, een van ons heeft de ander onrechtvaardig behandeld. Oordeel daarom rechtvaardig tussen ons en wijk niet af van de Waarheid en leid ons naar het rechte Pad.'),
(3993, 38, 23, 'Voorwaar, mijn broeder heeft negen en negentig ooien en ik heb één ooi. Toen zei hij: \'Sta haar aan mij af.\' En hij versloeg mij in het redetwisten."'),
(3994, 38, 24, 'Bij (Dâwôed) zei: "Voorzeker, hij heeft jou onrechtvaardig behandeld met de eis om jouw ooi am zijn ooien toe te voegen. En voorwaar, vele genoten behandelen elkaar onrechtvaardig, behalve degenen die geloven en die goede werken verrichten, en zij zijn slechts weinigen. En Dâwôed vermoedde dat Wij hem op de proef stelden, waarop hij zijn Heer om vergeving smeekte. Hij boog zich neer en hij toonde berouw.'),
(3995, 38, 25, 'Toen vergaven Wij hem dat. En voorwaar, voor hem is er bij Ons zeker (Onze) nabijheid en de beste plaats van terugkeer.'),
(3996, 38, 26, 'O Dâwôed, Wij hebben jou tot gevolmachtigde op aarde aangesteld, oordeel daarom met de Waarheid tussen de mensen en volg niet de begeerte, want die zal jou doen afdwalen van de Weg van Allah. Voorwaar, degenen die van de Weg van Allah afdwalen: voor hen is er een harde bestraffing omdat zij de Dag des Oordeels vergaten.'),
(3997, 38, 27, 'En Wij hebben de hemel en de aarde en wat dartussen is niet voor niets geschapen. Dat is een vermoeden van degenen die ongelovig zijn. Wee daarom degenen die niet in de Hel geloven.'),
(3998, 38, 28, 'Zullen Wij degenen die geloven en goede werken verrichten behandelen als de verderfzaaiers op de aarde? Of zullen Wij de Moettaqôen be handelen als de verdorvenen?'),
(3999, 38, 29, '(Dit is) een gezegend Boek dat Wij aan jou hebben neergezonden opdat zij Zijn Verzen zullen overpeinzen en opdat de bezitters van verstand er lering uit trekken.'),
(4000, 38, 30, 'En Wij schonken Dâwôed Soelaimân, de beste dienaar. Voorwaar, hij is de meest berouwtonende.'),
(4001, 38, 31, '(Gedenkt) toen hem in de avond dc snelle raspaarden werden getoond.'),
(4002, 38, 32, 'Toen zei hij: "Voorwaar, ik koesterde de liefde voor het goede ten koste van het gedenken van mijn Heer, totdat zij onzichtbaar werden door de zonsondergang.'),
(4003, 38, 33, 'Brengt ze naar mij terug." Daarna begon bij (de paarden) over de benen en de halzen te stirijken.'),
(4004, 38, 34, 'En voorzeker, Wij hebben Soelaimân op de proef gesteld en Wij zetten (hem) op zijn zetel, als een lichaam, waarna hij berouw toonde.'),
(4005, 38, 35, 'Hij zei: "Mijn Heer, vergeef rnij, en schenk mij een koninkrijk dat niemand na mij ooit zal bezitten. Voorwaar, U bent de Schenker.'),
(4006, 38, 36, 'En Wij maakten de wind un hem dienstbaar, die volgzaan waaide op zijn bevel, waarheen bij wilde.'),
(4007, 38, 37, 'En (ook) de Satans, allen waren bouwers en duikers.'),
(4008, 38, 38, 'En andere (Satans) vastgebonden in ketenen.'),
(4009, 38, 39, 'Dat is een ge-schenk van Ons, geef (het) dan weg of houd (het) achter; zonder afrekening.'),
(4010, 38, 40, 'En voorwaar, voor hem is er bij Ons zeker (Onze) nabijheid en de beste plaats van terugkeer.'),
(4011, 38, 41, 'En gedenk Onze dienaar, Ayyôeb, toen hij tot zij Heer riep: "Voorwaw, de Satan heeft mij getroffen met tegenspoed en bestraffing."'),
(4012, 38, 42, '(Allah antwoordde:) "Stamp met jouw voet, dit is koel water om te baden en om van te drinken."'),
(4013, 38, 43, 'En Wij hebben hem zijn familie en nog eens zoveel met hen geschonken, als een genade van Ons en als een tering voor de bezitters van verstand.'),
(4014, 38, 44, '(Allah zei tegen Ayyôeb:) "En neem een bosje gras in je hand, en sla daarmee (jouw vrouw) en breek jouw eed niet." Voorwaar, Wij troffen hem aan als een geduldige, de beste dienaar. Voorwaar, hij is de meest berouwtonende.'),
(4015, 38, 45, 'En gedenk Onze dienaren Ibrâhîm en Ishâq en Ya\'qôeb allen waren beziners van grote kracht en inzicht.'),
(4016, 38, 46, 'Voorwaar, Wij zuiverden hen volledig ten behoeve van (hun) godachtenis van het Hiernamaals.'),
(4017, 38, 47, 'En voorwaar, zij behoorden bij Ons zeker tot de beste uitgekozenen.'),
(4018, 38, 48, 'En gedeak Ismâ\'îl (en Al Yasa\' en Dzôelkifl: zij behoorden allen tot de besten.'),
(4019, 38, 49, 'Dit is een herinnering, en voorwaar, voor de Moettaqôen is er zeker de beste plaats van terrugkeer.'),
(4020, 38, 50, 'De Tuinen van \'Adn (het Paradijs) waarvan de poorten voor hen geopend zijn.'),
(4021, 38, 51, 'Daarin leunen zij, zij vragen daarin om vele vruchten en om drinken.'),
(4022, 38, 52, 'En bij hen zijn mooie gezellinnen met ingetogen blikken, gelijk in leeftijd.'),
(4023, 38, 53, 'Dat is wat aan jullie beloofd is voor de Dag des Oordeels.'),
(4024, 38, 54, 'Voorwaar, dit is zeker een voorziening van Ons die geen einde kent.'),
(4025, 38, 55, 'Zo is het. En voorwaar, voor de overtreders is er zeker de slechtste plaats van terugkeer.'),
(4026, 38, 56, 'De Hel, daarin zullen zij binnentreden en dat is de slechtste rustplaats.'),
(4027, 38, 57, 'Zo is het. Laten zij hem (de bestraffing) proeven: kokend water en etter.'),
(4028, 38, 58, 'En andere, daarop lijkende soorten.'),
(4029, 38, 59, 'Dit is een groep die samen niet jullie binnenstroomt. Er is geen welkom voor hen. Voorwaar, zij treden de Hel binnen.'),
(4030, 38, 60, 'Zij (hun volgelingen zullen) zeggen: "Nee, juist voor jullie is er geen welkom, jullie zijn het die dit over ons hebben gebracht." Dat is dan de slechtste vestiging.'),
(4031, 38, 61, 'Zij zullen zeggen: "Onze Heer, voor wie dit over ons heeft gebracht: vermenigvuldig de bestraffing in de Hel voor hem."'),
(4032, 38, 62, 'En zij zullen zeggen: "Hou komt het dat wij niet de mensen (gelovigen) zien, die wij tot de slechtsten rekenden?'),
(4033, 38, 63, 'Is het omdat wij hen tot onderwerp van bespotting maakten, of slagen de ogen er niet in hen te zien?"'),
(4034, 38, 64, 'Voorwaar, dat zal zeker de waarheid zijn: het redetwisten van de bewoners van de Hel.'),
(4035, 38, 65, 'Zeg: "Ik ben slechts een waarschuwer, en er is geen god dan Allah, de Ene, de Overweldiger.'),
(4036, 38, 66, 'De Heer van de hemelen en de aarde en wat er tussen hen is, de Almachtige, de Vergevensgezinde."'),
(4037, 38, 67, 'Zeg: "Dit is een geweldig bericht.'),
(4038, 38, 68, 'Waarvan jullie je afwenden.'),
(4039, 38, 69, 'Het is voor mij niet mogelijk dat ik kennis zou hebben over de Engelen toen zij redetwistten.'),
(4040, 38, 70, 'Het is aan mij geopenbaard dat ik slechts een duidelijke waarschuwer ben."'),
(4041, 38, 71, '(Gedenk) Toen jouw Heer tot de Engelen zei: "\'Voorwaar, Ik schep een mens uit aarde.'),
(4042, 38, 72, 'En Ik vervolmaak hem, en blaas hem van de Geest in." Toen knielden zij zich voor hem neer.'),
(4043, 38, 73, 'Toen knielden alle Engelen tezamen neer.'),
(4044, 38, 74, 'Behalve Iblîs, hij was hoogmoedig en hij behoorde tot de ongelovigen.'),
(4045, 38, 75, 'Hij (Allah) zei: "O Iblîs, wat heeft jou ervan weerhouden om neer te knielen toen Ik (Adam) met Mijn beide Handen had geschapen? Was jij hoogmoedig, of behoorde jij tot de hooghartigen?"'),
(4046, 38, 76, 'Hij zei: "Ik ben beter dan hij. U heeft mij uit vuur geschapen, terwijl U hem uit aarde heeft geschapen.'),
(4047, 38, 77, 'Hij (Allah) zei: "Ga dan weg uit haar (het Paradijs): voorwaar, jij bent een verworpene.'),
(4048, 38, 78, 'En voorwaar, op jou rust Mijn vervloeking, tot de Dag des Oordeels."'),
(4049, 38, 79, 'Hij (Iblîs) zei: "Mijn Heer, geef mij uitstel tot de Dag waarop zij zullen worden opgewekt."'),
(4050, 38, 80, 'Hij (Allah) zei: "Voorwaar, jij behoort dan tot hen aan wie uitstel is gegeven.'),
(4051, 38, 81, 'Tot de Dag van de vastgestelde tijd."'),
(4052, 38, 82, 'Hij (Iblîs) zei: "Bij Uw eer dan, ik zal hen zeker allen misleiden.'),
(4053, 38, 83, 'Behalve Uw dienaren onder hen die zuiver zijn in hun aanbidding."'),
(4054, 38, 84, 'Hij (Allah): "De Waarheid, en Ik spreek de Waarheid.'),
(4055, 38, 85, 'Ik zal zeker De Hel vullen met jou en met degenen onder hen die jou volgden, tezamen."'),
(4056, 38, 86, 'Zeg (O Moehammad): "Ik vraag jullie er geen beloning voor en ik behoor niet tot hen die verzinnen.'),
(4057, 38, 87, 'Hij (de Koran) is niets dan een Vermaning voor de werelden.'),
(4058, 38, 88, 'En over een tijd zullen jullie zeker weten wat zijn boodschap was."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, '(De Koran) is de neerzonding van het Boek van Allah, de Almachtige, de Alwijze.'),
(4060, 39, 2, 'Voorwaar, Wij hebben het Boek met de Waarheid aan jou (O Moehammad) gezonden, aanbid daarom Allah, Hem zuiver aanbiddend.'),
(4061, 39, 3, 'Weet dat Allah de zuivere aanbidding toekomt. En degenen die naast Hem beschermers nemen (zeggen:) "Wij aanbidden ben slechts opdat zij ons zo dicht mogelijk tot Allah brengen." Voorwaar, Allah zal tussen hen rechtspreken over dat waarover zij van mening verschillen. Voorwaar, Allah leidt niet degene die een zeer ongelovige leugenaar is.'),
(4062, 39, 4, 'Als Allah een zoon zou wensen te nemen, dan zou Hij kiezen wat Hij wilde van wat Hij heeft geschapen. Heilig is Hij, Hij is Allah, de Ene, de Overweldiger.'),
(4063, 39, 5, 'Hij heeft de hemelen en de aarde in Waarheid geschapen. Hij doet de nacht de dag bedekken en Hij doet de dag de nacht bedekken. En Hij heeft de zou en de maan dienstbaar gemaakt. Allen bewegen tot een bepaalde tijd. Weet dat Hij de Almachtige, de Vergevensgezinde is.'),
(4064, 39, 6, 'Hij heeft jullie uit één ziet geschapen, daarna maakte Hij ervan zijn vrouw, en Hij heeft voor jullie van het vee acht, in paren, neergezonden. Hij schept jullie in de schoten van jullie moeders, een schepping na een schepping, in drie duisternissen."\' Dat is Allah, jullie ie Heer, aan Hem behoort de herschappij, geen god is er dan Hij. Waarom wenden jullie je af?'),
(4065, 39, 7, 'Als jullie niet geloven: voorwaar, Allah heeft jullie niet nodig. En de ondankbaarheid van Zijn dienaren behaagt Hem nieg maar wanneer jullie dankbaar zijn, dan heeft Hij behagen in jullie. En geen lastdrager zal de last (zonden) van een andere dragen. Daarna is tot jullie Heer jullie terugkeer. Hij brengt jullie op de hoogte van wat jullie plachten te doen. Voorwaar, Hij is Alwetend over wat zich in de harten bevindt.'),
(4066, 39, 8, 'En als tegenspoed de mens treft, dan roept Hij zijn Heer aan, tot Hem terrugkerend; maar wanneer hem daarna een genieting van Hem geschonken wordt, dan vergeet hij (de tegenspoed) waarvoor hij Hem voorheen aanriep, en kent hij deelgenoten naast Allah toe om van Zijn Weg te doen afdwalen. Zeg (O Moehammad): "Geniet maar even van je ongeloof: voorwaar, jij behoort tot de bewoners van de Hel."'),
(4067, 39, 9, 'Is de gehoorzame die een gedeelte van de nacht, zich neerknielend en staand (in de shalât doorbrengt), die het Hiernamaals vreest en hoopt op de Barmhartigheid van zijn Heer beter (... of de ongelovige?) Zeg: "Zijn degenen die kennis hebben gelijk aan degenen die geen kennis hebben?" Voorwaar, het zijn slechts de bezitters van gezond verstand die er lering uit trekken.'),
(4068, 39, 10, 'Zeg: "O Mijn dienaren die geloven, vreest jullie Heer. Voor degenen die het goede doen in deze wereld is er goedheid, en de aarde van Allah is uitgegtrekt. Voorwaar, het zijn de geduldigen die hun beloning zonder berekening zal worden gegeven."'),
(4069, 39, 11, 'Zeg: "Voorwaar, ik ben bevelen om Allah te aanbidden, Hem zuiver aanbiddend.'),
(4070, 39, 12, 'En ik ben bevolen om de eerste van de Moslims te zijn."'),
(4071, 39, 13, 'Zeg: "Voorwaar, ik vrees. als ik mijn Heer ongehoorzaam ben, een bestraffing op een geweldige Dag."'),
(4072, 39, 14, 'Zeg: "Allah is het Die ik aanbid, Hem zuiver aanbiddend.'),
(4073, 39, 15, 'Aanbidt den maar naast Hem wat jullie willen." Zeg: "Voorwaar, de verliezers zijn degenen die zichzelf en hun familie (door hun slechte bestemming) verlies toebrengen op de Dag der Opstanding. Weet dat dat het duidelijke verlies is."'),
(4074, 39, 16, 'Er zullen boven hen lagen van vuur zijn en onder hen lagen. Zo maakt Allah Zijn dienaren daarmee bevreesd. O Mijn dienaren, vreest Mj daarom.'),
(4075, 39, 17, 'En degenen die het aanbidden van de Thagôet vemijden en die terugkeren tot Allah; voor Hen is er een verheugende tijding. Verheug daarom Mijn dienaren.'),
(4076, 39, 18, 'Degenen die naar het woord luisteren en daarna het beste ervan volgen, zij zijn degenen die Allah heeft geleid en zij zijn de bezitters van verstand.'),
(4077, 39, 19, 'Voor wie het woord van bestraffing bewaarheid is, zou jij (O Moehammad) hem kunnen redden die in de Hel is?'),
(4078, 39, 20, 'Maar degenen die hun Heer vreesden; voor hen zijn er woningen waarboven (nog andere) woningen zijn gebouwd waar onder door de rivieren stromen. Als een belofte van Allah, Allah breekt de belofte niet.'),
(4079, 39, 21, 'Zie jij niet dat Allah water uit de hemel doet neerdalen en het daarna doet samenstromen in bronnen die zich in de aarde bevinden? Vervolgens brengt Hij daar planten met verschillende kleuren me voort, en daarna worden ze droog en zie jij dat ze geel worden, den laat Hij ze tot stof worden. Voorwaar, daarin is zeker een lering voor de bezitters van verstand.'),
(4080, 39, 22, 'Is dan hij, wiens bart Allah heeft verruimd voor de Islam, zodat hij door een licht van zijn Heer wordt geleid... (gelijk aan degene wiens had verzegeld is?) Wee daarom hen, wiens harten zich hebben verbard tegen het gedenken van Allah. Zij zijn het die in duidelijke dwaling verkeren.'),
(4081, 39, 23, 'Allah heeft het beste Woord nemgezonden in een Doek, op elkaar lijkende, herhalende (gedeelten). De huid van degenen die hun Heer vrezen, huivert erdoor, daarna hun huid en hun harten zacht door het gedenken van Allah. Dat is de Leiding van Allah, Hij leidt daarmee wie Hij wil. En wie door Allah tot dwaling gebracht wordt, voor hem is er geen leider.'),
(4082, 39, 24, 'Is dan hij, die zijn gezicht beschermt voor de slechte bestraffing op de Dag der Opstanding... (gelijk aan hem wiens gezicht beschermd wordt?) En tot de onrehtvaardigen wordt gezegd: "Proeft wat jullie plachten te verrichten."'),
(4083, 39, 25, 'Degenen vóór hen loochenden, zodat de bestraffing tot hen kwam uit richtingen die zij niet vermoedden.'),
(4084, 39, 26, 'Zo doet Allah hen de vernedering tijdens het wereldse leven proeven, terwijl de bestraffing van het Hiernamaals groter is. Als zij het maar wisten!'),
(4085, 39, 27, 'En voorzeker, Wij hebben voor de mensen in deze Koran allerlei vergelijkingen gemaakt. Hopelijk zullen zij zich laten vermanen.'),
(4086, 39, 28, 'Door een Arabische Koran, waarin geen afwijkingen voorkomen. Hopelijk zullen zij (Allah) vrezen.'),
(4087, 39, 29, 'Allah heeft een vergelijking gemaakt van een slaaf die aan meerdere meesters behoort die onderling ruziën, met een slaaf die aan slechts één meester behoort. Zijn zij aan elkaar gelijk? Alle lof zij Allah, maar de meesten van hen weten het niet.'),
(4088, 39, 30, 'Voorwaar, jij zuk sterven en voorwaar, zij zullen sterven.'),
(4089, 39, 31, 'Daarna zullen jullie op de Dag der Opstanding bij jullie Heer met elkaar redetwisten.'),
(4090, 39, 32, 'Wie is dan onrechtvaardiger dan hij die liegt over Allah en de Waarheid loochent wanneer deze tot hem komt? Is de verblijfplaats voor de ongelovigen niet in de Hel?'),
(4091, 39, 33, 'En degene die met de Waarheid is gekomen, en deze bevestigt: hij is degene die tot de Moettaqôen behoort.'),
(4092, 39, 34, 'Voor hen is wat zij wensen bij hun Heer. Dat is de beloning voor de weldoeners.'),
(4093, 39, 35, 'Zodat Allah het slechtste dat zij hebben gedaan zal uitwissen en zodat Hij hun hun beloning zal geven naar het beste van wat zij plachten te doen.'),
(4094, 39, 36, 'Is Allah geen Beschermer voor Zijn dienaren? En zij maken jou bang voor degenen (afgoden) naast Hem. En wie door Allah tot dwaling gebracht wordt; voor hem is er geen leider.'),
(4095, 39, 37, 'En wie door Allah geleid wordt: niemand kan hem doen dwalen. Is Allah niet Almachtig, de Bezitter van de Vergelding.'),
(4096, 39, 38, 'En als jij hun vraagt wie de hemelen en de aarde heeft geschapen, dan zullen zij zeker zeggen: "Allah." Zeg: "Zien jullie dan niet wat jullie buiten Allah aanroepen? Als Allah voor mij rampspoed wenst, zijn zij (de afgoden) het dan die de ramp van Hem kunnen wegnemen? Of als Hij Barmhartigheid wenst, zijn zij het dan die Zijn Bamhartigheid kunnen tegenhouden? Zeg: "Allah is mij voldoende. Op Hem vertrouwen degenen die vertrouwen hebben."'),
(4097, 39, 39, 'Zeg: "O mijn volk, werk op jullie manier, voorwaar, ik werk (op mijn manier). Spoedig zullen jullie het weten."'),
(4098, 39, 40, 'Tot wie een bestraffing komt: die zal door hem vemederd worden, en een blijvende bestraffing zal op hem rusten.'),
(4099, 39, 41, 'Voorwaar, Wij hebben in Waarheid het Boek aan jou gezonden. Wie dan de Leiding aanvandt; het is ten gunste van zichzelf; maar wie dwaalt; hij benadeelt slechts zichzelf. En jij (O Moehammad) bent geen verantwoordelijke over hen.'),
(4100, 39, 42, 'Allah neemt de zielen weg bij hun sterven en bij degenen die niet stemen in hun slaap. Dan houdt Hij de zielen waarvoor Hij de dood leeft beslist achter, en stuurt Hij de overigen (terug) tot een vastgestelde tijd. Voorwaar, daarin zijn zeker Tekenen voor een volk dat nadenkt.'),
(4101, 39, 43, 'Nemen zij buiten Allah voorsprekers? Zeg: "Ook al hebben zij geen enkele machten kunnen zij niet nadenken?"'),
(4102, 39, 44, 'Zeg: "Aan Allah behoort alle voorspraak. Aan Hem behoort de heeershappij van de hemelen en de aarde, en tot Hem zullen jullie terugkeren."'),
(4103, 39, 45, 'En wanneer (de naam van) Allah als Enige God wordt genoemd, dan raken de harten van degenen die niet in het Hiernamaals geloven vervuld van ergernis. Maar wanneer degenen (de afgoden) naast Hem worden genoemd, dan zijn verheugen zij zich.'),
(4104, 39, 46, 'Zeg: "O Allah, de Schepper van de hemelen en de aarde, de Kenner van het onwaarneembare en het waarneembare, U oordeelt over Uw dienaren over dat waarover zij van mening verschillen."\''),
(4105, 39, 47, 'En voorwaar, als aan degenen die onrecht pleegden alles zou toebehoren wat zich op de aarde bevindt, en nog eens zoveel erbij, dan zouden zij zich ermee vrij (willen) kopen van de slechtste bestraffing op de Dag der Opstanding. En er zullen voor hen (betraffingen) van Allah verschijnen, waar zij nooit rekening mee gehouden hebben.'),
(4106, 39, 48, 'En er zullen voor hen slechte deden die zij hadden bedreven duidelijk worden en wat zij plachten te bespotten zal hen omsingelen.'),
(4107, 39, 49, 'En wanneer tegenspoed de mens treft, dan roept hij Ons aan; maar wanneer Wij hem een genieting van Ons schenken, dan zegt hij: "Dat is mij slechts wegens mijn kennis gegeven." Het is zelfs een beproeving, maar de meesten van hen weten het niet.'),
(4108, 39, 50, 'Waarlijk, dit zeiden degenen vóór hen, en wat zij plachten te verrichten baatte hun niet.'),
(4109, 39, 51, 'Daarna werden zij getroffen door de slechte daden die zij hadden verricht. En degenen die onrecht pleegden onder hen (de afgedenaanbidders) zullen getroffen worden door de slechte daden die zij verrichtten en zij zullen niet kunnen vluchten.'),
(4110, 39, 52, 'En weten zij niet dat Allah de voorzieningen verruimt voor wie Hij wil en dat Hij beperkt? Voorwaar, daarin zijn zeker Tekenen voor een volk dat gelooft.'),
(4111, 39, 53, 'Zeg: "O mijn dienaren die buitensporig zijn tegenover zichzelf, wanhoopt niet aan de Genade van Allah. Voorwaar, Allah vetgeeft alle zonden. Voorwaar, Hij is de Vergevensgezinde, de Meest Barmhartige.'),
(4112, 39, 54, 'En keert terug tot jullie Heer, en geeft jullie over aan Hem voordat de bestraffing tot jullie komt waarna jullie niet geholpen worden.'),
(4113, 39, 55, 'En volgt op de beste wijze wat aan jullie is neergezonden van jullie Heer, voordat de bestraffing onverwacht tot jullie komt, terwijl jullie het niet beseffen."'),
(4114, 39, 56, 'Zodat er voor geen ziel aanleiding zal zijn om te zeggen: "O wat heb ik een spijt omdat ik nalatig ben geweest (in mijn plicht) jegens Allah, hoewel ik tot de spotters behoorde."'),
(4115, 39, 57, 'Of zij zou zeggen: "Als Allah mij had geleid, dan zou ik zeker tot de Moettaqôen hebben behoord!"|'),
(4116, 39, 58, 'Of zij zou zeggen, toen zij de bestraffing zeg: "Had ik (nog) een kans, dan zou ik tot de weldoeners behoren!"'),
(4117, 39, 59, 'Nee! Mijn Tekenen zijn reeds tot jou gekomen, maar jij loochende ze en jij was hoogmoedig en jij behoorde tot de ongelovigen.'),
(4118, 39, 60, 'En op de Dag der Opstanding zal jij degenen zien die over Allah hebben gelogen, hun gezichten zullen zwart zijn. Is de Hel geen verblijfplaats voor de hoogmoedigen?'),
(4119, 39, 61, 'En Allah zal degenen die (Hem) vreesden redden vanwege hun overwinning, het slechte zal hen niet treffen, en zij zullen niet bedroefd zijn.'),
(4120, 39, 62, 'Allah is de Schepper van alle dingen. Hij is over alle dingen Toezichthouder.'),
(4121, 39, 63, 'Aan Hem behoren de sleutels van de hemelen en de aarde. En degenen die de Verzen van Allah verwerpen: zij zijn degenen die verliezers zijn.'),
(4122, 39, 64, 'Zeg (O Moehammad): "Bevelen jullie mij om anderen dan Allah te aanbidden, O jullie enwetenden?"'),
(4123, 39, 65, 'Terwijl reeds aan jou en aan degenen vóór jou is geopenbaard: "Als jij deelgenoten toekent, dan zullen jouw werken vruchteloos worden en zet jij zeker tot de verliezers behoren."'),
(4124, 39, 66, 'Nee, aanbid daarom Allah en behoor tot de dankbaren.'),
(4125, 39, 67, 'En zij achten Hem niet met de achting die Hem toekomt, terwijl Hij de gehele aarde in zijn greep heeft op de Dag van de Opstanding en de hemelen in Zijn Rechterhand opgerold zullen zijn. Heilig is Hij en Verheven boven wat zij toekennen.'),
(4126, 39, 68, 'En er zal op de bazuin geblazen worden, waarop allen die in de hemelen en op de aarde zijn bezwijken, behalve (voor) wie Allah wil (dat deze blijft leven). En er zal nog een keer op de bazuin worden geblazenen dan zullen zij staan en wachten.'),
(4127, 39, 69, 'En de aarde zal schijnen met het licht van haar geer en de boeken zullen naar voren gebracht worden en de Profeten en de getuigen zullen naar veren gebracht worden en er zal tussen hen in Waarheid beslist worden en hun zal geen onrecht aangedaan worden.'),
(4128, 39, 70, 'En iedere ziel zal volledig vergolden worden voor wat zij heeft verricht En Hij weet het beste wat zij gedaan hebben.'),
(4129, 39, 71, 'En degenen die ongelovig waren zullen in menigten naar de Hel gevoerd worden. Totdat, wanneer zij bij haar zijn aangekornen, haar poorten geopend zullen worden en haar bewakers tot hen zullen zeggen: "Zijn er geen Boodschappers van jullie volk tot jullie gekomen, die jullie de Verzen van jullie Heer hebben voorgedragen en die jullie hebben gewaarschuwd voor deze Dag van jullie?" Zij zullen zeggen: "Welzeker, maar het woord van de bestraffing is bewaarheid voor de ongelovigen."'),
(4130, 39, 72, 'Er wordt gezegd: "Gaat de poorten van de Hel binnen, daarin eeuwig levend. Dat is de slechtste plaats voor de hoogmoedigen.'),
(4131, 39, 73, 'En degenen die hun Heer vreesden zullen in menigten naar het Paradijs worden gevoerd, totdat, wanneer zij bij haar zijn aangekomen en haar poorten geopend zullen worden. En haar bewakers tot hen zullen zeggen: "Salâmoen \'alaikoem. (Vrede zij met jullie) Jullie hebben goed gchandeld, treedt binnen, eeuwig levend."'),
(4132, 39, 74, 'En zij zullen zeggen: "Alle lof zij Allah Die Zijn belofte aan ons heeft vervuld en Die ons de aarde heeft doen erven. En wij verblijven in het Paradijs zoals wij willen." Het is de beste beloning voor de verrichters van het goede.'),
(4133, 39, 75, 'En jij (O Moehammad) zal de Engelen zien rondgaan om de Troon, de Glorie van hun heer lofprijzend met de lofprijzing die Hem toekomt. En er zal in Waarheid tussen hen worden beslist en er zal gezegd worden: "Alle lof zij de Heer der Werelden."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'Ha Mîm.'),
(4135, 40, 2, 'De neerzending van het Boek is van Allah, de Almachtige, de Alwetende.'),
(4136, 40, 3, 'De Vergever van de zonden en de Aanvaarder van het berouw, de Harde in de bestraffing, de Bezitter van grote macht. Geen god is er dan Hij, tot Hem is de terugkeer.'),
(4137, 40, 4, 'Niemand redetwist over de Verzen van Allah behalve degenen die ongelovig zijn. Laat daarom hun rondtrekken in het land jou niet bedriegen.'),
(4138, 40, 5, 'Vóór hen loochende het volk van Nôeh (de Boodschappers) en de bondgenoten (van ongelovigen) na hen. En elk volk wenste hun Boodschappers te grijpen. En zij redetwistten met valsheid om er de Waarheid mee te vernietigen, waarop Ik hen greep. En hoe (hard) was Mijn bestraffing!'),
(4139, 40, 6, 'En zo werd het Woord van jouw Heer bewaarheid voor degenen die ongelovig waren. Voorwaar, zij zijn de bewoners van de Hel.'),
(4140, 40, 7, 'Degenen die de Troon dragen (de Engelen) en wie zich er omheen bevinden, prijzen de Glorie van hun Heer met Zijn lofprijzing en geloven in Hem en vragen om vergeving voor degenen die geloven. (En zeggen:) "Onze Heer, Uw Barmhartigheid en Kennis omvatten alle zaken, vergeef daarom degenen die berouw tonen en die Uw Weg volgen en bescherm hen tegen de bestraffing van de Hel.'),
(4141, 40, 8, 'Onze Heer, doe hen en wie rechtschapen waren van hun vaderen en hun echtgenotes en hun nakomelingen de Tuinen van \'Adn (het Paradijs) binentreden, die U ben beloofd hebt: voorwaar, U bent de Almachtige, de Alwijze.'),
(4142, 40, 9, 'En bescherm hen voor de slechte daden. En wie op die Dag de slechte daden vreest: hem heeft U dan waarlijk begenadigd. En dat is de geweldige overwinning."'),
(4143, 40, 10, 'Voorwaar, tot degenen die ongelovig waren zal (op die Dag) geroepen worden: "De woede van Allah is zeker groter dan jullie woede op jullie zelf toen jullie tot het geloof werden geroepen, werden jullie ongelovig.'),
(4144, 40, 11, 'Zij zullen zeggen: "Onze Heer, U heeft ons twee maal doen sterven en ons twee maal doen leven. Nu erkennen wij onze zonden: is er dan nog een terugkeer van de weg (de bestraffing)?"'),
(4145, 40, 12, 'Dat is omdat jullie ongelovig waren toen Allah als Enige God werd aangeroepen. Maar toen Hem deelgenoten toegekend werden, geloofden jullie (daarin). Het oordeel behoort aan Allah, de Verhevene, de Grootste.'),
(4146, 40, 13, 'Hij is Degene Die jullie Zijn Tekenen toont en Die voor jullie uit de hemel voorzieningen neerzendt. Niemand laat zich vermanen, behalve wie berouw toont.'),
(4147, 40, 14, 'Roept daarom Allah aan, Hem zuiver aanbiddend, ook al hebben de ongelovigen er een afkeer van.'),
(4148, 40, 15, 'Hij is Degenen Die Verheven is boven alle rangen, de Bezitter van de Troon. Hij zendt de Openbaring op Zijn bevel aan wie Hij wil van Zijn dienaren, opdat Hij waarschuwt voor de Dag van de Ontmoeting.'),
(4149, 40, 16, 'Op de Dag waarop zij tevoorschijn komen, zal niets van hen voor Allah verborgen zijn. Aan wie behoort op deze Dag de Heerschappij? Aan Allah de Ene, de Overweldiger.'),
(4150, 40, 17, 'Op die Dag zal iedere ziel vergolden worden voor wat zij heeft verricht. Op die Dag is er geen onrecht. Voorwaar, Allah is snel in de afrekening.'),
(4151, 40, 18, 'Waarschuw hen (O Moehammad) voor de Dag der Opstanding waarop de harten treurig in de kelen blijven steken. Voor de onrechtvaardigen is er geen boezemvriend en geen voorspreker die wordt gehoord.'),
(4152, 40, 19, 'Hij (Allah) kent het verraad van de ogen en wat de harten verbergen.'),
(4153, 40, 20, 'En Allah oordeelt volgens de Waarheid, en degenen die zij naast Hem (deelgenoten) aanroepen oordelen nergens over: voorwaar, Allah is Alhorend, Alziend.'),
(4154, 40, 21, 'Reizen zij niet op de aarde zodat zij zien hoe het einde was van degenen vóór hen? Zij overtroffen hen in macht en in gebouwen op de aarde. Maar Allah greep hen wegens hun zonden en er was voor hen geen beschermer tegen Allah.'),
(4155, 40, 22, 'Dat was omdat hun Boodschappers tot hen waren gekomen met de duidelijke bewijzen, maar zij waren ongelovigen, waarop Allah hen greep. Voorwaar, Hij is Sterk, hard in de bestraffing.'),
(4156, 40, 23, 'En voorzeker, Wij hebben Môesa met Onze Tekenen en met een duidelijk bewijs gezonden.'),
(4157, 40, 24, 'Naar Fir\'aun en Hâmân en Qârôen en zij zeiden: "(Hij is) een tovenaar, een leugenaar."'),
(4158, 40, 25, 'Toen hij dan lot hen kwam met de Waarheid van Onze Zijde, zeiden zij: "Doodt de zonen van degenen die geloven met hem, maar laat hun dochters leven." Maar, de list van de ongelovigen kan niet anders dan mislukken.'),
(4159, 40, 26, 'En Fir\'aun zei: "Laat mij Môesa doden en laat hem zijn Heer aanroepen: voorwaar, ik vrees dat hij jullie godsdienst zal veranderen of dat hij verderf op de aarde zal zaaien."'),
(4160, 40, 27, 'En Môesa zei: Voorwaar, ik zoek mijn toevlucht bij mijn Heer en bij jullie Heer tegen iedere hoogmoedige die niet gelooft in de Dag des Oordeels."'),
(4161, 40, 28, 'En een gelovige man van het volk van Fir\'aun, die zijn geloof had verborgen, zei: "Zullen jullie een man doden omdat hij zegt: \'Mijn Heer is Allah,\' terwijl hij met de duidelijke bewijzen van jullie Heer tot jullie is gekomen? Als hij een leugenaar is, dan rust zijn leugen op hem; maar als hij waarachtig is, dan zal een deel (van de rampen) die hij jullie anzegt, jullie treffen." Voorwaar, Allah leidt niet wie een buitensporige, een leugenaar is.'),
(4162, 40, 29, '(Môesa zei:) "O mijn volk, voor jullie is vandaag het koninkrijk, (jullie zijn er) als heersers in het land, maar wie zal ons helpen tegen de bestraffing van Allah wanneer die tot ons komt?" Fir\'aun zei: "Ik vertel jullie niets dan wat ik zie, en ik leid jullie slechts naar het rechte pad."'),
(4163, 40, 30, 'En degene die gelovig was, zei: "O mijn volk, voorwaar, ik vrees voor jullie het gelijke van op de dag van de bondgenoten (een bestraffing).'),
(4164, 40, 31, 'Gelijk het lot van (het volk van) Nôeh, en de \'Âd en de Tsamôed en degenen na hen." En Allah wenst geen onrecht voor de dienaren.'),
(4165, 40, 32, '(Môesa zei:) "O, mijn volk, voorwaar, ik vrees voor jullie de Dag van het wederzijds geroep (om hulp).'),
(4166, 40, 33, 'Op die Dag vluchten jullie, de rug toekerend, er is voor jullie geen redder. En wie door Allah tot dwaling gebracht wordt: voor hem is er geen gids."'),
(4167, 40, 34, 'En voorzeker, Yôesoef is ervoor met de duidelijke bewijzen tot jullie gekomen, maar jullie bleven twijfelen over dat waarmee hij tot jullie kwam. Zelfs toen hij gestorven was, zeiden jullie: "Allah zal na hem nooit meer een Boodschapper sturen." Zo doet Allah degene dwalen die een buitensporige, een twijfelaar is.'),
(4168, 40, 35, '(Zij zijn) degenen die over de Verzen van Allah redetwisten zonder dat een bewijs tot hen is gekomen. Groot is de woede bij Allah en bij degenen die geloven. Ze verzegek Allah het hart van iedere hoogmoedige (en iedere) onderdrukker.'),
(4169, 40, 36, 'En Fir\'aun zei: "O Hâmân, maak voor mij een hoog paleis, hopelijk bereik ik de poorten.'),
(4170, 40, 37, 'De pomen van de hemelen, zodat ik de God van Môesa kan zien. En voorwaar, ik veronderstel dat hij zeker een leugenaar is." En zo werden zijn slechte daden en het afhouden van de Weg voor Fir\'aun schoonschijnend gemaakt. Maar de list van Fir\'aun kan niet anden dan mislukken.'),
(4171, 40, 38, 'En degene die gelovig was, zei: "O mijn volk, volg mij, ik zal jullie leiden op de rechte Weg.'),
(4172, 40, 39, 'O mijn volk, dit wereldse leven is slechts een vergankelijke genieting. En voorwaar, het Hiernamaals is de eeuwige verblijfplaats."'),
(4173, 40, 40, 'Wie een slechte daad heeft verricht wordt niet anders dan met haar gelijke vergolden. En wie een goede daad heeft verricht, man of vrouw; terwijl hij (of zij) een gelovige is: zij zijn het die Paradijs zullen binnengaan, zij zullen daarin voorzien worden, zonder berekening.'),
(4174, 40, 41, 'En: "O mijn volk, het bevreemdt mij dat ik jullie tot de redding oproep, tefwijl jullie mij tot de Hel oproepen.'),
(4175, 40, 42, 'Jullie roepen mij op om aan Allah ongelovig te zijn en om Hem deelgenoten toe te kennen waarover ik geen kennis heb, terwijl ik jullie oproep om in de Almachtige, de Vergevensgezinde te geloven.'),
(4176, 40, 43, 'Het is zeker dat jullie mij oproepen tot dat wat geen aanroep (kan verhoren) in het wereldse leven en niet in het Hiernamaals; en dat onze terugkeer tot Allah is en dat de buitensporigen de bewoners van de Hel zijn.'),
(4177, 40, 44, 'Jullie zullen je herinneren wat ik tot jullie zeg, en ik laat mijn zaak over aan Allah: voorwaar, Allah is Alziende over de dienaren."\''),
(4178, 40, 45, 'Allah beschermde hen toen voor het slechte wat zij hadden beraamd, terwijl de ergste bestraffing Fir\'aun en zijn volgelingen omsingelde.'),
(4179, 40, 46, 'Ze zullen \'s ochtends en \'s avonds voor de Hel geplaatst worden. En de Dag waarop het Uur valt (zegt Allah tegen de Engelen:) "Laat Fir\'aun en zijn volgelingen de hardste bestraffing binnengaan!"'),
(4180, 40, 47, 'En wanneer zij met elkaar redetwisten in de Hel, dan zeggen de zwakken tot degenen die hoogmoedig waren: Voorwaar, wij waren volgelingen van jullie. Zullen jullie daarom ons tegen de bestraffing van de Het verdedigen?"'),
(4181, 40, 48, 'Degenen die hoogmoedig waren zullen zeggen: "Voorwaar, wij bevinden ons er allen in. Voorwaar, Allah heeft geoordeeld tussen de dienaren."'),
(4182, 40, 49, 'En degenen die in de Hel zijn, zullen tot de wakers (Engelen) van de Hel zeggen: "Roept jullie Heer aan, opdat Hij voor ons de bestraffing één dag verlicht."'),
(4183, 40, 50, 'Zij (de Engelen) zullen zeggen: "Waren jullie Boodschappen niet tot jullie gekomen met de duidelijke bewijzen?" Zij zullen zeggen: "Welzeker." Zij (de Engelen) zullen zegen: "Roept dan aan." Maar de aanroep van de ongelovigen kan niet anders dan mislukken!'),
(4184, 40, 51, 'Voorwaar, Wij zullen Onze Boodschappenrs en degenen die geloven zeker helpen, in het wereldse leven en op de Dag dat de getuigen zullen opstaan.'),
(4185, 40, 52, 'Op die Dag baat de verontschuldiging van de onrechtplegers hun niet. En voor hen is er de vervloeking en voor hen is er de slechtste verblijfplaats.'),
(4186, 40, 53, 'En voorzeker, Wij hebben Môesa de Leiding gegeven en Wij hebben de Kinderen van Israël de Schrift doen erven.'),
(4187, 40, 54, 'Als Leiding en als Vermaning voor de bezitters van verstand.'),
(4188, 40, 55, 'Wees daarom geduldig: voorwaar, de belofte van Allah is waar. En vraag vergeving voor jouw zonde en prijs de Glorie van jouw Heer met Zijn lofprijzing, in de avond en in de ochtend.'),
(4189, 40, 56, 'Voorwaar, degenen die over de Verzen van Allah redetwisten zonder een bewijs dat tot hen is gekomen: in hun harten is niets dan hoogmoed waardoor zij het (doel) niet bereiken. Zoek daarom je toevlucht tot Allah: voorwaar, Hij is de Alhorende, de Alziende.'),
(4190, 40, 57, 'De Schepping van de hemelen en de aarde is zeker groter dan de schepping van de mens, maar de meeste mensen weten het niet.'),
(4191, 40, 58, 'En de blinde en de ziende (van hart) zijn niet gelijk, en zij die geloven en goede werken verrichten zijn niet gelijk aan de kwaaddoeners. Gering is de lering die jullie eruit trekken.'),
(4192, 40, 59, 'Voorwaar, het Uur zal zeker komen, daaraan is geen twijfel, maar de meeste mensen geloven niet'),
(4193, 40, 60, 'En jullie Heer zei: "Roept Mij aan, Ik zal jullie verhoren. Voorwaar, degenen die te hoogmoedig zijn om Mij te dienen zullen de Hel binnengaan als vernederden."'),
(4194, 40, 61, 'Allah is Degene Die voor jullie de nacht heeft gemaakt opdat jullie er in zullen rusten en de dag als verlichter. Voorwaar, Allah is de zeker de Bezit ter van de Gunst voor de mensen, maar de meeste mensen zijn niet dankbaar.'),
(4195, 40, 62, 'Dat is Allah, jullie Heer, de Schepper van alle dingen, geen god is er dan Hij. Waarom worden jullie dan belogen?'),
(4196, 40, 63, 'Zo worden degenen die de Verzen van Allah plachten te ontkennen belogen.'),
(4197, 40, 64, 'Allah is Degene Die voor jullie de aarde tot een stevige plaats heeft gemaakt en de hemel tot een gewelf. En Hij heeft jullie gevormd en jullie vormen geschikt gemaakt en Hij heeft jullie van de goede dingen voorzien. Dat is Allah jullie Heer. Gezegend zij daarorn Allah, de Heer der Werelden.'),
(4198, 40, 65, 'Hij is de Levende, geen god is er dan Hij. Roept Hem daarom aan, Hem zuiver aanbiddend. Alle lof zij Allah, de Heer der Werelden.'),
(4199, 40, 66, 'Zeg (O Moehammad): "Voorwaar, het is mij verboden dat ik degenen aanbid die jullie naast Allah aanroepen, nadat de bewijzen van mijn Heer tot mij zijn gekomen. En mij is bevolen om mij over te geven aan de Heer der Werelden.'),
(4200, 40, 67, 'Hij is Degene Die jullie uit aarde heeft geschapen, daarna uit een druppel, daarop uit een bloedklonter, vervolgens deed Hij jullie naar buiten komen als een kind. Om jullie daarna jullie volwassenheid te doen bereiken, om jullie tenslotte oud te doen worden. En onder jullie zijn er die eerder overlijden, opdat jullie de vastgestelde tijd zullen bereiken. En hopelijk zullen jullie het begrijpen.'),
(4201, 40, 68, 'Hij is Degene Die doet leven en doet sterven. En wanneer Hij een zaak heeft bepaald, dan zegt Hij er slechts tegen: "Wees," en het is.'),
(4202, 40, 69, 'Zie jij niet dat degenen die over de Verzen van Allah redetwisten afgeleid (van de Waarheid) zijn?'),
(4203, 40, 70, '(Zij zijn) degenen die het Boek en hetgeen waarmee Wij de Boodschappers hebben gezonden loochenden. Zij zullen het weten.'),
(4204, 40, 71, 'Wanneer de ketenen en de kettingen om hun nekken hangen worden zij gesleept.'),
(4205, 40, 72, 'In het kokende water. vervolgens worden zij in de Hel verbrand.'),
(4206, 40, 73, 'Daarna wordt tot hen gezegd: "WAAr zijn (de afgoden) die jullie toegekend hebben?'),
(4207, 40, 74, 'Naast Allah?" Zij zullen zeggen: "Zij zijn van ons verdwenen. Wij plachten voorheen zelfs niets aan te roepen." Zo doet Allah de ongelovigen dwalen.'),
(4208, 40, 75, '(Er wordt gezegd:) "Dat was omdat jullie op aarde zonder recht blij plachten te leven en omdat jullie arrogant leefden.'),
(4209, 40, 76, 'Gaat de poorten van de Hel binnen, eeuwig levend daarin. Slecht is dan de verblijfplaats van de hoogmoedigen."'),
(4210, 40, 77, 'Wees daarom geduldig: voorwaar, de belofte van Allah is waar. Of Wij jou (Moehammad) nu een gedeelte van wat Wij hen hebben aangezegd laten zien (het is hoe dan ook terecht). Of dat Wij jou wegnemen: tot Ons zullen zij dan terugkeerd worden.'),
(4211, 40, 78, 'En voorzeker, Wij hebben vóór jou Boodschappers gezonden. Over sommigen van hen hebben Wij jou verteld en over sommigen hebben Wij jou niet verteld. Fn het paste een Boodschapper niet om met een Teken te komen zonder de toestemming van Allah. Wanneer dan het bevel van Allah is gekomen, dan wordt er volgens de Waarheid bepaald. En dan verliezen de opstandigen.'),
(4212, 40, 79, 'Allah is Degene Die voor jullie het vee heeft gemaakt om erop te rijden en ervan te eten.'),
(4213, 40, 80, 'En voor jullie zijn er baten in. En zodat jullie dàt door middel van hen zullen bereiken, waar in jullie harten behoefte aan is. En op hen en op de schepen worden jullie gedragen.'),
(4214, 40, 81, 'En Hij laat jullie Zijn Tekenen zien. Welke van de Tekenen van Allah ontkennen jullie dan?'),
(4215, 40, 82, 'Reizen zij dan niet op aarde en zien zij dan niet wat het einde was van degenen vóór hen? Zij waren met meer dan zij en zij overtroffen hen in macht en in gebouwen op de aarde. Maar wat zij hadden verworven, baatte hun niet.'),
(4216, 40, 83, 'En toen hun Boodschappers tot hen kwamen met de duidelijke bewijzen, waren zij blij met wat bij ben was aan kennis. Maar dat waar zij de spot mee plachten te drijven omsingelde hen.'),
(4217, 40, 84, 'En toen zij Onze straf zagen, zeiden zij: "Wij geloven in Allah, de Énige. En wij geloven niet in wat wij aan Hem plachten toe te kennen.'),
(4218, 40, 85, 'Maar hun geloof baatte hun niet toen zij Onze bestraffing zagen. Dat is de handelwijze van Allah, die reeds voor Zijn dienaren was. En de ongelovigen verloren toen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'Ha Mîm.'),
(4220, 41, 2, '(Dit is) een neerzending van de Erbarmer, de Meest Barmhartige.'),
(4221, 41, 3, 'Een Boek waarvan de Verzen duidelijk uitgelegd zijn, als een Arabische Koran, voor een volk dat weet.'),
(4222, 41, 4, 'Als een verkondiger van verheugende tijdingen en een waarschuwer, maar de meesten van hen hebben zich er van afgewend, zij luisteren niet.'),
(4223, 41, 5, 'En zij zeiden: "Onze harten zijn verhuld voor dat waar jij (O Moehammad) ons toe oproept, en in onze oren is doofheid en tussen en ons en jou is een afscheiding. Werk dan (op jouw manier), voorwaar, wij werken (op onze manier)."'),
(4224, 41, 6, 'Zeg: "Ik ben slechts een mens zoals jullie, aan mij is geopenbaard dat jullie God één God is, richt jullie daarom standvastig tot Hem, en smeekt Hem om vergeving, en wee de veelgodenaanbidders."'),
(4225, 41, 7, '(Zij zijn) degenen die de zakât niet geven en die niet in het Hiernamaals geloven.'),
(4226, 41, 8, 'Voorwaar, degenen die geloven en die goede werken verrichten, voor hen is er een beloning zonder onderbreking.'),
(4227, 41, 9, 'Zeg (O Moehammad): "Jullie geloven zeker niet in Hem, Die de aarde in twee dagen (perioden) heeft geschapen? En kennen jullie Hem deelgenoten toe? Dat is de Heer der Werelden!"'),
(4228, 41, 10, 'En Hij maakte bergen op haar en Hij zegende haar en Hij bepaalde de maat (van alle voorzieningen) in vier volledige dagen (perioden), voor de vragenden.'),
(4229, 41, 11, 'Daama wendde Hij Zich tot de hemel die een nevel was en Hij zei tot haar en tot de aarde: "Komt tot Ons, gewillig of ongewillig." Zij (de hemelen en de aarde) zeiden: "Wij zijn gewillig gekomen."'),
(4230, 41, 12, 'En Hij vervolmaakte hen, de zeven hemelen, in twee dagen (perioden) en Hij openbaarde in alle hemelen hun beschikking. En Wij versierden de nabije hemel met sterren, als een bescherming (tegen de Satan). Dat is de ordening van de Almachtige, de Alwetende.'),
(4231, 41, 13, 'Wanneer zij zich dan afwenden, zeg dan: "Ik heb jullie gewaarschuwd voor een bliksemslag als de bliksemslag die de \'Âd en de Tsamôed trof."'),
(4232, 41, 14, 'Toen de Boodschappers vóór hen en na hen waren gekomen (en zeiden:) "Aanbidt alleen Allah," zeiden zij: "Als Allah het had gewild, dan had Hij Engelen gezonden: voorwaar, wij geloven niet in dat waar jij mee gezonden bent."'),
(4233, 41, 15, 'Wat de \'Âd betreft: zij waren hoogmoedig op de aarde, zonder recht, en zij zeiden: "Wie is er sterker dan wij?" Zien zij niet dat Allah, Degene Die hen geschapen heeft, sterker is dan zij? En zij plachten Onze Tekenen te ontkennen.'),
(4234, 41, 16, 'Wij zonden toen een zeer koude stormwind over hen, tijdens enkele sombere dagen, om hen tijdens het wereldse leven de bestraffing van de vernedering te doen proeven. En de bestraffing van het Hiernamaals is zeker vernederender en zij worden niet geholpen.'),
(4235, 41, 17, 'En wat de Tsamôed betreft: Wij hebben hun Leiding geschonken, maar zij gaven de voorkeur aan blindheid boven de Leiding, waarop de bliksemslag van de vernederende bestraffing hen trof, wegens wat zij plachten te verrichten.'),
(4236, 41, 18, 'En Wij redden degenen die geloofden en (Allah) plachten te vrezen.'),
(4237, 41, 19, 'En die Dag zullen de vijanden van Allah bij de Het verzameld worden, dan zullen zij (daarheen) gesleept worden.'),
(4238, 41, 20, 'Totdat, wanneer Zij haar (de Hel) bereiken, hun oren, en hun ogen en kun huiden tegen hen zullen getuigen, wegens wat zij plachten te doen.'),
(4239, 41, 21, 'En zij zullen tegen hun huiden zeggen: "Waarom getuigen jullie tegen ons." Zij zullen zeggen: "Allah heeft ons doen spreken, Degene Die alle dingen heeft doen spreken. En Hij heeft jullie de eerste keer geschapen en tot Hem worden jullie teruggekeerd."'),
(4240, 41, 22, 'En jullie kunnen je er niet voor verbergen dat jullie oren, en jullie ogen en jullie huiden tegen jullie zullen getuigen. Maar jullie veronderstelden dat Allah niet veel weet over wat jullie doen.'),
(4241, 41, 23, 'En dat was jullie veronderstelling, waarmee jullie over jullie Heer veronderstelden die jullie in het ongeluk gestort heeft. Toen gingen jullie tot de verliezers behoren.'),
(4242, 41, 24, 'Als zij dan geduldig zijn (met de bestraffing): dan is de Hel een verblijfplaats voor hen. En als zij om verontschuldiging vragen, dan zullen zij niet behoren tot de verontschuldigden.'),
(4243, 41, 25, 'En Wij zullen voor hen metgezellen aanwijzen die voor hen schoonschijnend maken wat vóór hen is en wat achter hen is. En terecht is het woord (van bestraffing) voor hen van de voorafgaande volkeren van de mensen en de Djinn"s. Voorwaar, zij waren de verliezers.'),
(4244, 41, 26, 'En degenen die niet geloven zeiden: "Luistert niet naar deze Koran en verstoort het. Hopelijk zullen jullie overwinnen."'),
(4245, 41, 27, 'En Wij zullen degenen die niet geloven zeker een harde bestraffing doen proeven. En Wij zullen hen zeker vergelden naar het slechtste wat zij plachten te doen.'),
(4246, 41, 28, 'Dat is de vergelding voor de vijanden van Allah: de Hel. Voor hen is daar de eeuwigc verblijfplaats, als een vergelding omdat zij Onze Tekenen plachten te ontkennen.'),
(4247, 41, 29, 'En degenen die niet geloofden zeiden:\'"Onze Heer, laat ons degenen van de mensen en de Djinn\'s zien die ons hebben doen dwalen, wij zullen hen onder onze voeten plaatsen, zodat zij tot de allerlaagsten zullen beheren."'),
(4248, 41, 30, 'Voorwaar, degenen die zeggen: "Onze Heer is Allah," en die vervolgens standvastig zijn: over hen zullen de Engelen neerdalen (en zeggen:) "Weest niet bevreesd en niet treurig, en weest verheugd met het Paradijs dat aan jullie is beloofd.'),
(4249, 41, 31, 'Wij zijn jullie helpers tijdens het wereldse kven en in het Hiernamaals en voor jullie is daarin wat jullie verlangen en voor jullie is daarin wat jullie vragen.'),
(4250, 41, 32, 'Als een ontvangst van de Vergevensgezinde, de Meest Barmhartige."'),
(4251, 41, 33, 'En wiens woord is beter den dat van hem die oproept tot Allah en die goede werken verricht, en die zegt: "Voorwaar, ik behoor tot de Moslims."'),
(4252, 41, 34, 'En het goede en het kwade zijn niet gelijk: beantwoord (het kwade) met wat beter is, dan zal degene met wie je in vijandschap leefde als een oprechte vriend worden.'),
(4253, 41, 35, 'Maar dit wordt slechts gegeven aan degenen die geduldig zijn en dit wordt slechts gegeven aan de bezitter van een geweldig geluk.'),
(4254, 41, 36, 'En wanneer een verzoeking van de Satan jou treft, zoek dan je toevlucht bij Allah: voorwaar, Hij is de Alhorende, de Alwetende.'),
(4255, 41, 37, 'En tot Zijn Tekenen behoren de nacht en de dag, en de zon en de maan. Knielt jullie niet neer voorde zon en niet voorde maan, maar knielt jullie neer voor Allah, Degene Die hen heeft geschapen, als jullie alleen Hem aanbidden.'),
(4256, 41, 38, 'Als zij dan hoogmoedig min: degenen die zich bij jouw Heer bevinden prijzen dag en nacht Zijn Glorie en zij versagen niet.'),
(4257, 41, 39, 'En het behoort tot Zijn Tekenen dat jij de droge aarde ziet, en als Wij er dan water op doen neerdalen, dan beweegt zij en zet zij zich uit. Voorwaar, Degene Die haar doet leven, doet zeker ook de doden leven. Voorwaar, Hij is de Almachtige over alle zaken.'),
(4258, 41, 40, 'Voorwaar, degenen die Onze Verzen verdraaien zijn niet voor Ons verborgen. Is dan wie in het Vuur geworpen wordt beter, of wie veilig aankomt op de Dag der Opstanding? Doet wat jullie willen: voorwaar, Hij is Alziende over wat jullie doen.'),
(4259, 41, 41, 'Voorwaar, degenen die niet in de Vermaning (de Koran) geloven nadat deze tot hen was gekomen (zullen gestraft worden). Voorwaar, het is een verheven Boek.'),
(4260, 41, 42, 'De valsheid raakt hem (de Koran) niet, niet van voren en niet van achteren. (Het is) een neerzending van de Alwijze, de Geprezene.'),
(4261, 41, 43, 'Er wordt jou niets gezegd wat waarlijk niet tot de Boodschappers vóór jou is gezegd. Voorwaar, jouw Heer is zeker de Bezitter van vergeving en de Bezitter van een pijnlijke bestraffing.'),
(4262, 41, 44, 'En als Wij hem als een Koran in een vreemde taal hadden geopenbaard, dan hadden zij zeker gezegd: "Waren zijn Verzen maar duidelijk (in onze taal) uitgelegd!" (Hij is) in een vreemde taal, terwijl (de Profeet) een Arabier is! Zeg: "Hij is voor degenen die geloven Leiding en genezing. En degenen die niet geloven, in hun oren is doofheid, die bij hen blindheid (van hart) veroorzaakt. Zij zijn degenen die vanaf een verre plaats worden geroepen.'),
(4263, 41, 45, 'En voorzeker, Wij hebben Môesa de Schrift (de Taurât) gegeven, maar er werd over geredetwist. En als er geen woord (van uitsel) van jouw Heer was vooruitgegaan, dan was de zaak tussen hen zeker bepaald. En voorwaar, zij verkeren over hem (de Koran) zeker in vergaande twijfel.'),
(4264, 41, 46, 'Wie goede werken heeft verricht, het is voor hemzelf; en wie kwade werken verricht, hij is er verantwoordelijk voor. En jouw Heer is niet onrechtvaardig tegenover de dienaren.'),
(4265, 41, 47, 'Tot Hem wordt de kennis over het Uur teruggebracht. En er komen geen vruchten uit hun kolven; en er is geen vrouw die een kind draag of het baart, zonder dat het met Zijn Kennis gebeurt. Op de Dag waarop Hij tot hen roept: "Waar zij mijn deelgenoten?", zeggen zij: "Wij vertellen U dat niemand van ons daarvan getuigt."'),
(4266, 41, 48, 'En wat zij voorheen plachten aan te roepen is van hen weggedwaald, en zij zijn ervan overtuigd dat er voor hen geen uitweg is.'),
(4267, 41, 49, 'De mens versaagt niet om het goede te smeken; maar wanneer hem het slechte treft, dan is hij zonder hoop, wanhopig.'),
(4268, 41, 50, 'En als Wij hem Barmhartigheid van Ons doen proeven nadat tegenspoed hem trof, dan zal bij zeker zeggen: "Het komt mij toe, en ik denk niet dat het Uur zal komen, en als ik tot mijn Heer teruggekeerd zou worden, dan zou er zeker voor mij aan Zijn Zijde het goede zijn." En Wij zullen zeker aan degenen die niet geloofden mededelen wat zij hebben gedaan. En Wij zullen hen zeker een zware bestraffing doen proeven.'),
(4269, 41, 51, 'En als Wij de mens gunsten schenken, dan wendt hij zich af en verwijdert hij zich; maar als tegenspoed hem treft, dan richt hij vele smeekbeden (tot Allah).'),
(4270, 41, 52, 'Zeg: "Wat dachten jullie: als (deze Koran) van Allah afkomstig is, en jullie deze dan verwerpen; wie is er dan verder afgedwaald dan wie in vergaande twijfel verkeert?"'),
(4271, 41, 53, 'Wij zullen hun Onze Tekenen laten zien, aan de horizonten en in jullie zelf, tot het jullie duidelijk zal zijn dat bij (de Koran) de Waarheid is. Is het niet voldoende dat jouw Heer Getuige over alle zaken is?'),
(4272, 41, 54, 'Weet: voorwaar, zij verkeren in twijfel over de ontmoeting met hun Heer. Weet: voorwaar, Hij omvat alle zaken.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'Ha Mîm.'),
(4274, 42, 2, '\'Aîn Sîn Qâf.'),
(4275, 42, 3, 'Zó openbaart Allah aan jou en aan degenen vóór jou; Allah is de Almachtige, de Alwijze.'),
(4276, 42, 4, 'Aan Hem behoort alles wat in de hemelen en op de aarde is, en Hij is de Verhevene, de Geweldige.'),
(4277, 42, 5, 'Bijna scheuren de bovenkanten van de hemelen, en de Engelen prijzen de Glorie van hun Heer met Zijn lofprijzing en vragen om vergeving voor degenen die op de aarde zijn. Weet: voorvaar, Allah is de Vergevensgezinde, de Meest Barmhartige.'),
(4278, 42, 6, 'En degenen die naast Allah beschermen nemen: Allah is de Waker over hen en jij (O Moehammad) bent over hen geen verantwoordelijke.'),
(4279, 42, 7, 'Zó hebben Wij aan jou een Arabische Koran geopenbaard, om de bewoners van Oommoelqoerâ (Mekkah) en degenen die in de omgeving ervan wonen te waarshuwen. En om te waarschuwen voor de Dag der verzameling, waaraan geen twijfel is; een deel gaat naar het Paradijs en een deel gaat naar de Hel.'),
(4280, 42, 8, 'Als Allah het had gewild, dan had Hij hen (als behorend) tot één godsdienst (Oemmah) gemaakt, maar Allah laat tot Zijn Barmhartigheid toe wie Hij wil. En voor de onrechtvaardigen is er geen beschermer en geen helper.'),
(4281, 42, 9, 'Nemen jullie naast Allah beschermers? En Allah is de Beschermer. En Hij doet het dode leven, en Hij is Almachtig over alle zaken,'),
(4282, 42, 10, 'En waar jullie ook van mening over verschillen: de beslissing daarover is aan Allah. (Zeg:) "Dat is Allah, mijn Heer, op Hem vertrouw ik en tot Hem keer ik weer."'),
(4283, 42, 11, 'De Schepper van de hemelen en de aarde, Hij heeft voor jullie van julliezelf en van de dieren paren geschapen. Hij vermenigvuldigt jullie, niets is aan Hem gelijk. En Hij is de Alhorende, de Alziende.'),
(4284, 42, 12, 'Aan Hem behoren de sleutels van de hemelen en de aarde, Hij verruimt de voorzieningen voor wie Hij wil en Hij beperkt. Voorwaar, Hij is Alwetend over alle zaken.'),
(4285, 42, 13, 'Hij heeft jullie de godsdienst uitgelegd: wat Hij heeft opgedragen aan Nôeh, en hetgeen Wij aan jou geopenbaard hebben en wat Wij aan Ibrâhîm en Môesa en \'Isa hebben opgedragen: dat jullie de godsdienst onderhouden en dat jullie daarover niet verdeeld raken. Het is een zware zaak voor de veelgodenaanbidders, waartoe jullie hen oproepen. Allah kiest voor Zich wie Hij wil en Hij leidt naar Zich wie terugkeert.'),
(4286, 42, 14, 'Zij raakten pas verdeeld toen de kennis tot hen kwam, elkaar onrecht aandoend. En als er niet een Woord (bevel) van jouw Heer was uitgegaan, tot een bepaalde tijd, den zouden zij al bestraft zijn. En voorwaar, degenen die de Schrift na hen erfden (de Joden en de Christenen), verkeren daarover zeker in vergaande twijfel.'),
(4287, 42, 15, 'Roep daarom (op tot de Islam), en wees standvastig zoals jou is bevelen, en volg hun begeerten niet, en zeg: "Ik geloof in wat Allah van het Boek heeft neergezonden en ik ben bevolen om onder jullie rechtvaardig te oordelen. Allah is onze Heer en jullie Heer, voor ons zijn onze daden en voor julle zijn jullie daden. Er is geen reden tot twist tussen ons en jullie. Allah zal ons verzamelen. En tot Hem is de terugkeer."'),
(4288, 42, 16, 'En degenen die over Allah redetwisten nadat de (godsdienst van Allah) aanvaard is, hun verweer is nietig bij hun Heer. En op hen rust een grote toorn en voor hen is er een harde bestraffing.'),
(4289, 42, 17, 'Allah is het Degene Die het Boek (de Koran) in Waarheid en met de weegschaal heeft neergezonden. En hoe kan jij dat weten? Misschien is het Uur al nabij.'),
(4290, 42, 18, 'Degenen die er niet in geloven vragen om het (Uur) te bespoedigen. En degenen die geloven zijn er bevreesd voor, en zij weten dat het de Waarheid is. Weet: voorwaar, degenen die over het Uur redetwisten verkeren in vergaande dwaling.'),
(4291, 42, 19, 'Allah is Zachtmoedig voor Zijn dienaren, Hij voorziet wie Hij wil, en Hij is de Sterke, de Almachtige.'),
(4292, 42, 20, 'Wie de beloning van het Hiernamaals wenst, voor hem vermeerderen Wij zijn beloning; en wie de beloning van het wereldse leven wenst, am hem geven Wij daarvan, maar voor hem is er in het Hiernamaals geen aandeel.'),
(4293, 42, 21, 'Hebben zij deelgenoten die hun in de godsdienst dat voorschrijven waartoe Allah geen toestemming heeft gegeven? En als er niet een beslissend Woord (van Allah) was, dan zouden zij reeds bestraft zijn. Voorwaar, voor de onrechtvaardigen is er een pijnlijke bestraffing.'),
(4294, 42, 22, 'Jij ziet de onrechtvaardigen bevreesd zijn voor wat zij hebben verricht en hij (de bestraffing) treft hen. Maar degenen die geloven en goede werken verrichten, zij verkeren in de Tuinen van het Paradijs. Voor hen is er bij hun Heer wat zij maar wensen. Dat is de grote gunst.'),
(4295, 42, 23, 'Dat is wat Allah als verheugende tijding aan zijn dienaren verkondigt, degenen die geloven en goede werken verrichten. Zeg (O Moehammad): "Ik vraag van jullie geen beloning, alleen liefde voor de naaste verwanten. En voor wie m goede daad verricht vermenigvuldigen Wij het goede. Voorwaar, Allah is de Vergevensgezinde, de Waarderende.'),
(4296, 42, 24, 'Of zeggen zij dat hij (Moehammad) een leugen over Allah verzonnen heeft? Als Allah het wil, dan verzegek Hij jouw hart. Allah wist de valsheid uit en Hij bevestigt de Waarheid met Zijn Woorden. Voorwaar, Hij is Alwetend over wat zich in de harten bevindt.'),
(4297, 42, 25, 'En Hij is Degene Die het berouw van Zijn dienaren aanvaardt. En Hij vergeeft de slechte daden en Hij weet wat jullie doen.'),
(4298, 42, 26, 'Hij verhoort degenen die geloven en goede werken verrichten en vemenigvuldigt voor hen van Zijn gunst, maar voor de ongelovigen is er een harde bestraffing.'),
(4299, 42, 27, 'En als Allah de voorzieningen aan Zijn dienaren had verruimd, dan zouden zij zeker buitensporig hebben gehandeld op de aarde, maar Hij zendt volgens een bepaalde maat neer wat Hij wil. Voorwaar, Hij is Alwetend over Zijn dienaren, Alziend.'),
(4300, 42, 28, 'En Hij is Degene Die de regen neerzendt nadat zij wanhoopten, en Hij verspreidt Zijn Barmhartigheid, en Hij is de Beschermheer, de Geprezene.'),
(4301, 42, 29, 'En tot Zijn Tekenen behoort de schepping van de hemelen en de aarde en de levende wezens die daarop rondlopen. En Hij is in stad om hen te verzamelen, wanneer Hij wil.'),
(4302, 42, 30, 'En er treft jullie geen ramp, of het is vanwege wat jullie handen hebben verricht, maar Hij vergeeft veel.'),
(4303, 42, 31, 'En jullie zullen op de aarde nooit kunnen ontkomen. En er is voor jullie naast Allah geen beschermer en geen helper.'),
(4304, 42, 32, 'En tot Zijn Tekenen behoren de schepen zo groot als bergen die op de zee varen.'),
(4305, 42, 33, 'En als Hij wil, dan laat Hij de wind liggen, zodat de schepen stil komen te liggen op haar oppervlak. Voorwaar, daarin zijn zeker tekenen vooir iedere geduldige en dankbare.'),
(4306, 42, 34, 'Of Hij vernietigt deze (schepen) wegens wat zij verichtten, maar Hij vergeeft veel.'),
(4307, 42, 35, 'En opdat zij die over Onze Tekenen redetwisten weten dat er voor hen geen uitweg is.'),
(4308, 42, 36, 'En wat jullie gegeven is, is slechts een vergankelijke genieting van het wereldse leven. Maar wat zich bij Allah bevindt is beter en blijvender voor hen die geloven en die op hun Heer vertrouwen.'),
(4309, 42, 37, 'En (zij zijn) degenen die de grote zonden en de verdorvenheid mijden. En als zij boos zijn, dan vergeven zij.'),
(4310, 42, 38, 'En (zij zijn) degenen die gehoor geven aan hun Heer en de shalât onderhouden, en hun zaken in onderling overleg (Sjôera) beslissen, en zij geven uit van waar Wij hun mee voorzien hebben.'),
(4311, 42, 39, 'En (zij zijn) degenen die, als onrecht hen treft, zich verdedigen.'),
(4312, 42, 40, 'En de vergelding voor een slechte daad is dezelfde slechte daad, maar voor wie vergeeft en verzoent: zijn beloning is bij Allah. Voorwaar, Hij houdt niet van de onrechtplegers.'),
(4313, 42, 41, 'En wie zich verdedigt nadat hem onrecht is aangedaan: zij zijn degenen tegen wie er geen weg (tot bestraffing) is.'),
(4314, 42, 42, 'Er is wel een weg (tot bestraffing) tegen degenen die de mensen onrecht aandeden en die zonder recht buitensporig op aarde handelden. Zij zijn degenen voor wie er een pijnlijke bestraffing is.'),
(4315, 42, 43, 'Maar wie geduldig was en vergaf voorwaar, dat behoort zeker tot de aanbevolen daden.'),
(4316, 42, 44, 'En wie door Allah tot dwaling gebracht wordt: voor hem is er daarna geen helper. En jij zult zien dat wanneer de onrechtplegers de straf zien, zij zullen zeggen: "Is er voor ons nog een weg om (naar de aarde) terug, te keren?"'),
(4317, 42, 45, 'En jij zult zien hoe zij naar haar (de Hel) worden gesleept, vrezend door de vernedering. Zij kijken met een zwakke blik. En degenen die geloven zeggen: "Voorwaar, de verliezers zijn degenen die zichzelf en hun families verlies hebben toegebracht op de Dag der Opstanding." Weet dat de onrechtvaardigen in een eeuwige bestraffing zullen verken."'),
(4318, 42, 46, 'En er is voor hen naast Allah geen beschermer die hen kan helpen. En wie door Allah tot dwaling gebracht wordt, voor hem is er geen weg (om nog Leiding te vinden).'),
(4319, 42, 47, 'Geeft gehoor aan jullie Heer, voordat er een Dag komt van Allah die onafwendbaar is. Er zal voor jullie geen toevluchtsoord zijn en er is geen ontkenning voor jullie (van jullie zonden).'),
(4320, 42, 48, 'En als zij zich dan afwenden: Wij hebben jou niet als waker over hen gezonden, jij bent niets dan een verkondiger. En voorwaar, wanneer Wij de mens een genieting van Ons laten proeven, dan is hij er blij meer. Maar als een tegenslag hem treft wegens wat zijn handen hebben voortgebracht: voorwaar, de mens is zeer ondankbaar.'),
(4321, 42, 49, 'Aan Allah behoort de heerschappij van de hemelen en de aarde. Hij schept wat Hij wil en hij schenkt meisjes aan wie Hij wil en Hij schenkt jongens aan wie Hij wil.'),
(4322, 42, 50, 'Of Hij schenkt hun jongens en meisjes. En Hij maakt onvruchtbaar wie Hij wil. Voorwaar, Hij is Alwetend, Almachtig.'),
(4323, 42, 51, 'Het past de mens niet dat Allah tot hem spreekt, behalve door middel van een openbaring of van achter een scherm of door het zenden van een gezant (Djibrîl), die het dan openbaart met Zijn toestemming, zoals Hij wil. Voorwaar, Hij is Verheven, Alwijs.'),
(4324, 42, 52, 'Zó hebben Wij aan jou een openbaring neergezonden, een zaak van Ons. Jij wist toen niet wat het Boek (de Koran) was en wat het geloof was, maar Wij hebben hem tot een licht gemaakt waarmee Wij van Onze dienaren leiden wie Wij willen. En voorwaar, jij leidt zeker naar een recht Pad.'),
(4325, 42, 53, 'Het is het Pad van Allah, Degene aan Wie alles wat in de hemelen en op de aarde is toebehoort. Weet: alle zaken keren tot Allah terug.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'Ha Mîm.'),
(4327, 43, 2, 'Bij het duidelijke Boek. (de Koran)'),
(4328, 43, 3, 'Voorwaar, Wij hebben hem als een Arabische Koran gemaakt Hopelijk zullen jullie begrijpen.'),
(4329, 43, 4, 'En voorwaar, bij is bij Ons vastgelegd in de Moeder van de Schrift (Lauhoelmahfôezh), verheven en wijs.'),
(4330, 43, 5, 'Zullen Wij dan de Vermaning van jullie weghouden, Ons afwendend, omdat jullie een buitensporig volk zijn?'),
(4331, 43, 6, 'En hoeveel Profeten hebben Wij niet gezonden naar de vroegeren?'),
(4332, 43, 7, 'En er kwam geen Profeet tot hen, of zij dreven de spot met hem.'),
(4333, 43, 8, 'En Wij hebben (volken) vernietigd die machtiger waren dan zij. En de voorbeelden van de vroegeren zijn (hen) voorafgegaan.'),
(4334, 43, 9, 'En als jij hen vraagt wie de hemelen en de aarde heeft geschapen, dan zullen zij zeker zeggen: "De Almachtige, de Alwetende heeft hen geschapen."'),
(4335, 43, 10, 'Degene Die de aarde tot een uitspreide plaats heeft gemaakt en Die daarin voor jullie wegen heeft gemaakt. Hopelijk zullen jullie Leiding volgen.'),
(4336, 43, 11, 'En Degene Die uit de hemel water heeft doen neerdalen, volgens een bepaalde maat. Dan doen Wij daarmee dode aarde leven. Op die manier zullen jullie opgewekt worden.'),
(4337, 43, 12, 'En Degene Die alle soorten heeft geschapen en Die voor jullie schepen en vee heeft gemaakt, waarop jullie kunnen rijden.'),
(4338, 43, 13, 'Opdat jullie stevig op hun ruggen zullen zitten en dan de gunst van jullie Heer gedenken. Wanneer jullie op hen zitten, zeggen jullie: "Soebhânalladzi sachchara lanâ hâdzâ wâ mâ koennâ lahoe moeqrimîn, " (Heilig is Degene Die dit voor ons dienstbaar heeft gemaakt, en wij hebben er geen macht over)'),
(4339, 43, 14, '"Wa innâ ilâ rabbinâ lamoenqalibôen." (En voorwaar, tot onze Heer zullen wij zeker terugkeren.)'),
(4340, 43, 15, 'En zij stelden naast Hem dienaren van Hem aan als een deel van Hem. Voorwaar, de mens is zeker een duidelijke zeer ondankbare.'),
(4341, 43, 16, 'Of heeft Hij zich van wat Hij geschapen heeft dochters genomen en voor jullie zonen verkozen?'),
(4342, 43, 17, 'En wanneer een van hen het verheugende bericht wordt gegeven over wat zij de Erbarmer toeschrijven (een dochter), dan betrekt zijn gezicht en is hij verbitterd.'),
(4343, 43, 18, 'En is hij die temidden van sieraden is grootgebracht en die in het debat geen duidelijk argument kain aanvoeren (een kind van Allah)?'),
(4344, 43, 19, 'En zij maken de Engelen, degenen die dienaren van de Erbarmer zijn, tot vrouwen. Getuigden zij van hun schepping? Hun getuigenissen zullen worden opgetekend en zij zullen worden ondervraagd.'),
(4345, 43, 20, 'En zij zeiden: "Als de Erbarmer het had gewild, dan zouden wij hen niet hebben aanbeden. Zij hebben daarover geen kennis, zij liegen slechts.'),
(4346, 43, 21, 'Of hebben Wij hun vóór hem (de Koran) een Boek gegeven, dat zij daarna vasthielden?'),
(4347, 43, 22, 'Zij zeiden zelfs: "Voorwaar, Wij troffen onze voorvaderen aan in een godsdienst. En voorwaar, wij volgen hun geloof."'),
(4348, 43, 23, 'En zo zonden Wij vóór jou geen waarschuwer naar een stad, of de bewoners ervan, die in weelde leefden, zeiden: "Voorwaar, wij troffen onze voorvaderen aan in een godsdienst. En voorwaar, Wij volgen hun godsdienst."'),
(4349, 43, 24, 'Hij (de Boodschapper) zei: "Ook als ik jullie een rechtere Leiding heb gebracht do wat jullie bij jullie voorvaderen hebben aangetroffen?" Zij zeiden: "Voorwaar, wij verwerpen dat waar jullie mee gezondest zijn."'),
(4350, 43, 25, 'Toen vergolden Wij hun, en zie wat het einde was van de leugenaars.'),
(4351, 43, 26, 'En (gedenkt) toen Ibrâhîm tot zijn vader en zijn volk zei: "Voorwaar, Ik ben niet verantwoordelijk voor wat jullie aanbidden.'),
(4352, 43, 27, 'Behalve (voor mijn aanbidding van) Degene Die mij heeft geschapen: voorwaar, Hij zal mij leiden."'),
(4353, 43, 28, 'En hij maakte het (getuigen van de eenheid van Allah) tot een blijvend woord onder zijn nakomelingen. Hopelijk zullen zij terugkeren.'),
(4354, 43, 29, 'Ik gaf zelfs genietingen aan hen en aan hun vaderen, totdat de Waarheid en een verduidelijkende Boodschapper tot hen kwamen.'),
(4355, 43, 30, 'Maar toen de Waarheid tot hen kwam, zeiden zei: "Dit is tovenarij: en voorwaar, wij geloven er niet in."'),
(4356, 43, 31, 'En zij zeiden: "Was deze Koran maar neergezonden aan en vooraanstaande man in (ieder van) de twee steden!"'),
(4357, 43, 32, 'Zijn zij het, die de Barmhartigheid van jouw Heer uitdelen? Wij zijn het, die hun levensonderhoud uitdelen in het wereldse leven. En Wij verhieven sommigen van het in rang boven sommige anderen, opdat de ene groep de andere groep tot een bron van bespotting zal maken. En de Barmhartigheid van jouw Heer is beter dan wat zij verzamelen.'),
(4358, 43, 33, 'En als de mensheid dan niet tot één (ongelovige) godsdienst zou worden, dan hadden Wij voor degenen die niet in de Erbarmer geloven, de daken van hun huizen van zilver gemaakt, en ook de trappen waarlangs zij omhoog gaan.'),
(4359, 43, 34, 'En voor hun huizen (zilveren) deuren en rustbanken waarop zij zich neervleien.'),
(4360, 43, 35, 'En (gouden) versieringen. En dat alles is slechts een vergankelijke genieting van het aardse leven, mar het Hiernamaals is bij jouw Heer voor de Moettaqôen.'),
(4361, 43, 36, 'Ein wie nalating is in het gedenken van de Erbarmer; voor hem zullen Wij een Satan aanstellen en die zal een metgezel voor hem zijn.'),
(4362, 43, 37, 'En voorwaar, zij zullen hen zeker afhouden van de Weg en zij denken dat zij rechtgeleid zijn.'),
(4363, 43, 38, 'Totdat, wanneer hij tot Ons komt, hij (tot zijn metgezel) zegt: "O wee, was de afstand tussen mij en jou maar als die tussen de zonsopgang en de zonsondergang, (jij bent) de slechtste metgezel."'),
(4364, 43, 39, 'Maar het zal jullie op die Dag niet baten, wanneer jullie onrecht pleegden, dat jullie in de bestraffing bijelkaar zijn.'),
(4365, 43, 40, 'Kan jij dan de dove doen horen, of de blinde (van hart) leiden, of hij die in duidelijke dwaling verkeert?'),
(4366, 43, 41, 'Als Wij jou dan wegnemen (vóórdat Wij hen bestraffen), dan zullen Wij hen zeker bestraffen.'),
(4367, 43, 42, 'Of Wij laten jou zien, wat Wij hun hebben aangezegd: voorwaar, Wij zijn Machthebbers over hen.'),
(4368, 43, 43, 'Houd je daarom vast aan wat aan jou is geopenbaard. Voorwaar, jij bevindt je op het rechte Pad.'),
(4369, 43, 44, 'En voorwaar, hij (de Koran) is een eer voor jou en jouw volk. En zij zullen worden ondervraagd.'),
(4370, 43, 45, 'En vraag aan wie Wij vóór jou hebben gezonden van Onze Boodschappers: "Hebben Wij naast de Erbarmer goden gemada om te aanbidden?"'),
(4371, 43, 46, 'En voorzeker, Wij hebben Môesa met Onze Tekenen naar Fir\'aun en zijn vooraanstaanden gezonden, en hij zei toen: "Voorwaar, ik ben een Boodschapper van de Heer der Werelden."'),
(4372, 43, 47, 'Toen hij tot hen met Om Tekenen kwam, toen lachten zij erom.'),
(4373, 43, 48, 'En Wij lieten hun geen Teken zien, of het ene was nog erger dan het andere. En Wij grepen ben met de bestraffing. Hopelijk zullen zij terugkeren.'),
(4374, 43, 49, 'En zij zeiden: "O tovenaar, roep voor ons jouw Heer aan op grond van wat Hij jou beloofd heeft. Voorwaar, wij zullen zeker rechtgeleidenen worden."'),
(4375, 43, 50, 'Toen Wij dan de bestraffing van hen hadden weggenomen, braken zij hun belofte.'),
(4376, 43, 51, 'En Fir\'aun riep tot zijn volk, hij zei: "O mijn volk, is het koninkrijk van Mishr (Egypte) niet van mij? En deze rivieren, stromen die niet onder mij? Zien jullie dat niet in?'),
(4377, 43, 52, 'Ik ben zelfs beter dan die veractelijke en hij kan bijna niet duidelijk spreken.'),
(4378, 43, 53, 'Waren er maar gouden sieraden om hem gehangen of Engelen met hem meegekomen die hem vergezelden."'),
(4379, 43, 54, 'Hij (Fir\'aun) misleidde zijn volk, zodat zij hem volgden. Voorwaar, zij waren een zwaar zondig volk.'),
(4380, 43, 55, 'Toen zij Onze woede hadden opgewekt bestraften Wij hen en verdronken Wij hen allen.'),
(4381, 43, 56, 'Toen maakten Wij hen tot een voorbeeld en een lering voor de lateren.'),
(4382, 43, 57, 'En toen de zoon van Maryam als een voorbeeld werd genomen, schreeuwde jouw volk (O Moehammad) daarover.'),
(4383, 43, 58, 'En zij zeiden: "Zijn onze goden beter, of hij (\'Îsa)?" En zij gaven dit voorbeeld slechts om te redetwisten. Zij zijn zelfs een volk van redetwisters.'),
(4384, 43, 59, 'Hij is slechts een dienaar aan wie Wij genietingen hebben geschonken. En Wij maakten hem tot een voorbeeld voor de Kinderen van Israël.'),
(4385, 43, 60, 'En als Wij zouden willen, dan zouden Wij zeker op aarde Engelen maken die jullie opvolgen.'),
(4386, 43, 61, 'En voorwaar, het (terugkeren van \'Îsa op de aarde) is een Teken (van het naderen) van het Uur. Twijfelt er daarom niet aan en volgt Mij. Dit is het rechte Pad.'),
(4387, 43, 62, 'En laat de Satan jullie niet affhouden: voorwaar, hij is voor jullie een duidelijke vijand.'),
(4388, 43, 63, 'En toen \'Îsa met de duidelijk bewijzen kwam, zei hij: "Ik heb jullie waarlijk de wijsheid gebracht zodat ik jullie een aantal zaken, waarover jullie van mening verschillen, verduidelijk. Vreest daarom Allah en gehoorzaamt mij.'),
(4389, 43, 64, 'Voorwaar, Allah is mijn Heer en jullie Heer, aanbidt Hem daarom. Dit is het rechte Pad."'),
(4390, 43, 65, 'De groeperingen werden het toen met elkaar oneens. Wee degenen die niet geloven in de bestraffing van een pijnlijke Dag.'),
(4391, 43, 66, 'Zij wachten slechts op het Uur, dat plotseling tot hen zal komen, terwijl zij niet beseffen.'),
(4392, 43, 67, 'De boezemvrienden zullen op die Dag elkaars vijanden zijn, behalve de Moettaqôen.'),
(4393, 43, 68, 'O Mijn dienaren, op die Dag zal er geen vrees voor jullie zijn en jullie zullen niet treuren.'),
(4394, 43, 69, '(Zij zijn) degenen die in Onze Tekenen geloofden en die zich (aan Ons) overgaven.'),
(4395, 43, 70, '(Er wordt gezegd:) "Treedt het Pandijs binnen, jullie en jullie echtgenotes, in blijdschap."'),
(4396, 43, 71, 'Er zal tussen hen worden rondgegaan met schalen van goud en bekers. Daarin is wat de zielen verlangen, en de verrukking van de ogen. En jullie zullen er eeuwig levenden zijn.'),
(4397, 43, 72, 'En dat is het Paradijs dat jullie hebben geërfd wegens wat jullie hebben verricht'),
(4398, 43, 73, 'Daarin zijn voor jullie vele vruchten waarvan jullie eten.'),
(4399, 43, 74, 'Voorwaar, de misdadigers zullen eeuwig in de bestraffing van de Hel verkeren.'),
(4400, 43, 75, 'De bestraffing zal voor hen niet verlkht worden, terwijl zij daarin wanhopigen zijn.'),
(4401, 43, 76, 'Wij hebben hun geen onrecht aangedaan, maar zij hebben zichzelf onrecht aangedaan.'),
(4402, 43, 77, 'Zij roepen: "O Mâlik (bewaker van de Hel), laat jouw Heer een eind aan ons maken." Hij zegt: "Voorwaar, jullie blijven hier."'),
(4403, 43, 78, 'Voorzeker, Wij hebben jullie de Waarheid gebracht, maar de meesten van jullie haten de Waarheid.'),
(4404, 43, 79, 'Zij hebben zelfs een plan beraamd, daarom hebben Wij een plan beraamd.'),
(4405, 43, 80, 'Of denken zij, dat Wij hun geheimen en hun gefluister niet horen? Welzeker! Onze gezanten (Engelen) zijn bij hen, zij schrijven.'),
(4406, 43, 81, 'Zeg (O Moehammad): "Als de Erbarmer een zoon zou hebben, dan zou ik de eerste zijn om hem te dienen."'),
(4407, 43, 82, 'Heilig is de Heer van de hemelen en de aarde, de Heer van de Troon, boven wat zij (Hem) toeschrijven.'),
(4408, 43, 83, 'Laat hen in hun dwaling blijven en spelen totdat zij hun Dag ontmoeten die hun is aangezegd.'),
(4409, 43, 84, 'En Hij is Degene Die de God in de hemel is en de God op de aarde is. En Hij is de Alwijze, de Alwetende.'),
(4410, 43, 85, 'En gezegend is Degene aan Wie de heerschappij van de hemelen en de aarde behoort en wat er tussen is. En bij Hem is de kennis over het Uur en tot Hem zullen jullie terugkeerd worden.'),
(4411, 43, 86, 'En degenen die zij naast Hem aanroepen beschikken niet over de mogelijkheid tot voorspraak, behalve wie van de Waarheid getuigen terwijl zij kennis hebben.'),
(4412, 43, 87, 'En als jij hun vraagt wie hen geschapen heeft, dan zullen zij zeker zeggen: "Allah." Waarom worden zij dan belogen?'),
(4413, 43, 88, 'En (Allah kent) zijn (Moehammad\'s) woorden: "O mijn Heer, voorwaar, zij zijn een volk dat niet gelooft."'),
(4414, 43, 89, 'Verdraag hen maar, en zeg: "Vaarwel!" Zij zullen het weten.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'Ha Mîm.'),
(4416, 44, 2, 'Bij het duidelijke Boek. (de Koran)'),
(4417, 44, 3, 'Voorwaar, Wij hebben hem (de Koran) in de gezegende nacht neergezonden. Voorwaar, Wij zijn Waarschuwers.'),
(4418, 44, 4, 'Daarin worden alle wijze zaken uiteengezet.'),
(4419, 44, 5, 'Als een bevel van Ons: voorwaar, Wij zonden (de Profeten).'),
(4420, 44, 6, 'Als Barmhartigheid van jouw Heer. Voorwaar, Hij is de Alhorende, de Alwetende.'),
(4421, 44, 7, 'De Heer van de hemelen en de aarde en wat er tussen is, als jullie overtuigden zijn.'),
(4422, 44, 8, 'Er is geen god dan Hij, Hij doet leven en sterven, jullie Heer en de Heer van jullie voorvaderen.'),
(4423, 44, 9, 'Zij blijven zelfs in twijfel doorspelen.'),
(4424, 44, 10, 'Wacht dan op de Dag waarop in de hemel duidelijke rook verschijnt.'),
(4425, 44, 11, 'Die de mensen omhult. Dit is een pijnlijke bestraffing.'),
(4426, 44, 12, '(Zij zullen zeggen:) "Onze Heer, neem de bestraffing, van ons weg: voorwaar, wij zijn gelovigen."'),
(4427, 44, 13, 'Hoe zullen zij zich laten vermanen, terwijl er reeds een duidelijke Boodschapper tot hen is gekomen?'),
(4428, 44, 14, 'Toen wendden zij zich van hem af, en zeiden: "Hij is een bezeten onderwezene."'),
(4429, 44, 15, 'Voorwaar, Wij nemen iets van de bestraffing weg: voorwaar, jullie keren terug (tot ongeloof).'),
(4430, 44, 16, '(Gedenkt) de Dag dat Wij hen zullen grijpen met de zware bestraffing: voorwaar, Wij zijn Vergelders.'),
(4431, 44, 17, 'En voorzeker, Wij hebben voorheen het volk van Fir\'aun op de proef gesteld. En er was een edele Boodschapper (Môesa) tot hen gekomen.'),
(4432, 44, 18, '(Hij zei:) "Laat de dienaren van Allah (vrij) tot mij komen: voorwaar, ik ben voor jullie een betrouwbare Boodschapper.'),
(4433, 44, 19, 'En weest niet hoogmoedig tegenover Allah: voorwaar, ik kom tot jullie met een duidelijk bewijs.'),
(4434, 44, 20, 'En voorwaar, ik zocht mijn toevlucht bij mijn Heer en jullie Heer tegen jullie stenigen.'),
(4435, 44, 21, 'En als jullie mij niet geloven, laat mij dan met rust."'),
(4436, 44, 22, 'En hij bad toen tot zijn Heer: "Voorwaar, zij zijn een misdadig volk."'),
(4437, 44, 23, '(Allah zei toen:) "Ga in de nacht op weg met Mijn dienaren: voorwaar, jullie zullen worden achtervolgd.'),
(4438, 44, 24, 'En laat de zee zoals zij is (door een pad gespleten): voorwaar, zij zullen een verdronken leger worden."'),
(4439, 44, 25, 'Hoeveel tuinen en bronnen lieten zij niet achter.'),
(4440, 44, 26, 'En velden en prachtige plaatsen.'),
(4441, 44, 27, 'En genietingen die zij daarin kunnen smaken.'),
(4442, 44, 28, 'Zo is het. En Wij hebben het een ander volk doen erven.'),
(4443, 44, 29, 'De hemel en de aarde huilden niet om hen, en hun werd geen uitstel gegeven.'),
(4444, 44, 30, 'En voorzeker, Wij hebben de Kinderen van Israël van de vernederende bestraffing gered.'),
(4445, 44, 31, 'Van Fir\'aun: voorwaar, hij was een hoogmoedige onder de buitensporigen.'),
(4446, 44, 32, 'En voorzeker, Wij hebben hen (de Kinderen van Israël) op grond van (Onze) kennis verkozen boven de anderen (in hun tijd).'),
(4447, 44, 33, 'En Wij hebben hun van de Tekenen gegeven waarin een duidelijke beproeving was.'),
(4448, 44, 34, 'Voorwaar, zij (de ongelovigen) zullen zeker zeggen:'),
(4449, 44, 35, '"Het is slechts de enige dood van ons, en wij zullen niet opgewekt worden.'),
(4450, 44, 36, 'Brengt dan onze voorvaderen (terug), als jullie waarachtig zijn."'),
(4451, 44, 37, 'Zijn zij beter of het volk van Toebba\' en degenen vََr hen? Wij hebben hen vernietigd: voorwaar, zij waren misdadigers.'),
(4452, 44, 38, 'En Wij hebben de hemelen en de aarde en wat er tussen hen is niet als een spel geschapen.'),
(4453, 44, 39, 'Wij hebben beide niet anders dan in Waarheid geschapen, maar de meesten van hen weten het niet.'),
(4454, 44, 40, 'Voorwaar, de Dag van de Beslissing is hun vastgestelde tijd, tezamen.'),
(4455, 44, 41, 'De Dag waarop de ene vriend de andere vriend in niets kan bijstaan, en zij zullen niet geholpen worden.'),
(4456, 44, 42, 'Behalve degene die door Allah begenadigd wordt: voorwaar, Hij is de Almachtige, de Meest Barmhartige.'),
(4457, 44, 43, 'Voorwaar, de Zaqqôem-boom.'),
(4458, 44, 44, 'Voedsel van de zondaar.'),
(4459, 44, 45, 'Als gesmolten metaal dat in de buiken kookt.'),
(4460, 44, 46, 'Als kokend heet water.'),
(4461, 44, 47, 'Grijpt hem en sleept hem naar het midden van de Hel.'),
(4462, 44, 48, 'Daarna wordt een bestraffing van de Hel over zijn hoofd uitgegoten.'),
(4463, 44, 49, 'Proeft (deze bestraffing), voorwaar, jij bent de geweldige, de nobele. (Wordt er spottend tot de misdadiger gezegd.)'),
(4464, 44, 50, 'Voorwaar, dit is dat waarover jullie in twijfel plachten te verkeren.'),
(4465, 44, 51, 'Voorwaar, de Moettaqôen verblijven in een veilige plaats.'),
(4466, 44, 52, 'In tuinen en bij bronnen (het Paradijs).'),
(4467, 44, 53, 'Zij dragen kleding van fijne zijde en brokaat, (zij zijn) tegenover elkaar gezeten.'),
(4468, 44, 54, 'Zo is het. En Wij zullen hen huwen met schone maagden.'),
(4469, 44, 55, 'Daarin vragen zij in veiligheid om allerlei vruchten.'),
(4470, 44, 56, 'Zij zullen daarin, na de eerste dood, geen dood meer ondergaan, en Hij beschermt hen voor de bestraffing van de Hel.'),
(4471, 44, 57, 'Als een gunst van jouw Heer. Dat is de geweldige overwinning.'),
(4472, 44, 58, 'Voorwaar, Wij hebben hem (de Koran) gemakkelijk gemaakt in jouw taal. Hopelijk zullen zij er lering uit trekken.'),
(4473, 44, 59, 'Wacht daarom: voorwaar, zij wachten (op hun bestraffing).');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'Ha Mîm.'),
(4475, 45, 2, '(Dit is) de neerzending van het Boek van Allah, de Almachtige, de Alwijze.'),
(4476, 45, 3, 'Voorwaar, in de hemelen en de aarde zijn zeker Tekenen voor de gelovigen.'),
(4477, 45, 4, 'En in de schepping van jullie en wat er aan levende wezens (op de aarde) rondloopt, zijn Tekenen voor een volk dat overtuigd is.'),
(4478, 45, 5, 'En (ook) in de afwisseling van de nacht en de dag, en in wat Allah heeft neergezonden aan voorzieningen (regen) waarmee Hij vervolgens de aarde doet leven na haar dood, en in de verandering van de winden zijn Tekenen voor een volk dat begrijpt.'),
(4479, 45, 6, 'Dat zijn de Verzen van Allah die Wij aan jou in Waarheid voordragen. In welk woord na (het Woord van) Allah en Zijn Verzen geloven zij dan?'),
(4480, 45, 7, 'Wee iedere lasteraar, zondaar.'),
(4481, 45, 8, 'Hij hoort de Verzen van Allah die aan hem worden voorgedragen en vervolgens blijft hij hoogmoedig, alsof hij ze niet gehoord heeft. Verkondig hem daarom een pijnlijke bestraffing.'),
(4482, 45, 9, 'En als hij iets van Onze Verzen vernomen heeft, dan drijft hij er de spot mee. Zij zijn degenen voor wie er een vernederende bestraffing is.'),
(4483, 45, 10, 'Voor hen is de Hel, en wat zij verricht hebben zal hun niets baten, noch dat wat zij naast Allah als beschermers genomen hebben. En voor hen is er een geweldige bestraffing.'),
(4484, 45, 11, 'Deze (Koran) is de Leiding, en degenen die niet geloven in de Verzen van hun Heer: voor hen is een bestraffing door een pijnlijke kwelling.'),
(4485, 45, 12, 'Allah is Degene Die de zee voor jullie dienstbaar heeft gemaakt, opdat de schepen met Zijn toestemming erop kunnen varen. En opdat jullie van Zijn gunst zoeken. Hopelijk zullen jullie dankbaar zijn.'),
(4486, 45, 13, 'En Hij heeft voor jullie alles wat zich van Hem in de hemelen en de aarde bevindt dienstbaar gemaakt. Voorwaar, daarin zijn zeker Tekenen voor een volk dat nadenkt.'),
(4487, 45, 14, 'Zeg tot degenen die geloven dat zij degenen vergeven die niet op de Dagen van Allah hopen. Zodat Hij een volk zal vergelden voor wat het heeft verricht.'),
(4488, 45, 15, 'Wie een goede daad heeft verricht, het is voor hem; en wie een slechte daad heeft verricht, hij is er verantwoordelijk voor. Daarna zullen jullie tot jullie Heer worden teruggekeerd.'),
(4489, 45, 16, 'En voorzeker, Wij hebben de Kinderen van Israël de Schrift (de Taurât) en de heerschappij en het profeetschap gegeven. En Wij hebben hun van het goede voorzien en Wij hebben hen boven de volkeren (die toen leefden) bevoorrecht.'),
(4490, 45, 17, 'En Wij hebben hun duidelijke aanwijzingen gegeven over de zaak (de godsdienst). Zij raakten niet eerder verdeeld dan nadat de kennis tot hen gekomen was, elkaar onrecht aandoend. Voorwaar, jouw Heer zal tussen hen beslissen op de Dag der Opstanding, over dat waar zij van mening over verschilden.'),
(4491, 45, 18, 'Vervolgens plaatsten Wij jou (O Moehammad) op een juiste weg (Sjarî\'ah) van de godsdienst. Volg deze dan en volg niet de begeerten van degenen die niet weten.'),
(4492, 45, 19, 'Voorwaar, zij zullen jou beslist in niets kunnen beschermen tegen (de bestraffing van) Allah. En voorwaar, de onrechtvaardigen zijn elkaars helpers. En Allah is de Beschermer van de Moettaqôen.'),
(4493, 45, 20, 'Deze (Koran) is een duidelijke aanwijzing voor de mensheid en Leiding en Barmhartigheid voor een volk dat overtuigd is.'),
(4494, 45, 21, 'Dachten degenen die slechte daden verricht hebben, dat Wij hen hetzelfde zullen behandelen als degenen die geloofden en goede daden verricht hebben, zowel in hun leven als in hun sterven? Slecht is dat wat zij vermoeden!'),
(4495, 45, 22, 'En Allah heeft de hemelen en de aarde in Waarheid geschapen. Zodat elke ziel wordt vergolden voor wat zij heeft verricht. En zij worden niet onrechtvaardig behandeld.'),
(4496, 45, 23, 'Heb jij degene gezien die zijn begeerten tot god heeft genomen, en die door Allah tot dwaling wordt gebracht op grond van kennis, en wiens oren en wiens hart Hij verzegeld heeft, en over wiens ogen Hij een bedekking heeft aangebracht? Wie kan hem nog leiding geven nadat Allah (hem heeft doen dwalen)? Laten jullie je dan niet vermanen?'),
(4497, 45, 24, 'En zij zeiden: "Er is niets dan ons wereldse leven, wij sterven en wij leven, en niets vernietigt ons de tijd." Maar zij hebben daarover geen kennis, zij vermoeden slechts.'),
(4498, 45, 25, 'En wanneer Onze duidelijke Verzen aan hem worden voorgedragen, dan hebben zij geen verweer dan dat zij zeggen: "Brengt onze voorouders (terug), als jullie waarachtigen zijn."'),
(4499, 45, 26, 'Zeg: "Allah brengt jullie tot leven, daarna doet Hij jullie sterven en daarna verzamelt Hij jullie op de Dag der Opstanding, waaraan geen twijfel is." Maar de meeste mensen weten het niet.'),
(4500, 45, 27, 'En aan Allah behoort de heerschappij van de hemelen en de aarde, en de Dag waarop het Uur valt. Die Dag verliezen de vervalsers.'),
(4501, 45, 28, 'En jij zult alle volken zien neerknielen, ieder volk zal tot hun boek geroepen worden. Op die Dag zullen jullie vergolden worden voor wat jullie plachten te doen.'),
(4502, 45, 29, 'Dit is Ons Boek, dat in Waarheid tot jullie spreekt. Voorwaar, Wij schreven op wat jullie plegen te doen.'),
(4503, 45, 30, 'Wat betreft degenen die geloven en goede werken verrichten: hun Heer zal hen doen binnengaan in Zijn Barmhartigheid. Dat is de duidelijke overwinning.'),
(4504, 45, 31, 'En wat degenen die niet geloofden betreft: werden Mijn Verzen niet aan jullie voorgedragen waarop jullie hoogmoedig werden? En jullie waren een volk van misdadigers.'),
(4505, 45, 32, 'En toen er gezegd werd: "De belofte van Allah is waar en aan het Uur is geen twijfel," zeiden jullie: "Wij wisten niet wat het Uur was, wij koesterden slechts een vermoeden en wij waren daarvan niet overtuigd."'),
(4506, 45, 33, 'En het kwade van wat zij verricht hadden, werd hen duidelijk en zij werden omsingeld door dat waar zij de spot mee plachten te drijven.'),
(4507, 45, 34, 'Op die Dag wordt gezegd: "Wij vergeten jullie zoals jullie de ontmoeting met deze Dag van jullie hebben vergeten, en jullie verblijfplaats is de Hel en voor jullie zijn er geen helpers.'),
(4508, 45, 35, 'Dit is omdat jullie de Verzen van Allah tot onderwerp van spot maakten en het wereldse leven jullie verleid heeft." Op die Dag zullen zij er niet worden uitgehaald, en zij zullen niet naar hun berouw gevraagd worden.'),
(4509, 45, 36, 'Alle lof zij Allah, de Heer van de hemelen en de Heer van de aarde, de Heer der Werelden.'),
(4510, 45, 37, 'En Hem behoort alle grootheid in de hemelen en op de aarde, en Hij is de Almachtige, de Alwijze.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'Ha Mîm.'),
(4512, 46, 2, 'De neerzending van het Boek (de Koran) is van Allah, de Almachtige, de Alwijze.'),
(4513, 46, 3, 'Wij hebben de hemelen en de aarde en wat er tussen hen is slechts in Waarheid geschapen en voor een vastgestelde tijd. En degenen die niet geloofden, zij wendden zich af van dat waar zij voor gewaarschuwd werden.'),
(4514, 46, 4, 'Zeg (O Moehammad): "Wisten jullie wat jullie naast Allah aanriepen? Toont mij wat zij geschapen hebben van de aarde, of hebben zij soms een aandeel in de schepping van de aarde? Brengt mij een boek van vóór deze (Koran), of een overblijfsel van kennis, als jullie waarachtigen zijn."'),
(4515, 46, 5, 'En wie is verder afgedwaald dan hij die naast Allah afgoden aanbidt die hem, tot en met de Dag van de Opstanding, niet kunnen verhoren? En zij zijn achteloos met hun aanbidding.'),
(4516, 46, 6, 'En wanneer de mensheid bijeengebracht wordt, zullen zij voor hen vijanden worden en zullen zij hun aanbidding verwerpen.'),
(4517, 46, 7, 'En wanneer Onze duidelijke Verzen aan hen worden voorgedragen, dan zeggen degenen die ongelovig zijn over de Waarheid (de Koran) wanneer die tot hen komt: "Dit is duidelijke tovenarij."'),
(4518, 46, 8, 'Of zeggen zij dat hij hem (de Koran) heeft verzonnen? Zeg: "Als ik hem had verzonnen, dan zouden jullie geen enkele macht hebben om mij tegen Allah te beschermen. Hij weet het best waarover jullie zo uitvoerig praten. Hij is voldoende als Getuige tussen mij en jullie, en Hij is de Vergevensgezinde, de Meest Barmhartige."'),
(4519, 46, 9, 'Zeg: "Ik ben niet iemand die (iets afwijkends) toevoegt onder de Boodschappers. En ik weet niet wat mij en jullie zal worden aangedaan. Ik volg slechts wat aan mij geopenbaard is. En ik ben slechts een duidelijke waarschuwer."'),
(4520, 46, 10, 'Zeg: "Wat denken jullie? Als hij (de Koran) van Allah afkomstig is en jullie wijzen hen af, terwijl er een getuige van de Kinderen van Israël over het overeenkomstige ervan is die er in geloofde, en jullie waren hoogmoedig (dan zijn jullie toch onrechtvaardigen?)" Voorwaar, Allah leidt het onrechtvaardige volk niet.'),
(4521, 46, 11, 'En degenen die niet geloofden, zeiden tegen degenen die geloofden: "Als hij (de Koran) iets goeds was, dan zouden zij ons niet voorgaan daarin (te geloven)." En omdat zij zich er niet door laten leiden, zullen zij zeggen: "Dit is een oude leugen."'),
(4522, 46, 12, 'En ervóór was er het Boek van Môesa (de Taurât) als Gids en Barmhartigheid. En dit Boek bevestigt (het) in de Arabische taal, om degenen die onrechtvaardig zijn te waarschuwen en als een verheugende tijding voor de weldoeners.'),
(4523, 46, 13, 'Voorwaar, degenen die zeggen: "Onze Heer is Allah," en die vervolgens standvastig zijn: er zal geen vrees over hen komen en zij zullen niet treuren.'),
(4524, 46, 14, 'Zij zijn de bewoners van het Paradijs, daarin zijn zij eeuwig levenden, als een beloning voor wat zij plachten te doen.'),
(4525, 46, 15, 'En Wij hebben de mens goedheid tegenover zijn ouders bevolen. Zijn moeder heeft hem met moeite gedragen en hem met moeite gebaard. Zijn dragen en (de tijd tot) zijn spenen duurt dertig maanden, zodat wanneer hij de volwassenheid bereikt, en hij de leeftijd van veertig jaar bereikt, hij zegt: "Mijn Heer, onderwijs mij opdat ik dankbaar ben voor uw genietingen die U geschonken heeft aan mij en aan mijn ouders, en opdat ik goede werken verricht die U behagen, en maak mijn nakomelingen rechtschapen. Voorwaar, ik toon U mijn berouw; en voorwaar, ik behoor tot de Moslims."'),
(4526, 46, 16, 'Zij zijn degenen van wie Wij het beste aanvaarden van wat zij verrichtten, en Wij wissen hun slechte daden uit, (zij zijn) tezamen met de bewoners van het Paradijs, als een ware belofte die hun is gedaan.'),
(4527, 46, 17, 'En (slecht is) degene die tegen zijn ouders zegt: "Foei jullie! Waarschuwen jullie mij dat ik opgewekt zal worden, terwijl de generaties voor mij zijn heengegaan (en er nog niet een is opgestaan uit zijn graf)." En zij (de ouders) vragen Allah om hulp (en zeggen tegen hun kind): "Wee jij, geloof! Voorwaar, de belofte van Allah is waar." Waarop hij zegt: "Dit zijn niets anders dan de fabelen van de vroegeren."'),
(4528, 46, 18, 'Zij zijn degenen over wie het woord (van bestraffing) bewaarheid zal worden onder de volken die reeds zijn heengegaan, van de Djinn\'s en de mensen. Voorwaar, zij waren verliezers.'),
(4529, 46, 19, 'En voor allen zijn er rangen wegens wat zij deden en opdat Hij hun werken volledig zal vergelden. Zij zullen geen onrecht aangedaan worden.'),
(4530, 46, 20, 'En op de Dag dat degenen die niet geloofden de Hel getoond zal worden, (wordt gezegd): "Jullie hebben jullie goede dingen die jullie bezaten opgebruikt tijdens het wereldse leven en jullie hebben ervan genoten. Op deze Dag zullen jullie vergolden worden met een vernederende straf omdat jullie hooghartig plachten te leven op de aarde, zonder recht, en omdat jullie zware zonden plachten te begaan."'),
(4531, 46, 21, 'En gedenkt de broeder van de \'Âd (de Profeet Hôed) toen hij zijn volk waarschuwde bij Al Ahqâf. En waarlijk, de waarschuwers vóór hen en na hen zijn heengegaan, (zij zeiden): "Aanbidt geen ander dan Allah, voorwaar, ik vrees voor jullie de bestraffing van een geweldige Dag."'),
(4532, 46, 22, 'Zij zeiden: "Ben jij tot ons gekomen om ons van (het aanbidden) van onze goden af te houden? Geef wat jij ons aanzegt, als jij tot de waarachtigen behoort!"'),
(4533, 46, 23, 'Hij zei: "Voorwaar, de kennis is bij Allah en ik breng slechts dat over waarmee ik gezonden ben, maar ik zie dat jullie een volk zijn dat onwetend is."'),
(4534, 46, 24, 'En toen zij die ( bestraffing) als een wolk zagen die zich naar hun valleien verplaatste, zeiden zij: "Dit is een wolk die regen op ons zal doen vallen." Nee! Integendeel, het is juist (de bestraffing) waarvan jullie de bespoediging vroegen, een wind met daarin een pijnlijke bestraffing.'),
(4535, 46, 25, 'Die alle dingen zal verwoesten op bevel van zijn Heer, toen was er niets meer van hen zichtbaar dan de ruïnes van hun woningen. Zo vergelden Wij het volk van misdadigers.'),
(4536, 46, 26, 'En voorzeker, Wij hadden hun werkelijk macht en welvaart gegeven die Wij niet aan jullie gegeven hebben. En Wij gaven hun de vermogens van het horen, en het zien en de harten. Maar hun oren, en hun ogen en hun harten baatten hun niets omdat zij de Tekenen van Allah plachten te ontkennen. En zij zijn omsingeld door de bestraffing waarmee zij de spot plachten te drijven.'),
(4537, 46, 27, 'En voorzeker, Wij hebben de steden en jullie omgeving verwoest. En Wij herhaalden de Tekenen (van Onze macht). Hopelijk zullen zij terugkeren.'),
(4538, 46, 28, 'Hadden de afgoden die zij naast Allah namen als een toenadering tot een god, hen maar geholpen! Nee, zij zijn van hen verdwenen. En dat is hun leugen en wat zij plachten te verzinnen.'),
(4539, 46, 29, 'En (gedenkt) toen Wij een paar van de Djinn\'s bij jou brachten, om naar de Koran te luisteren. Toen zij daarbij aanwezig waren, zeiden zij: "Zwijgt!" En toen (de voordracht) beëindigd was, keerden zij tot hun volk terug als waarschuwers.'),
(4540, 46, 30, 'Zij zeiden: "O volk van ons, voorwaar wij hebben over een Boek gehoord dat is neergezonden na Môesa, ter bevestiging van wat daarvóór was, het leidt naar de Waarheid en de rechte Weg.'),
(4541, 46, 31, 'O volk van ons, geeft gehoor aan de oproeper van Allah en gelooft in hem, dan zal Hij jullie zonden vergeven en Hij zal jullie redden van een pijnlijke bestraffing."'),
(4542, 46, 32, 'En wie geen gehoor geeft aan de oproeper van Allah, die zal niet kunnen ontvluchten op de aarde en voor hem is er naast Hem geen beschermer. Zij zijn degenen die in duidelijke dwaling verkeren.'),
(4543, 46, 33, 'Zien zij niet dat Allah, Degene Die de hemelen en de aarde geschapen heeft en Die nooit moe wordt van het scheppen van hen, bij machte is om het dode tot leven te brengen? Welzeker, Hij is waarlijk Almachtig over alle zaken.'),
(4544, 46, 34, 'En (gedenkt) de Dag dat degenen die niet geloofden voor de Hel geplaatst zullen worden (en tot hen gezegd wordt): "Is dit niet de Waarheid?" Zij zeggen: "Welzeker", bij onze Heer." Hij (Allah) zegt: "Proeft dan de bestraffing wegens jullie ongelovig zijn."'),
(4545, 46, 35, 'Wees daarom geduldig, zoals de bezitters van standvastigheid (Oeloel\'azmi) onder de Boodschappers geduldig waren. En vraag niet om (de bestraffing) voor hen te bespoedigen. Het zal op de Dag, waarop zij zien wat hun aangezegd was, voor hen zijn alsof zij slechts een uur van de dag (op aarde) verbleven. Dit is een vermaning. Er wordt dan (niemand) vernietigd dan het zwaar zondige volk.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'Degenen die ongelovig zijn en afhouden van de Weg van Allah: Hij zal hun werken verloren doen gaan.'),
(4547, 47, 2, 'Maar degenen die geloven en goede werken verrichten en die geloven in wat aan Moehammad is neergezonden - en het is de Waarheid van hun Heer: Hij zal hun zonden uitwissen en hun toestand verbeteren.'),
(4548, 47, 3, 'Dat is omdat degenen die ongelovig zijn de valsheid volgen, terwijl degenen die gelovig zijn de Waarheid van hun Heer volgen. Zo maakt Allah voor de mensen vergelijkingen.'),
(4549, 47, 4, 'Wanneer jullie degenen die ongelovig zijn (op het slagveld) ontmoeten: doodt hen. Wanneer jullie hen verslagen hebben: bindt (de gevangenen) dan stevig vast, of laat (hen) vrij of vrij (na) een losgeld, tot de oorlog voorbij is. Zo is het. En als Allah het wil, dan vernietigt Hij hen zeker. Maar Hij wil een groep van jullie beproeven met een andere groep. En degenen die gedood worden op de Weg van Allah: Hij doet hun werk nooit verloren gaan.'),
(4550, 47, 5, 'Hij leidt hen en verbetert hun toestand.'),
(4551, 47, 6, 'En Hij doet hen het Paradijs binnengaan dat Hij aan hen bekend heeft gemaakt.'),
(4552, 47, 7, 'O jullie die geloven, als jullie (de godsdienst van) Allah helpen, dan zal Hij jullie helpen en jullie voeten stevig (op het slagveld) plaatsen.'),
(4553, 47, 8, 'En degenen die niet geloofden, vernietiging is er voor hen! En Hij doet hun werken verloren gaan.'),
(4554, 47, 9, 'Dat is omdat zij haatten wat Allah heeft neergezonden, dus maakte Hij hun daden vruchteloos.'),
(4555, 47, 10, 'Reizen zij dan niet op de aarde, zodat zij zien hoe het einde was vast degenen vóór hen? Allah vernietigde hen. En de ongelovigen wacht hetzelfde.'),
(4556, 47, 11, 'Dat is omdat Allah de Beschermer van degenen die geloven is, en omdat er voor de ongelovigen geen beschermer is.'),
(4557, 47, 12, 'Voorwaar, Allah zal degenen die geloven en goede werken verrichten doen binnengaan in Tuinen (het Paradijs) waar onderdoor de rivieren stromen, terwijl degenen die ongelovig zijn (van het wereldse leven) genieten en eten zoals het vee eet. De Hel zal hun verblijfplaats zijn.'),
(4558, 47, 13, 'En hoeveel steden zijn er niet sterker dan de stad die jou (O Moehammad) heeft verdreven? Wij hebben hen vernietigd en er was geen helper voor hen.'),
(4559, 47, 14, 'Is hij die zich op een duidelijk bewijs van zijn Heer beroept, gelijk aan degene wiens slechte daden schoonschijnend zijn gemaakt (door de Satan) en die zijn begeerten volgt?'),
(4560, 47, 15, 'Is het (geluk van) het Paradijs dat aan de Moettaqôen beloofd is, waarin rivieren zijn met versblijvend water, en van melk waarvan de smaak niet verandert, en rivieren van Khamr, als een genieting voor hen die drinken, en rivieren van zuivere honing, en waarin voor hen allerlei vruchten zijn en vergeving van hun Heer; gelijk (aan de ellende van) hen die eeuwig levenden in de Hel zijn, in wie kokend water wordt gegoten, dat hun ingewanden dan aan stukken snijdt?'),
(4561, 47, 16, 'En onder hen zijn er die naar jou luisteren; maar wanneer zij bij jou weggaan, zeggen zij tot degenen aan wie de kennis is gegeven (de metgezellen van de Profeet): "Wat zei hij daarnet?" Zij zijn degenen wiens harten Allah heeft verzegeld en die hun begeerten volgen.'),
(4562, 47, 17, 'Maar degenen die de Leiding volgden: Hij vermeerderde hun Leiding en Hij gaf hun hun vrees (voor Hem).'),
(4563, 47, 18, 'Zij (de ongelovigen) wachten slechts op het Uur dat plotseling tot hen zal komen. Voorzeker, de Tekenen ervan zijn al gekomen. Hoe kunnen zij wanneer (het Uur) tot hen komt, dan nog hun vermaning krijgen?'),
(4564, 47, 19, 'Weet dat er geen god is dan Allah en vraag om vergeving voor jouw zonden en voor (de zonden van) de gelovige mannen en de gelovige vrouwen. En Allah kent jullie bezigheden (op aarde) en jullie verblijfplaats (in het Hiernamaals).'),
(4565, 47, 20, 'En degenen die geloven zeggen: "Was er maar een Soerah neergezonden (die het voeren van oorlog beveelt). Maar wanneer dan een onweerlegbare (Moehkamah) Soerah wordt neergezonden, waarin (het bevel tot) oorlog wordt genoemd, dan zie jij degenen in wier harten een ziekte is, naar jou kijken met de blik van degene die in zwijm dreigt te vallen uit doodsangst. Wee hen!'),
(4566, 47, 21, 'Gehoorzaamheid en een juist woord (zouden beter zijn). Wanneer dan de zaak (van het bevel tot oorlog) is besloten, (dan hebben zij er een afkeer van). Maar als zij oprecht zouden zijn tegenover Allah, dan zou dat beter voor hen zijn.'),
(4567, 47, 22, 'Het kan zijn, als jullie je afwenden, dat jullie verderf zaaien op de aarde en jullie familiebanden verbreken.'),
(4568, 47, 23, 'Zij zijn degenen die Allah vervloekt heeft en die Hij doof gemaakt heeft, en Hij heeft hun ogen blind gemaakt.'),
(4569, 47, 24, 'Denken zij niet na over de Koran? Er zijn zelfs sloten op hun harten.'),
(4570, 47, 25, 'Voorwaar, degenen die afvallig werden nadat de Leiding voor hen duidelijk was geworden: de Satan maakte (hun daden) schoonschijnend voor hen en bedroog hen.'),
(4571, 47, 26, 'Dat is omdat zij tot degenen die haatten wat Allah heeft neergezonden (de Joden), zeiden: "Wij zullen jullie gehoorzamen in sommige zaken." Maar Allah kent hun geheimen.'),
(4572, 47, 27, 'Hoe zal het zijn wanneer de Engelen hen wegnemen en hun gezichten en hun ruggen slaan?'),
(4573, 47, 28, 'Dat is omdat zij dat wat de toorn van Allah opwekte volgden en zij dat wat Zijn behagen opwekte haatten. Toen deed Allah hun werken verloren gaan.'),
(4574, 47, 29, 'Of dachten zij in wier harten een ziekte is, dat Allah hun afgunst niet zou onthullen?'),
(4575, 47, 30, 'Als Wij het zouden willen, dan zouden Wij het jullie tonen. Zodat jij de tekenen ervan zeker zou kennen en jij die zeker zou herkennen door hun zinspelingen. En Allah kent jullie daden.'),
(4576, 47, 31, 'En Wij zullen jullie zeker beproeven, totdat Wij toetsen wie van jullie de strijders en de geduldigen zijn. En Wij beproeven jullie daden.'),
(4577, 47, 32, 'Voorwaar, degenen die niet geloven en die afhouden van de Weg van Allah en die de Boodschapper tegenstreven nadat de Leiding hun duidelijk was geworden zullen Allah nooit iets kunnen schaden. En Hij zal hun werken vruchteloos maken.'),
(4578, 47, 33, 'O jullie die geloven, gehoorzaamt Allah en gehoorzaamt de Boodschapper en maakt jullie werken niet nietig.'),
(4579, 47, 34, 'Voorwaar, degenen die ongelovig zijn en die afhouden van de Weg van Allah en die dan sterven terwijl zij ongelovigen zijn: Allah zal hen nooit vergeven.'),
(4580, 47, 35, 'Weest daarom niet vernederd en roept niet op tot vrede terwijl jullie de overhand hebben. En Allah is met jullie. En Hij benadeelt jullie nooit (in de beloning) voor jullie werken.'),
(4581, 47, 36, 'Voorwaar, het wereldse leven is slechts spel en vermaak. Maar als jullie geloven en (Allah) vrezen, dan zal Hij jullie je beloningen geven en Hij zal niet om jullie bezittingen vragen.'),
(4582, 47, 37, 'Als Hij jullie die (bezittingen) zou vragen en bij jullie zou aandringen, dan zouden jullie gierig worden en zou Hij jullie afgunst onthullen.'),
(4583, 47, 38, 'Weet dat jullie degenen zijn die opgeroepen worden om bijdragen te geven op de Weg van Allah. En onder jullie zijn er die gierig zijn, en wie gierig is: voorwaar, hij is slechts gierig ten nadele van zichzelf. En Allah is de Behoefteloze, terwijl jullie de behoeftigen zijn. En als jullie je afwenden, dan zal Hij jullie door een ander volk vervangen, waarna zij niet als jullie zijn.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Voorwaar, Wij hebben jou een duidelijke overwinning geschonken.'),
(4585, 48, 2, 'Opdat Allah jouw vroegere zonden zal vergeven en ook de latere. En Hij vervolmaakt Zijn gunst aan jou en Hij leidt jou op het rechte Pad.'),
(4586, 48, 3, 'En Allah helpt jou met machtige hulp.'),
(4587, 48, 4, 'Hij is Degene Die die de vrede in de harten van de gelovigen neerzond, opdat zij geloof zouden toevoegen aan hun geloof (dat al in hen was). En aan Allah behoren de legers van de hemelen en de aarde. En Allah is Alwetend, Alwijs.'),
(4588, 48, 5, 'Opdat Hij de gelovige mannen en de gelovige vrouwen Tuinen (het Paradijs) zal doen binnengaan, waar onder door de rivieren stromen, waarin zij eeuwig levenden zullen zijn. En Hij wist hun fouten uit. En dat is bij Allah een grote overwinning.'),
(4589, 48, 6, 'En Hij bestraft de huichelaars en de huichelaarsters, en de veelgodenaanbidders en de veelgodenaanbidsters, die slechte gedachten over Allah koesteren. Het slechte zal hen omsingelen en Allah is woedend op hen. En Hij heeft hen vervloekt en Hij heeft de Hel voor hen bereid, en dat is de slechtste plaats van terugkeer.'),
(4590, 48, 7, 'En aan Allah behoren de legers (van Engelen) van de hemelen en de aarde. En Allah is Almachtig, Alwijs.'),
(4591, 48, 8, 'Voorwaar, Wij hebben jou gezonden als een getuige en een verkondiger en een waarschuwer.'),
(4592, 48, 9, 'Opdat jullie in Allah en Zijn Boodschapper zullen geloven en jullie Hem helpen en Hem eren en Zijn lof prijzen, \'s ochtends en \'s avonds.'),
(4593, 48, 10, 'Voorwaar, degenen die jou trouw hebben gezworen: voorwaar, zij hebben Allah trouw gezworen. Allah\'s Hand is boven hun handen. Wie dan (zijn trouw) schendt, die schendt slechts te nadele van zichzelf; maar wie wat hij aan Allah beloofde nakomt, aan hem zal Hij een geweldige beloning geven.'),
(4594, 48, 11, 'Degenen onder de bedoeïenen die achtergebleven waren zullen tot jou zeggen: "Onze bezittingen en gezinnen hielden ons bezig, vraag daarom vergeving voor ons." Zij zeggen met hun tongen wat niet in hun harten is. Zeg: "Wie heeft dan de macht om iets van Allah voor jullie te voorkomen, als Hij iets slechts tegen jullie wenst te doen, of als Hij een gunst voor jullie wenst?" Maar Allah is Alwetend over wat jullie doen.'),
(4595, 48, 12, 'Jullie veronderstelden zelfs dat de Boodschapper en de gelovigen nooit tot hun families zouden terugkeren en dat werd (door de Satan) in jullie harten schoonschijnend gemaakt en jullie koesterden slechte gedachten en jullie werden een vernietigd volk.'),
(4596, 48, 13, 'En wie niet in Allah en Zijn Boodschapper gelooft: voorwaar, Wij hebben voor de ongelovigen een laaiend Vuur bereid.'),
(4597, 48, 14, 'En aan Allah behoort de heerschappij van de hemelen en de aarde. Hij vergeeft wie Hij wil en Hij bestraft wie Hij wil. En Allah is Vergevensgezind, Meest Barmhartig.'),
(4598, 48, 15, 'De achtergeblevenen zullen zeggen: "Wanneer jullie uittrekken naar de oorlogsbuit, om die te halen, laat ons dan met jullie meegaan." Zij willen het Woord van Allah veranderen. Zeg (O Moehammad): "Jullie zullen nooit met ons meegaan, zo heeft Allah eerder gesproken." Zij zullen dan zeggen: "Jullie zijn jaloers op ons." Zij begrepen beslist slechts weinig.'),
(4599, 48, 16, 'Zeg tot de achtergeblevenen van de bedoeïenen: "Jullie zullen worden opgeroepen tegen een volk met grote kracht en grote strijdlust, jullie zullen hen doden of zij zullen zich overgeven. Als jullie dan gehoorzamen, dan zal Allah jullie een goede beloning geven. Maar als jullie je afwenden, zoals jullie je eerder afwendden: Hij zal jullie bestraffen met een pijnlijke bestraffing."'),
(4600, 48, 17, 'Het is geen zonde voor de blinde, en niet voor de lamme, en niet voor de zieke (wanneer zij niet meevechten). En wie Allah en Zijn Boodschapper gehoorzaamt, die zal Hij Tuinen (het Paradijs) doen binnengaan, waar onder door de rivieren stromen. Maar wie zich afwendt, die zal Hij straffen met een pijnlijke bestraffing.'),
(4601, 48, 18, 'Voorzeker, het welgevallen van Allah was met de gelovigen toen zij jou trouw zwoeren onder de boom, en Hij wist wat in hun harten was. Toen deed Hij de vrede op hen neerdalen en beloonde Hij hen met een nabije overwinning.'),
(4602, 48, 19, 'En een grote oorlogsbuit die zij namen. En Allah is Almachtig, Alwijs.'),
(4603, 48, 20, 'Allah heeft jullie een grote oorlogsbuit beloofd die jullie zullen nemen en Hij heeft dat voor jullie bespoedigd. En Hij heeft de handen van de mensen (de vijanden) van jullie afgehouden, opdat het een teken voor de gelovigen zou zijn. En Hij leidt jullie op een recht Pad.'),
(4604, 48, 21, 'En andere (oorlogsbuiten) die jullie nog niet kunnen nemen. Allah heeft ze in Zijn macht, en Allah is Almachtig over alle zaken.'),
(4605, 48, 22, 'En als degenen die niet geloven jullie zouden bevechten, dan zouden zij zich omdraaien en vluchten, en zij zouden vervolgens geen beschermer en geen helper vinden.'),
(4606, 48, 23, '(Als) de handelwijze van Allah die reeds vroeger gold. En jij zult in de handelwijze van Allah nooit een verandering aantreffen.'),
(4607, 48, 24, 'En Hij is Degene Die hun handen van jullie heeft afgehouden en Die jullie handen van hen afgehouden heeft, in het midden van Mekkah (bij de Masdjid al Harâm), nadat Hij jullie de overwinning over hen had geschonken. En Allah is Alziende over wat jullie doen.'),
(4608, 48, 25, 'Zij zijn degenen die niet geloofden en die jullie afhielden van de Masdjid Al Harâm (de gewijde Moskee te Mekkah) en die tegenhielden dat de offerdieren hun slachtplaats zouden bereiken. En als er geen gelovige mannen en gelovige vrouwen in Mekkah waren geweest, waarvan jullie niet wisten of jullie hen zouden doden, waarna jullie vanwege hen in moeilijkheden zouden raken, zonder het te weten... (dan zou Allah de verovering van Mekkah reeds toegestaan hebben.) Opdat Allah tot Zijn Barmhartigheid toelaat wie Hij wil. En als zij (gelovigen en ongelovigen) van elkaar gescheiden waren, dan hadden Wij zeker degenen die niet geloofden onder hen met een pijnlijke bestraffing bestraft.'),
(4609, 48, 26, '(Gedenkt) toen degenen die niet geloofden trots in hun harten brachten, de trots van de onwetendheid, toen liet Allah de vrede neerdalen over Zijn Boodschapper en de gelovigen, en Hij verplichtte hen zich te houden aan het woord van Taqwa. En zij hadden er meer recht op en zij waren er geschikt voor. En Allah is Alwetend over alle zaken.'),
(4610, 48, 27, 'En voorzeker, Allah heeft de droom van Zijn Boodschapper bewaarheid: dat jullie zeker de Masdjid Al Harâm zullen binnengaan, als Allah het wil in veiligheid, jullie hoofden kaal scherend en het hoofdhaar kort snijdend, zonder vrees. Hij (Allah) weet wat jullie niet weten en Hij bracht daarnaast een nabije overwinning.'),
(4611, 48, 28, 'Hij is Degene Die Zijn Boodschapper met de Leiding en de ware godsdienst (de Islam) heeft gezonden om deze over alle godsdiensten te doen zegevieren. En Allah is voldoende als Getuige.'),
(4612, 48, 29, 'Moehammad is de Boodschapper van Allah en degenen die met hem zijn, zijn streng tegenover de ongelovigen, maar onderling barmhartig. Jij ziet hen zich neerbuigen en zich neerknielen. Zij zoeken een gunst van Allah en Zijn welgevallen. Hun kenmerken zijn zichtbaar in hun gezichten door de sporen van neerknielingen. Dat is hun beschrijving in de Taurât. En hun beschrijving in de Indjîl is als een jonge plant waarvan de loten ontspruiten, waardoor hij sterker wordt. Daarna wordt hij steviger en staat hij recht op zijn wortel. Bij de planters veroorzaakt hij blijdschap. Hij (Allah) wil daarmee de ongelovigen woedend maken. Allah heeft degenen onder hen die geloven en goede werken verrichten vergeving en een geweldige beloning beloofd.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'O jullie die geloven, plaatst julliezelf niet vóór Allah en Zijn Boodschapper, maar vreest Allah. Voorwaar, Allah is Alhorend, Alwetend.'),
(4614, 49, 2, 'O jullie die geloven, verheft jullie stemmen niet boven de stem van de Profeet en spreekt niet luid tegen hem, zoals sommigen van jullie luid tegen elkaar spreken, anders zullen jullie daden vruchteloos worden, terwijl jullie het niet beseffen.'),
(4615, 49, 3, 'Voorwaar, degenen die hun stemmen verzachten in de aanwezigheid van de Boodschapper van Allah: zij zijn degenen wier harten Allah heeft beproeft op Taqwa. Voor hen is er vergeving en een geweldige beloning.'),
(4616, 49, 4, 'Voorwaar, degenen die naar jou (O Moehammad) roepen van buiten de kamers (van jou): de meesten van hen begrijpen (de onbeleefdheid) ervan niet.'),
(4617, 49, 5, 'Als zij geduld hadden tot jij naar buiten kwam om hen te ontmoeten, dan zou dat beter voor hen zijn. En Allah is Vergevensgezind, Meest Barmhartig.'),
(4618, 49, 6, 'O jullie die geloven, als een verdorvene met een bericht komt, onderzoekt het dan nauwkeurig, anders treffen jullie uit onwetendheid een volk met een ramp, waarna jullie spijt zouden krijgen van wat jullie deden.'),
(4619, 49, 7, 'En weet dat onder jullie zich de Boodschapper van Allah bevindt. Als hij jullie (adviezen) zou volgen in vele aangelegenheden, dan zouden jullie zeker in moeilijkheden verkeren. Maar Allah heeft jullie doen houden van het geloof en Hij heeft het mooi gemaakt in jullie harten en Hij heeft jullie een afkeer doen hebben van ongeloof, zware zonden en opstandigheid. Zij zijn degenen die het rechte Pad volgen.'),
(4620, 49, 8, 'Als een gunst van Allah en een genieting. En Allah is Alwetend, Alwijs.'),
(4621, 49, 9, 'En als twee partijen van gelovigen met elkaar slaags raken, sticht dan vrede tussen hen. Als dan de ene partij de andere onrecht aandoet, bevecht dan degenen die onrecht plegen, tot zij terugkeren naar het bevel van Allah. Als zij dan terugkeren, sticht dan vrede tussen hen met rechtvaardigheid en weest onpartijdig. Voorwaar, Allah houdt van de onpartijdigen.'),
(4622, 49, 10, 'Voorwaar, de gelovigen zijn elkaars broeders, sticht daarom vrede tussen jouw broeders. En vreest Allah. Hopelijk zullen jullie begenadigd worden.'),
(4623, 49, 11, 'O jullie die geloven, laat een volk niet een ander volk beledigen, het kan zijn dat zij (die beledigd worden) beter zijn dan hen; en laat sommige vrouwen geen andere vrouwen beledigen, het kan zijn dat zij beter zijn dan hen. En hoont elkaar niet, en belastert elkaar niet met bijnamen. De slechtste naam is de verdorven (naam), na het geloof (ontvangen te hebben). En wie geen berouw toont: zij zijn de onrechtplegers.'),
(4624, 49, 12, 'O jullie die geloven, vermijdt veel van de kwade vermoedens. Voorwaar, een deel van de kwade vermoedens zijn zonden. En bespioneert elkaar niet en spreekt geen kwaad over elkaar in elkaars afwezigheid. Zou iemand van jullie het vlees van zijn dode broeder willen eten? Jullie zouden het zeker haten. En vreest Allah. Voorwaar, Allah is Berouwaanvaardend, Meest Barmhartig.'),
(4625, 49, 13, 'O mensheid, Wij hebben jullie geschapen uit een man en een vrouw en Wij hebben jullie tot volken en stammen gemaakt, opdat jullie elkaar leren kennen. Voorwaar, de meest edele van jullie is bij Allah degene die het meest (Allah) vreest. Voorwaar, Allah is van alles op de hoogte, Alwetend.'),
(4626, 49, 14, 'De bedoeïenen zeggen: "Wij geloven." Zeg (tegen hen O Moehammad): "Jullie geloven (nog) niet," maar zeg (dat zij zeggen): "Wij hebben ons overgegeven," want het geloof is jullie harten nog niet binnengegaan. Maar als jullie Allah en Zijn Boodschapper gehoorzamen, dan vermindert Hij niets (van de beloning) van jullie daden. Voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(4627, 49, 15, 'Voorwaar, de gelovigen zijn slechts degenen die in Allah en Zijn Boodschapper geloven, die vervolgens niet twijfelen en die met hun bezittingen en hun levens strijden op de Weg van Allah. Zij zijn de waarachtigen.'),
(4628, 49, 16, 'Zeg: "Zouden jullie Allah willen vertellen over (de waarachtigheid) van jullie geloof? Terwijl Hij kent wat zich in de hemelen en op de aarde bevindt? En Allah is Alwetend over alle zaken.'),
(4629, 49, 17, 'Zij menen jou een dienst te bewijzen door Moslim te worden. Zeg: "Bewijst mij geen dienst door jullie (toetreden tot) de Islam. Juist Allah heeft jullie begenadigd, doordat Hij jullie naar het geloof heeft geleid, als jullie waarachtig zijn."'),
(4630, 49, 18, 'Voorwaar, Allah kent het onwaarneembare van de hemelen en de aarde. En Allah is Alziend over wat jullie doen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Qaf, bij de edelmoedige Koran.'),
(4632, 50, 2, 'Zij zijn zelfs verbaasd dat er uit hun midden een waarschuwer tot hen is gekomen, zodat de ongelovigen zeggen: "Dat is een verbazingwekkende zaak!'),
(4633, 50, 3, 'Als wij gestorven zijn, en stof zijn geworden (worden wij dan weer opgewekt?) Dat is een onwaarschijnlijke terugkeer."'),
(4634, 50, 4, 'Waarlijk, Wij weten wat de aarde van hen zal wegnemen, en aan Onze Zijde is een Boek dat nauwkeurig bijgehouden wordt.'),
(4635, 50, 5, 'Zij loochenden zelfs de Waarheid toen die tot hen kwam, daarom nemen zij een verward standpunt in.'),
(4636, 50, 6, 'Kijken zij dan niet naar de hemel boven hen, hoe Wij die gebouwd hebben en hoe Wij die versierd hebben en hoe die geen enkele scheur heeft?'),
(4637, 50, 7, 'En hoe Wij de aarde uitgespreid hebben en Wij daarop stevige bergen geplaatst hebben en Wij daarop allerlei mooie gewassen deden groeien?'),
(4638, 50, 8, 'Als lering en herinnering voor iedere berouwvolle dienaar.'),
(4639, 50, 9, 'En Wij hebben uit de hemel gezegend water neer doen dalen, waarna Wij daarmee tuinen deden groeien en graan van oogstbare gewassen.'),
(4640, 50, 10, 'En rijzige dadelpalmen met boven elkaar gevoegde kolven.'),
(4641, 50, 11, 'Als een voorziening voor de dienaren. En Wij brengen daarmee (water) het dode land tot leven. Zo zal ook de Opwekking zijn.'),
(4642, 50, 12, 'Vóór hen loochende het volk van Nôeh, en de bewoners van Rass en de Tsamôed.'),
(4643, 50, 13, 'En de \'Ad, en Fir\'aun en de broeders van Lôeth.'),
(4644, 50, 14, 'En de bewoners van Aikah en het volk van Toebba\'. Allen loochenden de Boodschappers. Daarom was de bestraffing terecht.'),
(4645, 50, 15, 'Waren Wij dan niet in staat tot de eerste schepping? Zij verkeren beslist in twijfel over een nieuwe schepping.'),
(4646, 50, 16, 'En voorzeker, Wij hebben de mens geschapen en Wij weten wat zijn ziel hem influistert en Wij zijn dichter bij hem dan zijn halsslagader.'),
(4647, 50, 17, 'Wanneer de twee ontvangers (Engelen) aan de rechterzijde en aan de linkerzijde zitten te schrijven.'),
(4648, 50, 18, 'Is er geen woord dat Hij uit, of aan zijn Zijde bevindt zich een waker die gereed is.'),
(4649, 50, 19, 'En de doodsstrijd zal werkelijk komen, dat is waarvoor jullie proberen te vluchten.'),
(4650, 50, 20, 'En er zal op de bazuin geblazen worden. Dat is de Dag waarvoor gewaarschuwd is.'),
(4651, 50, 21, 'En elke ziel zal naar voren komen, met bij haar een (Engel als) voortdrijver en een (Engel als) getuige.'),
(4652, 50, 22, '(Er wordt hem gezegd:) "Voorzeker, jij verkeerde hiervoor in onachtzaamheid, toen hieven Wij van jou de bedekking van jouw (hart) op, toen was jouw waarneming op deze Dag scherp."'),
(4653, 50, 23, 'En zijn metgezel (een Engel) zegt: "Dit is wat bij mij gereed is."'),
(4654, 50, 24, '(Allah zegt tegen de twee Engelen:) "Werpt iedere opstandige ongelovige in de Hel.'),
(4655, 50, 25, 'Een tegenhouder van het goede, een overtreder een twijfelaar.'),
(4656, 50, 26, 'Degene die een andere god naast Allah nam: werpt hem daarom in de harde bestraffing."'),
(4657, 50, 27, 'En zijn metgezel (de Satan) zegt: "Onze Heer, ik heb hem niet tot dwaling gebracht; hij verkeerde zelf in vergaande dwaling."'),
(4658, 50, 28, 'Hij (Allah) zegt: "Redetwist niet in Mijn aanwezigheid, en Ik heb waarlijk een waarschuwing tot jullie gericht.'),
(4659, 50, 29, 'Het Woord van Mijn Zijde verandert niet en Ik ben niet onrechtvaardig jegens de dienaren."'),
(4660, 50, 30, '(Gedenkt) de Dag dat Wij de Hel zeggen: "Ben jij al vol?" En zij zegt: "Is er nog meer?"'),
(4661, 50, 31, 'En het Paradijs zal dicht bij de Moettaqoen gebracht worden, zonder ver te zijn.'),
(4662, 50, 32, '(Er wordt gezegd:) "Dit is wat jullie beloofd is, aan iedere berouwvolle, wakende.'),
(4663, 50, 33, 'Die de Barmhartige ongezien vreesde en die met een berouwvol hart kwam.'),
(4664, 50, 34, 'Treed haar binnen in vrede, dit is de Dag van de eeuwigheid."'),
(4665, 50, 35, 'Voor hen is daarin wat zij wensen en aan Onze Zijde is er nog meer.'),
(4666, 50, 36, 'En hoeveel generaties vóór hen hebben Wij niet vernietigd, die machtiger waren dan zij? Zij reisden toen in de landen, was er een kans om te vluchten?'),
(4667, 50, 37, 'Voorwaar, daarin is zeker een vermaning voor wie verstand heeft of wie luistert terwijl hij een getuige is.'),
(4668, 50, 38, 'En voorzeker, Wij hebben de hemelen en de aarde en wat ertussen is in zes dagen (perioden) geschapen, en vermoeienis raakte Ons niet.'),
(4669, 50, 39, 'Wees daarom geduldig met wat zij zeggen en prijs de Glorie met een lofprijzing van jouw Heer, vóór zonsopgang en vóór zonsondergang.'),
(4670, 50, 40, 'En prijs Hem in de nacht en na afloop van de neerknielingen (shalât).'),
(4671, 50, 41, 'En luister die Dag naar de roeper die vanaf een nabije plaats roept.'),
(4672, 50, 42, 'Op die Dag zullen zij het blazen (op de bazuin) in werkelijkheid horen. Dat is de Dag van de Opwekking.'),
(4673, 50, 43, 'Voorwaar, Wij doen leven en Wij doen sterven en tot Ons is de terugkeer.'),
(4674, 50, 44, 'Op de Dag waarop de aarde over hen gespleten zal worden, haasten (zij zich te verzamelen). Dat is een gemakkelijke verzameling voor Ons.'),
(4675, 50, 45, 'Wij weten het beste wat zij zeggen. En jij (Moehammad) bent niet als een dwinger over hen aangesteld. Vermaan daarom met de Koran degenen die Mijn Waarschuwing vrezen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Bij de winden die doen opwaaien.'),
(4677, 51, 2, 'Bij de wolken die een zware last dragen.'),
(4678, 51, 3, 'Bij de schepen die gemakkelijk voortdrijven.'),
(4679, 51, 4, 'Bij de verdelers (de Engelen) die volgens een verordening verdelen.'),
(4680, 51, 5, 'Voorwaar, wat jullie is aangezegd zal zeker bewaarheid worden.'),
(4681, 51, 6, 'En voorwaar, de (Dag der) Opstanding zal zeker plaatsvinden.'),
(4682, 51, 7, 'Bij de hemel met zijn banen (van sterren en planeten).'),
(4683, 51, 8, 'Voorwaar, jullie standpunt (tegenover de Profeet en de Koran) wisselt.'),
(4684, 51, 9, 'Degene die ervan afgewend wordt, die wordt belogen.'),
(4685, 51, 10, 'Verdoemd zijn de leugenaars!'),
(4686, 51, 11, 'Degenen die in achteloosheid verkeren.'),
(4687, 51, 12, 'Zij vragen: "Wanneer is de Dag van de Opstanding?"'),
(4688, 51, 13, 'Op die Dag zullen zij in de Hel verbrand worden.'),
(4689, 51, 14, '(De bewaker van de Hel zegt:) "Proeft jullie bestraffing. Dit is waar jullie de bespoediging van vroegen."'),
(4690, 51, 15, 'Voorwaar, de Moettaqôen verblijven in Tuinen en bij bronnen (in het Paradijs).'),
(4691, 51, 16, 'Zij nemen wat hun Heer hun geeft. Voorwaar, zij behoorden voorheen tot de weldoeners.'),
(4692, 51, 17, 'Zij plachten gedurende de nacht weinig te slapen.'),
(4693, 51, 18, 'En in de laatste uren van de nacht smeekten zij om vergeving.'),
(4694, 51, 19, 'En van hun bezittingen was een rechtmatig deel voor de bedelaar en voor degene die zich weerhield van bedelen.'),
(4695, 51, 20, 'En op de aarde zijn Tekens voor de overtuigden.'),
(4696, 51, 21, 'En ook in jullie zelf, zien jullie dan niet?'),
(4697, 51, 22, 'En in de hemel is jullie voorziening, en wat jullie is beloofd.'),
(4698, 51, 23, 'Bij de Heer van de hemel en de aarde: voorwaar, het is zeker waar, zo waar als (het feit) dat jullie spreken.'),
(4699, 51, 24, 'Heeft de geschiedenis van de geëerde gasten van Ibrahîm jou bereikt?'),
(4700, 51, 25, 'Toen zij bij hem kwamen, zeiden zij: "Vrede!" Hij zei: "Vrede!", (en hij dacht bij zichzelf:) "Onbekend volk."'),
(4701, 51, 26, 'Hij ging toen vlug naar zijn familie en bracht een geroosterd kalf.'),
(4702, 51, 27, 'Hij plaatste het daarop vóór hen, en zei: "Eten jullie het niet?"'),
(4703, 51, 28, '(Maar zij wilden niet eten.) Toen voelde hij angst voor hen. Zij zeiden: "Wees niet bang." En zij verkondigden hem de verheugende tijding over (de geboorte van) een verstandige jongeling (Ishâq).'),
(4704, 51, 29, 'Zijn vrouw kwam schreeuwend naar voren, en zij sloeg zich in haar gezicht, en zei: "Ik ben een oude, onvruchtbare vrouw!"'),
(4705, 51, 30, 'Zij zeiden: "Zo heeft jouw Heer gesproken: voorwaar, Hij is de Alwijze, de Alwetende."'),
(4706, 51, 31, 'Hij (Ibrâhîm) vroeg: "Wat is jullie boodschap, O gezanten?"'),
(4707, 51, 32, 'Zij zeiden: "Voorwaar, wij zijn gezonden naar een volk van misdadigers.'),
(4708, 51, 33, 'Opdat wij stenen van klei op hen neerzenden.'),
(4709, 51, 34, 'Die zijn gekenmerkt bij jouw Heer, voor de overtreders."'),
(4710, 51, 35, 'Toen deden Wij degenen die daar tot de gelovigen behoorden vertrekken.'),
(4711, 51, 36, 'Maar Wij troffen daar slechts één huis van degenen die zich (aan Allah) overgegeven hadden aan. (het huis van Lôeth)'),
(4712, 51, 37, 'En Wij lieten daar een Teken achter voor degenen die de pijnlijke bestraffing vreesden.'),
(4713, 51, 38, 'En ook in (de geschiedenis van) Môesa toen Wij hem naar Fir\'aun zonden met een duidelijk bewijs.'),
(4714, 51, 39, 'Hij (Fir\'aun) wendde zich af met zijn gevolg, en hij zei: "(Hij is) een tovenaar, of een bezetene!"'),
(4715, 51, 40, 'Daarop grepen Wij hem en zijn legers en wierpen hen in de zee. En hem (Fir\'aun) trof de blaam.'),
(4716, 51, 41, 'En in de \'Âd, toen Wij over hen een verwoestende wind zonden.'),
(4717, 51, 42, 'Die niets heel liet van wat hij tegenkwam, maar (alles) tot ruïnes maakte.'),
(4718, 51, 43, 'En in de Tsamôed, toen tot hen gezegd werd: "Geniet maar, tot een bepaalde tijd."'),
(4719, 51, 44, 'Toen waren zij hoogmoedig tegenover het gebod van hun Heer, waarop de bliksemslag hen greep, terwijl zij toezagen.'),
(4720, 51, 45, 'Toen konden zij niet meer opstaan en zij konden zichzelf niet helpen.'),
(4721, 51, 46, 'En het volk van Nôeh van daarvóór: voorwaar, zij waren een zwaar zondig volk.'),
(4722, 51, 47, 'En Wij hebben de hemel met een grote macht gebouwd. En voorwaar, Wij zijn zeker Machtigen.'),
(4723, 51, 48, 'En Wij hebben de aarde uitgespreid, en de beste Uitspreiders zijn Wij.'),
(4724, 51, 49, 'En van alle dingen hebben Wij paren geschapen. Hopelijk zullen jullie je laten vermanen.'),
(4725, 51, 50, '(Zeg, O Moehammad) "Vlucht daarom naar Allah: voorwaar, ik ben voor jullie van Hem (voor Zijn bestraffing) een duidelijke waarschuwer.'),
(4726, 51, 51, 'En neemt naast Allah geen andere god: voorwaar, ik ben voor jullie van Hem (voor Zijn bestraffing) een duidelijke waarschuwer."'),
(4727, 51, 52, 'Zo kwam er tot degenen vóór hen geen Boodschapper, of zij zeiden: "(Hij is) een tovenaar, of een bezetene."'),
(4728, 51, 53, 'Dragen zij dit aan elkaar over (van geslacht op geslacht)? Zij zijn zelfs een overtredend volk.'),
(4729, 51, 54, 'Wend je daarom van hen af, dan wordt jou niets verweten.'),
(4730, 51, 55, 'En vermaan: want voorwaar, de vermaning baat de gelovigen.'),
(4731, 51, 56, 'En Ik heb de Djinn\'s en de mens slechts geschapen om Mij te dienen.'),
(4732, 51, 57, 'Ik wens geen voorzieningen van hen, en ik wens niet dat zij Mij voeden.'),
(4733, 51, 58, 'Voorwaar, Allah is de Voorziener, de Bezitter van sterke kracht.'),
(4734, 51, 59, 'Voorwaar, de zonden van degenen die onrecht plegen zijn gelijk aan de zonden van hun soortgenoten (in vroegere generaties). Laten zij daarom Mij niet vragen (de bestraffing) te bespoedigen.'),
(4735, 51, 60, 'Wee dan degenen die ongelovig zijn op hun Dag die aangezegd is.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Bij de berg (Sinaï).'),
(4737, 52, 2, 'Bij een geschreven Boek.'),
(4738, 52, 3, 'In een opengerold perkament.'),
(4739, 52, 4, 'Bij het veel bezochte Huis (de Ka\'bah).'),
(4740, 52, 5, 'En het opgeheven gewelf (de hemel).'),
(4741, 52, 6, 'En de kolkende zee.'),
(4742, 52, 7, 'Voorwaar, de bestraffing van jouw Heer zal zeker plaatsvinden.'),
(4743, 52, 8, 'Niemand kan hem tegenhouden.'),
(4744, 52, 9, 'Op de Dag waarop de hemel heftig beeft.'),
(4745, 52, 10, 'En de bergen zich verplaatsen.'),
(4746, 52, 11, 'Wee op die Dag de loochenaars.'),
(4747, 52, 12, 'Degenen die zich vermaken met ijdelheden.'),
(4748, 52, 13, 'Op die Dag zullen zij met geweld naar de Hel worden geduwd.'),
(4749, 52, 14, '(En er zal gezegd worden:) "Dit is de Hel die jullie plachten te loochenen.'),
(4750, 52, 15, 'Is dit dan toverij? Of kunnen jullie niet zien?'),
(4751, 52, 16, 'Gaat haar binnen en weest geduldig of weest ongeduldig; het zal voor jullie niets uitmaken. Voorwaar, jullie worden slechts vergolden naar wat jullie plachten te verrichtten."'),
(4752, 52, 17, 'Voorwaar, de Moettaqôen bevinden zich in Tuinen (het Paradijs) en in genietingen.'),
(4753, 52, 18, 'Zich verheugend over wat hun Heer hun heeft gegeven. En hun Heer heeft hen behoed voor de straf van de Hel.'),
(4754, 52, 19, '(Er wordt gezegd:) "Eet en drinkt smakelijk wegens wat jullie plachten te verrichten."'),
(4755, 52, 20, 'Leunend op rustbanken, tegenover elkaar. En Wij zullen hen huwen met schone maagden.'),
(4756, 52, 21, 'En degenen die geloven en die in het geloof gevolgd worden door hun nakomelingen: Wij voegen hun nakomelingen bij hen en Wij verminderen (de beloning voor) hun daden niet. Ieder mens staat borg voor wat hij verrichtte.'),
(4757, 52, 22, 'En Wij geven hun vruchten en vlees, van welke soort zij wensen.'),
(4758, 52, 23, 'Zij reiken daar elkaar een beker aan waarbij geen onzin en geen zondigheid is.'),
(4759, 52, 24, 'En onder hen gaan jongelingen rond die lijken op welbewaarde parels.'),
(4760, 52, 25, 'Zij zullen zich tot elkaar wenden en elkaar vragen stellen.'),
(4761, 52, 26, 'Zij zeggen: "Voorwaar, wij waren vroeger temidden van onze verwanten bevreesd (voor Allah).'),
(4762, 52, 27, 'Allah begenadigde ons toen en Hij behoedde ons voor de bestraffing van de verschroeiende wind.'),
(4763, 52, 28, 'Voorwaar, Wij plachten Hem vroeger aan te roepen: voorwaar, Hij is de Weldoener, de Meest Barmhartige."'),
(4764, 52, 29, 'Vermaan daarom, jij bent door de Genade van jouw Heer geen waarzegger en geen bezetene.'),
(4765, 52, 30, 'Of zeggen zij: "Een dichter, wij wachten af of het noodlot hem met ongeluk zal treffen."'),
(4766, 52, 31, 'Zeg (O Moehammad): "Wacht maar af: voorwaar, Ik behoor met jullie tot de afwachtenden."'),
(4767, 52, 32, 'Of beveelt hun verstand hun dit, of zijn zij een overtredend volk?'),
(4768, 52, 33, 'Of zeggen zij: "Hij heeft hem (de Koran) verzonnen"? Nee, zij geloven niet.'),
(4769, 52, 34, 'Laten zij dan een bericht brengen dat daaraan gelijk is, als zij waarachtigen zijn.'),
(4770, 52, 35, 'Of zijn zij uit niets geschapen, of zijn zij (zelf) de scheppers?'),
(4771, 52, 36, 'Of hebben zij de hemelen en de aarde geschapen? Zelfs zij zijn er niet van overtuigd.'),
(4772, 52, 37, 'Of bevinden zich bij hen de schaften van jouw Heer, of hebben zij de heerschappij?'),
(4773, 52, 38, 'Of beschikken zij over een ladder (die tot in de hemel reikt) waarop zij kunnen afluisteren? Laat dan de afluisteraar onder hen met een duidelijk bewijs komen.'),
(4774, 52, 39, 'Of zijn voor Hem de dochters en voor jullie de zonen?'),
(4775, 52, 40, 'Of vraag jij van hen een beloning, zodat zij belast zijn met een schuld?'),
(4776, 52, 41, 'Of is bij hen het onwaarneembare, zodat zij het kunnen opschrijven?'),
(4777, 52, 42, 'Of willen zij een list beramen? Degenen die niet geloven zijn degenen die in een list versrikt raken.'),
(4778, 52, 43, 'Of hebben zij een andere god dan Allah? Heilig is Allah, boven de deelgenoten die zij (Hem) toekennen.'),
(4779, 52, 44, 'En als zij een stuk uit de hemel (zouden) zien neerstorten, dan zeggen zij: "Stapelwolken."'),
(4780, 52, 45, 'Laat hen maar, totdat zij hun Dag ontmoeten waarop zij door de bliksemslag getroffen zullen worden.'),
(4781, 52, 46, 'Op die Dag zal hun list niets baten, en zij zullen niet worden geholpen.'),
(4782, 52, 47, 'En voorwaar, voor degenen die onrecht pleegden is er daarnaast nog een bestraffing, maar de meesten van hen weten het niet.'),
(4783, 52, 48, 'Wees geduldig met de beschikking van jouw Heer. Voorwaar, jij bent in Onze Ogen. En prijs de Glorie van jouw Heer wanneer jij staat.'),
(4784, 52, 49, 'En in de nacht, prijst Zijn Glorie, en (ook) bij het vervagen van de sterren (in de vroege ochtend).');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Bij de ster wanneer zij valt.'),
(4786, 53, 2, 'Jullie metgezel (de Profeet) dwaalt niet en hij is niet misleid.'),
(4787, 53, 3, 'En hij spreekt niet uit begeerte.'),
(4788, 53, 4, 'Het is niets anders dan een Openbaring die aan hem geopenbaard is.'),
(4789, 53, 5, 'Een machtige in kracht (Djibrîl) onderwees hem.'),
(4790, 53, 6, 'Een bezitter van wijsheid, en hij (Djibrîl) verscheen (in zijn aardse vorm).'),
(4791, 53, 7, 'En hij bevond zich aan de hoogste horizon.'),
(4792, 53, 8, 'Daarna naderde hij en daalde neer.'),
(4793, 53, 9, 'Zodat hij zich op een afstand van twee booglengten (van Moehammad) bevond, of dichterbij.'),
(4794, 53, 10, 'Toen openbaarde Hij aan Zijn dienaar wat Hij openbaarde.'),
(4795, 53, 11, 'Het hart (van de Profeet) loog niet over wat het zag.'),
(4796, 53, 12, 'Willen jullie (veelgodenaanbidders) dan redetwisten over wat hij zag?'),
(4797, 53, 13, 'En voorzeker, hij (Moehammad) heeft hem (Djibrîl) bij een andere neerdaling gezien.'),
(4798, 53, 14, 'Bij Sidratilmoentaha.'),
(4799, 53, 15, 'Daarbij is de Tuin van de Verblijfplaats (het Paradijs).'),
(4800, 53, 16, 'Toen de Sidrah omhuld werd door wat hem omhulde.'),
(4801, 53, 17, 'Zijn blik week niet en dwaalde niet.'),
(4802, 53, 18, 'Voorzeker, hij heeft de grote Tekenen van zijn Heer gezien.'),
(4803, 53, 19, 'Zien jullie (veelgodenaanbidders) dan al Lâta en al \'Oezza?'),
(4804, 53, 20, 'En al Manât, de andere, de derde?'),
(4805, 53, 21, 'Zijn voor jullie de mannen en voor Hem de vrouwen?'),
(4806, 53, 22, 'Dat zou een oneerlijke verdeling zijn.'),
(4807, 53, 23, 'Het zijn alleen maar namen die jullie hebben verzonnen, jullie en jullie vaderen. Allah heeft daarover geen bewijs neergezonden. Zij volgen niets dan vermoedens en wat de zielen begeren. Voorzeker, er is tot hen van hun Heer de Leiding gekomen.'),
(4808, 53, 24, 'Krijgt de mens (alles) wat hij verlangt?'),
(4809, 53, 25, 'Aan Allah behoort het laatste (en het Hiernamaals) en het eerste (het wereldse leven).'),
(4810, 53, 26, 'En hoeveel Engelen zijn er niet in de hemelen wier voorspraak niets baat, behalve nadat Allah toestemming geeft voor wie Hij wil en voor wie Hem behaagt?'),
(4811, 53, 27, 'Voorwaar, degenen die niet geloven in het Hiernamaals geven de Engelen zeker vrouwelijke namen.'),
(4812, 53, 28, 'Terwijl zij daarover geen kennis hebben, zij volgen niets dan vermoedens. En voorwaar, vermoedens baten niets tegen de Waarheid.'),
(4813, 53, 29, 'Wend je daarom af (O Moehammad) van wie zich van Onze Vermaning heeft afgekeerd, en die niets wenst dan het wereldse leven.'),
(4814, 53, 30, 'Dat is het doel van hun kennis. Voorwaar, jouw Heer weet beter wie van Zijn Weg is afgedwaald en wie de Leiding heeft aanvaard.'),
(4815, 53, 31, 'En aan Allah behoort wat er in de hemelen en op de aarde is; opdat Hij degenen die kwaad verrichtten zal vergelden voor wat zij deden en opdat Hij degenen die goed deden zal belonen met het beste (het Paradijs).'),
(4816, 53, 32, '(Zij zijn) degenen die de grote zonden en zedeloosheden mijden, behalve (onvermijdbare) lichte fouten. Voorwaar, jouw Heer is alomvattend in de vergeving. Hij kent jullie beter: toen Hij jullie uit aarde voortbracht en toen jullie nog baby\'s waren in de schoten van jullie moeders. Prijst niet julliezelf; Hij weet het beter wie (Allah) vreest.'),
(4817, 53, 33, 'Heb jij degene gezien die zich afkeert?'),
(4818, 53, 34, 'En die weinig gaf en (daarna) ophield?'),
(4819, 53, 35, 'Heeft hij kennis over het onwaarneembare, zodat hij ziet?'),
(4820, 53, 36, 'Of is hij niet op de hoogte gebracht van wat in de geschriften van Môesa staat?'),
(4821, 53, 37, 'En (de geschriften van) Ibrâhîm die trouw was?'),
(4822, 53, 38, 'Dat geen enkele drager de zonden van een ander zal dragen?'),
(4823, 53, 39, 'En dat de mens slechts dat krijgt waarnaar hij gestreefd hoeft?'),
(4824, 53, 40, 'En dat hij (het resultaat van) zijn streven zal zien?'),
(4825, 53, 41, 'Waarop zal hij beloond (vergolden) worden met de volmaakte beloning (vergelding)?'),
(4826, 53, 42, 'En dat bij jouw Heer het einde is?'),
(4827, 53, 43, 'En dat Hij het is Die doet lachen en doet huilen?'),
(4828, 53, 44, 'En dat Hij het is Die doet sterven en Die doet leven?'),
(4829, 53, 45, 'En dat Hij de paren heeft geschapen; de man en de vrouw?'),
(4830, 53, 46, 'Van een druppel (sperma), wanneer die wordt uitgestort?'),
(4831, 53, 47, 'En dat Hij het andere leven (de opwekking) voortbrengt?'),
(4832, 53, 48, 'En dat Hij het is Die verrijkt en Die verarmt?'),
(4833, 53, 49, 'En dat Hij de Heer van Sirius is?'),
(4834, 53, 50, 'En dat Hij de vroegere \'Âd vernietigd heeft?'),
(4835, 53, 51, 'En de Tsamôed, toen geen van hen overbleef?'),
(4836, 53, 52, 'En daarvoor het volk van Nôeh? En voorwaar, zij waren het meest onrechtvaardig en het meest buitensporig.'),
(4837, 53, 53, 'En de omgekeerde steden richtte Hij ten gronde.'),
(4838, 53, 54, 'Toen bedekte Hij haar volledig.'),
(4839, 53, 55, 'Welke van de genietingen van jouw Heer betwijfelen jullie dan?'),
(4840, 53, 56, 'Hij (Moehammad) is een waarschuwer onder de voorafgaande waarschuwers.'),
(4841, 53, 57, 'Het nabije (Dag des Oordeels) is genaderd.'),
(4842, 53, 58, 'Er is buiten Allah niemand die haar kan onthullen.'),
(4843, 53, 59, 'Zijn jullie verbaasd over deze Koran?'),
(4844, 53, 60, 'En lachen jullie, en huilen jullie niet?'),
(4845, 53, 61, 'Terwijl jullie (de Koran) veronachtzamen?'),
(4846, 53, 62, 'Knielt dan neer voor Allah en aanbidt (Hem).');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Het Uur is nabij en de maan is gespleten.'),
(4848, 54, 2, 'En wanneer zij een Teken zien, dan wenden zij zich af, en zeggen: "Voortdurende toverij."'),
(4849, 54, 3, 'En zij loochenen en volgen hun begeerten, terwijl alle zaken al zijn vastgesteld.'),
(4850, 54, 4, 'En voorzeker, er zijn berichten tot hen gekomen waar een afschrikking in is.'),
(4851, 54, 5, 'Doeltreffende wijsheid, maar de waarschuwingen baten niet.'),
(4852, 54, 6, 'Keer je daarom van hen af De Dag (zal komen) waarop een oproeper (een Engel) tot iets verschrikkelijks oproept.'),
(4853, 54, 7, 'Met teneergeslagen blikken komen zij uit de graven tevoorschijn, alsof zij verspreidde sprinkhanen zijn.'),
(4854, 54, 8, 'Zich haastend naar de oproeper. De ongelovigen zullen zeggen: "Dit is een zware dag."'),
(4855, 54, 9, 'Vóór hen loochende het volk van Nôeh, zij loochenden Onze dienaar, en zij zeiden: "Een bezetene!" En hij werd verbannen.'),
(4856, 54, 10, 'Waarop hij zijn Heer aanriep (en zei:) "Ik ben verslagen, help daarom."'),
(4857, 54, 11, 'Wij openden toen de poorten van de hemel met neergietend water.'),
(4858, 54, 12, 'En Wij deden bronnen uit de aarde barsten, waarna de wateren elkaar ontmoetten volgens een beschikking die waarlijk bepaald was.'),
(4859, 54, 13, 'En Wij droegen hem op een vaartuig van planken en (houten) pinnen.'),
(4860, 54, 14, 'Dat voer onder Ons toezicht, als beloning voor degene die werd verworpen.'),
(4861, 54, 15, 'En voorzeker, Wij lieten het achter als een teken, is er dan iemand die er lering uit trekt?'),
(4862, 54, 16, 'En hoe was Mijn bestraffing en Mijn waarschuwing?'),
(4863, 54, 17, 'En voorzeker, Wij hebben de Koran gemakkelijk gemaakt ter vermaning, is er dan iemand die er lering uit trekt?'),
(4864, 54, 18, '(Het volk van) de \'Âd loochende en hoe was Mijn bestraffing en Mijn waarschuwing?'),
(4865, 54, 19, 'Voorwaar, Wij zonden een verwoestende wind over hen, op een langdurige zwaarmoedige dag.'),
(4866, 54, 20, 'Die de mensen wegrukte alsof zij ontwortelde palmbomen waren.'),
(4867, 54, 21, 'Hoe was Mijn bestraffing en Mijn waarschuwing?'),
(4868, 54, 22, 'En voorzeker, Wij hebben de Koran gemakkelijk gemaakt ter vermaning, is er dan iemand die er lering uit trekt?'),
(4869, 54, 23, '(Het volk van) de Tsamôed loochende de waarschuwingen.'),
(4870, 54, 24, 'En zij zeiden toen: "Zullen wij één mens van ons volgen? Voorwaar, dan zouden wij in dwaling en in een toestand van krankzinnigheid verkeren.'),
(4871, 54, 25, 'Is de vermaning juist aan hèm onder ons neergezonden, terwijl hij een schaamteloze leugenaar is?"'),
(4872, 54, 26, 'Zij zullen weten wie de schaamteloze leugenaar is.'),
(4873, 54, 27, 'Wij zullen de vrouwtjeskameel zenden als een beproeving voor hen, let daarom op hen (O Shâlih) en wees geduldig.'),
(4874, 54, 28, 'En bericht hun dat het water onder hen (en de kameel) verdeeld moet worden. Ieder een dronk, om de beurt.'),
(4875, 54, 29, 'Zij riepen toen hun metgezel, die overmoedig werd en (haar) slachtte.'),
(4876, 54, 30, 'Hoe was toen Mijn bestraffing en Mijn waarschuwing?'),
(4877, 54, 31, 'Voorwaar, Wij zonden één bliksemslag, waarop zij als dorre takken voor veevoer werden.'),
(4878, 54, 32, 'En voorzeker, Wij hebben de Koran gemakkelijk gemaakt ter vermaning, is er dan iemand die er lering uit trekt?'),
(4879, 54, 33, 'Het volk van Lôeth loochende de waarschuwingen.'),
(4880, 54, 34, 'Voorwaar, Wij zonden vulkanische stenen over hen, behalve over de familie van Lôeth. Wij redden hen in het laatste gedeelte van de nacht.'),
(4881, 54, 35, 'Als een gunst van Ons. Zo belonen Wij wie dankbaar is.'),
(4882, 54, 36, 'En voorzeker, Hij (Lôeth) waarschuwde hen voor Onze harde greep, maar zij twijfelden aan de waarschuwingen.'),
(4883, 54, 37, 'En voorzeker, zij probeerden zijn gasten over te halen (tot hun begeerten), waarop Wij hen blind maakten. (En Allah zei:) "Proeft dan Mijn bestraffing en Mijn waarschuwing."'),
(4884, 54, 38, 'En voorzeker, een blijvende bestraffing kwam in de ochtend van de volgende dag tot hen.'),
(4885, 54, 39, 'Proeft dan Mijn bestraffing en Mijn waarschuwing.'),
(4886, 54, 40, 'En voorzeker, Wij hebben de Koran gemakkelijk gemaakt ter vermaning, is er dan iemand die er lering uit trekt?'),
(4887, 54, 41, 'En voorzeker, tot Fir\'aun kwamen waarschuwingen.'),
(4888, 54, 42, 'Zij loochenden alle Tekenen van Ons, waarop Wij hen grepen met de greep van een machtige geweldige.'),
(4889, 54, 43, 'Zijn de ongelovigen onder jullie beter dan diegenen van hen (bovengenoemden), of hebben jullie een vrijbrief in de vroegere Schriften?'),
(4890, 54, 44, 'Of zeggen zij: "Wij vormen één (groep) die zal overwinnen (van Moehammad)."'),
(4891, 54, 45, 'De groep zal verslagen worden en zij zullen vluchten.'),
(4892, 54, 46, 'Nee, het Uur is hun belofte, en het Uur is het verschrikkelijkst en het bitterst.'),
(4893, 54, 47, 'Voorwaar, de misdadigers verkeren in dwaling en in de Hel.'),
(4894, 54, 48, 'Op de Dag dat zij naar de Hel gesleept worden op hun gezichten, zal gezegd worden: "Proeft de aanraking van de Hel (Saqar)."'),
(4895, 54, 49, 'Voorwaar, Wij hebben alle zaken volgens een bepaalde maatgeving geschapen.'),
(4896, 54, 50, 'En Ons bevel is niets meer dan één Woord, als een oogwenk.'),
(4897, 54, 51, 'En voorzeker, Wij hebben jullie soortgenoten vernietigd, is er dan iemand die er lering uit trekt?'),
(4898, 54, 52, 'En alle dingen die zij doen, staan in Schriften.'),
(4899, 54, 53, 'En al het kleine en het grote is vastgelegd.'),
(4900, 54, 54, 'Voorwaar, de Moettaqôen verblijven in Tuinen en rivieren (het Paradijs).'),
(4901, 54, 55, 'In een waarachtige verblijfplaats, bij een machtige Heerser.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'De Erbarmer.'),
(4903, 55, 2, 'Hij heeft de Koran onderwezen.'),
(4904, 55, 3, 'Hij heeft de mens geschapen.'),
(4905, 55, 4, 'Hij heeft hem de duidelijke verklaring onderwezen.'),
(4906, 55, 5, 'De zon en de maan volgen de berekende banen.'),
(4907, 55, 6, 'En de struiken en de bomen knielen zich neer.'),
(4908, 55, 7, 'En Hij heeft de hemel opgeheven en Hij heeft de weegschaal geplaatst.'),
(4909, 55, 8, 'Opdat jullie het evenwicht niet verstoren.'),
(4910, 55, 9, 'En houdt de weegschaal in evenwicht met rechtvaardigheid en neemt niets van de weegschaal af.'),
(4911, 55, 10, 'En Hij heeft de aarde bereid voor de schepselen.'),
(4912, 55, 11, 'Daarop zijn vruchten en dadelpalmen met kolven.'),
(4913, 55, 12, 'En graan in aren en geurige planten.'),
(4914, 55, 13, 'Welke gunsten van jullie (Djinn\'s en mensen) Heer loochenen jullie dan?'),
(4915, 55, 14, 'Hij heeft de mens geschapen van droge klei, als aardewerk.'),
(4916, 55, 15, 'En Hij heeft de Djinn\'s geschapen van een rookloze vlam van vuur.'),
(4917, 55, 16, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4918, 55, 17, 'De Heer van de twee opgangen en de twee ondergangen.'),
(4919, 55, 18, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4920, 55, 19, 'Hij heeft de twee zeeën laten stromen en zij ontmoeten elkaar.'),
(4921, 55, 20, 'Tussen beide is een scheiding, zij kunnen die niet passeren.'),
(4922, 55, 21, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4923, 55, 22, 'Uit beide komen parels en koraal voort.'),
(4924, 55, 23, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4925, 55, 24, 'En aan Hem behoren de over de zee varende schepen, als bergen.'),
(4926, 55, 25, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4927, 55, 26, 'Alles wat op aarde is zal vergaan.'),
(4928, 55, 27, 'En het Aangezicht van jouw Heer is blijvend, de Bezitter van Majesteit en Eer.'),
(4929, 55, 28, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4930, 55, 29, 'Allen die in de hemelen en op de aarde zijn vragen Hem. Iedere dag is Hij bezig.'),
(4931, 55, 30, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4932, 55, 31, 'Wij zullen voor jullie een afrekening maken, O jullie Djinn\'s en mensen!'),
(4933, 55, 32, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4934, 55, 33, 'O verzameling van Djinn\'s en mensen, als jullie in staat zijn de hoeken van de hemelen en de aarde te verlaten, verlaat die dan, maar jullie zullen die slechts met een (grote) macht kunnen verlaten.'),
(4935, 55, 34, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4936, 55, 35, 'Er zal een vlam van vuur en gesmolten koper tot jullie gezonden worden, waartegen jullie elkaar niet kunnen helpen.'),
(4937, 55, 36, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4938, 55, 37, 'Wanneer dan de hemel is gespleten is zij rozenrood, als een geverfde huid.'),
(4939, 55, 38, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4940, 55, 39, 'Op die Dag zullen de mensen en de Djinn\'s niet ondervraagd worden over hun zonden.'),
(4941, 55, 40, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4942, 55, 41, 'De misdadigers zullen herkend worden aan hun kenmerken en daarna gegrepen worden bij het haar van het voorhoofd en de voeten.'),
(4943, 55, 42, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4944, 55, 43, 'Dit is de Hel die de zondaren loochenden.'),
(4945, 55, 44, 'Zij dolen daar rond in het midden van kokend heet water.'),
(4946, 55, 45, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4947, 55, 46, 'En voor wie vreesde voor het staan voor zijn Heer zijn er twee Tuinen (in het Paradijs).'),
(4948, 55, 47, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4949, 55, 48, 'Beide met een overvloed aan takken en vruchten.'),
(4950, 55, 49, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4951, 55, 50, 'In beide zijn twee stromende bronnen.'),
(4952, 55, 51, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4953, 55, 52, 'In beide zijn er vruchten van elke soort, in paren.'),
(4954, 55, 53, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4955, 55, 54, 'Leunend, op tapijten met brokaat aan hun binnenzijden en (de vruchten) van beide Tuinen hangen binnen handbereik.'),
(4956, 55, 55, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4957, 55, 56, 'In de Tuinen bevinden zich schonen met ingetogen blikken, die geen mens en geen Djinn ooit vóór hen heeft aangeraakt.'),
(4958, 55, 57, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4959, 55, 58, 'Als waren zij van robijn en koraal.'),
(4960, 55, 59, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4961, 55, 60, 'Er is voor het verrichten van het goede geen andere beloning dan het goede.'),
(4962, 55, 61, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4963, 55, 62, 'En naast deze twee zijn nog twee Tuinen.'),
(4964, 55, 63, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4965, 55, 64, 'Donkergroen van kleur.'),
(4966, 55, 65, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4967, 55, 66, 'In beide bevinden zich twee overvloedige bronnen.'),
(4968, 55, 67, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4969, 55, 68, 'In beide zijn vruchten, dadels en granaatappels.'),
(4970, 55, 69, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4971, 55, 70, 'Er in bevinden zich goede en mooie vrouwen.'),
(4972, 55, 71, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4973, 55, 72, 'Vrouwen met prachtige ogen, afgezonderd in tentverblijven.'),
(4974, 55, 73, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4975, 55, 74, 'Die geen mens en geen Djinn ooit vóór hen heeft aangeraakt.'),
(4976, 55, 75, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4977, 55, 76, 'Leunend op groene kussens en op prachtige tapijten.'),
(4978, 55, 77, 'Welke gunsten van jullie Heer loochenen jullie dan?'),
(4979, 55, 78, 'Gezegend zij de Naam van jouw Heer, de Bezitter van Majesteit en Eer.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Wanneer de Dag der Opstanding plaatsvindt.'),
(4981, 56, 2, '(Dan) kent de gebeurtenis ervan geen loochening.'),
(4982, 56, 3, 'Verlagend (voor de één), verheffend (voor de ander).'),
(4983, 56, 4, 'Wanneer de aarde hevig geschud wordt.'),
(4984, 56, 5, 'En de bergen volledig vernietigd worden.'),
(4985, 56, 6, 'Zodat deze tot rondvliegend stof worden.'),
(4986, 56, 7, 'En jullie in drie groepen verdeeld zijn.'),
(4987, 56, 8, 'De mensen van de rechterzijde, (wat een voorspoed voor) de mensen van de rechterzijde!'),
(4988, 56, 9, 'En de mensen van de linkerzijde, (wat een tegenspoed voor) de mensen van de linkerzijde!'),
(4989, 56, 10, 'En de eersten (de derde groep) zijn de eersten.'),
(4990, 56, 11, 'Dat zijn degenen die nabij zijn gebracht.'),
(4991, 56, 12, 'In de Tuinen van gelukzaligheid (het Paradijs).'),
(4992, 56, 13, 'Een aantal van de vroegeren.'),
(4993, 56, 14, 'En weinig van de lateren.'),
(4994, 56, 15, 'Op (met goud) geborduurde rustbanken.'),
(4995, 56, 16, 'Daarop leunend, tegenover elkaar zittend.'),
(4996, 56, 17, 'Onder hen gaan eeuwig jeugdigen rond.'),
(4997, 56, 18, 'Met bokalen en kannen en glazen, gevuld aan een stromende bron.'),
(4998, 56, 19, 'Waarvan zij geen hoofdpijn krijgen en niet dronken worden.'),
(4999, 56, 20, 'En vruchten waaruit zij kunnen kiezen.'),
(5000, 56, 21, 'En vlees van gevogelte, wat zij maar verlangen.'),
(5001, 56, 22, 'En schonen met schitterende ogen.'),
(5002, 56, 23, 'Gelijk welbewaarde parels.'),
(5003, 56, 24, 'Als een beloning voor wat zij plachten te doen.'),
(5004, 56, 25, 'Zij horen daarin geen onzin en geen zondigheid.'),
(5005, 56, 26, 'Slechts het zeggen van: "Vrede! Vrede!"'),
(5006, 56, 27, 'En de mensen van de rechterzijde, (wat een voorspoed voor) de mensen van de rechterzijde!'),
(5007, 56, 28, 'Temidden van lotusbomen zonder doornen.'),
(5008, 56, 29, 'En bananenbomen vol met vruchten.'),
(5009, 56, 30, 'En langdurige schaduw.'),
(5010, 56, 31, 'En stromend water.'),
(5011, 56, 32, 'En fruit in overvloed.'),
(5012, 56, 33, 'Niet onderbroken en niet verboden.'),
(5013, 56, 34, 'Op verhoogde rustbedden.'),
(5014, 56, 35, 'Voorwaar, Wij hebben hen (de vrouwen in het Paradijs) opnieuw geschapen.'),
(5015, 56, 36, 'En Wij hebben hen maagdelijk gemaakt.'),
(5016, 56, 37, 'Liefdevol en gelijk in leeftijd.'),
(5017, 56, 38, 'Voor de mensen aan de rechterzijde.'),
(5018, 56, 39, 'Een aantal van de vroegeren (groepen).'),
(5019, 56, 40, 'En een aantal van de lateren.'),
(5020, 56, 41, 'En de mensen van de linkerzijde, (wat een tegenspoed voor) de mensen aan de linkerzijde!'),
(5021, 56, 42, '(Zij verkeren) in een verzengende wind en kokend water.'),
(5022, 56, 43, 'En schaduwen van zwarte rook.'),
(5023, 56, 44, 'Niet koel en niet weldadig.'),
(5024, 56, 45, 'Voorwaar, zij plachten voorheen in weelde te leven.'),
(5025, 56, 46, 'En zij volhardden in geweldige zondigheid.'),
(5026, 56, 47, 'Zij plachten te zeggen: "Als wij gestorven zijn en tot stof en botten zijn geworden, zullen wij dan zeker opgewekt worden?'),
(5027, 56, 48, 'En ook onze voorvaderen?"'),
(5028, 56, 49, 'Zeg: "Voorwaar, de vroegeren en de lateren."'),
(5029, 56, 50, 'Zij zullen zeker bijeengebracht worden op het bepaalde tijdstip van een bekende Dag.'),
(5030, 56, 51, 'Daarna zullen jullie, O dwalende, loochenaars.'),
(5031, 56, 52, 'Zeker van de Zaqqôem-boom eten.'),
(5032, 56, 53, 'Dan zullen jullie daarmee de buiken vullen.'),
(5033, 56, 54, 'Daarna zullen jullie er (kokend water) van drinken.'),
(5034, 56, 55, 'Jullie zullen dan drinken als smachtende kamelen.'),
(5035, 56, 56, 'Dit is hun onthaal op de Dag des Oordeels.'),
(5036, 56, 57, 'Wij hebben jullie geschapen, waren jullie maar overtuigd geweest!'),
(5037, 56, 58, 'Hoe denken jullie dan over dat (zaad) wat jullie uitstorten?'),
(5038, 56, 59, 'Hebben jullie dat geschapen of zijn Wij de Scheppers?'),
(5039, 56, 60, 'Wij hebben de dood onder jullie bepaald, en Wij zijn niet zwak.'),
(5040, 56, 61, 'Om schepselen zoals jullie te scheppen en om jullie opnieuw tot leven te brengen in (een schepping) waarvan jullie niet weten.'),
(5041, 56, 62, 'En voorzeker, jullie hebben kennis over de eerste schepping, hadden jullie er maar lering uit getrokken!'),
(5042, 56, 63, 'Hoe denken jullie dan over wat jullie zaaien?'),
(5043, 56, 64, 'Zijn jullie het die het doen groeien of zijn Wij het Die doen groeien?'),
(5044, 56, 65, 'Als Wij zouden willen, dan zouden Wij het laten verdorren, zodat jullie het zouden blijven betreuren.'),
(5045, 56, 66, '(En zeiden:) "Voorwaar, wij zijn zeker met schuld beladen.'),
(5046, 56, 67, 'Wij zijn zelfs beroofd."'),
(5047, 56, 68, 'Hoe denken jullie dan over het water dat jullie drinken?'),
(5048, 56, 69, 'Zijn jullie het die het uit de wolken doen neerkomen of zijn Wij de neerzenders?'),
(5049, 56, 70, 'Als Wij zouden willen, dan zouden Wij het tot zout water maken. Waren jullie maar dankbaar!'),
(5050, 56, 71, 'Hoe denken jullie dan over het vuur dat jullie ontsteken?'),
(5051, 56, 72, 'Hebben jullie de boom daarvoor voortgebracht, of zijn Wij de voortbrengers?'),
(5052, 56, 73, 'Wij hebben het (vuur) gemaakt ter herinnering en tot nut voor de reizigers.'),
(5053, 56, 74, 'Prijst daarom de Naam van jouw Heer, de Geweldige.'),
(5054, 56, 75, 'Ik zweer bij het vallen van de sterren.'),
(5055, 56, 76, 'En voorwaar, dat is zeker een geweldige eed, als jullie dat begrepen!'),
(5056, 56, 77, 'En voorwaar, dit is zeker de nobele Koran.'),
(5057, 56, 78, 'In een welbewaard Boek.'),
(5058, 56, 79, 'Dat niemand aanraakt dan de gereinigden.'),
(5059, 56, 80, 'Een neerzending van de Heer der Werelden.'),
(5060, 56, 81, 'Onderschatten jullie deze Koran dan?'),
(5061, 56, 82, 'En nemen jullie het loochenen (als dankbetuiging voor) jullie levensvoorziening?'),
(5062, 56, 83, 'Waren jullie maar, toen (de ziel) de keel bereikte.'),
(5063, 56, 84, 'En jullie op dat moment toezagen.'),
(5064, 56, 85, 'En Wij dichter bij hem waren dan jullie - maar jullie zagen het niet.'),
(5065, 56, 86, 'Waren jullie toen maar niet verantwoordelijk geweest.'),
(5066, 56, 87, 'Dan zouden jullie haar (de ziel) terugbrengen, als jullie waarachtig geweest waren.'),
(5067, 56, 88, 'Als hij (de ziel) tot de nabijgebrachten behoort.'),
(5068, 56, 89, '(Dan zijn er voor de ziel) rust en voorzieningen en de Tuinen van gelukzaligheid (het Paradijs).'),
(5069, 56, 90, 'Als hij tot de mensen aan de rechterzijde behoort.'),
(5070, 56, 91, '(Luidt de begroeting.) "Vrede zij met jou," van de mensen van de rechterzijde.'),
(5071, 56, 92, 'En als hij tot de dwalende loochenaars behoort.'),
(5072, 56, 93, 'Dan is er een onthaal van kokend water.'),
(5073, 56, 94, 'En roostering in Djahîm (de Hel).'),
(5074, 56, 95, 'Voorwaar, dit is zeker de stellige Waarheid.'),
(5075, 56, 96, 'Prijst daarom de Naam van jouw Heer, de Geweldige.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Wat er in de hemelen en op de aarde is prijst de Glorie van Allah, en Hij is de Almachtige, de Alwijze.'),
(5077, 57, 2, 'Aan Hem behoort de heerschappij van de hemelen en de aarde. Hij doet leven en Hij doet sterven en Hij is Almachtig over alle zaken.'),
(5078, 57, 3, 'Hij is de Eerste en de Laatste, Az Zhâhir en Al Bâthin. En Hij is Alwetend over alle zaken.'),
(5079, 57, 4, 'Hij is Degene Die de hemelen en de aarde heeft geschapen in zes dagen (perioden) waarna Hij Zich op de Troon zetelde. Hij kent wat de aarde ingaat en wat er uit komt en wat uit de hemel neerdaalt en wat er in opstijgt. En Hij is met jullie, waar jullie ook zijn. En Allah is Alziende over wat jullie doen.'),
(5080, 57, 5, 'Aan Hem behoort de heerschappij van de hemelen en de aarde. En tot Allah keren de zaken terug.'),
(5081, 57, 6, 'Hij doet de nacht overgaan in de dag en Hij doet de dag overgaan in de nacht. En Hij is de Kenner van wat in de harten is.'),
(5082, 57, 7, 'Gelooft in Allah en Zijn Boodschapper en geeft bijdragen van dat waarover Hij jullie als beheerders aangesteld heeft. Voor degenen van jullie die geloven en bijdragen geven is er een grote beloning.'),
(5083, 57, 8, 'En wat is er met jullie, dat jullie niet geloven in Allah, terwijl de Boodschapper jullie oproept om te geloven in jullie Heer? En Hij is waarlijk een verbond met jullie aangegaan, als jullie gelovig zijn!'),
(5084, 57, 9, 'Hij is Degene Die duidelijke Verzen naar Zijn dienaar doet neerdalen om jullie uit de duisternissen naar het licht te voeren. En voorwaar, Allah is voor jullie zeker Zachtmoedig en Meest Barmhartig.'),
(5085, 57, 10, 'En wat is er met jullie, dat jullie geen bijdragen geven op de Weg van Allah, terwijl aan Allah de erfenis van de hemelen en de aarde behoort? Degenen van jullie die vóór de overwinning (bij Mekkah) bijdragen gaven en vochten, zijn niet gelijk, hun rang is hoger dan die van degenen die daarna bijdragen gaven en vochten. Maar beide groepen heeft Allah het goede (het Paradijs) beloofd. En Allah is Alwetend over wat jullie doen.'),
(5086, 57, 11, 'Wie is degene die aan Allah een goede lening geeft? Dan zal Hij deze verdubbelen voor hem en voor hem is er een rijke beloning.'),
(5087, 57, 12, 'Gedenkt de Dag waarop jij de gelovige mannen en de gelovigen vrouwen ziet. Hun licht straalt vóór hen en rechts van hen. (Er wordt hun gezegd:) "Deze Dag is er een verheugend bericht voor jullie: een Tuin waar onder door de rivieren stromen, (jullie zijn) daarin eeuwig levend. Dat is de geweldige overwinning."'),
(5088, 57, 13, 'Op die Dag zullen de huichelaars en de huichelaarsters tegen degenen die geloven, zeggen: "Wacht op ons en zodat wij jullie licht gebruiken. " Er wordt gezegd: "Gaat terug, naar achteren jullie! Smeekt om licht!" Er zal dan een muur tussen hen worden geplaatst, die een poort heeft: aan de binnenkant is er de Barmhartigheid en aan de buitenkant is er de bestraffing.'),
(5089, 57, 14, 'Zij (de huichelaars) roepen tot hen (de gelovigen): "Waren wij niet met jullie?" Zij zeggen: \'"Welzeker, maar jullie brachten jezelf in verzoeking en jullie wachtten en jullie twijfelden en de ijdele wensen hebben jullie misleid, tot de beschikking van Allah bewaarheid werd. En de verleider (de Satan) heeft jullie van Allah weggeleid.'),
(5090, 57, 15, 'Op deze Dag, zal er van jullie geen losprijs worden aanvaard, en ook niet van de ongelovigen. Jullie verblijfplaats is de Hel, dat is jullie beschermer. En dat is de slechtste plaats van terugkeer!"'),
(5091, 57, 16, 'Is het voor degenen die geloven nog geen tijd, dat hun harten zich vernederen voor het gedenken van Allah en wat is geopenbaard van de Waarheid (de Koran)? En laten zij niet worden als degenen aan wie de Schrift (de Taurât) voorheen was gegeven. Toen werd de tijd voor hen te lang, zodat hun harten verhardden. En de meesten van hen zijn zwaar zondigen.'),
(5092, 57, 17, 'Weet dat Allah de aarde doet leven na haar dood. Waarlijk, Wij hebben voor jullie de Tekenen duidelijk gemaakt. Hopelijk zullen jullie begrijpen.'),
(5093, 57, 18, 'Voorwaar, de mannen die bijdragen geven en de vrouwen die bijdragen geven en Allah een goede lening geven: Hij zal (het) voor hen vermenigvuldigen en voor hen is er een rijke beloning.'),
(5094, 57, 19, 'En degenen die in Allah en Zijn Boodschapper geloven: zij zijn de getrouwen en de getuigen bij hun Heer, voor hen is er hun beloning en hun licht. En degenen die ongelovig zijn en die Onze Verzen loochenen: zij zijn de bewonen van Djahîm (de Hel).'),
(5095, 57, 20, 'Weet dat het wereldse leven slechts een spel is, een vermaak, een versiering en opschepperij tussen jullie en wedijver in vermeerdering van bezit en kinderen, als de gelijkenis van een regen waarvan de planten (die zij voortbrengt) bij de boeren verwondering wekt. Daarna worden ze droog en je ziet ze geel worden, en vervolgens worden ze tot vergane resten. En in het Hiernamaals is er een harde bestraffing en een vergeving van Allah en welbehagen. En het wereldse leven is niets dan een verleidende genieting.'),
(5096, 57, 21, 'Wedijvert naar vergeving van jullie Heer en een Tuin (het Paradijs) waarvan de breedte is als de breedte van de hemel en de aarde, die voorbereid is voor degenen die in Allah en Zijn Boodschapper geloven. Dat is de gunst van Allah die Hij schenkt aan wie Hij wil. En Allah is de Bezitter van de Geweldige Gunst.'),
(5097, 57, 22, 'Er treft de aarde of julliezelf geen ramp, of het het staat in een boek, vóórdat Wij het doen gebeuren. Voorwaar, dat is voor Allah gemakkelijk.'),
(5098, 57, 23, 'Opdat jullie niet zullen treuren over wat jullie is ontgaan en jullie niet opgetogen zullen raken over wat Hij jullie heeft gegeven. En Allah houdt van geen enkele verwaande opschepper.'),
(5099, 57, 24, 'Degenen die gierig zijn en die de mensen tot gierigheid oproepen, en die zich afwenden. Voorwaar, Allah is de Behoefteloze, de Geprezene.'),
(5100, 57, 25, 'Voorzeker, Wij hebben Onze Boodschappers met de duidelijke bewijzen gezonden en Wij hebben met hen het Boek en de wetgeving neergezonden, opdat de mens in het midden zou staan (rechtvaardig zou handelen). En Wij hebben het ijzer neergezonden, waarin grote macht is en voordelen voor de mensheid, opdat Allah zou doen weten wie Hem en Zijn Boodschappers helpt, zonder dat hij (bv. Allah en het Paradijs) kan waarnemen. Voorwaar, Allah is Sterk, Geweldig.'),
(5101, 57, 26, 'En voorzeker zonden Wij Nôeh en Ibrâhîm en vestigden onder hun nakomelingen het Profeetschap en de Schrift. Onder hen waren er die de Leiding volgden, maar velen onder hen waren zwaar zondigen.'),
(5102, 57, 27, 'Vervolgens deden Wij Onze Boodschappers elkaar in hun sporen opvolgen. En Wij deden \'Îsa, de zoon van Maryam, volgen en Wij gaven hem de Indjîl. En Wij plaatsten in de harten van degenen die hem volgden mededogen en barmhartigheid. En het monnikwezen (celibaat), dat hebben zij (zelf) toegevoegd; Wij hebben het hun niet voorgeschreven. (Zij deden het) slechts om het welbehagen van Allah te zoeken. Daarna onderhielden zij het niet naar behoren en Wij gaven degenen die geloven onder hen hun beloning, maar velen onder hen waren zwaar zondigen.'),
(5103, 57, 28, 'O jullie die geloven, vreest Allah en gelooft in Zijn Boodschapper, dan zal Hij jullie dubbel van Zijn Barmhartigheid geven en jullie een licht geven waarmee jullie (recht) zullen voortgaan en Hij zal jullie vergeving schenken. En Allah is Vergevensgezind. Meest Barmhartig.'),
(5104, 57, 29, 'Opdat de Lieden van de Schrift zullen weten dat zij geen enkele macht hebben over de gunst van Allah en dat de gunst in de Hand van Allah is. Hij geeft die aan wie Hij wil. En Allah is de Bezitter van de geweldige gunst.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'Waarlijk, Allah heeft de woorden gehoord van haar die bij jou twistte over haar echtgenoot en zij klaagt bij Allah. En Allah hoorde het gesprek van jullie beiden aan. En Allah is Alhorend, Alziend.'),
(5106, 58, 2, 'Degenen die onder jullie die de Zhihâr uitspreken over hun vrouwen: zij zijn niet hun moeders, hun moeders zijn slechts degenen die hen gebaard hebben. En voorwaar, zij spreken zeker een verwerpelijk en bedrieglijk woord. En voorwaar, Allah is zeker Berouwaanvaardend, Vergevensgezind.'),
(5107, 58, 3, 'En degenen die de Zhihâr uitspreken over hun vrouwen en daarna willen terugkomen op wat zij zeiden: zij moeten dan een slaaf vrijkopen, voordat zij elkaar aanraken (geslachtsgemeenschap hebben). Dat is waartoe jullie onderricht worden. En Allah is Alziende over wat jullie doen.'),
(5108, 58, 4, 'En wie daartoe geen mogelijkheid vindt (moet) dan gedurende twee opeenvolgende maanden vasten, voordat zij elkaar aanraken. En wie daartoe niet in staat is, (moet) dan zestig armen voeden; opdat jullie geloven in Allah en Zijn Boodschapper. En dat zijn de bepalingen van Allah. En voor de ongelovigen is er een pijnlijke bestraffing.'),
(5109, 58, 5, 'Voorwaar, degenen die zich verzetten tegen Allah en Zijn Boodschapper zullen vernederd worden, zoals degenen vóór hen vernederd werden. En waarlijk, Wij hebben duidelijke Tekenen neergezonden. En voor de ongelovigen is er een vernederende bestraffing.'),
(5110, 58, 6, '(Gedenkt) de Dag waarop Allah Hen allemaal opwekt, Hij zal hun daarop meedelen wat zij gedaan hebben. Allah zal het nauwkeurig opsommen, terwijl zij het vergeten hebben. Allah is van alles Getuige.'),
(5111, 58, 7, 'Zie jij niet dat Allah weet wat er in de hemelen en op de aarde is? Er is geen geheim gesprek tussen drie mensen, of Hij is hun vierde; en niet tussen vijf, of Hij is hun zesde; en niet minder of meer dan dat, of Hij is met hen, waar zij zich ook bevinden. En Hij zal hun dan op de Dag der Opstanding bekendmaken wat zij hebben gedaan. Voorwaar, Allah is Alwetend over alle zaken.'),
(5112, 58, 8, 'Zie jij degenen niet die het geheime gesprek verboden werd? Daarop vervielen zij tot dat wat hun verboden werd. En zij voerden geheime gesprekken omwille van zonde, vijandigheid en opstand tegen de Boodschapper. En wanneer zij tot jou kwamen, begroetten zij jou niet met de woorden waarmee Allah jou begroet, en zij zeggen onder elkaar: "Had Allah ons maar bestraft voor wat wij zeiden." De Hel is voldoende voor hen. Daarin zullen zij branden. En dat is de slechtste plaats van terugkeer!'),
(5113, 58, 9, 'O jullie die geloven, wanneer jullie geheime gesprekken voeren, voert dan geen geheime gesprekken omwille van zonde, vijandigheid en opstand tegen de Boodschapper, maar voert geheime gesprekken omwille van goedheid en het vrezen (van Allah). En vreest Allah tot Wie jullie teruggekeerd worden.'),
(5114, 58, 10, 'Voorwaar, de (slechte) geheime gesprekken zijn afkomstig van de Satan, om degenen die geloven te bedroeven. Maar hij kan hen geen enkel kwaad berokkenen, behalve met de toestemming van Allah. En laten de gelovigen dan Allah vertrouwen.'),
(5115, 58, 11, 'O jullie die geloven, wanneer tot jullie gezegd wordt: "Maakt ruimte in de plaatsen van samenkomsten," maakt dan ruimte; Allah zal voor jullie ruimte maken. En wanneer tot jullie gezegd wordt: "Staat op," staat dan op; Allah zal degenen onder jullie die geloven en degenen aan wie kennis is gegeven in rang verheffen. En Allah is Alwetend over wat jullie doen.'),
(5116, 58, 12, 'O jullie die geloven, wanneer jullie een persoonlijk gesprek met de Boodschapper willen voeren, geeft dan vóór jullie gesprek iets uit aan liefdadigheid. Dat is beter voor jullie, en reiner. Maar als jullie niets kunnen vinden: voorwaar, Allah is dan Vergevensgezind, Meest Barmhartig.'),
(5117, 58, 13, 'Zijn jullie bevreesd voor (armoede) door iets uit te geven aan liefdadigheid, vóór jullie gesprek? Als jullie dat dan niet doen, dan vergeeft Allah jullie. Verricht dan de shalât en geeft de zakât en gehoorzaamt Allah en Zijn Boodschapper. En Allah is Alwetend over wat jullie doen.'),
(5118, 58, 14, 'Zie jij degenen die zich (in vriendschap) wendden tot een volk waarop Allah vertoornd is? Zij behoren niet tot jullie en zij behoren niet tot hen en zij zweren bij een leugen terwijl zij het weten.'),
(5119, 58, 15, 'Allah heeft voor hen een harde bestraffing bereid. Voorwaar, slecht was het wat zij plachten te doen.'),
(5120, 58, 16, 'Zij maakten hun eden tot een hindernis (zodat zij zich beschermden) en zij hielden toen af van de Weg van Allah. Voor hen is er daarom een vernederende bestraffing.'),
(5121, 58, 17, 'Hun bezittingen en hun kinderen zullen hen niets baten tegen Allah. Zij zijn degenen die de bewoners van de Hel zijn. Zij zijn daarin eeuwig levenden.'),
(5122, 58, 18, 'Op de Dag waarop Allah hen allen zal doen opstaan, zullen zij voor Hem zweren zoals zij voor jullie zweren en zij denken dat zij iets hebben (dat hen baat). Weet: voorwaar, zij zijn leugenaars.'),
(5123, 58, 19, 'De Satan heeft hen overmeesterd, waarna hij hen de gedachtenis van Allah deed vergeten. Zij zijn degenen van de groep (volgelingen) van de Satan. Weet: voorwaar, de volgelingen van de Satan zijn de verliezers.'),
(5124, 58, 20, 'Voorwaar, degenen die Allah en Zijn Boodschapper tegenstreven: zij zijn degenen die tot de vernederden behoren.'),
(5125, 58, 21, 'Allah heeft bepaald: "Ik en Mijn Boodschapper zullen zeker overwinnen." Voorwaar, Allah is Sterk, Almachtig.'),
(5126, 58, 22, 'Jij vindt geen volk dat in Allah en in de Laatste Dag gelooft dat degenen die Allah en Zijn Boodschapper tegenstreven bevriendt, ook al zijn het hun vaders, of hun zonen of hun broeders of hun stamgenoten. Zij zijn degenen bij wie Hij het geloof in hun harten heeft geschreven en Hij versterkt hen met hulp van Hem, en Hij doet hen Tuinen (het Paradijs) binnengaan waar de rivieren onder door stromen. Zij zijn daarin eeuwig levenden. Allah heeft welbehagen aan hen en zij hebben welbehagen aan Hem. Zij zijn degenen die van de groep van Allah zijn. Weet: voorwaar, de groep van Allah is de winnaar.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Wat er in de hemelen en op de aarde is prijst de Glorie van Allah. En Hij is de Geweldige, de Alwijze.'),
(5128, 59, 2, 'Hij is Degene Die de ongelovigen onder de mensen van de Schrift uit hun woonplaatsen heeft verdreven bij de eerste verzameling (daartoe). Jullie dachten dat zij niet zouden weggaan en zij dachten dat hun burchten hen zouden beschermen tegen Allah. Toen kwam (de bestraffing van) Allah tot hen van waar zij het niet vermoedden, en Hij wierp grote angst in hun harten. Zij verwoestten hun huizen met hun eigen handen en (die werden verwoest) door de handen van de gelovigen. Trekt daar lering uit, O jullie bezitters van inzicht!'),
(5129, 59, 3, 'En als Allah voor hen de verdrijving niet had bepaald, dan zou Hij hen zeker hebben gestraft tijdens het wereldse leven. En voor hen is er in het Hiernamaals de bestraffing van de Hel.'),
(5130, 59, 4, 'Dat is omdat zij Allah en Zijn Boodschapper tegenwerkten. En wie Allah tegenwerkt: voorwaar, Allah is hard in de bestraffing.'),
(5131, 59, 5, 'Wat jullie omhakken van de dadelpalmen of wat jullie op hun wortels laten staan; het gebeurde met de toestemming van Allah. En zodat Allah de zwaar zondigen vergeldt.'),
(5132, 59, 6, 'Wat Allah aan buit (al Fay) van hen (o.a. de Bani Nadhîr) aan Zijn Boodschapper geeft: jullie hebben daartoe geen paarden en geen lastdieren aangespoord. Maar Allah geeft Zijn Boodschapper macht over wie Hij wil en Allah is Almachtig over alle zaken.'),
(5133, 59, 7, 'Wat Allah ook aan buit aan Zijn Boodschapper gegeven heeft, afkomstig van de bewoners van de steden; het is voor Allah en de Boodschapper; en de verwant; en de wezen; en de armen; en de reiziger zonder proviand, zodat deze (rijkdom) niet rondgaat onder de rijken van jullie. En wat de Boodschapper jullie geeft, neemt dat; maar wat Hij jullie verbiedt, onthoudt jullie daarvan. En vreest Allah: voorwaar, Allah is hard in de bestraffing.'),
(5134, 59, 8, '(De buit is) voor de armen van de uitgewekenen, degenen die zijn verdreven uit hun woonplaatsen en van hun bezittingen, zoekend naar een gunst en welbehagen van Allah. En zij helpen Allah en Zijn Boodschapper. Zij zijn degenen die de waarachtigen zijn.'),
(5135, 59, 9, 'En degenen die vóór hen in de stad (Medinah) woonden en geloofden (de Anshâr), zij houden van degenen die (vanuit Mekkah) naar hen zijn uitgeweken, Zij vinden in hun hart geen jaloezie op wat (aan hen) gegeven is. En zij geven aan (hen) voorrang boven zichzelf, ook al is er behoefte onder hen. En wie zich hoedt voor zijn eigen gierigheid: dat zijn degenen die zullen welslagen.'),
(5136, 59, 10, 'En degenen die na hen kwamen, zeiden: "Onze Heer, vergeef ons en onze broeders die ons zijn voorafgegaan in het geloof en maak in onze harten geen wrok jegens degenen die geloven. Onze Heer, voorwaar, U bent Zachtmoedig, Meest Barmhartige."'),
(5137, 59, 11, 'Zie jij degenen die huichelen niet? Zij zeggen tot hun broeders die ongelovig zijn onder de Lieden van de Schrift: "Als jullie verdreven worden, dan zullen wij met jullie wegtrekken. En wij zullen nooit iemand volgen (tegen jullie). En als jullie bestreden worden, zullen wij jullie zeker helpen." Maar Allah getuigt dat zij zeker leugenaars zijn.'),
(5138, 59, 12, 'Als zij worden verdreven, dan zullen zij niet met hen wegtrekken; en als zij worden bestreden, dan zullen zij hen niet helpen. En als zij hen helpen, dan keren zij (hen) de rug toe, zodat zij niet geholpen worden.'),
(5139, 59, 13, 'Zij hebben in hun hart zeker meer angst voor jullie dan voor Allah. Dat is omdat zij een volk zijn dat niet begrijpt.'),
(5140, 59, 14, 'Zij bestrijden jullie niet gezamenlijk, behalve in versterkte steden of van achter muren. Hun onderling geweld is hevig, jullie denken dat zij een eenheid vormen, maar hun harten zijn verdeeld. Dat is omdat zij een volk zijn dat niet begrijpt.'),
(5141, 59, 15, 'Zoals het geval was met degenen die hen kort daarvoor voorafgingen. Zij hebben het kwaad van hun wandaden geproefd. En voor hen is er een pijnlijke bestraffing.'),
(5142, 59, 16, '(De huichelaars zijn) zoals de Satan, toen hij tot de mens zei: "Wees ongelovig," maar wanneer hij dan ongelovig is, dan zegt hij (de Satan): "Voorwaar, ik ben niet verantwoordelijk voor jullie. Voorwaar, ik vrees de Heer der werelden."'),
(5143, 59, 17, 'Het gevolg voor beiden (de huichelaars en de Joden) zal zijn dat zij in de hel zullen zijn, zij zullen daarin eeuwig levenden zijn. En dat is de vergelding voor de onrechtplegers.'),
(5144, 59, 18, 'O jullie die geloven, vreest Allah en laat iedere ziel toezien op wat zij heeft vooruitgezonden voor de Volgende Dag. En vreest Allah. Voorwaar, Allah is Alziend over wat jullie doen.'),
(5145, 59, 19, 'En weest niet zoals degenen die Allah vergaten, waarop Hij hen zichzelf deed vergeten. Zij zijn degenen die de zwaar zondigen zijn.'),
(5146, 59, 20, 'De bewoners van de hel en de bewoners van het Paradijs zijn niet gelijk. De bewoners van het Paradijs zijn de winnaars.'),
(5147, 59, 21, 'Als Wij deze Koran tot een berg hadden neergezonden, dan had jij deze zich zeker zien onderwerpen en zich splijten uit vrees voor Allah. Dat zijn de voorbeelden die Wij de mens geven. Hopelijk zullen zij zich laten vermanen.'),
(5148, 59, 22, 'Hij is Allah, Degene naast Wie er geen god is dan Hij, de Kenner van het onwaarneembare en het waarneembare, Hij is de Erbarmer, de Meest Barmhartige.'),
(5149, 59, 23, 'Hij is Allah, Degene naast Wie er geen god is dan Hij, de Heerser, de Heilige, de Schenker van veiligheid, de Vertrouwende, de Beschermer, de Almachtige, de Onweerstaanbare Onderwerper, de Trotse. Heilig is Hij boven wat zij Hem toekennen.'),
(5150, 59, 24, 'Hij is Allah, de Schepper, de Voortbrenger, de Vormer, aan Hem behoren de Schone Namen. Wat er in de hemelen en op de aarde is prijst Zijn Glorie. En Hij is de Almachtige, de Alwijze.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'O jullie die geloven, neemt niet Mijn vijanden en jullie vijanden tot vrienden, aan wie jullie genegenheid betonen. Waarlijk, zij geloofden niet in wat tot jullie is gekomen van de Waarheid. Zij hebben de Boodschapper en jullie verdreven, omdat jullie in Allah, jullie Heer, geloven. Als jullie uittrekken, strijdend op Mijn weg, Mijn welbehagen zoekend, (bevriendt hen dan niet). Jullie verheimelijkten jullie genegenheid voor hen, maar Ik weet het beste wat jullie verborgen hielden en wat jullie openbaar maakten. En wie van jullie dit doet: waarlijk, die is afgedwaald van de rechte Weg.'),
(5152, 60, 2, 'Als zij de overhand over jullie krijgen, zullen zij vijanden voor jullie zijn. En zij zullen jullie met het kwaad raken door hun handen en hun tongen, en zij verlangen ernaar dat jullie niet zouden geloven.'),
(5153, 60, 3, 'Jullie bloedverwanten en jullie kinderen zullen jullie niet baten op de Dag der Opstanding. Hij (Allah) maakt een scheiding tussen jullie (en de ongelovigen). En Allah is Alziende over wat jullie doen.'),
(5154, 60, 4, 'Waarlijk, er was voor jullie een goed voorbeeld in Ibrâhîm en degenen die met hem waren, toen zij tot hun volk zeiden: "Wij zijn niet verantwoordelijk voor jullie en voor wat jullie naast Allah aanbidden. Wij geloven jullie niet en er is tussen ons en jullie vijandschap en haat ontstaan, voor altijd, tot jullie in Allah, de Enige geloven." Behalve het woord van Ibrâhîm tegen zijn vader: "Ik zal zeker voor u vergeving vragen, maar ik heb er voor u bij Allah niets over te zeggen." (Zij zeiden:) "Onze Heer, op U vertrouwen wij en tot U wenden wij ons in berouw en tot U is de terugkeer.'),
(5155, 60, 5, 'Onze Heer, maak ons niet tot een beproeving voor degenen die ongelovig zijn en vergeef ons. Onze Heer, voorwaar, U bent de Almachtige, de Alwijze."'),
(5156, 60, 6, 'Voorzeker, er was voor jullie in hen een goed voorbeeld, voor wie op (de beloning van) Allah hoopt en op de Laatste Dag. Maar wie zich afwendt: voorwaar, Allah is de Behoefteloze, de Geprezene.'),
(5157, 60, 7, 'Hopelijk zal Allah tussen jullie en degenen met wie jullie in vijandschap leven genegenheid brengen. En Allah is Almachtig. En Allah is Vergevensgezind, Meest Barmhartig.'),
(5158, 60, 8, 'Allah verbiedt jullie niet om met degenen die jullie niet bestrijden vanwege de godsdienst, en die jullie niet uit jullie woonplaatsen verdrijven, goed en rechtvaardig om te gaan. Voorwaar, Allah houdt van de rechtvaardigen.'),
(5159, 60, 9, 'Allah verbiedt jullie wel degenen te bevrienden die jullie hebben bestreden vanwege de godsdienst, en die jullie hebben verdreven uit jullie woonplaatsen en die (anderen) hebben geholpen om jullie te verdrijven. En wie hen tot vriend neemt: zij zijn degenen die de onrechtplegers zijn.'),
(5160, 60, 10, 'O jullie die geloven, als er gelovige vrouwen, als uitgewekenen, tot jullie gekomen zijn, ondervraagt hen dan. Allah kent hun geloof het beste. Als jullie dan zeker weten dat zij gelovigen zijn, stuurt hen dan niet terug naar de ongelovigen. Zij zijn niet toegestaan voor hen (de ongelovige mannen), en zij (de ongelovige mannen) zijn niet toegestaan voor hen (de gelovige vrouwen). En geeft hun (de ongelovige mannen) wat zij (aan bruidschat) hebben uitgegeven. En er is geen zonde voor jullie als jullie hun hun bruidschat geven om hen te huwen. En houdt niet vast aan de huwelijksbanden met de ongelovige vrouwen. En vraagt terug wat jullie hebben uitgegeven (aan bruidschat aan de ongelovige vrouwen) en laat hen (de ongelovige mannen) vragen om wat zij hebben uitgegeven. Dat is de Wet van Allah, die Hij tussen jullie bepaald heeft. En Allah is Alwetend, Alwijs.'),
(5161, 60, 11, 'En als een van jullie echtgenotes is weggelopen naar de ongelovigen, en jullie (de ongelovigen) vervolgens hebben gestraft, geeft dan (van de oorlogsbuit) aan hen wiens echtgenotes zijn weggelopen, zoveel als zij hebben uitgegeven (aan bruidschat). En vreest Allah, Degene in Wie jullie geloven.'),
(5162, 60, 12, 'O Boodschapper, als de gelovige vrouwen tot jou gekomen zijn om trouw aan jou te zweren, (zwerend) dat zij geen deelgenoot aan Allah toekennen, en niet stelen en geen ontucht plegen en hun kinderen niet vermoorden, en geen leugen verzinnen over wat tussen hun handen en hun voeten is, en dat zij jou niet in het goede ongehoorzaam zijn: aanvaard dan hun trouw en vraag voor hen vergeving aan Allah. Voorwaar, Allah is Vergevensgezind, Meest Barmhartig.'),
(5163, 60, 13, 'O jullie die geloven, neemt geen volk waarop Allah vertoornd is tot vrienden. Waarlijk, zij wanhopen aan het Hiernamaals, zoals de ongelovigen wanhopen aan (de opwekking van) de bewoners van de graven.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Wat er in de hemelen en op de aarde is prijst de Glorie van Allah. En Hij is de Almachtige, de Alwijze.'),
(5165, 61, 2, 'O jullie die geloven, waarom zeggen jullie wat jullie niet doen?'),
(5166, 61, 3, 'Groot is de woede bij Allah dat jullie zeggen wat jullie niet doen.'),
(5167, 61, 4, 'Voorwaar, Allah houdt van degenen die in slagorde strijden op Zijn Weg, alsof zij een hecht gevoegd bouwwerk zijn.'),
(5168, 61, 5, 'En (gedenkt) toen Môesa tot zijn volk zei: "O mijn volk, waarom kwetsen jullie mij terwijl jullie waarlijk weten dat ik voor jullie de Boodschapper van Allah ben?" Toen zij dwaling verkozen liet Allah hun harten dwalen. En Allah leidt het zwaar zondige volk niet.'),
(5169, 61, 6, 'En (gedenkt) toen \'Îsa, de zoon van Maryam, zei: "O Kinderen van Israël, voorwaar, ik ben voor jullie de Boodschapper van Allah, ter bevestiging van wat er vóór mij is van de Taurât en als verkondiger van een verheugende tijding over een Boodschapper die na mij komt, zijn naam is Ahmad. Toen hij dan met de duidelijke bewijzen tot hen kwam, zeiden zei: "Dit is duidelijk toverij."'),
(5170, 61, 7, 'En wie is zondiger dan degene die een leugen over Allah verzonnen heeft, terwijl hij tot de Islam opgeroepen wordt? En Allah leidt het onrechtplegende volk niet.'),
(5171, 61, 8, 'Zij willen het Licht van Allah doven met hun monden, maar Allah vervolmaakt Zijn Licht, ook al haten de ongelovigen het.'),
(5172, 61, 9, 'Hij is Degene Die Zijn Boodschapper heeft gezonden met de Leiding en de ware godsdienst (de Islam) om deze laten zegevieren over alle (vormen van) godsdienst, ook al haten de veelgodenaanbidders het.'),
(5173, 61, 10, 'O jullie die geloven, zal ik jullie een handel tonen die jullie van een pijnlijke bestraffing redden kan?'),
(5174, 61, 11, '(Het is) het geloof in Allah en Zijn Boodschapper geloven en jullie op de Weg van Allah strijden met jullie bezittingen en jullie zielen. Dat is beter voor jullie, als jullie het wisten!'),
(5175, 61, 12, 'Hij vergeeft jullie zonden en doet jullie Tuinen (het Paradijs) binnengaan waar de rivieren onder door stromen. En (Hij schenkt) een goede woonplaats in de Tuin van \'Adn. Dat is de geweldige overwinning.'),
(5176, 61, 13, 'En (Hij schenkt) nog iets waar jullie van houden: hulp van Allah en een nabije overwinning. En verkondig de gelovigen een verteugende tijding.'),
(5177, 61, 14, 'O jullie die geloven, weest helpers van (de godsdienst van) Allah, zoals \'Îsa, de zoon van Maryam, tot de metgezellen zei: "Wie zijn mijn helpers voor (de oproep) tot Allah?" De metgezellen zeiden: "Wij zijn de helpers van Allah." Waarop een deel van de Kinderen van Israël geloofde en een ander deel niet geloofde. Vervolgens versterkten Wij degenen die geloofden tegen hun vijanden zodat zij zegevierders werden.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Wat er in de hemelen en op de aarde is prijst de Glorie van Allah de Heerser, de Heilige, de Almachtige, de Alwijze.'),
(5179, 62, 2, 'Hij is Degene Die bij de ongeletterden een Boodschapper uit hun midden zond, die hun Zijn Verzen voordroeg, en die hen reinigde, en die hun het Boek en de Wijsheid onderwees, terwijl zij daarvoor in duidelijke dwaling verkeerden.'),
(5180, 62, 3, 'En (Allah zond hem) aan anderen onder hen die zich nog niet bij hem hebben gevoegd. En Hij is de Almachtige, de Alwijze.'),
(5181, 62, 4, 'Dat is de gunst van Allah die Hij geeft aan wie Hij wil. En Allah is de Bezitter van de Geweldige Gunst.'),
(5182, 62, 5, 'De gelijkenis van degenen aan wie de Taurât is opgedragen en zich er vervolgens niet aan houden, is als de gelijkenis van een ezel die boeken draagt. Slecht is de gelijkenis van het volk dat de Verzen van Allah loochent. En Allah leidt het onrechtplegende volk niet.'),
(5183, 62, 6, 'Zeg: "O jullie die Jood zijn, als jullie veronderstelden dat jullie de vrienden van Allah waren, met uitsluiting van de (andere) mensen: wenst dan de dood, als jullie waarachtig zijn!"'),
(5184, 62, 7, 'Maar zij zullen die nooit wensen wegens dat wat hun handen vooruit hebben gezonden. En Allah is Alwetend over de onrechtplegers.'),
(5185, 62, 8, 'Zeg: "Voorwaar, de dood die jullie trachten te voorkomen zal jullie zeker vinden, daarna zullen jullie worden teruggevoerd naar de Kenner van het onwaarneembare en het waarneembare en Hij zal jullie dan mededelen wat jullie plachten te doen."'),
(5186, 62, 9, 'O jullie die geloven, wanneer jullie tot het vrijdaggebed worden geroepen, haast jullie dan naar het gedenken van Allah en laat de handel achter. Dat is beter voor jullie, als jullie het wisten!'),
(5187, 62, 10, 'En wanneer de shalât is beëindigd, verspreidt jullie dan op de aarde en zoekt de gunst van Allah, en gedenkt Allah veelvuldig. Hopelijk zullen jullie welslagen.'),
(5188, 62, 11, 'En als zij handel of vermaak zien, dan lopen zij daarheen en laten jou staan (in de shalât). Zeg: "Wat zich bij Allah bevindt is beter dan het vermaak en dan de handel. En Allah is de Beste der Voorzieners."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Wanneer de huichelaars tot jou komen, dan zeggen zij: "Wij getuigen dat jij zeker de Boodschapper van Allah bent." En Allah weet dat jij zeker Zijn Boodschapper bent en Allah getuigt dat de huichelaars zeker leugenaars zijn.'),
(5190, 63, 2, 'Zij hebben hun eden tot een schild gemaakt en zij houden af van de Weg van Allah. Voorwaar, slecht is het wat zij plachten te doen.'),
(5191, 63, 3, 'Dat is omdat zij geloofden en daarna ongelovig werden, waarna hun harten verzegeld werden, zodat zij niet begrijpen.'),
(5192, 63, 4, 'En wanneer jij hen ziet, wekken hun lichamen (hun uiterlijk vertoon) jouw verwondering op; en wanneer zij spreken, dan luister jij naar hun woorden. Zij zijn als balken die gestut worden. Zij denken dat elke schreeuw tot hen gericht is. Zij zijn de vijanden, kijk daarom uit voor hen. Allah vervloekt hen. Hoe kunnen zij misleid worden?'),
(5193, 63, 5, 'En als tot hen gezegd wordt: "Komt, dan zal de Boodschapper van Allah vergeving voor jullie vragen," dan buigen zij hun hoofden en zie jij hen (de verkondiging) tegenhouden, terwijl zij hoogmoedig zijn.'),
(5194, 63, 6, 'Het is voor hen hetzelfde of jij voor hen vergeving vraagt, of dat jij geen vergeving voor hen vraagt: Allah zal hen nooit vergeven. Voorwaar, Allah leidt het zwaar zondige volk niet.'),
(5195, 63, 7, 'Zij zijn degenen die zeggen: "Geeft geen bijdragen aan hen die met de Boodschapper van Allah zijn, zodat zij weglopen (van de Profeet.)" Aan Allah behoren de schatten van de hemelen en de aarde, maar de huichelaars begrijpen het niet'),
(5196, 63, 8, 'Zij zeggen: "Als wij naar Medinah terugkeren, dan zal het sterke zeker het zwakke daaruit verdrijven." En alle macht behoort aan Allah en aan Zijn Boodschapper en aan de gelovigen, maar de huichelaars weten het niet.'),
(5197, 63, 9, 'O jullie die geloven, laat jullie bezittingen en jullie kinderen jullie niet afleiden van het gedenken van Allah. En wie dat doen: zij zijn degenen die de verliezers zijn.'),
(5198, 63, 10, 'En geeft bijdragen van waar Wij jullie mee voorzien hebben, voordat de dood tot een van jullie komt, en deze dan zal zeggen: "Mijn Heer, Had U mij maar een korte tijd uitstel gegeven, dan zou ik uitgeven aan liefdadigheid en tot de rechtschapenen behoren."'),
(5199, 63, 11, 'Maar Allah geeft geen ziel ooit uitstel als haar tijd is gekomen. En Allah is Alwetend over wat jullie doen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Wat er in de hemelen en op de aarde is prijst de Glorie van Allah. Aan Hem behoort de heerschappij en aan Hem behoort de lofprijzing. En Hij is Almachtig over alle zaken.'),
(5201, 64, 2, 'Hij is Degene Die jullie geschapen heeft, en onder jullie zijn er ongelovigen en onder jullie zijn gelovigen. En Allah is Alziende over wat jullie doen.'),
(5202, 64, 3, 'Hij heeft de hemelen en de aarde in Waarheid geschapen en Hij heeft jullie gevormd en jullie vorm nauwkeurig gemaakt. En tot Hem is de terugkeer.'),
(5203, 64, 4, 'Hij kent wat er in de hemelen en op de aarde is en Hij kent wat jullie verbergen en wat jullie openlijk doen. En Allah is Alwetend over wat zich in de harten bevindt.'),
(5204, 64, 5, 'Is het bericht over degenen die vroeger ongelovig waren niet tot jullie gekomen? Die toen het kwaad van hun wandaden geproefd hebben? En voor hen is er een pijnlijke bestraffing.'),
(5205, 64, 6, 'Dat is omdat zij zeiden, toen hun Boodschappers met de duidelijke bewijzen tot hen kwamen: "Zullen mènsen ons leiden?" Waarop zij ongelovig werden en zich afwendden. Maar Allah heeft (hun geloof) niet nodig en Allah is Behoefteloos, Geprezen.'),
(5206, 64, 7, 'Degenen die ongelovig zijn veronderstellen dat zij niet opgewekt zullen worden. Zeg: "Welzeker, bij mijn Heer! Jullie zullen zeker opgewekt worden en vervolgens zullen jullie op de hoogte gebracht worden van wat jullie bedreven hebben. En dat is voor Allah gemakkelijk.'),
(5207, 64, 8, 'Gelooft daarom in Allah en Zijn Boodschapper en het Licht (de Koran) dat Wij hebben neergezonden. En Allah is Alwetend over wat jullie doen.'),
(5208, 64, 9, '(Gedenkt) de Dag waarop Hij jullie zal bijeenbrengen voor de Dag van de samenkomst. Dat is de Dag van de oplichterij. En wie dan in Allah gelooft en goede daden verricht: Hij (Allah) zal zijn zonden uitwissen en hem Tuinen (het Paradijs) doen binnengaan waar de rivieren onder door stromen. Zij zijn daarin voor altijd eeuwig levenden, dat is de geweldige overwinning.'),
(5209, 64, 10, 'Maar degenen die ongelovig waren en Onze Verzen loochenden: zij zijn degenen die de bewoners van de Hel zijn, zij zijn daarin eeuwig levenden. En slecht is de plaats van terugkeer!'),
(5210, 64, 11, 'Er is niemand die een ramp overkomt, of deze gebeurt met het verlof van Allah. En wie in Allah gelooft: Hij zal diens hart leiden. En Allah is Alwetend over alle zaken.'),
(5211, 64, 12, 'En gehoorzaamt Allah en gehoorzaamt de Boodschapper. Als jullie je afwenden, dan is aan Onze Boodschapper slechts de duidelijke verkondiging.'),
(5212, 64, 13, 'Allah! Er is geen god dan Hij. En laat daarom de gelovigen op Allah vertrouwen.'),
(5213, 64, 14, 'O jullie die geloven, voorwaar, er zijn er onder jullie vrouwen en jullie kinderen die vijanden voor jullie zijn. Kijk daarom voor hen uit. En als jullie kwijtschelden en het niet aanrekenen en vergeven, dan is Allah waarlijk Vergevensgezind en Meest Barmhartig.'),
(5214, 64, 15, 'Voorwaar, jullie bezittingen en jullie kinderen zijn slechts een beproeving. En bij Allah is een geweldige beloning.'),
(5215, 64, 16, 'Vreest daarom Allah volgens jullie vermogen; en luistert en gehoorzaamt; en geeft bijdragen, het is beter voor jullie zelf. En wie wordt behoed voor zijn eigen gierigheid: zij zijn degenen die de welslagenden zijn.'),
(5216, 64, 17, 'Als jullie Allah een goede lening geven, dan zal Hij die voor jullie vermenigvuldigen en jullie vergeven. En Allah is Waarderend, Zachtmoedig.'),
(5217, 64, 18, 'De Kenner van het onwaarneembare en het waarneembare, de Almachtige, de Alwijze.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'O Profeet als jullie van de vrouwen scheiden, scheidt dan van hen met inachtneming van hun perioden, en berekent de perioden (precies) en vreest Allah, jullie Heer. Verwijdert hen niet uit hun huizen en zij mogen (deze) niet verlaten, behalve wanneer zij duidelijk zedeloosheden begaan. Dat zijn de Wetten van Allah en wie de Wetten van Allah overschrijdt, die heeft waarlijk zichzelf onrecht aangedaan. Jij weet niet of Allah misschien daarna een nieuwe omstandigheid zal doen ontstaan.'),
(5219, 65, 2, 'Wanneer zij dan hun termijn bereikt hebben, behoudt hen dan volgens de voorschriften of scheidt van hen volgens de voorschriften, en neemt twee rechtvaardige personen als getuigen. En weest oprecht in de getuigenis voor Allah. Dat is waartoe jullie gemaand worden, (voor) wie gelooft in Allah en in de Laatste Dag. En wie Allah vreest die zal Hij een oplossing geven.'),
(5220, 65, 3, 'En Hij voorziet hem van waar hij het niet verwacht, en (voor) wie op Allah vertrouwt, is Hij voldoende. Voorwaar, Allah voert Zijn zaak uit. Waarlijk, Allah heeft voor alle zaken een maatgeving bepaald.'),
(5221, 65, 4, 'En voor hen die (gezien hun leeftijd) onzeker zijn omtrent de menstruatie van jullie vrouwen; als jullie (mannen) twijfelen is hun wachttijd drie maanden. En (dit geldt ook voor) hen die nog geen menstruatie hebben. Voor hen die zwanger zijn geldt de wachttijd totdat zij hebben gebaard wat zij dragen. En wie Allah vreest, voor hem zal Hij zijn zaak makkelijk maken.'),
(5222, 65, 5, 'Dat is het bevel van Allah, dat Hij aan jullie neerzendt. En wie Allah vreest, voor hem zal Hij zijn zonden uitwissen en hem een geweldige beloning geven.'),
(5223, 65, 6, 'Laat hen (gedurende de wachttijd) wonen zoals jullie zelf wonen, naar jullie vermogens, en kwelt hen niet om hen in het nauw te drijven. En als zij zwanger zijn, voorziet hun dan tot zij gebaard hebben wat zij dragen. En als zij (de kinderen) voor jullie zogen, geeft hun dan hun vergoeding en pleegt onderling overleg op een redelijke wijze. En als jullie moeilijkheden ondervinden, laat dan een andere (vrouw het kind) voor hem zogen.'),
(5224, 65, 7, 'Laat de welvarende besteden volgens zijn welvaart; en degene wiens voorzieningen beperkt zijn, laat hem besteden van wat Allah hem heeft gegeven. Allah belast geen ziel dan volgens wat Hij haar heeft gegeven. Allah zal na moeilijkheden gemak schenken.'),
(5225, 65, 8, 'En hoeveel steden zijn er niet in opstand gekomen tegen het bevel van hun Heer en Zijn Boodschappers, waarop Wij met hen afrekenden met een harde afrekening. En Wij hebben hen met een verschrikkelijke bestraffing bestraft.'),
(5226, 65, 9, 'Zij proefden toen het kwaad van hun wandaden, en het einde van hun zaak was een verlies.'),
(5227, 65, 10, 'Allah heeft voor hen een harde bestraffing bereid. Vreest daarom Allah, O bezitters van verstand die geloven! Waarlijk, Allah heeft tot jullie een Vermaning gezonden.'),
(5228, 65, 11, 'Een Boodschapper die voor jullie de verduidelijkende Verzen van Allah voordraagt, om degenen die geloven en goede daden verrichten uit de duisternissen naar het licht te voeren. En wie in Allah gelooft en goede daden verricht: Hij zal hem Tuinen (het Paradijs) doen binnengaan waar de rivieren onder door stromen. Zij zijn daarin eeuwig levenden, voor altijd. Allah heeft hem waarlijk een goede voorziening gegeven.'),
(5229, 65, 12, 'Allah is Degene Die de zeven hemelen heeft geschapen en zo ook de aarde. De beschikking daalt tussen hen (hemel en aarde) neer, opdat jullie weten dat Allah de Almachtige over alle zaken is en dat Allah alle zaken in kennis omvat.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'O Profeet, waarom verbied iets wat Allah jou heeft toegestaan, om het welbehagen van jouw vrouwen te verkrijgen? En Allah is Vergevensgezind en Meest Barmhartig.'),
(5231, 66, 2, 'Allah heeft jullie waarlijk verplicht om jullie eden toegestaan te maken. En Allah is jullie Meester. En Hij is de Alwetende, de Alwijze.'),
(5232, 66, 3, 'En (gedenkt) toen de Boodschapper in het geheim een gebeurtenis aan één van zijn vrouwen toevertrouwde. En toen zij dit vertelde en Allah dit aan hem openbaar maakte, maakte hij er een gedeelte van bekend en liet een er gedeelte van onbesproken. Toen hij het haar vertelde, zei zij: "Wie heeft jou dit verteld?" Hij zei: "De Kenner, de Alwetende, heeft mij op de hoogte gebracht."'),
(5233, 66, 4, 'Als jullie beiden (Hafsah en Â\'isjah) Allah berouw tonen (is dat beter voor jullie), dan neigen jullie harten waarlijk (naar het goede). Maar als jullie elkaar bijstaan tegen hem (de Boodschapper), dan is Allah waarlijk jouw Helper, en Djibrîl en de rechtschapen gelovigen en daarnaast de Engelen zullen helpers zijn.'),
(5234, 66, 5, 'Het kan zijn dat wanneer hij van jullie (zijn echtgenotes) scheidt, dat zijn Heer aan hem betere echtgenotes in de plaats van jullie zal geven, die zich overgeven, die gelovend, gehoorzamend, berouwvol, aanbiddend en vastend zijn, die eerder getrouwd zijn geweest, of maagd zijn.'),
(5235, 66, 6, 'O jullie die geloven, behoedt julliezelf en jullie gezinsleden voor de Hel, die als brandstof mensen en stenen heeft, waarover strenge en hard optredende Engelen zijn aangesteld, die Allah niet ongehoorzaam zijn in wat Hij hun beveelt, en die uitvoeren wat hun is bevolen.'),
(5236, 66, 7, 'O jullie die ongelovig zijn, verontschuldigt jullie op die Dag niet: jullie worden slechts vergolden naar wat jullie hebben gedaan.'),
(5237, 66, 8, 'O jullie die geloven, toont Allah oprecht berouw, hopelijk zal jullie Heer jullie zonden uitwissen en jullie Tuinen (het Paradijs) doen binnengaan waar de rivieren onder door stromen op de Dag waarop Allah de Profeet en degenen die met hem geloven niet zal vernederen. Hun licht straalt vóór hen en rechts van hen. Zij zeggen: "Onze Heer, vervolmaak voor ons ons licht en vergeef ons: voorwaar, U bent Almachtig over alle zaken."'),
(5238, 66, 9, 'O Profeet, bestrijd de ongelovigen en de huichelaars en treed hard tegen hen op. Hun bestemming is de Hel, en dat is de slechtste plaats van bestemming.'),
(5239, 66, 10, 'Allah heeft degenen die niet geloven de vrouw van Nôeh en de vrouw van Lôeth als een voorbeeld gegeven. Zij waren onder de hoede van twee rechtschapenen van onder Onze dienaren, maar zij verraadden hen, toen baatten zij (Nôeh en Lôeth) hun in niets tegen Allah. En er werd tot hen (de beide vrouwen) gezegd: "Treedt de hel binnen, tezamen met de binnentreders."'),
(5240, 66, 11, 'En de gelovigen heeft Allah de vrouw van Fir\'aun als voorbeeld gegeven, toen zij zei: "Mijn Heer, bouw voor mij een huis aan Uw Zijde in het Paradijs, en red mij van Fir\'aun en zijn deden en red mij van het onrechtvaardige volk."'),
(5241, 66, 12, 'En (het voorbeeld van) Maryam, de dochter van Imrân, die haar eerbaarheid bewaard had, waarop Wij in haar van Onze geest bliezen en zij getuigde van de Waarheid van de Woorden van haar Heer en Zijn Schriften en zij behoorde tot de gehoorzamen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Gezegend is Degene in Wiens Hand de heerschappij is. En Hij is Almachtig over alle zaken.'),
(5243, 67, 2, 'Degenen Die de dood en het leven heeft geschapen om jullie te beproeven, (en te tonen) wie van jullie de beste daden verricht. En Hij is de Almachtige, de Vergevensgezinde.'),
(5244, 67, 3, 'Degene Die de hemel in lagen heeft geschapen. Jij ziet in de schepping van de Erbarmer geen onevenwichtigheid. Kijk dan nog een keer, zie jij een afwijking?'),
(5245, 67, 4, 'Kijk dan nog eens twee maal, jij zult jouw ogen nederig neerslaan, terwijl zij vermoeid zijn.'),
(5246, 67, 5, 'En voorzeker, Wij hebben de nabije hemel met lampen (sterren) gesierd en Wij maakten die om er de Satans mee te stenigen. En Wij hebben voor hen de bestraffing van Sa\'îr (de Hel) bereid.'),
(5247, 67, 6, 'En voor degenen die niet in hun Heer geloven, is er de bestraffing van de Hel. En dat is de slechtste plaats van bestemming!'),
(5248, 67, 7, 'En wanneer zij daarin geworpen worden, dan horen zij een afschrikwekkend gebrul ervan, terwijl zij (de Hel) raast.'),
(5249, 67, 8, 'Haast barst zij van woede. Telkens wanneer een groep erin wordt geworpen, vragen de wakers ervan hen: "Is er geen waarschuwer tot jullie gekomen?"'),
(5250, 67, 9, 'Zij zeggen: "Welzeker, er is waarlijk een waarschuwer tot ons gekomen, maar toen loochenden wij. En wij zeiden: "Allah heeft niets neergezonden, jullie verkeren slechts in grote dwaling."'),
(5251, 67, 10, 'Zij zeiden: "Als wij (het) konden horen of begrijpen, dan zouden wij niet tot de bewoners van Sa\'îr (de Hel) behoren!"'),
(5252, 67, 11, 'Zij bekennen dan hun zonden. Daarom, ten onder gaan de bewoners van Sa\'îr (de Hel)!'),
(5253, 67, 12, 'Voorwaar, voor degenen die hun Heer in het verborgene vrezen, voor hen is er vergeving en een grote beloning.'),
(5254, 67, 13, 'En houdt jullie woorden geheim of maakt ze openbaar: voorwaar, Hij is Alwetend over wat er in de harten is.'),
(5255, 67, 14, 'Zou Hij die schiep geen kennis hebben (over het geschapene)? Hij is de Zachtmoedige, de Alwetende.'),
(5256, 67, 15, 'Hij is Degene Die de aarde voor jullie begaanbaar heeft gemaakt. Wandelt dan over haar zijden en eet van Zijn voorzieningen. En tot Hem is de opwekking.'),
(5257, 67, 16, 'Voelen jullie je er veilig voor dat Hij Die in de hemel is, jullie niet zal doen wegzinken in de aarde? Zij begint al te schudden!'),
(5258, 67, 17, 'Of voelen jullie je er veilig voor dat Hij Die in de hemel is, niet een vulkanische regen over jullie zal zenden? Jullie zullen dan weten hoe (terecht) Mijn waarschuwing was.'),
(5259, 67, 18, 'En voorzeker, degenen die er vóór hen (de Boodschappers) waren loochenden, en hoe (verschrikkelijk) was toen Mijn verafschuwing.'),
(5260, 67, 19, 'En kijken jullie niet naar de vogels boven jullie, hoe zij hun vleugels uitslaan en weer ineenvouwen? Er is niemand die ze tegenhoudt dan de Erbarmer. Voorwaar, Hij is Alziende over alle zaken.'),
(5261, 67, 20, 'Of wie is het die voor jullie als een leger is en die jullie helpt naast de Erbarmer? De ongelovigen verkeren slechts in dwaling,'),
(5262, 67, 21, 'Of wie is het die jullie voorziet als Hij Zijn voorzieningen tegenhoudt? Nee, zij volharden in hoogmoed en afschuw.'),
(5263, 67, 22, 'Is hij dan, die voortkruipt met zijn gezicht over de grond, beter geleid dan hij die rechtop op het rechte Pad loopt?'),
(5264, 67, 23, 'Zeg: "Hij is Degene Die jullie heeft doen ontstaan en voor jullie het gehoor, het gezichtsvermogen en de harten heeft gemaakt. Weinig is de dankbaarheid die jullie tonen."'),
(5265, 67, 24, 'Zeg: "Hij is Degene Die jullie op de aarde vermenigvuldigd heeft en tot Hem zullen jullie verzameld worden."'),
(5266, 67, 25, 'En zij zeggen: "Wanneer zal deze aanzegging plaatsvinden, als jullie waarachtigen zijn?"'),
(5267, 67, 26, 'Zeg: "De kennis daarover is slechts bij Allah en ik ben slechts een duidelijke waarschuwer."'),
(5268, 67, 27, 'Wanneer zij dan (de bestraffing) van dichtbij zien, worden de gezichten van degenen die niet geloven bedroefd, en er zal worden gezegd: "Dit is waar jullie om plachten te vragen."'),
(5269, 67, 28, 'Zeg: "Wat dachten jullie, als Allah mij en wie met mij zijn vernietigt of als Hij ons begenadigt, wie beschermt dan de ongelovigen tegen een pijnlijke bestraffing?"'),
(5270, 67, 29, 'Zeg: "Hij is de Erbarmer, Wij geloven in Hem en wij vertrouwen op Hem." Jullie zullen weten wie in duidelijke dwaling verkeert."'),
(5271, 67, 30, 'Zeg: "Wat dachten jullie, als jullie water plotseling in de aarde verdwijnt, wie zal jullie dan stromend water brengen?"');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Nôen. Bij de pen en wat zij schrijven.'),
(5273, 68, 2, 'Jij bent dankzij de gunst van jouw Heer geen bezetene.'),
(5274, 68, 3, 'En voorwaar, voor jou is er zeker een beloning zonder onderbreking.'),
(5275, 68, 4, 'En voorwaar, jij beschikt over een hoogstaand karakter.'),
(5276, 68, 5, 'Jullie zullen zien en zij zullen zien.'),
(5277, 68, 6, 'Wie van jullie de (met bezetenheid) beproefde is.'),
(5278, 68, 7, 'Voorwaar, jouw Heer weet het beste wie van Zijn Weg is afgedwaald en Hij kent het beste de rechtgeleiden.'),
(5279, 68, 8, 'Gehoorzaam daarom de loochenaars niet.'),
(5280, 68, 9, 'Zij wensen dat jij toegeeflijk zal worden, dan zullen zij (ook) toegeeflijk worden.'),
(5281, 68, 10, 'En gehoorzaam geen enkele verachtelijke edenzweerder.'),
(5282, 68, 11, 'Een lasteraar die rondloopt met roddelpraat.'),
(5283, 68, 12, 'Een tegenhouder van het goede, buitensporig, zondig.'),
(5284, 68, 13, 'Die daarnaast ook nog een bruut is, en bastaard.'),
(5285, 68, 14, '(Hij is ongelovig) omdat hij een bezitter van rijkdom en zonen is.'),
(5286, 68, 15, 'Wanneer Onze Verzen aan hem voorgedragen worden, dan zegt hij: "Fabels van de vroegeren."'),
(5287, 68, 16, 'Wij zullen hem brandmerken op zijn snuit.'),
(5288, 68, 17, 'Wij hebben hen beproefd zoals Wij de bezitters van de tuin hebben beproefd, toen zij zwoeren in de morgen van haar (vruchten) te zullen plukken.'),
(5289, 68, 18, 'Maar zij maakten geen voorbehoud.'),
(5290, 68, 19, 'Toen ging er een bezoeking (storm) van jouw Heer in haar rond, terwijl zij sliepen.'),
(5291, 68, 20, 'Zij werd als een verschroeid stoppelveld.'),
(5292, 68, 21, 'Toen riepen zij tot elkaar in de ochtend.'),
(5293, 68, 22, '"Gaat deze ochtend naar jullie akkers, als jullie van plan zijn om te oogsten."'),
(5294, 68, 23, 'Zo vertrokken zij, terwijl zij naar elkaar fluisterden.'),
(5295, 68, 24, '(Zij zeiden:) "Laat er deze dag geen enkele arme bij jullie binnengaan."'),
(5296, 68, 25, 'En zij vertrokken die ochtend, vastbesloten om (de armen) te weren.'),
(5297, 68, 26, 'Maar toen zij haar (de tuin) zagen, zeiden zij: "Voorwaar, wij zijn zeker dwalenden.'),
(5298, 68, 27, 'Wij zijn zelfs beroofd."'),
(5299, 68, 28, 'De meest wijze onder hen zei: "Heb ik jullie niet gezegd dat jullie de Glorie (van Allah) hadden moeten prijzen!"'),
(5300, 68, 29, 'Zij zeiden: "Heilig is onze Heer: voorwaar, wij waren onrechtvaardig."'),
(5301, 68, 30, 'Toen keerde de ene groep zich tegen de andere, elkaar verwijten makend.'),
(5302, 68, 31, 'Zij zeiden: "Wee ons! Voorwaar, wij waren buitensporig.'),
(5303, 68, 32, 'Hopelijk zal onze Heer ons een betere (tuin) in de plaats van deze geven: voorwaar, wij hopen vurig (op vergeving) van onze Heer."'),
(5304, 68, 33, 'Zo was de bestraffing. En de bestraffing in het Hiernamaals is zeker groter, als zij het zouden weten!'),
(5305, 68, 34, 'Voorwaar, voor de Moettaqôen zijn er bij hun Heer Tuinen van gelukzaligheid (het Paradijs).'),
(5306, 68, 35, 'Zullen Wij hen die zich aan Allah hebben overgegeven net zo behandelen als de misdadigers?'),
(5307, 68, 36, 'Wat is er met jullie? Hoe oordelen jullie?'),
(5308, 68, 37, 'Of hebben jullie een Schrift waar jullie in kunnen studeren?'),
(5309, 68, 38, 'Waar waarlijk voor jullie in is wat jullie kiezen?'),
(5310, 68, 39, 'Of hebben jullie een verdrag met Ons dat tot aan de Dag der Opstanding geldt, dat er waarlijk voor jullie is wat jullie oordelen?'),
(5311, 68, 40, 'Vraag hun wie van hen daarvoor verantwoordelijk is.'),
(5312, 68, 41, 'Of beschikken zij over deelgenoten? Laat zij dan komen met kun deelgenoten, als zij waarachtig zijn.'),
(5313, 68, 42, '(Gedenkt) de Dag waarop de onderbenen ontbloot zullen worden en zij opgeroepen worden om neer te knielen, terwijl zij daartoe niet in staat zijn.'),
(5314, 68, 43, 'Hun ogen zullen angstig teneergeslagen zijn, vernedering zal hen bedekken. En waarlijk, zij werden opgeroepen om zich neer te knielen, terwijl zij (nog) gezond waren.'),
(5315, 68, 44, 'Laat daarom degene die deze Boodschap loochent aan Mij over. Wij zullen hen langzaam maar zeker vernietigen, op een manier dat zij het niet merken.'),
(5316, 68, 45, 'En ik zal kun uitstel geven. Voorwaar, Mijn plan is sterk.'),
(5317, 68, 46, 'Of vraag jij van hen een beloning, zodat zij met een schuld worden belast?'),
(5318, 68, 47, 'Of is bij hen (kennis) van het onwaarneembare, zodat zij het kunnen opschrijven?'),
(5319, 68, 48, 'Wees daarom geduldig tot het Oordeel van jouw Heer komt, en wees niet als de Profeet Yôenoes, toen hij (tot Allah) riep terwijl hij verbolgen was.'),
(5320, 68, 49, 'Als hem van zijn Heer geen genade bereikt had, dan was hij zeker op een kwade plaats neer gesmeten, met verwijten beladen.'),
(5321, 68, 50, 'Daarna verkoos zijn Heer hem en maakte Hij hem tot één van de rechtschapenen.'),
(5322, 68, 51, 'En bijna zouden degenen die niet geloven jou omwerpen met hun blikken, wanneer zij de Vermaning horen. En zij zeggen: "Voorwaar, hij is zeker bezeten."'),
(5323, 68, 52, 'Terwijl hij (de Koran) niets anders is dan een Vermaning voor de werelden.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'De verwezenlijking (de Dag der Opstanding).'),
(5325, 69, 2, 'Wat is de verwezenlijking?'),
(5326, 69, 3, 'En wat doet jou weten wat de verwezenlijking is?'),
(5327, 69, 4, 'De Tsamôed en de \'Âd loochenden de ramp (de Dag der Opstanding).'),
(5328, 69, 5, 'Wat de Tsmôed betreft: zij werden vernietigd door een geweldige kracht.'),
(5329, 69, 6, 'En wat betreft de \'Âd: zij werden vernietigd door een razende, beukende wind.'),
(5330, 69, 7, 'Hij (Allah) liet haar tegen hen woeden, gedurende zeven nachten en acht dagen, achtereenvolgend, waarop jij het volk daar had kunnen zien liggen, alsof zij geveld waren als palmstammen.'),
(5331, 69, 8, 'Zie jij dan nog iemand van hen die is overgebleven?'),
(5332, 69, 9, 'En Fir\'aun en degenen die er vóór hem waren en de (bewoners van) de op hun fundamenten gekeerde steden pleegden grote zonden.'),
(5333, 69, 10, 'En zij waren ongehoorzaam aan de Boodschapper van hun Heer. Toen groep Hij hen met een krachtige bestraffing.'),
(5334, 69, 11, 'Voorwaar, toen het water overstroomde, droegen Wij jullie (voorvader Nôeh en zijn familie) in het vaartuig (de ark).'),
(5335, 69, 12, 'Opdat Wij dit voor jullie tot een vermaning zouden maken en opdat een aandachtig oor er aandacht aan zou schenken.'),
(5336, 69, 13, 'Wanneer dan op de bazuin geblazen wordt met één stoot.'),
(5337, 69, 14, 'En de aarde en de bergen worden opgetild en dan in één klap worden verpulverd.'),
(5338, 69, 15, 'Op die Dag zal de gebeurtenis plaatsvinden.'),
(5339, 69, 16, 'En de hemel zal splijten, dan wordt zij broos.'),
(5340, 69, 17, 'En de Engelen zullen zich op haar randen bevinden en acht (van hen) zullen op die Dag de Troon van jouw Heer boven zich dragen.'),
(5341, 69, 18, 'Op die Dag zullen jullie voorgeleid worden, geen van jullie geheimen zal verborgen blijven.'),
(5342, 69, 19, 'Wat betreft degene die dan zijn boek in zijn rechterhand gegeven zal worden, hij zal zeggen: "Neemt, en leest mijn boek voor.'),
(5343, 69, 20, 'Voorwaar, ik was ervan overtuigd dat ik mijn afrekening zou ontmoeten."'),
(5344, 69, 21, 'Hij zal dan een leven van welbehagen leiden.'),
(5345, 69, 22, 'In een hooggelegen Tuin (het Paradijs).'),
(5346, 69, 23, 'Haar vruchten hangen nabij.'),
(5347, 69, 24, '(Er wordt gezegd:) "Eet en drinkt smakelijk wegens wat jullie hebben verricht in de vroegere dagen."'),
(5348, 69, 25, 'En wat betreft degene die zijn boek in zijn linkerhand gegeven zal worden, hij zal zeggen: "Wee mij! Was mijn boek maar niet (aan mij) gegeven!'),
(5349, 69, 26, 'En ik weet niet hoe mij afrekening zal zijn.'),
(5350, 69, 27, 'Was de dood maar de beëindiger van alles.'),
(5351, 69, 28, 'Mijn bezittingen baten mij niet.'),
(5352, 69, 29, 'Mijn macht is van mij heengegaan."'),
(5353, 69, 30, '(Allah zegt:) "Grijpt hem en bindt zijn handen om zijn nek.'),
(5354, 69, 31, 'En doet hem in Djahîm (de Hel) binnengaan.'),
(5355, 69, 32, 'Voert hem daarna binnen in ketenen waarvan de lengte zeventig ellen is.\'\''),
(5356, 69, 33, 'Voorwaar, hij geloofde niet in Allah, de Geweldige.'),
(5357, 69, 34, 'En hij moedigde niet aan tot het voeden van de armen.'),
(5358, 69, 35, 'Op deze Dag heeft hij hier geen trouwe vriend.'),
(5359, 69, 36, 'En er is geen voedsel den etter.'),
(5360, 69, 37, 'Niemand eet dat dan de zondaren.'),
(5361, 69, 38, 'Zo waarlijk zweer Ik bij wat jullie zien.'),
(5362, 69, 39, 'En bij wat jullie niet zien.'),
(5363, 69, 40, 'Voorwaar, het is zeker het Woord (verkondigd door) een nobele Boodschapper.'),
(5364, 69, 41, 'En het is niet het woord van een dichter. Weinig is het dat jullie geloven.'),
(5365, 69, 42, 'En het is niet het woord van een waarzegger. Weinig is de lering die jullie er uit trekken.'),
(5366, 69, 43, '(Het is) een neerzending van de Heer der Werelden.'),
(5367, 69, 44, 'En als hij (Moehammad) een paar woorden had verzonnen in Onze Naam.'),
(5368, 69, 45, 'Dan zouden Wij hem met kracht gegrepen hebben.'),
(5369, 69, 46, 'En dan zouden Wij zijn hartslagader doorgesneden hebben.'),
(5370, 69, 47, 'En niemand van jullie zou dat voor hem kunnen verhinderen.'),
(5371, 69, 48, 'En voorwaar, hij (de Koran) is zeker een Vermaning voor de Moettaqôen.'),
(5372, 69, 49, 'En voorwaar, Wij kennen zeker de loochenaars onder jullie.'),
(5373, 69, 50, 'En voorwaar, hij (de Koran) zal voor de ongelovigen zeker een oorzaak van spijt zijn.'),
(5374, 69, 51, 'En voorwaar, het is een zekere Waarheid.'),
(5375, 69, 52, 'Prijs daarom de Naam van jouw Heer, de Geweldige.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Een vraagsteller vroeg over een bestraffing die vallen zal.'),
(5377, 70, 2, 'Voor de ongelovigen is er geen afweer tegen.'),
(5378, 70, 3, '(Die komt) van Allah, de Bezitter van de trappen.'),
(5379, 70, 4, '(Waarvandaan) de Engelen en de Geest (Djibrîl) tot Hem opstijgen in een dag waarvan de maat vijftigduizend jaren is.'),
(5380, 70, 5, 'Volhard daarom geduldig op gepaste wijze.'),
(5381, 70, 6, 'Voorwaar, zij zien haar (de bestraffing) van ver weg.'),
(5382, 70, 7, 'Maar Wij zien haar van nabij.'),
(5383, 70, 8, 'Op die Dag zal de hemel als gesmolten metaal zijn.'),
(5384, 70, 9, 'En zullen de bergen als (vlokken) wol zijn.'),
(5385, 70, 10, 'En geen trouwe vriend zal naar een (andere) trouwe vriend vragen.'),
(5386, 70, 11, 'Zij kijken naar elkaar. De misdadiger zal wensen dat hij zich van de bestraffing van die Dag kan vrijkopen met zijn kinderen.'),
(5387, 70, 12, 'En met zijn vrouw en zijn broeder.'),
(5388, 70, 13, 'En zijn bloedverwanten die hem verzorgden.'),
(5389, 70, 14, 'En (hij wenst dat) allen die er op aarde zijn hem dan redden.'),
(5390, 70, 15, 'Nee, beslist niet! Voorwaar, zij is de Lazhâ (de Hel).'),
(5391, 70, 16, 'Die de hoofdhuid wegrukt.'),
(5392, 70, 17, 'Zij roept wie zijn rug toekeerde en zich afwendde.'),
(5393, 70, 18, 'Die (rijkdommen) verzamelde en achterhield.'),
(5394, 70, 19, 'Voorwaar, de mens is onstandvastig geschapen.'),
(5395, 70, 20, 'Als het kwade hem treft is hij teneergeslagen.'),
(5396, 70, 21, 'En als het goede hem overkomt, is hij gierig.'),
(5397, 70, 22, 'Behalve degenen die de shalât verrichten.'),
(5398, 70, 23, 'Degenen die hun shalât blijven onderhouden.'),
(5399, 70, 24, 'En degenen in wier bezittingen een rechtmatig deel is.'),
(5400, 70, 25, 'Voor de bedelaar en de behoeftige die niet bedelt.'),
(5401, 70, 26, 'En degenen die van de Dag des Oordeels overtuigd zijn.'),
(5402, 70, 27, 'En degenen die de bestraffing van hun Heer vrezen.'),
(5403, 70, 28, 'Voorwaar, voor de bestraffing van hun Heer is niemand veilig.'),
(5404, 70, 29, 'En degenen die over hun kuisheid waken.'),
(5405, 70, 30, 'Behalve bij hun vrouwen en de slaven waarover zij beschikken, er wordt hen dan niets verweten.'),
(5406, 70, 31, 'Wie dan daarnaast nog iets zoekt: zij zijn degenen die de overtreders zijn.'),
(5407, 70, 32, 'En degenen die over het hun toevertrouwde (Amânah) en hun beloften waken.'),
(5408, 70, 33, 'En degenen die trouw zijn bij hun getuigenissen.'),
(5409, 70, 34, 'En degenen die waken over hun shalât.'),
(5410, 70, 35, 'Zij zijn degenen die in de Tuinen (het Paradijs) geëerd zullen worden.'),
(5411, 70, 36, 'Wat is er met degenen die niet geloven, dat zij zich naar jou haasten?'),
(5412, 70, 37, 'Van rechts en van links, in groepen?'),
(5413, 70, 38, 'Wenst een ieder van hen dat hij de Tuin der gelukzaligheid (het Paradijs) binnengevoerd wordt?'),
(5414, 70, 39, 'Nee! Voorwaar, Wij hebben hen geschapen van wat zij weten.'),
(5415, 70, 40, 'Ik zweer bij de Heer van de opgang (van o.a. de zon) en de ondergang dat Wij zeker over de macht beschikken.'),
(5416, 70, 41, 'Om hen te vervangen (door anderen die) beter dan hen zijn en Wij zijn niet zwak.'),
(5417, 70, 42, 'Laat hen maar opgaan (in hun ijdele bezigheden) en laat hen spelen tot zij de Dag van hen, die hen aangezegd was, ontmoeten.'),
(5418, 70, 43, 'Op de Dag waarop zij zich haastig uit hun graven spoeden, alsof zij zich naar afgodsbeelden spoeden.'),
(5419, 70, 44, 'Hun blikken angstig teneergeslagen, overladen met vernedering. Dit is de Dag die jullie werd aangezegd.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Voorwaar, Wij hebben Nôeh tot zijn volk gezonden (en zeiden tot hem): "Waarschuw jouw volk voordat er een pijnlijke bestraffing tot hen komt."'),
(5421, 71, 2, 'Hij zei: "O mijn volk, ik ben voor jullie een duidelijke waarschuwer.'),
(5422, 71, 3, 'Aanbidt Allah en vreest Hem en gehoorzaamt mij.'),
(5423, 71, 4, 'Dan zal Hij jullie zonden vergeven en jullie uitstel geven tot een vastgesteld tijdstip. Voorwaar, wanneer het tijdstip van Allah komt, zal het niet worden uitgesteld. Als jullie dat wisten!\'\''),
(5424, 71, 5, 'Hij (Nôeh) zei: "Mijn Heer, ik heb mijn volk dag en nacht opgeroepen.'),
(5425, 71, 6, 'Maar mijn oproep deed hun vluchten (van de Waarheid) slechts toenemen.'),
(5426, 71, 7, 'En voorwaar, telkens wanneer ik hen opriep opdat U hun zou vergeven, stopten zij hun vingers in hun oren en bedekten zij (hun gezichten) met hun kleding en bleven zij uiterst hoogmoedig.'),
(5427, 71, 8, 'Toen heb ik hen waarlijk met een harde stem opgeroepen.'),
(5428, 71, 9, 'Vervolgens heb ik hun waarlijk openlijk toegesproken en hun vertrouwelijk toegesproken, heimelijk.\'\''),
(5429, 71, 10, 'Toen zei ik: "Vraagt jullie Heer om vergeving: voorwaar, Hij is Vergevensgezind.'),
(5430, 71, 11, 'Hij zal dan overvloedige regens uit de hemel over jullie neerzenden.'),
(5431, 71, 12, 'En jullie bezittingen en kinderen vermeerderen en Hij zal jullie tuinen schenken en Hij zal jullie rivieren schenken.'),
(5432, 71, 13, 'Wat is er met jullie, dat jullie de Grootheid van Allah niet vrezen?'),
(5433, 71, 14, 'Terwijl Hij jullie waarlijk in fasen heeft geschapen?'),
(5434, 71, 15, 'Zien jullie niet hoe Allah de zeven hemelen in lagen heeft geschapen?'),
(5435, 71, 16, 'En Hij heeft daarin de maan geplaatst als een licht en de zon als een lamp.'),
(5436, 71, 17, 'En Allah heeft jullie als schepselen voortgebracht uit de aarde.'),
(5437, 71, 18, 'Daarna keert Hij jullie in haar terug en brengt Hij jullie weer tevoorschijn.'),
(5438, 71, 19, 'En Allah heeft voor jullie de aarde als een tapijt uitgespreid.'),
(5439, 71, 20, 'Opdat jullie haar over brede wegen bereizen."'),
(5440, 71, 21, 'Nôeh zei: "Mijn Heer, zij gehoorzamen mij niet, en zij volgen degene wiens bezit en kinderen voor hen slechts verlies vermeerdert.'),
(5441, 71, 22, 'En zij beraamden een grote list.'),
(5442, 71, 23, 'En zij zeiden: "Verlaat jullie goden niet en verlaat Wadd niet, en niet Soewâ\'a, en niet Yaghôets en Ya\'ôeq en Nasr."'),
(5443, 71, 24, 'En waarlijk, zij deden velen dwalen. En (O Allah) doe voor de onrechtplegers slechts de dwaling toenemen."'),
(5444, 71, 25, 'Vanwege hun zonden werden zij verdronken en daarna in de Hel gevoerd. En zij vonden buiten Allah om voor zich geen helpers.'),
(5445, 71, 26, 'En Nôeh zei: "Mijn Heer, laat op de aarde geen enkele ongelovige in leven.'),
(5446, 71, 27, 'Voorwaar, als U hen (in leven) laat, zullen zij Uw dienaren doen dwalen en zij brengen niets voort dan losbandige ongelovigen.'),
(5447, 71, 28, 'Mijn Heer, vergeef mij en mijn ouders en wie mijn huis binnentreedt als gelovige, en de gelovige mannen en de gelovige vrouwen. En doe voor de onrechtplegers slechts de vernietiging toenemen."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'Zeg (O Moehammad): "Het is aan mij geopenbaard dat een aantal Djinn\'s heeft meegeluisterd, waarop zij zeiden: "Voorwaar, Wij hebben een verbazingwekkende Koran gehoord.'),
(5449, 72, 2, 'Hij (de Koran) leidt tot het juiste, toen geloofden wij erin, en wij zullen nooit één deelgenoot aan onze Heer toekennen.'),
(5450, 72, 3, 'En dat de Majesteit van onze Heer Verheven is, Hij heeft Zich geen echtgenote genomen en geen zoon.'),
(5451, 72, 4, 'En dat de dwazen onder ons plachten leugens te vertellen over Allah.'),
(5452, 72, 5, 'En dat wij dachten dat de mens en de Djinn\'s nooit leugens over Allah zouden vertellen.'),
(5453, 72, 6, 'En dat er mannen onder de mensen waren die hulp zochten bij mannen van de Djinn\'s, wat hun zondigheid vermeerderde.'),
(5454, 72, 7, 'En dat zij dachten zoals jullie dachten: dat Allah nooit iemand zou doen opstaan.'),
(5455, 72, 8, 'En dat wij hebben gevraagd naar de hemel (te mogen gaan): toen vonden wij dat zij vol was met strenge bewakers en brandende vlammen.'),
(5456, 72, 9, 'En dat, wij vroeger (vóór de Koran) op geheime plaatsen zaten om mee te luisteren, maar wie nu wil meeluisteren treft een pijl van vuur aan die op hem loert.'),
(5457, 72, 10, 'En dat wij niet weten of er kwaad bedoeld wordt voor degenen die op de aarde zijn, of dat hun Heer hun rechte Leiding wil geven.'),
(5458, 72, 11, 'En dat er onder ons rechtschapenen zijn, en er onder ons zijn die dat niet zijn. Wij waren op verschillende wegen.'),
(5459, 72, 12, 'En dat wij overtuigd zijn dat wij op de aarde Allah nooit kunnen ontkomen en dat wij nooit van Hem kunnen wegvluchten.'),
(5460, 72, 13, 'En dat, toen wij over de rechte Leiding hadden gehoord, wij erin geloofden. En wie in zijn Heer gelooft; hij zal geen vermindering (van beloning) en geen vermeerdering (van zondigheid) vrezen.'),
(5461, 72, 14, 'En dat er onder ons zijn die zich aan Allah hebben overgegeven, en er onder ons zijn die afwijken (van het rechte Pad). Wie zich heeft overgegeven: zij zijn degenen die de rechte Leiding gekozen hebben.'),
(5462, 72, 15, 'Maar wat de afwijkenden betreft: zij zijn brandhout voor de Hel."'),
(5463, 72, 16, '(En Allah zegt:) "Als zij op het rechte Pad zouden zijn gebleven, dan zouden Wij hun overvloedig water te drinken gegeven hebben.'),
(5464, 72, 17, 'Om hen daarmee te beproeven. En wie zich afwendt van de gedachtenis van zijn Heer, die voert Hij naar een zware bestraffing."'),
(5465, 72, 18, 'En voorwaar, de moskeeën behoren aan Allah toe: roept dan naast Allah niet één aan.'),
(5466, 72, 19, 'En voorwaar, wanneer de dienaar van Allah (Moehammed) in de shalât staat en Hem aanroept, dan bedelven zij (de Djinn\'s) hem bijna.'),
(5467, 72, 20, 'Zeg: "Ik roep alleen mijn Heer aan en ik ken Hem in niets deelgenoten toe."'),
(5468, 72, 21, 'Zeg: "Ik heb geen macht om voor jullie schade te voorkomen en niet om Leiding te geven."'),
(5469, 72, 22, 'Zeg: "Niemand zal mij ooit tegen Allah kunnen redden en ik zal nooit naast Hem een toevluchtsoord vinden.'),
(5470, 72, 23, '(Ik vind een toevluchtsoord) van Allah slechts door de verkondiging van Zijn Boodschappen." En wie opstaat tegen Allah en Zijn Boodschapper: voor hen is er het vuur van de Hel. Zij zijn daarin eeuwig levenden, voor altijd.'),
(5471, 72, 24, 'Wanneer zij dan zien wat hun is aangezegd, dan zullen zij weten wie de zwakste helpers heeft en het kleinst in aantal is.'),
(5472, 72, 25, 'Zeg: "Ik weet niet of het nabij is, wat jullie aangezegd is, of dat mijn Heer het nog lang uitstelt."'),
(5473, 72, 26, 'De Kenner van het onwaarneembare, Hij maakt voor niemand het onwaarneembare dat bij Hem is zichtbaar.'),
(5474, 72, 27, 'Behalve aan een Boodschapper die Hem welgevallig is, en voorwaar, dan laat Hij vóór hem en achter hem wachters (Engelen) gaan.'),
(5475, 72, 28, 'Om te doen weten dat zij de Boodschappen van hun Heer overbrachten. En Hij omvat wat er bij hen is en Hij somt alle zaken nauwkeurig op.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'O jij omhulde.'),
(5477, 73, 2, 'Sta op in de nacht om de shalât te verrichten, met uitzondering van een kort gedeelte (van de nacht).'),
(5478, 73, 3, 'De helft ervan of iets minder dan dat.'),
(5479, 73, 4, 'Of maak het iets langer en draag de Koran voor, nauwkeurig.'),
(5480, 73, 5, 'Voorwaar, Wij zullen zware Woorden tot jou neerzenden.'),
(5481, 73, 6, 'Voorwaar, het opstaan in de nacht (om te bidden) is zwaarder en maakt het woord zekerder.'),
(5482, 73, 7, 'Voorwaar, jij bent overdag belast met vele aangelegenheden.'),
(5483, 73, 8, 'En gedenk de Naam van jouw Heer en wijd je geheel aan Hem.'),
(5484, 73, 9, 'De Heer van de opgang (van de zon etc.) en de ondergang, geen god is er dan Hij, neem Hem daarom als Beschermer.'),
(5485, 73, 10, 'En wees geduldig met wat zij zeggen en houd op gepaste wijze afstand van hen.'),
(5486, 73, 11, 'En Laat de loochenaars, de bezitters van weelde, aan Mij over, en geef hen nog even uitstel.'),
(5487, 73, 12, 'Voorwaar, bij Ons bevinden zich (voor hen) ketenen en Djahim (de Hel).'),
(5488, 73, 13, 'En voedsel dat in de keel blijft steken en een pijnlijke bestraffing.'),
(5489, 73, 14, 'Op de Dag waarop de aarde en de bergen heftig zullen beven en de bergen tot uit elkaar geblazen stof zullen worden.'),
(5490, 73, 15, 'Voorwaar, Wij hebben een Boodschapper tot jullie gezonden als getuige over jullie, zoals Wij tot Fir\'aun een Boodschapper zonden.'),
(5491, 73, 16, 'Toen was Fir\'aun de Boodschapper ongehoorzaam, waarop Wij hem grepen met een verschrikkelijke greep.'),
(5492, 73, 17, 'Hoe zullen jullie je dan beschermen, als jullie de Dag waarop de kinderen grijsaards worden ontkennen?'),
(5493, 73, 18, 'De hemel zal dan gespleten zijn (en) Zijn aanzegging zal zeker uitgevoerd worden.'),
(5494, 73, 19, 'Voorwaar, dit is een Vermaning en wie wil, laat hem een Weg nemen naar zijn Heer.'),
(5495, 73, 20, 'Voorwaar, jouw Heer weet dat jij bijna tweederde van de nacht, of de helft of een derde (in gebed) staat, en (ook) een groep van degenen die bij jou horen. En Allah kent de nacht en de dag. Hij weet dat jullie nooit (de hele nacht) de shalât kunnen verrichten, daarom heeft Hij jullie berouw aanvaard. Verricht van het nachtgebed wat gemakkelijk is voor jullie. Hij weet dat er onder jullie zieken zijn en anderen die door het land reizen, zoekend naar de gunst van Allah, en anderen die strijden op de Weg van Allah. En verricht van het nachtgebed wat gemakkelijk is voor jullie. En onderhoudt de shalât en geeft de zakât en leent aan Allah een goede lening. En wat jullie vooruitzenden voor julliezelf aan goede daden, jullie zullen het bij Allah aantreffen. Het is een betere en grotere beloning. En vraagt Allah om vergeving. Voorwaar, Allah is Vergevensgezind, Meest Barmhartig.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'O jij ommantelde.'),
(5497, 74, 2, 'Sta op en waarschuw.'),
(5498, 74, 3, 'En prijs de grootheid van jouw Heer'),
(5499, 74, 4, 'En reinig jouw kleding.'),
(5500, 74, 5, 'En vermijd de zondigheid.'),
(5501, 74, 6, 'En geef niet om meer te ontvangen.'),
(5502, 74, 7, 'En wees geduldig omwille van jouw Heer.'),
(5503, 74, 8, 'Als dan op de bazuin geblazen wordt.'),
(5504, 74, 9, 'Dan is dat die Dag, een zware Dag.'),
(5505, 74, 10, '(Die Dag is) voor de ongelovigen niet gemakkelijk.'),
(5506, 74, 11, 'Laat hem aan Mij over die Ik alleenstaand geschapen heb.'),
(5507, 74, 12, 'En Ik heb hem vele bezittingen ter beschikking gesteld.'),
(5508, 74, 13, 'En kinderen, voortdurend aan zijn zijde.'),
(5509, 74, 14, 'En Ik verschafte hem gemak in ruime mate.'),
(5510, 74, 15, 'En vervolgens verlangt hij dat Ik (voor hem) vermeerder.'),
(5511, 74, 16, 'Nee! Hij is opstandig tegen Onze Verzen.'),
(5512, 74, 17, 'Ik zal hem beladen met een zware bestraffing.'),
(5513, 74, 18, 'Voorwaar, hij dacht na en nam een besluit.'),
(5514, 74, 19, 'Verdoemd is hij daarom. Hoe vreemd was zijn besluit!'),
(5515, 74, 20, 'Nogmaals, verdoemd is hij! Hoe vreemd was zijn besluit!'),
(5516, 74, 21, 'Toen dacht hij weer na.'),
(5517, 74, 22, 'Daarna fronste hij en keek somber.'),
(5518, 74, 23, 'Vervolgens keerde hij zijn rug toe en was hoogmoedig.'),
(5519, 74, 24, 'En hij zei: "Deze (Koran) is slechts overgedragen tovenarij.'),
(5520, 74, 25, 'Dit is slechts het woord van een mens."'),
(5521, 74, 26, 'Ik zal hem doen braden in Saqar (de Hel).'),
(5522, 74, 27, 'En wat doet jou weten wat Saqar is?'),
(5523, 74, 28, 'Zij (de Hel) laat niet achter en zij laat niet met rust.'),
(5524, 74, 29, 'Zij verschroeit (de huid) van de mens.'),
(5525, 74, 30, 'Over haar waken negentien (Engelen).'),
(5526, 74, 31, 'En Wij hebben geen anderen dan Engelen aangesteld als wachters van de Hel. En Wij hebben hun aantal slechts vastgesteld als een beproeving voor degenen die niet geloven en opdat degenen aan wie de Schrift gegeven is overtuigd zullen zijn. En opdat degenen die geloven zullen toenemen in geloof, en opdat degenen aan wie de Schrift is gegeven en de gelovigen niet zullen twijfelen. En opdat degenen in wier harten een ziekte is en de ongelovigen zullen zeggen: "Wat wil Allah met dit voorbeeld zeggen?" Zo laat Allah dwalen wie Hij wil, en leidt Hij wie Hij wil. En niemand kent de troepenmacht van jouw Heer dan Hij. En dit is niets anders dan een Vermaning voor de mensheid.'),
(5527, 74, 32, 'Nee, bij de maan!'),
(5528, 74, 33, 'En de nacht wanneer hij terugkeert.'),
(5529, 74, 34, 'En de dageraad wanneer hij gloort.'),
(5530, 74, 35, 'Voorwaar, zij (de Hel) is zeker een van de grootste verschrikkingen.'),
(5531, 74, 36, '(Zij is er) als een waarschuwing voor de mensheid.'),
(5532, 74, 37, 'Voor degenen onder hen die voort willen gaan (door goede daden te verrichten) of achter willen blijven (door slechte daden te verrichten).'),
(5533, 74, 38, 'Iedere ziel is een borg voor wat zij heeft verricht.'),
(5534, 74, 39, 'Behalve de mensen van de rechterzijde.'),
(5535, 74, 40, 'In Tuinen (het Paradijs) vragen zij elkaar.'),
(5536, 74, 41, 'Over de misdadigers.'),
(5537, 74, 42, '(Zij zeggen:) "Wat heeft hen naar Saqar (de Hel) gevoerd?"'),
(5538, 74, 43, 'Zij zeiden: "Wij behoorden niet tot degenen die de shalât verrichten.'),
(5539, 74, 44, 'En wij voedden de armen niet.'),
(5540, 74, 45, 'En wij plachten ijdele gesprekken te voeren met degenen die ijdel praatten.'),
(5541, 74, 46, 'En wij plachten de Dag des Oordeels te loochenen.'),
(5542, 74, 47, 'Tot het zekere (de dood) tot ons kwam."'),
(5543, 74, 48, 'De voorspraak van de voorsprekers baat hen niet.'),
(5544, 74, 49, 'Wat is er met hen dat zij zich van de Vermaning afwenden?'),
(5545, 74, 50, 'Alsof zij opgeschrikte ezels zijn.'),
(5546, 74, 51, 'Die vluchten voor een leeuw.'),
(5547, 74, 52, 'Toch wil een ieder van hen, dat hun opengespreidde bladen (van een Openbaring) worden gegeven.'),
(5548, 74, 53, 'Nee! Zij vrezen zelfs het Hiernamaals niet.'),
(5549, 74, 54, 'Nee, voorwaar, hij (de Koran) is een Vermaning.'),
(5550, 74, 55, 'Wie wil trekt er lering uit.'),
(5551, 74, 56, 'En zij trekken er geen lering uit, behalve als Allah het wil. Hij is het Die vrees toekomt en Hij is het Die het toekomt om te vergeven.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Ik zweer bij de Dag der Opstanding.'),
(5553, 75, 2, 'En Ik zweer bij de (zichzelf) verwijtende ziel.'),
(5554, 75, 3, 'Denkt de mens dat Wij Zijn botten nooit zullen bijeenbrengen?'),
(5555, 75, 4, 'Welzeker, Wij zijn in staat om zelfs zijn vingertoppen (opnieuw) volmaakt te vormen.'),
(5556, 75, 5, 'De mens wil zelfs in zondigheid voortleven.'),
(5557, 75, 6, 'Hij vraagt: "Wanneer is de Dag der Opstanding?"'),
(5558, 75, 7, 'Wanneer dan de ogen zich opensperren.'),
(5559, 75, 8, 'En de maan duister wordt.'),
(5560, 75, 9, 'En de zon en de maan bijeengebracht worden.'),
(5561, 75, 10, 'Die Dag zal de mens zeggen: "Waar is het toevluchtsoord?"'),
(5562, 75, 11, 'Nee! Er is geen toevluchtsoord.'),
(5563, 75, 12, 'Bij jouw Heer is die Dag de eindbestemming.'),
(5564, 75, 13, 'De mens zal die Dag worden medegedeeld wat hij heeft voortgebracht en wat hij heeft nagelaten.'),
(5565, 75, 14, 'Hij zal zelfs tegen zichzelf getuigen.'),
(5566, 75, 15, 'Ook al biedt hij zijn verontschuldigingen aan.'),
(5567, 75, 16, '(Allah zegt tot de Profeet:) "Beweeg jouw tong er niet mee (de Koran), om er haast mee te maken.'),
(5568, 75, 17, 'Voorwaar, het is aan Ons hem te doen bewaren en hem voor te doen dragen.'),
(5569, 75, 18, 'Wanneer Wij hem dan hebben doen voordragen, volg dan zijn voordracht.'),
(5570, 75, 19, 'Daarna is aan Ons de uitleg ervan."'),
(5571, 75, 20, 'Nee! Jullie houden van het voorbijgaande.'),
(5572, 75, 21, 'En jullie besteden geen aandacht aan het Hiernamaals.'),
(5573, 75, 22, 'Gezichten zullen op die Dag verlicht zijn.'),
(5574, 75, 23, 'Naar hun Heer zullen zij zien.'),
(5575, 75, 24, 'En gezichten zullen op die Dag duister zijn.'),
(5576, 75, 25, 'Zij weten zeker dat een verpletterende ramp over hen zal worden gebracht.'),
(5577, 75, 26, 'Nee, wanneer de (laatste) adem in de keel stokt.'),
(5578, 75, 27, 'En er gezegd wordt: "Wie kan genezen?"'),
(5579, 75, 28, 'En hij beseft dat het afscheid is gekomen.'),
(5580, 75, 29, 'En de benen (in doodsangst) over elkaar liggen.'),
(5581, 75, 30, 'Naar jouw Heer worden zij Die Dag gesleept.'),
(5582, 75, 31, 'Hij geloofde (de Koran en de Boodschapper) niet, en hij verrichtte de shalât niet.'),
(5583, 75, 32, 'Maar hij loochende en hij wendde zich af.'),
(5584, 75, 33, 'Daarna ging hij naar zijn verwanten, hoogmoedig.'),
(5585, 75, 34, 'Wee jou, wee!'),
(5586, 75, 35, 'Nogmaals, wee jou, wee!'),
(5587, 75, 36, 'Denkt de mens dat hij ongemoeid zal worden gelaten?'),
(5588, 75, 37, 'Was hij niet eerst een druppel van uitgestort sperma?'),
(5589, 75, 38, 'En vervolgens een bloedklonter waarna Hij (hem) schiep en nauwkeurig vormde?'),
(5590, 75, 39, 'Zo maakte Hij daarvan de twee geslachten, de man en de vrouw.'),
(5591, 75, 40, 'Is Degene met zo\'n macht niet in staat de doden tot leven te brengen?');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Voorzeker, er is voor de mens een periode geweest waarin hij in niets gedenkwaardig was.'),
(5593, 76, 2, 'Voorwaar, Wij hebben de mens geschapen uit een gemengde druppel om hem te beproeven. Daarop gaven Wij hem het gehoor en gezichtsvermogen.'),
(5594, 76, 3, 'Voorwaar, Wij wezen hem de Weg: wordt hij dankbaar of wordt hij ondankbaar?'),
(5595, 76, 4, 'Voorwaar, voor de ongelovigen hebben Wij kettingen, en ketens en Sa\'îr (de Hel) bereid.'),
(5596, 76, 5, 'Voorwaar, de deugdzamen zullen drinken uit een beker waarvan de mengdrank van Kâfôer (kamfer) is.'),
(5597, 76, 6, 'Een bron waarvan de dienaren van Allah drinken. Zij laten deze overvloedig stromen.'),
(5598, 76, 7, 'Zij vervulden hun geloften. En zij vreesden een Dag waarvan het kwaad verschrikkelijk is.'),
(5599, 76, 8, 'En zij gaven het voedsel waarvan zij hielden aan een arme, en een wees en een gevangene.'),
(5600, 76, 9, '(Zij zeiden:) "Wij voeden jullie slechts omwille van het welbehagen van Allah, wij verlangen van jullie geen beloning en geen dank.'),
(5601, 76, 10, 'Voorwaar, wij vrezen van onze Heer een angstaanjagende, huiveringwekkende Dag."'),
(5602, 76, 11, 'Allah zal hen op die Dag beschermen voor het kwaad en hen glans en blijdschap schenken.'),
(5603, 76, 12, 'En Hij zal hen vanwege hun geduldige volharding belonen met het Paradijs en met zijde.'),
(5604, 76, 13, 'Leunend zitten zij daarin op rustbanken. Zij vinden daarin geen zon en geen kou.'),
(5605, 76, 14, 'En haar schaduwen zijn voor hen dichtbij en haar vruchten zijn vlakbij, makkelijk te plukken.'),
(5606, 76, 15, 'En onder hen wordt rondgegaan met kruiken van zilver en glazen als van kristal.'),
(5607, 76, 16, 'Kristalhelder, van zilver gemaakt, die naar wens schenken.'),
(5608, 76, 17, 'En daarin wordt er voor hen geschonken uit een beker waarvan de mengdrank gember is.'),
(5609, 76, 18, 'Er bevindt zich daarin een bron die Salsabîl genoemd wordt.'),
(5610, 76, 19, 'En onder hen wordt rondgegaan door eeuwig jeugdigen. Als jij hen ziet, dan denk jij dat zij verstrooide parels zijn.'),
(5611, 76, 20, 'En als jij rondkijkt dan zie jij een genieting en een geweldig koninkrijk.'),
(5612, 76, 21, 'Zij dragen kleren van fijne groene zijde en brokaat en zij zullen gesierd worden met zilveren armbanden. En hun Heer zal hen een pure drank schenken.'),
(5613, 76, 22, '(Er wordt gezegd:) "Voorwaar, dat is een beloning voor jullie en jullie daden zijn aanvaard."'),
(5614, 76, 23, 'Voorwaar, Wij zijn het Die de Koran in fasen tot jou neergezonden hebben.'),
(5615, 76, 24, 'Wees dan geduldig met de wetten van jouw Heer en volg niet de zondaar of de ongelovige onder hen.'),
(5616, 76, 25, 'En gedenk (in jouw shalât) de Naam van jouw Heer, in de ochtend en de avond.'),
(5617, 76, 26, 'En in een gedeelte van de nacht, en kniel je neer voor Hem en prijs Zijn Glorie tijdens een lang deel van de nacht.'),
(5618, 76, 27, 'Voorwaar, zij (de ongelovigen) houden van het voorbijgaande en leggen achter hun rug een zware Dag.'),
(5619, 76, 28, 'Wij hebben hen geschapen en Wij hebben hun gestel stevig gemaakt. En als Wij het gewild hadden, dan zouden Wij hen vervangen door gelijksoortigen.'),
(5620, 76, 29, 'Voorwaar, dit is een Vermaning. Wie het dan wil, laat hij een Weg naar zijn Heer nemen.'),
(5621, 76, 30, 'En jullie zullen het niet willen, behalve als Allah het wil: voorwaar, Allah is Alwetend, Alwijs.'),
(5622, 76, 31, 'Hij doet in Zijn Barmhartigheid binnengaan wie Hij wil. En Hij heeft een pijnlijke bestraffing bereid voor de onrechtvaardigen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Bij de met goedheid uitgezondenen.'),
(5624, 77, 2, 'Bij de met spoed voortspoedenden.'),
(5625, 77, 3, 'En bij de verspreid verspreidenden.'),
(5626, 77, 4, 'Bij de scheidend scheidenden.'),
(5627, 77, 5, 'Bij de doorgevenden van de Vermaning.'),
(5628, 77, 6, 'Als een afwijzing van verontschuldigingen of als een waarschuwing.'),
(5629, 77, 7, 'Voorwaar, wat jullie is aangezegd, zal zeker gebeuren.'),
(5630, 77, 8, 'Wanneer dan de sterren gedoofd worden.'),
(5631, 77, 9, 'En wanneer de hemel gespleten wordt.'),
(5632, 77, 10, 'En wanneer de bergen verpulverd worden.'),
(5633, 77, 11, 'En wanneer voor de Boodschappers de tijd vastgesteld is.'),
(5634, 77, 12, '(Er wordt gezegd:) "Tot welke dag is er uitstel gegeven?"'),
(5635, 77, 13, 'Tot de Dag van de Beoordeling.'),
(5636, 77, 14, 'En wat doet jullie weten wat Dag van de Beoordeling is?'),
(5637, 77, 15, 'Wee die Dag de loochenaars!'),
(5638, 77, 16, 'Hebben Wij de vroegeren niet vernietigd?'),
(5639, 77, 17, 'Waarna Wij hen deden opvolgen door de lateren?'),
(5640, 77, 18, 'Zo behandelen Wij de misdadigers.'),
(5641, 77, 19, 'Wee die Dag de loochenaars!'),
(5642, 77, 20, 'Hebben Wij jullie niet uit een onaanzienlijk water geschapen?'),
(5643, 77, 21, 'Toen plaatsten Wij het in een beschermende rustplaats.'),
(5644, 77, 22, 'Tot een bekend tijdstip.'),
(5645, 77, 23, 'Wij beschikten (erover) en Wij zijn de beste Beschikkers.'),
(5646, 77, 24, 'Wee die Dag de loochenaars!'),
(5647, 77, 25, 'Hebben Wij de aarde niet tot een plaats ven verzameling gemaakt?'),
(5648, 77, 26, 'Zowel voor levenden en doden?'),
(5649, 77, 27, 'En Wij plaatsten daarop stevige bergen en Wij schonken jullie helder water.'),
(5650, 77, 28, 'Wee die Dag de loochenaars!'),
(5651, 77, 29, 'Gaat naar dat wat jullie plachten to loochenen!'),
(5652, 77, 30, 'Gaat naar een schaduw (van rook) die drie kolommen heeft.'),
(5653, 77, 31, 'Die geen schaduw geeft en die niet baat tegen het vlammende Vuur.'),
(5654, 77, 32, 'Zij (de Hel) werpt vonken als kastelen.'),
(5655, 77, 33, 'Alsof zij gele kamelen waren.'),
(5656, 77, 34, 'Wee die Dag de loochenaars!'),
(5657, 77, 35, 'Dit is een Dag waarop zij niet spreken.'),
(5658, 77, 36, 'En er wordt hun niet toegestaan zich te verontschuldigen.'),
(5659, 77, 37, 'Wee die Dag de loochenaars!'),
(5660, 77, 38, 'Dit is de Dag van de Beoordeling, Wij verzamelen jullie en de vroegeren.'),
(5661, 77, 39, 'Als jullie dan een list hebben, voert die dan uit.'),
(5662, 77, 40, 'Wee die Dag de loochenaars!'),
(5663, 77, 41, 'Voorwaar, de Moettaqôen verkeren in schaduwen en bij bronnen.'),
(5664, 77, 42, 'En er zijn vruchten die zij wensen.'),
(5665, 77, 43, '(Er wordt gezegd:) "Eet en drinkt smakelijk voor wat jullie plachten te verrichten."'),
(5666, 77, 44, 'Voorwaar, zo belonen Wij de weldoeners.'),
(5667, 77, 45, 'Wee die Dag de loochenaars!'),
(5668, 77, 46, 'Eet en geniet even: voorwaar, jullie zijn misdadigers.'),
(5669, 77, 47, 'Wee die Dag de loochenaars!'),
(5670, 77, 48, 'En wanneer er tot hen gezegd wordt: "Buigt jullie (in de shalât)," dan buigen zij niet.'),
(5671, 77, 49, 'Wee die Dag de loochenaars!'),
(5672, 77, 50, 'In welk Woord na hem (de Koran) zullen zij dan geloven?');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'Waarover stellen zij elkaar vragen?'),
(5674, 78, 2, 'Over de geweldige aankondiging.'),
(5675, 78, 3, 'Waarover zij redetwisten.'),
(5676, 78, 4, 'Nee! Zij zullen het weten.'),
(5677, 78, 5, 'Nogmaals nee, zij zullen het weten.'),
(5678, 78, 6, 'Hebben Wij de aarde niet tot een uitgespreide plaats gemaakt?'),
(5679, 78, 7, 'En de bergen als pinnen?'),
(5680, 78, 8, 'En Wij hebben jullie in paren geschapen.'),
(5681, 78, 9, 'En Wij hebben voor jullie de slaap gemaakt, als rust.'),
(5682, 78, 10, 'En Wij hebben de nacht als een bedekking gemaakt.'),
(5683, 78, 11, 'En Wij hebben de dag gemaakt om levensonderhoud te zoeken.'),
(5684, 78, 12, 'En Wij hebben boven jullie zeven hechte hemelen gebouwd.'),
(5685, 78, 13, 'En Wij hebben daarin een stralende lamp geplaatst.'),
(5686, 78, 14, 'En Wij hebben uit de wolken stromend water gezonden.'),
(5687, 78, 15, 'Opdat Wij daarmee graan en planten voortbrengen.'),
(5688, 78, 16, 'En dichtbegroeide tuinen.'),
(5689, 78, 17, 'Voorwaar, de Dag van de Beoordeling is op een vastgesteld tijdstip.'),
(5690, 78, 18, 'De Dag waarop op de bazuin wordt geblazen zullen jullie komen, groep na groep.'),
(5691, 78, 19, 'En de hemel wordt geopend en zij heeft vele poorten.'),
(5692, 78, 20, 'En de bergen worden verpulverd en worden tot luchtspiegelingen.'),
(5693, 78, 21, 'Voorwaar, de Hel is als een hinderlaag.'),
(5694, 78, 22, 'Een bestemmingsoord voor de overtreders.'),
(5695, 78, 23, 'Zij verblijven eeuwig daarin.'),
(5696, 78, 24, 'Zij zullen daarin geen koelte en geen drank proeven.'),
(5697, 78, 25, 'Behalve kokend water en etter.'),
(5698, 78, 26, 'Als passende vergelding.'),
(5699, 78, 27, 'Voorwaar, zij verwachtten nooit een afrekening.'),
(5700, 78, 28, 'En loochenden Onze Verzen geheel.'),
(5701, 78, 29, 'En Wij hebben alle zaken in een Boek opgesomd.'),
(5702, 78, 30, 'Proeft daarom de straf, en er is voor jullie geen vermeerdering, behalve van de bestraffing.'),
(5703, 78, 31, 'Voorwaar, voor de Moettaqôen is er een overwinning.'),
(5704, 78, 32, 'Tuinen en druivenstruiken.'),
(5705, 78, 33, 'En jeugdige gezellinnen, gelijk in leeftijd.'),
(5706, 78, 34, 'En een gevulde beker.'),
(5707, 78, 35, 'Zij horen daar geen onzin en geen leugens.'),
(5708, 78, 36, 'Als een beloning van jouw Heer, als afrekenende gift.'),
(5709, 78, 37, 'De Heer der hemelen en der aarde en wat er tussen beide is, de Barmhartige. Zij zijn niet in staat Hem aan te spreken.'),
(5710, 78, 38, 'Op die Dag staan de Geest (Djibrîl) en de Engelen in rijen opgesteld. Zij spreken niet, behalve na wie de Barmhartige toestemming verleent en die zegt wat juist is.'),
(5711, 78, 39, 'Dat is de Dag van de Waarheid. Laat wie wil daarom een terugkeer naar zijn Heer afleggen.'),
(5712, 78, 40, 'Voorwaar, Wij hebben jullie gewaarschuwd voor een nabije bestraffing op de Dag dat de mens zal kijken naar wat zijn handen vroeger bedreven, en waarop de ongelovige zal zeggen: "O wee, was ik maar aarde."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Bij de hard uitrukkenden.'),
(5714, 79, 2, 'Bij de zacht uittrekkenden.'),
(5715, 79, 3, 'Bij de snel uitvoerenden.'),
(5716, 79, 4, 'De snel voorbijstrevenden.'),
(5717, 79, 5, 'De uitvoerenden van een bevel.'),
(5718, 79, 6, 'Op de Dag waarop de bevende doet beven.'),
(5719, 79, 7, 'Zal deze gevolgd worden door de (tweede) beving.'),
(5720, 79, 8, 'De harten zullen op die Dag bonzen.'),
(5721, 79, 9, 'Hun ogen zullen angstig teneergeslagen zijn.'),
(5722, 79, 10, 'Zij zeggen: "Zullen wij zeker worden teruggebracht in de vorige staat?'),
(5723, 79, 11, 'Als wij vergruisde beenderen zijn geworden?"'),
(5724, 79, 12, 'Zij zeggen: "Dat is dan een verliesgevende terugkeer.'),
(5725, 79, 13, 'En voorwaar, het zal slechts één enkele stoot zijn.'),
(5726, 79, 14, 'En dan staan zij op het aardoppervlak.'),
(5727, 79, 15, 'Heeft het verhaal van Môesa jou bereikt?'),
(5728, 79, 16, 'Toen zijn Heer hem riep in de heilige vallei van Thoewa.'),
(5729, 79, 17, '(Allah zei:) "Ga naar Fir\'aun: voorwaar, hij was in overtreding.'),
(5730, 79, 18, 'En zeg tot hem: "Heb jij de wil om jezelf te reinigen (van zonde)?'),
(5731, 79, 19, 'En dat ik jou tot jouw Heer zal leiden, zodat jij (Hem) vreest?"'),
(5732, 79, 20, 'En hij toonde hem de grote Tekenen (wonderen).'),
(5733, 79, 21, 'Maar hij loochende en was ongehoorzaam.'),
(5734, 79, 22, 'Vervolgens draaide hij zich om en vluchtte.'),
(5735, 79, 23, 'Toen verzamelde hij (zijn tovenaars) en riep uit.'),
(5736, 79, 24, 'En zei: "Ik ben jullie heer, de hoogste."'),
(5737, 79, 25, 'En Allah greep hem met de bestraffing voor het eerste en het laatste (van wat hij zei)."'),
(5738, 79, 26, 'Voorwaar, daarin is zeker onderricht voor wie (Allah) vreest.'),
(5739, 79, 27, 'Is de schepping van jullie moeilijker dan die van de hemel die Hij gebouwd heeft?'),
(5740, 79, 28, 'Hij verhief haar (de hemel) en vervolmaakte haar.'),
(5741, 79, 29, 'En Hij maakte haar nacht duister en Hij maakte haar dag licht.'),
(5742, 79, 30, 'En daarna spreidde Hij de aarde uit.'),
(5743, 79, 31, 'En Hij bracht uit haar haar water en planten tevoorschijn.'),
(5744, 79, 32, 'En Hij verstevigde de bergen.'),
(5745, 79, 33, 'Als een voorziening voor jullie en voor jullie vee.'),
(5746, 79, 34, 'Wanneer dan de overweldigende gebeurtenis plaatsvindt.'),
(5747, 79, 35, 'Op die Dag zal de mens zich herinneren wat hij bedreef.'),
(5748, 79, 36, 'En de Hel zal getoond worden aan wie ziet.'),
(5749, 79, 37, 'Wat betreft degene die overtrad.'),
(5750, 79, 38, 'En de voorkeur gaf aan het wereldse leven.'),
(5751, 79, 39, 'Voorwaar, de Hel is de verblijfplaats!'),
(5752, 79, 40, 'En wat betreft degene die de macht van zijn Heer vreesde en zijn ziel weerhield van slechte begeerten.'),
(5753, 79, 41, 'Voorwaar, het Paradijs is de verblijfplaats.'),
(5754, 79, 42, 'Zij vragen jou naar het Uur: "Wanneer zal het plaatsvinden?"'),
(5755, 79, 43, 'Hoe kan jij dat noemen?'),
(5756, 79, 44, 'Bij jouw Heer is de kennis daarover.'),
(5757, 79, 45, 'Voorwaar, jij bent slechts een vermaner voor wie het (Uur) vreest.'),
(5758, 79, 46, 'Op de Dag dat zij het (Uur) zien, zal het zijn alsof zij slechts een avond of de morgen op de aarde verbleven.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Hij (Moehammad) fronste en wendde zich af.'),
(5760, 80, 2, 'Omdat de blinde tot hem kwam.'),
(5761, 80, 3, 'En wat doet jou het weten, misschien wilde hij zich reinigen (van zonden).'),
(5762, 80, 4, 'Of zich laten onderrichten en zou het onderricht hem baten.'),
(5763, 80, 5, 'Wat betreft degene die zich behoefteloos waant.'),
(5764, 80, 6, 'Aan hem schenk jij alle aandacht.'),
(5765, 80, 7, 'Terwijl jij niet verantwoordelijk bent als hij zich niet reinigt.'),
(5766, 80, 8, 'Maar wat betreft degene die haastig tot jou kwam.'),
(5767, 80, 9, 'En hij vreest (Allah).'),
(5768, 80, 10, 'Aan hem schenk jij geen aandacht.'),
(5769, 80, 11, 'Nee! Voorwaar, het is een Vermaning.'),
(5770, 80, 12, 'Laat wie het wil er lering uit trekken.'),
(5771, 80, 13, '(Geschreven) op edele bladen.'),
(5772, 80, 14, 'Verheven en gereinigd.'),
(5773, 80, 15, 'Door de handen van schrijvers (Engelen).'),
(5774, 80, 16, 'Edel, deugdzaam.'),
(5775, 80, 17, 'Verdoemd is de mens. Hoe ondankbaar is hij!'),
(5776, 80, 18, 'Waaruit heeft Hij hem geschapen?'),
(5777, 80, 19, 'Hij heeft hem uit een druppel geschapen en daarna voor hem beschikt.'),
(5778, 80, 20, 'Daarna vergemakkelijkt Hij voor hem de Weg.'),
(5779, 80, 21, 'Vervolgens doet Hij hem sterven en doet Hij hem begraven.'),
(5780, 80, 22, 'Daarop, als Hij het wil, wekt Hij hem op.'),
(5781, 80, 23, 'Nee, hij heeft nog niet verricht wat Hij hem opdroeg.'),
(5782, 80, 24, 'Laat de mens dan naar zijn voedsel kijken.'),
(5783, 80, 25, 'Voorwaar, Wij doen het water in stromen neerkomen.'),
(5784, 80, 26, 'Daarna doen Wij de aarde openploegen.'),
(5785, 80, 27, 'Dan doen Wij daarin granen groeien.'),
(5786, 80, 28, 'En druiven en groenten.'),
(5787, 80, 29, 'En olijfbomen on dadelpalmen.'),
(5788, 80, 30, 'En dichtbegroeide gaarden.'),
(5789, 80, 31, 'En vruchten en weidegras.'),
(5790, 80, 32, 'Als een voorziening voor jullie en voor jullie vee.'),
(5791, 80, 33, 'En wanneer dan de bazuinstoot komt.'),
(5792, 80, 34, 'Op die Dag vlucht de mens van zijn broeder.'),
(5793, 80, 35, 'En van zijn moeder en zijn vader.'),
(5794, 80, 36, 'En van zijn vrouw en van zijn kinderen.'),
(5795, 80, 37, 'Een ieder ven hen zal op die Dag een bezigheid hebben die hem genoeg is.'),
(5796, 80, 38, 'Gezichten (van de gelovigen) zullen op die Dag stralen.'),
(5797, 80, 39, 'Lachend, verblijd.'),
(5798, 80, 40, 'En gezichten (van de ongelovigen) zullen op die Dag met stof bedekt Zijn.'),
(5799, 80, 41, 'En een duisternis zal hen omhullen.'),
(5800, 80, 42, 'Zij zijn degenen die de zondige ongelovigen zijn.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Wanneer de zon opgerold wordt.'),
(5802, 81, 2, 'En wanneer de sterren vallen.'),
(5803, 81, 3, 'En wanneer de bergen bewogen worden.'),
(5804, 81, 4, 'En wanneer de drachtige kamelen achtergelaten worden.'),
(5805, 81, 5, 'En wanneer de wilde dieren verzameld worden.'),
(5806, 81, 6, 'En wanneer de zeeën tot koken gebracht worden.'),
(5807, 81, 7, 'En wanneer de zielen verenigd worden.'),
(5808, 81, 8, 'En wanneer het levend begraven meisje ondervraagd wordt.'),
(5809, 81, 9, 'Voor welke zonde zij gedood werd.'),
(5810, 81, 10, 'En wanneer de bladen opengeslagen worden.'),
(5811, 81, 11, 'En wanneer de hemel afgestroopt wordt.'),
(5812, 81, 12, 'En wanneer Djahîm (de Hel) ontstoken wordt.'),
(5813, 81, 13, 'En wanneer het Paradijs nabij gebracht wordt.'),
(5814, 81, 14, 'Dan weet een ziel wat zij verricht heeft.'),
(5815, 81, 15, 'Ik zweer bij de sterren.'),
(5816, 81, 16, 'De voortspoedenden, de ondergaanden.'),
(5817, 81, 17, 'En bij de nacht wanneer hij opdoemt.'),
(5818, 81, 18, 'En bij de dageraad wanneer hij gloort.'),
(5819, 81, 19, 'Voorwaar, het is zeker het woord van een edele Boodschapper.'),
(5820, 81, 20, 'Een bezitter van kracht die een ereplaats heeft bij de Bezitter van de Troon.'),
(5821, 81, 21, 'Die gehoorzaamd wordt en betrouwbaar is.'),
(5822, 81, 22, 'En jullie metgezel (Moehammad) is niet bezeten.'),
(5823, 81, 23, 'En voorzeker, hij heeft hem (Djibrîl) aan de heldere horizon gezien.'),
(5824, 81, 24, 'En hij is niet achterhoudend (met berichten) over het onwaarneembare.'),
(5825, 81, 25, 'En het is niet het Woord van een vervloekte Satan.'),
(5826, 81, 26, 'Waarheen wenden jullie je dan?'),
(5827, 81, 27, 'Dit is niets anders dan een Vermaning voor de werelden.'),
(5828, 81, 28, 'Voor wie van jullie het rechte (Pad) wil volgen.'),
(5829, 81, 29, 'En jullie kunnen niets willen, behalve wanneer Allah, de Heer der Werelden, het wil.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Wanneer de hemel gespleten wordt.'),
(5831, 82, 2, 'En wanneer de sterren vallen.'),
(5832, 82, 3, 'En wanneer de zeeën overstromen.'),
(5833, 82, 4, 'En wanneer de graven worden omgekeerd.'),
(5834, 82, 5, 'Dan weet de ziel wat zij heeft verricht en wat zij nagelaten heeft.'),
(5835, 82, 6, 'O mens, wat heeft jou weggeleid van jouw Heer, de Edele?'),
(5836, 82, 7, 'Degene Die jou geschapen heeft en daarna vervolmaakte en de juiste verhoudingen gaf?'),
(5837, 82, 8, 'In welke vorm Hij ook wilde heeft Hij jou samengesteld.'),
(5838, 82, 9, 'Nee, jullie loochenen zelfs de Dag des Oordeels.'),
(5839, 82, 10, 'En voorwaar, er zijn zeker bewakers (Engelen) over jullie.'),
(5840, 82, 11, 'Eervollen, schrijvenden.'),
(5841, 82, 12, 'Zij weten wat jullie doen.'),
(5842, 82, 13, 'Voorwaar, de deugdzamen verkeren zeker in gelukzaligheid.'),
(5843, 82, 14, 'En voorwaar, de zondaren verkeren zeker in de Hel.'),
(5844, 82, 15, 'Zij gaan erin op de Dag des Oordeels.'),
(5845, 82, 16, 'En zij zullen er nooit afwezig zijn.'),
(5846, 82, 17, 'En wat doet jou weten wat de Dag des Oordeels is?'),
(5847, 82, 18, 'Nogmaals, wat doet jou weten wat de Dag des Oordeels is?'),
(5848, 82, 19, 'Op die Dag is geen ziel bij machte iets voor een (andere) ziel te doen. En het bevel behoort op die Dag aan Allah.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Wee de zwendelaars!'),
(5850, 83, 2, 'Degenen die wanneer zij mensen voor zich laten wegen de volle maat eisen.'),
(5851, 83, 3, 'Maar wanneer zij voor anderen afmeten of voor hen afwegen, benadelen zij (hen).'),
(5852, 83, 4, 'Zijn diegenen dan er niet van overtuigd dat zij opgewekt zullen worden?'),
(5853, 83, 5, 'Op een geweldige Dag?'),
(5854, 83, 6, 'Op de Dag waarop de mensen voor de Heer der Werelden staan?'),
(5855, 83, 7, 'Nee, voorwaar, het boek van de zondigen is in Siddjîen.'),
(5856, 83, 8, 'En wat doet jou weten wat Siddjîen is?'),
(5857, 83, 9, 'Een volbeschreven boek.'),
(5858, 83, 10, 'Wee die Dag de loochenaars!'),
(5859, 83, 11, 'Degenen die de Dag des Oordeels loochenen.'),
(5860, 83, 12, 'En niemand loochent die behalve elke zondige overtreder.'),
(5861, 83, 13, 'Wanneer Onze Verzen aan hem worden voorgedragen, zegt hij: "Fabels van de vroegeren!"'),
(5862, 83, 14, 'Nee! Wat zij plachten te doen heeft zelfs hun harten bedekt.'),
(5863, 83, 15, 'Nee, voorwaar, zij zullen zeker op die Dag van hun Heer afgescheiden zijn.'),
(5864, 83, 16, 'Vervolgens zullen zij zeker Djahîm (de Hel) binnengaan.'),
(5865, 83, 17, 'Daarop wordt gezegd: "Dit is dat wat jullie plachten te loochenen."'),
(5866, 83, 18, 'Nee! Voorwaar, het boek van de deugdzamen is zeker in \'Illiyyôen.'),
(5867, 83, 19, 'En wat doet jou weten wat \'Illiyyôen is?'),
(5868, 83, 20, 'Een volbeschreven Boek.'),
(5869, 83, 21, 'De bij (Allah) gebrachten zijn er getuigen van.'),
(5870, 83, 22, 'Voorwaar, de deugdzamen zullen zeker in Na\'im (het Paradijs) vertoeven.'),
(5871, 83, 23, 'Op rustbanken kijken zij toe.'),
(5872, 83, 24, 'Jij herkent in hun gezichten de stralende gelukzaligheid'),
(5873, 83, 25, 'Hun wordt verzegeld drinken ingeschonken.'),
(5874, 83, 26, 'Waarvan het zegel van muskus is, en laten de wedijveraars hierom dan wedijveren.'),
(5875, 83, 27, 'En zijn mengdrank is van (de bron) Tasmîm.'),
(5876, 83, 28, 'Een bron waarvan de nabijgebrachten drinken.'),
(5877, 83, 29, 'Voorwaar, degenen die zondigden plachten over degenen die geloofden te lachen.'),
(5878, 83, 30, 'En wanneer zij aan hen voorbijgingen, knipoogden zij naar elkaar.'),
(5879, 83, 31, 'En wanneer zij terugkeerden naar hun volk, keerden zij verheugd terug.'),
(5880, 83, 32, 'En wanneer zij hen zagen, zeiden zij: "Voorwaar, zij zijn zeker dwalend."'),
(5881, 83, 33, 'En zij zijn niet als bewakers over hen gezonden.'),
(5882, 83, 34, 'Maar op deze Dag zullen degenen die geloven lachen over de ongelovigen.'),
(5883, 83, 35, 'Op rustbanken kijken zij toe.'),
(5884, 83, 36, 'Worden de ongelovigen niet vergolden voor wat zij plachten te doen?');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Wanneer de hemel verscheurd wordt.'),
(5886, 84, 2, 'En hij (de hemel) naar zijn Heer luistert en zijn plicht vervult.'),
(5887, 84, 3, 'En wanneer de aarde uitgespreid wordt.'),
(5888, 84, 4, 'En uitwerpt wat in haar is en zich ledigt.'),
(5889, 84, 5, 'En zij naar haar Heer luistert en haar plicht vervult.'),
(5890, 84, 6, 'O mens: voorwaar, jij streeft moeizaam naar jouw Heer en jij zult Hem ontmoeten.'),
(5891, 84, 7, 'Wat betreft degene die dan zijn boek in zijn rechterhand wordt gegeven.'),
(5892, 84, 8, 'Hij zal een lichte afrekening berekend worden.'),
(5893, 84, 9, 'En hij zal verheugd tot zijn familie terugkeren.'),
(5894, 84, 10, 'En wat betreft degene die dan zijn boek achter zijn rug wordt gegeven.'),
(5895, 84, 11, 'Hij zal om vernietiging schreeuwen.'),
(5896, 84, 12, 'En hij zal in Sa\'îr (de Hel) binnengaan.'),
(5897, 84, 13, 'Voorwaar, bij zijn familie was hij verheugd.'),
(5898, 84, 14, 'Voorwaar, hij dacht dat hij nooit (naar zijn Heer) zou terugkeren.'),
(5899, 84, 15, 'Nee! Voorwaar, zijn Heer sloeg hem gade.'),
(5900, 84, 16, 'Ik zweer bij het avondrood.'),
(5901, 84, 17, 'En bij de nacht en wat hij omhult.'),
(5902, 84, 18, 'En bij de maan wanneer zij vol is.'),
(5903, 84, 19, 'Jullie zullen zeker voortgaan, van fase naar fase.'),
(5904, 84, 20, 'Wat is er met hen, dat zij niet geloven?'),
(5905, 84, 21, 'En wanneer de Koran aan hen wordt voorgedragen knielen zij niet neer.'),
(5906, 84, 22, 'Degenen die niet geloven loochenen zelfs.'),
(5907, 84, 23, 'Maar Allah weet het beste wat zij verbergen.'),
(5908, 84, 24, 'Verkondig hun dan een pijnlijke bestraffing.'),
(5909, 84, 25, 'Behalve degenen die geloven en goede daden verrichten, voor hen is er een oneindige beloning.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Bij de hemel en zijn sterrenstelsels.'),
(5911, 85, 2, 'Bij de aangezegde Dag.'),
(5912, 85, 3, 'Bij de getuige en waarvan getuigd wordt.'),
(5913, 85, 4, 'Verdoemd zijn de gravers van de kuil.'),
(5914, 85, 5, 'Van het vuur met zijn brandhout.'),
(5915, 85, 6, 'Toen zij er omheen zaten.'),
(5916, 85, 7, 'En zij getuige waren van wat zij de gelovigen aandeden.'),
(5917, 85, 8, 'En zij wreekten zich alleen op hen omdat zij geloofden in Allah, de Geweldige, de Geprezene.'),
(5918, 85, 9, 'Degene Die de heerschappij over de hemelen en de aarde heeft. En Allah is Getuige over alle zaken.'),
(5919, 85, 10, 'Voorwaar, degenen die de gelovige mannen en vrouwen bestraften en daarna geen berouw toonden, voor hen is de bestraffing van de Hel en voor hen is de verbrandende bestraffing.'),
(5920, 85, 11, 'Voorwaar, degenen die geloven en goede daden verrichten, voor hen is het Paradijs waar de rivieren onder door stromen. Dat is de grote overwinning.'),
(5921, 85, 12, 'Voorwaar, de greep ven jouw Heer is zeker hard.'),
(5922, 85, 13, 'Voorwaar, Hij is het die schept en doet herleven.'),
(5923, 85, 14, 'Hij is de Vergevensgezinde, de Liefdevolle.'),
(5924, 85, 15, 'Bezitter van de Troon, de Meest Vrijgevige.'),
(5925, 85, 16, 'Uitvoerder van wat Hij wil.'),
(5926, 85, 17, 'Heeft het bericht van de legers jou bereikt?'),
(5927, 85, 18, 'Van Fir\'aun en de Tsamôed?'),
(5928, 85, 19, 'Degenen die niet geloven gaan zelfs door met loochenen.'),
(5929, 85, 20, 'En Allah omsingelt hen van achteren.'),
(5930, 85, 21, 'Het is zelfs een glorierijke Koran.'),
(5931, 85, 22, 'In de Lauhoelmahfôezh.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Bij de hemel en de verlichtende ster.'),
(5933, 86, 2, 'En wat doet jou weten wat de verlichtende ster is?'),
(5934, 86, 3, 'De doordringende ster.'),
(5935, 86, 4, 'Voorwaar, er is geen ziel of er is een bewaker over aangesteld.'),
(5936, 86, 5, 'Laat de mens er daarom over denken waarvan hij geschapen is.'),
(5937, 86, 6, 'Geschapen is hij van een spurtend water.'),
(5938, 86, 7, 'Dat komt van tussen de lendenen en de ribben.'),
(5939, 86, 8, 'Voorwaar, Hij heeft zeker macht over zijn terugkeer.'),
(5940, 86, 9, 'Op de Dag dat de harten beproefd worden.'),
(5941, 86, 10, 'Dan heeft hij geen kracht en geen helper.'),
(5942, 86, 11, 'Bij de hemel, regen bevattend.'),
(5943, 86, 12, 'En bij de aarde, planten bevattend.'),
(5944, 86, 13, 'Voorwaar, hij (de Koran) is zeker een beslissend Woord.'),
(5945, 86, 14, 'En hij bevat geen scherts.'),
(5946, 86, 15, 'Voorwaar, zij beramen een plan.'),
(5947, 86, 16, 'En Ik beraam een plan.'),
(5948, 86, 17, 'Geef daarom de ongelovigen uitstel, geef hun even uitstel.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Prijs de naam van jouw Heer, de Hoogste.'),
(5950, 87, 2, 'Degene Die geschapen heeft en vervolmaakt.'),
(5951, 87, 3, 'En Degene Die de maat bepaald heeft en Die geleid heeft.'),
(5952, 87, 4, 'Degene die het weidegras doet groeien.'),
(5953, 87, 5, 'En het daarna dor (en) zwart maakt.'),
(5954, 87, 6, 'Wij zullen (de Koran) aan jou voordragen en jij zult (hem) niet vergeten.'),
(5955, 87, 7, 'Behalve wat Allah wil. Voorwaar, Hij kent het openlijke en het verborgene.'),
(5956, 87, 8, 'En Wij zullen het gemakkelijke voor jou gemakkelijk maken.'),
(5957, 87, 9, 'Vermaan daarom, als de Vermaning (hun) baat.'),
(5958, 87, 10, 'Wie (Allah) vreest zal zich laten vermanen.'),
(5959, 87, 11, 'Maar de ellendige zal het vermijden.'),
(5960, 87, 12, 'Degene die het grote Vuur binnengaat.'),
(5961, 87, 13, 'Hij zal dan daarin niet sterven en niet leven.'),
(5962, 87, 14, 'Hij die zich reinigt (van zijn zonden) zal waarlijk slagen.'),
(5963, 87, 15, 'En (ook) die de Naam van zijn Heer gedenkt en de shalât verricht.'),
(5964, 87, 16, 'Jullie geven immers voorrang aan het wereldse leven.'),
(5965, 87, 17, 'Terwijl het Hiernamaals beter en blijvender is.'),
(5966, 87, 18, 'Voorwaar, dit staat zeker in de vroegere bladen.'),
(5967, 87, 19, 'De bladen van Ibrâhîm en Môesa.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'Heeft het bericht over de Opstanding jou bereikt?'),
(5969, 88, 2, 'Er zijn gezichten die op die Dag angstig zijn.'),
(5970, 88, 3, 'Werkend en zwoegend.'),
(5971, 88, 4, 'Die de brandende Hel binnengaan.'),
(5972, 88, 5, 'Hen wordt te drinken gegeven uit een kokende bron.'),
(5973, 88, 6, 'Er is voor hen geen ander voedsel dan van doornen.'),
(5974, 88, 7, 'Dat niet dik maakt en de honger niet stilt.'),
(5975, 88, 8, 'Er zijn gezichten die op die Dag verheugd zijn.'),
(5976, 88, 9, 'Over hun streven voldaan.'),
(5977, 88, 10, 'In een hooggelegen Paradijs.'),
(5978, 88, 11, 'Jij hoort daarin geen zinloos gepraat.'),
(5979, 88, 12, 'Daarin is een stromende bron.'),
(5980, 88, 13, 'Daarin zijn verhoogde rustbanken.'),
(5981, 88, 14, 'En gereedgezette bekers.'),
(5982, 88, 15, 'En in rijen gezetten kussens.'),
(5983, 88, 16, 'En uitgerolde tapijten.'),
(5984, 88, 17, 'Kijken zij dan niet naar hoe de kamelen zijn geschapen?'),
(5985, 88, 18, 'En naar hoe de hemel opgeheven is?'),
(5986, 88, 19, 'En naar hoe de bergen stevig gegrondvest zijn?'),
(5987, 88, 20, 'En naar hoe de aarde uitgespreid is?'),
(5988, 88, 21, 'Waarschuw daarom: voorwaar, jij (O Mohammed) bent slechts een waarschuwer.'),
(5989, 88, 22, 'Jij bent over hen geen heerser.'),
(5990, 88, 23, 'Maar degene die zich afwendt en ongelovig is.'),
(5991, 88, 24, 'Allah straft hem met de grootste bestraffing.'),
(5992, 88, 25, 'Voorwaar, tot Ons is hun terugkeer.'),
(5993, 88, 26, 'En voorwaar: aan Ons is hun afrekening.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Bij de dageraad.'),
(5995, 89, 2, 'Bij de tien nachten. (De eerste tien dagen en nachten van de maand Dzoelhiddjah)'),
(5996, 89, 3, 'Bij het even en het oneven.'),
(5997, 89, 4, 'Bij de nacht wanneer hij voorbijgaat.'),
(5998, 89, 5, 'Is daarin geen eed voor de bezitter van verstand?'),
(5999, 89, 6, 'Heb jij niet vernomen hoe jouw Heer de \'Âd heeft behandeld?'),
(6000, 89, 7, 'Van de stad Iram met zijn zuilen?'),
(6001, 89, 8, 'Zoals nog nooit een stad is geschapen in de landen?'),
(6002, 89, 9, 'En de Tsamôed die de rotsen uithieuwen in de vallei?'),
(6003, 89, 10, 'En Fir\'aun, de bezitter van de pinnen?'),
(6004, 89, 11, 'Degenen die overtraden in het land?'),
(6005, 89, 12, 'En daarin veelvuldig verderf zaaiden?'),
(6006, 89, 13, 'Toen deed jouw Heer de gesel van de bestraffing op hen neerdalen.'),
(6007, 89, 14, 'Voorwaar, jouw Heer is zeker waakzaam.'),
(6008, 89, 15, 'Wat de mens betreft, wanneer zijn Heer hem op de proef stelt en hem aanzien geeft en hem genietingen schenkt, dan zegt hij: "Mijn Heer heeft mij geëerd."'),
(6009, 89, 16, 'Maar wanneer Hij hem beproeft, en dan zijn voorzieningen beperkt, dan zegt hij: "Mijn Heer heeft mij vernederd."'),
(6010, 89, 17, 'Nee! Jullie ondersteunen immers de wees niet.'),
(6011, 89, 18, 'En jullie sporen elkaar niet aan tot het voeden van de behoeftigen.'),
(6012, 89, 19, 'En jullie verteren het erfdeel inhalig.'),
(6013, 89, 20, 'En jullie beminnen het bezit met overdreven liefde.'),
(6014, 89, 21, 'Nee, wanneer de aarde met klappen verpulverd wordt.'),
(6015, 89, 22, 'En jouw Heer komt, en de Engelen, rij na rij.'),
(6016, 89, 23, 'En op de Dag dat de Hel wordt getoond, op die Dag zal de mens zich (zijn slechte daden) herinneren, maar wat baat hem dan nog de herinnering?'),
(6017, 89, 24, 'Hij zegt: "Wee, had ik maar goede (daden) verricht tijdens mijn leven."'),
(6018, 89, 25, 'Maar op die Dag is er niemand die straft zoals Hij.'),
(6019, 89, 26, 'En niemand knevelt zoals Hij knevelt.'),
(6020, 89, 27, 'O tot rust gekomen ziel!'),
(6021, 89, 28, 'Keer terug tot jouw Heer, behaagd en welbehaagd (ontvangen door Hem).'),
(6022, 89, 29, 'En treed binnen onder Mijn dienaren.'),
(6023, 89, 30, 'En treed binnen in Mijn Paradijs.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Ik zweer bij deze stad (Mekka).'),
(6025, 90, 2, 'En jij (Mohammed) bent een bewoner van deze stad.'),
(6026, 90, 3, 'En bij de vader (Adam) en wat hij verwekte.'),
(6027, 90, 4, 'Voorzeker, Wij hebben de mens tot gezwoeg geschapen.'),
(6028, 90, 5, 'Denk hij dat niemand macht over hem heeft?'),
(6029, 90, 6, 'Hij zegt: "Ik heb veel bezit verkwist."'),
(6030, 90, 7, 'Denkt hij dat niemand hem ziet?'),
(6031, 90, 8, 'Hebben Wij niet voor hem een paar ogen gemaakt?'),
(6032, 90, 9, 'En een tong en een paar lippen?'),
(6033, 90, 10, 'En hebben Wij hem niet de twee wegen (van Leiding en dwaling) gewezen?'),
(6034, 90, 11, 'Was hij maar over de drempel gestapt!'),
(6035, 90, 12, 'En wat doet jou weten wat de drempel is?'),
(6036, 90, 13, 'Het vrijlaten van een slaaf.'),
(6037, 90, 14, 'Of het geven van voedsel op een dag van hongersnood.'),
(6038, 90, 15, 'Aan een verwante wees.'),
(6039, 90, 16, 'Of aan een arme behoeftige.'),
(6040, 90, 17, 'En dat hij behoort tot degenen die geloven en elkaar aansporen tot geduld en elkaar aansporen tot barmhartigheid.'),
(6041, 90, 18, 'Zij zijn degenen die de mensen van de rechterzijde zijn (zij zijn de bewoners van het Paradijs).'),
(6042, 90, 19, 'En degenen die niet in Onze verzen geloven; zij zijn degenen die de mensen van de linkerzijde zijn.'),
(6043, 90, 20, 'Over hen is een omhullend vuur (de Hel).');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Bij de zon en haar ochtendlicht.'),
(6045, 91, 2, 'Bij de maan wanneer zij haar (de zon) volgt.'),
(6046, 91, 3, 'Bij de dag wanneer hij het (duister) verdrijft.'),
(6047, 91, 4, 'Bij de nacht wanneer hij haar (de aarde) bedekt.'),
(6048, 91, 5, 'Bij de hemel en Wie haar gebouwd heeft.'),
(6049, 91, 6, 'Bij de aarde en Wie haar uitgespreid heeft.'),
(6050, 91, 7, 'Bij de ziel en Wie haar vervolmaakt heeft.'),
(6051, 91, 8, 'Hij Die haar haar zondigheid en haar vrees (voor Hem) bijgebracht heeft.'),
(6052, 91, 9, 'Voorwaar, hij die haar (de ziel) loutert, zal welslagen.'),
(6053, 91, 10, 'En waarlijk verliest hij die haar bederft.'),
(6054, 91, 11, 'De Tsamoed loochenden in hun buitensporigheid.'),
(6055, 91, 12, 'Toen de ellendigste onder hen opstond.'),
(6056, 91, 13, 'Daarop zei de Boodschapper van Allah (Shâlih) tot hen: "(Sla acht op) de vrouwtjeskameel van Allah en haar drinktijden."'),
(6057, 91, 14, 'Maar zij loochenden hem en slachtten haar. Toen vernietigde hun Heer hen wegens hun zonden en maakte hen met de grond gelijk.'),
(6058, 91, 15, 'En Hij vreesde de gevolgen daarvan niet.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Bij de nacht wanneer hij bedekt.'),
(6060, 92, 2, 'Bij de dag wanneer hij beschijnt.'),
(6061, 92, 3, 'Bij Wie de man en de vrouw schiep.'),
(6062, 92, 4, 'Voorwaar, jullie daden zijn zeker verschillend.'),
(6063, 92, 5, 'Wat betreft degene die geeft en (Allah) vreest.'),
(6064, 92, 6, 'En in de goede beloning (het Paradijs) gelooft.'),
(6065, 92, 7, 'Wij zullen voor hem het gemakkelijke vergemakkelijken.'),
(6066, 92, 8, 'En wat betreft degene die gierig is en zich behoefteloos waant.'),
(6067, 92, 9, 'En die de goede beloning loochent.'),
(6068, 92, 10, 'Wij zullen voor hem het moeilijke vergemakkelijken.'),
(6069, 92, 11, 'En zijn bezit zal hem niet baten wanneer hij (in de Hel) valt.'),
(6070, 92, 12, 'Voorwaar, aan Ons is zeker de Leiding.'),
(6071, 92, 13, 'En voorwaar, Ons behoort zeker het laatste (het Hiernamaals) en het eerste (het wereldse leven).'),
(6072, 92, 14, 'Daarom waarschuw Ik jullie voor een laaiend vuur (de Hel).'),
(6073, 92, 15, 'Daarin gaat slechts de ergste ellendeling binnen.'),
(6074, 92, 16, 'Die loochende en zich afwendde.'),
(6075, 92, 17, 'Maar degenen die (Allah) vrezen zal daar ver van gehouden worden.'),
(6076, 92, 18, 'Degene die van zijn bezit geeft om zich te reinigen.'),
(6077, 92, 19, 'En niet om voor een gunst aan iemand beloond te worden.'),
(6078, 92, 20, 'Maar om het welbehagen van zijn Heer, de Verhevene, te zoeken.'),
(6079, 92, 21, 'Hij zal zeker tevreden zijn.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Bij het ochtendlicht.'),
(6081, 93, 2, 'En bij de nacht wanneer het geheel donker is.'),
(6082, 93, 3, 'Jouw Heer heeft jou (O Moehammad) niet verlaten en Hij is niet kwaad (op jou).'),
(6083, 93, 4, 'En het latere (het Hiernamaals) is zeker beter voor jou dan het eerste (het wereldse leven).'),
(6084, 93, 5, 'En jouw Heer zal jou zeker gunsten schenken, zodat jij tevreden zult zijn.'),
(6085, 93, 6, 'Heeft Hij jou niet als wees gevonden en jou in bescherming genomen?'),
(6086, 93, 7, 'En Hij heeft jou dwalend gevonden en jou geleid.'),
(6087, 93, 8, 'En Hij heeft jou behoeftig gevonden en rijk gemaakt.'),
(6088, 93, 9, 'Wat de wees betreft: beledig hem niet.'),
(6089, 93, 10, 'En wat de bedelaar betreft: wijs hem niet af.'),
(6090, 93, 11, 'En wat de gunsten van jouw Heer betreft: spreek daarover!');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Hebben Wij niet jouw borst verruimd (O Mohammed)?'),
(6092, 94, 2, 'En Wij hebben jouw last van je weggenomen.'),
(6093, 94, 3, 'Die jouw rug belastte.'),
(6094, 94, 4, 'En Wij hebben jouw roem verhoogd.'),
(6095, 94, 5, 'Voorwaar, zo komt met de moeilijkeid de verlichting.'),
(6096, 94, 6, 'Voorwaar, met de moeilijkheid komt de verlichting.'),
(6097, 94, 7, 'Wanneer jij dan een taak volbracht hebt, streef dan (verder).'),
(6098, 94, 8, 'En richt jouw verlangen tot jouw Heer.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Bij de vijg en de olijf.'),
(6100, 95, 2, 'Bij de berg Sinaï.'),
(6101, 95, 3, 'Bij deze veilige stad (Mekkah).'),
(6102, 95, 4, 'Voorzeker, Wij hebben de mens in de beste vorm geschapen.'),
(6103, 95, 5, 'Daarna doen Wij hem terugkeren tot het laagste van het laagste.'),
(6104, 95, 6, 'Behalve degenen die geloven en goede daden verrichten. Voor hen is er een ononderbroken beloning.'),
(6105, 95, 7, 'Wat doet jullie dan het oordeel nog loochenen?'),
(6106, 95, 8, 'Is Allah niet de Rechtvaardigste der Rechters?');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Lees voor! In de naam van jouw Heer, Die heeft geschapen.'),
(6108, 96, 2, 'Hij heeft de mens geschapen van een bloedklomp.'),
(6109, 96, 3, 'Lees voor! En jouw Heer is de Meest Edele.'),
(6110, 96, 4, 'Degene Die onderwezen heeft met de pen.'),
(6111, 96, 5, 'Hij heeft de mens onderwezen wat hij niet wist.'),
(6112, 96, 6, 'Nee, voorwaar, de mens is zeker in overtreding.'),
(6113, 96, 7, 'Omdat hij zichzelf als behoefteloos beschouwt.'),
(6114, 96, 8, 'Voorwaar, tot jouw Heer is de terugkeer.'),
(6115, 96, 9, 'Wat denk jij van hem die verbiedt?'),
(6116, 96, 10, 'Een dienaar wanneer hij de shalât verricht.'),
(6117, 96, 11, 'Wat denk je, als hij (Mohammed) de leiding volgt?'),
(6118, 96, 12, 'Of hij tot Taqwa oproept?'),
(6119, 96, 13, 'Wat denk jij, als hij (Abôe Djahl) loochent en zich afwendt?'),
(6120, 96, 14, 'Weet hij dan niet dat Allah (hem) ziet?'),
(6121, 96, 15, 'Nee, als hij niet ophoudt, dan zullen Wij hem bij zijn voorhoofdslok grijpen.'),
(6122, 96, 16, 'Een leugenachtige, zondige voorhoofdslok.'),
(6123, 96, 17, 'Laat hem dan zijn bondgenoten roepen.'),
(6124, 96, 18, 'Wij zullen de Zabâniyah roepen.'),
(6125, 96, 19, 'Nee, gehoorzaam hem niet, en kniel je neer en zoek toenadering (tot Allah).');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Voorwaar, Wij hebben hem (de Koran) neergezonden in de Waardevolle Nacht (Lailatoel Qadr).'),
(6127, 97, 2, 'En wat doet jullie weten wat de Waardevolle Nacht is?'),
(6128, 97, 3, 'De Waardevolle Nacht is beter dan duizend maanden.'),
(6129, 97, 4, 'De Engelen en de Geest (Djibrîl) daalden in haar neer met de toestemming van hun Heer, voor elke beschikking.'),
(6130, 97, 5, 'Vrede heerst (in deze nacht), tot aan de ochtendschemering.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'De ongelovigen onder de Lieden van de Schrift en de veelgodenaanbidders houden niet op (ongelovig te zijn) tot er een duidelijk bewijs tot hen komt.'),
(6132, 98, 2, 'Een Boodschapper van Allah die gereinigde bladen voordraagt.'),
(6133, 98, 3, 'Waarin rechtzinnige boeken (de Koran) zijn.'),
(6134, 98, 4, 'En degenen aan wie het Boek is gegeven splitsten zich pas op nadat het duidelijke bewijs tot hen gekomen was.'),
(6135, 98, 5, 'Zij werden niets anders bevolen dan Allah met zuivere aanbidding te aanbidden, als Hoenafâ. En (ook) de shalât te verrichten en de zakât te geven en dat is de rechte godsdienst.'),
(6136, 98, 6, 'Voorwaar, degenen die ongelovig zijn onder de Lieden van de Schrift en de veelgodenaanbidders zullen in het vuur van de Hel eeuwig levenden zijn. Zij zijn degenen die de slechtste schepsels zijn.'),
(6137, 98, 7, 'Voorwaar, degenen die geloven en goede werken verrichten, zij zijn degenen die de beste schepselen zijn.'),
(6138, 98, 8, 'Hun beloningen bij hun Heer zijn de Tuinen van \'Adn (het Paradijs), waar de rivieren onder door stromen, zij zijn eeuwig levenden daarin, voor altijd. Allah is met hen behaagd en zij zijn met Hem behaagd. Dat is voor wie zijn Heer vreest.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Wanneer de aarde door haar beving wordt geschud.'),
(6140, 99, 2, 'En de aarde haar lasten naar buiten keert.'),
(6141, 99, 3, 'En de mens zegt: "Wat is er met haar aan de hand?"'),
(6142, 99, 4, 'Op die Dag zal zij haar berichten bekendmaken.'),
(6143, 99, 5, 'Omdat jouw Heer het haar bevolen heeft.'),
(6144, 99, 6, 'Op die Dag zullen de mensen in verschillende groepen tevoorschijn komen om hun daden te zien.'),
(6145, 99, 7, 'Wie iets goeds deed ter grootte van een mosterdzaadje, zal het dan zien.'),
(6146, 99, 8, 'En wie iets kwaads deed ter grootte van een mosterdzaadje, zal het dan zien.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Bij de snuivend voortrennenden.'),
(6148, 100, 2, 'Die vonken slaan.'),
(6149, 100, 3, 'Die in de ochtendschemering aanvallen.'),
(6150, 100, 4, 'Die daarbij stof opwerpen.'),
(6151, 100, 5, 'Die dan het midden van de gelederen (van de vijand) doorbreken.'),
(6152, 100, 6, 'Voorwaar, de mens is zijn Heer zeker ondankbaar.'),
(6153, 100, 7, 'En voorwaar, hij is daar zeker getuige van.'),
(6154, 100, 8, 'En voorwaar, hij heeft zeker een hevige liefde voor bezit.'),
(6155, 100, 9, 'Weet hij dan niet dat, wanneer naar buiten wordt gekeerd wat in de graven is.'),
(6156, 100, 10, 'En onthuld wordt wat in de harten is.'),
(6157, 100, 11, 'Dat hun Heer op die Dag zeker Alwetend over hen is?');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'De Daverende (De Dag der Opstanding).'),
(6159, 101, 2, 'Wat is de Daverende?'),
(6160, 101, 3, 'En wat doet jou weten wat de Daverende is?'),
(6161, 101, 4, 'Op de Dag dat de mensen als verstrooide motten zijn.'),
(6162, 101, 5, 'En de bergen als vespreide wolvlokken.'),
(6163, 101, 6, 'Wat betreft degene van wie dan zijn weegschaal (met goede daden) zwaar weegt.'),
(6164, 101, 7, 'Hij zal een behaaglijk leven leiden.'),
(6165, 101, 8, 'En wat betreft degene van wie zijn weegschaal licht weegt.'),
(6166, 101, 9, 'Zijn verblijfplaats is dan Hawiyah*2 (de Hel).'),
(6167, 101, 10, 'En wat doet jou weten wat zij is?'),
(6168, 101, 11, 'Een verbandende Hel.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Jullie gaan op in de wedijver om meer.'),
(6170, 102, 2, 'Totdat jullie in de graven terechtkomen.'),
(6171, 102, 3, 'Nee, jullie zullen het weten.'),
(6172, 102, 4, 'Nogmaals, nee! Jullie zullen het weten.'),
(6173, 102, 5, 'Nee! Als jullie het maar met zekere kennis zouden weten!'),
(6174, 102, 6, 'Jullie zullen zeker de Hel zien.'),
(6175, 102, 7, 'Nogmaals, jullie zullen haar met een zeker oog zien.'),
(6176, 102, 8, 'Dan zullen jullie op die Dag ondervraagd worden over de genietingen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Bij de tijd.'),
(6178, 103, 2, 'Voorwaar, de mens lijdt zeker verlies.'),
(6179, 103, 3, 'Behalve degenen die geloven en goede daden verrichten en elkaar aansporen tot de Waarheid en elkaar aansporen tot geduld.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Wee elke lasteraar en roddelaar.'),
(6181, 104, 2, 'Degene die bezit verzamelt en het telkens telt.'),
(6182, 104, 3, 'Hij denkt dat zijn bezit hem eeuwiglevend maakt.'),
(6183, 104, 4, 'Nee! Hij zal zeker in de vernietiger gesmeten worden.'),
(6184, 104, 5, 'En wat doet jou weten wat de vernietiger is?'),
(6185, 104, 6, 'Het door Allah aangestoken Vuur (de Hel).'),
(6186, 104, 7, 'Dat tot in de harten doordringt.'),
(6187, 104, 8, 'Voorwaar, het zal hen omhullen.'),
(6188, 104, 9, 'In langgerekte zuilen.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Heb jij niet vernomen hoe jouw Heer met de mensen van de olifant heeft gehandeld?'),
(6190, 105, 2, 'Heeft Hij hun plan niet verijdeld?'),
(6191, 105, 3, 'En Hij heeft over hen zwermen vogels gezonden.'),
(6192, 105, 4, 'Die stenen van klei op hen wierpen.'),
(6193, 105, 5, 'Zo maakte Hij hen als (door wormen) aangevreten bladeren.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'Vanwege de gewoonte*1 van de Qoeraisj.'),
(6195, 106, 2, 'Hun gewoonte van het maken van tochten in de winter en de zomer.'),
(6196, 106, 3, 'Daarom moeten zij de Heer van dit Huis aanbidden.'),
(6197, 106, 4, 'Degene Die hun tegen de honger voedt en hen veilig stelt voor de angst.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Weet jij wie degene is die (de Dag van) het Oordeel loochent?'),
(6199, 107, 2, 'Dat is degene die de wees wegduwt.'),
(6200, 107, 3, 'En hij spoort niet aan tot het geven van voedsel aan de behoeftigen.'),
(6201, 107, 4, 'Wee dan de verrichters van de shalât,'),
(6202, 107, 5, 'Degenen die onachtzaam zijn met hun shalât.'),
(6203, 107, 6, 'Degenen die er een vertoning van maken.'),
(6204, 107, 7, 'En die de levensbenodigdheden tegenhouden.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Waarlijk, Wij hebben jou de overvloed geschonken.'),
(6206, 108, 2, 'Verricht daarom de shalât voor jouw Heer en slacht offerdieren.'),
(6207, 108, 3, 'Voorwaar, jouw hater, hij is het die afgesneden is.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'Zeg (O Mohammed): "O ongelovigen.'),
(6209, 109, 2, 'Ik aanbid niet wat jullie aanbidden.'),
(6210, 109, 3, 'En jullie zijn geen aanbidders van wat ik aanbid.'),
(6211, 109, 4, 'En ik zal nooit een aanbidder worden van wat jullie aanbidden.'),
(6212, 109, 5, 'En jullie zullen nooit aanbidders worden van wat ik aanbid.'),
(6213, 109, 6, 'Daarom, voor jullie jullie godsdienst en voor mij mijn godsdienst.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Als de hulp van Allah en de overwinning zijn gekomen.'),
(6215, 110, 2, 'En jij (O Mohammed) de mensen in grote groepen tot de godsdienst van Allah ziet toetreden.'),
(6216, 110, 3, 'Prijs dan de Glorie van jouw Heer en vraag Hem om vergeving. Voorwaar, Hij is Berouwaanvaardend.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Vernietigd zijn de handen van Aboe Lahab en vernietigd is hij.'),
(6218, 111, 2, 'Zijn bezit en wat hij voortbracht, baat hem niet.'),
(6219, 111, 3, 'Hij zal een vuur van vlammen (de Hel) binnengaan.'),
(6220, 111, 4, 'En ook zijn vrouw, aandraagster van brandhout.'),
(6221, 111, 5, 'Om haar nek een touw van vezels.');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'Zeg: "Hij is Allah, de Enige.'),
(6223, 112, 2, 'Allah is de Enige van Wie al het geschapene afhankelijk is.'),
(6224, 112, 3, 'Hij heeft niet verwekt en is niet verwekt.'),
(6225, 112, 4, 'En niet één is aan Hem gelijkwaardig."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'Zeg: "Ik zoek bescherming bij de Heer der dageraad.'),
(6227, 113, 2, 'Tegen het kwaad dat Hij geschapen heeft.'),
(6228, 113, 3, 'En tegen het kwaad van de donkere nacht wanneer hij aanbreekt.'),
(6229, 113, 4, 'En tegen het kwaad van hen die op knopen blazen.'),
(6230, 113, 5, 'En tegen het kwaad van een jaloerse wanneer deze jaloers is."');
INSERT INTO `nl_siregar` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'Zeg: "Ik zoek bescherming bij de Heer van de mensen.'),
(6232, 114, 2, 'De Koning van de mensen.'),
(6233, 114, 3, 'De God van de mensen.'),
(6234, 114, 4, 'Tegen het kwaad van de wegsluipende influisteraar.'),
(6235, 114, 5, 'Degene die in de harten van de mensen influistert.'),
(6236, 114, 6, 'Van de Djinn\'s en de mensen.');
