INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'I Guds, den Barmhjertiges, den Nåderikes navn'),
(2, 1, 2, 'Lovet være Gud, all verdens Herre,'),
(3, 1, 3, 'Han, den Barmhjertige, den Nåderike,'),
(4, 1, 4, 'Han, Herren over dommens dag.'),
(5, 1, 5, 'Deg tilber vi, vi søker hjelp hos Deg.'),
(6, 1, 6, 'Led oss på den rette vei!'),
(7, 1, 7, 'Deres vei, som Du har beredt glede, ikke deres, som har vakt Din vrede, eller deres, som har valgt den falske vei.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'Alif Lam Mim'),
(9, 2, 2, 'Dette er skriften, tvil har ingen plass. Den gir ledelse for de gudfryktige,'),
(10, 2, 3, 'for dem som tror på det skjulte, forretter bønnen, og som gir av det som Vi har gitt dem,'),
(11, 2, 4, 'for dem som tror på det som er åpenbart for deg, og som er åpenbart før din tid, og som er fullt forvisset om det hinsidige.'),
(12, 2, 5, 'Disse er det som følger Herrens ledelse. Dem vil det gå godt!'),
(13, 2, 6, 'De vantro, for dem er det likegyldig om du advarer dem eller ikke. De vil ikke tro.'),
(14, 2, 7, 'Gud har forseglet deres hjerter og ører, og deres øyne er dekket. For dem er en svær straff i vente.'),
(15, 2, 8, 'Det er slike som sier: «Vi tror på Gud og dommens dag,» men de er ikke troende.'),
(16, 2, 9, 'De vil lure Gud og de troende, men de lurer ingen, unntatt seg selv. Men de fatter det ikke.'),
(17, 2, 10, 'De har en sykdom i sine hjerter, og Gud har latt sykdommen øke på. For dem er en smertelig straff i vente, fordi de har løyet.'),
(18, 2, 11, 'Og blir det sagt til dem: «Skap ikke ufred på jorden,» så sier de: «Vi setter bare tingene på rett plass.»'),
(19, 2, 12, 'Sannelig, ufredsskapere er de. Men de fatter det ikke.'),
(20, 2, 13, 'Og sier man til dem: «Tro nå, slik som godtfolk gjør,» så svarer de: «Skal vi tro som alminnelige tosker?» Sannelig, det er de selv som er tosker. Men dette forstår de ikke.'),
(21, 2, 14, 'Når de så møter troende, sier de: «Vi tror,» men når de er alene med sine demoner, sier de til dem: «Vi står last og brast med dere. Vi bare drev ap.»'),
(22, 2, 15, 'Men Gud skal lære dem å drive ap! Han vil la dem ture frem i deres oppsetsighet på deres blinde ferd.'),
(23, 2, 16, 'De har kjøpt villfarelse for rettlednings pris. Men dette er en dårlig handel, og de er ikke på rett vei.'),
(24, 2, 17, 'De kan sammenlignes med at noen tenner opp ild, men når den lyser opp, tar Gud deres lys bort og lar dem sitte i mørket uten å se.'),
(25, 2, 18, 'Døve, stumme, blinde, finner de ingen vei tilbake.'),
(26, 2, 19, 'Eller, det er som om det var i et skybrudd fra oven, med mørke, torden og lyn. De putter fingrene i ørene for tordenen, livende redde. Gud har makt over de vantro.'),
(27, 2, 20, 'Lynglimtene nesten blender dem. Hver gang det lyser opp for dem, går de fremover, men når det blir mørkt rundt dem, står de stille. Hvis det var Guds vilje, kunne Han frata dem både hørsel og syn! Gud evner alt!'),
(28, 2, 21, 'Dere mennesker, tjen Herren, Han som har skapt dere og dem som var før dere, så dere må bli gudfryktige –'),
(29, 2, 22, 'Han som har gjort jorden til leie og himmelen til tak, Han som sender regn fra oven, hvorved Han frembringer frukter til næring for dere. Sett ingen ved Guds side, når dere vet bedre!'),
(30, 2, 23, 'Om dere huser tvil om det Vi har åpenbart for Vår tjener, så bring en sure like godt, og kall frem de vitner dere har, som ikke er Gud, om dere snakker sant!'),
(31, 2, 24, 'Men greier dere det ikke, og dere vil aldri greie det, så pass dere for Ilden, hvis brensel er mennesker og steiner, gjort klar for de vantro!'),
(32, 2, 25, 'Men bebud for dem som tror og lever rettskaffent, at det venter dem haver, hvor bekker sildrer. Når frukt derfra blir gitt dem å spise, sier de: «Dette er jo som det vi før fikk å spise,» og det de får, ligner på det. Og det venter dem rene hustruer. Der skal de være og bli!'),
(33, 2, 26, 'Gud forsmår ikke å bruke fra den minste mygg som lignelse. De troende forstår at deri ligger sannhet fra Herren. De vantro derimot sier: «Hva kan nå Gud mene med noe slikt?» Med dette villeder Han mange, og rettleder mange, men Han villeder bare de syndefulle,'),
(34, 2, 27, 'slike som bryter forpliktelsene overfor Gud etter at de er inngått, som skjærer over det Gud har befalt skal høre sammen, og skaper ufred på jorden. Disse vil bli tapere.'),
(35, 2, 28, 'Hvordan kan dere avvise å tro på Gud? Dere var uten liv, og Han gav dere liv. Så vil Han la dere dø, og så vil Han atter gi dere liv og la dere vende tilbake, til Ham.'),
(36, 2, 29, 'Han har skapt for dere alt som på jorden er. Så begav Han seg til himmelen, og formet de syv himler. Han vet alle ting!'),
(37, 2, 30, 'En gang sa Herren til englene: «Jeg vil sette en forvalter på jorden.» De svarte: «Vil Du innsette en som lager ufred og blodsutgytelse, mens vi, vi lovsynger Din pris og Din hellighet?» Gud sa: «Jeg vet hva dere ikke vet.»'),
(38, 2, 31, 'Og Han lærte Adam alle tings navn – viste dem derpå for englene, og sa: «Si meg disses navn, om dere skal være ærlige.»'),
(39, 2, 32, 'De svarte: «Priset være Du, vi vet ikke annet enn det Du har lært oss! Det er Du som er den Allvitende, den Vise.»'),
(40, 2, 33, 'Så sa Han: «Nå Adam, si dem navnene.» Da han hadde sagt dem navnene, sa Han: «Har Jeg ikke sagt dere at Jeg kjenner det skjulte i himlene og på jord. Jeg vet alt dere legger for dagen eller skjuler.»'),
(41, 2, 34, 'En gang sa Vi til englene: «Fall ned for Adam!» De gjorde det alle, unntatt Iblis (Satan). Han nektet og var hovmodig, og var en gudsfornekter.'),
(42, 2, 35, 'Vi sa: «Adam, bo i haven, du og din hustru. Spis av den alt dere ønsker, hvor dere vil, men kom ikke nær dette tre, slik at dere blir urettferdige.»'),
(43, 2, 36, 'Men Satan forårsaket at de gled ut og ble jaget bort fra der de var. Gud sa: «Kom dere ut (ned)! Noen av dere skal være andres fiende, og på jorden skal dere bo og finne utkomme for en tid.»'),
(44, 2, 37, 'Så fikk Adam ord fra sin Herre, og Han vendte seg mot ham i nåde. Han er den Tilgivende, den Nåderike.'),
(45, 2, 38, 'Gud sa: «Ut med dere alle herfra! Dog vil det komme ledelse til dere fra Meg. Og de som følger Min ledelse, over dem skal ingen frykt hvile, ei heller sorg.'),
(46, 2, 39, 'Men de utakknemlige vantro som holder Vårt ord for løgn, de skal bli Ildens folk, og der skal de være og bli.»'),
(47, 2, 40, 'Israels barn, kom i hu den nåde som Jeg har vist dere! Oppfyll deres pakt med Meg. Jeg oppfyller Min del overfor dere. Så frykt Meg.'),
(48, 2, 41, 'Tro på det Jeg har åpenbart, som stadfester det som før er blitt dere til del. Vær ikke de første til å avvise det. Selg ikke Mitt ord for en billig pris! Meg skal dere frykte!'),
(49, 2, 42, 'Kle ikke sannheten i løgn og skjul den ikke når dere vet bedre.'),
(50, 2, 43, 'Forrett bønnen, gi det rituelle bidrag og bøy dere sammen med dem som bøyer seg.'),
(51, 2, 44, 'Pålegger dere andre fromhet og glemmer dere selv? Og det mens dere leser skriften? Forstår dere da ikke?'),
(52, 2, 45, 'Be om hjelp i tålmodighet og bønn. Dette er hardt, unntatt for de ydmyke,'),
(53, 2, 46, 'for dem som regner med at de skal møte sin Herre, at de skal vende tilbake til Ham.'),
(54, 2, 47, 'Israels barn, kom i hu den nåde som Jeg har vist dere, og at Jeg valgte dere fremfor all verden!'),
(55, 2, 48, 'Frykt den dag når ingen kan tre i en annens sted, når ingen som taler en annens sak blir godtatt, når ingen løsepenger godtas, når ingen hjelp kan gis.'),
(56, 2, 49, 'Kom i hu da Vi reddet dere fra Faraos folk, som påla dere store plager, som drepte deres sønner og lot deres kvinner leve. I dette lå en stor prøvelse fra Herren.'),
(57, 2, 50, 'Og da Vi spaltet havet og reddet dere, mens Vi lot Faraos folk drukne for øynene på dere.'),
(58, 2, 51, 'Og den gang da Vi gav Moses 40 dager. Da laget dere kalven mens han var borte, og gjorde det som urett var.'),
(59, 2, 52, 'Men Vi tilgav dere etterpå, så dere måtte vise takknemlighet.'),
(60, 2, 53, 'Og den gang da Vi gav Moses skriften og kriteriet, så dere måtte finne den rette vei.'),
(61, 2, 54, 'Og da Moses talte til sitt folk: «Dere har stelt dere ille ved å lage denne kalven. Vend om til deres skaper, og drep de skyldige blant dere. Dette ville være det beste for dere overfor deres skaper, og Han vil vise dere nåde. Han er den Tilgivende, den Nåderike!»'),
(62, 2, 55, 'Og den gang dere sa: «Hør Moses, vi kan ikke tro deg før vi ser Gud med våre egne øyne.» Men da ble dere rammet av lynet, mens dere stod der og stirret.'),
(63, 2, 56, 'Derpå kalte Vi dere tilbake til livet, etter at dere var døde, så dere måtte vise takknemlighet.'),
(64, 2, 57, 'Så lot Vi skyer overskygge dere, og sendte dere manna og vaktler: «Spis av det gode som Vi har skaffet dere!» For Oss rammet de ikke med sin urett. Det var dem selv det slo tilbake på.'),
(65, 2, 58, 'Og den gang Vi sa: «Dra inn i denne landsbyen og forsyn dere hvor dere måtte ønske i rikelig monn. Gå inn gjennom porten, ydmykt bøyet, med ordene ’syndenes forsakelse’. Vi vil tilgi deres overtredelser, og gi dem som gjør vel, mer til.»'),
(66, 2, 59, 'Men de urettferdige byttet og forvred disse ord til noe annet enn det som var foresagt dem. Så sendte Vi over de urettferdige en straffedom fra himmelen for deres ugudelighet.'),
(67, 2, 60, 'Og den gang da Moses bad om vann til sitt folk, og Vi sa: «Slå med din stav på fjellet.» Og 12 kilder brøt frem fra det. Alle kjente nå sin drikkeplass. «Spis og drikk av Guds gaver og synd ikke ved å stifte ufred på jorden.»'),
(68, 2, 61, 'Og da dere sa: «Hør Moses, vi holder ikke ut med bare én sort mat! Be til din Herre for oss, at Han frembringer til oss det jorden lar vokse av grønnsaker, agurker, korn, linser og løk.» Moses svarte: «Vil dere bytte det verdifulle mot det mindreverdige? Dra ned til Egypt! Der kan dere få det dere spør etter!» Og de ble slått med elendighet og usselhet og pådrog seg Guds vrede. Dette fordi de viste vantro og utakknemlighet overfor Herrens tegn, og i urett drepte profetene. Dette fordi de var ulydige og gikk over grensen.'),
(69, 2, 62, 'Men de troende, og jøder, kristne og sabeere, alle som tror på Gud og på dommens dag, og som gjør det som er rett, de har lønn i vente hos sin Herre. Over dem skal ingen frykt hvile, ei heller sorg.'),
(70, 2, 63, 'Da Vi sluttet pakten med dere, mens Vi hevet fjellet over dere: «Hold godt fast ved det Vi har gitt dere, og husk dets innhold, – at dere må bli gudfryktige.»'),
(71, 2, 64, 'Men etterpå vendte dere dere bort, og hadde det ikke vært for Guds velvilje og Hans nåde, ville dere vært fortapt.'),
(72, 2, 65, 'Dere vet hvordan det gikk med dem som overtrådte sabbatsbudet? Vi sa til dem: «Bli til frastøtende aper!»'),
(73, 2, 66, 'Dette gjorde Vi som et avskrekkende eksempel for både samtid og ettertid, og for de gudfryktige til oppbyggelse.'),
(74, 2, 67, 'Og den gang Moses sa til sitt folk: «Gud befaler dere å ofre en ku,» og de svarte: «Spøker du med oss?» Men Moses sa: «Jeg tar Gud til hjelp at jeg ikke er uvitende.»'),
(75, 2, 68, 'Og de svarte: «Påkall din Herre så Han klargjør hvordan den skal være.» Og Moses sa: «Gud sier at det skal være en ku, ikke gammel, ikke ung, men midt imellom. Gjør nå det dere er blitt befalt!»'),
(76, 2, 69, 'Men de sa: «Påkall din Herre så Han klargjør dens farge for oss.» Og Moses svarte: «Gud sier at den skal være en pen, gyllengul ku, som gleder dem som ser den.»'),
(77, 2, 70, 'Men de fortsatte: «Påkall din Herre, så Han klargjør hvordan den skal være. Kuer fortoner seg like for oss, men hvis Gud vil, kan vi nok komme på rett vei.»'),
(78, 2, 71, 'Moses sa: «Gud sier at det skal være en ku som ikke er skjemt av pløying eller vanning, lytefri og frisk.» Så sa de: «Nå kom du med sannheten.» Så slaktet de kua, men det var ikke mer enn så vidt de kom seg til det.'),
(79, 2, 72, 'Og den gang da dere hadde drept et menneske og kom i disputt om dette! Gud brakte lys over det dere holdt skjult.'),
(80, 2, 73, 'Vi sa: «Slå til ham (den døde) med en del av den (kua)!» Slik oppvekker Gud de døde til liv, og viser dere Sine jærtegn, så dere må få forståelse.'),
(81, 2, 74, 'Men så ble deres hjerter forherdet etter dette og ble som stein, eller enda hardere. For blant steiner kan bekker flyte, vann kommer ut av sprekker, og det er steiner som styrter ned, av respekt for Gud. Gud overser ikke hva dere gjør.'),
(82, 2, 75, 'Kan dere håpe på at disse vil tro dere, når en del av dem allerede har hørt Guds ord, og så forvrir det etter at de har oppfattet det, enda de vet bedre?'),
(83, 2, 76, 'Når de møter de troende, sier de: «Vi tror.» Men når de er alene med hverandre, sier de: «Har noen fortalt dem hva Gud har åpenbart for oss, slik at de kan argumentere med det mot oss overfor Herren? Forstår dere da ikke?»'),
(84, 2, 77, 'Er de ikke klar over at Gud vet om det de hemmeligholder og det de kunngjør?'),
(85, 2, 78, 'Blant dem finnes vanlige folk, som ikke kjenner skriften, men bare sine egne håp og formodninger.'),
(86, 2, 79, 'Så ve dem som skriver skriften med egne hender og sier: «Dette er fra Gud,» for å selge det for en billig penge. Ve dem for det deres hender har skrevet! Ve dem for det de har pådratt seg!'),
(87, 2, 80, 'De sier: «Ilden rører ikke oss mer enn noen få dager.» Si: «Har dere noen avtale med Gud? I så fall, Gud bryter aldri sitt løfte. Eller sier dere noe om Gud som dere ikke har kunnskap om?»'),
(88, 2, 81, 'Nei, sannelig, de som begår misgjerninger og domineres av synd, disse er Ildens folk, og der skal de være og bli.'),
(89, 2, 82, 'Men de som tror, og lever rettskaffent, disse er paradisets folk, og der skal de være og bli.'),
(90, 2, 83, 'Den gang Vi sluttet pakt med Israels barn: «Dere skal ikke tilbe noe annet enn Gud! Vis godhet mot foreldre og nærstående, mot faderløse og fattige. Tal vennlig til din neste, forrett bønnen, og gi det rituelle bidrag.» Så falt dere fra, unntatt noen få av dere, og vendte dere bort.'),
(91, 2, 84, 'En gang sluttet Vi pakt med dere: «Dere skal ikke utgyte deres eget blod, og ikke jage deres egne ut fra deres bosteder.» Dette godtok dere, og dere var selv vitner.'),
(92, 2, 85, 'Dog er dere slik at dere dreper deres egne, og driver bort en gruppe av deres egne fra deres bosteder, idet dere setter hverandre opp mot dem i synd og fiendskap. Og hvis de kommer til dere som fanger, ordner dere løskjøping. Men utjagingen var ulovlig. Er det slik at dere tror på en del av skriften og forkaster en annen? Det blir ingen annen belønning for dem som opptrer slik, enn skam under livet på jorden, og på oppstandelsens dag blir de sendt til den strengeste straff. Gud overser ikke hva dere gjør.'),
(93, 2, 86, 'Disse er det som har kjøpt jordelivet på bekostning av det hinsidige. Det blir ingen lettelse i straffen for dem. De kan ikke hjelpes.'),
(94, 2, 87, 'Vi gav i sin tid Moses skriften, og Vi sendte sendebud etter ham. Jesus, Marias sønn, gav Vi klare bevis, og Vi støttet ham med Den Hellige Ånd. Er det ikke slik at hver gang et sendebud kommer til dere med noe dere ikke liker, så opptrer dere hovmodig, beskylder noen av dem for løgn og dreper andre?'),
(95, 2, 88, 'De svarer: «Våre hjerter er uomskårne.» Sannelig, Gud forbanner dem for deres vantro. Hvor lite de tror!'),
(96, 2, 89, 'Da det så kom en bok fra Gud til dem, som stadfestet det de allerede har – og før hadde de bedt om fremgang overfor de vantro; når det nå fra dette hold kom til dem noe de kjenner, så ville de ikke tro på det. Guds forbannelse hviler over de vantro!'),
(97, 2, 90, 'Hvilken ussel pris de har solgt seg for! Å fornekte Guds åpenbaring i uvilje og nag over at Gud sender Sin nåde over hvem Han vil av Sine tjenere. De har pådratt seg vrede på vrede. De vantro venter en ydmykende straff.'),
(98, 2, 91, 'Og når man sier til dem: «Tro på det Gud har åpenbart,» så svarer de: «Vi tror på det som er åpenbart for oss.» De fornekter det som er kommet senere, selv om det er sannheten, som stadfester det de allerede har. Si: «Hvorfor drepte dere i tidligere tider Guds profeter hvis det er så at dere er troende?»'),
(99, 2, 92, 'Moses kom til dere med klar beskjed. Så laget dere gullkalven i hans fravær, og dere begikk urett.'),
(100, 2, 93, 'Den gang Vi sluttet pakten med dere, og hevet fjellet over dere: «Hold godt fast ved det Vi har gitt dere, og hør.» De sa: «Vi hører, men trosser.» For gullkalven var gitt plass i deres hjerter på grunn av deres vantro. Si: «Ondt er det deres tro krever av dere, om dere er troende.»'),
(101, 2, 94, 'Si: «Hvis det forholder seg slik at paradiset etter Guds bestemmelse er forbeholdt bare dere fremfor andre mennesker, så ønsk dere da døden, om dere snakker sant!»'),
(102, 2, 95, 'Men dette vil de aldri ønske på grunn av sine gjerninger. Gud kjenner de urettferdige.'),
(103, 2, 96, 'Og du vil finne at de er ivrigst av alle til å klynge seg til livet, langt mer enn hedningene. Enhver ønsker at han må få leve i tusen år. Men om så var, holder det ham ikke unna straffen. For Gud ser hva de gjør.'),
(104, 2, 97, 'Si: «Om noen er Gabriels fiende – og det var han som brakte Koranen til ditt hjerte med Guds bifall for å stadfeste det som forelå før den, og som ledelse og gledesbudskap for de troende –'),
(105, 2, 98, 'om noen er fiende for Gud og Hans engler, for Hans sendebud, for Gabriel og Mikael, så er Gud de vantros fiende.»'),
(106, 2, 99, 'Vi har åpenbart for deg klare ord, bare de syndefulle fornekter dem.'),
(107, 2, 100, 'Skal det være slik at hver gang de slutter en avtale, er det en del av dem som bryter den? Sannelig, de fleste av dem tror ikke.'),
(108, 2, 101, 'Og når et sendebud fra Gud er kommet til dem som stadfester det de har fra før, så slenger en del av dem som har fått skriften, den nye Guds bok bak ryggen, som om de ingenting visste.'),
(109, 2, 102, 'De følger det demonene lærte i Salomos tid. Men Salomo mistet ikke sin tro, men demonene, de var vantro. De lærte folk trolldom og det de to falne engler i Babylon, Harut og Marut, hadde fått. Men disse to underviste ingen uten å si: «Vi er bare en prøvelse! Så bli ikke vantro.» Fra disse to lærte de hvordan man kan så splid mellom mann og hustru. Men de skadet ingen ved dette, uten Guds samtykke. De lærte bare det som skadet dem, og var uten nytte. Og de som ervervet noe av dette, visste godt at de får ingen andel i livet etter dette. De har solgt seg for en ussel motytelse. Om de bare visste!'),
(110, 2, 103, 'Om de hadde trodd og vist gudsfrykt, ville Guds belønning vært bedre. Om de bare visste!'),
(111, 2, 104, 'Dere som tror, bruk ikke uttrykket «pass på oss», men si «se hen til oss», og legg dere dette på minnet. De vantro venter en smertelig straff.'),
(112, 2, 105, 'Ikke ønsker de vantro, verken blant skriftfolket eller blant hedningene, at noe godt skal bli sendt ned til dere fra Herren. Men Gud utmerker med Sin nåde hvem Han vil. Gud har godhet i overflod.'),
(113, 2, 106, 'Hvis Vi annullerer et skriftord, eller lar det gå i glemmeboken, sender Vi et bedre eller et tilsvarende. Vet du ikke at Gud har makt over alle ting?'),
(114, 2, 107, 'Vet du ikke at Gud alene har herredømmet over himlene og jorden? Utenom Gud har dere ingen beskytter eller hjelper.'),
(115, 2, 108, 'Vil dere vel forhøre deres sendebud, slik som Moses i sin tid ble forhørt? Den som bytter tro mot vantro, er sannelig kommet bort fra den strake vei.'),
(116, 2, 109, 'Mange blant skriftfolket ønsker igjen å gjøre dere til vantro etter at dere er kommet til troen, av nag i sitt indre når nå sannheten er blitt klar for dem. Men overse og tilgi inntil Gud gir Sin avgjørelse til kjenne. Gud makter alle ting.'),
(117, 2, 110, 'Forrett bønnen og gi det rituelle bidrag. Alt godt dere sender i forveien for dere selv, vil dere finne igjen i Guds varetekt. Gud ser hva dere gjør.'),
(118, 2, 111, 'De sier: «Ingen kommer til paradiset unntatt jøder og kristne.» Dette er hva de ønsker. Si: «Legg frem bevisene deres, om dere snakker sant!»'),
(119, 2, 112, 'Sannelig, den som gir seg inn under Guds vilje og handler vel, han får sin lønn hos sin Herre. Ingen frykt skal hvile over dem, ei heller sorg.'),
(120, 2, 113, 'Jødene sier: «De kristne har intet å bygge på,» og de kristne sier: «Jødene har intet å bygge på.» Men de leser skriften! De uvitende sier det samme! Men Gud skal på oppstandelsens dag treffe avgjørelse mellom dem, vedrørende det de er uenige om.'),
(121, 2, 114, 'Hvem gjør større urett enn de som stenger veien til Guds tempel, så at Guds navn ikke skal påkalles der, og som vil ødelegge dem? Slike mennesker skulle ikke tre inn der uten frykt. For dem venter skam i jordelivet, og stor straff i det neste!'),
(122, 2, 115, 'Øst og vest tilhører Gud. Hvor dere enn vender dere – der er Guds åsyn. Gud omfatter alt, og vet alt.'),
(123, 2, 116, 'De sier: «Gud har lagt seg til barn.» Ære være Ham! Nei, alt som er i himlene og på jord tilhører Ham, alt er Ham underdanig.'),
(124, 2, 117, 'Han er himlenes og jordens skaper. Når Han har besluttet en ting, sier Han bare «Bli», og så blir det.'),
(125, 2, 118, 'De som intet vet, sier: «Hvorfor taler ikke Gud til oss? Hvorfor kommer ikke et tegn til oss?» Slik snakket også tidligere generasjoner. Menneskehjertene er like. Men Vi har klargjort ordet for folk som har visshet.'),
(126, 2, 119, 'Vi har utsendt deg med sannheten, som gledesbringer og advarer. Du skal ikke avkreves regnskap for Ildens folk.'),
(127, 2, 120, 'Jøder og kristne vil aldri bli fornøyd med deg før du følger deres lære. Si: «Guds ledelse er ledelsen.» Om du følger deres meninger etter all den viten du har fått, da vil du ikke ha noen venn eller hjelper overfor Gud.'),
(128, 2, 121, 'De som Vi har gitt skriften, og som leser den som den skal leses, disse tror på den. Men de som ikke tror på den, de er taperne.'),
(129, 2, 122, 'Israels barn, kom i hu den nåde som Jeg viste dere, og at Jeg utvalgte dere fremfor all verden.'),
(130, 2, 123, 'Frykt den dag når ingen kan tre i en annens sted, når ingen løsepenger godtas, og ingen godtas for å tale en annens sak, når ingen hjelp kan gis dem.'),
(131, 2, 124, 'En gang satte Gud Abraham på prøve med visse ord, og han oppfylte dem. Gud sa: «Jeg vil innsette deg som leder for folket,» og han svarte: «Og hva med mitt avkom?» Gud sa: «Min avtale gjelder ikke de urettferdige.»'),
(132, 2, 125, 'En gang gjorde Vi Huset Ka’aba til besøksmål og fredlyst sted: «Ta Abrahams sted til et bønnested!» Og Vi gjorde avtale med Abraham og Ismael: «Rens Mitt hus for dem som skal vandre rundt det, som søker det i andakt, og som faller ned i bønn.»'),
(133, 2, 126, 'En gang sa Abraham: «Herre, gjør dette til en fredlyst by, og la dens innbyggere få frukt til utkomme, de som tror på Gud og på dommens dag.» Han sa: «Også til den vantro vil Jeg gi Mine gaver for en kort tid, men så vil Jeg kaste ham i Ildens straff – et sørgelig endelikt.»'),
(134, 2, 127, 'Da Abraham og Ismael reiste Husets fundament: «Vår Herre, ta imot dette av oss. Du er den Hørende, den Allvitende.'),
(135, 2, 128, 'Herre, gjør oss til Dine hengivne tjenere og våre etterkommere til et folk av hengivne tjenere. Vis oss vårt tilbedelsesritual, og vend Deg i nåde mot oss. Du er den Tilgivende, den Nåderike.'),
(136, 2, 129, 'Vår Herre – la stå frem blant dem et sendebud av deres egne, som kan opplese for dem Ditt ord, lære dem skriften og visdommen, og rense dem. Du er den Mektige, den Vise.»'),
(137, 2, 130, 'Hvem tar vel avstand fra Abrahams lære, unntatt den som har svak forstand? Vi har utvalgt ham i denne verden, og i det hinsidige vil han være blant de rettferdige.'),
(138, 2, 131, 'Da hans Herre sa til ham: «Hengi deg til Meg,» svarte han: «Jeg hengir meg til all verdens Herre.»'),
(139, 2, 132, 'Og Abraham påla sine sønner dette og Jakob likeså: «Mine sønner, Gud har valgt religionen for dere, så gå ikke i døden annet enn i tjenende hengivelse.»'),
(140, 2, 133, 'Var vel dere vitner da Jakob lå for døden, og han sa til sine sønner: «Hvem vil dere tilbe når jeg er borte?» Og de svarte: «Vi vil tilbe din Gud og dine fedres Gud, Abrahams, Ismaels og Isaks Gud, den ene Gud – Ham vil vi hengi oss og tjene.»'),
(141, 2, 134, 'Dette er et folk som er gått bort, de får etter sin fortjeneste, og dere får etter deres fortjeneste. For dere vil det ikke bli spørsmål om de henfarnes gjerninger.'),
(142, 2, 135, 'De sier: «Bli jøder eller kristne, så dere kan finne rett vei!» Si: «Nei, heller Abrahams, Gud-søkerens religion. Han var ingen avgudsdyrker!»'),
(143, 2, 136, 'Si: «Vi tror på Gud, på det som er åpenbart for oss, og for Abraham og Ismael, Isak, Jakob og stammene, på det som ble gitt Moses og Jesus og profetene fra deres Herre. Vi gjør ingen forskjell på noen av dem, og vi har hengitt oss til Ham i tjeneste.»'),
(144, 2, 137, 'Og hvis de tror på det samme som dere tror på, så er de på rett vei. Men hvis de vender seg bort, så skaper de splid. Gud er tilstrekkelig for deg mot dem. Han er den Hørende, den Allvitende.'),
(145, 2, 138, 'Guds tiltrekningskraft! Hvem har en vakrere tiltrekningskraft enn Gud? Vi vil være hans tjenere!'),
(146, 2, 139, 'Si: «Vil dere strides med oss om Gud? Han som er vår Herre, og deres Herre? Vi har våre gjerninger, og dere deres. Vi er Ham oppriktig hengivne!»'),
(147, 2, 140, 'Kanskje vil dere si: «Abraham, Ismael, Isak, Jakob og stammene var enten jøder eller kristne.» Si: «Kjenner dere bedre til dette enn Gud? Hvem er mer urettferdig enn den som skjuler et vitnesbyrd han har fått fra Gud?» Sannelig, Gud overser ikke hva dere gjør!'),
(148, 2, 141, 'Dette er et folk som er gått bort. De får etter sin fortjeneste, og dere får etter deres fortjeneste. For dere vil det ikke bli spørsmål om de henfarnes gjerninger.'),
(149, 2, 142, 'Uforstandige folk vil si: «Hva har fått dem til å gå fra den bønneretning de hadde?» Si: «Gud tilhører øst og vest, Han leder hvem Han vil på rett vei.»'),
(150, 2, 143, 'Således har Vi gjort dere til et folk av formidlere så dere kan bære vitnesbyrd til menneskene, idet Sendebudet bærer vitnesbyrd til dere. Vi anviste den bønneretning du har, bare for å kjenne dem som følger Sendebudet, fra dem som vender seg tvert om. Dette var en alvorlig ting, unntatt for dem som var under Guds ledelse. Men Gud er ikke den som lar deres tro gå tapt. Gud er velvillig og nåderik mot menneskene.'),
(151, 2, 144, 'Vi ser at du vender ansiktet søkende på himmelen. Vi vil vende deg mot en bønneretning du vil bli tilfreds med. Vend ditt ansikt mot den hellige moské! Hvor dere enn er, så vend deres ansikter mot den. De som har mottatt skriften, vil visselig vite at det er sannheten fra deres Herre. Gud overser ikke hva de gjør.'),
(152, 2, 145, 'Men selv om du brakte allslags jærtegn til dem som har mottatt skriften, så ville de ikke følge din bønneretning. Du følger ikke deres bønneretning, og noen av dem følger ikke andres bønneretning. Skulle du følge deres meninger etter at du har fått kunnskap, da ville du begå urett.'),
(153, 2, 146, 'De som Vi har gitt skriften, kjenner den som de kjenner sine barn, men en del av dem skjuler sannheten mot bedre vitende.'),
(154, 2, 147, 'Sannheten kommer fra Herren, så vær ikke du av dem som tviler.'),
(155, 2, 148, 'Enhver har sin retning som han følger, men konkurrerer i å gjøre det gode. Hvor dere enn ferdes, så vil Gud samle dere. Gud makter alle ting.'),
(156, 2, 149, 'Hvor du enn kommer fra, så vend ditt ansikt mot den hellige moské! Dette er sannheten fra Herren. Gud overser ikke hva dere gjør.'),
(157, 2, 150, 'Hvor du enn kommer fra, så vend ditt ansikt mot den hellige moské, og hvor dere enn er, så vend deres ansikter mot den, så ikke folk har noe å kritisere, unntatt de urettferdige. Frykt ikke dem, men frykt Meg, så Jeg kan fullbyrde Min nåde mot dere, så dere må finne den rette vei.'),
(158, 2, 151, 'Således har Vi sendt dere et sendebud fra deres midte, for å opplese Vårt ord for dere, rense dere, og lære dere skriften og visdommen, lære dere det dere ikke visste.'),
(159, 2, 152, 'Så kom Meg i hu, og Jeg vil komme dere i hu. Vis Meg takknemlighet, og vær ikke vantro.'),
(160, 2, 153, 'Dere som tror, søk hjelp i tålmod og bønn. Gud står de tålmodige bi.'),
(161, 2, 154, 'Dere skal ikke si om dem som har mistet livet for Guds sak at de er døde! Nei, de lever, men dette kan dere ikke fatte.'),
(162, 2, 155, 'Vi kan saktens stille dere på prøve med frykt og hunger, med tap av liv, eiendom og avlinger. Men bring de tålmodige det gledelige budskap,'),
(163, 2, 156, 'dem som som når ulykken rammer dem, sier: «Vi tilhører Gud, og til Ham skal vi vende tilbake.»'),
(164, 2, 157, 'Over dem hviler Guds velsignelse og nåde. Disse er på den rette vei.'),
(165, 2, 158, 'Stedene Safa og Marwa (i Mekka) er Guds landemerker. Og for den som utfører det store eller det lille pilegrimsritual, er det intet i veien for at han også gjør en rundgang rundt begge. Om noen frivillig gjør en god gjerning, så er Gud anerkjennende, allvitende.'),
(166, 2, 159, 'De som skjuler det Vi har åpenbart av klar beskjed og ledelse etter at vi har gjort det klart for folk i skriften, over dem hviler Guds og menneskers forbannelse,'),
(167, 2, 160, 'med mindre de angrer og forbedrer seg og viser det. Dem vil Jeg vende Meg mot. Jeg den Tilgivende, den Nåderike.'),
(168, 2, 161, 'Men over dem som viser vantro og dør i sin vantro, over dem hviler Guds, englers og menneskers forbannelse.'),
(169, 2, 162, 'Under denne skal de forbli. Det blir ingen lettelse for dem i straffen, og de gis ingen appell.'),
(170, 2, 163, 'Deres Gud er én Gud, det er ingen gud unntatt Ham, den Barmhjertige, den Nåderike.'),
(171, 2, 164, 'I himlenes og jordens skapelse, i vekslingen av natt og dag, i skipet som seiler på havet med det som er til menneskers nytte, i vannet som Gud sender ned fra oven, hvorved Han gir liv til jorden etter dens dvale og lar den vrimle av allslags dyr, i vindens veksling og de drivende skyers tjeneste mellom himmel og jord – i alt dette er jærtegn for mennesker som forstår.'),
(172, 2, 165, 'Allikevel er det mennesker som setter likestilte ved Guds side, og som elsker dem slik man elsker Gud. Men de som tror, elsker Gud enda mer. Måtte illgjerningsmennene innse, når de står overfor straffen, at all makt tilhører Gud og at Gud er streng med straffen.'),
(173, 2, 166, 'Når lederne sier seg løst fra sitt følge, og de får se straffen, og alle deres bånd brytes'),
(174, 2, 167, 'og følget sier: «Kunne vi bare vende tilbake og si oss løst fra dem slik de sier seg løst fra oss!» Så vil Gud vise dem deres gjerninger. Triste saker for dem. Og aldri kommer de ut fra Ilden.'),
(175, 2, 168, 'Hør, dere mennesker, spis av det jorden frembyr og som er tillatt og godt. Følg ikke i Satans fotspor! Han er deres erklærte fiende.'),
(176, 2, 169, 'Han oppfordrer dere til ondskap og skamløshet, til å si om Gud ting dere ikke vet.'),
(177, 2, 170, 'Sier man til dem: «Følg det som Gud har åpenbart,» så svarer de: «Nei, vi holder oss til våre fedres tradisjoner.» Og dette selv om deres fedre intet forstod og ikke var på rett vei?'),
(178, 2, 171, 'De vantro er som skapninger som når de tilropes, bare registrerer lyden uten å forstå, døve, stumme, blinde.'),
(179, 2, 172, 'Dere som tror, spis av goder som vi har gitt dere til livets opphold, og vis takknemlighet mot Gud, så sant det er Ham dere tilber.'),
(180, 2, 173, 'Han har bare forbudt dere det selvdøde, blod og svinekjøtt, og det som en annen enn Gud er påkalt over. Men om noen gjør det, drevet av nød, ikke begjær, og ikke går over grensen, han rammes ikke av skyld. Gud er tilgivende, nåderik.'),
(181, 2, 174, 'De som skjuler noe av skriften som Gud har åpenbart for dem, og tuskhandler med den for en billig penge, de skal få fylle sin buk med ild, og Gud vil ikke tale til dem på oppstandelsens dag, ei heller rense dem. Det venter dem en smertelig straff.'),
(182, 2, 175, 'Disse har byttet til seg villfarelse for ledelse, og straff for tilgivelse. Hvor meget de må tåle i Ilden!'),
(183, 2, 176, 'Dette fordi Gud har åpenbart skriften med sannheten, og de som strides om skriften, er gått langt i å skape splid.'),
(184, 2, 177, 'Fromheten består ikke i at dere vender ansiktene i bønn mot øst eller vest. Sann fromhet er: Å tro på Gud og på dommens dag, på englene, skriften og profetene, og gi bort av det man har, om det enn er kjært, til slektninger, faderløse og fattige, veifarende og tiggere, og til frikjøping av slaver, å forrette bønnen, å utrede det rituelle bidrag, å overholde inngåtte avtaler, å være tålmodig i ulykke og nød og i trengselens tid. Disse gjør troen sann. Disse er de sanne gudfryktige.'),
(185, 2, 178, 'Dere som tror, det er foreskrevet dere gjengjeldelse for drap, fri for fri, slave for slave, kvinne for kvinne. Men om noen ettergis noe av sin bror, så skal det følges opp på sømmelig vis, og betaling skje på tilbørlig måte. Dette er en lettelse og en nåde fra Herren. Men den som etter dette går over grensen, ham venter en pinefull straff.'),
(186, 2, 179, 'I gjengjeldelsen er det liv for dere, dere som forstår, så dere må bli gudfryktige.'),
(187, 2, 180, 'Det er foreskrevet dere at når en av dere ligger for døden og etterlater seg verdier, å bestemme testamentarisk til fordel for foreldre og nære slektninger på tilbørlig måte. Dette er en plikt for de gudfryktige.'),
(188, 2, 181, 'Hvis noen forandrer det etter at han har hørt det, så hviler synd på dem som forandrer det. Gud hører, vet.'),
(189, 2, 182, 'Men om noen frykter partiskhet eller synd hos testator og ordner saken mellom dem, så hviler ingen skyld på ham. Gud er tilgivende og nåderik.'),
(190, 2, 183, 'Dere som tror, det er foreskrevet dere å faste, slik det var foreskrevet dem som levde før dere, så dere må bli gudfryktige,'),
(191, 2, 184, 'noen bestemte dager. Hvis noen er syke eller på reise, så et antall andre dager. De som er i stand til å faste, kan frikjøpe seg ved å bespise en fattig. Men om noen frivillig gjør en slik god gjerning, er det bedre for ham. Dog, at dere faster, er det beste for dere, om dere bare visste.'),
(192, 2, 185, 'I måneden ramadan, hvori Koranen ble åpenbart som ledelse for menneskene, som ledelsens klare ord, og som kriterium for vrangt og rett – de av dere som er til stede i den måneden, skal faste i den. Om noen er syke eller på reise, skal han faste et antall andre dager. Gud vil gjøre det lett for dere, ikke vanskelig, og slik at dere kan fullføre dagtallet, så dere må prise Gud fordi Han leder dere og vise takknemlighet.'),
(193, 2, 186, 'Når Mine tjenere spør etter Meg, se, Jeg er nær. Jeg svarer den bedendes bønn når han ber til Meg. Måtte de så høre på Meg, og tro på Meg, så de må finne den rette vei.'),
(194, 2, 187, 'Det er tillatt for dere å ha omgang med deres hustruer om natten under fasten. De er en kledning for dere, og dere for dem. Gud vet at dere har skapt misforståelser hos dere selv, og Han har vendt seg i nåde mot dere og har unnskyldt. Så nå kan dere være sammen med dem, og søke det som er Guds ordning for dere. Spis og drikk til en hvit tråd kan skilles fra en svart i daggryet. Så skal dere fullføre fasten til kvelden, og ha ikke omgang med kvinner, idet dere skal samles i moskeene. Dette er de grenser Gud har satt, så kom dem ikke for nær. Således klargjør Gud sitt ord for menneskene, så de må bli gudfryktige.'),
(195, 2, 188, 'Bruk ikke det dere har til intet eller tilby det til dommere som bestikkelse, for å tilvende dere en del av folks eiendom ved synd, når dere vet bedre.'),
(196, 2, 189, 'De spør deg om nymånene, si: De er tidsbestemmelser for menneskene, og for valfarten. Det er ikke fromhet, men overtro å gå inn i huset fra baksiden. Fromhet finnes hos den som frykter Gud. Så gå inn i huset gjennom døren og frykt Gud, så det må gå dere godt.'),
(197, 2, 190, 'Kjemp for Guds sak mot dem som bekjemper dere, men gjør dere ikke skyldige i aggresjon. Gud liker ikke de aggressive.'),
(198, 2, 191, 'Drep dem hvor dere påtreffer dem, og driv dem ut fra det sted (Mekka) som de har drevet dere ut fra. For prøvelser for troen og forfølgelse er verre enn drap. Men bekjemp dem ikke ved den hellige moské før de angriper dere der. Men hvis de angriper, så drep dem. Det er de vantros lønn.'),
(199, 2, 192, 'Men hvis de gir seg, så er Gud tilgivende og nåderik.'),
(200, 2, 193, 'Bekjemp dem til det ikke lenger finnes prøvelser for troen, og forfølgelse, og religionen tilhører Gud. Hvis de gir seg, skal det ikke være fiendskap mer, unntatt overfor dem som gjør urett.'),
(201, 2, 194, 'Den fredlyste måned mot den fredlyste måned. Det fredlyste krever gjengjeldelse. Så hvis noen angriper dere, så angrip ham på tilsvarende måte. Frykt Gud, og vit at Gud står de gudfryktige bi.'),
(202, 2, 195, 'Gi av det dere har for Guds sak. Styrt dere ikke i undergangen med egne hender, men gjør det gode. Gud holder av dem som gjør godt.'),
(203, 2, 196, 'Fullfør pilegrimsreisen og besøket for Gud. Hvis dere er forhindret, så bring et offer som dere kan overkomme. Rak ikke hodet før offerdyrene har nådd frem til sitt offersted. Hvis noen av dere er syk eller det feiler ham noe i hodet, så kan han løse seg ved faste, en gave eller et offer. Når dere er trygge, og noen vil ta med besøket før pilegrimsritualet, så la hans offer være slikt som kan overkommes. Den som intet har, la ham faste tre dager under pilegrimsreisen og syv dager når dere kommer hjem. Dette er ti dager i alt. Dette gjelder ham hvis familie ikke bor ved den hellige moské. Frykt Gud og vit at Gud er streng når det gjelder straff.'),
(204, 2, 197, 'Pilegrimsreisen skal foregå i bestemte måneder. Den som i disse beslutter å foreta pilegrimsreisen, må avstå fra omgang med kvinner, ugudelig adferd og krangel. Hva dere gjør av godt, kjenner Gud til. Sørg for reiseutrustning. Den beste utrustning er gudsfrykt. Frykt Meg, dere som har hjertets forstand.'),
(205, 2, 198, 'Det er ingen synd om dere under pilegrimsreisen søker Herrens gaver ved handel. Når dere strømmer videre fra Arafat, så påkall Gud ved det hellige veimerket. Påkall Ham så som Han ledet dere, skjønt dere tidligere fór vill.'),
(206, 2, 199, 'Strøm videre fra det sted folk strømmer fra, og be om Guds tilgivelse. Gud er tilgivende, nåderik.'),
(207, 2, 200, 'Og når dere har fullført seremoniene, så kom Gud i hu, som dere kom deres fedre i hu, eller med større inderlighet. Det er noen som sier: «Herre, gi oss dine gaver i denne verden.» Men han får ingen andel i den hinsidige.'),
(208, 2, 201, 'Det er andre som sier: «Vår Herre, gi oss gode gaver i denne verden og godt i det hinsidige, og bevar oss for Ildens straff.»'),
(209, 2, 202, 'Disse vil få sitt av det de har fortjent. Og Gud er rask i avregningen.'),
(210, 2, 203, 'Påkall Gud i et visst antall dager. Hvis noen må bryte opp etter to dager, så pådrar han seg ingen skyld, og heller ikke den som trekker ut tiden, såfremt han frykter Gud. Frykt Gud og vit at hos Ham vil dere bli samlet.'),
(211, 2, 204, 'Det finnes noen som taler om livet på jorden på en måte som behager deg, og han tar Gud til vitne på det som bor i hans hjerte. Allikevel er han den argeste kverulant.'),
(212, 2, 205, 'Så snart han har vendt ryggen til, iler han omkring for å stifte ufred og ødelegge avlinger og fruktbarhet. Gud liker ikke ufred og fordervelse.'),
(213, 2, 206, 'Og sier man til ham: «Frykt Gud,» så driver hovmodet ham synden i vold. Helvete er til pass for ham. Et dårlig hvilested!'),
(214, 2, 207, 'Men det finnes også noen som gir sitt liv for å vinne Guds velbehag. Og Gud viser godhet mot Sine tjenere.'),
(215, 2, 208, 'Dere som tror, søk hen til fred og harmoni alle sammen. Følg ikke i Satans fotspor. Han er deres erklærte fiende.'),
(216, 2, 209, 'Og om dere skulle snuble etter at den klare beskjed er kommet til dere, så vit at Gud er mektig, vis.'),
(217, 2, 210, 'Venter de intet mindre enn at Gud skal komme til dem under skyene, og englene også, og at saken dermed avgjøres? Til Gud bringes alle ting tilbake.'),
(218, 2, 211, 'Spør Israels barn hvor mange klare jærtegn Vi har gitt dem! Men om noen bytter bort Guds nåde etter at den er gitt ham – se, Gud er streng i straff.'),
(219, 2, 212, 'Jordelivet synes herlig for den vantro, og de gjør narr av troende. Men de som frykter Gud, er ovenpå på oppstandelsens dag. Gud gir sine gaver til hvem Han vil, uten å holde regnskap.'),
(220, 2, 213, 'Menneskene var en gang ett folk. Så sendte Gud profetene med gledesbudskap og advarsler, og Han sendte med dem skriften med sannheten, for å avgjøre det folk var uenige om. Men de som hadde mottatt denne, ble uenige om den etter at den klare beskjed var blitt dem til del, av gjensidig nag. Men Gud ledet de troende til sannheten, som de var uenige om, ved Sin velvilje. Gud leder hvem Han vil på rett vei.'),
(221, 2, 214, 'Mener dere at dere kan gå inn i paradiset før det har overgått dere det samme som vederfartes dem som gikk bort før dere? Trengsel og nød rammet dem, og de rystedes så at Sendebudet og de som trodde med ham, ropte: «Når kommer Guds hjelp?» Sannelig, Guds hjelp er nær.'),
(222, 2, 215, 'De spør deg om hva de skal gi bort, si: «Hva dere gir bort av verdier skal gå til foreldre og nærskyldte, til faderløse, fattige og veifarende. Hva godt dere gjør, Gud vet om det.»'),
(223, 2, 216, 'Det er foreskrevet dere å kjempe, selv om dere føler motvilje. Det kan være at dere føler motvilje mot noe som tjener dere til beste. Det kan være at dere liker noe som er til skade for dere. Gud vet, men dere vet det ikke.'),
(224, 2, 217, 'De vil spørre deg om kamp i den hellige måned, si: «Å kjempe i den er stor synd, men å legge hindringer i Guds vei og fornekte Ham og den hellige moské og drive dens folk bort derfra, er større synd i Guds øyne. Prøvelser for troen og forfølgelse er verre enn drap. De vil fortsette å bekjempe dere til de får vendt dere bort fra deres religion, om de kan. Men de som faller fra og dør som vantro, deres innsats er verdiløs i denne verden og den hinsidige. De er Ildens folk, og der skal de være og bli.'),
(225, 2, 218, 'Men de som tror, og de som drog ut og kjempet for Guds sak, de kan håpe på Guds nåde. Gud er tilgivende, nåderik.'),
(226, 2, 219, 'De vil spørre deg om alkohol og lykkespill, si: «I begge ligger stor synd, men også noe godt for menneskene. Men synden i dem er større enn nytten i dem.» De vil spørre deg hva de skal gi bort, si: «Det dere kan avse.» Således klargjør Gud ordet for dere, så dere må komme til ettertanke,'),
(227, 2, 220, 'over jordelivet og det hinsidige. De spør deg om de faderløse, si: «Å ordne sakene for dem er bra, og hvis dere har noe med dem å gjøre, så er de deres brødre.» Gud kjenner den som ødelegger fra den som bygger opp. Om Gud ville, kunne Han gjøre det hardt for dere. Gud er mektig, vis.'),
(228, 2, 221, 'Gift dere ikke med hedningekvinner før de har antatt troen. En troende slavepike er bedre enn en avgudsdyrkende kvinne, selv om hun tiltaler dere. Gift ikke bort deres kvinner til hedninger før de har antatt troen. En troende slave er bedre enn en avgudsdyrkende mann, selv om han tiltaler dere. Disse innbyr til Ilden. Gud innbyr til paradiset, og tilgivelse ved Sin velvilje. Han klargjør Sitt ord for folk, så de må komme til ettertanke.'),
(229, 2, 222, 'De spør deg om menstruasjonen, si: «Den er en ulempe, så hold dere borte fra kvinner under menstruasjonen og søk ikke hen til dem før de er rene. Men når de er rene, så gå til dem slik Gud har anordnet det. Gud elsker de botferdige og dem som holder seg rene!»'),
(230, 2, 223, 'Deres kvinner er en åker for dere, så gå til deres åker slik dere ønsker, alt på beste måte. Frykt Gud og vit at dere skal møte Ham. Bring det glade budskap til de troende.'),
(231, 2, 224, 'Gjør ikke Gud til gjenstand for deres eder når det gjelder å være fromme og gudfryktige og skape forståelse mellom folk. Gud hører, og vet.'),
(232, 2, 225, 'Gud tar dere ikke fatt for uoverlagte ord i deres eder. Men Han tar dere fatt for det deres hjerter fortjener. Gud er tilgivende, mild.'),
(233, 2, 226, 'Den som forsverger sine hustruer, har en ventetid på fire måneder. Hvis de angrer seg, er Gud tilgivende, nåderik.'),
(234, 2, 227, 'Hvis de beslutter skilsmisse, så er Gud den som hører, vet.'),
(235, 2, 228, 'Fraskilte kvinner skal avvente tre perioder. Det er ikke tillatt for dem å skjule det Gud har skapt i deres skjød, om de tror på Gud og på dommens dag. Deres menn har full rett til å ta dem tilbake i denne tiden om de ønsker forsoning. Kvinnene har tilsvarende krav og plikter innenfor anerkjente grenser. Dog har mennene et fortrinn fremfor dem. Gud er mektig, vis.'),
(236, 2, 229, 'Skilsmisse kan foretas to ganger gjenkallelig. Deretter må man beholde hustruen på skikkelig vis, eller sende henne endelig fra seg på sømmelig måte. Det er ikke tillatt for dere å ta tilbake noe av det dere har gitt dem, med mindre partene frykter at de grenser Gud har satt, overtres. Hvis de frykter dette, oppstår ingen skyldbelastning for det hun kjøper seg fri med. Dette er de grenser Gud har satt, så overtred dem ikke. De som overtrer Guds grenser, de er visselig urettferdige.'),
(237, 2, 230, 'Hvis han ugjenkallelig skiller seg fra henne, så er hun senere ikke tillatt for ham, før hun har inngått ekteskap med en annen mann. Hvis denne skiller seg fra henne, har de ingen skyld om de søker sammen igjen, såfremt de mener at de vil overholde de grenser som Gud har satt. Dette er Guds grenser, som Han klargjør for folk som forstår.'),
(238, 2, 231, 'Når dere gjenkallelig skiller dere fra kvinner, og de har nådd sin termin, så behold dem på sømmelig vis, eller la dem gå på sømmelig vis. Hold dem ikke tilbake med makt for å gjøre urett. Den som handler slik, gjør urett som rammer ham selv. Driv ikke gjøn med Guds ord, men kom i hu Guds nåde mot dere, og skriften og visdommen som Han har åpenbart for dere, for å formane dere derved. Frykt Gud, og vit at Gud vet om alt.'),
(239, 2, 232, 'Når dere skiller dere fra kvinner, og de har nådd sin termin, så hindre dem ikke i gjengifte når partene er enige på vanlig måte. Dette er en formaning for den av dere som tror på Gud og dommens dag. Dette er renest og sømmeligst for dere. Gud vet, men dere vet ikke.'),
(240, 2, 233, 'Mødrene skal amme sine barn i to fulle år, for dem som ønsker at ammingen fullføres. Det pålegger faren å skaffe mat og klær, etter skikk og bruk, men ingen pålegges noe over evne. En mor skal ikke lide overlast for sitt barns skyld, heller ikke en far. For en arvtager gjelder det samme. Hvis paret ønsker å avvenne barnet etter overenskomst og rådslagning seg imellom, så pådrar de seg ingen skyld. Heller ikke om dere søker en amme for deres barn, såfremt dere betaler det dere har lovet på skikkelig måte. Frykt Gud og vit at Gud ser hva dere gjør.'),
(241, 2, 234, 'Når noen av dere dør og etterlater enker, så skal disse personlig avvente fire måneder og ti dager. Når de har nådd sin frist, har dere intet ansvar for hvordan de ordner seg, på vanlig måte. Gud er vel underrettet om hva dere gjør.'),
(242, 2, 235, 'Det er ingen synd å antyde ekteskapstilbud til dem, eller å ha tanker om slikt. Gud vet at dere vil tenke på dem. Men gjør ingen hemmelig avtale, med mindre dere fører redelig tale. Bestem ingen ekteskapsavtale før den foreskrevne termin er utløpt. Dere skal vite at Gud vet hva som bor i dere. Ta dere i akt for Ham. Og vit at Gud er tilgivende, mild.'),
(243, 2, 236, 'Dere rammes ikke av skyld om dere skiller dere fra kvinner som dere ikke har rørt eller satt brudesum for. Men la dem få underhold, den velhavende etter sine kår, den fattige etter sine, som seg hør og bør. Dette er en plikt for dem som handler vel.'),
(244, 2, 237, 'Hvis dere skiller dere fra dem før dere har rørt dem, men etter at dere har fastsatt brudesum, så gi dem halvparten av det som dere har fastsatt, med mindre de eller deres verge gir avkall. Å gi avkall er nærmest gudsfrykt. Glem ikke høysinnethet mot hverandre. Gud ser hva dere gjør.'),
(245, 2, 238, 'Pass på bønnene, også den midtre. Stå for Gud i ydmykhet.'),
(246, 2, 239, 'Hvis dere frykter fare, så be til fots eller ridende. Men når dere er i sikkerhet, så påkall Gud, så som han har lært dere ting dere ikke visste.'),
(247, 2, 240, 'De som dør og etterlater enker, skal testamentere til fordel for sine enker, ett års underhold med borett. Men hvis de flytter, påligger dere intet ansvar for hvordan de ordner seg, på vanlig måte. Gud er mektig, vis.'),
(248, 2, 241, 'Fraskilte hustruer skal ha underhold på vanlig måte. Dette er en plikt for de gudfryktige.'),
(249, 2, 242, 'Således klargjør Gud sitt ord for dere, så dere må forstå.'),
(250, 2, 243, 'Du kjenner vel til dem som forlot sine hus i tusentall i dødsfrykt? Gud sa til dem: «Dø.» Senere vekket Han dem til live igjen. Gud viser godhet mot menneskene. Men folk flest viser ingen takknemlighet.'),
(251, 2, 244, 'Kjemp for Guds sak og vit at Gud hører, vet.'),
(252, 2, 245, 'Hvem vil gi Gud et godt lån som han får mangfold igjen? Gud er påholden og raus. Til Ham vil dere vende tilbake.'),
(253, 2, 246, 'Du vet vel om stammerådet for Israels barn etter Mose tid, da de sa til en av sine profeter: «Utnevn en konge over oss, så vi kan kjempe for Guds sak.» Han svarte: «Det kan vel tenkes hvis dere blir kalt til kamp at dere ikke vil kjempe?» De sa: «Hvorfor skulle ikke vi kjempe for Guds sak, vi som er drevet bort fra våre hjem og våre barn?» Men da de ble kalt til kamp, snudde de ryggen til, unntatt noen få. Men Gud kjenner de urettferdige.'),
(254, 2, 247, 'Og deres profet sa til dem: «Gud har sendt dere Saul til konge.» De sa: «Hvordan skulle han være konge over oss når vi har større rett til autoriteten enn han, og han heller ikke har synderlig rikdom?» Han svarte: «Gud har utvalgt ham for dere, og Han har gitt ham overflod av kunnskap og kroppsstyrke! Gud gir kongeverdighet til hvem Han vil. Gud er storslagen, vis!»'),
(255, 2, 248, 'Deres profet la til: «Tegn på hans autoritet skal være at Paktens ark er gitt Guds nærvær og restene av hva Mose og Arons folk etterlot seg skal komme til dere, båret av engler. Heri er sannelig et tegn for dere, om dere er troende.»'),
(256, 2, 249, 'Da Saul brøt opp med hæren, sa han: «Gud vil sette dere på prøve ved en elv! Den som drikker av den, hører ikke meg til. Den som ikke smaker den, han hører meg til, unntatt den som tar en håndfull.» Men de drakk av den, unntatt noen få. Da han og de som trodde sammen med ham, hadde krysset elven, sa de: «I dag klarer vi ikke med Goliat og hans hær!» Men de som trodde at de skulle møte Gud, sa: «Hvor ofte har ikke en liten flokk vunnet over en stor, med Guds bifall? Gud står de standhaftige bi!»'),
(257, 2, 250, 'Da de rykket frem mot Goliat og hans hær, ropte de: «Herre, send over oss standhaftighet og la våre føtter stå fast, og hjelp oss mot det vantro folk!»'),
(258, 2, 251, 'Med Guds bifall drev de dem på flukt, og David drepte Goliat. Og Gud gav ham kongedømmet og visdom, og Han lærte ham det Han ville. Hvis ikke Gud holdt folkene i sjakk, noen av dem ved hjelp av andre, ville anarki herske på jorden. Men Gud er full av godhet mot all verden.'),
(259, 2, 252, 'Dette er Guds ord, som Vi meddeler deg i sannhet. Sannelig, du er ett av sendebudene.'),
(260, 2, 253, 'Av disse sendebud har Vi favorisert noen fremfor andre, noen har Gud talt til, andre har Han gitt en høyere plass. Vi gav Jesus, Marias sønn, klare bevis og støttet ham med Den Hellige Ånd. Hvis Gud hadde villet slik, ville ikke de som kom etter dem, kommet i strid etter at de hadde mottatt den klare beskjed. Men de kom i strid. Noen trodde, noen trodde ikke. Hvis Gud hadde villet slik, ville de ikke kommet i strid. Men Gud gjør som Han bestemmer.'),
(261, 2, 254, 'Dere som tror, gi av det som Vi har gitt dere før den dag kommer, da det er slutt med kjøp og salg, med vennskap, med at noen kan utøve innflytelse til andres fordel. De vantro, de er urettferdige.'),
(262, 2, 255, 'Gud! Det er ingen gud uten Ham, den Levende, den som Er. Ham griper verken tretthet eller søvn. Ham tilhører alt i himlene og på jord. Hvem kan vel utøve innflytelse hos Ham, uten Hans tillatelse? Han kjenner det som var før dem, og det som etter dem kommer, men av Hans kunnskap griper de intet, unntatt det Han vil. Hans trones herredømme omfatter himlene og jorden, og å bevare dem koster ham intet besvær. Han er den Opphøyde, den Veldige.'),
(263, 2, 256, 'Det er ingen tvang i religionen. Rett vei er tydelig skilt fra villfarelse. Den som avviser avguder og tror på Gud, han har grepet det påliteligste håndtak, som aldri svikter. Gud hører og vet.'),
(264, 2, 257, 'Gud er de troendes beskytter og leder. Han fører dem fra mørket til lyset. Men de vantros ledere er avgudene. De fører dem fra lyset til mørket. Disse er Ildens folk, og der skal de være og bli.'),
(265, 2, 258, 'Du vet vel om ham som tvistet med Abraham om hans Herre, om at Gud hadde gitt ham autoritet? Og Abraham sa: «Min Herre er den som bestemmer liv og død.» Han svarte: «Jeg bestemmer også liv og død.» Da sa Abraham: «Gud lar solen stå opp i øst. La nå du den stå opp i vest!» Den vantro ble forvirret. Gud leder ikke urettferdige mennesker.'),
(266, 2, 259, 'Eller om ham som drog forbi en by som var forfalt og i ruiner, og utbrøt: «Hvordan skal Gud kunne gi liv til dette etter at alt ligger dødt?» Så lot Gud ham ligge død i hundre år. Derpå oppvekket Han ham, og sa: «Hvor lenge har du ligget her?» Han svarte: «Jeg har vel ligget her en dags tid eller en del av en dag.» Gud sa: «Nei, du har ligget her i hundre år! Men se på din niste av mat og drikke, den er ikke ødelagt. Se nå på ditt esel! – Dette, for å gjøre deg til et jærtegn for menneskene! Se på disse knoklene hvordan vi reiser dem opp og kler dem med kjøtt!» Og da dette ble klart for ham, sa han: «Jeg vet nå at Gud evner alle ting.»'),
(267, 2, 260, 'Eller da Abraham sa: «Herre, vis meg hvordan Du gjør døde levende.» Han spurte: «Tror du da ikke?» Og han svarte: «Jo, men for at mitt hjerte skal finne ro.» Han sa: «Ta fire fugler, ta dem til deg og drep dem, og legg så hver av dem på en bakketopp. Så skal du kalle på dem, og de vil komme ilende til deg. Og vit at Gud er mektig, vis.»'),
(268, 2, 261, 'Det er med dem som gir av det de eier for Guds sak, som med et korn som gir syv aks med hundre korn i hvert. For Gud gir den Han vil mangedobbelt. Gud er storslagen, vis.'),
(269, 2, 262, 'De som gir av det de eier for Guds sak, og ikke følger opp sin gave med å trekke den frem, eller med å gi vondt fra seg, deres lønn er hos Herren. Frykt skal ikke hvile over dem, ei heller sorg.'),
(270, 2, 263, 'Godt ord og tilgivelse er bedre enn en gave fulgt av vondord. Gud er selvtilstrekkelig, mild.'),
(271, 2, 264, 'Dere som tror, gjør ikke deres gaver til intet ved å trekke dem frem eller ved vondord, som en som gir bort noe av sitt for å bli sett av folk, men ikke tror på Gud og på dommens dag. Han er som en glatt steinhelle, dekket av jord. Så kommer rikelig regn, og den ligger der naken. Slike har intet oppnådd med sin (tilsynelatende) fortjenstfulle handling. Gud leder ikke vantro folk.'),
(272, 2, 265, 'Med de derimot som gir av det de eier, for å oppnå Guds velbehag og for å styrke seg selv, de er som en have på et høydedrag. Når rikelig regn faller, gir den dobbelt høst. Og hvis regn ikke faller, så er det dugg. Gud ser hva dere gjør.'),
(273, 2, 266, 'Skulle noen av dere ønske at han hadde en have med palmer og druer hvor bekker sildrer, og hvor han har allslags frukt, så rammer alderstegn ham mens han har forsvarsløse barn, så rammer en syklon med lyn haven, og den brenner opp. På denne måten klargjør Gud sitt ord for dere, så dere må tenke etter.'),
(274, 2, 267, 'Dere som tror, gi gaver av de goder dere har ervervet, og av det Vi har frembrakt for dere av jorden. Søk ikke ut det dårligste av det for gaver, noe dere selv ikke ville ta imot uten å lukke øynene. Gud er selvtilstrekkelig, høylovet.'),
(275, 2, 268, 'Satan truer dere med fattigdom og påbyr skjendighet. Gud lover Sin tilgivelse, og Sin yndest. Gud er storslagen, allvitende.'),
(276, 2, 269, 'Han gir visdom til hvem Han vil. Den som gis visdom, har fått et stort gode. Dog, ingen tenker over det, unntatt de som har hjertets forstand.'),
(277, 2, 270, 'Alt dere gir bort eller lover bort, Gud vet det. De urettferdige har ingen hjelpere.'),
(278, 2, 271, 'Om dere gir almisser åpent, er det vel og bra. Men om dere gir dem til de fattige i det skjulte, er det enda bedre, og det utvisker noen av deres misgjerninger. Gud er vel underrettet om hva dere gjør.'),
(279, 2, 272, 'Det påligger ikke deg å lede dem, men Gud leder dem Han vil. Om dere gir bort gaver, så er det til deres eget beste, så fremt dere gir bort for å vinne velbehag for Guds åsyn. Og hva dere gir bort, får dere fullt ut igjen. Det blir ikke gjort urett mot dere.'),
(280, 2, 273, 'Dette kommer de trengende til gode, dem som har mistet sin bevegelsesfrihet for Guds sak, og ikke kan dra rundt i landet og handle. Den uvitende tror de står seg godt på grunn av deres tilbakeholdenhet. Du vil kjenne dem på det som utmerker dem, de er ikke påtrengende tiggere. Og det du gir av verdier, se Gud er vitende om det.'),
(281, 2, 274, 'De som gir bort av det de har, både om natten og om dagen, i det skjulte og åpent, dem venter deres lønn hos Herren. Frykt skal ikke hvile over dem, ei heller sorg.'),
(282, 2, 275, 'De som lever av renteutbytting, skal på oppstandelsens dag stå frem lik en som Satan har slått med sin berøring. Dette fordi de hevder: «Handel er som utbytting.» Men Gud tillater handel og forbyr utbytting! Den som får en påminnelse fra sin Herre og så slutter, la ham beholde det som vedrører fortiden, og hans sak beror hos Gud. Men de som faller tilbake, de er Ildens folk, og der skal de være og bli.'),
(283, 2, 276, 'Gud gjør utbytting til intet, men gaver øker Han med tillegg. Gud liker ikke forherdede syndere.'),
(284, 2, 277, 'Men de som tror og lever rettskaffent, som overholder bønnen og gir det rituelle bidrag, dem venter deres lønn hos Herren. Frykt skal ikke hvile over dem, ei heller sorg.'),
(285, 2, 278, 'Dere som tror, frykt Gud og ettergi utestående ågerrenter, så sant dere er troende.'),
(286, 2, 279, 'Om dere ikke gjør dette, er dere erklært krig fra Gud og Hans sendebud. Men hvis dere vender om, kan dere beholde den opprinnelige kapital. Dere skal ikke gjøre urett, men det skal heller ikke gjøres urett mot dere.'),
(287, 2, 280, 'Hvis noen er i vanskeligheter, så gi henstand inntil forholdene blir bedre. Men å ettergi som gave, er det beste for dere, om dere bare visste.'),
(288, 2, 281, 'Frykt den dag da dere bringes tilbake til Gud! Da vil enhver motta fullt ut det han har fortjent. Og ingen vil lide urett.'),
(289, 2, 282, 'Dere som tror, når dere avtaler et lån for en viss periode, så skriv det ned. La en skriver skrive det for dere på rett vis. Ingen skriver må unnslå seg for å skrive, noe Gud har latt ham lære. Så la ham skrive, mens debitor dikterer, og må han frykte Gud sin Herre, så han skriver verken mer eller mindre. Hvis debitor er mentalt eller fysisk svak eller ikke kan diktere, så la en som varetar hans interesser diktere på rett vis. Og tilkall to mannlige vitner, eller en mann og to kvinner, av slike som dere kan godta som vitner, slik at hvis en av kvinnene skulle huske feil, så kan den andre påminne henne. Vitner skal ikke unnslå seg når de blir tilkalt. Ta bryet med å skrive det ned, enten det er stort eller lite, med forfallstiden. Dette er det riktigste i Guds øyne, og best for bevisførsel og det nærmeste for å unngå tvil. Med mindre det gjelder varer som straks overleveres fra hånd til hånd. I så fall gjør dere ingen feil om dere unnlater å skrive, men bruk vitner når dere handler med hverandre. Verken skriver eller vitne må lide overlast. Hvis dette skjer, er det ugudelig adferd for dere. Frykt Gud, for Gud belærer dere, og Gud vet alt.'),
(290, 2, 283, 'Hvis dere er på reise og ikke kan finne en skriver, så kan det gis håndpant som sikkerhet. Om noen således betror en annen noe, så er denne ansvarlig for pantet, og han skal frykte Gud, sin Herre. Underslå ikke bevis. Den som gjør det, har synd i sitt hjerte. Gud vet hva dere gjør.'),
(291, 2, 284, 'Gud tilhører alt i himlene og på jorden. Om dere bærer åpent frem eller skjuler det som er i deres hjerter, så vil Gud trekke dere til regnskap for det. Og Han vil tilgi den Han bestemmer, og straffe den Han bestemmer. Gud har makt over alle ting.'),
(292, 2, 285, 'Sendebudet tror på det som er ham åpenbart fra Herren, så også de troende. Hver og en tror på Gud, Hans engler, Hans skrifter og Hans sendebud: «Vi gjør ingen forskjell på noen av Hans sendebud.» De sier: «Vi hører, og vi adlyder. Din tilgivelse, Herre! Til Deg fører vandringen hen!»'),
(293, 2, 286, 'Gud forlanger ikke mer av noen enn det han kan makte. Han godskrives det han har fortjent, og belastes det han har fortjent. Herre, ta oss ikke fatt om vi glemmer eller feiler! Herre, legg ikke på oss slike byrder som Du gav dem å bære som er gått foran oss! Herre, gi oss ikke å bære det vi ikke har krefter til! Forlat og tilgi og forbarm Deg over oss. Du er vår Herre og Mester. Hjelp oss mot vantroens folk.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'Alif Lam Mim'),
(295, 3, 2, 'Gud! Det er ingen gud uten Ham, den Levende, den som Er!'),
(296, 3, 3, 'Han har åpenbart deg skriften med sannheten, som stadfester det som forelå før den. Han har åpenbart loven og evangeliet før,'),
(297, 3, 4, 'som ledelse for menneskene. Han har åpenbart kriteriet. De som ikke tror på Guds ord, har i vente en streng straff. Gud er mektig, og har gjengjeldelsen i Sin hånd.'),
(298, 3, 5, 'For Gud er intet skjult i himmel og på jord.'),
(299, 3, 6, 'Han er det som former dere i mors liv slik Han vil. Det er ingen Gud unntatt Ham, den Mektige, den Vise.'),
(300, 3, 7, 'Han har åpenbart deg skriften. Det finnes skriftsteder som er klare og utvetydige, som er Bokens ryggrad, og andre som må fortolkes i samklang med disse. De, hvis hjerter er på vidvanke, fester seg ved dem som kan tolkes, idet de søker anstøtssteiner og begjærer deres endelige tolkning. Men ingen kjenner denne deres fortolkning unntatt Gud. De som er fast forankret i viten, sier: «Vi tror på det! Alt er fra Herren!» Men ingen lar seg formane unntatt de som har hjertets forstand.'),
(301, 3, 8, 'Herre, la ikke våre hjerter vandre etter at Du har ledet oss. Gi oss Din nåde. Du er Giveren.'),
(302, 3, 9, 'Herre, Du vil visselig samle menneskene på en dag hvorom ingen tvil hersker. Sannelig, Gud sviker ikke Sitt løfte.'),
(303, 3, 10, 'De vantro kan ikke verge seg mot Gud ved sin rikdom og sine barn.'),
(304, 3, 11, 'De vil bli brensel for Ilden, som Faraos folk og folk før dem. De fornektet Vårt ord. Så tok Gud dem fatt for deres synder, og Gud er streng med straffen.'),
(305, 3, 12, 'Si til de vantro: «Dere vil bli satt til veggs og samlet i helvete. Et dårlig hvilested!»'),
(306, 3, 13, 'Sannelig, det var et jærtegn for dere da de to kampgrupper møttes. Den ene kjempet for Guds sak, den annen var vantro, og denne fortonet seg dobbelt så stor for øyet. Men Gud styrker med Sin hjelp dem Han vil. I dette er en påminnelse for dem som har øyne.'),
(307, 3, 14, 'Fagert er for menneskene det som begjæret etterstreber, kvinner, barn, hauger av sølv og gull, fine hester, kveg og åkrer. Dette er jordelivets gleder. Men hos Gud er det herligste hjem!'),
(308, 3, 15, 'Si: «Skal jeg fortelle dere om dette som er bedre? For dem som frykter Gud, venter hos deres Herres haver, hvor bekker sildrer, og der skal de være og bli, og rene hustruer og Guds velbehag.» Gud har øye for Sine tjenere.'),
(309, 3, 16, 'For dem som sier: «Herre, vi tror! Tilgi oss våre synder, og bevar oss for Ildens straff,»'),
(310, 3, 17, '– og for de standhaftige, sannferdige, ydmyke, de rause med gaver, som ber om tilgivelse i morgenstunden.'),
(311, 3, 18, 'Gud selv bevitner, likeledes englene og de som er forankret i viten: «Det er ingen gud unntatt Ham, den Rettferdsutøvende, det er ingen gud unntatt Ham, den Mektige, den Vise.»'),
(312, 3, 19, 'Islam er religionen overfor Gud. De som har mottatt skriften, var ikke uenige før viten var blitt dem til del, og da drevet av gjensidig misunnelse. For den som fornekter Guds ord er Gud rask til å avregne.'),
(313, 3, 20, 'Hvis de vil strides med deg, si: «Jeg har stilt meg i Guds tjeneste, likeledes de som følger meg.» Si til dem som har mottatt skriften, og også til andre folk: «Har dere overgitt dere til Gud?» Hvis de har overgitt seg, er de på rett vei. Men snur de deg ryggen, så er din plikt kun å meddele budskapet. Gud holder øye med Sine tjenere.'),
(314, 3, 21, 'De som fornekter Guds ord, som urettmessig dreper profetene, og dreper dem som preker rettferd, for dem kan du bebude en smertelig straff.'),
(315, 3, 22, 'Disse er det, hvis gjerninger er verdiløse i denne verden og den hinsidige. De har ingen hjelpere.'),
(316, 3, 23, 'Du kjenner vel til dem som fikk en del av skriften, og således var kalt til Guds bok, for at den skulle gjøre rett og skjell mellom dem? Men så vendte en del av dem ryggen til, og fjernet seg.'),
(317, 3, 24, 'Dette fordi de hevdet: «Ilden vil bare røre oss et begrenset antall dager.» Og deres påfunn førte dem på avveie i deres religion.'),
(318, 3, 25, 'Hvordan vil det da gå når Vi samler dem til en dag hvorom ingen tvil hersker? Og enhver får fullt oppgjør for det han har fortjent? De skal ikke lide urett!'),
(319, 3, 26, 'Si: «Gud, kongenes Konge, Du gir herskermakten til den Du vil, og tar den fra den Du vil. Du opphøyer den Du vil, og fornedrer den Du vil. Det gode ligger i Din hånd. Du evner alle ting.'),
(320, 3, 27, 'Du lar natten bli dag, og dag bli natt. Du frembringer det levende av det døde, og dødt av liv. Du sørger for den Du vil, uten å regne på det!'),
(321, 3, 28, 'De som tror, må ikke slutte seg til vantro fremfor troende. De som gjør dette, hører slett ikke Gud til, med mindre de er presset av dem. Gud advarer dere mot Ham selv! Hos Ham er reisen slutt.»'),
(322, 3, 29, 'Si: «Om dere skjuler det som er i deres hjerter eller bærer det åpent frem, Gud vet om det. Gud vet hva som er i himlene og på jord. Gud evner alle ting!»'),
(323, 3, 30, 'Den dag da enhver konfronteres med det han har gjort av godt og ondt, vil han ønske at det hadde vært langt mellom ham og denne dag. Gud advarer dere mot Ham selv! Men Gud er mild mot Sine tjenere.'),
(324, 3, 31, 'Si: «Om dere elsker Gud, så følg meg, og Gud vil elske dere, og tilgi deres synder. Gud er tilgivende, nåderik!»'),
(325, 3, 32, 'Si: «Adlyd Gud og Sendebudet! Men vender de seg bort, så holder ikke Gud av vantro.»'),
(326, 3, 33, 'Gud utvalgte Adam og Noa, Abrahams hus og Imrans hus fremfor all verden,'),
(327, 3, 34, 'slekt etter slekt. Gud hører, vet.'),
(328, 3, 35, 'En gang sa Imrans hustru: «Min Herre, jeg lover Deg det som er i mitt skjød, som helliget Deg, så motta dette fra meg. Du er den Hørende, den Allvitende.»'),
(329, 3, 36, 'Og da hun hadde født henne, sa hun: «Min Herre, jeg har født en pike,» – skjønt Gud visste godt hva hun hadde født, for hankjønn er ikke som hunkjønn: «Og jeg har kalt henne Maria. Jeg anbefaler henne og hennes avkom i Din varetekt mot Satan, den forkastede.»'),
(330, 3, 37, 'Og Herren tok imot henne med velvilje, og lot henne vokse opp i vakker oppvekst, idet Han påla Sakarias å ta seg av henne. Hver gang Sakarias kom inn til henne i helligdommen, fant han henne velforsynt med mat. Han spurte: «Maria, hvor har du fått dette fra?» Hun svarte: «Det er kommet fra Gud.» Sannelig, Gud sørger for den Han vil, uten å regne på det.'),
(331, 3, 38, 'På denne tid bad Sakarias til Herren: «Herre, gi Du meg en god etterkommer! Du er den som hører bønn.»'),
(332, 3, 39, 'Og englene ropte til ham, mens han ennå stod og bad i helligdommen. «Gud gir deg det gledelige budskap om Johannes, som skal stadfeste et ord fra Gud, en leder, asket, profet, en rettferdig.'),
(333, 3, 40, 'Han sa: «Hvordan skal jeg kunne få en sønn, når høy alder har innhentet meg, og min hustru er ufruktbar?» Gud sa: «Det blir så, Gud gjør hva Han vil.»'),
(334, 3, 41, 'Så sa han: «Herre, gi meg et tegn!» Gud svarte: «Ditt tegn skal være at du ikke kan snakke til folk på tre dager, unntatt ved fakter. Kom din Herre ofte i hu, og pris Ham om aftenen og om morgenen.»'),
(335, 3, 42, 'En gang sa englene: «Maria, Gud har utvalgt deg og gjort deg ren, utvalgt deg fremfor all verdens kvinner.'),
(336, 3, 43, 'Maria, vær ydmyk overfor din Herre, kast deg ned og bøy deg i bønn med de bedende!»'),
(337, 3, 44, 'Dette er av fortellingene fra det skjulte som vi åpenbarer for deg, for du var ikke hos dem da de kastet lodd om hvem som skulle ha omsorg for Maria, og du var heller ikke der da de kranglet med hverandre.'),
(338, 3, 45, 'En gang sa englene: «Maria, Gud bebuder deg et ord fra Ham selv. Hans navn skal være Kristus, – Jesus, Marias sønn, og høyt æret skal han bli, i denne verden og den hinsidige, av dem som får komme Gud nær.'),
(339, 3, 46, 'Han skal tale til menneskene i vuggen og i moden alder, og han skal være en rettferdig.»'),
(340, 3, 47, 'Og hun sa: «Herre, hvordan skal jeg kunne få en sønn, når ingen mann har rørt meg?» Gud sa: «Det blir så, Gud skaper det Han vil. Når Han bestemmer en ting, sier Han bare ’Bli’, og den er.»'),
(341, 3, 48, 'Og Han skal lære ham skriften og visdommen, loven og evangeliet.'),
(342, 3, 49, 'Som sendebud til Israels barn vil han si: «Jeg er kommet til dere med et jærtegn fra deres Herre. Jeg vil forme for dere fugl av leire og blåse på den, og den vil med Guds samtykke bli levende fugl. Jeg vil med Guds samtykke helbrede blinde og spedalske, og jeg vil oppvekke døde. Jeg vil fortelle dere hva dere skal spise og hva dere skal lagre i deres hus. I dette er sannelig et jærtegn for dere, hvis dere er troende.'),
(343, 3, 50, 'Likeledes for å stadfeste loven som foreligger før meg, og tillate dere noe av det som har vært dere forbudt. Jeg er kommet med et jærtegn fra deres Herre, så frykt Gud og adlyd meg,'),
(344, 3, 51, 'Gud er min Herre og deres Herre, så tjen Ham. Det er rett vei.»'),
(345, 3, 52, 'Og da Jesus merket vantro hos dem, sa han: «Hvem vil bli mine medhjelpere på Guds vei?» Disiplene svarte: «Vi er Guds medhjelpere, vi tror på Gud. Bevitne du at vi har hengitt oss til Ham.'),
(346, 3, 53, 'Herre, vi tror på det Du har åpenbart, og vi følger Sendebudet. Så skriv oss opp blant dem som bærer vitnesbyrd.»'),
(347, 3, 54, 'De la planer, og Gud la planer. Men Gud er den beste planmaker.'),
(348, 3, 55, 'En gang sa Gud: «Jesus, jeg vil avslutte ditt liv, og ta deg opp til Meg, og befri deg for de vantro. Jeg vil sette dem som følger deg over de vantro til oppstandelsens dag. Så skal dere vende tilbake til Meg, og Jeg skal treffe avgjørelse mellom dere i det dere var uenige om.'),
(349, 3, 56, 'Hva dem angår som er vantro, så skal Jeg ramme dem med en hard straff, i dette livet og det hinsidige. De skal ikke ha noen hjelpere.»'),
(350, 3, 57, 'Dem derimot, som tror og gjør det som rett er, vil Han gi full lønn. Gud elsker ikke urettferdige.'),
(351, 3, 58, 'Dette meddeler Vi deg, av jærtegn og vis formaning.'),
(352, 3, 59, 'Forholdet med Jesus er overfor Gud det samme som med Adam. Han skapte ham av støv og sa til ham: «Bli!» Og dermed var han.'),
(353, 3, 60, 'Sannheten er fra Herren, så vær ikke en tviler.'),
(354, 3, 61, 'Om noen vil strides med deg om dette etter at viten er blitt deg til del, så si: «Kom, la oss kalle sammen våre sønner og deres sønner, våre kvinner og deres kvinner og oss selv og dere. Så skal vi anrope Gud, og nedkalle Guds forbannelse over dem som lyver.»'),
(355, 3, 62, 'For dette er den sanne fortelling. Det er ingen gud unntatt Gud. Gud er den Mektige, den Vise.'),
(356, 3, 63, 'Men om de vender seg bort, så kjenner Gud dem som skaper ufred.'),
(357, 3, 64, 'Si: «Dere skriftens folk, kom til et ord som vi har felles: ’Vi tilber bare Gud! Vi gir Ham ingen medguder, og ingen av oss har andre herrer enn Gud!’» Hvis de vender ryggen, si: «Vær vitne til at vi har hengitt oss til Gud!»'),
(358, 3, 65, 'Dere skriftens folk, hvorfor strides dere om Abraham? Når verken loven eller evangeliet ble åpenbart før etter hans tid? Forstår dere da ikke!'),
(359, 3, 66, 'Dere er jo slike som diskuterer ting dere har kunnskap om! Så hvorfor diskuterer dere da ting dere ikke kjenner til! Gud vet, men dere vet ikke!'),
(360, 3, 67, 'Abraham var verken jøde eller kristen, men han var en Gud-søker, en Gud-hengiven. Han hørte ikke til avgudsdyrkerne.'),
(361, 3, 68, 'De som står Abraham nærmest, er de som følger ham, og denne profet og de troende. Gud er de troendes beskytter og leder.'),
(362, 3, 69, 'En gruppe av skriftens folk ønsker å føre dere vill, men de fører ingen vill unntatt seg selv, og det uten at de vet det.'),
(363, 3, 70, 'Dere som har fått skriften, hvorfor fornekter dere Guds ord som dere selv bevitner?'),
(364, 3, 71, 'Dere som har fått skriften, hvorfor pakker dere sannheten i løgn, og skjuler sannheten, når dere vet bedre?'),
(365, 3, 72, 'En gruppe av skriftens folk sier: «Tro på det som er åpenbart for de troende om formiddagen og fornekt det om ettermiddagen, så kanskje de faller tilbake.'),
(366, 3, 73, 'Ha ingen tiltro til andre enn dem som følger deres religion!» Si: «Den sanne ledelse, Guds ledelse, viser seg i at andre mottar tilsvarende det som dere har mottatt! Ellers ville de kunne strides med dere for Herrens åsyn.» Si: «Gavmildheten er i Guds hånd. Han gir derav til hvem Han vil. Gud er storslagen, allvitende.'),
(367, 3, 74, 'Han velger for Sin nåde hvem Han vil. Guds er den største gavmildhet.»'),
(368, 3, 75, 'Blant skriftens folk finnes den du kan betro en formue, og han betaler tilbake. Men blant dem finnes også den, hvis du betror ham en enkel dinar, som ikke betaler tilbake med mindre du stadig er etter ham. Dette fordi de sier: «Vi kan ikke trekkes til ansvar når det gjelder folkene.» De lyver mot Gud, og de vet bedre.'),
(369, 3, 76, 'Men den som oppfyller sin forpliktelse og frykter Gud, se, Gud elsker de gudfryktige.'),
(370, 3, 77, 'De som selger sin forpliktelse overfor Gud, og sine eder, for en billig penge, har ingen del i det hinsidige. Gud vil ikke tale til dem eller se hen til dem på oppstandelsens dag, og heller ikke rense dem. Dem venter en smertefull straff.'),
(371, 3, 78, 'Det er en gruppe blant dem som vrir tungen henimot skriften, for at dere skal tro at det er fra skriften, når det slett ikke er fra skriften. Og de sier: «Dette er fra Gud,» når det slett ikke er fra Gud. De lyver mot Gud, og de vet det.'),
(372, 3, 79, 'Det anstår seg ikke for et menneske at Gud gir ham skriften og visdommen og profetkallet, og så sier han til folk: «Vær mine tjenere, fremfor Guds!» Da heller, vær læremestere fordi dere lærer folk skriften og har studert den!'),
(373, 3, 80, 'Aldri ville Han påby dere å ta engler og profeter til herre. Skulle Han vel påby vantro etter at dere har hengitt dere til Ham?'),
(374, 3, 81, 'En gang sluttet Gud pakt med profetene: «For alt det som Jeg gir dere av skrift og visdom, vil det senere komme et sendebud som stadfester det dere har fått. Ham skal dere tro og yte hjelp.» Og Han sa videre: «Godtar dere, og vil dere påta dere min byrde slik?» De svarte: «Vi godtar.» «Ham skal dere tro og yte hjelp, og Jeg vil være med dere som vitne.»'),
(375, 3, 82, 'De som vender ryggen til etter dette, disse er i sannhet ugudelige.'),
(376, 3, 83, 'Kan de vel ønske en annen religion enn Guds? Alle som er i himlene og på jorden har gitt seg Ham i vold, med eller mot sin vilje, og til Ham vil de bringes tilbake.'),
(377, 3, 84, 'Si: «Vi tror på Gud, på det som er åpenbart for oss, for Abraham, Ismael, Isak, Jakob og stammene, og på det som ble gitt Moses, Jesus og profetene fra Herren. Vi gjør ingen forskjell på noen av dem! Og til Ham har vi hengitt oss.»'),
(378, 3, 85, 'Hvis noen ønsker annet enn islam som religion, vil dette ikke godtas fra ham, og i det hinsidige liv vil han være blant taperne.'),
(379, 3, 86, 'Hvordan skulle vel Gud lede folk som faller fra etter at de er kommet til troen, som har vitnet at Sendebudet står for sannhet, og som har fått den klare beskjed? Gud leder ikke folk som handler ille!'),
(380, 3, 87, 'Deres lønn er at Guds, englers og menneskers forbannelse er over dem,'),
(381, 3, 88, 'samlet, og der skal de være for alltid. Det blir ingen lettelse i straffen for dem, og ingen appell.'),
(382, 3, 89, 'Unntatt er de som etterpå omvender seg og forbedrer seg. Gud er tilgivende, nåderik.'),
(383, 3, 90, 'De som faller fra etter at de er kommet til troen, og så bare tiltar i vantro, deres omvendelse godtas ikke. Disse er villfarende.'),
(384, 3, 91, 'De som er vantro, og dør som vantro, det vil ikke bli mottatt fra noen av dem en verden fylt av gull, om han ville løskjøpe seg med det. Dem venter en pinefull straff, og de har ingen til å hjelpe seg.'),
(385, 3, 92, 'Dere når ikke frem til rett fromhet før dere gir bort av det som er dere kjært. Hva dere enn gir, Gud vet om det.'),
(386, 3, 93, 'All mat var tillatt Israels barn, unntatt det Israel forbød seg selv før loven ble åpenbart. Si: «Legg frem loven og les opp fra den, om dere er sannferdige.»'),
(387, 3, 94, 'Og de som etter dette dikter opp løgn mot Gud, disse er urettferdige.'),
(388, 3, 95, 'Si: «Gud taler sant, så følg Abraham, Gud-søkerens religion. Han var ingen avgudsdyrker.»'),
(389, 3, 96, 'Det første tempel som ble grunnlagt for menneskene, er det i Mekka, som velsignelse og ledelse for all verden.'),
(390, 3, 97, 'I dette finnes klare tegn, Abrahams plass, og sikkerhet for den som betrer den. Det er en plikt for menneskene mot Gud å dra på pilegrimsreise til Huset, såfremt de finner utvei til det. Hvis noen er vantro, så er Gud selvtilstrekkelig og trenger ikke til verden.'),
(391, 3, 98, 'Si: «Dere skriftens folk, hvorfor fornekter dere Guds ord når Gud er vitne til det dere gjør?»'),
(392, 3, 99, 'Si: «Dere skriftens folk, hvorfor hindrer dere dem som tror, fra Guds vei, idet dere vil gjøre den krokete? Dere er jo selv vitner. Men Gud overser ikke hva dere gjør.»'),
(393, 3, 100, 'Dere som tror, om dere hører på en gruppe av dem som har fått skriften, vil de føre dere til frafall etter at dere er kommet til troen.'),
(394, 3, 101, 'Men hvordan skulle dere falle fra, når Guds ord er lest opp for dere og Hans sendebud er til stede blant dere? Den som holder fast ved Gud, han er blitt ledet til rett vei.'),
(395, 3, 102, 'Dere troende, frykt Gud slik som Han skal fryktes! Se til at dere er Ham hengivne når dere dør!'),
(396, 3, 103, 'Hold dere alle fast i Guds rep! Unngå splittelser, og kom i hu Guds nåde mot dere den gang dere var fiender og Han forenet deres hjerter, slik at dere ved Hans nåde ble brødre. Dere var på kanten av Ildens avgrunn, og Han reddet dere fra den. Slik klargjør Gud sitt ord for dere, at dere må finne ledelse!'),
(397, 3, 104, 'Måtte dere bli et folk som kaller til det gode, som forordner det som rett er, og forbyr det urette. Disse vil det gå godt!'),
(398, 3, 105, 'Vær ikke som dem som kommer i splittelse og uenighet, etter at de har mottatt klar beskjed. Disse er hjemfalne til stor straff,'),
(399, 3, 106, 'på den dag da ansiktene vil lyse, eller formørkes. De hvis ansikter formørkes: «Falt dere fra etter at dere var kommet til troen? Så ta straffen for deres vantro!»'),
(400, 3, 107, 'Men de hvis ansikter lyser, skal være i Guds nåde, og der skal de være og bli.'),
(401, 3, 108, 'Dette er Guds ord, som vi meddeler deg i sannhet. Gud ønsker ingen urett over Sine skapninger.'),
(402, 3, 109, 'Gud tilhører alt i himlene og på jord. Til Gud skal alt bringes tilbake.'),
(403, 3, 110, 'Dere er det beste folk som er sendt ut til menneskene. Dere forordner det som rett er, og forbyr det urette, og tror på Gud. Om skriftens folk hadde trodd, hadde det vært bedre for dem. Noen av dem tror, men de fleste av dem er ugudelige.'),
(404, 3, 111, 'De kan bare skade dere lite. Og hvis de stiller til kamp mot dere, snur de snart ryggen til flukt. Og de får ingen hjelp.'),
(405, 3, 112, 'De vil bli rammet av ydmykelser hvor de enn finnes, hvis de ikke finner forbund med Gud eller mennesker. De pådrar seg Guds vrede, og blir slått av fattigdom. Dette fordi de fornektet Guds ord og drepte profetene med urett. Dette fordi de var ulydige og gikk over grensen.'),
(406, 3, 113, 'Men de er ikke alle like. Blant skriftens folk finnes en rettferdig gruppe, som resiterer Guds ord i nattens timer og faller ned i bønn,'),
(407, 3, 114, 'som tror på Gud og dommens dag, som forordner det som rett er, og forbyr det urette, som kappes i å gjøre gode gjerninger. Disse er rettskafne.'),
(408, 3, 115, 'Det gode de gjør vil aldri savne anerkjennelse. Gud kjenner de gudfryktige.'),
(409, 3, 116, 'De vantro kan ikke verge seg mot Gud ved sin rikdom og sine barn. Disse er Ildens folk, og der skal de være og bli.'),
(410, 3, 117, 'Deres pengebruk i denne verden er som en ødeleggende vind, som rammer urettferdiges marker og tilintetgjør dem. Gud har ikke gjort urett mot dem, mens de har gjort urett til egen skade.'),
(411, 3, 118, 'Dere som tror, søk ikke venner utenfor deres egen krets, for disse sparer intet for å ødelegge dere, de ønsker det som plager dere. Hatet viser seg i deres munn, og det hjertet skjuler er enda verre. Vi har nå forklart ordet for dere, hvis dere forstår.'),
(412, 3, 119, 'For dere står saken slik: Dere kan like dem, de liker ikke dere. Dere tror på den hele skrift, og når de møter dere, sier de: «Vi tror.» Men når de er for seg selv, biter de fingertuppene i raseri mot dere. Si: «Dø i raseriet deres. Gud vet hva som bor i hjertet.»'),
(413, 3, 120, 'Hvis noe godt overgår dere, plager det dem. Men hvis dere rammes av noe ondt, gleder de seg over det. Hvis dere er standhaftige og gudfryktige, vil deres renker ikke skade dere det minste. Gud har oversikt over alt de gjør.'),
(414, 3, 121, 'Den gang du drog ut fra dine om morgenen og la de troende i kampstillinger. Gud hører, vet.'),
(415, 3, 122, 'To flokker av dere var i ferd med å miste motet, skjønt Gud var deres beskytter og leder. På Gud skal de troende forlate seg.'),
(416, 3, 123, 'Gud hjalp dere i slaget ved Badr, hvor dere var helt underlegne. Så frykt Gud, så dere må bli takknemlige.'),
(417, 3, 124, 'Du sa til de troende: «Er det ikke nok at deres Herre forstrekker dere med tre tusen engler sendt ned til dere?'),
(418, 3, 125, 'Sannelig, hvis dere er standhaftige og gudfryktige, og fienden kommer over dere i ett nå, vil Herren forstrekke dere med fem tusen utvalgte engler.»'),
(419, 3, 126, 'Gud gjorde dette som et gledelig budskap til dere, og for at deres hjerter skulle falle til ro derved. Hjelp kommer bare fra Gud, den Mektige, den Vise,'),
(420, 3, 127, 'for å avskjære en del av de vantro eller gi dem tilbakeslag, så de skulle vende om og miste motet.'),
(421, 3, 128, 'Du har intet med saken å gjøre, om Han viser dem nåde eller straffer dem, for de er illgjerningsmenn.'),
(422, 3, 129, 'Gud tilhører alt i himlene og på jord. Han tilgir dem Han vil, og straffer dem Han vil. Og Gud er tilgivende, nåderik.'),
(423, 3, 130, 'Dere som tror, driv ikke utbytting med doble eller flerdoble krav. Frykt Gud, at det må gå dere godt.'),
(424, 3, 131, 'Vokt dere for Ilden, som er gjort i stand for de vantro.'),
(425, 3, 132, 'Vis lydighet mot Gud og mot Sendebudet, så dere må bli vist nåde.'),
(426, 3, 133, 'Kappes med hverandre hen til tilgivelse fra Herren, og til paradiset, stort som himlene og jorden, gjort i stand for de gudfryktige,'),
(427, 3, 134, 'de som gir i velstand og nød, som holder sitt sinne i tømme og tilgir sin neste. Gud elsker dem som gjør godt,'),
(428, 3, 135, 'og dem som når de har gjort noe skammelig, og har gjort urett som slår tilbake på dem selv, kommer Gud i hu og ber om tilgivelse for sine synder, og hvem kan vel tilgi synder uten Gud, og ikke turer frem i det de har bedrevet, med vitende og vilje.'),
(429, 3, 136, 'Deres lønn er tilgivelse fra Herren, og paradisets haver, hvor bekker sildrer. Her skal de være og bli. Hvilken herlig lønn for dem som strever!'),
(430, 3, 137, 'Det er før deres tid skjedd mangt på jorden etter Guds ordning og bestemmelse. Reis rundt på jorden og se hvordan det endte med dem som fornektet!'),
(431, 3, 138, 'Dette er en klargjøring for menneskene, ledelse og formaning for gudfryktige.'),
(432, 3, 139, 'Bli ikke lei og bedrøvet! Dere er ovenpå, såfremt dere er troende!'),
(433, 3, 140, 'Om dere får sår, så har tilsvarende også rammet de andre. Vi fordeler slike dager blant menneskene, for at Gud kan vite hvem som tror, for at Han kan ta ut martyrer blant dere, Gud liker ikke urettferdige,'),
(434, 3, 141, 'og for at Han kan utmerke de troende, og tilintetgjøre de vantro.'),
(435, 3, 142, 'Eller trodde dere at dere kunne gå inn i paradiset, før Gud vet hvem av dere som kjemper, så Han kan kjenne de standhaftige?'),
(436, 3, 143, 'Dere ønsket dere martyrdøden før dere stod overfor den. Nå har dere sett den med egne øyne.'),
(437, 3, 144, 'Muhammad er bare et sendebud. Andre sendebud har gått bort før ham. Hvis nå han dør, eller drepes, vil dere da snu tvert om? Den som snur tvert om, skader ikke Gud det minste. Men Gud vil belønne de takknemlige.'),
(438, 3, 145, 'Intet menneske dør uten at Gud så vil, og når tiden kommer. Den som ønsker seg jordelivets belønning, ham gir Vi av den. Den som ønsker seg det hinsidige livs belønning, ham gir Vi av den. Vi belønner de takknemlige.'),
(439, 3, 146, 'Hvor mange profeter har vel ikke ikke kjempet, og med tallrike hjelpere? Og de mistet ikke motet av det som rammet dem for Guds sak, de ble ikke grepet av svakhet og overgav seg ikke. Gud elsker de standhaftige.'),
(440, 3, 147, 'Deres tale var: «Herre, tilgi oss våre synder og vår lettsindighet, la våre føtter stå fast, og hjelp oss mot det vantro folk!»'),
(441, 3, 148, 'Så gav Gud dem jordelivets belønning, og det hinsidige livs herlige belønning. Gud elsker dem som gjør godt.'),
(442, 3, 149, 'Dere som tror, hvis dere hører på de vantro, vil de få dere til å snu tvert om, og dere vil snu som tapere.'),
(443, 3, 150, 'Men Gud er deres beskytter, og Han er den beste hjelper.'),
(444, 3, 151, 'Vi skal kaste skrekk i vantros hjerter, fordi de setter ved Guds side noe Han aldri har gitt autoritet. Deres herberge blir Ilden! Bedrøvelig er illgjerningsmennenes herberge.'),
(445, 3, 152, 'Gud holdt jo sitt løfte til dere, da dere behersket fienden med Hans bifall, inntil dere sviktet og kom i krangel om saken og viste ulydighet, etter at Han hadde vist dere det dere ønsket. Blant dere var noen som ønsket denne verden, og noen som ønsket den hinsidige verden. Så førte Han dere bort fra dem, for å sette dere på prøve. Og Han unnskyldte dere, Gud er god mot de troende.'),
(446, 3, 153, 'Da dere sprang oppover uten å se dere tilbake etter noen, mens Sendebudet ropte etter dere, så belønnet Han dere med motgang etter motgang for at dere ikke skulle sørge over det dere hadde gått glipp av, eller det som overgikk dere, Gud vet hva dere gjør.'),
(447, 3, 154, 'Siden sendte Han over dere sikkerhet etter motgangen, en avslappethet kom over noen av dere. Men andre bekymret seg og tenkte urette tanker om Gud, hedenskapets tanker. De sa: «Har vi egentlig noe med denne saken å gjøre?» Si: «Saken i sin helhet ligger i Guds hånd.» De skjulte i sine hjerter noe de ikke viste deg, og sa: «Hadde vi noe med saken å gjøre, ville vi ikke blitt drept her.» Si: «Selv om dere hadde blitt igjen i deres hus, så ville de som døden var bestemt for, allikevel innfunnet seg på sitt dødsleie.» Dette for at Gud kunne utprøve hva som bor i deres bryst, og belyse det som var i deres hjerter. Gud kjenner det som bor i hjertene.'),
(448, 3, 155, 'De av dere som snudde ryggen den dag de to hærer møttes, Satan lot dem svikte for noe de hadde fortjent. Men Gud har tilgitt dem. Gud er tilgivende, og mild.'),
(449, 3, 156, 'Dere troende, vær ikke som de vantro, som sier om sine brødre når de drar rundt i landet eller går i kamp: «Om de bare hadde holdt seg hos oss, ville de ikke ha dødd eller blitt drept,» for at Gud kan legge dette som bekymring i deres hjerter. Gud gir liv og død, og Gud har øye med hva dere gjør.'),
(450, 3, 157, 'Om dere drepes eller dør for Guds sak, så er tilgivelse og nåde fra Gud bedre enn det dere samler i hop.'),
(451, 3, 158, 'Og om dere dør eller drepes, hos Gud skal dere samles.'),
(452, 3, 159, 'Ved en nåde fra Gud var du overbærende mot dem. Hadde du vært streng og hardhjertet, ville de skilt lag med deg. Så tilgi dem, be om tilgivelse for dem, rådfør deg med dem i saker. Og når du har bestemt deg, sett din lit til Gud. Gud elsker dem som setter sin lit til Ham.'),
(453, 3, 160, 'Hvis Gud hjelper dere, har dere ingen overmann. Lar Han dere i stikken, hvem kan da hjelpe dere i Hans sted? Til Gud må de troende sette sin lit!'),
(454, 3, 161, 'En profet kan ikke være uærlig. Den som er uredelig, skal på oppstandelsens dag komme med det han vant ved sin uredelighet. Så skal enhver få fullt tilmålt det han har fortjent, og det vil ikke bli gjort dem urett!'),
(455, 3, 162, 'Er vel den som søker Guds velbehag lik den som pådrar seg Guds mishag? Hans herberge blir helvete. Et sørgelig endelikt!'),
(456, 3, 163, 'De står på forskjellige trinn overfor Gud. Gud har øye med hva de gjør!'),
(457, 3, 164, 'Sannelig, Gud viste godhet mot de troende da Han sendte dem et sendebud av deres egne for å resitere Hans ord for dem, og gjøre dem rene og lære dem skriften og visdommen, for tidligere var de i klar villfarelse.'),
(458, 3, 165, 'Skulle dere vel si når et nederlag rammer dere, og dere nettopp har tilføyd et dobbelt så stort: «Hvordan kan dette ha seg?» Si: «Det kommer fra dere selv. Gud har makt over alle ting.»'),
(459, 3, 166, 'Det som overgikk dere den dag da de to hærer møttes, skjedde med Guds tillatelse,'),
(460, 3, 167, 'at Han skulle kjenne de troende, og kjenne hyklerne. Da det ble sagt til dem: «Kom, kjemp for Guds sak, eller i forsvaret,» svarte de: «Hadde vi bare visst noe om å kjempe, skulle vi nok blitt med dere.» Denne dagen var de nærmere vantro enn tro. De sa med sine lepper det som ikke var i deres hjerter. Men Gud vet hva de skjuler.'),
(461, 3, 168, 'Disse sa om sine brødre, mens de selv var hjemmesittere: «Hadde de bare hørt på oss, ville de ikke blitt drept.» Si: «Prøv å holde døden borte fra dere selv, hvis dere snakker sant!»'),
(462, 3, 169, 'Anse ikke dem som ble drept for Guds sak, som døde. Nei, de er levende hos Herren og mottar av Hans gaver.'),
(463, 3, 170, 'De gleder seg over det Gud har gitt dem av Sin godhet, gleder seg for dem som ikke er med dem, men er tilbake etter dem, fordi over dem skal ingen frykt hvile, ei heller sorg.'),
(464, 3, 171, 'De fryder seg ved Guds nåde og gavmildhet, ved at Gud aldri lar de troendes fortjeneste gå tapt.'),
(465, 3, 172, 'De som hørte på Gud og Sendebudet etter at de var blitt såret, for dem som handlet vel og viste gudsfrykt, er en herlig lønn i vente,'),
(466, 3, 173, 'for dem, til hvem man sa: «Folk har samlet seg mot dere, så pass dere,» mens dette bare øket deres tro, og de svarte: «Gud er nok for oss. En utmerket hjelper er Han!»'),
(467, 3, 174, 'De kom tilbake ved Guds nåde og yndest i god behold, og de fulgte Guds velbehag. Gud har godhet i overmål.'),
(468, 3, 175, 'Satan er slik, han innjager skrekk i dem som følger ham. Så vær ikke redd for dem. Men frykt Meg, hvis dere er troende.'),
(469, 3, 176, 'De som kappes i vantro, må ikke gjøre deg bedrøvet. De vil aldri kunne skade Gud det ringeste. Gud vil ikke gi dem andel i det hinsidige liv. Dem venter en stor straff.'),
(470, 3, 177, 'De som kjøper vantro for troens pris, de vil aldri kunne skade Gud det ringeste. Dem venter en smertelig straff.'),
(471, 3, 178, 'De vantro må ikke regne med at den utsettelse vi gir dem, er dem til beste. Vi gir dem utsettelse, slik at de kan tilta i synd. Dem venter en ydmykende straff.'),
(472, 3, 179, 'Gud vil ikke la de troende fortsette slik de har det nå, til Han har utskilt de dårlige fra de gode. Gud vil ikke avdekke det skjulte for dere. Men Han utvelger den Han vil av Sine sendebud. Hvis dere tror og har gudsfrykt, har dere en stor lønn i vente.'),
(473, 3, 180, 'De som er gjerrige med det Gud har gitt dem av Sin godhet, må ikke tro at dette er til deres beste. Nei, det er et onde for dem. Det de var gjerrige med, skal henges om halsen på dem på oppstandelsens dag. Gud arver alt som er i himlene og på jorden. Gud er vel underrettet om det dere gjør.'),
(474, 3, 181, 'Gud har hørt ordene til dem som sa: «Sannelig må Gud være fattig, og vi rike!» Vi skal notere det de sa, og at de drepte profetene i urett. Og Vi vil si: «Ta den brennende straffen!'),
(475, 3, 182, 'Dette, for det dere har sendt i forveien til regnskapet, for Gud gjør ingen urett mot Sine tjenere!»'),
(476, 3, 183, 'De som sa: «Gud har avtalt med oss at vi ikke skal tro noe sendebud før han bringer oss et offer som ild fortærer.» Si: «Sendebud før meg er kommet til dere med klar beskjed, og også det dere nevnte. Hvorfor drepte dere dem da, om dere skal snakke sant?»'),
(477, 3, 184, 'Og om de skulle kalle deg for løgner, så er sendebud før deg blitt kalt løgnere, som kom med den klare beskjed, med bøkene, og den opplysende skrift.'),
(478, 3, 185, 'Hvert menneske vil smake døden. På oppstandelsens dag vil dere få fullt oppgjør. Den som blir holdt unna Ilden, og føres inn i paradiset, han har vunnet seieren! Hva er jordelivet annet enn en bedragersk glede!'),
(479, 3, 186, 'Dere blir saktens satt på prøve, både når det gjelder eiendom og dere personlig. Dere vil få høre meget ondt fra dem som fikk skriften før dere, og fra avgudsdyrkerne, men hvis dere er standhaftige og gudfryktige, så avgjør dette saken.'),
(480, 3, 187, 'En gang inngikk Gud en avtale med dem som har fått skriften: «Gjør den kjent for folk, og skjul den ikke!» Men de kastet den bakom ryggen, og solgte den for en billig penge. Hvor avskyelig var denne handelen!'),
(481, 3, 188, 'Du må ikke tro at de som gleder seg over det de har brakt til veie, og gjerne vil prises for det de ikke har brakt til veie, tro ikke at de er sikre for straffen. De får en smertelig straff.'),
(482, 3, 189, 'Guds er herredømmet over himlene og jorden. Gud har makt over alle ting.'),
(483, 3, 190, 'I himlenes og jordens skapelse, i nattens og dagens veksling, sannelig, her er jærtegn for dem som har hjertets forstand,'),
(484, 3, 191, 'som kommer Gud i hu når de er stående, sittende eller liggende, som mediterer over himlenes og jordens skapelse: «Herre, Du har ikke skapt dette for intet! Priset være Du! Bevar oss for Ildens straff!'),
(485, 3, 192, 'Herre, den Du sender i Ilden, ham har Du gjort til skamme! De urettferdige har ingen hjelpere.'),
(486, 3, 193, 'Herre, vi har hørt en røst kalle oss til troen: ’Tro på deres Herre!’ Og vi har antatt troen. Herre, tilgi Du våre synder, utvisk våre misgjerninger, og ta oss til Deg, blant de rettferdige!'),
(487, 3, 194, 'Herre, gi oss det Du har lovet ved Dine sendebud, og gjør oss ikke til skamme på oppstandelsens dag! Du sviker ikke Ditt løfte!»'),
(488, 3, 195, 'Og Herren svarer dem: «Jeg vil ikke la en strevers strev gå tapt for noen blant dere, for mann eller kvinne, dere er ett. For dem som har forlatt sine hjem og ble drevet bort fra dem, som har lidd for Min sak, som har kjempet og mistet livet, sannelig, for dem vil Jeg utviske deres misgjerninger, og Jeg vil føre dem inn i paradisets haver, hvor bekker sildrer.» En belønning fra Gud, – og Gud, hos Ham er den herligste belønning.'),
(489, 3, 196, 'La deg ikke lure av de vantros farting omkring i landet.'),
(490, 3, 197, 'En liten fortjeneste! Men så blir deres herberge helvete. Et sørgelig hvilested.'),
(491, 3, 198, 'Men for dem som frykter Herren, venter paradisets haver hvor bekker sildrer. Der skal de være og bli, en gjestfrihet fra Gud. Det som finnes hos Gud er det beste for de fromme.'),
(492, 3, 199, 'For visst finnes blant skriftens folk slike som tror på Gud, på det som er åpenbart for dere, og det som er åpenbart for dem som er ydmyke for Gud, ikke selger Guds ord for en billig penge. For dem er deres lønn hos Herren. Og Gud er rask med avregningen.'),
(493, 3, 200, 'Dere som tror, vær standhaftige, streb mot standhaftighet og fasthet. Vis gudsfrykt, så det må gå dere godt.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'Dere mennesker, frykt Herren, Han som har skapt dere av et enkelt individ. Av dette skapte Han dets make. Fra disse to har Han spredt utover menn og kvinner i hopetall. Frykt Gud, ved hvis ordning dere har samkvem, og slektene består, Gud våker over dere.'),
(495, 4, 2, 'Gi de foreldreløse deres eiendom, og bytt ikke bort dårlig mot godt. Disponer ikke deres eiendom sammen med deres egen. Se, det ville være stor synd.'),
(496, 4, 3, 'Hvis dere er redde for ikke å gjøre rett mot de foreldreløse, ta til ekte det som passer dere av kvinner, to, tre eller fire. Men hvis dere er redde for ikke å kunne gjennomføre full likhet, så nøy dere med én, eller med deres slavinner. Det er det mest nærliggende for å unngå å gjøre urett.'),
(497, 4, 4, 'Gi kvinnene til egen disposisjon den brudegave de har krav på. Men hvis de selv finner for godt å overlate dere en del av den, så disponer den med god samvittighet.'),
(498, 4, 5, 'Gi ikke de mentalt uføre hånd om eiendom, som Gud har gitt dere til forvaltning, men underhold dem og kle dem derav, og tal til dem på en verdig måte.'),
(499, 4, 6, 'Sett de foreldreløse på prøve, inntil de når gifteferdig alder. Om dere da finner sunn dømmekraft hos dem, så overlat dem deres eiendom. Forbruk den ikke i sløsing og hast før de vokser til. Den velstående bør avstå, og den fattige bør forbruke i rimelighet. Når dere overlater dem deres eiendom, så sørg for vitner overfor dem. Gud strekker til for avregning.'),
(500, 4, 7, 'Det tilkommer menn en andel av det foreldre og nære slektninger etterlater, likeledes kvinner, enten det er meget eller lite, en bestemt andel.'),
(501, 4, 8, 'Hvis slektfolk, foreldreløse og fattige er til stede ved skiftet, så gi dem av boet og tal til dem på en verdig måte.'),
(502, 4, 9, 'La dem passe seg som hvis de etterlot barn i oppveksten, ville hatt bekymring for dem, la dem frykte Gud, og tale treffende ord.'),
(503, 4, 10, 'De som med urett forbruker foreldreløses gods, de fyller sin buk med ild. De vil brenne i Ilden.'),
(504, 4, 11, 'Gud bestemmer vedrørende deres barn: En mannlig arving tilkommer to kvinneliges andel. Hvis det er flere enn to kvinnelige og ingen mannlig arving, skal de dele to tredjedeler av arven, og er det bare én kvinnelig, under samme forutsetning, skal hun ha halvparten. Hver av avdødes foreldre skal ha en sjettepart av arven, hvis han etterlater barn. I motsatt fall arver foreldrene fullt ut, og moren får en tredjepart. Men har avdøde søsken, får moren en sjettepart, etter at legater og gjeld er dekket. Fedre og sønner, foreldre og barn, dere vet ikke hvem av dem som er nærmest i nytte for dere. Dette er en forordning fra Gud. Gud vet, er vis.'),
(505, 4, 12, 'Det tilkommer dere halvparten av det deres hustruer etterlater, såfremt de ikke har barn. I motsatt fall fjerdeparten, etter at legater og gjeld er dekket. Det tilkommer hustruene en fjerdepart av det dere etterlater, såfremt dere ikke har barn. I motsatt fall åttendeparten, etter at legater og gjeld er dekket. Hvis avdøde er uten arvtagere i rett opp- eller nedstigende linje, men har en bror og en søster, så får disse en sjettepart hver. Er det flere enn to, får de til sammen en tredjepart på deling, etter at legater og gjeld er dekket fullt ut. Dette er Guds forsoning. Gud vet, er mild.'),
(506, 4, 13, 'Dette er de grenser Gud har satt. Den som adlyder Gud og Hans sendebud, vil Han føre inn i paradisets haver, hvor bekker strømmer. Her skal de være og bli. Dette er den store seier.'),
(507, 4, 14, 'Men den som er ulydig mot Gud og Hans sendebud, og overskrider Hans grenser, ham vil Han sende i Ilden. Her skal han være og bli. Ham venter en ydmykende straff.'),
(508, 4, 15, 'Hvis noen av deres kvinner begår usømmelighet (hor), skal dere kreve fire vitner mot dem. Hvis de avgir vitnesbyrd, så hold dem i husarrest til døden henter dem, eller Gud finner en utvei for dem.'),
(509, 4, 16, 'Hvis to av dere begår dette, så straff begge. Hvis de angrer og forbedrer seg, så la dem være i fred. Gud viser miskunn, er nåderik.'),
(510, 4, 17, 'Guds miskunn er kun for dem som gjør feil i uvitenhet, og så straks angrer. Disse vil Gud vende seg mot i miskunn, Gud vet, er vis.'),
(511, 4, 18, 'Men det er ingen miskunn for dem som turer frem i onde gjerninger, inntil en av dem står overfor døden, og da sier: «Jeg angrer nå.» Heller ikke for dem som dør som vantro. For disse har Vi gjort klar en pinefull straff.'),
(512, 4, 19, 'Dere som tror! Det er ikke tillatt å overta kvinner ved arv mot deres vilje. Legg heller ikke vanskeligheter i veien for dem, for å legge hånd på en del av det som er gitt dem, med mindre de driver åpenbar usømmelighet. Omgås dem på skikkelig vis. Hvis det er slik at dere misliker dem, så kan det forholde seg slik at dere misliker noe, som Gud har nedlagt meget godt i.'),
(513, 4, 20, 'Om dere ønsker å skifte ut en kone med en annen, og har gitt den ene en stor gave, så krev intet tilbake. Vil dere vel kreve det ved falske beskyldninger og ved åpenbar synd?'),
(514, 4, 21, 'Hvordan kan dere vel gjøre det når dere har levd intimt sammen, og de har gjort en høytidelig avtale med dere?'),
(515, 4, 22, 'Dere skal ikke inngå ekteskap med kvinner som har vært gift med deres fedre, med mindre så allerede er skjedd. Dette var i sannhet skammelig og vederstyggelig, en ond skikk.'),
(516, 4, 23, 'Forbudt for dere er deres mødre, døtre, søstre, tanter, nieser. Videre deres ammer som har diet dere, og deres diesøstre, samt deres svigermødre, og deres fosterdøtre som står under deres formynderskap, og hvis mødre dere har hatt omgang med. Hvis så ikke har funnet sted, rammes dere ikke av skyld. Endelig hustruene til deres kjødelige sønner. Likeledes å ha to søstre på en gang, med mindre så allerede er skjedd. Gud er tilgivende, nåderik.'),
(517, 4, 24, 'Det samme gjelder ærbare kvinner, unntatt slavinner. Dette er Guds forskrift for dere. Bortsett fra disse står dere fritt i å søke kvinnelige partnere med de midler dere har, som ærbare menn, ikke i utukt. For den glede de gir dere, påligger det dere å gi dem det de tilkommer. Utover dette er det ingen urett i gjensidig overenskomst. Gud vet, er vis.'),
(518, 4, 25, 'De av dere som ikke har økonomisk utvei til å ekte frie, ærbare, rettroende kvinner, kan finne seg en blant rettroende slavinner. Gud kjenner vel til deres tro. Og dere er alle ett. Ta dem til ekte med deres foresattes samtykke, og gi dem det de tilkommer på vanlig vis, som ærbare kvinner, ikke som slike som driver utukt eller har elskere. Hvis disse, etter at de er blitt ærbare hustruer, begår usømmelighet, så rammes de av halvparten av den straff som gjelder frie kvinner. Å ta slavinner til ekte gjelder for dem blant dere som ellers ville falle i synd. Men at dere viser standhaftighet er bedre. Gud er tilgivende, nåderik.'),
(519, 4, 26, 'Gud ønsker å klargjøre for dere og lede dere ved forfedrenes praksis og vise dere miskunn. Gud vet, er vis.'),
(520, 4, 27, 'Gud ønsker å vise dere miskunn, men de som følger sine lyster, ønsker at dere kommer i fristelse og stort fall.'),
(521, 4, 28, 'Gud ønsker å lette tingene for dere, for mennesket ble skapt svakt.'),
(522, 4, 29, 'Dere som tror, sett ikke over styr det dere eier til intet, med mindre det dreier seg om en handel dere er blitt enige om. Drep ikke hverandre! Gud er nådig mot dere!'),
(523, 4, 30, 'Den som gjør dette i over tredelse og urett, vil Vi la møte Ilden! Dette er en lett sak for Gud.'),
(524, 4, 31, 'Om dere holder dere unna de store synder som er forbudt dere, vil Vi utslette deres misgjerninger, og føre dere inn gjennom ærens port.'),
(525, 4, 32, 'Begjær ikke det som Gud har gitt noen av dere fremfor andre! Menn får sin andel av det de har tjent, likeledes kvinner. Be Gud om Hans velvilje. Gud vet om alle ting.'),
(526, 4, 33, 'For alle har vi bestemt hvem som tiltrer arven, etter foreldre, nære slektninger og edsvorne livsfeller. Så gi dem deres del. Gud er vitne til alle ting.'),
(527, 4, 34, 'Menn er kvinners formyndere på grunn av det som Gud har utstyrt noen av dere med fremfor andre, og på grunn av de utgifter de bærer. Derfor skal rettskafne kvinner være lydige og bevare det som er hemmelig, fordi Gud ønsker det bevart. Dem som dere frykter oppsetsighet fra, skal dere formane, gå ikke til sengs med dem og gi dem stryk. Hvis de så er lydige, så forfølg ikke saken. Gud er opphøyet, stor.'),
(528, 4, 35, 'Hvis dere frykter brudd mellom et ektepar, send bud på en voldgiftsmann fra hans og fra hennes familie. Hvis disse går inn for forsoning, vil Gud fullbyrde dette mellom dem. Gud vet, er vel underrettet.'),
(529, 4, 36, 'Tjen Gud, og sett intet ved Hans side. Vis godhet mot foreldre, nærbeslektede, foreldreløse og fattige, mot naboer av klanen, og naboer utenfor, vennen ved deres side, den veifarende, og de slaver dere rår over. Gud liker ikke innbilske skrythalser.'),
(530, 4, 37, 'Heller ikke de gjerrige, som anbefaler gjerrighet, og gjemmer det Gud har gitt dem av Sin velvilje. For de vantro har Vi gjort klar en ydmykende straff.'),
(531, 4, 38, 'Heller ikke dem som gir av det de har for å bli sett av mennesker, som ikke tror på Gud og på dommens dag. Den som har Satan som følgesvenn, har fått en dårlig følgesvenn.'),
(532, 4, 39, 'Hva kunne det vel ha skadet dem om de hadde trodd på Gud og på dommens dag, og hadde gitt av det som Gud har gitt dem! Men Gud vet om dem.'),
(533, 4, 40, 'Gud gjør ikke et støvkorns urett. Og er det en god gjerning, gir Han den dobbelt vekt, og Han gir en stor lønn.'),
(534, 4, 41, 'Hvordan vil vel saken stå når vi fører frem et vitne fra hvert folk, og også fører deg som vitne mot dem?'),
(535, 4, 42, 'Denne dag vil de som var vantro og ikke hørte på Sendebudet, ønske at jorden var jevnet over dem. De vil ikke kunne skjule noe for Gud.'),
(536, 4, 43, 'Dere som tror, kom ikke til bønnen når dere er drukne, før dere vet hva dere sier. Kom heller ikke beflekket, unntatt underveis på reisefot, før dere har vasket dere. Men om noen er syk eller på reise, eller kommer fra avtredet, eller har hatt samkvem med kvinner, og ikke kan finne vann, så ta fin sand, og gni dere i ansiktet og på hendene. Gud forlater og tilgir.'),
(537, 4, 44, 'Du har vel hørt om dem som ble gitt en del av skriften? De kjøper villfarelse, og vil at dere også skal fare vill.'),
(538, 4, 45, 'Men Gud vet godt om deres fiender. Og Gud er tilstrekkelig som venn og hjelper.'),
(539, 4, 46, 'Blant jødene er slike som forvrir ordene, og tar dem ut av sin sammenheng, idet de sier: «Vi har hørt, men vi hører ikke,» og: «Hør, uten at det er deg gitt å høre,» og: «Ta oss i varetekt.» Alt dette ved å forvri språket, og for å gi religionen et stikk. Hadde de sagt: «Vi har hørt, og vi adlyder,» og: «Hør,» og: «Ta oss i din varetekt,» ville det vært bedre for dem, og mer rettskaffent. Men Guds forbannelse hviler over dem for deres vantro, så de tror kun lite.'),
(540, 4, 47, 'Dere som har mottatt skriften, tro på det som Vi har åpenbart, som stadfester det dere allerede har, før Vi utvisker ansiktstrekkene og snur dem bak frem, eller Vi forbanner dere, slik Vi forbannet sabbatsovertrederne. Guds bestemmelse blir iverksatt.'),
(541, 4, 48, 'Gud tilgir ikke at man setter noe ved Hans side, men Han tilgir det som er mindre enn dette overfor den Han vil. Den som setter noe ved Guds side, har i sannhet begått en svær synd.'),
(542, 4, 49, 'Du kjenner vel til dem som erklærer seg selv for rene? Men det er Gud som kjenner ren, den Han bestemmer. Og ingen gjøres urett, så meget som en daddeltrevle.'),
(543, 4, 50, 'Se, hvordan de dikter opp løgn mot Gud! Dette er i sannhet tilstrekkelig som åpenbar synd!'),
(544, 4, 51, 'Du kjenner vel til dem som har mottatt en del av skriften? De tror på demoner og avguder, og de sier om hedningene: «Disse er sannelig på rettere vei enn de (islam) troende!»'),
(545, 4, 52, 'Over disse hviler Guds forbannelse, og for den Gud forbanner, finner du ingen hjelper.'),
(546, 4, 53, 'Har vel disse noen andel i herredømmet? I så fall ville de ikke gitt sine medmennesker så meget som skinnet av en daddel!'),
(547, 4, 54, 'Eller er det slik at de misunner sine medmennesker det Gud har gitt dem av sin godhet? Og dog gav Vi Abrahams folk skriften og visdommen, og Vi gav dem et stort rike.'),
(548, 4, 55, 'Blant dem er det noen som tror, og noen som holder seg borte. Helvete er en ild som er tilstrekkelig.'),
(549, 4, 56, 'De som fornekter Vårt ord, vil Vi la møte Ilden. Hver gang deres hud er avbrent, vil Vi bytte den og gi dem en ny, så de kan få føle straffen. Gud er mektig, vis.'),
(550, 4, 57, 'Men de som tror og lever rettskaffent, vil Vi føre inn i paradisets haver, hvor bekker sildrer. Her skal de alltid være og bli. Her venter dem rene hustruer, og Vi vil lede dem til en skyggefull skygge.'),
(551, 4, 58, 'Gud påbyr at dere leverer tilbake betrodd gods til eiermannen, og at dere dømmer rettferdig når dere sitter til doms. Utmerket er det som Gud formaner dere til. Gud hører, ser.'),
(552, 4, 59, 'Dere som tror, vis lydighet mot Gud, mot Sendebudet, og mot dem som har autoritet blant dere. Hvis dere strides om noe, så bring det frem for Gud og Sendebudet, såfremt dere tror på Gud og dommens dag. Dette er det rette og den beste løsning.'),
(553, 4, 60, 'Du kjenner vel til dem som hevder at de tror, på det som er åpenbart for deg, og før din tid? Men de ønsker å gå til avgudene med sine tvister, selv om de er pålagt å forkaste dem! Satan ønsker å villede dem langt på villspor.'),
(554, 4, 61, 'Sier man til dem: «Kom til det Gud har åpenbart! Kom til Sendebudet!» Da ser du hyklerne holde seg og andre borte fra deg så godt de kan.'),
(555, 4, 62, 'Men hvordan er det hvis en prøvelse rammer dem på grunn av det de selv har stelt i stand? Da kommer de til deg og sverger ved Gud: «Vi ville jo bare godt og forsoning!»'),
(556, 4, 63, 'For disse vet Gud hva som bor i deres hjerter. Ta avstand fra dem, forman dem, og tal inntrengende til dem om deres holdning!'),
(557, 4, 64, 'Når Vi sender et sendebud, så er det for at han skal bli adlydt, ved Guds hjelp. Hvis de således, når de hadde pådratt seg skyld for urett, var kommet til deg, hadde bedt om Guds tilgivelse og Sendebudet hadde bedt om tilgivelse for dem, så ville de funnet at Gud er fylt av miskunn og nåde.'),
(558, 4, 65, 'Men sannelig, ved Herren, de er ikke troende før de lar deg dømme i sine stridigheter, og ikke finner det vanskelig å akseptere din avgjørelse, og overgir seg fullt ut!'),
(559, 4, 66, 'Hadde Vi pålagt dem: «Drep hverandre,» eller: «Forlat deres hjem,» så ville de rimeligvis ikke gjort dette, unntatt noen få. Men om de nå ville gjøre det de faktisk blir formant til, ville det vært godt for dem, og mer styrkende.'),
(560, 4, 67, 'Da ville Vi gi dem stor belønning,'),
(561, 4, 68, 'og lede dem på rett vei.'),
(562, 4, 69, 'De som viser lydighet mot Gud og mot Sendebudet, vil være sammen med dem som Gud har beredt glede, profetene, de rettferdige, martyrene og de rettskafne. Gode følgesvenner er dette.'),
(563, 4, 70, 'Slik er Guds godhet. Gud strekker til når det gjelder viten.'),
(564, 4, 71, 'Dere som tror, vis aktsomhet! Rykk ut til kamp i kompanier, eller samlet!'),
(565, 4, 72, 'Det finnes slike som henger etter, og hvis dere rammes av tap, sier han: «Gud viste meg nåde, ved at jeg ikke var sammen med dem.»'),
(566, 4, 73, 'Men hvis gunst fra Gud følger dere, sier han sannelig, som om det ikke bestod vennskap mellom dere: «Om jeg bare hadde vært med dem, så ville jeg gjort en stor innsats!»'),
(567, 4, 74, 'La slike kjempe for Guds sak, som er villig til å bytte det jordiske liv med det hinsidige. Den som kjemper for Guds sak, om han faller eller vinner, ham vil Vi gi en stor lønn.'),
(568, 4, 75, 'Hva er det med dere? At dere ikke vil kjempe for Guds sak, og for de svake, menn, kvinner og barn, som sier: «Herre, før oss ut fra denne by av illgjerningsmenn, send oss fra Deg en som kan ta seg av oss, en hjelper!»'),
(569, 4, 76, 'De troende kjemper for Guds sak. De vantro kjemper for de falske guders sak. Bekjemp Satans venner! Sannelig, Satans strategi er svak!'),
(570, 4, 77, 'Du kjenner vel til dem som det ble sagt til: «La våpnene falle! Forrett bønnen! Gi det rituelle bidrag!» Når det så ble pålagt dem å kjempe, så frykter en del av dem menneskene, slik de skulle frykte Gud, og mer til, og sier: «Herre, hvorfor har Du pålagt oss å kjempe? Kunne Du ikke gi oss en liten utsettelse?» Si: «Dette livets vinning er liten. Det hinsidige liv er bedre for den gudfryktige. Dere vil ikke lide urett så meget som en trevle av en daddel!»'),
(571, 4, 78, 'Hvor dere enn er, vil døden nå dere, selv om dere var i mektige tårn. Hvis noe godt møter dem, sier de: «Dette er fra Gud.» Men hvis noe ondt overgår dem, sier de: «Dette er din skyld!» Si: «Alt kommer fra Gud!» Hva er i veien med dette folk, at de knapt nok forstår noe av det som skjer?'),
(572, 4, 79, 'Det som møter deg av godt, er Guds verk. Det som overgår deg av ondt, er ditt eget verk. Deg har Vi sendt til menneskene som sendebud. Gud strekker til som vitne.'),
(573, 4, 80, 'Den som viser lydighet mot Sendebudet, viser lydighet mot Gud. Og den som vender seg bort, vel, Vi har ikke sendt deg som oppsynsmann over dem.'),
(574, 4, 81, 'De erklærer: «Lydighet!» Men når de går fra deg, bruker en del av dem natten til å pønske ut noe helt annet enn det du har sagt. Gud noterer det de pønsker ut! Bry deg ikke med dem, og sett din lit til Gud! Gud strekker til som verge.'),
(575, 4, 82, 'Reflekterer de ikke over Koranen? Hvis den stammet fra en annen enn Gud, så ville de nok funnet mange motsigelser i den!'),
(576, 4, 83, 'Hvis noe inntreffer som skaper sikkerhet eller frykt, så sprer de det utover. Men hadde de isteden fremlagt det for Sendebudet, og for dem som har autoritet blant dem, så ville de blant dem fått underretning som kan vurdere saken. Hadde det ikke vært for Guds velvilje og Hans nåde mot dere, ville dere sluttet dere til Satan, unntatt noen få.'),
(577, 4, 84, 'Så kjemp for Guds sak! Du har bare ansvar for deg selv. Og oppmuntre de troende. Kanskje Gud vil holde de vantros makt i tømme! Gud er større i makt, og større i straff.'),
(578, 4, 85, 'Den som legger inn ord for en god sak, godskrives en andel i den. Og den som legger inn ord for en urett sak, belastes fullt ut dens tilsvarende. Gud passer på alle ting.'),
(579, 4, 86, 'Når dere hilses med en hilsen, så svar med en enda bedre, eller en tilsvarende. Gud fører regnskap med alle ting.'),
(580, 4, 87, 'Gud, det er ingen gud unntatt Ham. Han vil visselig samle dere opp på oppstandelsens dag, det er ingen tvil om dette. Hvem er vel mer sannferdig enn Gud i sine meddelelser?'),
(581, 4, 88, 'Hva er det i veien med dere, at dere deler dere i to i synet på hyklerne? Når Gud har forstøtt dem for det de har pådratt seg? Vil dere prøve å rettlede en som Gud lar seile sin egen sjø? For den som Gud lar seile sin egen sjø, finner du ingen vei!'),
(582, 4, 89, 'De vil at dere skal være vantro, som de selv er vantro, slik at dere blir like. Slutt dere ikke til noen av dem, før de drar ut for Guds sak! Hvis de snur seg bort, så pågrip dem og drep dem der dere finner dem, og ta ingen av dem til venn eller hjelper,'),
(583, 4, 90, 'unntatt dem som drar hen til et folk dere har sluttet forbund med, eller de kommer til dere fordi de føler seg beklemt i hjertet ved å stride mot dere eller mot sitt eget folk. Hvis Gud hadde villet, kunne Han gitt dem makt over dere, og da ville de kjempet mot dere. Men hvis de holder seg unna dere og ikke kjemper mot dere, og tilbyr fred, så har Gud ikke anvist dere noen fremgangsmåte overfor dem.'),
(584, 4, 91, 'Dere vil finne andre som ønsker å leve i fred med dere og med sine egne. Hver gang de forledes til strid, bringes de til fall. Hvis de ikke holder seg unna dere, tilbyr fred og legger bånd på seg, pågrip dem og drep dem der dere finner dem. Overfor disse har Vi gitt dere klar fullmakt.'),
(585, 4, 92, 'En troende må ikke drepe en troende, med mindre det dreier seg om en feiltagelse. Om noen dreper en troende ved feiltagelse, så påligger det ham å frigjøre en troende slave, og betale blodpenger til hans familie, med mindre de ettergir. Tilhører den drepte et folk dere ligger i fiendskap med, og er han troende, så skal en troende slave frigjøres. Tilhører han et folk dere har forbund med, så skal det betales blodpenger til hans familie, og en troende slave frigjøres. Finner drapsmannen ingen utvei til dette, så skal han faste i to påfølgende måneder, dette som en miskunn fra Gud. Gud vet, er vis.'),
(586, 4, 93, 'Men den som forsettlig dreper en troende, hans lønn er helvete, og der skal han være og bli. Guds vrede og forbannelser er over ham, og Han har beredt ham en svær straff.'),
(587, 4, 94, 'Dere som tror når dere farter omkring for Guds sak, så se dere for! Si ikke til den første den beste som hilser dere: «Du er ingen troende,» idet dere er ute etter det denne verden har å by. Hos Gud er det store krigsbytte! Slik var dere en gang, men Gud har gitt dere av sin godhet, så se dere for! Gud er vel underrettet om det dere gjør.'),
(588, 4, 95, 'De troende som sitter hjemme, unntatt er de uføre, og de som satser liv og eiendom for Guds sak, de er ikke like. Gud har gitt dem som satser liv og eiendom rang over hjemmesittere. Gud har lovet begge det gode, men Han har utmerket de stridende fremfor hjemmesitterne med en stor lønn,'),
(589, 4, 96, 'i rang hos Ham, i tilgivelse og nåde. Gud er tilgivende, nåderik.'),
(590, 4, 97, 'Til dem som englene henter, mens de ennå pådrog seg skyld ved ikke å våge å anta islam, vil de si: «Under hvilke forhold levde dere?» Og de vil svare: «Vi var undertrykket i landet.» Englene sier: «Men var ikke Guds jord stor nok, så dere kunne utvandre?» Slike vil få helvete til herberge. Et sørgelig endelikt.'),
(591, 4, 98, 'Unntatt er menn, kvinner og barn som vel lever i undertrykkelse, men ikke er i stand til å finne på noe, og ikke finner noen utvei.'),
(592, 4, 99, 'Kanskje vil Gud stryke over det for disse. Gud stryker ut og tilgir.'),
(593, 4, 100, 'Den som utvandrer for Guds sak, vil finne mange muligheter på jorden. Hvis noen forlater sitt hus, idet han utvandrer til Gud og Hans sendebud, og så innhentes av døden, da påligger det Gud å utrede hans lønn. Gud er tilgivende, nåderik.'),
(594, 4, 101, 'Når dere farter omkring i landet, så er det ingen synd om dere korter ned bønnen, hvis dere frykter at de vantro kan plage dere. De vantro er en klar fiende for dere.'),
(595, 4, 102, 'Så når du er blant de troende og foreslår bønnen for dem, skal en gruppe av gangen stå frem hos deg, og de skal bære sine våpen. Når de faller ned i bønn, skal de andre stå bak dere. Derpå skal en ny gruppe, som ikke har deltatt i bønnen, komme frem og be sammen med deg. De må vise aktsomhet og bære sine våpen. De vantro så gjerne at dere neglisjerte våpen og utrustning så de plutselig kunne overfalle dere. Dog er det ingen synd å legge bort våpnene om dere plages av regn eller er syke. Men vis aktsomhet! Sannelig, Gud har beredt de vantro en ydmykende straff.'),
(596, 4, 103, 'Når dere har fullført bønnen, så kom Gud i hu, stående, sittende eller liggende. Når dere så er i sikkerhet, utfør bønnen på normal måte. Bønnen påhviler de troende som en forskrift med angitte tider.'),
(597, 4, 104, 'Men bli ikke lei av å være etter de vantro! Om dere lider, så lider også de på samme måte som dere. Men dere håper på noe fra Gud, som de ikke har håp om. Og Gud vet, er vis.'),
(598, 4, 105, 'Vi har åpenbart for deg skriften med sannheten, så du kan dømme mellom folk ved det Gud har vist deg. Vær ingen forkjemper for svikefulle.'),
(599, 4, 106, 'Be Gud om tilgivelse. Gud er tilgivende, nådig.'),
(600, 4, 107, 'Forsvar ikke dem som sviker sitt eget jeg. Gud elsker ikke den som sviker og synder.'),
(601, 4, 108, 'De skjuler seg for menneskene, men de kan ikke skjule seg for Gud. Han er hos dem når de nattestid pønsker ut ting å si som ikke behager Ham. Gud har full kontroll med det de gjør.'),
(602, 4, 109, 'Slik er dere! Dere forsvarer dem under jordelivet, men hvem vil forsvare dem fremfor Gud på oppstandelsens dag, eller da ta seg av deres sak?'),
(603, 4, 110, 'Men den som handler ondt eller pådrar seg skyld for urett, og så søker Guds tilgivelse, han vil finne at Gud er tilgivende, nåderik.'),
(604, 4, 111, 'Den som pådrar seg en synd, gjør det bare for egen regning. Gud vet, er vis.'),
(605, 4, 112, 'Og den som pådrar seg et feiltrinn eller en synd, og så kaster skylden på en uskyldig, han bærer byrden av falsk beskyldning og åpenbar synd.'),
(606, 4, 113, 'Var det ikke for Guds godhet og nåde mot deg, ville noen av dem gjerne ført deg på villspor. Men de fører ingen på villspor, unntatt seg selv, og de kan ikke skade deg det ringeste. Gud har åpenbart for deg skriften og visdommen, og lært deg det du ikke visste. Guds godhet mot deg er stor.'),
(607, 4, 114, 'Det er intet godt i meget av deres overlegninger, unntatt når én oppfordrer til velgjørenhet og det anerkjent gode, eller til å fremme fordragelighet mellom menneskene. Den som handler slik, idet han søker Guds velbehag, ham vil Vi gi en stor lønn.'),
(608, 4, 115, 'Den som bryter med Sendebudet etter at ledelsen er blitt klar for ham, og følger en annen vei enn de troendes, ham vil Vi overlate til det han har vendt seg mot. Og Vi vil la ham møte helvete. Og det er en trist skjebne.'),
(609, 4, 116, 'Gud tilgir ikke at Han gis med- guder. Men Han tilgir den Han vil alt som er mindre enn dette. Den som gir Gud medguder, er sannelig langt på villstrå!'),
(610, 4, 117, 'Det er bare kvinnelige makter de påkaller ved siden av Ham, og de påkaller en opprørsk Satan,'),
(611, 4, 118, 'som Gud har forbannet, og som svarte: «Jeg vil sannelig overta en bestemt del av dine tjenere!'),
(612, 4, 119, 'Jeg vil visselig lede dem på villspor og fylle dem med begjær. Jeg vil få dem til å skjære ørene av buskapen. Jeg vil få dem til å forandre Guds skapning!» Men den som slutter seg til Satan fremfor Gud, han er en klar taper.'),
(613, 4, 120, 'Han lover dem godt og fyller dem med begjær, men Satans løfter er bare narrespill.'),
(614, 4, 121, 'Disse er det hvis herberge blir helvete, og de finner ingen mulighet til å unnslippe.'),
(615, 4, 122, 'Men de som tror og gjør det som er rett og godt, dem vil Vi føre inn i paradisets haver, hvor bekker strømmer. Og her skal de være og bli. Guds løfte er sannhet. Hvem er sannferdigere enn Gud i sin tale?'),
(616, 4, 123, 'Det blir ikke slik dere ønsker, eller skriftfolkene ønsker. Den som gjør det som ondt er, får lønnen for det. Og han finner ingen utenom Gud, som kan ta seg av ham og hjelpe.'),
(617, 4, 124, 'Men den som handler rett og godt, det være seg mann eller kvinne, og er troende – disse vil gå inn i paradiset. De vil ikke lide den ringeste urett.'),
(618, 4, 125, 'Hvem er bedre faren når det gjelder religion enn den som innordner seg under Gud, handler vel og følger Abrahams, Gud-søkerens lære? Gud tok Abraham til venn!'),
(619, 4, 126, 'Gud tilhører alt i himlene og på jord. Guds makt omfatter alt.'),
(620, 4, 127, 'De ber deg om rettledning vedrørende kvinner. Si: «Gud gir dere rettledning vedrørende dem, og det som nevnes for dere i skriften, om foreldreløse piker som dere ikke gir det de har krav på, men ønsker å gifte dere med, og om hjelpeløse barn, så dere viser rettferd og rimelighet mot de foreldreløse. Det dere gjør av godt, Gud vet det.»'),
(621, 4, 128, 'Hvis en kvinne frykter mishandling eller likegyldighet fra sin mann, er det ikke galt av dem om de finner en ordning seg imellom, for fredelig ordning er best. Menneskene ligger under for grådighet. Men hvis dere handler vel og viser gudsfrykt, så er Gud vel underrettet om det dere gjør.'),
(622, 4, 129, 'Dere vil ikke være i stand til å behandle deres hustruer likt, hvor meget dere enn forsøker å holde på det. Men vis ingen klar preferanse, og la andre bli hengende i et tomrom. Hvis dere finner en god ordning, og viser gudsfrykt, så er Gud tilgivende, nåderik.'),
(623, 4, 130, 'Men om de skilles, vil Gud sørge for dem begge av Sin overflod. Gud er storslagen, vis.'),
(624, 4, 131, 'Gud tilhører alt i himlene og på jord! Vi har pålagt dem som fikk skriften før dere og dere: «Vis gudsfrykt!» Hvis dere forkaster i vantro, Gud tilhører alt i himlene og på jord. Gud er selvtilstrekkelig, verdig all pris.'),
(625, 4, 132, 'Gud tilhører alt i himlene og på jord. Gud strekker til som verge.'),
(626, 4, 133, 'Dere mennesker, om Han vil, kan Han la dere forgå og la andre komme. Gud makter dette!'),
(627, 4, 134, 'Om noen ønsker denne verdens belønning, så beror både den dennesidige og den hinsidige lønn hos Gud. Gud hører, er observant.'),
(628, 4, 135, 'Dere som tror, stå fast ved det rette, som vitner for Gud, selv om det går mot egne interesser eller foreldres og nærståendes. Rik eller fattig, Gud har omsorg for begge. Følg ikke det dere lyster på bekostning av rettferdighet, for hvordan dere snur og vender, Gud vet hva dere gjør.'),
(629, 4, 136, 'Dere som tror, tro på Gud, og tro Hans sendebud! Tro på skriften som Han har åpenbart for Sitt sendebud, og på skriften som Han har åpenbart tidligere. Den som ikke tror på Gud, på Hans engler, Hans hellige bøker, alle Hans sendebud og dommens dag, han er sannelig langt på villspor.'),
(630, 4, 137, 'Men de som tror, og så faller fra, så tror, og så faller fra og tiltar i vantro, de kan ikke vente at Gud tilgir dem og viser dem vei.'),
(631, 4, 138, 'Bebud for hyklerne at det venter dem en smertelig straff.'),
(632, 4, 139, 'De som søker vantro som venner fremfor troende, er det makt de søker hos dem! All makt tilhører Gud!'),
(633, 4, 140, 'Han har jo åpenbart for dere i skriften: «Når dere hører at man fornekter og spotter Guds ord, så sitt ikke hos dem før de skifter samtaleemne, ellers blir dere lik dem.» Gud vil samle hyklerne og de vantro i helvete alle sammen.'),
(634, 4, 141, 'Noen avventer tingenes gang for dere, og hvis Gud gir dere en seier, sier de: «Vi var da med dere?» Og går det i de vantros favør, sier de: «Var det ikke vi med vår nøytralitet som var avgjørende for dere? Har vi ikke dermed beskyttet dere mot de troende?» Gud vil dømme mellom dere på oppstandelsens dag. Gud vil aldri gi de vantro noen utvei til å rå med de troende!'),
(635, 4, 142, 'Hyklerne forsøker å narre Gud, men det er Gud som narrer dem. Når de stiller til bønn, gjør de det på en slapp måte for å demonstrere for folk. Gud har de lite i tankene.'),
(636, 4, 143, 'De vakler hele tiden, ikke disse, ikke disse heller. Den Gud lar seile sin egen sjø, for ham finner du ingen vei.'),
(637, 4, 144, 'Dere som tror, søk ikke venner blant de vantro fremfor de troende. Vil dere da gi Gud klart bevis mot dere?'),
(638, 4, 145, 'Sannelig, hyklerne vil havne i Ildens dypeste grunn, og du vil ikke finne noen som kan hjelpe dem.'),
(639, 4, 146, 'Med mindre de omvender seg og forbedrer seg, holder fast ved Gud og vier Gud sin religion i oppriktighet. Disse er ett med de troende. Og Gud vil gi de troende en stor lønn.'),
(640, 4, 147, 'Hva skulle vel Gud gjøre med å straffe dere hvis dere viser takknemlighet og tror? Gud er anerkjennende, allvitende.'),
(641, 4, 148, 'Gud liker ikke at man roper stygge ord, med mindre man påføres urett. Gud hører og vet.'),
(642, 4, 149, 'Om dere åpent eller i det skjulte gjør noe godt, eller unnskylder en urett, se, Gud unnskylder, og er mektig.'),
(643, 4, 150, 'De som fornekter Gud og de sendebud Han har sendt, og ønsker å gjøre en forskjell mellom Gud og Hans sendebud, de sier: «Vi tror på noen, og tror ikke på andre,» idet de søker en middelvei,'),
(644, 4, 151, 'disse er i sannhet vantro! De vantro har vi beredt en ydmykende straff.'),
(645, 4, 152, 'Men de som tror på Gud og Hans sendebud, og ikke gjør forskjell på noen av dem, disse vil Han gi deres lønn, Gud er tilgivende, nåderik.'),
(646, 4, 153, 'Skriftfolket ber deg besørge nedsendt til dem et skriftstykke fra himmelen. Av Moses forlangte de enda mer, idet de sa: «Vis oss Gud klart og tydelig.» Men da ble de rammet av lynet for sin urettferdighet. Så laget de gullkalven etter at de hadde mottatt klar beskjed. Vi tilgav dette, og Vi gav Moses klar autoritet.'),
(647, 4, 154, 'Vi hevet fjellet over dem ved paktslutningen, og Vi sa til dem: «Gå inn gjennom porten i underdanighet,» og videre: «Krenk ikke sabbaten!» Vi sluttet en høytidelig pakt med dem.'),
(648, 4, 155, 'Fordi de så brøt sin pakt og forkastet Guds ord, drepte profetene rettsstridig og sa: «Våre hjerter er uomskårne?» Nei, ikke slik, Gud har forseglet dem for deres vantro, og derfor er det kun få som tror,'),
(649, 4, 156, 'for deres vantro, og for det de sa om Maria, en svær bakvaskelse, og fordi de hevder:'),
(650, 4, 157, '«Se, vi har drept Messias, Jesus, Marias sønn, Guds sendebud!» Men de drepte ham ikke, og de korsfestet ham ikke, men det fortonet seg slik for dem. De som er uenige om ham, er i tvil med hensyn til dette. De har ingen kunnskap om det, men følger formodninger. De har ikke drept ham med sikkerhet.'),
(651, 4, 158, 'Tvert imot, Gud har tatt ham til Seg. Gud er mektig, vis.'),
(652, 4, 159, 'Alle blant skriftfolkene skal tro på ham før enden. På dommens dag vil han være vitne overfor dem.'),
(653, 4, 160, 'Og på grunn av jødenes urettferdighet har Vi forbudt dem goder, som tidligere var dem tillatt, fordi de legger hindringer på Guds vei for mange,'),
(654, 4, 161, 'fordi de driver åger, som vitterlig ble dem forbudt, og tilegner seg folks eiendom på uhederlig måte. Vi har beredt de vantro blant dem en smertelig straff.'),
(655, 4, 162, 'Men de av dem som er grunnfestet i viten, og de troende, som tror på det som er åpenbart for deg, og før deg, som forretter bønnen og betaler det rituelle bidrag, som tror på Gud og på dommens dag, disse vil Vi gi en stor lønn.'),
(656, 4, 163, 'Vi har gitt deg åpenbaringer, slik som Vi gav Noa åpenbaringer, og profetene etter ham, Abraham, Ismael, Isak, Jakob, stammehøvdingene, og Jesus, Job, Jonas, Aron og Salomo, og til David gav Vi salmene.'),
(657, 4, 164, 'Og sendebud som Vi før har fortalt deg om, og sendebud som Vi ikke har fortalt deg om, og Gud talte med Moses direkte,'),
(658, 4, 165, 'sendebud, med det glade budskap, og med advarsler, for at folk ikke skal ha noen innsigelser overfor Gud, etter sendebudene. Gud er mektig, vis.'),
(659, 4, 166, 'Gud selv innestår for det Han har åpenbart for deg, Han har åpenbart det ved Sin viten. Og englene bærer vitnesbyrd. Men Gud er tilstrekkelig som vitne.'),
(660, 4, 167, 'De som er vantro og legger hindringer på Guds vei, de er sannelig kommet langt i villfarelse.'),
(661, 4, 168, 'De som er vantro og øver rett, de kan ikke vente at Gud tilgir dem og viser dem vei,'),
(662, 4, 169, 'unntatt veien til helvete. Der skal de være og bli. Dette er en lett sak for Gud.'),
(663, 4, 170, 'Dere mennesker, Sendebudet er nå kommet til dere med sannheten fra Herren, så tro, til deres eget beste! Hvis dere ikke tror, Gud tilhører alt i himlene og på jord. Gud vet, er vis.'),
(664, 4, 171, 'Dere som har mottatt skriften, overdriv ikke i deres religion. Si ikke om Gud annet enn det som er sant! Messias, Jesus, Marias sønn, var bare et Guds sendebud, og Hans ord, som Han inngav Maria, en Ånd fra Ham! Tro på Gud, og Hans sendebud, og si ikke: «Gud er tre.» La det være, til deres eget beste! Gud er én Gud! Ære være Ham, at Han skulle ha en sønn! Ham tilhører alt i himlene og på jord! Gud er tilstrekkelig som beskytter!'),
(665, 4, 172, 'Messias forsmår ikke å være Guds tjener, og heller ikke englene, som er Ham nær. Den som forsmår å tjene Ham, og viser hovmod, Han vil samle dem alle hos seg!'),
(666, 4, 173, 'De som tror og handler rett, dem vil Han gi full lønn, og mer til av Sin overflod. Men de som forsmår og viser hovmod, dem vil Han straffe med en smertelig straff. De vil ikke finne utenom Gud noen venn eller hjelper.'),
(667, 4, 174, 'Dere mennesker, det er nå kommet bevis til dere fra Herren, og Vi har nedsendt til dere et klart lys!'),
(668, 4, 175, 'De som tror på Gud og holder fast ved Ham, dem vil Han ta inn i Sin nåde og velvilje, og lede dem på den rette vei til Seg.'),
(669, 4, 176, 'De ber deg om rettledning: Si: «Gud rettleder dere vedrørende arvinger i sidelinje. Dør en mann uten barn, men har en søster, så tilfaller henne halvparten av arven. Og han arver henne om hun er uten barn. Hvis det er to søstre, så tilfaller dem to tredjedeler av arven. Hvis det er brødre og søstre, så arver en bror som to søstre.» Gud gjør det klart for dere, for at dere ikke skal fare vill. Gud vet om alle ting.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'Dere som tror, oppfyll det som er fastsatt! Planteetere er tillatt for dere, unntatt det som særskilt nevnes. Men dere må ikke anse det fritt å drive jakt når dere er sakralisert for pilegrimsritualet. Gud bestemmer det Han vil.'),
(671, 5, 2, 'Dere som tror, respekter det sakrale i Guds kultsymboler, den fredlyste måned, offerdyrene av alle slag, og pilegrimene underveis til den hellige moské, idet de søker sin Herres gunst og velbehag. Men når dere har trådt ut av pilegrimens hellige status, da kan dere drive jakt. La ikke motvilje mot et folk som har stengt dere ute fra den hellige moské forlede dere til aggresjon. Hjelp hverandre henimot fromhet og gudsfrykt, ikke mot synd og fiendskap. Vis gudsfrykt, for Gud er streng i Sin straff.'),
(672, 5, 3, 'Forbudt som føde for dere er det selvdøde, blod, svinekjøtt, alt som er helliget en annen enn Gud, det som er kvalt, eller drept ved slag eller fall eller er stanget i hjel, eller som er angrepet av rovdyr, med mindre dere har nådd å slakte det rituelt, samt det som er slaktet ved en hedensk offerstein. Likeledes er det forbudt å utforske skjebnen ved spådomspiler. Dette er synd. I dag har de vantro oppgitt håpet om å rå med deres religion. Så frykt dem ikke, men frykt Meg! I dag har Jeg fullbrakt deres religion og fullbyrdet Min nåde mot dere, og godtatt islam som religion for dere. Men om noen tvinges av hunger til å overtre et spiseforbud, uten ond lyst, så er Gud tilgivende, nåderik.'),
(673, 5, 4, 'Man spør deg hva som er lovlig mat. Si: «Tillatt for dere er alle gode ting. Og når dere har lært opp jakthunder, og fugler, slik Gud har lært dere, så spis det de bringer dere, og påkall Guds navn over det. Frykt Gud, Gud er rask i avregningen!»'),
(674, 5, 5, 'I dag er alle gode ting tillatt for dere! Skriftfolkenes mat er tillatt for dere, og deres mat er tillatt for dem! Likeledes ærbare kvinner blant de troende, og ærbare kvinner blant dem som mottok skriften før dere, såfremt dere gir dem deres gave som ærbare menn, ikke som slike som driver utukt eller holder elskerinner. Den som fornekter troen, hans gjerninger er intet verdt. I det hinsidige liv er han blant taperne.'),
(675, 5, 6, 'Dere som tror, når dere stiller opp til bønn, vask ansiktet og hendene opp til albuen. Tørk over hodet, og vask føttene opp til anklene. Hvis dere er urene, så foreta renselse, er dere syke, eller på reise, eller kommer fra avtredet, eller fra samkvem med kvinner, og dere ikke finner vann, så ta fin sand og gni ansiktet og hendene med den. Gud ønsker ikke å pålegge dere en byrde, men Han ønsker å gjøre dere rene og fullbyrde Sin nåde mot dere, så dere må bli takknemlige.'),
(676, 5, 7, 'Kom i hu Guds nåde mot dere, og den pakt som Han sluttet med dere, da dere svarte: «Vi har hørt, og vi adlyder.» Frykt Gud, Gud vet hva som bor i hjertene.'),
(677, 5, 8, 'Dere som tror, stå fast ved det rette, som vitner for Gud, og må ikke motviljen mot visse mennesker forlede dere til ikke å være rettferdige. Vis rettferdighet, dette er gudsfrykten nærmest. Frykt Gud, Gud er vel underrettet om det dere gjør.'),
(678, 5, 9, 'Gud har lovet dem som tror og lever rettskaffent, at de skal få tilgivelse og en stor lønn.'),
(679, 5, 10, 'Men de som er vantro og forkaster Vårt ord, disse er helvetes beboere.'),
(680, 5, 11, 'Dere som tror, kom i hu Guds nåde mot dere, da et folk ønsket å legge hånd på dere, og Han holdt deres hender tilbake. Så frykt Gud, på Gud må de troende forlate seg.'),
(681, 5, 12, 'Gud sluttet en pakt med Israels barn. Vi lot tolv høvdinger fremstå blant dem, og Gud sa: «Jeg er med dere! Om dere forretter bønnen og gir det rituelle bidrag, tror på Mine sendebud og gir dem støtte, og gir Gud et vakkert lån, vil Jeg visselig stryke ut deres misgjerninger og føre dere inn i paradisets haver, hvor bekker strømmer. Den som etter dette viser vantro, har veket av fra den rette vei!'),
(682, 5, 13, 'Fordi de brøt sin pakt, har Vi forbannet dem og forherdet deres hjerter, så de forvrir ordene ut av sin sammenheng, og har glemt en del av sin formaning. Du vil stadig oppdage falskhet fra dem, unntatt noen få. Men forlat dem og tilgi. Gud elsker dem som gjør vel!'),
(683, 5, 14, 'Vi sluttet også en pakt med dem som sier: «Vi er kristne.» Men de har glemt en del av sin formaning. Og så har vi fremkalt fiendskap og hat blant dem til oppstandelsens dag. Gud vil fortelle dem hva de har stelt i stand.'),
(684, 5, 15, 'Dere skriftens folk! Vårt sendebud er nå kommet til dere for å klargjøre meget av det dere har skjult av skriften, og stryke over meget. Et lys er kommet til dere fra Gud og skriftens klare ord,'),
(685, 5, 16, 'hvorved Gud leder dem som søker Hans velbehag på fredens veier. Han fører dem fra mørket til lyset ved Sin bestemmelse og leder dem på den rette vei.'),
(686, 5, 17, 'De er vantro som sier: «Gud er Kristus, Marias sønn.» Si: «Hvem kan vel utrette det ringeste mot Gud, om Han ønsket å tilintetgjøre Kristus, Marias sønn, hans mor, og alle som er på jorden?» Guds er herredømmet over himlene og på jord og alt som mellom dem er. Han skaper det Han vil! Gud evner alle ting!'),
(687, 5, 18, 'Jødene og de kristne sier: «Vi er Guds barn som Han elsker.» Si: «Hvorfor straffer Han dere da for deres synd? Nei, dere er bare alminnelige mennesker som Han har skapt.» Han tilgir dem Han bestemmer, og straffer dem Han bestemmer. Guds er herredømmet over himlene og jorden og alt som mellom dem er. Hos Ham er reisen slutt.'),
(688, 5, 19, 'Dere skriftens folk! Vårt sendebud er nå kommet til dere for etter et profet-mellomrom å gi dere klar beskjed, slik at dere ikke skal kunne si: «Ingen budbringer med godt budskap eller en advarer er kommet til oss!» Men nå er det kommet en budbringer med godt budskap, og en advarer! Gud evner alle ting!'),
(689, 5, 20, 'En gang sa Moses til sitt folk: «Mitt folk, kom i hu Guds godhet mot dere, da Han oppreiste profeter blant dere, gjorde dere til herskere og gav dere det Han ikke hadde gitt noen i hele verden.'),
(690, 5, 21, 'Mitt folk, tred inn i Det hellige land, som Gud har bestemt for dere! Snu ikke om og dra tilbake, for da vil dere vende om som tapere!»'),
(691, 5, 22, 'De svarte: «Moses, det er et sterkt og stridbart folk i landet. Vi vil aldri komme inn i det før de drar ut. Så hvis de drar ut, vil vi dra inn.»'),
(692, 5, 23, 'Da sa to menn av dem som fryktet Gud og som Gud hadde vist nåde: «Dra mot dem gjennom porten! Når dere er kommet inn, vil dere seire! Forlat dere på Gud såfremt dere er troende!»'),
(693, 5, 24, 'Men de sa: «Moses, vi vil aldri komme inn så lenge de andre er der! Gå du, og din Herre, og kjemp! Vi blir sittende her!»'),
(694, 5, 25, 'Så sa Moses: «Herre, jeg befaler ikke over andre enn meg selv og min bror, så gjør skille mellom oss og de syndige folk!»'),
(695, 5, 26, 'Gud sa: «Så skal landet være dem forholdt i førti år, og de skal streife om på jorden. Fortvil du ikke over det syndige folk!»'),
(696, 5, 27, 'Fremles for dem sannferdig beretningen om Adams to sønner da de brakte offer, og det ble mottatt fra den ene, men ikke fra den andre. Denne sa: «Jeg skal sannelig slå deg i hjel!» Og den andre svarte: «Gud tar bare imot fra de gudfryktige!'),
(697, 5, 28, 'Hvis du løfter din hånd mot meg for å drepe meg, så løfter ikke jeg min hånd mot deg! For jeg frykter Gud, all verdens Herre.'),
(698, 5, 29, 'Jeg ønsker at du må bære min synd i tillegg til din egen, og bli av Ildens folk. Det er illgjerningsmenns lønn!»'),
(699, 5, 30, 'Men han ble drevet til å drepe sin bror. Så han drepte ham og ble en av taperne.'),
(700, 5, 31, 'Derpå sendte Gud en ravn som sparket i jorden, for å vise ham hvordan han skulle skjule ondskapen, sin brors lik. Han sa: «Ve meg, greier jeg ikke å være som denne ravn så jeg kan skjule ondskapen, min brors lik?» Og han angret.'),
(701, 5, 32, 'Derfor har vi pålagt Israels barn: Den som dreper et menneske, uten at det gjelder blodhevn eller straff for forbrytelse, skal anses som hadde han drept hele menneskeheten. Den som redder et menneske, skal anses som hadde han reddet hele menneskeheten. Våre sendebud kom til dem med klar beskjed, men etter dette har mange av dem vist lettsindighet.'),
(702, 5, 33, 'Deres lønn som bekjemper Gud og Hans sendebud og farter omkring og stifter ufred på jorden, skal være at de drepes eller korsfestes, at hender og føtter avhugges kryssvis, eller at de forvises fra landet. Dette vil være en skam i denne verden. I det hinsidige venter en svær straff.'),
(703, 5, 34, 'Dette, med mindre de angrer seg før dere har dem i deres makt. Vit at Gud er tilgivende, nåderik.'),
(704, 5, 35, 'Dere som tror, frykt Gud, søk hen til Ham så godt dere kan, og gjør innsats for Hans sak, så det må gå dere godt!'),
(705, 5, 36, 'Om de vantro eide alt som på jorden er og like meget til, for å løskjøpe seg fra straffen på oppstandelsens dag, så vil det ikke bli godtatt. Dem venter en smertelig straff.'),
(706, 5, 37, 'De vil ønske å komme seg ut av Ilden, men de kan ikke komme ut. Dem venter en straff som varer.'),
(707, 5, 38, 'Tyven, mann som kvinne, hugg av deres hender, som en gjengjeldelse de har fortjent, og som en preventiv straff fra Gud. Gud er mektig, vis.'),
(708, 5, 39, 'Men om noen omvender seg etter sin synd, og forbedrer seg, så vil Gud vende seg mot ham i miskunn. Gud er tilgivende, nåderik.'),
(709, 5, 40, 'Vet du da ikke at Gud alene tilhører herredømmet over himmel og jord? Han straffer dem Han bestemmer, og tilgir dem Han bestemmer, Gud evner alle ting!'),
(710, 5, 41, 'Du Sendebud, la ikke dem som kappes i vantro, bedrøve deg, de som sier: «Vi tror,» med sin munn, mens de ikke tror i sine hjerter. Blant jødene finnes slike som lytter ivrig til løgn, lytter til folk utenfor din krets, vrir ordene ut av sin sammenheng, og sier: «Hvis dette blir forkynt dere, så ta imot. Men hvis ikke, pass på!» Om Gud vil sette noen på prøve, kan du intet utrette for ham overfor Gud. Det er de hvis hjerter Gud ikke vil rense, og dem venter skam i denne verden, og en svær straff i det hinsidige.'),
(711, 5, 42, 'De som lytter til løgn, og tilegner seg det urettmessige, hvis de henvender seg til deg, så sitt til doms for dem eller avslå. Om du avslår, vil de aldri kunne skade deg det ringeste. Men hvis du dømmer, så døm med rettferd! Gud elsker dem som iakttar rettferd!'),
(712, 5, 43, 'Men hvordan skulle de vel gjøre deg til dommer, når de har loven med Guds bestemmelser? Men så etter dette vender de seg bort. Disse kan ikke kalles troende.'),
(713, 5, 44, 'Vi har åpenbart loven, med ledelse og lys, hvorved profetene som hadde stilt seg i Guds tjeneste dømte for jødene. Likeledes rabbinerne og de skriftlærde, ved det de var gitt å bevare av Guds bok, og som de kunne bevitne. Frykt ikke menneskene, men frykt Meg! Tusk ikke bort Mitt ord for en billig penge! De som ikke dømmer etter det som Gud har åpenbart, de er i sannhet vantro!'),
(714, 5, 45, 'Deri har Vi foreskrevet for dem: «Liv for liv, øye for øye, nese for nese, øre for øre, tann for tann, og for sår gjengjeldelse.» Men den som ettergir som et offer, kan deri finne soning. De som ikke dømmer ved det som Gud har åpenbart, de er i sannhet illgjerningsmenn.'),
(715, 5, 46, 'Vi lot Jesus, Marias sønn, følge i profetenes spor, for å stadfeste loven som var før ham. Og Vi gav ham evangeliet med ledelse og lys for å stadfeste det som forelå før det i loven, og som ledelse og formaning for de gudfryktige.'),
(716, 5, 47, 'Måtte evangeliets folk dømme ved det Gud deri har åpenbart. De som ikke dømmer ved det som Gud har åpenbart, de er i sannhet ugudelige!'),
(717, 5, 48, 'Vi har åpenbart for deg skriften med sannheten, for å stadfeste skriften som tidligere forelå, og til bekreftelse. Så døm mellom folk ved det Gud har åpenbart, og følg ikke deres forestillinger til fortrengsel for den sannhet som du har mottatt. Hver og en har Vi gitt en norm og en praksis! Om Gud hadde villet, kunne Han gjort dere til et samlet trossamfunn, men Han ønsket å stille dere på prøve ved det Han har gitt dere. Så kappes i å gjøre det gode! Til Gud skal dere alle vende tilbake. Han vil redegjøre for det dere var uenige om.'),
(718, 5, 49, 'Så døm mellom dem ved det Gud har åpenbart og følg ikke deres forestillinger, men vær på vakt mot dem, at de ikke frister deg bort fra noe av det Gud har åpenbart for deg. Snur de ryggen til, så vit at Gud bare ønsker å ramme dem for noen av deres synder! Folk flest er i sannhet ugudelige!'),
(719, 5, 50, 'Ønsker de seg da hedenskapets rettsordning? Hvem er vel bedre enn Gud til å treffe avgjørelser for folk som har fast tro?'),
(720, 5, 51, 'Dere som tror, slutt dere ikke til jøder og kristne! De henger sammen. Den som slutter seg til dem, blir en av dem. Gud leder ikke urettferdige!'),
(721, 5, 52, 'Du ser at de med sykdom i hjertet iler til dem, idet de sier: «Vi frykter at et omslag vil ramme oss.» Men kanskje Gud gir dere seier eller et tegn fra Seg, og da vil de angre det de holdt hemmelig i sitt bryst.'),
(722, 5, 53, 'Og de troende vil si: «Er dette de som svor ved Gud de dyreste eder, at de skulle stå ved deres side? Deres gjerninger er intet verdt, de har trukket det korteste strå.»'),
(723, 5, 54, 'Dere som tror om noen faller fra i religionen, så vil Gud sende andre som Han elsker og som elsker Ham, snille og omgjengelige mot de troende, faste og harde mot de vantro, som vil kjempe for Guds sak uten å frykte noen kritikk. Dette er Guds godhet, som Han gir hvem Han vil. Gud er storslagen, Han vet alt!'),
(724, 5, 55, 'Venner dere kan satse på, er kun Gud og Hans sendebud, samt de troende som forretter bønnen og gir det rituelle bidrag, idet de bøyer seg for Gud.'),
(725, 5, 56, 'Og de som slutter seg til Gud og Hans sendebud og de troende, se, Guds parti er de som seirer.'),
(726, 5, 57, 'Dere som tror, slutt dere ikke til slike av dem som fikk skriften før dere, eller av de vantro, som driver ap og spill med deres religion. Frykt Gud, hvis dere er troende.'),
(727, 5, 58, 'Når dere kaller til bønn, driver de ap og spill. Dette fordi de er mennesker uten forståelse.'),
(728, 5, 59, 'Si: «Dere skriftens folk, laster dere oss bare for at vi tror på Gud, og på det som er åpenbart for oss, og tidligere? De fleste av dere er i sannhet ugudelige!»'),
(729, 5, 60, 'Skal jeg fortelle om en gjengjeldelse verre enn som så fra Gud? Det gjelder dem som var under Guds forbannelse og mishag, som Han gjorde til aper og svin og avgudsdyrkere! Disse var verre stilt, og mer på villspor fra den rette vei.'),
(730, 5, 61, 'Når de kommer til dere, sier de: «Vi tror.» Men de kom inn i vantro, og de hadde den med da de gikk ut. Gud vet godt hva de skjuler.'),
(731, 5, 62, 'Du ser mange av dem kappes i synd og fiendskap og urettmessig tilegnelse. Sannelig, tarvelig er det de gjør!'),
(732, 5, 63, 'Hvorfor forbyr ikke rabbinerne og de skriftlærde deres syndige tale og urettmessige tilegnelse? Sannelig, tarvelig er det de bedriver!'),
(733, 5, 64, 'Jødene sier: «Guds hånd er lukket.» Nei, deres hender er lukkede, og forbannelse hviler over dem for det de sier. For Hans hender er vidåpne, og Han gir slik Han vil! Det Herren har åpenbart deg, vil øke oppsetsighet og vantro hos mange. Og vi har satt fiendskap og hat mellom dem til oppstandelsens dag. Men så ofte de tenner krigens flamme, så slukker Gud den. De tilstreber vold og ufred på jorden, men Gud liker ikke urostiftere.'),
(734, 5, 65, 'Men om skriftfolket hadde trodd og vist gudsfrykt, ville Vi utvisket deres misgjerninger, og ført dem inn i lykksalighetens haver.'),
(735, 5, 66, 'Hadde de overholdt loven og evangeliet og det som er åpenbart dem fra Herren, ville de fått spise av alt både over og under jorden. Det finnes blant dem en gruppe som viser måtehold, men de fleste av dem gjør det som er forkastelig.'),
(736, 5, 67, 'Du Sendebud, forkynn det som er åpenbart deg fra Herren! Om du ikke gjør det, har du ikke båret frem Hans budskap. Gud vil beskytte deg mot menneskene. Gud veileder ikke vantro folk.'),
(737, 5, 68, 'Si: «Dere som har mottatt skriften, dere har intet å fare med før dere overholder loven og evangeliet, og det som er åpenbart dere fra Herren!» Det som er åpenbart deg fra Herren, vil nok bare øke oppsetsighet og vantro hos mange, men fortvil ikke over det vantro folk!'),
(738, 5, 69, 'De som tror, og jøder, sabeere og kristne, de som tror på Gud og på dommens dag og gjør det som er rett og godt, over dem skal ingen frykt hvile, ei heller sorg.'),
(739, 5, 70, 'Vi inngikk pakt med Israels barn, og Vi sendte dem sendebud. Men hver gang et sendebud brakte noe som ikke passet dem, holdt de noen for løgnere, og drepte andre.'),
(740, 5, 71, 'De trodde ikke det ville bli noen prøvelse, blinde og døve! Så vendte Gud seg mot dem i nåde, men på nytt var mange av dem blinde og døve. Gud følger med i hva de gjør!'),
(741, 5, 72, 'Vantro er de som sier: «Gud, det er Kristus, Marias sønn!» Men Kristus sa: «Dere Israels barn, tilbe Gud, min Herre og deres Herre.» Sannelig, for dem som gir noen plass ved Guds side, har Gud stengt paradiset, og deres herberge blir Ilden! For illgjerningsmenn er det ingen hjelpere.'),
(742, 5, 73, 'Vantro er de som sier: «Gud er den tredje av tre.» Det er ingen gud unntatt én Gud! Hvis de ikke opphører med det de sier, så vil en smertelig straff ramme de vantro blant dem.'),
(743, 5, 74, 'Skulle de ikke heller vende seg til Gud og be om Hans tilgivelse? Gud er tilgivende, nåderik.'),
(744, 5, 75, 'Kristus, Marias sønn, var bare et sendebud! Sendebud var vandret bort før ham. Hans mor var en rettferdig kvinne. De tok begge til seg føde som vanlige mennesker. Se, hvordan Vi klargjør ordet for dem! Se, hvor forvridde de er!'),
(745, 5, 76, 'Si: «Vil dere da istedenfor Gud tilbe noe som verken kan skade eller nytte dere? Gud er den Hørende, den Allvitende.»'),
(746, 5, 77, 'Si: «Dere skriftens folk, overdriv ikke i deres religion hinsides sannheten. Følg ikke deres forestillinger som før har fart vill, og har ført mange vill, og som er langt borte fra den rette vei!»'),
(747, 5, 78, 'De av Israels barn som var vantro, ble forbannet ved Davids og Jesu, Marias sønns lepper. Dette på grunn av deres ulydighet og deres overtredelser.'),
(748, 5, 79, 'De sluttet ikke med det forkastelige som de gjorde. Ille var det de bedrev!'),
(749, 5, 80, 'Du ser mange av dem slutte seg til vantro. Dårlig er det de har sendt i forveien til sitt regnskap, så Guds mishag vil ramme dem, og i straffen skal de være og bli.'),
(750, 5, 81, 'Om de hadde trodd på Gud, og profeten, og det som er åpenbart ham, ville de ikke sluttet seg til dem. Men mange av dem er ugudelige.'),
(751, 5, 82, 'Du vil sikkert finne at jøder og hedninger er de mest fiendtligsinnede av alle overfor de troende. Og du vil finne at de som sier: «Vi er kristne,» er nærmest til å like de troende. Dette fordi det er prester og munker blant dem, og fordi de ikke er hovmodige.'),
(752, 5, 83, 'Når de hører det som er åpenbart for Sendebudet, ser du deres øyne fylles av tårer over det de erkjenner av sannhet. De sier: «Vår Herre, vi tror! Skriv oss opp blant dem som avgir vitnesbyrd!'),
(753, 5, 84, 'Skulle vi ikke tro på Gud og sannheten som har nådd oss, og håpe at vår Herre vil føre oss inn sammen med de rettferdige?»'),
(754, 5, 85, 'Og Gud vil belønne dem for det de sier med paradisets haver, hvor bekker sildrer. Her skal de være og bli. Dette er lønnen for dem som gjør det gode.'),
(755, 5, 86, 'Men de som er vantro og fornekter Vårt ord, de blir helvetes beboere!'),
(756, 5, 87, 'Dere som tror, forby ikke gode matemner som Gud har tillatt dere, men gå ikke over grensen. Gud liker ikke folk som går over grensen!'),
(757, 5, 88, 'Spis av det Gud har gitt dere i underhold, det tillatte og gode. Frykt Gud, Ham dere tror på!'),
(758, 5, 89, 'Gud tar ikke fatt for et uoverlagt ord i deres eder, men Han vil ta dere fatt for det dere påtar dere ved ed! Soning for mislighold er bespisning av ti fattige med vanlig familiekost, eller deres bekledning, eller frigjøring av en slave. Den som ikke finner utvei til dette, skal faste i tre dager. Dette er soning for eder dere har avlagt. Men hold deres eder! Slik klargjør Gud Sitt ord for dere, så dere må vise takknemlighet!'),
(759, 5, 90, 'Dere som tror, alkohol, hasard, offersteiner og spådomspiler er en gru og et Satans verk. Unngå det, så det må gå dere godt!'),
(760, 5, 91, 'Ved alkohol og hasard ønsker Satan bare å sette fiendskap og hat mellom dere, og holde dere borte fra tanken på Gud, og fra bønnen. Skulle dere ikke da la det være?'),
(761, 5, 92, 'Adlyd Gud, og adlyd Sendebudet, og vær på vakt! Men om dere snur dere vekk, så vit at det kun påligger vårt sendebud å gi en klar forkynnelse.'),
(762, 5, 93, 'De som tror og handler vel, rammes ikke av skyld for det de spiser, såfremt de viser gudsfrykt, tror og lever rettskaffent, viser gudsfrykt og tror, viser gudsfrykt og gjør det gode. Gud elsker dem som gjør det gode!'),
(763, 5, 94, 'Dere som tror, Gud vil nok sette dere på prøve ved vilt som dere på fredet sted kan felle med bue eller lanse, slik at Gud kan vite hvem som frykter Ham i det skjulte. Den som går over grensen, ham venter en smertelig straff.'),
(764, 5, 95, 'Dere som tror, drep intet vilt når dere er sakralisert som pilegrimer. Om noen med overlegg gjør dette, så er motytelsen tamkveg motsvarende det han har drept etter to gode menns skjønn, og som bæres frem ved Ka’aba, eller, som sonoffer, å bespise de fattige, eller faste tilsvarende dette. Dette for at han kan smake alvoret i det han har gjort. Gud stryker over det som før er gjort, men den som heretter går over grensen, ham vil Guds hevn ramme. Gud er mektig, og Han innehar hevnen!'),
(765, 5, 96, 'Tillatt for dere er havets fangst og å spise den som næring for dere og de reisende, men jakt på land er forbudt så lenge dere er sakralisert som pilegrimer. Frykt Gud, hos Ham skal dere samles.'),
(766, 5, 97, 'Gud har innrettet Ka’aba, det Hellige hus, som reisemål for menneskene, og likeledes den Hellige måned, og ofrene. Dette for at dere skal vite at Gud kjenner til alt i himmel og på jord, at Gud vet om alt!'),
(767, 5, 98, 'Vit at Gud er streng med straff og at Gud er tilgivende, nåderik.'),
(768, 5, 99, 'Sendebudet påligger bare forkynnelsen. Gud vet om det dere tilkjennegir, og det dere skjuler.'),
(769, 5, 100, 'Si: «Dårlig og godt er ikke likeverdig, selv om selve kvantiteten av det dårlige gjør det antagelig for deg. Vis gudsfrykt, dere som har forstand, så det må gå dere godt.'),
(770, 5, 101, 'Dere som tror, spør ikke om ting som bare ville plage dere, om de ble avslørt for dere. Men om dere spør mens Koranen åpenbares, vil de avsløres for dere. Gud unnskylder dette. Gud er tilgivende, mild.'),
(771, 5, 102, 'Folk før dere har stilt slike spørsmål, men derved ble de ført til vantro.'),
(772, 5, 103, 'Gud har ingenting forordnet om hedenske skikker så som Bahira, Sa’iba, Wasila, Hami. De vantro har oppdiktet løgn mot Gud, og de fleste av dem forstår ingenting.'),
(773, 5, 104, 'Sier man til dem: «Kom til det Gud har åpenbart! Kom til Sendebudet!» Så sier de: «Våre fedres tradisjoner er nok for oss.» Og dette selv om deres fedre intet forstod, og ikke var på rett vei.'),
(774, 5, 105, 'Dere som tror, ta vare på dere selv! Den villfarende kan ikke skade dere når dere er på rett vei. Til Gud skal dere alle vende tilbake. Og Han vil fortelle dere hva dere bedrev.'),
(775, 5, 106, 'Dere som tror når noen av dere ligger for døden, og tiden for testamentariske disposisjoner er kommet, da skal det sørges for vitner, to rettsindige menn blant dere, eller to andre utenfra hvis dere er på reise, og dødens begivenhet rammer dere. Dere skal holde dem tilbake etter bønnen og la dem sverge ved Gud, om dere er i tvil: «Vi har ingen økonomisk interesse i dette, heller ikke noen som står oss nær. Vi vil ikke underslå vitnesbyrdet, gitt for Guds åsyn. I så fall ville vi være syndere.»'),
(776, 5, 107, 'Hvis man kommer undervær med at de to har gjort seg skyldige i en synd, så skal to andre stå frem i deres sted, to av deres gruppe som de to første har gjort seg skyldige mot, og de skal sverge ved Gud: «Vårt vitnesbyrd er mer vederheftig enn deres. Vi går ikke over grensen. I så fall ville vi være illgjerningsmenn.»'),
(777, 5, 108, 'Denne ordning er den mest nærliggende for at folk skal avgi korrekt vitnesbyrd, idet de vil frykte at deres ed vil bli gjendrevet. Frykt Gud og adlyd! Gud leder ikke et ugudelig folk!'),
(778, 5, 109, 'Den dag da Gud samler alle sendebud vil Han si: «Hvilket svar fikk dere?» Og de vil svare: «Vi vet ikke. Bare Du kjenner det skjulte.»'),
(779, 5, 110, 'En gang sa Gud: «Jesus, Marias sønn, kom i hu Min nåde mot deg og din mor, da Jeg styrket deg med Den Hellige Ånd, så du talte til folk både i vuggen og i moden alder, da Jeg lærte deg skriften og visdommen, loven og evangeliet, da du laget som en fugl av leire med Mitt bifall, blåste på den, og den ble en fugl med Mitt bifall, da du helbredet den blinde og den spedalske med Mitt bifall, da du med Mitt bifall oppvekket de døde, da Jeg holdt Israels barn i tømme, den gang du kom til dem med klar beskjed, mens de vantro blant dem sa: ’Dette er intet annet enn åpenbar magi!’»'),
(780, 5, 111, 'Eller da Jeg inspirerte disiplene: «Tro på Meg og Mitt sendebud,» og de svarte: «Vi tror! Bevitne at vi har stilt oss i Herrens tjeneste.»'),
(781, 5, 112, 'Eller da disiplene sa: «Jesus, Marias sønn, er din Herre i stand til å sende oss et dekket bord fra himmelen?» Jesus svarte: «Vis gudsfrykt, om dere er troende.»'),
(782, 5, 113, 'Og de sa: «Vi ønsker å spise av det, så våre hjerter må finne ro, så vi må vite at du har talt sannhet til oss, så vi kan bevitne det!»'),
(783, 5, 114, 'Jesus, Marias sønn, sa: «O Gud, vår Herre, send oss et dekket bord fra himmelen, så det må bli en høytid for oss, fra den første til den siste, og som et jærtegn fra Deg! Gi oss Du underhold! Du er den beste forsørger!»'),
(784, 5, 115, 'Gud svarte: «Jeg vil sende det ned til dere. Men den som er vantro etter dette, ham vil Jeg ramme med straff som ingen annen i hele verden!»'),
(785, 5, 116, 'En gang sier Gud: «Jesus, Marias sønn, har du sagt til menneskene: ’Ta meg og min mor til guder utenom Gud?’» Og han svarte: «Deg alene all ære! Jeg ville ikke si noe som jeg ikke har rett til. Om jeg hadde sagt det, ville Du visst det! Du kjenner det som er i meg, men jeg kjenner ikke det som er i Deg.'),
(786, 5, 117, 'Du alene kjenner det skjulte. Jeg har bare sagt dem det Du har pålagt meg: ’Tilbe Gud, min Herre og deres Herre.’ Jeg var vitne overfor dem, så lenge jeg var hos dem, men etter at Du har kalt meg til Deg, har Du selv vært oppsynsmann over dem. Du er vitne til alle ting!'),
(787, 5, 118, 'Om Du straffer dem, se, de er Dine tjenere. Om Du tilgir dem, se, Du er mektig, vis.»'),
(788, 5, 119, 'Gud sier: «Dette er en dag da de sannferdiges sannferdighet skal gagne dem! Dem venter paradisets haver, hvor bekker sildrer. Der skal de være og bli! Guds velbehag er med dem, og de finner behag i Ham. Dette er den store seier!»'),
(789, 5, 120, 'Guds er herredømmet over himmel og jord og alt som i dem er! Han evner alle ting!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Lovet være Gud, som har skapt himlene og jord, og laget mørke og lys. Etter alt dette gir de vantro sin Herre likemenn.'),
(791, 6, 2, 'Han er det som skapte dere av leire, og så bestemte en tidsfrist. Hos Ham finnes en bestemt frist. Etter alt dette tviler dere!'),
(792, 6, 3, 'Han er Gud i himlene og på jord. Han kjenner deres hemmeligheter, og det dere bærer åpent frem. Han vet hva dere gjør dere fortjent til!'),
(793, 6, 4, 'Men det kommer ikke et eneste av Guds tegn til dem, uten at de vender seg bort.'),
(794, 6, 5, 'De kalte sannheten for løgn da den kom til dem. Men de skal få høre mer om det de drev ap med!'),
(795, 6, 6, 'Har de da ikke sett hvor mange generasjoner Vi har latt gå til grunne før dem? Generasjoner, som Vi etablerte i makt på jorden som Vi aldri lot bli dere til del? Vi sendte over dem rikelig regn, og lot bekker strømme fremfor dem! Men så lot Vi dem gå til grunne for deres synders skyld, og lot en annen generasjon stå frem etter dem.'),
(796, 6, 7, 'Selv om Vi sendte deg et skriftstykke på pergament, og de kunne berøre det med sine hender, så ville de vantro si: «Dette er intet annet enn åpenbar magi.»'),
(797, 6, 8, 'Og de sier: «Hvorfor er ikke en engel sendt ned til ham?» Men hadde Vi sendt en engel, ville saken vært avgjort, og så ville de ikke fått noen utsettelse.'),
(798, 6, 9, 'Og selv om Vi hadde latt Sendebudet være en engel, ville Vi iført ham manns skikkelse og virkelig forvirret dem i deres egen forvirring.'),
(799, 6, 10, 'Det er blitt drevet ap med sendebud før deg, men det de hånet, har satt dem som hånlo til veggs.'),
(800, 6, 11, 'Si: «Dra omkring på jorden og se hva enden ble for dem som kalte sannhet for løgn!»'),
(801, 6, 12, 'Si: «Hvem tilhører alt som er i himmel og på jord?» Si: «Det tilhører Gud.» Han har foreskrevet seg selv barmhjertighet! Han vil samle dere alle på oppstandelsens dag, derom er ingen tvil. Men de som kaster seg selv bort, de vil ikke tro.'),
(802, 6, 13, 'Ham tilhører alt som bor i natten og dagen, Han er den Hørende, den Allvitende.'),
(803, 6, 14, 'Si: «Skulle jeg vel slutte meg til en annen enn Gud? Himlenes og jordens skaper? Han som bespiser, og selv ikke bespises?» Si: «Jeg er blitt befalt å være den første som hengir seg til Gud: ’Du skal ikke være en avgudsdyrker!’»'),
(804, 6, 15, 'Si: «Jeg frykter at en svær dags straff vil ramme meg, om jeg viser Herren ulydighet.»'),
(805, 6, 16, 'Den som holdes unna straffen på denne dag, ham har Gud vist nåde. Dette er den klare seier!'),
(806, 6, 17, 'Og hjemsøker Gud deg med en prøvelse, kan ingen avvende den, unntatt Han.'),
(807, 6, 18, 'Hvis Han lar godt overgå deg, så evner Han alle ting. Han har overhøyhet over sine tjenere. Han er den Vise, den Innsiktsfulle.'),
(808, 6, 19, 'Si: «Hva er det største vitnesbyrd?» Si: «Gud er vitne mellom meg og dere! Denne Koran er åpenbart for meg, slik at jeg dermed kan advare dere og dem den måtte nå frem til. Vil dere virkelig bekrefte at det er guder sammen med Gud?» Si: «Jeg bekrefter det ikke!» Si: «Han er kun én Gud! Jeg vil intet ha å gjøre med deres medguder!»'),
(809, 6, 20, 'De som vi har gitt skriften, kjenner den som sine egne barn. Men de som kaster seg selv bort, de vil ikke tro!'),
(810, 6, 21, 'Hvem gjør vel større urett enn den som pådikter Gud løgn? Eller fornekter Hans ord? Dem som gjør urett, vil det ikke gå godt!'),
(811, 6, 22, 'Den dag når Vi samler alle, vil Vi si til dem som setter opp medguder: «Hvor er nå medgudene deres som dere gikk inn for?»'),
(812, 6, 23, 'De har ingen annen utvei enn å si: «Ved Gud, vår Herre, vi hadde ingen medguder.»'),
(813, 6, 24, 'Se hvordan de lyver mot seg selv, og hvordan alt de har oppdiktet lar dem i stikken.'),
(814, 6, 25, 'Det finnes også de blant dem som hører på deg, men vi har lagt et slør over deres hjerter så de intet forstår, og tunghørthet i deres ører, så selv om de fikk se alle mulige jærtegn, så ville de ikke tro dem. Så når de vantro kommer for å diskutere med deg, sier de: «Dette er intet annet enn gamle fabler!»'),
(815, 6, 26, 'Og de forbyr det å lytte og holder seg borte fra det. Det er bare seg selv de tilintetgjør, men dette oppfatter de ikke.'),
(816, 6, 27, 'Om du kunne se når de stilles frem for Ilden! Da vil de si: «Om vi bare kunne bli sendt tilbake til jorden! Da ville vi ikke fornekte Herrens ord, så vi kunne bli blant de troende!»'),
(817, 6, 28, 'Nei, det de før la skjul på, kommer nå klart til syne for dem. Men om de ble sendt tilbake, ville de tre frem med forbudte ting. De er i sannhet løgnere!'),
(818, 6, 29, 'De sier også: «Vårt liv er kun jordelivet. Vi blir ikke gjenoppvekket!»'),
(819, 6, 30, 'Om du kunne se når de stilles frem for Herren! Og Han vil si: «Er ikke dette sannhet?» De svarer: «Jo, ved vår Herre.» Han sier: «Smak så straffen for deres vantro!»'),
(820, 6, 31, 'De er tapere som kaller møtet med Gud for løgn inntil timen plutselig kommer over dem, og de utbryter: «Trøste oss for at vi overså den!» De må bære sine byrder på sin egen rygg. Det blir i sannhet triste saker de bærer!'),
(821, 6, 32, 'Jordelivet er bare lek og tidsfordriv. Det hinsidige livs hus er bedre for dem som viser gudsfrykt. Vil dere da ikke forstå?'),
(822, 6, 33, 'Vi kjenner nok til at det de sier, gjør deg bedrøvet. Men det er ikke du som blir beskyldt for løgn, men det er Guds ord som de urettferdige fornekter.'),
(823, 6, 34, 'Sendebud før deg er blitt beskyldt for løgn, men de bar med tålmod løgnbeskyldningene og sine plager til Vår hjelp nådde dem. Ingen kan endre Guds ord. Du har nå fått beretningen om sendebudene.'),
(824, 6, 35, 'Blir deres avstandtagen for tung for deg å bære, så finn deg et hull i jorden eller en stige til himmelen, om du kan, så har du et tegn å vise dem. Hadde Gud villet, kunne Han samlet dem alle om ledelsen! Vær ikke dum!'),
(825, 6, 36, 'Men velge å slutte seg til kan bare de som lytter. De døde vil Gud oppvekke, så føres de hjem til Ham.'),
(826, 6, 37, 'De sier også: «Hvorfor er det ikke sendt ham et jærtegn fra hans Herre?» Si: «Gud kunne visselig sende et jærtegn. Men folk flest vet ikke!»'),
(827, 6, 38, 'Det finnes ikke dyr på jorden eller fugl på vingen uten at de utgjør samfunn, som dere. Vi har ikke oversett noe i Boken. Og så samles de hos sin Herre.'),
(828, 6, 39, 'De som fornekter Vårt ord er døve og stumme og dveler i mørke. Gud lar den seile sin egen sjø som Han vil, og Han setter den på rett vei som Han vil.'),
(829, 6, 40, 'Si: «Hva mener dere? Hvis Guds straffedom, eller timen, kommer over dere, vil dere da påkalle noen annen enn Gud? Hvis dere skal snakke sant?»'),
(830, 6, 41, 'Nei, Ham vil dere påkalle, og Han vil fjerne det dere ber om, hvis Han vil, og da glemmer dere medgudene.'),
(831, 6, 42, 'Visselig, Vi sendte bud til folk før din tid, og Vi hjemsøkte dem med trengsel og nød, så de måtte vise ydmykhet.'),
(832, 6, 43, 'Men hadde de bare vist ydmykhet da Vår hardhet kom over dem? Men deres hjerter var forherdet, og Satan fikk all deres gjerning til å se pyntelig ut.'),
(833, 6, 44, 'Og så, da de glemte det de var formanet om, åpnet Vi portene til alle ting for dem inntil de gledet seg over alt de hadde fått, da tok vi dem plutselig fatt, og de ble styrtet i fortvilelse.'),
(834, 6, 45, 'Og siste rest av et folk av urettferdige ble skåret bort. Priset være Gud, all verdens Herre!'),
(835, 6, 46, 'Si: «Hva mener dere? Om Gud tar bort evnen til å høre og se og setter segl på deres hjerter, hvilken annen guddom enn Gud kan vel gi dere alt tilbake? Se, hvordan Vi snur og vender på ordet. Men så vender de seg bort.'),
(836, 6, 47, 'Si: «Hva mener dere? Hvis Guds straffedom overgikk dere, plutselig, eller klart og tydelig, vil vel noen bli tilintetgjort unntatt urettferdige folk?»'),
(837, 6, 48, 'Vi sendte budbringerne med det glade budskap og med advarsler. De som tror og forbedrer seg, over dem skal ingen frykt hvile, ei heller sorg.'),
(838, 6, 49, 'Men de som fornekter Vårt ord, dem skal straffen ramme for deres ugudelighet.'),
(839, 6, 50, 'Si: «Jeg sier ikke til dere ’Hos meg er Guds skatter’. Jeg kjenner ikke det skjulte. Jeg sier heller ikke ’Jeg er en engel’. Jeg følger bare det som er åpenbart for meg.» Si: «Er vel de blinde og den seende like? Vil dere da ikke tenke etter?»'),
(840, 6, 51, 'Advar med dette dem som frykter å samles hos Herren. De har utenom Ham ingen venner, og ingen som taler deres sak. Måtte de vise gudsfrykt.'),
(841, 6, 52, 'Vis ikke bort dem som påkaller Herren morgen og aften, søkende hans åsyns velbehag. Deres regnskap vedkommer deg ikke, og ditt vedkommer ikke dem. Så viser du dem bort, vil du gjøre urett.'),
(842, 6, 53, 'Således setter vi noen på prøve gjennom andre, til de sier: «Er det disse som Gud har vist godhet blant oss?» Vet ikke Gud om de takknemlige?'),
(843, 6, 54, 'Når de som tror på Vårt ord kommer til deg, så si: «Fred være med dere! Herren har foreskrevet seg selv barmhjertighet. Hvis én av dere handler galt i uvitenhet, og deretter vender om og forbedrer seg, så er Han tilgivende, nåderik.»'),
(844, 6, 55, 'Således forklarer Vi ordet, så syndernes vei må stå frem klart.'),
(845, 6, 56, 'Si: «Det er meg forbudt å tilbe det dere påkaller utenom Gud.» Si: «Jeg vil ikke følge deres forestillinger! I så fall var jeg på villspor, og ikke av dem på rett vei.»'),
(846, 6, 57, 'Si: «Jeg står fast på en klar beskjed fra min Herre, som dere fornekter. Jeg har ikke i min hånd den dommedag som dere ønsker påskyndet. Avgjørelsen ligger hos Gud alene. Han forteller sannheten, og Han er den beste til å treffe avgjørelse.»'),
(847, 6, 58, 'Si: «Hadde jeg i min hånd det dere ønsker påskyndet, sannelig, da var saken avgjort mellom dere og meg! Gud kjenner vel til de urettferdige.»'),
(848, 6, 59, 'Hos Ham beror det uutgrunneliges nøkler. Ingen annen enn Han kjenner til dem. Han vet hva som er på land og i hav. Ikke et blad faller uten at Han vet det. Det finnes intet korn i jordens mørke, intet friskt og intet tørt, unntatt ifølge en klar bok.'),
(849, 6, 60, 'Han kaller dere til seg om natten, og Han vet hva dere har bedrevet om dagen. Så lar Han dere våkne en ny dag, slik at den bestemte livsfrist fullbyrdes. Derpå vil dere vende hjem til Ham, og så vil Han la dere få vite hva dere har gjort.'),
(850, 6, 61, 'Han har overhøyhet over Sine tjenere. Han sender voktere over dere, også når døden kommer til en av dere, tar Våre utsendte ham bort, og de unnlater ikke sin oppgave.'),
(851, 6, 62, 'Så bringes de frem for Gud, deres sanne Herre, tilhører ikke dommen Ham? Og Han er den raskeste i avregningen!'),
(852, 6, 63, 'Si: «Hvem redder dere ut av mørke på land og hav? Dere påkaller Ham ydmykt og stille, og sier: ’Hvis Han redder oss ut av dette, vil vi visselig vise takknemlighet.’»'),
(853, 6, 64, 'Si: «Det er Gud som redder dere fra dette og fra all nød. Men så gir dere Ham medguder.»'),
(854, 6, 65, 'Si: «Han kan sende over dere en straffedom, ovenfra eller nedenfra, eller forlede dere til gruppedannelser og la dere smake hverandres vold.» Se, hvordan Vi snur og vender på ordet, så de må forstå.'),
(855, 6, 66, 'Ditt folk kaller det for løgn. Men det er sannheten. Si: «Jeg har intet ansvar for dere.'),
(856, 6, 67, 'Hver melding har sin plass. Dere vil få å vite!»'),
(857, 6, 68, 'Når du ser slike som kaster seg over Vårt ord i samtale, så vend deg bort fra dem til de finner et annet samtaleemne. Skulle Satan få deg til å glemme dette, så bli ikke sittende blant de urettferdige etter at du er kommet det i hu.'),
(858, 6, 69, 'Det påligger ikke de gudfryktige å kreve dem til regnskap, men bare å formane at de må bli gudfryktige.'),
(859, 6, 70, 'La dem alene, som tar sin religion som lek og tidsfordriv, som jordelivet har forført. Forman herved, slik at ingen skal gå i fortapelsen for det han har pådratt seg. Han vil ikke ha innflytelsesrike venner, utenom Gud. Og om han byr alt i kompensasjon, blir det ikke akseptert. Disse er det som går i fortapelsen for det de har pådratt seg, fordi de var vantro.'),
(860, 6, 71, 'Si: «Skulle vi vel utenom Gud påkalle slikt som verken kan nytte eller skade oss? Skulle vi vel la oss snu tvert om etter at Gud har ledet oss? Som den som satanene forleder så han går i ørske på jorden? Skjønt han har venner som kaller ham til ledelsen: ’Kom til oss!’» Si: «Guds ledelse, den er ledelsen, og vi er pålagt å hengi oss til all verdens Herre!»'),
(861, 6, 72, 'Og: «Forrett bønnen og frykt Ham! Hos Ham vil dere alle bli samlet!»'),
(862, 6, 73, 'Han er det som har skapt himlene og jorden på alvor. Den dag Han sier «Bli», så blir det. Hans ord er sannhet. Hans er herredømmet den dag det støtes i basunen. Han kjenner det skjulte og det synlige. Han er den Vise, den Innsiktsfulle.'),
(863, 6, 74, 'En gang sa Abraham til sin far, Azar: «Tar du avgudsbilder til guder? Du og ditt folk er sannelig klart på villspor.»'),
(864, 6, 75, 'Således viste vi Abraham himlenes og jordens rike, at han skulle bli fast i troen.'),
(865, 6, 76, 'Da natten bredte seg over ham, så han en stjerne. Han sa: «Dette er min Herre.» Men da den gikk ned, sa han: «Jeg holder ikke av slike som forsvinner.»'),
(866, 6, 77, 'Da han så månen stige opp, sa han: «Dette er min Herre.» Men da den gikk ned, sa han: «Hvis Herren ikke leder meg, vil jeg forbli av de villfarende folk!»'),
(867, 6, 78, 'Da han så soloppgangen, sa han: «Dette er min Herre, denne er størst!» Da også den gikk ned, sa han: «Hør mitt folk, jeg vil ikke ha noe å gjøre med det dere setter ved Guds side.'),
(868, 6, 79, 'Jeg vender mitt ansikt mot himlenes og jordens Skaper, som en Gud-søker. Jeg er ikke av dem som har medguder.»'),
(869, 6, 80, 'Men hans folk argumenterte med ham, og han sa: «Vil dere krangle med meg om Gud? Som har gitt meg ledelse? Jeg frykter ikke det dere setter ved Hans side, bare det min Herre måtte ønske. Min Herre omfatter alt i visdom! Vil dere da ikke tenke etter?'),
(870, 6, 81, 'Hvordan skulle vel jeg kunne frykte det dere setter ved Hans side når dere ikke frykter for å sette ved Guds side slikt som Han ikke har gitt dere noen autorisasjon for?» Hvilke av de to synsmåter fortjener best å få være i fred, om dere vet?'),
(871, 6, 82, 'De som tror, og ikke formørker sin tro ved urett, dem tilkommer fred, de er under ledelsen.'),
(872, 6, 83, 'Dette er Vårt argument som Vi gav Abraham overfor sitt folk. Vi hever i grader hvem Vi vil. Herren er vis, Han vet!'),
(873, 6, 84, 'Og Vi gav ham Isak og Jakob, og Vi gav dem begge ledelse, og Vi ledet tidligere Noa, og hans etterkommere David og Salomo, Job, Josef, Moses og Aron. Slik belønner Vi dem som gjør godt.'),
(874, 6, 85, 'Likeledes Sakarias, Johannes, Jesus, Elias, – alle var de rettferdige,'),
(875, 6, 86, 'og Ismael, Elisa, Jonas og Lot, alle utmerket Vi fremfor all verden –'),
(876, 6, 87, 'og noen av deres forfedre, etterkommere og brødre. Vi utvalgte dem og ledet dem til den rette vei.'),
(877, 6, 88, 'Slik er Guds ledelse! Han leder derved hvem Han vil av sine tjenere. Men hadde de gitt Ham medguder, ville deres gjerninger vært til fånyttes.'),
(878, 6, 89, 'Disse er det som Vi gav skriften, visdommen og profetkallet. Så om deres etterkommere ikke tror på det, så har Vi nå betrodd det til et annet folk, som ikke fornekter det.'),
(879, 6, 90, 'Disse er det som er under Guds ledelse, så følg den ledelse de har! Si: «Jeg krever ingen betaling av dere! Dette er en formaning for all verden!»'),
(880, 6, 91, 'De feilvurderer Gud når de sier: «Gud har ikke åpenbart noe for noe menneske.» Si: «Hvem sendte vel skriften som Moses brakte, som lys og ledelse for menneskene? Dere har laget den til pergamentruller, noe viser dere frem, meget holder dere skjult. Dere er blitt belært om ting dere ikke visste, verken dere eller deres fedre.» Si: «Gud sendte skriften.» Og la dem så drive sin lek med ord.'),
(881, 6, 92, 'Dette er en velsignet skrift som Vi har åpenbart, som stadfester det som alt foreligger, så du må advare Mekka og dens omgivelser. De som tror på det hinsidige liv, tror på den, og de passer på sine bønner.'),
(882, 6, 93, 'Hvem begår vel større urett enn den som oppdikter løgn om Gud, eller sier: «Jeg har mottatt en åpenbaring,» når intet er åpenbart for ham, eller som sier: «Jeg skal ’åpenbare’ noe like bra som det Gud har åpenbart.» Om du kunne se de urettferdige i dødsangsten, når englene rekker frem sine hender: «Gi fra dere deres sjeler! I dag vil dere bli belønnet med en ydmykelsens straff, for det dere sa om Gud som ikke er sant, og for å ha vendt dere bort fra Hans ord i hovmod.»'),
(883, 6, 94, '«Nå er dere kommet til Oss, som enkeltindivider, slik Vi skapte dere første gang, og alt Vi overlot dere på jorden, har dere latt bak dere. Vi ser ikke sammen med dere dem som skulle ordne deres saker, som dere hevdet var Guds medeiere i dere. Båndene mellom dere er nå brutt, det dere snakket om er nå borte.»'),
(884, 6, 95, 'Det er Gud som lar kornet og kjernen gro. Han frembringer det levende av det døde. Og Han frembringer det døde av det levende. Dette er Gud! Hvordan kan dere være så forvrengte!'),
(885, 6, 96, 'Det er Han som lar morgenen gry, og som har bestemt natten til hvile, solen og månen til tidsberegning. Dette er anordningen til den Mektige, den Allvitende.'),
(886, 6, 97, 'Han er det som har satt stjernene der, for at dere skal finne vei i mørket på land og hav. Nå har Vi forklart jærtegnene for folk som har kunnskap!'),
(887, 6, 98, 'Han er det som har frembrakt dere av et eneste vesen, og så gjennom far og mor (oppholdsplass og beholder). Nå har Vi forklart Vårt ord for folk som begriper.'),
(888, 6, 99, 'Han sendte ned vann fra oven, og Vi lot derved fremstå allslags planter og grønne vekster, korn i aks, daddelpalmens dadler tett i tett, vingårder, oliven og epler, likt og ulikt. Se på frukten når den kommer frem og modnes! I dette er visselig jærtegn for folk som tror.'),
(889, 6, 100, 'Men de setter dsjinnene som Guds jevnbyrdige, skjønt Han har skapt dem. Og de har funnet på at Han har sønner og døtre, uten å vite noe. Lovet være Han! Opphøyet er Han over det de beskriver!'),
(890, 6, 101, 'Himlenes og jordens skaper! Hvordan skulle Han ha barn, når Han ikke har noen hustru, og har skapt alt? Han vet alt.'),
(891, 6, 102, 'Dette er Gud, deres Herre. Det er ingen gud unntatt Ham, alle tings skaper. Så tjen Ham. Han har omsorg for alle ting.'),
(892, 6, 103, 'Blikkene kan ikke nå Ham, men Han kan merke dem. Han er den Uutgrunnelige, den Innsiktsfulle.'),
(893, 6, 104, 'Innsikt er nå kommet fra Herren. Den som får innsikt, gjør det til sitt eget beste. Den som forblir blind, gjør det til sitt eget tap. Jeg er ikke vokter for dere.'),
(894, 6, 105, 'Slik snur og vender Vi på ordet, så de til slutt sier: «Du har drevet studier!» Men også for å klargjøre det for folk som forstår.'),
(895, 6, 106, 'Følg du det som er deg åpenbart fra Herren. Det er ingen guddom unntatt Ham! Vend deg bort fra avgudsdyrkerne!'),
(896, 6, 107, 'Hadde Gud så villet, ville de ikke vært avgudsdyrkere. Vi har ikke satt deg til vokter over dem, og du er heller ikke deres ombudsmann.'),
(897, 6, 108, 'Tal ikke nedsettende om den de påkaller utenom Gud, ellers vil de som mottrekk tale nedsettende om Gud i sin uvitenhet. Vi har latt ethvert folk synes godt om sin fremferd! Men så vil de vende hjem til Herren, og Han vil fortelle dem hva de bedrev.'),
(898, 6, 109, 'De sverger sine dyreste eder ved Gud, at om de bare fikk et jærtegn, så ville de visselig tro på det. Si: «Jærtegn er i Guds hånd.» Hva kan få dere til å anta at de ikke tror, om det kommer?'),
(899, 6, 110, 'Vi kan jo snu opp ned på deres hjerter og deres syn, så alt blir som første gang da de heller ikke trodde på det, og Vi kan la dem forbli i deres oppsetsighet på deres blinde ferd.'),
(900, 6, 111, 'Om Vi sendte engler ned til dem, allikevel ville de ikke tro, med mindre Gud så ville. Dog de fleste av dem er uvitende.'),
(901, 6, 112, 'Vi har gitt hver profet en fiende, sataner blant mennesker og dsjinner, som gjensidig inngir hverandre fagre ord til å villede. Men om Herren hadde villet slik, så ville de ikke ha gjort det. Overlat dem til seg selv og alt de dikter opp,'),
(902, 6, 113, 'slik at deres hjerter som ikke tror på det hinsidige liv kan vende seg mot det, og finne behag i det oppdiktede, og få det de tar sikte på.'),
(903, 6, 114, 'Skulle jeg vel søke en annen dommer enn Gud? Han som har sendt dere skriften, klart fremlagt? De som Vi har gitt skriften, vet at det er en åpenbaring fra Herren med sannhet. Så vær ikke av dem som tviler.'),
(904, 6, 115, 'Fullkomment er Herrens ord i sannhet og rett. Ingen kan endre Hans ord. Han er den Hørende, den Allvitende.'),
(905, 6, 116, 'Men hvis du hører etter folk flest på jorden, så vil de føre deg bort fra Guds vei. De følger bare sine formodninger og antagelser.'),
(906, 6, 117, 'Herren kjenner vel til dem som forlater Hans vei, og Han vet vel om dem som er på rett vei.'),
(907, 6, 118, 'Spis av alt som Guds navn er påkalt over, om det er slik at dere tror på Hans ord.'),
(908, 6, 119, 'Og hvorfor skulle dere ikke spise av det som Guds navn er påkalt over? Han har jo klargjort for dere hva Han har forbudt dere, med unntak for tvangssituasjoner. Men det er mange som fører på villspor ved sine forestillinger. Herren kjenner vel dem som går over grensen.'),
(909, 6, 120, 'Hold dere unna synden, den åpenbare og den skjulte. Den som legger seg til synd, får lønn for det han har ervervet.'),
(910, 6, 121, 'Spis ikke av slikt som Guds navn ikke er påkalt over. Dette er ugudelig adferd. Satan inngir sine venner å strides med dere. Men hvis dere hører på dem, da er dere avgudsdyrkere.'),
(911, 6, 122, 'Er vel den som var død, som Vi gav nytt liv, og lys til å vandre ved blant menneskene, lik den som er i mørket og ikke kommer ut av det? Men slik er det, for de vantro selv synes deres ferd prydelig.'),
(912, 6, 123, 'Således har Vi i enhver by gjort dens største syndere til renkesmeder. Men deres renker går bare ut over dem selv. Men dette er de ikke klar over.'),
(913, 6, 124, 'Når et jærtegn kommer til dem, sier de: «Vi vil aldri tro, før vi får se det samme som Guds sendebud ble utstyrt med.» Gud vet best hvem Han skal betro Sitt budskap. Fornedrelse hos Gud og streng straff vil ramme synderne for deres renker.'),
(914, 6, 125, 'Den som Gud ønsker å gi ledelse, hans bryst åpner Han for islam. Den Han ønsker å la seile sin egen sjø, hans bryst gjør Han trangt og tungt, som var han i ferd med å klatre til himmels. Slik lar Gud Sitt mishag hvile over dem som ikke tror.'),
(915, 6, 126, 'Dette er Herrens vei, rett og strak. Nå har Vi utlagt ordet, klart og greit, for folk som tar det til etterretning.'),
(916, 6, 127, 'For slike er fredens bolig hos Herren! Han tar seg av dem for den innsats de har gjort.'),
(917, 6, 128, 'Den dag når Han samler alle: «Flokk utav dsjinner, dere har gjort godt hos menneskene!» Og deres venner blant menneskene vil si: «Herre, vi hadde gjensidig nytte av hverandre. Men nå har vi nådd slutten som Du bestemte for oss.» Han sier: «Ilden blir deres oppholdssted, og der skal dere være og bli,» – med mindre Gud vil annerledes. Herren er vis, og vet.'),
(918, 6, 129, 'Således lar vi de urettferdige slutte seg til hverandre, for det de har fortjent.'),
(919, 6, 130, '«Flokk utav dsjinner og mennesker, stod det ingen sendebud frem fra deres egne rekker som fremsa Mitt ord og advarte mot denne dags møte?» Og de svarer: «Jo, vi avgir vitnesbyrd mot oss selv!» Jordelivet forførte dem, og de avgir vitnesbyrd mot seg selv om at de var vantro.'),
(920, 6, 131, 'Således fordi Herren aldri ødelegger byer på urettvis måte, mens deres innbyggere er intetanende.'),
(921, 6, 132, 'For alle finnes grader i samsvar med deres gjerninger. Herren overser ikke hva de gjør.'),
(922, 6, 133, 'Herren er selvtilstrekkelig, full av nåde. Om Han vil, tar Han dere bort, og lar hvem Han vil overta etter dere, på samme måte som Han lot dere stå frem som andre menneskers etterkommere.'),
(923, 6, 134, 'Det som er lovet, vil visselig komme. Dere kan ikke forpurre det.'),
(924, 6, 135, 'Si: «Mitt folk, dere får handle som dere best kan. Jeg handler også, og dere skal snart få vite hvem som tilkommer evighetens hjem. De urettferdige går det ikke godt!»'),
(925, 6, 136, 'Noen mennesker setter av til Gud en andel av grøde og buskap som Han har frembrakt, og sier: «Dette er til Gud,» – så sier de: «– og dette er til våre medguder.» Men de tar ikke fra medgudene for å fylle Guds kvote, dog tar de fra Gud for å fylle deres kvote (egent. medgudenes når ikke Gud, men Guds når medgudene). De viser dårlig dømmekraft.'),
(926, 6, 137, 'Enn videre har avgudene latt det fortone seg som prydelig adferd for mange av avgudsdyrkerne å ofre sine barn, for å føre dem i fortapelsen, og for å formørke deres religion for dem. Hadde Gud villet, ville de ikke gjort dette. La dem og det de dikter opp i fred.'),
(927, 6, 138, 'De sier også: «Dette kveg og denne grøde er tabu! Ingen må spise derav unntatt den vi vil,» – så sier de. Og det finnes kveg som ingenting må bære, og kveg som de ikke nevner Guds navn over, idet de oppdikter løgner mot Ham. Han vil belønne dem for det de har oppdiktet.'),
(928, 6, 139, 'De sier videre: «Det som er i dette kvegs liv er forbeholdt menn og forbudt våre kvinner. Men er det dødt, står de likt.» Han vil belønne dem for det de beskriver. Han er vis, og vet.'),
(929, 6, 140, 'Fortapte er de som ofrer sine barn i dårskap, uten kunnskap, og som erklærer forbudt det som Gud har gitt dem til underhold, i uetterrettelighet mot Gud. De har fart vill, og er ikke på rett vei.'),
(930, 6, 141, 'Det er Han som har latt vokse frem haver med og uten vintrær på rekker, palmer og vekster med forskjellige slags frukter, oliven og granatepler, likt og ulikt. Spis av deres frukter når de bærer frukt, og gi det som tilbørlig er på innhøstingsdagen, men sløs ikke! Han liker ikke sløsere!'),
(931, 6, 142, 'Og av kveget, lastdyr og slaktedyr, spis det Gud har gitt dere. Følg ikke i Satans fotspor, for han er en klar fiende for dere.'),
(932, 6, 143, 'Åtte dyr i par, to sauer, to geiter. Si: «Er det de to av hankjønn Han har forbudt, eller de to av hunkjønn, eller det som er i mors liv? Fortell meg ut fra kunnskap, om dere skal snakke sant!»'),
(933, 6, 144, 'To kameler, to naut. Si: «Er det de to av hankjønn Han har forbudt eller de to av hunkjønn, eller det som er i mors liv? Var dere vitner til at Gud bestemte dette? Hvem er større i urett enn den som oppdikter løgn mot Gud, for å føre vill folk, uten kunnskap? Gud leder ikke urettferdige folk.'),
(934, 6, 145, 'Si: «Jeg finner ikke i det som er meg åpenbart noe som er forbudt for en som skal spise, unntatt selvdødt, utgytt blod og svinekjøtt, det er en gru, og det vanhellige som er helliget en annen enn Gud.» Men om noen drives av nød, ikke lyst, og ikke går over grensen, så er Herren tilgivende, nåderik.'),
(935, 6, 146, 'Jødene har Vi forbudt alle dyr med hover, og av storfe og sauer har vi forbudt dem deres fett, unntatt det som sitter på ryggen, i innvollene, eller på knoklene. Dette er lønn for deres oppsetsighet. Og Vi sier sannheten.'),
(936, 6, 147, 'Hvis de sier at du farer med løgn, så si: «Herrens nåde strekker seg langt, men Hans strenghet kan ikke avvendes fra et folk av syndere.»'),
(937, 6, 148, 'De som har guder ved Guds side, vil si: «Om Gud hadde villet, ville verken vi eller våre fedre gitt Ham medguder, og vi ville heller ikke erklært noe forbudt.» Slik holdt også de som var før dem sannhet for løgn, til de fikk smake Vår strenghet. Si: «Har dere noen kunnskap, så kom frem med den! Men dere følger ikke annet enn formodninger, og dere gjør ikke annet enn å gjette.»'),
(938, 6, 149, 'Si: «Hos Gud er det avgjørende bevis, om Han hadde villet slik, kunne Han gitt dere alle sin ledelse!»'),
(939, 6, 150, 'Si: «Bring deres vitner, som kan bevitne at Gud har forbudt dette!» Hvis de avgir vitnesbyrd, så la det være med det. Følg ikke deres forestillinger som holder Vårt ord for løgn, som ikke tror på det hinsidige liv, og som gir Herren jevnbyrdige.'),
(940, 6, 151, 'Kom nå, så skal jeg fremsi det som Herren har gitt sakral sanksjon: Gi Ham ingen medguder! Gjør godt mot far og mor! Drep ikke deres barn på grunn av fattigdom! – Vi vil gi dere og dem underhold. Hold dere unna umoral, åpen eller skjult. Drep ingen hvis liv Gud har gjort ukrenkelig, med mindre det er i samsvar med retten! Dette har Han pålagt dere, så dere må forstå.'),
(941, 6, 152, 'Hold dere unna den foreldreløses eiendom, med mindre det er til beste, inntil han når voksen alder. Gi fullt mål og full vekt, som rett og riktig er! Vi pålegger ingen noe ut over det han evner. Når dere uttaler dere, så vis rettferd, selv om en slektning er implisert! Oppfyll deres forpliktelser overfor Gud! Dette har Han pålagt dere, så dere må vise ettertanke.'),
(942, 6, 153, 'Dette er Min vei, den rette, så følg den. Følg ikke stier som vil skille dere fra Hans vei. Dette har Han pålagt dere, så dere må vise gudsfrykt.'),
(943, 6, 154, 'Så gav Vi Moses skriften, til fullbyrdelse for den som gjør godt, som forklaring på alle ting, som ledelse og nåde, så de måtte tro på møtet med Herren.'),
(944, 6, 155, 'Dette er også en skrift som vi har åpenbart, full av velsignelse. Så følg den, og vis gudsfrykt, så dere må finne nåde.'),
(945, 6, 156, 'Dette for at dere ikke skal si: «Bare to folk før oss har fått skriften åpenbart, og vi er ikke kjent med deres lærdom.»'),
(946, 6, 157, 'Eller: «Om skriften var blitt åpenbart for oss, ville vi vært bedre ledet enn dem!» Men nå er det kommet en klar beskjed fra Herren, og ledelse og nåde. Hvem gjør vel større urett enn den som forkaster Guds ord, og vender seg bort fra det? Vi skal belønne dem som vender seg bort fra Vårt ord med en slem straff for deres bortvending.'),
(947, 6, 158, 'Venter de vel at englene skal komme til dem? Eller at Herren skal komme? Eller et Herrens jærtegn? Den dag da et Herrens jærtegn inntreffer, da nytter det ingen å tro som ikke har trodd før, eller ikke har gjort noe godt i sin tro. Si: «Bare vent! Se, også Vi venter!»'),
(948, 6, 159, 'De som har splittet sin religion og er blitt sekter, dem har du intet å gjøre med. Deres sak vil Gud ta seg av. Så vil Han fortelle dem hva de bedrev.'),
(949, 6, 160, 'Den som kommer med en god gjerning, han får tifold igjen. Den som kommer med en ond gjerning, han får samme igjen. De skal ikke lide urett.'),
(950, 6, 161, 'Si: «Min Herre har ledet meg til rett vei, en tro som varer, til Abrahams, Gud-søkerens lære. Han var ingen avgudsdyrker!»'),
(951, 6, 162, 'Si: «Min bønn og min andakt, mitt liv og min død, alt tilhører Gud, all verdens Herre.'),
(952, 6, 163, 'Han har ingen medguder. Dette er blitt meg befalt, og jeg er den første som hengir seg til Gud.»'),
(953, 6, 164, 'Si: «Skulle jeg vel ønske meg en annen Herre enn Gud? Når Han er alle tings Herre?» Enhver erverver kun til sitt eget regnskap. Ingen bærer en annens byrde. Så vil dere vende hjem til Herren. Han vil redegjøre for det dere tvistet om.'),
(954, 6, 165, 'Han er det som har latt dere følge tidligere generasjoner på jorden. Noen av dere har Han plassert over andre i rang, slik at Han kan sette dere på prøve ved de gaver Han gir. Herren er rask med straffen, og Han er tilgivende, nåderik.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'Alif Lam Mim Sad'),
(956, 7, 2, 'Skriftens bok er det som er åpenbart for deg, så føl ingen engstelse i ditt bryst om dette, for at du skal bruke den som advarsel og formaning for de troende.'),
(957, 7, 3, 'Følg det som er sendt dere fra Herren! Følg ingen beskyttende venner utenom Ham! Lite kommer dere i hu!'),
(958, 7, 4, 'Hvor mang en by har Vi vel utslettet! Vår voldsmakt kom over dem, om natten eller i middagshvilen.'),
(959, 7, 5, 'Og de ropte ut da Vår voldsmakt kom over dem: «Vi har vært urettferdige mennesker!»'),
(960, 7, 6, 'Vi vil visselig forhøre dem som sendebud ble sendt til, og Vi vil visselig forhøre sendebudene.'),
(961, 7, 7, 'Så vil Vi fortelle dem noe, ut fra kunnskap! Vi var aldri fraværende.'),
(962, 7, 8, 'Veiingen på denne dag er sannhet. De hvis vektskåler er tunge, dem vil det gå godt.'),
(963, 7, 9, 'De hvis vektskåler er lette, de er fortapte fordi de forgikk seg mot Vårt ord.'),
(964, 7, 10, 'Vi har etablert dere fast på jorden, og gitt dere et utkomme. Lite viser dere takknemlighet.'),
(965, 7, 11, 'Vi skapte dere og formet dere. Så sa Vi til englene: «Bøy dere ned for Adam!» Og de bøyet seg unntatt Iblis (Satan). Han bøyet seg ikke.'),
(966, 7, 12, 'Gud sa: «Hva hindret deg i å bøye deg når Jeg gav deg ordre?» Han svarte: «Jeg er bedre enn Adam. Du skapte meg av ild, og ham skapte Du bare av leire.»'),
(967, 7, 13, 'Han sa: «Kom deg ut herfra! Du har ingenting med å være hovmodig her! Ut med deg! Du er sannelig en elendig!»'),
(968, 7, 14, 'Så svarte han: «Gi meg utsettelse til den dag de gjenoppvekkes.»'),
(969, 7, 15, 'Han sa: «Du har utsettelse.»'),
(970, 7, 16, 'Så svarte han: «Siden Du har ført meg på avveie, så skal jeg sannelig ligge på lur etter dem på Din rette vei.'),
(971, 7, 17, 'Jeg skal komme over dem forfra og bakfra, fra høyre og venstre, og Du vil ikke finne de fleste av dem takknemlige.»'),
(972, 7, 18, 'Han sa: «Kom deg ut, foraktet og forvist! Sannelig, med de av dem som følger deg, med dere alle sammen, vil Jeg fylle helvete.»'),
(973, 7, 19, '«Bo i haven, Adam, du og din hustru, og spis hvor dere vil. Men kom ikke nær dette tre, så dere ikke blir urettferdige.»'),
(974, 7, 20, 'Men Satan førte dem i fristelse for å gjøre synlig for dem deres nakne kjønn, som var dem skjult. Og han sa: «Herren har bare forbudt dere dette tre for at dere ikke skal bli engler, eller udødelige.»'),
(975, 7, 21, 'Og han bedyret: «Jeg er sannelig en oppriktig rådgiver for dere.»'),
(976, 7, 22, 'Så forførte han dem ved svik. Da de hadde smakt på treet, stod deres nakenhet klart for dem, og de tok og festet på seg av havens løv. Men Herren ropte til dem: «Har Jeg ikke forbudt dere dette tre? Har Jeg ikke sagt at Satan er en klar fiende for dere?»'),
(977, 7, 23, 'De svarte: «Herre, vi har stelt oss galt. Hvis Du ikke tilgir oss og forbarmer deg over oss, er vi visselig fortapt.»'),
(978, 7, 24, 'Han sa: «Kom dere ut! I fiendskap skal dere leve, og på jorden skal dere finne et bosted og utkomme for en tid!»'),
(979, 7, 25, 'Han sa: «Der skal dere leve, og der skal dere dø, og av den skal dere igjen bringes frem!»'),
(980, 7, 26, 'Dere Adams barn, Vi har gitt dere klær til å dekke deres nakenhet, og til pynt. Men fromhetens drakt er bedre. Dette er et av Guds tegn, så de måtte komme i hu.'),
(981, 7, 27, 'Dere Adams barn, la ikke Satan føre dere i fristelse, slik han fikk drevet deres forfedre ut av haven, idet han berøvet dem uskyldens klær for å la dem se sine nakne kjønn. Satan og hans klan ser dere der dere ikke ser dem. Vi har gjort satanene til venner og ledere for dem som ikke tror.'),
(982, 7, 28, 'Om noen begår en skjendighet, sier de: «Det er våre fedres tradisjon, og Gud har befalt oss å handle slik.» Si: «Gud påbyr ingen skjendighet! Vil dere da påstå noe om Gud som dere ikke vet?»'),
(983, 7, 29, 'Si: «Herren påbyr rettferdighet! Utfør de hellige handlinger på ethvert bønnested, påkall Ham idet dere tilegner Ham en oppriktig dyrkelse. Som Han lot dere fremstå første gang, vil dere også vende tilbake.»'),
(984, 7, 30, 'Noen har Han ledet, andre har villfarelse fått makt over. De har tatt satanene til venner og ledere fremfor Gud, og tror at de er på rett vei.'),
(985, 7, 31, 'Dere Adams barn, anlegg høytidsantrekk ved ethvert bønnested. Spis og drikk, men vær ikke lettsindige. Han liker ikke de lettsindige.'),
(986, 7, 32, 'Si: «Hvem har vel forbudt Guds høytidsantrekk som Han har fremskaffet for Sine tjenere, og Hans gode matemner som føde?» Si: «Dette er på oppstandelsens dag forbeholdt dem som tror i jordelivet.» Slik forklarer Vi ordet for folk som har kunnskap.'),
(987, 7, 33, 'Si: «Herren har bare forbudt skjendigheter, åpent eller i det skjulte, og synd og voldsdåd i urett, og at det settes ved Guds side noe som Han ikke har sendt noen autorisasjon for, samt å påstå om Gud slik som dere ikke vet.»'),
(988, 7, 34, 'Hvert folk har sin frist. Når deres tid kommer, kan de verken utsette den eller påskynde den en time.'),
(989, 7, 35, 'Dere Abrahams barn, hvis det skulle stå frem sendebud blant dere som forkynner dere Mitt ord, de som da viser gudsfrykt og forbedrer sitt levnet, over dem skal ingen frykt hvile, ei heller sorg.'),
(990, 7, 36, 'Men de som forkaster Vårt ord, og avviser det i hovmod, disse er Ildens folk, og der skal dere være og bli.'),
(991, 7, 37, 'For hvem er vel mer urettferdig enn den som oppdikter løgn mot Gud, eller forkaster Hans ord? Disse vil få den lodd som er foreskrevet dem, til Våre utsendinger kommer for å hente dem når deres dagers tall er fullt, og sier: «Hvor er nå de dere påkalte fremfor Gud? Og de vil svare: «De er borte for oss.» Og de vitner mot seg selv at de var vantro.'),
(992, 7, 38, 'Han vil si: «Gå inn i Ilden til de samfunn av dsjinner og mennesker som er gått bort før dere!» Hver gang et samfunn kommer inn, forbanner det det foregående, inntil de alle i tur og orden er kommet inn, da vil det siste si om det første: «Herre, det var disse som førte oss vill, så gi dem dobbelt straff i Ilden!» Og han vil svare: «Dobbelt for alle,» – men dette vet dere ikke.'),
(993, 7, 39, 'De første vil si til de siste: «Dere er ikke noe bedre enn oss, så smak straffen for det dere har fortjent!»'),
(994, 7, 40, 'De som forkaster Vårt ord og avviser det i hovmod, for dem vil ikke himmelens porter åpnes, og de vil ikke komme inn i paradiset før en kamel kommer gjennom et nåløye. Slik belønner Vi synderne.'),
(995, 7, 41, 'I helvete skal deres hvileleie være, med ild til overbredsel. Slik belønner Vi illgjerningsmenn.'),
(996, 7, 42, 'Men de som tror og lever rettskaffent, og Vi krever ikke av noen mer enn han evner, de er paradisets folk, og der skal de være og bli.'),
(997, 7, 43, 'Vi vil fjerne nag og bitterhet fra deres hjerter, foran dem vil bekker strømme, og de vil si: «Lovet være Gud som ledet oss til dette! Vi ville ikke funnet veien om ikke Gud hadde ledet oss! Herrens sendebud brakte visselig sannheten!» Og det lyder: «Dette er paradiset, som dere har arvet for det dere har gjort!»'),
(998, 7, 44, 'Og paradisets folk roper til Ildens folk: «Vi har funnet Herrens løfter sanne! Har dere funnet Herrens løfter sanne?» Og de svarer: «Ja.» Og en herold mellom dem vil utrope: «Guds forbannelse hviler over dem som gjør ondt,'),
(999, 7, 45, 'som stenger Guds vei og prøver å vri den krokete, og som fornekter det hinsidige liv!»'),
(1000, 7, 46, 'Mellom dem er et skille. På skillemuren er menn som kjenner alle på deres kjennetegn. De roper til paradisets folk: «Fred være med dere!» De har ikke kommet inn i paradiset, men de ønsker det inderlig.'),
(1001, 7, 47, 'Og de vender blikket mot Ildens folk, og sier: «Herre, plasser oss ikke sammen med de urettferdige!»'),
(1002, 7, 48, 'Folkene på skillemuren roper til menn de kjenner på deres kjennetegn: «Det dere har samlet sammen og deres hovmod, har ikke hjulpet dere!»'),
(1003, 7, 49, 'Er det disse dere svor på at Gud aldri ville nå med Sin nåde? «Gå inn i paradiset! Ingen frykt skal hvile over dere, ei heller sorg!»'),
(1004, 7, 50, 'Ildens folk roper til paradisets folk: «Tøm over oss litt vann, eller annet av Guds gaver til dere!» Men de vil svare: «Gud har forholdt vantro dette,'),
(1005, 7, 51, 'slike som tok sin religion som tidsfordriv og lek, som jordelivet har forført.» Denne dag glemmer Vi dem, som de glemte denne deres dags møte, og fordi de forkastet Vårt ord.'),
(1006, 7, 52, 'Vi har gitt dem en skriftens bok, og Vi har utlagt den klart med viten, som ledelse og nåde for folk som tror.'),
(1007, 7, 53, 'Er det så at de venter på det den innebærer? Den dag det den innebærer kommer, vil de som tidligere glemte den, si: «Herrens sendebud brakte sannheten! Har vi noen som kan legge inn ord for oss? Eller kan vi bli sendt tilbake til jorden, så vi kan handle annerledes enn vi gjorde?» De har kastet seg selv bort, og det som de diktet opp, har sveket dem.'),
(1008, 7, 54, 'Gud er deres Herre! Han som skapte himlene og jorden på seks dager. Så tok Han plass på tronen. Han breder natten over dagen, den følger beredvillig. Og sol, måne og stjerner, underdanige ved Hans bud. Skapelsen og makten tilhører Ham! Velsignelsesrik er Gud, all verdens Herre.'),
(1009, 7, 55, 'Påkall Herren ydmykt i det skjulte. Se, Han liker ikke dem som går over grensen.'),
(1010, 7, 56, 'Stift ikke ufred på jorden, når alt er satt i rett orden. Påkall Ham i frykt og håp. Guds nåde er dem nær som handler godt.'),
(1011, 7, 57, 'Han er det som løser vindene, som et godt forvarsel for Sin nåde. Når de er fylt med tunge skyer, sender Vi dem til et dødt land. Så sender Vi regnet fra dem, og frembringer derved allslags frukter. På samme måte vil Vi bringe frem de døde, så dere må gi det ettertanke.'),
(1012, 7, 58, 'I den gode jord spirer plantene frem, ved deres Herres ordning. I den dårlige jord spirer de bare sparsomt. Slik snur og vender vi på ordet for folk som viser takknemlighet.'),
(1013, 7, 59, 'Vi sendte Noa til sitt folk, og han sa: «Hør mitt folk! Tjen Gud! Dere har ingen annen Gud enn Ham! Jeg frykter at en svær dags straff vil ramme dere!»'),
(1014, 7, 60, 'Men rådsmennene i hans folk sa: «Vi mener du er i åpenbar villfarelse!»'),
(1015, 7, 61, 'Han svarte: «Mitt folk, det er ingen villfarelse hos meg. Jeg er sendebud fra Herren over all verden!'),
(1016, 7, 62, 'Jeg frembærer Herrens budskap til dere, og råder dere oppriktig. Jeg vet fra Gud det dere ikke vet!'),
(1017, 7, 63, 'Undrer det dere at en påminnelse fra Herren kommer ved en mann fra deres midte? At han skal advare dere, så dere kan vise fromhet, så dere må finne nåde?»'),
(1018, 7, 64, 'Men de holdt ham for løgner. Så reddet Vi ham og dem som var sammen med ham i arken, og Vi lot dem drukne som holdt Vårt ord for løgn. Sannelig, de var forblindede mennesker.'),
(1019, 7, 65, 'Til folket Ad sendte vi deres bror Hod, og han sa: «Hør, mitt folk! Tjen Gud! Dere har ingen annen gud enn Ham! Vil dere ikke vise gudsfrykt?»'),
(1020, 7, 66, 'De vantro rådsmenn i hans folk sa: «Vi anser deg fylt av dårskap og mener at du farer med løgn.»'),
(1021, 7, 67, 'Han svarte: «Det er ingen dårskap hos meg. Jeg er sendebud fra Herren over all verden!'),
(1022, 7, 68, 'Jeg frembærer Herrens budskap til dere, og jeg er dere en oppriktig og tro rådgiver.'),
(1023, 7, 69, 'Undrer det dere at en påminnelse fra Herren kommer ved en mann fra deres midte? At han skal advare dere. Kom i hu Han har gjort dere til Noas folks arvinger, og at Han har øket deres utbredelse blant skapningene. Kom i hu Guds velgjerninger, så det må gå dere godt!»'),
(1024, 7, 70, 'De sa: «Er du kommet til oss for at vi skal dyrke Gud alene, og oppgi det våre fedre dyrket? Nei, bare bring over oss det du truer oss med, om du taler sant!»'),
(1025, 7, 71, 'Da svarte han: «Måtte Herrens mishag og vrede ramme dere! Vil dere strides med meg om (tomme) navn, som dere og deres fedre har funnet på og som Gud ikke har sendt noen autorisasjon for? Bare vent dere! Jeg er likeledes avventende.»'),
(1026, 7, 72, 'Så reddet Vi i Vår nåde ham og dem som fulgte ham, og utryddet siste rest av dem som forkastet Vårt ord, og som ikke trodde.'),
(1027, 7, 73, 'Til folket Thamod sendte vi deres bror Salih, og han sa: «Hør, mitt folk! Tjen Gud! Dere har ingen annen Gud enn Ham! Nå er det kommet klar beskjed til dere fra deres Herre! Dette er Guds kamelhoppe, som et jærtegn for dere! La den beite i fred på Guds jord, og gjør den intet vondt! I så fall vil en smertelig straff ramme dere!'),
(1028, 7, 74, 'Kom i hu at Han har gjort dere til folket Ads arvinger, og bosatt dere i landet, og at dere laget slott på slettene og hus i fjellsidene. Kom i hu Guds velgjerninger, og far ikke frem med ondskap og ufred på jorden.»'),
(1029, 7, 75, 'De hovmodige rådsmenn i hans folk sa til de svake og undertrykte, til de av dem som var blitt troende: «Er dere sikre på at Salih er sendt fra Herren?» Og de svarte: «Vi tror på det budskap han er sendt med.»'),
(1030, 7, 76, 'Da sa de hovmodige: «Vi fornekter det dere tror på!»'),
(1031, 7, 77, 'Så skar de av hase-senene på kamelhoppen, og viste forakt for Herrens bud. Og de sa: «Nå Salih, bare bring over oss det du truer oss med, hvis det er så at du er et sendebud!»'),
(1032, 7, 78, 'Så tok jordskjelvet dem, og om morgenen lå de utstrakte i sine hjem.'),
(1033, 7, 79, 'Han vendte seg bort fra dem, og sa: «Mitt folk, jeg frembar til dere Herrens budskap og rådet dere vel. Men dere liker ikke folk som råder dere vel.»'),
(1034, 7, 80, 'Og Lot, da han sa til sitt folk: «Vil dere bedrive denne skjendighet? Ingen i verden har gjort dette før dere?'),
(1035, 7, 81, 'I begjær går dere til menn fremfor kvinner. Dere er i sannhet et folk som går over grensen.»'),
(1036, 7, 82, 'Hans folk svarte ganske enkelt: «Driv dem ut av byen. Disse folk foregir å være rene!»'),
(1037, 7, 83, 'Så reddet Vi ham og hans, unntatt hans hustru. Hun hang etter.'),
(1038, 7, 84, 'Og så lot Vi det regne ned over dem. Se, hva enden ble for synderne!'),
(1039, 7, 85, 'Og til Midian sendte Vi deres bror Shoaib, og han sa: «Hør, mitt folk! Tjen Gud! Dere har ingen annen Gud enn Ham! Nå er det kommet klar beskjed til dere fra deres Herre! Så gi fullt mål, og full vekt! Snyt ikke folk på deres eiendeler, og stift ikke ufred på jorden, etter at alt er satt i god orden. Dette er bedre for dere, om dere er troende.'),
(1040, 7, 86, 'Pass ikke opp folk på alle landeveier med trusler, og for å legge hindringer på Guds vei for dem som tror på Ham, idet dere forsøker å gjøre den kroket. Kom i hu den gang dere var få, og Han gjorde dere tallrike. Se hvordan det gikk med urostifterne!'),
(1041, 7, 87, 'Hvis det er noen av dere som tror på det budskap jeg er sendt med, og andre som ikke tror, så ta det med ro inntil Gud avgjør saken mellom oss. Han er den beste til å avgjøre!'),
(1042, 7, 88, 'De hovmodige rådsmenn i hans folk sa: «Sannelig skal vi jage deg bort fra byen, Shoaib, og de som tror sammen med deg, med mindre dere vender tilbake til vår lære!» Han svarte: «Selv om vi tar avstand fra den?'),
(1043, 7, 89, 'Vi ville laget en løgn mot Gud om vi vendte tilbake til deres lære, etter at Gud har reddet oss fra den. Vi kan ikke vende tilbake til den, med mindre Gud, vår Herre skulle bestemme slik. Gud omfatter alt i sin visdom. Vi forlater oss på Ham. Herre, åpne for samliv mellom oss og vårt folk ved sannheten! Du er den beste til å sprenge stengsler.»'),
(1044, 7, 90, 'De vantro rådsmenn i hans folk sa: «Hvis dere følger Shoaib, er dere tapere!»'),
(1045, 7, 91, 'Så tok jordskjelvet dem, og om morgenen lå de utstrakte i sine hjem.'),
(1046, 7, 92, 'Det var som om de som holdt Shoaib for løgner, aldri hadde bodd i dem. De som holdt Shoaib for løgner, var tapere.'),
(1047, 7, 93, 'Han vendte seg bort fra dem, og sa: «Mitt folk, jeg frembar til dere Herrens budskap, og rådet dere vel. Men hvorfor skulle jeg sørge over et folk av vantro?»'),
(1048, 7, 94, 'Vi sendte aldri en profet til en by uten at Vi hjemsøkte dens innvånere med trengsel og nød – så de må vise ydmykhet.'),
(1049, 7, 95, 'Men så byttet Vi ondt med godt til de visket bort det onde, og sa: «Gode og onde dager rammet også våre forfedre.» Men så tok Vi dem fatt, plutselig, uten at de ante noe.'),
(1050, 7, 96, 'Hadde disse byers folk vist tro og gudsfrykt, ville Vi øst over dem velsignelse fra himmel og jord. Men de forkastet og fornektet, og så tok Vi dem fatt for det de hadde fortjent.'),
(1051, 7, 97, 'Føler folk i byene seg sikre mot at Vår voldsmakt ikke rammer dem om natten, mens de sover?'),
(1052, 7, 98, 'Føler folk i byene seg sikre mot at Vår voldsmakt ikke rammer dem, på lyse dager, midt i deres lek og spill?'),
(1053, 7, 99, 'Føler de seg sikre mot de trekk Gud gjør? Ingen kan føle seg sikker mot Guds trekk, unntatt et folk av tapere.'),
(1054, 7, 100, 'Er det da ikke en ledetråd for dem som har arvet jorden etter dem som bodde der før, at Vi, om Vi ville, kunne ramme dem for deres synder? Men Vi forsegler deres hjerter så de intet oppfatter.'),
(1055, 7, 101, 'For disse byer har Vi fortalt deg deres hendelser. Deres sendebud kom til dem med klar beskjed, men de var ikke slik at de ville tro noe de tidligere hadde forkastet. Slik forsegler Gud de vantros hjerter.'),
(1056, 7, 102, 'Hos de fleste av dem fant Vi ikke paktstroskap. De fleste av dem fant Vi ugudelige.'),
(1057, 7, 103, 'Senere, etter disse profeter, sendte Vi Moses med Våre tegn til Farao og hans råd, men de tok ille imot dem. Men se, hva enden ble for dem som skaper urett og ufred.'),
(1058, 7, 104, 'Moses sa: «Hør Farao, jeg er et sendebud for all verdens Herre!'),
(1059, 7, 105, 'Jeg må ikke tale annet enn sannhet om Gud! Jeg er kommet til dere med klar beskjed fra Herren. Send med meg Israels barn!»'),
(1060, 7, 106, 'Farao svarte: «Hvis du er kommet med et jærtegn, så kom med det, om du taler sant.»'),
(1061, 7, 107, 'Så kastet Moses sin stav, og plutselig var den en tydelig slange.'),
(1062, 7, 108, 'Så trakk han frem sin hånd, og plutselig, den var hvit for alle tilskuerne.'),
(1063, 7, 109, 'Da sa Faraos råd: «Dette er sannelig en dyktig tryllekunstner.'),
(1064, 7, 110, 'Han vil fordrive dere fra deres land! Hva foreslår dere?»'),
(1065, 7, 111, 'De sa videre: «La saken med ham og hans bror bero, og send bud til byene'),
(1066, 7, 112, 'om å bringe deg alle dyktige tryllekunstnere.»'),
(1067, 7, 113, 'Tryllekunstnerne kom til Farao, og sa: «Vi får vel belønning hvis vi vinner?»'),
(1068, 7, 114, 'Og han svarte: «Ja, og dere vil høre til min nærmeste krets!»'),
(1069, 7, 115, 'Så sa de: «Moses, skal du kaste eller skal vi kaste?»'),
(1070, 7, 116, 'Han svarte: «Kast dere.» Og da de kastet, forgjorde de folks øyne, og hensatte dem i frykt, og brakte veldige tryllekunster.'),
(1071, 7, 117, 'Så gav Vi Moses denne inspirasjon: «Kast din stav!» Og se, den slukte deres bedragerier.'),
(1072, 7, 118, 'Således kom sannheten til sin rett, og alt det de gjorde ble til intet.'),
(1073, 7, 119, 'Så her ble de beseiret, og de vendte seg om i ydmykhet.'),
(1074, 7, 120, 'Og tryllekunstnerne ble ført til tilbedelse, og sa:'),
(1075, 7, 121, '«Vi tror på all verdens Herre,'),
(1076, 7, 122, 'Moses og Arons Herre.»'),
(1077, 7, 123, 'Så sa Farao: «Tror dere på ham før jeg har gitt tillatelse? Dette er renker dere har funnet på nede i byen for å jage ut dens innbyggere. Men dere skal få å vite!'),
(1078, 7, 124, 'Jeg skal la deres hender og føtter avhugge i kryss, og deretter korsfeste dere alle sammen!»'),
(1079, 7, 125, 'De svarte: «Til vår Herre skal vi vende tilbake!'),
(1080, 7, 126, 'Du hevner deg på oss bare fordi vi tror på vår Herres tegn da de kom til oss. Herre, utøs standhaftighet over oss, og ta oss bort som er Deg hengivne.»'),
(1081, 7, 127, 'Rådsherrene av Faraos folk sa: «Vil du la Moses og hans folk stifte ufred i landet, og forlate deg og dine guder?» Han svarte: «Vi skal drepe deres sønner og la kvinnene leve. Vi har makt over dem!»'),
(1082, 7, 128, 'Moses sa til sitt folk: «Be Gud om hjelp og vær standhaftige! Jorden tilhører Gud, og Han lar dem Han vil av Sine tjenere arve den. Sluttresultatet blir de gudfryktige til del.»'),
(1083, 7, 129, 'De svarte: «Vi har lidd før du kom til oss, og etterpå.» Han sa: «Herren vil kanskje tilintetgjøre deres fiender, og la dere overta landet, så Han kan få se hvordan dere bærer dere ad.»'),
(1084, 7, 130, 'Og Vi tok fatt Faraos folk med nødsår og matmangel, så de måtte komme til ettertanke.'),
(1085, 7, 131, 'Når noe godt ble dem til del, sa de: «Dette tilkommer oss.» Når noe ondt overgikk dem, så de et dårlig tegn i Moses og dem som var med ham. Men lå ikke deres skjebne i Guds hånd? Skjønt de fleste vet ikke dette.'),
(1086, 7, 132, 'Og de sa: «Hva du enn bringer av jærtegn for å trollbinde oss, så vil vi ikke tro deg.»'),
(1087, 7, 133, 'Så sendte Vi over dem oversvømmelsen, gresshoppene, lus, froskene, og blod, – klare jærtegn. Men de holdt frem i sitt hovmod, og var et folk av syndere.'),
(1088, 7, 134, 'Da straffedommen rammet dem, sa de: «Påkall din Herre for oss, Moses, ved den pakt Han har gjort med deg! Hvis du avvender straffedommen fra oss, så vil vi tro deg, og la Israels folk fare med deg!»'),
(1089, 7, 135, 'Men da Vi tok bort straffedommen til de nådde en fastsatt frist, så brøt de sitt løfte.'),
(1090, 7, 136, 'Så tok Vi hevn over dem, og druknet dem i havet. Dette fordi de forkastet Våre jærtegn og viste dem likegyldighet.'),
(1091, 7, 137, 'Og så lot Vi et folk som hadde levd i undertrykkelse, arve det land Vi hadde velsignet, fra øst til vest. Og Herrens vakreste ord ble fullbyrdet for Israels barn, for deres standhaftighet. Men Vi ødela alt som Farao og hans folk hadde laget og bygget.'),
(1092, 7, 138, 'Vi førte Israels barn gjennom havet. Så kom de til et folk som holdt seg til avguder de hadde. Og Israels folk sa: «Lag en gud til oss, Moses, slik som disse har.» Han svarte: «Dere er i sannhet uvitende folk!'),
(1093, 7, 139, 'Det disse holder på med, skal bli knust, og verdiløst er det de har gjort.»'),
(1094, 7, 140, 'Han sa videre: «Skulle jeg vel ønske en annen gud for dere enn Gud? Han som har utvalgt dere fremfor all verden?»'),
(1095, 7, 141, 'Da dere ble reddet fra Farao og hans folk, som påførte dere store lidelser, drepte deres sønner og lot deres kvinner leve! I dette var en stor prøvelse fra Herren!'),
(1096, 7, 142, 'Vi avsatte Moses 30 døgn, og Vi la til ti døgn, slik at hans tid med Herren ble 40 døgn. Moses sa til sin bror, Aron: «Overta for meg overfor mitt folk og gjør tingene vel. Følg ikke ufredsstifternes vei!»'),
(1097, 7, 143, 'Da Moses kom til den fastsatte tid, og Herren talte til ham, sa han: «Min herre, vis Deg for meg, så jeg kan se Deg!» Men Herren svarte: «Du får ikke se Meg. Men se på det fjellet! Hvis det forblir på sin plass, skal du få se Meg!» Men da Herren viste seg for fjellet, pulverisertes det, og Moses falt om besvimt. Da han kom til seg selv igjen, sa han: «Ære være Deg! Jeg vender meg til Deg som den første troende.»'),
(1098, 7, 144, 'Han sa: «Moses, Jeg utvelger deg fremfor alle mennesker for Mitt budskap og Mitt ord! Motta det Jeg gir deg, og vis takknemlighet!»'),
(1099, 7, 145, 'Og Vi skrev alt på tavlene for ham, til formaning og forklaring på alt. «Hold fast ved disse, og pålegg ditt folk å følge deres uforlignelige anvisninger. Jeg vil vise dere de ugudeliges bosted.'),
(1100, 7, 146, 'Jeg vil vende bort fra Mine jærtegn dem som viser ugrunnet hovmod på jorden. Om de enn ser alle tegn, så tror de ikke ved dem, og om de ser den rette vei, så vil de ikke ta den som vei. Men ser de villfarelsens vei, så slår de inn på den. Dette, fordi de forkaster Vårt ord, og viser det likegyldighet.'),
(1101, 7, 147, 'De som forkaster Vårt ord og møtet i det hinsidige, deres gjerninger er intet verdt. Og kan de vel belønnes for annet enn det de har gjort?»'),
(1102, 7, 148, 'Mose folk laget seg i hans fravær en kalv av sine smykker, en brekende skapning. Så de da ikke at den verken kunne tale eller vise dem vei? Men de antok den, og var illgjerningsmenn.'),
(1103, 7, 149, 'Da de ble klar over dette, og erkjente at de var på villspor, sa de: «Om Herren ikke viser oss barmhjertighet og tilgivelse, er det visselig ute med oss!»'),
(1104, 7, 150, 'Da Moses kom tilbake, var han sint og bedrøvet, og sa: «Ille har dere varetatt min oppgave mens jeg var borte! Vil dere påskynde Herrens inngripen?» Og han kastet tavlene, grep sin bror i hodet og trakk ham til seg. Aron sa: «Sønn av min mor, folket respekterte meg ikke, og hadde nesten drept meg! La ikke fiendene godte seg over meg! Plasser ikke meg sammen med illgjerningsmennene!»'),
(1105, 7, 151, 'Og Moses sa: «Herre, tilgi meg og min bror og ta oss inn i Din nåde! Du er den Allernådigste.»'),
(1106, 7, 152, 'De som laget kalven, dem vil Herrens vrede ramme, og fornedrelse i jordelivet! Slik lønner Vi dem som finner på det som er galt.'),
(1107, 7, 153, 'Men om noen gjør noe ondt, og angrer etterpå, og tror, så er Herren etter det hele tilgivende, nåderik.'),
(1108, 7, 154, 'Da vreden hadde lagt seg hos Moses, tok han opp tavlene, og i deres tekst var ledelse og nåde for dem som har fykt for Herren.'),
(1109, 7, 155, 'Og Moses valgte 70 menn av sitt folk for Vår fastsatte tid. Da jordskjelvet rammet dem, sa han: «Herre, om Du hadde villet, kunne Du ha ødelagt dem tidligere, og meg også. Vil Du nå ødelegge oss for det noen vettløse blant oss har stelt i stand? – Det var bare Din prøvelse, hvorved Du lar noen fare vill og leder andre på rett vei, slik Du vil. Du tar Deg av vår sak. Tilgi oss, og vis oss nåde, Du er den beste tilgiver!'),
(1110, 7, 156, 'Bestem for oss det gode i dette liv, og i det hinsidige. Vi er blitt ledet til Deg!» Han svarte: «Jeg hjemsøker hvem Jeg vil med Min straff! Min nåde omfatter alt, og Jeg vil gjøre den gjeldende for dem som viser gudsfrykt, som gir det rituelle bidrag og som tror på Vårt ord,'),
(1111, 7, 157, 'for dem som følger Sendebudet, profeten fra folkene, som de finner omtalt hos seg i loven og evangeliet, og som påbyr dem det som er rett og forbyr det urette, tillater gode ting og forbyr de stygge, som tar fra menneskene deres byrder og lenker som de var under. De som tror på ham, styrker ham og hjelper ham, og følger det lys som er sendt med ham, dem vil det gå godt.»'),
(1112, 7, 158, 'Si: «Hør folk, jeg er Guds sendebud til dere alle fra Ham, hvis herredømme omfatter himlene og jorden! Det er ingen Gud unntatt Ham! Han gir liv og død. Tro på Gud og Hans sendebud, profeten fra folkene, som tror på Gud og Hans ord. Følg ham, at dere må finne rett vei!»'),
(1113, 7, 159, 'Blant Mose folk finnes et samfunn som følger sannheten, og øver rettferd i samsvar med den.'),
(1114, 7, 160, 'Vi har delt dem i tolv stammesamfunn. Og Vi inspirerte Moses da hans folk bad ham om vann: «Slå med din stav på fjellet!» Så strømmet 12 kilder frem av det, så alle visste hvor de skulle drikke. Og Vi lot skyer overskygge dem, og sendte dem manna og vaktler: «Spis av de gode haver som Vi har gitt dere til underhold!» De rammet ikke Oss med sin urett. De rammet seg selv.'),
(1115, 7, 161, 'Det ble sagt til dem: «Slå dere ned i denne by, og spis av det dere finner der, hvor dere måtte ønske, og si: ’Syndens forsakelse.’ Gå inn gjennom porten ydmykt bøyet! Så vil Vi tilgi deres overtredelser, og gi dem som handler vel mer til!»'),
(1116, 7, 162, 'Men de urettferdige byttet det sagte med noe annet enn det som ble sagt dem. Så sendte Vi over dem en straffedom fra himmelen, fordi de bedrev urett.'),
(1117, 7, 163, 'Spør dem bare om byen ved havet, den gang de krenket sabbaten. Da fisk kom stimende til dem på sabbatsdagen. Men den dag de ikke holdt sabbaten, kom de ikke til dem. Således setter Vi dem på prøve for deres ugudelighet!'),
(1118, 7, 164, 'Og da et samfunn blant dem sa: «Hvorfor formaner dere et folk, som Gud står i ferd med å ødelegge eller straffe med en streng straffedom?» De svarte: «Som unnskyldning overfor Herren, og at de måtte komme til gudsfrykt.»'),
(1119, 7, 165, 'Så, da de glemte formaningen, reddet Vi dem som forbød ondskap, og tok fatt dem som gjorde ondt, med en slem straffedom, for deres ugudelighet.'),
(1120, 7, 166, 'Da de satte seg utover det de var forbudt i sabbatsbudet, sa Vi til dem: «Bli til foraktelige aper!»'),
(1121, 7, 167, 'Og Herren kunngjorde at Han til dommens dag vil sende over dem slike som ville hjemsøke dem med hard straffedom. Herren er snar med straffen, men også tilgivende, nåderik.'),
(1122, 7, 168, 'Vi har delt dem opp i samfunn på jorden, noen rettferdige, andre av annen beskaffenhet. Vi har satt dem på prøve med godt og ondt, så de måtte vende om.'),
(1123, 7, 169, 'Det fulgte etter dem etterkommere som arvet skriften, som tar det denne verden tilbyr, og sier: «Det blir tilgitt oss.» Og kommer det mer av samme sorten, så tar de imot. Er da ikke skriftens forpliktelse avkrevet dem, at de ikke skal si annet om Gud enn sannhet? Og de har studert det som står i den? Evighetens hjem er bedre for de gudfryktige, vil dere da ikke forstå –,'),
(1124, 7, 170, 'for dem som har grepet fast i skriften, og forretter bønnen. Sannelig, Vi lar ikke de rettskafnes lønn gå tapt!'),
(1125, 7, 171, 'Og da Vi rystet fjellet over dem som var det en teltduk, og de trodde det ville falle over dem! «Hold fast ved det Vi har gitt dere! Kom i hu det som der står! – så dere må bli gudfryktige!»'),
(1126, 7, 172, 'En gang lot Herren fremstå etterslekten fra Adams barns lender. Han tok dem til vitner overfor seg selv: «Er ikke Jeg deres Herre?» De svarte: «Jo, vi stadfester det!» Dette for at dere ikke på oppstandelsens dag skal si: «Dette var vi sannelig ikke oppmerksomme på!»'),
(1127, 7, 173, 'Eller si: «Våre fedre hadde avguder før, og vi var etterkommere som fulgte etter dem. Vil Du da ødelegge oss for det de gjorde som fór med intethet?»'),
(1128, 7, 174, 'Slik forklarer Vi ordet, at de må vende om.'),
(1129, 7, 175, 'Fortell dem historien om ham som Vi gav Vårt ord, og som så trakk seg unna. Satan fulgte etter ham, og han kom på gale veier.'),
(1130, 7, 176, 'Om Vi hadde villet slik, kunne Vi hevet ham opp ved det. Men han var knyttet til jorden, og fulgte sine lyster. Han ligner hunden. Den lar tungen henge ut, enten du går løs på den eller lar den være. Slik er det med folk som forkaster Vårt ord. Så fortell historien, så de må tenke over det.'),
(1131, 7, 177, 'Det står ille til med det folk som forkastet Vårt ord, og gjorde urett som rammet dem selv.'),
(1132, 7, 178, 'Den Gud veileder, er på rett vei. De Han lar seile sin egen sjø, de er tapere.'),
(1133, 7, 179, 'Vi har skapt mange dsjinner og mennesker som ender i helvete, de har hjerter, men forstår ikke, øyne, men ser ikke, ører, men hører ikke. De er som kveg, ja, enda lenger borte. De er i sannhet likegyldige.'),
(1134, 7, 180, 'Gud har de skjønneste navn. Påkall Ham ved dem. La slike være som misbruker Hans navn. De skal visselig få belønning for det de gjør!'),
(1135, 7, 181, 'Blant dem Vi har skapt, er et folk som følger sannheten, og ved den øver rettferd.'),
(1136, 7, 182, 'Men de som forkaster Vårt ord, vil Vi la sige mer og mer bort,'),
(1137, 7, 183, 'uten at de vet det. Jeg gir dem respitt. Mitt trekk er sikkert.'),
(1138, 7, 184, 'Har de da ikke brukt tankene? Deres bysbarn er ikke besatt! Han er bare en klar advarer!'),
(1139, 7, 185, 'Tenker de da ikke på himlenes og jordens rike og alt Gud har skapt? Og at deres ende kanskje er nær? På hva slags forkynnelse vil de da etter dette tro?'),
(1140, 7, 186, 'De som Gud lar seile sin egen sjø, har ingen veileder. Han lar dem vandre i sin oppsetsighet på sin blinde ferd.'),
(1141, 7, 187, 'De spør deg om timen, om når den skal inntreffe. Si: «Kunnskap om dette beror hos Herren alene. Han alene lar den inntreffe i sin tid. Tungt hviler den over himmel og jord. Plutselig vil den komme over dere!» De spør deg som om du skulle være underrettet om den. Si: «Kunnskap om dette beror hos Gud alene, men folk flest vet ikke dette.»'),
(1142, 7, 188, 'Si: «Jeg kan ikke gjøre verken gagn eller skade for meg selv, alt ligger i Guds hånd. Kjente jeg det skjulte, kunne jeg skaffe meg mange goder, og vondt ville ikke kommet meg nær. Men jeg er bare en overbringer av advarsler og et godt budskap til et folk som tror.»'),
(1143, 7, 189, 'Han er det som har skapt dere av et enkelt individ. Av dette frembrakte Han dets make, at han skulle finne ro hos henne. Da han hadde besvangret henne, bar hun omkring en lett byrde. Da hun ble tyngre, påkalte de begge Gud, deres Herre: «Om Du gir oss et velskapt barn, vil vi virkelig være takknemlige.»'),
(1144, 7, 190, 'Men da Han hadde gitt dem et velskapt barn, så gav de Ham medguder for det Han hadde gitt dem. Men Gud er hevet over det de gir Ham som kompaniskap!'),
(1145, 7, 191, 'Gir de Ham som medguder slikt som intet skaper, men selv er skapt,'),
(1146, 7, 192, 'som ikke kan hjelpe, verken dem eller seg selv?'),
(1147, 7, 193, 'Om dere kaller de vantro til ledelsen, så følger de dere ikke. Det er det samme for dere om dere kaller dem eller tier.'),
(1148, 7, 194, 'Dere vantro, de dere påkaller utenom Gud, er tjenere, likesom dere! Bare påkall dem, og la dem svare dere om dere taler sant!'),
(1149, 7, 195, 'Har de vel føtter de kan gå med, hender de kan gripe med, øyne til å se med, eller ører til å høre med? Si: «Påkall deres medguder! Prøv så deres renker mot meg, og la meg ikke vente.'),
(1150, 7, 196, 'Min beskytter er Gud, som har åpenbart skriften. Han tar seg av de rettferdige.'),
(1151, 7, 197, 'Men de som dere påkaller utenom Ham, de kan ikke hjelpe dere, og heller ikke seg selv.»'),
(1152, 7, 198, 'Men om dere kaller slike vantro til ledelsen, så hører de ikke. Du ser dem stå og se på deg, uten å innse.'),
(1153, 7, 199, 'Vis overbærenhet! Påby det som anses godt! Vend deg bort fra de uvitende!'),
(1154, 7, 200, 'Og hvis en inngivelse fra Satan trenger seg på deg, så søk tilflukt hos Gud. Han hører, vet.'),
(1155, 7, 201, 'Om de gudfryktige anfalles av Satans horder, så la dem tenke etter, og de vil innse.'),
(1156, 7, 202, 'Og de trekker deres brødre med i villfarelsen, så de gir seg ikke.'),
(1157, 7, 203, 'Når du ikke har et ord å bringe dem, sier de: «Hvorfor har du ikke funnet på noe?» Si: «Jeg følger kun det som åpenbares meg av Herren.» Dette er innsikt fra Herren, og ledelse og nåde for folk som tror.'),
(1158, 7, 204, 'Når Koranen resiteres, så lytt dertil i stillhet, så dere må finne nåde.'),
(1159, 7, 205, 'Kom Herren i hu i ditt indre, i frykt og ydmykhet og uten høye ord, ved morgen og kveld. Vær ikke likegyldige.'),
(1160, 7, 206, 'De som er i Herrens nærhet, er ikke for overmodige til å tjene Ham. De synger Hans pris, og bøyer seg ydmykt ned for Ham.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Man spør deg vedrørende hærfangst. Si: «Hærfanget tilhører Gud og Sendebudet! Vis gudsfrykt, hold fred dere imellom, vis lydighet mot Gud og Hans sendebud, om dere er troende.»'),
(1162, 8, 2, 'Troende er de hvis hjerter skjelver når Guds navn nevnes, og som tiltar i troen når Hans ord leses for dem, de som forlater seg på Herren,'),
(1163, 8, 3, 'som forretter bønnen, og gir av det Vi har gitt dem.'),
(1164, 8, 4, 'Disse er de rette troende. De står høyt i rang hos Herren, med tilgivelse og verdig omsorg.'),
(1165, 8, 5, 'Likeledes førte Herren deg ut fra ditt hjem, ved sannheten, skjønt en del av de troende mislikte dette,'),
(1166, 8, 6, 'og diskuterte med deg om sannheten etter at den stod klart fast, som om de skulle drives i døden med åpne øyne.'),
(1167, 8, 7, 'Og den gang da Gud stilte dere i utsikt en av de to hæravdelinger, og dere ønsket at det måtte bli den minst slagkraftige! Men Gud ønsket å virkeliggjøre sannheten ved sitt ord, og skjære bort de vantros siste rest.'),
(1168, 8, 8, 'Dette for å virkeliggjøre sannheten og tilintetgjøre falskheten, selv om det var synderne imot.'),
(1169, 8, 9, 'Da dere anropte Herren om hjelp, så bønnhørte Han dere. «Jeg vil forstrekke dere med tusen engler som følger opp.»'),
(1170, 8, 10, 'Dette gjorde Gud som en god melding og for å stille deres hjerter til ro. Hjelp kommer bare fra Gud. Gud er mektig, vis.'),
(1171, 8, 11, 'Da Han lot søvn komme over dere, var det for å gjøre dere trygge. Og Han sendte vann fra himmelen for å rense dere med det, fjerne Satans styggedom, gi styrke til deres hjerter og få deres føtter til å holde stand.'),
(1172, 8, 12, 'Og Herren inspirerte englene: «Jeg er med dere! La de troende holde stand! I de vantros hjerter vil Jeg kaste skrekk! Gi dem et slag over nakken, og slå dem over deres fingre!»'),
(1173, 8, 13, 'Dette fordi de har brutt med Gud og Hans sendebud. For den som bryter med Gud og Hans sendebud, sannelig, Gud er streng med straffen.'),
(1174, 8, 14, 'Slik er det, så smak den. Ildens straff venter de vantro.'),
(1175, 8, 15, 'Dere som tror, når dere møter de vantro på fremmarsj, så vend dem ikke ryggen til flukt.'),
(1176, 8, 16, 'Den som denne dag vender dem ryggen til flukt, såfremt han ikke vender tilbake til striden, eller slutter seg til en annen avdeling, han har pådratt seg Guds vrede, og helvete blir hans herberge. Et sørgelig endelikt!'),
(1177, 8, 17, 'Det var ikke dere som hugget dem ned, men det var Gud som gjorde det! Da du kastet, var det ikke du, men Gud som kastet, for å prøve de troende med velgjerning. Gud hører, vet!'),
(1178, 8, 18, 'Så er det, Gud gjør de vantros renker virkningsløse.'),
(1179, 8, 19, 'Om dere vantro søker en avgjørelse, så har dere nå fått den. Om dere holder opp, så er dette bedre for dere. Om dere setter i gang på nytt, så gjør Vi det samme, og deres antall vil ikke hjelpe dere det ringeste, hvor stort det enn er. Gud er med de troende.'),
(1180, 8, 20, 'Dere som tror, adlyd Gud og Hans sendebud! Vend dere ikke bort fra Ham, når dere dog hører.'),
(1181, 8, 21, 'Vær ikke som de som sier: «Vi hører,» når de intet oppfatter.'),
(1182, 8, 22, 'De verste krek i Guds øyne er dem som er døve og stumme og intet forstår.'),
(1183, 8, 23, 'Hvis Gud hadde visst om noe godt i dem, ville Han latt dem oppfatte. Men om Han lot dem oppfatte, ville de likevel vendt seg bort og tatt avstand.'),
(1184, 8, 24, 'Dere som tror, hør på Gud og Sendebudet, når Han kaller dere til det som gir dere liv. Og vit at Gud er mellom et menneske og dets hjerte. Hos Ham skal dere samles.'),
(1185, 8, 25, 'Pass dere for ufred og trengsel, som aldeles ikke rammer bare dem som gjør urett blant dere! Vit at Gud er hard med straffen.'),
(1186, 8, 26, 'Kom i hu da dere var få og underlegne i landet, og fryktet for at folk kunne rydde dere av veien. Men Han skjermet dere og støttet dere ved Sin hjelp, og gav dere gode ting til underhold, så dere måtte vise takknemlighet!'),
(1187, 8, 27, 'Dere som tror, vær ikke utro mot Gud og Sendebudet. Vær ikke utro i det som er betrodd dere, når dere vet bedre.'),
(1188, 8, 28, 'Men vit at eiendom og barn er en prøvestein, og at en stor lønn finnes hos Gud.'),
(1189, 8, 29, 'Dere som tror, om dere frykter Gud, vil Han gi dere innsiktens kriterium, stryke over deres misgjerninger og tilgi dere. Gud har godhet i overmål.'),
(1190, 8, 30, 'En gang smidde de vantro renker mot deg, for å sette deg fast, eller jage deg bort. De la planer, men Gud la også planer! Og Gud er den beste planmaker.'),
(1191, 8, 31, 'Eller da Vårt ord ble resitert for dem, og de sa: «Vi hører det du sier. Om vi ville, kunne vi ytret noe lignende! Dette er jo ikke annet enn gamle historier.»'),
(1192, 8, 32, 'Eller da de sa: «Å Gud, om dette her er sannhet fra Deg, så la steiner regne over oss fra himmelen eller send over oss en smertefull straffedom!»'),
(1193, 8, 33, 'Men Gud ville nok ikke straffe dem mens du var blant dem. Og Gud ville heller ikke straffe dem når de bad om tilgivelse.'),
(1194, 8, 34, 'Men hva har de vel nå, for at Gud ikke skal straffe dem, når de skaper hindringer ved den hellige moské, uten at de har noe med den å gjøre? Kun de gudfryktige har med den å gjøre, men de fleste av dem vet ikke dette.'),
(1195, 8, 35, 'Deres bønn ved Huset var bare plystrelyder og klapping i hendene. Smak nå straffen for deres vantro!'),
(1196, 8, 36, 'De vantro gir av sin eiendom for å legge hindringer på Guds vei. Og det vil de fortsette med, så blir det dem til plage, og så blir de overvunnet. I helvete vil de vantro bli samlet.'),
(1197, 8, 37, 'Så Gud kan skille den dårlige fra den gode, legge de dårlige oppå hverandre, stable dem opp alle sammen, og kaste dem i helvete! Dette er taperne!'),
(1198, 8, 38, 'Si til de vantro at hvis de gir seg, skal det som har vært, være dem tilgitt. Men om de faller tilbake, så er praksis satt i tidligere slektledd!'),
(1199, 8, 39, 'Kjemp mot dem til det ikke lenger finnes forfølgelse, og all religion tilegnes Gud! Men om de gir seg, så ser Gud visselig det de gjør.'),
(1200, 8, 40, 'Og om de vender ryggen, så vit at Gud er beskytter for dere. Hvilken utmerket beskytter! Hvilken utmerket hjelper!'),
(1201, 8, 41, 'Og vit, at om dere tar hærfang, så tilkommer en femtedel Gud, Sendebudet, de nære slektninger, foreldreløse, fattige og veifarende, såfremt dere tror på Gud og det Vi har åpenbart for Vår tjener på avgjørelsens dag, den dag de to hærer møttes. Gud har makt til alle ting.'),
(1202, 8, 42, 'Da dere var på den nærmeste dalside og de på den bortre, og rytteriet under dere. Om dere hadde gjort en avtale, ville dere blitt uenige om avtalen. Så Gud avgjorde saken slik den ble ordnet, slik at den som falt, falt, og den som overlevde, overlevde, ved et klart vitnesbyrd. Gud hører, vet!'),
(1203, 8, 43, 'Den gang viste Gud deg dem i drømme som få. Hadde Han vist deg dem som mange, ville dere ha mistet motet, og kommet i strid om saken. Men Gud bevarte dere. Han vet hva som bor i hjertene.'),
(1204, 8, 44, 'Og Han lot dem fremstå som få i deres øyne da dere møttes, og dere som få i de andres øyne, slik at Gud kunne avgjøre saken slik den ble ordnet. Alle ting føres tilbake til Gud.'),
(1205, 8, 45, 'Dere som tror, når dere møter en hæravdeling, så stå fast. Og kom Gud stadig i hu, så det må gå dere godt.'),
(1206, 8, 46, 'Adlyd Gud og Hans sendebud! Avstå fra krangel med hverandre, så dere mister motet, og kampviljen blir borte! Vis standhaftighet! Gud er med de standhaftige.'),
(1207, 8, 47, 'Vær ikke som de som drog av gårde fra sine bosteder i overmot, for å vise seg for folk og legge hindringer på Guds vei. Men Gud har full kontroll over det de gjør!'),
(1208, 8, 48, 'Og da Satan gjorde deres adferd prydelig i deres øyne, og sa: «Ingen skal i dag kunne overvinne dere! Jeg står dere bi!» Men da de to hæravdelingene fikk se hverandre, tok han flukten og sa: «Jeg vil ikke ha noe med dere å gjøre! Jeg ser det dere ikke kan se! Jeg frykter Gud, og Gud er streng med straffen.»'),
(1209, 8, 49, 'En gang sa hyklerne og de med sykdom i hjertet: «Deres religion har ført dem galt av sted.» Men den som forlater seg på Gud, se, Gud er mektig, vis!'),
(1210, 8, 50, 'Om du kunne se når englene tar bort de vantro, slår dem i ansiktet og over ryggen: «Smak Ildens straff!'),
(1211, 8, 51, 'Dette for det dere har sendt i forveien til regnskapet! For Gud gjør ingen urett mot sine tjenere.»'),
(1212, 8, 52, 'Slik gikk det også med Faraos folk og de før dem som fornektet Guds ord. Gud tok dem fatt for deres synder! Gud er mektig og streng med straffen.'),
(1213, 8, 53, 'Dette fordi Gud ikke vil gjøre forandring i en godhet Han har vist et folk, før de forandrer seg selv. Gud hører, vet.'),
(1214, 8, 54, 'Det gikk slik med Faraos folk og de før dem som forkastet Herrens ord. Vi ødela dem for deres synders skyld. Vi druknet Faraos folk. De var alle urettferdige.'),
(1215, 8, 55, 'De verste krek overfor Gud er de som er vantro, og ikke vil tro,'),
(1216, 8, 56, 'de som du har sluttet pakt med, og som så bryter sin pakt gang på gang. Disse viser ingen gudsfrykt.'),
(1217, 8, 57, 'Så får du befatning med dem i krig, så innjag ved dem skrekk i deres følge, så de måtte komme til ettertanke.'),
(1218, 8, 58, 'Men frykter du svik fra et folk, så forkast også du pakten på samme måte. Sannelig, Gud liker ikke svikefulle!'),
(1219, 8, 59, 'De vantro må bare ikke tro at de kan komme unna. De kan ikke forpurre noe.'),
(1220, 8, 60, 'Gjør klar mot dem alt dere makter av stridskrefter og rytteri, så dere kan sette skrekk i Guds og deres fiende og andre i tillegg, som dere ikke vet om. Men Gud kjenner dem. Alt dere legger ut for Guds sak, får dere fullt igjen. Dere skal ikke lide urett.'),
(1221, 8, 61, 'Men ønsker de fred, så skal du gjøre det samme. Forlat deg på Gud! Han er den Hørende, den Allvitende.'),
(1222, 8, 62, 'Om de prøver å lure deg, så er Gud nok for deg. Han har støttet deg ved Sin hjelp, og ved de troende.'),
(1223, 8, 63, 'Han har forenet deres hjerter. Om du hadde gitt bort alt som finnes på jorden, hadde du ikke kunnet forene deres hjerter. Men Gud har forenet dem! Han er mektig, vis.'),
(1224, 8, 64, 'Hør, profet! Måtte Gud være nok for deg og for de troende som følger deg!'),
(1225, 8, 65, 'Hør profet! Anspor de troende til kamp! Finnes det tyve standhaftige blant dere, vil de vinne over to hundre! Er det hundre blant dere, vil de overvinne tusen av de vantro! For de er folk som intet forstår!'),
(1226, 8, 66, 'Men Gud har nå gjort det lettere for dere, for Han vet at det er svakhet hos dere. Så om det finnes hundre standhaftige blant dere, skal de overvinne to hundre, og om det finnes tusen, skal de overvinne to tusen, med Guds bifall. Gud er med de standhaftige.'),
(1227, 8, 67, 'Det passer seg ikke for en profet å ha krigsfanger før han har nedkjempet hele landet. Dere ønsker det jordelivet tilbyr, men Gud tar sikte på det hinsidige liv. Gud er mektig, vis.'),
(1228, 8, 68, 'Var ikke en forskrift fra Gud kommet tidligere, ville en stor straff rammet dere for det dere har tatt.'),
(1229, 8, 69, 'Så forbruk det dere har tatt som hærfang, som tillatt og godt. Og vis gudsfrykt! Gud er tilgivende, nåderik.'),
(1230, 8, 70, 'Hør, profet! Si til fangene i deres hender: «Hvis Gud vet om noe godt i deres hjerter, vil Han gi dere noe bedre enn det som er tatt fra dere! Og Han vil gi dere tilgivelse!» Gud er tilgivende, nåderik.'),
(1231, 8, 71, 'Men om de prøver å lure deg, så har de jo allerede bedratt Gud. Nå har Han gitt deg makt over dem. Gud vet, er vis.'),
(1232, 8, 72, 'De som har antatt troen og har forlatt sine hjem (i Mekka), som har satset liv og eiendom for Guds sak, og de som har gitt dem tilflukt og hjelp (i Medina), disse er hverandres venner! De som har antatt troen, men ikke har forlatt sine hjem, overfor dem har dere intet ansvar før de utvandrer. Men ber de dere om hjelp for troens skyld, da påligger det dere å hjelpe, unntatt mot et folk dere har forbund med. Gud ser det dere gjør!'),
(1233, 8, 73, 'De som er vantro, er også hverandres venner. Om dere ikke opptrer slik, blir det trengsel og stor ufred i landet.'),
(1234, 8, 74, 'De som har antatt troen, og har forlatt sine hjem og kjempet for Guds sak, og de som har gitt dem tilflukt og hjelp, disse er de sanne troende. De får tilgivelse og en verdig omsorg.'),
(1235, 8, 75, 'De som senere antar troen, og forlater sine hjem og kjemper ved deres side, de er av dere. Dog, de som er forenet ved slektsbånd, er hverandre nærmere ifølge Guds ord. Gud vet om alle ting!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Gud og Hans sendebud sier seg fri fra inngåtte paktsavtaler med avgudsdyrkerne.'),
(1237, 9, 2, '«Dra fritt omkring i landet i fire måneder, men vit at dere ikke kan forpurre Guds vilje, og at Gud vil gjøre de vantro til skamme.»'),
(1238, 9, 3, 'Dette er en erklæring fra Gud og Hans sendebud til folket på den store valfartens dag: «Gud sier seg fri fra avgudsdyrkerne, likeledes Hans sendebud! Hvis dere omvender dere, så er det det beste for dere. Hvis dere vender dere bort, så vit at dere ikke kan forpurre Guds vilje.» Still de vantro i utsikt en smertelig straff!'),
(1239, 9, 4, 'Dog unntatt de av avgudsdyrkerne som dere har gjort avtale med, og som ikke har forbrutt seg på noen måte, eller ytet andre hjelp mot dere. Overhold avtalen overfor dem til fristen er ute. Gud holder av de gudfryktige.'),
(1240, 9, 5, 'Men når de fredlyste måneder er til ende, så drep avgudsdyrkerne hvor dere finner dem, pågrip dem, beleir dem, legg bakhold for dem overalt! Men hvis de omvender seg, forretter bønnen og betaler det rituelle bidrag, så la dem dra sin vei. Gud er tilgivende, nåderik.'),
(1241, 9, 6, 'Hvis noen av avgudsdyrkerne ber deg om beskyttelse, så gi ham det, så han kan få høre Guds ord. Før ham så et sted hvor han har sikkerhet. Dette fordi de er folk som intet vet.'),
(1242, 9, 7, 'Hvordan skulle vel en paktsavtale bestå mellom avgudsdyrkerne og Gud og Hans sendebud, unntatt dem dere sluttet pakt med ved den hellige moské? Så lenge de er redelige mot dere, så vær redelige mot dem. Gud holder av de gudfryktige.'),
(1243, 9, 8, 'Men hvordan? Hvis de får overtaket, respekterer de verken bånd eller avtale! De snakker dere etter munnen, men er avvisende i sine hjerter! De fleste av dem er ugudelige.'),
(1244, 9, 9, 'De har byttet bort Guds ord for en ussel pris, og lagt hindringer på hans vei! Ondt er sannelig det de gjør!'),
(1245, 9, 10, 'De respekterer verken bånd eller avtale med en troende. De går sannelig over grensen!'),
(1246, 9, 11, 'Men hvis de omvender seg, forretter bønnen og betaler det rituelle bidrag, så er de deres brødre i religionen. Vi klargjør ordet for folk som har kunnskap.'),
(1247, 9, 12, 'Men hvis de bryter sine eder etter avtaleinngåelsen og stikker deres religion i angrep, så bekjemp vantroens ledere, for dem består ingen edsforpliktelser, så de måtte gi seg!'),
(1248, 9, 13, 'Skulle dere ikke bekjempe folk som brøt sine eder, og ønsket å fordrive Sendebudet, og det var de som begynte mot dere første gang? Er dere redde for dem? Gud er mer berettiget til at dere er redde for Ham, om dere er troende!'),
(1249, 9, 14, 'Bekjemp dem! Så Gud kan straffe dem ved dere, og gjøre dem til skamme! Så Han kan hjelpe dere mot dem, og gi helbredelse til de troendes bryst,'),
(1250, 9, 15, 'og fjerne all vrede fra deres hjerter. Gud vender seg i nåde mot hvem Han vil. Gud vet, er vis.'),
(1251, 9, 16, 'Trodde dere vel at dere skulle bli latt i fred, når Gud ennå ikke vet hvem av dere som har kjempet, og ikke slutter vennskap med noen annen enn Gud, Hans sendebud og de troende?'),
(1252, 9, 17, 'Det passer seg ikke for avgudsdyrkere å ta seg av Guds moskeer så lenge de personlig fremviser vantro. Disse er det hvis gjerninger er verdiløse. I Ilden skal de være og bli.'),
(1253, 9, 18, 'De alene skal ta seg av Guds moskeer som tror på Gud og dommens dag, som forretter bønnen, betaler det rituelle bidrag og ikke frykter andre enn Gud. Disse vil kanskje finne rett vei.'),
(1254, 9, 19, 'Vil dere likestille ordning av vann til pilegrimene og betjening av den hellige moské med den som tror på Gud og dommens dag og kjemper for Guds sak? For Gud er de ikke like! Gud rettleder ikke urettferdige.'),
(1255, 9, 20, 'De som har antatt troen, har utvandret og kjempet for Guds sak med liv og eiendom, de står høyere i rang hos Gud. Det er disse som vinner frem.'),
(1256, 9, 21, 'Herren bebuder dem Sin nåde og Sitt velbehag! Dem venter paradisets haver med salighet som varer!'),
(1257, 9, 22, 'Der skal de være og bli. Sannelig, hos Gud finner en stor lønn.'),
(1258, 9, 23, 'Dere som tror, slutt dere ikke i avhengighet til deres fedre og brødre om de foretrekker vantro fremfor tro. De av dere som slutter seg til dem, handler urett.'),
(1259, 9, 24, 'Si: «Om deres fedre, sønner, brødre, hustruer, nærmeste slekt, eiendom dere har ervervet, handel som står på spill, boliger dere er glad i, om dette er dere kjærere enn Gud og Hans sendebud og å kjempe for Hans sak, så bare vent til Gud iverksetter sin beslutning! Gud rettleder ikke urettferdige folk.»'),
(1260, 9, 25, 'Gud har bistått dere på mange valplasser. I Hunaynkampen, da dere var selvsikre på grunn av deres antall, så hjalp det dere ingenting. Landet i all sin utstrekning snørte seg sammen for dere, og dere vendte om til flukt.'),
(1261, 9, 26, 'Da senket Gud guddommelig nærvær og ro over Sitt sendebud og de troende. Og Han sendte hærskarer dere ikke så, og refset vantro. Det er de vantros belønning.'),
(1262, 9, 27, 'Senere hen vender Gud seg i nåde mot den Han vil. Gud er tilgivende, nåderik.'),
(1263, 9, 28, 'Dere som tror, de vantro er en styggedom! De må ikke komme nær den hellige moské etter dette deres år! Om dere frykter magre tider, så vil Gud gi dere det dere trenger av sin overflod, om Han vil det slik. Gud vet, er vis.'),
(1264, 9, 29, 'Bekjemp dem som ikke tror på Gud og dommens dag, som ikke forbyr det Gud og Hans sendebud har erklært forbudt, og som ikke bekjenner sannhets religion av skriftfolkene, til de punger ut med tributt i ydmykhet.'),
(1265, 9, 30, 'Jødene sier: «Ezra er Guds sønn,» og de kristne sier: «Messias er Guds sønn.» Dette er faktisk det de sier, og de tar etter det som vantro før dem hevdet. Måtte Gud ta dem fatt! Hvor de er forvridd!'),
(1266, 9, 31, 'De har tatt sine rabbier og munker til herrer utenom Gud, og Messias, Marias sønn! Men de ble pålagt å tjene én Gud! Det er ingen gud unntatt Ham! Priset være Han, hevet over det de setter ved Hans side!'),
(1267, 9, 32, 'De vil blåse ut Guds lys, men Gud vil fullbyrde Sitt lys, selv om det er de vantro imot.'),
(1268, 9, 33, 'Han er det som sendte Sitt sendebud med ledelse og sannhets religion, for å la den stå frem overfor all religion, selv om det er avgudsdyrkerne imot.'),
(1269, 9, 34, 'Dere som tror, mange rabbier og munker fortærer folks eiendom til ingenting, og skaper hindringer på Guds vei. De som samler seg skatter av gull og sølv, og ikke bruker dem for Guds sak, for dem skal du bebude en smertelig straff,'),
(1270, 9, 35, 'den dag da skattene gjøres glødende i helvetes ild, og de brennemerkes med dem på pannen, sidene og ryggen: «Dette er det dere har samlet til dere selv! Smak nå det dere har samlet i lader!»'),
(1271, 9, 36, 'Gud har satt månedenes antall til tolv i Guds bok den dag Han skapte himlene og jorden: Fire av dem er fredlyste, dette er rett religion, gjør ingen urett mot hverandre i dem. Men kjemp alle mot de vantro, så som de alle kjemper mot dere, og vit at Gud er med de gudfryktige.'),
(1272, 9, 37, 'Tidsforskyvning ved en ekstra måned er et overmål av vantro, hvorved de vantro føres vill. Ett år gjør de den ikke fredlyst, et annet år fredlyst, for å få samsvar med det antall som Gud har fredlyst, men slik profanerer de det Gud har fredlyst. Deres onde gjerninger synes prydelige for dem. Men Gud rettleder ikke vantro folk.'),
(1273, 9, 38, 'Dere som tror, hva er i veien med dere, når det sies til dere: «Dra ut for Guds sak,» så synker dere sammen. Er dere mer tilfreds med jordelivet enn det hinsidige? Men jordelivets gleder er bare småting i det hinsidige.'),
(1274, 9, 39, 'Om dere ikke drar ut, vil Han refse dere med en smertelig straff og bytte dere ut med et annet folk. Dere skader ikke Ham det minste. Gud har makt til alle ting.'),
(1275, 9, 40, 'Om dere ikke yter profeten hjelp, så har Gud tidligere gitt ham hjelp, da de vantro fordrev ham, en av to, og han sa til sin følgesvenn, da de var i grotten: «Vær ikke bedrøvet! Gud er med oss.» Da senket Gud guddommelig nærvær og ro over ham og styrket ham med hærskarer dere ikke så, og gjorde de vantros ord underlegent, mens Guds ord ble ovenpå. Gud er mektig, vis.'),
(1276, 9, 41, 'Dra ut, lette eller tunge, og gjør innsats for Guds sak med liv og eiendom! Dette er det beste for dere, om dere bare visste.'),
(1277, 9, 42, 'Hadde det dreiet seg om lettvunnen vinning og en kort ekspedisjon, så ville de nok fulgt deg. Men avstanden forekom dem stor. Og de vil sverge ved Gud: «Hadde vi kunnet, ville vi dratt ut med dere.» De styrter seg i fortapelsen. Gud vet at de lyver.'),
(1278, 9, 43, 'Måtte Gud unnskylde deg! Hvorfor gav du dem fritagelse før det ble klart for deg hvem som snakket sant, og du visste hvem som løy?'),
(1279, 9, 44, 'De som tror på Gud og på dommens dag, ber deg ikke om fritagelse for å satse liv og eiendom. Gud kjenner de gudfryktige.'),
(1280, 9, 45, 'Kun de ber om fritagelse som ikke tror på Gud og på dommens dag, hvis hjerter er fylt av tvil, så de i sin tvil vakler hit og dit.'),
(1281, 9, 46, 'Om de hadde villet dra ut, ville de ha gjort forberedelser til det. Men Gud var imot at de skulle dra, så han inngjød i dem slapphet, og det ble sagt: «Sitt hjemme sammen med hjemmesitterne!»'),
(1282, 9, 47, 'Hadde de dratt ut med dere, ville de bare øket deres vanskeligheter, og drevet på for å undergrave disiplinen. Blant dere er de som ville vært lutter øre overfor dem, men Gud kjenner illgjerningsmennene.'),
(1283, 9, 48, 'De har tidligere søkt å skape uro og har snudd om tingene for deg, inntil sannheten inntraff og Guds anordning ble klar, hvor meget det enn var dem imot.'),
(1284, 9, 49, 'Noen av dem sier: «Hold meg unnskyldt! Utsett meg ikke for fristelse.» Har de ikke allerede falt i fristelse? Sannelig, helvete vil omslutte de vantro!'),
(1285, 9, 50, 'Hvis noe godt overgår deg, plager det dem. Hvis ulykke rammer deg, sier de: «Vi har for lengst tatt våre forholdsregler.» Og de vender seg bort i skadefryd.'),
(1286, 9, 51, 'Si: «Intet overgår oss unntatt det Gud har bestemt for oss. Han er vår beskytter, og på Gud skal de troende forlate seg.»'),
(1287, 9, 52, 'Si: «Forventer dere annet for oss enn en av de to gode ting (seier eller paradiset)? Vi derimot forventer for dere at Gud vil ramme dere med en refselse, direkte fra Ham eller gjennom oss. Så bare vent! Vi venter sammen med dere.»'),
(1288, 9, 53, 'Si: «Gi deres gave, villig eller motvillig, det vil ikke bli akseptert! Dere er ugudelige mennesker.»'),
(1289, 9, 54, 'Det er kun dette som hindrer at deres gave blir mottatt, at de ikke tror på Gud og Hans sendebud, viser latskap i bønnen, og gir sin gave med motvilje.'),
(1290, 9, 55, 'La deg ikke imponere av deres eiendom og barn! Gud ønsker bare å gi dem en prøvelse derved i det jordiske liv, så de tar avskjed mens de er vantro.'),
(1291, 9, 56, 'De sverger ved Gud at de er av dere, men de er ikke av dere. De er fryktsomme folk.'),
(1292, 9, 57, 'Om de fant et tilfluktssted eller huler eller krypinn, så ville de begi seg dit i hast.'),
(1293, 9, 58, 'Noen av dem kritiserer deg vedrørende almissegavene. Blir de tildelt noe av dem, er de vel fornøyd, hvis de intet får, blir de sinte.'),
(1294, 9, 59, 'Hadde de bare sagt seg fornøyde med det Gud og Hans sendebud gav dem, og sagt: «Gud er nok for oss! Gud vil gi oss av Sin godhet, likeledes Hans sendebud. Til Gud står vår hu!»'),
(1295, 9, 60, 'Almissegavene er for de fattige og trengende, for dem som arbeider med dem, for dem hvis hjerter skal forsones, til frikjøping av slaver og skyldnere, til fremme av Guds sak og til veifarende. Dette er Guds forskrift. Gud vet, er vis.'),
(1296, 9, 61, 'Noen av dem gjør profeten ondt ved å si: «Han er lutter øre for alt!» Si: «Han har øre for alt som er godt for dere. Han tror på Gud og han har tro på de troende. Han er en velsignelse for dem av dere som tror.» De som forulemper Guds sendebud, vil møte en smertelig straff.'),
(1297, 9, 62, 'De sverger fremfor dere ved Guds navn for å gjøre dere tilfredse, men Gud og Hans sendebud ser det rettere at de vil gjøre Ham tilfreds, om de er troende!'),
(1298, 9, 63, 'Vet de da ikke at den som trosser Gud og Hans sendebud, har helvetes ild i vente? Der skal han være og bli. Dette er den store fornedrelse.'),
(1299, 9, 64, 'Hyklerne frykter at en åpenbaring om dem skal komme, som forteller dem det som bor i deres hjerter. Si: «Bare driv ap! Gud vil bringe frem det dere frykter!»'),
(1300, 9, 65, 'Men om du spør dem, så vil de si: «Det var bare tomt prat og spøk.» Si: «Var det Gud, Hans ord og Hans sendebud dere drev ap med?»'),
(1301, 9, 66, 'Unnskyld dere ikke! Dere falt fra etter at dere antok troen. Om Vi unnskylder noen av dere, så vil Vi straffe andre fordi de var syndere.'),
(1302, 9, 67, 'Hyklerne, menn og kvinner, er samme sort. De forordner det urette og forbyr det rette, og deres hender er lukkede. De har glemt Gud, og Han har glemt dem. Hyklerne er ugudelige mennesker.'),
(1303, 9, 68, 'Gud har lovet hyklerne, menn og kvinner, og de vantro, helvetes ild. Der skal de være og bli. Dette er passende for dem. Guds forbannelse hviler over dem, og de har en varig straff i vente.'),
(1304, 9, 69, 'Som de som var før dere. De var mektigere enn dere, hadde større eiendom og flere barn, og de nøt sin lodd. Dere nyter også deres lodd, som de som var før dere nøt sin. Dere pludrer i tomt prat som de gjorde. Deres gjerninger er verdiløse i denne verden og den hinsidige. Disse er taperne.'),
(1305, 9, 70, 'Hadde de ikke fått beretningen om dem som var før dem? Om Noas folk, og om Ad og Thamod, om Abrahams folk, folket i Midian og de sammenstyrtede byer? Deres sendebud kom til dem med klar beskjed, for Gud ville ikke gjøre dem urett. Men deres urett rammet dem selv.'),
(1306, 9, 71, 'De troende, menn og kvinner, har venners ansvar for hverandre. De forordner det rette og forbyr det urette. De forretter bønnen og betaler det rituelle bidrag. De adlyder Gud og Hans sendebud. Disse vil Gud vise nåde! Gud er mektig, vis.'),
(1307, 9, 72, 'Gud har lovet de troende, menn og kvinner, paradisets haver, hvor bekker sildrer, og der skal de være og bli, herlige boliger i Edens haver. Og Guds velbehag, større enn alt. Dette er den store seier.'),
(1308, 9, 73, 'Hør, profet! Strid mot de vantro og hyklerne! Vær hard mot dem! Helvete blir deres herberge. Et sørgelig endelikt!'),
(1309, 9, 74, 'De sverger ved Gud at de intet har sagt, men sannelig har de sagt vantroens ord, og vist vantro, etter at de antok islam. De tok sikte på noe de ikke kunne oppnå, og mislikte det at Gud og Hans sendebud beriket dem av sin overflod. Hvis de omvender seg, vil det være til beste for dem. Hvis de vender seg bort, vil Gud ramme dem med en smertelig straff i denne verden og den hinsidige, og de vil ikke ha en venn eller hjelper på jorden.'),
(1310, 9, 75, 'Det finnes de som lover Gud: «Hvis Han gir oss av Sin overflod, skal vi sannelig gi gode gaver og være rettskafne!»'),
(1311, 9, 76, 'Men når Han så gir dem noe av Sin overflod, er de gjerrige med det og vender seg bort i avstandtagen.'),
(1312, 9, 77, 'Så la Han hykleriet i deres hjerter frem til den dag de skal møte Ham. Dette fordi de sviktet Gud i det de hadde lovet Ham, og fordi de løy.'),
(1313, 9, 78, 'Visste de ikke at Gud kjenner deres hemmeligheter og overlegninger, at Gud kjenner vel til det skjulte!'),
(1314, 9, 79, 'De kritiserer de troende, som er villige når det gjelder almissegavene, og de som kun har sin innsats å tilby, håner de. Måtte Gud håne dem! De har en smertelig straff i vente.'),
(1315, 9, 80, 'Be om tilgivelse for dem eller la det være. Om du så ber sytti ganger, Gud vil aldri tilgi dem. Dette fordi de fornekter Gud og Hans sendebud. Gud leder ikke ugudelige folk.'),
(1316, 9, 81, 'De som ble latt tilbake, gledet seg over å sitte igjen bak Guds sendebud. De hadde motvilje mot å satse liv og eiendom for Guds sak. De sa: «Dra ikke ut i heten!» Si: «Helvetes ild er hetere!» Om de bare kunne forstå.'),
(1317, 9, 82, 'La dem le lite og gråte meget for det de har pådratt seg!'),
(1318, 9, 83, 'Om Gud lar deg vende hjem til en flokk av dem, og de så ber deg om å få dra ut, så si: «Dere skal aldri dra ut med meg, og aldri kjempe mot en fiende ved min side! Det passet dere å sitte hjemme første gang, så bli bare sittende med dem som blir tilbake!»'),
(1319, 9, 84, 'Forrett aldri bønn over noen av disse som er døde, og stå ikke ved hans grav. De fornektet Gud og Hans sendebud, og døde som ugudelige.'),
(1320, 9, 85, 'La deg ikke imponere av deres eiendom og barn. Gud vil bare gi dem prøvelse derved i denne verden, så de tar avskjed mens de er vantro.'),
(1321, 9, 86, 'Og når en åpenbaring kommer: «Tro på Gud, og strid ved Hans sendebuds side,» da ber de velstående om fritagelse, og sier: «La oss bli hos hjemmesitterne.»'),
(1322, 9, 87, 'De er fornøyde med å bli hos dem som blir tilbake. Deres hjerter er forseglet, så de ingenting forstår.'),
(1323, 9, 88, 'Men Sendebudet, og de som tror sammen med ham, satser liv og eiendom. Disse har alt godt i vente. Disse vil det gå godt.'),
(1324, 9, 89, 'Gud har beredt for dem paradisets haver, hvor bekker sildrer. Her skal de være og bli. Dette er den store seier.'),
(1325, 9, 90, 'Ørkenaraberne kom med unnskyldninger og bad om fritagelse, og de som løy for Gud og Hans sendebud, ble sittende hjemme. En smertelig straff vil ramme de vantro blant dem!'),
(1326, 9, 91, 'Man kan intet bebreide svake og syke, og slike som intet har å bidra med, såfremt de er lojale mot Gud og Hans sendebud. Man kan ikke kritisere dem som opptrer rett. Gud er tilgivende, nåderik.'),
(1327, 9, 92, 'Og heller ikke de som kommer til deg for at du skal utruste dem, når du må si: «Jeg har ingenting å utruste dem,» – og som snur med tårer i øynene av sorg over at de intet kan satse.'),
(1328, 9, 93, 'Kritikk rammer bare dem som ber seg fritatt, skjønt de er rike. De er fornøyde med å være blant dem som blir tilbake. Gud har forseglet deres hjerter så de intet forstår.'),
(1329, 9, 94, 'De vil komme med unnskyldninger når dere kommer tilbake til dem. Si: «Kom ikke med unnskyldninger, vi tror dere ikke! Gud har latt oss få vite hvordan det har seg med dere. Gud vil se hva dere gjør, og Hans sendebud likeledes. Så vil dere bli sendt tilbake til Ham som kjenner det skjulte og det åpenbare. Han vil la dere få vite hva dere har gjort.»'),
(1330, 9, 95, 'De vil besverge dere ved Gud når dere vender tilbake til dem, at dere skal la dem være i fred. Så la dem være i fred. De er en vederstyggelighet, og helvete vil bli deres herberge, for det de har pådratt seg.'),
(1331, 9, 96, 'De vil besverge dere, at dere må akseptere dem. Men om dere aksepterer dem, så vil Gud aldri akseptere ugudelige folk.'),
(1332, 9, 97, 'Ørkenaraberne er de verste i vantro og hykleri, og de mest troendes til ikke å kjenne de lover som Gud har åpenbart for Sitt sendebud. Gud vet, er vis.'),
(1333, 9, 98, 'Noen av ørkenaraberne anser det de yter for religionen som bøter, og venter bare på at lykken skal snu seg for dere. For dem selv skal det bli en dårlig vending! Gud hører, vet.'),
(1334, 9, 99, 'Men blant ørkenaraberne finnes også slike som tror på Gud og på dommens dag, og som anser det de yter som en offerhandling for Guds åsyn, som Sendebudets bønner. Ja, det er en offerhandling for dem, og Gud vil føre dem inn i Sin nåde. Gud er tilgivende, nåderik.'),
(1335, 9, 100, 'Foregangsmennene, de første av utvandrerne og hjelperne, og de som fulgte dem på beste måte, med dem er Gud tilfreds, og de er tilfredse med Ham. Han har beredt for dem paradisets haver, hvor bekker sildrer. Der skal de være og bli. Det er den store seier.'),
(1336, 9, 101, 'Noen av ørkenaraberne omkring dere er hyklere, og noen av Medina-folket, helt hengitt i hykleri. Du kjenner dem ikke, men Vi vet om dem. Vi vil refse dem dobbelt opp, og så vil de bli overgitt til den store straffen.'),
(1337, 9, 102, 'Det er andre som erkjenner sine synder. De har blandet sammen gode gjerninger og slette. Kanskje Gud vil vende seg mot dem i nåde. Gud er tilgivende, nåderik.'),
(1338, 9, 103, 'Pålegg dem en gave, hvorved du kan rense dem og lutre dem. Fremsi bønn for dem. Dine bønner gir dem ro. Gud hører, vet.'),
(1339, 9, 104, 'Vet de da ikke at Gud mottar anger og omvendelse fra sine tjenere, og tar imot deres gaver, at Gud er den Tilgivende, den Nåderike?'),
(1340, 9, 105, 'Si: «Virk! Og Gud vil se dere virke, og Hans sendebud, og de troende. Dere vil bli brakt tilbake til Ham som kjenner det skjulte og det åpenbare, og Han vil la dere få vite hva dere har gjort.»'),
(1341, 9, 106, 'Og det er andre som er henvist til Guds avgjørelse, om Han vil straffe dem, eller vende seg mot dem i nåde. Gud vet, er vis.'),
(1342, 9, 107, 'Og så var det de som laget seg en egen moské, for skade, vantro og splittelse blant de troende, og som tilholdssted for dem som førte krig mot Gud og Hans sendebud. De vil sverge: «Vi hadde bare godt i sinne!» Men Gud bevitner at de i sannhet er løgnere!'),
(1343, 9, 108, 'Stå aldri frem i den. En moské som er grunnlagt på gudsfrykt fra første dag, er det rettere at du står frem i. I den finnes folk som ønsker å rense deg. Gud liker dem som renser seg.'),
(1344, 9, 109, 'Hvem er vel best! Den som har grunnet sin bygning på frykt for Gud og på Hans velbehag, eller den som har satt sin bygning på kanten av en undergravet sandbakke, som styrter ned med ham i helvetes ild? Gud leder ikke illgjerningsmenn.'),
(1345, 9, 110, 'Den bygningen de har bygget, vil ikke opphøre å skape tvil i deres hjerter før deres hjerter slites i stykker. Gud vet, er vis.'),
(1346, 9, 111, 'Gud har kjøpt de troendes liv og eiendom mot paradisets have. De kjemper for Guds sak, de feller eller felles. Dette er et bindende løfte for Ham, i loven, evangeliet og Koranen. Og hvem holder sin forpliktelse bedre enn Gud? Føl glede ved den handel dere har gjort! Det er den store seier!'),
(1347, 9, 112, 'De som omvender seg, som tjener og lovpriser, viser måtehold, som bøyer seg og kaster seg ned i bønn, som forordner det rette og forbyr det urette, som overholder de grenser Gud har satt, forkynn for de troende et gledelig budskap!'),
(1348, 9, 113, 'Det passer seg ikke for profeten og de troende å be om tilgivelse for avgudsdyrkere, selv om det er nær slekt, etter at det er blitt klart for dem at de blir helvetes beboere.'),
(1349, 9, 114, 'Abraham bad kun om tilgivelse for sin far på grunn av et løfte som han hadde gitt ham. Men da det ble klart for ham at han var Guds fiende, sa han seg løs fra ham. Abraham var medlidende, mild.'),
(1350, 9, 115, 'Gud sender ikke folk på villstrå etter å ha gitt dem ledelse, før Han har gjort det klart for dem hva de skal passe seg for. Gud vet om alle ting.'),
(1351, 9, 116, 'Gud tilhører herredømmet over himlene og jorden. Han gir liv og død. Dere har utenom Gud ingen venn eller hjelper.'),
(1352, 9, 117, 'Gud har vendt Seg i nåde mot profeten, og mot utvandrerne og hjelperne som fulgte ham i trengselens time, etter at hjertene hos noen av dem var nær ved å svikte. Så tilgav Han dem. Han er i sannhet mild og nåderik mot dem.'),
(1353, 9, 118, 'Så også mot de tre som ble latt tilbake. Da landet i all sin utstrekning syntes dem trangt, og deres hjerter snørte seg sammen, og de mente at det var ingen redning fra Gud unntatt hos Gud, så tilgav Han dem for at de skulle omvende seg. Gud er den Tilgivende, den Nåderike.'),
(1354, 9, 119, 'Dere som tror! Vis gudsfrykt, og hold dere til de sannferdige!'),
(1355, 9, 120, 'Det passer seg ikke for Medinafolket eller ørkenaraberne omkring dem å bli tilbake når Guds sendebud drar ut, og heller ikke å sette hensynet til seg selv fremfor ham. Dette fordi verken tørst, strabaser eller sult vil ramme dem for Guds sak, og heller ikke skal de ta skritt som opphisser de vantro, eller skades av en fiende uten at dette godskrives dem som fortjenstfull sak. Gud lar ikke deres lønn gå tapt som handler vel.'),
(1356, 9, 121, 'De får ingen utlegg, stort eller lite, ingen daler å krysse, uten at det godskrives dem, så Gud kan belønne dem for deres beste handlinger.'),
(1357, 9, 122, 'De troende skal ikke dra av gårde alle sammen. Men om en gruppe av hver avdeling drog av gårde for å instrueres i religionen, så de kan advare sitt folk når de vender tilbake, så de kan være på vakt?'),
(1358, 9, 123, 'Dere som tror, bekjemp de vantro som bor i nærheten! La dem finne hardhet i dere! Vit at Gud er med de gudfryktige.'),
(1359, 9, 124, 'Når en åpenbaring kommer, er det noen som sier: «Hvem av dere har fått sin tro øket av dette?» De som tror, har fått sin tro øket, og de gleder seg.'),
(1360, 9, 125, 'Men de som har sykdom i sine hjerter, dem har den gitt øket styggedom i tillegg til deres styggedom, og de dør som vantro.'),
(1361, 9, 126, 'Ser de da ikke at de hvert år blir satt på prøve en gang eller to? Men de omvender seg ikke, og lar seg ikke formane.'),
(1362, 9, 127, 'Når en åpenbaring kommer, ser de på hverandre: «Er det noen som ser oss?» Så snur de seg bort. Gud har vendt deres hjerter bort, for de er folk som intet begriper.'),
(1363, 9, 128, 'Det er nå kommet til dere et Sendebud fra deres egen krets. Det gjør ham ondt når det går galt med dere, han har omsorg for dere. Mot de troende viser han godhet og barmhjertighet.'),
(1364, 9, 129, 'Men vender de seg bort, så si: «Gud er nok for meg. Det er ingen gud unntatt Ham! På Ham forlater jeg meg! Han er Herren over den store trone!»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Dette er den vise skrifts ord.'),
(1366, 10, 2, 'Er det da en merkverdighet for folk at Vi har inspirert en mann fra deres krets: «Advar menneskene, og bebud for dem som tror at de har et sant fotfeste hos sin Herre.» De vantro sier: «Dette er tydeligvis en trollmann.»'),
(1367, 10, 3, 'Deres Herre er Gud, Han som skapte himlene og jorden på seks dager. Så tok Han plass på tronen for å ordne styringen. Ingen kan legge inn ord hos Ham, uten etter Hans godkjenning. Dette er Gud, deres Herre, så tjen Ham! Vil dere da ikke la dere formane?'),
(1368, 10, 4, 'Til Ham skal dere alle vende tilbake. Dette er Guds sanne løfte. Han frembringer skapningen, så gjør Han det på ny, for å belønne rettferdig dem som tror og handler rettskaffent. De vantro har i vente kokende vann som drikke og en smertelig straff, fordi de var vantro.'),
(1369, 10, 5, 'Han er det som har latt solen skinne og månen lyse, og bestemt dens posisjoner for at dere skulle kjenne årenes antall og tidsregningen. Gud har skapt dette med alvor og hensikt for å klargjøre tegnene for folk som har kunnskap.'),
(1370, 10, 6, 'I nattens og dagens veksling, og i alt som Gud har skapt i himlene og på jord, i dette er jærtegn for folk med gudsfrykt.'),
(1371, 10, 7, 'De som ikke ser frem til å møte Oss, og er fornøyd med jordelivet og finner seg til rette med det, og som ikke gir akt på Vårt ord,'),
(1372, 10, 8, 'de havner i Ilden for det de har pådratt seg.'),
(1373, 10, 9, 'Men de som tror og handler rettskaffent, dem vil Herren lede for deres tros skyld. Fremfor dem vil bekker sildre i lykksalighetens haver.'),
(1374, 10, 10, 'Der vil de rope ut: «Ære være Deg, O Gud!» Og det er deres hilsen: «Fred!» Og til slutt: «Lovet være Gud, all verdens Herre!»'),
(1375, 10, 11, 'Om Gud skulle påskynde ulykker for menneskene som de ber påskyndet goder, så ville det være ute med dem. Men Vi lar dem som ikke ser frem til å møte Oss, rave blindt videre i sin oppsetsighet.'),
(1376, 10, 12, 'Når ulykken rammer et menneske, så påkaller han Oss, liggende, sittende, stående. Men tar Vi bort hans ulykke, så går han sin vei, som om han aldri hadde påkalt Oss for ulykken som rammet ham. Slik fortoner deres adferd seg prydelig for de lettsindige.'),
(1377, 10, 13, 'Vi har utslettet generasjoner før dere, da de handlet urett. Deres sendebud kom til dem med klar beskjed, men de ville ikke tro. Slik belønner vi syndige folk.'),
(1378, 10, 14, 'Så gjorde Vi dere til deres etterfølgere i landet, slik at Vi kunne få se hvordan dere ville handle.'),
(1379, 10, 15, 'Men når Vårt ord tydelig og klart oppleses for dem, så sier de som ikke ser frem til å møte Oss: «Kom med en annen Koran enn denne, eller gjør forandring i den!» Si: «Det står ikke til meg å gjøre forandringer i den på egen hånd! Jeg retter meg bare etter det som er åpenbart meg. Sannelig, jeg frykter en svær dags straff, om jeg skulle vise ulydighet mot Herren.»'),
(1380, 10, 16, 'Si: «Om Gud hadde villet det slik, hadde jeg ikke forkynt den for dere, og Han ville ikke gjort dere kjent med den. Jeg har jo bodd en mannsalder blant dere før den kom. Skjønner dere da ingenting?»'),
(1381, 10, 17, 'Hvem gjør større urett enn den som dikter opp løgn om Gud? Eller erklærer hans ord for løgn? Synderne vil det ikke gå godt!'),
(1382, 10, 18, 'Utenom Gud dyrker de slikt som verken skader eller gagner dem, og erklærer: «Disse taler vår sak hos Gud!» Si: «Vil dere fortelle Gud noe Han ikke vet i himlene og på jord?» Ære være Ham! Han er opphøyet over det de setter ved Hans side!'),
(1383, 10, 19, 'Menneskeheten utgjorde en gang ett (tros)samfunn. Så ble de uenige. Hadde det ikke foreligget et ord fra Herren, så ville deres uenighet vært opp- og avgjort.'),
(1384, 10, 20, 'De sier også: «Hvorfor er ikke et jærtegn sendt ham fra hans Herre?» Si: «Det skjulte er Guds sak. Så bare vent. Jeg venter sammen med dere!»'),
(1385, 10, 21, 'Lar Vi menneskene smake barmhjertighet etter at trengsel har rammet dem, så har de for seg renker mot Vårt ord. Si: «Gud er raskere når det gjelder renker! Våre utsendinger skriver ned det dere smir i hop.»'),
(1386, 10, 22, 'Han er det som lar dere ferdes på land og hav. Men når dere er om bord på skip som seiler av gårde med folk i god vind, og de føler glede ved det, så kommer storm, og bølgene reiser seg på alle kanter så de føler seg maktesløse. Da påkaller de Gud i religiøs oppriktighet overfor Ham! «Om Du redder oss fra dette, så vil vi i sannhet vise takknemlighet!»'),
(1387, 10, 23, 'Men når Han så har frelst dem, så begynner de igjen sin voldsferd på jorden på urettmessig vis. «Dere mennesker, deres voldsferd slår tilbake på dere selv! En kort nytelse i jordelivet, og så vender dere tilbake til Oss. Og Vi skal fortelle dere hva dere bedrev.»'),
(1388, 10, 24, 'Jordelivet kan sammenlignes med regn vi sender ned fra himmelen. Det gjennomtrenger jordens vekster som folk og fe lever av. Når så jorden har tatt på sin fineste stas og er pyntet, og dens beboere tror de har alt i sin makt, så utgår Vårt bud over den, natt eller dag, og Vi gjør den til en stubbmark, som om den ikke var i trivsel dagen før. Slik forklarer Vi ordet for folk som tenker etter.'),
(1389, 10, 25, 'Gud kaller til fredens bolig, og det er i Hans hånd å lede til den rette vei.'),
(1390, 10, 26, 'De som handler godt, har det beste i vente, og mer til. Verken støv eller fornedrelse skal formørke deres åsyn. De er paradisets folk, og der skal de være og bli.'),
(1391, 10, 27, 'Men for dem som har pådratt seg onde gjerninger, er belønningen et tilsvarende onde. Fornedrelse vil være over dem. De har ingen som kan beskytte dem mot Gud, det er som om deres åsyn var dekket med stykker av nattmørket. Dette er Ildens folk, og der skal de være og bli.'),
(1392, 10, 28, 'Den dag Vi samler alle, vil Vi si til avgudsdyrkerne: «Kom på plass, dere og deres medguder!» Så skiller vi dem. Medgudene sier: «Det var ikke oss dere dyrket!'),
(1393, 10, 29, 'Gud rekker til som vitne mellom dere og oss. Vi var ikke klar over deres tilbedelse.»'),
(1394, 10, 30, 'Der skal enhver få erfare hva han har bak seg. De bringes tilbake til Gud, deres Herre, sannheten, og alt de diktet opp, forlater dem.'),
(1395, 10, 31, 'Si: «Hvem gir dere underhold fra himmel og jord? Hvem råder over hørsel og syn? Hvem frembringer liv av det døde, og dødt av liv? Hvem styrer alt?» De vil svare: «Gud!» Si: «Vil dere ikke da vise gudsfrykt?'),
(1396, 10, 32, 'Dette er Gud, deres Herre, sannheten! Hva er hinsides sannheten annet enn villfarelse? Hvordan kan dere være så bortvendte?»'),
(1397, 10, 33, 'Således fullbyrdes Herrens ord mot de ugudelige, fordi de ikke tror.'),
(1398, 10, 34, 'Si: «Finnes det blant deres medguder noen som først frembringer skapningen, og så gjør det på nytt?» Si: «Gud frembringer skapningen, og så gjenskaper Han den! Hvordan kan dere være så forvridde?'),
(1399, 10, 35, 'Si: «Finnes det blant deres medguder noen som leder til sannheten?» Si: «Gud leder til sannheten!» Er ikke den som leder til sannheten mer verdig til å følges enn den som ikke finner vei, med mindre han ledes? Hva er i veien med dere? Hvordan er det dere vurderer?'),
(1400, 10, 36, 'Folk flest følger bare formodninger, men formodninger er intet verdt overfor sannheten! Gud vet godt hva de gjør.'),
(1401, 10, 37, 'Denne Koran er ikke oppdiktet utenom Gud, men er en stadfestelse av det som forelå før den, og en forklaring av skriften, herom hersker ingen tvil, fra all verdens Herre.'),
(1402, 10, 38, 'De kan si: «Han har oppdiktet den.» Si: «Så bring en sure, som den, og bare påkall hva dere kan, utenom Gud, om dere taler sant!»'),
(1403, 10, 39, 'Nei, de forkaster det hvis visdom de ikke har fått tak i, og hvis utlegning ennå ikke har nådd dem. Slik forkastet også de som levde før dem. Men se hva enden ble for dem som handler ondt.'),
(1404, 10, 40, 'Noen av dem tror på den, og noen tror ikke. Herren kjenner best ufredsstifterne.'),
(1405, 10, 41, 'Om de kaller deg for løgner, så si: «Min gjerning er min, og deres gjerning deres! Dere har intet med hva jeg gjør, og jeg har intet med hva dere gjør.»'),
(1406, 10, 42, 'Noen av dem lytter til deg. Men vil du få døve til å høre, skjønt de intet oppfatter?'),
(1407, 10, 43, 'Og noen blant dem ser hen til deg. Men vil du vise en blind veien, skjønt han intet innser?'),
(1408, 10, 44, 'Gud gjør ikke menneskene den minste urett, men menneskene gjør urett som slår tilbake på dem selv.'),
(1409, 10, 45, 'Den dag Han samler dem, er det som de bare har ventet en times tid, og de gjenkjenner hverandre. De har tapt som fornektet møtet med Gud, og ikke var på rett vei.'),
(1410, 10, 46, 'Være seg at Vi lar deg se noe av det Vi har stilt dem i utsikt, eller at Vi kaller deg hjem, til Oss vender de tilbake. Gud er så vitne for det de bedriver.'),
(1411, 10, 47, 'Hvert folk har sitt sendebud. Når så deres sendebud ankommer, blir alt avgjort i rettferdighet blant dem, og de lider ingen urett.'),
(1412, 10, 48, 'De sier: «Når inntreffer dette som er lovet, om dere snakker sant?»'),
(1413, 10, 49, 'Si: «Jeg kan ikke skade eller gagne meg selv, alt står i Guds hånd. Hvert folk har sin tid. Når deres tid kommer, kan de ikke utsette den en time, og heller ikke fremskynde den.»'),
(1414, 10, 50, 'Si: «Hva mener dere om Hans straffedom kommer over dere natt eller dag? Hva vil vel synderne be påskyndet av slikt?'),
(1415, 10, 51, 'Når den så har inntruffet, er det da dere vil tro på den?» – «Nå: Dere vil jo ha den påskyndet?»'),
(1416, 10, 52, 'Så vil det bli sagt til dem som handlet ondt: «Smak straffen som varer! Kan dere vel lønnes for annet enn det dere har fortjent?»'),
(1417, 10, 53, 'De forhører seg hos deg: «Er dette sant?» Si: «Ja, ved Herren, dette er sannhet? Og dere kan intet forpurre.»'),
(1418, 10, 54, 'Om en urettferdig sjel eide alt på jorden, ville han prøve å løskjøpe seg med det. De skjuler i seg angeren når de ser straffen. Og det vil bli dømt blant dem med rettferd. De vil ikke lide urett.'),
(1419, 10, 55, 'Tilhører ikke alt i himlene og på jord Gud? Er ikke Guds løfte sannhet? Men folk flest vet ingenting.'),
(1420, 10, 56, 'Han gir liv og død, og til Ham blir dere brakt tilbake.'),
(1421, 10, 57, 'Hør, dere mennesker! Det er kommet til dere en formaning fra Herren. Helbredelse for det som er i hjertene, og ledelse og nåde for de troende.'),
(1422, 10, 58, 'Si: «Over Guds godhet og nåde skal de glede seg. Det er bedre enn alt de samler i hop!»'),
(1423, 10, 59, 'Si: «Hva mener dere om det Gud gir dere til underhold, noe gjør dere forbudt, noe tillatt?» Si: «Er det med Guds tillatelse, eller er det noe dere har pådiktet Ham?»'),
(1424, 10, 60, 'Og hva vil de som pådikter Gud løgn, tenke på oppstandelsens dag? Gud er full av godhet mot menneskene, men folk flest viser ikke takknemlighet.'),
(1425, 10, 61, 'Du befinner deg ikke i en situasjon, og fremfører ingen Koran, og dere gjør ingen forhandlinger, uten at Vi er vitner når dere innlater dere på det. Intet av et støvkorns vekt på jorden eller i himmelen unngår Herren. Det er intet mindre eller større enn dette som ikke står i en klar Bok.'),
(1426, 10, 62, 'Sannelig, Guds venner, over dem skal ingen frykt hvile, ei heller sorg.'),
(1427, 10, 63, 'De som tror og viser gudsfrykt,'),
(1428, 10, 64, 'de får et godt budskap i jordelivet og det hinsidige. For Guds ord finnes ingen forandring. Dette er den store seier.'),
(1429, 10, 65, 'La ikke deres tale bedrøve deg. Makten tilhører Gud fullt ut, Han den Hørende, den Allvitende.'),
(1430, 10, 66, 'Er det ikke slik at alle i himlene og på jord tilhører Gud? Hva følger vel de som påkaller medguder utenom Gud? De følger bare formodninger, og gjør bare antagelser.'),
(1431, 10, 67, 'Han er det som har skapt natten til hvile for dere, og dagen for å se. Sannelig, i dette er jærtegn for folk som hører!'),
(1432, 10, 68, 'De sier: «Gud har lagt seg til barn.» Ære være Ham, Han er selvtilstrekkelig, Ham tilhører alt i himlene og på jord! Dere har ingen autorisasjon for dette! Vil dere påstå om Gud noe dere ikke vet?'),
(1433, 10, 69, 'Si: «De som pådikter Gud løgn, dem vil det ikke gå godt.»'),
(1434, 10, 70, 'En kort nytelse i jordelivet, så går ferden hjem til Oss. Og så lar Vi dem smake den strenge straffen, fordi de var vantro.'),
(1435, 10, 71, 'Les opp for dem beretningen om Noa, da han sa til sitt folk: «Mitt folk, om mitt nærvær og min påminning om Guds tegn er tungt for dere, så forlater jeg meg på Gud. Ta en bestemmelse om dere og deres medguder, så blir ikke lenger deres sak en plage for dere. La meg få vite hva det blir til, og la meg ikke vente!'),
(1436, 10, 72, 'Om dere vender ryggen, så har jeg ikke bedt om lønn. Min lønn er Guds sak. Jeg er pålagt å være av dem som gir seg Gud i vold.»'),
(1437, 10, 73, 'Men de kalte ham løgner. Så reddet Vi ham og dem som var med ham i Arken, og lot dem overta (på jorden). Og Vi druknet dem som forkastet Vårt ord. Se, hva enden ble for dem som ble advart!'),
(1438, 10, 74, 'Senere, etter ham, sendte Vi sendebud til deres folk, og de kom til dem med klar beskjed. Men de kunne umulig tro på det de tidligere hadde forkastet. Slik forsegler Vi deres hjerter som går over grensen.'),
(1439, 10, 75, 'Så, etter dem, sendte Vi Moses og Aron til Farao og hans råd med Våre tegn. Men de viste hovmod og var syndige folk.'),
(1440, 10, 76, 'Da sannheten fra Oss kom til dem, sa de: «Dette er åpenbar trolldom!»'),
(1441, 10, 77, 'Moses spurte: «Sier dere om sannheten når den er kommet til dere: ’Er dette trolldom?’ Trollmenn går det aldri godt.»'),
(1442, 10, 78, 'De svarte: «Er du kommet for å vende oss bort fra våre fedres tradisjoner, så dere kan overta makten i landet? Vi har ingen tiltro til dere.»'),
(1443, 10, 79, 'Så sa Farao: «Bring meg alle dyktige trollmenn!»'),
(1444, 10, 80, 'Da trollmennene var kommet, sa Moses til dem: «Kast det dere har tenkt å kaste.»'),
(1445, 10, 81, 'Da de hadde kastet, sa Moses: «Det dere er kommet med, er trolldom. Gud vil gjøre det til intet. Gud fremmer ikke nedbryteres arbeid.'),
(1446, 10, 82, 'Gud realiserer sannheten ved Sitt ord, selv om det er synderne imot.'),
(1447, 10, 83, 'Men ingen trodde Moses, unntatt hans folks barn, av frykt for Farao og hans råd, så de skulle forfølge dem. Farao var på toppen i landet og disponerte fritt.'),
(1448, 10, 84, 'Moses sa: «Mitt folk, om dere tror på Gud, så forlat dere på Ham hvis dere har gitt dere Ham i vold.»'),
(1449, 10, 85, 'De svarte: «Vi forlater oss på Gud. Herre, gjør oss ikke til anstøt for dette urettferdige folk!'),
(1450, 10, 86, 'Redd oss ved Din nåde fra de vantro.»'),
(1451, 10, 87, 'Og Vi inspirerte Moses og hans bror: «Skaff deres folk hus i Egypten, og gjør deres bosetning til bønneretning, og forrett bønnen! Forkynn et godt budskap for de troende.»'),
(1452, 10, 88, 'Moses sa: «Herre, Du har gitt Farao og hans råd prakt og rikdom i jordelivet, så de fører folk bort fra Din vei. Herre, utvisk deres rikdom og forherd deres hjerter, så de ikke tror før de står overfor den smertelige straffen!»'),
(1453, 10, 89, 'Han sa: «Deres bønn er hørt. Hold rett kurs, og følg ikke deres vei som intet vet.»'),
(1454, 10, 90, 'Vi drog med Israels barn gjennom havet. Farao og hans hær fulgte dem med vold og fiendskap i sinne, inntil han var i ferd med å drukne, og sa: «Jeg tror at det er ingen annen gud enn Ham, som Israels barn tror på, og jeg gir meg Ham i vold.»'),
(1455, 10, 91, '«Nå; men du viste ulydighet tidligere, og var av nedbryterne!'),
(1456, 10, 92, 'I dag skal Vi redde deg og din kropp, så du kan være et jærtegn for dem som kommer etter deg. Dog, folk flest bryr seg ikke om Våre tegn!»'),
(1457, 10, 93, 'Vi bosatte Israels barn i en god bosetning, og Vi forsynte dem med gode ting. Det var ingen uenighet blant dem før kunnskapen kom til dem. Men Herren vil treffe avgjørelse mellom dem på oppstandelsens dag, i det de var uenige om.'),
(1458, 10, 94, 'Om du er i tvil om det som Vi har åpenbart for deg, så spør en som leser skriften fra før din tid. Sannheten er kommet til deg fra Herren, så vær ikke tviler,'),
(1459, 10, 95, 'ei heller av dem som forkaster Guds ord, så du blir en av taperne.'),
(1460, 10, 96, 'De, mot hvem Herrens ord realiseres, vil ikke tro,'),
(1461, 10, 97, 'selv om de mottok alle mulige jærtegn, før de står overfor den smertelige straff.'),
(1462, 10, 98, 'Hvorfor var det ingen by som antok troen og fikk nytte av sin tro, unntatt folket til Jonas? Da de antok troen, tok Vi bort fra dem den skammelige straffedom i jordelivet, og Vi lot dem nyte livet for en tid.'),
(1463, 10, 99, 'Om Herren hadde bestemt det slik, ville alle som én på jorden vært troende. Vil da du tvinge menneskene, så de blir troende?'),
(1464, 10, 100, 'Det er intet menneske gitt å tro, unntatt med Guds hjelp! Han rammer med avsky dem som ikke har forståelse.'),
(1465, 10, 101, 'Si: «Se det som er i himlene og på jorden!» Men verken jærtegn eller advarsler nytter for folk som ikke tror.'),
(1466, 10, 102, 'Kan de da vente annet enn slike dager som rammet de henfarne før dem?'),
(1467, 10, 103, 'Så vil Vi redde Våre sendebud og de troende, idet det er en plikt for Oss å redde de troende.'),
(1468, 10, 104, 'Si: «Hør, folk: Om dere er i tvil om min religion: Jeg tilber ikke dem dere tilber utenom Gud! Men jeg tilber Gud, som vil ta dere til seg, og jeg er pålagt å være troende.'),
(1469, 10, 105, 'Og: ’Vend ditt ansikt mot religionen som Gud-søker! Vær ikke avgudsdyrker!'),
(1470, 10, 106, 'Påkall ikke utenom Gud slik som verken nytter eller skader deg. Om du gjorde det, ville du visselig være av dem som handler ondt.’»'),
(1471, 10, 107, 'Om Gud hjemsøker deg med trengsel, så kan ingen fjerne den, unntatt Han. Om Han ønsker godt for deg, så kan ingen stanse Hans godhet. Han lar den overgå den Han vil av Sine tjenere. Han er den Ettergivende, den Nåderike.'),
(1472, 10, 108, 'Si: «Hør, folk! Sannheten er kommet til dere fra Herren. Den som finner rett vei, finner rett vei til egen fordel. Den som farer vill, farer vill til egen skade. Jeg er ingen ombudsmann for dere!»'),
(1473, 10, 109, 'Følg det som er åpenbart deg, og vær standhaftig til Gud treffer avgjørelse. Han er den beste dommer!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'En skrift, hvis ord er fastlagt og så forklart, fra en som er vis, vel underrettet:'),
(1475, 11, 2, '«Tjen ingen annen enn Gud!» Jeg bringer advarsler og et gledelig budskap til dere fra Ham,'),
(1476, 11, 3, 'at dere må be Herren om tilgivelse og omvende dere til Ham. Så vil Han gi dere deres behov i rikelig monn til den tid som er bestemt, enhver etter sin fortjeneste. Om dere vender dere bort, så frykter jeg en svær dags straff for dere.'),
(1477, 11, 4, 'Tilbake til Gud går ferden. Han makter alle ting.'),
(1478, 11, 5, 'De hyller inn sine bryst for å skjule seg for Ham. Men om de dekker seg med sine klær, vet Han vel om det de hemmeligholder, og det de bærer åpent frem. Han vet hva som bor i hjertene.'),
(1479, 11, 6, 'Det er intet krek på jorden unntatt at dets underhold påligger Gud. Han vet hvor det er og hvor det bor. Alt er klart foreskrevet.'),
(1480, 11, 7, 'Han er det som skapte himlene og jorden på seks dager, mens Hans trone var over vannet, for at Han kunne prøve dere, hvem av dere som er best i gjerninger. Om du sier: «Dere vil bli gjenoppvekket etter døden,» så vil nok de vantro si: «Dette er skjær magi!»'),
(1481, 11, 8, 'Om Vi utsetter straffen for dem til en viss tid, så sier de: «Hva holder den tilbake?» Sannelig, den dag den kommer, kan den ikke holdes borte fra dem, og det de drev ap med, vil ringe dem inn.'),
(1482, 11, 9, 'Om Vi viser mennesket en gunstbevisning og så trekker den tilbake, så er han fortvilet, utakknemlig.'),
(1483, 11, 10, 'Og gir Vi ham velstand etter at trengsel rammet ham, så sier han: «Det onde har veket bort fra meg!» – glad og pralende.'),
(1484, 11, 11, 'Unntatt er de som viser standhaftighet og handler rettskaffent. De har i vente tilgivelse og stor lønn!'),
(1485, 11, 12, 'Kanskje lar du noe av det som er åpenbart for deg ligge, og føler deg av den grunn beklemt i hjertet, fordi de sier: «Hvorfor er det ikke sendt ned til ham en skatt? Hvorfor er det ikke kommet en engel sammen med ham?» Men du er bare en advarer. Gud har hånd om alle ting.'),
(1486, 11, 13, 'Eller de sier: «Han har diktet det opp!» Si: «Så bring da ti likeverdige surer, oppdiktede, og påkall hva dere kan utenom Gud, om dere taler sant.»'),
(1487, 11, 14, 'Hvis de ikke etterkommer dette overfor dere, så vit at det som er åpenbart, er med Guds visdom, og at det er ingen Gud uten Ham. Vil dere da ikke gi dere inn under Ham?'),
(1488, 11, 15, 'Dem, som begjærer jordelivet og dets prakt, vil Vi gi fullt mål for deres gjerninger her, og de vil ikke bli snytt på vekten.'),
(1489, 11, 16, 'Disse er det som ikke har annet i vente i det hinsidige enn Ilden. Det de har prestert her er intet verdt, alt de har gjort er fåfengt.'),
(1490, 11, 17, 'Hva med dem som holder seg til en klar beskjed fra Herren fremført av et vitne fra Ham, og før ham av Moseboken, som rettesnor og nåde? Disse tror på det! Og de av særgruppene som fornekter det, de vil ha Ilden som møtested. Vær ikke i tvil om dette! Det er sannheten fra Herren. Men folk flest er ikke troende.'),
(1491, 11, 18, 'Hvem er større i urett enn dem som dikter opp løgn om Gud? Disse vil bli fremstilt for Herren, og vitnene vil si: «Disse er det som løy mot Herren.» Guds forbannelse vil hvile over de urettferdige,'),
(1492, 11, 19, 'som legger hindringer på Guds vei og prøver å gjøre den kroket, og som fornekter det hinsidige liv.'),
(1493, 11, 20, 'De kunne ikke forpurre noe på jorden, og de har ingen som tar seg av deres sak, utenom Gud. Dobbelt straff kan de vente! Ikke kunne de høre, og ikke kunne de se!'),
(1494, 11, 21, 'Disse har kastet seg selv bort. Det de diktet opp har sveket dem.'),
(1495, 11, 22, 'Disse er uten tvil de største tapere i det hinsidige.'),
(1496, 11, 23, 'Men de som tror og handler rett, og er ydmyke overfor Herren, disse er paradisets folk. Der skal de være og bli.'),
(1497, 11, 24, 'Det er med de to grupper som med en blind og døv, og en som ser og hører. Kan de vel settes likt? Vil dere da ikke tenke etter?'),
(1498, 11, 25, 'Vi sendte Noa til sitt folk: «Jeg er en klar advarer for dere,'),
(1499, 11, 26, 'så dere ikke skal tilbe noen annen enn Gud! Jeg frykter en smertelig dags straff for dere!»'),
(1500, 11, 27, 'Rådet i hans folk, som var vantro, sa: «Vi ser ikke annet enn at du er et vanlig menneske som oss. Vi ser ikke annet enn at bare folk fra vårt laveste sjikt følger deg, uten ettertanke. Vi kan ikke se at dere har noe fortrinn fremfor oss. Nei, vi anser dere for å fare med løgn!»'),
(1501, 11, 28, 'Noa svarte: «Hør, mitt folk! Hva mener dere? Hvis jeg holder meg til klar beskjed fra min Herre, og Han har sendt meg en nådegave som unngår deres blikk, skulle vi da påtvinge dere dette mot deres vilje?'),
(1502, 11, 29, 'Mitt folk, jeg ber ikke om penger for dette. Min lønn er Guds sak. Men jeg viser ikke bort dem som tror! De vil møte sin Herre! Jeg anser dere sannelig for uvitende folk.'),
(1503, 11, 30, 'Mitt folk, hvem kan vel hjelpe meg overfor Gud, om jeg viste dem bort? Vil dere da ikke tenke etter?'),
(1504, 11, 31, 'Jeg sier ikke til dere at jeg disponerer Guds skatter, eller at jeg kjenner det skjulte. Og jeg sier ikke at jeg er en engel. Heller ikke sier jeg til dem som dere ser ned på, at Gud vil aldri gi dem noe godt! Gud kjenner best det som bor i deres hjerter. Om jeg sa noe slikt, ville jeg handle urett!»'),
(1505, 11, 32, 'De sa: «Noa, du krangler med oss i det vide og brede. Så kom da med det du truer oss med, om du taler sant.»'),
(1506, 11, 33, 'Han svarte: «Gud alene kan bringe dere det, om Han vil. Og dere kan ikke forpurre det.'),
(1507, 11, 34, 'Mine gode råd, om jeg prøvde å råde dere, ville ikke gagne dere, hvis Gud vil la dere fare vill. Han er deres Herre, og til Ham bringes dere tilbake.»'),
(1508, 11, 35, 'Eller de kan si: «Han har oppdiktet det.» Si: «Om jeg har oppdiktet det, så rammer synden meg selv. Men jeg er uskyldig i det dere beskylder meg for!»'),
(1509, 11, 36, 'Noa ble inspirert dette: «Ingen av ditt folk vil komme til troen, unntatt de som allerede tror. Bli ikke fortvilet over det de gjør!'),
(1510, 11, 37, 'Lag Arken for Våre øyne, og etter Vår inspirasjon, og snakk ikke til Meg om dem som gjør urett. De skal druknes!»'),
(1511, 11, 38, 'Og han laget Arken. Hver gang folkets ledere gikk forbi, hånet de ham, og han svarte: «Om dere håner oss nå, så vil vi håne dere på samme måte,'),
(1512, 11, 39, 'og dere vil få vite hvem som rammes av en straff som gjør ham til skamme, og hvem som hjemsøkes av en varig straff.»'),
(1513, 11, 40, 'Når så vår beslutning ble iverksatt og vannet fløt over, sa Vi: «Ta om bord et par av alt, og din familie, unntatt den mot hvem ordet er utgått, og dem som tror.» Men det var få som trodde sammen med ham.'),
(1514, 11, 41, 'Noa sa: «Stig om bord! I Guds navn er ferd og landkjenning! Herren er tilgivende, nåderik.»'),
(1515, 11, 42, 'Og den drev rundt med dem i fjellhøye bølger, og Noa ropte til sin sønn som stod avsides: «Min sønn, kom om bord til oss! Bli ikke hos de vantro!»'),
(1516, 11, 43, 'Men han svarte: «Jeg tar tilflukt til et fjell som kan beskytte meg mot vannet.» Da sa Noa: «Det er i dag intet som kan beskytte mot Guds bestemmelse, unntatt for dem Han viser nåde.» Så skilte en bølge mellom de to, og han ble blant dem som druknet.'),
(1517, 11, 44, 'Det lød: «Jord, oppsluk ditt vann! Himmel, hold inne!» Og vannet sank. Beslutningen var fullført, og Arken kom til ro på fjellet al-Dsjudi. Og det lød: «Bort med de urettferdige!»'),
(1518, 11, 45, 'Noa påkalte Herren, og sa: «Herre, min sønn er jo av min familie, og Ditt løfte er sannhet? Du er den viseste dommer.»'),
(1519, 11, 46, 'Han sa: «Noa, han hører ikke til dine. Han står for en urett handling. Snakk ikke til meg om noe du ikke har greie på. Jeg formaner deg, at du ikke må bli en dåre.»'),
(1520, 11, 47, 'Så sa Noa: «Herre, bevar meg fra å snakke til Deg om noe jeg ikke har greie på! Om Du ikke tilgir meg og viser meg nåde, er jeg blant taperne.»'),
(1521, 11, 48, 'Det lød: «Noa, gå fra borde med Vår fred og velsignelse over deg, og over de folk som hvis opphav er med deg. Det vil være folk som Vi lar utfolde seg, men så vil en smertelig straff fra Oss gripe dem.»'),
(1522, 11, 49, 'Dette er av fortellingene fra det skjulte som Vi åpenbarer for deg. Verken du eller ditt folk kjente dem før dette. Så vis standhaftighet. Det endelige utfall er til de gudfryktiges fordel.'),
(1523, 11, 50, 'Til folket Ad sendte Vi deres bror Hod, og han sa: «Hør, mitt folk, tjen Gud! Dere har ingen annen gud enn Ham! Dere bare dikter opp!'),
(1524, 11, 51, 'Mitt folk, jeg krever ingen lønn av dere for dette. Min lønn er Hans sak, som har skapt meg. Vil dere da ikke forstå?'),
(1525, 11, 52, 'Mitt folk, be Herren om tilgivelse og omvend dere til Ham, så vil Han sende over dere rikelig regn fra oven, og legge styrke til deres styrke! Vend dere ikke bort som syndere!»'),
(1526, 11, 53, 'De svarte: «Hør Hod, du har ikke brakt oss noe klart bevis, og vi kan ikke oppgi våre guder på ditt ord. Vi tror deg ikke.'),
(1527, 11, 54, 'Vi har ikke annet å si enn at en av våre guder må ha slått deg med noe ondt.» Da svarte han: «Jeg tar Gud til vitne, og vær dere vitner at jeg intet har å gjøre med deres medguder utenom Ham.'),
(1528, 11, 55, 'Smi deres renker mot meg, alle sammen, og la meg ikke vente!'),
(1529, 11, 56, 'Jeg forlater meg på Gud, min Herre og deres Herre! Det finnes ingen skapning som ikke Han holder i luggen. Herren holder rett kurs.'),
(1530, 11, 57, 'Om dere vender ryggen, så har jeg overbrakt dere det jeg ble utsendt med. Herren kan sette et annet folk i deres sted. Dere kan ikke skade Ham det ringeste. Herren passer på alt!»'),
(1531, 11, 58, 'Da Vår beslutning ble iverksatt, reddet Vi Hod og dem som trodde med ham ved Vår nåde. Vi reddet dem fra en slem straff.'),
(1532, 11, 59, 'Dette var folket Ad. De bestred Herrens ord, og adlød ikke Hans sendebud, men fulgte hver egenrådig tyranns anvisninger.'),
(1533, 11, 60, 'De ble forfulgt av forbannelse i denne verden. Og på oppstandelsens dag: «Ad har fornektet sin Herre! Bort med Ad, Hods folk!»'),
(1534, 11, 61, 'Til folket Thamod sendte Vi deres bror Salih, og han sa: «Hør, mitt folk, tjen Gud! Dere har ingen annen gud enn Ham! Han har frembrakt dere av jorden og bosatt dere der! Be Ham om tilgivelse og omvend dere til Ham! Herren er nær, og hører bønn.»'),
(1535, 11, 62, 'De svarte: «Hør, Salih, vi stilte forhåpninger til deg før dette. Skal du nå forby oss å tilbe det våre fedre tilbad? Vi huser sannelig stor tvil om det du kaller oss til!»'),
(1536, 11, 63, 'Han sa: «Mitt folk, hva mener dere? Hvis jeg holder meg til en klar beskjed fra min Herre, og Han har sendt meg en nådegave, hvem kan vel gi meg hjelp mot Gud, om jeg viser Ham ulydighet? Dere kan bare gjøre vondt verre.'),
(1537, 11, 64, 'Mitt folk, dette er en kamelhoppe som tilhører Gud, som et tegn for dere. La den fritt beite på Guds jord og gjør den intet ondt, ellers vil en overhengende straff ramme dere.»'),
(1538, 11, 65, 'Men de lemlestet denne. Da sa Salih: «Nyt livet i deres hus i tre dager! Dette er ikke en uvederheftig trussel.»'),
(1539, 11, 66, 'Da Vår beslutning ble iverksatt, reddet Vi Salih og dem som trodde med ham ved en nåde fra Oss fra ydmykelsen på hin dag. Herren, Han er den Sterke, den Mektige.'),
(1540, 11, 67, 'Så tok skrallet de urettferdige, og om morgenen lå de utstrakte i sine hjem,'),
(1541, 11, 68, 'som om de aldri hadde levd der i beste velgående. Thamod fornektet sin Herre: «Bort med Thamod!»'),
(1542, 11, 69, 'Våre utsendinger kom til Abraham med et gledelig budskap, og de hilste: «Fred.» Han svarte: «Fred,» og var ikke sen med å bringe en stekt kalv.'),
(1543, 11, 70, 'Da han så at de ikke forsynte seg, fikk han en fremmedfølelse og fryktfølelse overfor dem. Men de sa: «Frykt ikke, vi er sendt til Lots folk.»'),
(1544, 11, 71, 'Hans hustru stod også der, og hun lo. Så bebudet vi henne Isak, og deretter Jakob.'),
(1545, 11, 72, 'Da sa hun: «Trøste meg, skal jeg føde barn når jeg er en gammel kone, og denne mannen min er en olding? Dette var sannelig en merkelig ting.»'),
(1546, 11, 73, 'De svarte: «Finner du Guds beslutning merkelig? Guds nåde og Hans velsignelse er over dere, folk i dette hus! Ham tilkommer pris og ære.»'),
(1547, 11, 74, 'Da skrekken forlot Abraham, og han fikk del i det gledelige budskap, begynte han å diskutere med oss om Lots folk.'),
(1548, 11, 75, 'Abraham var mild, medfølende og botferdig.'),
(1549, 11, 76, '«Abraham, bry deg ikke med dette! Herrens beslutning foreligger, og det vil overgå dem en straff som ikke kan avvendes.»'),
(1550, 11, 77, 'Da Våre utsendinger kom til Lot, følte han seg bekymret og hjelpeløs på deres vegne, og sa: «Dette er en hard dag.»'),
(1551, 11, 78, 'Folket kom strømmende til, og de hadde tidligere forøvet ondt. Han sa: «Mitt folk, se her er mine døtre! De er mer passende for dere! Vis gudsfrykt, bring ikke skam over meg når det gjelder mine gjester! Finnes det da ikke en rettsindig mann blant dere?»'),
(1552, 11, 79, 'De svarte: «Du vet godt at vi ikke har noen rett til dine døtre. Du vet godt hva vi er ute etter!»'),
(1553, 11, 80, 'Da sa han: «Om jeg bare hadde hatt makt å sette opp mot dere, eller kunne ta tilflukt til et fast støttepunkt.»'),
(1554, 11, 81, 'Utsendingene sa: «Hør, Lot, vi er Herrens utsendinger. De vil aldri få tak i deg, så dra du og dine i en nattestund. Ingen av dere må snu seg tilbake, unntatt din hustru! Hun vil bli rammet av det som overgår de andre. Tiden for dem er morgenstunden. Er ikke morgenstunden nær?»'),
(1555, 11, 82, 'Da Vår beslutning ble iverksatt, snudde Vi opp ned på byen, og lot teglstein med Herrens merke'),
(1556, 11, 83, 'regne over dem, lag etter lag. – Dette er ikke fjernt fra de urettferdige.'),
(1557, 11, 84, 'Til folket i Midian sendte Vi deres bror Shoaib, og han sa: «Hør, mitt folk, tjen Gud! Dere har ingen annen gud enn Ham! Snyt ikke på mål og vekt! Jeg ser dere lever i velferd og velstand, men jeg frykter at en altomfattende dags straff vil komme over dere.'),
(1558, 11, 85, 'Mitt folk, gi mål og vekt på rett måte! Snyt ikke folk på det de har! Gjør ikke urett i landet, som ufredsstiftere!'),
(1559, 11, 86, 'Det som beror hos Gud er bedre for dere, om dere er troende! Men jeg er ingen vokter for dere.»'),
(1560, 11, 87, 'De svarte: «Hør, Shoaib, krever din bønn av deg at vi skal oppgi det våre fedre har tilbedt, eller slutte å gjøre hva vi vil med vår eiendom? Du er da visselig mild og rettsindig!»'),
(1561, 11, 88, 'Da sa han: «Hør, mitt folk! Hva mener dere? Hvis jeg holder meg til klar beskjed fra min Herre, og Han har gitt meg alt jeg trenger? Jeg vil ikke skape strid ved det jeg har forbudt dere! Jeg vil bare sette tingene på plass, så langt jeg kan. Om jeg lykkes, står i Guds hånd. På Ham forlater jeg meg, og til Ham vender jeg meg i bot.'),
(1562, 11, 89, 'Hør, mitt folk! Måtte ikke bruddet med meg føre dere i synd, så noe tilsvarende vil ramme dere som rammet Noas folk, Hods folk, eller Salihs folk, og Lots folk er ikke fjernt fra dere.'),
(1563, 11, 90, 'Be Herren om tilgivelse, og omvend dere til Ham! Herren er nåderik, fylt av kjærlighet.»'),
(1564, 11, 91, 'De svarte: «Hør, Shoaib, vi skjønner ikke meget av det du sier. Men vi mener nå at du er maktesløs blant oss. Hadde det ikke vært for din slekt, ville vi sannelig steinjage deg. Du har ingen maktstilling overfor oss.»'),
(1565, 11, 92, 'Han sa: «Hør, mitt folk! Har vel min slekt en større maktstilling overfor dere enn Gud, at dere åpent kan vende Ham ryggen? Herren har alt dere gjør under kontroll!'),
(1566, 11, 93, 'Mitt folk, gjør som dere kan. Jeg gjør likeledes. Dere vil snart få vite hvem straffen overgår som gjør ham til skamme, og hvem som farer med løgn. Vær på utkikk: Jeg er også på utkikk.»'),
(1567, 11, 94, 'Da Vår beslutning ble iverksatt, reddet Vi Shoaib og dem som trodde med ham ved Vår nåde, og skrallet tok de urettferdige. Om morgenen lå de utstrakt i sine hjem'),
(1568, 11, 95, 'som om de aldri hadde levd der i beste velgående. «Bort med Midian!» som det før ble sagt «Bort med Thamod!».'),
(1569, 11, 96, 'Og Vi sendte Moses med Våre tegn og en klar fullmakt'),
(1570, 11, 97, 'til Farao og hans råd. De fulgte Faraos bud. Men Faraos bud var ikke rettsindig.'),
(1571, 11, 98, 'Han vil gå foran sitt folk på oppstandelsens dag og føre dem ned i Ilden. Hvilket sørgelig rastested å bli ført til!'),
(1572, 11, 99, 'De ble fulgt av en forbannelse her i verden, så også på oppstandelsens dag. Hvilken sørgelig gave å få!'),
(1573, 11, 100, 'Dette er historiene om byene, som vi forteller deg. Noen står, noen er som stubbmark.'),
(1574, 11, 101, 'Vi har ikke gjort dem urett, men deres egen urett har slått tilbake på dem selv. Deres guder som de påkalte utenom Gud, kunne intet gjøre for dem da Herrens beslutning ble iverksatt, men øket bare ødeleggelsen for dem.'),
(1575, 11, 102, 'Slik er Herrens grep når Han tar fatt byen som handler urett. Hans grep er smertelig, hardt.'),
(1576, 11, 103, 'Heri er et tegn for dem som frykter straffen i det hinsidige. Det er den dag hvortil menneskene samles. Det er den dag som må overværes!'),
(1577, 11, 104, 'Vi utsetter den bare til fastsatt tid.'),
(1578, 11, 105, 'Den dag dette kommer, kan ingen tale uten Hans tillatelse. Noen er ulykkelige, andre lykkelige.'),
(1579, 11, 106, 'De ulykkelige havner i Ilden. I den er sukk og stønn deres lodd,'),
(1580, 11, 107, 'og her skal de være og bli, så lenge himlene og jorden består, dog slik Herren vil. Herren gjør hva Han vil!'),
(1581, 11, 108, 'De som er lykkelige, går til paradiset, og her skal de være og bli, så lenge himlene og jorden består, dog slik Herren vil, som en gave uten ende.'),
(1582, 11, 109, 'Så vær ikke i tvil om det disse mennesker tilber. De tilber bare slik deres fedre tilbad før dem. Vi vil gi dem den andel de tilkommer fullt ut, uten avkortning!'),
(1583, 11, 110, 'Vi gav jo Moses skriften, men så ble de uenige om den. Hadde det ikke vært for et ord fra Herren som forelå, så ville det blitt opp og avgjort mellom dem. De er i stor tvil om den.'),
(1584, 11, 111, 'Sannelig, alle vil Herren gi full lønn for deres gjerninger. Han er vel kjent med det de gjør.'),
(1585, 11, 112, 'Så hold rett kurs, slik du er pålagt, og de som har omvendt seg sammen med deg. Vær ikke oppsetsige! Han ser vel det dere gjør.'),
(1586, 11, 113, 'Støtt dere ikke på dem som handler urett, slik at Ilden tar dere. Dere har ingen venner unntatt Gud, og dere finner ingen hjelp.'),
(1587, 11, 114, 'Forrett bønnen ved dagens ytterpunkter og i de nære nattetimer. De gode gjerninger fordriver de onde. Dette er en påminnelse for dem som viser omtanke.'),
(1588, 11, 115, 'Vis standhaftighet! Gud lar ikke deres lønn gå tapt som handler vel!'),
(1589, 11, 116, 'Hvorfor var det ikke blant slektene før dere en liten rest av dem som satte seg imot forfall og ufred på jorden, unntatt noen få av dem, og de var av dem Vi reddet, mens de urettferdige pleiet det vellevnet som var blitt dem til del, og var syndere?'),
(1590, 11, 117, 'Herren vil nok ikke ødelegge byene på urett vis, mens folket handler rett.'),
(1591, 11, 118, 'Om Herren hadde villet, kunne Han gjort menneskeheten til ett (tros)samfunn. Men de fortsetter å strides,'),
(1592, 11, 119, 'unntatt de som Herren viser nåde. Til dette har Han skapt dem, og Herrens ord fullbyrdes: «Jeg vil visselig fylle helvete med dsjinner og mennesker hulter til bulter.»'),
(1593, 11, 120, 'Alt Vi forteller deg fra sendebudenes historie er for å styrke ditt hjerte. Heri kommer sannheten til deg, og oppbyggelse og formaning til de troende.'),
(1594, 11, 121, 'Si til dem som ikke tror: «Gjør som dere kan, vi gjør likeledes.'),
(1595, 11, 122, 'Bare vent! Vi venter også!»'),
(1596, 11, 123, 'Guds er himlenes og jordens mysterium. Til Ham føres alt tilbake. Tjen Ham, og forlat deg på Ham! Herren er ikke likegyldig med det dere gjør.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'Dette er den klare skriftens ord.'),
(1598, 12, 2, 'Vi har åpenbart den som en arabisk Koran, så dere må forstå.'),
(1599, 12, 3, 'Vi forteller deg den vakreste fortelling når Vi nå åpenbarer denne del av Koranen, selv om du tidligere var likegyldig.'),
(1600, 12, 4, 'En gang sa Josef til sin far: «Far, jeg så elleve stjerner samt solen og månen, jeg så dem kaste seg ned foran meg.»'),
(1601, 12, 5, 'Han svarte: «Hør, min sønn, fortell ikke ditt drømmesyn til dine brødre, ellers smir de renker mot deg. Satan er menneskets klare fiende.'),
(1602, 12, 6, 'Slik vil Herren utvelge deg og lære deg å tyde hendingene, og fullbyrde Sin nåde mot deg og Jakobs slekt, som Han tidligere fullbyrdet den mot dine fedre, Abraham og Isak. Herren vet, er vis!»'),
(1603, 12, 7, 'Josef og hans brødre var jærtegn for dem som stiller spørsmål.'),
(1604, 12, 8, 'De sa: «Josef og hans bror er sannelig kjærere for vår far enn vi, enda vi er mange. Vår far er klart på villspor!'),
(1605, 12, 9, 'Drep Josef, eller forjag ham til fremmed land. Så vil dere få all plass alene for deres fars åsyn og kan leve som rettskafne folk når han er borte.»'),
(1606, 12, 10, 'En av dem sa: «Ikke drep Josef, men kast ham i brønnens dyp. Så vil en eller annen karavane plukke ham opp, om dere gjør slik.»'),
(1607, 12, 11, 'Så sa de: «Hva er det med deg, far, at du ikke stoler på oss når det gjelder Josef? Vi ønsker ham bare godt.'),
(1608, 12, 12, 'Send ham med oss i morgen, så han kan more seg og leke. Vi skal passe på ham!»'),
(1609, 12, 13, 'Han svarte: «Det bekymrer meg at dere skal dra av gårde med ham, jeg er redd for at sjakalen kan spise ham mens dere er uoppmerksomme.»'),
(1610, 12, 14, 'De sa: «Om sjakalen spiser ham når vi er så mange, da er vi sannelig tapere!»'),
(1611, 12, 15, 'Da de så hadde tatt ham med og var blitt enige om å kaste ham i brønnens dyp, åpenbarte Vi ham: «Du vil minne dem om denne deres dåd når de ingenting aner.»'),
(1612, 12, 16, 'Om kvelden kom de gråtende til sin far og sa:'),
(1613, 12, 17, '«Far, vi gikk bort for å løpe om kapp og lot Josef bli igjen ved tingene våre. Og så spiste sjakalen ham. Men du tror oss vel ikke selv om vi snakker sant.»'),
(1614, 12, 18, 'De brakte falskt blod på hans skjorte. Han sa: «Nei, dette er noe dere selv har funnet på. Nå trengs tålmod og standhaftighet! Gud alene kan påkalles om hjelp mot det dere forteller.»'),
(1615, 12, 19, 'Så kom en karavane, og de sendte ut en mann, en vannhenter. Han kastet ned bøtten og ropte: «Dette var hell! Her er en ung gutt!» Og de skjulte ham som handelsvare. Men Gud visste hva de gjorde.'),
(1616, 12, 20, 'De solgte ham for en ussel pris, noen få dinarer, så her viste de måtehold.'),
(1617, 12, 21, 'Mannen som kjøpte ham var fra Egypt, og sa til sin hustru: «Ta vel imot ham! Kanskje han kan være oss til nytte, eller at vi kan adoptere ham.» Slik gav Vi Josef fotfeste i landet, så Vi kunne lære ham å tyde hendingene. Gud mestret sin sak. Men folk flest forstår ingenting.'),
(1618, 12, 22, 'Da han nådde moden alder, gav Vi ham innsikt og visdom. Slik lønner Vi dem som gjør godt.'),
(1619, 12, 23, 'Nå ville hun, som han var i huset hos, forføre ham, stengte dørene og sa: «Kom igjen!» Han svarte: «Gud være min tilflukt! Han er min Herre, og Han har gitt meg et godt sted å være. Urettferdige går det ikke godt!»'),
(1620, 12, 24, 'Hun hadde lyst på ham, og han hadde lyst på henne. Så hvis han nå ikke hadde sett et avgjørende tegn fra Herren! Dette, så Vi kunne vende bort fra ham ondskap og skjensel. Han var av Våre oppriktige tjenere.'),
(1621, 12, 25, 'De løp om kapp til døren, og hun rev i stykker skjorten hans bak. I døren møtte de husets herre, og hun sa: «Hvilken lønn fortjener vel den som har onde hensikter overfor din kone, annet enn at han fengsles eller får en smertefull straff?»'),
(1622, 12, 26, 'Josef sa: «Det var hun som ville forføre meg.» En i hennes husholdning uttalte seg: «Hvis skjorten hans er revet foran, så taler hun sant, og han lyver.'),
(1623, 12, 27, 'Hvis skjorten hans er revet bak, så lyver hun, og han er sannferdig.»'),
(1624, 12, 28, 'Da han nå så at skjorten var revet bak, sa han: «Dette hører til deres kvinnerenker. Sannelig, deres renker er uhyrlige!'),
(1625, 12, 29, 'Josef, snu deg vekk fra dette! Og du, be om tilgivelse for din synd! Du har begått et feiltrinn.»'),
(1626, 12, 30, 'Kvinnene i byen sa: «Stormannens hustru prøver å forføre sin unge tjener. Han har gjennomtrengt henne med kjærlighet. Vi anser henne klart å være på gale veier.»'),
(1627, 12, 31, 'Da hun fikk høre om deres sladder, sendte hun invitasjon til dem og satte frem appelsiner med en kniv til hver. Så ropte hun: «Kom ut til dem!» Da de fikk se ham, ropte de ende over seg og skar seg i fingrene: «Bevare oss vel, dette er da ikke et menneske, men en herlig engel!»'),
(1628, 12, 32, 'Da sa hun: «Her ser dere ham som dere lastet meg for! Ja, jeg prøvde å forføre ham, men han stod imot. Men hvis han ikke gjør det jeg beordrer ham til, så skal han sannelig i fengsel og være blant de elendige.»'),
(1629, 12, 33, 'Men Josef sa: «Herre, fengselet er meg kjærere enn det de kaller meg til. Men hvis Du ikke vender deres renker bort fra meg, kan jeg få lyst til å innlate meg med dem, og bli en dåre.»'),
(1630, 12, 34, 'Og Herren bønnhørte ham og vendte deres renker bort fra ham, Han den Hørende, den Allmektige.'),
(1631, 12, 35, 'Derpå fant de for godt, etter at de hadde sett jærtegnene, å fengsle ham inntil videre.'),
(1632, 12, 36, 'Sammen med ham kom to unge menn i fengselet. En av dem sa: «Jeg drømte at jeg presset vindruer,» og den andre sa: «Jeg drømte at jeg bar brød på hodet som fuglene spiste av. Fortell oss hva det betyr. Vi ser at du er en snill mann.»'),
(1633, 12, 37, 'Han svarte: «Før dere får et måltid mat å spise, skal jeg ha fortalt dere betydningen, før dette skjer. Dette er av det som Herren har lært meg. Jeg har forlatt deres religion som ikke tror på Gud, og som fornekter det hinsidige.'),
(1634, 12, 38, 'Jeg følger mine fedres tro. Abrahams, Isaks og Jakobs. Vi er ikke slik at vi gir Gud medguder. Dette er en Guds velgjerning mot oss og mot menneskeheten, men folk flest viser ikke takknemlighet.'),
(1635, 12, 39, 'Hør, mine fengselsfeller, er flere adskilte guder mer verdt enn Gud, den Ene, den Allmektige?'),
(1636, 12, 40, 'Det dere tilber utenom Ham er bare navn, som dere og deres fedre har oppfunnet. Gud har ikke gitt noen autorisasjon for dem. Avgjørelsen tilligger Gud alene. Han har bestemt at dere skal tilbe Ham alene. Dette er religionen som står fast. Men folk flest vet ikke.'),
(1637, 12, 41, 'Mine fengselsfeller, hva angår den ene av dere, så skal han få skjenke vin for sin herre. Men hva den annen angår, så skal han bli korsfestet, og fuglene vil spise av hans hode. Den saken dere ber om beskjed om er avgjort.»'),
(1638, 12, 42, 'Og han sa til den av de to som han mente skulle berge seg: «Nevn meg for din herre.» Men Satan fikk ham til å glemme å påminne sin herre, så han forble i fengselet i årevis.'),
(1639, 12, 43, 'Kongen sa: «Jeg så i drømme syv fete kyr som syv magre åt opp, og syv grønne aks og syv tørre. Dere rådsmedlemmer, gi meg beskjed om mitt syn, om dere kan forklare drømmer.»'),
(1640, 12, 44, '«Et virvar av drømmer,» sa de. «Vi kjenner ikke til drømmetydning.»'),
(1641, 12, 45, 'Den av de to som hadde kommet fri, sa han etter å ha tenkt seg om en stund: «Jeg skal fortelle dere dets betydning. Send meg av gårde!»'),
(1642, 12, 46, '«Josef, du sannferdige mann, gi oss beskjed om syv fete kyr som syv magre åt opp, og syv grønne aks og syv tørre, så jeg kan vende tilbake til folkene slik at de forstår.»'),
(1643, 12, 47, 'Josef svarte: «Dere skal så i syv år som vanlig. Det dere høster skal dere la bli i aksene, unntatt litt som dere spiser.'),
(1644, 12, 48, 'Så vil det komme syv harde år som vil sluke alt dere har lagt til side for dem, unntatt litt som dere har på lager.'),
(1645, 12, 49, 'Men så, etter dette, kommer et år hvor menneskene unnsettes med regn, og i dette skal de presse vindruer.»'),
(1646, 12, 50, 'Kongen sa: «Før ham til meg.» Da så budbringeren kom til Josef, sa han: «Gå tilbake til din herre og spør ham hvordan det hadde seg med kvinnene som skar seg i fingrene! Min Herre kjenner deres renker.»'),
(1647, 12, 51, 'Og kongen spurte: «Hvordan var det med dere den gangen dere ville forføre Josef?» De svarte: «Bevare oss vel! Vi vet ikke noe galt om ham.» Stormannens hustru sa: «Sannheten er kommet for dagen. Jeg ville forføre ham. Han snakket sant.»'),
(1648, 12, 52, '«Dette, så han skal vite at jeg ikke øver svik i det skjulte, og at Gud ikke lar de svikefulles renker føre frem.'),
(1649, 12, 53, 'Men jeg frikjenner ikke meg selv. Naturen tilskynder til det som ondt er, med mindre Herren forbarmer seg. Herren er tilgivende, nåderik.»'),
(1650, 12, 54, 'Kongen sa: «Før ham til meg! Jeg vil knytte ham til meg personlig.» Da han hadde snakket med ham, sa han: «I dag er du en mektig og betrodd mann hos oss.»'),
(1651, 12, 55, 'Josef svarte: «Sett meg over landets lagere. Jeg er påpasselig og forstandig.»'),
(1652, 12, 56, 'Slik gav Vi Josef fotfeste i landet, så han kunne slå seg ned hvor han ville. Vi lar Vår nåde overgå hvem Vi vil, og Vi lar ikke deres lønn gå tapt som handler vel!'),
(1653, 12, 57, 'Men lønnen i det hinsidige liv er visselig bedre for dem som tror og viser gudsfrykt.'),
(1654, 12, 58, 'Så kom Josefs brødre, og trådte inn til ham. Han kjente dem, mens han var fremmed for dem.'),
(1655, 12, 59, 'Da han hadde rustet dem ut med deres forsyninger, sa han: «Ta med til meg deres bror fra deres far. Ser dere ikke at jeg gir fullt mål og er den beste vert?'),
(1656, 12, 60, 'Men om dere ikke tar ham med til meg, blir dere ikke tilmålt mer hos meg, og dere får ikke komme i min nærhet.»'),
(1657, 12, 61, 'De svarte: «Vi skal forsøke å overtale hans far, og vi skal nok greie det.»'),
(1658, 12, 62, 'Josef sa til sine tjenere: «Legg deres byttevarer tilbake i deres oppakning, så de må kjenne dem igjen når de kommer hjem til sine, og kanskje komme igjen.»'),
(1659, 12, 63, 'Da de kom hjem til sin far, sa de: «De nekter å måle ut mer til oss. Men send med oss vår bror, så får vi tilmålt korn. Vi skal passe på ham!»'),
(1660, 12, 64, 'Han svarte: «Kan jeg betro dere ham annerledes enn jeg betrodde dere hans bror før dette? Men Gud er den beste vokter, og den barmhjertigste av alle.»'),
(1661, 12, 65, 'Da de åpnet oppakningen, fant de sine byttevarer som var gitt dem tilbake. Og de sa: «Far, hva mer kan vi ønske! Vi har fått igjen byttevarene våre! Vi skal skaffe forsyninger til våre folk og passe på vår bror og få en kamellast i tillegg! Det er et lettjent kornmål!»'),
(1662, 12, 66, 'Faren svarte: «Jeg sender ham ikke med dere før dere lover meg ved Gud å bringe ham tilbake til meg, med mindre dere ikke har noen utvei!» Da de hadde avgitt sitt løfte, fortsatte han: «Gud innestår for det vi har uttalt.»'),
(1663, 12, 67, 'Og videre: «Mine sønner, dra ikke inn gjennom én port, men benytt forskjellige porter. Men jeg kan ikke hjelpe dere mot Gud. Avgjørelsen tilligger Gud alene, og jeg forlater meg på Ham. Måtte alle fortrøstningssøkende forlate seg på Ham!»'),
(1664, 12, 68, 'Da de nå hadde dratt inn slik som deres far hadde pålagt dem, skjønt dette kunne ikke hjelpe dem mot Gud, men det var et dyptfølt ønske hos Jakob som han oppfylte. Han var fylt av visdom, ut fra det vi hadde lært ham. Men folk flest vet ingenting.'),
(1665, 12, 69, 'Da de trådte inn foran Josef, tok han sin bror til seg og sa: «Jeg er din bror! Vær ikke fortvilet over det de har gjort!»'),
(1666, 12, 70, 'Da han hadde gitt dem deres forsyninger, la han et drikkebeger i sin brors bagasje. Derpå ropte en herold: «Dere i karavanen, dere er tyver!»'),
(1667, 12, 71, 'De vendte seg om mot dem og sa: «Hva er det dere savner?»'),
(1668, 12, 72, 'Og de svarte: «Vi savner kongens beger! Den som fremskaffer det, får en kamellast! Det garanterer jeg.»'),
(1669, 12, 73, 'Brødrene sa: «Ved Gud, dere vet godt at vi ikke kom for å lage ufred i landet. Vi er ikke tyver!»'),
(1670, 12, 74, 'De spurte: «Og hva blir straffen, om dere lyver?»'),
(1671, 12, 75, 'Brødrene sa: «Straffen bør være, at den i hvis bagasje begeret finnes, med sin person dekker straffen. Slik lønner vi illgjerningsmenn.»'),
(1672, 12, 76, 'Han lot søkingen begynne i deres sekker før sin yngste brors sekk. Og han trakk det frem fra brorens sekk. Slik ordnet Vi det til for Josef. Men ifølge kongens religion kunne han ikke ta sin bror til eie, med mindre Gud hadde villet det slik. Vi hever hvem Vi vil i rang. Og over alle vitende står den Allvitende.'),
(1673, 12, 77, 'Da sa brødrene: «Har han stjålet, så har hans bror stjålet før.» Josef gjemte ordene i sitt hjerte, men han viste ingenting overfor dem. Han sa: «Dere er ille stilt! Gud kjenner godt det dere sikter til.»'),
(1674, 12, 78, 'Så sa brødrene: «Mektige herre, han har en far som er gammel og grå. Ta en av oss istedenfor ham. Vi ser at du er en snill mann.'),
(1675, 12, 79, 'Josef svarte: «Gud bevare, at vi skulle ta en annen enn den vi har funnet vår gjenstand hos! I så fall ville vi være illgjerningsmenn.»'),
(1676, 12, 80, 'Da de gav opp Josef, trakk de seg tilbake for å rådslå, og den eldste sa: «Vet dere ikke at vår far avkrevet dere et løfte ved Gud, og tidligere sviktet dere når det gjaldt Josef. Jeg drar ikke fra dette land før min far gir meg tillatelse, eller Gud avgjør saken for meg. Han er den beste til å avgjøre.'),
(1677, 12, 81, 'Dra dere tilbake til far, og si: ’Far, din sønn har stjålet! Vi påstår bare det vi vet. Vi kunne ikke overvåke det skjulte.'),
(1678, 12, 82, 'Forespør i byen hvor vi bodde, og i karavanen som vi kom med, vi snakker sant!’»'),
(1679, 12, 83, 'Jakob svarte: «Nei, dette er noe dere selv har funnet på! Her trengs tålmod og standhaftighet. Kanskje Gud vil bringe dem til meg, alle sammen. Han er den Allvitende, den Vise.»'),
(1680, 12, 84, 'Så snudde han seg bort fra dem og utbrøt: «Å, hvor jeg sørger over Josef!» Og sorgens tårer blindet hans øyne. Han var helt overveldet.'),
(1681, 12, 85, 'Da sa brødrene: «Ved Gud, du slutter ikke å snakke om Josef, før du tæres bort eller går til grunne.»'),
(1682, 12, 86, 'Han svarte: «Jeg klager bare min sorg og bedrøvelse for Gud. Og jeg vet fra Gud hva dere ikke vet!'),
(1683, 12, 87, 'Mine sønner, dra ut og ettersøk Josef og hans bror. Fortvil ikke når det gjelder Guds trøst! Fortvile når det gjelder Guds trøst gjør bare vantro.»'),
(1684, 12, 88, 'Da de så trådte inn til Josef, sa de: «Mektige herre, vi og vår slekt er slått av nød! Vi kommer med byttevarer av mindre verdi, men gi oss fullt mål og vis godgjørenhet. Gud belønner dem som viser godgjørenhet.»'),
(1685, 12, 89, 'Da sa han: «Er dere klar over hva dere gjorde mot Josef og hans bror da dere levde i dårskap?»'),
(1686, 12, 90, 'De utbrøt: «Er du virkelig Josef?» «Jeg er Josef,» svarte han: «Og dette er min bror. Gud har vært oss nådig! Den som viser gudsfrykt og standhaftighet, sannelig, Gud lar ikke deres lønn gå tapt, som handler vel.»'),
(1687, 12, 91, 'De sa: «Ved Gud, Gud har sannelig utmerket deg fremfor oss! Vi var i sannhet syndere.»'),
(1688, 12, 92, 'Han svarte: «Ingen bebreidelser over dere i dag! Gud vil tilgi dere. Han er den største i barmhjertighet.'),
(1689, 12, 93, 'Gå nå, og ta med denne skjorten min. Kast den over min fars ansikt, så blir han seende. Bring så til meg hele deres familie!'),
(1690, 12, 94, 'Den gang da karavanen drog av gårde, sa faren: «Jeg synes faktisk jeg kjenner kroppslukten til Josef, selv om dere holder meg for alderdomssløv.»'),
(1691, 12, 95, 'Og de svarte: «Ved Gud! Du er fortsatt i din gamle villfarelse.»'),
(1692, 12, 96, 'Men da nå budet med de gode nyheter kom, og la Josefs skjorte over hans ansikt, ble han igjen seende. Og han utbrøt: «Sa jeg ikke til dere at jeg vet fra Gud ting dere ikke kjenner til!»'),
(1693, 12, 97, 'De svarte: «Far, be om tilgivelse for våre synder! Vi har i sannhet syndet.»'),
(1694, 12, 98, 'Faren sa: «Jeg vil be om tilgivelse for dere hos Herren. Han er den Ettergivende, den Nåderike.»'),
(1695, 12, 99, 'Da de så kom til Josef, tok han sine foreldre til seg, og sa: «Kom inn i Egypt i trygghet, om dette er Guds vilje.»'),
(1696, 12, 100, 'Han tok foreldrene opp til seg på tronen. Alle kastet seg ned for ham i ærefrykt. Han sa: «Far, dette er betydningen av mitt drømmesyn for lenge siden. Herren har gjort det til sannhet! Han viste meg godhet da Han befridde meg fra fengselet, og på nytt da Han førte dere hit fra ørkenen etter at Satan hadde sådd uvennskap mellom meg og mine brødre. Herren finner utvei for det Han vil! Han er den Allvitende, den Vise.'),
(1697, 12, 101, 'Herre, Du har gitt meg herredømme, og Du har lært meg å tyde hendingene. Himlenes og jordens skaper, Du er min venn og beskytter, i denne verden og den hinsidige. Ta imot meg som Deg hengiven, og foren meg med de rettferdige!»'),
(1698, 12, 102, 'Dette hører til beretningen fra det skjulte, som Vi åpenbarer deg. Du var ikke hos brødrene da de enedes om sin plan og la opp råd.'),
(1699, 12, 103, 'Men folk flest tror ikke, hvor meget du enn ønsker det.'),
(1700, 12, 104, 'Du ber dem ikke om lønn for det du gjør. Det er bare tale om en påminnelse til all verden.'),
(1701, 12, 105, 'Hvor mange jærtegn er det ikke i himlene og på jord som de går forbi, og vender seg bort fra?'),
(1702, 12, 106, 'De fleste tror ikke på Gud, uten samtidig å gi Ham medguder.'),
(1703, 12, 107, 'Føler de seg sikre mot en Guds straffedom som overdekker? Eller at timen plutselig inntreffer? Mens de intet aner?'),
(1704, 12, 108, 'Si: «Dette er min vei. Jeg kaller til Gud, og bygger på klarsyn og innsikt, jeg og de som følger meg. Gud være lovet. Jeg er ingen avgudsdyrker!»'),
(1705, 12, 109, 'Før din tid sendte Vi bare menn fra byene som Vi gav åpenbaringer. Har de ikke dratt omkring på jorden, og sett hva enden ble for folk som levde før dem. Sannelig, evighetens hjem er bedre for de gudfryktige. Forstår dere da ikke?'),
(1706, 12, 110, 'Men da sendebudene ble grepet av mismot, og tenkte at de kanskje hadde blitt forledet, da kom Vår hjelp til dem! Og alle ble reddet som Vi bestemte. Vår voldsmakt mot syndige mennesker kan ikke stanses!'),
(1707, 12, 111, 'I beretningen om dem er visselig noe å lære for dem som har hjertets forstand. Dette er ingen oppdiktet historie, men en stadfestelse av det som foreligger fra før, og en utlegging av det hele, som ledelse og nådesbevisning for folk som tror.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Dette er skriftens ord! Det som er åpenbart deg av Herren er sannhet. Men folk flest tror ikke.'),
(1709, 13, 2, 'Gud er det som plasserte himlene der oppe, uten synlige støttepilarer. Derpå tok Han plass på tronen. Han satte sol og måne i tjeneste. Enhver av dem løper med fastsatt tid. Han ordner med alle ting. Han klargjør ordet, så dere må få visshet om møtet med Herrren.'),
(1710, 13, 3, 'Han er det som har utbredt jorden, og laget faste fjell og elver på den. Alle frukter har Han skapt to og to i par. Og Han lar natten dekke over dagen. Sannelig, i dette er jærtegn for folk som tenker etter!'),
(1711, 13, 4, 'Og her finnes jordstykker ved siden av hverandre, vingårder, kornåkrer, palmetrær parvis og enkeltvis, alt vannet med samme vann. Noe foretrekker vi fremfor annet som frukt. Sannelig, i dette er jærtegn for folk som forstår.'),
(1712, 13, 5, 'Om du undrer deg, så er virkelig deres tale verdt undring. «Når vi er blitt støv, skal vi da gjenreises i ny skapning?» Disse er det som fornekter Herren. Disse er det som får lenker om halsen. Disse er det som blir Ildens folk, og der skal de være og bli.'),
(1713, 13, 6, 'De maser på deg om å påskynde det onde fremfor det gode. Det har dog inntruffet, før deres tid, eksempler på timelig straffedom. Herren har visselig tilgivelse for menneskene tross all deres urett, men Herren er streng i straff.'),
(1714, 13, 7, 'De vantro sier: «Hvorfor er det ikke sendt ham et tegn fra hans Herre?» Du er bare en advarer. Ethvert folk har sin rettleder.'),
(1715, 13, 8, 'Gud vet om det enhver kvinne bærer i sitt liv, om det livmoren vil kaste, og om det den vil utvikle. Hos Ham har alle ting sitt mål.'),
(1716, 13, 9, 'Han kjenner det skjulte og det synlige, Han, den Store, den Opphøyde.'),
(1717, 13, 10, 'Det er det samme om noen hemmeligholder det han sier, eller taler åpent, om han skjuler seg i natten eller står frem om dagen.'),
(1718, 13, 11, 'Enhver har et følge engler foran seg og bak seg, som passer på ham på Guds bud. Gud forandrer ikke et folks vilkår, før de forandrer seg selv! Om Gud tilsikter en refselse for et folk, kan det ikke avverges, og de har ingen unntatt Ham som tar seg av deres sak.'),
(1719, 13, 12, 'Han er det som lar dere se lynet med frykt og forventning, og Han lar de tunge skyer trekke opp.'),
(1720, 13, 13, 'Tordenen forkynner Hans pris, og englene, av ærefrykt for Ham. Han sender lynstrålen og rammer med den hvem Han vil. Allikevel strides de om Gud. Han er sterk i voldsomhet!'),
(1721, 13, 14, 'Ham tilkommer den sanne bønn. De som de ber til utenom Ham, kan ikke bønnhøre dem det ringeste. Det er som å strekke ut hendene mot vannet for å få det til munnen, det når den ikke. De vantros bønn er helt forfeilet.'),
(1722, 13, 15, 'For Gud bøyer alle seg i himlene og på jord, med eller mot sin vilje. Så også deres skygger om morgenen og om kvelden.'),
(1723, 13, 16, 'Si: «Hvem er himlenes og jordens Herre?» Si: «Det er Gud!» Si: «Har dere så tatt andre hjelpere utenom Ham som ikke makter å gjøre seg selv verken gagn eller skade!» Si: «Er vel den blinde og den seende like? Er vel lys og mørke likt? Eller har de gitt Gud medguder, som har skapt som hans skaping, så resultatet synes likt for dem?» Si: «Gud er alle tings Skaper! Han er den Ene, den Uovervinnelige.»'),
(1724, 13, 17, 'Han sender vann fra oven, og dalene gjennomstrømmes etter sitt mål. Strømmen bærer skum på overflaten, lignende skummet av det som legges i ilden når man ønsker å lage smykker eller husgeråd. Slik beskriver Gud det reelle og det verdiløse. Skummet forsvinner som bobler, mens det som gagner menneskene forblir på jorden. Slik lager Gud lignelser.'),
(1725, 13, 18, 'De som hører på sin Herre, har det beste i vente. Men de som ikke hører på Ham, om de eide alt på jorden dobbelt opp, ville de tilby det for å kjøpe seg fri. Disse får en slem avregning, og helvete blir deres herberge. Et sørgelig hvilested!'),
(1726, 13, 19, 'Er vel den som innser at det som er åpenbart for deg av Herren er sannhet, lik den som er blind? Bare de som har hjertets forstand tenker etter,'),
(1727, 13, 20, 'de som oppfyller pakten med Gud, og ikke bryter inngått forpliktelse,'),
(1728, 13, 21, 'som knytter det sammen som Gud har bestemt skal høre sammen, og frykter Herren og en hard avregning,'),
(1729, 13, 22, 'som viser standhaftighet og søker Herrens velbehag, som forretter bønnen og gir av det Vi har gitt dem, i det skjulte og åpent, som møter ondt med godt, disse har i vente det endelige hjem, Edens haver.'),
(1730, 13, 23, 'Der vil de tre inn, og de rettskafne av deres fedre, og deres hustruer og barn. Englene kommer inn til dem gjennom alle dører:'),
(1731, 13, 24, '«Fred være med dere for den standhaftighet dere viste!» Hvor herlig er det endelige hjem.'),
(1732, 13, 25, 'Men de som bryter pakten med Gud etter forpliktelsens inngåelse, som skjærer av det som Gud har bestemt skal høre sammen og stifter ufred på jorden, de har forbannelsen i vente, og et sørgelig husly!'),
(1733, 13, 26, 'Gud skjenker rikelig underhold eller begrenser det til dem Han vil. De gleder seg over jordelivet, men jordelivet er i forhold til det hinsidige liv bare en kortvarig glede.'),
(1734, 13, 27, 'De vantro sier: «Hvorfor er det ikke sendt ham et tegn fra hans Herre?» Si: «Gud lar dem Han vil seile sin egen sjø, og leder til Seg de botferdige,'),
(1735, 13, 28, 'dem som tror, og hvis hjerter finner ro ved å komme Gud i hu.» Ja, ved å komme Gud i hu finner hjertet ro.'),
(1736, 13, 29, 'De som tror og lever rettskaffent, dem venter lykksalighet og et godt hjem.'),
(1737, 13, 30, 'Så har Vi da sendt deg til et folk, mens folk før dem er gått bort, for å forkynne for dem det Vi har åpenbart for deg. Men de fornekter den Barmhjertige. Si: «Han er min Herre. Det er ingen gud unntatt Ham. Jeg forlater meg på Ham. Til Ham går min omvendelse.»'),
(1738, 13, 31, 'Om det fantes lesbare ord (Koran) som kunne flytte fjell eller kløve jorden eller nå de døde? Nei, heller ikke det ville nytte! Saken er i sin helhet i Guds hånd. Vet da ikke de troende, at om Gud ville, kunne Han gi alle mennesker ledelsen? De vantro vil ikke opphøre å bli slått av ulykken for det de har bedrevet, eller den vil slå til nær deres bosteder, slik at Guds løfte innfris. Gud bryter ikke sitt løfte.'),
(1739, 13, 32, 'Sendebud før deg er også blitt spottet. Jeg gav de vantro en frist, men så tok Jeg dem fatt. Og hvordan var Min straff!'),
(1740, 13, 33, 'Hva med Ham som våker over alle for det de pådrar seg? Allikevel setter de guder ved Hans side. Si: «Nevn deres navn! Eller vil dere fortelle Ham noe Han ikke vet på jorden, eller nevnes med klare ord?» Nei, de vantros renker synes prydelige for dem, og de blir holdt borte fra veien. Den Gud lar seile sin egen sjø har ingen rettleder.'),
(1741, 13, 34, 'Dem venter refselse i jordelivet. Men det hinsidiges straff er visselig verre, og de har ingen som kan verge dem mot Gud.'),
(1742, 13, 35, 'Slik er paradiset som er lovet de gudfryktige: Bekker sildrer, frukt og skygge er å finne bestandig. Det er deres belønning som viste gudsfrykt. De vantros belønning er Ilden.'),
(1743, 13, 36, 'De som Vi har gitt skriften, gleder seg over det som er åpenbart for deg. Noen særgrupper forkaster en del av det. Si: «Jeg er kun pålagt å tjene Gud, og ikke gi Ham medguder! Til Ham kaller jeg menneskene, og til Ham vender jeg tilbake!»'),
(1744, 13, 37, 'Så har Vi da også sendt dem åpenbaringen som en rettesnor på arabisk. Sannelig, om du følger deres innfall etter det som er blitt deg til del av visdom, så har du ingen som tar seg av din sak eller verger deg mot Gud.'),
(1745, 13, 38, 'Vi har sendt sendebud før deg, og gitt dem hustruer og barn. Men intet sendebud kunne bringe et jærtegn uten Guds bifall! Hver tidsepoke har sin skrift.'),
(1746, 13, 39, 'Gud utvisker eller fastholder det Han vil. Hos Ham finnes skriftens opphav!'),
(1747, 13, 40, 'Og om Vi lar deg se noe av det Vi har stilt de vantro i utsikt, eller Vi tar deg hjem til Oss, deg påligger bare forkynnelsen. Vi tar oss av regnskapet.'),
(1748, 13, 41, 'Har de ikke merket at Vi er på ferde i landet, og innskrenker det fra alle ender? Gud avgjør. Ingen kan omstøte Hans avgjørelse. Han er rask i avregningen!'),
(1749, 13, 42, 'De som levde før dem, smidde sine renker. Men Gud behersker alle renker. Han vet hva enhver fortjener, og de vantro vil snart få vite hvem det endelige hjem tilkommer.'),
(1750, 13, 43, 'De vantro sier: «Du er ikke utsending!» Si: «Gud rekker til som vitne mellom oss, og hertil de som har skriftens kunnskap.»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'En skrift har Vi åpenbart deg, så du må føre menneskene fra mørket til lyset med Herrens velvilje, til den Mektiges, den Lovpristes vei,'),
(1752, 14, 2, 'til Guds vei, Han, som alt i himlene og på jorden tilhører. Ve de vantro for en hard straff,'),
(1753, 14, 3, 'dem som foretrekker jordelivet fremfor det hinsidige, som legger hindringer på Guds vei og vil gjøre den kroket. Disse er kommet langt i villfarelse.'),
(1754, 14, 4, 'Vi har ikke sendt et sendebud uten i sitt folks språk, så han kan gjøre alt klart for dem. Så lar Gud seile sin egen sjø dem Han vil, og gir dem Han vil ledelse. Han er den Mektige, den Vise.'),
(1755, 14, 5, 'Vi sendte Moses med Våre tegn: «Før ditt folk fra mørket til lyset, og påminn dem om Herrens dager.» I dette er sannelig jærtegn for den som er standhaftig og takknemlig.'),
(1756, 14, 6, 'Den gang Moses sa til sitt folk: «Kom i hu Guds godhet mot dere, da Han frelste dere fra Faraos folk, som hjemsøkte dere med onde plager, og drepte deres sønner, men lot kvinnene leve.» I dette lå en stor prøvelse fra Herren!'),
(1757, 14, 7, 'Da Herren forkynte: «Om dere viser takknemlighet, vil Jeg gi dere mer! Men om dere viser utakknemlighet, sannelig, Min straff blir hard!»'),
(1758, 14, 8, 'Og Moses sa: «Om dere er utakknemlige, dere, og alle som på jorden er, så er visselig Gud uavhengig og selvtilstrekkelig, verdig all pris.»'),
(1759, 14, 9, 'Dere har vel hørt fortellingen om dem som levde før dere, Noas folk, Ad og Thamod, og dem som kom etter dem som bare Gud kjenner? Deres sendebud kom til dem med klar beskjed, men de satte hendene for munnen og ropte: «Vi fornekter det budskap dere er sendt med! Vi har foruroligende tvil om det dere kaller oss til!»'),
(1760, 14, 10, 'Deres sendebud svarte: «Er det noen tvil om Gud, himmelens og jordens Skaper? Han kaller dere for å tilgi deres synder og gi dere en viss frist.» Og de sa: «Dere er bare vanlige mennesker som oss som prøver å holde oss borte fra det våre fedre dyrket. Legg frem en klar fullmakt!»'),
(1761, 14, 11, 'Sendebudene svarte: «Vi er bare vanlige mennesker som dere, men Gud gir Sin nåde til hvem Han vil av Sine tjenere. Det er ikke vår sak å bringe dere en fullmakt, med mindre Gud samtykker. På Gud må de troende forlate seg.'),
(1762, 14, 12, 'Skulle ikke vi forlate oss på Gud, som har ledet oss på våre veier? Vi skal bære det dere plager oss med! På Gud må de fortrøstningssøkende fortrøste seg!»'),
(1763, 14, 13, 'De vantro sa da til sine sendebud: «Vi skal fordrive dere fra vårt land, eller dere får gå tilbake til vår tro!» Så åpenbarte Herren for dem: «Vi skal visselig tilintetgjøre illgjerningsmennene,'),
(1764, 14, 14, 'og bosette dere i landet etter dem! Dette, for dem som frykter Min maktstilling og Min advarsel.»'),
(1765, 14, 15, 'De bad om en avgjørelse, og enhver sta voldshersker ble gjort til skamme.'),
(1766, 14, 16, 'Foran ham er helvete, med sårveske å drikke, i små slurker,'),
(1767, 14, 17, 'nesten uten å greie å svelge det. Døden er over ham fra alle kanter, men han kan ikke dø. Foran ham ligger en hard straff.'),
(1768, 14, 18, 'Slik forholder det seg med dem som fornekter Herren. Deres gjerninger er som aske for vinden en stormfull dag. De oppnår intet ved det de har prestert. Dette er villfarelse, langt fremskreden.'),
(1769, 14, 19, 'Innser du ikke at Gud har skapt himlene og jorden på alvor? Om Han vil, kan Han la dere forgå og frembringe en ny skapning.'),
(1770, 14, 20, 'Dette er ingen stor sak for Gud.'),
(1771, 14, 21, 'Alle vil tre frem for Gud! Da vil de svake si til de sterke og hovmodige: «Vi var deres følgesvenner, kan ikke dere nå hjelpe oss av med litt av Guds straff?» Men de svarer: «Om Gud hadde veiledet oss, ville vi veiledet dere. Det spiller ingen rolle for oss om vi gir oss over eller bærer med tålmod, vi har ingen tilflukt.»'),
(1772, 14, 22, 'Satan sier når saken er avgjort: «Gud gav dere et sant løfte. Jeg gav dere også et løfte, men jeg svek dere. Jeg hadde ingen makt over dere. Jeg kunne bare kalle på dere, og så hørte dere på meg. Skyld ikke på meg, men skyld på dere selv. Jeg kan ikke hjelpe dere, og dere kan ikke hjelpe meg. Jeg trodde ikke at dere tidligere satte meg på linje med Gud.» De urettferdige går en smertelig straff i møte.'),
(1773, 14, 23, 'Men de som tror og lever rettskaffent, vil bli ført inn i paradisets haver, hvor bekker sildrer. Her skal de være og bli med Herrens samtykke. Der lyder deres hilsen: «Fred!»'),
(1774, 14, 24, 'Du har vel bemerket at Gud bruker lignelse for et godt ord? Det er som et godt tre, med fast rot og grenene inn i himmelen,'),
(1775, 14, 25, 'som gir sine frukter i sin tid med Herrens velvilje. Gud bruker lignelser for menneskene, så de må komme til ettertanke.'),
(1776, 14, 26, 'Et dårlig ord er som et dårlig tre, som slites opp av jorden, uten hold.'),
(1777, 14, 27, 'Gud befester dem som tror med det faste ord, i denne verden og det hinsidige. Gud lar de urettferdige seile sin egen sjø. Gud gjør det Han vil.'),
(1778, 14, 28, 'Du har vel lagt merke til dem som setter vantro istedenfor Guds nåde, og bringer sitt folk i fortapelsens hus,'),
(1779, 14, 29, 'i helvete, hvor de møter Ilden? Et sørgelig sted å være!'),
(1780, 14, 30, 'Og de har gitt Gud likemenn for å lede bort fra Hans vei. Si: «Ha det så bra! Deres endelikt er Ilden!»'),
(1781, 14, 31, 'Si til Mine tjenere som tror at de skal forrette bønnen og gi av det som Vi har gitt dem, i det skjulte eller åpent, før den dag opprinner hvor det ikke finnes kjøpmannskap eller vennskap.'),
(1782, 14, 32, 'Gud er det, som har skapt himlene og jorden. Han sender regn fra oven, og frembringer derved frukter som dere kan leve av. Han har stilt skipene til deres tjeneste, for å seile på havet på Hans bud. Han har stilt elvene til deres tjeneste,'),
(1783, 14, 33, 'og solen og månen som går trutt i sine baner, og natten og dagen.'),
(1784, 14, 34, 'Han har gitt dere alt dere har bedt Ham om. Om dere ville telle Guds velgjerninger, kunne dere ikke regne dem opp! Men sannelig, mennesket er urettferdig og utakknemlig.'),
(1785, 14, 35, 'En gang sa Abraham: «Herre, gjør denne by til et trygt sted, og hold meg og mine barn borte fra å dyrke avguder.'),
(1786, 14, 36, 'Herre, de har ført mange mennesker på villspor. Den som følger meg, tilhører meg. Om noen er ulydige mot meg, så er Du tilgivende, nåderik.'),
(1787, 14, 37, 'Herre, jeg har bosatt noe av mitt avkom i en dal hvor intet gror, ved Ditt hellige hus. Herre, måtte de forrette bønnen, og la menneskenes hjerter føle velvilje mot dem. La dem få frukt å spise, så de må vise takknemlighet.'),
(1788, 14, 38, 'Herre, Du kjenner til det vi skjuler og det vi bærer åpent frem. Intet er skjult for Gud på jorden eller i himmelen.'),
(1789, 14, 39, 'Gud være lovet som gav meg tross min alder Ismael og Isak. Sannelig, Herren hører min bønn.'),
(1790, 14, 40, 'Herre, la meg og mine etterkommere forrette bønnen. Herre, ta imot vår bønn.'),
(1791, 14, 41, 'Herre, tilgi meg og mine foreldre og de troende på regnskapets dag.»'),
(1792, 14, 42, 'Regn ikke med at Gud overser det de urettferdige bedriver. Han gir dem bare utsettelse, til den dag de blir stive i blikket,'),
(1793, 14, 43, 'med halsen strukket og hodet reist, de blunker ikke med øynene, deres hjerter er tomme.'),
(1794, 14, 44, 'Advar menneskene mot den dag når straffen kommer over dem! De som handlet ondt, vil si: «Herre, gi oss en liten utsettelse, så vi kan høre etter Ditt kall, og følge sendebudene.» Har dere ikke svoret tidligere at det blir ingen slutt for dere?'),
(1795, 14, 45, 'Dere har bodd på boplassene til dem hvis urett slo tilbake på dem selv, og det ble klart for dere hva Vi gjorde med dem, Vi har laget eksempler for dere,'),
(1796, 14, 46, 'og de smidde sine renker, men Gud har hånd om deres renker, selv om det var renker som kunne flytte fjell!'),
(1797, 14, 47, 'Tro ikke at Gud sviker Sitt løfte til Sine sendebud. Gud er mektig og innehar hevnen.'),
(1798, 14, 48, 'Den dag forvandles jorden til en annen jord, og også himlene, og alle trer frem for Gud den Ene, den Uovervinnelige.'),
(1799, 14, 49, 'På denne dag vil du se synderne bundet sammen i lenker,'),
(1800, 14, 50, 'i kjortler av tjære, med ansiktene dekket av ild,'),
(1801, 14, 51, 'slik at Gud kan belønne enhver etter fortjeneste. Sannelig, Gud er rask i avregningen!'),
(1802, 14, 52, 'Dette er en meddelelse til menneskeheten, at de må ta advarsel derved, og vite at Han er én Gud, så alle med hjertets forstand må tenke over det!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Alif Lam Ra. Dette er skriftens og den klare Korans ord.'),
(1804, 15, 2, 'Det kan vel være at de vantro ønsker at de hadde gitt seg over til Gud.'),
(1805, 15, 3, 'La dem spise og fornøye seg og leve i håpet, de får snart å vite!'),
(1806, 15, 4, 'Vi har aldri utslettet en by uten at den hadde sin skrevne bestemmelse.'),
(1807, 15, 5, 'Og intet folk kan foregripe sin frist eller utsette den.'),
(1808, 15, 6, 'Men de sier: «Du som har fått formaningen sendt ned til deg, du er sannelig besatt!'),
(1809, 15, 7, 'Hvorfor har du ikke med englene til oss, om du snakker sant?»'),
(1810, 15, 8, 'Men Vi sender ikke englene uten alvors grunn, og da får de ingen henstand!'),
(1811, 15, 9, 'Vi har åpenbart formaningen, og Vi passer på den.'),
(1812, 15, 10, 'Vi har sendt sendebud før deg, til henfarne folkegrupper.'),
(1813, 15, 11, 'Men ikke et eneste sendebud kom til dem uten at de holdt ham for narr.'),
(1814, 15, 12, 'Slik lar Vi den tilflyte syndernes hjerter.'),
(1815, 15, 13, 'De trodde ikke på den, selv om de henfarnes vanlige skjebne forelå.'),
(1816, 15, 14, 'Om Vi hadde åpnet en port i himmelen for dem, så de når som helst kunne stige opp gjennom den,'),
(1817, 15, 15, 'så ville de si: «Øynene bedrar oss! Vi er forhekset!»'),
(1818, 15, 16, 'Vi har satt stjernebildene på himmelen, og prydet den for dem som beskuer den.'),
(1819, 15, 17, 'Og Vi har befestet den mot enhver forkastet Satan,'),
(1820, 15, 18, 'unntatt den som stjeler seg til å lytte, og han blir forfulgt av en lysende flamme.'),
(1821, 15, 19, 'Vi har bredt ut jorden, og satt faste fjell på den. Og Vi har latt alt mulig i rett balanse vokse frem på den,'),
(1822, 15, 20, 'og gitt dere, og dem dere ikke trenger å forsørge, en levemåte der.'),
(1823, 15, 21, 'Det finnes intet som ikke har sitt forrådskammer hos Oss, men Vi sender det bare ned i bestemte mål.'),
(1824, 15, 22, 'Vi sender de skydrektige vinder og lar det regne fra oven. Så gir Vi dere det å drikke. Det er ikke dere som har det i forvaring.'),
(1825, 15, 23, 'Vi er det som gir liv og lar dø, og Vi alene tiltrer arven.'),
(1826, 15, 24, 'Vi kjenner til dem av dere som kommer først, og dem som kommer sist.'),
(1827, 15, 25, 'Herren vil samle dem. Han er vis, Han vet.'),
(1828, 15, 26, 'Vi skapte mennesket av fuktig leire.'),
(1829, 15, 27, 'Dsjinnene hadde Vi tidligere skapt av flammende ild.'),
(1830, 15, 28, 'En gang sa Herren til englene: «Jeg vil skape mennesket av fuktig leire!'),
(1831, 15, 29, 'Når Jeg har formet ham ferdig og innblåst Min ånd i ham, så fall til fote for ham.»'),
(1832, 15, 30, 'Og englene falt ham til fote.'),
(1833, 15, 31, 'Unntatt Iblis (Satan). Han nektet å falle til fote sammen med de andre.'),
(1834, 15, 32, 'Gud sa: «Hva er det med deg Iblis, at du ikke faller ned som de andre?»'),
(1835, 15, 33, 'Han svarte: «Jeg vil ikke falle et menneskevesen til fote som Du har skapt av fuktig leire.»'),
(1836, 15, 34, 'Gud sa: «Ut med deg herfra! Du er forkastet!'),
(1837, 15, 35, 'Forbannelsen skal hvile over deg til dommens dag!»'),
(1838, 15, 36, 'Han svarte: «Herre, gi meg frist til den dag de oppvekkes!»'),
(1839, 15, 37, 'Gud sa: «Du får frist'),
(1840, 15, 38, 'til en viss tids dag.»'),
(1841, 15, 39, 'Han svarte: «Herre, fordi Du har latt meg komme galt av sted, så vil jeg la alt fremtre prydelig for dem på jorden, og jeg skal sannelig føre dem galt av sted alle sammen,'),
(1842, 15, 40, 'unntatt de av dem som er Dine oppriktige tjenere.»'),
(1843, 15, 41, 'Gud sa: «Dette er en grei ordning for Meg,'),
(1844, 15, 42, 'men du skal ikke ha makt over Mine tjenere, unntatt de av dem som følger deg, som går galt av sted!»'),
(1845, 15, 43, 'Helvete skal bli møtested for dem alle.'),
(1846, 15, 44, 'Det har syv porter, og hver port er tildelt en del av dem.'),
(1847, 15, 45, 'Men de gudfryktige skal være blant haver og kilder:'),
(1848, 15, 46, '«Tred inn med ’Fred’, og i sikkerhet.»'),
(1849, 15, 47, 'Og Vi vil ta bort alt nag som finnes i deres bryst, som brødre skal de sitte overfor hverandre på myke benker.'),
(1850, 15, 48, 'Ingen tretthet vil røre dem, og de vil aldri bli sendt bort derfra.'),
(1851, 15, 49, 'Fortell Mine tjenere at Jeg er den Ettergivende, den Nåderike,'),
(1852, 15, 50, 'men at Min straff er den smertelige straff.'),
(1853, 15, 51, 'Og fortell dem om Abrahams gjester.'),
(1854, 15, 52, 'Da de trådte inn til ham, hilste de: «Fred!» Men han svarte: «Vi er redde for dere!»'),
(1855, 15, 53, 'De sa: «Vær ikke redd! Vi har godt nytt til deg om en forstandig gutt.»'),
(1856, 15, 54, 'Da svarte han: «Bringer dere meg godt nytt, skjønt høy alder har nådd meg? Hvilket godt nytt bringer dere?»'),
(1857, 15, 55, 'De sa: «Vi bringer deg sannhets nytt. Gi ikke opp håpet!»'),
(1858, 15, 56, 'Han svarte: «Hvem gir vel opp håpet om Herrens nåde, unntatt de som farer vill?»'),
(1859, 15, 57, 'Han fortsatte: «Og hva er så deres anliggende, dere utsendinger?»'),
(1860, 15, 58, 'De sa: «Vi er sendt til et folk av syndere,'),
(1861, 15, 59, 'unntatt Lots familie. Dem skal vi redde alle sammen,'),
(1862, 15, 60, 'unntatt hans kone. Vi har bestemt det slik. Hun vil bli av dem som blir tilbake.»'),
(1863, 15, 61, 'Da utsendingene kom til Lots hus, sa han:'),
(1864, 15, 62, '«Dere er fremmedfolk?»'),
(1865, 15, 63, 'Og de svarte: «Nei, vi kommer til deg med det som de alltid tvilte på.'),
(1866, 15, 64, 'Vi kommer til deg med sannheten, og vi er sannferdige.'),
(1867, 15, 65, 'Dra av gårde, du og dine, i en nattetime, og følg du etter bak dem. Ingen av dere må snu seg tilbake. Gå dit dere befales.»'),
(1868, 15, 66, 'Og Vi meddelte ham denne beslutning, så de vil være tilintetgjort før morgenen kommer, til siste mann.'),
(1869, 15, 67, 'Byens innvånere innfant seg med tegn på glede, og han sa:'),
(1870, 15, 68, '«Dette er mine gjester, gjør meg ikke vanære!'),
(1871, 15, 69, 'Frykt Gud, og bring ikke skam over meg!»'),
(1872, 15, 70, 'De svarte: «Har vi ikke forbudt deg å ta imot alskens folk?»'),
(1873, 15, 71, 'Han sa: «Her har dere mine døtre, om dere må gjøre noe galt mot noen.»'),
(1874, 15, 72, 'Så sant du lever, de var helt blinde i sin rus.'),
(1875, 15, 73, 'Men skrallet tok dem ved soloppgang.'),
(1876, 15, 74, 'Vi snudde opp ned på byen, og lot teglstein regne over dem.'),
(1877, 15, 75, 'Sannelig, i dette er jærtegn for de observante.'),
(1878, 15, 76, 'Den ligger ved en større vei.'),
(1879, 15, 77, 'Sannelig, i dette er tegn for de troende.'),
(1880, 15, 78, 'Folket i skogen var også illgjerningsmenn.'),
(1881, 15, 79, 'Vi rammet dem med Vår hevn. I begge er klar rettledning.'),
(1882, 15, 80, 'Folket i al-Hidsjer kalte sendebudene for løgnere.'),
(1883, 15, 81, 'Vi brakte dem Vårt ord, men de vendte seg bort fra det.'),
(1884, 15, 82, 'De hugget seg boliger i fjellene for å være sikre.'),
(1885, 15, 83, 'Men skrallet tok dem ved morgengry.'),
(1886, 15, 84, 'Alt de hadde skaffet seg hjalp ikke.'),
(1887, 15, 85, 'Vi har skapt himlene og jorden og alt som mellom dem er på alvor. Og timen vil inntreffe. Så bær over, med en vakker overbærenhet.'),
(1888, 15, 86, 'Herren er Skaperen, den Allvitende.'),
(1889, 15, 87, 'Vi har gitt deg de syv vers i åpningsbønnen som ofte gjentas, og den storslagne Koran.'),
(1890, 15, 88, 'La ikke ditt blikk strekke seg mot det Vi har gitt enkelte av dem til nytte og glede. Føl deg ikke trist på grunn av dem. Senk din vinge overfor de troende.'),
(1891, 15, 89, 'Si: «Jeg er en klar advarer.»'),
(1892, 15, 90, 'Så som Vi har åpenbart om dem som deler opp og gjør'),
(1893, 15, 91, 'Koranen til et lappverk!'),
(1894, 15, 92, 'Ved din Herre! Vi skal trekke dem til regnskap alle'),
(1895, 15, 93, 'sammen for det de har gjort!'),
(1896, 15, 94, 'Forkynn det du er pålagt, og vend deg bort fra avgudsdyrkerne.'),
(1897, 15, 95, 'For Vi er tilstrekkelig for deg mot dem som driver ap,'),
(1898, 15, 96, 'som setter en annen gud ved Guds side. De skal få å vite!'),
(1899, 15, 97, 'Vi vet at ditt bryst føles beklemt over det de sier.'),
(1900, 15, 98, 'Men utrop din Herres pris! Kast deg ned for Ham!'),
(1901, 15, 99, 'Tjen Herren til den sikre døden når deg.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'Guds ordning realiseres. Prøv ikke å påskynde det. Ære være Ham! Opphøyet er Han over alt de setter ved Hans side!'),
(1903, 16, 2, 'Han sender englene ned med Ånden av Sin ordning over hvem Han vil av Sine tjenere: «Advar, at det er ingen Gud utenom Meg! Så frykt Meg!»'),
(1904, 16, 3, 'Han skapte himlene og jorden på alvor. Opphøyet er Han over det de setter ved Hans side!'),
(1905, 16, 4, 'Mennesket har Han frembrakt av en sæddråpe, og straks er han klart kranglevoren.'),
(1906, 16, 5, 'Husdyrene har Han frembrakt for dere. I dem er varme klær, nyttige ting, og dere spiser dem.'),
(1907, 16, 6, 'De er et vakkert syn for dere når dere driver dem hjem om kvelden, og drar ut med dem om morgenen på beite.'),
(1908, 16, 7, 'De bærer deres oppakning til steder dere bare med stor møye kunne nå. Sannelig. Herren er velvillig, nåderik.'),
(1909, 16, 8, 'Og hesten, muldyret og eslet for at dere kan ri, og som pryd. Han skaper mer enn dere vet.'),
(1910, 16, 9, 'Gud påligger omsorg for veien, men noen går til siden. Om Han hadde villet, kunne Han vist dere alle til veien.'),
(1911, 16, 10, 'Han er det som sender vann fra oven, som drikke for dere. Ved dette vokser trær og kratt, hvor dere kan beite buskapen.'),
(1912, 16, 11, 'Og derved lar Han korn vokse, og oliven, palmetrær, druer og alskens frukt. Heri er sannelig jærtegn for folk som tenker.'),
(1913, 16, 12, 'Han har stilt i deres tjeneste natt og dag, sol og måne, og også stjernene, til tjeneste ved Hans ordning. Heri er sannelig jærtegn for folk som bruker forstanden.'),
(1914, 16, 13, 'I det som Han har latt vokse frem for dere på jorden av forskjellig sort, er det visselig jærtegn for folk som tenker etter.'),
(1915, 16, 14, 'Og Han har stilt havet i deres tjeneste, så dere kan spise fersk føde, og hente ut av det prydsaker, som dere kan bære. Og du ser skipene plaske seg frem for at dere kan søke lykken ved Hans gunst, så dere måtte vise takknemlighet.'),
(1916, 16, 15, 'Han har satt faste fjell på jorden, så den ikke skal svaie med dere. Og elver og fremkomstveier, så dere må finne frem,'),
(1917, 16, 16, 'og landemerker. Og ved stjernene finner man rett vei.'),
(1918, 16, 17, 'Er vel Han som skaper som den som intet skaper? Vil dere ikke tenke etter?'),
(1919, 16, 18, 'Om dere vil telle Guds velgjerninger, kan dere ikke regne dem opp! Gud er tilgivende, nåderik.'),
(1920, 16, 19, 'Gud vet om det dere skjuler, og det dere bærer åpent frem.'),
(1921, 16, 20, 'De guder som menneskene påkaller utenom Gud, har intet skapt.'),
(1922, 16, 21, 'De er selv laget, døde er de, ikke levende, intet aner de når alle gjenoppvekkes.'),
(1923, 16, 22, 'Deres Gud er én Gud! De som ikke tror på det hinsidige liv, deres hjerter avviser, og de viser hovmod.'),
(1924, 16, 23, 'Gud vet uten tvil om det de skjuler, og det de bærer åpent frem. Gud holder ikke av de hovmodige.'),
(1925, 16, 24, 'Om de blir spurt: «Hva har Herren åpenbart?» Så svarer de: «Fabler fra henfarne generasjoner!»'),
(1926, 16, 25, 'Måtte de på oppstandelsens dag få bære sin fulle byrde, og en del av deres som de har ført vill i sin uvitenhet! Ille er det de bærer!'),
(1927, 16, 26, 'De som levde før dem, smidde sine renker, men Gud angrep deres byggverk fra grunnen av. Taket falt ned over dem, og straffedommen kom over dem fra et hold de ikke tenkte seg.'),
(1928, 16, 27, 'Så vil Han på dommens dag gjøre dem til skamme, idet Han sier: «Hvor er mine medguder, som dere laget strid om?» De som har fått innsikt, sier: «I dag overgår skam og ulykke de vantro,'),
(1929, 16, 28, 'som englene henter, mens de gjør urett som slår tilbake på dem selv.» Og de foreslår fred: «Vi gjorde ikke noe galt!» «Jo, Gud vet godt om det dere bedrev!'),
(1930, 16, 29, 'Så gå inn gjennom helvetes porter, og der skal dere være og bli.» De hovmodiges herberge er et bedrøvelig sted!'),
(1931, 16, 30, 'Til de gudfryktige sies det: «Hva har Herren åpenbart?» – og så svarer de: «Det som godt er!» De som gjør det gode har godt i vente i denne verden. Men det hinsidiges bolig er bedre! De gudfryktiges bolig er visselig herlig.'),
(1932, 16, 31, 'I Edens haver går de inn, hvor bekker sildrer. Her får de alt de ønsker. Slik lønner Gud de gudfryktige,'),
(1933, 16, 32, 'dem som englene henter som gode mennesker, idet de sier: «Fred være med dere! Tred inn i paradiset for det dere utrettet.»'),
(1934, 16, 33, 'Forventer de vantro at englene skal komme til dem, eller at Herrens ordning skal realiseres? Slik opptrådte de som levde før dem. Gud gjorde dem ikke urett, men deres egen urett slo tilbake på dem selv.'),
(1935, 16, 34, 'Det onde de gjorde rammet dem, og det de drev ap med innringet dem.'),
(1936, 16, 35, 'De som gav Gud medguder, vil si: «Om Gud hadde villet det slik, ville verken vi eller våre fedre dyrket noe annet enn Ham, og ikke erklært hellig noe utenfor Ham.» Slik gjorde også de som levde før dem. Men påligger det vel sendebudene annet enn den klare forkynnelse?'),
(1937, 16, 36, 'Vi har sendt et sendebud til hvert folk: «Tjen Gud og hold dere unna avguder!» Og det var noen som Gud gav ledelse, og andre som villfarelsen tok. Bare reis omkring på jorden og se hva enden ble for dem som holdt sannhet for løgn.'),
(1938, 16, 37, 'Om du har sterkt ønske om å rettlede dem, så leder ikke Gud dem Han lar seile sin egen sjø, og de har ingen til å hjelpe seg.'),
(1939, 16, 38, 'De sverger sine dyreste eder ved Gud, at Gud ikke oppvekker den som dør. Jo, Han har gitt et bindende løfte! Men folk flest vet ikke.'),
(1940, 16, 39, 'Dette for å klargjøre det de var uenige om, og for at de vantro skal erkjenne at de fór med løgn.'),
(1941, 16, 40, 'Når Vi ønsker en ting, er Vårt ord til den ganske enkelt: «Bli!» Og den blir.'),
(1942, 16, 41, 'De som utvandret for Guds sak, etter at de hadde lidd urett, vil Vi visselig gi et godt sted å være i denne verden. Men lønnen i den hinsidige er større, om de bare visste,'),
(1943, 16, 42, 'de som viser standhaftighet og forlater seg på Herren.'),
(1944, 16, 43, 'Før deg har Vi sendt menn som Vi inspirerte, spør bare formaningens folk, om dere ikke vet,'),
(1945, 16, 44, 'med klar beskjed og med bøkene. For deg har Vi åpenbart formaningen, slik at du kan gjøre klart for menneskene hva som er blitt dem åpenbart, så de måtte tenke over det.'),
(1946, 16, 45, 'Kan vel de som smir onde renker, være sikre på at Gud ikke lar jorden oppsluke dem, eller at straffedommen ikke kommer over dem fra et hold de ikke venter?'),
(1947, 16, 46, 'Eller at Han ikke tar dem fatt der de farter omkring uten at de kan forpurre det?'),
(1948, 16, 47, 'Eller at Han tar dem fatt og hensetter dem i skrekk? Herren er visselig velvillig, nåderik.'),
(1949, 16, 48, 'Har de ikke lagt merke til at alt Gud har skapt, kaster skygger til høyre og venstre, legger seg ned på jorden for Gud i all ydmykhet?'),
(1950, 16, 49, 'Alt som er i himlene og alle krek på jord faller ned for Gud – og englene. De føler seg ikke for store.'),
(1951, 16, 50, 'De frykter Herren som er over dem, og utfører det de er pålagt.'),
(1952, 16, 51, 'Gud har sagt: «Ha ikke to guder! Han er én Gud!'),
(1953, 16, 52, 'Ham tilhører alt i himlene og på jorden. Hans er religionen for alltid. Skulle dere vel frykte noen annen enn Gud?'),
(1954, 16, 53, 'Alle nådebevisninger kommer fra Gud. Når nøden er over dere, så anroper dere Ham.'),
(1955, 16, 54, 'Men tar Han så nøden bort fra dere, se da,'),
(1956, 16, 55, 'da gir en del Herren medguder for å vise utakk for det Vi har gitt dem. Bare nyt livet. Dere får snart å vite!'),
(1957, 16, 56, 'De avsetter en del av grøden Vi har gitt dem til medgudene, som de intet vet om. Ved Gud! Dere skal få svare for det dere har diktet opp!'),
(1958, 16, 57, 'Og de tilskriver Gud døtre, ære være Ham, mens de selv skal ha det de ønsker.'),
(1959, 16, 58, 'Når noen får melding om et nyfødt pikebarn, formørkes hans ansikt, og han må legge bånd på seg.'),
(1960, 16, 59, 'Han gjemmer seg bort fra folk for den dårlige nyhet han har fått. Skal han beholde det i vanære eller grave det ned i sanden? Hvor dårlig er deres vurdering.'),
(1961, 16, 60, 'De som ikke tror på det hinsidige, står for det som dårlig er. Men Gud står for det høyeste. Han er den Mektige, den Vise.'),
(1962, 16, 61, 'Om Gud skulle ta menneskene fatt for deres ondskap, ville det ikke være et krek tilbake på jorden. Men Han gir dem en bestemt frist! Når deres tid kommer, kan de ikke forsinke den en eneste time, og heller ikke påskynde den.'),
(1963, 16, 62, 'De tilskriver Gud det de selv ikke liker, og deres tunger taler løgn, så de skal få det beste? Men det er ingen tvil om at de har Ilden i vente, og dit kommer de fort!'),
(1964, 16, 63, 'Ved Gud! Vi har sendt sendebud til folkene før deg. Men Satan fikk folks adferd til å fortone seg prydelig for dem. Han er leder for dem den dag i dag, men de vil få en smertelig straff.'),
(1965, 16, 64, 'Vi har åpenbart deg skriften for at du skal gjøre klart for dem det de er uenige om, og som ledelse og nådesbevisning til folk som tror.'),
(1966, 16, 65, 'Gud sender vannet fra oven, og gjør med det jorden levende, etter at den lå død. Heri er jærtegn for folk som vil høre.'),
(1967, 16, 66, 'Også i kveget har dere en lærepenge, Vi gir dere å drikke av det de har i sitt indre, blant urenhet og blod finnes ren melk, god å innta for dem som drikker.'),
(1968, 16, 67, 'Slik er det også med fruktene, av palmetrær, og vindruer. Fra disse henter dere berusende drikke og god næring. Heri er jærtegn for folk som bruker forstanden.'),
(1969, 16, 68, 'Herren har inngitt biene: «Finn dere hus i fjell og trær, og i det menneskene setter opp.'),
(1970, 16, 69, 'Spis så av alskens frukt og følg i lydighet Herrens veier.» Og ut av deres indre kommer en saft av skiftende sort, og i den er legedom for menneskene. Heri er jærtegn for folk som bruker tanken.'),
(1971, 16, 70, 'Gud har skapt dere. Så kaller Han dere bort, noen etter å være ført inn i den ynkeligste oldingealder, så de intet vet, som før visste. Gud vet, evner alt.'),
(1972, 16, 71, 'Gud har begunstiget noen fremfor andre med goder. Men de som er begunstiget overlater ikke sine goder til sine slaver, så de står likt i så måte. Skulle de fornekte Guds godhet?'),
(1973, 16, 72, 'Og Gud har gitt dere hustruer fra deres egen krets, og har ved dem gitt dere barn og barnebarn. Han har gitt dere gode gaver! Skulle menneskene da tro på det som er intet verdt, og fornekte Guds godhet?'),
(1974, 16, 73, 'Og istedenfor Gud dyrke slikt som ikke kan skaffe dem noe som helst, verken fra himlene eller jorden, som ikke er i stand til noe?'),
(1975, 16, 74, 'Dere skal ikke lage lignelser om Gud! Gud vet, dere vet intet.'),
(1976, 16, 75, 'Men Gud fremsetter en lignelse om den slave som er eiendom og som ikke råder over noe, og en fri mann som vi har gitt rikelige gaver, og som gir av dette i det skjulte og åpent. Er vel disse like? Gud være lovet! Men de fleste vet ingenting.'),
(1977, 16, 76, 'Gud fremsetter en lignelse om to menn, den ene er stum og evner intet, og er en byrde for sin herre. Hvor han enn sender ham, kommer det intet godt ut av det. Kan vel han settes likt med ham som påbyr rettferd, og selv er på den rette vei?'),
(1978, 16, 77, 'Guds er det uutgrunnelige i himlene og på jord. Og timens inntreffen kan være som et blunk med øyet, eller nærmere. Gud evner alt.'),
(1979, 16, 78, 'Gud har brakt dere frem fra mors liv, mens dere intet visste. Og Han har gitt dere hørsel, syn og forstand, slik at dere måtte vise takknemlighet.'),
(1980, 16, 79, 'Dere har vel sett fuglene som er satt til å være i luften under himmelen. Ingen holder dem, unntatt Gud. Heri er jærtegn for folk som tror.'),
(1981, 16, 80, 'Gud har gitt dere husvære til hvile. Han har gitt dere telt av kvegets skinn, som er lette å håndtere under reise og på rastedagen, og av deres ull, pels og hår, utstyr og redskap med en viss varighet.'),
(1982, 16, 81, 'Ved det Gud har skapt, har Han gitt fjellene som tilfluktssteder. Han har gitt dere klesplagg som verger mot heten, og andre som verger mot deres egen voldsbruk. Slik fullbyrder Han Sin nåde mot dere, at dere må gi dere inn under Hans vilje.'),
(1983, 16, 82, 'Men om de vender ryggen til, deg påligger kun den klare forkynnelse.'),
(1984, 16, 83, 'De erkjenner Guds godhet, og så tar de avstand fra den! De fleste av dem er utakknemlige!'),
(1985, 16, 84, 'Og den dag da Vi fra hvert folk lar et vitne fremstå, da vil ikke de vantro bli hørt, og de vil ikke kunne få rette opp noe.'),
(1986, 16, 85, 'Når de som handler ondt, står overfor straffen, så vil den ikke bli gjort lettere for dem, og de gis ingen utsettelse.'),
(1987, 16, 86, 'Når de som setter medguder ved Guds side, får se gudene sine, vil de si: «Herre, disse er medgudene våre som vi påkalte utenom Deg,» og disse gir dem dette svar: «Dere farer med løgn!»'),
(1988, 16, 87, 'På denne dag tilbyr de Gud underkastelse. Alt de diktet opp har sveket dem.'),
(1989, 16, 88, 'For dem som er vantro og legger hindringer på Guds vei, legger Vi straff til straffen for det onde de har stelt i stand.'),
(1990, 16, 89, 'Og den dag da Vi fra hvert folk lar et vitne fremstå mot dem selv fra deres egne rekker, da vil Vi også føre deg som vitne mot disse! Vi har åpenbart deg skriften for å klargjøre alle ting, og som ledelse og nådesbevisning og et godt budskap for dem som gir seg Gud i vold.'),
(1991, 16, 90, 'Gud påbyr rettferd og godhet, og gavmildhet mot familie. Han forbyr det skjendige og forkastelige, og voldsferd. Han formaner dere, så dere må vise ettertanke.'),
(1992, 16, 91, 'Oppfyll pakten med Gud når den er inngått. Bryt ikke eder når de er svoret, og dere har sagt at Gud innestår. Sannelig, Gud vet hva dere gjør.'),
(1993, 16, 92, 'Vær ikke som kvinnen som har spunnet en fast tråd, og så plukker den fra hverandre til fibre, idet dere bruker deres eder til å lure hverandre, slik at et folk er sterkere enn et annet. Gud setter dere på prøve ved dette. På oppstandelsens dag vil Han gjøre klart for dere det dere var uenige om.'),
(1994, 16, 93, 'Om Gud hadde villet, kunne Han gjort dere til ett eneste (tros)samfunn. Men Han lar dem seile sin egen sjø som Han vil, og gir dem ledelse som Han vil. Og Han trekker dere til regnskap for det dere har gjort.'),
(1995, 16, 94, 'Bruk ikke deres eder til å lure hverandre, så dere mister fotfeste etter å ha stått fast! Dere får smake det onde for å ha lagt hindringer i Guds vei, og dere vil møte en streng straff!'),
(1996, 16, 95, 'Bytt ikke bort Guds pakt for en billig penge! Det som er hos Gud er det beste for dere, om dere bare visste.'),
(1997, 16, 96, 'Det dere har tar en ende, men det som er hos Gud består. Vi vil visselig gi de standhaftige deres lønn etter det beste de har gjort.'),
(1998, 16, 97, 'Den som lever rettskaffent, mann eller kvinne, og som tror, vil Vi oppvekke til et godt liv. Vi vil visselig gi dem deres lønn etter det beste de har gjort.'),
(1999, 16, 98, 'Når du resiterer Koranen, så søk beskyttelse hos Gud mot den forkastede Satan.'),
(2000, 16, 99, 'Han har ingen makt over dem som tror, og setter sin lit til Herren.'),
(2001, 16, 100, 'Hans makt er bare over dem som slutter seg til ham, og gir Ham medguder.'),
(2002, 16, 101, 'Om Vi utskifter et skriftord med et annet, og Gud vet best hva Han åpenbarer, så sier de: «Du bare dikter opp!» Nei, de fleste av dem vet ingenting.'),
(2003, 16, 102, 'Si: «Den Hellige Ånd har åpenbart det fra Herren med sannhet for å styrke dem som tror, og som ledelse og godt budskap for dem som har gitt seg Gud i vold.»'),
(2004, 16, 103, 'Vi kjenner vel til at de sier: «Det er et vanlig menneske som lærer ham.» Men språket til den de sikter til er utenlandsk, mens dette er klart, arabisk språk!'),
(2005, 16, 104, 'De som ikke tror på Guds ord, veileder ikke Gud. De har en smertelig straff i vente.'),
(2006, 16, 105, 'De som ikke tror på Guds ord, dikter opp løgn. Disse er i sannhet løgnere.'),
(2007, 16, 106, 'Den som fornekter troen på Gud etter å ha trodd, ikke den som er under press og hvis hjerte har troens fred – men den som åpner sitt bryst for vantro, den skal Guds vrede ramme, den venter en svær straff.'),
(2008, 16, 107, 'Dette fordi de foretrekker jordelivet fremfor det hinsidige og fordi Gud ikke leder vantro folk.'),
(2009, 16, 108, 'Dette er folk hvis hjerter, øyne og ører Gud har forseglet. Disse er de likegyldige.'),
(2010, 16, 109, 'Det er ingen tvil, disse er taperne i det kommende liv.'),
(2011, 16, 110, 'Overfor dem som utvandret etter prøvelser, som kjempet, og bar med tålmod, vil Herren etter dette vise tilgivelse og nåde,'),
(2012, 16, 111, 'på den dag da enhver står frem for å føre sin egen sak, og enhver lønnes for det han har gjort, og de lider ingen urett.'),
(2013, 16, 112, 'Gud bruker et bilde: En by lå i sikkerhet og fred, og dens levemåte strømmet rikelig fra alle hold. Så viste den utakknemlighet for Guds velgjerninger. Og Gud lot den få over seg hunger og frykt, for det de hadde laget i stand.'),
(2014, 16, 113, 'Et sendebud var jo kommet til dem fra deres egen krets, men de beskyldte ham for å fare med løgn. Så tok straffen dem, urettferdige som de var!'),
(2015, 16, 114, 'Spis av det Gud har gitt dere, som er tillatt og godt. Vis takknemlighet for Guds godhet, om det er Ham dere tjener!'),
(2016, 16, 115, 'Han har bare forbudt dere det selvdøde, blod og svinekjøtt, og slikt som er viet til en annen enn Gud. Men om noen drives av nød, ikke av lyst, og ikke går for langt, så er Gud tilgivende, nåderik.'),
(2017, 16, 116, 'Si ikke om det som dere løgnaktig beskriver: «Dette er tillatt, og dette forbudt,» for å dikte opp løgn mot Gud. De som dikter opp løgn mot Gud, går det ikke godt.'),
(2018, 16, 117, 'De nyter livet en stakket stund, og så venter det dem en smertelig straff.'),
(2019, 16, 118, 'Jødene har Vi forbudt det som Vi har fortalt deg tidligere. Vi gjorde dem ikke urett, men deres egen urett slo tilbake på dem selv.'),
(2020, 16, 119, 'Overfor dem som i uvitenhet har handlet urett, og så omvender seg og forbedrer seg, er Herren etter dette, tilgivende, nåderik.'),
(2021, 16, 120, 'Abraham var en leder, ydmyk og Gud-søkende. Han var ingen avgudsdyrker.'),
(2022, 16, 121, 'Han viste takknemlighet for Hans godhet. Gud utvalgte ham og ledet ham på rett vei.'),
(2023, 16, 122, 'Vi gav ham det som var godt i denne verden, og i den hinsidige vil han visselig være blant de rettferdige.'),
(2024, 16, 123, 'Så gav Vi deg denne inspirasjon: «Følg Abrahams lære som Gud-søker! Han var ingen avgudsdyrker!»'),
(2025, 16, 124, 'Sabbaten ble bare innført for dem som var uenige om læren. Herren vil på oppstandelsens dag treffe avgjørelse mellom dem om det de var uenige om.'),
(2026, 16, 125, 'Kall til Herrens veier med visdom og vakker formaning! Diskuter med dem på beste måte! Herren vet best hvem som vandrer bort fra Hans vei, og hvem som er på rett vei.'),
(2027, 16, 126, 'Om dere gjengjelder overlast, så gjør det i samme mål som dere har lidd urett. Men om dere vil bære det, sannelig, dette er best, for dem som har standhaftighet.'),
(2028, 16, 127, 'Vis tålmod! Ditt tålmod kommer fra Gud! Vær ikke bedrøvet over menneskene, og vær ikke fortrykt for deres renker.'),
(2029, 16, 128, 'Gud er med dem som viser gudsfrykt, og dem som handler vel.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Ære være Ham, som førte sin tjener nattestid, fra den hellige moské til Aqsa-moskeen, hvis omgivelser Vi har velsignet for å vise ham noen av Våre tegn. For Han er den Hørende, den Seende.'),
(2031, 17, 2, 'Vi gav Moses skriften, og gjorde den til ledelse for Israels barn. «Velg ingen annen enn Meg til beskytter,'),
(2032, 17, 3, 'dere etterkommere av dem Vi lot seile med Noa! Han var en takknemlig tjener!»'),
(2033, 17, 4, 'Vi bestemte for Israels barn i skriften: «To ganger vil dere anstifte ufred og fordervelse på jorden, og slå dere opp i overmot.'),
(2034, 17, 5, 'Når løftet oppfylles første gang, sender Vi mot dere Våre tjenere med stor slagkraft, som herjer boplassene, og løftet er oppfylt.'),
(2035, 17, 6, 'Derpå vil Vi la det bli deres tur til å vinne overhånd over dem, og Vi vil gi dere velstand og barn og gjøre dere til en stor hærskare.'),
(2036, 17, 7, 'Om dere handler vel, er det for egen regning. Om dere handler ille, likeledes. Når så løftet oppfylles siste gang, skal de herjende gjøre dere fortvilet, og de vil trenge inn i templet, som de gjorde første gang, og ødelegge fullstendig alt de kommer over.'),
(2037, 17, 8, 'Kanskje Herren vil vise dere nåde, men om dere faller tilbake, kommer Vi også tilbake. Og Vi har gjort helvete til fengsel for de vantro.»'),
(2038, 17, 9, 'Denne Koran leder til det som er rettest, og forkynner et godt budskap til de troende som lever rettskaffent, så de har en stor lønn i vente,'),
(2039, 17, 10, 'mens Vi har gjort klar en smertelig straff for dem som ikke tror på det hinsidige.'),
(2040, 17, 11, 'Mennesket gir ondt fra seg, som han gir godt. Mennesket har det altfor travelt!'),
(2041, 17, 12, 'Vi har gjort natt og dag til jærtegn. Vi utvisker nattens tegn og bringer dagens tegn med muligheten til å se, så dere kan søke lykken ved Herrens gunst. Og for at dere kan holde rede på årenes tall og tidsregningen. Alt har Vi klargjort på en klar måte.'),
(2042, 17, 13, 'Hvert menneskes lodd har Vi festet ved ham selv, om hans hals. På oppstandelsens dag vil Vi bringe frem for ham en bok, som han finner vidt oppslått.'),
(2043, 17, 14, '«Les din bok! I dag strekker du til som regnskapsmann for deg selv!»'),
(2044, 17, 15, 'Den som følger den rette vei, gjør det til egen fordel. Den som går på villstrå, gjør det til eget tap. Ingen bærer en annens byrde. Og vi ilegger ingen straff før Vi har sendt et sendebud.'),
(2045, 17, 16, 'Når Vi vil utslette en by, sender Vi påbud til dens folk i vellevnet, som fortsetter i synd. Så bli ordet realitet for byen, og Vi ødelegger den fullstendig.'),
(2046, 17, 17, 'Hvor mange generasjoner har Vi vel utslettet etter Noa? Herren er tilstrekkelig vel underrettet og observant for Sine tjeneres synder.'),
(2047, 17, 18, 'Om noen begjærer det forgjengelige jordiske, så sender Vi straks til ham der det Vi vil, til dem Vi vil. Men så har Vi helvete rede for ham, hvor han stekes, vanæret og forstøtt.'),
(2048, 17, 19, 'Men om noen begjærer det hinsidige liv og streber etter det slik det skal gjøres, og er troende, disse får erkjennelse for sin innsats.'),
(2049, 17, 20, 'Alle sammen, både disse og de andre, forstrekker Vi med Herrens gaver. Herrens gaver kjenner ingen begrensning.'),
(2050, 17, 21, 'Se, hvordan Vi gir noen fortrinn fremfor andre! Men det hinsidige liv innebærer høyere grader og større fortrinn.'),
(2051, 17, 22, 'Sett ingen gud ved Guds side, så du blir sittende der vanæret og forlatt!'),
(2052, 17, 23, 'Herren har bestemt at dere skal tjene Ham alene. Mot foreldre skal dere vise godhet. Om en av dem, eller begge, når høy alder hos deg, så si ikke «Uff», og vær ikke brysk mot dem, men tal respektfullt til dem!'),
(2053, 17, 24, 'Senk vingen ydmykt av godhet overfor dem, og si: «Herre, forbarm Deg over dem, som de tok seg av meg da jeg var liten.»'),
(2054, 17, 25, 'Herren vet godt hva som bor i deres hjerter, om dere er rettskafne. Han er tilgivende mot de botferdige.'),
(2055, 17, 26, 'Gi din slektning det som tilkommer ham, og den fattige og den veifarende. Men øs ikke ut i sløseri.'),
(2056, 17, 27, 'Sløserne er Satans brødre, og Satan er utakknemlig mot sin Herre.'),
(2057, 17, 28, 'Men om du må avvise dem, idet du ønsker for dem Herrens godhet, som du håper på, så gi dem i hvert fall noen trøsterike ord.'),
(2058, 17, 29, 'La ikke din hånd være lenket til halsen, men strekk den heller ikke ut vid åpen, så du blir sittende der bebreidet og ruinert.'),
(2059, 17, 30, 'Herren gir den Han vil et rikelig eller begrenset underhold. Han er vel underrettet og observant overfor Sine tjenere.'),
(2060, 17, 31, 'Drep ikke deres barn av frykt for fattigdom! Vi vil sørge for dem og dere. Sannelig, å drepe dem er stor synd!'),
(2061, 17, 32, 'Innlat dere ikke på hor. Dette er skjendig adferd og en slett vei å vandre.'),
(2062, 17, 33, 'Drep ikke det liv som Gud har erklært ukrenkelig, med mindre retten krever det. For den som med urett blir drept, har Vi gitt hans nærmeste slektning hevn – autorisasjon. Men han må ikke gå for langt i sin blodhevn. Han vil få hjelp!'),
(2063, 17, 34, 'Kom ikke nær den foreldreløses eiendom unntatt på beste måte, inntil han når moden alder. Overhold forpliktelser. Det vil bli spørsmål om forpliktelsene.'),
(2064, 17, 35, 'Gi fullt mål når dere måler, og vei med riktig vekt! Slik er det godt, og best i siste omgang.'),
(2065, 17, 36, 'Fest deg ikke ved ting du ikke har kjennskap til. Øre, øye og hjerte – alt dette vil det bli spørsmål om.'),
(2066, 17, 37, 'Gå ikke i overmot omkring på jorden. Du kan ikke rive jorden sund eller bli stor som fjellene.'),
(2067, 17, 38, 'Det onde i alt dette vekker mishag hos Herren.'),
(2068, 17, 39, 'Dette hører til den visdom som Herren har åpenbart for deg. Sett ingen gud ved Guds side, så du blir kastet i helvete, fordømt og forstøtt.'),
(2069, 17, 40, 'Er det slik at Herren har hedret dere med sønner, mens Han selv har lagt seg til døtre blant englene? Sannelig, det er svære ting dere sier!'),
(2070, 17, 41, 'Vi har snudd og vendt på det i denne Koran for at de skal komme til ettertanke, men det øker bare deres motvilje.'),
(2071, 17, 42, 'Si: «Om det hadde vært guder sammen med Ham, slik de påstår, så ville de nok søkt en utvei til å nå tronens Herre.»'),
(2072, 17, 43, 'Ære være Ham! Høyt er Han hevet over det de sier!'),
(2073, 17, 44, 'De syv himler og jorden og alle som i dem er, priser Ham. Det finnes intet som ikke lovpriser Ham, men dere oppfatter ikke deres lovprisning. Han er mild, tilgivende.'),
(2074, 17, 45, 'Når du fremsier Koranen, senker Vi et beskyttende slør mellom deg og dem som ikke tror på det hinsidige.'),
(2075, 17, 46, 'Og Vi legger et dekke over deres hjerter og tunghørthet i deres ører, så de ikke forstår den. Når du nevner Herren, og Ham alene, i Koranen, vender de ryggen til i motvilje.'),
(2076, 17, 47, 'Vi vet vel om hvordan de lytter når de hører på deg, og når de har hemmelige overlegninger og de urettferdige sier: «Dere følger bare en forhekset mann!»'),
(2077, 17, 48, 'Se, hvordan de lager sammenligninger om deg, så de kommer på villstrå, og er ute av stand til å finne vei.'),
(2078, 17, 49, 'De sier: «Når vi er morkne ben, skal vi da gjenoppvekkes i en ny skapning?»'),
(2079, 17, 50, 'Si: «Om dere er stein eller jern'),
(2080, 17, 51, 'eller noe dere finner enda verre!» Da vil de svare: «Hvem skal så bringe oss tilbake til livet?» Si: «Han som skapte dere første gang!» Og de vil riste på hodet og si: «Og når skal så dette skje?» Svar: «Kanskje inntreffer det snart.'),
(2081, 17, 52, 'Den dag Han kaller på dere og dere svarer med lovprisning, vil dere tro at dere har ventet bare en kort tid i graven.»'),
(2082, 17, 53, 'Si til Mine tjenere at de skal ordlegge seg på beste måte. For Satan vil skape strid mellom dem. Satan er en klar fiende for menneskene.'),
(2083, 17, 54, 'Herren kjenner dere best. Hvis Han vil det slik, viser Han nåde, og hvis Han vil det slik, ilegger Han straff. Vi har ikke sendt deg som ombudsmann for dem!'),
(2084, 17, 55, 'Herren kjenner best de som er i himlene og på jorden. Vi har gitt noen profeter fortrinn fremfor andre. Til David gav Vi salmene.'),
(2085, 17, 56, 'Si: «Påkall dem dere hevder er ved Hans side!» De makter ikke å fjerne nød fra dere eller fremkalle noen forandring.'),
(2086, 17, 57, 'De som de påkaller, søker selv en måte til å komme Herren nær, og hvem som kan komme nærmest. De håper på Hans nåde, og frykter Hans straff. Sannelig, Herrens straff må man ta seg i akt for!'),
(2087, 17, 58, 'Det finnes ingen by som Vi ikke vil utslette før oppstandelsens dag, eller hjemsøke med streng refselse. Dette er opptegnet i Boken.'),
(2088, 17, 59, 'Det er intet annet som har avholdt oss fra å sende jærtegn enn at de henfarne fornektet dem. Vi gav jo Thamod kamelhoppen til innsikt, men de forgrep seg på den. Vi sender jærtegnene for å skremme.'),
(2089, 17, 60, 'Vi sa jo til deg: «Herren har menneskene under kontroll.» Og Vi gjorde den visjon Vi lot deg se til en anstøtsstein for menneskene. Likeledes treet, som er forbannet, i Koranen. Vi vil sette skrekk i dem, men det øker dem bare i stor oppsetsighet.'),
(2090, 17, 61, 'Eller da Vi sa til englene: «Fall ned for Adam!» Og de falt ned, unntatt Iblis (Satan). Han sa: «Skal jeg falle ned for en Du har skapt av leire!»'),
(2091, 17, 62, 'Og han fortsatte: «Hva mener Du? Denne, som Du har vist ære fremfor meg? Om Du gir meg frist til oppstandelsens dag, vil jeg sannelig underlegge meg hans etterkommere, unntatt få!»'),
(2092, 17, 63, 'Gud sa: «Gå med deg! Om noen av dem følger deg, blir helvete deres belønning, en rikelig belønning.'),
(2093, 17, 64, 'Forfør dem du kan med din røst, fall over dem med ditt rytteri og ditt fotfolk, vær deres kompanjong i eiendom og barn, og gi dem løfter,» men det Satan lover dem, er bare skinn og bedrag:'),
(2094, 17, 65, '«Over Mine tjenere skal du ikke ha makt!» Og Herren strekker til som beskytter.'),
(2095, 17, 66, 'Herren er det, som driver skipet frem på havet for at dere kan søke lykken ved Hans gunst. Sannelig, Han viser dere nåde.'),
(2096, 17, 67, 'Når dere er i havsnød, da er de borte, de dere påkaller, utenom Ham. Men når Han så redder dere i land, da vender dere dere bort. Mennesket er utakknemlig.'),
(2097, 17, 68, 'Er dere sikre på at Han ikke lar kyststripen oppsluke dere, eller sender en skur av stein over dere? Da finner dere ingen som kan ta seg av deres sak.'),
(2098, 17, 69, 'Og er dere sikre på at Han ikke sender dere inn i havsnød på nytt, og sender en stormvind mot dere og drukner dere for deres utakknemlighet? Da finner dere ingen som kan følge opp deres sak overfor Oss.'),
(2099, 17, 70, 'Vi har vist Adams barn ære! Vi har latt dem reise over land og hav, og gitt dem gode ting til underhold, og gitt dem store fortrinn fremfor mangt som vi har skapt.'),
(2100, 17, 71, 'En dag vil Vi kalle inn alle mennesker til deres regnskapsbok. De som får boken i sin høyre hånd, de leser boken opp, og de lider ikke urett så meget som en daddeltrevle.'),
(2101, 17, 72, 'Men den som er blind i denne verden, er også blind i den neste, og enda mer på villstrå.'),
(2102, 17, 73, 'Sannelig, de var nær ved å bringe deg i fristelse, bort fra det Vi har åpenbart deg og til å dikte opp noe annet enn det om Oss! Da ville de saktens trykket deg til sitt bryst.'),
(2103, 17, 74, 'Hadde Vi ikke styrket deg, kunne du vært på nippet til å gå på akkord med dem aldri så lite.'),
(2104, 17, 75, 'Men da ville Vi latt deg smake dobbelt i livet og dobbelt i døden. Og du ville ikke funnet deg noen til å hjelpe deg mot Oss.'),
(2105, 17, 76, 'De var nær ved å skremme deg bort fra landet for å bli kvitt deg. Men så ville de nok ikke blitt der lenge selv, etter deg,'),
(2106, 17, 77, 'den vanlige praksis for våre sendebud før deg. Du finner ingen endring i Vår bruk.'),
(2107, 17, 78, 'Forrett bønnen ved solnedgang frem til nattens frembrudd, og Koran-lesning ved daggry. Daggryets Koran-lesning blir bevitnet.'),
(2108, 17, 79, 'Og våk og be en del av natten som en ekstra fromhetsgjerning. Kanskje Herren vil oppvekke deg til en herlig rang.'),
(2109, 17, 80, 'Og si: «Herre, gi meg en inngang i rettferd, og en utgang i rettferd! Gi meg Din hjelpende kraft!»'),
(2110, 17, 81, 'Og si: «Sannheten er kommet! Det falske er forsvunnet! Det falske er forgjengelig.»'),
(2111, 17, 82, 'I Koranen åpenbarer Vi det som er legedom og nåde for de troende. Men det øker bare fortapelsen hos de urettferdige.'),
(2112, 17, 83, 'Viser Vi mennesket godhet, vender han seg bort og går til side. Men når ondt hjemsøker ham, er han fortvilet.'),
(2113, 17, 84, 'Si: «Enhver handler på sitt vis. Herren kjenner vel til hvem som er på rettest vei!»'),
(2114, 17, 85, 'De spør deg om Ånden. Si: «Ånden er ledd i Herrens styring, men dere er gitt liten kunnskap.»'),
(2115, 17, 86, 'Om Vi vil, kan vi ta bort det Vi har åpenbart for deg. Og du finner ingen som i den anledning kan fremme din sak overfor Oss,'),
(2116, 17, 87, 'unntatt Herrens nåde. Men Hans godhet mot deg er stor!'),
(2117, 17, 88, 'Si: «Om mennesker og dsjinner slo seg sammen for å legge frem noe tilsvarende denne Koran, kunne de aldri lage dens like, selv om de hjalp hverandre.»'),
(2118, 17, 89, 'Vi har snudd og vendt på det for folk, med alskens sammenligninger i denne Koran. Men folk flest avviser alt, unntatt vantro,'),
(2119, 17, 90, 'og sier: «Vi kan aldri tro på deg før du lar en kilde velle frem av jorden for oss,'),
(2120, 17, 91, 'eller du får en have med palmer og druer, og lar bekker sprudle mellom dem i overflod,'),
(2121, 17, 92, 'eller du lar himmelen falle ned over oss i småstykker, slik du har sagt, eller bringer Gud og englene som garanti,'),
(2122, 17, 93, 'eller du får et gullprydet hus, eller du stiger opp til himmelen! Og vi vil ikke tro på din himmelferd før du sender ned til oss et skriftstykke som vi kan lese!» Si: «Ære være Herren! Er jeg noe annet enn et menneske, et sendebud?»'),
(2123, 17, 94, 'Intet annet har avholdt disse mennesker fra å tro når ledelsen kom til dem, enn at de sa: «Har Gud sendt et vanlig menneske som sendebud?»'),
(2124, 17, 95, 'Si: «Om det var engler på jorden som vandret omkring i fred og ro, ville Vi saktens sendt ned til dem en engel fra himmelen som sendebud.»'),
(2125, 17, 96, 'Si: «Gud strekker til som vitne mellom meg og dere. Han er vel underrettet når det gjelder Hans tjenere, og observant.»'),
(2126, 17, 97, 'Den Gud leder er på rett vei. Dem Han lar seile sin egen sjø, for dem finner du aldri hjelpere utenom Ham. På oppstandelsens dag vil Vi samle dem, liggende hodekulls, blinde, stumme, døve. Deres herberge blir helvete. Hver gang det er i ferd med å slukne, frisker Vi på varmen for dem.'),
(2127, 17, 98, 'Dette er deres belønning for at de ikke trodde på Vårt ord og sa: «Når vi er morkne ben, skal vi da gjenoppvekkes i ny skapning?»'),
(2128, 17, 99, 'Innser de ikke at Gud som har skapt himlene og jorden, evner å skape slikt som dem? Han har satt en frist for dem, derom er ingen tvil. Men de urettferdige avviser alt, unntatt vantro.'),
(2129, 17, 100, 'Si: «Om dere disponerte Herrens forråd av nåde, ville dere være påholdne av frykt for å gi bort noe. Mennesket er gjerrig!»'),
(2130, 17, 101, 'Vi gav Moses ni klare jærtegn. Spør bare Israels barn. Han kom til dem, og Farao sa til ham: «Moses, jeg mener du er forhekset!»'),
(2131, 17, 102, 'Og han svarte: «Du vet godt at ingen annen enn Herren over himlene og jorden har sendt dem som jærtegn til innsikt. Jeg mener at du, Farao, er fortapt!»'),
(2132, 17, 103, 'Så bestemte han å jage dem ut av landet. Og Vi lot ham og hans folk drukne alle sammen.'),
(2133, 17, 104, 'Og etter hans tid sa vi til Israels barn: «Slå dere ned i landet!» Og når det hinsidige livs løfte innfris, vil Vi føre dere frem i flokk og følge.'),
(2134, 17, 105, 'Med sannhet har Vi sendt Koranen ned, og med sannhet er den kommet. Deg har Vi sendt som gledesbudbringer og advarer.'),
(2135, 17, 106, 'Koranen har vi delt opp, slik at du kan fremlese den i ro og mak for folk. Vi har sendt den i mange åpenbaringer.'),
(2136, 17, 107, 'Si: «Tro på den, eller tro ikke!» De som før ble gitt visdommen, kaster seg ned når den resiteres for dem,'),
(2137, 17, 108, 'og sier: «Ære være Herren! Herrens løfte er gått i oppfyllelse!»'),
(2138, 17, 109, 'De kaster seg ned på sine ansikter under tårer, og deres ydmykhet øker.'),
(2139, 17, 110, 'Si: «Påkall Gud» eller «påkall den Barmhjertige». Hva dere enn kaller Ham, Hans er de vakreste navn: Vær ikke høyrøstet i din bønn, og vær ikke hviskende, følg en middelvei.'),
(2140, 17, 111, 'Og si: «Lov og pris hører Gud til, som ikke har lagt seg til barn, som ingen har å dele herredømmet med, som ikke har noen hjelper utav svakhet!» Pris Hans storhet, med stor pris!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Lovet være Gud, som åpenbarte skriften for sin tjener, som ikke har gjort den skjev og forvridd, men rett og rak,'),
(2142, 18, 2, 'for å advare mot den voldsmakt som utgår fra Ham, og for å forkynne de troende som handler rett det gode budskap, så de har en herlig lønn i vente,'),
(2143, 18, 3, 'som vil bli dem til del for evig tid.'),
(2144, 18, 4, 'Og for å advare dem, som sier: «Gud har lagt seg til barn.»'),
(2145, 18, 5, 'De har ingen kunnskap om dette, heller ikke deres fedre. En svær ting er det som kommer fra deres munn, de taler intet annet enn løgn!'),
(2146, 18, 6, 'Kanskje vil du ta din død av sorg over stadig å være etter dem om de nå heller ikke vil tro denne fortelling?'),
(2147, 18, 7, 'Vi har skapt alt som på jorden er som pryd for den, og for å sette menneskene på prøve, hvem som handler best av dem.'),
(2148, 18, 8, 'Men se, en gang vil Vi gjøre alt som på den er til øde ørken.'),
(2149, 18, 9, 'Mener du at mennene i hulen og al-Raqim var et under blant Våre jærtegn?'),
(2150, 18, 10, 'Da de unge menn søkte tilflukt i hulen, så sa de: «Herre, vis oss Din nåde, og bered en god vei for oss ut av denne vår situasjon!»'),
(2151, 18, 11, 'Så slo Vi deres ører og gjorde dem bevisstløse mange år i hulen.'),
(2152, 18, 12, 'Derpå vekket Vi dem opp igjen, så Vi kunne få vite hvem som best kunne beregne tiden de hadde tilbrakt.'),
(2153, 18, 13, 'Vi skal i sannhet fortelle deg deres historie. De var unge menn som trodde på sin Herre, og Vi gav dem ytterligere ledelse.'),
(2154, 18, 14, 'Vi befestet deres hjerter, da de reiste seg og sa: «Vår Herre er himlenes og jordens Herre. Aldri vil vi påkalle en gud utenom Ham! I så fall ville vi si det uhyrlige.»'),
(2155, 18, 15, 'Dette vårt folk har lagt seg til guder utenom Ham. Kunne de enda legge frem klar autorisasjon for dem! Og hvem gjør større urett enn den som pådikter Gud løgn?'),
(2156, 18, 16, 'Så når dere har brutt med dem og det de dyrker, så søk tilflukt i hulen, og Herren vil sende Sin nåde over dere og berede en praktisk løsning i deres situasjon.'),
(2157, 18, 17, 'Du ville ha bemerket at når solen stod opp, trakk den seg til høyre for hulen, og når den gikk ned, holdt den seg til venstre for dem, mens de befant seg i området midt imellom. Dette hører til Guds jærtegn. Den Gud rettleder, er på rett vei. Den Han lar seile sin egen sjø, for ham finner du aldri en rettledende hjelper.'),
(2158, 18, 18, 'Du ville holdt dem for våkne der de lå og sov. Vi snudde dem over på høyre side, og på venstre, og hunden deres strakte sine ben ved inngangen. Om du hadde sett dem, ville du snudd og flyktet, fylt av skrekk for dem.'),
(2159, 18, 19, 'Og Vi vekket dem opp, så de kunne snakke med hverandre. En av dem sa: «Hvor lenge har vi vært her?» Og de andre svarte: «En dags tid, eller en del av en dag.» Og de fortsatte: «Herren vet best hvor lenge vi har vært her! Send en av oss av gårde ned til byen med denne sølvmynten, og la ham undersøke hvem som har mest velegnet mat, og så bringe oss forsyninger fra ham. Men han må gå fintfølende til verks, og ikke røpe oss for noen.'),
(2160, 18, 20, 'For hvis de får tak i oss, vil de steinjage oss, eller bringe oss tilbake til sin religion, og da vil det aldri gå oss godt.»'),
(2161, 18, 21, 'Men allikevel lot Vi dem snuble over dem, så de kunne få vite at Guds løfte er sannhet, og at det ikke er tvil om timen. Da de tvistet seg imellom om deres sak, så sa de: «Reis et hus over dem! Deres Herre kjenner dem meget godt!» De som gikk av med seieren i saken om dem, sa: «Vi skal sannelig lage et bedehus (moské) over dem!»'),
(2162, 18, 22, 'Noen vil si at de var tre, og den fjerde av dem var hunden deres, andre at de var fem, og den sjette var hunden, idet de gjettet om skjulte ting. De sier også syv, og at hunden deres var den åttende. Si: «Herren kjenner best deres antall. Bare et fåtall vet om dem.» Diskuter ikke om dem annet enn i forbifarten, og be ingen å uttale seg om dem.'),
(2163, 18, 23, 'Si aldri om noen ting: «Dette vil jeg gjøre i morgen,»'),
(2164, 18, 24, 'uten: «– om Gud vil.» Kom Herren i hu når du glemmer, og si: «Det kan være at Herren vil lede meg til noe som er det rette nærmere enn dette.»'),
(2165, 18, 25, 'De forble i hulen i tre hundre år, og ni til.'),
(2166, 18, 26, 'Si: «Gud vet best hvor lenge de forble. Ham tilhører det skjulte i himlene og på jord. Hvor godt Han ser! Hvor godt Han hører! Utenom Ham har de ingen som kan ta seg av deres sak! Han gir ingen del i Sitt styre!»'),
(2167, 18, 27, 'Forkynn det som er blitt deg åpenbart av Herrens bok. Ingen kan gjøre forandring i Hans ord! Du finner ingen tilflukt utenom Ham!'),
(2168, 18, 28, 'Hold deg sammen med dem som påkaller Herren morgen og kveld, og søker Hans velbehag. La ikke ditt blikk vandre bort fra dem, idet du søker denne verdens glitter. Hør ikke etter noen, hvis hjerte Vi har gjort likegyldig med å komme Oss i hu, som retter seg etter egne lyster og lar det gå over grensen.'),
(2169, 18, 29, 'Si: «Sannheten kommer fra Herren! La den tro som vil, og den være vantro som vil!» Vi har beredt en Ild for de urettferdige, som omslutter dem som et telt. Når de roper om vann, skal de få vann som smeltet kobber, som skålder deres ansikter. For en forferdelig drikk! Og for en forferdelig rasteplass!'),
(2170, 18, 30, 'Men de som tror, og lever rettskaffent, sannelig, Vi lar ikke deres lønn gå tapt, som gjør det gode!'),
(2171, 18, 31, 'Dem venter Edens haver, hvor bekker sildrer. Der vil de bli smykket med armbånd av gull, og får kle seg i grønne klær av silke og brokade. Der skal de ligge tilbakelenet på hvileleier. For en herlig lønn! Og hvilken god rasteplass!'),
(2172, 18, 32, 'Lag en sammenligning for dem om to menn. En av dem gav Vi to vingårder, og omgav dem med palmer med kornåkrer iblant.'),
(2173, 18, 33, 'Begge vingårder bar frukt, og skuffet ikke på noen måte. Gjennom dem hadde Vi latt en bekk strømme,'),
(2174, 18, 34, 'og han fikk rik høst. Da sa han til sin nabo, mens de snakket sammen: «Jeg er mer velstående enn du, og sterkere med hensyn til husfolk!»'),
(2175, 18, 35, 'Og han gikk inn i haven sin, og gjorde en urett som slo tilbake på ham selv, idet han sa: «Jeg tror ikke dette noen gang vil forgå!'),
(2176, 18, 36, 'Jeg tror heller ikke at timen inntreffer. Og om jeg bringes tilbake til Herren, så vil jeg nok finne et bedre oppholdssted enn denne haven!»'),
(2177, 18, 37, 'Hans nabo sa til ham, mens de snakket sammen: «Fornekter du Ham, som har skapt deg av jord, så av en sæddråpe og så formet deg til et menneske?'),
(2178, 18, 38, 'Men jeg derimot, Han er Gud, min Herre! Og jeg setter ingen ved Herrens side.'),
(2179, 18, 39, 'Hvorfor sa du ikke da du gikk inn i haven din: ’Som Gud vil! Det finnes ingen makt, unntatt hos Gud?’ Om du mener at jeg er mindre enn deg i gods og avkom,'),
(2180, 18, 40, 'så vil kanskje Herren gi meg noe som er bedre enn haven din. Og kanskje Han sender over den en avregning fra himmelen, så haven din blir til en øde myr,'),
(2181, 18, 41, 'eller at vannet der synker i jorden så du aldri kan finne det igjen.»'),
(2182, 18, 42, 'Så ble hans frukthøst tilintetgjort, og om morgenen vred han sine hender over alt han hadde lagt ut på den, der nå alt lå over ende, og han sa: «Hadde jeg bare ikke satt noe på linje med Herren!»'),
(2183, 18, 43, 'Han hadde intet husfolk som kunne hjelpe ham istedenfor Gud, og han kunne ikke hjelpe seg.'),
(2184, 18, 44, 'I et slikt tilfelle finnes hjelp og beskyttelse bare hos Gud, den Sanne. Han gir best belønning og best resultat.'),
(2185, 18, 45, 'Lag en sammenligning for dem om livet på jorden. Det er som vann som Vi sender ned fra oven, og som jordens vegetasjon blander seg med. Så blir den til tørre strå, som spres for vinden. Gud evner alle ting!'),
(2186, 18, 46, 'Eiendom og barn er en pryd for det jordiske liv. Men det som varer, de rettferdige gjerninger, gir bedre belønning hos Herren og bedre håp!'),
(2187, 18, 47, 'En dag setter Vi fjellene i bevegelse, og du ser all jorden tre frem. Vi vil samle dem, og ikke la en eneste tilbake.'),
(2188, 18, 48, 'De vil bli fremstilt for sin Herre rekkevis. «Nå er dere kommet til oss, slik Vi skapte dere første gang! Men dere påstod at Vi aldri kunne fastsette en bestemt tid for dere.»'),
(2189, 18, 49, 'Og Boken legges frem. Du ser at synderne er engstelige for dens innhold, og de sier: «Trøste oss, hva er vel dette for bok? Den utelater verken stort eller lite, men regner opp alt.» De finner alt de har gjort til stede. Herren gjør ingen urett.'),
(2190, 18, 50, 'Og Vi sa til englene: «Fall ned for Adam!» Og de kastet seg alle ned unntatt Iblis. Han hørte til dsjinnene, og han forsyndet seg mot Herrens befaling. Vil dere da ta ham og hans avkom til venner fremfor Meg. De som er deres fiender? Et sørgelig bytte for de urettferdige!'),
(2191, 18, 51, 'Jeg har ikke bedt dem avgi vitnemål om himlenes og jordens skapelse, og heller ikke deres egen. Jeg tar ikke slike som villeder folk til støtte!'),
(2192, 18, 52, 'En dag sier Han: «Kall frem Mine medguder som dere snakket om.» Og de kaller på dem, men de hører ikke på dem. Vi lager en avgrunn mellom dem!'),
(2193, 18, 53, 'Så får synderne se Ilden, og de skjønner at de skal falle ned i den, og finner ingen utvei til å vri seg unna.'),
(2194, 18, 54, 'Vi har snudd og vendt på allslags sammenligninger for folk i denne Koran. Mennesket er det mest kranglevorne av alt!'),
(2195, 18, 55, 'Intet annet avholdt menneskene fra å tro da ledelsen kom til dem, og fra å be Herren om tilgivelse, enn deres manglende tro på at henfarne slekters skjebne kunne ramme dem, eller at de kunne stå ansikt til ansikt med straffen.'),
(2196, 18, 56, 'Vi sender Våre sendebud som overbringere av gledesbudskap og advarsler. Men de vantro motsier dem med tomme ord, for derved å nedkjempe sannheten. De driver ap med Mitt ord, og det de er advart mot!'),
(2197, 18, 57, 'Hvem gjør større urett enn den som påminnes om Herrens ord, og vender seg bort fra det, og glemmer hva han har sendt i forveien til regnskapet? Sannelig, vi har lagt et dekke over deres hjerter så de intet forstår, og tunghørthet i deres ører. Om du kaller dem til ledelsen, vil de aldri finne rett vei.'),
(2198, 18, 58, 'Men Herren er den Ettergivende, full av nåde. Ville Han ta dem fatt for det de fortjente, så påskyndet Han straffen for dem. Men de har sin bestemte tid, og den kan de ikke komme unna.'),
(2199, 18, 59, 'Disse byene lot Vi forgå da de gjorde urett. Vi satte en bestemt tid for deres undergang.'),
(2200, 18, 60, 'En gang sa Moses til sin tjener: «Jeg gir ikke opp før jeg når dit hvor de to hav møtes, om jeg enn skal vandre i det uendelige!»'),
(2201, 18, 61, 'Da de nådde møtestedet, glemte de fisken sin, og den drog sin vei ut i havet fri og frank.'),
(2202, 18, 62, 'Da de var kommet videre, sa han til tjeneren: «Ta frem maten vår! Vi har sannelig hatt slit på denne reisen vår!»'),
(2203, 18, 63, 'Og han svarte: «Hva synes du om dette! Da vi rastet ved fjellet, så glemte jeg fisken, det var Satan som fikk meg til å glemme så jeg ikke husket på den, og den tok veien til havet på en underfull måte!»'),
(2204, 18, 64, 'Da sa Moses: «Det var dette vi var ute etter.» Og de snudde og gikk tilbake samme vei som de kom.'),
(2205, 18, 65, 'Så kom de over en av Våre tjenere som Vi hadde vist Vår nåde og undervist i Vår visdom.'),
(2206, 18, 66, 'Moses sa til ham: «Må jeg få følge deg, så du kan lære meg det du selv er blitt lært om å leve rett?»'),
(2207, 18, 67, 'Og han svarte: «Du vil nok ikke kunne bære over med meg i tålmod.'),
(2208, 18, 68, 'Og hvordan skulle du vel kunne bære med tålmod noe som du ikke kan gripe med din kunnskap?»'),
(2209, 18, 69, 'Da sa han: «Du vil finne meg tålmodig, så sant Gud vil, og jeg vil ikke sette meg opp mot deg i noe.»'),
(2210, 18, 70, 'Han svarte: «Hvis du så følger meg, spør meg ikke om noe, før jeg selv begynner å snakke om det til deg.»'),
(2211, 18, 71, 'Så gav de seg i vei, inntil de kom om bord i en båt, og han slo hull i den. Moses sa: «Slo du hull i den for å drukne alle om bord? Du har i sannhet gjort en skrekkelig ting!»'),
(2212, 18, 72, 'Han svarte: «Var det ikke det jeg sa, at du aldri vil kunne bære over med meg i tålmod?»'),
(2213, 18, 73, 'Da sa Moses: «Ta meg ikke fatt fordi jeg glemte, og gjør det ikke for vanskelig for meg.»'),
(2214, 18, 74, 'Så gikk de videre til de møtte en ung mann, og han drepte ham. Da sa Moses: «Har du drept et uskyldig menneske, uten at det er tale om blodhevn? Du har sannelig gjort en forferdelig ting!»'),
(2215, 18, 75, 'Han svarte: «Var det ikke det jeg sa, at du aldri vil kunne bære over med meg i tålmod?»'),
(2216, 18, 76, 'Da sa Moses: «Om jeg heretter spør deg om noe, så hold ikke følge med meg mer. Du har min unnskyldning.»'),
(2217, 18, 77, 'Så gikk de videre til de kom til noen landsbyboere, som de bad om mat. Men de nektet å yte dem gjestfrihet. Her kom de over en mur som var i ferd med å falle ned, og han rettet den opp. Da sa Moses: «Om du hadde villet, kunne du fått lønn for dette.»'),
(2218, 18, 78, 'Men da svarte han: «Dette blir avskjeden mellom oss! Men jeg vil fortelle deg betydningen av det du ikke greide å bære med tålmod.'),
(2219, 18, 79, 'Hva fartøyet angår, så tilhørte dette fattige folk, som arbeidet på havet. Jeg ville beskadige det, for en konge var etter dem, som tok alle båter med vold.'),
(2220, 18, 80, 'Hva den unge mann angår, så var hans foreldre troende, og vi fryktet at han ville påføre dem oppsetsighet og vantro.'),
(2221, 18, 81, 'Og vi ønsket at Herren skulle gi dem en frommere og mer familiekjær sønn i hans sted.'),
(2222, 18, 82, 'Hva muren angår, så tilhørte den to foreldreløse gutter i byen. Under den var en skatt til dem. Deres far var rettskaffen. Og Herren ville at de skulle nå moden alder, og så grave frem skatten sin som en nådegave fra Herren. Jeg gjorde ikke dette på egen hånd. Dette er betydningen av det du ikke greide å bære med tålmod.»'),
(2223, 18, 83, 'Folk spør deg om Dhul-Qarnayn. Si: «Jeg vil fremføre for dere en fortelling om ham.»'),
(2224, 18, 84, 'Vi gav ham makt på jorden, og åpnet en utvei for ham til alt.'),
(2225, 18, 85, 'Og han slo inn på en vei,'),
(2226, 18, 86, 'til han kom dit solen går ned. Han fant at den gikk ned i et gjørmete vannhull, og et folk som bodde ved det. Vi sa: «Dhul-Qarnayn, enten får du avstraffe dem, eller du får vise dem godhet.»'),
(2227, 18, 87, 'Og han svarte: «Den som gjør det onde, vil vi avstraffe. Så vil han bringes tilbake til sin Herre, og Han vil gi ham en svær straff.'),
(2228, 18, 88, 'Men den som tror og gjør det rette, kan vente det beste til gjengjeld, og vi vil behandle ham på en rimelig måte.»'),
(2229, 18, 89, 'Og han slo inn på en annen vei,'),
(2230, 18, 90, 'til han kom dit solen står opp. Han fant at den gikk opp over et folk som Vi ikke hadde gitt noen beskyttelse mot den.'),
(2231, 18, 91, 'Slik var det. Vi visste vel hvordan det var fatt med ham.'),
(2232, 18, 92, 'Og han slo inn på en annen vei,'),
(2233, 18, 93, 'til han kom mellom de to fjell. Han fant et folk som bodde ved deres fot, som nesten ikke kunne forstå det som ble sagt.'),
(2234, 18, 94, 'De sa: «Dhul-Qarnayn! Gog og Magog lager ufred og fordervelse i landet. Hva om vi betaler deg tributt, mot at du bygger en voll mellom oss og dem?»'),
(2235, 18, 95, 'Han svarte: «Den makt min Herre har gitt meg er mer verdt enn penger! Men hvis dere yter god hjelp, skal jeg bygge en mur mellom dere og dem.'),
(2236, 18, 96, 'Bring meg jernbarrer!» Da han hadde fylt opp mellom de to fjellsider, sa han: «Blås!» Da alt var glødende, sa han: «Kom med flytende metall som jeg kan tømme over det!»'),
(2237, 18, 97, 'Nå var de ute av stand til å komme over den, og de greide heller ikke å bryte gjennom.'),
(2238, 18, 98, 'Og han sa: «Dette er en nådesbevisning fra Herren. Men når Herrens løfte – dommedag – realiseres, vil Han jevne den. Herrens løfte er sannhet.'),
(2239, 18, 99, 'Denne dag vil Vi la dem bølge hit og dit. Det blir støtt i trompeten, og Vi samler dem inn alle sammen.'),
(2240, 18, 100, 'De vantro vil denne dag bli stilt overfor helvete,'),
(2241, 18, 101, 'de, hvis øyne var lukket mot Min formaning, som intet kunne høre.'),
(2242, 18, 102, 'Tror de vantro at de kan legge sin sak i Mine tjeneres hånd, fremfor Min? Vi har gjort i stand helvete til rastested for de vantro.'),
(2243, 18, 103, 'Si: «Skal vi fortelle dere om dem som kommer mest til kort med sin innsats?'),
(2244, 18, 104, 'Hvis møye er feilrettet i jordelivet, mens de mener at de handler rett vel?»'),
(2245, 18, 105, 'Det er de som fornekter Herrens ord, og møtet med Ham. Deres gjerninger er uten verdi, Vi tillegger dem ingen vekt på oppstandelsens dag.'),
(2246, 18, 106, 'Helvete er deres belønning, fordi de var vantro og drev ap med Mitt ord og Mine sendebud!'),
(2247, 18, 107, 'Men de som tror og lever rettskaffent, dem venter paradisets haver som rastested,'),
(2248, 18, 108, 'og der skal de være og bli, og ingen forandring vil de ønske.'),
(2249, 18, 109, 'Si: «Om havet var blekk til Herrens ord, ville det være uttømt før Herrens ord var uttømt, selv om vi hjalp på med like meget til!»'),
(2250, 18, 110, 'Si: «Jeg er bare et vanlig menneske, som dere. Men meg er åpenbart at Gud er én Gud. Så la dem som håper å møte sin Herre, gjøre det som rett er, og ikke forbinde med sin dyrkelse av Herren noen annen!»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'Kaf Ha Ya ‘Ayn Sad'),
(2252, 19, 2, 'Omtale av Herrens nåde mot sin tjener Sakarias,'),
(2253, 19, 3, 'da han påkalte Herren i stillhet.'),
(2254, 19, 4, 'Han sa: «Min kropp er gebrekkelig og hodet glinser av grå hår, men aldri er jeg blitt skuffet i min bønn til Deg, min Herre.'),
(2255, 19, 5, 'Jeg frykter mine nære slektninger når jeg er borte. Min hustru er ufruktbar, så skjenk Du meg en rettsetterfølger,'),
(2256, 19, 6, 'som kan arve meg og Jakobs hus, og la ham bli velbehagelig, Herre!»'),
(2257, 19, 7, '«Sakarias, Vi bebuder deg en sønn og hans navn skal være Johannes, Vi har ikke latt ham bli oppkalt etter noen!»'),
(2258, 19, 8, '«Herre,» sa han, «hvordan skal jeg få en sønn når min hustru er ufruktbar og jeg selv er gammel og avfeldig?»'),
(2259, 19, 9, 'Han sa: «Det blir slik! Herren sier: ’Det er lett for Meg! Jeg har før skapt deg, da du ingenting var.’»'),
(2260, 19, 10, 'Han svarte: «Herre, la meg få et tegn,» og Han sa: «Ditt tegn skal være at du ikke skal snakke til folk i tre samfulle døgn.»'),
(2261, 19, 11, 'Så gikk han ut fra helligdommen til folket, og forklarte dem: «Gi lovprisning morgen og kveld!»'),
(2262, 19, 12, '«Hør Johannes, hold deg fast til skriften!» Og Vi gav ham visdom mens han var barn,'),
(2263, 19, 13, 'og mildhet og renhet fra Oss. Han var gudfryktig,'),
(2264, 19, 14, 'og god mot sine foreldre, aldri arrogant og ulydig.'),
(2265, 19, 15, 'Fred over ham, den dag han ble født, og den dag han dør, og den dag han gjenreises levende.'),
(2266, 19, 16, 'Kom i hu Maria i skriften. Hun trakk seg tilbake fra sin familie til et sted i øst,'),
(2267, 19, 17, 'og anla slør fremfor dem. Vi sendte Vår ånd til henne, og den fremstod for henne som en vakker mann.'),
(2268, 19, 18, 'Hun sa: «Jeg ber den Barmhjertige bevare meg mot deg, om du har gudsfrykt.»'),
(2269, 19, 19, 'Han svarte: «Jeg er bare en Herrens utsending for å gi deg en sønn, ren av hjertet.»'),
(2270, 19, 20, 'Da sa hun: «Hvordan skal vel jeg få en sønn, da ingen mann har rørt meg? Jeg er ikke løsaktig!»'),
(2271, 19, 21, 'Han svarte: «Slik blir det! Herren sier: ’Dette er lett for meg! For Vi vil gjøre ham til et tegn for menneskene og en nåde fra Oss. Dette er avgjort sak.’»'),
(2272, 19, 22, 'Så ble hun svanger med barnet, og trakk seg tilbake til et avsides sted.'),
(2273, 19, 23, 'Fødselsveene kom over henne ved et palmetre, og hun sa: «Å, hadde jeg bare vært død før dette, og gjemt og glemt!»'),
(2274, 19, 24, 'Da ropte det til henne fra under henne: «Vær ikke lei deg! Herren har latt en bekk strømme ved dine føtter!'),
(2275, 19, 25, 'Og ryst palmestammen, så faller friske og modne dadler over deg!'),
(2276, 19, 26, 'Spis og drikk, og tørk tårene! Og får du se et menneske, så si: ’Jeg har lovet den Barmhjertige en faste, og vil ikke snakke med noen i dag.’»'),
(2277, 19, 27, 'Så kom hun hjem til sitt folk med barnet i sine armer, og de sa: «Maria, du har gjort noe uhørt!'),
(2278, 19, 28, 'Du Arons søster, din far var ingen dårlig mann, og din mor ingen løsaktig!»'),
(2279, 19, 29, 'Maria gjorde tegn henimot barnet, og de sa: «Hvordan vil du vi skal snakke til den som er et barn i vuggen?»'),
(2280, 19, 30, 'Da sa barnet: «Jeg er Guds tjener. Han har gitt meg skriften, og gjort meg til profet!'),
(2281, 19, 31, 'Han har velsignet meg hvor jeg enn ferdes, og Han har pålagt meg bønn og godgjørenhet så lenge jeg lever,'),
(2282, 19, 32, 'og godhet mot min mor! Han har ikke gjort meg arrogant eller ussel.'),
(2283, 19, 33, 'Måtte fred være over meg den dag jeg ble født, den dag jeg dør, og den dag jeg gjenreises levende!»'),
(2284, 19, 34, 'Dette er Jesus, Marias sønn, med sanne ord, som de er i tvil om.'),
(2285, 19, 35, 'Det er ikke Guds vis å legge seg til barn. Ære være Ham! Når Han bestemmer noe, sier Han bare: «Bli!» Og det blir.'),
(2286, 19, 36, 'Gud er min Herre, og deres Herre! Tjen Ham! Det er rett vei!'),
(2287, 19, 37, 'Men særgrupper er i innbyrdes strid. Ve dem som ikke tror, for den store dag de skal overvære!'),
(2288, 19, 38, 'Hvor godt vil de ikke både høre og se den dag de kommer til Oss! Men i dag er de urettferdige i åpenbar villfarelse.'),
(2289, 19, 39, 'Advar dem mot jammerens dag, når saken blir avgjort, sorgløse og vantro som de er!'),
(2290, 19, 40, 'Vi skal arve jorden, og alle som der er. Til Oss bringes de tilbake.'),
(2291, 19, 41, 'Kom i hu Abraham i skriften! Han var en rettferdig mann, en profet.'),
(2292, 19, 42, 'Han sa til sin far: «Min far, hvorfor tilber du slikt, som verken hører eller ser, og som ikke hjelper deg det ringeste?'),
(2293, 19, 43, 'Min far, kunnskap er blitt meg til del, som ikke har tilfalt deg. Følg meg, så kan jeg lede deg på strak vei.'),
(2294, 19, 44, 'Far, tjen ikke Satan! Satan er opprørsk mot den Barmhjertige.'),
(2295, 19, 45, 'Min far, jeg er redd for at en straff fra den Barmhjertige vil ramme deg, så du slutter deg til Satan.»'),
(2296, 19, 46, 'Han svarte: «Avskyr du mine guder, Abraham? Hvis du ikke holder opp, skal jeg steinjage deg! Hold deg unna meg inntil videre.»'),
(2297, 19, 47, 'Da sa han: «Fred være med deg! Jeg vil be Herren tilgi deg. Han har vist meg velvilje.'),
(2298, 19, 48, 'Men jeg vil holde meg borte fra dere og det dere påkaller fremfor Gud. Og jeg vil be til min Herre. Kanskje blir jeg ikke skuffet i min bønn til Herren.»'),
(2299, 19, 49, 'Da han hadde gått fra dem og det de dyrket fremfor Gud, gav Vi ham Isak og Jakob. Begge gjorde Vi til profeter.'),
(2300, 19, 50, 'Vi gav dem Vår nåde, og den høyeste sannferdighets tale.'),
(2301, 19, 51, 'Kom i hu Moses i skriften! Han var en hengiven mann, og et sendebud og en profet.'),
(2302, 19, 52, 'Vi kalte på ham fra fjellets høyre side, og lot ham komme nær i fortrolighet.'),
(2303, 19, 53, 'I Vår nåde gav Vi ham hans bror Aron, en profet.'),
(2304, 19, 54, 'Og kom i hu Ismael i Skriften! Han var en ordholden mann, og et sendebud og en profet.'),
(2305, 19, 55, 'Han påla sine bønn og godgjørenhet, og var Herren velbehagelig.'),
(2306, 19, 56, 'Kom også i hu Idris i skriften! Han var en rettferdig mann, og en profet.'),
(2307, 19, 57, 'Vi tok ham opp til en høy bolig.'),
(2308, 19, 58, 'Disse er det som Gud har vist nåde blant profetene av Adams slekt, av dem som Vi lot seile med Noa, av Abrahams og Israels barn, av dem Vi har ledet og utvalgt. Når den Barmhjertiges ord ble forkynt for dem, falt de ned i tilbedelse under tårer.'),
(2309, 19, 59, 'Men etter dem kom andre, som forsømte bønnen og fulgte sine lyster. De skal få møte fortapelsen,'),
(2310, 19, 60, 'unntatt de som omvender seg, og tror, og lever rettskaffent. Disse skal gå inn i paradisets haver, og vil ikke lide den minste urett,'),
(2311, 19, 61, 'i Edens haver, som den Barmhjertige har lovet Sine tjenere i det skjulte. Hans løfte oppfylles.'),
(2312, 19, 62, 'Der hører de intet tomt prat, kun ordet «Fred». Og de får sitt underhold morgen og aften.'),
(2313, 19, 63, 'Dette er paradiset som Vi gir i arv til Våre tjenere, som viser gudsfrykt.'),
(2314, 19, 64, 'Vi engler stiger bare ned på Herrens bud. Ham tilhører alt som var og som blir for oss, og det som derimellom er.'),
(2315, 19, 65, 'Herren glemmer intet, Herren over himlene og jorden, og det som derimellom er. Tjen Ham, og vær vedholdende i Hans tjeneste. Kjenner du vel noen som bærer Hans navn?'),
(2316, 19, 66, 'Mennesket sier: «Når jeg er død, skal jeg da bringes frem levende?»'),
(2317, 19, 67, 'Kommer mennesket ikke i hu at Vi skapte ham tidligere, da han intet var?'),
(2318, 19, 68, 'Ved Herren! Vi skal sannelig samle dem, og satanene også! Så skal Vi la dem stille opp rundt helvete på kne.'),
(2319, 19, 69, 'Og så skal Vi ta ut av hver gruppe dem som viste størst arroganse mot den Barmhjertige.'),
(2320, 19, 70, 'Da vil Vi best vite hvem som først og fremst fortjener å møte dets Ild.'),
(2321, 19, 71, 'Det er ingen blant dere som ikke vil komme hen til det. Dette er opp- og avgjort av Herren.'),
(2322, 19, 72, 'Men så vil Vi frelse de gudfryktige, og la de urettferdige bli stående der på kne.'),
(2323, 19, 73, 'Når Vårt ord resiteres for dem som klar beskjed, sier de vantro til de troende: «Hvilken av våre to grupper har den høyeste anseelse og den beste appell?»'),
(2324, 19, 74, 'Hvor mange slektledd har Vi ikke utslettet før dem? Som var mer imponerende i materielle ting og i ytre glans?'),
(2325, 19, 75, 'Si: «Den som beror i villfarelse, måtte den Barmhjertige forlenge hans frist. Inntil de får se det som er stilt dem i utsikt, det være seg en straffedom, eller timen, så får de vite hvem som er verst stilt og har minst forkjempere.»'),
(2326, 19, 76, 'Men Gud øker ledelsen for dem som er på rett vei. Og det som forblir og varer, de gode gjerninger, gir bedre lønn hos Herren, og mer igjen.'),
(2327, 19, 77, 'Du kjenner vel til den som fornekter Vårt ord, og sier: «Jeg skal nok få både rikdom og barn!»'),
(2328, 19, 78, 'Har han vel innblikk i det skjulte? Eller har han gjort avtale med den Barmhjertige?'),
(2329, 19, 79, 'Nei, sannelig! Vi vil notere det han sier og forlenge hans straff, kraftig.'),
(2330, 19, 80, 'Og Vi skal arve det han omtaler. Alene vil han komme til Oss!'),
(2331, 19, 81, 'De har lagt seg til guder utenom Gud for å oppnå makt ved dem.'),
(2332, 19, 82, 'Nei, sannelig! De vil frabe seg deres dyrkelse, og blir deres motstandere.'),
(2333, 19, 83, 'Du har vel sett at Vi har sendt satanene over de vantro, for å sette dem opp?'),
(2334, 19, 84, 'Ta det med ro med dem! Vi teller deres dager.'),
(2335, 19, 85, 'Den dag samler Vi de gudfryktige i én forsamling, hen til den Barmhjertige,'),
(2336, 19, 86, 'og driver synderne til helvete som en kvegflokk,'),
(2337, 19, 87, 'da kan de ikke få noen til å legge inn et ord, unntatt den som har fått avtale med den Barmhjertige.'),
(2338, 19, 88, 'De sier også: «Den Barmhjertige har lagt seg til barn.»'),
(2339, 19, 89, 'Dere har kommet med noe forferdelig!'),
(2340, 19, 90, 'Det er nesten så himlene flerres sund, jorden kløver seg og bergene styrter i grus,'),
(2341, 19, 91, 'fordi de kan tilskrive den Barmhjertige barn.'),
(2342, 19, 92, 'Det er ikke den Barmhjertiges vis å legge seg til barn!'),
(2343, 19, 93, 'Det er ingen i himlene og på jord som ikke vil tre frem for den Barmhjertige som tjener.'),
(2344, 19, 94, 'Han har talt dem opp, og talt dem grundig.'),
(2345, 19, 95, 'Alle trer frem for Ham på oppstandelsens dag alene.'),
(2346, 19, 96, 'De som tror og lever rettskaffent, dem vil den Barmhjertige møte med kjærlighet.'),
(2347, 19, 97, 'Nå har Vi gjort Koranen lettfattelig ved ditt eget språk, slik at du med den kan gi gudfryktige det gode budskap, og advare sta og stridbare folk.'),
(2348, 19, 98, 'Hvor mange slektledd har Vi ikke utslettet før dem? Kan de spore en eneste av dem, eller høre den minste lyd fra dem?');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'Ta Ha'),
(2350, 20, 2, 'Vi har ikke åpenbart Koranen for deg for å gjøre deg ulykkelig,'),
(2351, 20, 3, 'men som påminnelse for den som frykter Gud,'),
(2352, 20, 4, 'og som åpenbaring fra Ham, som skapte jorden og de høye himler.'),
(2353, 20, 5, 'Den Barmhjertige sitter i ro på tronen.'),
(2354, 20, 6, 'Ham tilhører alt i himlene og på jord, det som derimellom er, og det som er under jorden.'),
(2355, 20, 7, 'Tal bare høyt, for Han kjenner det hemmelige selv om det er godt skjult.'),
(2356, 20, 8, 'Gud, det er ingen gud unntatt Ham. Hans er de vakreste navn.'),
(2357, 20, 9, 'Har du hørt beretningen om Moses?'),
(2358, 20, 10, 'Da han fikk se en ild, sa han til sitt husfolk: «Bli her! Jeg har oppdaget en ild. Kanskje jeg kan hente varme fra den, eller jeg kan finne ledelse ved ilden.»'),
(2359, 20, 11, 'Da han gikk frem til den, lød en stemme: «Moses!'),
(2360, 20, 12, 'Jeg er Herren! Ta av deg sandalene! Du er i den hellige dal Tuwa.'),
(2361, 20, 13, 'Jeg har utvalgt deg! Så lytt til det som blir åpenbart!'),
(2362, 20, 14, 'Jeg er Gud. Det er ingen gud unntatt Meg. Så tjen Meg, og forrett bønnen til Min ihukommelse!'),
(2363, 20, 15, 'Timen kommer. Men Jeg holder den skjult en stakket stund, så enhver kan belønnes etter sin innsats.'),
(2364, 20, 16, 'La ikke noen holde deg tilbake fra den, som ikke tror på den, og som følger sine egne lyster, så du ikke går til grunne.'),
(2365, 20, 17, 'Men hva er det du har i din høyre hånd, Moses?»'),
(2366, 20, 18, 'Han svarte: «Det er min stav. Jeg støtter meg på den, og slår ned løv med den til småfeet, og jeg har også andre anvendelser for den.»'),
(2367, 20, 19, 'Han sa: «Kast den, Moses!»'),
(2368, 20, 20, 'Så kastet han den, og se, den ble til en buktende slange.'),
(2369, 20, 21, 'Han sa: «Grip den, og frykt ikke! Vi skal gjøre den slik den var.'),
(2370, 20, 22, 'Og stikk hånden din under armen, og du vil ta den frem igjen hvit, men uskadd. Dette som et annet jærtegn,'),
(2371, 20, 23, 'for å vise deg noen av Våre større tegn.'),
(2372, 20, 24, 'Gå til Farao! Han har vist seg oppsetsig.»'),
(2373, 20, 25, 'Moses svarte: «Herre, gjør mitt bryst åpent for oppgaven,'),
(2374, 20, 26, 'og gjør mitt oppdrag lett for meg.'),
(2375, 20, 27, 'Løs mitt tungebånd,'),
(2376, 20, 28, 'så de må forstå min tale!'),
(2377, 20, 29, 'Og gi meg en medhjelper blant mine,'),
(2378, 20, 30, 'Aron, min bror.'),
(2379, 20, 31, 'Gi meg kraft og styrke ved ham.'),
(2380, 20, 32, 'Knytt ham til mitt oppdrag.'),
(2381, 20, 33, 'så vi kan prise Deg storligen'),
(2382, 20, 34, 'og komme Deg stadig i hu.'),
(2383, 20, 35, 'Du holder øye med oss.»'),
(2384, 20, 36, 'Han sa: «Du får din anmodning oppfylt, Moses!'),
(2385, 20, 37, 'Og Vi har vist deg Vår gunst en gang før,'),
(2386, 20, 38, 'da Vi gav din mor denne inspirasjon,'),
(2387, 20, 39, '’Legg ham i kassen og kast den i floden! Floden vil føre ham til bredden, så en fiende både av Meg og ham kan ta vare på ham.’ Og Jeg sendte over deg Min kjærlighet, at du skulle formes for Mine øyne.'),
(2388, 20, 40, 'Da din søster kom gående, og sa: ’Skal jeg vise dere til noen som kan ta seg av ham?’ Så brakte Vi deg tilbake til din mor, så hun kunne tørke tårene, og ikke sørge. Så drepte du et menneske, og Vi reddet deg ut av elendigheten. Og Vi utsatte deg for andre prøvelser. Mange år tilbrakte du blant Midians folk. Så kom du hit, Moses, som bestemt var.'),
(2389, 20, 41, 'Jeg har utvalgt deg for Min tjeneste.'),
(2390, 20, 42, 'Gå derfor, du og din bror, med Mine jærtegn! Og bli ikke trett av å komme Meg i hu!'),
(2391, 20, 43, 'Gå til Farao! Han har vist seg oppsetsig!'),
(2392, 20, 44, 'Tal rolig og mykt til ham, kanskje lar han seg formane eller kommer i frykt.»'),
(2393, 20, 45, 'De svarte: «Herre, vi er redd for at han forgriper seg på oss, eller slår seg helt vrang.»'),
(2394, 20, 46, 'Han sa: «Frykt ikke! Jeg er med dere, og Jeg hører og ser.'),
(2395, 20, 47, 'Så gå til ham og si: ’Vi er utsendinger fra din Herre! Send Israels barn med oss, og plag dem ikke. Vi er kommet til deg med et jærtegn fra Herren! Fred være med den som følger ledelsen!'),
(2396, 20, 48, 'Det er blitt åpenbart for oss at straffen rammer den som holder den for løgn og vender seg bort.’»'),
(2397, 20, 49, 'Farao sa: «Og hvem er så deres Herre da, Moses?»'),
(2398, 20, 50, 'Han svarte: «Vår Herre er den som gav alt sin skapning, og deretter gav ledelse.»'),
(2399, 20, 51, 'Farao sa: «Og hva med de henfarne slektledd?»'),
(2400, 20, 52, 'Han svarte: «Beskjed om dem finnes hos Herren i en Bok. Herren feiler ikke, og glemmer ikke.'),
(2401, 20, 53, 'Han som gjorde jorden til et leie for dere, laget veier på kryss og tvers, og sendte vann fra oven, hvorved Vi lar planter av ulike slag komme frem.'),
(2402, 20, 54, 'Så spis, og beit deres fe! I dette er sannelig jærtegn for dem som har fornuft.'),
(2403, 20, 55, 'Av jord har Vi skapt dere, og til den lar vi dere vende tilbake, og av den skal Vi la dere stå frem en gang til.»'),
(2404, 20, 56, 'Og Vi viste Farao alle Våre jærtegn. Men han forkastet dem som løgn, og avslo.'),
(2405, 20, 57, 'Og han sa: «Moses, du er kommet til oss for å fordrive oss fra vårt land med din trolldom!'),
(2406, 20, 58, 'Vi skal sannelig vise deg tilsvarende trolldom! Fastsett et møte for oss begge, som ingen av oss må misligholde, og et passende sted!»'),
(2407, 20, 59, 'Moses sa: «Tiden skal være festdagen, og folket må samles tidlig på dagen.»'),
(2408, 20, 60, 'Så trakk Farao seg tilbake, og sanket i hop sine kunster og knep. Derpå kom han.'),
(2409, 20, 61, 'Moses sa til dem: «Ve dere! Dikt ikke opp løgn mot Gud, så Han tilintetgjør dere ved en straffedom! Den som oppdikter løgn, kommer alltid til kort.»'),
(2410, 20, 62, 'De drøftet saken med hverandre og holdt hemmelig råd.'),
(2411, 20, 63, 'Farao tok ordet: «Disse to er utvilsomt trollmenn, som vil fordrive dere fra deres land ved trolldommen sin, og gjøre det av med deres utmerkede profesjon.'),
(2412, 20, 64, 'Sank i hop alle deres kunster og knep, og stig frem på rad. Den som vinner i dag, skal det visselig gå godt!»'),
(2413, 20, 65, 'De sa: «Skal du kaste, Moses, eller skal vi kaste først?»'),
(2414, 20, 66, 'Han svarte: «Nei, kast dere.» Og sannelig, det så ut for ham som om deres taustumper og staver fór omkring ved trolldommen deres.'),
(2415, 20, 67, 'Og Moses kjente frykt i sitt indre.'),
(2416, 20, 68, 'Men Vi sa til ham: «Vær ikke redd! Du er ovenpå!'),
(2417, 20, 69, 'Kast det du har i din høyre hånd, og det vil sluke alt de har laget i stand. For det de har laget i stand er bare trolldomskunster, og en trollmann går det ikke godt, hvor han enn ferdes.»'),
(2418, 20, 70, 'Og trollmennene kastet seg ned i ærefrykt, og sa: «Vi tror på Arons og Mose Herre.»'),
(2419, 20, 71, 'Da sa Farao: «Tror dere på ham før jeg har gitt tillatelse? Så han er deres læremester som har lært dere trolldom! Jeg skal sannelig hugge av hender og føtter i kryss på dere, og derpå korsfeste dere på palmestammer. Dere skal sannelig få vite hvem av oss som er hardest, og mest vedholdende når det gjelder straff!»'),
(2420, 20, 72, 'De svarte: «Vi kan ikke gi deg fortrinn fremfor det vi har sett av klare bevis, fremfor Ham som har skapt oss! Treff avgjørelse slik du vil. Du avgjør bare livet på jorden.'),
(2421, 20, 73, 'Vi tror på vår Herre, at Han må tilgi våre feiltrinn, og det du har tvunget oss til av trolldom. Gud er best og mest vedvarende.»'),
(2422, 20, 74, 'Den som trer frem for Herren som synder, har helvete i vente, hvor man verken kan leve eller dø.'),
(2423, 20, 75, 'Men den som trer frem for Ham som troende, og som har levd rettskaffent, disse når de høyeste stadier,'),
(2424, 20, 76, 'Edens haver, hvor bekker sildrer, og her skal de være og bli. Dette er deres lønn, som renser seg.'),
(2425, 20, 77, 'Vi inspirerte Moses: «Dra av gårde nattestid med mine tjenere, og lag en tørr vei for dem gjennom havet! Frykt ikke å bli innhentet, og vær ikke redd.»'),
(2426, 20, 78, 'Farao forfulgte dem med sin hær, men de ble oppslukt av havet.'),
(2427, 20, 79, 'Farao førte sitt folk vill, og ledet dem ikke rett.'),
(2428, 20, 80, 'Israels barn, Vi reddet dere fra deres fiende! Og Vi sluttet pakt med dere på fjellet Sinais høyre side, og sendte manna og vaktler til dere!'),
(2429, 20, 81, '«Spis av det gode som vi har sendt dere til underhold, men overdriv ikke deri, så dere rammes av Min vrede. Den som rammes av Min vrede, havner i avgrunnen.'),
(2430, 20, 82, 'Men Jeg viser tilgivelse mot den som angrer og tror og gjør det rette, og så finner ledelsen.»'),
(2431, 20, 83, 'Han sa: «Hva har fått deg til å haste av sted, Moses, bort fra ditt folk?»'),
(2432, 20, 84, 'Han svarte: «Folket følger mine spor. Jeg hastet hen til Deg, Herre, for at Du skal bli tilfreds.»'),
(2433, 20, 85, 'Han sa: «Vi har satt ditt folk på prøve i ditt fravær, og samaritaneren har ført dem på gale veier.»'),
(2434, 20, 86, 'Og Moses vendte tilbake til sitt folk, sint og bedrøvet, og sa: «Mitt folk, har ikke Herren gitt dere et vakkert løfte? Falt den fastsatte tid dere lang? Eller ville dere at Herrens vrede skulle ramme dere, siden dere har brutt det dere lovte meg?»'),
(2435, 20, 87, 'De svarte: «Vi har ikke brutt det vi lovte deg på eget initiativ, men vi var nedlesset av byrden av folkets smykker, og så kastet vi dem inn i ilden. Det gjorde samaritaneren også.»'),
(2436, 20, 88, 'Og han brakte ut for dem en brekende kalvefigur, og de sa: «Dette er vår gud og Mose gud! Han har bare glemt ham!»'),
(2437, 20, 89, 'Så de da ikke at den ikke kunne svare på tiltale, og at den verken kunne gjøre dem skade eller gagn?'),
(2438, 20, 90, 'Og Aron hadde tidligere sagt til dem: «Mitt folk, dette er bare en prøve for dere. Deres Herre er den Barmhjertige! Så følg meg, og vær lydige mot min ordre.»'),
(2439, 20, 91, 'Men de svarte: «Vi vil holde oss til den til Moses kommer tilbake til oss.»'),
(2440, 20, 92, 'Da sa Moses: «Hør Aron, hva hindret deg, da du så dem slå inn på gale veier, i å følge meg?'),
(2441, 20, 93, 'Har du vært ulydig mot min ordre?»'),
(2442, 20, 94, 'Han svarte: «Kjære bror, slipp skjegget og hodet mitt! Jeg var redd du ville si: ’Du har splittet Israels barn, og ikke passet på det jeg sa.’»'),
(2443, 20, 95, 'Moses sa: «Og hva har du å si, samaritaner?»'),
(2444, 20, 96, 'Og han svarte: «Jeg har sett hva de ikke kunne se. Jeg tok opp en håndfull jord fra Sendebudets spor, og strødde den utover. Det var en tanke som kom over meg.»'),
(2445, 20, 97, 'Da sa Moses: «Forsvinn! Det skal være din lodd i livet å rope ut: ’Ikke rør meg.’ Et møte forestår deg som du ikke kan unndra deg. Se på din gud som du så ivrig holdt deg til! Vi skal brenne den opp, og så strø dens aske på havet!'),
(2446, 20, 98, 'Deres Gud er Gud alene, uten Ham finnes ingen gud! Han omfatter alt i sin visdom.»'),
(2447, 20, 99, 'På denne måten forteller Vi deg beretninger fra forgangne tider, og Vi har gitt deg en formaning fra Oss.'),
(2448, 20, 100, 'Den som snur seg bort fra den, vil få en byrde å bære på oppstandelsens dag,'),
(2449, 20, 101, 'og for alltid vil de være under den. Det er en sørgelig byrde for dem på oppstandelsens dag!'),
(2450, 20, 102, 'På den dag det støtes i basunen, samler Vi synderne,'),
(2451, 20, 103, 'vilt stirrende, mens de hvisker til hverandre: «Vi var da bare en ukes tid i graven?»'),
(2452, 20, 104, 'Vi vet godt hva de vil si. De mest eksemplariske i sin levemåte sier: «Vi var der bare en dags tid.»'),
(2453, 20, 105, 'De spør deg om fjellenes skjebne, svar: «Herren vil pulverisere dem helt'),
(2454, 20, 106, 'og etterlate dem som en eneste slette,'),
(2455, 20, 107, 'uten bølger eller ujevnhet.»'),
(2456, 20, 108, 'På denne dag følger de oppstandne utroperen som ikke godtar krokveier. Stemmene dempes ydmykt fremfor den Barmhjertige. Du hører bare subbingen av nakne føtter.'),
(2457, 20, 109, 'På denne dag nytter det ikke å legge inn godt ord for noen, med mindre den Barmhjertige går med på det, og da i ordelag som Han er tilfreds med.'),
(2458, 20, 110, 'Han vet hva som foreligger før dem, og etter dem, men de kan ikke mestre det med sin kunnskap.'),
(2459, 20, 111, 'Ansiktene bøyer seg ydmykt frem for den Levende, den Evige. Den som bærer uretts skyld kommer til kort.'),
(2460, 20, 112, 'Men den som lever rettskaffent, og er troende, skal ikke frykte urettferdighet eller tilsidesettelse.'),
(2461, 20, 113, 'Således har Vi åpenbart den som en arabisk Koran. I den har Vi snudd og vendt på advarslene, så menneskene må vise gudsfrykt, eller at den må skape ettertanke hos dem.'),
(2462, 20, 114, 'Opphøyet er Gud, Kongen, den Sanne! Ha ikke hastverk med Koranen til dens åpenbaring til deg er fullbyrdet, og si: «Herre, la meg tilta i visdom!»'),
(2463, 20, 115, 'Vi gjorde fordum en avtale med Adam. Men han glemte. Vi fant ikke hos ham fasthet.'),
(2464, 20, 116, 'Da Vi befalte englene: «Fall ned for Adam,» kastet alle seg ned, unntatt Iblis. Han nektet,'),
(2465, 20, 117, 'og Vi sa: «Adam, dette er en fiende for deg og din hustru. La ham ikke drive dere ut av paradisets have, så du blir ulykkelig.'),
(2466, 20, 118, 'For der trenger du verken å føle deg sulten eller naken,'),
(2467, 20, 119, 'verken tørst eller under solens brann.»'),
(2468, 20, 120, 'Så hvisket Satan til ham: «Adam, skal jeg vise deg til evighetens tre, og en maktstilling som ikke forringes?»'),
(2469, 20, 121, 'Og begge spiste av det. Så ble deres kjønn klart for dem, og de begynte å feste på seg av havens løv. Adam var ulydig mot Herren, og kom på villstrå.'),
(2470, 20, 122, 'Men så utvalgte Herren ham, vendte seg mot ham i nåde og gav ham ledelse.'),
(2471, 20, 123, 'Han sa: «Kom dere ned herfra begge to, alle, og dere mennesker skal være hverandres fiender! Men det vil komme ledelse fra Meg, og den som følger Min ledelse, farer ikke vill og blir ikke ulykkelig.'),
(2472, 20, 124, 'Men den som vender seg bort fra Min formaning, vil få et liv i trengsel, og på oppstandelsens dag vil Vi oppvekke ham blind.»'),
(2473, 20, 125, 'Han vil spørre: «Herre, hvorfor har Du oppvekket meg blind, jeg som var seende?»'),
(2474, 20, 126, 'Og Han vil svare: «Slik er det. Vårt ord kom til deg, og du glemte det. Slik blir du i dag glemt.»'),
(2475, 20, 127, 'Slik belønner Vi de lettsindige, som ikke tror på Herrens ord. Straffen i det hinsidige er ytterst streng, og varig.'),
(2476, 20, 128, 'Er det ikke en pekepinn for menneskene hvor mange slektledd vi har latt gå til grunne før dem, på hvis boplasser de nå kan vandre omkring? I dette er sannelig jærtegn for dem som har fornuft.'),
(2477, 20, 129, 'Om det ikke forelå et ord fra Herren, og en fastsatt frist, ville det tilsvarende vært iverksatt.'),
(2478, 20, 130, 'Bær med tålmod det de sier, og lov og pris Herren før solens oppgang og dens nedgang. Lov og pris Ham i nattetimene og ved dagens grenser, så du må være velbehagelig.'),
(2479, 20, 131, 'La ikke dine øyne dvele ved det Vi lar noen nyte, som pryd for jordelivet, for derved å sette dem på prøve. Herrens underhold er bedre, og mer varig.'),
(2480, 20, 132, 'Pålegg dine å forrette bønnen, og vær selv vedholdende i den. Vi ber ikke deg om underhold. Det er Vi som underholder deg! Det endelige utfall er til gudsfryktens gunst.'),
(2481, 20, 133, 'Folk sier: «Hvorfor bringer han oss ikke et tegn fra sin Herre?» Har de ikke fått klar beskjed om det som står på de gamle skriftblader?'),
(2482, 20, 134, 'Hadde Vi utslettet dem ved en straffedom før profeten, ville de visselig sagt: «Herre, hvorfor sendte Du oss ikke et sendebud, så vi kunne fulgt Ditt ord før ydmykelse og skam rammet oss?»'),
(2483, 20, 135, 'Si: «Alle venter, så vent også dere. Dere vil få vite hvem som sogner til den strake vei, som er på rett vei!»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'Regnskapsdagen nærmer seg for menneskene, men i sorgløshet vender de seg bort.'),
(2485, 21, 2, 'Ingen ny formaning fra Herren kommer til dem uten at de lytter lekende og lett om hjertet.'),
(2486, 21, 3, 'De urettferdige hvisker til hverandre: «Dette er jo bare et vanlig menneske som dere! Vil dere godta trolldom med åpne øyne?»'),
(2487, 21, 4, 'Sendebudet sier: «Herren vet hva som sies i himmel og på jord. Han er den Hørende, den Allvitende.»'),
(2488, 21, 5, 'Men de sier: «Dette er et virvar av drømmer! Han har oppdiktet det! Han er en skald! La Ham bringe oss et tegn, som de gamle profeter var utstyrt med.»'),
(2489, 21, 6, 'Ingen by før deres tid har Vi ødelagt, som trodde. Vil de da ikke tro?'),
(2490, 21, 7, 'Før deg sendte Vi også menn som Vi gav åpenbaringer. Bare spør de folk som har formaningen (jøder og kristne), om dere ikke vet det.'),
(2491, 21, 8, 'Men Vi gav profetene ikke legemer som ikke trengte mat, og de var ikke udødelige.'),
(2492, 21, 9, 'Men Vi innfridde Vårt løfte til dem, og reddet dem, og dem Vi bestemte, og tilintetgjorde de lettsindige.'),
(2493, 21, 10, 'Nå har Vi åpenbart for dere en skriftens bok med deres formaning. Vil dere da ikke forstå?'),
(2494, 21, 11, 'Hvor mang en by full av urett har Vi ikke lagt i grus, og latt andre folk fremstå?'),
(2495, 21, 12, 'Når de ante Vårt straffetiltak, så flyktet de over hals og hode, ut av dem.'),
(2496, 21, 13, 'Noen sa: «Flykt ikke! Vend tilbake til det vellevnet dere nøt og deres boliger, at dere kan avlegge regnskap.»'),
(2497, 21, 14, 'Andre sa: «Trøste oss, vi levde i urett!»'),
(2498, 21, 15, 'Og deres roping fortsatte til Vi meiet dem ned, og de lå der sluknet i døden.'),
(2499, 21, 16, 'Vi skapte ikke himmel og jord og det som mellom dem er for moro skyld.'),
(2500, 21, 17, 'Om Vi ønsket å finne tidsfordriv, kunne Vi finne det hos Oss selv, om så var.'),
(2501, 21, 18, 'Tvert imot, Vi vil slynge det ekte mot forgjengelighetens verdiløshet, og knuse dens hode, og se, den forgår! Ve over dere for det dere fremholder.'),
(2502, 21, 19, 'Ham tilhører alle i himlene og på jord! De som er i Hans nærhet føler seg ikke for store til å tjene Ham, og de slapper ikke av.'),
(2503, 21, 20, 'De priser Ham natt og dag, uten opphold.'),
(2504, 21, 21, 'Mon de har hentet guder fra jorden som gjenoppvekker de døde?'),
(2505, 21, 22, 'Om det fantes i himlene og på jord guddommer andre enn Gud, ville himlene og jorden gå under. Så ære være Gud, tronens Herre, langt borte fra alt de fremholder!'),
(2506, 21, 23, 'Han står ikke til ansvar for hva Han gjør, men de står til ansvar!'),
(2507, 21, 24, 'Har de lagt seg til guder utenom Ham? Si: «Legg frem deres bevis! Dette er mine samtidiges og de henfarnes formaning! Men folk flest kjenner ikke sannheten, og vender seg bort.»'),
(2508, 21, 25, 'Vi sendte aldri noe sendebud før deg uten at Vi gav ham denne åpenbaring: «Det er ingen gud unntatt Meg! Så tjen meg!»'),
(2509, 21, 26, 'De sier: «Den Barmhjertige har lagt seg til barn.» Ære være Ham! De er tvert imot beærede tjenere – englene!'),
(2510, 21, 27, 'De griper ikke ordet før Ham, og de utfører Hans ordrer.'),
(2511, 21, 28, 'Han vet hva de har foran seg og etter seg, og de legger ikke inn ord for noen, unntatt for dem som har Hans velbehag, og de skjelver av ærefrykt for Ham.'),
(2512, 21, 29, 'Og om noen av dem skulle si: «Jeg er en gud utenom Ham,» så ville Vi lønne ham for dette med helvete. Slik lønner Vi de urettferdige.'),
(2513, 21, 30, 'Innser ikke de vantro at himlene og jorden var tetteste masse, og at Vi skilte dem? Og av vannet frembrakte Vi alt levende. Vil de da ikke tro?'),
(2514, 21, 31, 'Vi satte på jorden de faste fjell, så den ikke skulle vakle, og laget på den veipassasjer, så menneskene måtte finne frem.'),
(2515, 21, 32, 'Og himmelen har Vi gjort til et vel bevart tak. Men de vender seg bort fra Våre jærtegn.'),
(2516, 21, 33, 'Han er det, som har skapt natt og dag og sol og måne som begge svømmer i sin bane.'),
(2517, 21, 34, 'Vi har ikke gitt noe menneskelig vesen før deg udødelighet. Om du dør, skulle vel de være udødelige?'),
(2518, 21, 35, 'Alle må smake døden. Vi setter dere på prøve, med ondt og godt som prøvelser, og så bringes dere tilbake til Oss.'),
(2519, 21, 36, 'Når de vantro ser deg, driver de ap med deg: «Er dette han som omtaler gudene våre?» Men den Barmhjertiges omtale tror de ikke på.'),
(2520, 21, 37, 'Menneskene ble skapt med hast i seg. Jeg skal vise dere Mine tegn, men mas ikke på Meg.'),
(2521, 21, 38, 'De sier: «Og når skal så denne trusselen realiseres? Om dere snakker sant?»'),
(2522, 21, 39, 'Kjente bare de vantro den tid da de verken kan berge ansikt eller rygg mot Ilden, og de ikke hjelpes kan!'),
(2523, 21, 40, 'Nei, den kommer over dem plutselig og lamslår dem, og de er ute av stand til å avverge den. Og ikke får de utsettelse.'),
(2524, 21, 41, 'Det ble drevet ap med sendebud før din tid. Men de som hånet dem, ble grepet nettopp av det de drev ap med!'),
(2525, 21, 42, 'Si: «Hvem skal beskytte dere mot den Barmhjertiges natt og dag?» Allikevel vender de seg bort fra Herrens formaning.'),
(2526, 21, 43, 'Eller har de guder utenom oss som kan forsvare dem? Disse kan ikke hjelpe seg selv, og de gis ingen beskyttelse mot Oss.'),
(2527, 21, 44, 'Vi gav dem selv og deres fedre gode dager til deres liv syntes dem langt. Ser de da ikke at Vi er kommet til jorden, og gjør grensene trangere? Er vel de seierherrene?'),
(2528, 21, 45, 'Si: «Jeg gir dere bare en advarsel med åpenbaringen.» Men de døve hører ikke ropet når de advares.'),
(2529, 21, 46, 'Men om et pust av Herrens straff rører dem, så vil de si: «Trøste oss! Vi gjorde urett!»'),
(2530, 21, 47, 'Vi skal sette opp riktige vektskåler til oppstandelsens dag, så ingen skal lide den minste urett. Og selv om det har et sennepskorns vekt, skal Vi trekke det frem. Vi er fullgode til å holde avregning.'),
(2531, 21, 48, 'Vi gav Moses og Aron kriteriet for ondt og godt, og lys og formaning for de gudfryktige,'),
(2532, 21, 49, 'som frykter Herren i det skjulte, og bever for timen.'),
(2533, 21, 50, 'Dette er en velsignet formaning som Vi har åpenbart. Vil dere da ikke vite noe av den?'),
(2534, 21, 51, 'Tidligere gav Vi Abraham hans rettskaffenhet, for Vi kjente ham.'),
(2535, 21, 52, 'Han sa til sin far og sitt folk: «Hva er dette for billedstøtter som dere holder dere til?»'),
(2536, 21, 53, 'Og de svarte: «Vi fant at våre fedre dyrket dem.»'),
(2537, 21, 54, 'Da sa han: «Da har både dere og deres fedre vært i klar villfarelse.»'),
(2538, 21, 55, 'De svarte: «Er du kommet til oss med sannhet, eller farer du med spøk?»'),
(2539, 21, 56, 'Han sa: «Nei, deres Herre er himlenes og jordens Herre, Han som skapte dem, og jeg bærer vitnesbyrd om dette.'),
(2540, 21, 57, 'Ved Gud! Jeg skal sannelig lure deres avguder, så snart dere har snudd ryggen til og gått!»'),
(2541, 21, 58, 'Så slo han dem i småstykker, unntatt en stor en som de hadde, så de kunne vende tilbake til denne.'),
(2542, 21, 59, 'De sa: «Hvem har gjort dette med våre guder? Han må være en illgjerningsmann!»'),
(2543, 21, 60, 'Noen sa: «Vi har hørt en ung mann omtale dem, han kalles Abraham.»'),
(2544, 21, 61, 'Da sa de: «Bring ham for folkets øyne, kanskje de kan avgi vitnemål.»'),
(2545, 21, 62, 'De spurte: «Er det du som har gjort dette med våre guder, Abraham?»'),
(2546, 21, 63, 'Han svarte: «Nei, det var denne storkaren blant dem som gjorde det. Bare spør dem, om de kan si noe.»'),
(2547, 21, 64, 'Så gikk de i seg selv, og sa: «Sannelig, vi gjør urett.»'),
(2548, 21, 65, 'Men så snudde de tvert om: «Du vet godt at disse ikke kan tale!»'),
(2549, 21, 66, 'Og han svarte: «Vil dere da tjene i Guds sted noe som verken kan gagne eller skade dere det ringeste?'),
(2550, 21, 67, 'Skam over dere og det dere tjener i Guds sted! Vil dere da ikke forstå?»'),
(2551, 21, 68, 'Da sa de: «Brenn ham, og hjelp gudene deres, om dere skal gjøre noe.»'),
(2552, 21, 69, 'Da sa Vi: «Ild, vær kjølig og uskadelig for Abraham!»'),
(2553, 21, 70, 'De forsøkte et knep med ham, men Vi gjorde dem til de største tapere.'),
(2554, 21, 71, 'Og Vi reddet ham og Lot til det land Vi har velsignet for all verden.'),
(2555, 21, 72, 'Og Vi gav ham Isak, og dertil Jakob. Og alle gjorde Vi rettferdige,'),
(2556, 21, 73, 'og til ledere for å rettlede ved Vårt bud. Og Vi inngav dem å gjøre gode gjerninger, å forrette bønnen, og å gi gode gaver. Og de tjente Oss.'),
(2557, 21, 74, 'Lot gav Vi visdom og kunnskap. Og Vi reddet ham fra den by som gjorde skjendige ting. De var onde og ugudelige folk.'),
(2558, 21, 75, 'Vi førte ham inn i Vår nåde. Han var visselig rettferdig.'),
(2559, 21, 76, 'Og Noa, da han i fordums tid anropte. Vi bønnhørte ham, og reddet ham og hans fra den store trengsel.'),
(2560, 21, 77, 'Og Vi hjalp ham mot et folk som forkastet Vårt ord. De var onde mennesker, så Vi druknet dem alle sammen.'),
(2561, 21, 78, 'Og David og Salomo, da de var voldgiftsmenn om åkeren, hvor fremmedes småfe hadde beitet om natten. Vi var vitne til deres avgjørelse.'),
(2562, 21, 79, 'Vi gav Salomo å forstå saken, og begge gav Vi visdom og kunnskap. Og Vi fikk fjellene og fuglene sammen med David til å synge Vår pris. Det gjorde Vi!'),
(2563, 21, 80, 'Og ham lærte Vi å lage dere stridsklær for å beskytte dere mot deres egen vold. Mon dere viser takknemlighet?'),
(2564, 21, 81, 'Og stormvinden for Salomo! Den blåste på hans bud til det land Vi har velsignet. Vi kjente til alt!'),
(2565, 21, 82, 'Og også sataner, som dykket etter perler, og også gjorde andre arbeider. Vi passet på dem!'),
(2566, 21, 83, 'Og Job, da han anropte sin Herre: «Plager er over meg! Ingen er så barmhjertig som Du!»'),
(2567, 21, 84, 'Så bønnhørte Vi ham, tok bort hans plager, og gav ham til hans familie og andre nærstående, som en nådesbevisning fra Oss, og en formaning til dem som tjener Oss.'),
(2568, 21, 85, 'Og Ismael, Idris og Dhul Kifl, de var alle standhaftige i tålmod,'),
(2569, 21, 86, 'Vi førte dem inn i Vår nåde. De var visselig rettferdige.'),
(2570, 21, 87, 'Og Dhul Nun (Jonas), da han gikk sin vei i sinne, og trodde at Vi aldri ville greie med ham. Så ropte han fra mørket: «Det er ingen annen gud enn Du! Ære være Deg! Jeg har handlet ondt og urett!»'),
(2571, 21, 88, 'Så bønnhørte Vi ham, og reddet ham fra angsten. Slik redder Vi de troende.'),
(2572, 21, 89, 'Og Sakarias, da han anropte Herren: «Min Herre, la meg ikke være alene, selv om Du er den beste arving!»'),
(2573, 21, 90, 'Så bønnhørte Vi ham, og gav ham Johannes, idet Vi satte hans hustru i stand. De kappedes i gode gjerninger, og påkalte Oss i håp og ærefrykt. De var ydmyke overfor Oss.'),
(2574, 21, 91, 'Og hun (Maria) som beskyttet sin kyskhet. Så innblåste vi vår ånd i henne, og gjorde henne og hennes sønn til et tegn for all verden.'),
(2575, 21, 92, '«Dette er deres trossamfunn. Ett samfunn er det! Og jeg er deres Herre, så tjen Meg!»'),
(2576, 21, 93, 'Men de har splittet opp dette anliggende seg imellom. Til Oss vender alle tilbake!'),
(2577, 21, 94, 'Men den som lever rettskaffent, og tror, hans innsats skal ikke mangle anerkjennelse! Vi selv skriver det opp for ham.'),
(2578, 21, 95, 'Det hviler et bånd på en by Vi har ødelagt. De skal ikke vende tilbake,'),
(2579, 21, 96, 'før det åpnes for Gog og Magog, og de strømmer ned fra alle høyder,'),
(2580, 21, 97, 'og det sanne løfte kommer nær. Da blir de vantro ville i blikket: «Trøste oss! Dette overså vi! Sannelig, vi handlet ondt og urett.»'),
(2581, 21, 98, '«Dere og det dere tjente fremfor Gud, skal bli helvetes brensel! Dit skal dere stige ned.»'),
(2582, 21, 99, 'Om disse var virkelige guder, ville de ikke havnet der. Men der skal de være og bli, alle sammen.'),
(2583, 21, 100, 'Der skal det bli sukk og stønn, men ingenting hører de der til trøst.'),
(2584, 21, 101, 'Men de som Vi allerede har gitt det beste, holdes fjernt fra dette.'),
(2585, 21, 102, 'De hører ikke en lyd derifra, og de skal være og bli i det deres hjerter begjærte.'),
(2586, 21, 103, 'Den store skrekk skal ikke plage dem, og englene vil ta imot dem: «Dette er deres dag, som dere var lovet!»'),
(2587, 21, 104, 'Den dag ruller Vi opp himmelen som man ruller opp en beskreven bokrull! Slik som Vi begynte den første skapelse, vil Vi gjøre den om igjen! Dette er et løfte som påligger Oss. Dette vil Vi gjøre.'),
(2588, 21, 105, 'Og Vi har skrevet ned i salmene, etter formaningen: «Mine rettferdige tjenere skal arve jorden.»'),
(2589, 21, 106, 'Dette er et budskap til folk som tjener Gud.'),
(2590, 21, 107, 'Vi har sendt deg som en nådesbevisning til all verden.'),
(2591, 21, 108, 'Si: «Det er åpenbart meg at deres Gud er én Gud. Vil dere overgi dere til Ham?»'),
(2592, 21, 109, 'Men om de vender seg bort, så si: «Jeg har forkynt for dere alle på like fot. Jeg vet ikke om det dere er stilt i utsikt er nært eller fjernt.'),
(2593, 21, 110, 'Men Han kjenner det som sies åpent, og det dere skjuler.'),
(2594, 21, 111, 'Jeg vet ikke, kanskje er det en prøve dere er satt på, en livsnytelse inntil videre?»'),
(2595, 21, 112, 'Han la til: «Min Herre, døm etter sannheten.» Vår Herre er den Barmhjertige, hvis hjelp må søkes mot det dere påstår.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'Dere mennesker, frykt Herren! Timens jordskjelv er en svær sak!'),
(2597, 22, 2, 'Den dag dere opplever det, vil ammende kvinner glemme sitt barn, og de svangre vil abortere. Og man vil anse menneskene for drukne, men de er ikke det. Men Guds straff er hard.'),
(2598, 22, 3, 'Blant menneskene er slike som strides om Gud, uten at de noe vet, og som følger enhver oppsetsig satan,'),
(2599, 22, 4, 'for hvem det er bestemt at den som slutter seg til ham, ham vil han føre vill, og lede ham til Ildens straff.'),
(2600, 22, 5, 'Dere mennesker, om dere er i tvil om oppstandelsen, sannelig, Vi skapte dere av jord, så av en sæddråpe, så av en kime, så et foster, fullbåret eller ikke fullbåret, så Vi kan gjøre det klart for dere. Vi lar forbli i mors liv det Vi bestemmer for en fastsatt tid. Så lar Vi dere komme ut som et lite barn, for deretter å nå moden alder. Noen rives bort, og noen holdes tilbake til den ynkeligste alderdom, så han ingenting mer vet av det han før visste. Og du ser jorden ligge i dvale. Men så, når Vi sender regn på den, da kommer den i bevegelse, sveller opp og lar allslags herlige planter vokste frem.'),
(2601, 22, 6, 'Alt dette, fordi Gud er den Sanne, Han gir de døde liv. Han evner alle ting.'),
(2602, 22, 7, 'Timen vil komme, hvorom ingen tvil hersker, og Gud vil gjenoppvekke alle som er i gravene.'),
(2603, 22, 8, 'Blant menneskene er slike som strides om Gud, uten at de noe vet, uten ledelse og uten en Bok som opplyser,'),
(2604, 22, 9, 'idet de vender ryggen til den for å lede vekk fra Guds vei. Dem venter skam i denne verden, og på oppstandelsens dag vil Vi la dem smake Ildens straff.'),
(2605, 22, 10, '«Dette er for dine gjerninger, som du har sendt i forveien til regnskapet, og fordi Gud aldri gjør urett mot Sine tjenere.»'),
(2606, 22, 11, 'Blant folk finnes også slike som tjener Gud, men er på grensen. Om noe godt overgår ham, faller han til ro med det. Men om en prøvelse rammer ham, snur han straks tvert om. Han taper denne verden og den hinsidige. Dette, det er det åpenbare tap!'),
(2607, 22, 12, 'Istedenfor Gud, påkaller han noe som verken kan skade eller gagne ham. Dette er langt kommen villfarelse.'),
(2608, 22, 13, 'Den han påkaller, er nærmere til å skade ham enn å være ham til nytte. En dårlig skytsherre, og en dårlig følgesvenn!'),
(2609, 22, 14, 'Gud vil føre dem som tror og lever rettskaffent inn i paradisets haver, hvor bekker sildrer. Gud gjør det Han vil.'),
(2610, 22, 15, 'Den som tror at Gud ikke vil hjelpe ham verken i jordelivet eller i det hinsidige, la ham strekke seg som om han klatret i et tau mot himmelen, og så kutte det av. La ham så se om ikke hans kunstgrep gjør det av med det som ergrer ham.'),
(2611, 22, 16, 'Slik har Vi åpenbart Koranen som klare ord. For Gud leder dem Han vil.'),
(2612, 22, 17, 'Mellom de troende muslimene, jødene, sabeerne, de kristne, zoroastrierne og avgudsdyrkerne vil Gud treffe avgjørelse på oppstandelsens dag. Gud er vitne til alt.'),
(2613, 22, 18, 'Du har vel sett hvordan alt i himlene og på jord, sol og måne, stjerner og fjell, trær og dyr, og mange mennesker, bøyer seg i ærefrykt for Gud. Men mange rammes av straffen. Og den Gud fornedrer, kan ingen gjenreise i ære. Gud handler slik Han bestemmer.'),
(2614, 22, 19, 'Dette er de to parter som strides om sin Herre: De som er vantro får tilskåret klær av ild, og det tømmes kokende vann over hodet på dem,'),
(2615, 22, 20, 'slik at alt som finnes i deres indre, og deres hud, smelter.'),
(2616, 22, 21, 'Og det venter dem klubber av jern!'),
(2617, 22, 22, 'Hver gang de i sin nød vil komme seg ut derfra, drives de tilbake: «Smak nå helvetesbrannens straff!»'),
(2618, 22, 23, 'Men Gud vil føre dem som tror og lever rettskaffent inn i paradisets haver, hvor bekker sildrer. Der vil de bli prydet med gullarmbånd og perler, og klær av silke.'),
(2619, 22, 24, 'Og de leder til den gode tale, ledes til den Prisverdiges vei.'),
(2620, 22, 25, 'Men de som er vantro, og holder andre borte fra Guds vei og fra den hellige moské, som Vi har grunnlagt for alle mennesker på like fot, både dem som bor derved og dem som bor utenfor, den som vil profanere den med urett, ham vil Vi la smake en smertelig straff!'),
(2621, 22, 26, 'Da vi bosatte Abraham ved Husets plass: «Sett intet ved min side! Hold Mitt hus rent for dem som vandrer rundt det, som står der, som bøyer seg og faller ned i ærefrykt!»'),
(2622, 22, 27, 'Kall menneskene til pilegrimsreisen, og de vil komme til deg til fots og på magre kameler fra alle dype pass,'),
(2623, 22, 28, 'for å overvære det som er godt for dem, og å påkalle Guds navn på bestemte dager over offerkveget, som Han har gitt dem. Spis derav, og bespis de ulykkelige fattige.'),
(2624, 22, 29, 'La dem så utføre personlig pleie igjen, ofre det de er pålagt og vandre rundt det gamle Hus.'),
(2625, 22, 30, 'Slik er det. Om noen holder Guds hellige ting i ære, så er dette godt for ham for Herrens åsyn. Kveget er tillatt mat for dere, unntatt det som er meddelt dere. Hold dere unna avgudsstyggedommen! Hold dere unna falsk tale!'),
(2626, 22, 31, 'Så dere søker Gud i oppriktighet, og ikke gir Ham medguder. Den som gir Gud medguder, han er som om han falt fra himmelen, og fuglene snapper ham opp, eller vinden feier ham bort til et fjernt sted.'),
(2627, 22, 32, 'Slik er det. Om noen holder Guds kultsymboler i ære, så er dette hjertets gudsfrykt.'),
(2628, 22, 33, 'Kveget er til nytte for dere for en viss tid. Så er dets offerplass ved det gamle Hus,'),
(2629, 22, 34, 'For hvert folk har vi innstiftet en ritus, at de ved slakting skal påkalle Guds navn over det kveg som Gud har gitt dem. Deres Gud er én Gud, så gi dere over til Ham. Bring det gode budskap til de ydmyke,'),
(2630, 22, 35, 'hvis hjerter bever når Guds navn nevnes, og som bærer i tålmod det som rammer dem, som forretter bønnen og gir gaver av det Vi har gitt dem.'),
(2631, 22, 36, 'Offerdyrene har Vi innstiftet for dere blant Guds kultsymboler. I dem ligger et gode for dere. Så påkall Guds navn over dem, når de står oppstilt i rekker. Når de er ferdig slaktet, så spis av dem, og bespis dem som ber, og dem som blygt holder seg tilbake. Vi har stilt dem til deres disposisjon, så dere må vise takknemlighet.'),
(2632, 22, 37, 'Deres kjøtt når ikke Gud, heller ikke deres blod, men deres fromhet når Ham. Han har stilt dem til deres disposisjon, så dere må prise Gud for at han har gitt dere ledelse. Bring det glade budskap til dem som handler vel!'),
(2633, 22, 38, 'Gud beskytter de troende. Gud liker ikke utakknemlige bedragere.'),
(2634, 22, 39, 'De som bekjempes har kamptillatelse, fordi de har lidt urett. Gud evner fullt ut å gi dem hjelp'),
(2635, 22, 40, 'som uforskyldt er drevet bort fra sine hjem bare fordi de sier: «Vår herre er Gud.» Om Gud ikke hadde holdt folk i sjakk, noen av dem ved hjelp av andre, så ville klostre og kirker, bedehus og moskeer, hvor Guds navn stadig påkalles, vært ødelagt. Gud vil hjelpe dem som hjelper Ham, og Gud er sterk og mektig,'),
(2636, 22, 41, 'dem, som hvis Vi gir dem fotfeste på jorden, forretter bønnen, betaler det rituelle bidrag, påbyr det rette og forbyr det urette. Gud tilhører alle tings endelige utgang.'),
(2637, 22, 42, 'Om de kaller deg for løgner, så har før dem Noas folk, Ad og Thamod,'),
(2638, 22, 43, 'Abrahams folk og Lots folk'),
(2639, 22, 44, 'og folket i Midian ropt løgn. Også Moses ble kalt løgner. Så gav Jeg de vantro en frist, men så tok Jeg dem fatt, og hvordan var vel Min refselse!'),
(2640, 22, 45, 'Hvor mang en by har Vi ikke tilintetgjort i dens synd, så de ligger i ruiner? Hvor mange forlatte brønner, hvor mange stolte slott?'),
(2641, 22, 46, 'Har de ikke reist omkring på jorden? Har de ikke fått hjerter som de kan fatte med, og ører som de kan høre med? Det er ikke deres øyne som er blinde, men hjertene i deres bryst er blinde.'),
(2642, 22, 47, 'De ber deg skynde på med straffen. Gud bryter ikke Sitt løfte! Men en dag for Herren er som tusen år etter deres regning.'),
(2643, 22, 48, 'Hvor mang en by har Jeg ikke gitt frist i dens synd, men så tok Jeg den fatt! Hos Meg er reisens mål.'),
(2644, 22, 49, 'Si: «Dere mennesker, jeg er bare en klar advarer for dere.»'),
(2645, 22, 50, 'De som tror, og lever rettskaffent, har tilgivelse og verdig omsorg i vente.'),
(2646, 22, 51, 'Men de som strever mot Vårt ord for å svekke det, de skal bli Ildens folk.'),
(2647, 22, 52, 'Vi har aldri sendt et sendebud eller en profet før din tid, uten at når han ønsket noe, så blandet Satan seg opp i hans ønske. Men så visker Gud ut Satans innblanding. Etter det fastlegger Gud sitt ord, Gud vet, og er vis,'),
(2648, 22, 53, 'slik at Han kan gjøre Satans innblanding til en prøvelse for dem som har sykdom i sine hjerter, og dem hvis hjerter er forherdet, de urettferdige er kommet langt i sitt avvik,'),
(2649, 22, 54, 'og for at de som er gitt kunnskapen, skal vite at det er sannheten fra Herren, slik at de tror på den, og deres hjerter bøyer seg for Ham. Gud leder dem som tror til rett vei.'),
(2650, 22, 55, 'Men de vantro vil ikke opphøre å tvile på dette, inntil timen kommer plutselig over dem, eller en uhellsvanger dags straffedom.'),
(2651, 22, 56, 'På denne dag tilhører herredømmet Gud alene. Han vil dømme mellom menneskene. De som tror, og lever rettskaffent, vil gå til lykksalighetens haver!'),
(2652, 22, 57, 'Men de som er vantro og forkaster Vårt ord, dem venter en ydmykende straff.'),
(2653, 22, 58, 'De som utvandret for Guds sak, og så ble drept, eller døde, dem vil Gud gi vakker omsorg. Gud er den beste forsørger.'),
(2654, 22, 59, 'Han vil føre dem inn en inngang som de vil være tilfreds med. Gud vet, er mild.'),
(2655, 22, 60, 'Slik er det. Den som gir igjen det samme som er overgått ham, og på nytt utsettes for vold, ham vil Gud hjelpe. Gud bærer over, er tilgivende.'),
(2656, 22, 61, 'Dette fordi Gud lar natt bli til dag, og dag bli til natt, at Gud hører, og ser.'),
(2657, 22, 62, 'Dette fordi Gud er den Sanne, og det de påkaller utenom Ham er verdiløs intethet, fordi Gud er den Opphøyde, den Store.'),
(2658, 22, 63, 'Du har vel sett at Gud sender vann fra oven og jorden grønnes? Gud kjenner alle detaljer, er vel underrettet.'),
(2659, 22, 64, 'Ham tilhører alt i himlene og på jord. Gud er den Selvtilstrekkelige, den Lovpriste.'),
(2660, 22, 65, 'Du har vel sett at Gud har latt dere disponere alt som er på jorden, og skipet som pløyer havet ved Hans bud, at Han holder tilbake himmelen så den ikke faller ned på jorden, med mindre Han tillater det? Gud er velvillig, nåderik mot menneskene.'),
(2661, 22, 66, 'Han er det, som har gitt dere livet. Så vil Han la dere dø, og derpå vil Han gi dere liv. Mennesket er sannelig utakknemlig!'),
(2662, 22, 67, 'For hvert trossamfunn har Vi innstiftet en ritus som de følger. La dem ikke strides med deg om dette. Kall du til Herren. Du har funnet rett ledelse.'),
(2663, 22, 68, 'Men om de strides med deg, så svar: «Gud vet godt hva dere gjør!'),
(2664, 22, 69, 'Gud vil felle dom mellom dere på oppstandelsens dag om det dere var uenige om.»'),
(2665, 22, 70, 'Du vet vel at Gud kjenner til alt som er i himmel og på jord? Dette står i en Bok. Dette er lett for Gud.'),
(2666, 22, 71, 'De tilber utenom Gud noe Han ikke har sendt noen autorisasjon for, noe som de ikke vet noe om. De urettferdige har ingen hjelper.'),
(2667, 22, 72, 'Og når Vårt ord tydelig og klart resiteres for dem, ser du motviljen i ansiktene til de vantro, og de er på nippet til å anfalle dem som resiterer Vårt ord. Si: «Skal jeg fortelle dere om noe som er enda verre? Det er Ilden, som Gud har stilt de vantro i utsikt!» En sørgelig skjebne!'),
(2668, 22, 73, 'Dere mennesker, det er gjort en lignelse, så lytt til! De, som dere påkaller utenom Gud, kan aldri skape en flue, om de så slo seg sammen om å gjøre det! Om en flue snappet bort noe fra dem, kan de ikke redde det fra den! Skrøpelig er de påkallende, og det påkalte!'),
(2669, 22, 74, 'De verdsetter ikke Gud med Hans rette verdi! Gud er sterk, mektig.'),
(2670, 22, 75, 'Gud utvelger sendebud blant englene og blant menneskene. Gud hører, og ser.'),
(2671, 22, 76, 'Han kjenner til hva som er foran dem og etter dem. Alle ting bringes tilbake til Gud.'),
(2672, 22, 77, 'Dere som tror, bøy dere og fall ned i bønn. Tjen Herren, og gjør det som godt er, så det må gå dere godt.'),
(2673, 22, 78, 'Gjør innsats for Guds sak, slik Han har krav på! Han har utvalgt dere, og har ikke pålagt dere noe trykkende i deres religion, deres far Abrahams lære. Han har kalt dere muslimer tidligere, og i denne Koran, så Sendebudet kan bære vitnesbyrd overfor dere, og dere overfor menneskene. Forrett bønnen, gi det rituelle bidrag og hold dere fast til Gud. Han er deres skytsherre. Og hvilken fortreffelig beskytter! Hvilken fortreffelig hjelper!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'De troende går det godt,'),
(2675, 23, 2, 'som er ydmyke i sine bønner,'),
(2676, 23, 3, 'som vender seg bort fra tomt prat,'),
(2677, 23, 4, 'som betaler det rituelle bidrag,'),
(2678, 23, 5, 'som holder sitt kjønnsliv i tømme,'),
(2679, 23, 6, 'unntatt når det gjelder hustruer og slavinner, for da kan de ikke lastes,'),
(2680, 23, 7, 'men de som begjærer noe utover dette, går over grensen,'),
(2681, 23, 8, 'som passer betrodd gods og sine forpliktelser,'),
(2682, 23, 9, 'og som overholder sine bønner.'),
(2683, 23, 10, 'Disse er arvingene,'),
(2684, 23, 11, 'som skal arve paradiset. Der skal de være og bli.'),
(2685, 23, 12, 'Vi skapte mennesket av en leirmasse.'),
(2686, 23, 13, 'Senere la Vi ham som en sæddråpe i sikker forvaring.'),
(2687, 23, 14, 'Så gjorde Vi dråpen til en kime, dette til det minste foster, og i fosteret formet Vi knokler, og knoklene dekket Vi med kjøtt. Så frembrakte Vi ham som en ny skapning. Velsignelsesrik er Gud, den beste skaper!'),
(2688, 23, 15, 'Så, etter dette, skal dere visselig dø.'),
(2689, 23, 16, 'Og så vil dere bli gjenoppvekket på oppstandelsens dag.'),
(2690, 23, 17, 'Over dere har Vi skapt syv himmelhvelv. Vi overså intet i Skapelsen.'),
(2691, 23, 18, 'Og Vi sendte ned vann fra himmelen i et visst mål, og lot det hvile i jorden. Men Vi er også i stand til å ta det bort.'),
(2692, 23, 19, 'Så frembrakte Vi ved det haver for dere, med palmer og druer. Dere har rikelig med frukt i dem, og av disse spiser dere.'),
(2693, 23, 20, 'Og et tre, som vokser frem av Sinais berg, som gir olje og krydder til dem som spiser.'),
(2694, 23, 21, 'I husdyrene er også noe å lære for dere. Vi gir dere å drikke av det som er i deres indre, og dere har mange anvendelser for dem, og de gir dere mat.'),
(2695, 23, 22, 'På dem og på skip bæres dere omkring.'),
(2696, 23, 23, 'Vi sendte Noa til hans folk, og han sa: «Mitt folk, tjen Gud! Dere har ingen annen Gud enn Ham! Vil dere ikke vise gudsfrykt?»'),
(2697, 23, 24, 'Rådet for hans folk, som var vantro, sa: «Dette er bare et vanlig menneske som oss, som vil være bedre enn oss. Om Gud hadde villet, kunne Han sendt ned engler. Vi har ikke hørt om noe slikt blant våre henfarne fedre.'),
(2698, 23, 25, 'Han er bare en besatt mann, så se ham an inntil videre.»'),
(2699, 23, 26, 'Da sa han: «Herre, hjelp meg, for de kaller meg løgner.»'),
(2700, 23, 27, 'Så gav Vi ham denne inspirasjon: «Lag arken for Våre øyne og ved Vår inspirasjon! Når Vår beslutning realiseres, og alt koker over, så før om bord et par av hver art, og din familie, unntatt ham, mot hvem ordet allerede foreligger. Snakk ikke til Meg om dem som har gjort det onde! De skal druknes.'),
(2701, 23, 28, 'Når så du og de som er med deg har tatt plass i arken, så si: ’Lovet være Gud, som har reddet oss fra et folk av urettferdige.’'),
(2702, 23, 29, 'Og si: ’Herre, la meg gå fra borde en velsignet landgang! Du er den beste los.’»'),
(2703, 23, 30, 'I dette er visselig jærtegn. Vi setter sannelig på prøve!'),
(2704, 23, 31, 'Så, etter dem, frembrakte Vi en annen generasjon.'),
(2705, 23, 32, 'Og Vi sendte dem et sendebud fra deres egne rekker, som sa: «Tjen Gud! Dere har ingen annen Gud enn Ham! Vil dere ikke vise gudsfrykt?»'),
(2706, 23, 33, 'Men rådet for hans folk, som var vantro og fornektet møtet i det hinsidige, og som Vi hadde gitt gode kår i jordelivet, sa: «Dette er bare et vanlig menneske som oss, som spiser og drikker som oss.'),
(2707, 23, 34, 'Hvis vi hører på et vanlig menneske som oss, da er vi sannelig tapere!'),
(2708, 23, 35, 'Er det ikke så at han lover dere at dere skal bringes frem, etter at dere er døde og er blitt jord og knokler?'),
(2709, 23, 36, 'Fullstendig forfeilet er det dere blir lovet!'),
(2710, 23, 37, 'Det er intet annet enn vårt jordeliv. Vi dør, vi lever, og vi gjenoppvekkes ikke.'),
(2711, 23, 38, 'Han er bare et menneske som har oppdiktet løgn om Gud, og vi har ingen tiltro til ham.»'),
(2712, 23, 39, 'Da sa han: «Herre, hjelp meg, for de kaller meg løgner.»'),
(2713, 23, 40, 'Han sa: «Om litt vil de nok angre!»'),
(2714, 23, 41, 'Så tok skrallet dem rettelig, og Vi gjorde dem til avfall. Bort med det folk som gjør det onde!'),
(2715, 23, 42, 'Så, etter dem, frembrakte Vi andre generasjoner.'),
(2716, 23, 43, 'Intet folk kommer før eller etter sin bestemte tid,'),
(2717, 23, 44, 'Så sendte Vi Våre sendebud den ene etter den andre. Men hver gang et sendebud kom til et folk, kalte de ham løgner. Vi lot dem følge etter hverandre, og Vi gjorde dem til legender. Bort med det folk som ikke tror.'),
(2718, 23, 45, 'Siden sendte Vi Moses og hans bror Aron med Våre jærtegn, og med klar fullmakt,'),
(2719, 23, 46, 'til Farao og hans råd, men de var hovmodige, et overlegent folk,'),
(2720, 23, 47, 'og de sa: «Skal vi ha tiltro til to vanlige mennesker som oss, hvis folk jo er våre tjenere?»'),
(2721, 23, 48, 'Så kalte de dem løgnere, og ble blant dem som ble utslettet.'),
(2722, 23, 49, 'Vi gav Moses skriften, så de kanskje kunne finne ledelse.'),
(2723, 23, 50, 'Og Vi gjorde Marias sønn og hans mor til et tegn. Vi gav dem tilflukt i en åsside med ro og vann.'),
(2724, 23, 51, '«Dere sendebud, spis av det som er godt og rent, og lev rettskaffent! Jeg kjenner til det dere gjør.'),
(2725, 23, 52, 'Dette er deres trossamfunn. Ett samfunn er det! Og Jeg er deres Herre, så frykt Meg.»'),
(2726, 23, 53, 'Men de har splittet opp dette sitt anliggende i sekter, og hver gruppe fryder seg over det de har.'),
(2727, 23, 54, 'La du dem bero i deres forvirrings avgrunn inntil videre.'),
(2728, 23, 55, 'Tror de kanskje at Vi ved det Vi har gitt dem av rikdom og barn'),
(2729, 23, 56, 'også vil være raske med de største goder? Nei, de fatter intet!'),
(2730, 23, 57, 'Men de som bever av ærefrykt for Herren.'),
(2731, 23, 58, 'som tror på Herrens ord,'),
(2732, 23, 59, 'som ikke setter noe ved Herrens side,'),
(2733, 23, 60, 'som gir sine gaver med bevende hjerter fordi de skal vende tilbake til Herren,'),
(2734, 23, 61, 'disse er det som kappes i å gjøre det gode, og er de første til å fullbyrde det.'),
(2735, 23, 62, 'Vi pålegger ingen mer enn han evner! Vi har en regnskapsbok som sier sannheten, og de gjøres ingen urett!'),
(2736, 23, 63, 'Nei, i deres hjerter er en forvirrings avgrunn om dette, og de er opphavsmenn til gjerninger andre enn disse.'),
(2737, 23, 64, 'Inntil Vi tar fatt dem som lever i vellevnet med straffen, da jamrer de seg!'),
(2738, 23, 65, 'Jamre dere ikke i dag! Dere får ingen hjelp fra Oss!'),
(2739, 23, 66, 'Mitt ord ble resitert for dere, men dere snudde dere om på hælen,'),
(2740, 23, 67, 'i hovmod overfor det, og unngikk ham som talte om kvelden.'),
(2741, 23, 68, 'Har de da ikke tenkt over det som ble sagt? Eller ble noe brakt frem for dem som ikke ble gitt deres henfarne fedre?'),
(2742, 23, 69, 'Eller kjente de ikke sitt Sendebud, så de ikke ville vite av ham?'),
(2743, 23, 70, 'Eller sier de: «Han er besatt!» Nei, han har brakt dem sannheten, men de fleste av dem avskyr sannheten.'),
(2744, 23, 71, 'Hadde sannheten rettet seg etter deres meninger, så ville himlene og jorden og alle som i dem er, gått fordervelse i møte! Nei, Vi har brakt dem deres formaning, men fra formaningen vender de seg bort.'),
(2745, 23, 72, 'Har du vel forlangt lønn av dem? Herrens lønn er bedre. Han er den beste forsørger.'),
(2746, 23, 73, 'Du kaller dem visselig til rett vei.'),
(2747, 23, 74, 'Men de som ikke tror på det hinsidige, viker av fra veien.'),
(2748, 23, 75, 'Men om Vi forbarmet oss over dem, og tok bort de trengsler de er under, så ville de ture frem i sin oppsetsighet, og vandre videre i blinde.'),
(2749, 23, 76, 'Vi har tatt dem fatt med straffen, men de bøyde seg ikke for Herren og viste ikke ydmykhet,'),
(2750, 23, 77, 'inntil Vi til slutt åpnet porten for en streng straff, da ble de fortvilet.'),
(2751, 23, 78, 'Han er det som gav dere hørsel, syn og hjertets forstand. Liten takk viser dere.'),
(2752, 23, 79, 'Han er det, som spredte dere på jorden. Til Ham samles dere inn.'),
(2753, 23, 80, 'Han er det som gir liv og død. I hans hånd ligger vekslingen av natt og dag. Forstår dere da ikke?'),
(2754, 23, 81, 'Nei, de sier det samme som de henfarne.'),
(2755, 23, 82, 'De sier: «Skal vi vel gjenoppvekkes når vi er døde, og er jord og knokler?'),
(2756, 23, 83, 'Vi og våre fedre er blitt lovet dette før. Dette er intet annet enn fabler fra gammel tid.»'),
(2757, 23, 84, 'Si: «Hvem tilhører jorden og alle som der er, om dere vet?»'),
(2758, 23, 85, 'Og de vil svare: «Gud tilhører det.» Si: «Vil dere da ikke komme til ettertanke?»'),
(2759, 23, 86, 'Si: «Hvem er Herre over de syv himler og den store trone?»'),
(2760, 23, 87, 'Og de vil svare: «Gud tilhører det.» Si: «Vil dere da ikke vise gudsfrykt?»'),
(2761, 23, 88, 'Si: «I hvis hånd er herredømmet over alle ting, så Han gir beskyttelse, mens det ikke gis beskyttelse mot Ham, om dere vet?»'),
(2762, 23, 89, 'Og de vil svare: «Gud tilhører det.» Si: «Hvordan kan dere være så forhekset?»'),
(2763, 23, 90, 'Nei, Vi har brakt dem sannheten. De er sannelig løgnere.'),
(2764, 23, 91, 'Gud har ikke lagt seg til barn. Det er ingen guder ved Hans side. I så fall ville hver gud dratt av gårde med det han hadde skapt, og noen av dem hadde reist seg mot andre. Ære være Gud! Og høyt hevet over det de fremholder!'),
(2765, 23, 92, 'Han kjenner det skjulte og det åpenbare. Han er opphøyet over det de setter ved Hans side.'),
(2766, 23, 93, 'Si: «Herre, om Du vil la meg se og oppleve det de er stilt i utsikt,'),
(2767, 23, 94, 'så plasser meg ikke, Herre, blant de urettferdige.»'),
(2768, 23, 95, 'Vi har visselig makt til å la deg se det Vi har stilt dem i utsikt.'),
(2769, 23, 96, 'Avvis det onde med det som er bedre. Vi er vel kjent med det de sier.'),
(2770, 23, 97, 'Og si: «Jeg søker tilflukt hos Deg, Herre, mot satanenes tilskyndelser.'),
(2771, 23, 98, 'Jeg søker tilflukt hos Deg, Herre, at de ikke må hjemsøke meg.»'),
(2772, 23, 99, 'Når døden så kommer til en av de urettferdige, da sier han: «Herre, send meg tilbake,'),
(2773, 23, 100, 'slik at jeg må leve rettskaffent i alt jeg har forsømt.» Nei, dette er bare ord han sier. Bak dem er en sperrende voll til den dag de gjenoppvekkes.'),
(2774, 23, 101, 'Når det støtes i basunen, så er ingen slektskapsbånd mellom dem på denne dag, og de snakker ikke til hverandre.'),
(2775, 23, 102, 'De, hvis vektskåler er tunge, dem vil det gå godt.'),
(2776, 23, 103, 'Men de, hvis vektskåler er lette, disse har kastet seg bort. I helvete må de være og bli,'),
(2777, 23, 104, 'hvor Ilden svir deres ansikter så de blir askegrå.'),
(2778, 23, 105, '«Ble ikke Mitt ord resitert for dere, og dere forkastet det?»'),
(2779, 23, 106, 'Og de vil svare: «Vår skrøpelige natur tok makten fra oss, Herre, og vi var folk som kom på villstrå.'),
(2780, 23, 107, 'Herre, før oss ut herfra! Hvis vi så faller tilbake, da er vi visselig illgjerningsmenn!»'),
(2781, 23, 108, 'Han sier: «Ned i helvete med dere, og snakk ikke til Meg!'),
(2782, 23, 109, 'Det var en flokk av mine tjenere som pleide å si: ’Herre, vi tror! Tilgi oss og forbarm Deg over oss. Ingen er barmhjertig som Du.’'),
(2783, 23, 110, 'Men dere holdt dem for narr, så det fikk dere til å glemme tanken på Meg. Og dere lo av dem.'),
(2784, 23, 111, 'Jeg belønner dem i dag for deres standhaftighet. De har vunnet seieren.»'),
(2785, 23, 112, 'Han fortsetter: «Hvor mange år var dere på jorden?»'),
(2786, 23, 113, 'Og de vil svare: «Vi var vel der en dags tid eller noe slikt, spør dem som holder regning.»'),
(2787, 23, 114, 'Han sier: «Dere var kun kort tid, om dere bare visste.'),
(2788, 23, 115, 'Trodde dere at Vi skapte dere for moro skyld, og at dere ikke ville bringes hjem til oss?»'),
(2789, 23, 116, 'Opphøyet er Gud, Kongen, den Sanne! Det er ingen gud utenom Ham, den ærverdige trones Herre.'),
(2790, 23, 117, 'Om noen påkaller en annen gud ved Guds side, som han ikke har noe bevis for, så beror hans avregning hos hans Herre. Sannelig, de vantro går det ikke godt.'),
(2791, 23, 118, 'Og si: «Herre, tilgi og vis Nåde! Ingen er barmhjertig som Du.»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'Dette er et avsnitt som Vi har åpenbart og gjort bindende. Vi har i det åpenbart klare ord, så dere må komme til ettertanke.'),
(2793, 24, 2, 'Horkvinnen og horkarlen, gi hver av dem hundre piskeslag, La ingen medlidenhet for dem gripe dere når det gjelder Guds religion, om dere tror på Gud og dommens dag. Og la en gruppe troende overvære deres avstraffelse.'),
(2794, 24, 3, 'En horkarl må bare gifte seg med en horkvinne eller avgudsdyrker. Og en horkvinne, ingen skal gifte seg med henne, unntatt en horkarl eller en avgudsdyrker. Dette er forbudt for de troende.'),
(2795, 24, 4, 'De som kaster tvil over ærbare kvinner, og så ikke skaffer fire vitner, gi dem åtti piskeslag, og motta aldri deres vitnemål mer. Disse er ugudelige!'),
(2796, 24, 5, 'Unntatt er slike som angrer etterpå, og forbedrer seg. Gud er tilgivende, nåderik.'),
(2797, 24, 6, 'Den som kaster tvil over sin hustru, og ikke har andre vitner enn seg selv, la hans vitnesbyrd være at han sverger en firfoldig ed ved Gud, at han taler sant.'),
(2798, 24, 7, 'Og en femte gang, at Guds forbannelse må ramme ham, om han er løgner.'),
(2799, 24, 8, 'Det vil avvende straffen fra henne, om hun sverger en firfoldig ed ved Gud at han lyver,'),
(2800, 24, 9, 'og en femte gang, at Guds vrede må ramme henne om han har snakket sant.'),
(2801, 24, 10, 'Hadde det ikke vært for Guds godhet mot dere, og Hans nåde, og at Gud viser miskunn, er vis.'),
(2802, 24, 11, 'De som kom med bakvaskelsen er en liten gruppe blant dere. Anse ikke dette som noe ondt for dere, det er tvert imot godt. Hver enkelt av dem skal belastes det han har fortjent av synd, og hovedmannen, ham venter en svær straff.'),
(2803, 24, 12, 'Hvorfor tok ikke de troende menn og kvinner for sitt vedkommende alt i beste mening da de hørte det, og sa: «Dette er klar bakvaskelse?»'),
(2804, 24, 13, 'Hvorfor brakte de ikke fire vitner i saken? Da de ikke har brakt vitner, så er de overfor Gud løgnere.'),
(2805, 24, 14, 'Hadde det ikke vært for Guds godhet og nåde mot dere i denne verden og den hinsidige, så ville en svær straff rammet dere'),
(2806, 24, 15, 'for det dere har spredt ut, da dere tok imot det og la det på tungen, og uttalte det med munnen det dere ikke har noe kjennskap til, og anså det for en liten ting. Men i Guds øyne er det en stor sak!'),
(2807, 24, 16, 'Hvorfor sa dere ikke da dere hørte det: «Det tilkommer ikke oss å snakke om dette! Ære være Deg! Dette er en svær beskyldning.»'),
(2808, 24, 17, 'Gud formaner dere ikke å falle tilbake til noe slikt noen gang, om dere er troende.'),
(2809, 24, 18, 'Gud klargjør ordet for dere. Gud vet, er vis.'),
(2810, 24, 19, 'De som liker at skammeligheter spres ut om dem som tror, dem venter en smertelig straff i denne verden og den hinsidige. For Gud vet, mens dere ikke vet.'),
(2811, 24, 20, 'Hadde det ikke vært for Guds godhet mot dere, og Hans nåde, og at Gud er velvillig, nåderik.'),
(2812, 24, 21, 'Dere som tror, følg ikke i Satans fotspor, for den som følger i Satans fotspor, se, han oppfordrer til skjendighet og urett. Hadde det ikke vært for Guds godhet mot dere, og Hans nåde, så ville ikke en eneste en av dere noen gang blitt ren. Men Gud renser den Han vil. Gud hører, vet.'),
(2813, 24, 22, 'De blant dere som har rikelighet og velstand, må ikke forsverge å gi noe til nærbeslektede og fattige og dem som har utvandret for Guds sak. La dem unnskylde og vise overbærenhet. Vil dere ikke gjerne at Gud skal tilgi dere? Gud er tilgivende, nåderik.'),
(2814, 24, 23, 'De som kaster tvil over ærbare kvinner, sorgløse, men rettroende, de er under forbannelse i denne verden og den hinsidige. Dem venter en svær straff'),
(2815, 24, 24, 'den dag da tunger, hender og føtter vitner om det de bedrev.'),
(2816, 24, 25, 'På denne dag vil Gud gi dem fullt ut det de rettelig har til gode, og de vil vite at Gud er den åpenbare rettferd.'),
(2817, 24, 26, 'Dårlige kvinner er for dårlige menn, og dårlige menn for dårlige kvinner. Gode kvinner er for gode menn, og gode menn for gode kvinner. Disse er uberørt av hva folk sier. Dem tilkommer tilgivelse og verdig omsorg.'),
(2818, 24, 27, 'Dere som tror, gå ikke inn i andres hus enn deres egne uten først å ha gitt deres nærvær til kjenne og hilst beboerne. Dette er bedre for dere, så dere må tenke etter.'),
(2819, 24, 28, 'Om dere ikke finner noen hjemme, så gå ikke inn før dere har fått tillatelse. Og sier man til dere: «Vend om!» så vend om. Det er mest sømmelig for dere. Gud vet om det dere gjør.'),
(2820, 24, 29, 'Det hviler ingen skyld på dere om dere går inn i ubebodde hus, hvor noe kan være dere til nytte. Gud vet om det dere bærer åpent frem, og det dere skjuler.'),
(2821, 24, 30, 'Si til de troende menn at de skal dempe sine øyekast og holde sitt kjønnsliv i tømme. Dette er mer sømmelig for dem. Gud er vel underrettet om det de foretar seg.'),
(2822, 24, 31, 'Og si til de troende kvinner at de skal dempe sine øyekast og holde sitt kjønnsliv i tømme, og ikke vise sin pryd, unntatt det av den som kommer til syne. La dem trekke sløret over sine bryst og ikke vise sin pryd til andre enn sine menn, sine fedre, svigerfedre, sønner, stesønner, brødre, nevøer eller deres hustruer, eller sine slaver, eller menn som betjener dem, men er hinsides kjønnsbegjær, eller barn som ikke forstår seg på kvinners nakenhet. La dem heller ikke trampe med føttene slik at det kan erkjennes hva de skjuler av pryd. Omvend dere alle til Gud, dere troende, så det må gå dere godt!'),
(2823, 24, 32, 'Sørg for ekteskap for den enslige blant dere, og rettskafne slaver og slavinner. Om de er fattige, så vil Gud gjøre dem rike ved sin gunst, Gud er storsinnet, og vet.'),
(2824, 24, 33, 'La dem som ikke finner utvei til ekteskap, være avholdne til Gud beriker dem ved sin gunst. Om noen av de slaver dere eier, ønsker frihetsbrev, så la dem få det, om dere vet om noe godt i dem. Og gi dem noe av den rikdom som Gud har gitt dere. Tving ikke deres slavepiker til prostitusjon, om de vil leve i ærbarhet, i begjær etter denne verdens gods. Men om noen tvinger dem, så er Gud etter tvangen tilgivende, nådig.'),
(2825, 24, 34, 'Nå har Vi åpenbart dere ordet som gjør alt klart, eksemplet til dem som gikk bort før dere, og en formaning til de gudfryktige.'),
(2826, 24, 35, 'Gud er himlenes og jordens lys. Hans lys kan lignes med en nisje hvor det er et bluss. Blusset er omgitt av glass, som om det var en funklende stjerne. Det tennes med brenne fra et velsignet tre, et oljetre, som ikke hører hjemme verken i øst eller vest, hvis olje nesten lyser uten at ild kommer den nær. Lys over lys! Gud leder dem Han vil til Sitt lys. Han lager lignelser for menneskene. Gud kjenner til alle ting.'),
(2827, 24, 36, 'Hans lys er i hellige hus som er reist med Hans bifall, hvor Han vil at Hans navn skal påkalles, hvor menn priser Ham morgen og kveld,'),
(2828, 24, 37, 'menn som ikke distraheres fra Guds ihukommen av varer og handel, fra å forrette bønnen, og å gi gode gaver, idet de frykter en dag da hjerter og blikk blir omsnudd,'),
(2829, 24, 38, 'og håper at Gud vil belønne dem for det beste de har gjort, og gi dem mer av Sin godhet. Gud forsørger hvem Han vil, uten å regne på det.'),
(2830, 24, 39, 'Men de vantro, deres gjerninger er som en luftspeiling over sletten, som den tørstende tror er vann, til han når dit og intet finner. Men han finner Gud ved sin side, og Han gjør opp hans regning. Og Gud er rask i avregningen.'),
(2831, 24, 40, 'Eller de er som mørket i havets dyp, dekket av en bølge, og atter en bølge, og så skyene, mørke på mørke. Om noen strekker frem hånden, kan han nesten ikke se den. Den ikke Gud gir lys, han har intet lys.'),
(2832, 24, 41, 'Du har vel sett at alt som er i himlene og på jord priser Gud, og fuglene, når de brer ut sine vinger. Enhver, Gud kjenner hans bønn og hans lovprisning. Gud vet om hva de gjør.'),
(2833, 24, 42, 'Gud tilhører herredømmet over himlene og jorden. Hos Gud er reisen slutt.'),
(2834, 24, 43, 'Du har vel sett hvordan Gud driver frem skyene, føyer dem sammen og stabler dem i hauger, og du ser regnet strømme ut blant dem. Og Han sender ned fra oven berg av hagl og rammer dermed hvem Han vil, og holder det borte fra hvem Han vil. Hans lysglimt blender nesten!'),
(2835, 24, 44, 'Gud lar natt og dag veksle. I dette er noe å lære for dem som har øyne.'),
(2836, 24, 45, 'Gud har skapt alle vesener av vann, noen går på buken, noen på to ben og noen på fire. Gud skaper det Han vil. Gud evner alt.'),
(2837, 24, 46, 'Vi har nå åpenbart ordet som klargjør. Gud leder dem Han vil til rett vei.'),
(2838, 24, 47, 'Folk sier: «Vi tror på Gud og på Sendebudet, og vi adlyder.» Etter dette vender noen av dem seg bort. Disse er ikke troende.'),
(2839, 24, 48, 'Når de kalles til Gud og Hans sendebud så han kan dømme mellom dem, da er det en flokk av dem som trekker seg bort.'),
(2840, 24, 49, 'Men har de retten på sin side, da kommer de til ham og føyer seg.'),
(2841, 24, 50, 'Har de en sykdom i sine hjerter, har de tvil eller frykter de at Gud og Hans sendebud skal gjøre dem urett? Nei, disse er i sannhet urettferdige.'),
(2842, 24, 51, 'Alt de troende sier når de kalles til Gud og Hans sendebud, at han må felle dom mellom dem, er: «Vi hører og vi adlyder.» Disse vil det gå godt.'),
(2843, 24, 52, 'Den som adlyder Gud og Hans sendebud og frykter Gud i ærefrykt, disse vil det gå godt.'),
(2844, 24, 53, 'Folk sverger sine dyreste eder ved Gud, at om du gir dem ordre, så vil de visselig rykke ut. Si: «Sverg ikke! Vanlig lydighet er nok. Gud er vel underrettet om det dere gjør.»'),
(2845, 24, 54, 'Si: «Adlyd Gud, og adlyd Sendebudet!» Men om dere så vender ryggen til, så påligger ham kun det han er pålagt, og dere det dere er pålagt. Om dere adlyder ham, har dere funnet rett vei. Sendebudet påligger kun å forkynne klart og greit.'),
(2846, 24, 55, 'Gud har lovet dem av dere som tror og lever rettskaffent, at Han vil la dem overta landet, som Han i sin tid lot dem som var før dem overta, og at Han vil befeste deres religion, som Han har godkjent for dem, og bytte deres frykt med sikkerhet. «Meg skal de tjene, og ikke sette noe ved Min side.» De som er vantro etter dette, de er ugudelige!'),
(2847, 24, 56, 'Forrett bønnen, betal ritualbidraget, og adlyd Sendebudet, så dere må finne nåde.'),
(2848, 24, 57, 'Tro endelig ikke at de vantro kan gjøre Gud avmektig på jorden! Deres herberge blir Ilden, et sørgelig endelikt.'),
(2849, 24, 58, 'Dere som tror, la deres slaver og dem blant dere som ennå ikke har nådd puberteten, be dere om tillatelse tre ganger, før morgenbønnen, når dere tar av klærne i middagsheten og etter aftenbønnen, de tre tider for nakenhet hos dere. Utenfor disse er det ingen feil hos dem eller dere om dere går om hverandre. Slik klargjør Gud ordet for dere. Gud vet, er vis.'),
(2850, 24, 59, 'Når barna deres når puberteten, la dem be om tillatelse, slik det ble gjort før dem. Slik klargjør Gud sitt ord for dere. Gud vet, er vis.'),
(2851, 24, 60, 'Kvinner hinsides overgangsalderen og som ikke håper på giftermål, rammes ikke av skyld om de legger bort klærne, om de ikke fremviser pryd. Men om de avstår, er det bedre for dem. Gud hører, vet.'),
(2852, 24, 61, 'Verken en blind, en krøpling, en syk trenger å gjøre seg noen bebreidelser, og heller ikke dere selv, om dere spiser i eget hus, i fedres, mødres, brødres, søstres, onklers og tanters, et hus dere har nøkkelen til, eller hos en venn av dere. Dere rammes ikke av skyld om dere spiser sammen, eller adskilt. Når dere går inn i et hus, så hils hverandre med fredshilsenen, en velsignet og god hilsen, kommet fra Gud. Slik klargjør Gud ordet for dere, så dere må forstå.'),
(2853, 24, 62, 'De er troende, som tror på Gud og Hans sendebud, som, når de er sammen med ham i et felles anliggende, ikke går sin vei før de har bedt ham om tillatelse. De som ber om din tillatelse, disse er det som tror på Gud og Hans sendebud. Ber de om tillatelse for et privat anliggende, så gi tillatelse til dem du vil, og be Gud tilgi dem. Gud er tilgivende, nåderik.'),
(2854, 24, 63, 'Anse ikke Sendebudets innkallelse som et opprop dere imellom. Gud kjenner dem av dere som sniker seg vekk og gjemmer seg. La dem akte seg som går imot Hans sak, at ikke en prøvelse overgår dem, eller at de rammes av en smertelig straff.'),
(2855, 24, 64, 'Tilhører ikke alt i himlene og på jord Gud? Han vet hva dere har fore. Den dag de bringes tilbake til Ham, vil Han fortelle dem hva de bedrev. Gud kjenner til alle ting!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Full av velsignelse er Han som har åpenbart kriteriet (Koranen) for Sin tjener, for at han skal være en advarer for all verden.'),
(2857, 25, 2, 'Han som har herredømmet over himlene og jorden, som ikke har lagt seg til barn, som ikke deler herredømmet med noen. Han har skapt alle ting og bestemt dem nøyaktig.'),
(2858, 25, 3, 'Allikevel har de lagt seg til andre guder, utenom Ham, som ingenting skaper, men selv er laget, som ikke makter å skaffe seg selv verken skade eller gagn, og ikke har makt over liv eller død eller oppstandelse.'),
(2859, 25, 4, 'De vantro sier: «Dette er ikke annet enn løgn som han har diktet opp, og andre folk har hjulpet ham med det.» De kommer bare med urett og løgn.'),
(2860, 25, 5, 'Og de sier: «Gamle fabler som han har skrevet ned! De dikteres for ham sent og tidlig.»'),
(2861, 25, 6, 'Si: «Han har åpenbart det som kjenner det hemmelige i himlene og på jord. Han er tilgivende, nåderik.»'),
(2862, 25, 7, 'De sier også: «Hvordan har det seg med dette sendebudet? Han tar jo til seg mat, og vandrer rundt på torvene? Hvorfor er ikke en engel sendt ned til ham, for å være advarer sammen med ham?'),
(2863, 25, 8, 'Hvorfor har han ikke fått overlatt en skatt? Hvorfor har han ikke en have å spise av?» De urettferdige sier: «Dere følger bare en forhekset mann!»'),
(2864, 25, 9, 'Se, hvordan de gjør sammenligninger om Deg, så de kommer på villspor og er ute av stand til å finne vei!'),
(2865, 25, 10, 'Full av velsignelse er Han, som om Han så vil, kan gi deg bedre enn som så, paradisets haver, hvor bekker sildrer, og slott.'),
(2866, 25, 11, 'Nei, de holder timen for løgn. Men for dem som holder timen for løgn, har Vi gjort klar helvetes ild.'),
(2867, 25, 12, 'Når Ilden ser dem i det fjerne, vil de høre et raseriutbrudd og brøl fra den.'),
(2868, 25, 13, 'Og når de sammenlenkede kastes i et trangt rom i den, så roper de på tilintetgjørelse.'),
(2869, 25, 14, '«Rop ikke i dag på en tilintetgjørelse, men rop på mange!»'),
(2870, 25, 15, 'Si: «Er dette bedre enn evighetens haver, som er lovet de gudfryktige, og som er deres lønn og skjebne?»'),
(2871, 25, 16, 'Der vil de ha alt de ønsker, der skal de være og bli. Det er et løfte som Herren står inne for.'),
(2872, 25, 17, 'Den dag Han samler dem, og dem de tilber utenom Gud, vil han spørre: «Har dere ført vill disse Mine tjenere, eller har de selv gått vill, bort fra veien?»'),
(2873, 25, 18, 'Og de vil svare: «Ære være Deg! Det ville ikke sømme seg for oss at vi skulle tas som beskyttende hjelpere fremfor Deg! Men Du gav dem og deres fedre gode dager til de glemte formaningen, og ble et lovløst folk.»'),
(2874, 25, 19, 'Nå har disse (avgudene) gjort dere til løgnere i det dere sier, og de kan verken avvende eller hjelpe. Den av dere som gjør urett, vil Vi la smake en svær straff.'),
(2875, 25, 20, 'Vi sendte ingen sendebud før deg uten at de tok til seg mat, og vandret rundt på torvene. Vi har gjort noen av dere til en prøve for andre, om dere har standhaftighet? Herren er observant.'),
(2876, 25, 21, 'De som ikke regner med møtet med Oss, sier: «Hvorfor er ikke englene sendt ned til oss? Hvorfor får vi ikke se Herren?» De viser en hovmodig holdning og går langt i overtredelser.'),
(2877, 25, 22, 'Men den dag de får se englene, den bærer intet godt bud for synderne, og de vil si: «Finn oss et sikkert tilfluktssted!»'),
(2878, 25, 23, 'Vi tar for oss de gjerninger de har gjort, og gjør dem til en sky av støv.'),
(2879, 25, 24, 'Paradisets folk vil denne dag være bedre stilt med hensyn til oppholdssted og hvileplass.'),
(2880, 25, 25, 'Den dag da himmelen revner som skyer som trekker unna og englene sendes stigende ned,'),
(2881, 25, 26, 'på denne dag tilhører det sanne herredømme den Barmhjertige, og det blir en hard dag for de vantro.'),
(2882, 25, 27, 'På denne dag vil den urettferdige bite seg i fingrene, og si: «Hadde jeg bare slått følge med Sendebudet!'),
(2883, 25, 28, 'Trøste meg! Hadde jeg bare ikke sluttet meg til en slik!'),
(2884, 25, 29, 'Han har ført meg bort fra formaningen, etter at den ble meg til del. Satan lar mennesket i stikken.»'),
(2885, 25, 30, 'Sendebudet sier: «Herre, mitt folk skyr denne Koran.»'),
(2886, 25, 31, 'Således har Vi gitt hver profet en fiende blant synderne. Men Herren strekker til som veileder og hjelper.'),
(2887, 25, 32, 'Den vantro sier: «Hvorfor er ikke Koranen åpenbart for ham, alt på en gang?» Men det ble gjort som det ble gjort, for at Vi derved kunne styrke ditt hjerte, og Vi har meddelt den med omhu.'),
(2888, 25, 33, 'Og de forelegger deg intet problem uten at Vi gir deg sannheten og det beste til forklaring.'),
(2889, 25, 34, 'De som blir samlet hodekulls til helvete, de er verst stilt, og mest på villspor fra veien.'),
(2890, 25, 35, 'Vi gav Moses skriften, og satte hans bror Aron som medhjelper.'),
(2891, 25, 36, 'Og Vi sa: «Gå til det folk som forkaster Vårt ord.» Så ødela Vi dem fullstendig.'),
(2892, 25, 37, 'Og Noas folk, da de forkastet Våre sendebud, så druknet Vi dem, og gjorde dem til et tegn for menneskene. For de urettferdige har Vi gjort i stand en smertelig straff.'),
(2893, 25, 38, 'Og Ad og Thamod og brønnfolket og mange slektledd derimellom.'),
(2894, 25, 39, 'Alle gav Vi advarende eksempler. Alle har Vi tilintetgjort, helt og holdent.'),
(2895, 25, 40, 'Folk har sikkert kommet forbi byen hvor det regnet ned et uhellsvangert regn. Har de ikke sett den? Nei, de håper ikke på oppstandelse.'),
(2896, 25, 41, 'Og når de ser deg, tar de deg bare som en spøk: «Er det denne som Gud har sendt som sendebud?'),
(2897, 25, 42, 'Han var nær ved å føre oss bort fra våre guder. Hadde vi bare ikke stått fast ved dem.» Men de vil få vite, når de ser straffen, hvem som er lengst borte fra veien.'),
(2898, 25, 43, 'Hva mener du om ham som har tatt seg en gud slik han lyster? Skal vel du være ombudsmann for ham?'),
(2899, 25, 44, 'Eller mener du at de fleste av dem hører og forstår? De er som fe, nei, de er enda mer på vidvanke.'),
(2900, 25, 45, 'Du ser vel hvordan Herren strekker skyggen? Om Han hadde villet, kunne Han gjort den urørlig. Men Vi satte solen til å dirigere skyggen.'),
(2901, 25, 46, 'Så trekker Vi den til oss, lett og gradvis.'),
(2902, 25, 47, 'Han er det, som har gjort natten til et dekke for dere, og søvnen til hvile, og dagen til å stå opp og virke.'),
(2903, 25, 48, 'Han er det, som sender vindene som budbærere foran Sin godhet. Og Vi sender ned fra oven det reneste vann,'),
(2904, 25, 49, 'for å gi liv til en jord i dvale, og leske med det, kveg og mennesker, som Vi har skapt i hopetall.'),
(2905, 25, 50, 'Vi har spredt det ut mellom dem, så de måtte komme til ettertanke, men folk flest avviser alt unntatt vantro.'),
(2906, 25, 51, 'Om Vi hadde villet, kunne Vi sendt en advarer til hver by.'),
(2907, 25, 52, 'Så hør ikke etter de vantro, men før en drabelig kamp mot dem ved hjelp av Koranen.'),
(2908, 25, 53, 'Han er det, som lar de to hav med vann strømme, det ene drikkbart og søtt, det andre salt og udrikkelig, og mellom dem har han satt en grense som står.'),
(2909, 25, 54, 'Og Han er det som har skapt mennesket av vann, og gitt ham slektskap ved blod og ekteskap. Herren er mektig.'),
(2910, 25, 55, 'Men de tilber istedenfor Gud slikt som verken kan skade eller gagne dem. Den vantro stiller alltid opp i striden mot Herren.'),
(2911, 25, 56, 'Vi har bare sendt deg som gledesbudbringer og advarer.'),
(2912, 25, 57, 'Si: «Jeg ber dere ikke om lønn for dette, annet enn at den som vil, må ta veien hen til Herren.»'),
(2913, 25, 58, 'Forlat deg på den Levende som aldri dør, og lov og pris Ham. Han er tilstrekkelig kjent med Sine tjeneres synd.'),
(2914, 25, 59, 'Han som skapte himlene og jorden, og alt som mellom dem er, på seks dager. Så tok Han plass på tronen. Den Barmhjertige! Spør den som underrettet om Ham!'),
(2915, 25, 60, 'Når man sier til dem: «Fall ned for den Barmhjertige,» så sier de: «Hva er den Barmhjertige? Skulle vi falle ned for det du beordrer oss til?» Og det øker deres motvilje.'),
(2916, 25, 61, 'Velsignelsesrik er Han som satte stjernebildene på himmelen, og har satt en lyskilde blant dem, og den lysende månen.'),
(2917, 25, 62, 'Han er det som har satt natt og dag til å følge hverandre, for den som ønsker å komme til ettertanke eller vise takknemlighet.'),
(2918, 25, 63, 'Den Barmhjertiges tjenere er de som vandrer ydmykt på jorden, som når de uvitende tiltaler dem, svarer: «Fred!»'),
(2919, 25, 64, 'Som tilbringer natten idet de faller ned eller står for Herrens åsyn.'),
(2920, 25, 65, 'Som sier: «Herre, avvend helvetes straff fra oss! Straffen der er forferdelig,'),
(2921, 25, 66, 'et sørgelig sted å være og bo.»'),
(2922, 25, 67, 'Som verken sløser eller er gjerrige når de gir, men holder fast ved en mellomting.'),
(2923, 25, 68, 'Som ikke påkaller en gud ved Guds side, ikke dreper det liv Gud har erklært ukrenkelig, unntatt når det er berettiget, og ikke driver hor. Den som gjør det, får syndens lønn.'),
(2924, 25, 69, 'Fordoblet blir straffen for ham på oppstandelsens dag, og han skal være og bli i den i fornedrelse.'),
(2925, 25, 70, 'Unntatt den som omvender seg og tror og lever rettskaffent. For dem kan Gud bytte ut deres onde gjerninger med gode. Gud er tilgivende, nåderik.'),
(2926, 25, 71, 'Og den som omvender seg og lever rettskaffent, han vender seg til Gud i sann anger.'),
(2927, 25, 72, 'Og de som ikke bærer falskt vitnemål, og som når de går forbi tomt prat, fortsetter med verdighet.'),
(2928, 25, 73, 'Som når de påminnes om Herrens ord, ikke tar imot det som stumme og blinde.'),
(2929, 25, 74, 'Som sier: «Herre, gi oss i våre hustruer og barn en fryd for øyet, og gjør oss til et eksempel for de gudfryktige.»'),
(2930, 25, 75, 'Disse vil bli lønnet med høye saler for deres standhaftighet, og der mottas de med hilsen og «Fred!».'),
(2931, 25, 76, 'Der skal de være og bli. Et herlig sted å være og bo!'),
(2932, 25, 77, 'Si: «Herren bryr seg ikke om dere, var det ikke for deres bønn. For dere har fornektet, og det vil klebe ved.»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'Ta Sin Mim'),
(2934, 26, 2, 'Dette er den klare skriftens ord.'),
(2935, 26, 3, 'Kanskje gremmer du deg til døde over at de ikke vil anta troen.'),
(2936, 26, 4, 'Om Vi vil, kan Vi sende ned over dem et tegn fra oven, som deres nakker vil forbli bøyet under.'),
(2937, 26, 5, 'Ingen ny formaning kommer til dem fra den Barmhjertige uten at de snur seg bort fra den.'),
(2938, 26, 6, 'De forkaster, men snart vil nyheter nå dem om det de drev ap med.'),
(2939, 26, 7, 'Legger de ikke merke til jorden, hvor mange herlige sorter Vi har latt vokse frem på den?'),
(2940, 26, 8, 'I dette er sannelig et jærtegn, men folk flest tror ikke.'),
(2941, 26, 9, 'Herren er i sannhet den Mektige, den Nåderike.'),
(2942, 26, 10, 'En gang kalte Herren på Moses: «Gå til det urettferdige folk,'),
(2943, 26, 11, 'Faraos folk! Vil de da ikke frykte Gud?»'),
(2944, 26, 12, 'Han svarte: «Herre, jeg frykter at de vil kalle meg løgner,'),
(2945, 26, 13, 'og mitt bryst vil snøre seg sammen og mitt tungebånd ikke løses. Så henvend Deg til Aron.'),
(2946, 26, 14, 'De har også en skyldanklage mot meg, og jeg frykter at de vil drepe meg.»'),
(2947, 26, 15, 'Han sa: «Å neida, men gå begge to med Vårt ord, og Vi skal være med dere og lytte til.'),
(2948, 26, 16, 'Gå til Farao, og si: ’Vi er sendebud for all verdens Herre!'),
(2949, 26, 17, 'Send med oss Israels barn!’»'),
(2950, 26, 18, 'Farao svarte: «Har vi ikke oppdratt deg blant oss som barn? Var ikke du hos oss mange år av ditt liv?'),
(2951, 26, 19, 'Og du gjorde den gjerning du gjorde, og var utakknemlig!»'),
(2952, 26, 20, 'Moses sa: «Jeg gjorde det den gang, da jeg var av dem som fór vill,'),
(2953, 26, 21, 'og så flyktet jeg, da jeg fryktet dere. Men Herren gav meg visdom, og gjorde meg til sendebud.'),
(2954, 26, 22, 'Er denne godhet du har vist meg en unnskyldning for å trellbinde Israels barn?»'),
(2955, 26, 23, 'Farao svarte: «Hva er da all verdens Herre?»'),
(2956, 26, 24, 'Han sa: «Herren over himlene og jorden og alt som mellom dem er, om dere hadde visshet.»'),
(2957, 26, 25, 'Da sa Farao til dem rundt seg: «Har dere hørt på maken!»'),
(2958, 26, 26, 'Moses sa: «Deres og deres henfarne fedres Herre.»'),
(2959, 26, 27, 'Da sa Farao: «Det sendebud som er sendt til dere, er visselig forrykt.»'),
(2960, 26, 28, 'Moses fortsatte: «Herren over øst og vest og alt som derimellom er, om dere forstår.»'),
(2961, 26, 29, 'Da sa Farao: «Hvis du antar en annen gud enn meg, vil jeg kaste deg rett i fengsel!»'),
(2962, 26, 30, 'Moses svarte: «Enn om jeg brakte deg noe som klargjør?»'),
(2963, 26, 31, 'Farao sa: «Så bring det, hvis du snakker sant.»'),
(2964, 26, 32, 'Så kastet Moses staven sin, og se, den ble en slange, tydelig og klart.'),
(2965, 26, 33, 'Og han trakk frem sin hånd, og se, den syntes hvit for dem som så på.'),
(2966, 26, 34, 'Da sa Farao til rådet rundt ham: «Dette er sannelig en habil trollmann!'),
(2967, 26, 35, 'Han vil fordrive dere fra deres land med trolldomskunstene sine. Hva foreslår dere?»'),
(2968, 26, 36, 'De svarte: «La det bero med ham og hans bror, og send oppbud til alle byer,'),
(2969, 26, 37, 'så de bringer deg alle dyktige trollmenn.»'),
(2970, 26, 38, 'Trollmennene ble samlet til fastsatt tid og dag.'),
(2971, 26, 39, 'Folket ble spurt: «Vil dere komme sammen?'),
(2972, 26, 40, 'Kanskje vi vil følge trollmennene om de blir vinnere.»'),
(2973, 26, 41, 'Da trollmennene hadde innfunnet seg, sa de til Farao: «Får vi en belønning om vi vinner?»'),
(2974, 26, 42, 'Og han svarte: «Ja, da blir dere av min nærmeste krets.»'),
(2975, 26, 43, 'Moses sa til dem: «Kast det dere vil kaste!»'),
(2976, 26, 44, 'Så kastet de sine tau og staver, og sa: «Ved Faraos makt, vi skal sannelig vinne!»'),
(2977, 26, 45, 'Så kastet Moses sin stav, og se, den slukte det de hadde svindlet i hop.'),
(2978, 26, 46, 'Og trollmennene måtte kaste seg ned i ærefrykt, og sa:'),
(2979, 26, 47, '«Vi tror på all verdens Herre,'),
(2980, 26, 48, '– Mose og Arons Herre!»'),
(2981, 26, 49, 'Da sa Farao: «Tror dere denne mannen før jeg har gitt dere tillatelse? Han er vel deres læremester som har lært dere trolldom. Nå skal dere får å vite! Jeg skal sannelig hugge av deres hender og føtter i kryss, og så korsfeste dere alle sammen.»'),
(2982, 26, 50, 'De svarte: «Det gjør ingenting! Vi vender tilbake til vår Herre.'),
(2983, 26, 51, 'Vi ønsker inderlig at vår Herre må tilgi oss våre feiltrinn, siden vi er de første troende.»'),
(2984, 26, 52, 'Så gav Vi Moses denne inspirasjon: «Dra av gårde med Mine tjenere nattestid! Dere blir nok forfulgt.»'),
(2985, 26, 53, 'Så sendte Farao opprop til byene,'),
(2986, 26, 54, '«Disse er bare en liten flokk,'),
(2987, 26, 55, 'men de har forarget oss,'),
(2988, 26, 56, 'skjønt vi er tallrike og på vakt.»'),
(2989, 26, 57, 'Så Vi fordrev dem fra haver og kilder'),
(2990, 26, 58, 'og skatter og et fint bosted.'),
(2991, 26, 59, 'Slik var det. Og Vi lot Israels barn arve det.'),
(2992, 26, 60, 'Men egypterne forfulgte dem på vei østover, og'),
(2993, 26, 61, 'da de to hærskarer fikk øye på hverandre, sa folket til Moses: «Vi blir innhentet!»'),
(2994, 26, 62, 'Moses sa: «Å nei, Herren er med meg! Han vil lede meg.»'),
(2995, 26, 63, 'Og Vi gav Moses denne inspirasjon. «Slå havet med din stav!» Og det delte seg, og hver side var som et stort fjell.'),
(2996, 26, 64, 'Vi lot de andre komme derhen.'),
(2997, 26, 65, 'Og Vi reddet Moses og dem som var med ham, alle sammen,'),
(2998, 26, 66, 'og så druknet Vi de andre.'),
(2999, 26, 67, 'I dette er visselig et tegn! Men folk flest tror ikke.'),
(3000, 26, 68, 'Sannelig, Herren er den Mektige, den Nåderike.'),
(3001, 26, 69, 'Les frem for dem historien om Abraham!'),
(3002, 26, 70, 'En gang sa han til sin far og sitt folk: «Hva er det dere tilber?»'),
(3003, 26, 71, 'De svarte: «Vi tilber avguder, og fortsetter å holde oss til dem.»'),
(3004, 26, 72, 'Da sa han: «Hører de dere, når dere ber?'),
(3005, 26, 73, 'Eller gir de dere gagn eller skade?»'),
(3006, 26, 74, 'De svarte: «Nei, men vi fant at våre fedre gjorde dette.»'),
(3007, 26, 75, 'Han sa: «Hva mener dere om dem dere har tilbedt,'),
(3008, 26, 76, 'dere og deres henfarne fedre?'),
(3009, 26, 77, 'De er en fiende for meg. Ikke så med all verdens Herre!'),
(3010, 26, 78, 'Som har skapt meg, og gir meg ledelse,'),
(3011, 26, 79, 'som gir meg mat og drikke,'),
(3012, 26, 80, 'som helbreder meg når jeg er syk,'),
(3013, 26, 81, 'som lar meg dø og så gir meg liv,'),
(3014, 26, 82, 'som jeg inderlig håper vil tilgi meg mine feiltrinn på dommens dag.'),
(3015, 26, 83, 'Herre, gi meg visdom, og foren meg med de rettferdige'),
(3016, 26, 84, 'og gi meg et godt ettermæle blant kommende slekter.'),
(3017, 26, 85, 'Gjør meg til arving av lykksalighetens have.'),
(3018, 26, 86, 'Tilgi min far! Han var av de villfarende.'),
(3019, 26, 87, 'Gjør meg ikke til skamme på den dag da alle gjenoppvekkes,'),
(3020, 26, 88, 'den dag da verken rikdom eller barn gagner,'),
(3021, 26, 89, 'kun den som kommer til Gud med et rent hjerte.»'),
(3022, 26, 90, 'Og paradiset bringes nær de gudfryktige.'),
(3023, 26, 91, 'Og helvete bringes for øynene på de villfarende.'),
(3024, 26, 92, 'Det sies til dem: «Hvor er nå det som dere tilbad'),
(3025, 26, 93, 'utenom Gud? Kan de hjelpe dere, eller seg selv?»'),
(3026, 26, 94, 'Så kastes de inn i det, både de og forførerne'),
(3027, 26, 95, 'og Satans skarer, alle sammen.'),
(3028, 26, 96, 'Og de sier, mens de krangler:'),
(3029, 26, 97, '«Ved Gud, vi var sannelig i klar villfarelse'),
(3030, 26, 98, 'da vi satte dere på linje med all verdens Herre.'),
(3031, 26, 99, 'Det var synderne som villedet oss.'),
(3032, 26, 100, 'Og nå har vi ingen til å tale vår sak,'),
(3033, 26, 101, 'ingen god venn.'),
(3034, 26, 102, 'Kunne vi bare gjøre det hele om igjen, og bli troende!»'),
(3035, 26, 103, 'I dette er visselig et jærtegn! Men folk flest tror ikke.'),
(3036, 26, 104, 'Herren er den Mektige, den Nåderike.'),
(3037, 26, 105, 'Noas folk kalte sendebudene for løgnere,'),
(3038, 26, 106, 'da deres bror Noa sa til dem: «Vil dere ikke være gudfryktige?'),
(3039, 26, 107, 'Jeg er et pålitelig sendebud til dere,'),
(3040, 26, 108, 'så frykt Gud, og adlyd meg!'),
(3041, 26, 109, 'Jeg ber dere ikke om noen lønn for dette, min lønn er en sak for all verdens Herre.'),
(3042, 26, 110, 'Så frykt Gud, og adlyd meg.»'),
(3043, 26, 111, 'De svarte: «Skal vi ha tiltro til deg, som bare de laveste lag slutter seg til?»'),
(3044, 26, 112, 'Han sa: «Jeg kjenner ikke til hva de har gjort.'),
(3045, 26, 113, 'Deres regnskap er Herrens sak, om dere bare ante.'),
(3046, 26, 114, 'Jeg driver ikke de troende bort!'),
(3047, 26, 115, 'Jeg er bare en klar advarer.»'),
(3048, 26, 116, 'De sa de: «Hvis du ikke holder opp, Noa, vil du bli steinjaget.»'),
(3049, 26, 117, 'Han sa: «Herre, mitt folk kaller meg løgner.'),
(3050, 26, 118, 'Finn Du en ordning mellom meg og dem, og redd meg og de troende som er med meg.»'),
(3051, 26, 119, 'Så reddet Vi ham og dem som var med ham i det fullastede skip.'),
(3052, 26, 120, 'Derpå druknet Vi dem som var igjen.'),
(3053, 26, 121, 'Heri er visselig et tegn! Men folk flest tror ikke.'),
(3054, 26, 122, 'Herren er den Mektige, den Nåderike.'),
(3055, 26, 123, 'Folket Ad kalte sendebudene for løgnere,'),
(3056, 26, 124, 'da deres bror Hod sa til dem: «Vil dere ikke være gudfryktige?'),
(3057, 26, 125, 'Jeg er et pålitelig sendebud til dere,'),
(3058, 26, 126, 'så frykt Gud, og adlyd meg!'),
(3059, 26, 127, 'Jeg ber dere ikke om noen lønn for dette. Min lønn er en sak for all verdens Herre.'),
(3060, 26, 128, 'Bygger dere på hver høyde et symbolsk tegn til ingen nytte,'),
(3061, 26, 129, 'og legger dere til borger, kanskje dere skal leve evig?'),
(3062, 26, 130, 'Og når dere går til angrep, angriper dere som voldsmenn.'),
(3063, 26, 131, 'Så frykt Gud, og adlyd meg,'),
(3064, 26, 132, 'frykt Ham som har hjulpet dere til alt dere vet,'),
(3065, 26, 133, 'hjulpet dere til fe og sønner,'),
(3066, 26, 134, 'haver og kilder!'),
(3067, 26, 135, 'Jeg frykter for at dere vil rammes av en svær dags straff.»'),
(3068, 26, 136, 'De svarte: «Det er det samme for oss om du formaner eller ikke formaner.'),
(3069, 26, 137, 'Dette er bare tradisjoner fra de henfarne.'),
(3070, 26, 138, 'Vi rammes ikke av straff!»'),
(3071, 26, 139, 'De kalte ham løgner, og Vi tilintetgjorde dem. Heri er visselig et tegn! Men folk flest tror ikke.'),
(3072, 26, 140, 'Herren er den Mektige, den Nåderike.'),
(3073, 26, 141, 'Folket Thamod kalte sendebudene for løgnere,'),
(3074, 26, 142, 'da deres bror Salih sa til dem: «Vil dere ikke være gudfryktige?'),
(3075, 26, 143, 'Jeg er et pålitelig sendebud til dere,'),
(3076, 26, 144, 'så frykt Gud, og adlyd meg!'),
(3077, 26, 145, 'Jeg ber dere ikke om noen lønn for dette. Min lønn er en sak for all verdens Herre.'),
(3078, 26, 146, 'Vil dere bli latt i fred med alt som her er,'),
(3079, 26, 147, 'haver og kilder,'),
(3080, 26, 148, 'åkerland og palmer med fine fruktkolber?'),
(3081, 26, 149, 'Og glade og fornøyde å hugge dere ut boliger i fjellsidene?'),
(3082, 26, 150, 'Så frykt Gud, og adlyd meg!'),
(3083, 26, 151, 'Hør ikke etter de lettsindiges påbud,'),
(3084, 26, 152, 'de som anstifter fordervelse på jorden og ikke setter tingene på rett plass.»'),
(3085, 26, 153, 'De svarte: «Du er bare en forhekset mann,'),
(3086, 26, 154, 'du er bare et vanlig menneske som oss! Kom med et jærtegn, om du snakker sant.»'),
(3087, 26, 155, 'Han sa: «Dette er en kamelhoppe. Hun skal ha drikke, og dere skal ha drikke på bestemte dager.'),
(3088, 26, 156, 'Gjør henne ikke noe ondt, så ikke en svær dags straff tar dere.»'),
(3089, 26, 157, 'Men de mishandlet henne, og det kom de til å angre!'),
(3090, 26, 158, 'For straffen tok dem. Heri er visselig et tegn! Men folk flest tror ikke.'),
(3091, 26, 159, 'Herren er den Mektige, den Nåderike.'),
(3092, 26, 160, 'Lots folk kalte sendebudene for løgnere,'),
(3093, 26, 161, 'da deres bror Lot sa til dem: «Vil dere ikke være gudfryktige?'),
(3094, 26, 162, 'Jeg er et pålitelig sendebud til dere,'),
(3095, 26, 163, 'så frykt Gud, og adlyd meg!'),
(3096, 26, 164, 'Jeg ber dere ikke om noen lønn for dette. Min lønn er en sak for all verdens Herre.'),
(3097, 26, 165, 'Er det slik at dere holder dere til menn fortrinnsvis,'),
(3098, 26, 166, 'og forsømmer deres hustruer som Herren har skapt for dere? Nei, dere er et folk som går over grensen.»'),
(3099, 26, 167, 'De svarte: «Hvis du ikke holder opp, Lot, vil du bli jaget bort.»'),
(3100, 26, 168, 'Da sa han: «Jeg avskyr i sannhet det dere gjør!'),
(3101, 26, 169, 'Herre, redd meg og mine fra det de bedriver.»'),
(3102, 26, 170, 'Så reddet Vi ham og hans, alle sammen,'),
(3103, 26, 171, 'unntatt en gammel kone blant de gjenværende.'),
(3104, 26, 172, 'Så ødela Vi de andre.'),
(3105, 26, 173, 'Vi lot et regn regne over dem. Ondt er regnet til dem som er advart.'),
(3106, 26, 174, 'Heri er visselig et tegn! Men folk flest tror ikke.'),
(3107, 26, 175, 'Herren er den Mektige, den Nåderike.'),
(3108, 26, 176, 'Skogfolket kalte sendebudene for løgnere,'),
(3109, 26, 177, 'da Shoaib sa til dem: «Vil dere ikke være gudfryktige?'),
(3110, 26, 178, 'Jeg er et pålitelig sendebud til dere,'),
(3111, 26, 179, 'så frykt Gud, og adlyd meg!'),
(3112, 26, 180, 'Jeg ber dere ikke om noen lønn for dette. Min lønn er en sak for all verdens Herre.'),
(3113, 26, 181, 'Gi fullt mål, og vær ikke av dem som snyter.'),
(3114, 26, 182, 'Vei med riktig vekt,'),
(3115, 26, 183, 'og snyt ikke folk på det som er deres. Stift ikke urett og fordervelse i landet.'),
(3116, 26, 184, 'Frykt Ham som har skapt dere og de henfarne slekter.»'),
(3117, 26, 185, 'De svarte: «Du er bare en forhekset mann,'),
(3118, 26, 186, 'du er bare et vanlig menneske som oss! Vi holder deg for å være en løgner.'),
(3119, 26, 187, 'La falle over oss stykker av himmelen, om du snakker sant.»'),
(3120, 26, 188, 'Han sa: «Herren kjenner vel til hva dere bedriver.»'),
(3121, 26, 189, 'De kalte ham løgner, men så tok skyggens dags straff dem. Det var i sannhet en svær dags straff.'),
(3122, 26, 190, 'Heri er visselig et tegn! Men folk flest tror ikke.'),
(3123, 26, 191, 'Herren er den Mektige, den Nåderike.'),
(3124, 26, 192, 'Dette er en åpenbaring fra all verdens Herre,'),
(3125, 26, 193, 'den betrodde Ånd steg ned med den'),
(3126, 26, 194, 'til ditt hjerte, så du kunne bli en advarer'),
(3127, 26, 195, 'på klart, arabisk språk.'),
(3128, 26, 196, 'Den finnes i de gamles skrifter!'),
(3129, 26, 197, 'Er det da ikke et tegn for dem at Israels skriftlærde kjenner til den?'),
(3130, 26, 198, 'Hadde Vi sendt den til noen blant de fremmede folk,'),
(3131, 26, 199, 'og han hadde fremlest den for dem, så ville de ikke ha trodd på den.'),
(3132, 26, 200, 'Slik bringer Vi den inn i syndernes hjerter.'),
(3133, 26, 201, 'Men de tror ikke på den, før de ser den smertefulle straff,'),
(3134, 26, 202, 'så den vil komme over dem plutselig, uten at de aner noe.'),
(3135, 26, 203, 'Og de vil si: «Kan vi få utsettelse?»'),
(3136, 26, 204, 'Skulle de be om å få Vår straff påskyndet?'),
(3137, 26, 205, 'Hva synes du? Vi har latt dem nyte livet i årevis,'),
(3138, 26, 206, 'og så kommer det over dem som er stilt dem i utsikt.'),
(3139, 26, 207, 'De gode dager de fikk nyte hjelper dem ikke!'),
(3140, 26, 208, 'Ingen by har Vi ødelagt uten at den hadde fått advarere'),
(3141, 26, 209, 'til påminnelse. Vi var ikke urettferdige!'),
(3142, 26, 210, 'Satanene har ikke brakt Koranen ned.'),
(3143, 26, 211, 'Det passer seg ikke for dem, og de kan det heller ikke.'),
(3144, 26, 212, 'De er avskåret fra å snappe opp noe.'),
(3145, 26, 213, 'Så påkall ingen gud ved Guds side, så du havner blant dem som lider straff.'),
(3146, 26, 214, 'Advar din familie og dine nærmeste.'),
(3147, 26, 215, 'Brus ikke med vingene, men senk din vinge overfor de troende som følger deg.'),
(3148, 26, 216, 'Om de ikke lyder deg, så si: «Jeg er ansvarsfri for det dere gjør.»'),
(3149, 26, 217, 'Forlat deg på den Mektige, den Nåderike,'),
(3150, 26, 218, 'som ser deg der du står,'),
(3151, 26, 219, 'og når du ferdes blant de bedende.'),
(3152, 26, 220, 'Han er den Hørende, den Allvitende.'),
(3153, 26, 221, 'Skal Jeg fortelle dere hvem satanene slår seg ned på?'),
(3154, 26, 222, 'De slår seg ned på enhver som er syndbelastet og falsk,'),
(3155, 26, 223, 'og forteller ham hva de har hørt. Men de fleste av dem er løgnere.'),
(3156, 26, 224, 'Likeledes skaldene, de forførte følger dem.'),
(3157, 26, 225, 'Har du ikke sett at de streifer om i alle daler,'),
(3158, 26, 226, 'og uttaler det de ikke selv praktiserer?'),
(3159, 26, 227, 'Unntatt er de som tror og lever rettskaffent og ideligen kommer Gud i hu, som finner hjelp når de har lidt urett. De som gjør urett skal få vite hvilken skjebne de vender seg mot.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'Dette er Koranens og en klar skrifts ord,'),
(3161, 27, 2, 'ledelse og godt budskap til de troende,'),
(3162, 27, 3, 'som forretter bønnen, gir det rituelle bidrag, og er forvisset om det hinsidige liv.'),
(3163, 27, 4, 'De som ikke tror på det hinsidige, i deres øyne har Vi latt deres gjerninger fortone seg prydelige, så de vandrer i blinde.'),
(3164, 27, 5, 'Disse har en ond straff i vente, og i det hinsidige er det de største taperne.'),
(3165, 27, 6, 'Du mottar Koranen fra Én som er vis, som vet.'),
(3166, 27, 7, 'Moses sa til sin familie: «Jeg har fått øye på en ild. Jeg skal bringe dere underretning om den, eller bringe dere en brann, om dere måtte ønske å varme dere.»'),
(3167, 27, 8, 'Da han kom hen til den, ropte en røst: «Velsignet er Han, som er i ilden, og han som er ved den! Ære være Gud, all verdens Herre!'),
(3168, 27, 9, 'Moses, se, det er Jeg, Gud, den Mektige, den Vise.'),
(3169, 27, 10, 'Kast staven din!» Da han så den bevege seg som en slange, snudde han ryggen og flyktet uten å vende seg. «Moses, vær ikke redd! Hos meg trenger ikke sendebudene å frykte,'),
(3170, 27, 11, 'unntatt den som gjør urett. Om han så etter urett bytter til godt, så er Jeg tilgivende, nåderik.'),
(3171, 27, 12, 'Stikk din hånd inn på brystet, og den vil komme ut hvit, men uskadd. Dette er av de ni tegn til Farao og hans folk. De er et ugudelig folk.»'),
(3172, 27, 13, 'Da Våre tegn kom til dem klart og tydelig, sa de: «Dette er skjære trolldom.»'),
(3173, 27, 14, 'Og de bestred dem i urett og hovmod, mens de i seg selv var overbevist av dem. Se hva enden ble for dem som sprer fordervelse!'),
(3174, 27, 15, 'Vi gav i sin tid David og Salomo visdom, og de sa: «Gud være lovet, som har gitt oss fortrinn fremfor mange av sine troende tjenere.»'),
(3175, 27, 16, 'Salomo ble Davids arvtager, og han sa: «Hør folk, vi er blitt lært fuglenes tale, og er blitt gitt av alle ting. Dette er i sannhet en klar gunstbevisning.»'),
(3176, 27, 17, 'For Salomo ble hans hærskarer samlet, dsjinner, mennesker, fugler, oppstilt på rad og rekke,'),
(3177, 27, 18, 'til de kom til maurdalen, og en maur sa: «Maur, gå inn i deres boliger, så Salomo og hans hær ikke tramper dere i hjel uten at de merker det.'),
(3178, 27, 19, 'Salomo smålo ved dens tale, og sa: «Herre, gi meg å takke for Din nåde som Du har vist meg og mine foreldre, og at jeg må leve rettskaffent som Deg behager. Og la meg gå inn, ved Din nåde, blant Dine rettferdige tjenere.»'),
(3179, 27, 20, 'Og han inspiserte fuglene, og sa: «Hvordan har det seg at jeg ikke ser hærfuglen? Er den fraværende?'),
(3180, 27, 21, 'Jeg vil gi ham en streng refselse, eller drepe ham, eller han må komme til meg med en klar grunn.»'),
(3181, 27, 22, 'Han lot ikke lenge vente på seg, og sa: «Jeg har fått rede på noe, som du ikke kjenner til, og kommer til deg fra Saba med sikker informasjon.'),
(3182, 27, 23, 'Jeg fant at en kvinne hersket over dem, hun er blitt gitt av alle ting, og har en stor trone.'),
(3183, 27, 24, 'Jeg fant at hun og hennes folk tilber solen, istedenfor Gud. Satan har latt deres gjerninger fortone seg prydelige for dem, og han stenger dem ute fra veien, og de finner ikke rett vei,'),
(3184, 27, 25, 'så de bøyer seg ikke for Gud, som bringer frem det skjulte i himlene og på jord, som vet hva dere skjuler og hva dere bringer åpent frem.'),
(3185, 27, 26, 'Gud! Det er ingen gud unntatt Ham, den herlige trones Herre.»'),
(3186, 27, 27, 'Da sa Salomo: «Vi skal se om du taler sant eller farer med løgn.'),
(3187, 27, 28, 'Dra av gårde med dette brev fra meg, og lever det til dem! Trekk deg så bort, og se hva de svarer!»'),
(3188, 27, 29, 'Dronningen sa: «Mitt råd, jeg har fått et aktverdig brev.'),
(3189, 27, 30, 'Det er fra Salomo, og lyder: ’I Guds, den Barmhjertiges, den Nåderikes navn.'),
(3190, 27, 31, 'Vis ikke overmot mot meg, men kom til meg, som undergivne (muslimer).’»'),
(3191, 27, 32, 'Hun fortsatte: «Mitt råd, gi meg en uttalelse i denne min sak. Jeg pleier ikke avgjøre saker før dere avgir uttalelse til meg.»'),
(3192, 27, 33, 'De svarte: «Vi har makt og stor kampkraft, avgjørelsen ligger hos deg. Så overvei hva du vil befale.»'),
(3193, 27, 34, 'Da sa hun: «Når konger trenger inn i en by, anstiller de ødeleggelser, og hensetter dens mektigste innbyggere i fornedrelse. Slik farer de frem.'),
(3194, 27, 35, 'Jeg vil sende dem en gave, og se hva utsendingen bringer tilbake.»'),
(3195, 27, 36, 'Da han kom tilbake til Salomo, sa denne: «Vil dere overdynge meg med rikdom, når det som Gud har gitt meg er bedre enn det Han har gitt dere? Men dere føler glede ved deres gave.'),
(3196, 27, 37, 'Vend tilbake til dem, og si at vi vil komme til dem med en hær som de ikke rår med, og drive dem ut derfra, ydmyket og fornedret.»'),
(3197, 27, 38, 'Han fortsatte: «Hør, rådsmenn, hvem av dere kan bringe meg hennes trone før de kommer til meg som undergivne?»'),
(3198, 27, 39, 'En av dsjinnene, en ifrit sa: «Jeg vil bringe den til deg før du reiser deg fra din plass, for jeg har styrke til det og kan stoles på.»'),
(3199, 27, 40, 'En som hadde kunnskap fra skriften, sa: «Jeg vil bringe den til deg før du kan lukke øyet.» Og så, da han så den stå foran ham, sa han: «Dette er en Herrens gunstbevisning for å sette meg på prøve, om jeg er takknemlig eller utakknemlig. Den som er takknemlig, takker til sitt eget beste, og den som er utakknemlig, Herren er selvtilstrekkelig, verdig!»'),
(3200, 27, 41, 'Og han fortsatte: «Gjør hennes trone ugjenkjennelig for henne, så vi kan se om hun er under ledelse, eller av dem som ikke er det.»'),
(3201, 27, 42, 'Så da hun kom, sa man: «Er din trone slik som dette?» Hun svarte: «Det ser ut som var det den. Vi ble gitt kunnskap før dette, og gav oss inn under Gud.»'),
(3202, 27, 43, 'Men det hun tjente, istedenfor Gud, holdt henne borte, og hun var av de vantro folk.'),
(3203, 27, 44, 'Så sa man til henne: «Kom inn i palasset!» Da hun fikk se det, trodde hun det var en vannflate og trakk kjolen oppover bena, men Salomo sa: «Det er et palass belagt med krystall.» Da sa hun: «Herre, jeg har gjort urett mot meg selv! Jeg overgir meg sammen med Salomo til Gud, all verdens Herre.»'),
(3204, 27, 45, 'Vi sendte til folket Thamod deres bror Salih: «Tjen Gud!» Men se, de ble to partier som lå i strid.'),
(3205, 27, 46, 'Han sa: «Mitt folk, hvorfor vil dere se påskyndet det onde før det gode? Hvorfor ber dere ikke Gud om tilgivelse, så dere må finne nåde?»'),
(3206, 27, 47, 'De svarte: «Vi ser et dårlig forvarsel i deg og de som er sammen med deg.» Da sa han: «Deres forvarsel ligger i Guds hånd! Nei, dere er et folk som settes på prøve.»'),
(3207, 27, 48, 'Nå var det i byen en gruppe på ni som skapte ufred i landet og ikke fred og orden,'),
(3208, 27, 49, 'og de sa: «Sverg ved Gud overfor hverandre: ’Vi skal overfalle ham og hans husfolk om natten, og så skal vi si til hans nærmeste blodhevnforpliktede at vi var ikke vitner til tilintetgjørelsen av hans hus, og vi snakker sant.’»'),
(3209, 27, 50, 'De smidde renker, men Vi la også planer, uten at de ante det.'),
(3210, 27, 51, 'Og se, hva enden ble for deres renker! Vi utryddet dem og deres folk, alle sammen.'),
(3211, 27, 52, 'Dette er deres boliger, forlatt i ruiner, på grunn av deres urett. Heri er et tegn for mennesker som vet.'),
(3212, 27, 53, 'Men Vi reddet dem som trodde og var gudfryktige.'),
(3213, 27, 54, 'Og Lot, da han sa til sitt folk: «Begår dere skammeligheter med åpne øyne?'),
(3214, 27, 55, 'Går dere i begjær til menn fremfor kvinner? Dere er i sannhet uvitende mennesker!»'),
(3215, 27, 56, 'Hans folk hadde ikke annet svar å gi enn å si: «Jag Lots hus ut av byen! Det er folk som tror de er rene.»'),
(3216, 27, 57, 'Så reddet Vi ham og hans hus, unntatt hans kone. Vi bestemte at hun skulle bli blant de gjenværende.'),
(3217, 27, 58, 'Vi lot regn regne over dem, og ondt er de advartes regn.'),
(3218, 27, 59, 'Si: «Lovet være Gud, og fred over Hans tjenere som Han har utvalgt. Hva er å foretrekke, Gud, eller det de setter ved Hans side?»'),
(3219, 27, 60, 'Han som har skapt himlene og jorden, og sender dere vann fra oven, hvorved vi frembringer haver full av skjønnhet. Deres trær kunne dere aldri frembringe. Er det vel en gud sammen med Gud? Nei, men de er et folk som setter annet på linje med Ham!'),
(3220, 27, 61, 'Han som har skapt det faste land og latt det gjennomstrømme av elver, satt fjellmassiver på det, og plassert et skille mellom de to hav. Er det vel en gud sammen med Gud? Nei, men folk flest vet det ikke.'),
(3221, 27, 62, 'Han som bønnhører den nødstedte når han kaller på Ham, tar bort det onde, og lar dere ta over jorden. Er det vel en gud sammen med Gud? Lite tenker de etter.'),
(3222, 27, 63, 'Han som viser dere vei i mørke på land og hav, som sender vindene som budbringere for sin barmhjertighet. Er det vel en gud sammen med Gud? Høyt hevet er Han over det de setter ved Hans side.'),
(3223, 27, 64, 'Han som frembringer skapningen, og gjør det på nytt, gir dere underhold fra himmel og jord. Er det vel en gud sammen med Gud? Si: «Kom med deres bevis, om dere snakker sant!»'),
(3224, 27, 65, 'Si: «Ingen i himlene eller på jord kjenner det skjulte, unntatt Gud.» De aner ikke når de skal gjenoppvekkes.'),
(3225, 27, 66, 'Nei, deres kunnskap svikter når det gjelder det hinsidige, de er i tvil om det, nei, de er blinde overfor det!'),
(3226, 27, 67, 'De vantro sier: «Skal vi og våre fedre gjenoppvekkes når vi er blitt støv?'),
(3227, 27, 68, 'Både vi og våre fedre er blitt lovet dette tidligere. Dette er bare gamle fabler!»'),
(3228, 27, 69, 'Si: «Reis omkring på jorden og se hva enden ble for synderne!»'),
(3229, 27, 70, 'Vær ikke bedrøvet over dem og føl deg ikke trykket over deres renker!'),
(3230, 27, 71, 'De sier: «Når kommer så det som er stilt i utsikt, om dere snakker sant?»'),
(3231, 27, 72, 'Si: «Kanskje er noe av det dere ønsker påskyndet like i hælene på dere.»'),
(3232, 27, 73, 'Sannelig, Herren er full av godhet mot menneskene. Men folk flest takker ikke.'),
(3233, 27, 74, 'Herren vet hva deres hjerter skjuler, og hva de bærer åpent frem.'),
(3234, 27, 75, 'Det er intet skjult i himlene og på jord, som ikke finnes i en klar Bok.'),
(3235, 27, 76, 'Denne Koran forteller Israels barn det meste av det de er uenige om.'),
(3236, 27, 77, 'Den er ledelse og nåde til de troende.'),
(3237, 27, 78, 'Herren vil treffe avgjørelse mellom dem ved sin dom. Han er den Mektige, den Allvitende.'),
(3238, 27, 79, 'Så forlat deg på Gud, du står på den klare sannhet!'),
(3239, 27, 80, 'Du kan visselig ikke få døde til å høre, og du kan heller ikke få døve til å høre kallet, når de vender ryggen til og går.'),
(3240, 27, 81, 'Du kan heller ikke lede blinde ut av deres villfarelse. Du får ingen til å høre, unntatt de som tror på Vårt ord, og gir seg hen til Gud.'),
(3241, 27, 82, 'Når ordet inntreffer overfor dem, vil Vi la stå frem av jorden et dyr som vil tale til dem, at menneskene ikke hadde tiltro til Vårt ord.'),
(3242, 27, 83, 'Den dag vil Vi samle fra alle folk en flokk av dem som forkastet Vårt ord, på rekke og rad.'),
(3243, 27, 84, 'Når de er kommet, vil Han si: «Holdt dere Mitt ord for løgn, uten at dere visste hva det dreiet seg om? Hva er det dere har gjort?»'),
(3244, 27, 85, 'Og ordet inntreffer overfor dem for den urett de har begått, og de har ingenting å si.'),
(3245, 27, 86, 'De har vel sett at Vi har gjort natten til hvile for dem, og dagen til å gi lys? Heri er visselig et jærtegn for folk som tror.'),
(3246, 27, 87, 'Den dag det støtes i basunen, vil alle som er i himlene og på jord bli slått av skrekk, unntatt den som Gud bestemmer. Alle trer frem for Ham i underkastelse.'),
(3247, 27, 88, 'Du vil se fjellene, som du trodde var faste og urørlige, flyte bort som skyer, Guds verk, Han fullbyrder alt. Han er vel underrettet om det dere gjør.'),
(3248, 27, 89, 'Den som kommer med en god gjerning, har noe som er bedre i vente, og de er trygge for denne dags skrekk.'),
(3249, 27, 90, 'Men den som kommer med en ond gjerning, går hodekulls i Ilden. «Belønnes dere for annet enn det dere har gjort?»'),
(3250, 27, 91, 'Jeg er kun pålagt å tjene Herren over dette sted, som Han har fredlyst. Ham tilhører alt. Jeg er pålagt å være av dem som gir seg Gud i vold,'),
(3251, 27, 92, 'og å fremlese Koranen. Om noen finner ledelse, så er han på rett vei til eget beste. Om noen går på villstrå, så si: «Jeg er bare en advarer.»'),
(3252, 27, 93, 'Og si: «Gud skje lov og pris!» Han vil vise dere Sine jærtegn, og dere vil erkjenne dem. Gud er ikke likegyldig med hva dere gjør!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'Ta Sin Mim'),
(3254, 28, 2, 'Dette er den klare skrifts ord!'),
(3255, 28, 3, 'Vi skal meddele deg noe av beretningen om Moses og Farao, rett og riktig, for folk som tror.'),
(3256, 28, 4, 'Farao var mektig og stor i landet, og delte dets innbyggere i grupper. En gruppe blant dem undertrykket han, drepte deres sønner og lot deres kvinner leve. Han anstiftet i sannhet ufred og elendighet.'),
(3257, 28, 5, 'Men Vi ønsket å vise nåde mot dem som var undertrykket i landet,'),
(3258, 28, 6, 'og gjøre dem til ledere og arvtagere og gi dem fotfeste i landet, og å vise Farao og Haman og deres hærskarer det de fryktet fra dem.'),
(3259, 28, 7, 'Så gav Vi Mose mor denne inspirasjon: «Gi ham bryst, og når du blir redd for ham, så sett ham ut på elven! Vær ikke redd og vær ikke bedrøvet! Vi vil bringe ham tilbake til deg, og gjøre ham til et sendebud.»'),
(3260, 28, 8, 'Faraos husstand plukket ham opp, så han kunne bli en fiende og en bekymring for dem. Farao og Haman og deres hærskarer var i sannhet syndere.'),
(3261, 28, 9, 'Faraos hustru sa: «Han vil bli en fryd for øyet både for meg og deg. Drep ham ikke! Kanskje han kan bli oss til nytte, eller vi kan adoptere ham som sønn,» – men de ante ingenting.'),
(3262, 28, 10, 'Men Mose mors hjerte ble tomt, og hun var på nippet til å røpe ham, om Vi ikke hadde styrket hennes hjerte, så hun kunne bli en troende.'),
(3263, 28, 11, 'Og hun sa til hans søster: «Følg etter ham,» og hun holdt øye med ham fra avstand, uten at de ante noe.'),
(3264, 28, 12, 'Vi hadde forbudt ammer for ham tidligere, så hun sa: «Skal jeg vise dere til en husstand som kan ta seg av ham for dere, og stelle pent med ham?»'),
(3265, 28, 13, 'Slik brakte Vi ham tilbake til hans mor, så hun kunne tørke tårene og ikke sørge, og vite at Guds løfte er sant. Men folk flest vet ikke.'),
(3266, 28, 14, 'Da han nådde modenhetsalder og var utvokst, gav Vi ham visdom og kunnskap. Slik lønner Vi dem som gjør godt!'),
(3267, 28, 15, 'Og han gikk inn i byen på en tid da innbyggerne ikke merket noe, og fant der to menn som sloss, en var av hans gruppe, og den annen av hans fiender. Den som var av hans gruppe, ropte på ham om hjelp mot den annen, som var av hans fiender. Og Moses gav ham et slag og gjorde det av med ham. Da sa han: «Dette er Satans verk! Han er en åpenbar fiende og villeder.»'),
(3268, 28, 16, 'Han fortsatte: «Herre, jeg har gjort en urett som rammer meg selv. Tilgi meg!» Og Han tilgav ham, for Han er den Ettergivende, den Nåderike.'),
(3269, 28, 17, 'Så sa han: «Herre, for den godhet Du har vist meg, vil jeg aldri være hjelper for syndere.»'),
(3270, 28, 18, 'Neste morgen var han i byen, engstelig og på vakt, men plutselig, der var mannen som i går hadde bedt om hjelp, og han anropte ham, men Moses svarte ham: «Du er visselig på ville veier.»'),
(3271, 28, 19, 'Og da han ville gå løs på den som var deres begges fiende, sa denne: «Moses, vil du slå meg i hjel, slik som du tok et liv i går? Du vil bare være en voldsmann i landet, du vil ikke være en som skaper fred og orden.»'),
(3272, 28, 20, 'Da kom en mann løpende fra den andre enden av byen og sa: «Moses, rådsmennene legger opp råd mot deg for å drepe deg, så dra bort! Jeg gir deg et godt råd.»'),
(3273, 28, 21, 'Så drog han bort, engstelig og på vakt. Han sa: «Herre, frels meg fra dette urettferdige folk.»'),
(3274, 28, 22, 'Da han tok retningen mot Midian, sa han: «Kanskje Herren vil lede meg på riktig vei.»'),
(3275, 28, 23, 'Da han kom til vanningsstedet i Midian, fant han en skare folk der, som vannet dyrene, og utenom dem to kvinner, som holdt sine dyr tilbake, og han sa: «Hva er i veien med dere?» De svarte: «Vi kan ikke gi våre dyr vann før hyrdene er ferdige og drar bort. Vår far er en meget gammel mann.»'),
(3276, 28, 24, 'Så vannet han dyrene for dem. Derpå gikk han hen i skyggen, og sa: «Herre, jeg trenger visselig det Du måtte sende meg av godt.»'),
(3277, 28, 25, 'En av de to kvinnene kom blygt bort til ham og sa: «Min far kaller på deg, så han kan gi deg lønn for vanningen for oss.» Da han så kom til ham og hadde fortalt sin historie, sa han: «Frykt ikke! Du har unnsluppet dette urettferdige folk.»'),
(3278, 28, 26, 'Da sa en av de to kvinnene: «Far, ansett ham! Den beste du kan ansette er en som er sterk og pålitelig.»'),
(3279, 28, 27, 'Han sa: «Jeg vil gifte deg med en av disse mine to døtre på betingelse av at du tar åtte års tjeneste. Vil du fullføre ti år, så er det din avgjørelse. Jeg vil ikke presse deg. Du vil finne meg rettskaffen, så sant Gud vil.»'),
(3280, 28, 28, 'Da sa Moses: «La det være slik mellom oss! Og hvilken av de to frister jeg måtte oppfylle, så skal det ikke innebære noen bebreidelse mot meg. Gud vil holde oppsyn med det vi avtaler.»'),
(3281, 28, 29, 'Da Moses hadde fullført fristen, og reiste av gårde med sine, bemerket han en ild på fjellets side, og han sa til sine: «Bli her! Jeg har bemerket en ild. Kanskje jeg kan bringe dere underretning om den, eller en brann fra ilden, så dere kan varme dere.»'),
(3282, 28, 30, 'Da han nådde frem til den, ble han anropt av en stemme fra høyre side av dalen, fra et tre på en velsignet jordflekk: «Moses, Jeg er Gud, all verdens Herre!'),
(3283, 28, 31, 'Kast staven din!» Da han så den bevege seg som en slange, vendte han seg og flyktet, uten å snu seg tilbake. «Moses, kom hit, og vær ikke redd! Du er i sikkerhet.'),
(3284, 28, 32, 'Stikk hånden inn på brystet, og den vil komme ut hvit, men uskadd. Ta deg sammen mot frykten! Dette skal være to bevis fra Herren til Farao og hans råd. De er i sannhet ugudelige mennesker.»'),
(3285, 28, 33, 'Han svarte: «Herre, jeg har slått i hjel en av dem, og jeg er redd for at de vil drepe meg.'),
(3286, 28, 34, 'Og min bror Aron er mer veltalende enn meg. Send ham sammen med meg som hjelper, så han kan bekrefte at jeg taler sant, for jeg er redd for at de vil kalle meg løgner.»'),
(3287, 28, 35, 'Han sa: «Vi vil styrke deg ved din bror, og Vi vil gi dere makt så de ikke kan komme dere til livs. Ved Våre tegn skal dere og de som følger dere seire.»'),
(3288, 28, 36, 'Da så Moses kom til dem med Våre tegn som klar beskjed, sa de: «Dette er bare oppdiktet magi! Vi har aldri hørt om dette blant våre forfedre.»'),
(3289, 28, 37, 'Men Moses sa: «Herren kjenner vel til hvem som bringer ledelsen fra Ham, og hvem som oppnår den endelige bolig. De onde går det ikke godt.»'),
(3290, 28, 38, 'Farao sa: «Hør mitt råd, jeg kjenner ikke til noen annen gud for dere enn meg selv. Haman, brenn teglstein og bygg meg et tårn, så jeg kan stige opp til Mose Gud! For jeg tror han farer med løgn.»'),
(3291, 28, 39, 'Han og hans hærskarer viste uberettiget hovmod på jorden, og trodde at de ikke ville bli brakt tilbake til Oss.'),
(3292, 28, 40, 'Så tok Vi ham og hans hærskarer fatt, og kastet dem i havet. Se, hva enden ble for dem som handler ondt!'),
(3293, 28, 41, 'Vi gjorde dem til eksempler på slike som kaller folk til Ilden. På oppstandelsens dag finner de ingen hjelp.'),
(3294, 28, 42, 'Vi lot forbannelse følge dem i denne verden, og på oppstandelsens dag vil de være avskydd.'),
(3295, 28, 43, 'Vi gav Moses skriften etter at Vi hadde tilintetgjort de tidligere slektledd, til innsikt for menneskene, og som ledelse og nåde, så de måtte tenke etter.'),
(3296, 28, 44, 'Du stod ikke på den vestre dalside da Vi gav befalingen til Moses. Du var ikke blant vitnene.'),
(3297, 28, 45, 'Vi har latt slektledd komme, og deres liv varte lenge. Du bodde heller ikke blant folket i Midian for å fremlese Vårt ord for dem. Men Vi sendte sendebud!'),
(3298, 28, 46, 'Du var ikke ved fjellsiden, den gang Vi anropte, men ved en Herrens nåde skal du advare et folk som ingen advarer er kommet til før deg, så de måtte komme til ettertanke.'),
(3299, 28, 47, 'Og for at de ikke kan si om en hjemsøkelse rammer dem for det de har gjort: «Herre, hvorfor sendte Du ikke et sendebud til oss, så vi kunne følge Ditt ord og bli blant de troende?»'),
(3300, 28, 48, 'Men da sannheten kom til dem fra Oss, sa de: «Hvorfor har han ikke fått noe slikt som Moses fikk?» Men har de da ikke fornektet det som Moses fikk tidligere? De sier: «Magi begge deler, det ene støtter det andre,» og fortsetter: «Vi avviser begge!»'),
(3301, 28, 49, 'Si: «Så bring en Bok fra Gud, som gir bedre ledelse enn disse, så jeg kan følge den, om dere snakker sant.»'),
(3302, 28, 50, 'Og hvis de ikke vil høre på deg, så vit, de følger bare sine egne ideer. Og hvem er mer på villstrå enn den som følger egne ideer, uten Guds ledelse? Gud leder ikke urettferdige folk.'),
(3303, 28, 51, 'Vi har latt ordet (Koranen) bli dem til del, så de måtte tenke etter.'),
(3304, 28, 52, 'De som Vi gav skriften tidligere tror på den,'),
(3305, 28, 53, 'og når den resiteres for dem, sier de: «Vi tror på det! Det er sannhet fra vår Herre. Vi har for lengst gitt oss Gud i vold.»'),
(3306, 28, 54, 'Disse skal få sin lønn dobbelt opp, fordi de var standhaftige, avviser ondt med godt, og gir av det som Vi har gitt dem.'),
(3307, 28, 55, 'Når de hører tomt prat, vender de seg bort fra det, og sier: «Vi har våre gjerninger, og dere har deres gjerninger. Fred være med dere! Vi setter ikke pris på uvitenhet.»'),
(3308, 28, 56, 'Du kan ikke veilede dem du gjerne vil, men Gud leder dem Han vil. Han vet godt hvem som lar seg lede.'),
(3309, 28, 57, 'Folk sier: «Skulle vi slutte oss til ledelsen sammen med deg, ville vi bli fjernet fra vårt land.» Har Vi ikke bosatt dem fredlyst og sikkert, og det bringes all slags frukt, som underhold fra Oss? Men de fleste av dem vet det ikke.'),
(3310, 28, 58, 'Hvor mang en by har Vi ikke ødelagt, i ryggesløs velstand? Dette er deres boplasser, ubebodd etter dem, unntatt noen få. Det er Vi som er arvtagere.'),
(3311, 28, 59, 'Men Herren ødela ingen byer før Han hadde sendt et sendebud til deres hovedby, som fremleste for dem Vårt ord. Vi ville ikke ødelagt byene, hvis ikke deres innbyggere var urettferdige.'),
(3312, 28, 60, 'Alt dere har fått er jordelivets glede og pryd. Men det som er hos Gud er bedre og mer varig. Vil dere da ikke forstå?'),
(3313, 28, 61, 'Er vel den som Vi har gitt et vakkert løfte, og som får det oppfylt, lik den som Vi har gitt jordelivets gleder, og så, på oppstandelsens dag, må stå til rette?'),
(3314, 28, 62, 'På denne dag kaller Han på dem, og sier: «Hvor er nå Mine medguder som dere snakket om?»'),
(3315, 28, 63, 'Medgudene som ordet inntreffer overfor, vil si: «Herre, disse som vi har ført vill, har vi ført vill så som vi selv fór vill. Vi erklærer oss uskyldige overfor Deg! De tilbad ikke oss.»'),
(3316, 28, 64, 'Det vil bli sagt: «Kall på deres medguder!» Og de vil kalle på dem, men de svarer dem ikke. Og de får se straffen! Hadde de bare vært på rett vei!'),
(3317, 28, 65, 'På denne dag kaller Han på dem, og sier: «Hvilket svar gav dere sendebudene?»'),
(3318, 28, 66, 'På denne dag forstår de ingenting, og de samtaler ikke.'),
(3319, 28, 67, 'Hva den angår, som omvender seg og tror og lever rettskaffent, ham vil det gå godt!'),
(3320, 28, 68, 'Gud skaper og velger det Han vil. De har intet valg. Ære være Gud! Høyt hevet er Han over det de setter ved Hans side.'),
(3321, 28, 69, 'Herren vet hva deres hjerter skjuler, og hva de bærer åpent frem.'),
(3322, 28, 70, 'Han er Gud! Det er ingen gud unntatt Ham! Han være lovet, først og sist, Ham tilhører dommen, og til Ham bringes dere tilbake.'),
(3323, 28, 71, 'Si: «Hva mener dere? Om Gud skulle gjøre natten varig over dere til oppstandelsens dag, hvilken gud utenom Gud kunne gi dere lys? Vil dere da ikke høre?»'),
(3324, 28, 72, 'Si: «Hva mener dere? Om Gud skulle gjøre dagen varig over dere til oppstandelsens dag, hvilken gud utenom Gud kunne gi dere natt til å hvile i? Vil dere da ikke se?»'),
(3325, 28, 73, 'I Sin nåde har Han gitt dere både natt og dag, så dere kan hvile, og søke Hans gode gaver, så dere måtte vise takknemlighet.'),
(3326, 28, 74, 'Den dag Han kaller på dem, vil Han si: «Hvor er Mine medguder som dere snakket om?»'),
(3327, 28, 75, 'Og Vi vil hente et vitne fra hvert folk, og si: «Kom med deres bevis!» Da vil de erkjenne at sannheten er Guds. Og det de oppdiktet forsvinner bort fra dem.'),
(3328, 28, 76, 'Kora hørte til Mose folk. Han undertrykket folket, Vi gav ham så store skatter at bare nøklene til dem var i tyngste laget for en flokk sterke menn. Hans folk sa til ham: «Vær ikke så kry! Gud liker ikke kry folk.'),
(3329, 28, 77, 'Men strev, midt i alt som Gud har gitt deg, etter den evige bolig. Dog, glem ikke din andel i denne verden, og gjør godt, som Gud har gjort godt mot deg. Søk ikke å stifte ufred på jorden. Gud holder ikke av dem som stifter ufred og elendighet.»'),
(3330, 28, 78, 'Han svarte: «Jeg har fått det kun for min kunnskaps skyld.» Visste han da ikke at Gud har tilintetgjort før hans tid generasjoner, som hadde større makt enn ham, og større rikdommer? Synderne blir ikke spurt om sine synder.'),
(3331, 28, 79, 'Så trådte han ut til sitt folk i sin pryd. De som begjærte jordelivet, sa: «Hadde vi bare hatt like meget som Kora har fått. Han har sannelig stort hell med seg.»'),
(3332, 28, 80, 'Men de som hadde fått visdom, sa: «Ve dere! Guds lønn er bedre for den som tror og handler rett! Men bare de standhaftige mottar den.»'),
(3333, 28, 81, 'Så lot Vi jorden sluke ham og hans bolig, og han hadde ingen folk som hjalp ham istedenfor Gud, og han fant ikke hjelp.'),
(3334, 28, 82, 'Om morgenen sa de som hadde ønsket å være i hans sted i går: «Å, det ser ut som om Gud strør ut eller begrenser Sine gaver til hvem Han vil av sine tjenere! Om Gud ikke hadde vist oss godhet, hadde Han også latt oss oppsluke. Det ser ut som det ikke går de vantro godt.»'),
(3335, 28, 83, 'Det evige hjem! Vi gir det til dem som ikke står etter storhet på jorden, eller ufred. Det gode resultat er for de gudfryktige.'),
(3336, 28, 84, 'Den som kommer med en god gjerning, skal ha det som bedre er. Den som kommer med en ond gjerning, vel, de som handler ondt, belønnes bare for det de har gjort.'),
(3337, 28, 85, 'Han som har gitt deg i oppdrag Koranen, vil visselig bringe deg hjem. Si: «Herren vet godt hvem som bringer ledelse, og hvem som er i klar villfarelse.»'),
(3338, 28, 86, 'Du hadde ikke håp om at skriften skulle betros deg, men det skjedde som en nåde fra Herren. Så vær ingen hjelper for de vantro!'),
(3339, 28, 87, 'La dem ikke holde deg borte fra Guds ord etter at det er åpenbart for deg, men kall menneskene til Herren, og vær ikke du en avgudsdyrker!'),
(3340, 28, 88, 'Påkall ingen annen gud ved Guds side! Det er ingen gud utenom Ham. Alle ting forgår, unntatt Hans åsyn. Hans er dommen, og til Ham bringes dere tilbake!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'Alif Lam Mim'),
(3342, 29, 2, 'Tror menneskene at de blir latt i fred ved at de sier: «Vi tror,» og at de ikke vil bli satt på prøve?'),
(3343, 29, 3, 'Vi satte på prøve dem som levde før. Gud vil visselig vite om de sannferdige og de løgnaktige.'),
(3344, 29, 4, 'Eller tror de som gjør onde gjerninger at de kan løpe fra Oss? Dårlig er deres dømmekraft!'),
(3345, 29, 5, 'Den som håper å møte Gud, se, Guds tid kommer, og Han er den Hørende, den Allvitende.'),
(3346, 29, 6, 'Den som strever, strever for seg selv. For Gud er uavhengig av denne verden.'),
(3347, 29, 7, 'De som tror og handler vel, sannelig, for Dem vil Vi utviske Deres misgjerninger og belønne dem for det beste de har gjort.'),
(3348, 29, 8, 'Vi har pålagt mennesket å vise godhet mot sine foreldre. Men hvis de prøver å få deg til å sette slikt ved Min side som du intet vet om, så adlyd dem ikke. Til Meg går hjemreisen, og Jeg skal gjøre dere kjent med hva dere gjorde.'),
(3349, 29, 9, 'De som tror og handler vel, Vi vil føre dem inn blant de rettferdige.'),
(3350, 29, 10, 'Blant folk er slike som sier: «Vi tror på Gud,» men blir de påført fortred for Guds sak, så anser de menneskers forfølgelse for Guds straffedom. Kommer så hjelp (til seier) fra Herren, da vil de si: «Vi var med dere.» Er ikke Gud den som vet best hva som bor i all verdens hjerter?'),
(3351, 29, 11, 'Gud kjenner de troende, og Han kjenner også hyklerne.'),
(3352, 29, 12, 'De som er vantro, sier til dem som tror: «Følg vår vei, og vi skal bære deres overtredelser.» Men de kan ikke bære det ringeste av deres overtredelser, de er løgnere.'),
(3353, 29, 13, 'Men sine egne byrder skal de bære, i godt monn. På oppstandelsens dag skal de trekkes til ansvar for det de har oppdiktet.'),
(3354, 29, 14, 'Vi sendte Noa til hans folk, og han var blant dem 950 år. Så tok syndfloden dem, idet de var urettferdige.'),
(3355, 29, 15, 'Men Vi reddet ham og de som var i arken, og gjorde dem til et tegn for all verden.'),
(3356, 29, 16, 'Og Abraham, da han sa til sitt folk: «Tjen Gud og frykt Ham. Dette er bedre for dere, om dere bare visste.'),
(3357, 29, 17, 'Istedenfor Gud tilber dere avgudsbilder, og skaper løgn. Det dere tilber istedenfor Gud, makter ikke å skaffe dere det dere trenger. Søk det dere trenger hos Gud, tilbe Ham, og vær takknemlige mot Ham. Til Ham skal dere bringes tilbake.'),
(3358, 29, 18, 'Om dere holder dette for løgn, så har folk før dere gjort det samme. Sendebudet påligger det bare å overbringe budskapet klart.»'),
(3359, 29, 19, 'Ser de ikke hvordan Gud frembringer alt skapt, og så gjør det på nytt. Dette er en lett sak for Gud.'),
(3360, 29, 20, 'Si: «Reis rundt på jorden og se hvordan Han har frembrakt skapningen. Så vil Gud la fremstå det siste verk. Gud evner alt!'),
(3361, 29, 21, 'Han straffer dem Han bestemmer, og viser barmhjertighet mot dem Han bestemmer, til Ham vil dere vende om,'),
(3362, 29, 22, 'og dere kan intet forpurre, verken i himmel eller på jord. Dere har ingen til å ta seg av deres sak og til å hjelpe utenom Gud.'),
(3363, 29, 23, 'De som fornekter Guds ord og møtet med Ham, de vil fortvile for Min nåde. Dem venter det en smertelig straff.»'),
(3364, 29, 24, 'Men hans folk hadde intet annet svar enn at de sa: «Drep ham eller brenn ham!» Så reddet Gud ham fra ilden. I dette er jærtegn for folk som tror.'),
(3365, 29, 25, 'Og han sa: «Dere har laget avgudene som et tegn på samhørighet under jordelivet, men på oppstandelsens dag vil noen av dere fornekte og forbanne andre. Deres herberge blir Ilden, og dere har ingen hjelpere.»'),
(3366, 29, 26, 'Men Lot trodde på ham, og sa: «Jeg søker tilflukt hos min Herre, Han er den Mektige, den Vise.»'),
(3367, 29, 27, 'Så gav Vi ham Isak og Jakob, og la profetiens gave og skriften blant hans etterkommere. Vi gav ham hans lønn på jorden, og i det hinsidige vil han være blant de rettferdige.'),
(3368, 29, 28, 'Og Lot, en gang sa han til sitt folk: «Dere gjør en skjendighet som ingen før dere på jorden har drevet det til.'),
(3369, 29, 29, 'Dere går til menn, driver landeveisrøveri, gjør ondt på rådsmøtene!» Men hans folk hadde intet annet svar enn at de sa: «Så send over oss Guds straff, om det du sier er sant!»'),
(3370, 29, 30, 'Han sa: «Herre, hjelp meg mot dette folk som lager ufred og elendighet.»'),
(3371, 29, 31, 'Da Våre budbringere kom til Abraham med det gledelige budskap, sa de: «Vi skal tilintetgjøre folket i denne byen, for de er illgjerningsmenn.»'),
(3372, 29, 32, 'Han svarte: «Men Lot bor der!» Og de sa: «Vi vet vel hvem som bor der, vi skal redde ham og hans hus, unntatt hans hustru. Hun er av dem som blir tilbake.»'),
(3373, 29, 33, 'Da Våre budbringere kom til Lot, følte han seg fortvilet og maktesløs for deres skyld, men de sa: «Frykt ikke, og vær ikke bedrøvet. Vi skal redde deg og ditt hus, unntatt din hustru. Hun er av dem som blir tilbake.'),
(3374, 29, 34, 'Vi skal sende over folket i denne by en straffedom fra himmelen, for deres ugudelighet.»'),
(3375, 29, 35, 'Og Vi har etterlatt den som et klart tegn, for folk som forstår.'),
(3376, 29, 36, 'Til Midian sendte Vi deres bror Shoaib, og han sa: «Mitt folk, tjen Gud, imøtese dommens dag, og driv det ikke til ufred og elendighet på jorden.»'),
(3377, 29, 37, 'Men de holdt ham for løgner. Så tok jordskjelvet dem, og om morgenen lå de utstrakte på sine boplasser.'),
(3378, 29, 38, 'Eller Ad og Thamod? Dere ser det klart fra deres boplasser. Satan lot deres gjerninger fremtre prydelige, og holdt dem borte fra veien, enda de hadde skarpt syn.'),
(3379, 29, 39, 'Eller Kora og Farao og Haman. Moses kom til dem med klar beskjed, men de var hovmodige på jorden og kom ikke unna oss.'),
(3380, 29, 40, 'Enhver tok Vi fatt for hans synder. Over noen sendte Vi en storm av småstein, andre tok braket, noen ble oppslukt av jorden og noen lot Vi drukne. Gud gjør ikke urett mot noen, men deres egen urett vendte seg mot dem selv.'),
(3381, 29, 41, 'De som har funnet seg beskyttere utenom Gud, ligner edderkoppen, som har bygget seg hus. Edderkoppens hus er i sannhet det skrøpeligste av alle hus, om de bare forstod!'),
(3382, 29, 42, 'Gud vet hva dere påkaller utenom Ham. Han er den Mektige, den Vise.'),
(3383, 29, 43, 'Disse lignelser har Vi satt frem for folk, men bare de som har kunnskap, forstår dem.'),
(3384, 29, 44, 'Gud har skapt himlene og jorden på alvor. I dette er tegn for dem som tror.'),
(3385, 29, 45, 'Les frem det som er blitt åpenbart deg av skriften, og utfør bønnen! Bønnen holder skjendighet og ondskap borte. Å komme Gud i hu er det største. Og Gud vet hva dere foretar dere.'),
(3386, 29, 46, 'Diskuter ikke med skriftfolket annet enn på beste måte, unntatt med de urettferdige blant dem, og si: «Vi tror på det som er åpenbart for oss, og det som er åpenbart for dere. Vår Gud og deres Gud er én, og til Ham har vi gitt oss hen.»'),
(3387, 29, 47, 'Således har Vi åpenbart deg skriften. De som Vi før har gitt skriften, tror på den. Av disse finnes dem som tror på den nye åpenbaringen. Fornekter Vårt ord gjør bare vantro.'),
(3388, 29, 48, 'Før dette fremleste du ingen skrift, og skrev ingen med din hånd. I så fall kunne de som hevder falskhet, sannelig tvilt.'),
(3389, 29, 49, 'Nei, dette er klare ord i deres bryst som er gitt viten. Bare de som gjør urett, bestrider Vårt ord, og sier:'),
(3390, 29, 50, '«Hvorfor er ingen tegn sendt ham fra hans Herre?» Si: «Tegnene er i Guds hånd, jeg er bare en klar advarer.»'),
(3391, 29, 51, 'Er det ikke nok for dem at Vi har åpenbart skriften for deg, så den kan fremleses for dem? I dette er sannelig nåde og formaning for folk som tror.'),
(3392, 29, 52, 'Si: «Gud er nok som vitne mellom meg og dere.» Han vet hva som er i himlene og på jord. De som tror på tomhet og fornekter Gud, disse er taperne.'),
(3393, 29, 53, 'De ber deg påskynde straffen. Hadde det ikke vært en fastsatt tid, så ville straffen kommet over dem. Men den vil komme plutselig over dem, uten at de aner.'),
(3394, 29, 54, 'De ber deg påskynde straffen. Helvete vil omslutte de vantro.'),
(3395, 29, 55, 'Den dag straffen dekker dem ovenfra og undenfra, og Han vil si: «Smak nå hva dere har gjort!»'),
(3396, 29, 56, 'Mine tjenere som tror, Min verden er stor! Så tjen Meg!'),
(3397, 29, 57, 'Hvert menneske skal smake døden! Så bringes dere tilbake til Meg!'),
(3398, 29, 58, 'De som tror og handler vel, dem vil Vi huse i saler i paradisets have, hvor bekker sildrer, og her skal de være og bli, en herlig lønn for dem som strever,'),
(3399, 29, 59, 'som er standhaftige, og setter sin lit til Gud.'),
(3400, 29, 60, 'Hvor mange dyr er det ikke som ikke fører med seg det de trenger? Gud underholder dem, og dere. Han er den Hørende, den Allvitende!'),
(3401, 29, 61, 'Hvis du spør dem: «Hvem har skapt himlene og jorden, og satt sol og måne i tjeneste?» Da vil de nok si: «Gud.» Men hvordan kan de da være så forvridde?'),
(3402, 29, 62, 'Gud gir rikelig eller tilmålt underhold til dem Han vil av Sine tjenere. Gud vet om alle ting.'),
(3403, 29, 63, 'Om du spør dem: «Hvem sender regn fra oven, og dermed gir jorden liv etter dvalen?» Da vil de nok si: «Gud.» Si: «Lovet være Gud!» Men de fleste fatter ikke.'),
(3404, 29, 64, 'Dette jordelivet er tidsfordriv og lek, men det hinsidige hjem er livet! Om de bare visste!'),
(3405, 29, 65, 'Når folk går om bord i skip, ber de til Gud i religiøs oppriktighet. Men når Han har reddet dem til land, setter de andre ved Hans side,'),
(3406, 29, 66, 'i utakk for hva Vi har gitt dem, og for å leve glade dager. Men de vil få å vite!'),
(3407, 29, 67, 'Ser de da ikke at Vi har gitt dem en fredlyst helligdom, mens folk rykkes bort omkring dem? Skulle de da tro på tomhet, og fornekte Guds godhet!'),
(3408, 29, 68, 'Hvem gjør større urett enn den som dikter opp løgn mot Gud, eller fornekter sannheten når den er kommet til ham? Er det ikke i helvete herberge for de vantro?'),
(3409, 29, 69, 'Men de som strever for Vår sak, dem vil Vi lede på Våre veier! Gud er med dem som gjør det gode.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'Alif Lam Mim'),
(3411, 30, 2, 'Østromerne har lidd nederlag'),
(3412, 30, 3, 'i nabolandet, men etter sitt nederlag vil de seire'),
(3413, 30, 4, 'om få år, – saken er i Guds hånd, før og etter, og på den dag vil de troende glede seg –,'),
(3414, 30, 5, 'seire ved Guds hjelp. Han gir hjelp til den Han vil. Han er den Mektige, den Nåderike.'),
(3415, 30, 6, 'Guds løfte! Gud bryter ikke sitt løfte. Men folk flest vet ikke.'),
(3416, 30, 7, 'De kjenner bare de ytre sider av jordelivet, men overser det hinsidige.'),
(3417, 30, 8, 'Reflekterer de ikke i sitt indre? Gud har skapt himlene og jorden og alt som mellom dem er, på alvor og for en fastsatt frist. Men mange mennesker fornekter møtet med sin Herre.'),
(3418, 30, 9, 'Har de ikke reist rundt på jorden og sett hva enden ble for dem som levde før? De var mektigere, og dyrket og bebygget jorden bedre enn de selv. Deres sendebud kom til dem med klar beskjed! Gud var ikke den som gjorde dem urett, mens deres egen urett vendte seg mot dem selv.'),
(3419, 30, 10, 'De som gjorde ondt, fikk en ond ende, for de kalte Guds ord for løgn og drev ap med det,'),
(3420, 30, 11, 'Gud frembringer all skapningen, og Han gjør det på nytt. Så vil dere bringes tilbake til Ham.'),
(3421, 30, 12, 'Den dag timen kommer, vil synderne fortvile.'),
(3422, 30, 13, 'De har ingen av sine medguder til å legge inn ord for seg, og de vil forkaste sine medguder.'),
(3423, 30, 14, 'Den dag timen kommer, blir menneskene delt.'),
(3424, 30, 15, 'De som trodde og handlet rett, vandrer i glede i paradisets enger.'),
(3425, 30, 16, 'Men de som var vantro og fornektet Vårt ord, og møtet i det kommende liv, de må møte til straffen.'),
(3426, 30, 17, 'Gud være lovet, når dere går inn i kvelden, og når dere møter morgenen.'),
(3427, 30, 18, 'Ham tilhører lovprisningen i himlene og på jord, i kveldingen og i middagstimen.'),
(3428, 30, 19, 'Han frembringer det levende av det døde og dødt av liv, og Han gir jorden liv etter dens dvale. På samme måte skal dere bli brakt frem.'),
(3429, 30, 20, 'Blant Hans tegn er at Han skapte dere av støv, og se, dere ble mennesker, spredt omkring.'),
(3430, 30, 21, 'Blant Hans tegn er også at Han skapte for dere hustruer av deres egen sort, for at dere kan finne ro hos dem. Og Han har lagt kjærlighet og godhet mellom dere. I dette er jærtegn for mennesker som tenker etter.'),
(3431, 30, 22, 'Blant Hans tegn er skapelsen av himlene og jorden, og forskjellen i tungemål og farge. I dette er jærtegn for dem som vet.'),
(3432, 30, 23, 'Til Hans tegn hører søvnen, om natten og om dagen, og deres bestrebelser for å oppnå noe av Hans goder. I dette er jærtegn for dem som hører.'),
(3433, 30, 24, 'Blant Hans tegn er lynet, med redsel og med håp, og at Han sender vann fra oven og gir jorden liv etter dens dvale. I dette er jærtegn for folk som forstår.'),
(3434, 30, 25, 'Blant Hans tegn er at himmel og jord består på Hans bud. I sin tid, når Hans røst kaller dere ut av jorden, se, dere vil komme frem.'),
(3435, 30, 26, 'Ham tilhører alle i himlene og på jord, alle er Ham underdanig.'),
(3436, 30, 27, 'Gud frembringer all skapningen, og Han gjør det på nytt. Dette er lett for Ham. Han er det høyeste som tenkes kan, i himlene og på jord. Han er den Mektige, den Vise.'),
(3437, 30, 28, 'Han fremsetter en lignelse for dere fra deres egne forhold. Er det vel så at dere slaver er kompanjonger i det Vi lar dere få? Så dere er likestilte i så henseende? Og respekterer dem som dere respekterer hverandre? Således forklarer Vi ordet for folk som forstår!'),
(3438, 30, 29, 'Nei, de urettferdige følger sine egne ideer, uten kunnskap. Hvem kan vel lede dem som Gud lar seile sin egen sjø? De har ingen hjelpere.'),
(3439, 30, 30, 'Vend deg mot religionen som Gud-søker, ved den Guds intuisjon som Han har utstyrt menneskene med. Det er ingen avvik i det Gud har skapt. Dette er rett religion, men folk flest vet det ikke.'),
(3440, 30, 31, 'Vend dere til Ham, frykt Ham, overhold bønnen, og vær ikke av dem som setter medguder ved Hans side,'),
(3441, 30, 32, 'av dem som splitter sin religion og blir sekter, hvor hver gruppe fryder seg over sitt.'),
(3442, 30, 33, 'Hvis nød rammer menneskene, påkaller de Herren i omvendelse, men så når Han lar dem smake sin barmhjertighet, vil noen av dem gi Ham medguder,'),
(3443, 30, 34, 'i utakknemlighet for det Vi har gitt dem: «Lev deres glade dager. Dere skal få å vite!»'),
(3444, 30, 35, 'Eller har Vi sendt dem autorisasjon som omtaler det de setter ved Hans side?'),
(3445, 30, 36, 'Viser Vi menneskene godhet, så fryder de seg over det. Men hvis ondt rammer dem for det de selv har stelt i stand, så er de fortvilede.'),
(3446, 30, 37, 'Har de ikke sett at Gud gir sine gaver, rikelig eller tilmålt, til den Han vil? I dette er jærtegn for folk som tror.'),
(3447, 30, 38, 'Så gi din slektning det som tilkommer ham, og den fattige og den veifarende. Dette er best for dem som vil oppnå Guds bevåkenhet. Dem vil det gå godt!'),
(3448, 30, 39, 'Det dere gir ut av utbyttingslån for forøkelse på folks bekostning, det gir ingen forøkelse hos Gud. Men det dere gir i gaver for å oppnå Guds bevåkenhet, gir mangedoblet verdi for giverne.'),
(3449, 30, 40, 'Gud er det som har skapt dere og underholder dere. Så vil Han la dere dø, og derpå gi dere nytt liv. Er det noen av dere medguder som kan gjøre slike ting?'),
(3450, 30, 41, 'Fordervelse og ødeleggelse er å se på land og hav, ved det menneskene ved sine henders verk har oppnådd, så Han kan gi dem å smake litt av det de har stelt i stand, så de må vende om!'),
(3451, 30, 42, 'Si: «Reis omkring på jorden og se hva enden ble for dem som levde før!» De fleste av dem var avgudsdyrkere!'),
(3452, 30, 43, 'Vend ditt ansikt mot den rette religion før det kommer en dag fra Gud som ikke kan avverges. Denne dag vil menneskeheten bli delt.'),
(3453, 30, 44, 'Den som ikke tror, blir belastet sin vantro. Den som handler rett, legger til rette for seg selv,'),
(3454, 30, 45, 'så Han kan belønne dem som tror og handler rett av sin overflod. Han liker ikke vantro!'),
(3455, 30, 46, 'Blant Hans jærtegn er vindene, som Han sender med godt budskap, så Han vil la dere smake av Sin nåde, så skipene kan seile ved Hans bud, så dere kan strebe etter Hans gode, så dere må bli takknemlige.'),
(3456, 30, 47, 'Vi har sendt sendebud til deres folk før deg. De kom til dem med klar beskjed. Vi tok hevn over dem som syndet. Det påligger Oss å hjelpe de troende.'),
(3457, 30, 48, 'Gud sender vindene som driver opp skyer. Så sprer Han dem på himmelen, slik Han vil. Så lar Han dem tetne, og du ser regnet strømme fra dem. Han lar regnet falle på hvem Han vil av Sine tjenere,'),
(3458, 30, 49, 'og de fryder seg, skjønt før det ble sendt ned til dem, var de i fortvilelse.'),
(3459, 30, 50, 'Se Guds nådes spor, hvordan Han gir jorden liv etter dens dvale. Han er også de dødes livgiver. Han har makt over alle ting.'),
(3460, 30, 51, 'Men om Vi sendte en vind, og de så alt gulne? Etter det ville de forbli vantro!'),
(3461, 30, 52, 'Du kan ikke få døde til å høre! Du kan heller ikke få døve til å høre kallet, når de snur ryggen til og går.'),
(3462, 30, 53, 'Du kan ikke lede blinde ut av deres villfarelse! Du kan ikke få noen til å høre, unntatt de som tror på Vårt ord, og gir seg inn under Guds vilje.'),
(3463, 30, 54, 'Gud er den som skaper dere i svakhet. Så, etter svakheten, gir Han styrke, og så, etter styrke, gir Han svakhet og hvite hår. Han skaper det Han vil. Han er den Allvitende, den Allmektige.'),
(3464, 30, 55, 'Den dag, når timen kommer, vil synderne sverge at de ikke hadde mer enn en times tid på seg. Således var de på avveie.'),
(3465, 30, 56, 'Men de som ble gitt kunnskap og tro, vil si: «Dere har hatt tid på dere, etter Guds bok, til oppstandelsens dag. Og dette er oppstandelsens dag, men dette visste dere ikke.»'),
(3466, 30, 57, 'Denne dag vil deres utflukter ikke tjene dem som gjør urett, og de gis ingen anledning til å forbedre seg.'),
(3467, 30, 58, 'Vi har gitt all slags lignelser for menneskene i denne Koran. Men kommer du til dem med et ord, så vil de vantro si: «Dere står for intet annet enn det verdiløse.»'),
(3468, 30, 59, 'Slik forsegler Gud hjertene hos dem som intet forstår.'),
(3469, 30, 60, 'Vær standhaftig. Guds løfter er sannhet! La ikke dem som ikke har troens visshet, få deg til å vakle!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'Alif Lam Mim'),
(3471, 31, 2, 'Dette er den vise skriftens ord,'),
(3472, 31, 3, 'ledelse og nåde for dem som gjør godt,'),
(3473, 31, 4, 'som forretter bønnen, gir det rituelle bidrag og har visshets tro på det hinsidige.'),
(3474, 31, 5, 'Disse følger ledelsen fra Herren. Dem vil det gå godt.'),
(3475, 31, 6, 'Det finnes slike som byr frem underholdende historier, for å føre folk bort fra Guds veier i uvitenhet og for å drive ap med det. Dem venter en ydmykende straff.'),
(3476, 31, 7, 'Når Vårt ord fremleses for en slik, vender han seg bort i hovmod, som om han intet hadde hørt, som om hans ører var tunghørte. Bebud en smertelig straff for ham.'),
(3477, 31, 8, 'Dem som tror og handler rett, venter lykksalighetens haver.'),
(3478, 31, 9, 'Her skal de være og bli. Guds løfte, i sannhet! Han er den Mektige, den Vise.'),
(3479, 31, 10, 'Han har skapt himlene og det er ingen støttepilarer, som dere ser. På jorden har Han lagt faste fjell, så den ikke skal vakle med dere. Alle slags dyr har Han spredt ut på den. Vi sender regn fra oven, og frembringer på den alle slags herlige arter.'),
(3480, 31, 11, 'Dette er Guds skapelse. Vis meg hva de har skapt som er utenom Ham. Nei, de urettferdige er i åpenbar villfarelse.'),
(3481, 31, 12, 'Vi gav visdom til Loqman: «Vis takknemlighet mot Gud. Den som er takknemlig, viser takknemlighet kun til eget beste. Og er noen utakknemlig, så er Gud selvtilstrekkelig, verdig all pris.»'),
(3482, 31, 13, 'En gang sa Loqman til sin sønn, idet han formante ham: «Min sønn, sett intet ved Guds side. Flerguderi er en svær urett!»'),
(3483, 31, 14, 'Vi har gitt mennesket anvisninger vedrørende hans foreldre, hans mor har båret ham med møye etter møye, og to år tar hans avvenning: Vis takknemlighet mot Meg og mot dine foreldre. Hos Meg er reisen slutt.'),
(3484, 31, 15, 'Men hvis de ønsker at du skal sette ved Min side noe du ikke vet hva er, så adlyd dem ikke. Ta deg av dem på beste måte i denne verden, men følg deres vei, som vender seg mot Meg. Så skal dere vende tilbake til Meg.'),
(3485, 31, 16, '«Kjære sønn, om det er så lite som et sennepskorn, og var skjult i en klippe eller i himlene eller på jorden, så vil Gud bringe det frem. Gud kjenner alle detaljer, Han er vel underrettet.'),
(3486, 31, 17, 'Min sønn, overhold bønnen, påby det rette og forby det urette, og bær standhaftig det som rammer deg. Dette er sann fasthet.'),
(3487, 31, 18, 'Sett ikke nesen i været overfor din neste, og gå ikke hovmodig omkring på jorden. Gud liker ikke innbilske skrytepaver.'),
(3488, 31, 19, 'Vær høvisk i din gange og demp din stemme. Den verste av alle låter er eselets stemme.»'),
(3489, 31, 20, 'Dere har vel sett hvordan Gud har gjort tjenlig for dere alt som er i himlene og på jord, og har overøst dere med gaver, i det åpne og i det skjulte. Det finnes slike som diskuterer om Gud, uten kunnskap, ledelse og en opplysende Bok.'),
(3490, 31, 21, 'Hvis man sier til dem: «Følg det som Gud har åpenbart,» svarer de: «Nei, vi vil følge våre fedres tradisjoner.» Og det selv om Satan kaller dem til Ildens straff?'),
(3491, 31, 22, 'Den som gir seg inn under Gud, og handler vel, han har grepet det påliteligste feste. Hos Gud beror alle tings utgang.'),
(3492, 31, 23, 'Om noen er vantro, så la ikke hans vantro bedrøve deg. Til Oss skal de vende tilbake, og Vi skal fortelle dem hva de bedrev. Gud er vel vitende om det som bor i hjertene.'),
(3493, 31, 24, 'Vi skal la dem få gode dager en kort tid, så vil Vi ubønnhørlig sende dem til hard straff.'),
(3494, 31, 25, 'Hvis du spør dem hvem som skapte himlene og jorden, vil de nok svare: «Gud.» Si: «Gud være lovet.» Men de fleste av dem vet ingenting.'),
(3495, 31, 26, 'Gud tilhører alt som er i himlene og på jord. Gud er den Selvtilstrekkelige, den Lovpriste.'),
(3496, 31, 27, 'Om alle trær som på jorden er, var penner, om havet, og syv hav til i tillegg var blekk, ville ikke Guds ord være uttømt. Gud er mektig, vis.'),
(3497, 31, 28, 'Menneskenes skapelse og oppstandelse er som om det var ett enkelt individ. Gud hører, ser.'),
(3498, 31, 29, 'Du har vel sett at Gud lar natt bli til dag, og dag til natt, at Han har satt solen og månen i tjeneste, hver med sin bestemte tid? Gud er vel underrettet om hva dere gjør.'),
(3499, 31, 30, 'Dette fordi Gud er den Sanne, og det de påkaller utenom Ham, er falskhet og intethet, fordi Gud er den Opphøyde, den Store.'),
(3500, 31, 31, 'Du har vel sett skipene som seiler på havet ved Guds godhet, så Han kan vise dere av Sine jærtegn? I dette er sannelig et jærtegn for den standhaftige, takknemlige.'),
(3501, 31, 32, 'Og når bølgene skyller over dem som skyer, da ber de til Gud i troens oppriktighet mot Ham. Når Han så berger dem til land, blir noen mer lunkne. Fornekte Vårt tegn gjør bare utakknemlige svikere.'),
(3502, 31, 33, 'Dere mennesker, frykt deres Herre, og frykt for den dag da en far intet kan overta for sin sønn, eller en sønn det ringeste for sin far. Guds løfter er sannhet! La ikke jordelivet forlede dere. La ikke forføreren forlede dere når det gjelder Gud.'),
(3503, 31, 34, 'Gud alene kjenner timen. Han sender regnet ned. Han vet hva som er i mors liv. Ingen vet hva morgendagen vil bringe. Ingen vet i hvilket land han vil dø. Gud vet, er vel underrettet.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'Alif Lam Mim'),
(3505, 32, 2, 'Skriftens åpenbaring er fra all verdens Herre, tvil har ingen plass.'),
(3506, 32, 3, 'Mon de sier: «Han har oppdiktet den?» Nei, den er sann- heten fra din Herre, for at du skal advare et folk, som det ikke er kommet noen advarer til før deg, så de må følge ledelsen.'),
(3507, 32, 4, 'Gud er den som skapte himlene og jorden og alt som mellom dem er, på seks dager. Så tok Han plass på tronen. Uten Ham har dere ingen som tar seg av dere, og ingen som taler deres sak. Vil dere da ikke komme til ettertanke?'),
(3508, 32, 5, 'Han utsender Sitt styrende ord fra himmelen til jorden. Så stiger det opp til Ham på en dag, hvis mål er tusen år etter deres regning.'),
(3509, 32, 6, 'Slik er Han, som kjenner det skjulte og det åpenbare, den Mektige, den Nåderike,'),
(3510, 32, 7, 'som har gjort vel alt Han har skapt. I begynnelsen skapte Han mennesket av leire.'),
(3511, 32, 8, 'Så frembrakte Han dets avkom av en slump skrøpelig væske,'),
(3512, 32, 9, 'formet ham, og blåste Sin ånd i ham, og Han har gitt dere hørsel, syn og forstand. Lite takknemlighet viser dere.'),
(3513, 32, 10, 'De sier: «Når vi er blitt borte i jorden, skal vi da bli en ny skapning?» Ja, de fornekter møtet med Herren.'),
(3514, 32, 11, 'Si: «Dødsengelen, som er gitt ansvar for dere, vil ta dere bort, og så vil dere bli brakt tilbake til deres Herre.»'),
(3515, 32, 12, 'Om du kunne se synderne henge med hodet fremfor sin Herre: «Vår Herre, vi har sett og hørt. Send oss tilbake så vi kan leve rettskaffent. Vi har full visshet nå.»'),
(3516, 32, 13, 'Om Vi hadde bestemt slik, kunne Vi gitt hver enkelt ledelse. Men Mitt ord er nå blitt realitet: «Jeg vil fylle helvete med folk og dsjinner, om hverandre.'),
(3517, 32, 14, 'Dere skal nå få føle, fordi dere glemte denne deres dags møte! Vi har glemt dere! Ta nå straffen, som varer, for det dere bedrev!»'),
(3518, 32, 15, 'Kun de tror på Vårt ord som faller ned i ærefrykt når de påminnes om det, idet de priser sin Herre og er uten hovmod.'),
(3519, 32, 16, 'De skyr sitt leie idet de stående påkaller Herren i frykt og lengsel, og gir av det som Vi har gitt dem.'),
(3520, 32, 17, 'Men ingen vet hvilken lyst for øyet som venter dem i det skjulte, som belønning for deres gjerninger.'),
(3521, 32, 18, 'Er vel en troende lik en ugudelig? De er ikke like!'),
(3522, 32, 19, 'De som tror og handler vel, venter paradisets haver som oppholdssted. En velkomsthilsen for deres gjerninger.'),
(3523, 32, 20, 'De som er ugudelige, venter Ilden som oppholdssted. Hver gang de ønsker å komme ut av den, blir de sendt tilbake i den, og det blir sagt til dem: «Føl Ildens straff, som dere pleide å fornekte!»'),
(3524, 32, 21, 'Men Vi skal la dem føle den dennesidige straffen, før den store, så de må vende om!'),
(3525, 32, 22, 'Hvem er vel mer urettferdig enn den som påminnes om Herrens ord, og så vender seg bort fra det. Vi søker hevn over synderne!'),
(3526, 32, 23, 'Vi gav Moses skriften, så vær ikke i tvil om møtet med Ham, og Vi gjorde den til ledelse for Israels barn.'),
(3527, 32, 24, 'Vi innsatte ledere fra deres midte, som rettledet ved Vårt bud, da de var standhaftige og trodde fast på Vårt ord.'),
(3528, 32, 25, 'Herren vil treffe avgjørelser mellom dem på oppstandelsens dag vedrørende det de var uenige om.'),
(3529, 32, 26, 'Finner de ingen rettledning i hvor mange slekter Vi har utslettet før dem, på hvis boplasser de vandrer omkring? I dette er visselig tegn. Vil de da ikke høre?'),
(3530, 32, 27, 'Har de da ikke sett hvordan Vi fører vannet til det tørre land, og frembringer med det avlinger som deres fe og de selv kan spise av? Ser de da ikke?'),
(3531, 32, 28, 'De sier: «Når er så denne seierens dag, om dere snakker sant?»'),
(3532, 32, 29, 'Si: «På seierens dag vil det ikke gagne de vantro å tro, og de får ingen utsettelse!»'),
(3533, 32, 30, 'Så vend deg bort fra dem og vent. De venter også.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'Du profet, vis gudsfrykt, og hør ikke etter de vantro og hyklerne! Gud vet, er vis.'),
(3535, 33, 2, 'Rett deg etter det som Herren har åpenbart for deg! Gud er vel underrettet om hva dere gjør.'),
(3536, 33, 3, 'Forlat deg på Gud! Gud strekker til for å ta seg av din sak.'),
(3537, 33, 4, 'Gud gir ikke en mann to hjerter i brystet. Og Han har heller ikke gjort deres hustruer, som dere skiller dere fra ved skilsmisseformularet «du er meg likegyldig som min egen mors rygg», til deres rette mødre. Heller ikke har Han gjort deres adoptivsønner til deres rette sønner. Dette er bare deres uttrykksmåter. Men Gud taler sannheten, og Han viser veien.'),
(3538, 33, 5, 'Oppkall adoptivsønner etter deres fedre. Dette er rettest overfor Gud. Hvis dere ikke kjenner deres fedre, så brødre i religionen, eller klienter. Dere rammes ikke av skyld om dere feiler. Men det er spørsmål om hva dere tilsikter i deres hjerter. Gud er tilgivende, nåderik.'),
(3539, 33, 6, 'Profeten står de troende nærmere enn de selv innbyrdes, og hans hustruer er deres mødre. Slektninger står hverandre nærmere ifølge Guds bok enn de øvrige troende og de utvandrede fra Mekka, men dere skal gjøre som skikk og bruk tilsier overfor deres venner. Dette er nedtegnet i skriften.'),
(3540, 33, 7, 'En gang sluttet Vi en avtale med profetene, og med deg, Noa og Abraham, Moses og Jesus, Marias sønn. Vi sluttet en høytidelig pakt med dem,'),
(3541, 33, 8, 'slik at Han kan forhøre de sannferdige om deres vederheftighet. Og Han har gjort i stand en smertelig straff for de vantro.'),
(3542, 33, 9, 'Dere som tror, kom i hu Guds godhet mot dere da hærer rykket frem mot dere, og Vi sendte mot dem en vind, og hærskarer dere ikke så,'),
(3543, 33, 10, '– Gud ser det dere gjør, da de kom over dere ovenfra og nedenfra, da blikket flakket og dere hadde hjertet i halsen, og dere tenkte deres tanker om Gud.'),
(3544, 33, 11, 'Der ble de troende stilt på prøve, og de ble sterkt rystet.'),
(3545, 33, 12, 'Da hyklerne og de som har sykdom i sine hjerter sa: «Det Gud og Hans sendebud har lovet oss, er bare bedrag.»'),
(3546, 33, 13, 'En gruppe av dem sa: «Dere folk fra Yathrib-byen, her er ikke blivende sted for dere! Dra hjem!» Og en del av dem bad profeten om tillatelse, og sa: «Våre hus ligger åpne.» Men de lå ikke åpne, de ønsket bare å flykte.'),
(3547, 33, 14, 'Men hvis de var blitt trengt fra alle kanter, og så oppfordret til frafall fra troen, så ville de gått med på det, og tatt det lett med å forbli i den.'),
(3548, 33, 15, 'Dog hadde de tidligere lovet Gud å ikke snu ryggen til. Det vil bli spørsmål om løftet til Gud.'),
(3549, 33, 16, 'Si: «Flukt vil ikke nytte dere, om dere flykter fra døden eller fra å bli drept. Da blir det bare en kort nytelse av livet.»'),
(3550, 33, 17, 'Si: «Hvem kan beskytte dere mot Gud, om Han vil noe ondt for dere, eller Han vil gi dere nåde?» Utenom Gud finner de ingen som kan ta seg av deres sak, og ingen hjelper.'),
(3551, 33, 18, 'Gud kjenner dem blant dere som legger hindringer i veien, og som sier til sine brødre: «Kom hit til oss,» som viser liten kampvilje'),
(3552, 33, 19, 'og er gjerrige med dere. Når frykten kommer over dem, ser du dem se på deg med rullende øyne, som en som besvimer overfor nøden. Men når frykten fortar seg, så treffer de dere med skarpe tunger, gjerrige med det som godt er. Disse er ikke troende, så Gud gjør deres gjerninger til intet. Dette er en lett sak for Gud.'),
(3553, 33, 20, 'De tror at ligaen av motstandere ikke er dratt bort, og hvis ligaen skulle komme, ønsker de at de var blant beduinene, så de kunne forespørre hvordan det gikk med dere. Og om de var blant dere, ville de vise litt kampinnsats.'),
(3554, 33, 21, 'I Guds sendebud har dere hatt et godt eksempel, for den som setter sitt håp til Gud og den ytterste dag, og stadig kommer Gud i hu.'),
(3555, 33, 22, 'Da de troende så ligaen, sa de: «Dette er hva Gud og Hans sendebud har lovet oss. Gud og Hans sendebud har talt sant!» Og det bare styrket dem i tro og hengivenhet.'),
(3556, 33, 23, 'Blant de troende finnes menn som stod ved sin pakt med Gud. Noen av dem har fullført sitt løfte, andre avventer. Men de har ikke forandret seg det ringeste.'),
(3557, 33, 24, 'Så Gud kan belønne de sannferdige for deres vederheftighet, og straffe hyklerne, om Han vil, eller vende seg mot dem i nåde. Gud er tilgivende, nåderik.'),
(3558, 33, 25, 'Gud drev tilbake de vantro i deres raseri, uten at de vant noe. Og han sparte de troende for kamp. Gud er visselig sterk, mektig.'),
(3559, 33, 26, 'Og Han brakte ned fra deres borger de av skriftfolkene som hadde ytet fienden bistand, og Han kastet skrekk i deres hjerter. Noen drepte dere, noen tok dere til fange.'),
(3560, 33, 27, 'Og Han lot dere arve deres jord, boplasser og eiendom, jord som dere ikke hadde satt fot på. Gud evner alt.'),
(3561, 33, 28, 'Du profet, si til dine hustruer: «Om dere begjærer jordelivet og dets pryd, så bare kom, og jeg vil utruste dere og sette dere fri på en pen måte.'),
(3562, 33, 29, 'Men om deres hu står til Gud og Hans sendebud, og det hinsidige hjem, se, Gud har gjort rede for dem av dere som handler vel en herlig lønn.»'),
(3563, 33, 30, 'Dere profetens hustruer, den av dere som begår en åpenbar skjendighet, for henne fordobles straffen. Dette er lett for Gud.'),
(3564, 33, 31, 'Men den av dere som er Gud og Hans sendebud underdanig, og handler vel, vil Vi gi dobbelt lønn, og for henne har Vi gjort rede en ærefull omsorg.'),
(3565, 33, 32, 'Dere profetens hustruer, dere er ikke som andre kvinner. Om dere frykter Gud, så vær ikke så spake og ydmyke i deres tale at begjæret vekkes hos en i hvis hjerte det er sykdom. Tal som skikk og bruk er.'),
(3566, 33, 33, 'Hold dere hjemme. Stas dere ikke opp som i fordums hedensk tid! Forrett bønnen, gi det rituelle bidrag og adlyd Gud og Hans sendebud! Gud ønsker bare å holde urenhet borte fra dere, dere husets folk, og å rense dere helt.'),
(3567, 33, 34, 'Kom i hu det som fremleses i deres hus av Guds ord og visdom. Gud ser alle detaljer, er vel underrettet.'),
(3568, 33, 35, 'Menn og kvinner som har gitt seg Gud i vold, menn og kvinner som tror, viser fromhet, er sannferdige, standhaftige, ydmyke, gir almisser, holder fasten, som holder sitt kjønnsliv i tømme, som stadig kommer Gud i hu, for dem har Gud rede tilgivelse og en herlig lønn.'),
(3569, 33, 36, 'Det sømmer seg ikke for en troende mann eller kvinne, når Gud og Hans sendebud har truffet avgjørelse i en sak, å ha fritt valg i denne sak. Den som ikke adlyder Gud og Hans sendebud, er klart på vidvanke.'),
(3570, 33, 37, 'Da du sa til din adoptivsønn som Gud og du selv har vist godhet: «Behold din hustru hos deg, og frykt Gud.» Og du skjulte i deg selv det som Gud ville gjøre klart, idet du fryktet menneskene. Men Gud har større rett til at du frykter Ham, så da Zaid hadde gjort opp med henne, gav Vi henne deg til hustru, slik at det ikke skulle være noen anfektelse hos de troende vedrørende deres adoptivsønners hustruer, når de har gjort opp med dem. Guds ordning må følges!'),
(3571, 33, 38, 'Det skal ikke være noen anfektelse for profeten i det Gud har fastlagt for ham. Det var Guds skikk for de henfarne, og Guds ordning er avgjort sak,'),
(3572, 33, 39, 'for dem som fremførte Guds budskap og fryktet Ham, og fryktet ingen annen enn Gud. Gud strekker til som regnskapsmann.'),
(3573, 33, 40, 'Muhammad er ikke far for noen av dere, men Guds sendebud og profetenes segl. Gud kjenner til alle ting.'),
(3574, 33, 41, 'Dere som tror, kom Gud i hu med stadig ihukommelse,'),
(3575, 33, 42, 'og pris Ham morgen og aften.'),
(3576, 33, 43, 'Han er det som med sine engler velsigner dere, for å føre dere fra mørket til lyset. Han er nåderik mot de troende.'),
(3577, 33, 44, 'Deres hilsen den dag de møter Ham, skal være: «Fred!» Han har beredt for dem en herlig lønn.'),
(3578, 33, 45, 'Du profet, Vi har sendt deg med vitnemål, godt budskap og advarsler,'),
(3579, 33, 46, 'idet du kaller til Gud med Hans bifall, og som et flammende bluss!'),
(3580, 33, 47, 'Forkynn det glade budskap til de troende, så de har i vente stor godhet fra Gud!'),
(3581, 33, 48, 'Hør ikke etter de vantro og hyklerne! La deres fortred være, og forlat deg på Gud! Gud strekker til for å ta hånd om din sak.'),
(3582, 33, 49, 'Dere som tror, når dere ekter troende kvinner og så skiller dere fra dem før dere har rørt dem, behøver dere ikke å regne med ventetid for dem, men utstyr dem og la dem gå på en pen måte.'),
(3583, 33, 50, 'Profet, Vi har tillatt for deg og dine hustruer, som du har gitt deres brudegave, de slavinner du eier, krigsbytte som Gud har gitt deg, dine kusiner på fars og mors side som utvandret sammen med deg, og enhver troende kvinne som gir seg til profeten, om profeten ønsker å ta henne til ekte. Dette gjelder særskilt for deg, fremfor de troende. Vi vet vel hva Vi har pålagt dem med hensyn til hustruer og slavinner. Dette for at du ikke skal ha noen anfektelse. Gud er tilgivende, nåderik.'),
(3584, 33, 51, 'Du kan la stå i bero hvem du vil av dem, og ta til deg den du vil. Og om du ønsker noen du har adskilt fra deg, så rammes du ikke av skyld. Dette er nærmest til å gi dem oppmuntring, og at de ikke skal sørge, og at de alle skal være tilfreds med det du gir dem. Gud vet om det som bor i deres hjerter. Gud vet, er mild.'),
(3585, 33, 52, 'Heretter er ikke kvinner tillatt for deg, og heller ikke å bytte dem ut med andre, selv om deres skjønnhet behager deg. Dette gjelder ikke slavinner. Gud vokter over alt.'),
(3586, 33, 53, 'Dere som tror, gå ikke inn i profetens hus, med mindre dere er invitert til et måltid, og da ikke uten å avvente spisetid. Men når dere er innbudt, så kom. Når dere så har spist, så trekk dere tilbake, uten å hengi dere til selskapelig samtale. Dette sjenerer profeten, og han skammer seg for dere. Men Gud skammer seg ikke for sannheten. Når dere anmoder profetens hustruer om noe dere trenger, så gjør det fra bak et forheng. Dette er mest sømmelig for deres og kvinnenes hjerter. Dere må ikke besvære Guds sendebud, og ikke noen gang gifte dere med hans hustruer etter ham. Det ville være en svær ting i Guds øyne.'),
(3587, 33, 54, 'Om dere bærer noe åpent frem, eller skjuler det, så vet Gud visselig om alle ting.'),
(3588, 33, 55, 'Kvinnene rammes ikke av skyld med hensyn til å omgås fritt sine fedre, sønner, brødre, nevøer, og sine kvinner og slavinner. Men de skal frykte Gud! Gud bevitner alle ting!'),
(3589, 33, 56, 'Gud og Hans engler velsigner profeten. Dere som tror, nedkall også velsignelse over ham, og fred.'),
(3590, 33, 57, 'De som forulemper Gud og Hans sendebud, dem har Gud forbannet i denne verden og den hinsidige, og Han har gjort klar for dem en nedverdigende straff.'),
(3591, 33, 58, 'De som forulemper troende menn og kvinner uten at de har fortjent det, de belaster seg med baktalelse og åpenbar synd.'),
(3592, 33, 59, 'Du profet, si til dine hustrer og døtre og de troendes kvinner at de skal svøpe om seg kledningen. Dette er nærmest til at de gjenkjennes og ikke sjeneres. Gud er tilgivende, nåderik.'),
(3593, 33, 60, 'Hvis hyklerne, og de som har sykdom i sine hjerter, og urostifterne i byen, ikke gir seg, vil Vi visselig la deg gå imot dem, og så vil de forbli dine naboer kun for kort tid.'),
(3594, 33, 61, 'Under forbannelse skal de være, hvor de enn påtreffes, skal de pågripes og hugges ned.'),
(3595, 33, 62, 'Dette var Guds vanlige fremgangsmåte overfor de henfarne. Du vil aldri finne noen endring i Guds praksis!'),
(3596, 33, 63, 'Folk spør deg om timen. Si: «Kunnskap om den finnes bare hos Gud! Hvordan skulle du kunne vite? Kanskje timen er nær!»'),
(3597, 33, 64, 'Gud har forbannet de vantro, og Han har gjort klar en Ild for dem,'),
(3598, 33, 65, 'hvor de skal være og bli, og ikke vil finne beskytter eller hjelper.'),
(3599, 33, 66, 'Den dag da deres ansikter vrir seg i Ilden, vil de si: «Hadde vi bare hørt på Gud og sendebudet!»'),
(3600, 33, 67, 'De vil si: «Herre, vi hørte på våre ledere og stormenn, og de førte oss bort fra veien.'),
(3601, 33, 68, 'Herre, gi dem dobbelt straff og legg dem under en svær forbannelse.»'),
(3602, 33, 69, 'Dere som tror, vær ikke som dem som krenket Moses! Men Gud frikjente ham for det de sa, og han var ansett hos Gud.'),
(3603, 33, 70, 'Dere som tror, vis gudsfrykt, og snakk rett ut!'),
(3604, 33, 71, 'Så vil Han gjøre deres gjerninger rette og gode, og tilgi dere deres synder. Den som adlyder Gud og Hans sendebud vil visselig vinne stor seier.'),
(3605, 33, 72, 'Vi ville betro tillitspakten til himlene og jorden og fjellene, men de vegret seg for å bære den, og vek tilbake. Men mennesket tok den på seg. Dog se, han er urettferdig og lovløs.'),
(3606, 33, 73, 'Og derfor vil Gud straffe hyklere og avgudsdyrkere, menn og kvinner, mens troende menn og kvinner vil Han vende seg mot i nåde. Gud er tilgivende, nåderik.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Gud være lovet, Ham som alt i himlene og på jord tilhører! Ham være lovet, også i det hinsidige! Han er den Vise, den Innsiktsfulle.'),
(3608, 34, 2, 'Han vet hva som trenger inn i jorden, og hva som kommer ut av den, hva som kommer ned fra himmelen, og hva som stiger opp til den. Han er den Nåderike, den Ettergivende.'),
(3609, 34, 3, 'De vantro sier: «Timen vil aldri komme over oss.» Si: «Jo, så sannelig, ved Herren, den vil komme over dere, ved Ham som kjenner det skjulte. Ikke et støvkorn unngår Ham i himlene eller på jord. Det er intet mindre, eller større, uten at det står i en tydelig Bok.'),
(3610, 34, 4, 'Så Han kan lønne dem som tror og lever rettskaffent. De har i vente tilgivelse og verdig omsorg.'),
(3611, 34, 5, 'Men de som strever mot Vårt ord for å forpurre det, dem venter en straff av smerteligste sort.»'),
(3612, 34, 6, 'De som er gitt kunnskap, innser at det som er åpenbart deg fra Herren er sannhet, og leder til den Mektiges, den Prisverdiges vei.'),
(3613, 34, 7, 'Men de som er vantro, sier: «Skal vi vise dere en mann, som forteller dere at når dere er totalt oppløst, skal dere fremtre i en ny skapning?'),
(3614, 34, 8, 'Har han pådiktet Gud en løgn, eller er han besatt?» Nei, de som ikke tror på det hinsidige liv, er hjemfalne til straffen, og helt på villspor.'),
(3615, 34, 9, 'Har de da ikke lagt merke til himmel og jord som ligger foran dem og bak dem? Om Vi ville, kunne Vi la jorden oppsluke dem, eller la stykker av himmelen falle over dem! Heri er visselig et jærtegn for hver botferdig Guds tjener.'),
(3616, 34, 10, 'Vi viste David Vår gunst: «Dere fjell, istem lovsang sammen med ham og fuglene!» Og vi gjorde jernet mykt for ham:'),
(3617, 34, 11, '«Lag panserskjorter, og tilpass lenkene nøye! Og gjør det som rett er! Jeg ser hva dere gjør.»'),
(3618, 34, 12, 'Og Salomo underla Vi vinden. Morgenblåsten tilsvarte en måneds reise, og kveldsblåsten likeså. Og Vi lot en kilde med kobber strømme frem for ham. Av dsjinner arbeidet noen for ham med Herrens bifall. De av dem som avvek fra vår ordre, vil Vi la smake Ildens straff.'),
(3619, 34, 13, 'De laget for ham alt han ville, slott, statuer, fat store som vanntrau, og kokekar som ikke kunne flyttes. «Vis takknemlighet i deres ferd, Davids hus! Få av Mine tjenere viser takknemlighet.»'),
(3620, 34, 14, 'Da Vi besluttet at han skulle dø, var det intet som antydet for dem hans død, unntatt at jordens krek spiste hans stav, og så, da han falt til jorden, ble det klart for dsjinnene at hadde de bare kjent det skjulte, ville de ikke fortsatt i et ydmykende straffarbeid.'),
(3621, 34, 15, 'Sabafolket hadde også et tegn på sitt hjemsted, to haver, en til høyre og en til venstre. «Spis av Herrens gaver, og vis Ham takknemlighet! Det er et godt land, og en tilgivende Herre.»'),
(3622, 34, 16, 'Men de vendte seg bort, og så sendte Vi over dem demningsoversvømmelsen. Og Vi byttet deres to haver i to andre, med bitre tornebuskfrukter, tamarisker, og noen få lotustrær.'),
(3623, 34, 17, 'Slik belønnet Vi dem for deres vantro. Men belønner Vi slik andre enn de vantro?'),
(3624, 34, 18, 'Og mellom disse, og byene som Vi har velsignet, plasserte Vi fremtredende byer, og fastla etappene mellom dem. «Tilbakelegg dem i trygghet, natt og dag.»'),
(3625, 34, 19, 'Men de sa: «Gjør våre etapper lengre, Herre!» De gjorde en urett som slo tilbake på dem selv. Og Vi gjorde dem til legender. Vi rev dem i stumper og stykker. Heri er jærtegn for enhver standhaftig, takknemlig.'),
(3626, 34, 20, 'Iblis fant sin mening om dem bekreftet. De fulgte ham, unntatt en flokk av troende.'),
(3627, 34, 21, 'Men han hadde ingen makt over dem! Vi ville bare bringe i erfaring hvem som tror på det hinsidige, og hvem som var i tvil om det. Herren passer på alt!'),
(3628, 34, 22, 'Si: «Bare påkall de dere hevder er ved siden av Gud! De eier ikke et støvkorns vekt i himlene eller på jord. De har ingen andel i himlene og jorden, og Han har ingen medarbeider blant dem.»'),
(3629, 34, 23, 'Det nytter ikke å legge inn et godt ord hos Ham, unntatt for den som er godkjent. Når så skrekken har fortatt seg i deres hjerter, vil de si: «Hva sa Herren?» Og de vil svare: «Sannheten! Han er den Opphøyde, den Store!»'),
(3630, 34, 24, 'Si: «Hvem gir dere livsens underhold fra himlene og jorden?» Si: «Gud!» Enten er vi eller dere under ledelse, eller klart på villspor!'),
(3631, 34, 25, 'Si: «Dere vil ikke bli trukket til regnskap for våre synder, og heller ikke vi for det dere gjør.»'),
(3632, 34, 26, 'Si: «Herren vil bringe oss sammen, og så vil Han avsi kjennelse mellom oss med sannhet. Han er Avgjøreren, den Allvitende.»'),
(3633, 34, 27, 'Si: «Vis meg det dere har hengt på Ham som medguder!» Nei, sannelig, Han er Gud, den Mektige, den Vise.'),
(3634, 34, 28, 'Vi har sendt deg til alle mennesker med godt budskap og som advarer. Men folk flest vet det ikke.'),
(3635, 34, 29, 'Og de sier: «Når kommer så det som er stilt i utsikt, om dere snakker sant?»'),
(3636, 34, 30, 'Si: «Tiden er satt for dere til en dag, som dere ikke kan utsette eller påskynde en time!»'),
(3637, 34, 31, 'De vantro sier: «Vi kan aldri tro på denne Koranen, og heller ikke på det som foreligger før den.» Om du bare kunne se når de urettferdige står foran Herren, og slenger ord til hverandre! De svake og undertrykte vil si til de store og hovmodige: «Hadde det ikke vært for dere, ville vi vært troende.»'),
(3638, 34, 32, 'Og de hovmodige vil svare de svake: «Har vi holdt dere borte fra ledelsen etter at den kom til dere? Nei, dere var nok syndere.»'),
(3639, 34, 33, 'Og de svake vil si til de hovmodige: «Nei, dere smidde renker natt og dag, da dere påla oss å fornekte Gud, og gi Ham likemenn.» Og de vil angre i sin sjel når de ser straffen, og Vi legger lenker om halsen på den vantro. Kan de vel belønnes for annet enn det de har gjort?'),
(3640, 34, 34, 'Vi sendte aldri en advarer til noen by uten at de som hadde et liv i vellevnet sa: «Vi tror ikke på det budskap som dere kommer med.»'),
(3641, 34, 35, 'Og de sa videre: «Vi står oss bedre med eiendom og barn! Vi får ingen straff!»'),
(3642, 34, 36, 'Si: «Herren gir rikelig til den Han bestemmer, eller knapt. Men folk flest vet ikke det.»'),
(3643, 34, 37, 'Det er ikke deres eiendom og barn som bringer dere nær til Oss. Det er for den som tror og lever rettskaffent. Men disse har i vente dobbelt lønn for sin innsats, og de vil få bo i trygghet i høye saler.'),
(3644, 34, 38, 'Men de som strever mot Vårt ord for å forpurre det, de må møte til straff!'),
(3645, 34, 39, 'Si: «Herren gir rikelig eller knapp levemåte til hvem Han vil av Sine tjenere. Og hva dere enn måtte gi bort, så vil Han erstatte det. Han er den beste forsørger.»'),
(3646, 34, 40, 'Den dag da Han samler dem alle sammen, så vil Han si til englene: «Var det dere disse tilbad?»'),
(3647, 34, 41, 'Og de vil svare: «Ære være Deg! Du er Vår venn fremfor dem. Nei, de dyrket dsjinnene! De fleste av dem trodde på disse.»'),
(3648, 34, 42, 'Men denne dag kan ingen nytte eller skade noen annen, og Vi vil si til de urettferdige: «Føl Ildens straff, som dere kalte løgn!»'),
(3649, 34, 43, 'Når Vårt ord resiteres for dem som klar beskjed, sier de: «Dette er bare en mann som vil holde dere borte fra det deres fedre tilbad.» De sier også: «Dette er ikke annet enn oppdiktet svindel.» Og de vantro sier om sannheten, når den er kommet til dem: «Dette er bare åpenbar magi.»'),
(3650, 34, 44, 'Vi har ikke gitt dem skrifter som de kunne studere, og heller ikke sendt dem noen advarer før deg.'),
(3651, 34, 45, 'De som levde før dem, kalte også sannhet for løgn, men dagens vantro har ikke oppnådd tiendeparten av det Vi gav dem av makt og rikdom. De holdt Mine sendebud for løgnere, og hva var ikke Min irettesettelse!'),
(3652, 34, 46, 'Si: «Jeg gir dere kun én formaning, at dere skal reise dere for Gud, to sammen eller alene, og så tenke etter! Deres bysbarn er ikke besatt. Han er bare en advarer for dere, før en forferdelig straff.»'),
(3653, 34, 47, 'Si: «Jeg har ikke bedt dere om lønn. Den kan dere beholde. Min lønn er Guds sak. Han er vitne til alle ting.»'),
(3654, 34, 48, 'Si: «Herren slynger ut sannheten, Han, som vel kjenner det skjulte.»'),
(3655, 34, 49, 'Si: «Sannheten er kommet! Verdiløs tomhet kan verken skape eller gjenskape.»'),
(3656, 34, 50, 'Si: «Om jeg farer vill, så gjør jeg det kun til egen skade. Om jeg har funnet veien, så er det ved det Herren har åpenbart for meg. Han hører, er nær.»'),
(3657, 34, 51, 'Om du bare kunne se når de gripes av frykt, og ikke kan unnkomme, og tas fast, fra nært hold!'),
(3658, 34, 52, 'Og de sier: «Vi tror på Ham!» Men hvordan kan de nå frem fra langt borte?'),
(3659, 34, 53, 'Tidligere fornektet de Ham og fremkastet tanker om det skjulte fra langt borte?'),
(3660, 34, 54, 'Det er lagt en hindring mellom dem og det de ønsker. Slik ble det gjort med deres like i gammel tid, som hadde betenkeligheter og tvil.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Lovet være Gud, himlenes og jordens skaper, Han som har gjort englene til budbringere, med vinger, to, tre, eller fire. Han øker på i skapningen som Han vil! Gud evner alle ting.'),
(3662, 35, 2, 'Den nådesbevisning Gud tildeler menneskene, kan ingen holde tilbake, og det Han holder tilbake, kan ingen slippe løs. Han er den Mektige, den Vise.'),
(3663, 35, 3, 'Dere mennesker, kom i hu Guds godhet mot dere. Finnes det vel noen skaper annen enn Gud, som gir dere det dere trenger fra himmel og jord? Det er ingen gud unntatt Ham! Hvordan kan dere være så forvridde?'),
(3664, 35, 4, 'Om de kaller deg for løgner, så har de holdt sendebud før deg for løgnere. Til Gud blir alle ting brakt tilbake.'),
(3665, 35, 5, 'Dere mennesker, Guds løfte er sannhet! Så la ikke jordelivet forlede dere! La ikke forføreren forlede dere når det gjelder Gud!'),
(3666, 35, 6, 'Satan er visselig en fiende for dere, så ta ham for en fiende! Han kaller bare på sine for at de skal bli Ildens folk.'),
(3667, 35, 7, 'De som er vantro, dem venter en hard straff. Men de som tror og lever rettskaffent, dem venter tilgivelse og stor lønn.'),
(3668, 35, 8, 'Hva med ham, hvis gjerningers ondskap synes ham prydelig, så han anser dem gode? Gud lar seile sin egen sjø den Han vil, og gir ledelse til den Han vil! Så kast deg ikke bort i klager over dem! Gud vet om det de steller i stand.'),
(3669, 35, 9, 'Det er Gud som sender vindene som bringer opp skyer. Så driver Vi dem til det døde land, og gir derved jorden liv, etter dens dvale. Slik er oppstandelsen!'),
(3670, 35, 10, 'Om noen ønsker seg makt, så tilhører all makt Gud. Til Ham stiger opp det gode ord, og den rette handling løfter Han opp til seg. Men de som smir sammen ondt, de har i vente en hard straff, og deres renker blir til intet.'),
(3671, 35, 11, 'Gud skapte dere av jord. Senere av en sæddråpe, så gjorde Han dere til par. Ingen kvinne blir svanger eller føder uten at Han vet det, og ingen gis et langt liv, eller hans liv forkortes, uten at det er i en Bok. Dette er lett for Gud.'),
(3672, 35, 12, 'Heller ikke er de to hav like. Det ene er søtt, forfriskende og behagelig å drikke, det andre er salt og bittert. Men fra begge får dere fersk føde, og dere henter prydsaker, som dere kan bære. Og du kan se skipene pløye seg vei gjennom det, så dere kan søke hans gode haver, så dere måtte vise takknemlighet.'),
(3673, 35, 13, 'Han lar natten gå over i dag, og lar dagen bli natt. Han har satt sol og måne i tjeneste. Begge løper med sin bestemte tid. Dette er Gud, deres Herre! Ham tilhører herreveldet, og de dere påkaller utenom Ham, eier ikke så meget som et daddelskinn.'),
(3674, 35, 14, 'Om dere påkaller dem, så hører de ikke deres bønn, og om de hørte, kunne de ikke bønnhøre dere. På oppstandelsens dag vil de fornekte deres avgudsdyrkelse. Ingen kan underrette deg som en som er vel underrettet.'),
(3675, 35, 15, 'Dere mennesker, det er dere som trenger Gud. Gud, Han er den Selvtilstrekkelige, den Lovpriste.'),
(3676, 35, 16, 'Om Han vil, kan Han fjerne dere, og bringe en ny skapning.'),
(3677, 35, 17, 'Dette er ingen stor sak for Gud.'),
(3678, 35, 18, 'Ingen kan bære en annens byrde! Om en hardt belastet ber om hjelp til å bære den, så kan intet av den bæres, selv om det var ens nærmeste. Du bare advarer dem som frykter Herren i det skjulte, og som forretter bønnen. Den som renser seg, gjør det kun til eget beste. Til Gud er reisens mål.'),
(3679, 35, 19, 'Den blinde og den seende er ikke like,'),
(3680, 35, 20, 'heller ikke mørke og lys,'),
(3681, 35, 21, 'skygge og solens brann.'),
(3682, 35, 22, 'De levende og de døde er ikke like. Gud lar den høre som Han vil. Du kan ikke få dem i gravene til å høre.'),
(3683, 35, 23, 'Du er bare en advarer.'),
(3684, 35, 24, 'Vi har sendt deg med sannheten, med gledesbudskap og advarsler. Det er intet folk uten at en advarer var hos dem.'),
(3685, 35, 25, 'Om de holder deg for løgner, så har de før dem gjort det samme. Deres sendebud kom til dem med klar beskjed, og den opplysende skrift.'),
(3686, 35, 26, 'Men så tok Jeg de vantro fatt, og hvordan var vel Min irettesettelse!'),
(3687, 35, 27, 'Du har vel lagt merke til hvordan Gud sender vann fra oven, og med det frembringer Vi frukt av forskjellige farger? I fjellene finnes forskjellige lag, hvite, røde og svarte,'),
(3688, 35, 28, 'også blant mennesker, dyr og kveg finnes forskjellige farger. Slik er det. Men kun de av Hans tjenere frykter Gud, som har kunnskap. Gud er mektig, tilgivende.'),
(3689, 35, 29, 'De som fremleser Guds ord, forretter bønnen, og gir av det som Vi har gitt dem, i det skjulte eller åpent, kan håpe å ha gjort en handel som ikke er bortkastet,'),
(3690, 35, 30, 'at Han må gi dem full lønn og mer til av Sin godhet. Han er tilgivende, full av anerkjennelse.'),
(3691, 35, 31, 'Det Vi har åpenbart deg av skriften er sannheten, til stadfestelse av det som foreligger før den. Gud er velunderrettet, observant når det gjelder Hans tjenere.'),
(3692, 35, 32, 'Og så har Vi gitt skriften i arv til dem av Våre tjenere, som Vi har utvalgt. Blant dem er slike som pådrar seg syndens lodd, andre er lunkne. Men noen er i første rekke med gode gjerninger – med Guds hjelp. Dette er den store velvilje.'),
(3693, 35, 33, 'I Edens haver skal de tre inn! Der blir de prydet med armbånd av gull og med perler og klær av silke.'),
(3694, 35, 34, 'Og de vil si: «Gud være lovet, som har fjernet all sorg fra oss! Herren er i sannhet tilgivende, full av anerkjennelse,'),
(3695, 35, 35, 'som i Sin godhet lar oss bo i den blivende bolig, hvor ingen plage eller tretthet kan røre oss.»'),
(3696, 35, 36, 'Men de vantro, dem venter helvetes ild, hvor det ikke gjøres ende på dem så de dør, og heller ikke lettes straffen for dem. Slik belønner Vi enhver utakknemlig.'),
(3697, 35, 37, 'Der roper de: «Herre, slipp oss ut, så vi kan leve rettskaffent, og ikke slik som vi gjorde.» Har Vi da ikke gitt dere så langt liv, at den som ville tenke etter, kunne ha kommet til ettertanke i løpet av det? Advareren kom til dere, så nå kan dere få føle! De urettferdige har ingen til å hjelpe seg.'),
(3698, 35, 38, 'Gud kjenner det skjulte i himlene og på jord. Han kjenner det som bor i hjertene.'),
(3699, 35, 39, 'Han er det som har latt dere overta på jorden. Den som er vantro, blir belastet sin vantro. Vantroen øker bare de vantro i avskyelighet i Herrens øyne. Vantroen øker bare de vantro i fortapelse.'),
(3700, 35, 40, 'Si: «Hva mener dere om deres medguder som dere påkaller utenom Gud? Vis meg det de har skapt på jorden, eller har de noen andel i himmelen? Har Vi gitt dem en Bok, så de har klar beskjed fra den å stå på?» Nei, de urettferdige lover hverandre bare bedrag.'),
(3701, 35, 41, 'Gud holder fast himlene og jorden, så de ikke kommer ut av lage. Om de kom ut av lage, så kunne ingen holde dem på plass etter Ham. Han er mild, tilgivende.'),
(3702, 35, 42, 'De har svoret sine dyreste eder ved Gud, at hvis en advarer kom til dem, så ville de visselig bli mer på rett vei enn noe annet folk. Men når så en advarer kom til dem, øket det dem bare i motvilje'),
(3703, 35, 43, 'og i hovmod på jorden, og i onde renker. Men onde renker fanger bare sin opphavsmann. Kan de vel vente noe annet enn den vanlige skjebne for de henfarne? Du finner aldri noen veksling i Guds praksis. Du finner aldri noen forandring i Guds praksis.'),
(3704, 35, 44, 'Har de da ikke reist rundt i landet, så de kunne se hva enden ble for dem som levde før dem? De var sterkere enn de nålevende i makt. Men Gud, det er intet i himlene eller på jord som kan sette Ham ut av spillet. Han vet, og Han evner.'),
(3705, 35, 45, 'Om Gud skulle ta menneskene fatt for det de fortjente, ville Han ikke la igjen et kryp på jordens overflate. Men Han gir dem utsettelse, til en fastsatt frist. Men når så deres frist utløper, visselig, Gud er observant når det gjelder hans tjenere.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'Ya Sin'),
(3707, 36, 2, 'Ved den vise Koran!'),
(3708, 36, 3, 'Du er visselig et sendebud:'),
(3709, 36, 4, 'På rett vei!'),
(3710, 36, 5, 'En åpenbaring fra den Mektige, den Nåderike,'),
(3711, 36, 6, 'så du kan advare et folk hvis fedre ikke ble advart, så de lever i likegyldighet.'),
(3712, 36, 7, 'Ordet er blitt realitet for de fleste av dem, allikevel tror de ikke.'),
(3713, 36, 8, 'Vi har lagt lenker om halsen på dem, opp til haken, så de må strekke hodet.'),
(3714, 36, 9, 'Og Vi har satt en vegg foran dem og bak dem, og Vi har dekket over dem, så de intet ser.'),
(3715, 36, 10, 'For dem er det likegyldig om du advarer dem, eller ikke advarer dem. De tror ikke.'),
(3716, 36, 11, 'Du kan bare advare den som følger formaningen, og frykter den Barmhjertige i det skjulte. Så bebud for ham tilgivelse og en verdig lønn.'),
(3717, 36, 12, 'Det er Vi som gir de døde liv, og noterer det de har sendt i forveien til regnskapet, og de spor de har latt etter seg.'),
(3718, 36, 13, 'Sett frem en lignelse for dem, folkene i byen da utsendingene kom til dem.'),
(3719, 36, 14, 'Vi sendte dem to, men de holdt dem for løgnere. Så forsterket Vi dem med en tredje, og de sa: «Vi er utsendinger til dere.»'),
(3720, 36, 15, 'De svarte: «Dere er bare vanlige mennesker som oss. Den Barmhjertige har ikke åpenbart noen ting. Dere farer bare med løgn.»'),
(3721, 36, 16, 'De sa: «Herren vet at vi er utsendinger til dere.'),
(3722, 36, 17, 'Oss påligger bare en klar forkynnelse.»'),
(3723, 36, 18, 'De svarte: «Vi ser onde varsler ved dere! Hvis dere ikke holder opp, vil vi steinjage dere, og en smertelig straff fra oss vil ramme dere.»'),
(3724, 36, 19, 'Da sa de: «Dere kan beholde deres varsler. Vil dere formanes? Nei, dere er lettsindige mennesker.»'),
(3725, 36, 20, 'Så kom en mann løpende fra lengst borte i byen og sa: «Mitt folk, følg utsendingene!'),
(3726, 36, 21, 'Følg slike som ikke ber om lønn av dere, som er på rett vei.'),
(3727, 36, 22, 'Skulle jeg ikke tjene Ham som har skapt meg, og til hvem dere blir brakt tilbake?'),
(3728, 36, 23, 'Skulle jeg legge meg til guder utenom Ham, guder, hvis inngripen ikke nytter meg det ringeste, om den Barmhjertige ønsker at nød skal ramme meg, og som ikke kan redde meg?'),
(3729, 36, 24, 'Da var jeg visselig i klar villfarelse!'),
(3730, 36, 25, 'Jeg tror virkelig på deres Herre, så hør på meg!»'),
(3731, 36, 26, 'Man sa: «Så gå inn i paradiset.» Han svarte: «Hadde bare mitt folk visst'),
(3732, 36, 27, 'at Herren har tilgitt meg, og plassert meg blant dem som ære er blitt til del.»'),
(3733, 36, 28, 'Etter ham sendte Vi ikke over hans folk hærskarer fra himmelen, Vi sendte ingen.'),
(3734, 36, 29, 'Det var bare et eneste skrall, og så var de utslukte.'),
(3735, 36, 30, 'Ve over menneskene! Aldri kom et sendebud til dem, uten at de drev ap med ham.'),
(3736, 36, 31, 'Har de ikke sett hvor mange slektledd Vi har utslettet før dem, så de ikke vender tilbake til dem?'),
(3737, 36, 32, 'Men de skal alle sammen møte opp hos Oss!'),
(3738, 36, 33, 'Et jærtegn for menneskene er den døde jord, som Vi gir liv, og bringer ut av den korn, som de så spiser av.'),
(3739, 36, 34, 'Og Vi har lagt palmelunder på den, og vinhaver, og Vi har latt kilder bryte frem,'),
(3740, 36, 35, 'så de kan spise av dens frukt og sine henders arbeid. Vil de da ikke vise takknemlighet?'),
(3741, 36, 36, 'Ære være Ham, som skapte alt parvis, det jorden frembringer, og dem selv, og det de ikke kjenner til.'),
(3742, 36, 37, 'Et jærtegn for dem er natten. Vi trekker dagen til side for den, og se, de er i mørke.'),
(3743, 36, 38, 'Og solen, den vandrer til sitt hvilested. Dette er bestemmelsen til den Mektige, den Allvitende.'),
(3744, 36, 39, 'Og månen, for den har Vi bestemt etapper, til den kommer igjen som en krokete palmegren.'),
(3745, 36, 40, 'Det er ikke solen gitt å innhente månen, og heller ikke kan natten løpe fra dagen. Enhver av dem svever i sin sfære.'),
(3746, 36, 41, 'Et jærtegn for dem er at Vi fraktet menneskebarn i den fullastede arken.'),
(3747, 36, 42, 'Og Vi har skapt noe lignende for dem nå, som de kan gå om bord i.'),
(3748, 36, 43, 'Om Vi ville, kunne Vi drukne dem. Da har de ingen å kalle til hjelp, og de blir ikke reddet,'),
(3749, 36, 44, 'unntatt som en nåde fra Oss, og for å nyte livet inntil videre.'),
(3750, 36, 45, 'Sier man til dem: «Pass dere for det som er foran dere, og det som etter dere kommer, så dere må finne nåde,»'),
(3751, 36, 46, 'så kommer det allikevel ikke et eneste av Herrens tegn til dem, uten at de vender seg bort fra det.'),
(3752, 36, 47, 'Og sier man til dem: «Gi av det som Gud har gitt dere,» så sier de vantro til de troende: «Skal vi bespise noen som Gud kunne bespise, om Han ville? Dere kan bare være klart på villspor.»'),
(3753, 36, 48, 'De sier også: «Når inntreffer så det dere har stilt i utsikt, om dere snakker sant?»'),
(3754, 36, 49, 'De kan bare vente et eneste skrall, som tar dem midt i deres disputter,'),
(3755, 36, 50, 'de kan ikke ordne med sine etterlatenskaper, og vender ikke tilbake til sine.'),
(3756, 36, 51, 'Det støtes i basunen, og de iler frem av gravene, hen til Herren.'),
(3757, 36, 52, 'De sier: «Trøste oss, hvem har kalt oss frem fra vårt hvilested? Dette er det den Barmhjertige lovet, sendebudene har talt sant!»'),
(3758, 36, 53, 'Det var bare et eneste skrall, og se, de møter alle frem hos Oss.'),
(3759, 36, 54, 'På denne dag gjøres ingen den ringeste urett, og dere belønnes bare for det dere har gjort.'),
(3760, 36, 55, 'På denne dag er paradisets folk opptatt med sitt, og gleder seg.'),
(3761, 36, 56, 'De og deres hustruer hviler i skyggen på divaner.'),
(3762, 36, 57, 'Der får de frukt, og alt de måtte be om.'),
(3763, 36, 58, '«Fred» er ordet fra en nåderik herre.'),
(3764, 36, 59, '«Hold dere for dere selv i dag, dere syndere!'),
(3765, 36, 60, 'Gjorde Jeg ikke en avtale med dere, Adams barn, at dere ikke skulle tjene Satan, han er for dere en klar fiende,'),
(3766, 36, 61, 'og at dere skulle tjene Meg? Det er rett vei.'),
(3767, 36, 62, 'Han har ført en stor mengde av dere vill.'),
(3768, 36, 63, 'Tenkte dere da ikke? Dette er helvete, som dere ble stilt i utsikt.'),
(3769, 36, 64, 'Ta i dag den stekende ild fordi dere var vantro!»'),
(3770, 36, 65, 'På denne dag vil Vi sette dem segl på munnen. Men deres hender vil tale til Oss, og deres føtter avlegger vitnesbyrd om det de har pådratt seg.'),
(3771, 36, 66, 'Om Vi ville, kunne Vi utviske deres øyne. Da ville de nok løpe om kapp med veien, men hvordan skulle de se?'),
(3772, 36, 67, 'Om Vi ville, kunne Vi omskape dem på stedet, så de ikke kunne gå verken frem eller tilbake.'),
(3773, 36, 68, 'Den Vi gir et langt liv, lar Vi reduseres kroppslig (snur Vi om i skapningen). Forstår de da ikke?'),
(3774, 36, 69, 'Vi har ikke lært ham dikterkunsten, det passer seg ikke for ham. Dette er bare en formaning, og en klar Koran,'),
(3775, 36, 70, 'så han kan advare de levende, så at ordet kan iverksettes overfor de vantro.'),
(3776, 36, 71, 'Har de da ikke sett at Vi har skapt kveg for dem ved Våre henders verk, som er deres eiendom?'),
(3777, 36, 72, 'Og Vi har temmet det for dem, noen av dem kan de ride på, andre kan de spise.'),
(3778, 36, 73, 'Også annen nytte har de av dem, og de gir drikke. Vil de da ikke vise takknemlighet?'),
(3779, 36, 74, 'Men de har lagt seg til guder utenom Gud, for at de kanskje måtte bli hjulpet.'),
(3780, 36, 75, 'Men de kan ikke yte dem hjelp, skjønt de for dem er en tilstedeværende hærskare.'),
(3781, 36, 76, 'La ikke deres tale bedrøve deg! Vi kjenner til det de holder hemmelig, og det de bærer åpent frem.'),
(3782, 36, 77, 'Har da ikke mennesket bemerket at Vi har skapt ham av en sæddråpe? Men så, så blir han stridbar og kranglete, klart og åpent.'),
(3783, 36, 78, 'Og han tar et eksempel for Oss, og har glemt sin skapelse. Han sier: «Hvem skal gi liv til morkne ben?»'),
(3784, 36, 79, 'Si: «Han skal gi dem liv, som frembrakte dem første gang! Han kjenner til all skapning.'),
(3785, 36, 80, 'Han som gir dere varme av grønne trær, og se, dere kan tenne opp med dem.'),
(3786, 36, 81, 'Evner ikke Han, som skapte himlene og jorden, å skape deres like? Jo, sannelig, Han er Skaperen, den Allvitende.'),
(3787, 36, 82, 'Hans bud, når Han ønsker noe, er at Han sier til det: ’Bli!’ – og det blir.'),
(3788, 36, 83, 'Ære være Ham, i hvis hånd herredømmet over alle ting beror, til Hvem dere blir brakt tilbake!»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Ved dem på rad og rekke,'),
(3790, 37, 2, 'de barskt tilrettevisende,'),
(3791, 37, 3, 'som leser opp formaningen.'),
(3792, 37, 4, 'Sannelig, deres Gud er én,'),
(3793, 37, 5, 'Herren over himlene og jorden, og det som mellom dem er, soloppgangens Herre!'),
(3794, 37, 6, 'Vi har prydet den nederste himmel med stjernenes pryd,'),
(3795, 37, 7, 'Og som et vern mot hver opprørsk Satan.'),
(3796, 37, 8, 'De kan ikke avlytte det himmelske råd, for de angripes fra alle kanter for å forjages,'),
(3797, 37, 9, 'og de har i vente en varig straff,'),
(3798, 37, 10, 'unntatt en enkelt som snapper opp litt, og så forfølges av en gnistrende flamme.'),
(3799, 37, 11, 'Be dem uttale seg: Er menneskene vanskeligere å skape, eller annet Vi har skapt? Vi skapte dem av myk leire!'),
(3800, 37, 12, 'Nei, du undrer deg, og de spotter.'),
(3801, 37, 13, 'Når de formanes, husker de ikke,'),
(3802, 37, 14, 'og når de ser et jærtegn, håner de,'),
(3803, 37, 15, 'og sier: «Dette er skjær magi!'),
(3804, 37, 16, 'Når vi er døde og er blitt jord og knokler,'),
(3805, 37, 17, 'skal vi da gjenoppvekkes, og våre henfarne fedre?»'),
(3806, 37, 18, 'Si: «Ja, og i all underdanighet.»'),
(3807, 37, 19, 'Det lyder bare et stort skrik, og så kan de se.'),
(3808, 37, 20, 'Og de sier: «Trøste oss, dette er dommens dag.»'),
(3809, 37, 21, 'Dette er avgjørelsens dag, som dere holdt for løgn.'),
(3810, 37, 22, '«Samle sammen de som handlet ondt, og deres hustruer, og det de tilbad,'),
(3811, 37, 23, 'utenom Gud, og før dem til veien til helvete!'),
(3812, 37, 24, 'Og still dem opp! De skal trekkes til regnskap.'),
(3813, 37, 25, 'Hva er i veien med dere, så dere ikke hjelper hverandre?»'),
(3814, 37, 26, 'Nei, på denne dag kapitulerer de,'),
(3815, 37, 27, 'går hen til hverandre i samtale og sier:'),
(3816, 37, 28, '«Dere pleide å komme til oss fra høyre side?»'),
(3817, 37, 29, 'Disse svarer: «Nei, dere var så allikevel ikke troende.'),
(3818, 37, 30, 'Vi hadde ingen makt over dere. Nei, dere var opprørske folk.'),
(3819, 37, 31, 'Nå er Herrens ord realitet for oss, vi får smake det.'),
(3820, 37, 32, 'Vi forførte dere. Vi var selv på villstrå.»'),
(3821, 37, 33, 'Denne dag har de alle sin del i straffen.'),
(3822, 37, 34, 'Slik gjør Vi med synderne,'),
(3823, 37, 35, 'for når man sa til dem: «Det er ingen gud utenom Gud,» viste de hovmod,'),
(3824, 37, 36, 'og sa: «Skal vi oppgi våre guder for en besatt rimsmed?»'),
(3825, 37, 37, 'Nei, han brakte sannheten, og stadfestet sendebudene.'),
(3826, 37, 38, 'Dere skal visselig få smake den smertelige straff,'),
(3827, 37, 39, 'og ikke belønnes for annet enn det dere har gjort.'),
(3828, 37, 40, 'Unntatt er Guds oppriktige tjenere.'),
(3829, 37, 41, 'Disse har i vente den omsorg som er kunngjort,'),
(3830, 37, 42, 'frukt, og de blir gjort ære på'),
(3831, 37, 43, 'i lykksalighetens haver,'),
(3832, 37, 44, 'på leier overfor hverandre,'),
(3833, 37, 45, 'og et drikkebeger går rundt blant dem, fylt fra en kilde,'),
(3834, 37, 46, 'rent og velsmakende for dem som drikker,'),
(3835, 37, 47, 'hvori ingen beruselse er, og de blir ikke utmattet.'),
(3836, 37, 48, 'Hos dem er storøyde jomfruer med blyge blikk,'),
(3837, 37, 49, 'de er som velbeskyttede perler.'),
(3838, 37, 50, 'De vender seg mot hverandre idet de samtaler.'),
(3839, 37, 51, 'En av dem sier: «Jeg hadde en venn, som sa:'),
(3840, 37, 52, '’Hører du til dem som holder det for sant?'),
(3841, 37, 53, 'At når vi er døde og er blitt jord og knokler, kommer vi for dommen?’»'),
(3842, 37, 54, 'Og han fortsetter: «Skal vi kikke ned?»'),
(3843, 37, 55, 'Så kikker han, og ser ham midt i helvete, og sier:'),
(3844, 37, 56, '«Ved Gud! Du var nær ved å styrte meg i fordervelsen!'),
(3845, 37, 57, 'Hadde det ikke vært for Herrens nåde, ville jeg måttet møte der.'),
(3846, 37, 58, 'Skal vi bare dø'),
(3847, 37, 59, 'vår første død, og ikke bli straffet?'),
(3848, 37, 60, 'Det er den store seier!'),
(3849, 37, 61, 'For noe slikt må de strevende streve!»'),
(3850, 37, 62, 'Hva er å foretrekke som gjestfrihet, dette, eller Zaqqumtreet i helvete?'),
(3851, 37, 63, 'Dette har Vi gjort til en prøvelse for de urettferdige.'),
(3852, 37, 64, 'Det er et tre som vokser opp fra helvetes grunn,'),
(3853, 37, 65, 'og dets fruktkolber ser ut som satanhoder.'),
(3854, 37, 66, 'De spiser av det, og fyller sin buk fra det,'),
(3855, 37, 67, 'og derpå får de et brygg av kokende vann.'),
(3856, 37, 68, 'Så vender de tilbake til helvetesilden.'),
(3857, 37, 69, 'De fant sine fedre på villspor.'),
(3858, 37, 70, 'Men de haster frem i deres spor.'),
(3859, 37, 71, 'Før dem fór de fleste av deres forfedre vill.'),
(3860, 37, 72, 'Vi sendte ut blant dem advarere,'),
(3861, 37, 73, 'men se, hva enden ble for de advarte!'),
(3862, 37, 74, 'Unntatt Guds oppriktige tjenere.'),
(3863, 37, 75, 'Noa ropte til Oss. Hvor utmerket er Vi som svarer!'),
(3864, 37, 76, 'Og Vi reddet ham og hans fra den store nød,'),
(3865, 37, 77, 'og Vi lot hans etterkommere bli overlevende.'),
(3866, 37, 78, 'Og Vi etterlot om ham blant senere slektledd disse ord:'),
(3867, 37, 79, '«Fred over Noa blant all verdens mennesker.»'),
(3868, 37, 80, 'Slik lønner Vi dem som handler vel!'),
(3869, 37, 81, 'Han er en av Våre troende tjenere!'),
(3870, 37, 82, 'Så druknet Vi resten.'),
(3871, 37, 83, 'Av hans sort var også Abraham,'),
(3872, 37, 84, 'da han kom til Herren med et rent hjerte, og'),
(3873, 37, 85, 'sa til sin far og sitt folk: «Hva er det dere tilber?'),
(3874, 37, 86, 'Vil dere på falsk vis ha guder fremfor Gud?'),
(3875, 37, 87, 'Hva er da deres mening om all verdens Herre?»'),
(3876, 37, 88, 'Og han kastet et blikk på stjernene, og sa:'),
(3877, 37, 89, '«Jeg er nok syk.»'),
(3878, 37, 90, 'Da snudde de seg rundt og gikk bort fra ham.'),
(3879, 37, 91, 'Så vendte han seg til deres guder, og sa: «Vil dere ikke spise?'),
(3880, 37, 92, 'Hva feiler det dere siden dere ikke snakker?»'),
(3881, 37, 93, 'Så gikk han innpå dem, og slo dem med sin høyre hånd.'),
(3882, 37, 94, 'Da kom de løpende hen til ham.'),
(3883, 37, 95, 'Han sa: «Tilber dere noe som dere har meislet til,'),
(3884, 37, 96, 'skjønt det er Gud som har skapt dere og alt dere lager?»'),
(3885, 37, 97, 'De sa: «Lag ham en ovn, og kast ham inn i ilden!»'),
(3886, 37, 98, 'De ville prøve et knep med ham, men Vi gjorde at de selv kom til kort.'),
(3887, 37, 99, 'Abraham sa: «Jeg går hen til Herren. Han vil lede meg.'),
(3888, 37, 100, 'Herre, gi meg en sønn av de rettferdige!»'),
(3889, 37, 101, 'Og Vi bebudet ham en mild sønn.'),
(3890, 37, 102, 'Da han var stor nok til å gå ham til hånde, sa Abraham: «Min sønn, jeg så i en drøm at jeg skal ofre deg. Tenk over hva du synes.» Han svarte: «Min far, gjør det som du er pålagt. Du vil finne meg standhaftig, om Gud vil.»'),
(3891, 37, 103, 'Da de hadde underkastet seg påbudet, og han hadde lagt sønnen'),
(3892, 37, 104, 'med ansiktet ned, ropte vi til ham: «Abraham!'),
(3893, 37, 105, 'Du har rettet deg etter drømmesynet! Slik lønner Vi dem som handler vel.'),
(3894, 37, 106, 'Dette var visselig en klar prøvelse.»'),
(3895, 37, 107, 'Og Vi løste ham ut ved et opphøyet offer.'),
(3896, 37, 108, 'Vi etterlot om ham blant senere slektledd disse ord:'),
(3897, 37, 109, '«Fred over Abraham.»'),
(3898, 37, 110, 'Slik lønner Vi dem som handler vel.'),
(3899, 37, 111, 'Han var en av Våre troende tjenere.'),
(3900, 37, 112, 'Og Vi bebudet ham Isak, en profet, en av de rettferdige.'),
(3901, 37, 113, 'Og Vi velsignet ham og Isak. Blant deres etterkommere handler noen vel, og andre belaster seg med åpenbar urett.'),
(3902, 37, 114, 'Vi viste også godhet mot Moses og Aron,'),
(3903, 37, 115, 'og Vi reddet dem og deres folk fra den store nød,'),
(3904, 37, 116, 'og hjalp dem, så de vant seier.'),
(3905, 37, 117, 'Vi gav dem den tydelige skriften,'),
(3906, 37, 118, 'og ledet dem på rett vei.'),
(3907, 37, 119, 'Vi etterlot om dem hos senere slektledd disse ord:'),
(3908, 37, 120, '«Fred over Moses og Aron!»'),
(3909, 37, 121, 'Slik lønner Vi dem som handler vel.'),
(3910, 37, 122, 'De var av Våre troende tjenere.'),
(3911, 37, 123, 'Elias var også et av sendebudene,'),
(3912, 37, 124, 'da han sa til sitt folk: «Vil dere ikke vise gudsfrykt?'),
(3913, 37, 125, 'Vil dere påkalle Bal, og oppgi den beste skaper?'),
(3914, 37, 126, 'Gud, deres Herre, og deres henfarne fedres Herre?»'),
(3915, 37, 127, 'Men de holdt ham for løgner, så de vil måtte møte for dommen.'),
(3916, 37, 128, 'Unntatt er Guds oppriktige tjenere.'),
(3917, 37, 129, 'Vi etterlot om ham hos senere slektledd disse ord:'),
(3918, 37, 130, '«Fred over Elias!»'),
(3919, 37, 131, 'Slik lønner Vi dem som handler vel.'),
(3920, 37, 132, 'Han var en av Våre troende tjenere.'),
(3921, 37, 133, 'Lot var også et av sendebudene,'),
(3922, 37, 134, 'da Vi reddet ham og alle hans,'),
(3923, 37, 135, 'unntatt en gammel kone som ble igjen.'),
(3924, 37, 136, 'Så utslettet Vi de andre.'),
(3925, 37, 137, 'Dere ferdes forbi dem'),
(3926, 37, 138, 'sent og tidlig. Vil dere da ikke forstå?'),
(3927, 37, 139, 'Jonas var også et av sendebudene,'),
(3928, 37, 140, 'da han flyktet om bord i det fullastede skip,'),
(3929, 37, 141, 'og kastet lodd med mannskapet, og tapte.'),
(3930, 37, 142, 'Så slukte hvalen ham, idet han var klanderverdig.'),
(3931, 37, 143, 'Hadde han ikke vært av dem som priser Gud,'),
(3932, 37, 144, 'ville han forblitt i dens buk til oppstandelsens dag.'),
(3933, 37, 145, 'Men Vi kastet ham opp på en øde strand, og han var syk.'),
(3934, 37, 146, 'Og Vi lot vokse opp over ham en vekst.'),
(3935, 37, 147, 'Så sendte Vi ham til hundre tusen, eller mer,'),
(3936, 37, 148, 'og de ble troende. Og Vi lot dem nyte livet inntil videre.'),
(3937, 37, 149, 'Be dem uttale seg, er det slik at Herren har døtre, mens de har sønner?'),
(3938, 37, 150, 'Har Vi skapt englene av kvinnekjønn, mens de var til stede?'),
(3939, 37, 151, 'Sier de ikke i sin løgnaktighet:'),
(3940, 37, 152, '«Gud har lagt seg til barn?» De er visselig løgnere!'),
(3941, 37, 153, 'Skulle Han foretrekke døtre fremfor sønner?'),
(3942, 37, 154, 'Hva er i veien med deres dømmekraft?'),
(3943, 37, 155, 'Vil dere ikke tenke etter?'),
(3944, 37, 156, 'Har dere noen klar autorisasjon?'),
(3945, 37, 157, 'Legg frem skriften som dere har, om dere snakker sant!'),
(3946, 37, 158, 'De har laget slektskap mellom ham og dsjinnene, skjønt dsjinnene vel vet at de må møte for dommen.'),
(3947, 37, 159, 'Ære være Gud, fjernt borte fra det de påstår.'),
(3948, 37, 160, 'Unntatt er Guds oppriktige tjenere.'),
(3949, 37, 161, 'Dere og det dere tilber,'),
(3950, 37, 162, 'dere kan ikke forføre noen mot Ham,'),
(3951, 37, 163, 'unntatt den som vil havne i Ilden.'),
(3952, 37, 164, 'Det er ingen av oss som ikke har sin bestemte plass!'),
(3953, 37, 165, 'Vi er det, som stiller opp i rad og rekke!'),
(3954, 37, 166, 'Vi er det, som priser Gud!'),
(3955, 37, 167, 'Om de nå sa:'),
(3956, 37, 168, '«Hadde vi bare hatt en formaning fra de henfarne,'),
(3957, 37, 169, 'så skulle vi vært Guds oppriktige tjenere.»'),
(3958, 37, 170, 'Men de forkaster den. De skal få å vite!'),
(3959, 37, 171, 'Visselig, Vårt ord er tidligere gitt til Våre tjenere, utsendingene,'),
(3960, 37, 172, 'og de er det som blir ytet hjelp.'),
(3961, 37, 173, 'Visselig, våre skarer, de vil vinne seieren.'),
(3962, 37, 174, 'Snu deg vekk fra dem inntil videre,'),
(3963, 37, 175, 'og hold øye med dem. De skal få se!'),
(3964, 37, 176, 'Ber de om å få Vår straff påskyndet?'),
(3965, 37, 177, 'Når den står for døren, blir det en ond morgen for dem som ble advart!'),
(3966, 37, 178, 'Snu deg vekk fra dem inntil videre,'),
(3967, 37, 179, 'og hold et øye. De skal få se!'),
(3968, 37, 180, 'Ære være Herren, maktens Herre, fjernt borte fra det de påstår!'),
(3969, 37, 181, 'Fred over sendebudene!'),
(3970, 37, 182, 'Lovet være Gud, all verdens Herre!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'Ved Koranen, som har formaningen!'),
(3972, 38, 2, 'Nei, de som er vantro befinner seg i overmot og tross.'),
(3973, 38, 3, 'Hvor mange slektledd har Vi vel utslettet før dem? De ropte, men det var ingen tid til å unnkomme.'),
(3974, 38, 4, 'Nå undrer de seg over at en advarer er kommet til dem fra deres egen krets, og de vantro sier: «Dette er en trollmann, som farer med løgn!'),
(3975, 38, 5, 'Har han gjort gudene til en eneste Gud? Dette er sannelig en selsom ting.»'),
(3976, 38, 6, 'Rådmennene blant dem fjernet seg, og sa: «Gå hver til sitt, og hold fast ved deres guder! Dette er en planlagt ting.'),
(3977, 38, 7, 'Vi hørte ingenting om dette i den siste læren. Det er bare dikt.'),
(3978, 38, 8, 'Skulle formaningen åpenbares ham, av oss alle?» Ja, de er i tvil om Min formaning, men de har ennå ikke smakt Min straff.'),
(3979, 38, 9, 'Har de hånd om Herrens nådeskatter, den Mektiges, Giverens?'),
(3980, 38, 10, 'Har de herredømmet over himlene og jorden og det som mellom dem er? La dem så klatre opp i rep!'),
(3981, 38, 11, 'En hel hær av motstandsgrupper er slått på flukt her.'),
(3982, 38, 12, 'De holdt sannhet for løgn før deres tid, Noas folk, og Ad, og Farao med pyramidene,'),
(3983, 38, 13, 'og Thamod, og Lots folk, og Skogfolket. Dette var folk som stod imot.'),
(3984, 38, 14, 'Det var ingen som ikke holdt sendebudene for løgnere, og så ble Min straff realitet.'),
(3985, 38, 15, 'Dere kan bare vente et eneste rop, som ikke kan stanses,'),
(3986, 38, 16, 'men de sier: «Herre, skynd på med vår andel, før regnskapets dag.»'),
(3987, 38, 17, 'Bær i tålmod det de sier, og kom i hu Vår tjener David, mektig og effektiv. Han var botferdig.'),
(3988, 38, 18, 'Sammen med ham påla Vi fjellene å gi lovprisning, om aftenen og ved soloppgang.'),
(3989, 38, 19, 'Likeså fuglene i skarer. Alt vendte seg mot Ham.'),
(3990, 38, 20, 'Vi styrket hans kongedømme, og gav ham visdom og domsevne.'),
(3991, 38, 21, 'Beretningen om tvistepartene har vel nådd deg, da de klatret inn i Helligdommen?'),
(3992, 38, 22, 'Da de kom inn til David, ble han redd for dem, og de sa: «Vær ikke redd! Vi er parter i en tvist, hvor den ene har krenket den annen. Døm du med rettferd mellom oss, og gjør ingen avvik, men vis oss en rett vei!'),
(3993, 38, 23, 'Denne min bror har niognitti får, og jeg har et eneste får, og så sa han: ’Overlat det til meg,’ og gikk hardt inn på meg i saken.»'),
(3994, 38, 24, 'David sa: «Han har gjort urett mot deg ved å kreve ditt får i tillegg til sine egne. Mange sameiere gjør urett mot hverandre, unntatt slike som tror og lever rettskaffent, og de er få.» David trodde at Vi hadde stilt ham på prøve, og bad Herren om tilgivelse, og kastet seg ned og bøyet seg i bot.'),
(3995, 38, 25, 'Og Vi tilgav ham dette. Han har nærhets plass hos oss, et godt sted å komme til.'),
(3996, 38, 26, '«David, Vi innsetter deg som en fullmektig på jorden! Så døm rett mellom menneskene, og følg ikke egne ideer, så det fører deg bort fra Guds vei. De som vandrer bort fra Guds vei, har streng straff i vente, fordi de har glemt regnskapets dag.»'),
(3997, 38, 27, 'Vi har ikke skapt himmel og jord og det som mellom dem er for intet. Dette er de vantros mening. Men ve over de vantro, for Ilden skyld!'),
(3998, 38, 28, 'Skulle Vi sette dem som tror og lever rettskaffent på linje med dem som anstiller ufred og elendighet på jorden? Skulle Vi sette gudfryktige på linje med dem som lever i synd?'),
(3999, 38, 29, 'Dette er en velsignet skrift, som Vi har åpenbart deg, så de må meditere over dens ord og komme til ettertanke, de som har hjertets forstand.'),
(4000, 38, 30, 'Og Vi gav David Salomo. Hvilken utmerket tjener! Han var visselig botferdig.'),
(4001, 38, 31, 'Da de raske og spenstige hester ble ført frem for ham om aftenen,'),
(4002, 38, 32, 'sa han: «Jeg har nok foretrukket kjærligheten til denne verdens goder fremfor Herrens ihukommen, inntil solen skjulte seg bak nattens forheng.'),
(4003, 38, 33, 'Før dem tilbake til meg!» Og han begynte å stryke dem over lendene og halsene.'),
(4004, 38, 34, 'Vi har visselig satt Salomo på prøve, idet Vi plasserte på hans trone en skikkelse. Da gjorde han bot.'),
(4005, 38, 35, 'Han sa: «Herre, tilgi meg, og gi meg et kongedømme, som ikke må tilkomme noen etter meg! Du er Giveren!»'),
(4006, 38, 36, 'Vi stilte vinden til hans disposisjon, så den på hans bud sakte beveget seg dit han ønsket.'),
(4007, 38, 37, 'Likeledes satanene, alle byggmestere og dykkere blant dem,'),
(4008, 38, 38, 'og andre, bundet sammen i lenker.'),
(4009, 38, 39, '«Dette er Vår gave! Vær raus eller påholdende uten regnskap!»'),
(4010, 38, 40, 'Han har nærhets plass hos Oss, et godt sted å komme til.'),
(4011, 38, 41, 'Omtal også Vår tjener Job, da han ropte til sin Herre: «Satan har rammet meg med elendighet og pine!»'),
(4012, 38, 42, 'Vi sa: «Stamp med foten! Her er kjølig bad, og drikke.»'),
(4013, 38, 43, 'Og Vi gav ham hans familie og andre nære, som en nådesbevisning fra oss, og en formaning for folk som har forstand.'),
(4014, 38, 44, 'Og videre. «Ta et knippe ris i din hånd, og slå med det, og svikt ikke!» Vi fant ham visselig standhaftig. Hvilken utmerket tjener! Han var botferdig.'),
(4015, 38, 45, 'Omtal også Våre tjenere Abraham, Isak og Jakob, mektige og effektive, og med innsikt.'),
(4016, 38, 46, 'Vi viet dem særlig til Husets ihukommelse.'),
(4017, 38, 47, 'De hører hos oss til de utvalgte, de beste.'),
(4018, 38, 48, 'Omtal også Våre tjenere Ismael, Elisa og Dhul-Kifl! Alle hørte til de beste.'),
(4019, 38, 49, 'Dette er en formaning. De gudfryktige har et godt sted å komme til,'),
(4020, 38, 50, 'Edens haver med vidåpne porter,'),
(4021, 38, 51, 'hvor de ligger tilbakelenet, og ber om rikelig med frukt og drikke.'),
(4022, 38, 52, 'De har hos seg jevnaldrende kvinner med blyge blikk.'),
(4023, 38, 53, '«Dette er det dere ble lovet til regnskapets dag!'),
(4024, 38, 54, 'Dette er Vår omsorg, som ikke tar ende.»'),
(4025, 38, 55, 'Slik er det. Men de oppsetsige har et dårlig sted å komme til,'),
(4026, 38, 56, 'helvete, hvor de stekes, et sørgelig hvilested!'),
(4027, 38, 57, 'Slik er det. Så la dem smake det, kokende vann og eiter,'),
(4028, 38, 58, 'og annet av samme slag, alt slått sammen.'),
(4029, 38, 59, 'Her er en flokk, som kommer hodekulls inn til dere! Det er ingen velkomsthilsen for dem, de skal stekes i Ilden.'),
(4030, 38, 60, 'De vil si: «Nei, ingen velkomsthilsen for dere! Det er dere som har stelt i stand dette for oss.» For et sørgelig sted å være!'),
(4031, 38, 61, 'Og de fortsetter: «Herre, gi dem som har stelt i stand dette for oss, dobbelt straff i Ilden!»'),
(4032, 38, 62, 'Og videre: «Hvordan kan det ha seg at vi ikke ser menn her,'),
(4033, 38, 63, 'som vi regnet blant de onde. Drev vi spott med dem med urette? Eller har vi sett forbi dem?»'),
(4034, 38, 64, 'Dette er visselig sant, helvetesfolket krangler innbyrdes.'),
(4035, 38, 65, 'Si: «Jeg er bare en advarer. Det er ingen gud utenom Gud, den Ene, den Uovervinnelige,'),
(4036, 38, 66, 'Herren over himlene og jorden, og det som mellom dem er, den Mektige, den Ettergivende.»'),
(4037, 38, 67, 'Si: «Det er stor meddelelse'),
(4038, 38, 68, 'som dere vender dere bort fra!'),
(4039, 38, 69, 'Jeg har ingen kunnskap om det himmelske råd, da de diskuterte.'),
(4040, 38, 70, 'Kun dette er meg åpenbart, jeg er bare en klar advarer.»'),
(4041, 38, 71, 'En gang sa Herren til englene: «Jeg vil skape mennesket av leire.'),
(4042, 38, 72, 'Når Jeg har formet ham og innblåst Min ånd i ham, så skal dere falle ham til fote.»'),
(4043, 38, 73, 'Og englene falt ned alle sammen,'),
(4044, 38, 74, 'unntatt Iblis. Han var hovmodig, og hørte til de vantro.'),
(4045, 38, 75, 'Han sa: «Nå Iblis, hva hindret deg i å falle ned for det Jeg har skapt med mine hender? Er du hovmodig, eller er du blitt stor på det?»'),
(4046, 38, 76, 'Han svarte: «Jeg er bedre enn ham! Du skapte meg av ild, og ham skapte Du av leire.»'),
(4047, 38, 77, 'Han sa: «Ut med deg herfra! Du er forkastet!'),
(4048, 38, 78, 'Min forbannelse skal hvile over deg til dommens dag.»'),
(4049, 38, 79, 'Han svarte: «Se det an med meg til den dag de gjenoppvekkes.»'),
(4050, 38, 80, 'Han sa: «Du er av dem det vil bli sett an med'),
(4051, 38, 81, 'til en bestemt tids dag.»'),
(4052, 38, 82, 'Han svarte: «Ved Din allmakt! Jeg skal føre dem vill alle sammen,'),
(4053, 38, 83, 'unntatt Dine oppriktige tjenere blant dem.»'),
(4054, 38, 84, 'Han sa: «Dette står fast, det Jeg sier står fast.'),
(4055, 38, 85, 'Jeg vil fylle helvete med deg og alle dem som følger deg.»'),
(4056, 38, 86, 'Si: «Jeg ber dere ikke om noen lønn for dette, og jeg er ikke av dem som gir seg av med geskjeftighet.»'),
(4057, 38, 87, 'Dette er en formaning for all verden.'),
(4058, 38, 88, 'Dere vil nok lære nyheten om den å kjenne etter noen tid!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'Skriftens åpenbaring er fra Gud, den Mektige, den Vise.'),
(4060, 39, 2, 'Vi har åpenbart deg skriften med sannheten, så tjen Gud, idet du vier Ham din religion i oppriktighet.'),
(4061, 39, 3, 'Tilhører ikke den oppriktige religion Gud? De som har valgt andre venner enn Ham: «Vi dyrker dem bare for at de skal føre oss nærmere Gud,» – Gud vil felle dom mellom dem i det de er uenige om. Gud leder ikke en utakknemlig løgner.'),
(4062, 39, 4, 'Om Gud ønsket å legge seg til barn, kunne Han ha utvalgt det Han ville, av det Han har skapt. Ære være Ham! Han er Gud, den Ene, den Uovervinnelige.'),
(4063, 39, 5, 'Han har i sannhet skapt himlene og jorden! Han svøper natten over dagen, og dagen over natten. De begge går med en fastsatt tid. Er ikke Han den Mektige, den Ettergivende?'),
(4064, 39, 6, 'Han har skapt dere fra ett individ. Av dette laget Han dets make. Og Han har sendt dere åtte husdyr i par. Han skaper dere i deres mødres liv, skapelse etter skapelse i trefoldig mørke. Dette er Gud, deres Herre! Ham tilhører herredømmet. Det er ingen gud utenom Ham. Hvordan kan dere la dere føres bort?'),
(4065, 39, 7, 'Om dere er utakknemlige, så er Gud uavhengig av dere. Men Han godtar ikke utakknemlighet hos Sine tjenere. Om dere viser takknemlighet, gir Han dere anerkjennelse for dette. Ingen kan bære en annens byrde! Så går deres hjemferd til Herren, og Han vil fortelle dere hva dere bedrev. Han kjenner det som bor i hjertene.'),
(4066, 39, 8, 'Når trengsel og nød rammer mennesket, påkaller han Herren i botferdighet. Senere, når Han sender ham en av Sine nådesbevisninger, glemmer han hva han tidligere bad om, og gir Gud likemenn for å lede bort fra Hans vei. Si: «Nyt livet i vantro, for en kort tid! Du hører til Ildens folk.»'),
(4067, 39, 9, 'Eller, den som viser ydmykhet i nattens timer, som faller ned eller står oppreist for Herren i engstelig respekt for det hinsidige, og håper på Herrens nåde. Si: «Er de vel like, de som vet og de som ikke vet?» Kun de som har hjertets forstand viser ettertanke.'),
(4068, 39, 10, 'Si: «Mine tjenere som tror, frykt Herren. De som handler godt i denne verden får godt igjen. Guds jord er vid. Og de standhaftige vil få sin lønn i fullt monn, uten å regne på det.»'),
(4069, 39, 11, 'Si: «Jeg er pålagt å tjene Gud i religiøs oppriktighet overfor Ham.'),
(4070, 39, 12, 'Jeg er pålagt å være den første som gir seg Ham i vold.»'),
(4071, 39, 13, 'Si: «Jeg frykter en svær dags straff, om jeg skulle være ulydig mot Herren.»'),
(4072, 39, 14, 'Si: «Gud tjener jeg, og jeg vier Ham i oppriktighet min religion.»'),
(4073, 39, 15, 'Så dyrk dere hva dere vil utenom Ham! Si: «De virkelige taperne er de som taper seg selv og sin familie på oppstandelsens dag!» Er ikke dette det åpenbare tap?'),
(4074, 39, 16, 'Under seg og over seg har de lag av ild. Slik setter Gud skrekk i Sine tjenere! «Mine tjenere, frykt Meg!»'),
(4075, 39, 17, 'Men de som holder seg unna avguder, og dyrkingen av dem, og vender seg i bot til Gud, for dem er godt i vente! Så gi Mine tjenere det gode budskap,'),
(4076, 39, 18, 'de som lytter til ordet og følger dets beste anvisninger. Disse er det som Gud veileder. Disse har hjertets forstand.'),
(4077, 39, 19, 'Men den, overfor hvem straffens ord blir realitet, kan du redde ham som er i Ilden?'),
(4078, 39, 20, 'Men de som frykter Herren, dem venter høye saler over saler, hvor bekker sildrer. Det er Guds løfte. Gud sviker ikke Sitt løfte.'),
(4079, 39, 21, 'Du har vel sett at Gud sender vann fra oven, og Han leder det frem til kilder på jorden? Så frembringer Han grøde av forskjellig slag. Siden visner den, og du ser den gulne, og så gjør Han den til sprø strå. I dette er visselig påminnelse for dem som har hjertets forstand.'),
(4080, 39, 22, 'Men den hvis hjerte Gud har åpnet for islam, så han vandrer i Herrens lys? Stakkars dem hvis hjerter er forherdet mot å komme Gud i hu! Disse er langt på villstrå.'),
(4081, 39, 23, 'Gud har åpenbart det skjønneste budskap, en Bok med indre samklang i sin hovedsak, som får dem som frykter Gud til å grøsse. Så blir huden og hjertene myke ved tanken på Gud. Dette er Guds ledelse. Ved denne leder Han dem Han vil. Den Gud lar seile sin egen sjø har ingen veileder.'),
(4082, 39, 24, 'Hva med den som prøver å verge ansiktet mot straffens onde på oppstandelsens dag? Da det blir sagt til de onde: «Smak nå det dere har fortjent.»'),
(4083, 39, 25, 'De som levde før dem, holdt sannhet for løgn, men så kom straffen over dem fra et hold de ikke ante,'),
(4084, 39, 26, 'og Gud lot dem smake fornedrelse i jordelivet. Men straffen i det hinsidige er visselig større, om de bare hadde visst.'),
(4085, 39, 27, 'Vi har laget all slags sammenligninger for folk i denne Koran, så de måtte komme til ettertanke.'),
(4086, 39, 28, 'En arabisk Koran, uten noe skjevt og vridd, så de måtte vise gudsfrykt.'),
(4087, 39, 29, 'Gud har laget en lignelse om en mann som tilhører uenige partnere, og en mann som tilhører bare én mann. Står disse to likt? Gud være lovet! Men folk flest ved ikke.'),
(4088, 39, 30, 'Du skal dø, og de skal også dø.'),
(4089, 39, 31, 'Så på oppstandelsens dag kan dere tviste med hverandre fremfor Herren.'),
(4090, 39, 32, 'Og hvem gjør større urett enn den som lyver mot Gud, og fornekter det sanne når det er kommet til ham? Er det ikke i helvete husly for de vantro?'),
(4091, 39, 33, 'Men den som kommer med det sanne og som stadfester det,'),
(4092, 39, 34, 'disse er de gudfryktige. De får alt de ønsker seg hos Herren. Dette er deres lønn som handler vel,'),
(4093, 39, 35, 'for Gud stryker ut deres dårligste gjerninger, og gir dem deres lønn for det beste de har gjort.'),
(4094, 39, 36, 'Er ikke Gud tilstrekkelig for Hans tjener? Men de forsøker å skremme deg med andre enn Ham. Den Gud lar seile sin egen sjø, har ingen veileder!'),
(4095, 39, 37, 'Men den som Gud veileder, har ingen som kan føre ham vill. Er ikke Gud mektig, Herre over gjengjeldelsen?'),
(4096, 39, 38, 'Om du spør dem: «Hvem skapte himlene og jorden?» Så vil de si: «Gud.» Si: «Hva mener dere da med de dere påkaller utenom, kan de fjerne hans nød? Eller om Han vil sende meg en nådesbevisning, kan de holde tilbake Hans nåde.» Si: «Gud er nok for meg! På Ham skal de som søker fortrøstning forlate seg.»'),
(4097, 39, 39, 'Si: «Mitt folk, dere får handle som dere kan.'),
(4098, 39, 40, 'Jeg handler også. Dere vil få å vite hvem som rammes av en straff som gjør ham til skamme, og hvem en varig straff overgår.'),
(4099, 39, 41, 'Vi har visselig åpenbart deg skriften med sannheten til menneskene. Den som lar seg veilede, gjør det til eget beste. Den som går på vidvanke, gjør det til egen belastning. Du har ikke ansvar for dem.'),
(4100, 39, 42, 'Gud tar til seg sjelene ved deres død, og også den som ikke er død, i dens søvn. Han holder tilbake den Han har bestemt døden for, og setter de andre fri til bestemt tid. I dette er visselig jærtegn for mennesker som tenker etter.'),
(4101, 39, 43, 'Har de funnet noen til å tale deres sak, utenom Gud? Si: ’Og det selv om de ikke formår det ringeste, og intet forstår?’'),
(4102, 39, 44, 'Si: ’De virksomme ord beror alle hos Gud! Hans er herredømmet over himmel og jord! Og til Ham blir dere brakt tilbake.’»'),
(4103, 39, 45, 'Når Gud omtales, alene, krymper hjertene seg hos dem som ikke tror på det hinsidige. Men hvis medgudene nevnes, da gleder de seg!'),
(4104, 39, 46, 'Si: «Herre Gud, himlenes og jordens skaper, Du som kjenner det skjulte og det åpenbare, Du vil dømme mellom Dine tjenere i det de var uenige.»'),
(4105, 39, 47, 'Om de urettferdige eide alt som på jorden er, og like meget til, så ville de tilby det som løsepenger fra straffens onde på oppstandelsens dag! De vil få se fra Gud det de aldri regnet med.'),
(4106, 39, 48, 'De vil få se det onde de har pådratt deg, og det de drev ap med ringer dem inn.'),
(4107, 39, 49, 'Når trengsel og ulykke kommer over mennesket, så påkaller han Oss. Men når Vi så viser ham nåde, så sier han: «Dette har jeg bare fått på grunn av kunnskap.» Nei, det er en prøvelse, men folk flest vet det ikke.'),
(4108, 39, 50, 'De som levde før dem, har allerede sagt det samme, og alt de hadde ervervet, hjalp dem ikke.'),
(4109, 39, 51, 'Men det onde de hadde pådratt seg, rammet dem! Og de som nå handler ondt, vil bli rammet av det onde de pådrar seg, de kan ikke forpurre noe!'),
(4110, 39, 52, 'De vet vel at Gud gir rikelig av sine gaver til den Han vil, eller begrenser? I dette er jærtegn for folk som tror.'),
(4111, 39, 53, 'Si: «Dere Mine tjenere, som har vist lettsindighet som vil ramme dere selv, fortvil ikke for Guds nåde. Gud vil forlate alle synder, Han er den Ettergivende, den Nåderike.'),
(4112, 39, 54, 'Vend dere i bot til Herren, og overgi dere til Ham, før straffen er over dere. For da kan dere ikke hjelpes.'),
(4113, 39, 55, 'Følg det beste, som er åpenbart for dere fra Herren, før straffen kommer over dere plutselig, uten at dere aner noe.'),
(4114, 39, 56, 'For at ingen må si: ’Trøste meg for det jeg forsømte overfor Gud! Jeg var visselig av dem som spottet,’'),
(4115, 39, 57, '– eller si: ’Hvis bare Gud hadde ledet meg, ville jeg vært av de gudfryktige,’'),
(4116, 39, 58, '– eller si når han ser straffen: ’Å, kunne jeg bare vende tilbake, så jeg kunne bli av dem som handler vel!’»'),
(4117, 39, 59, '«Å nei, Mitt ord kom til deg, og så kalte du det løgn, og viste hovmod og var vantro.»'),
(4118, 39, 60, 'På oppstandelsens dag vil du se dem som løp mot Gud med mørke ansikter. Er det ikke i helvete husly for de hovmodige?'),
(4119, 39, 61, 'Men Gud vil frelse dem som viste gudsfrykt, for deres innsats! Ondt skal ikke røre dem, ei heller skal de sørge.'),
(4120, 39, 62, 'Gud er alle tings skaper! Han tar seg av alle ting!'),
(4121, 39, 63, 'Ham tilhører himlenes og jordens nøkler. De som fornekter Guds ord, disse er taperne.'),
(4122, 39, 64, 'Si: «Vil dere pålegge meg å dyrke noen annen enn Gud, dere uvitende?»'),
(4123, 39, 65, 'Det er åpenbart for deg, og dem som levde før deg: «Hvis du gir Gud medguder, er visselig dine gjerninger til fånyttes, og du vil bli blant taperne.»'),
(4124, 39, 66, 'Nei. Gud skal du tjene, og vis takknemlighet!'),
(4125, 39, 67, 'Folk verdsetter ikke Gud med hans sanne verdi! Hele jorden blir en håndfull for Ham på oppstandelsens dag, og himlene blir rullet opp i Hans høyre! Ære være Ham! Høyt hevet er Han over det de setter ved Hans side.'),
(4126, 39, 68, 'Det støtes i basunen, og alle som er i himlene og på jord faller som rammet av lynet, unntatt den Gud bestemmer. Så støtes det annen gang, og se, de er stående og ser seg om.'),
(4127, 39, 69, 'Jorden vil stråle i Herrens lys! Regnskapsboken legges på plass, og profetene og vitnene bringes frem. Og det vil bi avsagt dom blant menneskene med rettferd, og de vil ikke lide urett!'),
(4128, 39, 70, 'Enhver skal få full belønning for det han har gjort. Han kjenner vel til hva de gjør.'),
(4129, 39, 71, 'De vantro drives i skarer til helvete, og når de kommer dit, åpnes dets porter, og vokterne vil si til dem: «Kom det ingen sendebud til dere fra deres egen krets, for å fremlese Herrens ord for dere, og advare dere mot denne dags møte?» De vil svare: «Jo!» men straffens ord er nå blitt realitet for den vantro.'),
(4130, 39, 72, 'Man sier: «Gå inn gjennom helvetes porter for der å være og bli!»'),
(4131, 39, 73, 'Men de som frykter Herren, føres i skarer til paradisets have, og når de kommer dit, åpnes deres porter, og vokterne vil si til dem: «Fred være med dere! Dere har gjort godt, så kom inn i haven, for der å være og bli!»'),
(4132, 39, 74, 'Og de vil si: «Gud skje lov og pris! Han har oppfylt sitt løfte til oss og gitt oss jorden i arv! Vi kan slå oss ned hvor vi ønsker i haven!» Hvilken herlig lønn for dem som strever!'),
(4133, 39, 75, 'Og du vil se englene kretsende om tronen, idet de synger Herrens pris. Og det vil bli avsagt dom blant dem med rettferd, og det lyder: «Gud være lovet, all verdens Herre!»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'Ha Mim'),
(4135, 40, 2, 'Skriftens åpenbaring er fra Gud, den Mektige, den Allvitende,'),
(4136, 40, 3, 'som tilgir synder, som godtar omvendelse, som er streng med straffen, som er langmodig. Det er ingen gud utenom Ham. Hos Ham ender ferden!'),
(4137, 40, 4, 'Kun vantro strides om Guds ord. La deg ikke lure av deres farting omkring i landet!'),
(4138, 40, 5, 'Før dem holdt Noas folk sannhet for løgn, og etter dem folkene som stod imot. Hvert samfunn ønsket å ta fatt sitt sendebud, og de argumenterte med verdiløs tomhet for å bringe sannheten til fall. Men så tok Jeg dem fatt, og hvordan var vel Min straff!'),
(4139, 40, 6, 'Slik ble Herrens ord realitet for de vantro, så de blir Ildens folk.'),
(4140, 40, 7, 'De engler som bærer tronen og de som er rundt den, lovpriser Herren og tror på Ham, og ber om tilgivelse for de troende: «Herre, Du omfatter alt i nåde og visdom. Så tilgi dem som har omvendt seg og følger Din vei, og bevar dem for helvetes straff!'),
(4141, 40, 8, 'Herre, før dem inn i Edens haver, som Du har lovet dem og de rettferdige blant deres fedre, hustruer og barn! For Du er den Mektige, den Vise.'),
(4142, 40, 9, 'Bevar dem for misgjerninger! Den Du verger mot ondt, på denne dag, ham har Du sannelig vist nåde! Dette er den store seier.»'),
(4143, 40, 10, 'Det vil bli ropt til de vantro: «Guds motvilje er større enn den motvilje dere viste mot egne interesser da dere ble kalt til troen, men forble vantro.»'),
(4144, 40, 11, 'De vil si: «Herre, Du har latt oss dø to ganger, og har to ganger gitt oss liv. Vi erkjenner våre synder. Er det noen mulighet til å komme ut?»'),
(4145, 40, 12, 'Dette fordi, da Gud alene ble påkalt, forble dere vantro, men hvis Han ble gitt medguder, så trodde dere. Dommen tilhører Gud, den Opphøyde, den Store.'),
(4146, 40, 13, 'Han er det, som viser dere Sine jærtegn, og sender dere det dere trenger fra oven. Men ingen kommer til ettertanke unntatt den som gjør bot.'),
(4147, 40, 14, 'Så påkall Gud idet dere vier Ham religiøs oppriktighet, selv om det er de vantro imot!'),
(4148, 40, 15, 'Høyt i rang er Han, tronens Herre! Han sender Ånden av Sin ordning til hvem Han vil av Sine tjenere, så Han kan advare dem mot møtets dag.'),
(4149, 40, 16, 'Den dag de skal tre frem, og intet hos dem er skjult for Gud. Hvem tilhører herredømmet denne dag? Det tilhører Gud, den Ene, den Uovervinnelige.'),
(4150, 40, 17, 'På denne dag vil enhver bli belønnet for det han har fortjent. Ingen urett på denne dag! Gud er rask i avregningen.'),
(4151, 40, 18, 'Så advar dem mot den tilstundende dag, når hjertet sitter i halsen så de intet får frem. De urettferdige har ingen varm venn, og ingen som taler deres sak, som blir hørt.'),
(4152, 40, 19, 'Han kjenner de forræderske øyekast, og hva hjertene skjuler.'),
(4153, 40, 20, 'Gud vil dømme med rettferd. De, som de påkaller utenom Ham, har intet med å dømme. Gud er den Hørende, den Seende.'),
(4154, 40, 21, 'Har de da ikke reist rundt jorden, og sett hva enden ble for dem som levde før dem? Disse var større enn dem i makt, og satte større spor etter seg på jorden. Men Gud tok dem fatt for deres synder, og de hadde ingen som kunne verge dem mot Gud.'),
(4155, 40, 22, 'Dette fordi sendebudene deres kom til dem med klar beskjed, men de trodde ikke, og Gud tok dem fatt. Han er sannelig sterk, og streng med straffen.'),
(4156, 40, 23, 'Vi sendte også Moses med Våre tegn og klar fullmakt'),
(4157, 40, 24, 'til Farao, Haman og Kora, men de sa: «En løgnaktig tryllekunstner!»'),
(4158, 40, 25, 'Og da han brakte dem sannheten fra Oss, sa de: «Slå i hjel sønnene til dem som tror sammen med ham, men la deres kvinner leve.» Men de vantros knep er helt forfeilet.'),
(4159, 40, 26, 'Og Farao sa: «La meg drepe Moses, og la ham bare påkalle sin Herre. Jeg er redd for at han vil bytte ut deres religion, eller fremkalle ufred og elendighet i landet.»'),
(4160, 40, 27, 'Og Moses sa: «Jeg søker tilflukt hos min Herre og deres Herre mot enhver hovmodig, som ikke tror på regnskapets dag.»'),
(4161, 40, 28, 'Da sa en troende av Faraos folk, som skjulte sin tro: «Vil dere drepe en mann fordi han sier: ’Min Herre er Gud!’ Og han er kommet med klar beskjed fra deres Herre? Er han en løgner, så er det hans egen sak. Men snakker han sant, så vil noe av det han truer med ramme dere. Gud leder ikke den som er lettsindig og løgnaktig.'),
(4162, 40, 29, 'Mitt folk, i dag er makten deres og dere er herrer i landet. Men hvem vil hjelpe oss mot Guds voldsmakt, om den kommer over oss?» Men Farao sa: «Jeg viser dere bare til det jeg selv mener. Jeg leder dere bare på rett vei.»'),
(4163, 40, 30, 'Den som trodde, sa: «Mitt folk, jeg er redd for at det vil ramme dere det samme som rammet folkene som var imot, i deres tid,'),
(4164, 40, 31, 'det samme som Noas folk, Ad, og Thamod, og dem som kom etter dem. Gud ønsker ikke urettferdighet mot Sine tjenere.'),
(4165, 40, 32, 'Mitt folk, jeg frykter for dere ropenes dag,'),
(4166, 40, 33, 'den dag dere vender dere til flukt, og ikke har noen til å beskytte dere mot Gud. Den Gud lar seile sin egen sjø, har ingen veileder.'),
(4167, 40, 34, 'Josef kom jo tidligere med klar beskjed, men dere forble i tvil om det han brakte dere, inntil, da han døde, da sa dere: ’Gud vil aldri sende et sendebud etter ham.’ Slik lar Gud fare vill den som er lettsindig og tvilende.»'),
(4168, 40, 35, 'De som diskuterer om Guds ord uten at noen autoritet er kommet til dem, stor er avskyen hos Gud og de troende. Slik setter Gud segl på hvert hovmodig og arrogant hjerte.'),
(4169, 40, 36, 'Farao sa: «Haman, bygg meg et tårn så jeg kan nå repene,'),
(4170, 40, 37, 'himlenes rep, og stige opp til Mose Gud, for jeg tror han farer med løgn!» Slik ble det onde i hans gjerninger gjort prydelig for Farao, og han ble sperret ute fra veien. Og Faraos knep førte bare til forderv.'),
(4171, 40, 38, 'Da sa han som trodde: «Mitt folk, følg meg, og jeg vil føre dere til det rettes vei!'),
(4172, 40, 39, 'Mitt folk, dette jordelivet er bare en kort nytelse, men i det hinsidige er et varig hjem.'),
(4173, 40, 40, 'Den som gjør en ond gjerning, belønnes bare med dens like, men den som handler rett, mann eller kvinne, og er troende, disse føres inn i paradisets haver, og der blir de sørget for, uten at det regnes på det.'),
(4174, 40, 41, 'Mitt folk, hvordan har det seg med meg, jeg kaller dere til frelse, og dere kaller meg til Ilden?'),
(4175, 40, 42, 'Dere kaller meg til å fornekte Gud og gi Ham medguder som jeg ikke vet noe om, mens jeg kaller dere til den Mektige, Benåderen.'),
(4176, 40, 43, 'Det står fast, at det dere kaller meg til, har ingen appell, verken i denne verden eller den hinsidige, og at til Gud skal vi vende tilbake, og at de lettsindige, disse er Ildens folk.'),
(4177, 40, 44, 'Dere vil komme i hu det jeg har sagt til dere. Jeg overgir min sak i Guds hånd. Gud holder øye med Sine tjenere.»'),
(4178, 40, 45, 'Og Gud bevarte ham mot deres onde renker, og en slem straff omsluttet Faraos folk,'),
(4179, 40, 46, 'Ilden, som de utsettes for sent og tidlig. Og den dag når timen kommer: «Før Faraos folk til den skarpeste straff!»'),
(4180, 40, 47, 'Når de tretter med hverandre i Ilden, vil de svake i samfunnet si til de hovmodige: «Vi var deres følgesvenner! Kan dere skaffe oss av med noe av Ilden?»'),
(4181, 40, 48, 'Og de hovmodige svarer: «Vi er alle i den. Gud har allerede felt dom blant Sine tjenere.»'),
(4182, 40, 49, 'De som er i Ilden, vil si til helvetes voktere: «Kall på deres Herre at Han må ettergi oss en dag av straffen.»'),
(4183, 40, 50, 'De vil svare: «Brakte ikke deres sendebud dere klar beskjed?» Og de vil si: «Jo,» og vokterne fortsetter: «Så påkall, dere!» Men de vantros påkallen er helt fåfengt.'),
(4184, 40, 51, 'Vi skal visselig bistå Våre sendebud og de troende i dette liv, og på den dag når vitnene står frem,'),
(4185, 40, 52, 'den dag når deres unnskyldninger ikke gagner de urettferdige, og forbannelsen og et ondt oppholdssted blir dem til del.'),
(4186, 40, 53, 'Vi gav også Moses ledelsen, og Vi lot Israels barn arve skriften'),
(4187, 40, 54, 'til ledelse og formaning for dem som har hjertets forstand.'),
(4188, 40, 55, 'Så vær tålmodig! Guds løfte er sannhet! Be om tilgivelse for din synd, og lovpris Herren morgen og kveld.'),
(4189, 40, 56, 'De som diskuterer om Guds tegn uten at autoritet er blitt dem til del, i deres hjerter er kun hovmod som de aldri kan overvinne. Søk tilflukt hos Gud. Han er den Hørende, den Seende.'),
(4190, 40, 57, 'Sannelig, skapelsen av himlene og jorden er større verk enn skapelsen av menneskene. Men folk flest vet det ikke.'),
(4191, 40, 58, 'Den blinde og den seende er ikke like, og heller ikke de som tror og handler rettskaffent, og den som gjør ondt. Lite tenker dere etter.'),
(4192, 40, 59, 'Timen vil visselig komme, det er ingen tvil om det, men folk flest tror ikke.'),
(4193, 40, 60, 'Herren sier: «Be til Meg, og Jeg vil bønnhøre dere. De som er for stolte til å tjene Meg, vil gå inn i helvete i ydmykelse.»'),
(4194, 40, 61, 'Gud er det, som har gjort natten til hvile for dere, og dagen til å gi lys. Gud har godhet for menneskene, men folk flest er utakknemlige.'),
(4195, 40, 62, 'Dette er Gud, deres Herre, alle tings Skaper. Det er ingen Gud utenom Han. Hvordan kan dere være så forvridde?'),
(4196, 40, 63, 'Slik forvris de som bestrider Guds ord.'),
(4197, 40, 64, 'Gud er det, som har gjort jorden til et fast bosted for dere, og himmelen til et overbygg. Og Han formet dere, og formet dere vel, og gav dere gode ting til underhold. Dette er Gud, deres Herre. Velsignelsesrik er Gud, all verdens Herre!'),
(4198, 40, 65, 'Han er den Levende. Det er ingen gud utenom Ham. Så påkall Ham, idet dere vier Ham religiøs oppriktighet. Lov og pris tilhører Gud, all verdens Herre.'),
(4199, 40, 66, 'Si: «Jeg er blitt forbudt å dyrke dem dere påkaller utenom Gud, etter at klar beskjed er kommet til meg fra min Herre, og jeg er påbudt å gi meg inn under all verdens Herre.»'),
(4200, 40, 67, 'Han er det, som skapte dere av jord, så av en sæddråpe, så av et begynnende foster, og så bringer Han dere frem som spedbarn, for at dere så skal nå manndomskraft, for så å bli gamle, om enn noen av dere dør før denne tid, for at dere skal nå en bestemt frist, så dere må forstå.'),
(4201, 40, 68, 'Han er det, som gir liv, og lar dø. Når Han bestemmer en ting, sier Han bare: «Bli!» – og så er den.'),
(4202, 40, 69, 'Du har vel sett dem som diskuterer om Guds ord? Hvordan de lar seg bringe på avveie?'),
(4203, 40, 70, 'De som holder skriften for løgn og det Vi har sendt Våre sendebud med, skal snart få å vite!'),
(4204, 40, 71, 'Når de får lenker og kjeder om halsen, og de slepes inn'),
(4205, 40, 72, 'i kokende vann, og så styrtes i Ilden.'),
(4206, 40, 73, 'Så blir det sagt til dem: «Hvor er nå deres medguder?»'),
(4207, 40, 74, 'Og de vil svare: «De har latt oss i stikken! Nei, vi påkalte tidligere intet.» Slik lar Gud de vantro fare vill.'),
(4208, 40, 75, '«Dette fordi dere frydet dere på jorden uten rett, og var overmodige.'),
(4209, 40, 76, 'Gå inn gjennom helvetes porter, for der å være og bli!» Et sørgelig husly for de hovmodige!'),
(4210, 40, 77, 'Så vær tålmodig! Guds løfter er sannhet. Om Vi lar deg se noe av det Vi har stilt dem i utsikt, eller Vi kaller deg bort, til Oss blir de brakt tilbake.'),
(4211, 40, 78, 'Vi sendte sendebud før deg. Noen har Vi fortalt deg om, andre har Vi ikke omtalt for deg. Intet sendebud får komme med et tegn uten Guds bifall. Når så Guds beslutning inntreffer, vil det bli dømt med rettferd. Da taper de som fremmet falskhet og løgn.'),
(4212, 40, 79, 'Gud er det, som har gitt dere kveget, så dere kan ri på noe av det, og spise annet.'),
(4213, 40, 80, 'Dere har det også til annen nytte, og til å utrette gjøremål som dere har på hjertet ved hjelp av det. På det og på skipene blir dere båret av sted.'),
(4214, 40, 81, 'Han lar dere se Sine jærtegn. Hvilke av Guds jærtegn vil dere benekte?'),
(4215, 40, 82, 'Har de ikke reist rundt på jorden og sett hva enden ble for dem som levde før dem? Disse var tallrikere enn dem, og større i makt, og satte større spor etter seg på jorden. Men det hjalp dem ingenting, alt de hadde ervervet.'),
(4216, 40, 83, 'Så da deres sendebud kom til dem med klar beskjed, var de glade og fornøyde med det de hadde av kunnskap, men det de drev ap med, ringet dem inn.'),
(4217, 40, 84, 'Og da de stod overfor Vår voldsmakt, sa de: «Vi tror på Gud alene, og forkaster alt vi satte ved Hans side.»'),
(4218, 40, 85, 'Men deres tro nyttet dem ikke, da de stod overfor Vår voldsmakt, Guds vanlige praksis fra gammel tid overfor Sine tjenere. Da taper de som er vantro.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'Ha Mim'),
(4220, 41, 2, 'En åpenbaring fra den Barmhjertige, den Nåderike,'),
(4221, 41, 3, 'en Bok, hvis ord er klargjort som en arabisk Koran, for mennesker som har innsikt,'),
(4222, 41, 4, 'med godt budskap og advarsler, men de fleste vender seg bort, og de hører ikke.'),
(4223, 41, 5, 'De sier: «Våre hjerter er tildekket mot det du kaller oss til, og det er tunghørthet i våre ører, og et slør mellom oss og deg. Gjør det du gjør. Vi gjør det vi gjør.»'),
(4224, 41, 6, 'Si: «Jeg er bare et vanlig menneske som dere. Meg er åpenbart at deres Gud er én Gud, så ta rett vei til Ham, og be Ham om tilgivelse! Ve over avgudsdyrkerne,'),
(4225, 41, 7, 'som ikke gir det rituelle bidrag, og fornekter det hinsidige!'),
(4226, 41, 8, 'Men de som tror, og lever rettskaffent, de får den lønn som tilkommer dem.»'),
(4227, 41, 9, 'Si: «Fornekter dere virkelig Ham, som skapte jorden på to dager, og gir Ham likemenn?» Han er all verdens Herre!'),
(4228, 41, 10, 'Han plasserte de faste fjell høyt opp på den, og velsignet den, og anordnet en levemåte på den på fire dager, til alle som søker på like fot.'),
(4229, 41, 11, 'Så drog Han til himmelen, som var røyk og damp, og sa til den og til jorden: «Kom igjen, godvillig eller motvillig!» De svarte: «Vi kommer godvillig.»'),
(4230, 41, 12, 'Og Han gjorde dem ferdig som syv himler på to dager, og Han inngav hver himmel dens oppgave. Vi prydet den nederste himmel med lysbluss, også til vern. Dette er anordningen til den Mektige, den Allvitende.'),
(4231, 41, 13, 'Om de vender seg bort, så si: «Jeg advarer dere mot et tordenskrall, lik det som rammet Ad og Thamod.»'),
(4232, 41, 14, 'Da deres sendebud kom til dem fra alle kanter: «Dyrk ingen annen enn Gud!» Da svarte de: «Om Herren hadde villet, ville Han sikkert sendt ned engler! Vi tror ikke på det budskap dere er sendt med.»'),
(4233, 41, 15, 'Hva Ad angår, så viste de uberettiget hovmod på jorden, og sa: «Hvem er større enn oss i makt?» Hadde de da ikke innsett at Gud, som skapte dem, var større enn dem i makt? Og de fornektet Vårt ord.'),
(4234, 41, 16, 'Vi sendte over dem en rasende vind i uhellsvangre dager for å la dem smake fornedrelsens straff i jordelivet. Men straffen i det hinsidige er mer fornedrende, og de kan ikke hjelpes.'),
(4235, 41, 17, 'Hva Thamod angår, så gav Vi dem ledelse, men de foretrakk blindhet fremfor ledelse. Så tok den fornedrende straffs tordenskrall dem, for det de hadde fortjent.'),
(4236, 41, 18, 'Men Vi reddet dem som trodde og var gudfryktige.'),
(4237, 41, 19, 'På den dag, når Guds fiender samles hen til Ilden gruppevis,'),
(4238, 41, 20, 'og når de så når frem til den, vil deres ører, øyne og hud bære vitnemål mot dem om hva de bedrev.'),
(4239, 41, 21, 'Og de vil si til huden: «Hvorfor vitner dere mot oss?» Den vil svare: «Gud lar oss tale, Han, som får alt til å tale. Han skapte dere første gang, og til Ham må dere vende tilbake.'),
(4240, 41, 22, 'Dere kunne ikke skjule dere fra at ører, øyne og hud vitner mot dere, men dere trodde at Gud ikke ville få vite stort av det dere gjør.'),
(4241, 41, 23, 'Og denne deres formodning, som dere hadde om Herren, har brakt dere i fordervelsen, og dere ble av taperne.»'),
(4242, 41, 24, 'Om de turer frem, så er Ilden husly for dem. Og om de ber om nåde, så blir de ikke hørt.'),
(4243, 41, 25, 'Vi lot dem få «kamerater», og de pyntet opp for dem, både det de hadde foran seg og bak seg. Så ble ordet realitet for dem, om henfarne samfunn av dsjinner og folk. De var visselig tapere.'),
(4244, 41, 26, 'De vantro sier: «Hør ikke på denne Koranen! Slå den hen i tomt prat, så kanskje dere vinner frem.»'),
(4245, 41, 27, 'Men Vi skal visselig la de vantro smake en svær straff! Og la dem få igjen for sine verste handlinger.'),
(4246, 41, 28, 'Dette er Guds fienders belønning, Ilden. Der har de varig bosted, som belønning for at de fornektet Vårt ord.'),
(4247, 41, 29, 'Og de vantro vil si: «Herre, vis oss dem som førte oss vill, av dsjinner og mennesker, så vi kan legge dem under våre føtter, så de kan bli av dem dypest nede.»'),
(4248, 41, 30, 'Men til dem som sier: «Vår Herre er Gud,» og så holder rett kurs, stiger englene ned: «Frykt ikke, og vær ikke bedrøvet! Fryd dere ved paradiset, som er lovet dere.'),
(4249, 41, 31, 'Vi tar oss av dere i denne verden og den hinsidige. Der vil dere få alt deres hjerter begjærer, og alt dere ber om,'),
(4250, 41, 32, 'som gjestfrihet fra en som er tilgivende, nåderik.»'),
(4251, 41, 33, 'Hvem taler vakrere ord enn den som kaller til Gud, lever rettskaffent, og sier: «Jeg har overgitt meg til Gud?»'),
(4252, 41, 34, 'En god og en ond gjerning er ikke like. Vis det onde tilbake med det som bedre er, og se, den du lever i fiendskap med, blir som en varm venn!'),
(4253, 41, 35, 'Men dette gis bare dem som viser tålmod. Det gis bare den som har en stor gave.'),
(4254, 41, 36, 'Om en djevelens provokasjon skulle slite i deg, så søk tilflukt hos Gud. Han er den Hørende, den Allvitende!'),
(4255, 41, 37, 'Av Hans tegn er natten og dagen, solen og månen. Fall ikke ned for sol og måne, men fall ned for Gud, som skapte dem, om det er Ham dere tilber.'),
(4256, 41, 38, 'Men om noen er for hovmodige, så lovpriser de som er hos Herren Ham natt og dag, og de blir ikke trette.'),
(4257, 41, 39, 'Av Hans tegn er at du ser jorden ligge livløs og uanselig, men så sender Vi vann over den, og den kommer til liv, og grønnes. Han, som gir den liv, gir også de døde liv. Han evner alt!'),
(4258, 41, 40, 'De som forvrir Vårt ord, er ikke skjult for oss. Er vel den som kastes i Ilden bedre enn den som med trygghet står frem på oppstandelsens dag? Gjør hva dere vil! Han ser hva dere gjør.'),
(4259, 41, 41, 'Se, de som ikke tror på formaningen når den er kommet til dem!'),
(4260, 41, 42, 'Det er visselig en mektig skrift! Falskhet når den ikke fra noen kant! En åpenbaring fra en som er vis, prisverdig!'),
(4261, 41, 43, 'Intet blir meddelt deg som ikke er meddelt sendebud før deg. Herren har tilgivelse, og også en smertelig straff.'),
(4262, 41, 44, 'Hadde Vi gjort den til en Koran på fremmed språk, ville de sagt: «Hvorfor er ikke dens ord gjort forståelige? Et fremmed språk, og en araber?» Si: «Den er ledelse og legedom for dem som tror.» Men de som ikke tror, har tunghørthet i sine ører, og de er blinde for den. De er som folk som kalles fra et sted i det fjerne.'),
(4263, 41, 45, 'Vi gav Moses skriften, og det ble uenighet om den. Hadde det ikke foreligget et ord fra Herren, ville saken blitt opp- og avgjort mellom dem. De er visselig i betenkelig tvil om den.'),
(4264, 41, 46, 'Om noen handler rettskaffent, så er det til egen vinning. Om noen handler ondt, så er det til egen belastning. Gud er ikke den som gjør Sine tjenere urett.'),
(4265, 41, 47, 'Kunnskap om timen beror hos Ham. Ingen frukt kommer ut av sitt hylster, og intet hunkjønn blir svanger eller føder uten Hans viten. På den dag da Han roper til dem: «Hvor er Mine medguder?» Da vil de svare: «Vi forsikrer Deg, ingen av oss kan bevitne det!»'),
(4266, 41, 48, 'Og det de påkalte tidligere er borte for dem, og de innser at de har ingen utvei.'),
(4267, 41, 49, 'Mennesket blir ikke trett av å be om godt, men om noe ondt rammer ham, blir han fortvilet og mismodig.'),
(4268, 41, 50, 'Og lar Vi ham smake en nådesbevisning fra Oss etter motgang har rammet ham, så vil han visselig si: «Dette tilkommer meg! Jeg tror ikke at timen inntreffer. Om jeg bringes tilbake til Herren, får jeg nok det beste hos Ham.» Men da skal Vi visselig fortelle de vantro hva de bedrev, og Vi skal la dem smake en hard straff.'),
(4269, 41, 51, 'Viser Vi mennesket godhet, vender han seg bort, og trekker seg unna, men når ondt rammer ham, er han full av bønner.'),
(4270, 41, 52, 'Si: «Hva mener dere? Hvis det kommer fra Gud, og dere så fornekter det? Hvem er mer villfarende enn den som går langt i tross?»'),
(4271, 41, 53, 'Vi skal vise dem Våre tegn på alle horisonter, og i dem selv, til det blir klart for dem at det er sannhetens realitet. Er det ikke nok at Herren er vitne til alle ting?'),
(4272, 41, 54, 'Er de da i tvil om møtet med Herren? Er det ikke slik at Han omslutter alle ting?');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'Ha Mim'),
(4274, 42, 2, '‘Ayn Sin Qaf'),
(4275, 42, 3, 'Således gir Gud, den Mektige, den Vise, åpenbaringer til deg, og dem som levde før deg.'),
(4276, 42, 4, 'Ham tilhører alt i himlene og på jord! Han er den Opphøyde, den Veldige!'),
(4277, 42, 5, 'Det er som om himlene spaltes over dem, når englene lovsynger sin Herre og ber om tilgivelse for dem på jorden. Gud er visselig den Ettergivende, den Nåderike.'),
(4278, 42, 6, 'De som har lagt seg til andre venner enn Ham, dem passer Gud på. Og du er ingen ombudsmann for dem.'),
(4279, 42, 7, 'Så har Vi da åpenbart deg en Koran på arabisk, slik at du kan advare byenes mor, Mekka, og dem omkring den, at du kan advare mot samlingens dag, hvorom ingen tvil hersker. En del vil gå til paradisets have, og en del til Ilden!'),
(4280, 42, 8, 'Om Gud ville, kunne han gjort dem til et eneste (tros)samfunn. Men Han førte dem Han vil inn i Sin nåde, og de urettferdige har ingen som tar seg av deres sak, og ingen hjelper.'),
(4281, 42, 9, 'Har de funnet seg venner utenom Ham? Men Gud, Han er Vennen! Han gir de døde liv, Han evner alt!'),
(4282, 42, 10, 'Hva dere enn strides om, dets avgjørelse beror hos Gud. Dette er Gud, min Herre! Ham forlater jeg meg på, og til Ham vender jeg meg i bot.'),
(4283, 42, 11, 'Himlenes og jordens skaper! Han har frembrakt for dere, dere selv og kveget parvis, og formerer dere derved. Det er ingenting som Han! Han er den Hørende, den Seende'),
(4284, 42, 12, 'Han har himlenes og jordens nøkler. Han gir sitt underhold rikelig eller tilmålt til den Han vil. Han vet om alle ting!'),
(4285, 42, 13, 'Han har fastsatt for dere som religion det Han bestemte for Noa, det Vi har åpenbart for deg, og det Vi bestemte for Abraham og Moses, og Jesus: «Overhold religionen! Unngå splittelser deri!» Det er hardt for avgudsdyrkerne, det du kaller dem til. Gud utvelger for dette dem Han vil, og leder dit de botferdige.'),
(4286, 42, 14, 'Først etter at kunnskapen var blitt dem til del, henfalt de i splittelser i gjensidig nid. Hadde det ikke vært for et ord som forelå for Herren, for en bestemt frist, så ville det vært truffet avgjørelse mellom dem. Men de som fikk skriften i arv etter dem, huser betenkelige tvil derom.'),
(4287, 42, 15, 'Så fremfør ditt kall, og hold rett kurs, slik du er pålagt. Følg ikke deres ideer, men si: «Jeg tror på en skrift som Gud har åpenbart! Jeg er pålagt å opptre rettferdig blant dere, Gud er vår Herre, og deres Herre. Vi har våre gjerninger, og dere deres! Det er ingen plass for strid mellom oss. Gud samler oss sammen. Hos Ham er reisen slutt.»'),
(4288, 42, 16, 'De som diskuterer om Gud etter at det er sagt «ja» til Ham, deres argumenter er uten verdi hos Herren. Vrede skal være over dem, og de har en streng straff i vente.'),
(4289, 42, 17, 'Det er Gud som har åpenbart skriften med sannheten, og rettesnoren. Hvem vet, kanskje er timen nær?'),
(4290, 42, 18, 'De som ikke tror på den, ber om å få den påskyndet, men de som tror på den, engster seg for den og vet at den er sannhet. Sannelig, de som tviler på timen, er kommet langt på avveie.'),
(4291, 42, 19, 'Gud er god mot Sine tjenere. Han gir livsens underhold til den Han vil. Han er den Sterke, den Mektige.'),
(4292, 42, 20, 'Den som ønsker å dyrke det hinsidige livs marker, for ham øker Vi avkastningen. Den som ønsker å dyrke jordelivets marker, ham gir Vi derav. Men han har ingen andel i det hinsidige.'),
(4293, 42, 21, 'Er det så at de har medguder som har bestemt for dem en religion som Gud ikke har tillatt? Hadde det ikke vært for et avgjørende ord, ville det vært truffet avgjørelse mellom dem. De urettferdige har i vente en smertelig straff.'),
(4294, 42, 22, 'Du ser de vantro engstes for det de har pådratt seg, og som vil falle over dem. Men de som tror og lever rettskaffent, vil være i paradisets enger. De får alt de ønsker hos Herren. Dette er den store godhet.'),
(4295, 42, 23, 'Det er det gode budskap som Gud gir Sine tjenere, som tror og lever rettskaffent. Si: «Jeg ber dere ikke om lønn for dette annet enn slektsbånds godhet!» Den som erverver en god gjerning, vil Vi gi godt i tillegg til den. Gud er tilgivende, full av anerkjennelse.'),
(4296, 42, 24, 'Kanskje folk sier: «Han har oppdiktet en løgn om Gud?» Om Gud ville, kunne Han forsegle ditt hjerte! Men Gud utvisker verdiløs intethet, og realiserer sannheten ved Sitt ord! Han vet om det som bor i hjertene.'),
(4297, 42, 25, 'Han er det som mottar omvendelse fra Sine tjenere, og unnskylder misgjerninger. Han vet hva dere bedriver.'),
(4298, 42, 26, 'Han bønnhører dem som tror og lever rettskaffent og gir dem mer til av Sin overflod. Men de vantro, dem venter en svær straff.'),
(4299, 42, 27, 'Hadde Gud øst ut livets fornødenheter over Sine tjenere, så ville de vist overmot på jorden. Men Han sender det tilmålt, det Han vil. Han er vel underrettet og observant overfor Sine tjenere.'),
(4300, 42, 28, 'Og Han sender regnet, etter at de er henfalt i fortvilelse. Og brer ut Sin nåde. Han er Vennen, den Lovpriste.'),
(4301, 42, 29, 'Av Hans tegn er skapelsen av himlene og jorden, og alt Han har spredt utover i dem av levende kryp. Og Han har makt til å samle dem når Han vil.'),
(4302, 42, 30, 'Hva som enn rammer dere av hjemsøkelse, kommer av det dere selv har pådratt dere, og Han unnskylder meget!'),
(4303, 42, 31, 'Dere kan ikke sette Ham ut av spill på jorden, og, bortsett fra Gud, har dere ingen venn eller hjelper.'),
(4304, 42, 32, 'Til Hans tegn hører også skipene, som seiler på havet som landemerker. Om'),
(4305, 42, 33, 'Han vil, kan Han få vinden til å stilne, og de forblir urørlige på dets rygg. I dette er jærtegn for envher som er tålmodig, takknemlig.'),
(4306, 42, 34, 'Eller Han lar dem forlise på grunn av det de har fortjent, og Han unnskylder meget.'),
(4307, 42, 35, 'Og de, som strides om Vårt ord, kan derved vite, de har ingen sikker tilflukt.'),
(4308, 42, 36, 'Alle ting dere er blitt gitt, er en jordelivets glede. Men det som er hos Gud er bedre og varigere, for dem som tror, og forlater seg på Herren,'),
(4309, 42, 37, 'for dem, som unngår de alvorligste synder og skjendigheter, som tilgir når de gripes av sinne,'),
(4310, 42, 38, 'som hører på Herren, forretter bønnen, som løser sitt anliggende ved rådslagning, som gir av det Vi har gitt dem,'),
(4311, 42, 39, 'og som når de rammes av overgrep, hjelper seg til rette.'),
(4312, 42, 40, 'Det ondes belønning er ondt av samme sort. Men den som tilgir og gjør opp saken, hans lønn er Guds sak. Gud holder ikke av de urettferdige.'),
(4313, 42, 41, 'Men den som hjelper seg til rette etter at urett er overgått ham, slike kan ikke klandres.'),
(4314, 42, 42, 'Klanderverdige er bare de som tilføyer folk urett, og begår vold i landet på urett vis. Dem venter en smertelig straff.'),
(4315, 42, 43, 'Visselig, den som bærer med tålmod, og unnskylder, dette er fasthet i livets tilskikkelser.'),
(4316, 42, 44, 'Den Gud lar seile sin egen sjø, har ingen venn, etter Ham. Du vil se at de urettferdige, når de får se straffen, sier: «Finnes det en utvei til å komme unna?»'),
(4317, 42, 45, 'Og du vil se dem når de føres frem til straffen i fryktsom ydmykhet og ser seg om med stjålne blikk, mens de troende sier: «Taperne er visselig de som taper seg selv og sine på oppstandelsens dag! Er ikke de urettferdige i varig straff?'),
(4318, 42, 46, 'De har ingen venner til å hjelpe seg utenom Gud, og for den som Gud lar seile sin egen sjø, finnes ingen utvei.»'),
(4319, 42, 47, 'Hør på Herren, før det kommer fra Gud en dag som ikke kan avverges. På denne dag har dere ingen tilflukt, og ingen mulighet til å nekte.'),
(4320, 42, 48, 'Men, om de snur seg bort, så sendte Vi ikke deg som vokter over dem. Deg påligger bare å levere budskapet. Lar vi mennesket smake godhet fra Oss, så fryder han seg derved. Men rammes de av noe ondt for deres henders gjerninger? Mennesket er sannelig utakknemlig.'),
(4321, 42, 49, 'Guds er herredømmet over himlene og jorden! Han skaper det Han vil!'),
(4322, 42, 50, 'Han gir hvem Han vil døtre, og hvem Han vil sønner, eller begge deler. Han gjør hvem Han vil ufruktbar. Han vet, evner alt.'),
(4323, 42, 51, 'Det er ikke for et menneske at Gud skulle tale til ham, unntatt ved åpenbarings inspirasjon, eller fra bak et slør, eller ved at Han sender et sendebud, som med Hans gode vilje inspirert meddeler det Han vil. Han er opphøyet, vis.'),
(4324, 42, 52, 'Således har Vi inngitt deg Ånd av Vår ordning. Du visste ikke hva skriften var, og heller ikke troen, men Vi har gjort det til et lys, hvorved Vi leder dem Vi vil av Våre tjenere. Sannelig, du leder til rett vei,'),
(4325, 42, 53, 'Guds vei, Hans, som alt tilhører i himlene og på jorden! Til Gud vender alle ting hjem!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'Ha Mim'),
(4327, 43, 2, 'Ved den klare skrift!'),
(4328, 43, 3, 'Vi gjorde den til en Koran på arabisk, så dere må forstå!'),
(4329, 43, 4, 'Se, den finnes i skriftens opphav, hos Oss, den er opphøyet, vis.'),
(4330, 43, 5, 'Skulle Vi forholde dere formaningen, fordi dere er et lettsindig folk?'),
(4331, 43, 6, 'Hvor mange profeter har Vi vel sendt de henfarne,'),
(4332, 43, 7, 'men det kom ingen profet til dem uten at de drev ap med ham.'),
(4333, 43, 8, 'Så Vi utslettet slike som var mer fryktinngytende enn de nålevende, og de henfarnes eksempel ble satt.'),
(4334, 43, 9, 'Om du spør dem: «Hvem skapte himlene og jorden?» Da svarer de: «Den Mektige, den Allvitende har skapt dem.»'),
(4335, 43, 10, 'Han har gjort jorden til leie for dere, og laget fremkomstveier for dere på den, så dere må finne frem.'),
(4336, 43, 11, 'Han sender vann fra oven i tilmålt mengde. Ved det vekker Vi til liv det døde land. På samme måte vil dere bli brakt frem fra gravene.'),
(4337, 43, 12, 'Han er det, som har skapt alt parvis, og Han har gitt dere skipene, og kveg som dere rir,'),
(4338, 43, 13, 'så dere kan ta plass på deres rygg, og så komme Herrens nåde i hu, når dere har satt dere til rette, og si: «Ære være Ham, som har gjort dette underdanig for oss! Vi ville ikke greid det!'),
(4339, 43, 14, 'Vi vender oss til Herren.»'),
(4340, 43, 15, 'Dog har de gitt Ham noen av Hans tjenere som en del av Ham selv! Mennesket er sannelig klart utakknemlig!'),
(4341, 43, 16, 'Skulle Han ha skaffet seg døtre av det Han skaper, og utsett sønnene for dere?'),
(4342, 43, 17, 'Når noen av dem får melding om nyfødte pikebarn, som han anser passende for den Barmhjertige, så formørkes hans ansikt, og han må legge bånd på seg.'),
(4343, 43, 18, 'Kanskje en som er oppfostret i velstand, og ikke er klar i argumentasjonen?'),
(4344, 43, 19, 'De har gjort englene, som er den Barmhjertiges tjenere, til kvinnelige vesener. Var de vel vitne til deres skapelse? Deres vitnemål skal bli oppskrevet, og de blir forhørt.'),
(4345, 43, 20, 'De sier også: «Om den Barmhjertige hadde villet, så ville vi ikke ha tilbedt dem.» De har ingen kunnskap om dette, de bare formoder.'),
(4346, 43, 21, 'Eller har Vi tidligere brakt dem en Bok, som de holder seg til?'),
(4347, 43, 22, 'Nei, de sier: «Vi fant at våre fedre dannet et (tros)samfunn, og vi følger i deres spor.»'),
(4348, 43, 23, 'Således har Vi før din tid aldri sendt en advarer til noen by, uten at de som levde i vellevnet sa: «Vi fant at våre fedre dannet et (tros)samfunn, og vi følger i deres spor.»'),
(4349, 43, 24, 'Si: «Om nå jeg kom til dere med bedre ledelse enn den dere fant at deres fedre holdt seg til?» Da vil de svare: «Vi tror ikke på det dere er sendt med.»'),
(4350, 43, 25, 'Så rammet Vi dem med gjengjeldelse, og se hva enden ble for dem som holdt sannhet for løgn!'),
(4351, 43, 26, 'En gang sa Abraham til sin far og sitt folk: «Jeg vil ikke ha noe å gjøre med det dere tilber,'),
(4352, 43, 27, 'unntatt Han som skapte meg, og Han vil lede meg.»'),
(4353, 43, 28, 'Og han gjorde dette til stående ord blant sine etterkommere, så de måtte vende om.'),
(4354, 43, 29, 'Ja, Jeg gav disse og deres fedre gode dager, til sannheten og et klart sendebud kom til dem.'),
(4355, 43, 30, 'Men da sannheten kom til dem, sa de: «Dette er trolldom! Vi tror ikke på det.»'),
(4356, 43, 31, 'De sier også: «Hvorfor ble ikke denne Koran åpenbart for en mann av betydning fra en av de to byer?»'),
(4357, 43, 32, 'Er det de som fordeler Herrens nåde? Det er Vi som har fordelt blant dem deres levemåte i jordelivet, og hevet noen over andre i rang, så noen kan sette andre i tjeneste. Men Herrens nåde er bedre enn det de samler sammen.'),
(4358, 43, 33, 'Hadde det ikke vært for at menneskene ville bli et eneste vantro folk, ville Vi gitt dem som fornekter den Barmhjertige, sølvtak på deres hus, og trapper til å stige opp,'),
(4359, 43, 34, 'og dører til deres hus, og divaner hvor de kan lene seg tilbake,'),
(4360, 43, 35, 'og forgylling. Alt dette er bare jordelivets gleder. Men det hinsidige liv hos Herren er for de gudfryktige.'),
(4361, 43, 36, 'Den som er blind for den Barmhjertiges formaning, ham tildeler vi en satan til følgesvenn.'),
(4362, 43, 37, 'De stenger dem ute fra veien, men de tror at de er ledet rett.'),
(4363, 43, 38, 'Til han kommer til Oss, og sier til sin følgesvenn: «Å, hadde det bare vært så langt mellom deg og meg som mellom øst og vest!» For en sørgelig følgesvenn!'),
(4364, 43, 39, 'Det vil ikke hjelpe dere noe denne dag, hvis dere har gjort ondt, at dere er flere om straffen.'),
(4365, 43, 40, 'Kan du få døve til å høre, eller rettlede blinde og dem som er klart på villstrå?'),
(4366, 43, 41, 'Om Vi tar deg bort, så vil Vi ramme dem med gjengjeldelse.'),
(4367, 43, 42, 'Eller Vi kan la deg se noe av det Vi har stilt dem i utsikt. Vi har makt over dem!'),
(4368, 43, 43, 'Hold fast ved det som er åpenbart for deg! Du er visselig på rett vei.'),
(4369, 43, 44, 'Det er en påminnelse til deg om ditt folk. Og dere vil bli trukket til regnskap.'),
(4370, 43, 45, 'Spør de av Våre sendebud som Vi sendte før din tid: «Har Vi gitt dem andre guder enn den Barmhjertige å tjene?»'),
(4371, 43, 46, 'Vi sendte Moses med Våre tegn til Farao og hans råd, og han sa: «Jeg er et sendebud fra all verdens Herre.»'),
(4372, 43, 47, 'Men da han kom med Våre tegn, så lo de av dem.'),
(4373, 43, 48, 'Og ikke et tegn viste Vi dem uten at det var større enn det foregående. Og Vi tok dem fatt med straffen, så de måtte vende om.'),
(4374, 43, 49, 'Men de sa: «Du trollmann, be til din Herre for oss ved den avtale Han har gjort med deg! Vi er på rett vei.»'),
(4375, 43, 50, 'Men da Vi tok bort fra dem straffen, brøt de sitt ord.'),
(4376, 43, 51, 'Og Farao lot utrope til sitt folk: «Er ikke herredømmet over Egypten mitt, og disse elver, som flyter foran meg? Ser dere da ikke?'),
(4377, 43, 52, 'Er ikke jeg bedre enn denne mann, en foraktelig mann, som nesten ikke kan uttrykke seg klart?'),
(4378, 43, 53, 'Hvorfor er det ikke gitt ham armbånd av gull, og hvorfor er ikke englene kommet sammen med ham?»'),
(4379, 43, 54, 'Slik forledet han sitt folk, og de adlød ham. De var sannelig ugudelige mennesker.'),
(4380, 43, 55, 'Så da de hadde vakt Vår bedrøvelse og indignasjon, lot Vi gjengjeldelsen ramme dem, og Vi druknet dem alle.'),
(4381, 43, 56, 'Vi gjorde dem til en forgangen slekt, og et eksempel for de senere.'),
(4382, 43, 57, 'Når Marias sønn blir nevnt som et eksempel, så skriker ditt folk opp,'),
(4383, 43, 58, 'og sier: «Er ikke våre guder like gode som han?» De nevner ham for deg bare for å krangle. Nei, de er trettekjære mennesker!'),
(4384, 43, 59, 'Han er bare en tjener som Vi har vist nåde, og Vi gjorde ham til et eksempel for Israels barn.'),
(4385, 43, 60, 'Om Vi ville, kunne Vi la engler fremstå blant dere, og overta jorden.'),
(4386, 43, 61, 'Han utgjør kunnskap om timen, så vær ikke i tvil om den, og følg meg! Dette er rett vei!'),
(4387, 43, 62, 'La ikke Satan hindre dere. Han er en klar fiende for dere!'),
(4388, 43, 63, 'Da Jesus kom med klar beskjed, sa han: «Jeg er kommet til dere med visdommen, og for å gjøre klart for dere noe av det dere strides om. Så frykt Gud, og adlyd meg!'),
(4389, 43, 64, 'Gud er min Herre og deres Herre, så tjen Ham! Dette er rett vei!»'),
(4390, 43, 65, 'Men grupper blant dem kom i strid. Ve over dem som gjør urett, for en smertelig dags straff!'),
(4391, 43, 66, 'Kan de se frem til annet enn at timen kommer over dem, plutselig, uten at de aner noe?'),
(4392, 43, 67, 'Venner blir på denne dag fiender, unntatt de gudfryktige.'),
(4393, 43, 68, '«Mine tjenere, frykt skal ikke hvile over dere denne dag, ei heller sorg,'),
(4394, 43, 69, 'dere som trodde på Vårt ord, og gav dere Gud i vold!'),
(4395, 43, 70, 'Gå inn i paradisets haver, dere og deres hustruer, og fryd dere!»'),
(4396, 43, 71, 'Det sendes rundt blant dem fat og drikkebegre av gull, med alt deres hjerter begjærer, og som fryder øyet. «Der skal dere være og bli!'),
(4397, 43, 72, 'Dette er paradisets have, som dere har fått i arv for deres gjerninger.'),
(4398, 43, 73, 'Der har dere rikelig med frukt til å spise av.»'),
(4399, 43, 74, 'Men synderne skal være og bli i helvetes straff,'),
(4400, 43, 75, 'som ikke mildnes for dem i deres fortvilelse der.'),
(4401, 43, 76, 'Vi har ikke gjort dem urett, men det var de som gjorde urett.'),
(4402, 43, 77, 'De vil rope: «Engel Malik, måtte Herren gjøre det av med oss,» og han svarer: «Dere må bli der.»'),
(4403, 43, 78, 'Vi kom jo til dere med sannheten, men de fleste av dere mislikte sannheten.'),
(4404, 43, 79, 'Eller har de funnet på noe? Vi kan også finne på noe!'),
(4405, 43, 80, 'Eller regner de med at Vi ikke hører deres hemmelighet, og deres overlegninger? Jo, Våre utsendinger er til stede hos dem, og skriver opp.'),
(4406, 43, 81, 'Si: «Om den Barmhjertige hadde en sønn, ville jeg være den første til å tjene ham!'),
(4407, 43, 82, 'Ære være Herren over himlene og jorden, Herren over tronen, langt borte fra det de beskriver.»'),
(4408, 43, 83, 'Så la dem prate og drive sitt spill, til de møter den dag som er stilt dem i utsikt.'),
(4409, 43, 84, 'Han er det, som er Gud i himmelen og Gud på jorden, Han er den Vise, den Allvitende.'),
(4410, 43, 85, 'Full av velsignelse er Han, som har herredømmet over himlene og jorden, og alt som mellom dem er. Hos Ham er kunnskapen om timen. Til Ham skal dere føres tilbake.'),
(4411, 43, 86, 'Det de påkaller utenom Ham, formår ikke å legge inn godt ord, bare slike som bevitner sannheten, og vet beskjed.'),
(4412, 43, 87, 'Om du spør dem: «Hvem skapte dere?» Så svarer de: «Gud.» Hvordan kan de da bli så forvridde?'),
(4413, 43, 88, 'Hans utsagn lyder: «Herre, dette er visselig vantro folk.»'),
(4414, 43, 89, 'Så tilgi dem, og si: «Fred!» De vil få å vite!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'Ha Mim'),
(4416, 44, 2, 'Ved den klare skrift!'),
(4417, 44, 3, 'Vi har åpenbart den i en velsignet natt. Vi sender alltid advarsler.'),
(4418, 44, 4, 'I denne natt bestemmes alle visdoms saker'),
(4419, 44, 5, 'ved Vårt bud. Vi sender alltid ut,'),
(4420, 44, 6, 'som en nåde fra Herren. Han er den Hørende, den Allvitende.'),
(4421, 44, 7, 'Herren over himlene og jorden og alt som mellom dem er, om dere har overbevisningens tro.'),
(4422, 44, 8, 'Det er ingen gud unntatt Ham. Han gir liv og død, deres og deres henfarne fedres Herre.'),
(4423, 44, 9, 'Nei, de er i tvil, og driver sitt spill.'),
(4424, 44, 10, 'Men se opp for en dag da himmelen bringer en tydelig røyk,'),
(4425, 44, 11, 'som dekker menneskene. Dette er en smertelig straff.'),
(4426, 44, 12, '«Herre, ta bort fra oss denne straffedom, vi tror!»'),
(4427, 44, 13, 'Men hvordan skulle formaningen nå dem, når et klart sendebud kom til dem,'),
(4428, 44, 14, 'og de så snudde seg vekk fra ham, og sa: «Han er opplært, besatt?»'),
(4429, 44, 15, 'Vi skal ta bort straffedommen en stund, men dere faller nok tilbake.'),
(4430, 44, 16, 'Men den dag da Vi slår hardt til! Vi vil ta hevn.'),
(4431, 44, 17, 'Vi satte Faraos folk på prøve før dem, idet et verdig sendebud kom til dem:'),
(4432, 44, 18, '«Overgi til meg Guds tjenere! Jeg er et pålitelig sendebud til dere!'),
(4433, 44, 19, 'Sett dere ikke opp mot Gud! Jeg kommer til dere med klar autoritet.'),
(4434, 44, 20, 'Jeg søker tilflukt hos min Herre og deres Herre, så dere ikke må steinjage meg.'),
(4435, 44, 21, 'Om dere ikke tror meg, så gå bort fra meg.»'),
(4436, 44, 22, 'Så påkalte han sin Herre: «Disse er syndige mennesker.»'),
(4437, 44, 23, 'Gud svarte: «Dra av gårde med Mine tjenere nattestid! Dere vil bli forfulgt!'),
(4438, 44, 24, 'Etterlat havet som det er, for de er en hær som skal druknes.»'),
(4439, 44, 25, 'Hvor mange haver og kilder,'),
(4440, 44, 26, 'åkrer og utsøkte boliger etterlot de!'),
(4441, 44, 27, 'Og velstand som de hadde gledet seg over!'),
(4442, 44, 28, 'Slik var det, Vi lot et annet folk ta det i arv.'),
(4443, 44, 29, 'Himmel og jord gråt ikke over dem. De fikk ingen utsettelse.'),
(4444, 44, 30, 'Vi reddet Israels barn fra den ydmykende plage, fra Farao.'),
(4445, 44, 31, 'Han var stor på det, og lettsindig.'),
(4446, 44, 32, 'Vi har med omtanke utvalgt dem fremfor all verden,'),
(4447, 44, 33, 'og sendte dem tegn som innebar en åpenbar prøvelse.'),
(4448, 44, 34, 'Disse menneskene sier:'),
(4449, 44, 35, '«Det er ikke noe annet enn vår første død! Vi blir ikke gjenoppvekket.'),
(4450, 44, 36, 'Bring oss våre forfedre, om dere snakker sant!»'),
(4451, 44, 37, 'Er de bedre enn Tubbas folk, og synderne før dem, som Vi utslettet?'),
(4452, 44, 38, 'Vi har ikke skapt himlene og jorden og alt som derimellom er, som lek!'),
(4453, 44, 39, 'Vi skapte dem på alvor. Men folk flest vet ikke.'),
(4454, 44, 40, 'Avgjørelsens dag er fristen for dem alle.'),
(4455, 44, 41, 'På denne dag kan ingen herre gjøre noe for den han har ansvar for, de får ingen hjelp,'),
(4456, 44, 42, 'unntatt dem Herren forbarmer seg over. Han er den Mektige, den Nåderike.'),
(4457, 44, 43, 'Helvetestreet Zaqqum'),
(4458, 44, 44, 'er synderens mat,'),
(4459, 44, 45, 'som flytende metall vil det koke i maven'),
(4460, 44, 46, 'som kokende vann koker!'),
(4461, 44, 47, '«Ta ham, og slep ham inn midt i helvete,'),
(4462, 44, 48, 'og pøs over hans hode det kokende vannets straff!'),
(4463, 44, 49, 'Bare smak! Du var jo så mektig og høyt i ære!'),
(4464, 44, 50, 'Dette er det dere tvilte på.»'),
(4465, 44, 51, 'Men de gudfryktige er i et trygt bosted,'),
(4466, 44, 52, 'blant haver og kilder,'),
(4467, 44, 53, 'kledd i silke og brokade, plassert overfor hverandre.'),
(4468, 44, 54, 'Slik er det. Og vi fører dem sammen med mørkøyde paradisjomfruer.'),
(4469, 44, 55, 'Der kan de be om all slags frukt i trygghet.'),
(4470, 44, 56, 'Der smaker de ikke døden etter den første død. Han har bevart dem fra helvetes straff'),
(4471, 44, 57, 'ved Herrens nåde. Dette er den store seier.'),
(4472, 44, 58, 'Vi har gjort Koranen lett tilgjengelig ved ditt eget tungemål, så de må komme til ettertanke.'),
(4473, 44, 59, 'Så vær på vakt! De er også på vakt.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'Ha Mim'),
(4475, 45, 2, 'Skriftens åpenbaring er fra Gud, den Mektige, den Vise.'),
(4476, 45, 3, 'Det er visselig i himlene og på jorden jærtegn for de troende.'),
(4477, 45, 4, 'Og i deres egen skapelse, og i det Han har spredt ut av all slags dyr, er jærtegn for folk som har troens visshet.'),
(4478, 45, 5, 'Og i vekslingen av natt og dag, og i det underhold Han sender ved regnet fra oven, hvorved Han gir liv til jorden etter dens dvale, og i de vekslende vinder, i dette er jærtegn for folk som forstår.'),
(4479, 45, 6, 'Dette er Guds tegn, som Vi meddeler deg ved sannheten. Hvilket budskap, etter Gud og Hans tegn, vil de da tro?'),
(4480, 45, 7, 'Ve over enhver full av falskhet og synd,'),
(4481, 45, 8, 'som hører Guds ord, opplest for seg, men forblir i hovmod, som om han ikke hadde hørt det! Bebud for ham en smertelig straff.'),
(4482, 45, 9, 'Og når han får vite om noe av Vårt budskap, driver han ap med det. Dem venter en ydmykende straff!'),
(4483, 45, 10, 'Foran dem ligger helvete. Alt de har skaffet seg hjelper dem ikke det ringeste, og heller ikke de venner de har lagt seg til utenom Gud. Dem venter en svær straff!'),
(4484, 45, 11, 'Dette er rettledning, og de som fornekter Herrens budskap, har i vente en smertelig vredens straff.'),
(4485, 45, 12, 'Gud er det, som har stilt havet i deres tjeneste, slik at skip kan seile på det ved Hans bud, og dere kan søke Hans gode gaver, så dere må vise takknemlighet.'),
(4486, 45, 13, 'Han har stilt i deres tjeneste alt som er i himlene og på jorden, en gave fra Ham. I dette er visselig tegn for folk som tenker etter.'),
(4487, 45, 14, 'Si til dem som tror, at de må tilgi dem som ikke ser frem til Guds dager, så Han kan belønne folk for det de har fortjent.'),
(4488, 45, 15, 'Om noen lever rettskaffent, er det til hans egen vinning. Om noen lever i urett, er det til egen belastning. Så blir dere brakt hjem til Herren.'),
(4489, 45, 16, 'Vi gav Israels barn skriften, visdommen og profetkallet, og Vi sørget for dem med gode ting, og gav dem fortrinn fremfor all verden.'),
(4490, 45, 17, 'Og Vi gav dem klar beskjed om ordningen, men etter at kunnskapen ble dem til del, kom de i strid og innbyrdes nid. Herren vil dømme mellom dem på oppstandelsens dag, i det de var uenige om.'),
(4491, 45, 18, 'Så satte Vi deg på ordningens rette vei, så følg den. Følg ikke deres ideer som intet vet.'),
(4492, 45, 19, 'De kan ikke hjelpe deg det ringeste mot Gud. De urettferdige står hverandre bi, men Gud bistår de gudfryktige.'),
(4493, 45, 20, 'Dette er innsikt for menneskene, og ledelse og nåde for folk som har troens visshet.'),
(4494, 45, 21, 'Eller tror de som handler ondt at Vi vil stille dem på lik linje med dem som tror og lever rettskaffent, i liv og død? Ille er deres vurdering!'),
(4495, 45, 22, 'Gud har skapt himlene og jorden på alvor, slik at enhver kan lønnes slik han har fortjent. De vil ikke lide urett.'),
(4496, 45, 23, 'Hva mener du om den som gjør sin egen oppfatning til gud, og som Gud etter kunnskap om dette lar seile sin egen sjø, og setter segl på hans ører og hans hjerte og dekker over hans øyne? Hvem skal veilede ham, etter Gud? Vil dere da ikke komme til ettertanke?'),
(4497, 45, 24, 'Men de sier: «Det er ikke noe annet enn jordelivet. Vi dør, og vi lever, det er kun tiden som tilintetgjør oss.» De har ingen kunnskap om dette, de bare formoder.'),
(4498, 45, 25, 'Og når Vårt klare ord fremleses for dem, er deres argumenter bare at de sier: «Bring våre fedre tilbake, om dere snakker sant!»'),
(4499, 45, 26, 'Si: «Gud gir dere liv, og lar dere så dø. Derpå vil Han samle dere til oppstandelsens dag, hvorom ingen tvil hersker. Men folk flest vet det ikke.»'),
(4500, 45, 27, 'Guds er herredømmet over himlene og jorden, og den dag da timen inntreffer, på denne dag er de fortapt, som fremmer verdiløs intethet.'),
(4501, 45, 28, 'Du vil se alle folk liggende på kne. Hvert folk blir fremkalt til sin bok: «I dag skal dere belønnes for det dere har gjort!'),
(4502, 45, 29, 'Dette er Vår bok, som taler sannhet om dere. Vi har latt nedtegne det dere har gjort.»'),
(4503, 45, 30, 'Hva dem angår som har trodd og levd rettskaffent, så vil Herren føre dem inn i Sin nåde. Dette er den åpenbare seier.'),
(4504, 45, 31, 'Men hva dem angår, som ikke trodde: «Ble ikke Mitt ord forkynt for dere, og dere viste hovmod, og var syndige folk?»'),
(4505, 45, 32, 'Da det ble sagt: «Guds løfte er sannhet, og om timen hersker ingen tvil,» da sa dere: «Vi vet ikke hva timen er. Vi gjør oss bare formodninger, og vi er ikke overbevist.»'),
(4506, 45, 33, 'Men det onde de har gjort, skal stå klart frem for dem, og det de drev ap med, skal ringe dem inn.'),
(4507, 45, 34, 'Det vil bli sagt: «I dag glemmer Vi dere, som dere glemte denne deres dags møte. Deres herberge er Ilden, og dere har ingen som hjelper!'),
(4508, 45, 35, 'Dette fordi dere drev ap med Guds ord, og jordelivet forførte dere.» Denne dag føres de ikke ut av den, og de kalles ikke til bot.'),
(4509, 45, 36, 'Lov og pris hører Gud til, himlenes Herre og jordens Herre, all verdens Herre!'),
(4510, 45, 37, 'Hans er makten og æren i himlene og på jord! Han er den Mektige, den Vise.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'Ha Mim'),
(4512, 46, 2, 'Skriftens åpenbaring er fra Gud, den Mektige, den Vise.'),
(4513, 46, 3, 'Vi skapte himlene og jorden og det som mellom dem er på alvor, og for en bestemt tid, men de vantro vender seg bort fra det de ble advart mot.'),
(4514, 46, 4, 'Si: «Hva mener dere om det dere påkaller utenom Gud? Vis meg det de har skapt av jorden! Eller har de andel i himmelen? Legg frem for meg en skrift, eldre enn denne, eller et spor av kunnskap, om dere snakker sant!»'),
(4515, 46, 5, 'Hvem er mer på villspor enn den som påkaller utenom Gud, noen som ikke vil høre ham til oppstandelsens dag, og som er likegyldige for deres bønner?'),
(4516, 46, 6, 'Når menneskene samles inn, vil de være deres fiender, og de vil fornekte deres tilbedelse.'),
(4517, 46, 7, 'Når Vårt ord fremleses for dem som klar beskjed, sier de vantro om sannheten, da den kom til dem:'),
(4518, 46, 8, '«Dette er skjær trolldom,» eller de sier: «Han har diktet det opp.» Si: «Om jeg har diktet opp, så makter dere ikke å hjelpe meg mot Gud det ringeste. Han vet godt hva dere innlater dere på å si. Han er tilstrekkelig som vitne mellom oss. Han er den Ettergivende, den Nåderike.»'),
(4519, 46, 9, 'Si: «Jeg er ikke noe nytt blant sendebudene. Jeg vet ikke hva som vil gjøres med meg eller med dere. Jeg følger bare det som er meg åpenbart, jeg er bare en klar advarer.»'),
(4520, 46, 10, 'Si: «Hva mener dere, om det kommer fra Gud og dere fornekter det, mens et vitne fra Israels barn bærer vitnemål om det samme, og tror, mens dere viser hovmod? Gud leder ikke urettferdige folk.»'),
(4521, 46, 11, 'De vantro sier om de troende: «Hadde dette vært noe verdt, ville ikke de kommet oss i forkjøpet hen til det.» Og når de ikke har latt seg veilede av det, vil de si: «Dette er gammel svindel.»'),
(4522, 46, 12, 'Før dette forelå Mose skrift som forbilde og som nåde. Dette er en stadfestende skrift på arabisk språk for å advare dem som gjør urett, og som et gledelig budskap for dem som gjør vel.'),
(4523, 46, 13, 'De som sier: «Gud er vår Herre,» og så holder rett kurs, over dem skal ingen frykt hvile, ei heller sorg.'),
(4524, 46, 14, 'Disse er paradisets folk, og der skal de være og bli, som lønn for det de gjorde.'),
(4525, 46, 15, 'Vi har foreskrevet mennesket godhet mot sine foreldre. Hans mor har båret ham med smerte, og føder ham med smerte, svangerskap og avvenning, det er tredve måneder. Så når han blir voksen, og blir førti år, sier han: «Herre, inngi meg takknemlighet for den nåde Du har vist meg og mine foreldre, og at jeg må handle rettskaffent, som Du finner velbehag i! Gjør mine etterkommere rettsindige og bra! Jeg vender meg til Deg, jeg er av Dine undergivne.»'),
(4526, 46, 16, 'Fra disse vil Vi ta imot det beste de har gjort, og gå forbi deres misgjerninger! De er av paradisets folk, et sannferdig løfte, som de ble gitt.'),
(4527, 46, 17, 'Men den som sier til sine foreldre: «Uff over dere! Lover dere meg at jeg skal bringes frem fra graven, når generasjoner er gått bort før meg?» Og de anroper Gud om hjelp. «Ve deg! Bli troende! Guds løfte er sant,» hvorpå han sier: «Dette er bare historier fra gamle dager.»'),
(4528, 46, 18, 'Det er mot slike at ordet realiseres, om henfarne samfunn før dem av dsjinner og mennesker. De var tapere.'),
(4529, 46, 19, 'Alle er inndelt i grader, alt etter det de har gjort, så Han kan gi dem full lønn for deres innsats, og de vil ikke lide urett.'),
(4530, 46, 20, 'Den dag når de vantro føres frem til Ilden: «Dere har sløst bort deres gode ting i deres liv på jorden og søkt nytelser ved dem! I dag vil dere bli belønnet med fornedrelsens straff, fordi dere var hovmodige på jorden, uten rett, og fordi dere levde syndig.'),
(4531, 46, 21, 'Kom i hu ham av Ad-folket da han advarte sitt folk ved sanddynene, og advarere er gått bort, både før, og etter ham: «Tilbe ingen, unntatt Gud! Jeg frykter at dere vil møte en svær dags straff!»'),
(4532, 46, 22, 'De svarte: «Er du kommet til oss for å få oss bort fra våre guder? Bare bring over oss det du truer oss med, om du snakker sant.»'),
(4533, 46, 23, 'Da sa han: «Viten om dette beror bare hos Gud. Jeg overbringer dere det jeg er sendt med! Men jeg ser at dere er uvitende folk.»'),
(4534, 46, 24, 'Da de fikk se det som skybanker som trakk opp i deres daler, sa de: «Dette er opptrekkende skyer, det vil gi oss regn.» Men nei: «Dette er det dere bad påskyndet, en vind, hvori en smertelig straff ligger,'),
(4535, 46, 25, 'som ødelegger alt på sin Herres Gud.» Da morgenen kom, var intet å se unntatt deres boplasser. Slik belønner vi syndige folk.'),
(4536, 46, 26, 'Og Vi hadde gitt dem en maktstilling, som Vi ikke har gitt dere. Vi hadde gitt dem hørsel, syn og forstand. Men deres hørsel, syn og forstand hjalp dem ikke det ringeste, for de fornektet Guds ord. Og det de drev ap med, ringet dem inn.'),
(4537, 46, 27, 'Vi har utslettet byer rundt omkring dere, og Vi snur og vender på tegnene, så de må vende om.'),
(4538, 46, 28, 'Men hvorfor ytet ikke de dem hjelp, som de la seg til utenom Gud som kontaktsfremmende? Nei, de lot dem i stikken. Dette er deres falskhet, og det de har diktet opp.'),
(4539, 46, 29, 'En gang viste Vi en flokk dsjinner til deg, for å høre Koranen. Da de var kommet til stede til den, sa de: «Vær stille!» Da lesningen var ferdig, vendte de tilbake til sitt folk og advarte.'),
(4540, 46, 30, 'De sa: «Vårt folk, vi har hørt en skriftens bok som er åpenbart etter Moses og som stadfester det som foreligger før den, og som leder til sannhet og til rett vei!'),
(4541, 46, 31, 'Vårt folk, hør på ham som kaller til Gud, og tro på Ham, så vil Han tilgi noe av deres synder, og verge dere mot en smertelig straff.'),
(4542, 46, 32, 'Om noen ikke hører på ham som kaller til Gud, så kan han ikke gjøre Ham avmektig på jorden, og han har ingen beskyttere utenom Ham. Disse er i åpenbar villfarelse.»'),
(4543, 46, 33, 'Har de da ikke innsett at Gud, som skapte himlene og jorden uten å føle tretthet av deres skapelse, er i stand til å gjenoppvekke de døde? Sannelig, Han evner alt!'),
(4544, 46, 34, 'Den dag når de vantro føres frem til Ilden: «Er ikke dette realitet?» De svarer: «Jo, ved vår Herre!» Han sier: «Smak så straffen for deres vantro!»'),
(4545, 46, 35, 'Så vis tålmod, slik sendebudene med fasthet viste tålmod. Be ikke om påskyndelse for dem. Det vil være slik for dem den dag de ser det de er stilt i utsikt, som om de bare har tilbrakt en times tid i graven! Klar beskjed! Men utslettes andre enn folk som lever i synd?');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'De som ikke tror, og som legger hindringer på Guds vei, deres gjerninger lar Gud løpe ut i intet.'),
(4547, 47, 2, 'Men de som tror og lever rettskaffent, og tror på det som er åpenbart Muhammad, og det er sannheten fra Herren, deres misgjerninger utslettet Han og gir deres tanker rett lei.'),
(4548, 47, 3, 'Dette er fordi de vantro retter seg etter verdiløs intethet, mens de som tror, retter seg etter sannheten fra Herren. Slik beskriver Gud menneskenes stilling.'),
(4549, 47, 4, 'Når dere møter de vantro i kamp, så gå løs på dem til de er helt nedkjempet, og bind stramme bånd. Og etterpå, frigivelse eller løsepenger, så krigen legger fra seg sin byrde. Slik er det. Om Gud ville, kunne Han selv ta seg til rette overfor dem, men Han ønsker å sette noen av dere på prøve gjennom andre. De som kjemper og dør for Guds sak, deres gjerninger lar Han aldri løpe ut i intet.'),
(4550, 47, 5, 'Han vil lede dem og gi deres tanker rett lei,'),
(4551, 47, 6, 'og føre dem inn i paradiset som Han har gjort dem kjent med.'),
(4552, 47, 7, 'Dere troende, om dere hjelper Gud, hjelper Han dere og gir dere fotfeste.'),
(4553, 47, 8, 'Men de som er vantro, undergang over dem. Gud lar deres gjerninger løpe ut i intet.'),
(4554, 47, 9, 'Dette fordi de viste motvilje mot det som Gud åpenbarte, og så gjør Han deres gjerninger fåfengte.'),
(4555, 47, 10, 'Har de da ikke reist rundt på jorden og sett hva enden ble for dem som levde før? Gud utslettet dem, og for de vantro er det tilsvarende i vente.'),
(4556, 47, 11, 'Dette fordi Gud tar seg av de troende, mens de vantro ingen har som tar seg av dem.'),
(4557, 47, 12, 'Gud vil visselig føre dem som tror og lever rettskaffent inn i paradisets haver, hvor bekker sildrer. Men de som er vantro, de søker nytelser og fråtser som kveg. Ilden blir deres herberge.'),
(4558, 47, 13, 'Hvor mang en by, større i makt enn din by, som drev deg ut, har Vi utslettet. Det fantes ingen hjelper for dem!'),
(4559, 47, 14, 'Er vel den som baserer seg på klar beskjed fra Herren, som den hvis gjerningers ondskap synes prydelig for ham, og som følger sine egne lyster?'),
(4560, 47, 15, 'Slik er paradiset som er lovet de gudfryktige: Det er bekker med uforurenset vann, bekker med melk av uforanderlig smak, bekker med vin som er en nytelse for dem som drikker den, og bekker med ren honning. Der har de all slags frukt, og Herrens tilgivelse. Er vel disse slik stillet som den som skal være og bli i Ilden, og gis kokende vann å drikke, som sliter deres innvoller i stykker?'),
(4561, 47, 16, 'Det er noen som lytter til deg til de går fra deg, da sier de til dem som ble gitt kunnskapen: «Hva var det han sa nå?» Disse er det hvis hjerte Gud har forseglet, og som følger sine egne ideer.'),
(4562, 47, 17, 'Men de som har funnet rett vei, dem gir Han øket ledelse, og gudsfrykt.'),
(4563, 47, 18, 'Har de annet å vente enn timen, at den skal komme plutselig over dem? Dens forvarsel er kommet allerede! Men når den er over dem, hvortil deres formaning?'),
(4564, 47, 19, 'Vit at det er ingen gud unntatt Gud, og be om tilgivelse for din synd og for de troende, menn og kvinner! Gud kjenner til hvor dere ferdes og deres hvilested.'),
(4565, 47, 20, 'De som er troende sier: «Hvorfor er ikke en korantekst åpenbart om dette?» Når så en utvetydig korantekst blir åpenbart og det nevnes kamp i den, da ser du dem i hvis hjerter er sykdom, se på deg med blikket til en som er i dødens avmakt.'),
(4566, 47, 21, 'Men lydighet ville være mer passende for dem, og redelig tale! Når saken er avgjort, ville det være bedre for dem om de var oppriktige mot Gud.'),
(4567, 47, 22, 'Mon dere kanskje, om dere vender dere bort, vil stifte ufred og strid i landet og bryte deres blodsbånd?'),
(4568, 47, 23, 'Slike er under Guds forbannelse, og Han har gjort dem døve, og deres øyne blinde.'),
(4569, 47, 24, 'Tenker de da ikke over Koranen? Eller er det låser på hjertene?'),
(4570, 47, 25, 'De som snur om etter at ledelsen er blitt klar for dem, det er Satan som har ført dem i fristelse og gitt dem falske håp.'),
(4571, 47, 26, 'Dette fordi de sier til dem som har motvilje mot det Gud har åpenbart: «Vi skal adlyde dere, delvis.» Gud kjenner deres hemmeligheter.'),
(4572, 47, 27, 'Men hvordan går det, når englene henter dem bort, og gir dem slag i ansiktet og over ryggen?'),
(4573, 47, 28, 'Dette fordi de følger det som vekker Guds vrede, og har motvilje mot Hans velbehag, så Han lar deres gjerninger bli til intet.'),
(4574, 47, 29, 'Eller mener de i hvis hjerter er sykdom, at Gud ikke vil bringe frem deres hatefulle nag?'),
(4575, 47, 30, 'Om Vi ville, kunne Vi la deg se dem, og du ville visselig kjenne dem på deres merke, og du vil sikkert kjenne dem igjen på deres tvetydige tale. Gud kjenner deres gjerninger.'),
(4576, 47, 31, 'Vi skal visselig sette dere på prøve, så Vi kan kjenne de innsatsvillige og de standhaftige blant dere og utprøve hvordan det står til med dere.'),
(4577, 47, 32, 'De som er vantro og legger hindringer på Guds vei, og bryter med Sendebudet etter at ledelsen er blitt klar for dem, de skader ikke Gud det minste, men Han lar deres gjerninger bli til intet.'),
(4578, 47, 33, 'Dere som tror, adlyd Sendebudet, og gjør ikke deres gjerninger verdiløse!'),
(4579, 47, 34, 'De som er vantro og legger hindringer på Guds vei, og så dør som vantro, dem gir Gud aldri tilgivelse!'),
(4580, 47, 35, 'Så slapp ikke av og be om fred! Dere blir vinnerne, og Gud er med dere. Og Han vil ikke snyte dere på deres gjerninger!'),
(4581, 47, 36, 'Jordelivet er kun lek og tidsfordriv. Om dere tror og viser gudsfrykt, vil Han gi dere lønn og ikke spørre dere om det dere eier.'),
(4582, 47, 37, 'Men om Han ber dere om det, og går på dere, da vil dere vise dere gjerrige, og hatefullt nag vil Han bringe i dagen.'),
(4583, 47, 38, 'Der står dere! Dere er bedt om å yte for Guds sak, men noen av dere er gjerrige. Den som er gjerrig, er kun gjerrig mot seg selv. Gud er rik, og dere er de fattige. Om dere snur ryggen til, vil Han skifte dere ut med et annet folk, og de vil ikke være som dere!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Vi har visselig gitt deg en åpenbar seier,'),
(4585, 48, 2, 'så Gud kan forlate deg dine tidligere og senere synder og fullbyrde Sin nåde mot deg og lede deg på rett vei,'),
(4586, 48, 3, 'så Gud kan hjelpe deg med en mektig hjelp.'),
(4587, 48, 4, 'Han er det, som sendte guddommelig nærvær i de troendes hjerter så de kunne legge tro til sin tro, Gud tilhører himlenes og jordens hærskarer, Gud vet, er vis,'),
(4588, 48, 5, 'og for å føre de troende, menn og kvinner, inn i paradisets haver, hvor bekker sildrer og hvor de skal være og bli, og stryke ut deres misgjerninger. Dette er den store seier i Guds øyne.'),
(4589, 48, 6, 'Og for å refse hyklerne og avgudsdyrkerne, menn og kvinner, som har onde tanker om Gud. For dem blir det en skjebnesvanger vending; Guds vrede og forbannelse er over dem, og Han har gjort klar helvete for dem, en sørgelig skjebne!'),
(4590, 48, 7, 'Gud tilhører himlenes og jordens hærskarer. Gud er mektig, vis.'),
(4591, 48, 8, 'Vi har visselig sendt deg som vitne, gledesbudbærer og advarer,'),
(4592, 48, 9, 'at dere må tro på Gud og Hans sendebud, hjelpe Ham og vise Ham ærefrykt og prise Ham morgen og kveld.'),
(4593, 48, 10, 'De som gir deg sin troskapsed, erklærer faktisk troskap overfor Gud. Guds hånd er over deres hånd. Om noen misligholder, gjør han det til egen skade. Den som oppfyller det han har påtatt seg overfor Gud, ham vil Gud gi stor lønn.'),
(4594, 48, 11, 'Hjemmesitterne blant beduinene vil si til deg: «Vi var travelt opptatt med vårt kveg og våre folk, så be om tilgivelse for oss.» De fører på tungen det som ikke er i deres hjerter. Si: «Hvem kan vel utrette det ringeste for dere overfor Gud, om Han ønsker å gi dere ondt eller godt? Gud er vel underrettet om det dere gjør.'),
(4595, 48, 12, 'Nei, dere trodde at Sendebudet og de troende aldri skulle komme tilbake til sine noen gang, og dette syntes vakkert for deres hjerter. Dere gjorde onde formodninger. Dere er et folk uten lov!»'),
(4596, 48, 13, 'De som ikke tror på Gud og Hans sendebud, for de vantro har Vi gjort klar en Ild.'),
(4597, 48, 14, 'Gud tilhører herredømmet over himlene og jorden. Han tilgir dem Han vil, og straffer dem Han vil. Gud er tilgivende, nåderik.'),
(4598, 48, 15, 'Hjemmesitterne vil si når dere drar av gårde for å hente krigsbytte: «La oss få bli med dere.» De ønsker å forandre Guds ord. Si: «Dere får ikke følge oss! Så har Gud sagt tidligere.» Så vil de si: «Dere bare misunner oss.» Nei, det er lite de forstår.'),
(4599, 48, 16, 'Si til hjemmesitterne blant beduinene: «Dere blir kalt på mot et folk med stor kampstyrke for å kjempe mot dem, eller de overgir seg. Om dere adlyder, vil Gud gi dere god lønn. Men om dere lar være, som dere gjorde tidligere, vil Han refse dere med en smertelig straff.»'),
(4600, 48, 17, 'Det er ingen bebreidelse mot blinde, uføre og syke. Den som adlyder Gud og Hans sendebud, vil Han føre inn i paradisets haver, hvor bekker sildrer, men den som lar være, vil Han hjemsøke med en smertelig straff.'),
(4601, 48, 18, 'Gud fant velbehag i de troende da de gav deg troskapsed under treet, og Han visste hva som var i deres hjerter. Og Han sendte guddommelig nærvær over dem, og gav dem seier i utsikt,'),
(4602, 48, 19, 'og stort krigsbytte å ta. Gud er mektig, vis.'),
(4603, 48, 20, 'Gud lovet dere stort krigsbytte å ta, og skyndet dette på for dere og holdt menneskenes hender tilbake fra dere. Dette for at det skulle være et jærtegn for de troende for å lede dere til rett vei.'),
(4604, 48, 21, 'Og annet krigsbytte, som dere ikke rådde med, har Gud under kontroll. Gud evner alt.'),
(4605, 48, 22, 'Om de vantro var gått til kamp mot dere, ville de tatt flukten, og så ville de ikke funnet verken beskytter eller hjelper.'),
(4606, 48, 23, 'Det er Guds praksis, anvendt i tidligere tider. Du vil aldri finne endring i Guds praksis.'),
(4607, 48, 24, 'Han er det, som holdt deres hender tilbake fra dere, og deres hender fra dem, i Mekkadalen etter at Han lot dere vinne moralsk seier over dem. Gud ser det dere gjør.'),
(4608, 48, 25, 'Disse er de som var vantro og holdt dere borte fra den hellige moské, og offerdyrene fra sitt offersted. Hadde det ikke vært for troende menn og kvinner som dere ikke kjente, som dere kunne trampet ned i kamptummelen, og uten viten pådratt dere skyld for dem, for Gud tar inn i Sin nåde hvem Han vil! Hadde de vært klart adskilt, så ville Vi refset de vantro blant dem med en smertelig straff.'),
(4609, 48, 26, 'Da de vantro slapp stridslidenskapen inn i sine hjerter, hedenskapets stridslidenskap, så sendte Gud sitt guddommelige nærvær over Sitt sendebud og de troende, og holdt dem til gudsfryktens ord. Og de fortjente det best, og var mest verdige til det. Gud er vitende om alle ting.'),
(4610, 48, 27, 'Gud har latt visjonen gå i oppfyllelse for Sitt sendebud: «Dere vil gå inn i den hellige moské, om Gud vil, i trygghet, med rakede hoder og stusset skjegg og uten frykt.» For Han visste hva dere ikke visste, og utenom dette stilte Han dere seier i utsikt.'),
(4611, 48, 28, 'Han er det som har sendt Sitt sendebud med ledelsen og sannhets religion, for å la den vinne frem overfor all religion. Gud er tilstrekkelig som vitne.'),
(4612, 48, 29, 'Muhammad er Guds sendebud, og de som er med ham, er harde mot de vantro og gode mot hverandre. Du ser dem bøye seg og falle ned idet de søker Guds gunst og velbehag. Merkene i deres ansikter er spor etter megen nedfallen. Slik er deres beskrivelse i Moseloven. Og i evangeliet sammenlignes de med et korn som skyter opp sitt skudd, og styrker det, så det blir kraftigere og står rakt på sin stengel, til glede for såmennene, for å ergre den vantro ved dem. Gud har lovet dem som tror og lever rettskaffent, tilgivelse og stor lønn!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'Dere som tror, vær ikke frempå overfor Gud og Hans sendebud! Frykt Gud: Gud hører, og vet.'),
(4614, 49, 2, 'Dere som tror, hev ikke deres stemmer over profetens stemme, og vær ikke høylytt i deres tale til ham, som dere er høylytte dere imellom, så at ikke deres gjerninger blir til fånyttes uten at dere aner det.'),
(4615, 49, 3, 'De som senker sine stemmer i Guds sendebuds nærvær, disse er det hvis hjerter Gud har prøvet i gudsfrykt. De har i vente tilgivelse og stor lønn.'),
(4616, 49, 4, 'De som står og roper på deg mens du er i dine gemakker, de fleste av dem forstår ingenting.'),
(4617, 49, 5, 'Om de hadde tålmodighet til du kom ut, ville det være bedre for dem. Men Gud er tilgivende, nåderik.'),
(4618, 49, 6, 'Dere som tror, om en ugudelig kommer til dere med et rykte, så bring på det rene, så dere ikke rammer folk i uvitenhet og så angrer på det dere har gjort!'),
(4619, 49, 7, 'Vit at Guds sendebud er blant dere. Om han i mange saker skulle høre på dere, ville dere få ubehageligheter. Men Gud har gjort troen kjær for dere, og gjort den vakker i deres hjerter, og gitt dere motvilje mot vantro, synd og ulydighet. Disse er de rettsindige,'),
(4620, 49, 8, 'ved godhet og nåde fra Gud. Gud vet, er vis.'),
(4621, 49, 9, 'Om to grupper av de troende kommer i strid, skal dere ordne opp mellom dem. Om en av dem så gjør overgrep mot den andre, så bekjemp den som gjør overgrep til den føyer seg etter Guds bud. Om den føyer seg, så skal dere ordne forholdet mellom dem med rettferd, og la rimelighet råde. Gud liker de rimelige.'),
(4622, 49, 10, 'De troende er jo brødre, så ordne opp mellom deres to brødre, og frykt Gud, så dere må finne nåde.'),
(4623, 49, 11, 'Dere som tror, ingen må spotte andre, kanskje er de bedre enn dem, og ingen kvinner andre kvinner, kanskje er de bedre enn dem. Tal ikke ondt om hverandre, og belast ikke hverandre med klengenavn. Et slikt ondt navn er «synder», etter at troen er antatt. De som ikke snur om, de handler sannelig ondt.'),
(4624, 49, 12, 'Dere som tror, unngå å gjøre for mange formodninger. Noen formodninger er synd. Spioner ikke, og baktal ikke hverandre. Ville vel noen like å spise kjøttet av sin døde bror som en åtselsfugl? Dere ville avsky det! Så frykt Gud! Gud viser miskunn, er nåderik.'),
(4625, 49, 13, 'Dere mennesker, Vi har skapt dere som mann og kvinne og gjort dere til folk og stammer, slik at dere må kjenne hverandre. Den mest ansette av dere i Guds øyne er den mest gudfryktige. Gud vet, er vel underrettet.'),
(4626, 49, 14, 'Beduinene sier: «Vi tror.» Si: «Dere tror ikke, men si heller: ’Vi har antatt islam,’ for troen er ennå ikke gått inn i deres hjerter. Hvis dere adlyder Gud og Hans sendebud, vil Han ikke beskjære deres gjerninger det minste. Gud er tilgivende, nåderik.»'),
(4627, 49, 15, 'Troende er kun de som tror på Gud og Hans sendebud og så ikke tviler, men satser liv og eiendom for Guds sak. Disse er de oppriktige.'),
(4628, 49, 16, 'Si: «Skulle vel dere belære Gud om hva deres religion er, Gud, som kjenner alt i himlene og på jord? Gud vet om alle ting!»'),
(4629, 49, 17, 'De ser det som en gunst mot deg at de har antatt islam! Si: «Se det ikke som en gunst mot meg at dere har antatt islam. Nei, det er Gud som viser dere gunst ved at Han ledet dere til troen, hvis dere snakker sant.»'),
(4630, 49, 18, 'Gud kjenner himlenes og jordens mysterier. Gud er observant for det dere gjør.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Ved den ærefulle Koran!'),
(4632, 50, 2, 'Nei, de undrer seg over at en advarer er kommet til dem fra deres egne rekker, og den vantro sier: «Dette er en selsom ting!'),
(4633, 50, 3, 'Når vi er døde og er blitt til jord? Det ville være en usannsynlig tilbakevending!»'),
(4634, 50, 4, 'Vi vet hva jorden tar av dem. Hos Oss er en Bok som inneholder alt!'),
(4635, 50, 5, 'Nei, de fornektet sannheten da den kom til dem, så de befinner seg i en floket situasjon.'),
(4636, 50, 6, 'Har de ikke sett på himmelen over seg, hvordan Vi har laget den og prydet den, og den er uten en sprekk?'),
(4637, 50, 7, 'Og jorden har Vi bredt ut, og satt faste fjell på den, og Vi har latt vokse frem på den all slags herlige arter,'),
(4638, 50, 8, 'til innsikt og ettertanke for hver botferdig Guds tjener.'),
(4639, 50, 9, 'Vi sender fra oven velsignet vann og frembringer derved haver og korn til å høste,'),
(4640, 50, 10, 'og høyt ragende palmer med tette fruktklaser,'),
(4641, 50, 11, 'til livsunderhold for Guds tjenere. Derved har Vi gitt liv til et land i dvale. Slik blir oppstandelsen!'),
(4642, 50, 12, 'Fornektet sannheten har før dem Noas folk, Brønnfolket, Thamod,'),
(4643, 50, 13, 'Ad, Farao, Lots brødre'),
(4644, 50, 14, 'Skogfolket og Tubbas folk. Alle holdt sendebudene for løgnere, og min trussel ble realitet.'),
(4645, 50, 15, 'Har vel den første skapelsen gjort Oss kraftløse? Nei, men de er i tvil om en ny skapelse.'),
(4646, 50, 16, 'Vi har skapt mennesket, og Vi vet hvor hans tanker vandrer. Vi er ham nærmere enn halspulsåren.'),
(4647, 50, 17, 'Når de to noterende engler noterer, sittende på høyre og venstre side,'),
(4648, 50, 18, 'kan han ikke si et ord uten at en overvåker er klar hos ham.'),
(4649, 50, 19, 'I dødsuklarheten kommer sannheten: «Dette er det du skydde.»'),
(4650, 50, 20, 'Og det støtes i basunen. «Dette er dagen det ble truet med.»'),
(4651, 50, 21, 'Alle kommer sammen med en som jager på, og et vitne,'),
(4652, 50, 22, '«Du brydde deg ikke om dette, så nå har Vi tatt bort ditt slør, og ditt syn er i dag skarpt.»'),
(4653, 50, 23, 'Og hans følgesvenn vil si: «Dette er det som er gjort klart fra min side.»'),
(4654, 50, 24, 'Det lyder: «Dere to, kast i helvete hver oppsetsig vantro'),
(4655, 50, 25, 'som forhindrer det som godt er, som går over grensen, som sår tvil,'),
(4656, 50, 26, 'som setter en gud ved Guds side, kast ham i svær straff!»'),
(4657, 50, 27, 'Og hans følgesvenn vil si: «Jeg har ikke satt ham opp, han var langt kommen i villfarelse.»'),
(4658, 50, 28, 'Han sier: «Strides ikke i Mitt nærvær! Jeg har sendt dere advarsler i forveien,'),
(4659, 50, 29, 'og ordet forandres ikke hos Meg. Jeg gjør ikke Mine tjenere urett.»'),
(4660, 50, 30, 'På den dag sier Vi til helvete: «Er du fullpakket?» Og det svarer: «Finnes det enda flere?»'),
(4661, 50, 31, 'Og paradiset blir brakt nær de gudfryktige, ikke langt borte.'),
(4662, 50, 32, '«Dette er det dere ble lovet, hver botferdig og påpasselig,'),
(4663, 50, 33, 'som frykter den Barmhjertige i det skjulte, og kommer med et botferdig hjerte!'),
(4664, 50, 34, 'Tred inn i fred! Dette er evighetens dag.»'),
(4665, 50, 35, 'Der får de alt de ønsker, og hos Oss er mer til!'),
(4666, 50, 36, 'Hvor mange slektledd har Vi ikke utslettet før dem, mer fryktinngytende enn dem. De streifer rundt i landet, mon der var et tilfluktssted?'),
(4667, 50, 37, 'I dette er visselig en påminnelse for den som har hjertets forstand, eller gir øre med våken oppmerksomhet.'),
(4668, 50, 38, 'Vi skapte himlene og jorden og det som mellom dem er på seks dager, og ingen tretthet rørte Oss.'),
(4669, 50, 39, 'Så tål det de sier og lovpris Herren ved soloppgang og solnedgang,'),
(4670, 50, 40, 'og om natten pris Ham også, og etter bønnen.'),
(4671, 50, 41, 'Lytt etter den dag da utroperen roper fra et nært sted,'),
(4672, 50, 42, 'den dag da de virkelig hører skriket. Det er oppstandelsens dag!'),
(4673, 50, 43, 'Det er Vi som gir liv og død, og hos Oss er reisens mål.'),
(4674, 50, 44, 'Den dag jorden spalter seg for de døde, og de haster frem, dette er en samling som er lett for Oss.'),
(4675, 50, 45, 'Vi kjenner vel til det de sier. Du kan ikke tvinge dem. Men forman med Koranen dem som frykter Min trussel.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Ved dem som skrider hurtig frem,'),
(4677, 51, 2, 'de lastbærende,'),
(4678, 51, 3, 'de lett glidende,'),
(4679, 51, 4, 'de som fordeler ting!'),
(4680, 51, 5, 'Det som dere er truet med er visselig sant,'),
(4681, 51, 6, 'og dommen inntreffer for visst!'),
(4682, 51, 7, 'Ved himmelen med alle sine baner,'),
(4683, 51, 8, 'dere fører motstridende tale,'),
(4684, 51, 9, 'og noen føres i falsk lei!'),
(4685, 51, 10, 'Død over alle løgnmakere,'),
(4686, 51, 11, 'som i sin forvirring tar seg blindt til rette,'),
(4687, 51, 12, 'og spør: «Når inntreffer dommens dag?»'),
(4688, 51, 13, 'Jo, den dag de prøves over Ilden:'),
(4689, 51, 14, '«Smak deres prøvelse! Dette er det dere ville ha påskyndet!»'),
(4690, 51, 15, 'De gudfryktige skal være i paradisets haver blant kilder,'),
(4691, 51, 16, 'og mottar det Herren gir dem, de handlet vel før dette,'),
(4692, 51, 17, 'brukte lite av natten til søvn,'),
(4693, 51, 18, 'og bad ved morgengry om tilgivelse,'),
(4694, 51, 19, 'og tiggere og utstøtte hadde del i det de eide.'),
(4695, 51, 20, 'På jorden er jærtegn for dem som har troens visshet,'),
(4696, 51, 21, 'og i dere selv! Kan dere ikke se?'),
(4697, 51, 22, 'I himmelen beror deres underhold, og det dere er lovet.'),
(4698, 51, 23, 'Ved himmelens og jordens Herre, det er så sant som at dere kan tale!'),
(4699, 51, 24, 'Har historien om Abrahams ærverdige gjester nådd deg?'),
(4700, 51, 25, 'Da de kom inn til ham, hilste de: «Fred,» og han svarte «Fred! Fremmede folk?»'),
(4701, 51, 26, 'Så gikk han bort til sine husfolk, og kom med en gjøkalv,'),
(4702, 51, 27, 'satte den for dem, og sa: «Vil dere ikke spise?»'),
(4703, 51, 28, 'Da ble han grepet av frykt for dem, men de sa: «Frykt ikke.» Og de bebudet ham en oppvakt gutt.'),
(4704, 51, 29, 'Så trådte hans hustru frem i stort opprør, slo seg i ansiktet, og sa: «En gammel, ufruktbar kone?»'),
(4705, 51, 30, 'De sa: «Så sier Herren! Han er den Vise, den Allvitende.»'),
(4706, 51, 31, 'Abraham sa: «Hva er så deres ærende, dere utsendinger?»'),
(4707, 51, 32, 'De svarte: «Vi er sendt til et syndig folk,'),
(4708, 51, 33, 'for å sende over dem steiner av leire,'),
(4709, 51, 34, 'merket av Herren for de lettsindige.'),
(4710, 51, 35, 'Vi har ført ut de troende som var der,'),
(4711, 51, 36, 'men vi fant bare ett hus der med Gud hengivne.»'),
(4712, 51, 37, 'Og vi etterlot et tegn deri for dem som frykter en smertelig straff.'),
(4713, 51, 38, 'Likeledes i Moses, da Vi sendte ham til Farao, med klar autorisasjon.'),
(4714, 51, 39, 'Men han vendte seg bort, sikker på sin makt, og sa: «En trollmann, eller en besatt!»'),
(4715, 51, 40, 'Så tok Vi ham fatt, ham og hans hær, og kastet dem i havet, for han var klanderverdig.'),
(4716, 51, 41, 'Likeledes i Ad, da Vi sendte over dem den fortærende vind,'),
(4717, 51, 42, 'som intet etterlot av det den møtte, men gjorde det til støv.'),
(4718, 51, 43, 'Likeledes i Thamod, da det ble sagt til dem: «Nyt livet inntil videre!»'),
(4719, 51, 44, 'Men de satte seg ut over Herrens bud, og så tok tordenskrallet dem, med åpne øyne,'),
(4720, 51, 45, 'og de kunne ikke stå imot eller finne hjelp.'),
(4721, 51, 46, 'Og Noas folk tidligere. De var folk som levde i synd.'),
(4722, 51, 47, 'Vi bygget himmelen med kraft, og Vi gjør det stort!'),
(4723, 51, 48, 'Og jorden, den har Vi bredt ut. Hvilken utmerket tilrettelegger!'),
(4724, 51, 49, 'Alt har Vi skapt parvis, så dere må komme til ettertanke.'),
(4725, 51, 50, 'Så fly hen til Gud! Jeg er en klar advarer til dere fra Ham!'),
(4726, 51, 51, 'Sett ingen annen gud ved Guds side! Jeg er en klar advarer til dere fra Ham.'),
(4727, 51, 52, 'Så har heller intet sendebud kommet til folk som levde før dem, uten at de sa: «Trollmann, eller besatt!»'),
(4728, 51, 53, 'Har de latt det gå i arv til hverandre? De er oppsetsige mennesker.'),
(4729, 51, 54, 'Så vend deg bort fra dem, og du vil ikke bli bebreidet,'),
(4730, 51, 55, 'Og forman, for påminnelsen gagner de troende.'),
(4731, 51, 56, 'Jeg har skapt dsjinner og mennesker kun for at de skal tjene Meg.'),
(4732, 51, 57, 'Jeg ønsker ikke underhold fra dem. Jeg ønsker ikke at de skal bespise Meg.'),
(4733, 51, 58, 'Gud er Forsørgeren som har makt og er fast.'),
(4734, 51, 59, 'De urettferdige får samme lodd som deres likesinnede fikk. De trenger ikke skynde på Meg.'),
(4735, 51, 60, 'Så ve over de vantro, for deres dag som de er stilt i utsikt!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Ved berget,'),
(4737, 52, 2, 'og den skrevne skrift'),
(4738, 52, 3, 'på utbredt pergament,'),
(4739, 52, 4, 'ved det besøkte tempel,'),
(4740, 52, 5, 'ved det høyt hvelvede tak,'),
(4741, 52, 6, 'og det brusende hav,'),
(4742, 52, 7, 'Herrens straff vil visselig inntreffe!'),
(4743, 52, 8, 'Ingen kan avverge den!'),
(4744, 52, 9, 'Den dag, når himmelen snurrer rundt,'),
(4745, 52, 10, 'og fjellene beveger seg,'),
(4746, 52, 11, 've denne dag over dem som kaller sannhet for løgn,'),
(4747, 52, 12, 'og driver med tomt snakk og lek,'),
(4748, 52, 13, 'den dag, når de støtes i helvetes ild!'),
(4749, 52, 14, '«Dette er Ilden, som dere fornektet!'),
(4750, 52, 15, 'Er dette trolldom, eller kan dere ikke se?'),
(4751, 52, 16, 'Møt den, og bær med tålmod eller ikke, det er det samme for dere. Dere belønnes bare for det dere har gjort.»'),
(4752, 52, 17, 'De gudfryktige skal være i paradisets haver og lykksalighet,'),
(4753, 52, 18, 'idet de gleder seg over det Herren har gitt dem. Herren har bevart dem for helvetes straff.'),
(4754, 52, 19, '«Spis og drikk og måtte det bekomme dere vel, for det dere har gjort.»'),
(4755, 52, 20, 'De ligger tilbakelenet på leier, rekkevis, og Vi forener dem parvis med mørkøyde kvinner.'),
(4756, 52, 21, 'De som tror, og hvis etterkommere fulgte dem i troen, dem vil Vi føre sammen med deres etterkommere, og Vi forminsker ikke deres innsats det ringeste. Hver mann står for det han har fortjent!'),
(4757, 52, 22, 'Og Vi forsørger dem rikelig med frukt og kjøtt slik de ønsker,'),
(4758, 52, 23, 'mens der går et beger rundt, som ikke innebærer tomt prat eller synd.'),
(4759, 52, 24, 'Og det er unge gutter som går rundt blant dem og betjener dem, skjønne som skjulte perler.'),
(4760, 52, 25, 'Og de henvender seg til hverandre og samtaler, og sier:'),
(4761, 52, 26, '«Tidligere var vi blant våre, og gikk i frykt,'),
(4762, 52, 27, 'men Gud var god mot oss, og bevarte oss fra den hete vindens straff.'),
(4763, 52, 28, 'Vi påkalte Ham tidligere. Han er den Rettsindige, den Nåderike.»'),
(4764, 52, 29, 'Så forman! Ved Herrens nåde er du verken en spåmann eller besatt.'),
(4765, 52, 30, 'Eller sier de: «En skald! Vi vil avvente skjebnens trekk for ham.»'),
(4766, 52, 31, 'Si: «Bare vent! Jeg venter sammen med dere.»'),
(4767, 52, 32, 'Kan det være deres drømmer som inngir dem slikt? Eller er de oppsetsige mennesker?'),
(4768, 52, 33, 'Eller de sier: «Det er bare noe han sier frem.»'),
(4769, 52, 34, 'Nei, de tror ikke! La dem bringe en tilsvarende forkynnelse, om de snakker sant.'),
(4770, 52, 35, 'Er de skapt av intet? Eller er de skaperne?'),
(4771, 52, 36, 'Har de skapt himlene og jorden? Nei, de er ikke overbeviste.'),
(4772, 52, 37, 'Eller har de hånd om Herrens skatter? Er det de som bestemmer?'),
(4773, 52, 38, 'Eller har de en stige, hvorfra de kan lytte ved himmelens port? Så la den som lytter komme med et klart bevis!'),
(4774, 52, 39, 'Eller er det slik at Han har døtre, og dere sønner?'),
(4775, 52, 40, 'Har du bedt dem om lønn, så de er trykket av gjeld?'),
(4776, 52, 41, 'Eller har de adgang til det skjulte, så de skriver ned?'),
(4777, 52, 42, 'Søker de en list? De vantro, de blir overlistet!'),
(4778, 52, 43, 'Har de en annen gud enn Gud? Ære være Ham! Han er fjernt fra det de setter ved Hans side!'),
(4779, 52, 44, 'Om de så stykker av himmelen falle ned, ville de si: «Det er haugskyer!»'),
(4780, 52, 45, 'Så la dem være, til de møter deres dag når de blir som slått av lynet,'),
(4781, 52, 46, 'den dag når deres list ikke hjelper dem, og de ikke finner hjelp.'),
(4782, 52, 47, 'De urettferdige har i vente en straff utover dette, men de fleste av dem vet ingenting.'),
(4783, 52, 48, 'Avvent tålmodig Herrens avgjørelse, for du er for Våre øyne, og lovpris Herren når du står opp'),
(4784, 52, 49, 'og ved nattetid, og når stjernene trekker seg tilbake.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Ved stjernen når den går ned!'),
(4786, 53, 2, 'Deres bysbarn farer ikke vill, og han tar ikke feil!'),
(4787, 53, 3, 'Ei heller taler han av eget påfunn!'),
(4788, 53, 4, 'Dette er intet annet enn en åpenbaring som er åpenbart!'),
(4789, 53, 5, 'En som er veldig i makt, har undervist ham.'),
(4790, 53, 6, 'Begavet med innsikt. Han stod der majestetisk'),
(4791, 53, 7, 'i den høyeste horisont.'),
(4792, 53, 8, 'Så nærmet han seg og svevet'),
(4793, 53, 9, 'på to buelengders avstand eller nærmere.'),
(4794, 53, 10, 'Så åpenbarte han for sin tjener det han åpenbarte.'),
(4795, 53, 11, 'Ikke har fantasien oppdiktet det han så!'),
(4796, 53, 12, 'Hvordan kan dere tviste med ham om det han selv har sett?'),
(4797, 53, 13, 'Han så ham også stige ned en annen gang,'),
(4798, 53, 14, 'ved det borteste lotustreet'),
(4799, 53, 15, 'ved Ma’wa-haven'),
(4800, 53, 16, 'da lotustreet var overdekket.'),
(4801, 53, 17, 'Blikket var fast, og vandret ikke!'),
(4802, 53, 18, 'Han så i sannhet ett av sin Herres største tegn.'),
(4803, 53, 19, 'Hva mener dere om al-Lat og al-Ussa,'),
(4804, 53, 20, 'og dertil den tredje gudinne, Manat?'),
(4805, 53, 21, 'Skal dere ha sønner, og Han døtre?'),
(4806, 53, 22, 'Det var en urettferdig fordeling!'),
(4807, 53, 23, 'Nei, de er bare navn, som dere og deres fedre har funnet på. Gud har ikke sendt noen autorisasjon for dem. De følger bare formodninger og det de selv ønsker, mens ledelsen faktisk er kommet til dem fra deres Herre.'),
(4808, 53, 24, 'Skal vel mennesket få alt det ønsker seg?'),
(4809, 53, 25, 'Gud tilhører det siste og det første!'),
(4810, 53, 26, 'Hvor mange engler er det ikke i himlene hvis forbønn ikke hjelper? Med mindre Gud har gitt samtykke for den Han vil og som finner Hans behag.'),
(4811, 53, 27, 'De som ikke tror på det kommende liv, gir englene kvinnenavn.'),
(4812, 53, 28, 'Men dette vet de ingenting om, de følger bare formodninger. Og formodninger hjelper ingenting mot sannhet.'),
(4813, 53, 29, 'Så vend deg bort fra den som vender Vår formaning ryggen, og ikke ønsker annet enn livet på jorden!'),
(4814, 53, 30, 'Dette er summen av deres kunnskap. Gud vet godt hvem som forvillet seg bort fra Hans vei, og Han vet godt hvem som lar seg lede.'),
(4815, 53, 31, 'Gud tilhører alt som er i himlene og på jord, så Han kan lønne dem som gjør ondt for deres gjerninger, og belønne dem som handler vel med det aller beste.'),
(4816, 53, 32, 'For dem som unngår grov synd og umoral, unntatt mindre feiltrinn, se, Gud er raus med tilgivelse. Han kjenner dere godt, da Han laget dere av jord og da dere var fostre i mors liv. Så rettferdiggjør ikke dere selv. Gud vet best hvem som er gudfryktige.'),
(4817, 53, 33, 'Hva mener du om den som vender ryggen til,'),
(4818, 53, 34, 'og gir lite, og bare motvillig?'),
(4819, 53, 35, 'Kjenner han vel det skjulte, så han kan se?'),
(4820, 53, 36, 'Er han ikke underrettet om hva som står på Mose skriftblader?'),
(4821, 53, 37, 'Eller Abrahams, han som'),
(4822, 53, 38, 'fullt ut oppfylte sin plikt? At ingen kan bære en annens byrde,'),
(4823, 53, 39, 'at intet tilkommer et menneske uten det han selv har strevet for, og'),
(4824, 53, 40, 'at hans strev skal komme for dagen.'),
(4825, 53, 41, 'Så vil han få sin lønn fullt ut!'),
(4826, 53, 42, 'At hos din Herre er slutten.'),
(4827, 53, 43, 'At Han er Den som fremkaller latter og gråt!'),
(4828, 53, 44, 'At Han er Den som fremkaller død og liv.'),
(4829, 53, 45, 'At Han skaper de to kjønn, hankjønn og hunkjønn,'),
(4830, 53, 46, 'av en dråpe som utstøtes.'),
(4831, 53, 47, 'At Han står for den annen skapelse.'),
(4832, 53, 48, 'At Han gir rikdom og velstand.'),
(4833, 53, 49, 'At Han er Sirius’ Herre.'),
(4834, 53, 50, 'At Han tilintetgjorde det gamle folket Ad,'),
(4835, 53, 51, 'og av Thamod sparte Han ingen.'),
(4836, 53, 52, 'Og Noas folk tidligere, de var særlig onde og oppsetsige.'),
(4837, 53, 53, 'Den sammenstyrtede by har Han latt falle over ende,'),
(4838, 53, 54, 'og det som dekker den, dekker den.'),
(4839, 53, 55, 'Hvilke av Guds velgjerninger vil du bestride?'),
(4840, 53, 56, 'Dette er en advarsel i tråd med tidligere advarsler!'),
(4841, 53, 57, 'Det overhengende er overhengende.'),
(4842, 53, 58, 'Bare Gud kan bringe det frem.'),
(4843, 53, 59, 'Er dere forundret over dette budskap?'),
(4844, 53, 60, 'Ler dere, og gråter ikke?'),
(4845, 53, 61, 'I ubekymret lek?'),
(4846, 53, 62, 'Fall ned for Gud, og tilbe Ham!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Timen kommer nær. Månen spalter seg.'),
(4848, 54, 2, 'Men om de får se et jærtegn, vender de seg bort og sier: «Mer trolldom!»'),
(4849, 54, 3, 'De fornekter sannhet og følger egne meninger, men alle ting står fast.'),
(4850, 54, 4, 'Det er kommet til deres kunnskap meddelelser med et avskrekkende innhold,'),
(4851, 54, 5, 'med dyp visdom, men advarsler hjelper ikke.'),
(4852, 54, 6, 'Så vend deg bort fra dem! Den dag når utroperen kaller inn til en fryktelig ting,'),
(4853, 54, 7, 'med nedslåtte blikk vil de komme frem fra gravene, som om de var svermende gresshopper,'),
(4854, 54, 8, 'med halsen utstrakt mot utroperen. De vantro vil si: «Dette er en hard dag!»'),
(4855, 54, 9, 'Noas folk fornektet sannhet før dem, og kalte Vår tjener for løgner, og sa: «Mannen er besatt!» Han ble forjaget.'),
(4856, 54, 10, 'Men han ropte til Herren: «Jeg er underlegen, så hjelp til Du!»'),
(4857, 54, 11, 'Så åpnet Vi himmelens porter for strømmende vann,'),
(4858, 54, 12, 'og lot kilder bryte frem av jorden, og vannene møttes som bestemt var.'),
(4859, 54, 13, 'Og Vi bar ham på et skip med planker og nagler,'),
(4860, 54, 14, 'som seilte for Våre øyne, en belønning for en som høstet utakk.'),
(4861, 54, 15, 'Vi etterlot det som et jærtegn. Men er det noen som vil la seg formane?'),
(4862, 54, 16, 'Hvordan var vel Min straff og Min advarsel?'),
(4863, 54, 17, 'Nå har Vi gjort Koranen lett tilgjengelig til formaning! Men finnes det noen som lar seg formane?'),
(4864, 54, 18, 'Ad forkastet, og hvordan var vel Min straff og Min advarsel?'),
(4865, 54, 19, 'Vi sendte over dem en hylende vind på en dag fylt av ulykke!'),
(4866, 54, 20, 'Den sopte menneskene med seg som stammer av opprevne palmetrær.'),
(4867, 54, 21, 'Hvordan var vel Min straff og Min advarsel?'),
(4868, 54, 22, 'Nå har Vi gjort Koranen lett tilgjengelig til formaning! Men finnes det noen som lar seg formane?'),
(4869, 54, 23, 'Thamod forkastet advarslene, og sa:'),
(4870, 54, 24, '«Skal vi følge et vanlig menneske fra vår egen krets? Da ville vi sannelig befinne oss i villfarelse og galskap.'),
(4871, 54, 25, 'Er formaningen blitt overlatt ham av oss alle? Nei, han er en frekk løgner.»'),
(4872, 54, 26, 'Men, i morgen skal de få vite hvem som er den frekke løgner!'),
(4873, 54, 27, 'Vi skal sende kamelhoppen som en prøvelse for dem. Så følg med, og bevar tålmodigheten!'),
(4874, 54, 28, 'Fortell dem at vannet skal fordeles blant dem, hver rasjon i sin tid.'),
(4875, 54, 29, 'Så ropte de på sin anfører, og han tok og lemlestet henne.'),
(4876, 54, 30, 'Hvordan var vel Min straff og Min advarsel?'),
(4877, 54, 31, 'Vi sendte over dem et eneste skrik, og de ble som tørt gjerdemateriale!'),
(4878, 54, 32, 'Nå har Vi gjort Koranen lett tilgjengelig til formaning! Men finnes det noen som lar seg formane?'),
(4879, 54, 33, 'Lots folk forkastet advarslene.'),
(4880, 54, 34, 'Vi sendte over dem en skur av småstein, unntatt Lots familie. Vi reddet dem ved daggry.'),
(4881, 54, 35, 'Som en nåde fra Oss. Slik belønner Vi den takknemlige.'),
(4882, 54, 36, 'Han hadde jo advart dem mot at Vi skulle slå til, men de tvilte på advarslene.'),
(4883, 54, 37, 'De hadde bedt ham overlevere sine gjester, så Vi slo dem med blindhet: «Smak nå Min straff og Min advarsel!»'),
(4884, 54, 38, 'Om morgenen kom over dem den bestemte straff,'),
(4885, 54, 39, '«Smak Min straff og Min advarsel!»'),
(4886, 54, 40, 'Nå har Vi gjort Koranen lett tilgjengelig til formaning! Men finnes det noen som lar seg formane?'),
(4887, 54, 41, 'Advarslene kom også til Faraos folk.'),
(4888, 54, 42, 'De forkastet Våre tegn, alle sammen. Så tok Vi dem fatt med et mektig, effektivt grep.'),
(4889, 54, 43, 'Er de vantro blant dere mer enn disse? Eller har dere fribrev i skriftene?'),
(4890, 54, 44, 'Eller hevder de: «Vi er folk som finner hjelp?»'),
(4891, 54, 45, 'Skaren skal bli slått, og de vil vende ryggen til flukt.'),
(4892, 54, 46, 'Nei, timen er fastsatt tid for dem, og timen er hard og bitter.'),
(4893, 54, 47, 'Sannelig, synderne befinner seg i villfarelse og galskap.'),
(4894, 54, 48, 'Den dag når de slepes hodekulls inn i Ilden: «Smak nå berøringen med Ilden!»'),
(4895, 54, 49, 'Vi har visselig skapt alt med beregning.'),
(4896, 54, 50, 'Vårt bud er bare ett eneste ord, som et blink med øyet.'),
(4897, 54, 51, 'Vi har utslettet deres likemenn, men finnes det noen som lar seg formane?'),
(4898, 54, 52, 'Alt de har gjort finnes i bøkene,'),
(4899, 54, 53, 'alt stort og smått er notert.'),
(4900, 54, 54, 'De gudfryktige skal være i paradisets haver, blant bekker,'),
(4901, 54, 55, 'på en sikker plass hos en effektiv konge.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'Den Barmhjertige'),
(4903, 55, 2, 'har belært om Koranen.'),
(4904, 55, 3, 'Han har skapt mennesket,'),
(4905, 55, 4, 'og lært ham den klare beskjed.'),
(4906, 55, 5, 'Sol og måne i fastsatte baner,'),
(4907, 55, 6, 'busker og trær faller Ham til fote.'),
(4908, 55, 7, 'Himmelen har Han hevet opp. Han har satt Vekten (likevekten),'),
(4909, 55, 8, 'så kluss ikke dere med vekten,'),
(4910, 55, 9, 'men gi rett vekt, og snyt ikke på vekten!'),
(4911, 55, 10, 'Jorden har han lagt på plass for alle skapninger.'),
(4912, 55, 11, 'På den finnes frukt, palmer med fruktkolber,'),
(4913, 55, 12, 'korn på strå, og aromatiske vekster.'),
(4914, 55, 13, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4915, 55, 14, 'Han skapte mennesket av leire, som pottemakeren,'),
(4916, 55, 15, 'og Han har skapt dsjinnene av klar ild.'),
(4917, 55, 16, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4918, 55, 17, 'Han er østens Herre og vestens Herre.'),
(4919, 55, 18, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4920, 55, 19, 'Han har bredt ut de to hav som møtes,'),
(4921, 55, 20, 'mellom dem er en skranke som de ikke kan forsere.'),
(4922, 55, 21, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4923, 55, 22, 'Fra dem begge kommer perler og koraller.'),
(4924, 55, 23, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4925, 55, 24, 'Ham tilhører skipene, hevet over havet lik landemerker.'),
(4926, 55, 25, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4927, 55, 26, 'Alt på jorden forgår,'),
(4928, 55, 27, 'men Herrens åsyn består, med makt og ære.'),
(4929, 55, 28, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4930, 55, 29, 'Alt som er i himlene og på jord henvender seg til Ham. Hver dag har Han noe fore.'),
(4931, 55, 30, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4932, 55, 31, 'Vi skal ta Oss av dere, dere to grupper av vekt (dsjinner og folk)!'),
(4933, 55, 32, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4934, 55, 33, 'Dere skarer av dsjinner og folk, om dere er i stand til å trenge gjennom himlenes og jordens regioner, så gjør det! Dere trenger ikke gjennom uten autorisasjon.'),
(4935, 55, 34, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4936, 55, 35, 'Det vil bli sendt mot dere stråler av flammer og smeltet kobber, og dere kan ikke finne hjelp.'),
(4937, 55, 36, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4938, 55, 37, 'Når himmelen sprekker opp og farges rød.'),
(4939, 55, 38, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4940, 55, 39, 'På denne dag spørres verken menneske eller dsjinn om sin synd.'),
(4941, 55, 40, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4942, 55, 41, 'Synderne gjenkjennes på sine merker, og de tas i luggen og bena.'),
(4943, 55, 42, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4944, 55, 43, 'Dette er helvete, som synderne fornektet,'),
(4945, 55, 44, 'og skal gå runden mellom det og kokende vann.'),
(4946, 55, 45, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4947, 55, 46, 'Men for dem som frykter Herrens maktstilling, for dem skal det være to paradisets haver.'),
(4948, 55, 47, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4949, 55, 48, 'Med skyggegivende grener.'),
(4950, 55, 49, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4951, 55, 50, 'Det finnes to rinnende kilder.'),
(4952, 55, 51, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4953, 55, 52, 'Det finnes to slag av alle frukter.'),
(4954, 55, 53, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4955, 55, 54, 'Der skal de ligge tilbakelenet på leier trukket med brokade, med havenes frukt hengende nær ved hånden.'),
(4956, 55, 55, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4957, 55, 56, 'Det er jomfruer med blyge blikk, som verken menneske eller dsjinn har rørt før dem.'),
(4958, 55, 57, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4959, 55, 58, 'Vakre som rubiner og koraller.'),
(4960, 55, 59, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4961, 55, 60, 'Skulle vel lønnen for det gode være annet enn godt?'),
(4962, 55, 61, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4963, 55, 62, 'Utenom disse finnes to haver,'),
(4964, 55, 63, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4965, 55, 64, '– dypgrønne,'),
(4966, 55, 65, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4967, 55, 66, '– med to rinnende kilder,'),
(4968, 55, 67, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4969, 55, 68, '– og frukt, dadler og granatepler.'),
(4970, 55, 69, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4971, 55, 70, 'Det finnes gode, vakre kvinner,'),
(4972, 55, 71, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4973, 55, 72, '– mørkøyde, holdt i telter,'),
(4974, 55, 73, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4975, 55, 74, '– som verken menneske eller dsjinn har rørt før dem.'),
(4976, 55, 75, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4977, 55, 76, 'Der skal de ligge tilbakelenet på grønne puter og skjønne matter.'),
(4978, 55, 77, 'Hvilke av Herrens velgjerninger vil dere fornekte?'),
(4979, 55, 78, 'Fylt av velsignelse er Herrens navn, Han som har makt og ære.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Når begivenheten inntreffer,'),
(4981, 56, 2, 'kan det ikke benektes at den inntreffer.'),
(4982, 56, 3, 'Den fornedrer og opphøyer.'),
(4983, 56, 4, 'Når jorden rystes i en veldig skaking,'),
(4984, 56, 5, 'når fjellene smuldres til smuler,'),
(4985, 56, 6, 'og blir som oppvirvlet støv,'),
(4986, 56, 7, 'da blir dere av tre slag:'),
(4987, 56, 8, 'De til høyre, hva med dem til høyre?'),
(4988, 56, 9, 'De til venstre, hva med dem til venstre?'),
(4989, 56, 10, 'Og de som ligger først i løpet? De som ligger først i løpet'),
(4990, 56, 11, '– disse er det som kommer i Herrens nærhet,'),
(4991, 56, 12, 'i lykksalighetens haver.'),
(4992, 56, 13, 'En stor flokk fra tidligere tider,'),
(4993, 56, 14, 'men få fra de senere.'),
(4994, 56, 15, 'På pyntede benker hviler de'),
(4995, 56, 16, 'overfor hverandre,'),
(4996, 56, 17, 'mens evig unge gutter går rundt blant dem'),
(4997, 56, 18, 'med skåler og krus og beger med den klareste drikk,'),
(4998, 56, 19, 'som man ikke får vondt i hodet eller mister vettet av,'),
(4999, 56, 20, 'med slike frukter som de velger seg ut,'),
(5000, 56, 21, 'og kjøtt og fugl som de ønsker seg.'),
(5001, 56, 22, 'Og storøyde kvinner er der,'),
(5002, 56, 23, 'lik vel forvarte perler.'),
(5003, 56, 24, 'Som belønning for deres innsats.'),
(5004, 56, 25, 'Der skal de ikke høre tomt snakk, og ingen bebreidelser,'),
(5005, 56, 26, 'bare ordene: «Fred, fred!»'),
(5006, 56, 27, 'Og de til høyre, hva med dem til høyre?'),
(5007, 56, 28, 'Blant tornefrie lotustrær'),
(5008, 56, 29, 'og tette akasier,'),
(5009, 56, 30, 'utstrakt skygge'),
(5010, 56, 31, 'og sprudlende vann,'),
(5011, 56, 32, 'frukt i overflod,'),
(5012, 56, 33, 'alltid for hånden og fri til å ta av,'),
(5013, 56, 34, 'på opphøyde leier.'),
(5014, 56, 35, 'Fullkomne har Vi skapt dem, paradisets kvinner,'),
(5015, 56, 36, 'som plettfrie jomfruer,'),
(5016, 56, 37, 'ømme og kjærlige, jevngamle'),
(5017, 56, 38, 'med dem til høyre,'),
(5018, 56, 39, '– en stor flokk fra tidligere tider,'),
(5019, 56, 40, 'og en stor flokk fra de senere.'),
(5020, 56, 41, 'Og de til venstre, hva med dem til venstre?'),
(5021, 56, 42, 'I het, kvelende vind og skåldende vann,'),
(5022, 56, 43, 'i skygge av kullsvart røyk,'),
(5023, 56, 44, 'som ikke svaler, og ikke er til behag.'),
(5024, 56, 45, 'Tidligere levde de et liv i vellevnet,'),
(5025, 56, 46, 'og turet frem i stor synd,'),
(5026, 56, 47, 'og pleide å si: «Når vi er døde og blitt til støv og tørre ben, skal vi da oppstå?'),
(5027, 56, 48, 'Og henfarne fedre også?»'),
(5028, 56, 49, 'Si: «Både de første og de siste,'),
(5029, 56, 50, 'alle vil bli samlet, til fastsatt tid en bestemt dag.»'),
(5030, 56, 51, 'Da vil dere som farer vill og holder sannhet for løgn,'),
(5031, 56, 52, 'få spise av Zaqqum-treet.'),
(5032, 56, 53, 'Fylle magen av det,'),
(5033, 56, 54, 'og drikke kokende vann ved siden av.'),
(5034, 56, 55, 'Og dere vil drikke som uttørstede kameler.'),
(5035, 56, 56, 'Dette er deres velkomsthilsen på dommens dag!'),
(5036, 56, 57, 'Det er Vi som har skapt dere! Hvorfor anser dere ikke dette for sant?'),
(5037, 56, 58, 'Hva med sæddråpene som dere avgir!'),
(5038, 56, 59, 'Har dere skapt dem? Eller er Vi skaperen?'),
(5039, 56, 60, 'Vi har fastsatt døden for dere, ingen kan foregripe Oss,'),
(5040, 56, 61, 'for å skifte dere ut med deres like og la dere oppstå i en skikkelse dere ikke kjenner.'),
(5041, 56, 62, 'Dere er kjent med den første oppståen (fødsel). Hvorfor tenker dere ikke etter?'),
(5042, 56, 63, 'Har dere tenkt over jorden dere dyrker?'),
(5043, 56, 64, 'Er det dere som står bak utsæden, eller er det Vi som sår?'),
(5044, 56, 65, 'Om Vi ville, kunne Vi gjøre den til en unyttig vekst, og dere ville utbryte:'),
(5045, 56, 66, '«Vi er ruinert!»'),
(5046, 56, 67, '«Vi er plyndret!»'),
(5047, 56, 68, 'Har dere tenkt over vannet som dere drikker?'),
(5048, 56, 69, 'Er det dere som sender det ned fra skyene? Eller er det Vi som sender det?'),
(5049, 56, 70, 'Om Vi ville, kunne Vi gjøre det udrikkelig! Hvorfor er dere da ikke takknemlige?'),
(5050, 56, 71, 'Har dere tenkt over ilden, som dere tenner?'),
(5051, 56, 72, 'Har dere frembrakt dens brensel? Eller Vi?'),
(5052, 56, 73, 'Det er Vi som har gjort dette, som en påminnelse, og til nytte for dem som er i ørkenen.'),
(5053, 56, 74, 'Så pris din Herre den Veldiges navn.'),
(5054, 56, 75, 'Nei, jeg sverger ved stjernenes nedgang,'),
(5055, 56, 76, 'og dette er en stor ed, om dere bare hadde visst.'),
(5056, 56, 77, 'Dette er en kostelig Koran'),
(5057, 56, 78, 'som finnes i en vel forvart Bok!'),
(5058, 56, 79, 'Bare de rene får røre den!'),
(5059, 56, 80, 'En åpenbaring fra all verdens Herre!'),
(5060, 56, 81, 'Skulle dere da forsmå denne forkynnelse?'),
(5061, 56, 82, 'Og gjøre det til en livssak å erklære den for løgn?'),
(5062, 56, 83, 'Når den døende har sjelen oppe i halsen,'),
(5063, 56, 84, 'mens dere står og ser på,'),
(5064, 56, 85, 'men Vi er ham nærmere enn dere, uten at dere ser det,'),
(5065, 56, 86, 'så, hvis dere ikke var Oss underlagt,'),
(5066, 56, 87, 'hvorfor bringer dere da ikke hans sjel tilbake, om dere snakker sant?'),
(5067, 56, 88, 'Hvis han er av dem som får komme i Herrens nærhet,'),
(5068, 56, 89, 'en aromatisk luftning, hvile, og lykksalighetens have.'),
(5069, 56, 90, 'Er han av dem til høyre,'),
(5070, 56, 91, '– «Fred være med deg, som er av høyre hånds flokk!»'),
(5071, 56, 92, 'Men er han av dem som holdt sannhet for løgn, og fór vill,'),
(5072, 56, 93, '– en velkomsthilsen av kokende vann,'),
(5073, 56, 94, 'og helvetes brann.'),
(5074, 56, 95, 'Dette er den visse sannhet!'),
(5075, 56, 96, 'Så pris din Herre den Veldiges navn!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Alt i himlene og på jord priser Gud! Han er den Mektige, den Vise.'),
(5077, 57, 2, 'Ham tilhører herredømmet over himlene og jorden! Han gir liv og død, Han evner alle ting.'),
(5078, 57, 3, 'Han er den Første og den Siste, den Åpenbare og den Skjulte, Han kjenner til alle ting.'),
(5079, 57, 4, 'Han er det, som skapte himlene og jorden på seks dager. Så tok Han plass på tronen. Han vet hva som går ned i jorden, og hva som kommer ut av den, hva som kommer ned fra himmelen, og hva som går opp dit. Han er med dere hvor dere enn er. Gud ser hva dere gjør.'),
(5080, 57, 5, 'Ham tilhører herredømmet over himlene og jorden! Fremfor Gud bringes alle ting.'),
(5081, 57, 6, 'Han lar natten bli til dag, og lar dagen bli til natt. Han kjenner vel til det som bor i hjertene.'),
(5082, 57, 7, 'Tro på Gud og Hans sendebud, og gi gode gaver av det Han har latt dere overta! Dem av dere som tror og gir gode gaver, venter stor lønn.'),
(5083, 57, 8, 'Hva er i veien med dere, at dere ikke tror på Gud, når Sendebudet kaller dere til å tro på deres Herre, og Han har gjort avtale med dere, så fremt dere tror?'),
(5084, 57, 9, 'Han er det, som åpenbarer Sin tjener skriftens klare ord for å føre dere ut fra mørket, til lys. Sannelig, Gud er mild og nåderik mot dere.'),
(5085, 57, 10, 'Hva er i veien med dere, at dere ikke vil gi for Guds sak, når himlene og jorden så allikevel til slutt er Guds arv? De av dere som gir før seieren er vunnet, og som kjemper, er ikke like, men har en høyere rang enn dem som gir og kjemper etterpå. Men Gud har lovet alle det beste. Gud er vel underrettet om hva dere gjør.'),
(5086, 57, 11, 'Så hvem gir Gud et godt lån, så Han kan fordoble det for ham? Og han har en herlig lønn i vente!'),
(5087, 57, 12, 'Den dag når du ser de troende menn og kvinner, og deres lys skinner foran dem og på deres høyre side: «Et godt budskap for dere på denne dag, paradisets haver, hvor bekker sildrer, og der skal dere være og bli! Dette er den store seier!»'),
(5088, 57, 13, 'På denne dag sier hyklerne, menn og kvinner, til dem som tror: «Vent på oss, så vi kan låne fra deres lys!» Det lyder: «Gå tilbake, og søk lys!» Og det settes en mur mellom dem med en port. Innenfor den er nåden, og utenfor på den andre siden er straffen.'),
(5089, 57, 14, 'De roper inn til dem: «Vi var da sammen med dere?» Og de svarer: «Ja, men dere gav dere hen i fristelser, stilte dere avventende, tvilte, og deres egne ønsker forførte dere, til Guds bestemmelse inntraff. Forføreren forførte dere vedrørende Gud!'),
(5090, 57, 15, 'Så denne dag mottar ingen løsepenger fra dere, eller fra de vantro. Deres herberge er Ilden! Den tar vare på dere. En sørgelig skjebne!»'),
(5091, 57, 16, 'Er ikke tiden inne for dem som tror, at deres hjerter viser ydmykhet for Guds påminnelse og den sannhet Han har åpenbart, så de ikke blir som dem som tidligere ble gitt skriften, hvem tiden falt lang og deres hjerter forherdet seg, så mange av dem ble syndefulle.'),
(5092, 57, 17, 'Vit at Gud gir jorden liv etter dens dvale. Vi har forklart for dere jærtegnene, slik at dere må forstå.'),
(5093, 57, 18, 'Kvinner og menn som gir gode gaver, og gir Gud et godt lån, det fordobles for dem og de har en herlig lønn i vente.'),
(5094, 57, 19, 'De som tror på Gud og alle Hans sendebud, disse er de rettferdige og de som gir vitnemål i Herrens øyne. De har sin lønn i vente, og sitt lys. Men de vantro, som fornektet Vårt ord, de er helvetes beboere.'),
(5095, 57, 20, 'Vit at jordelivet kun er lek og tidsfordriv, og pryd og skryt dere imellom, og kappestrid etter mer eiendom og barn, som en regnskur, med vegetasjon som fryder de vantro. Men så visner den, og du ser den gulne, og så blir den tørre strå. I det hinsidige finnes hard straff, – og Guds tilgivelse og velbehag. Jordelivet er bare en bedragersk nytelse.'),
(5096, 57, 21, 'Kappes om Herrens tilgivelse, og et paradis, like vidt som himmel og jord, som er gjort klar for dem som tror på Gud og alle Hans sendebud! Dette er Guds godhet, Han gir den til dem Han vil. Gud er full av godhet!'),
(5097, 57, 22, 'Ingen hjemsøkelse inntreffer på jorden eller overfor dere selv, uten at det finnes i en Bok, før Vi skaper den. Dette er lett for Gud.'),
(5098, 57, 23, 'Dette for at dere ikke skal være bedrøvet over det som går dere forbi, og ikke fryde dere for meget over det dere får. Gud liker ikke den innbilske skrythals,'),
(5099, 57, 24, 'dem som er gjerrige og oppfordrer folk til gjerrighet. Om noen vender seg bort, så er Gud den Selvtilstrekkelige, den Lovpriste.'),
(5100, 57, 25, 'Vi har sendt Våre sendebud med klar beskjed, og sendte med dem skriften og Vekten (likevekten) for folk å utøve rettferd. Og Vi har sendt jernet, hvori stor makt ligger, og nytte for menneskene, og for at Gud i det skjulte kan få vite hvem som hjelper Ham og Hans sendebud. Gud er sterk, mektig.'),
(5101, 57, 26, 'Og Vi sendte Noa og Abraham, og la profetkallet blant deres etterkommere, og skriften. Noen av dem har funnet rett vei, men mange er syndefulle.'),
(5102, 57, 27, 'Og Vi lot følge i deres fotspor Våre sendebud. Således sendte Vi Jesus, Marias sønn, og gav ham evangeliet, og la i deres hjerter som følger ham, mildhet og medfølelse. Munkevesenet fant de på, Vi foreskrev dem det ikke, kun for å søke Guds velbehag. Men de overholdt det ikke på rette måte. Vi gav de troende blant dem deres lønn, men mange av dem er syndefulle.'),
(5103, 57, 28, 'Dere som tror, frykt Gud og tro Hans sendebud, og Han vil gi dere dobbelt mål av Sin nåde og gi dere lys å vandre i, og tilgivelse, Gud er tilgivende, nåderik,'),
(5104, 57, 29, 'så skriftfolket kan vite at de ikke bestemmer over noe av Guds godhet, at godheten er i Guds hånd. Han gir dem til den Han vil. Gud har godhets fylde!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'Gud har hørt hennes innlegg, som talte sin sak overfor deg vedrørende sin ektemann, og klaget til Gud. Gud hørte deres samtale. Gud hører, er observant.'),
(5106, 58, 2, 'De blant dere som forstøter sine hustruer ved «ryggformelen» – du er meg likegyldig som min mors rygg – så er de dog ikke å anse som deres mødre. Deres mødre er kun de som har født dem, og de fører forkastelig og falsk tale. Men Gud er visselig unnskyldende og tilgivende.'),
(5107, 58, 3, 'De som forstøter sine hustruer ved «ryggformelen», og så ønsker å gå tilbake på det de sa, de skal sette fri en slave før de to gjenopptar samlivet. Dette formanes dere til. Gud er vel underrettet om det dere gjør.'),
(5108, 58, 4, 'Den som ikke finner økonomisk utvei, la ham faste i to påfølgende måneder før de to gjenopptar samlivet. Den som ikke er i stand til dette, la ham bespise seksti fattige. Dette for at dere skal tro på Gud og Hans sendebud. Dette er grenser som Gud har satt. De vantro venter smertelig straff.'),
(5109, 58, 5, 'De som setter seg opp mot Gud og Hans sendebud, vil bli kastet til jorden, som de før dem ble kastet over ende. Vi har nå åpenbart klar beskjed, og de vantro har ydmykende straff i vente,'),
(5110, 58, 6, 'den dag Gud gjenoppvekker dem alle og forteller dem hva de bedrev. Gud har notert det, men de har glemt det. Gud er vitne til alle ting.'),
(5111, 58, 7, 'Ser du ikke at Gud kjenner til alt som er i himlene og på jord? Det er ingen hemmelig rådslagning mellom tre, uten at Han er den fjerde, eller fem, uten at Han er den sjette av dem. Heller ikke mellom færre eller flere enn dette, uten at Han er med dem, hvor de enn er. Så vil Han fortelle dem hva de bedrev på oppstandelsens dag. Gud kjenner til alle ting.'),
(5112, 58, 8, 'Du har vel sett dem som ble forbudt å holde hemmelige råd, og så fortsetter med det som ble dem forbudt, og rådslår med hverandre i synd og fiendskap, og i ulydighet mot Sendebudet? Når de kommer til deg, hilser de deg med en hilsen som Gud aldri har gitt deg, og sier til seg selv: «Hvorfor refser ikke Gud oss for det vi sier?» Helvete er til pass for dem, hvor de møter stekende ild. Hvilken sørgelig skjebne!'),
(5113, 58, 9, 'Dere som tror, når dere holder hemmelig råd, så rådslå ikke i synd og fiendskap og ulydighet mot Sendebudet, men rådslå i fromhet og gudsfrykt. Frykt Gud, hos Ham vil dere bli samlet.'),
(5114, 58, 10, 'Hemmelig råd er Satans verk, for å gjøre de troende bedrøvet, men han kan ikke skade dem det ringeste, uten Guds tillatelse. På Gud skal de troende forlate seg!'),
(5115, 58, 11, 'Dere som tror, når det sies til dere «Gi plass» i forsamlingen, så gi plass, slik at Gud vil gi dere plass. Når det sies: «Reis opp,» så reis opp, slik at Gud vil heve opp i rang dem som tror og har fått kunnskap. Gud er vel underrettet om hva dere gjør.'),
(5116, 58, 12, 'Dere som tror, når dere vil rådslå med Sendebudet, så gi en god gave før deres samtale. Dette er godt og mest sømmelig for dere. Men om dere ikke finner økonomisk utvei, så er Gud tilgivende, nåderik.'),
(5117, 58, 13, 'Er dere betenkt på å gi en god gave før deres samtale? Om dere ikke gjør det, og Gud viser overbærenhet mot dere, så forrett bønnen, betal det rituelle bidrag, og adlyd Gud og Hans sendebud. Gud er vel underrettet om hva dere gjør.'),
(5118, 58, 14, 'Du har vel sett dem som slutter seg til folk som er under Guds vrede? De er ikke av dere og ikke av dem, og de sverger falskt og vet det!'),
(5119, 58, 15, 'Gud har gjort klar en hard straff for dem. Ille er det de har gjort!'),
(5120, 58, 16, 'De har gjort sine eder til et dekke, og har lagt hindringer på Guds vei. Dem venter en ydmykende straff.'),
(5121, 58, 17, 'Deres rikdom og barn hjelper dem ikke det ringeste mot Gud. Disse er Ildens folk, og der skal de være og bli.'),
(5122, 58, 18, 'Den dag når Gud gjenoppvekker dem alle, så sverger de overfor Ham som de sverger over dere, og tror de har noe å stå på. De er visselig løgnere!'),
(5123, 58, 19, 'Satan har fått dem i sin makt, og latt dem glemme Guds formaning. Disse utgjør Satans parti, og Satans parti, de er visselig tapere!'),
(5124, 58, 20, 'De som setter seg opp mot Gud og Hans sendebud, disse vil bli av de ynkeligste.'),
(5125, 58, 21, 'Gud har skrevet: «Jeg vil visselig seire, Jeg og Mine sendebud.» Gud er sterk, mektig.'),
(5126, 58, 22, 'Du vil ikke finne at folk som tror på Gud og dommens dag, har noe til overs for dem som setter seg opp mot Gud og Hans sendebud, om det så var deres fedre, deres sønner, deres brødre eller deres klan. Gud har skrevet troen i deres hjerter og styrket dem med sin ånd. Og Han vil føre dem inn i paradisets haver, hvor bekker sildrer, og her skal de være og bli. Gud har funnet velbehag i dem, og de i Ham. Disse utgjør Guds parti. Og Guds parti, dem vil det gå godt!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Alt som er i himlene og på jord, priser Gud! Han er den Mektige, den Vise.'),
(5128, 59, 2, 'Han er det som drev de vantro av skriftfolket bort fra sine bosteder, til en første samling. Dere trodde ikke at de skulle dra bort, og de trodde at deres befestninger ville beskytte dem mot Gud. Men Gud kom over dem fra et hold de ikke regnet med og kastet frykt i deres hjerter, så de ødela sine hus med egne hender og de troendes innsats. Så tenk over det, dere som har innsikt.'),
(5129, 59, 3, 'Om Gud ikke har bestemt evakuering for dem, så ville Han refset dem i denne verden, og i den neste har de Ildens straff.'),
(5130, 59, 4, 'Dette fordi de brøt med Gud og Hans sendebud. Og den som bryter med Gud, Gud er streng med straffen!'),
(5131, 59, 5, 'Det dere har hugget ned av palmetrær, eller latt stå på sine røtter, dette var med Guds tillatelse og for å gjøre synderne til skamme.'),
(5132, 59, 6, 'Og det krigsbytte som Gud gav Sitt sendebud etter dem, trengte dere ikke å oppby verken hester eller kameler for. Men Gud gir Sine sendebud makt over dem Han vil. Gud evner alt.'),
(5133, 59, 7, 'Det krigsbytte som Gud gav Sitt sendebud fra folket i byene, tilhører Gud og Hans sendebud, nærskyldte, foreldreløse, fattige og veifarende, så det intet blir til en utdelingsrunde blant de velstående blant dere. Det Sendebudet gir dere, ta det. Og hold dere borte fra det han forbyr dere. Frykt Gud, Gud er streng med straffen.'),
(5134, 59, 8, 'Det er for de fattige emigrantene fra Mekka, de som ble drevet bort fra sine hjem og sin eiendom, idet de søkte Guds godhet og velbehag, og hjelper Gud og Hans sendebud. Disse er de oppriktige.'),
(5135, 59, 9, 'Og de som før var installert trygt i hus og i troen, liker dem som har emigrert til dem, og føler intet begjær i sine bryst etter det disse er blitt gitt, og setter andre fremfor seg selv, selv om de er i trange kår. De som bevares mot sin egen gjerrighet, dem vil det gå godt.'),
(5136, 59, 10, 'Og de som ankom etter dem, sier: «Herre, tilgi oss og våre brødre som kom før oss til troen, og legg ikke i våre hjerter nag mot dem som tror. Herre, Du er mild og nåderik.»'),
(5137, 59, 11, 'Du har vel sett hyklerne, som sier til sine vantro brødre av skriftfolket: «Om dere drives bort, vil vi dra ut sammen med dere, og vi vil aldri adlyde noen når det gjelder dere. Om dere bekjempes, skal vi visselig hjelpe dere.» Gud bevitner, de er sannelig løgnere.'),
(5138, 59, 12, 'Om de drives bort, så drar de ikke ut med dem, og om de bekjempes, så hjelper de dem ikke. Og om de kom dem til unnsetning, ville de vende seg til flukt. Så de hjelpes ikke.'),
(5139, 59, 13, 'Dere skaper sannelig større frykt i deres hjerter enn Gud! Dette fordi de er et folk som intet forstår.'),
(5140, 59, 14, 'De vil ikke kjempe mot dere samlet, men i befestede byer eller bak murer. Deres kampkraft er stor dem imellom. Du anser dem som en enhet, men deres hjerter er forskjellige. Dette fordi de er et folk som intet forstår.'),
(5141, 59, 15, 'Som de som like før dem fikk smake følgene av sin adferd. Det venter dem en smertelig straff.'),
(5142, 59, 16, 'Det er som med Satan da han sa til mennesket: «Vær vantro,» og når han så ble vantro, sa han: «Jeg er ferdig med deg! For jeg frykter Gud, all verdens Herre.»'),
(5143, 59, 17, 'Og så blir enden for dem begge at de havner i Ilden, hvor de skal være og bli. Dette er de urettferdiges belønning.'),
(5144, 59, 18, 'Dere som tror, frykt Gud! Måtte enhver overveie hva Han har sendt i forveien til morgendagen. Frykt Gud! Gud er vel underrettet om det dere gjør.'),
(5145, 59, 19, 'Vær ikke som dem som glemmer Gud, og som Han lar glemme seg selv. Disse er syndefulle.'),
(5146, 59, 20, 'Ildens folk og paradisets folk er ikke like. Paradisets folk er de seirende.'),
(5147, 59, 21, 'Om Vi hadde sendt ned denne Koran på et fjell, ville du sett det synke sammen i ydmykhet og brytes i stykker av frykt for Gud. Slike lignelser lager Vi for menneskene, så de må tenke over det.'),
(5148, 59, 22, 'Han er Gud, det er ingen gud unntatt Ham! Han kjenner det skjulte og det åpenbare. Han er den Barmhjertige, den Nåderike.'),
(5149, 59, 23, 'Han er Gud, det er ingen gud unntatt Ham! Kongen, den Hellige, Frelsen, den Lojale, Beskytteren, den Mektige, den Allstyrende, Majesteten. Gud skje all ære! Han er fjernt fra det de setter ved Hans side.'),
(5150, 59, 24, 'Han er Gud, Skaperen, Fornyeren, Formeren! Hans er de vakreste navn! Alt som er i himlene og på jord priser Ham! Han er den Mektige, den Vise');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'Slutt dere ikke til Mine og deres fiender, idet dere tilbyr dem sympati, skjønt de fornekter det som er blitt dere til del av sannhet, og har fordrevet Sendebudet og dere fordi dere tror på Gud, deres Herre, om dere er dratt ut for å kjempe for Min sak og å søke Mitt velbehag. Dere betror dem hemmelig sympati, men Jeg vet hva dere skjuler og hva dere bærer åpent frem. Den av dere som gjør dette, er kommet bort fra den strake vei.'),
(5152, 60, 2, 'Hvis de kommer over dere, er de deres fiender og retter sine hender og tungens tale mot dere for å gjøre dere vondt, og ønsker at dere skal falle fra.'),
(5153, 60, 3, 'Verken blodsbånd eller barn skal være til nytte for dere på oppstandelsens dag. Han vil skille mellom dere. Gud er observant for det dere gjør.'),
(5154, 60, 4, 'I Abraham og de som var med ham, har dere et vakkert eksempel, da de sa til sitt folk: «Vi er ferdige med dere og det dere tilber utenom Gud! Vi tar avstand fra dere, og mellom dere og oss er oppstått fiendskap og hat for alltid, inntil dere måtte tro på Gud alene.» Dog sa Abraham til sin far: «Jeg skal visselig be om tilgivelse for deg, men jeg har ikke makt til å utrette noe som helst for deg overfor Gud,» Herre, vi forlater oss på Deg, og til Deg vender vi oss i bot. Til Deg er reisens mål!'),
(5155, 60, 5, 'Herre gjør oss ikke til anstøt for de vantro, men tilgi oss, Herre! Du er den Mektige, Vise.'),
(5156, 60, 6, 'Det er et vakkert eksempel for dere i dem, for alle som setter sitt håp til Gud og dommens dag. Men om noen vender seg bort, så er Gud den Selvtilstrekkelige, den Lovpriste.'),
(5157, 60, 7, 'Det kan hende at Gud skaper sympati mellom dere og dem dere levde i fiendskap med. Gud evner alt! Gud er tilgivende og nåderik.'),
(5158, 60, 8, 'Gud forbyr dere ikke å vise respekt og rimelighet mot dem som ikke bekjemper dere for religionens sak, og ikke drev dere bort fra deres hjem. Gud liker dem som viser rimelighet.'),
(5159, 60, 9, 'Gud forbyr dere bare at dere slutter dere til dem som bekjempet dere for religionens sak, og drev dere bort fra deres hjem eller hjalp til ved utdrivingen. De som sluttet seg til disse, de er urettferdige.'),
(5160, 60, 10, 'Dere som tror, når det kommer til dere troende kvinner som utvandrere, så sett dem på prøve. Gud kjenner best til deres tro. Hvis dere så vet at de er troende, så send dem ikke tilbake til de vantro. De er ikke tillatt for dem, og de vantro er ikke tillatt for disse. Men gi de vantro det de har lagt ut ved giftermål. Dere rammes ikke av skyld om dere gifter dere med dem, så fremt dere gir dem det de har krav på. Men hold ikke fast ved båndene til vantro kvinner. Krev det dere har lagt ut, og la dem kreve det de har lagt ut. Dette er Guds avgjørelse. Han treffer avgjørelse mellom dere. Gud vet, er vis.'),
(5161, 60, 11, 'Og hvis noen av deres hustruer kommer bort fra dere og til de vantro, så stå sammen om følgen, og gi dem hvis hustruer er blitt borte, tilsvarende det de har lagt ut. Og frykt Gud, Ham som dere tror på.'),
(5162, 60, 12, 'Du profet! Når troende kvinner kommer til deg og gir deg troskapsed, at de ikke skal sette noen ved Guds side, ikke stjele, ikke begå utroskap, ikke drepe sine barn, og ikke fare med bakvaskelser som de har grepet ut av luften, og ikke vise deg ulydighet i det som er godt og rett, så ta imot deres troskapsed og be Gud om tilgivelse for dem. Gud er tilgivende, nåderik.'),
(5163, 60, 13, 'Dere som tror, slutt dere ikke til folk som er under Guds vrede, som har avskrevet det kommende liv, slik de vantro har avskrevet dem som er i gravene!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Alt som er i himlene og på jord, priser Gud! Han er den Mektige, den Vise.'),
(5165, 61, 2, 'Dere som tror, hvorfor sier dere det dere ikke gjør?'),
(5166, 61, 3, 'Det er til stor avsky for Gud at dere sier det dere ikke gjør!'),
(5167, 61, 4, 'Gud holder av dem som kjemper for Hans sak på rad og rekke, som var de en kompakt mur.'),
(5168, 61, 5, 'En gang sa Moses til sitt folk: «Mitt folk, hvorfor plager dere meg, skjønt dere vet at jeg er Guds sendebud til dere?» Og da de kom på avveie, lot Gud deres hjerter komme på avveie. Gud leder ikke et syndefullt folk.'),
(5169, 61, 6, 'En gang sa Jesus, Marias sønn: «Israels barn, jeg er Guds sendebud til dere, for å stadfeste loven som foreligger før meg, og bebude et sendebud som kommer etter meg, hvis navn er Ahmad.» Og da han kom til dem med klare bevis, sa de: «Dette er skjær trolldom.»'),
(5170, 61, 7, 'Og hvem gjør større urett enn den som dikter opp løgn mot Gud, når han kalles til å gi seg Gud i vold? Gud leder ikke et urettferdig folk.'),
(5171, 61, 8, 'De ønsker å blåse ut Guds lys, men Gud fullbyrder Sitt lys, selv om de vantro misliker det.'),
(5172, 61, 9, 'Han er det, som har sendt Sitt sendebud med ledelse og sannhets religion, så Han kan la den vinne frem overfor all religion, selv om avgudsdyrkerne misliker det.'),
(5173, 61, 10, 'Dere som tror, skal Jeg henvise dere til en handel, som kan redde dere fra en smertelig straff?'),
(5174, 61, 11, 'Dere skal tro på Gud og Hans sendebud, og satse liv og eiendom for Guds sak. Dette er et gode for dere, om dere bare visste!'),
(5175, 61, 12, 'Han vil forlate dere deres synder, og føre dere inn i paradisets haver, hvor bekker sildrer, og i herlige boliger i Edens haver. Dette er den store seier.'),
(5176, 61, 13, 'Og annet, som dere har kjært, hjelp fra Gud og snarlig seier. Forkynn de troende det glade budskap!'),
(5177, 61, 14, 'Dere som tror, vær Guds hjelpesmenn, slik som Jesus, Marias sønn, sa til disiplene: «Hvem vil bli mine hjelpesmenn hen til Gud?» Disiplene sa: «Vi er Guds hjelpesmenn!» Og en gruppe av Israels barn trodde, og en gruppe fornektet. Så støttet Vi dem som trodde mot deres fiende, og de tok seieren!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Alt som er i himmel og på jord, priser Gud, Kongen, den Hellige, den Mektige, den Vise.'),
(5179, 62, 2, 'Han er det som lot fremstå blant folkene et sendebud fra deres egne rekker, for å fremlese Hans ord for dem, og for å rense dem, og lære dem skriften og visdommen. Men tidligere var de klart på villspor.'),
(5180, 62, 3, 'Og for andre som ennå ikke har sluttet seg til dem. Han er den Mektige, den Vise.'),
(5181, 62, 4, 'Det er en Guds gave. Han gir den til hvem Han vil. Gud er full av godhet!'),
(5182, 62, 5, 'Det er med dem som ble pålagt loven og så ikke kunne bære den, som med et esel som bærer bøker. Det står ille til med folk som fornekter Guds ord! Gud leder ikke urettferdige folk.'),
(5183, 62, 6, 'Si: «Dere som er jøder, om dere påstår at dere er Guds venner fremfor andre mennesker, så ønsk dere da døden, om dere snakker sant!»'),
(5184, 62, 7, 'Men den vil de aldri ønske seg, på grunn av de gjerninger de har sendt i forveien. Gud kjenner de urettferdige.'),
(5185, 62, 8, 'Si: «Sannelig, døden, som dere flykter fra, den vil møte dere! Så føres dere tilbake til Ham, som kjenner det skjulte og det åpenbare, og så vil Han fortelle dere hva dere bedrev.»'),
(5186, 62, 9, 'Dere som tror, når det kalles til samlingsdagens bønn, så skynd dere hen til Guds ihukommelse, og la handel ligge. Dette er et gode for dere, om dere bare visste!'),
(5187, 62, 10, 'Når så bønnen er slutt, så spre dere omkring i landet og søk av Guds gaver. Kom Gud jevnlig i hu, så det må gå dere godt.'),
(5188, 62, 11, 'Men når de får øye på handelsvarer eller adspredelse under bønnen, så løper de dit og lar deg stå. Si: «Det som finnes hos Gud er bedre enn fornøyelser og handel! Gud er den beste forsørger.»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Når hyklerne kommer til deg, sier de: «Vi vitner at du er visselig Guds sendebud.» Gud vet at du er Hans sendebud, og Gud vitner at hyklerne visselig er løgnere.'),
(5190, 63, 2, 'De tar sine eder som dekke, og så legger de hindringer på Guds vei. Ille er det de har gjort.'),
(5191, 63, 3, 'Dette fordi de trodde, og så ble vantro. Så ble deres hjerter forseglet, så de intet forstår.'),
(5192, 63, 4, 'Men når du ser dem, behager deres fremtoning deg, og om de taler, lytter du til deres tale, der de står stive som stokker, og tror at hvert utrop gjelder dem. De er fienden, så pass opp for dem. Måtte Gud gjøre det av med dem! Hvor forvridde de er!'),
(5193, 63, 5, 'Og når det sies til dem: «Kom hit, så vil Guds sendebud be om tilgivelse for dere,» så snur de hodet til siden, og du ser at de avslår i hovmod.'),
(5194, 63, 6, 'Det er likegyldig for dem om du ber om tilgivelse for dem eller ikke. Gud gir dem ikke tilgivelse, Gud leder ikke syndefulle mennesker.'),
(5195, 63, 7, 'Disse er det som sier: «Gi ingen økonomisk støtte til dem som er sammen med Guds sendebud, så sprer de seg.» Dog, Guds er himlenes og jordens skatter, men hyklerne forstår intet.'),
(5196, 63, 8, 'De sier: «Om vi vender tilbake til Medina, så vil nok den mektigere der drive ut den svakere.» Men makten tilhører Gud og Hans sendebud, og de troende, men hyklerne vet det ikke.'),
(5197, 63, 9, 'Dere som tror, la ikke deres rikdom og barn avlede dere fra Guds ihukommen. De som gjør det, de er tapere.'),
(5198, 63, 10, 'Gi av det Vi har gitt dere, før døden kommer til en av dere, og han sier: «Herre, om Du kunne gi meg en liten frist, så jeg kunne gi gode gaver, og bli av de rettferdige!»'),
(5199, 63, 11, 'Men Gud gir ingen utsettelse når hans tid er kommen! Og Gud er vel underrettet om det dere gjør.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Alt som er i himlene og på jorden, priser Gud! Hans er herredømmet og prisen. Han evner alt!'),
(5201, 64, 2, 'Han er det som skapte dere, og noen er vantro, andre troende. Gud er observant for det dere gjør.'),
(5202, 64, 3, 'Han skapte himlene og jorden på alvor, og Han formet dere, og formet dere vel. Til Ham går ferden.'),
(5203, 64, 4, 'Han vet hva som finnes i himlene og på jord! Han vet hva dere skjuler og hva dere bærer åpent frem. Gud kjenner til det som bor i hjertene.'),
(5204, 64, 5, 'Du har vel hørt om dem som var vantro i forgangne tider, og fikk smake følgene av sin adferd og har en smertelig straff i vente?'),
(5205, 64, 6, 'Dette fordi deres sendebud kom til dem med klar beskjed, og så sa de: «Skal alminnelige mennesker lede oss?» Og de fornektet og vendte seg bort. Men Gud er uavhengig. Gud er selvtilstrekkelig, all pris verdig!'),
(5206, 64, 7, 'De som er vantro, påstår at de aldri skal gjenoppvekkes. Si: «Jo, så sannelig, ved Herren! Dere skal gjenoppvekkes! Og så vil dere bli fortalt hva dere bedrev. Dette er lett for Gud.»'),
(5207, 64, 8, 'Så tro på Gud og Hans sendebud, og lyset som vi har nedsendt! Gud er vel underrettet om hva dere gjør.'),
(5208, 64, 9, 'Den dag Han samler dere til samlingens dag! Dette er desillusjoneringens dag. Den som tror på Gud og lever rettskaffent, hans misgjerninger vil Han stryke ut, og føre ham inn i paradisets haver, hvor bekker sildrer, for der å være og bli for alltid. Dette er den store seier!'),
(5209, 64, 10, 'Men de som er vantro og fornekter Vårt ord, disse er Ildens folk, og der skal de være og bli. En sørgelig skjebne!'),
(5210, 64, 11, 'Ingen hjemsøkelse rammer uten Guds tillatelse. Om noen tror på Gud, så leder Han hans hjerte. Gud kjenner til alle ting.'),
(5211, 64, 12, 'Så adlyd Gud og adlyd Sendebudet. Men om dere vender dere bort, så påligger det Vårt sendebud kun å gi et klart budskap.'),
(5212, 64, 13, 'Gud! Det er ingen gud unntatt Ham! På Gud skal de troende forlate seg!'),
(5213, 64, 14, 'Dere som tror, blant deres hustruer og barn finnes fiender for dere. Så pass på overfor dem. Men om dere unnskylder, viser overbærenhet, og tilgir, så er Gud tilgivende, nåderik.'),
(5214, 64, 15, 'Deres rikdom og deres barn er bare en prøvelse. Men hos Gud finnes stor lønn.'),
(5215, 64, 16, 'Vis gudsfrykt så langt dere kan, og hør og adlyd, og gi gode gaver til deres egen fordel! De som bevares mot sin egen gjerrighet, disse vil det gå godt!'),
(5216, 64, 17, 'Om dere gir Gud et godt lån, vil Han gi dere mangedobbelt igjen og tilgivelse. Gud er full av anerkjennelse, mild,'),
(5217, 64, 18, 'kjenner av det skjulte og det åpenbare, den Mektige, den Vise.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'Du profet, når dere skiller dere fra hustruer, så gjør dette under hensyntagen til deres ventetid. Beregn ventetiden, og frykt Gud, deres Herre. Send dem ikke bort fra sine hjem, de skal ikke dra med mindre de er skyldig i klar umoral! Dette er grenser som Gud har satt! Den som overskrider Guds grenser, gjør en urett som rammer ham selv. Du vet ikke, kanskje Gud etter dette vil bringe noe nytt.'),
(5219, 65, 2, 'Når de så har nådd sin termin, så behold dem på skikkelig vis, eller skill dere fra dem på skikkelig vis. To redelige menn blant dere skal være vitner, og bære vitnemålet overfor Gud. Ved dette formanes den som tror på Gud og dommens dag. Den som frykter Gud, vil Han finne en utvei for, og Han vil gi ham Sine gaver fra et hold han ikke regner med.'),
(5220, 65, 3, 'Om noen forlater seg på Gud, er Han nok for ham. Gud oppnår det Han vil. Gud har gitt alle ting sitt mål.'),
(5221, 65, 4, 'For dem av deres hustruer som har avskrevet menstruasjonen, om dere er i tvil, så er ventetiden tre måneder, og også for dem som ennå ikke har menstruert. For dem som er svangre, er deres frist fødselen. Den som frykter Gud, vil Han gjøre det lett for etter sin ordning.'),
(5222, 65, 5, 'Dette er Guds bud, som Han har åpenbart for dere. For den som frykter Gud, vil Han utslette hans misgjerninger og gjøre hans lønn stor.'),
(5223, 65, 6, 'La dem bo i ventetiden som dere bor, etter deres utkomme, og plag dem ikke for å gjøre det vanskelig for dem. Hvis de er svangre, så gi dem bidrag til fødselen. Hvis de ammer deres barn, så gi dem deres lønn og snakk sammen på skikkelig vis. Hvis dere har vanskeligheter, så skal en annen amme for ham.'),
(5224, 65, 7, 'Den velstående skal betale av sin velstand. Den som har begrensede kår, skal betale av det Gud har gitt ham. Gud forlanger ikke mer av noen enn Han har gitt ham. Gud lar vanskeligheter følges av lettelser.'),
(5225, 65, 8, 'Hvor mang en by som satte seg ut over Guds og Hans sendebuds bud, har Vi gitt en hard avregning og straffet med en fryktelig straff!'),
(5226, 65, 9, 'Så de fikk smake sin onde adferds følger, og slutten på det hele ble undergang.'),
(5227, 65, 10, 'Gud har gjort klar for dem en hard straff. Så frykt Gud, dere som har forstand!'),
(5228, 65, 11, 'Dere som tror, Gud har sendt ned til dere en påminnelse, et sendebud, som fremleser for dere Guds ord, de klargjørende, for å føre dem som tror og lever rettskaffent, fra mørke til lys. Den som tror og lever rettskaffent, vil Han føre inn i paradisets haver, hvor bekker sildrer, og hvor de skal være og bli for alltid. Gud gir ham en prektig omsorg.'),
(5229, 65, 12, 'Gud er det som skapte syv himler, og jorden likeledes! Ordet stiger ned mellom dem, så dere må vite at Gud evner alt, og at Gud omfatter alle ting i visdom!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'Du profet, hvorfor forbyr du det Gud har tillatt deg idet du søker å tekkes dine hustruer? Gud er tilgivende, nåderik.'),
(5231, 66, 2, 'Gud har foreskrevet dere løsningsmåten fra deres eder. Gud har omsorg for dere. Han er den Allvitende, den Vise.'),
(5232, 66, 3, 'Da profeten betrodde en av sine hustruer en sak, og hun fortalte det videre og dette avslørte Gud for ham, så hadde han fortalt noe av det og holdt noe tilbake. Når han så forela dette for henne, sa hun: «Hvem har fortalt deg dette?» Og han svarte: «Den Vise, den Innsiktsfulle har fortalt meg.»'),
(5233, 66, 4, 'Om dere to vender dere botferdige til Gud! For deres hjerter var på avveie. Men om dere hjelper hverandre mot Ham, så tar Gud seg av hans sak, og Gabriel, og den rettskafne blant de troende, og hertil yter englene hjelp!'),
(5234, 66, 5, 'Kanskje vil Herren, om han skiller seg fra dere, gi ham i bytte hustruer som er bedre enn dere, Gud-hengivne, troende, ydmyke, botferdige, fromme, asketiske, som har vært gifte før, eller jomfruer.'),
(5235, 66, 6, 'Dere som tror, pass dere selv og deres familier for Ilden, hvis brensel er mennesker og steiner! Strenge og harde engler forestår dem som ikke er ulydige mot Gud i det Han befaler dem, og gjør det de er pålagt.'),
(5236, 66, 7, '«Dere vantro! Kom ikke med unnskyldninger denne dag! Dere belønnes bare for det dere har gjort.»'),
(5237, 66, 8, 'Dere som tror, vend dere mot Gud i oppriktig bot! Kanskje Herren vil stryke ut deres misgjerninger, og føre dere inn i paradisets haver, hvor bekker sildrer. På den dag vil Gud ikke gjøre skam på profeten og dem som tror sammen med ham. Deres lys strømmer foran dem og på deres høyre side, og de sier: «Herre, la vårt lys fullbyrdes for oss, og tilgi oss! Du evner alt.»'),
(5238, 66, 9, 'Du profet, strid mot de vantro og hyklerne, og vær hard mot dem! Deres herberge blir helvete. En sørgelig skjebne!'),
(5239, 66, 10, 'Gud har gjort en lignelse for den vantro om Noas hustru, og Lots hustru. De var under to av Våre rettferdige tjenere, men de svek dem. Så kunne de ikke hjelpe dem det ringeste overfor Gud, og det ble sagt til dem: «Gå inn i Ilden sammen med dem som går dit.»'),
(5240, 66, 11, 'Gud har gjort lignelsen for de troende om Faraos hustru, da hun sa: «Herre, bygg meg et hus hos Deg i paradiset, og fri meg fra Farao og alt hans verk, og frels meg fra et folk av urettferdige.»'),
(5241, 66, 12, 'Og Maria, Imrans datter, som bevarte sitt legemes kyskhet, og Vi blåste inn i det av Vår ånd, og hun bekreftet Herrens ord og skrifter. Hun var av de ydmyke.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Velsignelsesrik er Han, i Hans hånd er herredømmet! Han evner alt!'),
(5243, 67, 2, 'Han er det som skapte død og liv for å prøve dere, hvem av dere som er best i gjerninger. Han er den Mektige, den Ettergivende.'),
(5244, 67, 3, 'Han er det som skapte syv himler lagvis. Du ser ikke i den Barmhjertiges skapning noen mangler. Blunk med øynene, ser du vel noen revner?'),
(5245, 67, 4, 'Om du blunker med øynene to ganger, så vender ditt blikk tilbake til deg, svakt og trett.'),
(5246, 67, 5, 'Vi har prydet den nederste himmel med bluss, og gjort dem til kastevåpen mot satanene. For dem har Vi gjort i stand Ildens straff.'),
(5247, 67, 6, 'Dem som fornekter sin Herre, venter helvetes straff.'),
(5248, 67, 7, 'En sørgelig skjebne! Når de kastes inn der, hører de et brøl,'),
(5249, 67, 8, 'idet det koker opp og er nær ved å sprekke av raseri. Hver gang en flokk kastes inn i det, spør dets voktere dem: «Kom det ingen advarer til dere?»'),
(5250, 67, 9, 'Og de svarer: «Jo, det kom en advarer til oss, men vi holdt det for løgn, og sa: ’Gud har ikke åpenbart noe! Dere er helt på villspor.’»'),
(5251, 67, 10, 'Og de tilføyer: «Om vi hadde hørt, eller forstått,'),
(5252, 67, 11, 'ville vi ikke vært Ildens folk.» De erkjenner sin synd. Bort med Ildens folk!'),
(5253, 67, 12, 'De som frykter Herren i det skjulte, dem venter tilgivelse og stor lønn.'),
(5254, 67, 13, 'Om dere holder hemmelig eller gir høylytt til kjenne det dere sier, så vet Han hva som bor i hjertene.'),
(5255, 67, 14, 'Skulle ikke Han kjenne den Han har skapt? Han er den Uutgrunnelige, den Innsiktsfulle.'),
(5256, 67, 15, 'Han er det som gjorde jorden underdanig for dere. Så dra rundt på dens overflate og spis av Hans gaver. Til Ham fører oppstandelsen!'),
(5257, 67, 16, 'Kan dere være sikre på Ham som er i himmelen, at Han ikke lar jorden oppsluke dere, for se, den skjelver?'),
(5258, 67, 17, 'Kan dere være sikre på Ham som er i Himmelen, at Han ikke sender over dere en skur av småstein, så dere får vite hva Min advarsel innebærer?'),
(5259, 67, 18, 'De som levde før dem, kalte også sannhet for løgn, og hvordan var vel Min refselse?'),
(5260, 67, 19, 'Har de ikke sett på fuglene over seg som brer ut sine vinger og slår dem sammen? Intet holder dem oppe, unntatt den Barmhjertige. Han er observant for alle ting.'),
(5261, 67, 20, 'Hvem er det vel som er som en hærskare som hjelper dere, unntatt den Barmhjertige? De vantro er helt på avveie.'),
(5262, 67, 21, 'Hvem er det vel som gir dere livets underhold, om Han holder tilbake sitt underhold? Nei, de turer frem i oppsetsighet og motvilje.'),
(5263, 67, 22, 'Er vel den som går og faller hodekulls, best rettledet, eller den som vandrer raskt på rett vei?'),
(5264, 67, 23, 'Si: «Han er det som har frembrakt dere og gitt dere hørsel, syn og forstand. Hvor lite dere takker!»'),
(5265, 67, 24, 'Si: «Han er det som har spredt dere på jorden. Og til Ham vil dere bli samlet inn.»'),
(5266, 67, 25, 'Så sier de: «Når kommer så denne trussel, om dere snakker sant?»'),
(5267, 67, 26, 'Si: «Kunnskapen beror hos Gud! Jeg er bare en klar advarer.»'),
(5268, 67, 27, 'Når de ser at den er nær, formørkes de vantros ansikter, og det lyder: «Dette er det dere etterlyste.»'),
(5269, 67, 28, 'Si: «Hva synes dere, om Gud utsletter meg og dem som følger meg, eller Han viser oss nåde, hvem skal da beskytte de vantro mot en smertelig straff?»'),
(5270, 67, 29, 'Si: «Han er den Barmhjertige! Vi tror på Ham, og på Ham forlater vi oss. Dere får snart vite hvem det er, som er klart på villstrå.»'),
(5271, 67, 30, 'Si: «Hva ville dere synes om deres vann en morgen var sunket ned i jorden, hvem skal da bringe dere rinnende vann?»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Ved pennen, og det man skriver!'),
(5273, 68, 2, 'Du er ikke besatt, takket være Guds nåde.'),
(5274, 68, 3, 'Du har velfortjent lønn i vente.'),
(5275, 68, 4, 'Du har en høy karakter.'),
(5276, 68, 5, 'Du vil se, og de vil se,'),
(5277, 68, 6, 'hvem av dere som er hjemsøkt av fristelse.'),
(5278, 68, 7, 'Herren kjenner best hvem som forviller seg bort fra Hans vei, og Han kjenner best hvem som er på rett vei.'),
(5279, 68, 8, 'Hør ikke etter dem som fornekter!'),
(5280, 68, 9, 'De ønsker at du viser kompromissvilje, så vil de gjøre det samme.'),
(5281, 68, 10, 'Hør ikke etter hver foraktelig storsverger,'),
(5282, 68, 11, 'som baktaler folk og går rundt med rykter,'),
(5283, 68, 12, 'som hindrer det som godt er, som begår overtredelser i synd,'),
(5284, 68, 13, 'rå og påtrengende,'),
(5285, 68, 14, 'fordi han har rikdom og sønner.'),
(5286, 68, 15, 'Når Vårt ord fremleses for ham, sier han: «Fabler fra gamle dager.»'),
(5287, 68, 16, 'Vi skal merke ham på snuten!'),
(5288, 68, 17, 'Vi har satt dem på prøve, som Vi satte haveeierne på prøve, da de bedyret at de visselig skulle høste den om morgenen,'),
(5289, 68, 18, 'uten å gjøre unntagelsen («om Gud vil»).'),
(5290, 68, 19, 'Men en hjemsøkelse fra Herren hjemsøkte den mens de sov,'),
(5291, 68, 20, 'og om morgenen var den som en stubbmark.'),
(5292, 68, 21, 'Om morgenen ropte de til hverandre:'),
(5293, 68, 22, '«Kom dere tidlig av gårde til deres marker, om dere skal høste.»'),
(5294, 68, 23, 'Så drog de av gårde, idet de hvisket til hverandre.'),
(5295, 68, 24, '«Ingen fattig stakkar skal i dag komme inn til oss.»'),
(5296, 68, 25, 'De gikk tidlig, bestemte og effektive.'),
(5297, 68, 26, 'Men da de fikk se den, sa de: «Vi må ha gått feil,'),
(5298, 68, 27, '– nei, vi er blitt plyndret!»'),
(5299, 68, 28, 'Den mest rimelige blant dem sa: «Sa jeg ikke til dere: ’Hvorfor gir dere ikke lovprisning?’»'),
(5300, 68, 29, 'Da sa de: «Ære være Herren! Vi handlet visselig ondt.»'),
(5301, 68, 30, 'Og de vendte seg mot hverandre med gjensidige bebreidelser.'),
(5302, 68, 31, 'De sa: «Ve over oss! Vi var oppsetsige.'),
(5303, 68, 32, 'Kanskje Herren vil gi oss en have som er bedre i bytte for den. Til Herren setter vi vårt håp.»'),
(5304, 68, 33, 'Slik er straffen. Men straffen i det hinsidige er større, om de bare visste.'),
(5305, 68, 34, 'De gudfryktige har i vente hos Herren lykksalighetens haver.'),
(5306, 68, 35, 'Skulle Vi vel gjøre de Gud-hengivne lik synderne?'),
(5307, 68, 36, 'Hva er det i veien med dere? Hvordan er det dere vurderer?'),
(5308, 68, 37, 'Eller har dere en Bok som dere studerer?'),
(5309, 68, 38, 'I den ville dere nok finne det dere ønsker!'),
(5310, 68, 39, 'Eller har dere en edsforpliktelse fra Oss, som gjelder til oppstandelsens dag, så dere får det slik som dere vurderer?'),
(5311, 68, 40, 'Spør dem hvem som innestår for dette!'),
(5312, 68, 41, 'Har de kanskje andre guder? Så la dem bringe sine guder, om de snakker sant.'),
(5313, 68, 42, 'Den dag man binder opp om seg i påvente av at noe skal skje, og de beordres til å falle ned, så kan de ikke.'),
(5314, 68, 43, 'Der de står med nedslagne blikk mens fornedrelsen ligger over dem, for de ble kalt på til å falle ned mens de var friske og sunne.'),
(5315, 68, 44, 'Overlat til Meg dem som fornekter dette budskap! Vi skal la dem gå videre, litt etter litt, uten at de er klar over det.'),
(5316, 68, 45, 'Jeg gir dem frist. Sannelig, Min plan står fast.'),
(5317, 68, 46, 'Forlanger du vel betaling av dem, så de nedtynges av gjeld?'),
(5318, 68, 47, 'Eller har de hånd om det skjulte, så de noterer?'),
(5319, 68, 48, 'Vent i tålmod på Herrens avgjørelse, og vær ikke som mannen i fisken (Jonas), som ropte i desperasjon.'),
(5320, 68, 49, 'Hvis ikke Herrens godhet hadde nådd ham, ville han vært kastet opp på en øde strand, og vært en bebreidet.'),
(5321, 68, 50, 'Men Herren utvalgte ham og gav ham plass blant de rettferdige.'),
(5322, 68, 51, 'Sannelig, de vantro er nær ved å få deg til å miste fotfestet med sine blikk når de hører formaningen, og sier: «Han er sannelig besatt!»'),
(5323, 68, 52, 'Men dette er intet annet enn en formaning for all verden!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'Realitetens time!'),
(5325, 69, 2, 'Hva er realitetens time?'),
(5326, 69, 3, 'Hva kan få deg til å innse hva realitetens time er?'),
(5327, 69, 4, 'Thamod og Ad holdt slaget for løgn.'),
(5328, 69, 5, 'Hva Thamod angår, så ble de utslettet av ild fra oven,'),
(5329, 69, 6, 'og hva Ad angår, så ble de utslettet av en hylende vind.'),
(5330, 69, 7, 'Han sendte den over dem i syv netter og åtte dager i ett strekk, og du kunne se folk liggende, som om de var morkne, omstyrtede palmestammer.'),
(5331, 69, 8, 'Ser du vel noe igjen av dem?'),
(5332, 69, 9, 'Likeledes Farao, og de som levde før ham, og de omstyrtede byer,'),
(5333, 69, 10, 'de begikk synd, og adlød ikke Herrens sendebud. Og Han tok dem fatt med et uimotståelig grep.'),
(5334, 69, 11, 'Da vannet svømmet over, bar Vi dere i fartøyet,'),
(5335, 69, 12, 'for å gjøre det til noe minneverdig for dere, og at ører som tar imot måtte bevare det.'),
(5336, 69, 13, 'Når det støtes ett støt i basunen,'),
(5337, 69, 14, 'og jorden og fjellene løftes opp og pulveriseres med ett slag!'),
(5338, 69, 15, 'På denne dag inntreffer hendelsen.'),
(5339, 69, 16, 'Himmelen går i stykker, for denne dag er den skjør.'),
(5340, 69, 17, 'Englene står ved kanten, og åtte bærer Herrens trone over seg.'),
(5341, 69, 18, 'På denne dag blir dere ført frem, og intet hos dere er skjult.'),
(5342, 69, 19, 'Hva ham angår, som får sin regnskapsbok i høyre hånd, han vil si: «Ta og les min regnskapsbok!'),
(5343, 69, 20, 'Jeg trodde at jeg skulle møte mitt regnskap.»'),
(5344, 69, 21, 'Han vil gå til et behagelig liv'),
(5345, 69, 22, 'i en opphøyet have'),
(5346, 69, 23, 'med dens frukter nær for hånden.'),
(5347, 69, 24, '«Spis og drikk og la det smake, for det dere utrettet i dagene som er gått.»'),
(5348, 69, 25, 'Hva ham angår, som får sin regnskapsbok i venstre hånd, han vil si: «Å, hadde jeg bare ikke fått min regnskapsbok!'),
(5349, 69, 26, 'Hadde jeg bare ikke visst hvordan mitt regnskap står!'),
(5350, 69, 27, 'Hadde det bare vært slutten!'),
(5351, 69, 28, 'Min rikdom hjelper meg ikke,'),
(5352, 69, 29, 'min makt er borte fra meg.»'),
(5353, 69, 30, '«Ta ham, og legg ham i lenker'),
(5354, 69, 31, 'og stek ham så i helvete!'),
(5355, 69, 32, 'Legg ham så i en sytti alens kjede!'),
(5356, 69, 33, 'Han trodde ikke på Gud, den Veldige!'),
(5357, 69, 34, 'Han talte ikke for å gi den fattige mat.'),
(5358, 69, 35, 'Så i dag har han ingen god venn her,'),
(5359, 69, 36, 'og ingen mat, unntatt kloakk,'),
(5360, 69, 37, 'som bare synderne tar inn.»'),
(5361, 69, 38, 'Nei, Jeg sverger ved det dere ser,'),
(5362, 69, 39, 'og ved det dere ikke ser'),
(5363, 69, 40, '– dette er et ærverdig sendebuds tale!'),
(5364, 69, 41, 'Det er ikke en skalds ord, lite tror dere,'),
(5365, 69, 42, 'og ikke en spåmanns ord, lite tenker dere etter.'),
(5366, 69, 43, 'Det er en åpenbaring fra all verdens Herre!'),
(5367, 69, 44, 'Hvis han hadde grepet ord om Oss ut av luften,'),
(5368, 69, 45, 'så ville Vi grepet ham i høyre hånd,'),
(5369, 69, 46, 'og skåret av hans pulsåre,'),
(5370, 69, 47, 'og ikke en av dere kunne redde ham unna!'),
(5371, 69, 48, 'Dette er en påminnelse for de gudfryktige.'),
(5372, 69, 49, 'Men Vi vet at det blant dere er fornektere.'),
(5373, 69, 50, 'For de vantro er den sorg og bedrøvelse.'),
(5374, 69, 51, 'Men den er jo den sikreste sannhet.'),
(5375, 69, 52, 'Så pris din Herre, den Veldiges navn!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'En som spør, etterlyser den tilstundende straff'),
(5377, 70, 2, 'for de vantro, som ingen kan avverge,'),
(5378, 70, 3, 'fra Gud, himmelstigens Herre.'),
(5379, 70, 4, 'til Ham stiger englene og Ånden opp, på en dag hvis utstrekning er femti tusen år.'),
(5380, 70, 5, 'Så vis vakkert tålmod.'),
(5381, 70, 6, 'De anser den langt borte,'),
(5382, 70, 7, 'men Vi ser den nær.'),
(5383, 70, 8, 'Den dag, når himmelen blir som smeltet kobber,'),
(5384, 70, 9, 'og fjellene som kardet ull,'),
(5385, 70, 10, 'når venn ikke snakker til venn'),
(5386, 70, 11, 'når de får se hverandre. Synderen vil ønske at han kunne løskjøpe seg fra denne dags straff med sine barn,'),
(5387, 70, 12, 'sin hustru, sin bror,'),
(5388, 70, 13, 'sin slekt som gav ham ly,'),
(5389, 70, 14, 'og alle som på jorden er, om dette så berget ham.'),
(5390, 70, 15, 'Men nei, det er en luende ild,'),
(5391, 70, 16, 'som tar hele huden av hodet,'),
(5392, 70, 17, 'som kaller på den som snudde ryggen og vendte seg bort,'),
(5393, 70, 18, 'som samlet og la i lader.'),
(5394, 70, 19, 'Mennesket er av natur småskårent.'),
(5395, 70, 20, 'Når ondt treffer ham, er han engstelig,'),
(5396, 70, 21, 'og når godt overgår ham, er han påholden.'),
(5397, 70, 22, 'Unntatt er de som forretter bønnen,'),
(5398, 70, 23, 'og som er vedholdende i bønnen,'),
(5399, 70, 24, 'som i det de eier anerkjenner en rett'),
(5400, 70, 25, 'for tiggeren og den trengende,'),
(5401, 70, 26, 'og som tror på dommens dag,'),
(5402, 70, 27, 'de som bever for Herrens straff,'),
(5403, 70, 28, 'for Herrens straff kan ingen føle seg trygg,'),
(5404, 70, 29, 'som holder sitt kjønnsliv i tømme,'),
(5405, 70, 30, 'unntatt overfor sine hustruer og sine slavinner, for da kan de ikke bebreides,'),
(5406, 70, 31, 'men den som begjærer noe utover dette, slike går over grensen,'),
(5407, 70, 32, 'som passer betrodd gods og sine forpliktelser,'),
(5408, 70, 33, 'som står fast i sine vitnemål,'),
(5409, 70, 34, 'og overholder sine bønner.'),
(5410, 70, 35, 'Disse, i paradisets haver skal de være og de vises all ære.'),
(5411, 70, 36, 'Hva er det med de vantro som kommer frem foran deg og strekker hals,'),
(5412, 70, 37, 'i smågrupper fra høyre og fra venstre?'),
(5413, 70, 38, 'Ønsker hver av dem å bli ført inn i lykksalighetens have?'),
(5414, 70, 39, 'Å nei, Vi har skapt dem av det de vet.'),
(5415, 70, 40, 'Å nei! Jeg sverger ved østens og vestens Herre, Vi ville visselig være i stand til'),
(5416, 70, 41, 'å skifte dem ut, med noe bedre enn dem, og ingen kan komme Oss i forkjøpet!'),
(5417, 70, 42, 'La dem prate og leke seg til de møter den dag som er lovet dem,'),
(5418, 70, 43, 'den dag de haster frem fra gravene, som kappløp de til et mål,'),
(5419, 70, 44, 'med nedslagne blikk mens fornedrelsen dekker dem. Dette er dagen som ble lovet dem.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Vi sendte Noa til hans folk: «Advar ditt folk, før en smertelig straff kommer over dem.»'),
(5421, 71, 2, 'Han sa: «Hør, mitt folk, jeg er en klar advarer for dere!'),
(5422, 71, 3, 'Tjen Gud, og frykt Ham, og adlyd meg,'),
(5423, 71, 4, 'så vil Han tilgi deres synder og gi dere en viss frist! Når Guds frist kommer, gis ingen utsettelse, om dere bare visste!»'),
(5424, 71, 5, 'Han sa videre: «Herre, jeg har kalt på mitt folk natt og dag,'),
(5425, 71, 6, 'men mitt kall har forsterket deres avstandtagen.'),
(5426, 71, 7, 'Så ofte som jeg kalte dem, så Du kunne tilgi dem, satte de fingrene i ørene og gjemte seg i sine klær, og turet frem i største hovmod.'),
(5427, 71, 8, 'Så kalte jeg på dem åpent,'),
(5428, 71, 9, 'og så talte jeg både offentlig og privat,'),
(5429, 71, 10, 'og jeg sa: ’Be om Herrens tilgivelse! Han er det som tilgir,'),
(5430, 71, 11, 'og så vil Han sende dere strømmende regn fra oven,'),
(5431, 71, 12, 'og forstrekke dere med rikdom og barn, og gi dere haver, og gi dere bekker.'),
(5432, 71, 13, 'Hva er det med dere, at dere ikke håper på Guds langmodighet,'),
(5433, 71, 14, 'da Han jo har skapt dere i utviklingsfaser?'),
(5434, 71, 15, 'Har dere ikke sett hvordan Gud har skapt syv himler lagvis,'),
(5435, 71, 16, 'og satt månen der som et bluss, og solen som en lysende lampe?'),
(5436, 71, 17, 'Gud har latt dere vokse opp av jorden,'),
(5437, 71, 18, 'og så lar Han dere vende tilbake dit, og så bringer Han dere frem på nytt.'),
(5438, 71, 19, 'Gud har bredt ut jorden for dere som et teppe,'),
(5439, 71, 20, 'så dere kan vandre på dens vide veier.’»'),
(5440, 71, 21, 'Han fortsatte: «Herre, de har ikke adlydt meg, men fulgt ham, hvis rikdom og barn kun øker hans fortapelse,'),
(5441, 71, 22, 'og de har smidd svære renker,'),
(5442, 71, 23, 'og sier: ’Oppgi ikke deres guder! Forlat ikke Wadd og Suwa, og ikke Yaroth, Yahok eller Nasr.’'),
(5443, 71, 24, 'De har ført mange vill. La de urettferdige bare tilta i villfarelse!»'),
(5444, 71, 25, 'For sine overtredelsers skyld ble de druknet, og ført inn i en ild, og de fant ingen hjelpere utenom Gud.'),
(5445, 71, 26, 'Og Noa sa: «Herre, etterlat ikke en eneste av de vantro på jorden!'),
(5446, 71, 27, 'Om Du lar dem tilbake, så vil de føre dine tjenere vill, og de vil ikke fostre annet enn syndefulle vantro.'),
(5447, 71, 28, 'Herre, tilgi meg og mine foreldre, og enhver som kommer inn i mitt hus som troende, og de troende menn og kvinner! Og la de urettferdige bare tilta i fordervelse.»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'Si: «Det er blitt åpenbart meg at en flokk dsjinner gav øre, og sa: ’Vi har visselig hørt en bemerkelsesverdig forkynnelse (Koran),'),
(5449, 72, 2, 'som leder til rettsindighet! Vi tror på den, og vi vil ikke gi Herren medguder.'),
(5450, 72, 3, 'Og Han – opphøyet er Herrens majestet – har ikke lagt seg til hustru eller barn.'),
(5451, 72, 4, 'De forstandssvake blant oss uttalte om Gud noe hinsides alt,'),
(5452, 72, 5, 'og vi mente at menneske eller dsjinn aldri ville si en løgn om Gud.'),
(5453, 72, 6, 'Men det var noen blant menneskene som søkte sammen med noen av dsjinnene, og forøket deres dårlighet,'),
(5454, 72, 7, 'og mente, som også dere mente, at Gud ville aldri gjenoppvekke noen.'),
(5455, 72, 8, 'Vi søkte over himmelen, men fant den fylt av strenge voktere og ildflammer.'),
(5456, 72, 9, 'Vi pleide å ta plass her og der for å lytte, men den som nå lytter, finner en ildflamme som lurer på ham.'),
(5457, 72, 10, 'Vi vet ikke om ondt er tiltenkt dem på jorden, eller om Herren har tiltenkt dem det som er godt.'),
(5458, 72, 11, 'Visselig er det blant oss rettskafne, og slike som ikke er det. Vi følger forskjellige veier.'),
(5459, 72, 12, 'Vi mente at vi aldri kunne sette Gud ut av spill på jorden, og heller ikke forpurre Ham ved flukt.'),
(5460, 72, 13, 'Da vi fikk høre ledelsen, trodde vi på den, og den som tror på Herren, han frykter ikke smålighet eller urett.'),
(5461, 72, 14, 'Noen av oss har gitt seg Gud i vold, og noen følger det de selv mener er rett: De som har gitt seg Gud i vold, disse har søkt det rette.'),
(5462, 72, 15, 'Men de som følger egne veier, de blir brensel for helvete.’»'),
(5463, 72, 16, '«Om de holdt seg på rett vei, ville Vi gi dem rikelig vann å drikke.'),
(5464, 72, 17, 'for derved å sette dem på prøve. Og den som snur seg bort fra Herrens formaning, sender Han til en svær straff.'),
(5465, 72, 18, 'Bedestedene tilhører Gud, så påkall ingen ved siden av Gud.'),
(5466, 72, 19, 'Da Guds tjener stod opp for å påkalle Ham, var de tett omkring ham.»'),
(5467, 72, 20, 'Si: «Jeg påkaller kun Herren, og setter ingen ved Hans side.»'),
(5468, 72, 21, 'Si: «Jeg formår ikke å gjøre dere ondt eller godt.»'),
(5469, 72, 22, 'Si: «Ingen kan beskytte meg mot Gud, og jeg finner ingen tilflukt utenom Ham.'),
(5470, 72, 23, 'Meg påligger kun formidling fra Gud og Hans budskap. Den som ikke adlyder Gud og Hans sendebud, har i vente helvetes ild, og der skal de være og bli for alltid.'),
(5471, 72, 24, 'Når de så endelig får se det som var stilt dem i utsikt, da vil de få vite hvem som står svakest med hensyn til hjelpere, og tallmessig.»'),
(5472, 72, 25, 'Si: «Jeg vet ikke om det som er stilt dere i utsikt er nær, eller om Herren vil gi det utsettelse.'),
(5473, 72, 26, 'Han kjenner det skjulte, og Han gjør ingen kjent med Sitt skjulte,'),
(5474, 72, 27, 'unntatt et sendebud Han har velbehag i. Og han ferdes med vakt, foran og bak seg,'),
(5475, 72, 28, 'så Han kan vite at de har levert Herrens budskap. Han omfatter alt som hos dem er, og opptegner alt nøyaktig.»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'Du som ligger overdekket,'),
(5477, 73, 2, 'stå opp og våk om natten, unntatt litt'),
(5478, 73, 3, '– halvparten av den, eller trekk fra litt,'),
(5479, 73, 4, 'eller legg til litt, og fremsi Koranen, langsomt og tydelig!'),
(5480, 73, 5, 'Vi har vektige ord å si deg!'),
(5481, 73, 6, 'Nattens første timer er egnet for de sterkeste inntrykk, og de beste ord.'),
(5482, 73, 7, 'Om dagen har du meget som opptar deg.'),
(5483, 73, 8, 'Kom Herrens navn i hu, og hengi deg fullt og helt til Ham!'),
(5484, 73, 9, 'Han er Herre over øst og vest. Det finnes ingen gud unntatt Ham. La Ham ta seg av deg.'),
(5485, 73, 10, 'Bær tålmodig det folk sier, og hold deg unna dem på en pen måte.'),
(5486, 73, 11, 'Overlat til Meg dem som fornekter, dem som lever et liv i velstand. Gi dem en liten frist.'),
(5487, 73, 12, 'Hos oss finnes lenker, og helvetes ild,'),
(5488, 73, 13, 'kvelende føde, og smertelig straff,'),
(5489, 73, 14, 'på den dag da jord og fjell rystes, da fjellene blir som løs sand.'),
(5490, 73, 15, 'Vi har sendt dere et sendebud, som frembærer vitnesbyrd for dere, som Vi også sendte et sendebud til Farao.'),
(5491, 73, 16, 'Men Farao adlød ikke Sendebudet, og Vi tok ham alvorlig fatt!'),
(5492, 73, 17, 'Hvordan vil dere, hvis dere ikke tror, verge dere mot en dag som gjør barna gråhårede?'),
(5493, 73, 18, 'Himmelen vil revne, og det Han har lovet, vil gå i oppfyllelse!'),
(5494, 73, 19, 'Dette er en påminnelse. La den som vil, finne en vei til Herren.'),
(5495, 73, 20, 'Herren vet at du holder nattevåk, henimot to tredjedeler av natten, eller halvparten eller en tredjepart, således også en flokk av dem som følger deg. Gud bestemmer utstrekningen av natt og dag. Han vet at dere ikke kan holde regning med det, og Han viser miskunn mot dere. Fremsi så meget av Koranen som dere rimeligvis kan. Han vet at det finnes syke blant dere, andre som farter rundt i landet for å søke Guds goder, atter andre er i felten og kjemper for Guds sak. Så fremsi så meget av den som dere rimeligvis kan. Overhold bønnen, betal det rituelle bidrag! Gi Gud et godt lån! Alt godt dere sender i forveien for dere, dere vil finne det igjen hos Gud, som en bedre og større belønning. Be Gud om tilgivelse. Gud er tilgivende, nåderik!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'Du som ligger innhyllet i din kappe,'),
(5497, 74, 2, 'stå opp og advar!'),
(5498, 74, 3, 'Lovpris din Herre!'),
(5499, 74, 4, 'Hold dine klær rene!'),
(5500, 74, 5, 'Sky det urene!'),
(5501, 74, 6, 'Gi ikke i håp om å få mer igjen!'),
(5502, 74, 7, 'Vis standhaftighet overfor din Herre!'),
(5503, 74, 8, 'Når det støtes i basunen;'),
(5504, 74, 9, 'det vil bli en hard dag;'),
(5505, 74, 10, 'for de vantro alt annet enn lett.'),
(5506, 74, 11, 'La Meg alene med ham Jeg har skapt!'),
(5507, 74, 12, 'Jeg gav ham rikdom i overflod,'),
(5508, 74, 13, 'og sønner som han har om seg.'),
(5509, 74, 14, 'Jeg tilrettela alt for ham!'),
(5510, 74, 15, 'Så begjærer han at Jeg gir ham mer!'),
(5511, 74, 16, 'Nei, så sannelig! Han viser tross mot Våre tegn!'),
(5512, 74, 17, 'Jeg skal gi ham en tung vei!'),
(5513, 74, 18, 'Han tenkte og vurderte,'),
(5514, 74, 19, 'du verden som han vurderte.'),
(5515, 74, 20, 'Ja, du verden, som han vurderte.'),
(5516, 74, 21, 'Derpå så han opp,'),
(5517, 74, 22, 'rynket pannen og så bister ut,'),
(5518, 74, 23, 'snudde ryggen til i hovmod,'),
(5519, 74, 24, 'og sa: «Dette er intet annet enn overlevert magi.'),
(5520, 74, 25, 'Dette er bare et vanlig menneskes ord!»'),
(5521, 74, 26, 'Jeg skal steke ham i Saqar!'),
(5522, 74, 27, 'Vet du hva Saqar er?'),
(5523, 74, 28, 'Det er helvetes ild, som ikke sparer eller skåner.'),
(5524, 74, 29, 'Den svir huden.'),
(5525, 74, 30, 'Den voktes av nitten.'),
(5526, 74, 31, 'Det er alene engler som Vi har satt som Ildens voktere. Deres antall har Vi satt som en prøvelse for de vantro, for at troende skal søke i tro. For at skriftfolkene og de troende ikke skal være i tvil, for at de i hvis hjerter er sykdom, og de vantro, kan si: «Hva kan vel Gud ha ment med noe slikt?» Således lar Gud hvem Han vil fare vill, og veileder hvem Han vil. Ingen kjenner Herrens hærskarer, uten Han selv. Dette er en påminnelse for menneskene.'),
(5527, 74, 32, 'Nei, ved månen!'),
(5528, 74, 33, 'Ved natten når den heller!'),
(5529, 74, 34, 'Ved morgenen når den gryr!'),
(5530, 74, 35, 'Dette er sannelig en av de største ting,'),
(5531, 74, 36, 'en advarsel til menneskene!'),
(5532, 74, 37, 'Til den av dere som vil gå fremover, eller henge etter.'),
(5533, 74, 38, 'Enhver står i pant for det han har gjort!'),
(5534, 74, 39, 'Unntatt er høyrehåndsflokken.'),
(5535, 74, 40, 'I havene vil de forhøre seg'),
(5536, 74, 41, 'om synderne,'),
(5537, 74, 42, '«Hva har ført dere til helvetes ild?»'),
(5538, 74, 43, 'De vil svare: «Vi var ikke av dem som bad,'),
(5539, 74, 44, 'og vi gav ikke den fattige mat.'),
(5540, 74, 45, 'Vi bare pratet med likesinnede,'),
(5541, 74, 46, 'og fornektet dommens dag,'),
(5542, 74, 47, 'inntil vissheten kom til oss.»'),
(5543, 74, 48, 'Nå nytter det dem ikke om noen taler deres sak.'),
(5544, 74, 49, 'Hva feiler det dem, siden de vender seg bort fra påminnelsen?'),
(5545, 74, 50, 'Som forskremte esler'),
(5546, 74, 51, 'som flykter for en løve?'),
(5547, 74, 52, 'Joda, hver og én av dem ønsker at utbredte blader legges frem for ham.'),
(5548, 74, 53, 'Nei så sannelig! De frykter ikke det hinsidige.'),
(5549, 74, 54, 'Nei. Dette er en formaning.'),
(5550, 74, 55, 'Og den som vil kan legge seg den på minnet.'),
(5551, 74, 56, 'Men de legger seg den ikke på minnet, uten at Gud vil. Ham tilligger gudsfrykten, og Ham tilligger tilgivelsen.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Nei! Jeg sverger ved oppstandelsens dag,'),
(5553, 75, 2, 'og enhver sjel som irettesetter seg selv.'),
(5554, 75, 3, 'Tror mennesket at Vi aldri kan samle sammen hans knokler?'),
(5555, 75, 4, 'Jo, sannelig, Vi kan til og med forme hans fingertupper!'),
(5556, 75, 5, 'Mennesket vil fortsette et syndefullt liv,'),
(5557, 75, 6, 'og han spør: «Når kommer så oppstandelsens dag?»'),
(5558, 75, 7, 'Når synet blendes,'),
(5559, 75, 8, 'og månen formørkes,'),
(5560, 75, 9, 'når sol og måne blir ett,'),
(5561, 75, 10, '– denne dag vil mennesket si: «Hvor finnes tilflukt?»'),
(5562, 75, 11, 'Nei! Det finnes intet fristed.'),
(5563, 75, 12, 'På denne dag blir oppholdsstedet hos Herren!'),
(5564, 75, 13, 'På denne dag får mennesket vite hva han har utrettet og hva han har forsømt.'),
(5565, 75, 14, 'Mennesket blir avklaring mot seg selv,'),
(5566, 75, 15, 'selv om han bringer sine unnskyldninger.'),
(5567, 75, 16, 'La ikke tungen løpe for å skynde på åpenbaringen.'),
(5568, 75, 17, 'Vår oppgave er dens sammensetning og dens fremføring.'),
(5569, 75, 18, 'Så når Vi resiterer den, så følg med i dens resitasjon.'),
(5570, 75, 19, 'Derpå er det Vår oppgave å utlegge den.'),
(5571, 75, 20, 'Nei, dere elsker det som iler hen,'),
(5572, 75, 21, 'og lar det hinsidige ligge.'),
(5573, 75, 22, 'På den dag finnes strålende ansikter'),
(5574, 75, 23, 'som ser på Herren,'),
(5575, 75, 24, 'og på den dag finnes ansikter som er formørkede,'),
(5576, 75, 25, 'så man kunne tro at noe skrekkelig er over dem.'),
(5577, 75, 26, 'Nei, når sjelen kommer opp i halsen, og man sier,'),
(5578, 75, 27, '«Hvem kan gjøre en besvergelse?»'),
(5579, 75, 28, 'Og den døende forstår at det er avskjeden,'),
(5580, 75, 29, 'og snor bena om hverandre.'),
(5581, 75, 30, 'Denne dag går turen til Herren!'),
(5582, 75, 31, 'Ikke trodde han, og ikke bad han,'),
(5583, 75, 32, 'men han fornektet og snudde seg bort,'),
(5584, 75, 33, 'og gikk så hjem til sine med stolte steg.'),
(5585, 75, 34, 'Ve deg, ve!'),
(5586, 75, 35, 'Ja, ve deg, ve!'),
(5587, 75, 36, 'Tror mennesket at det overlates til seg selv?'),
(5588, 75, 37, 'Var det ikke en dråpe utstøtt sæd?'),
(5589, 75, 38, 'Så ble det det minste foster. Og Han skapte og formet,'),
(5590, 75, 39, 'og laget det til to sorter, mann og kvinne.'),
(5591, 75, 40, 'Skulle ikke Han være i stand til å gi de døde liv?');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Var det ikke for mennesket en tid da han ikke var noe nevneverdig?'),
(5593, 76, 2, 'Vi har skapt mennesket av en sæddråpe, en blanding, for å prøve ham, og Vi gav ham hørsel og syn.'),
(5594, 76, 3, 'Og Vi har ledet ham til veien, enten han er takknemlig eller utakknemlig.'),
(5595, 76, 4, 'Vi har gjort klar for de vantro kjeder og lenker, og en Ild.'),
(5596, 76, 5, 'De fromme skal drikke av et beger med en blanding med kamfer,'),
(5597, 76, 6, 'en kilde som Guds tjenere drikker fra, og som de lar strømme frem rikelig.'),
(5598, 76, 7, 'De var tro mot sine løfter og fryktet den dag hvis hardhet overalt er på farten.'),
(5599, 76, 8, 'De gav mat til den fattige, den foreldreløse og fangen, selv om de trengte den.'),
(5600, 76, 9, '«Vi bespiser dere bare for Guds skyld, og vi ønsker verken gjengjeldelse eller takk fra dere!'),
(5601, 76, 10, 'Vi frykter en truende, uhellsvanger dag fra Herren.»'),
(5602, 76, 11, 'Men Gud har bevart dem fra denne dags hardhet, og har gitt dem fryd og glede,'),
(5603, 76, 12, 'og belønnet dem for deres standhaftighet med paradisets have, og silkeskrud.'),
(5604, 76, 13, 'Hvilende der på sine leier møter de verken solens hete eller bitter kulde.'),
(5605, 76, 14, 'Havens skygger hviler over dem, og fruktklaser henger ydmykt ned.'),
(5606, 76, 15, 'Det går rundt blant dem sølvkar og krystallbegre,'),
(5607, 76, 16, 'krystallblankt sølv, fint laget.'),
(5608, 76, 17, 'Der får de å drikke et beger med ingefærblanding.'),
(5609, 76, 18, 'fra en kilde der som kalles Salsabil.'),
(5610, 76, 19, 'Evig unge gutter kretser rundt dem. Når du ser dem, tar du dem for utstrødde perler.'),
(5611, 76, 20, 'Når du ser deg om, ser du lykksalighet og et herlig rike.'),
(5612, 76, 21, 'De bærer grønne klær av silke og brokade, og er smykket med sølvarmbånd, og Herren gir dem en ren drikk.'),
(5613, 76, 22, '«Dette er deres lønn! Deres innsats møter anerkjennelse.»'),
(5614, 76, 23, 'Vi har visselig sendt deg Koranen som åpenbaring.'),
(5615, 76, 24, 'Bær tålmodig Herrens tilskikkelser, og hør ikke etter synderen eller den vantro blant dem,'),
(5616, 76, 25, 'men kom Herrens navn i hu tidlig og sent.'),
(5617, 76, 26, 'Og når natten kommer, fall ned for Ham, og pris Ham inn i den dype natt.'),
(5618, 76, 27, 'Disse mennesker elsker det som iler hen, og kaster bak seg en tung dag.'),
(5619, 76, 28, 'Vi har skapt dem, og gitt dem deres styrke. Når Vi vil, bytter Vi dem ut med andre i deres sted.'),
(5620, 76, 29, 'Dette er en formaning, og den som vil, tar veien til sin Herre.'),
(5621, 76, 30, 'Men dere vil ikke, med mindre Gud vil. Gud vet, er vis.'),
(5622, 76, 31, 'Han lar dem Han vil gå inn til Hans nåde. Men de urettferdige har Han beredt en smertelig straff!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Ved den som sendes, den ene etter den andre,'),
(5624, 77, 2, 'som stormer frem,'),
(5625, 77, 3, 'som brer ut'),
(5626, 77, 4, 'og skiller ut,'),
(5627, 77, 5, 'og kaster ut en formaning'),
(5628, 77, 6, 'til unnskyldning eller advarsel!'),
(5629, 77, 7, 'Det dere er stilt i utsikt, vil visselig inntreffe.'),
(5630, 77, 8, 'Når stjernene slukkes,'),
(5631, 77, 9, 'når himmelen revner,'),
(5632, 77, 10, 'når fjellene pulveriseres'),
(5633, 77, 11, 'og sendebudenes tid fastsettes,'),
(5634, 77, 12, '– til hvilken dag er det berammet?'),
(5635, 77, 13, 'Til dommens dag!'),
(5636, 77, 14, 'Hvordan kan du vite hva dommens dag er?'),
(5637, 77, 15, 'Ve dem som holder sannhet for løgn på denne dag!'),
(5638, 77, 16, 'Har Vi ikke utslettet de tidligere?'),
(5639, 77, 17, 'Så lar Vi de senere følge dem.'),
(5640, 77, 18, 'Slik gjør Vi med synderne.'),
(5641, 77, 19, 'Ve dem som holder sannhet for løgn på denne dag!'),
(5642, 77, 20, 'Har Vi ikke skapt dere av en skrøpelig væske?'),
(5643, 77, 21, 'Og plassert den i sikker forvaring,'),
(5644, 77, 22, 'til en bestemt tid?'),
(5645, 77, 23, 'Vi bestemte, og Vi bestemmer godt!'),
(5646, 77, 24, 'Ve dem som holder sannhet for løgn på denne dag!'),
(5647, 77, 25, 'Har Vi ikke gjort jorden til et samlingssted'),
(5648, 77, 26, 'for levende og døde?'),
(5649, 77, 27, 'Vi satte bergmassiver på den. Vi har gitt dere ferskt vann å drikke.'),
(5650, 77, 28, 'Ve dem som holder sannhet for løgn på denne dag!'),
(5651, 77, 29, '«Gå hen til det dere holdt for løgn!'),
(5652, 77, 30, 'Gå hen til den trefoldige skygge,'),
(5653, 77, 31, 'som ikke svaler eller hjelper mot flammen,'),
(5654, 77, 32, 'som kaster gnister store som vedkubber,'),
(5655, 77, 33, 'som gule kameler.»'),
(5656, 77, 34, 'Ve dem som holder sannhet for løgn på denne dag!'),
(5657, 77, 35, 'Dette er dagen der de ikke snakker,'),
(5658, 77, 36, 'og det ikke blir tillatt dem å bringe unnskyldninger.'),
(5659, 77, 37, 'Ve dem som holder sannhet for løgn på denne dag!'),
(5660, 77, 38, 'Dette er dommens dag! Vi har samlet dere og de tidligere henfarne.'),
(5661, 77, 39, 'Om dere har en list, så prøv å overliste meg!'),
(5662, 77, 40, 'Ve dem som holder sannhet for løgn på denne dag!'),
(5663, 77, 41, 'De gudfryktige skal være blant skygger og kilder,'),
(5664, 77, 42, 'og frukt de ønsker seg.'),
(5665, 77, 43, '«Spis og drikk og la det smake, for den innsats dere har gjort!»'),
(5666, 77, 44, 'Slik lønner Vi dem som gjør vel.'),
(5667, 77, 45, 'Ve dem som holder sannhet for løgn på denne dag!'),
(5668, 77, 46, 'Spis og nyt livet en kort tid! Dere er visselig syndere.'),
(5669, 77, 47, 'Ve dem som holder sannhet for løgn på denne dag!'),
(5670, 77, 48, 'Om man sier til dem: «Bøy dere i bønn,» så bøyer de seg ikke.'),
(5671, 77, 49, 'Ve dem som holder sannhet for løgn på denne dag!'),
(5672, 77, 50, 'På hvilket budskap vil de vel tro etter dette?');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'Hva snakket de med hverandre om?'),
(5674, 78, 2, 'Om det store budskap,'),
(5675, 78, 3, 'som de er uenige om.'),
(5676, 78, 4, 'Nei, sannelig skal de få å vite!'),
(5677, 78, 5, 'Så sannelig skal de få å vite!'),
(5678, 78, 6, 'Har Vi ikke gjort jorden til et leie?'),
(5679, 78, 7, 'Og fjellene til støttepilarer?'),
(5680, 78, 8, 'Vi har skapt dere parvis'),
(5681, 78, 9, 'og gitt dere søvnen til hvile'),
(5682, 78, 10, 'og gjort natten til en kledning'),
(5683, 78, 11, 'og dagen til å skaffe en levemåte.'),
(5684, 78, 12, 'Over dere har Vi bygget syv solide himler'),
(5685, 78, 13, 'og plassert en skinnende lampe,'),
(5686, 78, 14, 'og Vi lot falle regn fra regntunge skyer i strømmer,'),
(5687, 78, 15, 'for derved å frembringe korn og planter'),
(5688, 78, 16, 'og bugnende haver.'),
(5689, 78, 17, 'Dommens dag er fastsatt.'),
(5690, 78, 18, 'Den dag det støtes i basunen, og dere kommer i flokker;'),
(5691, 78, 19, 'når himmelen åpnes og blir dører,'),
(5692, 78, 20, 'og fjellene settes i bevegelse og blir en luftspeiling.'),
(5693, 78, 21, 'Sannelig, helvete ligger på lur,'),
(5694, 78, 22, 'et hjem for de oppsetsige.'),
(5695, 78, 23, 'Der skal de bli gjennom tider,'),
(5696, 78, 24, 'og der smaker de ingen forfriskende kjølighet og intet å drikke,'),
(5697, 78, 25, 'unntatt kokende vann og kloakk,'),
(5698, 78, 26, 'som en passende belønning.'),
(5699, 78, 27, 'De så ikke frem til en avregning,'),
(5700, 78, 28, 'og fornektet trossig Vårt ord.'),
(5701, 78, 29, 'Alt har Vi opptegnet i en regnskapsbok.'),
(5702, 78, 30, '«Nå kan dere smake! Vi skal bare øke straffen enda mer.»'),
(5703, 78, 31, 'De gudfryktige har i vente seierens sted,'),
(5704, 78, 32, 'haver og vingårder,'),
(5705, 78, 33, 'høybarmede, jevngamle jomfruer,'),
(5706, 78, 34, 'og et fylt beger.'),
(5707, 78, 35, 'Der hører de verken tomt snakk eller løgnprat.'),
(5708, 78, 36, 'Som lønn fra Herren, som gave og som avregning,'),
(5709, 78, 37, 'fra Herren over himmel og jord og alt som mellom dem er, den Barmhjertige, som ingen formår å tale til.'),
(5710, 78, 38, 'Den dag, da Ånden og englene står på rekke og rad, da kan ingen tale, unntatt den som den barmhjertige har tillatt, og som taler rett.'),
(5711, 78, 39, 'Dette er sannhetens dag, og måtte alle som vil, finne hjem til Herren.'),
(5712, 78, 40, 'Vi har advart dere mot en overhengende straff, den dag når mennesket får se hva han har sendt i forveien til regnskapet, og den vantro utbryter: «Hadde jeg bare vært støv!»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Ved dem som trekker ut og spenner!'),
(5714, 79, 2, 'Ved dem som går ivrig frem!'),
(5715, 79, 3, 'Ved dem som siger frem,'),
(5716, 79, 4, 'og så tar ledelsen'),
(5717, 79, 5, 'og tar seg av saken!'),
(5718, 79, 6, 'Den dag når rystelsen ryster,'),
(5719, 79, 7, 'og følges av rystelse,'),
(5720, 79, 8, 'på denne dag vil hjertene banke'),
(5721, 79, 9, 'og blikkene slås ned.'),
(5722, 79, 10, 'De sier: «Skal vi bringes tilbake til det vi var?'),
(5723, 79, 11, 'Når vi er morkne ben?»'),
(5724, 79, 12, 'Og de tilføyer: «Det vil være en retur med tap!»'),
(5725, 79, 13, 'Men det lyder et eneste skrall,'),
(5726, 79, 14, 'og så er de lys våkne.'),
(5727, 79, 15, 'Historien om Moses har vel nådd deg?'),
(5728, 79, 16, 'Da Herren ropte til ham i den hellige dal Tuwa:'),
(5729, 79, 17, '«Gå til Farao! Han er oppsetsig!'),
(5730, 79, 18, 'Og si: ’Ønsker du ikke å rense deg,'),
(5731, 79, 19, 'og at jeg leder deg hen til Herren, så du kan vise gudsfrykt?’»'),
(5732, 79, 20, 'Så viste Han ham det største jærtegnet,'),
(5733, 79, 21, 'men han forkastet og var ulydig.'),
(5734, 79, 22, 'Så vendte han ryggen og fjernet seg raskt,'),
(5735, 79, 23, 'sammenkalte folk'),
(5736, 79, 24, 'og sa: «Jeg er deres høyeste herre.»'),
(5737, 79, 25, 'Så tok Gud ham fatt med den hinsidiges og denne verdens straff.'),
(5738, 79, 26, 'I dette er visselig en lærepenge for den som har gudsfykt!'),
(5739, 79, 27, 'Er dere mer krevende å skape, eller himmelen Han har bygget?'),
(5740, 79, 28, 'Han hevet opp dens tak og gav den form,'),
(5741, 79, 29, 'lot dens natt bli mørk og lot morgenlyset komme frem.'),
(5742, 79, 30, 'Så bredte Han jorden ut'),
(5743, 79, 31, 'og brakte frem av den vann og grønne enger,'),
(5744, 79, 32, 'og Han gav fjellene fast feste,'),
(5745, 79, 33, 'til glede for dere og deres hjorder.'),
(5746, 79, 34, 'Når så den store katastrofe inntreffer,'),
(5747, 79, 35, 'den dag når mennesket kommer i hu hva han jaget etter,'),
(5748, 79, 36, 'og helvete bringes frem for alle som ser,'),
(5749, 79, 37, 'da skal den som var oppsetsig'),
(5750, 79, 38, 'og foretrakk jordelivet,'),
(5751, 79, 39, 'få helvete til herberge.'),
(5752, 79, 40, 'Men den som fryktet Herrens maktstilling og nektet seg sine lyster,'),
(5753, 79, 41, '– se, paradisets have er herberget.'),
(5754, 79, 42, 'De spør deg om timen, om når den kommer.'),
(5755, 79, 43, 'Hva kan vel du si om dette?'),
(5756, 79, 44, 'Dens endelige tid beror hos Herren.'),
(5757, 79, 45, 'Du er bare en advarer for dem som frykter den.'),
(5758, 79, 46, 'Den dag de får se den, blir det som om de hadde ventet bare en kveld eller en morgenstund.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Han rynket pannen og vendte seg bort'),
(5760, 80, 2, 'fordi den blinde kom til ham.'),
(5761, 80, 3, 'Men hva vet du, kanskje vil han rense seg,'),
(5762, 80, 4, 'eller komme til ettertanke, så formaningen kan være til gagn for ham?'),
(5763, 80, 5, 'Men den som er seg selv nok,'),
(5764, 80, 6, 'ham tar du vel imot,'),
(5765, 80, 7, 'skjønt det er ikke ditt ansvar om han ikke renser seg.'),
(5766, 80, 8, 'Men den som kommer til deg full av iver'),
(5767, 80, 9, 'og frykt,'),
(5768, 80, 10, 'ham overser du.'),
(5769, 80, 11, 'Nei sannelig, dette er en påminnelse,'),
(5770, 80, 12, 'og den som vil, legger seg den på minne,'),
(5771, 80, 13, 'på ærverdige blad,'),
(5772, 80, 14, 'opphøyde og rene,'),
(5773, 80, 15, 'ved skriveres hender,'),
(5774, 80, 16, 'edle og fromme.'),
(5775, 80, 17, 'Død over mennesket! Hvor utakknemlig han er!'),
(5776, 80, 18, 'Av hva har Han vel skapt ham?'),
(5777, 80, 19, 'Av en sæddråpe har Han skapt ham, og så fastsatt rammen for ham.'),
(5778, 80, 20, 'Derpå har Han lagt til rette hans vei.'),
(5779, 80, 21, 'Så lar Han ham dø og bringer ham i graven.'),
(5780, 80, 22, 'Derpå, når Han vil, gjenoppvekker Han ham.'),
(5781, 80, 23, 'Nei, sannelig, han har ikke utført det Han påla ham!'),
(5782, 80, 24, 'Så la da mennesket betrakte sin føde!'),
(5783, 80, 25, 'Vi har latt vannet strømme rikelig,'),
(5784, 80, 26, 'og så laget Vi små sprekker i jorden.'),
(5785, 80, 27, 'Og i dem har Vi latt korn vokse,'),
(5786, 80, 28, 'og vinstokker og grønnsaker,'),
(5787, 80, 29, 'oliven og palmer'),
(5788, 80, 30, 'og trefylte haver'),
(5789, 80, 31, 'og frukt, og fôr,'),
(5790, 80, 32, 'til glede for dere og deres fe.'),
(5791, 80, 33, 'Men når drønnet kommer!'),
(5792, 80, 34, 'På denne dag flyr mannen bort fra sin bror,'),
(5793, 80, 35, 'sin mor og sin far,'),
(5794, 80, 36, 'sin hustru og sine barn.'),
(5795, 80, 37, 'På denne dag har enhver en sak som opptar ham fullt.'),
(5796, 80, 38, 'På denne dag vil det være ansikter som stråler,'),
(5797, 80, 39, 'og ler og er fulle av fryd.'),
(5798, 80, 40, 'På denne dag vil det være ansikter fylt av støv'),
(5799, 80, 41, 'og dekket av mørke:'),
(5800, 80, 42, 'Disse er de vantro, de syndefulle.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Når solen formørkes,'),
(5802, 81, 2, 'når stjernene fordunkles,'),
(5803, 81, 3, 'når fjellene settes i bevegelse,'),
(5804, 81, 4, 'når kameler som skal føde forsømmes,'),
(5805, 81, 5, 'når villdyrene flokker seg,'),
(5806, 81, 6, 'når havene syder,'),
(5807, 81, 7, 'når sjelene forenes med kroppene,'),
(5808, 81, 8, 'når småpiker som ble levende begravd spørres,'),
(5809, 81, 9, 'for hvilken synd de ble drept,'),
(5810, 81, 10, 'når skriftrullene (regnskapsbøkene) åpnes,'),
(5811, 81, 11, 'når himmelen flås vekk,'),
(5812, 81, 12, 'når det fyres opp i helvete,'),
(5813, 81, 13, 'når paradiset bringes nær,'),
(5814, 81, 14, 'da skal enhver få vite hva han har prestert.'),
(5815, 81, 15, 'Jeg sverger ved planetene,'),
(5816, 81, 16, 'de vandrende og forsvinnende stjerner,'),
(5817, 81, 17, 'ved natten, når den mørkner,'),
(5818, 81, 18, 'ved morgenen, når den trekker pusten:'),
(5819, 81, 19, 'Dette er sannelig ord fra en nobel utsending,'),
(5820, 81, 20, 'med stor makt hos tronens Herre,'),
(5821, 81, 21, 'fast, adlydt og trofast.'),
(5822, 81, 22, 'Deres bysbarn er aldeles ikke besatt!'),
(5823, 81, 23, 'Han har virkelig sett ham ved den klare horisont!'),
(5824, 81, 24, 'Han var ikke tilbakeholden når det gjelder det skjulte!'),
(5825, 81, 25, 'Det var ikke en forkastet satans ord!'),
(5826, 81, 26, 'Hvor vil dere da ta veien?'),
(5827, 81, 27, 'Dette er intet annet enn en formaning for all verden,'),
(5828, 81, 28, 'for den av dere som vil holde rett kurs.'),
(5829, 81, 29, 'Men dere vil ikke, med mindre Gud, all verdens Herre, vil.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Når himmelen revner,'),
(5831, 82, 2, 'når stjernene strøs utover,'),
(5832, 82, 3, 'når havene flyter over,'),
(5833, 82, 4, 'når gravene blir endevendt,'),
(5834, 82, 5, 'da skal enhver få vite hva han har utrettet og hva han har forsømt.'),
(5835, 82, 6, 'Menneske! Hva har forledet deg vedrørende din høysinnede Herre?'),
(5836, 82, 7, 'Han som har skapt deg, formet deg og gitt deg rette proporsjoner'),
(5837, 82, 8, 'i den skikkelse Han ønsket å gi deg?'),
(5838, 82, 9, 'Men sannelig, dere fornekter dommen!'),
(5839, 82, 10, 'Men over dere er voktere,'),
(5840, 82, 11, 'ærverdige skrivere'),
(5841, 82, 12, 'som vel vet hva dere gjør!'),
(5842, 82, 13, 'De fromme skal være i salighet,'),
(5843, 82, 14, 'mens de syndefulle skal være i helvete.'),
(5844, 82, 15, 'Der skal de brenne på dommens dag,'),
(5845, 82, 16, 'og derfra kan de ikke komme bort.'),
(5846, 82, 17, 'Hvordan kan du vite hva dommens dag er?'),
(5847, 82, 18, 'Hvordan kan du virkelig vite hva dommens dag er?'),
(5848, 82, 19, 'Den dag da ingen makter å utrette noe for en annen. Saken ligger denne dag i Guds hånd!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Ve dem som snyter!'),
(5850, 83, 2, 'Som når de tilmåles av folk, krever fullt mål,'),
(5851, 83, 3, 'men når de måler ut til dem eller veier til dem, så snyter de.'),
(5852, 83, 4, 'Tror ikke disse at de blir gjenoppvekket'),
(5853, 83, 5, 'til en svær dag,'),
(5854, 83, 6, 'den dag da menneskene skal stå frem for all verdens Herre?'),
(5855, 83, 7, 'Så sannelig, de syndefulles bok er i Sidsjin.'),
(5856, 83, 8, 'Men hvordan kan du vite hva Sidsjin er?'),
(5857, 83, 9, 'Det er en fullskreven bok.'),
(5858, 83, 10, 'Ve dem som holder sannhet for løgn på denne dag!'),
(5859, 83, 11, 'Som fornekter dommens dag!'),
(5860, 83, 12, 'Ingen fornekter den, unntatt enhver som går over grensen, full av synd.'),
(5861, 83, 13, 'Når Vårt ord fremleses for ham, sier han: «Fabler fra gamle dager!»'),
(5862, 83, 14, 'Nei så sannelig, det de har bedrevet, har rustet fast på deres hjerter!'),
(5863, 83, 15, 'Sannelig, på hin dag skal de stenges ute fra Herren,'),
(5864, 83, 16, 'og så skal de havne i helvete.'),
(5865, 83, 17, 'Så lyder det: «Dette er det dere fornektet!»'),
(5866, 83, 18, 'Så sannelig, de frommes bok er i Illiyun.'),
(5867, 83, 19, 'Men hvordan kan du vite hva Illiyun er?'),
(5868, 83, 20, 'Det er en fullskreven bok,'),
(5869, 83, 21, 'som de som er Gud nær, bevitner.'),
(5870, 83, 22, 'De fromme skal visselig være i lykksalighet.'),
(5871, 83, 23, 'På hvileleier lar de blikket streife,'),
(5872, 83, 24, 'og du ser i deres ansikter i lykksalighetens glans.'),
(5873, 83, 25, 'De får forseglet, edel vin å drikke,'),
(5874, 83, 26, 'dens segl er moskus, og måtte alle lengtende lengte etter dette!'),
(5875, 83, 27, 'Dens blanding er fra Tasnim,'),
(5876, 83, 28, 'en kilde hvorav de drikker som er Gud nær.'),
(5877, 83, 29, 'Sannelig, synderne lo av de troende,'),
(5878, 83, 30, 'og når de gikk forbi dem, blunket de til hverandre,'),
(5879, 83, 31, 'og når de kom hjem til sine egne, var de glade og fornøyde.'),
(5880, 83, 32, 'Og når de så dem, sa de: «Disse er helt på villspor!»'),
(5881, 83, 33, 'Men de var ikke sendt som voktere over dem!'),
(5882, 83, 34, 'Så på denne dag ler de troende av de vantro,'),
(5883, 83, 35, 'der de ligger på hvileleier og lar blikket streife.'),
(5884, 83, 36, 'Er ikke de vantro blitt belønnet for det de bedrev?');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Når himmelen revner,'),
(5886, 84, 2, 'adlyder Herren, og er under Hans bud.'),
(5887, 84, 3, 'Når jorden jevnes ut'),
(5888, 84, 4, 'og kaster frem det som er i den og tømmer seg,'),
(5889, 84, 5, 'adlyder Herren, og er under Hans bud.'),
(5890, 84, 6, 'Menneske, du strever med kraft henimot din Herre, og du skal møte Ham.'),
(5891, 84, 7, 'Hva den angår som får sin bok levert i høyre hånd,'),
(5892, 84, 8, 'han får en lett avregning'),
(5893, 84, 9, 'og vender glad om til sine.'),
(5894, 84, 10, 'Men hva den angår som får levert sin bok fra bak ryggen,'),
(5895, 84, 11, 'han vil rope på tilintetgjørelse,'),
(5896, 84, 12, 'og han havner i helvetes ild.'),
(5897, 84, 13, 'Han levde glad og fornøyd blant sine'),
(5898, 84, 14, 'og trodde visselig at han aldri skulle vende tilbake.'),
(5899, 84, 15, 'Men nei, Herren hadde ham for øyet.'),
(5900, 84, 16, 'Nei, jeg sverger ved aftenrøden,'),
(5901, 84, 17, 'ved natten og alt den bærer i seg,'),
(5902, 84, 18, 'og ved månen når den er full!'),
(5903, 84, 19, 'Dere skal sannelig gå fra tilstand til tilstand.'),
(5904, 84, 20, 'Hva er det med dem, at de ikke tror?'),
(5905, 84, 21, 'Og når Koranen fremleses for dem, at de ikke faller ned for Gud?'),
(5906, 84, 22, 'Nei, de som er vantro holder sannhet for løgn,'),
(5907, 84, 23, 'og Gud vet best hva de har i seg.'),
(5908, 84, 24, 'Bebud for dem en smertelig straff!'),
(5909, 84, 25, 'Unntatt er de som tror og lever rettskaffent. De skal få en velfortjent lønn!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Ved himmelen med sitt stjernemylder!'),
(5911, 85, 2, 'Ved den lovede dag!'),
(5912, 85, 3, 'Ved vitnet og vitnesbyrdet!'),
(5913, 85, 4, 'Død over branngrav-mennene'),
(5914, 85, 5, 'med den brenselsforsynte ild,'),
(5915, 85, 6, 'som satt omkring den,'),
(5916, 85, 7, 'og overvar hva man gjorde med de troende!'),
(5917, 85, 8, 'De hevnet seg på dem bare fordi de trodde på Gud, den Mektige, den Prisede.'),
(5918, 85, 9, 'Ham, som herredømmet over himlene og jorden tilkommer. Gud bevitner alle ting!'),
(5919, 85, 10, 'De som forfølger troende menn og kvinner, og så ikke vender om, de har i vente helvetes straff, de har i vente Ildens straff.'),
(5920, 85, 11, 'De som tror og lever rettskaffent, har i vente paradisets haver, hvor bekker sildrer. Dette er den store seier!'),
(5921, 85, 12, 'Visselig, Herren slår hardt til.'),
(5922, 85, 13, 'Han bringer frem, og gjør det på nytt.'),
(5923, 85, 14, 'Han er den Ettergivende, den Velvillige,'),
(5924, 85, 15, 'tronens Herre, den Ærerike,'),
(5925, 85, 16, 'Den som gjennomfører det Han vil.'),
(5926, 85, 17, 'Har historien om hærskarene nådd deg,'),
(5927, 85, 18, 'om Farao og Thamod?'),
(5928, 85, 19, 'Sannelig, de vantro turer frem i fornektelse.'),
(5929, 85, 20, 'Men Gud er bak dem og innringer.'),
(5930, 85, 21, 'Ja, dette er en herlig Koran,'),
(5931, 85, 22, 'på en vel forvart himmelsk tavle.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Ved himmelen og morgenstjernen!'),
(5933, 86, 2, 'Vet du hva morgenstjernen er?'),
(5934, 86, 3, 'Den tindrende stjerne!'),
(5935, 86, 4, 'Over hvert menneske er det en som passer på.'),
(5936, 86, 5, 'La mennesket betenke hvorav han ble skapt.'),
(5937, 86, 6, 'Han ble skapt av en frembrytende væske,'),
(5938, 86, 7, 'som kommer fra mellom lendene og brystbenet!'),
(5939, 86, 8, 'Han er visselig i stand til å bringe ham tilbake'),
(5940, 86, 9, 'den dag da alle hemmeligheter prøves,'),
(5941, 86, 10, 'og han har verken makt eller hjelper.'),
(5942, 86, 11, 'Ved himmelen med det tilbakevendende regn'),
(5943, 86, 12, 'og jorden, som åpner sitt skjød!'),
(5944, 86, 13, 'Dette er avgjørende ord,'),
(5945, 86, 14, 'det er ingen spøk.'),
(5946, 86, 15, 'De anvender knep.'),
(5947, 86, 16, 'Men også Jeg bruker list!'),
(5948, 86, 17, 'Så ta det med ro med de vantro, ta det med ro med dem for en tid.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Pris Herrens navn, den Høyeste,'),
(5950, 87, 2, 'som skapte og formet,'),
(5951, 87, 3, 'bestemte og ledet,'),
(5952, 87, 4, 'som frembrakte beitemarker'),
(5953, 87, 5, 'og lot dem bli tørre strå!'),
(5954, 87, 6, 'Vi skal la deg forkynne (Koran), og glem så ikke,'),
(5955, 87, 7, 'unntatt det Gud vil. Han kjenner til det som lyder høyt, og det som holdes skjult.'),
(5956, 87, 8, 'Vi skal lette det for deg hen til lettelsen.'),
(5957, 87, 9, 'Så forman, der formaning nytter!'),
(5958, 87, 10, 'Den som frykter, vil vise ettertanke,'),
(5959, 87, 11, 'men den usle vil unngå den,'),
(5960, 87, 12, 'han som skal havne i den store Ild,'),
(5961, 87, 13, 'og der kan han verken dø eller leve.'),
(5962, 87, 14, 'Ham går det godt, som har renset seg,'),
(5963, 87, 15, 'kommer Herrens navn i hu, og forretter bønnen.'),
(5964, 87, 16, 'Nei, dere foretrekker jordelivet,'),
(5965, 87, 17, 'men det hinsidige er bedre, og mer varig.'),
(5966, 87, 18, 'Dette står visselig i de gamle skrifter,'),
(5967, 87, 19, 'på Abrahams og Mose blader.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'Har du fått beretningen om det overdekkende?'),
(5969, 88, 2, 'Noen vil på denne dag være ydmykede,'),
(5970, 88, 3, 'anstrengte, utslitte,'),
(5971, 88, 4, 'idet de er i den hete Ild,'),
(5972, 88, 5, 'får drikke fra en kokhet kilde'),
(5973, 88, 6, 'og bare har kaktus til mat,'),
(5974, 88, 7, 'som ikke gir næring og ikke stiller hungeren.'),
(5975, 88, 8, 'Andre vil på denne dag være glade,'),
(5976, 88, 9, 'tilfredse med sitt strev,'),
(5977, 88, 10, 'idet de er i en frittliggende have,'),
(5978, 88, 11, 'hvor de ikke hører tomt prat.'),
(5979, 88, 12, 'Det er en sprudlende kilde,'),
(5980, 88, 13, 'behagelige benker,'),
(5981, 88, 14, 'fremsatte glass,'),
(5982, 88, 15, 'puter på rekke og rad'),
(5983, 88, 16, 'og tepper bredt ut.'),
(5984, 88, 17, 'Ser de da ikke på kamelen, på hvordan den er skapt?'),
(5985, 88, 18, 'På himmelen, hvordan den hvelves høyt oppe?'),
(5986, 88, 19, 'På fjellene, hvordan de er reist opp?'),
(5987, 88, 20, 'På jorden, hvordan den er strukket ut?'),
(5988, 88, 21, 'Så minn dem på! Du er bare en formaner.'),
(5989, 88, 22, 'Du har ingen makt over dem.'),
(5990, 88, 23, 'Den som vender seg bort i vantro,'),
(5991, 88, 24, 'vil Gud hjemsøke med den største straff.'),
(5992, 88, 25, 'Til Oss skal de vende tilbake,'),
(5993, 88, 26, 'og så skal Vi ordne med deres regnskap.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Ved morgengryet!'),
(5995, 89, 2, 'Ved ti netter!'),
(5996, 89, 3, 'Ved det like og det ulike!'),
(5997, 89, 4, 'Ved natten når den svinner hen!'),
(5998, 89, 5, 'Er det ikke i dette en ed for den som har innsikt?'),
(5999, 89, 6, 'Du har vel sett hva Herren gjorde med folket Ad?'),
(6000, 89, 7, 'Med det søylerike Iram,'),
(6001, 89, 8, 'hvis like ikke er skapt i verden?'),
(6002, 89, 9, 'Og med folket Thamod, som hugget seg inn i fjellsiden i dalen?'),
(6003, 89, 10, 'Og Farao, han med pyramidene?'),
(6004, 89, 11, 'De som vandret i overmot på jorden,'),
(6005, 89, 12, 'og spredte ufred og fordervelse?'),
(6006, 89, 13, 'Men Herren lot tuktens svepe ramme dem!'),
(6007, 89, 14, 'Herren er på vakt!'),
(6008, 89, 15, 'Når Herren setter mennesket på prøve ved ære og velstand, sier det: «Herren har hedret meg!»'),
(6009, 89, 16, 'Men hvis Han setter det på prøve ved å utmåle knapt hans levemåte, så sier det: «Herren ringeakter meg!»'),
(6010, 89, 17, 'Nei, sannelig, men dere hedrer ikke den faderløse'),
(6011, 89, 18, 'og ansporer ikke til å gi den fattige mat,'),
(6012, 89, 19, 'dere sluker grådig hans lille arv.'),
(6013, 89, 20, 'Dere elsker rikdom over all måte.'),
(6014, 89, 21, 'Nei, sannelig! Når jorden jevnes til støv,'),
(6015, 89, 22, 'når Herren kommer, og englene, rekke etter rekke,'),
(6016, 89, 23, 'når helvete bringes frem, på denne dag vil mennesket saktens erindre. Men hva tjener det da til med ettertanke?'),
(6017, 89, 24, 'Og det vil si: «Om jeg bare hadde sendt i forveien noe godt for mitt liv!»'),
(6018, 89, 25, 'Ingen kan straffe som Han gjør på denne dag.'),
(6019, 89, 26, 'Ingen lenker slik som Han.'),
(6020, 89, 27, 'Du sjel, som har ro og harmoni,'),
(6021, 89, 28, 'vend tilbake til din Herre tilfreds og godtatt:'),
(6022, 89, 29, 'Gå inn blant Mine tjenere!'),
(6023, 89, 30, 'Gå inn i Mitt paradis!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Nei, sannelig, jeg sverger ved denne byen,'),
(6025, 90, 2, 'og du bor jo i denne byen,'),
(6026, 90, 3, 'og ved en far og hans avkom!'),
(6027, 90, 4, 'Vi har visselig skapt mennesket til møye.'),
(6028, 90, 5, 'Tror han ikke at noen har makt over ham?'),
(6029, 90, 6, 'Han sier: «Jeg har satt over styr rikdom i massevis.»'),
(6030, 90, 7, 'Tror han at ingen har sett ham?'),
(6031, 90, 8, 'Har Vi ikke gitt ham to øyne,'),
(6032, 90, 9, 'tunge, og to lepper.'),
(6033, 90, 10, 'Har Vi ikke vist ham de to veier?'),
(6034, 90, 11, 'Men han gav seg ikke i kast med den trange og bratte.'),
(6035, 90, 12, 'Vet du da hva den trange vei er?'),
(6036, 90, 13, 'Det er å skaffe en slave frihet,'),
(6037, 90, 14, 'og på hungerens dag å gi mat'),
(6038, 90, 15, 'til en foreldreløs slektning,'),
(6039, 90, 16, 'eller til en fattig stakkar,'),
(6040, 90, 17, 'og så være av dem som tror, og som anbefaler hverandre standhaftighet, og råder hverandre til medfølelse.'),
(6041, 90, 18, 'Dette er høyre hånds folk!'),
(6042, 90, 19, 'Men de som fornekter Vårt ord, de er venstre hånds folk.'),
(6043, 90, 20, 'Over dem hvelver seg Ild.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Ved solen og dens formiddagsglød!'),
(6045, 91, 2, 'Ved månen, når den følger den!'),
(6046, 91, 3, 'Ved dagen, når den lar den lyse!'),
(6047, 91, 4, 'Ved natten, når den hyller den inn!'),
(6048, 91, 5, 'Ved himmelen, og Han som bygget den!'),
(6049, 91, 6, 'Ved jorden, og Han som bredte den ut!'),
(6050, 91, 7, 'Ved mennesket, og Han som formet det,'),
(6051, 91, 8, 'og utstyrte det med syndighet og gudsfrykt!'),
(6052, 91, 9, 'Ham går det godt, som holder seg ren.'),
(6053, 91, 10, 'Ham går det galt, som lar seg forkomme.'),
(6054, 91, 11, 'Thamod fornektet i sin oppsetsighet,'),
(6055, 91, 12, 'da den laveste blant dem stod frem.'),
(6056, 91, 13, 'Guds sendebud sa til dem: «Guds kamelhoppe! La den drikke!»'),
(6057, 91, 14, 'Men de holdt ham for løgner og lemlestet den, og så kom Herren over dem for deres synd, og jevnet dem med jorden.'),
(6058, 91, 15, 'Han var ikke redd for følgene!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Ved natten når den dekker til!'),
(6060, 92, 2, 'Ved dagen når den stråler opp!'),
(6061, 92, 3, 'Ved Han som skapte mann og kvinne!'),
(6062, 92, 4, 'Deres innsats er visselig av mange slag.'),
(6063, 92, 5, 'Hva ham angår, som gir gaver og viser gudsfrykt,'),
(6064, 92, 6, 'og tror på det beste,'),
(6065, 92, 7, 'ham vil Vi lette det for hen til lettelsen.'),
(6066, 92, 8, 'Men hva ham angår, som er gjerrig og seg selv nok,'),
(6067, 92, 9, 'og fornekter det beste,'),
(6068, 92, 10, 'ham vil Vi lette det for hen til det tunge,'),
(6069, 92, 11, 'og hans rikdom kan ikke hjelpe ham når han går i fortapelsen.'),
(6070, 92, 12, 'Ledelsen er visselig Vår sak,'),
(6071, 92, 13, 'og Vårt er det siste, og det første.'),
(6072, 92, 14, 'Så advarer Jeg dere da mot en flammende Ild,'),
(6073, 92, 15, 'hvor bare den elendigste skal havne,'),
(6074, 92, 16, 'han som fornekter og vender ryggen til.'),
(6075, 92, 17, 'Men den fromme og gudfryktige skal holdes unna den'),
(6076, 92, 18, 'som gir ut det han eier for å rense seg,'),
(6077, 92, 19, 'og som ikke viser noen en gunst for at den skal gjengjeldes,'),
(6078, 92, 20, 'men kun søker Herrens, den Høyestes velbehag.'),
(6079, 92, 21, 'Han skal visselig bli tilfreds!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Ved formiddagens glød!'),
(6081, 93, 2, 'Ved nattens stillhet!'),
(6082, 93, 3, 'Ikke har din Herre forlatt deg, og ikke er Han misfornøyd med deg!'),
(6083, 93, 4, 'Enden skal bli bedre for deg enn begynnelsen.'),
(6084, 93, 5, 'Herren skal gi deg så du blir tilfreds!'),
(6085, 93, 6, 'Fant Han deg ikke foreldreløs, og gav deg ly?'),
(6086, 93, 7, 'Han fant deg villfarende, og veiledet deg!'),
(6087, 93, 8, 'Han fant deg trengende, og gjorde deg uavhengig!'),
(6088, 93, 9, 'Så vær ikke hard mot den faderløse,'),
(6089, 93, 10, 'og vis ikke bort den som ber.'),
(6090, 93, 11, 'Og hva Herrens godhet angår, så gjør den kjent!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Har Vi ikke åpnet ditt hjerte!'),
(6092, 94, 2, 'Har Vi ikke tatt fra deg din byrde?'),
(6093, 94, 3, 'Som tynget på din rygg?'),
(6094, 94, 4, 'Og Vi har hevet din anseelse!'),
(6095, 94, 5, 'Motgang og medgang følges ad,'),
(6096, 94, 6, 'ja, visselig, motgang og medgang følges ad.'),
(6097, 94, 7, 'Når du nå er fri, så anstreng deg,'),
(6098, 94, 8, 'og søk din Herre!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Ved fikentreet og oljetreet'),
(6100, 95, 2, 'og ved Sinais berg!'),
(6101, 95, 3, 'Ved denne fredlyste by!'),
(6102, 95, 4, 'Vi skapte mennesket på beste måte.'),
(6103, 95, 5, 'Så lot Vi ham synke dypt,'),
(6104, 95, 6, 'unntatt dem som tror og lever rettskaffent, de skal få sin velfortjente lønn.'),
(6105, 95, 7, 'Hva kan vel så få deg til å fornekte dommen?'),
(6106, 95, 8, 'Er ikke Gud den rettferdigste dommer?');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Forkynn i din Herres navn! Han som skaper,'),
(6108, 96, 2, 'skaper mennesket av en liten seig klump.'),
(6109, 96, 3, 'Forkynn! Se, Herren er den store velgjører,'),
(6110, 96, 4, 'som ved pennen har undervist,'),
(6111, 96, 5, 'undervist mennesket om ting det ikke kjente.'),
(6112, 96, 6, 'Nei, mennesket er sannelig oppsetsig,'),
(6113, 96, 7, 'for det mener å være seg selv nok!'),
(6114, 96, 8, 'Til Herren vender alt tilbake!'),
(6115, 96, 9, 'Du har vel sett ham som forbyr'),
(6116, 96, 10, 'en trell når han forretter bønnen?'),
(6117, 96, 11, 'Mener du at han følger ledelsen,'),
(6118, 96, 12, 'eller fremmer gudsfrykt?'),
(6119, 96, 13, 'Eller mener du at han fornekter og vender seg bort?'),
(6120, 96, 14, 'Vet han ikke at Gud ser!'),
(6121, 96, 15, 'Nei, sannelig, hvis han ikke slutter, skal Vi ta ham i luggen,'),
(6122, 96, 16, 'en løgnaktig, syndefull lugg!'),
(6123, 96, 17, 'La ham bare tilkalle sin klikk,'),
(6124, 96, 18, 'så skal Vi tilkalle vokterne!'),
(6125, 96, 19, 'Nei! Adlyd ham ikke! Fall ned i bønn, og kom din Gud nær!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Vi har sendt den ned i skjebnenatten.'),
(6127, 97, 2, 'Hvordan kan du vel vite hva skjebnenatten er?'),
(6128, 97, 3, 'Skjebnenatten er mer verdt enn tusen måneder!'),
(6129, 97, 4, 'I den stiger englene og Ånden ned, på sin Herres bud, med verv av alle slag.'),
(6130, 97, 5, 'Den er fred til morgenrøden stiger frem.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'De vantro blant skriftfolket, og avgudsdyrkerne, kunne ikke bryte ut, før den klare beskjed kom til dem,'),
(6132, 98, 2, '– et sendebud fra Gud, som fremleste rene blader'),
(6133, 98, 3, 'med skriftens rette ord.'),
(6134, 98, 4, 'Og de som har fått skriften, kom ikke i splittelse, før etter at den klare beskjed var kommet til dem.'),
(6135, 98, 5, 'De ble kun pålagt å tjene Gud i religiøs oppriktighet overfor ham, som Gud-søkere, og å forrette bønnen og gi det rituelle bidrag. Dette er rett religion.'),
(6136, 98, 6, 'De vantro blant skriftfolkene og avgudsdyrkerne skal være i helvetes ild, og der skal de være og bli. Disse er de verste skapninger.'),
(6137, 98, 7, 'Men de som tror og lever rettskaffent, disse er de beste skapninger.'),
(6138, 98, 8, 'Deres lønn er hos Herren, Edens haver, hvor bekker sildrer, hvor de skal være og bli for alltid. Gud er tilfreds med dem, og de er tilfreds med Ham. Dette for dem som frykter Herren.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Når jorden rystes i det store jordskjelv,'),
(6140, 99, 2, 'og velter opp det som hviler i den,'),
(6141, 99, 3, 'og menneskene vil si: «Hva går det av den?»'),
(6142, 99, 4, 'På denne dag vil den fortelle sin beretning,'),
(6143, 99, 5, 'for Herren har inspirert den.'),
(6144, 99, 6, 'På denne dag vil menneskene stige frem i skarer, for å bli vist sine gjerninger.'),
(6145, 99, 7, 'Og den som har gjort et støvfnuggs vekt av godt, skal se det.'),
(6146, 99, 8, 'Og den som har gjort et støvfnuggs vekt av ondt, skal se det.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Ved dem som pruster i galopp,'),
(6148, 100, 2, 'som slår gnister,'),
(6149, 100, 3, 'som stormer på ved morgengry'),
(6150, 100, 4, 'og virvler opp støv under dette,'),
(6151, 100, 5, 'og derved trenger inn i flokkens midte!'),
(6152, 100, 6, 'Mennesket er sannelig utakknemlig mot Herren,'),
(6153, 100, 7, 'og han er selv vitne til det!'),
(6154, 100, 8, 'Han er sterk i sitt begjær etter livets goder.'),
(6155, 100, 9, 'Vet han da ikke når det som er i gravene, tømmes ut,'),
(6156, 100, 10, 'og det som bor i hjertene, bringes frem,'),
(6157, 100, 11, 'på denne dag er Herren vel underrettet om dem?');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'Det knusende slag!'),
(6159, 101, 2, 'Hva er det knusende slag?'),
(6160, 101, 3, 'Hvordan kan du vite hva det knusende slag er?'),
(6161, 101, 4, 'Den dag blir menneskene som flagrende mygg,'),
(6162, 101, 5, 'og fjellene som kardet ull!'),
(6163, 101, 6, 'Den, hvis vektskåler er tunge,'),
(6164, 101, 7, 'vil gå til et godt liv!'),
(6165, 101, 8, 'Men den, hvis vektskåler er lette,'),
(6166, 101, 9, 'avgrunnen har omsorg for ham.'),
(6167, 101, 10, 'Og hvordan kan du vite hva det er?'),
(6168, 101, 11, 'Det er het ild!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Eie-mest mentaliteten opptar dere'),
(6170, 102, 2, 'til dere går til gravene!'),
(6171, 102, 3, 'Nei, sannelig, dere skal få å vite!'),
(6172, 102, 4, 'Så sannelig, dere skal få å vite!'),
(6173, 102, 5, 'Nei, sannelig, om dere bare visste med visshets kunnskap!'),
(6174, 102, 6, 'Dere skal visselig se helvete!'),
(6175, 102, 7, 'Ja, sannelig skal dere se det med sikkert blikk!'),
(6176, 102, 8, 'Og så, på den dag, skal dere visselig få svare for velstanden.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Ved tidens makt!'),
(6178, 103, 2, 'Mennesket kommer visselig til kort!'),
(6179, 103, 3, 'Unntatt er de som tror og lever rettskaffent, som legger hverandre sannheten på hjertet, og standhaftighet.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Ve over hver ivrig baktaler,'),
(6181, 104, 2, 'som samler rikdom, og teller og teller,'),
(6182, 104, 3, 'og tror at hans rikdom har gjort ham udødelig!'),
(6183, 104, 4, 'Nei, så sannelig, han skal bli kastet i al-Hotama.'),
(6184, 104, 5, 'Hvordan kan du vite hva al-Hotama er?'),
(6185, 104, 6, 'Det er Guds opptente Ild,'),
(6186, 104, 7, 'som går oppover hjertene.'),
(6187, 104, 8, 'Den hvelver seg over dem'),
(6188, 104, 9, 'i langstrakte søyler.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Du har vel sett hva Herren gjorde med elefantfolkene?'),
(6190, 105, 2, 'Lot Han ikke deres list slå feil?'),
(6191, 105, 3, 'Han sendte over dem fugleflokker,'),
(6192, 105, 4, 'som kastet på dem steiner av brent leire,'),
(6193, 105, 5, 'og lot dem bli som avspist vegetasjon.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'For Quraysh-stammens samordning,'),
(6195, 106, 2, 'samordning av vinter- og sommerkaravanen,'),
(6196, 106, 3, 'så la dem dyrke Herren over dette Hus (Ka’aba),'),
(6197, 106, 4, 'Han som gir dem mat mot sult, og trygghet mot frykt!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Du har vel sett ham som fornekter dommen?'),
(6199, 107, 2, 'Det er han som støter bort den foreldreløse,'),
(6200, 107, 3, 'og ikke oppfordrer til å gi den fattige mat.'),
(6201, 107, 4, 'Ve dem som holder bønn,'),
(6202, 107, 5, 'men som ikke samler seg om den,'),
(6203, 107, 6, 'som gjør det bare for syns skyld,'),
(6204, 107, 7, 'som nekter å gi en håndsrekning!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Vi har visselig gitt deg i overflod!'),
(6206, 108, 2, 'Så be til Herren, og bring offer!'),
(6207, 108, 3, 'Sannelig, han som har lagt deg for hat, han har intet etter seg.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'Si: «Dere vantro!'),
(6209, 109, 2, 'Ikke kommer jeg til å tilbe det dere tilber,'),
(6210, 109, 3, 'og ikke kommer dere til å tilbe det jeg tilber.'),
(6211, 109, 4, 'Ikke tilber jeg det som dere tilber.'),
(6212, 109, 5, 'Ikke tilber dere det jeg tilber.'),
(6213, 109, 6, 'Dere har deres religion, og jeg har min!»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Når Guds hjelp kommer, og seieren,'),
(6215, 110, 2, 'og du ser menneskene tre inn i Guds religion flokkevi'),
(6216, 110, 3, 'lovpris da Herren, og be Ham om tilgivelse! Han er full av miskunn.');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'I undergangen med Abu Lahabs verk, i undergangen med ham!'),
(6218, 111, 2, 'Hans rikdom og det han har tjent, hjelper ham ikke.'),
(6219, 111, 3, 'Han skal havne i flammende ild,'),
(6220, 111, 4, 'mens hans hustru, som bærer brennefang,'),
(6221, 111, 5, 'har en snor av palmefibre rundt halsen!');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'Si: «Han er Gud, Én.'),
(6223, 112, 2, 'Gud, den Evigvarende.'),
(6224, 112, 3, 'Ikke har Han avlet noen, ei heller er Han selv blitt avlet.'),
(6225, 112, 4, 'Ingen er Hans like.»');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'Si: «Jeg søker tilflukt hos morgengryets Herre,'),
(6227, 113, 2, 'mot ondt fra det Han har skapt,'),
(6228, 113, 3, 'mot ondt fra mørket når det bryter inn,'),
(6229, 113, 4, 'mot ondt fra dem som blåser på knuter og utøver magi,'),
(6230, 113, 5, 'mot ondt fra en misunnelig, når han misunner (kaster det onde øye).');
INSERT INTO `no_berg` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'Jeg søker tilflukt hos menneskenes Herre,'),
(6232, 114, 2, 'menneskenes Konge,'),
(6233, 114, 3, 'menneskenes Gud,'),
(6234, 114, 4, 'mot ondt fra den smygende frister,'),
(6235, 114, 5, 'som hvisker i menneskenes hjerter,'),
(6236, 114, 6, 'av dsjinner eller mennesker.');
