INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'الله ٻاجھاري مھربان جي نالي سان (شروع)'),
(2, 1, 2, 'سڀ ساراھ (خاص) جھانن جي پالڻھار الله کي جڳائي.'),
(3, 1, 3, '(جو) ٻاجھارو مھربان.'),
(4, 1, 4, 'قيامت جي ڏينھن جو مالڪ (آھي).'),
(5, 1, 5, 'تنھنجي ئي عبادت ڪيون ٿا ۽ تو کان ئي مدد گھرون ٿا.'),
(6, 1, 6, 'اسان کي سڌي واٽ ڏيکار.'),
(7, 1, 7, 'جا انھن جي واٽ آھي جن تي فضل ڪيو اٿئي، نه انھن جي (واٽ) جن تي (تنھنجو) ڏمر ٿيل آھي ۽ نه گمراھن جي (واٽ).');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الٓمٓ'),
(9, 2, 2, 'ھيءُ ڪتاب، جنھن ۾ ڪو شڪ ڪونھي، اُنھن خدا ترسن کي سِڌو رستو ڏيکاريندڙ آھي،'),
(10, 2, 3, 'جيڪي اڻ ڏٺي تي ايمان آڻيندا آھن ۽ نماز پڙھندا آھن ۽ کين جيڪي رزق ڏنوسين تنھن مان خرچيندا آھن.'),
(11, 2, 4, '۽ اُھي (اي پيغمبر) جيڪو (قرآن) تو ڏانھن لاٿو ويو ۽ جيڪي توکان اڳ (ٻـين پيغمبرن تي) لاٿو ويو تنھن کي مڃيندا آھن ۽ اُھي آخرت تي يقين رکندا آھن.'),
(12, 2, 5, 'اِھي ئي (انھيءَ) سڌي واٽ تي آھن جا سندن پالڻھار کان (ڏسيل) آھي ۽ اِھي ئي ڇوٽڪاري وارا آھن.'),
(13, 2, 6, 'بيشڪ جن انڪار ڪيو تن تي (اي پيغمبر!) تنھنجو کين ڊيڄارين يا نه ڊيڄارين (سو) ھڪجھڙو آھي اِھي ايمان نه آڻيندا.'),
(14, 2, 7, 'الله سندن دلين تي ۽ سندن ڪنن تي مُھر ھنئي آھي، ۽ سندن اکين تي ڇَوڙُ (چڙھيل) آھي ۽ اُنھن لاءِ وڏو عذاب آھي.'),
(15, 2, 8, '۽ ماڻھن مان ڪي (اھڙا) آھن جي چوندا آھن ته الله ۽ قيامت جي ڏينھن کي مڃيوسين ۽ حقيقت ۾ اُھي مؤمن نه آھن.'),
(16, 2, 9, 'الله ۽ مومنن کي دلبو ڏيندا آھن، ۽ حالانڪ پاڻ کان سواءِ (ٻئي ڪنھن کي) دلبو نه ڏيندا آھن ۽ نه سمجھندا آھن.'),
(17, 2, 10, 'سندن دلين ۾ (منافقيءَ جي) بيماري آھي پوءِ الله سندين بيماري وڌائي، ۽ انھيءَ ڪري جو (ھو) ڪُوڙ ڳالھائيندا آھن انھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(18, 2, 11, '۽ جڏھن کين چئبو آھي ته مُلڪ ۾ فساد نه وجھو، چوندا آھن ته اسين سڌاريندڙ ئي آھيون.'),
(19, 2, 12, 'خبردار! اِھي پاڻ فسادي آھن پر نه سمجھندا آھن.'),
(20, 2, 13, '۽ جڏھن کين چئبو آھي ته جھڙي طرح ٻـين ماڻھن ايمان آندو آھي (تھڙي طرح اوھين به) ايمان آڻيو (تڏھن) چوندا آھن ته جئن بي سمجھن ايمان آندو آھي تئن ايمان آڻيون ڇا؟ خبردار، اُھي ئي بي سمجھ آھن پر نه ڄاڻندا آھن.'),
(21, 2, 14, '۽ جڏھن مؤمنن کي ملندا آھن (تڏھن) چوندا آھن ته اسان ايمان آندو آھي، ۽ جڏھن پنھنجن شيطانن (سردارن) کي ھيڪلو ملندا آھن (تڏھن) چوندا آھن ته اسين (پر ۾) اوھان سان آھيون اسين (مؤمنن سان) رڳو چٿر ڪندڙ آھيون.'),
(22, 2, 15, 'الله ساڻن چٿر ڪندو آھي ۽ انھن کي پنھنجي سرڪشي ۾ ڊيگھ ڏيندو آھي جو عقل جا انڌا آھن.'),
(23, 2, 16, 'اِھي اُھي آھن جن ھدايت جي بدران گمراھي ڳڌي، پوءِ سندن واپار کين فائدو نه ڏنو ۽ نڪي اُھي ھدايت وارا ٿيا.'),
(24, 2, 17, 'سندن مثال انھيءَ جي مثال جھڙو آھي جنھن باھ ٻاري، پوءِ جنھن مھل باھ پنھنجي آس پاس کي روشن ڪيو (تنھن مھل) الله سندن سوجھرو اجھايو ۽ کين اونداھيءَ ۾ ڇڏي ڏنائين جو نه ڏسن.'),
(25, 2, 18, '(اُھي) ٻوڙا، گونگا، انڌا آھن پوءِ اُھي نه موٽندا.'),
(26, 2, 19, 'يا (سندن مثال اُنھي) آسماني برسات (وارن) جھڙو آھي جنھن ۾ اونداھيون ۽ گوڙ ۽ وڄ ھجي، اُھي پنھنجيون آڱريون پنھنجن ڪنن ۾، ڪڙڪن کان موت جي ڀَو ڪري وجھن، ۽ الله ڪافرن کي گھيرو ڪندڙ آھي.'),
(27, 2, 20, 'وڄ سندن اکين (جي سوجھري کي) اُمالڪ کسڻ تي ھُجي، جڏھن انھن لاءِ چمڪي ته منجھس ھلن ٿا، ۽ جڏھن مٿن اوندھ ڪري تڏھن بيھي رھن ٿا، ۽ جيڪڏھن الله گھري ته سندن اکيون ۽ سندن ڪن ضرور وڃائي ڇڏي، بيشڪ الله سڀ ڪنھن شيء تي وس وارو آھي.'),
(28, 2, 21, 'اي ماڻھؤ پنھنجي انھيءَ پالڻھار جي عبادت ڪريو جنھن اوھان کي ۽ جي اوھان کان اڳ هُئا تن کي پيدا ڪيوته مانَ اوھين ڊڄو.'),
(29, 2, 22, 'جنھن اوھان لاءِ زمين کي وڇاڻو بڻايو ۽ آسمان کي ڇت، ۽ (مينھن جو) پاڻي آسمان کان وسايائين پوءِ اُن سان (ھر جنس جي) ميون منجھان اوھان لاءِ روزي پيدا ڪيائين، پوءِ اوھين ڄاڻ ھوندي الله سان ڪنھن کي شريڪ نه ڪريو.'),
(30, 2, 23, '۽ پنھنجي ٻانھي (ﷴ ﷺ) تي جيڪو (قرآن) لاٿوسون تنھن کان جيڪڏھن اوھين شڪ ۾ آھيوته جھڙيس ڪائي سُورة بڻائي آڻيو، ۽ الله کان سواءِ پنھنجا مددگار (به) سڏيو جيڪڏھن سچا آھيو.'),
(31, 2, 24, 'پوءِ جيڪڏھن نه ڪندؤ، ۽ ڪري به ڪڏھن نه سگھندؤ، ته انھيءَ باھ کان ڊڄو جنھن جو ٻل ماڻھو ۽ پھڻ آھن، اُھا نه مڃيندڙن لاءِ تيار ڪئي وئي آھي.'),
(32, 2, 25, '۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي ھيءَ خوشخبري ڏي ته انھن لاءِ بھشت آھن جن جي ھيٺان واھيون پيون وھن، جڏھن اُن مان ڪو به ميوو کاڄ لاءِ ڏنو پيو ويندن (تنھن مھل) پيا چوندا ته ھيءُ (ميوو) اُھو آھي جو اڳ اسان کي ڏنو ويو ھو ۽ اُھو (شڪل ۾) ھڪ جھڙو (پيو) ڏبن، ۽ انھن لاءِ منجھس پاڪ زالون آھن ۽ اُھي منجھس سدائين رھڻ وارا آھن.'),
(33, 2, 26, 'الله مڇر ۽ اُن کان وڌيڪ جي مثال ڏيڻ کان حياء ئي نه ڪندوآھي، پوءِ جن ايمان آندو آھي سي (پڪ) ڄاڻندا آھن ته اُھو سندن پالڻھار وٽان (آيل) سچ آھي، ۽ جن انڪار ڪيو سي چوندا آھن ته ھن مثال ڏيڻ ۾ الله جو ڇا مطلب آھي؟ (الله) ان مثال (ڏيڻ) سببان گھڻن کي ڀلائيندو آھي ۽ ان سببان گھڻن کي سڌو رستو ڏيکاريندو آھي، اُن سان اھڙن بي دينن کانسواءِ (ٻي ڪنھين کي) نه ڀلائيندو آھي.'),
(34, 2, 27, 'جيڪي الله جو انجام ان جي پڪي ڪرڻ کانپوءِ ڀڃندا آھن، ۽ الله جن ڪمن جي ڳنڍڻ جو حڪم ڪيو آھي تن کي ڇنندا آھن ۽ مُلڪ ۾ فساد وجھندا آھن، اُھي ئي ڇيئي وارا آھن.'),
(35, 2, 28, 'اوھين ھن ھوندي به الله کي ڪھڙي طرح نه ٿا مڃيو؟ جو بي ساھا ھُيؤ پوءِ اوھان کي جيئرو ڪيائين، وري اوھان کي ماريندو، وري اوھان کي جياريندو ۽ وري ڏانھس موٽايا ويندؤ.'),
(36, 2, 29, 'اُھو (الله) آھي جنھن توھان (جي فائدي) لاءِ جيڪي مڙيئي (شيون) زمين ۾ آھن سي پيدا ڪيون، وري آسمان ڏانھن متوجه ٿيو پوءِ اُنھن کي ست آسمان ڪري برابر بيھاريائين، ۽ اُھو (الله) سڀڪنھن شيءِ کي ڄاڻندڙ آھي.'),
(37, 2, 30, '۽ (اي پيغمبر) جڏھن تنھنجي پالڻھار ملائڪن کي چيو ته آءٌ زمين ۾ ھڪ نائب مُقرر ڪرڻ وارو آھيان، (تڏھن) چيائون ته ڇا منجھس اھڙي کي ٿو پيدا ڪرين جو منجھس فساد ڪندو ۽ (ناحق) رت ھاريندو، ۽ اسين تنھنجي ساراہ سان (تنھنجي) پاڪائي واکاڻيون ٿا ۽ توکي پاڪ ڪري مڃيون ٿا، فرمايائين ته جيڪي آءٌ ڄاڻندو آھيان سو توھان نٿا ڄاڻو.'),
(38, 2, 31, '۽ (الله) آدم کي سڀني شين جا نالا سيکاريا وري اُھي شيون ملائڪن جي آڏو ڪيائين پوءِ فرمايائين ته جيڪڏھن سچا آھيو ته ھنن جا نالا ڏسيوم.'),
(39, 2, 32, 'چيائون ته تون پاڪ آھين جيڪي اسان کي ڄاڻايو اٿيئي تنھن کانسواءِ اسان کي (ٻي) ڪا خبر نه آھي، بيشڪ تون ئي ڄاڻندڙ حڪمت وارو آھين.'),
(40, 2, 33, '(الله) چيو ته اي آدم انھن جا نالا کين ڏس، پوءِ جڏھن کين انھن جا نالا ڏسيائين (تڏھن الله) چيو ته اوھان کي نه چيو ھوم ڇا ته آءٌ آسمانن ۽ زمين جو ڳجھ ڄاڻندو آھيان ۽ جيڪي پڌرو ڪيو ٿا ۽ جيڪي لڪايو ٿا سو به ڄاڻندو آھيان.'),
(41, 2, 34, '۽ جڏھن ملائڪن کي چيوسون ته آدم کي سجدو ڪريو تڏھن شيطان ڌاران (ٻـين) سجدو ڪيو، ھن انڪار ڪيو ۽ ھٺ ڪيائين ۽ ڪافرن مان ٿيو.'),
(42, 2, 35, '۽ چيوسون ته اي آدم تون ۽ تنھنجي زال بھشت ۾ ٽڪو ۽ جتان وڻيوَ تتان مزي سان کائو، ۽ ھن وڻ کي ويجھا نه وڃجو نه ته ظالمن (جي شمار) مان ٿيندؤ.'),
(43, 2, 36, 'پوءِ شيطان اُتاھون انھن کي ٿيڙيو پوءِ جنھن (مزي) ۾ ھئا تنھن مان کين (ٻاھر) ڪڍيائين، ۽ چيوسون ته (بھشت مان) نڪري ھيٺ ٿيو اوھين ھڪ ٻئي جا ويري آھيو، ۽ اوھان لاءِ ڪنھن وقت تائين زمين ۾ رھڪ جو ھنڌ ۽ (گذران جو) سامان (ٺھرايل) آھي.'),
(44, 2, 37, 'پوءِ آدم پنھنجي پالڻھار وٽان ڪي لفظ سکيا پوءِ (الله) سندس توبه قبول ڪئي، بيشڪ اُھو ئي توبه قبوليندڙ مھربان آھي.'),
(45, 2, 38, 'چيوسين ته منجھانئس سڀ لھي ھيٺ وڃو، پوءِ جڏھن مون وٽان اوھان وٽ ڪا ھدايت اچي تڏھن جيڪي منھنجي ھدايت تي ھلندا تن کي ڪو ڀؤ ڪونھي ۽ نڪي اُھي غمگين رھندا.'),
(46, 2, 39, '۽ جن نه مڃيو ۽ اسانجي آيتن کي ڪُوڙو ڄاتو سي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.'),
(47, 2, 40, 'اي يعقوب جي اولاد اُھو منھنجو ڳڻ ياد ڪريو جيڪو مون توھان تي ڪيو ۽ منھنجو انجام پاڙيو ته آءٌ (به) اوھان جو انجام پاڙيان ۽ رڳو مون کان ڊپ رکو.'),
(48, 2, 41, '۽ (اي بني اسرائيل) جيڪو اوھان وٽ آھي (يعني توريت) تنھن کي سچو ڪندڙ جيڪو (قرآن ﷴ ﷺ تي) لاٿم تنھنکي مڃيو ۽ ان جا پھريان منڪر نه ٿيو، ۽ منھنجي آيتن کي (دنيا جي) ٿوري ملھ سان نه وڪڻو، ۽ رڳو مون کا ڊڄو.'),
(49, 2, 42, '۽ سچ کي ڪوڙ سان نه ملايو ۽ نڪي اوھين ڄاڻ ھوندي به سچ لڪايو.'),
(50, 2, 43, '۽ نماز پڙھو ۽ زڪوٰة ڏيو ۽ رڪوع ڪندڙن سان گڏجي رڪوع ڪيو.'),
(51, 2, 44, 'ماڻھن کي چڱائيءَ جو حڪم ڪريو ٿا ۽ پاڻ کي وساريو ٿا؟ ھن ھوندي به جو توھين ڪتاب پڙھو ٿا، پوءِ ڇو نه ٿا سمجھو؟'),
(52, 2, 45, '۽ صبر ۽ نماز سان مدد وٺو، ۽ اُھا انھن آزي ڪندڙن کانسواءِ ٻـين کي ضرور ڏکي (لڳندي) آھي.'),
(53, 2, 46, 'جيڪي پڪ ڀائيندا آھن ته اُھي پنھنجي پالڻھار کي ملڻ وارا آھن ۽ اُھي ڏانھس موٽڻ وارا آھن.'),
(54, 2, 47, 'اي بني اسرائيلو اُھو منھنجو ڳڻ ياد ڪريو جيڪو مون اوھان تي ڪيو ۽ مون اوھان کي جھان (وارن) تي سڳورو ڪيو.'),
(55, 2, 48, '۽ اُنھيءَ ڏينھن کان ڊڄو جنھن ۾ ڪو (به) ڪنھين کان ڪجھ به ٽاري نه سگھندو ۽ نڪي کانئس ڪا پارت قبول ڪبي ۽ نڪي کانئس ڪو عِوض وٺبو نڪي کين مدد ڏبي.'),
(56, 2, 49, '۽ (ھي ڳڻ به ياد ڪريو) ته جڏھن اوھان کي فرعون جي ماڻھن کان ڇڏايوسون جي اوھان کي ڏاڍو ايذاءُ چکائيندا ھئا، جو اوھان جا پُٽ ڪھندا ھئا ۽ اوھان جون ڌيئرون جيئريون ڇڏيندا ھئا، ۽ اُن ۾ اوھان جي پالڻھار کان اوھان لاءِ وڏي پرک ھُئي.'),
(57, 2, 50, '۽ (ياد ڪريو ته) جڏھن اوھان لاءِ سمنڊ کي چيريوسون پوءِ اوھان کي (ٻڏڻ کان) بچايوسون ۽ فرعون جي ماڻھن کي ٻوڙيوسون ۽ اوھين ڏسندا رھيؤ.'),
(58, 2, 51, '۽ (ياد ڪريو ته) جڏھن موسىٰ کي چاليھن راتين جو انجام ڏنوسون وري ان (جي وڃڻ) کانپوءِ گابي کي (خدا ڪري) ورتؤ ۽ اوھين ظلم ڪندڙ ھيؤ.'),
(59, 2, 52, 'وري اُن کان پوءِ اوھان کان ٽارو ڪيوسون ته مان اوھين شڪرانو ڪريو.'),
(60, 2, 53, '۽ (ياد ڪريو ته) جڏھن موسىٰ کي ڪتاب ۽ (سچ ۽ ڪوڙجي وچ ۾) سنڌو وجھندڙ ڏنوسين ته مان اوھين ھدايت وارا ٿيو.'),
(61, 2, 54, '۽ (ياد ڪريو ته) جڏھن موسىٰ پنھنجي قوم کي چيو ته اي منھنجي قوم اوھان گابي کي خُدا ڪري وٺڻ سببان پاڻ تي ظلم ڪيو آھي تنھنڪري پنھنجي پالڻھار ڏانھن موٽو ۽ پاڻ کي ڪُھو، اھو اوھان لاءِ توھان جي پيدا ڪندڙ وٽ چڱو آھي، پوءِ اوھان تي ٻاجھ ڪيائين، بيشڪ اُھو ئي معافي ڏيندڙ مھربان آھي.'),
(62, 2, 55, '۽ (ياد ڪريو ته) جڏھن اوھان چيو ته اي موسىٰ جيستائين الله چٽو (نه) ڏسنداسون تيستائين توکي ڪڏھن به نه مڃينداسون پوءِ اوھان تي وڄ اچي ڪڙڪي ۽ اوھين ڏسندا رھيؤ.'),
(63, 2, 56, 'وري اوھان جي مرڻ کان پوءِ اوھان کي جيئرو ڪيوسون ته مانَ اوھين شڪرانو ڪريو.'),
(64, 2, 57, '۽ اوھان تي ڪڪر جي ڇانوَ ڪئي سون ۽ اوھان تي مَنّ ۽ سلوىٰ لاٿاسون، ته جيڪي اوھان کي سٺين شين مان رزق ڏنوسين سو کائو، ۽ اسان تي (ڪي) ظلم نه ڪيائون پر پاڻ تي ظلم ڪندا ھئا.'),
(65, 2, 58, '۽ جڏھن چيوسون ته ھِن ڳوٺ ۾ گھِڙو پوءِ منجھانئس جتان وڻيوَ تتان مزي سان کائو ۽ دروازي کان سجدو ڪندڙ ٿي لنگھو ۽ چئو ته اسان جا ڏوھ بخش ته اوھان جون مدايون اوھان کي بخشيون، ۽ چڱائي ڪندڙن کي سگھو وڌائينداسون.'),
(66, 2, 59, 'پوءِ ظالمن جيڪي کين چيو ويو تنھن ڳالھ کي مٽائي ٻيو چيو پوءِ اُنھن ظالمن تي سندن بڇڙي ھجڻ سببان آسمان کان عذاب لاٿوسون.'),
(67, 2, 60, '۽ (ياد ڪر ته) جڏھن موسىٰ پنھنجي قوم لاءِ پاڻي گھريو تڏھن چيوسون ته پنھنجي لٺ پھڻ کي ھڻ، پوءِ منجھانئس ٻارھن واھيون ڦاٽي نڪتيون، سڀڪنھن ماڻھو بيشڪ پنھنجو تڙ سُڃاتو، (چيوسون ته) الله جي رزق مان کائو پيو ۽ زمين ۾ فسادي ٿي بگيڙ نه وجھو.'),
(68, 2, 61, '۽ جڏھن چيوَ ته اي موسىٰ ھڪ طعام تي ڪڏھن به صبر نه ڪنداسون تنھنڪري پنھنجي پالڻھار کان اسان لاءِ (دُعا) گھر ته زمين جيڪو پنھنجو ساڳ ۽ پنھنجا بادرنگ ۽ پنھنجي ڪڻڪ ۽ پنھنجي مُھري ۽ پنھنجا بصر ڄمائيندي آھي سي اسان لاءِ پيدا ڪري، چيائين ته جيڪي چڱيون آھن سي سادين سان ڇو ٿا مٽايو؟ (جي نٿا رھو ته) ڪنھن شھر ۾ لھي وڃو ۽ پوءِ جيڪي گھرو ٿا سو سڀ اوھان لاءِ (اُتي موجود) آھي، خواري ۽ محتاجي مٿن ھنئي وئي ۽ الله جي ڏمر ھيٺ وريا، ھِيءُ ھن ڪري آھي جو اُنھن الله جي حڪمن کي نه ٿي مڃيو ۽ پيغمبرن کي ناحق ٿي ڪُٺائون، اھو ڪم بي فرمانيءَ سببان ٿي ڪيائون ۽ حد کان لنگھندڙ ھوا.'),
(69, 2, 62, 'مؤمنن ۽ يھودين ۽ نصارن ۽ صابين منجھان جن الله ۽ قيامت جي ڏينھن کي مڃيو ۽ چڱا ڪم ڪيا تن لاءِ سندن پالڻھار وٽ سندن اجر آھي، ۽ کين نڪي ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.'),
(70, 2, 63, '۽ جڏھن اوھان کان انجام ورتوسين ۽ اوھان جي مٿان (جبل) طور کي کڙو ڪيوسون، (تڏھن چيوسون ته) جيڪو (توريت) اوھانکي ڏنوسون سوگھو وٺو ۽ جيڪي منجھس آھي سو ياد ڪريو مانَ اوھين ڊڄو.'),
(71, 2, 64, 'وري اُن کانپوءِ اوھين ڦريؤ، پوءِ جڏھن اوھان تي الله جو فضل ۽ سندس ٻاجھ نه ٿئي ھا ته اوھين ضرور ڇيئي وارن مان ٿيو ھا.'),
(72, 2, 65, '۽ انھن کي بيشڪ ڄاتو اٿو جي اوھان مان ڇنڇر بابت حد کان لنگھيا پوءِ کين چيوسين ته خوار ڀولڙا ٿيو.'),
(73, 2, 66, 'پوءِ اُن (قصّه) کي اُن ڳوٺ جي آس پاس وارن ۽ اُنھن جي پوين لاءِ عبرت ۽ خُدا ترسن لاءِ نصيحت ڪئي سون.'),
(74, 2, 67, '۽ جڏھن موسىٰ پنھنجي قوم کي چيو ته الله اوھان کي ڳـئون ڪُھڻ جو حڪم ڪري ٿو، (تڏھن) چيائون ته تون اسان سان چٿر ڪرڻ لڳين ٿو ڇا؟ (موسىٰ) چيو ته آءٌ الله کان جاھلن مان ھجڻ جي پناہ گُھران ٿو.'),
(75, 2, 68, 'چيائون ته اسان لاءِ پنھنجي پالڻھار کان دُعا گھر ته اسان لاءِ چٽائي ڪري ته اُھا (ڳـئون) ڪھڙي (قسم جي) آھي، (موسىٰ) چيو ته الله چوي ٿو ته اُھا ڳـئون نڪي پوڙھي ۽ نڪي جوان آھي، اِنھي وچ ۾ وچٿري آھي، پوءِ جيڪي اوھان کي حڪم ڏنو ويو سو ڪريو.'),
(76, 2, 69, 'چيائون ته پنھنجي پالڻھار کان اسان لاءِ دُعا گُھرته اسان لاءِ چٽائي ڪري ته ان جو رنگ ڪھڙو آھي، (موسىٰ) چيو ته الله چوي ٿو ته اُھا ڳـئون ھيڊي ڪڪڙي آھي سندس رنگ ڳوڙھو ھيڊو آھي ڏسندڙن کي سُرھائي ڏيندي آھي.'),
(77, 2, 70, 'چيائون ته پنھنجي پالڻھار کان اسان لاءِ دُعا گھر ته اسان لاءِ چٽائي ڪري ته اُھا (ڳـئون) ڪھڙي (پارين) آھي ڇوته ڳـئون اسان وٽ (پاڻ جھڙين ۾) گڏيل آھي، ۽ جيڪڏھن الله گھريو ته اسين ضرور لھنداسين.'),
(78, 2, 71, '(موسىٰ) چيو ته الله چوي ٿو ته اُھا اھڙي ڳـئون آھي (جو) نڪي وھو آھي جو ھر کيڙي ۽ نڪي پوک پياري، اڻ وڍ آھي منجھس ڪو دٻڪ ڪونھي، چيائون ته ھاڻي تو پورو پار آندو، پوءِ اُن کي ڪُٺائون ۽ (انھي ڪم) ڪرڻ تي نه ھوا.'),
(79, 2, 72, '۽ جڏھن ھڪ شخص کي ڪُٺوَ تڏھن ان (جي ماريندڙ) بابت تڪرار ڪيوَ، ۽ جيڪي لڪائيندا آھيو سو الله پڌرو ڪندڙ آھي.'),
(80, 2, 73, 'پوءِ چيوسين ته سندس (گوشت جو) ڪُجھ ڀاڱو اُن (مئل) کي ھڻو، اھڙي طرح الله مئلن کي جياريندو آھي ۽ اوھان کي پنھنجيون نشانيون ڏيکاريندو آھي ته مانَ اوھين سمجھو.'),
(81, 2, 74, 'وري ان کانپوءِ اوھان جون دليون ڏاڍيون ٿيون پوءِ پھڻ وانگر بلڪ ڏاڍيون سخت آھن، ۽ پھڻن مان ڪي اھڙا (به) آھن جو منجھائن واھيون ڦاٽي نڪرنديون آھن، ۽ منجھائن ڪو ڦاٽندو آھي پوءِ منجھانئس پاڻي نڪرندو آھي، ۽ منجھائس ڪو الله جي ڀؤ کان ڪرندو آھي، ۽ جيڪي ڪندا آھيو تنھن کان الله بيخبر نه آھي.'),
(82, 2, 75, '(اي مسلمانؤ! اوھين يھودين ۾) پاڻ لاءِ فرمانبردار ھجڻ جو ھِن ھوندي به اڃان آسرو رکو ٿا ڇا؟ جو بيشڪ منجھائن ھڪ ٽولي اِھڙي آھي جو الله جو ڪلام ٻڌندا آھن وري ان جي سمجھڻ کان پوءِ اُھي ڄاڻ ھوندي ان کي مٽائيندا آھن.'),
(83, 2, 76, '۽ جڏھن مؤمنن کي ملندا آھن تڏھن چوندا آھن ته مڃيوسين، ۽ جڏھن ھڪ ٻئي کي ھيڪلو ملندا آھن (تڏھن پاڻ ۾) چوندا آھن ته جيڪي الله اوھان تي کوليو آھي تنھن جي ڳالھ ساڻن ڇو ڪندا آھيو ته اِن سببان اوھان جي پالڻھار وٽ اوھان تي حجت وٺندا رھن، اوھين ڇونه سمجھندا آھيو؟'),
(84, 2, 77, 'نه ڄاڻندا آھن ڇا ته جيڪي لڪائيندا آھن ۽ جيڪي پڌرو ڪندا آھن سو الله ڄاڻندو آھي.'),
(85, 2, 78, '۽ منجھائن ڪي اڻ پڙھيا آھن جي ڪوڙن خيالن کانسواءِ ڪتاب کي نه ڄاڻندا آھن ۽ اُھي رڳو اٽڪل ھلندا آھن.'),
(86, 2, 79, 'پوءِ اُنھن لاءِ خرابي آھي جيڪي پنھنجو ھٿراڌو ڪتاب لکندا آھن، موٽي (ماڻھن کي) چوندا آھن ته ھيءُ الله وٽان (آيو) آھي ھِن لاءِ ته ان سان (دنيا جو) ٿورو ملھ وٺن، پوءِ جيڪي سندن ھٿن لکيو تنھن سببان انھن لاءِ خرابي آھي ۽ جيڪي ڪمائيندا آھن تنھن سببان (پڻ) انھن لاءِ خرابي آھي.'),
(87, 2, 80, '۽ چون ٿا ته باھ ڳڻيلن ڏينھن کان سواءِ اسان کي ڪڏھن نه ڇھندي. اي (پيغمبرکين) چئو ته الله وٽان اھڙو انجام ورتو اٿوَ ڇا؟ ته الله پنھنجي انجام کي ڪڏھن نه ڦيريندو يا الله تي اڻ ڄاتو (ڪوڙ) ڳالھائيندا آھيو.'),
(88, 2, 81, 'ھائو جن مدائي ڪمائي ۽ سندن بڇڙائي کين ويڙھي وئي سي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.'),
(89, 2, 82, '۽ جن ايمان آندو ۽ چڱا ڪم ڪيا سي بھشتي آھن، اُھي منجھس سدائين رھڻ وارا آھن.'),
(90, 2, 83, '۽ جڏھن بني اسرائيلن کان انجام ورتوسين ته الله کانسواءِ ٻئي ڪنھن جي عبادت نه ڪريو، ۽ ماءُ پيءُ ۽ مِٽن ۽ ڇورن (ٻارن) ۽ مسڪينن سان چڱائي ڪريو ۽ ماڻھن سان مِٺو ڳالھايو ۽ نماز پڙھو ۽ زڪوٰة ڏيو، وري اوھان منجھان ٿورن کانسواءِ (سڀ) ڦِري ويؤ ۽ اوھين منھن موڙيندا ھيؤ.'),
(91, 2, 84, '۽ جڏھن اوھان کان انجام ورتوسون ته پنھنجا خون نه ڪندؤ ۽ نڪي پنھنجي قوم کي پنھنجن ديسن مان لڏائيندؤ پوءِ باسيوَ ۽ (ان جا) اوھين گواھ آھيو.'),
(92, 2, 85, '(تنھن کان) پوءِ اوھين اھڙا ٿيا آھيو جو پنھنجي قوم کي ڪُھو ٿا ۽ پنھنجي قوم مان ھڪ ٽوليءَ تي ايذاء ۽ ڏاڍ ڪرڻ لاءِ (پاڻ ۾) ھڪ ٻئي کي مدد ڏئي کين سندن ديس مان لڏايو ٿا، ۽ جيڪڏھن (ڪي) باندي ٿي اوھان وٽ ايندا آھن ته چَٽي ڀري کين ڇڏائيندا آھيو ھن ھوندي جو اُھو سندن لوڌڻ اوھان تي حرام ٿيل آھي، پوءِ ڇاکون ڪي (حڪم) ڪتاب (توريت) جا مڃيندا آھيو ۽ ڪي نه مڃيندا آھيو؟ پوءِ اوھان مان جيڪو اھڙو ڪم ڪري تنھن جي سزا دنيا جي حياتيءَ ۾ خواريءَ کانسواءِ ڪانه آھي، ۽ قيامت جي ڏينھن سخت عذاب ڏانھن ورايا ويندا، ۽ جيڪي ڪندا آھيو تنھن کان الله بي خبر نه آھي.'),
(93, 2, 86, 'اِھي اُھي آھن جن آخرت جي بدران دنيا جي حياتي ڳڌي، پوءِ کانئن عذاب ھلڪو نه ڪيو ويندو ۽ نڪي کين مدد ڏبي.'),
(94, 2, 87, '۽ بيشڪ مُوسىٰ کي ڪتاب (توريت) ڏنوسون ۽ اُن کان پوءِ (ٻيا) پيغمبر ھڪ ٻئي پٺيان موڪلياسون، ۽ عيسىٰ پٽ مريم کي پڌرا مُعجزا ڏناسون ۽ کين پاڪ روح سان مدد ڏني سون، پوءِ جڏھن به ڪنھن پيغمبر اوھان وٽ اُھي حُڪم آندا ٿي جن کي اوھان جي دلين نٿي گھريو تڏھن اوھان (ان جي مڃڻ کان) وڏائي ڪئي، پوءِ (پيغمبرن جي) ھڪ ٽولي کي ڪُوڙو ڄاتوَ، ۽ ٻئي کي ڪُٺَوَ.'),
(95, 2, 88, '۽ چون ٿا ته اسان جون دليون ڍڪيل آھن، (نه) بلڪ الله سندن ڪفر سببان مٿن لعنت ڪئي آھي پوءِ ٿورا ايمان آڻيندا.'),
(96, 2, 89, '۽ جڏھن الله وٽان ڪتاب (قرآن) انھي (توريت) جو سچو ڪندڙ آين جيڪو وٽن آھي ۽ (ھن کان) اڳ ڪافرن تي سوڀ جون (دعائون) گھرندا ھئا، پوءِ جڏھن وٽن آيو جنھن کي سڃاتائون تڏھن ان جو انڪار ڪيائون، پوءِ ڪافرن تي الله جي لعنت آھي.'),
(97, 2, 90, 'اُھو بڇڙو (سودو) آھي جنھن ۾ پاڻ ڏئي جيڪي الله (ﷴ ﷺ تي) لاٿو آھي تنھن جو نه مڃڻ ھن (ڳالھ) جي ساڙ ڪري ڳڌائون ته الله پنھنجي ٻانھن مان جنھن تي گھرندو آھي تنھن تي پنھنجي ٻاجھ سان ڪتاب لاھيندو آھي، پوءِ (الله جي) ڏمر تي ڏمر ھيٺ وريا، ۽ ڪافرن لاءِ خواري ڏيندڙ عذاب آھي.'),
(98, 2, 91, '۽ جڏھن کين چئبو آھي ته جيڪو (قرآن) الله لاٿو تنھن کي مڃيو تڏھن چون ٿا ته جيڪو (توريت) اسان تي لٿل آھي تنھن کي مڃيندا آھيون ۽ جيڪي ان کانسواءِ آھي تنھن کي نه مڃيندا آھن، ۽ (حقيقت ڪري) اھو (قرآن) سچو آھي جيڪو (توريت) وٽن آھي تنھنجو سچو ڪندڙ (به) آھي، (اي پيغمبر) چؤ ته جيڪڏھن (توريت جا) مڃيندڙ آھيو ته الله جي پيغمبرن کي ڇو ڪُٺوَ.'),
(99, 2, 92, 'بيشڪ مُوسىٰ اوھان وٽ چٽا معجزا آندا وري کانئس پوءِ گابي کي (خدا ڪري) ورتوَ ۽ اوھين ظالم آھيو.'),
(100, 2, 93, '۽ جڏھن اوھان کان انجام ورتوسون ۽ اوھان جي مٿان طُور (جبل) کڙو ڪيوسون، (چيوسون ته) جيڪو اوھان کي ڏنوسون (يعني توريت) سو سوگھو وٺو ۽ ٻڌو، تڏھن چيائون ته ٻڌوسون ۽ نه مڃيوسون، ۽ انھن جي دلين ۾ سندن ڪفر سببان گابي جي پريت وڌي وئي، (اي پيغمبر کين) چؤ ته اوھان جو ايمان اوھان کي جنھن شيءِ جو حڪم ڪندو آھي سا بڇڙي آھي جيڪڏھن اوھين (توريت) جا مڃيندڙ آھيو.'),
(101, 2, 94, '(اي پيغمبرکين) چؤ ته جيڪڏھن آخرت جو گَھرُ الله وٽ ٻـين ماڻھن کان سواءِ خلاصو اوھان لاءِ آھي ته جيڪڏھن (پنھنجي چوڻ ۾) سچا آھيو ته موت گھرو.'),
(102, 2, 95, '۽ جيڪي ڪرتوت سندن ھٿن اڳي موڪليا آھن تن سببان اُن (موت) کي ڪڏھن به نه گھرندا، ۽ الله ظالمن کي (چڱي طرح) ڄاڻندڙ آھي.'),
(103, 2, 96, '۽ (اي پيغمبر) انھن (يھودين) کي جيئڻ تي ماڻھن ۽ مشرڪن کان وڌيڪ حرص وارو لھندين، منجھائن ھر ڪو گھرندو آھي ته جيڪر ھزار ورہيه جياريو وڃي ھا، ۽ اھو (ايڏو جيئڻ) کيس عذاب کان پري ڪرڻ وارو نه آھي، ۽ جيڪي ڪندا آھن تنھن کي الله ڏسندڙ آھي.'),
(104, 2, 97, '(اي پيغمبرکين) چئو ته جيڪو جبرائيل جو ويري ٿيندو (سو الله جو ويري آھي) ڇوته جبرائيل قرآن کي الله جي حڪم سان تنھنجي دل تي لاٿو آھي جيڪي کانئس اڳي آھي تنھن کي سچو ڪندڙ آھي ۽ مؤمنن لاءِ ھدايت ۽ خوشخبري آھي.'),
(105, 2, 98, 'جيڪو الله ۽ سندس ملائڪن ۽ سندس پيغمبرن ۽ جبرائيل ۽ ميڪائيل جو ويري بڻبو ته بيشڪ الله (به اھڙن) ڪافرن جو ويري آھي.'),
(106, 2, 99, '۽ (اي پيغمبر) بي شڪ تو ڏانھن صاف آيتون موڪليون سون، ۽ بي دينن کانسواءِ ڪوبه اُن جو منڪر نه ٿيندو.'),
(107, 2, 100, 'جڏھن به ڪو انجام ٻڌائون ٿي ته ان کي ڇو منجھائن ھڪ ٽوليءَ ڀڳو ٿي، بلڪ منجھائن گھڻا ايمان نه آڻيندا آھن.'),
(108, 2, 101, '۽ جڏھن پيغمبر (مُحمّدﷺ) انھي (يعني توريت) جو سچو ڪندڙ جيڪو وٽن آھي الله وٽان آين (تڏھن) جن کي ڪتاب (توريت) ڏنو ويو تن مان ھڪ ٽوليءَ الله جي ڪتاب کي پنھنجن پُٺين پويان اُڇليو ڄڻڪ اُھي ڄاڻندا ئي نه آھن.'),
(109, 2, 102, '۽ اُھي (يھودي توريت جي پيروي ڇڏي) اھڙن (باطل علمن) جي پٺيان لڳا جيڪي سليمان جي واري ۾ شيطان پڙھندا ھوا، ۽ سليمان (جادو ڪرڻ جو) ڪفر نه ڪيو پر شيطانن ڪفر ڪيو جو ماڻھن کي جادو سيکاريندا ھوا، ۽ پڻ (جادوءَ کي اسماء الٰهيه ساڻ بيجا استعمال ڪرڻ جي پٺيان پيا) جيڪي (اسماء الٰهيه جا اسرار) ٻن ملائڪن ھاروت ۽ ماروت تي بابل (شھر) ۾ نازل ٿيا ھئا، ۽ اُھي ٻئي (انھن سڳورن علمن مان) ڪجھ به ايسين ڪنھن ھڪڙي کي نه سيکاريندا ھوا جيسين (ھي) چوندا ھئا ته اسين رڳو پرکه آھيون تنھنڪري (انکي بيجا ڪم ۾ آڻي) ڪافر نه بڻج (جو ناجائز ڪمن لاءِ اسماءِ الٰهي ڪم آڻڻ ڪفر آھي) پوءِ کانئن اُھو سکندا هُئا جنھن سان مڙس ۽ سندس زال جي وچ ۾ ڦيٽو وجھندا هُئا، ۽ اُھي اُن سان ڪنھين ھڪڙي کي الله جي حڪم ڌاران ڏکوئيندڙ نه هُئا، جيڪا کين ڏکوئيندي ۽ فائدو نه ڏيندي سا سکندا هُئا، ۽ بيشڪ ڄاتائون ٿي ته جنھن اُھا ڳڌي تنھن کي آخرت ۾ ڪو نصيب نه آھي، ۽ جنھن سان پاڻ کي وڪيائون سا ضرور بڇڙي آھي، جيڪر ڄاڻن ھا (ته پاڻ پلين ھا).'),
(110, 2, 103, '۽ جيڪڏھن اُھي (يھودي) ايمان آڻين ھا ۽ پرھيزگار بڻجن ھا ته الله وٽان ضرور چڱو بدلو (ملين ھا)، جيڪڏھن ڄاڻن ھا.'),
(111, 2, 104, 'اي ايمان وارؤ (پيغمبر کي عرض ڪرڻ مھل) راعِنا (جو لفظ) نه چئو ۽ اُنظرنا (جو لفظ) چئو ۽ (اھو حڪم دل سان) ٻڌو، ۽ ڪافرن لاءِ ڏکوئيندڙ عذاب آھي.'),
(112, 2, 105, 'نڪي ڪو ڪتاب وارن ڪافرن مان ۽ نڪي ڪو مشرڪن مان اوھان جي پالڻھار کان اوھان تي ڪنھن چڱائيءَ جو لھڻ گھرندو آھي، ۽ الله پنھنجي ٻاجھ سان جنھن کي وڻيس تنھن کي چونڊيندو آھي، ۽ الله وڏي فضل وارو آھي.'),
(113, 2, 106, '(اي پيغمبر) جي ڪا آيت منسوخ ڪندا آھيون يا (تنھنجي دل تان) اُھا وسارائيندا آھيون ته انھي کان چڱي يا جھڙيس آڻيندا آھيون. نه ڄاڻندو آھين ڇا ته الله سڀڪنھن شيء تي وس وارو آھي؟'),
(114, 2, 107, '(اي پيغمبر) نه ڄاڻندو آھين ڇا ته آسمانن ۽ زمين جي بادشاھي الله جي آھي، ۽ الله کانسواءِ اوھان جو نڪو سنڀاليندڙ ۽ نڪو واھرو آھي؟'),
(115, 2, 108, '(اي مسلمانؤ) اوھين پنھنجي پيغمبر کان ايئن سوال ڪرڻ ڇو ٿا گھرو جيئن اڳ (يھودين) موسىٰ کان پڇيو ھو؟ ۽ جيڪو ايمان کي ڪفر سان مٽائيندو سو بيشڪ سِڌيءَ واٽ کان ڀُلو.'),
(116, 2, 109, 'ڪتاب وارن مان گھڻا گھرندا آھن ته جيڪر اوھان جي ايمان آڻڻ کان پوءِ به اوھان کي ساڙ سببان جو سندين دلين ۾ آھي کين جو حق پڌرو ٿي چڪو آھي ان کانپوءِ به موٽائي ڪافر ڪن، پوءِ ايسين ٽري وڃو ۽ مُڙي وڃو جيسين الله پنھنجو حڪم موڪلي، ڇو ته الله سڀ ڪنھن شيء تي وس وارو آھي.'),
(117, 2, 110, '۽ (اي مسلمانؤ) نماز پڙھو ۽ زڪوٰت ڏيو، ۽ جيڪا چڱائي پاڻ لاءِ اڳي موڪليندؤ سا الله وٽ لھندؤ جيڪي اوھان ڪندا آھيو تنھن کي الله ڏسندڙ آھي.'),
(118, 2, 111, '۽ (يھودي ۽ نصارىٰ) چون ٿا ته يھودين ۽ نصارن کانسواءِ بھشت ۾ ڪوئي نه گھڙندو. اِھي سندن سڌون آھن. (اي پيغمبر کين) چئو ته جيڪڏھن سچا آھيو ته پنھنجو ڪو دليل آڻيو.'),
(119, 2, 112, 'ھائو، جنھن پنھنجو مُنھن الله لاءِ نواڙيو ۽ اُھو ڀلارو ھوندو تنھن لاءِ سندس پالڻھار وٽ سندس اجر آھي، ۽ نڪي کين ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.'),
(120, 2, 113, '۽ يھودي چون ٿا ته نصارىٰ ڪنھن دين تي نه آھن، ۽ نصارىٰ چون ٿا ته يھودي ڪنھن دين تي نه آھن ۽ (حقيقت ڪري) اُھي ڪتاب (توريت ۽ انجيل) پڙھندا آھن، اھڙي طرح جيڪي نه ڄاڻندا آھن سي (به) سندن چوڻ جھڙو چوندا آھن. پر الله قيامت جي ڏينھن سندن وچ ۾ جنھن (ڳالھ) بابت جھڳڙو ڪندا آھن تنھن جو نبيرو ڪندو.'),
(121, 2, 114, '۽ ڪير اُن کان وڌيڪ ظالم آھي جنھن الله جي مسجدين کي منجھس سندس نالي جي ياد ڪرڻ کان جھليو ۽ انھن (مسجدين) جي ڦٽائڻ ۾ ڪوشش ڪيائين؟ اھڙن کي انھن (مسجدين) ۾ ڊڄندڙ ٿي گھڙڻ ڌاران (ٻئي طرح گھڙڻ) نه جڳائيندو آھي، انھن لاءِ دُنيا ۾ خواري آھي ۽ انھن لاءِ آخرت ۾ وڏو عذاب آھي.'),
(122, 2, 115, '۽ اوڀر ۽ اولھ الله جو آھي، پوءِ جاڏي پنھنجي مھاڙ کي ڦيرايو تاڏي الله جي سامھانئي آھي، ڇو ته الله (مھرباني ۾) ڪشادگي وارو ڄاڻندڙ آھي.'),
(123, 2, 116, '۽ (ڪافر) چون ٿا ته الله پنھنجي لاءِ اولاد ورتو آھي. (نه) اھو پاڪ آھي، بلڪ جيڪي آسمانن ۽ زمين ۾ آھي سو سندس (مِلڪ) آھي، مڙيئي سندس فرمانبردار آھن.'),
(124, 2, 117, 'آسمانن ۽ زمين جو ٺاھيندڙ آھي، ۽ جڏھن ڪوئي ڪم ڪندو آھي تڏھن رڳو چوندو آھي ته ٿيء ته ٿي پوندو آھي.'),
(125, 2, 118, '۽ جيڪي نه ڄاڻندا آھن سي چوندا آھن ته الله اسان سان ڇو نه ڳالھائيندو آھي يا ڪا نشاني اسان وٽ ڇو نه ايندي آھي؟ اھڙي طرح جيڪي کانئن اڳ هُئا تن سندن چوڻ وانگر چيو، سندن دليون ھڪ جھڙيون آھن، بيشڪ انھيءَ قوم لاءِ نشانيون بيان ڪيون اٿئون جيڪي ويساھ رکندا آھن.'),
(126, 2, 119, '(اي پيغمبر) بيشڪ اسان توکي سچ سان خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو ۽ (تون) دوزخين بابت نه پڇبين.'),
(127, 2, 120, '۽ (اي پيغمبر) يھودي ايسين توکان ڪڏھن راضي نه ٿيندا ۽ نڪي نصارىٰ جيسين تون سندن دين جي تابعداري ڪرين، (کين) چؤ ته الله جي ھدايت اھا (اسلام) جي ھدايت آھي، ۽ (اي پيغمبر) تو وٽ جو علم آيو آھي تنھن کان پوءِ به جيڪڏھن (تون) سندن سَڌن جي تابعداري ڪندين ته تو لاءِ الله وٽان نڪو سنڀاليندڙ ۽ نڪو واھرو ٿيندو.'),
(128, 2, 121, 'جن کي ڪتاب ڏنوسون سي ان کي اھڙي طرح پڙھندا آھن جھڙو سندس پڙھڻ جو حق آھي، اھي ان کي مڃيندا آھن، ۽ جيڪي اُن کي نه مڃيندا سي ئي ڇيئي وارا آھن.'),
(129, 2, 122, 'اي بني اسرائيلؤ اُھي مُنھنجا ڳُڻ ياد ڪريو جيڪي مون اوھان تي ڪيا ۽ مون جھان (وارن) تي اوھان کي سڳورو ڪيو.'),
(130, 2, 123, '۽ انھيءَ ڏينھن کا ڊڄو جنھن ۾ ڪو جيءُ ڪنھن جيءَ کان ڪجھ به نه ٽاري سگھندو ۽ نڪي کانئس ڪو بدلو قبول ڪبو ۽ نڪي کيس ڪا پارت نفعو ڏيندي ۽ نڪي کين مدد ڏبي.'),
(131, 2, 124, '۽ جڏھن ابراھيمؑ کي سندس پالڻھار ڪن ڳالھين سان پرکيو پوءِ اُھي پوريون ڪيائين، ۽ (الله) فرمايو ته آءٌ توکي ماڻھن لاءِ امام مُقرّر ڪرڻ وارو آھيان، (تڏھن ابراھيم) چيو ته منھنجي اولاد مان به (امام ڪندين؟ الله) فرمايو ته ظالمن کي اھو منھنجو انجام نه پھچندو.'),
(132, 2, 125, '۽ جڏھن بيت الله کي ماڻھن جي موٽڻ جو ماڳ ۽ امن جو ھنڌ مقرر ڪيوسون، ۽ (ھن امت کي حُڪم ڪيوسون) ته مقام ابراھيم کي (به) نماز پڙھڻ جي جاءِ ڪري وٺو، ۽ ابراھيم ۽ اسمٰعيل کي وصيّت ڪئي سون ته منھنجي گھر کي طواف ڪندڙن ۽ اعتڪاف وارن ۽ رڪوع (۽) سجدي ڪندڙن لاءِ پاڪ رکو.'),
(133, 2, 126, '۽ جڏھن ابراھيم چيو ته اي منھنجا پالڻھار ھن شھر (مڪي) کي امن جو ھنڌ ڪر ۽ سندس رھندڙن مان انھن کي ميون جي روزي ڏي جيڪي منجھائن الله ۽ قيامت جي ڏينھن کي مڃين، (تڏھن الله) فرمايو ته جنھن نه مڃيو (تنھن کي به) پوءِ اُن کي (حياتيءَ جا) ٿورڙا ڏينھن آسودو ڪندس وري کيس باھ جي عذاب ڏانھن بيوس ڪندس، ۽ اُھا بُڇڙي جاءِ آھي.'),
(134, 2, 127, '۽ جڏھن ابراھيم ۽ اسمٰعيل بيت (الله) جا بنياد اوساريندا هُئا (تڏھن چوندا هُئا ته) اي اسان جا پالڻھار (ھيءُ پورھيو) اسان کان قبول ڪر، ڇوته تون ئي ٻڌندڙ ڄاڻندڙ آھين.'),
(135, 2, 128, '۽ اسان جا پالڻھار اسان کي پنھنجو فرمانبردار ڪر ۽ اسان جي اولاد مان (به) ھڪ ٽوليءَ کي پنھنجو فرمانبردار ڪر، ۽ اسان کي اسان جي حج جا ارڪان سيکار ۽ اسان جون (خطائون) معاف ڪر، ڇو ته تون ئي وڏو معاف ڪندڙ مھربان آھين.'),
(136, 2, 129, '۽ اي اسان جا پالڻھار منجھانئن ھڪ پيغمبر منجھن موڪل جو تنھنجون آيتون مٿن پڙھي ۽ کين ڪتاب ۽ حڪمت سيکاري ۽ کين سڌاري، ڇو ته تون ئي غالب حڪمت وارو آھين.'),
(137, 2, 130, '۽ جنھن پاڻ کي بي سمجھ ڪيو تنھن کانسواءِ (ٻيو) ڪير ابراھيم جي دين کان مُڙندو، ۽ بيشڪ کيس دنيا ۾ چونڊيو سون، ۽ اُھو آخرت ۾ ضرور ڀلارن مان آھي.'),
(138, 2, 131, 'جڏھن کيس سندس پالڻھار چيو ته منھنجا حڪم مڃ (تڏھن) چيائين ته جھان جي پالڻھار جا (سڀ) حڪم مڃيم.'),
(139, 2, 132, '۽ ابراھيم ۽ يعقوب پنھنجي پٽن کي ان ڳالھ جي وصيت ڪئي، ته اي اسانجا اولاد (ھن) دين کي الله اوھان لاءِ چونڊيو آھي پوءِ اوھين مسلمان ھجڻ کانسواءِ نه مرجو.'),
(140, 2, 133, '(اي ڪتاب وارؤ) اوھين تڏھن حاضر ھيؤ ڇا؟ جڏھن يعقوب کي موت ويجھو ٿيو جڏھن پنھنجن پٽن کي چيائين ته مون کان پوءِ ڪنھن جي عبادت ڪندؤ؟ (تڏھن) چيائون ته تنھنجي خُدا ۽ تنھنجي پيءُ ڏاڏي ابراھيم ۽ اسمٰعيل ۽ اسحٰق جي خدا جي جو اڪيلو خدا آھي عبادت ڪنداسون، ۽ اسين سندس حڪم مڃيندڙ آھيون.'),
(141, 2, 134, 'اھا ٽولي بيشڪ گذري وئي، جيڪي انھن ڪمايو سو انھن لاءِ آھي ۽ جيڪي اوھان ڪمايو سو اوھان لاءِ آھي ۽ جيڪي (اُھي) ڪندا تنھن بابت اوھين نه پڇبؤ.'),
(142, 2, 135, '۽ (ڪتاب وارا مسلمانن کي) چون ٿا ته يھودي يا نصارىٰ ٿيو ته ھدايت وارا ٿيندؤ. (اي پيغمبر کين) چؤ ته اسين ابراھيم حنيف (ھڪ طرفي) جي دين جا تابعدار آھيون، ۽ اُھو مشرڪن مان نه ھو.'),
(143, 2, 136, '(کين) چئو ته الله کي مڃيو اٿئون ۽ (پڻ) جيڪو (قرآن) اسان ڏانھن لاٿو ويو ۽ جيڪي (صحيفا) ابراھيم ۽ اسمٰعيل ۽ اسحٰق ۽ يعقوب ۽ يعقوب جي اولاد ڏانھن لاٿا ويا آھن ۽ جيڪي موسىٰ ۽ عيسىٰ کي ڏنو ويو ۽ جيڪي ٻـين پيغمبرن کي سندن پالڻھار کان ڏنو ويو، تنھن (سڀ کي مڃيون ٿا) منجھائن ڪنھن ھڪ جي وچ ۾ وِٿي نه ٿا ڀانيون، ۽ اسين اُن (ھڪ الله) کي مڃيندڙ آھيون.'),
(144, 2, 137, 'پوءِ جيڪڏھن الله کي اوھان جي مڃڻ جھڙو مڃين ته بيشڪ ھدايت وارا ٿيندا، ۽ جيڪڏھن ڦرندا ته اُھي ڦيٽي ۾ ئي پوندا، پوءِ الله توکي انھن (جي شر) کان ڪافي ٿيندو، ۽ اھو ٻڌندڙ ڄاڻندڙ آھي.'),
(145, 2, 138, 'الله (پنھنجي دين جي رنگ سان) اسان کي رنڱيو آھي، ۽ رڱڻ ۾ الله کان وڌيڪ چڱو ڪير آھي؟ ۽ اسين سندس عبادت ڪندڙ آھيون.'),
(146, 2, 139, '(اي پيغمبر کين) چؤ ته الله (جي دين) بابت اسان سان تڪرار ڪريو ٿا ڇا؟ ۽ (حقيقت ڪري) اُھو اسانجو پالڻھار ۽ اوھانجو پالڻھار آھي، ۽ اسان لاءِ اسانجا ڪم ۽ اوھان لاءِ اوھان جا ڪرتوت آھن، ۽ سندس نِلا ٻانھا آھيون.'),
(147, 2, 140, 'يا چئو ٿا ته ابراھيم ۽ اسمٰعيل ۽ اسحٰق ۽ يعقوب ۽ يعقوب جو اولاد يھودي يا نصارىٰ هُئا. (اي پيغمبر کين) چؤ ته اوھين وڌيڪ ڄاڻندڙ آھيو يا الله (وڌيڪ ڄاڻندڙ آھي)؟ ۽ جيڪو اھڙي شاھدي لڪائي جا الله وٽان وٽس ھجي تنھن کان وڌيڪ ظالم ڪير آھي؟ ۽ جيڪي ڪندا آھيو تنھن کان الله بيخبر نه آھي.'),
(148, 2, 141, 'اِھا ٽولي بيشڪ گذري وئي، جيڪي ڪمايائون سو انھن لاءِ آھي ۽ جيڪي اوھان ڪمايو سو اوھان لاءِ آھي، ۽ جيڪي اُھي ڪندا هُئا تنھن بابت (اوھين) نه پڇيا ويندؤ.'),
(149, 2, 142, 'ماڻھن منجھان ڪي بي سمجھ سگھو چوندا ته اُنھن (يعني مسلمانن) کي سندن اُھو قبلو جنھن ڏي (نماز پڙھندا) ھئا تنھن کان ڪھڙيءَ ڳالھ ڦيرايوَ؟ (اي پيغمبر کين) چؤ ته اوڀر ۽ اولھ الله جو آھي، جنھن کي گھرندو آھي تنھن کي سِڌيءَ واٽ ڏانھن دڳ ڏيکاريندو آھي.'),
(150, 2, 143, '۽ (جھڙي طرح اوھان جو قبلو ڀلو ڪيوسين) اھڙيءَ طرح اوھان کي ڀلي ٽولي ڪئي سون ته ماڻھن تي شاھد ھجو ۽ پيغمبر (ﷴ ﷺ) اوھان تي شاھد ھجي، ۽ (اي پيغمبر) جنھن (طرف) تي (اڳي) ھئين تنھن کي قبلو مقرر نه ڪيو ھو سون مگر ھن لاءِ ته جيڪو پيغمبر جي پٺيان لڳي تنھن کي اُنھيءَ کان نکيڙيون جيڪو پنھنجين کڙين (ڀر پوءِ) تي ڦري، ۽ بيشڪ (اِھو) جن کي الله ھدايت ڪئي تن کان سواءِ (ٻـين) تي ضرور ڏکيو آھي، ۽ الله جو شان اھڙو نه آھي جو اوھان جي ايمان کي وڃائي، ڇو ته الله ماڻھن سان ضرور شفقت ڪندڙ مھربان آھي.'),
(151, 2, 144, '(اي پيغمبر) آسمان ڏانھن تنھنجو منھن (ھرھر) ڦيرائڻ بيشڪ ڏسون ٿا، پوءِ جنھن قبلي کي پسند ڪرين ٿو (اوڏانھن) توکي ضرور ڦيرائينداسين، پوءِ (اي پيغمبر) پنھنجي مُنھن کي تعظيم واريءَ مسجد ڏانھن ڦيراءِ، ۽ (اي مسلمانؤ) اوھين جتي ھجو تتي پنھنجا منھن اوڏانھن ڦيرايو، ۽ جن کي ڪتاب ڏنو ويو آھي سي ضرور ڄاڻن ٿا ته اِھو (ڦيرائڻ) سندن پالڻھار وٽان بلڪل سچ آھي، ۽ جيڪي ڪندا آھن تنھن کان الله بي خبر نه آھي.'),
(152, 2, 145, '۽ (قسم آھي) ته جن کي ڪتاب ڏنو ويو آھي تن وٽ جيڪڏھن (تون) سڀ دليل آڻين ته (به) تنھنجي قبلي جي تابعداري نه ڪندا، ۽ نڪي تون سندن قبلي جي تابعداري ڪندڙ آھين، ۽ نڪي اُنھن مان ڪي ڪن جي قبلي جي تابعداري ڪندڙ آھن، ۽ (قسم آھي) ته ان کانپوءِ جو تو وٽ علم آيو جيڪڏھن سندن سَڌن جي تابعداري ڪندين ته تون اُن مھل ظالمن مان آھين.'),
(153, 2, 146, 'جن کي ڪتاب (توريت) ڏنوسون سي اُن (قبلي جي ڦير) کي اِئين سڃاڻندا آھن جئن پنھنجن پٽن کي سڃاڻندا آھن، ۽ منجھانئن ڪا ٽولي ڄاڻ ھوندي بيشڪ سچ کي لڪائيندي آھي.'),
(154, 2, 147, 'اِھو تنھنجي پالڻھار وٽان سچ آھي تنھنڪري تون شڪ وارن مان بنھ نه ٿيءُ.'),
(155, 2, 148, '۽ سڀ ڪنھن لاءِ (ڪو) ھڪڙو قبلو آھي جنھن ڏانھن اُھو مھاڙ ڪندو آھي پوءِ چڱاين ڏانھن اڳرائي ڪريو، جتي ھوندؤ (اُتان) الله اُوھان کي گڏ ڪري آڻيندو، ڇوته الله سڀ ڪنھن شيء تي وس وارو آھي.'),
(156, 2, 149, '۽ (اي پيغمبر) جتان (به) نڪرين ته پنھنجو منھن تعظيم واريءَ مسجد ڏانھن ڦيراءِ، ۽ بيشڪ اِھو (ڦيرائڻ) تنھنجي پالڻھار وٽان بلڪل سچ آھي، ۽ جيڪي ڪندا آھيو تنھن کان الله بي خبر نه آھي.'),
(157, 2, 150, '۽ جتان (به) نڪرين ته پنھنجو منھن تعظيم واريءَ مسجد ڏانھن ڦيراءِ، ۽ جتي ھجو (تتي) پنھنجا منھن ان ڏانھن ڦيرايو (ھن لاءِ) ته منجھانئن جن ظلم ڪيو تن کان سواءِ ٻـين ماڻھن کي اوھان تي ڪا حجت نه رھي، پوءِ کانئن نه ڊڄو، ۽ (ھن لاءِ) ته (آءٌ) پنھنجي نعمت اوھان تي پوري ڪريان ۽ مانَ اوھين ھدايت وارا ٿيو.'),
(158, 2, 151, 'جھڙيءَ طرح اوھان تي نعمت پوري ڪئي سون (تھڙيءَ طرح) اوھان منجھان پيغمبر اوھان ۾ موڪليوسون جو اسان جون آيتون اوھان تي پڙھندڙ آھي ۽ اوھان کي پاڪ ڪندو آھي ۽ اوھان کي ڪتاب ۽ حڪمت سيکاريندو آھي ۽ جيڪي نه ڄاڻندا ھيؤ سو (پڻ) اوھان کي سيکاريندو آھي.'),
(159, 2, 152, 'تنھنڪري مون کي ياد ڪريو ته آءٌ اوھان کي ياد ڪريان ۽ منھن جو شڪر ڪريو ۽ منھن جي بي شڪري نه ڪريو.'),
(160, 2, 153, 'اي مؤمنؤ صبر ۽ نماز سان مدد وٺو، ڇوته الله صابرن سان آھي.'),
(161, 2, 154, '۽ جيڪي الله جي واٽ ۾ ڪُسن تن کي مئل نه چئو، بلڪ (اُھي) جيئرا آھن پر اوھين نه ٿا پروڙيو.'),
(162, 2, 155, '۽ اوھان کي ڀوَ ۽ بک ۽ مالن جي ۽ جندن جي ۽ ميون جي گھٽتائيءَ مان ڪنھن شيء سان ضرور پرکينداسون، ۽ (اي پيغمبر) اُنھن صابرن کي خُوشخبري ڏي.'),
(163, 2, 156, 'جن کي جڏھن ڪا اوکائي پھچي ٿي (تڏھن) چون ٿا ته اسين الله جا (ٻانھا) آھيون ۽ اسين ان ڏانھن موٽندڙ آھيون.'),
(164, 2, 157, 'اُنھن تي سندن پالڻھار وٽان مھربانيون ۽ ٻاجھ آھي، ۽ اِھي ئي سڌيءَ واٽ وارا آھن.'),
(165, 2, 158, 'صفا ۽ مروہ (جبل) الله (جي عبادت) جي نشانين مان آھن، پوءِ جيڪو بيت الله جو حج ڪري يا عمرو بجاءِ آڻي تنھن کي ٻنھي جي طواف ڪرڻ ۾ گناھ نه آھي، ۽ جيڪو وڌيڪ چڱائي ڪندو (تنھن جي الله قبول ڪندو) ڇو ته الله قبول ڪندڙ ڄاڻندڙ آھي.'),
(166, 2, 159, 'جيڪي پڌريون ڳالھيون ۽ ھدايتون لاٿيون اٿئون تن کي جيڪي (ماڻھو) اُن کانپوءِ جي اُنھن کي ماڻھن لاءِ ڪتاب (توريت) ۾ ظاھر ڪيوسون، لڪائيندا آھن تن تي الله لعنت ڪندو آھي ۽ لعنت ڪندڙ (به) انھن تي لعنت ڪندا آھن.'),
(167, 2, 160, 'پر جن توبه ڪئي ۽ (پاڻ) سڌاريائون ۽ بيان ڪيائون تن تي (ٻاجھ سان) موٽندس، ۽ آءٌ ڏاڍو ٻاجھ سان موٽندڙ مھربان آھيان.'),
(168, 2, 161, 'جن ڪفر ڪيو ۽ اُھي ڪافر ٿي مُئا تن تي الله ۽ ملائڪن ۽ مڙني ماڻھن جي لعنت آھي.'),
(169, 2, 162, 'اُن لعنت ۾ سدائين رھڻ وارا آھن، کانئن عذاب ھلڪو نه ڪبو ۽ نڪي کين مھلت ڏبي.'),
(170, 2, 163, '۽ اوھان جو خدا اڪيلو خدا آھي، ان کانسواءِ ڪوئي عبادت جو لائق نه آھي، (اُھو) ٻاجھارو مھربان آھي.'),
(171, 2, 164, 'آسمانن ۽ زمين جي بڻائڻ (۾)، ۽ رات ۽ ڏينھن جي ڦيرگھير (۾)، ۽ ٻيڙيون جي اُنھيءَ (سامان) سان جو ماڻھن کي نفعو ڏيندو آھي درياءَ ۾ ترنديون آھن تن (۾)، ۽ جيڪي آسمان کان (مينھن جو) پاڻي الله وسايو پوءِ اُن سان زمين کي ان جي وسامڻ کانپوءِ آباد ڪيائين تنھن (۾)، ۽ اُن (زمين) ۾ سڀ جنس جا جانور پکيڙيائين تنھن (۾)، ۽ ھوائن جي گھلڻ (۾)، ۽ آسمانن ۽ زمين جي وچ ۾ نوايلن ڪڪرن ۾، سمجھ واريءَ قوم لاءِ ضرور نشانيون آھن.'),
(172, 2, 165, '۽ ماڻھن مان ڪي (اھڙا) آھن جي الله کانسواءِ (ٻـين کي) الله جھڙو ڪري وٺندا آھن الله جي پيار وانگر پيارو رکندا اٿن، ۽ جن ايمان آندو سي الله جي دوستيءَ ۾ ڏاڍا زور آھن، ۽ جن ظلم ڪيو سي جيڪڏھن (ھن ڳالھ کي ھاڻي) سمجھن ته جنھن مھل (قيامت جي ڏينھن) عذاب ڏسندا (۽ سمجھندا) ته سموري سگھ الله کي آھي ۽ الله سخت عذاب ڪرڻ وارو آھي (ته جيڪر ڏاڍا پشيمان ٿين).'),
(173, 2, 166, 'جنھن مھل اڳواڻ پٺيان لڳندڙن کان بيزار ٿيندا ۽ عذاب کي ڏسندا ۽ سندن (سڀ) لاڳاپا ڇڄندا،'),
(174, 2, 167, '۽ (تنھن مھل) پٺيءَ لڳندڙ چوندا ته جيڪر اسان کي (وري دنيا ۾) موٽڻو ھجي ھا ته کانئن ائين بيزار ٿيون ھا جئن اسان کان بيزار ٿيا آھن، اھڙي طرح الله سندن ڪرتوتن کي مٿن پشيمانيون بڻائي ڏيکاريندو، ۽ اُھي باھ مان نڪرڻ وارا نه آھن.'),
(175, 2, 168, 'اي ماڻھؤ جيڪي شيون زمين ۾ آھن تن مان پاڪ حلال کائو، ۽ شيطان جي وکن تي نه ھلو، ڇوته اُھو اوھان جو پڌرو ويري آھي.'),
(176, 2, 169, '(شيطان) رڳو اوھان کي بڇڙائيءَ ۽ بي حيائيءَ جو حڪم ڪري ٿو ۽ ھيءُ ته جيڪي ڳالھيون نه ڄاڻندا آھيو سي الله جي نسبت ۾ چئو.'),
(177, 2, 170, '۽ جڏھن کين چئجي ٿو ته جيڪي الله لاٿو تنھن جي تابعداري ڪريو (تڏھن) چون ٿا ته (نه) بلڪ جنھن تي پنھنجن پئن ڏاڏن کي ڏٺوسون تنھن جي پٺيان لڳنداسون، جيتوڻيڪ سندن ابا ڏاڏا ڪجھ به نه ڄاڻندا هُئا ۽ نڪي سڌيءَ واٽ تي هُئا.'),
(178, 2, 171, '۽ ڪافرن (کي سڏيندڙ) جو مثال اُن ماڻھوءَ جي مثال وانگر آھي جو اھڙِيءَ شي کي واڪو ڪري ٻڌائي جا سڏ ۽ ڪوڪ کانسواءِ (ڪجھ) نه ٻڌي. (ڪافر) ٻوڙا، گونگا، انڌا آھن تنھنڪري اُھي (ڪجھ به) نه ٿا سمجھن.'),
(179, 2, 172, 'اي مؤمنؤ جيڪا اوھان کي روزي ڏني سون تنھن مان سٺيون شيون کائو ۽ جيڪڏھن اوھين نلو الله جي عبادت ڪندڙ آھيو ته سندس شڪرانو ادا ڪريو.'),
(180, 2, 173, 'اوھان تي رڳو مُئل ۽ رَت ۽ سُوئر جو ماس ۽ جنھن شي تي الله کانسواءِ ٻئي جو نالو پڪارجي سا حرام ڪيائين، پوءِ جيڪو نڪي بي فرماني ڪندڙ ۽ نڪي حد کان لنگھندڙ ٿي لاچار ٿئي (۽ کائي) تنھن تي گناہ نه آھي، ڇوته الله بخشڻھار مھربان آھي.'),
(181, 2, 174, 'الله ڪتاب مان جيڪي لاٿو سو جيڪي ماڻھو لڪائيندا آھن ۽ اُن جي بدلي ٿورو ملھ وٺندا آھن اِھي پنھنجن پيٽن ۾ باھ کانسواءِ نه کائيندا آھن ۽ الله ساڻن قيامت جي ڏينھن نه ڳالھائيندو ۽ نڪي کين سنواريندو، ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(182, 2, 175, 'اِھي (اُھي) آھن جن ھدايت جي بدران گمراھي ۽ بخشش جي بدران عذاب ڳڌو، پوءِ عجب آھي ته کين (دوزخ جي) باہ تي ڪھڙيءَ (ڳالھ) صبر ڪرايو.'),
(183, 2, 176, 'اِھو ھِن ڪري (آھي) جو الله سچ سان ڪتاب (توريت) لاٿو، ۽ جن ڪتاب بابت تڪرار ڪيو سي ضرور وڏي ڦيٽي ۾ آھن.'),
(184, 2, 177, 'اوڀر ۽ اولھ ڏانھن اوھان جو پنھنجن مُنھَن کي ڦيرائڻ ڪا چڱائي نه آھي پر (اصل) چڱائي (ان لاءِ) آھي جنھن الله ۽ قيامت جي ڏينھن ۽ ملائڪن ۽ ڪتابن ۽ پيغمبرن کي مڃيو، ۽ جنھن (ماڻھوءَ پنھنجو) مال اُن جي پيار ھوندي (به) مٽيءَ وارن ۽ ڇورن ۽ مسڪينن ۽ مسافرن ۽ سوال ڪندڙن کي ۽ ٻانھَن جي گردنين (آجي ڪرڻ) ۾ ڏنو، ۽ نماز پڙھيائين ۽ زڪوٰة ڏنائين، ۽ (اُھي ماڻھو جي) جڏھن انجام ڪندا آھن تڏھن پنھنجو انجام پاڙيندا آھن، ۽ سڃائيءَ ۽ اوکائيءَ ۾ ۽ جنگ مھل صبر ڪرڻ وارا آھن، اِھي سچا آھن، ۽ اِھي ئي پرھيزگار آھن.'),
(185, 2, 178, 'اي مؤمنؤ خون ٿيلن بابت اوھان تي بدلي وٺڻ ۾ برابري لازم ڪئي ويئي، (جئن ته) آجي جي عيوض آجو ٻانھي جي عيوض ٻانھو ۽ زال جي عيوض زال آھي، پوءِ جنھن کي سندس ڀاءُ جي طرفان ڪجھ ڇڏيو وڃي ته پوءِ (خون بھا وٺڻ ۾) چڱيءَ ريت سان (ان جي) پويان پوڻ گھرجي ۽ ان (وارث) ڏانھن (خون جي بھا) چڱيءَ ريت سان پھچائڻ گھرجي، اِھو (حڪم) اوھان جي پالڻھار وٽان ھلڪائي ۽ ٻاجھ آھي، پوءِ جيڪو ھن (حڪم) کانپوءِ حد کان لنگھندو تنھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(186, 2, 179, 'اي ڌيان وارؤ اوھان لاءِ قصاص (وٺڻ) ۾ حياتي آھي ته مانَ اوھين پرھيزگاري ڪريو.'),
(187, 2, 180, 'جڏھن اوھان مان (ڪنھن) ھڪ کي موت ويجھو ٿئي (اُھو) جيڪڏھن (ڪُجھ) مال ڇڏي ته تڏھن اوھان تي ماءُ پيءُ ۽ مِٽن لاءِ چڱائيءَ سان وصيّت ڪرڻ لازم ڪئي ويئي، اِھو پرھيزگارن تي لازم آھي.'),
(188, 2, 181, 'پوءِ جيڪو اُن (وصيّت) کي اُن جي ٻڌڻ کانپوءِ ڦيرائيندو ته اُن (ڦيرائڻ) جو گُناہ اُن جي ڦيرائيندڙن کانسواءِ ٻـين تي نه آھي، ڇوته الله ٻڌندڙ ڄاڻندڙ آھي.'),
(189, 2, 182, 'پوءِ جيڪو وصيّت ڪندڙ جي طرفداري يا گناہ ڄاڻي سو انھن (وصيّت ڪيل ۽ وارثن) جي وچ ۾ پرچاءُ ڪري ته مٿس گناہ نه آھي، ڇوته الله بخشڻھار مھربان آھي.'),
(190, 2, 183, 'اي مؤمنؤ جئن اوھان کان اڳين تي (روزا) فرض ڪيا ويا ھئا تئن اوھان تي (به) روزا فرض ڪيا ويا آھن ته مانَ اوھين پرھيزگاري ڪريو.'),
(191, 2, 184, '(روزا) ڳڻيل ڏينھن آھن، پوءِ اوھان مان جيڪو بيمار (ھجي) يا مسافريءَ تي ھجي (تنھن تي) ٻـين ڏينھن مان ڳڻي رکڻ (لازم) آھي، ۽ جن کي (مسڪين جي کاڌي ڏيڻ جي) سگھ آھي تن تي ھڪ مسڪين جو کاڌو فديه (يعني فطرانو ڏيڻ) لازم آھي، پوءِ جيڪو چڱائي وڌيڪ ڪندو تنھن لاءِ اُھا ڀلي آھي، ۽ جيڪڏھن عِلم وارا آھيو (ته اوھين سمجھي چڪا ھوندؤ) اوھان جو روزو رکڻ اوھان لاءِ ڀلو آھي.'),
(192, 2, 185, 'رمضان جو مھينو اھڙو (ڀلو) آھي جنھن ۾ قرآن لاٿو ويو جو ماڻھن لاءِ سڌو رستو ڏيکاريندڙ آھي ۽ (اُن ۾) ھدايت ڪرڻ ۽ (سچ ۽ ڪوڙ جي وچ ۾) سَنڌي وجھڻ جون چٽيون ڳالھيون آھن، پوءِ اوھان مان جيڪو اُنھيءَ مھيني ۾ (گھر ۾) حاضر ھجي تنھن کي اُن ۾ روزو رکڻ گهرجي، ۽ جيڪو بيمار ھُجي يا سفر تي (ھُجي) تنھن کي ٻـين ڏينھن مان (اوترو) ڳاڻاٽو (لازم) آھي. الله اوھان تي آساني ڪرڻ گھرندو آھي ۽ اوھان تي (ڪا) اوکائي ڪرڻ نه گھرندو آھي، ۽ (گھرندو آھي) ته اوھين ڳَڻ پوري ڪريو ۽ جيڪا اوھان کي ھدايت ڪيائين تنھن تي الله کي وڏائيندا رھو ۽ مانَ اوھين شُڪرانو ڪريو.'),
(193, 2, 186, '۽ (اي پيغمبر) جڏھن مُنھنجا ٻانھا توکان مُنھنجي پُڇا ڪن (تڏھن چؤ) ته آءٌ ويجھو آھيان، جڏھن (ڪو) مُون کي سڏي ٿو تڏھن سڏيندڙ جو سڏ ورنايان ٿو تنھنڪري مُنھنجو حُڪم قبول ڪرڻ گھرجين ۽ مون تي ايمان آڻڻ گھرجين ته مانَ اُھي سُڌرن.'),
(194, 2, 187, 'روزي جي رات ۾ اوھان لاءِ پنھنجين زالن سان صُحبت ڪرڻ حلال ڪئي ويئي، (جو) اُھي اوھان جو ڍڪ آھن ۽ اوھين اُنھن جو ڍڪ آھيو. الله ڄاتو ته اوھين پاڻ سان خيانت ڪندا ھيؤ پوءِ اوھان تي (ٻاجھ سان) موٽيو ۽ اوھان کي معاف ڪيائين، پوءِ ھاڻي ساڻن صُحبت ڪريو ۽ الله اوھان لاءِ جيڪي (اولاد) لکيو آھي تنھن جي طلب ڪريو ۽ (ايستائين) کائو ۽ پيئو جيستائين ڪاري ڌاڳي کان باکه جو اڇو ڌاڳو اوھان لاءِ پڌرو ٿئي، پوءِ روزو رات توڻي پورو ڪريو، ۽ جنھن حالت ۾ اوھين مسجدين ۾ اعتڪاف وارا ھجو ته اُنھن (زالن) سان صُحبت نه ڪريو. اِھي الله جون حدون آھن پوءِ اُنھن کي ويجھا نه وڃو، اھڙيءَ طرح الله ماڻھن لاءِ پنھنجا حُڪم پڌرا ڪندو آھي ته مانَ اُھي پرھيزگاري ڪن.'),
(195, 2, 188, '۽ اوھين پنھنجا مال پاڻ ۾ ناحق سان نه کائو ۽ اُن (مال) کي حاڪمن ڏانھن ھن لاءِ (وڍيءَ طور) نه پھچايو ته ماڻھن جي مال مان ڪجھ (ڀاڱو) ڏوھ سان کائو ھن ھوندي به جو اوھين ڄاڻندا آھيو.'),
(196, 2, 189, '(اي پيغمبر) توکان چنڊن (يعني مھينن) بابت پڇن ٿا، چؤ ته اِھي ماڻھن ۽ حج لاءِ وقت مُقرر ٿيل آھن، ۽ چڱائي (ھن ۾) نه آھي ته (احرام جي حالت ۾) گھرن ۾ سندن پٺين پاسن کان اچو پر (اصل) چڱائي (اُنھيءَ لاءِ) آھي جيڪو (الله کان) ڊڄي، ۽ گھرن ۾ سندن درن کان اچو، ۽ الله کان ڊڄو ته مانَ اوھين ڇٽو.'),
(197, 2, 190, 'جيڪي اوھان سان وڙھن تن سان الله جي واٽ ۾ وڙھو ۽ حد کان نه لنگھو، ڇوته الله حد کان لنگھندڙن کي پيارو نه رکندو آھي.'),
(198, 2, 191, '۽ جتي لَھونِ اتي ڪُھونِ ۽ جتان اوھان کي لوڌيائون اُتان لوڌيونِ ۽ فتنو (يعني شرڪ) ويڙھ کان ڏاڍو (بڇڙو) آھي، ۽ اوھين تعظيم واريءَ مسجد وٽ (ايسين) نه وڙھونِ جيسين منجھس اوھان سان (نه) وڙھن. پوءِ جيڪڏھن اوھان سان وڙھن ته اُنھن کي ماريو، ڪافرن جي سزا اِئين آھي.'),
(199, 2, 192, '۽ جيڪڏھن بس ڪن (ته اوھين به بس ڪريو ڇو) ته الله بخشڻھار مھربا ن آھي.'),
(200, 2, 193, '۽ ايستائين اُنھن سان وڙھو جيستائين فتنو (يعني شرڪ) نه رھي ۽ خاص الله جو دين ٿئي، پوءِ جيڪڏھن بس ڪن ته ظالمن کانسواءِ ڪنھين تي وڌيڪ ھلت ڪرڻ نه جڳائي.'),
(201, 2, 194, 'تعظيم وارو مھينو تعظيم واري مھيني جي بدلي آھي ۽ تعظيمون (ھڪ ٻئي جون) آڙ پاڙ آھن، پوءِ جيڪو اوھان تي وڌيڪ ھلت ڪري تنھن تي اوتري وڌيڪ ھلت ڪريو جيتري اوھان تي وڌيڪ ھلت ڪيائين، ۽ الله کان ڊڄو ۽ ڄاڻو ته الله پرھيزگارن ساڻ آھي.'),
(202, 2, 195, '۽ الله جي واٽ ۾ خرچيو ۽ (پاڻ کي) پنھنجي ھٿن سان ھلاڪت ۾ نه وجھو، ۽ چڱائي ڪريو، ڇوته الله چڱائي ڪندڙن کي دوست رکندو آھي.'),
(203, 2, 196, '۽ حج ۽ عُمرو الله (جي راضپي) لاءِ پورو ڪريو، پوءِ جيڪڏھن روڪيا وڃو ته جا قرباني آسانيءَ سان لڀي (سا لازم آھي)، ۽ پنھنجا مٿا تيسين نه ڪوڙايو جيسين قرباني پنھنجيءَ جاءِ تي (نه) پھچي. پوءِ اوھان مان جيڪو بيمار ھجي يا سندس مٿي ۾ اھنج ھجي (ته جي وقت کان اڳيئي ئي ڪوڙايائين) ته روزا يا خيرات يا قرباني عوض ڏيڻ (لازم) آھي، پوءِ جڏھن بي ڀَوَا ٿيو تڏھن جيڪو فائدو وٺي (يعني) عمري کي حج سان (گڏي) سو جا قرباني آسانيءَ سان لڀي (سا ڪري). پوءِ جيڪو (قرباني) نه لھي (تنھن تي) حج (جي ڏينھن) ۾ ٽي ڏينھن روزا لازم آھن ۽ ست تڏھن جڏھن (سفرکان) موٽو، اِھي پورا ڏھ (روزا) آھن، اِھو (حُڪم) اُنھيءَ لاءِ آھي جنھنجو ڪٽنب تعظيم واريءَ مسجد جي ويجھو رھندڙ نه ھجي، ۽ الله کان ڊڄو ۽ ڄاڻو ته الله ڏاڍو عذاب ڪندڙ آھي.'),
(204, 2, 197, 'حج (جو وقت) معلوم مھينا آھن، پوءِ جنھن منجھن (احرام ٻڌڻ سان پاڻتي) حج فرض ڪيو ته حج ۾ نه جماع جائز آھي ۽ نه گار ۽ نه جھيڙو، ۽ جيڪا چڱائي ڪريو ٿا تنھن کي الله ڄاڻندڙ آھي، ۽ سَمر کڻو ڇو ته ڀلو سَمر پرھيزگاري آھي، ۽ ڌيان وارؤ مون کان ڊڄو.'),
(205, 2, 198, '(حج ۾) پنھنجي پالڻھار کان روزيءَ گھرڻ ۾ اوھان تي ڪو گناھ ڪونھي، پوءِ جڏھن عَرفات کان موٽو تڏھن مَشۡعَرالۡحَرام (يعني مزدلفہ) وٽ الله کي ياد ڪريو، ۽ جئن اوھان کي سڌو رستو ڏيکاريائين تئن اوھين (به) کيس ياد ڪريو، ۽ بيشڪ اُن کان اڳ (اوھين) اڻڄاڻن مان ھيؤ.'),
(206, 2, 199, 'پوءِ (اوھين) اُتان (عرفات کان) موٽو جتان عام ماڻھو موٽن ٿا ۽ الله کان بخشش گھرو، ڇوته الله بخشڻھار مھربان آھي.'),
(207, 2, 200, 'پوءِ جڏھن پنھنجي حج جا اَرڪان پُورا ڪريو تڏھن (مِنىٰ ۾) اوھين پنھنجن اَبن ڏاڏن جي ياد ڪرڻ وانگر بلڪ (اُن کان وڌيڪ) گھڻي قدر الله کي ياد ڪريو. پوءِ ماڻھن مان ڪي (اھڙا) آھن جي چوندا آھن ته اسان جا پالڻھار اسان کي دنيا ۾ (ئي چڱائي) ڏي تن کي آخرت ۾ (ڪا) چڱائي نصيب نه آھي.'),
(208, 2, 201, '۽ منجھائن ڪي (اھڙا) آھن جي چوندا آھن ته اي اسان جا پالڻھار اسان کي دنيا ۾ (به) چڱائي ۽ آخرت ۾ (به) چڱائي ڏي ۽ اسان کي باھ جي عذاب کان بچاءِ.'),
(209, 2, 202, 'اُھي ماڻھو جيڪي ڪمايائون تنھن مان کين نصيب آھي، ۽ الله سگھو حساب ڪندڙ آھي.'),
(210, 2, 203, '۽ ڳڻيلن ڏينھن (مِنىٰ وارن) ۾ الله کي ياد ڪريو، پوءِ جيڪو ٻن ڏينھن ۾ تڪڙو نڪري تنھن تي گناھ ڪونھي، ۽ جيڪو دير ڪري تنھن تي (به) گناھ نه آھي (اھو حُڪم) اُن لاءِ آھي جيڪو پرھيزگاري ڪري، ۽ الله کان ڊڄو ۽ ڄاڻو ته اوھين ڏانھس گڏ ڪيا ويندؤ.'),
(211, 2, 204, '۽ ماڻھن مان ڪو (اِھڙو) آھي جو سندس ڳالھائڻ دنيا جي حياتيءَ ۾ توکي عجب ۾ وجھندو آھي ۽ جيڪي سندس دل ۾ آھي تنھن تي الله کي شاھد ڪندو آھي ھن ھوندي به جو اُھو وڏو جھيڙاگر آھي.'),
(212, 2, 205, '۽ جڏھن توکان موٽندو آھي تڏھن ملڪ ۾ ھن لاءِ ڪوشش ڪندو آھي ته منجھس فساد وجھي ۽ پوک ۽ نسل ناس ڪري، ۽ الله فساد کي پسند نه ڪندو آھي.'),
(213, 2, 206, '۽ جڏھن اُن کي چئبو آھي ته الله کان ڊڄ تڏھن وڏائي کيس گناہ تي آڪڙائيندي آھي پوءِ کيس (سزا) دوزخ بس آھي، ۽ اُھو ضرور بڇڙو ھنڌ آھي.'),
(214, 2, 207, 'ماڻھن مان ڪو اھڙو آھي جو الله جي راضپي حاصل ڪرڻ لاءِ پاڻ کي وڪڻندو آھي، ۽ الله ٻانھن تي مھربان آھي.'),
(215, 2, 208, 'اي ايمان وارؤ پورا اسلام ۾ داخل ٿيو، ۽ شيطان جي وِکن تي نه ھلو، ڇوته اُھو اوھان جو پڌرو ويري آھي.'),
(216, 2, 209, 'پوءِ جيڪڏھن اوھين ان کانپوءِ جو اوھان وٽ چٽا دليل آيا (اِسلام کان) ٿڙندؤ ته ڄاڻو ته الله غالِب حڪمت وارو آھي.'),
(217, 2, 210, '(ڪافر) ھن کان سواءِ انتظار نه ڪندا آھن ته الله (جو عذاب) جُھڙ جي پاڇن ۾ وٽن اچي ۽ ملائڪ (به) ۽ ڪم فيصل ڪيو وڃي، ۽ الله ڏانھن سڀ ڪم موٽايا ويندا آھن.'),
(218, 2, 211, '(اي پيغمبر) بني اِسرائيلن کان پُڇا ڪر ته کين ڪيتريون چٽيون نشانيون ڏنيون سين، ۽ جيڪي الله جي نعمت کي پاڻ وٽ اچڻ کانپوءِ مٽائيندو ته بيشڪ الله سخت عذاب ڪندڙ آھي.'),
(219, 2, 212, 'ڪافرن لاءِ دُنيا جي حياتي سينگاري ويئي ۽ مؤمنن سان ٺـٺوليون ڪندا آھن، ۽ جيڪي پرھيزگار آھن سي قيامت جي ڏينھن اُنھن (ڪافرن) جي مٿئون ھوندا، ۽ الله جنھن کي گھرندو آھي تنھن کي اَڻ ڳڻي روزي ڏيندو آھي.'),
(220, 2, 213, 'سڀ ماڻھو ھڪ ٽولي هُئا، پوءِ (ڦري اِختلاف ۾ پيا تنھنڪري) الله پيغمبرن کي خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو، ۽ ساڻن سچ سان ڪتاب ھن لاءِ لاٿائين ته (اُھو ڪتاب) ماڻھن جي وچ ۾ جنھن (ڳالھ) بابت جھڳڙو ڪندا آھن (تنھن بابت) نبيرو ڪري، ۽ اُنھيءَ بابت (ٻئي ڪنھن به) تڪرار نه ڪيو پر جن کي اُھو (ڪتاب) ڏنو ويو تن پڌرن حڪمن اچڻ کانپوءِ پاڻ ۾ ساڙ پيل سببان (تڪرار ڪيو)، پوءِ الله اُھا سچي ڳالھ جنھن بابت جھڳڙو ڪيائون تنھن ڏانھن پنھنجي حُڪم سان مؤمنن کي سڌو رستو ڏيکاريو، ۽ الله جنھن کي گھرندو آھي تنھن کي سڌو رستو ڏيکاريندو آھي.'),
(221, 2, 214, '(اي مسلمانؤ اوھين) ھن ھوندي به بھشت ۾ گھڙڻ جو گمان ڪندا آھيو ڇا؟ جو اڃان اوھان کي اُنھن جي حالت نه آئي آھي جيڪي اوھان کان اڳ گذريا، جن کي سڃائي ۽ ڏُک پُھتو ۽ ايستائين لوڌيا ويا جيستائين پيغمبر ۽ جي مؤمن ساڻس هُئا (تن دُعا گُھري) چيو ٿي ته الله جي مدد ڪڏھن پھچندي؟ (چيو وين ته) ھوشيار رھو ته الله جي مدد ويجھي آھي.'),
(222, 2, 215, '(اي پيغمبر) توکان پڇن ٿا ته (اُھي) ڇا خرچين؟ (کين) چؤ ته جيڪو مال خرچي سو ماءُ پيءُ ۽ مائٽن ۽ ڇورن (ٻارن) ۽ مسڪينن ۽ مسافرن کي (ڏيڻ) جڳائي، ۽ جيڪا چڱائي ڪندؤ (سا لھندؤ) ڇوته الله اُنکي ڄاڻندڙ آھي.'),
(223, 2, 216, '(اي مؤمنؤ) اوھان تي (ڪافرن سان) ويڙھ ڪرڻ فرض ڪئي ويئي آھي ۽ اُھا اوھان کي ناخوش لڳندي آھي، ۽ شايد (اوھين) ڪنھن شي کي ناخوش ڀانيو ۽ (حقيقت ڪري) اُھا اوھان لاءِ ڀلي ھُجي، ۽ شايد ڪنھن شي کي پيارو رکو ۽ اُھا اوھان لاءِ بڇڙي ھجي، ۽ الله ڄاڻندو آھي ۽ اوھين نه ڄاڻندا آھيو.'),
(224, 2, 217, '(اي پيغمبر) توکان تعظيم وارن مھينن ۾ وڙھڻ بابت پُڇن ٿا، چؤ ته مَنجھن ويڙھ ڪرڻ وَڏو گناھ آھي، ۽ الله جي واٽ کان جھلڻ ۽ الله کي نه مڃڻ ۽ تعظيم واريءَ مسجد کان روڪڻ، ۽ سندس رھڻ وارن کي منجھائنس لوڌڻ الله وٽ (اُن کان) تمام وڏو گناھ آھي، ۽ فتنو (يعني شرڪ) خون ڪرڻ کان بلڪل وڏو ڏوھ آھي. ۽ (ڪافر) جيڪڏھن پڄي سگھندا ته اوھان سان ايستائين سدائين پيا وڙھندا جيستائين اوھان کي اوھان جي دين (اسلام) کان ڦيرائين، ۽ اوھان مان جيڪي پنھنجي دين کان ڦرندا پوءِ اُھي ڪافر ٿي مرندا تن جي ڪئي ڪمائي دُنيا ۽ آخرت ۾ ڪُٽ ٿي ويئي، ۽ اُھي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.'),
(225, 2, 218, 'جن ايمان آندو ۽ جن الله جي واٽ ۾ وطن ڇڏيو ۽ جھاد ڪيو سي الله جي ٻاجھ جا اميدوار آھن، ۽ الله بخشڻھار مھربان آھي.'),
(226, 2, 219, '(اي پيغمبر) توکان شراب ۽ جؤا بابت پڇن ٿا، چؤ ته اُنھن ۾ وڏو گناھ ۽ ماڻھن لاءِ (ڪجھ) فائدا آھن، ۽ سندن فائدي کان اُنھن جو گناھ تمام وڏو آھي. ۽ (پڻ) توکان پڇن ٿا ته (اُھي) ڇا خرچين، چؤ ته (گھرج کان) وڌيڪ (خرچيو) اھڙيءَ طرح الله اوھان لاءِ آيتون بيان ڪندو آھي ته مانَ اوھين ڌيان رکو.'),
(227, 2, 220, 'دنيا ۽ آخرت (جي ڪمن) ۾، ۽ (اي پيغمبر) توکان يتيمن بابت پڇن ٿا، چؤ ته اُنھن لاءِ سڌار ڀلي آھي، جيڪڏھن کين (کاڌي پيتي ۾ پاڻ سان) گڏيو ته اوھان جا ڀائر آھن، ۽ الله بگاڙيندڙ کي سڌاريندڙ کان ڄاڻندو آھي، ۽ جيڪڏھن الله گھري ھا ته اوھان کي سختيءَ ۾ وجھي ھا، ڇوته الله غالب حڪمت وارو آھي.'),
(228, 2, 221, '۽ (اي مسلمانؤ ايستائين) مشرڪياڻين کي نه پرڻيو جيستائين ايمان آڻين، ۽ بيشڪ مؤمنياڻي ٻانھي مشرڪياڻيءَ کان ڀلي آھي جيتوڻيڪ اوھان کي (مشرڪياڻي) زيادہ وڻي، ۽ اوھين (ايسين) مشرڪن کي نه پرڻايو جيسين ايمان آڻين، ۽ بيشڪ مؤمن ٻانھو مُشرڪ کان ڀلو آھي جيتوڻيڪ اوھان کي (مشرڪ) زيادہ وڻي، اِھي (مُشرڪ) باھ ڏانھن سڏيندا آھن، ۽ الله پنھنجي ارادي مُوجب بھشت ۽ بخشش ڏانھن سڏيندو آھي، ۽ پنھنجون آيتون ماڻھن لاءِ بيان ڪندو آھي ته مانَ اُھي نصيحت وٺن.'),
(229, 2, 222, '۽ (اي پيغمبر) توکان حيض (جي حڪم) بابت پُڇن ٿا، چؤ ته اُھا پليتي آھي تنھنڪري زالن کان حيض ۾ پري ٿيو ۽ ايستائين کين ويجھا نه وڃو جيستائين پاڪ ٿين، پوءِ جڏھن بلڪل پاڪ ٿين تڏھن جنھن واٽ کان الله اوھان کي موڪل ڏني آھي (اُنھيءَ کان) اُنھن ڏانھن اچو، ڇوته الله توبه ڪندڙن کي دوست رکندو آھي ۽ (پڻ) پاڪائي ڪندڙن کي دوست رکندو آھي.'),
(230, 2, 223, 'اوھان جو زالون اوھان لاءِ پوک آھن، پوءِ پنھنجيءَ پوک ڏانھن جئن وڻيوَ تئن اچو، ۽ پاڻ لاءِ (چڱا ڪم) اڳي موڪليو، ۽ الله کان ڊڄو ۽ ڄاڻو ته اوھين ساڻس ملاقي ٿيندڙ آھيو، ۽ مُؤمنن کي خوشخبري ڏي.'),
(231, 2, 224, '۽ اوھين پنھنجن قسمن لاءِ چڱائي ڪرڻ ۽ پرھيزگاري ڪرڻ ۽ ماڻھن جي وچ ۾ صُلح ڪرائڻ کان (پاسو ڪرڻ واسطي) الله جو اِسم پيش نه ڪريو، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(232, 2, 225, 'الله اوھان کي اوھان جي اجاين قسمن کڻڻ ۾ نه وٺندو آھي پر اوھان جي دلين جيڪا نيت ڪئي آھي تنھن تي پڪڙيندو آھي، ۽ الله بخشڻھار بُردبار آھي.'),
(233, 2, 226, 'جيڪي پنھنجي زالن (جي صُحبت) کان قسم کڻن تن کي چار مھينا ترسڻو آھي، پوءِ جيڪڏھن (اُنھن ڏانھن اُن وقت ۾) موٽيا ته (ڀلي موٽن) ڇوته الله بخشڻھار مھربان آھي.'),
(234, 2, 227, '۽ جيڪڏھن طلاق جو پڪو ارادو ڪيائون (ته ڀلي ڪن) ڇوته الله ٻڌندڙ ڄاڻندڙ آھي.'),
(235, 2, 228, '۽ طلاق ڏنلون زالون پاڻ کي (ٻئي نڪاح ڪرڻ کان) ٽي حيض جھلين، ۽ جيڪڏھن الله ۽ قيامت جي ڏينھن کي مڃينديون آھن ته الله سندن ڳڀيرڻن ۾ جيڪي (ٻار يا حيض) پيدا ڪيو آھي سو کين لڪائڻ روا نه آھي، ۽ اِن (مدت) ۾ سندن مڙس جيڪڏھن سڌارو گھرن ته سندن موٽائڻ جا وڌيڪ حقدار آھن، ۽ جھڙا (مُڙس جا حق) اُنھن (زالن) تي آھن تھڙا اُنھن (زالن) جا (حق مڙسن تي) چڱيءَ ريت سان آھن، مُڙسن کي مٿن وڏو مانُ (يعني حڪومت) آھي، ۽ الله غالب حڪمت وارو آھي.'),
(236, 2, 229, 'طلاق (رجعي پورا) ٻه ڀيرا آھي، پوءِ چڱيءَ طرح سان جھلڻ يا اِحسان سان ڇڏڻ (لازم) آھي، ۽ جيڪو کين ڏنو اَٿوَ تنھن مان ڪجھ (ورائي) وٺڻ اوھان کي ھن (حالت) کان سواءِ حلال نه آھي جو ٻئي الله جي حدن کي نه پوري ڪرڻ کان ڊڄن، پوءِ جيڪڏھن (اي مسلمانؤ) اوھين ڊڄو ته ٻئي الله جي حدن کي پورو ڪري نه سگھندا ته (زال) جيڪا شي عيوض ۾ ڏئي تنھن (جي ڏيڻ وٺڻ) ۾ ٻنھي تي گناھ نه آھي. اِھي الله جون حدون آھن اُنھن کان لنگھي نه وڃو، ۽ جيڪي الله جي حدن کان لنگھي ويندا سي ئي ظالم آھن.'),
(237, 2, 230, 'پوءِ جيڪڏھن اُن کي (ٽين) طلاق (به) ڏنائين ته اُن کانپوءِ (اُھا زال) اُن کي (ايستائين) حلال نه ٿيندي جيستائين (اُھا) ٻيو مُڙس پرڻي، پوءِ جيڪڏھن (اُن به) کيس طلاق ڏني ته جيڪڏھن ٻئي (ڄڻا) الله جي حدن کي قائم رکي سگھڻ (جي پڪ) ڀانئين ٿا ته (تڏھن پاڻ ۾ نڪاح ڪري) موٽن ته ٻنھي تي گناھ نه آھي، ۽ اِھي اَلله جون حدون آھن اُنھن کي اُن قوم لاءِ بيان ڪندو آھي جي ڄاڻندا آھن.'),
(238, 2, 231, '۽ جڏھن زالن کي (ٻه ڀيرا) طلاق ڏيو پوءِ اُھي پنھنجي (عدت جي) مدي کي (اچي) پھچڻ لڳن ته (تڏھن يا) اُنھن کي چڱيءَ ريت سان رکو يا اُنھن کي چڱيءَ ريت سان ڇڏيو، ۽ ڏکوئڻ لاءِ کين نه جھليو نه زيادتي ڪريو، ۽ جيڪو ائين ڪندو تنھن بيشڪ پاڻ تي ظلم ڪيو، ۽ الله جي حُڪمن کي ٺـٺوليون ڪري نه وٺو، ۽ پاڻ تي الله جي نعمت ياد ڪريو ۽ جيڪي اوھان تي ڪتاب ۽ دانائيءَ مان لاٿائين جنھن سان اوھان کي نصيحت ڏيندو آھي (اُھو احسان به ياد ڪريو)، ۽ الله کان ڊڄو ۽ ڄاڻو ته الله سڀڪنھن شيء کي ڄاڻندڙ آھي.'),
(239, 2, 232, '۽ جڏھن زالن کي طلاق ڏيو پوءِ پنھنجي (عدت جي) مُدّت کي پھچن ته کين پنھنجن مُڙسن سان پرڻجڻ کان نه جھليو جڏھن ته پاڻ ۾ چڱيءَ ريت سان راضي ٿين، اِھو (حُڪم) آھي جيڪو اوھان مان الله ۽ قيامت جي ڏينھن کي مڃي تنھن کي اُن سان نصيحت ڏجي ٿي، اِھو (حُڪم) اوھان کي صاف ڪندڙ ۽ بلڪل پاڪ ڪندڙ آھي، ۽ الله ڄاڻندو آھي ۽ اوھين نه ڄاڻندا آھيو.'),
(240, 2, 233, '۽ مائر پنھنجن ويائن کي پورا ٻه ورھيه ٿڃ پيارين (اِھو حُڪم) اُنھيءَ لاءِ آھي جيڪو ٿڃ پيارڻ جي مُدّت پُوري ڪرڻ گُھري، ۽ اُنھن (مائرن) جي خوراڪ ۽ اُنھن جي پوشاڪ ھليءَ چليءَ وانگر پيءُ تي آھي، ڪنھن کي سندس وس کان وڌيڪ تڪليف نه ٿي ڏجي، ماءُ کي وياءُ سببان تڪليف نه ٿي ڏجي ۽ نڪي پيءُ کي وياءُ سببان (تڪليف ڏجي)، ۽ وارث تي (به) اھڙو ئي (حق) آھي، پوءِ جيڪڏھن ٻئي پنھنجي رضامندي ۽ صَلاح سان (ٻار جي ٿڃ) ڇڏائڻ گھرن ته مٿن گناھ نه آھي، ۽ جيڪڏھن پنھنجي اولاد کي (ٻئي کان) کير پيارڻ گھرو ته جڏھن اوھان ھليءَ چليءَ وانگر (پياريندڙ کي) جيڪي ڏيڻو ڪيو سو پھچايو تڏھن اوھان تي ڪو گناھ نه آھي، ۽ الله کان ڊڄو ۽ ڄاڻو ته جيڪي اوھين ڪندا آھيو سو الله ڏسندڙ آھي.'),
(241, 2, 234, '۽ اوھان مان جيڪي مرن ۽ زالون ڇڏين ته اُھي (زالون) پاڻ کي چار مھينا ۽ ڏھ ڏينھن ترسائين، پوءِ جڏھن پنھنجيءَ (عدّت جي) مُدّت کي پُھچن تڏھن جيڪي ھليءَ چليءَ وانگر (يعني شرعي نڪاح) پنھنجي حق ۾ ڪن تنھن ۾ اوھان تي ڪو گناھ ڪونھي، ۽ جيڪي اوھين ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.'),
(242, 2, 235, '۽ زالن جي سڱ ڇڪائڻ بابت (عدت ۾) جيڪي آڇ ڪريو يا دلين ۾ لڪايو تنھن ۾ اوھان تي گناھ نه آھي، الله ڄاتو آھي اوھين اُنھن (زالن) کي سگھو ئي ياد ڪندؤ پر رواجيءَ طرح چئي چوڻ کانسواءِ کين ڳجھو انجام نه ڏيو، ۽ نڪي ايستائين نڪاح جي پڪي نيت ڪريو جيستائين عدت پنھنجيءَ مدت کي پھچي، ۽ ڄاڻو ته جيڪي اوھان جي دلين ۾ آھي سو الله ڄاڻندو آھي تنھنڪري کانئس ڊڄو، ۽ ڄاڻو ته الله بخشڻھار بُردبار آھي.'),
(243, 2, 236, 'جيڪڏھن زالن کي (اُنھيءَ وقت) طلاق ڏيو جڏھن اڃا کين ھٿ نه لاتو اَٿوَ يا اُنھن لاءِ ڪابين مُقرر نه ڪيو اَٿوَ ته اوھان تي گناھ ڪونھي، ۽ کين نفعي وٺڻ لاءِ ھليءَ چليءَ وانگر جورو ڏيو، آسودي تي پنھنجي وس آھر ۽ سڃي تي پنھنجي وس آھر آھي، (اِھو ڪم) ڀلارن تي لازم آھي.'),
(244, 2, 237, '۽ جيڪڏھن کين ھٿ لائڻ کان اڳ ھن حالت ۾ انھن کي طلاق ڏيو جو ڪابين مُقرّر ڪيو اٿو تنھن جو اَڌ (ڏيڻ لازم آھي) پر جي (زالون) بخشين يا جنھن جي ھٿ ۾ نڪاح جي ڳنڍ آھي (يعني مڙس) سو بخشي (يعني پورو ڏي)، ۽ اوھان جو بخشڻ پرھيزگاريءَ کي ڏاڍو ويجھو آھي، ۽ اوھين پاڻ ۾ نيڪيءَ کي نه وساريو، ڇوته جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.'),
(245, 2, 238, 'سڀني نمازن تي ۽ (خصوصاً) وچين نماز تي نگھباني ڪريو، ۽ الله لاءِ فرمانبردار ٿي بيھو.'),
(246, 2, 239, 'پوءِ جيڪڏھن دشمنن کان ڊڄو ته پيادا ٿي يا سوار ٿي (اشارن سان نماز پڙھو)، پوءِ جڏھن بي ڀَوَا ٿيو تڏھن جئن اوھان کي (نماز جا ارڪان) جي نه ڄاڻندا ھيؤ سيکاريائين تئن (سڀني رڪنن جي بجا آوريءَ سان) الله کي ياد ڪريو.'),
(247, 2, 240, '۽ اوھان مان جيڪي مرن ۽ زالون ڇڏين، تن کي ھڪ ورھيه تائين پنھنجين زالن لاءِ (کين) فائدي ڏيڻ (۽) نه لوڌڻ جي وصيّت ڪرڻ لازم آھي، پوءِ جيڪڏھن (اُھي) نڪرن ته جيڪي پنھنجي حق ۾ (شريعت جي) دستور مُوجب ڪن تنھن ۾ اوھان تي گُناھ نه آھي، ۽ الله غالب حِڪمت وارو آھي.'),
(248, 2, 241, '۽ طلاق ڏنلن لاءِ ھليءَ چليءَ وانگر پرھيزگارن تي خرچ لازم آھي.'),
(249, 2, 242, 'اِھڙيءَ طرح الله پنھنجا حُڪم اوھان لاءِ پَڌرا ڪري ٿو ته مانَ اوھين سمجھو.'),
(250, 2, 243, '(اي ڏسندڙ) اُنھن ڏانھن نه ڏٺو اٿئي ڇا؟ جيڪي پنھنجي ديس مان موت جي ڀؤ کان نڪتا ۽ اُھي (ڪيئي) ھزار هُئا، پوءِ الله اُنھن کي فرمايو ته مرو (ته مري ويا)، وري کين جياريائين، ڇوته الله ماڻھن تي وڏي فضل ڪرڻ وارو آھي پر گھڻا ماڻھو شڪر نه ڪندا آھن.'),
(251, 2, 244, '۽ (اي مُسلمانؤ) الله جي واٽ ۾ (ڪافرن سان) ويڙھ ڪريو ۽ ڄاڻو ته الله ٻڌندڙ ڄاڻندڙ آھي.'),
(252, 2, 245, 'اُھو ڪير آھي جو الله کي چڱو قرض اُڌارو ڏئي؟ ته (الله) اُن لاءِ گھڻي قدر وڌائي، ۽ (رزق) الله تنگ ڪري ٿو ۽ (الله) ڪشادو ڪري ٿو، ۽ ڏانھنس موٽايا ويندؤ.'),
(253, 2, 246, 'موسىٰؑ کان پوءِ بني اِسرائيلن جي ٽوليءَ ڏانھن نه ڏٺو اٿيئي ڇا؟ جڏھن پنھنجي پيغمبر کي چيائون ته اسان لاءِ ڪو بادشاھ مُقرّر ڪر ته الله جي واٽ ۾ ويڙھ ڪريون، (تڏھن پيغمبر) چيو ته جيڪڏھن اوھان تي ويڙھ لازم ٿي ته اوھين وڙھڻ کي ويجھا نه ويندؤ، چيائون ته اسان کي ڇا (ٿيو) آھي جو الله جي واٽ ۾ اِنھيءَ ھوندي به نه وڙھنداسون جو (اسين) پنھنجن ديسن ۽ پنھنجن اولادن مان لوڌيا ويا آھيون، پوءِ جڏھن مٿن ويڙھ لازم ڪئي ويئي تڏھن منجھانئن ٿورڙن کانسواءِ (سڀ) ڦري ويا، ۽ الله ظالمن کي ڄاڻندڙ آھي.'),
(254, 2, 247, '۽ سندن پيغمبر کين چيو ته الله طالوت کي اوھان لاءِ بادشاہ مُقرّر ڪيو آھي، چيائون ته اُن جي بادشاھي اسان تي ڪئن ٿيندي ھن حالت ۾ جو اسين کانئس بادشاھيءَ جا وڌيڪ حقدار آھيون ۽ مال مان گھڻائي نه ڏني ويئي اٿس. (پيغمبر کين) چيو ته الله اُن کي اوھان تي پسند ڪيو آھي ۽ کيس عِلم ۽ جسم ۾ گھڻو وڌايو اٿس، ۽ الله جنھن کي گھُرندو آھي تنھن کي پنھنجو مُلڪ ڏيندو آھي، ۽ الله (مھربانيءَ ۾) ڪشادگيءَ وارو ڄاڻندڙ آھي.'),
(255, 2, 248, '۽ کين سندن پيغمبر چيو ته اُن جي بادشاھيءَ جي نشاني اوھان وٽ صندوق جو اچڻ آھي جنھن ۾ اوھان جي پالڻھار کان ھڪ دِل جو آرام ۽ جيڪي مُوسىٰ جي خاندان ۽ ھارون جي خاندان (تبرڪ) ڇڏيو تنھن جي بچت آھي اُن کي مَلائِڪ کڻي ايندا، ڇوته اُن ۾ اوھان لاءِ بي شڪ (وڏي) نشاني آھي جيڪڏھن اوھين مڃيندڙ آھيو.'),
(256, 2, 249, 'پوءِ جڏھن طالُوت لشڪر سوڌو نڪتو (تڏھن لشڪر کي) چيائين ته الله نديءَ جي پاڻيءَ سان توھان کي پرکيندڙ آھي، پوءِ جيڪو منجھانئس پيئندو سو منھنجو نه آھي، ۽ جيڪو منجھانئس نه چکندو سواءِ ھِن جي جو پنھنجي ھٿ سان لَپ ڀري (پيئي) سو بيشڪ مُنھنجو آھي. پوءِ منجھانئن ٿورڙن کانسواءِ (سڀني) اُن مان پيتو، پوءِ جنھن مھل اُھو (طالوت) ۽ سندس سنگتي مُؤمن اُن (نديءَ) کان لنگھيا (تنھن مھل پيئندڙن) چيو ته اَڄ اسان کي جالوت ۽ سندس لشڪر سان (وڙھڻ جي) سگھ نه آھي، (۽ تنھن مھل) جيڪي پڪو ڀروسو رکندا هُئا ته اُھي الله کي ملندا تن چيو ته ڪيتريون ٿوريون ٽوليون گھڻن ٽولين تي الله جي حُڪم سان غالب ٿي ويون آھن، ۽ الله صابرن ساڻ آھي.'),
(257, 2, 250, '۽ جنھن مھل (اُھي طالُوتي) جالُوت ۽ سندس لشڪر (جي وڙھڻ) لاءِ نڪتا (تنھن مھل) چيائون ته اي اسان جا پالڻھار اسان تي صبر پلٽ ۽ اسان جان پير ثابت رَک ۽ ڪافر ٽوليءَ تي اسان کي سوڀ ڏئي.'),
(258, 2, 251, 'پوءِ الله جي حُڪم سان کين ڀڄايائون، ۽ دائُود جالُوت کي ڪُٺو ۽ الله اُن (دائُود) کي بادشاھي ۽ حِڪمت ڏِني ۽ جيڪي اُن لاءِ گھريائين سو سيکاريائين، ۽ جيڪڏھن الله جو ٽارڻ ھڪڙن ماڻھن سان ٻـين کي نه ھُجي ھا ته زمين ضرور خراب ٿئي ھا پر الله جھان (وارن) تي ڀلائي ڪرڻ وارو آھي.'),
(259, 2, 252, 'اِھي الله جا حُڪم آھن ھي توتي سَچ سان پڙھون ٿا، ۽ تون ضرور پيغمبرن مان آھين.'),
(260, 2, 253, 'اِھي پيغمبر آھن اُنھن مان ھڪڙن کي ٻـين کان ڀلو ڪيوسون، منجھانئن ڪن سان الله ڳالھايو ۽ ڪن کي درجن ۾ وڏو ڪيائين، ۽ عيسىٰ پٽ مريم جي کي چٽا مُعجزا ڏناسون ۽ کيس پاڪ رُوح سان مدد ڏني سون، ۽ جيڪڏھن الله گُھري ھا ته جيڪي (ماڻھو) اُنھن (پيغمبرن) کان پوءِ هُئا سي پاڻ وٽ چِٽن مُعجزن اچڻ پڄاڻان (پاڻ ۾) نه وڙھن ھا پر اختلاف ۾ پيا پوءِ منجھانئن ڪن ايمان آندو ۽ منجھانئن ڪن ڪفر ڪيو، ۽ جيڪڏھن الله گھري ھا ته (پاڻ ۾) نه وڙھن ھا، پر الله جيڪي گھرندو آھي سو ڪندو آھي.'),
(261, 2, 254, 'اي مؤمنؤ جيڪي اوھان کي ڏنوسون تنھن مان اُن ڏينھن جي اچڻ کان اڳ خرچ ڪريو جنھن ۾ نه ڪو واپار ۽ نه ڪا ياراڻي آھي ۽ نه ڪا پارت ھلندي، ۽ ڪافر ئي ظالم آھن.'),
(262, 2, 255, 'الله (اُھو آھي جو) اُن کان سواءِ ڪو عبادت جي لائق نه آھي، (اُھو سدائين) جيئرو (جھانن کي) بيھڪ ڏيندڙ آھي، کيس نڪي پنڪي ۽ نڪي ننڊ وٺندي آھي، جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) سندس (ملڪيت) آھي. ڪير آھي جو سندس موڪل ڌاران وٽس پارت ڪري سگھي؟ جيڪي اُنھن (مخلوقن) جي اڳيان آھي ۽ جيڪي سندن پوئتان آھي سو (سڀ) ڄاڻندو آھي، ۽ جيڪي گھري تنھن کان سواءِ سندس علم مان ڪنھن ذري کي (اُھي) گھيرو ڪري نه سگھندا آھن، سندس (بادشاھيءَ جي) ڪرسيءَ آسمانن ۽ زمين کي لپيٽيو آھي، ۽ اُنھن جي سنڀال کيس نه ٿڪائيندي آھي، ۽ اُھو (سڀ کان) مٿاھون وڏي وڏائيءَ وارو آھي.'),
(263, 2, 256, 'دين مڃائڻ ۾ ڪا زبردستي نه آھي، (ڇوته) بيشڪ گمراھيءَ کان ھدايت (جدا ٿي) پڌري ٿي آھي، تنھنڪري جيڪو طاغوت (يعني خُدا کانسواءِ ٻـين معبودن) کي نه مڃي ۽ الله کي مڃي تنھن بيشڪ اھڙي مُحڪم ڪڙي کي ورتو، جنھن کي ڇڄڻ نه آھي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(264, 2, 257, 'الله مؤمنن جو سنڀاليندڙ آھي کين اونداھين مان سوجھري ڏانھن ڪڍندو آھي، ۽ جيڪي ڪافر آھن تن جا مددگار (خُدا کانسواءِ جن جي عبادت ڪن ٿا سي) بت آھن کين سوجھري مان اونداھين ڏانھن ڪڍندا آھن، اُھي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.'),
(265, 2, 258, '(اي پيغمبر) تو اُنھن ڏانھن نه ڏٺو ڇا؟ جنھن پنھنجي پالڻھار بابت ابراھيم سان (انھيءَ مغروريءَ سبب) تڪرار ڪيو جو الله کيس بادشاھي ڏني ھئي، جڏھن ابراھيم چيو ته منھنجو پالڻھار اُھو آھي جو جياريندو آھي ۽ ماريندو آھي، (ڪافر) چيو ته آءٌ (به) جياريان ٿو ۽ ماريان ٿو. ابراھيم چيو ته الله سج کي اوڀر کان اوڀاريندو آھي تنھنڪري (تون) اُن کي اولھ کان اوڀار پوءِ ڪافر ھَڪو ٻَڪو ٿي ويو، ۽ الله ظالم ٽوليءَ کي سڌو رستو نه ڏيکاريندو آھي.'),
(266, 2, 259, 'يا اِنھيءَ طرح اُنھي (پيغمبر) کي (ڏٺئي ڇا؟) جو ھڪ ڳوٺ وٽان ھن حالت ۾ لنگھيو جو اُھو پنھنجي ڇتين سوڌو ڊٺل ھو، (ڏسي ڪري) چيائين ته ھن کي الله ڪھڙي طرح سندس ڦٽڻ کانپوءِ وسائيندو، پوءِ الله کيس سؤ ورھيه ماريو وري کيس (جياري) کڙو ڪيائين، الله چيو ته ڪيترو رھينءِ؟ چيائين ته ھڪ ڏينھن يا ڪجھ پھر دير ڪئي اٿم، الله چيو ته بلڪ سؤ ورھيه دير ڪئي اٿيئي پوءِ پنھنجي طعام ۽ پاڻيءَ ڏانھن نھار جو اُھو نه سينواريو آھي، ۽ پنھنجي (مئل) گڏھ ڏانھن نھار (ته جياري توکي ڏيکاريون) ۽ (اِھو ھِن لاءِ ڪيوسين) ته توکي ماڻھن لاءِ نشاني ڪريون ۽ (ھاڻي گڏھ جي) ھڏن ڏانھن ڏس ته کين ڪئن چوريون ٿا وري اُنھن (ھڏن) کي گوشت پھرايون ٿا. پوءِ جنھن مھل ان تي (اِھو حال) پڌرو ٿيو (تڏھن) چيائين ته ڄاڻان ٿو ته الله سڀ ڪنھن شيءِ تي وس وارو آھي.'),
(267, 2, 260, '۽ (ياد ڪر) جڏھن ابراھيم چيو ته اي مُنھنجا پالڻھار مون کي ڏيکار ته ڪھڙي طرح مئلن کي جياريندو آھين، (الله) چيو ته نه ٿو وسھين ڇا؟ (ابراھيم) چيو ته ھائو (وسھان ٿو) پر ھن لاءِ ته منھنجي دل کي تسلّىٰ ٿئي. (الله) چيو ته چار پکي وٺ پوءِ کين پاڻ وٽ ذرا ذرا ڪر پوءِ منجھانئن ڪو ڀاڱو ھر ھڪ جبل تي رک وري کين سڏ ته توڏانھن ڊوڙندا ايندا، ۽ ڄاڻ ته الله غالب حڪمت وارو آھي.'),
(268, 2, 261, 'جيڪي (ماڻھو) پنھنجا مال الله جي واٽ ۾ خرچيندا آھن تن جو مثال اُنھيءَ داڻي جي مثال وانگر آھي جو ست سنگ ڄمائي ھر ھڪ سنگ ۾ سڀ داڻو ھجي، ۽ الله جنھن کي گھرندو آھي (تنھن کي) ٻيڻو ڏيندو آھي، ۽ الله (مھربانيءَ ۾) ڪشادگي وارو ڄاڻندڙ آھي.'),
(269, 2, 262, 'جيڪي (ماڻھو) پنھنجو مال الله جي واٽ ۾ خرچيندا آھن وري پنھنجي ڏيڻ پٺيان نڪي ٿورو رکندا آھن ۽ نڪي ايذائيندا آھن تن لاءِ سندن پالڻھار وٽ سندن ثواب آھي، ۽ نڪي کين ڪو ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.'),
(270, 2, 263, 'مِٺو ڳالھائڻ ۽ معاف ڪرڻ اُنھيءَ خيرات کان ڀَلو آھي جنھن جي پٺيان ايذاءُ ھُجي، ۽ الله بي پرواہ بُردبار آھي.'),
(271, 2, 264, 'اي مؤمنؤ پنھنجين خيراتن کي اِحسان رکڻ ۽ ايذائڻ سان اُنھيءَ ماڻھوءَ وانگر نه وڃايو جيڪو پنھنجو مال ماڻھن جي ڏيکاري لاءِ خرچي ۽ الله ۽ قيامت جي ڏينھن کي نه مڃي، پوءِ سندس مثال (اُنھيءَ) لسي پھڻ وانگر آھي جنھن تي ٿوري مٽي (پيل) ھجي ۽ مٿس وڏو مينھن وسي پوءِ ڇنڊي ڇڏيس، (ته رياء وارن) جيڪي ڪمايو تنھن مان ڪنھن ذري تي (اُھي) وس وارا نه ٿيندا، ۽ الله ڪافر ٽوليءَ کي سڌو رستو نه ڏيکاريندو آھي.'),
(272, 2, 265, '۽ جيڪي پنھنجن مالن کي الله جي رضامندي حاصل ڪرڻ ۽ پنھنجين دلين ۾ پڪي يقين پيدا ڪرڻ لاءِ خرچيندا آھن تن جو مثال ھڪ باغ وانگر آھي جو دڙي تي ھجي اُن تي وڏڦڙو مينھن وسي ۽ پنھنجو ڦر ٻيڻو ڪري، پر جيڪڏھن مٿس وڏڦڙو مينھن نه وسي ته ماڪ بس ھجيس، ۽ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.'),
(273, 2, 266, 'اوھان مان ڪو ھڪ (دل سان) گھرندو آھي ڇا؟ ته اُن کي کجين ۽ ڊاکن جو ھڪ باغ ھجي جنھن جي ھيٺان واھيون وھنديون ھجن اُن لاءِ منجھس سڀ ڪنھن (جنس جا) ميوا ھجن ۽ کيس پوڙھائي اچي وٺي ۽ ان کي ھيڻو اولاد ھجي، پوءِ اُن (باغ) کي (اھڙو) واچوڙو لڳي جنھن ۾ باھه ھجي تنھنڪري (سندس باغ) سڙي وڃي، اھڙي طرح الله اوھان لاءِ (چٽيون) نشانيون بيان ڪندو آھي ته مانَ اوھين سمجھو.'),
(274, 2, 267, 'اي مؤمنؤ جيڪي ڪمايوَ تنھن مان (۽ پڻ) جيڪي اوھان لاءِ زمين مان پيدا ڪيوسين تنھن مان ڪي سٺيون شيون (خدا ڪارڻ) خرچ ڪيو، ۽ ان مان خراب شيء خرچ ڪرڻ جي نيت ھن ھوندي نه ڪريو جو (پاڻ) اُن ۾ پنھنجين اکين ٻوٽڻ کانسواءِ کيس وٺڻ وارا نه ھجو، ۽ ڄاڻو ته الله بي پرواہ ساراھيل آھي.'),
(275, 2, 268, '(اي مسلمانؤ) شيطان اوھان کي سڃائيءَ جو ڀؤ ڏيندو آھي ۽ اوھان کي بي حيائيءَ (جي ڪمن) جو حڪم ڪندو آھي، ۽ الله اوھان کي پنھنجي پاران بخشش ۽ ڀلائيءَ جو وعدو ڏيندو آھي، ۽ الله (مھربانيءَ ۾) ڪشادگيءَ وارو ڄاڻندڙ آھي.'),
(276, 2, 269, 'جنھن کي گھرندو آھي تنھن کي سمجھ ڏيندو آھي، ۽ جنھن کي سمجھ ڏني ويئي تنھن کي بيشڪ گھڻي چڱائِي ڏِني ويئي، ۽ سمجھ وارن کانسواءِ ٻيو ڪو نصيحت نه مڃيندو آھي.'),
(277, 2, 270, '۽ (اي مسلمانؤ) جيڪي اوھان مال خرچيو يا باس باسيو تنھن کي الله ڄاڻندو آھي، ۽ ظالمن جو ڪو مددگار نه آھي.'),
(278, 2, 271, 'جيڪڏھن خيراتون پڌريون ڪري ڏيندؤ ته اُھي (ڪھڙيون نه) چڱيون آھن، ۽ جيڪڏھن اُنھن کي ڳجھو ڪندؤ ۽ اُھي محتاجن کي ڏيندؤ ته اُھي اوھان لاءِ (ويتر) ڀليون آھن، ۽ الله اوھان جا ڪي گناہ اوھان کان ميٽيندو، ۽ جيڪي اوھان ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.'),
(279, 2, 272, '(اي پيغمبر) توتي اُنھن (ڪافرن) کي ھدايت (يعني دل ۾ اثر پيدا) ڪرڻ لازم نه آھي پر الله جنھن کي گھرندو آھي تنھن کي ھدايت ڪندو آھي ۽ جيڪي ڪجھ مال خرچيندا آھيو سو اوھان لاءِ آھي، ۽ الله جي رضامندي طلبڻ کانسواءِ (اوھين) خرچ نه ڪريو، ۽ جيڪي ڪجھ مال خرچيو (اُن جو ثواب) اوھان کي پورو ڏبو ۽ اوھان تي ظلم نه ڪبو.'),
(280, 2, 273, 'اھڙن فقيرن کي (خيرات ڏيڻ لازم آھي) جيڪي الله جي واٽ ۾ روڪيل آھن (واپار لاءِ) مُلڪ ۾ سفر ڪري نه سگھندا آھن، ناواقف (سندن) بي طمعي سببان کين آسودا ڀائيندا آھن، سندين پيشانين مان کين سڃاڻندو آھين، چنبڙي ماڻھن کان نه پنندا آھن، ۽ جيڪي ڪجھ مال خرچيندا آھيو سو الله ڄاڻندڙ آھي.'),
(281, 2, 274, 'جيڪي پنھنجا مال رات جو ۽ ڏينھن جو ڳجھو ۽ پَڌرو خرچيندا آھن تن لاءِ سندن پالڻھار وٽ سندن اَجر آھي، ۽ نڪي کين ڪو ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.'),
(282, 2, 275, 'جيڪي وياج کائيندا آھن سي (قبرن مان) رڳو اُنھيءَ (ماڻھوءَ) جي اُٿڻ جھڙو اُٿندا جنھن کي جِنّ اثر سببان چريو ڪيو ھجي، اِھو ھن ڪري آھي جو اُھي (وياج خور) چون ٿا ته واپار وياج جھڙو ئي آھي، ھن ھوندي جو الله واپار کي حلال ڪيو آھي ۽ وياج کي حرام ڪيو آھي، پوءِ جنھن کي پنھنجي پالڻھار وٽان نصيحت آئي ۽ (وياج) ڇڏيائين تنھن لاءِ جيڪي اڳي ڪيائين سو (ھينئر معاف) آھي، ۽ (آخرت ۾) سندس ڪم الله ڏانھن (سونپيل) آھي، ۽ جن ھيءَ عادت نه ڇڏي سي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.'),
(283, 2, 276, 'الله وياج (جو کٽيو) ناس ڪري ڇڏيندو آھي ۽ خيراتن کي وڌائيندو آھي، ۽ الله سڀ ڪنھن بي شڪر گنھگار کي دوست نه رکندو آھي.'),
(284, 2, 277, 'جن ايمان آندو ۽ چڱا ڪم ڪيا ۽ نماز پڙھيائون ۽ زڪوٰة ڏنائون تن لاءِ سندن پالڻھار وٽ سندن اَجر آھي، ۽ نڪي کين ڪو ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.'),
(285, 2, 278, 'اي ايمان وارؤ الله کان ڊڄو ۽ جيڪڏھن اوھين (الله ۽ رسول کي) مڃيندڙ آھيو ته جيڪي وياج مان رھيل آھي سو ڇڏيو.'),
(286, 2, 279, 'پوءِ جيڪڏھن (ائين) نه ڪندؤ ته الله ۽ سندس رسول جي پاران اوھان کي جنگ جي سُڌ ھجي، ۽ جيڪڏھن توبه ڪيوَ ته اوھان لاءِ اوھان جي مالن جي موڙي آھي، نڪي وڌيڪ وٺو ۽ نڪي اوھان کي گھٽ ڏبو.'),
(287, 2, 280, '۽ جيڪڏھن (قرضي) سڃو ھجي ته سگھ تائين مُھلت ڏيڻ لازم آھي، ۽ جيڪڏھن اوھين ڄاڻندا آھيو ته اوھان جو (قرض) بخشڻ اوھان لاءِ ڀلو آھي.'),
(288, 2, 281, '۽ (اي ماڻھو) اُنھيءَ ڏينھن کان ڊڄو جنھن ۾ الله ڏانھن موٽايا ويندؤ، پوءِ سڀڪنھن ماڻھوءَ کي جيڪي ڪمايائين تنھن جو پورو بدلو ڏبو ۽ اُنھن تي ظلم نه ڪبو.'),
(289, 2, 282, 'اي مؤمنؤ جڏھن ڪنھن ٺھرايل (مُدت) تائين قرض جو معاملو ڪريو تڏھن اُن کي لکو، ۽ اوھان جي وچ ۾ لکندڙ کي انصاف سان لکڻ گھرجي، ۽ جئن کيس الله سيکاريو تئن لکندڙ لکڻ کان انڪار نه ڪري پوءِ لکڻ گھرجيس، ۽ جنھن تي قرض آھي سو (مطلب) بيان ڪري ۽ پنھنجي پالڻھار الله کان ڊڄڻ گھرجيس ۽ منجھانئس ڪُجھ نه گھٽائي، پوءِ جنھن تي قرض آھي سو جيڪڏھن اَٻوجھ يا ھيڻو ھجي يا بيان نه ڪري سگھي ته سندس سنڀاليندڙ کي اِنصاف سان بيان ڪرڻ گھرجي، ۽ پنھنجن (مسلمانن) مڙسن مان ٻه گواھ شاھد ڪريو، پوءِ جيڪڏھن ٻه مڙس نه ھجن ته ھڪ مڙس ۽ ٻه زالون (اُنھن) شاھدن مان جيڪي اوھان کي وڻن (شاھد ڪريو ھن لاءِ) ته جي اُنھن (ٻنھي) مان ھڪڙي وساريندي ته اُنھن مان ٻي کيس ياد ڏياريندي، ۽ جڏھن شاھد (شاھديءَ ڏانھن) سڏيا وڃن ته انڪار نه ڪن، ۽ اُن جي مدت تائين ٿوري يا گھڻي (قرض) جي لکي ڏيڻ کان نه ٿڪجو، اِھو (لکڻ) الله وٽ انصاف کي تمام ويجھو ۽ شاھديءَ لاءِ بلڪل بيھڪ وارو آھي ۽ اوھان جي شڪ ۾ نه پوڻ کي (به) ڏاڍو ويجھو آھي پر جيڪڏھن اوھان جو واپار رُوبرو ھجي جو اوھين ھٿون ھٿ پاڻ ۾ پيا ڪريو ته اُن جي نه لکڻ ۾ اوھان تي ڪو گناھ نه آھي، ۽ جڏھن ڳنھو وڪڻو ته شاھد ڪريو، ۽ لکندڙ ۽ شاھد کي نه ڏکوئجي، ۽ جيڪڏھن (اھڙو ڪم) ڪندؤ ته اُھو اوھان کي گُناہ آھي، ۽ الله کان ڊڄو، ۽ الله اوھان کي سيکاري ٿو، ۽ الله سڀڪنھن شيء کي ڄاڻندڙ آھي.'),
(290, 2, 283, '۽ جيڪڏھن اوھين مسافريءَ ۾ ھُجو ۽ لکندڙ نه لھو ته ھٿڻ گروي وٺڻ لازم آھي، پوءِ جيڪڏھن اوھان مان ھڪڙا ٻـين تي اعتبار رکن ته جنھن تي اعتبار رکيو ويو تنھن کي پنھنجي امانت (يعني قرض) ادا ڪرڻ گھرجي ۽ پنھنجي پالڻھار الله کان ڊڄڻ گھرجي، ۽ شاھدي نه لڪايو، ۽ جيڪو اُن کي لڪائيندو ته بيشڪ سندس دل ڏوھارڻ آھي، ۽ جيڪي اوھين ڪندا آھيو سو الله ڄاڻندڙ آھي.'),
(291, 2, 284, 'جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سا الله جي ئي (ملڪيت) آھي، ۽ جيڪي اوھان جي دلين ۾ آھي سو جيڪڏھن پڌرو ڪريو توڻي اُھو لڪايو (ته به) الله اوھان سان اُن جو حساب ڪندو، پوءِ جنھن لاءِ گھرندو تنھن کي بخشيندو، ۽ جنھن لاءِ گھرندو تنھن کي عذاب ڪندو، ۽ الله سڀڪنھن شيءِ تي وس وارو آھي.'),
(292, 2, 285, 'پيغمبر (ﷴ ﷺ) ڏانھن جيڪي سندس پالڻھار کان لاٿو ويو سو اُن مڃيو آھي ۽ مؤمنن (به)، سڀڪنھن الله ۽ سندس ملائڪن ۽ سندس ڪتابن ۽ سندس رسولن کي مڃيو آھي، (چون ٿا ته) سندس پيغمبرن مان ڪنھن ھڪ (جي مڃڻ) ۾ فرق نه ڪندا آھيون، ۽ چيائون ته ٻڌوسون ۽ سندس (حُڪم) مڃيوسون، اي اسان جا پالڻھار تنھنجي بخشش گھرون ٿا ۽ تو ڏانھن موٽڻو آھي.'),
(293, 2, 286, 'الله ڪنھن کي سندس وس ڌاران تڪليف نه ڏيندو آھي، جيڪي (چڱو) ڪمايائين سو سندس (فائدي) لاءِ آھي ۽ جيڪي (بڇڙو) ڪمايائين سو مٿس آھي، اي اسان جا پالڻھار جيڪڏھن وساريون يا چوڪ ڪريون ته اسان کي نه پڪڙ، ۽ اي اسان جا پالڻھار اسان تي اھڙو بار نه رک جھڙو اسان کان اڳين تي رکيو ھيئي، ۽ اي اسان جا پالڻھار (جنھن بار) جي کڻڻ جي اسان کي سگھ ڪانھي سو اسان تي نه رک، ۽ اسان کي معاف ڪر، ۽ اسان کي بخش، ۽ اسان تي مِھر ڪر، تون اسان جو سائين آھين تنھنڪري اسان کي ڪافر ٽوليءَ تي سوڀ ڏي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الٓمَّٓ.'),
(295, 3, 2, 'الله (اُھو آھي جو) اُن کانسواءِ ڪوئي عبادت جو لائق نه آھي (جو سدائين) جيئرو (جڳ جي) بيھڪ ڏيڻ وارو آھي.'),
(296, 3, 3, 'توتي (اُھو) ڪتاب سچ سان لاٿائين (جو) جيڪي (ڪتاب) کانئس اڳي آھن تن جو سچو ڪندڙ آھي ۽ توريت ۽ اِنجيل کي لاٿائين.'),
(297, 3, 4, 'اڳي ئي ماڻھن جي ھدايت لاءِ ۽ (سچ ۽ ڪُوڙ ۾) سنڌو وجھندڙ لاٿائين، جن الله جي آيتن جو اِنڪار ڪيو تن لاءِ سخت عذاب آھي، ۽ الله غالب بدلي وٺڻ وارو آھي.'),
(298, 3, 5, 'بيشڪ الله تي ڪائي شيء نڪي زمين ۾ ۽ نڪي آسمان ۾ ڳجھي رھندي آھي.'),
(299, 3, 6, 'اُھو (الله) اھو آھي جيڪو (مائن) ڳڀيرڻن ۾ جئن گھرندو آھي (تئن) اوھان جي شڪل ٺاھيندو آھي، اُن کانسواءِ ڪوئي عبادت جو لائق نه آھي (اُھوئي) غالب حِڪمت وارو آھي.'),
(300, 3, 7, 'اُھو (الله) آھي جنھن توتي ڪتاب لاٿو جنھن مان ڪي آيتون مُحڪم آھن (يعني اُنھن جي معنىٰ صاف آھي) اُھي ڪتاب جو اصل آھن ۽ ٻيون متشابه آھن (يعني اُنھن جون معنائون گھڻيون ٿي سگھن ٿيون)، پوءِ جن دلين ۾ ڏنگائي آھي سي مَنجھانئس جيڪي مُتشابه آھن تن جي پُٺيان خرابي ڳولڻ ۽ سندس مراد ڳولڻ لاءِ لڳندا آھن، ۽ الله کانسواءِ اُن جي مُراد ڪوئي نه ڄاڻندو آھي، ۽ (جيڪي) علم ۾پڪا (آھن سي) چوندا آھن ته اُنھن کي مڃيوسُون اِھي مڙئي اسان جي پالڻھار وٽان آھن، ۽ ڌيان وارن کانسواءِ (ٻيو) ڪوبه نصيحت نه وٺندو آھي.'),
(301, 3, 8, 'اي اسان جا پالڻھار جڏھن تو اسان کي ھدايت ڪئي تنھن کان پوءِ اسان جي دلين کي ڏنگائي نه ڏي ۽ پاڻ وٽان اسان کي ٻاجھ عطا ڪر، ڇوته تون ئي بخشڻھار آھين.'),
(302, 3, 9, 'اي اسان جا پالڻھار تون اُن ڏينھن ۾ ماڻھن کي گڏ ڪندڙ آھين جنھن ۾ ڪو شڪ نه آھي، ڇوته الله (پنھنجي) انجام کي نه ڦيرائيندو آھي.'),
(303, 3, 10, 'بيشڪ جن ڪفر ڪيو تن کان ڪڏھن نه سندن مال ۽ نڪي سندن اولاد الله (جي عذاب) مان ڪجھ نه ٽاريندو، ۽ اِھي دوزخ جو ٻارڻ آھن.'),
(304, 3, 11, '(اُنھن جو حال) فرعونين جي ۽ اُنھن جي حال جھڙو آھي جي کانئن اڳ ھؤا، (اُنھن) اسان جي آيتن کي ڪُوڙو ڀانيو، پوءِ الله سندن گناھن سببان کين پڪڙيو، ۽ الله سخت سزا ڏيندڙ آھي.'),
(305, 3, 12, '(اي پيغمبر) ڪافرن کي چؤ ته سگھو ھيڻا ٿيندؤ ۽ دوزخ ڏانھن گڏ ڪيا ويندؤ، ۽ (اُھو) بڇڙو ھنڌ آھي.'),
(306, 3, 13, 'اُنھن ٻن ٽولين جي (پاڻ ۾) ويڙھ ڪرڻ ۾ اوھان لاءِ بيشڪ نشاني آھي، ھڪڙي ٽولي الله جي واٽ ۾ وڙھي ۽ ٻي (ڪافرن جي) ھئي (مسلمانن) کين پنھنجو ٻيڻو اکين جي ڏيٺ سان ڏٺو ٿي، ۽ الله جنھن کي وڻيس (تنھن کي) پنھنجيءَ مدد سان سگھ ڏيندو آھي، ڇوته اُن ۾ (سمجھ جي) اکين وارن لاءِ ضرور عبرت آھي.'),
(307, 3, 14, 'ماڻھن لاءِ زالن ۽ پُٽن ۽ سونَ ۽ چانديءَ جي (وڏن وڏن) سٿيلن ڍيرن ۽ نشان ڪيلن گھوڙن ۽ ڍورن ۽ پوک جي سڌن جو پيار سينگاريو ويو آھي، اِھو دنيا جي حياتيءَ جو سامان آھي، ۽ الله وٽ چڱو موٽڻ جو ھنڌ آھي.'),
(308, 3, 15, '(اي پيغمبر کين) چؤ ته آءٌ ان کان ڀليءَ (شيء) جي سڌ ڏيان؟ جن (شرڪ کان) پاڻ بچايو تن لاءِ سندن پالڻھار وٽ بھشت آھن جن جي ھيٺان واھيون وھنديون آھن منجھن سدائين رھڻ وارا آھن ۽ پاڪ زالون ۽ (پڻ) الله جي پاران راضپو آھي، ۽ الله ٻانھن کي ڏسندڙ آھي.'),
(309, 3, 16, 'جيڪي چوندا آھن ته اي اسان جا پالڻھار اسان توکي مڃيو آھي تنھنڪري اسان جا ڏوھ اسان کي بخش ۽ باہ جي عذاب کان اسان کي بچاءِ.'),
(310, 3, 17, 'اُھي صابر ۽ سَچا ۽ تابعداري ڪندڙ ۽ سخي ۽ پرڀاتين جو بخشش گھرندڙ آھن.'),
(311, 3, 18, 'الله (ھي) گواھي ڏني آھي ته الله کانسواءِ ڪوئي عبادت جو لائق نه آھي، ۽ مَلائِڪ ۽ علم وارا (به) انصاف تي بيھڪ وارا ٿي (اِھا شاھدي ڏيندا آھن)، اُن کانسواءِ ڪوئي عبادت جو لائق نه آھي اُھو غالب حِڪمت وارو آھي.'),
(312, 3, 19, 'بيشڪ وڻندڙ دين الله وَٽ اِسلام آھي، ۽ جن کي ڪتاب (توريت ۽ انجيل) ڏنو ويو تن (اڳ) تڪرار نه ڪيو پر وٽن علم آيو تنھن کانپوءِ پاڻ ۾ ضد سببان (تڪرار ڪيائون)، ۽ جيڪو الله جي حڪمن کي نه مڃيندو ته بيشڪ الله (به) سگھو حساب وٺندڙ آھي.'),
(313, 3, 20, 'پوءِ جيڪڏھن توسان جھيڙين ته چؤ پنھنجو مُنھن (يعني سر) مُون ۽ جنھن منھنجي تابعداري ڪئي تنھن الله لاءِ نِوايو آھي، ۽ (اي پيغمبر) جن کي ڪتاب (توريت ۽ انجيل) ڏنو ويو تن کي ۽ اڻ پڙھيلن کي چؤ ته اسلام آندو اَٿوَ؟ (يا نه) پوءِ جيڪڏھن اسلام آڻين ته بيشڪ سِڌيءَ واٽ وارا ٿيا، ۽ جيڪڏھن ڦري وڃن ته توتي رڳو پيغام پُھچائڻ آھي، ۽الله ٻانھن کي ڏسندڙ آھي.'),
(314, 3, 21, 'بيشڪ جيڪي الله جي آيتن کي نٿا مڃين ۽ پيغمبرن کي ناحق ٿا ڪُھن ۽ ماڻھن مان جيڪي (کين) اِنصاف جو حُڪم ڪن ٿا تن کي ٿا ڪُھن پوءِ کين ڏکوئيندڙ عذاب جي سُڌ ڏي.'),
(315, 3, 22, 'اِھي (اُھي) آھن جن جي ڪمائي دُنيا ۽ آخرت ۾ ناس ٿي ويئي، ۽ اُنھن جو ڪو واھرو ڪونھي.'),
(316, 3, 23, '(اي پيغمبر) جن کي ڪتاب مان ڪو ڀاڱو ڏنو ويو تن کي نه ڏٺو اَٿئي ڇا؟ (جو) الله جي ڪتاب ڏانھن (ھن لاءِ) سڏيا وڃن ٿا ته سندن وچ ۾ نبيرو ڪري پوءِ منجھانئن ھڪ ٽولي ڦري ويندي آھي ۽ اُھي مُنھن موڙيندڙ (به) آھن.'),
(317, 3, 24, 'اِھو ھِن سببان آھي جو اُھي چون ٿا ته باھ ڪن ڳاڻوَن ڏينھن کانسواءِ اسان کي ڪڏھن نه ڇھندي، ۽ جيڪو ٺاھ ٺاھيندا رھيا تنھن کين سندن دين ۾ ٺڳيو آھي.'),
(318, 3, 25, 'پوءِ ڪھڙو (حال ٿيندن) جڏھن کين اُھو ڏينھن جنھن ۾ ڪو شڪ نه آھي تنھن ۾ گڏ ڪنداسين، ۽ سڀڪنھن ماڻھوءَ جيڪي ڪمايو تنھن جو کيس پُورو بَدلو ڏبو ۽ اُنھن سان ظلم نه ڪبو.'),
(319, 3, 26, '(اي پيغمبر) چؤ ته اي الله مُلڪ جا مالِڪ جنھن کي گھرين تنھن کي بادشاھي ڏئين ۽ جنھن کان وڻيئي تنھن کان بادشاھي کسين، ۽ جنھن کي گھرين تنھن کي مانوارو ڪرين ۽ جنھن کي وڻيئي تنھن کي بي مانو ڪرين، تنھنجي ھٿ ۾ چڱائي آھي، ڇوته تون سڀڪنھن شيء تي وسوارو آھين.'),
(320, 3, 27, 'رات کي (گھٽائي) ڏينھن آڻين ٿو ۽ ڏينھن کي (گھٽائي) رات ۾ آڻين ٿو، ۽ مُئي مان جِيئري کي ڪڍين ٿو ۽ جيئري مان مُئي کي ڪڍين ٿو، ۽ جنھن کي گھُرين تنھن کي اَڻ ڳڻي روزي ڏيندو آھين.'),
(321, 3, 28, '(گھُرجي ته) مُؤمن مُؤمنن کانسواءِ ڪافرن کي دوست نه بڻائين، ۽ جيڪو اِئين ڪندو تنھن جو الله (جي دين) سان (ڪُجھ) واسطو نه آھي ھِن کانسواءِ ته اِنھن (ڪافرن) کان پاڻ بچائڻ لاءِ بچاءُ ڪريو، ۽ الله پاڻ کان اوھان کي ڊيڄاريندو آھي، ۽ الله ڏانھن موٽڻو آھي.'),
(322, 3, 29, '(اي پيغمبر کين) چؤ ته جيڪي اوھان جي دلين ۾ آھي سو جي لڪائيندؤ يا اُھو پڌرو ڪندؤ ته (به) اُن کي الله ڄاڻندو آھي، ۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي (سوبه) ڄاڻندو آھي، ۽ الله سڀ ڪنھن شيءِ تي وسوارو آھي.'),
(323, 3, 30, 'اُنھيءَ ڏينھن سڀڪو (ماڻھو) جيڪو چڱو ڪم ڪيائين ۽ جيڪو بڇڙو ڪم ڪيائين تنھن کي آڏو لھندو، (بڇڙي ڪم کي ڏسي) گھرندو ته جيڪر سندس ۽ اُن جي وچ ۾ گھڻي ڊيگھ ھجي ھا، ۽ الله پاڻ کان اوھان کي ڊيڄاريندو آھي، ۽ الله ٻانھن تي گھڻو مھربان آھي.'),
(324, 3, 31, '(اي پيغمبر) چؤ ته جي اوھين الله کي دوست رکندا آھيو ته منھنجي تابعداري ڪريو ته الله اوھان کي دوست رکندو ۽ اوھان جا گناھ اوھان کي بخشيندو، ۽ الله بخشڻھار مھربان آھي.'),
(325, 3, 32, '(اي پيغمبر) چؤ ته الله ۽ پيغمبر جي فرمانبرداري ڪريو، پوءِ جيڪڏھن ڦري ويا ته بيشڪ الله (به) ڪافرن کي دوست نه رکندو آھي.'),
(326, 3, 33, 'بيشڪ الله آدم ۽ نُوح ۽ ابراھيم جي اولاد ۽ عِمران جي اولاد کي جھانن (وارن) تي سدورو ڪيو آھي.'),
(327, 3, 34, 'جو پاڻ ۾ ھڪ ٻئي جو نسل آھن، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(328, 3, 35, 'جڏھن عِمران جي زال چيو ته اي مُنھنجا پالڻھار جيڪي مُنھنجي پيٽ ۾ آھي سو تولاءِ آجو ڪيل نذر ڪيم تنھنڪري مون کان قبول ڪر، ڇوته تون ئي ٻڌندڙ ڄاڻندڙ آھين.'),
(329, 3, 36, 'پوءِ جنھن مھل اُن کي ڄڻيائين تڏھن چيائين ته اي مُنھنجا پالڻھار مون ڌيءُ ڄڻي، ۽ جيڪي اُن (بي بيءَ) ڄڻيو تنھن کي الله چڱو ڄاڻندڙ آھي، ۽ (چيائين ته) پُٽ ڌيءُ جھڙو نه آھي، ۽ مون سندس نالو مريم رکيو ۽ آءٌ اُن کي ۽ سندس نسل کي تڙيل شيطان (جي شر) کان تنھنجي پناھ ۾ سونپيان ٿي.'),
(330, 3, 37, 'پوءِ اُن کي سندس پالڻھار چڱي قبولڻ سان قبوليو ۽ اُن کي چڱي پالڻ طرح پاليائين ۽ سندس سنڀاليندڙ زڪريا کي ڪيائين، جڏھن ڪڏھن زڪريا مٿس حُجري ۾ لنگھندو ھو ته وٽس طعام (رکيل) لھندو ھو، (ھڪ وقت) چيائين ته اي مريم ھيءُ ڪٿان تولاءِ (آيو) آھي چيائين ته اِھو الله وٽان (آيو) آھي، چوته الله جنھن کي وڻيس (تنھن کي) اَڻ ڳڻي روزي ڏيندو آھي.'),
(331, 3, 38, 'اُتي زڪريا پنھنجي پالڻھار کي سڏيو، چيائين اي منھنجا پالڻھار پاڻ وٽان مون کي چڱو اولاد بخش، ڇو ته تون دُعا جو ٻڌندڙ آھين.'),
(332, 3, 39, 'پوءِ مَلائڪن سڏيس ۽ اُن مَسجد ۾ بيٺي نماز پڙھي ٿي (ملائڪن چيس ته) الله توکي يحيىٰ (جي ڄمڻ) جي خوشخبري ڏئي ٿو جو الله جي حُڪم (سان پيدا ٿيل عيسىٰ) جو سَچو ڪندڙ ۽ سردار ۽ جَتو سَتو ۽ صالحن مان پيغمبر آھي.'),
(333, 3, 40, 'چيائين ته اي مُنھنجا پالڻھار مون کي پُٽ ڪئن ٿيندو جو بيشڪ مُون کي ته پوڙھائي اچي رسي آھي ۽ مُنھنجي زال سَنڍ آھي، (ملائڪ) چيو ته الله جيڪي گھرندو آھي سو اھڙيءَ طرح ڪندو آھي.'),
(334, 3, 41, 'چيائين ته اي منھنجا پالڻھار مُون کاءِ ڪا نشاني مُقرّر ڪر، (الله) فرمايو ته تنھنجي نشاني (ھيءَ) آھي ته ٽي ڏينھن اشاري کانسواءِ ماڻھن سان ڳالھائي نه سگھندين، ۽ پنھنجي پالڻھار کي گھڻو ياد ڪر ۽ صُبح جو ۽ سانجھيءَ جو ساراھِ.'),
(335, 3, 42, '۽ (ياد ڪر) جڏھن ملائڪن چيو ته اي مريم الله توکي سڳورو ڪيو ۽ توکي پاڪ ڪيائين ۽ توکي جھانن جي زالن تي سڳورو ڪيائين.'),
(336, 3, 43, 'اي مريم پنھنجي پالڻھار جي فرمانبرداري ڪر ۽ سجدو ڪر ۽ رُڪُوع ڪندڙن سان گڏجي رُڪُوع ڪر.'),
(337, 3, 44, 'اِھا ڳجھين خبرن مان آھي اُن کي توڏانھن وحي ڪيوسون، ۽ (تون) تڏھن وٽن نه ھئين جڏھن پنھنجا قلم (پُکن لاءِ واھيءَ ۾) وڌائون ٿي ته اُنھن مان ڪير مريم کي سنڀاليندو، ۽ تڏھن به وٽن نه ھئين جڏھن تڪرار ڪندا رھيا.'),
(338, 3, 45, 'جڏھن ملائڪن مريم کي چيو ته اي مريم الله پاڻ وٽان توکي ھڪ (خاص) حُڪم (سان پيدا ڪيل پٽ) جي خوشخبري ڏئي ٿو، سندس نالو عِيسىٰ پُٽ مريم جو مرتبي وارو دنيا ۽ آخرت ۾ ٿيندو ۽ ويجھن مان آھي.'),
(339, 3, 46, '۽ پينگھي ۾ (خاص طرح) ۽ جوانيءَ ۾ ماڻھن سان ڳالھائيندو ۽ صالحن مان آھي.'),
(340, 3, 47, 'چيائين ته اي مُنھنجا پالڻھار مون کي ڪئن وياءُ ٿيندو؟ جو مون کي (ڪنھين) ماڻھوءَ نه ڇھيو آھي، (ملائڪن) چيو ته الله جيڪي گھرندو آھي سو اھڙي طرح پيدا ڪندو آھي، جڏھن ڪو ڪم ڪندو آھي ته اُن لاءِ ڇڙو چوندو آھي ته ٿيءُ ته ٿي پوندو آھي.'),
(341, 3, 48, '۽ کيس ڪتاب ۽ دانائي ۽ توريت ۽ انجيل سيکاريندو.'),
(342, 3, 49, '۽ (اُن کي) بني اسرائيلن ڏانھن پيغمبر ڪري موڪليندو، (عيسىٰ وڏو ٿيو ۽ چيائين ته) مون اوھان جي پالڻھار وٽان نشاني آندي آھي، ته آءٌ اوھان لاءِ مٽيءَ مان پکيءَ جي شڪل جھڙو (بوتو) بڻائيندس پوءِ منجھس ڦوڪيندس ته الله جي حُڪم سان پکي ٿيندو، ۽ (ماءُ ڄاول) اَنڌي ۽ ڪُوڙھي کي ڇُٽائيندس ۽ الله جي حُڪم سان مُئي کي جياريندس، ۽ جيڪي کائيندا آھيو ۽ جيڪي پنھنجن گھرن ۾ سانڀيندا آھيو سو اوھان کي ڏسيندس، جيڪڏھن اوھين مڃيندڙ آھيو ته اوھان لاءِ اُن ۾ نشانيون آھن.'),
(343, 3, 50, '۽ جيڪو مون کان اڳ توريت (جو ڪتاب) آيل آھي تنھن جو سچو ڪندڙ آھيان ۽ جيڪي (شيون) اوھان تي حرام ڪيل آھن تن مان ڪي اوھان لاءِ حلال ڪندس ۽ اوھان جي پالڻھار وٽان اوھان لاءِ نشانيون آنديون اٿم، پوءِ الله کان ڊڄو ۽ مُنھنجي تابعداري ڪريو.'),
(344, 3, 51, 'بيشڪ الله مُنھنجو پالڻھار ۽ اوھان جو پالڻھار آھي تنھنڪري سندس عبادت ڪريو، اھا سنئين واٽ آھي.'),
(345, 3, 52, 'پوءِ جنھن مھل عيسىٰ کائن ڪُفر (جا ڪم) اکين سان ڏٺا (تنھن مھل) چيائين ته الله (جي دين) ڏانھن مُنھنجا واھرو ڪير آھن؟ حوارين چيو ته اسين الله (جي دين) جا واھرو آھيون، الله کي مڃيوسون، ۽ شاھد ھُج ته اسين پَڪ مُسلمان آھيون.'),
(346, 3, 53, '(چيائون) اي اسانجا پالڻھار جيڪي تو لاٿو سو مڃيوسون ۽ پيغمبر جي تابعداري ڪئي سون تنھنڪري اسانکي شاھدن ۾ لک.'),
(347, 3, 54, '۽ ((يھودين) رٿ رٿي ۽ الله به (ڳجھي) رٿ رٿي، ۽ الله چڱو (ڳجھي) رٿ رٿيندڙ آھي.'),
(348, 3, 55, '(ياد ڪر) جڏھن الله چيو ته اي عيسىٰ آءٌ توکي ماريندڙ ۽ پاڻ ڏانھن مٿاھون ڪندڙ آھيان ۽ جن ڪُفر ڪيو تن (جي بھتانن) کان توکي پاڪ ڪندڙ آھيان ۽ جن تنھنجي تابعداري ڪئي تن کي قيامت تائين ڪافرن تي مٿاھون (غالب) ڪندڙ آھيان، وري مون ڏانھن اوھان جو موٽڻ آھي پوءِ اوھان جي وچ ۾ جنھن ڳالھ بابت اوھين تڪرار ڪندا آھيو (تنھن بابت) نبيرو ڪندس.'),
(349, 3, 56, 'پوءِ (اُنھن مان) جن ڪُفر ڪيو تن کي دُنيا ۽ آخرت ۾ بلڪل سخت سزا ڏيندس، ۽ اُنھن جو ڪو واھرو نه آھي.'),
(350, 3, 57, '۽ (اُنھن مان) جن ايمان آندو۽ چڱا ڪم ڪيا تن کي (الله) سندن پورھيو پُورو ڏيندو، ۽ الله ظالمن کي دوست نه رکندو آھي.'),
(351, 3, 58, 'اھو (قصّو) جو توتي پڙھون ٿا سو نشانين ۽ حِڪمت واري ڪتاب مان آھي.'),
(352, 3, 59, 'الله وٽ عيسىٰ (جي پيدا ڪرڻ) جو مثال آدم جي مثال جھڙوئي آھي، اُن (آدم) کي مِٽيءَ مان بڻايائين وري اُن چيائين ته ٿي پؤ ته ٿي پيو.'),
(353, 3, 60, '(اِھا ڳالھ) تنھنجي پالڻھار وٽان سچي آھي تنھنڪري شڪ ڪندڙن مان نه ھُج.'),
(354, 3, 61, 'پوءِ اُن پڄاڻا جو تو وٽ ڄاڻپ آئي جيڪو توسان اِن بابت جھڳڙو ڪري ته چؤ ته اچو ته (اسين) پنھنجن پُٽن ۽ اوھان جي پُٽن ۽ پنھنجين زالن ۽ اوھان جي زالن ۽ پاڻ کي ۽ اوھان کي سڏيون، پوءِ زاريءَ سان دُعا گھرون پوءِ الله جي لَعنت ڪُوڙن تي ڪريون.'),
(355, 3, 62, 'اِھو قِصو ضرور سَچّو آھي، ۽ الله کانسواءِ ڪوبه عبادت جو لائق نه آھي، ۽ الله ئي غالب حِڪمت وارو آھي.'),
(356, 3, 63, 'پوءِ جيڪڏھن ڦرن ته بيشڪ الله به فسادين کي ڄاڻندڙ آھي.'),
(357, 3, 64, '(اي پيغمبر) چؤ ته اي ڪتاب وارؤ ھڪ ڳالھ ڏانھن اچو جا اسان ۽ اوھان جي وچ ۾ ھڪ جھڙي آھي ته الله کانسواءِ ڪنھن جي عبادت نه ڪريون ۽ نڪي ساڻس ڪنھن شيء کي شريڪ ڪريون ۽ نڪي اسان مان ڪو ڪنھن کي (به) الله ڌاران پالڻھار ڪري مڃي، پوءِ جيڪڏھن ڦرن ته چؤ ته شاھد ھُجو ته اسين پڪ مُسلمان آھيون.'),
(358, 3, 65, 'اي ڪتاب وارؤ ابراھيم بابت ڇالاءِ جھڳڙو ڪريو ٿا؟ ۽ توريت ۽ انجيل نه لاٿا ويا پر کانئس پوءِ، پوءِ اوھين نه سمجھندا آھيو ڇا؟'),
(359, 3, 66, '(اي يھوديؤ) اوھين اُھي آھيو جو جنھن شيء جي اوھان کي ڄاڻپ آھي تنھن بابت ته جھڳڙو ڪندا آھيو پر جي اوھان کي ڪا ڄاڻپ آھي ئي نه تنھن بابت ڇو جھڳڙو ڪندا آھيو؟ ۽ الله ڄاڻندو آھي ۽ اوھين نه ڄاڻندا آھيو.'),
(360, 3, 67, 'ابراھيم نڪو يھودي ھو ۽ نڪي نصراني پر حَنيف (ھڪ طرفو) مُسلمان ھو، ۽ مُشرڪن مان نه ھو.'),
(361, 3, 68, 'ابراھيم کي اُھي ماڻھو ويجھا آھن جن سندس تابعداري ڪئي ۽ ھيءُ پيغمبر ۽ جن مٿس ايمان آندو، ۽ الله مؤمنن جو سنڀاليندڙ آھي.'),
(362, 3, 69, 'ڪتاب وارن مان ڪا ٽولي گھرندي آھي ته جيڪر اوھان کي ڀُلائين، ۽ اُھي پاڻ کانسواءِ (ٻئي) ڪنھن کي نه ڀلائيندا آھن ۽ نه پروڙيندا آھن.'),
(363, 3, 70, 'اي ڪتاب وارؤ الله جي آيتن کي ڇالاءِ نه ٿا مڃو؟ ۽ حالانڪ اوھين شاھدي ڏيو ٿا.'),
(364, 3, 71, 'اي ڪتاب وارؤ ڇالاءِ سچ کي ڪُوڙ سان ملائيندا آھيو ۽ سچ کي لڪائيندا آھيو؟ ھن ھوندي (به) جو اوھين ڄاڻندا آھيو.'),
(365, 3, 72, '۽ ڪتاب وارن مان ڪا ٽولي (پاڻ ۾) چوي ٿي ته جيڪي مؤمنن تي لاٿو ويو آھي تنھن کي ڏينھن جي اڳياڙيءَ ۾ مڃيو ۽ ان جي پوياڙيءَ ۾ انڪار ڪريو ته مانَ اُھي ڦري وڃن.'),
(366, 3, 73, '۽ جيڪو اوھان جي دين جي تابعداري ڪري تنھن کانسواءِ ٻئي ڪنھن کي نه مڃيو، چئو ته ھدايت الله واري ھدايت آھي ۽ چون ٿا ته ھيءَ به نه مڃيو ته جھڙو (دين) اوھان کي ڏنو ويو آھي تھڙو دين اُنھن کي ڏنو ويندو يا اوھان جي پالڻھار وٽ اوھان کي الزام ڏيندا، چؤ ته ڀلائي الله جي ھٿ ۾ آھي، جنھن کي وڻيس تنھن کي اُھا ڏيندو آھي، ۽ الله (مھربانيءَ ۾) ڪشادگيءَ وارو ڄاڻندڙ آھي.'),
(367, 3, 74, 'جنھن کي وڻيس تنھنکي پنھنجي ٻاجھه سان خاص ڪندو آھي، ۽ الله وڏي فضل وارو آھي.'),
(368, 3, 75, '۽ ڪتاب وارن مان ڪي اھڙا آھن جو جيڪڏھن وٽن تمام گھڻو مال امانت رکين ته اُھو (سمورو) توکي ورائي ڏيندا، ۽ منجھائن ڪي اھڙا آھن جو جيڪڏھن وٽن ھِڪ مُھر امانت رکين ته مٿن سدائين بيھڻ کانسواءِ توکي ورائي نه ڏيندا، اِھو (نه ڏئڻ) ھن سببان آھي جو چون ٿا ته اڻ پڙھيلن (جي مال کسڻ) ۾ اسان تي ڪا پڪڙ پڇاڙ نه آھي، ۽ الله تي اھي ڄاڻڻ ھوندي به ڪُوڙ چوندا آھن.'),
(369, 3, 76, 'ھائو جنھن پنھنجو انجام پاڙيو ۽ ڊنو ته بيشڪ الله ڊڄندڙن کي دوست رکندو آھي.'),
(370, 3, 77, 'جيڪي الله سان ڪيل انجامن ۽ پنھنجن قسمن جي عوض ٿورو مُلھ وٺندا آھن اِھي (اُھي) آھن جن کي آخرت ۾ ڪو بھرو نه آھي ۽ نڪي قيامت جي ڏينھن الله ساڻن ڳالھائيندو ۽ نڪي اُنھن ڏانھن نھاريندو ۽ نڪي کين (گناھن کان) پاڪ ڪندو، ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(371, 3, 78, '۽ منجھائن ھڪ ٽولي آھي جا ڪتاب (پڙھڻ) ۾ پنھنجين زبانن کي ھن لاءِ وچڙائيندي آھي ته ان کي اوھين ڪتاب مان ڀانيو ۽ (حقيقت ڪري) اُھو ڪتاب مان نه آھي، ۽ (پڻ) چوندا آھن ته اُھو الله وٽان آھي ۽ (حقيقت ڪري) اُھو الله وٽان نه آھي، ۽ اُھي ڄاڻڻ ھوندي به الله تي ڪُوڙ چوندا آھن.'),
(372, 3, 79, 'ڪنھن به ماڻھوءَ کي نه جڳائيندو آھي جو الله کيس ڪتاب ۽ دانائي ۽ پيغمبري ڏئي اُھو وري ماڻھن کي چوي ته الله کانسواءِ مُنھنجا ٻانھا ٿيو پر (چوندو ته) رب جا (سچا ٻانھان) ٿيو انھيءَ ڪري جو ڪتاب سيکاريو ٿا ۽ پڻ انھيءَ ڪري جو پڙھو ٿا.'),
(373, 3, 80, '۽ ملائڪن ۽ پيغمبرن کي پالڻھار ڪري مڃڻ جو اوھان کي حڪم نه ڪندو، جڏھن اوھين خُدا جا فرمانبردار ٿيا ھجو ته پوءِ اوھان کي ڇو ڪُفر جو حُڪم ڪندو.'),
(374, 3, 81, '۽ (ياد ڪر) جڏھن الله (سڀني) پيغمبرن کان انجام ورتو ته جيڪي ڪتاب ۽ حڪمت اوھان کي ڏيان تنھن کانپوءِ ھڪ پيغمبر جيڪو (علم) اوھان وٽ آھي تنھنجو سچو ڪندڙ اوھان وٽ اچي ته کيس ضرور مڃجو ۽ کيس ضرور مدد ڏجو، (الله) چيو ته اوھين اقرار ڪري چڪؤ ۽ اُن تي مُنھنجي (حُڪم جي) بار کي کنيوَ، چيائون ته اقرار ڪيوسون، (الله) چيو ته اوھين شاھد ھجو، ۽ آءٌ (به) اوھان سان گڏ شاھد آھيان.'),
(375, 3, 82, 'پوءِ جيڪي ھن پڃاڻان ڦرندا سي ئي بي دين آھن.'),
(376, 3, 83, 'الله جي دين کانسواءِ ڇو ٻيو (دين) طلبيندا آھن ۽ جيڪي آسمانن ۽ زمين ۾ آھن تن سرھو توڙي ارھو ٿي کيس مڃيو آھي ۽ ڏانھس موٽايا ويندا.'),
(377, 3, 84, '(اي پيغمبر) چؤ ته اسان الله کي ۽ جيڪي اسان تي لاٿو ويو آھي تنھن کي ۽ جيڪي ابراھيم ۽ اسماعيل ۽ اسحٰق ۽ يعقوب ۽ (اُن جي) اولاد تي لاٿو ويو ۽ جيڪي موسٰي ۽ عيسٰي ۽ (ٻين) پيغمبرن کي سندن پالڻھار کان ڏنو ويو تنھن کي مڃيون ٿا، اُنھن مان ڪنھن ھڪ ۾ فرق نه ٿا ڀانيون، ۽ اسان اُن (الله) کي مڃيندڙ آھيون.'),
(378, 3, 85, '۽ جيڪو اسلام ڌاران ٻيو دين طلبيندو تنھن کان اُھو ڪڏھن نه قبول ڪبو، ۽ اُھو آخرت ۾ ڇيئي وارن مان آھي.'),
(379, 3, 86, 'الله اُن قوم کي ڪئن ھدايت ڪندو جي پنھنجي اِيمان آڻڻ کانپوءِ ڪافر ٿيا ۽ (پھريائين) اقرار ڪيائون ته پيغمبر سچو آھي ۽ وٽن معجزا (به) آيا، ۽ الله ظالم ٽولي کي ھدايت نه ڪندو آھي.'),
(380, 3, 87, 'اِھي اُھي آھن جن تي سزا (ھيءَ) آھي ته مٿن الله ۽ ملائڪن ۽ مڙني ماڻھن جي لَعنت آھي.'),
(381, 3, 88, 'اُن ۾ سدائين رھڻ وارا آھن، کانئن عذاب ھلڪو نه ڪبو ۽ نه اُنھن کي مُھلت ڏبي.'),
(382, 3, 89, 'پر جن اُن کانپوءِ توبه ڪئي ۽ (پاڻ) سڌاريائون، ته بيشڪ الله (به) بخشڻھار مھربان آھي.'),
(383, 3, 90, 'جن پنھنجي ايمان آڻڻ کانپوءِ ڪُفر ڪيو وري ڪُفر ۾ وڌيا تن جي توبه ڪڏھن قبول نه ڪبي، ۽ اُھي ئي ڀليل آھن.'),
(384, 3, 91, 'جن ڪُفر ڪيو ۽ اُھي ڪافر ٿي مُئا تن مان ڪو ھڪڙو اگرچه سڄيءَ زمين جيترو سونُ (پنھنجن گناھن جي) عوض ڏئي ته به اُھو اُن کان قبول نه ڪبو، اِھي (اُھي) آھن جن لاءِ ڏکوئيندڙ عذاب آھي ۽ اُنھن جو ڪو واھرو ڪونھي.'),
(385, 3, 92, 'چڱائيءَ کي ايسين ڪڏھن نه پھچندؤ جيسين جن شين کي دوست رکندا آھيو تن مان (الله جي واٽ ۾ نه) خرچيو، ۽ جيڪا شيء (الله لڳ خرچيو سا ڀَلي ھئڻ گھرجي) ڇوته الله اُن کي ڄاڻندڙ آھي.'),
(386, 3, 93, 'سڀ (ڪنھن قسم جو) طعام بني اسرائيلن لاءِ حلال ھو پر جيڪي يعقوب پاڻ تي حرام ڪيو ھو سو توريت جي لھڻ کان اڳ ھو، (اي پيغمبر) چؤ ته جيڪڏھن اوھين سچا آھيو ته توريت آڻيو پوءِ اُھو پڙھو.'),
(387, 3, 94, 'پوءِ جيڪي ھن کانپوءِ الله تي ڪوڙ ٺاھيندا سي ئي ظالم آھن.'),
(388, 3, 95, '(اي پيغمبر) چؤ ته الله سچ چيو آھي، پوءِ ابراھيم حنيف (ھڪ طرفي) جي دين جي تابعداري ڪريو، ۽ (ابراھيم) مشرڪن مان نه ھو.'),
(389, 3, 96, '(عبادت جو) پھريون گھرُ جو ماڻھن لاءِ بڻايو ويو سو اُھو ئي آھي جو مَڪي ۾ برڪت وارو ۽ جڳ لاءِ ھدايت آھي.'),
(390, 3, 97, 'جنھن ۾ پڌريون نشانيون آھن (ھڪڙي) مقام ابراھيم آھي (جو اُن ۾ ابراھيم جي پيرن جو نشان آھي)، ۽ جيڪو اُتي داخل ٿيو سو امن وارو آھي، ۽ الله لاءِ خانه ڪعبه جو حج اُنھن ماڻھن تي لازم آھي جنھن کي ڏانھس واٽ وڃڻ جي سگھه آھي، ۽ جنھن انڪار ڪيو (ته الله کي پرواہ نه آھي) ڇو ته الله جھانن کان بي پرواہ آھي.'),
(391, 3, 98, '(اي پيغمبر کين) چؤ ته اي ڪتاب وارؤ الله جي آيتن کي ڇونه ٿا مڃيو؟ ۽ (حقيقت ڪري) جيڪي ڪندا آھيو تنھن تي الله حاضر آھي.'),
(392, 3, 99, '(اي پيغمبر) چؤ ته اي ڪتاب وارؤ جيڪو ايمان آڻي تنھن کي الله جي واٽ کان اُن (واٽ) جي ڏنگائي گھُري ڇو ٿا جھليو؟ ھن ھوندي به جو اوھين ڄاڻندڙ آھيو، ۽ جيڪي (اوھين) ڪندا آھيو تنھن کان الله بي خبر نه آھي.'),
(393, 3, 100, 'اي ايمان وارؤ جيڪڏھن ڪتاب وارن مان ڪنھن به ٽوليءَ جي فرمانبرداري ڪندؤ ته اوھان جي ايمان آڻڻ کان پوءِ اوھان کي ڪافر ڪري ڦيرائيندا.'),
(394, 3, 101, '۽ (اوھين) ھن ھوندي ڪئن ڪافر ٿا ٿيو؟ جو الله جون آيتون اوھان تي پڙھجن ٿيون ۽ سندس پيغمبر اوھان ۾ آھي، ۽ جيڪو الله (جي دين) کي چنبڙي پٺندو تنھن کي بيشڪ سڌيءَ واٽ ڏانھن رستو ڏيکاريو ويندو.'),
(395, 3, 102, 'اي مؤمنؤ الله کان (اِئين) ڊڄو جئن کانئس ڊڄڻ جو حق آھي ۽ اوھين مسلمان ھجڻ کانسواءِ نه مرجو.'),
(396, 3, 103, '۽ الله جي رسيءَ (يعني قرآن) کي سڀئي چنبڙي وٺو ۽ ڌاروڌار نه ٿيو، ۽ پاڻ تي الله جي نعمت کي ياد ڪريو جو جڏھن پاڻ ۾ ويري ھيؤ پوءِ اوھان جي دلين ۾ ميلاپ وڌائين پوءِ سندس فضل سان ھڪ ٻئي جا ڦري ڀائر ٿيؤ، ۽ اوھين دوزخ جي کڏ جي ڀَر تي ھيؤ پوءِ اوھان کي کانئس ڇڏايائين، اھڙيءَ طرح الله اوھان لاءِ پنھنجيون نشانيون بيان ڪندو آھي ته مانَ اوھين ھدايت لھو.'),
(397, 3, 104, '۽ اوھان ۾ اھڙي ٽولي ھئڻ گھرجي جي چڱائيءَ ڏانھن سڏين ۽ چڱن ڪمن جو حُڪم ڪن ۽ مَدن ڪمن کان جھلين، ۽ اھي ئي ڇٽل آھن.'),
(398, 3, 105, '۽ اوھين اُنھن جھڙا نه ٿيو جي فرقو فرقو ٿي ويا ۽ وٽن معجزا آيا اُن کانپوءِ بهتڪرار ڪيائون، ۽ اُنھن لاءِ وڏو عذاب آھي.'),
(399, 3, 106, 'اُنھيءَ ڏينھن جو ڪي مُنھن اڇا ٿيندا ۽ ڪي مُنھن ڪارا ٿيندا، پوءِ جن جا مُنھن ڪارا ٿيندا (چيو ويندن ته) اوھين پنھنجي اِيمان (آڻڻ) کانپوءِ ڇو ڪافر ٿيؤ؟ پوءِ اُنھيءَ سببان جو اوھين ڪافر ٿيا ھيؤ عذاب (جو مزو) چکو.'),
(400, 3, 107, '۽ جن جا منھن اڇا ھوندا سي الله جي رحمت ھيٺ رھندا، اُھي منجھس سدائين رھڻ وارا ھوندا.'),
(401, 3, 108, '(اي پيغمبر) اِھي الله جون آيتون آھن جي (اسين) توتي سچيءَ طرح پڙھون ٿا، ۽ الله جھانن لاءِ ظُلم ڪرڻ نه گھرندو آھي.'),
(402, 3, 109, '۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي (سو خاص) الله جو آھي، ۽ (سڀ) ڪم الله ڏانھن موٽايا ويندا.'),
(403, 3, 110, 'اوھين اھڙي ڀَلي اُمت ماڻھن لاءِ پيدا ٿيل آھيو جو چڱن ڪمن جو حُڪم ڪندا آھيو ۽ مدن ڪمن کان جھليندا آھيو ۽ الله کي مڃيندا آھيو، ۽ جيڪڏھن ڪتاب وارا ايمان آڻين ھا ته ضرور کين ڀَلو ھُجي ھا، منجھانئن ڪي مؤمن آھن ۽ منجھانئن گھڻا بي دين آھن.'),
(404, 3, 111, '(اِھي زباني) اِيذاءُ کانسواءِ اوھان کي ڪڏھن ڏکوئي نه سگھندا، ۽ جيڪڏھن اوھان سان وڙھندا ته اوھان کي پُٺي ڏيئي ڀڄندا، وري مدد نه ڏبن.'),
(405, 3, 112, 'الله جي ذمي ۽ ماڻھن جي پناھ کانسواءِ جتي لڌا ويندا اُتي مٿن خواري ھنئي ويئي آھي ۽ الله جي ڏمر ھيٺ وري وريا ۽ مٿن محتاجي ھنئي ويئي آھي، اِھا (سزا) ھن سببان آھي جو الله جي آيتن کي نه ٿي مڃيائون ۽ پيغمبرن کي ناحق ٿي ڪُٺائون، اِھي (عادتون) اِنھيءَ سبب آھن جو بي فرماني ڪيائون ۽ حد کان لنگھندڙ ھئا.'),
(406, 3, 113, 'اِھي (سڀ) ھڪ جھڙا نه آھن، ڪتاب وارن مان ھڪڙي اھڙي سڌي ٽولي آھي جو الله جون آيتون رات جي وقتن ۾ پڙھندا آھن ۽ اُھي نماز (به) پڙھندا آھن.'),
(407, 3, 114, 'الله ۽ قيامت جي ڏينھن کي مڃيندا آھن ۽ چڱن ڪمن جو حڪم ڪندا آھن ۽ مَدن ڪمن کان جھليندا آھن ۽ چڱاين (يعني دين مڃڻ) ۾ اڳرائي ڪندا آھن، ۽ اِھي ڀلارن مان آھن.'),
(408, 3, 115, '۽ جيڪا ڪا به چڱائي ڪندا آھن تنھن جي بي قدري پٽيءَ نه ڪبي، ۽ الله پرھيزگارن کي ڄاڻندڙ آھي.'),
(409, 3, 116, 'جن اِنڪار ڪيو تن کان نڪي سندن مال سندن اولاد الله جو ڪُجھ (عذاب) ٽاري سگھندا، ۽ اُھي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.'),
(410, 3, 117, 'ھن دنيا جي حياتيءَ ۾ جيڪي (رياءُ لاءِ) خرچيندا آھن تنھن جو مثال اُنھيءَ واءُ جي مثال وانگر آھي جنھن ۾ پارو ھجي جو اُن ٽوليءَ جي پوکه کي پھچي جن پاڻ تي ظلم ڪيو پوءِ اُن (سندن پوکه) کي ناس ڪري، ۽ الله ساڻن ظلم نه ڪيو آھي پر اُھي پاڻ تي ظلم ڪن ٿا.'),
(411, 3, 118, 'اي اِيمان وارؤ پنھنجن (مسلمانن) کانسواءِ ٻين (قومن) کي ڳجھو دوست ڪري نه مڃيو (جو) اوھان جي خرابي ڪرڻ ۾ ڪا گھٽتائي نه ڪندا آھن، اوھان جي ڏکه کي دوست رکيائون، بيشڪ سندن واتن مان دشمني پڌري ٿي آھي، ۽ جيڪي (ضد) سندن سينن لڪايو آھي سو تمام وڏو آھي، بيشڪ اسان اوھان لاءِ نشانيون بيان ڪيون آھن جيڪڏھن سمجھندا آھيو.'),
(412, 3, 119, 'اوھين اُھي آھيو جو اُنھن کي دوست رکندا آھيو ۽ اُھي اوھان کي دوست نه رکندا آھن ۽ اوھين الله جي سڄي ڪتاب کي مڃيندا آھيو، ۽ جڏھن اوھان کي مِلن ٿا (تڏھن) چون ٿا ته مڃيوسين، ۽ جڏھن ھيڪلا ٿين ٿا (تڏھن) اوھان تي ڪاوڙ کان آڱرين کي چٻين ٿا، (اي پيغمبر کين) چؤ ته (اوھين) پنھنجيءَ ڪاوڙ سببان مرو، ڇوته الله سينن وارو (ڀيد) ڄاڻندو آھي.'),
(413, 3, 120, '(اي مؤمنؤ) جيڪڏھن اوھان کي ڪا چڱائي پھچندي آھي ته کين ڏکوئيندي آھي، ۽ جيڪڏھن اوھان کي ڪو ڏکه پھچندو آھي ته اُن سببان سرھا ٿيندا آھن، ۽ جيڪڏھن اوھين صبر ڪريو ۽ پرھيزگاري ڪريو ته سندن مڪر اوھان کي ڪجھ به نه ڏکوئيندو، ڇوته جيڪي ڪندا آھن تنھن کي الله گھيرو ڪندڙ آھي.'),
(414, 3, 121, '(اي پيغمبر ياد ڪر) جنھن مھل (تون) پنھنجي گھر مان سويرو نڪتين جو مؤمنن کي جنگ لاءِ مورچن تي وھاريئي ٿي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(415, 3, 122, '(ياد ڪر) جنھن مھل اوھان مان ٻن ٽولين بي ھمت ٿيڻ جو ارادو ڪيو ۽ الله سندن مددگار ھو، ۽ مؤمنن کي جڳائي ته الله تي ڀروسو ڪن.'),
(416, 3, 123, '۽ بيشڪ الله اوھان کي اُنھيءَ حالت ۾ جو اوھين بي زور ھيؤ بدر ۾ سوڀ ڏني، پوءِ الله کان ڊڄو مانَ اوھين شڪر ڪريو.'),
(417, 3, 124, '(اي پيغمبر اُنھيءَ مھل کي ياد ڪر) جنھن مھل تو مؤمنن کي چيو ٿي ته اوھان کي اوھان جي پالڻھار جي ٽن ھزارن ملائڪن (آسمان کان) لاٿلن سان مدد ڪرڻ ڪافي نه آھي ڇا؟'),
(418, 3, 125, 'ھائو جيڪڏھن اوھين صبر ڪريو ۽ پرھيزگار بڻجو ۽ ھڪدم اھي (ڪافر) اوھان تي اچي ڪڙڪن ته اوھان جو پاڻھار پنجن ھزارن ملائڪن نشان ڪيلن سان اوھان کي مدد ڏيندو.'),
(419, 3, 126, '۽ الله اُن (مدد) کي رڳو اوھان جي خوشخبريءَ لاءِ ڪيو آھي ۽ ھن لاءِ ته اوھان جون دليون اُن سان (ڀَلي ته) آرام وٺن، ۽ الله غالب حڪمت واري جي پار کانسواءِ ڪا مدد (ملڻي) نه آھي.'),
(420, 3, 127, '(۽) ھن لاءِ ته ھڪڙو پاسو (يعني ڀاڱو) ڪافرن جو ڇني ڇڏي يا کين ھيڻو ڪري ڇڏي ته بي نصيب ٿي ڦِرن.'),
(421, 3, 128, '(اي پيغمبر) اُن ڪم ۾ تنھنجو ڪو واسطو نه آھي (خداءِ) يا مٿن ٻاجھ سان موٽندو يا کين عذاب ڪندو ڇو ته اُھي ظالم آھن.'),
(422, 3, 129, '۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي (سو خاص) الله جو آھي، جنھن کي گھرندو آھي تنھن کي بخشيندو آھي ۽ جنھن کي گھرندو آھي تنھن کي عذاب ڪندو آھي، ۽ الله بخشڻھار مھربان آھي.'),
(423, 3, 130, 'اي ايمان وارؤ (اوھين) وياج ٻيڻو چؤڻو نه کائو، ۽ الله کان ڊڄو مانَ اوھين ڇٽو.'),
(424, 3, 131, '۽ اُن باھ کان ڊڄو جا ڪافرن لاءِ تيار ڪئي ويئي آھي.'),
(425, 3, 132, '۽ الله ۽ رسول جي فرمانبرداري ڪريو ته مانَ اوھان تي ٻاجھ ڪئي وڃي.'),
(426, 3, 133, '۽ (اوھين) پنھنجي پالڻھار جي بخشش ۽ اُنھيءَ بھشت ڏانھن اڳرائي ڪريو جنھن جي ويڪر آسمانن ۽ زمين جيتري آھي اُنھن پرھيزگارن لاءِ تيار ڪيو ويو.'),
(427, 3, 134, 'جيڪي سُک ۽ ڏک ۾ (الله ڪارڻ) خرچيندا آھن ۽ ڪاوڙ کي کائيندڙ ۽ ماڻھن (جي ڏوہ) کان ٽارو ڪندڙ آھن، ۽ الله ڀلارن کي دوست رکندو آھي.'),
(428, 3, 135, '۽ اُھي جڏھن ڪو بي حيائيءَ جو ڪم ڪندا آھن يا پاڻ تي ظلم ڪندا آھن (تڏھن) الله کي ياد ڪندا آھن ۽ پنھنجن گناھن جي بخشش گھرندا آھن، ۽ الله کانسواءِ ڪير گناہ بخشيندو؟ ۽ جيڪي ڪيائون تنھن تي ڄاڻي ٻجھي ھميشگي نه ڪندا آھن.'),
(429, 3, 136, 'اُنھن جو اَجر سندن پالڻھار وٽان بخشش ۽ بھشت آھن جن جي ھيٺان واھيون وھنديون آھن منجھن سدائين رھڻ وارا آھن، ۽ ڪمائيندڙن جو اَجر چَڱو آھي.'),
(430, 3, 137, 'بيشڪ اوھان کان اڳ ڪيترا واقعا گذريا آھن پوءِ (اوھين) ملڪ ۾ گھمو ڦرو پوءِ نھاريو ته نه مڃيندڙن جي پڇاڙي ڪئن ھئي.'),
(431, 3, 138, 'ھيءُ ماڻھن لاءِ بيان آھي ۽ پرھيزگارن لاءِ ھدايت ۽ نصيحت آھي.'),
(432, 3, 139, '۽ نڪي سُست ٿيو ۽ نڪي غمگين ٿيو ۽ جيڪڏھن مؤمن آھيو ته (رڳو) اوھين غالب (ٿيڻ وارا) آھيو.'),
(433, 3, 140, 'جيڪڏھن اوھان کي (اُحُد ۾) ڪو گھاءُ پھتو آھي ته بيشڪ اُن جھڙو گھاءُ ڪافرن کي (بَدۡر ۾) پُھتو ھو، ۽ ھيءُ اھڙا واقعا آھن جو (اسين) اُنھن ماڻھن جي وچ ۾ ڦيرائيندا آھيون (ته نصيحت وٺن)، ۽ ھن لاءِ ته الله مؤمنن کي نِکيڙي ۽ اوھان مان ڪن کي شھيد ڪري، ۽ الله ظالمن کي دوست نه رکندو آھي.'),
(434, 3, 141, '۽ ھن لاءِ ته الله مؤمنن کي کرو ڪري ۽ ڪافرن کي ناس ڪري.'),
(435, 3, 142, 'اوھان بھشت ۾ گِھڙڻ جو گمان ڪيو آھي ڇا؟ ھن ھوندي جو الله اڃا اوھان مان مجاھدن کي نه نِکيڙيو آھي ۽ نڪي صابرن کي نِکيڙيو آھي.'),
(436, 3, 143, '۽ بيشڪ اوھين موت کي اُن جي ملڻ کان اڳ گھُرندا ھيؤ، پوءِ بيشڪ ان کي ڏٺوَ ۽ اوھين نھاريندا ھيؤ.'),
(437, 3, 144, '۽ ﷴ (ﷺ الله جو) پيغمبر آھي، بيشڪ اُن کان اڳ ڪيترا پيغمبر گذري ويا، پوءِ جيڪڏھن (اُھو) مري وڃي يا قتل ٿئي ته (اوھين) پنھنجين کڙين (ڀَر پوءِ) تي ڦرندؤ ڇا؟ ۽ جيڪو پنھنجين کڙين (ڀَرپوءِ) تي ڦرندو سو الله کي ڪجھ نقصان ڪڏھن نه لائيندو، ۽ الله شاڪرن (احسان مڃيندڙن) کي ستت ثواب ڏيندو.'),
(438, 3, 145, '۽ الله جي ٺھرايل وقت لکيل حُڪم کانسواءِ ڪنھن کي مرڻو نه آھي، ۽ جيڪو دنيا جو ثواب گھرندو تنھن کي اُن مان ڏينداسون، ۽ جيڪو آخرت جو ثواب گھرندو تنھن کي اُن مان ڏينداسون، ۽ شاڪرن کي (چڱو) ثواب ستت ڏينداسون.'),
(439, 3, 146, '۽ پيغمبرن مان گھڻا (ڪافرن سان) وڙھيا ساڻن گھڻا خداءِ وارا ھئا، پوءِ جيڪي الله جي واٽ ۾ کين پھتو تنھن سببان نڪي سُست ٿيا ۽ نڪي ھيڻا ٿيا ۽ نڪي عاجزي ڪيائون، ۽ الله صابرن کي دوست رکندو آھي.'),
(440, 3, 147, '۽ سندن چوڻ (ھن) چوڻ کانسواءِ نه ھو ته اي اسان جا پالڻھار اسان کي اسان جاگناہ (بخش) ۽ اسان جي ڪم ۾ اسان جي بيجا ھلت بخش ۽ اسان جا پير ثابت رک ۽ اسان کي ڪافرن جي ٽوليءَ تي فتح ڏي.'),
(441, 3, 148, 'پوءِ الله کين دنيا جو ثواب ۽ آخرت جو چڱو ثواب ڏنو، ۽ الله ڀلارن کي دوست رکندو آھي.'),
(442, 3, 149, 'اي اِيمان وارؤ جيڪڏھن اوھين ڪافرن جو چيو مڃيندؤ ته اوھان کي اوھان جي کڙين (ڀَرپوءِ) تي ڦيرائيندا پوءِ اوھين ڦِري ڇيئي وارا ٿيندؤ.'),
(443, 3, 150, 'بلڪ الله اوھان جو سائين آھي، ۽ اُھو چڱو مدد ڏيندڙ آھي.'),
(444, 3, 151, 'ڪافرن جي دلين ۾ ھن سببان سگھو دھشت وجھنداسون جو الله سان اھڙيءَ شيءِ کي شريڪ ڪيائون جنھن لاءِ الله ڪا سَنَد نه لاٿي آھي، ۽ سندن جاءِ باہ آھي، ۽ (اُھو) ظالمن جو ھنڌ بڇڙو آھي.'),
(445, 3, 152, '۽ بيشڪ الله اوھان سان پنھنجو وعدو سچو ڪيو آھي جڏھن ته اُنھن (ڪافرن) کي ايستائين سندس حُڪم سان ڪُٺوَ، جيستائين بي ھمت ٿيؤ ۽ ڪم (پوري ڪرڻ) ۾ تڪرار ڪيوَ ۽ جيڪي دوست رکندا ھيؤ سو اوھان کي ڏيکاريائين تنھن کانپوءِ بي فرماني ڪيوَ، اوھان مان ڪو (اھڙو) آھي جو دُنيا کي گھرندو آھي ۽ اوھان مان ڪو (اھڙو) آھي جو آخرت کي گھرندو آھي، وري اوھان کي اُنھن کان ھن لاءِ ڦيرايائين ته اوھان کي پرکي، ۽ بيشڪ اوھان کي معافي ڏنائين، ۽ الله مؤمنن تي فضل ڪرڻ وارو آھي.'),
(446, 3, 153, 'جڏھن اوھين پري ٿي ڀَڳؤ ۽ ڪنھن ھڪ تي (ڪنڌ ڦيري) نه ٿي واجھايوَ ۽ پيغمبر اوھان کي اوھان جي پُٺيان سڏيندو رھيو پوءِ (خداءِ) اوھان کي ڏک تي ڏک جو بدلو ڏنو (اِھو) ھن لاءِ ته جيڪي اوھان کان ٿي ويو نڪي تنھن تي ۽ جيڪي اوھان کي پھتو نڪي تنھن تي غمگين ٿيو، ۽ جيڪي اوھان ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.'),
(447, 3, 154, 'وري ڏک کانپوءِ اوھان تي آرام (جا) اوجھراڪي (ھئي) لاٿائين جو اوھان مان ھڪ ٽوليءَ کي ڍڪيندي رھي ۽ ٻي ٽولي ھئي جنھن کي سندن نفسن غمگين ڪيو ھو ناحق جاھليت جو گمان الله تي ڀانيائون ٿي، چيائون ٿي ته اسان لاءِ اِنھيءَ ڪم مان ڇا ڪجھ (ٿيڻو) آھي، (اي پيغمبر) چؤ ته سڀ اختيار الله جو ئي آھي، جيڪي توتي پڌرو نه ڪندا آھن سو پنھنجين دلين ۾ لڪائيندا آھن، چوندا آھن ته جيڪڏھن اسان جي وس ۾ ڪجھ به ڪم ھجي ھا ته اسين ھِتِ نه ڪُسون ھا، (اي پيغمبر کين) چؤ ته جيڪڏھن اوھين پنھنجن گھرن ۾ ھجو ھا ته جن ڪُسڻ لکيل ھو سي ضرور پنھنجي ڪوس جي جاين ڏانھن نڪرن ھا، ۽ (ھن لاءِ) ته جيڪي اوھان جي سينن ۾ آھي سو الله پرکي ۽ جيڪي اوھان جي دلين ۾ آھي سو کرو ڪري، ۽ الله سينن وارو (ڀيد) ڄاڻندڙ آھي.'),
(448, 3, 155, 'اوھان مان جن اُنھيءَ ڏينھن مُنھن موڙيو جو ٻه ٽوليون (ھڪ ٻئي جي) سامھون ٿيون ته اُنھن کي شيطان سندن ڪجھ ڪِئي جي شامت کان سواءِ نه ٿيڙيو، ۽ بيشڪ الله اُنھن کي معافي ڏني، ڇوته الله بخشڻھار بُردبار آھي.'),
(449, 3, 156, 'اي ايمان وارؤ اُنھن ڪافرن وانگر نه ٿيو جي پنھنجن ڀائرن لاءِ جڏھن اُھي ملڪ ۾ مسافري ڪن يا لڙائي ڪندڙ ھجن (تڏھن) چون ٿا ته جيڪڏھن اسان وٽ ھجن ھا ته نه مرن ھا ۽ نڪي ڪُسن ھا، ته الله اُھو اَرمان سندن دلين ۾ ڪري، ۽ الله جياريندو ۽ ماريندو آھي، ۽ جيڪي اوھين ڪندا آھيو سو الله ڏسندڙ آھي.'),
(450, 3, 157, '۽ جيڪڏھن اوھين الله جي واٽ ۾ ڪُسي پيؤ يا مري پيؤ ته جيڪي گڏ ڪندا آھن تنھن کان الله جي بخشش ۽ ٻاجھ (بلڪل) ڀلي آھي.'),
(451, 3, 158, '۽ جيڪڏھن (اوھين) مري وڃو يا ڪُسي پئو ته ضرور الله ڏانھن گڏ ڪيا ويندؤ.'),
(452, 3, 159, 'پوءِ (اي پيغمبر) الله جي ٻاجھ سببان اُنھن لاءِ نرم ٿيو آھين، ۽ جيڪڏھن ڪاوڙيل سخت سخت دل ھجين ھا ته ضرور تنھنجي آسپاس کان ڀڄي اُٿن ھا، پوءِ کين معافي ڏي ۽ اُنھن لاءِ بخشش گھُر ۽ ساڻن ڪم ۾ صلاح ڪر، پوءِ جڏھن تون پڪو اِرادو ڪرين تڏھن الله تي ڀروسو ڪر، ڇوته الله ڀروسي ڪرڻ وارن کي دوست رکندو آھي.'),
(453, 3, 160, 'جيڪڏھن الله اوھان کي مدد ڏيندو ته اوھان تي ڪو غالب (ٿيڻو) نه آھي، ۽ جيڪڏھن اوھان کي مدد نه ڏيندو ته اُن کانپوءِ ڪير اوھان کي مدد ڏيندو؟ ۽ مؤمنن کي جڳائي ته الله تي ڀروسو ڪن.'),
(454, 3, 161, '۽ ڪنھين پيغمبر کي (غنيمت جي مال ۾) خيانت ڪرڻ نه جڳائي، ۽ جيڪو خيانت ڪندو سو جيڪا شيء خيانت ڪيائين تنھن سميت قيامت جي ڏينھن ايندو، پوءِ سڀڪنھن ماڻھوءَ جيڪي ڪمايو تنھن جو پورو بدلو کيس ڏنو ويندو ۽ اُنھن تي ظلم نه ڪبو.'),
(455, 3, 162, 'جيڪو الله جي رضامنديءَ جي پٺيان لڳو سو اُن جھڙو آھي ڇا؟ جيڪو الله جي ڪاوڙ ھيٺ ڦري آيو ۽ سندس جاءِ دوزخ آھي، ۽ (اُھو) ورڻ جو ھنڌ بُڇڙو آھي.'),
(456, 3, 163, 'اُھي الله وٽ (وڏن) درجن وارا آھن، ۽ جيڪي ڪندا آھن سو الله ڏسندڙ آھي.'),
(457, 3, 164, 'بيشڪ الله مؤمنن تي وڏو احسان ڪيو آھي جڏھن ته انھن ۾ سندن قوم مان پيغمبر موڪليائين جو کين سندس آيتون پڙھي ٻڌائيندو آھي ۽ کين پاڪ ڪندو آھي ۽ کين ڪتاب ۽ دانائي سيکاريندو آھي، بيشڪ ھن کان اڳ پڌريءَ ڀل ۾ (ڀليل) ھؤا.'),
(458, 3, 165, '۽ جڏھن اوھانکي (جنگ اُحد ۾) ڏک پھتو توڻيڪ (پنھنجن دشمنن کي) اُن جو ٻيڻو (ڏُک بَدۡر ۾) پھچايو ھيو ته به چيوَ ٿي ته ھي (مصيبت) ڪٿان (پھتي) آھي، (اي پيغمبر) چؤ ته اُھا اوھان کي پاڻ وٽان (پھتي) آھي، ڇوته الله سڀڪنھن شيء تي وسوارو آھي.'),
(459, 3, 166, '۽ اُنھيءَ ڏينھن جو ٻه ٽوليون پاڻ ۾ سامھون ٿيون (تنھن ڏينھن) جيڪي اوھانکي پھتو سو الله جي حُڪم سان ھو ۽ ھن لاءِ ته مؤمنن کي نِکيڙي.'),
(460, 3, 167, '۽ منافقن کي به نِکيڙي، ۽ کين چيو ويو ته اچو الله جي واٽ ۾ وڙھو يا (ڪافرن کي) جواب ڪريو، چيائون ته جيڪڏھن اسان کي ڪنھن جنگ ٿيڻ جي خبر ھجي ھا ته ضرور اوھان سان ھجون ھا، اُھي اُن ڏينھن پاڻ اِيمان کان ڪفر ڏي بلڪل ويجھو ھؤا، پنھنجن واتن سان اُھي (ڳالھيون) چوندا آھن جيڪي سندين دلين ۾ نه آھن، ۽ جيڪي لڪائيندا آھن سو الله بلڪل ڄاڻندڙ آھي.'),
(461, 3, 168, '(ته) اِھي اُھي آھن جن پنھنجن ڀائرن کي چيو ۽ ويٺا رھيا ته جيڪڏھن (اُھي) اسان جو چيو مڃن ھا ته نه ڪُسن ھا، (اي پيغمبر کين) چؤ ته جيڪڏھن اوھين سچا آھيو ته پاڻ کان موت کي ٽاريو.'),
(462, 3, 169, '۽ جيڪي الله جي واٽ ۾ ڪُٺا ويا تن کي مئل پٽيءَ نه ڀانءِ، بلڪ (اُھي) جيئرا آھن (۽) پنھنجي پالڻھار وٽ روزي ڏنل آھن.'),
(463, 3, 170, 'جيڪي الله اُنھن کي پنھنجي فضل سان ڏنو آھي تنھن (حال) سان خوش ٿيندڙ آھن ۽ جيڪي سندن پٺيان آھن تن مان جيڪي ساڻن نه گڏيا آھن اُنھن لاءِ ھن ڪري خوش ٿيندا آھن ته کين ڪو ڀؤ نه آھي ۽ نڪي اُھي غمگين رھندا.'),
(464, 3, 171, 'الله جي نعمت ۽ فضل سان خوش ٿيندا آھن ۽ الله مؤمنن جو اجر نه وڃائيندو آھي.'),
(465, 3, 172, 'جن پاڻ کي ڦٽ پھچڻ کان پوءِ به الله ۽ رسول جو حُڪم قبول ڪيو، انھن مان جن چڱائي ڪئي ۽ ڊنا تن لاءِ وڏو اجر آھي.'),
(466, 3, 173, 'جن کي ماڻھن چيو ته (ڪافرن) ماڻھن اوھان لاءِ لشڪر گڏ ڪيو آھي تنھن ڪري کانئن ڊڄو پوءِ (اُن چوڻ) ھٿون اُنھن جو ايمان وڌايو ۽ چيائون ته اسان کي الله ڪافي آھي ۽ (اُھو) چڱو وڪيل (ڀرجھلو) آھي.'),
(467, 3, 174, 'پوءِ (اُھي مسلمان) الله جي نعمت ۽ فضل سان موٽيا کين ڪا سختي نه پھتي ۽ الله جي راضپي پٺيان لڳا، ۽ الله وڏي فضل وارو آھي.'),
(468, 3, 175, '(اُھو اوھان کي جنگ کان جھليندڙ) شيطان کان سواءِ ٻيو نه آھي جو پنھنجن دوستن کي ڊيڄاريندو آھي، جيڪڏھن (اوھين) مؤمن آھيو ته اُنھن (ڪافرن) کان نه ڊڄو ۽ مون کان ڊڄو.'),
(469, 3, 176, '۽ (اي پيغمبر) جيڪي ڪفر (جي سوڀ) ۾ اڳرائي ڪندا آھن سي توکي غمگين نه ڪن، ڇو ته اُھي الله کي ڪجھ به نقصان نه لائيندا، الله گھرندو آھي ته اُنھن کي آخرت ۾ ڪجھ بھرو نه ڏي، ۽ اُنھن لاءِ وڏو عذاب آھي.'),
(470, 3, 177, 'جن اِيمان جي بدران ڪفر ڳڌو سي الله کي ڪجھ به نقصان نه لائيندا، ۽ اُنھن لاءِ ڏکوئيندڙ عڊاب آھي.'),
(471, 3, 178, '۽ ڪافر (ھيءُ) نه ڀانئن ته کين اسان جي مُھلت ڏيڻ اُنھن لاءِ ڀلي آھي، اُنھن کي ھن کانسواءِ ڍر نه ڏني سون ته گناھ ۾ وڌن، ۽ انھن لاءِ خوار ڪندڙ عذاب آھي.'),
(472, 3, 179, '(اي منافقؤ) الله کي نه جڳائيندو آھي ته جنھن تي اوھين آھيو تنھن تي مؤمنن کي (به) ڇڏي ڏي تان جو پليت کي پاڪ کان ڌار ڪري، ۽ الله کي نه جڳائيندو آھي جو اوھان کي ڳُجھ تي واقف ڪري پر الله پنھنجن پيغمبرن مان جنھن کي گھرندو آھي تنھن کي چونڊيندو آھي، پوءِ الله ۽ سندس پيغمبرن کي مڃيو، ۽ جيڪڏھن (اوھين) مڃيندؤ ۽ ڊڄندؤ ته اوھان لاءِ وڏو اجر آھي.'),
(473, 3, 180, '۽ جيڪي الله پنھنجي فضل سان کين ڏنو آھي تنھن ۾ جيڪي بخل ڪن سي (اِئين) پٽيءَ نه ڀانئين ته اُھو (بُخل) کين ڪو چڱو آھي، بلڪ اُھو اُنھن لاءِ بڇڙو آھي، جنھن شيء جو بخل ڪيائون سا قيامت جي ڏينھن ڳٽ ڪري وجھبين، ۽ آسمانن ۽ زمين جي ميراث الله جي آھي، ۽ جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.'),
(474, 3, 181, 'بيشڪ الله اُنھن جو چوڻ ٻڌو جن چيو ته الله سڃو آھي ۽ اَسين شاھوڪار آھيون، جيڪي چيائون سو لکنداسون ۽ اُنھن جو پيغمبرن کي ناحق ڪھڻ به ۽ (آخرت ۾) چونداسون ته ساڙيندڙ عذاب (جو مزو) چکو.'),
(475, 3, 182, 'اھا (سزا) اِنھيءَ سببان آھي جو اوھان جي ھٿن اڳ موڪليو آھي ۽ (ھن سببان) ته الله ٻانھن تي ظلم ڪندڙ نه آھي.'),
(476, 3, 183, 'جيڪي چون ٿا ته الله اسان سان انجام ڪيو آھي ته ڪنھن پيغمبر تي ايستائين ايمان نه آڻيون جيستائين (ھو اھڙي) قرباني اسان وٽ (نه) آڻي جو اُن (قربانيءَ) کي باہ کائي، (اي پيغمبر کين) چئو ته بيشڪ مون کان اڳ ڪيترن پيغمبرن مُعجزا ۽ (ٻيو) جيڪي چيوَ سو اوھان وٽ آندو پوءِ جيڪڏھن اوھين سچا آھيو ته اُنھن (پيغمبرن) کي ڇالاءِ ڪٺوَ؟'),
(477, 3, 184, 'پوءِ (اي پيغمبر) جيڪڏھن توکي ڪوڙو ڀانيائون ته بيشڪ توکان اڳ ڪيترا پيغمبر ڪوڙا ڀانيا ويا ھؤا (جن) ڪيتريون حجتون ۽ ڪيترا صحيفا ۽ نوراني ڪتاب (وٽن) آندا.'),
(478, 3, 185, 'سڀڪو جيءُ موت (جو مزو) چکندڙ آھي، ۽ ھن کان سواءِ (ٻيوڪجھ ڪرڻو) نه آھي جو اوھان کي قيامت جي ڏينھن پورو اجر ڏبو، پوءِ جنھن کي باہ کان پري ڪيو ويو ۽ بھشت ۾ گھيڙيو ويو سو بيشڪ مراد کي پھتو، ۽ دنيا جي حياتي ٺڳيءَ جي سامان کانسواءِ نه آھي.'),
(479, 3, 186, '(اوھين) پنھنجن مالن ۽ پنھنجين جانين ۾ ضرور پرکبو، ۽ جن کي اوھان کان اڳ ڪتاب ڏنو ويو آھي تن کان ۽ مشرڪن کان گھڻيون گاريون ضرور ٻڌندؤ، ۽ جيڪڏھن صبر ڪندؤ ۽ الله کان ڊڄندؤ ته اِھا (عادت) ھمت جي ڪمن مان آھي.'),
(480, 3, 187, '۽ (ياد ڪر) جڏھن الله ڪتاب وارن کان ھن لاءِ (پڪو) انجام ورتو ته اُن (ڪتاب) کي ماڻھن لاءِ بيان ڪجو ۽ اُن کي نه لڪائجو، پوءِ اُن (انجام) کي پنھنجين پٺين پوئتان اُڇلايائون ۽ اُن جي عيوض ٿورو مُلھ ڳڌائون، پوءِ جيڪي ڳنھندا آھن سو بڇڙو آھي.'),
(481, 3, 188, '(اي پيغمبر تون) اُنھن کي چڱو نه ڀانءِ جيڪي پنھنجي ڪئي تي خوش ٿيندا آھن ۽ جيڪي نه ڪيائون تنھن تي پنھنجي ساراھڻ کي دوست رکندا آھن پوءِ تون اُنھن کي عذاب کان ڇٽل نه ڀانءِ، ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(482, 3, 189, '۽ آسمانن ۽ زمين جي بادشاھي الله جي آھي، ۽ الله سڀڪنھن شيءِ تي وسوارو آھي.'),
(483, 3, 190, 'آسمانن ۽ زمين جي بڻاوت (۾) ۽ رات ۽ ڏينھن جي ڦير گھير ۾ اُنھن سمجھ وارن لاءِ ضرور نشانيون آھن.'),
(484, 3, 191, 'جيڪي بيٺي ۽ ويٺي ۽ پنھنجن پاسن ڀر الله کي ياد ڪندا آھن ۽ آسمانن ۽ زمين جي بڻاوت ۾ فڪر ڪندا آھن، (۽ چوندا آھن ته) اي اسانجا پالڻھار ھنن کي بي رٿو نه بڻايو اَٿيئي، توکي پاڪ ڄاڻون ٿا تنھنڪري اسان کي باہ جي عذاب کان بچاءِ.'),
(485, 3, 192, 'اي اسانجا پالڻھار جنھن کي تو باہ ۾ گھيڙيو تنھن کي بيشڪ خوار ڪيئي، ۽ ظالمن جو ڪو مددگار نه آھي.'),
(486, 3, 193, 'اي اسانجا پالڻھار اسان سڏيندڙ جي سڏ کي ٻڌو جو ايمان ڏانھن سڏيندو آھي ته پنھنجي پالڻھار تي ايمان آڻيو پوءِ مڃيوسون، اي اسانجا پالڻھار اسان کي اسانجا گناہ بخش ۽ اسان کان اسان جون مدايون ميٽ ۽ اسان کي ڀلارن سان گڏ مار.'),
(487, 3, 194, 'اي اسانجا پالڻھار اسان کي پنھنجن پيغمبرن (جي زبان) سان جيڪي انجام ڏنو اٿيئي سو پار ۽ قيامت جي ڏينھن اسان کي خوار نه ڪج، ڇوته تون وعدي خلافي نه ڪندو آھين.'),
(488, 3, 195, 'پوءِ سندن پالڻھار اُنھن جي دعا قبول ڪئي ته آءٌ اوھان مان عمل ڪندڙ مڙس يا زال جو عمل نه وڃائيندس، اوھان مان ڪي ڪن مان (پيدا ٿيل) آھن (يعني سڀ ھڪ جنس آھيو) پوءِ جن وطن ڇڏيو ۽ پنھنجن گھرن مان ڪڍيا ويا ۽ مُنھنجي واٽ ۾ ڏکويا ويا ۽ وڙھيا ۽ ڪُٺا ويا تن کان سندن مدايون ضرور ميٽيندس ۽ اُنھن کي (بھشت جي اھڙن) باغن ۾ ضرور داخل ڪندس جن جي ھيٺان واھيون وھنديون آھن، (اِھو) بدلو الله وٽان آھي، ۽ الله وٽ چڱو بدلو آھي.'),
(489, 3, 196, '(اي پيغمبر) ملڪن ۾ ڪافرن جي اَچ وڃ توکي نه ٺڳي.'),
(490, 3, 197, 'اِھو فائدو ٿورو آھي، وري سندن جاءِ دوزخ آھي، ۽ (اُھو) بڇڙو ھنڌ آھي.'),
(491, 3, 198, 'پر جيڪي پنھنجي پالڻھار کان ڊنا تن لاءِ باغ آھن جن جي ھيٺان واھيون وھنديون آھن اُن ۾ سدائين رھڻ وارا آھن اِھا مھماني الله وٽان آھي، ۽ جيڪي الله وٽ آھي سو ڀلارن لاءِ چڱو آھي.'),
(492, 3, 199, '۽ ڪتاب وارن مان ڪي اُھي آھن جيڪي الله کي ۽ جيڪي توڏانھن (قرآن) لاٿو ويو تنھن کي ۽ جيڪي (توريت) اُنھن ڏانھن لاٿو ويو تنھن کي الله جي عاجزي ڪندڙ ٿي مڃيندا آھن الله جي آيتن کي ٿوري مُلھ سان نه وڪڻندا آھن، اِھي (اُھي) آھن جن لاءِ سندن پالڻھار وٽ سندن اجر آھي، ڇوته الله سگھو حساب وٺندڙ آھي.'),
(493, 3, 200, 'اي ايمان وارؤ صبر ڪريو ۽ (جھاد لاءِ) بيھڪ وارا ٿيو ۽ مُحڪم رھو، ۽ الله کان ڊڄو ته مانَ اوھين ڇٽو.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'اي ماڻھؤ اوھين اُنھيءَ پنھنجي پالڻھار کان ڊڄو جنھن اوھان کي ھڪ جِيءَ (آدم) مان پيدا ڪيو ۽ منجھانئس سندس زال پيدا ڪيائين ۽ اُنھن مان گھڻا مڙس ۽ زالون پکيڙيائين، ۽ اُنھيءَ الله کان ڊڄو جنھن (جي نالي) سان (پاڻ ۾) ھڪ ٻئي کان گھرندا آھيو ۽ مائٽي (ڇنڻ) کان (به ڊڄو)، ڇوته الله اوھان تي نگھبان آھي.'),
(495, 4, 2, '۽ يتيمن کي سندن مال ڏيو ۽ سٺي شيءِ کي بڇڙيءَ شيءِ سان نه مٽايو، ۽ اُنھن (يتيمن) جو مال پنھنجن مالن سان (گڏي) نه کائو، ڇوته اُھو وڏو ڏوہ آھي.'),
(496, 4, 3, '۽ جيڪڏھن ڊڄو ته يتيمن ڇوڪرين (جي نڪاح ڪرڻ) ۾ عدل نه ڪندؤ ته (ٻـين) زالن مان جيڪي اوھان کي وڻن سي ٻه ٻه ۽ ٽي ٽي ۽ چار چار پرڻيو، پوءِ جيڪڏھن ڊٺو ٿا ته اوھان کان انصاف ٿي نه سگھندو ته ھڪ (پرڻيو) يا اوھان جا ھٿ جنھن جا مالڪ ٿيا (سا ٻانھي رکو) اِھو ھن کي ويجھو آھي ته ڏاڍائي نه ڪريو.'),
(497, 4, 4, '۽ زالن کي سندن ٺھرايل ڪابين خوشيءَ سان ڏيو، پوءِ جيڪڏھن اوھان کي پاڻ خوشيءَ سان اُنھيءَ مان ڪجھ ڇڏين ته اُھا سُھائيندڙ وڻندڙ ڪري کائو.'),
(498, 4, 5, '۽ بي سمجھن کي پنھنجا اُھي مال نه ڏيو جن کي الله اوھان لاءِ گذران جو سبب بڻايو آھي ۽ منجھانئس کين کارايو ۽ کين پھرايو ۽ کين چڱي ڳالھ چئو.'),
(499, 4, 6, '۽ ايستائين يتيمن کي پرکو جيستائين پرڻي (جي عمر) کي پھچن، پوءِ جيڪڏھن اُنھن ۾ ڪا سياڻپ لھو ته سندن مال کين ورائي ڏيو، ۽ اُھي (مال) اجايو ۽ (سندن) وڏي ٿيڻ جي ڀؤ کان تڪڙ ڪري نه کائو، ۽ جيڪو (سنڀاليندڙ) آسودو ھجي تنھن کي گھرجي ته پاسي رھي، ۽ جيڪو مسڪين ھجي سو مناسب (خدمت) آھر کائيندو رھي، پوءِ جڏھن سندس مال کين ورائي ڏيو تڏھن مٿن شاھد ڪريو، ۽ الله حساب ڪندڙ بس آھي.'),
(500, 4, 7, 'جيڪو (مال) ماءُ پيءُ ۽ مٽن ڇڏيو تنھن مان مڙسن لاءِ حصّو آھي، ۽ جيڪو (مال) ماءُ پيءُ ۽ مٽن ڇڏيو تنھن مان زالن لاءِ (به) حصو آھي اھو ڇڏيل مال ٿورو ھجي يا گھڻو، حصّا (الله جا) مقرر ڪيل آھن.'),
(501, 4, 8, '۽ جڏھن ورھاڱي مھل مائٽيءَ وارا ۽ يتيم ۽ مسڪين حاضر ٿين تڏھن اُن (مال) مان اُنھن ماڻھن کي (ڪجھ) ڏيو ۽ اُنھن کي مٺي ڳالھ چئو.'),
(502, 4, 9, '۽ اُنھن کي (الله کان) ڊڄڻ گھرجي جيڪي جيڪڏھن پنھنجي پٺيان ھيڻو اولاد ڇڏين ته مٿن ڊڄن (ته ڪھڙو حال ٿيندن)، پوءِ گھرجي ته الله کان ڊڄن ۽ گھرجي ته سڌي ڳالھه چون.'),
(503, 4, 10, 'جيڪي يتيمن جو مال ڏاڍ سان کائيندا آھن سي پنھنجن پيٽن ۾ رڳو باھ کائيندا آھن، ۽ دوزخ ۾ سگھو گھِڙندا.'),
(504, 4, 11, 'الله اوھان جي اولاد بابت اوھان کي وصيت ٿو ڪري، ته پُٽ لاءِ (حصّو) ٻن ڌيئرن جي ڀاڱي جيترو آھي، ۽ جيڪڏھن (فقط) ڌيئرون ٻن کان وڌيڪ ھجن ته (مُئَل) جيڪي ڇڏيو تنھن جون ٻه ٽھايون اُنھن لاءِ آھن، ۽ جيڪڏھن (فقط) ھڪ آھي ته اُن لاءِ اڌ آھي، ۽ مئل جيڪي ڇڏيو تنھن مان اُن جي ماءُ پيءُ ھر ھڪ لاءِ ڇھون ڀاڱو آھي (سو تڏھن) جڏھن اُن (مئل) کي اولاد ھجي، ۽ جيڪڏھن اُن کي اولاد نه ھُجي ۽ اُن جا وارث ماءُ پيءُ ھجن ته ماڻس لاءِ ٽيون ڀاڱو (۽ باقي پڻس لاءِ) آھي، ۽ جيڪڏھن اُن (مئل) کي ڀائر ھجن ته ماڻس لاءِ ڇھون حصّو آھي (اِھو ورھاڱو) وصيّت (جي ادائيءَ کان پوءِ) جنھن لاءِ اُھا وصيّت ڪيائين يا قرض جي ادائيءَ کانپوءِ (ڪرڻو) آھي، اوھان جا پيئر ۽ اوھان جا پُٽ، (اوھين) نه ڄاڻندا آھيو ته اُنھن مان ڪھڙو اوھان لاءِ فائدي جي ڪري وڌيڪ ويجھو آھي، (اِھو ورھاڱو) الله جي (پارکان) فرض ڪيل آھي، ڇوته الله ڄاڻندڙ حڪمت وارو آھي.'),
(505, 4, 12, '۽ جيڪي (مال) اوھان جي زالن ڇڏيو تنھن جو اَڌ اوھان لاءِ آھي جيڪڏھن انھن کي ڪو وياءُ ڪونه ھجي، پر جيڪڏھن اُنھن کي ڪو وياءُ ھجي ته جيڪي ڇڏيائون تنھن مان چوٿون ڀاڱو وصيت (جي ادائيءَ کانپوءِ) جنھن لاءِ اُھا وصيت ڪيائون يا قرض جي ادائيءَ کان پوءِ اوھان لاءِ آھي، ۽ جيڪي (مال) اوھان ڇڏيو تنھن مان جيڪڏھن اوھان کي ڪو وياءُ ڪونھي ته اُنھن (زالن) لاءِ چوٿون ڀاڱو آھي، ۽ جيڪڏھن اوھان کي وياءُ آھي ته جيڪي (مال) اوھان ڇڏيو تنھن مان وصيت (جي ادائيءَ) جنھن لاءِ اُھا وصيت ڪئي اَٿَوَ يا قرض جي ادائيءَ کانپوءِ اُنھن (زالن) لاءِ اٺون ڀاڱو آھي، ۽ جيڪڏھن ڪو (مئل) مڙس يا زال ورثو ڇڏي (۽ اُھو) ڪلاله ھجي (يعني جنھنکي ماءُ پيءُ ۽ وياءُ نه ھجي) ۽ اُن کي (مائيتو) ھڪ ڀاءُ يا ھڪ ڀيڻ ھجي ته اُنھن منجھان ھر ھڪ لاءِ ڇھون حصو آھي، ۽ جيڪڏھن اِنھن کان وڌيڪ ھجي ته اُھي (سڀ) جا وصيت نقصان واري نه ڪئي ويئي ھجي تنھن يا قرض جي ادائيءَ کانپوءِ ٽھائيءَ ۾ ڀائيوار آھن، اُھو الله جو حُڪم آھي، ۽ الله ڄاڻندڙ بُردبار آھي.'),
(506, 4, 13, 'اِھي الله جون حدون آھن، ۽ جيڪو الله ۽ سندس رسول جي فرمانبرداري ڪندو تنھن کي (الله اھڙن) باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن اُن ۾ سدائين رھڻ وارا آھن، ۽ اِھا وڏي مراد ماڻڻ آھي.'),
(507, 4, 14, '۽ جيڪو الله ۽ سندس رسول جي بي فرماني ڪندو ۽ سندس حدن کان ٻاھر نڪرندو تنھن کي باہ ۾ داخل ڪندو اُن ۾ سدائين رھڻ وارو آھي، ۽ اُن لاءِ خوار ڪندڙ عذاب آھي.'),
(508, 4, 15, '۽ اُوھان جي زالن مان جيڪي بي حيائيءَ جو ڪم ڪن تن تي پاڻ مان چار (مڙس) شاھد ڪريو، پوءِ جيڪڏھن (شاھد) شاھدي ڏين ته اُنھن کي ايستائين گھرن ۾ جھليو جيستائين کين موت ماري يا الله اُنھن لاءِ ڪا واٽ ڪري.'),
(509, 4, 16, '۽ اوھان مان جيڪي ٻه (زاني ۽ زانياڻي) زنا ڪن تن کي مار ڏيو، پوءِ جيڪڏھن توبه ڪن ۽ پاڻ سڌارين ته اُنھن (جي مار ڏيڻ) کان مُڙو، ڇوته الله توبه قبول ڪندڙ مھربان آھي.'),
(510, 4, 17, 'الله کي رڳو اُنھن جي توبه ڪرڻ جڳائيندي آھي جيڪي بي سمجھائيءَ سان گناہ ڪندا آھن وري جھٽ پٽ توبه ڪندا آھن اُنھن تي الله (ٻاجھ سان) موٽندو آھي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(511, 4, 18, '۽ اُنھن جي توبه قبول ٿيڻي نه آھي جيڪي بڇڙايون ڪندا (رھندا) آھن (۽ نه ڊڄندا آھن) تان جو جنھن مھل اُنھن مان ڪنھن کي موت پھچي تنھن مھل (ڊڄي) چوي ته ھاڻي آءٌ توبه ڪريان ٿو ۽ نڪي اُنھن جي (توبه قبول آھي) جيڪي ڪافر ٿي مرندا آھن، اُنھن لاءِ ڏکوئيندڙ عذاب تيار ڪيو اٿئون.'),
(512, 4, 19, 'اي ايمان وارؤ! اوھان کي زالن جو (سندن) بي مرضيءَ وارث ٿيڻ روا نه آھي، ۽ نڪي سندن پڌري گناہ ڪرڻ کان سواءِ کين ھن لاءِ جھليو ته جيڪي کين ڏنو ھجيوَ تنھن مان ڪجھ ورائي وٺو، ۽ ساڻن ھلي چلي وانگر گذاريو، پوءِ جيڪڏھن اُھي اوھان کي نه وڻن ته (ڌيرج ڪريو ڇوته) جا شي اوھان کي نه وڻندي ھجي تنھن ۾ ٿيڻي آھي ته الله گھڻي چڱائي ڪري.'),
(513, 4, 20, '۽ جيڪڏھن (ھڪ) زال جي بجاءِ ٻي زال پرڻڻ جو ارادو ڪريو ۽ اُنھن مان ھڪڙيءَ کي گھڻو مال (ڪابين ۾) ڏنو ھجيوَ ته منجھانئس ڪجھ موٽائي نه وٺو، (اوھين) بھتان ۽ پڌري بدي لائي اُھو (مال) ڇو کسيندا آھيو؟'),
(514, 4, 21, '۽ بيشڪ اوھان ھڪ ٻئي کان نفعو وٺي چڪؤ ۽ (زالن به) اوھان کان (مھر ڏيڻ جو) پڪو انجام ورتو آھي اِنھيءَ ھوندي به اُھو (مال) ڪھڙي طرح کسيندا آھيو؟'),
(515, 4, 22, '۽ جيڪي زالون اوھان جي پيئن پرڻيون سي اوھين نه پرڻيو پر جيڪي اڳي گذريو آھي (سو گذريو) ڇوته اُھا بي حيائي ۽ گناہ آھي، ۽ اُھا بُڇڙي واٽ آھي.'),
(516, 4, 23, '(اي مسلمانؤ) اوھان تي اوھان جون مائر ۽ اوھانجون ڌيئر ۽ اوھانجون ڀينر ۽ اوھانجون پڦيون ۽ اوھانجون ماسيون ۽ ڀائٽيون ۽ ڀاڻيجيون ۽ اُھي اوھان جون مائر جن اوھان کي ٿڃ پياري ۽ اوھان جون رضائي (ٿڃ گڏ پيتل) ڀينر ۽ اوھان جي زالن جون مائر (يعني سَسون) ۽ اُھي اوھانجون اڳ ڄايون جيڪي اوھانجي ھنجن ۾ پليون (۽) اوھان جي اُنھن زالن مان ھجن جن کي اوھين ويجھا ويا آھيو پر جيڪڏھن اوھين اُنھن (سندن مائرن) کي ويجھا نه ويا آھيو ته اوھان تي گناہ نه آھي (اِھي سڀ) حرام ڪيون ويون آھن، ۽ اوھان جي اُنھن پُٽن جون زالون جيڪي (پٽ) اوھانجي پٺين مان (پيدا ٿيل) آھن ۽ ٻن ڀينرن جو گڏ پرڻڻ (حرام ڪيو ويو آھي) پر جيڪي اڳ گذريو آھي (سو گذريو) ڇوته الله بخشڻھار مھربان آھي.'),
(517, 4, 24, '۽ پرڻيل زالون (به حرام آھن) پر اوھان جا ھٿ جن جا مالڪ ٿيا آھن (سي حرام نه آھن)، اُھو حڪم الله اوھان تي لکيو آھي، ۽ اُنھن (حرام ڪيلن) کانسواءِ (ٻيون سڀ زالون) اوھان لاءِ حلال آھن جڏھن پنھنجن مالن سان (کين) پرڻيندڙ نه زنا جو ارادو ڪندڙ ھجو، اھو مال جنھن جي عوض انھن (زالن) کان فائدو ورتوَ تن کي سندن ڪابين مُقرر ڪيل ڏيو، ۽ ڪابين مقرر ڪرڻ کان پوءِ جيتري (ڪابين) ۾ اوھين پاڻ ۾ راضي ٿيو ته اوھان تي گناہ نه آھي، ڇوته الله ڄاڻندڙ حڪمت وارو آھي.'),
(518, 4, 25, '۽ اوھان مان جيڪو آجين مؤمنياڻين جي پرڻجڻ جي سگھ نه رکندو ھجي سو اُنھن مؤمنياڻين ٻانھين مان جن جا مالڪ اوھان (مؤمنن) مان آھن (ڪا پرڻجي ته عار نه آھي)، ۽ الله اوھان جي ايمان کي وڌيڪ ڄاڻندڙ آھي، اوھانجا ھڪڙا ٻـين مان (پيدا ٿيل) آھن (يعني پاڻ ۾ ھڪ جنس آھيو)، پوءِ کين سندن مالڪن جي موڪل سان پرڻجو ۽ سندن ڪابين ھلي چليءَ وانگر کين ڏيو (اُھي) نڪاح ٻَڌلون نه زنا ڪندڙيون ۽ نڪي ڳجھن يارن وٺڻ واريون ھجن، پوءِ جڏھن نڪاح ۾ آيون تڏھن جيڪڏھن (اُھي) ڪو بي حيائيءَ جو ڪم ڪن ته جيڪا سزا آجين تي آھي تنھن جو اڌ مٿن آھي، (اِھا ٻانھيءَ کي پرڻجڻ جي اجازت) اوھان مان انھيءَ لاءِ آھي جيڪو زنا (جي گناہ ۾ پوڻ) کان ڊڄي، ۽ اوھان جو صبر ڪرڻ اوھان لاءِ ڀلو آھي، ۽ الله بخشڻھار مھربان آھي.'),
(519, 4, 26, 'الله گھرندو آھي ته اوھان لاءِ کولي بيان ڪري ۽ جيڪي اوھان کان اڳ (گذريا) آھن تن جون واٽون اوھان کي ڏيکاري ۽ اوھان تي ٻاجھ سان موٽي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(520, 4, 27, '۽ الله اوھان تي ٻاجھ سان موٽڻ گھرندو آھي، ۽ جيڪي پنھنجي سَڌن جي پٺيان لڳندا آھن سي اوھان جو (سڌيءَ واٽ کان) تمام پري ٿڙي وڃڻ گھرندا آھن.'),
(521, 4, 28, 'الله اوھان تان بار ھلڪو ڪرڻ گھرندو آھي، ۽ ماڻھو (اصل) ھيڻو پيدا ڪيو ويو آھي.'),
(522, 4, 29, 'اي ايمان وارؤ! اوھين پاڻ ۾ ھڪ ٻئي جو مال ناحق نه کائو پر پاڻ ۾ رضامندي سان واپار جي ڏيتي ليتي ھجي ته (ڀلي کائو)، ۽ اوھان پاڻ کي ھلاڪ نه ڪريو، ڇوته الله اوھان تي مھربان آھي.'),
(523, 4, 30, '۽ جيڪو حد کان ٻاھر نڪرڻ ۽ ظلم سان ايئن ڪندو تنھن کي باہ ۾ سگھو وجھنداسين، ۽ اھو ڪم الله تي سھنجو آھي.'),
(524, 4, 31, 'جيڪڏھن وڏن گناھن کان جن کان اوھانکي جھليو ويو پاسو ڪندا رھندؤ ته اوھان جيون (ھلڪيون) مدايون اوھان کان ميٽينداسين ۽ اوھان کي عزت واريءَ جاءِ ۾ داخل ڪنداسين.'),
(525, 4, 32, 'جن شين سان الله اوھان مان ھڪڙن کي ٻـين کان فضيلت ڏني آھي تن جي ريس نه ڪريو، مڙسن لاءِ جيترو (پاڻ) ڪمايائون تنھن مان حصّو آھي، ۽ زالن لاءِ جيترو پاڻ ڪمايائون تنھن مان حصّو آھي، ۽ الله کان سندس فضل گھرو، ڇوته الله سڀڪنھن شيءِ کي ڄاڻندڙ آھي.'),
(526, 4, 33, '۽ ماءُ پيءُ ۽ مائٽن جيڪي (مال) ڇڏيو تنھن سڀ لاءِ وارث مُقرّر ڪياسون، ۽ جن کي (مائٽيءَ جھڙي دوستيءَ ۾) اوھانجي قَسمن ٻَڌو آھي تن کي سندن حصّو ڏيو، ڇوته الله سڀڪنھن شي تي حاضر آھي.'),
(527, 4, 34, 'مڙس زالن تي انھيءَ سببان حاڪم آھن جو الله انھن مان ھڪڙن کي ٻـين کان ڀلو ڪيو آھي ۽ (ٻيو) ھن سببان جو پنھنجن مالن مان خرچيندا آھن، پوءِ ڀلاريون فرمانبردارڻيون پرپٺ نگاہ رکڻ واريون الله جي نگھباني موجب آھن، ۽ جن (زالن) جي بي فرماني معلوم ڪريو تن کي (زباني) نصيحت ڪريو ۽ پاٿارين ۾ کين ڌار ڪريو ۽ مار ڏيونِ، پوءِ جيڪڏھن اوھان جي فرمانبرداري ڪن ته مٿن ڪا (ايذاءُ جي) واٽ نه ڳوليو، ڇوته الله وڏو وڏيءَ وڏائيءَ وارو آھي.'),
(528, 4, 35, '۽ جيڪڏھن سندن وچ ۾ ڦيٽي کان ڊڄو ته ھِن جي گھراڻي مان ھڪ امين ۽ ھُن جي گھراڻي مان (به) ھڪ امين مُقرّر ڪريو، جيڪڏھن ٻئي (امين) صُلح ڪرائڻ گھرندا ته الله سندن وچ ۾ ميلاپ ڪندو، ڇوته الله ڄاڻندڙ خبر رکندڙ آھي.'),
(529, 4, 36, '۽ (خاص) الله جي عبادت ڪريو ۽ ساڻس ڪنھن شيء کي شريڪ نه ڪريو ۽ ماءُ پيءُ سان ۽ مٽيءَ وارن سان ۽ ڇورن (ٻارن) ۽ مسڪينن ۽ مٽيءَ واريءَ پاڙيچي ۽ ڌاريي پاڙيچي ۽ ويجھي سنگتي ۽ مسافر ۽ پنھنجن ٻانھن سان چڱائي ڪريو، ڇوته جيڪو ھٺيلو وڏائي ڪندڙ ھجي تنھن کي الله دوست نه رکندو آھي.'),
(530, 4, 37, '(۽ نڪي اُنھن کي) جيڪي (پاڻ) ڪنجوسي ڪندا آھن ۽ ماڻھن کي به ڪنجوسي ڪرڻ جو حُڪم ڪندا آھن ۽ الله پنھنجي فضل سان کين جيڪي ڏنو آھي سو لڪائيندا آھن، ۽ اھڙن نه مڃيندڙن لاءِ خواريءَ وارو عذاب تيار ڪيو اٿئون.'),
(531, 4, 38, '۽ (اُنھن لاءِ به) جيڪي پنھنجن مالن کي ماڻھن جي ڏيکاريءَ لاءِ خرچيندا آھن ۽ نڪي الله کي ۽ نڪي قيامت جي ڏينھن کي مڃيندا آھن، ۽ جنھن جو سنگتي شيطان ٿيو ته اُھو سنگتي بڇڙو آھي.'),
(532, 4, 39, '۽ مٿن ڪھڙي (ڏکيائي) ھئي جيڪڏھن الله ۽ قيامت جي ڏينھن کي مڃين ھا ۽ الله جيڪي کين ڏنو آھي ان مان خرچين ھا، ۽ الله انھن (جي حال) کي (چڱو) ڄاڻندڙ آھي.'),
(533, 4, 40, 'الله ذري جيترو به ظلم نه ڪندو آھي، ۽ جيڪڏھن (ڪا) چڱائي ھوندي ته ان کي ٻيڻو ڪندو آھي ۽ پاڻ وٽان وڏو اجر ڏيندو آھي.'),
(534, 4, 41, 'پوءِ (تڏھن) ڪئن (ڪندا) جڏھن سڀڪنھن ٽوليءَ مان (ھڪ) شاھد آڻينداسون ۽ (اي پيغمبر) توکي (به) ھن ٽوليءَ تي شاھد ڪري آڻينداسون.'),
(535, 4, 42, 'اُنھيءَ ڏينھن ڪافر ۽ جن پيغمبر جو چيو نه مڃيو سي گھرندا ته جيڪر مٿن زمين سنئين ڪئي وڃي، ۽ الله کان ڪائي ڳالھ لڪائي نه سگھندا.'),
(536, 4, 43, 'اي ايمان وارؤ! اوھان نشي ۾ ٿي ايستائين نماز کي ويجھا نه وڃو جيستائين جيڪي ڳالھايو ٿا سو سمجھو ۽ نڪي مسافرن کانسواءِ (مقيم) تڙ وارو (نماز پڙھي) جيسين تڙ ڪريو، ۽ جيڪڏھن اوھين بيمار ھجو يا مسافري تي ھجو يا اوھان مان ڪوئي جاءِ ضرور کان اچي يا زالن کي ويجھا ويا ھجو ۽ پاڻي نه لھو ته پاڪ مٽيءَ سان تيمّم ڪريو پوءِ پنھنجن مُنھن ۽ پنھنجن ھٿن کي مک ڪريو، ڇوته الله معافي ڏيندڙ بخشڻھار آھي.'),
(537, 4, 44, '(اي پيغمبر) اُنھن ڏانھن نه ڏٺو اٿئي ڇا؟ جن کي ڪتاب مان حصّو ڏنو ويو آھي اُھي گمراھي ڳنھندا آھن ۽ اوھان جو واٽ کان ڀلڻ (به) گھُرندا آھن.'),
(538, 4, 45, '۽ الله اوھان جي ويرين کي وڌيڪ ڄاڻندڙ آھي، ۽ الله (سڀني ڪمن جو) ڪارساز بس آھي، ۽ الله مددگار بس آھي.'),
(539, 4, 46, 'يھودين مان ڪي (توريت ۾) لفظن کي سندن جاين کان مٽائيندا آھن ۽ سَمِعۡنَا وَ عَصَيۡنَا ۽ اِسۡمَعۡ غَيۡرَ مُسۡمَع ۽ رَاعِنَا (جي لفظن) کي (چوڻ وقت) پنھنجيون زبانون وچڙائي ۽ دين ۾ طعني ھڻڻ لاءِ چوندا آھن، ۽ جيڪڏھن (سَمِعۡنَا وَعَصَيۡنَا جي بجاءِ) سَمِعۡنَا وَاَطَعۡنَا (يعني ٻڌوسون ۽ فرمانبرداري ڪئيسون) ۽ (اِسۡمَعۡ غَيۡرَ مُسۡمَع جي ھنڌ) اِسۡمَعۡ (يعني ٻڌ) ۽ (رَاعِنَا لفظ جي بجاءِ لفظ) اُنۡظُرۡنَا (يعني اسان جو خيال رک) چون ھا ته اُنھن لاءِ ڀلو ۽ ڏاڍو سنئون ھجي ھا پر الله سندن ڪُفر سببان مٿن لعنت ڪئي آھي پوءِ ٿورن کانسواءِ (ٻيا) ايمان نه ٿا آڻين.'),
(540, 4, 47, 'اي ڪتاب وارؤ جيڪو (قرآن) لاٿوسون جيڪي اوھان وٽ آھي تنھن جو (اُھو) سچو ڪندڙ (به) آھي تنھنکي انھي کان اڳ مڃيو جو (اوھان جي) منھن جي صورت ميٽي ڇڏيون پوءِ ڦيرائي انھن کي ڪياڙي جي شڪل ڪريون يا مٿن اھڙي لعنت ڪريون جھڙي ڇنڇر وارن تي لعنت ڪئيسون، ۽ الله جو (جيڪو) حُڪم (جاري ٿيندو آھي) سو ٿي چڪندو آھي.'),
(541, 4, 48, 'بيشڪ الله ھيءُ (ڏوہ) نه بخشيندو جو (ڪنھن کي به) ساڻس شريڪ ڪيو وڃي ۽ اُن کانسواءِ ٻيا (ڏوہ) جنھن کي وڻيس تنھن کي بخشيندو، ۽ جيڪو الله سان (ڪنھن کي به) شريڪ بڻائيندو تنھن بيشڪ وڏي گناہ جو بُھتان ٻڌو.'),
(542, 4, 49, '(اي پيغمبر) انھن ڏانھن نه ڏٺو اٿيئي ڇا؟ جيڪي پاڻون پاڻ کي پاڪ چوندا آھن، (نه) بلڪ الله جنھن کي گھرندو آھي پاڪ ڪندو آھي ۽ تند جيترو به ظلم نه ڪبن.'),
(543, 4, 50, '(اي پيغمبر) ڏس ته الله تي ڪيئن ڪوڙ ٺاھيندا آھن؟ ۽ اھو پڌرو گناہ بس آھي.'),
(544, 4, 51, '(اي پيغمبر) اُنھن ڏانھن نه ڏٺو اٿيئي ڇا؟ جن کي ڪتاب مان ڀاڱو ڏنو ويو ته اُھي بتن ۽ شيطان کي مڃيندا آھن ۽ ڪافرن لاءِ چوندا آھن ته ھيءَ ٽولي مؤمنن کان وڌيڪ سنئين واٽ واري آھي.'),
(545, 4, 52, 'اِھي (اُھي) آھن جن تي الله لعنت ڪئي آھي، ۽ جنھن تي الله لعنت ڪندو آھي تنھن جو ڪو مددگار نه لھندين.'),
(546, 4, 53, 'اُنھن کي بادشاھيءَ مان ڪو ڀاڱو آھي ڇا؟ (جي کين کڻي ھجي به ته) اُن مھل ماڻھن کي تر جيترو (به) نه ڏين.'),
(547, 4, 54, 'ويتر ماڻھن سان انھيءَ تي حسد ڪندا آھن جيڪي الله پنھنجي فضل سان کين ڏنو آھي، پوءِ بيشڪ ابراھيم جي اولاد کي ڪتاب ۽ دانائي ڏنيسون ۽ کين وڏي بادشاھي ڏني سون.'),
(548, 4, 55, 'پوءِ منجھائن ڪي آھن جن اُن ڪتاب کي مڃيو ۽ منجھائن ڪي آھن جي کانئس اٽڪي رھيا، ۽ (انھن لاءِ) ٻرندڙ دوزخ بس آھي.'),
(549, 4, 56, 'جن اسان جي آيتن کي نه مڃيو تن کي سگھو باہ ۾ گھيرينداسون، جڏھن ڪڏھن سندن کلون ڀُڄنديون (تڏھن) سندن ٻيون کلون ھن لاءِ پيا مٽائينداسون ته ڀلي عذاب (جو مزو) چکن، ڇوته الله غالب حڪمت وارو آھي.'),
(550, 4, 57, 'جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي (اُنھن) باغن ۾ سگھو داخل ڪنداسون جن جي ھيٺان واھيون وھنديون آھن منجھن اصلي سدائين رھڻ وارا آھن، اُنھن لاءِ منجھن پاڪ زالون آھن، ۽ کين گھاٽين ڇانون ۾ داخل ڪنداسون.'),
(551, 4, 58, 'بيشڪ الله اوھان کي حُڪم ٿو ڪري ته امانتون سندن مالڪن کي پھچائي ڏيو ۽ جڏھن ماڻھن جي وچ ۾ فيصلو ڪريو (تڏھن) انصاف سان حڪم ڪريو، بيشڪ الله اوھان کي جيڪا نصيحت ڪري ٿو سا چڱي آھي، ڇوته الله ٻڌندڙ ڏسندڙ آھي.'),
(552, 4, 59, 'اي ايمان وارؤ الله جي فرمانبرداري ڪريو ۽ پيغمبر جي فرمانبرداري ڪريو ۽ اوھان مان جيڪو حاڪم ھجي تنھن جي (به) پوءِ جيڪڏھن ڪنھن شيءِ بابت جھڳڙو ڪريو ته جيڪڏھن الله ۽ قيامت جي ڏينھن کي مڃيندا آھيو ته ان کي الله ۽ پيغمبر جي حُڪم ڏانھن رجوع ڪريو، اِھو ڀلو ۽ نتيجي جي ڪري ڏاڍو چڱو آھي.'),
(553, 4, 60, '(اي پيغمبر) اُنھن ڏانھن نه ڏٺو اٿيئي ڇا؟ جيڪي ڀائيندا آھن ته جيڪي تو ڏانھن لاٿو ويو ۽ جيڪي توکان اڳ لاٿو ويو تنھن کي پاڻ مڃيو اٿن اھي ھن ھوندي به طاغوت (يعني غير خدا) ڏانھن (مقد مونيئي) فيصلو ڪرائڻ گھرندا آھن حالانڪ ان جي نه مڃڻ جو حڪم ڪيو ويو اٿن، ۽ شيطان کين وڏي ڀُل ۾ ڀُلائڻ گھرندو آھي.'),
(554, 4, 61, '۽ جڏھن کين چئجي ته جيڪي الله لاٿو تنھن ڏانھن ۽ پيغمبر ڏانھن اچو ته منافقن کي ڏٺئي ته توکان پوري طرح جھلجي ويندا آھن.'),
(555, 4, 62, 'پوءِ (تڏھن) ڪئن (ڪندا) جڏھن کين ڪا اوکائي اُنھيءَ سببان پھچندي جو سندن ھٿن اڳي ڪيو آھي ته وري تو وٽ اچي الله جو قسم کڻندا ته اسان جو چڱائي ۽ ٺھراءَ کانسواءِ (ٻيو) ڪو ارادو نه ھو.'),
(556, 4, 63, 'اِھي (اُھي) آھن جن جي دلين ۾ جيڪي (ڪفر ۽ نفاق) آھي سو الله ڄاڻندو آھي، پوءِ کانئن مُڙ ۽ کين نصيحت ڪر ۽ کين سندين دلين ۾ پھچندڙ ڳالھه چؤ.'),
(557, 4, 64, 'ڪوئي پيغمبر نه موڪليوسون پر ھن لاءِ ته الله جي حڪم سان سندس تابعداري ڪئي وڃي، ۽ جڏھن انھن (ماڻھن) پاڻ تي ظلم ڪيو تڏھن جيڪڏھن تو وٽ اچن ھا پوءِ الله کان بخشش گھرن ھا ۽ پيغمبر انھن لاءِ بخشش گھري ھا ته الله کي ٻاجھ سان موٽندڙ مھربان لھن ھا.'),
(558, 4, 65, 'پوءِ تنھنجي پالڻھار جو قسم آھي ته (اھي ماڻھو) مسلمان نه آھن جيستائين انھي معاملي ۾ جو سندن وچ ۾ پيو آھي توکان فيصلو ڪرائن پوءِ (تون) جيڪو نبيرو ڪرين تنھن کان پنھنجي دلين ۾ اڙکُڙ نه ڏسن ۽ پوري طرح مڃين.'),
(559, 4, 66, '۽ جيڪڏھن اسين مٿن لازم ڪريون ھا ته اوھين پاڻ کي ڪُھو يا پنھنجن ديسن مان نڪرو ته منجھانئن ٿورڙن کانسواءِ (ٻِيا) اُھو نه ڪن ھا، ۽ جيڪا نصيحت ڪئي وين تنھن موجب جيڪڏھن اُھي ڪم ڪن ھا ته اُنھن لاءِ ضرور ڀلو ۽ (ايمان کي) زيادہ پڪي ڪرڻ وارو ھجي ھا.'),
(560, 4, 67, '۽ انھيءَ وقت پاڻ وٽان وڏو اجر کين ضرور ڏيون ھا.'),
(561, 4, 68, '۽ ضرور کين سڌي واٽ وٺايون ھا.'),
(562, 4, 69, '۽ جيڪي الله ۽ پيغمبر جي تابعداري ڪندا سي انھن سان گڏ رھندڙ آھن جن تي الله فضل ڪيو آھي (يعني) پيغمبر ۽ صديق ۽ شھيد ۽ صالح، ۽ اُھي چڱا سنگتي آھن.'),
(563, 4, 70, 'اِھو فضل الله جي طرفان آھي، ۽ الله ڄاڻندڙ بس آھي.'),
(564, 4, 71, 'اي ايمان وارؤ پنھنجي بچاءُ جو سامان وٺو پوءِ لڙائيءَ لاءِ ٽولي ٽولي ٿي نڪرو يا گڏجي نڪرو.'),
(565, 4, 72, '۽ بيشڪ اوھان مان ڪو (اھڙو) آھي جو (ڄاڻي واڻي) دير ڪندو آھي، پوءِ جيڪڏھن اوھان کي ڪو اھنج پھچندو ته چوندو آھي ته بيشڪ الله مون تي احسان ڪيو جو (آءٌ) ساڻن نه حاضر ھوس.'),
(566, 4, 73, '۽ جيڪڏھن الله (جي پار) کان ڪو احسان اوھان کي پھچي ته (انھيءَ وانگر جو) ڄڻ ته اوھان جي ۽ سندس وچ ۾ دوستي نه ھئي ضرور چوندو آھي ته مون کي ارمان آھي جيڪر ساڻن ھجان ھا ته وڏي مراد ماڻيان ھا.'),
(567, 4, 74, 'پوءِ اُنھن (مؤمنن) کي جڳائي ته الله جي واٽ ۾ وڙھن جيڪي دنيا جي حياتيءَ کي آخرت سان وڪڻندا آھن، ۽ جيڪو الله جي واٽ ۾ وڙھندو پوءِ ماريو ويو يا غالب ٿيو تنھن کي وڏو اجر سگھوئي ڏينداسون.'),
(568, 4, 75, '۽ (اي مسلمانؤ) اوھان کي ڇا (ٿيو) آھي؟ جو الله جي واٽ ۾ نٿا وڙھو؟ حالانڪ ھيڻن مڙسن ۽ زالن ۽ ٻارن مان اُھي آھن جي چون ٿا ته اي اسان جا پالڻھار ھن شھر مان جنھن جا رھاڪو ظالم آھن اسان کي (ٻاھر) ڪڍ، ۽ پاڻ وٽان اسان جو (ڪو) سنڀاليندڙ ڪر، ۽ پاڻ وٽان اسان جو ڪو مددگار ڪر.'),
(569, 4, 76, 'جيڪي مؤمن آھن سي الله جي واٽ ۾ وڙھندا آھن، ۽ جيڪي ڪافر آھن سي شيطان جي واٽ ۾ وڙھندا آھن تنھنڪري شيطان جي ساٿين کي ماريو، ڇوته شيطان جي ٺڳي ھيڻي آھي.'),
(570, 4, 77, '(اي پيغمبر) اُنھن ڏانھن نه ڏٺو اٿيئي ڇا؟ جن کي چيو ويو ته پنھنجن ھٿن کي (ويڙھ کان) جھليو ۽ نماز پڙھو ۽ زڪٰوة ڏيو، پوءِ جنھن مھل مٿن ويڙھ لازم ڪئي ويئي تنھن مھل منجھانئن اوچتو ھڪ ٽولي الله جي خوف وانگر ماڻھن کان ڊڄي ٿي بلڪ ان کان به وڌيڪ ڊڄي ٿي، ۽ چوڻ لڳا ته اي اسانجا پالڻھار ڇالاءِ اسان تي ويڙھ لازم ڪئي اٿيئي، ويجھي وقت تائين تو اسان کي ڇونه ڍر ڏني؟ (اي پيغمبر) چؤ ته دنيا جو سامان ٿورڙو آھي، ۽ آخرت انھيءَ لاءِ ڀلي آھي جيڪو پرھيزگاري ڪندو، ۽ تند جيترو به ظلم نه ڪبو.'),
(571, 4, 78, 'جتي ھوندؤ اُتي اوھان کي موت پھچندو جيتوڻيڪ مُحڪم ڪوٽن ۾ ھوندؤ، ۽ جيڪڏھن کين ڪا چڱائي پھچندي آھي ته چوندا آھن ته ھي الله وٽان (پھتي) آھي، ۽ جيڪڏھن کين ڪو اھنج پھچندو آھي ته ھي تو وٽان پھتو آھي، (کين) چؤ ته (ڏک سک) سڀ الله وٽان آھي، پوءِ ھن قوم کي ڇا (ٿيو) آھي؟ جو ڳالھ جي سمجھڻ کي ويجھو (ئي) نه آھن.'),
(572, 4, 79, 'توکي جيڪا ڪا چڱائي پھچي ٿي سا الله وٽان (ڄاڻ)، ۽ توکي جيڪو ڪو اھنج پھچي ٿو سو پنھنجي نفس (جي ڪمن جي شامت) کان (ڀانءِ)، ۽ توکي ماڻھن لاءِ پيغمبر ڪري موڪليوسون، ۽ الله شاھد بس آھي.'),
(573, 4, 80, 'جيڪو پيغمبر جي فرمانبرداري ڪندو تنھن بيشڪ الله جي فرمانبرداري ڪئي، ۽ جيڪو ڦرندو تن تي توکي نگھبان ڪري نه موڪليو اٿئون.'),
(574, 4, 81, '۽ (منافق تو وٽ) چوندا آھن فرمانبرداري (ڪنداسون) پوءِ جڏھن تو وٽان ھليا ويندا آھن تڏھن منجھانئن ھڪ ٽولي تنھنجي ڳالھين جي ابتڙ رات جو صلاحون ڪندي آھي، ۽ جيڪي رات جو صلاحون ڪندا آھن سي الله لکندو آھي، تنھنڪري کانئن مُنھن موڙ ۽ الله تي ڀروسو ڪر، ۽ الله وڪيل بس آھي.'),
(575, 4, 82, 'قرآن ۾ ڇونه سوچ ڪندا آھن، جيڪڏھن الله کانسواءِ ٻئي (ڪنھن) وٽان ھجي ھا ته منجھس گھڻو اختلاف لھن ھا.'),
(576, 4, 83, '۽ جڏھن وٽن امن جي يا ڀَو جي ڪا خبر ايندي آھي ته ان کي پڌرو ڪندا آھن، ۽ جيڪڏھن ان کي پيغمبر ڏانھن ۽ پنھنجن (مسلمانن) مان اميرن ڏانھن پھچائين ھا ته منجھائن جيڪي اُنکي جاچيندا آھن سي ضرور ان کي (چڱي طرح) ڄاڻن ھا، ۽ جيڪڏھن الله جو فضل ۽ سندس ٻاجھ اوھان تي نه ھجي ھا ته ٿورڙن کانسواءِ ضرور شيطان جي پٺيان لڳو ھا.'),
(577, 4, 84, 'پوءِ (اي پيغمبر) الله جي واٽ ۾ ويڙھ ڪر، تنھنجي سِر کانسواءِ توتي بار نه رکيو ويو آھي ۽ مؤمنن کي ھمٿاءِ، ويجھو ئي الله ڪافرن جي ويڙھ کي روڪڻ تي آھي، ۽ الله ويڙھ جي ڪري ڏاڍو زور وارو ۽ سزا ڏيڻ ۾ (به) ڏاڍو زور وارو آھي.'),
(578, 4, 85, 'جيڪو چڱيءَ ڳالھ جي سفارش ڪندو تنھن لاءِ منجھانئس حصّو آھي، جيڪي بڇڙي ڳالھ جي سفارش ڪندو تنھن لاءِ منجھانئس حصّو آھي، ۽ الله سڀ شيءِ تي نگھبان آھي.'),
(579, 4, 86, '۽ جڏھن اوھانکي سلام ڪرڻ سان تعظيم ڏني وڃي ته ان کان بھتر تعظيم سان (سلام) ورايو يا اوترو ورايو، ڇوته الله سڀڪنھن شيءِ جو حساب ڪندڙ آھي.'),
(580, 4, 87, 'الله (اُھو آھي) جو اُن کانسواءِ ڪو عبادت جو لائق نه آھي، قيامت جي ڏينھن جنھن ۾ (ڪو) شڪ نه آھي اوھان کي ضرور گڏ ڪندو، ۽ الله کان ڪير (پنھنجي) ڳالھ ۾ زور سچو آھي.'),
(581, 4, 88, 'اوھان کي ڇا (ٿيو) آھي جو منافقن جي بابت ٻه ٽوليون (ٿي پيا) آھيو ۽ الله سندن ڪرتوتن سببان کين اونڌو ڪيو آھي، جنھن کي الله ڀلايو تنھن کي اوھين ھدايت ڪرڻ گھُرندا آھيو ڇا؟ جنھن کي الله ڀلائيندو آھي تنھن لاءِ ڪا واٽ ڪڏھن نه لھندين.'),
(582, 4, 89, '(اُھي) گھرندا آھن ته جيڪر اوھين ڪافر ٿيو جھڙا پاڻ ڪافر آھن ته سڀ ھڪ جھڙا ٿيون پوءِ منجھائن ڪنھن کي ايستائين دوست ڪري نه وٺو جيستائين الله جي واٽ ۾ وطن ڇڏين، پوءِ جيڪڏھن ڦري وڃن ته پڪڙيونِ ۽ جتي لھونِ تتي قتل ڪريونِ، ۽ منجھائن ڪنھنکي نڪي دوست ۽ نڪي مددگار ڪري وٺو.'),
(583, 4, 90, 'پر (انھن سان نه وڙھو) جيڪي اھڙيءَ قوم سان ڳنڍيل ھجن جو اوھان جي ۽ انھن جي وچ ۾ انجام ٿيل ھجي يا اوھان وٽ ھن حال ۾ اچن جو اوھان سان وڙھڻ يا پنھنجي قوم سان وڙھڻ کان سندن سينا دِڪ ٿيا ھجن، ۽ جيڪڏھن الله گھري ھا ته کين اوھان تي غالب ڪري ھا پوءِ اوھان کي ضرور مارين ھا، پوءِ جيڪڏھن اوھان کان پاسو ڪن ۽ اوھان سان نه وڙھن ۽ اوھان ڏانھن صُلح (جي پيغام) کي اُڇلين پوءِ الله اوھان لاءِ مٿن ڪا ويڙھ جي واٽ نه ڪئي آھي.'),
(584, 4, 91, 'ٻيءَ ٽوليءَ کي (اوھين) سگھوئي لھندؤ جو اوھان کان امن ۾ ھجڻ گھرندا ۽ پنھنجيءَ قوم کان (به) امن ۾ ھجڻ گھرندا، جڏھن ڪڏھن ڪنھن فتني ڏانھن سڏيا ويندا تڏھن منجھس جھٽ پٽ اونڌي منھن ڪري پوندا، پوءِ جيڪڏھن اوھان کان پاسو نه ڪن ۽ نڪي اوھان ڏانھن صُلح جي پيغام کي اُڇلين ۽ نڪي پنھنجي ھٿن کي (ويڙھ کان) جھلين ته انھن کي پڪڙيو ۽ جتي انھن کي لھو تِتي اُنھن کي ماريو، ۽ انھن تي اسان اوھان کي پڌرو غلبو ڏنو آھي.'),
(585, 4, 92, '۽ مؤمنن جو مارڻ ڪنھن مؤمن کي ڀُل چُڪ کانسواءِ (ڪنھن حال ۾) نه جڳائي، ۽ جيڪو مؤمن ڀُل چُڪ سان ماري ته (ھڪ ته) مؤمن ٻانھي کي آجو ڪري ۽ (ٻيو ته) چَٽي سندس وارثن کي پھچائي پر جي بخشين (ته انھن کي اختيار آھي)، پوءِ جيڪڏھن اوھان جي ويرين جي قوم مان آھي ۽ اُھو مؤمن ھجي ته (ڇڙو) مؤمن ٻانھي کي آجو ڪرڻ لازم آھي (۽ چٽي معاف آھي)، ۽ جيڪڏھن انھيءَ قوم مان آھي جو اوھان جي ۽ انھن جي وچ ۾ پڪو انجام ٿيل آھي ته چٽي سندس وارثن کي پھچائجي ۽ مؤمن ٻانھي کي آجو ڪرڻ لازم آھي، ۽ جيڪو نه لھي ته ٻه مھينا لڳو لڳ روزا رکي، (اھو ڪُفّارو) الله (جي پار) کان توبه جي (قبول ٿيڻ) ٿيڻ لاءِ آھي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(586, 4, 93, '۽ جيڪو مؤمن کي ڄاڻي واڻي ماريندو تنھن جي سزا دوزخ آھي منجھس سدائين رھڻ وارو آھي ۽ الله مٿس ڏمربو ۽ مٿس لعنت ڪندو ۽ اُن لاءِ وڏو عذاب تيار ڪيو اٿس.'),
(587, 4, 94, 'اي ايمان وارؤ جڏھن اوھين الله جي واٽ ۾ (جھاد لاءِ) مسافري ڪريو تڏھن پوري طرح جاچيو ۽ جيڪو اوھانکي اَلسَّلام ڪري تنھن کي اوھين دنيا جي حياتيءَ جو سامان طلبيندڙ ٿي نه چؤ ته تون مؤمن نه آھين، ڇوته الله وٽ گھڻيون غنيمتون آھن، اڳ اوھين به اھڙا ھيؤ پوءِ الله اوھان تي احسان ڪيو پوءِ اوھين (پوري طرح) جاچيو، ڇوته جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.'),
(588, 4, 95, 'مؤمنن مان ڪنھن عذر ڌاران (جھاد کان گھر ۾) ويھي رھندڙ ۽ الله جي واٽ ۾ پنھنجي مالن ۽ پنھنجن سِرن سان جھاد ڪندڙ (اِھي ٻئي) برابر نه آھن، پنھنجن مالن ۽ پنھنجن سِرن سان جھاد ڪندڙن کي الله (گھر ۾) ويھي رھندڙن کان مرتبي ۾ ڀلو ڪيو آھي، ۽ الله ھر ھڪ کي چڱائيءَ جو وعدو ڏنو آھي، ۽ الله جھاد ڪندڙن کي ويھي رھندڙن کان وڏي اجر جي ڪري ڀلو ڪيو آھي.'),
(589, 4, 96, 'پاڻ وٽان درجا ۽ بخشش ۽ ٻاجھ ڪئي اٿس، ۽ الله بخشڻھار ۽ مھربان آھي.'),
(590, 4, 97, 'بيشڪ جن پاڻ تي ظلم ڪيو آھي تن کي ملائڪ مارڻ مھل چوندا آھن ته اوھين ڪھڙي (حال) ۾ ھيؤ؟ اُھي چوندا ته اسين مُلڪ ۾ عاجز هُئاسون، (ملائڪ کين) چوندا ته الله جو مُلڪ ويڪرو نه ھو ڇا؟ جو اُن ۾ لڏي وڃو ھا، پوءِ اُنھن جي جاءِ دوزخ آھي، ۽ اُھو ھنڌ بڇڙو آھي.'),
(591, 4, 98, 'پر مڙسن ۽ زالن ۽ ٻارن مان ھيڻا جو ڪا واہ لھي نه سگھندا آھن ۽ نڪا نڪرڻ جي ڪا واٽ وٺي سگھندا آھن.'),
(592, 4, 99, 'ويجھو انھن کي الله معاف ڪرڻ تي آھي، ۽ الله معافي ڏيندڙ بخشڻھار آھي.'),
(593, 4, 100, 'جيڪو الله جي واٽ ۾ وطن ڇڏيندو سو زمين ۾ گھڻيون رھڻ جون جايون ۽ (روزيءَ) جي ڪشادگي لھندو، ۽ جيڪو الله (جي واٽ ۾) ۽ سندس پيغمبر ڏانھن ھجرت ڪندڙ ٿي پنھنجي گھر کان نڪرندو پوءِ کيس موت پھچندو تنھن جو اجر بيشڪ الله تي (لازم) آھي، ۽ الله بخشڻھار مھربان آھي.'),
(594, 4, 101, '۽ جڏھن اوھين ملڪ ۾ مسافري ڪريو تڏھن جيڪڏھن ڊڄو ٿا ته ڪافر اوھان کي پريشان ڪندا ته نماز مان ڪجھ گھٽائڻ ۾ اوھان تي گناہ ڪونھي، ڇوته ڪافر اوھان جا پڌرا ويري آھن.'),
(595, 4, 102, '۽ (اي پيغمبر ويڙھ مھل) جڏھن منجھن ھجين پوءِ کين نماز پڙھائين ته منجھائن ھڪ ٽوليءَ کي توسان (نماز ۾) بيھڻ گھرجي ۽ پنھنجن ھٿيارن کي (ساڻ) وٺڻ گھرجين، پوءِ جنھن مھل (اُھي) سجدو ڏيئي رھن (تنھن مھل) اوھان جي پوئتان ٿين، ۽ جنھن ٻئي ٽوليءَ نماز نه پڙھي آھي سي اچن تن کي توسان نماز پڙھڻ گھرجي ۽ پنھنجي بچاءَ (جي شين) ۽ پنھنجن ھٿيارن کي وٺڻ گھرجين، ڪافر گھرندا آھن ته جيڪر اوھين پنھنجن ھٿيارن ۽ پنھنجي سامان کان (ڪجھ) بي خبر ٿيو ته اوھان تي ھڪ ئي ڀيري حملو ڪن، ۽ جيڪڏھن اوھان کي مينھن کان ڪو اھنج ھجي يا اوھين بيمار ھجو ته اوھان تي گناہ نه آھي ته پنھنجا ھٿيار لاھي رکو، ۽ پنھنجي بچاءَ جون شيون (ساڻ) وٺو، بيشڪ الله ڪافرن لاءِ خواري ڏيندڙ عذاب تيار ڪيو آھي.'),
(596, 4, 103, 'پوءِ جڏھن نماز پوري ڪريو (تڏھن) بيٺي ۽ ويٺي ۽ پنھنجي پاسن ڀر الله کي ياد ڪريو، پوءِ جڏھن اوھين دلجاءِ ڪريو ته پوءِ (اصلي دستور تي) نماز پڙھو، بيشڪ نماز مؤمنن تي ٺھرايل وقت ۾ فرض آھي.'),
(597, 4, 104, '۽ ڪافرن جي ڳول ڦول ۾ سُستي نه ڪريو، جيڪڏھن اوھين ڏکوئبا آھيو ته اُھي به اوھان جھڙا ڏکوئبا آھن، ۽ اوھين الله ۾ اھا اميد ٿا رکو جا اميد انھن کي نه آھي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(598, 4, 105, '(اي پيغمبر) بيشڪ اسان توڏانھن سچو ڪتاب ھن لاءِ لاٿو آھي ته جئن الله توکي سيکاريو آھي تئن ماڻھن جي وچ ۾ فيصلو ڪرين، ۽ خيانت ڪندڙن جي پاس نه ڪر.'),
(599, 4, 106, '۽ الله کان بخشش گُھر، ڇوته الله تعالىٰ بخشڻھار مھربان آھي.'),
(600, 4, 107, '۽ جيڪي پنھنجن نفسن سان خيانت ڪندا آھن تن جي پاران (تون) نه جھيڙ، بيشڪ الله خيانتي گنھگار کي دوست نه رکندو آھي.'),
(601, 4, 108, 'ماڻھن کان لڪندا آھن ۽ الله کان لڪي نه سگھندا ۽ جڏھن رات جو اُھا صلاح ڪندا آھن جنھن صلاح کان الله راضي نه آھي (تڏھن) اُھو (الله) ساڻن آھي، ۽ جيڪي ڪندا آھن تنھن کي الله گھيرو ڪندڙ آھي.'),
(602, 4, 109, 'خبردار اوھين اھڙي قوم آھيو جو دنيا جي حياتي ۾ (ته) سندن پاران بحث ڪندا آھيو، پوءِ قيامت جي ڏينھن سندن پاران الله سان ڪير جھيڙو ڪندو؟ يا مٿن ڪير ڀرجھلو ٿيندو؟'),
(603, 4, 110, '۽ جيڪو بڇڙو ڪم ڪندو يا پاڻ تي ظلم ڪندو وري الله کان بخشش گھرندو سو الله کي بخشڻھار مھربان لھندو.'),
(604, 4, 111, '۽ جيڪوئي گناہ ڪري ٿو سو پاڻ لاءِ ئي ڪري ٿو، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(605, 4, 112, '۽ جيڪو ڪا خطا يا گناہ ڪري وري اُھا (تھمت) ڪنھن بيگناہ تي ھڻي تنھن بيشڪ بُھتان ۽ پڌرو گناہ کنيو.'),
(606, 4, 113, '۽ جيڪڏھن توتي الله جو فضل ۽ سندس ٻاجھ نه ھجي ھا ته منجھانئن ھڪ ٽوليءَ توکي غلطي ۾ وجھڻ جي پڪي نيت ڪئي ھئي، ۽ (اُھي) پاڻ کانسواءِ (ڪنھن کي) نه ڀلائيندا آھن ۽ توکي ڪوبه ضرر نه پھچائيندا، ۽ الله توتي ڪتاب ۽ دانائي لاٿي آھي ۽ جيڪي تون نه ڄاڻندو ھئين سو توکي سيکاريائين، ۽ توتي الله جو وڏو فضل آھي.'),
(607, 4, 114, 'سندين گھڻين ڳجھين صلاحن ۾ ڪا چڱائي نه آھي پر جنھن خيرات ڏيڻ يا چڱي ڪم يا ماڻھن جي وچ ۾ سڌاري ڪرڻ جو حڪم ڪيو (تنھن جي صلاح ۾ چڱائي آھي)، ۽ جيڪو اُھو ڪم الله جي رضامندي ڳولڻ لاءِ ڪندو پوءِ سگھوئي اُن کي وڏو اجر ڏينداسون.'),
(608, 4, 115, '۽ جيڪو رستي جي پڌري ٿيڻ کانپوءِ پيغمبر جي مخالفت ڪندو ۽ مؤمنن جي واٽ کانسواءِ ٻي واٽ وٺندو سو جيڏانھن متوجه ٿيو آھي تيڏانھن کيس ڦيرينداسون ۽ کيس دوزخ ۾ گھيرينداسون، ۽ (اھا) جاءِ بڇڙي آھي.'),
(609, 4, 116, 'بيشڪ الله ھيءُ (ڏوہ) نه بخشيندو جو (ڪنھن کي) ساڻس شريڪ ڪيو وڃي ۽ اُن کانسواءِ (ٻيو گناہ) جنھن لاءِ گھرندو تنھن کي بخشيندو، ۽ جيڪو الله سان (ڪنھن کي) شريڪ بڻائيندو سو بيشڪ وڏي ڀُل ۾ ڀلو.'),
(610, 4, 117, '(ھيءُ مشرڪ) ان (الله) کانسواءِ عورتن کي ئي سڏيندا آھن، ۽ (حقيقت ۾) تڙيل شيطان کانسواءِ ٻئي کي نه سڏيندا آھن.'),
(611, 4, 118, 'ان تي الله لعنت ڪئي آھي، ۽ (شيطان) چيو ته تنھنجي ٻانھن مان مُقرّر ڪيل حصّو ضرور وٺندس.'),
(612, 4, 119, '۽ کين ضرور گمراہ ڪندس ۽ کين ضرور سَڌن ۾ وجھندس ۽ کين ضرور حُڪم ڪندس پوءِ ضرور ڍورن جي ڪنن کي چيريندا ۽ کين ضرور حُڪم ڪندس ته الله جي جوڙيل (صورت) کي ڦٽائيندا (رھندا) ۽ جيڪو الله کانسواءِ شيطان کي دوست ڪري وٺندو تنھن بيشڪ پڌرو ڇيو پاتو.'),
(613, 4, 120, 'شيطان کين انجام ڏيندو آھي ۽ کين سَڌن ۾ وجھندو آھي، ۽ شيطان ٺڳيءَ کانسواءِ (ٻيو ڪو) انجام نه ڏيندو اٿن.'),
(614, 4, 121, 'اھڙن (ماڻھن) جي جاءِ دوزخ آھي، ۽ اُن کان ڀڄڻ جي ڪا واہ نه لھندا.'),
(615, 4, 122, '۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي سگھوئي (اھڙن) باغن ۾ داخل ڪنداسين جن جي ھيٺان واھيون وھنديون آھن منجھن سدائين رھڻ وارا آھن، (اھو) الله جو پڪو انجام آھي، ۽ انجام ڏيڻ ۾ الله کان وڌيڪ سچو ڪير آھي.'),
(616, 4, 123, '(۽) نڪي اوھان جي سَڌن تي نڪي ڪتاب وارن جي سَڌن تي (ڇوٽڪارو) آھي، جيڪو ڪا مدائي ڪندو تنھن کي ان جي سزا ڏبي ۽ الله کانسواءِ پاڻ لاءِ نڪو سنڀاليندڙ ۽ نڪو مددگار لھندو.'),
(617, 4, 124, '۽ جيڪي (ماڻھو) مرد يا زال مؤمن ٿي چڱا ڪم ڪندا سي بھشت ۾ گھڙندا ۽ تر جيترو به ظلم نه ڪبن.'),
(618, 4, 125, '۽ دين جي ڪري انھيءَ کان وڌيڪ چڱو ڪير آھي؟ جنھن پنھنجو مُنھن (سر) الله لاءِ نوايو ۽ اُھو چڱائي ڪندڙ آھي ۽ ابراھيم حنيف (ھڪ طرفي) جي دين جي پيروي ڪئي اٿس، ۽ الله ابراھيم کي پنھنجو خاص دوست بڻايو ھو.'),
(619, 4, 126, 'جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله جو آھي، ۽ الله سڀڪنھن شيءِ تي گھيرو ڪندڙ آھي.'),
(620, 4, 127, '(اي پيغمبر) زالن بابت توکان پڇن ٿا، چؤ ته الله انھن بابت اوھان کي حُڪم ڪري ٿو ۽ (پڻ) جيڪي اوھان کي (ھن) ڪتاب ۾ (اڳ) پڙھي ٻڌايو ويو آھي سو انھن يتيمن زالن بابت آھي جن کي سندن حق جيڪو انھن لاءِ لازم ڪيو ويو سو نه ڏيندا آھيو ۽ کين پرڻجڻ جي خواھش ڪندا آھيو ۽ (پڻ) ھيڻن ٻارن بابت (سو اُھو حُڪم قائم آھي) ۽ يتيمن (جي حقن ادا ڪرڻ) لاءِ انصاف تي بيھڪ وٺو، ۽ اوھين جيڪا ڪا چڱائي ڪندؤ تنھن کي الله ڄاڻندڙ آھي.'),
(621, 4, 128, '۽ جيڪڏھن ڪا زال پنھنجي مڙس جي بي پرواھيءَ يا منھن موڙڻ کان ڊڄي ته ٻئي ڄڻا پاڻ ۾ صُلح ڪن (ڪنھن به قسم جو صُلح) ته ٻنھين تي گناھ نه آھي، ۽ صُلح ڀلو آھي، ۽ نفس (ته) بخل کي ويجھو ڪيا ويا آھن، ۽ جيڪڏھن اوھين چڱائي ڪندؤ ۽ پرھيزگار ٿيندؤ ته جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.'),
(622, 4, 129, '۽ اوھين زالن جي وچ ۾ ڪڏھن به برابري رکي نه سگھندؤ توڻيڪ گھڻي خواھش ڪريو تنھنڪري اوھين (ھڪ پاسي اھڙو) سارو لاڙو نه لڙو جو ٻيءَ کي لڙڪيل وانگر ڇڏيو، ۽ جيڪڏھن اوھين (پاڻ ۾) صُلح ڪريو ۽ پرھيزگار ٿيو ته الله بخشڻھار مھربان آھي.'),
(623, 4, 130, '۽ جيڪڏھن ٻئي جدا ٿيندا ته الله پنھنجي فضل سان ھر ڪنھن کي آسودو ڪندو، ۽ الله (مھربانيءَ ۾) ڪشادگيءَ وارو حڪمت وارو آھي.'),
(624, 4, 131, 'جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله جو آھي، ۽ اوھان کان اڳ ڪتاب وارن کي ۽ (ھاڻي) خلاصو اوھان کي بيشڪ وصيت ڪئي اٿئون ته الله کان ڊڄو، ۽ جيڪڏھن نه مڃيندؤ ته (سمجھو ته) جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (خاص) الله جو آھي، ۽ الله بي پرواہ ساراھيل آھي.'),
(625, 4, 132, '۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (خاص) الله جو آھي، ۽ الله ڀرجھلو بس آھي.'),
(626, 4, 133, 'اي ماڻھؤ جيڪڏھن الله گھري ته اوھان کي فنا ڪري ڇڏي ۽ ٻـين کي پيدا ڪري، ۽ الله انھيءَ (ڪم) تي وس وارو آھي.'),
(627, 4, 134, 'جيڪو دنيا ۾ (پنھنجين چڱائين جو) ثواب گھرندو آھي ته الله وٽ دنيا ۽ آخرت جو ثواب آھي، ۽ الله ٻڌندڙ ڏسندڙ آھي.'),
(628, 4, 135, 'اي ايمان وارؤ! انصاف تي بيھندڙ الله ڪارڻ شاھدي ڏيندڙ ھجو توڻيڪ اوھان کي پاڻ تي يا ماءُ پيءُ يا مٽن تي ھجي، جيڪڏھن (اُھو ماڻھو جنھن تي حق ثابت ٿئي ٿو سو) آسودو ھجي يا سڃو ته الله اُنھن تي وڌيڪ مھربان آھي، پوءِ انصاف ڪرڻ ۾ سَڌن جي پٺيان نه لڳو، ۽ جيڪڏھن اوھين وچڙائي ڳالھائيندؤ يا منھن موڙيندؤ ته جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.'),
(629, 4, 136, 'اي مسلمانؤ الله کي ۽ سندس پيغمبر کي ۽ جيڪو ڪتاب پنھنجي پيغمبر تي لاٿائين ۽ جيڪو کانئس اڳ لاٿائين تنھن کي (به مڃيو)، ۽ جيڪو الله ۽ سندس ملائڪن ۽ سندس ڪتابن ۽ سندس پيغمبرن ۽ قيامت جي ڏينھن کي نه مڃيندو سو بيشڪ وڏيءَ ڀل ۾ ڀلو.'),
(630, 4, 137, 'جن ايمان آندو وري ڪفر ڪيائون وري ايمان آندائون وري ڪفر ڪيائون وري ڪفر ۾ وڌندا ويا تن کي الله ڪڏھن نه بخشيندو ۽ نڪي کين ڪا واٽ ڏيکاريندو.'),
(631, 4, 138, '(اي پيغمبر) انھن منافقن کي (ھيءَ) سڌ ڏي ته انھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(632, 4, 139, 'جيڪي مؤمنن ڌاران ڪافرن کي دوست ڪري وٺندا آھن، اُنھن وٽان مانُ گھرندا آھن ڇا؟ ڇوته ھيڪاندو مانُ الله جوئي آھي.'),
(633, 4, 140, '۽ بيشڪ الله اوھان تي ڪتاب ۾ (ھيءُ حڪم) لاٿو آھي ته جڏھن (اوھين) ٻڌو ته الله جي آيتن جو انڪار ڪيو وڃي ٿو ۽ مٿن ٺـٺولي ڪئي وڃي ٿي تڏھن (اوھين) ساڻن ايسين نه ويھو جيسين ٻيءَ ڪنھن ڳالھ ۾ بحث نه ڪن، اوھين اُنھيءَ مھل (جي وھندؤ ته) انھن جھڙا ٿيندؤ، ڇوته الله منافقن ۽ ڪافرن سڀني کي دوزخ ۾ گڏ ڪندڙ آھي.'),
(634, 4, 141, 'اھي (منافق) اوھان جو انتظار ڪڍندا آھن، پوءِ جيڪڏھن الله (جي پارکان) اوھان لاءِ ڪا فتح آھي ته چون ٿا ته (اسين) اوھان سان گڏ نه هُئاسون ڇا؟ ۽ جيڪڏھن (سوڀ جو) ڀاڱو ڪافرن جو آھي ته ڪافرن کي چون ٿا ته اسين اوھان تي غالب نه آھيون ڇا؟ ۽ مؤمنن کان اوھان کي نه بچايوسون ڇا؟ پوءِ الله قيامت جي ڏينھن اوھانجي وچ ۾ نبيرو ڪندو، ۽ الله ڪافرن کي مؤمنن تي غلبي جي (ڪا) واٽ نه ڪندو.'),
(635, 4, 142, 'منافق (پنھنجي ڀانئڻ ۾) الله کي دلبو ڏيندا آھن ۽ (حقيقت ڪري) الله کين دلبو ڏيندڙ آھي، ۽ جڏھن نماز ڏانھن اٿندا آھن تڏھن آرسي ٿي اُٿندا آھن رڳو ماڻھن کي ڏيکاريندا آھن ۽ ٿورڙي کانسواءِ الله کي ياد نه ڪندا آھن.'),
(636, 4, 143, 'نه ھِن ٽولي ڏانھن آھن ۽ نڪي ھُن ٽولي ڏانھن آھن انھيءَ وچ ۾ حيران آھن، ۽ جنھن کي الله ڀلائيندو تنھن لاءِ ڪڏھن واٽ نه لھندين.'),
(637, 4, 144, 'اي ايمان وارؤ اوھين ڪافرن کي مؤمنن کانسواءِ دوست ڪري نه وٺو، پاڻ تي الله جو پڌرو الزام ثابت ڪرڻ گھرندا آھيو ڇا؟'),
(638, 4, 145, 'بيشڪ مُنافق باھ جي ھيٺين طبقي ۾ ھوندا، ۽ انھن جو مددگار ڪوئي نه لھندين.'),
(639, 4, 146, 'پر جن توبه ڪئي ۽ پاڻ سڌاريو ۽ الله کي چنبڙي ورتو ۽ خالص الله جا حُڪم مڃيا سي مؤمنن سان آھن، ۽ الله مؤمنن کي وڏو اجر سگھو ڏيندو.'),
(640, 4, 147, 'جيڪڏھن (سندس نعمتون ڄاڻي) شڪر ڪندؤ ۽ ايمان آڻيندؤ ته الله اوھانکي ڇالاءِ عذاب ڪندو؟ ۽ الله ته قدر سڃاڻندڙ ڄاڻندڙ آھي.'),
(641, 4, 148, 'الله مظلوم کانسواءِ ٻئي ڪنھن جو (ڪنھين کي) وڏي سڏ (۽ کليل طرح) بڇڙو ڳالھائڻ پسند نه ڪندو آھي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(642, 4, 149, 'جيڪڏھن اوھين ڪا چڱائي پڌري طرح ڪندا آھيو يا اُھا لڪائي ڪندا آھيو يا مدائي کان ٽارو ڪندا آھيو ته بيشڪ الله معافي ڏيندڙ وس وارو آھي.'),
(643, 4, 150, 'بيشڪ جيڪي الله ۽ سندس پيغمبرن کي نه مڃيندا آھن ۽ الله ۽ سندس پيغمبرن جي وچ ۾ فرق ڪرڻ گھرندا آھن ۽ چوندا آھن ته ڪن پيغمبرن کي مڃيون ٿا ۽ ڪن (پيغمبرن) کي نه ٿا مڃيون ۽ اُنھيءَ وچ ۾ ڪا ٻي واٽ وٺڻ گھرندا آھن.'),
(644, 4, 151, 'اُھي شخص سي پڪ ڪافر آھن، ۽ ڪافرن لاءِ خواري ڏيندڙ عذاب تيار ڪيو اٿئون.'),
(645, 4, 152, '۽ جن الله ۽ سندس پيغمبرن کي مڃيو ۽ منجھانئن ڪنھن ھڪ جي وچ ۾ فرق نه ڪيو اُنھن کي الله سندن اجر سگھو ڏيندو، ۽ الله بخشڻھار مھربان آھي.'),
(646, 4, 153, '(اي پيغمبر) ڪتاب وارا توکي سوال ٿا ڪن ته آسمان کان (ھڪ ئي ڀيري) مٿن ڪتاب لاھين (ان جو خيال نه ڪر) پوءِ تحقيق ان کان (به) وڏو سوال موسىٰ کان ڪيو ھوائون پوءِ چيائون ته اسان کي الله (اکين سان) چٽو ڏيکار پوءِ سندن گناہ سببان مٿن وڄ ڪري، وري پاڻ وٽ مُعجزن جي اچڻ کانپوءِ گابي کي (خُدا ڪري) ورتائون پوءِ اُن (سندن ڏوھ) کان معافي ڏنيسون، ۽ موسىٰ کي پڌرو غلبو ڏنوسون.'),
(647, 4, 154, '۽ کانئن انجام وٺڻ لاءِ سندن مٿان (جبل) طُور کڙو ڪيوسون ۽ کين چيوسون ته (شھر جي) دروازي کان سجدو ڪندڙ ٿي گھڙو ۽ کين (ھيءُ به) فرمايوسون ته ڇنڇر (جي ڏينھن مڇي نه مارڻ جي حڪم ڪيل) ۾ حد کان نه لنگھو ۽ کانئن پڪو انجام ورتوسون.'),
(648, 4, 155, 'پوءِ سندن انجام ڀڃڻ ۽ الله جي آيتن کي سندن نه مڃڻ ۽ پيغمبرن کي سندن ناحق مارڻ (سببان سزا جوڳا ٿيا) ۽ سندن (انھيءَ) چوڻ سببان ته اسان جو دليون ڍڪيل آھن، (نه!) بلڪ الله سندن ڪفر سببان مٿن مُھر ھنئي آھي تنھنڪري ٿورن کانسواءِ ايمان نه آڻيندا.'),
(649, 4, 156, '۽ سندن ڪفر سببان ۽ مريم تي سندن وڏي بُھتان چوڻ سببان.'),
(650, 4, 157, '۽ سندن ھن چوڻ سببان ته الله جي پيغمبر مَسِيح عيسىٰ پٽ مريم جي کي ڪُٺو اٿئون، حالانڪ نه کيس ڪُٺائون ۽ نڪي کيس سوريءَ تي چاڙھيائون پر انھن کي شُبه ۾ وڌو ويو، ۽ بيشڪ جن ان بابت تڪرار ڪيو سي کانئس شڪ ۾ آھن، گمان کانسواءِ کين ان جي ڪا پڪي ڄاڻپ نه آھي، ۽ پڪ سان نه ڪُٺو اٿن.'),
(651, 4, 158, 'بلڪ الله کيس پاڻ ڏانھن مٿي کڻي ورتو، ۽ الله غالب حڪمت وارو آھي.'),
(652, 4, 159, '۽ ڪتاب وارن مان ڪوئي نه رھندو پر سندس موت کان اڳ (سڀئي) مٿس ضرور ايمان آڻيندا، ۽ قيامت جي ڏينھن (عيسى) مٿن شاھد ھوندو.'),
(653, 4, 160, 'پوءِ يھودين جي ظلم سببان (گھڻيون) سٺيون شيون جي انھن لاءِ حلال ڪيل ھيون سي مٿن حرام ڪيوسون ۽ (پڻ) گھڻن ماڻھن کي الله جي واٽ کان سندن جھلڻ سببان.'),
(654, 4, 161, '۽ پڻ سندن وياج وٺڻ سببان حالانڪ بيشڪ کانئس جھليا (به) ويا ھئا ۽ (پڻ) ماڻھن جي مالن کي سندن ناحق کائڻ سببان (گھڻيون حلال شيون مٿن حرام ڪيون ويون)، ۽ اُنھن مان ڪافرن لاءِ ڏکوئيندڙ عذاب تيار ڪيو اٿئون.'),
(655, 4, 162, 'پر منجھانئن علم ۾ پڪا ۽ مؤمن جيڪي توڏانھن نازل ڪيو ويو تنھن کي ۽ جيڪي توکان اڳ نازل ڪيو ويو تنھن کي مڃيندا آھن ۽ نماز پڙھندڙ ۽ زڪٰوة ڏيندڙ ۽ الله ۽ قيامت جي ڏينھن کي مڃيندڙ آھن، سگھوئي انھن کي وڏو اجر ڏينداسون.'),
(656, 4, 163, 'اسان توڏانھن (اھڙيءَ طرح) وحي ڪيو جھڙي طرح نوح ڏانھن ۽ کانئس پوءِ (ٻـين) پيغمبرن ڏانھن وحي ڪيوھوسون، ۽ ابراھيم ۽ اسماعيل ۽ اسحاق ۽ يعقوب ۽ يعقوب جي اولاد ۽ عيسىٰ ۽ اَيُّوب ۽ يُونس ۽ ھارون ۽ سليمان ڏانھن وحي ڪيوسون، ۽ داؤد کي زبور ڏنوسون.'),
(657, 4, 164, '۽ ٻيا (گھڻا) پيغمبر آھن جن جو ھن کان اڳ توکي بيان ٻڌايوسون ۽ (ٻيا گھڻا) پيغمبر آھن جن جو بيان توکي نه ٻڌايوسون، ۽ الله موسىٰ سان گفتگو ڪئي.'),
(658, 4, 165, 'پيغمبر خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ھن لاءِ آھن ته پيغمبرن (جي اچڻ) کانپوءِ ماڻھن جي ڪا حُجّت الله تي نه رھي، ۽ الله غالب حڪمت وارو آھي.'),
(659, 4, 166, 'پر الله جيڪو (ڪتاب) توڏانھن لاٿو آھي تنھن لاءِ (پاڻ) شاھدي ٿو ڏئي ته اُھو پنھنجي علم سان نازل ڪيو اٿس، ۽ ملائڪ به شاھدي ٿا ڏين، ۽ الله گواہ بس آھي.'),
(660, 4, 167, 'بيشڪ جن انڪار ڪيو ۽ الله جي واٽ کان (ماڻھن) کي جھليو سي بيشڪ وڏي ڀُل ۾ ڀُلا.'),
(661, 4, 168, 'تحقيق جن انڪار ڪيو ۽ ظُلم ڪندا رھيا ته الله (جو شان اھڙو) نه آھي جو کين بخشي ۽ نڪي (اھڙو) آھي جو کين (ٻي) ڪا (سڌي) واٽ ڏيکاري.'),
(662, 4, 169, 'دوزخ جي واٽ کانسواءِ منجھس سدائين رھڻ وارا آھن، ۽ اھو الله تي سھنجو آھي.'),
(663, 4, 170, 'اي ماڻھو اوھان جي پالڻھار وٽان پيغمبر (مُحَمَّد صَلَّي اللهُ عَلَيۡہِ وَسَلَّم) اوھان وٽ سچ وٺي آيو آھي تنھنڪري (مٿس) ايمان آڻيو جو اوھان لاءِ چڱو آھي ۽ جيڪڏھن نه مڃيندؤ (ته اوھان جو نقصان آھي) ڇوته جيڪي آسمانن ۽ زمين ۾ آھي سو الله جو آھي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(664, 4, 171, 'اي ڪتاب وارؤ پنھنجي دين ۾ حد کان نه لنگھو ۽ نڪي سچ کانسواءِ الله تي (ڪي ٻيو) چئو، عيسىٰ پُٽ مريم جو الله جو پيغمبر ۽ سندس (خاص) حُڪم (سان پيدا ڪيل) آھي، اُھو مريم ڏانھن ھَلايائين ۽ سندس پار کان ھڪ رُوح آھي، تنھنڪري الله ۽ سندس پيغمبرن کي مڃيو، ۽ (اوھين) ٽي (خدا) نه چئو، (اھڙي چوڻ کان پاڻ کي) جھليو (اِھو) اوھان لاءِ ڀلو آھي، ھڪ الله کانسواءِ ڪو عبادت جو لائق نه آھي، پُٽن جي ھجڻ کان اُھو پاڪ آھي، جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو اُن جو آھي، ۽ الله سنڀاليندڙ بس آھي.'),
(665, 4, 172, 'عيسىٰ الله جي ٻانھي ھُجڻ کان بنھ عار نه ڪندو آھي ۽ نڪي مُقرّب مَلائِڪ، ۽ جيڪو سندس ٻانھپ کان عار ڪندو ۽ وڏائي ڪندو تن سڀني کي سگھوئي (الله) پاڻ وٽ گڏ ڪندو.'),
(666, 4, 173, 'پوءِ جن ايمان آندو آھي ۽ چڱا ڪم ڪيا آھن تن کي سندن اجر پُورو ڏيندو ۽ (بلڪ) پنھنجي فضل سان کين وڌيڪ ڏيندو، ۽ جن (ٻانھي ھجڻ کان) عار ڪيو ۽ وڏائي ڪئي تن کي ڏکوئيندڙ عذاب جي سزا ڏيندو.'),
(667, 4, 174, 'اي ماڻھؤ بيشڪ اوھان وٽ اوھان جي پالڻھار کان ھڪ پڪي سند آئي آھي ۽ اوھان ڏانھن چِٽو سوجھرو (قرآن) لاٿوسون.'),
(668, 4, 175, 'پوءِ جن الله کي مڃيو ۽ کيس چنبڙي ورتو تن کي پنھنجي پاران ٻاجھ ۽ فضل ھيٺ سگھو داخل ڪندو ۽ پاڻ ڏانھن کين سڌو رستو ڏيکاريندو.'),
(669, 4, 176, '(اي پيغمبر جنھن کي اولاد ۽ ماءُ پيءُ نه ھجي تنھنجي ميراث بابت) توکان پڇن ٿا، چؤ ته الله ڪلاله (جي ميراث) بابت اوھان کي فتوىٰ ٿو ڏئي، جيڪڏھن (ڪو اھڙو) مڙس مري جنھنکي اولاد نه آھي (۽ نه ماءُ پيءُ) ۽ کيس ھڪ ڀيڻ آھي ته جيڪي ڇڏيائين تنھنجو اڌ اُن لاءِ آھي، ۽ (جيڪڏھن اھڙي زال مري وڃي ۽ ڀاءُ جيئرو اٿن ته) ڀاءُ سندس وارث آھي سو تڏھن جو کيس اولاد نه ھجي، پوءِ جيڪڏھن ٻه ڀينر ھجن ته جيڪي ڇڏيائين تنھن مان ٻنھي لاءِ ٻه ٽھايون آھن، ۽ جيڪڏھن (وارث) ڀينرون ۽ ڀائر ھجن ته مڙس (يعني ڀاءُ) لاءِ ٻن زالن (ڀينرن) جي حصّي جيترو آھي، الله اوھان لاءِ بيان ڪندو آھي ته متان (اوھين) گمراہ ٿيو، ۽ الله سڀڪنھن شيءِ کي ڄاڻندڙ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'اي ايمان وارؤ (پنھنجن) انجامن کي پاڙيو، جيڪي اوھان تي پڙھبو تنھن کانسواءِ (ٻيا) چوپايا ڍور اوھان لاءِ حلال ڪيا ويا آھن پر اوھين احرام وارا ٿي شڪار کي حلال سمجھندڙ نه ٿيو، بيشڪ الله جيڪي گھرندو آھي سو حُڪم ڪندو آھي.'),
(671, 5, 2, 'اي ايمان وارؤ الله جي عبادت جي نشانين جي بي تعظيمي نه ڪريو ۽ نڪي تعظيم واري مھيني ۾ ۽ نڪي (ڪعبه ڏانھن موڪليل) قربانيءَ (جي جانور) جي ۽ نڪي قلائد (الله لاءِ نذر ڪيل جانورن جي ڳچين ۾ ٻڌل ڳانن) جي ۽ نڪي تعظيم واري گھر (بيت الله) ڏانھن ويندڙن جي جيڪي پنھنجي پالڻھار جو فضل ۽ رضامندي گھرندڙ آھن، ۽ جڏھن اوھين احرام کوليو تڏھن (ڀَلي) شڪار ڪريو، ۽ اوھان کي انھن ماڻھن جي دشمني جن اوھان کي تعظيم واري مسجد کان جھليو ھن ڳالھ جو سبب نه ٿئي جو (اوھين) حد کان لنگھو، ۽ چڱائي ۽ پرھيزگاريءَ تي پاڻ ۾ مدد ڏيو، ۽ گناہ ۽ حد کان لنگھڻ تي ھڪ ٻئي کي مدد نه ڏيو، ۽ الله کان ڊڄو، بيشڪ الله سخت عذاب ڪرڻ وارو آھي.'),
(672, 5, 3, 'اوھان تي حرام ڪيو ويو آھي مُئل جانور ۽ (وھندڙ) رت ۽ سُوئر جو گوشت ۽ جنھن شيءِ کي الله جي نالي کانسواءِ ٻئي جي نالي سان سڏيو وڃي (يعني الله کان سواءِ ٻئي جي نالي مڃتا ڪيل ھجي) ۽ گھُٽو ڏيئي ماريل ۽ ڌڪ سان مُئل ۽ ڪِرڻ سان مُئل ۽ ڍور جي اُکل سان مُئل (حرام ڪيو ويو آھي) ۽ جيڪو (جانور) ڦاڙيندڙ کاڌو (سو به) پر جنھنکي (مرڻ کان اڳ) ذبح ڪيوَ (سو حرام نه آھي) ۽ جو (جانور) آستانن تي ذبح ڪجي (سو حرام ڪيو ويو آھي) ۽ اوھانجو تيرن سان وراھڻ (به)، اِھي (مڙئي) گناہ جا ڪم آھن، اڄ ڪافر اوھان جي دين کان نا اميد ٿيا تنھنڪري کانئن نه ڊڄو ۽ مون کان ڊڄو، اڄ اوھان جو دين اوھان لاءِ ڪامل ڪيم ۽ اوھان تي پنھنجي نعمت پوري ڪيم ۽ اوھان لاءِ دين اسلام پسند ڪيم، پوءِ جيڪو گناہ ڏانھن نه لاڙو ڪندڙ ٿي بک ۾ لاچار ٿئي (۽ مٿيون حرام شيون کائي ته روا آھي)، پوءِ تحقيق الله بخشڻھار مھربان آھي.'),
(673, 5, 4, '(اي پيغمبر) توکان پڇن ته اُنھن لاءِ ڪھڙيون شيون حلال ڪيون ويون آھن، چؤ ته اوھان لاءِ (سڀ) سٺيون (شيون) حلال ڪيون ويون آھن ۽ (اُھو شڪار به حلال آھي) جيڪي شڪاري جانورن کي شڪار سيکاريندڙ ٿي (اوھان) سيکاريو آھي (يعني) جيڪي الله (شڪار ڪرڻ جو دستور) اوھان کي سيکاريو تنھن مان انھن کي سيکاريو ھجي، ته جيڪو (شڪار اُھي) اوھان لاءِ جھلين تنھن مان کائو ۽ اُن (جانور کي بڇڻ مھل) تي الله جو نالو (يعني بِسۡمِ اللهِ اَللهُ اَڪۡبرُ) چئو ۽ الله کان ڊڄو، بيشڪ الله سخت حساب وٺندڙ آھي.'),
(674, 5, 5, 'اڄ اوھان لاءِ سٺيون شيون حلال ڪيون ويون آھن، ۽ ڪتاب وارن جو طعام (به) اوھان لاءِ حلال آھي، ۽ اوھان جو طعام انھن لاءِ حلال آھي، ۽ (پڻ) پاڪدامن مؤمنياڻيون ۽ جن کي اوھان کان اڳ ڪتاب ڏنو ويو تن مان پاڪدامن (عورتون) جڏھن پرڻو ڪندڙ نه زنا ڪندڙ ۽ نه ڳجھي آشنائي رکندڙ ٿي کين سندن ڪابين ڏيو (ته حلال ڪيون ويون)، ۽ جيڪو ايمان (يعني اُنھن حُڪمن مڃڻ) جو انڪار ڪندو تنھنجو ڪيو ڪمايو بيشڪ ناس ٿي ويو، ۽ اُھو آخرت ۾ ڇيئي وارن مان آھي.'),
(675, 5, 6, 'اي ايمان وارؤ جڏھن اوھين نماز لاءِ اُٿو تڏھن پنھنجن مُنھن کي ۽ پنھنجن ھٿن کي ٺونٺين تائين ڌوئو ۽ پنھنجن مٿن کي مسح ڪريو ۽ پنھنجن پيرن کي ڀيڏين تائين (ڌوئو) ۽ جيڪڏھن اوھين جُنبي (يعني مٿي ميرا) ھجو ته پاڪ ٿيو (يعني تڙ ڪريو)، ۽ جيڪڏھن بيمار ھجو يا مسافريءَ تي (ھجو) يا اوھان مان ڪوئي پائخانه مان اچي يا اوھين زالن کي ويجھا ويا ھجو پوءِ اوھين پاڻي نه لھو ته پاڪ مٽيءَ سان تيمم ڪريو پوءِ پنھنجن مُنھن کي ۽ پنھنجن ھٿن کي منجھانئس مَسح ڪريو، ۽ الله اوھان تي ڪا تنگي نه گھرندو آھي پر گھُرندو آھي ته اوھان کي پاڪ ڪري ۽ اوھان تي پنھنجي نعمت پوري ڪري مانَ اوھين شڪرانو ڪريو.'),
(676, 5, 7, '۽ الله جي نعمت جا اوھان تي آھي سا ياد ڪريو ۽ سندس اُھو اَنجام جيڪو اوھان سان پڪيءَ طرح ڪيائين تنھن کي (به ياد ڪريو) جڏھن اوھان چيو ته ٻڌوسون ۽ مڃيوسون، ۽ الله کان ڊڄو، ڇوته الله سينن وارو (ڀيد) ڄاڻندڙ آھي.'),
(677, 5, 8, 'اي ايمان وارؤ انصاف سان شاھدي ڏيندڙ ٿي الله لڳ بيھڪ وٺندڙ ھُجو، ۽ ڪنھن قوم جي دشمني اوھان کي اوھان جي بي انصافي جو سبب نه بڻي، انصاف ڪريو، اُھو پرھيزگاري کي ڏاڍو ويجھو آھي، ۽ الله کان ڊڄو، ڇوته جيڪي اوھين ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.'),
(678, 5, 9, 'جن ايمان آندو ۽ چڱا ڪم ڪيا تن سان الله وعدو ڪيو آھي ته اُنھن لاءِ بخشش ۽ وڏو اجر آھي.'),
(679, 5, 10, '۽ جن ڪفر ڪيو ۽ اسان جي آيتن کي ڪُوڙو ڀانيو اُھي دوزخي آھن.'),
(680, 5, 11, 'اي ايمان وارؤ الله جي نعمت جا اوھان تي آھي سا ياد ڪريو جڏھن ھڪ قوم پنھنجن ھٿن کي اوھان ڏانھن (ويڙھ لاءِ) ڊگھي ڪرڻ جو ارادو ڪيو تڏھن سندس ھٿ اوھان کان جھليائين، ۽ الله کان ڊڄو، ۽ الله تي ڀلي ته مؤمن ڀروسو ڪن.'),
(681, 5, 12, '۽ الله بني اسرائيلن کان بيشڪ انجام ورتو، ۽ منجھن ٻارھن سردار مقرر ڪياسون، ۽ الله چيو ته تحقيق آءٌ اوھان ساڻ آھيان، جيڪڏھن نماز پڙھيو ۽ زڪٰوة ڏنوَ ۽ مُنھنجن پيغمبرن کي مڃيوَ ۽ کين مدد ڪيو ۽ الله کي چڱو قرض ڏنوَ ته البت اوھان جون مدايون اوھان کان ضرور ميٽيندس ۽ البت اوھان کي اھڙن باغن ۾ ضرور داخل ڪندس جن جي ھيٺان واھيون وھنديون آھن، وري ھن کان پوءِ اوھان مان جيڪو ڪافر ٿيندو سو سڌيءَ واٽ کان بيشڪ ڀُلو.'),
(682, 5, 13, 'پوءِ سندن انجام ڀڃڻ سببان مٿن لعنت ڪئي سون ۽ سندين دلين کي سخت ڪيوسون، (اُھي توريت ۾) ڪن لفظن کي پنھنجن جاين کان بدلائين ٿا ۽ جيڪا نصيحت ڪئي وئي تنھن مان حصو وساريائون، ۽ منجھائن ٿورڙن کانسواءِ انھن (سڀني) جي (ھڪ نه ٻي) خيانت تي ھميشہ واقف ٿيندو رھندين تنھنڪري کين معاف ڪر ۽ مُڙي وڃ، بيشڪ الله ڀلارن کي دوست رکندو آھي.'),
(683, 5, 14, '۽ جيڪي چون ٿا ته اسين نصارىٰ آھيون تن کان (به) سندن انجام ورتوسون ۽ جيڪا نصيحت ڪئي ويئي تنھن مان ڀاڱو وساريائون، انھيءَ ڪري سندن وچ ۾ ساڙ ۽ دُشمني قيامت جي ڏينھن تائين وڌي سون، ۽ جيڪي ڪندا آھن تنھن جي الله کين سگھو سُڌ ڏيندو.'),
(684, 5, 15, 'اي ڪتاب وارؤ بيشڪ اسان جو پيغمبر (ﷴ ﷺ) اوھان وٽ آيو اوھين (الله جي) ڪتاب مان گھڻيون ڳالھيون لڪائيندا ھيؤ تن مان گھڻيون (ڳالھيون) اوھان کي بيان ڪري ٿو ۽ گھڻين (ڳالھين) کان ٽارو ڪري ٿو، بيشڪ اوھان وٽ الله وٽان نُور ۽ پڌرو ڪتاب (قرآن) آيو آھي.'),
(685, 5, 16, 'انھيءَ جي سببان الله انھن کي سڌو رستو ڏيکاريندو آھي جيڪي سلامتي جي واٽن ڏانھن سندس راضپو ڳولين ۽ کين اونداھين مان سوجھري ڏانھن پنھنجي حڪم سان ٻاھر ڪڍي ٿو ۽ کين سڌيءَ واٽ ڏانھن رستو ڏيکاري ٿو.'),
(686, 5, 17, 'بيشڪ جن چيو ته الله پاڻ مسيح پٽ مريم جو آھي سي بيشڪ ڪافر ٿيا، چؤ ته جيڪڏھن الله عيسىٰ پُٽ مريم جي کي ۽ سندس ماءُ کي ۽ جيڪي سڀيئي زمين ۾ آھن ناس ڪرڻ گھري ته ڪير الله کان انھن کي ڪُجھ بچائي سگھندو؟ ۽ آسمانن ۽ زمين جي ۽ جيڪي اُنھن جي وچ ۾ آھي تنھنجي بادشاھي الله جي آھي، جيڪي گھري ٿو سو پيدا ڪري ٿو، ۽ الله سڀڪنھن شيءِ تي وس وارو آھي.'),
(687, 5, 18, 'يھودي ۽ نصارىٰ چوندا آھن ته اسين الله جا پُٽ ۽ سندس پيارا آھيون، (اي پيغمبر کين) چؤ ته پوءِ سندوَ گناھن سببان ڇو عذاب ڪندا اٿوَ؟ بلڪ اوھين به انھيءَ جنس مان ماڻھو آھيو جيڪي خلقيائين، جنھن کي وڻيس (تنھنکي) بخشي ۽ جنھن وڻيس (تنھنکي) عذاب ڪري، ۽ آسمانن ۽ زمين جي ۽ جيڪي سندن وچ ۾ آھي تنھنجي بادشاھي الله جي آھي، ۽ ڏانھس موٽڻ آھي.'),
(688, 5, 19, 'اي ڪتاب وارؤ بيشڪ اوھان وٽ اسان جو پيغمبر اھڙي وقت ۾ آيو جو (ڪجھ وقت تائين) پيغمبرن جو اچڻ بند ٿي چڪو ھو اوھان تي بيان ٿو ڪري متان چئو ته نڪو خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ اسان وٽ آيو، (ھائو) انھي ڪري بيشڪ اوھان وٽ خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ آيو آھي، ۽ الله سڀڪنھن شيء تي وس وارو آھي.'),
(689, 5, 20, '۽ (ياد ڪر) جڏھن مُوسىٰ پنھنجي قوم کي چيو ته اي منھنجي قوم پاڻ تي الله جي نعمت ياد ڪريو جو اوھان ۾ پيغمبر مُقرّر ڪيائين ۽ اوھان کي بادشاہ ڪيائين، ۽ اوھان کي اھي (نعمتون) ڏنائين جي جھانن وارن مان ڪنھن کي (به) نه ڏنائين.'),
(690, 5, 21, 'اي منھنجي قوم پاڪ زمين ۾ گھڙو جا الله اوھان لاءِ ٺھرائي آھي ۽ پوئتي نه ڦرو نه ته ڦري ڇيئي وارا ٿيندؤ.'),
(691, 5, 22, 'چيائون ته اي مُوسىٰ بيشڪ اتي ڏاڍي زور واري قوم آھي، ۽ اسين منجھس ايسين ڪڏھن نه گھڙنداسون جيسين اُھي منجھانس (نه) نڪرن، پوءِ جيڪڏھن اُھي اُتان نڪرن ته اسين گھِڙڻ وارا آھيون.'),
(692, 5, 23, 'جيڪي خدا کان ڊڄندا ھئا تن مان ٻه ڄڻا جن تي الله نعمت ڪئي ھئي سي چوڻ لڳا ته در کان مٿن گھڙو، پوءِ جڏھن در کان لنگھيؤ تڏھن اوھين پڪ غالب آھيو، ۽ جيڪڏھن مؤمن آھيو ته الله تي ڀروسو ڪريو.'),
(693, 5, 24, 'چيائون ته اي موسىٰ بيشڪ اسين اصلي ڪڏھن منجھس نه گھڙنداسون جيسين اُھي منجھس رھندا تنھنڪري تون ۽ تنھنجو پالڻھار وڃو پوءِ وڙھو بيشڪ اسين ھتي (ئي) ويٺا آھيون.'),
(694, 5, 25, 'مُوسىٰ چيو ته اي منھنجا پالڻھار آءٌ پنھنجي سِرَ ۽ پنھنجي ڀاءُ کانسواءِ (ٻئي) ڪنھن تي (به) اختيار نٿو رکان تنھنڪري اسان جي ۽ بي فرمان قوم جي وچ ۾ جدائي ڪر.'),
(695, 5, 26, '(الله) چيو ته اُھا (پاڪ زمين) چاليھ ورھيه مٿن حرام ڪئي ويئي آھي، زمين (يعني بر) ۾ حيران پريشان رھندا، تنھنڪري بي دين قوم تي ڏک نه ڪر.'),
(696, 5, 27, '(اي پيغمبر) آدم جي ٻن پُٽن (ھابيل ۽ قابيل) جي خبر کين سچي طرح پڙھي ٻُڌاءِ، جڏھن ٻنھي (ھڪ ھڪ) قرباني نذر ڪئي تڏھن تڏھن ٻنھي مان ھڪڙي جي قبول ڪئي وئي ۽ ٻئي جي قبول نه ڪئي وئي، (قابيل) چيو ته ضرور ماريندوسانءِ، ۽ (ھابيل) چيو ته الله رڳو پرھيزگارن جي (قرباني) قبول ڪندو آھي.'),
(697, 5, 28, 'البت جيڪڏھن پنھنجو ھٿ منھنجي مارڻ لاءِ مون ڏانھن ڊگھو ڪندين ته آءٌ پنھنجو ھٿ تو ڏانھن ڪڏھن به ڊگھو نه ڪندس ته توکي ماريان، بيشڪ آءٌ جھانن جي پالڻھار الله کان ڊڄان ٿو.'),
(698, 5, 29, 'مُنھنجو ارادو آھي ته مُنھنجو گُناھ ۽ پنھنجو گُناھ تون کڻين ته دوزخين مان ٿئين، ۽ ظالمن جي اِھا سزا آھي.'),
(699, 5, 30, 'پوءِ سندس نفس کيس ڀاءُ جي مارڻ تي رغبت ڏياري پوءِ کيس ڪُٺائين پوءِ ڇيئي وارن مان ٿيو.'),
(700, 5, 31, 'پوءِ الله ھڪ ڪانوَ کي موڪليو جنھن زمين ۾ ھن لاءِ کوٽيو ٿي ته کيس ڏيکاري ته پنھنجي ڀاءُ جي لاش کي ڪئن لڪائي، (ڪانوَ کي ڏسي) چيائين ته مون لاءِ ارمان آھي ڇا آءٌ ھن ڳالھ کان (به) عاجز ٿيس جو ھن ڪانوَ جھڙو ھُجان ھا ته پنھنجي ڀاءُ جو لاشو لڪايان ھا، پوءِ پشيمانن مان ٿيو.'),
(701, 5, 32, 'انھيءَ ڪري بني اسرائيلن تي لازم ڪيوسون ته جيڪو ڪنھن جان جي عوض يا زمين ۾ فساد وجھڻ (واري) کانسواءِ ٻئي ڪنھن کي ڪُھندو تنھن ڄڻڪ مڙني ماڻھن کي ڪُٺو، ۽ جنھن اُن کي بچايو تنھن ڄڻڪ مڙني ماڻھن کي بچايو، ۽ البت بيشڪ اسان جا (گھڻا) پيغمبر چٽن مُعجزن سان وٽن آيا، وري بيشڪ اُن کانپوءِ منجھائن گھڻا مُلڪ ۾ حد کان لنگھندڙ ٿيا.'),
(702, 5, 33, 'جيڪي الله ۽ سندس پيغمبر سان وڙھندا ۽ ملڪ ۾ فساد (وجھڻ) لاءِ ڪوشش ڪندا تن جي سزا ھن کانسواءِ (ٻي ڪا) نه آھي ته ڪُھجينِ يا سُوريءَ چاڙھجينِ يا سندن ھٿ ۽ سندن پير اُبتڙ سبتڙ وڍجن يا ديس نيڪالي ڏجينِ، دُنيا ۾ اُنھن لاءِ اِھا خواري آھي ۽ آخرت ۾ اُنھن لاءِ وڏو عذاب آھي.'),
(703, 5, 34, 'پر جن اوھان جي قابو پوڻ کان اڳ توبه ڪئي، پوءِ ڄاڻو ته الله بخشڻھار مھربان آھي.'),
(704, 5, 35, 'اي ايمان وارؤ الله کان ڊڄو ۽ ڏانھس وسيلو ڳوليو (يعني نيڪ عملن جي وسيلي سان الله تعالىٰ ڏي ويجھا ٿيو) ۽ سندس واٽ ۾ ڪوشش ڪريو مانَ اوھين ڇٽو.'),
(705, 5, 36, 'بيشڪ جن ڪفر ڪيو تن کي جيڪي زمين ۾ آھي سو مڙئي جيڪڏھن ھجي ۽ اُن جيترو ٻيو به گڏ (ھجين) اُھو قيامت جي ڏينھن جي عذاب کان (بچاءَ) لاءِ عوض ڏين ته کانئن قبول نه ڪبو، ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(706, 5, 37, 'باہ مان نڪرڻ گُھرندا ۽ (حقيقت ڪري) منجھانئس اُھي نڪرڻ وارا نه آھن، ۽ اُنھن لاءِ سدائين جو عذاب آھي.'),
(707, 5, 38, 'چورُ (مڙس) ۽ چورِ (زال) انھن جا ھٿ سندن ڪئي سببان سزا لاءِ وڍيو الله (جي پار) کان اُھو عذاب آھي، ۽ الله غالب حڪمت وارو آھي.'),
(708, 5, 39, 'پوءِ جنھن پنھنجي ڪيل پڃاڻا توبه ڪئي ۽ چڱا ڪم ڪيا تنھن تي الله ٻاجھ سان موٽندو، ڇوته الله بخشڻھار مھربان آھي.'),
(709, 5, 40, '(اي پيغمبر) نه معلوم اٿئي ڇا؟ ته آسمانن ۽ زمين جي بادشاھي الله جي آھي، جنھن کي وڻيس تنھن کي عذاب ڪري ۽ جنھن کي وڻيس تنھن کي بخشي، ۽ الله سڀڪنھن شيءِ تي وس وارو آھي.'),
(710, 5, 41, 'اي پيغمبر انھن جو حال توکي غمگين نه ڪري جيڪي اھڙن (ماڻھن) مان ڪفر ۾ ڪاھي پوندا آھن جو پنھنجن واتن سان چوندا آھن ته ايمان آندوسون ۽ سندن دلين ايمان نه آندو آھي، ۽ (پڻ) يھودين مان، جو ڏاڍا ڪُوڙ ٻڌندڙ آھن ٻيءَ قوم (ڪافرن) لاءِ جي اڃا تو وٽ نه آيا آھن جاسُوسي ڪندڙ آھن، ڪن (صحيح) ڳالھين کي پنھنجن ھنڌن تي قائم ھجڻ کانپوءِ بدلائيندا آھن، (ماڻھن کي) چوندا آھن ته جيڪڏھن ھيءُ (حُڪم) ڏنو وڃيوَ ته اھو قبول ڪجو ۽ جيڪڏھن اُھو نه ڏنو وڃيوَ ته پري ٿجو، ۽ الله جنھن جي گمراہ ڪرڻ جو ارادو ڪندو آھي تنھن لاءِ الله (جي پار) کان (ھدايت ڪرڻ جو) ڪُجھ اختيار نه اٿيئي، اُھي (اھڙا) آھن جو انھن جي دلين کي الله پاڪ ڪرڻ نه گھرندو آھي،، دنيا ۾ انھن لاءِ خواري آھي ۽ آخرت ۾ انھن لاءِ وڏو عذاب آھي.'),
(711, 5, 42, 'ڪوڙين ڳالھين جا ٻڌندڙ آھن حرام (جو مال) کائيندڙ آھن، پوءِ جيڪڏھن تو وٽ اچن ته سندن (تڪرار جي) وچ ۾ فيصلو ڪر يا کانئن مُنھن موڙ، ۽ جيڪڏھن کانئن مُنھن موڙين ته توکي ڪجھ نقصان رسائي نه سگھندا، ۽ جيڪڏھن نبيرو ڪرين ته سندن وچ ۾ انصاف سان فيصلو ڪر، بيشڪ الله انصاف ڪندڙن کي دوست رکندو آھي.'),
(712, 5, 43, '۽ ڪئن توکان ھن حالت ۾ فيصلو ڪرائيندا؟ جو وٽن توريت آھي اُن ۾ الله جو حُڪم آھي وري اُن کانپوءِ ڦري ويندا آھن، ۽ اُھي مُؤمن نه آھن.'),
(713, 5, 44, 'تحقيق اسان توريت لاٿو آھي منجھس ھدايت ۽ نُور آھي، فرمانبردارن پيغمبرن ۽ الله وارن ۽ عالمن اُن سان يھودين لاءِ حُڪم ڪيو آھي (يعني) انھيءَ سان جيڪي الله جي ڪتاب مان ياد ڪرايو وين ۽ مٿن شاھد ھوا، پوءِ (کين اسان سمجھايو ھوته) ماڻھن کان نه ڊڄو ۽ مون کان ڊڄو ۽ مُنھنجي آيتن کي ٿوري مُلھ سان نه وڪڻو، ۽ جيڪي الله لاٿو آھي تنھن سان جيڪي حُڪم نه ڪندا سي ئي ڪافر آھن.'),
(714, 5, 45, '۽ انھن (يھودين) تي توريت ۾ لازم ڪيوسون ته ماڻھوءَ جو عوض ماڻھو ۽ اک جو عوض اک ۽ نڪ جو عوض نڪ ۽ ڪنّ جو عوض ڪنّ ۽ ڏند جو عوض ڏند ۽ ڦٽ جو عوض اوترو ڦٽ آھي، پوءِ جيڪو ان کي بخشيندو تنھن لاءِ اُھو (بخشڻ سندس گناھن جو) ڪفّارو آھي، ۽ جيڪو الله لاٿو آھي تنھن سان جيڪي حُڪم نه ڪندا سي ئي ظالم آھن.'),
(715, 5, 46, '۽ اُنھن (پيغمبرن) جي طريقي تي عيسىٰ پٽ مريم جي کي پوئتان موڪليوسون جو جيڪو کانئس اڳ (توريت) ھو، تنھنجو سچو ڪندڙ ھو، ۽ کيس انجيل ڏنوسون جنھن ۾ ھدايت ۽ نُور آھي، ۽ توريت جيڪو کانئس اڳ ھو تنھنجو سچو ڪندڙ ۽ پرھيزگارن لاءِ ھدايت ۽ نصيحت ھو.'),
(716, 5, 47, '۽ انجيل وارن کي جڳائي ته جيڪي الله منجھس لاٿو تنھن سان نبيرو ڪن، ۽ جيڪي الله لاٿو تنھن موجب جيڪي نبيرو نه ڪندا سي ئي بي دين آھن.'),
(717, 5, 48, '۽ (اي پيغمبر) توڏانھن سچائيءَ سان ڪتاب لاٿوسون جيڪي ڪتاب کانئس اڳ آھن تن جو سچو ڪندڙ ۽ مٿن نگھبان آھي تنھنڪري الله جيڪي لاٿو آھي تنھن موجب سندن وچ ۾ حُڪم ڪر ۽ جيڪي حق تو وٽ آيو آھي تنھن کان مُڙي سندين سَڌن جي تابعداري نه ڪر، اوھان مان ھر ڪنھن ٽوليءَ لاءِ شريعت ۽ (ھڪ سنئين) واٽ مُقرّر ڪئي سون، ۽ جيڪڏھن الله گھري ھا ته اوھان مڙني کي ھڪ ٽولي ڪري ھا پر (گھريائين) ته جيڪي اوھان کي ڏنائين تنھن ۾ اوھان کي پرکي تنھنڪري چڱائيءَ ڏانھن اڳرائي ڪريو، اوھان مڙني جو الله ڏانھن موٽڻ آھي پوءِ جنھن (ڳالھ) بابت اوھين تڪرار ڪندڙ ھيؤ تنھنجي فيصلي جي اوھان کي خبر ڏيندو.'),
(718, 5, 49, '۽ (وري به توکي حُڪم ٿو ڪجي) ته جيڪي الله لاٿو آھي تنھن سان سندن وچ ۾ حُڪم ڪر ۽ سندين سڌن جي تابعداري نه ڪر ۽ اُنھن کان خوف رک جيڪي الله توڏانھن لاٿو آھي تنھن جي ڪنھن (حُڪم) کان متان توکي ٿيڙين، پوءِ جيڪڏھن ڦِرن ته ڄاڻ ته الله سندن ڪن گناھن سببان کين عذاب پُھچائڻ گھُرندو آھي، ۽ ماڻھن مان گھڻا بي دين آھن.'),
(719, 5, 50, 'اڃا جاھليت وارو حُڪم گھُرندا آھن ڇا؟ ۽ (ڪتاب الٰھي تي) يقين ڪندڙ قوم لاءِ حُڪم ڪرڻ ۾ الله کان وڌيڪ چڱو ڪير آھي؟'),
(720, 5, 51, 'اي مؤمنؤ يھودين ۽ نصارن کي دوست ڪري نه وٺو، انھن مان ھڪڙا ٻـين جا دوست آھن، ۽ اوھان منجھان جيڪو کين دوست ڪري وٺندو سو بيشڪ منجھانئن آھي، بيشڪ الله ظالم ٽوليءَ کي سِڌو رستو نه ڏيکاريندو آھي.'),
(721, 5, 52, 'پوءِ جن جي دلين ۾ (منافقيءَ جي) بيماري آھي تن کي ڏسين ٿو اُنھن (يھودين ۽ نصارن جي دوستيءَ) ۾ ڊوڙندا ويندا آھن چوندا آھن ته پاڻ تي زماني جي ڦيري پُھچڻ کان ڊڄون ٿا، پوءِ ستت ئي الله فتح يا ٻيو حُڪم پاڻ وٽان موڪليندو پوءِ پنھنجي دلين ۾ جيڪي لڪائيندا آھن تنھن تي پشيمان ٿيندا.'),
(722, 5, 53, '۽ (قيامت ۾) مؤمن چوندا ته ھي اُھي آھن ڇا جي (دنيا ۾) الله جا وڏا وڏا قسم کڻندا ھئا ته اسين اوھان سان آھيون، (اڄ منافقيءَ سببان) سندن (سڀ) ڪئي ڪمائي ناس ٿي پوءِ اُھي ڇيئي وارا ٿيا.'),
(723, 5, 54, 'اي مؤمنؤ اوھان منجھان جيڪو دين کان ڦرندو ته سگھو ئي الله اھڙي قوم کي پيدا ڪندو جن کي (پاڻ) دوست رکندو ۽ اُھي کيس دوست رکندا اُھي مؤمنن سان نوڙت ڪندڙ (۽) ڪافرن تي سخت ھوندا، الله جي واٽ ۾ جھاد ڪندا ۽ ڪنھن ملامت ڪندڙ جي ملامت کان نه ڊڄندا، اھو الله جو فضل آھي جنھن کي وڻيس ڏيندو آھي، ۽ الله ڪشادي فضل وارو ڄاڻندڙ آھي.'),
(724, 5, 55, '(اي مؤمنؤ) الله ۽ سندس پيغمبر ۽ اُنھن مؤمنن کان سواءِ اوھانجو ڪو دوست نه آھي جيڪي نماز پڙھندا آھن ۽ زڪوٰة ڏيندا آھن ۽ اُھي رڪوع ڪندڙ آھن.'),
(725, 5, 56, '۽ جيڪو الله ۽ سندس پيغمبر ۽ مؤمنن کي دوست ڪري وٺندو (سو الله جي ٽولي مان ٿيندو) ڇوته الله جي ٽولي ئي غالب آھي.'),
(726, 5, 57, 'اي ايمان وارؤ جن کي اوھان کان اڳ ڪتاب ڏنو ويو تن منجھان جن اوھان جي دين کي چٿر ۽ راند بڻايو آھي تن کي ۽ ڪافرن کي دوست نه بڻايو، ۽ جيڪڏھن مؤمن آھيو ته الله کان ڊڄو.'),
(727, 5, 58, '۽ جڏھن (اوھين) نماز لاءِ بانگ ڏيندا آھيو (تڏھن اُھي) اُن کي چٿر ۽ راند ڀائيندا آھن، اِھو ھِن سببان آھي جو اُھي بي سمجھه قوم آھن.'),
(728, 5, 59, '(اي پيغمبر) چؤ ته اي ڪتاب وارؤ ھن کانسواءِ اسان تي (ٻيو) ڪو عيب رکي سگھو ٿا ڇا؟ ته الله کي ۽ جيڪي اسان ڏانھن لاٿو ويو ۽ جيڪي اڳ لاٿو ويو آھي تنھن کي مڃيو اٿئون ۽ بيشڪ اوھان منجھان گھڻا بي دين آھن.'),
(729, 5, 60, '(اي پيغمبر کين) چؤ ته الله وٽ ھن کان وڌيڪ بُڇڙي عاقبت واري جي اوھان کي خبر ڏيان؟ (بڇڙي عاقبت وارا) اُھي آھن جن تي الله لعنت ڪئي آھي ۽ مٿن ڏمر ڪيو آھي ۽ منجھانئن ڪي ڀولڙا ۽ سوئر ڪيائين جنھن شيطان جي پوڄا ڪئي، اِھي بڇڙي ھنڌ رھڻ وارا آھن ۽ سڌي واٽ کان بلڪل ڀُلا آھن.'),
(730, 5, 61, '۽ جڏھن اوھان وٽ ايندا آھن تڏھن چوندا آھن ته ايمان آندوسون ھن ھوندي جو (اُھي) ڪُفر سان ايندا آھن ۽ اُھي اُن (ڪفر) سان ويندا آھن، ۽ جيڪي لڪائيندا آھن تن کي الله چڱو ڄاڻندڙ آھي.'),
(731, 5, 62, '۽ (اي پيغمبر) منجھانئن گھڻن کي ڏسين ٿو ته گناہ ۽ حد کان لنگھڻ ۽ پنھنجي حرامخوريءَ ۾ تڪڙ ڪندا آھن، ۽ جيڪي ڪندا آھن سو ضرور بڇڙو آھي.'),
(732, 5, 63, 'الله وارا ۽ عالم کين سندن ڪُوڙ ڳالھائڻ ۽ سندن حرامخوريءَ کان ڇونه جھليندا آھن؟ جيڪي ڪندا آھن سو ضرور بڇڙو آھي.'),
(733, 5, 64, '۽ يھودي چوندا آھن ته الله جو ھٿ تنگ آھي (يعني بخيل آھي)، سندن ھٿ بنديل ھجن جيڪي چيائون تنھن سببان لعنت ڪئي وين، (ايئن نه آھي) بلڪ سندس ٻئي ھٿ ڪشادا آھن جئن وڻندو اٿس تئن خرچيندو آھي، ۽ تنھنجي پالڻھار وٽان توڏانھن جيڪو (قرآن) لاٿو ويو آھي سو منجھانئن گھڻن (يھودين) کي بي فرمانيءَ ۽ ڪفر ۾ ضرور وڌائيندو، ۽ قيامت جي ڏينھن تائين سندن وچ ۾ دشمني ۽ بغض وجھي ڇڏيوسون، جنھن مھل جنگ لاءِ باھ ٻاريندا (تنھن مھل) الله اُھا وسائيندو آھي ۽ فساد لاءِ مُلڪ ۾ ڪوشش ڪندا آھن، ۽ الله فسادين کي دوست نه رکندو آھي.'),
(734, 5, 65, '۽ جيڪڏھن ڪتاب وارا ايمان آڻين ھا ۽ پرھيزگاري ڪن ھا ته سندن گناہ کانئن ضرور ميٽيون ھا ۽ کين نعمت جي باغن ۾ ضرور داخل ڪريون ھا.'),
(735, 5, 66, '۽ جيڪڏھن اُھي توريت ۽ انجيل ۽ جيڪي سندن پالڻھار وٽان اُنھن ڏانھن لاٿو ويو (تن جي حڪمن کي) پورو بيھارين ھا ته پنھنجي مٿان ۽ پنھنجي پيرن ھيٺان ضرور (روزي) کائين ھا، منجھانئن ھڪ ٽولي سئين رستي آھي، ۽ اُنھن مان گھڻا جيڪي ڪندا آھن سو بڇڙو آھي.'),
(736, 5, 67, 'اي پيغمبر جيڪو (قرآن) توڏانھن تنھنجي پالڻھار وٽان لاٿو ويو آھي سو پُھچاءِ، ۽ جيڪڏھن (ايئن) نه ڪيئي ته سندس پيغام نه پھچايئي، ۽ الله توکي ماڻھن کان پناہ ڏيندو، ۽ الله ڪافرن جي قوم کي سِڌو رستو نه ڏيکاريندو آھي.'),
(737, 5, 68, '(اي پيغمبر کين) چؤ ته اي ڪتاب وارؤ اوھين ايستائين ڪنھن واٽ تي نه آھيو جيستائين توريت ۽ انجيل ۽ جيڪي پالڻھار کان اوھان ڏانھن لاٿو ويو تنھن (جي حڪمن) تي بيھڪ نه ڪريو، ۽ جيڪي تنھنجي پالڻھار کان توڏانھن لاٿو ويو آھي سو منجھانئن گھڻن (يھودين) جي بي فرمَانيءَ ۽ ڪُفر کي وڌائيندو، تنھنڪري ڪافرن جي قوم تي ارمان نه ڪر.'),
(738, 5, 69, '(جيڪي) مؤمن ۽ يھودي ۽ صابي ۽ نصارىٰ (آھن اُنھن مان) جن الله کي ۽ قيامت جي ڏينھن کي مڃيو ۽ چڱا ڪم ڪيا تن کي ڪو ڀؤ ڪونه آھي ۽ نڪي اُھي غمگين رھندا.'),
(739, 5, 70, 'بيشڪ بني اسرائيلن کان انجام ورتوسون ۽ اُنھن ڏانھن (گھڻا) پيغمبر موڪلياسون، جڏھن ڪڏھن وٽن ڪنھن پيغمبر جيڪي سندن دلين نه ٿي گھريو سو حُڪم ٿي آندو تڏھن ھڪ ٽوليءَ کي ڪُوڙو ٿي ڀانيائون ۽ ھڪ ٽوليءَ کي ڪُٺائون ٿي.'),
(740, 5, 71, '۽ ڀانيائون ٿي ته ڪا خرابي ٿيڻي نه آھي پوءِ (حق کان) انڌا ٿيا ۽ ٻوڙا ٿيا وري (به) الله مٿن ٻاجھ سان موٽيو (جو پيغمبر موڪليائين) وري منجھانئن گھڻا (حق کان) انڌا ٿيا ۽ ٻوڙا ٿيا، ۽ جيڪي ڪندا آھن سو الله ڏسندڙ آھي.'),
(741, 5, 72, 'بيشڪ جن چيو ته عيسىٰ پٽ مريم جو پاڻ الله آھي سي ڪافر ٿيا، ۽ عيسىٰ چوندو ھو ته اي بني اسرائيلؤ الله جي عبادت ڪريو جو منھنجو پالڻھار ۽ اوھان جو پالڻھار آھي، (ھيءَ پڪي ڳالھ آھي ته) جيڪو الله سان شريڪ مقرر ڪندو تنھن تي بيشڪ الله بھشت حرام ڪيو آھي ۽ سندس ھنڌ باھ آھي، ۽ ظالمن جو ھو مددگار آھي.'),
(742, 5, 73, 'جن چيو ته الله ٽن مان ھڪڙو آھي سي بيشڪ ڪافرٿيا، حالانڪ ھڪ الله کانسواءِ ڪوئي عبادت جو لائق نه آھي، جيڪي چوندا آھن تنھن کان جيڪڏھن پاڻ کي نه جھليندا ته منجھانئن ڪافرن کي ڏکوئيندڙ عذاب ضرور پھچندو.'),
(743, 5, 74, 'پوءِ الله ڏانھن ڇونه موٽندا آھن ۽ سندس بخشش ڇونه گھرندا آھن؟ ۽ الله بخشڻھار مھربان آھي.'),
(744, 5, 75, 'عيسىٰ پٽ مريم جو رڳو پيغمبر آھي، بيشڪ کانئس اڳ (گھڻا) پيغنبر گذري ويا، ۽ سندس ماءُ سچي (فرمانبردار) ھئي، اُھي کاڄ کائيندا ھئا، (اي پيغمبر) ڏس ته ڪئن انھن لاءِ نشانيون بيان ڪريون ٿا موٽي ڏس ته اُھي ڪاڏي اُلٽا ڪيا ويندا آھن.'),
(745, 5, 76, '(اي پيغمبر کين) چؤ ته الله کانسواءِ اُنھن جي عبادت ڇو ڪندا آھيو؟ جي اوھان لاءِ نڪي ڏک ۽ نڪي سُک جا مالڪ آھن، ۽ الله ئي ٻڌندڙ ڄاڻندڙ آھي.'),
(746, 5, 77, '(اي پيغمبر کين) چؤ ته اي ڪتاب وارؤ حق کانسواءِ پنھنجي دين ۾ حد کان نه لنگھو ۽ انھيءَ قوم جي سڌن جي پٺيان نه لڳو جيڪي بيشڪ (پاڻ به) اڳ ڀُلا آھن ۽ (ٻـين) گھڻن کي (به) ڀُلايو اٿن ۽ سڌيءَ واٽ کان ڀُلا آھن.'),
(747, 5, 78, 'بني اسرائيلن مان جيڪي ڪافر ٿيا تن تي داؤد ۽ عيسىٰ پٽ مريم جي زبان سان لعنت ڪئي ويئي آھي، اُھو ھن سببان آھي جو بي فرماني ڪيائون ۽ حد کان لنگھندا ھئا.'),
(748, 5, 79, 'جيڪي بد ڪم ڪندا ھئا تن کان ھڪ ٻئي کي نه جھليندا ھوا، جيڪي ڪندا ھئا سو ضرور بد آھي.'),
(749, 5, 80, '(اي پيغمبر) منجھانئن گھڻن کي ڏسين ٿو ته ڪافرن سان دوستي رکندا آھن، جيڪي اُنھن پاڻ لاءِ اڳ موڪليو آھي سو بڇڙو آھي جو مٿن الله جو ڏمرجڻ آھي ۽ اُن عذاب ۾ سدائين رھڻ وارا آھن.'),
(750, 5, 81, '۽ جيڪڏھن (اُھي) الله ۽ (پنھنجي) پيغمبر کي ۽ جيڪي پيغمبر ڏانھن لاٿو ويو تنھن کي مڃين ھا ته انھن (مشرڪن) کي دوست ڪري نه وٺن ھا پر منجھانئن گھڻا ايمان کان نڪتل آھن.'),
(751, 5, 82, '(اي پيغمبر) يھودين ۽ مشرڪن کي مؤمنن لاءِ ٻـين ماڻھن کان بلڪل وڌيڪ دشمني ڪرڻ وارو ضرور ڏسندين، ۽ جيڪي چون ٿا ته اسين نصارىٰ آھيون تن کي مسلمانن سان دوستي ڪرڻ ۾ ڏاڍو ويجھو ڏسندين، اِھو ھن سببان آھي جو منجھانئن ڪي عالم ۽ ڪي فقير آھن ۽ بيشڪ اُھي وڏائي نه ڪندا آھن.'),
(752, 5, 83, '۽ جيڪي پيغمبر ڏانھن لاٿو ويو سو جڏھن ٻڌندا آھن تڏھن سندين اکين کي انھيءَ سببان جو سچ کي سڃاتائون ڳوڙھا ڳاڙيندو ڏسندين، چوندا آھن ته اي اسان جا پالڻھار ايمان آندوسين تنھنڪري اسان کي شاھدن ۾ لِک.'),
(753, 5, 84, '۽ اسان کي ڇا (ٿيو) آھي؟ جو الله کي ۽ جيڪي اسان وٽ سچ (يعني قرآن) آيو تنھن کي ھن ھوندي به نه مڃيون جو اُميد ٿا رکون ته اسان جو پالڻھار اسان کي صالحن جي ٽوليءَ سان (گڏي بھشت ۾) گھيريندو.'),
(754, 5, 85, 'پوءِ جيڪي چيائون تنھن سببان الله کين بھشت ڏنو جنھن جي ھيٺان واھيون وھنديون آھن منجھس سدائين رھڻ وارا آھن، ۽ اِھو ڀلارن جو بدلو آھي.'),
(755, 5, 86, '۽ جن ڪفر ڪيو ۽ اسان جي آيتن کي ڪوڙو ڀانيو اُھي دوزخي آھن.'),
(756, 5, 87, 'اي ايمان وارؤ جيڪي پاڪ شيون الله اوھان لاءِ حلال ڪيون آھن سي حرام نه بڻايو ۽ نڪي حد کان لنگھو، بيشڪ الله حد کان لنگھندڙن کي دوست نه رکندو آھي.'),
(757, 5, 88, '۽ جيڪي الله اوھان کي پاڪ حلال رزق ڏنو آھي تنھن مان کائو، ۽ اُن الله کان ڊڄو جنھن کي مڃيندڙ آھيو.'),
(758, 5, 89, 'الله اوھان جي اجاين قسمن کڻڻ ۾ اوھان کي نه پڪڙيندو پر جيڪي قسم اوھان پڪي نيت سان کنيا تنھن سببان اوھانکي وَٺ ڪندو، پوءِ ان جي ڪفارت وچٿرو (کاڌو) جيڪي پنھنجي عيال کي کارائيندا آھيو تنھن مان ڏھن مسڪينن کي کاڌو ڏيڻ آھي يا کين ويس ڍڪائڻ يا ٻانھون آجو ڪرڻ (جڳائي)، پوءِ جيڪو نه لھي ته ٽي ڏينھن روزو رکڻ لازم آھي، جڏھن اوھين قسم کڻو (۽ ڀڃو تڏھن) اِھا ڪفارت اوھان جي قسمن جي آھي، ۽ پنھنجي قسمن کي (ڀڃڻ کان) سوگھو رکو، اھڙي طرح الله اوھان لاءِ پنھنجا حڪم بيان ڪندو آھي مانَ اوھين شڪرانو ڪريو.'),
(759, 5, 90, 'اي ايمان وارؤ شراب ۽ جؤا ۽ پوڄا آستانا ۽ ڍارا پليتُ شيطاني ڪمن مان آھن تنھنڪري اوھين کانئن پرھيز ڪريو ته مانَ ڇٽو.'),
(760, 5, 91, 'شيطان جو ارادو شراب (پيارڻ) ۽ جؤا (کيڏائڻ) ۾ ھن کانسواءِ نه آھي ته اوھان جي وچ ۾ دشمني ۽ بغض وجھي ۽ الله جي ياد ڪرڻ کان ۽ نماز کان اوھان کي جھلي، پوءِ (ھاڻي به) اوھين (نه) جھلبؤ ڇا؟'),
(761, 5, 92, '۽ الله جي فرمانبرداري ڪريو ۽ پيغمبر جي فرمانبرداري ڪريو ۽ ڊڄو، پوءِ جيڪڏھن اوھين ڦرندؤ ته (پڪ) ڄاڻو ته اسان جي پيغمبر تي رڳو پڌرو پيغام پھچائڻ آھي.'),
(762, 5, 93, 'جن ايمان آندو ۽ چڱا ڪم ڪيا تن تي جيڪي (اڳ) کاڌائون تنھن ۾ گناہ نه آھي جڏھن پرھيزگاري ڪيائون ۽ ايمان آندائون ۽ چڱا ڪم ڪيائون وري پرھيزگاري ڪيائون ۽ ايمان آندائون وري پرھيزگاري ڪيائون ۽ چڱائي ڪيائون، ۽ الله ڀلارن کي دوست رکندو آھي.'),
(763, 5, 94, 'اي ايمان وارؤ الله ڪنھن شڪار (جي حڪم) سان جنھن کي اوھان جا ھٿ يا اوھان جا تير پھچن اوھان کي ھن لاءِ ضرور پرکيندو ته الله نکيڙي ته ڪير کانئس پرپُٺ ڊڄندو آھي، پوءِ جيڪو ھن کانپوءِ حد کان لنگھندو تنھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(764, 5, 95, 'اي ايمان وارؤ اوھين احرام (جي حالت) ۾ شڪار نه ڪريو، ۽ اوھان مان جنھن ڄاڻي واڻي ان کي ماريو پوءِ جيڪي ماريو اٿس تنھن جھڙو جانور جنھن لاءِ اوھان مان ٻه معتبر حُڪم ڪن سزا لازم آھي اھا قرباني ڪعبي وٽ پھچڻي آھي يا (سزا) مسڪينن جو کاڌو ڪفارت آھي يا ان جي برابر روزا رکي ھن لاءِ ته پنھنجي ڪم جي سزا چکي، جيڪي اڳي گذريو سو اَلله معاف ڪيو، ۽ جنھن موٽي ڪيو تنھن کان الله بدلو وٺندو، ۽ الله غالب بدلي وٺڻ وارو آھي.'),
(765, 5, 96, 'درياءَ جو شڪار اوھان لاءِ حلال ڪيو ويو ۽ ان جو کائڻ اوھان لاءِ ۽ مسافرن لاءِ نفعي وارو آھي، ۽ جھنگ جو شڪار جيستائين اوھين احرام وارا ھجو تيستائين اوھان تي حرام ڪيو ويو، ۽ اُن الله کان ڊڄو جنھن ڏانھن گڏ ڪيا ويندؤ.'),
(766, 5, 97, 'الله تعظيم واري گھر ڪعبه تعظيم واري مھيني ۽ (ڪعبي ڏانھن موڪليل) قرباني ۽ قلائد کي ماڻھن لاءِ بيھڪ جو سبب ڪيو، اِھو (ھن لاءِ) ته اوھين ڄاڻو ته جيڪي آسمانن ۽ جيڪي زمين ۾ آھي سو بيشڪ الله ڄاڻندو آھي ۽ الله سڀڪنھن شيء کي ڄاڻندڙ آھي.'),
(767, 5, 98, 'ڄاڻو ته الله سخت سزا ڏيندڙ آھي ۽ (ھيءبه ته) الله بخشڻھار مھربان آھي.'),
(768, 5, 99, 'پيغمبر تي رڳو پيغام پھچائڻ آھي، ۽ جيڪي پڌرو ڪندا آھيو ۽ جيڪي لڪائيندا آھيو (سو سڀ) الله ڄاڻندو آھي.'),
(769, 5, 100, '(اي پيغمبر) چؤ ته پليت ۽ پاڪ برابر نه آھن جيتوڻيڪ پليت جي گھڻائي توکي زيادہ وڻي، پوءِ اي ڌيان وارؤ الله کان ڊڄو ته مانَ اوھين ڇٽو.'),
(770, 5, 101, 'اي ايمان وارؤ انھن شين بابت سوال نه ڪريو جن جي حقيقت جيڪڏھن اوھان تي پڌري ڪئي وڃي ته اوھان کي ڏکي لڳي، ۽ جيڪڏھن قرآن لھڻ مھل ان بابت سوال ڪريو ته اوھان لاءِ پڌرو ڪيو ويندو، انھن (سوالن) کان الله اوھان کي معافي ڏني، ۽ الله بخشڻھار بردبار آھي.'),
(771, 5, 102, 'بيشڪ ھڪ قوم اوھان کان اڳ اھڙين (ڳالھين) جو سوال ڪيو ھو (پر) سمجھائڻ تي وري اُن جا ڦري مُنڪر ٿيا.'),
(772, 5, 103, 'الله نڪي بحيرہ ۽ نڪي سائبہ ۽ نڪي وصيلہ ۽ نڪي حام مُقرّر ڪيو آھي پر ڪافر الله تي ڪوڙ ٻڌندا آھن، ۽ منجھانئن گھڻا نٿا سمجھن.'),
(773, 5, 104, '۽ جڏھن کين چئبو آھي ته جيڪي الله لاٿو آھي تنھن ڏانھن ۽ پيغمبر ڏانھن اچو (تڏھن) چوندا آھن ته جنھن تي پنھنجن ابن ڏاڏن کي (ھلندو) ڏٺوسون سو اسان کي بس آھي، جيتوڻيڪ سندن ابا ڏاڏا ڪجھ نه ڄاڻندا ھئا ۽ نڪي ھدايت وارا ھئا.'),
(774, 5, 105, 'اي مؤمنؤ اوھين پنھنجن نفسن جي ڪريو، جڏھن اوھين ھدايت وارا ٿيؤ تڏھن اُھو اوھان کي ضرر نه رسائيندو جيڪو ڀُلو، الله ڏانھن اوھان مڙني جو موٽڻ آھي پوءِ جيڪي اوھين ڪندا آھيو تنھنجي اوھان کي سُڌ ڏيندو.'),
(775, 5, 106, 'اي ايمان وارؤ جڏھن اوھان مان ڪنھن کي موت ويجھو ٿئي ته وصيّت ڪرڻ مھل ٻه معتبر وصّي ڪريو جي اوھان (مسلمانن) مان ھجن يا اوھان کانسواءِ ٻيا (يعني غيرقوم جا) ھجن (ھي تڏھن) جيڪڏھن اوھين مُلڪ ۾ مسافر ھجو پوءِ اوھان کي موت جو واقعو پُھچي، جيڪڏھن (اوھين سندن گواھي ڏيڻ ۾ ڪو) شڪ رکو ٿا ته (وچينءَ) نماز کانپوءِ اوھين ٻنھي کي جھليو پوءِ اُھي (ٻئي) الله جو قسم کڻي چون ته توڻيڪ مائٽ ھوندا ته به اسين قسم جي عوض ڪا بھا نه وٺنداسون ۽ نڪي الله جي (حڪم واري) شاھدي لڪائينداسون (جي لڪائي سون) ته اُتي جو اُتي ضرور گنھگارن مان ٿينداسون.'),
(776, 5, 107, 'پوءِ جيڪڏھن معلوم ٿئي ته اُھي ٻئي گنھگار ٿيا ته جن جي مال تي ٻئي حق رکيو آھي تن جي ويجھي مائٽي وارن مان ٻيا ٻه ڄڻا انھن جي بجاءِ کڙا ٿين پوءِ الله جو قسم کڻي چون ته ٻنھي جي شاھديءَ کان اسان جي شاھدي ڏاڍي سچي آھي ۽ حد کان نه لنگھنداسون، نه ته اتي جو اتي ضرور ظالمن مان ٿينداسون.'),
(777, 5, 108, 'اِھو (حُڪم ھن ڳالھ کي) ويجھو آھي ته اُھي (وصّي ماڻھو) سچي ڳالھه جي شاھدي ڏيڻ يا پنھنجن قسمن کڻڻ کانپوءِ قسم جي رد ٿيڻ کان ڊڄن، ۽ الله کان ڊڄو ۽ ھي (حُڪم دل سان) ٻڌو، ۽ الله بي دين قوم کي سڌو رستو نه ڏيکاريندو آھي.'),
(778, 5, 109, '(ياد ڪر) جنھن ڏينھن الله سڀني پيغمبرن کي گڏ ڪندو (تنھن ڏينھن) چوندو ته اوھان کي ڇا جواب ڏنو ويو، چوندا ته اسان کي (ماڻھن جي دلين جي) خبر نه آھي، ڇوته تون ئي ڳجھن جو وڏو ڄاڻندڙ آھين.'),
(779, 5, 110, 'جڏھن (اُن ڏينھن) الله عيسىٰ پٽ مريم جي کي چوندو ته پاڻ تي ۽ پنھنجي ماءُ تي مُنھنجي نعمت ياد ڪر، جڏھن توکي پاڪ رُوح سان مدد ڏنم، (جو) ماڻھن سان پينگھي ۾ (خاص طرح) ۽ وڏي وھيءَ ۾ ڳالھايُئي ٿي، ۽ جڏھن توکي ڪتاب ۽ دانائي ۽ توريت ۽ انجيل سيکاريم، ۽ جڏھن منھنجي حڪم سان مٽيءَ مان پکيءَ جي شڪل جھڙو بڻايئي پوءِ منجھس ڦوڪيئي ته مُنھنجي حڪم سان پکي ٿيو ۽ مُنھنجي حُڪم سان ڄاول انڌي ۽ ڪوڙھي کي ڇٽايئي ٿي، ۽ جڏھن مئل کي منھنجي حُڪم سان (جيئرو ڪري) ڪڍيئي ٿي، ۽ جڏھن بني اسرائيلن (جي شر) کي توکان جھليم جڏھن وٽم معجزن سان آئين تڏھن منجھانئن ڪافرن چيو ته ھيءُ رڳو پڌرو جادو آھي.'),
(780, 5, 111, '۽ جڏھن حوارين ڏانھن الھام ڪيم ته مون تي ۽ مُنھنجي پيغمبر تي ايمان آڻيو، چيائون ته ايمان آندوسون ۽ (اي عيسى) شاھد ھج ته اسان مسلمان آھيون.'),
(781, 5, 112, '(ياد ڪر) جڏھن حوارين چيو ته اي عيسىٰ پُٽ مريم جا تنھنجو پالڻھار اسان تي آسمان کان ڪو (طعام جو) خونچو لاھي سگھندو ڇا؟ (عيسى) چيو ته جيڪڏھن مؤمن آھيو ته الله کان ڊڄو.'),
(782, 5, 113, 'چيائون (اسين ھيءُ) گھرون ٿا ته منجھانئس کائون ۽ اسان جون دليون آرام وٺن ۽ ڄاڻون ته اسان کي سچ چيو اٿيئي ۽ مٿس شاھد ھجون.'),
(783, 5, 114, '(تڏھن) عيسىٰ پٽ مريم جي چيو ته اي اسان جا پالڻھار الله اسان تي ھڪ خونچو آسمان کان لاھ ته اسان جي اڳين ۽ اسان جي پوين (سڀني) لاءِ عيد ٿئي ۽ تنھنجي نشاني ٿئي، ۽ اسان کي روزي ڏي ۽ تون چڱو روزي ڏيندڙ آھين.'),
(784, 5, 115, 'الله چيو ته آءٌ اوھان تي (خونچو) لاھيندس، پوءِ اوھان مان جيڪو ھن کان پوءِ بي فرماني ڪندو تنھن کي آءٌ اھڙو سخت عذاب ڪندس جو جھانن (جي رھندڙن) مان ڪنھن ھڪ کي عذاب نه ڪندس.'),
(785, 5, 116, '۽ (اُھو وقت ياد ڪر) جڏھن الله چوندو ته اي عيسىٰ پٽ مريم جا تو ماڻھن کي چيو ھو ڇا؟ ته الله کانسواءِ مونکي ۽ منھنجي ماءُ کي به خدا ڪري مڃيو، (عيسى) چوندو ته تون پاڪ آھين جنھن چوڻ جو مون کي ڪو حق نه آھي سو چوڻ مون کي ڪئن جڳائيندو ھو، جيڪڏھن مون اھو چيو ھوندو ته بيشڪ اُھو توکي معلوم ھوندو، جيڪي منھنجي دل ۾ آھي سو (تون) ڄاڻندو آھين ۽ جيڪي تنھنجي دل ۾ آھي سو (آءٌ) نه ڄاڻندو آھيان، بيشڪ تون ئي ڳُجھن جو ڏاڍو ڄاڻندڙ آھين.'),
(786, 5, 117, 'جنھن لاءِ مون کي حُڪم ڪيو اٿيئي تنھن کانسواءِ (ٻيو ڪي) کين نه چيو اٿم (يعني) ھي ته الله جي عبادت ڪريو جو منھنجو پالڻھار ۽ اوھان جو پالڻھار آھي، جيسين منجھن رھيس ته (آءٌ) مٿن نگھبان رھيس، پوءِ جنھن مھل تو مونکي کڻي ورتو ته تون ئي مٿن نگھبان رھيئن، ۽ تون سڀڪنھن شيء تي خبردار آھين.'),
(787, 5, 118, 'جيڪڏھن کين عذاب ڪرين ته اُھي تنھنجا ٻانھا آھن، جيڪڏھن کين بخشين ته تون ئي غالب حڪمت وارو آھين.'),
(788, 5, 119, 'الله فرمائيندو ته ھي اُھو ڏينھن آھي جو سچن کي سندين سچائي فائدو ڏيندي، اُنھن لاءِ (اُھي) بھشت آھن جن جي ھيٺان واھيون وھنديون آھن منجھس اصلي سداءِ رھڻ وارا آھن، الله کانئن راضي آھي ۽ اُھي کانئس راضي آھن، اِھا وڏي مراد ماڻڻ آھي.'),
(789, 5, 120, 'آسمانن ۽ زمين جي ۽ جيڪي منجھن آھي تنھنجي بادشاھي الله جي آھي، ۽ اُھو سڀڪنھن شيء تي وس وارو آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'سڀ ساراہ (خاص) انھيءَ الله کي جڳائي جنھن آسمان ۽ زمين کي بڻايو ۽ اوندھ ۽ سوجھرو پيدا ڪيو، وري (به) ڪافر پنھنجي پالڻھار سان ٻـين کي برابر ڪندا آھن.'),
(791, 6, 2, 'اُھو (ئي الله) آھي جنھن اوھان کي مٽيءَ مان بڻايو وري (اوھان جي جئڻ ۽ موت جي) مُدّت ٺھرايائين، ۽ (ھڪڙي ٻي) مُدّت وٽس مُقرّر ٿيل آھي وري (اي ڪافرؤ الله بابت) اوھين شڪ ڪندا آھيو.'),
(792, 6, 3, '۽ اھوئي الله آسمانن ۽ زمين ۾ (سڀڪنھن جو معبود) آھي، اوھان جو ڳجھ ۽ اوھان جو پڌرو ڄاڻندو آھي ۽ جيڪي ڪندا آھيو سوبه ڄاڻندو آھي.'),
(793, 6, 4, 'سندن پالڻھار جي آيتن مان ڪا آيت وٽن نه ايندي آھي پر کانئس منھن موڙيندا آھن.'),
(794, 6, 5, 'پوءِ بيشڪ سچ (يعني قرآن) کي جنھن مھل وٽن آيو (تنھن مھل) ڪوڙ ڀانيائون، پوءِ جنھن (ڳالھ) بابت چٿر ڪندا آھن تنھنجي (سزا جون) خبرون وٽن سگھو پھچنديون.'),
(795, 6, 6, '(ڪافر) نه ڏسندا آھن ڇا؟ ته کانئن اڳي ڪيترا طبقا ناس ڪياسون جن کي زمين ۾ اھڙي قدرت ڏني ھئي سون جو اوھان کي به نه ڏني اٿئون ۽ مٿن آسمانن مينھن ساندہ وساياسون، ۽ سندن ھيٺان واھيون وھندڙ ڪيون سون پوءِ انھن کي سندن گناھن سببان ھلاڪ ڪيوسون ۽ سندن پوئتان ٻـين قومن کي پيدا ڪيوسون.'),
(796, 6, 7, '۽ جيڪڏھن توتي ڪاغذن ۾ (لکيل) ڪتاب (قرآن) نازل ڪريون ھا پوءِ اُھي پنھنجا ھٿ کيس لائين ھا ته جيڪي ڪافر آھن سي ضرور چون ھا ته ھيءُ رڳو پڌرو جادو آھي.'),
(797, 6, 8, '۽ چون ٿا ته مٿس مَلڪ ڇونه لاٿو ويو؟ ۽ جيڪڏھن اسين مَلڪ نازل ڪريون ھا ته ضرور ڪم پورو ٿي وڃي ھا (يعني مري وڃن ھا) وري کين مُھلت (مور) نه ڏجي ھا.'),
(798, 6, 9, '۽ جيڪڏھن کيس مَلڪ ڪريون ھا ته ضرور کيس مڙس (جي شڪل ۾) بڻايون ھا ۽ جھڙو شبہ (ھاڻي) ڪندا آھن تھڙو شبہ مٿن قائم رکون ھا.'),
(799, 6, 10, '۽ بيشڪ توکان اڳ (ٻـين) پيغمبرن سان (به) چٿر ڪئي ويئي (آھي) پوءِ منجھانئن جن چٿرون ڪيون تن تي جنھن (ڳالھ) تي چٿر ڪندا ھئا تنھن (جي سزا اچي) گھيرو ڪيو.'),
(800, 6, 11, '(اي پيغمبر کين) چؤ ته ملڪ ۾ گھمو وري ڏسو ته ڪوڙن جي پڇاڙي ڪيئن ٿي؟'),
(801, 6, 12, '(اي پيغمبر کانئن) پُڇا ڪر ته جيڪي آسمانن ۽ زمين ۾ آھي سو ڪنھنجو آھي؟ چؤ ته الله جو آھي، جنھن (ٻانِھن تي) ٻاجھه (ڪرڻ) پاڻ تي لازم ڪئي آھي، قيامت جي ڏينھن جنھن ۾ شڪ نه آھي ضرور اوھان کي گڏ ڪندو، جنھن پاڻ کي ڇيئي ۾ وڌو سي ڪڏھن ايمان نه آڻيندا.'),
(802, 6, 13, '۽ جيڪو رات ۽ ڏينھن ۾ رھندو آھي، سو ان جي مِلڪ آھي، ۽ اُھو ٻڌندڙ ڄاڻندڙ آھي.'),
(803, 6, 14, '(اي پيغمبر کين) چؤ ته آسمانن ۽ زمين جي بڻائيندڙ الله کانسواءِ ٻئي کي سنڀاليندڙ ڪري ڇو وٺان؟ ۽ اُھو (سڀني کي) روزي ڏيندو آھي ۽ کيس روزي ڪونه ڏيندو آھي، چؤ ته مون کي حُڪم ڏنو ويو ته پھريون مسلمان آءٌ ھجان ۽ (فرمايو ويو ته) تون مشرڪن مان مُور نه ھج.'),
(804, 6, 15, 'چؤ ته جيڪڏھن پنھنجي پالڻھار جو چيو نه مڃيان ته آءٌ وڏي ڏينھن جي عذاب (پھچڻ) کان ڊڄان ٿو.'),
(805, 6, 16, 'اُن ڏينھن جنھن (ماڻھو) کان (عذاب) ٽاريو ويندو تنھن تي بيشڪ الله ٻاجھ ڪئي، ۽ اھا پڌري مراد ماڻڻ آھي.'),
(806, 6, 17, '۽ جيڪڏھن الله توکي ڪو ڏک پُھچائي ته اُن (الله) کانسواءِ ان جو ڪو لاھيندڙ نه آھي، ۽ جيڪڏھن اُھو توکي سُک پُھچائي ته اُھو سڀڪنھن شيء تي وس وارو آھي.'),
(807, 6, 18, '۽ اُھو ٻانھن تي غالب آھي، ۽ اُھو حڪمت وارو خبر رکندڙ آھي.'),
(808, 6, 19, 'پُڇا ڪر ته شاھديءَ ۾ ڪير وڏو آھي، چؤ منھنجي ۽ اوھان جي وچ ۾ الله ئي گواہ آھي، ۽ مون ڏانھن ھيءُ قرآن ھن لاءِ وحي ڪيو ويو آھي ته اُن سان اوھان کي ۽ جن جن کي (ھيءُ قرآن) پھچي (تن سڀني کي) ڊيڄاريان، (ھيءَ ڳالھ) اوھين ثابت ڪري سگھندؤ ڇا؟ ته الله ساڻ ٻيا (به) عبادت جا لائق آھن، چؤ ته آءٌ (اھڙي باطل ڪم جي) شاھدي نٿو ڏيان، (بلڪ سچي ڳالھ جي ثابتيءَ لاءِ) چؤ ته ھڪ الله کان سواءِ ڪوئي عبادت جو لائق ڪونھي ۽ جنکي اوھين شريڪ بڻائيندا آھيو تن کان آءٌ بيزار آھيان.'),
(809, 6, 20, 'جن کي ڪتاب ڏنوسين سي اُن (پيغمبر) کي اھڙيءَ طرح سڃاڻندا آھن جھڙيءَ طرح پنھنجن پٽن کي سڃاڻندا آھن، جن پاڻ کي ڇيئي ۾ وڌو سي ايمان نه آڻيندا.'),
(810, 6, 21, 'ان کان وڌيڪ ظالم ڪير آھي؟ جنھن الله تي ڪوڙو ٺاھ ٺاھيو يا سندس آيتن کي ڪوڙو ڄاتو، ڪو شڪ نه آھي ته ظالم نه ڇٽندا.'),
(811, 6, 22, '۽ (اُھو وقت ياد ڪر) جنھن ڏينھن اُھي مڙئي گڏ ڪنداسين وري مشرڪن کي چونداسين ته اوھان جا اُھي شريڪ جن کي اوھين (الله جو شريڪ) ڀائيندا ھيؤ ڪٿي آھن؟'),
(812, 6, 23, 'وري سندن ڪوئي عذر ھن چوڻ کان سواءِ نه رھندو ته پنھنجي پالڻھار الله جو قسم اٿئون ته اسين شريڪ ڪندڙ نه ھئاسون.'),
(813, 6, 24, '(اي پيغمبر) ڏس ته پاڻ تي ڪھڙو ڪوڙ چيائون جيڪي ٺاھ ٺاھيندا آھن سي کانئن ڀلجي ڀڄي ويا.'),
(814, 6, 25, '۽ منجھائن ڪو اھڙو آھي جو تو ڏانھن (قرآن ٻڌڻ لاءِ) ڪَن ڏيندو آھي، ۽ سندين دلين تي ان جي سمجھڻ کان ڍڪ ۽ سندن ڪنن ۾ گھٻرائي ڪئي اٿئون، ۽ جيڪڏھن سڀ مُعجزا ڏسندا ته (به) کين نه مڃيندا، تان جو جنھن مھل توسان جھڳڙو ڪندڙ ٿي تو وٽ ايندا (تنھن مھل) ڪافر چوندا ته ھيءُ اڳين جي آکاڻين کانسواءِ ٻيو ڪجھ به نه آھي.'),
(815, 6, 26, '۽ اُھي اُن (جي ٻڌڻ) کان (ٻـين کي) جھليندا آھن ۽ (پاڻ به) کانئن پري ڀڄندا آھن، ۽ پاڻ کانسواءِ (ٻئي ڪنھن کي) ھلاڪ نه ٿا ڪن ۽ نه ڄاڻندا آھن.'),
(816, 6, 27, '۽ جڏھن (اُھي) باہ تي بيھاربا تڏھن جيڪر کين ڏسين (ته عجب ڪرين) پوءِ چوندا اسان کي ارمان آھي (جيڪر دنيا ۾) موٽايا وڃون ۽ پنھنجي پالڻھار جي آيتن کي ڪوڙو نه ڀانيون ۽ مؤمنن مان ٿيون.'),
(817, 6, 28, '(نه!) بلڪ ھن کان اڳ جيڪي (ڪفر ۽ گناہ) لڪائيندا ھئا سي (اڄ) کين پڌرا نظر آيا، ۽ جيڪڏھن (دُنيا ڏانھن) موٽايا وڃن ته جنھن (ڪم) کان جھليا ويا سوئي وري ڪرڻ لڳندا ۽ بيشڪ اُھي ضرور ڪوڙا آھن.'),
(818, 6, 29, '۽ چون ٿا ته ھن دُنيا جي حياتي کانسواءِ (ٻيو ڪجھ) نه آھي ۽ اسين (مرڻ کانپوءِ وري) اُٿڻ وارا نه آھيون.'),
(819, 6, 30, '۽ جنھن مھل پنھنجي پالڻھار جي آڏو بيھاربا (تنھن مھل) جيڪڏھن کين ڏسين (ته عجب ڪرين، الله) چوندو ته ھي (وري جيئرو ٿيڻ) سچ نه آھي ڇا؟ چوندا ھائو اسانکي پنھنجي پالڻھار جو قسم آھي (ته سچ آھي، الله) چوندو ته جيڪي اوھين ڪفر ڪندا ھيؤ تنھن سببان عذاب (جو مزو) چکو.'),
(820, 6, 31, 'جن الله جي ملڻ کي ڪوڙ ڀانيو سي بيشڪ ڇيئي وارا ٿيا، ايتري قدر جڏھن اوچتو انھن تي قيامت ايندي (تڏھن) چوڻ لڳندا ته اسان اُن (قيامت) بابت جيڪا ڪوتاھي ڪئي تنھن تي اسان کي افسوس آھي ۽ اُھي پنھنجو بار پنھنجي پٺين تي کڻندا، خبردار جيڪي کڻندا سو بڇڙو آھي.'),
(821, 6, 32, '۽ دُنيا جي حياتي رڳو راند ۽ چرچو آھي، ۽ جيڪي پرھيزگار آھن تن لاءِ آخرت جي جاءِ ڀلي آھي، پوءِ ڇونه ٿا سمجھو؟'),
(822, 6, 33, 'بيشڪ ڄاڻون ٿا ته جيڪي (اُھي) چوندا آھن سو توکي ضرور غمگين ڪندو پوءِ اُھي نه (رڳو) توکي ڪوڙو چوندا آھن پر ظالم الله جي آيتن جو انڪار ڪندا آھن.'),
(823, 6, 34, '۽ بيشڪ توکان اڳ (گھڻا) پيغمبر ڪوڙا سمجھيا ويا ھئا پوءِ پنھنجي ڪوڙي ڀائنجڻ ۽ پنھنجي ڏکوئجڻ تي ايسين صبر ڪندا رھيا جيسين کين اسان جي مدد پُھتي، ۽ الله جي ڳالھين (يعني انجامن) کي (ڪوبه) ڦيريندو نه آھي، ۽ بيشڪ تو وٽ پيغمبرن جي ڪُجھ خبر آئي آھي.'),
(824, 6, 35, '۽ جيڪڏھن سندن منھن موڙڻ توکي ڏکيو لڳي ٿو ته جيڪڏھن زمين ۾ ڪا سُرنگ ھڻي سگھين يا آسمان تي ڪا ڏاڪڻ ٺاھي سگھين يا وٽن ڪو معجزو آڻين ته آڻي انھن کي ڏيکار، ۽ جيڪڏھن الله گھري ھا ته مڙني کي ھدايت تي گڏ ڪري ھا پوءِ اڻ ڄاڻن مان نه ھُج.'),
(825, 6, 36, 'جيڪي (حق طلبيندڙ ٿي حق ڳالھ کي) ٻڌندا آھن سي ئي (اسلام جي دعوت) قبول ڪندا آھن (اُنھن کي الله ھدايت ڪندو آھي)، ۽ مئلن (يعني ڪافرن) کي الله قيامت جي ڏينھن کڙو ڪندو وري ڏانھس موٽايا ويندا.'),
(826, 6, 37, '۽ چون ٿا ته سندس پالڻھار وٽان اُن (پيغمبر) تي ڪا نشاني ڇونه لاٿي وئي؟ (اي پيغمبر) کين چؤ ته الله نشاني جي لاھڻ تي وس وارو آھي پر منجھانئن گھڻا (ماڻھو) نه ڄاڻندا آھن.'),
(827, 6, 38, '۽ نڪو زمين ۾ ڪوئي چرندڙ آھي ۽ نڪو پکي آھي جو پنھنجين کنڀڙاٽين سان اُڏامي ٿو پر اوھان جھڙيون ئي ٽوليون آھن، (سڀ) ڪا شيء ڪتاب (يعني لَوح مَحفُوظ) ۾ لکڻ کانسواءِ نه ڇڏي سون وري پنھنجي پالڻھار ڏانھن گڏ ڪيون وينديون.'),
(828, 6, 39, '۽ جن اسان جي آيتن کي ڪُوڙو ڄاتو سي گونگا ۽ ٻوڙا اونداھين ۾ (پيل) آھن، الله جنھن کي گھُري تنھن کي ڀلائيندو آھي، ۽ جنھن کي گھُري تنھن کي سڌي واٽ تي بيھڪ ڏيندو آھي.'),
(829, 6, 40, '(اي پيغمبر کين) چؤ ته (اوھين) ڏسو جيڪڏھن الله جو عذاب اوھان کي پُھچي يا قيامت اوھان وٽ اچي ته جيڪڏھن اوھان سچا آھيو ته الله کانسواءِ ڪنھن کي سڏيندؤ؟'),
(830, 6, 41, '(نه!) بلڪ خاص ان کي سڏيندا پوءِ جيڪڏھن گھري ته جنھن (مصيبت جي لاھڻ) لاءِ اوھين کيس سڏيندا آھيو سا لاھي ڇڏي ۽ جنھن کي الله سان شريڪ ڪندا آھيو (سو انھيءَ مھل) وساريندا آھيو.'),
(831, 6, 42, '۽ بيشڪ توکان اڳ (گھڻين) اُمتن ڏانھن (پيغمبر) موڪلياسون پوءِ (اُنھن جي بي فرمانيءَ سبب) کين اسان سختي ۽ تڪليف ۾ پڪڙيو ته مانَ اُھي زاري ڪن.'),
(832, 6, 43, 'پوءِ جنھن مھل اسان جو عذاب وٽن پُھتو ٿي (تنھن مھل) ڇونه ٿي زاري ڪيائون؟ پر سندن دليون سخت ٿي ويون ھيون ۽ جيڪي ڪم ڪندا ھئا سي شيطان انھن لاءِ سينگاريا.'),
(833, 6, 44, 'پوءِ جيڪا نصيحت ڏني وين سا جنھن مھل وساريائون (تنھن مھل) مٿن ھر شيء جا دروازا کولياسون، ايتريقدر جو جيڪي ڏنو وين تنھن سان جڏھن (چڱي طرح) خوش ٿيا (ته) کين اوچتو پڪڙيوسون پوءِ اُھي اُنھيءَ مھل نا اُميد ٿيا.'),
(834, 6, 45, 'پوءِ ظالم قوم جي پاڙ پٽي ويئي، ۽ سڀ ساراہ الله جھانن جي پالڻھار کي جڳائي.'),
(835, 6, 46, '(اي پيغمبر کين) چؤ ته ڏسو ته جيڪڏھن الله اوھان جا ڪن ۽ اوھان جون اکيون کسي وٺي ۽ اوھان جي دلين تي مُھر ھڻي ته انھيءَ الله کانسواءِ ڪير ٻيو خُدا اوھان کي اُھو ڏيندو؟ ڏس ته ڪھڙا دليل بيان ڪريون ٿا وري به اُھي پاسو ڪندا آھن.'),
(836, 6, 47, '(اي پيغمبر کين) چؤ ته اوھين خبر ڏيو ته جيڪڏھن الله جو عذاب اوچتو يا پڌرو اوھان کي پُھچي ته ظالم قوم کانسواءِ (ٻيو ڪو) ھلاڪ ٿيندو ڇا؟'),
(837, 6, 48, '۽ پيغمبرن کي خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ئي ڪري موڪليوسون، پوءِ جن مڃيو ۽ پاڻ سڌاريو تن کي ڪو ڀؤ نه آھي ۽ نڪي اُھي غمگين رھندا.'),
(838, 6, 49, '۽ جن اسان جي آيتن کي ڪوڙو ڀانيو تن کي انھيءَ سببان جو بدڪاري ڪندا ھئا عذاب پھچندو.'),
(839, 6, 50, '(اي پيغمبر کين) چؤ ته (آءٌ) اوھان کي نه ٿو چوان ته مون وٽ الله جا خزانا آھن ۽ نڪي (آءٌ) ڳُجھ ڄاڻندو آھيان ۽ نڪي اوھان کي چوان ٿو ته آءٌ مَلَڪ آھيان، ۽ جيڪي مون ڏانھن وحي ڪيو ويندو آھي تنھن کان سواءِ (ٻئي جي) تابعداري نه ڪندو آھيان، چؤ ته انڌو ۽ سڄو ھڪ جھڙا آھن ڇا؟ پوءِ اوھين ڇونه فڪر ڪندا آھيو.'),
(840, 6, 51, '۽ (اي پيغمبر) جيڪي (ماڻھو) پنھنجي پالڻھار وٽ گڏ ٿيڻ کان ڊڄندا آھن تن کي ھن قرآن سان ڊيڄار الله کان سواءِ انھن جو نڪو سنڀاليندڙ ۽ نڪو سفارش ڪندڙ آھي مانَ اُھي پرھيزگاري ڪن.'),
(841, 6, 52, '۽ جيڪي پنھنجي پالڻھار کي صُبح ۽ سانجھيءَ جو سندس رضامندي گھرندڙ ٿي سڏيندا آھن تن کي نه تَڙ، توتي سندن ڪجھ به حساب نه آھي ۽ نڪي مٿن تنھنجو ڪُجھ به حساب آھي جو انھن کي تڙيندين (جي کين تڙيندين) ته نامناسب ڪم ڪندڙن مان ٿيندين.'),
(842, 6, 53, '۽ اھڙي طرح منجھانئن ڪن کي ڪن سان پرکيوسون ته چون ته ھيءَ ٽولي اُھا آھي ڇا جن تي اسان منجھان الله احسان ڪيو آھي، الله شڪر ڪندڙن کي نه ڄاڻندو آھي ڇا؟'),
(843, 6, 54, '۽ جيڪي اسان جي آيتن کي مڃيندا آھن سي جڏھن تو وٽ اچن ته (انھن کي) چؤ ته اَلسَّلاَمُ عَلَيۡڪُمۡ اوھان جي پالڻھار پاڻ تي ٻاجھ لازم ڪئي آھي ته اوھان مان جيڪو بي علمي سان ڪا بڇڙائي ڪندو وري اُن کانپوءِ توبه ڪيائين ۽ سُڌريو ته اُھو (الله) بخشڻھار مھربان آھي.'),
(844, 6, 55, '۽ اھڙي طرح آيتون بيان ڪندا آھيون (ته عمل ڪريو) ۽ (ھن لاءِ) ته گنھگارن جو رستو (ماڻھن کي) پڌرو ٿئي.'),
(845, 6, 56, '(اي پيغمبر کين) چؤ ته الله کانسواءِ جن کي سڏيو ٿا تن جي عبادت ڪرڻ کان مون کي منع ڪئي وئي آھي، ۽ چؤ ته آءٌ اوھانجي سَڌن جي تابعداري نه ڪندس (نه ته) بيشڪ اُنھي مھل گمراہ ٿيندس ۽ آءٌ ھدايت وارن مان نه ھوندس.'),
(846, 6, 57, 'چؤ ته آءٌ پنھنجي پالڻھار جي پڌري حُجّت تي آھيان ۽ اوھان ان کي ڪوڙ ڀانيو آھي، جنھن شيء جي اٻھر ڪندا آھيو سا مون وٽ نه آھي، (اھڙو) حُڪم الله کانسواءِ ٻئي ڪنھن جي اختيار ۾ نه آھي، اُھو سچ کي بيان ڪندو آھي ۽ اُھو (سڀني) فيصلي ڪندڙن کان ڀلو آھي.'),
(847, 6, 58, 'چؤ ته جنھن جي اٻھر ڪندا آھيو سا جيڪڏھن مون وٽ ھجي ھا ته منھنجي ۽ اوھان جي وچ ۾ فيصلو ٿي وڃي ھا، ۽ الله ظالمن کي چڱي طرح ڄاڻندڙ آھي.'),
(848, 6, 59, '۽ وٽس ڳُجھ جون ڪنجيون آھن ان (ڳجھ جي ڪنجين) کي الله کانسواءِ (ٻيو ڪوبه) ڪونه ڄاڻندو آھي، ۽ جيڪي پٽن ۽ دريائن ۾ آھي سو (به اُھو) ڄاڻندو آھي، ۽ ڪوئي پَن نه ڪرندو آھي پر اُن کي اُھو ڄاڻندو آھي ۽ نڪي ڪو داڻو زمين جي اونداھين ۾ ۽ نڪي ڪا آلي ۽ نڪي ڪا سُڪي شيء پڌري ڪتاب ۾ (لکيل ھُجڻ) کانسواءِ آھي.'),
(849, 6, 60, '۽ اُھو (الله) آھي جيڪو اوھان کي رات جو (ڄڻڪ) ماري ٿو ۽ جيڪي اوھين ڏينھن جو ڪمايو ٿا سو (به) ڄاڻندو آھي وري اوھان کي ان مان ھن لاءِ (جاڳائي) اٿاريندو آھي ته ٺھرايل مُدّت پوري ڪرائي وڃي، وري اوھان (سڀني) جو ڏانھس موٽڻ آھي وري جيڪي اوھين ڪندا آھيو تنھنجي اوھان کي سُڌ ڏيندو.'),
(850, 6, 61, '۽ اُھو پنھنجن ٻانھن تي غالب آھي ۽ اوھان تي پنھنجا نگھبان موڪليندو آھي، ته جڏھن اوھان منجھان ڪنھن ھڪ کي موت اچي تڏھن اسان جا قاصد (ملائڪ) ان کي ماريندا آھن ۽ اُھي ڪوتاھي نه ڪندا آھن.'),
(851, 6, 62, 'وري (سڀ مئل) سندن سچي ڌڻي الله ڏانھن موٽايا ويندا، خبردار ان جو حُڪم (غالب) آھي، ۽ اُھو جلد حساب وٺندڙ آھي.'),
(852, 6, 63, '(اي پيغمبر) چؤ ته اوھان کي پَٽن ۽ دريائن جي اونداھين کان ڪير ڇڏائيندو آھي (جڏھن) کيس زاريءَ سان ۽ ڳجھو سڏيندا آھيو؟ (۽ چوندا آھيو) ته جيڪڏھن اسان کي ھن (ڏُک) کان الله ڇڏايو ته شڪر ڪندڙن مان ضرور ٿينداسون.'),
(853, 6, 64, '(اي پيغمبر کين) چؤ ته الله اوھان کي ان کان ۽ سڀڪنھن ڏک کان ڇڏائيندو آھي وري به اوھين (ان سان) شرڪ ڪندا آھيو.'),
(854, 6, 65, '(اي پيغمبر کين) چؤ ته اُھو (الله) اوھان تي اوھان جي مٿان يا اوھان جي پيرن جي ھيٺان عذاب موڪلڻ لاءِ وس وارو آھي يا اوھان کي ٽولي ٽولي ڪري وِڇڙائي ۽ اوھان مان ڪن کي ڪن جي لڙائي (جو مزو) چکائي، ڏس ته اسين ڪھڙي طرح (طرحين طرحين) دليل بيان ٿا ڪريون ته مانَ اُھي سمجھن.'),
(855, 6, 66, '۽ تنھنجي قوم اُن (قرآن) کي ڪوڙ ڀانيو ۽ (حقيقت ڪري) اُھو بلڪل سچ آھي، (اي پيغمبر کين) چؤ ته آءٌ اوھان جو ذمي وار نه آھيان.'),
(856, 6, 67, '(۽) ھر ھڪ شيء لاءِ ھڪ وقت مقرّر آھي ۽ سگھو ڄاڻندؤ.'),
(857, 6, 68, '۽ جيڪي اسان جي آيتن بابت اجايو بحث ڪندا آھن تن کي جڏھن ڏسين ته کانئن مُنھن موڙ جيستائين اُن ڌاران ٻيءَ ڪنھن ڳالھه ۾ بحث ڪن، ۽ جيڪڏھن شيطان توکي وسارائي ته ياد اچڻ کانپوءِ ظالم ٽوليءَ سان نه ويھ.'),
(858, 6, 69, '۽ جيڪي پرھيزگاري ڪندا آھن تن تي اُنھن (ظالمن) جي حساب جي ڪجھ به (ڇڪ تاڻ) نه آھي پر (انھن تي) نصيحت ڪرڻ لازم آھي مانَ اُھي ڊڄن.'),
(859, 6, 70, '۽ (اي پيغمبر) جن پنھنجي دين کي راند ۽ تماشو بڻايو آھي ۽ کين دُنيا جي حياتيءَ ٺڳيو آھي تن کي ڇڏي ڏي ۽ قرآن سان نصيحت ڪر متان (قيامت جي ڏينھن) ڪوئي پنھنجي ڪئي جي سببان (ھلاڪت ۾) ڦاسي، الله کانسواءِ ان جو نڪو مددگار ۽ نڪو سفارش ڪندڙ آھي، ۽ جيڪڏھن سڀڪا شيء عوض ڏيندو ته کانئس نه ورتو ويندو، اِھي اُھي آھن جو جيڪي ڪيائون تنھن سببان (ھلاڪت ۾) ڦاسي ويا، اُنھن لاءِ ٽھڪندڙ پاڻي مان پيڻ ۽ ڏکوئيندڙ عذاب انھيءَ ڪري آھي جو ڪُفر ڪندا ھوا.'),
(860, 6, 71, '(اي پيغمبر کين) چؤ ته الله کانسواءِ اھڙي کي سڏيون ڇا جيڪو اسان کي نڪي سُک ڏيندو آھي ۽ نڪي ڏُک ڏيندو آھي ۽ اُنھيءَ کان پوءِ جو الله اسان کي ھدايت ڪئي انھيءَ وانگر پنھنجين کڙين (ڀرپوءِ) تي موٽايا وڃون ڇا جنھن کي شيطانن جھنگ ۾ ڀلائي ڇڏيو ھجي اھو حيران پيو ڦري، سندس ڪيئي دوست ھُجن جي ان کي سڌي رستي ڏانھن پيا سڏين ته اسان ڏانھن اچ (۽ اھو اچي ئي نه، اي پيغمبر کين) چؤ ته الله جي ڏسيل واٽ اھائي سڌي واٽ آھي، ۽ اسان کي حڪم ڏنو ويو آھي ته جھانن جي پالڻھار جي فرمانبرداري ڪريون.'),
(861, 6, 72, '۽ (ھيءُ به حڪم ڏنو ويو آھي) ته نماز پڙھو ۽ کانئس ڊڄو، ۽ اُھو (الله) آھي جنھن ڏانھن اُٿاريا ويندؤ.'),
(862, 6, 73, '۽ اُھو ئي آھي جنھن آسمانن ۽ زمين کي رِٿ سان بڻايو آھي، ۽ جنھن ڏينھن (قيامت لاءِ) چوندو ته ٿيءُ ته ٿي پوندي. سندس ڳالھ سچي آھي، ۽ جنھن ڏينھن صور ۾ ڦوڪيو ويندو (تنھن ڏينھن) رڳو اُن جي بادشاھي آھي، ڳجھ ۽ ظاھر جو ڄاڻندڙ آھي، ۽ اُھو حِڪمت وارو خبردار آھي.'),
(863, 6, 74, '۽ (ياد ڪر) جڏھن ابراھيم پنھنجي پيءُ آزر کي چيو ته بُتن کي خدا ڪري ڇوٿا وٺو؟ بيشڪ آءٌ توکي ۽ تنھنجي قوم کي پڌري گمراھيءَ ۾ ڏسان ٿو.'),
(864, 6, 75, '۽ اھڙي طرح ابراھيم کي آسمانن ۽ زمين جي بادشاھي ڏيکاري سون ٿي (ھِن لاءِ) ته (اُھو) يقين ڪندڙن مان ٿيئي.'),
(865, 6, 76, 'پوءِ جنھن مھل رات مٿس اونداھي ڪئي (ته آسمان ۾) ھڪ تارو ڏٺائين، چيائين ھي منھنجو پالڻھار آھي، پوءِ جنھن مھل لٿو (ته) چيائين ته لھندڙن کي پيارو نه ٿو رکان.'),
(866, 6, 77, 'پوءِ جنھن مھل چنڊ کي چمڪندڙ ڏٺائين ته چيائين ھي منھنجو پالڻھار آھي، پوءِ جنھن مھل لٿو (ته) چيائين ته جيڪڏھن منھنجو پالڻھار مون کي ھدايت نه ڪندو ته آءٌ ضرور گمراہ قوم مان ٿيندس.'),
(867, 6, 78, 'پوءِ جنھن مھل سج کي چمڪندڙ ڏٺائين (ته) چيائين ھي منھنجو پالڻھار آھي (جو) ھي وڏو آھي، پوءِ جنھن مھل لٿو (ته) چيائين اي منھنجي قوم جن کي اوھين خُدا سان شريڪ بڻائيندا آھيو تن کان آءٌ بيزار آھيان.'),
(868, 6, 79, 'مون (سڀني کان) ھڪ طرفو ٿي پنھنجي مھاڙ کي انھيءَ الله ڏانھن سامھون ڪيو آھي جنھن آسمانن ۽ زمين کي بڻايو آھي ۽ آءٌ مشرڪن مان نه آھيان.'),
(869, 6, 80, '۽ سندس قوم ساڻس تڪرار ڪيو، چيائين ته اوھين الله بابت مون سان ھن حالت ۾ ڇوٿا تڪرار ڪيو جو بيشڪ مونکي سڌي رستي لاتو اٿس جنھن کي ساڻس شريڪ ڪريو ٿا تنھن کان ڪين ڊڄندس پر منھنجي پالڻھار جو ڪجھ (ڪرڻ) گھريو (سو ٿيڻو آھي)، سڀڪا شيء منھنجي پالڻھار جي عِلم ۾ سمائي آھي، پوءِ اوھين ڇونه نصيحت مڃيندا آھيو.'),
(870, 6, 81, '۽ جنھن کي الله سان شريڪ ڪريو ٿا تنھن کان ڪئن ڊڄندس ۽ (اوھين) ھن کان نه ٿا ڊڄو اوھين الله ساڻ اھو شريڪ ڪندا آھيو جنھن جي اوھان وٽ ڪا حُجّت لاٿل نه آھي، جيڪڏھن اوھين ڄاڻندا آھيو (ته ٻڌايو ته) ٻنھي ٽولين مان ڪھڙي امن جي وڌيڪ حقدار آھي؟'),
(871, 6, 82, 'جن ايمان آندو ۽ پنھنجي ايمان کي ڪفر سان نه وچڙايو اُنھن لاءِ امن آھي ۽ اُھي ھدايت وارا آھن.'),
(872, 6, 83, '۽ ھي اسان جو دليل آھي جو ابراھيم کي سندس قوم تي (مقابلي لاءِ) ڏنوسون، جنھن کي گھرندا آھيون تنھنکي درجن ۾ مٿاھون ڪندا آھيون، ڇوته تنھنجو پالڻھار حڪمت وارو ڄاڻندڙ آھي.'),
(873, 6, 84, '۽ ابراھيم کي اسحاق ۽ يعقوب عطا ڪيوسون، سڀني کي ھدايت ڪئي سون، ۽ اُن کان اڳ نُوح کي ھدايت ڪئي سون ۽ سندس اولاد مان داؤد کي ۽ سُلَيمان ۽ اَيُّوب ۽ يُوسف ۽ مُوسىٰ ۽ ھارُون کي (ھدايت ڪئي سون)، ۽ اھڙي طرح اسين ڀلارن کي بدلو ڏيندا آھيون.'),
(874, 6, 85, '۽ ذڪريّا ۽ يَحۡيىٰ ۽ عيسىٰ ۽ الياس کي به، جو ھر ھڪ ڀلارن مان ھو.'),
(875, 6, 86, '۽ اسماعيل ۽ يَسَعَ ۽ يُونس ۽ لُوط کي به، ۽ سڀني کي جڳ (جي رھندڙن) کان ڀلائي ڏني سون.'),
(876, 6, 87, '۽ سندن پين ڏاڏن ۽ سندن اولاد ۽ سندن ڀائرن مان به، ۽ کين چونڊيوسون ۽ کين سڌي واٽ ڏانھن ھدايت ڪئي سون.'),
(877, 6, 88, 'اھا الله جي ھدايت آھي پنھنجن ٻانھن مان جنھن لاءِ گھري تنھن کي ان رستي لائي، ۽ جيڪڏھن (اھي ماڻھو) شرڪ ڪن ھا ته جيڪي ڪمايائون سو کانئن ضرور ناس ٿئي ھا.'),
(878, 6, 89, 'اِھي اُھي آھن جن کي ڪتاب ۽ حِڪمت ۽ پيغمبري ڏني سون، پوءِ جيڪڏھن ھي ماڻھو (قرآن جي انھن) ڳالھين کي نه مڃين ته بيشڪ اسان ھڪ قوم کي مقرّر ڪيو آھي جي (قرآن جي انھن) ڳالھين جا انڪار ڪندڙ نه ٿيندا.'),
(879, 6, 90, 'اِھي اُھي آھن جن کي الله ھدايت ڪئي آھي پوءِ تون به سندن طريقي جي پيروي ڪر، چؤ ته اوھان کان اُن (پيغام پُھچائڻ) تي ڪو اجورو نٿو گھُران، اِھو قرآن خاص جھانن لاءِ نصيحت آھي.'),
(880, 6, 91, '۽ اُنھن (يھودين) الله جو سندس شان مطابق قدر نه ڪيو ھن ڪري جو چيائون ته الله ڪنھن ماڻھوءَ تي ڪُجھ نه لاٿو آھي، (اي پيغمبر کين) چؤ ته اُھو ڪتاب ڪنھن لاٿو جيڪو موسىٰ آندو ھو؟ جو ماڻھن لاءِ نُور ۽ ھدايت ھو جنھن کي اوھين جزون جزون ڪريو ٿا اُن جي ڪنھن ڀاڱي کي پڌرو ڪندا آھيو ۽ گھڻو لڪائيندا آھيو، ۽ جيڪي (ڳالھيون) نڪي اوھين ۽ نڪي اوھان جا پيءُ ڏاڏا ڄاڻندا ھئا سي اوھان کي سيکاريون ويون، چؤ ته الله (ڪتاب لاٿو آھي) وري انھن کي سندن اجائي بحث ۾ راند ڪندو ڇڏي ڏي.'),
(881, 6, 92, '۽ ھي ڪتاب آھي جو ان کي برڪت وارو جيڪي اُن کان اڳ آھن تن کي سچو ڪندڙ لاٿوسون ھن لاءِ ته (تون) مڪّي وارن ۽ سندس آس پاس وارن کي ڊيڄارين، ۽ جيڪي آخرت کي مڃيندا آھن سي اُن (قرآن) کي مڃيندا آھن ۽ اُھي پنھنجي نمازن جي سنڀال ڪندا آھن.'),
(882, 6, 93, '۽ اُن کان وڌيڪ ظالم ڪير آھي جيڪو الله تي ڪوڙو ٺاھ ٺاھي يا چوي ته مون ڏانھن وحي ڪيو ويو آھي ۽ ڏانھنس ڪُجھ به وحي نه ڪيو ويو ھجي يا چوي ته جئن الله (ڪتاب) نازل ڪيو آھي تئن آءٌ (به) ڪتاب نازل ڪندس، ۽ (اي پيغمبر) جيڪڏھن ڏسين (ته جيڪر عجب لڳيئي) جڏھن ظالم موت جي سڪراتين ۾ ھوندا ۽ ملائڪ پنھنجن ھٿن کي ڊگھو ڪندا، (۽ چوندا ته) پنھنجن ساھن کي ٻاھر ڪڍو، اڄ اوھان کي خواري واري عذاب جي سزا انھيءَ سببان ڏني ويندي جو اوھين الله تي ناحق چوندا ھيؤ ۽ سندس آيتن کان وڏائي ڪندا ھيؤ.'),
(883, 6, 94, '۽ بيشڪ اسان وٽ (ائين) ھڪ ھڪ ٿي آيؤ جئن اوھان کي پھرين ڀيري پيدا ڪيو ھوسون ۽ جيڪي اوھان کي عطا ڪيو ھوسون سو اوھان پنھنجين پٺين پوءِ تان ڇڏي ڏنو، ۽ اوھان جا سفارش ڪندڙ اوھان سان نه ٿا ڏسون جن کي اوھين پنھنجن (احتياجن پوري ڪرڻ) ۾ (الله سان) شريڪ ڀائيندا ھيؤ، بيشڪ اوھان جو پاڻ ۾ ڳانڍاپو ڇنو ۽ جن کي اوھين ڀائيندا ھيؤ سي اوھان کان ڀُلجي ويا.'),
(884, 6, 95, 'الله ٻج ۽ ڪڪڙيءَ کي ڦٽائيندڙ آھي، مُئي مان جيئرو ڪڍندو آھي ۽ جيئري مان مُئو ڪڍندڙ آھي، اُھو (ئي) الله آھي پوءِ اوھين ڪيڏانھن ڀلايا ويندا آھيو.'),
(885, 6, 96, 'پرھ کي ڦٽائيندڙ آھي، ۽ رات کي آرام ۽ سج ۽ چنڊ کي (وقتن جي) حساب لاءِ (پيدا) ڪيائين، اھو اندازو (الله) غالب ڄاڻندڙ ٺاھيو آھي.'),
(886, 6, 97, '۽ اُھو (الله) آھي جنھن تارن کي اوھان لاءِ ھن ڪري (پيدا) ڪيو ته پٽن ۽ دريائن جي اونداھين ۾ واٽون لھو، بيشڪ ڄاڻندڙ قوم لاءِ نشانيون (کولي) بيان ڪيون سون.'),
(887, 6, 98, '۽ اُھو (الله) آھي جنھن اوھان کي ھِڪ جِيءَ مان پيدا ڪيو پوءِ (اوھان لاءِ) ٽڪڻ جو ھنڌ ۽ سانڀڻ جي جاءِ ڪيائين، بيشڪ سمجھندڙ ٽولي لاءِ نشانيون (کولي) بيان ڪيون سون.'),
(888, 6, 99, '۽ اُھو (الله) آھي جنھن آسمان مان پاڻي وسايو، پوءِ اُن سان ھر شي جا اوڀڙ ڄماياسون پوءِ اُن مان ساوڪ کي ڪڍيوسون پوءِ منجھانئس (سنگن ۾) گڏيل داڻا ڪڍون ٿا، ۽ کجيءَ جي چپڙن مان گوشا لڙڪيل ۽ ڊاکن جا باغ ۽ زيتون ۽ ڏاڙھون ھڪ ٻئي جھڙا ۽ ڌار ڌار (پيدا ڪياسون)، جڏھن ڦرجن ته سندن ڦر ۽ ان جي پچڻ ڏانھن نھاريو، ڇوته اُن ۾ مڃيندڙ ٽولي لاءِ ضروري نشانيون آھن.'),
(889, 6, 100, '۽ (ڪافرن) جنن کي الله سان شريڪ بڻايو آھي ھن ھوندي جو کين (الله) خلقيو آھي ۽ اُن لاءِ بي علميءَ سان پُٽ ۽ ڌيئرون ٺاھيون اٿن، اھو پاڪ آھي ۽ جيڪي بيان ڪندا آھن تنھن کان مٿاھون آھي.'),
(890, 6, 101, 'آسمانن ۽ زمين جو بڻائيندڙ آھي، انھيءَ کي اولاد جو احتياج ڪئن ٿيندو؟ ۽ اُن کي زال (جو احتياج) نه آھي، ۽ سڀڪا شي (پاڻ) خلقيائين، ۽ اُھو سڀڪنھن شي کي ڄاڻندڙ آھي.'),
(891, 6, 102, 'اھو اوھان جو پالڻھار آھي، اُن کانسواءِ ڪو عبادت جو لائق نه آھي، سڀڪنھن شي کي پيدا ڪندڙ آھي تنھنڪري سندس عبادت ڪريو، ۽ اُھو سڀڪنھن شي کي سنڀاليندڙ آھي.'),
(892, 6, 103, '(دنيا ۾) اکيون کيس ڏسي نه سگھنديون ۽ اُھو اکين کي ڏسندو آھي، ۽ اُھو باريڪ بين خبر رکندڙ آھي.'),
(893, 6, 104, 'بيشڪ اوھان جي پالڻھار کان اوھان وٽ نشان آيا آھن، پوءِ جنھن ڏٺو (يعني سمجھيو) تنھن پاڻ لاءِ (نفع) ڪمايو، ۽ جيڪو انڌو ٿيو تنھن تي ان جو نقصان آھي، ۽ (اي پيغمبر چؤ ته) آءٌ اوھان تي نگھبان نه آھيان.'),
(894, 6, 105, '۽ اھڙي طرح طرحين طرحين دليل بيان ڪريون ٿا ۽ ھن لاءِ متان چون ته تون پڙھيو آھين ۽ ھن لاءِ ته ھن (دين) کي اُنھيءَ قوم لاءِ پڌرو ڪريون جي ڄاڻن ٿا.'),
(895, 6, 106, '(اي پيغمبر) جيڪي تنھنجي پالڻھار وٽان توڏانھن وحي ڪيو ويو آھي تنھنجي تابعداري ڪر، اُن کانسواءِ ڪو عبادت جو لائق نه آھي، ۽ مُشرڪن کان مُنھن موڙ.'),
(896, 6, 107, '۽ جيڪڏھن الله گُھري ھا ته شرڪ نه ڪن ھا، ۽ توکي مٿن نگھبان نه ڪيو اٿئون، ۽ نڪي تون مٿن ڪو نظر ڪندڙ آھين.'),
(897, 6, 108, '(اي مسلمانؤ) جن کي (مُشرڪ) الله کانسواءِ سڏيندا آھن تن کي گاريون نه ڏيو جي متان (اُھي) بي سمجھائي جي ڪري دشمنيءَ کان الله کي ڳالھائين، اھڙي طرح ھر ٽوليءَ لاءِ سندن ڪم سينگارياسون، وري سندن پالڻھار ڏانھن سندن موٽڻ آھي پوءِ جيڪي ڪندا آھن تنھن جي کين سُڌ ڏيندو.'),
(898, 6, 109, '۽ ڪافر پنھنجي سخت سُنھن سان الله جو قسم کڻندا آھن ته وٽن جيڪڏھن ڪو معجزو اچي ته ان کي ضرور مڃيندا، چؤ ته مُعجزا ته (خاص) الله وٽ آھن (اي مسلمانؤ) اوھان کي ڪنھن سمجھايو آھي ته اُھي مڃيندا (سو معجزا وٽن) جڏھن آيا ته به نه مڃيندا؟'),
(899, 6, 110, '۽ سندن دلين کي ۽ سندن اکين کي ڦيرائينداسون جئن ان کي پھرين ڀيري نه مڃيائون ۽ کين سندين گُمراھي ۾ حيران ٿيل ڇڏينداسون.'),
(900, 6, 111, '۽ جيڪڏھن انھن تي اسين ملائڪ لاھيون ھا ۽ ساڻن مُئا (به) ڳالھائين ھا ۽ ھر شيء سندن آمھون سامھون ڪري گڏ (به) ڪريون ھا ته به الله جي گھُر کانسواءِ ڪڏھن ايمان نه آڻين ھا پر اُنھن مان گھڻا بي سمجھائي ڪن ٿا.'),
(901, 6, 112, '۽ اھڙي طرح ھر ڪنھن پيغمبر لاءِ ماڻھن ۽ جنن مان شيطان ويري بڻاياسون (ته پاڻ ۾) ھڪ ٻئي کي وسوسو وجھي ڳالھ جي مُلَمَّعۡ سان ٺڳين، ۽ جيڪڏھن تنھنجو پالڻھار گھُري ھا ته اھو ڪم نه ڪن ھا تنھنڪري کين ۽ جيڪي ٺاہ ٺاھيندا تنھن کي ڇڏي (ڏي).'),
(902, 6, 113, '۽ (ھن لاءِ وسوسو وجھندا آھن) ته جيڪي آخرت کي نه مڃيندا آھن تن جون دليون ڏانھس لڙن ۽ ان کي پسند ڪن ۽ (ھن لاءِ) ته اُھي جيڪي ڪم ڪندڙ آھن سي ڪندا رھن.'),
(903, 6, 114, '(اي پيغمبر چؤ ته) الله ڌاران ٻيو فيصلو ڪندڙ ھن ھوندي ڇو گُھران؟ جو اُنھيءَ اوھان ڏانھن کولي بيان ڪندڙ ڪتاب لاٿو آھي، ۽ جن کي ڪتاب (توريت) ڏنوسون سي (چڱي طرح) ڄاڻندا آھن ته اُھو تنھنجي پالڻھار کان جائي لاٿل آھي تنھنڪري تون شڪ ڪندڙن مان مور نه ٿج.'),
(904, 6, 115, '۽ تنھنجي پالڻھار جو سخن سچائي ۽ انصاف ۾ پورو آھي، سندس سخنن کي ڪو مٽائيندڙ نه آھي، ۽ اُھو ٻڌندڙ ڄاڻندڙ آھي.'),
(905, 6, 116, '۽ جيڪڏھن زمين وارن (ماڻھن) مان گھڻن (يعني ڪافرن) جو چيو مڃيندين ته توکي الله جي واٽ کان ڀُلائيندا، (اُھي) رڳو گمان تي ھلندا آھن ۽ اُھي رڳو اٽڪل ھڻندا آھن.'),
(906, 6, 117, 'بيشڪ تنھنجو پالڻھار اُنھن کي (به) چڱو ڄاڻندڙ آھي جيڪي سندس واٽ کان ڀُلا، ۽ اُھو ھدايت وارن کي (به) چڱو ڄاڻندڙ آھي.'),
(907, 6, 118, 'پوءِ جيڪڏھن اوھين سندس آيتن کي مڃيندا آھيو ته جنھن تي الله جو نالو ياد ڪجي تنھن مان کائو.'),
(908, 6, 119, '۽ اوھان کي ڇا (ٿيو) آھي جو جنھن (شيء) تي الله جو نالو ياد ڪجي تنھن مان نه کائيندا آھيو ھن ھوندي جو جيڪي (شيون) الله اوھان تي حرام ڪيون آھن سي اوھان لاءِ بيشڪ بيان ڪيون اٿس؟ پر جنھن (حرام جي کائڻ) لاءِ اوھين لاچار ٿيو (سو به روا آھي) ۽ گھڻا اڻ ڄاڻائيءَ سان پنھنجن سَڌن سببان ضرور ڀلائين ٿا، ڇوته تنھنجو پالڻھار حد کان لنگھندڙن کي چڱي طرح ڄاڻندڙ آھي.'),
(909, 6, 120, '۽ ظاھري گناہ ۽ باطني گناہ (سڀ طرح) ڇڏي ڏيو، جيڪي گناہ ڪندا آھن سي جيڪي ڪن ٿا تنھن سببان سگھو سزا ڏبين.'),
(910, 6, 121, '۽ جنھن (شيء) تي الله جو نالو ياد نه ڪجي تنھن مان نه کائو جو اُھو (کائڻ) ضرور گُناہ آھي، ۽ شيطان پنھنجن يارن کي ھن لاءِ وسوسو وجھندا آھن ته اوھان سان جھيڙو ڪن، ۽ جيڪڏھن سندن چيو مڃيندؤ ته اوھين (به) ضرور مشرڪ ٿيندؤ.'),
(911, 6, 122, 'جيڪو مئل ھو تنھن کي جياريوسون ۽ اُن لاءِ سوجھرو ڪيوسون جنھن سان ماڻھن ۾ گھُمي ٿو اُھو اُن جي مثال جھڙو آھي ڇا جيڪو اونداھين ۾ آھي منجھانئس نڪرڻ وارو نه آھي، اھڙي طرح ڪافر جيڪي ڪندا آھن سو انھن لاءِ سينگاريو ويو آھي.'),
(912, 6, 123, '۽ اھڙي طرح ھر ڳوٺ ۾ اُنھي (ڳوٺ) جا وڏيرا ھن لاءِ بدڪار ڪياسون ته اُن (ڳوٺ) ۾ فساد وجھندا رھن، ۽ (اُھي) پاڻ کانسواءِ ٻئي ڪنھن لاءِ فساد نه ڪندا آھن ۽ نه ڄاڻندا آھن.'),
(913, 6, 124, '۽ جڏھن وٽن ڪا آيت ايندي آھي (ته) چوندا آھن ته جھڙي (پيغمبري) الله جي پيغمبرن کي ڏني وئي آھي تھڙي جيستائين اسان کي (نه) ڏني ويندي تيسين ڪڏھن نه مڃينداسون، الله جنھن ھنڌ پنھنجي پيغمبري کي موڪلي (سو ھنڌ) چڱو ڄاڻندڙ آھي، جن ڏوہ ڪيو تن کي الله وٽان خواري ۽ سخت عذاب انھي ڪري سگھو پھچندو جو شرارتون ڪندا آھن.'),
(914, 6, 125, 'پوءِ الله جنھن کي گھرندو آھي ته ھدايت ڪري تنھن جو سينو اسلام لاءِ ڪشادو ڪندو آھي، ۽ جنھن کي ڀلائڻ گھرندو آھي تنھن جي سيني کي اھڙو سخت سوڙھو ڪندو آھي جو ڄڻڪ آسمان تي چڙھي ٿو، جيڪي نه مڃيندا آھن تن تي الله اھڙي پليتي رکندو آھي.'),
(915, 6, 126, '۽ تنھنجي پالڻھار جي اِھا سِڌي واٽ آھي، بيشڪ اسان نشانيون انھيءَ قوم لاءِ کولي بيان ڪيون آھن جي نصيحت مڃيندا آھن.'),
(916, 6, 127, 'اُنھن لاءِ سندن پالڻھار وٽ بھشت آھي ۽ اُھو سندن سنڀاليندڙ آھي (ھيءُ سڀ) انھيءَ ڪري (آھي) جو (چڱا) ڪم ڪندا ھوا.'),
(917, 6, 128, '۽ جنھن ڏينھن اُنھن مڙني کي گڏ ڪندو، (چوندو) ته اي جنن جون ٽوليون بيشڪ ماڻھن مان گھڻا پنھنجا (تابع) ڪيا ھيوَ، ۽ ماڻھن مان سندن سنگتي چوندا ته اي اسان جا پالڻھار اسان منجھان ھڪڙن ٻـين کان فائدو ورتو ۽ انھي پنھنجي ٺھرايل مُدّت کي پُھتاسون جيڪا تون اسان لاءِ ٺھرائي، ھيءُ (الله) چوندو ته اوھان جو رھڻ جو ھنڌ دوزخ آھي ان ۾ ھميشہ رھڻ وارا ھوندؤ مگر جيڪي خُدا گھريو، ڇوته تنھنجو پالڻھار حڪمت وارو ڄاڻندڙ آھي.'),
(918, 6, 129, '۽ اھڙي طرح ظالمن مان ڪن کي ڪن مان جيڪي ڪندا آھن تنھن جي (شامت) سببان پاڻ ۾ گڏينداسون.'),
(919, 6, 130, '(۽) اي جنّن ۽ ماڻھن جون ٽوليون اوھان مان اوھان وٽ پيغمبر نه آيا ھئا ڇا؟ جو منھنجون آيتون اوھان تي پڙھيائون ٿي ۽ ھن ڏينھن جي آڏي ٿيڻ کان اڳ اوھان کي ڊيڄاريائون ٿي، چوندا ته پاڻ تي شاھدي ڏنيسون ۽ کين دنيائي حياتيءَ ٺڳيو ھو ۽ (ھاڻي) پاڻ تي شاھدي ڏنائون ته پاڻ ڪافر ھوا.'),
(920, 6, 131, 'اھو (پيغمبر جو موڪلڻ) ھن سببان آھي ته تنھنجو پالڻھار ڪنھن ڳوٺ کي ھن حالت ۾ ظُلم سان ڪڏھن ناس ڪرڻ وارو نه آھي جو سندس رھاڪو بيخبر ھجن.'),
(921, 6, 132, '۽ ھر ڪنھن جيڪي عمل ڪيا تنھن مان انھن لاءِ درجا آھن، ۽ جيڪي ڪندا آھن تنھن کان تنھنجو پالڻھار بي خبر نه آھي.'),
(922, 6, 133, '۽ تنھنجو پالڻھار بي پرواہ ٻاجھ وارو آھي، جيڪڏھن گھري ته اوھان کي ناس ڪري ۽ اوھان کان پوءِ جنھن لاءِ گھري تنھن کي (اُن ھنڌ) پيڙھيءَ تي وھاري جئن اوھان کي ٻي قوم جي نسل مان پيدا ڪيائين.'),
(923, 6, 134, 'بيشڪ جيڪو انجام اوھان سان ڪجي ٿو سو ضرور اچڻو آھي اوھين ٿڪائڻ وارا نه آھيو.'),
(924, 6, 135, '(اي پيغمبر) چؤ ته اي منھنجي قوم اوھين پنھنجي ھنڌ عمل ڪريو آءٌ (به) عمل ڪندڙ آھيان، پوءِ سگھو ڄاڻندؤ ته آخرت ۾ ڪنھن جي عاقبت چڱي ھوندي، سچ آھي ته ظالم نه ڇٽندا.'),
(925, 6, 136, '۽ جيڪي پوک ۽ ڍورن مان الله پيدا ڪيو آھي تنھن مان ھڪ حصّو (ڪافر) الله جو مقرّر ڪندا آُھن ۽ پنھنجي گمان سان چوندا آھن ته ھيءُ الله جو آھي ۽ ھي اسان جي بتن جو آھي، وري جيڪا (شيء) سندن بتن جي ھوندي آھي سا الله وٽ پھچي نه سگھندي آھي، ۽ جيڪا (شيء) الله جي ھوندي آھي سا سندن بتن ڏانھن وڃي سگھندي آھي، جيڪي فيصلو ڪندا آھن سو بڇڙو آھي.'),
(926, 6, 137, '۽ اھڙي طرح گھڻن مشرڪن لاءِ سندن شريڪن (جن کي خدا ڪري مڃيندا آھن) پنھنجي اولاد جو مارڻ سينگاريو آھي ته کين ھلاڪ ڪن ۽ سندن دين مٿن رلائي ملائي ڇڏين، ۽ جي الله گھُري ھا ته اُھو نه ڪن ھا تنھنڪري کين ۽ جيڪي ٺاھ ٺاھيندا آھن تنھن کي ڇڏي ڏي.'),
(927, 6, 138, '۽ پنھنجي خيال سان ھي (به) چوندا آھن ته ھيءُ ڍور ۽ پوک حرام آھن جنھن لاءِ گھرون تنھن کانسواءِ ان کي ڪوئي نه کائي ۽ (چوندا آھن) ته ھڪڙن ڍورن جي پُٺي (سواريءَ لاءِ) حرام ڪئي ويئي آھي ۽ (ٻـين) ڍورن تي (ذبح وقت) الله جو نالو ياد نه ڪندا آھن (اھي ڳالھيون) الله تي ڪوڙي ٺاہ ٺاھڻ سان (ڪندا آھن) جيڪي ٺاہ ٺاھيندا آھن تنھنجي کين سگھو سزا ڏيندو.'),
(928, 6, 139, '۽ چوندا آھن ته جيڪي ھِنن ڍورن جي پيٽن ۾ آھي سو خلاصو اسان جي مڙسن لاءِ آھي ۽ اسان جي زالن تي حرام ڪيل آھي، ۽ جيڪڏھن مُئل ھوندو ته اُھي (سڀ) اُن ۾ ڀائيوار آھن، سندن تقرير جي کين سگھي سزا ڏيندو، بيشڪ الله حڪمت وارو ڄاڻندڙ آھي.'),
(929, 6, 140, 'جن پنھنجي اولاد کي بي سمجھائي ڪري ڄاڻپ کانسواءِ ڪُٺو ۽ جيڪي الله سندن رزق ڪيو سو الله تي ڪوڙي ٺاہ ٺاھڻ سان حرام ڪيائون سي بيشڪ ڇيئي وارا ٿيا، (اُھي) بيشڪ ڀُلا ۽ ھدايت وارا نه آھن.'),
(930, 6, 141, '۽ اُھو (الله) آھي جنھن ڇَٽئي وارا ۽ ريء ڇَٽئي وارا باغ ۽ کجيون ۽ پوکون جن جا ڦر قسم قسم ۽ زيتون ۽ ڏاڙھون ھڪ جھڙا ۽ جُدا جُدا قسم جا پيدا ڪيا، جڏھن ڦر جھلين تڏھن سندن ڦر مان کائو ۽ سندن لاباري وقت الله جو حق (سندن زڪوٰة) ڏيو، ۽ اجايو نه وڃايو، ڇوته الله وڃائيندڙن کي دوست نه رکندو آھي.'),
(931, 6, 142, '۽ ڍورن مان بار کڻڻ وارا ۽ زمين سان لڳل (ننڍا پيدا ڪيائين)، الله جيڪا اوھان جي روزي ڪئي آھي تنھن مان کائو ۽ شيطان جي وکن تي نه ھلو، ڇوته اُھو اوھان جو پڌرو ويري آھي.'),
(932, 6, 143, '(ڍور) اَٺ قسم آھن، رڍن مان ٻه ۽ ٻڪرين مان ٻه (نر ۽ ماديون) آھن، (اي پيغمبر کانئن) پُڇ ته الله ٻنھي نرن کي حرام ڪيو آھي يا مادين کي يا جنھن کي مادين جي ٻنھي قسمن جي ڳڀيرڻين لپيٽيو آھي؟ جيڪڏھن اوھين سچا آھيو ته مون کي دليل سان خبر ڏيو.'),
(933, 6, 144, '۽ اُٺن مان ٻه قسم ۽ ڳونئين مان ٻه قسم، (اي پيغمبر کانئن) پُڇ ته نرن کي الله حرام ڪيو آھي يا مادين کي يا جنھن کي مادين جي ٻنھي قسمن جي ڳڀيرڻين لپيٽيو آھي، يا جنھن مھل الله اوھان کي اھا وصيت ڪئي ته (اُوھين) اُن وقت حاضر ھيؤ؟ پوءِ جيڪو الله ڪوڙو ٺاہ ھن لاءِ ٺاھي ته ڄاڻپ کانسواءِ ماڻھن کي ڀُلائي تنھن کان وڌيڪ ظالم ڪير آھي؟ بيشڪ الله ظالم قوم کي سڌو رستو نه ڏيکاريندو آھي.'),
(934, 6, 145, '(اي پيغمبر) چؤ ته جيڪي حُڪم مُون ڏانھن نازل ٿيا آھن تن ۾ ڪا چيز جنھن کي کائيندڙ کائي حرام نٿو لھان ھن کانسواءِ ته (اُھو) مُئل ھجي يا وھندڙ رَتُ يا سُوئر جو گوشت جو اھي (سڀ) حرام آھن يا گناہ طرح جنھن (شيء) کي الله جي نالي کانسواءِ ٻئي جي نالي سان سڏيو وڃي، پوءِ جيڪو لاچاري حالت ۾ نڪي بي فرمان ۽ نڪي حد کان لنگھندڙ ٿيء (۽ کائي) ته بيشڪ تنھنجو پالڻھار بخشڻھار مھربان آھي.'),
(935, 6, 146, 'يَھُودين تي سڀئي نَھن وارا جانور حرام ڪيا ھواسون، ۽ (پڻ) ڳئن ۽ ٻڪرين مان انھن جي پنيءَ واري لڳل وَہ يا آنڊي واري يا ھڏن سان گڏيل وھ کانسواءِ (ٻي سڀ وھ) مٿن حرام ڪئي ھئي سون، اِھا سندن بي فرمانيءَ سببان کين سزا ڏني سون، ۽ اسين ته ضرور سچ چوڻ وارا آھيون.'),
(936, 6, 147, 'پوءِ (اي پيغمبر) جيڪڏھن توکي ڪوڙو ڀانئين ته چؤ ته اوھان جو پالڻھار گھڻي رحمت وارو آھي، ۽ ڏوھارڻ قوم کان سندس عذاب نه ٽاربو.'),
(937, 6, 148, 'مشرڪ سگھو چوندا ته جي الله گھري ھا ته نڪي اسين شرڪ ڪريون ھا ۽ نڪي اسان جا اَبا ڏاڏا (شرڪ ڪن ھا) ۽ نڪي اسين ڪا شيء حرام ڪريون ھا، اھڙي طرح کانئن جيڪي اڳ ھوا تن به ڪوڙ ڀانيو ايتري قدر جو اسان جي سزا (جو مزو) چکيائون، (اي پيغمبر کين) چؤ ته اوھان وٽ ڪو دليل آھي ته اُھو اسان لاءِ ڪڍي آڻين، (اوھين) رڳو گمان تي ھلندا آھيو ۽ اوھين رڳو اٽڪل ھڻندا آھيو.'),
(938, 6, 149, '(اي پيغمبر کين) چؤ ته الله جي حُجّت مُحڪم آھي، جيڪڏھن گھري ھا ته ضرور اوھان مڙني کي سڌو رستو ڏيکاري ھا.'),
(939, 6, 150, '(اي پيغمبر کين) چؤ ته پنھنجن شاھدن کي سڏيو جيڪي شاھدي ڏين ته (تون) انھن سان شاھدي نه ڏي، ۽ جن اسان جي آيتن کي ڪُوڙ ڄاتو ۽ جيڪي آخرت کي نه مڃيندا آھن تن جي چوڻ تي نه ھل ۽ اُھي پنھنجي پالڻھار سان ٻـين کي برابر ڪن ٿا.'),
(940, 6, 151, '(اي پيغمبر کين) چؤ ته اوھان جي پالڻھار اوھان تي جيڪي حرام ڪيو آھي سو اچو ته آءٌ پڙھي ٻُڌايان (اُھو ھيء آھي ته) اوھين ڪنھن شيء کي ساڻس شريڪ نه ڪريو ۽ ماءُ پيءُ سان چڱائي ڪريو، ۽ اوھين پنھنجي اولاد کي سڃائي سببان نه ڪُھو، اسين اوھانکي (به) ۽ خاص انھن کي (به) روزي ڏيندا آھيون، ۽ بي حيائي جي ڪمن مان جيڪي پڌرو ھجي ۽ جيڪي ڳجھو ھجي تنھن (سڀ) کي (اوھين) ويجھا نه وڃو، ۽ نڪي (اھڙي) ماڻھوءَ کي ماريو جنھنجو مارڻ حق (شرعي قانون) کانسواءِ الله حرام ڪيو ھجي، اِھو (حُڪم) آھي جيڪو الله اوھان کي ڏنو آھي ته اوھين سمجھو.'),
(941, 6, 152, '۽ جيڪا (واٽ) بلڪل چڱي ھجي تنھن کانسواءِ ٻيءَ طرح سان يتيم جي مال کي ويجھا نه وڃو جيسين اُھو پنھنجي جوانيءَ کي پھچي، ۽ ماپ ۽ تور انصاف سان پُوري ڏيو وٺو، اسين ڪنھن ماڻھوءَ کي سندس وس کان (وڌيڪ) تڪليف نه ٿا ڏيون، ۽ جڏھن اوھين (پاڻ ۾) ڳالھايو تڏھن انصاف سان ھلو جيتوڻيڪ مائٽي وارو ھجي، ۽ الله جو انجام پاڙيو، اھو (حُڪم) اوھان کي ڏنو اٿس ته مانَ اوھين نصيحت وٺو.'),
(942, 6, 153, '۽ ھيءَ ته اِھا مُنھنجي سِڌي واٽ آھي تنھنڪري اُن تي ھلو، ۽ ٻـين واٽن تي نه ھلو جو اُھي واٽون اوھان کي سندس واٽ کان جدا ڪنديون، اھو اوھان کي حُڪم فرمايو اٿس مانَ اوھين پرھيزگاري ڪريو.'),
(943, 6, 154, 'وري مُوسىٰ کي ڪتاب اُنھيءَ ماڻھو تي پنھنجي نعمت جي پوري ڪرڻ لاءِ جو ڀلارو ھجي ۽ سڀڪنھن شيء جي چٽائيءَ لاءِ ۽ ھدايت ۽ ٻاجھ لاءِ ڏنوسون ته مانَ اُھي پنھنجي پالڻھار جي ديدار کي مڃين.'),
(944, 6, 155, 'ھيءُ برڪت وارو ڪتاب (يعني قرآن) لاٿوسون تنھنڪري ان جي تابعداري ڪريو ۽ ڊڄو ته اوھان تي ٻاجھ ڪئي وڃي.'),
(945, 6, 156, '(ھن لاءِ لاٿوسون ته اي ڪافرؤ) متان چوڻ لڳو ته اسان کان اڳ وارين ٻن ٽولين کانسواءِ ٻئي (ڪنھن) تي ڪتاب نه لاٿو ويو آھي، ۽ اسين سندس پڙھي پڙھائي کان بيخبر ھئاسون.'),
(946, 6, 157, 'يا چوڻ لڳو ته جيڪڏھن اسان تي ڪو ڪتاب نازل ٿئي ھا ته (اسين) ضرور اُنھن کان وڌيڪ ھدايت وارا ٿيون ھا، پوءِ بيشڪ اوھان جي پالڻھار کان (روشن) دليل ۽ ھدايت ۽ ٻاجھ اوھان وٽ آئي آھي، پوءِ اُن کان کان وڌيڪ ظالم ڪير آھي جو الله جي آيتن کي ڪُوڙ چوي ۽ کانئن مُنھن موڙي؟ جيڪي اسان جي آيتن کان مُنھن موڙيندا تن کي انھيءَ سببان بُڇڙي عذاب جي سزا ڏينداسون جو مُنھن موڙيندا ھوا.'),
(947, 6, 158, 'ھن کانسواءِ انتظار نه ڪندا آھن ته وٽن ملائڪ اچن يا تنھنجو پالڻھار اچي يا تنھنجي پالڻھار جون ڪي نشانيون اچن، جنھن ڏينھن تنھنجي پالڻھار جون ڪي نشانيون اينديون (تنھن ڏينھن) اھڙي ڪنھن ماڻھو کي سندس ايمان آڻڻ فائدو نه ڏيندو جنھن اڳ ايمان نه آندو ھوندو يا پنھنجي ايمان ۾ چڱائي نه ڪئي ھوندي، (اي پيغمبر کين) چؤ ته انتظار ڪريو اسين به انتظار ڪندڙ آھيون.'),
(948, 6, 159, 'جن پنھنجي دين کي ڌاروڌار ڪري ڇڏيو ۽ ٽوليون ٽوليون ٿي ويا تن سان تُنھنجو ڪو واسطو نه آھي، سندن ڪم الله جي ئي حوالي آھي وري جيڪي ڪندا آھن تنھن جي کين سُڌ ڏيندو.'),
(949, 6, 160, 'جيڪو ڪا چڱائي (الله جي حضور ۾) آڻيندو تنھن لاءِ اُن جھڙيون ڏھوڻيون آھن، ۽ جيڪو بڇڙائي آڻيندو تنھن کي اُن جھڙي کانسواءِ (ٻي) سزا نه ڏبي ۽ اُنھن تي ظلم نه ڪبو.'),
(950, 6, 161, '(اي پيغمبر کين) چؤ ته منھنجي پالڻھار مون کي سڌي واٽ ڏانھن ھدايت ڪئي آھي، جو مُحڪم دين ابراھيم حنيف (خُدا ڏانھن ھڪ طرفي) جو طريقو آھي، ۽ (ابراھيم) مُشرڪن مان نه ھو.'),
(951, 6, 162, '(اي پيغمبر کين ھيء به) چؤ ته مُنھنجي نماز ۽ منھنجون مڙئي عبادتون ۽ منھنجو جيئڻ ۽ منھنجو مرڻ (سڀ) جھانن جي پالڻھار الله لاءِ آھي.'),
(952, 6, 163, 'کيس ڪو شريڪ نه آھي، ۽ مون کي ان لاءِ حُڪم ٿيل آھي ۽ آءٌ پھريون مسلمان آھيان.'),
(953, 6, 164, 'چؤ الله کانسواءِ ٻيو پالڻھار ڇو گھران؟ ۽ اُھو ھر شيء جو پالڻھار آھي، ۽ سڀڪو ماڻھو جيڪي ڪندو سو (بار) رڳو مٿس ھوندو، ۽ ڪو کڻندڙ ٻئي جو بار نه کڻندو، وري اوھان کي پنھنجي پالڻھار ڏانھن موٽڻ جو ھنڌ آھي پوءِ جنھن ڳالھ بابت اوھين تڪرار ڪندا آھيو تنھنجي اوھان کي سُڌ ڏيندو.'),
(954, 6, 165, '۽ اُھو (الله) آھي جنھن اوھان کي زمين جو بادشاہ ڪيو آھي ۽ اوھان مان ڪن جا درجا ڪن کان ھن لاءِ بلند ڪيائين ته جيڪي اوھان کي ڏنو اٿس تنھن ۾ اوھان کي پرکي، ڇوته تنھنجو پالڻھار جلد سزا ڏيندڙ (به) آھي، ۽ بيشڪ اُھو بخشڻھار مھربان (به) آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'الٓمّٓصٓ.'),
(956, 7, 2, '(اي پيغمبر ھيء) ڪتاب توڏانھن لاٿو ويو ته ساڻس (ماڻھن) کي ڊيڄارين تنھنڪري ان بابت تنھنجي سيني ۾ ڪا اوکائي ٿيڻ نه گھرجي ۽ مؤمنن لاءِ نصيحت آھي.'),
(957, 7, 3, 'جيڪي اوھان ڏانھن اوھان جي پالڻھار کان لاٿو ويو تنھن جي پيروي ڪريو ۽ اُن (الله) کانسواءِ ٻـين دوستن جي پيروي نه ڪريو، اوھان (تمام) ٿوري نصيحت وٺندا آھيو.'),
(958, 7, 4, '۽ گھڻا ئي ڳوٺ ھئا جي ويران ڪياسون پوءِ (يا) راتوواہ (سمھڻ مھل) اُنھن کي اسان جو عذاب پُھتو يا اُھي منجھند جو ننڊ ڪرڻ وارا ھوا.'),
(959, 7, 5, 'پوءِ جڏھن کين اسان جو عذاب پھتو (تڏھن) سندن پڪار ھن چوڻ کانسواءِ نه ھئي ته اسين پاڻ تي ظلم ڪندا ھواسون.'),
(960, 7, 6, 'پوءِ جن ڏانھن پيغمبر موڪليا ويا تن کان (به) ضرور پُڇنداسون ۽ پيغمبرن کان (به) ضرور پُڇنداسون.'),
(961, 7, 7, 'پوءِ (سندن ڪرتوت) مٿن علم سان بيان ڪنداسون ۽ اسين پري نه ھواسون.'),
(962, 7, 8, '۽ اُن ڏينھن (عملن جي) تور سچ آھي، پوءِ جن جا تراڙا ڳرا ٿيا سي ئي ڇُٽل آھن.'),
(963, 7, 9, '۽ جن جا تراڙا ھلڪا ٿيا سي اُھي آھن جن پاڻ کي انھيءَ سببان ڇيئو لاتو جو اسان جي آيتن بابت انڪار ڪندا ھئا.'),
(964, 7, 10, '۽ بيشڪ اوھان کي زمين ۾ ٽڪايوسون ۽ منجھس اوھان لاءِ گذران (جو اُپاءُ) رکيوسون، (پر اوھين) ٿورو شڪر ڪندا آھيو.'),
(965, 7, 11, '۽ بيشڪ اوھان کي پيدا ڪيوسون وري اوھان جي شڪل بڻائي سون وري ملائڪن کي چيوسون ته آدم کي سجدو ڪريو، پوءِ شيطان کانسواءِ (ٻـين) سجدو ڪيو، شيطان سجدي ڪندڙن مان نه ٿيو.'),
(966, 7, 12, '(الله) چيو ته توکي سجدي ڪرڻ کان ڪھڙي (ڳالھ) جھليو؟ جڏھن ته توکي حُڪم (به) ڪيم، چيائين ته آءٌ کانئس ڀلو آھيان، مون کي باہ مان پيدا ڪيو اٿئي ۽ ان کي مٽيءَ مان پيدا ڪيو اٿئي.'),
(967, 7, 13, '(الله) چيو ته اُن (آسمان) مان ھيٺ لھي وڃ توکي منجھس وڏائي ڪرڻ نه جڳائيندي آھي تنھنڪري (تون) نِڪر ڇوته تون خوار ٿيلن مان آھين.'),
(968, 7, 14, 'چيائين ته قيامت جي ڏينھن تائين مون کي مُھلت ڏي.'),
(969, 7, 15, 'چيائين ته تون مُھلت ڏنلن مان آھين.'),
(970, 7, 16, 'چيائين انھيءَ سببان جو تو مونکي گمراہ ڪيو (آءٌ ته) ماڻھن لاءِ تنھنجي سڌي واٽ تي ضرور وھندس.'),
(971, 7, 17, 'پوءِ سندن اڳيان ۽ سندن پوئتان ۽ سندن سڄي پاسي کان ۽ سندن کٻي پاسي کان وٽن ايندس، ۽ منجھائن گھڻا شڪر ڪندڙ نه لھندين.'),
(972, 7, 18, '(الله) فرمايو ته تون آسمانن مان خوار (۽) تڙيل ٿي نڪري وڃ، انھن مان جيڪو تنھنجي تابعداري ڪندو (تنھنکي ۽ توکي دوزخ ۾ وجھي) اوھان مڙني مان دوزخ ضرور ڀريندس.'),
(973, 7, 19, '۽ (چيوسون ته) اي آدم تون ۽ تنھنجي زال بھشت ۾ وڃي رھو ۽ (جيڪي وڻيوَ سو) جتان وڻيوَ تِتان کائو ۽ ھن وڻ کي ويجھا نه وڃو نه ته ظالمن مان ٿيندؤ.'),
(974, 7, 20, 'پوءِ شيطان انھن کي ھن لاءِ وسوسو وڌو ته سندن اوگھڙن مان جيڪي ڍڪيل ھو سو اُنھن لاءِ پڌرو ڪري ۽ چيائين ته اوھان جي پالڻھار اوھان کي ھن وڻ کان رڳو ھن ڪري جھليو آھي ته (متان) اوھين ملائڪ ٿي پئو يا (اُن ۾) سدائين رھڻ وارا ٿي پئو.'),
(975, 7, 21, '۽ سُنھن کڻي انھن کي چيائين ته آءٌ (ته) اوھان جو خيرخواہ آھيان.'),
(976, 7, 22, 'پوءِ اُنھن کي ٺڳيءَ سان لاڙي وڌائين، پوءِ جنھن مھل وڻ مان چکيائون (ته) سندين اوگھڙ کين پڌري ٿي پئي ۽ بھشت جي وڻ مان پن ھڪ ٻئي سان ڳنڍي پاڻ تي ويڙھڻ لڳا، ۽ سندن پالڻھار انھن کي سڏيو (۽ چيائين ته) اوھان کي ھن وڻ کان نه جھليو ھوم ڇا؟ ۽ اوھان کي نه چيو ھوم ڇا ته شيطان اوھان جو پڌرو ويري آھي؟'),
(977, 7, 23, 'چيائون ته اي اسان جا پالڻھار اسان پاڻ تي ظلم ڪيو آھي ۽ جيڪڏھن (تون) اسان کي نه بخشيندين ۽ اسان تي ٻاجھ نه ڪندين ته ضرور ڇيئي وارن مان ٿينداسين.'),
(978, 7, 24, '(الله) فرمايو ته ھيٺ لھي وڃو اوھين پاڻ ۾ ھڪ ٻئي جا ويري آھيو، ۽ اوھان لاءِ زمين ۾ رھڻ جو ھنڌ ۽ (گذران جو) سامان (ھڪ) وقت تائين (ٺھرايل) آھي.'),
(979, 7, 25, '(الله) چيو ته اوھين ان ۾ حياتي گذاريندؤ ۽ منجھس مرندؤ ۽ منجھانئس (جيئرا ٿي) نڪرندؤ.'),
(980, 7, 26, 'اي آدم جي اولاد تحقيق اوھان تي ھڪ پوشاڪ بيشڪ لاٿي سون جا اوھان جي اوگھڙن کي ڍڪيندي ۽ سينگار جا ڪپڙا، ۽ پرھيزگاري جي پوشاڪ اھا (سڀني کان) ڀلي آھي، اھا الله جي نشانين مان آھي مانَ اُھي نصيحت وٺن.'),
(981, 7, 27, 'اي آدم جا اولاد شيطان اوھان کي اھڙيءَ طرح خرابيءَ ۾ نه وجھي جھڙي طرح اوھان جي ماءُ پيءُ کي بھشت مان ٻاھر ڪڍيائين جو سندين پوشاڪ کانئن ھن لاءِ ڇڪي لاٿائين ٿي ته کين سندين اوگھڙ ڏيکاري، سچ آھي ته اُھو پاڻ ۽ سندس لشڪر جتان اوھين انھن کي نه ڏسي سگھندا آھيو (تِتان اُھي) اوھان کي ڏسندا آھن، جيڪي نه مڃيندا آھن تن جو اسان شيطانن کي دوست بڻايو آھي.'),
(982, 7, 28, '۽ (پڻ انھن جا دوست جي) جڏھن ڪو بي حيائي جو ڪم ڪن ٿا (ته) چون ٿا ته اُن (واٽ) تي اسان جا ابا ڏاڏا (ھلندا) ڏٺاسون ۽ الله اسانکي اُنھن (جي ڪرڻ) جو حُڪم ڪيو آھي، (اي پيغمبر کين) چؤ ته بيشڪ الله بيحيائيءَ جي ڪمن جو حُڪم نه ڪندو آھي، اوھين الله تي (اھو ڪوڙ) ڇو چوندا آھيو جنھن جي (سند) اوھين نه ڄاڻندا آھيو.'),
(983, 7, 29, 'چؤ ته منھنجي پالڻھار انصاف جو حُڪم ڪيو آھي، ۽ (حُڪم ڪيو اٿس ته) پنھنجا مُنھن سڀڪنھن نماز مھل (الله ڏانھن) سِڌا ڪريو ۽ اُن لاءِ سچا فرمانبردار ٿي سندس عبادت ڪريو، جئن پھريون اوھان کي پيدا ڪيائين تئن (اوھين) موٽندؤ.'),
(984, 7, 30, 'ھڪ ٽوليءَ کي ھدايت ڪيائين ۽ ٻي ٽوليءَ تي گمراھي لازم ٿي چڪي آھي، ڇوته اُنھن الله کانسواءِ شيطانن کي مددگار ڪري ورتو آھي ۽ ڀائيندا آھن ته پاڻ ھدايت وارا آھن.'),
(985, 7, 31, 'اي آدم جا اولاد سڀڪنھن نماز مھل پنھنجي زينت (جي پوشاڪ) ڍڪيو ۽ کائو ۽ پيئو ۽ اجايو نه وڃايو، ڇوته الله اجائي وڃائيندڙن کي دوست نه رکندو آھي.'),
(986, 7, 32, '(اي پيغمبر) چؤ ته الله جي (بڻايل) زينت جيڪا جيڪا پنھنجن ٻانھن لاءِ پيدا ڪئي اٿس ۽ روزيءَ مان سٺيون شيون ڪنھن حرام ڪيون آھن؟ چؤ ته اُھي (شيون) دُنيا جي حياتي ۾ (۽) خلاصيون قيامت جي ڏينھن مؤمنن لاءِ آھن، اھڙيءَ طرح جا قوم ڄڻندي آھي تنھن لاءِ نشانيون بيان ڪندا آھيون.'),
(987, 7, 33, '(اي پيغمبر) چؤ ته منھنجي پالڻھار بي حيائيءَ جي ڪمن مان جيڪي پڌرو ھجي ۽ جيڪي ڳجھو ھجي ۽ (سڀ) گناہ ۽ ناحق حد کان لنگھڻ ۽ اھڙي شيء کي اوھان جو الله سان شريڪ ڪرڻ جنھن بابت ڪا حُجّت نه لاٿي اٿس ۽ جيڪي نه ڄاڻندا آھيو سو اوھان جو الله تي چوڻ اھي ئي حرام ڪيون آھن.'),
(988, 7, 34, '۽ ھر ڪنھن اُمّت جو ھڪ وقت ٺھرايل آھي، پوءِ جڏھن سندن مُدّت پھچندي (ته) ھڪ گھڙي نڪي دير ڪندا ۽ نڪي اڳڀرو ويندا.'),
(989, 7, 35, '(چيو ويو ته) اي آدم جا اولاد جيڪڏھن اوھان وٽ اوھان مان پيغمبر اچن (جي) اوھان کي منھنجون آيتون پڙھي ٻڌائين ته جيڪي ڊڄندا ۽ پاڻ سڌاريندا تن کي نڪي ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.'),
(990, 7, 36, '۽ جن اسان جي آيتن کي ڪوڙ ڀانيو ۽ ان جي (قبولڻ) کان وڏائي ڪئي سي دوزخي آھن، اُھي اُن ۾ سدائين رھڻ وارا آھن.'),
(991, 7, 37, 'جنھن الله تي ڪوڙو ٺاھ ٺاھيو يا سندس آيتن کي ڪوڙ ڀانيو تنھن کان وڌيڪ ظالم ڪير آھي؟ اُنھن کي (لوح محفوظ ۾) لکيل مان سندن حِصّو (سُک ۽ ڏُک جو) ملندو رھندو، ايتري قدر جو جنھن مھل اسان جا قاصد (ملائڪ) سندن رُوح ڪڍڻ لاءِ ايندا چوندا ته اوھين الله کانسواءِ جن کي سڏيندا ھيؤ سي ڪٿي آھن؟ (تنھن مھل چوندا) ته اسان کان لڪي ويا ۽ پاڻ تي شاھدي ڏيندا ته پاڻ ڪافر ھوا.'),
(992, 7, 38, '(الله) چوندو ته اُنھي ٽولي سان دوزخ ۾ گھڙو جيڪي اوھان کان اڳ جنن ۽ ماڻھن مان گذري ويا، جنھن مھل ھڪ ٽولي پئي گھڙندي تنھن مھل پنھنجي سنگت تي لعنت پئي ڪندي، تان جو جڏھن منجھس مڙئي گھڙندا ته (اُن وقت) سندين پوئين ٽولي پنھنجي پھرين ٽوليءَ لاءِ چوندي ته اي اسانجا پالڻھار ھنن اسان کي گمراہ ڪيو ھو تنھنڪري اُنھن کي باھ جو ٻيڻو عذاب ڏي، (الله) چوندو ته سڀڪنھن لاءِ ٻيڻو عذاب آھي پر اوھين نه ٿا ڄاڻو.'),
(993, 7, 39, '۽ منجھانئن پھرين (ٽولي) اُنھن مان پوئينءَ لاءِ چوندي ته اسان تي اوھان جي ڪا وڏائي نه آھي تنھنڪري جيڪي ڪمايو ھيؤ تنھن سببان عذاب (جو مزو) چکو.'),
(994, 7, 40, 'جن اسان جي حُڪمن کي ڪوڙ ڀانيو ۽ کانئن وڏائي ڪئي تن لاءِ آسمان جا دروازا نه کوليا ويندا ۽ نڪي ايستائين بھشت ۾ گھڙندا جيستائين اُٺ سُئي جي پاکي مان لنگھي، ۽ اھڙي طرح ڏوھارين کي سزا ڏيندا آھيون.'),
(995, 7, 41, 'اُنھن لاءِ وڇاڻو (به) دوزخ جو ھوندو ۽ سندن مٿان پوش (به دوزخ جو) رھندو، ۽ اھڙي طرح ظالمن کي سزا ڏيندا آھيون.'),
(996, 7, 42, '۽ جن ايمان آندو ۽ چڱا ڪم ڪيا ڪنھن ماڻھوءَ کي سندس وس کان وڌيڪ تڪليف ڏيندا ئي نه آھيون، اُھي بھشتي آھن، اُھي منجھس سدائين رھڻ وارا آھن.'),
(997, 7, 43, '۽ جيڪو غير سندين دلين ۾ ھوندو سو ڪڍي ڇڏينداسون سندين (ماڙين جي) ھيٺان واھيون پيون وھنديون، ۽ چوندا ته سڀ ساراہ انھي کي جڳائي جنھن اسان کي ھن (بھشت) ڏانھن رستو لاتو، جيڪڏھن الله اسان کي ھدايت نه ڪري ھا ته ڪڏھن اسين ھدايت نه لھون ھا، بيشڪ اسان وٽ اسان جي پالڻھار جا پيغمبر سچ وٺي آيا، ۽ سڏيو ويندو ته ھي بھشت (اوھان کي ڏنو ويو) اُن جا اُنھيءَ سببان (اوھين) وارث آھيو جو (چڱائي) ڪندا ھيؤ.'),
(998, 7, 44, '۽ بھشتي دوزخين کي سڏيندا (۽ چوندا) ته اسان جي پالڻھار اسان سان جيڪي انجام ڪيو ھو سو بيشڪ سچو لڌوسون پوءِ اوھان جي پالڻھار اوھان سان جيڪو انجام ڪيو ھو سو سچو (نه) لڌوَ ڇا؟ چوندا ته ھائو! پوءِ سڏيندڙ سندن وچ ۾ سڏيندو ته اُنھن ظالمن تي الله جي لعنت آھي.'),
(999, 7, 45, 'جيڪي الله جي واٽ کان (ماڻھن کي) جھليندا رھيا ۽ ان جي واٽ جي ڏنگائي گھرندا رھيا، ۽ اُھي آخرت کي نه مڃيندڙ ھوا.'),
(1000, 7, 46, '۽ سندن وچ ۾ پردو ڪيو ويندو، ۽ اَعراف ۾ اھڙا ماڻھو ھوندا جو سڀڪنھن کي سندين پيشانين مان سڃاڻندا، ۽ بھشتين کي سڏي اَلسّلَامُ عَلَيۡڪُمۡ چوندا، (اڃا) اُن ۾ (پاڻ) نه گھڙيا ھوندا ۽ اُھي اميدوار ھوندا.'),
(1001, 7, 47, '۽ جڏھن سندن اکيون دوزخين ڏانھن ڦيرايون وينديون (ته) چوندا اي اسان جا پالڻھار اسان کي ظالم ٽوليءَ سان گڏ نه ڪر.'),
(1002, 7, 48, '۽ اَعراف وارا جن مُڙسن کي سندين پيشانين مان سڃاڻندا تن کي سڏيندا چوندا ته اوھان جيڪي گڏ ڪيو ھو سو ۽ جيڪا وڏائي ڪندا ھيؤ تنھن اوھان کان ڪجھ به نه ٽاريو.'),
(1003, 7, 49, 'ھيءُ اُھي (ماڻھو) آھن ڇا جن لاءِ اوھين (دنيا ۾) قسم کڻندا ھيؤ ته الله مٿن ٻاجھ نه ڪندو، (کين چئبو ته) بھشت ۾ گھڙو اوھان کي ڪو ڀؤ نه آھي ۽ نڪي اوھين غمگين رھندؤ.'),
(1004, 7, 50, '۽ دوزخي بھشتين کي سڏيندا (چوندا) ته ڪجھ پاڻي اسان تي پلٽيو يا الله اوھان کي جيڪي رزق ڏنو آھي تنھن مان (ڪجھ ڏيو)، چوندا ته اُھي ٻئي ڪافرن تي الله حرام ڪيا آھن.'),
(1005, 7, 51, 'جن پنھنجي دين کي کِل ۽ راند ڪري ورتو ۽ انھن کي دُنيا جي حياتي ٺڳيو، پوءِ اُنھن کي اڄ ايئن وساريوسون جئن (اُنھن) منھنجي ھن ڏينھن جي ملڻ کي وساريو ھو ۽ (جئن) اسان جي آيتن جو انڪار ڪندا ھوا.'),
(1006, 7, 52, '۽ بيشڪ انھن کي ھڪ ڪتاب ڏنوسون جنھن کي دانائيءَ سان کولي بيان ڪيوسون (۽) اُنھيءَ قوم لاءِ ھدايت ۽ ٻاجھ آھي جي مڃيندا آھن.'),
(1007, 7, 53, '(ڪافر) ھن کانسواءِ انتظار نه ڪندا آھن ته اُن (جي وعدہ) جي مُراد اچي، جنھن ڏينھن سندس مراد ايندي (تنھن ڏينھن) جن ان کي اڳي وساريو سي چوندا ته بيشڪ اسان جي پالڻھار جا پيغمبر سچ سان آيا، پوءِ (اڄ) اسان لاءِ ڪي سفارش ڪرڻ وارا آھن؟ جو اسان لاءِ پارت ڪن يا وري موٽايا وڃون ته جيڪي (عمل) ڪياسون تن کانسواءِ ٻيا (چڱا) عمل ڪريون، بيشڪ پاڻ کي ڇيئو لاتائون ۽ جيڪو ٺاھ ٺاھيندا ھوا سو کانئن ڀلجي ويو.'),
(1008, 7, 54, 'اوھان جو پالڻھار اھو الله آھي جنھن آسمانن ۽ زمين کي ڇھن ڏينھن ۾ بڻايو وري عرش ڏي متوجه ٿيو، رات کي ڏينھن سان ڍڪيندو آھي ۽ سج ۽ چنڊ ۽ تارا سندس حُڪم سان نوايل آھن، خبردار پيدا ڪرڻ ۽ حُڪم ڪرڻ اُن جو (ڪم) آھي، جھانن جو پالڻھار وڏي برڪت وارو آھي.'),
(1009, 7, 55, 'پنھنجي پالڻھار کي زاري ڪندڙ ۽ ڳجھا ٿي سڏيو، ڇوته اُھو حد کان لنگھندڙن کي دوست نه رکندو آھي.'),
(1010, 7, 56, '۽ اوھين مُلڪ ۾ ان جي سڌاري کانپوءِ فساد نه وجھو ۽ اُن (الله) کي (کانئس) ڊڄي ۽ طمع رکي سڏيو، بيشڪ الله جي ٻاجھ ڀلارن کي ويجھي آھي.'),
(1011, 7, 57, '۽ اُھو (الله) آھي جو ھوائن کي پنھنجي رحمت (واريءَ برسات) جي اڳيان خوشخبري ڏيندڙ ڪري گھلائيندو آھي، ايتري قدر جو جنھن مھل (اُھي) ڳرن ڪڪرن کي کڻن ٿيون ته انھن کي ڪنھن غير آباد ٿيل ڏيہ ڏانھن روانو ڪريون ٿا پوءِ انھن (ڪڪرن) مان پاڻي وسايون ٿا پوءِ اُن سان ھر جنس جا ميوا ڄمايون ٿا، اھڙي طرح اسين مُئن کي (قبرن مان) ٻاھر ڪڍنداسون ته مانَ اوھين نصيحت وٺو.'),
(1012, 7, 58, '۽ (جا) سٺي زمين (آھي تنھن) جا سَلا سندس پالڻھار جي حُڪم سان ڄمندا آھن، ۽ جيڪا اَدنىٰ آھي (تنھن جا سَلَا) خراب سَلَن کان سواءِ ٻيا نه ڄمندا آھن، اھڙي طرح جيڪا قوم شُڪرانو ڪندڙ آھي تنھن لاءِ نشانيون طرحين طرحين بيان ڪندا آھيون.'),
(1013, 7, 59, 'بيشڪ نوح کي سندس قوم ڏانھن پيغمبر ڪري موڪليوسون پوءِ چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اُن (الله) کانسواءِ ڪو اوھان جو خدا نه آھي، آءٌ اوھان تي وڏي ڏينھن جي عذاب (اچڻ) کان ڊڄان ٿو.'),
(1014, 7, 60, 'سندس قوم جي سردارن چيو ته اسين توکي پڌريءَ گمراھيءَ ۾ (پيل) ڏسون ٿا.'),
(1015, 7, 61, 'چيائين ته اي منھنجي قوم مون کي ڪا گمراھي ڪانه آھي پر آءٌ جھانن جي پالڻھار جو پيغمبر آھيان.'),
(1016, 7, 62, 'اوھان کي پنھنجي پالڻھار جو پيغام پھچائيندو آھيان ۽ اوھان کي نصيحت ڪندو آھيان ۽ الله (جي پار) کان اُھو ڄاڻندو آھيان جيڪي اوھين نه ڄاڻندا آھيو.'),
(1017, 7, 63, 'اوھان کي (ھن ڳالھ جو) عجب لڳو ڇا ته اوھان جي پالڻھار کان ھڪ سمجھاڻي اوھان مان ھڪ مڙس جي ھٿان اوھان وٽ ھِن لاءِ آئي ته اوھان کي ڊيڄاري ۽ تان ته اوھين پرھيزگار ٿيو ۽ ته اوھان تي ٻاجھ ڪئي وڃي.'),
(1018, 7, 64, 'پوءِ کيس ڪوڙو ڀانيائون پوءِ اُن کي ۽ جيڪي ساڻس ھُيا تن کي ٻيڙيءَ ۾ بچايوسون ۽ اسان جي آيتن کي جن ڪُوڙ ڄاتو تن کي ٻوڙيوسون، ڇوته اُھا انڌي قوم ھُئي.'),
(1019, 7, 65, '۽ عاد (قوم) ڏانھن سندن ڀاءُ ھود (پيغمبر ڪري موڪليوسون) چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اُن (الله) کانسواءِ ڪو ٻيو اوھان جو خُدا نه آھي، پوءِ ڇونه ڊڄندا آھيو؟'),
(1020, 7, 66, 'سندس قوم مان ڪافرن سردارن چيو ته اسين بلاشڪ توکي بي سمجھيءَ ۾ ڏسندا آھيون ۽ اسين بلاشڪ توکي ڪوڙن مان ڀائيندا آھيون.'),
(1021, 7, 67, 'چيائين ته اي منھنجي قوم مونکي ڪابه بي سمجھي ڪونھي پر آءٌ جھانن جي پالڻھار جو موڪليل آھيان.'),
(1022, 7, 68, 'اوھان کي پنھنجي پالڻھار جو پيغام پھچايان آءٌ اوھان لاءِ امين نصيحت ڪندڙ آھيان.'),
(1023, 7, 69, '(ھِن ڳالھ جو) عجب لڳو ڇا جو اوھان جي پالڻھار کان اوھان مان ھڪ مڙس جي ھٿان سمجھاڻي ھن لاءِ اوھان وٽ آئي ته اوھان کي ڊيڄاري؟ ۽ (اوھين اُھو وقت به) ياد ڪريو جڏھن اوھان کي نُوح جي قوم کان پوءِ (سندن) پيڙھي تي وھاريائين ۽ اوھان جي جُسن ۾ سگھ وڌايائين، تنھنڪري الله جون نعمتون ياد ڪريو ته مانَ اوھين ڇٽو.'),
(1024, 7, 70, 'چيائون ته (تون) ھن لاءِ اسان وٽ آيو آھين ڇا؟ ته رڳو ھڪ الله جي عبادت ڪريون ۽ اسانجا ابا ڏاڏا جن کي پوڄيندا ھئا تن کي ڇڏي ڏيون؟ جيڪڏھن تون سچو آھين ته جنھن (عذاب) کان اسانکي ڊيڄارين ٿو سو آڻ.'),
(1025, 7, 71, 'چيائين ته بيشڪ اوھان تي اوھان جي پالڻھار وٽان عذاب ۾ ڏمر لازم ٿي چڪو آھي، اوھين مون ساڻ رڳو (انھن) نالن ۾ ڇو جھيڙو ڪندا آھيو جن تي اوھان ۽ اوھان جي ابن ڏاڏن ڇڙو نالو رکيو آھي جن جي مڃڻ بابت الله ڪو دليل نه لاٿو آھي؟ پوءِ انتظار ڪريو بيشڪ آءٌ (به) اوھان سان گڏ انتظار ڪندڙ آھيان.'),
(1026, 7, 72, 'پوءِ ان کي ۽ جيڪي ساڻس ھوا تن کي پنھنجي ٻاجھ سان بچايوسين ۽ جن اسان جي آيتن کي ڪوڙ ڄاتو تن جي پاڙ پٽي ڇڏي سون ۽ اُھي مؤمن نه هُئا.'),
(1027, 7, 73, '۽ ثمودين ڏانھن سندن ڀاءُ صالِح (پيغمبر ڪري موڪليوسون)، چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اوھان جو الله کانسواءِ ٻيو ڪو خُدا نه آھي، بيشڪ اوھان وٽ اوھان جي پالڻھار کان ھڪ مُعجزو آيو آھي، ھيءَ الله جي ڏاچي اوھان لاءِ نشاني آھي تنھنڪري اُنھيءَ کي ڇڏي ڏيو (ته) الله جي زمين ۾ چَرندي رھي ۽ اُن کي ڪو ايذاءُ نه پُھچائجو نه ته اوھان کي ڏکوئيندڙ عذاب وٺندو.'),
(1028, 7, 74, '۽ جڏھن اوھان کي عادين کانپوءِ پيڙھيءَ تي وھاريائين (سو احسان به) ياد ڪريو ۽ اوھان کي مُلڪ ۾ جاءِ ڏنائين (۽) اُن جي پٽن مان ماڙيون بڻائيندا آھيو ۽ ٽڪرن کي ٽُڪي گھر بڻائيندا آھيو، تنھنڪري الله جو نعمتون ياد ڪريو ۽ (اوھين) مُلڪ ۾ فسادي ٿي بگيڙو نه وجھو.'),
(1029, 7, 75, 'سندس قوم مان وڏائي ڪندڙ سردارن انھن کي چيو جن منجھائن ھيڻن (ماڻھن) ايمان آندو ھو ته اوھين ڄاڻندا آھيو ڇا ته صالِح پنھنجي پالڻھار (جي طرف) کان موڪليل آھي، اُنھن چيو ته جنھن (شريعت) سان اُنکي موڪليو ويو آھي تنھن تي اسين ايمان آڻيندڙ آھيون.'),
(1030, 7, 76, 'وڏائي ڪندڙن چيو ته جنھن تي اوھان ايمان آندو آھي تنھن جا اسين مُنڪر آھيون.'),
(1031, 7, 77, 'پوءِ ڏاچيءَ جون کُچون ڪپي ڇڏيائون ۽ پنھنجي پالڻھار جي حُڪم کان بي فرمان ٿيا ۽ (چٿر ڪري) چيائون ته اي صالِح جيڪڏھن پيغمبرن مان آھين ته جنھن (عذاب) جو اسان سان انجام ڪيو اٿيئي سو اسان تي آڻ.'),
(1032, 7, 78, 'پوءِ زلزلي (جي عذاب) ورتن پوءِ جنھن گھرن ۾ گوڏن ڀر ڪرڻ وارا ٿيا.'),
(1033, 7, 79, 'پوءِ (صالِح) کانئن مُنھن موڙيو ۽ چيائين ته اي منھنجي قوم بيشڪ اوھان کي پنھنجي پالڻھار جو پيغام پھچايم ۽ اوھان کي نصيحت ڪيم پر اوھين نصيحت ڪندڙن کي دوست نه رکندا آھيو.'),
(1034, 7, 80, '۽ لُوط کي (پيغمبر ڪري موڪليوسون) جڏھن پنھنجي قوم کي چيائين ته اوھين اھڙي بي حيائي ڇو ڪندا آھيو جا اوھان کان اڳ ساري جھان (جي ماڻھن) مان ڪنھن ھڪ به نه ڪئي آھي؟'),
(1035, 7, 81, 'جو اوھين زالن کي ڇڏي مڙسن ڏانھن شھوت سان ويندا آھيو، بلڪ اوھين حد کان لنگھندڙ قوم آھيو.'),
(1036, 7, 82, '۽ سندس قوم جي ورندي ھن کانسواءِ نه ھُئي ته اُنھن کي (لُوط سميت) پنھنجن ڳوٺن مان لوڌي ڇڏيو، ڇوته اُھي ماڻھو پاڪ بڻبا آھن.'),
(1037, 7, 83, 'پوءِ اسان لُوط کي ۽ سندس گھر وارن کي سندس زال کانسواءِ بچايوسون (اُھا) رھيلن مان ھُئي.'),
(1038, 7, 84, '۽ مٿن (پھڻن جو) مينھن وسايوسون، پوءِ (اي پيغمبر) ڏس ته ڏوھارين جي پڇاڙي ڪھڙي (خراب) ٿي.'),
(1039, 7, 85, '۽ مَدين (وارن) ڏانھن سندن ڀاءُ شعيب کي (پيغمبر ڪري موڪليوسون)، چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اوھان جو الله کان سواءِ ٻيو ڪو خُدا ڪونه آھي، بيشڪ اوھان وٽ اوھان جي پالڻھار کان (چٽيون) نشانيون آيون آھن تنھنڪري ماڻ ۽ تور پوري ڏيو وٺو ۽ ماڻھن کي سنديون شيون گھٽ نه ڏيو ۽ مُلڪ ۾ اُن جي سُڌاري کان پوءِ فساد نه وجھو، جيڪڏھن اوھين مؤمن آھيو ته اِھو اوھان لاءِ ڀلو آھي.'),
(1040, 7, 86, '۽ سڀ ڪنھن رستي تي ھن لاءِ نه وھو ته جيڪو الله کي کي مڃي تنھن کي ڌمڪايو ۽ الله جي واٽ کان جھليو ۽ اُن (واٽ) جي ڏنگائي گھرو، ۽ ياد ڪريو جڏھن اوھين ٿورڙا ھيؤ تڏھن اوھان کي وڌايائين، ۽ ڏسو ته فساد وجھندڙن جي پڇاڙي ڪھڙي (بڇڙي) ٿي.'),
(1041, 7, 87, '۽ جيڪڏھن اوھان مان ھڪ ٽوليءَ انھن کي مڃيو آھي جنھن سان موڪليو ويو آھيان ۽ ٻي ٽوليءَ نه مڃيو آھي ته (اوھان کي جڳائي ته ايستائين) صبر ڪريو جيسين اسان جي وچ ۾ الله حُڪم ڪري، ۽ اُھو چڱو حُڪم ڪندڙ آھي.'),
(1042, 7, 88, 'سندس قوم مان ھٺيلن سردارن چيو ته اي شعيب توکي ۽ جن توکي مڃيو آُھي تن کي پنھنجي شھر مان ضرور لوڌيندا سون يا ته اوھين اسان جي پر ۾ موٽي اچو، (شعيب) چيو ته جيتوڻيڪ اسان اوھانجي دين کي بُڇان ڀائيندڙ آھيون (ته به موٽنداسون ڇا؟)'),
(1043, 7, 89, 'جيڪڏھن اسين اوھان جي پر ۾ اُنھيءَ کانپوءِ جو الله اسان کي کانئس ڇڏايو موٽي اچون ته بيشڪ الله تي ڪُوڙ چيوسون، ۽ اسان جي پالڻھار الله جي گھُر کان سواءِ اسان کي منجھس موٽڻ نه جڳائيندو آھي، اسان جي پالڻھار جي علم سڀني شيئن کي سمايو آھي، اسين الله تي ڀروسو ڪريون ٿا، اي اسانجا پالڻھار اسان جي ۽ اسان جي قوم جي وچ ۾ انصاف سان فيصلو ڪر ۽ تون چڱو فيصلو ڪندڙ آھين.'),
(1044, 7, 90, '۽ سندس قوم مان ڪافرن جي ٽوليءَ (پنھنجي يارن کي) چيو ته جيڪڏھن اوھين شعيب جي تابعداري ڪندؤ ته اوھين اُتي جو اُتي ڇيئي وارا ٿيندؤ.'),
(1045, 7, 91, 'پوءِ اُنھن کي زلزلي (جي عذاب) ورتو پوءِ پنھنجي جاين ۾ گوڏن ڀر ڪري مري ويا.'),
(1046, 7, 92, 'جن شعيب کي ڪوڙو ڀانيو سي ڄڻڪ اُنھن (ڳوٺن) ۾ رھيا ئي نه ھوا، جن شعيب کي ڪوڙو ڀانيو سي ڇيئي وارا ھوا.'),
(1047, 7, 93, 'پوءِ کائن مُنھن موڙيائين ۽ چيائين ته اي منھنجي قوم بيشڪ اوھان کي پنھنجي پالڻھار جا پيغام پھچايا اٿم ۽ اوھانجي خيرخواھي ڪيم، تنھنڪري (ھاڻي) ڪافر قوم تي ڇالاءِ ڏک ڪريان.'),
(1048, 7, 94, '۽ ڪنھن ڳوٺ ۾ پيغمبرن مان ڪوبه نه موڪليوسون پر اُتي جي رھاڪن کي (پيغمبرن جي نافرماني سبب) سختي ۽ مصيبت ۾ پڪڙيندا رھياسون ته مانَ اُھي عاجزي ڪن.'),
(1049, 7, 95, 'وري (ايستائين) ڏک جي بجاءِ سُک مٽائي ڏنوسون جيستائين آسودا ٿيا ۽ چيائون ته بيشڪ اسان جن ابن ڏاڏن کي (ايئن) ڏک سُک پھتو ھو پوءِ اوچتو ئي اُنھن کي سندن بيخبريءَ جي حالت ۾ پڪڙيوسون.'),
(1050, 7, 96, '۽ جيڪڏھن اُنھن ڳوٺن وارا ايمان آڻين ھا ۽ پرھيزگار بڻجن ھا ته مٿن آسمانن ۽ زمين کان برڪتون ضرور کوليون ھا پر ڪوڙ ڀانيائون تنھنڪري انھن جيڪي ڪيو تنھن سببان کين پڪڙيوسون.'),
(1051, 7, 97, 'ڳوٺن وارا جنھن حال ۾ سمھيل ھجن تنھن مھل راتوواہ پاڻ تي اسانجي عذاب پھچڻ کان ڇو بي پرواہ ٿيا آھن؟'),
(1052, 7, 98, 'يا ڳوٺن وارا ڇو بي ڀوا ٿيا آھن؟ جو اسان جو عذاب ڏينھن چڙھي مھل وٽن اچي ۽ اُھي راند ۾ مشغول ھجن؟'),
(1053, 7, 99, 'الله جي رٿ کان ڇو بي ڀوا ٿيا آھن؟ جو ڇيئي واريءَ قوم کانسواءِ (ٻيو) ڪو الله جي رٿ کان بي ڀئو نه ٿيندو آھي.'),
(1054, 7, 100, 'جيڪي مُلڪ جي رھندڙن کانپوءِ وارث ٿين ٿا تن کي پڌرو نه ٿيو آھي ڇا ته جيڪڏھن گھرون ھا ته کين سندن گناھن سببان سزا ڏيون ھا، ۽ سندين دلين تي مُھر ھئين ان کان جنھنڪري اُھي نٿا ٻُڌن.'),
(1055, 7, 101, '(اي پيغمبر) اُھي ڳوٺ آھن جن جون خبرون توکي بيان ڪريون ٿا، ۽ بيشڪ وٽن سندن پيغمبر پڌرن معجزن سان آيا، پوءِ اُنھيءَ سببان جو اڳي ڪُوڙ ڀانيائون اُھي ڪڏھن به نه مڃيندا ھئا، اھڙي طرح الله ڪافرن جي دلين تي مُھر ھڻندو آھي.'),
(1056, 7, 102, '۽ منجھائن گھڻن کي انجام پاڙيندڙ نه ڏٺوسون، ۽ منجھائن گھڻن کي بي دين ڏٺوسون.'),
(1057, 7, 103, 'وري کائن پوءِ مُوسىٰ کي فرعون ۽ سندس سردارن ڏانھن پنھنجن مُعجزن سان پيغمبر ڪري موڪليوسون پوءِ اُنھن (نه مڃڻ سببان) آيتن تي ظلم ڪيو، (اي پيغمبر) ڏس ته فساد ڪندڙن جي پڇاڙي ڪھڙي طرح ٿي.'),
(1058, 7, 104, '۽ موسىٰ (فرعون کي) چيو ته اي فرعون آءٌ جھانن جي پالڻھار جو پيغمبر آھيان.'),
(1059, 7, 105, 'ھن (ڳالھ) تي قائم آھيان ته الله تي سچ کانسواءِ ڪي ڪين چوان، بيشڪ اوھان وٽ اوھان جي پالڻھار کان چٽي مُعجزي سان آيو آھيان تنھنڪري بني اسرائيلن کي مون سان (ھلڻ جي) موڪل ڏي.'),
(1060, 7, 106, '(فرعون) چيو ته جيڪڏھن ڪو معجزو آندو اٿئي ته جيڪڏھن سچن مان آھين ته اُھو آڻ.'),
(1061, 7, 107, 'پوءِ پنھنجي لٺ اُڇليائين ته اُڇلڻ سان ئي اُھو پڌرو واسينگ (نانگ) ٿي پيئي.'),
(1062, 7, 108, '۽ پنھنجو ھٿ ٻاھر ڪڍيائين ته اُھو ڪڍڻ سان ئي ڏسندڙن لاءِ روشن ٿيو.'),
(1063, 7, 109, 'فرعون جي قوم مان سردار ماڻھو چوڻ لڳا ته ھيءُ پڪو داناءُ جادوگر آھي.'),
(1064, 7, 110, 'اوھان کي اوھان جي مُلڪ مان ڪڍي ڇڏڻ گھري ٿو، تنھنڪري اوھين ڇا ٿا صلاح ڏيو؟'),
(1065, 7, 111, '(فرعون کي) چيائون ته کيس ۽ ڀاڻس کي ترساءِ ۽ ڪوٺيندڙ شھرن ڏانھن موڪل.'),
(1066, 7, 112, 'ته تو وٽ سڀئي داناء جادوگر آڻين.'),
(1067, 7, 113, '۽ فرعون وٽ جادوگر آيا چوڻ لڳا ته جيڪڏھن اسين غالب ٿيون ته اسان لاءِ ضرور (ڪو) انعام (ھئڻ) گھرجي.'),
(1068, 7, 114, 'فرعون چيو ته ھائو (انعام به ڏبو) ۽ بيشڪ مرتبي وارن مان (به) ٿيندؤ.'),
(1069, 7, 115, 'چيائون ته اي موسىٰ توکي (جادوءَ جي شيء) اڇلڻ گھرجي يا (پھرين) اسين اڇليندڙ ٿيون.'),
(1070, 7, 116, '(موسى) چيو ته (اوھين) اُڇليو، پوءِ جنھن مھل اُڇليائون (ته) ماڻھن جون اکيون جادوءَ سان بند ڪيائون ۽ انھن کي ڊيڄاريائون ۽ وڏا جادو آندائون.'),
(1071, 7, 117, '۽ مُوسىٰ ڏانھن وحي موڪليوسون ته پنھنجي لٺ اُڇل، پوءِ جيڪو جادو ڪيائون تنھنکي اُھا اُنھيءَ مھل ئي ڳھندا وئي.'),
(1072, 7, 118, 'پوءِ سچ پڌرو ٿيو ۽ جيڪي ڪندا رھيا سو ڪوڙو ٿيو.'),
(1073, 7, 119, 'پوءِ (فرعون وارا) اُن ھنڌ مغلوب ٿيا ۽ خوار ٿي موٽيا.'),
(1074, 7, 120, '۽ جادوگر سجدي ۾ ڪري پيا.'),
(1075, 7, 121, 'چيائون ته جھانن جي پالڻھار تي ايمان آندوسون.'),
(1076, 7, 122, '(جو) مُوسىٰ ۽ ھارون جو پالڻھار (آھي).'),
(1077, 7, 123, 'فرعون چيو ته اوھان منھنجي موڪل کان اڳي ئي اُن تي ايمان ڇو آندو آھي؟ ھيء (اوھان جو) فريب آھي (جو پاڻ ۾ گڏجي) ھن ڪري (ھن) شھر ۾ ڪيو اٿو ته سندس رھاڪن کي منجھانئس ڪڍي ڇڏيو، پوءِ سگھو کي ڄاڻندو.'),
(1078, 7, 124, 'اوھان جا ھٿ ءِ اوھان جا پير اُبتڙ سبتڙ ضرور وڍيندس وري اوھان مڙني کي ضرور سوريءَ چاڙھيندس.'),
(1079, 7, 125, 'چيائون ته بيشڪ اسان پنھنجي پالڻھار ڏانھن ضرور موٽندڙ آھيون.'),
(1080, 7, 126, '۽ تون رڳو ھن ڪري اسان سان وير ٿو رکين ته اسان جي پالڻھار جا حُڪم جڏھن اسان وٽ آيا (تڏھن) اسان مڃيا آھن، اي اسان جا پالڻھار اسان تي صبر پلٽ ۽ اسان کي مسلمان ڪري مار.'),
(1081, 7, 127, '۽ فرعون جي قوم جي سردارن (کيس) چيو ته موسىٰ ۽ سندس قوم کي ھن لاءِ ڇڏي ڏئين ٿو ڇا؟ ته مُلڪ ۾ فساد وجھن ۽ توکي ۽ تنھنجي معبودن (جي عبادت) کي ڇڏي ڏين؟ چيائين ته سندن پٽ سگھوئي ڪھنداسون ۽ سندين ڌيئرون ڇڏي ڏينداسون، ۽ اسين سندن مٿان بيشڪ زور وارا آھيون.'),
(1082, 7, 128, 'مُوسىٰ پنھنجي قوم کي (دلگير ڏسي) چيو ته الله کان مدد گھرو ۽ صبر ڪريو، ڇوته ساري زمين الله جي آھي، پنھنجي ٻانھن مان جنھن کي گھرندو آھي تنھنکي اُن جو وارث بڻائيندو آھي، ۽ (چڱي) عاقبت پرھيزگارن لاءِ آھي.'),
(1083, 7, 129, 'چيائون ته اسين تنھنجي اچڻ کان اڳ (به) ۽ تنھنجي اچڻ کان پوءِ (به) ايذايا ويا آھيون، (مُوسى) چين ته اُميد آھي ته اوھانجو پالڻھار اوھان جي دشمن کي ناس ڪري ڇڏيندو ۽ مُلڪ ۾ اوھان کي پيڙھيءَ تي وھاريندو پوءِ ڏسندؤ ته (اوھين) ڪھڙي طرح عمل ٿا ڪريو.'),
(1084, 7, 130, '۽ بيشڪ فرعون جي قوم کي ڏڪار ۽ ميون جي گھٽتائيءَ ۾ گرفتار ڪيوسون ته مانَ اُھي نصيحت وٺن.'),
(1085, 7, 131, 'پوءِ جڏھن اُنھن کي سُک پھتو ٿي ته چيائون ٿي ته ھيء اسان جو (لائق) آھي، ۽ جيڪڏھن اُنھن کي ڪو ڏک پھچندو ھو ته موسىٰ ۽ سندس سنگت کي نڀاڳو ڀائيندا ھوا، خبردار الله وٽ سندين ئي نڀاڳائي آھي پر منجھائن گھڻا نه ٿا ڄاڻن.'),
(1086, 7, 132, '۽ چيائون ته (اي موسى) جڏھن ڪڏھن ڪابه نشاني اسان وٽ ھن لاءِ آڻيندين ته اُن سان اسان تي جادو ڪرين ته اسين توکي مڃڻ وارا نه آھيون.'),
(1087, 7, 133, 'پوءِ مٿن طوفان ۽ ماڪڙ ۽ جونء ۽ ڏيڏر ۽ رت کليل مُعجزا موڪلياسون، پوءِ وڏائي ڪيائون ۽ (اُھا) ڏوھاري قوم ھئي.'),
(1088, 7, 134, '۽ جڏھن مٿن سزا پھتي ته (تڏھن) چيائون ٿي ته اي موسىٰ پنھنجي پالڻھار کان انھيءَ وعدي سان جو توسان ڪيو اٿس اسان لاءِ دُعا گھُر، ته جيڪڏھن اسان تان عذاب لاٿئي ته توتي ضرور ايمان آڻينداسون ۽ بني اسرائيلن کي (به) ضرور توسان ھلڻ جي موڪل ڏينداسون.'),
(1089, 7, 135, 'پوءِ جنھن مھل عذاب جيڪو کين ٺھرايل مدت توڻي پھچڻو ھو سو کانئن لاٿوسون ٿي (ته) اُتي جو اُتي انھن اَنجام ڀڳو ٿي.'),
(1090, 7, 136, 'پوءِ اسان کانئن پورو وير ورتو جو انھن کي سمنڊ ۾ انھيءَ ڪري ٻوڙيوسون جو اُنھن اسان جي آيتن کي ڪوڙ ڄاتو ۽ اُن کان بي خبر ھوا.'),
(1091, 7, 137, '۽ اُھا قوم جيڪا ھيڻي ڀانئبي ھئي تنھن کي اُنھيءَ اوڀر ۽ اولھ جي زمين (يعني شام ۽ مِصر) جو وارث ڪيوسون جنھن ۾ برڪت رکي ھئي سون، ۽ تنھنجي پالڻھار جو بني اسرائيلن سان چڱو انجام انھي سببان پورو ٿيو جو صَبر ڪيائون، ۽ فرعون ۽ سندس قوم جيڪي بڻايو ۽ جيڪا (اڏاوت) بلند ڪئي ھوائون سا (سڀ) ناس ڪري ڇڏي سون.'),
(1092, 7, 138, '۽ بني اسرائيل کي سمنڊ کان پار لنگھايوسون پوءِ اُھي ھڪڙي اھڙي قوم تي پُھتا جو پنھنجي بتن جا مجاور ھوا، (موسى) کي چيائون ته اي موسىٰ اسان لاءِ (به) ھڪڙو اھڙو خُدا بڻاءِ جھڙا ھنن لاءِ خُدا آھن، (موسى) چيو ته بيشڪ اوھين جاھل قوم آھيو.'),
(1093, 7, 139, 'ھيء ماڻھو پاڻ جنھن (مذھب) ۾ پيل آھن سو رد آھي ۽ جيڪي ڪندا رھندا آھن سو غلط آھي.'),
(1094, 7, 140, 'مُوسىٰ چيو ته الله کانسواءِ اوھان لاءِ ٻيو معبود ڇو گھران؟ جنھن اوھان کي جھانن جي ماڻھن کان ڀلو ڪيو آھي.'),
(1095, 7, 141, '۽ (ياد ڪر) جڏھن اوھان کي فرعون جي ماڻھن کان ڇڏايوسين جن اوھان کي بڇڙو عذاب چکايو ٿي، جو اوھان جا پٽ ڪُھندا ھوا ۽ اوھان جو ڌيئر جيئريون ڇڏيندا ھوا، ۽ اُن ۾ اوھان جي پالڻھار کان اوھان لاءِ وڏي پرک ھُئي.'),
(1096, 7, 142, '۽ مُوسىٰ کي ٽيھن راتين جو انجام ڏنوسون ۽ انھي کي (ٻـين) ڏھن سان پورو ڪيوسون پوءِ سندس پالڻھار جو انجام چاليھن راتين جو پورو ٿيو، ۽ مُوسىٰ پنھنجي ڀاءُ ھارون کي چيو ته منھنجي قوم ۽ منھنجي پوئتان خليفو ٿي ۽ سڌارو ڪج ۽ فسادين جي واٽ تي نه ھلج.'),
(1097, 7, 143, '۽ جنھن مھل مُوسىٰ اسان جي انجام واري ھنڌ آيو ۽ سندس پالڻھار ساڻس ڳالھايو (تنھن مھل) چيائين ته اي منھنجا پالڻھار مونکي پاڻ ڏيکار ته آءٌ توکي ڏسان، (الله) چيو ته مونکي ڪڏھن نه ڏسي سگھندين پر جبل ڏانھن نھار پوءِ جيڪڏھن پنھنجي جاءِ ٽڪاءُ ڪيو ته تون مونکي ڏسي سگھندين، پوءِ جنھن مھل سندس پالڻھار جبل ڏانھن تجلّو ڪيو (ته) اُن کي ٽڪر ٽڪر ڪري ڇڏيائين ۽ موسىٰ بيھوش ٿي ڪري پيو، پوءِ جنھن مھل ھوش ۾ آيو (ته) چوڻ لڳو ته توکي پاڪائي جڳائي تنھنجي درگاہ ۾ توبه ڪيم ۽ آءٌ (سڀ کان) پھريون مؤمن آھيان.'),
(1098, 7, 144, '(الله) فرمايو ته اي موسىٰ مون توکي پنھنجي پيغمبريءَ ۽ پنھنجي ڳالھائڻ سان ماڻھن کان ڀلارو ڪيو آھي، جيڪو (توريت) توکي ڏنو اٿم سو وٺ ۽ شڪر ڪندڙن مان ھُج.'),
(1099, 7, 145, '۽ اُن (مُوسى) لاءِ سڀڪنھن شيء جي نصيحت واسطي ۽ سڀڪنھن شيء جي ڌار ڌار بيان واسطي (توريت) تختن ۾ لکي ڏنوسون، (۽ چيو ويس ته) اُن کي پختائيءَ سان وٺ ۽ پنھنجي قوم کي حڪم ڪر ته اُن جون چڱيون ڳالھيون (عمل ۾) وٺندا رھن، اوھان کي بدڪارن جي جاءِ سگھو ڏيکاريندس.'),
(1100, 7, 146, 'جيڪي زمين ۾ ناحق وڏائي ڪندا رھندا آھن تن کي سگھو پنھنجي آيتن (جي سمجھ) کان جھليندس، ۽ جيڪڏھن سڀ نشانيون ڏسندا ته (به) انھن تي ايمان نه آڻيندا، ۽ جيڪڏھن ھدايت جي واٽ ڏسندا ته ان کي واٽ نه بڻائيندا، ۽ جيڪڏھن گمراھي جي واٽ ڏسندا ته ان کي واٽ بڻائيندا، اِھو ھن سببان آھي جو اسان جي آيتن کي ڪوڙ ڀانيائون ۽ انھن کان غافل رھيا ھوا.'),
(1101, 7, 147, '۽ جن اسان جي آيتن کي ۽ آخرت جي ملڻ کي ڪوڙ ڄاتو تن جا اَعمال ناس ٿيا، جيڪي ڪمائيندا آھن تنھن کانسواءِ ٻيو ڪو بدلو نه ڏبن.'),
(1102, 7, 148, '۽ مُوسىٰ جي قوم کانئس پوءِ انھن (قبطين) جي زيورن مان گابي جو بوتو اھڙو ٺاھي ورتو جنھن کي رنڀ ھئي، نه ٿي ڏٺائون ڇا ته اُھو ساڻن نه ڳالھائيندو ھو ۽ نڪي انھن کي واٽ ڏيکاريندو ھو؟ اُن کي (خدا ڪري) ورتائون ۽ ظالم ھوا.'),
(1103, 7, 149, '۽ جڏھن پشيمان ٿيا ۽ سمجھيائون ته اُھي پڪ گمراہ ٿيا (ته) چيائون ته جيڪڏھن اسان جو پالڻھار اسان تي ٻاجھ نه ڪندو ۽ اسان کي نه بخشيندو ته ضرور ڇيئي وارن مان ٿينداسون.'),
(1104, 7, 150, '۽ جڏھن مُوسىٰ پنھنجي قوم ڏانھن ڏمريل افسوس ڪندڙ ٿي موٽيو (تڏھن) چيائين ته جيڪي منھنجي پرپٺ مون کانپوءِ ڪيو سو بڇڙو آھي، پنھنجي پالڻھار جي حُڪم کان (اڳ) ڇو تڪڙ ڪيوَ؟ ۽ ڦرھيون اُڇليائين ۽ پنھنجي ڀاءُ جي مٿي (جي وارن) کان وٺي کيس پاڻ ڏانھن ڇڪڻ لڳو، (ھارون) چيو ته اي مُنھنجا ڀاءُ بيشڪ (ھن) قوم مون کي ھيڻو ڀانيو ۽ مُنھنجي مارڻ تي سنبريا، تنھنڪري مون تي دشمن نه کلاءِ ۽ نڪي مون کي ظالم قوم سان گڏ.'),
(1105, 7, 151, '(موسى) چيو اي منھنجا پالڻھار مون کي ۽ مُنھنجي ڀاءُ کي بخش ۽ اسان کي پنھنجي ٻاجھ ۾ داخل ڪر، ۽ تون (سڀني) ٻاجھ وارن کان ٻاجھ وارو آھين.'),
(1106, 7, 152, 'جن گابي کي (خُدا ڪري) ورتو تن کي سندن پالڻھار وٽان ڏمر ۽ دُنيا جي حياتيءَ ۾ خواري سگھو پھچندي، ۽ اھڙيءَ طرح ٺاھ ٺاھيندڙن کي سزا ڏيندا آھيون.'),
(1107, 7, 153, '۽ (اي پيغمبر) جن بڇڙا ڪم ڪيا وري اُن کان پوءِ توبه ڪيائون ۽ ايمان آندائون، (تن کي) تنھنجو پالڻھار اُن (توبه) کان پوءِ بخشڻھار مھربان آھي.'),
(1108, 7, 154, '۽ جڏھن مُوسىٰ کان ڏمر لٿو تڏھن (توريت جون) ڦرھيون ورتائين، ان جي لکيي ۾ اُنھن لاءِ ھدايت ۽ ٻاجھ ھُئي جيڪي پنھنجي پالڻھار کان ڊڄندا آھن.'),
(1109, 7, 155, '۽ مُوسىٰ پنھنجي قوم مان ستر ڄڻا اسان جي انجام جي وقت لاءِ چونڊيا، پوءِ جنھن مھل انھن کي زلزلي ورتو (ته موسى) چيو ته اي منھنجا پالڻھار جيڪڏھن (تون) گھرين ھا ته ھن کان اڳ انھن کي ۽ خاص مونکي ھلاڪ ڪري ڇڏين ھا، اسان منجھان بي عقلن جيڪي ڪيو تنھن سببان اسانکي ڇو ٿو ھلاڪ ڪرين؟ اِھا تنھنجي پرک (حڪمت) کانسواءِ نه آھي، جنھن کي گھرين تنھن کي ان سان گمراھ ڪندو آھين ۽ جنھن کي گھرندو آھين ھدايت ڪندو آھين، تون اسان جو سنڀاليندڙ آھين تنھنڪري اسانکي بخش ۽ اسان تي ٻاجھ ڪر ۽ تون (سڀ کان) چڱو بخشيندڙ آھين.'),
(1110, 7, 156, '۽ اسان لاءِ ھن دُنيا ۾ ۽ آخرت ۾ چڱائي لک ڇوته اسين توڏانھن رُجوع ٿيا آھيون، (الله) فرمايو ته آءٌ پنھنجو عذاب جنھن لاءِ وڻندو اٿم تنھنکي پھچائيندو آھيان، ۽ منھنجي ٻاجھ سڀڪنھن شيء کي شامل آھي، ۽ پوءِ آءٌ پنھنجي ٻاجھ انھن لاءِ لکندس جيڪي پرھيزگاري ڪندا آھن ۽ زڪواة ڏيندا آھن ۽ جيڪي منھنجي آيتن تي ايمان آڻيندا آھن.'),
(1111, 7, 157, '(۽ پڻ آخرت جي رحمت اُنھن لاءِ لکندس) جيڪي انھيء نبّي اُمّي پيغمبر جي تابعداري ڪندا آھن جنھن جون وصفون توريت ۽ انجيل ۾ پاڻ وٽ لکيلون لھندا آھن، (اُھو) اُنھن کي چڱن ڪمن جو حُڪم ڪندو آھي ۽ مندن ڪمن کان اُنھن کي جھليندو آھي ۽ سٺيون شيون اُنھن لاءِ حلال ڪندو آھي ۽ پليت شيون مٿن حرام ڪندو آھي ۽ سندن ڳرو بار ۽ جيڪي تڪليفون مٿن ھيون سي کانئن لاھيندو آھي، پوءِ جن اُن تي ايمان آندو ۽ سندس تعظيم ڪئي ۽ کيس مدد ڏنائون ۽ اُنھيءَ نُور (ڪلام الله) جي تابعداري ڪيائون جيڪو ساڻس لاٿو ويو سي ئي ڇٽل آھن.'),
(1112, 7, 158, '(اي پيغمبر) چؤ ته اي انسانؤ آءٌ اوھان مڙني ڏانھن اُن الله جو پيغمبر (ٿي آيو) آھيان جنھن جي آسمانن ۽ زمين ۾ بادشاھي آھي، اُن الله کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي اُھو جياريندو آھي ۽ ماريندو آھي، تنھنڪري الله ۽ سندس انھي اُمّي نبيء تي ايمان آڻيو جيڪو الله ۽ سندس حُڪمن کي مڃيندو آھي ۽ اُن جي تابعداري ڪريو ته مانَ اوھين سڌي واٽ لھو.'),
(1113, 7, 159, '۽ مُوسىٰ جي قوم مان ھڪڙي اھڙي ٽولي آھي جو (ماڻھن کي) سڌو رستو ڏيکاريندي آھي ۽ اُھي ان سان انصاف تي ھلندا آھن.'),
(1114, 7, 160, '۽ بني اسرائيلن مان ٻارھن پاڙا ٽوليون ڪري جدا ڪياسون، جڏھن موسىٰ کان سندس قوم پاڻي گھريو تڏھن ڏانھس وحي موڪليوسون ته پنھنجي لٺ پھڻ کي ھڻ، پوءِ (ھنيائين ته) اُن مان ٻارھن واھيون وھيون، بيشڪ سڀڪنھن ماڻھوءَ پنھنجي پاڻي ڀرڻ جي جاءِ ڄاتي ٿي، ۽ اسان مٿن ڪڪر جي ڇانو ڪئي ۽ مٿن مَن ۽ سَلۡوىٰ لاٿاسون، (۽ چيوسون ته) جيڪا اوھان جي سٺي روزي بڻائي اٿون تنھن مان کائو، ۽ اسان جو ڪُجھ نه وڃائون پر پاڻ کي نقصان ٿي لاٿائون.'),
(1115, 7, 161, '۽ جڏھن انھن کي چيو ٿي ويو ته ھن ڳوٺ ۾ وڃي ٽڪو ۽ منجھانئس جتان وڻيو تتان کائو ۽ چؤ ته بخش (ڪر) ۽ دروازي کان سجدو ڪندڙ ٿي لنگھو ته اوھان کي اوھان جون خطائون بخشيون، ڀلارن کي وڌيڪ (چڱائي) ڏينداسون.'),
(1116, 7, 162, 'پوءِ انھن (بني اسرائيلن) مان جيڪي ظالم ھئا تن کي جيڪا ڳالھ چئي وئي سا انھن مٽائي ٻي ڳالھ چئي پوءِ مٿن آسمان کان انھيءَ سببان سزا موڪلي سون جو ظلم ٿي ڪيائون.'),
(1117, 7, 163, '۽ (اي پيغمبر) کائن اُن ڳوٺ (جي حال) بابت پڇا ڪر جيڪو درياءَ جي ڪناري تي ھو، جڏھن (اُھي منع ھوندي) ڇنڇر (جي حُڪم) ۾ حُڪم عدولي ڪرڻ لڳا جڏھن انھن جون مَڇيون سندن ڇنڇر ڏينھن (پاڻي جي مٿان) سندن سامھون پڌريون ٿي اينديون ھيون ۽ جنھن ڏينھن ڇنڇر نه ڪندا ھئا تنھن ڏينھن (ٻاھر نه) اينديون ھيون، اھڙي طرح اُنھن کي انھي سببان پرکيوسون جو نافرماني ٿي ڪيائون.'),
(1118, 7, 164, '۽ جڏھن منجھائن ھڪ ٽوليء (نصيحت ڪندڙن کي) چيو ته اھڙي قوم کي اوھين ڇو نصيحت ڪندا آھيو جن کي الله ھلاڪ ڪندڙ آھي يا کين سخت عذاب ڪندڙ آھي؟ (تڏھن) چيائون ته (اسان جي نصيحت) اوھانجي پالڻھار وٽ عذر آھي ۽ مانَ اُھي ڊڄن.'),
(1119, 7, 165, 'پوءِ جيڪا (نصيحت) ڏني وئي ھُين سا جنھن مھل وساريائون اُنھي مھل اُنھن ماڻھن کي بچايوسون جيڪي مدائي کان جھليندا ھُين ۽ ظالمن کي سخت عذاب سان انھيء ڪري پڪڙيوسون جو ھو بدڪار ھوا.'),
(1120, 7, 166, 'پوءِ جنھن کان جھليو ويو ھُين تنھن کان جڏھن حد کان لنگھي ويا ته چيوسون ته خوار ٿيل ڀولڙا ٿيو.'),
(1121, 7, 167, '۽ (ياد ڪر) جڏھن تنھنجي پالڻھار (يھودين کي) سُڌ ڏني ته (اُھو) مٿن قيامت تائين (اھڙو حاڪم) ضرور مقرر ڪندو جيڪو انھن کي بڇڙي سزا چکائيندو رھندو، ڇوته تنھنجو پالڻھار سَگھو سزا ڏيندڙ آھي، ۽ اُھو بخشڻھار مھربان (به) آھي.'),
(1122, 7, 168, '۽ اُنھن (بني اسرائيلن) کي ملڪ ۾ ٽوليون ٽوليون ڪري ڇڙو ڇڙ ڪري ڇڏيوسون، منجھائن ڪي سُڌريل ۽ ڪي اَڻ سڌريل آھن، ۽ اُنھن کي سکن ۽ ڏکن سان پرکيوسون ته مانَ اُھي موٽن.'),
(1123, 7, 169, 'وري کائن پوءِ اھڙا بُڇڙا اُنھن جي جاڳھن تي ويٺا جو ڪتاب (توريت) جا (ڇڙا) وارث بڻيا ھن دنيا جو سامان اختيار ڪندا آھن ۽ چوندا آھن ته بخشبا سون، ۽ جيڪڏھن وٽن ڪو اھڙو سامان ايندو آھي ته (اُھي پشيمان نه ٿيندا آھن ۽) اُھو وٺندا رھندا آھن، کائن ڪتاب (توريت) ۾ ھي انجام نه ورتو ويو ھو ڇا ته سچ کانسواءِ الله تي ڪي به ڪين چون؟ ۽ جيڪي منجھس آھي سو پڙھيو به اٿن، ۽ آخرت جو مُلڪ اُنھن لاءِ ڀلو آھي جيڪي پرھيزگار آھن، پوءِ ڇونه سمجھندا آھيو؟'),
(1124, 7, 170, '۽ جيڪي ڪتاب کي مُحڪم وٺن ٿا ۽ نماز پڙھن ٿا، (اھڙن) نيڪن جو اجر اسين نه وڃائيندا آھيون.'),
(1125, 7, 171, '۽ (ياد ڪر) جڏھن انھن جي مٿان جبل کي ٽنگيوسون ڄڻ ته اُھو ڇٽي ھو ۽ ڀانيائون ته اُھو مٿن ڪرندو، (کين چيوسون ته) جيڪي اسان اوھان کي ڏنو آھي سو مُحڪم ڪري وٺو ۽ جيڪي منجھس آھي سو ياد ڪندا رھو ته مانَ اوھين (عذاب کان) بچو.'),
(1126, 7, 172, '۽ (اي پيغمبر ياد ڪر) جڏھن تنھنجي پالڻھار آدم جي اولاد مان (يعني) پٺين مان سندن اولاد کي پيدا ڪيو ۽ سندن وجود کي مٿن شاھد ڪيائين، (۽ پڇيائين) ته (آءٌ) اوھانجو پالڻھار نه آھيان ڇا؟ چيائون ته ھائو (آھين)، گواہ ٿيا آھيون، (اھا گواھي ھن لاءِ وٺڻي ھئي) ته متان قيامت جي ڏينھن چئو ته اسين ھن (ڳالھ) کان بي خبر ھئاسون.'),
(1127, 7, 173, 'يا متان چئو ته اڳئي شرڪ اسان جي ابن ڏاڏن ڪيو ھو ۽ اسين کائن پوءِ اولاد ھئاسون، تنھنڪري جيڪي بيھودن ڪيو تنھن سببان اسان کي ڇو ٿو ھلاڪ ڪرين؟'),
(1128, 7, 174, '۽ اھڙي طرح اسين آيتون کليل طرح بيان ڪندا آھيون ته مانَ اُھي موٽن.'),
(1129, 7, 175, '۽ (اي پيغمبر) تون اُن (ماڻھو) جو قصو کين پڙھي ٻڌاءِ جنھن کي پنھنجين آيتن جو علم ڏنوھوسون (اُھو) اُن (آيتن جي علم) مان نڪتو پوءِ شيطان سندس پُٺيان لڳو پوءِ (اُھو) گمراھن مان ٿيو.'),
(1130, 7, 176, '۽ جيڪڏھن گھرون ھا ته کيس انھن (آيتن) سان ضرور مٿاھون ڪريون ھا پر ھُن (ھيٺ) زمين ڏانھن لاڙو ڪيو ۽ پنھنجن سڌن پٺيان لڳو، پوءِ سندس مثال ڪُتي جي مثال وانگر ٿيو، (جو) جيڪڏھن (تون) مٿس (ڪا شيء) اولارين ته سھڪي يا جي ڇڏي ڏئين ته (به) سھڪي، اھو انھي قوم جو مثال آھي جن اسان جي آيتن کي ڪُوڙ ڄاتو، پوءِ (اي پيغمبر انھن کي) اِھا نصيحت پڙھي ٻڌاءِ ته مانَ اُھي سوچ ڪن.'),
(1131, 7, 177, 'جن ماڻھن اسان جي آيتن کي ڪوڙ ڄاتو تن جو حال بڇڙو آھي ۽ اُنھن پاڻ تي ئي ظلم ڪيو.'),
(1132, 7, 178, 'جنھن کي الله ھدايت ڪئي سو ھدايت وارو آھي، ۽ جنھن کي ڀلايائين سي ئي ڇيئي وارا آھن.'),
(1133, 7, 179, '۽ بيشڪ دوزخ لاءِ جنن ۽ ماڻھن مان گھڻا پيدا ڪيا اٿون، انھن جو اھڙيون دليون آھن جو انھن سان (الله جو حُڪم) نه سمجھندا آھن، ۽ اُنھن جون اھڙيون اکيون آھن جو اُنھن سان (حقانيت) ڏسي نه سگھندا آھن، ۽ اُنھن جا اھڙا ڪن آھن جو انھن سان (حق کي) ٻڌي نه سگھندا آھن، اھي ڍورن وانگر آھن بلڪ اُنھن کان وڌيڪ گمراہ آھن، اُھي ئي غافل آھن.'),
(1134, 7, 180, '۽ الله جا چڱا نالا آھن انھن نالن سان کيس سڏيو، ۽ جيڪي سندس نالن ۾ ڏنگائي ھلندا آھن تن کي ڇڏي ڏيو، (۽) جيڪي ڪندا آھن تنھنجي سگھو سزا ڏبين.'),
(1135, 7, 181, '۽ جيڪي اسان پيدا ڪيا آھن تن مان ھڪ ٽولي آھي جا سچي (دين) جو رستو ڏيکاريندي آھي ۽ اُن (دين) سان انصاف ڪندا آھن.'),
(1136, 7, 182, '۽ جن اسان جي آيتن کي ڪوڙ ڄاتو تن کي سگھو وڌائي وڌائي (اُتاھون) سزا ڏينداسون جتان ڄاڻندا ئي نه.'),
(1137, 7, 183, '۽ اُنھن کي مُھلت ڏيندس، ڇوته اسان جي رٿ پڪي آھي.'),
(1138, 7, 184, '(اُھي) ڌيان نه ڪندا آھن ڇا ته سندن سنگتي (ﷴ ﷺ) کي ڪا چريائي ڪانه آھي، اُھو ته رڳو پڌرو ڊيڄاريندڙ آھي.'),
(1139, 7, 185, 'آسمانن ۽ زمين جي بادشاھيءَ (۾) ۽ الله ھر جنس مان جيڪي پيدا ڪيو آھي تنھن ۾ نه نھاريندا آھن ڇا؟ ۽ ته متان سندن (موت جي) ٺھرايل مُدّت پوري ٿيڻ تي ھجي، پوءِ قرآن پڄاڻا ڪھڙي ڳالھ تي ايمان آڻيندا.'),
(1140, 7, 186, 'جنھن کي الله گمراہ ڪندو آھي تنھن کي ڪو ھدايت ڪرڻ وارو ڪونھي، ۽ الله اُنھن کي سندن گمراھيءَ ۾ حيران ٿيل ڇڏي ڏيندو آھي.'),
(1141, 7, 187, '(اي پيغمبر) توکان قيامت بابت پُڇن ٿا ته ڪھڙي مھل اچڻي آھي؟ چؤ ته انھيء جي خبر رڳو منھنجي پالڻھار وٽ آھي، الله کانسواءِ کيس سندس وقت تي ڪو پڌرو نه ڪندو، (اُنجي اچڻ جي مھل جي ڄاڻپ) آسمانن ۽ زمين ۾ ڳري ٿي آھي (يعني ڪو سمجھي نه ٿو سگھي)، اُھا ته اوھان وٽ اوچتو ئي ايندي، (اي پيغمبر) توکان (ائين) پڇن ٿا جو ڄڻڪ تون اُنھن جو کوٽيندڙ (يعني جاچي معلوم ڪندڙ) آھين، چؤ ته اُن جي خبر الله کانسواءِ ٻئي ڪنھن وٽ نه آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.'),
(1142, 7, 188, 'چؤ ته الله جي گھُر ڌاران آءٌ پاڻ کي نڪي فائدو ۽ نڪي نقصان پھچائي سگھان ٿو، ۽ جيڪڏھن ڳُجھ ڄاڻان ھا ته گھڻيون چڱايون گڏ ڪريان ھا، ۽ مون کي ڪو ڏک نه پھچي ھا، آءٌ ته قوم مؤمنن لاءِ رڳو ڊيڄاريندڙ ۽ خوشخبري ڏيندڙ (پيغمبر) آھيان.'),
(1143, 7, 189, 'اُھو (الله) آھي جنھن اوھان کي ھڪ وجود مان بڻايو ۽ منجھانئس سندس زال ھن لاءِ بڻايائين ته ساڻس آرام وٺي، پوءِ جنھن مھل (اُھو) گڏ ٿيندو آھي ته اُھا ھلڪي بار سان ڳورھاري ٿيندي آھي ۽ اُن سان گھمندي رھندي آھي، وري جنھن مھل ڏاڍي ڳري ٿيندي آھي (تنھن مھل) ٻئي پنھنجي پالڻھار کي سڏيندا آھن (ته قسم آھي) ته جيڪڏھن اسان کي صحيح سالم ٻار ڏيندين ته ضرور شڪر ڪندڙن مان ٿينداسون.'),
(1144, 7, 190, 'پوءِ جنھن مھل انھن کي صحيح سالم ٻار ڏيندو آھي (ته) جيڪي اُنھن کي ڏنائين تنھن ۾ الله سان شريڪ مقرر ڪندا آھن، ۽ جنھن شيء کي الله سان شريڪ بڻائيندا آھن تنھن کان الله جو شان مٿاھون آھي.'),
(1145, 7, 191, '(ھي مشرڪ) اھڙن کي ڇو ٿا شريڪ بڻائين جي ڪجھ بڻائي نه سگھندا آھن ۽ اُھي پاڻ خلقيل آھن.'),
(1146, 7, 192, '۽ نڪي انھن کي ڪا مدد ڏيئي سگھندا آھن ۽ نڪي پاڻ کي (ئي) مدد ڏيئي سگھندا آھن.'),
(1147, 7, 193, '۽ (اي پيغمبر) جيڪڏھن اُنھن کي ھدايت ڏانھن سڏيندؤ ته اوھان جي تابعداري نه ڪندا، اوھان تي ھڪ جھڙو آھي ته انھن کي سڏيو يا اوھين ماٺ ڪريو.'),
(1148, 7, 194, 'الله کان سواءِ جن کي سڏيندا آھيو سي اوھان جھڙا ئي ٻانھا آھن تنھنڪري جيڪڏھن (اوھين) سچا آھيو ته انھن کي سڏيو ته ڀلي اوھان کي ورندي ڏين.'),
(1149, 7, 195, 'اُنھن (بتن) کي ڪي پير آھن ڇا جن سان گھمندا آھن، يا انھن کي ڪي ھٿ آھن ڇا جن سان وٺندا آھن، يا انھن کي ڪي اکيون آھن ڇا جن سان ڏسندا آھن، يا اُنھن کي ڪي ڪن آھن ڇا جن سان ٻڌندا آھن؟ چؤ ته (اوھين) پنھنجن بتن کي سڏيو پوءِ مون لاءِ جيڪا رٿ اچيو سا رٿيو ۽ مون کي مُھلت نه ڏيو.'),
(1150, 7, 196, 'بيشڪ منھنجو مددگار اُھو الله آھي جنھن ڪتاب نازل ڪيو، ۽ اُھو نيڪن کي مدد ڏيندو آھي.'),
(1151, 7, 197, '۽ ان (الله) کان سواءِ جن کي سڏيندا آھيو سي اوھان کي ڪا مدد ڏيئي نه سگھندا آھن ۽ نڪي پاڻ کي مدد ڏيئي سگھندا آھن.'),
(1152, 7, 198, '۽ جيڪڏھن اوھين انھن کي ھدايت ڏانھن سڏيو ته ڪين ٻڌندا، کين ڏسندو آھين (ته ڄڻ) ته توڏانھن تڪين ٿا مگر اُھي ڏسندا ئي نه آھن.'),
(1153, 7, 199, '(اي پيغمبر) معافي ڏيڻ جي عادت ڪر ۽ چڱي ڪم ڪرڻ جو حُڪم ڪر ۽ جاھلن کان منھن موڙ.'),
(1154, 7, 200, '۽ جيڪڏھن توکي شيطان کان ڪو وسوسو پوي ته الله کان پناھ گھر، بيشڪ اُھو ٻُڌندڙ ڄاڻندڙ آھي.'),
(1155, 7, 201, 'جيڪي پرھيزگار آھن تن کي جڏھن شيطان کان ڪو وسوسو پھچندو آھي تڏھن الله کي ياد ڪندا آھن پوءِ اُھي اُنھيءَ ئي مھل ڏسندڙ ٿيندا آھن.'),
(1156, 7, 202, '۽ اُنھن (شيطانن) جا ڀائر کين گمراھيءَ ۾ ڇڪيندا آھن وري نه گھٽائيندا آھن.'),
(1157, 7, 203, '۽ (اي پيغمبر) جڏھن وٽن ڪا آيت نه آڻيندو آھين (ته) چوندا آھن ته (پاڻون) ڇونه ٺاھيندو آھين؟ چؤ ته جيڪي مون ڏانھن پنھنجي پالڻھار جو وحي ٿيندو آھي تنھن کانسواءِ (ڪنھن جي) تابعداري نه ڪندو آھيان، ھي (قرآن) اسانجي پالڻھار وٽان سوجھرو ۽ ھدايت ۽ ٻاجھ انھيءَ قوم لاءِ آھي جي مڃيندي آھي.'),
(1158, 7, 204, '۽ جڏھن قرآن پڙھيو وڃي (ته) اُھو ڪن ڏيئي ٻڌو ۽ چُپ ڪريو مانَ ٻاجھ ڪيا وڃو.'),
(1159, 7, 205, '۽ پنھنجي پالڻھار کي پنھنجي دل ۾ عاجزيءَ ۽ ڀو سان وڏي آواز ڌاران صبح ۽ سانجھيءَ جو ياد ڪر ۽ غافلن مان نه ھُج.'),
(1160, 7, 206, 'تحقيق جيڪي تنھنجي پالڻھار وٽ (ويجھا) آھن (سي) سندس عبادت کان وڏائي نه ڪندا آھن ۽ ان جي پاڪائي بيان ڪندا آھن ۽ ان کي سجدو ڪندا آھن.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, '(اي پيغمبر) غنيمت جي مال بابت توکان پڇن ٿا، چؤ ته غنيمت جو مال الله ۽ پيغمبر جو آھي، پوءِ الله کان ڊڄو ۽ اوھين پاڻ ۾ ٺھراءُ ڪريو، ۽ جيڪڏھن مؤمن آھيو ته الله ۽ سندس رَسُول جي فرمانبرداري ڪريو.'),
(1162, 8, 2, 'مُؤمن اُھي ئي آھن جن جون دليون جڏھن الله ياد ڪيو ويندو آھي (ته) ڊڄنديون آھن ۽ جڏھن سندس آيتون انھن کي پڙھي ٻڌائبيون آھن تڏھن اُھي سندن ايمان کي وڌائينديون آھن ۽ پنھنجي پالڻھار تي ڀروسو ڪندا آھن.'),
(1163, 8, 3, 'اُھي نماز پڙھندا آھن ۽ جيڪي کين رزق ڏنوسون تنھن مان خرچيندا آھن.'),
(1164, 8, 4, 'اِھي ئي پڪا مؤمن آھن، انھن لاءِ سندن پالڻھار وٽ (وڏا) درجا ۽ بخشش ۽ چڱي روزي آھي.'),
(1165, 8, 5, 'جئن تنھنجي رب توکي تنھنجي گھر (مدينه) مان سچي تدبير لاءِ ٻاھر آندو، ۽ بيشڪ مؤمنن منجھان ھڪ ٽولي ضرور ناراض ھئي.'),
(1166, 8, 6, 'اُھي سچي ڳالھه بابت ان جي پڌري ٿيڻ کانپوءِ (به) توسان گفتگو ڪندا رھيا ڄڻ ته اُھي (اکين سان) ڏسندي موت ڏانھن ھڪاليا وڃن ٿا.'),
(1167, 8, 7, '۽ (ياد ڪريو) ته جڏھن الله اوھان سان انجام ڪيو ته (ڪافرن جي) ٻن ٽولين مان ھڪ اوھان کي ھٿ لڳندي ۽ اوھان گھريو ٿي ته بي ھٿيار (ٽولي) اوھان لاءِ ھُجي ۽ الله گھريو ٿي ته پنھنجي حُڪم سان حق کي ثابت ڪري ۽ ڪافرن جي پاڙ ھِنَ لاءِ وڍي.'),
(1168, 8, 8, 'ته حق کي ثابت ڪري ۽ باطل کي باطل ڪري توڻيڪ گنھگار ناراض رھن.'),
(1169, 8, 9, 'جڏھن جو پنھنجي پالڻھار کي فرياد ٿي ڪيوَ ته (اُھا) اوھانجي (فرياد) قبول ڪيائين (فرمايائين) ته آءٌ لڳولڳ ھڪ ٻئي پٺيان ايندڙن ھڪ ھزار ملائڪن سان اوھان جي مدد ڪندڙ آھيان.'),
(1170, 8, 10, 'الله ان (مدد) کي رڳو خوشخبري ڪيو ھو ۽ ته اُن سان اوھان جون دليون تسلّي واريون ٿين، ۽ الله (جي پار کان) سواءِ (ٻئي ھنڌان) مدد ملڻي نه آھي، ڇوته الله غالب حڪمت وارو آھي.'),
(1171, 8, 11, '(ياد ڪر) جنھن مھل پنھنجي طرف کان امن ڏيڻ لاءِ اوھان کي اوجھراڪي ڍڪايائين ۽ اوھان تي آسمان مان پاڻي ھِن لاءِ وسايائين ته اُن سان اوھان کي پاڪ ڪري ۽ اوھان کان شيطان جي پليتي نئي ۽ اوھان جي دلين کي مُحڪم رکي ۽ ان سان (اوھان جي) قدمن کي مُحڪم ڪري.'),
(1172, 8, 12, '(ياد ڪر) جڏھن تنھنجي پالڻھار ملائڪن ڏانھن وحي ڪيو ته آءٌ اوھان سان آھيان تنھنڪري مؤمنن کي ھمٿايو، ڪافرن کي دل ۾ سگھو دھشت وجھندس پوءِ (اي مؤمنؤ) گردنين تي ڌڪ ھڻونِ ۽ اُنھن جي آڱرين جا سنڌ ڀڃو.'),
(1173, 8, 13, 'اِھو (حُڪم) ھن سببان آھي جو انھن الله ۽ سندس پيغمبر جي مخالفت ڪئي، ۽ جيڪو الله ۽ سندس پيغمبر جي مُخالفت ڪندو ته بيشڪ الله سخت سزا ڏيندڙ آھي.'),
(1174, 8, 14, 'اھا (سزا اٿو) پوءِ ان کي چکو ۽ پڪ ڄاڻو ته ڪافرن لاءِ باھ جو عذاب آھي.'),
(1175, 8, 15, 'اي مؤمنؤ جڏھن (ويڙھ ۾) ڪافرن جي مُچي سان آمھون سامھون ٿيو تڏھن اُنھن کي پٺي ڏئي نه ڦرو.'),
(1176, 8, 16, '۽ اُنھيءَ ڏينھن لڙائيءَ جي کيڏاريءَ يا (پنھنجي لشڪر جي) ڪنھن ٽوليءَ ڏانھن ڀر وٺندڙ کانسواءِ جيڪو انھن کي پٺي ڏئي ڦِرندو سو بيشڪ الله جي ڏمر ھيٺ آيو ۽ سندس جاءِ دوزخ آھي، ۽ اُھا بڇڙي جاءِ آھي.'),
(1177, 8, 17, 'پوءِ کين اوھان نه ڪُٺو پر الله کين ڪٺو آھي، ۽ (اي پيغمبر) جڏھن تو (ڌوڙ جي مُٺ) اُڇلي ته (اُھا) تو نه اُڇلي پر الله اڇلي، ۽ (ھن لاءِ) ته پاڻ وٽان مؤمنن کي (نعمت ڏيڻ سان) چڱي طرح آزمائي، بيشڪ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(1178, 8, 18, 'اِھو (حال) آھي ۽ الله ڪافرن جي مڪر کي سُست ڪندڙ آھي.'),
(1179, 8, 19, '(اي ڪافرؤ) جڏھن (بدر جي لڙائيءَ جي سنبرڻ مھل ڪُفر ۽ اسلام جي سچائي پرکڻ لاءِ) سوڀ گھريوَ ته بيشڪ اوھان جي آڏو (اسلام کي) سوڀ آئي، ۽ جيڪڏھن اوھين (اسلام جي سچائي سمجھي ڪفر کان) بس ڪندؤ ته اھو اوھان لاءِ چڱو آھي ۽ جيڪڏھن ٻيھر (ويڙھ لاءِ) ايندؤ ته وري (اسلام کي فتح) ڏينداسون، ۽ اوھانجو لشڪر جيتوڻيڪ گھڻو ھوندو ته به اوھان کان ڪُجھ ٽاري نه سگھندو ۽ بيشڪ الله مؤمنن سان (ساڻ آھي.'),
(1180, 8, 20, 'اي ايمان وارؤ الله ۽ سندس پيغمبر جي فرمانبرداري ڪريو ۽ اُن کان منھن نه موڙيو حالانڪ اوھين (قُرآن) ٻُڌو ٿا.'),
(1181, 8, 21, '۽ انھن (ڪافرن ۽ منافقن) جھڙا نه ٿيو جن چيو ته ٻُڌوسون ۽ اُھي نه ٿا ٻڌن.'),
(1182, 8, 22, 'الله وٽ (سڀني) جاندارن مان تمام بڇڙا ٻوڙا ۽ گونگا آھن جي (ڪُجھ به) نه سمجھندا آھن.'),
(1183, 8, 23, '۽ جيڪڏھن الله منجھس ڪا چڱائي ڄاڻي ھا ته ضرور کين ٻُڌائي ھا، ۽ جيڪڏھن کين ٻُڌائي ھا ته اُھي مُنھن موڙيندڙ ٿي ڦرن ھا.'),
(1184, 8, 24, 'اي مسلمانؤ الله ۽ پيغمبر جو حُڪم قبول ڪريو جڏھن (پيغمبر) اوھان کي ھن لاءِ سڏي ٿو ته اوھانکي جيئرو ڪري، ۽ ڄاڻو ته الله آدمي جي ۽ سندس دل جي وچ ۾ اوٽ ھوندو آھي ۽ ڏانھس گڏ ڪبؤ.'),
(1185, 8, 25, '۽ اُنھيءَ فتني کان ڊڄو جو اوھان مان جن ظلم ڪيو تن کي خلاصو نه پھچندو (بلڪ عام ھوندو)، ۽ ڄاڻو ته الله سخت سزا ڏيندڙ آھي.'),
(1186, 8, 26, '۽ (اي مھاجرؤ سندس احسانن کي) ياد ڪريو جڏھن زمين (مُلڪ) ۾ اوھين ٿورڙا (۽) ھيڻا ھيؤ ڊنؤ ٿي ته متان ماڻھو اوھان کي تڪڙو کڻي وٺن، پوءِ اوھان کي جاءِ ڏنائين ۽ اوھان کي پنھنجي مدد سان سگھ ڏنائين ۽ اوھان کي سٺين شين مان روزي ڏنائين ته مانَ اوھين شڪرانو ڪريو.'),
(1187, 8, 27, 'اي مؤمنؤ الله ۽ پيغمبر جي خيانت نه ڪريو ۽ اوھين ڄاڻي واڻي پاڻ ۾ (ھڪ ٻئي جي) امانت جي خيانت (به) نه ڪريو.'),
(1188, 8, 28, '۽ ڄاڻو ته اوھان جا مال ۽ اوھان جو اولاد رڳو پَرک آھن ۽ الله ئي وٽ وڏو اَجر آھي.'),
(1189, 8, 29, 'اي ايمان وارؤ جيڪڏھن الله کان ڊڄندا رھندؤ ته اوھان لاءِ (اوھان جي دلين ۾ حق ۽ باطل کي) نکيڙيندڙ (نُور) پيدا ڪندو ۽ اوھانجا گناھ اوھان کان ٽاريندو ۽ اوھان کي بخشيندو، ۽ الله وڏي فضل وارو آھي.'),
(1190, 8, 30, '۽ (اي پيغمبر ياد ڪر) جنھن مھل تولاءِ ڪافرن رٿ ٿي ڪئي ته توکي بند ڪن يا توکي شھيد ڪن يا توکي ڪڍي ڇڏين، ۽ (بڇڙي) رٿ ڪيائون ٿي ۽ الله (به) رٿ ڪئي ٿي، ۽ الله چڱو رٿ ڪندڙن مان آھي.'),
(1191, 8, 31, '۽ جڏھن اسان جون آيتون کين پڙھي ٻڌائجن ٿيون (ته) چوندا آھن ته ٻڌوسون جيڪڏھن گھُرون ته اُنھي جھڙو (اسين به) چئون ته ھيءُ ته رڳو اڳين جون آکاڻيون آھن.'),
(1192, 8, 32, '۽ (ياد ڪريو) جڏھن (ڪافرن) چيو ته اي الله جيڪڏھن ھي (قرآن) تو وٽان حق آھي ته اسان تي آسمان کان پَھڻ وساءِ يا ڪو ڏکوئيندڙ عذاب اسان تي آڻ.'),
(1193, 8, 33, '۽ الله کي نه جڳائيندو آھي جو منجھن تنھنجي ھوندي کين عذاب ڪري، ۽ الله کي (ھي به) نه جڳائيندو آھي جو کين ھن ھوندي عذاب ڪري جو اُھي بخشش گھرندا ھجن.'),
(1194, 8, 34, '۽ کين ڇا (ٿيو) آھي جو الله کين (پيغمبر جي ھجرت کانپوءِ) عذاب نه ڪري ھن ھوندي جو اُھي تعظيم واريءَ مسجد کان (ماڻھن کي) جھلن ٿا ۽ اُن جا مُتولّي نه آھن، پرھيزگارن کانسواءِ ٻيو ڪو اُن جو متولّي نه آھي پر انھن مان گھڻا نه ڄاڻندا آھن.'),
(1195, 8, 35, '۽ سندين نماز الله جي گھر وٽ سيٽين ۽ تاڙين ھڻڻ کانسواءِ (ڪُجھ) نه آھي، پوءِ انھيءَ سببان جو ڪفر ڪندا آھيو عذاب چکو.'),
(1196, 8, 36, '(۽) ڪافر پنھنجا مال ھن لاءِ خرچين ٿا ته (ماڻھن کي) الله جي واٽ کان جھلين، پوءِ اھو سگھو انھن (مالن) کي خرچ ڪندا وري (اھو خرچڻ) مٿن ارمان ٿيندو وري مغلوب ڪيا ويندا، ۽ ڪافر دوزخ ڏانھن گڏ ڪيا ويندا.'),
(1197, 8, 37, 'ته الله پليت کي پاڪ کان ڌار ڪري ۽ پليت کي ھڪ ٻئي تي رکي پوءِ ھيڪاندو ڍير ڪري پوءِ اُن کي دوزخ ۾ وجھي، اِھي ئي ڇيئي وارا آھن.'),
(1198, 8, 38, '(اي پيغمبر) ڪافرن کي چؤ ته جيڪڏھن اوھين مُڙندؤ ته جو ڪُجھ گذريو سو کين بخشبو، ۽ جيڪڏھن ٻيھر ويڙھ ڪندا ته بيشڪ اڳين جي (اھا) عادت ٿي گذري آھي.'),
(1199, 8, 39, '۽ (اي مؤمنؤ) اُنھن سان (ايستائين) لڙائي ڪريو جيسين (ڪفر جو) غلبو نه رھي ۽ سموري حڪومت رڳو الله جي ٿئي (يعني قانونِ الاھي جو عمل ھلي)، پوءِ جيڪڏھن مڙي وڃن ته جيڪي ڪندا آھن سو الله ڏسندڙ آھي.'),
(1200, 8, 40, '۽ جيڪڏھن مُنھن موڙيائون (۽ باز نه آيا) ته ڄاڻو ته الله اوھان جو سنڀاليندڙ آھي، (۽ اُھو) چڱو سنڀاليندڙ ۽ چڱو مددگار آھي.'),
(1201, 8, 41, '۽ (ھيءَ ڳالھ) ڄاڻو ته جيڪو مال (ڪافرن تي فتح لھڻ کانپوءِ) اوھان جي ھٿ ۾ آيو تنھنجو پنجون حصّو الله ۽ پيغمبر ۽ (پيغمبر جي) عزيزن ۽ يتيمن ۽ مسڪينن ۽ مسافرن لاءِ آھي جيڪڏھن اوھان الله کي مڃيو آھي ۽ (پڻ) جيڪو پنھنجي ٻانھي (ﷴ) تي فيصلي جي ڏينھن جنھن ڏينھن ٻئي ٽوليون پاڻ ۾ مقابل ٿيون نازل ڪيوسون (تنھن تي يقين رکندا آھيو ته اُن حُڪم تي عمل ڪريو)، ۽ الله سڀڪنھن شيء تي وسوارو آھي.'),
(1202, 8, 42, '(اِھو اُھو وقت ھو) جنھن مھل اوھين اورين ڀر ۽ ھو پرينءَ ڀر ھوا ۽ قافلو اوھان کان ھيٺين ڀَر (ھليو ويو)، ۽ جيڪڏھن پاڻ ۾ انجام ڪريو ھا ته ضرور انجام ۾ برخلاف ٿيو ھا پر ھِن لاءِ ته جيڪو ڪم الله کي ڪرڻو ھو سو پورو ڪري (ته حق پڌرو ٿئي، ۽) ھن لاءِ ته جيڪو ھلاڪ (گمراہ) ٿئي سو چٽائي کانپوءِ مرته ھلاڪ ٿئي ۽ جيڪو جيئرو ٿئي (يعني حق کي پروڙي) سو چٽائي کانپوءِ مرته جيئرو رھي، ۽ بيشڪ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(1203, 8, 43, '(ياد ڪر) جنھن مھل الله تنھنجي خواب ۾ توکي اُھي (ڪافر) ٿورا ڏيکاريا، ۽ جيڪڏھن توکي اُھي گھڻا ڏيکاري ھا ته ضرور بي ھمّت ٿيو ھا ۽ ضرور (اُن) ڪم ۾ ھڪٻئي سان تڪرار ڪريو ھا پر الله (اُن تڪرار کان اوھان کي) بچايو، بيشڪ اُھو سينن وارو (ڀيد) ڄاڻندڙ آھي.'),
(1204, 8, 44, '۽ (ياد ڪريو) جڏھن اوھين ھڪٻئي جي سامھون ٿيؤ تڏھن (الله) اوھان جي اکين ۾ اُھي ٿورڙا ڪري ڏيکاريا ٿي ۽ انھن (ڪافرن) جي نگاھن ۾ اوھانکي ٿورڙا ڪري ڏيکاريائين ٿي ھن لاءِ ته الله اھو پورو ڪري جيڪو ڪرڻو ھو، ۽ الله ڏانھن (سڀ) ڪم موٽايا ويندا.'),
(1205, 8, 45, 'اي ايمان وارؤ! جڏھن ڪنھن (ڪافر) ٽوليءَ سان (جنگ ۾) مقابلو ڪريو تڏھن ثابت قدم رھو ۽ الله کي گھڻو ياد ڪريو ته مانَ اوھين ڇُٽو.'),
(1206, 8, 46, '۽ الله ۽ سندس پيغمبر جي فرمانبرداري ڪريو ۽ پاڻ ۾ تڪرار نه ڪريو نه ته سست ٿيندؤ ۽ اوھان جي ((اتفاق جي) بوءِ ھلي ويندي ۽ صبر ڪريو، بيشڪ الله صابرن سان آھي.'),
(1207, 8, 47, '۽ اوھين اُنھن (بدر وارن ڪافرن) وانگر نه ٿيو جيڪي پنھنجي ديس مان تڪبر سان ۽ ماڻھن جي ڏيکاري لاءِ نڪتا ۽ (ماڻھن کي) الله جي واٽ کان جھلڻ لڳا، ۽ جيڪي ڪندا آھن تنھن کي الله گھيروڪندڙ آھي.'),
(1208, 8, 48, '۽ جڏھن شيطان سندن ڪرتوت اُنھن لاءِ سينگاريا ۽ (ڪافرن کي) کي چيائين ته اڄ ماڻھن مان ڪوبه اوھان تي غالب (ٿيڻو) نه آھي ۽ بيشڪ آءٌ اوھان جو واھرو آھيان، پوءِ جڏھن ٻئي لشڪر آمھون سامھون ٿيا ته (شيطان) پنھنجين کڙين (ڀرپوءِ) تي ڀڳو ۽ چيائين ته آءٌ اوھان کان بيزار آھيان تحقيق جيڪي مان ڏسان ٿو سو اوھين نٿا ڏسو تحقيق آءٌ الله کان ڊڄان ٿو، ۽ الله سخت سزا ڏيندڙ آھي.'),
(1209, 8, 49, '(اُھو وقت ياد ڪريو) جنھن مھل منافقن ۽ جن جي دلين ۾ بيماري ھئي تن چيو ته ھنن (مسلمانن) کي سندن دين ٺڳيو آھي، ۽ جيڪو الله تي ڀروسو ڪندو آھي ته بيشڪ الله غالب حڪمت وارو آھي.'),
(1210, 8, 50, '(اي ڏسندڙ) جيڪڏھن (اُنھي مھل) ڏسين جنھن وقت ملائڪ ڪافرن جو ساہ ڪڍندا آھن (۽) سندن مُنھن ۽ پٺين کي سٽيندا ڪٽيندا آھن (ته عجب ڪرين) ۽ (چوندا اٿن ته ھاڻي) ساڙيندڙ عذاب (جو مزو) چکو.'),
(1211, 8, 51, 'اِھو ھن سببان آھي جو جيڪي اوھان جي ھٿن اڳي موڪليو آھي ۽ بيشڪ الله ٻانھن تي ظلم ڪندڙ نه آھي.'),
(1212, 8, 52, '(انھن جو حال) فرعونين ۽ انھن جھڙو آھي جيڪي کانئن اڳ ھوا، (جو) الله جي آيتن کي نه مڃيائون پوءِ الله سندن گناھن سببان کين پڪڙيو، بيشڪ الله سگھارو سخت سزا ڏيندڙ آھي.'),
(1213, 8, 53, 'اھو ھن سببان آھي جو الله جا نعمت ڪنھن قوم کي عطا ڪندو آھي تنھن کي (اُھو) ايستائين ڪڏھن نه مٽائيندو آھي جيسين جيڪي سندن دلين ۾ آھي سو (پاڻ نه) مٽائيندا آھن ۽ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(1214, 8, 54, '(انھن جو حال) فرعونين ۽ پڻ جيڪي کانئن اڳ ھوا تن جھڙو آھي، (جو) پنھنجي پالڻھار جي آيتن کي ڪوڙ ڄاتائون تنھنڪري انھن کي سندن گناھن سببان ھلاڪ ڪيوسون ۽ فرعونين کي ٻوڙيوسون، مڙئي ظالم ھوا.'),
(1215, 8, 55, 'جاندارن مان (سڀ کان) بڇڙا الله وٽ ڪافر آھن پوءِ اُھي ايمان نه ٿا آڻين.'),
(1216, 8, 56, 'انھن مان جن سان (صلح جو) انجام ڪيو اٿيئي پوءِ وري ھر ڀيري پنھنجو انجام ڀڃيندا آھن ۽ اُھي نه ڊڄندا آھن.'),
(1217, 8, 57, 'پوءِ جيڪڏھن لڙائيءَ ۾ کين لھين ته (انھن کي اھڙي سزا ڏي جنھن جي ڪري) جيڪي سندن پوئتان ھجن تن کي ڀڄاءِ ته مانَ اھي ياد ڪن.'),
(1218, 8, 58, '۽ جيڪڏھن ڪنھن قوم جي (انجام ڀڃڻ جي) خيانت کان ڊڄين ته اُنھن ڏانھن (سندن انجام) پوري ٿيڻ مھل بيزاريءَ جو اطلاع موڪل، ڇوته الله خيانت ڪندڙن کي دوست نه رکندو آھي.'),
(1219, 8, 59, '۽ ڪافر (ايئن) نه ڀائين ته (ڪو) اڳرائي ڪيائون، بيشڪ اُھي نه ٿڪائيندا.'),
(1220, 8, 60, '۽ انھن لاءِ (لڙائي جي ھٿيارن جي) سگھ مان ۽ گھوڙن جي تيار رکڻ مان جيڪي ٿي سگھيوَ سو الله جي ويرين ۽ پنھنجن ويرين کي دھشت ڏيڻ لاءِ تيار رکو ۽ انھي کانسواءِ ٻي قوم کي (به) کين نه ڄاڻندا آھيو، اُنھن کي الله ڄاڻندو آھي، ۽ جيڪي ڪنھن شيء مان الله جي واٽ ۾ خرچيو سو اوھان کي پورو ڏبو ۽ اوھان کي گھٽ نه ڏبو.'),
(1221, 8, 61, '۽ جيڪڏھن صلح لاءِ لاڙو ڪن ته (تون به) اُن ڏانھن لاڙو ڪر ۽ الله تي ڀروسو ڪر، بيشڪ اُھو ٻڌندڙ ڄاڻندڙ آھي.'),
(1222, 8, 62, '۽ جيڪڏھن توکي دولاب ڏيڻ جو ارادو ڪن ٿا ته بيشڪ تو لاءِ الله ڪافي آھي، (۽) اُھوئي آھي جنھن توکي ۽ مؤمنن کي پنھنجي مدد سان سگھ ڏني.'),
(1223, 8, 63, '۽ اُنھن (مؤمنن) جي دلين ۾ ميلاپ ڏنائين، جيڪي زمين ۾ آھي سو مڙئي جيڪڏھن تون خرچين ھا ته سندين دلين ۾ ميلاپ ڏئي نه سگھين ھا پر الله سندن وچ ۾ ميلاپ ڏنون، بيشڪ اُھو غالب حڪمت وارو آھي.'),
(1224, 8, 64, 'اي پيغمبر توکي ۽ پڻ مؤمنن مان جن تنھنجي تابعداري ڪئي تن کي الله ڪافي آھي.'),
(1225, 8, 65, 'اي پيغمبر مؤمنن کي لڙائيءَ تي ھمتاءِ، جيڪڏھن اوھان مان صبر وارا وِيھ آھن ته ٻن سَون تي غالب ٿيندا، ۽ جيڪڏھن اوھان مان سَو آھن ته ھڪ ھزار ڪافرن تي غالب ٿيندا ھن ڪري جو اُھا اھڙي قوم آھي جا نه سمجھندي آھي.'),
(1226, 8, 66, 'ھاڻي الله اوھان تان بار ھلڪو ڪيو ۽ اوھان ۾ ھيڻائي معلوم ڪيائين، پوءِ جيڪڏھن اوھان مان صبر وارا سَو ھوندا ته ٻن سون تي غالب ٿيندا، ۽ جيڪڏھن اوھان مان ھزار ھوندا ته ٻن ھزارن تي الله جي حڪم سان غالب ٿيندا، ۽ الله صابرن ساڻ آھي.'),
(1227, 8, 67, 'پيغمبر کي نه جڳائيندو آھي جو وٽس قيدي ھجن (بلڪ ڪُٺا وڃن) جيستائين مُلڪ ۾ (ڪافرن جي عام) خونريزي نه ڪري، (اوھين) دنيا جو سامان گھرندا آھيو، ۽ الله آخرت (جو ثواب ڏيڻ) گھرندو آھي، ۽ الله غالب حِڪمت وارو آھي.'),
(1228, 8, 68, 'جيڪڏھن خدا جي تقدير ۾ اڳي لکيل نه ھجي ھا ته اوھان جيڪي (فديو) ورتو تنھن ۾ اوھان کي ضرور وڏو عذاب پھچي ھا.'),
(1229, 8, 69, 'پوءِ جيڪي اوھان مال غنيمت ورتو سو پاڪ حلال (ڀانيو ۽) کائو، ۽ الله کان ڊڄو، بيشڪ الله بخشڻھار مھربان آھي.'),
(1230, 8, 70, 'اي پيغمبر، قيدين مان جيڪي اوھان جي ھٿن ۾ آھن تن کي چؤ ته جيڪڏھن الله اوھان جي دلين ۾ ڪا چڱائي ڄاتي ته جيڪي اوھان کان ورتو ويو تنھن کان چڱو اوھان کي ڏيندو ۽ اوھان کي بخشيندو، ۽ الله بخشڻھار مھربان آھي.'),
(1231, 8, 71, '۽ جيڪڏھن توسان خيانت ڪرڻ گھرندا ته بيشڪ الله سان (به) اڳ خيانت ڪئي ھوائون پوءِ (اوھان کي) انھن تي سگھارو ڪيائين، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(1232, 8, 72, 'جن ايمان آندو ۽ حجرت ڪئي ۽ پنھنجن مالن ۽ پنھنجن جندن سان الله جي واٽ ۾ جھاد ڪيو ۽ جن جاءِ ڏني ۽ مدد ڪئي اھي ھڪ ٻئي جا دوست آھن، ۽ جن ايمان آندو ۽ ھجرت نه ڪئي اوھان تي انھن جي دوستيءَ جو ڪو حق نه آھي جيسين ھجرت ڪن، ۽ جيڪڏھن دين (جي ڪم) ۾ اوھان کان مدد گھُرن ته اوھان کي انھيءَ قوم کانسواءِ ٻي قوم تي مدد ڏيڻ لازم آھي جن جو اوھانجي ۽ ھنن جي وچ ۾ انجام ٿيل ھجي، ۽ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.'),
(1233, 8, 73, '۽ جيڪي ڪافر آھن تن مان ھڪڙا ٻين جا سنگتي آھن، پر (اي مسلمانؤ) جيڪڏھن اوھين (ائين) نه ڪندؤ ته مُلڪ ۾ فتنو ۽ وڏو فساد ٿيندو.'),
(1234, 8, 74, '۽ جن ايمان آندو ۽ ھجرت ڪئي ۽ الله جي واٽ ۾ جھاد ڪيو ۽ جن جاءِ ڏني ۽ مدد ڪئي اِھي ئي سچا مؤمن آھن، اُنھن لاءِ بخشش ۽ سڳوري روزي آھي.'),
(1235, 8, 75, '۽ جن (انھن کان) پوءِ ايمان آندو ۽ ھِجرت ڪئي ۽ اوھان سان گڏ جھاد ڪيو سي به اوھان مان آھن، ۽ مائٽيءَ وارا الله جي حُڪم موجب (پاڻ ۾) ھڪ ٻئي جا وڌيڪ حقدار آھن، ڇوته الله سڀڪنھن شيء کي ڄاڻندڙ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'جن مشرڪن سان اوھان انجام ڪيو تن ڏانھن الله ۽ سندس پيغمبر جي پاران (انجام کان) بيزاري آھي.'),
(1237, 9, 2, '(اي مشرڪؤ) مُلڪ ۾ چار مھينا سير ڪريو ۽ ڄاڻو ته اوھين الله کي ٿَڪائڻ وارا نه آھيو ۽ (ڄاڻو) ته الله ڪافرن کي خراب ڪرڻ وارو آھي.'),
(1238, 9, 3, '۽ وڏي حَج جي ڏينھن الله ۽ سندس پيغمبر جي طرفان ماڻھن ڏانھن پڙھو آھي ته الله ۽ سندس پيغمبر مشرڪن کان بيزار آھي، پوءِ (اي مشرڪؤ) جيڪڏھن (شرڪ کان) توبه ڪندؤ ته اُھا اوھان لاءِ ڀلي آھي، ۽ جيڪڏھن اوھين مُنھن ڦيرائيندؤ ته ڄاڻو ته اوھين الله کي ٿَڪائڻ وارا نه آھيو، ۽ (اي پيغمبر) ڪافرن کي ڏکوئيندڙ عذاب جي خبر ڏي.'),
(1239, 9, 4, 'پر مشرڪن مان جن انجام ڪيو ھيوَ وري (انھن) اوھان سان (انجام ۾) ڪُجھ قُصور نه ڪيو ۽ نڪي اوھان جي خلاف ڪنھن کي مدد ڏنائون تن جو انجام سندين مدت تائين ساڻن پورو ڪريو، ڇوته الله پرھيزگارن کي دوست رکندو آھي.'),
(1240, 9, 5, 'پوءِ (جن مھينن ۾ لڙائي) حرام (ڪئي ويئي آھي سي) مھينا جڏھن پورا ٿين ته (عرب جي) مشرڪن کي جتي لھو تتي انھن کي ماريو ۽ انھن کي پڪڙيو ۽ انھن کي بند رکو ۽ سڀني گھَٽن تي انھن لاءِ ويھو، پوءِ جيڪڏھن توبه ڪن ۽ نماز پڙھن ۽ زڪوٰة ڏين ته سندن واٽ بند نه ڪريو، ڇوته الله بخشڻھار مھربان آھي.'),
(1241, 9, 6, '۽ جيڪڏھن مشرڪن مان ڪو ھڪڙو توکان امانَ گھُري ته کيس امانَ ڏي جيستائين (ھو) ڪلام الله ٻڌي پوءِ کيس سندس امن جي ھنڌ پھچاءِ، اِھو ھن ڪري آھي جو اُھا قوم اھڙي آھي جو نه ڄاڻندي آھي.'),
(1242, 9, 7, 'الله وٽ ۽ سندس پيغمبر وٽ مشرڪن لاءِ اَنجام ڪئن ٿيندو سواءِ انھن جي جن سان تعظيم واريءَ مسجد وٽ انجام ڪيو ھُيوَ، پوءِ اُھي جيستائين اوھان جي انجام تي بيھڪ ڪن ته اوھين انھن لاءِ بيھڪ وٺو، بيشڪ الله پرھيزگارن کي دوست رکندو آھي.'),
(1243, 9, 8, 'ڪئن (سندن انجام رھندو) ۽ جيڪڏھن اوھان تي غالب ٿين ته اوھان بابت نڪي مائٽي جو ۽ نڪي انجام جو خيال رکن، اوھان کي پنھنجين (مِٺين) زبانن سان راضي ڪندا آھن ۽ سندن دليون انڪاري آھن، ۽ منجھانئن گھڻا بي دين آھن.'),
(1244, 9, 9, 'الله جي آيتن کي ٿوري مُلھ تي وڪيائون پوءِ (ماڻھن کي) سندس واٽ کان جھليائون، اُھي جيڪي ڪندا آھن سو بڇڙو آھي.'),
(1245, 9, 10, 'ڪنھن مؤمن بابت نڪي مائٽي ۽ نڪي انجام جو خيال رکندا آھن، ۽ اھي ئي حد کان لنگھيل آھن.'),
(1246, 9, 11, 'پوءِ جيڪڏھن (ڪفر کان) توبه ڪن ۽ نماز پڙھن ۽ زڪوٰة ڏين ته اوھان جا دين ۾ ڀائر آھن، ۽ جيڪي ماڻھو ڄاڻندا آھن تن لاءِ نشانيون کولي بيان ڪريون ٿا.'),
(1247, 9, 12, '۽ جيڪڏھن پنھنجن انجامن ٻَڌڻ کانپوءِ پنھنجا قسم ڀڃن ۽ اوھانجي دين ۾ طعنو ھڻن ته ڪفر جي سرڪردن سان جنگ ڪريو ڇو ته اُنھن جو قسم (اعتبار جوڳو) نه آھي مان اُھي جھلجن.'),
(1248, 9, 13, 'اھڙي قوم سان ڇونه ٿا وڙھو جن پنھنجن قسمن کي ڀڳو ۽ جي پيغمبر جي ڪڍي ڇڏڻ لاءِ سنبريا ۽ منڍئون اوھان سان (دشمني) اوّل ۾ ڪرڻ لڳا؟ کائن ڇو ٿا ڊڄو؟ جيڪڏھن مؤمن آھيو ته الله (ھن لاءِ) وڌيڪ حقدار آھي ته کانئس ڊڄو.'),
(1249, 9, 14, 'انھن سان وڙھو ته الله اوھان جي ھٿان کين سزا ڏيندو ۽ کين خوار ڪندو ۽ اوھانکي مٿن سوڀ ڏيندو ۽ مؤمنن جي قوم جي سينن کي شفا بخشيندو (ٺاريندو).'),
(1250, 9, 15, '۽ سندين دلين جي ڪاوڙ وڃائيندو، ۽ الله جنھن لاءِ گھرندو تنھن تي ٻاجھ سان موٽندو، ۽ الله ڄاڻندڙ حِڪمت وارو آھي.'),
(1251, 9, 16, 'ڀائيندا آھيو ڇا ته اوھان کي (جھاد جي تڪليف کان) ڇڏيو ويندو؟ حقيقت ڪري اوھان مان جن جھاد ڪيو ۽ نڪي الله ۽ نڪي پيغمبر ۽ نڪي مؤمنن کانسواءِ (ٻيو) ڪو ڳجھو دوست ورتو تن کي الله اڃا نه نکيڙيو آھي، ۽ جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.'),
(1252, 9, 17, 'پاڻ تي ڪفر جو اقراري ٿي مشرڪن کي الله جي مسجدين جو آباد ڪرڻ روا نه آھي، اِھي اُھي آھن جن جا عمل چٽ ٿي ويا، ۽ اُھي باھ ۾ سدائين رھڻ وارا آھن.'),
(1253, 9, 18, 'الله جي مسجدين کي آباد ڪرڻ رڳو انھي کي جڳائيندو آھي جنھن الله ۽ قيامت جي ڏينھن کي مڃيو ۽ نماز پڙھي ۽ زڪوٰة ڏني ۽ الله کانسواءِ (ٻئي ڪنھن کان) نه ڊنو، پوءِ اُميد آھي ته اُھي ھدايت وارن مان آھن.'),
(1254, 9, 19, 'اوھان حاجين کي پاڻي پيارڻ ۽ تعظيم واري مسجد کي آباد رکڻ اُن (جي عملن) جھڙو ڪيو آھي ڇا جنھن الله ۽ قيامت جي ڏينھن کي مڃيو ۽ الله جي واٽ ۾ جھاد ڪيو، الله وٽ (ھي ۽ اُھو) برابر نه آھن، الله ظالم قوم کي ھدايت نه ڪندو آھي.'),
(1255, 9, 20, 'جن مڃيو ۽ ھجرت ڪئي ۽ الله جي واٽ ۾ پنھنجين مالن ۽ پنھنجين جانين سان جھاد ڪيو سي الله وٽ مرتبي ۾ بلڪل وڏا آھن، ۽ اُھي مُراد ماڻڻ وارا آھن.'),
(1256, 9, 21, 'انھن کي سندن پالڻھار پاڻ وٽان ٻاجھ ۽ راضپي ۽ انھن باغن جي خوشخبري ڏئي ٿو جن ۾ انھن لاءِ سدائين نعمتون آھن.'),
(1257, 9, 22, 'منجھس سدائين رھڻ وارا آھن، ڇوته الله ئي وٽ وڏو اجر آھي.'),
(1258, 9, 23, 'اي مؤمنؤ پنھنجن پئرن ۽ پنھنجن ڀائرن کي دوست نه وٺو جيڪڏھن (اُھي) ڪفر کي ايمان کان پيارو رکن (ته)، ۽ جيڪي اوھان مان انھن سان دوستي ڪندا سي ئي ظالم آھن.'),
(1259, 9, 24, '(اي پيغمبر) چؤ ته جيڪڏھن اوھان جا پيئر ۽ اوھان جو اولاد ۽ اوھان جا ڀائر ۽ اوھان جون زالون ۽ اوھان جا مائٽ ۽ اُھي مال جي اوھان ڪمايا آھن ۽ واپار جنھن جي بند ٿيڻ کان ڊڄو ٿا ۽ جايون جن کي پسند ڪريو ٿا سي اوھان کي الله ۽ سندس پيغمبر ۽ سندس واٽ ۾ جھاد ڪرڻ کان وڌيڪ پيارا آھن ته (ايسين) ترسو جيسين الله پنھنجو عذاب آڻي، ۽ الله بي دين قوم کي (سڌو) رستو نه ڏيکاريندو آھي.'),
(1260, 9, 25, 'بيشڪ الله اوھان کي گھڻن ھنڌن ۾ سوڀ ڏني ۽ (پڻ جنگ) حُنين جي ڏينھن جو جڏھن اوھان جي گھڻائي اوھانکي عجب ۾ وڌو ۽ اوھان کان اوھان جي گھڻائي ڪجھ به ٽاري نه سگھي ۽ زمين پنھنجي ويڪرائي ھوندي (به) اوھان تي سوڙھي ٿي ويئي پوءِ اوھين پُٺيرا ٿي ڦريؤ.'),
(1261, 9, 26, 'موٽي الله پنھنجي پار کان آرام پنھنجي پيغمبر تي ۽ مؤمنن تي لاٿو ۽ (ملائڪن جو) لشڪر لاٿائين جن کي نه ٿي ڏٺوَ، ۽ ڪافرن کي سزا ڏنائين، ۽ اِھا ڪافرن جي سزا آھي.'),
(1262, 9, 27, 'موٽي اُن کانپوءِ الله جنھن لاءِ وڻيس تنھن تي ٻاجھ سان موٽندو، ۽ الله بخشڻھار مھربان آھي.'),
(1263, 9, 28, 'اي مؤمنؤ مشرڪ رڳو پليت آھن تنھنڪري ھن ورہ پڄاڻا تعظيم واريءَ مسجد جي ويجھو نه وڃن، ۽ جيڪڏھن (اوھين) سڃائيءَ کان ڊڄندا آھيو ته جيڪڏھن الله گھريو ته پنھنجي فضل سان اوھانکي سگھوئي آسودو ڪندو، ڇوته الله ڄاڻندڙ حڪمت وارو آھي.'),
(1264, 9, 29, 'جن کي ڪتاب ڏنو ويو تن مان جيڪي نڪي الله کي ۽ نڪي قيامت جي ڏينھن کي مڃيندا آھن ۽ نڪي الله ۽ سندس پيغمبر جي حرام ڪيل شيء کي حرام ڪري ڄاڻندا آھن ۽ نڪي سچو دين قبول ڪندا آھن تن سان ايستائين وڙھو جيسين (اُھي) رعيتي پنھنجي ھٿان جزيو ڏين.'),
(1265, 9, 30, '۽ يَھُودين چيو ته عُزير الله جو پُٽ آھي ۽ نصارن چيو ته عيسىٰ الله جو پُٽ آھي، اِھو سندن واتن سان آھي، اڳين ڪافرن جي چوڻ جھڙي رِيس ڪندا آھن، الله مٿن لعنت ڪري، ڪيڏي جو ڪيڏي ڀُلايا ويندا آھن.'),
(1266, 9, 31, '(۽) پنھنجن عالمن ۽ پنھنجن فقيرن ۽ عيسىٰ پُٽ مريم جي کي الله کانسواءِ پالڻھار ڪري ورتائون، ۽ ھڪ الله جي عبادت ڪرڻ کانسواءِ کين ٻيو حڪم نه ڪيو ويو آھي، اُن کانسواءِ ڪو عبادت جو لائق نه آھي، جيڪي ساڻس شريڪ ڪندا آھن تنھن کان ھو پاڪ آھي.'),
(1267, 9, 32, '(۽) پنھنجن واتن سان الله جي نُور کي وسائڻ گھرندا آھن ۽ الله پنھنجي نُور کي پوري ڪرڻ کانسواءِ نه رھندو جيتوڻيڪ ڪافر ارھا ٿين.'),
(1268, 9, 33, 'اُھو (الله) اھي جنھن پنھنجي پيغمبر کي ھدايت ۽ سچي دين سان ھن لاءِ موڪليو ته ان کي سڀني دينن تي غالب ڪري جيتوڻيڪ مشرڪ رنج ٿين.'),
(1269, 9, 34, 'اي ايمان وارؤ ڪتاب وارن عالمن ۽ فقيرن مان گھڻا ماڻھن جو مال ٺڳيءَ سان کائيندا آھن ۽ الله جي واٽ کان (ماڻھن کي) جھليندا آھن، ۽ جيڪي سون ۽ رپي جو خزانو گڏ ڪندا آھن ۽ اُھو الله جي واٽ ۾ نه خرچيندا آھن تن کي ڏکوئيندڙ عذاب جي خبر ڏي.'),
(1270, 9, 35, 'جنھن ڏينھن (سندن مال) دوزخ جي باھ ۾ تپائبو (جڏھن تپندو ته) پوءِ اُن سان سندن پيشانيون ۽ سندن پاسريون ۽ سندن پٺيون ڏنڀبيون، (چئبن ته) ھيءُ اُھو (خزانو) آھي جيڪو پاڻ لاءِ گڏ ڪيو ھيوَ (ان جو) مزو چکو.'),
(1271, 9, 36, 'بيشڪ مھينن جو ڳاڻيٽو يقيناً ڪتاب الاھي ۾ الله وٽ ٻارھن مھينا آھن جنھن ڏينھن کان آسمانن ۽ زمين کي بڻايائين منجھانئس چار (مھينا) تعظيم وارا آھن، اِھو پڪو دين آھي، تنھنڪري اوھين پاڻ کي منجھن نقصان نه لايو، ۽ سڀني مشرڪن سان وڙھو جھڙي طرح اوھان سان سڀيئي وڙھن ٿا، ۽ ڄاڻو ته الله پرھيزگارن سان گڏ آھي.'),
(1272, 9, 37, 'تعظيم وارن مھينن جو اڳي پوءِ ڪرڻ رڳو ڪفر ۾ واڌارو آھي اُن سان ڪافر ڀُلايا ويندا آھن اُن (مھيني) کي ھڪ سال حلال ڳڻين ٿا ۽ ٻئي سال اُن کي حرام ڀائين ٿا ھن لاءِ ته الله جي حرام ڪيلن مھينن جي ڳاڻيٽي جي پورائي ڪن پوءِ جن کي الله حرام ڪيو آھي تن کي حلال ٿا ڪن، اُنھن لاءِ سندن بڇڙا ڪرتوت سينگاريا ويا، ۽ الله ڪافر ٽوليءَ کي ھدايت نه ڪندو آھي.'),
(1273, 9, 38, 'اي ايمان وارؤ اوھان کي ڇا (ٿيو) آھي جو جڏھن اوھان کي چئبو آھي ته الله جي واٽ ۾ (جھاد لاءِ) نڪرو ته زمين ڏانھن ڳرا ٿي ڪرندا آھيو، آخرت جي بدران دنيا جي حياتي سان راضي ٿيا آھيو، دنيا جي حياتي جو سامان آخرت (جي مقابلي) ۾ ٿورڙي کانسواءِ نه آھي.'),
(1274, 9, 39, 'جيڪڏھن نه نڪرندؤ ته اوھان کي ڏکوئيندڙ عذاب جي سزا ڏيندو، ۽ اوھان کانسواءِ ٻي قوم مٽائي آڻيندو ۽ اوھين کيس ڪُجھ ضرر نه رسائيندؤ، ۽ الله سڀڪنھن شيء تي وس وارو آھي.'),
(1275, 9, 40, 'جيڪڏھن پيغمبر کي مدد نه ڏيندؤ ته بيشڪ کيس الله مدد ڏني آھي جنھن مھل ڪافرن (مڪي مان) کيس ڪڍي ڇڏيو ٻن مان ھڪڙو پاڻ ھو جڏھن اُھي غار ۾ ھوا تڏھن پنھنجي سنگتيءَ کي چيائين ته اُلڪو نه ڪر تحقيق الله اسان سان (گڏ) آھي، پوءِ الله مٿس پنھنجو آرام لاٿو ۽ کيس اھڙي لشڪر سان مدد ڪيائين جو اُن کي اوھان نه ڏٺو ۽ ڪافرن جي ڳالھ کي ھيٺ ڪيائين، الله جي ڳالھ (ھميشه) مٿاھين آھي، ۽ الله غالب حڪمت وارو آھي.'),
(1276, 9, 41, '(ھر حال جنگ جي) ٿوري سامان ۽ گھڻي سامان سوڌا (جھاد لاءِ) نڪرو ۽ پنھنجن مالن ۽ پنھنجين جِندن سان الله جي واٽ ۾ جھاد ڪريو، اھو اوھان لاءِ ڀلو آھي جيڪڏھن ڄاڻندا آھيو.'),
(1277, 9, 42, 'جيڪڏھن سامان سولو ۽ سفر ھلڪو ھجي ھا ته ضرور تنھنجي پٺيان لڳن ھا پر مٿن پنڌ پري ٿيو، ۽ الله جو قسم سگھوئي کڻندا (چوندا ته) جيڪڏھن اسين توفيق ساريون ھا ته اوھان سان گڏ نڪرون ھا، پاڻ کي (منافقيءَ سببان) ھلاڪ ڪن ٿا، ۽ الله ڄاڻي ٿو ته اُھي ڪوڙا آھن.'),
(1278, 9, 43, 'الله توکي معاف ڪيو، کين تو ڇالاءِ موڪل ڏني جيسين توکي سچا پڌرا ٿين ھا ۽ (تون) ڪوڙن کي ڄاڻين ھا.'),
(1279, 9, 44, 'جن الله ۽ قيامت جي ڏينھن کي مڃيو آھي سي پنھنجن مالن ۽ پنھنجين جِندن سان جھاد ڪرڻ ۾ توکان موڪل نٿا گھرن، ۽ الله پرھيزگارن کي ڄاڻندڙ آھي.'),
(1280, 9, 45, 'انھن کان سواءِ ٻيو ڪو توکان موڪل نه گھرندو جيڪي الله ۽ قيامت جي ڏينھن کي نه مڃيندا آھن ۽ سندن دلين ۾ شڪ پيل آھن پوءِ اُھي پنھنجي شڪ ۾ حيران آھن.'),
(1281, 9, 46, '۽ جيڪڏھن ٻاھر نڪرڻ گھرن ھا ته ان لاءِ تياريءَ جو سامان تيار ڪن ھا پر الله سندن (جھاد تي) وڃڻ پسند نه ڪيو تنھنڪري کين توفيق نه ڏنائين ۽ چيو ويونِ ته وھندڙن سان گڏجي وِھو.'),
(1282, 9, 47, 'جيڪڏھن اوھان ۾ (گڏجي) نڪرن ھا ته اوھان ۾ شرارت کانسواءِ ٻيو ڪُجھ نه وڌائين ھا ۽ ضرور اوھانجو ٻگيٿرو گھرندڙ ٿي اوھان جي وچ ۾ فساد وجھڻ لاءِ ڊوڙن ھا، ۽ اوھان ۾ اُنھن جا (ڪي) جاسوس آھن، ۽ الله ظالمن کي ڄاڻندڙ آھي.'),
(1283, 9, 48, 'اُھي اڳ بيشڪ فساد جي فڪر ۾ ھوا ۽ تولاءِ بڇڙيون صلاحون ڪندا رھيا ايتري تائين جو مدد آئي ۽ الله جو حُڪم غالب ٿيو ۽ اُھي ناراض ھوا.'),
(1284, 9, 49, '۽ منجھائن ڪو آھي جو چوي ٿو ته مون کي موڪل ڏي ۽ مونکي فتني ۾ نه وجھ، خبردار (اُھي پاڻ) فتني ۾ پيل آھن، ۽ بيشڪ دوزخ ڪافرن کي گھيرو ڪندڙ آھي.'),
(1285, 9, 50, '(اي پيغمبر) جيڪڏھن توکي ڪو سھنج پھچندو آھي ته کين ڏکيو لڳندو آھي، ۽ جيڪڏھن ڪو توکي اھنج پھچندو آھي ته چوندا آھن ته بيشڪ پنھنجو ڪم اڳيئي سنڀالي ورتو اٿؤن ۽ اُھي سرھا ٿي ڦرندا آھن.'),
(1286, 9, 51, '(اي پيغمبر) چؤ ته جيڪي الله اسان لاءِ لکيو آھي تنھن کانسواءِ ٻيو ڪي اسانکي ڪڏھن نه پھچندو، اُھو اسانجو سنڀاليندڙ آھي، ۽ مؤمنن کي رڳو الله تي ڀروسو ڪرڻ جڳائي.'),
(1287, 9, 52, '(اي پيغمبر منافقن کي) چؤ ته اوھين اسان لاءِ ٻن چڱاين مان ھڪڙي کانسواءِ انتظار نه ڪڍندا آھيو، ۽ اسين (به) اوھان لاءِ انتظار ڪڍندا آھيون ته الله پاڻ وٽان يا اسان جي ھٿان اوھان کي سزا پھچائي، پوءِ اوھين انتظار ڪڍو اسين (به) اوھان سان گڏ منتظر آھيون.'),
(1288, 9, 53, 'چؤ (اي منافقو) سَرھا ٿي توڻي ارھا ٿي خرچ ڪريو ته اوھان کان ڪڏھن قبول نه ڪبو، ڇوته اوھين بي دين ٽولي آھيو.'),
(1289, 9, 54, '۽ کانئن سندن خرچ جو قبول ڪرڻ ھن کانسواءِ نه جھليو ويو ته اُھي الله ۽ سندس پيغمبر جا مُنڪر آھن ۽ نڪي آرس ڪرڻ کان سواءِ نماز پڙھندا آھن ۽ نڪي (الله جي واٽ ۾) رنج کانسواءِ خرچيندا آھن.'),
(1290, 9, 55, 'پوءِ توکي نڪي سندن مال ۽ نڪي سندن اولاد عجب ۾ وجھي، الله رڳو گھرندو آھي ته دنيا جي حياتي ۾ اُن سان کين عذاب ڪري ۽ سندن ساہ ڪُفر جي ئي حالت ۾ نڪرن.'),
(1291, 9, 56, '۽ الله جو قسم کڻندا آھن ته اُھي اوھان منجھان آھن، ۽ (حقيقت ڪري) اُھي اوھان منجھان نه آھن پر اُھا ڊڄڻي قوم آھي.'),
(1292, 9, 57, 'جي ڪا پناھ جي جاءِ يا ڪي چُرون يا ڪو گھڙڻ جو ھنڌ لھن ھا ته اُن ڏانھن اُھي ڊوڙندا ئي ڦِرن ھا.'),
(1293, 9, 58, '۽ منجھائن ڪي اھڙا آھن جي صدقن (جي وراھڻ) ۾ طعنو ھڻندا آھن، پوءِ جيڪڏھن منجھانئس ڏجين ته سَرھا ٿيندا آھن ۽ جيڪڏھن منجھانئس نه ڏجين ته انھي ئي مھل ڪاوڙبا آھن.'),
(1294, 9, 59, '۽ الله ۽ سندس پيغمبر جيڪي کين ڏنو آھي تنھن تي جيڪڏھن راضي ٿين ھا ۽ چون ھا ته اسان کي الله ڪافي آھي اسان کي الله پنھنجي فضل سان ڏيندو ۽ سندس پيغمبر (به ڏيندو) اسين الله ڏانھن اميدوار آھيون (ته اُنھن لاءِ ڀلو ھجي ھا).'),
(1295, 9, 60, 'صدقا رڳو فقيرن ۽ مسڪينن ۽ ان جي اُڳاڙي ڪندڙن ۽ جن جون دليون (اسلام ڏانھن) ميلاپ ڪرايل آھن (تن لاءِ) ۽ ٻانھن جي آجي ڪرڻ ۽ قرضين ۽ الله جي واٽ ۾ (جھاد ڪرڻ) ۽ مسافرن لاءِ آھن، (اِھو) الله (جي پار) کان حُڪم ٿيل آھي، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(1296, 9, 61, '۽ منجھائن ڪي اھڙا آھن جي پيغمبر کي ايذائيندا آھن ۽ چوندا آھن ته پر ٻڌڙيو آھي، چؤ ته اوھان لاءِ پر ٻڌڙيو ڀلو آھي الله کي مڃيندو آھي ۽ مؤمنن (جي صلاح) کي (به) مڃيندو آھي ۽ اوھان مان ايمان وارن لاءِ ٻاجھارو آھي، ۽ جيڪي الله جي پيغمبر کي ايذائيندا آھن تن لاءِ ڏکوئيندڙ عذاب آھي.'),
(1297, 9, 62, 'اوھان لاءِ الله جو قسم کڻندا آھن ته اوھان کي راضي ڪن، ۽ جيڪڏھن مؤمن آھن ته الله ۽ سندس پيغمبر وڌيڪ حقدار آھن جو اُن کي راضي ڪن.'),
(1298, 9, 63, 'نه ڄاڻندا آھن ڇا ته جيڪي الله ۽ سندس پيغمبر جي مخالفت ڪندو تنھن لاءِ بيشڪ دوزخ جي باھ آھ؟ منجھس سدائين رھڻ وارو آھي، اِھا وڏي خواري آھي.'),
(1299, 9, 64, 'منافق مسلمانن تي ڪنھن سورة جي لھڻ کان ڊڄن ٿا ته جيڪي سندن دلين ۾ آھي سو متان مسلمانن کي ڄاڻائي، (اي پيغمبر کين) چؤ ته اوھين پيا ٺـٺولي ڪريو، ڇوته جنھن کان ڊڄندا آھيو سو الله پڌرو ڪندڙ آھي.'),
(1300, 9, 65, '۽ (اي پيغمبر) جيڪڏھن کائن پڇين ته ضرور چوندا ته بيشڪ کِل ۽ راند ٿي ڪئي سون، (کين) چؤ ته الله ۽ سندس آيتن ۽ سندس پيغمبر سان مسخري ڪيوَ ٿي ڇا؟'),
(1301, 9, 66, 'بھانو نه ڪريو بيشڪ ايمان کانپوءِ ڪُفر اٿوَ، جيڪڏھن اوھان مان ھڪ ٽوليءَ کان ٽرنداسون ته ٻيءَ ٽوليءَ کي ھن سببان سزا ڏينداسون جو اُھي ڏوھي ھوا.'),
(1302, 9, 67, 'منافق ۽ منافقياڻيون ھڪ ٻئي جھڙا آھن، (اُھي) مَدن ڪمن جو حُڪم ڪندا آھن ۽ چڱن ڪمن کان جھليندا آھن ۽ پنھنجا ھٿ (خيرات ڏيڻ کان) بند ڪندا آھن، الله کي وساريائون تنھنڪري الله (به) کين وساريو، بيشڪ منافق بي دين آھن.'),
(1303, 9, 68, 'الله منافقن ۽ منافقياڻين ۽ ڪافرن کي دوزخ جي باھ جو انجام ڏنو آھي منجھس سدائين رھڻ وارا آھن، اھا کين بس آھي، ۽ الله مٿن لعنت ڪئي آھي، ۽ انھيءَ لاءِ ھميشه جو عذاب آھي.'),
(1304, 9, 69, 'اوھين (به) اھڙا ٿيا آھيو جيڪي اوھان کان اڳ ھوا (اُھي) اوھان کان سگھ ۽ مال ۽ اولاد ۾ گھڻو وڌيڪ ھوا، پوءِ پنھنجي نصيب آھر فائدو ورتائون پوءِ اوھان به پنھنجي نصيب آھر فائدو ورتو جھڙي طرح اوھان کان اڳين پنھنجي نصيب آھر فائدو ورتو ۽ (اوھين به) اُنھن وانگر اجاين بحثن ۾ پئجي ويا آھيو جئن اُھي اجاين بحثن ۾ پيا، انھن جي ڪمائي دنيا ۽ آخرت ۾ ناس ٿي، ۽ اُھي ئي ڇيئي وارا آھن.'),
(1305, 9, 70, 'کانئن اڳين جي خبر نه پھتي اٿن ڇا جي نُوح ۽ عاد ۽ ثمود جي قوم، ۽ ابراھيم جي قوم ۽ مَدين وارا ۽ اونڌن ٿيلن ڳوٺن وارا ھوا، جو اُنھن ڏانھن سندن پيغمبر پڌرن مُعجزن سان آيا (ته نه مڃيائون)، پوءِ الله جو شان نه ھو جو مٿن ظلم ڪري پر پاڻ تي ظُلم ڪيائون ٿي.'),
(1306, 9, 71, '۽ مؤمن ۽ مؤمنياڻيون (پاڻ ۾) ھڪ ٻئي جا مددگار آھن، چڱن ڪمن جو حُڪم ڪندا آھن ۽ خراب ڪمن کان جھليندا آھن ۽ نماز کي قائم ڪندا آھن ۽ زڪوٰة ڏيندا آھن ۽ الله ۽ سندس پيغمبر جي فرمانبرداري ڪندا آھن، اُنھن تي الله سگھوئي ٻاجھ ڪندو، تحقيق الله غالب حڪمت وارو آھي.'),
(1307, 9, 72, 'الله مؤمنن ۽ مؤمنياڻين سان بھشت جو انجام ڪيو آھي جنھن جي ھيٺان واھيون آھن منجھس سدائين رھڻ وارا آھن ۽ (پڻ) ھميشه رھڻ وارن باغن ۾ چڱين جاين جو (انجام اٿن)، ۽ الله جو راضپو (سڀ کان) تمام وڏو آھي، اھا وڏي مراد ماڻڻ آھي.'),
(1308, 9, 73, 'اي پيغمبر ڪافرن ۽ منافقن سان جھاد ڪر ۽ مٿن سختي ڪر، ۽ سندن جاءِ دوزخ آھي، ۽ اُھا جاءِ بُڇڙي آھي.'),
(1309, 9, 74, 'الله جو قسم کڻندا آھن ته (پاڻ) نه چيو اٿن، ۽ بيشڪ ڪفر جو ڪلمو چيو اٿن ۽ پنھنجي اسلام (آڻڻ) کانپوءِ ڪُفر ڪيائون ۽ جنھن کي پھچي نه سگھيا تنھن لاءِ زور لاتائون، ۽ اِھو اُنھن رڳو ھن ڳالھ جو بدلو ڏنو جو الله پنھنجي فضل سان ۽ سندس رَسُول کين شاھوڪار ڪيو، ۽ پوءِ جيڪڏھن توبه ڪن ته انھن لاءِ ڀلي آھي، ۽ جيڪڏھن ڦرندا ته الله دُنيا ۽ آخرت ۾ کين ڏکوئيندڙ عذاب سان سزا ڏيندو، ۽ مُلڪ ۾ انھن لاءِ نڪو سنڀاليندڙ ۽ نڪو مددگار آھي.'),
(1310, 9, 75, '۽ منجھائن ڪي آھن جن الله سان انجام ڪيو آھي ته جيڪڏھن پنھنجي فضل سان اسان کي ڏنائين ته ضرور خيرات ڏينداسون ۽ ڀلارن مان ٿينداسون.'),
(1311, 9, 76, 'پوءِ جنھن مھل پنھنجي فضل سان کين مال ڏنائين ته اُن جي ڪنجوسائي ڪيائون ۽ اُھي مُنھن موڙيندڙ ٿي ڦريا.'),
(1312, 9, 77, 'پوءِ الله سندن دلين ۾ کين سندن ملڻ جي ڏينھن تائين انھي سببان منافقي لاڳو ڪري ڇڏي جو الله سان اُھو انجام نه پاريائون جيڪو ساڻس ڪيائون ۽ اُنھي سببان جو ڪوڙ چوندا رھيا.'),
(1313, 9, 78, 'نه ڄاڻندا آھن ڇا ته الله سندن ڳجھا ۽ سندن پڌرا (سخن) ۽ سندن ڳجھيون صلاحون ڄاڻندو آھي ۽ تحقيق الله وڏو ڳُجھ ڄاڻندڙ آھي.'),
(1314, 9, 79, 'اِھي اھڙا آھن جو صدقي ڏيندڙن مؤمنن جي صدقي ڏئڻ ۾ طعنا ھڻندا آھن ۽ انھن کي (به طعنا ھڻندا آھن) جيڪي (مزدوري ڪرڻ جي) اوکائي کانسواءِ ڪي نه لھندا آھن ۽ انھن سان مسخريون ڪندا آھن، الله (به) سندن مسخرين جو کين بدلو ڏيندو ۽ انھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(1315, 9, 80, 'انھن لاءِ بخشش گھُرين يا انھن لاءِ بخشش نه گھُرين، جيڪڏھن اُنھن لاءِ ستر ڀيرا بخشش گھُرين ته به الله انھن کي ڪڏھن نه بخشيندو، اھو ھن سببان آھي جو اُنھن الله ۽ سندس پيغمبر کي نه مڃيو، ۽ الله بي دين قوم کي ھدايت نه ڪندو آھي.'),
(1316, 9, 81, 'الله جي پيغمبر جي (جھاد تي) وڃڻ کانپوءِ پٺ تي رھندڙ پنھنجي (رھجي) وھڻ تي خوش ٿيا ۽ پنھنجن مالن ۽ پنھنجن جِندن سان الله جي واٽ ۾ جِھاد ڪرڻ ناپسند ڪيائون ۽ (پاڻ ۾) چيائون ته گرميءَ ۾ (جھاد لاءِ) نه نڪرو، (اي پيغمبرکين) چؤ ته دوزخ جي باھ ڏاڍي گرم آھي، جيڪڏھن سمجھن ھا (ته ايئن نه ڪن ھا).'),
(1317, 9, 82, 'پوءِ ٿورو کلڻ گھرجين ۽ گھڻو روئڻ گھرجين، اُنھن (اعمالن) جي بدلي ۾ جو جيڪي ڪندا آھن.'),
(1318, 9, 83, 'پوءِ جيڪڏھن الله توکي منجھائن ڪنھن ٽوليءَ ڏانھن موٽائي نئي ته نڪرڻ لاءِ توکان موڪلائيندا پوءِ چؤ ته مون سان ڪڏھن نه نڪرندؤ ۽ مون سان گڏجي ڪنھن دشمن سان ڪڏھن نه وڙھندؤ، بيشڪ اوھين پھريون ڀيرو وھڻ سان راضي ٿيؤ تنھنڪري (ھاڻي به) پوئتي وھندڙن سان گڏجي ويھو.'),
(1319, 9, 84, '۽ منجھائن جيڪو مري تنھن تي بلڪل نماز نه پڙھ ۽ نڪي سندس قبر تي بيھ، بيشڪ انھن الله ۽ سندس پيغمبر کي نه مڃيو ۽ اُھي بي دين ٿي مُئا.'),
(1320, 9, 85, '۽ تون سندن مال ۽ سندن اولاد تي عجب نه ڪر، الله گھرندو آھي ته ان جي سببان دنيا ۾ ئي کين عذاب ڪري ۽ سندن ساھ ڪفر جي ئي حالت ۾ نِڪرن.'),
(1321, 9, 86, '۽ جيڪڏھن ڪا سُورة (ھِن لاءِ) نازل ڪئي ويندي آھي ته الله کي مڃيو ۽ سندس پيغمبر سان گڏجي جھاد ڪريو ته منجھائن آسودا توکان موڪلائيندا آھن ۽ چوندا آھن ته اسان کي ڇڏ ته ويٺلن سان گڏ رھون.'),
(1322, 9, 87, 'ويٺلن زالن سان گڏ ھُجڻ ۾ راضي ٿيا ۽ سندين دلين تي مُھر ھنئي وئي پوءِ اُھي نه سمجھندا آھن.'),
(1323, 9, 88, 'پر پيغمبر ۽ جن ساڻس ايمان آندو تن پنھنجن مالن ۽ پنھنجين جِندن سان جھاد ڪيو، ۽ اِھي اُھي آھن جن لاءِ چڱايون آھن، ۽ اُھي ئي ڇٽل آھن.'),
(1324, 9, 89, 'انھن لاءِ الله اھڙا باغ تيار ڪيا آھن جن جي ھيٺان واھيون وھنديون آھن منجھس سدائين رھڻ وارا آھن، اھا وڏي مراد ماڻڻ آھي.'),
(1325, 9, 90, '۽ بَدوِين مان بھاني ڪرڻ وارا آيا ته کين موڪل ڏني وڃي ۽ جن الله ۽ سندس رسول کي ڪوڙو ڄاتو سي ويھي رھيا، منجھائن ڪافرن کي ڏکوئيندڙ عذاب سِگھو ئي پھچندو.'),
(1326, 9, 91, 'نڪي عاجزن تي ۽ نڪي بيمارن تي ۽ نڪي اُھي جو جيڪي خرچين سو نه ٿا لھن تن تي ڪو گناھ آھي جڏھن ته الله ۽ سندس پيغمبر جا خيرخواھ ھجن، ڀلارن تي ڪو ڏوراپو نه آھي، ۽ الله بخشڻھار مھربان آھي.'),
(1327, 9, 92, '۽ نڪي اُنھن تي (ڪو گناھ آھي) جي جڏھن تو وٽ اچن ته کين (وھٽ تي) چاڙھين (۽ تون) چوين ته (ڪو وھٽ) نه ٿو لھان جنھن تي اوھان کي چاڙھيان، تڏھن اُھي موٽن ۽ ھن ڏُک کان سندن اکيون ڳوڙھا ھاريندڙ ھجن ته جيڪي (الله جي واٽ ۾) خرچين سو نه ٿا لھن،'),
(1328, 9, 93, 'ڏوراپي جي واٽ انھن کانسواءِ ٻـين تي نه آھي جن توکان موڪلايو ۽ اُھي شاھوڪار ھوا، رھيلن زالن سان گڏ ھجڻ ۾ راضي ٿيا ۽ الله سندين دلين تي مُھر ھنئي پوءِ اُھي نه ڄاڻندا آھن.'),
(1329, 9, 94, 'جڏھن (اوھين) اُنھن ڏانھن موٽندؤ (تڏھن) اوھان وٽ بھانو ڪندا، (انھن کي) چؤ ته بھانو نه ڪريو اوھان (جي ڳالھ) کي ڪڏھن نه مڃينداسون بيشڪ اوھان جي حال کان الله اسان کي خبر ڏني آھي، ۽ الله ۽ سندس پيغمبر اوھان جا عمل سِگھو ئي ڏسندو وري ڳجھ ۽ ظاھر جي ڄاڻندڙ (الله) ڏانھن موٽايا ويندؤ (۽) پوءِ جيڪي ٿي ڪيؤ تنھنجي اوھان کي سُڌ ڏيندو.'),
(1330, 9, 95, 'جڏھن انھن ڏانھن موٽندؤ (ته) ستت ئي اوھان جي آڏو الله جو قسم ھِن لاءِ کڻندا ته (اوھين) کائن مڙي وڃو، پوءِ کائن مڙي وڃو، بيشڪ اُھي پليت آھن، ۽ جيڪي ٿي ڪمايائون تنھن جي عوض ۾ سندين جاءِ دوزخ آھي.'),
(1331, 9, 96, 'اوھان وٽ ھن لاءِ قسم کڻندا ته (اوھين) کائن راضي ٿيو، پوءِ جيڪڏھن (اوھين) کائن راضي ٿيندؤ ته (به) الله بي دين ماڻھن کان راضي نه ٿيندو.'),
(1332, 9, 97, 'بَدوي ڪُفر ۽ منافقيءَ ۾ تمام سخت آھن ۽ ھن (عادت) جوڳا آھن ته الله پنھنجي پيغمبر تي جيڪي حُڪم لاٿا تنھن جون (شرعي) حدون نه ڄاڻن، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(1333, 9, 98, '۽ ڳوٺاڻن مان ڪي اھڙا آھن جو خرچ ڪن ٿا سو چَٽي ڄاڻندا آھن ۽ اوھان تي زماني جي ڦيرگھير (اچڻ) جا منتظر آھن، زماني جو بڇڙو ڦيرو مٿن ھجي (شال)، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(1334, 9, 99, '۽ ٻھراڙي وارن مان ڪي اھڙا آھن جي الله ۽ قيامت جي ڏينھن کي مڃيندا آھن ۽ جيڪي خرچيندا آھن تنھن کي الله وٽ ويجھائي جو ۽ پيغمبر جي دُعا حاصل ڪرڻ جو وسيلو ڪري وٺندا آھن، بيشڪ اھا انھن لاءِ ويجھائيءَ (جو سبب) آھي الله کين پنھنجي رحمت ھيٺ سگھوئي داخل ڪندو، بيشڪ الله بخشڻھار مھربان آھي.'),
(1335, 9, 100, '۽ مُھاجرن ۽ انصارن مان (ايمان ۾) اڳرائي ڪرڻ وارا ۽ جن چڱائيءَ سان سندين تابعداري ڪئي اُنھن (سڀني) کان الله راضي آھي ۽ اُھي کانئس راضي آھن ۽ اُنھن لاءِ باغ تيار ڪيا اٿس جن جي ھيٺان واھيون وھنديون آھن منجھس سدائين رھڻ وارا آھن، اھا وڏي مُراد ماڻڻ آھي.'),
(1336, 9, 101, '۽ اوھان جي آس پاس وارن بدوين مان (به) ڪي منافق آھن، ۽ مديني وارن مان (به)، ڪي منافقيءَ تي عادي ٿي ويا آھن، (تون) کين نه ڄاڻندو آھين، اسين انھن کي ڄاڻندا آھيون، سگھوئي اسين کين ٻه ڀيرا عذاب ڪنداسون وري وڏي عذاب ڏانھن موٽايا ويندا.'),
(1337, 9, 102, '۽ ٻيا (اھڙا) آھن جن پنھنجا گناھ باسيا آھن (انھن) چڱا ڪم ۽ ٻيا بڇڙا ڪم گڏي سڏي ڪيا آھن، اميد آھي ته الله مٿن ٻاجھ سان موٽندو، بيشڪ الله بخشڻھار مھربان آھي.'),
(1338, 9, 103, 'سندن مالن مان زڪوٰة وصول ڪر ته اُن سان کين (ظاھر ۾ به) پاڪ ڪندو رھين ۽ کين (باطن ۾) سٺو ڪندو رھين ۽ مٿن رحمت جي دُعا گھر، ڇوته تنھنجي دُعا انھن لاءِ آرام آھي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(1339, 9, 104, 'نه ڄاڻندا آھن ڇا ته الله ئي پنھنجن ٻانھن جي توبه قبول ڪندو آھي ۽ (سندين) خيراتون قبول ڪندو آھي ۽ بيشڪ الله ئي توبه قبول ڪندڙ مھربان آھي.'),
(1340, 9, 105, '۽ چؤ ته عمل ڪريو پوءِ الله ۽ سندس پيغمبر ۽ مؤمن (سڀئي) اوھان جا عمل ڏسندا، ۽ ڳُجھ ۽ ظاھر جي ڄاڻندڙ (الله) ڏانھن سگھو موٽايا ويندؤ پوءِ جيڪي ڪندا آھيو تنھن جي اوھان کي سُڌ ڏيندو.'),
(1341, 9, 106, '۽ ٻيا الله جي حُڪم لاءِ رھايل آھن ته يا ته کين عذاب ڪري يا کين معاف ڪري، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(1342, 9, 107, '۽ (انھن مان اھڙا به آھن) جن نقصان پُھچائڻ ۽ ڪفر ڪرڻ ۽ مسلمانن جي وچ ۾ ڏڦيڙ وجھڻ ۽ جن (ماڻھن) الله ۽ سندس پيغمبر سان اڳ ويڙھ ڪئي آھي تن واسطي گھٽ ٺاھڻ لاءِ (ھڪ) مسجد بڻائي آھي، ۽ (اُھي) ضرور قسم کڻندا ته چڱائيءَ کانسواءِ اسانجو (بڻائڻ ۾) ٻيو ڪو ارادو نه آھي، ۽ الله شاھدي ڏئي ٿو ته اُھي ڪوڙا آھن.'),
(1343, 9, 108, 'منجھس اصلي نه بيھ، جا مسجد پھرين ڏينھن پرھيزگاريءَ تي اڏائي ويئي آھي تنھن ۾ توکي بيھڻ (يعني نماز پڙھڻ ۽ پڙھائڻ) وڌيڪ لائق آھي، جنھن ۾ اھڙا مڙس آھن جي پاڪ رھڻ پسند ڪندا آُھن، ۽ الله پاڪ رھڻ وارن کي پسند ڪندو آھي.'),
(1344, 9, 109, 'ڀلا جنھن پنھنجي اڏاوت الله جي ڊڄ ۽ (سندس) رضامنديءَ تي اڏي سو ڀلو آھي يا اُھو جنھن پنھنجي اڏاوت نئن جي ڪرندڙ ڀر تي اَڏي؟ پوءِ اُھا اڏيندڙ سميت دوزخ جي باھ ۾ ڪري، ۽ الله ظالمن جي قوم کي ھدايت نه ڪندو آھي.'),
(1345, 9, 110, 'جو بُڻياد بڻايائون سو سدائين سندين دلين ۾ شڪ (جو سبب) رھندو سندن دلين جي وڍڻ کانسواءِ نه ويندو، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(1346, 9, 111, 'الله مؤمنن کان سندن سر ۽ مال ھِن ڪري ڳڌا جو پڪ بھشت انھن لاءِ آھي، (اِھي) اُن (انجام) تي الله جي واٽ ۾ وڙھندا آھن پوءِ ماريندا (به) آھن ۽ ماربا (به) آھن، جو انجام توريت ۽ انجيل ۽ قرآن ۾ پڪي طرح ٿيل آھي، ۽ الله کان وڌيڪ پنھنجي انجام کي پورو ڪندڙ ڪير آھي؟ پوءِ جنھن سودي جو اوھان واپار ڪيو آھي تنھن تي خوش ٿيو، ۽ اِھا ئي وڏي مُراد ماڻڻ آھي.'),
(1347, 9, 112, 'اِھي توبه ڪندڙ عبادت ڪندڙ ساراھ ڪندڙ (خُدا جي واٽ ۾) مُسافري ڪندڙ رُڪوع ڪندڙ سجدو ڪندڙ چڱائيءَ جو حُڪم ڪندڙ ۽ مدائيءَ کان جھليندڙ ۽ الله جي حدن کي نگاہ ۾ رکندڙ آھن، ۽ (اھڙن) مؤمنن کي خوشخبري ڏي.'),
(1348, 9, 113, 'پيغمبر ۽ مؤمنن کي مشرڪن لاءِ کين (ھن ڳالھ جي) پڌري ٿيڻ کانپوءِ ته اُھي دوزخي آھن بخشش گھرڻ نه جڳائي توڻيڪ مائٽيءَ وارا ھجن.'),
(1349, 9, 114, '۽ ابراھيم جو پنھنجي پيءُ لاءِ بخشش گھرڻ انجام کانسواءِ نه ھو جو اُن ساڻس انجام ڪيو ھو، پوءِ جنھن دم کيس پڌرو ٿيو ته اُھو الله جو ويري آھي (ته ھڪدم) کانئس بيزار ٿيو، بيشڪ ابراھيم بلڪل ڪئنري دل وارو بُردبار ھو.'),
(1350, 9, 115, '۽ الله جو ڪم نه آھي جو ڪنھن قوم کي سڌي رستي ڏيکارڻ کانپوءِ گمراہ ڪري جيسين اُھي (ڳالھيون) اُنھن لاءِ پڌريون (نه) ڪري جن کان انھن کي بچڻ گھرجي بيشڪ الله ھر شيء ڄاڻندڙ آھي.'),
(1351, 9, 116, 'بيشڪ آسمانن ۽ زمين جي بادشاھي الله جي ئي آھي، (اُھو) جياري ٿو ۽ ماري ٿو، ۽ الله کانسواءِ اوھان جو نڪو دوست ۽ نڪو مدد ڏيندڙ آھي.'),
(1352, 9, 117, 'پيغمبر ۽ انھن مھاجرن ۽ انصارن تي بيشڪ الله ٻاجھ سان موٽيو جن سوڙھ جي وقت ۾ پيغمبر جي تابعداري ڪئي اُن کانپوءِ جو منجھائن ھڪ ٽوليءَ جون دليون لڏي وڃڻ تي ويجھيون ھيون وري (الله) مٿن ٻاجھ سان موٽيو، ڇوته اُھو مٿن شفقت ڪندڙ مھربان آھي.'),
(1353, 9, 118, '۽ انھن ٽن تي (به) جيڪي پوئتي رھايل، ايتري قدر جو زمين ايتري ويڪرائي ھوندي به مٿن سوڙھي ٿي وئي ھئي ۽ سندن ساہ مٿن منجھيا ھوا ۽ ڀانيائون ته (ھاڻي) الله کانسواءِ ڪا جاءِ پناھ جي نه آھي، وري مٿن ھِن لاءِ ٻاجھ سان موٽيو ته توبه ڪن، تحقيق الله توبه قبول ڪندڙ مھربان آھي.'),
(1354, 9, 119, 'اي ايمان وارؤ الله کان ڊڄو ۽ سچن سان گڏ ھجو.'),
(1355, 9, 120, 'مديني وارن ۽ سندس آس پاس وارن بدوين کي نڪي الله جي پيغمبر کان (جھاد ۾) پوءِ تي رھڻ ۽ نڪي سندس وجود کان پاڻ کي پيارو رکڻ جڳائي، اھو ھن ڪري جو کين نڪي اُڃ ۽ نڪي ڏُک ۽ نڪي بُک الله جي واٽ ۾ پھچندي آھي ۽ نڪي اھڙي ھنڌ ويندا آھن جتي ڪافر ڪاوڙجن ۽ نڪي دشمن کي (ڪو) اھنج پھچائيندا آھن پر انھن لاءِ ھر ھڪ (ڳالھ) بابت چڱو عمل لکيو ويندو آھي، تحقيق الله ڀلارن جو اَجر نه وڃائيندو آھي.'),
(1356, 9, 121, '۽ ڪوبه خرچ ٿورو يا گھڻو نه ڪندا آھن ۽ نڪو ڪو ميدان لنگھندا آھن پر انھن لاءِ (چڱو عمل) ھن لاءِ لکيو ويندو آھي ته جيڪو تمام چڱو ڪم ڪندا آھن تنھنجو الله کين بدلو ڏئي.'),
(1357, 9, 122, '۽ مؤمنن کي نه جڳائيندو آھي جو مڙيئي (گڏ) نڪرن، پوءِ ٽوليءَ مان ڪي ڄڻا منجھائن ھن لاءِ ڇونه نڪرندا آھن ته دين ۾ عالِم ٿين ۽ جڏھن پنھنجي قوم ڏانھن موٽي وڃن ته ان کي ڊيڄارين ته مانَ اُھي ڊڄن.'),
(1358, 9, 123, 'اي مؤمنو ڪافرن مان جيڪي اوھان جي آس پاس آھن تن سان وڙھو ۽ ڀَلي ته (اُھي) اوھان ۾ ڪا ڏاڍائي ڏسن، ۽ ڄاڻو ته الله پرھيزگارن سان گڏ آھي.'),
(1359, 9, 124, '۽ جڏھن ڪابه سُورة لاھبي آھي ته منجھائن ڪو اھڙو آھي جو چوندو آھي ته ھن سورة اوھان مان ڪھڙي جو ايمان وڌايو آھي؟ پوءِ جن ايمان آندو آھي تن جي ايمان کي وڌايو اٿس ۽ اُھي خوش ٿيندا آھن.'),
(1360, 9, 125, '۽ جن جي دلين ۾ بيماري آھي تن کي پليتي تي پليتي وڌائي اٿس ۽ اُھي ڪافر ٿي مُئا.'),
(1361, 9, 126, 'نه ڏسندا آھن ڇا ته ھر سال ھڪ ڀيرو يا ٻه ڀيرا سزا ڏني ويندي اٿن؟ وري نڪي توبه ڪندا آھن ۽ نڪي اُھي نصيحت وٺندا آھن.'),
(1362, 9, 127, '۽ جڏھن ڪا سورة (انھن بابت) نازل ڪبي آھي (ته) انھن مان ھڪڙا ٻـين ڏانھن (شرمندگيءَ کان) نھاريندا آھن، (۽ چوندا آھن ته) ڪنھن اوھانکي ڏٺو آھي ڇا؟ وري (گھرن ڏانھن) موٽندا آھن، الله سندن دليون ھن سببان ڦيرايون آھن جو اُھا بي سمجھ قوم آھي.'),
(1363, 9, 128, '(اي ماڻھو) بيشڪ اوھان جي جنس منجھان اوھان وٽ پيغمبر آيو آھي اوھان جو ڏُک مٿس اوکو آھي اوھان (جي ھدايت) تي حَرِيص آھي مسلمانن تي شفقت ڪندڙ مھربان آھي.'),
(1364, 9, 129, 'پوءِ جيڪڏھن ڦرن ته چؤ ته مون کي الله ڪافي آھي، اُن کانسواءِ ڪو عبادت جو لائق نه آھي، مٿس ڀروسو ڪيم ۽ اُھو وڏي عرش جو مالڪ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الٓرٰ، ھيءُ آيتون حِڪمت واري ڪتاب جون آھن.'),
(1366, 10, 2, 'ماڻھن کي (ھيءَ ڳالھ) عجب ۾ وجھندي آھي ڇا ته منجھائن ھڪ مڙس ڏانھن وحي موڪليوسون ته ماڻھن کي ڊيڄار ۽ جن ايمان آندو تن کي خوشخبري ڏي ته انھن لاءِ سندن پالڻھار وٽ وڏومرتبو آھي، ڪافرن چيو ته ھي پڌرو جادوگر آھي.'),
(1367, 10, 3, 'بيشڪ اوھان جو پالڻھار اُھو الله آھي جنھن آسمانن ۽ زمين کي ڇھن ڏينھن ۾ بڻايو (۽) وري عرش ڏانھن توجّه ڪيائين اھوئي ڪمن کي رِٿيندو آھي، ڪوئي سفارش ڪندڙ نه آھي پر سندس اجازت کانپوءِ، اھوئي اوھان جو پالڻھار الله آھي تنھنڪري سندس عبادت ڪريو، پوءِ ڇونه نصيحت وٺندا آھيو؟'),
(1368, 10, 4, 'اوھان مڙني جو ڏانھس موٽڻ آھي، الله جو انجام سچو آھي، اھوئي پھريون (به) بڻائيندو آھي وري (به) کين ھن لاءِ موٽائي بڻائيندو ته جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي انصاف سان بدلو ڏئي، ۽ جن نه مڃيو تن لاءِ ٽھڪندڙ پاڻي ۽ ڏکوئيندڙ عذاب انھيءَ ڪري آھي جو ڪُفر ڪيو ھئائون.'),
(1369, 10, 5, 'اُھو (الله) آھي جنھن سج کي چمڪندڙ ۽ چنڊ کي روشن ڪيو ۽ اُن (چنڊ) جون منزلون ھن لاءِ مَقرّر ڪيون ته اوھين ورھن جو ڳاڻيٽو ۽ حساب ڄاڻو، الله اِھو سچ کانسواءِ نه بڻايو آھي، ڄاڻندڙ قوم لاءِ کولي نشانيون بيان ڪندو آھي.'),
(1370, 10, 6, 'بيشڪ رات ۽ ڏينھن جي ڦيرگھير (۾) ۽ الله جيڪي آسمانن ۽ زمين ۾ بڻايو تنھن ۾ پرھيزگار قوم لاءِ ضرور نشانيون آھن.'),
(1371, 10, 7, 'بيشڪ جيڪي اسان جي ملڻ جو آسرو نه ٿا رکن ۽ دنيا جي حياتيءَ ساڻ ريجھي ويا ۽ اُنھيءَ سان آرامي ٿي رھيا ۽ اُھي جيڪي اسان جي آيتن کان بيخبر رھيا.'),
(1372, 10, 8, 'اھي جيڪي ڪمائيندا رھيا تنھن سببان انھن جو مڪان باھ آھي.'),
(1373, 10, 9, '(۽) جن مڃيو ۽ چڱا ڪم ڪيا تن کي سندن پالڻھار سندن ايمان سببان (اھڙن بھشتن ڏانھن) سڌو رستو ڏيکاريندو، جن جي ھيٺان نعمتن وارن باغن ۾ واھيون وھنديون آھن.'),
(1374, 10, 10, 'منجھن سندين دُعا (ھيءَ) آھي ته اي الله توکي پاڪائي جڳائي ۽ منجھن سندن آڌرڀاءُ سلام آھي، سندين پوئين دُعا ھيءَ آھي ته سڀ ساراھ جھانن جي پالڻھار الله کي جُڳائي.'),
(1375, 10, 11, '۽ جيڪڏھن الله ماڻھن لاءِ مدائي جلد موڪلي ھا جھڙو اُھي پنھنجي چڱائي جلد گھرندا آھن ته سندين (ٺھرايل) مدت انھن لاءِ پوري ڪئي وڃي ھا، پوءِ جيڪي اسان جي ملڻ جي اميد نه رکندا آھن تن کي سندن گمراھيءَ ۾ حيران ڇڏيندا آھيون.'),
(1376, 10, 12, '۽ جنھن مھل ماڻھوءَ کي ڪو ڏُک پھچندو آھي (ته) پنھنجي پاسي ڀر ويٺي يا بيٺي اسان کي سڏيندو آھي، پوءِ جنھن مھل کانئس سندس ڏک لاھيندا آھيون (ته اُن وقت اھڙي ويساري ۾ پئجي) ھليو ويندو آھي (جو) ڄڻڪ کيس جا اوکائي پُھتي ھئي تنھن (جي لاھڻ) لاءِ اسان کي سڏيو ئي نه ھوائين، اھڙي طرح حد کان لنگھندڙ جيڪي ڪندا آھن سو اُنھن لاءِ سينگاريو ويو آھي.'),
(1377, 10, 13, '۽ بيشڪ اوھان کان اڳ (گھڻن) جُڳن وارن جڏھن ظلم ڪيو ته (کين) ھلاڪ ڪيو اٿون ۽ وٽن سندن پيغمبر چِٽن معجزن سان آيا ۽ اھڙا نه ھوا جو ايمان آڻين، اھڙي طرح ڏوھارڻ قوم کي سزا ڏيندا آھيون.'),
(1378, 10, 14, 'وري کائن پوءِ مُلڪ ۾ اوھان کي سندين جاڳھن تي وھاريوسون ته اوھين ڪھڙي طرح ڪم ڪريو ٿا.'),
(1379, 10, 15, '۽ جڏھن اسان جون چٽيون آيتون کين پڙھي ٻڌائبيون آھن (ته) جيڪي اسان جي ملڻ جي اُميد نه رکندا آھن سي چوندا آھن ته ھن کانسواءِ ٻيو قرآن آڻ يا کيس مٽاءِ، چؤ ته پنھنجي طرفان ان جي مٽائڻ جو مون کي اختيار ڪونھي، جيڪي مونکي وحي ڪيو ويندو آھي تنھن کانسواءِ (ٻئي ڪنھن جي) تابعداري نه ڪندو آھيان، بيشڪ آءٌ جيڪڏھن پنھنجي پالڻھار جي نافرماني ڪريان ته وڏي ڏينھن جي عذاب کان ڊڄان ٿو.'),
(1380, 10, 16, 'چؤ ته جيڪڏھن الله گھري ھا ته آءٌ اُن (قرآن) کي اوھان تي نه پڙھان ھا ۽ نڪي (الله) اُن سان اوھان کي ڄاڻي ھا، بيشڪ آءٌ اوھان ۾ ھن کان اڳ (سڄي) ڄمار رھيس (ته نه پڙھيم)، پوءِ ڇونه ٿا سمجھو؟'),
(1381, 10, 17, 'پوءِ ان کان وڌيڪ ظالم ڪير آھي جو الله تي ڪُوڙو ٺاھ ٺاھي يا سندس آيتن کي ڪوڙ ڄاڻي، سچ آھي ته ڏوھاري نه ڇُٽندا.'),
(1382, 10, 18, '۽ الله کانسواءِ جن کي پوڄيندا آھن سي نڪي کين ڏک پھچائيندا آھن ۽ نڪي کين سُک پھچائيندا آھن ۽ چوندا آھن ته ھي الله وٽ اسان جا شفاعت ڪندڙ آھن، چؤ ته اوھين الله کي اھڙيءَ شيءَ بابت چتائيندا آھيو ڇا جيڪا شيء (ھو) نڪي آسمانن ۾ ۽ نڪي زمين ۾ ڄاڻندو آھي، اُھو پاڪ آھي ۽ جنھن شيء کي ساڻس شريڪ ڪندا آھيو تنھن کان اُھو مٿاھون آھي.'),
(1383, 10, 19, '۽ (اصل) ماڻھو ھڪ ٽوليءَ کانسواءِ نه ھوا پوءِ اختلاف ڪيائون، ۽ جيڪڏھن تنھنجي پالڻھار جو حُڪم اڳ ٿيل نه ھجي ھا ته جنھن (ڳالھ) بابت اختلاف ڪن ٿا تنھنجو سندن وچ ۾ فيصلو پورو ڪيو وڃي ھا.'),
(1384, 10, 20, '۽ چوندا آھن ته سندن پالڻھار کان مٿس ڪو مُعجزو ڇونه لاٿو ويو آھي؟ چؤ ته ڳُجھ (جي خبر) الله کانسواءِ ٻئي ڪنھن کي نه آھي پوءِ انتظار ڪريو، آءٌ به اوھان سان گڏ انتظار ڪندڙ آھيان.'),
(1385, 10, 21, '۽ جڏھن ماڻھن کي اوکائي پھچڻ کانپوءِ (پنھنجي) ٻاجھ (سان سُک جو مزو) چکائيندا آھيون (ته) اُتي جو اُتي اُنھن کي اسانجي آيتن ۾ بدگمانيون ٿينديون آھن، چؤ ته الله رِٿ رٿڻ ۾ ڏاڍو تڪڙو آھي، بيشڪ جيڪي اوھين بڇڙيون رٿون ڪندا آھيو سي اسانجا ملائڪ لکندا آھن.'),
(1386, 10, 22, 'اُھو (الله) آھي جو اوھان کي بر ۽ دريائن ۾ گھمائيندو آھي، ايتري تائين جو جڏھن ٻيڙين ۾ ھوندا آھيو، ۽ (اُھي) پاتڻين سميت سڻائن وائن سان ھلنديون آھن ۽ اُنھن سان سرھا ٿيندا آھن ته اوچتو کين اڻائو واءُ پھچندو آھي ۽ کين چوڌاري لھر ايندي آھي ۽ ڀائيندا آھن ته بيشڪ (اُھا) کين ويڙھي ويئي (ته) الله جي عبادت ۾ سچائيءَ وارا ٿي کيس سڏيندا آھن، (۽ چوندا آھن) ته جيڪڏھن ھن (اوکائي) کان اسان کي بچائين ته ضرور شڪراني ڪندڙن مان ٿينداسون.'),
(1387, 10, 23, 'پوءِ جنھن مھل کين ڇڏايائين (ته) اُتي جو اُتي مُلڪ ۾ ناحق شرارت ڪندا آھن، اي ماڻھؤ اوھان جو شرارتون اوھان تي ئي آھن (ھيءُ) دُنيا جي حياتي جو فائدو (لڌو) وري اوھان جو موٽڻ اسان ڏانھن آھي پوءِ جيڪي ڪندا آھيو تنھن جي اوھان کي سُڌ ڏينداسون.'),
(1388, 10, 24, 'دُنيا جي حياتي جو مثال رڳو انھي پاڻي وانگر آھي جنھن کي آسمان کان وسايوسون پوءِ ساڻس زمين جا (اُھي) اوڀڙ رليا مليا جن مان ماڻھو ۽ ڍور کائيندا آھن، ايتري تائين جو جنھن مھل زمين پنھنجي ساوڪ ورتي ۽ سينگاري ۽ سندس رھڻ وارن ڀانيو ته پاڻ مٿس وسوارا آھن (تنھن مھل) اوچتو اُن کي رات جو يا ڏينھن جو اسانجو حُڪم پھتو پوءِ اُن کي لُڻيل (ناس) ڪري ڇڏيوسون ڄڻڪ ڪالھ ئي نه ھُئي، اھڙي طرح سمجھندڙ قوم لاءِ کولي نشانيون بيان ڪندا آھيون.'),
(1389, 10, 25, '۽ الله سلامتيءَ جي گھر ڏانھن سڏيندو آھي، ۽ جنھن کي وڻيس تنھن کي سڌي واٽ ڏانھن ھدايت ڪندو آھي.'),
(1390, 10, 26, 'جن چڱائي ڪئي تن لاءِ (اَھڙي) چڱائي ۽ بلڪ وڌيڪ آھي، ۽ سندن منھن کي نڪي ڪارنھن ۽ نڪي خواري ويڙھيندي، اھي بھشتي آھن، اُھي منجھس سدائين رھڻ وارا آھن.'),
(1391, 10, 27, '۽ جن بڇڙائي ڪمائي تن لاءِ اُن جھڙي بڇڙائي ۽ کين خواري ڍڪيندي، اُنھن کي الله (جي عذاب) کان ڪو بچائيندڙ نه آھي، ڄڻڪ سندن مُنھن کي اونداھي رات مان ذرو ڍڪايو ويو آھي، اھي دوزخي آھن، منجھس سدائين رھن وارا آھن.'),
(1392, 10, 28, '۽ جنھن ڏينھن انھن مڙني کي گڏ ڪنداسون وري مشرڪن کي چونداسون ته اوھين ۽ اوھان جا (الله سان ٺھرايل) شريڪ پنھنجي ھنڌ بيھو، پوءِ سندن وچ ۾ جدائي وجھنداسون ۽ سندن شريڪ چوندا ته (اوھين) اسان کي نه پوڄيندا ھيؤ.'),
(1393, 10, 29, 'پوءِ اسان جي ۽ اوھان جي وچ ۾ الله شاھد بس آھي ته اسين اوھان جي پوڄا کان ضرور بي خبر ھواسون.'),
(1394, 10, 30, 'اُن ھنڌ سڀ ڪنھن ماڻھو جيڪي اڳي موڪليو ھوندو سو (اُھو) لھندو ۽ پنھنجي سچي ڌڻي الله ڏانھن موٽايا ويندا ۽ جيڪو ٺاھ ٺاھيائون ٿي سو کائن ڀلجي ويندو.'),
(1395, 10, 31, 'چؤ ته اوھان کي آسمان ۽ زمين مان ڪير روزي ڏيندو آھي؟ ڪنن ۽ اکين جو ٻيو ڪير مالڪ آھي ڇا؟ ۽ ڪير جيئري کي مُئي مان ڪڍندو آھي؟ ۽ ڪير مُئي کي جيئري مان ڪڍندو آھي؟ ۽ ڪير ڪمن کي رٿيندو آھي، پوءِ سُتت ئي چوندا ته الله، پوءِ چؤ ته ڇونه ڊڄندا آھيو؟'),
(1396, 10, 32, 'پوءِ اُھو ئي اوھان جو سچو پالڻھار الله آھي، پوءِ سچ کانپوءِ گمراھيءَ کانسواءِ (ٻيو) ڇا آھي، پوءِ ڪيڏي جو ڪيڏي ڦيرايا وڃو ٿا؟'),
(1397, 10, 33, 'اھڙي طرح تنھنجي پالڻھار جو حُڪم انھن بدڪارن تي لازم ٿيو آھي جو اُھي ايمان نه آڻيندا.'),
(1398, 10, 34, 'چؤ ته اوھان جي معبودن مان ڪو آھي ڇا جو خلق کي نئي سر بڻائي وري کين (ماري) موٽائي جياري؟ چؤ ته الله نئي سر خلق کي بڻائي ٿو وري کيس موٽائي جياريندو پوءِ اوھين ڪيڏي جو ڪيڏي ڦيرايا ويندا آھيو.'),
(1399, 10, 35, 'چؤ ته اوھان جي معبودن مان ڪو آھي جو حق ڏانھن سِڌو رستو ڏيکاريندو آھي، چؤ ته الله سِڌو رستو ڏيکاريندو آھي، پوءِ جيڪو حق ڏانھن سِڌو رستو ڏيکاري سو تابعداري ڪرڻ جو وڌيڪ حقدار آھي يا اُھو جيڪو (پاڻ) رستي ڏيکارڻ کانسواءِ رستو نه لھندو ھُجي؟ پوءِ اوھان کي ڇا ٿيو، ڪھڙو نبيرو ڪريو ٿا؟'),
(1400, 10, 36, '۽ اُنھن مان گھڻا رڳو گُمان تي ھلندا آھن، بيشڪ گُمان سچ جي پورائي ڪجھ به نه ڪندو آھي، تحقيق جيڪي ڪندا آھن سو الله ڄاڻندڙ آھي.'),
(1401, 10, 37, '۽ ھي قُرآن الله کان سواءِ پاڻون ٺھيل نه آھي پر کانئس اڳ جيڪي ڪتاب آھن تن جو سچو ڪندڙ آھي ۽ حُڪمن کي کولي بيان ڪندڙ آھي منجھس ڪو شڪ نه آھي جھانن جي پالڻھار کان آھي.'),
(1402, 10, 38, 'يا چوندا آھن ته (ﷴ ﷺ) اُھو پاڻون ٺاھيو آھي، چؤ ته جيڪڏھن سچا آھيو ته جھڙيس ڪا سورة آڻيو ۽ الله کان سواءِ جنھن کي (سڏي) سگھو تنھن کي سڏيو.'),
(1403, 10, 39, 'بلڪ جنھن علم کي پھچي نه سگھيا تنھن کي ڪوڙ ڀانيائون ۽ اڃا وٽن سندس وعدن جي سچائي نه آئي آھي، جيڪي کائن اڳ ھوا تن به اھڙي طرح ڪوڙ ڀانيو ھو پوءِ ڏس ته ظالمن جي پڇاڙي ڪھڙي طرح ٿي؟'),
(1404, 10, 40, '۽ منجھائن ڪو اُن (قرآن) تي ايمان آڻيندو آھي ۽ منجھائن ڪو اُن تي ايمان نه آڻيندو آھي، تنھنجو پالڻھار فساد ڪندڙن کي چڱو ڄاڻندڙ آھي.'),
(1405, 10, 41, '۽ جيڪڏھن توکي ڪوڙو ڀائين ته چؤ مون لاءِ پنھنجو عمل ۽ اوھان لاءِ پنھنجو عمل آھي، جيڪي (آءٌ) ڪريان ٿو تنھن کان اوھين بيزار آھيو ۽ جيڪي (اوھين) ڪريو ٿا تنھن کان آءٌ بيزار آھيان.'),
(1406, 10, 42, '۽ منجھائن ڪي توڏانھن (ڪن ڏيئي قرآن کي) ٻڌندا آھن، پوءِ تون ٻوڙن کي ٻڌائيندين ڇا جيتوڻيڪ نه سمجھندا ھجن؟'),
(1407, 10, 43, '۽ منجھائن ڪوئي توڏانھن نھاريندو آھي، پوءِ انڌن کي سِڌو رستو ڏيکاريندين ڇا؟ جيتوڻيڪ ڏسندا ئي نه ھُجن.'),
(1408, 10, 44, 'الله ماڻھن تي ڪُجھ ظلم نه ڪندو آھي پر ماڻھو پاڻ تي ظلم ڪندا آھن.'),
(1409, 10, 45, '۽ جنھن ڏينھن (الله) کين گڏ ڪندو تنھن ڏينھن ڄڻڪ (دنيا ۾) ڏينھن جي ھڪ گھڙيءَ کانسواءِ (وڌيڪ) ترسيائي نه ھوا پاڻ ۾ ھڪ ٻئي کي سڃاڻندا، بيشڪ جن الله جي ديدار کي ڪوڙ ڀانيو تن ڇيئو پاتو ۽ ھدايت وارا نه ھوا.'),
(1410, 10, 46, '۽ جيڪو انجام ساڻن ڪريون ٿا تنھن منجھان ڪجھ جي توکي ڏيکاريون ٿا يا توکي فوت ڪريون ته (ھر حال ۾) سندن موٽڻ اسان ڏانھن آھي وري جيڪي ڪندا آھن تنھن تي الله شاھد آھي.'),
(1411, 10, 47, '۽ سڀ ڪنھن اُمت لاءِ ھڪ پيغمبر آھي، پوءِ جڏھن سندن پيغمبر آيو (ته) سندن وچ ۾ اِنصاف سان نبيرو ڪيو ويو ۽ انھن تي ظلم نه ڪيو ويو.'),
(1412, 10, 48, '۽ چوندا آھن ته جيڪڏھن اوھين سچا آھيو ته اھو انجام ڪڏھن ٿيندو.'),
(1413, 10, 49, 'چؤ ته (آءٌ) الله جي مرضيءَ کانسواءِ پاڻ کي نڪي ڏک ۽ نڪي سُک پھچائي سگھان ٿو، سڀ ڪنھن اُمّت لاءِ ٺھرايل مُدت آھي، جڏھن سندين ٺھرايل مُدّت پُھچي ٿي ته ھڪ گھڙي دير نه ڪندا ۽ نڪي اڳڀرو ويندا.'),
(1414, 10, 50, 'چؤ ته مون کي خبر ڏيو ته جيڪڏھن سندس عذاب رات جو يا ڏينھن جو اوھان کي پُھچي ته (ڇا ڪري سگھندؤ) ڏوھاري ان کي ڪھڙو جلد گھُرندا آھن.'),
(1415, 10, 51, 'وري جنھن مھل (اُھو) پھچندو (ته) ان تي ايمان آڻيندؤ ڇا، (۽ چيو ويندن ته) ھاڻي (مڃيؤ)؟ ۽ بيشڪ (اڳي) ان کي تڪڙو گھرندا ھُيؤ.'),
(1416, 10, 52, 'وري ظالمن کي چيو ويندو ته ھميشگيءَ وارو عذاب چکو، جيڪي اوھين ڪمائيندا ھيؤ تنھن کانسواءِ اوھان کي ٻيو ڪو بدلو نه ڏبو.'),
(1417, 10, 53, '۽ توکان خبر پڇندا آھن ته اھو (وعدو) سچ آھي؟ چؤ ته ھائو! مون کي پنھنجي پالڻھار جو قسم آھي ته ضرور سچ آھي، ۽ اوھين ٿڪائڻ وارا نه آھيو.'),
(1418, 10, 54, '۽ جيڪڏھن سڀڪنھن ظلم ڪندڙ جيءَ کي جيڪي زمين ۾ آھي سو ھجي ھا (اُھو) ضرور پنھنجو عوض ڏئي ڇڏي ھا، ۽ جڏھن عذاب ڏسندا ته ڳجھو لڄي ٿيندا، ۽ سندن وچ ۾ انصاف سان نبيرو ڪيو ويندو ۽ انھن تي ظلم نه ڪبو.'),
(1419, 10, 55, 'خبردار بيشڪ جيڪي آسمانن ۽ زمين ۾ آھي سو الله جو آھي، خبردار الله جو انجام سچو آھي پر اُنھن مان گھڻا نه ڄاڻندا آھن.'),
(1420, 10, 56, 'اُھو جياريندو آھي ۽ ماريندو آھي ۽ اوھين ڏانھس موٽايا ويندؤ.'),
(1421, 10, 57, 'اي ماڻھو اوھان جي پالڻھار کان اوھان وٽ نصيحت ۽ جيڪا (بيماري) سينن ۾ آھي تنھن لاءِ شفا ۽ مؤمنن لاءِ ھدايت ۽ رحمت آئي آھي.'),
(1422, 10, 58, 'چؤ ته الله جي فضل سان ۽ سندس ٻاجھ سان (نازل ٿيو آھي) پوءِ اُن سان خوش ٿيڻ گھرجين، جو گڏ ڪندا آھن تنھن کان اُھو ڀلو آھي.'),
(1423, 10, 59, 'چؤ ته ڪي ڏسو ٿا ته الله روزيءَ مان جيڪي اوھان ڏانھن لاٿو آھي پوءِ منجھانئس اوھان (پاڻ) ڪُجھ حرام ۽ ڪُجھ حلال ڪيو آھي، چؤ ته الله اوھان کي موڪل ڏني آھي ڇا؟ يا الله تي ٺاھ ٺاھيندا آھيو؟'),
(1424, 10, 60, '۽ جيڪي الله تي ڪوڙ ٺاھيندا آھن تن کي قيامت جي ڏينھن ڪھڙو گمان آھي (ته کين) عذاب نه ڪندو ڇا؟ بيشڪ الله ماڻھن تي ضرور وڏي فضل ڪرڻ وارو آھي پر اُنھن مان گھڻا شڪرانو نه ڪندا آھن.'),
(1425, 10, 61, '۽ ڪنھن حال ۾ نه ھوندو آھين ۽ الله جي طرف کان آيل قرآن مان جيڪي نه پڙھندو آھين ۽ ڪوبه ڪم اوھين نه ٿا ڪريو جڏھن منجھس لڳا رھندا آھيو ته اسين ئي اوھان وٽ حاضر آھيون، ۽ تنھنجي پالڻھار کان ذري جيترو نڪي زمين ۾ نڪي آسمان ۾ ڳجھو رھندو آھي ۽ نڪي اُن کان ٿورو ۽ نڪي گھڻو پر (سڀ) پڌري ڪتاب ۾ (لکيل) آھي.'),
(1426, 10, 62, 'خبردار! الله جي پيارن کي نڪي ڀؤ آھي ۽ نڪي اُھي غمگين رھندا.'),
(1427, 10, 63, 'جن ايمان آندو ۽ پرھيزگاري ڪئي اٿن.'),
(1428, 10, 64, 'تن لاءِ دُنيا جي حياتي ۽ آخرت ۾ خوشخبري آھي، الله جي حُڪمن کي ڪا ڦير گھير ڪانھي، اھائي وڏي مُراد ماڻڻ آھي.'),
(1429, 10, 65, '۽ سندن چوڻ توکي نه ڏکوئي، بيشڪ سڀ غلبو الله جو ئي آھي، اُھو ٻڌندڙ ڄاڻندڙ آھي.'),
(1430, 10, 66, 'خبردار بيشڪ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله جو آھي، ۽ جيڪي الله کانسواءِ ٻـين شريڪن جي عبادت ڪندا آھن سي (حقيقت ۾) گمان کانسواءِ ٻئي جي تابعداري نه ڪندا آھن ۽ اُھي رڳو اٽڪلون ڪندا آھن.'),
(1431, 10, 67, 'اُھو (الله) آھي جنھن اوھان لاءِ رات کي ھن ڪري بڻايو ته منجھس آرام ڪريو ۽ ڏينھن کي روشن بڻايائين، بيشڪ اُن ۾ ٻڌندڙ قوم لاءِ نشانيون آھن.'),
(1432, 10, 68, 'ڪافر چون ٿا ته الله (پنھنجي لاءِ) اولاد ورتو آھي اُھو پاڪ آھي، اُھو بي پرواہ آھي، جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو ان جو آھي، ھن (پٽ جي چوڻ) بابت اوھان وٽ ڪا سَند نه آھي، جيڪي نه ڄاڻندا آھيو سو ڇو الله تي چوندا آھيو.'),
(1433, 10, 69, 'چؤ جيڪي الله تي ڪوڙو ٺاھ ٺاھيندا آھن سي نه ڇٽندا.'),
(1434, 10, 70, 'دنيا ۾ ٿورو آسودو گذران ھوندن وري اسان وٽ سندن موٽڻ آھي پوءِ کين انھي سببان سخت عذاب چکائينداسين جو ڪُفر ڪندا آھن.'),
(1435, 10, 71, '۽ نُوح جو قصو کين پڙھي ٻُڌاءِ، جڏھن پنھنجي قوم کي چيائين ته اي منھنجي قوم جيڪڏھن منھنجو رھڻ ۽ الله جي آيتن سان منھنجي نصيحت ڏيڻ اوھان تي ڳوري ٿي آھي ته الله تي ڀروسو ڪيم پوءِ پنھنجي ڪم ۽ پنھنجن شريڪن جي مدد سان گڏجي صلاح ڪريو پوءِ اوھان جو ڪم اوھان کان ڳجھو رھجي نه وڃي ته پوءِ مون سان ڪري گذرو ۽ مون کي ڍر نه ڏيو.'),
(1436, 10, 72, 'پوءِ جيڪڏھن (منھنجي چوڻ کان) ڦريؤ (ته ڇا ٿيو) مون اوھان کان ڪو اجورو نه گھريو ھو، منھنجو اُجورو الله کانسواءِ ٻئي تي نه آھي ۽ مون کي حُڪم ڪيو ويو آھي ته مُسلمانن مان ھجان.'),
(1437, 10, 73, 'پوءِ کيس ڪوڙو ڀانيائون پوءِ کيس ۽ جيڪي ٻيڙي ۾ ساڻس ھوا تن کي بچايوسون ۽ کين انھن (ڪافرن) جو جانشين ڪيوسون ۽ جن اسان جي آيتن کي ڪوڙو ڀانيو تن کي ٻوڙيوسون، پوءِ ڏس ته جن کي ڊيڄاريو ويو تن جي پڇاڙي ڪھڙي طرح ٿي.'),
(1438, 10, 74, 'پوءِ اُن (نوح) کانپوءِ (گھڻا) پيغمبر سندن قوم ڏانھن موڪلياسون پوءِ وٽن پڌرن مُعجزن سان آيا پوءِ اھڙا نه ھوا جو جنھن شيء کي پھريائين ڪوڙ ڀانيو ھئائون تنھن تي ايمان آڻين، اھڙي طرح حد کان لنگھندڙن جي دلين تي مھر ھڻندا آھيون.'),
(1439, 10, 75, 'وري کائن پوءِ مُوسىٰ ۽ ھارون کي فرعون ۽ سندس سردارن ڏانھن پنھنجن مُعجزن سميت موڪليوسون پوءِ وڏائي ڪيائون ۽ ڏوھارڻ قوم ھُئي.'),
(1440, 10, 76, 'پوءِ جنھن مھل وٽن اسان وٽان سچي ڳالھ آئي (تڏھن) چيائون ته بيشڪ ھي پڌرو جادو آڳي.'),
(1441, 10, 77, 'مُوسىٰ چيو جڏھن اوھان وٽ حق آيو (ته) اُن لاءِ ڇو چوندا آھيو (ته جادو آھي)؟ ھي جادو آھي ڇا؟ ۽ جادوگر نه ڇٽندا.'),
(1442, 10, 78, 'چيائون ته اسان وٽ ھن لاءِ آيو آھين ڇا ته جنھن (مذھب) تي اسان پنھنجا پيءُ ڏاڏا ڏٺا تنھن کان اسان کي روڪين ۽ مُلڪ ۾ اوھان (ٻنھي) لاءِ وڏائي ھجي؟ ۽ اسين اوھان کي مڃڻ وارا نه آھيون.'),
(1443, 10, 79, '۽ فرعون چيو ته سڀئي ھوشيار جادوگر مون وٽ آڻيو.'),
(1444, 10, 80, 'پوءِ جنھن مھل جادوگر آيا (تنھن مھل) مُوسىٰ انھن کي چيو ته جيڪي اُڇلائڻو اٿو سو اوھين اڇليو.'),
(1445, 10, 81, 'پوءِ جنھن مھل اُڇليائون (ته) موسىٰ چيو ته جيڪي آندو اٿَوَ سو جادو آھي، بيشڪ الله ان کي سگھوئي باطل ڪندو، بيشڪ الله فسادين جو ڪم نه ٺاھيندو آھي.'),
(1446, 10, 82, '۽ الله حق کي پنھنجي حُڪم سان ثابت ڪندو آھي جيتوڻيڪ ڏوھاري بڇان ڀانئين.'),
(1447, 10, 83, 'پوءِ موسىٰ تي سندس قوم مان ھڪ ٽوليءَ کانسواءِ فرعون ۽ سندس سردارن کان ڊڄڻ جي ڪري ته متان کين ايذائين (ڪنھن) ايمان نه آندو، ۽ تحقيق فرعون (اُن) مُلڪ ۾ ھٺيلو ھو، ۽ اُھو ضرور حَد کان لنگھندڙن مان ھو.'),
(1448, 10, 84, '۽ موسىٰ چيو ته اي منھنجي قوم جيڪڏھن اوھان الله تي اِيمان آندو آھي ته جي مُسلمان آھيو ته مٿس ڀروسو ڪريو.'),
(1449, 10, 85, 'پوءِ چيائون ته الله تي ڀروسو ڪيوسون، اي اسان جا پالڻھار اسان کي ظالمن جي قوم جي پرک نه ڪر.'),
(1450, 10, 86, '۽ ڪافرن جي قوم کان پنھنجي ٻاجھ سان اسان کي بچاءِ.'),
(1451, 10, 87, '۽ موسىٰ ۽ سندس ڀاءُ ڏانھن وحي ڪيوسون ته اوھين پنھنجي قوم لاءِ مِصر ۾ گھر بڻايو ۽ (اُنھن) پنھنجن گھرن کي قبلي جي سامھون ڪريو ۽ نماز پڙھو، ۽ مؤمنن کي خوشخبري ڏي.'),
(1452, 10, 88, '۽ مُوسىٰ چيو ته اي اسان جا پالڻھار تو فرعون ۽ سندس سردارن کي سينگار ۽ (گھڻو) مال دنيا جي حياتيءَ ۾ (ھن لاءِ) ڏنو آھي ته اي اسان جا پالڻھار تنھنجي واٽ کان ماڻھن کي ڀُلائين، اي اسان جا پالڻھار سندن مال کي ناس ڪر ۽ سندين دلين مُھر ھَڻ جو جيستائين ڏکوئيندڙ عذاب (نه) ڏسن تيسين ايمان نه آڻين.'),
(1453, 10, 89, '(الله) چيو ته بيشڪ اوھان (ٻنھي) جي دُعا قبول ڪئي وئي پوءِ ٻئي قائم رھو ۽ جيڪي نه ڄاڻندا آھن تن جي واٽ جي تابعداري نه ڪريو.'),
(1454, 10, 90, '۽ بني اسرائيلن کي سمنڊ کان (پار) لنگھايوسون پوءِ سندن پويان فرعون ۽ سندس لشڪر ظلم ۽ سرڪشي ڪرڻ لاءِ ايتري قدر سندن پُٺيان پيا جيسين ٻُڏا (فرعون) چيو ته ايمان آندم سچ آھي ته جنھن تي بني اسرائيلن ايمان آندو آھي تنھن کانسواءِ ڪوئي عبادت جو لائق نه آھي ۽ آءٌ مسلمانن منجھان آھيان.'),
(1455, 10, 91, '(چيو ويو ته) ھاڻي (ٿو ايمان آڻين)؟ ۽ بيشڪ توکان پوءِ اچن تن لاءِ نشاني ھجين، ۽ گھڻا ماڻھو اسان جي نشانين کان ضرور بيخبر آھن.'),
(1456, 10, 92, 'پوءِ اڄ تنھنجي لاشي کي ھِن لاءِ بچائينداسون ته جيڪي توکان پوءِ اچن تن لاءِ نشاني ھجين، ۽ گھڻا ماڻھو اسان جي نشانين کان ضرور بيخبر آھن.'),
(1457, 10, 93, '۽ بيشڪ بني اسرائيلن کي چڱي ھنڌ رھڻ جي جاءِ ڏني سون ۽ کين سُٺين شين مان روزي ڏني سون، پوءِ (ايستائين) اختلاف نه ڪيائون جيسين وٽن علم (يعني قرآن نه) آيو، بيشڪ تنھنجو پالڻھار جنھن ڳالھ بابت جھڳڙو ڪندا آھن تنھن جو قيامت جي ڏينھن سندن وچ ۾ فيصلو ڪندو.'),
(1458, 10, 94, 'پوءِ جيڪي توڏانھن نازل ڪيوسون تنھن کان جيڪڏھن شڪ ۾ آھين ته جيڪي توکان اڳ ڪتاب پڙھندا آھن تن کان پُڇ، بيشڪ تنھنجي پالڻھار کان توڏانھن حق آيو آھي پوءِ تون شڪ ڪندڙن مان نه ھُج.'),
(1459, 10, 95, '۽ نڪي تون انھن مان ھج جن الله جي آيتن کي ڪُوڙ ڄاتو ته تون ڇيئي وارن مان ٿيندين.'),
(1460, 10, 96, 'بيشڪ جن تي پنھنجي پالڻھار (جي عذاب) جو حُڪم لازم ٿي چڪو آھي سي ايمان نه آڻيندا.'),
(1461, 10, 97, 'جيتوڻيڪ وٽن سڀ دليل اچن جيستائين ته ڏکوئيندڙ عذاب ڏسن.'),
(1462, 10, 98, 'يُونُس جي قوم کانسواءِ ڪن ڳوٺن (وارن عذاب نازل ٿيڻ وقت) ڇونه ايمان آندو ته سندن ايمان کين فائدو ڏئي ھا، جنھن مھل (يونس جي قوم) ايمان آندو (ته اُن وقت) خواريءَ وارو عذاب دُنيا جي حياتيءَ ۾ کانئن لاٿوسون ۽ کين ڪُجھ وقت آسُودو ڪيوسون.'),
(1463, 10, 99, '۽ جيڪڏھن تنھنجو پالڻھار گھري ھا ته جيڪي زمين ۾ آھن سي سڀ سمورا ضرور ايمان آڻين ھا، پوءِ تون ماڻھن کي زور ڪندين ڇا ته مؤمن ٿين؟'),
(1464, 10, 100, '۽ ڪنھن ماڻھوءَ کي الله جي گھُر کانسواءِ مؤمن ٿيڻ جو اختيار نه آھي، ۽ جيڪي نه ڄاڻندا آھن تن تي (ڪفر جي) پليتي رکندو آھي.'),
(1465, 10, 101, 'چؤ ته نھاريو ته آسمانن ۽ زمين ۾ ڪھڙيون (سندن قدرتون) آُھن؟ ۽ نه مڃيندڙ قوم کي نشانيون ۽ ڊيڄارڻ فائدو نه ڏيندا آھن.'),
(1466, 10, 102, 'پوءِ اُنھن جھڙين مُصيبتن کانسواءِ انتظار نه ڪندا آھن جيڪي کانئن اڳ گذري ويون، (کين) چؤ ته انتظار ڪريو آءٌ به اوھان سان گڏ انتظار ڪندڙن مان آھيان.'),
(1467, 10, 103, 'وري اسين پيغمبرن کي ۽ جن ايمان آندو تن کي بچائيندا آھيون اھڙي طرح اسان تي حق آھي ته مؤمنن کي بچايون.'),
(1468, 10, 104, 'چؤ ته اي ماڻھؤ جيڪڏھن منھنجي دين کان شڪ ۾ آھيو ته (ٻُڌو ته) الله کانسواءِ جن کي پوڄيندا آھيو تن کي نه پوڄيندس پر اُن الله کي پوڄيندس جيڪو اوھان کي ماريندو آھي، ۽ مُون کي حڪم ڪيو ويو آھي ته مؤمنن مان ھجان.'),
(1469, 10, 105, '۽ (ھيءَ) ته پنھنجي مھاڙ کي حنيف دين لاءِ بيھڪ وٺاءِ، ۽ مُشرڪن مان نه ھُج.'),
(1470, 10, 106, '۽ الله کانسواءِ انھي جي عبادت نه ڪر جيڪو توکي نڪي سُک پُھچائي ۽ نڪي توکي ڏُک پھچائي، پوءِ جيڪڏھن (ائين) ڪندين ته اُنھي مھل تون ظالمن مان ٿيندين.'),
(1471, 10, 107, '۽ جيڪڏھن الله توکي ڪو ڏک پھچائي ته اُن ڌاران ڪو اُن جي لاھڻ وارو ڪونھي، ۽ جيڪڏھن تولاءِ چڱائي گھري ته سندس فضل ڪو ھٽائڻ وارو نه آھي، پنھنجي ٻانھن مان جنھن کي وڻيس تنھن کي فضل پُھچائيندو آھي، ۽ اُھو بخشڻھار مھربان آھي.'),
(1472, 10, 108, 'چؤ ته اي ماڻھؤ بيشڪ اوھان وٽ اوھان جي پالڻھار کان حق آيو آھي، پوءِ جيڪو ھدايت وارو ٿيو سو پاڻ لاءِ ھدايت ٿو لھي، ۽ جيڪو ڀُلو تنھن تي گمراھيءَ ھجڻ کانسواءِ (ٻيو ڪي) نه آھي، ۽ آءٌ اوھان جو ذمي وار نه آھيان.'),
(1473, 10, 109, '۽ جيڪي توڏانھن وحي ڪيو وڃي ٿو تنھنجي تابعداري ڪر ۽ (ايستائين) صبر ڪر جيسين الله نبيرو ڪري، ۽ اُھو چڱو نبيرو ڪندڙ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الرٰ، (ھي) ڪتاب آھي جنھن جون آيتون مُحڪم آھن وري حِڪمت واري داناء الله وٽان بيان ڪيل آھن.'),
(1475, 11, 2, 'ته الله کانسواءِ ٻئي ڪنھن جي عبادت نه ڪريو، آءٌ سندس طرف کان اوھان کي ڊيڄاريندڙ ۽ خوشخبري ڏيندڙ آھيان.'),
(1476, 11, 3, '۽ ته پنھنجي پالڻھار کان بخشش گھرو وري ڏانھس (توبه ڪري) موٽو ته ٺھرايل مُدّت تائين اوھان کي چڱي نفعي سان فائدو ڏئي ۽ سڀڪنھن ڀلائيءَ واري کي سندس ڀلائي جو عوض ڏئي، ۽ جيڪڏھن اوھين ڦرندؤ ته بيشڪ آءٌ اوھان تي وڏي ڏينھن جي عذاب (پھچڻ) کان ڊڄان ٿو.'),
(1477, 11, 4, 'الله ڏانھن اوھان جو موٽڻ آھي، ۽ اُھو سڀڪنھن شي تي وس وارو آھي.'),
(1478, 11, 5, 'خبردار بيشڪ اُھي پنھنجن سينن کي ھن لاءِ وٽيندا آھن ته کانئس لِڪن، خبردار جنھن دم (اُھِي) پنھنجا ڪپڙا ڍڪيندا آھن (تنھن دم ۾) جيڪي ڳجھو ڪندا آھن ۽ جيڪي پڌرا ڪندا آھن سو (الله) ڄاڻندو آھي، بيشڪ اُھو سينن وارو (ڀيد) ڄاڻندڙ آھي.'),
(1479, 11, 6, '۽ ڪو چُرندڙ زمين ۾ آھي ئي نه پر سندس روزي الله تي آھي ۽ سندس رھڻ جو ھنڌ ۽ سنديس سانڀڻ جي جاءِ (الله) ڄاڻندو آھي، سڀ پڌري ڪتابن ۾ لکيل آھي.'),
(1480, 11, 7, '۽ اُھو (الله) آھي جنھن آسمانن ۽ زمين کي ڇھن ڏينھن ۾ بڻايو ۽ سندس عرش پاڻي تي ھو (اھو بڻائڻ ھن ڪري آھي) ته اوھان کي پرکي ته اوھان مان عملن جي ڪري ڪير تمام چڱو آھي، ۽ جيڪڏھن انھن کي چوين ته موت کانپوءِ اوھين جياريا ويندؤ ته ڪافر ضرور چوندا ته ھي پڌرو جادو آھي.'),
(1481, 11, 8, '۽ جيڪڏھن ڪنھن مُقرّر مُدّت تائين کائن عذاب کڻي ھٽايون ته ضرور چوندا ته اُن کي ڪنھن جھليو آھي؟ خبردار جنھن ڏينھن (اُھو عذاب) وٽن ايندو (تنھن ڏينھن) کائن ٽرڻو نه آھي ۽ جنھن (شيء) سان ٺـٺوليون ڪندا ھوا سا کين وڪوڙي ويندي.'),
(1482, 11, 9, '۽ جيڪڏھن ماڻھوءَ کي پاڻ وٽان ڪا ٻاجھ چکايون ٿا (۽) وري اُھا کانئس کسيون ٿا، ته اُھو نااميد بي شڪر (بڻيو) آھي.'),
(1483, 11, 10, '۽ جيڪڏھن ان کي ڏک پھچڻ کانپوءِ سُک پُھچايون ته ضرور چوندو ته مون کان ڏک ويا، اھو پڪ سرھو وڏائي ڪندڙ ٿيندو آھي.'),
(1484, 11, 11, 'پر جن صبر ڪيو ۽ چڱا ڪم ڪيا، تن لاءِ بخشش ۽ وڏو اجر آھي.'),
(1485, 11, 12, 'پوءِ (اي پيغمبر) جيڪي توڏانھن وحي ڪجي ٿو تنھن مان متان ڪي ڇڏي ڏئين ۽ (پڻ) متان دل تنگ ڪرين ڪافرن جي ھن چوڻ تي ته مٿس خزانو ڇونه لاٿو ويو يا ملائڪ ساڻس ڇونه آيو، (نه ڇڏ ۽ نه دل تنگ ڪر) ڇوته تون ڊيڄاريندڙ ئي آھين ۽ الله ھر شيء جو سنڀاليندڙ آھي.'),
(1486, 11, 13, 'بلڪ (ائين) به چون ٿا ته اُھو (قرآن) پاڻ ٺاھيو اٿس، چؤ ته جيڪڏھن سچا آھيو ته (اوھين به) اُن جھڙيون ڏہ سورتون پاڻون ٺاھيل آڻيو ۽ الله کانسواءِ (ٻـين) جن کي سڏي سگھو تن کي سڏيو.'),
(1487, 11, 14, 'پوءِ جيڪڏھن اوھان جو (اھو چوڻ) قبول نه ڪن ته ڄاڻو ته (اُھو قرآن) رڳو الله جي علم سان لاٿو ويو آھي ۽ (ڄاڻو) ته ان کانسواءِ ٻيو ڪو عبادت جو لائق نه آھي، پوءِ اوھين اسلام تي (مُحڪم) ڇونه رھندؤ.'),
(1488, 11, 15, 'جيڪو دنيا جي حياتي ۽ سندس سينگار گھرندو ھجي تن کي دنيا ۾ (ئي) سندن عملن جو اجورو پورو ڏينداسون ۽ انھن کي اُن ۾ گھٽ نه ڏبو.'),
(1489, 11, 16, 'اِھي اُھي آھن جن لاءِ آخرت ۾ باہ کانسواءِ (ٻيو ڪي) نه آھي، ۽ جيڪي دنيا ۾ ڪيائون سو چٽ ٿيو ۽ جيڪي ڪمائيندا ھوا سو باطل ٿيو.'),
(1490, 11, 17, '(اُھو ماڻھو گمراھن جھڙو نه آھي) جيڪو پنھنجي پالڻھار (جي طرف) کان پڌري حجت تي ھجي ۽ الله کان اُن لاءِ شاھد (يعني قرآن پڙھندو) ھجي ۽ اُن (قرآن) کان اڳ موسىٰ جو ڪتاب (توريت) امام ۽ رحمت (به گواہ ھجي)، اِھي (ماڻھو) اُن (قرآن) تي ايمان آڻين ٿا، (پوءِ انھن جي سڀني) ٽولين مان جيڪو اُن (قرآن) جو انڪار ڪندو تنھن جي رھڻ جو ھنڌ باھ آھي، تنھنڪري (تون) کانئس شڪ ۾ نه ھُج، اُھو تنھنجي پالڻھار جي پار کان حق (لٿو) آھي پر گھڻا ماڻھو ايمان نه ٿا آڻين.'),
(1491, 11, 18, '۽ جيڪو الله تي ڪوڙو ٺاھ ٺاھي تنھن کان وڌيڪ ظالم ڪير آھي، اھي پنھنجي پالڻھار جي آڏو ڪيا ويندا ۽ شاھد چوندا ته اھي آھن جن پنھنجي پالڻھار تي ڪوڙ چيو، خبردار (انھن) ظالمن تي الله جي لعنت آھي.'),
(1492, 11, 19, 'جيڪي الله جي واٽ کان جھليندا آھن ۽ ان جي ڏنگائي گھرندا آھن، ۽ اُھي ئي آخرت کي نه مڃيندا آھن.'),
(1493, 11, 20, 'اُھي مُلڪ ۾ ڪڏھن عاجز ڪرڻ وارا نه آھن ۽ الله کانسواءِ (ٻيا ڪي) انھن جا مددگار نه آھن، انھن کي ٻيڻو عذاب ڪيو ويندو، جو ٻُڌي نه ٿي سگھيا ۽ نه ٿي ڏٺائون.'),
(1494, 11, 21, 'اِھي اُھي آھن جن پاڻ کي ڇيئو لاتو ۽ جيڪو ٺاھ ٺاھيندا ھوا سو کانئن ڀلجي ويو.'),
(1495, 11, 22, 'بيشڪ اِھي (ماڻھو) ضرور آخرت ۾ ڇيئي وارا آھن.'),
(1496, 11, 23, 'بيشڪ جن ايمان آندو ۽ چڱا عمل ڪيا ۽ پنھنجي پالڻھار ڏانھن نويا سي بھشتي آھن، اُھي منجھس سدائين رھڻ وارا آھن.'),
(1497, 11, 24, 'انھن ٻن ٽولين جو مثال انڌي ۽ ٻوڙي ۽ ڏسندڙ ۽ ٻڌندڙ وانگر آھي، (اھي) مثال ۾ برابر آھن ڇا؟ پوءِ ڇونه ٿا نصيحت وٺو.'),
(1498, 11, 25, '۽ بيشڪ اسان نوح کي سندس قوم ڏانھن موڪليو (اُن چيو ته) آءٌ اوھان لاءِ پڌرو ڊيڄارندڙ آھيان.'),
(1499, 11, 26, 'ته الله کانسواءِ (اوھين ٻئي جي) عبادت نه ڪريو، بيشڪ آءٌ اوھان کي ڏکوئيندڙ ڏينھن جي عذاب (پھچڻ) کان ڊڄان ٿو.'),
(1500, 11, 27, 'پوءِ سندس قوم مان ڪافر ٽوليءَ چيو ته توکي پاڻ جھڙي ماڻھو کانسواءِ (ٻيو) نٿا ڏسون ۽ اسان مان بي سوچ خسيسن کان سواءِ (ٻيو) تنھنجو تابعدار نه ٿا ڏسون، ۽ پاڻ کان اوھان جي ڪا زيادتي نه ٿا ڏسون بلڪ اوھان کي ڪوڙ ڀائيندا آھيون.'),
(1501, 11, 28, '(نوح) چيو ته اي منھنجي قوم (مون کي) ڏسيو ته سھين ته جيڪڏھن (آءٌ) پنھنجي پالڻھار جي پڪي حجت تي ھجان ۽ پاڻ وٽان مون کي رحمت ڏني ھجيس ۽ (جنھن جي حقيقت) اوھان کان لڪائي وئي ھجي ته اُن (جي مڃڻ) جو ھن ھوندي اوھان کي ڇو زور ڪريون جو اوھين اُن کي بڇان ڀائيندا ھجو؟'),
(1502, 11, 29, '۽ اي منھنجي قوم (آءٌ) اوھان کان اُن بابت ڪو مال نٿو گھران، منھنجو اجورو الله کانسواءِ ٻئي تي نه آھي ۽ آءٌ مؤمنن کي تڙڻ وارو نه آھيان، ڇوته اُھي پنھنجي پالڻھار کي ملڻ وارا آھن پر آءٌ اوھان کي جاھل قوم ڏسان ٿو.'),
(1503, 11, 30, '۽ اي منھنجي قوم جيڪڏھن کين تڙيندس ته مون کي الله کان ڪير ڇڏائيندو، پوءِ ڇونه سمجھندا آھيو؟'),
(1504, 11, 31, '۽ آءٌ اوھان کي نٿو چوان ته مون وٽ الله جا خزانا آھن ۽ نڪي (ڪو) ڳُجھ ڄاڻندو آھيان ۽ نڪي چوان ٿو ته آءٌ ملائڪ آھيان ۽ جن کي اوھان جون اکيون ھيڻو ڏسن ٿيون تن لاءِ نٿو چوان ته الله کين ڪا چڱائي نه ڏيندو، جيڪي سندين دلين ۾ آھي سو الله چڱو ڄاڻندڙ آھي، (جي چوندس ته) آءٌ انھيءَ مھل ظالمن مان ٿيندس.'),
(1505, 11, 32, 'چيائون اي نوح بيشڪ تو اسان سان جھڳڙو ڪيو پوءِ اسان سان گھڻو جھيڙو ڪيئي جيڪڏھن سچن مان آھين ته اسان سان جيڪو انجام ڪرين ٿو سو اسان وٽ آڻ.'),
(1506, 11, 33, '(نوح) چيو ته اُھو اوھان وٽ رڳو الله آڻيندو جيڪڏھن اُن گھريو ته ۽ اوھين (ڀڄي) عاجزي ڪرڻ وارا نه آھيو.'),
(1507, 11, 34, '۽ جيڪڏھن آءٌ اوھان کي نصيحت ڏيڻ گھران ته جيڪڏھن الله اوھان جو ڀُلائڻ گھرندو ته منھنجي نصيحت اوھان کي ڪو نفعو نه ڏيندي، اُھو اوھان جو پالڻھار آھي، ۽ ڏانھس موٽايا ويندؤ.'),
(1508, 11, 35, 'بلڪ (ھيئن به) چوندا آھن ته (ھن پيغمبر) اُھو پاڻون ٺاھيو آھي، چئو ته جيڪڏھن مون اُن کي پاڻ ٺاھيو آھي ته منھنجو ڏوھ مون تي آھي ۽ جيڪي اوھين ڏوھ ڪندا آھيو تنھن کان آءٌ بيزار آھيان.'),
(1509, 11, 36, '۽ نُوح ڏانھن وحي ڪيو ويو ته جنھن ايمان آندو آھي تنھن کانسواءِ تنھنجي قوم مان ٻيو ڪوبه ڪڏھن ايمان نه آڻيندو تنھنڪري جيڪي ڪندا آھن تنھن تي تون رنج نه ٿي.'),
(1510, 11, 37, '۽ اسان جي روبرو ۽ اسان جي حُڪم سان ٻيڙي جوڙ ۽ ظالمن بابت مون کي نه چئج، ڇوته اُھي ٻوڙيا ويندا.'),
(1511, 11, 38, '۽ ٻيڙي بڻائڻ لڳو، جڏھن ڪڏھن سندس قوم مان ڪا ٽولي وٽانئس لنگھي ٿي (ته) مٿس ٺـٺوليون ڪيائون، (نوح) ٿي چيو ته جيڪڏھن اوھين اسان تي ٺـٺوليون ڪريو ٿا ته اسين (به) اھڙيون ٺـٺوليون اوھان تي ڪنداسون جھڙيون ٺـٺوليون (اوھين) ڪريو ٿا.'),
(1512, 11, 39, 'پوءِ جنھن کي عذاب پھچندو تنھن کي سگھوئي ڄاڻندؤ جو (اُھو) ان کي خوار ڪندو ۽ مٿس سدائين عذاب (پيو) لھندو.'),
(1513, 11, 40, '(انھيءَ ڪم ۾ ھوا) ايتري تائين جو اسان جو حُڪم آيو ۽ تنور اُڀاميو چيوسون ته سڀڪنھن جنس (جي جانورن) مان ٻه ٻه (نر ۽ مادي) ۽ جن بابت حڪم ٿي چڪو آھي (ته ھلاڪ ڪبا) تن ڌاران ٻيا پنھنجي گھر وارا ۽ جن ايمان آندو سي منجھس چاڙھ، ۽ ٿورن کانسواءِ مٿس ايمان نه آندو ھوائون.'),
(1514, 11, 41, '۽ (نوحؑ) چيو ته منجھس چڙھو الله جي نالي سان سندس ھلڻ ۽ سندس بيھڻ آھي، ڇوته منھنجو پالڻھار ضرور بخشيندڙ مھربان آھي.'),
(1515, 11, 42, '۽ اُھا جبلن جھڙين لھرين ۾ کين (کڻي) ھلڻ لڳي، ۽ نوحؓ پنھنجي پٽ کي سڏيو ۽ اُھو ڪناري تي ھو ته اي مُنھنجا پُٽ اسان سان چڙھ ۽ ڪافرن سان گڏ نه ھُج.'),
(1516, 11, 43, 'چيائين ته (اھڙي) جبل تي پناھ وٺندس جو مون کي پاڻيءَ کان بچائيندو، (نوحؑ) چيو ته اڄ الله جي عذاب کان ڪو بچائيندڙ ڪونھي پر جنھن تي (پاڻ) رحم ڪري (سو بچي سگھندو)، ايتري ۾ ٻنھي جي وچ ۾ لھر اچي پئي ۽ (اُھو) ٻڏلن مان ٿيو.'),
(1517, 11, 44, '۽ فرمايو ويو ته اي زمين (تون) پنھنجو پاڻي چُھي وڃ ۽ اي آسمان (تون پنھنجو پاڻي) بند ڪر ۽ پاڻي سُڪايو ويو ۽ ڪم پورو ڪيو ويو ۽ (ٻيڙي جبل) جوديءَ تي بيٺي ۽ چيو ويو ته (شل) ظالمن جي قوم ناس ٿئي.'),
(1518, 11, 45, '۽ نوحؑ پنھنجي پالڻھار کي سڏيو ۽ چيائين اي منھنجا پالڻھار منھنجو پٽ (به) منھنجي گھر وارن منجھان آھي ۽ بيشڪ تنھنجو انجام بلڪل سچو آھي ۽ تو حاڪمن جو حاڪم آھين.'),
(1519, 11, 46, '(الله) فرمايو ته اي نوح اُھو تنھنجي گھر وارن مان نه آھي، ڇوته اَڻ سڌريل ڪمن وارو آھي، تنھنڪري جنھن جي توکي ڪا خبر نه آھي تنھن بابت مون کان نه پُڇ، آءٌ توکي نصيحت ڪريان ٿو ته متان جاھلن مان ٿئين.'),
(1520, 11, 47, '(نوحؓ) چيو ته اي منھنجا پالڻھار جنھن (ڳالھ جي حقيقت) جي مون کي خبر نه آھي تنھن بابت توکان پڇڻ جي آءٌ تو وٽان پناھ گھران ٿو، ۽ جي تون نه بخشيندين ۽ رحم نه ڪندين ته ڇيئي وارن مان ٿيندس.'),
(1521, 11, 48, 'چيو ويو ته اي نوحؑ اسان جي طرف کان سلامتي ۽ برڪتن سان لھ جي توتي ھجن ۽ انھن ٽولين تي (به) جيڪي توساڻ آھن، ۽ (ٻيون ڪي) جماعتون ھونديون جن کي آسودو ڪنداسون وري انھن (مان ڪافرن) کي اسان وٽان ڏکوئيندڙ عذاب پھچندو.'),
(1522, 11, 49, 'اِھي (قصا) ڳُجھين خبرن مان آھن جي تو ڏانھن وحي ڪريون ٿا، ھن کان اڳ نڪي (ان کي) تون ڄاڻندو ھئين ۽ نڪا تنھنجي قوم ڄاڻندي ھئي، پوءِ صبر ڪريو، بيشڪ پڇاڙي پرھيزگارن لاءِ آھي.'),
(1523, 11, 50, '۽ عادين ڏانھن سندن ڀاءُ ھود کي (موڪليوسون)، چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اُن (الله) کانسواءِ اوھان جو ڪو معبود نه آھي، اوھين رڳو ٺاھ ٺاھيندڙ آھيو.'),
(1524, 11, 51, 'اي منھنجي قوم اوھان کان (ھن پيغام پھچائڻ تي) ڪو اجورو نٿو گھران، منھنجو اجورو رڳو انھي تي آھي جنھن مون کي پيدا ڪيو آھي، پوءِ اوھين ڇونه ٿا سمجھو.'),
(1525, 11, 52, '۽ اي منھنجي قوم پنھنجي پالڻھار کان بخشش گھرو وري سندس اڳيان توبه ڪريو ته اوھان تي لڳولڳ وسندڙ ڪڪر موڪليندو ۽ اوھان جي سگھ تي سگھ وڌائيندو ۽ اوھين ڏوھي ٿي منھن نه ڦيريو.'),
(1526, 11, 53, 'چيائون ته اي ھُود اسان وٽ ڪوئي دليل نه آندو اٿئي ۽ اسين تنھنجي چوڻ تي پنھنجن معبودن کي ڇڏڻ وارا نه آھيون ۽ نڪي توکي مڃيندڙ آھيون.'),
(1527, 11, 54, 'اسين توکي رڳو (ھي ڳالھ) چئون ٿا ته اسان جي ڪِن بُتن توکي ڪي ايذايو آھي، (ھود) چيو بيشڪ آءٌ الله کي شاھد ڪريان ٿو ۽ اوھين به شاھد ھُجو تحقيق آءٌ انھن کان بيزار آھيان جن کي (الله سان) شريڪ بڻائيندا آھيو.'),
(1528, 11, 55, 'اُن (الله) ڌاران پوءِ مون سان سڀئي مدائي ڪريو وري مون کي مُھلت نه ڏيو.'),
(1529, 11, 56, 'تحقيق مون پنھنجي پالڻھار ۽ اوھان جي پالڻھار الله تي ڀروسو ڪيو آھي، ڪو (جاندار) چرندڙ نه آھي جنھنجي پيشانيءَ (جي چڳه) اُن (الله) کانسواءِ ٻيو ڪو (ھٿ ۾) وٺندڙ ھجي بيشڪ منھنجو پالڻھار سڌيءَ واٽ تي آھي.'),
(1530, 11, 57, 'پوءِ جيڪڏھن ڦرندؤ ته (مون کي ڇا ٿيندو) بيشڪ جنھن پيغام سان اوھان ڏانھن موڪليو ويو آھيان سو اوھان کي پھچايم، ۽ منھنجو پالڻھار اوھان کانسواءِ ٻيءَ قوم کي اوھان جي جاءِ تي وھاريندو، ۽ اوھين کيس ڪُجھ ذرو نقصان پھچائي نه سگھندؤ، بيشڪ منھنجو پالڻھار سڀني شين تي نگھبان آھي.'),
(1531, 11, 58, '۽ جنھن مھل اسان جو عذاب پھتو (تنھن مھل) ھود کي ۽ جن ساڻس ايمان آندو تن کي پنھنجي ٻاجھ سان بچايوسون، ۽ کين سخت عذاب کان ڇڏايوسون.'),
(1532, 11, 59, '۽ اھو حال عادين جو آھي جو پنھنجي پالڻھار جي نشانين جو انڪار ڪيائون ۽ پنھنجي پيغمبرن جي نافرماني ڪيائون ۽ سڀڪنھن ھٺيلي فساديءَ جي حُڪم جا تابعدار ٿيا.'),
(1533, 11, 60, '۽ ھن دُنيا ۾ (به) ۽ قيامت جي ڏينھن (به) پويان لعنت لاتي وين، خبردار تحقيق عادين پنھنجي پالڻھار جو انڪار ڪيو، خبردار عادين کي لعنت ھجي جو ھود جي قوم ھوا.'),
(1534, 11, 61, '۽ ثمود جي قوم ڏانھن سندن ڀاءُ صالحؑ کي موڪليوسون، چيائين ته اي منھنجي قوم الله جي عبادت ڪريو ان کانسواءِ (ڪو ٻيو) معبود نه آھي، اُنھيءَ اوھان کي زمين مان پيدا ڪيو ۽ منجھس اوھان کي آباد ڪيائين تنھنڪري کانئس بخشش گھرو وري سندس اڳيان توبه ڪريو، بيشڪ منھنجو پالڻھار ويجھو (۽) قبول ڪندڙ آھي.'),
(1535, 11, 62, 'چيائون ته اي صالح ھن کان اڳ بيشڪ اسين تو ۾ (ڪيئي) اُميدون رکندا ھواسون (جي ھاڻي بند ٿيون) اسان جي ابن ڏاڏن جن کي پوڄيو تن جي پوڄڻ کان اسان کي ڇو ٿو جھلين؟ ۽ جنھن ڏانھن اسان کي سڏين ٿو تنھن کان اسين بلڪل شڪ ۾ پيل آھيون.'),
(1536, 11, 63, '(صالح) چيو ته اي منھنجي قوم (مون کي اوھين) ڏسو ته سھين ته جيڪڏھن آءٌ پنھنجي پالڻھار کان چٽيءَ حُجت تي ھجان ۽ پنھنجي طرف کان مون تي ٻاجھ ڪئي ھجيس ته جيڪڏھن (آءٌ اوھان جو چيو مڃي) سندس نافرماني ڪريان ته الله کان مون کي ڪير ڇڏائيندو؟ پوءِ اوھان مون لاءِ رڳو نقصان وڌايو؟'),
(1537, 11, 64, '۽ اي منھنجي قوم ھيءَ الله جي ڏاچي اوھان لاءِ نشاني (مُعجزو) آھي پوءِ ان کي ڇڏيو ته الله جي زمين ۾ (جتي وڻيس اُتي) چَري ۽ ان کي ڪو ايذاءُ نه پھچايو نه ته پوءِ اوھان کي ستت ئي عذاب اچي وٺندو.'),
(1538, 11, 65, 'پوءِ اُن جو کُچون ڪپي (ماري) وڌائون پوءِ (صالحؑ) چيو ته اوھين پنھنجن گھرن ۾ (ڇڙا) ٽي ڏينھن فائدو وٺو، اُھو فائدو ڪوڙو نه آھي.'),
(1539, 11, 66, 'پوءِ جنھن مھل اسان جو عذاب آيو (تنھن مھل) صالح ۽ جن ساڻس ايمان آندو ھو تن کي پنھنجي ٻاجھ سان بچايوسون ۽ قيامت جي ڏينھن جي خواري کان (به) تحقيق تنھنجو پالڻھار طاقت وارو ۽ غالب آھي.'),
(1540, 11, 67, '۽ (انھن) ظالمن کي ھڪ خوفناڪ آواز ورتو پوءِ صبح جو پنھنجن حويلين ۾ ڪرونڊڙا ٿي مري ويا.'),
(1541, 11, 68, 'ڄڻڪ منجھس رھيا ئي نه ھوا، خبردار ثمودي پنھنجي رب کان منڪر ٿيا، ٻڌو! ثمودين تي لعنت ھجي.'),
(1542, 11, 69, '۽ بيشڪ اسانجا قاصد ابراھيمؑ وٽ خوشخبري (ڏيڻ) لاءِ آيا (پوءِ) سلام ڏنائون، (ابراھيمؑ) سلام ورايو پوءِ ڀُڳل گابي (جي گوشت آڻڻ) ۾ ڪا دير نه ڪيائين.'),
(1543, 11, 70, 'پوءِ جنھن مھل سندن ھٿن کي اُن (گوشت) ڏانھن نه پھچندو ڏنائين (تنھن مھل) کين اوپرو ڀانيائين ۽ کائن دل ۾ ڊنون چيائون ته نه ڊڄ اسان کي لُوط جي قوم ڏانھن موڪليو ويو آھي.'),
(1544, 11, 71, '۽ سنديس زال جا بيٺل ھُئي سا کِلي پوءِ ان کي اسحاق جي (ڄمڻ جي) ۽ اسحاق جي پٺيان يعقوب جي مُبارڪ ڏنيسون.'),
(1545, 11, 72, 'چيائين ته مون لاءِ افسوس آھي آءٌ ھن حالت ۾ ڄڻينديس ڇا جو آءٌ پاڻ ڪراڙي جھور آھيان ۽ ھيءُ منھنجو مڙس ٻڍو آھي، ھي ته ھڪ عجيب ڳالھ آھي.'),
(1546, 11, 73, 'چيائون ته الله جي حُڪم تي عجب ڇو ٿي ڪرين اي ھن گھر وارؤ اوھان تي الله جي ٻاجھ ۽ سندس برڪتون آھن، بيشڪ اھو (الله) ساراھيل وڏائيءَ وارو آھي.'),
(1547, 11, 74, 'پوءِ جنھن مھل ابراھيمؑ کان ڀؤ لٿو ۽ خوشخبري پُھتس (ته اُن وقت) لُوط جي قوم بابت اسان سان تڪرار ڪرڻ لڳو.'),
(1548, 11, 75, 'ڇوته اِبراھيم بُردبار نرم دل (الله ڏانھن) موٽندڙ ھو.'),
(1549, 11, 76, '(چيوسون ته) اي ابراھيمؑ اِن خيال کان مُڙي وڃ، تحقيق تنھنجي پالڻھار جو حُڪم اچي چڪو آھي، ۽ کين اَڻ ٽر عذاب اچڻ وارو آھي.'),
(1550, 11, 77, '۽ جڏھن اسان جا قاصد لُوط وٽ آيا (تڏھن) سندن (اچڻ) سببان خفي ٿيو ۽ انھن جي سببان تنگدل ٿيو ۽ چيائين ته ھي ڏينھن سخت آھي.'),
(1551, 11, 78, '۽ سندس قوم ڏانھس ڊوڙندي آئي، ۽ (اُھي) اڳي بُڇڙا ڪم ڪندا ھئا، چيائين ته اي منھنجي قوم ھيءُ منھنجون ڌيئرون اوھان جي (نڪاح) لاءِ ڏاڍيون سٺيون آھن پوءِ الله کان ڊڄو ۽ منھنجي مھمانن (سان بڇڙي ھلت ڪري) ۽ مونکي لڄي نه ڪريو، اوھان ۾ ڪو ڀلارو مڙس ڪونھي ڇا (جو اوھان کي جھلي)؟'),
(1552, 11, 79, 'چيائون ته تون پڪي طرح ڄاڻي چڪو آھين ته اسان کي تنھنجي ڌيئرن ۾ ڪا احتياجي ڪانه آھي، ۽ توکي ته معلوم آھي جو اسين گھرون ٿا.'),
(1553, 11, 80, '(لُوط) چيو ته جيڪڏھن مون کي اوھان سان مقابلي جي سگھ ھجي ھا يا ڪنھن ڏاڍي اوٽ جي پناھ وٺان ھا (ته جيڪي ڪرڻو ھو سو ڪريان ھا).'),
(1554, 11, 81, '(ملائڪن) چيو ته اي لُوط اسين تنھنجي پالڻھار جا موڪليل آھيون (اُھي) تو وٽ ڪڏھن نه پھچي سگھندا پوءِ (تون) پنھنجي گھر وارن سوڌو ڪجھ رات ڏئي نِڪر ۽ اوھان کي جڳائي ته ڪو ھڪڙو پوءِ تي ڪافرن جي عذاب ڏانھن نه واجھائي پر تنھنجي زال (واجھائيندي)، ھن ڪري ته جو (عذاب) اُن (ڪافرن جي) قوم کي پھچندو سو اُن کي (به) پھچڻو آھي، سندن (عذاب پھچڻ جي) انجام جي مھل صبح آھي، صُبح ويجھو نه آھي ڇا؟'),
(1555, 11, 82, 'پوءِ جنھن مھل اسان جو حُڪم پھتو ته اُن (ڳوٺ) کي ھيٺ مٿي ڪيوسون ۽ اُن تي پڪي مٽيءَ جا ڳوڙھا لڳولڳ وسايا سون.'),
(1556, 11, 83, 'جو تنھنجي پالڻھار وٽان نشان ٿيل ھوا، ۽ اُھا (سزا) ظالمن کان پري نه ھئي.'),
(1557, 11, 84, '۽ مدين (جي قوم) ڏانھن سندن ڀاءُ شعيبؑ کي موڪليوسون، (شعيب) چيو ته اي منھنجي قوم الله جي عبادت ڪريو اُن کانسواءِ اوھان جو ڪو معبُود ڪونھي، ۽ ماڻ ۽ تور گھٽائي نه ڏيو آءٌ اوھان کي آسودو ڏسان ٿو آءٌ اوھان تي وڪوڙيندڙ ڏينھن جي عذاب (پھچڻ) کان ڊڄان ٿو.'),
(1558, 11, 85, '۽ اي منھنجي قوم ماڻ ۽ تور انصاف سان پوري ڏيو وٺو ۽ ماڻھن کي سندين شيون گھٽائي نه ڏيو ۽ نڪي مُلڪ ۾ فسادي ٿي بگيڙو وجھو.'),
(1559, 11, 86, 'جيڪڏھن اوھين مؤمن آھيو ته الله جي بچايل بچت اوھان لاءِ ڀلي آھي، ۽ آءٌ اوھان تي نگھبان نه آھيان.'),
(1560, 11, 87, 'چيائون ته اي شعيب تنھنجي نماز توکي حڪم ڪيو آھي ڇا ته جنھن کي اسان جا ابا ڏاڏا پوڄيندا ھوا تنھن کي يا پنھنجن مالن ۾ جيڪي ڪرڻ گھرندا آھيون سو ڇڏي ڏيون، (چٿر ڪري چيائون) ته بيشڪ بردبار سنئين رستي وارو آھين.'),
(1561, 11, 88, '(شعيبؑ) چيو ته اي منھنجي قوم (اوھين ڏسو ته سھي ته آءٌ) جيڪڏھن پنھنجي پالڻھار کان چِٽي حُجّت تي ھجان ۽ پنھنجي پار کان چڱي روزي مون کي ڏني ھجيس (ته اوھان جو چيو ڪئن مڃيندس) ۽ جنھن کان اوھان کي جھليان ٿو تنھن کان اوھان (کي جھلڻ) جي ابتڙ ھلڻ نٿو گھران، جيڪي ٿي سگھيم سو سڌارڻ کانسواءِ نه گھرندس، ۽ الله (جي فضل) کانسواءِ مون کي ڪا سگھ نه آھي، مٿس ڀروسو ڪيم ۽ ڏانھس موٽان ٿو.'),
(1562, 11, 89, '۽ اي منھنجي قوم اوھان کي مون سان دشمني ڪرڻ اھڙي عذاب جو سبب نه بڻجي جھڙو عذاب نوح جي قوم يا ھود جي قوم يا صالح جي قوم کي پُھتو، ۽ لُوط جي قوم اوھان کان ڪا پري نه آھي.'),
(1563, 11, 90, '۽ پنھنجي پالڻھار کان بخشش گھرو وري اڳيانس توبه ڪريو، تحقيق منھنجو پالڻھار مھربان (سڀڪنھن جو) سڄڻ آھي.'),
(1564, 11, 91, 'چيائون ته اي شعيب جيڪي تون چوين ٿو تنھن مان گھڻو نٿا سمجھون ۽ توکي پاڻ مان بلڪل ھيڻون ڏسون ٿا، ۽ جيڪڏھن تنھنجو ڪڙم نه ھجي ھا ته توکي پٿرن سان ماريون ھا، ۽ تون اسان تي ڪو سردار نه آھين.'),
(1565, 11, 92, '(شعيب) چيو ته اي منھنجي قوم اوھان وٽ منھنجو ڪڙم الله کان وڌيڪ پيارو آھي ڇا؟ ۽ اُن (الله جي حُڪم) کي پنھنجي پٺيءَ پويان اُڇلي ڇڏيو اٿوَ، تحقيق جيڪي ڪندا آھيو سو منھنجي پالڻھار جي قابو ۾ آھي.'),
(1566, 11, 93, '۽ اي منھنجي قوم اوھين پنھنجي پر تي عمل ڪريو آءٌ (به) عمل ڪندڙ آھيان، سگھوئي ڄاڻندؤ ته ڪنھن کي عذاب پھچي ٿو جو کيس خوار ڪندو ۽ جيڪو ڪوڙو آھي تنھن کي به، ۽ انتظار ڪريو آءٌ (به) اوھان سان گڏ انتظار ڪندڙ آھيان.'),
(1567, 11, 94, '۽ جنھن مھل اسان جو عذاب آيو تنھن مھل شعيبؑ کي ۽ جن ساڻس ايمان آندو ھو تن کي پنھنجي ٻاجھ سان بچايوسون، ۽ ظالمن کي سخت ڪڙڪي (اچي) پڪڙيو پوءِ پنھنجن گھرن ۾ گوڏن ڀر مري ويا.'),
(1568, 11, 95, 'ڄڻڪ منجھن رھيا ئي نه ھوا، خبردار مدين وارن تي ڦِٽ ھجي جھڙي ثمود قوم تي لعنت ٿي.'),
(1569, 11, 96, '۽ بيشڪ موسىٰ کي پنھنجين نشانين ۽ پڌري حُجّت سان موڪليوسون.'),
(1570, 11, 97, 'فرعون ۽ سندس سردارن ڏانھن پوءِ (سڀ ماڻھو) فرعون جي حُڪم جي پويان لڳا، ۽ فرعون جو حُڪم سڌو نه ھو.'),
(1571, 11, 98, 'قيامت جي ڏينھن (فرعون) پنھنجي قوم جو اڳواڻ ٿيندو پوءِ کين باھ ۾ گھيڙيندو، ۽ جنھن گھٽ ۾ گھيڙيا ويندا سو بڇڙو آھي.'),
(1572, 11, 99, '۽ ھن (دنيا) ۾ (به) لعنت انھن کي پويان لڳائي ويئي ۽ قيامت جي ڏينھن (به) جيڪو (لعنت جو) انعام ملين سو بڇڙو آھي.'),
(1573, 11, 100, '(اي پيغمبر) اھي ڳوٺن جون خبرون آھن جي توتي بيان ڪريون ٿا انھن مان (ڪي) قائم (آھن) ۽ (ڪي) ناس ٿيل.'),
(1574, 11, 101, '۽ اسان انھن تي ظلم نه ڪيو پر ھنن پاڻ تي (پاڻھين) ظلم ڪيو پوءِ جنھن مھل تنھنجي پالڻھار جو حُڪم پھتو (ته) سندن معبودن جن کي الله کان سواءِ سڏيندا ھوا کائن ڪُجھ به نه ٽاريو، ۽ ھلاڪي کانسواءِ (ٻيو ڪي) کين نه وڌايائون.'),
(1575, 11, 102, '۽ ائين تنھنجي پالڻھار جو اُن مھل ڳوٺن (وارن) کي اُن حالت ۾ پڪڙڻ ٿيندو آھي جڏھن اُھي ظُلم ڪندڙ ٿيندا آھن، بيشڪ سندس پڪڙ سخت ڏکوئيندڙ آھي.'),
(1576, 11, 103, 'بيشڪ ان (ڳالھ) ۾ انھيءَ لاءِ نشاني آھي جو آخرت جي عذاب کان ڊنو، اِھو اُھو ڏينھن آھي جنھن ۾ ماڻھن کي ميڙبو ۽ اِھو اُھو ڏينھن آھي جنھن ۾ (سڀ) حاضر ٿيندا.'),
(1577, 11, 104, '۽ مُقرّر مُدت کانسواءِ ان کي نه ٿا ڍر ڏيون.'),
(1578, 11, 105, 'جنھن ڏينھن (اُھو) ايندو ته ڪو ماڻھو اُن (الله) جي موڪل کانسواءِ نه ڳالھائيندو، پوءِ اُنھن مان ڪي بدبخت ھوندا ۽ ڪي نيڪ بخت ھوندا.'),
(1579, 11, 106, 'پوءِ جيڪي بدبخت ٿيا سي باہ ۾ (گھڙندا) انھن کي منجھس چيڪَ ۽ دانھَن ھوندي.'),
(1580, 11, 107, 'مگر جيترو وقت تنھنجي پالڻھار گھريو ان کانسواءِ آسمانن ۽ زمين جي بقا ھجڻ تائين منجھس سدائين رھندا، بيشڪ تنھنجو پالڻھار جيڪي گھرندو آھي سو ڪندو آھي.'),
(1581, 11, 108, '۽ جيڪي نيڪ بخت ڪيا ويا سي بھشت ۾ (گھڙندا) مگر جيترو وقت تنھنجي پالڻھار گھريو (ان کانسواءِ) آسمانن ۽ زمين جي بقا ھجڻ تائين منجھس سدائين رھندا، (اِھا) بخشش اَڻ کٽ آھي.'),
(1582, 11, 109, 'تنھنڪري اِھي جيڪا پوڄا ڪندا آھن تنھن کان تون شڪ ۾ نه رھ، جھڙو سندن پيءُ ڏاڏا اڳ پوڄيندا ھوا اِن کانسواءِ اِھي نه پوڄيندا آھن، ۽ اسين کين اَڻ کٽ (عذاب) جو سندن پورو حصّو ڏيندڙ آھيون.'),
(1583, 11, 110, '۽ بيشڪ موسىؑ کي ڪتاب ڏنوسون پوءِ منجھس اختلاف پيو، ۽ جيڪڏھن تنھنجي پالڻھار جو حڪم اڳي نه ٿي چڪي ھا ته سندن وچ ۾ فيصلو ڪيو وڃي ھا، ۽ اُھي کانئس وڏي شڪ ۾ پيل آھن.'),
(1584, 11, 111, '۽ جنھن مھل (اُٿاريا ويندا ته) سڀڪنھن کي تنھنجو پالڻھار سندن اعمالن جو پورو بدلو ڏيندو، ڇوته جيڪي ڪندا آھن تنھنجي اُھو خبر رکندڙ آھي.'),
(1585, 11, 112, 'پوءِ جئن توکي حُڪم ڪيو ويو آھي تئن تون مُحڪم رھ ۽ جن توسان توبه ڪئي (سي به محڪم رھن) ۽ حد کان نه لنگھو، بيشڪ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.'),
(1586, 11, 113, '۽ ظالمن ڏانھن لاڙو نه ڪريو نه ته اوھان کي باھ لڳندي ۽ الله کان سواءِ ڪو اوھان جو دوست نه آھي وري مدد نه ڏبوَ.'),
(1587, 11, 114, '۽ ڏينھن جي اڳياڙيءَ ۽ پوياڙي جي وقتن ۾ ۽ ڪو وقت رات ۾ (به) نماز پڙھ، تحقيق چڱايون مداين کي نينديون آھن، نصيحت وٺندڙن لاءِ اِھا نصيحت آھي.'),
(1588, 11, 115, '۽ صبر ڪر بيشڪ الله ڀلارن جو اجر نه وڃائيندو آھي.'),
(1589, 11, 116, 'پوءِ اوھان کان اڳ اڳين صدين وارن مان (گھڻا) سمجھ ڀريا ڇونه ٿيا جو (ماڻھن کي) ملڪ ۾ فساد (وجھڻ) کان جھلين ھا پر ڪي ٿورڙا ٿيا جن کي منجھائن بچايوسون، ۽ جيڪي ظالم ھوا سي جنھن (مال) ۾ آسودا ڪيا ويا تنھنجي پٺيان لڳا ۽ اُھي ڏوھاري ھوا.'),
(1590, 11, 117, '۽ (اي پيغمبر) تنھنجو پالڻھار اھڙو نه آھي جو ڳوٺن کي سندن سڌريل رھاڪُن ھوندي ظلم سان ناس ڪري ڇڏي.'),
(1591, 11, 118, '۽ جيڪڏھن تنھنجو پالڻھار گھري ھا ته (سڀني) ماڻھن کي ھڪ ئي مذھب (تي) ڪري ھا ۽ (اُھي) سدائين مختلف (مذھب تي) رھندا.'),
(1592, 11, 119, 'پر جن تي تنھنجي پالڻھار رحم ڪيو (سي ھڪ ئي مذھب تي رھندا)، ۽ کين انھيءَ لاءِ (الله) پيدا ڪيو آھي، ۽ تنھنجي پالڻھار جو ٻولُ پورو ٿيو جو جنن ۽ ماڻھن مان گڏي دوزخ ضرور ڀريندس.'),
(1593, 11, 120, '۽ (اي پيغمبر) پيغمبرن جي خبرن مان تو وٽ ايترو بيان ڪريون ٿا جنھن سان تنھنجي دل کي مضبوط ڪريون، ۽ اُنھن (خبرن) ۾ حق ۽ مؤمنن لاءِ نصيحت ۽ سمجھاڻي آئي آھي.'),
(1594, 11, 121, '۽ (اي پيغمبر) جيڪي نه مڃيندا آھن تن کي چؤ ته اوھين پنھنجي جاءِ عمل ڪريو، اسين (به) عمل ڪندڙ آھيون.'),
(1595, 11, 122, '۽ انتظار ڪريو، اسين (به) انتطار ڪندڙ آھيون.'),
(1596, 11, 123, '۽ آسمانن ۽ زمين جو ڳُجھ رڳو الله کي (معلوم) آھي ۽ سڀ ڪم ڏانھس موٽايو ويندو آھي پوءِ سندس عبادت ڪر ۽ مٿس ڀروسو رک، ۽ جيڪي اوھين ڪندا آھيو تنھن کان تنھنجو پالڻھار بي خبر نه آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الرٰ، ھي پڌري ڪتاب جون آيتون آھن.'),
(1598, 12, 2, 'بيشڪ اسان اُن کي قُرآن عربي (ٻوليءَ وارو) ڪري نازل ڪيو ته مانَ اوھين سمجھو.'),
(1599, 12, 3, '(اي پيغمبر) اسين توڏانھن ھن قرآن ۾ پنھنجي وحي موڪلڻ سان ڏاڍو چڱو قصّو توکي پڙھي ٻڌايون ٿا، ۽ تون ھن کان اڳ بيشڪ بي خبرن مان ھئين.'),
(1600, 12, 4, '(ياد ڪر) جڏھن يوسفؑ پنھنجي پيءُ کي چيو ته اي ابا مون يارھن تارا ۽ سج ۽ چنڊ ڏٺا انھن کي ڏٺم ته مون کي سجدو ٿا ڪن.'),
(1601, 12, 5, '(يعقوبؑ) چيو ته اي منھنجا پٽ پنھنجو خواب پنھنجن ڀائرن وٽ بيان نه ڪج نه ته تولاءِ ڪو ڦند ڦيريندا، بيشڪ شيطان انسان جو پڌرو ويري آھي.'),
(1602, 12, 6, '۽ اھري طرح تنھنجو پالڻھار توکي سڳورو ڪندو ۽ توکي خوابن جو تعبير سيکاريندو ۽ پنھنجي نعمت توتي ۽ يعقوبؑ جي اولاد تي اھڙي طرح پوري ڪندو جھڙي اڳ تنھنجي پيءُ ڏاڏي ابراھيمؑ ۽ اسحاقؑ تي پوري ڪيائين، بيشڪ تنھنجو پالڻھار ڄاڻندڙ حڪمت وارو آھي.'),
(1603, 12, 7, 'بيشڪ يُوسفؑ ۽ سندس ڀائرن (جي قصي) ۾ پڇندڙن لاءِ (گھڻيون) نشانيون آھن.'),
(1604, 12, 8, 'جڏھن (پاڻ ۾) چيائون ته بيشڪ يُوسفؑ ۽ سندس (سڳو) ڀاءُ (بن يامين) اسان جي پيءُ وٽ اسان کان ڏاڍا پيارا آھن جيتوڻيڪ اسين (گھڻا ڄڻا) ڏاڍي ٽولي آھيون، بيشڪ اسان جو پيءُ پڌري ڀُل ۾ آھي.'),
(1605, 12, 9, '(ھاڻي گھرجي ته) يُوسفؑ کي ڪُھو يا کيس ڪنھن ملڪ ۾ ڦٽي ڪري اچو ته اوھان جي پيءُ جي مھاڙ اوھان لاءِ نرالي ٿئي ۽ اُن (ڪم) کان پوءِ اوھين سڌريل ٿجو.'),
(1606, 12, 10, 'منجھائن (ھڪ) چوندڙ چيو ته يُوسف کي نه ماريو ۽ جيڪڏھن (ڪي) ڪندڙ آھيو ته کوھ جي تري ۾ (کڻي) اُڇلايوس ته ڪو واٽھڙو کيس ڪڍي ويندو.'),
(1607, 12, 11, '(يعقوبؑ کي) چيائون ته اي بابا توکي ڇا ٿيو آھي يُوسفؑ جي نسبت ۾ توکي اسان تي ھن ھوندي به ڪو ڀروسو نٿو ٿئي جو اسين سندس خيرخواہ آھيون.'),
(1608, 12, 12, 'سڀاڻي اسان سان کيس موڪل ته کائي ۽ کيڏي ۽ اسين سندس نگھبان آھيون.'),
(1609, 12, 13, '(يعقوبؑ) چيو ته اوھان جو کيس نئَڻ مون کي ضرور مونجھو ٿو ڪري ۽ ڊڄان ٿو ته (متان) کيس بگھڙ کائي ۽ کانئس اوھين بي خبر ھجو.'),
(1610, 12, 14, 'چيائون ته اسان جي (گھڻن ڄڻن جي) ڏاڍي ٽولي ھوندي به جيڪڏھن کيس بگھڙ کاڌو ته اسين انھيءَ مھل ضرور ٽوٽي وارا ھونداسون.'),
(1611, 12, 15, 'پوءِ جنھن مھل کيس نيائون (ته) اُن کي کوھ جي تري ۾ اڇلڻ جي پڪ ڪيائون، ۽ ڏانھس وحي ڪيوسون ته سندن ھن واقعي جي کين ضرور سڌ ڏيندين ۽ اُھي (توکي) نه ڄاڻندا.'),
(1612, 12, 16, '۽ پنھنجي پيءُ وٽ رات جو روئيندا آيا.'),
(1613, 12, 17, 'چيائون ته اي بابا اسين ڊڪون پورائڻ وياسون ۽ يُوسف کي پنھنجي وسلن تي ڇڏيوسون پوءِ اُن کي بگھڙ کاڌو ۽ توڻي اسين سچا آھيون ته به تون اسان کي وسھندڙ نه ٿيندين.'),
(1614, 12, 18, '۽ سندس پھراڻ تي ڪوڙو رت لائي آندائون، (يعقوب) چيو ته (ائين نه آھي) بلڪ اوھان جي نفسن اوھان لاءِ (اُن) ٺاھ کي ٺاھيو آھي، تنھنڪري (مون کي) چڱو صبر ڪرڻ گھرجي، ۽ جيڪي اوھين بيان ڪندا آھيو تنھن تي الله کان مدد گھري وڃي ٿي.'),
(1615, 12, 19, '۽ (اُن کوھ تي) قافلو آيو پوءِ پنھنجي پاڻي ڀريندڙ کي موڪليائون جنھن پنھنجو ٻوڪو وڌو، چيائون وھ واھ! ھي نينگر آھي، ۽ کيس ھڪ (چڱي) وٿ سمجھي لڪايائون، ۽ جيڪي ڪيائون ٿي سو الله ڄاڻندڙ آھي.'),
(1616, 12, 20, '۽ کيس (ڀائرن) ھلڪيءَ بھا سان وڪيو جو ڪي ٿورا پئسا ھوا، سندس حق ۾ بيزاري ڪندڙ ھوا.'),
(1617, 12, 21, '۽ مصر وارن مان جنھن کيس ڳڌو تنھن پنھنجي زال (زليخا) کي چيو ته ھن جي چڱي تعظيم ڪج مانَ اسان کي ڪو فائدو ڏئي يا کيس پٽيلو ڪري رکون، ۽ ا ھڙي طرح يُوسفؑ کي مصر ۾ جاءِ ڏني سون، ۽ ھن لاءِ ته کيس خوابن جو تعبير سيکاريون، ۽ الله پنھنجي ڪم تي غالب آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.'),
(1618, 12, 22, '۽ جنھن مھل (يُوسفؑ) پنھنجي جوانيءَ کي پھتو (ته) حِڪمت ۽ علم ڏنوسون، ۽ اھڙي طرح ڀلارن کي بدلو ڏيندا آھيون.'),
(1619, 12, 23, '۽ جنھن زال جي گھر ۾ اُھو ھو تنھن سندس نفس کي راڻيو ٿي ۽ دروازا بند ڪيائين ۽ چيائين ته اوري آءُ، (يوسف) چيائين ته الله جي پناھ گھريم انھي منھنجي نپائيندڙ (يعني عزيز) مون کي چڱي ھنڌ رکيو آھي، ھيءَ (ڳالھ) سچي آھي ته ظالم نه ڇٽندا.'),
(1620, 12, 24, '۽ بيشڪ (اُھا) ان ڏانھن لڙي ۽ (يُوسفؑ) به اُن ڏانھن لڙيو، جيڪڏھن پنھنجي پالڻھار جو دليل نه ڏسي ھا (ته جيڪي ٿيڻو ھو سو ٿئي ھا)، اھڙي طرح (انھي ڪري ٿيو) ته بڇڙائي ۽ بي حيائي کانئس ٽاري سون، سچ آھي ته اھو اسان جي خالص ٻانھن مان ھو.'),
(1621, 12, 25, '۽ ٻئي دروازي ڏانھن ڊوڙيا ۽ زال ان (يُوسف) جو پھراڻ پٺيءَ کان ڦاڙيو ۽ سندس مڙس کي دروازي وٽ مليا، (زليخا) چيو ته جيڪو تنھنجي گھر واريءَ سان مدائي ڪرڻ جو ارادو ڪري تنھنجي سزا قيد ڪرڻ يا ڏکوئيندڙ عذاب کانسواءِ (ٻي ڪا) نه آھي.'),
(1622, 12, 26, '(يُوسفؑ) چيو ته ھن مون کي منھنجي نفس کان راڻيو ٿي ۽ سندس گھر جي ڀاتين مان ھڪ شاھد شاھدي ڏني، ته جيڪڏھن ھن جو پھراڻ اڳ کان ڦاڙيو ويو آھي ته (زليخا) سچي آھي ۽ اُھو ڪوڙن مان آھي.'),
(1623, 12, 27, '۽ جيڪڏھن سندس پھراڻ پٺيءَ کان ڦاڙيو ويو آھي ته (زليخا) ڪوڙي ۽ اھو سچن مان آھي.'),
(1624, 12, 28, 'جنھن مھل (عزيز) پھراڻ پٺيءَ کان ڦاٽل ڏٺو (ته) چيائين ته (اي زالون) اھو (چوڻ) اوھان جي مڪرن مان آھي، ڇوته اوھان جو مڪر وڏو آھي.'),
(1625, 12, 29, '(اي) يوسف اِن (ڳالھ) کان منھن موڙ، ۽ (اي زليخا) تون (به) پنھنجي مدائيءَ جي بخشش گھر، ڇوته تون ئي خطا وارن مان آھين.'),
(1626, 12, 30, '۽ شھر ۾ ڪيترين زالن پچاريو ته عزيز جي زال پنھنجي جوان ٻانھي کي سندس نفس کان (بڇڙي ڪم لاءِ) راڻيندي آھي، بيشڪ (يُوسف جو) پيار سندس دل ۾ پيھي ويو آھي، اسين کيس پڌري گمراھيءَ ۾ ضرور ڏسنديون آھيون.'),
(1627, 12, 31, 'پوءِ جنھن مھل (زليخا) سندن گلا ڪرڻ ٻڌي (ته) انھن ڏانھن (ماڻھو) موڪليائين ۽ انھن لاءِ مجلس تيار ڪيائين ۽ منجھائن ھر ھڪ کي ڪپ ڏنائين ۽ چيائين ته (اي يُوسفؑ) مٿن نِڪر، جنھن دم کيس ڏٺائون ته کيس وڌيڪ (سھڻو) ڄاتائون ۽ پنھنجا ھٿ وڍيائون، ۽ چيائون ته الله پاڪ وڏو آھي ھيءُ (نينگر) ماڻھو نه آھي، پر ھڪ سڳورو ملائڪ آھي.'),
(1628, 12, 32, '(زليخا) چيو اِھو اُھو آھي جنھن (جي محبت) ۾ اوھين مون کي ٽوڪينديون ھُيؤ، ۽ بيشڪ سندس نفس کان کيس راڻيم ٿي پوءِ پاڻ بچايائين، ۽ کيس جيڪي حُڪم ڪريان ٿي سو جيڪڏھن نه ڪندو ته ضرور قيد ڪيو ويندو ۽ ضرور بي فرمانن مان ٿيندو.'),
(1629, 12, 33, '(يُوسفؑ) چيو اي منھنجا پالڻھار جنھن (ڪم) ڏانھن مون کي سڏين ٿيون تنھن کان مون کي قيد پسند آھي، ۽ جيڪڏھن سندن مڪر مون کان نه ٽاريندين ته انھن ڏانھن لڙندس ۽ جاھلن مان ٿيندس.'),
(1630, 12, 34, 'پوءِ سندس پالڻھار ان جي دُعا قبول ڪئي ۽ کانئس اُنھن جو مڪر ٽاريائين، ڇوته اُھو ٻڌندڙ ڄاڻندڙ آھي.'),
(1631, 12, 35, 'وري به نشانين جي ڏسڻ کان پوءِ کين ھيءَ (ڳالھ) نظر آئي ته ڪجھ وقت تائين کيس ضرور قيد وجھن.'),
(1632, 12, 36, '۽ ساڻس ٻه جوان قيد ۾ گھڙيا، انھن مان ھڪ چيو ته مون پاڻ کي (ننڊ ۾) ڏٺو ته شراب نپوڙيان ٿو، ۽ ٻئي چيو ته مون پاڻ کي مٿي تي ماني کڻندو ۽ منجھانئس پکين کي کائيندو ڏٺو، اسان کي اُن جي تعبير جي خبر ڏي، بيشڪ اسين توکي ڀلارن مان ڏسون ٿا.'),
(1633, 12, 37, '(يُوسفؑ) چيو ته جيڪو طعام اوھين کائيندا آھيو سو اوھان وٽ ايندو ئي نه پر اوھان وٽ ان جي اچڻ کان اڳيئي سندس تعبير آءٌ اوھان کي ڏسيندس، اِھو (تعبير ڪرڻ) انھن (ڳالھين) مان آھي جي منھنجي رب مونکي سيکاريون آھن، بيشڪ مون اُن قوم جي پر ڦٽي ڪئي آھي جيڪي الله کي نه مڃيندا آھن ۽ اُھي آخرت جا به مُنڪر آھن.'),
(1634, 12, 38, '۽ پنھنجي پيءُ ڏاڏن ابراھيمؑ ۽ اسحاقؑ يعقوبؑ جي دين جي پيروي ڪئي اٿم، ڪنھن شيءِ کي الله سان شريڪ ڪرڻ اسان کي نه جڳائيندو آھي، اھو اسان تي ۽ ٻـين ماڻھن تي الله جو فضل آھي پر گھڻا ماڻھو شڪرانو نه ڪندا آھن.'),
(1635, 12, 39, 'اي منھنجا سنگتي قيدي (ھيءَ ڳالھ سمجھندا آھيو ته) ڌارو ڌار گھڻا رب چڱا آھن يا اڪيلو غالب الله (چڱو آھي)؟'),
(1636, 12, 40, 'الله کانسواءِ جن جي اوھين پوڄا ڪندا آھيو اُھي رڳو نالا آھن جي اوھان ۽ اوھان جي پيءُ ڏاڏن نالا رکيا آھن انھن جي بابت الله ڪا سندَ نازل نه ڪئي آھي، الله کانسواءِ ٻئي ڪنھن جي حڪومت نه آھي، حُڪم ڪيائين ته خاص اُن (الله) کانسواءِ ڪنھن جي عبادت نه ڪريو، اُھو دين قائم آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.'),
(1637, 12, 41, 'اي منھنجا قيدي سنگتي اوھان مان ھڪڙو پنھنجي بادشاھ کي شراب پياريندو، ۽ ٻيو سوريءَ چاڙھيو ويندو ۽ سندس مٿي مان پکي کائيندا، جنھن بابت پڇو ٿا سو ڪم پورو ٿي ويو.'),
(1638, 12, 42, '۽ انھن ٻنھي مان جنھن لاءِ گمان ھوس ته (نيٺ) ڇٽندو تنھن کي چيائين ته پنھنجي بادشاھ وٽ مون کي ياد ڪج، پوءِ شيطان سندس بادشاھ وٽ کانئس يادگيري وسارائي ڇڏي تنھنڪري (يُوسفؑ) ڪيترائي ورھيه قيد ۾ رھيو.'),
(1639, 12, 43, '۽ بادشاھ چيو ته مون (ننڊ) ۾ ست ٿُلھيون ڳـئون ڏٺيون آھن جن کي ستن ڏٻرين کاڌو ٿي ۽ (پڻ) ست ساوا سنگ ۽ ٻيا ست سُڪا (ڏٺا اٿم)، اي سردارؤ جيڪڏھن اوھين خواب جو تعبير ڪندا آھيو ته منھنجي خواب جو مون کي تعبير ٻڌايو.'),
(1640, 12, 44, 'چيائون (اھي) پريشان خواب آھن، ۽ اسين ڪوڙن خوابن جو تعبير نه ڄاڻندا آھيون.'),
(1641, 12, 45, '۽ جيڪو ٻنھي مان ڇُٽو ھو ۽ گھڻي دير کانپوءِ ياد آيس تنھن چيو ته ان جي تعبير جي آءٌ توکي سُڌ ڏيندس پوءِ مونکي موڪليو.'),
(1642, 12, 46, '(پوءِ بندي خاني ڏانھن ويو چيائين) اي سچا يُوسفؑ ستن ٿلھين ڳئن بابت جن کي ستن ڏٻرين کاڌو ۽ ستن ساون سنگن ۽ ٻـين (ستن) سُڪن بابت تعبير ٻڌاءِ ته آءٌ ماڻھن ڏانھن موٽان ته مانَ اُھي ڄاڻن.'),
(1643, 12, 47, '(يوسف) چيو ته ست ورھيه ساندہ پوک ڪندا رھو، پوءِ جيڪو فصل لُڻو تنھن مان جيڪي کائو تنھن کانسواءِ ٻيو (سڀ) سندن سَنگن ۾ (رکي) ڇڏيو انھن ٿورن کانسواءِ جيڪي اوھين کائو.'),
(1644, 12, 48, 'وري اُن کانپوءِ ست سال سختيءَ جا ايندا انھن لاءِ جيڪي (اَن) اڳي رکي ڇڏيو ھوندوَ سو جيڪي بچائي رکندؤ تنھن مان ٿورڙي کانسواءِ (ٻيو سڀ) کائي ويندا.'),
(1645, 12, 49, 'وري اُن کانپوءِ اھڙو ورھ ايندو جنھن ۾ ماڻھن تي مينھن وسايو ويندو ۽ منجھس (ڊاک وغيرہ) نپوڙيندا.'),
(1646, 12, 50, '۽ بادشاھ چيو ته يُوسفؑ کي مون وٽ آڻيو، جنھن مھل وٽس قاصد آيو (ته ان وقت يُوسفؑ) چيو ته پنھنجي بادشاھ ڏانھن موٽ پوءِ کانئس پُڇ ته انھن زالن جو ڪھڙو حال ھو جن پنھنجا ھٿ وڍيا، ڇوته منھنجو پالڻھار سندن مِڪر کي ڄاڻندڙ آھي.'),
(1647, 12, 51, '(بادشاھ عورتن کان) پڇيو ته اوھان جو ڇا حال ھو جڏھن يُوسفؑ کي سندس نفس کان راڻيوَ ٿي، چيائون ته الله پاڪ آھي ڪا بُڇڙائي مٿس نه ڄاتي سون، عزيز جي زال چيو ته ھاڻي حق پڌرو ٿيو، مون کيس سندس نفس کان راڻڻ گھريو ھو ۽ اُھو بيشڪ سچن مان آھي.'),
(1648, 12, 52, '(يُوسفؑ چيو ته) اِھو ھن لاءِ ھو ته (عزيز) ڄاڻي ته مون پرپٺ سندس خيانت نه ڪئي ھئي ۽ الله خيانت ڪندڙن جو فريب پورو ٿيڻ نه ڏيندو آھي.'),
(1649, 12, 53, '۽ (آءٌ) پنھنجي نفس کي پاڪ نٿو ڀانيان، تحقيق نفس بڇڙائيءَ ڏانھن ڇڪيندو آھي پر جنھن تي مُنھنجو رب رحم ڪري، بيشڪ منھنجو پالڻھار بخشڻھار مھربان آھي.'),
(1650, 12, 54, '۽ بادشاھ چيو ته کيس مون وٽ آڻيو ته (آءٌ) کيس پاڻ لاءِ مقرر ڪريان، پوءِ جنھن مھل ساڻس ڳالھايائين (تنھن مھل سندس سياڻپ سمجھي) چيائين ته تون اڄ اسان وٽ وڏي مرتبي وارو امانت وارو آھين.'),
(1651, 12, 55, '(يوسف) چيو ته مون کي ھن مُلڪ (مصر) جي خزانن تي مقرر ڪر، تحقيق آءٌ نگھبان (سڀني ڪمن جو) وڌيڪ ڄاڻندڙ آھيان.'),
(1652, 12, 56, '۽ اھڙي طرح يُوسف کي (انھي) ملڪ ۾ مرتبي وارو ڪيوسون، جتي وڻندو ھوس تِتي ٽڪندو ھو، جنھن لاءِ وڻندو اٿؤن تنھن تي ٻاجھ پھچائيندا آھيون ۽ نيڪَن جو اجر نه وڃائيندا آھيون.'),
(1653, 12, 57, '۽ جن ايمان آندو ۽ پرھيزگاري ڪندا رھيا تن لاءِ آخرت جو اجر ضرورڀلو آھي.'),
(1654, 12, 58, '۽ يُوسف جا ڀائر آيا پوءِ وٽس ويا ته کين سڃاتائين ۽ اُھي کيس نه سڃاڻندا ھوا.'),
(1655, 12, 59, '۽ جنھن مھل انھن لاءِ سندن سامان تيار ڪيائين (تنھن مھل کين) چيائين ته پنھنجي پيئيتي ڀاءُ کي مون وٽ آڻيو، اوھين نه ٿا ڏسو ڇا ته آءٌ پورو ماڻ ڏيان ٿو ۽ آءٌ چڱو مھماني ڪندڙ آھيان؟'),
(1656, 12, 60, 'پوءِ جيڪڏھن کيس مون وٽ نه آڻيندؤ ته اوھان لاءِ مون وٽ ڪا مئپ ڪانھي ۽ نڪي مون کي ويجھا ٿجو.'),
(1657, 12, 61, 'چيائون سگھو ان بابت (ڪنھن بھاني سان) سندس پيءُ کي راڻينداسون ۽ ضرور اسين (ايئن) ڪرڻ وارا ٿينداسون.'),
(1658, 12, 62, '۽ (يوسف) پنھنجن ماڻھن کي چيو ته سندين موڙي سندين مڏين ۾ وجھي ڇڏيو پوءِ جڏھن پنھنجن گھرن ڏانھن موٽي وڃن مانَ انھي کي سڃاڻن مانَ اُھي وري اچن.'),
(1659, 12, 63, 'پوءِ جنھن مھل پنھنجي پيءُ وٽ (ڪنعان ۾) موٽي آيا (تنھن مھل) چيائون ته اي ابا (اَنَّ) مئي ڏيڻ اسان کان روڪيو ويو آھي تنھنڪري اسان مان اسان جي ڀاءُ (بنيامين) کي موڪل (ته) مئپ وٺون ۽ اسين سندس ضرور نگھبان آھيون.'),
(1660, 12, 64, '(يعقوب کين) چيو ته ھن جي نسبت اوھان تي اھڙو ئي اعتبار ٿو ڪريان جھڙو اڳ سندس ڀاءُ جي به نسبت اوھان تي اعتبار ڪيو ھوم، پوءِ الله چڱو نگھبان آھي، ۽ اُھو ٻاجھارن کان کان وڏو ٻاجھارو آھي.'),
(1661, 12, 65, 'پوءِ جنھن مھل پنھنجو سامان کوليائون (ته اُن وقت) پنھنجي موڙي پاڻ وٽ موٽايل ڏٺائون، چيائون ته اي اسان جا ابا (ٻيو) اسان کي ڇا گھرجي، (جو) ھي اسان جي موڙي اسان ڏانھن وري موٽايل آھي (ته موٽي وڃون)، ۽ پنھنجن گھر وارن لاءِ اَن آڻيون ۽ پنھنجي ڀاءُ جي نگھباني ڪنداسون ۽ ھڪ اُٺ جو بار وڌيڪ به وٺنداسون، اِھو (جيڪي اَن آندو اٿئون سو) ماڻ ٿورو آھي.'),
(1662, 12, 66, '(يعقوب کين) چيو ته (آءٌ) اوھان سان ان کي (ايسين) ڪڏھن نه موڪليندس جيسين مون وٽ انجام ڪري الله جو سُنھن (نه) کڻندؤ ته اوھين ڪنھن اوکائي پوڻ کانسواءِ ان کي مون وٽ ضرور آڻيندؤ، پوءِ جنھن مھل کيس پنھنجو پڪو انجام ڏنائون (ته يعقوب) چيو ته جيڪي اسين چئون ٿا تنھن تي الله نگھبان آھي.'),
(1663, 12, 67, '۽ چيائين ته اي پٽؤ اوھين ھڪ دروازي کان نه گھِڙجو ۽ الڳ الڳ دروازن مان گھڙجو، ۽ آءٌ اوھان کان الله جي تقدير مان ڪابه شيء ٽاري نه ٿو سگھان، الله کانسواءِ (ٻئي) ڪنھن جو حُڪم نه آھي، مٿس ڀروسو ڪيم، ۽ سڀڪنھن ڀروسي ڪندڙ کي مٿس ڀروسو ڪرڻ گھرجي.'),
(1664, 12, 68, '۽ جھڙي طرح سندن پيءُ کين حُڪم فرمايو ھو تھڙي طرح جنھن مھل (مصر جي دروازن کان) گھڙيا، (تنھن مھل جيڪا سختي پھچين ھا) ته سندن پيءُ الله جي تقدير مان کائن ڪجھ به ٽاري نه ٿي سگھيو (اھو حُڪم ڪرڻ) يعقوب جي دل ۾ رڳو ھڪ خيال ھو جو اُن کي پورو ڪيائين، ۽ جيڪي کيس سيکاريو ھوسون تنھن سببان (اُھو) علم وارو ھو پر گھڻا ماڻھو نه ڄاڻندا آھن.'),
(1665, 12, 69, '۽ جنھن مھل يُوسف وٽ ويا تنھن مھل اُن پنھنجي (سڳي) ڀاءُ کي پاڻ وٽ جاءِ ڏني چيائين ته سچ پچ آءٌ تنھنجو ڀاءُ آھيان تنھنڪري جيڪي ڪيو اٿن تنھن تي ڏک نه ڪر.'),
(1666, 12, 70, 'پوءِ جنھن مھل اُنھن لاءِ سندن سامان سنبرايائين (تنھن مھل) پنھنجي (سڳي) ڀاءُ جي مڏيءَ ۾ پيالو لڪايائين وري آواز ڪندڙ آواز ڪيو ته اي قافلي وارؤ اوھين ضرور چور اھيو.'),
(1667, 12, 71, '(قافلي وارن ٻڌي) انھن ڏانھن مُنھن ڪري چيو ته ڇا وڃايو اٿو؟'),
(1668, 12, 72, 'چيائون ته بادشاھ جو پيالو وڃايو اٿون ۽ جنھن اُھو (لھي) آندو تنھن لاءِ اُٺ جو بار (انعام) آھي ۽ آءٌ (ان انجام) جو ذمي وار آھيان.'),
(1669, 12, 73, 'چيائون ته الله جو قسم آھي ته اوھان کي خبر ھوندي ته اسين ھن لاءِ نه آيا آھيون ته مُلڪ ۾ ڏڦيڙ وجھون ۽ نڪي اسين چور آھيون.'),
(1670, 12, 74, '(وري نوڪرن) چيو ته جيڪڏھن ڪوڙا ھُجو ته اُن (چور) جي سزا ڪھڙي آھي؟'),
(1671, 12, 75, 'چيائون ته اُن جي سزا (ھيء) آھي ته جنھنجي مڏيءَ مان اُھو لڌو سو (پاڻ) ان جو بدلو آھي، اھڙي طرح اسين ظالمن کي سزا ڏيندا آھيون.'),
(1672, 12, 76, 'پوءِ (يُوسف) پنھنجي ڀاءُ جي ٻوري (ڳولڻ) کان اڳ انھن (ٻـين) جون ٻوريون ڳولڻ شروع ڪيون وري اُھو (پيالو) پنھنجي ڀاءُ جي ٻوري مان ڪڍيائين، اھڙي طرح يُوسف کي تجويز سيکاري سون، جو بادشاہ جي قانون موجب پنھنجي ڀاءُ کي الله جي گھُر کانسواءِ وٺي نٿي سگھيو، جنھن کي گھرندا آھيون تنھن کي درجن ۾ مٿاھون ڪندا آھيون، ۽ ھر ڪنھن سياڻي جي مٿان سياڻو آھي.'),
(1673, 12, 77, 'چيائون ته جيڪڏھن (ھن) چوري ڪئي آڳي ته اڳ سندس ڀاءُ (به) بيشڪ چوري ڪئي ھئي، پوءِ ان (ڳالھ) کي يوسف پنھنجي دل ۾ لڪايو ۽ کين اُھا پڌري نه ڪيائين، (دل ۾) چيائين ته اوھين بڇڙي مان وارا آھيو، ۽ جيڪي بيان ڪريو ٿا سو الله چڱو ڄاڻندڙ آھي.'),
(1674, 12, 78, 'چيائون ته اي عزيز ھن جو پيءُ پوڙھو وڏيءَ ڄمار وارو آھي اِن ڪري اسان مان ھڪڙو سندس بدران وٺ، اسين توکي نيڪن مان ڏسون ٿا.'),
(1675, 12, 79, 'چيائين الله پناھ ۾ رکي جنھن وٽ پنھنجو سامان لڌوسون تنھن کانسواءِ ٻئي ڪنھن کي پڪڙيون (جيڪڏھن وٺون ته) انھي مھل اسين ظالم آھيون.'),
(1676, 12, 80, 'جنھن مھل کانئس نا اميد ٿيا (تنھن مھل) نويڪلا ٿي صلاح ڪندڙ ٿيا، سندن وڏي (ڀاءُ) چيو ته نه ڄاڻندا آھيو ڇا؟ ته اوھان جي پيءُ اوھان کان الله جو پڪو انجام قسم تي ورتو آھي ۽ اڳ اوھان يوسف جي نسبت ۾ ڪجھ نه گھٽايو ھو، تنھنڪري (جيسين) منھنجو پيءُ مون کي موڪل (نه) ڏيندو يا الله مون لاءِ حُڪم نه ڪندو تيسين آءٌ ھن مُلڪ مان ڪڏھن نه نڪرندس، ۽ اُھو چڱو حُڪم ڏيڻ وارو آھي.'),
(1677, 12, 81, 'اوھين پنھنجي پيءُ وٽ موٽي وڃو پوءِ چئو ته اي ابا تنھنجي پُٽ چوري ڪئي، ۽ جيڪو ڄاتوسون ان کانسواءِ ٻئي (ڳالھ) جي شاھدي نه ڏنيسون ۽ ڳُجھ (جي خبر) نه ڄاڻندا ھواسون.'),
(1678, 12, 82, '۽ جنھن ڳوٺ ۾ ھواسون انھي کان ۽ جنھن قافلي ۾ آياسون تنھن کان پُڇا ڪر، اسين ضرور سچا آھيون.'),
(1679, 12, 83, '(يعقوب) چيو ته (نه!) بلڪ اوھان جي نفس اوھان لاءِ (ڪا) ڳالھ ٺاھي آھي، تنھنڪري چڱو صبر ڪرڻ (منھنجو ڪم) آھي، اميد آھي ته الله انھن سڀني کي مون وٽ آڻيندو، ڇوته اُھو ڄاڻندڙ حِڪمت وارو آھي.'),
(1680, 12, 84, '۽ کانئن مُنھن موڙيائين ۽ چيائين مون کي يُوسف لاءِ افسوس آھي ۽ سندس ٻئي اکيون ڏک کان اڇيون ٿيون پوءِ اُھو ڏک ڀريل ھو.'),
(1681, 12, 85, '(۽ سندس گھراڻي وارن) چيو ته الله جو قسم آھي ته تون سدائين يوسف کي ساريندو آھين جيسين بيمار ٿئين يا مري وڃين.'),
(1682, 12, 86, 'چيائين ته آءٌ پنھنجو ڏک ۽ بيقراري الله جي جناب ۾ بيان ڪندو آھيان ۽ الله جي طرف کان اھو ڄاڻندو آھيان جو اوھين نه ڄاڻندا آھيو.'),
(1683, 12, 87, 'اي منھنجا پٽؤ وڃو ۽ يُوسف ۽ سندس ڀاءُ جي ڳولا ڪريو ۽ الله جي رحمت کان نا اميد نه ٿيو، ڇوته الله جي ٻاجھ کان ڪافرن جي قوم کانسواءِ (ٻيو) ڪو (به) نا اميد نه ٿيندو آھي.'),
(1684, 12, 88, 'پوءِ جنھن مھل يوسف وٽ آيا (تنھن مھل) چيائون ته اي عزيز اسان کي ۽ اسان جي گھر وارن کي ڏک پھتو آھي ۽ نڪمو سامان کڻي آيا آھيون پوءِ اسان کي پوري مئپ ڏي ۽ اسان کي (ڪجھ) خيرات (به) ڏي، ڇوته الله خيرات ڪندڙن کي چڱو بدلو ڏيندو آھي.'),
(1685, 12, 89, '(يُوسف) کين چيو ته جڏھن اوھين بي سمجھ ھيؤ ته جيڪي اوھان يُوسف ۽ سندس ڀاءُ سان ڪيو سو ڄاتو اٿوَ؟'),
(1686, 12, 90, 'چيائون ته تون ئي پڪ يُوسف آھين ڇا؟ چيائين (ھائو) آءٌ يُوسف آھيان ۽ ھيءُ منھنجو ڀاءُ آھي، بيشڪ الله اسان تي ٻاجھ ڪيو آھي، جيڪو پرھيز گاري ڪندو ۽ صبر ڪندو ته الله ڀلارن جو اجر نه وڃائيندو آھي.'),
(1687, 12, 91, 'چيائون ته الله جو قسم آھي ته بيشڪ الله توکي اسان کان ڀلو ڪيو آھي ۽ بيشڪ اسين خطا ڪندڙ ھواسون.'),
(1688, 12, 92, '(يوسف) چيو ته اڄ اوھان تي ڪا ميار نه آھي، الله اوھان کي بخشيندو، ۽ اُھو (سڀني) ٻاجھارن کان بلڪل ٻاجھارو آھي.'),
(1689, 12, 93, 'ھي منھنجو پھراڻ نيو پوءِ اُھو منھنجي پيءُ جي مُنھن تي گھمايو ته ڏسندڙ ٿيندو، ۽ مڙئي پنھنجي گھر جا آدمي مون وٽ آڻيو.'),
(1690, 12, 94, '۽ جنھن مھل قافلو (مصر مان) نڪتو (ته) سندن پيءُ چيو ته جيڪڏھن (اوھين) مون کي ڀورڙو نه ڄاڻو ته مون کي يُوسف جي بوءِ اچي ٿي.'),
(1691, 12, 95, 'چيائون ته الله جو قسم ته تون اڳئين ويساري ۾ آھين.'),
(1692, 12, 96, 'جنھن مھل خوشخبري ڏيندڙ آيو ته سندس مُنھن تي اھو (پھراڻ) گھمايائين ته موٽي ڏسندڙ ٿيو، چيائين ته اوھان کي نه چيو ھوم ڇا، ته آءٌ الله جي پار کان اھو ڄاڻندو آھيان جو اوھين نه ڄاڻندا آھيو؟'),
(1693, 12, 97, 'چيائون ته اي اسان جا ابا اسان لاءِ اسان جي گناھن جي بخشش گھُر اسين بيشڪ گنھگار آھيون.'),
(1694, 12, 98, '(يعقوب) چيو ته آءٌ سِگھو اوھان لاءِ پنھنجي پالڻھار کان بخشش گھرندس، ڇوته اُھو ئي بخشڻھار مھربان آھي.'),
(1695, 12, 99, 'جنھن مھل يُوسف وٽ پُھتا (ته اُن وقت) پنھنجي ماءُ پيءُ کي پاڻ وٽ جاءِ ڏنائين ۽ چيائين جيڪڏھن الله گھريو ته مصر ۾ بي ڀوا ٿي گِھڙو.'),
(1696, 12, 100, '۽ پنھنجي ماءُ پيءُ کي تخت تي وھاريائين ۽ اُھي ڏانھس سجدو ڪندي ڪريا، ۽ (يُوسف) چيو ته اي منھنجا ابا ھيءُ منھنجي اڳين خوابن جو تعبير آھي، بيشڪ منھنجي پالڻھار ان کي سچو ڪيو، ۽ (الله) مون سان تڏھن بيشڪ احسان ڪيو جڏھن مون کي قيد مان ٻاھر ڪڍيائين ۽ منھنجي ۽ منھنجي ڀائرن جي وچ ۾ شيطان جھيڙو وڌو تنھن کانپوءِ اوھان کي ٻھراڙيءَ کان (شھر) ۾ آندائين، ڇوته منھنجو پالڻھار جنھن لاءِ وڻيس تنھن تي احسان ڪندڙ آھي، سچ آھي ته اُھو ڄاڻندڙ حڪمت وارو آھي.'),
(1697, 12, 101, '(يُوسف چيو ته) اي منھنجا پالڻھار بيشڪ مون کي بادشاھي ڏني اٿئي ۽ مون کي خوابن جو تعبير سيکاريو اٿئي، اي آسمانن ۽ زمين جا بڻائيندڙ، تون دُنيا ۽ آخرت ۾ منھنجو وارث آھين، مون کي مسلمان ڪري مار ۽ مون کي صالحن سان شامل ڪر.'),
(1698, 12, 102, '(اي پيغمبر) اھو (قصو) ڳُجھين خبرن مان آھي جو تو ڏانھن وحي ڪريون ٿا، انھن جڏھن فريب ڪندڙ ٿي پنھنجي ڪم جو پڪو ارادو ڪيائون تڏھن تون وٽن ڪونه ھئين.'),
(1699, 12, 103, '۽ (اي پيغمبر) جيتوڻيڪ حرص ڪرين ته به گھڻا ماڻھو ايمان آڻيندڙ نه آھن.'),
(1700, 12, 104, '۽ (تون) اُن (قرآن جي پُھچائڻ) تي کانئن ڪو اُجورو نه گھرندو آھين، اُھو (قرآن) جھانن جي نصيحت کانسواءِ (ٻيو ڪي) نه آھي.'),
(1701, 12, 105, '۽ ڪيتريون ئي نشانيون آسمانن ۽ زمين ۾ آھن جن تان لنگھندا آھن ۽ اُھي (ڏِسي ڪري) اُنھن (تي ڌيان ڏيڻ) کان مُنھن موڙيندا آھن.'),
(1702, 12, 106, '۽ منجھانئن گھڻا الله تي ايمان نه آڻيندا آھن پر اُھي (الله سان) شريڪ بڻائيندڙ آھن.'),
(1703, 12, 107, '(اُھي) الله جي عذاب مان پاڻ وٽ ڇِپ اچڻ کان يا قيامت جي اوچتي اچڻ کان بي ڀَوَا ٿيا آھن ڇا؟ جو اُھي نه ڄاڻندا ھُجن؟'),
(1704, 12, 108, '(اي پيغمبر) چؤ ته ھيءُ منھنجي واٽ آھي پڌري حُجّت سان الله ڏانھن سڏيان ٿو آءٌ ۽ منھنجا تابعدار به (سڏيندا آھن)، ۽ الله پاڪ آھي ۽ مان مشرڪن مان نه آھيان.'),
(1705, 12, 109, '۽ (اي پيغمبر) اسان توکان اڳ شھر وارن مان مڙسن کانسواءِ ٻيو ڪونه موڪليوسون جن ڏانھن وحي ڪيوسون، پوءِ ڇونه زمين ۾ گھمي ڏسندا آھن ته جيڪي کانئن اڳ ھوا تن جي پڇاڙي ڪھڙي طرح ٿي؟ ۽ آخرت جو گھر انھن لاءِ ڀلو آھي جيڪي ڊڄندا آھن، پوءِ ڇونه ڄاڻندا آھيو؟'),
(1706, 12, 110, '(ايستائين ڍر ڏنيسون) جيستائين پيغمبر (مدد پھچڻ کان) نا اميد ٿيڻ لڳا ۽ (سندين قوم) ڀانيو ته انھن سان ڪوڙو انجام ڪيو ويو آھي وٽن اسان جي مدد پھتي پوءِ جنھن کي گھريوسون تنھن کي ڇوٽڪارو ڏنو ويو، ۽ گنھگار قوم کان اسان جو عذاب نه ٽاريو ويندو آھي.'),
(1707, 12, 111, 'بيشڪ ڌيان وارن لاءِ انھن (پيغمبرن) جي قِصّن ۾ عِبرت آھي، (ھي قرآن) بڻاوتي آکاڻي نه آھي پر جيڪي (ڪتاب) کانئس اڳ آھن تن کي سچو ڪندڙ آھي ۽ سڀڪنھن شيء لاءِ نکيڙ آھي ۽ ايمان واري قوم لاءِ ھدايت ۽ ٻاجھ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'المّرٰ، ھيءُ ڪتاب جون آيتون آھن، ۽ جيڪي توڏانھن تنھنجي پالڻھار وٽان نازل ڪيو ويو سو حق آھي پر گھڻا ماڻھو نه مڃيندا آھن.'),
(1709, 13, 2, 'الله اُھو آھي جنھن آسمانن کي ريءَ ٿنڀن جي کڙو ڪيو آھي انھن کي ڏسو ٿا وري عرش ڏي متوجه ٿيو ۽ سج ۽ چنڊ کي فرمانبردار ڪيائين، جو سڀڪو ٺھرايل مُدّت تائين ھلندو آھي، (اُھو) سڀني ڪمن کي رٿيندو آھي نشانيون چٽائيءَ سان بيان ڪندو آھي ته مانَ اوھين پنھنجي پالڻھار جي مِلڻ جو يقين ڪريو.'),
(1710, 13, 3, '۽ اُھو (الله) آھي جنھن زمين کي پکيڙيو ۽ منجھس جبل ۽ درياء بڻايائين، ۽ منجھس سڀڪنھن ميوي مان ٻه ٻه قِسم بڻايائين ڏينھن کي رات سان ڍڪيندو آھي، بيشڪ اُن ۾ پروڙيندڙ قوم لاءِ نشانيون آھن.'),
(1711, 13, 4, '۽ زمين ۾ (پاڻون واڻا) ٽڪرا لڳولڳ آھن ۽ ڊاکن جا باغ آھن ۽ پوکون ۽ کجيون آھن (انھن مان ڪي) جُوھا ۽ (ڪي) ھڪ ٿُڙ واريون آھن اُنھن کي ھڪ (ئي) پاڻي پياريو ويندو آھي، ۽ اسين ميون ۾ ڪن (جي مزي) کي ڪِن کان وڌيڪ ڪندا آھيون، بيشڪ اُن ۾ سمجھندڙ قوم لاءِ نشانيون آھن.'),
(1712, 13, 5, '۽ (اي پيغمبر) جيڪڏھن توکي (سندن انڪار جو) عجب ٿيندو آھي ته سندن (ھن) چوڻ جو به عجب (ڪرڻو) آھي (جو چوندا آھن ته) جڏھن مٽي ٿينداسون ته اسين وري نئين بڻاوت ۾ بڻايا وينداسون ڇا؟ اِھي اُھي آھن جن پنھنجي پالڻھار جو انڪار ڪيو، ۽ اِھي اُھي آھن جن جي ڳچين ۾ ڳٽ ھوندا، ۽ اُھي دوزخي آھن، اُھي منجھس سدائين رھڻ وارا آھن.'),
(1713, 13, 6, '۽ (اي پيغمبر) چڱائي (جي پھچڻ) کان اڳي ئي توکان مدائي جلد گھرندا آھن ۽ بيشڪ کانئن اڳ (ماڻھن تي) عذاب ٿي چُڪا آھن، ۽ تنھنجو پالڻھار ماڻھن کي سندن ڏوھي ھجڻ ھوندي به بخشڻ وارو آھي، ۽ تنھنجو پالڻھار ڏاڍي عذاب ڪرڻ وارو (به) آھي.'),
(1714, 13, 7, '۽ ڪافر چوندا آھن ته ھن (پيغمبر) تي سندس پالڻھار کان ڪا (به) نشاني ڇونه نازل ڪئي وئي آھي؟ (اي پيغمبر) تون رڳو ڊيڄاريندڙ آھين ۽ سڀڪنھن قوم لاءِ ھدايت ڪرڻ وارو ھوندو آھي.'),
(1715, 13, 8, 'سڀڪا مادي (پنھنجي پيٽ ۾) جيڪو (حمل) کڻندي آھي سو الله ڄاڻندو آھي ۽ ڳڀيرڻيون جيڪي گھٽائينديون آھن ۽ جيڪي وڌائينديون آھن (سو به ڄاڻندو آھي)، ۽ سڀڪا شيء وٽس اندازي سان آھي.'),
(1716, 13, 9, 'ڳجھ ۽ ظاھر جو ڄاڻندڙ وڏو مٿاھون آھي.'),
(1717, 13, 10, 'اوھان مان جيڪو ڳالھ ھوريان ڪري ۽ جيڪووڏي سڏ ڳالھائي ۽ جيڪو رات جو لِڪي ۽ ڏينھن جو پڌرو ٿي گھمي (سي سندس علم ۾) ھڪ جھڙا آھن.'),
(1718, 13, 11, 'اُن (ماڻھوءَ) لاءِ (ملائڪ پھريدار) ھڪ ٻئي پٺيان ايندڙ سندس پويان (مقرر) آھن جو الله جي حُڪم سان اُن جي نگھباني ڪندا آھن، بيشڪ الله ڪنھن قوم جي حالت کي (ايستائين) نه ڦيرائيندو آھي جيسين اُھي جيڪي سندين دلين ۾ آھي سو (پاڻ) نه ڦيرائين، ۽ جڏھن الله ڪنھن قوم جي مدائيءَ جو ارادو ڪندو آھي ته اُن کي ڪو ٽَرڻ ڪونه آھي، ۽ نڪي انھن جو الله کانسواءِ ڪو مددگار آھي.'),
(1719, 13, 12, 'اُھو (الله) آھي جيڪو اوھان کي ڀؤ ۽ اُميد لاءِ وِڄ ڏيکاريندو آھي ۽ ڳَرن ڪڪرن کي کڻندو آھي.'),
(1720, 13, 13, '۽ رعد ۽ (سڀ) ملائڪ سندس ڀؤ کان سندس ساراھ سان پاڪائي واکاڻيندا آھن، ۽ وِڄون موڪليندو آھي پوءِ اُھي جنھن لاءِ گھرندو آھي تنھن تي ڪيرائيندو آھي ۽ ڪافر الله بابت تڪرار ڪندا آھن، حالانڪ اُھو سخت عذاب ڪرڻ وارو آھي.'),
(1721, 13, 14, 'کيس (ھر حال ۾) سڏڻ حق آھي، ۽ (ڪافر) الله کانسواءِ جن کي سڏيندا آھن سي (ٻي) ڪنھن (به) طرح ورندي ڏئي نه سگھندا (انھن سڏيندڙن جو مثال) رڳو انھيءَ (ماڻھوءَ) وانگر آھي جو پنھنجو ٻُڪ پاڻيءَ ڏانھن ھن لاءِ ڊگھيڙيندڙ ھجي ته پاڻي سندس وات ۾ پوي ۽ اُھو پاڻي سندس وات ۾ پوڻ وارو نه آھي، ۽ ڪافرن جو سڏڻ رڳو گمراھيءَ ۾ ھجڻ آھي.'),
(1722, 13, 15, '۽ جيڪي آسمانن ۽ زمين ۾ آھي سي سَرھا ٿي ۽ ارھا ٿي الله کي سجدو ڪندا آھن ۽ سندن پاڇا (به) صبح جو ۽ سانجھيءَ جو (سجدو ڪندا آھن).'),
(1723, 13, 16, '(اي پيغمبر کانئن) پُڇ ته آسمانن ۽ زمين جو پالڻھار ڪير آھي، (سندن پاران) چؤ ته الله، (وري کانئن) پڇا ڪر ته پوءِ الله کانسواءِ ٻيا اھڙا مددگار ڇو ورتا اَٿو جي پنھنجي جيئن کي نڪي سُک ۽ نڪي ڏک پھچائي سگھندا آھن؟ (۽ اي پيغمبر کين) چؤ ته انڌو ۽ سڄو ڪو برابر آھي ڇا؟ يا اونداھيون ۽ سوجھرو برابر آھن ڇا؟ الله لاءِ (اھڙا) شريڪ مُقرر ڪيا اٿن ڇا جو (انھن) الله جي مخلوقات جھڙي (خلق) خلقي ھجي جو (اُھا) خلق مٿن ھڪ جھڙي ٿي پئي آھي، (اي پيغمبر کين) چؤ ته سڀڪنھن جو خلقڻھار الله آھي ۽ اُھوئي اڪيلو غالب آھي.'),
(1724, 13, 17, '(جنھن) آسمان کان پاڻي وَسايو پوءِ نيون پنھنجي قدر سارو وھيون پوءِ ڇَر اُڀامندڙ گجيءَ کي کنيو، ۽ جيڪا شيء زيورن يا سامان جي طلب ڪرڻ لاءِ باھ ۾ ڳاريندا آھن اُن تي اُن جھڙي گجي (ٿيندي) آھي، اھڙي طرح الله کَري ۽ کوٽي جو مثال ڏيندو آھي، پر گجي ته گُم ٿي ويندي آھي، ۽ جا شيء ماڻھن کي نفعو ڏيندي آھي سا زمين ۾ رھندي آھي، اھڙي طرح الله مثال ڏيندو آھي.'),
(1725, 13, 18, 'جن پنھنجي پالڻھار جو حُڪم قبول ڪيو تن لاءِ ڀلائي آھي، ۽ جيڪي سندس (حُڪم) قبول نه ٿا ڪن سي جيڪي زمين ۾ آھي سو سڀ ۽ پڻ اوترو ساڻس (ٻيو) انھن کي ھجي جيڪڏھن عوض ڏين (ته به قبول نه ڪبو)، انھن لاءِ حساب جي سختي آھي، ۽ سندين جاءِ دوزخ آھي، ۽ اُھا بڇڙي جاءِ آھي.'),
(1726, 13, 19, 'پوءِ جيڪي توڏانھن تنھنجي پالڻھار کان نازل ڪيو ويو آھي تنھن کي جيڪو ڄاڻندو آھي ته سچ آھي سو ڪو اُن جھڙو آھي ڇا جيڪو انڌو ھجي؟ سمجھ وارن کانسواءِ ڪو نصيحت نه وٺندو آھي.'),
(1727, 13, 20, 'جيڪي الله جو انجام پاڙيندا آھن ۽ انجام نه ڀڃندا آھن.'),
(1728, 13, 21, '۽ الله جنھن جي ڳنڍڻ جو حُڪم ڪيو تنھن کي جيڪي (ماڻھو) ڳنڍيندا آھن ۽ پنھنجي پالڻھار کان ڊڄندا آھن ۽ حساب جي سختيءَ جو ڀؤ ڪندا آھن.'),
(1729, 13, 22, '۽ جن پنھنجي پالڻھار جي رضامندي حاصل ڪرڻ لاءِ صبر ڪيو ۽ نماز پڙھي ۽ جيڪي کين روزي ڏني سون تنھن مان ڳجھو ۽ پڌرو خرچيائون ۽ چڱاين سان مدايون ٽاريندا آھن تن لاءِ آخرت جو گھر آھي.'),
(1730, 13, 23, '(جي) ھميشگيءَ وارا باغ آھن اُن ۾ گھڙندا ۽ سندن زالن ۽ سندن اولاد مان جيڪي سڌريل ھوندا سي (به گھڙندا) ۽ سڀڪنھن دروازي کان مٿن ملائڪ گھڙندا.'),
(1731, 13, 24, '(چوندا ته) انھي ڪري جو (ڏکن ۾) صبر ڪيو ھيؤ سلامتي آھي اوھان تي پوءِ آخرت جو گھر ڀلو آھي.'),
(1732, 13, 25, '۽ جيڪي الله جو انجام ان جي پڪي ڪرڻ کان پوءِ ڀڃندا آھن ۽ الله جن ڪمن جي ڳنڍڻ جو حُڪم ڪيو آھي سو ڇنندا آھن ۽ زمين ۾ فساد وجھندا آھن، انھن تي لعنت آھي ۽ انھن لاءِ بڇڙو گھر آھي.'),
(1733, 13, 26, 'الله جنھن لاءِ گھرندو آھي تنھن لاءِ روزي ڪشادي ڪندو آھي ۽ گھٽ ڪندو آھي، ۽ (ڪافر) دنيا جي حياتي سان خوش ٿيندا آھن، ۽ دنيا جي حياتي آخرت ۾ ٿوريءَ وٿ کانسواءِ نه آھي.'),
(1734, 13, 27, '۽ ڪافر چوندا آھن ته ھن جي پالڻھار کان ھن تي معجزو ڇونه نازل ڪيو ويو آھي؟ (اي پيغمبر کين) چؤ ته الله جنھن لاءِ گھرندو آھي تنھن کي ڀلائيندو آھي ۽ جيڪي موٽيو تنھن کي پاڻ ڏانھن ھدايت ڪندو آھي.'),
(1735, 13, 28, '(اِھي اُھي آھن) جن ايمان آندو ۽ سندن دليون الله جي ذڪر سان آرامي ٿينديون آھن، خبردار الله جي ذڪر سان دليون آرام وٺنديون آھن.'),
(1736, 13, 29, 'جن ايمان آندو ۽ چڱا عمل ڪيا تن لاءِ (آخرت ۾) خوشي ۽ چڱي جاءِ آھي.'),
(1737, 13, 30, 'اھڙي طرح توکي ھڪ اھڙيءَ ٽوليءَ ڏانھن جو بيشڪ کانئن اڳ ڪيتريون ٽوليون گذري ويون آھن ھن لاءِ موڪليوسون ته جيڪي توڏانھن وحي ڪيوسون سو کين پڙھي ٻڌائين ۽ اُھي رحمان جا مُنڪر آھن، (اي پيغمبر کين) چؤ ته اُھو منھنجو پالڻھار آھي اُن کانسواءِ ٻيو ڪو مَعبُود نه آھي، ۽ مٿس ڀروسو ڪيم ۽ ڏانھس منھنجو موٽڻ آھي.'),
(1738, 13, 31, '۽ جيڪڏھن سچ پچ قرآن اھڙو ھجي ھا جو اُن سان جبل ھلائجن ھا يا اُن سان زمين ٽڪر ٽڪر ڪجي ھا يا اُن سان مئلن کي ڳالھرائجي ھا (ته به اُھي ايمان نه آڻين ھا) بلڪ سڀ ڪم الله جي وس ۾ آھن، اڃا مؤمنن نه ڄاتو آھي ڇا ته جيڪڏھن الله گھُري ھا ته سڀني ماڻھن کي ھدايت ڪري ھا، ۽ ڪافرن کي سندن ڪرتوتن سببان سدائين سزا پھچندي آھي يا ايستائين سندن گھرن جي آس پاس پئي پھچندي جيسين الله جو انجام ايندو، بيشڪ الله پنھنجي انجام کي نه ڦيرائيندو آھي.'),
(1739, 13, 32, '۽ (اي پيغمبر) بيشڪ توکان اڳ پيغمبرن تي ٺـٺوليون ڪيون ويون ھيون پوءِ ڪافرن کي ڍر ڏنم وري کين پڪڙيم، پوءِ منھنجو عذاب ڪئن ھو.'),
(1740, 13, 33, 'سڀڪنھن ماڻھو جيڪي ڪمايو تنھن جي جيڪو خبر رکندڙ ھجي (سو بيخبر بتن جھڙو آھي) ڇا؟ ۽ (ڪافرن) الله جا شريڪ مقرّر ڪيا آھن، (اي پيغمبر کين) چؤ ته اُنھن (جي وصفن) جا نالا بيان ڪريو، يا کيس اُھو جتائيندا آھيو ڇا جيڪي (ھو) زمين ۾ نه ڄاڻندو آھي؟ يا (بنا حقيقت جي الله سان شريڪ ڪرڻ اِھي) ڇڙيون ڳالھيون آھن، بلڪ ڪافرن کي سندن بڇڙو خيال سھڻو ڪري ڏيکاريو ويو آھي ۽ اُھي سنئين واٽ کان روڪيا ويا آھن، ۽ جنھن کي الله گمراھ ڪري تنھن کي ڪو ھدايت ڪرڻ وارو ڪونه آھي.'),
(1741, 13, 34, 'اُنھن لاءِ دنيا جي حياتيءَ ۾ عذاب آھي ۽ بيشڪ آخرت جي سزا ته بلڪل سخت آھي، ۽ کين الله (جي عذاب) کان ڪو بچائڻ وارو نه آھي.'),
(1742, 13, 35, 'جنھن بھشت جو پرھيزگارن سان انجام ڪيو ويو آھي تنھن جو بيان (ھيءُ) آھي، ته ان جي ھيٺان واھيون وھنديون آھن، (۽) اُن جا ميوا ھميشہ آھن ۽ اُن جي ڇانوَ (سدائين رھندڙ آھي) پرھيزگارن جي اھا پڇاڙي آھي، ۽ ڪافرن جي پڇاڙي باھ آھي.'),
(1743, 13, 36, '۽ جن کي ڪتاب ڏنو اٿئون سي جيڪي توڏانھن نازل ڪيو ويو آھي تنھن سان سرھا ٿيندا آھن ۽ (انھن) جماعتن مان ڪو اھڙو آھي جو ان جي ڪن (ڳالھين) جو انڪار ڪندو آھي، (اي پيغمبر کين) چؤ مون کي انھيءَ کانسواءِ ٻيو حُڪم نه ڪيو ويو آھي ته الله جي عبادت ڪريان ۽ ساڻس ڪنھن کي شريڪ نه ڪريان، ڏانھس (ماڻھن کي) سڏيان ٿو ۽ ڏانھس منھنجو موٽڻ آھي.'),
(1744, 13, 37, '۽ اھڙي طرح اُن (قرآن) کي (ھڪ) حُڪم (ڪندڙ) عربي (ٻوليءَ) ۾ موڪليوسون، ۽ (اي پيغمبر) جيڪڏھن تو پاڻ وٽ علم جي اچڻ کان پوءِ سندن سَڌن جي تابعداري ڪندين ته الله وٽان نڪو سنڀاليندڙ ۽ نڪو بچائيندڙ لھندين.'),
(1745, 13, 38, '۽ بيشڪ توکان اڳ (ڪيترائي) پيغمبر موڪلياسون ۽ کين زالون ۽ اولاد ڏنوسون، ۽ ڪنھن پيغمبر کي الله جي حُڪم کان سواءِ ڪو مُعجزو آڻڻ نه جڳائيندو آھي، ھر ڪنھن مُدّت لاءِ لکيل حُڪم آھي.'),
(1746, 13, 39, 'جيڪي الله کي وڻندو آھي سو ميٽيندو آھي ۽ قائم رکندو آھي، ۽ وٽس اصل ڪتاب آھي.'),
(1747, 13, 40, '۽ (اي پيغمبر) جيڪو انجام ساڻن ڪريان ٿو (تنھن مان) جيڪڏھن توکي ڪجھ ڏيکاريان يا توکي ماريان ته (بھرحال) توتي پيغام پھچائڻ ۽ اسان تي حساب وٺڻ کانسواءِ (ٻيو ڪي) نه آھي.'),
(1748, 13, 41, 'اڃا نه ڏسندا آھن ڇا ته اسين زمين کي سندس چوڌاري گھٽائيندا آھيون، ۽ الله جو به حُڪم ڪندو آھي ته سندس حُڪم کي ڪو روڪڻ وارو نه آھي، ۽ اُھو (الله) سگھو حساب وٺندڙ آھي.'),
(1749, 13, 42, '۽ جيڪي کائن اڳ ھوا تن بيشڪ فريب ڪيو پوءِ سڀ بڇڙي سزا الله جي ھٿ ۾ آھي، سڀڪو جيءُ جيڪا ڪمائي ڪندو آھي (سا الله) ڄاڻندو آھي، ۽ ڪافر سگھو ڄاڻندا ته ڪنھن لاءِ آخرت جو گھر آھي.'),
(1750, 13, 43, '۽ ڪافر چوندا آھن ته تون پيغمبر نه آھين، (کين) چؤ ته منھنجي وچ ۾ ۽ اوھان جي وچ ۾ الله شاھد بس آھي ۽ اُھو (به) جنھن وٽ ڪتاب جو علم آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الٓرٰ، ھي ڪتاب آھي جو (اي پيغمبر) اُھو اسان توڏانھن ھن لاءِ نازل ڪيو آھي ته ماڻھن کي اونداھين مان سوجھري ڏانھن ڪڍين، (۽) سندس رب جي حُڪم سان انھي ساراھيل جي واٽ تي آڻين.'),
(1752, 14, 2, 'جنھن جو اُھو (سڀئي) آھي جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي، ۽ سخت عذاب جي خرابي انھن ڪافرن لاءِ آھي.'),
(1753, 14, 3, 'جيڪي دنيا جي حياتيءَ کي آخرت جي حياتيءَ کان وڌيڪ پيارو رکندا آھن ۽ (ماڻھن کي) الله جي واٽ کان جھليندا آھن ۽ ان جي ڏنگائي گھرندا آھن، اھي وڏيءَ گمراھي ۾ آھن.'),
(1754, 14, 4, '۽ ڪنھن پيغمبر کي سندس قوم جي ٻوليءَ کانسواءِ ٻيء (ٻوليءَ) سان نه موڪليوسون (ھن لاءِ) ته انھن کي (پڌرو) بيان ڪري، پوءِ الله جنھن کي گھري ٿو تنھن کي ڀُلائي ٿو ۽ جنھن لاءِ وڻيس تنھن کي ھدايت ٿو ڪري، ۽ اُھو غالب حڪمت وارو آھي.'),
(1755, 14, 5, '۽ بيشڪ موسىٰ کي پنھنجن معجزن سان موڪليوسون (چيوسون) ته پنھنجي قوم کي اونداھين مان سوجھري ڏانھن ڪڍ، ۽ کين الله (جي ڏمر) وارا ڏينھن ياد ڏيار، بيشڪ اُن ۾ ھر ڪنھن صابر شاڪر لاءِ نشانيون آھن.'),
(1756, 14, 6, '۽ (ياد ڪر) جڏھن موسىٰ پنھنجي قوم کي چيو ته پاڻ تي الله جون نعمتون ياد ڪريو جڏھن ته فرعون جي (انھن) ماڻھن کان اوھان کي بچايائين جيڪي اوھان کي سخت عذاب چکائيندا ھوا، ۽ اوھان جي پُٽ ڪُھندا ھوا ۽ اوھان جون ڌيئرون جيئريون ڇڏيندا ھوا، ۽ ھن ۾ اوھان جي پالڻھار کان اوھان لاءِ وڏي آزمائش ھُئي.'),
(1757, 14, 7, '۽ جڏھن اوھان جي پالڻھار اوھان کي خبردار ڪيو ته جيڪڏھن اوھين (منھنجو) شڪرانو ڪندؤ ته اوھان کي ضرور وڌيڪ ڏيندس ۽ جيڪڏھن بي شُڪري ڪندؤ ته بيشڪ منھنجو عذاب سخت آھي.'),
(1758, 14, 8, '۽ موسىٰؑ چيو ته اوھان جو ۽ سڀني جھان وارن جو انڪار ڪرڻ (الله کي ڪجھ نقصان لائي نه سگھندو) ڇوته الله بي پرواھ ساراھيل آھي.'),
(1759, 14, 9, 'اوھان کي انھن جي سُڌ نه پھتي آھي ڇا جيڪي اوھان کان اڳ نوح ۽ عاد جي قوم جا ھوا، ۽ جيڪي کانئن پوءِ ھوا، جن کي الله کان سواءِ ڪو ڪونه ڄاڻندو ھو، سندن پيغمبر وٽن پڌرن مُعجزن سان آيا پوءِ پنھنجا ھٿ واتن ۾ ورايائون ۽ چيائون ته جنھن شيء سان اوھان کي موڪليو ويو آھي تنھن جا اسين مُنڪر آھيون ۽ جنھن ڏانھن اوھين اسان کي سڏيندا آھيو تنھن کان اسين سخت شڪ ۾ پيل آھيون.'),
(1760, 14, 10, 'سندن پيغمبر چيو ته (اوھان کي) آسمانن ۽ زمين جي بڻائيندڙ الله جي بابت ڪو شڪ آھي ڇا؟ (اُھو) اوھان کي ھن لاءِ سڏيندو آھي ته اوھان جا گناھ اوھان کي بخشي ۽ ٺھرايل مُدّت تائين اوھان کي مُھلت ڏئي، چيائون ته اوھين به رڳو اسان جھڙا ماڻھو آھيو (تنھنڪري اوھانجي چوڻ تي ڪئن لڳون)، جن کي اسان جا ابا ڏاڏا پوڄيندا ھوا تن کان اسان کي جھلڻ جو ارادو رکندا آھيو (جي سچا آھيو) ته اسان وٽ ڪو پڌرو دليل آڻيو.'),
(1761, 14, 11, 'سندن پيغمبر کين چيو ته اسين اوھان جھڙا ئي ماڻھو آھيون پر الله پنھنجي ٻانھن مان جنھن لاءِ گھرندو آھي تنھن تي احسان ڪندو آھي، ۽ الله جي حُڪم کانسواءِ اوھان وٽ ڪنھن دليل جي آڻڻ جي اسان کي ڪا سگھ نه آھي، ۽ مؤمنن کي جڳائي ته الله تي ڀروسو ڪن.'),
(1762, 14, 12, '۽ اسان کي ڇا (ٿيو) آھي ته الله تي ڀروسو نه ڪريون ۽ بيشڪ اسان کي اسانجي سِڌن رستن جي ھدايت ڪيائين، ۽ جيڪي اسان کي ڏکوئيندا آھيو تنھن تي ضرور صبر ڪنداسون، ۽ ڀروسي ڪندڙن کي الله تي ڀروسو ڪرڻ گھرجي.'),
(1763, 14, 13, '۽ ڪافرن پنھنجن پيغمبرن کي چيو ته اوھان کي پنھنجي مُلڪ مان ڪڍي ڇڏينداسون يا اسان جي مذھب ڏانھن ضرور موٽي اچو، پوءِ سندن پالڻھار انھن ڏانھن وحي ڪيو ته ظالمن کي ضرور ناس ڪنداسون.'),
(1764, 14, 14, '۽ اُنھن کان پوءِ مُلڪ ۾ اوھان کي ضرور رھائينداسين، اِھو انجام انھي لاءِ آھي جيڪو منھنجي اڳيان بيھڻ کان ڊڄي ۽ منھنجي دڙڪي کان ڊڄي.'),
(1765, 14, 15, '۽ (پيغمبرن) سوڀ جون دُعائون گھريون ۽ سڀڪو ھٺيلو ضدي نامراد ٿيو.'),
(1766, 14, 16, 'ان جي پويان دوزخ آھي ۽ اُن کي پُونءَ جو پاڻي پياريو ويندو.'),
(1767, 14, 17, 'ان کي ڍُڪ ڍُڪ ڪري پيئندو ۽ اُن کي ڳيت ڏئي پي نه سگھندو ۽ سڀڪنھن پاسي کان موت ايندس ۽ ھو مُئل نه ھوندو، ۽ ان جي پويان سخت عذاب آھي.'),
(1768, 14, 18, 'جن پنھنجي پالڻھار کان انڪار ڪيو تن جي ڪرتوتن جو مثال انھيءَ ڪيريءَ جھڙو آھي جنھن کي آنڌيءَ واري ڏينھن ۾ واءُ اڏائي، جيڪي ڪمايائون تنھن جي ذري تي پُھچي نه سگھندا، اھا ئي وڏي گمراھي آھي.'),
(1769, 14, 19, '(اي پيغمبر) نه ڏٺو اٿئي ڇا ته الله آسمانن ۽ زمين کي رِٿ سان بڻايو آھي، جيڪڏھن وڻيس ته اوھان کي نئي ۽ ٻي نئين خلق آڻي.'),
(1770, 14, 20, '۽ اِھو (ڪرڻ) الله تي اوکو نه آھي.'),
(1771, 14, 21, '۽ مڙيئي الله جي اڳيان حاضر ٿيندا پوءِ ھيڻا ھٺيلن کي چوندا ته اسين اوھان جي پٺيان لڳا ھئاسون پوءِ اوھين الله جي عذاب مان ڪجھ اسان کان ٽارڻ وارا آھيو ڇا؟ (ھٺيلا) چوندا ته الله اسان کي ھدايت ڪري ھا ته اسين به اوھان کي ھدايت ڪريون ھا، (ھاڻي) اسان جو رئڻ رڙڻ يا اسان جو صبر ڪرڻ اسان لاءِ ھڪ جھڙو آھي اسان کي ڪا واہ ڪانه آھي.'),
(1772, 14, 22, '۽ جڏھن ڪم فيصل ڪيو ويندو تڏھن شيطان چوندو الله اوھان سان سچو انجام ڪيو ھو ۽ مون اوھان سان (ڪوڙو) وعدو ڪيو ھو پوءِ اوھان سان نه پاڙيم، مون کي اوھان تي اوھان جي سڏڻ کانسواءِ (ٻيو ڪو) زور نه ھو پوءِ اوھان منھنجو چيو قبول ڪيو، تنھنڪري مون کي ڦٺ جٺ نه ڪريو ۽ (ھاڻي) پاڻ کي ڦٺ جٺ ڪريو، آءٌ اوھان جي دانھن ورنائڻ وارو نه آھيان ۽ نڪي اوھين منھنجي دانھن ٻڌڻ وارا آھيو، اوھان اڳي مون کي (الله) جو شريڪ بڻايو ھو تنھن کان آءٌ بيزار آھيان، ڇوته ظالمن لاءِ ڏکوئيندڙ عذاب آھي.'),
(1773, 14, 23, '۽ جن ايمان آندو ۽ چڱا ڪم ڪيا سي (انھن) باغن ۾ داخل ڪيا ويندا جن جي ھيٺان واھيون وھنديون آھن پنھنجي پالڻھار جي موڪل سان منجھن سدائين رھندڙ آھن، منجھس سندين کيڪار سلام آھي.'),
(1774, 14, 24, '(اي پيغمبر) توکي معلوم نه آھي ڇا؟ ته الله ڪھڙو مثال بيان ڪيو آھي پاڪ ڪلمون پاڪ وڻ وانگر آھي سنديس پاڙ مُحڪم آھي جنھن جون ٽاريون آسمان آھن.'),
(1775, 14, 25, 'پنھنجي پالڻھار جي حُڪم سان سڀ ڪنھن وقت پنھنجا ميوا پچائيندو آھي، ۽ الله ماڻھن لاءِ مثال بيان ڪندو آھي مانَ اُھي نصيحت وٺن.'),
(1776, 14, 26, '۽ بڇڙي ڳالھ جو مثال بڇڙي وڻ وانگر آھي جو زمين تان پٽيو ويو اُن کي ڪو ٽِڪاءُ نه آھي.'),
(1777, 14, 27, 'جن ايمان آندو تن کي الله دُنيا جي حياتيءَ ۾ ثابت قول سام مُحڪم رکندو آھي ۽ آخرت ۾ به، ۽ الله ظالمن کي ڀُلائيندو آھي، ۽ الله جيڪي گھرندو آھي سو ڪندو آھي.'),
(1778, 14, 28, '(اي پيغمبر) توکي اُھي معلوم نه آھن ڇا؟ جن بي شُڪريءَ سببان الله جي نعمتن کي مٽايو ۽ پنھنجي قوم کي ھلاڪيءَ جي گھر ۾ وڌو.'),
(1779, 14, 29, 'جو دوزخ آھي، اُھي منجھس گھڙندا، اُھو بڇڙو ٽڪاڻو آھي.'),
(1780, 14, 30, '۽ الله سان شريڪ مقرّر ڪيائون ته سندس واٽ کان ماڻھن کي ڀُلائين، (اي پيغمبرکين) چؤ ته اوھين آسودا رھو ڇوته اوھان جو (دوزخ جي) باہ ڏانھن موٽڻ آھي.'),
(1781, 14, 31, '(اي پيغمبر) منھنجن اُنھن ٻانھن کي چؤ جن ايمان آندو آھي ته نماز پڙھندا رھن ۽ جيڪي کين رزق ڏنو اٿون تنھن منجھان ڳجھو ۽ پڌرو اُن ڏينھن جي اچڻ کان اڳ (الله جي واٽ ۾) خرچ ڪندا رھن جنھن ۾ نڪو واپار ۽ نڪا يراني ھوندي.'),
(1782, 14, 32, 'الله اُھو آھي جنھن آسمانن ۽ زمين کي بڻايو ۽ آسمان کان (مينھن جو) پاڻي وسايو پوءِ اُن سان سڀڪنھن جنس جي ميون مان اوھان جي روزي ڪڍيائين، ۽ اوھان کي ٻيڙيون ھن لاءِ نِوائي ڏنائين ته سندس حُڪم سان سمنڊ ۾ ترنديون رھن، ۽ اوھان کي نديون (به) نوائي ڏنائين.'),
(1783, 14, 33, '۽ اوھان لاءِ سدائين ھلندڙ سج ۽ چنڊ نوائي ڏنائين، ۽ اوھان لاءِ رات ۽ ڏينھن نوائي ڏنائين.'),
(1784, 14, 34, '۽ جيڪا شيءِ کانئس گھريوَ تنھن سڀ مان ڪي اوھان کي ڏنائين، ۽ جيڪڏھن الله جي نعمتن کي ڳڻيندؤ ته اُھي ڳڻي نه سگھندؤ، بيشڪ ماڻھو ضرور بي انصاف بي شڪر آھي.'),
(1785, 14, 35, '۽ (ياد ڪر) جڏھن ابراھيم چيو ته اي منھنجا پالڻھار ھن شھر (مڪي) کي امن وارو ڪر ۽ مون کي ۽ منھنجي اولاد کي بُتن جي پوڄڻ کان پاسي ڪر.'),
(1786, 14, 36, 'اي منھنجا پالڻھار انھن (بتن) ماڻھن مان گھڻن کي ڀُلايو آھي، پوءِ جيڪو منھنجي پويان لڳو سو بيشڪ منھنجو آھي، ۽ جنھن منھنجي نافرماني ڪئي ته تون بيشڪ بخشڻھار مھربان آھين.'),
(1787, 14, 37, 'اي اسانجا پالڻھار مون پنھنجي اولاد مان ڪن کي غير آباد ميدان ۾ تنھنجي تعظيم واري گھر وٽ ھِن لاءِ ٽڪايو آھي ته اي اسانجا پالڻھار اُھي نماز پڙھن پوءِ ڪن ماڻھن جون دليون انھن ڏانھن لڙندڙيون ڪر ۽ کين ميون جي روزي ڏي ته مانَ اُھي شُڪرانو ڪن.'),
(1788, 14, 38, 'اي اسان جا پالڻھار جيڪي اسين ڳجھو ڪريون ٿا ۽ جيڪي پڌرو ڪريون ٿا سو تون ڄاڻندو آھين، ۽ ڪابه شيءِ زمين ۾ الله کان ڳُجھي نه آھي ۽ نڪي آسمان ۾.'),
(1789, 14, 39, 'سڀ ساراھ انھي الله کي جڳائي جنھن مون کي ٻڍاپي ۾ اسماعيل ۽ اسحاق عطا ڪيو، ڇوته منھنجو پالڻھار ضرور دُعا ٻڌندڙ آھي.'),
(1790, 14, 40, 'اي منھنجا پالڻھار مون کي ۽ منھنجي اولاد مان ڪن کي نماز پڙھندڙ ڪر، اي اسان جا پالڻھار منھنجي دُعا قبول ڪر.'),
(1791, 14, 41, 'اي اسان جا پالڻھار مون کي ۽ منھنجي ماءُ پيءُ کي ۽ مؤمنن کي (انھي ڏينھن) بخش جنھن ڏينھن حساب کڙو ٿيندو.'),
(1792, 14, 42, '۽ جيڪي ظالم ڪندا آھن تنھن کان الله کي ئي بي خبرنه ڀانءِ، الله رڳو کين انھي ڏينھن تائين ڍر ڏني آھي جنھن ڏينھن اکيون ٽڙي وينديون.'),
(1793, 14, 43, 'پنھنجا ڳاٽ کڻندڙ ڊوڙندڙ ھوندا پاڻ ڏانھن سندن نظر وري نه سگھندي، ۽ سندين دليون پيون دھڪنديون.'),
(1794, 14, 44, '۽ (اي پيغمبر) ماڻھن کي اُن ڏينھن کان ڊيڄار (جنھن ڏينھن) کين عذاب پھچندو پوءِ ظالم چوندا ته اي اسان جا پالڻھار اسان کي ويجھي مُدّت توڻي مُھلت ڏي ته تنھنجي سڏڻ کي قبول ڪريون ۽ پيغمبرن جي فرمانبرداري ڪريون، (چيو ويندن ته) ھن کان اڳ اوھين قسم نه کڻندا ھيؤ ڇا ته اوھان کي ڪو زوال نه ٿيندو؟'),
(1795, 14, 45, '۽ اوھين انھن جي جاين ۾ ٽڪيؤ جن پاڻ تي ظلم ڪيو ۽ اوھان کي پڌرو ٿيو ته انھن سان ڪئن ڪيوسون ۽ اوھان کي ڪيئي مثال بيان ڪياسون.'),
(1796, 14, 46, '۽ بيشڪ انھن پنھنجو وڏو مڪر ڪيو ۽ الله وت سندن مڪر (جي سزا) آھي، ۽ سندن مڪر ڪو اھڙو نه آھي جنھن سان جبل ٽري وڃن.'),
(1797, 14, 47, 'پوءِ (اي پيغمبر) الله کي پنھنجن پيغمبرن سان پنھنجي وعدي جي خلافي ڪندڙ ھرگز نه ڀانءِ، ڇوته الله غالب (اُن ڏينھن) بدلي وٺڻ وارو آھي.'),
(1798, 14, 48, 'جنھن ڏينھن ھيءَ زمين مٽائي ٻي زمين ڪئي ويندي ۽ آسمان (به) ۽ (سڀ ماڻھو) الله ھڪڙي زبردست جي آڏو ٿيندا.'),
(1799, 14, 49, '۽ اُن ڏينھن گنھگارن کي زنجيرن ۾ ٻَڌل ڏسندين.'),
(1800, 14, 50, 'سندن ڪپڙا گندرف مان ھوندا ۽ سندن مُنھن کي باھ وڪوڙيندي.'),
(1801, 14, 51, 'ھن لاءِ ته سڀڪنھن جيءَ جيڪي ڪمايو تنھن جو اُن کي الله بدلو ڏئي، بيشڪ الله جلد حساب ڪندڙ آھي.'),
(1802, 14, 52, 'اِھو (قرآن) ماڻھن کي پھچائڻو آھي ته اُن سان ڊيڄاريا وڃن ۽ ڄاڻن ته انھي ھڪ الله کانسواءِ ٻيو ڪو معبود نه آھي ۽ ڌِيان وارا نصيحت وٺن.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الٓرٰ، ھي آيتون ڪتاب ۽ پڌري قرآن جون آھن.'),
(1804, 15, 2, 'ڪافر گھڻو ئي (ھيءَ) خواھش ڪندا ته جيڪر مُسلمان ھُجون ھا.'),
(1805, 15, 3, '(اي پيغمبر) کين ڇڏي ڏي ته (دُنيا ۾) کائن ۽ (لذتن ۾) آسودا رھي گذارين ۽ کين آسرو رينجھائي ڇڏي ۽ سگھوئي ڄاڻندا.'),
(1806, 15, 4, '۽ ڪنھن ڳوٺ کي ويران نه ڪيوسون (سندن بڇڙن ڪمن جي ڪري) پر اُن لاءِ مُقرّر حڪم ڪيل ھو.'),
(1807, 15, 5, 'ڪائي ٽولي پنھنجي ٺھرايل مُدّت کان نڪي تڪڙ ڪندي ۽ نڪي پوءِ تي رھي سگھندي.'),
(1808, 15, 6, '۽ (ڪافرن) چيو ته اي شخص جنھن تي قرآن لاٿو ويو آھي بيشڪ تون ضرور چريو آھين.'),
(1809, 15, 7, 'جيڪڏھن تون سچن مان آھين ته اسان وٽ ملائڪ ڇو نه آڻيندو آھين؟'),
(1810, 15, 8, '(اي پيغمبر) کين چؤ ته ملائڪ پڪي رٿ کانسواءِ نازل نه ڪندا آھيون ۽ (جيڪڏھن نازل ڪريون ۽ نه مڃين ته) اُنھيءَ مھل اُنھن کي مُھلت نه ڏني ويندي.'),
(1811, 15, 9, 'اسان نصيحت (يعني قرآن) نازل ڪيو ۽ اسين ضرور ان جي حفاظت ڪندڙ آھيون.'),
(1812, 15, 10, '۽ بيشڪ توکان اڳي پھرين ٽولين ۾ پيغمبر موڪلياسون.'),
(1813, 15, 11, '۽ ڪو پيغمبر وٽن نه ايندو ھو جو انھن سان ٺـٺوليون ڪرڻ کانسواءِ (ٻيو ڪي) نه ڪندا ھوا.'),
(1814, 15, 12, '۽ اھڙي طرح ڏوھارين جي دلين ۾ (اھڙو خيال) وجھندا آھيون.'),
(1815, 15, 13, 'جو اُن کي نه مڃيندا ۽ بيشڪ اڳين جي ريت ھلي ايندي آھي.'),
(1816, 15, 14, '۽ جيڪڏھن آسمان مان ڪو دروازو مٿن کوليون ھا ته ان ۾ سدائين چڙھندا رھن.'),
(1817, 15, 15, 'ضرور چون ھا ته رڳو اسان جون بند ڪيون ويون آھن بلڪ اسين جادو ڪيل قوم آھيون.'),
(1818, 15, 16, '۽ بيشڪ آسمان ۾ بُرج بڻاياسون ۽ اُن کي ڏسندڙن لاءِ سينگاريو سون.'),
(1819, 15, 17, '۽ اُن کي سڀڪنھن تڙيل شيطان کان بچايوسون.'),
(1820, 15, 18, 'پر جيڪو چوريءَ ٻڌي تنھن جي پويان پڌرو اُلنبو پوندو آھي.'),
(1821, 15, 19, '۽ زمين کي پکيڙيوسون ۽ منجھس جبل کوڙيا سون ۽ منجھس ھر شيءِ اندازي سان ڄمائي سون.'),
(1822, 15, 20, '۽ منجھس اوھان لاءِ (قسم قسم جي) روزي (پيدا) ڪئي سون جن کي (اوھين) روزي ڏيندڙ نه آھيو.'),
(1823, 15, 21, '۽ اھڙي شيءِ آھي ئي ڪانه جنھن جو خزانون اسان وٽ نه آھي، انھن شين کي اسين ٺھرايل اندازي کانسواءِ نه لاھيندا آھيون.'),
(1824, 15, 22, '۽ وائِن ڪڪرن کڻندڙن کي موڪليندا آھيون پوءِ آسمان مان پاڻي وسائيندا آھيون پوءِ اُھو اوھان کي پياريندا آھيون، ۽ اُھو اوھين گڏ ڪرڻ وارا نه آھيو.'),
(1825, 15, 23, '۽ اسين ئي جياريندا آھيون ۽ ماريندا آھيون ۽ اسين سڀڪنھن جا وارث آھيون.'),
(1826, 15, 24, '۽ بيشڪ اوھان کان اڳ وارا ڄاڻندا آھيون ۽ بيشڪ (اوھان کان) پوءِ ايندڙ (به) ڄاڻندا آھيون.'),
(1827, 15, 25, '۽ بيشڪ تنھنجو پالڻھار کين کڙو ڪندو، ڇوته اُھو حڪمت وارو ڄاڻندڙ آھي.'),
(1828, 15, 26, '۽ بيشڪ اسان آدم کي کڙڪندڙ مٽيءَ مان جا ڳوھيل گندي ٿيل ھئي بڻايو.'),
(1829, 15, 27, '۽ ھن کان اڳ جنن کي ڄَرَ واريءَ باھ مان بڻايوسون.'),
(1830, 15, 28, '۽ جڏھن تنھنجي پالڻھار ملائڪن کي چيو ته آءُ آدم کي کڙڪندڙ مٽيءَ مان جا ڳوھيل گندي ٿيل ھئي بڻائيندڙ آھيان.'),
(1831, 15, 29, 'پوءِ جڏھن کيس پورو ڪري رھان ۽ منجھس پنھنجي روح مان ڦوڪيان ته کيس سجدو ڪندڙ ٿي ڪري پئجو.'),
(1832, 15, 30, 'پوءِ اُنھن مڙني ملائڪن گڏجي سجدو ڪيو.'),
(1833, 15, 31, 'سواءِ ابليس جي، سجدي ڪندڙن سان گڏ ھجڻ کان انڪار ڪيو.'),
(1834, 15, 32, '(الله) چيو ته اي ابليس توکي ڇا ٿيو جو سجدي ڪرڻ وارن مان نه ٿئين.'),
(1835, 15, 33, '(ھن) چيو ته مون کي نه جڳائيندو آھي ته آءٌ اھڙي ماڻھوءَ کي سجدو ڪريان جنھن کي کڙڪندڙ مٽيءَ مان جا ڳوھيل گندي ٿيل ھئي بڻايو اٿئي.'),
(1836, 15, 34, '(الله) فرمايو ته اُن (بھشت) مان نڪر ڇوته تون تڙيل آھين.'),
(1837, 15, 35, '۽ توتي قيامت تائين لعنت آھي.'),
(1838, 15, 36, '(ابليس) چيو ته اي منھنجا پالڻھار مون کي قيامت جي ڏينھن تائين مُھلت ڏي.'),
(1839, 15, 37, '(الله) فرمايو ته تون بيشڪ مُھلت ڏنلن مان آھين.'),
(1840, 15, 38, 'ٺھرايل وقت واري ڏينھن تائين.'),
(1841, 15, 39, '(ابليس) چيو ته اي منھنجا پالڻھار تو جو مون کي گمراہ ڪيو آھي تنھنڪري آءٌ انھن لاءِ زمين ۾ (گناھن کي) ضرور سينگاريندس ۽ (ٻـين) سڀني کي گمراہ ڪندس.'),
(1842, 15, 40, 'سواءِ اُنھن تنھنجن ٻانھن جي جي سچا آھن.'),
(1843, 15, 41, '(الله) فرمايو ته ھيءَ سڌي واٽ آھي مون تائين.'),
(1844, 15, 42, 'گمراھن مان جن تنھنجي تابعداري تنھن کان سواءِ (ٻـين) منھنجن ٻانھن تي تنھنجو ڪو زور نه آھي.'),
(1845, 15, 43, '۽ اُنھن مڙني گمراھن جي انجام واري جاءِ دوزخ آھي.'),
(1846, 15, 44, 'اُن کي ست دروازا آھن، ھر ڪنھن دروازي لاءِ انھن مان وراھيل ڀاڱو آھي.'),
(1847, 15, 45, 'بيشڪ پرھيزگار باغن ۽ چشمن ۾ ھوندا.'),
(1848, 15, 46, '(کين چئبو ته) انھن ۾ بي فڪر ٿي سلامتيءَ سان گھڙو.'),
(1849, 15, 47, '۽ سندن سينن ۾ جيڪي ڪينو ھوندو سو انھن مان ڪڍنداسين (ھڪ ٻئي جا) ڀائر ٿي آمھون سامھون تختن تي ويٺا ھوندا.'),
(1850, 15, 48, 'اُتي کين نڪو ڏک پھچندو ۽ نڪي اُتان ڪڍيا ويندا.'),
(1851, 15, 49, '(اي پيغمبر) منھنجن ٻانھن کي خبر ڏي ته آءٌ بخشڻھار مھربان آھيان.'),
(1852, 15, 50, '۽ منھنجو عذاب ئي ڏکوئيندڙ عذاب آھي.'),
(1853, 15, 51, '۽ (اي پيغمبر) انھن کي ابراھيمؑ جي مھمانن جي خبر ڏي.'),
(1854, 15, 52, 'جنھن مھل وٽس پھتا (تنھن مھل) چيائون سلام، ابراھيم چيو ته اسين اوھان کان ڊڄندڙ آھيون.'),
(1855, 15, 53, 'چيائون ته نه ڊڄ اسين توکي علم واري پُٽ جي مبارڪ ڏيون ٿا.'),
(1856, 15, 54, '(ابراھيم) چيو ته مون کي ھن ھوندي به مبارڪ ڏيو ٿا جو مون کي ٻڍاپڻ پھتي آھي پوءِ ڪھڙي طرح مُبارڪ ڏيو ٿا؟'),
(1857, 15, 55, 'چيائون ته توکي جائي مبارڪ ڏيون ٿا تنھنڪري نا اُميدن مان نه ٿيءُ.'),
(1858, 15, 56, '(ابراھيمؑ) چيو ته پنھنجي پالڻھار جي رحمت کان گُمراھن کانسواءِ (ٻيو) ڪير نا اُميد ٿيندو؟'),
(1859, 15, 57, '(وري ابراھيمؑ) چيو ته اي موڪليل (ملائڪو) اوھان جو ڪھڙو ڪم آھي.'),
(1860, 15, 58, 'چيائين ته اسين ڏوھارين جي قوم ڏانھن موڪليل آھيون.'),
(1861, 15, 59, 'مگر لُوط جي گھر وارا، جن مڙني کي اسين ڇوٽڪارو ڏيندڙ آھيون.'),
(1862, 15, 60, 'سواءِ سندس زال جي جنھن لاءِ ٺھراء ڪيوسون ته اُھا رھيلن مان آھي.'),
(1863, 15, 61, 'پوءِ جنھن مھل موڪليل (ملائڪ) لُوط جي گھراڻي وارن ڏانھن آيا.'),
(1864, 15, 62, '(تنھن مھل لُوط) چيو ته اوھين اوپري قوم آھيو.'),
(1865, 15, 63, 'چيائون ته (نه) بلڪ تو وٽ اُھو (عذاب) وٺي آيا آھيون جنھن ۾ (ڪافر) شڪ ڪندا ھوا.'),
(1866, 15, 64, '۽ تو وٽ سچ سان آندو اٿون ۽ بيشڪ اسين ضرور سچا آھيون.'),
(1867, 15, 65, 'پوءِ پنھنجي ڪٽنب سميت راتو واھ نڪر ۽ (تون) سندن پويان ھل ۽ اوھان مان ڪو ھڪڙو (به) پوئتي نه واجھائي ۽ جتي حُڪم ڪيو اَٿون اتي وڃو.'),
(1868, 15, 66, '۽ لُوط ڏانھن ھن حُڪم جو وحي ڪيوسون ته انھن جي پاڙ پرڀات مھل پٽي ويندي.'),
(1869, 15, 67, '۽ ڳوٺ وارا خوشيون ڪندا آيا.'),
(1870, 15, 68, '(لُوط) چيو ته ھي منھنجا مھمان آھن پوءِ مون لڄي نه ڪريو.'),
(1871, 15, 69, '۽ الله کان ڊڄو ۽ خوار نه ڪريوم.'),
(1872, 15, 70, 'چيائون ته توکي جھان وارن (جي مھماني) کان نه جھليو ھوسون ڇا؟'),
(1873, 15, 71, '(لوط) چيو ته جيڪڏھن اوھين (منھنجو چيو) ڪندڙ ٿيندؤ ته ھي منھنجون ڌيون حاضر آھن.'),
(1874, 15, 72, '(اي پيغمبر) تنھنجي حياتيءَ جو قسم آھي ته اھي (ڪافر) گمراھيءَ ۾ حيران رھن ٿا.'),
(1875, 15, 73, 'پوءِ سج اُڀرڻ مھل ڪَڙڪي ورتن.'),
(1876, 15, 74, '۽ اُن شھر جو مٿيون پاسو ان جي ھيٺ ڪيوسون ۽ مٿن پڪل مٽيءَ جا پھڻ وساياسون.'),
(1877, 15, 75, 'بيشڪ ان ۾ عِبرت وٺندڙن لاءِ نشانيون آھن.'),
(1878, 15, 76, '۽ اِھو ڳوٺ ھميشه (جي اچ وڃ) واري واٽ تي ھو.'),
(1879, 15, 77, 'بيشڪ اُن ۾ مؤمنن لاءِ نشانيون آھن.'),
(1880, 15, 78, '۽ ايڪه (جي ڳوٺن) وارا ظالم ھوا.'),
(1881, 15, 79, 'پوءِ کائن بدلو ورتوسون، (لُوطين ۽ ايڪه وارن جا) ٻئي ڳوٺ پڌري رستي تي ھوا.'),
(1882, 15, 80, '۽ بيشڪ حِجر وارن پيغمبرن کي ڪوڙو ڀانيو.'),
(1883, 15, 81, '۽ کين پنھنجون نشانيون ڏنيون سون پوءِ اُنھن کان مُنھن موڙيندڙ ٿيا.'),
(1884, 15, 82, '۽ بي فڪر ٿي جبلن مان ٽُڪي گھر ٺاھيندا ھوا.'),
(1885, 15, 83, 'پوءِ کين پرڀات مھل ڪڙڪي (اچي) ورتو.'),
(1886, 15, 84, 'پوءِ سندين ڪمائي کائن ڪجھ به ٽاري نه سگھيو.'),
(1887, 15, 85, '۽ آسمانن ۽ زمين کي ۽ جيڪي منجھن آھي سو رِٿ کانسواءِ نه بڻايو اٿون، ۽ قيامت ضرور اچڻي آھي پوءِ (سندن بي ادبين کان) سھڻي طرح ٽارو ڪر.'),
(1888, 15, 86, 'بيشڪ تنھنجو پالڻھار ڏاڍو پيدا ڪندڙ ڄاڻندڙ آھي.'),
(1889, 15, 87, '۽ بيشڪ توکي ٻٽيون ست آيتون (يعني سُورة فاتحة) ۽ وڏو قرآن ڏنوسون.'),
(1890, 15, 88, 'اُنھن کي ھر جنس جي سامان مان جيڪي شيون ڏنيوسون تن ڏانھن پنھنجيون اکيون نه کول ۽ نڪي مٿن ڏک ڪر ۽ مؤمنن لاءِ پنھنجون ٻانھون جھڪيون ڪر.'),
(1891, 15, 89, '۽ چؤ ته بيشڪ آءٌ پڌرو ڊيڄاريندڙ آھيان.'),
(1892, 15, 90, 'جھڙي طرح اُنھن وراھيندڙن تي (عذاب) نازل ڪيوسون.'),
(1893, 15, 91, 'جن قرآن کي پرزا پرزا ڪيو.'),
(1894, 15, 92, '(تھڙي طرح) تنھنجي پالڻھار جو قسم آھي ته انھن مڙني کان ضرور پڇنداسون.'),
(1895, 15, 93, 'جيڪي ڪندا ھوا تنھن بابت.'),
(1896, 15, 94, 'جيڪي توکي حُڪم ڪجي ٿو سو کولي ٻُڌاءِ ۽ مُشرڪن کان مُنھن موڙ.'),
(1897, 15, 95, 'اسان تنھنجي طرفان انھن چٿر ڪندڙن جي پورائي ڪئي.'),
(1898, 15, 96, 'جي الله سان ٻيو معبود مُقرّر ڪندا آُھن، پوءِ سگھوئي ڄاڻندا.'),
(1899, 15, 97, '۽ بيشڪ ڄاڻندا آھيون ته جيڪي چوندا آھن تنھن سببان تنھنجو سينو تنگ ٿيندو آھي.'),
(1900, 15, 98, '۽ تون پاڪائيءَ سان پنھنجي پالڻھار جي ساراھ ڪر ۽ سجدي ڪندڙن مان ھُج.'),
(1901, 15, 99, '۽ ايستائين پنھنجي پالڻھار جي عبادت (پيو) ڪر جيسين توکي موت پھچي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'الله جو حُڪم پُھتو تنھنڪري ان جي تڪڙ نه ڪريو، الله پاڪ آھي ۽ انھيءَ شيءِ کان مٿان آھي جيڪا ساڻس شريڪ مقرّر ڪندا آھن.'),
(1903, 16, 2, 'پنھنجن ٻانھن مان جنھن لاءِ گھرندو آھي تنھن تي پنھنجي ارادي سان ملائڪن کي وحيءَ سان ھن لاءِ نازل ڪندو آھي ته (ماڻھن کي) ڊيڄاريو (ته خدا ٿو فرمائي) ته مون کانسواءِ ٻيو ڪو عبادت جو لائق نه آھي تنھنڪري مون کان ڊڄو.'),
(1904, 16, 3, 'آسمانن ۽ زمين کي رِٿ سان بڻايائين، جيڪا شيءِ ساڻس شريڪ ڪندا آھن تنھن کان اُھو پاڪ آھي.'),
(1905, 16, 4, 'آدميءَ کي منيءَ مان خلقيائين پوءِ اوچتوئي اُھو پڌرو جھڳڙالو ٿي پيو.'),
(1906, 16, 5, '۽ اوھان لاءِ ڍور خلقيائين انھن مان گرم پوشاڪون ۽ (ٻيا) فائدا آھن ۽ منجھائن ڪي کائيندا آھيو.'),
(1907, 16, 6, 'جنھن مھل (اُھي ڍور) سھڙائيندا ايندا آھيو ۽ جنھن مھل ڇيڙيندا آھيو (تنھن مھل) اوھان لاءِ سونھن آھي.'),
(1908, 16, 7, '۽ جنھن شھر ڏانھن اوھين پنھنجي جيءُ جي اوکائيءَ کانسواءِ پھچي نه سگھندا آھيو تنھن ڏانھن اوھان جو بار کڻندا آھن، ڇوته اوھان جو پالڻھار وڏو ٻاجھارو مھربان آھي.'),
(1909, 16, 8, '۽ گھوڙا ۽ خچر ۽ گڏھ ھن لاءِ (بڻايائين) ته انھن تي اوھين چڙھو ۽ (انھن) کي زينت ڄاڻو، ۽ جيڪي شيون (اوھين) نه ڄاڻندا آھيو سي (به) پيدا ڪندو آھي.'),
(1910, 16, 9, '۽ الله ڏانھن سِڌو رستو پھچندو آھي ۽ اُن مان ڪي (رستا) مُڙيل آھن، ۽ جيڪڏھن (الله) گھري ھا ته اوھان مڙني کي سِڌو رستو ڏيکاري ھا.'),
(1911, 16, 10, 'اُھو (الله) آھي جنھن آسمان مان اوھان لاءِ پاڻي وسايو منجھانئس ڪجھ پئڻ جو آھي ۽ منجھانئس جھنگ (پيدا ٿيندو) آھي جنھن ۾ مال چاريندا آھيو.'),
(1912, 16, 11, 'اُن سان پوک ۽ زيتون ۽ کجيون ۽ ڊاکون ۽ ھر جنس جا ميوا اوھان لاءِ ڄمائيندو آھي، بيشڪ اُن ۾ پروڙيندڙ قوم لاءِ ضرور نشاني آھي.'),
(1913, 16, 12, '۽ اوھان لاءِ رات ۽ ڏينھن سج ۽ چنڊ تابع ڪيائين، ۽ تارا سندس حُڪم سان تابع ڪيل آھن، بيشڪ اُن ۾ ڄاڻندڙ قوم لاءِ ضرور نشانيون آھن.'),
(1914, 16, 13, '۽ قسمين قسمين رنگن جون (شيون ۽ جانور) جيڪي زمين ۾ پيدا ڪيائين سي به اوھان لاءِ آھن، بيشڪ اُن ۾ نصيحت وٺندڙ قوم لاءِ نشاني آھي.'),
(1915, 16, 14, '۽ اُھو (الله) آھي جنھن درياء کي ھن لاءِ تابع ڪيو ته اوھين منجھانئس تازو گوشت (يعني مڇي) کائو ۽ منجھانئس اُھي زيور (يعني موتي) ڪڍو جيڪي اوھين ڍڪيندا آھيو، ۽ منجھس ترندڙ ٻيڙيون ڏسندو آھين ۽ انھيءَ لاءِ ته اوھين سندس فضل ڳوليو ۽ ته مانَ اوھين شڪر ڪريو.'),
(1916, 16, 15, '۽ زمين ۾ مُحڪم جبل کوڙيائين ته متان اُھا اوھان کي ڌوڏو ڏئي ۽ نديون ۽ رستا بڻايائين ته مانَ اوھين سِڌو رستو لھو.'),
(1917, 16, 16, '۽ نشان (پيدا ڪيائين)، ۽ تارن سان اُھي سِڌو رستو لھندا آھن.'),
(1918, 16, 17, 'جيڪو پيدا ڪري سو اُنھيءَ جھڙو آھي ڇا جو (ڪي به) پيدا نه ڪري؟ پوءِ ڇونه نصيحت وٺندا آھيو؟'),
(1919, 16, 18, '۽ جيڪڏھن اوھين الله جي نعمتن کي ڳڻيو ته انھن کي (ڪڏھن به) ڳڻي نه سگھندؤ، ڇوته الله بخشڻھار مھربان آھي.'),
(1920, 16, 19, '۽ جيڪي لڪائيندا آھيو ۽ جيڪي پڌرو ڪندا آھيو سو الله ڄاڻندو آھي.'),
(1921, 16, 20, '۽ جن کي الله کانسواءِ سڏيندا آھن سي ڪا شيء خلقي نه سگھندا آھن ۽ اُھي (پاڻ) خلقيا ويا آھن.'),
(1922, 16, 21, 'بي جان مُردا آھن، ۽ نه ڄاڻندا آھن ته ڪڏھن کڙا ڪيا ويندا.'),
(1923, 16, 22, 'اوھان جو معبود ھڪڙو خُدا آھي، پوءِ جيڪي آخرت کي نه مڃيندا آھن تن جون دليون مڃڻ واريون نه آھن ۽ اُھي وڏائي ڪندڙ آھن.'),
(1924, 16, 23, '(ڪافر) جيڪي لڪائيندا آھن ۽ جيڪي (ھو) پڌرو ڪندا آھن سو بلاشڪ الله ڄاڻندو آھي، بيشڪ اُھو وڏائي ڪندڙن کي پيار نه ڪندو آھي.'),
(1925, 16, 24, '۽ جڏھن اُنھن کي چئبو آھي ته اوھان جي پالڻھار ڇا نازل ڪيو آھي؟ (تڏھن) چوندا آھن ته اڳين جون اکاڻيون.'),
(1926, 16, 25, '(اھو سندن چوڻ) ھن لاءِ آھي جو قيامت جي ڏينھن پنھنجو پورو بار کڻندا ۽ جن کي اڻ ڄاڻائيءَ سان گمراہ ڪندا آھن تن جو (به) ڪجھ بار کڻندا، خبردار جيڪو (به) بار کڻندا سو بڇڙو آھي.'),
(1927, 16, 26, 'جيڪي کائن اڳ ھوا تن بيشڪ فريب ڪيو پوءِ الله جو حُڪم سندن اڏاوتن کي پيڙھي کان پٽيو ۽ انھن تي مٿانئن ڇت اچي ڪري ۽ کين عذاب اُتان پُھتو جتان ڄاتائون ئي نه ٿي.'),
(1928, 16, 27, 'وري قيامت جي ڏينھن کين خوار ڪندو ۽ چوندو ته (اوھان جا) مون سان ٺھرايل شريڪ جن جي بابت اوھين تڪرار ڪندا ھيؤ سي ڪٿي آھن؟ جن کي عِلم ڏنو ويو سي چوندا ته اَڄ انھن ڪافرن تي خواري ۽ بڇڙائي آھي.'),
(1929, 16, 28, 'جن جو ملائڪ اھڙيءَ حالت ۾ ساہ ڪڍندا آھن جو پاڻ تي ظالم ھوا، اُھي صُلح پيش ڪندا (۽ چوندا ته) اسان ڪا بڇڙائي نه ڪئي ھئي، (ملائڪ چوندا ته) ھائو! جيڪي اوھين ڪندا ھيؤ سو الله ڄاڻندڙ آھي.'),
(1930, 16, 29, 'پوءِ دوزخ جي دروازن مان گھڙو منجھس سدائين رھندڙ ھجو، پوءِ وڏائي ڪندڙن جي جاءِ بڇڙي آھي.'),
(1931, 16, 30, '۽ پرھيزگارن کي چيو ويندو ته ته اوھان جي پالڻھار ڇا نازل ڪيو آھي، چوندا ته چڱائي، جن ھن دنيا ۾ چڱائي ڪئي تن لاءِ چڱائي آھي، ۽ آخرت جو گھر ضرور ڀلو آھي، ۽ پرھيزگارن جو گھر ضرور ڀلو آھي.'),
(1932, 16, 31, 'جو ھميشه جي رھڻ وارا باغ آھن اُن ۾ گھڙندا انھن جي ھيٺان واھيون وھنديون آھن جيڪي گھرندا سو انھن لاءِ منجھس آھي، اھڙي طرح الله پرھيزگارن کي بدلو ڏيندو.'),
(1933, 16, 32, '(اِھي اُھي آھن) جن کي ملائڪ پاڪ حالت ۾ ماريندا آھن (ملائڪ) چوندا آھن اَلسّلامُ عليڪم جيڪي ڪندا ھيؤ تنھن سببان بھشت ۾ گھِڙو.'),
(1934, 16, 33, 'ڪافر (موت جي) ملائڪن جي پاڻ وٽ اچڻ يا تنھنجي پالڻھار (جي عذاب) جي حُڪم اچڻ کانسواءِ (ٻيو) ڪو انتظار نه ڪندا آھن، جيڪي کائن اڳ ھوا تن به اھڙي طرح ڪيو ھو، ۽ الله مٿن ظلم نه ڪيو پر اُھي پاڻ تي ظلم ڪندا ھوا.'),
(1935, 16, 34, 'پوءِ کين سندن ڪرتوتن جي بڇڙائي پھتي ۽ جنھن لاءِ ٺـٺوليون ڪندا ھوا سا کين ويڙھي ويئي.'),
(1936, 16, 35, '۽ مُشرڪ چوندا آھن ته جيڪڏھن الله گھري ھا ته اُن ڌاران ڪا شيء نڪي اسين پوڄيون ھا ۽ نڪي اسان جا ابا ڏاڏا (پوڄين ھا) ۽ نڪي اُن جي حُڪم کانسواءِ ڪا شيء حرام ڪريون ھا، اھڙي طرح جيڪي کائن اڳ ھوا تن به ڪيو ھو، پوءِ پيغمبرن تي پڌري (پيغام) پھچائڻ کانسواءِ (ٻيو ڪي لازم) نه آھي.'),
(1937, 16, 36, '۽ بيشڪ سڀڪنھن ٽولي ۾ (ھڪ) پيغمبر موڪليوسون ته الله جي عبادت ڪريو ۽ بتن (جي پوڄا) کان پري ٿيو، پوءِ منجھائن ڪي اھڙا آھن جن کي الله ھدايت ڪئي ۽ منجھائن ڪي اھڙا آھن جن تي گمراھي ثابت ٿي، پوءِ مُلڪن ۾ گھُمو وري ڏسو ته ڪوڙن جي پڇاڙي ڪيئن ٿي؟'),
(1938, 16, 37, '(اي پيغمبر) جيڪڏھن ھنن کي ھدايت ڪرڻ لاءِ حرص ڪندين (ته منجھائن ڪوبه فائدو نه آھي) ڇوته الله جنھن کي گمراھ ڪندو آھي تنھن کي ھدايت نه ڪندو آھي ۽ انھن جو ڪوبه مددگار نه آھي.'),
(1939, 16, 38, '۽ الله جو پڪي طرح قسم کنيائون ته جيڪو مرندو تنھن کي الله وري نه جياريندو، ھائو! (وري جياريندو) اُن ڳالھ تي پڪ وعدو ٿي چڪو آھي مگر گھڻا ماڻھو نه ڄاڻندا آھن.'),
(1940, 16, 39, 'ھن لاءِ (وري جياريندو) ته جنھن بابت تڪرار ڪندا آھن سو انھن کي پڌرو ڪري ٻڌائي ۽ ته ڪافر ڄاڻن ته اُھي (پاڻ) ڪُوڙا ھوا.'),
(1941, 16, 40, 'ڪنھن به شيءِ لاءِ اسان جو چوڻ ھن (چوڻ) کانسواءِ نه آھي ته جڏھن اسين اُنکي پيدا ڪرڻ گھرندا آھيون ته اُن لاءِ چوندا آھيون ته ٿيءُ ته ٿي پوندي آھي.'),
(1942, 16, 41, '۽ جن پاڻ تي ظلم ٿيڻ کانپوءِ الله (جي واٽ) ۾ وطن ڇڏيو تن کي ضرور دنيا ۾ چڱي جاءِ ڏيندا آھيون، ۽ ضرور آخرت جو اجر تمام وڏو آھي، جيڪڏھن ڄاڻن ھا.'),
(1943, 16, 42, '(اُھي نعمتون انھن کي آھن) جن صبر ڪيو ۽ (جي) پنھنجي پالڻھار تي ڀروسو ڪن ٿا.'),
(1944, 16, 43, '۽ (اي پيغمبر) توکان اڳ (ڪو پيغمبر) مَردن کان کانسواءِ نه موڪليو اٿون انھن ڏانھن وحي ٿي ڪيوسون پوءِ (اي ماڻھو) جيڪڏھن اوھين نه ڄاڻندا آھيو ته ڪتاب وارن کان پڇو.'),
(1945, 16, 44, 'دليلن ۽ ڪتابن سان (موڪلياسون)، ۽ اسان توڏانھن قرآن ھن لاءِ نازل ڪيو ته جيڪي ماڻھن ڏانھن نازل ڪيو ويو آھي سو اُنھن کي بيان ڪري ٻڌائين ۽ مانَ اُھي پروڙين.'),
(1946, 16, 45, 'جن بڇڙي رٿ رٿي سي (ھن کان) بي ڀوا ٿيا آھن ڇا ته الله کين زمين ۾ ڳھائي ڇڏي يا جتان نه ڄاڻندا ھُجن اُتان مٿن عذاب اچي.'),
(1947, 16, 46, 'يا اُنھن کي سندن گھمڻ ڦرڻ ۾ پڪڙي پوءِ اُھي عاجز ڪرڻ وارا نه آھن.'),
(1948, 16, 47, 'يا ڀوائتيءَ حالت ۾ کين پڪڙي، پوءِ اوھان جو پالڻھار ٻاجھارو مھربان آھي.'),
(1949, 16, 48, 'الله جيڪي شيون خلقيون تن ڏانھن ڇونه ڏسندا آھن جو سندن پاڇا سڄي ۽ کٻي (پاسي) الله کي سجدو ڪندڙ ٿي لڙندا آھن ۽ اُھي عاجزي ڪندڙ آھن.'),
(1950, 16, 49, '۽ جيڪي آسمانن ۾ آھن سي ۽ جيڪي جانور زمين ۾ آھن سي ۽ ملائڪ الله کي سجدو ڪندا آھن ۽ اُھي وڏائي نه ڪندا آھن.'),
(1951, 16, 50, '(ملائڪ) پنھنجي پالڻھار کان ڊڄندا آھن جو سندن مٿان غالب آھي ۽ جيڪي حُڪم ڪبو اٿن سو ڪندا آھن.'),
(1952, 16, 51, '۽ الله فرمايو آھي ته اوھين ٻه خدا ڪري نه وٺو، اُن ھڪ الله کانسواءِ ٻيو ڪوبه عبادت جو لائق نه آھي، تنھنڪري اوھين خاص مون کان ڊڄندا رھو.'),
(1953, 16, 52, '۽ جيڪي آسمانن ۽ زمين ۾ آھي سو سندس آھي ۽ اُن جي عبادت سدائين (لازم) آھي، پوءِ الله کانسواءِ ٻـين کان ڇو ڊڄندا آھيو؟'),
(1954, 16, 53, '۽ جيڪا نعمت اوھان تي ٿيل آھي سا الله جي پار کان آھي پوءِ جڏھن اوھان تي سختي پھچي ٿي ته ڏانھس دانھون ڪندا آھيو.'),
(1955, 16, 54, 'وري جڏھن اوھان کان ڏک لاھي (تڏھن) جھٽ پٽ اوھان مان ڪا ٽولي پنھنجي پالڻھار سان شريڪ مُقرّر ڪندي آھي.'),
(1956, 16, 55, 'ھن لاءِ ته کين جيڪي ڏنوسون تنھن جي بي شڪري ڪن، پوءِ دنيا ۾ خوش گذاريو، پوءِ سِگھو ڄاڻندؤ.'),
(1957, 16, 56, '۽ جيڪي کين ڏنوسون تنھن مان (ڪافر) اُنھن (بُتن) لاءِ حصّو مُقرّر ڪندا آھن جي نه ڄاڻندا آھن، الله جو قسم آھي ته جيڪي ٺاھ ٺاھيندا آھيو تنھن بابت ضرور پڇيا ويندؤ.'),
(1958, 16, 57, '۽ الله لاءِ ڌيئرون مُقرّر ڪندا آھن اُھو پاڪ آھي ۽ پاڻ لاءِ جيڪي وڻندو اٿن (يعني پُٽ سو مُقرّر ڪندا آھن).'),
(1959, 16, 58, '۽ جڏھن منجھائن ڪنھن ھڪ کي ڌيءُ (ڄمڻ) جي خبر ڏبي آھي (تڏھن) سندس مُنھن ڪارو ٿي ويندو آھي ۽ اُھو ڏک ڀريل ھوندو آھي.'),
(1960, 16, 59, 'جيڪا کيس بڇڙي خبر ڏني وئي تنھنجي ناراضپي سببان قوم کان پيو لڪندو آھي، (ڳڻتي ۾ پوندو آھي ته) اُن ڌيءُ کي خواريءَ ۾ رکي يا اُن کي مٽيءَ ۾ دٻي ڇڏي، خبردار اُھي جيڪي نبيرو ڪندا آھن سو بڇڙو آھي.'),
(1961, 16, 60, 'جيڪي آخرت کي نه مڃيندا آھن تن جو مثال بڇڙو آھي، ۽ الله جو مثال مٿاھون آھي، ۽ اھو غالب حڪمت وارو آھي.'),
(1962, 16, 61, '۽ جيڪڏھن الله ماڻھن کي سندن ظلم سببان پڪڙي ته ڪنھن چرندڙ کي زمين تي نه ڇڏي پر اُنھن کي ٺھرايل مدّت تائين مُھلت ڏني اٿس، پوءِ جنھن مھل سندن مھلت پھچندي (تنھن مھل) ھڪ گھڙي نڪي دير ڪندا ۽ نڪي اڳڀرو ويندا.'),
(1963, 16, 62, '۽ الله لاءِ اُھا شيء مقرر ڪندا آھن جيڪا (پاڻ) ناپسند ڪندا آھن ۽ سندن زبانون ڪوڙ بڪنديون آھن ته سندن لاءِ (ڇوٽڪاري جي) جي چڱائي آھي، بلاشڪ اُنھن لاءِ (دوزخ جي) باھ آھي ۽ اُھي حد کان لنگھي ويل آھن.'),
(1964, 16, 63, 'الله جو قسم آھي ته بيشڪ توکان اڳ اُمّتن ڏانھن (پيغمبر) موڪلياسون پوءِ کين شيطان سندن عمل چڱا ڪري ڏيکاريا پوءِ اُھو اڄ سندن دوست آھي ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(1965, 16, 64, '۽ (اي پيغمبر) توتي ڪتاب ھن کانسواءِ نازل نه ڪيوسون ته جن بابت تڪرار ڪيو تن کي تون بيان ڪرين ۽ (پڻ) مؤمن قوم لاءِ ھدايت ۽ ٻاجھ آھي.'),
(1966, 16, 65, '۽ الله آسمانن کان (مينھن جو) پاڻي وسايو پوءِ اُن سان زمين کي سندس ويرانيءَ کانپوءِ آباد ڪيائين، بيشڪ اُن ۾ ٻڌندڙ قوم لاءِ نشاني آھي.'),
(1967, 16, 66, '۽ بيشڪ ڍورن ۾ اوھان لاءِ عبرت آھي، جيڪي سندن پيٽن ۾ آھي تنھن مان ڇيڻن ۽ رت جي وچون نج کير اوھان کي پيارينديون آھن جو پيئندڙن کي وڻندڙ آھي.'),
(1968, 16, 67, '۽ کجين جي ۽ ڊاکن جي ڦرن مان ڪو (اھڙو قسم) آھي جو اُن مان شراب ۽ سٺي روزي بڻائيندا آھيو، بيشڪ اُن ۾ ڄاڻندڙ قوم لاءِ نشاني آھي.'),
(1969, 16, 68, '۽ (اي پيغمبر) تنھنجي پالڻھار ماکيءَ جي مَک ڏانھن الھام ڪيو ته ڪن جبلن ۾ ۽ وڻن ۾ ۽ جن شين مان (ماڻھو گھرن جون) ڇتيون جوڙيندا آھن تن ۾ گھر بڻاءِ.'),
(1970, 16, 69, 'وري ھر ڪنھن جنس جي ميون مان کاءُ پوءِ پنھنجي پالڻھار جي واٽن تي عاجزيءَ سان ھل، سندس پيٽن مان رنگارنگ پئڻ جي شيء (يعني ماکي) نڪرندي آھي منجھس ماڻھن لاءِ شفا آھي، بيشڪ اُن ۾ سوچ ڪندڙ قوم لاءِ عبرت آھي.'),
(1971, 16, 70, '۽ الله اوھان کي خلقيو ۽ وري اوھان کي ماريندو ۽ اوھان مان نڪميءَ عُمر ڏانھن ھن لاءِ موٽايو ويندو آھي ته ڄاڻڻ کانپوءِ ڪُجھ نه ڄاڻي، ڇوته الله ڄاڻندڙ وس وارو آھي.'),
(1972, 16, 71, '۽ الله اوھان مان ھڪڙن کي ٻـين کان وڌيڪ روزي ڏني آھي، پوءِ اُھي وڌ ڏنل روزيءَ وارا پنھنجي روزيءَ کي پنھنجن ٻانھن تي موٽائڻ وارا نه آھن ته اُھي (سڀ) منجھس برابر ھُجن، پوءِ الله جي نعمتن جو ڇو انڪار ڪندا آھن؟'),
(1973, 16, 72, '۽ الله اوھان لاءِ اوھان جي جنس مان زالون پيدا ڪيون ۽ اوھان جي زالن مان پُٽ ۽ پوٽا پيدا ڪيائين ۽ اوھان کي سٺين شين مان روزي ڏنائين، پوءِ ڇو ڪوڙ کي مڃيندا آھن ۽ اُھي ڇو الله جي نعمتن جو انڪار ڪندا آھن.'),
(1974, 16, 73, '۽ الله ڌاران انھن جي عبادت ڪندا آھن جيڪي آسمانن ۽ زمين مان کين روزي ڏيڻ جو ڪجھ اختيار نه رکندا آھن ۽ نڪي (ڪجھ ڪري) سگھندا آھن.'),
(1975, 16, 74, 'پوءِ الله لاءِ (بڇڙا) مثال بيان نه ڪريو، ڇوته الله ڄاڻندڙ آھي ۽ اوھين نه ڄاڻندا آھيو.'),
(1976, 16, 75, 'الله ھڪ خريد ٿيل ٻانھي جو مثال بيان ڪري ٿو جنھن جو ڪنھن شيءِ تي وس نه ھُجي ۽ (ٻيو) اُھو جنھن کي پاڻ وٽان چڱي روزي ڏنيسون پوءِ اُھو اُن سان (اسان جي واٽ ۾) ڳجھو ۽ پڌرو خرچ ڪري، (اھي ٻئي پاڻ ۾) برابر آھن ڇا؟ سڀ ساراھ الله کي جڳائي، پر انھن مان گھڻا نه ڄاڻندا آھن.'),
(1977, 16, 76, '۽ الله ٻن مڙسن جو (ٻيو) مثال بيان ڪري ٿو جن ٻنھي مان ھڪڙو گنگو ھجي ڪنھن شيء تي وس ڪونه ھجيس ۽ اُھو پنھنجي مالڪ تي بار ھجي جيڏانھن موڪليس اُتان ڪا ڀَلائي نه آڻي اُھو اُنھيءَ جي برابر آھي ڇا جيڪو انصاف جو حُڪم ڏئي ۽ اُھو سڌيءِ واٽ تي ھُجي.'),
(1978, 16, 77, '۽ آسمانن ۽ زمين جو ڳُجھ الله کي (معلوم) آھي، ۽ قيامت (آڻڻ) جو ڪم (سندس اڳيان) رڳي اک ڇنڀ (جي دير) جھڙو آھي بلڪ اُھو انھيءَ کان به وڌيڪ نزديڪ آھي، ڇوته الله سڀڪنھن شيء تي وس وارو آھي.'),
(1979, 16, 78, '۽ الله اوھان کي اوھان جي مائن جي پيٽن مان ڪڍيو جو ڪُجھ نه ڄاڻندا ھيؤ ۽ اوھان جا ڪنّ ۽ اکيون ۽ دليون بڻايائين ته مانَ اوھين شڪرانو ڪريو.'),
(1980, 16, 79, 'آسمانن جي ھوا ۾ تابع ڪيل پکين ڏانھن نه ڏسندا آھن ڇا؟ انھن کي الله کانسواءِ ڪونه جھليندو آھي، بيشڪ اُن ۾ ايمان واريءَ قوم لاءِ نشانيون آھن.'),
(1981, 16, 80, '۽ الله اوھان جي گھرن مان اوھان لاءِ آرام (جو ھنڌ) بڻايو ۽ ڍورن جي چمڙن مان اوھان لاءِ گھر بڻايائين جن کي اوھين پنھنجي مسافريءَ جي ڏينھن ۽ پنھنجي ھڪ ھنڌ رھڻ جي ڏينھن ۾ ھلڪو ڄاڻندا آھيو ۽ سندين اُنّن ۽ سندين ملسن ۽ سندين ڏاسن مان (گھر جو) اسباب ۽ فائدي جو سامان بڻايو ٿا (جوڳچ) مُدّت تائين (ڪم ڏئي ٿو).'),
(1982, 16, 81, '۽ الله پنھنجين خلقيل شين مان اوھان لاءِ ڇانئون بڻايون ۽ جبلن مان اوھان لاءِ چُرون بڻايائين ۽ اوھان لاءِ ڪپڙا بڻايائين جي اوھان کي گرميءَ (۽ سرديءَ) کان بچائيندا آھن، اھڙي طرح اوھان تي پنھنجي نعمت پوري ڪندو آھي ته مانَ اوھين فرمانبرداري ڪريو.'),
(1983, 16, 82, 'پوءِ جيڪڏھن ڦرندا ته (اي پيغمبر) توتي پڌري پيغام پھچائڻ کانسواءِ (ٻيو ڪي) نه آھي.'),
(1984, 16, 83, 'الله جي نعمت ڄاڻندا آھن وري اُن کي نه مڃيندا آھن ۽ اُنھن مان گھڻا ڪافر آھن.'),
(1985, 16, 84, '۽ جنھن ڏينھن ھر ڪنھن ٽوليءَ مان (ھڪ) شاھد کڙو ڪنداسون اُنھي ڏينھن ڪافرن کي (عُذر ڪرڻ لاءِ) موڪل نه ڏبي ۽ نڪي اُنھن جا عُذر قبول ڪيا ويندا.'),
(1986, 16, 85, '۽ جڏھن ظالم عذاب ڏسندا تڏھن کائن (عذاب) نڪي ھلڪو ڪبو ۽ نڪي اُنھن کي مُھلت ڏبي.'),
(1987, 16, 86, '۽ جڏھن مشرڪ پنھنجن شريڪن کي ڏسندا (تڏھن) چوندا ته اي اسان جا پالڻھار ھيء اسان جا اُھي شريڪ آھن جن کي توکانسواءِ سڏيندا ھواسين، پوءِ اُھي (بُت) انھن کي ورندي ڏيندا ته اوھين ڪُوڙا آھيو.'),
(1988, 16, 87, '۽ انھيءَ ڏينھن الله جي آڏو عاجزي ڪندا ۽ جيڪو ٺاھ ٺاھيندا ھوا سو کائن ڀُلجي ويندو.'),
(1989, 16, 88, 'جن انڪار ڪيو ۽ الله جي واٽ کان (ماڻھن کي) جھليو تن کي عذاب جي مٿان عذاب انھيءَ سببان وڌائينداسون جو (ھو) فساد ڪندا ھوا.'),
(1990, 16, 89, '۽ اُن ڏينھن سڀڪنھن ٽوليءَ ۾ سندين جنس مان مٿن شاھد کڙا ڪنداسون ۽ (اي پيغمبر) توکي ھنن (يعني اُمّت) تي شاھد آڻينداسون، ۽ ھر شيء جي پڌرائيءَ لاءِ ۽ مُسلمانن جي ھدايت ۽ ٻاجھ ۽ خوشخبري ڏيڻ لاءِ توتي ڪتاب نازل ڪيوسون.'),
(1991, 16, 90, 'بيشڪ الله انصاف ۽ ڀلائي (ڪرڻ) ۽ مائٽي وارن کي ڏيڻ جو حُڪم ڪري ٿو ۽ بي حيائي ۽ مدائي ۽ زبردستي کان جھلي ٿو، اوھان کي نصيحت ڏئي ٿو ته مانَ اوھين نصيحت وٺو.'),
(1992, 16, 91, '۽ جڏھن اوھين (الله جو نالو وٺي) پاڻ ۾ انجام ڪريو (تڏھن) الله جو انجام پاڙيو ۽ اوھين قَسمن کي سندن پڪي ڪرڻ کانپوءِ نه ڀڃو جو بيشڪ الله کي پاڻ تي نگھبان ڪيو اٿوَ، بيشڪ جيڪي ڪندا آھيو سو الله ڄاڻندو آھي.'),
(1993, 16, 92, '۽ اُن زال وانگر نه ٿيو جنھن پنھنجو سُٽ ڪتيل پڪي ڪرڻ کانپوءِ ڇني ٽُڪر ٽُڪر ڪري ڇڏيو، اوھين قسمن کي ھن لاءِ پاڻ ۾ حيلو بڻايو ٿا ته (ھڪ) ٽولي ٻي ٽوليءَ کان وڌيڪ ٿئي، الله ته اوھان کي رڳو اُن سان پرکيندو آھي، ۽ جنھن بابت اوھين تڪرار ڪندا آھيو سو قيامت جي ڏينھن اوھان جي لاءِ ضرور بيان ڪندو.'),
(1994, 16, 93, '۽ جيڪڏھن الله گھري ھا ته اوھان کي ھڪ ٽولي ڪري ھا پر جنھن لاءِ وڻندو اٿس تنھن کي گمراھ ڪندو آھي ۽ جنھن لاءِ وڻندو اٿس تنھن کي ھدايت ڪندو آھي، ۽ جيڪي ڪندا آھيو تنھن بابت اوھين ضرور پُڇبؤ.'),
(1995, 16, 94, '۽ اوھين پنھنجن قسمن کي پاڻ ۾ ٺڳي نه بڻايو نه ته اُنھن جي پڪي ڪرڻ کانپوءِ اوھان جا پيرَ ترڪندا ۽ (ماڻھن کي) الله جي واٽ کان جھلڻ سببان سزا چکندؤ، ۽ اوھان کي وڏو عذاب ٿيندو.'),
(1996, 16, 95, '۽ الله جي انجام کي ٿوري مُلھ سان نه وِڪڻو، ڇوته جيڪڏھن اوھين ڄاڻندا آھيو ته جيڪي الله وٽ آھي سو اوھان جي لاءِ چڱو آھي.'),
(1997, 16, 96, 'جيڪي اوھان وٽ آھي سو کپي ويندو ۽ جيڪي الله وٽ آھي سو ھميشہ رھڻ وارو آھي، ۽ جن صبر ڪيو تن کي سندن عملن جو بلڪل چڱي طرح بدلو ڏينداسون.'),
(1998, 16, 97, 'جنھن چڱو عمل ڪيو (پوءِ) اُھو مڙس ھُجي يا زال ۽ اُھو مؤمن ھوندو ته ضرور کيس چڱي حياتي ڏئي جيئرو ڪنداسون، ۽ جيڪي ڪندا ھوا تنھنجو بدلو کين تمام چڱيءَ طرح ڏينداسون.'),
(1999, 16, 98, 'پوءِ (اي پيغمبر) جڏھن قرآن پڙھين تڏھن تڙيل شيطان (جي شر) کان الله جي سام پؤ.'),
(2000, 16, 99, 'ان کي ايمان وارن تي ۽ جي پنھنجي پالڻھار تي ڀروسو ڪندا آھن تن تي ڪو غلبو نه آھي.'),
(2001, 16, 100, 'سندس غلبو رڳو اُنھن تي آھي جيڪي کيس دوست رکندا آھن ۽ (انھن تي به) جيڪي الله سان شريڪ (مقرّر) ڪندا آھن.'),
(2002, 16, 101, '۽ جڏھن ھڪڙي آيت جي جاءِ تي ٻي آيت مٽائي آڻيندا آھيون ۽ الله جيڪي نازل ڪندو آھي سو چڱي طرح ڄاڻندڙ آھي (تڏھن) چوندا آھن ته تون رڳو پاڻون بڻائيندڙ آھين، (نه!) بلڪ اُنھن مان گھڻا نه ڄاڻندا آھن.'),
(2003, 16, 102, '(اي پيغمبر) کين چؤ ته اُھو قرآن تنھنجي پالڻھار وٽان جبرئيل سچ سان ھن لاءِ نازل ڪيو آھي ته مؤمنن کي ثابت رکي ۽ مُسلمانن لاءِ ھدايت ۽ خوشخبري ھجي.'),
(2004, 16, 103, '۽ بيشڪ ڄاڻندا آھيون ته اُھي چوندا آھن ته اُن (پيغمبر) کي ڪو ماڻھو سيکاريندو آھي، جنھن ڏانھن اھا نسبت ڪندا آھن تنھن جي ٻولي عجمي آھي ۽ ھي (قرآن) پڌري عربي ٻوليءَ ۾ آھي.'),
(2005, 16, 104, 'بيشڪ جيڪي الله جي آيتن کي نه مڃيندا آھن تن کي الله سڌو رستو نه ڏيکاريندو آھي ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(2006, 16, 105, 'ڪوڙو ٺاھ انھن کان سواءِ ڪونه ٺاھيندو آھي جيڪي الله جي آيتن کي نه مڃيندا آھن، ۽ اُھي (پاڻ) ڪُوڙا آھن.'),
(2007, 16, 106, 'جنھن کي (ايمان کان ڦرڻ جو) زور ڪيو وڃي (اُھو زبان سان ڪي چوي) ۽ سندس دل ايمان سان آراميل ھجي اُن کانسواءِ جيڪو پنھنجي ايمان آڻڻ کانپوءِ الله جو انڪار ڪري بلڪ ڪُفر سان سينو ويڪرو ڪري (يعني راضي ٿئي) اھڙن تي الله جو ڏمر آھي، ۽ انھن تي وڏو عذاب آھي.'),
(2008, 16, 107, 'اھو ھن ڪري آھي جو انھن دنيا جي حياتيءَ کي آخرت کان زيادہ پسند ڪيو آھي ۽ (انھيءَ ڪري) ته الله ڪافرن جي قوم کي ھدايت نه ڪندو آھي.'),
(2009, 16, 108, 'اِھي اُھي آھن جو سندين دلين ۽ سندن ڪَنن ۽ سندن اکين تي الله مُھر ھنئي، ۽ اُھي بي خبر آھن.'),
(2010, 16, 109, 'بلاشڪ اُھي ئي آخرت ۾ ڇيئي وارا آھن.'),
(2011, 16, 110, 'وري بيشڪ تنھنجو پالڻھار انھن کي (بخشي ٿو) جيڪي (ڪفر جي ڪلمي چوڻ لاءِ ڪافرن جي ھٿان) ايذايا ويا پوءِ وطن ڇڏيائون وري جھاد ڪيائون ۽ صبر ڪيائون بيشڪ تنھنجو پالڻھار ان کانپوءِ ضرور بخشڻھار مھربان آھي.'),
(2012, 16, 111, 'انھيءَ ڏينھن سڀڪو جيءُ پنھنجي طرفان جھيڙو ڪندو ايندو ۽ سڀڪنھن نفس جيڪي ڪمايو تنھنجو کيس پورو بدلو ڏبو ۽ انھن سان ظلم نه ڪبو.'),
(2013, 16, 112, '۽ الله ھڪ ڳوٺ جو (ھڪ) قِصو بيان ڪيو جو بي ڀئو آراميل ھو ان کي سندس روزي سڀڪنھن ھنڌان سوکائيءَ سان ايندي ھئي پوءِ الله جي نعمتن جي بي شڪرائي ڪيائون پوءِ جيڪي ڪندا ھوا تنھن سببان الله کين بُک ۽ ڀؤ جي پوشاڪ چکائي.'),
(2014, 16, 113, '۽ بيشڪ منجھائن (ھڪ) پيغمبر وٽن آيو پوءِ کيس ڪُوڙو ڀانيائون تنھنڪري کين عذاب پڪڙيو ۽ اُھي ظالم ھوا.'),
(2015, 16, 114, 'پوءِ الله اوھان کي جيڪا حلال (۽) سُٺي روزي ڪري ڏني تنھن مان کائو، ۽ جيڪڏھن اُھين خاص الله جي عبادت ڪندا آھيو ته سندس نعمتن جو شڪرانو ڪريو.'),
(2016, 16, 115, 'مئل ڍور ۽ رت ۽ سوئر جو ماس ۽ جنھن شيء کي الله جي نالي کانسواءِ ٻئي جي نالي سان سڏيو وڃي تنھن کانسواءِ ٻيو (ڪي به) اوھان تي حرام نه ڪيائين، پوءِ جيڪو نافرماني ڪندڙ نه ھجي ۽ نڪي حد کان لنگھندڙ ٿي لاچار ٿئي (۽ کائي) ته بيشڪ الله بخشڻھار مھربان آھي.'),
(2017, 16, 116, '۽ جن شين کي اوھان جون زبانون ڪوڙ سان بيان ڪنديون آھن تن لاءِ (ھنڪري) نه چئو ته ھي حلال آھي ۽ ھيءُ حرام آھي ته الله تي ڪُوڙو ٺاھ ٺاھيو، بيشڪ جيڪي الله تي ڪُوڙو ٺاھ ٺاھيندا آھن سي (ڪڏھن) نه ڇٽندا.'),
(2018, 16, 117, '(۽ ھيءُ حياتي) ٿوري وَٿ آھي، ۽ اُنھن لاءِ آخرت ۾ ڏکوئيندڙ عذاب آھي.'),
(2019, 16, 118, '۽ جيڪي توتي ھن کان اڳ بيان ڪيوسون سو يھودين تي حرام ڪيو ھوسون، ۽ اسان مٿن ظلم نه ڪيو پر (ھو) پاڻ تي ظلم ڪندا ھوا.'),
(2020, 16, 119, 'وري جن بي سمجھائي سان بڇڙا ڪم ڪيا وري اُن کانپوءِ توبه ڪيائون ۽ پاڻ سڌاريائون تن لاءِ تنھنجو پالڻھار اُن (توبه) کانپوءِ بلڪل بخشڻھار مھربان آھي.'),
(2021, 16, 120, 'ابراھيمؑ ھڪ طرفو ٿيل الله جي عبادت ڪندڙ امام ھو، ۽ مُشرڪن مان نه ھو.'),
(2022, 16, 121, 'الله جي نعمتن جو شُڪر ڪندڙ ھو، (الله) کيس سڳورو ڪيو ۽ سڌي واٽ ڏانھن ھدايت ڪيائينس.'),
(2023, 16, 122, '۽ دنيا ۾ کيس چڱائي ڏني سون، ۽ بيشڪ اُھو آخرت ۾ ڀلارن مان آھي.'),
(2024, 16, 123, '(اي پيغمبر اُن کان) پوءِ توڏانھن وحي ڪيوسون ته ھڪ مُھاڙي دين ابراھيم جي تابعداري ڪر، ۽ ابراھيم مشرڪن مان نه ھو.'),
(2025, 16, 124, 'جن ڇنڇر بابت تڪرار ڪيو تن کانسواءِ ٻـين تي اُن (جي تعظيم) جو حُڪم لازم نه ڪيو ويو، ۽ بيشڪ تنھنجو پالڻھار قيامت جي ڏينھن سندن وچ ۾ ضرور نبيرو ڪندو جنھن ۾ تڪرار ڪندا آھن.'),
(2026, 16, 125, '(اي پيغمبر) حڪمت ۽ چڱي نصيحت سان پنھنجي پالڻھار جي واٽ ڏانھن (ماڻھن کي) سڏ ۽ چڱي طرح کين الزام ڏي، تحقيق جيڪو الله جي واٽ کان گمراھ ٿيو تنھنکي تنھنجو پالڻھار چڱو ڄاڻندڙ آھي ۽ اُھو ھدايت وارن کي (به) چڱو ڄاڻندڙ آھي.'),
(2027, 16, 126, '۽ جيڪڏھن (ڪافرن کان) بدلو وٺو ته جيترو ايذايا ويا ھجو اوترو انھن کي ايذايو، ۽ جيڪڏھن اوھين صبر ڪريو ته اُھو صابرن لاءِ ڀلو آھي.'),
(2028, 16, 127, '۽ (اي پيغمبر) صبر ڪر ۽ تنھنجو صبر الله ئي جي توفيق سان آھي ۽ جيڪو فريب ڪندا آھن تنھن بابت نڪي مٿن ڏک ڪر ۽ نڪي خفي رہ.'),
(2029, 16, 128, 'جيڪي پرھيزگاري ڪندڙ آھن ۽ جيڪي ڀلارا آھن تن سان الله گڏ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'اھو (الله) پاڪ آھي جنھن پنھنجي ٻانھي (ﷴ ﷺ) کي ھڪڙي رات (جي اندر) تعظيم واري مسجد مان اُنھي مَسجد اَقصىٰ تائين جنھن جي چوڌاري برڪت رکي سون ھن لاءِ سير ڪرايو ته کيس پنھنجيون نشانيون ڏيکاريون، بيشڪ اُھو ئي ٻڌندڙ ڏسندڙ آھي.'),
(2031, 17, 2, '۽ مُوسىٰؑ کي ڪتاب (توريت) ڏنوسون ۽ ان کي بني اسرائيلن لاءِ ھدايت ڪرڻ وارو ڪيوسون (۽ چيوسون) ته مون کان سواءِ (ٻيو) ڪو ڀرجھلو نه وٺو.'),
(2032, 17, 3, 'اي (ان قوم جو) اولاد جن کي نوح سان گڏ (ٻيڙي ۾) چاڙھيوسون، بيشڪ اھو شاڪر ٻانھو ھو.'),
(2033, 17, 4, '۽ بني اسرائيلن ڏانھن ڪتاب ۾ (ھيءُ حُڪم) موڪليوسون ته اوھين مُلڪ ۾ ٻه ڀيرا فساد ضرور ڪندؤ ۽ اوھين وڏي وڏائي سان ضرور ھٺيلائي ڪندؤ.'),
(2034, 17, 5, 'پوءِ جڏھن ٻنھي مان پھريون انجام (فساد جو) ايندو (تڏھن) اوھان تي پنھنجا ڏاڍا سورھ ٻانھان کڙا ڪنداسون جي شھرن جي وچ ۾ ڪاھي پوندا، ۽ اھو انجام (پورو) ٿيڻو آھي.'),
(2035, 17, 6, 'وري اوھان کي ھڪ ڀيرو مٿن غلبو ڏينداسون ۽ مال ۽ اولاد سان اوھان کي (لڳولڳ) وڌائينداسون ۽ اوھان کي لشڪر ڪري گھڻا ڪنداسون.'),
(2036, 17, 7, 'جيڪڏھن اوھين چڱائي ڪندؤ ته (اُھا چڱائي) پاڻ لاءِ ڪندؤ، ۽ جيڪڏھن اوھين بڇڙائي ڪندؤ ته پاڻ لاءِ، پوءِ جڏھن ٻيو انجام (فساد جو) پھچندو ته ٻيا اوھان جا منھن ارھا ڪندا ۽ اھڙي طرح مسجد ۾ گھڙندا جھڙي طرح ان ۾ پھريون ڀيرو گھڙيا ھوا ۽ جنھن تي غالب ٿيندا تنھن کي چڱي طرح اجاڙيندا.'),
(2037, 17, 8, '(چيو ويو ته) اوھان جو پالڻھار ويجھو ئي اوھان تي ٻاجھ ڪندو، ۽ جيڪڏھن اوھين (وري) ڦرندؤ ته اسين به (سزا ڏيڻ ڏانھن) ڦرنداسون، ۽ ڪافرن لاءِ دوزخ کي بندي خانه مقرر ڪيوسون.'),
(2038, 17, 9, 'ھيءُ قرآن انھي (واٽ) ڏانھن رستو ڏيکاريندو آھي جيڪا ڏاڍي سِڌي آھي ۽ انھن مؤمنن کي خوشخبري ڏيندو آھي جيڪي چڱا ڪم ڪندا آھن انھن لاءِ وڏو اجر آھي.'),
(2039, 17, 10, '۽ جيڪي آخرت کي نه مڃيندا آھن تن لاءِ ڏکوئيندڙ عذاب تيار ڪيو اٿون.'),
(2040, 17, 11, '۽ ماڻھو مدائيءَ کي (اھڙو) گھرندو آھي جھڙو چڱائيءَ کي سندس گھرڻ ھوندو آھي، ۽ ماڻھو اٻھرو آھي.'),
(2041, 17, 12, '۽ رات ۽ ڏينھن کي ٻه نشانيون ڪيون سون پوءِ رات جي نشاني کي ميٽايوسون ۽ ڏينھن جي نشاني کي ھن لاءِ روشن ڪيوسون ته پنھنجي پالڻھار جو فضل ڳوليو ۽ (پڻ ھن لاءِ) ته اوھين ورھن جو ڳاڻيٽو ۽ حساب ڄاڻو، ۽ سڀ ڪنھن شيء جو کولي بيان ڪيوسون.'),
(2042, 17, 13, '۽ سڀ ڪنھن ماڻھوءَ جي ڳچيءَ ۾ سندس عمل نامو ٻڌوسون، ۽ قيامت جي ڏينھن ڪتاب انھيءَ لاءِ ڪڍنداسون جو ان کي کليل ڏسندؤ.'),
(2043, 17, 14, '(چيو ويندو ته) پنھنجو ڪتاب پڙھ، اڄ تنھنجو جيءُ توتي حساب وٺندڙ بس آھي.'),
(2044, 17, 15, 'جنھن ھدايت لڌي تنھن پاڻ لاءِ ئي ھدايت لڌي، ۽ جيڪو ڀُلو سو رڳو پاڻ لاءِ ڀُلندو، ۽ ڪو بار کڻندڙ ٻئي ڪنھن جو بار نه کڻندو، ۽ اسين ايستائين عذاب ڪندڙ نه آھيون جيستائين ڪنھن پيغمبر کي موڪليون.'),
(2045, 17, 16, '۽ اسين جڏھن ڪنھن ڳوٺ جو ناس ڪرڻ گھرندا آھيون (تڏھن) انھن مان آسودن کي (پيغمبر جي معرفت پنھنجي عبادت جو) حُڪم ڪندا آھيون پوءِ منجھس نافرماني ڪندا آھن تنھنڪري مٿن عذاب لازم ٿيندو آھي پوءِ پاڙ پٽڻ طرح ان جي پاڙ پٽيندا آھيون.'),
(2046, 17, 17, '۽ ڪيترائي جڳ نُوحؑ کان پوءِ ناس ڪياسون، ۽ تنھنجو پالڻھار پنھنجن ٻانھن جي گناھن جي خبر رکندڙ ڏسندڙ ڪافي آھي.'),
(2047, 17, 18, 'جيڪو دنيا (جي آسودگي) گھرندو آھي تنھن کي ان ھنڌ اھائي جلد ڏيندا آھيون جيڪي گھرندا آھيون سو جنھن لاءِ گھرندا آھيون (تنھن کي ڏيندا آھيون) وري ان لاءِ دوزخ مقرر ڪيوسون، جنھن ۾ (ھو) خوار (۽) تڙيل ٿي گھڙندو.'),
(2048, 17, 19, '۽ جنھن آخرت گھري ۽ ان لاءِ چڱي ڪوشش ڪئي ۽ اُھو مؤمن (به) ھجي سي اُھي آھن جن جي ڪوشش قبول ٿيل آھي.'),
(2049, 17, 20, 'ھن ٽوليءَ جي ۽ ھن ٽوليءَ جي ھر ھڪ کي تنھنجي پالڻھار جي بخشش مان مدد ڪريون ٿا، ۽ تنھنجي پالڻھار جي ڏات ڪا اٽڪيل نه آھي.'),
(2050, 17, 21, 'ڏس ته انھن مان ھڪڙن کي ٻـين کان (دنيا ۾ رزق ۽ مرتبه ۾) ڪئن ڀلو ڪيو اٿون، ۽ آخرت ته درجن ڪري بلڪل وڏي ۽ مانَ ڏيڻ ڪري بلڪل وڌيڪ آھي.'),
(2051, 17, 22, '(اي ماڻھو تون) الله سان ٻيو ڪو معبود مقرّر نه ڪر نه ته گِلارو (۽) خوار ٿي وھندين.'),
(2052, 17, 23, '۽ تنھنجي پالڻھار حُڪم ڪيو آھي ته ان کانسواءِ ٻئي ڪنھن جي عبادت نه ڪريو ۽ ماءُ پيءُ سان نيڪي ڪريو، جيڪڏھن ٻنھين مان ڪو ھڪڙو تنھنجي آڏو ٻُڍائيءَ کي پھچي يا ٻَئي ته تون اُف (به) نه چئج ۽ نڪي کين جھڻڪج ۽ ساڻن چڱي ڳالھ (ادب واري) ڳالھائج.'),
(2053, 17, 24, '۽ انھن لاءِ عاجزيءَ جون ٻانھون ٻاجھ مان ھيٺاھيون ڪج ۽ چئج ته اي منھنجا پالڻھار اُنھن تي (اھڙي طرح) ٻاجھ ڪر جھڙي طرح مون کي ننڍپڻ ۾ پاليائون ٿي.'),
(2054, 17, 25, 'جيڪي اوھان جي دلين ۾ آھي سو اوھان جو پالڻھار چڱي طرح ڄاڻندڙ آھي جيڪڏھن اوھين سڌريل ھوندؤ (ته بخشيندو) ڇوته الله توبه ڪندڙن کي بخشڻھار آھي.'),
(2055, 17, 26, '۽ مائٽي وارا ۽ مسڪينن کي ۽ مُسافرن کي سندن حق ڏي ۽ اڍنگائيءَ سان اجايو نه وڃاءِ.'),
(2056, 17, 27, 'ڇوته اجايو وڃائيندڙ شيطانن جا ڀائر آھن، ۽ شيطان پنھنجي پالڻھار جي نافرماني ڪندڙ آھي.'),
(2057, 17, 28, '۽ جيڪڏھن تون (رزق جي اڻھوند ڪري) انھن کان منھن موڙين جنھن رزق جي ملڻ جي پنھنجي پالڻھار کان اميد رکين ٿو ته (کين جھڻڪي نه موٽاءِ ۽) ساڻن مٺي ڳالھ ڳالھاءِ.'),
(2058, 17, 29, '۽ نڪي پنھنجي ھٿ کي پنھنجي ڳچيءَ ۾ بند ڪر ۽ نڪي اھو سارو کولي ڇڏ نه ته ملامتي ۽ عاجز ٿي ويھي رھندين.'),
(2059, 17, 30, 'تنھنجو پالڻھار جنھن لاءِ گھرندو آھي تنھنجي روزي ڪشادي ڪندو آھي ۽ گھٽ به ڪندو آھي، تحقيق اھو پنھنجن ٻانھن جي خبر رکندڙ ڏسندڙ آھي.'),
(2060, 17, 31, '۽ پنھنجن ٻارن کي سُڃائيءَ جي ڊپ کان نه ڪُھو، اسين کين ۽ (خود) اوھان کي روزي ڏيندا آھيون، بيشڪ انھن کي ڪُھڻ وڏو ڏوھ آھي.'),
(2061, 17, 32, '۽ زنا کي ويجھا نه وڃو ڇوته اھا بي حيائي آھي، ۽ اِھا واٽ بُڇڙي آھي.'),
(2062, 17, 33, '۽ اھڙي جيءَ کي نه ڪُھو جنھن جو (شرعي) حق کانسواءِ ڪُھڻ الله حرام ڪيو آھي، ۽ جيڪو ناحق ماريو وڃي ان جي وارث کي (قصاص جو) اختيار ڏنوسون پوءِ جڳائي ته مارڻ ۾ زيادتي نه ڪري، ڇوته اھو مدد ڏنل ٿيو آھي.'),
(2063, 17, 34, '۽ نڪي ڪنھن ڇوري ٻار جي مال کي چڱي نيت کانسواءِ ويجھا وڃو جيستائين ھو بلوغت کي پھچي، ۽ پنھنجو انجام پاڙيو، تحقيق انجام بابت بلڪل پڇا ٿيندي.'),
(2064, 17, 35, '۽ جڏھن ماڻ ڀريو تڏھن پورو ڀريو ۽ سنئين ترازيءَ سان توريو، اھا (پوري ماپ ۽ تور) ڀلي ۽ عاقبت جي ڪري بلڪل چڱي آھي.'),
(2065, 17, 36, '۽ (اي ٻانھا) جنھن شيء جي توکي ڪا سُڌ نه آھي اُن جي پويان نه لڳ، ڇوته ڪَن ۽ اک ۽ دل انھن سڀني (عضون) کان ان بابت بلڪل پڇا ٿيندي.'),
(2066, 17, 37, '۽ زمين ۾ تون آڪڙ ڪري نه گُھم، ڇوته تون نڪي زمين کي چيري سگھندين ۽ نڪي ڊيگھ ۾ جبل (جي چوٽي) کي پھچين سگھندين.'),
(2067, 17, 38, 'اُنھن سڀني (عادتن) جي بڇڙائي تنھنجي پالڻھار وٽ ناپسند آھي.'),
(2068, 17, 39, 'اھي حُڪم انھن مان آھن جيڪي تنھنجي پالڻھار توڏانھن حڪمت مان وحي ڪيا آھن، ۽ الله سان ٻيو ڪوبه معبُود مُقرّر نه ڪر نه ته تون ملامتي (۽) تڙيل ٿي دوزخ ۾ وڌو ويندين.'),
(2069, 17, 40, '(اي مشرڪؤ) اوھان جي پالڻھار اوھان کي پُٽن جي لاءِ چونڊيو ۽ (پاڻ) ملائڪن کي (پنھنجيون) ڌيئر ڪري ورتائين ڇا؟ بيشڪ اوھين ھيءَ وڏي ڳالھ ڪندا آھيو.'),
(2070, 17, 41, '۽ بيشڪ ھن قرآن ۾ ھن لاءِ وري وري سمجھايوسون ته نصيحت وٺن، ۽ اھو انھن بابت نفرت کانسواءِ (ٻيو ڪي) ڪين ٿو وڌائي.'),
(2071, 17, 42, '(اي پيغمبر) چؤ ته جيڪڏھن ساڻس ٻيا معبود جھڙي طرح ھوندا آھن (تھڙي طرح) ھجن ھا ته اھي انھي دم عرش جي مالڪ ڏانھن (جنگ جي) واٽ طلبين ھا.'),
(2072, 17, 43, 'اھو پاڪ آھي ۽ جيڪي چوندا آھن تنھن کان وڏو (۽) مٿاھون آھي.'),
(2073, 17, 44, 'ست آسمان ۽ زمين ۽ جيڪي منجھن آھي سي ان جي پاڪائي بيان ڪندا آھن، ۽ ڪا (به) شيء سندس ساراھ سان گڏ پاڪائي بيان ڪرڻ کانسواءِ نه آھي پر (اوھين) سندس تسبيح نه سمجھندا آھيو، بيشڪ اُھو بُردبار بخشڻھار آھي.'),
(2074, 17, 45, '۽ جڏھن (تون) قرآن پڙھندو آھين (تڏھن) تنھنجي وچ ۾ ۽ جيڪي آخرت کي نه مڃيندا آھن تن جي وچ ۾ (اسين) لڪل پردو ڪندا آھيون.'),
(2075, 17, 46, '۽ ان جي سمجھڻ کان سندن دلين ۾ پردو ۽ سندن ڪَنن ۾ گھٻرائي وجھندا آھيون، ۽ جڏھن پنھنجي ھڪڙي پالڻھار کي قرآن ۾ ياد ڪرين ٿو تڏھن نفرت ڪري پنھنجين پُٺين (ڀرپوءِ) تي موٽندا آھن.'),
(2076, 17, 47, '(اي پيغمبر) جنھن مھل توڏانھن ڪن ڏيئي جنھن (ٺـٺولي ڪرڻ جي ارادي) سان (قرآن کي) ٻڌندا آھن ۽ جنھن مھل ڳجھيون صلاحون ڪندا آھن جڏھن ظالم چوندا آھن ته (اوھين) رڳو جادو ڪيل مڙس جي تابعداري ڪريو ٿا (تنھن مھل سندين انھن ڳالھين کي) چڱي طرح ڄاڻندا آھيون.'),
(2077, 17, 48, 'ڏس ته تولاءِ ڪھڙا مٿال ڏيندا آھن پوءِ گمراھ ٿيا تنھنڪري سڌي واٽ لھي نه سگھندا آھن.'),
(2078, 17, 49, '۽ چوندا آھن ته جڏھن اسين (مري ڳري) ھڏا ۽ ذرا ذرا ٿينداسين (تڏھن) اسين وري نوان بڻجي اٿنداسين ڇا؟'),
(2079, 17, 50, 'چؤ ته پھڻ ٿيو يا لوھ ٿيو.'),
(2080, 17, 51, 'يا پنھنجين دلين ۾ جيڪا (ٻي) وڏي خلق ڀائيندا آھيو (سا ٿيو ته به اٿندؤ)، پوءِ سگھوئي چوندا ته ڪير اسان کي وري جياريندو؟ چؤ ته جنھن اوھان کي پھريون ڀيرو پيدا ڪيو (سو جياريندو)، پوءِ توڏانھن پنھنجو ڪنڌ لوڏيندا ۽ چوندا ته اھو ڪڏھن ٿيندو؟ چؤ ته اميد آھي ته سگھو ٿيڻو آھي.'),
(2081, 17, 52, 'جنھن ڏينھن الله اوھان کي سڏيندو (تنھن ڏينھن) اوھين سندس ساراہ ڪندي ورندي ڏيندؤ ۽ ڀائيندؤ ته ٿوروئي وقت رھيا آھيون.'),
(2082, 17, 53, '۽ (اي پيغمبر) منھنجن ٻانھن کي چؤ ته (ڪافرن سان) بلڪل چڱي (ڳالھ) چوندا رھن، ڇوته شيطان سندن وچ ۾ جھيڙو وجھندو آھي، ڇوته شيطان ماڻھو جو پڌرو ويري آھي.'),
(2083, 17, 54, 'اوھان جو پالڻھار اوھان کي چڱي طرح ڄاڻندو آھي، جيڪڏھن وڻيس ته اوھان تي ٻاجھ ڪري ۽ جيڪڏھن وڻيس ته اوھان کي عذاب ڪري، ۽ اسان توکي مٿن وڪيل ڪري نه موڪليو آھي.'),
(2084, 17, 55, '۽ جيڪي آسمانن ۽ زمين ۾ آھي سو تنھنجو پالڻھار چڱي طرح ڄاڻندو آھي، ۽ بيشڪ ھڪڙن نبين کي ٻـين کان وڌيڪ ڪيوسون ۽ داؤد کي زبور ڏنوسون.'),
(2085, 17, 56, '(اي پيغمبر) چؤ ته الله کانسواءِ ٻـين جن کي (الله جھڙو) ڀائيندا آھيو تن کي سڏيو پوءِ (اھي) اوھان کان نڪي ڪنھن اھنج لاھڻ جو ۽ نڪي ڦيرائڻ جو اختيار رکندا آھن.'),
(2086, 17, 57, 'ھي (ڪافر خُدا کانسواءِ) جن کي سڏيندا آھن سي پاڻ پنھنجي پالڻھار ڏانھن وسيلو ڳوليندا آھن ته انھن مان ڪير ڏاڍو ويجھو آھي؟ ۽ سندس ٻاجھ جي اميد رکندا آھن ۽ سندس عذاب کان ڊڄندا آھن، ڇوته تنھنجي پالڻھار جو عذاب ڊڄڻ جھڙي شيء آھي.'),
(2087, 17, 58, '۽ ڪوبه ڳوٺ نه آھي مگر (جنھن جي رھڻ وارن) کي (سندن بڇڙن ڪرتوتن سبب) قيامت جي ڏينھن کان اڳ اسين ناس ڪرڻ وارا آھيون يا ان کي سخت عذاب ڏيندڙ آھيون، اِھو حُڪم ڪتاب ۾ لکيل آھي.'),
(2088, 17, 59, '۽ اسان کي مُعجزن موڪلڻ کان انھيءَ ڳالھ کانسواءِ ٻئي ڪنھن نه روڪيو جو اڳين (ماڻھن) انھن کي ڪوڙ ڄاتو ھو، ۽ ثمود (جي قوم) کي ڏاچي مُعجزہ لاءَ ڏني سون ته اُن کي نه مڃيائون، ۽ اسين رڳو ڊيڄارڻ لاءِ مُعجزا موڪليندا آھيون.'),
(2089, 17, 60, '۽ (اي پيغمبر ياد ڪر) جنھن مھل توکي چيوسون ته تنھنجي پالڻھار ماڻھن کي (پنھنجي قدرت سان) لپيٽيو آھي، ۽ اسان جيڪو توکي نظارو ڏيکاريوسون جو لعنت ڪيل وڻ قرآن ۾ بيان ڪيو ويو آھي سو ماڻھن جي پرک کانسواءِ نه آھي، ۽ اسين جو کين ڊيڄاريندا آھيون سو انھن بابت وڏي شرارت کانسواءِ (ٻيو ڪي) نه وڌائيندو آھي.'),
(2090, 17, 61, '۽ جڏھن ملائڪن کي چيوسون ته آدم کي سجدو ڪريو پوءِ ابليس کانسواءِ (ٻـين) سجدو ڪيو، (ابليس) چيو ته جنھن کي مٽيءَ مان پيدا ڪيئي تنھن کي ڇو سجدو ڪريان؟'),
(2091, 17, 62, '(وري ابليس) چيو ته ڏس ھي شخص جنھن کي تو مون تي شرف ڏنو، جيڪڏھن مون کي قيامت جي ڏينھن تائين مُھلت ڏيندين ته ٿورڙن کان سواءِ سندس (ٻئي) اولاد کي پاڙئون پٽيندس.'),
(2092, 17, 63, '(الله) چيو ته وڃ پوءِ منجھانئن جن تنھنجي تابعداري ڪئي تن اوھان سڀني جي سزا دوزخ آھي (۽ اھا) بلڪل پوري سزا آھي.'),
(2093, 17, 64, '۽ منجھانئن جنھن کي (ڀُلائي) سگھين تنھن کي پنھنجي سڏ سان ڀُلاءِ ۽ پنھنجن پيادن ۽ سوارن سان مٿن چڙھائي ڪر ۽ سندن مال ۽ اولاد ۾ ڀائيواري ڪر ۽ کين انجام ڏي، ۽ شيطان ٺڳيءَ کانسواءِ کين ڪو دلاسو نه ڏيندو آھي.'),
(2094, 17, 65, 'بيشڪ منھنجن ٻانھن تي توکي ڪا سگھ نه آھي، ۽ تنھنجو پالڻھار نگھبان بس آھي.'),
(2095, 17, 66, 'اوھان جو پالڻھار اھو آھي جيڪو دريائن ۾ ٻيڙيون ھن لاءِ ھلائيندو آھي ته سندس فضل ڳوليو، ڇوته اُھو اوھان تي ٻاجھارو آھي.'),
(2096, 17, 67, '۽ جڏھن اوھان کي درياءَ ۾ ڪا اوکائي پھچندي آھي تڏھن خاص ان (الله) کانسواءِ ٻـين جن کي اوھين سڏيندا آھيو سي وسري ويندا آھن، پوءِ جنھن مھل اوھان کي سُڪي ڏي بچائيندو آھي (تنھن مھل) مُنھن موڙيندا آھيو، ۽ ماڻھو ڏاڍو بي شڪر آھي.'),
(2097, 17, 68, '(اوھين) پاڻ کي سڪيءَ ۾ گھائڻ کان يا پاڻ تي (پھڻن جي) آنڌي اچڻ کان بي ڀوا ٿيا آھيو ڇا؟ وري اوھين پاڻ لاءِ ڪو واھرو نه لھندؤ.'),
(2098, 17, 69, 'يا ٻئي ڀيري ان (درياء) ۾ موٽائڻ کان بي ڀَوَا ٿيا آھيو ڇا؟ پوءِ اوھان تي واءُ جو طوفان موڪلي پوءِ اوھان کي اوھان جي انڪار سببان ٻوڙي وري انھي (ٻوڙڻ جي) ڪري پاڻ لاءِ اسان تي ڪو پيڇي ڪرڻ وارو نه لھندؤ.'),
(2099, 17, 70, '۽ بيشڪ آدم جي اولاد کي شرف ڏنوسون ۽ کين پَٽن ۽ دريائن ۾ کنيوسون ۽ کين سٺين شين مان روزي ڏنيسون ۽ جيڪي خلقياسون تن مان گھڻن کان کين زيادہ مانَ وارو ڪيوسون.'),
(2100, 17, 71, 'ھر ڪنھن ٽوليءَ کي سندن اڳواڻ سان سڏينداسون، پوءِ جنھن کي سندس اعمالنامون سندس ساڄي ھٿ ۾ ڏنو ويندو سي پنھنجو اعمالنامو پڙھندا ۽ تند جيترو به ظلم نه ڪبن.'),
(2101, 17, 72, '۽ جيڪو ھن دنيا ۾ انڌو ٿيو سو آخرت ۾ (به) انڌو (ھوندو) ۽ واٽ کان بِنھ ڀُليل رھندو.'),
(2102, 17, 73, '۽ جيڪي توڏانھن (پنھنجن حُڪمن جو) وحيُ ڪيوسون تنھن کانسواءِ توکي ھن لاءِ دوکي ۾ وجھڻ لڳا ھوا ته تون ان کانسواءِ ٻيو ڪو اسان تي ٺاھ ٺاھين، ۽ انھيءَ مھل توکي دوست بڻائين.'),
(2103, 17, 74, '۽ جيڪڏھن اسين توکي (پنھنجي ڳالھ تي) پختو نه رکون ھا ته ويجھو ھو جو بيشڪ اُنھن ڏانھن ڪي ٿوروئي لڙي پون ھا.'),
(2104, 17, 75, '۽ اتي جو اتي حياتي جو ٻيڻو عذاب ۽ موت جو (به) ٻيڻو عذاب توکي چکايون ھا وري پاڻ لاءِ اسان تي ڪو واھرو نه لھين ھا.'),
(2105, 17, 76, '۽ (اي پيغمبر مڪي جي) زمين ۾ سگھو ئي توکي ھن لاءِ بيقرار ڪندا ته ان مان توکي لڏائين ۽ انھيءَ مھل توکان پوءِ (پاڻ به) ٿورو وقت رھندا.'),
(2106, 17, 77, 'توکان اڳ جيڪي پنھنجا پيغمبر موڪلياسون تن جو دستور موافق (جو سندن قوم کين لڏايو ته ناس ڪيو وين) ۽ اسان جي دستور کي ڪا ڦيرڦار نه لھندين.'),
(2107, 17, 78, 'سج لڙئي کان وٺي رات جي اونداھيءَ تائين نماز پڙھ ۽ فجر جو قرآن پرھ، بيشڪ فجر جو قرآن پڙھڻ مھل ملائڪ حاضر ٿين ٿا.'),
(2108, 17, 79, '۽ ڪجھ رات جو (قرآن) سان تھجّد پرھ جو (اھا) تولاءِ زيادہ عبادت آھي، تنھنجو پالڻھار توکي سگھوئي ساراھيل جاءِ ۾ اُٿاريندو.'),
(2109, 17, 80, '۽ (اي پيغمبر) چؤ ته اي منھنجا پالڻھار مون کي (مديني ۾) سچائيءَ سان داخل ڪر ۽ (مڪي مان) مون کي سچائيءَ سان ٻاھر ڪڍ ۽ پاڻ وٽان مون لاءِ ڏاڍي سگھ مدد ڏيندڙ ڪر.'),
(2110, 17, 81, '۽ (اي پيغمبر) چؤ ته سچ آيو ۽ ڪوڙ ڀڳو، بيشڪ ڪوڙ ڀڄندڙ آھي.'),
(2111, 17, 82, '۽ اسين قرآن کي نازل ڪريون ٿا جو اھو سارو مؤمنن لاءِ شفا ۽ ٻاجھ آھي ۽ ظالمن بابت نقصان کانسواءِ (ٻيو ڪي) نه وڌائيندو آھي.'),
(2112, 17, 83, '۽ جڏھن انسان تي ڪا نعمت نازل ڪندا آھيون (تڏھن) مُنھن موڙيندو آھي ۽ کانئس پاسيرو ٿيندو آھي، ۽ جڏھن کيس مدائي پھچندي آھي ته نا اميد ٿيندو آھي.'),
(2113, 17, 84, '(اي پيغمبر) چؤ ته سڀڪو پنھنجي ڊول تي ڪم ڪندو آھي، پوءِ جيڪو ڏاڍيءَ سڌيءَ واٽ تي آھي تنھن کي تنھنجو پالڻھار چڱي طرح ڄاڻندو آھي.'),
(2114, 17, 85, '۽ (اي پيغمبر) توکان رُوح بابت پُڇن ٿا، چؤ ته رُوح منھنجي پالڻھار جي امر مان آھي ۽ اوھان کي ڪي ٿورڙي علم کانسواءِ نه ڏنو ويو آھي.'),
(2115, 17, 86, '۽ جيڪو (قرآن) توڌانھن وحي ڪيوسون سو جيڪڏھن گھرون ته ورائي وٺون ته پوءِ تون پاڻ لاءِ اسان تي ڪو ڀرجھلو نه لھندين.'),
(2116, 17, 87, 'پر تنھنجي پالڻھار جي ٻاجھ (ان ورائي وٺڻ کي جھلي رکيو) آھي، ڇوته سندس فضل توتي وڏو آھي.'),
(2117, 17, 88, '(اي پيغمبر) چؤ ته جيڪڏھن ماڻھو ۽ جِنّ ھن قرآن جھڙي (ٻئي ڪنھن ڪتاب) آڻڻ لاءِ گڏ ٿين ته جھڙس ڪڏھن نه آڻي سگھندا جيتوڻيڪ ھڪ ٻئي کي مدد ڏين.'),
(2118, 17, 89, '۽ بيشڪ ھن قرآن ۾ سڀڪو مثال ماڻھن لاءِ بيان ڪيو اٿئون، پر گھڻا ماڻھو بي شڪر ٿي انڪار ڪندا آھن.'),
(2119, 17, 90, '۽ چوندا آھن ته اسين توکي (ايستائين) ھرگز نه مڃينداسون جيستائين زمين ۽ اسان لاءِ ڪو چشمو وھائين.'),
(2120, 17, 91, 'يا کجين ۽ ڊاکن مان تولاءِ باغ ٿئي پوءِ سندس وچ ۾ تون وھندڙ واھيون وھائين.'),
(2121, 17, 92, 'يا جھڙي طرح تو ڀانيو تھڙي طرح اسان تي آسمان ٽُڪر ٽُڪر ڪيرائين يا تون الله ۽ ملائڪن کي سامھون آڻين.'),
(2122, 17, 93, 'يا توکي ڪو سونون گھر ھُجي يا آسمان تي چڙھين، ۽ تنھنجي چڙھڻ کي (ايستائين) ڪڏھن نه مڃينداسون جيستائين اسان تي (اھڙو لکيل) ڪتاب لاھين جنھن کي پڙھون، چؤ ته منھنجو پالڻھار پاڪ آھي آءٌ رڳو پيغام پھچائڻ وارو ماڻھو آھيان.'),
(2123, 17, 94, '۽ جڏھن ماڻھن وٽ ھدايت آئي (تڏھن) کين ايمان آڻڻ کان ھن ڏاران ڪنھن نه جھليو آھي جو چوندا آھن ته الله پيغمبرن کي ڇو ماڻھو ڪري موڪليو.'),
(2124, 17, 95, '(اي پيغمبر) چؤ ته جيڪڏھن ملائڪ (اوھان جان) زمين ۾ ٽڪندڙ ٿي گھمن ڦرن ھا ته اسين مٿن ضرور آسمان مان ملائڪ پيغمبر ڪري موڪليون ھا.'),
(2125, 17, 96, '(اي پيغمبر) چ‍ؤ ته الله اوھان ۽ اسان جي وچ ۾ شاھد بس آھي، بيشڪ اھو پنھنجي ٻانھن جي خبر رکندڙ ڏسندڙ آھي.'),
(2126, 17, 97, '۽ جنھن کي الله ھدايت ڪئي سو ھدايت وارو آھي، ۽ جيڪي گمراھ ٿيا تن لاءِ تون الله کانسواءِ ڪوئي دوست نه لھندين، ۽ کين قيامت جي ڏينھن سندن مُنھن ڀر انڌا ۽ گنگا ۽ ٻوڙا ڪري گڏ ڪنداسون، سندن جاءِ دوزخ آھي، جنھن مھل ڍرو ٿيندو تنھن مھل انھن لاءِ دوزخ کي وڌيڪ تپائينداسون.'),
(2127, 17, 98, 'اھا سندن سزا ھن سببان آھي جو اسانجي نشانين جو انڪار ڪيائون ۽ چيائون ته جڏھن اسين ھڏا ۽ چُورو ٿينداسون (تڏھن) وري نئين سر پيدا ٿي اٿاريا وينداسون ڇا؟'),
(2128, 17, 99, 'نه ڏسندا آھن ڇا ته جنھن الله آسمانن ۽ زمين کي بڻايو سو سندن اھڙي بڻائڻ تي وس وارو آھي ۽ انھن لاءِ اھا مُدّت مقرر ڪيائين جنھن ۾ ڪو شڪ نه آھي، پوءِ ظالم انڪار کانسواءِ ڪي قبول نه ڪندا آھن.'),
(2129, 17, 100, '(اي پيغمبر) چؤ ته جيڪڏھن اوھين منھنجي پالڻھار جي خزانن جا مالڪ ھجو ھا ته انھيءَ مھل (سڀ) خرچ ٿي وڃڻ جي ڀَو کان اوھين ضرور بند رکو ھا، ۽ ماڻھو پڪو پيچ آھي.'),
(2130, 17, 101, '۽ بيشڪ مُوسىؑ کي نَوَ پڌريون نشانيون ڏنيوسون پوءِ بني اسرائيلن کان پُڇ ته جڏھن (اھو) وٽن آيو تڏھن کيس فرعون چيو ته اي مُوسىؑ بيشڪ آءٌ توکي يقيناً جادو ٿيل ڀائيندو آھيان.'),
(2131, 17, 102, '(مُوسىؑ) چيو ته بيشڪ تو ڄاتو آھي ته اھي (نشانيون) آسمانن ۽ زمين جي پالڻھار کانسواءِ (ٻئي ڪنھن) نه نازل ڪيون آھن (اوھان جي) سمجھائڻ لاءِ (ته ڏسي مون مڃيو)، ۽ اي فرعون آءٌ توکي (انھن جي انڪار سببان) ھلاڪ ٿيل ڀائيندو آھيان.'),
(2132, 17, 103, 'پوءِ فرعون ملڪ مان سندن لوڌڻ جو ارادو ڪيو پوءِ فرعون ۽ سندس ساري سنگت کي ٻوڙيوسون.'),
(2133, 17, 104, '۽ کانئس پوءِ بني اسرائيلن کي چيوسون ته مُلڪ ۾ ٽڪو پوءِ جڏھن آخرت جو وعدو آيو (تڏھن) اوھان کي لپيٽي وٺنداسون.'),
(2134, 17, 105, '۽ ان (قرآن) کي اسان سچ سان نازل ڪيو آھي ۽ اھو سچ سان نازل ٿيو آھي، ۽ توکي خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ کانسواءِ نه موڪليوسون.'),
(2135, 17, 106, '۽ ان قرآن کي جُدا جُدا (سُورتون ۽ آيتون) ھن لاءِ ڪيوسون ته اھو ماڻھن کي آھسته آھسته پڙھي ٻڌائين ۽ ان کي آھسته آھسته ڪري لاٿوسون.'),
(2136, 17, 107, '(اي پيغمبر) چؤ ته اوھين ان قرآن کي مڃو توڙي نه مڃو، بيشڪ جن کي ھن کان اڳ علم ڏنو ويو آھي تن وٽ جڏھن (قرآن) پڙھبو آھي (تڏھن) سجدو ڪندڙ ٿي کاڏين ڀر ڪِرندا آھن.'),
(2137, 17, 108, '۽ چوندا آھن ته اسان جو پالڻھار پاڪ آھي بيشڪ اسان جي پالڻھار جو انجام ضرور (پورو) ٿيڻو آھي.'),
(2138, 17, 109, '۽ روئيندا کاڏين ڀر ڪِرندا آھن ۽ (قرآن) منجھن عاجزي وڌائيندو آھي.'),
(2139, 17, 110, '(اي پيغمبر) چؤ ته (الله کي) الله (جي نالي سان) سڏيو يا رحمان (جي نالي سان) سڏيو، جنھن نالي سان (اوھين) سڏيو انھي جا (سڀ) نالا چڱا آھن، ۽ پنھنجي نماز ۾ نڪي تمام ڏاڍيان پڙھ ۽ نڪي تمام ھوريان پڙھ ان جي وچ واري واٽ اختيار ڪر.'),
(2140, 17, 111, '۽ چؤ ته سڀ ساراھ انھي الله کي جڳائي جنھن (پاڻ لاءِ) ڪو اولاد نه ورتو آھي ۽ نڪي سندس مُلڪ ۾ ڪو ڀائيوار آھي ۽ نڪي ڪنھن ھيڻائي سببان ان جو ڪو مددگار آھي ۽ ھن جي گھڻي قدر وڏائي بيان ڪر.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'سڀ ساراھ انھي الله کي جڳائي جنھن پنھنجي ٻانھي (ﷴ ﷺ) تي ڪتاب لاٿو ۽ ان ۾ ڪو ڏِنگ نه رکيائين.'),
(2142, 18, 2, 'ھن لاءِ سڌو ڪيائين ته الله وٽان سخت مُصيبت کان ڪافرن کي ڊيڄاري ۽ اُنھن مؤمنن کي خوشخبري ڏي جيڪي چڱا ڪم ڪندا آھن ته اُنھن لاءِ چڱو اجر آھي.'),
(2143, 18, 3, 'منجھس سدائين رھڻ وارا آھن.'),
(2144, 18, 4, '۽ انھن کي ڊيڄاري جيڪي چون ٿا ته الله (پاڻ لاءِ) پُٽ ورتو آھي.'),
(2145, 18, 5, 'جنھن جو نڪي کين ۽ نڪي سندن پيءُ ڏاڏن کي ڪو علم آھي، وڏي (گناھ جي) ڳالھ آھي جا سندن واتان نڪري ٿي، اھي ڪوڙ کانسواءِ (ٻيو ڪي) نه چوندا آھن.'),
(2146, 18, 6, 'جيڪڏھن اھي ھن ڳالھ کي نه مڃن ته متان تون ارمان کان سندن پويان پاڻ کي ھلاڪ ڪرين.'),
(2147, 18, 7, 'جيڪي زمين تي آھي سو اسان ان جي سينگار لاءِ ھن ڪري ڪيو ته سندن آزمائش وٺون ته انھن مان عملن ۾ ڏاڍو چڱو ڪير آھي.'),
(2148, 18, 8, '۽ ان زمين تي جيڪا (ساول) آھي سا اسين چٽي پٽي ڪرڻ وارا آھيون.'),
(2149, 18, 9, 'پر ڀانيو اٿيئي ته غار ۽ رقيم وارا اسان جي نشانين مان عجيب آھن.'),
(2150, 18, 10, 'جڏھن غار ۾ ڪيترن جوانن جاءِ ورتي تڏھن چيائون ته اي اسان جا پالڻھار پاڻ وٽان اسان تي ٻاجھ عطا ڪر ۽ اسان جي ڪم ۾ اسان لاءِ چڱائي ڪر.'),
(2151, 18, 11, 'پوءِ ڪيترائي ورهيه کين غار ۾ ڪَنن ڀر سمھاريوسون.'),
(2152, 18, 12, 'وري کين ھن لاءِ اٿاريوسون ته ڏيکاريون ته جا مدت رھيا سا ٻنھي ٽولين مان ڪھڙي کي ياد آھي.'),
(2153, 18, 13, 'اسين توکي سندن سچي خبر بيان ڪريون ٿا، بيشڪ اھي ڪيترا جوان ھوا جو پنھنجي پالڻھار کي مڃيو ھوائون ۽ کين وڌيڪ ھدايت ڪئي ھئي سون.'),
(2154, 18, 14, '۽ سندن دلين تي (سچائي) ٻڌي سون جڏھن اٿيا تڏھن چيائون ته اسان جو پالڻھار آسمانن ۽ زمين جو پالڻھار آھي ان کانسواءِ ٻئي ڪنھن کي معبُود ڪري نه سڏينداسون (جي سڏيوسون ته) ضرور بيشڪ اُنھي مھل بيھودي ڳالھ چئي سون.'),
(2155, 18, 15, 'اسان جي ھن قوم الله کانسواءِ ٻيا معبود ورتا آھن، مٿن ڇونه ڪو پڌرو دليل آڻيندا آھن؟ پوءِ جيڪو الله تي ڪوڙ ٺاھي ان کان وڌيڪ ظالم ڪير آھي؟'),
(2156, 18, 16, '۽ جڏھن انھن (مشرڪن) کان ۽ (اھي) الله کانسواءِ جن کي پوڄيندا آھن تن کان پاسي ٿيو تڏھن غار ڏانھن پناہ وٺو ته اوھان جو پالڻھار اوھان لاءِ ٻاجھ پکيڙيندو ۽ اوھان جي ڪم ۾ اوھان لاءِ سولائي موجود ڪندو.'),
(2157, 18, 17, '۽ (اي ڏسندڙ) جڏھن سج اڀري (تڏھن) ڏسندين ته (اُس) سندن غار کان سڄي پاسي لڙندي آھي ۽ جڏھن لھي تڏھن سندن کٻي پاسي مڙندي آھي ۽ اُھي ان (غار جي) ويڪرائي ۾ آھن، اِھا الله جي نشانين مان آھي، جنھن کي الله ھدايت ڪري سو ھدايت وارو آھي، ۽ جنھن کي ڀُلائي ان لاءِ ڪوبه دوست واٽ ڏيکارندڙ نه لھندين.'),
(2158, 18, 18, '۽ تون کين جاڳندڙ ڀائيندين پر اُھي (ننڊ ۾) سمھيل آھن، ۽ کين سڄي پاسي تي ۽ کٻي پاسي تي اٿلائيندا آھيون، ۽ سندن ڪتو چائٺ تي اڳيون ٽنگون ڊگھيريو پيو آھي، جيڪڏھن مٿن بيھي ڏسين ته کانئن ڀڄندڙ ٿي منھن ڦيرين ۽ کانئن دھشت کان ڀرجي وڃين.'),
(2159, 18, 19, '۽ اھڙي طرح کين ھن لاءِ کڙو ڪيوسون ته پاڻ ۾ پڇن، منجھانئن چوندڙ چيو ته (اوھين ھتي) ڪيترو ٽڪيؤ، چيائون ته ھڪ ڏينھن يا ڏينھن کان گھٽ رھياسون، (ڪن) چيو ته جيڪي اوھين رھيا آھيو سو اوھان جو پالڻھار چڱو ڄاڻندڙ آھي، پوءِ مان پاڻ مان ھڪ کي ھِن پنھنجي رپئي سان شھر ڏانھن موڪليو پوءِ ڀلي ڏسي ته اُن (ڳوٺ) جو ڪھڙو طعام سٺو آھي پوءِ ان مان ڪجھ تعام اوھان وٽ آڻي ۽ جڳائي ته نرمي ڪري ۽ اوھان جو حال ڪنھن ھڪڙي کي نه ڄاڻائي.'),
(2160, 18, 20, 'ڇوته جيڪڏھن اھي اوھان تي غالب ٿيندا ته اوھان کي سنگسار ڪندا يا پنھنجي مذھب ۾ اوھان کي موٽائيندا ۽ اُن مھل اوھين ھرگز نه ڇٽندؤ.'),
(2161, 18, 21, '۽ اھڙي طرح (تڏھن) ماڻھن کي ھن لاءِ مٿن خبردار ڪيوسون ته ڄاڻن ته الله جو انجام سچو آھي ۽ بيشڪ قيامت (ٿيڻي) آھي ان ۾ ڪو شڪ نه آھي، تڏھن انھن جي ڳالھ بابت پاڻ ۾ جھڳڙو ڪرڻ لڳا پوءِ چيائون ته انھن جي (غار) تي ڪا اڏاوت اڏيو، سندن پالڻھار کين ڄاڻندڙ آھي، جيڪي پنھنجي ڳالھ ۾ غالب ٿيا تن چيو ته انھن (جي غار) تي مسجد ضرور بڻائينداسون.'),
(2162, 18, 22, '(ڪي) چوندا ته (اُھي) ٽي آھن چوٿون سندن ڪتو آھي، ۽ (ڪي) چوندا ته پنج آھن ڇھون سندن ڪتو آھي ڳُجھ بابت اٽڪل ھڻندا آھن، ۽ چوندا آھن ته ست آھن اٺون سندن ڪتو آھي، (اي پيغمبر) چؤ ته سندن ڳاڻيٽو منھنجو پالڻھار وڌيڪ ڄاڻندڙ آھي انھن کي رڳو ڪي ٿورا ڄاڻندا آھن، پوءِ انھن بابت سرسري جھڳڙي ڪرڻ کانسواءِ جھڳڙو نه ڪر، ۽ نڪي انھن بابت ڪافرن مان ڪنھن ھڪ کان پڇا ڪر.'),
(2163, 18, 23, '۽ ڪنھن ڪم لاءِ انشاءالله کانسواءِ نه چؤ ته آءٌ ھي سڀاڻي ڪندس.'),
(2164, 18, 24, '۽ جڏھن پنھنجي پالڻھار کي وسارين (تڏھن تون اُن کي) ياد ڪر ۽ چؤ ته سگھوئي منھنجو پالڻھار ھن کان به ڏاڍي ويجھي سڌي ھدايت ڪندو.'),
(2165, 18, 25, '۽ اُھي پنھنجي غار ۾ ٽن سون ورھن کان وڌيڪ نَو ورھ رھيا.'),
(2166, 18, 26, '(جيڪڏھن اُھي نه مڃين ته) چئو ته جيڪي رھيا سو الله وڌيڪ ڄاڻندڙ آھي، اُن کي آسمانن ۽ زمين جو ڳجھ (معلوم) آھي، ڪھڙو عجيب ڏسندڙ ۽ ڪھڙو عجيب ٻڌندڙ آھي، اُن کانسواءِ ڪو انھن جو مددگار نه آھي، ۽ پنھنجي حُڪم ۾ ڪنھن ھڪ کي ڀائيوار نه ڪندو آھي.'),
(2167, 18, 27, '۽ تنھنجي پالڻھار جي ڪتاب مان جيڪي توڏانھن وحي ڪيو ويو سو پڙھ، سندس حُڪم کي ڪا ڦير ڦار نه آھي، ۽ اُن کانسواءِ تون ڪا واھ نه لھندين.'),
(2168, 18, 28, '۽ پاڻ کي انھن سان صبر ۾ رک جيڪي پنھنجي پالڻھار کي صُبح ۽ سانجھيءَ جو ياد ڪندا آھن (۽) سندس رضا مندي گھرندا آھن ۽ (گھرجي ته) تنھنجون اکيون دُنيا جي حياتيءَ جي سينگار جو ارادو رکي انھن (ياد ڪندڙن) کان (ٻئي پاسي) نه واجھائين، ۽ انھيءَ جي فرمانبرداري نه ڪر جنھن جي دل اسان پنھنجي ياد ڪرڻ کان غافل ڪئي ۽ پنھنجي خواھش جي پٺيان لڳو ۽ سندس ڪم حد کان وڌيل آھي.'),
(2169, 18, 29, '۽ چؤ ته (اھو سخن) اوھان جي پالڻھار وٽان (آيل) سچ آھي، پوءِ جنھن کي وڻي سو ايمان آڻي ۽ جنھن کي وڻي سو نه مڃي، اسان ظالمن لاءِ باھ تيار ڪئي آھي (۽) اُن جا پردا اُنھن کي ويڙھيندا، ۽ جيڪڏھن فرياد ڪندا ته انھن جي فرياد اھڙي پاڻيءَ سان ورائي ويندي جو پگھاريل ٽامي وانگر ھوندو سندن منھن کي ڀڃندو، اُھو بڇڙو پيڻ آھي، ۽ بڇڙو آرام جو ھنڌ آھي.'),
(2170, 18, 30, 'بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا ته جنھن چڱو ڪم ڪيو آھي تنھن جو اجر اسين نه وڃائينداسون.'),
(2171, 18, 31, 'اِھي (اُھي) آھن جن لاءِ ھميشگيءَ جا بھشت (ھوندا) جن جي ھيٺان واھيون وھنديون آھن منجھس سونن ڪنگڻن جا زيور انھن کي پارايا ويندا ۽ سَنھي ۽ ٿُلھي پٽ جي سائي پوشاڪ ڍڪيندا (۽) اتي تختن تي تڪيو لائي وھندا، اِھو بدلو چڱو آھي، ۽ آرام جي جاءِ به چڱي آھي.'),
(2172, 18, 32, '۽ (اي پيغمبر) کين ٻن مڙسن جو قصو بيان ڪر ۽ انھن مان ھڪڙي لاءِ ڊاکن جا ٻه باغ پيدا ڪيا ھئاسون ۽ انھن جي چوڌاري کجيون پيدا ڪيو ھيوسون ۽ انھن جي وچ ۾ پوک پيدا ڪئي ھئي سون.'),
(2173, 18, 33, 'ٻنھين باغن پنھنجا ميوا پچايا ۽ منجھانئن ڪجھ گھٽ نه ڪيائون ۽ انھن جي وچ ۾ واھيون وھايون سون.'),
(2174, 18, 34, '۽ ان کي (گھڻو) ڦر مليو، پوءِ پنھنجي سنگتيءَ سان ڳالھائيندي ان کي چيائين ته آءٌ توکان مال ۾ وڌيڪ آھيان ۽ ماڻھن جي ڪري (به) وڌيڪ عِزّت وارو آھيان.'),
(2175, 18, 35, '۽ پنھنجي باغ ۾ گھڙيو ۽ ھو پاڻ لاءِ ظالم ھو، چيائين ته مون کي ھن (باغ) جي ناس ٿيڻ جو ڪڏھن به گمان نه آھي.'),
(2176, 18, 36, '۽ نڪي قيامت جي قائم ٿيڻ جو مون کي ڪو گمان آھي ۽ جيڪڏھن پنھنجي پالھڻار ڏانھن موٽيس (ته) ھن کان (به) ڀلي جاءِ لھندس.'),
(2177, 18, 37, 'سندس سنگتي ساڻس گفتگو ڪندي کيس چيو ته جنھن توکي مٽيءَ مان بڻايو وري نطفي سان وري سنئين لڱين مڙس ڪيائين تنھن جو تو ڇو انڪار ڪيو؟'),
(2178, 18, 38, '(آءٌ ھي ويساھ رکندو آھيان ته) منھنجو پالڻھار الله آھي ۽ آءٌ پنھنجي پالڻھار سان ڪوبه شريڪ نه ڪندس.'),
(2179, 18, 39, '۽ جڏھن تون پنھنجي باغ ۾ گھڙين تڏھن ڇونه چيئي ته جيڪي الله گھريو (سو ٿيڻو آھي) جو الله (جي مدد) کانسواءِ ڪا سگھ نه آھي، جيڪڏھن تون پاڻ کان مون کي مال ۽ اولاد ۾ گھٽ ڏسين ٿو.'),
(2180, 18, 40, 'ته اُميد آھي جو منھنجو پالڻھار تنھنجي باغ کان چڱو مونکي ڏيندو ۽ اُن (تنھنجي باغ) تي آسمان کان ڪا آفت موڪلي پوءِ ڦري چِٽي پَٽي ٿي پَوي.'),
(2181, 18, 41, 'يا سندس پاڻي سُڪي وڃي پوءِ ان کي ڪڏھن لھي نه سگھين.'),
(2182, 18, 42, '۽ سندس ڦر کي ڇٽ ڪيو ويو پوءِ اُن (جي بڻائڻ) ۾ جيڪي خرچيو ھوائين تنھن تي پنھنجا ھٿ مَليندو رھيو ۽ اُھو (باغ) پنھنجين (بڻايلن) ڇتين سميت ڪريل ھو ۽ چيائين ٿي ته افسوس آھي پنھنجي پالڻھار سان ڪنھن کي شريڪ مُقرر نه ڪريان ھا (ته چڱو ھو).'),
(2183, 18, 43, '۽ نڪي ان لاءِ ڪا ٽولي ھئي جو الله کانسواءِ کيس مدد ڏئي ۽ نڪي (پاڻ) بدلو وٺڻ وارو ھو.'),
(2184, 18, 44, 'ھن ھنڌ الله جي سچي بادشاھي آھي، اُھو ثواب (ڏيڻ) ۾ ڀلو ۽ بدلي ڏيڻ ۾ (به) ڀلو آھي.'),
(2185, 18, 45, '۽ (اي پيغمبر) انھن کي دُنيا جي حياتيءَ جو مثال بيان ڪر جا انھي پاڻيءَ وانگر آھي جنھن کي آسمان کان وسايوسون پوءِ اُن سان زمين جا اوڀڙ گھاٽا ڄميا پوءِ ڦري ڀُريل ڪک ٿيا اُنھن کي وائن اُڏايو، ۽ الله سڀڪنھن شيءِ تي وس وارو آھي.'),
(2186, 18, 46, 'مال ۽ اولاد دنيا جي حياتيءَ جو سينگار آھي، ۽ سدائين رھندڙ چڱايون تنھنجي پالڻھار وٽ ثواب ڪري ڀليون ۽ اُميد ڪري چڱيون آھن.'),
(2187, 18, 47, '۽ (ياد ڪر) جنھن ڏينھن جبلن کي ھلائينداسون ۽ زمين کي صفا پٽي ڏسندين ۽ ماڻھن کي گڏ ڪنداسون پوءِ منجھانئن ڪنھن ھڪ کي نه ڇڏينداسون.'),
(2188, 18, 48, '۽ (اُھي) تنھنجي پالڻھار وٽ صفون ڪري پيش ڪيا ويندا (چونداسون ته) جھڙي طرح اوھان کي پھريون ڀيرو پيدا ڪيوھوسون (تھڙي طرح) اسان وٽ آيا آھيو، بلڪ ڀائيندا ھيؤ ته اوھان جي لاءِ انجام جو ھنڌ نه ڪنداسون.'),
(2189, 18, 49, '۽ اعمالنامون رکيو ويندو پوءِ جيڪي منجھس ھوندو تنھن کان ڏوھارين کي ڊڄندڙ ڏسندين ۽ چوندا ته اسان لاءِ ارمان آھي ھن اعمالنامي جو ڇا حال آھي جو نڪي ننڍي (گناھ) ۽ نڪي وڏي کي ڳاڻيٽي کانسواءِ ڇڏيو اٿس، ۽ جيڪي ڪيائون سو حاضر لھندا، ۽ تنھنجو پالڻھار ڪنھن ھڪڙي تي به ظلم نه ڪندو.'),
(2190, 18, 50, '۽ جڏھن ملائڪن کي چيوسون ته آدم کي سجدو ڪريو تڏھن ابليس کانسواءِ ٻـين سجدو ڪيو، اھو جِنّن مان ھو پوءِ پنھنجي پالڻھار جي حُڪم جي نافرماني ڪيائين، پوءِ اوھين کيس ۽ سندس اولاد کي مون کانسواءِ ڇو دوست وٺندا آھيو حالانڪ اُھي اوھان جا ويري آھن، (شيطان) ظالمن لاءِ بڇڙو بدلو آھي.'),
(2191, 18, 51, 'نڪي آسمانن ۽ زمين جي بڻائڻ مھل اُنھن (شيطانن) کي حاضر ڪيو ھوم ۽ نڪي سندن جيئن بڻائڻ مھل به، ۽ نڪي گمراھن کي مددگار وٺندڙ آھيان.'),
(2192, 18, 52, '۽ جنھن ڏينھن الله چوندو ته (اي مشرڪؤ!) اوھين انھن کي سڏيو جن کي منھنجا شريڪ ڄاڻندا ھيؤ ۽ پوءِ انھن کي سڏيندا ۽ اُھي کين ورندي نه ڏيندا ۽ سندن وچ ۾ ھلاڪت جو ھنڌ ڪنداسون.'),
(2193, 18, 53, '۽ ڏوھاري باھ کي ڏسندا پوءِ ڀائيندا ته اُھي اُن ۾ ڪرندا ۽ کانئس موٽڻ جي واھ نه لھندا.'),
(2194, 18, 54, '۽ بيشڪ ھن قرآن ۾ ھر طرح جا مثال ماڻھن لاءِ بيان ڪياسون، ۽ ماڻھو سڀ شيء کان گھڻو جھيڙاڪار آھي.'),
(2195, 18, 55, '۽ ماڻھن وٽ جڏھن ھدايت آئي تڏھن کين ايمان آڻڻ ۽ پنھنجي پالڻھار کان بخشش گھرڻ کان ھن ڌاران ڪنھن نه جھليو جو اڳين جي رسم (جھڙي پاڙ پَٽ سزا) وٽن اچي يا وٽن سامھون عذاب پُھچي.'),
(2196, 18, 56, '۽ پيغمبرن کي رڳو خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليون ٿا، ۽ ڪافر ڪوڙو جھڳڙو ھن لاءِ ڪندا آھن ته ان سان سچ کي ٿيڙين ۽ منھنجين نشانين کي ۽ جنھن سان ڊيڄاريا ويا تنھنکي ٺـٺولي ڪري ورتائون.'),
(2197, 18, 57, '۽ اُن کان وڌيڪ ظالم ڪير آھي جنھن کي سندس پالڻھار جي نشانين سان نصيحت ڏني وڃي ته اُن کان مُنھن موڙي ۽ جيڪي سندس ھٿن اڳي ڪري موڪليو سو وساري؟ اسان ان جي سمجھڻ کان سندين دلين تي پردا ڪيا ۽ سندن ڪنن ۾ گھٻرائي (وڌي سون) ۽ جيڪڏھن تون کين ھدايت ڏانھن سڏين ته اُن وقت سِڌو رستو ڪڏھن به نه لھندا.'),
(2198, 18, 58, '۽ تنھنجو پالڻھار بخشڻھار ٻاجھارو آھي، جيڪڏھن کين سندن ڪيتي سببان پڪڙي ھا ته انھن لاءِ عذاب تڪڙو موڪلي ھا، (نه!) بلڪ انھن لاءِ انجام ٿيل آھي جو اُن کانسواءِ ڪا واھ نه لھندا.'),
(2199, 18, 59, '۽ اھي ڳوٺ آھن جو انھن کي (تڏھن) ناس ڪيوسون جڏھن ظلم ڪيائون ۽ انھن جي ناس ڪرڻ لاءِ (ھڪ) انجام مقرر ڪيوسون.'),
(2200, 18, 60, '۽ (ياد ڪر) جڏھن موسىٰ پنھنجي سنگتيءَ کي چيو ته سدا پيو پنڌ ڪندس جيسين ٻن دريائن جي گڏ ٿيڻ جي ھنڌ پُھچندس يا ورھن تائين پيو ھلندس.'),
(2201, 18, 61, 'پوءِ جنھن مھل ٻن دريائن جي گڏ ٿيڻ جي ھنڌ کي پُھتا (تنھن مھل) پنھنجي مڇي وساريائون جنھن پنھنجي واٽ درياءَ ۾ سرنگھ جان ورتي.'),
(2202, 18, 62, 'پوءِ جڏھن اڳي ھليا (تڏھن موسىٰؑ) پنھنجي سنگتيءَ کي چيو ته اسان کي اسان جو منجھند جو طعام ڏي، بيشڪ اسان جي ھن مسافريءَ ۾ اسان کي اوکائي پُھتي آھي.'),
(2203, 18, 63, 'چيائين ته (نه) ڏٺئي ڇا جڏھن پھڻ وٽ ترسيا ھواسون تڏھن مون (اتي) مڇي وساري، ۽ ان جي يادگيري مون کان شيطان کانسواءِ ٻئي ڪنھن نه وسارائي، ۽ پنھنجي واٽ درياء ۾ عجب طرح ورتائين.'),
(2204, 18, 64, '(موسىٰؑ) چيو ته اِھو ھنڌ اُھو آھي جنھن کي اسان ڳوليو ٿي، پوءِ ھو پنھنجي پوئين پيرين ڳوليندا موٽيا.'),
(2205, 18, 65, 'پوءِ مُنھنجن ٻانھن مان اُھو ٻانھون لڌائون جنھن کي پاڻ وٽا ٻاجھ ڏني ھئي سون ۽ پاڻ وٽان کيس عِلم سيکاريو ھوسون.'),
(2206, 18, 66, 'موسىٰؑ ان کي چيو ته آءٌ تنھنجي ڪڍ ھلان ھن شرط تي ته توکي جيڪي ھدايت مان سيکاريو ويو اُن مان مون کي به سيکارين.'),
(2207, 18, 67, '(خضرؑ) چيو ته تون مون سان ڪڏھن به صبر ڪري نه سگھندين.'),
(2208, 18, 68, '۽ جنھن جي توکي خبر ئي نه آھي تنھن تي تون ڪٿي صبر ڪندين.'),
(2209, 18, 69, '(موسىٰؑ) چيو ته جي الله گھريو ته مون کي صبر ڪندڙ لھندين ۽ آءٌ تنھنجي حُڪم جي نافرماني نه ڪندس.'),
(2210, 18, 70, '(خضرؑ) چيو ته جيڪڏھن منھنجي سنگت ڪرين ته (ايستائين) مون کان ڪنھن ڳالھ بابت نه پُڇج جيسين (آءٌ) پاڻ توسان ان جو بيان (نه) ڪريان.'),
(2211, 18, 71, 'پوءِ ٻئي (اُٿي) ھليا، تان جو ٻيڙيءَ ۾ چڙھيا تڏھن (خضرؑ) اُن کي سوراخ ڪيو، (موسىٰؑ) چيو ته تو اُن کي انھيءَ لاءِ سوراخ ڪيو ڇا ته ٻيڙي وارن کي ٻوڙين؟ بيشڪ تو اڍنگو ڪم ڪيو.'),
(2212, 18, 72, '(خضرؑ) چيو ته توکي نه چيو ھوم ڇا ته تون مون سان صبر ڪري نه سگھندين.'),
(2213, 18, 73, '(موسىٰؑ) چيو ته جيڪي مون وساريو تنھن سببان مون کي نه پڪڙ ۽ منھنجي ڪم ۾ مون تي سختي نه وجھ.'),
(2214, 18, 74, 'پوءِ ٻئي ھليا، تان جو جڏھن ھڪ ڇوڪر کي مليا تڏھن (خضرؑ) اُن کي ڪُٺو (موسىٰؑ) چيو ته قصاص ڌاران بيگناھ جِيءَ کي ڇو ڪُٺئي؟ بيشڪ تو خراب ڪم ڪيو.'),
(2215, 18, 75, '(خضرؑ) چيو ته توکي نه ھوم ڇا ته تون مون سان (گڏ رھي) ڪڏھن نه صبر ڪري سگھندين؟'),
(2216, 18, 76, '(موسىٰؑ) چيو ته جيڪڏھن ھِن کانپوءِ ڪنھن ڳالھ بابت توکان پڇان ته منھنجي سنگت نه ڪج، بيشڪ منھنجي پاران عذر (جي حد) کي پھتين.'),
(2217, 18, 77, 'پوءِ (ٻئي) ھليا، تان جو جنھن مھل ھڪڙي ڳوٺ وارن وٽ آيا (تنھن مھل) ان جي رھاڪن کان طعام گھريائون ته (انھن) سندن مھماني ڪرڻ کان انڪار ڪيو پوءِ اُن (ڳوٺ) ۾ ھڪ ڊھڻ کي ويجھي ڀِت ڏٺائون پوءِ اُھا (خضرؑ) سڌي ڪئي، (موسىٰؑ) چيو ته جيڪڏھن گھرين ھا ته اُن (جي سڌي ڪرڻ) تي مزدوري ضرور وٺين ھا.'),
(2218, 18, 78, '(خضرؑ) چيو ته ھاڻي منھنجي ۽ تنھنجي وچ ۾ جُدائي پئي، ھاڻي توکي انھن ڳالھين جي حقيقت جي سُڌ ڏيان ٿو جن تي تون صبر نه ڪري سگھين.'),
(2219, 18, 79, 'پر ٻيڙي (جا ڀڳي ويئي سا) مسڪينن جي ھئي (جنھن تي) ھو درياءَ ۾ پورھيو ڪندا (وتندا) ھوا پوءِ اُن کي عيب دار ڪرڻ گھريم جو سندن پوئتان اھڙو بادشاھ ھو جنھن سڀڪا ٻيڙي بيگر ۾ ٿي ورتي.'),
(2220, 18, 80, '۽ نينگر (جو ماريو ويو) تنھن جا ماءُ پيءُ مؤمن ھوا تنھنڪري ڊناسون ته نافرمانيءَ ۽ ڪفر ڪرڻ سان ٻنھي کي عاجز ڪندو.'),
(2221, 18, 81, 'پوءِ گھُريوسون ته سندن پالڻھار کين اُن کان به چڱو بدلو سُٺائي جي ڪري ۽ ٻاجھ جي ڪري تمام ويجھو ڏئي.'),
(2222, 18, 82, '۽ جا ڀِت (ٺاھي وئي) سا شھر ۾ ٻن ڇورن ٻارن جي ھُئي ۽ ان جي ھيٺان سندن خزانون ھو ۽ سندن ماءُ پيءُ نيڪ ھوا، پوءِ تنھنجي پالڻھار گھريو ته ٻئي پنھنجي جوانيءَ کي پھچن ۽ پنھنجو خزانو تنھنجي پالڻھار جي ٻاجھ سان ڪڍن، ۽ اُھو پنھنجي راءِ سان نه ڪيم، اِھا اُن جي حقيقت آھي جنھن تي تون صبر نه ٿي ڪري سگھئين.'),
(2223, 18, 83, '۽ (اي پيغمبر) توکان ذُوالۡقَرۡنَيۡن بابت پڇن ٿا، چؤ ته اوھان کي ان جو (ڪُجھ) بيان ڪري ٻڌايان ٿو.'),
(2224, 18, 84, 'بيشڪ اسان ان کي زمين ۾ ھٿ پھچ ڏني ۽ سڀڪنھن شيء جو سان کيس ڏنوسون.'),
(2225, 18, 85, 'پوءِ سامان (جي مدد) سان ھليو.'),
(2226, 18, 86, 'ايترو (سير ڪيائين) جو سچ لھڻ جي ھنڌ پھتو اُن کي گپڙاٺي تلاءُ ۾ لھندو ڏٺائين ۽ اُن (تلاءُ) وٽ ھڪ قوم کي ڏٺائين، چيوسون ته اي ذوالقرنين انھن کي سزا ڏئين يا منجھن ڪو چڱائيءَ جو سڌارو ڪرين (ته توکي اختيار آھي).'),
(2227, 18, 87, '(ذوالقرنين) چيو ته جيڪو (ماڻھو) ظلم ڪندو تنھن کي سزا ڏينداسون وري پنھنجي پالڻھار ڏانھن ورايو ويندو پوءِ اُھو کيس سخت عذاب جي سزا ڏيندو.'),
(2228, 18, 88, '۽ جيڪو ايمان آڻيندو ۽ چڱا ڪم ڪندو تنھن کي چڱو بدلو ملندو، ۽ پنھنجي معاملي مان اُن کي سولائي (جھڙو ڪم) چونداسون.'),
(2229, 18, 89, 'وري سامان (جي مدد) سان ھليو.'),
(2230, 18, 90, 'ايترو (سير ڪيائين) جو جڏھن سج اُڀرڻ جي ھنڌ پھتو ته اُھو ھڪ اھڙي قوم تي اُڀرندو ڏٺائين جنھن لاءِ سج کانسواءِ ڪا اوٽ نه بڻائي ھئي سون.'),
(2231, 18, 91, 'اھڙي طرح (سندس قِصّوآھي)، ۽ بيشڪ اسان وٽ ان جي ساري خبر آھي.'),
(2232, 18, 92, 'وري سامان (جي مدد) سان ھليو.'),
(2233, 18, 93, 'ايترو جوجڏھن ٻن جبلن جي وچ ۾ پھتو ته انھن جي ھُن پاسي اھڙي قوم ڏٺائين جا ڪابه ڳالھ سمجھي نه ٿي سگھي.'),
(2234, 18, 94, 'چيائون ته اي ذوالقرنين بيشڪ ياجوج ۽ ماجوج مُلڪ ۾ فساد وجھندڙ آھن تنھن ڪري تو لاءِ اسين ھن شرط تي ڏن مُقرّر ڪريون ته تون اسان جي ۽ سندن وچ ۾ اَڙ بڻاءِ.'),
(2235, 18, 95, '(ذوالقرنين) چيو ته منھنجي پالڻھار جيڪا مون کي ان بابت سگھ ڏني آھي سا ڀلي آھي پوءِ اوھين مون سان زور بار جي مدد ڪريو ته اوھان جي ۽ سندن وچ ۾ اَڙ بڻايان.'),
(2236, 18, 96, 'لوھ جا ٽُڪر مون وٽ آڻيو، جنھن دم ٻنھي جبلن جي وچ ۾ (ڀري) پورائي ڪيائين (تنھن دم) چيائين ته ڌنئون، تان جو جڏھن ان کي (تپائي) باھ ڪيائين (تڏھن) چيائين ته مون وٽ آڻيو ته مٿس پگھاريل ٽامون پلٽيان.'),
(2237, 18, 97, 'پوءِ (ياجوج ماجوج) نڪي ان تي چڙھي سگھيا ۽ نڪي اُن کي ڪا سرنگھ ھڻي سگھيا.'),
(2238, 18, 98, 'چيائين ته ھي منھنجي پالڻھار جي (ھڪڙي) ٻاجھ آھي، جنھن مھل منھنجي پالڻھار جو انجام ايندو (تنھن مھل) ان کي سنئون ڪري ڇڏيندو ۽ منھنجي پالڻھار جو انجام سچو آھي.'),
(2239, 18, 99, '۽ اُن ڏينھن ڇڏينداسون ته ھڪ ٻئي ۾ ڳاھٽ ٿيندا (ايندا) ۽ صُور ۾ ڦوڪيو ويندو پوءِ انھن مِڙني کي گڏ ڪنداسون.'),
(2240, 18, 100, '۽ اُن ڏينھن اُنھن ڪافرن کي دوزخ آڏو ڪنداسون جئن آڏو ڪجي.'),
(2241, 18, 101, 'جن جون اکيون منھنجي يادگيريءَ کان ڍَڪ ۾ ھيون ۽ (ساڙ کان ڪجھ) ٻُڌي نه سگھندا ھوا.'),
(2242, 18, 102, 'ڪافرن (اڃا پاڻ کي سزا جوڳو) نه ڀانيو آھي ڇا جو مون کان سواءِ منھنجن ٻانھن کي دوست ورتو اٿن؟ اسان ڪافرن جي رھڻ لاءِ دوزخ تيار ڪيو آھي.'),
(2243, 18, 103, '(اي پيغمبر) چؤ ته ڪرتوتن جي ڪري ٽوٽي وارن جي اوھان کي سُڌ ڏيون ڇا؟'),
(2244, 18, 104, 'جن پنھنجي ڪوشش دنيا جي حياتيءَ ۾ اَجائي وڃائي ۽ اُھي (پنھنجي لاءِ) ڀائيندا آھن ته اُھي (پاڻ) چڱو ڪم ڪندا آھن.'),
(2245, 18, 105, 'اِھي اُھي آھن جن پنھنجي پالڻھار جي آيتن ۽ سندن ملڻ جو انڪار ڪيو پوءِ سندن ڪئي ڪمائي چٽ ٿي پوءِ اُنھن لاءِ قيامت جي ڏينھن ڪا تور کڙي نه ڪنداسون.'),
(2246, 18, 106, 'اِھا سندين سزا دوزخ انھي سببان آھي جو ڪفر ڪيائون ۽ منھنجي آيتن ۽ منھنجي پيغمبرن کي ٺـٺولي ڪري ورتائون.'),
(2247, 18, 107, 'جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ فردوس جا باغ مھماني (جي جاءِ) آھن.'),
(2248, 18, 108, 'منجھس سدائين رھڻ وارا آھن کانئس وري اٿلڻ نه گھرندا.'),
(2249, 18, 109, 'چؤ ته جيڪڏھن منھنجي پالڻھار جي سُخنن (لکڻ) لاءِ درياء مسُ ٿئي ته منھنجي پالڻھار جي سُخنن پوري ٿيڻ کان اڳ درياء کَپي ويندو توڻيڪ جھڙس (ٻيا) مدد لاءِ آڻيون.'),
(2250, 18, 110, 'چؤ ته آءٌ اوھان جي جھڙوئي ماڻھو آھيان مون ڏانھن وحي موڪليو ويندو آھي ته اوھان جو معبود رڳو ھڪ الله آھي، پوءِ جيڪو پنھنجي پالڻھار جي ملڻ جي اميد رکي تنھنکي جڳائي ته چڱا ڪم ڪري ۽ پنھنجي پالڻھار جي عبادت ۾ ڪنھن ھڪڙي کي شريڪ نه ڪري.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'ڪٓھٰيٰعٓصٓ.'),
(2252, 19, 2, 'تنھنجي پالڻھار جي ٻاجھ سندس ٻانھي زڪريا تي ٿيل جو (ھيءُ) ذڪر آھي.'),
(2253, 19, 3, 'جڏھن پنھنجي پالڻھار کي ڳجھو ٻاڏائي سڏيائين.'),
(2254, 19, 4, 'چيائين ته اي منھنجا پالڻھار مُنھنجا (لڱن جا) ھڏ سُست ٿيا آھن ۽ ٻُڍائيءَ کان منھنجو مٿو (اڇو) چمڪيو آھي ته اي منھنجا پالڻھار توکي سڏڻ کان نا اميد نه آھيان.'),
(2255, 19, 5, '۽ آءٌ پنھنجي پوئتان پنھنجن مائٽن کان ڊڄان ٿو ۽ منھنجي زال سنڍ آھي تنھنڪري پاڻ وٽان مون کي ڪو وارث بخش.'),
(2256, 19, 6, 'جو منھنجو وارث ٿئي ۽ يعقوب جي اولاد جو به وارث ٿئي، ۽ اي منھنجا پالڻھار ان کي صالح ڪر.'),
(2257, 19, 7, '(چيوسون ته) اي زڪريا اسان توکي ھڪ نينگر جي مبارڪ ڏيون ٿا جنھنجو نالو يحيىٰ آھي اڳ اھڙي نالي وارو ڪونه ڪيوسون.'),
(2258, 19, 8, 'چيائين اي منھنجا پالڻھار مون کي ھن حالت ۾ پٽ ڪئن ٿيندو جو منھنجي زال سنڍ آھي ۽ بيشڪ آءٌ پيريءَ کان جھورائيءَ کان پھتو آھيان.'),
(2259, 19, 9, '(جبرئيل) چيو ته اھڙي طرح (ٿيڻو) آھي، تنھنجي پالڻھار چيو آھي ته اِھو مون کي آسان آھي ۽ بيشڪ توکي ھن کان اڳ پيدا ڪيو اٿم ۽ تون (اصل) ڪجھ نه ھُئين.'),
(2260, 19, 10, 'زڪريا چيو ته اي منھنجا پالڻھار مون لاءِ ڪا نشاني ٺھراءِ، فرمايائين ته تنھنجي نشاني (ھيءَ آھي) ته ماڻھن سان ساندہ ٽي ڏينھن راتيون ڳالھائي نه سگھندين.'),
(2261, 19, 11, 'پوءِ محراب مان پنھنجي قوم ڏانھن نڪتو پوءِ انھن اشارو ڏنائين ته صُبح ۽ سانجھيءَ جو (الله جي) پاڪائي بيان ڪريو.'),
(2262, 19, 12, '(۽ يحيىٰ ڄمي ڏھن ورھن جو ٿيو ته) چيوسون ته اي يحيىٰ ڪتاب کي مُحڪم وٺ، ۽ ٻاراڻي ۾ کيس عِلم ڏنوسون.'),
(2263, 19, 13, '۽ پاڻ وٽان کيس شفقت ۽ سُٺائي ڏني سون، ۽ (اُھو) پرھيزگار ھو.'),
(2264, 19, 14, '۽ پنھنجي ماءُ پيءُ سان نيڪي ڪرڻ وارو ھو ۽ ھٺيلو نافرمان نه ھو.'),
(2265, 19, 15, '۽ جنھن ڏينھن ڄائو ۽ جنھن ڏينھن مرندو ۽ جنھن ڏينھن جيئرو ٿي اُٿندو (تنھن ڏينھن) مٿس سلام ھجي.'),
(2266, 19, 16, '۽ مريم جو قِصّو ڪتاب ۾ ياد ڪر، جڏھن پنھنجي گھر وارن کان اوڀرندي ھنڌ پاس ڀري ٿي.'),
(2267, 19, 17, 'پوءِ کانئن ھڪ اوٽ ورتائين، پوءِ ڏانھنس پنھنجو ملائڪ موڪليوسون پوءِ ھو ڏسجڻ ۾ اُن لاءِ پورو ماڻھو بڻجي بيٺو.'),
(2268, 19, 18, '(مريم) چيو آءٌ توکان ٻاجھاري (الله) جي سام پوان ٿي جيڪڏھن پرھيزگار آھين (ته مون کان پري ٿيءُ).'),
(2269, 19, 19, '(ملائڪ) چيو ته آءٌ ته تنھنجي پالڻھار جو ھن لاءِ موڪليل آھيان ته توکي چڱو نينگر بخشيان.'),
(2270, 19, 20, '(مريم) چيو ته مون کي ٻار ڪيئن ٿيندو جو مون کي ڪنھن ماڻھوءَ نه ڇُھيو آھي ۽ نڪي آءٌ بدڪار آھيان.'),
(2271, 19, 21, '(ملائڪ) چيو ته اھڙي طرح (ٿيڻو) آھي، تنھنجي پالڻھار فرمايو ته اِھو مون کي سولو آھي، ۽ (ھن لاءِ) ته ان کي ماڻھن جي لاءِ پنھنجي طرف کان نشاني ۽ ٻاجھ (جو وسيلو) ڪريون، ۽ (اِھو) ڪم ٺھرايل آھي.'),
(2272, 19, 22, 'پوءِ اُن سان پيٽ ٿيس پوءِ اُن سان پرڀري ھنڌ الڳ ٿي.'),
(2273, 19, 23, 'پوءِ ويَم جي سورن کجيءَ جي ٿڙ ڏانھن نِيس، چيائين ته جيڪر ھن کان اڳ مران ھا ۽ اصل وسري ويل ھجان ھا (ته چڱو ھو).'),
(2274, 19, 24, 'پوءِ کيس ملائڪ ھيٺين طرف کان سڏ ڪيو ته ڏک نه ڪر بيشڪ تنھنجي پالڻھار تنھنجي ھيٺان تلاءُ پيدا ڪيو آھي.'),
(2275, 19, 25, '۽ کجيءَ جي ٿڙ کي پاڻ ڏانھن ڌوڻ ته توتي تازيون کارڪون ڇاڻي.'),
(2276, 19, 26, 'پوءِ کاءُ ۽ پيءُ ۽ نيڻ ٺار، پوءِ جيڪڏھن ڪنھن ماڻھوءَ کي ڏسين ته چؤ ته مون الله لاءِ روزو باسيو آھي تنھنڪري اڄ ڪنھن ماڻھوءَ سان پٽيءَ نه ڳالھائيندس.'),
(2277, 19, 27, 'پوءِ اُن کي پنھنجي قوم وٽ کڻي آئي، چيائون ته اي مريم بيشڪ بڇڙي شيء آندي اٿيئي.'),
(2278, 19, 28, 'اي ھارون جي ڀيڻ نڪي تنھنجو پيءُ بڇڙو مڙس ھو ۽ نڪي تنھنجي ماءُ ڪا بدڪار ھئي.'),
(2279, 19, 29, 'پوءِ ڏانھنس اشارو ڏنائين، چيائون ته جيڪو پينگھي ۾ ڇوڪر آھي تنھن سان ڪيئن ڳالھايون.'),
(2280, 19, 30, '(عيسى) چيو ته آءٌ الله جو ٻانھو آھيان، مون کي ڪتاب ڏنو اٿس ۽ مون کي پيغمبر ڪيو اٿس.'),
(2281, 19, 31, '۽ جتي ھوندس اُتي مون کي برڪت وارو ڪيائين، ۽ جيسين جيئرو ھوندس تيسين مون کي نماز (پڙھڻ) ۽ زڪوٰة (ڏيڻ) جو حُڪم ڪيائين.'),
(2282, 19, 32, '۽ پنھنجي ماءُ سان چڱائي ڪندڙ (ڪيائين)، ۽ مون کي ھٺيلو ۽ بدبخت نه ڪيائين.'),
(2283, 19, 33, '۽ جنھن ڏينھن ڄائس ۽ جنھن ڏينھن مرندس ۽ جنھن ڏينھن وري جيئرو ٿي اٿندس تنھن ڏينھن مون تي سلام آھي.'),
(2284, 19, 34, 'اِھو (قِصّو) عيسىٰ پٽ مريم جو آھي، سچي ڳالھ آھي جنھن ۾ (ماڻھو) شڪ ڪندا آھن.'),
(2285, 19, 35, 'الله کي (پاڻ لاءِ) ڪو پٽ وٺڻ نه جڳائيندو آھي اُھو پاڪ آھي، جڏھن ڪو ڪم ڪندو آھي تڏھن اُن لاءِ رڳو چوندو آھي ته ٿيءُ ته ٿي پوندو آھي.'),
(2286, 19, 36, '۽ الله منھنجو پالڻھار ۽ اوھان جو پالڻھار آھي تنھنڪري سندس عبادت ڪريو، اِھا سڌي واٽ آھي.'),
(2287, 19, 37, 'پوءِ ڪن ٽولين پاڻ ۾ اختلاف ڪيو، پوءِ ڪافرن لاءِ وڏي ڏينھن جي آڏي ٿيڻ کان خرابي آھي.'),
(2288, 19, 38, 'جنھن ڏينھن اسان وٽ ايندا (تنھن ڏينھن) ڪھڙو نه چڱو ٻڌندا ۽ ڪھڙو نه چڱو ڏسندا پر ظالم اڄ پڌريءَ گمراھي ۾ آھن.'),
(2289, 19, 39, '۽ کين پشيماني جي جي ڏينھن کان ڊيڄار جنھن ڏينھن ڪم پورو ڪبو، ۽ اُھي غفلت ۾ آھن ۽ اُھي ايمان نه ٿا آڻين.'),
(2290, 19, 40, 'بيشڪ اسين زمين جا ۽ جيڪي مٿس آھي تنھنجا وارث آھيون ۽ اسان ڏانھن موٽايا ويندا.'),
(2291, 19, 41, '۽ ابراھيم (جو قصو) ڪتاب ۾ ياد ڪر، بيشڪ اُھو سچو پيغمبر ھو.'),
(2292, 19, 42, 'جڏھن پنھنجي پيءُ کي چيائين ته اي ابا جيڪو نڪي ڏسي ۽ نڪي توکان ڪجھ ٽاري تنھنجي پوڄا ڇو ڪندو آھين؟'),
(2293, 19, 43, 'اي ابا بيشڪ (الله جي پار کان) مون وٽ سمجھ آئي آھي جا تو وٽ نه آئي آھي تنھنڪري منھنجي تابعداري ڪر ته توکي سڌو رستو ڏيکاريان.'),
(2294, 19, 44, 'اي ابا شيطان کي نه پوڄ، ڇوته شيطان الله جو نافرمان آھي.'),
(2295, 19, 45, 'اي ابا آءٌ توتي الله جي عذاب پھچڻ کان ڊڄان ٿو متان شيطان جي سنگتي ٿئين.'),
(2296, 19, 46, '(پڻس) چيو ته اي ابراھيم تون منھنجي معبودن کان ڦرڻ وارو آھين ڇا؟ جيڪڏھن نه روڪبين ته توکي ضرور پٿرن سان ماريندس ۽ گھڻي مدت مون کان پاسي ٿيءُ.'),
(2297, 19, 47, '(ابراھيم) چيو ته توتي سلام ھجي، آءٌ تولاءِ پنھنجي پالڻھار کان بخشش گھرندس ڇوته اُھو مون تي مھربان آھي.'),
(2298, 19, 48, '۽ الله کانسواءِ جن کي سڏيندا آھيو تن کان ۽ اوھان کان پاسو ڪريان ٿو ۽ پنھنجي پالڻھار جي عبادت ڪندس، اُميد آھي پنھنجي پالڻھار جي عبادت ڪرڻ کان بدبخت نه ٿيندس.'),
(2299, 19, 49, 'پوءِ جڏھن کانئن ۽ الله کانسواءِ پوڄيندا ھوا تن کان پاسو ڪيائين (تڏھن) کيس اسحاق ۽ يعقوب عطا ڪيوسين، ۽ ھر ھڪ کي پيغمبر ڪيوسون.'),
(2300, 19, 50, '۽ کين پنھنجي ٻاجھ مان (گھڻيون شيون) عطا ڪيوسون ۽ انھن جي چڱي يادگيري بلند ڪئي سون.'),
(2301, 19, 51, '۽ ڪتاب ۾ موسىٰ (جو قصو) ياد ڪر، بيشڪ اُھو چونڊيل ھو ۽ پيغمبر نبي ھو.'),
(2302, 19, 52, '۽ طور (جبل) جي سڄي ڀر کان کيس سڏيوسون ۽ ھمراز ٿيڻ لاءِ کيس ويجھو ڪيوسون.'),
(2303, 19, 53, '۽ پنھنجي ٻاجھ سان سندس ڀاءُ ھوارون پيغمبر ڪري کيس عطا ڪيوسون.'),
(2304, 19, 54, '۽ ڪتاب ۾ اسماعيل (جو قصّو) ياد ڪر، بيشڪ اُھو انجام جو پورو ۽ پيغمبر نبي ھو.'),
(2305, 19, 55, '۽ پنھنجي گھر وارن کي نماز ۽ زڪوٰة جو حُڪم ڪندو ھو، ۽ پنھنجي پالڻھار وٽ پسند ھو.'),
(2306, 19, 56, '۽ ڪتاب ۾ ادريس (جو قصّو) ياد ڪر، بيشڪ اُھو سچو پيغمبر ھو.'),
(2307, 19, 57, '۽ وڏي مرتبي ڪري سندس مانُ مٿاھون ڪيوسون.'),
(2308, 19, 58, 'جن تي الله فضل ڪيو سي آدم جي اولاد منجھان پيغمبرن مان آھن، ۽ جن کي نوح سان (ٻيڙي ۾) کنيوسون، ۽ ابراھيم ۽ يعقوب جي اولاد مان، ۽ جن کي ھدايت ڪئي سون ۽ چونڊيوسون تن مان آھن، جڏھن الله جون آيتون کين پڙھي ٻڌائبيون ھيون تڏھن سجدو ڪندڙ ۽ روئيندڙ ٿي ڪري پوندا ھوا.'),
(2309, 19, 59, 'پوءِ سندن پوئتان ڪيترا (ندورا) پيڙھيءَ تي ويٺا (جو) نماز ڇڏي ڏنائون ۽ سڌن جي پٺيان لڳا پوءَ سگھو گمراھيءَ جي سزا لھندا.'),
(2310, 19, 60, 'پر جن توبه ڪئي ۽ ايمان آندو ۽ چڱا ڪم ڪيا سي بھشت ۾ گھڙندا ۽ مٿن ڪجھ ظلم نه ڪبو.'),
(2311, 19, 61, 'ھميشه جا باغ آھن جن لاءِ الله پنھنجي ٻانھن سان پرپٺ انجام ڪيو آھي، بيشڪ سندس انجام پھچڻو آھي.'),
(2312, 19, 62, 'اُن ۾ سلامتي کانسواءِ (ٻي) ڪا بيھودي ڳالھه نه ٻڌندا، ۽ منجھس اُنھن لاءِ صُبح ۽ سانجھيءَ جو سندين روزي آھي.'),
(2313, 19, 63, 'اِھو بھشت اُھو آھي جو پنھنجن ٻانھن مان جيڪو پرھيزگار بڻبو تنھن کي (اُن جو) وارث ڪنداسون.'),
(2314, 19, 64, '۽ (ملائڪن چيو ته) تنھنجي پالڻھار جي حُڪم کانسواءِ نه لھندا آھيون، جيڪو اسان جي آڏو آھي ۽ جيڪي اسان جي پويان آھي ۽ جيڪي انھن جي وچ ۾ آھي تنھنجي (سڀ خبر) اُن کي آھي، ۽ تنھنجو پالڻھار وسارڻ وارو نه آھي.'),
(2315, 19, 65, 'آسمانن ۽ زمين ۾ جو ۽ جيڪي سندن وچ ۾ آھي تنھنجو پالڻھار آھي پوءِ سندس عبادت ڪر ۽ سندس عبادت تي صبر ڪر (يعني مُحڪم رھ)، تون اُن نالي ڪو ڄاڻندو آھين ڇا؟'),
(2316, 19, 66, '۽ ماڻھو چوندو آھي ته جڏھن مرندس (تڏھن) جيئرو ٿي ڪڍبس ڇا؟'),
(2317, 19, 67, 'ماڻھو ياد نه ڪندو آھي ڇا ته کيس اڳ پيدا ڪيوسون ڪجھ به نه ھو؟'),
(2318, 19, 68, 'پوءِ پنھنجي پالڻھار جو قسم آھي ته انھن کي ۽ شيطانن کي کڙو ڪنداسون وري دوزخ جي چوڌاري کين گوڏن ڀر حاضر ڪنداسون.'),
(2319, 19, 69, 'وري سڀ ڪنھن ٽوليءَ مان اھڙن ماڻھن کي ڌار ڪنداسون جن انھن مان ٻاجھاري (الله) جي ڏاڍي نافرماني ڪئي.'),
(2320, 19, 70, 'وري جيڪي انھن ۾ گھڙڻ جوڳا آھن تن کي اسين چڱو ڄاڻندا آھيون.'),
(2321, 19, 71, '۽ اوھان مان ڪو اھڙو ڪونھي جو اُن تي پھچڻ وارو نه آھي، تنھنجي پروردگار تي اھو انجام لازم مُقرّر آھي.'),
(2322, 19, 72, 'وري پرھيزگارن کي بچائينداسون ۽ ظالمن کي منجھس گوڏن ڀر ڪرندڙ ڇڏينداسون.'),
(2323, 19, 73, '۽ جڏھن اسان جون پڌريون آيتون انھن کي پڙھي ٻڌائبيون آھن (تڏھن) ڪافر مؤمنن کي چوندا آھن ته ٻنھي ٽولين مان ڪھڙي (ٽولي) مانَ جي ڪري ڀلي ۽ صحبت جي ڪري ڏاڍي چڱي آھي.'),
(2324, 19, 74, '۽ کانئن اڳ ڪيترائي جُڳ ناس ڪياسون جو اُھي سامان ۽ ڏيک جي ڪري ڏاڍا چڱا ھئا.'),
(2325, 19, 75, 'چؤ ته جيڪو گمراھيءَ ۾ آھي تنھن کي الله وڌيڪ وڌائيندو آھي، تان جو جيڪو انجام يا ته عذاب جو (ساڻن) ڪيو ويو سو جڏھن ڏسندا، تڏھن ھو ڄاڻندا ته بڇڙي مانَ وارو ۽ لشڪر جي ڪري ڏاڍو ھيڻو ڪير آھي.'),
(2326, 19, 76, '۽ الله ھدايت وارن کي ھدايت وڌيڪ ڪندو آھي، ۽ سدا رھندڙ چڱايون ثواب ڪري ڀليون ۽ موٽڻ ڪري چڱيون تنھنجي پالڻھار وٽ آھن.'),
(2327, 19, 77, '(اي پيغمبر) اُنھي کي نه ڏٺو اٿيئي ڇا جنھن اسان جي آيتن کي نه مڃيو ۽ چيائين ته مون کي مال ۽ اولاد ضرور ڏنو ويندو؟'),
(2328, 19, 78, '(اُھو) ڳجھ ڄاڻندو آھي ڇا يا الله وٽان ڪو انجام ورتو اٿس.'),
(2329, 19, 79, 'ائين نه آھي، جيڪي چوندو آھي سو سگھوئي لکنداسون ۽ اُن لاءِ ڊگھو عذاب وڌائينداسون.'),
(2330, 19, 80, '۽ جيڪي چوي ٿو سو کانئس کسينداسون ۽ اسان وٽ ھيڪلو ايندو.'),
(2331, 19, 81, '۽ الله کانسواءِ ٻيا معبود ھن لاءِ ورتا اٿن ته اُنھن لاءِ مانَ جو سبب ٿين.'),
(2332, 19, 82, 'ائين نه آھي، سندين پوڄا جو انڪار ڪندا ۽ اُنھن جا دشمن (۽ مخالف) ٿيندا.'),
(2333, 19, 83, '(اي پيغمبر) نه ڏٺو اٿيئي ڇا ته شيطانن کي ڪافرن تي موڪليوسون ته (اُھي) کين لوڏا ڏيئي ڌُوڻين؟'),
(2334, 19, 84, 'پوءِ مٿن تڪڙ نه ڪر، انھن لاءِ پوري ڳڻ ڳڻيندا آھيون.'),
(2335, 19, 85, 'اُن ڏينھن پرھيزگارن کي مھمانن وانگر ٻاجھاري (الله) وٽ کڙو ڪنداسون.'),
(2336, 19, 86, '۽ ڏوھين کي دوزخ ڏانھن اُڃو روانو ڪنداسون.'),
(2337, 19, 87, 'جنھن الله وٽان انجام ورتو تنھن کانسواءِ (ٻيا ڪي) شفاعت ڪري نه سگھندا.'),
(2338, 19, 88, '۽ چوندا آھن ته الله پاڻ لاءِ اولاد ورتو آھي.'),
(2339, 19, 89, '(چئبن ته) بيشڪ اوھان بڇڙي شيء آندي.'),
(2340, 19, 90, 'اِنھي (چوڻ) کان اِجھو آسمان ڦاٽندا ۽ زمين ڦاٽندي ۽ جبل ذرا ذرا ٿي ڪرندا.'),
(2341, 19, 91, 'اُنھي سببان جو (انھن) الله لاءِ پُٽ (بڻائي) سڏيا.'),
(2342, 19, 92, '۽ الله کي اولاد وٺڻ نه جڳائيندو آھي.'),
(2343, 19, 93, 'جيڪي آسمانن ۽ زمين ۾ آھي سي سڀ الله وٽ ٻانھا ٿي ايندا.'),
(2344, 19, 94, 'بيشڪ الله کين ڳڻيو آھي ۽ سندن ڳاڻاٽو ڳڻيو اٿس.'),
(2345, 19, 95, '۽ قيامت جي ڏينھن انھن مان ھر ھڪ وٽس ھيڪلو ايندو.'),
(2346, 19, 96, 'بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ سگھو الله محبّت پيدا ڪندو.'),
(2347, 19, 97, 'پوءِ اُھو (قرآن) تنھنجي زبان تي رڳو ھن لاءِ آسان ڪيوسون ته (تون) ساڻس پرھيزگارن کي خوشخبري ڏئين ۽ ان سان جھيڙا گر قوم کي ڊيڄارين.'),
(2348, 19, 98, '۽ کانئن اڳ گھڻا جُڳ ناس ڪياسون، منجائن ڪنھن ھڪ جو پتو لھين ٿو ڇا يا سندس ڀڻڪ ٻڌين ٿو ڇا؟');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طٰہٰ.'),
(2350, 20, 2, 'توتي قرآن ھن لاءِ نه لاٿوسون ته تون اؤکو ٿئين.'),
(2351, 20, 3, 'پر جيڪو ڊڄي تنھن جي نصيحت ڏيڻ لاءِ (لاٿوسون).'),
(2352, 20, 4, 'اُنھي (جي طرف) کان لھڻ وارو آھي جنھن زمين ۽ مٿاھن آسمانن کي بڻايو آھي.'),
(2353, 20, 5, '(اُھو) ٻاجھارو عرش تي قائم آھي.'),
(2354, 20, 6, 'جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي ۽ جيڪي ٻنھي جي وچ ۾ آھي ۽ جيڪي زمين جي تري ھيٺان آھي سو اُن جو آھي.'),
(2355, 20, 7, '۽ جيڪڏھن (تون) ڏاڍيان ڳالھائين ته بيشڪ اُھو ڳُجھ ۽ پڌرو ڄاڻندڙ آھي.'),
(2356, 20, 8, 'الله (اُھو آھي جو) اُن کانسواءِ ٻيو ڪو معبود نه آھي، اُن جا چڱا نالا آھن.'),
(2357, 20, 9, '۽ (اي پيغمبر) توکي مُوسىٰ جي ڳالھ نه پُھتي آھي ڇا؟'),
(2358, 20, 10, 'جڏھن باھ ڏٺائين تڏھن پنھنجي گھر جي ڀاتين کي چيائين ته ترسو جو مون باھ ڏٺي آھي اميد ته منجھانئس ڪو اڱر اوھان وٽ کڻي اچان يا باھ تي ڪو ڏَس ڏيڻ وارو لھان.'),
(2359, 20, 11, 'پوءِ جنھن مھل باھ وٽ آيو (تنھن مھل) ان کي سڏيو ويو ته اي موسى.'),
(2360, 20, 12, 'بيشڪ آءٌ تنھنجو پالڻھار آھيان تنھنڪري پنھنجي جُتي (پري) لاھ، ڇوته تون پاڪ ميدان طُوىٰ ۾ آھين.'),
(2361, 20, 13, '۽ مون توکي پسند ڪيو آھي تنھنڪري جيڪي وحي ڪجي سو ٻُڌ.'),
(2362, 20, 14, 'سچ پچ آءٌ الله آھيان مون کان سواءِ (ٻيو ڪو) معبود نه آھي تنھنڪري منھنجي عبادت ڪر ۽ منھنجي ياد ڪرڻ لاءِ نماز قائم ڪر.'),
(2363, 20, 15, 'بيشڪ قيامت اچڻي آھي اُن (جي وقت جي اچڻ) کي ھِن ڪري ڳجھو ڪريان ٿو ته سڀڪنھن جيءَ کي سندس ڪوشش جو بدلو ڏجي.'),
(2364, 20, 16, 'جيڪو اُن کي نه مڃيندو آھي ۽ پنھنجن سَڌن جي پٺيان لڳو آھي سو توکي اُن (جي وسھڻ) کان متان ڦيرائي نه ته ھلاڪ ٿيندين.'),
(2365, 20, 17, '۽ اي مُوسىٰ اِھا تنھنجي سڄي ھٿ ۾ ڇا آھي؟'),
(2366, 20, 18, 'چيائين ته ھيءَ منھنجي لٺ آھي، مٿس ٽيڪ ڏيندو آھيان ۽ اُن سان پنھنجين ٻڪرين تي (وڻ جا) پن ڇاڻيندو آھيان ۽ منجھس مون لاءِ ٻيا (به) فائدا آھن.'),
(2367, 20, 19, '(الله) فرمايو ته اي مُوسىٰ اُھا اُڇلي ڇڏ.'),
(2368, 20, 20, 'پوءِ اُھا اُڇليائين ته اُھا انھي مھل ڊوڙندڙ واسينگ نانگ ٿي پئي.'),
(2369, 20, 21, '(الله) فرمايو ته اُنھيءَ کي وٺ ۽ نه ڊڄ، اُنھيءَ کي سندس پھرين شڪل ۾ موٽائينداسون.'),
(2370, 20, 22, '۽ پنھنجو ھٿ پنھنجي بغل تي رک ته بي عيبو اڇو ٿي نڪري اِھا ٻي نشاني (ھن لاءِ) آھي.'),
(2371, 20, 23, 'ته توکي پنھنجين وڏين نشانين مان ڏيکاريون.'),
(2372, 20, 24, 'فرعون ڏانھن وڃ جو اُھو بيشڪ نافرمان ٿيو آھي.'),
(2373, 20, 25, '(مُوسى) چيو ته اي منھنجا پالڻھار مون لاءِ منھنجو سينو کول.'),
(2374, 20, 26, '۽ منھنجو ڪم مون لاءِ سولو ڪر.'),
(2375, 20, 27, '۽ منھنجي زبان جي ھٻڪ لاھ.'),
(2376, 20, 28, 'ته (اُھي) مُنھنجي ڳالھ سمجھن.'),
(2377, 20, 29, '۽ منھنجي گھر وارن منجھان مون لاءِ ڪو وزير مُقرّر ڪر.'),
(2378, 20, 30, '(يعني) منھنجي ڀاءُ ھارون کي.'),
(2379, 20, 31, 'اُن سان منھنجي سگھ مُحڪم ڪر.'),
(2380, 20, 32, '۽ منھنجي ڪم ۾ اُن کي ڀائيوار ڪر.'),
(2381, 20, 33, 'ته تنھنجي گھڻي پاڪائي بيان ڪريون.'),
(2382, 20, 34, '۽ توکي گھڻو ياد ڪريون.'),
(2383, 20, 35, 'بيشڪ تون اسان جو حال ڏسندڙ آھين.'),
(2384, 20, 36, '(الله) چيو ته اي موسىٰ بيشڪ (تولاءِ) تنھنجو سوال قبول ڪيو ويو.'),
(2385, 20, 37, '۽ بيشڪ توتي ھڪ ڀيرو (ٻيو به) احسان ڪيوسون.'),
(2386, 20, 38, 'جڏھن تنھنجي ماءُ ڏانھن اُھو الھام ڪيوسون جيڪو الھام ڪرڻو ھو.'),
(2387, 20, 39, 'ته اُن نينگر کي صندوق ۾ وِجھ پوءِ اُن کي درياءَ ۾ اُڇل ته درياءُ کيس ڀر ڏانھن اُڇليندو (۽) منھنجو ويري ۽ سندس ويري کيس وٺندو، ۽ پاڻ وٽان توتي پيار وڌم، ۽ (گھريم) ته منھنجي اکين اڳيان پلجن.'),
(2388, 20, 40, 'جڏھن تنھنجي ڀيڻ گھمي ٿي تڏھن چيائين ٿي جيڪو ان کي سنڀالي تنھنجو اوھان کي ڏس ڏيان، پوءِ توکي تنھنجي ماءُ ڏانھن موٽايوسون ته اُن جا نيڻ ٺرن ۽ غمگين نه رھي، ۽ ھڪڙو ماڻھو ماريئي پوءِ توکي ڏک کان ڇڏايوسين ۽ توکي پرکڻ طرح پرکيوسون، پوءِ مدين وارن ۾ ڪيترائي ورھ رھين، وري اي موسىٰ تون تقدير (الٰھي) موافق آئين.'),
(2389, 20, 41, '۽ (اي موسى) توکي خلاصو پاڻ لاءِ بڻايم.'),
(2390, 20, 42, 'تون ۽ تنھنجو ڀاءُ منھنجين نشانين سميت (فرعون ڏانھن) وڃو ۽ منھنجي ياد ڪرڻ ۾ ڍرائي نه ڪجو.'),
(2391, 20, 43, 'فرعون ڏانھن وڃو جو اُھو نافرمان ٿيو آھي.'),
(2392, 20, 44, 'پوءِ اُن سان نرميءَ سان ڳالھائجو ته مانَ اُھو سوچ ڪري يا ڊڄي.'),
(2393, 20, 45, 'چيائون ته اي اسان جا پالڻھار اسين ڊڄون ٿا ته متان اسان تي ڏاڍ ڪري يا زيادتي ڪري.'),
(2394, 20, 46, '(الله) چيو ته نه ڊڄو بيشڪ آءٌ اوھان سان گڏ آھيان ٻڌان ٿو ۽ ڏسان ٿو.'),
(2395, 20, 47, 'پوءِ وٽس وڃو ۽ چئو ته اسين پنھنجي پالڻھار جا موڪليل آھيون پوءِ بني اسرائيلن کي اسان سان (ھلڻ) جي موڪل ڏي، ۽ کين نه ڏکوءِ بيشڪ تو وٽ تنھنجي پالڻھار (جي طرف) کان نشانيءَ سان آيا آھيون، ۽ جيڪو سڌي رستي تي ھلي تنھن تي سلامتي آھي.'),
(2396, 20, 48, 'بيشڪ اسان ڏانھن وحي موڪليو ويو آھي ته جيڪو الله جي حُڪمن کي ڪُوڙ ڀانئي ۽ منھن موڙي تنھن تي عذاب (مقرر ٿيل) آھي.'),
(2397, 20, 49, '(فرعون) چيو ته پوءِ اي مُوسىٰ اوھان جو پالڻھار ڪير آھي؟'),
(2398, 20, 50, '(موسى) چيو ته اسان جو پالڻھاراُھو آھي جنھن ھر شيء کي (پاڻو داڻي) رُؤاني بخشي وري ھدايت ڪيائين.'),
(2399, 20, 51, '(فرعون) چيو ته پھرين ٽولين جو ڪھڙو حال آھي؟'),
(2400, 20, 52, '(موسى) چيو ته اُن جي خبر منھنجي پالڻھار وٽ ڪتاب ۾ آھي، منھنجو پالڻھار نڪي ڀُلندو آھي ۽ نڪي وساريندو آھي.'),
(2401, 20, 53, 'جنھن اوھان لاءِ زمين کي وڇاڻو بڻايو ۽ توھان لاءِ منجھس رستا جاري ڪيا ۽ آسمان کان پاڻي وَسايو، ۽ پوءِ اُن سان ھر جنس جا اوڀڙ پاڻون داڻا ڄماياسون.'),
(2402, 20, 54, '(چيوسون ته) کائو ۽ پنھنجن ڍورن کي چاريو، بيشڪ اُن ۾ سمجھ وارن لاءِ نشانيون آھن.'),
(2403, 20, 55, 'منجھانئس اوھان کي بڻايوسون ۽ منجھس اوھان کي ورائينداسون ۽ منجھانئس ٻيو ڀيرو اوھان کي ڪڍنداسون.'),
(2404, 20, 56, '۽ بيشڪ اُن (فرعون) کي سڀئي نشانيون ڏيکاريوسون پوءِ ڪوڙ ڀانيائين ۽ انڪار ڪيائين.'),
(2405, 20, 57, '(فرعون) چيو ته اي موسىٰ اسان وٽ ھِن لاءِ آيو آھين ڇا ته پنھنجي جادوءَ سان اسان جي مُلڪن مان اسان کي لوڌين؟'),
(2406, 20, 58, 'پوءِ اسان به اھڙائي جادو تو وٽ ضرور آڻينداسون (جھڙا تون اسان کي ڏيکارين ٿو) تنھنڪري اسانجي ۽ پنھنجي وچ ۾ اھڙو انجام (جو وقت) مقرر ڪر ۽ نڪي اسين ابتڙ ٿيون ۽ (اُھو) ھڪ صاف پڌر ۾ ھُجي.'),
(2407, 20, 59, 'چيائين ته اوھان جي انجام جو وقت جشن جو ڏينھن آھي ۽ ماڻھو ڏينھن چڙھي گڏ ٿين.'),
(2408, 20, 60, 'پوءِ فرعون (گھر ڏانھن) موٽيو ۽ پنھنجا ڦند گڏ ڪيائين وري آيو.'),
(2409, 20, 61, 'مُوسىٰ جادوگرن کي چيو ته اوھان تي خرابي ھجي الله تي ڪوڙو ٺاھ نه ٺاھيو نه ته اوھان کي ڪنھن آفت سان کپائيندو، ۽ جنھن ٺاھ ٺاھيو سو بيشڪ مراد کي نه پھتو.'),
(2410, 20, 62, 'پوءِ پنھنجي ڪم ۾ پاڻ ۾ گفتگو ڪيائون ۽ ڳجھيون صلاحون ڪيائون.'),
(2411, 20, 63, 'چيائون ته ھي ٻئي ضرور جادوگر آھن پنھنجي جادوءَ سان اوھان کي اوھان جي مُلڪ مان لوڌڻ ۽ اوھان جي بلڪل چڱي مذھب کي وڃائڻ گھرندا آھن.'),
(2412, 20, 64, 'تنھنڪري پنھنجا جادوءَ (جا سامان) گڏ ڪريو وري صفون ٻڌي اچو، ۽ جيڪو اڄ ڏاڍو ٿيو سو بيشڪ ڇٽو.'),
(2413, 20, 65, 'چيائون ته اي مُوسىٰ (تون) اُڇليندين يا پھرين اسين (پنھنجو جادو) اُڇليون.'),
(2414, 20, 66, '(مُوسى) چيو ته ڀلي (اوھين) اُڇليو، پوءِ اوچتوئي سندن رسا ۽ سندن لٺيون جادوءَ کان موسىٰ جي خيال ۾ آيو ته ڄڻڪ اھي ڊوڙن ٿيون.'),
(2415, 20, 67, 'پوءِ مُوسىٰ پنھنجي دل ۾ ڊپ ڀانيو.'),
(2416, 20, 68, 'چيوسون ته نه ڊڄ ڇوته تون ئي غالب آھين.'),
(2417, 20, 69, '۽ جيڪي تنھنجي سڄي ھٿ ۾ آھي سو اُڇل ته جيڪي بڻايائون سو ڳِھي وڃي، جيڪي بڻايائون سو جادوگر جي فريب کانسواءِ نه آھي، ۽ جادوگر جِتي ويندو اُتي نه ڇٽندو.'),
(2418, 20, 70, 'پوءِ سڀ جادوگر سجدو ڪندڙ ٿي ڪري پيا چيائون ته مُوسىٰ ۽ ھارون جي پالڻھار تي آيمان آندوسون.'),
(2419, 20, 71, '(فرعون) چيو ته اوھان منھنجي موڪل ڏيڻ کان اڳ اُن تي ڇو ايمان آندو، بيشڪ اُھو اوھان جو ضرور وَڏو (يعني استاد) آھي جنھن اوھان کي جادو سيکاريو آھي، تنھنڪري اوھان جا ھٿ ۽ اوھان جا پير اُبتا سُبتا ضرور وڍيندس ۽ اوھان کي کجين جي ٿُڙن ۾ ضرور سُوريءَ چاڙھيندس، ۽ ضرور ڄاڻندؤ ته اسان مان ڪنھنجو عذاب بلڪل سخت ۽ بلڪل ھميشه رھڻ وارو آھي.'),
(2420, 20, 72, 'چيائون ته چِٽن دليلن مان جيڪي اسان وٽ آيا تن تي ۽ جنھن اسان کي پيدا ڪيو تنھن تي توکي ڪڏھن زيادتي نه ڏينداسون تنھنڪري جيڪي تون ڪرڻ وارو آھين سو ڪر، (تون) رڳو دُنيا جي حياتيءَ ۾ ڪندين.'),
(2421, 20, 73, 'بيشڪ اسان پنھنجي پالڻھار تي ھن لاءِ ايمان آندو آھي ته اسان جون خطائون ۽ جنھن جادو ڪرڻ تي تو اسان کي زور ڪيو سو اسان کي بخشي، ۽ الله ڀلو ۽ سدائين رھڻ وارو آھي.'),
(2422, 20, 74, 'سچ آھي ته جيڪو منھنجي پالڻھار وٽ ڏوھاري ٿي ايندو تنھن لاءِ بيشڪ دوزخ آھي، اُھو منجھس نڪي مرندو ۽ نڪي جيئندو.'),
(2423, 20, 75, '۽ جيڪي مؤمن ٿي وٽس چڱا ڪم ڪري ايندا سي اُھي آھن جن لاءِ وڏا مرتبا آھن.'),
(2424, 20, 76, 'سدائين رھڻ جا باغ جن جي ھيٺان واھيون وھنديون آھن منجھس سدائين رھڻ وارا آھن، ۽ جيڪو پاڪ ٿيو تنھنجو اِھو بدلو آھي.'),
(2425, 20, 77, '۽ بيشڪ موسىٰ ڏانھن وحي ڪيوسون ته راتو واھ منھنجي ٻانھن سميت نڪر پوءِ سمنڊ ۾ انھن لاءِ سُڪي واٽ بڻاءِ (ويرين کان) پڪڙجڻ جي ڀَو نه ڪر ۽ نڪي (ٻُڏڻ کان) ڊڄ.'),
(2426, 20, 78, 'پوءِ فرعون پنھنجي لشڪر سميت سندن پوئتان پيو پوءِ سمنڊ مان (پاڻيءَ جو اُھو ڪُنَّ) کين وري ويو جيڪو کين ورڻو ھو.'),
(2427, 20, 79, '۽ فرعون پنھنجي قوم کي گمراھ ڪيو ۽ سڌي رستي نه لاتو.'),
(2428, 20, 80, 'اي بني اسرائيلؤ بيشڪ اوھان کي اوھان جي ويريءَ کان بچايوسون ۽ طور (جبل) جي سڄي پاسي کان اوھان کي (توريت ڏئڻ جو) انجام ڪيوسون ۽ مَنّ ۽ سَلوىٰ اوھان تي لاٿاسون.'),
(2429, 20, 81, '(۽ چيوسون ته) سٺين (شين) مان جيڪا اوھان جي روزي ڪئي سون سا کائو ۽ منجھس نافرماني نه ڪريو نه ته اوھان تي منھنجو ڏمر نازل ٿيندو، ۽ جنھن تي منھنجو ڏمر نازل ٿيو سو بيشڪ اُجڙيو.'),
(2430, 20, 82, '۽ جنھن توبه ڪئي ۽ ايمان آندو ۽ چڱا ڪم ڪيا وري سڌو رستو لڌائين تنھن کي بيشڪ آءٌ بخشيندڙ آھيان.'),
(2431, 20, 83, '۽ اي موسىٰ ڪنھن توکي پنھنجي قوم کان (اڳ اچڻ جي) تڪڙ ڪرائي؟'),
(2432, 20, 84, 'چيائين ته اُھي منھنجي پوئتان آھن ۽ اي منھنجا پالڻھار توڏانھن ھن لاءِ تڪڙ ڪيم ته راضي ٿئين.'),
(2433, 20, 85, '(الله) چيو ته اسان تنھنجي پويان تنھنجي قوم کي پرکيو ۽ کين سامريءَ گمراھ ڪيو.'),
(2434, 20, 86, 'پوءِ مُوسىٰ پنھنجي قوم ڏانھن ڪاوڙجي ارمان ۾ موٽيو، چيائين ته اي منھنجي قوم اوھان جي پالڻھار اوھان کي چڱو انجام نه ڏنو ھو ڇا؟ پوءِ اوھان تي مُدو ڊگھو ٿي پيو ڇا يا پنھنجي پالڻھار جو ڏمر پاڻ تي نازل ٿيڻ گھريوَ؟ تنھنڪري منھنجي انجام جي ابتڙائي ڪيوَ.'),
(2435, 20, 87, 'چيائون ته پنھنجي وس کان تنھنجي انجام جي برخلافي نه ڪئي سون پر (فرعون جي) قوم جي زيورن مان بار کڻايا وياسون پوءِ اُھي (باھ ۾) وڌاسون پوءِ اھڙي طرح سامري وڌا.'),
(2436, 20, 88, 'پوءِ انھن لاءِ گابي جو بوتو ڪڍيائين جنھن کي رنڀ ھئي پوءِ چيائون ته ھيءُ اوھان جو معبود ۽ موسىٰ جو معبود آھي، ۽ (مُوسى) ڀلجي ويو.'),
(2437, 20, 89, 'نه ٿي ڏٺائون ڇا ته (اُھو) اُنھن کي ورندي ورائي نٿو ڏئي، ۽ کين نڪي ڏُک ۽ نڪي سُک ڏئي سگھي ٿو؟'),
(2438, 20, 90, '۽ بيشڪ ھارُون پھريون ئي کين چيو ھو ته اي منھنجي قوم اوھين اُن (گابي) سان پرکيا ويا آھيو، بيشڪ اوھان جو پالڻھار ٻاجھارو (الله) آھي تنھنڪري منھنجي تابعداري ڪريو ۽ منھنجو حُڪم مڃيو.'),
(2439, 20, 91, 'چيائون ته (اسين ايسين) مٿس مجاور رھنداسون جيسين مُوسىٰ اسان ڏانھن موٽي.'),
(2440, 20, 92, '(مُوسىٰ اچي) چيو ته اي ھارون توکي ڪنھن جھليو جڏھن کين گمراھ ٿيندو ڏٺئي.'),
(2441, 20, 93, 'ته منھنجي پٺيان نه لڳين، منھنجي حُڪم جي نافرماني ڪيئي ڇا؟'),
(2442, 20, 94, '(ھارون) چيو ته اي ادا نڪي مون کي ڏاڙھيءَ کان وٺ ۽ نڪي منھنجي مٿي کان، آءٌ (ھن ڳالھ کان) ڊنس ته متان چوين ته يعقوب جي اولاد جي وچ ۾ جدائي وڌءِ ۽ منھنجي چوڻ جو انتظار نه ڪيئي.'),
(2443, 20, 95, '(موسى) چيو ته اي سامري تنھنجو ڇا حال آھي.'),
(2444, 20, 96, 'چيائين ته اُھا (شيء) ڏٺم جا (ماڻھن) نه ڏٺي پوءِ اُن موڪيل جي پيرن جي (مٽيءَ جي) ھڪ مُٺ ڀريم پوءِ اُھا (گابي جي بوتي ۾) وڌم ۽ اھڙي طرح مُنھنجي نفس (اُھو) مون لاءِ سينگاريو.'),
(2445, 20, 97, '(موسى) چيو ته وڃ بيشڪ توکي حياتيءَ ۾ (ھيءَ سزا) آھي ته چوندو رھندين ته نه ڇُھو، ۽ تولاءِ بيشڪ (ٻيو به) انجام آھي جنھن جي توسان ابتڙائي نه ڪبي، ۽ پنھنجي معبود ڏانھن نھار جنھن تي تون مجاور ٿي ويٺو ھُئين، ته اُن کي ساڙينداسون وري اُن (جي ڪيريءَ) کي درياءَ ۾ کنڊائڻ وانگر کنڊائينداسين.'),
(2446, 20, 98, 'اوھان جو معبود الله ئي آھي انھيءَ کانسواءِ ڪو عبادت جو لائق ڪونھي، سڀڪا شيء سندس علم ۾ سمائي آھي.'),
(2447, 20, 99, '(اي پيغمبر) اھڙي طرح جيڪي گذري ويا تن جي خبرن مان توتي بيان ڪريون ٿا، ۽ پاڻ وٽان توکي نصيحت (يعني قرآن) ڏنوسون.'),
(2448, 20, 100, 'جيڪو کانئس مُنھن موڙيندو سو قيامت جي ڏينھن (گناھن جو) بار کڻندو.'),
(2449, 20, 101, 'منجھس سدائين رھڻ وارا آھن، ۽ قيامت جي ڏينھن انھن کي بار کڻڻ بڇڙو آھي.'),
(2450, 20, 102, 'جنھن ڏينھن صُور ۾ ڦوڪيو ويندو ۽ اُن ڏينھن ڏوھارين کي جھرو ڪري اُٿارينداسون.'),
(2451, 20, 103, '(تنھن ڏينھن) پاڻ ۾ سُ پُس ڪندا ته اوھين (دُنيا ۾) رڳو ڏھاڪو ڏينھن رھيؤ.'),
(2452, 20, 104, 'جيڪي چوندا سو اسين چڱو ڄاڻندڙ آھيون اُن مھل منجھائن پوري رستي وارو چوندو ته ھڪ ڏينھن کانسواءِ نه رھيا آھيو.'),
(2453, 20, 105, '۽ (اي پيغمبر) جبلن بابت توکان پُڇن ٿا چؤ ته منھنجو پالڻھار اُنھن کي اُڏائي کنڊائيندو.'),
(2454, 20, 106, 'پوءِ اُن ھنڌ کي صاف پٽ ڪري ڇڏيندو.'),
(2455, 20, 107, 'منجھس نڪي ھيٺاھين ۽ نڪي مٿاھين ڏسندين.'),
(2456, 20, 108, 'اُن ڏينھن سڏيندڙ جي پوئتان لڳندا جنھن کي ڪا ڏنگائي نه ھوندي، ۽ سڀ آواز الله لاءِ جھڪا ٿيندا پوءِ ڀُڻڪ کانسواءِ ڪُجھ نه ٻڌندين.'),
(2457, 20, 109, 'جنھن کي الله موڪل ڏني ۽ اُن جي چوڻ کان راضي ٿيو تنھن کانسواءِ اُن ڏينھن ڪا سفارش فائدو نه ڏيندي.'),
(2458, 20, 110, 'جيڪي سندن اڳيان ۽ جيڪي سندن پوئتان آھي سو (الله) ڄاڻندو آھي ۽ (ماڻھو) کيس علم ڪري نه ويڙھيندا.'),
(2459, 20, 111, '۽ سڀ مُنھن سدائين جيئري بيھڪ ڏيندڙ لاءِ عاجزي ڪندڙ آھن، ۽ جنھن ظلم (جو بار) کَنيو سو ڇيئي وارو ٿيو.'),
(2460, 20, 112, '۽ جيڪي چڱا ڪم ڪندو ۽ اُھو مؤمن (به) ھوندو سو نڪي ظلم کان ۽ نڪي نقصان کان ڊڄندو.'),
(2461, 20, 113, '۽ اھڙي طرح اِھو عربي قرآن لاٿوسون ۽ منجھس عذاب جو انجام طرحين طرحين بيان ڪيوسون ته مانَ اُھي ڊڄن يا (اُھو) اُنھن لاءِ نصيحت پيدا ڪري.'),
(2462, 20, 114, 'پوءِ الله سچو بادشاھ مٿاھون آھي، ۽ قرآن (جي پڙھڻ) ۾ اُن کان اڳ جو توڏانھن ان جو لھڻ پورو ڪجي تڪڙ نه ڪر، ۽ چؤ ته اي منھنجا پالڻھار منھنجو علم وڌاءَ.'),
(2463, 20, 115, '۽ بيشڪ آدم کي اڳ حُڪم ڪيوھوسون پوءِ وساريائين ۽ سندس ڪا پڪي نيت نه لڌي سون.'),
(2464, 20, 116, '۽ (ياد ڪر) جڏھن ملائڪن کي چيوسون ته آدم کي سجدو ڪريو پوءِ ابليس کانسواءِ سڀني سجدو ڪيو، ۽ اُن انڪار ڪيو.'),
(2465, 20, 117, 'پوءِ چيوسون ته اي آدم ھيءُ تنھنجو ۽ تنھنجي زال جو ويري آھي متان اوھان کي بھشت مان ڪڍي ته ڏکيا ٿيو.'),
(2466, 20, 118, 'بيشڪ تولاءِ (ھي آھي ته) منجھس نڪي بُک لڳندءِ نڪي اُگھاڙو ٿيندين.'),
(2467, 20, 119, '۽ تون منجھس نڪي اُڃو ٿيندين ۽ نڪي اُس لڳندءِ.'),
(2468, 20, 120, 'پوءِ کيس شيطان ڌوڪو ڏنو چيائين ته اي آدم سدائين رھڻ جي وڻ جو ۽ سدائين رھڻ واري بادشاھيءَ جو توکي ڏَس ڏيان؟'),
(2469, 20, 121, 'پوءِ منجھانئس ٻنھي کاڌو تنھنڪري انھن کي سندين اوگھڙ پڌري ٿي ۽ بھشت (جي وڻ) جا پن پاڻ تي ويڙھڻ لڳا، ۽ آدم پنھنجي پالڻھار کان نافرمان ٿيو تنھنڪري ڀُلو.'),
(2470, 20, 122, 'وري سندس پالڻھار کيس سڳورو ڪيو پوءِ مٿس ٻاجھ سان موٽيو ۽ (کيس) سڌي رستي لاتائين.'),
(2471, 20, 123, 'چيائين ته منجھانئس ٻئي گڏ لھي وڃو اوھان مان ڪي ڪن جا ويري ٿيندا، پوءِ جڏھن اوھان وٽ منھنجي ھدايت اچي، تڏھن جنھن منھنجي ھدايت جي تابعداري ڪئي سو گمراھ نه ٿيندو ۽ نڪي ڏکيو ٿيندو.'),
(2472, 20, 124, '۽ جيڪي منھنجي ياد ڪرڻ کان منھن موڙيندو تنھن لاءِ بيشڪ حياتي اھنجي ٿيندي ۽ قيامت جي ڏينھن کيس انڌو ڪري اٿارينداسون.'),
(2473, 20, 125, 'چوندو اي منھنجا پالڻھار مون کي انڌو ڪري ڇو اُٿاريئي ۽ بيشڪ (اڳي) سڄو ھوس.'),
(2474, 20, 126, '(الله) چوندو ته ائين آھي تو وٽ اسان جو آيتون آيون پوءِ کين وساريئي، ۽ اھڙي طرح اڄ وساربين.'),
(2475, 20, 127, '۽ اھڙي طرح انھي کي سزا ڏينداسون جيڪو حد کان لنگھيو ۽ پنھنجي پالڻھار جي نشانين کي نه مڃيائين، ۽ آخرت جو عذاب ڏاڍو سخت ۽ سدائين رھندڙ آھي.'),
(2476, 20, 128, '(ھن ڳالھ) کين سڌو رستو نه ڏيکاريو ڇا ته کانئن اڳ ڪيترائي جُڳ ناس ڪياسون جن جائن ۾ گھمندا آھن؟ بيشڪ اُن ۾ ڌيان وارن لاءِ نشانيون آھن.'),
(2477, 20, 129, '۽ (اي پيغمبر) جيڪڏھن تنھنجي پالڻھار جو حُڪم آڳاٽو نه ٿئي ھا ۽ (پڻ) مدت مقرر ٿيل نه ھجي ھا ته ضرور (عذاب) لازم ٿئي ھا.'),
(2478, 20, 130, 'پوءِ جيڪي چوندا آھن تنھن تي صبر ڪر ۽ سج اُڀرڻ کان اڳ ۽ ان جي لھڻ کان اڳ پنھنجي پالڻھار جي پاڪائي ساراھ سان گڏ بيان ڪر، ۽ رات جي (پھرين) گھڙين ۾ ۽ ڪجھ گھڙيون ڏينھن جون پاڪائي بيان ڪر ته مانَ تون راضي ٿئين.'),
(2479, 20, 131, '۽ (اي پيغمبر) تون پنھنجي اکين کي انھن ڏانھن نه کول جنھن سان منجھائن (ڪافرن جي) ٽولين کي دنيا جي حياتي جي سينگار سان ھن لاءِ آسودو ڪيوسون ته منجھس کين پرکيون، ۽ تنھنجي پالڻھار جو رزق ڀلو ۽ سدائين رھڻ وارو آھي.'),
(2480, 20, 132, '۽ پنھنجي گھر وارن کي نمازجو حڪم ڪر ۽ مٿس صبر ڪر (يعني قائم رھ) توکان رزق نه گھرندا آھيون، اسين توکي رزق ڏيندا آھيون، ۽ چڱي عاقبت پرھيزگارن لاءِ آھي.'),
(2481, 20, 133, '۽ چون ٿا ته اسان وٽ پنھنجي پالڻھار جي ڪا نشاني ڇونه آڻيندو آھي؟ جيڪي پھرين صحيفن ۾ آھي تنھن جي نشاني وٽن نه آئي آھي ڇا؟'),
(2482, 20, 134, '۽ جيڪڏھن اسين کين ھن (پيغمبر موڪلڻ) کان اڳ عذاب سان ناس ڪريون ھا ته ضرور چون ھا ته اي اسانجا پالڻھار اسان ڏانھن پيغمبر ڇونه موڪليئي ته پنھنجي ذليل ٿيڻ ۽ خوار ھجڻ کان اڳ تنھنجي آيتن جي تابعداري ڪريون ھا.'),
(2483, 20, 135, '(اي پيغمبر) چؤ ته سڀڪو منتظر آھي پوءِ (اوھين به) منتظر رھو، پوءِ سگھوئي ڄاڻندو ته سڌي واٽ وارا ڪير آھن ۽ ھدايت وارو ڪير ٿيو؟');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'ماڻھن لاءِ سندن حساب ويجھو ٿيو آھي ۽ اُھي غفلت ۾ (رھي کانئس) منھن موڙيندڙ آھن.'),
(2485, 21, 2, 'سندن پالڻھار وٽان ڪا نئين نصيحت وٽن نه ايندي آھي پر اُن کي (ٺـٺوليءَ وانگر راند) کيڏندي ٻڌندا آھن.'),
(2486, 21, 3, 'سندن دليون وائڙيون آھن، ۽ ظالم ڳجھيون صلاحون ڪندا آھن، ته ھي (پيغمبر) اوھان جھڙو ئي ماڻھو آھي، ۽ اوھين ڏسي وائسي جادوءَ کي ڇو ويجھا ويندا آھيو؟'),
(2487, 21, 4, '(پيغمبر) چيو ته منھنجو پالڻھار سڀ ڳالھ ڄاڻندڙ آھي (جا) آسمان ۽ زمين ۾ (جتي ھُجي)، ۽ اُھو ٻڌندڙ ڄاڻندڙ آھي.'),
(2488, 21, 5, 'بلڪ چيائون ته (اھو قرآن) اجايا خيالات آھن بلڪ اُن کي ٺاھ ٺاھيو اٿس بلڪ اُھو شاعر آھي، پوءِ جڳائي ته اسان وٽ ڪا نشاني آڻي جھڙي طرح پھريان (پيغمبر) موڪليا ويا ھوا.'),
(2489, 21, 6, '(ھائو) ڪوبه ڳوٺ جنھن کي اڳ ناس ڪيوسون تنھن (جي رھاڪن پنھنجن پيغمبرن تي) ايمان نه آندو ھو، پوءِ ھيءَ ايمان آڻيندا آھن ڇا؟'),
(2490, 21, 7, '۽ (اي پيغمبر) توکان اڳ رڳا مرد موڪلياسون جن ڏانھن وحي ڪيوھوسون (چ‍ؤ ته) جيڪڏھن نه ڄاڻندا آھيو ته ڪتاب وارن کان پڇو.'),
(2491, 21, 8, '۽ اُنھن (پيغمبرن) جو جُسو اھڙو نه بڻايوسون جو طعام نه کائين ۽ نه سدائين رھڻ وارا ھوا.'),
(2492, 21, 9, 'وري ساڻن (پنھنجي) انجام کي سچو ڪيوسون پوءِ اُنھن کي ۽ جنھن لاءِ گھريوسون تنھن کي بچايوسون ۽ حد کان لنگھندڙن کي ناس ڪيوسون.'),
(2493, 21, 10, 'بيشڪ اوھان ڏانھن ھڪ ڪتاب نازل ڪيوسون جنھن ۾ اوھان لاءِ نصيحت آھي، پوءِ ڇونه سمجھندا آھيو؟'),
(2494, 21, 11, '۽ ڪيترائي ڳوٺ جو اُھي ظالم ھوا ويران ڪياسون ۽ کانئن پوءِ ٻيو قومون پيدا ڪيون سون.'),
(2495, 21, 12, 'پوءِ جنھن دم اسان جو عذاب سھي ڪيائون (تنھن دم) اُمالڪ اُتاھون اُٿي ڀڳا ٿي.'),
(2496, 21, 13, '(چيوسون ته) نه ڀڄو ۽ جن (نعمتن) ۾ آسودا ھُيؤ اُنھن ڏانھن ۽ پنھنجن گھرن ڏانھن موٽو ته مانَ اوھان کان پڇيو وڃي.'),
(2497, 21, 14, 'چوندا رھيا ته اسان لاءِ خرابي آھي بيشڪ اسين ظالم ھئاسون.'),
(2498, 21, 15, 'پوءِ سدائين سندن اھو پڪارڻ ھو تانجو کين لڻيل (۽) وسانيل ڪيوسون.'),
(2499, 21, 16, '۽ آسمان ۽ زمين کي ۽ جيڪي سندن وچ ۾ آھي سا ڪا اسان راند نه بڻائي آھي.'),
(2500, 21, 17, 'جيڪڏھن اسان کي راند بڻائڻ (يعني ٻارن ٻچن) جو ڪو ارادو ھجي ھا ته ضرور اُنکي پاڻ وٽان وٺون ھا (يعني پاڻ جھڙا ڪريون ھا)، جيڪڏھن اسين ڪندڙ ھجون ھا (پر اسين ائين ڪندڙ نه آھيون).'),
(2501, 21, 18, 'بلڪ سچ کي ڪُوڙ تي اُڇليندا آھيون پوءِ اُھو سندن مٿو ڀڃندو آھي پوءِ اُنھيءَ مھل اُھو (ڪوڙ) چٽ ٿيندڙ آھي ۽ جيڪي (الله جون) وصفون اوھين بيان ڪندا آھيو تنھن سببان اوھان لاءِ خرابي آھي.'),
(2502, 21, 19, '۽ جيڪي آسمانن ۽ زمين ۾ آھي سو الله جو آھي، ۽ جيڪي وٽس آھن سي سندس عبادت کان وڏائي نه ڪندا آھن ۽ نڪي ٿڪبا آھن.'),
(2503, 21, 20, '(اُھي) رات جو ۽ ڏينھن جو پاڪائي بيان ڪندا آھن سُست نه ٿيندا آھن.'),
(2504, 21, 21, 'زمين (جي شين) مان اھڙا معبود ورتا اٿن ڇا جو اُھي (مئي کانپوءِ کين) کڙو ڪندا؟'),
(2505, 21, 22, 'جيڪڏھن آسمان ۽ زمين ۾ الله کانسواءِ (گھڻا) معبود ھجن ھا ته (آسمان ۽ زمين) ٻئي اُجڙن ھا، پوءِ ڪافر جيڪو بيان ڪندا آھن تنھن (ڳالھ) کان عرش جو پالڻھار پاڪ آھي.'),
(2506, 21, 23, '(الله) جيڪي ڪندو آھي تنھن بابت (اُھو) نه پڇبو ۽ اُنھن کان پڇا ڪبي.'),
(2507, 21, 24, 'اُن کانسواءِ (ٻيا) معبود ڇا ورتائون؟ (اي پيغمبر کين) چؤ ته پنھنجو دليل آڻيو، ھي (قرآن) منھنجي سنگت جي نصيحت آھي ۽ (ھي) مون کان اڳ وارن جي نصيحت آھي (يعني توريت ۽ انجيل انھن مان دليل ڏيو) بلڪ انھن مان گھڻا حق کي نه ڄاڻندا آھن اُن ڪري اُھي مُنھن موڙيندڙ آھن.'),
(2508, 21, 25, '۽ توکان اڳ جيڪو (به) پيغمبر موڪليوسون تنھن ڏانھن ھي ئي وحي ڪيوسون ته مون کانسواءِ ڪو عبادت جو لائق نه آھي تنھنڪري منھنجي عبادت ڪريو.'),
(2509, 21, 26, '۽ چون ٿا ته الله (پنھنجي لاءِ) اولاد ورتو آھي اُھو پاڪ آھي، بلڪ (ملائڪ الله جا) سڳورا ٻانھا آھن.'),
(2510, 21, 27, 'ڳالھائڻ ۾ اُن کان آڳاٽائي نه ڪندا آھن ۽ سندس حُڪم موجب ڪم ڪندا آھن.'),
(2511, 21, 28, 'جيڪي سندن اڳتان ۽ جيڪي سندن پوئتان آھي سو (الله) ڄاڻندو آھي ۽ (الله) جنھن کان راضي ٿئي تنھن کان سواءِ ٻئي لاءِ سفارش نه ڪندا آھن ۽ اُھي سندس ڀَوَ کان ڊڄندڙ آھن.'),
(2512, 21, 29, '۽ منجھائن جيڪو چوي ته آءٌ الله کان سواءِ معبود آھيان تنھن کي دوزخ جي سزا ڏينداسون، ظالمن کي اھڙي ئي سزا ڏيندا آھيون.'),
(2513, 21, 30, 'ڪافرن نه ڏٺو آھي ڇا ته آسمان ۽ زمين بند ٿيل ھوا؟ پوءِ انھن کي کوليوسون، ۽ سڀڪنھن جيئري شيء کي پاڻيءَ مان پيدا ڪيوسون، پوءِ ڇونه ايمان آڻيندا آھن؟'),
(2514, 21, 31, '۽ زمين ۾ جبل پيدا ڪياسون ته متان کين ڌوڏي ۽ منجھس ويڪريون واٽون بڻايوسون ته مانَ اُھي واٽ لھن.'),
(2515, 21, 32, '۽ آسمان کي مُحڪم ڇت بڻائي سون، ۽ اُھي اُن جي نشانين کان مُنھن موڙيندڙ آھن.'),
(2516, 21, 33, '۽ (الله) اُھو آھي جنھن رات ۽ ڏينھن ۽ سج ۽ چنڊ بڻايو، سڀڪو (تارو پنھنجي) آسمان ۾ ترندو آھي.'),
(2517, 21, 34, '۽ توکان اڳ ڪنھن ماڻھوءَ کي سدائين رھڻ وارو نه ڪيوسون، جيڪڏھن تون مُئين ته اُھي سدائين رھڻ وارا آھن.'),
(2518, 21, 35, 'سڀڪو جيءُ موت (جو مزو) چکڻ وارو آھي، ۽ اوھان کي مدائي ۽ چڱائي سان جاچڻ طرح پرکيندا آھيون، ۽ اسان ڏانھن موٽائبؤ.'),
(2519, 21, 36, '۽ جڏھن ڪافر توکي ڏسندا آھن تڏھن توکي رڳو ٺـٺولي ڪري وٺندا آھن، (۽ چوندا آھن) ته ھيءُ اُھو آھي ڇا جيڪو اوھان جي معبودن (جي عيبن) جو ذڪر ڪندو آھي، ۽ اُھي الله ياد ڪرڻ کان انڪار ڪندا آھن.'),
(2520, 21, 37, 'ماڻھو اُٻھر مان بڻايو ويو آھي، سگھو پنھنجون نشانيون اوھان کي ڏيکاريندس پوءِ مون کان عذاب جلد نه گھرو.'),
(2521, 21, 38, '۽ چون ٿا ته جيڪڏھن اوھين سچا آھيو ته اُھو انجام ڪڏھن ٿيندو؟'),
(2522, 21, 39, 'جيڪڏھن ڪافر اُن وقت کي ڄاڻن ھا جنھن مھل نڪي پنھنجن منھن ۽ نڪي پنھنجين پٺين کان باھ کي جھلي سگھندا ۽ نڪي کين مدد ڏبي (ته ائين نه چون ھا).'),
(2523, 21, 40, 'بلڪ (اھا گھڙي) وٽن اوچتو ايندي پوءِ کين حيران ڪندي پوءِ نڪي اُھا ٽاري سگھندا ۽ نڪي کين مُھلت ڏبي.'),
(2524, 21, 41, '۽ بيشڪ توکان اڳ پيغمبرن سان ٺـٺوليون ڪيون ويون پوءِ منجھائن جن ٺـٺوليون ڪيون تن تي اُھو عذاب اُلٽيو جنھن سان ٺـٺوليون ڪندا ھوا.'),
(2525, 21, 42, '(اي پيغمبر کين) چؤ ته رات ۽ ڏينھن ۾ ٻاجھاري (الله جي عذاب) کان ڪير اوھان جي نگھباني ڪندو آھي، بلڪ اُھي، پنھنجي پالڻھار جي ياد ڪرڻ کان منھن موڙيندا آھن.'),
(2526, 21, 43, 'اسان کانسواءِ (بچائڻ وارا) ڪي معبود آھن ڇا جو انھن کي بچائيندا؟ اُھي پاڻ کي به مدد ڏيئي نه سگھندا آھن ۽ نڪي اُھي (ھڪ ٻئي جي مدد سان) اسان (جي عذاب) کان بچندا.'),
(2527, 21, 44, 'بلڪ کين ۽ پيءُ ڏاڏن کي آسودو ڪيوسون تان جو سندن ڄمار ڊگھي ٿي، پوءِ نه ٿا ڏسن ڇا ته (ڪافرن جي) مُلڪ کي اُن جي چوڌاريون (مسلمانن جي غلبي سان) گھٽائيندا آھيون؟ پوءِ اُھي غالب ٿيندا ڇا؟'),
(2528, 21, 45, '(اي پيغمبر کين) چؤ ته آءٌ اوھان کي وحي سان ئي ڊيڄاريندو آھيان، ۽ جڏھن (ماڻھو) ڊيڄاريا ويندا آھن تڏھن ٻوڙو (ڊيڄارڻ جو) سڏ نه ٻڌندو آھي.'),
(2529, 21, 46, '۽ جيڪڏھن تنھنجي پالڻھار جي عذاب مان ٿوروئي کين پھچي ته ضرور چون ته ھيء ارمان اسين ظالم ھواسون.'),
(2530, 21, 47, '۽ قيامت جي ڏينھن عدل جي ترازي رکندسون پوءِ ڪنھن جيءُ تي ڪجھ ظلم نه ڪبو، ۽ جيڪڏھن عمل اَھر جي داڻي جي تور ھوندو ته به اُن کي آڻينداسون، ۽ اسين حساب ڪندڙ بس آھيون.'),
(2531, 21, 48, '۽ بيشڪ مُوسىٰ ۽ ھارون کي نبيريندڙ ڪتاب ۽ انھن پرھيزگارن لاءِ سوجھرو ۽ نصيحت ڏني سون.'),
(2532, 21, 49, 'جيڪي پنھنجي پالڻھار کان پرپٺ ڊڄندا آھن ۽ اُھي قيامت کان (پڻ) ڊڄندڙ آھن.'),
(2533, 21, 50, '۽ ھي (قرآن) ھڪ برڪت واري نصيحت آھي جنھن کي نازل ڪيوسون، پوءِ به اوھين ان جا منڪر ٿيندا آھيو ڇا؟'),
(2534, 21, 51, '۽ بيشڪ ابراھيم کي اڳي سندس ھدايت ڏني سون ۽ کيس ڄاڻندڙ ھواسين.'),
(2535, 21, 52, 'جڏھن پنھنجي پيءُ ۽ پنھنجي قوم کي چيائين ته ھي ڪھڙيون مورتون آھن جن تي اوھين ويھي عبادت ٿا ڪريو.'),
(2536, 21, 53, 'چيائين ته پنھنجا ابا ڏاڏا اُنھن جا پوڄيندڙ ڏٺاسون.'),
(2537, 21, 54, '(ابراھيم) چيو ته بيشڪ اوھين پاڻ ۽ اوھان جا ابا ڏاڏا پڌريءَ گمراھيءَ ۾ آھيو.'),
(2538, 21, 55, 'چيائون اسان وٽ سچ آندو اٿئي يا تون راند ڪندڙن مان آھين؟'),
(2539, 21, 56, '(ابراھيم) چيو ته بلڪ اوھان جو پالڻھار اُھوئي آسمانن ۽ زمين جو پالڻھار آھي جنھن اُھي بڻايا آھن، ۽ آءٌ اُن (ڳالھ) تي شاھدي ڏيندڙن مان آھيان.'),
(2540, 21, 57, '۽ الله جو قسم آھي ته اوھان جي پٺيري ٿي ڦرڻ کانپوءِ اوھان جي بتن جو ضرور ڪو حيلو ڪندس.'),
(2541, 21, 58, 'پوءِ اُنھن کي سندن وڏي کانسواءِ ٽُڪر ٽُڪر ڪيائين ته مانَ اھي ڏانھس موٽن.'),
(2542, 21, 59, 'چيائون ته اسان جي بُتن سان ڪنھن ھھڙو حال ڪيو؟ بيشڪ اُھو ظالمن مان آھي.'),
(2543, 21, 60, 'چيائون ته ھڪ جوان کان ٻڌوسون ته سندين گلا ٿي ڪيائين اُن کي ابراھيم سڏيندا آھن.'),
(2544, 21, 61, 'چيائون ته کيس ماڻھن جي اکين اڳيان آڻيو ته مانَ اُھي گواھي ڏين.'),
(2545, 21, 62, '(جڏھن آندائون تڏھن) چيائون ته اي ابراھيم تو اسان جي بُتن جو ھي حال ڪيو آھي ڇا؟'),
(2546, 21, 63, '(ابراھيم) چيو ته (نه)! بلڪ اھو سندن ھن وڏي ڪيو آھي جيڪڏھن ڳالھائن ٿا ته کانئن پڇو.'),
(2547, 21, 64, 'پوءِ پاڻ ڏانھن (سوچيندڙ ٿي) موٽيا پوءِ (ھڪ ٻئي کي) چيائون ته بيشڪ اوھين پاڻ بي انصاف آھيو.'),
(2548, 21, 65, 'پوءِ پنھنجا ڪنڌ اُونڌا ڪيائون، (چيائون) ته بيشڪ ڄاڻندو آھين ته ھي نه ڳالھائيندا آھن.'),
(2549, 21, 66, '(ابراھيم) چيو ته الله کانسواءِ انھن کي پوڄيندا آھيو ڇا جيڪي اوھان کي نڪي نفعو پھچائيندا آھن ۽ نڪي اوھان کي نقصان پھچائيندا آھن.'),
(2550, 21, 67, 'اوھان کي ۽ جنھن کي الله کانسواءِ پوڄيندا آھيو تنھن کي ڌِڪار ھُجي، (اوھين) نه ڄاڻندا آھيو ڇا؟'),
(2551, 21, 68, 'چيائون ته اُن کي ساڙيو ۽ جيڪڏھن اوھين ڪي ڪرڻ گھرو ٿا ته پنھنجن بُتن جو ڀَلو وٺو.'),
(2552, 21, 69, '(پوءِ باھ ۾ وڌائونس) چيوسون ته اي باھ ابراھيم تي ٿڌي ۽ سلامتي واري ٿي پؤ.'),
(2553, 21, 70, '۽ کيس ايذائڻ گھريائون تنھنڪري کين بلڪل ڇيئي وارو ڪيوسون.'),
(2554, 21, 71, '۽ کيس ۽ لوط کي اھڙي زمين ڏانھن بچائي آندوسون جنھن ۾ جھان وارن لاءِ برڪت ڪئي سون.'),
(2555, 21, 72, '۽ ابراھيم کي اسحاق بخشيوسون، ۽ يعقوب (جو سندس پوٽو ھو) وڌيڪ (بخشيوسون)، ۽ سڀني کي صالح ڪيوسون.'),
(2556, 21, 73, '۽ اُھي اھڙا امام ڪياسون جو اسان جي حُڪم موجب سڌو رستو ڏيکاريندا ھوا ۽ انھن ڏانھن چڱن ڪمن ڪرڻ ۽ نماز پڙھڻ ۽ زڪوٰة ڏيڻ جو حُڪم ڪيوسون، ۽ اسان جي عبادت ڪندڙ ھوا.'),
(2557, 21, 74, '۽ لُوط کي حڪمت ۽ علم ڏنوسون ۽ کيس اھڙي ڳوٺ وارن کان بچايوسون جو مدا ڪم ڪندا ھوا، ڇوته اُھي بڇڙي نافرمان قوم ھوا.'),
(2558, 21, 75, '۽ کيس پنھنجي ٻاجھ ھيٺ داخل ڪيوسون، بيشڪ اُھو صالحن مان ھو.'),
(2559, 21, 76, '۽ (اي پيغمبر) نوح (جي قصي) کي (ياد ڪر) جڏھن (اُنھن پيغمبرن کان) اڳ دُعا گھريائين تڏھن سندس دُعا قبول ڪئي سون پوءِ کيس ۽ سندس گھر وارن کي وڏي اوکائيءَ کان بچايو سون.'),
(2560, 21, 77, '۽ کيس اھڙي قوم تي سوڀارو ڪيوسون جنھن اسان جي آيتن کي ڪوڙ ڀانيو، بيشڪ اُھي بڇڙي قوم ھوا تنھنڪري انھن مڙني کي ٻوڙيوسون.'),
(2561, 21, 78, '۽ دائود ۽ سليمان (جي قصي) کي (ياد ڪر) جڏھن پوک بابت فيصلو ڪيائون جو ھڪ قوم جي ڌڻ رات جو منجھس ڀيل ڪئي ھئي، ۽ سندن حُڪم ڪرڻ وقت حاضر ھواسون.'),
(2562, 21, 79, 'تڏھن اُھو سليمان کي سمجھايوسون، ۽ ھر ھڪ کي حڪمت ۽ علم ڏنوسون، ۽ دائود کي جبل ۽ پکي تابع ڪري ڏناسون جو (ساڻس) تسبيح چوندا ھوا، ۽ (اسين ايئن) ڪرڻ وارا ھواسون.'),
(2563, 21, 80, '۽ دائود کي اوھان لاءِ زرھ بڻائڻ ھن لاءِ سيکاري سون ته اوھان کي اوھان جي لڙائي (جي نقصان) کان بچائي، پوءِ اوھين ڪي شڪر ڪندڙ آھيو ڇا؟'),
(2564, 21, 81, '۽ سُليمان کي تِکو واءُ (تابع ڪري ڏنوسون) جو سندس حُڪم سان اھڙي زمين ڏانھن ھلندو ھو جنھن ۾ برڪت رکي سون، ۽ سڀڪنھن شيء کي ڄاڻندڙ آھيون.'),
(2565, 21, 82, '۽ ديوَن مان ڪي اھڙا (تابع ڪري ڏناسون) جي اُن لاءِ ٽٻندا ھوا ۽ اُن کانسواءِ ٻيو ڪم (به) ڪندا ھوا، ۽ سندن نگھبان ھواسون.'),
(2566, 21, 83, '۽ (اي پيغمبر) ايوب (جي قصي) کي (ياد ڪر) جڏھن پنھنجي پالڻھار کي سڏيائين ته مون کي ڏُک پھتو آھي ۽ تون (سڀني) کان تمام ٻاجھارو آھين.'),
(2567, 21, 84, 'پوءِ سندس دُعا قبول ڪئي سون پوءِ جيڪو کيس ڏک پھتو ھو سو لاٿوسون ۽ سندس گھر وارا کيس (ورائي) ڏناسون ۽ اُنھن جھڙا ساڻن گڏ پنھنجي ٻاجھ وٽان (ڏناسون) ۽ (ته) عبادت ڪندڙن لاءِ يادگيري ھجي.'),
(2568, 21, 85, '۽ اسماعيل ۽ ادريس ۽ ذوالڪفل کي (ياد ڪر)، ھر ھڪ صابرن مان ھو.'),
(2569, 21, 86, '۽ کين پنھنجي ٻاجھ ۾ داخل ڪيوسون، بيشڪ اُھي صالحن مان ھوا.'),
(2570, 21, 87, '۽ مڇي واري (جي قصي) کي (ياد ڪر) جڏھن ھو (پنھنجي قوم) تي ڪاوڙجي ويو پوءِ (ذوالنون ائين) ڀانيو ته اسان جي مٿس ڪڏھن پڪڙ نه ھلندي پوءِ اونداھين ۾ دانھون ڪيائين ته توکان سواءِ ڪو معبود نه آھي تون پاڪ آھين، آءٌ گنھگارن مان آھيان.'),
(2571, 21, 88, 'پوءِ سندس دُعا قبول ڪئي سون ۽ کيس غم کان بچايوسون، ۽ اھڙي طرح مؤمنن کي بچائيندا آھيون.'),
(2572, 21, 89, '۽ زڪريا (جو قصّو ياد ڪر) جڏھن پنھنجي پالڻھار کي سڏيائين ته اي منھنجا پالڻھار مون کي ھيڪلو نه ڇڏ ۽ تون چڱو وارث آھين.'),
(2573, 21, 90, 'پوءِ سندس دُعا قبول ڪئي سون ۽ کيس يحيىٰ بخشيوسون ۽ سندس زال کي سندس لاءِ چڱو ڪيوسون، اُھي چڱاين ۾ آڳاٽائي ڪندا ھوا ۽ اسان کي اُميد ۽ ڀؤ ڪري سڏيندا ھوا، ۽ اسان کان ڊڄندڙ ھوا.'),
(2574, 21, 91, '۽ (انھيءَ زال جوقصّو ياد ڪر) جنھن پنھنجي اُگھڙ بچائي پوءِ منجھس پنھنجو روح ڦوڪيوسون ۽ کيس ۽ سندس پٽ کي ساري جڳ لاءِ نشاني ڪئي سون.'),
(2575, 21, 92, 'بيشڪ اِھو اوھان جو دين ھڪ دين آھي، ۽ آءٌ اوھان جو پالڻھار آھيان تنھنڪري منھنجي عبادت ڪريو.'),
(2576, 21, 93, '۽ پنھنجي (دين جي) ڪمن کي پاڻ ۾ ٽُڪر ٽُڪر ڪيائون، سڀئي مون ڏانھن موٽندڙ آھن.'),
(2577, 21, 94, 'پوءِ جيڪو چڱا ڪم ڪندو ۽ اُھو مؤمن به ھوندو تنھنجي ڪوشش جي بي قدري نه ڪبي، ۽ اسين اُن لاءِ لکندڙ آھيون.'),
(2578, 21, 95, '۽ جنھن ڳوٺ کي ناس ڪيوسون تنھن تي حرام ٿيل آھي ته وري موٽي ايندا اُھي.'),
(2579, 21, 96, 'تان جو جڏھن يَاجُوج ۽ مَاجُوج کي کولبو ۽ اُھي سڀڪنھن دڙي کان ڊوڙندا.'),
(2580, 21, 97, '۽ (جڏھن) سچو وعدو پھچندو تڏھن اوچتو ئي ڪافرن جو اکيون ٽڙي وينديون، (چوندا ته) ھَيءِ اَرمان بيشڪ ھن (حال) کان بي خبريءَ ۾ ھواسون بلڪ اسين ئي ظالم ھواسون.'),
(2581, 21, 98, 'اوھين ۽ جن کي الله کانسواءِ پوڄيندا آھيو سي دوزخ جو ٻارڻ آھيو، اُن ۾ اوھين گھڙندڙ آھيو.'),
(2582, 21, 99, 'جيڪڏھن اُھي (بُت) معبود ھجن ھا ته اُن (دوزخ) ۾ نه گھڙن ھا، ۽ مڙيئي منجھس سدائين رھڻ وارا آھن.'),
(2583, 21, 100, 'منجھس انھن کي رڙيون ھونديون ۽ اُھي منجھس (ڪي) نه ٻڌندا.'),
(2584, 21, 101, 'جن لاءِ اڳي ئي اسان وٽ چڱائي (لکيل) آھي اُھي اُن (دوزخ) کان پري ڪبا.'),
(2585, 21, 102, '(اُھي) اُن جي ڀڻڪ نه ٻڌندا، ۽ جنھن (عيش) ۾ سندن جيءُ (رھڻ) گھرندو تنھن ۾ اُھي سدائين رھندا.'),
(2586, 21, 103, 'کين تمام وڏو خوف ملول نه ڪندو ۽ کين مَلائڪ گڏبا، (۽ چوندن ته) ھيءُ اُھو ڏينھن آھي جنھن جو اوھان کي وعدو ڏنو ويندو ھو.'),
(2587, 21, 104, 'جنھن ڏينھن آسمان کي ڪاغذ جي ويڙھيءَ وانگي ويڙھينداسون، جئن پھريون بڻايوسون (تئن) ان کي وري بڻائينداسون، اھو انجام اسان تي لازم آھي، اسين ڪرڻ وارا آھيون.'),
(2588, 21, 105, '۽ بيشڪ توريت کان پوءِ زبور ۾ لکيوسون ته منھنجا صالح ٻانھان زمين جا وارث ٿيندا.'),
(2589, 21, 106, 'ھن (قِصّي) ۾ عبادت ڪندڙ قوم لاءِ (مطلب جي) پورائي آھي.'),
(2590, 21, 107, '۽ (اي پيغمبر) توکي خاص جھانن جي رحمت لاءِ موڪليوسون.'),
(2591, 21, 108, 'چ‍ؤ ته مون وٽ ھن حُڪم کانسواءِ وحي نه ٿيندو آھي ته ھڪ الله کانسواءِ اوھان جو ڪو معبود نه آھي، پوءِ اوھين ڇونه حُڪم مڃيندڙ آھيو؟'),
(2592, 21, 109, 'پوءِ جيڪڏھن ڦرن ته چؤ ته اوھان سڀني کي (الله جي حڪم تي) ھڪ جھڙو خبردار ڪيم، ۽ جيڪو اوھان کي انجام ڏنو ويو سو نه ٿو ڄاڻان ته ويجھو آھي يا پري آھي.'),
(2593, 21, 110, 'بيشڪ الله ظاھري ڳالھ (به) ڄاڻندو آھي ۽ جيڪي اوھين لڪائيندا آھيو سو (به) ڄاڻندو آھي.'),
(2594, 21, 111, '۽ آءٌ نه ڄاڻندو آھيان ته متان اُھا ٺھرايل (دير) اوھان لاءِ پرک ھجي ۽ ھڪ وقت تائين (اوھان لاءِ) نفعو ھُجي.'),
(2595, 21, 112, '(پيغمبر) چيو ته اي منھنجا پالڻھار سچو فيصلو ڪر، ۽ اسان جو پالڻھار ٻاجھارو آھي جيڪي اوھين بيان ڪندو آھي تنھن تي کانئس مدد گھري ويندي آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'اي ماڻھؤ پنھنجي پالڻھار کان ڊڄو، ڇوته قيامت وارو ڌڪاءُ وڏي شيء آھي.'),
(2597, 22, 2, 'جنھن ڏينھن اُن کي ڏسندؤ (تنھن ڏينھن) سڀڪا کير پياريندڙ جنھن کي کير پياريندي ھوندي تنھن کي (دھشت کان) وساريندي ۽ سڀڪا ڳڀ واري پنھنجي ڳڀ کي ڪيريندي ۽ ماڻھن کي بيھوش ڏسندين حالانڪ اُھي بيھوش نه ھوندا پر الله جو عذاب سخت آھي.'),
(2598, 22, 3, '۽ ماڻھن مان ڪو اھڙو آھي جو الله جي شان ۾ بي سمجھيءَ سان تڪرار ڪندو آھي ۽ سڀڪنھن شيطان تڙيل جي پٺيان لڳندو آھي.'),
(2599, 22, 4, 'اُن (شيطان) تي لازم ڪيو ويو آھي ته جيڪو کيس دوست رکندو تنھن کي بيشڪ اُھو گمراھ ڪندو ۽ ان کي دوزخ جي عذاب ڏانھن سڌو رستو ڏيکاريندو.'),
(2600, 22, 5, 'اي ماڻھؤ جيڪڏھن وري جيئري ٿئڻ کان اوھين شڪ ۾ پيل آھيو ته اسان اوھان کي مٽيءَ مان بڻايو وري نُطفي مان وري رت جي دڳ مان وري گوشت جي ٻوٽيءَ مان پورو بڻايل ۽ اڌ گابرو بڻايل ھن لاءِ ته اوھان کي کولي ٻڌايون، ۽ جنھن کي گھرندا آھيون تنھن کي مُقرر مُدّت تائين ڳڀيرڻين ۾ ٺھرائيندا آھيون وري ٻار بڻائي اوھان کي ٻاھر ڪڍندا آھيون وري نپائيندا آھيون ته اوھين پنھنجي جواني کي پھچو، ۽ اوھان مان ڪو اڳي مرندو آھي ۽ اوھان مان ڪنھن کي (جھورائي جي) نڪميءَ ڄمار ڏانھن ھن لاءِ ورايو ويندو آھي ته ڄاڻپ کانپوءِ ڪجھ نه ڄاڻي، ۽ زمين کي غير آباد ڏسندو آھين پوءِ جڏھن مٿس پاڻي وسائيندا آھيون تڏھن تازي ٿيندي ۽ اُڀامندي آھي ۽ سڀڪنھن جنس مان چڱا اوڀڙ ڄمائيندي آھي.'),
(2601, 22, 6, 'اِھو (بيان) ھن لاءِ آھي جو الله (جو ئي ھجڻ) حق آھي جو اُھو مُئن کي جياريندو آھي ۽ اُھو سڀڪنھن شيء تي وس وارو آھي.'),
(2602, 22, 7, '۽ ته قيامت اچڻي آھي منجھس ڪو شڪ نه آھي ۽ ته جيڪي قبرن ۾ ھوندا تن کي الله اُٿاريندو.'),
(2603, 22, 8, '۽ ماڻھن مان ڪو اھڙو آھي جو الله جي شان ۽ ڄاڻپ ۽ ھدايت ۽ پڌري ڪتاب ڌاران تڪرار ڪندو آھي.'),
(2604, 22, 9, '(وڏائيءَ کان) ڪنڌ موڙيندڙ ٿي ھن لاءِ ته (ماڻھن کي) الله جي واٽ کان گمراھ ڪري، ان لاءِ دنيا ۾ خواري آھي ۽ قيامت جي ڏينھن کيس سڙڻ جو عذاب چکائينداسون.'),
(2605, 22, 10, '(چئبس ته) اھا سزا انھن ڪمن جي ڪري آھي جو تنھنجي ھٿن اڳي موڪليا آھن ۽ (انھيءَ ڪري) ته الله ٻانھن تي ظلم ڪندڙ نه آھي.'),
(2606, 22, 11, '۽ ماڻھن مان ڪو اھڙو آھي جو الله جي عبادت آسيري پاسيري ڪندو آھي، پوءِ جيڪڏھن کيس ڪو نفعو پھچندو آھي ته اُن (عبادت) سان آرامي ٿيندو آھي، ۽ جيڪڏھن کيس ڪو ڏک پھچندو آھي ته پنھنجي مُنھن ڀر ڦرندو آھي، دُنيا ۽ آخرت گنوايائين، اھوئي پڌرو ڇيئو آھي.'),
(2607, 22, 12, '(اُھو) الله کانسواءِ اُنھي کي سڏيندو آھي جيڪو کيس نڪي نقصان لائيندو آھي ۽ نڪي کيس نفعو ڏيندو آھي، اِھا ئي وڏي گمراھي آھي.'),
(2608, 22, 13, 'انھيءَ کي سڏيندو آھي جنھن جو نقصان سندس نفعي کان وڌيڪ ويجھو آھي، بيشڪ (اھڙو) سائين بڇڙو آھي ۽ (اھڙو) ھمراھ (به) بڇڙو آھي.'),
(2609, 22, 14, 'بيشڪ جن ايمان آندو ۽ چڱا عمل ڪيا تن کي الله اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن، بيشڪ الله جيڪي گھرندو آھي سو ڪندو آھي.'),
(2610, 22, 15, 'جيڪو ڀائيندو آھي ته الله پيغمبر کي دنيا ۽ آخرت ۾ ڪڏھن مدد نه ڪندو تنھن کي گھرجي ته آسمان ڏانھن رسيءَ سان چڙھي وري گھرجيس ته ڇني پوءِ نھاري ته اُنھي جي حيلي اُن کي وڃايو (يا نه) جنھن اُن کي ڪاوڙايو.'),
(2611, 22, 16, '۽ اھڙي طرح اُن (قرآن) کي پڌريون آيتون ڪري نازل ڪيوسون ۽ الله جنھن کي گھرندو آھي تنھن کي ھدايت ڪندو آھي.'),
(2612, 22, 17, 'بيشڪ جن ايمان آندو ۽ جيڪي يھودي ۽ صابي ۽ نصارىٰ ۽ مجوسي ۽ مشرڪ آھن تن (سڀني) جي وچ ۾ الله قيامت جي ڏينھن نبيرو ڪندو، ڇوته الله سڀڪنھن شيء تي حاضر آھي.'),
(2613, 22, 18, '(اي پيغمبر) نه ڏٺئي ڇا ته جيڪي آسمانن ۾ آھن ۽ جيڪي زمين ۾ آھن سي ۽ سج ۽ چنڊ ۽ تارا ۽ جبل ۽ وڻ ۽ ڍور ۽ ماڻھن مان گھڻا الله کي سجدو ڪندا آھن، ۽ گھڻا (ماڻھو) آھن جن تي عذاب لازم ٿيو آھي، ۽ جنھن کي الله خوار ڪري تنھن کي ڪو مانُ ڏيڻ وارو ڪونھي، ڇو ته الله جيڪي گھرندو آھي سو ڪندو آھي.'),
(2614, 22, 19, 'اِھي ٻئي ٽوليون پاڻ ۾ ويري آھن پنھنجي پالڻھار بابت تڪرار ڪندڙ آھن، پوءِ جن ڪفر ڪيو تن لاءِ باھ جا ڪپڙا ورڇيا ويندا، (۽) سندن مٿن جي مٿان ٽھڪندڙ پاڻي ھاربو.'),
(2615, 22, 20, 'جيڪي سندن پيٽن ۾ ھوندو سو ۽ کَلون اُن سان ڳاربيون.'),
(2616, 22, 21, '۽ اُنھن جي ڪُٽڻ لاءِ لوھ جا وڏاڻ ھوندا.'),
(2617, 22, 22, 'جڏھن ڪڏھن منجھانئس ڏک جي ڪري نڪرڻ گھُرندا تڏھن منجھس ورائبا، ۽ (چيو ويندن ته) سڙڻ جو عذاب چکو.'),
(2618, 22, 23, 'بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي الله اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون پيون وھنديون منجھن سون جا زيور ۽ موتي پارائبا، ۽ منجھن سندين پوشاڪ پَٽَ جي ھوندي.'),
(2619, 22, 24, '۽ (دُنيا ۾) کين چڱي ڳالھ ڏانھن رستو ڏيکاريو ويو ھو، ۽ الله ساراھيل جي واٽ ڏانھن رستي لاتا ويا ھوا.'),
(2620, 22, 25, 'بيشڪ جن ڪفر ڪيو ۽ (ماڻھن کي) الله جي واٽ ۽ اُنھي تعظيم واريءَ مسجد کان جھليندا آھن جا ماڻھن لاءِ بڻائي سون جنھن ۾ شھري ۽ ٻھراڙيءَ وارا ھڪ جھڙا آھن، ۽ جيڪو منجھس ظلم سان زيادتيءَ جو ارادو ڪندو تنھن کي ڏکوئيندڙ عذاب چکائينداسون.'),
(2621, 22, 26, '۽ (ياد ڪر) جڏھن ابراھيم لاءِ بَيۡتُ الله جو ھنڌ مقرّر ڪيوسون، (۽ سمجھايوسون) ته مون سان ڪنھن کي شريڪ نه ڪر ۽ منھنجي گھر کي طواف ڪندڙن ۽ بيھندڙن ۽ رڪوع ڪندڙن (۽) سجدي ڪندڙن لاءِ پاڪ رک.'),
(2622, 22, 27, '۽ ماڻھن ۾ حج جو پڙھو ڏي ته توڏانھن پيادا ۽ ڏٻرن اُٺن تي چڙھي ايندا جو ڏورين واٽن کان (ھِن لاءِ) ايندا.'),
(2623, 22, 28, 'ته پنھنجي فائدي جي ھنڌ تي پھچن ۽ ڍورن مان جيڪا روزي کين الله ڏني تنھن تي ڄاڻايلن ڏينھن ۾ (ذبح مھل) الله جو نالو ياد ڪن، پوءِ انھن منجھان (پاڻ به) کائو ۽ عاجز فقير کي به کارايو.'),
(2624, 22, 29, 'وري ڀلي ته پنھنجي (جسي جي) مَرُّ لاھين ۽ پنھنجيون مڃتائون پوريون ڪن ۽ ھن قديم گھر جو طواف ڪن.'),
(2625, 22, 30, 'اِھو (حُڪم) آھي، ۽ جيڪو الله جي عبادت جي رڪنن جو ادب ڪندو تنھن لاءِ اُھو سندن پالڻھار وٽ ڀلو آھي، ۽ جيڪي اوھان کي پڙھي ٻڌائبو تنھن کانسواءِ ٻيا ڍور اوھان تي حلال ڪيا ويا تنھنڪري بُتن جي پليتيءَ کان پاسو ڪريو ۽ ڪوڙي ڳالھ کان (به) پاسو ڪريو.'),
(2626, 22, 31, 'الله ڏانھن ھڪ طرفا ٿي (۽) ساڻس شريڪ مُقرّر ڪندڙ نه ٿي (پاسو ڪريو)، ۽ جيڪو الله سان شريڪ مقرّر ڪندو سو آسمان کان ڪِريو جنھن کي (مردار خور) پکي کڻي ويا يا کيس واءُ (اُڏائي) پري ھنڌ اُڇليو.'),
(2627, 22, 32, 'اِھو (حُڪم) آھي، ۽ جيڪو الله جي (عبادت جي) نشانين جو اَدب ڪري (۽ بُتن ۽ ڪوڙ کان پاڻ پَلي سو پرھيزگار آھي) ڇوته اِھو (ڪم) دلين جي پرھيزگاريءَ سان آھي.'),
(2628, 22, 33, 'اوھان لاءِ انھن (ڍورن) ۾ گھڻا نفعا مقرّر مُدت تائين آھن وري قديم گھر ڏانھن (ڪسڻ لاءِ) پھچڻ جو ھنڌ اٿن.'),
(2629, 22, 34, '۽ سڀڪنھن ٽوليءَ لاءِ قرباني جي طرح ھن لاءِ مقرر ڪئي سون ته کين ڍورن مان جيڪي روزي الله ڏني تنھن تي الله جو نالو ياد ڪن، پوءِ اوھان جو معبود ھڪ خُدا آھي تنھنڪري سندس حُڪم مڃيو، ۽ انھن عاجزي ڪندڙن کي خوشخبري ڏي.'),
(2630, 22, 35, 'جن جون دليون جڏھن الله کي ياد ڪبو آھي (تڏھن) ٿَڙڪنديون آھن ۽ (پڻ انھن ماڻھن کي جو) جيڪي مٿانِ آيو تنھن تي صبر ڪندڙ آھن ۽ نماز پڙھندڙ آھن ۽ کين جيڪا روزي ڏني سون تنھن مان خرچيندا آھن.'),
(2631, 22, 36, '۽ قربانيءَ وارن اُٺن کي اوھان لاءِ الله جي عبادت جي نشانين مان ڪيوسون منجھس اوھان لاءِ ڀلائي آھي، پوءِ قطاري مٿن الله جو نالو ياد ڪريو (يعني ذبح ڪريو)، پوءِ جڏھن اُھي پاسي ڀر ليٽن تڏھن منجھائن کائو ۽ (فقيرن) نه سوال ڪندڙ ۽ سوال ڪندڙ کي کارايو، اھڙي طرح اوھان کي اُھي تابع ڪري ڏناسون ته مانَ اوھين شڪرانو ڪريو.'),
(2632, 22, 37, 'نڪي اُنھن (قربانين) جو گوشت ۽ نڪي اُنھن جو رتُ الله کي پھچندو آھي پر اوھان جي پرھيزگاري الله کي پھچندي آھي، اھڙي طرح ھو اوھان کي تابع ڪري ڏنائين ته اوھين الله جي وڏائي (ھن شڪراني سببان) بيان ڪريو جو اوھان کي ھدايت ڪيائين، ۽ ڀلارن کي خوشخبري ڏي.'),
(2633, 22, 38, 'بيشڪ الله مؤمنن کان (سندن ويري) ٽاريندو آھي، ڇوته الله سڀڪنھن خيانت ڪندڙ بي شڪر کي دوست نه رکندو آھي.'),
(2634, 22, 39, 'جن سان (ڪافر) وڙھندا آھن تن کي (جھاد لاءِ) انھيءَ ڪري موڪل ڏني وئي جو انھن تي ظلم ڪيو ويو آھي، ۽ بيشڪ الله کين مدد ڏيڻ تي وس وارو آھي.'),
(2635, 22, 40, 'جن کي پنھنجن ديسن مان ناحق لوڌيو ويو تن (ھن) چوڻ کانسواءِ (ٻيو) نه چيو ته اسانجو پالڻھار الله آھي، ۽ جيڪڏھن ڪن ماڻھن سان ڪن کي الله نه ٽاري ھا ته (جيڪر) خانقاھون ۽ گرجائون ۽ ڪليسا ۽ مسجدون جن ۾ الله جو نالو گھڻو ياد ڪبو آھي سي ضرور ڊاٺيون وڃن ھا، ۽ جيڪو الله (جي دين) جي مدد ڪندو آھي تنھنکي الله ضرور مدد ڏيندو آھي، ڇو ته الله ضرور ڏاڍو ۽ غالب آھي.'),
(2636, 22, 41, 'انھن ماڻھن کي جو جيڪڏھن کين زمين ۾ غلبو ڏينداسون ته نماز پڙھندا ۽ زڪوٰة ڏيندا ۽ چڱن ڪمن جو حُڪم ڪندا ۽ مدن ڪمن کان منع ڪندا، ۽ سڀني ڪمن جي پڇاڙي الله لاءِ آھي.'),
(2637, 22, 42, '۽ (اي پيغمبر) جيڪڏھن توکي ڪوڙو ڀانئين ٿا ته بيشڪ کائن اڳ نوح جي قوم ۽ عادين ثمودين (پيغمبرن کي) ڪوڙ ڄاتو ھو.'),
(2638, 22, 43, '۽ ابراھيم جي قوم ۽ لُوط جي قوم (به).'),
(2639, 22, 44, '۽ مَدۡيَن وارن (به پنھنجي پيغمبرن کي ڪُوڙو ڄاتو)، ۽ مُوسىٰ کي (به) ڪوڙو ڄاتو ويو پوءِ انھن ڪافرن کي ڍر ڏنم وري کين پڪڙيم، پوءِ منھنجي سزا ڪھڙي (نه سخت) ھُئي.'),
(2640, 22, 45, 'پوءِ ڪيترائي ڳوٺ آھن جن کي ناس ڪيوسون ۽ اُھي ظالم ھوا پوءِ اُھي پنھنجي ڇتن سميت ڊٺل آھن، ۽ گھڻا کُوھ اُجڙيل ۽ گھڻيون بلند ماڙيون سُڃيون پيون آھن.'),
(2641, 22, 46, 'پوءِ مُلڪ ۾ نه گھميا آھن ڇا؟ ته کين (اھڙيون) دليون ھجن جن سان اُھي سمجھن يا کين (اھڙا) ڪن ھجن جن سان ٻُڌن، بيشڪ ھي (حالت) آھي ته سندن اکيون انڌيون نه آھن پر دليون انڌيون اٿن جيڪي سندن سينن ۾ آھن.'),
(2642, 22, 47, '۽ (اي پيغمبر) توکان عذاب جو جلد اچڻ گھرندا آھن ۽ الله پنھنجو انجام ڪڏھن اُبتڙ نه ڪندو، ۽ تنھنجي پالڻھار وٽ ھڪ ڏينھن اوھان جي ڳاڻيٽي جي ھزار ورھ جي برابر آھي.'),
(2643, 22, 48, '۽ گھڻا ڳوٺ آھن جن کي مُھلت ڏنم ۽ اُھي ظالم ھوا وري کين پڪڙيم، ۽ مون ڏانھن موٽڻ آھي.'),
(2644, 22, 49, '(اي پيغمبر) چؤ ته اي ماڻھؤ آءٌ اوھان کي رڳو پڌرو ڊيڄاريندڙ آھيان.'),
(2645, 22, 50, 'پوءِ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ بخشش ۽ سڳوري روزي آھي.'),
(2646, 22, 51, '۽ جيڪي اسان جي آيتن جي جھڪي ڪرڻ ۾ ڊوڙيا سي دوزخي آھن.'),
(2647, 22, 52, '۽ (اي پيغمبر) توکان اڳ نه ڪوئي پيغمبر ۽ نه ڪوئي نبي موڪليوسون پر جڏھن ڪا (انساني) آرزو ڪيائين تڏھن شيطان سندن آرزوءِ ۾ (حرڪت) وجھڻ لڳو، پوءِ جيڪو شيطان وڌو سو الله مٽائيندو رھيو وري الله پنھنجين آيتن کي مُحڪم ڪري ٿو، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(2648, 22, 53, 'ھن لاءِ ته شيطان جيڪا حرڪت وڌي تنھن سان انھن کي ڌار ڪري پرکي جن جي دلين ۾ بيماري آھي ۽ سندين دليون سخت آھن، ۽ بيشڪ ظالم وڏي ڦيٽي ۾ آھن.'),
(2649, 22, 54, '۽ ته جن کي علم ڏنو ويو سي ڄاڻن ته اُھو تنھنجي پالڻھار وٽان سچ آھي پوءِ اُن تي ايمان آڻين ۽ سندن دليون الله لاءِ عاجزي ڪن، ۽ بيشڪ الله مؤمنن کي سڌي واٽ ڏانھن ضرور سڌو رستو ڏيکاريندو آھي.'),
(2650, 22, 55, '۽ ڪافر اُنھيءَ (وحي) کان (ايسين) سدائين شڪ ۾ ھوندا جيسين اوچتو وٽن قيامت پھچي يا ندوري ڏينھن جو عذاب وٽن پھچي.'),
(2651, 22, 56, 'اُنھيءَ ڏينھن الله جي بادشاھي آھي، (اُھو) سندن وچ ۾ نبيرو ڪندو، پوءِ جن ايمان آندو ۽ چڱا عمل ڪيا سي نعمتن وارن باغن ۾ ھوندا.'),
(2652, 22, 57, '۽ جن انڪار ڪيو ۽ اسان جي آيتن کي ڪوڙو ڀانيو سي اُھي آھن جن لاءِ خواريءَ وارو عذاب آھي.'),
(2653, 22, 58, '۽ جن الله جي واٽ ۾ ديس ڇڏيو وري ڪُسجي ويا يا مُئا تن کي الله چڱو رِزق ڏيندو، ۽ الله چڱو روزي ڏيندڙ آھي.'),
(2654, 22, 59, 'جو ھنڌ ھنن کي وڻندو تنھن ۾ ضرور کين داخل ڪندو، ۽ بيشڪ الله ضرور ڄاڻندڙ بُردبار آھي.'),
(2655, 22, 60, 'اِھو (حڪم) آھي، ۽ جيڪو جيترو ايذايو ويو اوترو ايذائي وري مٿس زيادتي ڪئي وڃي ته اُن (ايذايل) کي الله مدد ڏيندو، بيشڪ الله معافي ڏيندڙ بخشڻھار آھي.'),
(2656, 22, 61, 'اِھا (مدد) ھن سببان آھي ته الله رات کي ڏينھن ۾ وجھندو آھي ۽ ڏينھن کي رات ۾ وجھندو آھي ۽ (ھن ڪري) ته الله ٻڌندڙ ڏسندڙ آھي.'),
(2657, 22, 62, 'اِھا (مدد) ھن ڪري آھي ته الله پاڻ برحق آھي ۽ الله کانسواءِ جنھن کي سڏيندا آھن سو ڪوڙو آھي ۽ (ھن ڪري) ته الله ئي مٿاھون وڏو آھي.'),
(2658, 22, 63, '(اي پيغمبر) نه ڏٺو اٿئي ڇا ته الله آسمانن مان (مينھن جو) پاڻي وسايو، پوءِ زمين ساول واري ٿيندي آھي، ڇوته الله لُطف ڪندڙ خبر رکندڙ آھي.'),
(2659, 22, 64, 'جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله جو آھي، ۽ الله ضروربي پرواھ ساراھيل آھي.'),
(2660, 22, 65, '(اي پيغمبر) نه ڏٺو اٿئي ڇا ته جيڪي زمين ۾ آھي سو الله اوھان لاءِ نوايو آھي ۽ ٻيڙيون درياءَ ۾ سندس حُڪم سان تَرنديون آھن، ۽ آسمان کي زمين تي ڪرڻ کان جھليندو آھي پر (جي ڪيرائيس ته) سندس اختيار آھي، بيشڪ الله ماڻھن تي بخشڻھار مھربان آھي.'),
(2661, 22, 66, '۽ اُھوئي آھي جنھن اوھان کي جياريو، وري اوھان کي ماريندو وري اوھان کي جياريندو، بيشڪ ماڻھو بي شڪر آھي.'),
(2662, 22, 67, 'سڀڪنھن ٽوليءَ لاءِ ھڪ شريعت مقرر ڪئي سون جنھن تي اُھي عمل ڪندڙ آھن پوءِ جڳائين ته ھِن ڪم ۾ جھڳڙو نه ڪن ۽ (تون) پنھنجي پالڻھار ڏانھن (ماڻھن کي) سڏيندو رھ، بيشڪ تون سڌيءَ واٽ تي آھين.'),
(2663, 22, 68, '۽ جيڪڏھن توسان جھڳڙو ڪن ته چؤ جيڪي ڪندا آھيو تنھنکي الله چڱو ڄاڻندڙ آھي.'),
(2664, 22, 69, 'جنھن بابت اوھين تڪرار ڪندا آھيو تنھن بابت قيامت جي ڏينھن الله اوھان جي وچ ۾ نبيرو ڪندو.'),
(2665, 22, 70, '(اي پيغمبر) معلوم نه اٿئي ڇا ته جيڪي آسمانن ۽ زمين ۾ آھي سو الله ڄاڻندو آھي؟ بيشڪ اِھا (ڳالھ) ڪتاب (لوح محفوظ) ۾ لکيل آھي، بيشڪ اُھو الله کي سولو آھي.'),
(2666, 22, 71, '۽ (ڪافر) الله کانسواءِ اُنھيءَ کي پوڄيندا آھن جنھن لاءِ ڪا سند نه لاٿي اٿس ۽ (پڻ) انھيءَ کي (پوڄيندا آھن) جنھن جي کين ڪا خبر نه آھي، ۽ ظالمن جو ڪو مددگار ڪونه آھي.'),
(2667, 22, 72, '۽ جڏھن اسان جيون چٽيون آيتون کين پڙھي ٻڌائبيون آھن تڏھن ڪافرن جي منھن ۾ ناراضپو معلوم ڪندو آھين، جيڪي مٿن اسان جون آيتون پڙھندا آھن تن تي ڪاھي پوڻ لاءِ ويجھا ٿيندا آھن، (اي پيغمبر کين) چؤ ته ھن کان وڌيڪ بڇڙي ڳالھ جي سُڌ اوھان کي ڏيان ڇا؟ (اُھا) باھ آھي جنھن جو الله ڪافرن کي انجام ڏنو آھي، اُھا جاءِ بُڇڙي آھي.'),
(2668, 22, 73, 'اي ماڻھؤ ھڪ مثال بيان ڪجي ٿو اُھو اوھين ٻڌو، بيشڪ الله کانسواءِ جن کي سڏيندا آھيو سي ڪا مَک پيدا نه ڪندا آھن توڻيڪ اُن (جي بڻائڻ) لاءِ گھڻا گڏ ٿين، ۽ جيڪڏھن مَک کانئن ڪا شيء وٺي ته اُھا کانئس ڇڏائي نه سگھندا، طالب ۽ مطلوب (پوڄارا ۽ بت ٻئي) ھيڻا آھن.'),
(2669, 22, 74, 'جھڙو الله جو قدر آھي (تھڙو) سندس قدر بجاءِ نه آندائون، بيشڪ الله ڏاڍو غالب آھي.'),
(2670, 22, 75, 'الله ملائڪن ۽ ماڻھن مان پيغام پھچائڻ وارا چونڊيندو آھي، بيشڪ الله ٻڌندڙ ڏسندڙ آھي.'),
(2671, 22, 76, 'جيڪي سندن اڳيان ۽ سندن پويان آھي سو الله ڄاڻندو آھي، ۽ الله ڏانھن سڀ ڪم موٽايا وڃن ٿا.'),
(2672, 22, 77, 'اي ايمان وارؤ رڪوع ڪريو ۽ سجدو ڪريو ۽ پنھنجي پالڻھار جي عبادت ڪريو ۽ چڱا ڪم ڪريو ته مانَ اوھين ڇُٽو.'),
(2673, 22, 78, '۽ الله (جي دين) ۾ اھڙو جھاد ڪريو جھڙو اُن جي جھاد جو حق آھي، اُنھيءَ اوھان کي پسند ڪيو ۽ اوھان تي دين ۾ ڪا اوکائي نه ڪيائين، اوھان جي پيءُ ابراھيم جو دين (اوھان جو دين ڪيائين)، الله اوھان جو نالو ھن کان اڳ ۽ ھن (قرآن) ۾ مسلمان ھن لاءِ رکيو آھي ته پيغمبر (ﷴ رسُول ﷺ) اوھان تي شاھد ھجي ۽ اوھين ماڻھن تي شاھد ھجو، پوءِ نماز پڙھو ۽ زڪوٰة ڏيو ۽ الله کي چنبڙي وٺو، (جو) اُھو اوھان جو سائين آھي، پوءِ چڱو سائين ۽ چڱو مددگار آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'بيشڪ اُھي مؤمن ڇٽا.'),
(2675, 23, 2, 'جيڪي پنھنجي نماز ۾ عاجزي ڪندڙ آھن.'),
(2676, 23, 3, '۽ جيڪي اجائي ڳالھ کان منھن موڙيندڙ آھن.'),
(2677, 23, 4, '۽ جي زڪوٰة ادا ڪندڙ آھن.'),
(2678, 23, 5, '۽ جي پنھنجين اُگھڙن کي (زنا کان) بچائيندڙ آھن.'),
(2679, 23, 6, 'رڳو پنھنجين زالن يا پنھنجين ٻانھين سا (گڏ ٿيندا آھن) پوءِ بيشڪ اُھي ملامت ڪيل نه آھن.'),
(2680, 23, 7, 'پوءِ جيڪي اُنھن (پنھنجين زالن يا پنھنجين ٻانھين) کانسواءِ طلبيندا سي ئي حد کان لنگھندڙ آھن.'),
(2681, 23, 8, '۽ جيڪي پنھنجين امانتن کي ۽ پنھنجي انجام کي سنڀاليندڙ آھن.'),
(2682, 23, 9, '۽ جيڪي پنھنجين نمازن جي نگاھ رکندا آھن.'),
(2683, 23, 10, 'اِھي ئي اُھي وارث آھن.'),
(2684, 23, 11, 'جيڪي نيٺ بھشت جا وارث ٿيندا، اُھي منجھس سدائين رھڻ وارا آھن.'),
(2685, 23, 12, '۽ بيشڪ ماڻھوءَ کي مٽيءَ جي ست مان بڻايوسون.'),
(2686, 23, 13, 'وري اُن کي نطفو ڪري مُحڪم جاءِ ۾ رکيوسون.'),
(2687, 23, 14, 'وري (اُن) نُطفي کي رت جو دڳ بڻايوسون وري (اُن) رت جي دڳ کي ٻوٽي بڻايوسون وري (اُن) ٻوٽي کي ھڏا بڻايوسون پوءِ (اُنھن) ھڏن کي گوشت پھرايوسون، وري اُن کي ٻي (نئين) بڻاوت بڻائي سون، پوءِ الله (وڏيءَ) برڪت وارو (سڀ کان) ڏاڍو چڱو خلقيندڙ آھي.'),
(2688, 23, 15, 'وري بيشڪ اوھين ان کانپوءِ ضرور مرندؤ.'),
(2689, 23, 16, 'وري بيشڪ اوھين قيامت جي ڏينھن اُٿاربؤ.'),
(2690, 23, 17, '۽ بيشڪ اوھان جي مٿان ست آسمان بڻاياسون، ۽ بڻائڻ کان بي خبر نه ھواسون.'),
(2691, 23, 18, '۽ آسمان کان پاڻي اندازي سان وسايوسون پوءِ اُن کي زمين ۾ رھايوسون، بيشڪ اسين اُن جي ورائي نيڻ تي وس وارا آھيون.'),
(2692, 23, 19, 'پوءِ اُن سان کجين ۽ ڊاکن جا باغ اوھان لاءِ ڄماياسون، اوھان لاءِ انھن باغن ۾ گھڻا ميوا آھن ۽ انھن مان ڪي اوھان کائيندا آھيو.'),
(2693, 23, 20, '۽ اھُو (زيتون جو) وڻ (پيدا ڪيوسون) جو طُور سَينا مان نڪرندو آھي (اھڙي طرح) ڄمندو آھي جو تيل ۽ کائڻ وارن لاءِ ٻوڙ پيدا ٿيندو آھي.'),
(2694, 23, 21, '۽ بيشڪ اوھان لاءِ ڍورن ۾ (به) عبرت آھي، (جو) جيڪي سندن پيٽن (يعني اوھن) ۾ آھي تنھن مان اوھان کي (کير) پياريندا آھيون ۽ اوھان لاءِ منجھن (ٻيا به) گھڻا فائدا آھن ۽ منجھائن ڪي کائيندا (به) آھيو.'),
(2695, 23, 22, '۽ انھن تي ۽ ٻيڙين تي اوھان کي سوار ڪبو آھي.'),
(2696, 23, 23, '۽ بيشڪ نوح کي سندس قوم ڏانھن موڪليوسون پوءِ چيائين ته اي منھنجي قوم الله جي عبادت ڪريو اُن کانسواءِ اوھان جو ڪو معبود نه آھي، پوءِ ڇونه ڊڄندا آھيو؟'),
(2697, 23, 24, 'پوءِ سندس قوم مان جيڪي سردار ڪافر ھوا تن چيو ته ھي رڳو اوھان جھڙو ماڻھو آھي اوھان تي سرادري (حاصل) ڪرڻ گھرندو آھي، ۽ جيڪڏھن الله گھري ھا ته ملائڪن کي موڪلي ھا، اِھا (ڳالھ ته) پنھنجن ابن ڏاڏن ۾ نه ٻڌيسون.'),
(2698, 23, 25, 'اِھو ته رڳو ھڪڙو ديوانو شخص آھي تنھنڪري اُن لاءِ ھڪ وقت تائين ترسو.'),
(2699, 23, 26, '(نوح) چيو اي منھنجا پالڻھار مون کي مدد ڏي انھيءَ ڪري جو مون کي ڪوڙو ڀانيائون.'),
(2700, 23, 27, 'پوءِ ڏانھنس وحي ڪيوسون ته اسان جي اکين آڏو ۽ اسان جي حُڪم سان ٻيڙي ٺاھ پوءِ جڏھن اسان جو حُڪم اچي ۽ تنور اُڀامي تڏھن سڀڪنھن جوڙي مان ٻه ٻه (نر ۽ مادي) ۽ پنھنجي گھر وارا منجھس چاڙھ پر منجھائن جن بابت (ھلاڪ ٿيڻ جو) حُڪم ٿي چڪو (سي نه چاڙھ)، ۽ ظالمن بابت مون کي نه چؤ، ڇوته اُھي ٻُڏل آھن.'),
(2701, 23, 28, 'پوءِ جڏھن ٻيڙيءَ تي تون چڙھي وِھين ۽ تنھنجا سنگتي (به وِھن) تڏھن چؤ ته سڀ ساراھ اُنھيءَ الله کي جڳائي جنھن ظالمن جي قوم کان اسان کي بچايو.'),
(2702, 23, 29, '۽ چؤ ته اي منھنجا پالڻھار مون کي برڪت واري ھنڌ لاھ ۽ تون (سڀ کان) چڱو لاھيندڙ آھين.'),
(2703, 23, 30, 'بيشڪ ھن (قِصّي) ۾ نشانيون آھن ۽ بيشڪ اسين پرکڻ وارا ھواسون.'),
(2704, 23, 31, 'وري کانئن پوءِ ٻيو جُڳ پيدا ڪنداسون.'),
(2705, 23, 32, 'پوءِ منجھن ھڪ پيغمبر اُنھن (جي قبيلي) مان موڪليوسون (تنھن چين) ته الله جي عبادت ڪريو اُن کانسواءِ اوھانجو ڪو معبود نه آھي، پوءِ ڇونه ڊڄندا آھيو؟'),
(2706, 23, 33, '۽ سندس قوم مان جن سردارن ڪفر ڪيو ۽ آخرت جي ملڻ کي ڪوڙ ڀانيو ۽ کين دنيا جي حياتيءَ ۾ آسودو به ڪيو ھوسون تن چيو ته ھيءُ (پيغمبر) رڳو اوھان جھڙو ماڻھو آھي (جو) جنھن (قسم) منجھان (اوھين) کائيندا آھيو تنھن مان (اُھو به) کائيندو آھي ۽ جنھن (قسم) منجھان اوھين پيئندا آھيو تنھن مان (اُھو به) پيئندو آھي.'),
(2707, 23, 34, '۽ جيڪڏھن پاڻ جھڙي ماڻھوءَ جو چيو مڃيندؤ ته اوھين ان مھل ڇيئي وارا ٿيندؤ.'),
(2708, 23, 35, 'اوھان سان انجام ڪندو آھي ڇا ته جڏھن (اوھين) مرندؤ ۽ مٽي ۽ ھڏا ٿيندؤ (تڏھن) اوھين ٻاھر ڪڍبؤ؟'),
(2709, 23, 36, 'جنھن جو اوھان کي انجام ڏجي ٿو سو (ٻڌڻ ۽ چوڻ کان) پري (بلڪل) پري آھي.'),
(2710, 23, 37, 'اسان جي رڳي ھيءَ دنيا جي حياتي آھي (جنھن ۾) مرندا آھيون ۽ جيئندا آھيون ۽ اسين (وري جيئرا ٿي) اُٿڻ وارا نه آھيون.'),
(2711, 23, 38, 'ھيءُ رڳو ھڪ ماڻھو آھي جنھن الله تي ڪوڙ ٻڌو آھي ۽ اسين کيس (ڪڏھن) مڃڻ وارا نه آھيون.'),
(2712, 23, 39, '(پيغمبر) چيو ته منھنجا پالڻھار مون کي مدد ڏي ھن ڪري جو مون کي ڪوڙو ڀانيائون.'),
(2713, 23, 40, '(الله) فرمايو ته ٿوري مُدت پڄاڻا پشيمان ٿيندا.'),
(2714, 23, 41, 'پوءِ کين سچي انجام جي ڪري ڌڪاءَ پڪڙيو پوءِ کين ڪُٽو ڪيوسون، پوءِ ظالمن جي قوم تي لعنت ھجي.'),
(2715, 23, 42, 'وري کانئن پوءِ ٻيا جُڳ پيدا ڪياسون.'),
(2716, 23, 43, 'ڪائي ٽولي پنھنجي مُدت کان اڳي نه ويندي آھي ۽ نڪي (اُھي) دير ڪندا آھن.'),
(2717, 23, 44, 'وري لڳو لڳ پنھنجن پيغمبرن کي موڪليوسون، جڏھن ڪڏھن ڪنھن ٽوليءَ وٽ سندن پيغمبر آيو (تڏھن) کيس ڪوڙو ڄاتائون پوءِ انھن کي ھڪ ٻئي پٺيان (ناس) ڪيوسون ۽ کين (ٻـين لاءِ) آکاڻيون ڪيوسون، پوءِ نه مڃيندڙ قوم تي لعنت ھُجي.'),
(2718, 23, 45, 'وري پنھنجين نشانين ۽ پڌريءَ حجت سان موسىٰ ۽ سندس ڀاءُ ھارون کي موڪليوسون.'),
(2719, 23, 46, 'فرعون ۽ سندس سردارن ڏانھن پوءِ انھن وڏائي ڪئي ۽ (اُھي) ھٺيلي قوم ھوا.'),
(2720, 23, 47, 'پوءِ چيائون ته پاڻ جھڙن ٻن ماڻھن تي ايمان آڻيون ڇا؟ ۽ سندن قوم (ته) اسان جو ٻيلپو ڪندڙ آھي.'),
(2721, 23, 48, 'پوءِ انھن کي ڪوڙو ڄاتائون پوءِ ھلاڪ ڪيلن مان ٿيا.'),
(2722, 23, 49, '۽ بيشڪ موسىٰ کي ڪتاب ڏنوسون ته مانَ اُھي سِڌو رستو لھن.'),
(2723, 23, 50, '۽ مريم جي پٽ کي ۽ سندس ماءُ کي ھڪ نشاني ڪئي سون ۽ انھن کي سنئين ۽ وھندڙ پاڻي واري مٿاھين زمين تي (رھڻ جي) جاءِ ڏني سون.'),
(2724, 23, 51, '(چيوسون ته) اي پيغمبرؤ سٺين شين مان کائو ۽ چڱا ڪم ڪريو، بيشڪ جيڪي ڪندا آھيو سو آءٌ ڄاڻندڙ آھيان.'),
(2725, 23, 52, '۽ بيشڪ اوھان جو ھيءُ دين ھڪ دين آھي ۽ آءٌ اوھان جو پالڻھار آھيان تنھنڪري مون کان ڊڄو.'),
(2726, 23, 53, 'پوءِ (اُنھن قومن) پنھنجي ڪم کي پاڻ ۾ ذرا ذرا ڪري ڇنون، سڀڪنھن ٽوليءَ وٽ جيڪي آھي تنھن سان اُھا سرھي (رھندي) آھي.'),
(2727, 23, 54, 'پوءِ (اي پيغمبر) کين سندن غفلت ۾ ھڪ حد تائين ڇڏي ڏي.'),
(2728, 23, 55, 'جنھن مال ۽ اولاد سان کين زيادتي ڏني سون تنھن کي ڀانئيندا آھن ڇا؟'),
(2729, 23, 56, 'ته ڪا انھن لاءِ چڱاين ڏيڻ ۾ تڪر ٿا ڪريون، (نه!) بلڪ نه سمجھندا آھن.'),
(2730, 23, 57, 'بيشڪ اُھي جيڪي پنھنجي پالڻھار جي ڀَوَ کان ڊڄندڙ آھن.'),
(2731, 23, 58, '۽ اُھي جيڪي پنھنجي پالڻھار جي نشانين تي ايمان آڻيندا آھن.'),
(2732, 23, 59, '۽ اُھي جيڪي پنھنجي پالڻھار سان شريڪ مُقرّر نه ڪندا آھن.'),
(2733, 23, 60, '۽ اُھي جيڪي (اُھي) ڏيندا آھن جيڪي (سندس واٽ ۾) ڏيندا آھن ۽ سندن دليون انھي ڪري ڏڪنديون آھن ته اُھي پنھنجي پالڻھار ڏانھن موٽڻ وارا آھن.'),
(2734, 23, 61, 'اھي (ماڻھو) چڱاين ۾ تڪڙائي ڪندا آھن ۽ اُھي اُن لاءِ اڳرائي ڪندڙ آھن.'),
(2735, 23, 62, '۽ (اسين) ڪنھن به جيءَ تي سندس طاقت کانسواءِ تڪليف نه ٿا رکون ۽ اسان وٽ ھڪ ڪتاب آھي جو سچ ڳالھائيندو آھي ۽ انھن تي ظلم نه ڪبو.'),
(2736, 23, 63, 'بلڪ سندن دليون انھن (ڳالھين) کان غفلت ۾ آھن ۽ ھن کانسواءِ انھن جا ٻيا ڪرتوت به آھن جي اُھي ڪندا آھن.'),
(2737, 23, 64, 'تان جو منجھائن آسودن (ماڻھن) کي جڏھن عذاب سان پڪڙيوسون تڏھن دانھون ڪندا رھيا.'),
(2738, 23, 65, '(چيوسون) ته اڄ دانھون نه ڪريو، ڇوته اسان کان اوھان کي مدد نه ملندي.'),
(2739, 23, 66, 'بيشڪ اوھان کي منھنجيون آيتون پڙھي ٻڌائبيون ھيون تڏھن اوھين پنھنجين کڙين (ڀرپوءِ) تي ڀڄندا ھُيؤ.'),
(2740, 23, 67, 'اُن (قرآن) کان وڏائي ڪندڙ (۽) آکاڻين ۾ مشغول ٿي (کيس) ڇڏيوَ ٿي.'),
(2741, 23, 68, 'ھن ڳالھ (قرآن) کي نه سوچيائون ڇا يا وٽن اھڙي شيء آئي آھي جا سندن اڳين پيءُ ڏاڏن وٽ نه آئي ھُئي؟'),
(2742, 23, 69, 'يا پنھنجي پيغمبر کي نه سڃاتائون ڇا تنھنڪري اُھي سندس مُنڪر (ٿيا) آھن.'),
(2743, 23, 70, 'يا چوندا آھن ته کيس چريائي آھي، (نه!) بلڪ (پيغمبر) وٽن سچي ڳالھ آندي آھي ۽ منجھائن گھڻا سچ کي بُرو ڀائيندڙ آھن.'),
(2744, 23, 71, '۽ جيڪڏھن الله سَندن سَڌن تي ھلي ھا ته آسمان ۽ زمين ۾ جيڪي منجھن آھي سي ويران ٿين ھا، (نه!) بلڪ سندين نصيحت وٽن آندي سون ۽ اُھي پنھنجيءَ نصيحت (ڪتاب) کان مُنھن موڙيندڙ آھن.'),
(2745, 23, 72, 'يا کائن ڪو اُجورو گھرندو آھين ڇا؟ پوءِ تنھنجي پالڻھار جو اُجورو (ڏئڻ) ڀلو آھي، ۽ اُھو چڱو روزي ڏيندڙ آھي.'),
(2746, 23, 73, '۽ بيشڪ تون سڌيءَ واٽ ڏانھن کين سڏيندو آھين.'),
(2747, 23, 74, '۽ بيشڪ جيڪي آخرت کي نه مڃيندا آھن سي سڌي واٽ کان پاسيرا ٿيندڙ آھن.'),
(2748, 23, 75, '۽ جيڪڏھن مٿن رحم ڪريون ھا ۽ جيڪو کين ڏک پُھتو سو لاھيون ھا ته (به) سدائين پنھنجيءَ گمراھيءَ ۾ حيران رھن ھا.'),
(2749, 23, 76, '۽ بيشڪ کين عذاب سان پڪڙيوسون پوءِ (به) پنھنجي پالڻھار جي اڳيان نه نِوڙيا ۽ نڪي عاجزي ڪيائون.'),
(2750, 23, 77, '(پنھنجي غفلت ۾ رھيا) تانجو جنھن مھل مٿن سخت عذاب وارو دروازو کوليوسون (تنھن مھل) اُتي جو اُتي اُنھي ۾ نا اُميد ٿيا.'),
(2751, 23, 78, '۽ (الله) اُھو آھي جنھن اوھان لاءِ ڪن ۽ اکيون ۽ دليون بڻايون، (پر اوھين تمام) ٿورو شڪر ڪندا آھيو.'),
(2752, 23, 79, '۽ (الله) اُھو آھي جنھن اوھان کي زمين ۾ پيدا ڪيو ۽ ڏانھس وري گڏ ڪيا ويندؤ.'),
(2753, 23, 80, '۽ (الله) اُھو آھي جيڪو جياري ٿو ۽ ماري ٿو ۽ رات ۽ ڏينھن جو ڦير ڦار ڪرڻ ان جو (ڪم) آھي، پوءِ نه سمجھندا آھيو ڇا؟'),
(2754, 23, 81, 'بلڪ جئن اڳين چيو ته تئين چوندا آھن.'),
(2755, 23, 82, 'چون ٿا ته ھان جڏھن مرنداسون ۽ مٽي ۽ ھڏا ٿينداسون (تڏھن) وري جيئل ٿي اٿنداسون ڇا؟'),
(2756, 23, 83, 'بيشڪ اسان کي ۽ اسان جي ابن ڏاڏن کي ھن کان اڳ انھي (ڳالھ) جو انجام ڏنو ويو آھي اھي رڳو اڳين جا قصّا آھن.'),
(2757, 23, 84, 'چؤ ته جيڪڏھن اوھين ڄاڻندا آھيو ته زمين ۽ جيڪي منجھس آھي سو ڪنھن جو آھي؟'),
(2758, 23, 85, 'سگھوئي چوندا ته (اُھو سڀ) الله جو آھي، چؤ ته پوءِ ڇونه نصيحت وٺندا آھيو؟'),
(2759, 23, 86, '(اي پيغمبر کين) چؤ ته ستن آسمانن جو پالڻھار ۽ وڏي عرش جو پالڻھار ڪير آھي؟'),
(2760, 23, 87, 'سگھوئي چوندا ته (اھو سڀ) الله جو آھي، چؤ ته پوءِ ڇونه ڊڄندا آھيو؟'),
(2761, 23, 88, '(کين) چؤ ته (اُھو) ڪير آھي جنھنجي ھٿ ۾ سڀڪنھن شيء جي حڪومت آھي ۽ اُھو پناھ ڏيندو آھي ۽ اُن (جي عذاب) کان ڪنھن کي پناھ نه ڏيئي سگھبي آھي جيڪڏھن ڄاڻندا آھيو (ته ڏسيو).'),
(2762, 23, 89, 'سگھوئي چوندا ته (اھو سڀ) الله جي (ھٿ ۾) آھي، (کين) چؤ ته پوءِ اوھين ڪٿان ٺڳجو ٿا.'),
(2763, 23, 90, 'بلڪ وٽن سچي (ڳالھ) آندي اٿئون ۽ بيشڪ اُھي ڪوڙا آھن.'),
(2764, 23, 91, 'الله نڪي (پاڻ لاءِ) پٽ ڪري ورتو آھي ۽ نڪي ساڻس ڪو (ٻيو) معبود آھي (جيڪڏھن ائين ھجي ھا) ته ھر ڪو معبود پنھنجي بڻائي کي وٺيو وڃي ھا ۽ اُھي ھڪ ٻئي تي ڏاڍ ڪن ھا، (ڪافر) جيڪي بيان ڪندا آھن تنھن کان الله پاڪ آھي.'),
(2765, 23, 92, 'ڳجھ ۽ ظاھر جو ڄاڻندڙ آھي جنھن کي (اُن سان) شريڪ بڻائيندا آھن تنھن کان مٿاھون آھي.'),
(2766, 23, 93, '(اي پيغمبر) چؤ ته اي منھنجا پالڻھار جيڪو انجام ڪرين ٿو سو جيڪر مون کي ڏيکارين.'),
(2767, 23, 94, 'اي منھنجا پالڻھار مون کي ظالمن جي قوم ۾ (داخل) نه ڪر.'),
(2768, 23, 95, '۽ جيڪو کين انجام ڏيون ٿا تنھن جي توکي ڏيکارڻ تي بيشڪ اسين وسوارا آھيون.'),
(2769, 23, 96, 'ڏاڍي چڱيءَ عادت سان مدائي کي ٽار، (ڪافر) جيڪي بيان ڪندا آھن سو اسين وڌيڪ ڄاڻندڙ آھيون.'),
(2770, 23, 97, '۽ (اي پيغمبر) چؤ ته اي منھنجا پالڻھار شيطانن جي وسوسن کان تنھنجي پناھ گھران ٿو.'),
(2771, 23, 98, '۽ اي منھنجا پالڻھار پاڻ وٽ انھن جي ويجھي ٿيڻ کان (به) تنھنجي پناھ وٺان ٿو.'),
(2772, 23, 99, '(ڪافر پنھنجي ويسلائي ۾ ھوندا) تانجو جنھن مھل منجھانئن ڪنھن کي موت ايندو (تنھن مھل) چوندو ته اي منھنجا پالڻھار مون کي دنيا ۾ (ورائي) موٽاءِ.'),
(2773, 23, 100, 'ته جيڪر جيڪي (اُتي) ڇڏيم ته آءٌ چڱا عمل ڪريان ائين (ڪڏھن ٿيڻو) نه آھي، بيشڪ اِھا ڳالھ آھي جا ھو چوندڙ آھي، ۽ سندن اڳيان ھڪ پردو (عالَم قبر) انھيءَ ڏينھن تائين رھندو (جنھن ڏينھن قيامت ۾) اُٿاريا ويندا.'),
(2774, 23, 101, 'پوءِ جنھن مھل صور ۾ ڦوڪبو (تنھن مھل) اُن ڏينھن سندن وچ ۾ نه ذاتيون رھنديون ۽ نڪي پاڻ ۾ پُڇا ڳاڇا ڪندا.'),
(2775, 23, 102, 'پوءِ جنھن (جي نيڪيءَ) جو تراڙو ڳرو ٿيو سي ئي ڇُٽل آھن.'),
(2776, 23, 103, '۽ جنھن جو تراڙو ھلڪو ٿيو سي اُھي آھن جن پاڻ کي ڇيئو لاتو (اُھي) دوزخ ۾ سدائين رھندڙ آھن.'),
(2777, 23, 104, 'سندن مُنھن کي باھ ساڙيندي ۽ اُھي منجھس بد شڪلا ٿيندا.'),
(2778, 23, 105, '(چيو ويندن ته) منھنجون آيتون اوھان کي نه پڙھي ٻڌائبيون ھيون ڇا؟ پوءِ اوھين انھن کي ڪوڙو ڀائيندا ھيؤ؟'),
(2779, 23, 106, 'چوندا ته اي اسان جا پالڻھار اسان جي بدبختي اسان تي غالب ٿي وئي ۽ گمراھ قوم ھياسون.'),
(2780, 23, 107, 'چوندا ته اي اسان جا پالڻھار ھتان اسان کي ٻاھر ڪڍ پوءِ جيڪڏھن موٽي (ڪفر) ڪريون ته بيشڪ اسين ظالم ٿيون.'),
(2781, 23, 108, '(الله) فرمائيندو ته منجھس خوار پيا رھو ۽ مون سان نه ڳالھايو.'),
(2782, 23, 109, 'ڇوته منھنجن ٻانھن مان ھڪ ٽولي ھئي جا چوندي ھئي ته اي اسان جا پالڻھار اسان ايمان آندو تنھنڪري اسان کي بخش ۽ اسان تي رحم ڪر ۽ تون (سڀ کان) چڱو ٻاجھارو آھين.'),
(2783, 23, 110, 'پوءِ ايسين کين ٺـٺولي ڪري ورتوَ جيسين (سندن دشمنيءَ) اوھان (جي دل) کان منھنجي يادگيري وسارائي ۽ اوھين مٿن کِليؤ ٿي.'),
(2784, 23, 111, 'بيشڪ اڄ کين چڱو بدلو انھي ڪري ڏنم جو صبر ڪيو ھئائون جو اُھي مراد ماڻيندڙ آھن.'),
(2785, 23, 112, '(الله) فرمائيندو ته زمين ۾ (اوھين) ڪيترا ورھ رھيؤ؟'),
(2786, 23, 113, 'چوندا ته ھڪ ڏينھن يا ڏينھن جو ڀاڱو رھياسون پوءِ ڳڻيندڙن کان پُڇا ڪر.'),
(2787, 23, 114, '(الله) فرمائيندو ته ٿوريءَ (دير) کانسواءِ نه رھيا ھيؤ جيڪر اوھين ڄاڻو ھا.'),
(2788, 23, 115, 'اوھين ڀانيو ٿا ڇا ته اوھان کي اجايو بڻايو اٿئون ۽ اوھين اسان ڏانھن وري نه موٽايا ويندؤ؟'),
(2789, 23, 116, 'پوءِ الله سچو بادشاھ تمام مٿاھون آھي، اُن کانسواءِ ڪو عبادت جو لائق نه آھي (اھو) سڳوري عرش جو پالڻھار آھي.'),
(2790, 23, 117, '۽ جيڪو الله سان گڏ ٻئي معبود کي سڏيندو جنھن جو کيس ڪو دليل ڪونھي تنھن جو حساب سندن پالڻھار وٽ ئي آھي، سچ آھي ته ڪافر نه ڇُٽندا.'),
(2791, 23, 118, '۽ چؤ ته اي منھنجا پالڻھار بخش ۽ رحم ڪر ۽ تون (سڀني) ٻاجھارن کان چڱو آھين.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, '(ھيءَ ھڪ) سُورة آھي جا نازل ڪئي سون ۽ اُن (جي حڪمن) کي فرض ڪيوسون ۽ منجھس پڌريون آيتون نازل ڪيوسون ته مانَ نصيحت وٺو.'),
(2793, 24, 2, 'زنا ڪندڙ زال ۽ زنا ڪندڙ مڙس سو ٻنھيءَ مان ھر ھڪ کي سؤ دُرا ھڻو، ۽ جيڪڏھن اوھين الله ۽ قيامت جي ڏينھن کي مڃيندا آھيو ته الله جي دين ۾ اوھان کي انھي ٻنھي تي نرمي ڪرڻ نه جڳائي، ۽ مؤمنن مان ھڪ ٽوليءَ کي انھن ٻنھي جي سزا تي حاضر ٿيڻ گھرجي.'),
(2794, 24, 3, 'زنا ڪندڙ مرد زنا ڪندڙ زال يا مشرڪياڻيءَ کانسواءِ ٻي نه پرڻبو، ۽ زنا ڪندڙ زال کيس زنا ڪندڙ مڙس يا مشرڪ کانسواءِ ٻيو نه پرڻبو، ۽ اِھو (زنا ڪندڙ زال سان نڪاح ڪرڻ) مؤمنن تي حرام ڪيو ويو.'),
(2795, 24, 4, '۽ جيڪي پاڪدامن عورتن کي تھمت لائين وري چار شاھد نه آڻين ته کين اسي دُرا ھڻو ۽ انھن جي گواھي اصلي قبول نه ڪريو، ۽ اُھي بي دين آھن.'),
(2796, 24, 5, 'پر ھن کان پوءِ جن توبه ڪئي ۽ پاڻ سُڌاريو، ته الله بخشڻھار مھربان آھي.'),
(2797, 24, 6, '۽ جيڪي پنھنجي زالن کي تُھمت لائين ۽ کين پاڻ کانسواءِ ڪي شاھد نه ھجن ته اھڙي ھر ھڪ جي (سچي) شاھدي چار ڀيرا (ھيئڻ ڏيڻ) آھي ته الله جو قسم ته بيشڪ پاڻ سچ چوندڙن مان آھي.'),
(2798, 24, 7, '۽ پنجون ڀيرو چوي ته جيڪڏھن ڪوڙن مان ھوندو ته مٿس الله جي لعنت ھجي.'),
(2799, 24, 8, '۽ اُن زال کان (ھيءَ ڳالھ) سزا ٽاريندي جو چار ڀيرا شاھدي ڏئي ته الله جو قسم ته بيشڪ اُھو مُڙس ڪُوڙ چوندڙن مان آھي.'),
(2800, 24, 9, '۽ پنجون ڀيرو (ھيءَ شاھدي ڏئي) ته جيڪڏھن اُھو مڙس سچن مان آھي ته اُن (زال) تي الله جو ڏمر ھُجي.'),
(2801, 24, 10, '۽ جيڪڏھن اوھان تي الله جو فضل ۽ سندس ٻاجھ نه ھجي ھا (ته عذاب ٿئيوَ ھا) ۽ بيشڪ الله توبه قبول ڪندڙ حِڪمت وارو آھي.'),
(2802, 24, 11, 'بيشڪ جن ڪُوڙو بُھتان آندو سي اوھان مان ھڪ ٽولو آھن، (اوھين) اُن (بُھتان) کي نه ڀانيو ته اوھان لاءِ (ڪو) مدو آھي، بلڪ اُھو اوھان لاءِ ڀلو آھي، منجھائن سڀڪنھن ماڻھوءَ گناھن مان جيڪي ڪمايو سو سندس لاءِ آھي، ۽ منجھائن جنھن بھتان جو وڏو لُڙ مچايو تنھن لاءِ وڏو عذاب آھي.'),
(2803, 24, 12, 'جڏھن اُھو ٻُڌو تڏھن مؤمنن مڙسن ۽ مؤمنياڻن زالن پنھنجن (ماڻھن) ۾ چڱو گمان ڇونه ڪيو ۽ ڇونه چيائون ته ھيءُ پڌرو بُھتان آھي؟'),
(2804, 24, 13, 'اِن (ڳالھ) تي چار شاھد ڇونه آندائون؟ پوءِ جڏھن شاھد نه آندائون تڏھن اُھي ئي الله وٽ ڪُوڙا آھن.'),
(2805, 24, 14, '۽ جيڪڏھن الله جو فضل ۽ سندس ٻاجھ دنيا ۽ آخرت ۾ اوھان تي نه ھجي ھا ته جنھن (ڳالھ) بابت چرچو مچايوَ (تنھن بابت) اوھان کي (تڏھن ئي) وڏو عذاب پھچي ھا.'),
(2806, 24, 15, 'جڏھن ان کي پنھنجن زبانن سان ٻاڦيندا رھيؤ ۽ جنھن (ڳالھ جي حقيقت) جي اوھان کي ڪا خبر نه ھئي سا پنھنجن واتن سان چوندا رھيؤ ۽ اُن کي ھلڪو ٿي ڀانيوَ، ۽ (حقيقت ڪري) اُھو الله وٽ وڏو (گناہ) ھو.'),
(2807, 24, 16, '۽ جڏھن اُھو ٻڌوَ (تڏھن) ڇونه چيوَ ته اسان کي اھو چوڻ نه جڳائيندو آھي، (اي الله) تنھنجي پاڪائي بيان ڪريون ٿا ھي وڏ بُھتان آھي.'),
(2808, 24, 17, 'الله اوھان کي نصيحت ٿو ڪري ته جيڪڏھن مؤمن آھيو ته (ٻيھر) وري اصلي اھڙو (ڪم) نه ڪجو.'),
(2809, 24, 18, '۽ الله اوھان لاءِ آيتون بيان ٿو ڪري، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(2810, 24, 19, 'بيشڪ جيڪي (ماڻھو) مؤمنن (جي نسبت) ۾ بدڪاريءَ (جي تھمت) جي پڌري ٿيڻ کي دوست رکندا آھن تن لاءِ دنيا ۽ آخرت ۾ ڏکوئيندڙ عذاب آھي، ۽ الله ڄاڻندو آھي ۽ اوھين نه ڄاڻندا آھيو.'),
(2811, 24, 20, '۽ جيڪڏھن اوھان تي الله جو فضل ۽ سندس ٻاجھ نه ھجي ھا (ته ضرور خراب ٿيو ھا) ۽ الله بخشڻھار مھربان آھي.'),
(2812, 24, 21, 'اي ايمان وارؤ اوھين شيطان جي وِکن تي نه ھلو، ۽ جيڪو شيطان جي وِکن تي ھلندو ته شيطان ته بيحيائي (جون ڳالھيون) ۽ مندا ڪم ڏسيندو آھي، ۽ جيڪڏھن اوھان تي الله جو فضل ۽ سندس ٻاجھ نه ھجي ھا ته اوھان مان ڪو ھڪ ڪڏھن نه سُڌري ھا پر الله جنھن کي گھرندو آھي تنھن کي پاڪ ڪندو آھي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(2813, 24, 22, '۽ اوھان مان فضل وارا ۽ آسودا مائٽي وارن ۽ مسڪينن ۽ الله جي واٽ ۾ وطن ڇڏيندڙن کي (ڪي به) نه ڏيڻ جو قسم نه کڻن، ۽ جڳائي ته معاف ڪن ۽ ٽارو ڪن، اوھين نه گھرندا آھيو ڇا ته الله اوھان کي بخشي؟ ۽ الله بخشڻھار مھربان آھي.'),
(2814, 24, 23, 'بيشڪ جيڪي (ماڻھو) پاڪدامن بي خبر مؤمنياڻن زالن کي تُھمت لائيندا آھن تن تي دنيا ۽ آخرت ۾ لعنت آھي، ۽ اُنھن لاءِ (اُنھيءَ ڏينھن) وڏو عذاب آھي.'),
(2815, 24, 24, 'انھن جيڪي ڪمايو تنھن بابت سندين زبانون ۽ سندن ھٿ ۽ سندن پير جنھن ڏينھن مٿن شاھدي ڏيندا.'),
(2816, 24, 25, 'اُن ڏينھن الله کين انصاف سان سندن بدلو پُورو ڏيندو ۽ (ھيءُ) ڄاڻندا ته الله ئي سچو پڌري ڪرڻ وارو آھي.'),
(2817, 24, 26, 'پليت زالون پليت مردن لاءِ (لائق) آھن ۽ پليت مرد پليت زالن لاءِ (لائق) آھن، ۽ پاڪ زالون پاڪ مردن لاءِ (لائق) آھن ۽ پاڪ مرد پاڪ زالن لاءِ (لائق) آھن، (انھن جي به نسبت ماڻھو) جيڪي چوندا آھن تنھن (ڳالھ) کان اُھي پاڪ آھن، انھن لاءِ بخشش ۽ سڳوري روزي آھي.'),
(2818, 24, 27, 'اي ايمان وارؤ پنھنجن گھرن کانسواءِ ٻـين (ماڻھن جي) گھرن ۾ (ايسين) نه گھڙو جيسين (نه) موڪلايو ۽ اُن جي رھڻ وارن تي سلام (نه) ڏيو، اِھو اوھان لاءِ چڱو آھي مانَ اوھين نصيحت وٺو.'),
(2819, 24, 28, 'پوءِ جيڪڏھن منجھس ڪنھن کي نه ڏسو ته اُن ۾ (ايسين) نه گھڙو جيسين اوھان کي موڪل (نه) ڏجي، ۽ جيڪڏھن اوھان کي چئجي ته موٽي وڃو ته موٽي وڃو اِھو اوھان لاءِ تمام سٺو آھي، ۽ جيڪي اوھين ڪندا آھيو سو الله ڄاڻندڙ آھي.'),
(2820, 24, 29, 'اھڙن گھرن ۾ گھڙڻ کان اوھان تي ڪو گناھ ڪونھي جنھن ۾ ڪوئي نه رھندو ھجي منجھس اوھان جو سامان (رکيل) ھجي، ۽ جيڪي پڌرو ڪندا آھيو ۽ جيڪي لڪائيندا آھيو سو الله ڄاڻندو آھي.'),
(2821, 24, 30, 'مؤمن مڙسن کي چؤ ته پنھنجين اکين کي ھيٺ ڪن ۽ پنھنجن اُگھڙن کي بچائيندا رھن، اھو انھن لاءِ تمام سٺو آھي، جيڪي ڪندا آھن تنھنجي بيشڪ الله خبر رکندڙ آھي.'),
(2822, 24, 31, '۽ مؤمن زالن کي چؤ ته پنھنجيون اکيون جھڪيون ڪن ۽ پنھنجن اوگھڙن کي بچائينديون رھن ۽ پنھنجي سينگار (يعني زيوتن جي جاين) کي پڌرو نه ڪن پر اُنھن مان جيڪي ظاھر (ضروري) آھن (جھڙا ھٿ پير) ۽ جڳائين ته پنھنجين چادرن کي پنھنجي گريبانن تي لاھي ڇڏين، ۽ پنھنجن مڙسن يا پنھنجن پين يا پنھنجن سُھرن يا پنھنجن پُٽن يا پنھنجن مڙسن جي پُٽن يا پنھنجن ڀائرن يا پنھنجن ڀائيٽن يا پنھنجن ڀاڻيجن يا پنھنجن زالن يا پنھنجن ٻانھن يا خدمت گار مڙسن مان (زالن ڏانھن) اڻ خواھش وارن يا جن ٻارن کي زالن جي اوگھڙ جو سماءُ نه پوي تن ڌاران ٻـين (سڀني) جي اڳيان پنھنجو سينگار پڌرو نه ڪن، ۽ پنھنجن پيرن سان (زيور) ھن لاءِ نه ڇمڪائين ته سندن سينگار مان جيڪي ڳجھو آھي سو ڄاتو وڃي، ۽ اي مسلمانؤ اوھين مڙيئي الله جي اڳيان توبه ڪريو ته مانَ اوھين ڇُٽو.'),
(2823, 24, 32, '۽ پنھنجيءَ (قوم) مان رنن زالن کي پرڻايو ۽ پنھنجن ٻانھن ۽ پنھنجين ٻانھين مان نيڪن کي (به پرڻايو)، جيڪڏھن (ھو) غريب ھوندا ته الله پنھنجي فضل سان کين آسُودو ڪندو، ۽ الله (مھربانيءَ ۾) ڪشادگيءَ وارو ڄاڻندڙ آھي.'),
(2824, 24, 33, '۽ جيڪي پرڻجي نٿا سگھن تن کي ايسين پاڪدامن رھڻ گھرجي جيسين کين الله پنھنجي فضل سان آسودو ڪري، ۽ اوھان جي ٻانھن مان جيڪي (آزاديءَ لاءِ عوض ڏيڻ جو) لکيل انجام گھرن تن ۾ جيڪڏھن ڪا چڱائي ڄاڻو ته کين لکيل انجام ڏيو، ۽ جيڪو مال الله اوھان کي ڏنو آھي تنھن مان کين ڏيو، ۽ اوھان جون ٻانھيون جيڪڏھن پاڪدامن رھڻ جو ارادو ڪن ٿيون ته انھن کي اوھين دُنيا جي حياتيءَ جي سامان حاصل ڪرڻ لاءِ زنا تي زور نه ڪريو، ۽ جيڪو کين زنا تي زور ڪندو ته زور ڪرڻ کانپوءِ انھن (ٻانھين) کي الله بخشڻھار مھربان آھي.'),
(2825, 24, 34, '۽ بيشڪ اوھان ڏانھن چٽيون نشانيون ۽ جيڪي اوھان کان اڳ گذري ويا تن جا مثال ۽ پرھيزگارن لاءِ نصيحت لاٿي سون.'),
(2826, 24, 35, 'الله آسمانن ۽ زمين کي روشن کي ڪندڙ آھي، سندس نُور جو مثال انھيءَ جاري وانگر آھي جنھن ۾ ڏيو ھجي، ڏيو قنديل ۾ ھجي، اُھو قنديل ڄڻڪ چمڪندڙ ستارو آھي جو انھيءَ برڪت واري زيتون جي وڻ (جي تيل) مان ٻارجي جو نڪي اوڀر ۽ نڪي اولھ (ڄمندو) آھي سندس تيل جيتوڻيڪ باھ نه پھتي ھجيس ته به سوجھري ڪرڻ کي ويجھو آھي، نُور تي نُور آھي، الله جنھن کي گھرندو آھي تنھنکي پنھنجي نُور سان سِڌو رستو ڏيکاريندو آھي، ۽ الله ماڻھن لاءِ مثال بيان ڪندو آھي، ۽ الله سڀڪنھن شيء کي ڄاڻندڙ آھي.'),
(2827, 24, 36, '(اُنھي نُور جو ھنڌ) اُنھن گھرن ۾ (آھي) جن لاءِ الله حُڪم ڪيو آھي ته انھن جي تعظيم ڪجي ۽ انھن ۾ سندس نالو ياد ڪجي انھن ۾ صبح ۽ سانجھيءَ جو الله جي پاڪائي بيان ڪندا آھن.'),
(2828, 24, 37, '(يعني اھڙا) مڙس جو انھن کي واپار ۽ نڪي سودو الله جي ياد ڪرڻ ۽ نماز پڙھڻ ۽ زڪوٰة ڏيڻ کان جھليندو آھي، انھي ڏينھن کان ڊڄندا آھن جنھن ۾ دليون ۽ اکيون اُبتيون ٿينديون.'),
(2829, 24, 38, 'ھن لاءِ ته جيڪي تمام چڱا عمل ڪيائون تن جوالله کين بدلو ڏئي ۽ کين پنھنجي فضل سان وڌيڪ به ڏئي، ۽ الله جنھن کي گھرندو آھي (تنھن کي) اَڻ ڳڻي روزي ڏيندو آھي.'),
(2830, 24, 39, '۽ جن ڪُفر ڪيو تن جا اعمال رڻ پَٽ جي رُڃ وانگر آھن اُڃو اُن کي پاڻي ڀانئي، تان جو جڏھن اُن وٽ پُھچي (تڏھن) اُن کي ڪا شيء نه لھي ۽ الله کي ئي پاڻ وٽ لھي پوءِ اُھو کيس سندس حساب پورو پُھچائي ڏئي، ۽ الله جلد حساب وٺندڙ آھي.'),
(2831, 24, 40, 'يا (ڪافر جي عملن جو مثال) اونداھين وانگر آھي جو اُونھيءَ درياءَ ۾ لھر جي مٿان لھر ويڙھيس مٿانئس جھُڙ ھُجي، ھڪ ٻئي جي مٿان (اُھي) اونداھيون ھُجن، جڏھن پنھنجو ھٿ ڪڍي (تڏھن) اُن کي ڏسي نه سگھي، ۽ جنھن کي الله سوجھرو نه ڏنو تنھن کي ڪو سوجھرو ڪونھي.'),
(2832, 24, 41, 'نه ڏٺو اٿئي ڇا ته جيڪي آسمانن ۽ زمين ۾ آھن سي ۽ کليل پرن وارا پکي الله جي پاڪائي بيان ڪندا آھن، بيشڪ سڀئي پنھنجي نماز ۽ پنھنجي تسبيح (پڙھڻ جي طرح) کان واقف آھن، ۽ جيڪي ڪندا آھن سو الله ڄاڻندڙ آھي.'),
(2833, 24, 42, 'آسمانن ۽ زمين جي بادشاھت الله جي آھي، ۽ الله ڏانھن ورڻو آھي.'),
(2834, 24, 43, '(اي پيغمبر) نه ڏٺو اٿئي ڇا ته الله ڪڪرن کي ھلائيندو آھي وري انھن کي پاڻ ۾ گڏيندو آھي وري کين کين تھن تي تھ ڪندو آھي پوءِ ڏسندو آھين ته ڦڙي سندس وچ مان نڪرندي آھي، ۽ آسمان کان اُتي جي ڳڙن جي ٽڪرن مان ڳڙو وسائيندو آھي پوءِ جنھن کي وڻيس تنھن کي اُھو پُھچائيندو ۽ جنھن کان وڻيس تنھن کان اُھو ٽاريندو آھي، اُن (ڪڪر) جي وِڄ جو چمڪاٽ (ڄڻ ته) اجھو اکيون ٿو نئي.'),
(2835, 24, 44, 'الله رات ۽ ڏينھن کي مٽائيندو آھي، بيشڪ اُن ۾ ڏسڻ وارن لاءِ عبرت آھي.'),
(2836, 24, 45, '۽ الله سڀڪو جاندار پاڻيءَ مان بڻايو آھي، پوءِ منجھائن ڪي پيٽ ڀر ھلندا آھن، ۽ منجھائن ڪي ٻن پيرن تي ھلندا آھن، ۽ منجھائن ڪي چئن (پيرن) تي ھلندا آھن، الله جيڪي گھرندو آھي سو پيدا ڪندو آھي، ڇوته الله سڀڪنھن شيء تي وس وارو آھي.'),
(2837, 24, 46, 'بيشڪ پڌريون آيتون نازل ڪيوسون، ۽ الله جنھن کي گھري تنھن کي سڌو رستو ڏيکاريندو آھي.'),
(2838, 24, 47, '۽ (منافق) چوندا آھن ته الله تي ۽ جنھن پيغمبر تي ايمان آندوسون ۽ فرمانبرداري ڪئي سون وري اُن کانپوءِ منجھانئن ھڪ ٽولي ڦري وڃي ٿي، ۽ اُھي اِيمان وارا نه آھن.'),
(2839, 24, 48, '۽ جڏھن الله ۽ سندس پيغمبر ڏانھن سڏبا آھن ته سندن وچ ۾ (پيغمبر) نبيرو ڪري (تڏھن) اُمالڪ منجھانئن ھڪ ٽولي منھن موڙيندڙ آھي.'),
(2840, 24, 49, '۽ جيڪڏھن (ڪنھن تي) سندن حق ھوندو آھي ته پيغمبر ڏانھن ڪنڌ نوائي ايندا آھن.'),
(2841, 24, 50, 'سندين دلين ۾ بيماري آھي ڇا يا شڪ ۾ پيا آھن يا ڊڄندا آھن ته الله ۽ سندس پيغمبر مٿن ظلم ڪندو؟ (نه!) بلڪ اُھي پاڻ بي انصاف آھن.'),
(2842, 24, 51, 'جڏھن مؤمنن کي الله ۽ سندس پيغمبر ڏانھن سڏيو آھي ته (پيغمبر) سندن وچ ۾ نبيرو ڪري تڏھن انھن جو چوڻ رڳو ھيءُ ھوندو آھي ته ٻڌوسون ۽ حُڪم مڃيوسون، ۽ اُھي ڇٽل آھن.'),
(2843, 24, 52, '۽ جيڪو الله ۽ سندس پيغمبر جو چيو مڃيندو ۽ الله کان ڊڄندو ۽ اُن جي نافرمانيءَ کان بچندو پوءِ اُھي مراد ماڻيندڙ آھن.'),
(2844, 24, 53, '۽ الله جا وڏا وڏا قسم پڪيءَ طرح کڻندا آھن ته جيڪڏھن کين حُڪم ڪرين ته ضرور (سڀ ڦٽي ڪري پنھنجي ديس مان) نڪرندا، (اي پيغمبر کين) چؤ ته قسم نه کڻو، دستوري فرمانبرداري (ڪريو)، ڇوته جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.'),
(2845, 24, 54, 'چؤ ته الله جي فرمانبرداري ڪريو ۽ پيغمبر جي فرمانبرداري ڪريو، پوءِ جيڪڏھن ڦرندؤ ته جيڪي پيغمبر تي (الله جي حُڪمن پُھچائڻ جو حق) رکيو ويو تنھن جو ذمو مٿس ئي آھي ۽ جيڪي اوھان تي رکيو ويو تنھن تي عمل ڪرڻ جو حق اوھان تي ئي آھي، ۽ جيڪڏھن سندس چيو مڃيندؤ ته ھدايت وارا ٿيندؤ، ۽ پيغمبر تي رڳو پڌرو پيغام پُھچائڻ لازم آھي.'),
(2846, 24, 55, 'اوھان مان جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي الله انجام ڏنو آھي ته مُلڪ ۾ ضرور کين خليفو ڪندو جئن کانئن اڳين کي حاڪم ڪيائين، ۽ سندن انھيءَ دين کي جو انھن لاءِ پسند ڪيو اٿس انھن لاءِ ضرور پختو ڪندو ۽ سندس ڊڄ کان پوءِ کين مٽائي اَمن ڏيندو، (اُھي) منھنجي عبادت ڪندا رھندا، (۽) مون سان ڪنھن شيء کي شريڪ نه ڪندا، ۽ جيڪي ان کانپوءِ ناشڪري ڪندا سي ئي بي دين آھن.'),
(2847, 24, 56, '۽ نماز پڙھو ۽ زڪوٰة ڏيو ۽ پيغمبر جي فرمانبرداري ڪريو ته مانَ اوھان تي رحم ڪيو وڃي.'),
(2848, 24, 57, 'ڪافرن کي نه ڀانءِ ته زمين ۾ ٿَڪائيندڙ آھن، ۽ سندين جاءِ دوزخ آھي، البت اُھو موٽڻ جو ھنڌ بڇڙو آھي.'),
(2849, 24, 58, 'اي ايمان وارؤ اوھان جي ٻانھن ۽ جيڪي اوھان مان بلوغت کي نه پُھتا آھن تن کي ٽي وقت (گھرن ۾ اچڻ لاءِ) اوھان کان موڪلائڻ گھرجي، فجر جي نماز کان اڳ ۽ منجھند کي جو اوھين ڪپڙا لاھي رکندا آھيو ۽ سومھاڻي جي نماز کان پوءِ، (جو اھي) اوھان لاءِ ٽي اُگھڙ جا وقت آھن، انھن کان پوءِ ھڪ ٻئي وٽ اوھان جي اچڻ وڃڻ کان نڪي اوھان تي ۽ نڪي اُنھن تي گناھ آھي، اھڙي طرح الله اوھان لاءِ حُڪم بيان ڪري ٿو، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(2850, 24, 59, '۽ جڏھن اوھان مان ٻار بلوغت کي پھچن تڏھن کين اھڙي طرح موڪل وٺڻ گھرجي جھڙي طرح انھن (ڇوڪرن) کان اڳيان (وڏا) موڪل وٺندا آھن، اھڙي طرح الله اوھان لاءِ پنھنجا حُڪم بيان فرمائي ٿو، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(2851, 24, 60, '۽ جيڪي ٻڍيون زالون پرڻي جو ڪو آسرو نه رکنديون ھجن سي جيڪڏھن سينگارجي پڌري نه ڪرڻ جي ارادي سان پنھنجون چادرون نه ڍڪين ته مٿن گناھ ڪونھي، ۽ جيڪڏھن (اُن کان به) بچن ته اُنھن لاءِ ڀلو آھي، ۽ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(2852, 24, 61, 'انڌي تي ڪا اوکائي نه آھي ۽ نڪي منڊي تي اوکائي ۽ نڪي بيمار تي ڪا اوکائي ۽ نڪي خود اوھان (ماڻھن) تي (ڪا اوکائي) آھي جي اوھين پنھنجن گھرن (مان) يا پنھنجن پيُن جي گھرن يا پنھنجن مائرن جي گھرن يا پنھنجي ڀائرن جي گھرن يا پنھنجن ڀينرن جي گھرن يا پنھنجن چاچن جي گھرن يا پنھنجن پڦين جي گھرن يا پنھنجن مامن جي گھرن يا پنھنجي ماسين جي گھرن يا جن جي گھرن جون ڪنجيون اوھان جي ھٿ آھن تن ۽ پنھنجن دوستن جي گھرن مان کائو، گڏجي (کائڻ) يا ڌار ڌار ٿي کائڻ ۾ اوھان تي ڪو گناھ ڪونھي، پوءِ جڏھن گھرن ۾ گھڙو تڏھن پنھنجن ماڻھن تي سلام ڏيو جو (اھا) الله وٽان برڪت واري سٺي دُعا آھي، اھڙي طرح الله اوھان لاءِ حُڪم بيان ڪندو آھي ته مانَ اوھين سمجھو.'),
(2853, 24, 62, 'مؤمن رڳا اُھي آھن جن الله ۽ سندس پيغمبر تي ايمان آندو آھي ۽ جڏھن گڏ ڪَرڻ واري ڪنھن ڪم ۾ ساڻس ھوندا آھن (تڏھن) ايسين ڪيڏي نه ويندا آھن جيسين کانئس موڪل (نه) وٺندا آھن، بيشڪ (اي پيغمبر) جيڪي توکان موڪلائيندا آھن سي اُھي آھن جي الله ۽ سندس پيغمبر کي مڃيندا آھن، پوءِ جڏھن پنھنجن ڪن ڪمن لاءِ توکان موڪلائين تڏھن انھن مان جنھن لاءِ گھرين تنھن کي موڪل ڏي ۽ اُنھن لاءِ الله کان بخشش گُھر، ڇوته الله بخشڻھار مھربان آھي.'),
(2854, 24, 63, 'اوھين پاڻ ۾ پيغمبر کي سڏڻ اھڙي طرح نه ڀانيو جھڙيءَ طرح اوھان جو ھڪ ٻئي کي سڏڻ آھي بيشڪ الله انھن کي ڄاڻندو آھي جيڪي اوھان مان ٻي جي اوٽ ڪري کسڪي ويندا آھن، پوءِ جيڪي سندس حڪم جي ابتڙ ھلندا آھن تن کي پاڻ تي بلا پھچڻ کان يا پاڻ تي ڪنھن ڏکوئيندڙ عذاب پھچڻ کان ڊڄڻ گھرجي.'),
(2855, 24, 64, 'خبردار ھجو جيڪي آسمانن ۽ زمين ۾ آھي سو بيشڪ الله جو آھي، جنھن (حال) تي اوھين آھيو تنھن کي بيشڪ الله ڄاڻندو آھي، ۽ جنھن ڏينھن ڏانھس موٽايا ويندا (تنھن ڏينھن) جيڪي ڪمايائون تنھنجي کين سُڌ ڏيندو، ۽ الله سڀڪنھن شيء کي ڄاڻندڙ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'اھو (الله) وڏو برڪت وارو آھي جنھن پنھنجي ٻانھي تي (حق ۽ باطل جو) فرق ڪندڙ (قرآن) ھن لاءِ نازل ڪيو ته جھان وارن لاءِ ڊيڄاريندڙ ھجي.'),
(2857, 25, 2, '(اُھو آھي) جنھن کي آسمانن ۽ زمين جي بادشاھي آھي ۽ (پاڻ لاءِ) نڪو اولاد ورتائين ۽ نڪي بادشاھيءَ ۾ ساڻس ڪو شريڪ آھي ۽ سڀڪنھن شيء کي بڻايائين پوءِ ان جو اندازو پوري طرح ٺھرايائين.'),
(2858, 25, 3, '۽ (ڪافرن) الله کانسواءِ اُھي معبود ورتا آھن جو ڪجھ بڻائي نه سگھندا آھن ۽ اُھي پاڻ پيدا ڪيل آھن ۽ پاڻ لاءِ نڪي ڏک ۽ نڪي سُک جو اختيار اٿن ۽ نڪي موت ۽ نڪي حياتي ۽ نه وري جيئري ٿيڻ جو اختيار اٿن.'),
(2859, 25, 4, '۽ ڪافر چوندا آھن ته ھيءُ (قرآن) رڳو ڪوڙو قصّو آھي جو اُھو پاڻون ٺاھيو اٿس ۽ اُن (جي ٺاھڻ) تي ٻي قوم به کيس مدد ڏني آھي، (اھڙي طرح چوڻ سان) بيشڪ اُنھن ظلم ۽ ڪوڙ (ڪم ۾) آندو آھي.'),
(2860, 25, 5, '۽ چيائون ته (ھي قرآن) اڳين جا قصّا آھن جو اُھي مٿس صُبح ۽ سانجھيءَ پڙھبا آھن.'),
(2861, 25, 6, '(اي پيغمبر کين) چؤ ته اُھو (قرآن) اُنھي (الله) نازل ڪيو آھي جيڪو آسمانن ۽ زمين جو ڳجھ ڄاڻندو آھي، بيشڪ اُھو بخشڻھار مھربان آھي.'),
(2862, 25, 7, '۽ (ڪافر) چون ٿا ته ھن پيغمبر کي ڇا (ٿيو) آھي جو طعام کائيندو آھي ۽ بازارين ۾ گھمندو آھي، ڏانھس ملائڪ ڇو نه نازل ڪيو ويو آھي ته اُھو ساڻس (گڏ) ڊيڄاريندڙ ھجي.'),
(2863, 25, 8, 'يا ڏانھس (آسمان کان) خزانو ڇو نه اُڇليو ويو يا اُن کي ڪو باغ ڇو نه آھي جو منجھانئس کائي، ۽ ظالم چوندا آھن ته اوھين رڳو جادو ڪيل ماڻھو جي تابعداري ڪندا آھيو.'),
(2864, 25, 9, '(اي پيغمبر) ڏس ته تو لاءِ ڪھڙي طرح جون ڳالھيون ڪندا آھن پوءِ (اُھي) گمراھ ٿيا ۽ (ھاڻي) ڪا واٽ لھي نٿا سگھن.'),
(2865, 25, 10, 'اُھو (الله) وڏو برڪت وارو آھي جيڪڏھن گھري ته (دنيا ۾) تولاءِ ھن (چيل) کان ڀلا باغ جن جي ھيٺان واھيون پيون وھن ڏيئي ۽ توکي وڏيون ماڙيون (به) ڏئي.'),
(2866, 25, 11, 'بلڪ قيامت کي ڪُوڙو ڀانيو اٿن ۽ جنھن قيامت کي ڪوڙو ڀانيو تنھن لاءِ دوزخ تيار ڪيو اٿئون.'),
(2867, 25, 12, 'جڏھن (دوزخ) انھن کي ڏوراھين پنڌ کان ڏسندو (تڏھن) اُن جون ڪاوڙ جون ھڪلون ۽ گجڪار ٻڌندا.'),
(2868, 25, 13, '۽ جڏھن اُتان ھٿ پير جڪڙيل سوڙھي ھنڌ سٽيا ويندا (تڏھن) اُتي (پاڻ لاءِ) ھلاڪ ٿيڻ کي سڏيندا.'),
(2869, 25, 14, '(چيو ويندن ته) اڄ اوھين ھڪ ھلاڪ ٿيڻ کي نه پڪاريو پر گھڻن ھلاڪتن کي پُڪاريو.'),
(2870, 25, 15, '(اي پيغمبر کين) چؤ ته اُھو ڀلو آھي يا اُھو ھميشه وارو بھشت جنھن جو پرھيزگارن کي انجام ڏنو ويو آھي؟ جو اُنھن لاءِ بدلو ۽ موٽڻ جو ھنڌ ھوندو.'),
(2871, 25, 16, 'جيڪي گھرندا سو انھن لاءِ منجھس سدائين (موجود) ھوندو، تنھنجي پالڻھار کان (اھو) وعدو گھريل آھي.'),
(2872, 25, 17, '۽ اُن ڏينھن کين ۽ جن کي الله کانسواءِ پوڄيندا آھن تن کي الله گڏ ڪندو پوءِ چوندو ته اوھان ھنن منھنجن ٻانھن کي گمراھ ڪيو ھو يا اُھي پاڻ واٽ کان گمراھ ٿيا ھوا؟'),
(2873, 25, 18, 'چوندا ته تنھنجي پاڪائي بيان ٿا ڪريون تو کانسواءِ اسان کي ڪو دوست وٺڻ نه جڳائيندو ھو پر انھن کي ۽ سندن پين ڏاڏن کي آسودو ڪيئي تان جو نصيحت وساريائون، ۽ ھلاڪ ٿيل قوم ٿيا.'),
(2874, 25, 19, 'پوءِ (چونداسون ته اي ڪافرؤ) بيشڪ جيڪي (ڳالھيون ھنن معبودن بابت) چَيوَ ٿي تن ۾ اُنھن اوھان کي ڪُوڙو ڪيو پوءِ (اوھين) نڪي عذاب ٽاري سگھو ٿا ۽ نڪي مدد وٺي سگھو ٿا، ۽ اوھان مان جنھن ظلم ڪيو ھوندو تنھن کي وڏو عذاب چکائينداسون.'),
(2875, 25, 20, '۽ توکان اڳ پيغمبرن مان جيترا به موڪلياسون سي (به تو ئي وانگر) طعام کائيندا ھوا ۽ بازارين ۾ گھمندا ھوا، ۽ اوھان کي (پاڻ ۾) ھڪ ٻئي لاءِ پرک ڪيو اٿئون، (ته ڏسون ته) صبر ڪريو ٿا (يا نه)؟ ۽ تنھنجو پالڻھار ڏسندڙ آھي.'),
(2876, 25, 21, '۽ جيڪي اسان جي ملڻ جي اُميد نه رکندا آھن سي چوندا آھن ته اسان تي ڇو نه ملائڪ نازل ڪيا ويا يا پنھنجي پالڻھار کي (ڇو نه ٿا) ڏسون؟ بيشڪ اُنھن پنھنجن جيئن ۾ وڏائي ڪئي ۽ تمام وڏو ھٺ ڪيائون.'),
(2877, 25, 22, 'جنھن ڏينھن ملائڪن کي ڏسندا تنھن ڏينھن ڏوھارين لاءِ ڪا خوشخبري نه آھي ۽ چوندا ته (الله ڪري اوھين شال) گھڻو پري ھجو.'),
(2878, 25, 23, '۽ جيڪو ڪو عمل ڪيائون تنھن ڏانھن اسين مھاڙ ڪنداسون پوءِ اُن کي پڻي ڪري ڇڏينداسون.'),
(2879, 25, 24, 'اُنھيءَ ڏينھن بھشتي رھڻ جي جاءِ ڪري ڀلا ۽ آرام ڪرڻ جي جاءِ ڪري تمام چڱا ھوندا.'),
(2880, 25, 25, '۽ جنھن ڏينھن آسمان ڪڪرن سان ڦاٽندو ۽ ملائڪ لڳو لڳ لاھبا.'),
(2881, 25, 26, 'اُن ڏينھن سچي حڪومت الله جي آھي، ۽ اُھو ڏينھن ڪافرن تي سخت ٿيندو.'),
(2882, 25, 27, '۽ جنھن ڏينھن ظالم پنھنجا ٻئي ھٿ چٻيندو چوندو ته ھَيء اَرمان (جيڪر) پيغمبر سان گڏ واٽ وٺان ھا (ته چڱو ھو).'),
(2883, 25, 28, 'ھَيء ھَيء ارمان جيڪر فلاڻي کي دوست نه وٺان ھا.'),
(2884, 25, 29, 'بيشڪ جڏھن مون وٽ نصيحت آئي تنھن پڄاڻا ھن مون کي اُن کان گمراہ ڪيو، ۽ شيطان ماڻھوءَ کي (وقت تي) دغا ڏيندڙ آھي.'),
(2885, 25, 30, '۽ پيغمبر چوندو ته اي منھنجا پالڻھاربيشڪ منھنجي قوم ھن قرآن کي ڇڏي ڏنو ھو.'),
(2886, 25, 31, '۽ اھڙيءَ طرح سڀڪنھن پيغمبر لاءِ ڏوھارين مان ويري بڻاياسون، ۽ تنھنجو پالڻھار ھدايت ڪرڻ وارو ۽ مدد ڏيڻ وارو ڪافي آھي.'),
(2887, 25, 32, '۽ ڪافر چون ٿا ته پيغمبر تي سارو قرآن ھڪ ڀيري ڇو نه نازل ڪيو ويو؟ اھڙي طرح (ٿورو ٿورو ڪري لاٿوسون)، ته اُن سان تنھنجي دل کي مُحڪم ڪريون ۽ اُھو آھستو آھستو پڙھي ٻڌايوسون.'),
(2888, 25, 33, '۽ (ڪافر) تو وٽ اھڙي ڪا حُجّت نه ٿا آڻين جنھنجو اسين سچو (جواب) ۽ ڏاڍو چڱو بيان تو وٽ نه ٿا آڻيون.'),
(2889, 25, 34, 'جيڪي پنھنجي منھن ڀر دوزخ ڏانھن گڏ ڪيا ويندا اُھي جاءِ جي ڪري تمام بُڇڙا ۽ واٽ کي بلڪل وڃائيندڙ آھن.'),
(2890, 25, 35, '۽ بيشڪ مُوسىٰ کي ڪتاب ڏنوسون ۽ ساڻس سندس ڀاءُ ھارون وزير ڪيوسون.'),
(2891, 25, 36, 'پوءِ چيوسون ته (ٻئي) اُنھي قوم ڏانھن وڃو جن اسان جي آيتن کي ڪوڙ ڀانيو، پوءِ (آخر) کين پاڙئون پٽيوسون.'),
(2892, 25, 37, '۽ نوح جي قوم جڏھن پيغمبرن کي ڪُوڙو ڄاتو (تڏھن) کين ٻوڙيوسون ۽ کين ماڻھن لاءِ نشاني ڪئي سون، ۽ ظالمن لاءِ ڏکوئيندڙ عذاب تيار ڪيوسون.'),
(2893, 25, 38, '۽ عادين ۽ ثمودين ۽ رَس وارن ۽ اُنھن جي وچ ۾ گھڻن جُڳن کي (ناس ڪيوسون).'),
(2894, 25, 39, '۽ ھر ھڪ کي بيان ڪري ٻڌايوسون، ۽ سڀني کي ناس ڪيوسون (جئن) ناس ڪرڻ گھرجي.'),
(2895, 25, 40, '۽ بيشڪ (ڪافر) اُن ڳوٺ وٽان ٿي آيا آھن جنھن تي (پٿرن جو) بڇڙو مينھن وسايو ويو ھو، اُن کي نه ٿي ڏٺائون ڇا؟ بلڪ اُھي وري جيئري ٿيڻ جي اُميد نه رکندا ھوا.'),
(2896, 25, 41, '۽ جڏھن توکي ڏسن ٿا (تڏھن) توکي رڳو ٺـٺولي ڪري وٺن ٿا، (چون ٿا ته) ھيءُ اُھو (شخص) آھي ڇا جنھن کي الله پيغمبر ڪري موڪليو آھي.'),
(2897, 25, 42, 'بيشڪ اسان کي پنھنجن بتن (جي پوڄا) کان تڏھن ئي ڀلائي ڇڏي ھا جيڪڏھن اسين مٿن مُحڪم نه رھون ھا، ۽ جڏھن عذاب ڏسندا (تڏھن) ڄاڻندا ته واٽ کي بلڪل وڃائيندڙ ڪير آھي.'),
(2898, 25, 43, '(اي پيغمبر) تو اُن کي ڏٺو جنھن پنھنجي سَڌ کي پنھنجو معبود ڪري ورتو؟ پوءِ تون مٿس ڪو نگھبان ٿيندين ڇا؟'),
(2899, 25, 44, 'ڀانيو اٿئي ڇا ته منجھانئن گھڻا ٻڌن ٿا يا سمجھن ٿا؟ اِھي رڳو ڍورن وانگر آھن بلڪ اُھي واٽ کي بلڪل وڃائيندڙ آھن.'),
(2900, 25, 45, '(اي پيغمبر) پنھنجي پالڻھار ڏانھن نه ڏٺو اٿئي ڇا ته پاڇي کي ڪيئن ڊگھو ڪيائين؟ جيڪڏھن گھري ھا ته اُن کي ٺھرائي ڇڏي ھا، وري سج کي مٿس (پنھنجي قدرت جو) دليل ڪيوسون.'),
(2901, 25, 46, 'وري اُن (پاڇي) کي پاڻ ڏانھن ھوريان ھوريان سوڙھو ڪيوسون (جئن) سوڙھو ڪرڻ گھرجي.'),
(2902, 25, 47, '۽ (الله) اُھو آھي جنھن اوھان لاءِ رات کي پردو ۽ ننڊ کي آرام ڪيو ۽ ڏينھن کي اُٿڻ جو وقت ڪيائين.'),
(2903, 25, 48, '۽ (الله) اُھو آھي جنھن پنھنجي ٻاجھ (يعني مينھن) کان اڳي وائُن کي خوشخبري ڏيندڙ ڪري موڪليو، ۽ آسمان مان پاڪ پاڻي (ھن لاءِ) وسايوسون'),
(2904, 25, 49, 'ته اُن سان ويران شھر کي آباد ڪريون ۽ ته پنھنجن پيدا ڪيل ڍورن ۽ گھڻن ماڻھن کي اُھو پياريون.'),
(2905, 25, 50, '۽ بيشڪ اُن (مينھن) کي سندن وچ ۾ ھن ڪري ڦيرائيندڙ آھيون ته نصيحت وٺن، پوءِ گھڻا ماڻھو بي شڪريءَ کانسواءِ نٿا رھن.'),
(2906, 25, 51, '۽ جيڪڏھن گھرون ھا ته سڀڪنھن ڳوٺ ۾ ھڪ ڊيڄاريندڙ موڪليون ھا.'),
(2907, 25, 52, 'پوءِ تون ڪافرن جو چيو نه مڃ ۽ اُن (قرآن) جي حُڪم سان (مقابلي طرح) ساڻن وڏو جھاد ڪر.'),
(2908, 25, 53, '۽ (الله) اُھو آھي جنھن ٻن دريائن کي پاڻ ۾ ملايو جو ھي (ھڪڙو) مِٺو اُڃ لاھيندڙ ۽ ھيءُ (ٻيو) لوڻاٺيو کارو آھي، ۽ سندن وچ ۾ اوٽ ۽ مُحڪم پردو بڻايائين.'),
(2909, 25, 54, '۽ (الله) اُھو آھي جنھن پاڻيءَ مان ماڻھوءَ کي بڻايو پوءِ کيس پيڙھي ۽ سيڻپيءَ جي مائٽي وارو ڪيائين، ۽ تنھنجو پالڻھار وس وارو آھي.'),
(2910, 25, 55, '۽ الله کان سواءِ اھڙن جي پوڄا ڪندا آھن جي کين نڪي سُک ڏين ۽ نڪي کين ڏک پھچائن، ۽ ڪافر پنھنجي پالڻھار کي پُٺي ڏيڻ وارو آھي.'),
(2911, 25, 56, '۽ (اي پيغمبر) اسان توکي رڳو خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو آھي.'),
(2912, 25, 57, '(کين) چؤ ته آءٌ اوھان کان اُن (قرآن جي پھچائڻ) تي ڪو اُجورو نٿو گھران پر جيڪو پنھنجي پالڻھار ڏانھن واٽ وٺڻ گھري (سو ڀلي ته وٺي).'),
(2913, 25, 58, '۽ (اي پيغمبر تون اُنھيءَ الله) جيئري تي ڀروسو ڪر جيڪو (ڪڏھن) نه مرندو ۽ سنديس ساراہ سان گڏ پاڪائي بيان ڪر، ۽ اُھو پنھنجن ٻانھن جي گناھن جي خبر رکندڙ ڪافي آھي.'),
(2914, 25, 59, 'جنھن آسمانن ۽ زمين کي ۽ جيڪي سندن وچ ۾ آھي (سو) ڇھن ڏينھن ۾ بڻايو وري عرش تي قائم ٿيو، (اُھو) ٻاجھارو آھي پوءِ سندس (صفتون) ڪنھن خبر رکندڙ کان پُڇ.'),
(2915, 25, 60, '۽ جڏھن کين چئبو آھي ته رحمٰن کي سجدو ڪريو، (تڏھن) چوندا آھن ته رَحمٰن ڇا آھي جنھن لاءِ اسان کي حُڪم ٿو ڪرين؟ تنھن کي سجدو ڪريون ڇا؟ ۽ (اُنھي ڳالھ ھٿئون) سندن نفرت وڌائي.'),
(2916, 25, 61, 'اُھو (الله) وڏي برڪت وارو آھي جنھن آسمان ۾ برج بڻايائين ۽ منجھس ڏيو (يعني سِج) ۽ چمڪندڙ چنڊ بڻايو.'),
(2917, 25, 62, '۽ (الله) اُھو آھي جنھن رات ۽ ڏينھن کي ھڪ ٻئي جي پوئتان ايندڙ اُنھيءَ (ماڻھو) لاءِ بڻايو جيڪو نصيحت وٺڻ گھري يا شڪرانو گھري.'),
(2918, 25, 63, '۽ ٻاجھاري (الله) جا ٻانھا اُھي آھن جيڪي زمين تي نِوَڙت سان گھمندا آھن ۽ جڏھن بي عقل ساڻن ڳالھائيندا آھن (تڏھن) چوندا آھن ته سلامت ھجو (شال).'),
(2919, 25, 64, '۽ اُھي جيڪي پنھنجي پالڻھار کي سجدو ڪندڙ ۽ قيام ڪندڙ ٿي راتيون گذاريندا آھن.'),
(2920, 25, 65, '۽ اُھي جيڪي چوندا آھن ته اي اسان جا پالڻھار اسان کان دوزخ جو عذاب ٽار، بيشڪ دوزخ جو عذاب (وڏي) چَٽي آھي.'),
(2921, 25, 66, 'بيشڪ اُھو آرام ڪرڻ جو ھنڌ ۽ رھڻ جو ھنڌ بڇڙو آھي.'),
(2922, 25, 67, '۽ اُھي جڏھن خرچ ڪندا آھن (تڏھن) نڪي اجايو خرچيندا آھن ۽ نڪي تنگي ڪندا آھن ۽ انھن (ٻنھي) جي وچ ۾ پورا ھلندا آھن.'),
(2923, 25, 68, '۽ اُھي جيڪي الله ساڻ ڪنھن ٻئي معبود کي نه پوڄيندا آھن ۽ نڪي اھڙي جيءَ کي ماريندا آھن جنھنجو حق کانسواءِ مارڻ الله حرام ڪيو آھي ۽ نڪي زنا ڪندا آھن، ۽ جيڪو اُھو ڪم ڪندو سو سخت عذاب لھندو.'),
(2924, 25, 69, 'قيامت جي ڏينھن اُن کي ٻيڻو عذاب ڪبو ۽ منجھس سدائين خوار رھندو.'),
(2925, 25, 70, 'پر جن توبه ڪئي ۽ ايمان آندو ۽ چڱا ڪم ڪيا سي اُھي آھن جن جون مدايون الله ڦيرائي نيڪيون ڪندو، ۽ الله بخشڻھار مھربان آھي.'),
(2926, 25, 71, '۽ جيڪو توبه ڪري ۽ چڱا ڪم ڪري سو بيشڪ الله ڏانھن وري موٽي ٿو.'),
(2927, 25, 72, '۽ اُھي جيڪي ڪوڙي شاھدي نه ڏيندا آھن ۽ جڏھن بيھودي ڪم تي لنگھندا آھن (تڏھن چڱي ريت) ڀلارن وانگر لنگھندا آھن.'),
(2928, 25, 73, '۽ اُھي جو جڏھن کين سندن پالڻھار جي آيتن سان نصيحت ڏبي آھي (ته) اُن (جي ٻڌڻ) سان ٻوڙا ۽ انڌا ٿي نه ڪرندا آھن.'),
(2929, 25, 74, '۽ اُھي جيڪي چوندا آھن ته اي اسانجا پالڻھار اسان جي زالن ۽ اسان جي اولاد مان اسان کي اکين جو ٺار بخش ۽ اسان کي پرھيزگارن جو اِمام ڪر.'),
(2930, 25, 75, 'اِھي (اُھي) آھن جن کي سندن صبر سببان وڏيون ماڙيون عوض ۾ ڏبيون ۽ اُن ۾ چڱي دُعا ۽ سلام پھچائبن.'),
(2931, 25, 76, 'منجھس سدائين رھڻ وارا آھن، اُھو آرام ڪرڻ جو ھنڌ چڱو ۽ رھڻ جو ھنڌ چڱو آھي.'),
(2932, 25, 77, '(اي پيغمبر کين) چؤ ته جيڪڏھن (الله کي) اوھان جو سڏڻ نه ھجي ته منھنجي پالڻھار کي اوھان جي ڪا پرواھ نه آھي، بيشڪ اوھان (پيغمبر کي) ڪوڙو ڀانيو تنھنڪري سگھو اوھان لاءِ عذاب لازم ٿيندو.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طٰسٓمّٓ.'),
(2934, 26, 2, 'ھي پڌري ڪتاب جون آيتون آھن.'),
(2935, 26, 3, 'متان تون پاڻ کي ھن سببان گھٽي ماريندڙ ٿئين ته (ڪافر) مسلمان نه ٿا ٿين.'),
(2936, 26, 4, 'جيڪڏھن اسين گھرون ته آسمان کان مٿن ڪا (اھڙي) نشاني نازل ڪريون جنھن جي آڏو سندن گردنون جھڪيون ٿين.'),
(2937, 26, 5, '۽ ٻاجھاري (الله) کان ڪائي نئين نصيحت وٽن نه ايندي آھي جو کانئس منھن موڙيندڙ (نه) آھن.'),
(2938, 26, 6, 'پوءِ بيشڪ ڪُوڙو ڀانئي چُڪا تنھنڪري سگھوئي وٽن اُھي خبرون اينديون جن سان اُھي ٺـٺوليون ڪندا ھوا.'),
(2939, 26, 7, 'زمين ڏانھن نه ڏٺو اٿن ڇا ته منجھس ھر ڪنھن جنس جا ڪيترائي سُٺا اوڀڙ ڄماياسون؟'),
(2940, 26, 8, 'بيشڪ اُن ۾ نشاني آھي، ۽ منجھانئن گھڻا مڃڻ وارا نه آھن.'),
(2941, 26, 9, '۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.'),
(2942, 26, 10, '۽ جڏھن تنھنجي پالڻھار موسىٰ کي سڏيو ته ظالمن جي قوم ڏانھن وڃ.'),
(2943, 26, 11, 'جا فرعون جي قوم آھي، (کانئن پُڇ) ته پرھيزگاري نه ڪندا ڇا؟'),
(2944, 26, 12, 'مُوسىٰ چيو ته اي منھنجا پالڻھار بيشڪ آءٌ ڊڄان ٿو ته متان مونکي ڪُوڙو ڀانئين.'),
(2945, 26, 13, '۽ منھنجو سينو تنگ ٿيندو آھي ۽ منھنجي زبان به پوري نه اُٿلندي آھي تنھنڪري ھارون ڏانھن (پيغمبريءَ جو حُڪم) موڪل.'),
(2946, 26, 14, '۽ انھن جو مون تي ڏوھ به آھي تنھنڪري ڊڄان ٿو ته متان مون کي ماري ڇڏين.'),
(2947, 26, 15, '(الله) فرمايو ته ايئن (ٿيڻو) نه آھي، پوءِ منھنجي نشانين سان ٻئي (ڄڻا) وڃو بيشڪ اسين اوھان سان گڏ ٻڌندڙ آھيون.'),
(2948, 26, 16, 'تنھنڪري فرعون وٽ وڃو ۽ چئوس ته بيشڪ اسين جھانن جي پالڻھار جا (ھن پيغام سان) موڪليل آھيون.'),
(2949, 26, 17, 'ته بني اسرائيل کي اسان سان موڪل.'),
(2950, 26, 18, 'فرعون چيو ته توکي پنھنجن (ڀاتين) ۾ ننڍڙو ڪري نه پاليو ھوسون ڇا؟ ۽ پنھنجي عمر جا ڪيترائي ورھ اسان ۾ گذاريئي.'),
(2951, 26, 19, '۽ تو پنھنجو اُھو ڪم ڪيو جو تون ڪري چڪين ۽ تون بي شڪرن مان آھين.'),
(2952, 26, 20, '(موسى) چيو ته جنھن مھل اُھو ڪم ڪيو ھوم (تنھن مھل) آءٌ بي سمجھن مان ھوس.'),
(2953, 26, 21, 'پوءِ جنھن مھل اوھان (جي ايذاءَ) کان ڊنس تنھن مھل اوھان کان ڀڳس پوءِ منھنجي پالڻھار مون کي سياڻپ بخشي ۽ مون کي پيغمبرن مان ڪيائين.'),
(2954, 26, 22, '۽ اِھو اُھو احسان آھي ڇا جنھنجو مون تي ٿورو رکين ٿو؟ ته بني اسرائيلن کي ٻانھون ڪري ورتو اٿئي.'),
(2955, 26, 23, 'فرعون چيو ته جھانن جو پاليندڙ ڇا (کي چئبو) آھي؟'),
(2956, 26, 24, '(موسى) چيو ته آسمانن ۽ زمين جو ۽ جيڪي سندن وچ ۾ آھي تنھنجو پالڻھار آھي، جيڪڏھن اوھين ويساہ رکندا آھيو.'),
(2957, 26, 25, '(فرعون) پنھنجي آس پاس وارن کي چيو ته (اوھين) نه ٿا ٻُڌو ڇا؟'),
(2958, 26, 26, '(مُوسى) چيو ته (اُھو) اوھان جو پالڻھار ۽ اوھان جي ابن ڏاڏن جو پالڻھار آھي.'),
(2959, 26, 27, '(فرعون) چيو ته اوھان جو پيغمبر جيڪو اوھان ڏانھن موڪليو ويو آھي سو بيشڪ چريو آھي.'),
(2960, 26, 28, '(مُوسى) چيو ته اوڀر ۽ اولھ ۽ جيڪي انھن ٻنھي جي وچ ۾ آھي تنھنجو (به) پالڻھار آھي، جيڪڏھن اوھين سمجھ رکندا آھيو (ته اِھا سمجھاڻي بس آھي).'),
(2961, 26, 29, '(فرعون) چيو ته جيڪڏھن مون کانسواءِ ڪو ٻيو خدا ورتئي ته ضرور توکي قيدين مان ڪندس.'),
(2962, 26, 30, '(مُوسى) چيو ته جيتوڻيڪ تو وٽ پڌري شيء آڻيان ته به (قيد ڪندين) ڇا؟'),
(2963, 26, 31, '(فرعون) چيو ته جيڪڏھن تون سَچن مان آھين ته اُھا آڻ.'),
(2964, 26, 32, 'پوءِ پنھنجي لَٺ اُڇليائين ته اُھا اُمالڪ پڌرو واسينگ (نانگ) ٿي پئي.'),
(2965, 26, 33, '۽ پنھنجو ھٿ ڪڍيائين ته اوچتو اُھو ڏسندڙن لاءِ چمڪندڙ ٿي پيو.'),
(2966, 26, 34, '(فرعون) پنھنجن آس پاس وارن سردارن کي چيو ته بيشڪ ھي شخص داناءُ جادوگر آھي.'),
(2967, 26, 35, '(ھو) اوھان کي اوھان جي ملڪ مان پنھنجي جادوءَ سان لوڌڻ گھري ٿو پوءِ مون کي (ھاڻي) ڇا ٿا حُڪم ڪريو؟'),
(2968, 26, 36, 'چيائون ته کيس ۽ سندس ڀاءُ کي ترساءِ ۽ شھرن ۾ ڪوٺيندڙ کي موڪل.'),
(2969, 26, 37, 'ته سڀڪنھن ڄاڻندڙ وڏي جادوگر کي کي تو وٽ آڻين.'),
(2970, 26, 38, 'پوءِ ھڪ ڏينھن ٺھرايل جي انجام تي جادوگر گڏ ڪيا ويا.'),
(2971, 26, 39, '۽ ماڻھن کي چيو ويو ته اوھين (به) گڏ ٿيڻ وارا آھيو ڇا؟'),
(2972, 26, 40, 'جيڪڏھن اُھي (جادوگر) غالب ٿيا ته مانَ اسين انھن جا تابعدار ٿيون.'),
(2973, 26, 41, 'پوءِ جنھن مھل جادوگر آيا (تنھن مھل) فرعون کي چيائون ته جيڪڏھن اسين ڏاڍا ٿيون ته اسان لاءِ ڪو انعام آھي (يا نه!).'),
(2974, 26, 42, '(فرعون) چيو ته ھائو ۽ بيشڪ اوھين اُنھيءَ مھل (منھنجن) ويجھن مان ٿيندؤ.'),
(2975, 26, 43, 'کين مُوسىٰ چيو ته جيڪي اوھين اڇلڻ وارا آھيو سو اُڇليو.'),
(2976, 26, 44, 'پوءِ ھنن پنھنجا رسا ۽ پنھنجيون لٺيون اڇليون ۽ چيائون ته فرعون جي عزت جو قَسم آھي ته بيشڪ اسين ئي غالب ٿينداسون.'),
(2977, 26, 45, 'پوءِ مُوسىٰ پنھنجي لَٺ اُڇلي ته اُھا اُتي جو اُتي جيڪو سانگ بڻائيندا ويا سو ڳھندي وئي.'),
(2978, 26, 46, 'پوءِ جادوگر سجدو ڪندڙ ٿي نِويا.'),
(2979, 26, 47, 'چيائون ته جھانن جي پالڻھار تي ايمان آندوسون.'),
(2980, 26, 48, '(جو) مُوسىٰ ۽ ھارون جو پالڻھار آھي.'),
(2981, 26, 49, '(فرعون) چيو ته اُن کان اڳ جو اوھان کي موڪل ڏيان اوھان موسىٰ تي ڇو ايمان آندو، بيشڪ اُھو اوھان جو وڏو آھي جنھن اوھان کي جادو سيکاريو، پوءِ سگھو ڄاڻندؤ، اوھان جا ھڪ پاسي جا ھٿ ۽ اوھان جا ٻئي پاسي جا پير ضرور وڍيندس ۽ اوھان مڙني کي (گڏ) ضرور سوريءَ تي چاڙھيندس.'),
(2982, 26, 50, 'چيائون ته ڪا حرڪت نه آھي بيشڪ اسين پنھنجي پالڻھار ڏانھن موٽڻ وارا آھيون.'),
(2983, 26, 51, 'بيشڪ اسين اميد ٿا رکون ٿا ته اسان جو پالڻھار اسان جا ڏوھ اسان کي بخشيندو انھي ڪري جو (پھرين اسين) مُسلمان ٿيا آھيون.'),
(2984, 26, 52, '۽ موسىٰ ڏانھن وحي ڪيوسون ته منھنجن ٻانھن کي راتو واہ وٺي نِڪر ڇو ته (فرعون ۽ سندس لشڪر) اوھان جي پٺيان پوندا.'),
(2985, 26, 53, 'پوءِ فرعون شھرن ۾ ڪوٺيندڙ موڪليا.'),
(2986, 26, 54, '(چيائين) ته بيشڪ ھيء (ماڻھو) ٿوري ٽولي آھن.'),
(2987, 26, 55, '۽ بيشڪ انھن اسان کي ڪاوڙايو آھي.'),
(2988, 26, 56, '۽ اسين ھٿيارن واري جماعت آھيون.'),
(2989, 26, 57, '(الله فرمائي ٿو ته) پوءِ انھن (فرعونين) کي باغن ۽ چشمن مان ٻاھر ڪڍيوسون.'),
(2990, 26, 58, '۽ خزانن ۽ چڱي جاءِ مان (به).'),
(2991, 26, 59, 'اھڙي طرح (ڪيوسون)، ۽ انھن (شين) جو بني اسرائيلن کي وارث ڪيوسون.'),
(2992, 26, 60, 'پوءِ سج اُڀرندي ئي اُنھن (بني اسرائيلن) جي پوئتان پيا.'),
(2993, 26, 61, 'پوءِ جنھن مھل ٻنھي ٽولين ھڪ ٻئي ڏٺو (تنھن مھل) مُوسىٰ جي سنگتين چيو ته بيشڪ اسان کي پھچي ويا.'),
(2994, 26, 62, '(موسى) چيو ته ايئن نه آھي، بيشڪ منھنجو پالڻھار مون ساڻ آھي مون کي رستي لائيندو.'),
(2995, 26, 63, 'پوءِ مُوسىٰ ڏانھن وحي ڪيوسون ته پنھنجي لٺ سمنڊ کي ھڻ، پوءِ اُھو چيرجي پيو پوءِ سڀڪو ڀاڱو وڏي پھاڙ جھڙو ٿيو.'),
(2996, 26, 64, '۽ انھن ٻـين (فرعونين) کي اُن ھنڌ ويجھو ڪيوسون.'),
(2997, 26, 65, '۽ مُوسىٰ ۽ سندس سنگتين مڙني کي بچايوسين.'),
(2998, 26, 66, 'وري (ھنن) ٻـين (يعني فرعونين) کي ٻوڙيوسون.'),
(2999, 26, 67, 'بيشڪ اُنھي (قصي) ۾ نشاني آھي، ۽ انھن مان گھڻا مڃڻ وارا نه ھوا.'),
(3000, 26, 68, '۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.'),
(3001, 26, 69, '۽ اُنھن کي ابراھيم جو قِصّو پڙھي ٻڌاءِ.'),
(3002, 26, 70, 'جڏھن پنھنجي پيءُ ۽ پنھنجي قوم کي چيائين ته ڇا کي پوڄيندا آھيو؟'),
(3003, 26, 71, 'چيائون ته (اسين) بُتن کي پوڄيندا آھيون پوءِ سارا ڏينھن انھن جا مجاور ٿي وھندا آھيون.'),
(3004, 26, 72, '(ابراھيم) چيو ته جڏھن (ھنن کي) سڏيو ٿا (تڏھن) اوھان جو (سڏ) ٻڌن ٿا ڇا؟'),
(3005, 26, 73, 'يا اوھان کي سُک پھچائيندا آھن يا ڏک ڏيندا آھن؟'),
(3006, 26, 74, 'چيائون (نه!) بلڪ پنھنجن ابن ڏاڏن کي ائين ڪندي ڏٺوسون.'),
(3007, 26, 75, '(ابراھيم) چيو ته (انھن کي) ڏٺو اٿوَ جن کي پوڄيندا رھيؤ؟'),
(3008, 26, 76, 'اوھين ۽ اوھان جا اڳيان پيءُ ڏاڏا.'),
(3009, 26, 77, 'بيشڪ اُھي جھانن جي پالڻھار کانسواءِ منھنجا ويري آھن.'),
(3010, 26, 78, 'جنھن مون کي پيدا ڪيو آھي اُھو ئي مون کي سڌو رستو ڏيکاريندو آھي.'),
(3011, 26, 79, '۽ اُھو آھي جو مون کي کارائيندو آھي ۽ مون کي پياريندو آھي.'),
(3012, 26, 80, '۽ جڏھن بيمار ٿيندو آھيان تڏھن مون کي شفا ڏيندو آھي.'),
(3013, 26, 81, '۽ اُھو آھي جو مون کي ماريندو ۽ وري مون کي جياريندو.'),
(3014, 26, 82, '۽ اُھو آھي جو آسرو اٿم ته قيامت جي ڏينھن منھنجون خطائون مون کي بخشيندو.'),
(3015, 26, 83, 'اي منھنجا پالڻھار مُون کي دانائي بخش ۽ مون کي صالحن سان شامل ڪر.'),
(3016, 26, 84, '۽ پوين ۾ منھنجي چڱي يادگيري (قائم) ڪر.'),
(3017, 26, 85, '۽ مون کي نعمتن واري بھشت جي وارثن مان ڪر.'),
(3018, 26, 86, '۽ منھنجي پيءُ کي بخش جو اُھو گمراھن مان آھي.'),
(3019, 26, 87, '۽ جنھن ڏينھن (ماڻھو جيئرا ٿي) اُٿندا (تنھن ڏينھن) مون کي خوار نه ڪج.'),
(3020, 26, 88, 'جنھن ڏينھن نڪي مال ۽ نڪي پٽ نفعو ڏيندا.'),
(3021, 26, 89, 'پر اُھو (فائدي وارو ھوندو) جو الله وٽ سالم دل آڻيندو.'),
(3022, 26, 90, '۽ پرھيزگارن لاءِ بھشت ويجھو ڪيو ويندو.'),
(3023, 26, 91, '۽ گمراھن لاءِ دوزخ پڌرو ڪيو ويندو.'),
(3024, 26, 92, '۽ کين چيو ويندو ته اُھي ڪٿي آھن جي کي پوڄيندا ھيؤ.'),
(3025, 26, 93, 'الله ڌاران؟ (ھاڻي) اُھي اوھان کي مدد ڏين ٿا ڇا يا (پاڻ) بدلو وٺي سگھن ٿا.'),
(3026, 26, 94, 'پوءِ اُھي گمراہ (پوڄيندڙ) ۽ بُت منجھس اونڌا ڪري اُڇلايا ويندا.'),
(3027, 26, 95, '۽ مڙئي شيطاني لشڪر به (اڇلائبا).'),
(3028, 26, 96, '۽ (تڏھن) اُھي منجھس ھڪ ٻئي سان جھڳڙو ڪندي پيا چوندا.'),
(3029, 26, 97, 'ته الله جو قسم آھي بيشڪ اسين ته پڌريءَ گمراھي ۾ ھواسون.'),
(3030, 26, 98, 'جڏھن اوھان کي جھانن جي پالڻھار سان برابر ٿي ڪيوسون.'),
(3031, 26, 99, '۽ (ھنن) ڏوھارين کانسواءِ ٻئي ڪنھن اسان کي گمراہ نه ڪيو.'),
(3032, 26, 100, 'پوءِ اسان جو (ھاڻي) نڪو ڪو سفارش ڪرڻ وارو آھي.'),
(3033, 26, 101, '۽ نڪو ڪو غم ٽار دوست آھي.'),
(3034, 26, 102, 'پوءِ جيڪر ھڪ ڀيرو اسان کي (وري دنيا ۾ وڃڻ) ملي ته مؤمنن مان ٿيون.'),
(3035, 26, 103, 'بيشڪ اِن ۾ ضرور نشاني آھي، ۽ انھن مان گھڻا مؤمن نه آھن.'),
(3036, 26, 104, '۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.'),
(3037, 26, 105, 'نوح جي قوم پيغمبرن کي ڪوڙو ڀانيو.'),
(3038, 26, 106, 'جڏھن کين سندن ڀاءُ نوح چيو ته (الله کان) ڇونه ٿا ڊڄو.'),
(3039, 26, 107, 'بيشڪ آءٌ اوھان لاءِ امين پيغمبر آھيان.'),
(3040, 26, 108, 'پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.'),
(3041, 26, 109, '۽ اوھان کان اِن (پيغام جي پھچائڻ) تي ڪو اُجورو نٿو گھران، منھنجو اجورو ته رڳو جھانن جي پالڻھار تي آھي.'),
(3042, 26, 110, 'پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.'),
(3043, 26, 111, 'چيائون ته توتي ايمان ڇو آڻيون حالانڪ نيچ ماڻھن (ڪنھن دنياوي لالچ لاءِ) تنھنجي تابعداري ڪئي آھي.'),
(3044, 26, 112, '(نوح) چيو ته مون کي (سندن اندر جي) ڪھڙي سُڌ ته (ھو) ڇا (نيت) ڪندا رھندا آھن.'),
(3045, 26, 113, 'جيڪڏھن اوھين (سندين نيت) ڄاڻندا آھيو ته سندن حساب منھنجي پالڻھار کانسواءِ ٻئي ڪنھن تي نه آھي.'),
(3046, 26, 114, '۽ آءٌ مؤمنن کي تڙڻ وارو نه آھيان.'),
(3047, 26, 115, 'آءٌ ته رڳو پڌرو ڊيڄاريندڙ آھيان.'),
(3048, 26, 116, 'چيائون ته اي نوح جيڪڏھن تون نه رھندين ته ضرور سنگسار ٿيندين.'),
(3049, 26, 117, '(نوح) چيو ته اي منھنجا پالڻھار بيشڪ منھنجي قوم مون کي ڪوڙو ڀانيو.'),
(3050, 26, 118, 'تنھنڪري منھنجي ۽ سندن وچ ۾ چڱي طرح فيصلو ڪر ۽ مونکي ۽ جيڪي مؤمن مون ساڻ آھن تن کي بچاءِ.'),
(3051, 26, 119, 'پوءِ کيس ۽ سندس سنگتين کي ڀريل ٻيڙيءَ ۾ بچايوسون.'),
(3052, 26, 120, 'وري کانئن پوءِ رھيلن کي ٻوڙيوسون.'),
(3053, 26, 121, 'بيشڪ ان ۾ وڏي نشاني آھي، انھن مان گھڻا مڃيندڙ نه ھوا.'),
(3054, 26, 122, '۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.'),
(3055, 26, 123, 'عاد (جي قوم) پيغمبرن کي ڪوڙو ڄاتو.'),
(3056, 26, 124, 'جڏھن سندن ڀاءُ ھود کين چيو ته (الله کان) ڇونه ڊڄندا آھيو؟'),
(3057, 26, 125, 'بيشڪ آءٌ اوھان جو امين پيغمبر آھيان.'),
(3058, 26, 126, 'پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.'),
(3059, 26, 127, '۽ آءٌ اوھان کان اُن (پيغام پھچائڻ) تي ڪو اُجورو نٿو گھران، منھنجو اُجورو رڳو جھانن جي پالڻھار تي آھي.'),
(3060, 26, 128, 'سڀڪنھن دڙي تي اجايا مشغول ٿي نشان ڇو بڻائيندا آھيو؟'),
(3061, 26, 129, '۽ (ڇو) مُحڪم جايون بڻائيندا آھيو؟ ڄڻڪ اوھين سدائين رھندؤ.'),
(3062, 26, 130, '۽ جڏھن اوھين دست درازي ڪندا آھيو ته زبردست ٿي ڪندا آھيو.'),
(3063, 26, 131, 'پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.'),
(3064, 26, 132, '۽ انھي (الله) کان ڊڄو جنھن اوھان کي انھي (شيء) جي مدد ڏني جا (اوھين) ڄاڻندا آھيو.'),
(3065, 26, 133, 'اوھان کي ڍورن ۽ پُٽن (ڏيڻ) سان مدد ڏني اٿس.'),
(3066, 26, 134, '۽ باغن ۽ پاڻي جي چشمن (ڏيڻ) سان به.'),
(3067, 26, 135, 'بيشڪ آءٌ اوھان تي وڏي ڏينھن جي عذاب (پھچڻ) کان ڊڄان ٿو.'),
(3068, 26, 136, 'چيائون ته (جي) تون (اسان کي) نصيحت ڏئين يا نصيحت ڏيندڙن مان نه ھجين ته اسان لاءِ ھڪ جھڙو آھين.'),
(3069, 26, 137, 'اِھا اڳين جي ئي عادت آھي.'),
(3070, 26, 138, '۽ اسين (ڪڏھن) عذاب ڪيل نه ٿينداسون.'),
(3071, 26, 139, 'پوءِ کيس ڪوڙو ڀانيائون تنھنڪري کين ھلاڪ ڪيوسون، بيشڪ اِن (قصي) ۾ نشاني آھي، ۽ منجھانئن گھڻا مڃڻ وارا نه ھوا.'),
(3072, 26, 140, '۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.'),
(3073, 26, 141, 'ثمود (جي قوم) پيغمبرن کي ڪوڙو ڀانيو.'),
(3074, 26, 142, 'جڏھن کين سندن ڀاءُ صالح چيو ته (الله کان) ڇونه ڊڄندا آھيو؟'),
(3075, 26, 143, 'بيشڪ آءٌ اوھان لاءِ امين پيغمبر آھيان.'),
(3076, 26, 144, 'پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.'),
(3077, 26, 145, '۽ آءٌ اوھان کان اُن (پيغام پھچائڻ) تي ڪو اُجورو نه ٿو گھران، منھنجو اُجورو رڳو جھانن جي پالڻھار تي آھي.'),
(3078, 26, 146, 'اُھو جيڪي ھتي (دنيا ۾) آھي تنھن ۾ اوھان کي بي ڀئو ڇڏي ڏبو ڇا (ته ھميشه آرام ۾ رھو)؟'),
(3079, 26, 147, 'باغن ۽ چشمن ۾.'),
(3080, 26, 148, '۽ پوکن ۽ کجين ۾ جن جا گوشا ڪنئرا آھن.'),
(3081, 26, 149, '۽ ڪاريگر ٿي جبلن مان (تراشي) گھر بڻائيندا آھيو.'),
(3082, 26, 150, 'پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.'),
(3083, 26, 151, '۽ انھن حد کان لنگھندڙن جو چيو نه مڃيو.'),
(3084, 26, 152, 'جيڪي ملڪ ۾ بگيڙ وجھندا آھن ۽ سڌارا نه ڪندا آھن.'),
(3085, 26, 153, 'چيائون ته تون رڳو جادو ڪيلن مان آھين.'),
(3086, 26, 154, 'تون رڳو اسان جھڙو ماڻھو آھين، جيڪڏھن تون سچن مان آھين ته ڪا نشاني آڻ.'),
(3087, 26, 155, '(صالح) چيو ته ھيءَ ڏاچي آھي اُن جي (پاڻي) پيڻ لاءِ ۽ اوھان جي (پاڻي) پيڻ لاءِ واري جو ڏينھن ٺھرايل آھي.'),
(3088, 26, 156, '۽ اوھين کيس ڪو ايذاءُ نه پھچائجو نه ته اوھان کي وڏي ڏينھن جو عذاب پڪڙيندو.'),
(3089, 26, 157, 'پوءِ اُن جون کُچون ڪپيائون (ماري وڌائون) پوءِ پشيمان ٿيا.'),
(3090, 26, 158, 'پوءِ کين عذاب پڪڙيو، بيشڪ اُن ۾ نشاني آھي، ۽ منجھانئن گھڻا مڃڻ وارا نه ھوا.'),
(3091, 26, 159, '۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.'),
(3092, 26, 160, 'لُوط جي قوم پيغمبرن کي ڪُوڙو ڀانيو.'),
(3093, 26, 161, 'جڏھن کين سندن ڀاءُ لُوط چيو ته (الله کان) ڇونه ڊڄندا آھيو؟'),
(3094, 26, 162, 'بيشڪ آءٌ اوھان جو امين پيغمبر آھيان.'),
(3095, 26, 163, 'پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.'),
(3096, 26, 164, '۽ اُن (پيغام پھچائڻ) تي اوھان کان اُجورو نٿو گھُران، منھنجو اُجورو رڳو جھانن جي پالڻھار تي آھي.'),
(3097, 26, 165, 'جھانن جي مڙسن ڏانھن (بڇڙائيءَ لاءِ) ڇو ويندا آھيو؟'),
(3098, 26, 166, '۽ جيڪي اوھان جي پالڻھار اوھان جي زالن مان بڻايو سو ڇڏيندا آھيو؟ بلڪ اوھين حد کان لنگھندڙ قوم آھيو.'),
(3099, 26, 167, 'چيائون ته اي لُوط جيڪڏھن نه رھندين ته ضرور لوڌائبين.'),
(3100, 26, 168, 'لُوط چيو ته بيشڪ آءٌ اوھان جي ڪم جو ويري آھيان.'),
(3101, 26, 169, 'اي منھنجا پالڻھار جيڪي (ھو) ڪندا آھن تنھنجي شر کان مونکي ۽ منھنجي گھر جي ڀاتين کي بچاءِ.'),
(3102, 26, 170, 'پوءِ کيس ۽ سندس مڙني ڀاتين کي بچايوسون.'),
(3103, 26, 171, 'سواءِ (ھڪ) ٻڍڙي زال جي جا رھيلن مان ھئي.'),
(3104, 26, 172, 'وري (انھن) ٻـين جي پاڙ پٽي سون.'),
(3105, 26, 173, '۽ مٿن (پھڻن جو) مينھن وسايوسون، پوءِ ڊيڄاريلن جو مينھن بڇڙو آھي.'),
(3106, 26, 174, 'بيشڪ ھن ۾ (وڏي) نشاني آھي، ۽ منجھانئن گھڻا مڃڻ وارا نه ھوا.'),
(3107, 26, 175, '۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.'),
(3108, 26, 176, '(شھر) ايڪه وارن پيغمبرن کي ڪُوڙو ڀانيو.'),
(3109, 26, 177, 'جڏھن کين شعيب چيو ته (الله کان) ڇونه ٿا ڊڄو؟'),
(3110, 26, 178, 'بيشڪ آءٌ اوھان لاءِ امين پيغمبر آھيان.'),
(3111, 26, 179, 'پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.'),
(3112, 26, 180, 'بيشڪ آءٌ اوھان کان اُن (پيغام پھچائڻ) تي ڪو اُجورو نٿو گھُران، منھنجو اُجورو رڳو جھانن جي پالڻھار تي آھي.'),
(3113, 26, 181, 'ماڻ پورو مَيو ۽ ڇيئي لائيندڙن مان نه ھجو.'),
(3114, 26, 182, '۽ سنئين تارازيءَ سان توريو.'),
(3115, 26, 183, '۽ ماڻھن کي سندن شيون گھٽ نه ڏيو ۽ مُلڪ ۾ فسادي ٿي بگيڙ نه وجھو.'),
(3116, 26, 184, '۽ اُنھيءَ الله کان ڊڄو جنھن اوھان کي ۽ اڳين خلق کي خلقيو.'),
(3117, 26, 185, 'چيائون ته تون رڳو جادو ڪيلن مان آھين.'),
(3118, 26, 186, '۽ تون رڳو اسان جھڙو ماڻھو آھين ۽ بيشڪ اسين توکي ڪوڙن مان ڀائيندا آھيون.'),
(3119, 26, 187, 'جيڪڏھن تون سچن مان آھين ته اسان تي آسمان مان ڪا ڇِپ ڪيراءِ.'),
(3120, 26, 188, '(شعيب) چيو ته جيڪي اوھين ڪندا آھيو سو منھنجو پالڻھار چڱي طرح ڄاڻندڙ آھي.'),
(3121, 26, 189, 'پوءِ اُن کي ڪوڙو ڀانيائون تنھنڪري ڇانوَ واري ڏينھن جي عذاب کين پڪڙيو، بيشڪ اُھو وڏي ڏينھن جو عذاب ھو.'),
(3122, 26, 190, 'بيشڪ ھن (احوال) ۾ (وڏي) نشاني آھي، ۽ منجھانئن گھڻا مڃڻ وارا نه ھوا.'),
(3123, 26, 191, '۽ بيشڪ تنھنجو پالڻھار ئي غالب مھربان آھي.'),
(3124, 26, 192, '۽ بيشڪ اھو (قرآن) جھانن جي پالڻھار کان لاٿل آھي.'),
(3125, 26, 193, 'اُن کي رُوح الامين (يعني جبرئيل) لاٿو.'),
(3126, 26, 194, 'تنھنجي دل تي (ھن لاءِ) ته ڊيڄاريندڙن مان ٿئين.'),
(3127, 26, 195, '(جو) پڌريءَ عربي ٻوليءَ ۾ آھي.'),
(3128, 26, 196, '۽ بيشڪ سندس خبر اڳين صحيفن ۾ (لکيل) آھي.'),
(3129, 26, 197, 'اُنھن لاءِ (اِھا) نشاني نه آھي ڇا ته بني اسرائيلن جا عالم اُن (جي حقانيت) کي ڄاڻن ھا.'),
(3130, 26, 198, '۽ جيڪڏھن قرآن کي (عربن کانسواءِ ٻئي) ڪنھن عجميءَ تي نازل ڪريون ھا.'),
(3131, 26, 199, 'پوءِ اُھو اُن کي مٿن پڙھي ھا ته اُن کي (به) مڃڻ وارا نه ٿين ھوا.'),
(3132, 26, 200, 'اھڙي طرح گنھگارن جي دلين ۾ اُھو (انڪار) وڌوسون.'),
(3133, 26, 201, 'اُن تي (ايسين) ايمان نه آڻيندا جيسين ڏکوئيندڙ عذاب (نه) ڏسندا.'),
(3134, 26, 202, 'پوءِ اوچتو وٽن عذاب ايندو ۽ اُھي بي خبر ھوندا.'),
(3135, 26, 203, 'پوءِ چوندا ته اسين مُھلت ڏنل ٿينداسون ڇا؟'),
(3136, 26, 204, 'پوءِ اسان جو عذاب تڪڙو گھرندا آھن ڇا؟'),
(3137, 26, 205, 'ڀلا ڏٺو اٿئي ته جيڪڏھن کين ڪيئي ورھ آسودو ڪريون.'),
(3138, 26, 206, 'وري وٽن اُھو اچي جنھن جو انجام ڏنو پئي وين.'),
(3139, 26, 207, 'ته سندن آسودو ھجڻ کانئن ڇا (عذاب) ٽاريندو.'),
(3140, 26, 208, '۽ اھڙي ڪنھن ڳوٺ کي ناس نه ڪيوسون جنھن لاءِ ڊيڄاريندڙ (نه) ھوا.'),
(3141, 26, 209, 'نصيحت ڏيڻ لاءِ، ۽ اسين ظالم نه ھواسون.'),
(3142, 26, 210, '۽ اُن (قرآن) کي شيطانن نازل نه ڪيو آھي.'),
(3143, 26, 211, '۽ نڪي کين جڳائيندو آھي ۽ نڪي ڪري سگھندا آھن.'),
(3144, 26, 212, 'بيشڪ اُھي (ملائڪن جي ڪلام) ٻڌڻ (جي جاءِ) کان پري ڪيل آھن.'),
(3145, 26, 213, 'تنھنڪري الله سان گڏ ڪنھن ٻئي معبُود کي نه سڏ ته عذاب وارن مان ٿيندين.'),
(3146, 26, 214, '۽ پنھنجن ويجھن مائٽن کي ڊيڄار.'),
(3147, 26, 215, '۽ مؤمنن مان جيڪو تنھنجي تابعداري ڪري تنھن لاءِ پنھنجو بازو جھڪو ڪر (يعني نئڙت ڪر).'),
(3148, 26, 216, 'پوءِ جيڪڏھن تنھنجي نافرماني ڪن ته چؤ ته جيڪي ڪندا آھيو تنھن کان بيشڪ آءٌ بيزار آھيان.'),
(3149, 26, 217, '۽ اُنھيءَ غالب مھربان (الله) تي ڀروسو ڪر.'),
(3150, 26, 218, 'جيڪو (تھجّد وقت) تنھنجي اُٿڻ مھل کي ڏسي ٿو.'),
(3151, 26, 219, '۽ سجدي ڪندڙن ۾ تنھنجو ڦرڻ به ڏسي ٿو.'),
(3152, 26, 220, 'بيشڪ اُھوئي ٻڌندڙ ڄاڻندڙ آھي.'),
(3153, 26, 221, 'اوھان کي سُڌ (نه) ڏيان ڇا ته ڪنھن تي شيطان لھندا آھن؟'),
(3154, 26, 222, 'سڀڪنھن ٻٽاڪيءَ گنھگار تي لھندا آھن.'),
(3155, 26, 223, 'جو ٻڌل (ڳالھ اُن جي ڪنّ ۾ آڻي) وجھندا آھن ۽ منجھائن گھڻا ڪوڙا آھن.'),
(3156, 26, 224, '۽ گمراہ (ماڻھو) ئي شاعرن جي تابعداري ڪندا آھن.'),
(3157, 26, 225, 'نه ڏٺو اٿئي ڇا ته اُھي سڀڪنھن ميدان ۾ حيران ٿيندا آھن.'),
(3158, 26, 226, '۽ بيشڪ اُھي اُھو چوندا آھن جيڪي نه ڪندا آھن.'),
(3159, 26, 227, 'پر جن ايمان آندو ۽ چڱا عمل ڪيا ۽ الله کي گھڻو ساريائون ۽ (پنھنجي) ڏکوئجڻ کانپوءِ بدلو ورتائون (اُھي گمراہ نه آھن)، ۽ ظالم سگھو ئي ڄاڻندا ته ڪھڙي موٽڻ جي ھنڌ وري موٽندا.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طٰسٓ، ھي قرآن ۽ پڌري ڪتاب جون آيتون آھن.'),
(3161, 27, 2, 'اُنھن مؤمنن لاءِ ھدايت ۽ خوشخبري آھي.'),
(3162, 27, 3, 'جيڪي نماز پڙھندا آھن ۽ زڪوٰة ڏيندا آھن ۽ اُھي آخرت تي (به) يقين رکندا آھن.'),
(3163, 27, 4, 'بيشڪ جيڪي آخرت کي نه مڃيندا آھن تن لاءِ سندن ڪرتوت سينگارياسون پوءِ اُھي حيران رھندا آھن.'),
(3164, 27, 5, 'اِھي اُھي آھن جن لاءِ عذاب جي سختي آھي ۽ اُھي ئي آخرت ۾ ٽوٽي وارا آھن.'),
(3165, 27, 6, '۽ (اي پيغمبر) بيشڪ توکي حِڪمت واري ڄاڻندڙ (الله) وٽان قرآن ڏنو وڃي ٿو.'),
(3166, 27, 7, '(ياد ڪر) جڏھن موسىٰ پنھنجي گھر جي ڀاتين کي چيو ته بيشڪ مون باھ ڏٺي آھي، اجھو اوھان وٽ اُتان ڪا خبر آڻيندس يا اوھان وٽ (ڪو) ٻاريل ٽانڊو آڻيندس ته مانَ اوھين پاڻ سيڪيو.'),
(3167, 27, 8, 'پوءِ جنھن مھل (باھ) وٽ آيو (تنھن مھل) سڏيو ويو ته جوئي باھ ۾ ۽ سندس چوگرد آھي سو برڪت وارو ڪيو ويو آھي، ۽ جھانن جي پالڻھار کي پاڪائي آھي.'),
(3168, 27, 9, '(چيو ويو ته) اي مُوسىٰ بيشڪ (ڳالھ ھيءَ آھي ته) آءٌ غالب حڪمت وارو الله آھيان.'),
(3169, 27, 10, '۽ پنھنجي لٺ کي اُڇل، پوءِ جنھن مھل اُن کي چرندو ڏٺائين ڄڻڪ اُھا نانگ آھي (تنھن مھل) پٺي ڏئي ڀڳو ۽ پوءِ تي نه موٽيو، (چيوسون ته) اي مُوسىٰ نه ڊڄ، بيشڪ مون وٽ پيغمبر نه ڊڄندا آھن.'),
(3170, 27, 11, 'پر جنھن ظلم ڪيو وري مدائي کانپوءِ مٽائي چڱائي ڪيائين ته بيشڪ (اُنھيءَ لاءِ) بخشڻھار مھربان آھيان.'),
(3171, 27, 12, '۽ پنھنجو ھٿ پنھنجي گريبان ۾ وجھ ته بي عيب چمڪندڙ ٿي نڪري (انھن ٻن معجزن سان) جي نون معجزن ۾ داخل آھن فرعون ۽ سندس قوم ڏانھن (وڃ)، بيشڪ اُھي بدڪار قوم آھن.'),
(3172, 27, 13, 'پوءِ جنھن مھل اسان جون چِٽيون آيتون وٽن پھتيون (تنھن مھل) چيائون ته ھي پڌرو جادو آھي.'),
(3173, 27, 14, '۽ بي انصافي ۽ وڏائيءَ کان اُنھن (نشانين) جو انڪار ڪيائون حالانڪ سندين دلين انھن کي پڪ ڄاتو ھو (ته الله جي طرف کان آيل آھن)، پوءِ ڏس ته فسادين جي پڇاڙي ڪھڙي طرح ٿي.'),
(3174, 27, 15, '۽ بيشڪ داؤد ۽ سليمان کي علم ڏنوسون، ۽ ھنن چيو ته سڀ ساراھ اُنھي الله کي جڳائي جنھن پنھنجن گھڻن مؤمن ٻانھن تي اسان کي ڀلائي ڏني آھي.'),
(3175, 27, 16, '۽ سليمان داؤد جو وارث ٿيو ۽ چيائين ته اي ماڻھؤ اسان کي پکين جي ٻولي سيکاري وئي آھي ۽ اسان کي سڀڪا نعمت ڏني وئي آھي، بيشڪ اھا پڌري ڀلائي آھي.'),
(3176, 27, 17, '۽ سليمان لاءِ جنن ۽ ماڻھن ۽ پکين مان سندس لشڪر گڏ ڪيا ويا پوءِ اُھي لڳولڳ صفون ٻڌائي بيھاريا ويندا ھوا.'),
(3177, 27, 18, 'تان جو جڏھن ڪِوۡلين جي ميدان تي پھتا (تڏھن) ھڪ ڪِوۡل چيو ته اي ڪيوۡليون اوھين پنھنجي ٻرڙن ۾ گھڙو، ته سليمان ۽ سندس لشڪر پنھنجي بي خبريءَ ۾ اوھان کي نه لتاڙين.'),
(3178, 27, 19, 'پوءِ (سليمان) اُن جي ڳالھائڻ کان مُرڪي کِليو ۽ چيائين ته اي منھنجا پالڻھار مون کي توفيق ڏي ته تنھنجي اُنھن نعمتن جو شڪر ڪريان جي مون تي ۽ منھنجي ماءُ پيءُ تي ڪيون اٿيئي ۽ (توفيق ڏيم) ته اھڙا چڱا ڪم ڪريان جن کان تون راضي ٿئين ۽ مون کي پنھنجي ٻاجھ سان پنھنجي صالح ٻانھن ۾ داخل ڪر.'),
(3179, 27, 20, '۽ پکين جو حال احوال معلوم ڪيائين پوءِ (ھُد ھُد کي نه ڏسي ڪري) چيائين ته مون کي ڇا (ٿيو) آھي جو ھُد ھُد نه ٿو ڏسان، يا ھو غير حاضر آھي؟'),
(3180, 27, 21, 'ضرور سخت سزا ڏيندوسانس يا ضرور ڪُھندوسانس يا مون وٽ ڪو پڌرو دليل آڻي.'),
(3181, 27, 22, 'پوءِ گھڻو نه ترسيو ته (اچي) چيائين ته اھڙي خبر آندي اٿم جنھنجي توکي ڪا سُڌ نه ھئي ۽ (شھر) سبا کان (ھيءَ) پڪي خبر تووٽ آندي اٿم.'),
(3182, 27, 23, 'بيشڪ مون ھڪ زال ڏٺي جا اُنھن تي بادشاھي ٿي ڪري ۽ کيس سڀڪا نعمت ڏني ويئي آھي ۽ اُن کي ھڪ وڏو تخت (به) آھي.'),
(3183, 27, 24, 'کيس ۽ سندس قوم کي الله کان سواءِ سج جي پوڄا ڪندو ڏٺم ۽ شيطان انھن لاءِ سندن ڪرتوت سينگاريا آھن ۽ کين (الله جي) واٽ کان جھليو اٿس تنھنڪري اُھي ھدايت نٿا لھن.'),
(3184, 27, 25, '(اُھي) اُنھيءَ الله کي سجدو ڇو نه ٿا ڪن جو آسمانن ۽ زمين جون ڳجھيون شيون پڌريون ڪندو آھي؟ ۽ جيڪي لڪائيندا آھيو ۽ جيڪي پڌرو ڪندا آھيو سو (به) ڄاڻندو آھي.'),
(3185, 27, 26, 'الله (اُھو آھي جو) اُن کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي (اُھو) وڏي عرش جو پالڻھار آھي.'),
(3186, 27, 27, '(سليمان) چيو ته ڏسنداسين ته سچ چيو اٿيئي يا ڪوڙن مان آھين.'),
(3187, 27, 28, 'ھيءُ منھنجو خط کڻي وڃ پوءِ اُھو اُنھن ڏانھن اُڇل وري کانئن منھن ڦيراءِ پوءِ ڏس ته ڇا ٿا ورندي ڏين؟'),
(3188, 27, 29, '(بلقيس خط ڏسي) چيو ته اي سردارؤ مون ڏانھن سڳورو خط اڇليو ويو آھي.'),
(3189, 27, 30, 'بيشڪ اُھو (خط) سليمان وٽان (آيل) آھي ۽ بيشڪ اُھو الله ٻاجھاري مھربان جي نالي سان (شروع) آھي.'),
(3190, 27, 31, '(ھن مضمون سان لکيل آھي) ته منھنجي آڏو وڏائي نه ڪريو ۽ مون وٽ مسلمان ٿي اچو.'),
(3191, 27, 32, '(بلقيس وري) چيو ته اي سردارؤ منھنجي ڪم ۾ مونکي صلاح ڏيو، آءٌ ڪو ڪم ايسين نبيريندڙ نه آھيان جيسين اوھين حاضر (نه) ٿيندا آھيو.'),
(3192, 27, 33, 'چيائون ته اسين زور وارا ۽ ڏاڍا جنگي جوان آھيون، ۽ (سڀڪو) ڪم توکي (وس ۾) آھي پوءِ نھار ته ڇا ٿي حُڪم ڪرين.'),
(3193, 27, 34, 'چيائين ته بيشڪ بادشاھ جنھن مھل ڪنھن ڳوٺ ۾ گھڙندا آھن (تنھن مھل) اُن کي اُجاڙيندا آھن ۽ اُن جي مانَ وارن کي بي مانو ڪندا آھن، ۽ (سڀئي بادشاھ) ائين ئي ڪندا آھن.'),
(3194, 27, 35, '۽ بيشڪ آءٌ اُنھن ڏانھن ڪا سوکڙي موڪليان ٿي پوءِ ڏسان ته قاصد ڇا سان موٽن ٿا.'),
(3195, 27, 36, 'پوءِ جنھن مھل سُليمان وٽ (قاصد) آيو (تنھن مھل سليمان) چيو ته اوھين مونکي مال سان مدد ڏيو ٿا ڇا؟ جو جيڪي مونکي الله ڏنو آھي سو انھيءَ کان ڀلو آھي جيڪي اوھان کي ڏنو اٿس، بلڪ اوھين پنھنجن سوغاتن سان خوش ٿيو ٿا.'),
(3196, 27, 37, '(سندن سوغاتن سميت) انھن ڏانھن موٽي وڃ (اسين) بيشڪ اھڙا ڪٽڪ وٽن آڻينداسون جن جي سامھون ٿيڻ جي انھن کي ڪا مجال نه ھوندي ۽ اُن ھنڌان کين بلڪل بي مانو ڪري ڪڍنداسين ۽ اُھي خوار ھوندا.'),
(3197, 27, 38, '(سليمان) چيو ته اي سردارؤ اوھان مان ڪير انھن جي مسلمان ٿي مون وٽ اچڻ کان اڳ مون وٽ سندس تخت آڻيندو؟'),
(3198, 27, 39, 'جنَن مان ھڪڙي وڏي جن چيو ته تون پنھنجي جاءِ تان اُٿين تنھن کان اڳي آءٌ اھو تو وٽ آڻيندس، ۽ بيشڪ آءٌ مٿس وڏو سگھارو معتبر آھيان.'),
(3199, 27, 40, '(ٻيو) شخص جنھن وٽ ڪتاب مان علم ھو (تنھن) چيو ته تنھنجي نگاھ جي توڏانھن موٽڻ کان اڳي آءٌ اھو (تخت) تو وٽ آڻيندس، پوءِ جنھن مھل (سليمان) اُھو پاڻ وٽ رکيل ڏٺو (تنھن مھل) چيائين ته ھيءُ منھنجي پالڻھار جي فضل مان آھي، (ھن لاءِ) ته مونکي پرکي ته شڪر ڪريان ٿو يا بي شڪري ڪريان ٿو، ۽ جيڪو شڪر ڪندو سو رڳو پنھنجي (نفعي) لاءِ شڪر ڪندو، ۽ جنھن بي شڪري ڪئي ته منھنجو پالڻھار بي پرواہ ڪرم ڪندڙ آھي.'),
(3200, 27, 41, '(سُليمان) چيو ته اُن جي تخت کي اُن (جي آزمائش) لاءِ مٽايو ته ڏسون ته سُڃاڻي ٿي يا اُنھن مان ٿئي ٿي جيڪي نه سڃاڻن؟'),
(3201, 27, 42, 'پوءِ جنھن مھل (بلقيس) آئي (تنھن مھل) چيو ويو ته تنھنجو تخت ھھڙو آھي ڇا؟ چيائين ته ھيءُ ڄڻڪ اُھوئي آھي، ۽ ھن کان اڳ (سليمان جي سچائي بابت) اسان کي ڄاڻپ ڏني وئي ھئي ۽ اسين حڪم مڃيندڙ آھيون.'),
(3202, 27, 43, '۽ الله کانسواءِ جنھنجي پوڄا ڪندي ھئي تنھن کان کيس (سليمان) جھليو، بيشڪ اُھا ڪافرن جي قوم مان ھئي.'),
(3203, 27, 44, 'اُن کي چيو ويو ته محلات ۾ گھِڙ پوءِ اُھا جنھن مھل ڏٺائين (تنھن مھل) اُن کي پاڻيءَ جو تلاءُ ڀانيائين ۽ پنھنجن پنين تان ڪپڙو لاٿائين، (سليمانؑ) چيو ته اھا محلات شيشن سان جڙاء ٿيل آھي، چيائين ته اي منھنجا پالڻھار مون پاڻ تي ظلم ڪيو ۽ (ھاڻي) جھانن جي پالڻھار الله کي سُليمان سميت مڃيم.'),
(3204, 27, 45, '۽ بيشڪ ثمود (قوم) ڏانھن سندن ڀاءُ صالح موڪليوسون ته الله جي عبادت ڪريو پوءِ اُھي اُن مھل ٻه ٽوليون ٿي جھڳڙو ڪنديون رھيون.'),
(3205, 27, 46, '(صالح) چيو ته اي منھنجي قوم اوھين چڱائيءَ کان اڳ تڪڙو عذاب ڇو گھرندا آھيو؟ الله کان بخشش ڇونه ٿا گھرو ته مانَ اوھان تي رحم ڪيو وڃي.'),
(3206, 27, 47, 'چيائون ته توکي ۽ تنھنجي سنگت کي نڀاڳو سمجھيوسون، (صالح) چيو ته اوھان جو نڀاڳ الله وٽ آھي بلڪ اوھين قوم پرکبؤ.'),
(3207, 27, 48, '۽ (اُن) شھر ۾ نَو ڄڻا ھوا جي مُلڪ ۾ ڏڦير وجھندا ھوا ۽ سُڌارو نه ڪندا ھوا.'),
(3208, 27, 49, 'اُنھن الله جو قسم کڻي پاڻ ۾ کڻي چيو ته صالح ۽ سندس گھر وارن کي راتاھو ھڻي ماريون وري سندس وارث کي ضرور چونداسون ته (اسين) سندس گھر وارن جي ھلاڪ ٿيڻ مھل حاضر نه ھواسون ۽ بيشڪ اسين سچا آھيون.'),
(3209, 27, 50, '۽ ھنن ھڪڙي قسم جو فريب ڪيو ۽ اسان (به) ھڪڙي قسم جي رٿ رٿي ۽ اُھي بي خبر ھوا.'),
(3210, 27, 51, 'پوءِ (اي پيغمبر) نھار ته سندن فريب جي پڇاڙي ڪھڙي طرح ٿي جو اسان کين ۽ سندن ساري قوم کي ناس ڪيو.'),
(3211, 27, 52, 'پوءِ اُنھيءَ سببان جو ظلم ڪيائون اُھي سندن گھر اُجڙيل آھن، ڄاڻندڙ قوم لاءِ بيشڪ ھن ۾ (وڏي) نشاني آھي.'),
(3212, 27, 53, '۽ جن ايمان آندو ۽ پرھيزگاري ڪئي تن کي بچايوسون.'),
(3213, 27, 54, '۽ لُوط کي (موڪليوسون) جڏھن پنھنجي قوم کي چيائين ته اوھين بي حيائي (جو ڪم) ڪندا آھيو ڇا؟ ۽ اوھين ڏسندا (به) آھيو.'),
(3214, 27, 55, 'ڇا اوھين زالن کي ڇڏي مردن ڏانھن شھوت سان ويندا آھيو، بلڪ اوھين جاھل قوم آھيو.'),
(3215, 27, 56, 'تڏھن سندن قوم جي ورندي رڳو ھيءَ ھئي ته چيائون ته پنھنجي ڳوٺ مان لُوط جي گھر وارن کي تڙيو، ڇو ته اُھي ماڻھو پاڪ ھجڻ گھرندا آھن.'),
(3216, 27, 57, 'پوءِ کيس ۽ سندس گھر وارن کي بچايوسون سواءِ سندس زال جي، جنھن کي پوءِ تي رھيلن مان ٺھرايوسين.'),
(3217, 27, 58, '۽ مٿن مينھن وسايوسون، پوءِ ڊيڄايلن جو مينھن بڇڙو آھي.'),
(3218, 27, 59, '(اي پيغمبر) چؤ ته سڀ ساراہ الله کي جڳائي ۽ سندس انھن ٻانھن تي سلام ھجي جن کي پسند ڪيائين، الله ڀلو آھي يا اُھو جنھن کي (اُھي الله جو) شريڪ بڻائيندا آھن؟'),
(3219, 27, 60, '(نه بلڪ پڇون ٿا) اُھو ڪير آھي جنھن آسمانن ۽ زمين کي بڻايو ۽ اوھان لاءِ آسمان مان پاڻي وسايو؟ پوءِ ان سان (ھر طرح جا) رونق وارا باغ ڄماياسون، اُنھن (باغن) جي وڻن جي ڄمائڻ جي اوھان کي ڪا سگھ نه ھئي الله سان ڪو ٻيو معبود آھي ڇا؟ بلڪ اُھي (اھڙي قوم آھن جي سڌي رستي کان) ڏنگو ھلندا آھن.'),
(3220, 27, 61, '(نه بلڪ پڇون ٿا ٿه) زمين کي ٽڪاءَ وارو ڪنھن ڪيو ۽ اُن جي وچ ۾ نھرون (ڪنھن) پيدا ڪيون ۽ مٿس جبل (ڪنھن) پيدا ڪيا ۽ ٻن دريائن جي وچ ۾ (ڪنھن) اوٽ بڻائي؟ الله سان ڪو ٻيو معبود آھي ڇا؟ بلڪ اُنھن مان گھڻا نه ڄاڻندا آھن.'),
(3221, 27, 62, '(نه بلڪ پُڇون ٿا ته) عاجز جي دُعا ڪير قبول ڪندو آھي جڏھن (ھو) الله کي سڏيندو آھي؟ ۽ (ڪير) ڏک لاھيندو آھي؟ ۽ اوھان کي زمين ۾ (ڪير اڳين جو) وارث ڪندو آھي، الله سان ڪو ٻيو معبود آھي ڇا؟ اوھين ٿوري نصيحت وٺندا آھيو.'),
(3222, 27, 63, '(نه بلڪ پڇون ٿا ته) اوھان کي بر ۽ بحر جي اونداھين ۾ ڪير (سڌو) رستو ڏيکاريندو آھي؟ ۽ پنھنجي ٻاجھ (مينھن) کان اڳ خوشخبري ڏيندڙ وائن کي ڪير گھلائيندو آھي؟ الله کانسواءِ ڪو ٻيو معبود آھي ڇا؟ (ڪافر) جيڪي شريڪ مقرر ڪندا آھن تن کان الله تمام مٿاھون آھي.'),
(3223, 27, 64, 'ڀلا نئين سر مخلوق ڪير بڻائيندو آھي وري ٻيھر اُن کي بڻائيندو؟ ۽ اوھان کي آسمان ۽ زمين مان ڪير روزي ڏيندو آھي؟ الله سان ڪو ٻيو معبود آھي ڇا؟ (اي پيغمبر کين) چؤ ته جيڪڏھن اوھين سچا آھيو ته پنھنجي حُجت آڻيو.'),
(3224, 27, 65, '(اي پيغمبر کين) چؤ ته آسمانن ۽ زمين ۾ جيڪي به آھن سي الله کانسواءِ ڳجھ نه ڄاڻندا آھن، ۽ نڪي ڄاڻندا آھن ته ڪڏھن وري اُٿاربن.'),
(3225, 27, 66, 'بلڪ سندن ڄاڻپ آخرت (جي نسبت) ۾ عاجز ٿي پئي آھي، بلڪ اُھي کانئس شڪ ۾ آھن، (نه!) بلڪ (ان جي سمجھ) کان انڌا آھن.'),
(3226, 27, 67, '۽ ڪافر چوندا آھن ته جڏھن اسين مٽي ٿينداسون ۽ اسان جا پيءُ ڏاڏا (به تڏھن به) اسين وري قبرن مان ڪڍباسون ڇا؟'),
(3227, 27, 68, 'بيشڪ اسان کي ۽ اسان جي پيءُ ڏاڏن کي ھن کان اڳ انھيءَ ڳالھ جو اَنجام ڏنو ويو آھي ھيءُ رڳو اڳين جون آکاڻيون آھن.'),
(3228, 27, 69, '(اي پيغمبر کين) چؤ ته زمين ۾ گُھمو ۽ ڏسو ته گھنھگارن جي پڇاڙي ڪھڙي طرح ٿي ھئي.'),
(3229, 27, 70, '۽ (اي پيغمبر) مٿن ڏک نه ڪر ۽ سندن فريب ڪرڻ کان خفگيءَ ۾ نه رھ.'),
(3230, 27, 71, '۽ چوندا آھن ته جيڪڏھن اوھين سچا آھيو ته اھو انجام ڪڏھن ٿيندو.'),
(3231, 27, 72, '(کين) چؤ ته جيڪي تڪڙو گھرندا آھيو تنھن مان ڪجھ متان اوھان جي پُٺيءَ پويان (ويجھو) ھجي.'),
(3232, 27, 73, '۽ بيشڪ تنھنجو پالڻھار ماڻھن تي فضل ڪرڻ وارو آھي پر گھڻا ماڻھو شڪر نه ڪندا آھن.'),
(3233, 27, 74, '۽ سندن سينا جيڪي لڪائيندا ۽ جيڪي پڌرو ڪندا آھن سو تنھنجو پالڻھار بيشڪ ڄاڻندو آھي.'),
(3234, 27, 75, '۽ آسمان ۽ زمين ۾ اھڙي ڪا (شيء) ڳجھي نه آھي جا پڌري ڪتاب ۾ (لکيل) نه آھي.'),
(3235, 27, 76, 'بيشڪ ھي قرآن بني اسرائيلن کي گھڻيون ڳالھيون اُھي ڳالھيون بيان ڪندو آھي جن بابت اُھي تڪرار ڪندا ھوا.'),
(3236, 27, 77, '۽ بيشڪ اِھو مُؤمن لاءِ ھدايت ۽ ٻاجھ آھي.'),
(3237, 27, 78, 'بيشڪ تنھنجو پالڻھار پنھنجي حُڪم سان سندن وچ ۾ نبيرو ڪندو، ۽ اُھو غالب ڄاڻندڙ آھي.'),
(3238, 27, 79, 'پوءِ (اي پيغمبر تون) الله تي ڀروسو ڪر، بيشڪ تون پڌري سچي (دين) تي آھين.'),
(3239, 27, 80, 'بيشڪ تون نڪي مُئن کي ٻُڌائي سگھندين ۽ نڪي ٻوڙن کي سڏ ٻڌائي سگھندين جڏھن (ھو) پٺي ڏيئي ڦرن.'),
(3240, 27, 81, '۽ نڪي تون انڌن کي سندن گمراھيءَ کان (موٽڻ لاءِ) سِڌو رستو ڏيکاريندڙ آھين، (تون) رُڳو انھيءَ کي ٻڌائيندين جيڪو اسان جي آيتن تي ايمان آڻيندو پوءِ اُھي فرمانبردار آھن.'),
(3241, 27, 82, '۽ جڏھن مٿن (عذاب جو) انجام لازم ٿيندو (تڏھن) انھن لاءِ ھڪ جانور زمين مان ڪڍنداسون جو ساڻن ڳالھائيندو انھيءَ ڪري جو ماڻھو اسان جي آيتن تي يقين نه رکندا ھوا.'),
(3242, 27, 83, '۽ اُنھي ڏينھن جو سڀڪنھن اُمّت مان اھڙيءَ ٽوليءَ کي کڙو ڪنداسون جنھن اسان جي آيتن کي ڪُوڙو ڄاتو ٿي پوءِ اُھي (ھڪٻئي جي پھچڻ تائين) بيھاريا ويندا.'),
(3243, 27, 84, 'تان جو جڏھن (سڀ) ايندا (تڏھن الله) چوندو ته اوھان منھنجن آيتن کي ڇو ڪوڙ ڄاتو ھو ۽ انھن کي اوھان سمجھو به نه ھُيو يا (چؤ ته) اوھان ڪھڙو ڪم ڪيو ٿي؟'),
(3244, 27, 85, '۽ سندن ظلم ڪرڻ سببان مٿن (عذاب جو) انجام لازم ٿي چُڪو پوءِ اُھي (ماڻھو) ڳالھائي نه سگھندا.'),
(3245, 27, 86, 'نه ڏٺو اٿن ڇا ته اسان رات کي (ھن لاءِ) پيدا ڪيو ته منجھس آرام وٺن ۽ ڏينھن کي روشن (بڻايوسون)، بيشڪ ھن ۾ مڃيندڙ قوم لاءِ (منھنجي قدرت جون) نشانيون آھن.'),
(3246, 27, 87, '۽ (ياد ڪريو) جنھن ڏينھن صُور ۾ ڦوڪبو (تنھن ڏينھن) جيڪي آسمانن ۾ آھن ۽ جيڪي زمين ۾ آھن سي سڀئي گھٻرائيندا پر جن لاءِ الله گھريو (سي نه گھٻرائيندا)، ۽ سڀڪو وٽن خوار ٿيل ايندو.'),
(3247, 27, 88, '۽ تون جبلن کي ڏسين ٿو انھن کي (زمين) ۾ مُحڪم ڀائين ٿو؟ حالانڪ اُھي ڪڪرن جي ھلڻ جان ھلندا، اِھا (اُنھي) الله جي جوڙ آھي جنھن سڀڪنھن شيء کي مُحڪم رکيو، بيشڪ جيڪي ڪندا آھيو تنھنجي اُھو خبر رکندڙ آھي.'),
(3248, 27, 89, 'جيڪو نيڪي آڻيندو تنھن لاءِ اُن کان ڀلو (بدلو) آھي، ۽ اُھي اُن ڏينھن گھٻرائڻ کان امن وارا ھوندا.'),
(3249, 27, 90, '۽ جيڪو مدائي آڻيندو پوءِ تن جا منھن باھ ۾ اونڌا ڪري وجھبا، (چيو ويندنِ) ته جيڪي ڪندا ھُيؤ تنھن کانسواءِ ڪو بدلو نه ٿو ڏجيوَ.'),
(3250, 27, 91, '(اي پيغمبر) چؤ ته مون کي ته (ھيءُ) حُڪم ڪيل آھي ته ھن شھر جي پالڻھار جي عبادت ڪريان جنھن اُن کي تعظيم وارو ڪيو ۽ سڀڪا شيء سندس آھي ۽ مونکي حُڪم ڪيل آھي ته مُسلمانن مان ھُجان.'),
(3251, 27, 92, '۽ (ھيءُ) قرآن پڙھان، پوءِ جيڪو ھدايت وارو ٿيو سو رڳو پاڻ لاءِ ھدايت وارو ٿئي ٿو، ۽ جيڪو گمراھ ٿيو ته چؤ ته آءٌ رڳو ڊيڄاريندڙ آھيان.'),
(3252, 27, 93, '۽ چؤ ته سڀ ساراھ الله کي جڳائي (اُھو) اوھان کي پنھنجون نشانيون سگھو ڏيکاريندو پوءِ جنکي اوھين سُڃاڻندؤ، ۽ جيڪي ڪندا آھيو تنھن کان اوھان جو پالڻھار بي خبر نه آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طٰسٓمّٓ.'),
(3254, 28, 2, 'ھي پڌري ڪتاب جون آيتون آھن.'),
(3255, 28, 3, '(اي پيغمبر) مُوسىٰ ۽ فرعون جو ڪُجھ احوال مؤمن ماڻھن (جي ٻڌائڻ) لاءِ توکي سچيءَ طرح پڙھي ٻڌايون ٿا.'),
(3256, 28, 4, 'بيشڪ فرعون مُلڪ ۾ وڏائي ڪئي ھئي ۽ ان جي رھڻ وارن کي ٽوليون ٽوليون ڪري ڇڏيو ھوائين منجھائن ھڪ ٽوليءَ کي ھيڻو ڪيو ھوائين جو سندن پُٽ ڪُھندو ھو ۽ سندن ڌيئرون جيئريون ڇڏيندو ھو، بيشڪ اُھو فسادين مان ھو.'),
(3257, 28, 5, '۽ گھُريوسون ته جن کي مُلڪ ۾ ھيڻو ڪيو ويو تن تي اِحسان ڪريون ۽ کين سردار بڻايون ۽ کين وارث ڪريون.'),
(3258, 28, 6, '۽ کين مُلڪ ۾ دٻدٻو ڏيون ۽ فرعون ۽ ھامان ۽ سندن لشڪرن کي اُنھن (ھيڻن) کان اُھا (ڳالھ) ڏيکاريون جنھن کان ڊڄندا ھوا.'),
(3259, 28, 7, '۽ مُوسىٰ جي ماءُ ڏانھن اِلھام ڪيوسون ته اُن کي کِير پيار، جڏھن ھن جي لاءِ ڊڄين تڏھن کيس درياءَ ۾ اُڇلج ۽ نڪي ڊڄج ۽ نڪي غمگين ٿِج، ڇوته اسين کيس توڏانھن ورائڻ وارا آھيون ۽ کيس پيغمبرن مان ڪرڻ وارا آھيون.'),
(3260, 28, 8, 'پوءِ فرعون جي گھراڻي وارن اُن کي جھٽيو ته (نيٺ) اُنھن لاءِ ويري ۽ ڏک جو سبب ٿئي، ڇوته فرعون ۽ ھامان ۽ سندن لشڪر خطا ڪندڙ ھوا.'),
(3261, 28, 9, '۽ فرعون جي زال چيو ته (ھيءُ ڇوڪر) منھنجي لاءِ ۽ تنھنجي لاءِ اکين جو ٺار آھي، اُن کي نه ڪُھو، مانَ اسان کي نفعو ڏئي يا کيس پُٽ ڪريون ۽ اُھي (اُن جي حال جي حقيقت) نه ڄاڻندا ھوا.'),
(3262, 28, 10, '۽ مُوسىٰ جي ماءُ جي دِل بي قرار ٿي، بيشڪ ان ڳالھ کي ستت ئي پڌرو ڪري ھا جيڪڏھن سندس دل تي اسين بندش نه ڪريون ھا ھِن لاءِ ته وسھندڙن مان ٿئي.'),
(3263, 28, 11, '۽ مُوسىٰ جي ڀيڻ کي چيائين ته سندس پيرو کڻ، پوءِ (اُھا) اُن کي پري کان ڏسندي رھي ۽ اُنھن نه ٿي ڄاتو.'),
(3264, 28, 12, '۽ (ڀيڻس جي پھچڻ کان) اڳ مٿس دائين جو کير حرام ڪيوسون پوءِ ڀيڻس چيو ته (آءٌ) اوھان کي اھڙو گھراڻو ڏسيان جو (اُھي) اوھان جي پاران اُن جي سنڀال ڪن؟ ۽ اُھي اُن جا گھڻ گھرا ھجن.'),
(3265, 28, 13, 'پوءِ کيس سندس ماءُ ڏانھن وري پھچايوسين ته سندس اکيون ٺرن ۽ غمگين نه ٿئي ۽ (ھيء به) ڄاڻي ته بيشڪ الله جو انجام سچو آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.'),
(3266, 28, 14, '۽ جنھن مھل پنھنجي جوانيءَ کي پھتو ۽ پختو ٿيو (تنھن مھل) کيس دانائي ۽ عِلم ڏنوسون، ۽ اھڙي طرح ڀلارن کي چڱو بدلو ڏيندا آھيون.'),
(3267, 28, 15, '۽ (موسى) شھر ۾ اُتي جي رھاڪن جي غفلت جي وقت داخل ٿيو پوءِ اتي ٻه مڙس پاڻ ۾ وڙھندي ڏٺائين، ھي (ھڪڙو) سندس قوم مان ھو ۽ ھي (ٻيو) سندس ويريءَ (فرعون جي قوم) مان ھو، پوءِ جيڪو سندس قوم مان ھو تنھن اُنھيءَ تي ھمراھي گھري جيڪو سندس ويريء (جي قوم) مان ھو پوءِ مُوسىٰ ان کي مُڪ ھنئي ۽ اُن کي پورو ڪيائين، چيائين ته ھي شيطان جي ڪم مان آھي، بيشڪ اُھو پڌرو گمراھ ڪندڙ ويري آھي.'),
(3268, 28, 16, 'چيائين ته اي منھنجا پالڻھار بيشڪ مون پاڻ تي ظلم ڪيو تنھنڪري مون کي بخش پوءِ کيس بخشيائين، بيشڪ اُھو بخشڻھار مھربان آھي.'),
(3269, 28, 17, 'چيائين ته اي منھنجا پالڻھار جيڪو مون تي فضل ڪيو اٿئي تنھن سببان ڏوھارين جو مددگار ڪڏھن نه ٿيندس.'),
(3270, 28, 18, 'پوءِ صُبح تائين شھر ۾ ڊڄندڙ منتظر رھيو ته اوچتو جنھن کانئس مدد گھري ھئي تنھن کانئس (وري) مدد گھري، مُوسىٰ کيس چيو ته تون پڌرو بي راھو آھين.'),
(3271, 28, 19, 'پوءِ جيڪو ٻنھي جو ويري ھو تنھن ڏانھن (موسى) جنھن مھل ھٿ ڊگھيرڻ جو ارادو ڪيو (تنھن مھل فرياد ڪندڙ مُوسىٰ کي پاڻ تي سنبريل ڏسي) چيو ته اي مُوسىٰ جئن ڪالھ ھڪڙي ماڻھوءَ کي ڪُٺئي تئن مون کي (به) ڪُھڻ جو ارادو ڪرين ٿو ڇا؟ مُلڪ ۾ زبردست ٿيڻ کانسواءِ تنھنجو ٻيو ڪو ارادو نه آھي تون سُڌاريندڙن مان ھُجڻ نه ٿو گھُرين.'),
(3272, 28, 20, '۽ ھڪ مڙس شھر جي پرين پاسي کان ڊوڙندو آيو، چيائين ته اي مُوسىٰ بيشڪ (ھتون جا) سردار تولاءِ رٿ ڪن ٿا ته توکي ڪُھن تنھنڪري نيڪري وڃ بيشڪ آءٌ تولاءِ خير گھرندڙن مان آھيان.'),
(3273, 28, 21, 'پوءِ (مُوسى) ڊڄندو انتظار ڪڍندو اُن (شھر) مان نڪتو، چيائين ته اي منھنجا پالڻھار ظالمن جي قوم کان مونکي بچاءِ.'),
(3274, 28, 22, '۽ جنھن مھل مدين (شھر) جي طرف منھن ڪيائين (تنھن مھل) چيائين ته اُميد اٿم ته منھنجو پالڻھار مون کي سڌي واٽ ڏيکاريندو.'),
(3275, 28, 23, '۽ جنھن مھل مدين جي تڙ تي پھتو (تنھن مھل) اُن ماڻھن جي ھڪ ٽولي (جانورن کي) پاڻي پياريندي ڏٺائين، ۽ اُنھن کانسواءِ ٻه زالون (به پنھنجو ڌڻ پاڻي پيڻ کان) جھليندي ڏٺائين، (مُوسى) چيو ته اوھان جو ڪھڙو ڪم آھي؟ چيائون ته (اسين) ايسين (پنھنجي ڌڻ کي پاڻي) نه پيارينديون سين جيسين ڌنار (پنھنجا جانور پياري نه) موٽائين، ۽ اسان جو پيءُ وڏو ٻڍڙو آھي.'),
(3276, 28, 24, 'پوءِ اُنھن (جي ڌڻ) کي پاڻي پياريائين وري (رھڻ لاءِ) پاڇي ڏانھن ڦِريو پوءِ چيائين ته اي منھنجا پالڻھار بيشڪ جيڪي مون ڏانھن چڱائيءَ مان موڪليءِ تنھن لاءِ آءٌ مُحتاج آھيان.'),
(3277, 28, 25, 'پوءِ منجھائن ھڪڙي لڄ سان گھمندي وٽس آئي، چيائين ته منھنجو پيءُ توکي ھن لاءِ سڏي ٿو ته جيڪو اسان (جي ڌڻ) کي پاڻي پياريئي تنھنجي مزوري توکي ڏي، پوءِ جنھن مھل وٽس آيو ۽ سارو قصّو وٽس بيان ڪيائين (تنھن مھل شعيب) چيو ته نه ڊڄ، (ھاڻي) ظالم قوم کان ڇُٽين.'),
(3278, 28, 26, 'منجھائن ھڪ چيو ته اي ابا ھن کي مزور ڪري رک، ڇوته ڀلو (نوڪر) جنھن کي مزور ڪري رکين اُھو آھي جو سگھارو معتبر ھجي.'),
(3279, 28, 27, '(شعيب) چيو ته منھنجو ارادو آھي ته ھنن پنھنجن ٻن ڌيئن مان ھڪ ھن شرط تي توکي پرڻايان ته اَٺ سال منھنجي مزوري ڪرين، ۽ جيڪڏھ ڏھ (ورھ) پورا ڪندين ته اُھو تنھنجي پار کان (احسان) آھي ۽ آءٌ توتي (ڪا) اوکائي ڪرڻ نه ٿو گھران، جيڪڏھن الله گھريو ته مون کي خير گھرن مان ڏسندين.'),
(3280, 28, 28, '(مُوسى) چيو ته منھنجي ۽ تنھنجي وچ ۾ اھو (انجام) آھي، انھن ٻنھي مدتن مان ڪا پورائي ڪريان ته مون تي ڪو زور نه ھوندو، ۽ جيڪي چئون ٿا تنھن تي الله شاھد (بس) آھي.'),
(3281, 28, 29, 'پوءِ جنھن مھل مُوسىٰ (اھا) مُدّت پوري ڪئي ۽ پنھنجي گھر وارن کي وٺي ھليو (تنھن مھل وچ رستي ۾ ڪوہ) طور جي پاسي ڏانھن باھ ڏٺائين، پنھنجي گھر وارن کي چيائين ته ترسو بيشڪ مون باھ ڏٺي آھي اُميد اٿم ته اُتاھون ڪا خبر يا ڪو باھ جو ٽانڊو اوھان وٽ آڻيندس ته مانَ اوھين پاڻ سيڪيو.'),
(3282, 28, 30, 'پوءِ جڏھن اُنھيءَ (باھ) وٽ آيو (تڏھن) ميدان جي سڄي ڀر کان مُبارڪ جاءِ ۽ وڻ مان سڏيو ويو ته اي مُوسىٰ بيشڪ آءٌ جھانن جو پالڻھار آھيان.'),
(3283, 28, 31, '۽ تون پنھنجي لٺ اُڇل، پوءِ جنھن مھل اُن کي چرندو ڏٺائين ڄڻڪ اُھا نانگ آھي (تنھن مھل) پٺيرو ٿي منھن موڙيائين ۽ پوئتي نه موٽيو، (چيوسون ته) اي موسىٰ اوري اچ ۽ نه ڊڄ، جو بيشڪ تون امن وارن مان آھين.'),
(3284, 28, 32, 'پنھنجو ھٿ گريبان ۾ گھيڙ ته بي عيب چمڪندڙ ٿي نڪري، ۽ خوف کان (تسلّي ٿيڻ لاءِ) پنھنجون ٻانھون پاڻ ڏانھن گڏ پوءِ اُھي ٻئي حُجّتون فرعون ۽ سندس لشڪر ڏانھن تنھنجي پالڻھار (جي طرف) کان آھن، بيشڪ اُھي بدڪار ماڻھو ھوا.'),
(3285, 28, 33, 'چيائين ته اي منھنجا پالڻھار بيشڪ منجھائن ھڪ شخص کي مون ماريو آھي تنھنڪري آءٌ پنھنجي مارجڻ کان ڊڄان ٿو.'),
(3286, 28, 34, '۽ منھنجو ڀاءُ ھارون مون کان زبان جي ڪري وڌيڪ چٽو ڳالھائيندڙ آھي تنھنڪري اُن کي مون سان مدد ڏيندڙ ڪري موڪل ته منھنجي تصديق ڪري ڇوته آءٌ پنھنجي ڪوڙي ٿيڻ کان ڊڄان ٿو.'),
(3287, 28, 35, '(الله) چيو ته سِگھو تنھنجا بازو تنھنجي ڀاءُ سان مُحڪم ڪنداسون ۽ اوھان کي غلبو ڏينداسون پوءِ (اُھي) اوھان ڏانھن (ايذاءَ لاءِ) پھچي نه سگھندا، ۽ اسانجي پڌرن معجزن سببان، اوھين ٻئي ۽ جيڪي اوھان جي پُٺيان لڳا سي غالب رھندا.'),
(3288, 28, 36, 'پوءِ جنھن مھل مُوسىٰ اسان جي پڌرن معجزن سان وٽن آيو (تنھن مھل) چيائون ته ھيء رڳو ٺاھوڪو جادو آھي ۽ اِھا ڳالھ پنھنجن پھرين پيءُ ڏاڏن ۾ نه ٻُڌي سون.'),
(3289, 28, 37, '۽ مُوسىٰ چيو ته منھنجو پالڻھار اُنھيءَ کي وڌيڪ ڄاڻندو آھي جنھن وٽانئس ھدايت آندي آھي ۽ جنھن لاءِ (آخرت ۾) موچاري عاقبت جو گھر آھي، بيشڪ ظالم نه ڇُٽندا.'),
(3290, 28, 38, '۽ فِرعون چيو ته اي سردارؤ مون اوھان لاءِ پاڻ کانسواءِ ڪو ٻيو معبود نه ڄاتو آھي، پوءِ اي ھامان مون لاءِ سرن جو (کورو) ٻار پوءِ مون لاءِ ھڪ ماڙي بڻاءِ ته مان موسىٰ جي معبُود ڏانھن چڙھان ۽ آءٌ بيشڪ کيس ڪُوڙن مان ڀانيان ٿو.'),
(3291, 28, 39, '۽ اُن (فرعون) ۽ سندس لشڪر ناحق مُلڪ ۾ وڏائي ڪئي ۽ ڀانيائون ته اُھي اسان ڏانھن نه موٽائبا.'),
(3292, 28, 40, 'پوءِ کيس ۽ سندس لشڪرن کي پڪڙيوسون ۽ پوءِ کيس سمنڊ ۾ اُڇليوسون، پوءِ ڏس ته ظالمن جي پڇاڙي ڪھڙي طرح ٿي.'),
(3293, 28, 41, '۽ اسان اُھي (دوزخين جا اڳواڻ) بڻايا جو (ماڻھن) کي باھ ڏانھن سڏيندا ھوا، ۽ قيامت جي ڏينھن مدد نه ڏبين.'),
(3294, 28, 42, '۽ ھن دنيا ۾ لعنت سندن پوئتان لاٿي سون، ۽ قيامت جي ڏينھن اُھي بُڇڙن مان ھوندا.'),
(3295, 28, 43, '۽ بيشڪ مُوسىٰ کي ڪتاب ڏنوسون اُن کان پوءِ جو پھرين جُڳن کي ھلاڪ ڪيوسون جو (اُھو) ماڻھن لاءِ چِٽو دليل ۽ ھدايت ڪندڙ ۽ ٻاجھ ھو ته مانَ اُھي نصيحت وٺن.'),
(3296, 28, 44, '۽ (اي پيغمبر تون تڏھن) اُلھندي پاسي کان نه ھئين جڏھن مُوسىٰ ڏانھن وَحي موڪليوسون ۽ نڪي (تون اُتي) حاضرن مان ھُئين.'),
(3297, 28, 45, 'پر ڪيترائي جُڳ (مُوسىٰ کانپوءِ) پيدا ڪياسون پوءِ مٿن مُدت ڊگھي گذري، ۽ نڪي (تون) مدين وارن ۾ رھندڙ ھئين جو اسان جون کين آيتون پڙھي ٻڌايئي ٿي پر اسين (پيغمبرن) کي موڪليندا رھياسون.'),
(3298, 28, 46, '۽ نڪي (تون ڪوہ) طور جي پاسي ھُئين جڏھن موسىٰ کي سڏيوسون پر تنھنجي پالڻھار جي ٻاجھ توتي (ھن لاءِ ٿيل) آھي ته تون اُنھي قوم کي ڊيڄارين جن وٽ توکان اڳ ڪو ڊيڄاريندڙ نه آيو ته مانَ اُھي نصيحت وٺن.'),
(3299, 28, 47, '۽ جيڪڏھن ائين نه ھجي ھا ته سندن ھٿن جيڪي اڳي موڪليو آھي تنھنجي سبب اُنھن تي جڏھن مُصيبت پھچي تڏھن (متان) چون ته اي اسان جا پالڻھار اسان ڏانھن ڪو پيغمبر ڇونه موڪليئي ته تنھنجن آيتن جا تابعدار بڻجون ھا ۽ مؤمنن مان ھجون ھا (ته توکي نه موڪليون ھا).'),
(3300, 28, 48, 'پوءِ جنھن مھل اسان وٽان اُنھن وٽ سچو (وحي) آيو (تنھن مھل) چوڻ لڳا ته جھڙو (معجزو) مُوسىٰ کي ڏنو ويو تھڙو ھن پيغمبر کي ڇونه ڏنو ويو آھي، جيڪي اڳي مُوسىٰ کي ڏنو ويو تنھن جو انڪار نه ڪيو اٿن ڇا؟ (ھاڻي) چون ٿا ته (توريت ۽ قرآن) ٻئي ھڪ جھڙا جادو آھن، ۽ چون ٿا ته اسين ھر ھڪ جا مُنڪر آھيون.'),
(3301, 28, 49, '(کين) چؤ ته جيڪڏھن سچا آھيو ته الله وٽان ڪو ڪتاب آڻيو جو انھن ٻنھي کان وڌيڪ سڌو رستو ڏيکاريندڙ ھجي ته اُن جي تابعداري ڪريان.'),
(3302, 28, 50, 'پوءِ جيڪڏھن تنھنجي ڳالھ قبول نه ڪن ته ڄاڻ ته اُھي رڳو پنھنجن سَڌن تي ھلندا آھن، ۽ جيڪو الله جي رستي ڏيکارڻ کانسواءِ پنھنجي سَڌ تي ھلي تنھن کان وڌيڪ گمراھ ڪير آھي، بيشڪ الله ظالمن جي قوم کي سِڌو رستو نه ڏيکاريندو آھي.'),
(3303, 28, 51, '۽ بيشڪ ھن قرآن کي انھن لاءِ لڳولڳ موڪليوسون ته مانَ اُھي نصيحت وٺن.'),
(3304, 28, 52, 'جن کي ھن (قرآن) کان اڳ ڪتان ڏنو اٿون سي اُن (قرآن) کي مڃيندا آھن.'),
(3305, 28, 53, '۽ جڏھن کين (ھيءُ قرآن) پڙھي ٻڌائبو آھي (تڏھن) چوندا آھن ته اُن تي ايمان آندوسون بيشڪ اُھو اسان جي پالڻھار وٽان سچو آھي بيشڪ اسان اُن (جي نازل ٿيڻ) کان اڳيئي مُسلمان ھواسون.'),
(3306, 28, 54, 'اِھي اُھي آھن جن کي سندن اَجر ٻه ڀيرا ڏبو انھي ڪري جو صبر ڪيائون ۽ مدائي کي چڱائي سان ٽاريندا آھن ۽ جيڪا کين روزي ڏني اٿون تنھن مان خرچيندا آھن.'),
(3307, 28, 55, '۽ جڏھن ڪا بيھودي ڳالھ ٻڌندا آھن (تڏھن) اُن کان مُنھن موڙيندا آھن ۽ چوندا آھن ته اسان جا ڪم اسان لاءِ آھن ۽ اوھان جا ڪم اوھان لاءِ آھن، اوھان تي سلام ھجي، اسين جاھلن (جي سنگت) کي نه ٿا گھرون.'),
(3308, 28, 56, '(اي پيغمبر) بيشڪ تون جنھن کي گھرين تنھن کي ھدايت ڪري نه ٿو سگھين پر الله جنھن کي گھرندو آھي تنھن کي ھدايت ڪندو آھي، ۽ اُھو ھدايت وارن کي چڱي طرح ڄاڻندڙ آھي.'),
(3309, 28, 57, '۽ (ڪي) چوندا آھن ته جيڪڏھن توساڻ ھدايت جي تابعداري ڪنداسون ته پنھنجي مُلڪ مان تڙجي وينداسون، کين حرم ۾ امن سان نه ٽڪاياسون ڇا؟ جو اُن ڏانھن سڀڪنھن جنس جو ميوو پاڻ وٽان روزيءَ طرح پھچائبو آھي پر اُنھن مان گھڻا (ماڻھو) نه ڄاڻندا آھن.'),
(3310, 28, 58, '۽ ڳوٺن مان ڪيترائي ويران ڪياسون جي پنھنجي گذران ۾ حد کان لنگھيا ھوا، پوءِ اُھي سندين رھڻ جون جايون آھن اُنھن کانپوءِ ڪن ٿورڙن (وقتن) کانسواءِ (اُتي) رھيوئي ڪونه آھي، ۽ آخر اسين (ئي) وارث ھواسون.'),
(3311, 28, 59, '۽ (اي پيغمبر) تنھنجو پالڻھار ڳوٺن کي (ايسين) ڪڏھن به ناس ڪرڻ وارو نه آھي جيسين اُن جي ڪنھن وڏي شھر ۾ ڪو پيغمبر (نه) موڪلي جو اسان جون آيتون کين پڙھي ٻڌائي، ۽ ڳوٺن کي ڪڏھن به ناس ڪرڻ وارا نه آھيون سواءِ ان حالت جي جو سندن رھڻ وارا ظالم ھجن.'),
(3312, 28, 60, '۽ ھر ھڪ جنس مان جيڪي اوھان کي ڏنو ويو آھي سو دُنيا جي حياتيءَ جو سامان ۽ ان جو سينگار آھي، ۽ جيڪي الله وٽ آھي سو ڀلو ۽ سدائين رھڻ وارو آھي، پوءِ ڇونه سمجھ رکندا آھيو؟'),
(3313, 28, 61, 'ڀلا جنھن کي چڱو انعام ڏنوسون اُھو ھو لھڻ وارو آھي سو اُنھي جھڙو آھي ڇا جنھن کي دنيا جي سامان سان آسودو ڪيوسون وري اُھو قيامت جي ڏينھن (عذاب ۾) حاضر ڪيلن مان ھجي؟'),
(3314, 28, 62, '۽ جنھن ڏينھن (الله) کين سڏي چوندو ته مُنھنجا اُھي شريڪ ڪٿي آھن جن کي اوھين (مون جَھڙو) ڀائيندا ھيؤ.'),
(3315, 28, 63, '(ان ڏينھن) جن تي عذاب (جو حُڪم) لازم ٿي چڪو ھوندو سي چوندا ته اي اسانجا پالڻھار ھي اُھي آھن جن کي گمراھ ڪيوسون، جھڙو پاڻ گمراھ ٿياسون تھڙو کين (به) گمراھ ڪيوسون، (ھاڻي سڀني کان) تنھنجي آڏو بيزار ٿيون ٿا، اُھي اسان کي نه پوڄيندا ھوا.'),
(3316, 28, 64, '۽ چيو ويندو ته پنھنجن شريڪن کي سڏيو پوءِ کين سڏيندا ته اُھي کين ورندي نه ڏيندا ۽ عذاب ڏسندا، (تڏھن سَڌ ڪندا ته) جيڪر دنيا ۾ ھدايت وارا ھجون ھا.'),
(3317, 28, 65, '۽ جنھن ڏينھن (الله) کين سڏيندو پوءِ چوندو ته اوھان پيغمبرن کي ڪھڙي ورندي ڏني.'),
(3318, 28, 66, 'پوءِ اُن ڏينھن (سڀ) ڳالھيون کين ڀُلجي وينديون پوءِ اُھي پاڻ ۾ (به) نه پڇندا.'),
(3319, 28, 67, 'پوءِ جنھن توبه ڪئي ۽ ايمان آندو ۽ چڱا ڪم ڪيا سو اُميد آھي ته ڇٽلن مان ٿيندو.'),
(3320, 28, 68, '۽ (اي پيغمبر) تنھنجو پالڻھار جيڪي گھرندو آھي سو خلقيندو آھي (۽ جنھن کي گھرندو تنھن کي) پسند ڪندو آھي، انھن کي ڪو اختيار ڪونھي، الله کي پاڪائي آھي ۽ جنھن کي ساڻس شريڪ مَقرّر ڪندا آھن تنھن کان تمام مٿاھون آھي.'),
(3321, 28, 69, '۽ سندن سِينا جيڪي لڪائيندا آھن ۽ جيڪي پاڻ پڌرو ڪندا آھن سو تنھنجو پالڻھار ڄاڻندو آھي.'),
(3322, 28, 70, '۽ اُھو الله آھي اُن کانسواءِ ڪو عبادت جو لائق نه آھي، دُنيا ۽ آخرت ۾ سندس ساراھ آھي، ۽ سندس ئي حڪومت آھي ۽ ڏانھس اوھان کي موٽائبو.'),
(3323, 28, 71, '(کين) چؤ ته ڏسو ته سھين جيڪڏھن الله اوھان تي قيامت جي ڏينھن تائين ھميشہ رات (قائم) ڪري ته الله کانسواءِ (ٻيو) ڪھڙو معبود آھي جو اوھان وٽ سوجھرو آڻيندو، پوءِ نه ٻڌندا آھيو ڇا؟'),
(3324, 28, 72, 'چؤ ته ڏسو ته سھين ته جيڪڏھن الله اوھان تي قيامت جي ڏينھن توڻي ھميشہ ڏينھن (قائم) ڪري ته الله کانسواءِ (ٻيو) ڪھڙو معبود آھي جو اوھان وٽ رات آڻيندو جنھن ۾ اوھين آرام وٺو، پوءِ اوھين نه ٿا ڏسو ڇا؟'),
(3325, 28, 73, '۽ پنھنجي ٻاجھ سان اوھان لاءِ رات ۽ ڏينھن بڻايائين ته اوھين منجھس آرام وٺو ۽ سندس فضل مان (روزي) طلب ڪريو ۽ ته مانَ اوھين شڪرانو ڪريو.'),
(3326, 28, 74, '۽ جنھن ڏينھن (الله) کين سڏي چوندو ته اُھي منھنجا شريڪ ڪٿي آھن جن کي اوھين (مون جھڙو) ڀائيندا ھيؤ؟'),
(3327, 28, 75, '۽ ھر ھڪ ٽوليءَ مان شاھد جُدا ڪنداسين ۽ چونداسين ته پنھنجو دليل پيش ڪريو پوءِ اُھي ڄاڻندا ته بيشڪ حق الله جي پاسي آھي ۽ جيڪو ٺاھ ٺاھيندا ھوا سو کانئن ڀُلي ويندو.'),
(3328, 28, 76, 'بيشڪ قارون مُوسىٰ جي قوم مان ھو پوءِ مٿن شرارت ڪرڻ لڳو، ۽ کيس خزانن مان ايترو ڏنو ھوسون جو سندس ڪنجين سگھ واري ٽوليءَ کي ٿَڪايو ٿي، جڏھن سندس قوم کيس چيو ته (مال ڏسي) نه مَرۡڪُ ڇوته الله مَرۡڪَ ڪندڙن کي دوست نه رکندو آھي.'),
(3329, 28, 77, '۽ الله توکي جيڪي ڏنو آھي تنھن سان آخرت جي گھر جي ڀلائي طلب ڪر ۽ دنيا مان پنھنجو (نيڪ عمل ڪرڻ جو) حصّو نه وسار ۽ جھڙي طرح الله توسان چڱائي ڪئي آھي تھڙي طرح (تون سندس خلق سان) چڱائي ڪر ۽ زمين ۾ فساد وجھڻ نه گھُر، ڇوته الله فساد ڪرڻ وارن کي دوست نه رکندو آھي.'),
(3330, 28, 78, '(قارون) چيو ته اُھو (گھڻومال) ته رڳو ھڪ ھُنر سان جو مون وٽ آھي مون کي مليو آھي، نه ڄاتو ھوائين ڇا ته بيشڪ الله کانئس اڳ (اُھي) ڪيترائي جُڳ ناس ڪري ڇڏيا ھوا جيڪي کانئس ڏاڍي سگھ وارا ۽ گھڻي جمعيّت وارا ھوا، ۽ ڏوھارين کان سندن ڏوھن بابت (عذر) نه پڇبو.'),
(3331, 28, 79, 'پوءِ (ھڪ ڏينھن) پنھنجي قوم جي سامھون پنھنجي سينگار سان نڪتو، جيڪي دنيا جي حياتي گھڙندا ھوا تن چيو ته ھيء ارمان اھڙو (مال) جيڪر اسان کي (به) ھجي ھا جھڙو قارون کي ڏنو ويو آھي بيشڪ اُھو وڏي نصيب وارو آھي.'),
(3332, 28, 80, '۽ جن کي علم ڏنو ويو تن چيو ته اوھان لاءِ خرابي ھُجي الله جو ثواب اُنھيءَ ماڻھوءَ لاءِ ڀَلو آھي جنھن ايمان آندو ۽ چڱا ڪم ڪيا، اِھا (ڳالھ) صابرن کانسواءِ (ٻئي) ڪنھنجي دل ۾ نه گِھڙندي آھي.'),
(3333, 28, 81, 'پوءِ کيس ۽ سندس گھر کي زمين ۾ ڳھايوسون، پوءِ الله کانسواءِ ڪا ٽولي اُن لاءِ ڪانه ھئي جا کيس مدد ڏئي، ۽ نڪي پاڻ وير وٺڻ وارن مان ھو.'),
(3334, 28, 82, '۽ جن ڪالھ سندس مرتبي جي سَڌ ٿي ڪئي سي صبح جو چوڻ لڳا ته عجب آھي ته الله پنھنجن ٻانھن مان جنھن لاءِ گھرندو آھي (تنھنجي) روزي ڪشادي ڪندو آھي ۽ تنگ ڪندو آھي، جيڪڏھن الله اسان تي احسان نه ڪري ھا ته اسان کي (به زمين ۾) ڳھائي ڇڏي ھا، نه ڄاتو اٿيئي ته ڪافرن کي ڇوٽڪارو نه آھي.'),
(3335, 28, 83, 'اِھو آخرت جو گھر آھي جنھن کي اسان انھن لاءِ مقرر ڪيو آھي جيڪي مُلڪ ۾ وڏائي نه ڪندا آھن ۽ نڪي فساد وجھڻ (گھرندا آھن)، ۽ (چڱي) عاقبت پرھيزگارن لاءِ آھي.'),
(3336, 28, 84, 'جيڪو چڱائي آڻيندو تنھن لاءِ اُن کان ڀلو (بدلو) آھي، ۽ جيڪو مدائي آڻيندو ته مدائين ڪرڻ وارا رڳو اُھا سزا لھندا جيڪي ڪيائون ٿي.'),
(3337, 28, 85, 'بيشڪ جنھن (الله) توتي قرآن نازل ڪيو سو موٽڻ جي جاءِ ڏانھن توکي ضرور موٽائڻ وارو آھي، چؤ ته جنھن ھدايت آندي تنھن کي منھنجو پالڻھار چڱو ڄاڻندڙ آھي ۽ اُنھيءَ کي به جيڪو پڌريءَ گمراھي ۾ آھي.'),
(3338, 28, 86, '۽ توکي ڪو آسرو نه ھو ته تو ڏانھن (ھيءُ) ڪتاب لاھبو پر تنھنجي پالڻھار جي ٻاجھ سان (لاٿو ويو) پوءِ ڪافرن جو مددگار ھرگز نه ٿج.'),
(3339, 28, 87, '۽ (اي پيغمبر) توکي الله جي آيتن (جي پھچائڻ) کان اُن پڃاڻا جو تو ڏانھن موڪليون ويون (ڪافر) نه جھلين ۽ پنھنجي پالڻھار ڏانھن (ماڻھن) کي سڏ، ۽ مشرڪن مان ڪڏھن نه ٿيءُ.'),
(3340, 28, 88, '۽ الله سان گڏ ٻئي ڪنھن کي معبود (سمجھي) نه سڏ، اُن کانسواءِ ڪو عبادت جو لائق نه آھي، سندس ذات کانسواءِ سڀڪا شيء ناس ٿيڻ واري آھي، سڀڪو حُڪم اُن جو ئي آھي ۽ ڏانھس موٽايا ويندؤ.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الٓمّٓ.'),
(3342, 29, 2, 'ماڻھن ڀانيو آھي ڇا ته سندن (رڳو ايتري) چوڻ تي ته ايمان آندوسون اُھي ڇڏي ڏبا ۽ اُھي نه آزمائبا؟'),
(3343, 29, 3, '۽ جيڪي کانئن اڳ ھوا تن کي بيشڪ پرکيوسون پوءِ جيڪي سچا آھن تن کي الله ضرور ڌار ڪندو ۽ ڪُوڙن کي (به) ضرور ڌار ڪندو.'),
(3344, 29, 4, 'جيڪي بڇڙايون ڪن ٿا تن ڀانيو آھي ڇا ته اسان کون اڳتي نڪري ويندا؟ جيڪو نبيرو ڪن ٿا سو بڇڙو آھي.'),
(3345, 29, 5, 'جيڪو الله جي ملڻ جي اميد رکندو آھي (سو سمجھي) ته بيشڪ الله جي ٺھرايل مُدت ضرور اچڻي آھي، ۽ اُھو ٻڌندڙ ڄاڻندڙ آھي.'),
(3346, 29, 6, '۽ جيڪو (الله جي دين لاءِ) ڪوشش ڪري سو رڳو پنھنجي لاءِ ئي ڪوشش ڪري ٿو، ڇوته الله جھانن کان بي پرواھ آھي.'),
(3347, 29, 7, '۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کان سندن مدايون ضرور ميٽيندا سون ۽ جيڪي ڪندا ھوا تنھن جو کين ڏاڍو چڱو بدلو ڏينداسون.'),
(3348, 29, 8, '۽ ماڻھوءَ کي پنھنجي ماءُ پيءُ سان چڱائي ڪرڻ جو حُڪم ڪيوسون، ۽ (اي ماڻھو) جيڪڏھن (اُھي) مون سان ڪنھن کي شريڪ ڪرڻ جي توکي ڪوشش ڪن جنھن (جي حقيقت) جي توکي ڪا ڄاڻپ نه آھي ته اُنھن جو چيو نه مڃ، اوھان جو مون ڏانھن موٽڻ آھي تنھنڪري جيڪي ڪندا ھيؤ تنھنجي اوھان کي سُڌ ڏيندس.'),
(3349, 29, 9, '۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي صالحن (جي ٽوليءَ) ۾ ضرور داخل ڪنداسون.'),
(3350, 29, 10, '۽ ماڻھن مان ڪي (اھڙا) آھن جي چوندا آھن ته الله تي ايمان آندو اٿون پوءِ جڏھن الله (جي واٽ) ۾ اُن کي ڪو ايذاءُ پھچايو ويندو آھي (تڏھن) ماڻھن جي ايذائڻ کي الله جي عذاب وانگر سمجھندو آھي، ۽ جيڪڏھن تنھنجي پالڻھار وٽان ڪا مدد ايندي آھي ته چوندا آھن ته بيشڪ اسين اوھان ساڻ ھواسون، جيڪي جھان (وارن) جي دلين ۾ آھي سو الله چڱي طرح ڄاڻندڙ نه آھي ڇا؟'),
(3351, 29, 11, '۽ ته جن ايمان آندو آھي تن کي الله ڌار ڪندو آھي ۽ منافقن کي به ڌار ڪندو.'),
(3352, 29, 12, '۽ ڪافر مؤمنن کي چون ٿا (اوھين) اسان جي واٽ جي تابعداري ڪريو ۽ (اسين) اوھان جون مدايون کڻنداسين، حالانڪ اُھي سندين مداين مان ڪجھ به کڻڻ وارا نه آھن، بيشڪ اُھي ڪُوڙا آھن.'),
(3353, 29, 13, '۽ پنھنجن (گناھن جون) ڳٺڙيون ۽ (ٻيون ڪيئي) ڳٺڙيون پنھنجن گناھن سان ضرورکڻندا، ۽ جيڪو ٺاھ ٺاھيندا آھن تنھن بابت قيامت جي ڏينھن ضرور پڇيا ويندا.'),
(3354, 29, 14, '۽ بيشڪ نوح کي سندس قوم ڏانھن موڪليوسون ۽ پوءِ پنجاھ ورھ گھٽ (ھڪ) ھزار سال منجھن رھيو، پوءِ کين طوفان پڪڙيو ۽ اُھي ظالم ھوا.'),
(3355, 29, 15, 'پوءِ کيس ۽ ٻيڙيءَ وارن کي (ٻڏڻ کان) بچايوسون ۽ اُن (ٻيڙيءَ) کي جھان (وارن) لاءِ نشاني ڪيوسون.'),
(3356, 29, 16, '۽ ابراھيم کي (موڪليوسون) جڏھن پنھنجي قوم کي چيائين ته الله جي عبادت ڪريو ۽ کانئس ڊڄو، اھو اوھان لاءِ ڀلو آھي جيڪڏھن اوھين ڄاڻندا آھيو.'),
(3357, 29, 17, 'اوھين ته الله کانسواءِ بُتن کي پوڄيندا آھيو ۽ اوھين (ڪوڙو) ٺاھ ٺاھيندا آھيو، بيشڪ الله کانسواءِ جن کي پوڄيندا آھيو سي ته اوھان کي روزي (ڏيڻ) جي (ڪي) مالڪ نه آھن تنھنڪري الله وٽان رزق طلب ڪريو ۽ سندس عبادت ڪريو ۽ سندس شڪرانو ڪريو، ڏانھس موٽايا ويندؤ.'),
(3358, 29, 18, '۽ جيڪڏھن ڪوڙو ڀانيو ٿا ته (ڇا ٿيو) بيشڪ اوھان کان اڳ (ڪيترين) اُمتن (پنھنجن پيغمبرن کي) ڪوڙو ڀانيو ھو، ۽ پيغمبر تي پڌري پيغام پھچائڻ کانسواءِ (ٻيو ڪي رکيل) نه آھي.'),
(3359, 29, 19, 'نه ڏٺو اٿن ڇا ته الله خلق کي ڪھڙي طرح نئين سِر بنائيندو آھي؟ وري ٻيھر اُن کي بڻائيندو، بيشڪ اِھو (ڪم) الله تي آسان آھي.'),
(3360, 29, 20, '(کين) چؤ ته مُلڪ ۾ گھمو پوءِ نظر ڪريو ته الله ڪئن نئين سِر خلق کي پيدا ڪيو وري اُن پيدا ڪيل کي الله ٻيھر پيدا ڪندو، ڇوته الله سڀڪنھن شيء تي وس وارو آھي.'),
(3361, 29, 21, 'جنھن کي وڻيس تنھن کي عذاب ڪري ۽ جنھن تي وڻيس رحم ڪري، ۽ (اوھين) ڏانھس موٽايا ويندؤ.'),
(3362, 29, 22, '۽ اوھين نڪي زمين ۾ ۽ نڪي آسمان ۾ عاجز ڪرڻ وارا آھيو، ۽ اوھان جو الله کانسواءِ نڪو سڄڻ ۽ نڪو مددگار آھي.'),
(3363, 29, 23, '۽ جن الله جي آيتن ۽ سندس ملڻ جو انڪار ڪيو سي منھنجي ٻاجھ کان نا اُميد ٿيا آھن ۽ اُنھن لاءِ ئي ڏکوئيندڙ عذاب آھي.'),
(3364, 29, 24, 'پوءِ ابراھيم جي قوم جي ورندي ھن چوڻ کانسواءِ (ٻي) ڪانه ھئي ته اُن کي ڪُھو يا اُن کي ساڙيو پوءِ الله کيس باھ کان بچايو، بيشڪ ھن (احوال) ۾ مڃيندڙ قوم لاءِ وڏيون نشانيون آھن.'),
(3365, 29, 25, '۽ (ابراھيم) چيو ته اوھان دنيا جي حياتيءَ ۾ رڳو پاڻ ۾ دوستيءَ جي ڪري الله کانسواءِ بتن کي اختيار ڪيو آھي، وري قيامت جي ڏينھن اوھين ھڪ ٻئي جو انڪار ڪندؤ ۽ اوھان مان ھڪڙا ٻـين تي لعنت ڪندا، ۽ اوھان جي جاءِ دوزخ آھي ۽ اوھان جو ڪوبه مددگار نه آھي.'),
(3366, 29, 26, 'پوءِ ابراھيم تي لُوط ايمان آندو، ۽ (ابراھيم) چيو ته بيشڪ آءٌ پنھنجي پالڻھار ڏانھن وطن ڇڏڻ وارو آھيان، بيشڪ اُھو غالب حڪمت وارو آھي.'),
(3367, 29, 27, '۽ اُن کي اسحاق ۽ يعقوب بخشيوسون ۽ سندس اولاد ۾ پيغمبري ۽ ڪتاب مقرر ڪيوسون ۽ کيس دنيا ۾ سندس اجر ڏنوسون، ۽ بيشڪ اُھو آخرت ۾ (به) صالحن مان آھي.'),
(3368, 29, 28, '۽ لُوط کي (پيغمبر ڪري موڪليوسون) جڏھن پنھنجي قوم کي چيائين ته اوھين اُھو بي حيائي جو ڪم ڪندا آھيو جو جھان وارن مان ڪنھن ھڪ (به) اھو اوھان کان اڳ نه ڪيو آھي.'),
(3369, 29, 29, 'ڇا اوھين مڙسن ڏانھن (شھوت سان) ويندا آھيو ۽ واٽ تي ڦُر ڪندا آھيو، ۽ پنھنجن مجلسن ۾ بد ڪم ڪندا آھيو؟ پوءِ سندس قوم جي ورندي ھن چوڻ کان سواءِ نه ھئي ته جيڪڏھن سچن مان آھين ته الله جو عذاب اسان وٽ آڻ.'),
(3370, 29, 30, '(لُوط) چيو ته اي منھنجا پالڻھار مون کي فسادين جي قوم تي مدد ڏي.'),
(3371, 29, 31, '۽ جنھن مھل اسان جي قاصدن ابراھيم وٽ خوشخبري آندي (تنھن مھل) چيائون ته اسين ھن ڳوٺ وارن کي ناس ڪرڻ وارا آھيون، ڇوته اُتي جي رھڻ وارا ظالم (ٿيا) آھن.'),
(3372, 29, 32, '(ابراھيم) چيو ته بيشڪ اُن ۾ لُوط آھي، چيائون ته جيڪو به منجھس آھي تنھن کي اسين چڱو ڄاڻندڙ آھيون، کيس ۽ سندس گھر جي ڀاتين کي ضرور بچائينداسون سواءِ سنديس زال جي جا باقي رھيلن مان ٿيندي.'),
(3373, 29, 33, '۽ جنھن مھل اسان جا قاصد لُوط وٽ آيا تنھن مھل انھن جي (اچڻ) سببان ناراض ٿيو ۽ اُنھن جي (اچڻ) سببان دل تنگ ٿيو ۽ (اُنھن) چيو ته نڪي ڊڄ ۽ نڪي غم کاءُ، بيشڪ اسين توکي ۽ تنھنجي گھر جي ڀاتين کي بچائڻ وارا آھيون سواءِ تنھنجي زال جي جا باقي رھيلن مان ٿيندي.'),
(3374, 29, 34, 'بيشڪ اسين ھن ڳوٺ وارن تي ھڪ عذاب انھي سببان آسمان کان لاھڻ وارا آھيون جو بدڪار ٿيا آھن.'),
(3375, 29, 35, '۽ بيشڪ اُن ڳوٺ جي پڌري نشاني سمجھندڙ قوم لاءِ ڇڏي سون.'),
(3376, 29, 36, '۽ مدين (وارن) ڏانھن سندن ڀاءُ شعيب کي موڪليوسون پوءِ چيائين ته اي منھنجي قوم الله جي عبادت ڪريو ۽ قيامت جي ڏينھن جي اُميد رکو ۽ فسادي ٿي مُلڪ ۾ نه ڦِرو.'),
(3377, 29, 37, 'پوءِ اُن کي ڪوڙو ڀانيائون تنھنڪري کين زلزلي اچي ورتو پوءِ پنھنجن گھرن ۾ اونڌا ٿي (مري) پيا.'),
(3378, 29, 38, '۽ عاد ۽ ثمود کي (چٽ ڪيوسون) ۽ بيشڪ سندين ڪن جاين مان اوھان کي پڌرو ٿي چڪو آھي، ۽ شيطان اُنھن لاءِ سندن عمل سينگاريا ھوا پوءِ کين سِڌي واٽ کان جھليائين ۽ (اُھي) سمجھ وارا (به) ھوا.'),
(3379, 29, 39, '۽ قارون ۽ فرعون ۽ ھامان کي (به چٽ ڪيوسون)، ۽ بيشڪ مُوسىٰ پڌرن معجزن سان وٽن آيو پوءِ مُلڪ ۾ وڏائي ڪيائون ۽ (اڳتي) وڌي ويندڙ نه ھوا.'),
(3380, 29, 40, 'پوءِ سڀڪنھن کي سندس گناھ سببان پڪڙيوسون، پوءِ منجھانئن ڪن تي سخت آنڌي موڪلي سون، ۽ منجھانئن ڪن کي ڪڙڪي (اچي) ورتو، ۽ منجھانئن ڪن کي زمين ۾ ڳھايوسون، ۽ منجھانئن ڪن کي ٻوڙيوسون، ۽ ڪڏھن ٿيڻو نه ھو جو الله مٿن ظلم ڪري پر ھنن پنھنجو پاڻ تي ظلم ٿي ڪيو.'),
(3381, 29, 41, 'جن الله کانسواءِ ٻيا دوست ورتا آھن تن جو مثال ڪوريئڙي جي مثال وانگر آھي، جنھن گھر ٺاھيو، ۽ بيشڪ سڀني گھرن ۾ وڌيڪ ھيڻو ڪوريئڙي جو گھر آھي، جيڪڏھن (اھا ڳالھ) ڄاڻن ھا (ته ائين نه ڪن ھا).'),
(3382, 29, 42, 'بيشڪ جنھن شيء کي الله کانسواءِ پوڄيندا آھن سا جيڪا به ھجي تنھن کي الله ڄاڻندو آھي، ۽ اُھو غالب حڪمت وارو آھي.'),
(3383, 29, 43, '۽ اھي مثال آھن جن کي ماڻھن لاءِ بيان ڪندا آھيون، ۽ انھن کي ڄاڻندڙن کانسواءِ ڪونه سمجھندو آھي.'),
(3384, 29, 44, 'الله آسمانن ۽ زمين کي (چڱيء) رِٿ سان خلقيو آھي، بيشڪ اِن (ڪم) ۾ مؤمنن لاءِ (وڏي) نشاني آھي.'),
(3385, 29, 45, '(اي پيغمبر) ڪتان مان جيڪي توڏانھن وحي ڪيو ويو سو پڙھ ۽ نماز پڙھ، بيشڪ نماز بي حيائي ۽ مَدي ڪم (ڪرڻ) کان جھليندي آھي، بيشڪ الله جو ياد ڪرڻ سڀ کان وڏو آھي، ۽ جيڪي ڪندا آھيو سو الله ڄاڻندو آھي.'),
(3386, 29, 46, '۽ ڪتاب وارن مان جن بي انصافي ڪئي تن کانسواءِ ٻـين سان ڏاڍي چڱي طريقي کانسواءِ تڪرار نه ڪريو ۽ چئو ته جيڪي اسان ڏانھن نازل ڪيو ويو آھي ۽ جيڪي اوھان ڏانھن نازل ڪيو ويو آھي تنھن تي اسان ايمان آندو آھي ۽ اسان جو معبود ۽ اوھان جو معبُود ھڪ آھي ۽ اسين سندس فرمانبردار آھيون.'),
(3387, 29, 47, '۽ (جھڙي طرح اڳيان ڪتاب نازل ڪياسون) اھڙي طرح اسان توڏانھن ڪتاب نازل ڪيو، پوءِ جن کي ڪتاب ڏنوسون سي اُن (قرآن) کي مڃيندا آھن، ۽ انھن (عربن) مان ڪو (اھڙو) آھي جو اُن تي ايمان آڻيندو آھي، ۽ ڪافرن کانسواءِ (ٻيو) ڪو اسان جي آيتن جو انڪار نه ڪندو آھي.'),
(3388, 29, 48, '۽ اُنھيءَ (قرآن جي لھڻ) کان اڳ ڪو ڪتاب نڪي تون پڙھندو ھئين ۽ نڪي اُھو پنھنجي سڄي ھٿ سان لکندو ھئين (جيڪڏھن لکين پڙھين ھا) ته اُنھيءَ مھل اُھي ڪوڙا ضرور شڪ ۾ پون ھا.'),
(3389, 29, 49, 'بلڪ اُھو (قرآن) پڌريون آيتون (آھن) انھن جي سينن ۾ (محفوظ) آھي جن کي عِلم ڏنو ويو آھي، ۽ ظالمن کان سواءِ (ٻيو) ڪو اسان جي آيتن جو انڪار نه ڪندو آھي.'),
(3390, 29, 50, '۽ چون ٿا ته سندس پالڻھار کان مٿس مُعجزا ڇونه نازل ڪيا ويا آھن؟ (کين) چؤ مُعجزا (ته رڳو منھنجي) الله وٽ آھن، ۽ آءٌ ته رڳو پڌرو ڊيڄاريندڙ آھيان.'),
(3391, 29, 51, 'اسان توتي ڪتاب نازل ڪيو جو کين پڙھي ٻڌائبو آھي سو کين ڪافي نه آھي ڇا، بيشڪ ھن ۾ مؤمنن جي قوم لاءِ ٻاجھ ۽ نصيحت آھي.'),
(3392, 29, 52, '(کين) چؤ ته الله منھنجي ۽ اوھان جي وچ ۾ شاھد بس آھي، جيڪي آسمانن ۽ زمين ۾ آھي سو اُھو ڄاڻندو آھي، ۽ جن ڪوڙ کي مڃيو ۽ الله جو انڪار ڪيو سي ئي ڇيئي وارا آھن.'),
(3393, 29, 53, '۽ توکان عذاب تڪڙو گھرندا آھن، ۽ جيڪڏھن (اُن جي) مُدّت مُقرر ٿيل نه ھجي ھا ته کين عذاب ضرور پھچي ھا، ۽ (اھو) مٿن پڪ اوچتو ايندو ۽ اُھي بي خبر ھوندا.'),
(3394, 29, 54, 'توکان عذاب تڪڙو گھرندا آھن، بيشڪ دوزخ ڪافرن کي (اُنھيءَ ڏينھن) وڪوڙيندڙ آھي.'),
(3395, 29, 55, 'جنھن ڏينھن کين عذاب سندن مٿن کان ۽ سندن پيرن جي ھيٺ کان ويڙھيندو ۽ (الله) چوندو ته جيڪي ڪندا ھيؤ تنھنجو (مزو) چکو.'),
(3396, 29, 56, 'اي منھنجا مؤمن ٻانھؤ بيشڪ منھنجي زمين ويڪري آھي تنھنڪري خاص منھنجي عبادت ڪريو.'),
(3397, 29, 57, 'سڀڪو جيءُ موت جو مزو چکڻ وارو آھي، وري اسان ڏي موٽائبؤ.'),
(3398, 29, 58, '۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي ضرور بھشت جي ماڙين ۾ ٽِڪائينداسون جن جي ھيٺان واھيون وھنديون آھن منجس سدائين رھڻ وارا آھن، (انھن چڱن) ڪمن ڪندڙن جي مزوري چڱي آھي.'),
(3399, 29, 59, 'جن صبر ڪيو ۽ پنھنجي پالڻھار تي ڀروسو ڪندا آھن.'),
(3400, 29, 60, '۽ ڪيترائي جانور پنھنجي روزي ساڻ نه کڻندا آھن، الله تعالىٰ انھن کي ۽ اوھان کي روزي ڏيندو آھي، ۽ اُھو ئي ٻڌندڙ ۽ ڄاڻندڙ آھي.'),
(3401, 29, 61, '۽ جيڪڏھن کائن پُڇين ته ڪنھن آسمانن ۽ زمين کي بڻايو ۽ (ڪنھن) سج ۽ چنڊ کي نِوايو آھي؟ ته ضرور چوندا ته الله، پوءِ ڪيڏانھن ڦيرايا ويندا آھن.'),
(3402, 29, 62, 'الله پنھنجن ٻانھن مان جنھن لاءِ گھري تنھنجي روزي ڪشادي ڪندو آھي ۽ (جنھن لاءِ گھري ته تَنھنجي) گھٽ ڪندو آھي، ڇوته الله ھر شيءَ کي ڄاڻندڙ آھي.'),
(3403, 29, 63, '۽ جيڪڏھن کائن پڇين ته ڪنھن آسمان کان پاڻي وسايو پوءِ اُن سان زمين کي اُن جي مرڻ کانپوءِ جيئرو ڪيو ته ضرور چوندا ته الله، (اي پيغمبر کين) چؤ ته سڀ ساراھ الله کي جڳائي، بلڪ منجھائن گھڻا نه ڄاڻندا آھن.'),
(3404, 29, 64, '۽ ھيءَ دنيا جي حياتي ته تماشي ۽ راند کانسواءِ (ٻيو) ڪي نه آھي، ۽ بيشڪ آخرت جي حويلي ئي سدائين حياتيءَ واري آھي، جيڪڏھن ڄاڻن ھا (ته آخرت کي دنيا کان وڌيڪ پسند ڪن ھا).'),
(3405, 29, 65, 'پوءِ (اُھي) جڏھن ٻيڙين ۾ چڙھندا آھن (تڏھن) رڳو الله کي سندس اعتقاد رکي سڏيندا آھن، پوءِ جڏھن کين ڀر ڏانھن پھچائيندو (بچائي) آھي (تڏھن) اُتي جو اُتي (ھن ڪري) شرڪ ڪندا آھن.'),
(3406, 29, 66, 'ته جيڪي کين ڏنوسون تنھنجي بي شُڪري ڪن، ۽ ته خوش گذارين، پوءِ سگھوئي (حال جي حقيقت) ڄاڻندا.'),
(3407, 29, 67, 'نه ڏٺائون ڇا ته اسان (مڪي جي) حرم کي امن جي جاءِ ڪئي؟ حالانڪ سندس آس پاس کان ماڻھو اُمالڪ کڄي ويندا آھن، پوءِ ڇا ڪوڙ کي مڃيندا آھن ۽ الله جي نعمتن جو انڪار ڪندا آھن؟'),
(3408, 29, 68, '۽ اُن کان وڌيڪ ڪير ظالم آھي جيڪو الله تي ڪوڙو ٺاھ ٺاھي يا جڏھن وٽس حق آيو تڏھن اُن کي ڪوڙو ڄاڻي؟ ڪافرن جي جاءِ دوزخ ۾ نه آھي ڇا؟'),
(3409, 29, 69, '۽ جن اسان جي واٽ ۾ ڪوشش ڪئي تن کي ضرور پنھنجون واٽون ڏيکارينداسون، ۽ بيشڪ الله ڀلارن سان گڏ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الٓمّٓ.'),
(3411, 30, 2, 'رُومين شڪست کاڌي.'),
(3412, 30, 3, 'ويجھي مُلڪ ۾ ۽ اُھي ئي پنھنجي شڪست کائڻ کانپوءِ سگھوئي غالب ٿيندا.'),
(3413, 30, 4, 'ٿورن ورھن ۾، الله جي ئي (ھٿ ۾) حڪومت اڳي توڙي پوءِ آھي، ۽ اُنھيءَ ڏينھن مُؤمن خوش ٿيندا.'),
(3414, 30, 5, 'الله جي مدد سان، (الله) جنھن کي گھري تنھن کي سوڀ ڏيندو آھي، ۽ اُھو ئي غالب مھربان آھي.'),
(3415, 30, 6, 'الله انجام ڪيو آھي، الله پنھنجو انجام نه ڦيرائيندو پر گھڻا ماڻھو نه ڄاڻندا آھن.'),
(3416, 30, 7, 'ظاھر دنيا جي حياتيءَ جون (رٿون) ڄاڻندا آھن، ۽ اُھي آخرت کان بي خبر آھن.'),
(3417, 30, 8, 'پنھنجين دلين ۾ نه پروڙيائون ڇا؟ ته الله آسمانن ۽ زمين کي ۽ جيڪي انھن جي وچ ۾ آھي تنھنکي پوريءَ رِٿ ۽ مُقرّر مُدّت کانسواءِ نه بڻايو آھي، ۽ بيشڪ ماڻھن مان گھڻا پنھنجي پالڻھار جي مِلڻ کان مُنڪر آھن.'),
(3418, 30, 9, 'مُلڪ ۾ نه گھميا آھن ڇا جو نھارين ته جيڪي کانئن اڳ ھوا تن جي پڇاڙي ڪھڙي طرح ٿي، جي کائن وڌيڪ سگھ وارا ھوا ۽ زمين کيڙيائون ۽ اُن کي آباد ڪيائون وڌيڪ تنھن کان جا ھنن آباد ڪئي ۽ سندن پيغمبر وٽن مُعجزن سان آيا، پوءِ الله جو شان اھڙو نه ھو جو مٿن ظلم ڪري پر پاڻ تي پاڻ ظلم ڪيائون ٿي.'),
(3419, 30, 10, 'وري جن بڇڙائي ڪئي تن جي پڇاڙي بڇڙي ٿي ھِن ڪري جو الله جي آيتن کي ڪوڙ ڀانيائون ۽ مٿن ٺـٺوليون ڪندا ھوا.'),
(3420, 30, 11, 'الله خلق کي نئين سر بڻائي ٿو وري ان کي ٻيھر بڻائيندو وري ڏانھس اوھين موٽائبؤ.'),
(3421, 30, 12, '۽ جنھن ڏينھن قيامت کڙي ٿيندي تنھن ڏينھن گنھگار نا اُميد ٿيندا.'),
(3422, 30, 13, '۽ (الله سان مُقرّر ڪيلن) سندن شريڪن مان ڪوبه انھن لاءِ سفارش ڪرڻ وارو نه ٿيندو ۽ پنھنجن (ٺھرايل) شريڪن جا منڪر ٿيندا.'),
(3423, 30, 14, '۽ جنھن ڏينھن قيامت قائم ٿيندي تنھن ڏينھن ماڻھو ڌاروڌار ٿيندا.'),
(3424, 30, 15, 'پوءِ جن ايمان آندو ۽ چڱا ڪم ڪيا سي باغن ۾ خوش ڪبا.'),
(3425, 30, 16, '۽ جن ڪُفر ڪيو ۽ اسان جي آيتن کي ۽ قيامت جي ملڻ کي ڪوڙ ڄاتو سي عذاب ۾ حاضر ڪيل ھوندا.'),
(3426, 30, 17, 'پوءِ جنھن مھل سانجھي ڪريو ۽ جنھن مھل صبح ڪريو تنھن مھل الله جي پاڪائي بيان ڪريو.'),
(3427, 30, 18, '۽ آسمانن ۽ زمين ۾ اُن جي ساراھ آھي ۽ پوئين پھر ۽ جنھن مھل منجھند ڪريو (تنھن مھل به پاڪائي بيان ڪريو).'),
(3428, 30, 19, 'مُئي مان جيئرو ڪڍندو آھي ۽ جيئري مان مئو ڪڍندو آھي ۽ زمين کي ان جي مرڻ کانپوءِ جياريندو آھي، ۽ اھڙي طرح (اوھين قبرن مان) ڪڍبؤ.'),
(3429, 30, 20, '۽ سندس نشانين مان ھيءُ آھي جو اوھان کي مٽيءَ مان بڻايائين وري اُنھيءَ مھل اوھين ماڻھو ٿي گھمو ڦرو ٿا.'),
(3430, 30, 21, '۽ سندس نشانين مان ھيءُ (به) آھي جو اوھان جي جنس مان اوھان لاءِ زالون پيدا ڪيائين ته انھن سان آرام وٺو ۽ اوھان جي وچ ۾ پيار ۽ ٻاجھ پيدا ڪيائين، بيشڪ ھن ۾ اُنھيءَ قوم لاءِ نشانيون آھن جيڪي ڌيان رکندا آھن.'),
(3431, 30, 22, 'آسمانن ۽ زمين جو بڻائڻ ۽ اوھان جي زبانن ۽ شڪلن جو قِسمين قِسمين ھجڻ سندس نشانين مان آھي، بيشڪ ان ۾ ڄاڻندڙن لاءِ نشانيون آھن.'),
(3432, 30, 23, '۽ اوھان جو رات ۽ ڏينھن ۾ سمھڻ ۽ اوھان جو سندس فضل مان روزي طلب ڪرڻ سندس نشانين مان آھي، بيشڪ ھن ۾ اُن قوم لاءِ نشانيون آھن جيڪي ٻڌندا آھن.'),
(3433, 30, 24, '۽ سندس نشانين مان (ھيءُ به) آھي جو اوھان کي ڊيڄارڻ ۽ اُميد رکڻ لاءِ وِڄ ڏيکاريندو آھي ۽ آسمان کان پاڻي وسائيندو آھي پوءِ اُن سان زمين کي اُن جي مرڻ کانپوءِ جياريندو آھي، بيشڪ ھِن ۾ اُنھيءَ قوم لاءِ نشانيون آھن.'),
(3434, 30, 25, '۽ سندس حُڪم سان آسمان ۽ زمين جو بيھڻ سندس نشانين مان آھي، وري جنھن مھل اوھان کي زمين مان ھڪ ئي ڀيرو سڏيندو (تنھن مھل) جھٽ پٽ اوھين نڪرندؤ.'),
(3435, 30, 26, '۽ جيڪي آسمانن ۽ زمين ۾ آھي سو سندس آھي، سڀئي سندس فرمانبردار آھن.'),
(3436, 30, 27, '۽ اُھوئي آھي جيڪو خلق کي نئين سِر بڻائيندو آھي وري اُن کي ورائي بڻائيندو ۽ اُھو کيس بلڪل سھنجو آھي، ۽ آسمانن ۽ زمين ۾ سندس ئي صفت تمام مٿاھين آھي، ۽ اھو غالب حڪمت وارو آھي.'),
(3437, 30, 28, '(الله) اوھان لاءِ اوھان جي حال جو ھڪ مثال بيان ڪيو آھي، (ته) اوھان جا ھٿ جن (ٻانھن) جا مالڪ ٿيا آھن سي جيڪي (مال) اوھان کي ڏنوسين تنھن ۾ اوھان جا ڀائيوار آھن ڇا؟ جو اُھي ۽ اوھين اُن (مال) ۾ برابر (حصّي وارا) ھجو (۽ اوھين) انھن جي (دعوى) کان اھڙي طرح ڊڄندا آھيو جھڙي طرح اوھان کي پاڻ جھڙن (جي ڀائيواريءَ) کان ڀؤ آھي، اھڙي طرح انھيءَ قوم لاءِ نشانيون کولي بيان ڪندا آھيون جيڪي سمجھ رکندا آھن.'),
(3438, 30, 29, 'بلڪ ظالم اَڻ ڄاڻائي سان پنھنجن سَڌن جا تابعدار ٿيا آھن، پوءِ جنھن کي الله گمراھ ڪيو تنھن کي ڪير ھدايت ڪندو، ۽ اُنھن لاءِ ڪو مددگار ڪونھي.'),
(3439, 30, 30, 'پوءِ (اي پيغمبر) پنھنجي منھن کي دين لاءِ ھڪ طرفو ڪري بيھار، الله جي دين جي (پيروي ڪر) جنھن تي ماڻھن کي پيدا ڪيائين، الله جي خلقڻ کي ڪا مٽ سٽ نه آھي، اھو سِڌو دين آھي، پر گھڻا ماڻھو نه ڄاڻندا آھن.'),
(3440, 30, 31, 'ڏانھس موٽندڙ ٿي (عبادت ڪريو) ۽ کانئس ڊڄو ۽ نماز پڙھندا رھو ۽ مشرڪن مان نه ٿيو.'),
(3441, 30, 32, 'اُنھن مان (نه ٿيو) جن پنھنجي دين کي ڌارو ڌار ڪيو ۽ ٽوليون ٽوليون ٿي ويا، سڀڪا ٽولي اُنھيءَ تي خوش آھي جيڪي وٽس آھي.'),
(3442, 30, 33, '۽ جڏھن ماڻھن کي ڪا اوکائي پھچندي آھي (تڏھن) پنھنجي پالڻھار ڏي مُھاڙ ڪري کيس سڏيندا آھن وري جڏھن اُھو پاڻ وٽان کين ٻاجھ چکائيندو آھي (تڏھن) اُنھيءَ ئي مھل منجھائن ڪا ٽولي پنھنجي پالڻھار سان (ھن لاءِ) شريڪ بڻائيندي آھي.'),
(3443, 30, 34, 'ته جيڪي کيس ڏنوسون تنھنجي بي شُڪري ڪن، پوءِ آسودا ٿيو، پوءِ سگھوئي ڄاڻندؤ.'),
(3444, 30, 35, 'مٿن ڪا سَند لاٿي سون ڇا جو اُھا اُنھيءَ (شيء) جي (سچائي) بابت ڳالھائي ٿي جنھن شيءَ کي الله سان شريڪ ٺھرائيندا آھن.'),
(3445, 30, 36, '۽ جڏھن ماڻھن کي ٻاجھ چکائيندا آھيون (تڏھن) اُن سان سَرھا ٿيندا آھن، ۽ جيڪڏھن انھي سببان جو سندن ھٿن (مدايون) اڳي موڪليون، کين ڪو ڏک پھچندو آھي ته اُنھي ئي مھل نا اُميد ٿيندا آھن.'),
(3446, 30, 37, 'نه ڏٺو اٿن ڇا؟ ته الله جنھن لاءِ گھرندو آھي تنھنجي روزي ڪشادي ڪندو آھي ۽ (جنھن لاءِ گھرندو آھي تنھنجي) گھٽ ڪندو آھي، بيشڪ ھن ۾ مڃيندڙ قوم لاءِ نشانيون آھن.'),
(3447, 30, 38, 'تنھنڪري مائٽي واري ۽ مِسڪين ۽ مُسافر کي سندس حق ڏي، اھو انھن لاءِ ڀلو آھي جيڪي الله جو راضپو گھرندا آھن، ۽ اُھي ئي ڇوٽڪاري وارا آھن.'),
(3448, 30, 39, '۽ وياج مان جيڪي ڏنو ھجيوَ ته ماڻھن جي مال ۾ وَڌي سو الله وٽ نه وڌندو، ۽ الله جو راضپو گھرندڙ ٿي زڪوٰة مان جيڪي ڏنو ھجيوَ پوءِ اُھي ئي ٻيڻو ڪندڙ آھن.'),
(3449, 30, 40, 'الله اُھو آھي جنھن اوھان کي بڻايو وري اوھان کي روزي ڏنائين وري اوھان کي ماريندو وري اوھان کي جياريندو، اوھان جي (ٺھرايل) شريڪن مان ڪو اھڙو آھي ڇا جو اھڙن ڪَمن مان ڪجھ ڪري (سگھي)؟ الله پاڪ آھي ۽ اُن کان مٿاھون آھي جنھن کي (ساڻس) شريڪ ٺھرائيندا آھن.'),
(3450, 30, 41, 'ماڻھن جي ھٿن جي ڪئي سببان بَر ۽ بَحر ۾ فساد ھن لاءِ پڌرو ٿيو آھي ته جيڪي (مَدا ڪم) ڪيائون تنھن جي ڪُجھ (سزا) کين چکائي ته مانَ اُھي موٽن.'),
(3451, 30, 42, 'چؤ ته مُلڪ ۾ گھُمو پوءِ ڏسو ته جيڪي اڳ ھوا تن جي پڇاڙي ڪئن ٿي، منجھائن گھڻا مشرڪ ھوا.'),
(3452, 30, 43, 'پوءِ پنھنجي منھن کي اُن ڏينھن جي اچڻ کان اڳ سڌي دين لاءِ بيھار جو الله وٽان اُن (ڏينھن) کي رد ٿيڻو نه آھي اُن ڏينھن (ماڻھو) الڳ الڳ ٿيندا.'),
(3453, 30, 44, 'جيڪو ڪافر ٿيو تنھن تي سندس ڪفر آھي، ۽ جن چڱا ڪم ڪيا سي پاڻ لاءِ ھنڌ وڇائين ٿا.'),
(3454, 30, 45, 'ته جن ايمان آندو ۽ چڱا عمل ڪيا تن کي الله فضل سان بدلو ڏئي، بيشڪ اُھو ڪافرن کي دوست نه رکندو آھي.'),
(3455, 30, 46, '۽ سندس نشانين مان ھي آھي ته خوشخبري ڏيندڙ ھيرون گھُلائيندو آھي ۽ ته اوھان کي پنھنجي ٻاجھ مان ڪجھ چکائي ۽ ته سندس حُڪم سان ٻيڙيون تَرن ۽ ته سندس فضل مان روزي طلب ڪريو ۽ ته مانَ اوھين شڪرانو ڪريو.'),
(3456, 30, 47, '۽ بيشڪ توکان اڳ گھڻن پيغمبرن کي سندن قوم ڏانھن موڪليوسون پوءِ وٽن مُعجزن سان آيا پوءِ جن نافرماني ڪئي تن کان بدلو ورتوسون، ۽ مؤمنن کي مدد ڪرڻ اسان تي حق آھي.'),
(3457, 30, 48, 'الله اُھو آھي جيڪو وائن کي موڪليندو آھي پوءِ (اُھي) ڪڪر کي کڻنديون آھن پوءِ جئن الله گھرندو آھي تئن آسمان ۾ اُن (ڪڪر) کي کنڊرائيندو آھي پوءِ اُن کي تَه تَه ڪندو آھي پوءِ (تون) ڦُڙين کي ڏسندو آھين جو اُن (ڪڪر) جي وچ مان نڪرنديون آھن، پوءِ جڏھن پنھنجن ٻانھن مان جنھن تي وڻندو اٿس تنھن تي اُھو (مينھن) پھچائيندو آھي (تڏھن) اُنھيءَ ئي مھل اُھي خوش ٿيندا آھن.'),
(3458, 30, 49, '۽ جيتوڻيڪ ان (مينھن) جي مٿن وَسائجڻ کان اڳ اڳيئي نااُميد ٿيندا ھوا.'),
(3459, 30, 50, 'پوءِ الله جي ٻاجھ جي نشانين ڏانھن نھار ته ڪھڙي طرح زمين کي سندس مرڻ کان پوءِ جياري ٿو، بيشڪ اُھو (الله) مُئن کي ضرور جيارڻ وارو آھي، ۽ اُھو (الله) سڀڪنھن شيء تي وس وارو آھي.'),
(3460, 30, 51, '۽ جيڪڏھن ڪو واءُ (جو جھولو) گھُلايون پوءِ اُن (پوک) کي ھيڊو ٿيندو ڏسن ته اُن کانپوءِ بي شڪري ڪرڻ لڳندا آھن.'),
(3461, 30, 52, 'پوءِ (اي پيغمبر) تون سڏُ نڪي مئن کي ٻڌائي سگھندين ۽ نڪي ٻَوڙن کي ٻڌائي سگھندين جڏھن ته اُھي پٺي ڏئي مُنھن ڦيرائين.'),
(3462, 30, 53, '۽ نڪي تون انڌن کي سندن گمراھيءَ کان (روڪي) سڌو رستو ڏيکاريندڙ آھين، تون ته رڳو اُنھيءَ کي ٻڌائي سگھندين جنھن اسان جي آيتن تي ايمان آندو پوءِ اُھي فرمانبردار به آھن.'),
(3463, 30, 54, 'الله اُھو آھي جنھن اوھان کي (پھرين) ھيڻائي (واري شيء) مان خلقيو آھي وري ھيڻائيءَ کان پوءِ (اوھان ۾) سگھ پيدا ڪيائين وري سگھ کان پوءِ (اوھان کي) ھيڻائي ۽ ٻڍاپو ڏنائين، جئن گھرندو آھي تئن بڻائيندو آھي، ۽ اُھو ڄاڻندڙ وس وارو آھي.'),
(3464, 30, 55, '۽ جنھن ڏينھن قيامت قائم ٿيندي (تنھن ڏينھن) قسم کڻندا ته (دنيا ۾) ھڪ گھڙي کانسواءِ (وڌيڪ) نه رھيا ھواسون، اھڙي طرح (رستي کان) ڦيرايا ويندا ھوا.'),
(3465, 30, 56, '۽ جن کي عِلم ۽ ايمان ڏنو ويو آھي سي کين چوندا ته الله جي ڪتاب ۾ لکي موافق جيئري ٿي اُٿڻ جي ڏينھن توڻي (دنيا ۾) رھيا آھيو، پوءِ ھي جيئري ٿي اُٿڻ جو ڏينھن آھي پر اوھين نه ڄاڻندا ھيؤ.'),
(3466, 30, 57, 'پوءِ اُن ڏينھن ظالمن کي سندن عذر ڪرڻ فائدو نه ڏيندو ۽ نڪي انھن کان توبه طلب ڪبي.'),
(3467, 30, 58, '۽ بيشڪ ماڻھن لاءِ ھن قرآن ۾ سڀڪنھن جنس جا مثال بيان ڪياسون، ۽ جيڪڏھن وٽن ڪا نشاني (تون) آڻين ته ڪافر ضرور چوندا ته اوھين رڳو ڪُوڙا آھيو.'),
(3468, 30, 59, 'اھڙيءَ طرح جيڪي نه ڄاڻندا آھن تن جي دلين تي الله مُھر ھڻندو آھي.'),
(3469, 30, 60, 'پوءِ (اي پيغمبر) صبر ڪر ڇوته الله جو واعدو سچ آھي ۽ جيڪي يقين نه رکندا آھن سي توکي ھلڪو نه ڪن.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الٓمّٓ.'),
(3471, 31, 2, 'ھي آيتون حِڪمت واري ڪتاب جون آھن.'),
(3472, 31, 3, '(جو) ھدايت ۽ ٻاجھ انھن ڀلارن لاءِ آھي.'),
(3473, 31, 4, 'جيڪي نماز پڙھندا آھن ۽ زڪوٰة ڏيندا آھن ۽ اُھي آخرت جو ويساھ (به) رکندا آھن.'),
(3474, 31, 5, 'اُھي پنھنجي پالڻھار جي سڌي رستي تي آھن ۽ اُھي ئي ڇوٽڪاري وارا آھن.'),
(3475, 31, 6, '۽ ماڻھن مان ڪو اھڙو آھي جو بيھوديءَ ڳالھ کي ڳنھندو آھي ته (ماڻھن کي) اڻ ڄاڻائيءَ سان الله جي واٽ کان ڀلائي، ۽ اُن (واٽ) کي ٺـٺولي ڪري وٺي، اُنھن لاءِ ئي خواريءَ وارو عذاب آھي.'),
(3476, 31, 7, '۽ جڏھن اسان جون آيتون کيس پڙھي ٻڌائبيون آھن (تڏھن) ھٺيلو ٿي منھن ڦيرائيندو آھي ڄڻڪ اُنھن کي ٻڌوئي نه ھوائين ڄڻڪ سندس ٻنھي ڪنن ۾ گھٻرائي آھي، تنھنڪري کيس ڏکوئيندڙ عذاب جي خبر ڏي.'),
(3477, 31, 8, 'بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ نعمت وارا باغ آھن.'),
(3478, 31, 9, 'منجھس سدائين رھڻ وارا آھن، (اِھو) الله جو پڪو انجام ڏنل آھي، ۽ اُھو غالب حِڪمت وارو آھي.'),
(3479, 31, 10, 'آسمانن کي ريءَ ٿنڀن جي بڻايائين جو کين ڏسو ٿا ۽ زمين ۾ جبل وڌائين ته متان اوھان کي ڌوڏي ۽ منجھس ھر جنس جا جانور پکيڙيائين، ۽ آسمان کان پاڻي وسايو سون پوءِ منجھس سٺا ھر جنس جا اوڀڙ ڄماياسون.'),
(3480, 31, 11, 'اِھا الله جي بڻاوت آھي پوءِ مونکي ڏيکاريو ته جيڪي اُن (الله) کانسواءِ (ٻيا) آھن تن ڇا بڻايو آھي، بلڪ ظالم پڌريءَ ڀُل ۾ آھن.'),
(3481, 31, 12, '۽ بيشڪ لُقمان کي حِڪمت ڏني سون (چيوسون) ته الله جو شُڪر ڪر، ۽ جيڪو شُڪر ڪندو سو رڳو پنھنجي جيءَ (جي نفع) لاءِ شُڪر ڪندو، ۽ جيڪو بي شڪري ڪندو ته بيشڪ الله بي پرواھ ساراھيل آھي.'),
(3482, 31, 13, '۽ (ياد ڪر) جڏھن لُقمان پنھنجي پُٽ کي چيو ۽ اُنھيءَ کيس سمجھايو ٿي ته اي مُنھنجا پُٽڙا الله سان (ڪنھن کي) شريڪ نه ڪر، ڇوته شرڪ وڏي بي انصافي آھي.'),
(3483, 31, 14, '۽ ماڻھوءَ کي سندس ماءُ پيءُ بابت حڪم ڪيوسون، جو ماڻس تڪليف جي مٿان تڪليف سھي کيس (پنھنجي پيٽ ۾) کنيو ۽ ٻن ورھن ۾ سندس (بُبن جو) ڇڏائڻ آھي (حُڪم جو مطلب ھيءُ آھي) ته منھنجو ۽ پنھنجي ماءُ پيءُ جو شڪرانو ڪر (جو آخر) مون ڏانھن (ئي) موٽڻ آھي.'),
(3484, 31, 15, '۽ جيڪڏھن (اُھي) مون سان (ڪنھين کي) شريڪ ڪرڻ جي توکي ڪوشش ڪن جنھن جي توکي ڪا ڄاڻپ نه آھي ته سندن (انھي چوڻ جي) فرمانبرداري نه ڪر ۽ دُنيا ۾ ساڻن چڱي ريت سنگت ڪر، ۽ جنھن مون ڏانھن مُھاڙ ڪئي ھجي تنھنجي واٽ جي تابعداري ڪر، وري اوھان جو مون ڏانھن ورڻ آھي پوءِ جيڪي ڪندا ھيؤ تنھنجي اوھان کي سُڌ ڏيندس.'),
(3485, 31, 16, '(لُقمان ھيءَ به چيو ته) اي منھنجا پُٽڙا ڪابه (شيء) جيڪڏھن آھر جي داڻي جتري آھي پوءِ اُھا پھڻ ۾ ھجي يا آسمانن ۾ يا زمين ۾ (ھجي) ته الله اُن کي آڻي حاضر ڪندو، ڇوته الله باريڪ بين خبر رکندڙ آھي.'),
(3486, 31, 17, 'اي منھنجا پٽڙا نماز پڙھ ۽ چڱي ڪم جو حُڪم ڪر ۽ مدي (ڪم) کان منع ڪر ۽ توکي جيڪا اوکائي پھچي تنھن تي صبر ڪر، بيشڪ اھي (ڪم) ھمت وارن ڪمن مان آھن.'),
(3487, 31, 18, '۽ ماڻھن لاءِ پنھنجو ڳل (يعني منھن) چٻو نه ڪر ۽ زمين ۾ لاڏ سان نه گھُم، بيشڪ الله ھٺيلي وڏائي ڪندڙ کي دوست نه رکندو آھي.'),
(3488, 31, 19, '۽ پنھنجي گھمڻ ۾ وچولي چال ھل ۽ پنھنجو آواز ڍرو ڪر، ڇوته آوازن مان بڇڙو (آواز) گڏھ جي ھينگ آھي.'),
(3489, 31, 20, 'نه ڏٺو اٿو ڇا ته جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله اوھان کي تابع ڪري ڏنو آھي ۽ پنھنجون پڌريون ۽ ڳجھون نعمتون اوھان تي ڀرپور ڪيائين، ۽ ماڻھن مان ڪي (اھڙا) آھن جو اَڻ ڄاڻائي سان ۽ بنا ھدايت ۽ روشن ڪتاب ڌاران الله بابت جھڳڙو ڪندا آھن.'),
(3490, 31, 21, '۽ جڏھن کين چئبو آھي ته جيڪي الله نازل ڪيو تنھن جي تابعداري ڪريو (تڏھن) چوندا آھن (ته نه!) بلڪ اسان جنھن تي پنھنجا پيءُ ڏاڏا (ھلندا) ڏٺا تنھن جي تابعداري ڪنداسون، توڻيڪ شيطان دوزخ جي عذاب ڏانھن کين سڏي ٿو ته به (ان جي پيروي ڪندا ڇا)؟'),
(3491, 31, 22, '۽ جنھن پنھنجي مھاڙ الله ڏانھن ڪئي ۽ اُھو چڱن ڪمن ڪرڻ وارو به آھي تنھن بيشڪ مُحڪم رسيءَ کي پڪڙيو، ۽ (سڀني) ڪمن جي پڇاڙي الله ڏانھن آھي.'),
(3492, 31, 23, '۽ جيڪو ڪفر ڪري تنھن جو ڪفر توکي غمگين نه ڪري، کين اسان ڏانھن ورڻو آھي پوءِ جيڪي ڪيائون تنھن جي کين سُڌ ڏينداسون، ڇوته الله سينن وارو ڳجھ ڄاڻندڙ آھي.'),
(3493, 31, 24, '(دنيا ۾) کين ٿورو (وقت) آسودو ڪريون ٿا وري کين سخت عذاب ڏانھن ڇڪي نينداسون.'),
(3494, 31, 25, '۽ جيڪڏھن کائن پڇندين ته آسمانن ۽ زمين کي ڪنھن بڻايو آھي ته ضرور چوندا ته الله، چؤ ته سڀ ساراھ الله کي جڳائي، بلڪ اُنھن مان گھڻا نه ڄاڻندا آھن.'),
(3495, 31, 26, 'جيڪي آسمانن ۽ زمين ۾ آھي سو الله جو ئي آھي، بيشڪ الله اُھوئي بي پرواھ (ڏاڍو) ساراھيل آھي.'),
(3496, 31, 27, '۽ جيڪي وڻ زمين ۾ آھن سي جيڪڏھن قلم ٿين ۽ سمنڊ (جو پاڻي) اُن جي مَسُ ٿئي اُن کانپوءِ ست سمنڊ (ٻيا به) ھجن ته الله (جي قدرت) جون ڳالھيون نه کُٽنديون، بيشڪ الله غالب حِڪمت وارو آھي.'),
(3497, 31, 28, 'اوھان (سڀني) جو خلقڻ ۽ اوھان جو وري جيارڻ رڳو ھڪڙيءَ ماڻھوءَ (جي خلقڻ ۽ وري جيارڻ) جھڙو آھي بيشڪ الله ٻڌندڙ ڏسندڙ آھي.'),
(3498, 31, 29, 'نه ڏٺو اٿيئي ڇا ته الله رات کي ڏينھن ۾ داخل ڪندو آھي ۽ ڏينھن کي رات ۾ داخل ڪندو آھي ۽ سج ۽ چنڊ کي نِوايائين، سڀڪو مقرر مُدت توڻي ھلندو آھي ۽ (نه ڏٺو اٿيئي ڇا) ته جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.'),
(3499, 31, 30, 'اِھي (ڪم) ھن ڪري آھن ته الله ئي برحق آھي ۽ ته اُن کانسواءِ جنھن به ٻي کي سڏيندا آھن سو ڪوڙ آھي ۽ ته الله ئي تمام مٿاھون وڏو آھي.'),
(3500, 31, 31, 'نه ڏٺو اٿيئي ڇا ته ٻيڙيون الله جي فضل سان سمنڊ ۾ ترنديون آھن (ھن لاءِ) ته (الله) اوھان کي پنھنجين نشانين مان ڏيکاري، بيشڪ ھن ۾ سڀڪنھن وڏي صابر شاڪر لاءِ نشانيون آھن.'),
(3501, 31, 32, '۽ جڏھن کين (درياءَ جي) لھر پاڇي وارن پھاڙن وانگر ويڙھيندي آھي (تڏھن) خاص الله کي نِلو ڏانھس مھاڙ ڪري سڏيندا آھن، پوءِ جنھن مھل کين ڀر ڏانھن بچارائي پھچائيندو آھي (تنھن مھل) منجھائن ڪي وچٿرا ھوندا آھن، سڀڪنھن انجام ڀڃندڙ بي شڪر کانسواءِ ٻيو ڪوبه اسان جي آيتن جو انڪار نه ڪندو آھي.'),
(3502, 31, 33, 'اي ماڻھؤ پنھنجي پالڻھار کان ڊڄو ۽ اُن ڏينھن کان (به) جنھن ۾ ڪو پيءُ پنھنجي پُٽ جي ڪم نه ايندو، ۽ نه پُٽ پنھنجي پيءُ جي ڪجھ ڪم ايندڙ ھوندو، بيشڪ الله جو وعدو سچو آھي تنھنڪري نڪي اوھان کي دنيا جي حياتي ٺڳي، ۽ نڪي اوھان کي الله بابت ٺڳيندڙ (شيطان) ٺڳي.'),
(3503, 31, 34, 'بيشڪ قيامت جي خبر الله ئي وٽ آھي، ۽ اُھوئي مينھن وسائيندو آھي، ۽ جيڪي ڳڀيراڻين ۾ آھي سو (به) ڄاڻندو آھي، ۽ ڪوئي جيءُ نه ڄاڻندو آھي ته (پاڻ) سڀاڻي ڇا ڪندو، ۽ ڪوئي جيءُ نه ڄاڻندو آھي ته (پاڻ) ڪھڙي زمين تي مرندو، تحقيق الله ڄاڻندڙ خبر رکندڙ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الٓمّٓ.'),
(3505, 32, 2, '(ھن) ڪتاب جو لاھڻ جنھن ۾ ڪو شڪ ڪونھي جھانن جي پالڻھار وٽان آھي.'),
(3506, 32, 3, 'چوندا آھن ڇا ته اُن (قرآن) کي پاڻون ٺاھيو اٿس، (نه!) بلڪ اُھو تنھنجي پالڻھار وٽان سچو (آيل) آھي (ھِن لاءِ) ته اُنھيءَ قوم کي ڊيڄارين جن وٽ توکان اڳ ڪو ڊيڄاريندڙ نه آيو آھي ته مانَ اُھي ھدايت لھن.'),
(3507, 32, 4, 'الله اُھو آھي جنھن آسمانن ۽ زمين کي ۽ جيڪي سندن وچ ۾ آھي سوڇھن ڏينھن ۾ بڻايو وري عرش ڏانھن متوجه ٿيو، اُن کانسواءِ اوھان لاءِ نڪي ڪو سڄڻ ۽ نڪي ڪو پارت ڪندڙ آھي، پوءِ نصيحت نه ٿا وٺو ڇا؟'),
(3508, 32, 5, 'آسمان کان زمين ڏانھن ڪم جو بندوبست ڪندو آھي، وري (اُھو بندوبست) اُن ڏينھن ۾ ڏانھس مٿاھون ويندو آھي جنھن ڏينھن جو اندازو ھزار ورھ جيترو انھيءَ ليکي مان آھي جو ڳڻيندا آھيو.'),
(3509, 32, 6, 'اِھو آھي جو ڳجھ ۽ ظاھر جو ڄاڻندڙ غالب مھربان آھي.'),
(3510, 32, 7, 'جنھي جيڪا شيء پيدا ڪئي سا سڀڪا چڱيءَ طرح بڻائي ۽ ماڻھوءَ جي پيدائش کي مٽيءَ مان نئون شروع ڪيائين.'),
(3511, 32, 8, 'وري سندس اولاد بي قدر پاڻيءَ جي نچوڙي مان بڻايائين.'),
(3512, 32, 9, 'وري کيس سئين لڱين ڪيائين ۽ منجھس پنھنجو روح ڦوڪيائين ۽ اوھان لاءِ ڪن ۽ اکيون ۽ دليون بڻايائين، اوھين ٿورو شڪر ڪندا آھيو.'),
(3513, 32, 10, '۽ چون ٿا ته جڏھن زمين ۾ رلي ملي وياسون (تڏھن به) اسين وري نئين سر بڻباسين ڇا؟ (نه!) بلڪ اُھي پنھنجي پالڻھار جي ملڻ کان منڪر آھن.'),
(3514, 32, 11, '(اي پيغمبر کين) چؤ ته جيڪو موت جو فرشتو اوھان تي مقرّر ڪيل آھي سو اوھان جو روح ڪڍندو وري پنھنجي پالڻھار ڏانھن موٽائبؤ.'),
(3515, 32, 12, '۽ (اُنھي مھل) جيڪڏھن ڏسين جنھن مھل گنھگار پنھنجي پالڻھار جي سامھون پنھنجا ڪنڌ اونڌا ڪندڙ ھوندا (ته عجب ڪرين)، (۽ چوندا ته) اي اسانجا پالڻھار ڏٺوسون ۽ ٻڌوسون ھاڻي اسان کي (دُنيا ۾) موٽاءِ ته چڱا ڪم ڪريون بيشڪ اسين يقين ڪندڙ آھيون.'),
(3516, 32, 13, '۽ جيڪڏھن گھُرون ھا ته سڀڪنھن شخص کي ھدايت ڪريون ھا پر مون وٽان (ھي) حُڪم ٺھرائجي چڪو آھي ته جِنّن ۽ ماڻھن (انھن ئي) مڙني مان دوزخ ضرور ڀريندس.'),
(3517, 32, 14, 'پوءِ ھن ڏينھن جي ملڻ کي جو وساريو ھيؤ تنھن سببان عذاب چکو، بيشڪ اسان (به) اوھان کي وساريو ۽ جيڪي ڪندا ھيؤ تنھن سببان ھميشگيءَ جو عذاب چکو.'),
(3518, 32, 15, 'اسان جي آيتن تي رڳو اُھي ايمان آڻيندا جن کي اُنھن (آيتن) سان جڏھن نصيحت ڏبي آھي تڏھن سجدو ڪندڙ ٿي ڪِرندا آھن ۽ پنھنجي پالڻھار جي ساراھ پاڪائي سان بيان ڪندا آھن ۽ اُھي وڏائي نه ڪندا آھن.'),
(3519, 32, 16, 'سندن پاسا سمھڻ جي ھنڌ کان الڳ ٿي پنھنجي پالڻھار کي خوف ۽ اُميد سان سڏيندا آھن، ۽ جيڪا کين روزي ڏني سون تنھن مان خرچيندا آھن.'),
(3520, 32, 17, 'پوءِ جيڪو اکين جو ٺار انھن لاءِ لڪايو ويو آھي سو ڪو جيء نه ڄاڻندو آھي، اُھو بدلو ھنن جيڪي ڪمايو ھو تنھن سببان آھي.'),
(3521, 32, 18, 'جيڪو مؤمن آھي سو بدڪار جي برابر آھي ڇا؟ (اُھي ٻئي ڪڏھن) برابر نه ٿيندا.'),
(3522, 32, 19, 'جن ايمان آندو ۽ چڱا ڪم ڪيا تِن لاءِ بھشت (جا باغ) مھماني طور رھڻ جو ھنڌ آھي اُنھي سببان جو (چڱا) ڪم ڪندا رھيا.'),
(3523, 32, 20, 'پر جن بڇڙائي ڪئي تن جي رھڻ جو ھنڌ دوزخ آھي، جنھن مھل منجھانئس نڪرڻ جو ارادو ڪندا (تنھن مھل) منجھس ورائي (پيا وجھبا) ۽ کين چئبو ته اُن باھ جو عذاب چکو جنھن کي اوھين ڪوڙ ڀائيندا ھئؤ.'),
(3524, 32, 21, '۽ کين وڏي عذاب کانسواءِ ھلڪي سزا (دنيا ۾ به) ضرور چکائينداسون ته مانَ اُھي (سڌي واٽ ڏانھن) ورن.'),
(3525, 32, 22, '۽ اُن کان وڌيڪ ظالم ڪير آھي جنھن کي سندس پالڻھار جي آيتن سان نصيحت ڏجي ته وري اُن کان مُنھن موڙي، بيشڪ اسين ڏوھارين کان بدلو وٺندڙ آھيون.'),
(3526, 32, 23, '۽ بيشڪ مُوسىٰ کي ڪتاب ڏنوسون پوءِ تون اُن (قرآن) جي ملڻ کان شڪ ۾ نه ٿج ۽ اُن موسىٰ کي بني اسرائيلن لاءِ ھدايت بڻايوسون.'),
(3527, 32, 24, 'منجھانئن پيشوا ڪيا (ھوا) سون جي اسان جي حُڪم سان سڌو رستو ڏيکاريندا ھوا جڏھن جو (اُھي ڏکن تي) صبر ڪندا رھيا، ۽ اُھي اسان جي آيتن تي يقين رکندا ھوا.'),
(3528, 32, 25, 'بيشڪ تنھنجو پالڻھار ئي قيامت جي ڏينھن سندن وچ ۾ انھيءَ (ڳالھ) جو نبيرو ڪندو جنھن بابت جھڳڙو ڪندا ھوا.'),
(3529, 32, 26, 'کين پڌرو نه ٿيو آھي ڇا ته کانئن اڳ ڪيترا (ئي) جُڳ ناس ڪياسون جن جي گھرن ۾ (اُھي) گھُمندا آھن، بيشڪ ھن (ڳالھ) ۾ وڏيون نشانيون آھن، پوءِ نه ٻڌندا آھن پا؟'),
(3530, 32, 27, 'نه ڏٺائون ڇا ته اسين اُٻڙٺ زمين ڏانھن پاڻي وھائيندا آھيون پوءِ اُن سان پوک ڄمائيندا آھيون جنھن مان سندن ڍور ۽ اُھي پاڻ کائيندا آھن، پوءِ نه ڏسندا آھن ڇا؟'),
(3531, 32, 28, '۽ چون ٿا ته جيڪڏھن اوھين سچا آھيو ته اھو فيصلو ڪڏھن ٿيندو؟'),
(3532, 32, 29, '(اي پيغمبر کين) چؤ ته فيصلي جي ڏينھن ڪافرن کي سندن ايمان آڻڻ فائدو نه ڏيندو ۽ نڪي کين مُھلت ڏبي.'),
(3533, 32, 30, 'پوءِ کائن مُنھن موڙ ۽ انتظار ڪڍ بيشڪ اُھي (به) انتظار ڪڍندڙ آھن.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'اي پيغمبر الله کان ڊڄ ۽ ڪافرن ۽ منافقن جو چيو نه مڃ، بيشڪ الله ڄاڻندڙ حڪمت وارو آھي.'),
(3535, 33, 2, '۽ جيڪي تنھنجي پالڻھار وٽان توڏانھن وحي ڪجي ٿو تنھنجي تابعداري ڪر، بيشڪ جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.'),
(3536, 33, 3, '۽ الله تي ڀروسو ڪر، ۽ الله سنڀاليندڙ بس آھي.'),
(3537, 33, 4, 'الله ڪنھن مڙس جي اندر ۾ ٻه دليون (پيدا) نه ڪيون آھن، ۽ نڪي اوھان جي انھن زالن کي جن کي اوھين ماءُ چئي ويٺا آھيو سي اوھان جون (حقيقي) مائر ڪيائين، ۽ نڪي اوھان جي پُٽيلن کي اوھان جا (حقيقي) پُٽ ڪيائين اھو اوھان جو چوڻ اوھان جي واتن جو آھي، ۽ الله سچ چوندو آھي ۽ اُھوئي (سڌو) رستو ڏيکاريندو آھي.'),
(3538, 33, 5, 'کين سندن پِين جا (پٽ ڪري) سڏيو اھو الله وٽ بلڪل انصاف آھي، پوءِ جيڪڏھن سندن پيئر نه ڄاڻندا ھجو ته اوھان جا دين ۾ ڀائر ۽ اوھان جا دوست آھن، ۽ جنھن ڳالھائڻ ۾ چوڪ ڪئي ھجيوَ تنھن ۾ اوھان تي ڪو گناھ نه آھي پر اوھان جي دلين جيڪي ڄاڻي واڻي ڪيو (تنھن ۾ گناھ آھي)، ۽ الله بخشڻھار مھربان آھي.'),
(3539, 33, 6, 'پيغمبر مؤمنن تي سندن جندن کان وڌيڪ حق رکندو آھي ۽ سندس زالون سندين مائر آھن، ۽ مائٽي وارا ھڪ ٻئي جا پاڻ ۾ الله جي ڪتاب ۾ (ٻـين) مؤمنن ۽ ھجرت ڪندڙن کان وڌيڪ ويجھا آھن پر پنھنجن دوستن سان احسان ڪريو (ته جائز آھي)، اِھو (حُڪم) ڪتاب ۾ لکيل آھي.'),
(3540, 33, 7, '۽ (ياد ڪر) جڏھن پيغمبرن کان سندن انجام ورتوسون ۽ (پڻ) توکان ۽ نُوح ۽ ابراھيم ۽ مُوسىٰ ۽ عِيسىٰ پٽ مريم جي کان به، ۽ (ھن لاءِ) کانئن پڪو انجام ورتوسون.'),
(3541, 33, 8, 'ته (الله) سچن کان سندين سچائي بابت پُڇي، ۽ ڪافرن لاءِ ڏکوئيندڙ عذاب تيار ڪيو اٿس.'),
(3542, 33, 9, 'اي ايمان وارؤ پاڻ تي الله جو احسان ياد ڪريو جڏھن (ڪافرن جا) لشڪر اوھان تي (چڙھائي ڪري) آيا تڏھن مٿن واءُ کي موڪليوسون ۽ (اوھان جي مدد لاءِ ملائڪن جا) اھڙا لشڪر به جن کي اوھان نه ٿي ڏٺو (پوءِ ڪافرن کي ڀڄائي اوھان کي بچايوسون) ۽ جيڪي ڪريو ٿا سو الله ڏسندڙ آھي.'),
(3543, 33, 10, 'جڏھن (اُھي) اوھان جي مٿان ۽ اوھان جي ھيٺان اوھان وٽ آيا ۽ اُنھيءَ مھل اکيون ٽِڙي ويون ۽ دليون (دھشت کان) نِڙ گھٽن کي پُھتيون ۽ الله جي به نسبت ڪيئي گمان ڀانيوَ ٿي.'),
(3544, 33, 11, 'اُتي مُؤمنن کي پرکيو ويو ۽ کين تمام سخت لوڏيو ويو.'),
(3545, 33, 12, '۽ اُنھيءَ مھل منافقن ۽ جن جي دلين ۾ بيماري آھي تن چيو ٿي ته الله ۽ سندس پيغمبر اسان کي ٺڳيءَ کانسواءِ (ٻيو) ڪو انجام نه ڏنو آھي.'),
(3546, 33, 13, '۽ اُنھي مھل منجھانئن ھڪ ٽوليءَ چيو ته اي مديني وارؤ اوھان لاءِ (ھتي ترسڻ جي) ڪا جاءِ ڪانھي تنھنڪري موٽو، ۽ منجھانئن ھڪ ٽوليءَ پيغمبر کان موڪلايو ٿي ۽ چوڻ لڳا ته اسان جا گھر ھيڪلا آھن، حالانڪ اُھي ھيڪلا نه ھوا، ڀڄڻ کانسواءِ (ٻيو) ڪو ارادو نه ھون.'),
(3547, 33, 14, '۽ جيڪڏھن اُن (مديني) جي آس پاس کان مٿن ڪا (ڪافرن جي فوج) اچي ڪڙڪي ھا وري کانئن گھُرو جنگ گھُرڻ ۾ اچي ھا ته ضرور اُھا ڪن ھا ۽ اُن لاءِ ٿوري (دير) کان سواءِ ترسن ئي نه ھا.'),
(3548, 33, 15, '۽ (ھن کان) اڳ الله سان انجام ڪيو ھوائون ته (ڀڄڻ لاءِ) پٺيون نه ڦيريندا، ۽ الله جي انجام جي (ضرور) پُڇا ڪئي ويندي.'),
(3549, 33, 16, '(کين) چؤ ته جيڪڏھن اوھين مرڻ يا مارجڻ کان ڀڄندؤ ته ڀڄڻ اوھان کي فائدو نه ڏيندو ۽ اُنھيءَ مھل بلڪل ٿورو فائدو ڏنو ويندوَ.'),
(3550, 33, 17, '(اي پيغمبر کين) چؤ ته اوھان کي الله (جي عذاب) کان ڪير بچائيندو جيڪڏھن اُھو اوھان کي ڪا اوکائي پھچائڻ گھري يا اوھان کي ڪا ٻاجھ پھچائڻ گھري (ته اُھا ڪير روڪيندو؟)، ۽ الله کانسواءِ پاڻ لاءِ نڪو دوست ۽ نڪو مددگار لھندا.'),
(3551, 33, 18, 'اوھان مان (جھاد کان) جھليندڙن ۽ پنھنجن ڀائرن کي (ھئن) چوندڙن کي ته اسان ڏانھن اچو بيشڪ الله ڄاڻندو آھي، ۽ ٿورن کانسواءِ (ٻيا) لڙائي ۾ نه ٿا اچن.'),
(3552, 33, 19, '(ھٿون) اوھان تي پبچائي ڪندڙ آھن، پوءِ جنھن مھل ڀَـوَ (جو وقت) اچي (تنھن مھل) کين ڏسين ته توڏانھن نھاريندا آھن جو سندن اکيون اُن (ماڻھوءَ) وانگر ڦِرنديون آھن جنھن تي موت (جي سڪرات) جي بيھوشي پھتي ھجي، پوءِ جنھن مھل (جنگ جو) ڀؤ لھندو آھي تنھن مھل (غنيمت جي) مال (وٺڻ) تي لالچي ٿي اوھان کي تکين زبانن سان ايذائيندا آھن، انھن ايمان نه آندو تنھنڪري الله سندن اعمال چَٽ ڪيا، ۽ اھو ڪم الله کي آسان آھي.'),
(3553, 33, 20, '(خوف جي ڪري) ڀائيندا آھن ته (ڪافرن جا) ڪٽڪ نه ويا آھن، ۽ جيڪڏھن (ڪافرن جا) ڪي ڪٽڪ اچن ھا ته (ھيءَ ڳالھ) گھُرن ھا ته اُھي جيڪر جھنگلن ۾ ويٺل ھجن ھا (۽) اوھان جون خبرون (ھرڪنھن کان) پڇندا رھن ھا، ۽ جيڪڏھن اوھان ۾ ھجن ته رڳو ٿورو (اچي) جنگ ۾ وڙھن.'),
(3554, 33, 21, 'بيشڪ اوھان لاءِ الله جي پيغمبر جي طريقي ۾ عمدي پيروي اُنھيءَ لاءِ آھي جيڪو الله (جي رضا جي اُميد) ۽ قيامت جي ڏينھن (جي اچڻ) جي اميد رکندو ھجي ۽ الله کي گھڻو ياد ڪندو ھجي.'),
(3555, 33, 22, '۽ جنھن مھل مؤمنن (ڪافرن جي) لشڪرن کي ڏٺو (تنھن مھل) چيائون ته ھي اُھو انجام آھي جيڪو الله ۽ سندس پيغمبر اسان کي ڏنو ھو ۽ الله ۽ سندس پيغمبر سچ فرمايو ھو، ۽ اِنھيءَ (حالت) اُنھن کي ايمان ۽ فرمانبرداريءَ ۾ ھيڪارو وڌايو.'),
(3556, 33, 23, 'مؤمنن مان ڪي اھڙا ماڻھو آھن جو جيڪو الله سان انجام ڪيو ھوائون تنھن کي سچو ڪيائون، پوءِ منجھانئن ڪن پنھنجو ذمون پورو ڪيو ۽ منجھانئن ڪو انتظار ڪندو آھي، ۽ (پنھنجي چوڻ کي) ڪنھن طرح نه مٽايائون.'),
(3557, 33, 24, '(اھو ھن ڪري آھي) ته الله سچن کي سندن سچائي سببان بدلو ڏئي ۽ جيڪڏھن گھري ته منافقن کي عذاب ڪري يا (توبه جي توفيق ڏئڻ سان) مٿن ٻاجھ سان موٽي، ڇوته الله بخشڻھار مھربان آھي.'),
(3558, 33, 25, '۽ الله ڪافرن کي سندن ڏمر سميت موٽايو جو ڪا چڱائي نه لڌائون، ۽ الله مؤمنن جي پاران ويڙھ لاءِ ڪافي ٿيو، ۽ الله سگھارو غالب آھي.'),
(3559, 33, 26, '۽ ڪتاب وارن مان جن انھن (مشرڪن) جي مدد ڪئي ھئي تن کي (الله) سندن ڪوٽن مان لاٿو ۽ سندين دلين ۾ دھشت وڌائين (جو) ھڪ ٽوليءَ کي ڪُٺوَ ٿي ۽ ٻيءَ ٽوليءَ کي قيدي ڪيوَ ٿي.'),
(3560, 33, 27, '۽ (نيٺ) اوھان کي سندين زمين ۽ سندين گھرن ۽ سندن انھيءَ زمين جو وارث ڪيائين جنھن تي پير (به) نه گھُمايو ھيوَ، ۽ الله سڀڪنھن شيء تي وس وارو آھي.'),
(3561, 33, 28, 'اي پيغمبر پنھنجي زالن کي چؤ ته جيڪڏھن اوھين دنيا جي حياتي ۽ اُن جو سينگار گھرنديون ھجو ته اچو ته اوھان کي جورو ڏيان ۽ چڱي طرح ڇڏيان.'),
(3562, 33, 29, '۽ جيڪڏھن اوھين الله ۽ سندس پيغمبر ۽ آخرت جي گھر (يعني بھشت) کي گھرو ٿيون ته بيشڪ اوھان مان نيڪ بختن لاءِ الله وڏو اجر تيار ڪيو آھي.'),
(3563, 33, 30, 'اي پيغمبر جون زالون اوھان مان جيڪا پڌري بي حيائي ڪندي تنھن کي وڌائي ٻيڻو عذاب ڪيو ويندو، ۽ اِھو الله تي آسان آھي.'),
(3564, 33, 31, '۽ اوھان مان جيڪا الله ۽ سندس پيغمبر جي تابعداري ڪندي ۽ چڱا ڪم ڪندي تنھن کي سندس اجر ٻه ڀيرا ڏينداسين ۽ اُن لاءِ عزت واري روزي تيار ڪنداسين.'),
(3565, 33, 32, 'اي پيغمبر جون زالون اوھين ڪن ٻـين زالن جھڙيون نه آھيو جيڪڏھن اوھين (الله کان) ڊڄنديون آھيو ته (ڪنھن ڌارئي ماڻھوءَ سان) ڳالھائڻ ۾ ھيٺاھين نه ڪريو ڇوته جنھن جي دل ۾ بيماري آھي سو (متان) طمع ڪري ۽ چڱو سُخن ڳالھايو.'),
(3566, 33, 33, '۽ پنھنجن گھرن ۾ رھو ۽ پھرينءَ جاھليت جي سينگار وانگر سينگار نه ڪريو ۽ نماز قائم رکو ۽ زڪوٰة ڏيو ۽ الله ۽ سندس پيغمبر جي فرمانبرداري ڪريو، اي پيغمبر جا گھر وارؤ الله رڳو ھي گھرندو آھي ته اوھان کان پليتي دور ڪري ۽ ته اوھان کي چڱي طرح پاڪ ڪري.'),
(3567, 33, 34, '۽ اوھان جي گھرن ۾ جيڪي الله جي آيتن ۽ دانائيءَ جي ڳالھين مان پڙھيو وڃي ٿو سو ياد ڪريو، بيشڪ الله لُطف ڪندڙ خبر رکندڙ آھي.'),
(3568, 33, 35, 'بيشڪ مُسلمان مرد ۽ مُسلمان زالون ۽ مؤمن ۽ مؤمنياڻيون ۽ فرمانبردار ۽ فرمانبردارڻيون ۽ سچا ۽ سچيون ۽ صبر وارا ۽ صبر واريون ۽ (خدا کان) ڊڄندڙ ۽ (خدا کان) ڊڄندڙيون ۽ خيرات ڪرڻ وارا ۽ خيرات ڪرڻ واريون ۽ روزي رکڻ وارا ۽ روزي رکڻ واريون ۽ پنھنجي اگھڙن کي نگاھ رکڻ وارا ۽ نگاھ رکڻ واريون ۽ الله کي گھڻو ياد ڪندڙ ۽ ياد ڪندڙيون انھن (سڀني) لاءِ الله بخشش ۽ وڏو اجر تيار ڪيو آھي.'),
(3569, 33, 36, '۽ جڏھن الله ۽ سندس پيغمبر ڪنھن ڪم بابت حُڪم ڪري (تڏھن) ڪنھن مؤمن ۽ ڪنھن مؤمنياڻيءَ کي انھي پنھنجي ڪم ۾ ڪو اختيار ھجڻ نه جڳائي، ۽ جيڪو الله ۽ سندس پيغمبر جي نافرماني ڪندو سو بيشڪ پڌريءَ ڀُل ۾ ڀُلو.'),
(3570, 33, 37, '۽ (اي پيغمبر ياد ڪر) جڏھن تو اُنھيءَ شخص کي چيو ٿي جنھن تي الله فضل ڪيو آھي ۽ تو (به) مٿس احسان ڪيو ته پنھنجيءَ زال کي جھل (۽ طلاق نه ڏي) ۽ الله کان ڊڄ ۽ تون پنھنجي دل ۾ اُھا ڳالھ لڪائين ٿو جنھن کي الله پڌرو ڪندو آھي ۽ ماڻھن کان ڊنين ٿي، حالانڪ الله (ھن ڳالھ جو) وڌيڪ حقدار آھي جو اُن کان ڊڄين، پوءِ جنھن مھل زيد اُن (عورت) کان پنھنجي حاجت پوري ڪئي (يعني طلاق ڏنائين تنھن مھل) اُن کي توسان پرڻايوسون (ھن لاءِ) ته مؤمنن تي پنھنجي پٽيلين جي زالن پرڻجڻ ۾ ڪا اوکائي نه رھي جڏھن (اُھي) اُنھن کان پنھنجي حاجت پوري ڪن (يعني طلاق ڏين)، ۽ الله جو (جيڪو) حُڪم (آھي سو) ٿيڻو آھي.'),
(3571, 33, 38, 'پيغمبر تي اُنھيءَ ۾ ڪا اوکائي نه آھي جيڪي الله اُن (پيغمبر) لاءِ حلال ڪيو آھي، جيڪي اڳ گذري ويا تن ۾ (به) الله جو اھو دستور ھو، ۽ الله جو حُڪم ٺھرايل (اُنھن لاءِ) مُقرّر آھي.'),
(3572, 33, 39, 'جيڪي الله جا پيغام پھچائيندا آھن ۽ کانئس ڊڄندا آھن ۽ الله کانسواءِ ٻئي ڪنھن کان نه ڊڄندا آھن، ۽ الله حساب ڪندڙ بس آھي.'),
(3573, 33, 40, 'ﷴ (ﷺ) اوھان جي مردن مان ڪنھن ھڪ جو پيءُ نه آھي پر الله جو پيغمبر ۽ سڀني پيغمبرن کان پڇاڙيءَ ۾ اچڻ وارو آھي، ۽ الله ھر شيء کي ڄاڻندڙ آھي.'),
(3574, 33, 41, 'اي ايمان وارؤ الله کي تمام گھڻو ياد ڪريو.'),
(3575, 33, 42, '۽ صبح ۽ سانجھيءَ جو کيس پاڪائيءَ سان واکاڻيو.'),
(3576, 33, 43, 'ھو اُھو آھي جو اوھان تي رحمت موڪلي ٿو ۽ سندس ملائڪ به اوھان لاءِ رحمت جي دُعا گھرندا آھن ته اوھان کي اونداھين مان سوجھري ڏانھن ڪڍي، ۽ الله مؤمنن تي مھربان آھي.'),
(3577, 33, 44, 'جنھن ڏينھن الله کي ملندا (تنھن ڏينھن) سندن کِيڪر سلام آھي، ۽ اُنھن لاءِ عزت وارو اجر تيار ڪيو اٿس.'),
(3578, 33, 45, 'اي پيغمبر بيشڪ اسان توکي شاھدي ڏيندڙ ۽ خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو.'),
(3579, 33, 46, '۽ الله ڏانھن سندس حُڪم سان سڏيندڙ ۽ روشن ڏيئو (ڪري موڪليوسون).'),
(3580, 33, 47, '۽ مؤمنن کي خوشخبري ڏي ته انھن لاءِ الله جي پار کان وڏو فضل آھي.'),
(3581, 33, 48, '۽ ڪافرن ۽ مُنافقن جو چيو نه مڃ ۽ سندن ايذائڻ (جي خيال) کي ڇڏ ۽ الله تي ڀروسو ڪر، ۽ الله ڪارساز بس آھي.'),
(3582, 33, 49, 'اي ايمان وارؤ جڏھن مؤمنياڻين کي پرڻجو (۽) وري کين ھٿ لائڻ کان اڳ طلاق ڏيو تڏھن مٿن ڪا عِدّت اوھان لاءِ ڪانھي جنھن کي (اوھين) ڳڻيو، پوءِ کين (ڇڙو) جورو ڏيو ۽ چڱيءَ طرح سان کين ڇڏيو.'),
(3583, 33, 50, 'اي پيغمبر بيشڪ اسان تولاءِ اُھي زالون حلال ڪيون جن کي تو سندن ڪابين ڏنو ۽ جيڪي ٻانھيون الله توتي عنايت ڪيون تن مان جن تي تنھنجو ھٿ مالڪ ٿيو سي ۽ تنھنجي چاچي جون ڌيئر ۽ تنھنجي پڦين جون ڌيئر ۽ ۽ تنھنجي مامي جون ڌيئر ۽ تنھنجي ماسين جون ڌيئر جن توسان ھجرت ڪئي حلال ڪيوسون، ۽ اُھا مؤمنياڻي زال به (حلال ڪئي سون) جيڪا پنھنجي جِند پيغمبر کي (سواءِ مھر وٺڻ جي) بخشي بشرطيڪ پيغمبر کيس پرڻجڻ جو ارادو ڪري، (اھو ڪم) مؤمنن کانسواءِ خلاصو تولاءِ آھي، انھن (مؤمنن) تي سندن زالن ۽ سندن ٻانھين بابت جن تي سندن ھٿ مالڪ ٿيا آھن جيڪي (ڪابين ۽ خرچ ڏيڻ) فرض ڪيو اٿون سو بيشڪ ڄاتو اٿون (اھو) ھن ڪري ته توتي ڪا اوکائي نه ھجي، ۽ الله بخشڻھار مھربان آھي.'),
(3584, 33, 51, 'منجھانئن جنھن کي گھرين تنھن کي ڇڏي ڏئين ۽ جنھن کي گھرين تنھن کي پاڻ وٽ جاءِ ڏئين، ۽ جن کي تو پاسي ڪيو تن مان وري جنھن کي طلب ڪرين ته توتي ڪو گناھ نه آھي، اِھو (اختيار) ھن (ڳالھ) کي تمام ويجھو آھي ته سندن اکيون ٺرن ۽ (اُھي) غمگين نه ٿين ۽ جيڪي تون کين ڏئين تنھن کان اُھي سڀ خوش ٿين، ۽ جيڪي اوھان جي دلين ۾ آھي سو الله ڄاڻندو آھي، ۽ الله ڄاڻندڙ بُردبار آھي.'),
(3585, 33, 52, '(اي پيغمبر) انھن (ٻن قسمن) کانپوءِ توکي ٻيون زالون حلال نه آھن ۽ نڪي انھن جي بدلي (ٻيون) زالون نڪاح ڪرين جيتوڻيڪ سندن سونھن توکي پسند اچي پر تنھنجو ھٿ جنھن جو مالڪ ٿيو آھي (تنھن ۾ توکي اختيار آھي)، ۽ الله سڀڪنھن شيء تي نگھبان آھي.'),
(3586, 33, 53, 'اي مؤمنو پيغمبر جي گھرن ۾ اُنھيءَ (وقت) کانسواءِ اندر نه وڃو (جنھن وقت) اوھان کي ڪنھن طعام (کائڻ) لاءِ اچڻ جي موڪل ڏني وڃي ۽ (اھڙي وقت وڃو جو) ان جي تيار ٿيڻ جو انتظار ڪندڙ نه ھجو پر جڏھن اوھان کي سڏيو وڃي تڏھن اندر وڃو پوءِ جڏھن کائي رھو تڏھن نڪري وڃو ۽ ڪنھن ڳالھ ۾ ريجھي ويھي نه رھو، ڇوته انھيءَ (ڪم) پيغمبر کي ايذايو ٿي پوءِ (ھن) اوھان کان لڄ ڪئي ٿي، ۽ الله حق (چوڻ) کان لڄ نه ڪندو آھي، ۽ جڏھن انھن (پيغمبر جي بيبين) کان ڪا شيء گھرو تڏھن پردي جي ٻاھران کانئن گھرو، اھو (ڪم) اوھان جي دلين ۽ انھن جي دلين لاءِ ڏاڍو پاڪ آھي، ۽ اوھان کي نه جڳائيندو آھي ته الله جي پيغمبر کي ايذايو ۽ نڪي (جڳائيندو آھي) ته اُن کانپوءِ سندس زالون اوھين ڪڏھن پرڻجو، ڇوته اُھو (ڪم) الله وٽ وڏو (گناھ) آھي.'),
(3587, 33, 54, 'جيڪڏھن ڪنھن ڳالھ کي پڌرو ڪريو يا اُن کي لڪايو ته بيشڪ الله ھر شيء کي ڄاڻندڙ آھي.'),
(3588, 33, 55, 'زالن تي نڪي پنھنجن پين ۽ نڪي پنھنجن پٽن ۽ نڪي پنھنجن ڀائرن ۽ نڪي پنھنجن ڀاٽين ۽ نڪي پنھنج ڀاڻيجن ۽ نڪي پاڻ جھڙين زالن ۽ نڪي جن تي سندن ھٿ مالڪ ٿيا آھن تن جي اڳيان (پردي نه ڪرڻ ۾) گناھ آھي، ۽ (اي زالون) الله کان ڊڄو ڇوته الله سڀ ڪنھن شيء تي حاضر آھي.'),
(3589, 33, 56, 'بيشڪ الله ۽ سندس ملائڪ پيغمبر تي رحمت موڪليندا آھن، اي ايمان وارؤ اوھين به مٿس صلوات پڙھو ۽ پوري طرح سلام چئو.'),
(3590, 33, 57, 'بيشڪ جيڪي الله ۽ سندس پيغمبر کي ايذائيندا آھن تن الله دنيا ۽ آخرت ۾ لعنت ڪئي آھي ۽ اُنھن لاءِ خواريءَ وارو عذاب تيار ڪيو اٿس.'),
(3591, 33, 58, '۽ جيڪي (ماڻھو) م‍ؤمنن ۽ مؤمنياڻين کي اھڙي ڪم (جي تھمت) سان ايذائيندا آھن جو اُنھن نه ڪيو ھجي تن بيشڪ ڪوڙي ٺاھ ۽ پڌري گناھ جو بار کنيو.'),
(3592, 33, 59, 'اي پيغمبر پنھنجين زالن ۽ پنھنجين ڌيئرن ۽ مؤمنن جي زالن کي چؤ ته (جيڪڏھن ٻاھر نڪرن ته) پاڻ تي پنھنجون چادرون وجھن، اِھو (ھِن ڳالھ کي) تمام ويجھو آھي ته اُھي سڃاتيون وڃن پوءِ کين ايذاءُ نه ڏنو وڃي، ۽ الله بخشڻھار مھربان آھي.'),
(3593, 33, 60, 'منافق ۽ جن جي دلين ۾ بيماري آھي سي ۽ مديني ۾ ڪوڙين ڳالھين پکيڙڻ وارا جيڪڏھن (پنھنجي عادت کان) نه جھلبا ته مٿن توکي بڇينداسون وري ٿورن (ڏينھن) کانسواءِ اُن (يعني مديني) ۾ تنھنجا پاڙيسري نه ٿيندا.'),
(3594, 33, 61, 'لعنت ڪيل آھن، جتي لڀندا تِتي پڪڙبا ۽ چڱي طرح ماربا.'),
(3595, 33, 62, 'جيڪي (ھن کان) گذري ويا تن ۾ (به) الله جو اھو دستور ھو، ۽ الله جي دستور کي ڪڏھن مت سٽ نه ڏسندين.'),
(3596, 33, 63, 'ماڻھو توکان قيامت بابت پڇن ٿا، (کين) چؤ ته اُن جي خبر رڳو الله وٽ آھي، ۽ ڪھڙيءَ (ڳالھ) توکي ڄاڻايو متان قيامت ويجھڙو ئي ٿيندي ھجي.'),
(3597, 33, 64, 'بيشڪ الله ڪافرن تي لعنت ڪئي آھي ۽ انھن لاءِ ساڙيندڙ باھ تيار ڪئي اٿس.'),
(3598, 33, 65, 'منجھس سدائين رھڻ وارا آھن، نڪي ڪو سڄڻ ۽ نڪي ڪو مددگار لھندا.'),
(3599, 33, 66, 'جنھن ڏينھن سندن منھن باھ ۾ اونڌا ڪبا (تنھن ڏينھن) چوندا ته اسان لاءِ ھيءَ ارمان (جيڪر) الله جي فرمانبرداري ڪريون ھا ۽ پيغمبر جي (به) فرمانبرداري ڪريون ھا (ته چڱو ھو).'),
(3600, 33, 67, '۽ چوندا ته اي اسان جا پالڻھار اسان پنھنجن سردارن ۽ پنھنجن وڏن جي فرمانبرداري ڪئي ته انھن اسان کي واٽ کان ڀُلايو.'),
(3601, 33, 68, 'اي اسانجا پالڻھار کين ٻيڻو عذاب ڪر ۽ مٿن تمام وڏي لعنت ڪر.'),
(3602, 33, 69, 'اي ايمان وارؤ (اوھين) اُنھن وانگر نه ٿيو جن مُوسىٰ کي ايذايو پوءِ جيڪي چيائون تنھن کان الله کيس پاڪ ڪيو، ۽ (اھو) الله وٽ مانَ وارن مان ھو.'),
(3603, 33, 70, 'اي ايمان وارؤ الله کان ڊڄو ۽ سڌي ڳالھ چئو.'),
(3604, 33, 71, '(ته) اوھان جا عمل اوھان لاءِ سُڌاري ۽ اوھان جا گناھ اوھان کي بخشي، ۽ جيڪو الله ۽ سندس پيغمبر جي فرمانبرداري ڪندو تنھن بيشڪ وڏي مراد ماڻي.'),
(3605, 33, 72, 'بيشڪ اسان آسمانن ۽ زمين ۽ جبلن کي امانت آڇي ته اُن جي کڻڻ کان انڪار ڪيائون ۽ کانئس ڊنا ۽ اُن کي انسان کنيو، بيشڪ اُھو تمام ظالم جاھل آھي.'),
(3606, 33, 73, '(ھيءَ آڇ ھن لاءِ آھي) ته الله منافقن ۽ منافقياڻين ۽ مشرڪن ۽ مشرڪياڻين کي عذاب ڪري ۽ الله مؤمنن ۽ مؤمنياڻين تي ٻاجھ سان موٽي، ۽ الله بخشڻھار مھربان آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'سڀ ساراھ اُنھيءَ الله کي جڳائي جو جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) سندس آھي ۽ اُن جي ئي ساراھ آخرت ۾ آھي، ۽ اُھو ئي حڪمت وارو خبر رکندڙ آھي.'),
(3608, 34, 2, 'جيڪي زمين ۾ گھڙندو آھي ۽ جيڪي منجھانئس نڪرندو آھي ۽ جيڪي آسمان مان لھندو آھي ۽ جيڪي منجھس چڙھندو آھي سو (سڀ اُھو) ڄاڻندو آھي، ۽ اھوئي بخشڻھار مھربان آھي.'),
(3609, 34, 3, '۽ ڪافر چوندا آھن ته اسان تي قيامت (ڪڏھن) نه ايندي، (کين) چؤ ته ھائو مون کي پنھنجي (اُنھيءَ) پالڻھار ڳُجھ ڄاڻيندڙ جو قسم آھي ته اوھان وٽ ضرور ايندي، جنھن کان ذري جيترو نڪي آسمانن ۽ نڪي زمين ۾ ڳُجھو رھي ٿو ۽ اُن کان تمام ننڍي (ڪا شيء اھڙي) ڪانھي ۽ نڪي تمام وڏي جا پڌري ڪتاب ۾ (لکيل) نه آھي.'),
(3610, 34, 4, 'ھن لاءِ ته جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي بدلو ڏئي، انھن لاءِ ئي بخشش ۽ سڳوري روزي آھي.'),
(3611, 34, 5, '۽ جيڪي اسان جي آيتن ۾ مقابلو ڪندڙ ٿي ڊوڙيا انھن لاءِ ئي ڏکوئيندڙ عذاب جي سزا آھي.'),
(3612, 34, 6, '۽ جن کي عِلم ڏنو ويو سي سمجھندا آھن ته جيڪو (قرآن) تنھنجي پالڻھار وٽان توڏانھن نازل ڪيو ويو آھي سو برحق آھي ۽ غالب ساراھيل (الله) جي واٽ ڏانھن رستو ڏيکاريندڙ آھي.'),
(3613, 34, 7, '۽ ڪافر (ھڪ ٻئي) کي چوندا آھن ته اوھان کي اھڙو مڙس ڏسيون ڇا جو اوھان کي ٻڌائي ٿو ته جڏھن (اوھين) تمام ڳري ذرا ذرا ٿيندؤ (تڏھن) بيشڪ اوھين نئين سر بڻايا ويندؤ.'),
(3614, 34, 8, '(اُنھيءَ) الله تي ڪوڙو ٺاھ ٺاھيو آھي يا کيس ڪا چريائي آھي، (نه!) بلڪ جيڪي آخرت کي نه مڃيندا آھن سي عذاب ۾ ھُوندا ۽ (اُھي) وڏي ڀُل ۾ آھن.'),
(3615, 34, 9, 'جيڪي سندن اڳيان ۽ جيڪي سندن پويان آسمان ۽ زمين مان (بڻايو ويو) آھي سو (غور ڪري) نه ڏٺائون ڇا؟ جيڪڏھن گھُرون ته کين زمين ۾ ڳھائي ڇڏيون يا آسمان کان مٿن ٽُڪرا ڪيرايون، بيشڪ ان ۾ سڀڪنھن (الله ڏانھن) ورندڙ ٻانھي لاءِ (وڏي) نشاني آھي.'),
(3616, 34, 10, '۽ بيشڪ داؤد کي پاڻ وٽان بزرگي ڏني سون، (۽ حُڪم ڪيوسون ته) اي جبلؤ ساڻس تسبيحون چئو ۽ پکين کي (اُن جو فرمانبردار ڪيوسون)، ۽ اُن لاءِ لوھ کي نرم ڪيوسون.'),
(3617, 34, 11, '(حڪم ڏنوسون) ته ويڪريون زرھون بڻاءِ ۽ اندازي سان ڪڙيون ٺاھ ۽ (اي داؤد جي گھراڻي وارؤ) چڱا ڪم ڪريو، ڇوته جيڪي اوھان ڪندا آھيو سو بيشڪ آءٌ ڏسندڙ آھيان.'),
(3618, 34, 12, '۽ واءُ کي سُليمان لاءِ (نوايوسون) اُن جي اڳياڙيءَ جي منزل مھيني جو پنڌ ۽ پوياڙيءَ جي منزل (به) مھيني جو پنڌ ھئي ۽ اُن لاءِ پگھريل ٽامي جو تلاءُ وَھايوسون، ۽ جنّن مان (به ڪن کي فرمانبردار ڪيوسون) جو سندس پالڻھار جي حڪم سان سندس اڳيان ڪمائيندا ھوا، ۽ منجھائن جيڪو اسان جي حُڪم کان مُنھن موڙيندو تنھن کي باھ جو عذاب چکائينداسون.'),
(3619, 34, 13, 'جيڪي گھرندو ھو سو اُن لاءِ ڪي قلعا ۽ مُورتون ۽ حوض جيڏا پيالا ۽ وڏيون ديڳيون جي نه چُرنديون ھيون سي بڻائيندا ھوا، (چيوسون ته) اي داؤد جا اولاد شُڪرانو ڪريو، ۽ مُنھنجن ٻانھن مان ٿورا شُڪرانو ڪندڙ آھن.'),
(3620, 34, 14, 'پوءِ جنھن مھل مٿس موت کي حُڪم ڪيوسون (تنھن مھل) سندس موت تي اُڏيھيءَ کانسواءِ (ٻئي ڪنھن) اُنھن (جِنّن) کي نه چتايو جو سندس لٺ کي ٿي کاڌائين، پوءِ جنھن مھل ڪريو (تنھن مھل) جِنّن معلوم ڪيو ته جيڪڏھن ڳجھ ڄاڻن ھا ته خواريءَ واري عذاب ۾ نه رھن ھا.'),
(3621, 34, 15, 'بيشڪ سبا جي قوم لاءِ سندن ڳوٺ ۾ ھڪ نشاني ھُئي، (يعني) ٻه باغ سڄي ۽ کٻي پاسي کان ھوا، (چيو وين ته) پنھنجي پالڻھار جي رزق مان کائو ۽ سندس شڪرانو ڪريو، (اوھان لاءِ ھي) شھر سٺو آھي ۽ پالڻھار بخشڻھار آھي.'),
(3622, 34, 16, 'پوءِ (شڪر ڪرڻ کان) منھن موڙيائون تنھنڪري (بند ڀڃي) سخت ڇَـرَ کي مٿن وھايوسين ۽ سندن (اُنھن) ٻن باغن جي بدلي کين (ٻيا) ٻه باغ ڪسارن ميون ۽ لَئِن وارا ۽ ڪجھ ٿورين ٻيرين وارا ڏناسون.'),
(3623, 34, 17, 'سندن بي شڪريءَ سببان کين اھا سزا ڏني سون، ۽ بي شڪر کانسواءِ (ٻئي) ڪنھن کي سزا نه ڏيندا آھيون.'),
(3624, 34, 18, '۽ سندن وچ ۾ اُھي ڳوٺ جن ۾ برڪت رکي سون تن جي وچ ۾ پڌرا ڳوٺ (لڳولڳ) ڪياسون ۽ انھن ۾ اچ وڃ جو پورو بندوبست رکيوسون، (چيوسون ته) راتيان ۽ ڏينھان منجھن بي ڀؤا گھمندا رھو.'),
(3625, 34, 19, 'پوءِ چيائون ته اي اسان جا پالڻھار اسان جي مسافرين ۾ ڏورائي ڪر ۽ پاڻ تي ظلم ڪيائون تنھن ڪري انھن کي (ٻـين جي لاءِ) آکاڻيون ڪيوسين ۽ کين تمام پرزا پرزا ڪري ڇني ڦاڙي ڇڏيوسون، بيشڪ ھن ۾ سڀڪنھن وڏي صابِر شاڪر لاءِ نشانيون آھن.'),
(3626, 34, 20, '۽ بيشڪ ابليس انھن به نسبت پنھنجي گمان کي سچو لڌو جو مؤمنن جي ھڪ ٽوليءَ کانسواءِ ٻـين اُن جي تابعداري ڪئي.'),
(3627, 34, 21, '۽ اُن جو مٿن ڪو زور نه ھو پر (اسين پرکون) ته جيڪو آخرت کي مڃيندو ھجي تنھن کي اُنھيءَ کان ڌار ڪريون جيڪو اُن کان شڪ ۾ (پيل) ھجي، ۽ تنھنجو پالڻھار سڀڪنھن شيء تي نگھبان آھي.'),
(3628, 34, 22, '(کين) چؤ ته الله کانسواءِ جن ۾ اوھين گمان ڪندا آھيو تن کي سڏيو، اُھي نڪي آسمانن ۾ ۽ نڪي زمين ۾ ذري جيترا (به) مالڪ آھن ۽ نڪي منجھن ڪا ڀائيواري آھي ۽ نڪي منجھائن ڪو الله جو مددگار آھي.'),
(3629, 34, 23, '۽ نڪي وٽس (ٻئي ڪنھن جي) سفارش فائدو ڏيندي مگر جنھن لاءِ رخصت ڏني ھوندائين (فرشتن جو ھي حال آھي ته جنھن مھل ڪو حُڪم خدا جو نازل ٿئي ٿو ته اُنھن جي طبيعت ۾ گھٻراھٽ پيدا ٿئي ٿي) تانجو جنھن وقت ھنن جي دلين تان گھٻراھٽ لھي ٿي (تنھن مھل) چون ٿا ته اوھان جي پالڻھار ڇا چيو آھي (مٿان ملائڪ ھيٺين کي) چوندا ته سچ فرمايو اٿس ۽ اُھو (سڀ کان) مٿاھون وڏو آھي.'),
(3630, 34, 24, '(کين) چؤ ته اوھان کي آسمانن ۽ زمين مان ڪير روزي ڏيندو آھي، چؤ ته الله (ڏيندو آھي) ۽ بيشڪ اسين يا اوھين (يا ته) ھدايت تي آھيون يا پڌريءَ گُمراھي ۾.'),
(3631, 34, 25, 'چؤ ته جيڪي اسان ڏوھ ڪيا آھن تنھن بابت اوھين پڇيا نه ويندؤ ۽ جيڪي اوھين ڪندا آھيو تنھن بابت اسين پُڇيا نه وينداسون.'),
(3632, 34, 26, 'چؤ ته اسان جو پالڻھار اسان (مڙني) کي گڏ ڪندو وري اسان ۽ (اوھان) جي وچ ۾ حق سان فيصلو ڪندو، ۽ اھوئي (سچو) فيصلو ڪندڙ ڄاڻندڙ آھي.'),
(3633, 34, 27, 'چؤ ته جن کي اوھان ساڻس شريڪ بڻائي ملايو آھي سي مون کي ڏيکاريو (چؤ ته) ائين نه آھي، بلڪ اُھو الله غالب حِڪمت وارو آھي.'),
(3634, 34, 28, '(اي پيغمبر) توکي ته مڙني ماڻھن لاءِ خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليوسون پر گھڻا ماڻھو نه ڄاڻندا آھن.'),
(3635, 34, 29, '۽ چوندا آھن ته جيڪڏھن اوھين سچا آھيو ته اھو (قيامت جو) انجام ڪڏھن ٿيندو.'),
(3636, 34, 30, 'چؤ ته اوھان لاءِ اھڙي ڏينھن جو انجام آھي جنھن کان ھڪ گھڙي نڪي دير ڪندؤ ۽ نڪي اڳي ويندؤ.'),
(3637, 34, 31, '۽ ڪافر چون ٿا ته ھن قُرآن کي ڪڏھن نه مڃينداسون ۽ نڪي اُنھي کي جو کانئس اڳ آھي، ۽ (اي ڏسندڙ) جڏھن ظالم پنھنجي پالڻھار اڳيان بيھاربا (تڏھن) جيڪڏھن ڏسين ته (عجب ڪرين)، جو (پاڻ ۾) انھن مان ھڪڙا ٻـين ڏي ڳالھ ورائيندا، جيڪي ھيڻا آھن سي ھٺيلن کي چوندا ته جيڪڏھن اوھان نه ھجوھا ته اسين مؤمن ھجون ھا.'),
(3638, 34, 32, 'ھٺيلا ھيڻن کي چوندا ته جڏھن (ھدايت) اوھان وٽ آئي تڏھن اسان اوھان کي ھدايت کان جھليو ھو ڇا؟ (نه!) بلڪ اوھين پاڻ گنھگار ھئؤ.'),
(3639, 34, 33, '۽ ھيڻا ھٺيلن کي چوندا ته (ائين نه آھي) بلڪ رات ۽ ڏينھن جي (اوھان جي) مڪر ڪرڻ (اسان کي جھليو ھو) جڏھن اسان کي حُڪم ڪندا ھيؤ ته الله سان ڪفر ڪيو ۽ ساڻس شريڪ ٺھرايون، ۽ جڏھن عذاب کي ڏسندا (تڏھن) پشيماني (دل ۾) لڪائيندا، ۽ ڪافرن جي ڳچين ۾ ڳٽ وجھنداسون، جيڪي ڪندا ھوا تنھن کانسواءِ ٻئي جي سزا نه ڏبين.'),
(3640, 34, 34, '۽ ڪنھن ڳوٺ ۾ ڪو ڊيڄاريندڙ نه موڪليوسون مگر اُن جي آسودن (ماڻھن) چيو ته بيشڪ جنھن شيء سان اوھين موڪليا ويا آھيو (تنھنکي) اسين نه مڃيندڙ آھيون.'),
(3641, 34, 35, '۽ چيائون ته اسين گھڻي مال ۽ اولاد وارا آھيون ۽ اسان کي عذاب ٿيڻو نه آھي.'),
(3642, 34, 36, 'چؤ ته منھنجو پالڻھار جنھن لاءِ گھري تنھنجي روزي ڪشادي ڪندو آھي ۽ (جنھن لاءِ گھري تنھن جي) تنگ ڪندو آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.'),
(3643, 34, 37, '۽ نڪي اوھان جا مال ۽ نڪي اوھان جا اولاد اھڙي شيء آھن جو اوھان کي اسان وٽ مرتبي جي ڪري ويجھو ڪن پر (ويجھا اُھي آھن) جن ايمان آندو ۽ چڱا ڪم ڪيا، تن جيڪي ڪمايو تنھن سببان اُنھن لاءِ ٻيڻو بدلو آھي ۽ اُھي ماڙين ۾ امن سان رھندا.'),
(3644, 34, 38, '۽ جيڪي اسان جي آيتن (جي رد ڪرڻ) ۾ مقابلو ڪندڙ ٿي ڪوشش ڪندا آھن اُھي عذاب ۾ حاضر ڪبا.'),
(3645, 34, 39, 'چؤ ته بيشڪ منھنجو پالڻھار پنھنجن ٻانھن مان جنھن لاءِ گھرندو آھي تنھنجي روزي ڪشادي ڪندو آھي ۽ (جنھن لاءِ گھرندو آھي تنھنجي) تنگ ڪندو آھي، ۽ جيڪي اوھين ڪُجھ خرچيندؤ تنھن جو الله بدلو ڏيندو، ۽ اُھو چڱو روزي ڏيندڙ آھي.'),
(3646, 34, 40, '۽ جنھن ڏينھن اُنھن مڙني کي گڏ ڪندو وري ملائڪن کي چوندو ته (ھيءُ) اُھي آھن ڇا جيڪي اوھان کي پوڄيندا ھوا؟'),
(3647, 34, 41, '(ملائڪ) چوندا ته تون پاڪ ذات آھين تون اسان جو سائين آھين نه اُھي، بلڪ اُھي جنّن کي پوڄيندا ھوا، انھن مان گھڻا کين مڃيندڙ ھوا.'),
(3648, 34, 42, 'پوءِ اڄ اوھان مان ڪوبه ھڪ ٻئي کي نڪي سُک پُھچائڻ جو نڪي ڏک پھچائڻ جو اختيار رکي ٿو، ۽ ظالمن کي چونداسون ته اُن باھ جي سزا جو (جو مزو) چکو جنھن کي اوھين ڪُوڙ ڀائيندا ھوا.'),
(3649, 34, 43, '۽ جڏھن اسان جون پڌريون آيتون کين پڙھي ٻڌائبيون آھن تڏھن (ھڪ ٻئي کي) چوندا آھن ته ھيءُ رڳو ھڪ ماڻھو آھي جو جنھن کي اوھان جا ابا ڏاڏا پوڄيندا ھوا تنھن کان اوھان کي جھلڻ گھرندو آھي، ۽ چوندا آھن ته ھيءُ (قرآن) رڳو ڪوڙ ٺھيل آھي، ۽ ڪافرن وٽ جڏھن سچي ڳالھ آئي (تڏھن) اُن لاءِ چوندا آھن ته ھي رڳو پڌرو جادو آھي.'),
(3650, 34, 44, 'حالانڪ اُنھن (مشرڪن) کي نڪو ڪو ڪتاب ڏنوسون جنھن کي پڙھن ۽ نڪي توکان اڳ ڪو ڊيڄاريندڙ اُنھن ڏانھن موڪليوسون.'),
(3651, 34, 45, '۽ کائن اڳين به ڪوڙ ڄاتو ھو حالانڪ جيڪي انھنکي ڏنوھوسون تنھن جي ڏھين پتيءَ کي (به اُھي مشرڪ) نه پُھتا آھن پوءِ اُنھن اسان جي پيغمبرن کي ڪوڙو ڄاتو، پوءِ منھنجو عذاب ڪئن ھو.'),
(3652, 34, 46, '(کين) چؤ ته اوھان کي رڳو ھڪ ڳالھ جي نصيحت ڪريان ٿو، ته ٻه ٻه ۽ ھڪ ھڪ ٿي الله (جي ڪم) لاءِ کڙا ٿيو وري پروڙيو، ته اوھان جي سنگتيءَ کي ڪو سوداءُ نه آھي، (بلڪ) اُھو ته سخت عذاب (جي پھچڻ) کان اڳ اوھان کي ڊيڄاريندڙ آھي.'),
(3653, 34, 47, '(۽ کين) چؤ ته اوھان کان جيڪو ڪو اُجورو گھريو ھجيم سو اوھان کي ئي ھجي، منھنجو اُجورو الله کانسواءِ ٻئي ڪنھن تي نه آھي، ۽ اُھو ھر شيء تي شاھد آھي.'),
(3654, 34, 48, '(کين) چؤ ته بيشڪ منھنجو پالڻھار حق کي منھنجي دل ۾ وجھندو آھي، (اھو) ڳُجھن جو ڄاڻندڙ آھي.'),
(3655, 34, 49, '(کين) چ‍ؤ ته حق آيو ۽ ڪوڙو معبود نڪي نئي سر پيدا ڪري ٿو نڪي بِھر پيدا ڪندو.'),
(3656, 34, 50, '(کين) چؤ ته جيڪڏھن گمراھ ٿيس ته رڳو پاڻ لاءِ گمراھ ٿيان ٿو، ۽ جيڪڏھن ھدايت وارو ٿيس ته (اُھا) اُنھيءَ سببان آھي جو منھنجو پالڻھار مون ڏانھن وحي موڪليندو آھي، بيشڪ اُھو ٻڌندڙ ويجھو آھي.'),
(3657, 34, 51, '۽ جنھن مھل پريشان ٿيندا ۽ (الله جي عذاب کان) ڇوٽڪارو نه لھندا ۽ ويجھي ھنڌان پڪڙبا (تنھن مھل) جيڪڏھن ڏسين (ته عجب ڪرين).'),
(3658, 34, 52, '۽ چوندا ته (ھاڻي) اُن (قرآن) تي ايمان آندوسون، ۽ (اُتي دُنيا جي) ڏورين ھنڌ کان کين (ايمان) ھٿ آڻڻ ڪٿان ٿيندو؟'),
(3659, 34, 53, 'حالانڪ اڳي اُن قرآن کي نه مڃيندا ھوا، ۽ ڏورين ھنڌ کان اَڻ ڏٺو (گُمان جا تير) اُڇليندا رھيا.'),
(3660, 34, 54, '۽ اُنھن جي ۽ اُھي جيڪي سَڌون ڪندا رھيا تن جي وچ ۾ اَڙ وڌي وئي جھڙي طرح انھن جھڙن (ٻـين) کان اڳ ڪيو ويو، بيشڪ اُھي به وڏي شڪ ۾ پيل ھوا.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'سڀ ساراھ خاص الله کي جُڳائي جو آسمانن ۽ زمين جو بڻائيندڙ آھي ملائڪن کي قاصد ڪرڻ وارو آھي جو ٻن ٻن ۽ ٽن ٽن ۽ چَئن چَئن پَرن وارا آھن، جيڪي گھري سو خلقت ۾ وڌائي ٿو، بيشڪ الله ھر شيء تي وس وارو آھي.'),
(3662, 35, 2, 'الله ماڻھن لاءِ (پنھنجيءَ) ٻاجھ مان جيڪي کوليندو تنھن کي ڪو بند ڪرڻ وارو نه آھي، ۽ جيڪي بند ڪندو تنھنکي اُن (جي بند ڪرڻ) کانپوءِ ڪو کولڻ وارو نه آھي، ۽ اُھو غالب حڪمت وارو آھي.'),
(3663, 35, 3, 'اي ماڻھؤ پاڻ تي الله جون نعمتون ياد ڪريو، الله کانسواءِ ڪو بڻائيندڙ آھي ڇا؟ جو آسمان ۽ زمين مان اوھان کي روزي ڏئي ٿو، اُن (الله) کانسواءِ ڪو عبادت جو لائق نه آھي پوءِ ڪيڏانھن ڦيرايا ويندا آھيو.'),
(3664, 35, 4, '۽ (اي پيغمبر) جيڪڏھن ڪوڙو ڀائين ته بيشڪ تو کان اڳ (ٻيا ڪيترائي) پيغمبر ڪوڙا ڀانيا ويا، ۽ سڀ ڪم الله ڏانھن موٽائجن ٿا.'),
(3665, 35, 5, 'اي ماڻھؤ بيشڪ الله جو اَنجام سچّو آھي تنھنڪري نڪي اوھان کي دنيا جي حياتي ٺڳي، ۽ نڪي فريبيندڙ (شيطان) الله بابت اوھان کي فريبي.'),
(3666, 35, 6, 'بيشڪ شيطان اوھان جو ويري آھي تنھنڪري اُن کي ويري (ڪري) وٺو، (ھو) پنھنجيءَ ٽوليءَ کي رڳو ھن لاءِ سڏيندو آھي ته دوزخين مان ٿين.'),
(3667, 35, 7, 'جن ڪُفر ڪيو تن لاءِ سخت عذاب آھي، ۽ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ بخشش ۽ وڏو اجر آھي.'),
(3668, 35, 8, 'جنھن لاءِ سندس بڇڙو ڪم سينگاريو ويو پوءِ اُن کي ھُن چڱو (ڪري) ڏٺو (سو چڱي ڪم ڪندڙ جھڙو ھوندو) ڇا؟ پوءِ بيشڪ الله جنھن کي گھرندو آھي تنھن کي گمراھ ڪندو آھي ۽ جنھن کي گھرندو آھي تنھن کي ھدايت ڪندو آھي، تنھنڪري مٿن ارمان ڪرڻ سببان متان تنھنجو ساھ نڪري، ڇوته جيڪي ڪندا آھن سو الله ڄاڻندڙ آھي.'),
(3669, 35, 9, '۽ الله اُھو آھي جنھن وائن کي موڪليو پوءِ اُنھن ڪڪر کي کنيو پوءِ اُن (ڪڪر) کي ويران ڳوٺ ڏانھن روانو ڪيوسون پوءِ اُن (جي پاڻيءَ) سان زمين کي سندس ويرانيءَ کانپوءِ جياريوسون، اھڙي طرح (قبرن مان) مُئن جو اُٿڻ آھي.'),
(3670, 35, 10, 'جيڪو مانُ گھري (سو ياد رکي) ته سڀ مانُ الله ئي کي آھي (اُن وٽان عزت ملندي آھي)، ڏانھس پاڪ سُخن چڙھندو آھي ۽ اُن (پاڪ سُخن) کي چڱو عمل مٿي چاڙھيندو آھي، ۽ جيڪي بڇڙاين جي رٿ ڪندا آھن تن لاءِ سخت عذاب آھي، ۽ اُھا ٺڳي اُنھن جي ٽٽندي.'),
(3671, 35, 11, '۽ الله اوھان کي مٽيءَ مان پيدا ڪيو وري نطفي مان (بڻايائين) وري اوھان کي جوڙو جوڙو (مڙس ۽ زال) ڪيائين، ۽ مادين مان ڪابه سندس علم کانسواءِ نڪي ڍُڪي ٿيندي ۽ نڪي ڄڻيندي آھي، ۽ نڪي ڪنھن وڏي عمر واري کي ڄمار ڏبي آھي ۽ نڪي سندس ڄمار مان (ڪجھ) گھٽائبو آھي مگر (اُھو) ڪتاب ۾ لکيل آھي، بيشڪ اھو (ڪم) الله کي آسان آھي.'),
(3672, 35, 12, '۽ ٻه درياءَ ھڪ جھڙا نه آھن (جن مان) ھيءُ ھڪ مِٺو اُڃ لاھيندڙ آھي سندس پاڻي وڻندڙ آھي ۽ اُھو ٻيو کارو ڪوڙو آھي، ۽ ھر ھڪ مان (مڇيءَ جو) تازو گوشت کائيندا آھيو ۽ زيور (يعني موتي منجھائن) ڪڍندا آھيو جي پائيندا آھيو، ۽ منجھس (پاڻي کي) چيريندڙ ٻيڙيون ڏسندو آھين (ھن لاءِ) ته سندس فضل مان (روزي) طلب ڪريو ۽ ته مانَ اوھين شڪرانو ڪريو.'),
(3673, 35, 13, 'رات کي ڏينھن ۾ گھيڙيندو آھي ۽ ڏينھن کي رات ۾ گھيڙيندو آھي ۽ سج ۽ چنڊ کي تابع ڪيائين، ھر ھڪ ٺھرايل مدت تي ھلندو آھي، اُھو الله اوھان جو پالڻھار آھي اُن جي بادشاھي آھي، ۽ جن کي الله کانسواءِ سڏيندا آھيو سي کارڪ جي ککڙيءَ جي ڇلُ (جيتري ذري) جا (به) مالڪ نه آھن.'),
(3674, 35, 14, 'جيڪڏھن کين سڏيو ته اوھان جو سڏ نه ٻڌندا، ۽ جيڪڏھن (کڻي) ٻڌن (به) ته به اوھان کي ورندي نه ڏيندا، ۽ قيامت جي ڏينھن اوھان جي شريڪ ٺھرائڻ جا منڪر ٿيندا، ۽ توکي خبر رکندڙ جھڙي ڪوبه خبر نه ڏيندو.'),
(3675, 35, 15, 'اي ماڻھو اوھين الله ڏانھن مُحتاج آھيو، ۽ الله اُھو بي پرواھ ساراھيل آھي.'),
(3676, 35, 16, 'جيڪڏھن گھري ته اوھان کي ناس ڪري ۽ نئين خلق پيدا ڪري.'),
(3677, 35, 17, '۽ اِھو (ڪم) الله کي اَوکو نه آھي.'),
(3678, 35, 18, '۽ ڪوبه بار کڻندڙ ٻئي جو بار نه کڻندو، ۽ جيڪڏھن (گناھن جي) ڳوري بار وارو پنھنجي بار کڻڻ ڏانھن ڪنھن کي سڏيندو توڻيڪ مائٽي وارو ھوندو ته به منجھانئس ڪجھ به نه کنيو ويندو، تون ته انھن کي ڊيڄاري سگھندين جيڪي پنھنجي پالڻھار کان پرپُٺ ڊڄندا آھن ۽ نماز پڙھندا آھن، ۽ جيڪو پاڪ ٿيو سو رڳو پاڻ لاءِ پاڪ ٿئي ٿو، ۽ الله ڏانھن موٽڻ آھي.'),
(3679, 35, 19, '۽ انڌو ۽ سڄو برابر نه آھن.'),
(3680, 35, 20, '۽ نڪي اونداھيون ۽ نڪي سوجھرو.'),
(3681, 35, 21, '۽ نڪي ڇانوَ ۽ نڪي اُس (پاڻ ۾ ھڪ جھڙا آھن).'),
(3682, 35, 22, '۽ نڪي جيئرا ۽ نڪي مُئا برابر ٿين ٿا، بيشڪ الله جنھن کي گھري تنھن کي ٻڌائيندو آھي، ۽ تون ڪو قبر وارن کي ٻڌائيندڙ نه آھين.'),
(3683, 35, 23, 'تون ته رڳو ڊيڄاريندڙ آھين.'),
(3684, 35, 24, 'بيشڪ اسان توکي سچي دين سان خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو، ۽ اھڙي ڪا ٽولي آھي ئي ڪانه جنھن ۾ ڪو ڊيڄاريندڙ نه گذريو آھي.'),
(3685, 35, 25, '۽ جيڪڏھن توکي ڪوڙو ڀائين ٿا ته کائن اڳين به بيشڪ ڪوڙو ڀانيو ھو، جن وٽ سندن پيغمبر چِٽن معجزن ۽ صحيفن سان ۽ پڌرن ڪتابن سان آيا ھوا.'),
(3686, 35, 26, 'وري ڪافرن کي پڪڙيم ۽ منھنجي سزا ڪيئن ھئي.'),
(3687, 35, 27, 'نه ڏٺو اٿئي ڇا ته الله آسمانن کان پاڻي وسايو، پوءِ اُن سان ميوا پيدا ڪياسون جن جا رنگ قسمين قسمين آھن، ۽ جبلن مان ٽُڪر آھن ڪي اڇا ۽ ڪي ڳاڙھا انھن جا طرحين طرحين رنگ آھن ۽ ڪي نھايت ڪارا (به) آھن.'),
(3688, 35, 28, '۽ اھڙيءَ طرح ماڻھن ۽ چرندڙن ۽ ڍورن مان (به) جو انھن جا رنگ طرح طرح جا آھن (بڻاياسين)، الله کان سندس ٻانھن مان رڳو سمجھ وارا ڊڄندا آھن، بيشڪ الله غالب بخشڻھار آھي.'),
(3689, 35, 29, 'بيشڪ جيڪي (ماڻھو) الله جو ڪتاب پڙھندا آھن ۽ نماز (پوريءَ طرح) پڙھندا آھن ۽ جيڪي کين ڏنوسون تنھن مان ڳُجھو ۽ پڌرو خرچيندا آھن سي اھڙيءَ سوداگري جي اميد رکندا آھن جا ڪڏھن نه برباد ٿيندي.'),
(3690, 35, 30, '(ھن لاءِ) ته خُدا کين سندن اجر پورو ڏئي ۽ پنھنجي فضل سان کين وڌيڪ به ڏئي، ڇوته اھو بخشڻھار قدر ڄاڻندڙ آھي.'),
(3691, 35, 31, '۽ جيڪي توڏانھن ڪتاب مان وحي ڪيوسون سو سچ آھي جيڪي کانئس اڳ آھن تن کي به سچّو ڪندڙ آھي، بيشڪ الله پنھنجن ٻانھن جي خبر رکندڙ ڏسندڙ آھي.'),
(3692, 35, 32, 'وري انھن کي ڪتابن جو وارث ڪيوسون جن کي پنھنجن ٻانھن مان چونڊيوسون، پوءِ منجھائن ڪي پاڻ لاءِ ظالم آھن، ۽ منجھائن ڪي وچٿرا آھن، ۽ منجھائن ڪي الله جي حُڪم سان چڱاين ۾ اڳرائي ڪندڙ آھن، اِھوئي وڏو فضل آھي.'),
(3693, 35, 33, '(انھن لاءِ ھميشه وارا باغ آھن) جن ۾ (اُھي) گھڙندا منجھن سون جا ڪنگڻ ۽ موتي پھرائبن، ۽ انھن ۾ سندن لباس پَٽ ھوندو.'),
(3694, 35, 34, '۽ چوندا ته سڀ ساراھ انھي الله کي جڳائي جنھن اسان کان (سڀ) ڏُک ٽاريا، بيشڪ اسان جو پالڻھار بخشڻھار قدر ڄاڻندڙ آھي.'),
(3695, 35, 35, 'جنھن اسان کي پنھنجي فضل سان سدائين رھڻ واري ھنڌ لاٿو، نڪي اسان کي منجھس ڪو ڏک پھچندو ۽ نڪي اسان کي منجھس ڪو ٿَڪ پھچندو.'),
(3696, 35, 36, '۽ جن ڪفر ڪيو تن لاءِ دوزخ جي باھ آھي، نڪي مٿن حُڪم ڪبو ته مرن ۽ نڪي کائن اُن جي عذاب مان ڪي ھلڪو ڪبو، سڀڪنھن بي شُڪر کي اھڙي طرح سزا ڏيندا آھيون.'),
(3697, 35, 37, '۽ اُھي (دوزخي) منجھس دانھون ڪندا، ته اي اسان جا پالڻھار اسان کي ڪڍ ته جيڪي ڪم ڪندا ھواسون تن ڌاران ٻيا چڱا ڪم ڪريون، (چيو ويندن ته) اوھان کي ايتري ڄمار نه ڏني ھئي سون ڇا ته جيڪو نصيحت وٺڻ گھري ھا سو منجھس نصيحت وٺي سگھي ھا؟ ۽ اوھان وٽ ڊيڄاريندڙ (به) آيو، تنھنڪري (پنھنجي ڪمائيءَ جو مزو) چکو ڇوته ظالمن جو ڪو مددگار نه آھي.'),
(3698, 35, 38, 'بيشڪ الله آسمانن ۽ زمين جو ڳُجھ ڄاڻندڙ آھي، بيشڪ اُھو سِينن وارو (ڳُجھ) ڄاڻندڙ آھي.'),
(3699, 35, 39, '(الله) اُھو آھي جنھن اوھان کي زمين ۾ (اڳين جو) پيڙھيءَ وارو ڪيو، پوءِ جيڪو ڪُفر ڪندو تنھن تي سندس ڪفر (جو نقصان) آھي، ۽ نڪي ڪافرن لاءِ سندن ڪُفر سندن پالڻھار وٽ ڏمر کانسواءِ (ٻيو ڪجھ) وڌائيندو، ۽ نڪي ڪافرن لاءِ سندن ڪفر ڇيئي کانسواءِ (ٻيو ڪجھ) وڌائيندو.'),
(3700, 35, 40, '(کين) چؤ ته الله کانسواءِ جن پنھنجن (ٺھرايلن) بُتن کي سڏيندا آھيو سي ڏٺا اٿوَ ڇا؟ مون کي ڏيکاريو ته زمين مان ڇا بڻايو اٿن يا آسمانن ۾ سندن ڪا ڀائيواري آھي، يا کين ڪو ڪتاب ڏنو اٿون ڇا جو اُھي اُن جي پڌري حُجّت تي آھن؟ (نه!) بلڪ ظالم ھڪ ٻئي کي رڳو ٺڳيءَ جو انجام ڏيندا آھن.'),
(3701, 35, 41, 'بيشڪ الله آسمانن ۽ زمين کي ڊھڻ کان نگاھ رکندو آھي، ۽ جيڪڏھن ڊَھن ته خُدا کانسواءِ انھن کي ڪوبه ڪونه نگاھ رکندو، بيشڪ اُھو بُردبار بخشڻھار آھي.'),
(3702, 35, 42, '۽ اُنھن پنھنجن پڪّن قسمن سان الله جو قسم کنيو ته جيڪڏھن وٽن ڪوبه ڊيڄاريندڙ آيو (تنھن مھل اُن جي اچڻ) کين رڳو نفرت وڌائي.'),
(3703, 35, 43, 'مُلڪ ۾ وڏائي ڪرڻ ۽ بڇڙي رِٿ رِٿڻ سبب (نفرت ڪيائون)، ۽ بڇڙي رٿ رٿڻ (جو نتيجو) اُن جي ھلت ڪرڻ وارن کانسواءِ ٻئي تي نه پوندو آھي، تنھن ڪري رڳو اڳين جي (عذاب جي) دستور جا منتظر آھن، پوءِ (تون) ڪڏھن الله جي دستور کي ڪا مَٽ سَٽ نه ڏسندين، ۽ (تون) ڪڏھن الله جي دستور کي ڪا ڦيرگھير نه ڏسندين.'),
(3704, 35, 44, 'زمين ۾ نه گھميا آھن ڇا جو ڏسن ته جيڪي کائن اڳ ھوا تن جي پڇاڙي ڪھڙي ٿي اُھي کائن سگھ ۾ به تمام ڏاڍا ھوا، ۽ الله اھڙو نه آھي جو کيس آسمانن ۾ ڪابه شيء ٿڪائي ۽ نڪي زمين ۾ (ٿڪائي)، بيشڪ اُھو ڄاڻندڙ وس وارو آھي.'),
(3705, 35, 45, '۽ جيڪڏھن الله ماڻھن کي سندن ڪرتوتن سببان پڪڙي ھا ته ڪو چُرندڙ زمين جي پُٺيءَ تي نه ڇڏي ھا پر ٺھرايل مُدّت توڻي کين مُھلت ڏئي ٿو، پوءِ جڏھن سندن مُدت آئي تڏھن بيشڪ الله پنھنجن ٻانھن کي ڏسندڙ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'اي پيغمبر.'),
(3707, 36, 2, '(ھن) حِڪمت واري قرآن جوقسم آھي.'),
(3708, 36, 3, 'ته بيشڪ تون پيغمبرن مان آھين.'),
(3709, 36, 4, '(بيشڪ تون) سڌي واٽ تي آھين.'),
(3710, 36, 5, '(الله) غالب مھربان وٽان (قرآن جو) لاھڻ آھي.'),
(3711, 36, 6, '(ھن لاءِ) ته اھڙي قوم کي ڊيڄارين جو سندن پيءُ ڏاڏا نه ڊيڄاريا ويا آھن تنھنڪري اُھي بي خبر آھن.'),
(3712, 36, 7, 'بيشڪ انھن مان گھڻن تي (عذاب جو) قول لازم ٿيو آھي تنھنڪري اُھي ايمان نه ٿا آڻين.'),
(3713, 36, 8, 'بيشڪ اسان سندن ڳچين ۾ ڳَٽ وڌا پوءِ اُھي کاڏين تائين (پيل) اٿن تنھنڪري اُھي ڳاٽي ٽنگيل آھن.'),
(3714, 36, 9, '۽ سندن اڳيان اوٽ ۽ سندن پوئتان (به) اوٽ ڪئي سون پوءِ کين ڍڪيوسون تنھنڪري اُھي (ڪجھ) نه ڏسندا.'),
(3715, 36, 10, '۽ کين تون ڊيڄارين يا نه ڊيڄارين سندن لاءِ ھڪ جھڙو آھي تنھنڪري اُھي ايمان نه آڻيندا.'),
(3716, 36, 11, 'تون رڳو اُن کي ڊيڄاريندين جيڪو نصيحت تي ھلي ۽ ٻاجھاري (الله) کان پرپُٺ ڊڄي، ته پوءِ اُن کي بخشش ۽ سڳوري اجر جي خوشخبري ڏي.'),
(3717, 36, 12, 'بيشڪ اسين مُئن کي جياريندا آھيون ۽ جيڪي (مَدا توڻي چڱا ڪم ڪري) اڳي موڪليائون سي ۽ سندن پيرن جا نشان لکون ٿا، ۽ ھر شيء کي پڌري ڪتاب (لوح محفوظ) ۾ ڳڻيو اٿون.'),
(3718, 36, 13, '۽ اُنھن سان ڳوٺ (انطاڪيہ) وارن جو قصّو بيان ڪر، جڏھن وٽن پيغمبر آيا.'),
(3719, 36, 14, '(يعني) جڏھن اُنھن ڏانھن ٻن پيغمبرن کي موڪليوسون پوءِ ٻنھي کي ڪوڙو ڀانيائون تنھنڪري ٽئين سان غلبو ڏنوسون پوءِ (گڏجي) چيائون ته بيشڪ اسين اوھان ڏانھن موڪليل آھيون.'),
(3720, 36, 15, 'چيائون ته اوھين ته رڳو اسان جھڙا ماڻھو آھيو ۽ ٻاجھاري (الله) ڪا شيءَ نه لاٿي آھي اوھين رڳو ڪُوڙا آھيو.'),
(3721, 36, 16, 'چيائون ته اسان جو پالڻھار ڄاڻندو آھي ته بيشڪ اسين اوھان ڏانھن موڪليل آھيون.'),
(3722, 36, 17, '۽ اسان تي پڌري پيغام پھچائڻ کانسواءِ (ٻيو ڪجھ) لازم نه آھي.'),
(3723, 36, 18, 'چيائون ته بيشڪ اسان اوھان جي (اچڻ) سببان بدبختي سمجھي آھي، جيڪڏھن اوھين نه جھلبؤ ته ضرور اوھان کي پھڻن سان مارينداسون ۽ ضرور اسان کان اوھان کي ڏکوئيندڙ عذاب پھچندو.'),
(3724, 36, 19, '(پيغمبرن) چيو ته اوھان جي بدبختي اوھان سان آھي، جيڪڏھن اوھان کي نصيحت ڏبي آھي ته (انھيءَ ڪري) ڇا؟ بلڪ اوھين حد کان لنگھيل قوم آھيو.'),
(3725, 36, 20, '۽ شھر جي پرين پاسي کان ھڪ مڙس ڊوڙندو آيو چيائين ته اي منھنجي قوم پيغمبرن جي تابعداري ڪريو.'),
(3726, 36, 21, '(اوھين) انھن جي تابعداري ڪريو جيڪي اوھان کان اُجورو نه گھرندا آھن ۽ اُھي سڌيءَ واٽ وارا آھن.'),
(3727, 36, 22, '۽ مون کي ڇا (ٿيو) آھي جو انھيءَ خُدا جي عبادت نه ڪريان جنھن مونکي پيدا ڪيو ۽ (اوھين به) ڏانھس موٽايا ويندؤ.'),
(3728, 36, 23, 'اُن کانسواءِ ٻيا معبود وٺان ڇا؟ جيڪڏھن ٻاجھارو (الله) مونکي ڏُک پھچائڻ گھري ته اُنھن جي شفاعت مونکان ڪجھ (به ڏک) نه ٽاريندي ۽ نڪي (اُھي) مون کي ڇڏائيندا.'),
(3729, 36, 24, '(جي ائين ڪندس ته) بيشڪ آءٌ اُنھيءَ مھل پڌري گمراھيءَ ۾ ھوندس.'),
(3730, 36, 25, 'بيشڪ مون اوھان جي پالڻھار تي ايمان آندو آھي، پوءِ مون کان ٻُڌو.'),
(3731, 36, 26, '(اِن چوڻ تي کيس شھيد ڪيائون) چيو ويس ته بھشت ۾ گھڙ، چيائين ھيء ارمان (جيڪر) منھنجي قوم ڄاڻي ھا.'),
(3732, 36, 27, 'ته ڇاجي ڪري منھنجي پالڻھار مون کي بخشيو ۽ مون کي عزت وارن مان ڪيائين.'),
(3733, 36, 28, '۽ کانئس پوءِ سندس قوم تي آسمان کان ڪو لشڪر نه لاٿوسون ۽ نڪي (لشڪر) لاھيندڙ آھيون.'),
(3734, 36, 29, '(سندن سزا) رڳو ھڪ سخت رڙ ھئي پوءِ اُنھي (ئي) دم اُھي (باھ وانگر) وسامي (مري ويا).'),
(3735, 36, 30, 'ٻانھن تي ارمان آھي، (جو) وٽن ڪو (به اھڙو) پيغمبر نه آيو جنھن سان ٺـٺوليون نه ٿي ڪيائون.'),
(3736, 36, 31, 'نه ڏٺو اٿن ڇا ته کانئن اڳ ڪيترائي (ماڻھن جا) طبقا ناس ڪياسون (جو) اُھي اُنھن ڏانھن موٽي نه ٿا اچن.'),
(3737, 36, 32, '۽ جيڪي به آھن سي سڀ اسان وٽ حاضر ڪبا.'),
(3738, 36, 33, '۽ ھڪ نشاني اُنھن (جي جيارڻ) لاءِ مُئل زمين آھي، جنھن کي جياريوسون ۽ منجھانئس (اَن جا داڻا) ڪڍياسون جو اُن مان کائين ٿا.'),
(3739, 36, 34, '۽ منجھس کجورين ۽ ڊاکن جا باغ پيدا ڪياسون ۽ اُنھن (باغن) ۾ ڪي چشما وھاياسون.'),
(3740, 36, 35, '(ھن لاءِ ته) سندن ڦر مان کائيندا رھن ۽ اُنھن (ميون) کي سندن ھٿن نه بڻايو آھي، پوءِ ڇونه شُڪرانو ڪندا آھن؟'),
(3741, 36, 36, 'اُھو (الله) پاڪ آھي جنھن اُھا شيء جيڪا زمين ڄمائيندي آھي سا ۽ سندن جيئن مان ۽ جيڪي (شيون) نه ڄاڻندا آھن تن مان سڀني جنس کي (جوڙو جوڙو ڪوڙو ۽ مِٺو ۽ نَر ۽ مادي) پيدا ڪيو.'),
(3742, 36, 37, '۽ اُنھن لاءِ ھڪ نشاني رات آھي، (جو) اُن مان ڏينھن کي ڇڪي ڪڍندا آھيون پوءِ اُنھي مھل اُھي اونداھي ۾ رھندا آھن.'),
(3743, 36, 38, '۽ سج پنھنجي ٽڪاءَ جي جاءِ تي پيو ھلندو آھي، اِھو غالب (الله) جو اندازو (ٺھرايل) آھي.'),
(3744, 36, 39, '۽ چنڊ لاءِ منزلون مقرر ڪيوسون تان جو کجيءَ جي پراڻي بھاري جي ڳنيءَ وانگر وري ڏنگو ٿيندو آھي.'),
(3745, 36, 40, 'نڪي سج کي چنڊ جي پھچڻ جي (ڪا) مجال آھي ۽ نڪي رات (سج لھڻ کان اڳ) ڏينھن تي اڳرائي ڪندڙ آھي، ۽ (سج ۽ چنڊ ۽ تارن مان) سڀئي آسمان ۾ تَرن ٿا.'),
(3746, 36, 41, '۽ اُنھن لاءِ ھڪ نشاني (ھيءَ به آھي) جو اسان سندن اولاد کي ڀريل ٻيڙي ۾ کنيو.'),
(3747, 36, 42, '۽ اُنھن لاءِ اُن (ٻيڙيء) جھڙيون (ٻيون سواريون به) بڻايوسون جن تي سواري ڪندا آھن.'),
(3748, 36, 43, '۽ جيڪڏھن گھرون ته کين ٻوڙي ڇڏيون پوءِ انھن لاءِ ڪو دانھن ورنائڻ وارو نه ٿئي ۽ نڪي اُھي ڇڏائبا.'),
(3749, 36, 44, 'پر پاڻ وٽان ٻاجھ ڪئي سون ۽ ھڪ وقت تائين (اُھي) آسودا ڪياسون.'),
(3750, 36, 45, '۽ جڏھن کين چئبو آھي ته اُنھي (عذاب) کان ڊڄو جيڪو اوھان جي اڳيان ۽ اوھان جي پٺيان (اچڻو) آھي ته مانَ اوھان تي ٻاجھ ڪجي.'),
(3751, 36, 46, '۽ سندن پالڻھار جي نشانين مان ڪائي (اھڙي) نشاني وٽن نه ايندي آھي جنھن کان (ھو) منھن موڙيندڙ نه ھوندا آھن.'),
(3752, 36, 47, '۽ جڏھن کين چئبو آھي ته جيڪي الله اوھان کي رزق ڏنو آھي تنھن مان خرچيو (تڏھن) ڪافر مؤمنن کي چوندا آھن ته اھڙي کي ڇو کارايون جو جيڪڏھن الله گھري ھا ته کيس کارائي ھا، اوھين اڳو پڌري گمراھيءَ ۾ آھيو.'),
(3753, 36, 48, '۽ چوندا آھن ته جيڪڏھن اوھين سچا آھيو ته اِھو انجام ڪڏھن (پورو) ٿيندو.'),
(3754, 36, 49, 'اِھي رڳو ھڪ سخت آواز جو انتظار ڪندا آھن جو اُھي اُنھي حالت ۾ کين (اوچتو) پڪڙيندو جو اُھي جھڳڙو ڪندا ھوندا.'),
(3755, 36, 50, 'پوءِ (اُھي) نڪي وصيت ڪري سگھندا ۽ نڪي پنھنجي گھر وارن ڏانھن موٽي سگھندا.'),
(3756, 36, 51, '۽ صُور ۾ ڦوڪبو پوءِ اُتي جو اُتي پنھنجي پالڻھار ڏانھن قبرن مان ڊوڙندا.'),
(3757, 36, 52, 'چوندا ته اسان لاءِ ويل آھي اسان کي اسان جي سمھڻ جي جاءِ مان ڪنھن اُٿاريو؟ (ملائڪ چوندا ھائو) ھيءُ اُھو آھي جيڪو ٻاجھاري الله انجام ڏنو ھو ۽ پيغمبرن سچ چيو ھو.'),
(3758, 36, 53, '(اِھو واقعو) رڳو ھڪ سخت رڙ ھوندي پوءِ اُھي اُتي جو اُتي گڏ ڪري اسان وٽ حاضر ڪيا ويندا.'),
(3759, 36, 54, 'پوءِ اڄ ڪنھن جيءَ تي ڪجھ به ظلم نه ڪبو ۽ جيڪي ڪندا ھئو رڳو اُن جو اوھان کي بدلو ڏبو.'),
(3760, 36, 55, 'بيشڪ بھشتي اڄ ھڪ ڪم ۾ خوش ھوندا.'),
(3761, 36, 56, 'اُھي ۽ سندين زالون ڇانوَن ۾ تختن تي ٽيڪ ڏئي وھندڙ ھوندا.'),
(3762, 36, 57, 'انھن لاءِ بھشت ۾ ميوا آھن ۽ جيڪي گھرندا سو (پڻ) انھن لاءِ آھي.'),
(3763, 36, 58, 'چيو ويندو ته پالڻھار مھربان (الله) کان (اوھان تي) سلام ھجي.'),
(3764, 36, 59, '۽ (چونداسون ته) اي گنھگارو اڄ (نيڪن کان) جدا ٿيو.'),
(3765, 36, 60, 'اي آدم جا اولاد اوھان ڏانھن پيغام نه موڪليو ھوم ڇا ته شيطان کي نه پوڄجو، جو بيشڪ اوھان جو پڌرو ويري آھي.'),
(3766, 36, 61, '۽ ته منھنجي عبادت ڪجو، (جو) اِھا سڌي واٽ آھي.'),
(3767, 36, 62, '۽ بيشڪ (شيطان) اوھان مان گھڻيءَ خلق کي گمراھ ڪيو، پوءِ اوھين نه سمجھندا ھُئو ڇا؟'),
(3768, 36, 63, 'ھيءُ اُھو دوزخ آھي (جنھن) جو اوھان کي وعدو ڏبو ھو.'),
(3769, 36, 64, 'اُن ۾ اڄ انھي ڪري گِھڙو جو ڪفر ڪندا ھُئو.'),
(3770, 36, 65, 'اڄ سندن واتن تي مُھر ھڻنداسون ۽ جيڪي ڪندا ھوا تنھن بابت سندن ھٿ اسان سان ڳالھائيندا ۽ سندن پير شاھدي ڏيندا.'),
(3771, 36, 66, '۽ جيڪڏھن گھرون ته سندين اکين کي ناس ڪريون پوءِ واٽ ڏانھن ڊوڙن ته پوءِ ڪٿان ڏسندا؟'),
(3772, 36, 67, '۽ جيڪڏھن گھرون ته سندين جاين تي (ئي) کين بد شڪل ڪري ڇڏيون ته پوءِ نڪي اڳتي ھلي سگھن ۽ نڪي پوءَ تي موٽن.'),
(3773, 36, 68, '۽ جنھن کي وڏي ڄمار وارو ڪندا آھيون تنھن کي بڻاوت ۾ اونڌو ڪندا آھيون، (ايتري ڳالھ به) نه سمجھندا آھن ڇا؟'),
(3774, 36, 69, '۽ اُن (پيغمبر) کي نڪي شعر سيکاريو اٿون ۽ نڪي (شعر) اُن جو لائق آھي، ھيءَ ته نصيحت ۽ پڌرو قرآن آھي.'),
(3775, 36, 70, 'ته اُنھي کي ڊيڄاري جيڪو جيئرو (يعني سمجھ وارو) ھجي ۽ ڪافرن تي حجت ثابت ٿئي.'),
(3776, 36, 71, 'نه ڏٺو اٿن ڇا ته اسان انھن لاءِ انھن شين مان ڍور پيدا ڪيا جن کي اسان جي ھٿن بڻايو؟ پوءِ اُھي اُنھن جا مالڪ آھن.'),
(3777, 36, 72, '۽ اُنھن (ڍورن) کي اُنھن جو تابعدار ڪيوسون پوءِ منجھانئن ڪي سندن چڙھيءَ جا آھن ۽ منجھانئن ڪي (اُھي) کائيندا آھن.'),
(3778, 36, 73, '۽ اُنھن لاءِ اُنھن (ڍورن) ۾ گھڻا فائدا آھن ۽ پيئڻ جون شيون (يعني کِير) آھن، پوءِ اُھي ڇونه شُڪر ڪندا آھن؟'),
(3779, 36, 74, '۽ الله کانسواءِ ٻيا معبود ڪري ورتا اٿن ته مانَ کين مدد ڏني وڃي.'),
(3780, 36, 75, '(اُھي) کين مدد ڏئي نه سگھندا ۽ اُھي (بُت) اُنھن جو لشڪر ٿي گڏ ڪيا ويندا.'),
(3781, 36, 76, 'پوءَ (اي پيغمبر) توکي سندن چوڻ نه ڏکوئي، جيڪي ڳجھو ڪندا آھن ۽ جيڪي پڌرو ڪندا آھن سو بيشڪ اسين ڄاڻون ٿا.'),
(3782, 36, 77, 'ماڻھوءَ نه ڏٺو آھي ڇا ته اسان کيس نُطفي مان بڻايو پوءِ تڏھن ته اُھو پڌرو جھيڙاڪ ٿيندو آھي.'),
(3783, 36, 78, '۽ اسان لاءِ ھڪ مثال بيان ڪندو آھي ۽ پنھنجي بڻاوت وساريندو آھي، ۽ چوندو آھي ته (ھنن) ھڏن کي ڪير جياريندو ۽ اُھو سڀڪنھن پيدائش کي ڄاڻندڙ آھي.'),
(3784, 36, 79, '(اي پيغمبر کين) چؤ ته جنھن اُنھن کي پھريون ڀيرو بڻايو سو اُنھن کي (وري) جياريندو ۽ اُھو سڀڪنھن پيدائش کي ڄاڻندڙ آھي.'),
(3785, 36, 80, 'جنھن سائي وڻ مان اوھان لاءِ باھ پيدا ڪئي پوءِ اوچتو اوھين اُن مان باھ ٿا ٻاريو.'),
(3786, 36, 81, 'جنھن آسمانن ۽ زمين کي بڻايو سو انھن جھڙن جي بڻائڻ تي وس وارو نه آھي ڇا؟ ھائو! (وس وارو آھي)، ۽ اُھو وڏو بڻائيندڙ (سڀ) ڄاڻندڙ آھي.'),
(3787, 36, 82, 'سندس حُڪم ھن کانسواءِ (ٻيو) ڪونه آھي ته جڏھن ڪا شيء بڻائڻ گھرندو آھي ته اُن لاءِ رڳو چوندو آھي ته ٿيءُ ته (اھا شي) ٿي پوندي آھي.'),
(3788, 36, 83, 'پوءِ اُھو (الله) پاڪ آھي جنھن جي ھٿ ۾ ھر شيءَ جي حڪومت آھي ۽ ڏانھس اوھين موٽايا ويندؤ.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, '(پنھنجي پالڻھار وٽ) قطار ڪري صفن ٻڌڻ وارن (ملائڪن) جو قسم آھي.'),
(3790, 37, 2, 'پوءِ جھڻڪ ڏيئي (شيطانن) کي ڌمڪائڻ وارن (ملائڪن) جو قسم آھي.'),
(3791, 37, 3, 'پوءِ (قرآن) پڙھڻ واري جماعت جو قسم آھي.'),
(3792, 37, 4, 'ته اوھان جو معبُود ھڪڙو (ئي) آھي.'),
(3793, 37, 5, '(جو اُھو) آسمانن ۽ زمين جو ۽ جيڪي اُنھن ٻنھي جي وچ ۾ آھي تنھن جو پالڻھار آھي ۽ (اُھو) اُڀرڻ (۽ لَھڻ) وارن طرفن جو (به) پالڻھار آھي.'),
(3794, 37, 6, 'بيشڪ اسان دُنيا جي آسمانن کي تارن جي سونھن سان سينگاريو.'),
(3795, 37, 7, '۽ سڀڪنھن سرڪش شيطان کان (انھيءَ) کي نگاھ رکيوسون.'),
(3796, 37, 8, 'ته (شيطان) مَلَاءِ الۡاَعۡلىٰ ڏانھن ڪن ڏيئي (ڳالھيون) نه ٻڌن ۽ سڀڪنھن طرف کان (اُنھن ڏانھن) اُماڙيون اُڇلبيون آھن.'),
(3797, 37, 9, '(اُنھن جي) تڙڻ لاءِ ۽ اُنھن لاءِ ھميشه جو عذاب آھي.'),
(3798, 37, 10, 'پر جنھن (چوريءَ ڪن ڏئي مَلَاءِاَعۡلىٰ جي ڳالھين مان ڪنھن ڳالھ کي) جھٽيو تنھن جي پوئتان ٻرندڙ اُماڙي پوندي آھي.'),
(3799, 37, 11, 'پوءِ کانئن پُڇ ته اُھي پيدا ڪرڻ ۾ تمام مشڪل آھن ڇا يا (ذڪر ڪيل شيون) جن کي اسان بڻايو آھي؟ بيشڪ اسان کين چيڪيءَ مٽيءَ مان بڻايو.'),
(3800, 37, 12, 'بلڪ (ڪافرن جي حال کان) عجب ڪيئي ۽ اُھي ٺـٺوليون ڪندا آھن.'),
(3801, 37, 13, '۽ جڏھن سمجھائبو اٿن (تڏھن) نه سمجھندا آھن.'),
(3802, 37, 14, '۽ جڏھن (ھو) ڪا نشاني ڏسن (تڏھن) ٺـٺوليون ڪندا آھن.'),
(3803, 37, 15, '۽ چوندا آھن ته ھيءُ (قرآن) رڳو پڌرو جادو آھي.'),
(3804, 37, 16, 'ھان جڏھن مرنداسون ۽ مٽي ۽ ھڏا ٿينداسون (تڏھن) اسين وري (جيئرا ٿي) کڙا ٿينداسون ڇا؟'),
(3805, 37, 17, 'اسان جا اڳيان پيءُ ڏاڏا به (اٿاربا) ڇا؟'),
(3806, 37, 18, 'چؤ ته ھائو (اُٿاربا) حالانڪ اوھين خوار ھوندؤ.'),
(3807, 37, 19, 'پوءِ اُھو (کڙو ٿيڻ) رڳو ھڪ سخت جھڻڪار ھوندي پوءِ اُھي اُتي جو اُتي ڏسڻ لڳندا.'),
(3808, 37, 20, '۽ چوندا ته ھيء ارمان اسان لاءِ ھي بدلي (وٺڻ) جو ڏينھن آھي.'),
(3809, 37, 21, '(چئبن ته) نبيري جو ڏينھن جنھن کي اوھين ڪوڙو ڀائيندا ھيؤ سو ھي آھي.'),
(3810, 37, 22, '(چئبو ته اي ملائڪو) ظالمن کي ۽ سندن سنگتين کي ۽ جن کي الله کانسواءِ پوڄيندا ھوا'),
(3811, 37, 23, 'تن کي گڏ ڪريو پوءِ کين دوزخ جي واٽ ڏانھن ڏس ڏيو.'),
(3812, 37, 24, '۽ کين ترسايو جو بيشڪ کانئن پڇبو.'),
(3813, 37, 25, '(ته) اوھان کي ڇا (ٿيو) آھي جو ھڪ ٻئي جي مدد نٿا ڪريو؟'),
(3814, 37, 26, 'بلڪ اُھي اڄ ڪنڌ نوائيندڙ آھن.'),
(3815, 37, 27, '۽ ڪي ڪن ڏانھن مھاڙ ڪري ھڪ ٻئي کان پڇڻ لڳندا.'),
(3816, 37, 28, '(ماڻھو پنھنجن سردارن کي) چوندا ته بيشڪ اوھين (گمراھ ڪرڻ لاءِ) سڄي (۽ کٻي) پاسي کان اسان وٽ ايندا ھُيؤ.'),
(3817, 37, 29, '(سردار) چوندا ته (نه!) بلڪ اوھين پاڻ مُسلمان نه ھُيؤ.'),
(3818, 37, 30, '۽ اوھان تي اسان جو ڪو زور نه (ھلندو) ھو، بلڪ (اوھين) پاڻ حد کان لنگھيل قوم ھُيؤ.'),
(3819, 37, 31, 'پوءِ اسان تي اسان جي پالڻھار جو چوڻ ثابت ٿيو ته بيش اسين (عذاب) چکندڙ ھُجون.'),
(3820, 37, 32, 'پوءِ اوھان کي گمراھ ڪيوسون جو بيشڪ اسين (پاڻ) به گمراھ ھواسون.'),
(3821, 37, 33, 'پوءِ بيشڪ اُھي اُنھيءَ ڏينھن عذاب ۾ ھڪ ٻئي سا گڏ شريڪ ھوندا.'),
(3822, 37, 34, 'بيشڪ اسين ڏوھارين سان اھڙي طرح ڪندا آھيون.'),
(3823, 37, 35, 'بيشڪ اُھي (اھڙا) ھوا جو جڏھن کين چئبو ھو ته الله کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي (تڏھن) وڏائي ڪندا ھوا.'),
(3824, 37, 36, '۽ چوندا ھوا ته اسان ھڪ چريي شاعر جي (چوڻ) تي پنھنجن معبودن کي ڇڏينداسون ڇا؟'),
(3825, 37, 37, 'بلڪ (پيغمبر ﷴ ﷺ) سچ (يعني قرآن) آندو ۽ (سڀني) پيغمبرن کي سچو ڄاتائين.'),
(3826, 37, 38, 'بيشڪ اوھين ڏکوئيندڙ عذاب (جو مزو) چکڻ وارا آھيو.'),
(3827, 37, 39, '۽ جيڪي ڪندا ھئو تنھن کانسواءِ (ٻيو) ڪو بدلو نه ڏبؤ.'),
(3828, 37, 40, 'پر الله جا خاص ٻانھا (عذاب کان بچندا).'),
(3829, 37, 41, 'اُنھن لاءِ روزي مُقرّر (ٿيل) آھي.'),
(3830, 37, 42, '(قسمين) ميوا، ۽ اُھي عزت وارا آھن.'),
(3831, 37, 43, 'نعمت وارن باغن ۾.'),
(3832, 37, 44, 'تختن تي ھڪ ٻئي جي آمھون سامھون ويٺل ھوندا.'),
(3833, 37, 45, 'وھندڙ شراب جو پيالو مٿن پيو ڦيرائبو.'),
(3834, 37, 46, 'جو (بلڪل) اڇو پيئندڙن لاءِ مزيدار ھوندو.'),
(3835, 37, 47, 'نڪي منجھس مٿي جي ڦيري ھوندي ۽ نڪي اُھي کانئس نشي وارا ٿيندا.'),
(3836, 37, 48, '۽ وڏين اکين واريون ھيٺ نھاريندڙيون (زالون) وٽن ھونديون.'),
(3837, 37, 49, 'ڄڻڪ اُھي (حورون) ڍَڪيل آنا آھن.'),
(3838, 37, 50, 'پوءِ ڪي (ماڻھو) ڪن ڏانھن مھاڙ ڪري ڪري ھڪ ٻئي کان پڇڻ لڳندا.'),
(3839, 37, 51, 'منجھانئن ڪو چوندڙ چوندو ته بيشڪ (دنيا ۾) منھنجو ھڪ سنگتي ھو.'),
(3840, 37, 52, 'جو چوندو ھو ته تون (قيامت جو) مڃيندڙ ٿيو آھين ڇا؟'),
(3841, 37, 53, 'ھا جڏھن مرنداسون ۽ مٽي ۽ ھڏا ٿينداسون (تڏھن) اسان کي بدلو ڏنو ويندو ڇا؟'),
(3842, 37, 54, '(وري اُھو) چوندو ته اوھين (به دوزخين کي) ڏسو ٿا ڇا؟'),
(3843, 37, 55, 'پوءِ (پاڻ) جھاتي پائيندو ته اُھو (سنگتي) دوزخ جي وچ ۾ ڏسندو.'),
(3844, 37, 56, 'چوندو ته الله جو قسم ته تو مونکي ذري گھٽ ھلاڪ ٿي ڪيو.'),
(3845, 37, 57, '۽ جيڪڏھن منھنجي پالڻھار (جو مون تي) فضل نه ٿئي ھا ته بيشڪ آءٌ (به عذاب ۾) حاضر ڪيلن مان ھجان ھا.'),
(3846, 37, 58, 'بيشڪ اسين نڪي (ھاڻي وري) مرڻ وارا آھيون.'),
(3847, 37, 59, '(اھو) رڳو اسان جو پھريون مرڻ ھو (جو ٿي چڪو) ۽ نڪي عذاب ڪيل آھيون.'),
(3848, 37, 60, 'بيشڪ اِھا وڏي مراد ماڻڻ آھي.'),
(3849, 37, 61, 'اھڙي (نعمت) لاءِ ڀلي ته عمل ڪندڙ عمل ڪن.'),
(3850, 37, 62, 'ڀلا اِھا (بيان ڪيل نعمت) مھمانيءَ طور ڀلي آھي يا ٿوھر جو وڻ؟'),
(3851, 37, 63, 'بيشڪ اسان اِن (وڻ) کي ظالمن جي لاءِ عذاب ٺھرايو آھي.'),
(3852, 37, 64, 'بيشڪ اِھو ھڪ وڻ آھي جو دوزخ جي تري ۾ پيدا ٿيندو آھي.'),
(3853, 37, 65, 'اُن جا گوشا ڄڻڪ شيطانن جون سِسيون آھن.'),
(3854, 37, 66, 'پوءِ بيشڪ دوزخي اُن مان کائيندا پوءِ منجھانئس پيٽ ڀريندا.'),
(3855, 37, 67, 'وري اُنھن لاءِ اُن (کاڄ) تي ٽھڪندڙ پاڻيءَ جي ملاوت ھوندي.'),
(3856, 37, 68, 'وري بيشڪ کائڻ پيئڻ کانپوءِ سندن موٽڻ دوزخ ڏانھن ھوندو.'),
(3857, 37, 69, 'بيشڪ اُنھن پنھنجن پيءُ ڏاڏن کي گمراھ ڏٺو.'),
(3858, 37, 70, 'تنھنڪري اُھي (به) سندن پيرن تي ڊوڙن ٿا.'),
(3859, 37, 71, '۽ بيشڪ کانئن اڳ اڳين مان گھڻا گمراھ ٿيا ھوا.'),
(3860, 37, 72, '۽ بيشڪ منجھن ڊيڄاريندڙ موڪليا ھواسون.'),
(3861, 37, 73, 'پوءِ نھار ته ڊيڄاريلن جي پڇاڙي ڪئن ٿي؟'),
(3862, 37, 74, 'پر الله جا خاص ٻانھا (انھن جي پڇاڙي چڱي ٿي).'),
(3863, 37, 75, '۽ بيشڪ نوح اسان کي سڏيو پوءِ چڱا (دُعا کي) قبول ڪندڙ آھيون.'),
(3864, 37, 76, '۽ کيس ۽ سندس (تعلق وارن) ماڻھن کي وڏي مصيبت کان بچايوسون.'),
(3865, 37, 77, '۽ (فقط) سندس اولاد کي ئي باقي رھندڙ ڪيوسون.'),
(3866, 37, 78, '۽ پوين ۾ اُن لاءِ (چڱي يادگيري) ڇڏي سون.'),
(3867, 37, 79, 'ساري جَڳ ۾ نوح تي شال سلام ھجي.'),
(3868, 37, 80, 'بيشڪ اسين ڀلارن کي اھڙي طرح بدلو ڏيندا آھيون.'),
(3869, 37, 81, 'بيشڪ اُھو اسان جي مؤمنن ٻانھن مان آھي.'),
(3870, 37, 82, 'وري ٻـين کي ٻوڙيوسون.'),
(3871, 37, 83, '۽ بيشڪ نوح جي پيروري ڪندڙن مان ابراھيم ھو.'),
(3872, 37, 84, '(ياد ڪر) جڏھن (ھو) پنھنجي پالڻھار وٽ سالم دِل سان آيو.'),
(3873, 37, 85, '(ياد ڪر) جڏھن پنھنجي پيءُ ۽ پنھنجي قوم کي چيائين ته اوھان ڇاکي پوڄيندا آھيو؟'),
(3874, 37, 86, 'الله کانسواءِ ھٿرادو ٺھيل معبودن کي گھرو ٿا ڇا؟'),
(3875, 37, 87, 'پوءِ جھانن جي پالڻھار بابت اوھان جو ڪھڙو گُمان آھي؟'),
(3876, 37, 88, 'پوءِ تارن ۾ ھڪ ڀيرو چتائي ڏٺائين.'),
(3877, 37, 89, 'پوءِ چيائين ته آءٌ بيمار ٿيندس.'),
(3878, 37, 90, 'پوءِ کانئس پُٺي ڏيئي ڦِريا.'),
(3879, 37, 91, 'پوءِ سندن معبودن ڏانھن منھن ڪيائين ته اوھين ڇونه ٿا کائو؟'),
(3880, 37, 92, 'اوھان کي ڇا (ٿيو آھي) جو نه ٿا ڳالھايو؟'),
(3881, 37, 93, 'پوءِ سڄي ھٿ (يعني زور) سان مٿن ڏاڍا ڌَڪ ھڻڻ لڳو.'),
(3882, 37, 94, 'پوءِ (قوم وارا) ڏانھس ڊوڙندا سامھون آيا.'),
(3883, 37, 95, '(کين) چيائين ته جنھن کي اوھين (پاڻ) گھَڙيندا آھيو تنھن کي ڇو پوڄيندا آھيو؟'),
(3884, 37, 96, 'ھن ھوندي جو الله اوھان کي ۽ جيڪي اوھين ڪريو ٿا تنھن کي پيدا ڪيو.'),
(3885, 37, 97, '(ھڪ ٻئي کي) چيائون ته ابراھيم لاءِ اڏاوت اڏيو پوءِ کيس (باھ جي) آڙاھ ۾ اُڇليو.'),
(3886, 37, 98, 'پوءِ ساڻس فريب ڪرڻ جو ارادو ڪيائون پوءِ کين تمام زير ڪيوسون.'),
(3887, 37, 99, '۽ ابراھيم چيو ته بيشڪ آءٌ پنھنجي پالڻھار ڏانھن وڃڻ وارو آھيان (اھو) سگھوئي مون کي واٽ ڏيکاريندو (جتي سندس رضا ھوندي).'),
(3888, 37, 100, 'اي منھنجا پالڻھار صالحن مان (ڪو پُٽ) مون کي عطا ڪر.'),
(3889, 37, 101, 'پوءِ کيس ھڪ حليمت واري نينگر جي خوشخبري ڏني سون.'),
(3890, 37, 102, 'پوءِ جڏھن ساڻس گڏ ڊوڙڻ (واريءَ عمر) کي پھتو (تڏھن ابراھيم) چيو ته اي منھنجا پُٽڙا بيشڪ آءٌ ننڊ ۾ ڏسان ٿو ته آءٌ توکي ڪُھان ٿو پوءِ تون نظر ڪر ته تنھنجي دل ۾ ڇا ٿو اچي؟ چيائين ته اي مُنھنجا ابا جنھن ڪم جو توکي حُڪم ڏنو وڃي ٿو سو ڪر جيڪڏھن الله گھريو ته مون کي صبر ڪرڻ وارن مان لھندين.'),
(3891, 37, 103, 'پوءِ جڏھن ٻنھي حُڪم مڃيو ۽ ابراھيم (پنھنجي) پُٽ کي پيشانيءَ ڀر ليٽايو.'),
(3892, 37, 104, '۽ کيس سڏيوسون ته اي ابراھيم.'),
(3893, 37, 105, 'بيشڪ تو خواب کي سچو ڪيو، بيشڪ اسين ڀلارن کي اھڙي طرح بدلو ڏيندا آھيون.'),
(3894, 37, 106, 'بيشڪ اِھا پڌري پرک آھي.'),
(3895, 37, 107, '۽ ذُبح جي وڏي جانور سان اُن جو عوض ڏنوسون.'),
(3896, 37, 108, '۽ پوين ۾ (چڱي ساراھ) اُن لاءِ ڇڏي سون.'),
(3897, 37, 109, 'ابراھيم تي شال سلام ھجي.'),
(3898, 37, 110, 'ڀلارن کي اھڙي طرح بدلو ڏيندا آھيون.'),
(3899, 37, 111, 'بيشڪ اُھو اسان جي مؤمنن ٻانھن مان آھي.'),
(3900, 37, 112, '۽ کيس اسحٰق جي خوشخبري ڏني سون جو صالحن مان ھڪڙو نبي آھي.'),
(3901, 37, 113, '۽ ابراھيم تي ۽ اسحٰق تي برڪت نازل ڪئي سون، ۽ ٻنھي جي اولاد مان ڪي چڱا آھن ۽ ڪي پاڻ تي پڌرو ظلم ڪندڙ آھن.'),
(3902, 37, 114, '۽ بيشڪ موسىٰ ۽ ھارون تي احسان ڪيوسون.'),
(3903, 37, 115, '۽ کين ۽ سندن قوم کي وڏي سختيءَ کان بچايوسون.'),
(3904, 37, 116, '۽ کين مدد ڏني سون پوءِ اُھي غالب رھيا.'),
(3905, 37, 117, '۽ کين پڌرو ڪتاب ڏنوسون.'),
(3906, 37, 118, '۽ کين سِڌي رستي لاتوسون.'),
(3907, 37, 119, '۽ پوين ۾ (چڱي ساراھ) انھن لاءِ ڇڏي سون.'),
(3908, 37, 120, 'مُوسىٰ ۽ ھارون تي شال سلام ھجي.'),
(3909, 37, 121, 'بيشڪ اسين ڀلارن کي اھڙيءَ طرح بدلو ڏيندا آھيون.'),
(3910, 37, 122, 'بيشڪ اُھي (ٻئي) اسان جي مؤمن ٻانھن مان آھن.'),
(3911, 37, 123, '۽ بيشڪ الياس پيغمبرن مان ھو.'),
(3912, 37, 124, 'جڏھن پنھنجي قوم کي چيائين ته (الله کان) ڇونه ڊڄندا آھيو؟'),
(3913, 37, 125, 'ڇو بَعل کي پوڄيندا آھيو ۽ تمام چڱي خلقيندڙ کي ڇڏيندا آھيو؟'),
(3914, 37, 126, '(جو) الله اوھان جو پالڻھار ۽ اوھان جي اڳين پِين ڏاڏن جو پالڻھار آھي.'),
(3915, 37, 127, 'پوءِ کيس ڪوڙو ڀانيائون تنھنڪري اُھي بيشڪ (دوزخ ۾) حاضر ڪيل آھن.'),
(3916, 37, 128, 'پر الله جا خاص ٻانھا (ڇٽل آھن).'),
(3917, 37, 129, '۽ پوين ۾ (چڱي ساراھ) اُن لاءِ ڇڏي سون.'),
(3918, 37, 130, 'الياس تي شال سلام ھجي.'),
(3919, 37, 131, 'بيشڪ اسين ڀلارن کي اھڙي طرح بدلو ڏيندا آھيون.'),
(3920, 37, 132, 'بيشڪ اُھو اسان جي مؤمن ٻانھن مان آھي.'),
(3921, 37, 133, '۽ بيشڪ لُوط پيغمبرن مان ھو.'),
(3922, 37, 134, '(ياد ڪر) جڏھن اُن کي ۽ سندس مڙني گھر وارن کي بچايوسون.'),
(3923, 37, 135, 'سواءِ ھڪڙي ڪراڙي زال جي جا پُٺ تي رھندڙن مان ٿي.'),
(3924, 37, 136, 'وري (ھنن) ٻـين کي ھلاڪ ڪيوسون.'),
(3925, 37, 137, '۽ بيشڪ اوھين انھن (جي ڳوٺن) وٽان صبح جو لنگھندا آھيو.'),
(3926, 37, 138, '۽ رات جو (به) پوءِ ڇونه سمجھندا آھيو؟'),
(3927, 37, 139, '۽ بيشڪ يُونس پيغمبرن مان ھو.'),
(3928, 37, 140, 'جڏھن (ھو) ڀريل ٻيڙيءَ ڏانھن ڀڳو.'),
(3929, 37, 141, 'پوءِ (ٻيڙي وارن سان) پُکو وڌائين ته (پاڻ) پُکي پيلن مان ٿيو.'),
(3930, 37, 142, 'پوءِ کيس مڇي ڳھي وئي ۽ اُھو (اھڙو ڪم ڪندڙ ھو جو) ملامت جو لائق ھو.'),
(3931, 37, 143, 'پوءِ اُھو جيڪڏھن تسبيح چوندڙن مان نه ھجي ھا.'),
(3932, 37, 144, 'ته اُنھي ڏينھن تائين جنھن ۾ (ماڻھو) اُٿارجن اُن جي پيٽ ۾ رھي ھا.'),
(3933, 37, 145, 'پوءِ کيس صاف پَٽ ۾ اُڇليوسون ۽ اُھو بيمار ھو.'),
(3934, 37, 146, '۽ مٿس وڻ ڪدوءَ (جي قِسم) مان ڄمايوسون.'),
(3935, 37, 147, '۽ کيس (ھڪ) لک يا (اُن کان) وڌيڪ (ماڻھن) ڏانھن موڪليوسون.'),
(3936, 37, 148, 'پوءِ انھن ايمان آندو پوءِ ھڪ وقت تائين کين فائدو ڏنوسون.'),
(3937, 37, 149, 'پوءِ (اي پيغمبر) اُنھن (يعني مشرڪن) کان پُڇ ته تنھنجي پالڻھار کي ڌيئر آھن ڇا يا کيس پُٽ آھنئ'),
(3938, 37, 150, 'يا ملائڪن کي زالون بڻايوسون ۽ اُھي (اُن وقت) حاضر ھوا.'),
(3939, 37, 151, 'خبردار ھُجو (ته) بيشڪ اُھي پنھنجي (ھٿرادو ٺھيل) ڪوڙ مان چوندا آھن.'),
(3940, 37, 152, 'ته الله (اولاد کي) ڄڻيو آھي ۽ اُھي ڪوڙا آھن.'),
(3941, 37, 153, 'پٽن کان ڌيئر پسند ڪيائين ڇا؟'),
(3942, 37, 154, 'اوھان کي ڇا ٿيو آھي، ڪيئن نبيرو ڪندا آھيو؟'),
(3943, 37, 155, '(اوھين) ڇونه ڌيان ڪندا آھيو؟'),
(3944, 37, 156, 'يا اوھان کي ڪا پڌري حُجّت آھي؟'),
(3945, 37, 157, 'جيڪڏھن اوھين سچا آھيو ته پنھنجو ڪتاب آڻيو.'),
(3946, 37, 158, '۽ (ڪافرن) الله ۽ جِنن جي وچ ۾ مائٽي مُقرر ڪئي، ۽ بيشڪ جِنن ڄاتو آھي ته اُھي (قيامت ۾ حساب لاءِ) حاضر ڪيا ويندا.'),
(3947, 37, 159, '(ڪافر) جيڪي بيان ڪندا آھن تنھن کان الله پاڪ آھي.'),
(3948, 37, 160, 'پر الله جا خاص ٻانھا (انھن جو چوڻ سچو آھي).'),
(3949, 37, 161, 'پوءِ (اي ڪافرؤ) بيشڪ اوھين ۽ جن (بتن) کي پوڄيندا آھيو.'),
(3950, 37, 162, 'اوھين (سڀئي) اُن (يعني ڪوڙن معبُودن جي عبادت) لاءِ گمراھ ڪرڻ وارا نه آھيو.'),
(3951, 37, 163, 'پر اُنھن کي جيڪو (پاڻ) دوزخ ۾ گھڙڻ وارو آھي.'),
(3952, 37, 164, '۽ (ملائڪ چوندا آھن ته) اسان مان اھڙو آھي ئي ڪونه جنھن لاءِ (عبادت جي) ھڪ جاءِ ٺھرايل نه آھي.'),
(3953, 37, 165, '۽ بيشڪ اسين صِف ٻَڌڻ وارا آھيون.'),
(3954, 37, 166, '۽ بيشڪ اسين تسبيح چوڻ وارا آھيون.'),
(3955, 37, 167, '۽ بيشڪ (عرب جا ڪافر) چوندا ھوا.'),
(3956, 37, 168, 'ته جيڪڏھن اسان وٽ پھرين جو احوال ھجي ھا.'),
(3957, 37, 169, 'ته ضرور الله جا خاص ٻانھا ٿيون ھا.'),
(3958, 37, 170, 'وري اُن (يعني قرآن) جا مُنڪر ٿيا پوءِ سِگھوئي (ڪفر جي پڄاڻي) ڄاڻندا.'),
(3959, 37, 171, '۽ بيشڪ پنھنجن ٻانھن پيغمبرن لاءِ اسان جو وعدو اڳي ئي ٿي چڪو..'),
(3960, 37, 172, 'ته بيشڪ اُھي (اسان جا پيغمبر) ئي فتح مند آھن.'),
(3961, 37, 173, '۽ بيشڪ اسان جو لشڪر ئي غالب آھي.'),
(3962, 37, 174, 'پوءِ کانئن ھڪ وقت تائين منھن موڙ.'),
(3963, 37, 175, '۽ کين ڏسندو رھ پوءِ اُھي به سگھوئي ڏسندا.'),
(3964, 37, 176, '(ھي ڪافر) اسان جو عذاب جلد گھرندا آھن ڇا؟'),
(3965, 37, 177, 'پوءِ جڏھن سندن (گھرن جي) اڱڻن ۾ (عذاب) لھندو تڏھن ڊيڄاريلن جو صُبح بڇڙو ٿيندو.'),
(3966, 37, 178, '۽ کانئن ھڪ وقت تائين مُنھن موڙ.'),
(3967, 37, 179, '۽ ڏسندو رھ پوءِ اُھي به سگھوئي (نتيجو) ڏسندا.'),
(3968, 37, 180, 'تنھنجو پالڻھار پاڪ آھي جيڪي (اِھي ڪافر) بيان ڪندا آھن تنھن کان (اُھو) عزّت وارو آھي.'),
(3969, 37, 181, '۽ (الله جي) پيغمبرن تي شال سَلام ھجي.'),
(3970, 37, 182, '۽ جھانن جي پالڻھار الله کي ئي سڀ ساراھ جڳائيندي آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'صٓ (ھن) قرآن نصيحت (ڏيڻ) واري جو قسم آھي (ته جنھن دين ڏانھن سڏين ٿو سو سچ آھي).'),
(3972, 38, 2, 'بلڪ ڪافر وڏائي ۽ مخالفت ۾ (پيل) آھن.'),
(3973, 38, 3, 'کانئن اڳ گھڻائي جُڳ ھلاڪ ڪياسون پوءِ دانھون ڪرڻ لڳا ۽ اُھو ڇوٽڪاري جو وقت نه ھو.'),
(3974, 38, 4, '۽ منجھانئن ھڪ ڊيڄاريندڙ وٽن آيو ته عجب ڪرڻ لڳا، ۽ (اِھي) ڪافر چوڻ لڳا ته ھيءُ (شخص) جادوگر ڪوڙو آھي.'),
(3975, 38, 5, 'ڀلا (ڏسو ته) سڀني معبودن کي ھڪ معبود ڪيائين؟ بيشڪ ھي ڏاڍي عجيب ڳالھ آھي.'),
(3976, 38, 6, '۽ منجھانئن (سردارن جي) ٽولي (ھڪ ٻئي کي چوندي) ھلي وئي ته ھلو ۽ پنھنجن معبودن (جي پوڄا) تي مُحڪم رھو، بيشڪ ھيءُ (دين) اھڙو آھي جو (اُن ۾ ڪو) غرض رکيو ويو آھي.'),
(3977, 38, 7, 'اِھا (ڳالھ) پوئين دين ۾ (ڪڏھن) نه ٻڌي سون، ھيءُ ته رڳو ٺاھ آھي.'),
(3978, 38, 8, 'اسان مڙني جي وچان مٿس قرآن نازل ڪيو ويو ڇا؟ بلڪ اِھي (ڪافر) منھنجي نصيحت کان شڪ ۾ (پيل) آھن، (نه!) بلڪ اڃا منھنجو عذاب نه چکيو اٿن.'),
(3979, 38, 9, 'تنھنجي پالڻھار غالب بخشڻھار جي ٻاجھ جا (ڪي) خزانا وٽن آھن ڇا؟'),
(3980, 38, 10, 'آسمانن ۽ زمين جي ۽ جيڪي اِنھن (ٻنھي) جي وچ ۾ آھي تنھنجي بادشاھي انھن جي آھي ڇا؟ (جي ايئن آھي) ته جڳائي ته رسين سان (لڙڪي) مٿي چڙھن.'),
(3981, 38, 11, 'شڪست کاڌل ٽولين مان ھيءُ (ڪافر) ھتي ھڪ (گڏ ٿيل) لشڪر آھي.'),
(3982, 38, 12, 'انھن کان اڳ نوح جي قوم ۽ عاد ۽ فرعون ميخن وارو ڪوڙ ڄاتو.'),
(3983, 38, 13, '۽ ثمود ۽ لُوط جي قوم ۽ (شھر) ايڪه وارن، انھن جماعتن به (ڪوڙ ڀانيو).'),
(3984, 38, 14, 'اِھي ٽوليون (جيڪي به ھيون تن) سڀني پيغمبرن کي ڪوڙو ڀانيو تنھنڪري منھنجي سزا (مٿن) لازم ٿي.'),
(3985, 38, 15, '۽ ھيءُ (ھن وقت جا ڪافر به) رڳو ھڪ آواز جا منتظر رھندا آھن جنھن کي ڪا ساھي نه ھوندي.'),
(3986, 38, 16, '۽ (ٺـٺولي طرح) چوندا آھن ته اي اسان جا پالڻھار حساب جي ڏينھن کان اڳ اسان (جي عذاب) جي چٺي اسان کي جلد ڏي.'),
(3987, 38, 17, '(اي پيغمبر) جيڪي چوندا آھن تنھن تي صبر ڪر ۽ اسان جي سگھ واري ٻانھي داؤد کي ياد ڪر، بيشڪ اُھو رجوع ڪرڻ وارو ھو.'),
(3988, 38, 18, 'بيشڪ اسان جبلن کي سندس تابع ڪيو (جو) سانجھي ۽ صبح جو (ساڻس) تسبيحون پڙھندا ھوا.'),
(3989, 38, 19, '۽ گڏ ڪيل پکين کي به (سندس تابع ڪيوسين)، ھر ھڪ (تسبيح گڏ چوڻ ۾) سندس فرمانبردار ھو.'),
(3990, 38, 20, '۽ سندس بادشاھيءَ کي مُحڪم ڪيوسين ۽ کيس دانائي ۽ نبير نڇيڙ ڏني سون.'),
(3991, 38, 21, '۽ (پاڻ ۾ ٻن) وڙھندڙ جي خبر تو وٽ نه پھتي آھي ڇا؟ جڏھن اُھي ڀِٽ ٽپي عبادت خانه ۾ آيا.'),
(3992, 38, 22, 'جڏھن داؤد وٽ اندر پھتا تڏھن (ھو) کائن ڊڄي ويو چيائون ته نه ڊڄ، (اسين) ٻه جھڳڙو ڪندڙ آھيون اسان مون ھڪڙي ٻئي تي ڏاڍ ڪيو آھي تنھنڪري اسان جي وچ ۾ انصاف سان نبيرو ڪر ۽ (فيصلي ۾) بي انصافي نه ڪر ۽ اسان کي سڌيءَ واٽ ڏانھن رستي لاءِ.'),
(3993, 38, 23, 'بيشڪ ھيءُ منھنجو ڀاءُ آھي، جنھن کي نوانوي رڍون آھن ۽ مون کي ھڪ رڍ آھي، پوءِ ھن چيو ته اِھا (ھڪ رڍ) به منھنجي حوالي ڪر ۽ مون کي ڳالھائڻ ۾ سخت ورتائين.'),
(3994, 38, 24, '(داؤد) چيو ته (ھن) تنھنجي (ھڪ) رڍ جي گھرڻ ڪري ته پنھنجن رڍن سان گڏي توتي بيشڪ ظلم ڪيو، ۽ بيشڪ ڀائيوارن مان گھڻا ھڪ ٻئي تي ظلم ڪندا آھن سواءِ اُنھن جي جن ايمان آندو ۽ چڱا ڪم ڪيا ۽ اُھي (ظلم نه ڪندڙ) ٿورا آھن، ۽ داؤد ڄاتو ته کيس پرکيو اٿون تنھنڪري پنھنجي پالڻھار کان بخشش گھريائين ۽ سجدو ڪندڙ ٿي ڪري پيو ۽ (الله ڏانھن) رجوع ڪيائين.'),
(3995, 38, 25, 'پوءِ کيس اِھو (سندس گناھ) بخشيوسون، ۽ بيشڪ اُن کي اسان وٽ مرتبو آھي ۽ چڱو موٽڻ جو ھنڌ آھي.'),
(3996, 38, 26, '(چيوسون ته) اي داؤد بيشڪ اسان توکي زمين ۾ نائب مُقرّر ڪيو تنھنڪري ماڻھن جي وچ ۾ انصاف سان فيصلو ڪر ۽ (نفس جي) سَڌ جي تابعداري نه ڪر جو (اھا) توکي الله جي واٽ کان ڀلائيندي، بيشڪ جيڪي الله جي واٽ کان ڀُلن ٿا تن لاءِ انھيءَ سببان سخت عذاب آھي جو حساب جو ڏينھن وساريائون.'),
(3997, 38, 27, '۽ آسمان ۽ زمين کي ۽ جيڪي انھن جي وچ ۾ آھي سو اجايو نه بڻايوسون، اھو ڪافرن جو گمان آھي، تنھنڪري ڪافرن لاءِ باھ (جي عذاب) جي خرابي آھي.'),
(3998, 38, 28, 'جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي زمين ۾ فساد وجھڻ وارن جي برابر ڪنداسون ڇا؟ يا پرھيزگارن کي بڇڙن وانگر ڪنداسون ڇا؟'),
(3999, 38, 29, 'ھي (قرآن) برڪت وارو ڪتاب آھي جو اُن کي توڏانھن نازل ڪيوسون ته (ماڻھو) اُن جي آيتن ۾ ڌيان ڪن ۽ عقل وارا نصيحت وٺن.'),
(4000, 38, 30, '۽ داؤد کي سُليمان (نالي پُٽ) عطا ڪيوسون، (سُليمان) چڱو ٻانھون ھو، بيشڪ اُھو (خُدا ڏانھن) ورڻ وارو ھو.'),
(4001, 38, 31, '(ياد ڪر) جڏھن پوياڙيءَ جو تکا گھوڙا وٽس پيش ڪيا ويا.'),
(4002, 38, 32, 'پوءِ چيائين ته بيشڪ مون پنھنجي پالڻھار جي ياد ڪرڻ کان (ڀُلي ھنن گھوڙن جي) مال جي پيار کي (وڌيڪ) دوست رکيو، تان جو (سج) اوٽ جي پٺيان لڪو (۽ وچين نماز قضا ٿي).'),
(4003, 38, 33, '(چيائين ته ھنن) گھوڙن کي مون وٽ موٽائي آڻيو، پوءِ (گھوڙن جي) ڄنگھن ۽ ڪنڌن تي (ذبح جو) ھٿ لائڻ شروع ڪيائين.'),
(4004, 38, 34, '۽ بيشڪ سليمان کي پرکيوسون ۽ سندس تخت تي ھڪ ڌڙ اُڇليوسون وري (خُدا ڏانھن) موٽيو.'),
(4005, 38, 35, 'چيائين ته اي منھنجا پالڻھار مون کي بخش ۽ مون کي اھڙي بادشاھي عطا ڪر جو مون کان پوءِ ڪنھن کي به نه جڳائي، بيشڪ تون ئي بخشڻھار آھين.'),
(4006, 38, 36, 'پوءِ واءُ کي سندس تابع ڪيوسون جتي پھچڻ گھرندو ھو (تتي) آرام سان سندس حُڪم سان ھلندي ھئي.'),
(4007, 38, 37, '۽ ديون کي (سندس تابع ڪيوسون) جو ھر ھڪ عمارتون اڏيندڙ ۽ (درياءُ ٽٻندڙ ھو).'),
(4008, 38, 38, '۽ ٻيا (ديوَ) زنجيرن ۾ جڪڙيل (تابع ڪياسون).'),
(4009, 38, 39, '(چيوسون ته) ھيءُ اسان جو اِحسان آھي پوءِ احسان ڪر يار کي ڇڏ (جئن توکي وڻي) ته (توتي) حساب نه آھي.'),
(4010, 38, 40, '۽ بيشڪ اُن کي اسان وٽ مرتبو ۽ چڱو موٽڻ جو ھنڌ آھي.'),
(4011, 38, 41, '۽ اسان جي ٻانھي ايّوب کي ياد ڪر، جڏھن پنھنجي پالڻھار کي ٻاڏايائين ته مون کي شيطان ايذاءُ ۽ ڏک پھچايو آھي.'),
(4012, 38, 42, '(تڏھن چيوسون ته) زمين تي پنھنجي لت ھڻ، جو ھي (چشمو) وھنجڻ جو ھنڌ ٿڌو ۽ پيئڻ لاءِ (مٺو) آھي.'),
(4013, 38, 43, '۽ کيس سندس گھر وارا ۽ ساڻن اوترا (ٻيا به) عطا ڪيوسون (ھيء) اسان جي طرف کان ٻاجھ ۽ ڌيان وارن لاءِ نصيحت آھي.'),
(4014, 38, 44, '۽ (چيوسون ته) پنھنجي ھٿ ۾ ٻھار وٺ پوءِ اُن سان (پنھنجي زال کي) ڌڪ ھڻ ۽ قسم نه ڀڃ، بيشڪ اسان کيس صابر لڌو، (ايّوب) چڱو ٻانھو ھو، اُھو (خُدا ڏانھن) ورڻ وارو ھو.'),
(4015, 38, 45, '۽ اسان جي ٻانھن ابراھيم ۽ اسحاق ۽ يعقوب کي ياد ڪر جي ھٿن ۽ اکين وارا (يعني علم ۽ عمل وارا) ھوا.'),
(4016, 38, 46, 'بيشڪ اسان کين ھڪ پاڪ عادت لاءِ نرالو ڪيو جو آخرت جو ياد ڪرڻ آھي.'),
(4017, 38, 47, '۽ بيشڪ اُھي اسان وٽ سڳورن نيڪن مان ھوا.'),
(4018, 38, 48, '۽ اسماعيل ۽ اَلۡيَسَعَ ۽ ذُوالۡڪِفۡل کي ياد ڪر، ۽ ھر ھڪ نيڪن مان ھوا.'),
(4019, 38, 49, 'ھيءُ (قرآن) نصيحت آھي، ۽ بيشڪ پرھيزگارن لاءِ چڱو موٽڻ جو ھنڌ آھي.'),
(4020, 38, 50, '(يعني) ھميشه (رھڻ) جا باغ آھن (جن جا) دروازا انھن لاءِ کُليل آھن.'),
(4021, 38, 51, 'اُتي ٽيڪ ڏيندڙ ھوندا اُتي گھڻا ميوا ۽ شراب پيا گھرائيندا.'),
(4022, 38, 52, '۽ وٽن ھيٺ نگاھ ڪرڻ واريون ۽ عمر ۾ ھڪ جيڏيون (زالون) ھونديون.'),
(4023, 38, 53, 'ھي اُھو آھي جنھنجو حساب جي ڏينھن لاءِ انجام ڏنو وڃيوَ ٿو.'),
(4024, 38, 54, 'بيشڪ اُھو اسان جو رزق آھي جنھن کي ڪا کوٽ نه ھوندي.'),
(4025, 38, 55, 'اھو (بدلو ڀلارن لاءِ آھي)، ۽ بيشڪ حد کان لنگھندڙن لاءِ بڇڙو موٽڻ جو ھنڌ آھي.'),
(4026, 38, 56, '(جو) دوزخ آھي، اُن ۾ گھڙندا، پوءِ اُھو بڇڙو وڇاڻو آھي.'),
(4027, 38, 57, 'ھي (عذاب) ڪوسو پاڻي آھي ۽ پُونءِ آھي پوءِ ڀلي ته اُن کي چَکن.'),
(4028, 38, 58, '۽ اھڙو قسمين قسمين ٻيو (عذاب) به آھي.'),
(4029, 38, 59, 'ھي ھڪ فوج آھي جا اوھان سان گڏ گھرڻ واري آھي، شال کين ڪا ويڪري جاءِ نه ملي، ڇوته اُھي باھ ۾ گھڙڻ وارا آھن.'),
(4030, 38, 60, '(تابع) چوندا ته (نه!) بلڪ اوھان کي شال ڪا ويڪري جاءِ نه ھجي، (جو) اوھان اسان لاءِ ڪُفر کي پراڻي رسم ڪري ڇڏيو، پوءِ دوزخ بڇڙي جاءِ آھي.'),
(4031, 38, 61, 'چوندا ته اي اسان جا پالڻھار جنھن اسان لاءِ ھيءَ (ڪُفر جي) پراڻي رسم ٺاھي ھجي تنھنجي حق ۾ دوزخ ۾ ٻيڻو عذاب زيادہ ڪر.'),
(4032, 38, 62, '۽ دوزخي چوندا ته اسان کي ڇا (ٿيو) آھي جو مڙسن (مسلمانن مسڪينن) کي نٿا ڏسون جن کي بڇڙن مان ڳڻيندا ھواسون؟'),
(4033, 38, 63, '(ناحق) ساڻن ٺـٺوليون ڪندا ھواسون ڇا يا کانئن اسان جون اکيون ترڪيون آھن.'),
(4034, 38, 64, 'بيشڪ دوزخين جو پاڻ ۾ جھيڙو ڪرڻ اِھا (ھڪ) سچي (ڳالھ) آھي.'),
(4035, 38, 65, '(کين) چؤ ته آءٌ رڳو ڊيڄاريندڙ آھيان، ۽ (رڳو) اڪيلي زبردست الله کانسواءِ (ٻيو) ڪوبه عبادت جو لائق نه آھي.'),
(4036, 38, 66, 'آسمانن ۽ زمين جو ۽ جيڪي انھن (ٻنھي) جي وچ ۾ آھي تنھنجو پالڻھار غالب بخشڻھار آھي.'),
(4037, 38, 67, '(کين) چؤ ته اِھو (قرآن) ھڪ وڏي خبر آھي.'),
(4038, 38, 68, 'اوھين کانئس مُنھن موڙيندا آھيو.'),
(4039, 38, 69, 'مون کي (اُنھيءَ جماعت) وڏي مرتبي وارن ملائڪن (جي حال) جي ڪا خبر نه آھي جڏھن (اُھي) پاڻ ۾ سوال جواب ڪندا آھن.'),
(4040, 38, 70, 'مون ڏانھن ھيء ئي وحي ٿيندو آھي ته آءٌ رڳو پڌرو ڊيڄاريندڙ آھيان.'),
(4041, 38, 71, '(ياد ڪر) جڏھن تنھنجي پالڻھار ملائڪن کي چيو ته بيشڪ آءٌ مٽيءَ مان ھڪ ماڻھو بڻائڻ وارو آھيان.'),
(4042, 38, 72, 'پوءِ جڏھن کيس پورو ٺاھي رھان ۽ منجھس پنھنجو رُوح ڦوڪيان تڏھن کيس سجدو ڪندڙ ٿي ڪري پئجو.'),
(4043, 38, 73, 'پوءِ ملائڪن مڙني گڏ سجدو ڪيو.'),
(4044, 38, 74, 'مگر ابليس (نه سجدو ڪيو)، ھن وڏائي ڪئي ۽ ڪافرن مان ٿيو.'),
(4045, 38, 75, '(الله) چيو ته اي ابليس جنھن کي پنھنجي ٻنھي ھٿن سان پيدا ڪيم تنھن کي سجدي ڪرڻ کان توکي ڪھڙيءَ ڳالھ جھليو، تو وڏائي ڪئي ڇا يا تون (حقيقت) ۾ وڏي مرتبي وارن مان آھين؟'),
(4046, 38, 76, '(ابليس) چيو ته آءٌ اُن کان ڀلو آھيان، (جو) مون کي باھ مان بڻايئي ۽ اُن کي مٽيءَ مان بڻايئي.'),
(4047, 38, 77, '(الله) چيو ته انھيءَ ڪري اُن مان نڪر جو بيشڪ تون تڙيل آھين.'),
(4048, 38, 78, '۽ بيشڪ توتي بدلي جي ڏينھن تائين منھنجي لعنت آھي.'),
(4049, 38, 79, '(اُن) چيو ته اي منھنجا پالڻھار تڏھن اُنھي ڏينھن تائين مونکي مُھلت ڏي (جنھن ڏينھن سڀ ماڻھو قبر مان) اُٿاريا وڃن.'),
(4050, 38, 80, 'الله فرمايو ته (چڱو) بيشڪ تون مُھلت ڏنلن مان آھين.'),
(4051, 38, 81, 'اُنھيءَ ٺھرايل وقت جي ڏينھن تائين.'),
(4052, 38, 82, '(ابليس) چيو ته تڏھن تنھنجي عزت جو قسم آھي ته ضرور اُنھن مڙني کي گمراھ ڪندس.'),
(4053, 38, 83, 'منجھانئن تنھنجي خالص ٻانھن کانسواءِ (جو انھن تي وس نه ھلندو).'),
(4054, 38, 84, 'الله فرمايو ته (اِھا ڳالھ) سچ آھي ۽ (آءٌ) سچ چوان ٿو.'),
(4055, 38, 85, 'ته توسان ۽ منجھانئن جيڪي تنھنجي پيروي ڪندا تن مڙني سان دوزخ کي ضرور ڀريندس.'),
(4056, 38, 86, '(اي پيغمبر کين) چؤ ته آءٌ اوھان کان قرآن (پھچائڻ) بابت ڪو اُجورو نٿو گھران ۽ نڪي آءٌ (ڪوڙ) ٺاھڻ وارن مان آھيان.'),
(4057, 38, 87, 'ھي (قرآن) ته جھانن لاءِ نصيحت آھي.'),
(4058, 38, 88, '۽ ڪنھن وقت کانپوءِ اُن جي سچائي جي خبر ضرور اوھين ڄاڻندؤ.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, '(ھن) ڪتاب جو نازل ڪرڻ غالب حِڪمت واري الله (جي طرف) کان آھي.'),
(4060, 39, 2, 'بيشڪ اسان توڏانھن سچ سان ڪتاب نازل ڪيو ته الله لاءِ (پنھنجي) عبادت کي خالص ڪري خاص اُن جي عبادت ڪر.'),
(4061, 39, 3, 'خبردار ٿيءُ خالص عبادت الله ئي جي لاءِ آھي، ۽ جن الله کانسواءِ ٻيا دوست ڪري ورتا، (سي چوندا آھن ته) انھن کي رڳو ھن لاءِ پوڄيندا آھيون ته اسان کي الله ڏانھن مرتبي ۾ ويجھو ڪن، جنھن ڳالھ بابت اُھي جھڳڙو ڪندا آھن تنھن بابت بيشڪ انھن جي وچ ۾ الله فيصلو ڪندو، بيشڪ الله اُنھي کي سڌو رستو نه ڏيکاريندو جيڪو ڪوڙو (۽) بي شُڪر آھي.'),
(4062, 39, 4, 'جيڪڏھن الله (پاڻ لاءِ) اولاد وٺڻ گھري ھا ته جيڪي (سڀئي) بڻايو اٿس تنھن مان جيڪو وڻيس ھا سو چونڊي ھا پر اُن جي ذات پاڪ آھي، اُھو ته الله اڪيلو (۽) زبردست آھي.'),
(4063, 39, 5, 'آسمانن ۽ زمين کي (پوريءَ) رِٿ سان بڻايائين، رات کي ڏينھن تي لپيٽيندو آھي ۽ ڏينھن کي رات تي لپيٽيندو آھي ۽ سج ۽ چنڊ کي تابع ڪيائين، ھر ھڪ ٺھرايل مُدّت تي ھلندو آھي، خبردار اُھو (ئي) غالب بخشڻھار آھي.'),
(4064, 39, 6, 'اوھان کي ھڪ جيءَ مان بڻايائين وري منجھانئس سندس زال کي پيدا ڪيائين ۽ ڍورن مان اَٺ قسم اوھان لاءِ لاٿائين، اوھان کي اوھان کي مائن جي پيٽن ۾ ھڪ (قسم جي) بڻاوت کانپوءِ ٻئي (قسم جي) بڻاوت ٽن اونداھين ۾ پيدا ڪندو آھي، اُھو الله اوھان جو پالڻھار آھي اُن جي ئي (ساري) بادشاھي آھي، ان کانسواءِ ٻيو ڪو عبادت جو لائق نه آھي، پوءِ ڪيڏانھن ڦيرايا ويندا آھيو.'),
(4065, 39, 7, 'جيڪڏھن بي شُڪري ڪندؤ ته بيشڪ الله اوھان کان بي پرواھ آھي، ۽ پنھنجن ٻانھن جي بي شُڪريءَ کي پسند نه ڪندو آھي، ۽ جيڪڏھن شڪر ڪندؤ ته بيشڪ الله اوھان لاءِ اُن کي پسند ڪندو، ۽ ڪو بار کڻندڙ ٻئي جو بار نه کڻندو، وري اوھان جو موٽڻ پنھنجي پالڻھار ڏانھن آھي پوءِ جيڪي ڪندا ھئو تنھنجي اوھان کي سُڌ ڏيندو، بيشڪ اُھو سِينن وارو (ڳجھ) ڄاڻندڙ آھي.'),
(4066, 39, 8, '۽ جڏھن ماڻھوءَ کي ڪو ڏک پھچندو آھي (تڏھن) پنھنجي پالڻھار کي ڏانھس ورندڙ ٿي سڏيندو آھي وري جڏھن پاڻ وٽان کيس ڪا نعمت بخشيندو آھي (تڏھن) جنھن (مطلب) لاءِ اڳ سڏيندو ھو تنھنکي وساريندو آھي ۽ الله لاءِ شريڪ مقرّر ڪندو آھي ته سندس واٽ کان (ماڻھن کي) ڀُلائي، (اي پيغمبر) چؤ ته (اي ڪافر) پنھنجي ڪُفر (جي حالت) ۾ ٿورو (وقت) نفعو وٺ، بيشڪ تون دوزخين مان آھين.'),
(4067, 39, 9, 'ڀلا جيڪو رات جي وقتن ۾ سجدو ڪندڙ ۽ بيھي عبادت ڪندڙ آھي آخرت (جي عذاب) کان ڊڄندو آھي ۽ پنھنجي پالڻھار جي رحمت جي اميد رکندو آھي (سو ڀَلو آھي يا اُھو بي شُڪر مُشرڪ؟)، (اي پيغمبر) چؤ ته سمجھ وارا ۽ بي سمجھ (پاڻ ۾) برابر ٿيندا ڇا؟ ڌيان وارا ئي نصيحت وٺندا آھن.'),
(4068, 39, 10, 'چؤ ته اي منھنجا مُؤمن ٻانھؤ پنھنجي پالڻھار کان ڊڄو، جن ھن دُنيا ۾ چڱائي ڪئي تن ئي لاءِ (آخرت ۾) چڱائي آھي، ۽ الله جو مُلڪ ويڪرو آھي، رڳو صابرن کي سندن بدلو اَڻ کُٽ ڏبو.'),
(4069, 39, 11, '(اي پيغمبر کين) چؤ ته بيشڪ مون کي حُڪم ٿيو آھي ته الله لاءِ (پنھنجي) عبادت خالص ڪري رڳو اُن جي عبادت ڪريان.'),
(4070, 39, 12, '۽ مون کي حُڪم ٿيو آھي ته (سڀ کان) پھريون حُڪم مڃيندڙ (آءٌ) آھيان.'),
(4071, 39, 13, 'چؤ ته جيڪڏھن آءٌ پنھنجي پالڻھار جي نافرماني ڪريان ته بيشڪ آءٌ وڏي ڏينھن جي عذاب کان ڊڄان ٿو.'),
(4072, 39, 14, 'چؤ ته پنھنجي عبادت الله لاءِ خالص ڪري فقط اُن جي عبادت ڪريان ٿو.'),
(4073, 39, 15, 'پوءِ الله کانسواءِ جنھن کي (اوھين) گھرو تنھن کي پوڄيو، (کين) چؤ ته بيشڪ ڇيئي وارا اُھي آھن جن گمراھ ٿيڻ سبب پاڻ کي ۽ پنھنجي گھر وارن کي قيامت جي ڏينھن ڇيئي ۾ وڌو، خبردار اھوئي پڌرو ڇيئو آھي.'),
(4074, 39, 16, 'سندن مٿان باھ جون ڇٽيون ۽ سندن ھيٺان به (اھڙا) وڇاڻا ھوندا، اِھو (عذاب) آھي جنھن کان الله پنھنجن ٻانھن کي ڊيڄاري ٿو، تنھنڪري اي منھنجا ٻانھؤ مون کان ڊڄو.'),
(4075, 39, 17, '۽ جن (ماڻھن) بتن جي پوڄا ڪرڻ کان پاسو ڪيو ۽ الله ڏانھن وريا تن لاءِ خوشخبري آھي.'),
(4076, 39, 18, 'پوءِ (اي پيغمبر) منجھن اُنھن ٻانھن کي خوشخبري ڏي جيڪي ڳالھ ٻڌندا آھن. پوءِ اُن مان تمام چڱيءَ تي ھلندا آھن، اِھي (اُھي) آھن جن کي الله ھدايت ڪئي آھي ۽ اھي ئي ڌيان وارا آھن.'),
(4077, 39, 19, 'ڀلا جنھن تي عذاب جو حُڪم ثابت ٿيو (سو بچي سگھندو ڇا؟)، تون باھ ۾ پيل کي ڇڏائي سگھندين ڇا؟'),
(4078, 39, 20, 'پر جيڪي پنھنجي پالڻھار کان ڊنا تن لاءِ ماڙيون آھن اُنھن جي مٿان (ٻيون) ماڙيون اڏيليون آھن انھن جي ھيٺان واھيون وھنديون آھن، الله (اِھو) انجام ڪيو آھي، الله انجام نه ڦيرائيندو آھي.'),
(4079, 39, 21, 'نه ڏٺو اٿيئي ڇا ته الله آسمان کان پاڻي وسايو پوءِ اُن کي زمين جي چشمن ۾ وھايائين وري اُن سان ڀاتين ڀاتين رنگ جي پوک ڄمائيندو آھي وري سُڪي ويندي آھي پوءِ اُن کي زرد ٿيل ڏسندو آھين وري اُن کي ذرا ذرا ڪندو آھي، بيشڪ اُن ۾ ڌيان وارن لاءِ نصيحت آھي.'),
(4080, 39, 22, 'ڀلا جنھن جو سينو (دين) اسلام لاءِ الله کوليو آھي ۽ اُھو پنھنجي پالڻھار جي (نُور جي سوجھري) تي ھجي (سو سخت دل وارن ڪافرن جھڙو ٿيندو ڇا؟)، پوءِ اُنھن لاءِ ويل آھي جن جون دليون الله جي ياد ڪرڻ کان سخت (ٿيون) آھن، اِھي پڌريءَ گمراھي ۾ آھن.'),
(4081, 39, 23, 'الله تمام چڱي ڳالھ (يعني) اھڙو ڪتاب نازل ڪيو جو ھڪ ٻئي جھڙا (سندس مضمون دُھرايل آھن اُن جي ٻڌڻ) کان اُنھن (ماڻھن) جون کَلون ڪانڊاربيون آھن جيڪي پنھنجي پالڻھار کان ڊڄندا آھن، وري سندين کَلون ۽ سندين دليون الله جي ياد ڪرڻ تي ڪنئريون ٿينديون آھن، اِھا الله جي ھدايت آھي جنھن کي گھري (تنھن کي) اُن سان واٽ ڏيکاري ٿو، ۽ جنھن کي الله گمراھ ڪري تنھن کي ڪو ھدايت ڪندڙ ڪونھي.'),
(4082, 39, 24, 'ڀلا جيڪو قيامت جي ڏينھن پنھنجي مُنھن سان بڇڙي عذاب کي روڪي ٿو (سو ڇٽل جي برابر ٿيندو ڇا؟)، ۽ ظالمن کي چئبو ته جيڪي ڪندا ھيؤ (تنھنجي سزا) چڱو.'),
(4083, 39, 25, 'جيڪي کانئن اڳ ھوا تن به ڪوڙ ڀانيو (ھو) پوءِ وٽن اُتان عذاب پھتو جتان ڄاڻندا ئي نه ھوا.'),
(4084, 39, 26, 'پوءِ الله کين دنيا جي حياتيءَ ۾ خواري چکائي، ۽ بيشڪ آخرت جو عذاب (ته) تمام وڏو آھي، جيڪڏھن (اھا ڳالھ) ڄاڻن ھا (ته پاڻ کي عذاب کان بچائين ھا).'),
(4085, 39, 27, '۽ بيشڪ ماڻھن لاءِ ھن قرآن ۾ ھر قسم جا مثال بيان ڪياسون ته مانَ اُھي نصيحت وٺن.'),
(4086, 39, 28, 'قرآن عربي (ٻوليءَ ۾) بي عيب (نازل ڪيوسون) ته مانَ اُھي ڊڄن.'),
(4087, 39, 29, 'الله مثال بيان ٿو ڪري ھڪ ٻانھو جنھن ۾ (گھڻا) بد مزاج (مالڪ) ڀائيوار آھن ۽ (ٻيو) ٻانھو خلاصو ھڪڙي ماڻھوءَ جي ملڪيت آھي مثال ۾ اھي ٻئي (ٻانھا) برابر ٿيندا ڇا؟ سڀ ساراھ الله کي ئي جُڳائي، پر انھن مان گھڻا نه ڄاڻندا آھن.'),
(4088, 39, 30, '(اي پيغمبر) بيشڪ تون (به) مرندين ۽ بيشڪ اُھي (به) مرندا.'),
(4089, 39, 31, 'وري بيشڪ اوھين قيامت جي ڏينھن پنھنجي پالڻھار وٽ پاڻ ۾ جھڳڙو ڪندؤ.'),
(4090, 39, 32, 'پوءِ اُن کان وڌيڪ ظالم ڪير آھي جنھن الله تي ڪوڙ ٺاھيو ۽ جڏھن سچو دين وٽس آيو (تڏھن) اُن کي ڪوڙ ڄاتائين، ڪافرن جي جاءِ دوزخ ۾ نه آھي ڇا؟'),
(4091, 39, 33, '۽ جنھن سچو دين آندو ۽ (جنھن) کيس سَچو ڄاتو اِھي ئي پرھيزگار آھن.'),
(4092, 39, 34, 'جيڪي گھرندا سو انھن لاءِ سندن پالڻھار وٽ آھي، ڀلارن جو بدلو اِھو آھي.'),
(4093, 39, 35, 'ته جيڪي بڇڙا ڪم ڪيائون سي الله کانئن ميٽي ۽ جيڪي چڱا ڪم ڪندا ھوا تن بابت سندن (چڱو) اُجورو کين بدلي ۾ ڏئي.'),
(4094, 39, 36, 'الله پنھنجي ٻانھي (ﷴؐ) کي ڪافي نه آھي ڇا؟ ۽ الله کانسواءِ جيڪي (ٻيا) آھن تن کان توکي ڊيڄاريندا آھن، ۽ جنھن کي الله ڀُلائي تنھن کي ڪو ھدايت ڪرڻ وارو نه آھي.'),
(4095, 39, 37, '۽ جنھن کي الله ھدايت ڪري تنھن کي ڪو ڀلائڻ وارو نه آھي، الله غالب بدلي وٺڻ وارو نه آھي ڇا؟'),
(4096, 39, 38, '(اي پيغمبر) جيڪڏھن کانئن پُڇين ته آسمانن ۽ زمين کي ڪنھن بڻايو آھي؟ ته ضرور چوندا ته الله، (پوءِ کين) چؤ ته الله کانسواءِ جن کي سڏيندا آھيو تن کي ڏسو (ته سھي) جيڪڏھن الله مون کي ڪا سختي (پھچائڻ) گھري ته اِھي اُن جي (پھچايل) سختيءَ جي لاھڻ وارا آھن ڇا؟ يا مون کي ڪو سُک (پھچائڻ) گھري ته اِھي (بُت) سندس (پھچايل) سُک جي بند ڪرڻ وارا آھن ڇا؟ (تون کين) چؤ ته مون کي الله ڪافي آھي، ڀروسي ڪندڙ مٿس (ئي) ڀروسو ڪندا آھن.'),
(4097, 39, 39, '(کين) چؤ ته اي منھنجي قوم اوھين پنھنجي جاءِ عمل ڪريو بيشڪ آءٌ (به پنھنجي جاءِ) عمل ڪندڙ آھيان، پوءِ سِگھوئي ڄاڻندؤ.'),
(4098, 39, 40, 'ته ڪنھن تي (اُھو) عذاب اچي ٿو جو کيس خوار ڪندو ۽ مٿس ھميشه جو عذاب لھي ٿو.'),
(4099, 39, 41, 'بيشڪ اسان توتي ڪتاب ماڻھن لاءِ سچ سان لاٿو، پوءِ جيڪو ھدايت وارو ٿيو سو خاص پنھنجي (فائدي) لاءِ، ۽ جيڪو گُمراھ ٿيو سو رڳو پنھنجي (نقصان) لاءِ گُمراھ ٿئي ٿو، ۽ تون مٿن ڪو ذمي وار نه آھين.'),
(4100, 39, 42, 'الله ماڻھن جا رُوح سندن مرڻ جي مھل ڪڍندو آھي ۽ جيڪو نه مُئو آھي (تنھن جو روح) سندس ننڊ (جي حالت) ۾ (ڪڍندو آھي)، پوءِ جنھن تي موت جو حُڪم ڪيو اٿس تنھن کي جھليندو آھي ۽ اُن ٻئي کي ھڪ مُقرر مُدّت تائين ڇڏيندو آھي، بيشڪ ھن ۾ ماڻھن لاءِ نشانيون آھن جيڪي ڌيان ڪندا آھن.'),
(4101, 39, 43, 'الله کانسواءِ ٻيا شفاعت ڪرڻ وارا ورتا اٿن ڇا؟ (کين) چؤ ته جيتوڻيڪ اُھي نڪي ڪجھ اختيار رکندا ھجن ۽ نڪي سمجھندا ھجن (ته به شفاعت ڪندا ڇا؟).'),
(4102, 39, 44, '(کين) چؤ ته سڀ شفاعت الله جي وس آھي، آسمانن ۽ زمين جي بادشاھي ان جي آھي، (اوھين) وري ڏانھس موٽائبؤ.'),
(4103, 39, 45, '۽ جڏھن اڪيلي الله (جي نالي) کي ياد ڪبو آھي (تڏھن) جيڪي آخرت کي نه مڃيندا آھن تن جون دليون ٽھنديون آھن، جڏھن الله کانسواءِ ٻـين کي ياد ڪبو آھي تڏھن اُنھيءَ ئي مھل اُھي سَرھا ٿيندا آھن.'),
(4104, 39, 46, '(اي پيغمبر) چؤ ته اي الله آسمانن ۽ زمين جا پيدا ڪندڙ ڳجھ ۽ ظاھر ڄاڻندڙ تون ئي پنھنجن ٻانھن جي وچ ۾ جنھن (ڳالھ) بابت (اُھي) جھڳڙو ڪندا رھيا (تنھن بابت) نبيرو ڪندين.'),
(4105, 39, 47, '۽ جيڪي زمين ۾ سڀئي مال آھي سو ۽ اُن جيترو ٻيو ساڻس جيڪڏھن ظالمن کي ھجي ته جيڪر اُھو قيامت جي ڏينھن بڇڙي عذاب کان (بچڻ لاءِ) بدلو ڏئي چڪن، ۽ الله جي طرف کان اھڙو (معاملو) انھن کي پڌرو ٿيندو جنھنجو گُمان (ئي) نه رکندا ھوا.'),
(4106, 39, 48, '۽ جيڪي بڇڙايون ڪمايون ھوائون سي انھن کي پڌريون ٿينديون ۽ جنھن (سزا) جون ٺـٺوليون ڪندا ھوا سا کين ويڙھي ويندي.'),
(4107, 39, 49, 'پوءِ جڏھن ماڻھوءَ کي ڪا اوکائي پھچندي آھي (تڏھن) اسان کي سڏيندو آھي، وري جڏھن اُن کي پاڻ وٽان ڪا نعمت ڏيندا آھيون (تڏھن) چوندو آھي ته اُھا رڳو پنھنجي سمجھ سان (جا مون ۾ آھي) مون کي ڏني وئي آھي، (ايئن نه آھي) بلڪ اُھا (نعمت) پرک آھي پر اُنھن مان گھڻا نه ڄاڻندا آھن.'),
(4108, 39, 50, 'بيشڪ کانئن اڳين (به) اِھا (ڳالھ) چئي ھئي پوءِ جيڪي (ڪرتوت) ڪيائون ٿي تن کانئن ڪجھ به (مُصيبت) نه ٽاري.'),
(4109, 39, 51, 'پوءِ جيڪي ڪمايائون تنھنجون بڇڙايون کين پھتيون، ۽ ھنن مان (به) جيڪي ظالم آھن تن جيڪي ڪمايائون تنھنجون بڇڙايون کين سِگھوئي پھچنديون ۽ اُھي عاجز ڪرڻ وارا نه آھن.'),
(4110, 39, 52, 'نه ڄاتو اٿن ڇا ته الله جنھن لاءِ گھرندو آھي (تَنھنجو) رزق ڪُشادو ڪندو آھي ۽ (جنھن لاءِ گھرندو آھي تَنھنجو) گھٽ ڪندو آھي، بيشڪ ھن (ڪم) ۾ اُنھن ماڻھن لاءِ نشانيون آھن جيڪي ايمان آڻيندا آھن.'),
(4111, 39, 53, '(منھنجي طرفان اُنھن کي) چؤ ته اي منھنجا اُھي ٻانھؤ جن پاڻ تي ظلم ڪيو (اوھين) الله جي ٻاجھ کان نا اُميد نه ٿيو، ڇوته الله سڀ گناھ بخشيندو آھي، بيشڪ اُھوئي بخشڻھار مھربان آھي.'),
(4112, 39, 54, '۽ پنھنجي پالڻھار ڏانھ ورو ۽ اُن کان اڳي سندس فرمانبردار ٿيو جو اوھان وٽ عذاب اچي (۽) وري مدد نه ڏنا ويندؤ.'),
(4113, 39, 55, '۽ اوھان جي پالڻھار کان اوھان ڏانھن جيڪا بلڪل چڱي شيء نازل ڪئي وئي آھي تنھن جي اُنھيءَ کان اڳ تابعداري ڪريو جو اوھان وٽ اوچتو عذاب اچي ۽ اوھين بي خبر ھجو.'),
(4114, 39, 56, '(انھيءَ ڪري خُداءِ ڏانھن مھاڙ ڪريو ۽ قرآن جي تابعداري ڪريو) ته متان پوءِ ڪوبه جيءُ چوي ته ھيءَ ارمان جو الله جي دين ۾ سُستي ڪيم ۽ بيشڪ (آءٌ ته) ٺـٺوليون ڪرڻ وارن مان ھوس.'),
(4115, 39, 57, 'يا چوي ته جيڪڏھن الله مون کي ھدايت ڪري ھا ته ضرور پرھيزگارن مان ٿيان ھا.'),
(4116, 39, 58, 'يا جڏھن عذاب ڏسي (تڏھن) چوي ته جي منھنجو ٻيو ڀيرو (دنيا ۾ وڃڻ) ٿئي ته جيڪر ڀلارن مان ٿيان.'),
(4117, 39, 59, '(چئبس ته) ھائو! بيشڪ تو وٽ منھنجون نشانيون آيون ھيون پوءِ اُنھن کي ڪُوڙو ڄاتئي ۽ وڏائي ڪيئي ۽ ڪافرن مان ٿيئن.'),
(4118, 39, 60, '۽ قيامت جي ڏينھن تون انھن کي ڏسندين جن الله تي ڪوڙ ٻَڌو ته سندن مُنھن ڪارا ٿيل ھوندا، وڏائي ڪندڙن جي جاءِ دوزخ ۾ نه آھي ڇا؟'),
(4119, 39, 61, '۽ جن پرھيزگاري ڪئي تن کي الله سندن ڇوٽڪاري جي ھنڌ ۾ (دوزخ کان) بچائي پھچائيندو، نڪي کين ڪا مدائي پھچندي ۽ نڪي اُھي غمگين رھندا.'),
(4120, 39, 62, 'الله (ئي) سڀڪنھن شيء جو خلقيندڙ آھي، ۽ اُھو سڀڪنھن شي جي سنڀال ڪندڙ آھي.'),
(4121, 39, 63, 'آسمانن ۽ زمين جون ڪُنجيون انھي ئي وٽ آھن، ۽ جن الله جي نشانين جو انڪار ڪيو اُھي ئي ڇيئي وارا آھن.'),
(4122, 39, 64, 'چؤ ته اي بي عقلؤ مون کي (ھي) ڏسيندا آھيو ڇا ته الله کانسواءِ ٻئي ڪنھن جي عبادت ڪريان.'),
(4123, 39, 65, '۽ (اي پيغمبر) بيشڪ توڏانھن وحي ڪيو ويو ۽ جيڪي توکان اڳ (ٻيا پيغمبر) ھوا تن (ھر ھڪ) ڏانھن (به) ته جيڪڏھن (الله سان ڪو) شريڪ مقرر ڪندين ته تنھنجا عمل ضرور ناس ٿيندا ۽ ضرور ڇيئي وارن مان ٿيندين.'),
(4124, 39, 66, '(نه!) بلڪ رڳو الله جي عبادت ڪر ۽ شُڪر ڪرڻ وارن مان ھُج.'),
(4125, 39, 67, '۽ (اُنھن) الله کي جئين سندس ڄاڻڻ جو حق آھي (تئين) نه سڃاتو، ۽ قيامت جي ڏينھن ساري زمين سندس مُٺ ۾ ھوندي ۽ آسمان سندس سڄي ھٿ ۾ ويڙھيل ھوندا، اُھو پاڪ آھي ۽ اُنھيءَ کان مٿاھون آھي جيڪي ساڻس شريڪ ٺھرائيندا آھن.'),
(4126, 39, 68, '۽ (پھريون ڀيرو) صُور ۾ ڦوڪيو ويندو پوءِ جيڪي آسمانن ۾ آھي ۽ جيڪو زمين ۾ آھي سو (سڀڪو) مرندو پر جنھن لاءِ الله گھريو (سو نه)، وري اُن ۾ ٻيو ڀيرو ڦوڪبو ته اُمالڪ کڙا ٿي (پيا) ڏسندا.'),
(4127, 39, 69, '۽ زمين پنھنجي پالڻھار جي نُور سان روشن ٿيندي ۽ (عملن جو) ڪتاب رکبو ۽ پيغمبرن ۽ شاھدن کي آڻبو ۽ سندن وچ ۾ حق سان نبيرو ڪبو ۽ اُنھن تي ظلم نه ڪبو.'),
(4128, 39, 70, 'سڀڪنھن ماڻھوءَ جيڪي ڪمايو تنھنجو پورو بدلو کيس ڏبو ۽ جيڪي ڪندا آھن تنھن کي الله ڄاڻندو آھي.'),
(4129, 39, 71, '۽ ڪافرن کي ٽوليون ٽوليون ڪري دوزخ ڏانھن ھڪالبو، تان جو جڏھن اُن وٽ پھچندا (تڏھن) اُن جا دروازا کولبا ۽ اُن جا داروغا کين چوندا ته اوھان مان اوھان وٽ ڪي پيغمبر نه آيا ھوا ڇا؟ جو اوھان کي اوھان جي پالڻھار جون آيتون پڙھي ٻڌائين ۽ اوھان کي ھن ڏينھن جي پيش اچڻ کان ڊيڄارين، چوندا ته ھائو (آيا ھوا ڊيڄاريو به ھوائون) پر ڪافرن تي عذاب جو حُڪم ثابت ٿيو.'),
(4130, 39, 72, '(پوءِ انھن کي) چئبو ته دوزخ جي دروازن مان گھڙو منجھس سدائين رھندڙ آھيو، پوءِ وڏائي ڪندڙن جي جاءِ بڇڙي آھي.'),
(4131, 39, 73, '۽ جيڪي پنھنجي پالڻھار کان ڊنا سي ٽوليون ٽوليون بھشت ڏانھن روانا ڪبا، تانجو جڏھن اُن وقت ايندا ۽ اُن جا دروازا کولبا ۽ اُن جا دواروغا کين چوندا ته اوھان تي سلام ھجي اوھين (ھاڻي) خوش رھو پوءِ منجھس ھميشه رھڻ لاءِ گھڙو.'),
(4132, 39, 74, '۽ چوندا ته سڀ ساراھ انھيءَ الله کي جُڳائي جنھن پنھنجو انجام اسان سان سچو ڪيو ۽ اسان کي (ھن) زمين جو وارث ڪيائين بھشت مان جتي وڻندو اٿون اُتي جڳه وٺون ٿا، پوءِ عمل ڪندڙن جو اَجر (ڪھڙونه) چڱو آھي.'),
(4133, 39, 75, '۽ ملائڪن کي عرش جي چوڌاري وڪوڙيندڙ ڏسندين پنھنجي پالڻھار جي ساراھ سان گڏ پاڪائي بيان ڪندا آھن، ۽ اُنھن جي وچ ۾ انصاف جو حُڪم ڪبو ۽ (آخر ۾) چيو ويندو ته سڀ ساراھ جھانن جي پالڻھار الله کي جُڳائي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حٰمٓ.'),
(4135, 40, 2, '(ھن) ڪتاب جو ھيٺ لاھڻ الله غالب ڄاڻندڙ وٽان آھي.'),
(4136, 40, 3, 'جو گناھ بخشيندڙ ۽ توبه قبول ڪندڙ سخت سزا ڏيندڙ سگھ وارو آھي، اُن کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي، ڏانھس ورڻو آھي.'),
(4137, 40, 4, 'ڪافرن کانسواءِ الله جي آيتن بابت (ٻيو) ڪوبه تڪرار نه ڪندو آھي پوءِ شھرن ۾ (واپار لاءِ) سندن اچ وڃ توکي نه ٺڳي.'),
(4138, 40, 5, 'کانئن اڳ نُوح جي قوم ۽ انھن پڄاڻا ٻـين (ڪيترين ئي) ٽولين ڪُوڙ ڄاتو، ۽ سڀڪنھن ٽوليءَ پنھنجن پيغمبر جو قصد ڪيو ته کيس پڪڙين ۽ اجاين (ڳالھين) سان جھيڙو ڪندا رھيا ته اُن سان سچيءَ ڳالھ کي ناس ڪن پوءِ کين ورتم، پوءِ منھنجي سزا (ڏيڻ) ڪيئن ھئي.'),
(4139, 40, 6, '۽ اھڙي طرح تنھنجي پالڻھار جو حُڪم ڪافرن تي ثابت ٿيو آھي ته اُھي دوزخي آھن.'),
(4140, 40, 7, 'جيڪي (ملائڪ) عرش کي کڻندا آھن ۽ جيڪي اُن جي چوڌاري آھن سي پنھنجي پالڻھار جي پاڪائي ساراھ سان گڏ بيان ڪندا آھن ۽ مٿس ايمان آڻيندا آھن ۽ مؤمن لاءِ بخشش گھرندا آھن، (چوندا آھن ته) اي اسان جا پالڻھار سڀڪنھن شيء کي پنھنجي ٻاجھ ۽ علم ۾ وڪوڙيو اٿيئي تنھنڪري جن توبه ڪئي ۽ تنھنجي واٽ جي تابعداري ڪئي تن کي بخش ۽ کين دوزخ جي عذاب کان بچاءِ.'),
(4141, 40, 8, 'اي اسان جا پالڻھار کين اُن ھميشه رھڻ وارن باغن ۾ داخل ڪر جن جو ساڻن وعدو ڪيو اٿيئي ۽ جيڪي سندن پين ڏاڏن ۽ سندن زالن ۽ سندن اولاد مان سڌريل ھجي تنھن کي به (اُنھن باغن ۾ داخل ڪر)، بيشڪ تون ئي غالب حڪمت وارو آھين.'),
(4142, 40, 9, '۽ کين سختين کان بچاءِ، ۽ جنھن کي اُن ڏينھن ۾ سختين کان بچايئي تنھن تي بيشڪ رحم ڪيئي، ۽ اِھو (بچائڻ) وڏي مُراد ماڻڻ آھي.'),
(4143, 40, 10, 'بيشڪ جن ڪُفر ڪيو تن کي سڏبو (۽ چئبو) ته الله جو (اوھان تي) ڏمرجڻ اوھان جي پاڻ تي (اڄ جي) ڏمرجڻ کان تمام وڏو آھي جڏھن جو اوھان کي (دنيا ۾) ايمان ڏانھن سڏبو ھو ۽ نه مڃيندا ھئو.'),
(4144, 40, 11, 'چوندا ته اي اسانجا پالڻھار اسان کي ٻه ڀيرا ماريئي ۽ اسان کي ٻه ڀيرا جياريئي تنھنڪري (ھاڻي) پنھنجن گناھن کي (پاڻ) مڃيوسون پوءِ (ھاڻي) نڪرڻ جي ڪا واھ (ڪٿي) آھي؟'),
(4145, 40, 12, '(چئبن) اِھو (عذاب اوھان کي) انھيءَ ڪري آھي جو جڏھن (خاص) اڪيلي الله کي سڏبو ھو (تڏھن) اوھين انڪار ڪندا ھئو، ۽ جيڪڏھن ساڻس شريڪ ٺھرايو ويندو ھو ته (ان ڳالھ کي) مڃيندا ھئو، پوءِ (اڄ) تمام مٿاھين وڏي الله جو حڪم آھي.'),
(4146, 40, 13, '(الله) اُھو آھي جيڪو اوھان کي پنھنجون نشانيون ڏيکاريندو آھي ۽ آسمان مان اوھان لاءِ روزي نازل ڪندو آھي، ۽ نصيحت رڳو اُھو وٺندو جيڪو (خدا ڏانھن) موٽندو.'),
(4147, 40, 14, 'پوءِ الله لاءِ پنھنجي عبادت کي خالص ڪري اُن کي سڏيو جيتوڻيڪ ڪافر بڇان ڀانئين.'),
(4148, 40, 15, '(اُھو) مرتبن کي وڏي ڪرڻ وارو ۽ عرش جو صاحب آھي، پنھنجن ٻانھن مان جنھن تي گھرندو آھي (تنھن تي) پنھنجي حڪم سان وحي موڪليندو آھي ته (اُھو ٻانھو ماڻھن کي الله جي) ملڻ جي ڏينھن کان ڊيڄاري.'),
(4149, 40, 16, 'جنھن ڏينھن اُھي (قبرن مان) ٻاھر نڪرندا، انھن جي ڪابه شيء الله کان ڳُجھي نه رھندي، (الله فرمائيندو ته) اڄ ڪنھن جي بادشاھي آھي، (وري پاڻ چوندو) خاص اڪيلي غالب الله جي (بادشاھي) آھي.'),
(4150, 40, 17, 'اڄ سڀڪنھن جِيءَ کي اُنھي جو بدلو ڏبو جيڪو ڪمايو اٿس، اڄ ڪو ظلم ڪونھي، بيشڪ الله جلد حساب وٺڻ وارو آھي.'),
(4151, 40, 18, '۽ (اي پيغمبر) کين قيامت جي ڏينھن (جي اُنھيءَ مھل) کان ڊيڄار جنھن مھل دليون نِڙ گھٽن کي ويجھو ٿينديون (اُھي) ڏک ڀريل ھوندا، (اُن ڏينھن) ظالمن جو ڪو دوست ۽ نه ڪو پارت ڪندڙ ھوندو جنھن جي ڳالھ مڃجي.'),
(4152, 40, 19, 'اکين جي خيانت (واري نگاھ) ۽ جيڪي سينا لڪائيندا آھن سو (الله) ڄاڻندو آھي.'),
(4153, 40, 20, '۽ الله انصاف سان حُڪم ڪندو آھي، ۽ (منڪر) الله کانسواءِ جن کي سڏيندا آھن سي ڪجھ به حُڪم نه ڪندا آھن، بيشڪ الله ئي ٻڌندڙ ڏسندڙ آھي.'),
(4154, 40, 21, 'مُلڪ ۾ نه گھميا آھن ڇا؟ ته (نھاري) ڏسن ته انھن جي پڇاڙي ڪھڙي طرح ٿي جيڪي کانئن اڳ ھوا، اُھي کائن سگھ ۾ زمين تي نشانين (يعني محلاتن جوڙڻ) ۾ تمام ڏاڍا ھوا پوءِ الله سندن گناھن سببان کين پڪڙيو، ۽ الله (جي عذاب) کان اُنھن کي ڪو بچائيندڙ نه ھو.'),
(4155, 40, 22, 'اِھا (سزا) ھن ڪري ھين جو سندن پيغمبر (پڌرن) مُعجزن سان وٽن آيا ٿي پوءِ انڪار ڪيائون تنھنڪري الله کين پڪڙيو، بيشڪ اُھو سگھارو سخت سزا ڏيڻ وارو آھي.'),
(4156, 40, 23, '۽ بيشڪ موسىٰ کي پنھنجن مُعجزن ۽ پڌريءَ حُجّت سان موڪليوسون.'),
(4157, 40, 24, 'فرعون ۽ ھامان ۽ قارون ڏانھن پوءِ چيائون ته ڪُوڙو جادوگر آھي.'),
(4158, 40, 25, 'پوءِ جنھن مھل اسان وٽان ھنن وٽ سچي (پيغام) سان آيو (تنھن مھل) چيائون ته جن مٿس ايمان آندو آھي تن جا پُٽ ڪُھو ۽ سندن ڌيئرون جيئرون ڇڏيو، ۽ ڪافرن جو فريب ته رڳو غلطيءَ ۾ آھي.'),
(4159, 40, 26, '۽ فرعون (دربار وارن کي) چيو ته مون کي ڇڏيو ته موسىٰ کي ڪُھان ۽ ڀلي ته پنھنجي پالڻھار کي سڏي، بيشڪ آءٌ ڊڄان ٿو ته متان اوھان جو دين مٽائي يا مُلڪ ۾ فساد پيدا ڪري.'),
(4160, 40, 27, '۽ موسىٰ چيو ته بيشڪ مون سڀڪنھن ھٺيلي (جي لچائيءَ) کان جو حساب جي ڏينھن کي نه مڃيندو آھي پنھنجي پالڻھار ۽ اوھان جي پالڻھار جي پناھ ورتي.'),
(4161, 40, 28, '۽ فرعون جي مائٽن مان ھڪ مؤمن مڙس جو پنھنجو ايمان لڪائيندو ھو (تنھن) چيو ته اھڙي مڙس کي ڪھو ٿا ڇا جو چوي ٿو ته منھنجو پالڻھار الله آھي ۽ بيشڪ اوھان وٽ اوھان جي پالڻھار کان (چٽا) مُعجزا آندا اٿس، جيڪڏھن کڻي ڪوڙو ھجي ته سندس ڪوڙ (جو وبال) مٿس آھي، ۽ جيڪڏھن سچو ھوندو ته جيڪو وعدو اوھان سان ڪري ٿو تنھن مان ڪجھ اوھان کي ضرور پھچندو، ڇوته جيڪو حد کان لنگھندڙ ڪوڙو ھوندو تنھن کي الله سڌو رستو نه ڏيکاريندو آھي.'),
(4162, 40, 29, 'اي منھنجي قوم اڄ اوھان جي بادشاھي آھي مُلڪ ۾ (اوھين) غالب آھيو، جيڪڏھن (عذاب) اسان وٽ اچي ته الله جي عذاب کان (بچڻ لاءِ) اسان کي ڪير واھر ڏيندو؟ فرعون چيو ته جيڪا (چڱي مصلحت) ڏسان ٿو تنھن کانسواءِ (ٻي) اوھان کي نه ٿو ڏيکاريان ۽ اوھان کي سڌي رستي کانسواءِ (ٻيو رستو) نه ٿو ڏيکاريان.'),
(4163, 40, 30, '۽ جنھن ايمان آندو ھو تنھن چيو ته اي منھنجي قوم بيشڪ آءٌ اوھان تي اڳين ٽولين جھڙي (عذاب جي) ڏينھن (اچڻ) کان ڊڄان ٿو.'),
(4164, 40, 31, 'جھڙو حال نوح جي قوم ۽ عاد ۽ ثمود ۽ جيڪي کائن پوءِ ھوا تن جو (ٿيو ھو تھڙو متان ٿئي)، ۽ الله ٻانھن تي ظلم ڪرڻ نه گھرندو آھي.'),
(4165, 40, 32, '۽ اي منھنجي قوم بيشڪ آءٌ اوھان تي ھڪ ٻئي کي سڏڻ جي (انھيءَ) ڏينھن (اچڻ) کان ڊڄان ٿو.'),
(4166, 40, 33, 'جنھن ڏينھن اوھين پٺيرا ٿي ڀڄندؤ، الله کانسواءِ (ٻيو) اوھان کي ڪو بچائڻ وارو نه ھوندو، ۽ جنھن کي الله ڀُلائي ڇڏي تنھن کي ڪو سِڌو رستو ڏيکارڻ وارو ڪونھي.'),
(4167, 40, 34, '۽ بيشڪ ھن کان اڳ اوھان وٽ يوسف مُعجزن سان آيو ھو پوءِ جيڪي اوھان وٽ آندائين تنھن کان (اوھين) ھميشه شڪ ۾ ھُئؤ، تان جو جڏھن مُئو (تڏھن) چيوَ ته اُن کان پوءِ (ٻيو) ڪو پيغمبر الله نه موڪليندو، اھڙي طرح جيڪو حد کان لنگھندڙ شڪ ڪندڙ آھي تنھن کي الله ڀُلائيندو آھي.'),
(4168, 40, 35, 'اُنھن کي (ڀُلائيندو آھي) جيڪي الله جي نشانين بابت اھڙيءَ حُجّت ڌاران جا وٽن آئي ھجي جھڳڙو ڪندا آھن، (اھو سندن جھڳڙو ڪرڻ) الله وٽ ۽ مُؤمنن وٽ وڏي غضب جھڙي ڳالھ آھي، اھڙي طرح الله سڀڪنھن وڏائي ڪندڙ ھٺيلي جي دل تي مُھر ھڻندو آھي.'),
(4169, 40, 36, '۽ فرعون چيو ته اي ھامان مون لاءِ ھڪ ماڙي بڻاءِ ته مانَ (انھن) واٽن سان پھچان.'),
(4170, 40, 37, 'آسمانن جي واٽن کي ته مُوسىٰ جي معبُود جي سڌ لھان ۽ بيشڪ آءٌ کيس ڪوڙو ڀائيندو آھيان، ۽ اھڙيءَ طرح فرعون کي سندس بڇڙو ڪم چڱو ڪري ڏيکاريو ويو ۽ سڌيءَ واٽ کان جھليو ويو، ۽ فرعون جو مڪر رُڳو خرابيءَ ۾ ھو.'),
(4171, 40, 38, '۽ جنھن ايمان آندو ھو تنھن چيو ته اي منھنجي قوم منھنجي تابعداري ڪريو ته (آءٌ) اوھان کي سڌي رستي لايان.'),
(4172, 40, 39, 'اي منھنجي قوم ھيءَ دنيا جي حياتي (ٿوري) وٿ کانسواءِ (ٻيو) ڪي نه آھي، ۽ بيشڪ آخرت ئي سدائين رھڻ جو ھنڌ آھي.'),
(4173, 40, 40, 'جنھن بد ڪم ڪيو تنھن کي رڳو اُھڙو بدلو ڏبو، ۽ جنھن چڱو ڪم ڪيو خواہ مڙس ھجي خواہ زال ۽ اُھو مؤمن به ھجي ته اُھي بھشت ۾ گھڙندا منجھس اڻ ڳڻي روزي ڏبين.'),
(4174, 40, 41, 'اي منھنجي قوم مون کي ڇا (ٿيو) آھي جو (آءٌ) اوھان کي ڇوٽڪاري ڏانھن سڏيان ٿو ۽ (اوھين) مون کي باھ ڏانھن سڏيو ٿا.'),
(4175, 40, 42, '(اوھين) مون کي سڏيو ٿا ته الله جو انڪار ڪريان ۽ ساڻس اھڙي شيء شريڪ ٺھرايان جنھن (جي حقيقت) جي مون کي ڄاڻپ نه آھي، ۽ آءٌ اوھان کي غالب بخشڻھار ڏانھن سڏيان ٿو.'),
(4176, 40, 43, 'بلاشڪ اوھين مون کي اُنھيءَ ڏانھن سڏيو ٿا جنھن کي دنيا ۽ آخرت ۾ سڏڻ جو ڪو حق نه آھي ۽ بيشڪ اسان جو موٽڻ الله ڏانھن آھي ۽ بيشڪ حد کان لنگھندڙ ئي دوزخي آھن.'),
(4177, 40, 44, 'پوءِ جيڪي اوھان کي چوان ٿو سو سگھو ياد ڪندؤ، ۽ (آءٌ) پنھنجو ڪم الله کي سونپيان ٿو، ڇو ته الله ٻانھن (جي حال) کي ڏسندڙ آھي.'),
(4178, 40, 45, 'پوءِ جيڪي ڦند سٽيائون تن جي بڇڙائيءَ کان الله کيس بچايو ۽ فرعون جي ماڻھن کي بڇڙي عذاب گھيريو.'),
(4179, 40, 46, 'جا باھ آھي جنھن تي صبح ۽ سانجھيءَ جو کين حاضر ڪبو آھي، ۽ جنھن ڏينھن قيامت قائم ٿيندي، (تنھن ڏينھن حُڪم ڪبو ته) فرعونين کي سخت عذاب ۾ گھيڙيو.'),
(4180, 40, 47, '۽ (ياد ڪر) جڏھن باھ ۾ ھڪ ٻئي سان جھڳڙو ڪندا (تڏھن) ھيڻا ھٺيلن کي چوندا ته بيشڪ اسين اوھان جا تابعدار ھواسون تنھنڪري باھ (جي عذاب) مان ڪو حصّو اوھين اسان کان ٽارڻ وارا آھيو (يا نه)؟'),
(4181, 40, 48, 'ھٺيلا چوندا ته بيشڪ اسين (پاڻ) سڀ منجھس (پيل) آھيون بيشڪ الله ٻانھن جي وچ ۾ نبيرو ڪري چڪو آھي.'),
(4182, 40, 49, '۽ دوزخي دوزخ جي داروغن کي چوندا ته اوھين پنھنجي پالڻھار کي عرض ڪريو ته اسان کان ھڪڙو ڏينھن ڪجھ عذاب ھلڪو ڪري.'),
(4183, 40, 50, '(داروغا) چوندا ته اوھان جا پيغمبر (پڌرن) معجزن سان اوھان وٽ نه ايندا ھوا ڇا؟ چوندا ته ھائو (ايندا ھوا)، (داروغا) چوندا ته پوءِ (اوھين) پاڻ پڪاريو، ۽ ڪافرن جو پڪارڻ رڳو گمراھي ۾ آھي.'),
(4184, 40, 51, 'بيشڪ اسين پنھنجن پيغمبرن جي ۽ مؤمنن جي دنيا جي حياتيءَ ۾ ضرور مدد ڪنداسون ۽ جنھن ڏينھن شاھد کڙا ٿيندا (تنھن ڏينھن به مدد ڪنداسون).'),
(4185, 40, 52, 'جنھن ڏينھن ظالمن کي سندن بھانو نفعو نه ڏيندو ۽ کين لعنت آھي ۽ انھن لاءِ (اُتي) بُڇڙي جاءِ آھي.'),
(4186, 40, 53, '۽ بيشڪ مُوسىٰ کي ھدايت ڏني سون ۽ بني اسرائيلن کي ڪتاب جو وارث ڪيوسون.'),
(4187, 40, 54, 'جو ڌيان وارن جي سمجھائڻ ۽ نصيحت ڏيڻ لاءِ آھي.'),
(4188, 40, 55, 'پوءِ (اي پيغمبر) صبر ڪر ڇوته الله جو وعدو سچّو آھي ۽ پنھنجي گناھ جي بخشش گھر ۽ صبح سانجھيءَ جو پنھنجي پالڻھار جي ساراھ سان گڏ پاڪائي بيان ڪر.'),
(4189, 40, 56, 'بيشڪ جيڪي الله جي آيتن بابت (اُنھيءَ) حُجّت ڌاران جا وٽن آئي ھجي جھڳڙو ڪندا آھن تن جي سينن ۾ وڏائي (جي ارادي) کانسواءِ (ٻيو) ڪي نه آھي جنھن کي اُھي پُھچڻ وارا نه آھن، پوءِ الله جي پناھ گھر، بيشڪ اُھو ٻڌندڙ ڏسندڙ آھي.'),
(4190, 40, 57, 'بيشڪ آسمانن ۽ زمين جي بڻاوت ماڻھن جي بڻاوت کان تمام وڏي آھي پر گھڻا ماڻھو نه ڄاڻندا آھن.'),
(4191, 40, 58, '۽ انڌو ۽ سڄو (پاڻ ۾) برابر نه آھي، ۽ (نڪي) جن ايمان آندو ۽ چڱا ڪم ڪيا (سي) نڪي مدائي ڪرڻ وارو (برابر آھن)، اوھين ٿوري نصيحت وٺندا آھيو.'),
(4192, 40, 59, 'بيشڪ قيامت ضرور اچڻي آھي منجھس ڪو شڪ نه آھي، پر گھڻا ماڻھو ويساھ نه رکندا آھن.'),
(4193, 40, 60, '۽ اوھان جو پالڻھار فرمائي ٿو ته اوھين مون کان دُعا گھرو ته اوھان جي دُعا قبول ڪريان، بيشڪ جيڪي منھنجي عبادت کان وڏائي ڪندا آھن سي خوار ٿي دوزخ ۾ سگھوئي گھڙندا.'),
(4194, 40, 61, 'الله اُھو آھي جنھن اوھان لاءِ رات کي پيدا ڪيو ته منجھس آرام وٺو ۽ ڏينھن کي اوھان جي (ھڪ ٻئي کي) ڏسڻ لاءِ (پيدا ڪيو) بيشڪ الله ماڻھن تي فضل (ڪرڻ) وارو آھي پر گھڻا ماڻھو شڪر نه ڪندا آھن.'),
(4195, 40, 62, 'اھو الله اوھان جو پالڻھار ھر شيء کي خلقيندڙ آھي، اُن کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي، پوءِ اوھين ڪيڏانھن ڦيرايا ويندا آھيو.'),
(4196, 40, 63, 'اھڙي طرح اُھي (سئين واٽ کان) ڀُلائبا آھن جن الله جي آيتن جو انڪار ٿي ڪيو.'),
(4197, 40, 64, 'الله اُھو آھي جنھن اوھان لاءِ زمين کي آرام جو ھنڌ ۽ آسمان کي ڇت بڻايو ۽ اوھان جي شڪل ٺاھيائين پوءِ اوھان جون شڪليون تمام چڱيون بڻايائين ۽ اوھان کي سٺين سٺين شين مان روزي ڏنائين، اھو الله اوھان جو پالڻھار آھي، پوءِ جھانن جو پالڻھار وڏي برڪت وارو آھي.'),
(4198, 40, 65, 'اُھو (سدائين) جيئرو آھي اُن کانسواءِ ڪو عبادت جو لائق نه آھي تنھنڪري اُن لاءِ (پنھنجي) عبادت کي خالص ڪري کيس سڏيو، سڀ ساراھ جھانن جي پالڻھار الله کي جڳائي.'),
(4199, 40, 66, '(اي پيغمبر کين) چؤ ته الله کانسواءِ جن کي (اوھين) سڏيندا آھيو تن جي پوڄڻ کان جڏھن منھنجي پالڻھار وٽان مون وٽ (صاف) نشانيون آيون (تڏھن اُنھيءَ وقت) بيشڪ آءٌ جھليو ويو آھيان ۽ مون کي حڪم ڪيو ويو آھي ته جھانن جي پالڻھار جو فرمانبردار ھجان.'),
(4200, 40, 67, '(الله) اُھو آھي جنھن اوھان کي مٽيءَ مان بڻايو وري نُطفي مان وري رت جي دڳ مان وري اوھان کي ٻار ڪري ڪڍي ٿو وري (ڇڏي ٿو) ته (اوھين) پنھنجي پوريءَ سگھ کي پُھچو وري (ڇڏي ٿو) ته ٻُڍڙا ٿيو، ۽ اوھان مان ڪنھن کي (اِن کان) اڳ ماربو آھي ۽ (ڇڏي ٿو) ته مُقرّر مدت کي پھچو ۽ مانَ اوھين سمجھو.'),
(4201, 40, 68, '(الله) اُھو آھي جيڪو جياريندو آھي ۽ ماريندو آھي، پوءِ جڏھن ڪنھن ڪم جو حُڪم ڪندو آھي تڏھن اُن کي رڳو چوندو آھي ته ٿيءُ ته ٿي پوندو آھي.'),
(4202, 40, 69, '(اي پيغمبر) انھن ڏانھن نه ڏٺو اٿيئي ڇا جيڪي الله جي آيتن بابت جھڳڙو ڪندا آھن؟ (اُھي) ڪيڏانھن ڦيرايا ويندا آھن.'),
(4203, 40, 70, 'جن ڪتاب کي ڪُوڙ ڄاتو ۽ جنھن (شي) سان پنھنجن پيغمبرن کي موڪليوسون تنھن کي (به) سگھوئي ڄاڻندا.'),
(4204, 40, 71, 'جڏھن سندن ڳِچين ۾ ڳَٽ ۽ زنجير (ھوندا)، اُھي گِھلبا.'),
(4205, 40, 72, 'ٽھڪندڙ پاڻيءَ ۾، وري باھ ۾ ٽچڪايا ويندا.'),
(4206, 40, 73, 'وري کين چئبو ته جيڪي اوھين شريڪ ٺھرائيندا ھيؤ سي ڪِٿي آھن؟'),
(4207, 40, 74, 'الله ڌاران (ٻيا) (اُھي) چوندا ته اسان (جي نظر) کان گُم ٿي ويا بلڪ ڪنھن شي کي اڳي نه پوڄيندا ھواسون، اھڙيءَ طرح الله ڪافرن کي گُمراھ ڪندو آھي.'),
(4208, 40, 75, 'اِھا (سزا) اِنھيءَ سببان آھي جو اوھين زمين ۾ ناحق خوشيءَ (سبب آڪڙ) ڪندا ھئو ۽ ھن سببان (به) جو اوھين لاڏ ڪندا ھئو.'),
(4209, 40, 76, '(ھاڻي) دوزخ جي دروازن مان گھڙو منجھس سدائين رھندڙ (آھيو)، پوءِ (ڏسو ته دوزخ) ھٺيلن جي جاءِ بڇڙي آھي.'),
(4210, 40, 77, 'پوءِ (اي پيغمبر تون) صبر ڪر بيشڪ الله جو وعدو سچو آھي، تنھنڪري جيڪڏھن کين جيڪو انجام ڏيون ٿا تنھن مان ڪجھ توکي (دُنيا ۾) ڏيکاريون يا (عذاب جي ظاھر ٿيڻ کان اڳ) توکي ماريون ته (نيٺ) اسان ڏانھن موٽائبا.'),
(4211, 40, 78, '۽ بيشڪ توکان اڳ ڪيترائي پيغمبر موڪلياسون منجھائن ڪنھن جو احوال توکي بيان ڪيوسون ۽ منجھائن ڪنھن جو احوال توکي بيان نه ڪيوسون، ۽ ڪنھن به پيغمبر کي الله جي حُڪم کانسواءِ ڪو (به) مُعجزو آڻڻو نه ھو، پوءِ جڏھن الله جو حُڪم آيو تڏھن انصاف سان نبيرو ڪيو ويندو ۽ اُتي ڪوُڙن ڇيئو پاتو.'),
(4212, 40, 79, 'الله اُھو آھي جنھن اوھان لاءِ ڍور پيدا ڪيا ته اُنھن مان ڪن تي چڙھو ۽ منجھائن ڪي کائيندا آھيو.'),
(4213, 40, 80, '۽ منجھن اوھان لاءِ (گھڻا) فائدا آھن ۽ ته مٿن چڙھي اُنھي مطلب کي پھچو جو اوھان جي دلين ۾ آھي ۽ مٿن ۽ ٻيڙين تي اوھان کي چاڙھبو آھي.'),
(4214, 40, 81, '۽ (الله) اوھان کي پنھنجون نشانيون ڏيکاريندو آھي، پوءِ الله جي ڪھڙين (ڪھڙين) نشانين کان انڪار ڪندؤ.'),
(4215, 40, 82, 'پوءِ زمين ۾ نه گھميا آھن ڇا؟ جو نھارين ته جيڪي کائن اڳ ھوا تن جي پڇاڙي ڪيئن ٿي، (جو) کائن گھڻا ھوا ۽ سگھ ۾ ۽ زمين تي نشانين ڪري (جي ڇڏي ويا) تمام ڏاڍا ھوا پوءِ جيڪي (ڪرتوت) ڪمايائون تن کائن ڪجھ به نه ٽاريو.'),
(4216, 40, 83, 'پوءِ جنھن مھل سندن پيغمبر مُعجزن سان وٽن آيا (تنھن مھل) جيڪو (دنيا ھلائڻ جو) عِلم وٽن ھو تنھن سببان خوش ٿيا ۽ جنھن (ڳالھ) تي ٺـٺوليون ڪندا ھوا تنھن (جي بڇڙائي) کين ويڙھيو.'),
(4217, 40, 84, 'پوءِ جنھن مھل اسان جو عذاب ڏٺائون (تنھن مھل) چيائون ته اڪيلي الله تي ايمان آندوسون ۽ جنھن شي کي (ساڻس) شريڪ مقرر ڪندا ھواسون تنھنجا منڪر ٿياسون.'),
(4218, 40, 85, 'پوءِ جڏھن اسان جو عذاب ڏٺائون (تڏھن) سندن ايمان آڻڻ کين فائدو ڪڏھن نه ڏنو، (اھو) الله جو دستور آھي جو سندس ٻانھن ۾ ھليو ايندو آھي، اُن ھنڌ ڪافر ٽوٽي وارا ٿيا.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حٰمٓ.'),
(4220, 41, 2, '(ھن ڪتاب جو الله) ٻاجھاري مھربان وٽان نازل ٿيڻ آھي.'),
(4221, 41, 3, '(ھيءُ اھڙو) ڪتاب جو سندس آيتون کولي کولي بيان ڪيون ويون آھن قُرآن عربيءَ ۾ سمجھ وارن ماڻھن لاءِ آھي.'),
(4222, 41, 4, 'جو خوشخبري ڏيندڙ ڊيڄاريندڙ آھي، پوءِ گھڻن (ماڻھن) مُنھن موڙيو تنھنڪري اُھي (اُن کي) ٻڌندائي نه آھن.'),
(4223, 41, 5, '۽ چوندا آھن ته جنھن ڏانھن اسان کي سڏيندو آھين تنھن کان اسان جون دليون پردي ۾ آھن ۽ اسان جي ڪَنن ۾ گھٻرائي آھي ۽ اسان جي ۽ اوھان جي وچ ۾ اوٽ آھي تنھنڪري (تون پنھنجيءَ طرح) ڪم ڪر بيشڪ اسين (پنھنجيءَ طرح) ڪم ڪرڻ وارا آھيون.'),
(4224, 41, 6, '(اي پيغمبر کين) چؤ ته آءٌ (به) اوھان ئي جھڙو ماڻھو آھيان مگر مون ڏانھن وحي ڪيو ويندو آھي ته اوھان جو معبُود ھڪ خدا آھي تنھنڪري ڏانھس سڌا متوجه ٿيو ۽ کانئس بخشش گھرو، ۽ (اُنھن) مشرڪن لاءِ ويل آھي.'),
(4225, 41, 7, 'جيڪي زڪوٰة نه ڏيندا آھن ۽ اُھي آخرت کي (به) نه مڃيندا آھن.'),
(4226, 41, 8, 'بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ اَڻ کُٽ اَجر آھي.'),
(4227, 41, 9, '(اي پيغمبر کين) چؤ ته اوھين اُن (الله) جو انڪار ڪندا آھيو ڇا جنھن زمين کي ٻن ڏينھن ۾ بڻايو؟ ۽ اُن لاءِ (ٻـين کي) شريڪ بڻائيندا آھيو، اِھو الله جھانن جو پالڻھار آھي.'),
(4228, 41, 10, '۽ اُن (زمين) ۾ مٿانئس ڳرا جبل بڻايائين ۽ منجھس برڪت رکيائين ۽ چئن ڏينھن ۾ منجھس اُن (جي رھاڪن) جا قُوۡت مُقرّر ڪيائين، پڇندڙن لاءِ برابر (کولي بيان ڪيو ويو) آھي.'),
(4229, 41, 11, 'وري آسمان ڏانھن مُھڙ ڪيائين ۽ اُھو (اُن وقت) دُونھون ھو پوءِ اُن کي ۽ زمين کي چيائين ته سرھائيءَ سان خواھ اَرھائيءَ سان اچو، (ٻنھي) چيو ته خُوشيءَ سان آياسون.'),
(4230, 41, 12, 'پوءِ (ٻـين) ٻن ڏينھن ۾ اُنھن کي سَت آسمان بڻايائين ۽ سڀڪنھن آسمان ۾ اُن (جي ڪم) جو حُڪم موڪليائين، ۽ دنيا جي آسمان کي (تارن جي) ڏينئن سينگاريوسون، ۽ (شيطانن کان اُنھيءَ جي) حفاظت ڪئي سون، اِھا (الله) غالب ڄاڻندڙ جي رِٿ آھي.'),
(4231, 41, 13, 'پوءِ جيڪڏھن (اُھي) مُنھن موڙين ته چؤ ته اوھان کي اھڙي عذاب کان ڊيڄاريم جھڙو عاد ۽ ثَمُود جو عذاب (ھو).'),
(4232, 41, 14, 'جڏھن پيغمبر سندن اڳيان ۽ سندن پويان انھن وٽ آيا (پيغمبرن چيو) ته الله کانسواءِ (ٻئي) ڪنھن جي عبادت نه ڪريو، چيائون ته جيڪڏھن اسان جو پالڻھار گھري ھا ته ضرور ملائڪن کي موڪلي ھا تنھنڪري (اوھين) جنھن سان موڪليا ويا آھيو تنھنکي اسين مڃڻ وارا نه آھيون.'),
(4233, 41, 15, 'پوءِ عاد (وارن) زمين ۾ ناحق وڏائي ڪئي ۽ چيائون ته سگھ ۾ اسان کان وڌيڪ ڏاڍو ڪير آھي؟ نه ڏٺو اٿن ڇا ته اھو الله جنھن کين پيدا ڪيو سا کائن سگھ ۾ وڌيڪ ڏاڍو آھي، ۽ (اُھي) اسان جي نشانين جو انڪار ڪندا رھيا.'),
(4234, 41, 16, 'تنھنڪري نحس (يعني مُصيبت جي) ڏينھن ۾ مٿن سخت واءُ موڪليوسون ته کين دُنيا جي حياتيءَ ۾ خواريءَ وارو عذاب چکايون، ۽ بيشڪ آخرت جو عذاب ته وڌيڪ خراب ڪندڙ آھي ۽ اُنھن کي (ڪا) مدد نه ڏبي.'),
(4235, 41, 17, '۽ ثمود (قوم) سو اُنھن کي پڌرو رستو ڏيکاريوسون پوءِ ھدايت کان انڌائي کي پسند ڪيائون پوءِ خوار ڪندڙ عذاب جي ڪڙڪي کين پڪڙيو انھي ڪري جو (بڇڙا ڪم) ڪمائيندا ھوا.'),
(4236, 41, 18, '۽ جن ايمان آندو ھو ۽ پرھيزگاري ڪندا ھوا تن کي بچايوسون.'),
(4237, 41, 19, '۽ جنھن ڏينھن الله جا ويري باھ ڏانھن گڏ ڪيا ويندا پوءِ (تنھن ڏينھن) اُھي (ھڪ ٻئي جي اچڻ تائين) ترسايا ويندا.'),
(4238, 41, 20, 'تانجو جڏھن (اُھي مڙيئي) اُن (دوزخ) وٽ ايندا (تنھن مھل) سندن ڪَن ۽ سندن اکيون ۽ سندن کَلون جيڪي اُھي (دنيا ۾) ڪندا ھوا تنھنجي مٿان شاھدي ڏينديون.'),
(4239, 41, 21, '۽ پنھنجي کَلُن کي چوندا ته اسان تي شاھدي ڇو ڏنوَ؟ (سڀئي) چوندا ته اسان کي اُنھيءَ الله ڳالھرايو جنھن ھر شيء کي ڳالھرايو ۽ اُنھيءَ اوھان کي پھريون ڀيرو بڻايو ۽ (وري به) ڏانھس موٽائبؤ.'),
(4240, 41, 22, '۽ اوھين پنھنجن ڪَنّن ۽ پنھنجين اکين ۽ پنھنجين کَلُن جي پاڻ تي شاھدي ڏيڻ کان پردو نه ڪندا ھئؤ پر اوھان ئي ڀانيو ھو ته جيڪي ڪندا ھئو تنھن مان گھڻو (احوال) الله نه ڄاڻندو آھي.'),
(4241, 41, 23, '۽ اوھان جو اُھو گمان جيڪو پنھنجي پالڻھار جي حق ۾ (غلطيءَ سان) ڀانيو ھُئو تنھن (ھاڻي) اوھان کي ھلاڪ ڪيو تنھنڪري (اڄ) ڇيئي وارن مان ٿيؤ.'),
(4242, 41, 24, 'پوءِ جيڪڏھن صبر ڪندا ته (به) اُنھن جي جاءِ باھ آھي، جيڪڏھن معافي گھرندا ته (به) اُنھن کي معافي نه ملندي.'),
(4243, 41, 25, '۽ انھن لاءِ (شيطان) سنگتي مُقرّر ڪياسون پوءِ جيڪي سندن اڳيان ۽ جيڪي سندن پويان آھي تنھنکي اُنھن لاءِ (سندن سنگتين) سينگاريو ۽ (انھيءَ ڪري اُنھن ٽولين ۾ گڏجي) مٿن سزا ثابت ٿي جيڪي کائن اڳ جِنّن ۽ ماڻھن مان گذري ويا آھن، بيشڪ اُھي (سڀ) ٽوٽي وارا ھوا.'),
(4244, 41, 26, '۽ ڪافرن (ھڪ ٻئي کي) چيو ته ھن قَرآن کي نه ٻُڌو ۽ اُن (جي پڙھڻ جي وچ ۾) لُڙ مچايو ته مانَ اوھين غالب ٿيو.'),
(4245, 41, 27, 'پوءِ ڪافرن کي ضرور سخت سزا جو مزو چکائينداسون ۽ جيڪي تمام بڇڙو (ڪم) ڪندا ھوا تنھن جو بدلو ضرور کين ڏينداسون.'),
(4246, 41, 28, 'الله جي ويرين جي سزا اِھا (ئي) باھ آھي، اُنھن لاءِ اُتي ھميشه جو گھر ھوندو، انھيءَ ڪري سزا (ھوندين) جو اسان جي آيتن جو انڪار ڪندا ھوا.'),
(4247, 41, 29, '۽ ڪافر چوندا ته اي اسان جا پالڻھار جِنّن ۽ ماڻھن مان جن اسان کي گُمراھ ڪيو سي اسان کي ڏيکار ته پنھنجن پيرن جي ھيٺان ڪري لتاڙيون ته ڀلي ته (سڀ کان) ھيٺاھان ھجن.'),
(4248, 41, 30, 'بيشڪ جن چيو ته اسان جو پالڻھار الله آھي وري (ان ڳالھ تي) بيھڪ ورتائون تن تي (موت وقت رحمت جا) ملائڪ لھندا آھن (چوندا اَٿن) ته نه ڊڄو ۽ نڪي ڳڻتي ڪريو ۽ اُنھيءَ بھشت (جي خوشخبريءَ) سان خوش ٿيو جنھن جو اوھان کي وعدو ڏنو ويندو ھو.'),
(4249, 41, 31, 'اسين دُنيا جي حياتيءَ ۾ اوھان جا دوست ھواسون ۽ آخرت ۾ به (اوھان جا دوست آھيون)، ۽ جيڪي اوھان جو جيءُ خواھش ڪندو سو اوھان لاءِ منجھس آھي ۽ جيڪي اوھين گھرندؤ سو (پڻ) اوھان لاءِ منجھس آھي.'),
(4250, 41, 32, '(اھا) مھماني بخشڻھار مھربان (خدا) وٽان آھي.'),
(4251, 41, 33, '۽ انھيءَ کان وڌيڪ چڱو ڳالھ ۾ ڪير آھي جنھن الله ڏانھن سڏيو ۽ (پاڻ ۾) چڱا ڪم ڪيائين ۽ چيو ته بيشڪ آءٌ مُسلمانن مان آھيان؟'),
(4252, 41, 34, '۽ نيڪي ۽ بدي پاڻ ۾ برابر نه آھن، (۽ نيڪي) جا ڏاڍي چڱي آھي تنھن سان (مدائي کي) ٽاريو، جو اُنھيءَ جي ۽ تنھنجي وچ ۾ دشمني آھي سو ڄڻڪ دوست مائٽي وارو ٿيندو.'),
(4253, 41, 35, '۽ اِھا (ڳالھ) صابرن کانسواءِ ٻئي کي حاصل نه ٿيندي آھي، ۽ نڪي وڏي نصيب واري کانسواءِ (ٻئي کي) اِھا حاصل ٿيندي آھي.'),
(4254, 41, 36, '۽ جيڪڏھن توکي شيطان کان (دل ۾) ڪا چوڪ اچي ته الله جي پناھ گھُر، ڇوته اُھو ٻڌندڙ ڄاڻندڙ آھي.'),
(4255, 41, 37, '۽ سندس (قدرت جي) نشانين مان رات ۽ ڏينھن ۽ سج ۽ چنڊ آھي، نڪي سج کي ۽ نڪي چنڊ کي سجدو ڪريو ۽ انھيءَ الله کي سجدو ڪريو جنھن انھن کي خلقيو جيڪڏھن خاص سنديس عبادت ڪندا آھيو.'),
(4256, 41, 38, 'پوءِ جيڪڏھن وڏائي ڪن ته (ڇا ٿيو) جيڪي تنھنجي پالڻھار وٽ آھن سي رات ۽ ڏينھن سندس تسبيحون چوندا آھن ۽ اُھي نه ٿڪبا آھن.'),
(4257, 41, 39, '۽ سندس (قدرت جي) نشانين مان ھيءُ به آھي جو زمين کي وساڻل ڏسندو آھين پوءِ جڏھن مٿس (مينھن جو) پاڻي وسايو سون (تڏھن) چري ۽ اُڀامي، بيشڪ جنھن اُنھي (زمين) کي جياريو آھي سو ضرور مُئن کي (به) جيارڻ وارو آھي، بيشڪ اُھو سڀڪنھن شيءَ تي وس وارو آھي.'),
(4258, 41, 40, 'بيشڪ جيڪي اسان جي آيتن ۾ ڏنگائي ڪندا آھن سي اسان کان ڳُجھا نه آھن، ڀلا جيڪو باھ ۾ وجھبو سو ڀلو آھي يا جيڪو قيامت جي ڏينھن اَمن وارو ٿي ايندو؟ جيڪي وڻيوَ سو ڪريو بيشڪ جيڪي ڪندا آھيو سو خُدا ڏسندڙ آھي.'),
(4259, 41, 41, 'بيشڪ جن قرآن جو (اُنھي وقت) انڪار ڪيو جڏھن وٽن آيو (سي اسان کان ڳُجھا نه آھن)، ۽ بيشڪ اُھو ڪتاب عزّت وارو آھي.'),
(4260, 41, 42, 'جو نڪي اُن جي اڳيان ۽ نڪي اُن جي پويان ڪُوڙ داخل ٿيندو آھي، حڪمت واري ساراھيل (الله جي پار) کان لاٿل آھي.'),
(4261, 41, 43, '(اي پيغمبر) توکي اُنھيءَ کانسواءِ (ٻيو) ڪي نه چئبو آھي جيڪي توکان اڳ (ٻـين) پيغمبرن کي چيو ويو ھو، بيشڪ تنھنجو پالڻھار بخشش وارو (به) آھي ۽ ڏکوئيندڙ سزا ڏيڻ وارو (به) آھي.'),
(4262, 41, 44, '۽ جيڪڏھن اِن (ڪتاب) کي عجمي (يعني عربيءَ کان ڌارين ٻوليءَ ۾) قرآن ڪريون ھا ته ضرور (ڪافر) چون ھا ته سنديس آيتون ڇونه کولي بيان ڪيون ويون، ڀلا (قرآن) عجمي (ٻوليءَ ۾) ۽ (ٻڌڻ وارا) عربيءَ (ٻوليءَ) وارا آھن (سو ڇو)؟ (اي پيغمبر کين) چؤ ته اُھو مؤمنن لاءِ ھدايت ۽ شفا آھي، ۽ جيڪي نه مڃيندا آھن تن جي ڪَنّن ۾ گھٻرائي آھي ۽ اُھو (قرآن) مٿن انڌائي آھي، اِھي (اُنھن وانگر آھن جو) ڄڻڪ ڏورانھين ھنڌ کان سڏيا ويندا آھن.'),
(4263, 41, 45, '۽ بيشڪ مُوسىٰ کي ڪتاب ڏنوسون پوءِ منجھس اختلاف ڪيو ويو، ۽ جيڪڏھن تنھنجي پالڻھار کان حُڪم آڳاٽو نه ٿئي ھا ته سندن وچ ۾ ضرور نبيرو ڪيو وڃي ھا، ۽ بيشڪ اُھي کانئس وڏي شڪ ۾ (پيل) آھن.'),
(4264, 41, 46, 'جيڪو چڱا ڪم ڪندو سو پاڻ لاءِ (ڪندو) تنھن تي (ئي اُن جو نقصان) آھي، ۽ تنھنجو پالڻھار ٻانھن تي ظلم ڪرڻ وارو آھي.'),
(4265, 41, 47, 'قيامت (اچڻ) جي خبر الله (ئي) جي حوالي آھي، ۽ سندس ڄاڻپ ڌاران ڪي ميوا پنھنجن ڳڀين مان نه نڪرندا آھن ۽ (سندس ڄاڻپ ڌاران) ڪابه مادي نڪي ڍُڪي ٿيندي آھي ۽ نڪي ڄڻيندي آھي، ۽ اُن ڏينھن (الله) کين سڏيندو ته منھنجا شريڪ (جي اوھين مُقرّر ڪندا ھئو سي) ڪٿي آھن چوندا ته توکي خبر ڏني سون ته اسان سان ڪوبه (اُنھن جو) شاھد ڪونھي.'),
(4266, 41, 48, '۽ جن کي (ھن کان) اڳ (دنيا ۾) پوڄيندا ھوا سي کائن ڀُلجي ويندا ۽ ڀانئيندا ته کين ڪو ڇوٽڪارو ڪونھي.'),
(4267, 41, 49, 'ماڻھو چڱائيءَ جي سڏڻ (دُعا گھرڻ) کان نه ٿڪبو آھي، ۽ جيڪڏھن کيس (ڪا) مدائي پھچندي آھي ته پوءِ آسرو ڇِني نا اُميد ٿيندو آھي.'),
(4268, 41, 50, '۽ جيڪڏھن کيس اوکائي پھچڻ کان پوءِ پاڻ وٽان ڪا ٻاجھ چکائيندا آھيون ته ضرور چوندو آھي ته ھيءَ مون (ئي) لاءِ آھي ۽ نه ٿو ڀانيان ته قيامت قائم ٿيڻي آھي ۽ جيڪڏھن (آءٌ) پنھنجي پالڻھار ڏانھن (کڻي) موٽائبس ته بيشڪ مون لاءِ وٽس (به) ضرور چڱي حالت ھوندي، پوءِ ڪافرن جيڪي ڪيو آھي تنھن بابت کين ضرور خبردار ڪنداسون، ۽ کين سخت عذاب ضرور چکائينداسون.'),
(4269, 41, 51, '۽ جڏھن ماڻھوءَ تي فضل ڪندا آھيون (تڏھن) مُنھن ڦيرائيندو آھي ۽ پنھنجو پاسو موڙيندو آھي، ۽ جڏھن کيس ڪا اوکائي پھچندي آھي تڏھن وڏيون دعائون گھُرندو آھي.'),
(4270, 41, 52, '(اي پيغمبر کين) چؤ ته اوھين ڏسو ته جيڪڏھن (قرآن) الله وٽان ھجي وري سندس انڪار ڪريو ته جيڪو وڏي ضِدّ ۾ پيل ھجي تنھن کان وڌيڪ گُمراھ ڪير آھي؟'),
(4271, 41, 53, 'پنھنجيون نشانيون مُلڪن ۾ سندن وجودن ۾ کين سگھوئي ڏيکاريندا سون تان جو کين پڌرو ٿيندو بيشڪ اِھو (قرآن) سچو آھي، تنھنجو پالڻھار ڪافي نه آھي ڇا جيڪو سڀڪنھن شيء تي حاضر آھي؟'),
(4272, 41, 54, 'خبردار بيشڪ اُھي پنھنجي پالڻھار جي ملڻ کان شڪ ۾ (پيل) آھن، خبردار اُھو ھر ڪنھن شيء کي وڪوڙيندڙ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حٰمٓ.'),
(4274, 42, 2, 'عٓسٓقٓ.'),
(4275, 42, 3, 'غالب حڪمت وارو الله اھڙيءَ طرح توڏانھن وحي موڪلي ٿو ۽ اُنھن ڏانھن (به) جيڪي توکان اڳ ھوا (موڪليندو) رھيو.'),
(4276, 42, 4, 'جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) سندس آھي، ۽ اُھو (سڀ کان) مٿاھون (۽) وڏو آھي.'),
(4277, 42, 5, '(خُدا جي دھشت کان) آسمان پنھنجي مٿان ستت ئي ڦاٽندا ۽ ملائڪ پنھنجي پالڻھار جي ساراھ سان گڏ پاڪائي بيان ڪندا آھن ۽ جيڪي زمين ۾ آھن تن لاءِ بخشش گھرندا آھن، خبردار بيشڪ الله ئي بخشڻھار مھربان آھي.'),
(4278, 42, 6, '۽ جن اُن کانسواءِ ٻيا دوست ورتا آھن تن تي الله نگھبان آھي، ۽ تون مٿن ڪو ذمه وار نه آھين.'),
(4279, 42, 7, '۽ اھڙيءَ طرح توڏانھن عربيءَ (ٻوليءَ) ۾ قرآن وحي ڪيوسون ته مڪّي وارن ۽ سندس آس پاس وارن کي ڊيڄارين ۽ قيامت جو ڏينھن جنھن ۾ ڪو شڪ نه آھي تنھن کان (پڻ) ڊيڄارين، ھڪ ٽولي بھشت ۾ ۽ ٻي ٽولي دوزخ ۾ ھوندي.'),
(4280, 42, 8, '۽ جيڪڏھن الله گھُري ھا ته اُنھن سڀني کي ھڪ ٽولي ڪري ھا پر جنھن کي گھري تنھن کي پنھنجي ٻاجھ ۾ داخل ڪندو آھي، ۽ ظالمن جو نڪي ڪو دوست ۽ نڪي ڪو مددگار آھي.'),
(4281, 42, 9, 'الله کانسواءِ ٻيا ڪارساز ڪري ورتا اٿن ڇا؟ ڇوته الله ئي ڪارساز آھي ۽ اُھو ئي مُئن کي جياريندو آھي، ۽ اُھو سڀڪنھن شيء تي وس وارو آھي.'),
(4282, 42, 10, '۽ جنھن (ڳالھ) بابت تڪرار ڪيو اٿوَ سو ڪجھ به ھجي ته اُنھيءَ جو نبيرو الله ڏانھن (حوالي ڪيل) آھي، منھنجو پالڻھار اھو الله آھي مٿي ڀروسو ڪيم، ۽ ڏانھس موٽان ٿو.'),
(4283, 42, 11, 'آسمان ۽ زمين جو بڻائيندڙ آھي، اوھان جي جنس مان اوھان لاءِ زالون پيدا ڪيائين ۽ ڍورن (جي جنس) مان ڪيترائي ئي قِسم (بڻايائين)، انھي طرح سان اوھان کي پکيڙي ٿو، اُن جھڙي ڪا (به) شيءَ نه آھي، ۽ اُھو ٻڌندڙ ڏسندڙ آھي.'),
(4284, 42, 12, 'آسمانن ۽ زمين جون ڪنجيون کيس آھن، جنھن لاءِ گُھري (تنھن لاءِ) روزي ڪشادي ڪندو آھي ۽ (جنھن لاءِ گھُري تنھن لاءِ) تنگ ڪندو آھي، بيشڪ اُھو ھر شيء کي ڄاڻندڙ آھي.'),
(4285, 42, 13, 'اوھان لاءِ اھو دين مُقرّر ڪيائين جنھن (جي قائم ڪرڻ) جو نُوح کي حُڪم ڪيو ھوائين ۽ جيڪو توڏانھن به وحي ڪيوسين ۽ اُھو جنھن (جي قائم ڪرڻ) جو ابراھيم ۽ مُوسىٰ ۽ عيسىٰ کي حُڪم ڪيوسون ته دين کي قائم ڪريو ۽ منجھس ڌار ڌار نه ٿيو، مُشرڪن کي اُھا (ڳالھ) ڏکي لڳي جنھن ڏانھن کين سڏيندو آھين، الله جنھن کي گھُري (تنھن کي) پاڻ ڏانھن چونڊيندو آھي ۽ جيڪو (ڏانھس) ورندو آھي تنھن کي پاڻ ڏانھن ھدايت ڪندو آھي.'),
(4286, 42, 14, '۽ نه جُدا ٿيون (اُمتون) مگر بعد اُن جي جو آيو اُنھن وٽ علم از روءِ سرڪشي جي پاڻ ۾، ۽ جيڪڏھن تنھنجي پالڻھار کان مقرر مُدّت تائين (مھلت ڏيڻ جو) حڪم اڳي (جاري ٿيل) نه ھجي ھا ته ضرور سندن وچ ۾ (ھاڻي ئي) فيصلو ڪجي ھا، ۽ بيشڪ جي انھن (پيغمبرن) کان پوءِ ڪتاب جا وارث ڪيا ويا سي دين کان وڏي شڪ ۾ (پيل) آھن.'),
(4287, 42, 15, 'پوءِ (تون) انھيءَ (دين) ڏانھن سڏ، ۽ جئن توکي حُڪم ڪيو ويو آھي (تيئن) بيھڪ وٺ، ۽ سندن سَڌن جي تابعداري نه ڪر، ۽ چؤ ته جيڪو به ڪتاب الله لاٿو تنھن کي مڃيم، ۽ مون کي حُڪم ٿيل آھي ته اوھان جي وچ ۾ انصاف ڪريان، الله اسان جو پالڻھار ۽ اوھان جو پالڻھار آھي، اسان لاءِ اسان جا عمل ۽ اوھان لاءِ اوھان جا عمل آھن، اسان جي ۽ اوھان جي وچ ۾ ڪو تڪرار ڪونھي، الله اسان سڀني کي (قيامت جي) ڏينھن گڏ ڪندو، ۽ ڏانھس موٽڻ آھي.'),
(4288, 42, 16, '۽ الله (جي دين) بابت سندس فرمان قبول ٿيڻ کانپوءِ جيڪو جھڳڙو ڪندا آھن تن جي حُجت سندن پالڻھار وٽ ڪوڙي (ٿيڻ واري) آھي ۽ مٿن (الله جو) ڏمر آھي ۽ اُنھن لاءِ سخت عذاب آھي.'),
(4289, 42, 17, 'الله اُھو آھي جنھن سچو ڪتاب ۽ (انصاف جي) ترازي لاٿي، توکي ڪھڙيءَ ڳالھ ڄاڻايو متان قيامت ويجھي ھجي.'),
(4290, 42, 18, 'جيڪي قيامت کي نه مڃيندا آھن سي اُن کي تڪڙو گھرندا آھن، ۽ جن ايمان آندو آھي سي اُن کان ڊڄندا آھن ۽ ڄاڻندا آھن ته بيشڪ اُھا سچ (اچڻي) آھي، خبردار بيشڪ جيڪي قيامت بابت جھڳڙو ڪندا آھن سي بيشڪ وڏيءَ گُمراھيءَ ۾ آھن.'),
(4291, 42, 19, 'الله پنھنجن ٻانھن تي مھربان آھي جنھن کي گھري تنھن کي روزي ڏيندو آھي، ۽ اُھو سگھارو غالب آھي.'),
(4292, 42, 20, 'جيڪو آخرت جي پوک گھُرندو آھي تنھن لاءِ سندس پوک ۾ واڌارو ڪندا آھيون، ۽ جيڪو دنيا جي پوک جو ارادو ڪندو آھي تنھنکي ڪجھ منجھانئس ڏيندا آھيون ۽ آخرت ۾ اُن جو ڪو حصّو نه آھي.'),
(4293, 42, 21, 'اُنھن (يعني ڪافرن) جا ڪي اھڙا شريڪ آھن ڇا جن اُنھن لاءِ دين مان (اُھو) نئون رستو بڻايو آھي جنھن جو الله حُڪم نه ڏنو آھي، ۽ جيڪڏھن فيصلي ڪرڻ جو وعدو (ٿيل) نه ھجي ھا ته ضرور (ھاڻي ئي) سندن وچ ۾ نبيرو ڪجي ھا، ۽ بيشڪ ظالمن لاءِ ئي ڏکوئيندڙ عذاب آھي.'),
(4294, 42, 22, '(اي پيغمبر) ظالمن جيڪي ڪمايو آھي تنھن (جي عذاب) سببان کين ڊڄندڙ ڏسندين ۽ اُھو (عذاب) کين پھچندڙ آھي، ۽ جن ايمان آندو ۽ چڱا ڪم ڪيا سي بھشتن جي باغن ۾ ھوندا، جيڪي گھرندا سو سندن پالڻھار وٽ اُنھن لاءِ (موجود) آھي، اِھوئي وڏو فضل آھي.'),
(4295, 42, 23, 'اِھو (ثواب) اُھو آھي جنھن جي الله پنھنجن اُنھن ٻانھن کي خوشخبري ڏيندو آھي جن ايمان آندو آھي ۽ چڱا ڪم ڪيا آھن، (اي پيغمبر کين) چؤ ته آءٌ اوھان کان اُن (پيغام پھچائڻ) تي ڪو اُجورو نه ٿو گھُران پر (جڳائي ته) مائٽن (جي وچ) ۾ محبت (رکو)، ۽ جيڪو چڱائي ڪمائيندو تنھن لاءِ اُن ۾ چڱائي وڌائينداسون، ڇوته الله بخشڻھار قدر ڄاڻڻ وارو آھي.'),
(4296, 42, 24, 'چوندا آھن ڇا ته (پيغمبر پاڻ) الله تي ڪوڙ ٺاھيو آھي، پوءِ جيڪڏھن الله گھري ته تنھنجيءَ دل تي مُھر ھڻي، ۽ الله ڪُوڙ کي ناس ڪندو آھي ۽ پنھنجن سُخنن سان سچي دين کي ثابت ڪندو آھي، بيشڪ اُھو سينن وارو (ڳجھ) ڄاڻندڙ آھي.'),
(4297, 42, 25, '۽ اُھو آھي جو پنھنجن ٻانھن جي توبه قبول ڪندو آھي ۽ مداين کان ٽارو ڪندو آھي ۽ جيڪي ڪندا آھيو سو ڄاڻندو آھي.'),
(4298, 42, 26, '۽ جن ايمان آندو آھي ۽ چڱا ڪم ڪيا آھن تن جي دُعا قبول ڪندو آھي ۽ پنھنجي فضل سان کين وڌيڪ (به) ڏيندو آھي، ۽ ڪافرن لاءِ ئي سخت سزا آھي.'),
(4299, 42, 27, '۽ جيڪڏھن الله پنھنجن ٻانھن جي روزي ڪشادي ڪري ھا ته مُلڪ ۾ ضرور بگيڙو وجھن ھا پر جيترو (پاڻ) گھرندو آھي (اوترو) نازل ڪندو آھي، ڇوته اُھو پنھنجن ٻانھن کي ڄاڻندڙ ڏسندڙ آھي.'),
(4300, 42, 28, '۽ (الله) اُھو آھي جو (ماڻھن جي) نا اميد ٿيڻ کان پوءِ مينھن ھيٺ موڪليندو آھي ۽ پنھنجي رحمت پکيڙيندو آھي، اُھو (ئي) ڪارساز ساراھيل آھي.'),
(4301, 42, 29, '۽ آسمانن ۽ زمين جو بڻائڻ ۽ جيڪي سندن وچ ۾ چرندڙ پکيڙيائين تن جو (بڻائڻ) سنديس نشانين مان آھي، ۽ اُھو جڏھن گھرندو (تڏھن) سندن گڏ ڪرڻ تي وس وارو آھي.'),
(4302, 42, 30, '۽ جيڪا به اوکائي اوھان کي پھچندي آھي سا اوھان جي ھٿن جي ڪمائي سببان آھي ۽ گھڻن (گناھن) کان (ته خُدا) در گذر ڪندو آھي.'),
(4303, 42, 31, '۽ اوھين زمين ۾ (ڀڄي) عاجز ڪرڻ وارا نه آھيو، ۽ الله کانسواءِ اوھان جو نڪو ڪارساز ۽ نڪو مددگار آھي.'),
(4304, 42, 32, '۽ سنديس نشانين مان درياءَ ۾ جبلن وانگر جھاز (ھلندا) آھن.'),
(4305, 42, 33, 'جيڪڏھن (خدا) گھري ته وائن کي جھلي پوءِ ٻيڙا درياءَ جي پٺي تي (ھلڻ کان) بيھي رھن، بيشڪ ان ۾ سڀڪنھن صبر ڪندڙ لاءِ نشانيون آھن.'),
(4306, 42, 34, 'يا ھلاڪ ڪرين تنھن سببان جو ڪمايائون ۽ عفو ٿو ڪري گھڻن کان.'),
(4307, 42, 35, '۽ ته جيڪي اسان جي آيتن بابت جھڳڙو ڪندا آھن سي ڄاڻن ته انھن لاءِ ڇوٽڪارو نه آھي.'),
(4308, 42, 36, 'پوءِ جيڪي اوھان کي ڏنو ويو آھي سو ڪجھ به ھجي ته اُھو دنيوي حياتيءَ جو (ٿورو) سامان آھي، ۽ جيڪي الله وٽ آھي سو ڀَلو ۽ ھميشه رھڻ وارو انھن لاءِ آھي جن ايمان آندو آھي ۽ پنھنجي رب تي ڀروسو ڪندا آھن.'),
(4309, 42, 37, '۽ (اُنھن لاءِ به) جيڪي وڏن گناھن ۽ بي حيائين کان پاسو ڪن ٿا ۽ جيڪڏھن ڪاوڙبا آھن (تڏھن) اُھي بخشيندا آھن.'),
(4310, 42, 38, '۽ جن پنھنجي پالڻھار جو حُڪم مڃيو ۽ نماز پڙھي، ۽ سندن ڪم پاڻ ۾ صلاح سان ھوندو آھي، ۽ جيڪي کين رزق ڏنوسون تنھن مان خرچيندا آھن.'),
(4311, 42, 39, '۽ اُھي (اھڙا آھن) جو جڏھن کين ڪو ظلم پھچي (تڏھن مظلوم جو مناسب) بدلو وٺندا آھن.'),
(4312, 42, 40, '۽ مدائيءَ جو بدلو اُھڙي مدائي آھي، پوءِ جيڪو معاف ڪري ۽ صُلح ڪري تنھن جو اجر الله تي آھي، بيشڪ الله ظالمن کي دوست نه رکندو آھي.'),
(4313, 42, 41, '۽ بيشڪ جيڪي پنھنجي ايذائجڻ کانپوءِ وير وٺن تن تي ڪا (ڏوراپي جي) واٽ ڪانه آھي.'),
(4314, 42, 42, '(ڏوراپي جي) واٽ رڳو انھن تي آھي جيڪي ماڻھن کي ايذائيندا آھن ۽ مُلڪ ۾ ناحق فساد وجھندا آھن، اُنھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(4315, 42, 43, '۽ البت جيڪو صبر ڪري ۽ بخشي ته بيشڪ اھو (عفو) ھمّت جي ڪمن مان آھي.'),
(4316, 42, 44, '۽ جنھن کي الله گُمراھ ڪري تنھن جو اُن کانپوءِ ڪو ڀرجھلو ڪونھي، ۽ (تون) ظالمن کي ڏسندين ته جڏھن (اُھي) سزا ڏسندا (تڏھن) چوندا ته (ھاڻي دنيا ۾) ورڻ جي ڪا واھ آھي ڇا؟'),
(4317, 42, 45, '۽ ڏسندين تون اُنھن کي جو پيش ڪيا ويندا باھ تي عاجزي ڪندڙ خواريءَ کان اک ڏٻي ڪيون پيا ڏسندا، ۽ مؤمن چوندا ته بيشڪ ڇيئي وارا اُھي آھن جن پاڻ کي ۽ پنھنجن گھر وارن کي قيامت جي ڏينھن ڇيئو لاتو، خبردار بيشڪ ظالم ھميشه جي عذاب ۾ ھوندا.'),
(4318, 42, 46, '۽ الله کانسواءِ اُنھن جا ڪي ڀر جھلا نه ھوندا جي کين مدد ڏين، ۽ جنھن کي الله گُمراھ ڪري تنھن لاءِ ڪا واٽ نه آھي.'),
(4319, 42, 47, 'پنھنجي پالڻھار جو حُڪم اُن ڏينھن (جي اچڻ) کان اڳ قبول ڪريو جنھن کي الله (جي پار) کان ٽَرڻو نه آھي، اُن ڏينھن نڪا اوھان کي واھ آھي ۽ نه ھوندو اوھان لاءِ ڪو انڪار.'),
(4320, 42, 48, 'پوءِ جيڪڏھن منھن موڙين ته توکي مٿن نگھبان ڪري نه موڪليو اٿون، توتي پيغام پھچائڻ کانسواءِ (ٻيو) ڪي لازم نه آھي، ۽ بيشڪ اسين جڏھن ماڻھوءَ کي پاڻ وٽان ٻاجھ (جو مزو) چکائيندا آھيون (تڏھن) اُن سان خُوش ٿيندو آھي، ۽ جيڪڏھن ڪا اوکائي سندن ھٿن جي اڳ ڪئي سببان کين پھچندي آھي ته بيشڪ ماڻھو بي شڪر (ٿيندو) آھي.'),
(4321, 42, 49, 'آسمانن ۽ زمين جي بادشاھي خاص الله جي آھي، جيڪي گھري (سو) خلقيندو آھي، جنھن کي وڻيس (تنھن کي) ڌيئرون بخشيندو آھي ۽ جنھن کي وڻيس (تنھن کي) پُٽ بخشيندو آھي.'),
(4322, 42, 50, 'يا کين پُٽ ۽ ڌيئرون گڏ ڏيندو آھي، ۽ جنھن کي وڻيس (تنھن کي) سَنڍ ڪندو آھي، بيشڪ اُھو ڄاڻندڙ وس وارو آھي.'),
(4323, 42, 51, '۽ ڪنھن ماڻھوءِ لاءِ ٿيڻو ئي نه آھي ته الله ساڻس ڳالھائي سواءِ ھن جي ته الھام سان يا پردي جي پويان يا ڪنھن قاصد موڪلڻ سان (ڳالھائي) پوءِ جيڪي الله گھريو سو سندس حُڪم سان پھچائي ڏي، بيشڪ اُھو (سڀ کان) مٿاھون حڪمت وارو آھي.'),
(4324, 42, 52, '۽ اھڙيءَ طرح اسان پنھنجي ڪلام سان توڏانھن قرآن وحي ڪيو، (اڳ) تون نه ڄاڻندو ھئين ته ڪتاب ڇا آھي ۽ نڪي (ڄاڻندو ھئين ته) ايمان (ڇا آھي) پر قُرآن کي نُور ڪيو اٿون پنھنجن ٻانھن مان جنھن کي گھرون (تنھن کي) اُن سان سڌو رستو ڏيکاريندا آھيون، ۽ بيشڪ تون سڌي رستي ڏانھن ھدايت ڪندو آھين.'),
(4325, 42, 53, '(يعني) اُنھي الله جو رستو جو جيڪي آسمانن ۽ زمين ۾ آھي سو سندس آھي، خبردار الله ڏانھن (ئي سڀ) ڪم وري ويندا آھن.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حٰمٓ.'),
(4327, 43, 2, 'پڌري ڪتاب جو قسم آھي.'),
(4328, 43, 3, 'ته بيشڪ اسان ھن (ڪتاب) کي عربيءَ (ٻوليءَ ۾) قرآن ڪيو آھي ته مانَ اوھين سمجھو.'),
(4329, 43, 4, '۽ بيشڪ اُھو (ڪتاب) لَوح مَحۡفوظ ۾ اسان وٽ (لکيل) آھي بيشڪ (اُھو ڪتاب) وڏي مرتبي وارو حڪمت وارو آھي.'),
(4330, 43, 5, 'اوھان کان نصيحت کي ھن ڪري مُنھن موڙي جھلينداسون ڇا ته اوھين حد کان لنگھيل قوم آھيو.'),
(4331, 43, 6, '۽ اڳين (قومن) ۾ گھڻائي پيغمبر موڪلياسون.'),
(4332, 43, 7, '۽ وٽن ڪوبه پيغمبر نه ٿي آيو مگر ساڻس ٺـٺوليون ٿي ڪيائون.'),
(4333, 43, 8, 'پوءِ اُنھن (قريشن) کان سگھ ۾ تمام ڏاڍن (عاد ۽ ثمود) کي (جو کائن بلڪل سگھارا ھوا) ناس ڪياسون ۽ اڳين جي حالت (اڳين بيان ۾) گذري چڪي آھي.'),
(4334, 43, 9, '۽ جيڪڏھن کائن پُڇين ٿه آسمانن ۽ زمين کي ڪنھن بڻايو ته ضرور چوندا ته انھن کي غالب ڄاڻندڙ (الله) بڻايو.'),
(4335, 43, 10, '(اُنھيءَ الله) جنھن اوھان لاءِ زمين کي وڇاڻو بڻايو ۽ اوھان لاءِ منجھس رستا بڻايا ته مانَ اوھين سِڌو رستو لھو.'),
(4336, 43, 11, '۽ (اُنھيءَ الله) جنھن آسمان مان اندازي سان پاڻي وسايو، پوءِ اُن (پاڻيءَ) سان ويران ڳوٺ آباد ڪياسون، اھڙي طرح اوھان کي (قبرن مان) ڪڍيو ويندو.'),
(4337, 43, 12, '۽ (اُنھيءَ الله) جنھن ھر قسم جا جانور بڻايائين ۽ اوھان لاءِ ٻيڙيون ۽ اُھي جانور بڻايائين جن تي (اوھين) چڙھو.'),
(4338, 43, 13, 'ته اُنھن جي پُٺين تي سِڌا ٿي ويھو وري جڏھن مٿن سنوان ٿي ويھو (تڏھن) پنھنجي پالڻھار جي نعمت ياد ڪريو ۽ چئو ته اُھو (الله) پاڪ آھي جنھن ھيءُ اسان کي نِوائي ڏنو ۽ (اسين) اُن جي نوائڻ جي (ڪڏھن) طاقت رکندڙ نه ھواسون.'),
(4339, 43, 14, '۽ بيشڪ اسين پنھنجي پالڻھار ڏانھن موٽڻ وارا آھيون.'),
(4340, 43, 15, '۽ الله لاءِ سندس ٻانھن مان اولاد مقرر ڪيائون، بيشڪ ماڻھو پڌرو بي شُڪر آھي.'),
(4341, 43, 16, '(الله) پنھنجي مخلوق مان (پاڻ لاءِ) ڌيئرون اختيار ڪيون ڇا ۽ اوھان کي پٽن سان چونڊيو اٿس (ڇا)؟'),
(4342, 43, 17, '۽ جڏھن منجھائن ڪنھنکي اُنھيءَ شيءَ (يعني ڌيءُ جي ڄمڻ) جي خبر ڏجي جيڪا ٻاجھاري (الله) لاءِ مثال بيان ڪئي اٿس (تڏھن) سندن منھن ڪاراٽجي ويندو آھي ۽ اُھو ڏک ڀريو ھوندو آھي.'),
(4343, 43, 18, 'جيڪو ڳھن ۾ پالجي ٿو ۽ اُھو جھڳڙي ۾ (پنھنجي ڳالھ کي) پڌرو ڪرڻ وارو نه آھي.'),
(4344, 43, 19, '۽ ملائڪن کي (الله جون) ڌيئرون مُقرّر ڪيون اٿن جي (پاڻ) الله جا ٻانھا آھن، (اھي) سندن پيدا ٿيڻ وقت حاضر ھوا ڇا؟ سندين (اھا) شاھدي سِگھو لکبي ۽ (ھُو قيامت ۾) پُڇيا ويندا.'),
(4345, 43, 20, '۽ چوندا آھن ته جيڪڏھن ٻاجھارو (الله) گھري ھا ته انھن (ملائڪن) جي عبادت نه ڪريون ھا کين ھن (ڳالھ) جي ڪا ڄاڻپ نه آھي، اُھي رڳو اٽڪل ھڻندا آھن.'),
(4346, 43, 21, 'کين ھن (قرآن) کان اڳ ڪو ڪتاب ڏنو اٿون ڇا جو اُنھن اُن کي چنبڙي ورتو آھي.'),
(4347, 43, 22, '(نه!) بلڪ چوندا آھن ته بيشڪ اسان پنھنجن ابن ڏاڏن کي ھڪ مذھب تي ڏٺو ۽ بيشڪ اسين سندن پيرن تي رستي لھڻ وارا آھيون.'),
(4348, 43, 23, '۽ اھڙي طرح توکان اڳ ڪنھن ڳوٺ ۾ ڪو ڊيڄاريندڙ نه موڪليوسون پر اُن جي آسودن (ماڻھن) چيو ته اسان پنھنجا پيءُ ڏاڏا ھڪ دين تي ڏٺا ۽ بيشڪ اسين انھن (ئي) جي پيرن تي پيروي ڪندڙ آھيون.'),
(4349, 43, 24, '(پيغمبر کين) چيو ته (پنھنجن ابن ڏاڏن جي پيروي ڪندؤ) ڇا؟ جيتوڻيڪ اوھان وٽ اُھو (دين) آندو اٿم جنھن (دين) تي اوھان پنھنجن پين ڏاڏن کي ڏٺو تنھن کان وڌيڪ سڌو رستو ڏيکارڻ وارو آھي، چيائون ته جنھن شيء سان اوھان کي موڪليو ويو آھي تنھنجا اسين مُنڪر آھيون.'),
(4350, 43, 25, 'پوءِ کائن بدلو ورتوسون پوءِ ڏس ته ڪوڙن جي پڇاڙي ڪئن ٿي.'),
(4351, 43, 26, '۽ (ياد ڪر) جڏھن ابراھيم پنھنجي پيءُ ۽ پنھنجي قوم کي چيو ته جنھن شيء کي (اوھين) پوڄيندا آھيو تنھن کان بيشڪ آءٌ بيزار آھيان.'),
(4352, 43, 27, 'پر جنھن مون کي پيدا ڪيو سو بيشڪ مون کي سگھوئي ھدايت ڪندو.'),
(4353, 43, 28, '۽ الله سندس اولاد ۾ اِھا (توحيد جي ڪلمي جي) ڳالھ ھميشه رھائي ته مانَ اُھي (ڪافر) موٽن.'),
(4354, 43, 29, 'بلڪ مان انھن ۽ سندن ابن ڏاڏن کي آسودو ڪيو تان جو وٽن سچّو دين ۽ پڌرو پيغمبر (ﷺ) آيو.'),
(4355, 43, 30, '۽ جنھن مھل سچو دين وٽن آيو (تنھن مھل) چيائون ته ھيءَ جادو آھي ۽ بيشڪ اسين اُن جا مُنڪر آھيون.'),
(4356, 43, 31, '۽ چيائون ته (ھنن) ٻن ڳوٺن وارن مان ڪنھن سردار مڙس تي ھيءُ قرآن ڇونه نازل ڪيو ويو.'),
(4357, 43, 32, 'اِھي تنھنجي پالڻھار جي رحمت وراھيندا آھن ڇا؟ اسان سندن گذران سندن وچ ۾ دنيا جي حياتيءَ ۾ وراھيو آھي ۽ اُنھن مان ھڪڙن کي ٻـين کان وڏي مرتبي وارو ڪيوسون ته ھڪڙا ٻـين کي فرمانبردار ڪري وٺن، ۽ جيڪو (دنيا جو مال) گڏ ڪندا آھن تنھن کان تنھنجي پالڻھار جي رحمت ڀلي آھي.'),
(4358, 43, 33, '۽ جيڪڏھن ائين نه ھجي ھا ته (متان) ماڻھو ھڪ ٽولي (ڪفر جي) ٿي وڃن جيڪي ٻاجھاري (الله) جو انڪار ڪندا آھن تن لاءِ سندن گھرن جون ڇتيون ۽ ڏاڪڻيون جن تي چڙھن سي روپيون ڪريون ھا.'),
(4359, 43, 34, '۽ سندن گھرن جا دروازا ۽ تخت جن تي ٽيڪ ڏيئي ويھن (سي به روپا ڪريون ھا).'),
(4360, 43, 35, '۽ اِھو سڀ رڳو دنيا جي حياتيءَ جو (ٿورو) آسودو رھڻ آھي، ۽ تنھنجي پروردگار وٽ آخرت پرھيزگارن لاءِ آھي.'),
(4361, 43, 36, '۽ جيڪو ٻاجھاري (الله) جي ياد ڪرڻ کان غافل رھندو آھي تنھن لاءِ ھڪ شيطان مقرر ڪندا آھيون جو اُھو اُن جو سنگتي ھوندو آھي.'),
(4362, 43, 37, '۽ بيشڪ اُھي شيطان سڌي واٽ کان اُنھن کي جھليندا آھن ۽ (ماڻھو پاڻ کي) ڀانئيندا آھن ته اُھي ھدايت وارا آھن.'),
(4363, 43, 38, 'تانجو جڏھن اسان وٽ ايندو (تڏھن پنھنجيءَ سنگتي شيطان کي) چوندو ته ھيء افسوس (جيڪر) منھنجي ۽ تنھنجي وچ ۾ اوڀر ۽ اولھ جيتري ڏورائي ھجي ھا پوءِ بڇڙو سنگتي آھين (تون).'),
(4364, 43, 39, '۽ ھرگز نه نفعو ڏيندي اوھان کي اڄ اھا سڌ جڏھن ظُلم ڪيو اوھان تحقيق اوھان عذاب ۾ شريڪ ٿيڻ وارا آھيو.'),
(4365, 43, 40, '(اي پيغمبر) تون ٻوڙن کي ٻڌائي ٿو سگھين ڇا يا انڌن کي ۽ اُھو جيڪو پڌريءَ گُمراھيءَ ۾ آھي تنھن کي ھدايت ڪري سگھين ٿو ڇا؟'),
(4366, 43, 41, 'پوءِ جيڪڏھن توکي (ھن جھان مان) نيون ته بيشڪ اسين (اتي ئي) کائن بدلو وٺڻ وارا آھيون.'),
(4367, 43, 42, 'يا جيڪو کين انجام ڏنو اٿون سو توکي ڏيکاريون ته به بيشڪ اسين مٿن وسوارا آھيون.'),
(4368, 43, 43, 'تنھن ڪري جيڪي توڏانھن وحي ڪيو ويو تنھن کي تون چنبڙي وٺ، بيشڪ تون سڌيءَ واٽ تي آھين.'),
(4369, 43, 44, '۽ بيشڪ اِھو (قرآن) تولاءِ ۽ تنھنجي قوم لاءِ نصيحت آھي، ۽ (اوھين) سگھوئي پڇيا ويندؤ.'),
(4370, 43, 45, '۽ پنھنجن پيغمبرن مان جيڪي توکان اڳ موڪليا ھواسون تن جو (احوال) پُڇ، ته ٻاجھاري (الله) کانسواءِ (ڪي) ٻيا معبود بڻايا ھواسون ڇا جن جي پوڄا ڪئي وڃي؟'),
(4371, 43, 46, '۽ بيشڪ مُوسىٰ کي پنھنجين نشانين سان فرعون ۽ سندس سردارن ڏانھن موڪليوسون پوءِ چيائين ته بيشڪ آءٌ جھانن جي پالڻھار جو موڪليل آھيان.'),
(4372, 43, 47, 'پوءِ جنھن مھل وٽن اسان جي نشانين سان آيو (تنھن مھل) اوچتو اُھي اُنھن (معجزن) کان کلندا رھيا.'),
(4373, 43, 48, '۽ ڪابه نشاني کين نه ٿي ڏيکاري سون پر اِھا (نشاني) اُن جي اڳين کان تمام وڏي ھئي، ۽ کين عذاب سان پڪڙيوسون ته مانَ اُھي (ڪفر کان) موٽن.'),
(4374, 43, 49, '۽ چيائون ته اي جادوگر پنھنجي پالڻھار کان اسان لاءِ اُنھيءَ (وسيلي) سان دُعا گھر جنھن جو توسان انجام ڪيو اٿس، ته بيشڪ اسين ھدايت وارا ٿينداسون.'),
(4375, 43, 50, 'پوءِ جنھن مھل کائن عذاب لاٿوسون اوچتو اُنھن عھد ڀڳو.'),
(4376, 43, 51, '۽ فرعون پنھنجي قوم ۾ پڙھو ڏنو چيائين ته اي منھنجي قوم مِصۡر جي بادشاھي منھنجي نه آھي ڇا؟ ۽ ھي نھرون منھنجي (ماڙين جي) ھيٺان (نه) وھنديون آھن (ڇا؟) اوھين نه ڏسندا آھيو ڇا؟'),
(4377, 43, 52, '(نه!) بلڪ آءٌ ھن (مُوسى) کان ڀَلو آھيان جو اُھو خوار آھي، ۽ چٽو ڳالھائي به نه ٿو سگھي.'),
(4378, 43, 53, 'پوءِ مٿس سون جا ڪنگڻ ڇونه لاٿا ويا يا ساڻس گڏ ٿي ملائڪ ڇونه آيا؟'),
(4379, 43, 54, 'پوءِ پنھنجي قوم کي بي عقل ڪيائين تنھنڪري سندس فرمانبرداري ڪيائون، بيشڪ اُھي بي دين قوم ھوا.'),
(4380, 43, 55, 'پوءِ جڏھن اسان کي ڪاوڙايائون (تڏھن) کائن وير ورتوسون پوءِ اُنھن سڀني کي ٻوڙيوسون.'),
(4381, 43, 56, 'پوءِ کين (ڪافرن جو) اڳواڻ ۽ پوين لاءِ آکاڻي ڪيوسون.'),
(4382, 43, 57, '۽ جڏھن مريم جي پُٽ جو قصّو بيان ڪيو ويو (تڏھن) اُمالڪ اُن (قِصّي) کان تنھنجي قوم لُڙ مچائيندي آھي.'),
(4383, 43, 58, '۽ (اُھي) چوندا آھن ته اسان جا معبُود ڀلا آھن يا عيسى؟ اِھو مثال توسان رڳو جھيڙي لاءِ بيان ڪندا آھن، بلڪ اُھي جھيڙاڪ قوم آھن.'),
(4384, 43, 59, 'عيسىٰ رڳو ٻانھو آھي جنھن تي ٻاجھ ڪئي اٿون ۽ بني اسرائيلن لاءِ کيس (ھڪ) عبرت ڪئي سون.'),
(4385, 43, 60, '۽ جيڪڏھن گھرون ھا ته اوھان جي بدران زمين ۾ ملائڪن کي نائب ڪريون ھا.'),
(4386, 43, 61, '۽ بيشڪ عيسىٰ قيامت جي ھڪ نشاني آھي پوءِ اوھين قيامت ۾ شڪ نه ڪريو ۽ (اي پيغمبر چؤ ته) منھنجي تابعداري ڪريو (جو) اِھا سِڌي واٽ آھي.'),
(4387, 43, 62, '۽ اوھان کي شيطان نه جھلي، ڇوته اھو اوھان جو پڌرو ويري آھي.'),
(4388, 43, 63, '۽ جنھن مھل عيسىٰ چٽن معجزن سان آيو (تنھن مھل) چيائين ته اوھان وٽ حڪمت آندي اٿم ۽ (آيو آھيان) ته جن ڳالھين ۾ اختلاف ڪند آھيو تن مان ڪي اوھان لاءِ بيان ڪريان، پوءِ الله کان ڊڄو ۽ منھنجو چيو مڃيو.'),
(4389, 43, 64, 'بيشڪ الله ئي منھنجو پالڻھار ۽ اوھان جو پالڻھار آھي تنھنڪري سنديس عبادت ڪريو، اِھا سِڌي واٽ آھي.'),
(4390, 43, 65, 'پوءِ ٽولين پاڻ ۾ اختلاف ڪيو، تنھنڪري ڏکوئيندڙ ڏينھن جي عذاب کان ظالمن لاءِ خرابي آھي.'),
(4391, 43, 66, '(اُھي) رڳو قيامت (جي اچڻ) جو انتظار ڪندا آھن ته اوچتو وٽن اچي ۽ اُھي بي خبر ھجن.'),
(4392, 43, 67, 'اُنھيءَ ڏينھن پرھيزگارن کانسواءِ (ٻيا) سڀئي دوست ھڪ ٻئي جا دشمن ھوندا.'),
(4393, 43, 68, '(چيو ويندو ته) اي منھنجا (اُھي) ٻانھا اڄ اوھان کي نڪي ڀؤ آھي نڪي اوھين غمگين ٿيو.'),
(4394, 43, 69, 'جن اسان جي آيتن تي ايمان آندو ۽ مُسلمان ھوا.'),
(4395, 43, 70, '(چيو ويندو ته) اوھين ۽ اوھان جون زالون بھشت ۾ خُوش ٿي گھِڙو.'),
(4396, 43, 71, 'مٿن سون جون رڪيبيون ۽ آبخورا ڦيرائبا، ۽ جيڪي دليون گھرنديون ۽ (جنھن جي ڏسڻ کان) اکيون ٺرنديون سو منجھس ھوندو، اوھين منجھس سدائين رھندؤ.'),
(4397, 43, 72, '۽ ھي اُھو بھشت آھي جنھن جو اوھان کي انھي سببان وارث ڪيو ويو جو اوھين (چڱا) ڪم ڪندا ھيؤ.'),
(4398, 43, 73, 'منجھس اوھان لاءِ گھڻا ميوا آھن جن منجھان پيا ٿا کائو.'),
(4399, 43, 74, 'بيشڪ ڏوھي دوزخ جي عذاب ۾ سدائين رھڻ وارا آھن.'),
(4400, 43, 75, 'کائن عذاب ھلڪو نه ڪبو ۽ اُھي منجھس نا اُميد (ٿي) رھندا.'),
(4401, 43, 76, '۽ مٿن ظلم نه ڪيوسون پر اُھي پاڻ ظالم ھوا.'),
(4402, 43, 77, '۽ (دوزخ جي دربان کي) سڏيندا ته اي مالڪ جڳائي ته تنھنجو پالڻھار اسان تي (موت جو) حڪم ڪري (ته عذاب کان ڇُٽون، مالڪ) چوندو ته بيشڪ اوھين ھميشه رھڻ وارا آھيو.'),
(4403, 43, 78, 'بيشڪ اوھان وٽ سچو دين آندوسون پر اوھان مان گھڻا سچي ڳالھ کي ناپسند ڪرڻ وارا آھن.'),
(4404, 43, 79, '(ڪافرن به) ڪنھن ڪم جي رِٿ رٿي آھي ڇا؟ پوءِ بيشڪ اسين به پڪي رِٿ رٿيندا آھيون.'),
(4405, 43, 80, 'ڀائيندا آھن ڇا ته اسين سندن ڳجھيون ڳالھيون ۽ سندن صلاحون نه ٻڌندا آھيون؟ ھائو (ٻڌندا آھيون) ۽ اسان جا موڪليل ملائڪ وٽن لکندا آھن.'),
(4406, 43, 81, '(تون) چؤ ته جيڪڏھن ٻاجھاري (الله) کي ڪو وياءُ ھجي، ته آءٌ (سڀ کان) پھرين (اُن جي) عبادت ڪندڙ ھجان.'),
(4407, 43, 82, '(اِھي) جيڪي بيان ڪندا آھن تنھن کان آسمانن ۽ زمين جو پالڻھار عرش جو مالڪ پاڪ آھي.'),
(4408, 43, 83, 'پوءِ کين ڇڏ ته (ايسين ڪوڙين ڳالھين ۾) بڪ ڪن ۽ راند ڪن جيسين پنھنجي اُنھي ڏينھن کي ملن جنھنجو کين انجام ڏجي ٿو.'),
(4409, 43, 84, '۽ (الله) اُھو آھي جيڪو آسمانن ۾ معبود آھي ۽ زمين ۾ (به) معبود آھي، ۽ اُھو حڪمت وارو ڄاڻندڙ آھي.'),
(4410, 43, 85, '۽ اُھو وڏي برڪت وارو آھي جنھن جي آسمانن ۽ زمين جي ۽ جيڪي اُنھن جي وچ ۾ آھي تنھن جي بادشاھت آھي، ۽ وٽس ئي قيامت (جي اچڻ) جي خبر آھي، ۽ ڏانھنس موٽائبؤ.'),
(4411, 43, 86, '۽ (ڪافر) جن (بُتن) کي الله کانسواءِ سڏيندا آھن سي شفاعت ڪري نه سگھندا آھن پر اُھي جن (دين جي) سچي شاھدي ڏني ھوندي ۽ اُھي (اُن جي حقيقت) ڄاڻندا آھن (اُھي شفاعت ڪندا).'),
(4412, 43, 87, '۽ جيڪڏھن کائن پڇين ته کين ڪنھن خلقيو ته ضرور چوندا ته الله (خلقيو آھي) پوءِ ڪيڏانھن ڦيرايا ويندا آھن.'),
(4413, 43, 88, '۽ (قسم آھي) پيغمبر جي انھي چوڻ تي ته اي منھنجا پالڻھار بيشڪ ھيءَ اھڙي قوم آھن جو (ڪنھن طرح) ايمان نه ٿا آڻين.'),
(4414, 43, 89, 'پوءِ کائن مُنھن موڙ ۽ (موڪلائيءَ جو) سلام چؤ، پوءِ سگھوئي ڄاڻندا.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حٰمٓ.'),
(4416, 44, 2, '(ھن) پڌريءَ ڪتاب جو قسم آھي.'),
(4417, 44, 3, 'ته بيشڪ اسان ھن (ڪتاب) کي برڪت واريءَ رات ۾ لاٿو بيشڪ اسين ڊيڄاريندڙ ھواسون.'),
(4418, 44, 4, 'اُن (برڪت واريءَ رات) ۾ سڀڪو ڪم حڪمت وارو فيصل ڪبو آھي.'),
(4419, 44, 5, '(اھو) پاڻ وٽان (پنھنجي) حڪم سان (لاٿوسون) بيشڪ اسين (ئي پيغمبر کي) موڪلڻ وارا ھواسون.'),
(4420, 44, 6, 'تنھنجي پالڻھار جي ٻاجھ سبب (اُن کي لاٿوسون)، بيشڪ اُھوئي ٻڌندڙ ڄاڻندڙ آھي.'),
(4421, 44, 7, '(جو) آسمانن ۽ زمين جو ۽ جيڪي انھن جي وچ ۾ آھي تنھنجو پالڻھار آھي، جيڪڏھن اوھين وسھندڙ آھيو (ته وسھو).'),
(4422, 44, 8, 'اُن کانسواءِ (ٻيو) ڪوبه عبادت جو لائق نه آھي (جو) جياريندو آھي ۽ ماريندو آھي (اُھوئي) اوھان جو پالڻھار ۽ اوھان جي اڳين پين ڏاڏن جو پالڻھار آھي.'),
(4423, 44, 9, 'بلڪ ڪافر (قيامت) کان شڪ ۾ آھن جو راند ڪندا آھن.'),
(4424, 44, 10, 'پوءِ جنھن ڏينھن آسمان پڌرو دونھون آڻيندو (تنھن ڏينھن جو) تون انتظار ڪڍ.'),
(4425, 44, 11, 'جو ماڻھن کي ويڙھي ويندو، اھو ڏکوئيندڙ عذاب آھي.'),
(4426, 44, 12, '(پوءِ چوندا ته) اي اسان جا پالڻھار اسان کان اِھو عذاب ٽار (جو) بيشڪ اسين مُسلمان ٿيون ٿا.'),
(4427, 44, 13, 'کين نصيحت وٺڻي ڪيئن ٿيندي حالانڪ بيشڪ وٽن پڌرو پيغمبر آيو ھو.'),
(4428, 44, 14, 'وري کانئس مُنھن موڙيائون ۽ چيائون (ٿي) ته (ھيءُ) ماڻھو سيکاريل (۽) چريو آھي.'),
(4429, 44, 15, 'بيشڪ اسين ٿورو (وقت) عذاب ٽارڻ وارا آھيون بيشڪ اوھين (وري ڪُفر ۾) ڦِرڻ وارا آھيو.'),
(4430, 44, 16, 'جنھن ڏينھن تمام وڏي پڪڙ ڪنداسون (تنھن ڏينھن) بيشڪ اسين بدلو وٺڻ وارا آھيون.'),
(4431, 44, 17, '۽ بيشڪ کائن اڳ فرعون جي قوم جي آزمائش ڪئي سون ۽ وٽن (ھن ڳالھ سان) سڳورو پيغمبر آيو.'),
(4432, 44, 18, 'ته الله جي ٻانھن کي منھنجي حوالي ڪريو، بيشڪ آءٌ اوھان وٽ امانت دار پيغمبر (آيو) آھيان.'),
(4433, 44, 19, '۽ (چوڻ آيو آھيان) ته الله جي آڏو ھٺ نه ڪريو، بيشڪ آءٌ اوھان وٽ پڌري حُجت آڻيندس.'),
(4434, 44, 20, '۽ بيشڪ مون پنھنجي پالڻھار ۽ اوھان جي پالڻھار جي پناھ ورتي ته (متان) مون کي پھڻن سان ماريو.'),
(4435, 44, 21, '۽ جيڪڏھن مون تي ايمان نه آڻيو ته مون کان پاسو ڪريو.'),
(4436, 44, 22, 'پوءِ پنھنجي پالڻھار کي سڏيائين ته ھيءَ ڏوھاري قوم آھن.'),
(4437, 44, 23, 'پوءِ (حُڪم ڪيوسون ته) منھنجن ٻانھن کي راتو رات وٺي نڪر جو اوھان جي پٺيان پوندا.'),
(4438, 44, 24, '۽ (تون) سمنڊ کي سڪل ڇڏ، بيشڪ اُھي ٻڏڻ وارا لشڪر آھن.'),
(4439, 44, 25, 'باغ ۽ تلاءَ گھڻائي ڇڏيائون.'),
(4440, 44, 26, '۽ پوکون ۽ خاصيون جايون (به).'),
(4441, 44, 27, '۽ اُھي نعمتون (به) جنھن ۾ مزا ماڻيندا ھوا.'),
(4442, 44, 28, 'اھڙي طرح (ٿيو)، ۽ ٻي قوم کي انھن (وسلن) جو وارث ڪيوسون.'),
(4443, 44, 29, 'پوءِ مٿن آسمان ۽ زمين نه رُنو ۽ نڪي اُنھن کي مُلت ڏني ويئي.'),
(4444, 44, 30, '۽ بيشڪ بني اسرائيلن کي خوار ڪندڙ عذاب کان بچايوسون.'),
(4445, 44, 31, 'جو فرعون کان (پھچندو) ھونِ، بيشڪ اُھو ھٺيلو حد کان لنگھندڙن مان ھو.'),
(4446, 44, 32, '۽ بيشڪ بني اسرائيلن کي ڄاڻي واڻي (ساري) جھان وارن کان پسند ڪيوسون.'),
(4447, 44, 33, '۽ کين اُھي مُعجزا ڏناسون جن ۾ پڌري آزمائش ھئي.'),
(4448, 44, 34, 'بيشڪ اُھي (مُشرڪ) چوندا آھن.'),
(4449, 44, 35, 'ته (اسان جي پڄاڻي) رڳو اسان جو (ھيءُ) پھريون موت آھي ۽ اسين وري نه اُٿارباسون.'),
(4450, 44, 36, 'جيڪڏھن اوھين سچا آھيو ته اسان جي ابن ڏاڏن کي (جياري) آڻيو.'),
(4451, 44, 37, 'اُھي ڀلا آھن يا تبع (حميري) جي قوم ۽ جيڪي کائن اڳ ھوا سي؟ اُنھن (سڀني) کي ناس ڪيوسون، بيشڪ اُھي ڏوھاري ھوا.'),
(4452, 44, 38, '۽ آسمان ۽ زمين کي ۽ جيڪي اُنھن جي وچ ۾ آھي سو راند ڪري نه بڻايوسون.'),
(4453, 44, 39, 'انھن کي پوريءَ رِٿ کانسواءِ نه بڻايوسون پر انھن مان گھڻا نه ڄاڻندا آھن.'),
(4454, 44, 40, 'بيشڪ انھن مڙني جو انجام نبيري جو ڏينھن آھي.'),
(4455, 44, 41, 'جنھن ڏينھن ڪوبه دوست ڪنھن دوست کان ڪجھ به (عذاب) نه ٽاريندو ۽ نڪي کين مدد ڏبي.'),
(4456, 44, 42, 'سواءِ اُنھيءَ جي جنھن تي الله رحم ڪيو، بيشڪ الله ئي غالب مھربان آھي.'),
(4457, 44, 43, 'بيشڪ ٿوھر جو وڻ.'),
(4458, 44, 44, 'گنھگار جو کاڄ آھي.'),
(4459, 44, 45, 'پگھريل ٽامي جان، پيٽن ۾ ٽچڪندو.'),
(4460, 44, 46, 'تَتل پاڻي جي ٽچڪڻ وانگر.'),
(4461, 44, 47, '(ملائڪن کي چونداسون ته) ھن (ڏوھيءَ کي پڪڙيو) ۽ دوزخ جي وچ ڏانھن گھليوس.'),
(4462, 44, 48, 'وري سندس مٿي تي تَتل پاڻي ھاريو جو (اِھو) عذاب (اٿس).'),
(4463, 44, 49, '(چئبس ته اِھا سزا) چَک ڇوته تون (پنھنجي گمان ۾) سگھورو مان وارو آھين.'),
(4464, 44, 50, 'بيشڪ ھيءُ اُھو عذاب آھي جنھن ۾ اوھين شڪ ڪندا ھئو.'),
(4465, 44, 51, 'بيشڪ پرھيزگار امن واريءَ جاءِ ۾ ھُوندا.'),
(4466, 44, 52, 'باغن ۽ تلاون ۾.'),
(4467, 44, 53, 'سنھي پَٽ جا (ڪپڙا) ۽ ٿُلھي پَٽ جا (ڪپڙا) ھڪ ٻئي جي آمھون سامھون ٿي ڍڪندا.'),
(4468, 44, 54, '(سندن حال) ائين ھوندو، ۽ کين وڏين (سھڻين) اکين واريون حورون پرڻائينداسون.'),
(4469, 44, 55, 'منجھس سڀڪو ميوو گھرندا اھڙي حال ۾ جو امن وارا ھوندا.'),
(4470, 44, 56, 'پھرين موت کانسواءِ (جو دنيا ۾ مري چڪا) اتي (ٻيو) موت نه چکندا، ۽ (الله) کين دوزخ جي عذاب کان بچايو.'),
(4471, 44, 57, 'تنھنجي پالڻھار جي فضل سبب، اھائي وڏي مراد ماڻڻ آھي.'),
(4472, 44, 58, 'قرآن کي تنھنجيءَ ٻوليءَ ۾ آسان ڪيوسون ته مانَ اُھي نصيحت وٺن.'),
(4473, 44, 59, 'پوءِ (تون) انتظار ڪڍ بيشڪ اُھي به انتظار ڪڍڻ وارا آھن.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حٰمٓ.'),
(4475, 45, 2, 'ڪتاب جو لاھڻ الله غالب حڪمت واري کان آھي.'),
(4476, 45, 3, 'بيشڪ آسمانن ۽ زمين ۾ ايمان وارن لاءِ پڪيون نشانيون آھن.'),
(4477, 45, 4, '۽ اوھان جي بڻائڻ ۾ ۽ جيڪي چرندڙن مان پکيڙي ٿو تنھن (جي بڻائڻ) ۾ اُنھن ماڻھن لاءِ نشانيون آھن جيڪي يقين رکندا آھن.'),
(4478, 45, 5, '۽ رات ۽ ڏينھن جي ڦيرگھير ۾ ۽ جيڪي الله آسمان کان روزي نازل ڪئي پوءِ اُن سببان زمين کي سندس غيرآباديءَ کانپوءِ آباد ڪيائين تنھن ۾ ۽ وائن جي ھلائڻ ۾ اُنھن ماڻھن لاءِ نشانيون آھن جيڪي سمجھ رکندا آھن.'),
(4479, 45, 6, 'اھي الله جون آيتون آھن جي توکي سچ سان پڙھي ٻڌايون ٿا، پوءِ الله ۽ سندس آيتن پڃاڻا ڪھڙي ڳالھ تي ايمان آڻيندا.'),
(4480, 45, 7, 'ھر ڪنھن ٻٽاڪيءَ گنھگار لاءِ خرابي آھي.'),
(4481, 45, 8, 'الله جون آيتون کيس پڙھي ٻڌائبيون آھن ته ٻڌندو آھي وري آڪڙجي ضد ڪندو آھي ڄڻڪ اُھي ٻڌيون ئي نه ھوائين، تنھنڪري کيس ڏکوئيندڙ عذاب جي خوشخبري سُڻاءِ.'),
(4482, 45, 9, '۽ جڏھن اسان جي آيتن مان ڪنھن ڳالھ تي واقف ٿيندو آھي (تڏھن) ان کي ٺـٺوليون ڪري وٺندو آھي، اُنھن ئي لاءِ خوار ڪندڙ عذاب آھي.'),
(4483, 45, 10, 'سندن ٻئي طرف کان دوزخ آھي، ۽ جيڪي ڪمايائون نڪي اُھو کائن ڪجھ ٽاري سگھندو ۽ نڪي اُھي جن کي الله ڌاران سڄڻ ڪري ورتائون (کائن ٽاري سگھندا)، ۽ اُنھن لاءِ وڏو عذاب آھي.'),
(4484, 45, 11, 'ھيءُ (قرآن) ھدايت آھي، ۽ جن پنھنجي پالڻھار جي آيتن جو انڪار ڪيو تن کي سخت سزا جو ڏکوئيندڙ عذاب آھي.'),
(4485, 45, 12, 'الله اُھو آھي جنھن درياءُ اوھان جي تابع ڪيو ته سندس حُڪم سان منجھس ٻيڙيون ھلن ۽ سندس فضل مان گذران طلب ڪريو ۽ مانَ اوھين شڪرانو ڪريو.'),
(4486, 45, 13, '۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو سڀئي پنھنجي پار کان اوھان جي ڪم ۾ لڳايائين، بيشڪ اُن ۾ اُنھن ماڻھن لاءِ نشانيون آھن جي پروڙيندا آھن.'),
(4487, 45, 14, 'مؤمنن کي چؤ ته جيڪي الله جي واقعن جي اُميد نه رکندا آھن تن کان ھن ڪري ٽارو ڪن ته اُنھي سببان جو جيڪي ڪمايو ھوائون (انھن مان) ڪنھن قوم کي (الله) سزا ڏئي.'),
(4488, 45, 15, 'جيڪو چڱا ڪم ڪندو سو پاڻ لاءِ، ۽ جيڪو مدائي ڪندو تنھن تي (اُن جو نقصان) آھي، وري پنھنجي پالڻھار ڏانھن موٽايا ويندؤ.'),
(4489, 45, 16, '۽ بيشڪ بني اسرائيلن کي ڪتاب ۽ دانائي ۽ پيغمبري ڏني سون ۽ سٺين شين مان کين روزي ڏني سون ۽ کين جھان وارن کان ڀلارو ڪيوسون.'),
(4490, 45, 17, '۽ دين بابت کين پڌرا دليل ڏناسون، پوءِ انھن (اڳي) اختلاف نه ڪيو پر وٽن ڄاڻپ آئي تنھن کانپوءِ پاڻ ۾ ساڙ (پيل) سببان (اختلاف ڪيائون)، بيشڪ جنھن ڳالھ بابت جھڳڙو ڪندا ھوا تنھن بابت قيامت جي ڏينھن تنھنجو پالڻھار سندن وچ ۾ نبيرو ڪندو.'),
(4491, 45, 18, 'وري (اي پيغمبر) توکي دين جي پڌريءَ واٽ تي ڪيوسون تنھنڪري (تون) اُن دين جي تابعداري ڪر ۽ جيڪي نه ڄاڻندا آھن تن جي سَڌن پٺيان نه لڳ.'),
(4492, 45, 19, 'ڇوته اِھي الله جي آڏو توکان ڪجھ به ٽاري نه سگھندا، ۽ بيشڪ ظالم ھڪ ٻئي جا دوست آھن، ۽ الله پرھيزگارن جو دوست آھي.'),
(4493, 45, 20, 'ھيءُ (قرآن) ماڻھن لاءِ چٽا دليل آھن جيڪي ماڻھو يقين رکندا آھن تن لاءِ ھدايت ۽ ٻاجھ آھي.'),
(4494, 45, 21, 'جن مدايون ڪيون سي ڀائيندا آھن ڇا ته کين انھن وانگر ڪنداسون جن ايمان آندو ۽ چڱا ڪم ڪيا؟ ته سندن جيئڻ ۽ مرڻ ھڪ جھڙو ھوندو، (اھو) جيڪو فيصلو ڪندا آھن سو بڇڙو آھي.'),
(4495, 45, 22, '۽ الله آسمانن ۽ زمين کي پوري رِٿ سان بڻايو ۽ ھن لاءِ ته ھر ڪنھن شخص جيڪي ڪمايو تنھن بابت کيس بدلو ڏجي ۽ مٿن ظلم نه ڪبو.'),
(4496, 45, 23, '(اي پيغمبر) اُنھيءَ کي نه ڏٺو اٿيئي ڇا؟ جنھن پنھنجي (نفس جي) سَڌ کي معبود ڪري ورتو ۽ ڄاڻڻ ھوندي الله کيس گمراھ ڪيو ۽ سندس ڪنّن تي ۽ سندين دل تي مُھر ھنيائين ۽ سنديس اکين تي پردو وڌائين، پوءِ الله ڌاران کيس ڪير ھدايت ڪندو، پوءِ ڇونه نصيحت وٺندا آھيو؟'),
(4497, 45, 24, '۽ چوندا آھن ته اسان جي رڳو ھي دنيا جي حياتي آھي جو مرندا آھيون ۽ جيئندا آھيون ۽ اسان کي رڳو زمانو ماريندو آھي، ۽ ان (ڳالھ) جي کين ڪا ڄاڻپ نه آھي، اُھي رڳو اٽڪل ھڻندا آھن.'),
(4498, 45, 25, '۽ جڏھن اسان جون پڌريون آيتون کين پڙھي ٻڌائبيون آھن (تڏھن) سندين حجت رڳو ھي چوڻ ھوندو آھي ته جيڪڏھن اوھين سچا آھيو ته اسان جي پيءُ ڏاڏن کي (جيئرو ڪري) آڻيو.'),
(4499, 45, 26, '(اي پيغمبر کين) چؤ ته الله اوھان کي جياريندو آھي وري اوھان کي ماريندو وري اوھان کي قيامت جي ڏينھن ۾ ڪو شڪ نه آھي گڏ ڪندو پر گھڻا ماڻھو (اِن ڳالھ کي) نه ڄاڻندا آھن.'),
(4500, 45, 27, '۽ آسمانن ۽ زمين جي بادشاھي الله جي آھي، ۽ جنھن ڏينھن قيامت ٿيندي تنھن ڏينھن ڪُوڙا ڇيئو پائيندا.'),
(4501, 45, 28, '۽ (اي پيغمبر) ھر ڪنھن ٽوليءَ کي گوڏن ڀر (ويٺل) ڏسندين، سڀڪا ٽولي پنھنجي اعمالنامي ڏانھن سڏبي، (چئبو) ته جيڪي (اوھين) ڪندا ھئو تنھن جو اڄ اوھان کي بدلو ڏبو.'),
(4502, 45, 29, 'ھي اسان جو دفتر آھي جو اوھان تي پوري حقيقت پڌري ڪري ٿو، بيشڪ جيڪي اوھين ڪندا ھئو سو اسين لکندا ھواسون.'),
(4503, 45, 30, 'پوءِ جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي سندن پالڻھار پنھنجي ٻاجھ ھيٺ داخل ڪندو، اھا پڌري مراد ماڻڻ آھي.'),
(4504, 45, 31, '۽ جن ڪُفر ڪيو (تن کي چئبو) ته اوھان وٽ منھنجيون آيتون نه پڙھبيون ھيون ڇا؟ پوءِ اوھان وڏائي ڪئي ۽ ڏوھاري قوم ھئو.'),
(4505, 45, 32, '۽ جڏھن چئبو ھُيَوَ ته الله جو وعدو سچو آھي ۽ قيامت ۾ ڪو شڪ نه آھي (تڏھن) چوندا ھئو ته (اسين) نه ڄاڻندا آھيون ته قيامت ڇا آھي اسين رڳو ھڪ (ٿورو) گمان ڀائيندا آھيون ۽ اسين (ڪڏھن) يقين ڪرڻ وارا نه آھيون.'),
(4506, 45, 33, '۽ جيڪي ڪمايو ھوائون تنھن جون مدايون کين پڌريون ٿينديون ۽ جنھن شيءَ تي چٿرون ڪندا ھوا سا کين وڪوڙي ويندي.'),
(4507, 45, 34, '۽ چئبو ته اڄ اوھان کي (ائين) وسارينداسون جيئن اوھان پنھنجي ھن ڏينھن جي اچي پھچڻ کي وساريو ھو ۽ (ھاڻي) اوھان جي جاءِ دوزخ آھي ۽ اوھان جو ڪو مددگار نه آھي.'),
(4508, 45, 35, 'اِھو (عذاب اوھان کي) ھن ڪري آھي جو اوھان الله جي آيتن کي ٺـٺوليون ڪري ورتو ھو ۽ دنيوي حياتيءَ اوھان کي ٺڳيو ھو، تنھنڪري اڄ منجھانئس نه ڪڍبن ۽ نڪي عذر طلب ڪبن.'),
(4509, 45, 36, 'پوءِ سڀ ساراھ خاص الله کي جڳائي جو آسمانن جو پالڻھار ۽ زمين جو پالڻھار ۽ جھانن جو پالڻھار آھي.'),
(4510, 45, 37, '۽ آسمانن ۽ زمين ۾ سنديس وڏائي آھي، ۽ اُھو غالب حڪمت وارو آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حٰمٓ.'),
(4512, 46, 2, 'ڪتاب جو لاھڻ غالب حڪمت واري الله وٽان آھي.'),
(4513, 46, 3, 'نه پيدا ڪيوسون آسمانن ۽ زمين کي ۽ جيڪي انھن جي وچ ۾ آھي مگر پوريءَ رِٿ سان ۽ ھڪ مقرر مدت تائين (رھڻ لاءِ)، ۽ ڪافر جنھن کان ڊيڄاريا ويا (تنھن کان) مُنھن موڙيندڙ آھن.'),
(4514, 46, 4, '(کين) چؤ ته ڏسيو ته سھي الله کانسواءِ جن کي سڏيندا آھيو تن ڪا بڻاوت بڻائي آھي ڇا مون کي ڏيکاريو ته زمين (جي شين) منجھان ڪھڙي شيء پيدا ڪئي اٿن يا آسمانن (جي بڻائڻ) ۾ انھن جي ڪا ڀائيواري (ثابت) آھي ڇا؟ جيڪڏھن سچا آھيو ته ھن کان اڳ (آيل) ڪوبه ڪتاب يا ڪا علمي سَند مون وٽ آڻيو.'),
(4515, 46, 5, '۽ اُنھيءَ کان وڌيڪ گمراھ ڪير آھي جيڪو الله ڌاران (ٻئي) اھڙي شيء کي سڏي جو قيامت تائين سندس سڏ کي نه ورنائي ۽ اُھي سندن سڏڻ کان (ئي) بي خبر آھن.'),
(4516, 46, 6, '۽ جڏھن (قيامت ۾) ماڻھو گڏ ڪبا (تڏھن) اھي سندن ويري ھوندا ۽ سندين پوڄا جا منڪر ھوندا.'),
(4517, 46, 7, '۽ جڏھن اسان جون پڌريون آيتون کين پڙھي ٻڌائبيون آھن (تڏھن اُھي) ڪافر سچي ڳالھ (يعني قرآن) به نسبت جنھن مھل وٽن آيو (تنھن مھل) چوندا آھن ته ھي پڌرو جادو آھي.'),
(4518, 46, 8, 'بلڪ چوندا آھن ته اُن کي (پيغمبر) ٺاھيو آھي، (کين) چؤ ته جيڪڏھن اُن کي پاڻ ٺاھيو اٿم ته اوھين الله کان (ڇڏائڻ واسطي) مون لاءِ ڪجھ ڪري نٿا سگھو، جيڪي قرآن بابت گفتگو ڪندا آھيو (سو) الله چڱي طرح ڄاڻندڙ آھي، منھنجي ۽ اوھان جي وچ ۾ (خدا) حق کي ظاھر ڪندڙ بس آھي، ۽ اُھو بخشڻھار مھربان آھي.'),
(4519, 46, 9, 'چؤ ته آءٌ پيغمبرن مان ڪو نئون (آيل) نه آھيان ۽ نٿو ڄاڻان ته مون سان ۽ اوھان سان (دنيا ۾) ڇا ڪيو ويندو، جيڪي مون ڏانھن وحي ڪيو ويندو آھي تنھن کانسواءِ (ٻيءَ) ڪنھن (ڳالھ) جي تابعداري نه ڪندو آھي ۽ آءٌ رڳو پڌرو ڊيڄاريندڙ آھيان.'),
(4520, 46, 10, '(کين) چؤ ته ڏسو ته سھي جيڪڏھن (اھو قرآن) الله وٽان آيل آھي ۽ اوھين اُن کي نه مڃيو ۽ بني اسرائيلن مان ھڪ شاھد اھڙي (ڪتاب) تي شاھدي ڏني ھجي پوءِ اُنھيءَ ايمان آندو ۽ اوھان (مڃڻ کان) وڏائي ڪئي (ته ضرور ظالم ٿيندؤ) بيشڪ الله ظالمن جي قوم کي سِڌو رستو نه ڏيکاريندو آھي.'),
(4521, 46, 11, '۽ ڪافرن مؤمنن به نسبت چيو ته جيڪڏھن (اھو دين) چڱو ھجي ھا ته اُھي اُن ڏانھن اسان کان اڳرائي نه ڪن ھا، ۽ جڏھن (ڪافرن) اُن سان واٽ نه لڌي تڏھن چوندا ته ھيءُ (قرآن) ھڪ پراڻو ڪوڙ آھي.'),
(4522, 46, 12, '۽ ھن (قرآن) کان اڳ مُوسىٰ جو ڪتاب اڳواڻ ۽ ٻاجھ ھو، ۽ ھي ڪتاب (ان جو) سچّو ڪندڙ عربيءَ ٻوليءَ ۾ آھي ھن لاءِ ته ظالمن کي ڊيڄاري، ۽ ڀلارن کي خوشخبري ڏئي.'),
(4523, 46, 13, 'بيشڪ جن چيو ته اسان جو پالڻھار الله آھي پوءِ اِن (چوڻ) تي مُحڪم رھيا تن کي نڪو ڀَؤ آھي ۽ نڪي اُھي غمگين ھوندا.'),
(4524, 46, 14, 'اِھي بھشتي آھن منجھس سدائين رھڻ وارا آھن، جيڪي عمل ڪندا ھوا تنھنجو بدلو آھي.'),
(4525, 46, 15, '۽ ماڻھوءَ کي پنھنجي ماءُ پيءُ سان چڱائي ڪرڻ جو حُڪم ڪيوسون، (جو) اوکائيءَ سان سنديس ماءُ کيس (پيٽ ۾) کنيو آھي ۽ اوکائيءَ سان کيس ڄڻيو آھي، ۽ اُنجو (پيٽ ۾) کڻڻ ۽ اُن جو (کير کان) ڇڏائڻ ٽيھ مھينا آھي، تان جو جڏھن پنھنجيءَ جواني کي پُھتو ۽ چاليھن ورھن کي پھتو (تڏھن) چوندو آھي ته اي منھنجا پالڻھار مون کي توفيق ڏي ته تنھنجي انھيءَ نعمت جو شڪر ڪريان جيڪا مون تي ۽ منھنجي ماءُ پيءُ تي انعام ڪئي اٿيئي ۽ ته (اھڙا) چڱا ڪم ڪريان جن کان راضي رھين ۽ منھنجي اولاد مون لاءِ سُڌار، بيشڪ آءٌ توڏانھن موٽيس ۽ بيشڪ آءٌ مسلمانن منجھان آھيان.'),
(4526, 46, 16, 'اِھي اُھي آھن جن جا تمام چڱا ڪم جيڪي ڪيائون سي قبول ڪندا آھيون ۽ سندين مداين کان ٽرندا آھيون (اھي) بھشتين ۾ ھوندا، جيڪو ساڻن سچو وعدو ڪبو ھو تنھن جي ڪري.'),
(4527, 46, 17, '۽ جنھن پنھنجي ماءُ پيءُ کي چيو ته اوھان کان بيزار آھيان مون کي انجام ڏيو ٿا ڇا ته (قبرن مان) ڪڍيو ويندس ۽ بيشڪ مون کان اڳ (ماڻھن جا) ڪيترائي جُڳ گذري ويا آھن، ۽ اُھي ٻئي الله کي ٻاڏائيندا آھن ۽ (چوندا اٿس ته) پنھنجي ڪم بختي ڇڏي ايمان آڻ، بيشڪ الله جو انجام سچو آھي، پوءِ چوندو آھي ته اِھي رڳو اڳوڻن جو آکاڻيون آھن.'),
(4528, 46, 18, 'اِھي اُھي آھن جن تي اھڙي سزا لازم ٿي (جھڙي اُنھن) ٽولين ۾ ٿي جي جِنن ۽ ماڻھن مان کائن اڳ گذري ويا، بيشڪ اُھي ڇيئي وارا ھوا.'),
(4529, 46, 19, '۽ سڀڪنھن لاءِ درجا انھيءَ آھر آھن جيڪي ڪم ڪيائون، ۽ (ھيُ ھِن لاءِ) ته (خُدا) سندن عملن جو کين پورو بدلو ڏئي ۽ مٿن ظلم نه ڪبو.'),
(4530, 46, 20, '۽ انھيءَ ڏينھن جو ڪافر باھ جي آڏو ڪبا، (چئبن ته) پنھنجون نعمتون پنھنجي دنيائي حياتيءَ ۾ وٺي چڪؤ ۽ اُن سان آسودا رھيؤ، تنھنڪري اڄ اوھان کي خواريءَ جي عذاب جو بدلو انھي ڪري ڏبو جو زمين ۾ ناحق وڏائي ڪندا ھئو ۽ انھيءَ ڪري جو بدڪاري ڪندا ھئو.'),
(4531, 46, 21, '۽ عاد جي ڀاءُ (ھود) کي ياد ڪر، جڏھن پنھنجي قوم کي (واديءَ) اَحقاف ۾ ڊيڄاريائين ۽ بيشڪ سندس اڳيان ۽ سندس پوئتان (ڪيترائي) ڊيڄاريندڙ گذريا ھوا ته الله کانسواءِ ڪنھين جي عبادت نه ڪريو، بيشڪ آءٌ اوھان تي وڏي ڏينھن جي عذاب کان ڊڄان ٿو.'),
(4532, 46, 22, 'چيائون ته اسان وٽ ھن لاءِ آيو آھين ڇا ته اسان کي پنھنجن معبودن کان ڦيرائن، پوءِ جيڪڏھن سچن مان آھين ته جيڪو وعدو اسان کي ڏئين ٿو سو اسان وٽ آڻ.'),
(4533, 46, 23, 'چيائين ته اِھو علم رڳو الله کي آھي، ۽ جنھن (پيغام) سان موڪليو ويو آھيان سو اوھان کي پھچايان ٿو پر اوھان کي ھڪ جاھل قوم ڏسان ٿو.'),
(4534, 46, 24, 'پوءِ جنھن مھل اُن کي ڪڪر (جھڙو) ڏٺائون جو سندن ميدانن ڏانھن سامھون ايندڙ ھو (تنھن مھل) چيائون ته ھيءُ ڪڪر آھي جو اسان تي وَسندو، (ھود چيو ته نه نه!) بلڪ اِھو اُھو آھي جنھن کي جلدي گھريؤ، (اھا) ھڪ آنڌي آھي جنھن ۾ ڏکوئيندڙ عذاب آھي.'),
(4535, 46, 25, '(جو) پنھنجي پالڻھار جي حُڪم سان سڀڪنھن شيءَ کي ناس ڪندي پوءِ (اھڙا ناس) ٿيا جو سندن گھرن کانسواءِ ڪجھ نه ٿي ڏٺو ويو، اھڙي طرح ڏوھاري قوم کي سزا ڏيندا آھيون.'),
(4536, 46, 26, '۽ بيشڪ انھن کي اھڙي جاءِ ڏني ھئي سون جو اوھان کي به جاءِ نه ڏني سون ۽ انھن کي ڪنّ ۽ اکيون ۽ دليون ڏنيون ھيون سون، پوءِ جڏھن الله جي آيتن جو انڪار ڪيائون تڏھن نڪي سندن ڪَنن ۽ نڪي سندن اکين ۽ نڪي سندن دلين ڪجھ به (عذاب) کائن ٽاريو ۽ جنھن (عذاب) بابت ٺـٺوليون ڪندا ھوا سو مٿن ڪڙڪيو.'),
(4537, 46, 27, '۽ بيشڪ جيڪي ڳوٺ اوھان جي آس پاس آھن سي (سڀ) ناس ڪياسون ۽ طرحين طرحين نشانيون پڌريون ڪيون سون ته مانَ اُھي موٽن.'),
(4538, 46, 28, 'پوءِ الله کانسواءِ جن کي ويجھائي لاءِ معبود ڪري ورتو ھوائون تن کين مدد ڇونه ڏني ھئي، بلڪ کائن گم ٿي ويا، ۽ اھو سندن ڪُوڙ آھي جيڪو ٺاھ ٺاھيندا ھوا.'),
(4539, 46, 29, '۽ (ياد ڪر) جڏھن جِنّن مان ھڪ ٽوليءَ کي توڏانھن لاڙيوسون جو قرآن ٻڌائون ٿي، پوءِ جنھن مھل پيغمبر وٽ پھتا چيائون ته ماٺ ڪريو، پوءِ جنھن مھل پورو ڪيو ويو ته پنھنجي قوم ڏانھن ڊيڄاريندڙ ٿي موٽيا.'),
(4540, 46, 30, 'چيائون ته اي اسان جي قوم بيشڪ اسان اھڙو ڪتاب ٻڌو جو ُموسىٰ کانپوءِ لاٿو ويو آھي جو جيڪي اُن کان اڳ (نازل ٿيل) ھو تنھن کي سچو ڪندڙ آھي سچي دين ڏانھن ۽ سِڌي واٽ ڏانھن رستو ڏيکاري ٿو.'),
(4541, 46, 31, 'اي اسان جي قوم الله جي (طرف) سڏيندڙ کي سڏ ڏيو ۽ اُن تي ايمان آڻيو ته (الله) اوھان جا ڪي ڏوھ اوھان کي بخشي ۽ ڏکوئيندڙ عذاب کان اوھان کي ڇڏائي.'),
(4542, 46, 32, '۽ جيڪو الله ڏانھن سڏيندڙ کي سڏ نه ڏيندو سو مُلڪ ۾ (ڀڄي) ٿَڪائڻ وارو نه آھي ۽ الله کانسواءِ اُن جا ڪي سڄڻ نه آھن، اُھي پڌريءَ گمراھي ۾ آھن.'),
(4543, 46, 33, 'نه ڏٺو اٿن ڇا ته الله جنھن آسمانن ۽ زمين کي بڻايو آھي ۽ انھن جي بڻائڻ ۾ نه ٿڪو سو ھن (ڳالھ) تي وس وارو آھي ته مُئن کي جياري، ھائو! بيشڪ اِھو سڀڪنھن شيءَ تي وس وارو آھي.'),
(4544, 46, 34, '۽ جنھن ڏينھن ڪافر باھ جي سامھون آڻبا، (تنھن ڏينھن چئبن ته) ھي (وعدو) سچ نه آھي ڇا؟ چوندا ھائو! اسان جي پالڻھار جو قسم آھي، (الله) چوندو ته انھي ڪري عذاب چکو جو انڪار ڪندا ھئو.'),
(4545, 46, 35, 'پوءِ (اي پيغمبر) جيئن وڏي ھمت وارن پيغمبرن صبر ڪيو ھو تئين صبر ڪر ۽ اُنھن لاءِ (عذاب) جلد نه گھُر، جنھن (قيامت) جو کين انجام ڏجي ٿو سو جنھن ڏينھن ڏسندا تنھن ڏينھن (ائين ڀائيندا ته دنيا ۾) ڄڻڪ اُھي ھڪ گھڙيءَ کانسواءِ رھيائي نه ھوا، (اھُو پيغام) پھچائڻو آھي، پوءِ بدڪارن جي قوم کانسواءِ ڪو ھلاڪ نه ڪبو.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'جن ڪُفر ڪيو ۽ الله جي واٽ کان جھليو تن جي عملن کي (الله) چَٽ ڪيو.'),
(4547, 47, 2, '۽ جن ايمان آندو ۽ چڱا ڪم ڪيا ۽ جيڪي مُحمّدؑ تي نازل ڪيو ويو ۽ اُھو سندن پالڻھار وٽان سچ آھي تنھن کي مڃيائون تن جون مدايون کائن ميٽيائين ۽ سندن حال کي سُڌاريائين.'),
(4548, 47, 3, 'اِھو ھن ڪري آھي جو ڪافر ڪُوڙ جي پٺيان لڳا ۽ مؤمن پنھنجي پالڻھار جي سچي دين جي پيروي ڪئي، اھڙيءَ طرح ماڻھن لاءِ سندن احوال بيان ڪندو آھي.'),
(4549, 47, 4, 'پوءِ جڏھن اوھين ڪافرن سان جنگ ڪريو تڏھن (انھن جون) گردنون ڪٽيو، تانجو جڏھن منجھن گھڻو رت ڇاڻ ڪيوَ تڏھن مضبوطيءَ سان قيد ٻڌو وري (قيد کان) پوءِ يا ته احسان ڪرڻ (سان ڇڏڻ) يا چَٽي وٺڻ (گھُرجي) تانجو جنگ پنھنجا ھٿيار رکي، اِھو (حُڪم) آھي، ۽ جيڪڏھن الله گھري ھا ته کائن بدلو وٺي ھا پر (گھرندو آھي) ته اوھان مان ڪن کي ڪن سان پرکي، ۽ جيڪي الله جي واٽ ۾ قتل ٿيا تن جا عمل ڪڏھن چَٽ نه ڪندو.'),
(4550, 47, 5, 'کين سڌو رستو ڏيکاريندو ۽ سندن حال سڌاريندو.'),
(4551, 47, 6, '۽ کين اُنھيءَ بھشت ۾ داخل ڪندو جيڪو کين ڄاڻايو اٿس.'),
(4552, 47, 7, 'اي ايمان وارؤ جيڪڏھن اوھين الله (جي دين) جي مدد ڪندؤ ته (الله) اوھان جي مدد ڪندو ۽ اوھان جا قدم مُحڪم ڪندو.'),
(4553, 47, 8, '۽ جن انڪار ڪيو تن کي شال ھلاڪي ھجي ۽ الله سندن عملن کي چَٽ ڪيو.'),
(4554, 47, 9, 'اِھا (سزا) ھِن ڪري آھي جو جيڪي الله نازل ڪيو آھي تنھنکي انھن ناپسند ڪيو تنھن ڪري (الله) سندن عملن کي چٽ ڪيو.'),
(4555, 47, 10, 'زمين ۾ نه گھميا آھن ڇا ته نھارين ته جيڪي کائن اڳ ھوا تن جي پڇاڙي ڪيئن ٿي، ۽ الله مٿن ھلاڪي نازل ڪئي، ۽ ڪافرن لاءِ اھڙيون (ئي سزائون) آھن.'),
(4556, 47, 11, 'اِھو ھن ڪري آھي جو الله مؤمنن جو ڪارساز آھي ۽ (ھن ڪري) ته ڪافرن جو ڪو سڄڻ نه آھي.'),
(4557, 47, 12, 'جن ايمان آندو آھي ۽ چڱا ڪم ڪيا آھن تن کي بيشڪ الله اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن، ۽ جن انڪار ڪيو (سي) دنيا جو نفعو وٺندا آھن ۽ ائين کائيندا آھن جئين ڍور کائيندا آھن ۽ سندين جاءِ باھ آھي.'),
(4558, 47, 13, '۽ (اي پيغمبر) تنھنجي ڳوٺ جنھن (جي ماڻھن) توکي ڪڍي ڇڏيو تنھن کان وڌيڪ سگھ وارا ڪيترائي ڳوٺ ھوا، جو اُنھن کي ناس ڪيوسون پوءِ انھن جو ڪو واھرو نه ھو.'),
(4559, 47, 14, 'جيڪو پنھنجي پالڻھار کان پڌريء حُجت تي ھجي سو اُنھيءَ جھڙو آھي ڇا؟ جنھن کي سندس بڇڙا ڪم چڱا ڪري ڏيکاريا ويا ۽ پنھنجين سَڌن جي پٺيان لڳا.'),
(4560, 47, 15, 'جنھن بھشت جو پرھيزگارن کي انجام ڏنو ويو آھي تنھنجي وصف ھي آھي جو اُن ۾ اھڙي پاڻي جون واھيون آھن جو (گھڻي رھڻ ڪري) نه سينواريو آھي، ۽ اھڙي کير جون واھيون آھن جنھن جو مزو نه مَٽيو آھي، ۽ پيئندڙن کي لذت ڏيندڙ شراب جون واھيون آھن، ۽ صفا ڪيل ماکيءَ جون به واھيون آھن، ۽ اُنھن لاءِ منجھس ھر جنس جا ميوا آھن ۽ سندن پالڻھار وٽان بخشش آھي، (اھي) انھن جھڙا آھن ڇا جيڪو باھ ۾ سدائين رھڻ وارو ھجي ۽ جن کي ڪوسو پاڻي پياريو وڃي جو سندن آنڊن کي ڇِني ڇڏي.'),
(4561, 47, 16, '۽ منجھائن ڪو اھڙو آھي جو توڏانھن ڪن ڏيندو آھي، تان جو جنھن مھل تو وٽان نڪرندا آھن (تنھن مھل) جن کي علم ڏنو ويو تن کي چوندا آھن ته ھاڻي (پيغمبر) ڇا چيو؟ اِھي اُھي آھن جن جي دلين تي الله مُھر ھنئي آھي ۽ جيڪي پنھنجي سَڌن تي ھليا.'),
(4562, 47, 17, '۽ جن سِڌو رستو ورتو تن کي خدا ھدايت ۾ وڌايو ۽ کين سندين پرھيزگاري ڏني.'),
(4563, 47, 18, '(ڪافر) پاڻ وٽ قيامت جي اوچتي اچڻ کانسواءِ (ٻي ڳالھ) منتظر نه آھن، پوءِ بيشڪ اُن جون نشانيون اچي چڪيون آھن، پوءِ جڏھن وٽن (قيامت) ايندي تڏھن کين نصيحت وٺڻ ڪٿان (نفعي واري ٿيندي).'),
(4564, 47, 19, 'پوءِ ڄاڻ ته الله کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي ۽ پنھنجن گناھن جي ۽ مؤمنن ۽ مؤمنياڻين لاءِ بخشش گھُر، ۽ الله اوھان جي اچ وڃ جو ھنڌ ۽ اوھان جي رھڻ جي جڳھ ڄاڻندو آھي.'),
(4565, 47, 20, '۽ مؤمن چوندا آھن ته ڪا سُورة ڇونه نازل ڪئي وئي، پوءِ جڏھن پڌري معنىٰ واري سُورة نازل ڪئي وڃي ۽ اُن ۾ جھاد جو ذڪر ڪيو وڃي ته جن جي دلين ۾ بيماري آھي تن کي ڏسندين ته تو ڏانھن اُنھيءَ (ماڻھو) جي نھارڻ وانگر ڏسندا آھن جنھن کي موت (جي سڪرات) کان بيھوشي پُھتي ھجي، پوءِ اُنھن لاءِ خرابي آھي.'),
(4566, 47, 21, '(چڱي ڳالھ ته کين) چيو وٺڻ ۽ چڱي ڳالھ چوڻ آھي، پوءِ جڏھن (جھاد جو) ڪم پڪو ٿي چڪي، ته جيڪڏھن جيڪڏھن الله سان سچا رھن ھا اُنھن لاءِ ڀلو ھجي ھا.'),
(4567, 47, 22, 'پوءِ (اي ضعيف ايمان وارؤ) جيڪڏھن اسلام کان ڦريئو ته (اوھين) ھن (ڳالھ) کي ويجھا آھيو ته زمين ۾ فساد ڪندؤ ۽ پنھنجيون مائٽيون ڇنندؤ.'),
(4568, 47, 23, 'اِھي اُھي آھن جن کي الله لعنت ڪئي پوءِ کين ٻوڙو ڪيائين ۽ سندين اکيون انڌيون ڪيائين.'),
(4569, 47, 24, 'ڇا قرآن ۾ ڌيان نه ڪندا آھن يا (سندين) دلين تي اُنھن جا قُفل لڳل آھن؟'),
(4570, 47, 25, 'بيشڪ جيڪي (ماڻھو) اُن کانپوءِ جو کين ھدايت پڌري ٿي پنھنجين پٺين ڀر ڦريا شيطان (اھو ڪم) چڱو ڪري ڏيکاريو آھي، ۽ کين مھلت (ھجڻ) جو وعدو ڏنو اٿس.'),
(4571, 47, 26, 'اھو ھن ڪري آھي ته جيڪي الله نازل ڪيو آھي تنھن کي جن (ماڻھن) ناپسند ڪيو تن کي اُنھن (منافقن) چيو ته ڪن ڪمن ۾ اوھان جو چيو مڃينداسون، ۽ الله سندين ڳجھين ڳالھين کي ڄاڻندو آھي.'),
(4572, 47, 27, 'پوءِ (تڏھن سندن حال) ڪيئن ٿيندو جڏھن ملائڪ سندن رُوح ڪڍندا جنھن حال سندن مُنھن ۽ سندن پُٺيون ڪُٽيندا.'),
(4573, 47, 28, 'اِھا (سزا) ھن ڪري آھي ته جنھن شيء الله کي ڏمرايو تنھنجي پٺيان لڳا ۽ سندس رضامندي کي ناپسند ڪيائون تنھنڪري (خدا) سندن عملن کي چٽ ڪيو.'),
(4574, 47, 29, 'جن جي دلين ۾ بيماري آھي تن ڀانيو آھي ڇا ته الله سندن وير ڪڏھن پڌرا نه ڪندو.'),
(4575, 47, 30, '۽ جيڪڏھن گھرون ھا ته اُھي توکي ڏيکاريون ھا پوءِ سندن چھرن مان کين سڃاڻين ھا، ۽ ضرور کين ڳالھائڻ جي ڍنگ مان معلوم ڪندين، ۽ الله اوھان جا ڪرتوت ڄاڻندو آھي.'),
(4576, 47, 31, '۽ ضرور اوھان کي پرکينداسون ته اوھان مان مجاھدن ۽ صابرن کي معلوم ڪريون ۽ اوھان جا حال جاچيون.'),
(4577, 47, 32, 'بيشڪ جن ڪفر ڪيو ۽ الله جي واٽ کان جھليو ۽ کين ھدايت پڌري ٿي تنھن کانپوءِ پيغمبر جي مخالفت ڪيائون سي الله جو ڪجھ نقصان نه ڪندا، ۽ (الله) سندن عمل سگھوئي چٽ ڪندو.'),
(4578, 47, 33, 'اي ايمان وارؤ الله جو چيو مڃيو ۽ پيغمبر جو چيو مڃيو ۽ پنھنجا عمل نه وڃايو.'),
(4579, 47, 34, 'بيشڪ جن ڪفر ڪيو ۽ الله جي واٽ کان جھليو وري ڪافر ٿي مُئا تن کي الله ڪڏھن نه بخشيندو.'),
(4580, 47, 35, 'پوءِ سُست نه ٿيو ۽ صلح ڏانھن نه سڏيو، ۽ اوھين ئي غالب آھيو، ۽ الله اوھان ساڻ آھي ۽ اوھان جي عملن کي ڪڏھن ضايع نه ڪندو.'),
(4581, 47, 36, 'دنيا جي حياتي ته رڳو راند ۽ تماشو آھي، ۽ جيڪڏھن ايمان آڻيندؤ ۽ پرھيزگاري ڪندؤ ته اوھان جو اجر اوھان کي ڏيندو ۽ اوھان کان اوھان جو (سڀ) مال نه گھرندو.'),
(4582, 47, 37, 'جيڪڏھن اوھان کان (اوھان جو) مال گھري پوءِ (گھرڻ ۾) اوھان کي تنگ ڪري ته (انھيءَ وقت ضرور) بخيلي ڪندؤ ۽ اُھا (بخيلي) اوھان جون بدنيتون ظاھر ڪندي.'),
(4583, 47, 38, 'خبردار ھجو اوھين اُھي (ماڻھو) آھيو جو اوھان کي سڏيو وڃي ٿو ته الله جي واٽ ۾ خرچيو، پوءِ اوھان مان ڪو (ته اھڙو آھي جو) بخيلي ٿو ڪري، ۽ جيڪو بخيلي ٿو ڪري سو رڳو پاڻ سان بخيلي ڪري ٿو، ۽ الله بي پرواھ آھي ۽ اوھين محتاج آھيو، ۽ جيڪڏھن منھن موڙيندؤ ته اوھان کانسواءِ (ٻي) ڪنھن قوم کي مٽائي آڻيندو پوءِ اُھي اوھان جھڙا نه ھوندا.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, '(اي پيغمبر) بيشڪ اسان توکي پڌري فتح ڏني.'),
(4585, 48, 2, 'ته تنھنجا قُصور جيڪي اڳي گذريا ۽ جيڪي پوءِ تي رھيا سي (سڀ) الله توکي معاف ڪري ۽ توتي پنھنجي نعمت پوري ڪري ۽ توکي سِڌي واٽ ڏيکاري.'),
(4586, 48, 3, '۽ الله غالب سوڀ سان توکي سوڀارو ڪري.'),
(4587, 48, 4, '(الله) اُھو آھي جنھن مؤمنن جي دلين ۾ آرام نازل ڪيو ته (انھن جو) ايمان (اڳين) ايمان سان وڌيڪ ٿئي، ۽ آسمانن ۽ زمين جا لشڪر الله جا (ئي) آھن، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(4588, 48, 5, 'ته مؤمنن ۽ مؤمنياڻين کي اھڙن باغن ۾ داخل ڪري جن جي ھيٺان واھيون پيون وھن اُتي سدائين رھندا ۽ سندن مدايون کائن ميٽيندو، ۽ اِھا الله وٽ وڏي مراد ماڻڻ آھي.'),
(4589, 48, 6, '۽ (ته) منافقن ۽ منافقياڻين کي ۽ مشرڪن ۽ مشرڪياڻين کي عذاب ڪري جي الله تي تمام بڇڙو گمان ڀانئيندڙ آھن، بڇڙي مصيبت مٿن ھجي شال، ۽ مٿن الله ڏمريو ۽ کين لعنت ڪيائين ۽ اُنھن لاءِ دوزخ تيار ڪيائين، ۽ اُھو بڇڙو ھنڌ آھي.'),
(4590, 48, 7, '۽ آسمانن ۽ زمين جا لشڪر الله جا آھن، ۽ الله غالب حڪمت وارو آھي.'),
(4591, 48, 8, 'بيشڪ اسان توکي شاھدي ڏيندڙ ۽ خوشخبري ڏيندڙ ۽ ڊيڄاريندڙ ڪري موڪليو.'),
(4592, 48, 9, 'ته اوھين الله ۽ سندس پيغمبر تي ايمان آڻيو ۽ سندس (دين جي) مدد ڪريو ۽ سندس عِزّت ڪريو، ۽ صبح ۽ سانجھيءَ جو الله کي پاڪائيءَ سان ساراھيو.'),
(4593, 48, 10, 'بيشڪ جيڪي توسان بيعت ڪندا آھن سي الله ئي سان بيعت ڪندا آھن، سندن ھٿن مٿان الله جو ھٿ آھي، پوءِ جيڪو (انجام) ڀڃندو سو رڳو پنھنجي ڇيئي لاءِ ٿو ڀڃي، ۽ جنھن (ڳالھ) تي الله سان انجام ڪيو اٿس تنھنکي جيڪو پورو ڪندو پوءِ سگھو تنھن کي (الله) وڏو اجر ڏيندو.'),
(4594, 48, 11, '(اي پيغمبر) ٻھراڙيءَ وارن مان پوءِ تي رھيل توکي چوندا ته اسان کي پنھنجن مالن ۽ پنھنجي گھر جي ڀاتين رُجھايو تنھنڪري اسان لاءِ بخشش گھر، پنھنجين زبانن سان اھا (ڳالھ) چوندا آھن جا اُنھن جي دلين ۾ نه آھي، (کين) چؤ ته جيڪڏھن (الله) اوھان کي ڏک پھچائڻ جو ارادو ڪري يا اوھان کي سُک پھچائڻ جو ارادو ڪري ته الله جي سامھون اوھان لاءِ ڪير ڪجھ ڪري سگھي ٿو، بلڪ جيڪي ڪجھ ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.'),
(4595, 48, 12, 'بلڪ ڀانيوَ ته پيغمبر ۽ مؤمن پنھنجن گھرن ڏانھن اصلي ڪڏھن نه موٽندا ۽ اِھو (گمان) اوھان جي دلين ۾ چڱو (ڪري) ڏيکاريو ويو ۽ (تمام) بڇڙو گمان ڀانيوَ، ۽ (اوھين) ھلاڪ ٿيل قوم ٿيؤ.'),
(4596, 48, 13, '۽ جنھن الله ۽ سندس پيغمبر تي ايمان نه آندو ته بيشڪ اسان انھن ڪافرن لاءِ ٻرندڙ باھ تيار ڪئي آھي.'),
(4597, 48, 14, '۽ آسمانن ۽ زمين جي بادشاھي خاص الله جي آھي، جنھن کي وڻيس تنھن کي بخشي ۽ جنھن کي وڻيس تنھن کي عذاب ڪري، ۽ الله بخشڻھار مھربان آھي.'),
(4598, 48, 15, 'جڏھن غنيمتن جي ھٿ ڪرڻ لاءِ ويندؤ تڏھن پوءِ تي رھيل چوندا اسان کي ڇڏيو ته اوھان سان ھلون، گھرندا آھن ته الله جي وعدي جي مخالفت ڪن، (کين) چؤ ته اسان سان ڪڏھن نه ھلندؤ اھڙي طرح الله اڳي ئي فرمائي چڪو آھي، پوءِ سگھو چوندا ته بلڪ اوھين اسان سان حسد ٿا ڪريو، بلڪ (ھميشه) ٿورڙي کانسواءِ (ڪجھ به) نه سمجھندا ھوا.'),
(4599, 48, 16, '(اي پيغمبر) پوءِ تي رھيلن ٻھراڙي وارن کي چؤ ته سخت ويڙھ واريءَ قوم (جي جنگ) ڏانھن (اوھين) سگھو سڏيا ويندؤ جو ساڻن ويڙھ ڪندؤ يا ته (اُھي) مُسلمان ٿيندا، پوءِ جيڪڏھن چيو مڃيندؤ ته الله اوھان کي چڱو اجر ڏيندو، ۽ جيڪڏھن (ائين) ڦرندؤ جيئن (اوھين) ھن کان اڳ ڦريا ھئو ته (الله) اوھان کي ڏکوئيندڙ عذاب جي سزا ڏيندو.'),
(4600, 48, 17, '(جھاد جي رھڻ کان) نڪي انڌي تي ڪو گناھ آھي، ۽ نڪي مندي تي ڪو گناھ آھي ۽ نڪي بيمار تي ڪو گناھ آھي، ۽ جيڪو الله ۽ سندس پيغمبر جو چيو مڃيندو تنھن کي (الله اھڙن) باغن ۾ داخل ڪندو جن جي ھيٺان واھيون پيون وھن، ۽ جيڪو ڦرندو تنھنکي ڏکوئيندڙ عذاب جي سزا ڏيندو.'),
(4601, 48, 18, 'بيشڪ الله مؤمنن کان راضي ٿيو جنھن وقت وڻ ھيٺ توسان بيعت ٿي ڪيائون پوءِ جيڪي سندين دلين ۾ ھو سو ڄاتائين پوءِ مٿن (دل جو) آرام لاٿائين ۽ کين ھڪ ويجھي سوڀ انعام ڏنائين.'),
(4602, 48, 19, '۽ گھڻيون غنيمتون (به) جو اُنھن کي (اُھي) ھٿ آڻيندا، ۽ الله غالب حڪمت وارو آھي.'),
(4603, 48, 20, 'الله اوھان کي گھڻن غنيمتن جو وعدو ڏنو آھي جن کي ھٿ آڻيندؤ پوءِ ھيُ (خيبر جون غنيمتون) اوھان کي عطا ڪيائين ۽ اوھان کان ماڻھن جا ھٿ جھلي ڇڏيائين، ۽ ھن لاءِ ته مؤمنن لاءِ ھڪ نشاني ھجي ۽ اوھان کي (الله) سڌو رستو ڏيکاري.'),
(4604, 48, 21, '۽ ٻيون (به) جن تي سگھارانه ٿيا آھيو بيشڪ الله کي انھن جي خبر آھي، ۽ الله سڀڪنھن شيءَ تي وس وارو آھي.'),
(4605, 48, 22, '۽ جيڪڏھن ڪافر اوھان سان جنگ ڪن ھا ته ضرور پٺيون ڦيرائين ھا وري نڪو دوست ۽ نڪو مددگار لھن ھا.'),
(4606, 48, 23, '(اھوئي) الله جو قانون آھي جو اڳ ھليو ايندو آھي، ۽ الله جي قانون کي ڪڏھن ڪا ڦير ڦار نه ڏسندين.'),
(4607, 48, 24, '۽ (الله) اُھو آھي جنھن ڪافرن جا ھٿ اوھان کان ۽ اوھان جا ھٿ انھن کان جھليا مڪّي جي وچ ۾ اوھان کي مٿن سوڀاري ڪرڻ کان پوءِ، ۽ جيڪي ڪندا آھيو تنھن کي الله ڏسندڙ آھي.'),
(4608, 48, 25, 'اِھي اُھي آھن جن انڪار ڪيو ۽ اوھان کي تعظيم واري مسجد کان جھليو ۽ قرباني کي به جو پنھنجي جاءِ تي پھچڻ کان روڪيل رھي، ۽ جيڪڏھن (اھڙا ڪي) مڙس مؤمن ۽ (ڪي) زالون مؤمنياڻيون (مڪي ۾) نه ھجن ھا جن کي اوھان نه ڄاڻندا آھيو ته (متان) کين لتاڙي ڇڏيو پوءِ بي خبريءَ ۾ انھن جو اوھان کي گناھ پھچي (ته ھاڻي ئي فتح ٿئي ھا)، جنھن کي الله گھري پنھنجي ٻاجھ ۾ داخل ڪري، جيڪڏھن (ٻئي ٽوليون) ڌار ھجن ھا ته ضرور مڪي وارن مان ڪافرن کي ڏکوئيندڙ عذاب ڪريون ھا.'),
(4609, 48, 26, 'جڏھن ڪافرن پنھنجن دلين ۾ جاھليت جو جوش پيدا ڪيو تڏھن الله پنھنجي پيغمبر تي ۽ مؤمنن تي پنھنجو آرام نازل ڪيو ۽ کين پرھيزگاريءَ جي ڳالھ تي قائم رکيائين ۽ (اِھي) ان جا وڌيڪ حقدار ۽ اُن جا جوڳا ھوا، ۽ الله سڀڪنھن شيء کي ڄاڻندڙ آھي.'),
(4610, 48, 27, 'بيشڪ الله پنھنجي پيغمبر کي سچو خواب ڏيکاريو، ته جيڪڏھن الله گھريو آھي ته پنھنجا مٿا ڪوڙائي ۽ وار ڪترائي تعظيم واريءَ مسجد ۾ بي ڀوا ٿي ضرور گھڙندؤ (۽) نه ڊڄندؤ، پوءِ جيڪي نه ڄاڻندا آھيو سو الله ڄاتو پوءِ اُن کان اڳ ھڪ ويجھي فتح ڏني.'),
(4611, 48, 28, '(الله) اُھو آھي جنھن پنھنجي پيغمبر کي ھدايت ۽ سچي دين سان موڪليو ته اُن کي سڀني دينن تي غالب ڪري، ۽ الله شاھد ڪافي آھي.'),
(4612, 48, 29, 'مُحمّد الله جو موڪليل آھي، ۽ جيڪي سندس سنگتي آھن سي ڪافرن تي ڏاڍا سخت آھن ۽ پاڻ ۾ مھربان آھن کين رڪوع ڪندڙ سجدو ڪندڙ ڏسندو آھين الله جو فضل ۽ رضامندي طلبيندا آھن، انھن جي نشاني سندن منھن ۾ سجدي جي اثر کان (پڌري) آھي، اھا سندن وصف توريت ۾ آھي، ۽ سندس وصف اِنجيل ۾ آھي، (اھي) اُنھيءَ پوک وانگر آھن جنھن پنھنجو سَلو ڪڍيو پوءِ اُن کي مضبوط ڪيائين پوءِ ٿلھو ٿيو پوءِ پنھنجي سلي تي سڌي بيٺي جو ڪڙمين کي پئي وڻي (اسلامي غلبي جو به آخر اھڙو حال آھي) تانجو مؤمنن جي (ڏسڻ) ڪري ڪافرن کي ڪاوڙائي، جن ايمان آندو ۽ منجھائن چڱا ڪم ڪيا (تن کي) بخشش ۽ وڏي اجر جو الله انجام ڏنو آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'اي ايمان وارؤ الله ۽ سندس پيغمبر جي آڏو اڳرائي نه ڪريو ۽ الله کان ڊڄو، بيشڪ الله ٻڌندڙ ڄاڻندڙ آھي.'),
(4614, 49, 2, 'اي ايمان وارؤ پنھنجا آواز پيغمبر جي آواز کان مٿي نه ڪريو ۽ جيئن اوھان جو ھڪ ٻئي سان وڏي سڏ ڳالھائڻ آھي (تئين) ساڻس ڳالھ ڏاڍيان نه ڳالھايو جو متان اوھان جا عمل چٽ ٿي وڃن ۽ اوھين نه پروڙيندا ھجو.'),
(4615, 49, 3, 'بيشڪ جيڪي پنھنجن آوازن کي الله جي پيغمبر جي آڏو جھڪو ڪندا آھن اِھي اُھي آھن جن جي دلين کي الله پرھيزگاريءَ لاءِ آزمايو آھي، اُنھن لاءِ بخشش ۽ وڏو اجر آھي.'),
(4616, 49, 4, 'بيشڪ جيڪي حُجرن جي ٻاھران توکي سڏيندا آھن تن مان گھڻا بي سمجھ آھن.'),
(4617, 49, 5, 'جيڪڏھن اُھي تنھنجي ڏانھُن نڪرڻ تائين صبر ڪن ھا ته اُنھن لاءِ ڀَلو ھُجي ھا، ۽ الله بخشڻھار مھربان آھي.'),
(4618, 49, 6, 'اي ايمان وارؤ جيڪڏھن ڪو بدڪار ڪا خبر اوھان وٽ آڻي ته جاچ ڪريو جو (متان) ڪنھن قوم کي بي خبريءَ سان نقصان پھچايو پوءِ جيڪي اوھان ڪري چڪؤ تنھن تي پشيمان ٿيو.'),
(4619, 49, 7, '۽ ڄاڻو ته اوھان ۾ الله جو پيغمبر آھي، جيڪڏھن گھڻن ڪمن ۾ ھو اوھان جو چيو مڃيندو ته اوھين ڏکيا ٿيندؤ پر الله اوھان وٽ ايمان کي پيارو ڪرايو ۽ اوھان جي دلين ۾ ان کي چڱو ڪري ڏيکاريائين ۽ اوھان وٽ ڪفر ۽ بي ديني ۽ گناھ کي ناپسند ڪرايائين، اھي سڌي رستي وارا آھن.'),
(4620, 49, 8, 'الله جي فضل ۽ احسان سان، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(4621, 49, 9, '۽ جيڪڏھن مؤمنن جون ٻه ٽوليون پاڻ ۾ وڙھن ته سندن وچ ۾ صُلح ڪريو، پوءِ جيڪڏھن انھن مان ھڪڙي ٻي تي زيادتي ڪري ته جيڪا زيادتي ٿي ڪري تنھن سان وڙھو جيسين تائين الله جي حُڪم ڏانھن موٽي اچي، پوءِ جيڪڏھن موٽي آئي ته سندن وچ ۾ انصاف سان صُلح ڪريو ۽ انصاف سان ھلو، ڇوته الله انصاف وارن کي پيارو رکندو آھي.'),
(4622, 49, 10, 'مؤمن ته سڀ ڀائر آھن تنھنڪري پنھنجي ٻنھي ڀائرن جي وچ ۾ صلح ڪريو، ۽ الله کان ڊڄو ته مانَ اوھان تي ٻاجھ ڪئي وڃي.'),
(4623, 49, 11, 'اي ايمان وارؤ ڪا ٽولي ڪنھن ٽولي تي ٺـٺولي نه ڪري جو متان اُھي اُنھن کان ڀلا ھجن ۽ نه زالون ٻـين زالن سان (ٺـٺوليون ڪن) جو متان اُھي اُنھن کان ڀَليون ھجن، ۽ نڪي پاڻ ۾ (ھڪ ٻئي کي) عيب لايو ۽ نڪي ھڪ ٻئي کي بڇڙن لقبن سان سڏيو، (جو) ايمان آڻڻ کانپوءِ بي دينيءَ جو نالو (پوڻ) خراب آھي، ۽ جن توبه نه ڪئي سي ئي ظالم آھن.'),
(4624, 49, 12, 'اي ايمان وارؤ گھڻو ڪري گمان کان بچو، بيشڪ ڪي گمان گناھ آھن ۽ نڪي جاسوسي ڪريو ۽ نڪو ڪو اوھان مان ھڪ ٻئي جي گلا ڪري، ڀلا اوھان مان ڪو ھڪڙو پنھنجي مُئي ڀاءُ جو گوشت کائڻ پسند ڪندو آھي ڇا جنھن کان نفرت ڪندا آھيو، ۽ الله کان ڊڄو، بيشڪ الله توبه قبول ڪندڙ مھربان آھي.'),
(4625, 49, 13, 'اي ماڻھؤ بيشڪ اسان اوھان کي ھڪ مڙس ۽ ھڪ زال مان پيدا ڪيو ۽ اوھان کي ذاتيون ۽ پاڙا ڪيوسون ته ھڪ ٻئي کي سڃاڻو، بيشڪ اوھان مان تمام عزت وارو الله وٽ اُھو آھي جيڪو اوھان مان ڏاڍو پرھيزگار آھي، بيشڪ الله ڄاڻندڙ خبر رکندڙ آھي.'),
(4626, 49, 14, 'جھنگلي چوندا آھن ته ايمان اندو اٿون، (اي پيغمبر کين) چؤ ته (حقيقت ڪري) ايمان نه آندو اٿو پر (ايئن) چؤ ته اسين مُسلمان ٿيا آھيون ۽ اڃا اوھان جي دلين ۾ ايمان گھڙيو (ئي) نه آھي، ۽ جيڪڏھن الله ۽ سندس پيغمبر جو چيو مڃيندؤ ته اوھان جي عملن مان اوھان کي ڪجھ (به) نه گھٽائيندو، بيشڪ الله بخشڻھار مھربان آھي.'),
(4627, 49, 15, 'مؤمن رڳو اُھي آھن جن الله ۽ سندس پيغمبر تي ايمان آندو وري (ڪو) شڪ نه ڪيائون ۽ الله جي واٽ ۾ پنھنجن مالن ۽ پنھنجن جُسن سان جھاد ڪيائون، اھي ئي سچا آھن.'),
(4628, 49, 16, '(اي پيغمبر کين) چؤ ته پنھنجو دين الله کي ڄاڻائيندا آھيو ڇا؟ ۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو سڀ الله ڄاڻندو آھي، ۽ الله سڀڪنھن شيءَ کي ڄاڻندڙ آھي.'),
(4629, 49, 17, '(اي پيغمبر) اسلام آڻڻ جو توتي احسان رکندا آھن، چوينِ ته مون تي پنھنجي اسلام (آڻڻ) جو احسان نه رکو، بلڪ اوھان تي الله احسان رکندو آھي جو اوھان کي ايمان جي ھدايت ڪيائين جيڪڏھن سچا آھيو.'),
(4630, 49, 18, 'بيشڪ الله آسمانن ۽ زمين جو ڳجھ ڄاڻندو آھي، ۽ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'قٓ، سڳوري قرآن جو قسم آھي.'),
(4632, 50, 2, 'بلڪ ھن ڳالھ ڪري عجب ۾ پيا ته اُنھن وٽ اُنھن مان (ئي) ھڪ ڊيڄاريندڙ آيو ته پوءِ ڪافرن چيو ته ھي عجب ڳالھ آھي.'),
(4633, 50, 3, 'ڀلا جڏھن مرنداسون ۽ مٽي ٿينداسون، اھو وري جيئرو ٿيڻ (عقل کان) پري آھي.'),
(4634, 50, 4, 'انھن مان جيڪي زمين گھٽائيندي آھي سو بيشڪ ڄاتو اٿون، ۽ اسان وٽ ھڪ حفاظت رکندڙ ڪتاب آھي.'),
(4635, 50, 5, 'بلڪ سچو دين جڏھن وٽن آيو ته اُن کي ڪوڙ ڄاتائون پوءِ اُھي منجھيل ڪم ۾ آھن.'),
(4636, 50, 6, 'پوءِ آسمان جو سندن مٿان آھي تنھن ڏانھن نه ڏٺو اٿن ڇا ته اُن کي ڪيئن بڻايو اٿون ۽ اُن کي (ڪيئن) سينگاريو اٿون ۽ اُن ۾ ڪا ڦوٽ نه آھي.'),
(4637, 50, 7, '۽ زمين کي پکيڙيوسون ۽ منجھس جبل وڌاسون ۽ منجھس سڀڪنھن وڻندڙين جنسن مان سَلّا ڄماياسون.'),
(4638, 50, 8, 'سڀڪنھن ورندڙ ٻانھي جي واٽ ڏيکارڻ ۽ نصيحت ڏيڻ لاءِ.'),
(4639, 50, 9, '۽ آسمان کان برڪت وارو پاڻي لاٿوسون پوءِ اُن سان باغ ۽ لڻڻ واري پوک جو اَن ڄمايوسون.'),
(4640, 50, 10, '۽ ڊگھيون کجيون (به) جن جا گوشا تہ ٿيل آھن.'),
(4641, 50, 11, 'ٻانھن جي روزي (ڏيڻ) لاءِ ۽ اُن سان ويران شھر کي آباد ڪيوسون، ۽ اھڙي طرح (قبرن مان) نڪرڻ آھي.'),
(4642, 50, 12, 'کائن اڳ نوح جي قوم ۽ رس (جي شھر) وارن ۽ ثمود (قوم) ڪوڙو ڀانيو.'),
(4643, 50, 13, '۽ عاد ۽ فرعون ۽ لُوط جي ڀائرن.'),
(4644, 50, 14, '۽ ايڪه وارن ۽ تُبّع جي قوم، ھر ھڪ پيغمبرن کي ڪُوڙو ڄاتو پوءِ (مٿن) منھنجي عذاب جو انجام لازم ٿيو.'),
(4645, 50, 15, 'پھرين ڀيري (جي) خلقڻ ۾ (ڪو) ٿڪا آھيو ڇا؟ (نه!) بلڪ اُھي نئين سر پيدا ڪرڻ کان شڪ ۾ آھن.'),
(4646, 50, 16, '۽ بيشڪ ماڻھوءَ کي پيدا ڪيوسون ۽ سندس نفس کيس جيڪو وسوسو وجھندو آھي سو ڄاڻندا آھيون، ۽ اسين ڏانھس (سندس) ساہ جي رڳ کان وڌيڪ ويجھا آھيون.'),
(4647, 50, 17, 'جڏھن جو ٻه لکندڙ (ھڪ) سڄي پاسي کان ۽ (ٻيو) کٻي پاسي کان وھندڙ لکندا آھن.'),
(4648, 50, 18, 'ڪا ڳالھ نه ڳالھائيندو آھي جنھن جي لاءِ وٽس ھڪ نگھبان موجود نه آھي.'),
(4649, 50, 19, '۽ موت جي سختي سچ پچ ايندي، (چئبو ته) اِھا اُھا شيءِ آھي جنھن کان پاسو ڪندو ھُئين.'),
(4650, 50, 20, '۽ صُور ۾ ڦوڪيو ويندو، اھو عذاب جو ڏينھن آھي.'),
(4651, 50, 21, '۽ سڀڪو جيءَ ايندو جو ساڻس ھڪ ھڪليندڙ ۽ ھڪ شاھِد ھوندو.'),
(4652, 50, 22, '(چئبو ته) بيشڪ ھن (ڏينھن) کان بي خبريءَ ۾ ھُئين ھاڻي توکان تنھنجو پردو کوليوسون تنھنڪري اڄ تنھنجي نگاھ تِکي آھي.'),
(4653, 50, 23, '۽ سندس سنگتي (ملائڪ) چوندو ته جيڪي مون وٽ (لکيل) موجود ھو سو ھي آھي.'),
(4654, 50, 24, '(چئبو ته) اي ٻئي (ملائڪؤ) سڀڪنھن ناشڪر ھٺيلي کي دوزخ ۾ اُڇليو.'),
(4655, 50, 25, '(جو) خير کان جھليندڙ حد کان لنگھندڙ شڪ ڪندڙ آھي.'),
(4656, 50, 26, 'جنھن الله سان ٻيو معبود ٺھرايو اھڙي (ھر ھڪ) کي سخت عذاب ۾ اُڇليو.'),
(4657, 50, 27, 'سندس سنگتي (شيطان) چوندو ته اي منھنجا پالڻھار مون اُن کي گمراھ نه ڪيو ھو پر اُھو (پاڻ) وڏي گمراھيءَ ۾ (پيل) ھو.'),
(4658, 50, 28, '(الله) چوندو ته مون وٽ جھڳڙو نه ڪريو ھن ھوندي جو بيشڪ اوھان ڏانھن عذاب جو انجام (ھن کان) اڳي موڪليو ھوم.'),
(4659, 50, 29, 'نڪي مون وٽ انجام ڦيرائبو آھي ۽ نڪي آءٌ ٻانھن تي ظلم ڪرڻ وارو آھيان.'),
(4660, 50, 30, 'جنھن ڏينھن دوزخ کي چونداسون ته ڀريو آھين (يا نه!) ۽ (دوزخ) چوندو ته (ٻيو به) ڪجھ (اڃا) ھن کان وڌيڪ آھي ڇا؟'),
(4661, 50, 31, '۽ بھشت پرھيزگارن لاءِ ويجھو ڪبو نه پري.'),
(4662, 50, 32, '(چئبو ته) جنھن جو اوھان کي سڀڪنھن (الله ڏانھن) رجوع ڪرڻ واري ۽ ادب نگاھ رکڻ واري لاءِ انجام ڏبو ھو سو ھي آھي.'),
(4663, 50, 33, 'جيڪو پرپٺ ٻاجھاري (الله) کان ڊنو ۽ (الله ڏانھن) رجوع ٿيل دل سان آيو.'),
(4664, 50, 34, '(چئبو ته) سلامتيءَ سان بھشت ۾ گھڙو، اھو سدائين رھڻ جو ڏينھن آھي.'),
(4665, 50, 35, 'جيڪي گھرندا سو منجھس انھن لاءِ (موجود) آھي ۽ اسان وٽ (اُن کان) وڌيڪ به آھي.'),
(4666, 50, 36, '۽ انھن کان اڳ ڪيترائي جُڳ ھلاڪ ڪياسون جي پڪڙ ۾ اُنھن کان به تمام ڏاڍا ھوا پوءِ شھرن ۾ ڳولا ڪرڻ لڳا، ته ڪا (بچڻ جي) واھ آھي؟'),
(4667, 50, 37, 'بيشڪ انھيءَ ڳالھ ۾ اُنھي لاءِ نصيحت آھي جنھن کي دل ھجي يا جو دل لائي ڪن ڏئي.'),
(4668, 50, 38, '۽ بيشڪ آسمانن ۽ زمين کي ۽ جيڪي ٻنھي جي وچ ۾ آھي تنھن کي ڇھن ڏينھن ۾ بڻايوسون، ۽ اسان کي ڪو ٿَڪ نه ٿيو.'),
(4669, 50, 39, 'پوءِ (اي پيغمبر) جيڪي (ڪافر) چوندا آھن تنھن تي صبر ڪر ۽ سج اُڀرڻ کان اڳ ۽ لھڻ کان اڳ پنھنجي پالڻھار جي پاڪائي ساراھ سان بيان ڪر.'),
(4670, 50, 40, '۽ ڪجھ (مھل) رات جو الله کي پاڪائيءَ سان ساراھ ۽ نماز کان پوءِ به.'),
(4671, 50, 41, '۽ (ھي ڳالھ) ٻُڌ ته جنھن ڏينھن ويجھي ھنڌ کان سڏيندڙ سڏيندو.'),
(4672, 50, 42, 'جنھن ڏينھن سچ پچ سخت ھَڪل ٻُڌندا، اُھو (قبرن مان) نڪرڻ جو ڏينھن آھي.'),
(4673, 50, 43, 'بيشڪ اسين جياريندا آھيون ۽ ماريندا آھيون ۽ اسان ڏانھن موٽڻ آھي.'),
(4674, 50, 44, 'جنھن ڏينھن زمين انھن کان ڦاٽندي (تنھن ڏينھن) تڪڙا وڃڻ وارا ھوندا، اِھو گڏ ڪرڻ اسان کي آسان آھي.'),
(4675, 50, 45, 'جيڪي (ڪافر) چوندا آھن سو اسين چڱي طرح ڄاڻندا آھيون ۽ تون مٿن (ڪو) ڏاڍ ڪرڻ وارو نه آھين، پوءِ جيڪو منھنجي عذاب جي ڌمڪيءَ کان ڊڄي تنھن کي قرآن سان سمجھاءِ.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'قسم آھي اُڏائڻ وارن (وائن) جو (خاڪ کي) اُڏائڻ.'),
(4677, 51, 2, 'پوءِ (پاڻي جي) بار کڻڻ وارن (ڪڪرن) جو قسم آھي.'),
(4678, 51, 3, 'پوءِ آسانيءَ سان ھلندڙين (ٻيڙين) جو قسم آھي.'),
(4679, 51, 4, 'پوءِ ڪم وراھڻ وارن (ملائڪن) جو قسم آھي.'),
(4680, 51, 5, 'ته بيشڪ جيڪو اوھان سان انجام ڪجي ٿو سو سچ آھي.'),
(4681, 51, 6, '۽ بيشڪ (عملن جو) بدلو ضرور ٿيڻو آھي.'),
(4682, 51, 7, 'واٽن واري آسمان جو قسم آھي.'),
(4683, 51, 8, 'ته (اوھين) پاڻ ۾ تڪرار واريء ھڪ ڳالھ ۾ (پيل) آھيو.'),
(4684, 51, 9, 'قرآن کان اُھو ڦيرايو ويندو آھي جيڪو (ازل) ۾ ڦيرايل آھي.'),
(4685, 51, 10, 'اُنھن ڪوڙ ھڻندڙن کي لعنت ڪئي ويئي.'),
(4686, 51, 11, 'جيڪي اَڻ ڄاڻائي ۾ وسارڻ وارا آھن.'),
(4687, 51, 12, 'پُڇندا آھن ته قيامت جو ڏينھن ڪڏھن ٿيندو؟'),
(4688, 51, 13, '(ھائو) اُنھي ڏينھن جو اُنھن کي باھ ۾ عذاب ڪبو.'),
(4689, 51, 14, '(چئبو ته ھيُ) پنھنجو عذاب چکو، ھيُ اُھو آھي جنھن کي اوھين جلد گھرندا ھئو.'),
(4690, 51, 15, 'بيشڪ پرھيزگار باغن ۽ چشمن ۾ ھوندا.'),
(4691, 51, 16, 'جيڪي سندن پالڻھار کين ڏنو سو وٺڻ وارا ھوندا، بيشڪ اُھي ھن کان اڳ چڱائي ڪندڙ ھوا.'),
(4692, 51, 17, 'رات جو ٿورو سمھندا ھوا.'),
(4693, 51, 18, '۽ اُھي اَسرن جو بخشش گھرندا ھوا.'),
(4694, 51, 19, '۽ سندن مالن ۾ سوال ڪندڙ ۽ نه سوال ڪندڙ مُحتاج جو حصّو ھو.'),
(4695, 51, 20, '۽ يقين ڪندڙن لاءِ زمين ۾ نشانيون آھن.'),
(4696, 51, 21, '۽ اوھان جي جندن ۾ به، پوءِ نٿا ڏسو ڇا؟'),
(4697, 51, 22, '۽ اوھان جي روزي آسمان ۾ آھي ۽ اُھو (به) جنھنجو اوھان کي انجام ڏجي ٿو.'),
(4698, 51, 23, 'پوءِ آسمان ۽ زمين جي پالڻھار جو قسم آھي ته اھا خبر سچي آھي جھڙي طرح اوھين ڳالھائيندا آھيو.'),
(4699, 51, 24, 'اِبراھيم جي سڳورن مھمانن جي خبر تو وٽ (نه) آئي آھي ڇا؟'),
(4700, 51, 25, 'جڏھن اُھي وٽس آيا تڏھن سلام چيائين، ھن (به) سلام چيو، (دل ۾ ڄاتائين ته ھي) اوپرا ماڻھو آھن.'),
(4701, 51, 26, 'پوءِ پنھنجي گھر وارن ڏانھن تکو ھليو ۽ ٿلھو گابو (پچائي) آندائين.'),
(4702, 51, 27, 'پوءِ اھو انھن کي ويجھو ڪيائين چيائين ته ڇونه ٿا کائو؟'),
(4703, 51, 28, 'پوءِ کائن دل ۾ ڊپ رکيائين، چيائون ته نه ڊڄ، ۽ اُن کي ھڪ داناءَ نينگر جي مبارڪ ڏنائون.'),
(4704, 51, 29, 'پوءِ سنديس زال دانھن ڪندي سامھون آئي پوءِ پنھنجو منھن ڪٽيائين ۽ چيائين ته ٻڍڙي سنڍ (ڄڻيندي ڇا؟).'),
(4705, 51, 30, 'چيائون ته ائين ئي تنھنجي پالڻھار فرمايو آھي، ڇوته اُھو حِڪمت وارو ڄاڻندڙ آھي.'),
(4706, 51, 31, '(ابراھيم) چيو ته اي قاصد‍ؤ اوھان جو ڪھڙو غرض آھي.'),
(4707, 51, 32, 'چيائون ته بيشڪ اسين ھڪڙي ڏوھاري قوم ڏانھن موڪليا ويا آھيون.'),
(4708, 51, 33, 'ته مٿن پڪي مٽيءَ جون گليليون وسايون.'),
(4709, 51, 34, 'جي حد کان لنگھندڙن لاءِ تنھنجي پالڻھار وٽ نشان ڪيل آھن.'),
(4710, 51, 35, 'پوءِ مؤمنن مان اتي جيڪو ھو تنھن کي ٻاھر ڪڍيوسون.'),
(4711, 51, 36, 'پوءِ اُتي مسلمانن جي ھڪ گھر کانسواءِ (ٻيو ڪو گھر) نه ڏٺوسون.'),
(4712, 51, 37, '۽ جيڪي ڏکوئيندڙ عذاب کان ڊڄندا آھن تن لاءِ اُتي (عبرت لاءِ) نشاني ڇڏي سون.'),
(4713, 51, 38, '۽ مُوسىٰ (جي قصّي) ۾ (به عِبرت آھي) جڏھن کيس پڌري دليل سان فرعون ڏانھن موڪليوسون.'),
(4714, 51, 39, 'پوءِ ھن پنھنجي لشڪر سميت منھن موڙيو چيائين ته (ھي) جادوگر يا چريو آھي.'),
(4715, 51, 40, 'تنھنڪري اُن کي ۽ سندس لشڪر کي پڪڙيوسون پوءِ اُن کي ملامت وارو ڪري سمنڊ ۾ اُڇليوسون.'),
(4716, 51, 41, '۽ عاد جي (قصي) ۾ (به عبرت آھي) جڏھن اھڙو بي برڪتو واءُ موڪليوسون.'),
(4717, 51, 42, 'جو جنھن شيء تي اچي پھچي تنھن کي ڳريل ھڏي وانگر ڪرڻ کانسواءِ نه ڇڏي.'),
(4718, 51, 43, '۽ ثمود (جي قصّي) ۾ (به عبرت آھي) جڏھن کين چيو ويو ته ھڪ مُدت تائين مزا ماڻيو.'),
(4719, 51, 44, 'پوءِ پنھنجي پالڻھار جي حُڪم (مڃڻ) کان ھٺ ڪيائون تنھنڪري سندن ڏسندي کين (ھڪ) سخت ڪڙڪي ورتو.'),
(4720, 51, 45, 'پوءِ نڪي اُٿي سگھيا ۽ نڪي وير وٺڻ وارا ھوا.'),
(4721, 51, 46, '۽ (ھن کان) اڳ نوح جي قوم (کي ھلاڪ ڪيوسون) بيشڪ اُھي بدڪار ماڻھو ھوا.'),
(4722, 51, 47, '۽ آسمان کي (پنھنجيءَ) قوت سان بڻايوسون ۽ بيشڪ اسين سگھارا آھيون.'),
(4723, 51, 48, '۽ زمين کي پکيڙيوسون جو چڱا پکيڙيندڙ آھيون.'),
(4724, 51, 49, '۽ سڀڪنھن شيءَ مان جوڙو جوڙو بڻايوسون ته مانَ اوھين نصيحت وٺو.'),
(4725, 51, 50, 'پوءِ الله ڏانھن ڀڄو، بيشڪ آءٌ سندس طرف کان اوھان لاءِ پڌرو ڊيڄاريندڙ آھيان.'),
(4726, 51, 51, '۽ الله سان (ٻيو) ڪو معبُود نه ٺھرايو، بيشڪ آءٌ سندس پار کان اوھان لاءِ پڌرو ڊيڄاريندڙ آھيان.'),
(4727, 51, 52, 'اھڙي طرح جيڪي انھن کان اڳ ھوا تن وٽ ڪوبه پيغمبر نه آيو پر چيائون ته جادوگر يا چريو آھي.'),
(4728, 51, 53, 'ان (چوڻ) جي ھڪ ٻئي کي وصيت ڪندا آيا آھن ڇا؟ (نه!) بلڪ اُھي نافرمان قوم آھن.'),
(4729, 51, 54, 'تنھنڪري (اي پيغمبر) کائن منھن موڙ ھاڻي تون ميار وارو نه آھين.'),
(4730, 51, 55, '۽ سمجھاڻي ڏيندو رھ ڇوته سمجھائڻ مؤمنن کي نفعو ڏيندو آھي.'),
(4731, 51, 56, '۽ جِنّن ۽ ماڻھن کي رڳو پنھنجي عبادت لاءِ پيدا ڪيوسون.'),
(4732, 51, 57, 'نڪي کائن ڪا روزي ٿو گھران ۽ نڪي گھران ٿو ته مون کي کارائين.'),
(4733, 51, 58, 'بيشڪ الله ئي روزي ڏيندڙ آھي جو تمام سگھارو زبردست آھي.'),
(4734, 51, 59, 'پوءِ بيشڪ جن ظلم ڪيو تن لاءِ سندن يارن جي سزا جھڙو عذاب (جو ڀاڱو) آھي پوءِ مون کان جلد (عذاب) نه گُھرن.'),
(4735, 51, 60, 'پوءِ ڪافرن لاءِ اُن سندن ڏينھن کان خرابي آھي جنھن جو کين وعدو ڏنو وڃي ٿو.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, '(جبل) طُور جو قسم آھي.'),
(4737, 52, 2, '۽ لکيل ڪتاب جو (قسم) آھي.'),
(4738, 52, 3, 'جو کُليل ورقن ۾ (لکيل) آھي.'),
(4739, 52, 4, '۽ آباد گھر جو (قسم) آھي.'),
(4740, 52, 5, '۽ مٿاھينءَ ڇت جو (قسم) آھي.'),
(4741, 52, 6, '۽ اُڀامندڙ درياءَ جو (قسم) آھي.'),
(4742, 52, 7, 'ته بيشڪ تنھنجي پالڻھار جو عذاب ضرور ٿيڻو آھي.'),
(4743, 52, 8, 'اُن کي ڪوبه ٽارڻ وارو ڪونھي.'),
(4744, 52, 9, 'جنھن ڏينھن آسمان موج ھڻي چُرندو.'),
(4745, 52, 10, '۽ جبل چڱيءَ طرح ھلندا.'),
(4746, 52, 11, 'تنھن ڏينھن اُنھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.'),
(4747, 52, 12, 'جيڪي اَجاين ڳالھين ۾ راند ڪندا آھن.'),
(4748, 52, 13, 'جنھن ڏينھن دوزخ جي باھ ڏانھن ڌڪا ڏيئي روانا ڪبا.'),
(4749, 52, 14, '(تنھن ڏينھن چئبن ته) ھي اُھا باھ آھي جنھن کي ڪُوڙ ڀائندا ھئو.'),
(4750, 52, 15, 'ھيءُ جادو آھي ڇا يا اوھين نه ٿا ڏسو؟'),
(4751, 52, 16, 'ان ۾ گھڙو پوءِ صبر ڪريو يا صبر نه ڪريو، (ته) اوھان لاءِ ھڪ جھڙي (ڳالھ) آھي، جيڪي اوھين ڪندا ھئو رڳو تنھنجو بدلو اوھان کي ڏنو ويندو.'),
(4752, 52, 17, 'بيشڪ پرھيزگار باغن ۽ نعمتن ۾ ھوندا.'),
(4753, 52, 18, 'جيڪا (نعمت) کين سندن پالڻھار ڏني تنھن سان خوش ھوندا، ۽ (اُنھي ڪري به خوش ھوندا جو) سندن پالڻھار کين دوزخ جي عذاب کان بچايو.'),
(4754, 52, 19, '(چئبن ته) انھي ڪري جو (چڱا) ڪم ڪندا ھئو مزي سان کائو ۽ پيئو.'),
(4755, 52, 20, 'ھڪ ٻئي جي برابر وڇايلن تختن تي ٽيڪ ڏيئي ويٺل ھوندا، ۽ کين وڏين اکين وارين حورن سان پرڻائينداسون.'),
(4756, 52, 21, '۽ جن ايمان آندو ۽ سندن اولاد ايمان ۾ سندن رستو ھليا تن سان سندن اولاد کي ملائينداسون ۽ کين سندن عملن مان ڪجھ به نه گھٽائينداسون، سڀڪنھن مڙس جيڪي ڪمايو تنھن ۾ (اُھو پاڻ) ڳه ٿيل آھي.'),
(4757, 52, 22, '۽ انھن کي ميوو ۽ گوشت جنھن (قسم) مان گھرندا (تنھن مان) پيا ڏينداسون.'),
(4758, 52, 23, 'اُتي شراب جا پيالا ھڪ ٻئي کان پيا وٺندا جنھن ۾ نڪي بڪ بڪ ۽ نڪو گناھ ۾ پوڻ ھوندو.'),
(4759, 52, 24, '۽ وٽن اھڙا نينگر سندن آس پاس (خدمت لاءِ) پيا ايندا ويندا جو ڄڻڪ اُھي ڍڪيل موتي آھن.'),
(4760, 52, 25, '۽ اُنھن مان ھڪڙا ٻـين ڏانھن پاڻ ۾ پڇا ڪرڻ ڪرڻ لاءِ مُنھن ڪندا.'),
(4761, 52, 26, 'چوندا ته بيشڪ اسين ھن کان اڳ پنھنجي گھر وارن ۾ ڊڄندڙ ھئاسون.'),
(4762, 52, 27, 'پوءِ الله اسان تي احسان ڪيو ۽ اسان کي دوزخ جي عذاب کان بچايائين.'),
(4763, 52, 28, 'بيشڪ اسين ھن کان اڳ کيس سڏيندا ھواسون، بيشڪ اُھوئي اِحسان ڪندڙ مھربان آھي.'),
(4764, 52, 29, 'پوءِ (اي پيغمبر کين) نصيحت ڪر جو تون پنھنجي پالڻھار جي فضل سان نڪي ڀُوپُو آھين ۽ نڪي چريو آھين.'),
(4765, 52, 30, 'بلڪ چوندا آھن ڇا ته شاعر آھي (۽ اسين) سندس حق ۾ زماني جي ڦير گھير اچڻ جا منتظر آھيون.'),
(4766, 52, 31, '(انھن ڪافرن کي) چؤ ته اوھين به انتظار ڪڍو جو بيشڪ آءٌ به اوھان سان انتظار ڪڍندڙ آھيان.'),
(4767, 52, 32, 'کين سندن عقل اھو ڏس ڏيندا آھن ڇا يا اُھي شرارتي قوم آھي.'),
(4768, 52, 33, 'چوندا آھن ڇا ته ھِن قرآن کي پاڻون بڻايو اٿس، بلڪ ايمان نه ٿا آڻين.'),
(4769, 52, 34, 'پوءِ (کين) جڳائي ته جيڪڏھن سچا آھن ته اُن جھڙو ڪو سخن (بڻائي) آڻين.'),
(4770, 52, 35, 'اھي بي سبب (پاڻھي) پيدا ٿي پيا آھن ڇا يا اُھي (پاڻ) پيدا ڪندڙ آھن؟'),
(4771, 52, 36, 'آسمانن ۽ زمين کي بڻايو اٿن ڇا، بلڪ اُھي يقين نه ٿا آڻين.'),
(4772, 52, 37, 'تنھنجي پالڻھار جا خزانا انھن وٽ آھن ڇا يا اُھي داروغا آھن؟'),
(4773, 52, 38, 'کين ڪا ڏاڪڻ آھي ڇا جنھن تي (چڙھي) ڪن ڏئي ٻڌندا آھن؟ پوءِ جڳائي ته اُنھن مان ٻُڌندڙ ڪو پڌرو دليل آڻي.'),
(4774, 52, 39, 'الله کي ڌيئر آھن ڇا ۽ اوھان کي پُٽ؟'),
(4775, 52, 40, 'اُنھن کان مزوري گھرين ٿو ڇا؟ جو اُھي (اُنھيءَ) چٽيءَ کان ڳوري بار (ھيٺ) ٿيا آھن.'),
(4776, 52, 41, 'اُنھن وٽ ڳُجھ جو علم آھي ڇا جو اُھي لکندا آھن.'),
(4777, 52, 42, 'ڪا بڇڙي رٿ ڪرڻ گھرندا آھن ڇا؟ پوءِ جن ڪُفر ڪيو سي ئي فريب ۾ پيل آھن.'),
(4778, 52, 43, 'انھن لاءِ الله کانسواءِ (ٻيو) ڪو عبادت جو لائق آھي ڇا؟ (ساڻس) جيڪو شريڪ مقرر ڪندا آھن تنھن کان الله پاڪ آھي.'),
(4779, 52, 44, '۽ جيڪڏھن آسمان مان ڪو ٽڪرو ڪرندڙ ڏسندا ته چوندا ته گھاٽو ڪَڪر آھي.'),
(4780, 52, 45, 'پوءِ (اي پيغمبر) اُنھن کي ايستائين ڇڏي ڏي جيستائين پنھنجو اُھو ڏينھن ڏسن جنھن ۾ بيھوش ڪيو ويندن.'),
(4781, 52, 46, 'جنھن سندن فريب ڪجھ به نه ٽاريندو ۽ نڪي کين مدد ڏبي.'),
(4782, 52, 47, '۽ بيشڪ ظالمن لاءِ ھن کانسواءِ ٻيو عذاب (به) آھي پر اُنھن مان گھڻا نه ڄاڻندا آھن.'),
(4783, 52, 48, '۽ (اي پيغمبر) تون پنھنجي پالڻھار جي حُڪم (اچڻ) تائين صبر ڪر ڇوته تون اسان جي نظر ھيٺ آھين ۽ جنھن مھل تون اُٿين (تنھن مھل) پنھنجي پالڻھار جي ساراھ سان پاڪائي بيان ڪر'),
(4784, 52, 49, '۽ ڪجھ رات جو (به) سندس پاڪائي بيان ڪر ۽ ستارن جي لھڻ کانپوءِ (به).');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'ستاري جو قسم آھي جڏھن لھي.'),
(4786, 53, 2, 'ته اوھان جو (ھي) سنگتي (محمّد) نڪي ڀُلو ۽ نڪي بي راھو ھليو.'),
(4787, 53, 3, '۽ نڪي (نفس جي) سَڌ کان ڳالھائيندو آھي.'),
(4788, 53, 4, 'ھيُ قرآن رڳو وحي آھي جو موڪليو ويندو آھي.'),
(4789, 53, 5, 'اُن کي ڏاڍي سگھ واري سيکاريو آھي.'),
(4790, 53, 6, 'جو زور وارو آھي، پوءِ اُھو سِڌو بيٺو.'),
(4791, 53, 7, '۽ اُھو (آسمان جي) مٿاھين ڪناري تي ھو.'),
(4792, 53, 8, 'وري ويجھو ٿيو پوءِ ھيٺ لٿو.'),
(4793, 53, 9, 'پوءِ ٻن ڪمانن جيتري وِٿي ھئي يا (اُن کان به) تمام ويجھو ھو.'),
(4794, 53, 10, 'پوءِ الله جي ٻانھي ڏانھن پيغام پھچايائين جيڪي پھچايائين.'),
(4795, 53, 11, 'جيڪي (پيغمبر جي) دل ڏٺو سو اُن ڪوڙ نه ڄاتو.'),
(4796, 53, 12, 'جيڪي (پيغمبر) ڏسندو آھي تنھن ۾ اُن سان جھڳڙو ڇو ڪندا آھيو؟'),
(4797, 53, 13, '۽ بيشڪ اُنھيءَ (ملائڪ) کي (انھيءَ مھل) ٻيو ڀيرو (سندس اصلي صورت ۾) ڏٺو ھوائين.'),
(4798, 53, 14, 'سِدۡرَةُ الۡمُنۡتَھىٰ وٽ.'),
(4799, 53, 15, 'اُن وٽ رھڻ جو بھشت آھي.'),
(4800, 53, 16, 'جنھن مھل سِدرة کي انھيءَ ٿي ڍڪيو جنھن ڍڪيو ٿي.'),
(4801, 53, 17, '(پيغمبر جي اک) نڪي لڙي ۽ نڪي حد کان لنگھي.'),
(4802, 53, 18, 'بيشڪ ڪي وڏيون نشانيون پنھنجي پالڻھار جون ڏٺائين.'),
(4803, 53, 19, 'لات ۽ عُزّىٰ کي ڏٺو اَٿوَ ڇا؟'),
(4804, 53, 20, '۽ پوئين ٽين منات کي به؟'),
(4805, 53, 21, 'ڇا اوھان لاءِ پٽ آھن ۽ الله لاءِ ڌيئرون.'),
(4806, 53, 22, 'اِھو ورھاڱو اُنھيءَ مھل بي انصافيءَ وارو آھي.'),
(4807, 53, 23, 'اھي (سڀ) رڳا نالا آھن جي اوھان پاڻ ۽ اوھان جي پِين ڏاڏن مُقرّر ڪيا آھن الله اُن (جي ثابتيءَ) جو ڪو دليل نازل نه ڪيو آھي، (ھيُ) رڳو گمان تي ھلندا آھن ۽ جيڪي (سندن) جيءَ سَڌ ڪندا آھن (تنھن تي به)، ۽ بيشڪ سندن پالڻھار کان وٽن ھدايت آئي آھي.'),
(4808, 53, 24, 'ماڻھو جيڪا سڌ ڪندو آھي سا کيس ملندي ڇا؟'),
(4809, 53, 25, 'پوءِ ھو جَڳ ۽ ھيُ جَڳ الله جو آھي.'),
(4810, 53, 26, '۽ آسمانن ۾ ڪيترائي ملائڪ آھن جن جي پارت ذري جيترو به نفعو نه ڪندي پر الله جي موڪل ڏيڻ کانپوءِ جنھن لاءِ گھري ۽ راضي ٿئي.'),
(4811, 53, 27, 'بيشڪ جيڪي آخرت کي نه مڃيندا آھن سي ملائڪن تي ڌيئرن جا نالا رکندا آھن.'),
(4812, 53, 28, '۽ کين ان (ڳالھ) جي ڪا خبر نه آھي، رُڳو گمان تي ھلندا آھن، ۽ بيشڪ گمان حقيقت جي سڃاڻڻ ۾ ڪجھ به نفعو نه ڏيندو آھي.'),
(4813, 53, 29, 'پوءِ (اي پيغمبر) انھيءَ کان منھن موڙ جنھن اسان جي ياد ڪرڻ کان مُنھن ڦيرايو ۽ دنيا جي حياتيءَ کانسواءِ (ٻيو) ڪي نه گھريائين.'),
(4814, 53, 30, 'اھا سندن علم جي پڄاڻي آھي، بيشڪ تنھنجو پالڻھار اُنھيءَ کي چڱيءَ طرح ڄاڻندڙ آھي جيڪو سندس واٽ کان ڀُلو ۽ جيڪو سڌي رستي ھليو تنھنکي (به) اُھو چڱي طرح ڄاڻندڙ آھي.'),
(4815, 53, 31, '۽ جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله جو آھي ته جن بڇڙا ڪم ڪيا تن کي انھن ڪمن جي ڪري نيٺ سزا ڏئي ۽ جن چڱايون ڪيون تن کي ڀلائيءَ جو بدلو ڏئي.'),
(4816, 53, 32, 'جيڪي ننڍن گناھن کانسواءِ وڏن گناھن ۽ بي حيائيءَ کان پاڻ کي بچائيندا آھن، بيشڪ تنھنجو پالڻھار (اُنھن لاءِ) وڏي بخشش وارو آھي، اُھو اوھان کي وڌيڪ ڄاڻندڙ آھي جڏھن اوھان کي زمين مان پيدا ڪيائين ۽ جڏھن اوھين پنھنجي مائن جي پيٽن ۾ ٻار ھئؤ، تنھنڪري اوھين پاڻ کي نه ساراھيو، الله انھيءَ کي چڱي طرح ڄاڻندڙ آھي جيڪو پرھيزگار ٿيو.'),
(4817, 53, 33, '(اي پيغمبر) ڇا پوءِ ڏٺو اٿئي انھي کي جيڪو ڦريو.'),
(4818, 53, 34, '۽ ٿورو (مال) ڏنائين ۽ سخت دل ٿيو.'),
(4819, 53, 35, 'وٽس ڳجھ جي خبر آھي ڇا جو ڄڻڪ اُھو (سڀ ڪجھ) اکين سان ڏسندو آھي.'),
(4820, 53, 36, 'اُنھيءَ جي خبر کيس نه ڏني وئي ڇا جيڪي موسىٰ جي صحيفن ۾ (لکيل) آھي.'),
(4821, 53, 37, '۽ (جيڪي) ابراھيم (جي صحيفن ۾ آھي) جنھن (الله جي حَقن کي) پورو ڪيو.'),
(4822, 53, 38, '(ھيُ حُڪم) ته ڪو بار کڻندڙ ڪنھن ٻئي (جي گناھ) جو بار نه کڻندو.'),
(4823, 53, 39, '۽ ته ماڻھوءَ کي رڳو اُھائي (ڪمائي) ملندي جيڪا ڪيائين.'),
(4824, 53, 40, '۽ ته سندس ڪمائي جلد ڏٺي ويندي.'),
(4825, 53, 41, 'وري کيس تمام پورو بدلو (انھي ڪمائي موجب) ڏنو ويندو.'),
(4826, 53, 42, '۽ ته تنھنجي پالڻھار ڏانھن موٽڻ آھي.'),
(4827, 53, 43, '۽ ته اُن کلايو ۽ رئاريو.'),
(4828, 53, 44, '۽ ته اُن ماريو ۽ جياريو.'),
(4829, 53, 45, '۽ ته اُن (الله) نر ۽ مادي جوڙو جوڙو بڻايو.'),
(4830, 53, 46, 'منيءَ جي ھڪ ڦڙي مان جڏھن (ڳڀيرڻ ۾) ھاريو وڃي.'),
(4831, 53, 47, '۽ ته اُن تي اُھو ٻيو (ڀيرو) پيدا ڪرڻ لازم آھي.'),
(4832, 53, 48, '۽ ته اُن آسودو ڪيو ۽ موڙي ڏنائين.'),
(4833, 53, 49, '۽ ته اُھو ئي شِعۡرىٰ تاري جو پالڻھار آھي.'),
(4834, 53, 50, '۽ ته اُن پھرين عاد کي ناس ڪيو.'),
(4835, 53, 51, '۽ ثمود کي (به) پوءِ انھن مان (ڪنھين کي) باقي نه ڇڏيائين.'),
(4836, 53, 52, '۽ اُن کان اڳ نُوح جي قوم کي به ھلاڪ ڪيائين، ڇوته اُھي ڏاڍا ظالم ۽ تمام حد کان لنگھيل ھوا.'),
(4837, 53, 53, '۽ (شھر) اونڌي ڪيل کي ھيٺ اُڇليائين.'),
(4838, 53, 54, 'پوءِ اُن کي ڍڪيو جنھن ڍڪيو.'),
(4839, 53, 55, 'پوءِ اي ماڻھو پنھنجي پالڻھار جي نعمتن مان ڪھڙيءَ ۾ شڪ ٿو ڪرين.'),
(4840, 53, 56, 'ھي (پيغمبر) اڳين ڊيڄاريندڙن (جي جنس) مان (ھڪ) ڊيڄاريندڙ آھي.'),
(4841, 53, 57, 'ويجھي اچڻ واري (قيامت) ويجھي آھي.'),
(4842, 53, 58, 'اُن کي الله کانسواءِ ڪو ظاھر ڪرڻ وارو ڪونھي.'),
(4843, 53, 59, 'ھن ڳالھ کان تعجب ڪندا آھيو ڇا؟'),
(4844, 53, 60, '۽ کلندا آھيو ۽ نه رئندا آھيو.'),
(4845, 53, 61, '۽ اوھين راند ڪندڙ آھيو.'),
(4846, 53, 62, 'پوءِ الله کي سجدو ڪريو ۽ عبادت ڪريو.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'قيامت ويجھي آئي ۽ چنڊ چيرجي پيو.'),
(4848, 54, 2, '۽ جيڪڏھن (ڪافر) ڪا نشاني ڏسندا ته مُنھن موڙيندا ۽ چوندا ته ھيء قديمي جادو آھي.'),
(4849, 54, 3, '۽ ڪوڙ ڄاتائون ۽ پنھنجن سَڌن تي ھليا ۽ سڀڪو ڪم پنھنجي وقت تي ٺھرايل آھي.'),
(4850, 54, 4, '۽ بيشڪ وٽن اُھي خبرون آيون آھن جن ۾ جھڻڪ آھي.'),
(4851, 54, 5, 'پوري ڏاھپ (به) آھي پوءِ عِبرت جون ڳالھيون کين ڪو فائدو نه ٿيون ڏين.'),
(4852, 54, 6, 'تنھنڪري کانئن مُنھن موڙ، جنھن ڏينھن سڏيندڙ اَڻ وڻندڙ شيءَ ڏانھن سڏيندو.'),
(4853, 54, 7, '(تنھن ڏينھن) پنھنجن جھڪن اکين سان قبرن مان ائين نڪرندا جو ڄڻڪ اُھي پکڙيل مڪڙ آھن.'),
(4854, 54, 8, '(اُن) سڏيندڙ ڏانھن ڊوڙندا ويندا، ڪافر ته ھيُ سخت ڏينھن آھي.'),
(4855, 54, 9, 'انھن کان اڳ نوح جي قوم ڪوڙ ڀانيو پوءِ اسان جي ٻانھي کي ڪوڙو ڄاتائون ۽ چيائون ته چريو آھي ۽ ساڻس سخت ڳالھايو ويو.'),
(4856, 54, 10, 'پوءِ ھن پنھنجي پالڻھار کي عرض ڪيو (۽ چيائين) ته بيشڪ آءٌ ھيڻو آھيان تنھنڪري تون کائن وير وٺ.'),
(4857, 54, 11, 'پوءِ آسمان جا دروازا گھڻي وھندڙ پاڻيءَ سان کولياسون.'),
(4858, 54, 12, '۽ زمين مان چشمان جاري ڪياسون پوءِ (چوڌاري) پاڻي گڏ ٿيو جنھن ڪم لاءِ (اُھو) ٺھرايو ويو ھو.'),
(4859, 54, 13, '۽ کيس تختن ۽ ميخن واريءَ (ٻيڙي) تي چاڙھيوسون.'),
(4860, 54, 14, 'جا اسان جي اکين آڏو ٿي ھلي، انھيءَ شخص جي بدلي وٺڻ لاءِ جنھن کي نه مڃيو ھوائون.'),
(4861, 54, 15, '۽ بيشڪ اُنھي (سزا) کي (عبرت لاءِ) ھڪ نشاني ڪري ڇڏيوسون پوءِ آھي ڪو نصيحت وٺڻ وارو؟'),
(4862, 54, 16, 'پوءِ منھنجو عذاب ۽ منھنجا دڙڪا ڪھڙي طرح ھوا.'),
(4863, 54, 17, '۽ بيشڪ قرآن کي نصيحت وٺڻ لاءِ آسان ڪيو اٿون پوءِ آھي ڪو نصيحت وٺڻ وارو؟'),
(4864, 54, 18, 'عاد (قوم) ڪوڙ ڀانيو پوءِ منھنجو عذاب ۽ منھنجا دڙڪا ڪھڙيءَ طرح ھوا؟'),
(4865, 54, 19, 'بيشڪ اسان سخت طوفان کي تمام نڀاڳي ڏينھن ۾ مٿن موڪليو.'),
(4866, 54, 20, 'جو ماڻھن کي پٽيائين ٿي ڄڻڪ اُھي پاڙون پٽيل کجيءَ جا تڙ آھن.'),
(4867, 54, 21, 'پوءِ منھنجو عذاب ۽ منھنجا دڙڪا ڪھڙي طرح ھوا؟'),
(4868, 54, 22, '۽ بيشڪ قرآن کي نصيحت وٺڻ لاءِ آسان ڪيو اٿون پوءِ آھي ڪو نصيحت وٺڻ وارو؟'),
(4869, 54, 23, 'ثمود جي قوم ڊيڄاريندڙن کي ڪوڙو ڀانيو.'),
(4870, 54, 24, 'پوءِ چيائون ته پنھنجي قوم مان ھڪ اھڙي ماڻھوءَ جي تابعداري ڪريون ڇا؟ (جي ائين ڪريون ته) بيشڪ اسين انھي مھل ضرور گمراھي ۽ چريائي ۾ ھونداسون.'),
(4871, 54, 25, 'اسان جي وچان اُنھيءَ تي (ئي) وحيُ نازل ڪيو ويو ڇا؟ بلڪ اُھو وڏو ڪوڙو پاڻ پڏائيندڙ آھي.'),
(4872, 54, 26, 'سڀاڻي ڄاڻندا ته پاڻ پڏائيندڙ ڏاڍو ڪوڙو ڪير آھي.'),
(4873, 54, 27, 'بيشڪ اسين سندن پرک لاءِ (ھڪ) ڏاچي موڪلينداسون پوءِ (اي صالح) انھن جو منتظر رھ ۽ صبر ڪر.'),
(4874, 54, 28, '۽ کين خبردار ڪر ته سندن وچ ۾ پاڻي وراھيل آھي، ھر ڪو (پنھنجي) واري تي حاضر ٿئي.'),
(4875, 54, 29, 'پوءِ پنھنجي سنگتيءَ کي سڏيائون پوءِ وڌيڪ ھلت ڪيائين ۽ منڊو ڪيائين.'),
(4876, 54, 30, 'پوءِ منھنجو عذاب ۽ منھنجا دڙڪا ڪھڙي طرح ھوا؟'),
(4877, 54, 31, 'بيشڪ اسان ھڪ ڌڪاءُ مٿن موڪليو پوءِ ڀتي پيھر وانگر (ڍير) ٿي پيا.'),
(4878, 54, 32, '۽ بيشڪ قرآن کي نصيحت وٺڻ لاءِ آسان ڪيو اَٿون پوءِ آھي ڪو نصيحت وٺڻ وارو؟'),
(4879, 54, 33, 'لُوط جي قوم ڊيڄاريندڙن کي ڪوڙو ڄاتو.'),
(4880, 54, 34, 'بيشڪ اسان مٿن ڪنڪريٽ وسائيندڙ واءُ کي موڪليو سواءِ لُوط جي گھر وارن جي، جو انھن کي اَسر مھل بچايوسون.'),
(4881, 54, 35, 'پاڻ وٽان فضل سان، جنھن شڪرانو ڪيو تنھن کي ائين ئي بدلو ڏيندا آھيون.'),
(4882, 54, 36, '۽ بيشڪ (لُوط) اسان جي پڪڙڻ کان کين ڊيڄاريو ھو پوءِ ھٿون انھن ڊيڄارڻ جي ڪري تڪرار ڪيائون.'),
(4883, 54, 37, '۽ بيشڪ لُوط کي سندس مھمانن بَنسبت ڇڪتاڻ ڪيائون پوءِ سندن اکين (جي ديد) کي وڃايوسون (پوءِ چيوسون) ته منھنجو عذاب ۽ منھنجا دڙڪا چکو.'),
(4884, 54, 38, '۽ بيشڪ ھميشه وارو عذاب انھن تي صُبح جو پھتو.'),
(4885, 54, 39, 'پوءِ (چيو وين) ته منھنجو عذاب ۽ منھنجا دڙڪا چکو.'),
(4886, 54, 40, '۽ بيشڪ قرآن کي نصيحت وٺڻ لاءِ آسان ڪيو اَٿون پوءِ آھي ڪو نصيحت وٺڻ وارو.'),
(4887, 54, 41, '۽ بيشڪ فرعون جي قوم ڏانھن ڊيڄاريندڙ آيا.'),
(4888, 54, 42, 'ھنن اسان جي مڙني نشانين کي ڪوڙو ڄاتو تنھنڪري کين غالب پڪڙڻ واري وانگر پڪڙيوسون.'),
(4889, 54, 43, '(اي قريشؤ) اوھان جا ڪافر انھن ٽولين کان وڌيڪ ڀلا آھن ڇا يا اوھان لاءِ اڳين ڪتابن ۾ ڪا ڇوٽڪي جي چٺي آھي ڇا؟'),
(4890, 54, 44, 'چوندا آھن ڇا ته اسين وير وٺڻ واري جماعت آھيون.'),
(4891, 54, 45, 'انھي جماعت کي جلد شڪست ڏبي ۽ پُٺي ڦيرائيندا.'),
(4892, 54, 46, 'بلڪ سندن انجام جي جاءِ قيامت آھي ۽ قيامت ڏاڍي سخت ۽ تمام ڪوڙي آھي.'),
(4893, 54, 47, 'بيشڪ ڏوھاري گمراھي ۽ چريائي ۾ آھن.'),
(4894, 54, 48, '(ياد ڪر) جنھن ڏينھن انھن کي سندن مُنھن ڀر (دوزخ جي) باھ ۾ گھلبو، چئبو ته ھاڻي دوزخ جو مزو چکو.'),
(4895, 54, 49, 'بيشڪ اسان سڀڪنھن شيء کي ٺھرايل انداز سان بڻايو.'),
(4896, 54, 50, '۽ اسان جو ڪم رڳو اک ڇنڀ ۾ ٿيڻ جھڙو ھڪ سُخن آھي.'),
(4897, 54, 51, '۽ بيشڪ اوھان جھڙيون (ڪيتريون ئي) ٽوليون ھلاڪ ڪيون سون پوءِ آھي ڪو نصيحت وٺڻ وارو.'),
(4898, 54, 52, '۽ جيڪي ڪيو اٿن سو (سڀ سندن) اعمالنامن ۾ (لکيل) آھي.'),
(4899, 54, 53, '۽ سڀڪي ننڍو وڏو لکيل آھي.'),
(4900, 54, 54, 'بيشڪ پرھيزگار باغن ۽ نھرين ۾ ھوندا.'),
(4901, 54, 55, 'اُھي سچي مجلس ۾ سگھ واري بادشاھ وٽ رھندا.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'ٻاجھاري (الله).'),
(4903, 55, 2, 'قرآن سيکاريو.'),
(4904, 55, 3, 'ماڻھو کي پيدا ڪيائين.'),
(4905, 55, 4, 'ان کي پڌرو ڳالھائڻ سيکاريائين.'),
(4906, 55, 5, 'سج ۽ چنڊ پوري حساب تي ھلندا آھن.'),
(4907, 55, 6, '۽ وليون ۽ وڻ (الله کي) سجدو ڪندا آھن.'),
(4908, 55, 7, '۽ آسمان کي بلند ڪيائين ۽ انصاف جي ترازي رکيائين.'),
(4909, 55, 8, 'ھن لاءِ ته تور ۾ حد کان نه لنگھو.'),
(4910, 55, 9, '۽ انصاف سان پورو توريندا رھو ۽ تور ۾ کوٽ نه ڪريو.'),
(4911, 55, 10, 'ماڻھن لاءِ زمين کي پکيڙيائين.'),
(4912, 55, 11, 'منجھس (ھر جنس) جو ميوو ۽ چپڙين واريون کجيون آھن.'),
(4913, 55, 12, '۽ بُھ وارو اَنُّ ۽ سرھاڻيون (به اُن ۾) آھن.'),
(4914, 55, 13, 'پوءِ (اي جنّؤ ۽ ماڻھو) پنھنجي رب جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4915, 55, 14, 'ماڻھوءَ کي ٽِڪر جھڙي وڄندڙ مٽيءَ مان پيدا ڪيائين.'),
(4916, 55, 15, '۽ جنّ کي باھ جي الانبي مان بڻايائين.'),
(4917, 55, 16, 'پوءِ (اي جنّؤ ۽ ماڻھو) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4918, 55, 17, 'جو ٻنھي اوڀرن جو پالڻھار آھي ۽ ٻنھي اولھن جو پالڻھار آھي.'),
(4919, 55, 18, 'پوءِ (اي جنّؤ ۽ ماڻھو) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4920, 55, 19, 'ٻن دريائن (کاري ۽ مٺيءَ) کي وھايائين جو پاڻ ۾ گڏجي ٿا وھن.'),
(4921, 55, 20, 'انھن ٻنھي جي وچ ۾ اوٽ آھي جو ھڪ ٻئي تي زيادتي نه ڪندا آھن.'),
(4922, 55, 21, 'پوءِ (اي جنّؤ ۽ ماڻھو) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4923, 55, 22, 'انھن ٻنھي مان موتي ۽ مرجان نڪرندا آھن.'),
(4924, 55, 23, 'پوءِ (اي جنّو ۽ ماڻھو) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4925, 55, 24, '۽ سمنڊ ۾ ھلندڙ جھاز (سڙھ کنيل) جبلن وانگر اُچا الله جا آھن.'),
(4926, 55, 25, 'پوءِ (اي جنّو ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4927, 55, 26, 'جيڪو به زمين تي آھي سو سڀ ناس ٿيندو.'),
(4928, 55, 27, '۽ (اي پيغمبر) تنھنجي پالڻھار جي ذات باقي رھندي جو عزت وارو ۽ نعمتن وارو آھي.'),
(4929, 55, 28, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4930, 55, 29, 'جيڪو به آسمانن ۽ زمين ۾ آھي سو سڀڪو الله کان گھرندو آھي، سڀڪنھن ڏينھن اُھو ڪم ۾ آھي.'),
(4931, 55, 30, 'پوءِ (اي جنّو ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4932, 55, 31, 'اي جنّو ۽ ماڻھؤ (اسين واندا ٿي) اوھان ڏانھن جلد متوجه ٿينداسون.'),
(4933, 55, 32, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4934, 55, 33, 'اي جِنّن ۽ ماڻھن جي ٽولي جيڪڏھن اوھين آسمانن ۽ زمين جي پاسن کان ٻاھر نڪري سگھندا آھيو ته نڪرو، وڏي ھمت کانسواءِ ٻي طرح نڪري نه سگھندؤ.'),
(4935, 55, 34, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4936, 55, 35, 'اوھان تي باھ جو اُلنبو ۽ دُونھون موڪلبو جو مقابلو ڪري نه سگھندؤ.'),
(4937, 55, 36, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4938, 55, 37, 'پوءِ جڏھن آسمان ڦاٽندو تڏھن اڌوڙ وانگر گلابي ٿيندو.'),
(4939, 55, 38, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4940, 55, 39, 'پوءِ اُن ڏينھن نڪي ماڻھو ۽ نڪي جن پنھنجي گناھ بابت پُڇبا.'),
(4941, 55, 40, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙن نعمتن جو انڪار ڪندؤ.'),
(4942, 55, 41, 'ڏوھاري پنھنجي شڪلين مان پيا سڃاتا ويندا ۽ پوءِ ڄُنڊن ۽ پيرن مان پڪڙبا.'),
(4943, 55, 42, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4944, 55, 43, '(چئبو ته) ھي اُھو دوزخ آھي جنھن کي گنھگارن ڪوڙ ڀانيو ھو.'),
(4945, 55, 44, 'انھيءَ (دوزخ) ۾ ۽ تَتي ٽھڪندڙ پاڻيءَ ۾ پيا ايندا ويندا.'),
(4946, 55, 45, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4947, 55, 46, '۽ جيڪو پنھنجي پالڻھار جي آڏو بيھڻ کان ڊنو تنھن لاءِ ٻه باغ آھن.'),
(4948, 55, 47, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4949, 55, 48, 'ٻئي باغ گھاٽا آھن.'),
(4950, 55, 49, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4951, 55, 50, 'انھن (ٻن باغن) ۾ ٻه چشما وھن ٿا.'),
(4952, 55, 51, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4953, 55, 52, 'انھن ٻنھي (باغن) ۾ سڀڪنھن ميوي مان ٻه ٻه جنسون آھن.'),
(4954, 55, 53, 'پوءِ (اي جنّو ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4955, 55, 54, 'اھڙن وڇاڻن تي ٽيڪ ڏئي ويٺا ھوندا جنھن جو استر اِسۡتَبۡرق پٽ مان ھوندو، ۽ انھن ٻنھي باغن جو ميوو ويجھو ھوندو.'),
(4956, 55, 55, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4957, 55, 56, 'اُنھن (باغن جي ماڙين) ۾ (شرم ڪري) ھيٺ نگاھ ڪرڻ واريون (حُورون) ھونديون جن کي اُنھن کان اڳ نڪي ڪنھن ماڻھو ۽ جنّ ڇھيو آھي.'),
(4958, 55, 57, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4959, 55, 58, 'اُھي (حُورون) ڄڻڪ ياقُوت ۽ مَرجان آھن.'),
(4960, 55, 59, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4961, 55, 60, 'چڱائيءَ جو بدلو چڱائيءَ کانسواءِ ٻيو نه آھي.'),
(4962, 55, 61, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھارجي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4963, 55, 62, '۽ اُنھن ٻن (باغن) کانسواءِ ٻيا ٻه باغ به آھن.'),
(4964, 55, 63, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4965, 55, 64, 'ٻئي ڏاڍا ساوا چھچ آھن.'),
(4966, 55, 65, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4967, 55, 66, 'اُنھن ٻنھي (باغن) ۾ ڦوھاري وانگر جوش سان پاڻي نڪرڻ وارا ٻه چشما ھوندا.'),
(4968, 55, 67, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4969, 55, 68, 'انھن ٻنھي باغن ۾ (ھر جنس جو) ميوو ۽ کجيون ۽ ڏاڙھون ھوندا.'),
(4970, 55, 69, 'پوءِ (ي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4971, 55, 70, 'انھن (باغن) ۾ سڳوريون سھڻيون زالون ھونديون.'),
(4972, 55, 71, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4973, 55, 72, 'حُورون تنبن منجھ پردہ نشين ھونديون.'),
(4974, 55, 73, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4975, 55, 74, 'انھن کي انھن (بھشتين) کان اڳ نڪي ماڻھو ۽ نڪي ڪنھن جنّ ڇھيو آھي.'),
(4976, 55, 75, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4977, 55, 76, 'ساون وھاڻن ۽ سھڻن پٿراڻين تي ٽيڪ ڏيئي ويٺل ھوندا.'),
(4978, 55, 77, 'پوءِ (اي جنّؤ ۽ ماڻھؤ) پنھنجي پالڻھار جي ڪھڙين نعمتن جو انڪار ڪندؤ.'),
(4979, 55, 78, 'تنھنجي پالڻھار عزت واري جو نالو وڏي برڪت وارو آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'جڏھن قيامت ايندي.'),
(4981, 56, 2, 'جنھنجي اچڻ ۾ ڪو ڪوڙ نه آھي.'),
(4982, 56, 3, 'ڪن کي جھڪي ڪرڻ واري ۽ ڪن کي مٿاھين ڪرڻ واري.'),
(4983, 56, 4, 'جڏھن زمين سخت ڌوڏبي.'),
(4984, 56, 5, '۽ جبل پوري طرح ڀڃي پڻي ڪيا ويندا.'),
(4985, 56, 6, 'پوءِ اُڏاريل دُز وانگر ٿيندا.'),
(4986, 56, 7, '۽ اوھين تڏھن ٽي ٽوليون ٿيندؤ.'),
(4987, 56, 8, 'پوءِ سڄي پاسي وارا، سڄي پاسي وارا ڪھڙا ته چڱا ھوندا.'),
(4988, 56, 9, '۽ کٻي پاسي وارا، کٻي پاسي وارا ڪھڙا (ته) بدحال ھوندا؟'),
(4989, 56, 10, '۽ (چڱاين ۾) اڳرائي ڪرڻ وارا اڳي ٿيڻ وارا.'),
(4990, 56, 11, 'اھي (ئي) مقرب آھن.'),
(4991, 56, 12, 'نعمتن وارن باغن ۾ ھوندا.'),
(4992, 56, 13, 'گھڻا پھرين مان.'),
(4993, 56, 14, '۽ ٿورا پوين مان.'),
(4994, 56, 15, '(سون سان) اُڻيل تخت تي.'),
(4995, 56, 16, 'ھڪ ٻئي جي آمھون سامھون ٿي مٿن ٽيڪ ڏئي ويٺل ھوندا.'),
(4996, 56, 17, 'ھميشه رھڻ وارا نينگر وٽن پيا ايندا ويندا.'),
(4997, 56, 18, 'آبخورن ۽ ڪوزن سان ۽ وھندڙ شراب مان (ڀريل) پيالا (آڻيندن).'),
(4998, 56, 19, '(اھڙو صاف) جنھن کان نڪي مٿي ۾ سور پوندن ۽ نڪي بيھوش ھوندا.'),
(4999, 56, 20, '۽ (پڻ ھر جنس جو) ميوو جتان وڻندن.'),
(5000, 56, 21, '۽ پکين جو گوشت جنھن قسم مان (پيو) گھرين.'),
(5001, 56, 22, '۽ وڏين اکين وارون حورون.'),
(5002, 56, 23, '(ڄڻڪ) ڍڪيل موتين جھڙيون.'),
(5003, 56, 24, 'جيڪي ڪمايو ھوائون تنھنجي بدلي ۾.'),
(5004, 56, 25, 'اتي نڪا اجائي بَڪ نڪا گناھ جھڙي ڳالھ ٻڌندا.'),
(5005, 56, 26, 'پر ھر ڪو سلام سلام پيو چوندو.'),
(5006, 56, 27, '۽ سڄي پاسي وارا، سڄي پاسي وارا ڪھڙي حال ھوندا.'),
(5007, 56, 28, 'لاسڙين ٻيرين ۾.'),
(5008, 56, 29, '۽ ڀريل ڦردار ڪيوڙن ۾.'),
(5009, 56, 30, '۽ ڊگھن پاڇن ۾.'),
(5010, 56, 31, '۽ (ھيٺ) ڪرندڙ پاڻي ۾.'),
(5011, 56, 32, '۽ گھڻن ميون ۾.'),
(5012, 56, 33, 'نڪي کُٽندا ۽ نڪي (کائن) جھلبا.'),
(5013, 56, 34, '۽ اُچا وڇاڻا.'),
(5014, 56, 35, 'بيشڪ اسان حورن کي ھڪ نموني تي پيدا ڪيو آھي.'),
(5015, 56, 36, 'پوءِ کين ڪنواريون بڻايوسون.'),
(5016, 56, 37, 'مڙسن کي وڻندڙ ھڪ جيڏيون.'),
(5017, 56, 38, 'سڄي پاسي وارن لاءِ.'),
(5018, 56, 39, 'گھڻي جماعت پھرين مان آھي.'),
(5019, 56, 40, '۽ گھڻي جماعت پوين مان آھي.'),
(5020, 56, 41, '۽ کٻي پاسي وارا، کٻي پاسي وارا ڪھڙا بدحال ھوندا.'),
(5021, 56, 42, 'ڪوسي جھولي ۽ تَتي پاڻيءَ ۾ ھوندا.'),
(5022, 56, 43, '۽ ڪاري دونھين جي پاڇي ۾ ھوندا.'),
(5023, 56, 44, 'جو نڪي ٿڌو ھوندو ۽ نڪي سھائيندڙ.'),
(5024, 56, 45, 'بيشڪ اُھي ھن کان اڳ (دُنيا ۾) سُک ۾ پيل ھوا.'),
(5025, 56, 46, '۽ وڏي گناھ (شرڪ) تي ضد ڪندا ھوا.'),
(5026, 56, 47, '۽ چوندا ھوا ته، جڏھن مرنداسون ۽ مٽي ۽ ھڏا ٿينداسون (تڏھن) اسين وري (قبرن مان) اُٿاريا وينداسون ڇا؟'),
(5027, 56, 48, 'يا اسان جا پھريان پيءُ ڏاڏا.'),
(5028, 56, 49, '(اي پيغمبر) چؤ ته بيشڪ اڳيان ۽ پويان.'),
(5029, 56, 50, 'ھڪ مقرّر ڪيل ڏينھن جي وقت تي ضرور گڏ ڪيا ويندا.'),
(5030, 56, 51, 'وري بيشڪ اوھين اي گمراھو ڪوڙ ڀائيندڙؤ.'),
(5031, 56, 52, 'ضرور اوھين ٿوھر جي وڻ مان کائيندؤ.'),
(5032, 56, 53, 'پوءِ منجھانئس پيٽن کي ڀريندؤ.'),
(5033, 56, 54, 'پوءِ مٿانئس تَتل پاڻيءَ مان پيئندؤ.'),
(5034, 56, 55, 'پوءِ اُڃايل اُٺن جي پيئڻ وانگر پيئندؤ.'),
(5035, 56, 56, 'قيامت جي ڏينھن اِھا سندن مھماني آھي.'),
(5036, 56, 57, 'اسان اوھان کي پيدا ڪيو پوءِ (وري جيارڻ کي) ڇونه سچ ڀائيندا آھيو.'),
(5037, 56, 58, 'ڀلا ڏسو ته سھي ته جيڪا مني وجھندا آھيو.'),
(5038, 56, 59, 'تنھن مان اوھين خلقيو ٿا ڇا؟ يا اسين خلقڻ وارا آھيون؟'),
(5039, 56, 60, 'اسان اوھان ۾ موت مقرّر ڪيو آھي ۽ اسين عاجز نه آھيون.'),
(5040, 56, 61, 'ته اوھان جھڙن کي مٽائي پيدا ڪريون ۽ اوھان کي اھڙي (شڪل) ۾ پيدا ڪريون جنھن کي ڄاڻندا ئي ئي نه ھجو.'),
(5041, 56, 62, '۽ بيشڪ پھرين پيدائش ڄاڻي چڪؤ پوءِ ڇونه ٿا نصيحت وٺو؟'),
(5042, 56, 63, 'ڏسو ته سھي جيڪي اوھين پوکيندا آھيو.'),
(5043, 56, 64, 'سو اوھين ڄمائيندا آھيو ڇا يا اسين ڄمائيندا آھيون؟'),
(5044, 56, 65, 'جيڪڏھن گھُرون ته اُن کي ناس ڪري ڇڏيون پوءِ اوھين حيرت ۾ رھجي وڃو.'),
(5045, 56, 66, '(۽ پيا چئو) ته بيشڪ اسين ٽوٽي ۾ پئجي وياسون.'),
(5046, 56, 67, 'بلڪ اسين بي نصيب آھيون.'),
(5047, 56, 68, 'ڀلا ڏسو ته سھي جيڪو پاڻي پيئندا آھيو.'),
(5048, 56, 69, 'اُھو اوھين ڪڪرن مان ھيٺ لاھيندا آھيو ڇا يا اسين ھيٺ لاھيندا آھيون؟'),
(5049, 56, 70, 'جيڪڏھن گھُرون ته اُن کي کارو ڪري ڇڏيون پوءِ ڇونه شڪرانو ڪندا آھيو؟'),
(5050, 56, 71, 'ڀلا ڏسو ته سھي ته جا باھ ٻاريندا آھيو.'),
(5051, 56, 72, 'تنھن جي وڻ کي اوھان پيدا ڪيو آھيو آھي ڇا؟ يا اسين پيدا ڪندڙ آھيون.'),
(5052, 56, 73, 'اسان اُن (وڻ) کي نصيحت وٺڻ لاءِ ۽ مسافرن جي فائدي لاءِ پيدا ڪيو آھي.'),
(5053, 56, 74, 'تنھنڪري پنھنجي وڏي پالڻھار جي نالي کي پاڪائي سان ياد ڪر.'),
(5054, 56, 75, 'پوءِ تارن جي ڪِرڻ جي جاءِ جو قسم کڻان ٿو.'),
(5055, 56, 76, '۽ جيڪڏھن ڄاڻو ته بيشڪ اُھو قسم وڏو آھي.'),
(5056, 56, 77, 'بيشڪ اھو سڳورو قرآن آھي.'),
(5057, 56, 78, 'لڪل ڪتاب ۾ لکيل آھي.'),
(5058, 56, 79, 'پاڪائيءَ وارن کانسواءِ ٻيو ڪو اُن کي ھٿ نه لائيندو آھي.'),
(5059, 56, 80, 'جھانن جي پالڻھار وٽان نازل ڪيل آھي.'),
(5060, 56, 81, 'پوءِ اوھين ھن ڳالھ (جي مڃڻ) ۾ سُستي ڪرڻ وارا آھيو ڇا.'),
(5061, 56, 82, '۽ اُن کي ڪوڙ ڀانئين پنھنجو حصّو ڪريو ٿا.'),
(5062, 56, 83, 'پوءِ جنھن مھل (ڪنھنجو) ساھ نر گھٽ وٽ پھچي.'),
(5063, 56, 84, '۽ اوھين انھيءَ مھل پيا ٿا ڏسو.'),
(5064, 56, 85, '۽ اسين اوھان جي به نسبت اُن ڏانھن تمام ويجھا آھيون پر اوھين نه ڏسندا آھيو.'),
(5065, 56, 86, 'پوءِ جيڪڏھن (اوھين خدا جي حُڪم ۾) لاچار ٿيل نه آھيو.'),
(5066, 56, 87, 'ته جيڪڏھن سچا آھيو ته روح کي ڇو نه موٽايو.'),
(5067, 56, 88, 'پوءِ جيڪڏھن (مئل الله جي) ويجھن ٻانھن مان ھوندو.'),
(5068, 56, 89, 'ته اُن لاءِ خوشي ۽ سُرھاڻ ۽ نعمت وارو باغ آھي.'),
(5069, 56, 90, '۽ جيڪڏھن سڄي پاسي وارن مان ھوندو.'),
(5070, 56, 91, 'ته سڄي پاسي وارن مان توکي سلامتي آھي.'),
(5071, 56, 92, 'پر جيڪڏھن اُھو ڪُوڙ ڀانئيندڙن گمراھن مان ھوندو.'),
(5072, 56, 93, 'ته (اُن لاءِ) تَتل پاڻي مھماني.'),
(5073, 56, 94, '۽ دوزخ ۾ گھيڙڻ آھي.'),
(5074, 56, 95, 'بيشڪ ھي خبر پوري يقيني آھي.'),
(5075, 56, 96, 'تنھنڪري پنھنجي پالڻھار وڏي جي نالي کي پاڪائيءَ سان ياد ڪر.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'جيڪي آسمانن ۽ زمين ۾ آھي سو سڀ الله جي پاڪائي بيان ڪندو آھي، ۽ اُھو غالب حڪمت وارو آھي.'),
(5077, 57, 2, 'آسمانن ۽ زمين جي بادشاھي اُن جي آھي، (اُھو) جياريندو آھي ۽ ماريندو آھي، ۽ اُھو سڀڪنھن شيءِ تي وس وارو آھي.'),
(5078, 57, 3, 'اُھو (سڀ کان) پھريون ۽ (سڀ کان) پويون ۽ پڌرو ۽ ڳجھو آھي، ۽ اُھو سڀڪنھن شيءِ کي ڄاڻندڙ آھي.'),
(5079, 57, 4, '(الله) اُھو آھي جنھن آسمانن ۽ زمين کي ڇھن ڏينھن ۾ بڻايو وري عرش تي متوجه ٿيو، جيڪي زمين ۾ گھڙندو آھي ۽ جيڪي نڪرندو آھي ۽ جيڪي آسمان مان لھندو آھي ۽ جيڪي منجھس چڙھي ويندو آھي سو (سڀ) اُھو ڄاڻندو آھي، ۽ جتي اوھين ھجو اُتي اھو اوھان سان گڏ آھي، ۽ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.'),
(5080, 57, 5, 'آسمانن ۽ زمين جي بادشاھي اُن جي آھي، ۽ الله ڏانھن سڀ ڪم ورايا وڃن ٿا.'),
(5081, 57, 6, 'رات کي ڏينھن ۾ گھيڙيندو آھي ۽ ڏينھن کي رات ۾ گھيڙيندو آھي، ۽ اُھوسِينن وارو (ڳجھ) ڄاڻندڙ آھي.'),
(5082, 57, 7, 'الله ۽ سندس پيغمبر تي ايمان آڻيو ۽ اُنھيءَ مال مان خرچيو جنھن ۾ اوھان کي ٻـين جو وارث ڪيو اٿس، ۽ اوھان مان جن ايمان آندو ۽ (الله جي واٽ ۾) خرچ ڪيو تن لاءِ وڏو اجر آھي.'),
(5083, 57, 8, '۽ اوھان کي ڇا (ٿيو) آھي جو الله تي ايمان نه ٿا آڻيو؟ حالانڪ پيغمبر اوھان کي سڏي ٿو ته اوھين پنھنجي پالڻھار تي ايمان آڻيو ۽ بيشڪ الله اوھان کان انجام ورتو آھي جيڪڏھن مڃڻ وارا آھيو.'),
(5084, 57, 9, '(الله) اُھو آھي جيڪو پنھنجي ٻانھي (محمّد) تي پڌريون آيتون نازل ڪندو آھي ته (ڪفر جي) اونداھين مان (اسلام جي) سوجھري ڏانھن اوھان کي ڪڍي، ۽ بيشڪ الله اوھان تي شفقت ڪندڙ مھربان آھي.'),
(5085, 57, 10, '۽ اوھان کي ڇا (ٿيو) آھي جو الله جي واٽ ۾ نه خرچيندا آھيو حالانڪ آسمانن ۽ زمين جي ميراث خاص الله جي آھي، جنھن (مڪي جي) فتح کان اڳ خرچ ڪيو ۽ (الله جي دين لاءِ ڪافرن سان) وڙھيو تنھنجي برابر اوھان مان ڪوبه ڪونھي، اھي انھن کان تمام وڏي مرتبي وارا آھن جن (فتح کان) پوءِ خرچ ڪيو ۽ (دين لاءِ) وڙھيا، ۽ ھر ھڪ کي الله چڱو انجام ڏنو آھي، ۽ جيڪي ڪندا آھيو تنھن جي الله (پوري) خبر رکندڙ آھي.'),
(5086, 57, 11, 'اُھو ڪير آھي جو الله کي چڱو قرض ڏئي پوءِ (الله) اُھو (قرض) اُن کي ٻيڻو ڪري ڏيندو ۽ اُن لاءِ سڳورو اجر آھي.'),
(5087, 57, 12, '(ياد ڪر) جنھن ڏينھن مؤمنن ۽ مؤمنياڻين کي (ھن حالت ۾) ڏسندين جو سندن نُور اُنھن جي اڳيان ۽ انھن جي ساڄن پاسن کان پيو ڊوڙندو (چئبن ته) اوھان کي خوشخبري ھجي جو اڄ اوھان لاءِ اھڙا باغ آھن جن جي ھيٺان واھيون پيون وَھن اُتي سدائين رھندؤ، اھا وڏي مراد ماڻڻ آھي.'),
(5088, 57, 13, 'جنھن ڏينھن منافق ۽ منافقياڻيون (گڏجي) مؤمنن کي چوندا ته ترسو (ته سھي) ته اوھان جي (ايماني) نُور مان (ڪجھ) روشني وٺون چيو ويندو ته اوھين پنھنجي پٺ تي (دنيا ڏانھن) موٽو پوءِ ڪو سوجھرو ڳوليو، پوءِ سندن وچ ۾ ھڪ اھڙي ڀت ھنئي ويندي جنھن کي دروازو ھوندو، اُن ڀت جي اندارن رحمت ھوندي ۽ ان جي ٻاھرين پاسي کان عذاب.'),
(5089, 57, 14, 'منافق مُسلمانن کي سڏيندا ۽ چوندا ته اسين اوھان سان (دنيا ۾) گڏ نه ھواسون ڇا؟ (مُسلمان) چوندا ته ھائو پر پنھنجو پاڻ کي جوکي ۾ وڌوَ ۽ (ايمان آڻڻ ۾) دير ڪندا رھيؤ ۽ (نبوت ۾) شڪ آندو ۽ اوھان کي سَڌن ٺڳي ڇڏيو تان جو الله جو حُڪم آيو ۽ اوھان کي الله (جي حُڪم مڃڻ) کان (شيطان) ٺڳيندڙ ٺڳي ڇڏيو.'),
(5090, 57, 15, 'تنھن ڪري (اي منافقؤ) اڄ نڪي اوھان کان ۽ نڪي ڪافرن کان ڪو بدلو وٺبو، اوھان جي جاءِ (دوزخ جي) باھ آھي، اھا اوھان جي لائق آھي، ۽ اھا موٽڻ جي جاءِ بڇڙي آھي.'),
(5091, 57, 16, 'اڃا مؤمنن لاءِ اُھو وقت نه آيو آھي ڇا؟ جو الله جي ياد ڪرڻ وقت ۽ وحي الٰھيءَ مان جيڪو نازل ٿيو تنھن (جي ياد) آڻڻ وقت سندن دليون آزي ڪن ۽ انھن وانگر نه ٿين جن کي ھن کان اڳ ڪتاب ڏنو ويو پوءِ مٿن ڊگھي مدت گذري تنھنڪري سندن دليون سخت ٿي ويون، ۽ منجھائن گھڻا بي دين آھن.'),
(5092, 57, 17, 'ڄاڻو ته الله زمين کي سندس ويرانيءَ کانپوءِ آباد ڪندو آھي، بيشڪ اسان اوھان لاءِ چٽيون نشانيون بيان ڪيون آھن ته مانَ اوھين پرجھو.'),
(5093, 57, 18, 'بيشڪ خيرات ڏيندڙن ۽ خيرات ڏيندڙن کي، ۽ جن الله کي سھڻو قرض ڏنو آھي تن کي ٻيڻو ڪري ڏبو ۽ اُنھن لاءِ سڳورو اجر آھي.'),
(5094, 57, 19, '۽ جن الله ۽ سندس پيغمبرن تي ايمان آندو آھي اُھي ئي پنھنجي پالڻھار وٽ بلڪل سچا ۽ شھيد آھن، اُنھن لاءِ سندن اجر ۽ سندن نُور آھي، ۽ جن ڪفر ڪيو ۽ اسان جي آيتن کي ڪُوڙ ڀانيو اُھي دوزخي آھن.'),
(5095, 57, 20, 'ڄاڻو ته دُنيا جي حياتي رڳو راند ۽ بي فائدِي ۽ سينگار ۽ پاڻ ۾ فخر ڪرڻ ۽ مالن ۽ اولادن ۾ (ھڪ ٻئي کان) گھڻائي طلبڻ آھي، (انھيءَ جو مثال) انھيءَ مينھن وانگر (آھي) جنھن جو سلو (ڄمائڻ) ڪڙمين کي عجب ۾ وجھي وري سُڪي وڃي پوءِ اُن کي پيلو ٿيل ڏسين وري ڀُري وڃي، ۽ آخرت ۾ (الله جي دشمنن لاءِ) سخت عذاب آھي ۽ (الله جي دوستن لاءِ) الله کان بخشش ۽ رضامندي آھي، ۽ دنيا جي حياتي رڳو ٺڳيءَ جو سامان آھي.'),
(5096, 57, 21, 'پنھنجي پالڻھار جي بخشش ۽ انھي بھشت ڏانھن اڳرائي ڪريو جنھن جي ويڪرائي آسمان ۽ زمين جي ويڪرائي وانگر آھي انھن لاءِ تيار ڪيو ويو آھي جن الله ۽ سندس پيغمبرن تي ايمان آندو، اھو الله جو فضل آھي جنھن کي وڻيس تنھن کي ڏيندو آھي، ۽ الله وڏي فضل وارو آھي.'),
(5097, 57, 22, 'ڪابه مُصيبت نڪي زمين ۾ ۽ نڪي اوھان جي جندن ۾ پھچندي آھي پر اُنھي کان اڳ اُن کي پيدا ڪريون ھڪ ڪتاب ۾ لکيل آھي، بيشڪ اھو (ڪم) الله کي آسان آھي.'),
(5098, 57, 23, 'ھن ڪري (اھا خبر ڏني اٿون) ته جيڪي اوھان جي ھٿن مان ويو تنھن تي ارمان نه ڪريو ۽ جيڪي اوھان کي عطا ڪيائين تنھن تي خوشي نه ڪريو، ۽ الله سڀڪنھن ھٺيلي پاڻ سڏائيندڙ کي دوست نه رکندو آھي.'),
(5099, 57, 24, 'جيڪي پيچائي ڪندا آھن ۽ ماڻھن کي به پيچائي ڏسيندا آھن، ۽ جيڪو منھن موڙيندو ته بيشڪ الله ئي بي پرواھ ساراھيل آھي.'),
(5100, 57, 25, 'بيشڪ پنھنجن پيغمبرن کي پڌرين نشانين سان موڪليوسون ۽ ساڻن ڪتاب ۽ تارازي ھن لاءِ موڪليوسون ته ماڻھو انصاف سان بيھڪ وٺن، ۽ لوھ موڪليوسون جنھن جي ڪري سخت ويڙھ آھي ۽ ماڻھن لاءِ (ٻيا) فائدا (به) آھن ۽ ته جيڪو الله ۽ سندس پيغمبرن کي پرپُٺ مدد ڏئي تنھن کي الله معلوم ڪري، بيشڪ الله سگھارو غالب آھي.'),
(5101, 57, 26, '۽ بيشڪ نُوح ۽ ابراھيم کي موڪليوسون ۽ سندن اولاد ۾ پيغمبري ۽ ڪتاب (امانت) رکيوسون پوءِ منجھائن ڪي ھدايت وارا آھن، ۽ منجھائن گھڻا بي دين آھن.'),
(5102, 57, 27, 'وري پٺيانئن پنھنجا (ٻيا) پيغمبر موڪلياسون ۽ پوئتان عيسىٰ پُٽ مريم جي کي موڪليوسون ۽ کيس انجيل ڏنوسون، ۽ جن سندس تابعداري ڪئي تن جي دلين ۾ نرمي ۽ ٻاجھ رکي سون، ۽ اُھا گوشه نشيني جا پاڻ ٺاھي ھوائون سا اسان مٿن فرض نه ڪئي ھُئي پر الله جي رضامندي جي طلب لاءِ پاڻ ٺاھي ھوائون پوءِ اُن جو پورو حق نظر نه رکيائون، پوءِ منجھائن جن ايمان آندو تن کي سندن اجر ڏنوسون، ۽ منجھائن گھڻا بي دين آھن.'),
(5103, 57, 28, 'اي ايمان وارؤ الله کان ڊڄو ۽ سندس پيغمبر تي ايمان آڻيو ته اوھان کي پنھنجي ٻاجھ مان به ڀاڱا ڏئي ۽ اوھان کي اھڙو نُور ڏئي جنھن سان (رستو) ھلو ۽ اوھان کي بخشي، ۽ الله بخشڻھار مھربان آھي.'),
(5104, 57, 29, 'ھن ڪري (الله ھي خبر ڏني) ته اھل ڪتاب جا ڄاڻن ته اُھي الله جي فضل مان ڪنھن شيءَ تي سگھ رکي نه سگھندا آھن ۽ ته فضل الله جي ھٿ ۾ آھي جنھن کي وڻيس تنھن کي ڏيندو آھي، ۽ الله وڏي فضل وارو آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'بيشڪ الله انھيءَ (زال) جو ڳالھائڻ ٻڌو جنھن مڙس بابت توسان جھڳڙو ٿي ڪيو ۽ الله جي آڏو (پنھنجو) ڏک جو احوال (بيان) ٿي ڪيو، ۽ الله اوھان ٻنھي جو پاڻ ۾ ڳالھائڻ ٻڌو ٿي، بيشڪ الله ٻڌندڙ ڏسندڙ آھي.'),
(5106, 58, 2, 'اوھان مان جيڪي پنھنجين زالن کي ماءُ ڪوٺي وھندا آھن اُھي (زالون ڪي) اُنھن جون مائر نه آھن، سندن مائر (ته) اُھي آھن جن کين ڄڻيو آھي، بيشڪ اُھي (ائين چوندڙ) ھڪ اڻ جڳائيندڙ ڳالھ ۽ ڪوڙ چوندا آھن، ۽ بيشڪ الله معافي ڏيندڙ بخشڻھار آھي.'),
(5107, 58, 3, '۽ جيڪي پنھنجي زالن ئي ماءُ ڪوٺي وھندا آھن سي وري جيڪي چيائون تنھن کان (پوئتي) موٽندا آھن ته ٻنھي (زال مڙس) جي پاڻ ۾ ھٿ لائڻ کان اڳ (ھڪ) ٻانھون آجو ڪرڻ واجب آھي، انھي (حُڪم) سان اوھان کي نصيحت ڏجي ٿي، ۽ جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.'),
(5108, 58, 4, 'پوءِ جيڪو (ٻانھي کي) نه لھي (تنھن تي) ٻنھي جي پاڻ ۾ ھٿ لائڻ کان اڳ ٻه مھينا لڳولڳ روزا رکڻ (واجب) آھن، پوءِ جيڪي (روزا رکي) نه سگھي تنھن تي سٺ مسڪينن جو کارائڻ (واجب) آھي، اِھو (حُڪم) ھن لاءِ آھي ته (اوھين) الله ۽ سندس پيغمبر (جي حڪم) کي مڃيو، ۽ اھي الله جون (مقرر ڪيل) حدون آھن، ۽ مُنڪرن لاءِ ڏکوئيندڙ عذاب آھي.'),
(5109, 58, 5, 'بيشڪ جيڪي الله ۽ سندس پيغمبر جي مُخالفت ڪندا آھن تن کي (ائين) خوار ڪيو ويو جيئن اُنھن کي خوار ڪيو ويو جيڪي کائن اڳ ھوا ۽ بيشڪ پڌريون آيتون نازل ڪيون سون، ۽ مُنڪرن لاءِ خوار ڪندڙ عذاب آھي.'),
(5110, 58, 6, 'جنھن ڏينھن انھن مڙني کي الله اُٿاريندو (تنھن ڏينھن) کين (انھيءَ جي) سُڌ ڏيندو جيڪي ڪمايو ھوائون، الله اُن کي ڳڻي رکيو آھي ۽ (ھنن) اُھو وساري ڇڏيو، ۽ الله ھر شيءِ تي خبر رکڻ وارو آھي.'),
(5111, 58, 7, 'نه ڏٺو اٿئي ڇا ته جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو الله ڄاڻندو آھي، (ڪٿي به پاڻ ۾) ٽن ڄڻن جي ڳُجھي صلاح ڪرڻ نه ھوندي آھي پر اُنھن مان چوٿون الله آھي ۽ نڪي پنجن (ڄڻن) جي پر انھن ۾ ڇھون الله آھي ۽ نڪي ان (ڳاڻاٽي) کان تمام گھٽ ۽ تمام وڌ آھن پر الله ساڻن آھي جتي به ھجن، وري جيڪي ڪيو اٿن (تنھنجي) قيامت جي ڏينھن کين سُڌ ڏيندو، بيشڪ الله سڀڪنھن شيءِ کي ڄاڻندڙ آھي.'),
(5112, 58, 8, '(اي پيغمبر) انھن ڏانھن نه ڏٺو اٿئي ڇا جن کي ڳُجھين صلاحن ڪرڻ کان جھليو ويو وري جنھن کان جھليا ويا سوئي ڪندا آھن ۽ گناھ ۽ حد کان لنگھڻ ۽ پيغمبر جي نافرماني ڪرڻ بابت پاڻ ۾ ڳجھيون صلاحون ڪندا آھن، ۽ جڏھن تو وٽ ايندا آھن (تڏھن) توکي انھي (لفظ) سان سلام ڪندا آھن جنھن سان توکي الله سلام نه ڪيو آھي ۽ پنھنجين دلين ۾ چوندا آھن ته جيڪي چوندا آھيون تنھن جي ڪري الله اسان کي ڇونه ٿو عذاب ڪري؟ کين دوزخ بس آھي، ان ۾ پوندا، پوءِ (دوزخ) بڇڙي جاءِ آھي.'),
(5113, 58, 9, 'اي ايمان وارؤ جڏھن (اوھين) پاڻ ۾ (ڪا) ڳُجھي صلاح ڪريو ته گناھ ۽ حد کان لنگھڻ ۽ پيغمبر جي نافرمانيءَ بابت (ڪابه) پاڻ ۾ صلاح نه ڪريو ۽ پاڻ ۾ چڱائي ۽ پرھيزگاريءَ بابت صلاح ڪريو، ۽ انھيءَ الله کان ڊڄو جنھن ڏانھن گڏ ڪيا ويندؤ.'),
(5114, 58, 10, 'بڇڙي صلاح ڪرڻ ته شيطان جو ڪم آھي ھن لاءِ ته مؤمنن کي دلگير ڪري ۽ ھو الله جي حُڪم کانسواءِ کين ڪجھ به نُقصان پُھچائڻ وارو نه آھي، ۽ جڳائي ته مؤمن الله تي ڀروسو ڪن.'),
(5115, 58, 11, 'اي ايمان وارؤ جڏھن اوھان کي چئجي ته (پنھنجين) مجلسن ۾ جاءِ ڪشادي ڪريو پوءِ (جاءِ) ڪشادي ڪريو ته الله به اوھان جي لاءِ ڪشادگي ڪري، ۽ جڏھن چئجي ته اُٿو تڏھن اُٿو ته اوھان مان جن ايمان آندو آھي ۽ جن کي علم ڏنو ويو تن جا مرتبا الله وڏا ڪري، ۽ جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.'),
(5116, 58, 12, 'اي ايمان وارؤ جڏھن اوھين (گھرو ته) پيغمبر سان ڪا ڳجھي ڳالھ ڪريو تڏھن پنھنجي ڳجھي ڳالھ ڪرڻ کان اڳ پھريائين خيرات ڏيو، اھو (ڪم) اوھان لاءِ ڀَلو ۽ تمام سٺو آھي، پوءِ جيڪڏھن ڪُجھ نه لھو ته بيشڪ الله بخشڻھار مھربا آھي.'),
(5117, 58, 13, 'اوھين (انھي ڳالھ کان) ڊڄي ويؤ ڇا ته پنھنجي ڳالھ چوڻ کان اڳي خيرات ڏيو، پوءِ جڏھن اوھان نه ڪيو ۽ الله اوھان کان ٽارو ڪيو تڏھن نماز پڙھندا رھو ۽ زڪوٰة ڏيندا رھو ۽ الله ۽ سندس پيغمبر جو چيو مڃيو، ۽ جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.'),
(5118, 58, 14, '(اي پيغمبر) انھن ڏانھن نه ڏٺو اٿئي ڇا جن ھڪ اھڙي قوم سان دوستي رکي جن تي الله ڏمريو آھي، نڪي اُھي (يعني منافق) اوھان مان آھن ۽ نڪي اُنھن (يھودين) مان آھن، ۽ اُھي ڄاڻي ٻجھي ڪوڙ تي قسم کڻندا آھن.'),
(5119, 58, 15, 'اُنھن لاءِ الله سخت عذاب تيار ڪيو آھي، بيشڪ اُھي جيڪي ڪندا آھن سو بڇڙو آھي.'),
(5120, 58, 16, 'پنھنجن قَسمن کي ڍال ڪري ورتو اٿن ۽ الله جي واٽ کان (ماڻھن کي) جھليائون تنھنڪري انھن لاءِ خواري ڏيندڙ عذاب آھي.'),
(5121, 58, 17, 'الله (جي عذاب) کان نڪي سندن مال ۽ نڪي سندن اولاد کانئن ڪجھ به ٽاريندو، اُھي دوزخي آھن، اِھي اُن ۾ سدائين رھندا.'),
(5122, 58, 18, 'جنھن ڏينھن انھن مڙني کي الله اٿاريندو تنھن ڏينھن سندس آڏو (ائين) قسم کڻندا جئن اوھان جي آڏو قَسم کڻندا آھن ۽ ڀائيندا آھن ته اُھي ڪنھن چڱي ڳالھ تي آھن، خبردار ٿيو بيشڪ اُھي اصل ڪوڙا آھن.'),
(5123, 58, 19, 'مٿن شيطان غالب ٿي ويو آھي پوءِ اُنھن (جي دل) کان الله جي يادگيري وساريائين، اِھي (ماڻھو) شيطان جي جماعت آھن، خبردار (ٿيو ته) بيشڪ شيطان جي جماعت ئي ٽوٽي واري آھي.'),
(5124, 58, 20, 'بيشڪ جيڪي الله ۽ سندس پيغمبر (جي حُڪم) کان مخالفت ڪندا آھن سي تمام خواريءَ وارن (ماڻھن) ۾ آھن.'),
(5125, 58, 21, 'الله لکي ڇڏيو آھي ته آءٌ ضرور غالب رھندس ۽ منھنجا پيغمبر به، بيشڪ الله سگھارو غالب آھي.'),
(5126, 58, 22, '(اي پيغمبر) اھڙي قوم جيڪا الله ۽ قيامت جي ڏينھن کي مڃيندي ھجي تنھن کي تون (ھن وصف سان) نه ڏسندين ته جنھن الله ۽ سندس پيغمبر جي مخالفت ڪئي آھي تنھن سان دوستي رکندا ھجن توڙيڪ اُھي سندن پيئر يا سندن پُٽ يا سندن ڀائر يا سندن مائٽ ھجن، اِھي (مؤمن اُھي) آھن جن جي دلين ۾ الله ايمان لکيو آھي ۽ جن کي پنھنجي فيض سان سگھ ڏني اٿس، ۽ انھن کي اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن ھو اُنھيءَ ۾ سدائين رھندا، الله کانئن راضي ٿيو ۽ اُھي کانئس راضي ٿيا، اُھي الله جي جماعت آھن، خبردار (ٿيو ته) بيشڪ الله جي جماعت ئي ڇُٽڻ واري آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو سڀ الله جي پاڪائي بيان ڪندو آھي، ۽ اُھو غالب حڪمت وارو آھي.'),
(5128, 59, 2, '(الله) اُھو آھي جنھن ڪتاب وارن مان ڪافرن کي سندن گھرن مان پھرين ئي ويڙھ ۾ ٻاھر ڪڍي ڇڏيو، (اي مسلمانؤ) انھن جي نڪرڻ جو اوھان ڪو گمان نه ڪندا ھئو ۽ اُھي ڀائيندا ھوا ته الله (جي عذاب) کان سندن قلعا کين بچائڻ وارا ھوندا پوءِ (اُتان) وٽن الله (جو عذاب) پھتو جتان ڄاڻندا ئي نه ھوا ۽ سندن دلين ۾ اھڙي دھشت وڌائين جو پنھنجن ھٿن سان ۽ مُسلمانن جي ھٿن سان پنھنجن گھرن کي (پاڻ) ڊاھڻ لڳا، تنھنڪري اي اکين وارؤ عبرت وٺو.'),
(5129, 59, 3, '۽ جيڪڏھن الله مٿن ديس نيڪالي نه لکي ھا ته کين (ٻيءَ طرح) دنيا ۾ عذاب ڪري ھا، ۽ انھن کي آخرت ۾ (به) باھ جو عذاب آھي.'),
(5130, 59, 4, 'اِھو عذاب (ڪرڻ) ھن ڪري آھي جو انھن الله ۽ سندس پيغمبر جي مخالفت ڪئي، ۽ جيڪو الله جي مخالفت ڪندو ته بيشڪ الله سخت عذاب (ڪرڻ) وارو آھي.'),
(5131, 59, 5, '(اي مؤمنؤ) جيڪي کجين جا وڻ وڍيوَ يا انھن کي پنھنجي پاڙن تي بيٺل ڇڏيوَ سو الله جي حُڪم سان ۽ ھِن لاءِ ته ھو ڪافرن کي خوار ڪري.'),
(5132, 59, 6, '۽ انھن (يعني بني نضير جي مالن) مان جيڪو (مال) الله پنھنجي پيغمبر کي ھٿ ۾ ڏنو تنھن تي اوھان نڪي گھوڙا ۽ نڪي ڪي اُٺ ڊوڙايا ھوا پر الله پنھنجن پيغمبرن کي جنھن تي وڻيس (تنھن تي) غالب ڪندو آھي، ۽ الله سڀڪنھن شيءَ تي وس وارو آھي.'),
(5133, 59, 7, 'ڳوٺاڻن (جي مالن) مان جيڪي الله پنھنجي پيغمبر جي ھٿ ۾ ڏنو سو خاص الله لاءِ پيغمبر لاءِ ۽ (پيغمبر جي) مائٽن لاءِ ۽ ڇورن ٻارن ۽ مسڪينن ۽ مسافرن لاءِ آھي (اِھو ھن لاءِ آھي) ته اُھو اوھان مان دنيا وارن جي وچ ۾ ڏيڻ وٺڻ ۾ نه اچي، ۽ جيڪي پيغمبر اوھان کي ڏئي سو وٺو، ۽ جنھن کان اوھان کي جھلي تنھن کي ڇڏي ڏيو، ۽ الله کان ڊڄو، بيشڪ الله سخت عذاب (ڪرڻ) وارو آھي.'),
(5134, 59, 8, '(۽ اُھو فَيءَ جو مال) انھن ھجرت ڪرڻ وارن مسڪينن لاءِ (به) آھي جن کي سندن گھرن مان ۽ مالن مان ٻاھر ڪڍيو ويو جيڪي (پنھنجي) الله کان فضل ۽ سندس رضامندي طلبيندا آھن ۽ الله کي ۽ سندس پيغمبر کي مدد ڏيندا آھن، اُھي ئي سچا آھن.'),
(5135, 59, 9, '۽ (انھن لاءِ به آھي) جن دارالاسلام (يعني مديني) ۾ ۽ ايمان ۾ مُھاجرن کان اڳ گھر بڻايو جيڪو وٽن لڏي اچي تنھن کي دوست رکندا آھن ۽ جيڪي مھاجرن کي ڏنو ويو تنھن بابت پنھنجين دلين ۾ ڪا پريشاني نه لھندا آھن ۽ (ٻـين کي) پاڻ کان پسند ڪندا آھن توڻيڪ کين ڪو احتياج ھوندو آھي، ۽ جن کي پنھنجي نفس جي حرص کان بچايو ويو سي ئي ڇٽڻ وارا آھن.'),
(5136, 59, 10, '۽ (اھو مال اُنھن لاءِ به آھي) جيڪي انھن (مھاجرن ۽ انصارن) کانپوءِ آيا چوندا آھن ته اي اسان جا پالڻھار اسان کي بخش ۽ اسان جي (انھن) ڀائرن کي (به) جن ايمان آڻڻ ۾ اسان کان اڳرائي ڪئي ۽ مؤمنن به نسبت اسان جي دلين ۾ ڪو ڪينو نه وجھ اي اسان جا پالڻھار بيشڪ تون ڏاڍو شفقت ڪندڙ مھربان آھين.'),
(5137, 59, 11, 'اُنھن ڏانھن نه ڏٺو اٿئي ڇا جيڪي منافق ٿيا جي پنھنجن انھن ڀائرن کي چوندا آھن جيڪي ڪتاب وارن مان مُنڪر آھن ته جيڪڏھن اوھان کي (وطن مان) تڙايو ويندو ته (به اسين) اوھان سان گڏ نڪرنداسون ۽ اوھان جي حق ۾ ڪڏھن به ڪنھن جو چيو نه مڃينداسون ۽ جيڪڏھن اوھان سان ويڙھ ڪئي ويندي ته اوھان جي ضرور مدد ڪنداسون، ۽ الله شاھدي ٿو ڏيئي ته بيشڪ اُھي ڪوڙا آھن.'),
(5138, 59, 12, 'جيڪڏھن تڙبُن ته (اُھي منافق) ساڻن (وطن مان) نه نڪرندا، ۽ جيڪڏھن اُنھن سان جنگ ڪبي ته کين مدد نه ڏيندا، پر جيڪڏھن (کڻي) مدد ڏيندا ته ضرور پٺيون ڦيرائيندا، وري کين مدد نه ملندي.'),
(5139, 59, 13, '(اي مُسلمانؤ) بيشڪ اوھين انھن جي دلين ۾ الله کان (به) وڌيڪ دھشت وارا آھيو، اِھو ھِن ڪري آھي جو اُھي بي سمجھ قوم آھن.'),
(5140, 59, 14, '(سڀ) ھڪ ھنڌ تي اوھان سان ويڙھ نه ڪندا پر قلعي واري (محڪم) ڳوٺن ۾ يا ڀتين جي آڙ ۾، پاڻ ۾ انھن جي ويڙھ تمام سخت پيل آھي، کين گڏ ٿيندو ڀانئيندو آھي پر اُنھن جون دليون ڌار ڌار آھن، اھو ھن ڪري آھي جو اھي بي عقل قوم آھن.'),
(5141, 59, 15, '(سندن مثال) انھن جي مثال جھڙو آھي جيڪي کانئن اڳ ويجھو (گذريل) ھوا (جن) پنھنجي ڪئي جي سزا چکي، ۽ انھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(5142, 59, 16, '(انھن جو مثال) شيطان جي مثال وانگر آھي جڏھن ماڻھوءَ کي چوندو آھي ته ڪافر ٿيءُ، پوءِ جنھن مھل ڪافر ٿيو (تنھن مھل ھو) چوندو آھي ته بيشڪ آءٌ توکان بيزار آھيان آءٌ پنھنجي پالڻھار الله کان ڊڄان ٿو.'),
(5143, 59, 17, 'پوءِ ٻنھي جي پڇاڙي ھيئن ٿي ته ٻئي باھ ۾ ھميشه ھوندا، ۽ اھا ظالمن جي سزا آھي.'),
(5144, 59, 18, 'اي ايمان وارؤ الله کان ڊڄو ۽ جڳائي ته سڀڪو شخص سوچي ته سڀاڻي (قيامت) لاءِ ڇا اڳي موڪليو اٿس، ۽ الله کان ڊڄندا رھو، ڇوته جيڪي ڪندا آھيو تنھن بابت الله خبر رکندڙ آھي.'),
(5145, 59, 19, '۽ انھن جھڙا نه ٿيو جن الله کي وساريو پوءِ الله انھن کان سندن جيئن (جي سڌاري) کي وسارايو، اھي بي دين آھن.'),
(5146, 59, 20, 'دوزخي ۽ بھشتي برابر نه آھن، بھشتي ئي مُراد ماڻيندڙ آھن.'),
(5147, 59, 21, 'جيڪڏھن ھن قرآن کي ڪنھن جبل تي نازل ڪريون ھا ته ضرور ان کي الله جي خوف کان عاجز پرزا پرزا ٿيل ڏسين ھا، ۽ اھي مثال ماڻھن لاءِ بيان ڪريون ٿا ته مانَ اُھي ڌيان ڪن.'),
(5148, 59, 22, 'الله اُھو آھي جنھن کانسواءِ (ٻيو) ڪو عبادت جو لائق ڪونھي، (جو) ڳجھ ۽ ظاھر جو ڄاڻندڙ آھي، اُھو ٻاجھارو مھربان آھي.'),
(5149, 59, 23, 'الله اُھو آھي جنھن کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي، (جو) بادشاھ تمام پاڪ ذات (سڀني عيبن کان) سلامت امن ڏيڻ وارو نگھبان غالب زبردست وڏائي جو سائين آھين، انھن جي شريڪ مقرر ڪرڻ کان الله پاڪ آھي.'),
(5150, 59, 24, 'اُھو الله خلقڻھار نئون بڻائيندڙ شڪليون ٺاھيندڙ آھي اُن جا (سڀ) چڱا نالا آھن، جيڪي (به) آسمانن ۽ زمين ۾ آھي سي (سڀ) سندس پاڪائي بيان ڪندا آھن، ۽ اُھو غالب حڪمت وارو آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'اي ايمان وارؤ منھنجي دشمنن ۽ پنھنجي دشمنن کي دوست (ڪري) نه وٺو اُنھن ڏانھن دوستي جي ڪري پيغام موڪليو ٿا حالانڪ جيڪو اوھان وٽ سچو دين آيو آھي تنھن جا اُھي مُنڪر ٿي چڪا آھن پيغمبر کي ۽ اوھان کي (پنھنجي وطن مان) ھن ڪري ڪڍين ٿا پنھنجي پالڻھار الله تي ايمان آندو اَٿو جيڪڏھن (خاص) منھنجيءَ واٽ ۾ جھاد ڪرڻ لاءِ ۽ منھنجي رضامندي حاصل ڪرڻ لاءِ نڪتا آھيو انھن ڏانھن دوستي جي ڪري ڳُجھو پيغام ٿا موڪليو، حالانڪ جيڪي ڳجھو ڪريو ٿا ۽ جيڪي پڌرو ڪريو ٿا سو آءٌ چڱي طرح ڄاڻندو آھيان، ۽ اوھان مان جيڪو ائين ڪندو سو بيشڪ سِڌو رستو ڀُلو.'),
(5152, 60, 2, 'جيڪڏھن (ڪافر) اوھان کي لھندا ته اوھان جا پڪا ويري ھوندا ۽ اوھان ڏانھن پنھنجا ھٿ ۽ پنھنجيون زبانون ايذاءَ لاءِ ڊگھيون ڪندا ۽ گھُرندا ته جيڪر (ڪنھن طرح اوھين به) ڪافر ٿيو.'),
(5153, 60, 3, 'ڪڏھن نه اوھان کي اوھان جا مائٽ ۽ نڪي اوھان جو اولاد قيامت جي ڏينھن ڪو فائدو ڏيندو، ۽ اوھان جي وچ ۾ (الله) فيصلو ڪندو، ۽ جيڪي (اوھين) ڪندا آھيو سو الله ڏسندڙ آھي.'),
(5154, 60, 4, 'بيشڪ ابراھيم ۽ جيڪي ساڻس ھوا تن جي طريقي ۾ اوھان لاءِ چڱي پيروي آھي، جڏھن پنھنجي قوم کي چيائون ته بيشڪ اسين اوھان کان ۽ جن کي الله کانسواءِ پوڄيندا آھيو تن کان بيزار آھيون، اوھان جا مُنڪر ٿي چڪاسون ۽ اسان جي ۽ اوھان جي وچ ۾ ھميشه دشمني ۽ وير پڌرو ٿي چڪو جيستائين ھڪ الله تي ايمان (نه) آڻيندؤ (ابراھيم جي پيروي چڱي آھي) سواءِ ھن (ڳالھ) جي ته ابراھيم جو پنھنجي پيءُ کي چوڻ ته تولاءِ ضرور بخشش گھرندس ۽ الله کان تولاءِ ڪجھ (ڪري) نه ٿو سگھان، (ابراھيم ۽ سندس ساٿين چيو ته) اي اسان جا پالڻھار توتي ڀروسو ڪيوسون ۽ توڏانھن موٽياسون ۽ تو ڏانھن (ئي) موٽڻ آھي.'),
(5155, 60, 5, 'اي اسان جا پالڻھار اسان کي ڪافرن جو زيردست نه ڪر ۽ اي اسان جا پالڻھار اسان کي بخش، ڇوته تون ئي غالب حڪمت وارو آھين.'),
(5156, 60, 6, 'بيشڪ انھيءَ (مٿينءَ) جماعت ۾ چڱي پيروي اوھان مان انھيءَ لاءِ آھي جيڪو الله (جي لقاءَ) ۽ قيامت جي ڏينھن (جي اچڻ) جي اميد رکندو ھجي، ۽ جيڪو منھن موڙيندو ته بيشڪ الله سڀ کان بي پرواھ ساراھيل آھي.'),
(5157, 60, 7, 'ويجھو آھي ته الله اوھان جي ۽ انھن جي وچ ۾ دوستي پيدا ڪري جن سان انھن منجھان دشمني رکي اٿوَ، ۽ الله سگھارو آھي، ۽ الله بخشڻھار آھي.'),
(5158, 60, 8, 'الله اوھان کي انھن (سان ڳنڍڻ) کان نٿو جھلي جن اوھان سان دين (جي ڳالھ) ۾ نه جنگ ڪئي آھي ۽ نڪي اوھان جي گھرن مان اوھان کي (ٻاھر) ڪڍيو آھي (۽ ھن ڳالھ کان به نٿو جھلي) ته ساڻن احسان ڪريو ۽ سندن حق ۾ انصاف ڪريو، بيشڪ الله انصاف ڪرڻ وارن کي دوست رکندو آھي.'),
(5159, 60, 9, 'الله اوھان کي رڳو انھن (سان ڳنڍڻ) کان جھلي ٿو جن اوھان سان دين بابت ويڙھ ڪئي ۽ اوھان کي اوھان جي گھرن مان ڪڍيو آھي ۽ اوھان جي ڪڍڻ ۾ (ٻـين جي) مدد ڪئي آھي (ھن کان منع ڪري ٿو) ته ساڻن دوستي رکو، ۽ جيڪي ساڻن دوستي رکندا سي ئي ظالم آھن.'),
(5160, 60, 10, 'اي ايمان وارؤ جڏھن اوھان وٽ مؤمنياڻيون وطن ڇڏي اچن تڏھن کين جانچيو، الله انھن جي ايمان کي چڱو ڄاڻندڙ آھي، پوءِ جيڪڏھن کين مؤمنياڻيون ڄاڻو ته انھن کي ڪافرن ڏانھن موٽائي نه موڪليو، نڪي اُھي (مُسلمان زالون) اُنھن (جي مڙسن ڪافرن) کي حلال آھن ۽ نڪي اُھي (ڪافر مڙس) انھن (مسلمان زالن) کي حلال آھن، ۽ جيڪي (ڪابين بابت) خرچ ڪيائون سو اُنھن مڙسن کي ڏيو، ۽ جڏھن کين سندن ڪابين ڏيو ته انھن جي نڪاح ڪرڻ ۾ اوھان تي (ڪو) گناھ نه آھي، ۽ پاڻ وٽ نه جھليو ڪافرياڻين جي لاڳاپن کي (به) ۽ جيڪي اوھان (پنھنجين زالن تي ڪابين) خرچ ڪيو سو جڳائي ته انھن کان گھرو ۽ جيڪي انھن (ڪافرن) پنھنجين زالن تي خرچ ڪيو سو ڀلي ته اُھي (اوھان کان) گھرن، اھو الله جو حُڪم آھي، جو اوھان جي وچ ۾ فيصلو ٿو ڪري، ۽ الله ڄاڻندڙ حڪمت وارو آھي.'),
(5161, 60, 11, '۽ جيڪڏھن اوھان جي زالن مان ڪا ڪافرن ڏانھن (مُرتد ٿي) وئي ھجي پوءِ (ڪافرن کي) ايذايو ته جن جون زالون ويئون ھجن تن کي (اُن مال مان) اوترو ڏيو جيترو انھن (ڪابين ۾) خرچ ڪيو ھو، ۽ انھيءَ الله کان ڊڄو جو جنھن تي اوھين ويساھ رکندڙ آھيو.'),
(5162, 60, 12, 'اي پيغمبر جڏھن تو وٽ مؤمن زالون اچن ھن (شرط) تي ته توسان بيعت ڪن ته ڪنھن کي الله سان شريڪ مُقرّر نه ڪنديون ۽ نڪي چوري ڪنديون ۽ نڪي زنا ڪنديون ۽ نڪي پنھنجي اولاد کي ڪُھنديون ۽ نڪي اھڙي ڪوڙي تھمت آڻينديون جنھن کي پنھنجن ھٿن ۽ پنھنجن پيرن سان ٺاھيو ھجين ۽ نڪي ڪنھن چڱي ڪم ۾ تنھنجي نافرماني ڪنديون تڏھن سندن بيعت قبول ڪر ۽ انھن لاءِ الله کان بخشش گھر، ڇوته الله بخشڻھار مھربان آھي.'),
(5163, 60, 13, 'اي ايمان وارؤ اوھين انھيءَ قوم سان دوستي نه رکو جنھن تي الله ڏمريو آھي بيشڪ اُھي آخرت (جي ثواب) کان (اھڙو) نا اُميد ٿيا آھن جھڙوڪ ڪافر قبرن وارن کان نا اُميد ٿيا آھن.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) الله جي پاڪائي بيان ڪندو آھي، ۽ اُھو غالب حِڪمت وارو آھي.'),
(5165, 61, 2, 'اي ايمان وارؤ جيڪي نه ڪندا آھيو سو ڇو چوندا آھيو.'),
(5166, 61, 3, 'الله وٽ اھا (ڳالھ) ڏاڍي ناپسند آھي جو اُھا (ڳالھ) چئو جا نٿا ڪريو.'),
(5167, 61, 4, 'بيشڪ الله انھن کي دوست رکندو آھي جي سندس واٽ ۾ (اھڙي) صف ٻڌي وڙھندا آھن (جو) ڄڻڪ اُھي ھڪڙي سِيھي پلٽيل ڀِت آھن.'),
(5168, 61, 5, '۽ (ياد ڪر) جڏھن مُوسىٰ پنھنجي قوم کي چيو ته اي منھنجي قوم مون کي ڇو ايذائيندا آھيو ۽ حالانڪ (اوھين) ڄاڻندا آھيو ته آءٌ اوھان ڏانھن الله جو موڪيل آھيان، پوءِ جنھن مھل ڏنگائي ڪيائون ته الله سندن دلين کي ڏنگو ڪيو، ۽ الله بدڪار قوم کي سڌو رستو نه ڏيکاريندو آھي.'),
(5169, 61, 6, '۽ (اُھو وقت ياد ڪر) جڏھن عيسىٰ پُٽ مريم جي چيو ته اي بني اسرائيلو بيشڪ آءٌ اوھان ڏانھن الله جو موڪليل آھيان جيڪو توريت منھنجي اڳيان آھي تنھنجو سچو ڪندڙ آھيان ۽ ھڪ اھڙي پيغمبر جي خوشخبري ڏيندڙ آھيان جو مُون کانپوءِ ايندو جنھن جو نالو احمد ھوندو، پوءِ جنھن مھل اُھو چِٽن مُعجزن سان وٽن آيو چيائون ته ھي پڌرو جادو آھي.'),
(5170, 61, 7, '۽ اُن کان وڌيڪ ظالم ڪير آھي جنھن الله تي ڪوڙ ٻَڌو حالانڪ ان کي اِسلام ڏانھن سڏيو ويندو آھي، ۽ الله ظالمن جي قوم کي سِڌو رستو نه ڏيکاريندو آھي.'),
(5171, 61, 8, '(اُھي ڪافر) گھرندا آھن ته الله جي نُور کي پنھنجي واتن سان وِسائين، حالانڪ الله پنھنجي نُور کي پوري ڪرڻ وارو آھي توڻيڪ ڪافر مٺيان ڀانئين.'),
(5172, 61, 9, '(الله) اُھو آھي جنھن پنھنجي پيغمبر کي ھدايت ۽ سچي دين سان موڪليو ته اُن کي سڀني دينن تي غالب ڪري توڻيڪ مشرڪ بڇان ڀانئين.'),
(5173, 61, 10, 'اي ايمان وارؤ اوھان کي انھيءَ سوداگريءَ جو ڏس نه ڏيان ڇا جو اوھان کي ڏکوئيندڙ عذاب کان بچائي؟'),
(5174, 61, 11, '(ته) الله ۽ سندس پيغمبر تي ايمان آڻيو ۽ پنھنجن مالن ۽ پنھنجن جندن سان الله جي واٽ ۾ لڙائي ڪريو، جيڪڏھن ڄاڻندا آھيو ته اُھو اوھان لاءِ ڀلو آھي.'),
(5175, 61, 12, 'اوھان جا گناھ اوھان کي بخشيندو ۽ اوھان کي (اھڙن) باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن ۽ سُٺين جاين ھميشه (رھڻ) وارن بھشتن ۾، اھا وڏي مراد ماڻڻ آھي.'),
(5176, 61, 13, '۽ ٻي (نعمت به ڏيندو) جنھن کي دوست رکندا آھيو، الله جي طرف کان مدد ۽ فتح ويجھي، ۽ مؤمنن کي خوشخبري ڏي.'),
(5177, 61, 14, 'اي ايمان وارؤ الله جي دين جا مددگار ھجو جھري طرح عيسىٰ پٽ مريم جي پنھنجي خاص يارن کي چيو ته الله جي دين ۾ منھنجا مددگار ڪير آھن؟ انھن خاص يارن چيو ته اسين الله (جي دين) جا مددگار آھيون پوءِ بني اسرائيلن مان ھڪ ٽولي ايمان آندو ۽ ٻئي ٽوليءَ انڪار ڪيو، پوءِ ايمان وارن کي سندن دشمنن تي غلبو ڏنوسون پوءِ غالب ٿيا.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) الله جي پاڪائي بيان ڪندو آھي (جو) بادشاھ تمام پاڪ (ذات) غالب حڪمت وارو آھي.'),
(5179, 62, 2, '(الله) اُھو آھي جنھن اڻ پڙھيلن ۾ انھن (جي قوم) مان ھڪ پيغمبر پيدا ڪيو جو وٽن سندس آيتون پڙھندو آھي ۽ کين پاڪ ڪندو آھي ۽ کين ڪتاب ۽ دانائي سيکاريندو آھي، ۽ بيشڪ اِھي (ھن کان) اڳ پڌري گمراھي ۾ ھوا.'),
(5180, 62, 3, '۽ انھن مان ٻـين لاءِ به جي اڃا انھن (مسلمانن) سان نه مليا آھن، ۽ اُھو غالب حڪمت وارو آھي.'),
(5181, 62, 4, 'اِھو الله جو فضل آھي انھن کي ڏيندو آھي جنھن کي وڻندو اٿس، ۽ الله وڏي فضل وارو آھي.'),
(5182, 62, 5, 'جن (ماڻھن) کي توريت کڻايو ويو پوءِ اُھو نه کنيائون تن جو مثال (انھيءَ) گڏھ جي مثال وانگر آھي جو (گھڻا) ڪتاب کڻي، جن الله جي آيتن کي ڪوڙ ٿي ڀانيو تنھن قوم جو مثال بڇڙو آھي، ۽ الله ظالم ماڻھن کي سڌو رستو نه ڏيکاريندو آھي.'),
(5183, 62, 6, '(اي پيغمبر) چؤ ته اي يھوديو جيڪڏھن ڀائيندا آھيو ته ٻـين (سڀني) ماڻھن کانسواءِ رڳو اوھين الله جا دوست آھيو ته جيڪڏھن (انھي ڳالھ ۾) سچا آھيو ته موت گھرو.'),
(5184, 62, 7, '۽ جيڪي سندن ھٿن اڳي موڪليو آھي تنھنجي ڪري اُھو ڪڏھن نه گھرندا، ۽ الله ظالمن کي ڄاڻندڙ آھي.'),
(5185, 62, 8, '(اي پيغمبر) چؤ ته بيشڪ اُھو موت جنھن کان ڀڄندا آھيو سو ته ضرور اوھان کي پھچڻ وارو آھي وري (اوھين) ڳجھ ڄاڻندڙ ڏانھن موٽايا ويندؤ پوءِ جيڪي (اوھين دُنيا ۾) ڪندا ھئو تنھن جي اوھان کي سُڌ ڏيندو.'),
(5186, 62, 9, 'اي ايمان وارؤ جڏھن جُمعي جي ڏينھن نماز لاءِ بانگ ڏني وڃي تڏھن الله جي ياد ڪرڻ ڏانھن ڊُڙو ۽ واپار ڇڏيو، جيڪڏھن ڄاڻندا آھيو ته اھو اوھان لاءِ ڀلو آھي.'),
(5187, 62, 10, 'پوءِ جڏھن نماز پوري ڪئي وڃي ته زمين تي کنڊري وڃو ۽ الله جي فضل جي طلب ڪريو ۽ الله کي گھڻو ياد ڪريو ته مانَ اوھين ڇٽو.'),
(5188, 62, 11, '۽ (اُھي مسلمان) جڏھن ڪو سودو (ھلندو) يا ڪو تماشو ڏسن ٿا (ته) اُن ڏانھن کنڊري وڃن ٿا ۽ توکي (خطبي ۾) بيٺل ڇڏين ٿا، (انھن کي) چؤ ته جيڪي الله وٽ آھي سو تماشي ۽ واپار کان (وڌيڪ) ڀَلو آھي، ۽ الله (سڀني) روزي ڏيندڙن کان ڀَلو آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'جڏھن منافق تو وٽ ايندا آھن (ته) چوندا آھن ته شاھدي ڏيون ٿا ته بيشڪ تون الله جو پيغمبر آھين، ۽ الله ڄاڻندو آھي ته بيشڪ تون سندس پيغمبر آھين، ۽ الله شاھدي ٿو ڏئي ته بيشڪ (اُھي) منافق ڪوڙا آھن.'),
(5190, 63, 2, 'پنھنجن قَسمن کي ڍال (ڪري) ورتو اٿن پوءِ الله جي واٽ کان جھلن ٿا، بيشڪ اُھي جيڪي ڪندا آھن سو بڇڙو آھي.'),
(5191, 63, 3, 'اھو ھن ڪري آھي جو اُنھن ايمان آندو وري انڪار ڪيائون پوءِ انھن جي دلين تي مُھر ھنئي وئي تنھنڪري اُھي نه سمجھندا آھن.'),
(5192, 63, 4, '۽ (اي پيغمبر تون) جڏھن کين ڏسندو آھين (تڏھن) سندن جُسا توکي وڻندا آھن، ۽ جيڪڏھن ڳالھائيندا آھن ته اُنھن جي ڳالھ ڪَن ڏئي ٻڌندو آھين، ڄڻڪ اُھي اُڀيون ڀِت سان ڪاٺيون رکيل آھن، سڀڪنھن آواز کي پاڻ تي ڀو ڀائيندا آھن، اُھي ويري آھن تنھنڪري کائن حِذ رک، الله کين ھلاڪ ڪري، ڪيڏانھن ڦيرايا ٿا وڃن.'),
(5193, 63, 5, '۽ جڏھن کين چئبو آھي ته اچو ته الله جو پيغمبر اوھان لاءِ بخشش گھري تڏھن پنھنجا مٿا موڙيندا آھن ۽ انھن کي ڏسندو آھين ته وڏائي ڪندڙ ٿي جھلجن ٿا.'),
(5194, 63, 6, 'تون انھن لاءِ بخشش گھرين يا انھن لاءِ بخشش نه گھرين ته انھن لاءِ ھڪ جھڙي (ڳالھ) آھي، انھن کي الله ڪڏھن نه بخشيندو، بيشڪ الله بي دينن جي قوم کي سِڌو رستو نه ڏيکاريندو آھي.'),
(5195, 63, 7, 'اھي اھڙا آھن جو (ھڪ ٻئي کي) چوندا آھن ته جيڪي (مھاجر) الله جي پيغمبر وٽ آھن تن تي خرچ نه ڪريو تان جو ڇڏي وڃن، ۽ (ھيءَ نه ڄاڻندا آھن ته) آسمانن ۽ زمين جا خزانا خاص الله جا آھن پر (ان ڳالھ کي) مُنافق نه سمجھندا آھن.'),
(5196, 63, 8, 'چوندا آھن ته جيڪڏھن (اسين) مديني ۾ موٽي پُتاسون ته وڌيڪ عِزّت وارو تمام بي عزتي کي اُتان ضرور ڪڍي ڇڏيندو، ۽ عِزّت الله کي ۽ سندس پيغمبر کي ۽ مؤمنن کي آھي پر (اھا ڳالھ) منافق نه ڄاڻندا آھن.'),
(5197, 63, 9, 'اي ايمان وارؤ اوھان کي نڪي اوھان جا مال ۽ نڪي اوھان جا اولاد الله جي ياد ڪرڻ کان ويسلو ڪري، ۽ جيڪي ائين ڪندا سي ئي ڇيئي وارا آھن.'),
(5198, 63, 10, '۽ جيڪي اوھان کي روزي ڏني اٿون تنھن منجھان اُنھي کان اڳ خرچ ڪريو جو اوھان مان ڪنھن ھڪڙي کي موت پھچي پوءِ چوڻ لڳي ته اي منھنجا پالڻھار جيڪر مون کي ھڪ ٿوري مُدت تائيم مُھلت ڏين ھا ته خيرات ڪريان ھا ۽ صالحن مان ٿيان ھا.'),
(5199, 63, 11, '۽ جڏھن ڪنھن جيءَ جو اجل ايندو آھي (تڏھن) اُن کي الله ڪڏھن مُھلت نه ڏيندو آھي، ۽ جيڪي ڪندا آھيو تنھنجي الله خبر رکندڙ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'جيڪي آسمانن ۾ آھي ۽ جيڪي زمين ۾ آھي سو (سڀ) الله جي پاڪائي بيان ڪندو آھي، سندس ئي بادشاھي آھي ۽ سنديس ئي ساراھ آھي، ۽ اُھو سڀڪنھن شيءِ تي وس وارو آھي.'),
(5201, 64, 2, '(الله) اھو آھي جنھن اوھان کي پيدا ڪيو پوءِ اوھان مان ڪو ڪافر ۽ اوھان مان ڪو مؤمن آھي، ۽ جيڪي ڪندا آھيو سو الله ڏسندڙ آھي.'),
(5202, 64, 3, 'آسمانن ۽ زمين کي پوريءَ رِٿ سان بڻايائين ۽ اوھانکي صُورت ڏنائين پوءِ اوھان جون صُورتون تمام چڱيون بڻايائين ۽ ڏانھس موٽڻ آھي.'),
(5203, 64, 4, 'جيڪي آسمانن ۽ زمين ۾ آھي سو (اُھو) ڄاڻندو آھي ۽ جيڪي لڪائيندا آھيو ۽ جيڪي پڌرو ڪندا آھيو سو (به) ڄاڻندو آھي، ۽ الله سينن جو ڳجھ ڄاڻندڙ آھي.'),
(5204, 64, 5, '(ھن کان) اڳ جيڪي ڪافر ھئا تن جي اوھان وٽ خبر نه آئي آھي ڇا؟ پوءِ پنھنجي ڪئي جي سزا چکيائون ۽ اُنھن لاءِ ڏکوئيندڙ عذاب آھي.'),
(5205, 64, 6, 'اِھو (عذاب) ھِن ڪري آھي جو سندن پيغمبر (چِٽن) مُعجزن سان وٽن ايندا ھئا ته چوندا ھوا ته اسان کي (اسان جھڙا) ماڻھو (سڌو) رستو ڏيکاريندا ڇا، پوءِ انڪار ڪيائون ۽ منھن ڦيرايائون ۽ الله (به) بي پرواھي ڪئي، ۽ الله بي پرواھ ساراھيل آھي.'),
(5206, 64, 7, 'ڪافر ڀائيندا آھن ته (قبرن مان) کين ڪڏھن نه اُٿاربو، (کين) چؤ ته ھائو! منھنجي پالڻھار جو قسم ته (اوھين) ضرور اُٿاربؤ وري جيڪي ڪندا ھئو تنھن جي اوھان کي ضرور سُڌ ڏبي، ۽ اھو (ڪم) الله کي آسان آھي.'),
(5207, 64, 8, 'تنھنڪري الله ۽ سندس پيغمبر تي ايمان آڻيو ۽ اُنھيءَ نُور (قرآن) تي به جيڪو نازل ڪيوسون، ۽ جيڪي ڪندا آھيو تنھن جي الله خبر رکندڙ آھي.'),
(5208, 64, 9, 'جنھن مھل اوھان کي قيامت جي ڏينھن ۾ گڏ ڪندو اُھو ڏينھن ھارجڻ ۽ کٽجڻ جو آھي، ۽ جيڪو الله تي ايمان آڻيندو ۽ چڱا ڪم ڪندو تنھن کان سندس مدايون الله ميٽيندو ۽ اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن اُتي سدائين رھندا، اُھا وڏي مراد ماڻڻ آھي.'),
(5209, 64, 10, '۽ جن ڪُفر ڪيو ۽ اسان جي آيتن کي ڪوڙو ڄاتو اھي دوزخي آھن اُن ۾ سدائين رھندا، ۽ (اُھو) ھنڌ بڇڙو آھي.'),
(5210, 64, 11, 'الله جي حُڪم کانسواءِ ڪابه مصيبت نه پھچندي آھي، ۽ جيڪو الله تي ايمان آڻيندو تنھن جي دل کي (اُھو) ھدايت ڪندو، ۽ الله ھر شيءَ کي ڄاڻندڙ آھي.'),
(5211, 64, 12, '۽ الله جو چيو مڃيو ۽ پيغمبر جو چيو مڃيو، پوءِ جيڪڏھن مُنھن موڙيندؤ ته اسان جي پيغمبر تي رڳو پڌرو پيغام پھچائڻ آھي.'),
(5212, 64, 13, 'الله (اُھو آھي جو) اُن کانسواءِ (ٻيو) ڪو عبادت جو لائق نه آھي، ۽ مؤمنن کي جڳائي ته الله تي (ئي) ڀروسو ڪن.'),
(5213, 64, 14, 'اي ايمان وارؤ بيشڪ ڪي اوھان جون زالون ۽ ڪي اوھان جو اولاد اوھان جو دشمن آھي تنھنڪري انھن کان حذر ڪريو، ۽ جيڪڏھن معافي ڪريو ۽ ٽارو ڪريو ۽ بخشيو ته بيشڪ الله (به) بخشڻھار مھربان آھي.'),
(5214, 64, 15, 'اوھان جا مال ۽ اوھان جو اولاد رڳو پرک آھن، ۽ الله ئي وٽ وڏو اجر آھي.'),
(5215, 64, 16, 'تنھنڪري جيترو ٿي سگھيوَ اوترو الله کان ڊڄو ۽ (الله جو حُڪم) ٻڌو ۽ چيو مڃيو ۽ خرچ ڪريو ته اوھان جي جِندن لاءِ چڱو آھي، ۽ جيڪي پنھنجي نفس جي حرص کان بچايا ويا سي ئي ڇُٽڻ وارا آھن.'),
(5216, 64, 17, 'جيڪڏھن الله کي چڱيءَ طرح قرض ڏيندؤ ته (الله) اوھان کي اُھو (قرض) ٻيڻون (ورائي ڏيندو) ۽ اوھان کي بخشيندو، ۽ الله قدردان بردبار آھي.'),
(5217, 64, 18, 'ڳجھ ۽ ظاھر جو ڄاڻندڙ غالب حڪمت وارو آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'اي پيغمبر (پنھنجي اُمت کي چؤ ته اوھين) جڏھن زالن کي طلاق ڏيڻ جو ارادو ڪريو تڏھن کين عِدت (جي شروع) ۾ طلاق ڏيو ۽ عِدَّت ڳڻيو، ۽ پنھنجي پالڻھار الله کان ڊڄو، کين سندن گھرن مان (عِدّت جي مُدّت ۾) پڌري بي حيائي جي ڪرڻ کانسواءِ نه ڪڍو ۽ (جڳائي ته) اُھي (پاڻ به) نه نڪرن، اھي الله جون (مُقرّر ڪيل) حدون آھن، ۽ جيڪو الله جون حدون (لتاڙي) لنگھندو تنھن بيشڪ پنھنجي جيءُ تي ظلم ڪيو، ڪوبه نه ڄاڻندو آھي ته اُن (طلاق ڏيڻ) کانپوءِ (متان) الله ڪو ٻيو رستو پيدا ڪري.'),
(5219, 65, 2, 'پوءِ جڏھن (طلاق واريون) پنھنجي مُدّت کي پھچن تڏھن چڱي طرح سان کين رکو يا چڱي طرح سان کين ڇڏي ڏيو ۽ پاڻ مان ٻه معتبر شاھد ڪريو ۽ الله لڳ شاھدي پوري ڏيو، انھيءَ (حُڪم) سان انھيءَ کي نصيحت ڏجي ٿي جيڪو الله ۽ قيامت جي ڏينھن کي مڃيندو ھجي ۽ جيڪو الله کان ڊڄندو تنھن جي نڪرڻ جي (الله) ڪا واھ ڪندو.'),
(5220, 65, 3, '۽ کيس اُتاھون روزي ڏيندو جتان گمان ئي نه ھوندس، ۽ جيڪو الله تي ڀروسو ڪندو تنھن کي الله ڪافي آھي، بيشڪ الله پنھنجي ڪم کي پورو ڪندڙ آھي، بيشڪ الله سڀڪنھن شيءِ جو اندازو ڪيو آھي.'),
(5221, 65, 4, '۽ اوھان جي (طلاق ڏنل) زالن مان جيڪي حيض کان نااُميد ٿيون ھجن (تن جي عِدّت بابت) جيڪڏھن شڪ ۾ پيا آھيو ته انھن جي عِدّت ٽي مھينا آھي ۽ جيڪي اڃا حيض واريون نه ٿيون آھن (تن جي به)، ۽ پيٽ وارين زالن جي عِدّت اِھا آھي جو ٻار ڄڻين، ۽ جيڪو الله کان ڊڄندو تنھن لاءِ الله سندس ڪم ۾ آساني ڪندو.'),
(5222, 65, 5, 'اھو الله جو حُڪم آھي جو اوھان ڏانھن ڏانھن نازل ڪيائين، ۽ جيڪو الله کان ڊڄندو تنھن جون مدايون الله کانئن ميٽيندو ۽ ان کي اجر وڌيڪ ڏيندو.'),
(5223, 65, 6, 'جتي پنھنجي وس آھر (اوھين) ٽڪندا آھيو تتي انھن کي (عدت جا ڏينھن) ٽڪايو ۽ کين ھن لاءِ نه ايذايو ته انھن کي تنگ ڪريو، ۽ جيڪڏھن پيٽ سان ھجن ته جيسين پنھنجو ٻار ڄڻين (تيسين) کين خرچ ڏيو، پوءِ جيڪڏھن اوھان جي حُڪم موجب (اُھي ٻار کي) کير پيارين ته کين سندن مزوري ڏيو، ۽ ھلي چليءَ موجب پاڻ ۾ صلاح ڪريو، ۽ جيڪڏھن پاڻ ۾ سختي ڪريو ته اُن کي ٻي (ڪا) کير پياري.'),
(5224, 65, 7, 'جڳائي ته وس وارو پنھنجي وس آھي خرچ ڪري، ۽ جنھن تي روزي تنگ ڪئي ويئي آھي تنھن کي جڳائي ته جيڪي الله کيس ڏنو آھي تنھن مان خرچ ڪري، ڪنھن کي الله تڪليف نه ڏيندو آھي پر (اوتري قدر) جيتري قدر کيس الله ڏنو آھي، سگھوئي الله ڏکيائيءَ کانپوءِ سکيائي ظاھر ڪندو.'),
(5225, 65, 8, '۽ (اھڙا) گھڻائي ڳوٺ (ھوا) جن پنھنجي پالڻھار جي حُڪم ۽ سندس پيغمبرن (جي حڪم) کان نافرماني ڪئي پوءِ سخت عذاب سان انھن کي پڪڙيوسون ۽ سخت عذاب سان کين عذاب ڪيوسون.'),
(5226, 65, 9, 'پوءِ انھن (ڳوٺ وارن) پنھنجي ڪم جي سزا چکي ۽ انھن جي ڪم جي پڇاڙي خراب ٿي.'),
(5227, 65, 10, 'انھن لاءِ الله سخت عذاب تيار ڪيو آھي تنھنڪري اي عقل وارا مؤمنؤ الله کان ڊڄو، بيشڪ الله اوھان ڏانھن ھڪ نصيحت موڪلي آھي.'),
(5228, 65, 11, 'جو رسول آھي جو اوھان تي الله جون پڌريون آيتون (ھن لاءِ) پڙھندو آھي ته جن ايمان آندو ۽ چڱا ڪم ڪيا تن کي اونداھين کان سوجھري ڏانھن ڪڍي، ۽ جيڪو الله تي ايمان آڻيندو ۽ چڱا ڪم ڪندو تنھن کي اھڙن باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن اُتي ھميشه رھندا، بيشڪ اُن جي روزي چڱي طرح ٺاھي آھي.'),
(5229, 65, 12, 'الله اُھو آھي جنھن ستن آسمانن کي بڻايو ۽ اوتريون زمينون به، اُنھن جي وچ ۾ اُن جو حُڪم نازل ٿيندو آھي ته ڄاڻو ته الله سڀڪنھن شيءَ تي وس وارو آھي ۽ ته الله عِلم سان سڀڪنھن شيءِ کي وڪوڙيو آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'اي پيغمبر جنھن شيءِ کي الله تولاءِ حلال ڪيو آھي سا ڇو ٿو حرام ڪرين؟ پنھنجين زالن جو راضپو گھرين ٿو، ۽ الله بخشڻھار مھربان آھي.'),
(5231, 66, 2, 'بيشڪ الله اوھان لاءِ اوھان جي قسمن جو کولڻ درست ڪيو آھي، ۽ الله اوھان جو سائين آھي، ۽ اُھو ڄاڻندڙ حِڪمت وارو آھي.'),
(5232, 66, 3, '۽ (ياد ڪر) جڏھن پيغمبر پنھنجين زالن مان ڪنھن ھڪڙي کي ڪا ڳالھ ڳجھي چئي، پوءِ جڏھن اُن (ڳالھ) کي اُن (بي بيءَ) پڌرو ڪيو ۽ الله پيغمبر کي اُن (ڳجھي ڳالھ جي پڌري ڪرڻ) تي واقف ڪيو (تڏھن) پيغمبر ڪُجھ اُھا (ڳالھ بي بيءَ کي) ڄاڻائي ڪجھ چوڻ کان ٽارو ڪيائين، پوءِ جڏھن اُن (بي بيءَ) کي ڳجھيءَ ڳالھ (جي پڌري ڪرڻ) کان واقف ڪيائين (تڏھن) اُن (بي بي) چيو ته اھا توکي ڪنھن ڄاڻائي، (پيغمبر) فرمايو ته مون کي (الله) ڄاڻندڙ خبر رکندڙ خبر ڏني.'),
(5233, 66, 4, '(اي پيغمبر جون زالون) جيڪڏھن اوھين ٻئي الله وٽ توبه ڪنديون ته بيشڪ اوھان جي دلين لاڙو ڪيو آھي، ۽ جيڪڏھن پيغمبر (جي ايذائڻ تي ٻئي گڏجي ڏاڍ ڪنديون ته بيشڪ الله سندس ڀرجھلو آھي ۽ جبرئيل ۽ سُڌريل مؤمن (به)، ۽ ھن کانپوءِ ملائڪ (به) مددگار آھن.'),
(5234, 66, 5, 'جيڪڏھن پيغمبر اوھان کي طلاق ڏيندو ته اميد آھي ته سندس پالڻھار اوھان کان چڱيون زالون فرمانبردار مؤمنياڻيون نماز پڙھڻ واريون توبه ڪرڻ واريون عبادت ڪرڻ واريون رنيلان ۽ ڪنواريون (ٻيون) ان کي عوض ۾ ڏيندو.'),
(5235, 66, 6, 'اي ايمان وارؤ پاڻ کي پنھنجي گھر جي ڀاتين کي انھيءَ باھ کان بچايو جنھن جو ٻارڻ ماڻھو ۽ پھڻ ھوندا جنھن (باھ) تي سخت طبع زبردست ملائڪ (مُقرر) آھن جيڪي (الله) کين فرمايو آھي تنھن ۾ الله جي نافرماني نه ڪندا آھن ۽ جيڪي کين حُڪم ڪبو آھي سو ڪندا آھن.'),
(5236, 66, 7, '(ان ڏينھن چئبو ته) اي ڪافرؤ اڄ بھانو (پيش) نه ڪريو، جيڪي ڪندا ھئو رڳو تَنھنجي اوھان کي سزا ڏجي ٿي.'),
(5237, 66, 8, 'اي ايمان وارؤ الله وٽ توبه ڪريو سچي توبه، اوھان جي پالڻھار ۾ اميد آھي ته اوھان جون مدايون اوھان کان ميٽيندو ۽ اوھان کي (اھڙن) باغن ۾ داخل ڪندو جن جي ھيٺان واھيون وھنديون آھن جنھن ڏينھن جو الله پيغمبر کي ۽ جن ساڻس ايمان آندو آھي تن کي خوار نه ڪندو، سندن (ايماني) نُور سندن اڳيان ۽ سندن سڄن پاسن ۾ ڊوڙندو چوندا (ته) اي اسان جا پالڻھار اسان لاءِ اسان جو نُور ڀرپور ڪر ۽ اسان کي بخش، بيشڪ تون سڀڪنھن شيءَ تي وس وارو آھين.'),
(5238, 66, 9, 'اي پيغمبر ڪافرن ۽ منافقن سان لڙائي ڪر ۽ مٿن سختي ڪر، ۽ سندن جاءِ دوزخ آھي، ۽ اُھو ھنڌ بڇڙو آھي.'),
(5239, 66, 10, 'الله نُوح جي زال ۽ لُوط جي زال جو ھڪ مثال ڪافرن لاءِ بيان ڪري ٿو، (اُھي) اسان جي ٻن صالحن ٻانھن (جي نڪاح) ھيٺ ھيون پوءِ اُنھن زالن ٻنھي سان خيانت ڪئي ۽ پوءِ اُھي ٻئي (پنھنجين) زالن کان الله جو عذاب ڪجھ به ٽاري نه سگھيا (انھن کي) چئبو ته باھ ۾ گھڙندڙن سان گڏ گھڙو.'),
(5240, 66, 11, '۽ الله مؤمنن لاءِ فرعون جي زال جو ھڪ مثال بيان ڪري ٿو، جڏھن چيائين ته اي منھنجا پالڻھار پاڻ وٽ بھشت ۾ مون لاءِ ڪو گھر ٺاھ ۽ فرعون ۽ سندس ڪم کان مون کي بچاءِ ۽ ظالمن جي ٽوليءَ کان (پڻ) مون کي بچاءِ.'),
(5241, 66, 12, '۽ مَرۡيم ڌِيءُ عِمۡران جي جنھن پنھنجي اُگھڙ کي بچايو پوءِ اُن (جي رحم) ۾ پنھنجو رُوح ڦوڪيوسون ۽ پنھنجي پالڻھار جي حُڪمن ۽ سندس ڪتابن کي سچ ڄاتو ھوائين ۽ فرمانبردارن مان ھُئي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'اُھو (الله) وڏي برڪت وارو آھي جنھن جي ھٿ ۾ (ساري مُلڪ جي) بادشاھي آھي، ۽ اُھو سڀڪنھن شيءِ تي وس وارو آھي.'),
(5243, 67, 2, '(الله اُھو آھي) جنھن موت ۽ حياتي پيدا ڪئي (انھيءَ لاءِ) ته اوھان کي پرکي ته عمل جي ڪري تمام چڱو اوھان مان ڪير آھي؟ ۽ اُھو غالب بخشڻھار آھي.'),
(5244, 67, 3, '(الله اُھو آھي) جنھن ستن آسمانن کي طبقا طبقا بڻايو، (اي ڏسندڙ تون الله) ٻاجھاري جي بڻاوت ۾ ڪو فرق نه ڏسندين. پوءِ ورائي (نظر ڪري) ڏس ته (ڪٿي) ڪا ڦوٽ ڏسين ٿو؟'),
(5245, 67, 4, 'پوءِ وري به ٻه ڀيرا ڏس ته (ضرور تنھنجي) نظر تو ڏانھن جھڪي ٿي ٿڪجي موٽندي.'),
(5246, 67, 5, '۽ بيشڪ دنيا جي آسمان کي (ستارن جي) ڏيئن سان سينگاريو اٿون ۽ اُنھن کي شيطانن جي چُٽڻ لاءَ (به) بڻايوسون ۽ شيطانن لاءِ دوزخ جو عذاب تيار ڪيو اٿون.'),
(5247, 67, 6, '۽ جن پنھنجي پالڻھار جو انڪار ڪيو تن لاءِ دوزخ جي سزا آھي، ۽ اُھا جاءِ بڇڙي آھي.'),
(5248, 67, 7, 'جڏھن کين دوزخ ۾ اڇليو ويندو (تڏھن) اُن جون (گڏھ جھڙيون) ھينگون ٻڌندا ۽ اُھو پيو اُڀامندو.'),
(5249, 67, 8, 'جو ڪاوڙ کان (ڄڻ ته) اجھو ٿو ڦاٽي. جنھن مھل ڪنھن ٽوليءَ کي اُن ۾ پيو وجھبو (تنھن مھل) دوزخ جا داروغا انھن کان پڇندا ته اوھان وٽ ڪو ڊيڄاريندڙ (پيغمبر) نه آيو ھو ڇا؟'),
(5250, 67, 9, 'چوندا ھائو! بيشڪ اسان وٽ ڊيڄاريندڙ آيو ھو، پر (اُن کي) ڪوڙو ڄاتوسون ۽ چيوسون ته الله ڪا (به) شيءِ نه لاٿي آھي، اوھين ته رڳو وڏيءَ گمراھيءَ ۾ (پيل) آھيو.'),
(5251, 67, 10, '۽ چوندا ته جيڪڏھن اسين ٻُڌون ھا يا سمجھون ھا ته دوزخين (جي ٽوليءَ) ۾ (ڪڏھن داخل) نه ٿيون ھا.'),
(5252, 67, 11, 'پوءِ پنھنجو ڏوھ (پاڻ) مڃيندا، پوءِ دوزخين تي لعنت ھجي شال.'),
(5253, 67, 12, 'بيشڪ جيڪي پنھنجي پالڻھار کان پرپٺ ڊڄندا آھن تن لاءِ بخشش ۽ وڏو اجر آھي.'),
(5254, 67, 13, '۽ پنھنجي ڳالھ ڳجھي ڳالھايو يا اُھا پڌري چئو، ته بيشڪ الله سينن جو ڳُجھ ڄاڻندڙ آھي.'),
(5255, 67, 14, 'جنھن پيدا ڪيو سو ڪيئن نه ڄاڻندو؟ ۽ اُھو اونھي نظر وارو خبر رکندڙ آھي.'),
(5256, 67, 15, '(الله) اُھو آھي جنھن زمين کي اوھان جي لاءِ تابع ڪيو ته ان جي رستن ۾ گھمو ۽ الله جي رزق مان کائو، ۽ (جيئرو ٿي) ڏانھس کڙو ٿيڻو آھي.'),
(5257, 67, 16, '(اوھين) اُنھيءَ کان بي ڀَوا ٿيا آھيو ڇا ته جيڪو آسمان ۾ آھي سو متان اوھان کي زمين ۾ ڳِھائي ڇڏي پوءِ اُتي جو اُتي زمين ڌڏي؟'),
(5258, 67, 17, '(اوھين) انھيءَ کان بي ڀَوا ٿيا آھيو ڇا ته جيڪو آسمان ۾ آھي سو متان اوھان تي پھڻ وسائيندڙ واءُ موڪلي، پوءِ سگھو ڄاڻندؤ ته منھنجو ڊيڄارڻ ڪيئن آھي؟'),
(5259, 67, 18, '۽ بيشڪ جيڪي انھن کان اڳ ھوا تن (به) ڪوڙ ڀانيو پوءِ منھنجي سزا ڪھڙي طرح ھئي.'),
(5260, 67, 19, 'پنھنجي مٿاھون پکين ڏانھن نه ڏٺو اٿن ڇا؟ جي کنڀڙاٽيون ٽيڙي ھلندا آھن ۽ (ڪڏھن ڪڏھن) بند ڪندا آھن، انھن کي ٻاجھاري (الله) کانسواءِ (ٻيو ڪو) به جھلي نه رکندو آھي، بيشڪ اُھو سڀڪنھن شيءِ کي ڏسندڙ آھي.'),
(5261, 67, 20, 'اُھو اوھان جو لشڪر ڪھڙو آھي جو اوھان کي ٻاجھاري (الله) کانسواءِ مدد ڏئي؟ ڪافر رڳو ڀولي ۾ (پيل) آھن.'),
(5262, 67, 21, '(ڀلا) اُھو ڪير آھي جو جيڪڏھن الله پنھنجو رزق جھلي ته اھو اوھان کي روزي ڏئي؟ بلڪ (ڪافر) سرڪشي ۽ (حق کان) نفرت ۾ اڙجي پيا آھن.'),
(5263, 67, 22, 'ڀلا جيڪو پنھنجي مُنھن ڀر اونڌو ڪريل ھلندو ھجي سو وڌيڪ سڌي رستي وارو آھي يا اُھو جيڪو سڌو سنئون سڌي واٽ تي ھلندو ھجي؟'),
(5264, 67, 23, 'چئو ته (الله) اُھو آھي جنھن اوھان کي پيدا ڪيو ۽ اوھان جا ڪن ۽ اکيون ۽ دليون بڻايائين، (اوھين) ٿورڙو احسان مڃيندا آھيو.'),
(5265, 67, 24, 'چئونَ ته (الله) اُھو آھي جنھن اوھان کي زمين ۾ پکيڙيو ۽ (اوھين) ڏانھس گڏ ڪيا ويندؤ.'),
(5266, 67, 25, '۽ (ڪافر) چوندا آھن ته اھو انجام ڪڏھن (پورو) ٿيندو جيڪڏھن سچا آھيو؟'),
(5267, 67, 26, '(اي پيغمبر کين) چؤ ته (اھا) خبر رڳو الله وٽ آھي، ۽ آءٌ رڳو پڌرو ڊيڄاريندڙ آھيان.'),
(5268, 67, 27, '۽ جنھن مھل ان (انجام) کي ويجھو ٿيل ڏسندا ته ڪافرن جا مُنھن ڪاراٽجي ويندا، ۽ چئبن ته جنھن کي طلبيندا ھئو سو ھيءُ آھي.'),
(5269, 67, 28, '(اي پيغمبر کين) چؤ ته ڏٺو اٿوَ ڇا ته جيڪڏھن مون کي ۽ جيڪي مون ساڻ آھن تن کي الله ھلاڪ ڪري يا الله اسان تي ٻاجھ ڪري ته (هر صورت ۾) ڪافرن کي ڏکوئيندڙ عذاب کان ڪير ڇڏائيندو؟'),
(5270, 67, 29, '(اي پيغمبر کين) چؤ ته اُھو ٻاجھارو آھي اُن تي ايمان آندوسون ۽ مٿس ڀروسو ڪيوسون، پوءِ سگھو ئي ڄاڻندؤ ته پڌريءَ گمراھي ۾ ڪير آھي.'),
(5271, 67, 30, '(اي پيغمبر کين) چؤ ته ڏٺو اٿو ڇا ته جيڪڏھن اوھان جو پاڻي ھيٺ پيھي وڃي ته اوھان وٽ وھندڙ پاڻي ڪير آڻيندو؟');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'نٓ قَلَم جو قسم آھي ۽ اُن جو به جيڪي لکندا آھن.'),
(5273, 68, 2, 'ته تون پنھنجي پالڻھار جي فضل سان ڪو چريو نه آھين.'),
(5274, 68, 3, '۽ بيشڪ تولاءِ اڻ کٽ ثواب آھي.'),
(5275, 68, 4, '۽ بيشڪ تون وڏي خُلق تي آھين.'),
(5276, 68, 5, 'پوءِ سگھوئي ڏسندين ۽ اُھي (به) ڏسندا.'),
(5277, 68, 6, 'ته اوھان مان ڪير چريو آھي.'),
(5278, 68, 7, 'بيشڪ تنھنجو پالڻھار ئي انھيءَ کي تمام چڱي طرح ڄاڻندڙ آھي جيڪو سندس واٽ کان گمراھ ٿيو، ۽ اھو سنئين واٽ ويندڙن کي به تمام چڱي طرح ڄاڻندڙ آھي.'),
(5279, 68, 8, 'تنھنڪري ڪوڙ ڀائيندڙن جو چيو نه مڃ.'),
(5280, 68, 9, 'اُھي گھرندا آھن ته جيڪڏھن نرمي ڪرين ته اُھي به نرمي ڪن.'),
(5281, 68, 10, '۽ تون سڀڪنھن وڏي قسميءَ خوار جو چيو نه مڃ.'),
(5282, 68, 11, '(جو) گھڻو عيب لائيندڙ چغلي ھڻندڙ گھمندڙ.'),
(5283, 68, 12, 'خير کان جھليندڙ حد کان لنگھيل گنھگار.'),
(5284, 68, 13, 'بدعادتو اِن سڀنيءَ کانپوءِ (حرامي (به) ھجي.'),
(5285, 68, 14, 'انھي ڪري جو مال ۽ اولاد وارو آھي.'),
(5286, 68, 15, 'جڏھن وٽس اسان جون آيتون پڙھجن (تڏھن) چوي ته اڳين جون آکاڻيون آھن.'),
(5287, 68, 16, 'سگھوئي کيس سونڍ تي داغ ڏينداسون.'),
(5288, 68, 17, 'بيشڪ اسان ھنن کي (اھڙي طرح) آزمايو جھڙي طرح باغ وارن کي آزمايو ھوسون، جڏھن قسم کنيائون ته سويرو (ئي) اُن پوک کي ضرور لُڻندا (سين).'),
(5289, 68, 18, '۽ اِنشاءَالله نه چيائون.'),
(5290, 68, 19, 'پوءِ تنھنجي پالڻھار (جي پار) کان اُن (باغ) تي ھڪ (اھڙي) بلا پلٽي ۽ اُھي (اڃا) سمھيل ئي ھوا.'),
(5291, 68, 20, 'جو (اُھو) ناڙ وانگر ٿي پيو.'),
(5292, 68, 21, 'پوءِ سويرو ھڪ ٻئي کي سڏڻ لڳا.'),
(5293, 68, 22, 'جيڪڏھن اوھين لاباري ڪرڻ وارا آھيو ته پوءِ پنھنجي پوک تي سويرو ھلو.'),
(5294, 68, 23, 'پوءِ اُھي ھليا ۽ ھڪ ٻئي کي ھوريان چوڻ لڳا.'),
(5295, 68, 24, 'ته (جئن) ھن (پوک) ۾ اڄ اوھان وٽ ڪو مسڪين اندر اچي نه سگھي.'),
(5296, 68, 25, '۽ (پنھنجي گمان ۾) بخل (جي نيت) تي سگھارا ٿي سويرو پھتا.'),
(5297, 68, 26, 'پوءِ جنھن مھل اُن (پوک) کي ڏٺائون (ته) چيائون ته بيشڪ اسين رستو ڀليل آھيون.'),
(5298, 68, 27, '(نه نه!) بلڪ اسين بي نصيب (ٿيل) آھيون.'),
(5299, 68, 28, 'سندن چڱي (مڙس) چيو ته اوھان کي نه چيو ھومِ ڇا ته الله جي پاڪائي ڇونه ٿا بيان ڪريو؟'),
(5300, 68, 29, 'چيائون ته (اسين) پنھنجي پالڻھار جي پاڪائي بيان ڪريون ٿا ۽ بيشڪ (انھي نيت ڪري) ظالم ھواسون.'),
(5301, 68, 30, 'پوءِ ھڪ ٻئي کي ملامت ڪندڙ ٿي ھڪڙن ٻـين ڏانھن مھاڙ ڪئي.'),
(5302, 68, 31, 'چوڻ لڳا ته ويل اسان لاءِ بيشڪ اسين حد کان لنگھندڙ ھواسون.'),
(5303, 68, 32, 'مانَ اسان جو پالڻھار اسان کي ھن کان ڀلو (ٻيو باغ) بدلو ڏئي بيشڪ اسين پنھنجي پالڻھار ڏانھن اُميد رکندڙ آھيون.'),
(5304, 68, 33, 'ائين ئي آفت (پوڻي) آھي، ۽ بيشڪ آخرت جو عذاب تمام وڏو آھي، ارمان! جيڪڏھن سمجھن ھا.'),
(5305, 68, 34, 'بيشڪ پرھيزگارن لاءِ سندن پالڻھار وٽ نعمت جا باغ آھن.'),
(5306, 68, 35, 'مسلمانن کي گنھگارن جھڙو ڪنداسون ڇا؟'),
(5307, 68, 36, 'اوھان کي ڇا ٿيو آھي؟ ڪھڙو ٺھراءُ ٿا ڪيو؟'),
(5308, 68, 37, 'اوھان وٽ ڪو ڪتاب آھي ڇا جنھن ۾ (ھيءَ ڳالھ) پڙھندا آھيو.'),
(5309, 68, 38, 'ته بيشڪ جيڪي اوھين پسند ڪندا آھيو سو آخرت ۾ اوھان لاءِ ھوندو.'),
(5310, 68, 39, 'اوھان جا اسان جي ذمي (ھن ڳالھ جا) ڪي پڪا انجام ٿيل آھن ڇا جو اُنھن جو قيامت تائين حُڪم ھلندو ته بيشڪ جيڪو ٺھراءُ ڪريو ٿا سو اوھان لاءِ آھي.'),
(5311, 68, 40, '(اي پيغمبر) مشرڪن کان پڇ ته انھن مان ڪير ھن (ڳالھ) جو ذمي وار آھي.'),
(5312, 68, 41, 'انھن (مشرڪن) کي (ھن ڳالھ ۾) ڪي شريڪ آھن ڇا؟ جيڪڏھن سچا آھن ته جڳائين ته پنھنجي شريڪن کي (مدد لاءِ) آڻين.'),
(5313, 68, 42, 'جنھن ڏينھن پنّي جي تجلّي ٿيندي ۽ اُنھن کي سجدي ڏانھن سڏبو پوءِ نه ڪري سگھندا.'),
(5314, 68, 43, 'انھن جون اکيون جھڪيون ھونديون. (۽) کين خواري وٺي ويندي، ۽ بيشڪ اُنھن کي سجدي ڏانھن سڏبو ھو حالانڪ اُھي چڱا ڀلا (به) ھوا.'),
(5315, 68, 44, 'پوءِ (اي پيغمبر) مون کي ۽ انھيءَ کي جو ھن (قرآن جي) ڳالھ کي ڪوڙ ڀائيندو آھي ڇڏي ڏي، (اسين) انھن کي درجي بدرجي (اُتان دوزخ ۾) ڇڪينداسون جتان اُھي نه ڄاڻندا.'),
(5316, 68, 45, 'انھن کي مُھلت ڏيان ٿو، بيشڪ منھنجي رِٿ پڪي آھي.'),
(5317, 68, 46, 'انھن کان ڪو اجورو گھرندو آھين ڇا جنھنڪري (ان) چٽيءَ کان ڳوري بار آھن.'),
(5318, 68, 47, '(يا) انھن وٽ ڪو ڳجھ (جو علم) آھي ڇا جو اھي لکندا آھن.'),
(5319, 68, 48, 'پوءِ (تون) پنھنجي پالڻھار جي حُڪم (اچڻ) تائين صبر ڪر ۽ مڇيءَ واري (يونس) وانگر نه ھُج، جڏھن دانھن ڪيائين ۽ اُھو ڏک ۾ ڀرجي ويو ھو.'),
(5320, 68, 49, 'جيڪڏھن کيس سندس پالڻھار جي طرفان رحمت نه پھچي ھا ته ضرور سُڃ ۾ اڇليو وڃي ھا ۽ اُھو بڇڙي حال ھجي ھا.'),
(5321, 68, 50, 'پوءِ سندس پالڻھار کيس نوازيو ۽ صالحن مان ڪيائينس.'),
(5322, 68, 51, '۽ بيشڪ ڪافر جڏھن قرآن ٻڌندا آھن (تڏھن) توکي پنھنجن (تکين) اکين سان ٿيڙڻ تي آھن ۽ چوندا آھن ته بيشڪ ھي (پيغمبر) چريو آھي.'),
(5323, 68, 52, '۽ (حقيقت ۾) ھي (قرآن) ته جھانن لاءِ نصيحت آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'قيامت.'),
(5325, 69, 2, 'ڇا آھي (اھا) قيامت.'),
(5326, 69, 3, '۽ ڪھڙي شيءِ توکي ڄاڻايو ته قيامت ڇا آھي؟'),
(5327, 69, 4, 'ثمود ۽ عاد (قوم) قيامت کي ڪُوڙ ڄاتو.'),
(5328, 69, 5, 'پوءِ ثمود (قوم) سي ته ھڪڙي رڙ سان ناس ڪيا ويا.'),
(5329, 69, 6, '۽ عاد سي ھڪ بيحد سخت واءُ سان ناس ڪيا ويا.'),
(5330, 69, 7, '(الله) اُن (واءُ) کي ست راتيون ۽ اٺ ڏينھن لڳولڳ مٿن کڙو ڪيو پوءِ قوم کي اُن (زمين) ۾ ڪريل ڏسين ھا ڄڻ ته اُھي ڀتي کجيءَ جا ٿُڙ آھن.'),
(5331, 69, 8, 'پوءِ انھن کي ڪجھ بچيل ڏسين ٿو ڇا؟'),
(5332, 69, 9, '۽ فرعون ۽ جيڪي اُن کان اڳ ھوا ۽ ناس ڪيل شھرن وارا (به) گناھ (جا ڪم) ڪري آيا.'),
(5333, 69, 10, '۽ پنھنجي پالڻھار جي رسول جي نافرماني ڪيائون تنھنڪري (الله) وڏي پڪڙ سان کين پڪڙيو.'),
(5334, 69, 11, 'بيشڪ جڏھن پاڻي حد کان لنگھي ويو (تڏھن) اسان اوھان کي ھلندڙ ٻيڙيءَ ۾ چاڙهيو.'),
(5335, 69, 12, 'ھن ڪري ته اُن (ڳالھ) کي اوھان لاءِ يادگيري ڪريون ۽ ان کي ڪو ياد رکڻ وارو ڪَنُّ ياد رکي.'),
(5336, 69, 13, 'پوءِ جڏھن صُور ۾ ھڪڙي ڦوڪ ڦوڪبي.'),
(5337, 69, 14, '۽ زمين ۽ جبل کنيا ويندا پوءِ ھڪ ڀيرو انھن کي ڪٽيو ويندو.'),
(5338, 69, 15, 'پوءِ اُن ڏينھن قيامت قائم ٿيندي.'),
(5339, 69, 16, '۽ آسمان ڦاٽندو پوءِ اُھو اُن ڏينھن ڍلو ٿيل ھوندو.'),
(5340, 69, 17, '۽ ملائڪ آسمان جي ڪنارن تي ھوندا، ۽ تنھنجي پالڻھار جو تخت اُن ڏينھن اٺ ملائڪ پنھنجي مٿان کڻندا.'),
(5341, 69, 18, 'اُن ڏينھن اوھان کي (الله جي) آڏو ڪيو ويندو اوھان جي ڪا ڳالھ ڳجھي لڪي نه رھندي.'),
(5342, 69, 19, 'پوءِ جنھن کي سندس اعمالنامون سندس سڄي ھٿ ۾ ڏنو ويو سو چوندو ته وٺو منھنجو اعمالنامو پڙھو.'),
(5343, 69, 20, 'بيشڪ مون يقين رکيو ھو ته آءٌ پنھنجي حساب کي پھچندس.'),
(5344, 69, 21, 'اُھو چڱي گذران ۾ ھوندو.'),
(5345, 69, 22, 'مٿاھين بھشت ۾.'),
(5346, 69, 23, 'جنھن جا ميوا ويجھا ھوندا.'),
(5347, 69, 24, '(چئبن ته) وڻنديءَ تي کائو ۽ پيئو انھيءَ ڪري جو گذريل ڏينھن ۾ اڳي موڪليو ھيؤ.'),
(5348, 69, 25, '۽ جنھن کي سندس اعمالنامون سندس کٻي ھٿ ۾ ڏنو ويو، سو چوندو ھاءِ ارمان جيڪر منھنجو اعمالنامون مون کي نه ملي ھا.'),
(5349, 69, 26, 'نه ڄاڻان ھا ته منھنجو حساب ڇا آھي؟'),
(5350, 69, 27, 'ھاءِ ارمان جيڪر موت نبيريندڙ ھجي ھا.'),
(5351, 69, 28, 'منھنجي مال مون کي (ڪو) فائدو نه ڏنو.'),
(5352, 69, 29, 'مون کان منھنجي بادشاھت چٽ ٿي.'),
(5353, 69, 30, '(ملائڪن کي چئبو ته) پڪڙيوس پوءِ ڳچيءَ ۾ ڳٽ ٻڌوس.'),
(5354, 69, 31, 'وري دوزخ ۾ وجھوس.'),
(5355, 69, 32, 'وري اھڙي زنجير ۾ قابو ڪريوس جنھن جي ڊيگھ ستر گز ھجي.'),
(5356, 69, 33, 'ڇو ته اُھو (ماڻھو) الله وڏي کي نه مڃيندو ھو.'),
(5357, 69, 34, '۽ نڪي (ٻـين کي) مسڪينن جي کارائڻ تي تاڪيد ڪندو ھو.'),
(5358, 69, 35, 'تنھنڪري اُن جو اڄ ھت ڪو دوست ڪونھي.'),
(5359, 69, 36, '۽ نڪي اُن جو ڦٽن جي روڳ کانسواءِ ٻيو ڪو کاڌو آھي.'),
(5360, 69, 37, '(جو) ڏوھارين کانسواءِ (ٻيو) ڪو ان کي نه کارائيندو.'),
(5361, 69, 38, 'پوءِ جيڪي ڏسو ٿا اُن جو قسم کڻان ٿو.'),
(5362, 69, 39, '۽ جيڪي نه ٿا ڏسو تنھن جو (به).'),
(5363, 69, 40, 'ته بيشڪ ھي (قرآن) ھڪ سڳوري قاصد جو سنيھو آھي.'),
(5364, 69, 41, '۽ اُھو ڪنھن شاعر جو ڪلام نه آھي، اوھين ٿورڙو يقين رکو ٿا.'),
(5365, 69, 42, '۽ نڪي ڪنھن ڳجھ بڪندڙ جو ڪلام آھي، ٿوري نصيحت وٺو ٿا.'),
(5366, 69, 43, '(ھيءُ) جھانن جي پالڻھار کان نازل ٿيل آھي.'),
(5367, 69, 44, '۽ جيڪڏھن (پيغمبر) اسان تي ڪي ڳالھيون (پاڻون) ٺاھي ھا.'),
(5368, 69, 45, 'ته ضرور سڄي ھٿ کان کيس پڪڙيون ھا.'),
(5369, 69, 46, 'وري سندس دل جي رڳ وڍي ڇڏيون ھا.'),
(5370, 69, 47, 'پوءِ اوھان مان ڪو ھڪڙو (اسان جي عذاب کي) کانئس جھلڻ وارو نه آھي.'),
(5371, 69, 48, '۽ بيشڪ ھي (قرآن) پرھيزگارن لاءِ نصيحت آھي.'),
(5372, 69, 49, '۽ بيشڪ اسين ڄاڻون ٿا ته ڪي اوھان مان ڪوڙ ڀانئيندڙ آھن.'),
(5373, 69, 50, '۽ بيشڪ ھي (قرآن) ڪافرن لاءِ ارمان (جو سبب) آھي.'),
(5374, 69, 51, '۽ بيشڪ ھي (قرآن) پورو سَچو آھي.'),
(5375, 69, 52, 'تنھنڪري تون پنھنجي وڏي پالڻھار جي نالي کي پاڪائيءَ سان ياد ڪر.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'ھڪ سوال ڪندڙ ڪافرن تي پوندڙ عذاب جو سوال ڪيو.'),
(5377, 70, 2, 'جنھن کي ڪو ھٽائڻ وارو نه آھي.'),
(5378, 70, 3, 'جو وڏن مرتبن واري الله جي طرف کان (پوندڙ) آھي.'),
(5379, 70, 4, 'جنھن ڏينھن جي ڊيگھ پنجاھ ھزار ورھيه آھي تنھن ۾ ملائڪ ۽ جبرئيل الله ڏانھن چڙھندا.'),
(5380, 70, 5, 'تنھنڪري تون چڱو صبر ڪر.'),
(5381, 70, 6, 'ڇوته ڪافر انھيءَ (ڏينھن) کي پري ڏسن ٿا.'),
(5382, 70, 7, '۽ اسين اُن کي ويجھو ٿا ڏسون.'),
(5383, 70, 8, 'جنھن ڏينھن آسمان پگھريل ٽامي وانگر ٿيندو.'),
(5384, 70, 9, '۽ جبل رڱيل اُن جان ٿيندا.'),
(5385, 70, 10, '۽ ڪو مائٽ ڪنھن ٻئي مائٽ کان نه پڇندو.'),
(5386, 70, 11, '(توڙي جو) اُھي کين ڏسڻ ۾ ايندا، گنھگار گھرندو ته جيڪر اُن ڏينھن جي عذاب جي بدران پنھنجا پُٽ بدلو ڏئي.'),
(5387, 70, 12, '۽ پنھنجي زال ۽ پنھنجو ڀاءُ (به).'),
(5388, 70, 13, '۽ پنھنجو اُھو سارو قبيلو (به) جنھن کيس ٽڪايو ٿي.'),
(5389, 70, 14, '۽ اُھو (به) جيڪي سڀئي زمين ۾ آھي پوءِ سندس اھو (بدلو ڏيڻ) ڇڏائيس.'),
(5390, 70, 15, 'ائين نه آھي، بيشڪ اِھو (دوزخ) الانبن واري باھ آھي.'),
(5391, 70, 16, 'مَٿي جي کَل کي ڇڪيندڙ آھي.'),
(5392, 70, 17, 'جيڪو (خدا کان) پٺيرو ٿيو ۽ منھن موڙيائين تنھنکي سڏي ٿي.'),
(5393, 70, 18, '۽ (مال) گڏ ڪيائين پوءِ سانڀيائين.'),
(5394, 70, 19, 'بيشڪ ماڻھو حرص وارو پيدا ٿيل آھي.'),
(5395, 70, 20, 'جڏھن (ڪو) ڏُک پھچندو اٿس (ته) دانھون ڪندو آھي.'),
(5396, 70, 21, '۽ جڏھن ڪو سُک پھچندو اٿس (ته) ڪنجوس ٿيندو آھي.'),
(5397, 70, 22, 'پر (اُھي) نمازي (نه).'),
(5398, 70, 23, 'جيڪي پنھنجي نماز تي ھميشه پورا آھن.'),
(5399, 70, 24, '۽ اُھي (به) جن جي مالن ۾ (خداءَ جو) حصّو مقرر ٿيل آھي.'),
(5400, 70, 25, 'سوال ڪندڙ ۽ نه سوال ڪندڙن لاءِ.'),
(5401, 70, 26, '۽ اُھي جيڪي قيامت جي ڏينھن جو يقين رکندا آھن.'),
(5402, 70, 27, '۽ اُھي جيڪي پنھنجي پالڻھار جي عذاب کان ڊڄڻ وارا آھن.'),
(5403, 70, 28, 'بيشڪ سندس پالڻھار جو عذاب (اھڙو آھي) جنھن کان بچي نه سگھبو.'),
(5404, 70, 29, '۽ اُھي جيڪي پنھنجين اگھڙن کي نگاھ رکڻ وارا آھن.'),
(5405, 70, 30, 'پنھنجين زالن يا پنھنجين ٻانھين کانسواءِ پوءِ بيشڪ اُھي ملامت وارا نه آھن.'),
(5406, 70, 31, 'پوءِ جيڪي اُن کانسواءِ (ٻي ڳالھ) طلبيندا سي ئي حد کان لنگھڻ وارا آھن.'),
(5407, 70, 32, '۽ اُھي جيڪي پنھنجين امانتن کي ۽ پنھنجن انجامن کي پوري ڪرڻ وارا آھن.'),
(5408, 70, 33, '۽ اُھي جيڪي پنھنجين شاھدين تي قائم آھن.'),
(5409, 70, 34, '۽ اُھي جيڪي پنھنجي نماز جي سنڀال ڪندڙ آھن.'),
(5410, 70, 35, 'اُھي (بھشت جي) باغن ۾ عزت سان رھندا.'),
(5411, 70, 36, 'پوءِ ڪافرن کي ڇا (ٿيو) آھي جو توڏانھن ڪنڌ کڻي ڊوڙندا آھن.'),
(5412, 70, 37, 'سڄي پاسي کان ۽ کٻي پاسي کان ٽوليون ٽوليون ٿي.'),
(5413, 70, 38, 'منجھائن سڀڪو شخص نعمت واري بھشت جي گھڙڻ جي طمع ڪري ٿو ڇا؟'),
(5414, 70, 39, 'ائين نه آھي، بيشڪ اسان اُنھن کي اُنھيءَ شيءِ مان پيدا ڪيو آھي جنھنجي خبر اٿن.'),
(5415, 70, 40, 'پوءِ اُڀرندن ۽ اُلھندن جي پالڻھار جو قسم کڻان ٿو ته بيشڪ اسين وسوارا آھيون.'),
(5416, 70, 41, 'ھن (ڳالھ) تي جو اُنھن کان چڱا (ٻيا) مٽائي آڻيون ۽ اسين ڪي عاجز ٿيڻ وارا نه آھيون.'),
(5417, 70, 42, 'پوءِ (اي پيغمبر) انھن کي (ايسين) ٺاھ ٺاھيندو ۽ راند ڪندو ڇڏي ڏي جيسين اُھي پنھنجو اُھو ڏينھن ڏسن جنھن جو کين انجام ڏجي ٿو.'),
(5418, 70, 43, 'جنھن ڏينھن قبرن مان ڊوڙندا نڪرندا ڄڻ ته اُھي ڪنھن جھنڊي ڏانھن ڊڪن ٿا.'),
(5419, 70, 44, 'ان وقت سندن اکيون جھڪيون ھونديون انھن کي خواري وٺي ويندي، اِھو اُھو ڏينھن آھي جنھن جو کين انجام ڏنو ٿي ويو.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'بيشڪ اسان نوح کي سندس قوم ڏانھن موڪليو ته پنھنجي قوم کي انھن وٽ ڏکوئيندڙ عذاب جي پھچڻ کان اڳ ۾ ڊيڄار.'),
(5421, 71, 2, 'چيائين ته اي منھنجي قوم بيشڪ آءٌ اوھان لاءِ پڌرو ڊيڄاريندڙ آھيان.'),
(5422, 71, 3, 'ته الله جي عبادت ڪريو ۽ کانئس ڊڄو ۽ منھنجو چيو مڃيو.'),
(5423, 71, 4, 'ته اوھان جا گناھ اوھان کي بخشي ۽ اوھان کي ٺھرايل وقت تائين ڍر ڏئي، بيشڪ الله جو ٺھرايل وقت ايندو (تڏھن) ان کي ڍر نه ڏبي، جيڪڏھن ڄاڻندا آھيو.'),
(5424, 71, 5, '(نوح) چيو ته اي منھنجا پالڻھار بيشڪ آءٌ پنھنجي قوم کي رات ۽ ڏينھن سڏيندو رھيس.'),
(5425, 71, 6, 'پوءِ منھنجي سڏڻ سندن حق ۾ ڀڄڻ کانسواءِ (ٻيو) ڪي نه وڌايو.'),
(5426, 71, 7, '۽ بيشڪ مون جڏھن به انھن کي سڏيو ته تون انھن کي بخشين (تڏھن) پنھنجيون آڱريون پنھنجن ڪَنن ۾ وڌائون ۽ پنھنجا ڪپڙا پاڻ تي ويڙھيائون ۽ (ڪُفر تي) قائم رھيا ۽ تمام گھڻو ھٺ ڪيائون.'),
(5427, 71, 8, 'وري بيشڪ مون اُنھن کي ڏاڍيان سڏيو.'),
(5428, 71, 9, 'وري بيشڪ مون انھن کي ظاھر ظھور چيو ۽ انھن کي ڳجھي طرح (به) چيم.'),
(5429, 71, 10, 'پوءِ چيم ته پنھنجي پالڻھار کان بخشش گھرو، ڇوته اُھو بخشڻھار آھي.'),
(5430, 71, 11, 'ته گھڻو وسندڙ مينھن اوھان تي لڳولڳ وسائي.'),
(5431, 71, 12, '۽ اوھان کي مال ۽ پٽ گھڻا ڏئي ۽ اوھان کي باغ ڏئي ۽ اوھان کي نَھرون بڻائي ڏي.'),
(5432, 71, 13, 'اوھان کي ڇا (ٿيو) آھي جو الله جي وڏائي جو ويساھ نه ٿا رکو.'),
(5433, 71, 14, 'حالانڪ اوھان کي قسمن قسمن ۾ بڻايائين.'),
(5434, 71, 15, 'نه ڏسندا آھيو ڇا ته الله ڪيئن ستن آسمانن کي طبقا طبقا بڻايو.'),
(5435, 71, 16, '۽ انھن ۾ چنڊ کي روشن ڪيائين ۽ سج کي (چمڪندڙ) ڏيئو ڪيائين.'),
(5436, 71, 17, '۽ الله اوھان کي زمين منجھان ھڪڙي ڄمائڻ طرح ڄمايو.'),
(5437, 71, 18, 'وري اوھان کي زمين ۾ موٽائي نيندو ۽ وري اوھان کي پوري طرح ٻاھر ڪڍندو.'),
(5438, 71, 19, '۽ الله اوھان لاءِ زمين کي وڇاڻو بڻايو.'),
(5439, 71, 20, 'ته اُن ۾ ويڪرين واٽن سان ھلو.'),
(5440, 71, 21, 'نوح چيو ته اي منھنجا پالڻھار بيشڪ انھن منھنجو چيو نه مڃيو ۽ اُنھن جي پويان لڳا جن جي حق ۾ سندن مال ۽ سندن اولاد ڇيئي کانسواءِ (ٻيو) ڪي نه وڌايو.'),
(5441, 71, 22, '۽ انھن تمام وڏو فريب پکيڙيو.'),
(5442, 71, 23, '۽ چيائون ته پنھنجن بُتن کي ڪڏھن نه ڇڏيو ۽ نڪي وَد ۽ نڪي سُوَاع ۽ نڪي يَغُوث ۽ يَعُوق ۽ نَسۡر کي ڇڏيو.'),
(5443, 71, 24, '۽ بيشڪ گھڻن کي گمراھ ڪيائون، ۽ (اي الله) ظالمن کي گمراھيءَ کانسواءِ (ٻيو) ڪي وڌيڪ نه ڏي.'),
(5444, 71, 25, 'انھن کي سندن گناھن جي سبب ڪري ٻوڙيو ويو وري انھن کي باھ ۾ داخل ڪيو ويو، پوءِ الله کانسواءِ (ٻيو) ڪو پاڻ لاءِ مددگار نه لڌائون.'),
(5445, 71, 26, '۽ نُوح چيو ته اي منھنجا پالڻھار ڪافرن مان ڪو زمين تي رھندڙ نه ڇڏ.'),
(5446, 71, 27, 'بيشڪ تون جيڪڏھن انھن کي ڇڏيندين ته تنھنجن ٻانھن کي گمراھ ڪندا ۽ اُھي بدڪار مُنڪر کانسواءِ (ٻيو) نه ڄڻيندا.'),
(5447, 71, 28, 'اي منھنجا پالڻھار مون کي ۽ منھنجي ماءُ پيءُ کي بخش ۽ جيڪو منھنجي گھر ۾ مؤمن ٿي اچي تنھن کي ۽ سڀني مؤمنن ۽ مؤمنياڻين کي (بخش)، ۽ ظالمن کي ھلاڪيءَ کانسواءِ (ٻيو) ڪي وڌيڪ نه ڏي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, '(اي پيغمبر) چؤ ته مون ڏانھن (ھن ڳالھ جو) وحي موڪليو ويو ته جِنّن مان ھڪ ٽوليءَ (قرآن) ٻڌو پوءِ چيائون ته بيشڪ اسان ھڪ عجيب قرآن ٻڌو.'),
(5449, 72, 2, 'جو سڌي رستي ڏانھن ڏس ڏئي ٿو تنھنڪري ان تي ايمان آندوسون، ۽ پنھنجي پالڻھار سان ڪنھن ھڪڙي کي شريڪ ھرگز نه ڪنداسون.'),
(5450, 72, 3, '۽ (بيان ڪرڻ لڳا) ته اسان جي پالڻھار جو شان تمام وڏو آھي نڪي ڪو زال ۽ اولاد رکيو اٿس.'),
(5451, 72, 4, '۽ ھي ته الله جي نسبت ۾ اسان مان بيوقوف ڪوڙو ٺاھ ٺاھيو ٿي.'),
(5452, 72, 5, '۽ ھي ته اسان ڀانيو ھو ته اسان ۽ جنّ ڪڏھن الله تي ڪوڙ نه چوندا.'),
(5453, 72, 6, '۽ ھي ته ماڻھن مان ڪيترا مڙس جنّن جي مڙسن جي سام پوندا ھوا پوءِ ماڻھن جنّن (جي حق) ۾ (ھٿئون) وڏائي وڌائي.'),
(5454, 72, 7, '۽ ھي ته (اي جنّو) ماڻھن اھڙو گمان ڪيو ھو جھڙو اوھان گمان ڪيو ھو ته الله ھرگز ڪنھن کي پيغمبر ڪري نه موڪليندو.'),
(5455, 72, 8, '۽ ھي ته اسان آسمان کي جاچيو پوءِ ڏٺوسونس ته سخت چوڪيدارن ۽ اُلانبن سان ڀرجي ويو آھي.'),
(5456, 72, 9, '۽ ھي ته اسان (اڳي) آسمان جي (گھڻن) رستن تي (ملائڪن جي گفتگو) ٻڌڻ لاءِ وھندا ھواسين، پوءِ جيڪو ھاڻي ڪن ڏيندو آھي سو پاڻ لاءِ تيار ٿيل اُلانبي وارو تارو ڏسندو آھي.'),
(5457, 72, 10, '۽ ھي ته اسين نه ٿا ڄاڻون ته جيڪي زمين ۾ آھن تن لاءِ ڪنھن مدائي جو ارادو ڪيو ويو آھي يا انھن لاءِ سندن پالڻھار ڪو چڱائي جو ارادو ڪيو آھي.'),
(5458, 72, 11, '۽ ھي ته ڪي اسان مان سڌريل آھن ۽ اسان مان ڪي اُن کانسواءِ (به) آھن، (اسين) مختلف ٽوليون ٽوليون ھواسون.'),
(5459, 72, 12, '۽ ھي ته اسان ڄاتو ته زمين ۾ الله کي ڪڏھن به ٿَڪائي نه سگھنداسون ۽ نڪي ڀڄي ڪڏھن ٿَڪائي سگھنداسونس.'),
(5460, 72, 13, '۽ ھي ته جڏھن اسان ھدايت (جي ڳالھ) ٻڌي (تڏھن) اُن تي ايمان آندوسون، پوءِ جيڪو پنھنجي پالڻھار تي ايمان آڻيندو سو نڪي ڪنھن نقصان کان ۽ نڪي ڪنھن ظلم کان ڊڄندو.'),
(5461, 72, 14, '۽ ھي ته اسان مان ڪي مسلمان آھن ۽ اسان مان ڪي گنھگار آھن، پوءِ جيڪي مُسلمان ٿيا تن سِڌي واٽ جي نيت ڪئي.'),
(5462, 72, 15, '۽ گنھگار سي دوزخ جي ڪاٺيون ھوندا.'),
(5463, 72, 16, '۽ (اي پيغمبر) ھيءَ (به چؤ ته مون ڏانھن موڪليو ويو آھي) ته جيڪڏھن ماڻھو سڌيءَ واٽ تي بيھڪ وٺن ھا ته ضرور انھن (جي پوکن) کي گھڻو پاڻي پياريون ھا.'),
(5464, 72, 17, 'ته اُن ۾ کين پرکيون ۽ جيڪو پنھنجي پالڻھار جي ياد ڪرڻ کان مُنھن موڙيندو تنھن کي (الله) سخت عذاب ۾ ڦاسائيندو.'),
(5465, 72, 18, '۽ ھي ته سڀ مسجدون (خاص) الله جون آھن تنھنڪري الله سان گڏ ڪنھن جي عبادت نه ڪريو.'),
(5466, 72, 19, '۽ ھي ته جنھن مھل الله جو ٻانھو (ﷴ ﷺ) بيٺو ته سندس عبادت ڪري تنھن مھل جنّ مٿس ڳاھٽ ڪرڻ تي ھوا.'),
(5467, 72, 20, 'چؤ ته آءٌ رڳو پنھنجي پالڻھار جي عبادت ڪريان ٿو ۽ ڪنھن ھڪڙي کي ساڻس شريڪ نه ٿو ڪريان.'),
(5468, 72, 21, 'چؤ ته بيشڪ آءٌ اوھان کي ڏک پھچائڻ ۽ سنئي رستي تي آڻڻ جو وس نٿو رکان.'),
(5469, 72, 22, 'چؤ ته بيشڪ مون کي الله (جي عذاب) کان ڪوبه نه بچائيندو، ۽ اُن کانسواءِ ڪا پناھ ڪڏھن نه لھندس.'),
(5470, 72, 23, 'مگر الله جي پار کان خبر پھچائڻي آھي ۽ سندس پيغام پھچائڻا آھن ۽ جيڪو الله ۽ سندس پيغمبر جي نافرماني ڪندو تنھن لاءِ بيشڪ دوزخ جي باھ آھي جنھن ۾ سدائين رھندا.'),
(5471, 72, 24, '(ايستائين غفلت ۾ رھندا) جيسين ڪه جيڪو اُنھن کي انجام ڏجي ٿو سو ڏسن پوءِ ڄاڻندا ته مددگار جي ڪري وڌيڪ ھيڻو ۽ ڳاڻيٽي جي ڪري تمام ٿورو ڪير آھي.'),
(5472, 72, 25, '(کين) چؤ ته آءٌ نه ٿو ڄاڻان ته جنھنجو اوھان کي انجام ڏجي ٿو سو (الائي) ويجھو آھي يا منھنجو پالڻھار اُن جي ڪا (ٻي) مُدت مُقرر ڪندو.'),
(5473, 72, 26, '(اُھو) ڳُجھ جو ڄاڻندڙ آھي پوءِ پنھنجي ڳجھ تي ڪنھن ھڪڙي کي واقف نه ڪندو آھي.'),
(5474, 72, 27, 'سواءِ انھيءَ جي جو جنھن پيغمبر کي پسند ڪيائين بيشڪ الله سندس اڳتان ۽ سندس پوئتان نگھبان (ملائڪ) موڪليندو آھي.'),
(5475, 72, 28, '(ھن لاءِ) ته ڄاڻي ته پنھنجي پالڻھار جا حُڪم سچ پچ پھچايا اٿن ۽ جيڪي وٽن آھي تنھنکي الله ھر طرح پنھنجي وس رکيو آھي ۽ سڀڪنھن شيءَ جو ڳاڻيٽو ڳڻي ڇڏيو اٿس.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'اي (پاڻ تي) ڪپڙي ويڙھڻ ورا مڙس.'),
(5477, 73, 2, 'رات جو کڙو ٿيءُ پر ٿورو.'),
(5478, 73, 3, 'رات جو اڌ يا اڌ رات کان ٿورو گھٽاءِ.'),
(5479, 73, 4, 'يا اڌ رات تي وڌاءِ ۽ قرآن ٺاھي ٺاھي پڙھ.'),
(5480, 73, 5, 'بيشڪ اسين توتي ھڪ ڳرو حُڪم نازل ڪنداسين.'),
(5481, 73, 6, 'بيشڪ رات جو اُٿڻ (نفس جي) جھڪي ڪرڻ ۾ تمام سخت آھي ۽ لفظن (چوڻ) ۾ تمام پڪو آھي.'),
(5482, 73, 7, 'بيشڪ توکي ڏينھن ۾ گھڻو ڌنڌو آھي.'),
(5483, 73, 8, '۽ پنھنجي پالڻھار جو نالو ياد ڪر ۽ سڀني پاسن کان ھڪ طرفو ٿي چڱي طرح ڏانھس متوجه ٿيءُ.'),
(5484, 73, 9, 'جو اوڀر ۽ اولھ جو پالڻھار آھي ان کانسواءِ ٻيو ڪو عبادت جو لائق نه آھي تنھنڪري تون کيس ڀرجھلو ڪري وٺ.'),
(5485, 73, 10, '۽ (ڪافر) جيڪي چوندا آھن تنھن تي صبر ڪر ۽ چڱي طرح انھن کي ڇڏي ڏي.'),
(5486, 73, 11, '۽ مون کي ۽ جيڪي ڪوڙ ڀائيندڙ آسودا آھن تن کي ڇڏي ڏي ۽ اُنھن کي ٿوري مُھلت ڏي.'),
(5487, 73, 12, 'بيشڪ اسان وٽ (ڳرا) نيل ۽ ٻاريل باھ آھي.'),
(5488, 73, 13, '۽ نڙي گھٽيندڙ طعام ۽ ڏکوئيندڙ عذاب آھي.'),
(5489, 73, 14, 'جنھن ڏينھن زمين ۽ جبل ڌٻندا ۽ جبل واريءَ جا ڍڳ ھاريل ٿيندا.'),
(5490, 73, 15, '(اي ماڻھو) بيشڪ اسان اوھان ڏانھن ھڪ پيغمبر اوھان تي شاھدي ڏيندڙ موڪليو جھڙي طرح فرعون ڏانھن (موسى) پيغمبر (ڪري) موڪليوسون.'),
(5491, 73, 16, 'پوءِ فرعون اُن پيغمبر جي نافرماني ڪئي تنھنڪري اُن کي سخت پڪڙ ۾ پڪڙيوسون.'),
(5492, 73, 17, 'پوءِ جيڪڏھن (اوھين) ڪافر رھندؤ ته اھڙي ڏينھن کان ڪيئن بچندو جيڪو ٻارن کي ٻڍو ڪندو.'),
(5493, 73, 18, 'اُن ڏينھن ۾ آسمان ڦاٽندو، الله جو انجام (ضرور پورو) ٿيڻو آھي.'),
(5494, 73, 19, 'بيشڪ ھيءَ نصيحت آھي، پوءِ جيڪو گھري سو پنھنجي پالڻھار ڏانھن واٽ وٺي.'),
(5495, 73, 20, 'بيشڪ تنھنجو پالڻھار ڄاڻندو آھي ته تون ٻن ٽھائن رات جي ويجھو اُٿندڙ آھين ۽ اڌ رات جو ۽ ٽھائي رات جي ۽ جيڪي تو ساڻ آھن تن مان به ھڪ ٽولي (اٿندي آھي)، ۽ الله رات ۽ ڏينھن جو اندازو ڪندو آھي، الله ڄاتو ته اوھين رات جو اُٿڻ پورو ڪري نه ٿا سگھو تنھنڪري اوھان تي (ٻاجھ سان) موٽيو پوءِ جيترو آسان ٿي سگھي سو قرآن مان پڙھو، الله ڄاڻي چڪو ته اوھان مان ڪي بيمار ٿيندا ۽ ٻيا جي زمين ۾ مسافري ڪن ٿا الله جي فضل مان (روزيءَ جي) طلب ڪن ٿا ۽ ٻيا الله جي واٽ ۾ (ڪافرن سان) وڙھن ٿا، تنھنڪري جيترو قرآن مان آسان ٿي سگھي سو پڙھو ۽ نماز کي قائم رکو ۽ زڪوٰة ڏيو ۽ الله کي چڱيءَ طرح قرض ڏيو، ۽ جيڪي پاڻ لاءِ چڱائي مان اڳي موڪليندؤ تنھن کي الله وٽ وڌيڪ چڱو ۽ اجر ڪري تمام وڏو لھندؤ، ۽ الله کان بخشش گھرو، ڇوته الله بخشڻھار مھربان آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'اي (پاڻتي) ڪپڙي ويڙھڻ وارا مڙس.'),
(5497, 74, 2, 'کڙو ٿي پوءِ ڊيڄار.'),
(5498, 74, 3, '۽ پنھنجي پالڻھار جي وڏائي بيان ڪر.'),
(5499, 74, 4, '۽ پنھنجا ڪپڙا پاڪ رک.'),
(5500, 74, 5, '۽ پليتي کان پري رھ.'),
(5501, 74, 6, '۽ (اھڙو) احسان نه ڪر جو زيادہ عوض گھرين.'),
(5502, 74, 7, '۽ پنھنجي پالڻھار (جي حُڪم) لاءِ صبر اختيار ڪر.'),
(5503, 74, 8, 'پوءِ جڏھن صُور ۾ ڦوڪبو.'),
(5504, 74, 9, 'تڏھن اُن ڏينھن اوکو وقت ٿيندو.'),
(5505, 74, 10, 'ڪافرن تي اڻ سکيو (وقت) ھوندو.'),
(5506, 74, 11, 'مون کي ۽ جنھن کي اڪيلو پيدا ڪيم تنھن کي ڇڏي ڏي.'),
(5507, 74, 12, '۽ اُن کي گھڻو مال ڏنم.'),
(5508, 74, 13, '۽ (مجلسن ۾) حاضر ٿيڻ وارا پُٽ (به ڏنم).'),
(5509, 74, 14, '۽ ان کي پوري طرح وڌايم.'),
(5510, 74, 15, 'وري (به) لالچ رکندو آھي ته (کيس) وڌيڪ ڏيان.'),
(5511, 74, 16, 'ائين نه آھي، بيشڪ اُھو اسان جي آيتن سان جھڳڙو ڪندڙ آھي.'),
(5512, 74, 17, 'جلد اُن کي وڏي سختي سان تڪليف ڏيندس.'),
(5513, 74, 18, 'بيشڪ اُن دل ۾ سوچ ڪئي ۽ ٺھراءُ ڪيو.'),
(5514, 74, 19, 'پوءِ لعنت ھجيس شال ڪيئن ٺھراء ڪيائين.'),
(5515, 74, 20, 'وري به لعنت ھجيس شال ڪيئن ٺھراءُ ڪيائين.'),
(5516, 74, 21, 'وري (غور ڪري) ڏٺائين.'),
(5517, 74, 22, 'وري منھن ۾ شور وڌائين ۽ گھنڊ پاتائين.'),
(5518, 74, 23, 'وري پٺيرو ٿيو ۽ وڏائي ڪيائين.'),
(5519, 74, 24, 'پوءِ چيائين ته ھي (قرآن) رڳو جادو آھي جو (جادوگرن کان) ھليو اچي ٿو.'),
(5520, 74, 25, 'ھي (قرآن) رڳو ماڻھوءَ جو ڪلام آھي.'),
(5521, 74, 26, 'جلد اُن کي دوزخ ۾ وجھندس.'),
(5522, 74, 27, '۽ توکي ڪھڙي شيءَ ڄاڻايو ته دوزخ ڇا آھي.'),
(5523, 74, 28, 'اُھو نڪي ڪجھ (گوشت) رھائي ٿو ۽ نڪي (ھڏا) ڇڏي ٿو.'),
(5524, 74, 29, 'ماڻھن کي (ساڙي) ڪارو ڪندڙ آھي.'),
(5525, 74, 30, 'اُن تي اُڻويہ ڄڻا دربان آھن.'),
(5526, 74, 31, '۽ دوزخ جا دربان رڳو ملائڪ ڪيا اٿون، ۽ سندن ڳاڻيٽو رڳو ڪافرن لاءِ پرک ڪئي اٿون ته ڪتاب وارا يقين ڪن ۽ ايمان وارا پنھنجي ايمان ۾ وڌن ۽ ڪتاب وارا ۽ مؤمن شڪ ۾ نه پون ۽ ته جن جي دلين ۾ (منافقيءَ جي) بيماري آھي سي ۽ ڪافر چون ته ھن مثال ڏيڻ ۾ الله جو ڇا مطلب آھي، اھڙيءَ طرح الله جنھن کي گھري (تنھن کي) ڀلائيندو آھي ۽ جنھن کي گھري (تنھن کي) ھدايت ڪندو آھي، ۽ تنھنجي پالڻھار جا لشڪر اُن کانسواءِ (ٻيو) ڪونه ڄاڻندو آھي، ۽ ھيءَ (دوزخ) سڀ ڪنھن ماڻھوءَ لاءِ رڳو نصيحت آھي.'),
(5527, 74, 32, 'سچ چوان ٿو چنڊ جو قسم آھي.'),
(5528, 74, 33, '۽ رات جو جڏھن پٺ ڦيري.'),
(5529, 74, 34, '۽ صبح جو (قسم آھي) جڏھن روشن ٿئي.'),
(5530, 74, 35, 'ته بيشڪ دوزخ ھڪ وڏين شين مان آھي.'),
(5531, 74, 36, '(سڀڪنھن) ماڻھو کي ڊيڄاريندڙ آھي.'),
(5532, 74, 37, 'اوھان مان انھي لاءِ جيڪو گھري ته (بھشت) لاءِ اڳي وڌي يا (دوزخ ۾) پٺ تي رھي.'),
(5533, 74, 38, 'سڀڪو جيءُ پنھنجي ڪئي ۾ ڳه پيل آھي.'),
(5534, 74, 39, 'پر سڄي پاسي وارا (ڇٽل آھن).'),
(5535, 74, 40, '(بھشت جي) باغن ۾ ھوندا، گڏجي پڇندا.'),
(5536, 74, 41, 'ڏوھارين کان.'),
(5537, 74, 42, 'ته اوھان کي دوزخ ۾ ڪھڙي ڳالھ وڌو.'),
(5538, 74, 43, 'چوندا ته نڪي نمازين مان ھواسون.'),
(5539, 74, 44, '۽ نڪي مسڪينن کي کارائيندا ھواسون.'),
(5540, 74, 45, '۽ بڪواتن سان گڏ بڪوات ڪندا ڪندا ھواسون.'),
(5541, 74, 46, '۽ بدلي جي ڏينھن کي ڪوڙ ڀانئيندا ھواسون.'),
(5542, 74, 47, 'تان جو اسان کي موت پھتو.'),
(5543, 74, 48, 'پوءِ کين پارت ڪندڙن جي پارت نفعو نه ڏيندي.'),
(5544, 74, 49, 'پوءِ انھن کي ڇا (ٿيو) آھي جو نصيحت کان منھن موڙيندڙ (ٿيا) آھن.'),
(5545, 74, 50, 'ڄڻ ته اُھي ٽھندڙ گڏھ آھن.'),
(5546, 74, 51, 'جي شينھن کان ڀڳا ھجن.'),
(5547, 74, 52, 'بلڪ منجھائن ھر ھڪ مڙس گھرندو آھي ته کيس (آسمان) مان کليل ڪتاب ڏجن.'),
(5548, 74, 53, 'ائين نه آھي، بلڪ آخرت کان نه ٿا ڊڄن.'),
(5549, 74, 54, 'سچ آھي ته بيشڪ قرآن ھڪ نصيحت آھي.'),
(5550, 74, 55, 'پوءِ جيڪو گُھري سو اُن کي پڙھي.'),
(5551, 74, 56, '۽ الله جي گھر کانسواءِ (ان کي) ياد ڪري نه سگھندا آھن، (الله) اُھو آھي جنھن کان ڊڄڻ گھرجي ۽ اھو بخشش جو به مالڪ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'قيامت جي ڏينھن جو قسم کڻان ٿو.'),
(5553, 75, 2, '۽ ملامت ڪرڻ واري نفس جو قسم ٿو کڻان (ته قيامت ۾ سڀ اُٿندا).'),
(5554, 75, 3, 'ماڻھو ڀائيندو آھي ڇا ته سندس ھڏا ڪڏھن گڏ نه ڪنداسون.'),
(5555, 75, 4, 'ھائو! سنديس آڱرين جي ڏوڏين جي سڌي ڪرڻ تي وسوارا آھيون.'),
(5556, 75, 5, 'بلڪ ماڻھو ھي گھرندو آھي ته پنھنجي اڳ (جي وقت) لاءِ گناھ ڪري.'),
(5557, 75, 6, 'پڇي ٿو ته قيامت جو ڏينھن ڪڏھن ٿيندو؟'),
(5558, 75, 7, 'پوءِ جڏھن اکيون ڦاٽي وينديون.'),
(5559, 75, 8, '۽ چنڊ گرھبو.'),
(5560, 75, 9, '۽ سج ۽ چنڊ (ھڪ جاءِ) گڏ ڪبا.'),
(5561, 75, 10, '(تڏھن) چوندو ماڻھو ته ڀڄڻ جي واھ ڪٿي آھي؟'),
(5562, 75, 11, 'نه نه! ڪا واھ ڪانھي.'),
(5563, 75, 12, 'تنھنجي پالڻھار وٽ اُن ڏينھن ٽڪاءُ جو ھنڌ آھي.'),
(5564, 75, 13, 'اُن ڏينھن ماڻھوءَ کي (انھيءَ بابت) خبردار ڪبو جيڪي اڳي موڪليو ھوائين ۽ پوئتي رھايائين.'),
(5565, 75, 14, 'بلڪ ماڻھو پنھنجي لاءِ (پاڻ) ھڪ حُجت آھي.'),
(5566, 75, 15, 'توڻيڪ پنھنجا بھانا پيش پيو ڪري.'),
(5567, 75, 16, '(اي پيغمبر وحي جي) قرآن پڙھڻ مھل پنھنجي زبان کي (ھن لاءِ) نه چور ته اُھو جلدي (ياد ڪري) وٺين.'),
(5568, 75, 17, 'ڇوته (تنھنجي سيني ۾) قرآن جو گڏ ڪرڻ ۽ اُن جو پڙھڻ (آسان ڪرڻ) اسان جي ذمي آھي.'),
(5569, 75, 18, 'پوءِ جڏھن قرآن (ملائڪ کان) پڙھايون تڏھن (پنھنجي دل) اُنجي پڙھڻ جي پٺيان لڳاءِ.'),
(5570, 75, 19, 'وري بيشڪ اُنجو پڌرو ڪرڻ اسان جي ذمي آھي.'),
(5571, 75, 20, 'نه نه! بلڪ (اي ڪافرؤ) دنيا کي دوست رکندا آھيو.'),
(5572, 75, 21, '۽ آخرت کي ڇڏيندا آھيو.'),
(5573, 75, 22, 'ڪي مُنھن اُن ڏينھن سرھا ھوندا.'),
(5574, 75, 23, 'پنھنجي پالڻھار ڏانھن ڏسندڙ ھوندا.'),
(5575, 75, 24, '۽ ڪي مُنھن اُن ڏينھن ڪارا ٿيل ھوندا.'),
(5576, 75, 25, 'ڀانئيندا ته ساڻن پٺيءَ توڙ معاملو ڪيو ويندؤ.'),
(5577, 75, 26, 'نه نه! جڏھن (ساھ) نِڙ گھٽ تي پھچندو.'),
(5578, 75, 27, '۽ چيو ويندو ته ڪو، ڦيڻو رکڻ وارو آھي.'),
(5579, 75, 28, '۽ ڀانئيندو ته (ھاڻي روح جي) جُدا ٿيڻ (جو) وقت اچي ويو.'),
(5580, 75, 29, '۽ ھڪ پِني ٻئي پِنيءَ سان وچڙندي.'),
(5581, 75, 30, 'اُن ڏينھن تنھنجي پالڻھار وٽ ھلڻو آھي.'),
(5582, 75, 31, 'پوءِ نڪي (الله جي ڪلام کي) سچو ڪيائين ۽ نڪي نماز پڙھيائين.'),
(5583, 75, 32, 'پر ڪوڙ ڀانيائين ۽ منھن موڙيائين.'),
(5584, 75, 33, 'وري پنھنجي گھر وارن ڏانھن آڪڙجي ھليو.'),
(5585, 75, 34, 'توتي خرابي ھجي پوءِ توتي خرابي ھجي.'),
(5586, 75, 35, 'وري (ٻيھر چوان ٿو ته) توتي خرابي ھجي.'),
(5587, 75, 36, 'ماڻھو (ايئن) ڀانئي ٿو ڇا ته (کيس) بيڪار ڇڏي ڏبو.'),
(5588, 75, 37, '(اھو) منيءَ جو ٽيپو نه ھو ڇا جو (ماءُ جي ڳڀيرڻ ۾) اڇليو ٿي ويو.'),
(5589, 75, 38, 'وري (رت ۾) دڳ ھو پوءِ الله (اُن کي) بڻايو ۽ اُن کي سئين لڱين ڪيائين.'),
(5590, 75, 39, 'پوءِ منيءَ مان ٻه جنسون نَر ۽ مادي بڻايائين.'),
(5591, 75, 40, 'اھو الله ھن (ڳالھ) تي وس وارو نه آھي ڇا جو مُئن کي جياري؟');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'ڪڏھن ماڻھوءَ تي زماني مان ھڪ (اھڙو) وقت گذري چڪو آھي جو (ھو اھڙي) ڪا شيءَ نه ھو جا يادگيريءَ ۾ اچي.'),
(5593, 76, 2, 'بيشڪ اسان ماڻھوءَ کي ھڪڙي (پاڻ ۾) گڏيل منيءَ مان پيدا ڪيو، ته اُن کي پرکيون پوءِ کيس ٻڌندڙ ڏسندڙ ڪيوسون.'),
(5594, 76, 3, 'بيشڪ اسان کيس سڌو دڳ ڏسيو (ھاڻي ھو) شُڪر ڪندڙ ٿئي يا بي شُڪر.'),
(5595, 76, 4, 'بيشڪ اسان ڪافرن لاءِ زنجير يا ڳٽ ۽ ٻرندڙ باھ تيار ڪئي آھي.'),
(5596, 76, 5, 'بيشڪ نيڪ بخت ٻانھا (انھي شراب جي) پيالي مان پيئندا جنھن جي ملاوت ڪافور (جي چشمي جي پاڻي) جي ھوندي.'),
(5597, 76, 6, 'اھو ھڪ چشمو آھي جنھن مان الله جا (پيارا) ٻانھا پيئندا جنھن کي چڱي طرح وھائيندا.'),
(5598, 76, 7, 'جي (الله جي) باس پوري ڪندا آھن ۽ اُن ڏينھن کان ڊڄندا آھن جنھن جي بڇڙائي (ھر طرف) پڌري ھوندي.'),
(5599, 76, 8, '۽ طعام جي احتياج ھوندي مُحتاج ۽ يتيم ۽ قيدي کي کارائيندا آھن.'),
(5600, 76, 9, '(۽ چوندا آھن ته) اسين ته اوھان کي الله جي واسطي کارائيندا آھيون اوھان کان نڪو بدلو ۽ نڪا شڪر گذاري گھرندا آھيون.'),
(5601, 76, 10, 'بيشڪ اسين پنھنجي پالڻھار کان ھڪ ڏکئي ڏينھن کان ڊڄندا آھيون جو تمام سخت آھي.'),
(5602, 76, 11, 'پوءِ الله اُن ڏينھن جي مدائي کان کين بچايو ۽ اُنھن کي تازگي ۽ خوشي پھچائي.'),
(5603, 76, 12, '۽ سندن صبر ڪرڻ سببان باغ ۽ پَٽ (جو ويس) کين بدلو ڏنائين.'),
(5604, 76, 13, 'اُن (باغ) ۾ تختن تي ٽيڪ ڏئي وھندا، اتي نڪي (سج جي) اُتت ۽ نڪي ڪا سخت سردي ڏسندا.'),
(5605, 76, 14, '۽ اُن (باغ) جون ڇانئون مٿن ويجھيون ٿيل ھونديون ۽ اُن جا ميوا (پٽڻ ۾) چڱي طرح ويجھا ڪبا.'),
(5606, 76, 15, '۽ روپن ٿانون ۽ پيالن سان وٽن اچ وڃ ٿيندي جي شيشن وانگر ھوندا.'),
(5607, 76, 16, '(پر) شيشا (به) چاندي جا جن کي انھن (پياريندڙن) پوري انداز سان ٺھرايو آھي.'),
(5608, 76, 17, '۽ اُتي کين شراب جا پيالا پياربا جنھن (جي پاڻي) جي ملاوت سُنڍ (جي چشمي) جي ھوندي.'),
(5609, 76, 18, 'اُتي اھو ھڪ چشمو آھي جنھن جو نالو سَلۡسَبِيۡل رکيو وڃي ٿو.'),
(5610, 76, 19, '۽ وٽن ھميشه رھندڙ نينگر پيا گھمندا ڦرندا، (اي ڏسندڙ) جڏھن انھن کي ڏسندين (ته) انھن کي کنڊريل موتي پيو ڀائيندين.'),
(5611, 76, 20, '۽ جڏھن اُتي نھاريندين (ته) اڻ کٽ نعمت ۽ وڏي بادشاھي پيو ڏسندين.'),
(5612, 76, 21, 'مٿانئن سنھي سائي پَٽ ۽ ٿُلھي پَٽ جا ڪپڙا ھوندا، ۽ چاندِيءَ جا ڪنگڻ کين پارايا ويندا، ۽ سندن پالڻھار کين تمام پاڪ شراب پياريندو.'),
(5613, 76, 22, 'بيشڪ اھو اوھان (جي عملن) جو بدلو آھي ۽ اوھان جو سعيو قبول ڪيل آھي.'),
(5614, 76, 23, 'بيشڪ اسان توتي قرآن درجي بدرجي لاٿو.'),
(5615, 76, 24, 'پوءِ پنھنجي پالڻھار جي حڪم (اچڻ) تائين صبر ڪر ۽ انھن (ماڻھن) مان ڪنھن گنھگار يا بي شڪر جو چيو نه مڃ.'),
(5616, 76, 25, '۽ پنھنجي پالڻھار جو نالو صبح ۽ سانجھيءَ ياد ڪر.'),
(5617, 76, 26, '۽ ڪجھ (وقت) رات ۾ اُن لاءِ نماز پڙھ ۽ گھڻي رات سندس پاڪائي ساراھ.'),
(5618, 76, 27, 'بيشڪ اُھي (ڪافر) دنيا کي پيارو رکندا آھن ۽ ڳوري ڏينھن کي پنھنجي پٺيان ڦٽو ڪندا آھن.'),
(5619, 76, 28, 'اسان کين پيدا ڪيو ۽ سندن سَنڌ پُختا ڪياسون، ۽ جڏھن گھرون (ته) اھڙا ٻيا عوض ۾ مٽائي آڻيون.'),
(5620, 76, 29, 'بيشڪ اھا نصيحت آھي، پوءِ جيڪو گھري سو پنھنجي پالڻھار ڏانھن واٽ وٺي.'),
(5621, 76, 30, '۽ اوھين نه گھرندؤ مگر اُھو جيڪي الله گھري، ڇوته الله ڄاڻندڙ حڪمت وارو آھي.'),
(5622, 76, 31, 'جنھن کي گھري (تنھن کي) پنھنجي رحمت ۾ داخل ٿو ڪري، ۽ ظالمن لاءِ ڏکوئيندڙ عذاب تيار ڪيو اٿس.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'چڱين گُھلندڙن ھيرُن جو قسم آھي.'),
(5624, 77, 2, 'پوءِ زور سان لڳڻ وارن وائن جو قسم آھي.'),
(5625, 77, 3, '۽ (ڪڪرن کي) کڻندڙن پکيڙيندڙن وائن جو قسم آھي.'),
(5626, 77, 4, 'پوءِ (ڪڪرن کي) ھڪ ٻئي کان جدا ڪندڙ وائن جو قسم آھي.'),
(5627, 77, 5, 'پوءِ وحي آڻيندڙن ملائڪن جو قسم آھي.'),
(5628, 77, 6, '(جي) عذر وڃائڻ لاءِ يا ڊيڄارڻ لاءِ (وحي) آڻيندا آھن.'),
(5629, 77, 7, 'ته بيشڪ جنھن (ڳالھ) جو اوھان کي انجام ڏجي ٿو سا ضرور ٿيڻي آھي.'),
(5630, 77, 8, 'پوءِ جڏھن تارا ميٽي ڇڏبا.'),
(5631, 77, 9, '۽ جڏھن آسمان چيربو.'),
(5632, 77, 10, '۽ جڏھن جبل اڏائبا.'),
(5633, 77, 11, '۽ جڏھن پيغمبر (ٺھرايل وقت تي) گڏ ڪبا.'),
(5634, 77, 12, '(تڏھن ثابت ٿيندو ته پيغمبرن کي) ڪھڙي ڏينھن لاءِ ترسايو ويو.'),
(5635, 77, 13, 'فيصلي جي ڏينھن لاءِ.'),
(5636, 77, 14, '۽ توکي ڪھڙي شيءَ ڄاڻايو ته فيصلو جو ڏينھن ڇا آھي.'),
(5637, 77, 15, 'اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.'),
(5638, 77, 16, 'پھرين کي ھلاڪ نه ڪيو اٿون ڇا؟'),
(5639, 77, 17, 'وري (اُن کانپوءِ) پوين کي انھن جي پٺيان آڻيندا آھيون.'),
(5640, 77, 18, 'ائين ڏوھارين سان ڪندا آھيون.'),
(5641, 77, 19, 'اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.'),
(5642, 77, 20, 'اوھان کي خسيس پاڻي مان نه بڻايو اٿون ڇا؟'),
(5643, 77, 21, 'پوءِ اُن کي ھڪ پختي ھنڌ ۾ رکيوسون.'),
(5644, 77, 22, 'ھڪ مُقرّر وقت تائين.'),
(5645, 77, 23, 'پوءِ (بڻاوت ۾) پورو ڪيوسون جو (اسين) چڱي سگھ وارا آھيون.'),
(5646, 77, 24, 'اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.'),
(5647, 77, 25, 'زمين کي ماپائڻ واري نه ڪئي اٿون ڇا؟'),
(5648, 77, 26, '(جو) جيئرن ۽ مُئن کي (ماپائي).'),
(5649, 77, 27, '۽ زمين ۾ اُچا جبل ڪياسون ۽ اوھان کي مٺو پاڻي پياريوسون.'),
(5650, 77, 28, 'اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.'),
(5651, 77, 29, '(چيو ويندو ته ھاڻي) جنھن (دوزخ) کي اوھين ڪوڙ ڀائيندا ھوا تنھن ڏانھن وڃو.'),
(5652, 77, 30, '(چئبن ته) ٽن شاخن واريءَ ڇانوَ ڏانھن وڃو.'),
(5653, 77, 31, 'نه ٿڌي ڇانوَ ھوندي ۽ نه (باھ جي) توَ کان توکي بچائيندي.'),
(5654, 77, 32, 'بيشڪ اُھا (باھ) ماڙيءَ جيڏيون چڻنگون اڇليندي آھي.'),
(5655, 77, 33, 'ڄڻ ته اُھي ھيڊا اُٺ آھن.'),
(5656, 77, 34, 'اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.'),
(5657, 77, 35, 'اُھو ڏينھن اُھو آھي جنھن ۾ نه ڳالھائيندا.'),
(5658, 77, 36, '۽ نڪي کين موڪل ڏبي ته (ڪو) عذر (بيان) ڪن.'),
(5659, 77, 37, 'اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.'),
(5660, 77, 38, 'ھيءُ فيصلي جو ڏينھن آھي، اوھان کي ۽ اڳين کي گڏ ڪيوسون.'),
(5661, 77, 39, 'پوءِ جيڪڏھن اوھان کي بڇڙي رِٿ ڪرڻي آھي ته منھنجي حق ۾ رٿيو.'),
(5662, 77, 40, 'اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.'),
(5663, 77, 41, 'بيشڪ پرھيزگار ڇانوَن ۽ چشمن ۾ ھوندا.'),
(5664, 77, 42, '۽ جنھن جنس مان گھرندا (تنھن جنس جي) ميون ۾ ھوندا.'),
(5665, 77, 43, '(چئبن ته) چڱا ڪم ڪندا ھئو تنھنڪري خوش ٿي کائو ۽ پيئو.'),
(5666, 77, 44, 'بيشڪ اسين نيڪن کي ائين بدلو ڏيندا آھيون.'),
(5667, 77, 45, 'اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.'),
(5668, 77, 46, '(اي ڪوڙ ڀائيندڙؤ دنيا ۾) ٿوري دير کائي وٺو ۽ آسودا رھو بيشڪ اوھين ڏوھاري آھيو.'),
(5669, 77, 47, 'اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.'),
(5670, 77, 48, '۽ جڏھن کين چئجي ته نماز پڙھو (ته) نماز نه پڙھندا آھن.'),
(5671, 77, 49, 'اُن ڏينھن ڪوڙ ڀائيندڙن لاءِ خرابي آھي.'),
(5672, 77, 50, 'پوءِ اُن (قرآن) کانپوءِ ڪھڙي ڳالھ تي ويساھ رکندا.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'ڪھڙيءَ ڳالھ بابت ھڪ ٻئي کان پڇن ٿا.'),
(5674, 78, 2, '(ھائو!) ھڪڙي اھڙي وڏي واقعي بابت.'),
(5675, 78, 3, 'جنھن (جي ٿيڻ) ۾ اُھي (پاڻ ۾) مختلف آھن.'),
(5676, 78, 4, 'ائين (سندن خيالن وانگر) نه آھي سگھوئي ڄاڻندا.'),
(5677, 78, 5, 'وري (به چئون ٿا) ته ائين نه آھي سگھوئي ڄاڻندا.'),
(5678, 78, 6, 'زمين کي وڇاڻو نه ڪيو اٿون ڇا؟'),
(5679, 78, 7, '۽ جبلن کي ميخون؟'),
(5680, 78, 8, '۽ اوھان کي جوڙو جوڙو (نر ۽ مادي) بڻايوسون.'),
(5681, 78, 9, '۽ اوھان جي ننڊ کي فرحت بڻايوسون.'),
(5682, 78, 10, '۽ رات کي ڍَڪ ڪيوسون.'),
(5683, 78, 11, '۽ ڏينھن کي گذران (جو وقت) ڪيوسون.'),
(5684, 78, 12, '۽ اوھان جي مٿون ست مُحڪم (آسمان) بڻاياسون.'),
(5685, 78, 13, '۽ ھڪ چمڪندڙ ڏيئو (سج) بڻايوسون.'),
(5686, 78, 14, '۽ ڪڪرن مان پلٽندڙ پاڻي وسايوسون.'),
(5687, 78, 15, 'ته اُن سان اَن ۽ سَلا ڄمايون.'),
(5688, 78, 16, '۽ گھاٽاباغ (به).'),
(5689, 78, 17, 'بيشڪ فيصلي جو ڏينھن ھڪ مقرر مھل آھي.'),
(5690, 78, 18, 'جنھن ڏينھن صُور ۾ ڦوڪبو ته اوھين ٽوليون ٽوليون ٿي ايندؤ.'),
(5691, 78, 19, '۽ آسمان کولبو پوءِ دروازا ٿي پوندو.'),
(5692, 78, 20, '۽ جبل ھلائبا پوءِ رُڃ وانگر ٿيندا.'),
(5693, 78, 21, 'بيشڪ دوزخ انتظار ڪڍڻ وارو آھي.'),
(5694, 78, 22, 'جو ھٺيلن جو رھڻ جو ھنڌ آھي.'),
(5695, 78, 23, 'منجھس ڪيئي ڊگھيون مُدتون رھندا.'),
(5696, 78, 24, 'اُن ۾ نڪا ٿڌائي ۽ نڪا پيڻ جي شيءِ چکندا.'),
(5697, 78, 25, 'تتي پاڻي ۽ روڳ کانسواءِ.'),
(5698, 78, 26, '(اھو سندن) پورو پورو بدلو آھي.'),
(5699, 78, 27, 'ڇوته اُھي حساب ڏيڻ جي اُميد نه رکندا ھوا.'),
(5700, 78, 28, '۽ اسان جي حُڪمن کي بلڪل ڪوڙ ڀائيندا ھوا.'),
(5701, 78, 29, '۽ سڀ شيءِ کي لکي ڳڻي ڇڏيو اٿون.'),
(5702, 78, 30, 'پوءِ (چئبن ته مزو) چکو جو عذاب کانسواءِ اوھان تي (ٻيو) ڪي (به) نه وڌائينداسون.'),
(5703, 78, 31, 'بيشڪ پرھيزگارن لاءِ ڪاميابي آھي.'),
(5704, 78, 32, 'جو باغ ۽ ڊاکون.'),
(5705, 78, 33, '۽ ھم عمر نوجوان زالون.'),
(5706, 78, 34, '۽ شراب جا ڇلڪندڙ پيالا (به).'),
(5707, 78, 35, 'اتي نڪا اجائي بڪ ۽ نڪو ڪوڙ ٻڌندا.'),
(5708, 78, 36, '(اھو پورو) بدلو تنھنجي پالڻھار کان (انعام) عطا ڪيل حساب روءِ آھي.'),
(5709, 78, 37, 'جو آسمانن ۽ زمين جو ۽ جيڪي ٻنھي جي وچ ۾ آھي تن جو پالڻھار ٻاجھارو آھي اُن (جي ھيبت) کان ڪوبه ورندي ڏئي نه سگھندو.'),
(5710, 78, 38, 'جنھن ڏينھن روح ۽ (سڀئي) ملائڪ صفون ٻڌي بيھندا (تنھن ڏينھن) جنھن کي ٻاجھاري (الله) موڪل ڏني ھوندي ۽ پوري ڳالھ ڳالھائي ھوندائين تنھن کانسواءِ (ٻيو ڪي به) نه ڳالھائيندا.'),
(5711, 78, 39, 'اِھو ڏينھن سچ پچ (ٿيڻو) آھي، تنھنڪري جيڪو گھري سو پنھنجي پالڻھار ڏانھن (پنھنجي) رھڻ جو ھنڌ ٺاھي.'),
(5712, 78, 40, 'بيشڪ اسان اوھان کي ويجھي عذاب (اچڻ) کان ڊيڄاري چڪاسون، جنھن ڏينھن آدمي اُھو ڏسندو جيڪي سندس ٻنھي ھٿن اڳي ڪري موڪليو ۽ ڪافر چوندو ته ھاءِ ارمان جيڪر آءٌ مٽي ھجان ھا.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'اندر گھڙي ڇڪي آئيندڙن جو قسم آھي.'),
(5714, 79, 2, '۽ کولي آسانيءَ سان ڳنڍ ڇوڙيندڙن جو قسم آھي.'),
(5715, 79, 3, '۽ چڱي طرح سان ترندڙن جو قسم آھي.'),
(5716, 79, 4, 'پوءِ ڊوڙي اڳرائي ڪندڙن جو قسم آھي.'),
(5717, 79, 5, 'پوءِ (سڀ) ڪم جي رٿيندڙن ٽولين جو قسم آھي.'),
(5718, 79, 6, 'ته جنھن ڏينھن ڌٻڻ واري ڌٻندي.'),
(5719, 79, 7, 'ان کي پٺيان لڳندڙ (ٻيو ڌوڏو) پٺيان لڳندو.'),
(5720, 79, 8, 'تنھن ڏينھن ڪيتريون ئي دليون ڏڪي وڃڻ واريون ھونديون.'),
(5721, 79, 9, 'انھن جون اکيون (خواريءَ سبب) جھڪيون ٿينديون.'),
(5722, 79, 10, 'چون ٿا ته اسين پوئين پيرين موٽائباسون ڇا؟'),
(5723, 79, 11, 'ھان جڏھن ڀُتا ھڏا ٿينداسون؟'),
(5724, 79, 12, 'چوندا آھن ته انھي مھل اھو ٻيھر موٽڻ (وڏو) نقصان وارو آھي.'),
(5725, 79, 13, 'پوءِ اھو (واقعو) ھڪڙي سخت ھڪل آھي.'),
(5726, 79, 14, 'پوءِ اتي جو اُتي ھڪ پڌري ميدان ۾ حاضر ٿيل ھوندا.'),
(5727, 79, 15, '(اي پيغمبر) تو وٽ مُوسىٰ جي خبر پُھتي آھي ڇا؟'),
(5728, 79, 16, 'جڏھن سندس پالڻھار کيس پاڪ ميدان طُوىٰ ۾ سڏيو.'),
(5729, 79, 17, 'ته فرعون ڏانھن وڃ ڇوته ھو حد کان لنگھيو آھي.'),
(5730, 79, 18, 'پوءِ (ان کي) چؤ ته ھن ڏانھن خيال اٿئي ته تون سڌرجين؟'),
(5731, 79, 19, '۽ تنھنجي پالڻھار ڏانھن توکي (سڌو) رستو ڏيکاريان ته (تون اُن کان) ڊڄين.'),
(5732, 79, 20, 'پوءِ فرعون کي وڏي نشاني ڏيکاريائين.'),
(5733, 79, 21, 'پوءِ (فرعون موسى) کي ڪوڙو ڀانيو ۽ نافرماني ڪيائين.'),
(5734, 79, 22, 'وري (اُتان) پٺيرو ٿي تکو ھلڻ لڳو.'),
(5735, 79, 23, 'پوءِ (پنھنجي قوم کي) گڏ ڪيائين پوءِ سڏيائين.'),
(5736, 79, 24, 'پوءِ چيائين ته آءٌ اوھان جو تمام مٿاھون پالڻھار آھيان.'),
(5737, 79, 25, 'پوءِ الله ان کي آخرت ۽ دُنيا جي عذاب ۾ پڪڙيو.'),
(5738, 79, 26, 'بيشڪ ھن (قصّي) ۾ انھي لاءِ نصيحت آھي جيڪو ڊڄي.'),
(5739, 79, 27, 'ڀلا بڻاوت ۾ اوھين ڏکيا آھيو يا آسمان جنھن کي الله بڻايو؟'),
(5740, 79, 28, 'اُن جي مٿاھين بلند ڪيائين پوءِ اُن کي برابر بيھاريائين.'),
(5741, 79, 29, '۽ اُن جي رات کي اوندھ ڪيائين ۽ اُن جو سوجھرو (ڏينھن جو) پڌرو ڪيائين.'),
(5742, 79, 30, '۽ اُن کان پوءِ زمين کي وڇايائين.'),
(5743, 79, 31, 'اُن مان سندس پاڻي ۽ سندس گاھ ڪڍيائين.'),
(5744, 79, 32, '۽ جبلن کي مُحڪم کوڙيائين.'),
(5745, 79, 33, 'اوھان جي نفعي لاءِ ۽ اوھان جي ڍورن (جي نفعي) لاءِ.'),
(5746, 79, 34, 'پوءِ جڏھن وڏو واقعو (قيامت جو) ايندو.'),
(5747, 79, 35, 'اُن ڏينھن ماڻھو اھو ياد ڪندو جيڪي (دنيا ۾) ڪمايو ھوائين.'),
(5748, 79, 36, '۽ دوزخ اُنھيءَ لاءِ پڌرو ڪبو جيڪو ڏسڻ گھرندو.'),
(5749, 79, 37, 'پوءِ جيڪو حد کان لنگھيو ھوندو.'),
(5750, 79, 38, '۽ ھن دنيا جي حياتيءَ کي ڀَلو سمجھيائين.'),
(5751, 79, 39, 'تنھن جي جاءِ بيشڪ دوزخ آھي.'),
(5752, 79, 40, '۽ جيڪو پنھنجي پالڻھار جي (حضور ۾) روبرو بيھڻ کان ڊنو ۽ نفس کي سَڌ کان جھليائين.'),
(5753, 79, 41, 'پوءِ تنھنجي جاءِ بيشڪ بھشت آھي.'),
(5754, 79, 42, '(اي پيغمبر) توکان قيامت بابت پُڇن ٿا ته اُن جو (واقعو) ٿيڻ ڪڏھن آھي؟'),
(5755, 79, 43, '(اي پڇندڙ) اُن جي خبر بابت تون ڪھڙي خيال ۾ آھين؟'),
(5756, 79, 44, 'اُن جي پڄاڻي (جي خبر) تنھنجي پالڻھار وٽ آھي.'),
(5757, 79, 45, 'جيڪو قيامت کان ڊڄي رڳو تنھن لاءِ تون ڊيڄاريندڙ آھين.'),
(5758, 79, 46, 'جنھن ڏينھن اُن کي ڏسندا (تنھن ڏينھن) ائين ڀانئيندا ته ڄڻڪ ھڪڙي وقت سانجھي يا ان جي صبح کانسواءِ (دنيا ۾) رھيا ئي نه ھوا.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'مُنھن ۾ گُھنڊ وڌائين ۽ مُنھن موڙيائين.'),
(5760, 80, 2, 'ھن ڪري جو وٽس نابين آيو.'),
(5761, 80, 3, '۽ ڪنھن سمجھايئي جيڪر اُھو سڌرجي ھا.'),
(5762, 80, 4, 'يا نصيحت ٻڌي ھا پوءِ اُھا نصيحت ٻڌڻ نفعو ڏئيس ھا.'),
(5763, 80, 5, 'پر جنھن بي پرواھي ڪئي.'),
(5764, 80, 6, 'پوءِ تنھن (جي سڌاري) لاءِ تون پٺيان پيو آھين.'),
(5765, 80, 7, 'ھن ۾ توتي (ڪا ميار) ڪانھي ته اُھو نه سُڌرجي.'),
(5766, 80, 8, '۽ جيڪو تو وٽ ڊوڙندو آيو.'),
(5767, 80, 9, '۽ اُھو (پنھنجي الله کان) ڊڄي ٿو.'),
(5768, 80, 10, 'پوءِ تنھن کان تون ويسلو ٿئين ٿو.'),
(5769, 80, 11, 'ائين نه آھي بيشڪ اھي (قرآن جون آيتون) ھڪ نصيحت آھي.'),
(5770, 80, 12, 'پوءِ جيڪو گھُري سو قرآن ياد رکي.'),
(5771, 80, 13, '(بيشڪ قرآن جون آيتون) تعظيم وارن ورقن ۾ لکيل آھن.'),
(5772, 80, 14, 'جي مٿاھان ۽ پاڪ آھن.'),
(5773, 80, 15, 'لکندڙن جي ھٿن ۾.'),
(5774, 80, 16, 'جي سڳورا ڀلارا آھن.'),
(5775, 80, 17, 'ماڻھو ماريو وڃي جو ڪھڙو نه بي شُڪر آھي.'),
(5776, 80, 18, '(ڌيان ڪري ته الله) ڪھڙي شيءَ مان پيدا ڪيس.'),
(5777, 80, 19, 'مني جي ڦڙي مان، خلقيائينس پوءِ اندازو مقرر ڪيائينس.'),
(5778, 80, 20, 'وري (نڪرڻ جي) واٽ سنھنجي ڪيائينس.'),
(5779, 80, 21, 'وري ماريائينس پوءِ قبر ۾ رکايائينس.'),
(5780, 80, 22, 'وري جڏھن گھرندو تڏھن اُن کي جيئرو ڪندو.'),
(5781, 80, 23, 'ائين نه آھي جيڪي فرمايائينس سو پورو نه ڪيائين.'),
(5782, 80, 24, 'پوءِ جڳائي ته ماڻھو پنھنجي کاڌي ڏانھن نھاري ڏسي.'),
(5783, 80, 25, 'ته اسان پاڻي کي چڱيءَ طرح آسمان کان پلٽيو.'),
(5784, 80, 26, 'وري زمين کي پوري طرح چيريوسون.'),
(5785, 80, 27, 'پوءِ منجھس اَن ڄمايوسون.'),
(5786, 80, 28, '۽ ڊاکون ۽ ترڪاريون.'),
(5787, 80, 29, '۽ زيتون ۽ کجيون.'),
(5788, 80, 30, '۽ گھاٽا باغ.'),
(5789, 80, 31, '۽ ميوو ۽ گاھ (به).'),
(5790, 80, 32, 'اوھان جي نفعي لاءِ ۽ اوھان جي ڍورن لاءِ.'),
(5791, 80, 33, 'پوءِ جڏھن ڪَن ٻوڙا ڪندڙ (دھشت واري) ھڪل ايندي.'),
(5792, 80, 34, 'انھي ڏينھن آدمي پنھنجي ڀاءُ کان ڀڄندو.'),
(5793, 80, 35, '۽ (پڻ) پنھنجي ماءُ کان ۽ پنھنجي پيءُ کان.'),
(5794, 80, 36, '۽ پنھنجي گھر واريءَ کان ۽ پنھنجي پٽن کان ڀڄندو.'),
(5795, 80, 37, 'انھن مان سڀڪنھن مڙس لاءِ اُن ڏينھن اھڙي حالت ھوندي جو کيس بس ھوندي.'),
(5796, 80, 38, 'ڪيترائي منھن اُن ڏينھن روشن ھوندا.'),
(5797, 80, 39, 'کِلندڙ ۽ (تمام) سرھا ھوندا.'),
(5798, 80, 40, '۽ ڪيترائي منھن (ھوندا) جن تي اُن ڏينھن رئي پيل ھوندي.'),
(5799, 80, 41, 'جن کي ڪارنھن ويڙھي ويندي.'),
(5800, 80, 42, 'اھي ئي مُنڪر بدڪار آھن.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'جڏھن سج ويڙھبو.'),
(5802, 81, 2, '۽ جڏھن تارا ميرا ٿيندا.'),
(5803, 81, 3, '۽ جڏھن جبل ھلايا ويندا.'),
(5804, 81, 4, '۽ جڏھن ڍُڪيون ويامڻ تي ڏاچيون بيڪار ڇڏبيون.'),
(5805, 81, 5, '۽ جڏھن جھنگلي جانور گڏ ڪبا.'),
(5806, 81, 6, '۽ جڏھن درياءَ (باھ وانگر) تپائبا.'),
(5807, 81, 7, '۽ جڏھن جيءَ پاڻ ۾ ملائبا.'),
(5808, 81, 8, '۽ جڏھن جيئري پوريل نياڻي پڇبي ته.'),
(5809, 81, 9, 'ڪھڙي ڏوھ ڪري ماري وئي.'),
(5810, 81, 10, '۽ جڏھن اعمالناما کولبا.'),
(5811, 81, 11, '۽ جڏھن آسمان ڇلبو.'),
(5812, 81, 12, '۽ جڏھن دوزخ ٻاربو.'),
(5813, 81, 13, '۽ جڏھن بھشت ويجھو ڪبو.'),
(5814, 81, 14, '(تڏھن) ھر ھڪ جيءُ جيڪي حاضر آندو سو ڄاڻندو.'),
(5815, 81, 15, 'پوئتي ھٽندڙ تارن جو قَسم کڻان ٿو.'),
(5816, 81, 16, 'جي (ڪڏھن) سڌا ھلندڙ آھن (ڪڏھن) لڪي ويندڙ آھن.'),
(5817, 81, 17, '۽ قسم آھي رات جو جڏھن پوئتي موٽندي آھي.'),
(5818, 81, 18, '۽ قسم آھي صُبح جو جڏھن روشن ٿئي.'),
(5819, 81, 19, 'ته بيشڪ اھو (قرآن) ھڪ موڪليل سڳوري (مَلَڪ) جو پيغام ڏيڻ آھي.'),
(5820, 81, 20, 'جو سگھ واري عرش جي مالڪ (الله) وٽ وڏي مرتبي وارو آھي.'),
(5821, 81, 21, 'جو مڃوتيءَ وارو اُتي (مَلڪوت ۾) امين آھي.'),
(5822, 81, 22, '۽ (ھي) اوھان جو سنگتي (ﷴ ﷺ ڪو) چريو نه آھي.'),
(5823, 81, 23, '۽ بيشڪ (اوھان جي سنگتي) اُن (مَلَڪ) کي آسمان جي پڌري ڪناري تي (چتائي) ڏٺو آھي.'),
(5824, 81, 24, '۽ اُھو (اوھان جو سنگتي) ڳجھ (جي ڳالھ ڏسڻ) تي بخل ڪندڙ نه آھي.'),
(5825, 81, 25, '۽ قرآن تڙيل شيطان جو ڪلام نه آھي.'),
(5826, 81, 26, 'پوءِ ڪيڏانھن وڃو ٿا؟'),
(5827, 81, 27, 'ھيءُ (قرآن) جھان وارن جي نصيحت کانسواءِ (ٻيو) ڪي نه آھي.'),
(5828, 81, 28, 'اوھان مان جيڪو سڌو ھلڻ گھري تنھن لاءِ آھي.'),
(5829, 81, 29, '۽ جھانن جي پالڻھار الله جي گھُر کانسواءِ (ٻيو) ڪي اوھين گھري نه سگھندؤ.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'جڏھن آسمان ڦاٽندو.'),
(5831, 82, 2, '۽ جڏھن تارا ڇڻي پوندا.'),
(5832, 82, 3, '۽ جڏھن سمنڊ تمام جوش سان وھائبا.'),
(5833, 82, 4, '۽ جڏھن قبرون پٽبيون.'),
(5834, 82, 5, '(تڏھن) سڀڪو شخص اُھو ڄاڻندو جيڪي اڳي موڪليو ھوائين ۽ جيڪي پوئتي ڇڏيو ھوائين.'),
(5835, 82, 6, 'اي ماڻھو توکي تنھنجي (انھي) سڳوري پالڻھار کان ڪھڙي شيءَ ٺڳيو؟'),
(5836, 82, 7, 'جنھن توکي پيدا ڪيو پوءِ توکي سنئين لڱين ڪيائين پوءِ توکي برابر ڪيائين.'),
(5837, 82, 8, 'جھڙي صورت ۾ گھريائين (تھڙيءَ ۾) تنھنجو جوڙ جوڙيائين.'),
(5838, 82, 9, 'ائين نه آھي بلڪ اوھين عملن جي جزا ڏيڻ کي ڪوڙ ڀائيندا آھيو.'),
(5839, 82, 10, '۽ بيشڪ اوھان تي نگھبان (مقرر ٿيل) آھن.'),
(5840, 82, 11, 'سڳورا لکڻ وارا.'),
(5841, 82, 12, 'جيڪي اوھين ڪندا آھيو سو ڄاڻندا آھن.'),
(5842, 82, 13, 'بيشڪ ڀلارا (بھشت جي) نعمت ۾ ھوندا.'),
(5843, 82, 14, '۽ بيشڪ گنھگار دوزخ ۾ ھوندا.'),
(5844, 82, 15, 'عملن جي بدلي جي ڏينھن دوزخ ۾ داخل ٿيندا.'),
(5845, 82, 16, '۽ اُن کان اُھي لڪل نه ھوندا.'),
(5846, 82, 17, '۽ ڪنھن سمجھايئي ته عملن جي بدلي جو ڏينھن ڇا آھي؟'),
(5847, 82, 18, 'وري (به چئون ٿا ته) ڪنھن سمجھايئي ته عملن جي بدلي جو ڏينھن ڇا آھي؟'),
(5848, 82, 19, 'اُھو ھڪ ڏينھن آھي جو ڪو جيءُ ڪنھن جيءَ کي ڪجھ فائدو پھچائي نه سگھندو، ۽ اُن ڏينھن (ساري) حڪومت خاص الله جي ھوندي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, '(تور ۽ ماپ ۾) انھن گھٽ ڪري ڏيندڙن لاءِ ويل آھي.'),
(5850, 83, 2, 'جي جڏھن (پاڻ لاءِ) ماڻھن کان مَئي وٺندا آھن ته بلڪل پورو ڪري وٺندا آھن.'),
(5851, 83, 3, '۽ جڏھن اُنھن کي مَئي ڏيندا آھن يا کين توري ڏيندا آھن (ته) گھٽائي ڏيندا آھن.'),
(5852, 83, 4, 'اھي گمان نه ٿا ڪن ڇا ته کين (ضرور) اُٿاربو.'),
(5853, 83, 5, 'اُنھيءَ وڏي ڏينھن لاءِ.'),
(5854, 83, 6, 'جنھن ڏينھن ماڻھو جھانن جي پالڻھار اڳيان ٿي بيھندا.'),
(5855, 83, 7, 'سچ آھي ته بيشڪ بدڪارن جو اعمالنامو سجين ۾ آھي.'),
(5856, 83, 8, '۽ ڪنھن سمجھايئي ته سجين ڇا آھي؟'),
(5857, 83, 9, 'ھڪ لکيل دفتر آھي.'),
(5858, 83, 10, 'اُن ڏينھن (انھن) ڪوڙ ڀانئيندڙن لاءِ ويل آھي.'),
(5859, 83, 11, 'جيڪي عملن جي بدلي جي ڏينھن کي ڪوڙ ڀانئيندا آھن.'),
(5860, 83, 12, '۽ اُن کي سڀڪنھن حد کان لنگھندڙ گنھگار کانسواءِ (ٻيو) ڪو ڪوڙ نه ڀانئيندو.'),
(5861, 83, 13, 'جڏھن اسان جون آيتون کيس پڙھي ٻڌائبيون آھن ته چوندو آھي ته (اھي) اڳين جون آکاڻيون آھن.'),
(5862, 83, 14, 'ائين بلڪل نه آھي! بلڪ، جيڪي ڪمائيندا ھوا تنھن جي ڪَٽ سندين دلين تي ڄمي ويئي آھي.'),
(5863, 83, 15, 'ائين نه آھي پنھنجي پالڻھار (جي سامھون اچڻ) کان انھي ڏينھن روڪيل ھوندا.'),
(5864, 83, 16, 'وري بيشڪ اُھي دوزخ ۾ گھڙڻ وارا ھوندا.'),
(5865, 83, 17, 'وري چئبن ته ھي اُھو دوزخ آھي جنھن کي اوھين ڪوڙ ڀانئيندا ھُيؤ.'),
(5866, 83, 18, 'سچ آھي بيشڪ ڀلارن جو اعمالنامو عِلّيِّيۡن ۾ آھي.'),
(5867, 83, 19, '۽ ڪنھن سمجھايئي ته عِلِّيّين ڇا آھي؟'),
(5868, 83, 20, 'ھڪ لکيل دفتر آھي.'),
(5869, 83, 21, 'اُن وٽ (الله جا) مقرب حاضر رھندا آھن.'),
(5870, 83, 22, 'بيشڪ ڀلارا (بھشت) جي نعمتن ۾ ھوندا.'),
(5871, 83, 23, 'تختن تي ويھي نظارو پيا ڪندا.'),
(5872, 83, 24, '(سڀني پاسن کان) سندن منھن مان ئي نعمت جي تازگي پيو معلوم ڪندين.'),
(5873, 83, 25, 'مھر لڳل صاف شراب مان پيو پياربن.'),
(5874, 83, 26, 'جنھن جي مُھر مشڪ (جي) ھوندي، ۽ شوقينن کي جڳائي ته اُنھيءَ ۾ شوق رکن.'),
(5875, 83, 27, 'اُن جي ملاوت تَسۡنِيم (جي پاڻيءَ) مان ھوندي.'),
(5876, 83, 28, 'جو ھڪ چشمو آھي جنھن مان الله جا ويجھا (ٻانھا) پيئندا.'),
(5877, 83, 29, 'بيشڪ ڏوھاري (دنيا ۾) مؤمنن تي کلندا ھوا.'),
(5878, 83, 30, '۽ جڏھن وٽائن لنگھندا ھوا ته مؤمنن تي حقارت ڪري پاڻ ۾ ميڇون ڏيندا ھوا.'),
(5879, 83, 31, '۽ جڏھن پنھنجن گھر وارن ڏانھن موٽندا ھوا (ته) ڏاڍيون خوشيون ڪندا موٽندا ھوا.'),
(5880, 83, 32, '۽ جڏھن مؤمنن کي ڏسندا ھوا ته چوندا ھوا ته بيشڪ اُھي گمراھ آھن.'),
(5881, 83, 33, '۽ (حقيقت ڪري) اُھي مؤمنن تي نگھبان ڪري نه موڪليا ويا ھوا.'),
(5882, 83, 34, 'تنھنڪري اڄ مؤمن ڪافرن تي کِلن ٿا.'),
(5883, 83, 35, 'تختن تي (ويھي) نظارو ڪن ٿا.'),
(5884, 83, 36, '(۽ چون ٿا) ته ڪافرن کي انھيءَ جو بدلو ڏنو ويو آھي ڇا جيڪي اُھي ڪندا ھوا.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'جڏھن آسمان ڦاٽندو.'),
(5886, 84, 2, '۽ پنھنجي پالڻھار جو حُڪم ڪن ڏئي ٻڌندو ۽ (ٻڌڻ) حق اٿس.'),
(5887, 84, 3, '۽ جڏھن زمين پکيڙبي.'),
(5888, 84, 4, '۽ جيڪي اُن ۾ ھوندو سو (سڀ) ڪڍي اُڇليندي ۽ خالي ٿيندي.'),
(5889, 84, 5, '۽ پنھنجي پالڻھار جو حُڪم ڪن ڏئي ٻڌندي ۽ (ٻڌڻ) حق اٿس.'),
(5890, 84, 6, 'اي ماڻھو تون پنھنجي پالڻھار ڏانھن (پھچڻ ۾) تمام گھڻي تڪليف وٺڻ وارو آھين جو اُن کي (وڃي) ملندين.'),
(5891, 84, 7, 'پوءِ اُھو (ماڻھو) جنھن کي سندس سڄي ھٿ ۾ سندس اعمالنامو ڏبو.'),
(5892, 84, 8, 'تنھن سان آسانيءَ طرح سگھوئي حساب ڪبو.'),
(5893, 84, 9, '۽ پنھنجن گھر وارن ڏانھن خوش ٿي موٽندو.'),
(5894, 84, 10, '۽ اُھو ماڻھو جنھن کي سندس اعمالنامو سندس پٺي پويان ڏبو.'),
(5895, 84, 11, 'سو سگھوئي موت کي سڏيندو.'),
(5896, 84, 12, '۽ دوزخ ۾ گھڙندو.'),
(5897, 84, 13, 'ڇوته اُھو (دنيا ۾) پنھنجن گھر وارن ۾ خوش (حال) ھو.'),
(5898, 84, 14, 'بيشڪ انھيءَ ڀانيو ھو ته (الله ڏانھن) ڪڏھن نه موٽندو.'),
(5899, 84, 15, 'ھائو! (موٽندو) ڇوته سندس پالڻھار اُن کي ڏسندڙ آھي.'),
(5900, 84, 16, 'پوءِ شفق جو قسم کڻان ٿو.'),
(5901, 84, 17, '۽ رات جو (قسم) ۽ جنھن کي گڏ ڪيو اٿس.'),
(5902, 84, 18, '۽ چنڊ جو (قسم) جڏھن پورو روشن ٿئي.'),
(5903, 84, 19, 'ته ھڪ حال کان ٻئي حال تي ضرور چڙھندؤ.'),
(5904, 84, 20, 'پوءِ ھنن (ڪافرن) کي ڇا (ٿيو) آھي جو ايمان نه آڻيندا آھن.'),
(5905, 84, 21, '۽ جڏھن وٽن قرآن پڙھبو آھي (ته) سجدو نه ڪندا آھن.'),
(5906, 84, 22, 'بلڪ (اھي) ڪافر ڪوڙ ڀانئيندا آھن.'),
(5907, 84, 23, '۽ جيڪي (پنھنجين دلين ۾) سانڀيندا آھن سو الله چڱي طرح ڄاڻندڙ آھي.'),
(5908, 84, 24, 'پوءِ کين ڏکوئيندڙ عذاب جي خبر ڏي.'),
(5909, 84, 25, 'پر جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ اَڻ کُٽ اَجر آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'بُرجن واري آسمان جو قسم آھي.'),
(5911, 85, 2, '۽ وعدي ڪيل ڏينھن جو قسم آھي.'),
(5912, 85, 3, '۽ حاضر ٿيڻ واري ۽ جنھن وٽ حاضر ڪيو وڃي تنھن جو قسم آھي.'),
(5913, 85, 4, 'ته کاھين وارا ماريا وڃن.'),
(5914, 85, 5, 'جا باھ گھڻي ٻَل واري ھئي.'),
(5915, 85, 6, 'جڏھن اُھي اُنھن باھ جي (کاھين) تي ويٺل ھوا.'),
(5916, 85, 7, '۽ جيڪي اُنھن مسلمانن سان (ظلم) ٿي ڪيو تنھن کي روبرو ڏسڻ وارا ھوا.'),
(5917, 85, 8, '۽ انھن ھن کانسواءِ (ٻيءَ) ڪنھن ڳالھ جو وير نه ٿي ورتو ته انھن اُنھيءَ غالب ساراھيل الله تي ايمان آندو ھو.'),
(5918, 85, 9, 'جنھن کي آسمانن ۽ زمين جي بادشاھي آھي، ۽ الله سڀ شيءَ جي خبر رکندڙ آھي.'),
(5919, 85, 10, 'بيشڪ جن (ماڻھن) مؤمنن ۽ مؤمنياڻين کي ايذايو وري توبه نه ڪيائون تن لاءِ دوزخ جو عذاب آھي ۽ اُنھن لاءِ سڙڻ جو عذاب (به) آھي.'),
(5920, 85, 11, 'بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ اھڙا باغ آھن جن جي ھيٺان واھيون پيون وھن، اھا وڏي مراد ماڻڻ آھي.'),
(5921, 85, 12, 'بيشڪ تنھنجي پالڻھار جي پڪڙ بلڪل سخت آھي.'),
(5922, 85, 13, 'بيشڪ اُھو پھريون دفعو بڻائي ٿو ۽ وري ٻيھر موٽائيندو.'),
(5923, 85, 14, '۽ اُھو بخشڻھار پيار رکندڙ آھي.'),
(5924, 85, 15, '(جو) عرش جو مالڪ تمام وڏي شان وارو آھي.'),
(5925, 85, 16, 'جيڪي گھري سو ڪرڻ وارو ئي آھي.'),
(5926, 85, 17, 'تو وٽ لشڪرن جي خبر (نه) آئي آھي ڇا؟'),
(5927, 85, 18, '(يعني) فرعون ۽ ثمود جي.'),
(5928, 85, 19, 'بلڪ ڪافر ڪوڙ ڀانئڻ ۾ (لڳا پيا) آھن.'),
(5929, 85, 20, '۽ الله سندن چؤڦير گھيرو ڪندڙ آھي.'),
(5930, 85, 21, 'بلڪ ھي سڳورو قرآن آھي.'),
(5931, 85, 22, 'حفاظت ڪيل تختي ۾ لکيل آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'آسمان جو ۽ رات جي وقت ۾ ايندڙ جو قسم آھي.'),
(5933, 86, 2, '۽ ڪنھن سمجھايئي ته رات جو ايندڙ ڇا آھي؟'),
(5934, 86, 3, '(اُھو) چمڪندڙ تارو آھي.'),
(5935, 86, 4, 'ته ڪو جيءُ اھڙو ڪونھي جنھن تي ھڪ نگھبان مقرر ٿيل (نه) آھي.'),
(5936, 86, 5, 'تنھنڪري ماڻھوءَ کي گھرجي ته نھاري ڏسي ته کيس ڇا مان بڻايو ويو آھي.'),
(5937, 86, 6, 'ٽپي سان نڪرندڙ پاڻي مان بڻايو ويس.'),
(5938, 86, 7, 'جو پُٺيءَ ۽ اُرھ جي وچ مان نڪرندو آھي.'),
(5939, 86, 8, 'بيشڪ الله سندس وري موٽائڻ تي وس وارو آھي.'),
(5940, 86, 9, 'جنھن ڏينھن (ماڻھن جي دلين جا) سڀ ڳُجھ جاچيا ويندا.'),
(5941, 86, 10, 'پوءِ تنھن ڏينھن ڪانه اُن کي سگھ رھندي ۽ نڪو واھرو (ھوندس).'),
(5942, 86, 11, '۽ مينھن واري آسمان جو قسم آھي.'),
(5943, 86, 12, '۽ ڦاٽن واريءَ زمين جو (به) قسم آھي.'),
(5944, 86, 13, 'ته بيشڪ قرآن ھڪ فيصلو ڪندڙ ڳالھ آھي.'),
(5945, 86, 14, '۽ ھيُ قرآن ڪا ٺـٺولي (جي ڳالھ) نه آھي.'),
(5946, 86, 15, 'بيشڪ ڪافر ھڪ (طرح جي) ڳجھي رٿ رٿين ٿا.'),
(5947, 86, 16, '۽ (آءٌ به) ھڪ (طرح جي) رٿ رٿيان ٿو.'),
(5948, 86, 17, 'پوءِ ڪافرن کي ڍر ڏي ۽ ٿوري مُدت کين ڇڏي ڏي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, '(اي پيغمبر) اُنھي پنھنجي تمام مٿاھين پالڻھار جي نالي جي پاڪائي بيان ڪر.'),
(5950, 87, 2, 'جنھن (ساري مخلوق کي) خلقيو پوءِ سنئين لڱين ڪيائين.'),
(5951, 87, 3, '۽ جنھن اندازو ڪيو پوءِ رستو ڏيکاريائين.'),
(5952, 87, 4, '۽ جنھن تازو گاھ ڄمايو.'),
(5953, 87, 5, 'پوءِ اُن کي سُڪو سڙيو ڪيائين.'),
(5954, 87, 6, 'اسين توکي (قرآن) پاڙھينداسون پوءِ نه وساريندين.'),
(5955, 87, 7, 'مگر جيڪي الله گھريو آھي، بيشڪ اُھو ظاھر کي ڄاڻندو آھي ۽ جيڪي ڳُجھو آھي تنھن کي (به).'),
(5956, 87, 8, '۽ آسان رستو توکي سنھنجو ڪري ڏينداسين.'),
(5957, 87, 9, 'پوءِ جيڪڏھن نصيحت ڏئڻ فائدو ڪري ته نصيحت ڪر.'),
(5958, 87, 10, 'جيڪو ڊڄندو سو سُتت سمجھ وٺندو.'),
(5959, 87, 11, '۽ اُن (نصيحت وٺڻ) کان (اُھو) تمام نڀاڳو پاسو ڪندو.'),
(5960, 87, 12, 'جو وڏيءَ باھ ۾ پوندو.'),
(5961, 87, 13, 'وري نڪي اُتي مرندو ۽ نڪي جيئرو رھندو.'),
(5962, 87, 14, 'بيشڪ اُھو ڇٽو جيڪو سڌريو.'),
(5963, 87, 15, '۽ پڻ پنھنجي پالڻھار جو نالو ياد ڪري نماز پڙھندو رھيو.'),
(5964, 87, 16, 'بلڪ دُنيا جي حياتيءَ کي (آخرت کان) پسند ڪندا آھيو.'),
(5965, 87, 17, '۽ (حقيقت ڪري) آخرت ڀَلي آھي ۽ ھميشه رھڻ واري آھي.'),
(5966, 87, 18, 'بيشڪ ھيءَ (بيان) پھرين صحيفن ۾ (به لکيل) آھي.'),
(5967, 87, 19, 'جي صحيفا ابراھيم ۽ مُوسىٰ جا آھن.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, '(اي پيغمبر) تو وٽ ڍڪيندڙ (قيامت) جي خبر پھتي آھي ڇا؟'),
(5969, 88, 2, 'اُن ڏينھن ڪيترائي مُنھن خوار ھوندا.'),
(5970, 88, 3, 'ڪمائيندڙ ڏک ڀوڳيندا ھوندا.'),
(5971, 88, 4, 'ٻرندڙ باھ ۾ پوندا.'),
(5972, 88, 5, 'بلڪل تتي چشمي مان پاڻي پياربن.'),
(5973, 88, 6, 'ڪنڊن واري گاھ کانسواءِ (ٻيو) ڪو کاڄ انھن لاءِ نه ھوندو.'),
(5974, 88, 7, '(جو اُھو) نڪي (کين) ڀُلھو ڪندو ۽ نڪي بُک لاھيندو.'),
(5975, 88, 8, 'ڪيئي مُنھن اُن ڏينھن تازا ھوندا.'),
(5976, 88, 9, 'پنھنجي ڪمائيءَ کان راضي ھوندا.'),
(5977, 88, 10, 'مٿاھين بھشت ۾ ھوندا.'),
(5978, 88, 11, 'اتي ڪا اجائي ڳالھ نه ٻڌندين.'),
(5979, 88, 12, 'اُتي چشما وھندڙ آھن.'),
(5980, 88, 13, 'اُتي وڏا تخت مٿي وڇايل.'),
(5981, 88, 14, '۽ آبخورا رکيل.'),
(5982, 88, 15, '۽ وھاڻا قطار ڪري رکيل.'),
(5983, 88, 16, '۽ غاليچا وڇايل آھن.'),
(5984, 88, 17, '(ماڻھو) اُٺن ڏانھن نه ڏسندا آھن ڇا؟ ته ڪيئن خلقيا ويا.'),
(5985, 88, 18, '۽ آسمان ڏانھن ته ڪيئن مٿي ڪيو ويو.'),
(5986, 88, 19, '۽ جبلن ڏانھن ته ڪيئن کڙا ڪيا ويا.'),
(5987, 88, 20, '۽ زمين ڏانھن ته ڪيئن وڇائي وئي.'),
(5988, 88, 21, 'پوءِ (اي پيغمبر) تون، رڳو نصيحت ڪرڻ وارو آھين.'),
(5989, 88, 22, 'تون مٿن ڪو داروغو نه آھين.'),
(5990, 88, 23, 'پر جنھن مُنھن موڙيو ۽ ڪُفر ڪيو.'),
(5991, 88, 24, 'تنھن کي الله وڏي عذاب جي سزا ڏيندو.'),
(5992, 88, 25, 'بيشڪ اسان ڏانھن سندن موٽڻ آھي.'),
(5993, 88, 26, 'وري بيشڪ سندن حساب وٺڻ اسان جي ذمي آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'پرھ جو قسم آھي.'),
(5995, 89, 2, '۽ ڏھن راتين جو قسم آھي.'),
(5996, 89, 3, '۽ ٻڌيءَ ۽ اِڪئيءَ جو قسم آھي.'),
(5997, 89, 4, '۽ رات جو قسم آھي جڏھن ھلي.'),
(5998, 89, 5, 'ھان ڌيان واري لاءِ ھن (قسمن) ۾ بيشڪ پورو قسم آھي.'),
(5999, 89, 6, 'نه ڏٺئي ڇا ته (قوم) عاد سان تنھنجي پالڻھار ڪيئن ڪيو.'),
(6000, 89, 7, '(جي) ارم وارا وڏن ٿنڀن (جي عمارتن) وارا ھوا.'),
(6001, 89, 8, 'جن جھڙو شھرن ۾ ڪونه بڻايو ويو آھي.'),
(6002, 89, 9, '۽ (قوم) ثمُود سان (ڪيئن ڪيائين) جن ميدان (قرٰا) ۾ پھڻن جون ڇپون گھڙيون ھيون.'),
(6003, 89, 10, '۽ ميخن واري فرعون سان (ڪئن ڪيائين).'),
(6004, 89, 11, 'انھن (مڙني ٽولين) جن شھرن ۾ سرڪشي ڪئي.'),
(6005, 89, 12, 'پوءِ منجھن گھڻو فساد ڪيائون.'),
(6006, 89, 13, 'تنھنڪري تنھنجي پالڻھار مٿن عذاب جو چھبڪ وھايو.'),
(6007, 89, 14, 'بيشڪ تنھنجو پالڻھار گھَٽَ (واري ھنڌ) ۾ (عملن کي ڏسندڙ) آھي.'),
(6008, 89, 15, 'پوءِ ماڻھو کي جڏھن سندس پالڻھار (ھن طرح) پرکيندو آھي جو اُن کي مانوارو ڪندو آھي ۽ نعمت ڏيندو اٿس، ته چوندو آھي ته منھنجي پالڻھار مون کي نوازيو.'),
(6009, 89, 16, '۽ جڏھن کيس (ٻئي طرح ھيئن) پرکيندو آھي جو مٿس روزي تنگ ڪندو آھي ته چوندو آھي ته منھنجي پالڻھار مون کي بي مانو ڪيو.'),
(6010, 89, 17, '(ھرگز) ائين نه آھي بلڪ ڇوري ٻار کي تعظيم نه ڏيندا آھيو.'),
(6011, 89, 18, '۽ نڪي مسڪينن جي کارائڻ تي ھڪ ٻئي کي رغبت ڏياريندا آھيو.'),
(6012, 89, 19, '۽ مُئن جو مال سموروئي ڳھي ويندا آھيو.'),
(6013, 89, 20, '۽ گھڻي پيار سان مال کي پيارو رکندا آھيو.'),
(6014, 89, 21, 'ائين نه ڪجي جڏھن زمين (جي اونچاڻ) ڪٽي ڪٽي سنئين ڪئي ويندي.'),
(6015, 89, 22, '۽ تنھنجو پالڻھار ايندو ۽ ملائڪ صِف صِف ٿي (ايندا).'),
(6016, 89, 23, '۽ اُن ڏينھن دوزخ کي آڻبو، اُن ڏينھن ماڻھو نصيحت وٺندو ۽ اُنھيءَ مھل (اھا نصيحت وٺڻ) اُن کي ڪٿي فائدي واري آھي.'),
(6017, 89, 24, 'چوندو ته ھاءِ ارمان جيڪر پنھنجي (ھن ھميشه جي) حياتيءَ لاءَ (ڪجھ) اڳي موڪليان ھا.'),
(6018, 89, 25, 'پوءِ انھيءَ ڏينھن الله جي عذاب جھڙو ڪوبه ڪونه عذاب ڪندو.'),
(6019, 89, 26, '۽ نڪو ڪو الله جي قيد ڪرڻ جھڙو قيد ڪندو.'),
(6020, 89, 27, '(مؤمنن کي چئبو ته) اي آرام وٺڻ وارا جيءُ.'),
(6021, 89, 28, 'تون پنھنجي پالڻھار ڏانھن موٽِ ھل (تون اُن کان) راضي ٿيل آھين (ھو توکان) راضي ٿيل آھي.'),
(6022, 89, 29, 'تنھنڪري (تون) منھنجي ٻانھن (جي ٽوليءَ) ۾ داخل ٿيءُ.'),
(6023, 89, 30, '۽ منھنجي بھشت ۾ گِھڙ.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'ھن شھر (مڪي) جو قسم کڻان ٿو.'),
(6025, 90, 2, '۽ تون ھن شھر ۾ (ته) رھندڙ آھين.'),
(6026, 90, 3, '۽ ڄڻيندڙ جو (قسم کڻان ٿو) ۽ ان جو جيڪو ڄايو.'),
(6027, 90, 4, 'ته بيشڪ ماڻھو کي ڏک ۾ (پوڻ لاءِ) خلقيوسون.'),
(6028, 90, 5, '(ماڻھو) ڀانئيندو آھي ڇا ته مٿس ڪوبه غالب نه ٿيندو؟'),
(6029, 90, 6, 'چوي ٿو ته مون تمام گھڻو مال کپايو.'),
(6030, 90, 7, 'ڀانئيندو آھي ڇا ته ڪنھن ڪونه ڏٺو اٿس؟'),
(6031, 90, 8, 'کيس ٻه اکيون نه ڏنيون اٿون ڇا؟'),
(6032, 90, 9, '۽ ڄڀ ۽ ٻه چَپ به؟'),
(6033, 90, 10, '۽ ٻه رستا ڏيکارياسونس.'),
(6034, 90, 11, 'پوءِ ڪوبه سخت لَڪ نه لنگھيو.'),
(6035, 90, 12, '۽ ڪنھن سمجھايئي ته سخت لَڪّ ڇا آھي؟'),
(6036, 90, 13, 'ٻانھو آجو ڪرڻ آھي.'),
(6037, 90, 14, 'يا بُک جي ڏينھن ۾ کاڄ کارائڻ.'),
(6038, 90, 15, 'مائٽي واري ڇوري ٻار کي.'),
(6039, 90, 16, 'يا مٽيءَ ۾ رُلندڙ محتاج کي.'),
(6040, 90, 17, 'ان کانپوءِ (اُھو) مؤمنن مان ھجي ۽ ھڪ ٻئي کي صبر جي وصيت ڪيائون ۽ (پڻ) ھڪ ٻئي کي (خلق تي) ٻاجھ ڪرڻ جي وصيت ڪيائون.'),
(6041, 90, 18, 'اُھي نيڪ بخت سڄي پاسي وارا آھن.'),
(6042, 90, 19, '۽ جن اسان جي آيتن جو انڪار ڪيو سي بدبخت کٻي وارا آھن.'),
(6043, 90, 20, 'مٿن دميل باھ چوڌاري ويڙھيل (موڪلبي) آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سج ۽ اُن جي سوجھري جو قسم آھي.'),
(6045, 91, 2, '۽ چنڊ جوقسم آھي جڏھن ان جي پوئتان اچي.'),
(6046, 91, 3, '۽ ڏينھن جو (قسم آھي) جڏھن سج کي روشن ڪري.'),
(6047, 91, 4, '۽ رات جو (قسم آھي) جڏھن سج کي ڍڪي.'),
(6048, 91, 5, '۽ آسمان جو ۽ اُن جو (قسم آھي) جنھن اُن کي بڻايو.'),
(6049, 91, 6, '۽ زمين جو ۽ اُنجو (قسم آھي) جنھن اُن کي پکيڙيو.'),
(6050, 91, 7, '۽ (ماڻھوءَ جي) جيءُ جو ۽ اُنجو (قسم آھي) جنھن اُن کي جوڙي برابر ڪيو.'),
(6051, 91, 8, 'پوءِ سندس بدڪاري ۽ سندس پرھيزگاريءَ (جي سڃاڻ) اندر ۾ وڌائينس.'),
(6052, 91, 9, 'جنھن اُن (نفس) کي (بدڪاريءَ کان) پاڪ ڪيو سو بيشڪ ڇٽو.'),
(6053, 91, 10, '۽ جنھن اُن کي (گناھن ۾) پوري ڇڏيو سو بيشڪ ٽوٽي وارو ٿيو.'),
(6054, 91, 11, '(قوم) ثمود پنھنجي سرڪشيءَ سببان (پيغمبر کي) ڪوڙو ڀانيو.'),
(6055, 91, 12, 'جڏھن منجھانئن بلڪل نڀاڳو اُٿيو.'),
(6056, 91, 13, 'پوءِ الله جي پيغمبر کين چيو ته الله جي ڏاچي ۽ اُن جي پاڻي پيڻ کي آڏو نه ٿيو.'),
(6057, 91, 14, 'پوءِ پيغمبر کي ڪوڙو ڀانيائون پوءِ ڏاچيءَ جو کُچون ڪپيائون، پوءِ سندن گناھن سببان سندن پالڻھار مٿن عذاب پلٽيو پوءِ اُن (سزا) کي (سڀني) تي ھڪ جھڙو ڪري ڇڏيائين.'),
(6058, 91, 15, '۽ (الله) اُن (سزا) جي پڇاڙيءَ کان نه ٿو ڊڄي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'رات جو قسم آھي جڏھن ڍَڪي ٿي.'),
(6060, 92, 2, '۽ ڏينھن جو (قسم آھي) جڏھن روشن ٿئي ٿو.'),
(6061, 92, 3, '۽ اُن جو (قسم آھي) جنھن نر ۽ مادي خلقي.'),
(6062, 92, 4, 'ته بيشڪ اوھان جي ڪوشش قسمين قسمين آھي.'),
(6063, 92, 5, 'پوءِ جنھن (الله لڳ) ڏنو ۽ پرھيزگاري ڪئي.'),
(6064, 92, 6, '۽ چڱائي کي سچ ڄاتائين.'),
(6065, 92, 7, 'تنھنکي (نجات جو) آسان رستو سھنجو ڪري ڏينداسين.'),
(6066, 92, 8, '۽ جنھن بخل ڪيو ۽ (پاڻ کي) بي پرواھ ڄاتائين.'),
(6067, 92, 9, '۽ چڱائي کي ڪوڙ ڀانيائين.'),
(6068, 92, 10, 'تنھن کي اوکائيءَ (۾ پوڻ) لاءِ (رستو) آسان ڪري ڏينداسين.'),
(6069, 92, 11, '۽ جڏھن اونڌو ڪرندو ته سندس مال کانئس (ڪجھ) نه ٽاريندو.'),
(6070, 92, 12, 'بيشڪ سڌو رستو ڏيکارڻ اسان تي (لازم) آھي.'),
(6071, 92, 13, '۽ بيشڪ آخرت ۽ دُنيا (ٻئي) اسان جي وس ۾ آھن.'),
(6072, 92, 14, 'پوءِ اوھان کي باھ کان ڊيڄاريم جا ڄڀي ڪري ٿي.'),
(6073, 92, 15, '(اھڙي) وڏي نڀاڳي کانسواءِ (ٻيو) ڪو اُن ۾ ڪونه گھڙندو.'),
(6074, 92, 16, 'جنھن (اسلام کي) ڪوڙ ڄاتو ۽ پٺيرو ٿيو.'),
(6075, 92, 17, '۽ اُن (باھ) کان انھي وڏي پرھيزگار کي پاسي ڪبو.'),
(6076, 92, 18, 'جيڪو پنھنجو مال (الله جي واٽ ۾) ڏئي ٿو ته پاڪ ٿئي.'),
(6077, 92, 19, '۽ مٿس اھڙي ڪنھن جو ڪو احسان ٿيل نه آھي جو بدلو ڏجي.'),
(6078, 92, 20, 'پر پنھنجي تمام مٿاھين پالڻھار جي رضامندي طلبڻ لاءِ (ڏئي ٿو).'),
(6079, 92, 21, '۽ ضرور جلد خوش ٿيندو.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'مٿي آيل ڏينھن جو قسم آھي.'),
(6081, 93, 2, '۽ رات جو (قسم) آھي، جڏھن ڍَڪي ٿي.'),
(6082, 93, 3, 'ته (اي پيغمبر) تنھنجي پالڻھار توکي نه ڇڏيو ۽ نڪي ناراض ٿيو.'),
(6083, 93, 4, '۽ بيشڪ آخرت تولاءِ دنيا کان گھڻو چڱي آھي.'),
(6084, 93, 5, '۽ تنھنجو پالڻھار سگھوئي توکي (ايترو) ڏيندو جو (تون) خوش ٿيندين.'),
(6085, 93, 6, 'توکي يتيم نه ڏٺائين ڇا پوءِ (توکي) جاءِ ڏنائين.'),
(6086, 93, 7, '۽ توکي ناواقف ڏٺائين پوءِ (شريعت جي) رستي لاتائين.'),
(6087, 93, 8, '۽ توکي مسڪين ڏٺائين پوءِ آسودو ڪيائين.'),
(6088, 93, 9, 'تنھنڪري يتيم تي تون ڏاڍ نه ڪر.'),
(6089, 93, 10, '۽ سُوالي کي نه جھڻڪ.'),
(6090, 93, 11, '۽ پنھنجي پالڻھار جي نعمت جو بيان ڪندو رھ.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, '(اي پيغمبر) تنھنجو سِينو تولاءِ ڪشادو نه ڪيو اٿون ڇا؟'),
(6092, 94, 2, '۽ تنھنجو (اُھو) بار توکان لاٿوسون.'),
(6093, 94, 3, 'جنھن تنھنجي پٺي کي ڳرو ڪيو ھو.'),
(6094, 94, 4, '۽ تنھنجي ساراھ تولاءِ مٿاھين ڪئي سون.'),
(6095, 94, 5, 'ڇوته بيشڪ اھنج سان گڏ سھنج آھي.'),
(6096, 94, 6, 'بيشڪ اھنج سان گڏ سھنج آھي.'),
(6097, 94, 7, 'تنھنڪري جڏھن واندو ٿئين ته (عبادت لاءِ) محنت ڪر.'),
(6098, 94, 8, '۽ خاص پنھنجي پالڻھار ڏانھن دل لڳاءِ.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'انجير ۽ زيتون جو قسم آھي.'),
(6100, 95, 2, '۽ طُور سينا جو.'),
(6101, 95, 3, '۽ ھِن اَمن واري شھر (مڪي) جو.'),
(6102, 95, 4, 'ته بيشڪ ماڻھوءَ کي تمام سھڻي نموني پيدا ڪيوسون.'),
(6103, 95, 5, 'وري سڀني ھيٺاھين کان ھيٺ موٽايوسونس.'),
(6104, 95, 6, 'پر جن ايمان آندو ۽ چڱا ڪم ڪيا تن لاءِ اَڻ کُٽ اجر آھي.'),
(6105, 95, 7, 'پوءِ (اي ماڻھو) ھن (نصيحت) پڄاڻا ڪھڙي شيءِ توکي عملن جي بدلي ملڻ بابت منڪر بڻائي ٿي؟'),
(6106, 95, 8, 'حاڪمن جو وڏو حاڪم الله نه آھي ڇا؟');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, '(اي پيغمبر) پنھنجي پالڻھار جي نالي سان پڙھ جنھن (سارو جڳ) خلقيو.'),
(6108, 96, 2, 'ماڻھوءَ کي رت جي دِڳ مان بڻايائين.'),
(6109, 96, 3, 'پڙھ ۽ تنھنجو پالڻھار ڏاڍو سڳورو آھي.'),
(6110, 96, 4, 'جنھن (ماڻھوءَ کي علم) قلم سان سيکاريو.'),
(6111, 96, 5, 'ماڻھوءَ کي اُھو سيکاريائين جو نه ڄاڻندو ھو.'),
(6112, 96, 6, 'سچ آھي ته بيشڪ ماڻھو سرڪشي ڪندو آھي.'),
(6113, 96, 7, 'جڏھن اُھو پاڻ کي بي پرواھ ڏسندو آھي.'),
(6114, 96, 8, 'بيشڪ تنھنجي پالڻھار ڏانھن موٽي وڃڻو آھي.'),
(6115, 96, 9, 'اُھو ڏٺو اٿئي ڇا جيڪو جھليندو آھي.'),
(6116, 96, 10, '(منھنجي) ٻانھي کي جڏھن اھو نماز پڙھندو آھي.'),
(6117, 96, 11, 'ڏٺو اٿئي ڇا ته جيڪڏھن (اُھو ماڻھو) سڌي رستي تي ھجي ھا.'),
(6118, 96, 12, 'يا پرھيزگاريءَ جو حڪم ڪري ھا.'),
(6119, 96, 13, 'ڏٺو اٿئي ڇا ته جيڪڏھن ڪوڙ ڀانيائين ۽ پٺيرو ٿيو.'),
(6120, 96, 14, 'ته (ھيُ) نه ڄاتائين ڇا ته الله ڏسي ٿو.'),
(6121, 96, 15, 'ائين نه آھي! قسم آھي ته جيڪڏھن پاڻ نه پليندو، ته ضرور پيشانيءَ جي وارن کان وٺي گھلينداسونس.'),
(6122, 96, 16, 'اُھا پيشاني جا ڪوڙي گنھگار آھي.'),
(6123, 96, 17, 'پوءِ ڀلي ته پنھنجي ڪچھريءَ وارن کي سڏي.'),
(6124, 96, 18, 'اسين به دوزخ جي داروغن کي سڏينداسون.'),
(6125, 96, 19, 'ائين نه آھي، تون چيو نه مڃيس ۽ سجدو ڪر ۽ (الله کي) ويجھو ٿيءُ.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'بيشڪ اسان قرآن شب قدر ۾ لاٿو.'),
(6127, 97, 2, '۽ ڪنھن سمجھايئي ته شب قدر ڇا آھي.'),
(6128, 97, 3, 'شب قدر، ھزار مھينن کان ڀلي آھي.'),
(6129, 97, 4, 'منجھس ملائڪ ۽ رُوح پنھنجي پالڻھار جي حڪم سان سڀڪنھن ڪم (جي پورائي) لاءِ لھندا آھن.'),
(6130, 97, 5, 'اُنھيءَ رات پرھ ڦُٽڻ تائين سلامتي آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'ڪتاب وارن ۽ مشرڪن مان جن انڪار ڪيو سي (ايسين تائين پنھنجي رسم کان) الڳ ٿيڻ وارا نه ھوا جيسين تائين وٽن پڌري حُجّت (نه) اچي.'),
(6132, 98, 2, '(يعني) الله جو پيغمبر جو پاڪ صحيفا پڙھي.'),
(6133, 98, 3, 'جن ۾ سڌا سنوان لکيل حُڪم آھن.'),
(6134, 98, 4, '۽ جن کي ڪتاب ڏنو ويو سي پاڻ وٽ پڌري حُجّت جي اچڻ کانپوءِ مُختلف ٿيا.'),
(6135, 98, 5, '۽ ھن کانسواءِ (ٻيو) ڪو حڪم نه ڪيو وين ته باطل کان مُنھن موڙي الله جي عبادت ڪن ۽ نماز پڙھندا رھن ۽ زڪوٰة ڏيندا رھن ۽ اھو آھي سڌو دين.'),
(6136, 98, 6, 'بيشڪ ڪتاب وارن ۽ مشرڪن مان جن ڪفر ڪيو سي دوزخ جي باھ ۾ پوندا منجھس سدائين رھندڙ ھوندا، اھي ئي (ساريءَ) خلق کان (تمام) بڇڙا آھن.'),
(6137, 98, 7, 'بيشڪ جن ايمان آندو ۽ چڱا ڪم ڪيا اھي ئي (ساريءَ) خَلق کان ڀَلا آھن.'),
(6138, 98, 8, 'سندن بدلو سندن پالڻھار وٽ ھميشه جي رھڻ وارا باغ آھن جن جي ھيٺان واھيون پيون وھن انھن ۾ ھميشه سدائين رھندا، الله کائن راضي ٿيو ۽ اُھي کانئس راضي ٿيا، اھو (وعدو) انھيءَ لاءِ جيڪو پنھنجي پالڻھار کان ڊنو.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'جڏھن زمين پنھنجي سخت ڌوڏجڻ سان ڌوڏبي.'),
(6140, 99, 2, '۽ زمين پنھنجا بار ٻاھر ڪڍندي.'),
(6141, 99, 3, '۽ ماڻھو چوندو ته زمين کي ڇا ٿيو آھي.'),
(6142, 99, 4, 'اُن ڏينھن (اُھا) پنھنجيون سڀ خبرون بيان ڪندي.'),
(6143, 99, 5, 'ھن ڪري جو تنھنجي پالڻھار ڏانھس حُڪم ڪيو.'),
(6144, 99, 6, 'اُن ڏينھن ماڻھو ڌار ڌار ٿي ورندا، ته سندس ڪم کين ڏيکارجن.'),
(6145, 99, 7, 'پوءِ جنھن ذري جيترو چڱو ڪم ڪيو ھوندو سو اُن کي ڏسندو.'),
(6146, 99, 8, '۽ جنھن ذري جيترو بد ڪم ڪيو ھوندو سو اُن کي ڏسندو.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'ھانبارو ھڻي ڊوڙندڙن گھوڙن جو قَسم آھي.'),
(6148, 100, 2, 'پوءِ پھڻن تي نعلن ھڻڻ سان چڻنگن ڪڍندڙن (گھوڙن) جوقسم آھي.'),
(6149, 100, 3, 'پوءِ صبح جي مھل لُٽ ڪندڙ (گھوڙن) جو قسم آھي'),
(6150, 100, 4, 'جو اُن مھل رئي اُٿارين.'),
(6151, 100, 5, 'پوءِ اُن گھڙي (دشمنن جي) ٽوليءَ جي وچ ۾ پون.'),
(6152, 100, 6, 'ته بيشڪ ماڻھو پنھنجي پالڻھار جو وڏو بي شُڪر آھي.'),
(6153, 100, 7, '۽ بيشڪ اُھو پاڻ انھن تي شاھد آھي.'),
(6154, 100, 8, '۽ بيشڪ اھو مال جي محبت ۾ ڏاڍو آھي.'),
(6155, 100, 9, '(اُنھيءَ وقت کي) نه ڄاڻندو آھي ڇا جنھن وقت انھن کي اُٿاربو جيڪي قبرن ۾ نه آھن.'),
(6156, 100, 10, '۽ جيڪي سينن ۾ آھي سو پڌرو ڪبو.'),
(6157, 100, 11, 'بيشڪ اُن ڏينھن سندن پالڻھار سندن (حال جي) خبر رکندڙ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'سخت ٺوڪيندڙ.'),
(6159, 101, 2, '(اُھا) سخت ٺوڪيندڙ ڇا آھي؟'),
(6160, 101, 3, '۽ ڪنھن سمجھايئي ته (اُھا) سخت ٺوڪيندڙ ڇا آھي؟'),
(6161, 101, 4, 'جنھن ڏينھن ماڻھو پکيڙيلن پتنگن جان ھوندا.'),
(6162, 101, 5, '۽ جبل تنبيل رڱيل اُن جان ھوندا.'),
(6163, 101, 6, 'پوءِ جنھن (جي عملن) جا ترارا ڳرا ٿيا.'),
(6164, 101, 7, 'سو دلپسند گذران ۾ ھوندو.'),
(6165, 101, 8, '۽ جنھن (جي عملن) جا ترارا ھلڪا ٿيا.'),
(6166, 101, 9, 'تنھن (جي رھڻ) جي جاءِ ھاوِيَه آھي.'),
(6167, 101, 10, '۽ ڪنھن سمجھايئي ته ھاويه ڇا آھي؟'),
(6168, 101, 11, 'ھڪ ٻرندڙ باھ آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, '(اي ماڻھو) پاڻ ۾ گھڻائي (تي مرڪڻ) اوھان کي ويسلو ڪيو.'),
(6170, 102, 2, 'تان جو قبرن کي ڏٺوَ.'),
(6171, 102, 3, 'ائين نه آھي سگھوئي ڄاڻندؤ.'),
(6172, 102, 4, 'وري (چئون ٿا ته) سگھوئي ڄاڻندؤ.'),
(6173, 102, 5, 'نه نه! جيڪڏھن پڪي ويساھ سان ڄاڻو ھا (ته ويسلو نه ويھو ھا).'),
(6174, 102, 6, 'قسم آھي ته ضرور دوزخ کي ڏسندؤ.'),
(6175, 102, 7, 'وري به ان کي يقين واريءَ اک سان ضرور ڏسندؤ.'),
(6176, 102, 8, 'وري اُن ڏينھن نعمتن بابت اوھان کان ضرور پڇبو.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'پوياڙيءَ جو قسم آھي.'),
(6178, 103, 2, 'ته بيشڪ ماڻھو گھاٽي ۾ آھي.'),
(6179, 103, 3, 'سواءِ انھن جي جن ايمان آندو ۽ چڱا ڪم ڪيا ۽ ھڪ ٻئي کي سچ جي وصيّت ڪيائون، ۽ ھڪ ٻئي کي صبر جي وصّيت ڪيائون.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, '(انھيءَ) سڀڪنھن گلا ڪندڙ عيب ڳوليندڙ لاءِ ويل آھي.'),
(6181, 104, 2, 'جنھن مال گڏ ڪيو ۽ اُھو ڳڻي ڳڻي رکيائين.'),
(6182, 104, 3, 'ڀانئيندو آھي ته سندس مال سدائين رھندو.'),
(6183, 104, 4, 'ھرگز ائين نه آھي ضرور حُطمه ۾ اُڇلائبس.'),
(6184, 104, 5, '۽ ڪنھن سمجھايئي ته حُطَمَه ڇا آھي؟'),
(6185, 104, 6, 'الله جي ٻاريل باھ آھي.'),
(6186, 104, 7, 'جا دلين تي پھچندي.'),
(6187, 104, 8, 'بيشڪ اُھا مٿن در بند ڪيل آھي.'),
(6188, 104, 9, '(جنھن حالت ۾ اُھي) ڊگھن ٿنڀن ۾ (ٻڌل ھوندا).');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, '(اي پيغمبر) نه ڏٺئي ڇا؟ ته تنھنجي پالڻھار ھاٿي وارن سان ڪئن ڪيو؟'),
(6190, 105, 2, 'سندن بڇڙي رٿ کي ناس نه ڪيائين ڇا؟'),
(6191, 105, 3, '۽ وَلر وَلر پکي مٿن موڪليائين.'),
(6192, 105, 4, 'جن ڪنڪريٽ جون پھڻيون کين ھنيون ٿي.'),
(6193, 105, 5, 'پوءِ اولڙ گاھ جان کين ڪيائين.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'قريشين کي اُلفت ڏيارڻ لاءِ.'),
(6195, 106, 2, 'جو سياري ۽ اونھاري جي سفر سببان کين اُلفت ڏيارڻ آھي.'),
(6196, 106, 3, 'تنھنڪري گُھرجين ته ھِن گھر جي (انھيءَ) پالڻھار جي عبادت ڪن.'),
(6197, 106, 4, 'جنھن کين بُک ۾ کاڄ کاريايو، ۽ کين ڀؤ کان امن ڏنو.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, '(اي پيغمبر) انھي کي ڏٺيءِ ڇا جيڪو عملن جي بدلي ملڻ کي ڪوڙ ڀانئيندو آھي.'),
(6199, 107, 2, 'پوءِ اُھو آھي جو ڇوري ٻار کي ڌِڪيندو آھي.'),
(6200, 107, 3, '۽ (ڪنھن کي) مسڪين جي کارائڻ تي رغبت نه ڏياريندو آھي.'),
(6201, 107, 4, 'پوءِ انھن نمازين لاءِ وَيل آھي.'),
(6202, 107, 5, 'جي پنھنجين نمازن کان ويسلا رھندا آھن.'),
(6203, 107, 6, 'اُھي جيڪي رياءُ ڪندا آھن.'),
(6204, 107, 7, '۽ اُڌاريون شيون جھليندا آھن.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, '(اي پيغمبر) بيشڪ اسان توکي ڪوثر عطا ڪيو.'),
(6206, 108, 2, 'تنھنڪري پنھنجي پالڻھار جي لاءِ نماز پڙھ ۽ قرباني ڪر.'),
(6207, 108, 3, 'بيشڪ تنھنجو ويري ئي بي نسل آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, '(اي پيغمبر ڪافرن کي) چؤ ته اي ڪافرؤ.'),
(6209, 109, 2, 'نڪي آءٌ ان جي عبادت ڪندس جنھنجي اوھين عبادت ڪندا آھيو.'),
(6210, 109, 3, '۽ نڪي اوھين ان جي عبادت ڪندؤ جنھن جي آءٌ عبادت ڪندو آھيان.'),
(6211, 109, 4, '۽ نڪي آءٌ ان جي عبادت ڪندس جنھنجي اوھان عبادت ڪئي.'),
(6212, 109, 5, '۽ نڪي اوھين ان جي عبادت ڪندؤ جنھنجي آءٌ عبادت ڪندو آھيان.'),
(6213, 109, 6, 'اوھان لاءِ اوھان جو دين ۽ مون لاءِ منھنجو دين آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'جڏھن الله جي مدد ۽ سوڀ اچي،'),
(6215, 110, 2, '۽ ماڻھن کي الله جي دين ۾ ٽوليون ٽوليون ٿي گھڙندو ڏسين،'),
(6216, 110, 3, 'تڏھن (تون) پنھنجي پالڻھار جي ساراھ سان گڏ پاڪائي بيان ڪر ۽ کانئس بخشش گھر. بيشڪ اُھو ٻاجھ سان موٽندو آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'ابولھب جا ھٿ ڀڳا ۽ (پاڻ به) ھلاڪ ٿيو.'),
(6218, 111, 2, 'سندس مال ۽ جيڪي ڪمايائين تنھن کانئس (الله جو عذاب) نه ٽاريو.'),
(6219, 111, 3, 'ڇيئي واري باھ ۾ سگھو پوندو.'),
(6220, 111, 4, '۽ سندس زال به، جا ڪاٺين (جي) لَڏ کڻندڙ آھي.'),
(6221, 111, 5, 'جنھن جي ڳچيءَ ۾ کاٻر جو نوڙ پيل آھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, '(اي پيغمبر) چؤ ته الله اڪيلو آھي.'),
(6223, 112, 2, 'الله بي احتياج آھي.'),
(6224, 112, 3, 'نڪي ڄڻيائين، ۽ نڪي ڄڻيو ويو.'),
(6225, 112, 4, '۽ سندس برابر ڪوبه ڪونھي.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, '(اي پيغمبر) چؤ ته صبح جي پالڻھار جي پناھ گھران ٿو.'),
(6227, 113, 2, 'جيڪي پيدا ڪيو اٿس تنھنجي بڇڙائي کان.'),
(6228, 113, 3, '۽ اونداھي ڪندڙ (رات) جي بڇڙائي کان جڏھن اُھا پکڙجي.'),
(6229, 113, 4, '۽ ڳنڍين ۾ ڦوڪيندڙين (عورتن) جي بڇڙائي کان.'),
(6230, 113, 5, '۽ مَسَھُوء جي بڇڙائيءَ کان جڏھن (اُھو) مَسَھائي ڪري.');
INSERT INTO `sd_amroti` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, '(اي پيغمبر) چؤ ته ماڻھن جي پالڻھار جي پناه گھران ٿو.'),
(6232, 114, 2, 'ماڻھن جي بادشاھ جي.'),
(6233, 114, 3, 'ماڻھن جي (سچي) معبود جي.'),
(6234, 114, 4, 'پٺ تي ھٽي ويندڙ (شيطان) جي وَسَوَسن جي بڇڙائي کان.'),
(6235, 114, 5, 'جيڪي ماڻھن جي سينن ۾ وَسَوَسو وجھي ٿو.'),
(6236, 114, 6, 'جو جنن (جي جنس) مان ھجي يا ماڻھن (جي جنس) مان.');
