INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'Magaca Eebe yaan kubillaabaynaa ee Naxariis guud iyo mid gaaraba Naxariista.'),
(2, 1, 2, 'Mahad Eebaa iska leh ee barbaariyaha Caalamka ah (Koonka).'),
(3, 1, 3, 'Ee naxariis guud iyo mid gaaraba naxariista.'),
(4, 1, 4, 'Ee hanta maalinta abaalmarinta (Qiyaamada).'),
(5, 1, 5, 'adiga unbaan ku caabudaynaa, adiga unbaana kaa kaalmo waydiisanaynaa (Eebow).'),
(6, 1, 6, 'ee nagu hanuuni jidka toosan (Xaqa).'),
(7, 1, 7, 'Jidka kuwii aad unicmaysay eedan u cadhoonin dhuminna.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'Aad baa Macnaheeda looga hadlay, waxaana la sheegay inay magaca suuradda tahay, ama ay ku tusin Muejisadda Quraanka, Eebe unbaa waa la dhahaa.'),
(9, 2, 2, 'Kitaabkaas (Quraanka) shaki kuma jiro Hanuunbaana ugu sugan kuwa dhawrsada.'),
(10, 2, 3, 'ee ah kuwa rumeeya waxan la arkayn (ee xaq ah) oogana Salaadda, waxaan ku arzaaqnayna wax ka bixiya.'),
(11, 2, 4, 'ee ah kuwa rumeeya waxa lagugu dajiyey Nabiyow, iyo wixii la dajiyey horay, Aakhirana yaqiinin.'),
(12, 2, 5, 'kuwaasi hanuun Eebe yey ku suganyihiin, kuwaasina waa uun kuwa liibaanay.'),
(13, 2, 6, 'kuwii Gaaloobay waxaa isugumid ah udigtayaa iyo umaadan diginaa mana rumeeyaan (xaqa).'),
(14, 2, 7, 'Lebaa daabacay Quluubtooda iyo Maqalkooda. aragoodana wuxuu yeeley Dabool waxaana u sugnaaday Cadaab weyn.'),
(15, 2, 8, 'Dadka waxaa ka mid ah kuwo dhihi waxaan rumeynay Eebe iyo maalintii dambaysay (Qiyaamada) haddana aan Mu\'miniin ahyn.'),
(16, 2, 9, 'waxay khiyaameyn Eebe iyo kuwa (Xaqa) rumeeyey naftooda ahayn mase kasayaan.'),
(17, 2, 10, 'Quluubtay ka bukaan markaasaa Eebe u siyaadiyay Cuduro waxayna mudan Cadaab daran Beentoodii Darteed.'),
(18, 2, 11, 'marka lagu dhoho ha fasaadinina dhulka waxay dhahaan annagu waxaan uun nahay Hagaajiyayaal.'),
(19, 2, 12, 'iyaga unbaa fasaadiyayaal ah hasa yeeshee ma kasayaan.'),
(20, 2, 13, 'marka lagu dhaho rumeeya sida Dadku u rumeeyey waxay dhihi mawaxaannu u rumayn sida sufahada (Caqli gaabka), waxaase sufaha ah iyaga uun laakiin ma oga.'),
(21, 2, 14, 'markay la kulmaan kuwa ru-meeyey (Xaqa) waxay dhihi waan rumaynay, markay la kaliyoobaan shaydaamiidana waxay dhahaan annagu waan idinla Jirraa, ee waxaan ahayn uun kuwa ku jees jeesa (Mu\'miniinta).'),
(22, 2, 15, 'Eebaa ja abaal marin Jees jeeska, wuxuuna u siyaadin kibirkooda iyo Baadidooda, iyagoo ku dhexwareeri.'),
(23, 2, 16, 'kuwaase waxay ku gateen haadida hanuunka, mana ribxin (faa\'iidin) ganacsigoodu mana hanuunsana.'),
(24, 2, 17, 'waxay la mid yihiin mid huriyey Dab, markuu iftiimiyay gaararkoodana yaa Eche la tagay Nuurkoodii ugagana tadax arkayn.'),
(25, 2, 18, 'waa Dhaga la\'aan, Hadala\'aan, aragla\'aan, xaqana uma soo noqdaan.'),
(26, 2, 19, 'wuxuu la midyahay xaalkoodu sidii Roob Samada ka soo degay oo Mugdiyo onkod iyo Hillaac leh, oy yeelayaan Farahooda Dhagahooda hanqadhka (Danabka) xaggiisa geeri ka digtooni darteed, Eebana waa koobay Gaalo.'),
(27, 2, 20, 'wuxuu u dhawyahay hillaacu (xaqu) inuu dafo aragooda, markastoos u Ifo way ku socdaan, markuu ku mugdiyoobana way istaagaan, hadduu Eebe doonana wuxuu la tagaa Maqalkooda iyo Aragooda Eebana wax walba waa karaa (oos doonoo).'),
(28, 2, 21, 'Dadow caabuda Echihiinna idin abuuray idinka iyo kuwii idinka horrecyey, waxaadna mudataan inaad dhowrsataan.'),
(29, 2, 22, 'Eebaha Dhulka idiin ka yeelay Gogol, Samadana dhismo, idiinkanasoo dajiyey Samada Biyo, kuna soo bixiyey Midho si laydiinku arzuqo ee hayeelina Eebe kuwa la mid ah idiinkoo og.'),
(30, 2, 23, 'haddaad shakisantihiin waxaan ku dejinay addoonkanaga (Nabiga) keena Suurad la mid ah, una yeedha markhaatiyadin (kaalmeeyayaashiinna) ee Eebe ka soo hadhay haddaad runlowtihiin.'),
(31, 2, 24, 'haddaydaan falin mana falaysaane ka dhawrsada Naar lagu shido Dadka iyo Dhagaxa, loona darbay gaalada.'),
(32, 2, 25, 'ugu bishaaree kuwa rumeeyey (xaqa) Camal fiicanna falay inay mudan Janooyin ay socoto dhexdeeda Wabiyaalkii, markastoo laga arxaaqo xaggeeda midho waxay dhahaan kani waa kii horay naloogu arzuqay, waxaa la siiyey isagoo isu eg (araga) waxayna dhexdeeda ku leeyihiin Haween (Janno) oo nadiif ah, dhexdeedana way ku waari.'),
(33, 2, 26, 'Eebe kama xishoodo (reebtoonaado) inuu tusaale u yeelo kaneeco iyo waxkasareeyaba, kuwa (xaqa) ru-meeyeyse waxay ogyihiin inuu xaqyahay (Quraanku) kana yimid xagga Eebahood kuwa gaaloobayse waxay dhihi muxuu Eebe ula jeedaatusaalahan, wuxuu ku dhumin Eebe in badan wuxuuna ku hanuunin in badan waxaan faasiqiin ahayna kuma dhumiyo.'),
(34, 2, 27, 'ee ah kuwa buriya ballanka Eebe intay adkeeyeen ka dib, oo gooya wuxuu faray Eebe in la xidhiidhiyo oo fasaadiya Dhulka kuwaasu waa kuwa khasaaray.'),
(35, 2, 28, 'seed uga gaaloobaysaan Eebe idinkoo ahaa waxaan noolayn, markaas Eebe idin soo noolayn ka dibna xaggiisa laydiin eelin.'),
(36, 2, 29, 'Eebe waa kan idiin abuuray waxa dhulka ku sugan dhamaan, markaas Samada ahaysiiyey kana dhigay Todobo Samo, Eebe wax walbana waa ogyahay.'),
(37, 2, 30, 'xus markuu ku yidhi Eebahaa Malaa\'igta waxaan yeeli Dhulka cid u hadha, oyna dheheen ma waxaad yeeli dhexdeeda cid fasaadisa oo kudaadisa dhiig annagoo ku wayneynayna markaasuu yidhi anigu waxaan ogahay waxaydaan ogayn.'),
(38, 2, 31, 'Eebe wuxuu baray Nabi Aadam magacyada dhammaan, markaasuu u bandhigay Eebe malaa\'igtakuna yidhi\'iiga warama magacyada kuwaas hadaad runlowtihiin.'),
(39, 2, 32, 'waxayna dheheen nasahnidaada Eebow, cilmi malihin waxaad na barto mooyee waxaad tahay adigu wax oge falsan.'),
(40, 2, 33, 'wuxuuna yidhi Eebe Aadamow uga warran magacyadooda, markuu magacyadooda uga warramay yuu yidhi Eebe miyaanan idiin ku dhihin anigu waxaan ogahay waxa ku maqan Samooyinka. iyo inaan ogahay waxaad muujinaysaan iyo waxaad qarsanaysaan.'),
(41, 2, 34, 'xus markaan ku nidhi Malaa\'igay u sujuuda Aadam, markaasay sujnudeen Ibliis mooye, wuu diiday wuuna iskibriyay, wuxuuna ka midnoqday galaada.'),
(42, 2, 35, 'waxaana nidhi Aadamow dag adida iyo Haweenaydaadu janada, kana cuna xageeda (cunno) shifo oo waasac ah meeshaad doontaan hana u dhawaanina geedan oo markaas aad kamid noqotaan daalimiinta.'),
(43, 2, 36, 'Waxaana ka fogeeyey xagooda Jannada shaydaan wuxuuna ka bixiyey waxay ku sugnaayeen, waxaana ku nidhi ka hoobta Jannada, qaarkiinna dhulka idiinku sugnaaday meel aad ku nagaataan iyo raaxo tan iyo waqti.'),
(44, 2, 37, 'waxuuna kala kulmay Aadam xagga Eebe kalimooyin, wuuna ka toobad aqbalay Eebana waa toobad aqbalbadane waana naxariiste.'),
(45, 2, 38, 'waxaan ku nidhi ka hoobta xaggeeda dhamaantiin, hadduu idiinka yi-maaddo xaggayga hanuun ruuxii raaca hanuunka cabsi korkiisa ma ahaato iyo murug midna.'),
(46, 2, 39, 'kuwa gaaloobayse oo beeniyey aayaadkanaga, kuwaasu waa chclunaar dhexdeedana way ku waari.'),
(47, 2, 40, 'Bini Israa\'iilow xusa nicmadayda aan idiinku nicmeeyey, oofiyana ballankaygii aan oofiyo ballankiina, aniga uun igacabsada.'),
(48, 2, 41, 'rumeeyana waxaan dejiyey isagoo rumayn waxaad haysataan, hana noqonina mid ka gaaloobay quraanka kii ugu horreeyay, hana ku gadanina aayaadkayga qiimo yar (aduunka) aniga uun iga dhawrsada.'),
(49, 2, 42, 'hana ku khaldina xaqa baadilka, ood qarisaan xaqa idinkoo og.'),
(50, 2, 43, 'ooga salaada, hixiyana sakada, lana rukuuca (lukada).'),
(51, 2, 44, 'ma waxaad faraysaan dadka wanaaga od halmaansantihin naftiina idinkoo akhrin Kitaabka miyeydaan wax ka sayn.'),
(52, 2, 45, 'kaalmaysta samirka iyo salaadda wayna ku wayn tahay salaaddu (ku culustahay) kuwa khushuuca mooyee.'),
(53, 2, 46, 'kuwa citiqaadsan inay la kulmi Echahood xagiisana loo celin.'),
(54, 2, 47, 'Bini\'israa\'iillow xusa nicmadeydii aan idiinku nicmeeyey, iyo inaan idinka fadilay caalamkii (markaas).'),
(55, 2, 48, '48 kana dhawrsada maalin aan naf abaalmarinayn naf (kale) qiyaamada aan lagana aqbalayn shafeeeo lagana qaadayn (ogalayn) furasho loona gargaarayn.'),
(56, 2, 49, 'xusa markaan idinka korinay fircoon iyo colkiisii ce idin dhadhansiin jiray cadaab daran, ayna gawrici jireen wiilashiina ayna dayn jireen gabdhihiina, taasina waxaa ku sugan irntixaam wayn oo Eebihiin.'),
(57, 2, 50, 'xusa markaan idiin dhanballay badda oon idin korinay oona maanshaynay fircoon iyo colkiisii idinkoo cegaya.'),
(58, 2, 51, 'xusa markaan u yaboohnay (Nabi) muuse afartan habeen, markaas aad yeelateen dibi (aad caabudeen) gadaashiis idinkoo daalimiin ah.'),
(59, 2, 52, 'markaas aan idin saamaxnay arrintaas ka dib si aad u mahdisaan.'),
(60, 2, 53, 'xusa markaan siinay (Nabi) muusc kitaab iyo kala bixiyc (xaqa iyo baadilka) si aad u hanuuntaan.'),
(61, 2, 54, 'xusa markuu (Nabi) muuse ku yidhi qoonkisa qoonkiisa qoonkayiw waxaad dulmideennaftiina yeelashadiini dibiga (caabudo) ee u toobadkeena Eebihiina dilana naftiina, saasaa idinku khayr badan Eebihiin agtiis, markaasu idinka toobad aqbalay maxaa yeelay Eche waa toobad aqbal badane naxariista.'),
(62, 2, 55, 'xusa markaad dhahdeen muusow kuma rumaynayno intaan ka aragno Eebe si cadaan ah, markaasay idin qabatay qaylo iyo naar idinkoo eegi.'),
(63, 2, 56, 'markaasaan idin soo bixinay geeridiinii ka dib si aad u mahadisaan.'),
(64, 2, 57, 'waxaan idinku haddaynay Daruur, waxaan idinku soo dajinay macaan iyo hilib (shimbireed) waxaana idinku nidhi wax ka cuna wanaaggan idiinku arzaaqnay, mana ayan na dulmin, laakiin waxay ahaayeen naftooda uun kuwa dulmiya.'),
(65, 2, 58, 'Xusa markaan nidhi gala magaaladan oo kacuna xageeda Meejaad doontaan barwaaqa ah. kana gala irridda idinkoo sujuudsan dhahana (Eebow) hoobi (danbi) aan idiin dhaafno gafafkiinee. waxaan u siyaadinayna samafalayaasha (wanaag).'),
(66, 2, 59, 'markaasay ku badaleen kuwwi dulmi falay hadalkii loo yidhi mid aan ahayn, markaasaan kaga soodajinay kuwii dulmiga falay Cadaab samada faasiqnimadoodii ay falayeen darteed.'),
(67, 2, 60, 'Xusa markuu (Nabi) Muuse u roobdoonay qoonkiisa markaasaan ku nidhi ku garaac ushaada Dhagaxa, waxaana ka dilaacay xaggeeda Labo iyo toban ilood, wunna ogaaday ruuxkasto (Koox kasta) meeshay ka cabbilahayd, waxaan ku nidhi ka cuna oo ka cabba rizqiga Eebe Dhulkana fasaad ha ku xumeynina.'),
(68, 2, 61, 'Xusa markaad dhahdeen Muusow kuma samrayno cunno kaliya ee noobari Eebaha ha noo soo bixiyo waxa dhulku soo dhaliyo, oo ah Bagal, Xinidh, Toon, cadas iyo Basal, wuxuuna ku yidhi mawaxaad ku bedalanaysaan wax xun kii khayrka badnaa, u (hoobta) daga masar waxaad helaysaan waxaad warsateen, waxaana laguqadaray dulli iyo miskiinimo, waxayna lanoqdeen cadho Eeve, arrintaasna waxaa ugu wacan inay yihiin kuwo ka Gaalooba aayaadka Eebe, dilayeenna Nabiyada xaql…aan, taasina waa caasinimadooda darteed iyo xadgudubkoodii.'),
(69, 2, 62, 'kuwwi rumeeyey iyo kuwii Yuhuudda ah iyo Nasaarada iyo saab\'iiinta cidii rumeysa Eebe iyo Maalintii dambee oo camal suubanna falay waxay ku leeyihiin Eebe agtiisa ajir, cabsina korkooda ma noqoto mana tiiraanyoodan.'),
(70, 2, 63, 'Xusa markaan idinka qaadnay ballan (adag) oo korkiina yeelay (Buurta) Dhuur oon idinku nidhi ku qaata waxaan idinsiinay niyad adag, xusuustana waxa ku sugan, si aad u dhawrsataan.'),
(71, 2, 64, 'markaasaad jeedsateen intaas ka dib, hadduusan jirin fadliga Eebe ee korkiina iyo naxariistiisa waxaad ahaan lahaydeen kuwa Khasaaray.'),
(72, 2, 65, 'dhab ahaanbaad u ogtihin kuwii xadgudbay Sabtida, oo idinka mid ah markaasaan ku nidhi noqda daanyeer la fageeyo (oo dullaysan).'),
(73, 2, 66, 'waxaana kayeelay ciqabtaas waanada waxa ka horeeya iyo wax ka danbeeyaba, iyo waano kuwa dhowrsada.'),
(74, 2, 67, 'xusa markuu ku (yidhi) (Nabi) Muuse Qoomkiisa Eebe wuxuu idin fari inaad gawraedaan sac, waxayna dheheen miyaad nagu jees jeesi, wuxuuna yidhi waxaan ka magangali Eebe inaan ka mid noqdo Jaahiliinta.'),
(75, 2, 68, 'waxaayna dhaheen noobari Eebahaa ha noo caddeeyo waxay tahaye, wuxuuna yidhi Eebe wuxuu leeyahay waa sac oon duq ahayn yarayna, oo u dhaxaysa taas (labadaas) ee fala waxa laydin fari.'),
(76, 2, 69, 'waxay dhaheen noobari Eebahaa ha noo caddeeyo Midabkeeda, wuxuuna yidhi Eebe wuxuu leeyahay waa sac Boora ah, booranimadeed darantahay, oo ka farxin (cajabin) kuwa eegi.'),
(77, 2, 70, 'waxay dhaheen no hari Eebahaa ha noo caddeeyo waxay tahay, maxaa yeelay waxaa is kaga, kaana shabbahday (isu ekaatay) Lo\'dee, hadduu Eebe doonana waan toosaynaa.'),
(78, 2, 71, 'wuxuu yidhi Eebe wuxuu leeyahay waa sac aan laylanayn oon qodayn dhulka, oon waraabinayna Beerta, wayna ka nabadgashantahay (eeeb) midab kalana ma leh, waxay dheheen haddaad la timid dhabta, markaasay gawraccen Siccii, waxayna udhwadeen inayna falin.'),
(79, 2, 72, 'xusa markaad disheen naf ood isku khilaafteen Eebana wuu soo bixin (muujin) Waxaad qarineyseen.'),
(80, 2, 73, 'waxaan ku nidhi ku garaaca kan la dilay Saca qaarkiis, saasuuna u nooleeyaa Eebe wixii dhintay, idiina tusin aayaadkiisa si aad wax u kastaan.'),
(81, 2, 74, 'markaasay ingagtay Quluubtiinu intaas ka dib, waxayna la mid tahay Dhagax, ama waa ka darantahay ingeega, Dhagaxyada waxaa ka mid ah kuway ka burqadaan wabiyaal, waxaana ka mid ah kuwo dildillaaca oo ka soo.'),
(82, 2, 75, 'ma waxaad damcaysaan Mu\'miniintaay inay rumeeyaan yuhuudda xaqa oy (idinraacaan) iyahoo kooz ka mid ahi ay maqlayaan hadalka Eebe (Towreed) haddana ay xarrifaan (leexiyaan) intay kaseen kadib, iyagoo og (inay gafsanyihiin).'),
(83, 2, 76, 'markay la kulmaan kuwii rumeeye (xaqana) waxay dhahaan waan rumaynay, markuu qaarkood qaarka kale la kaliyoobana waxay dhahaan ma waxaad uga sheekaynaysaan (u qireeysaan) Mu\'miniinta, waxa Eebe idiinfaray (oo xaqnimada NabigaMuxamed ah) si ay idiinkula doodaan Eebe agtiisa miyaydaan waxkasayn.'),
(84, 2, 77, 'miyayna ogayn in Eebe ogyahay waxay qarsan iyo waxay muujin.'),
(85, 2, 78, 'waxaa ka mid ah Ehelu Kitaabka kuwo aan wax qorayn waxna akhriyeyn, oon aqooninna Kitaab (Towreed) hasa yeeshee rumayn yididiilo, waxaan malo ahayna kuma socdaan.'),
(86, 2, 79, 'halaag wuxuu u sugnaaday kuwa ku qoraya Kitaabka (Towreed) Gacmhooda hadana dhihi kani wuxuu ka yimid xagga Eebe si ay qiimo yar ugu gataan, halaag baana ugu sugnaaday waxay qori Gacmahoodu, halaag baana ugu suganaaday waxay kasban.'),
(87, 2, 80, 'waxay dhaheen yuhuud nama taabanayso naaru waxaan ayaamo tirsan ahayn, waxaad dhahdaa ma ballan baad ka qaadateen Eebe agtiisa oosan idinkaga baxayn Eebe ballankiisa. mise waxaad ku sheegaysaan waxaydaan ogayn.'),
(88, 2, 81, 'saas ma aha ee ruuxii kasbada xumaan oo uu koobo gafkiisu ciddaasi waa asaxaabta naarta, wayna ku waari dhexdeeda.'),
(89, 2, 82, 'kuwa rumeeyey (xaqse) oo camal wanaagsan sameeya kuwaasi waa asaxaabta Jannada wayna kuwaari dhexdeeda.'),
(90, 2, 83, 'xusa markaan ka qaadnay Banii Israa\'iil ballan inaydaan caabudin Eebe mooyee, iyo inay u sama falaan labada Waalid iyo qaraabada, agoonta masaakiinta iyo inay u dhahaan wanaag, salaaddana ooaan bixiyaana sakada, markaasaad jeesateen in yar oo ininka mid ah mooyee, idinkoo jeedsan.'),
(91, 2, 84, 'xusa markaan idinka qaadnay ballan (adag) inaydaan iska daadin Dhiig (inaydaan isdilin) aydaan qaarkiin (qaarka kale) Guryihiinna, ka bixinin markaasaad aqoonsateen arrintaas idinkoo marag furi.'),
(92, 2, 85, 'hadana waxaad ahaateen kuwo dili Naftooda, oo ka erya kooxo idinka mid ah Guryahooda, idinkoo isugu kaalmayn korkoodu dambi iyo colnimo haddii la qafaashana waad furanaysaan, iyadooy dhabtu tahay inay xaaraan idinka tahay bixintoodu, Miyaad rumaynaysaan Kitaabtka qaarkiis, kana gaaloobaysaan qaar, ruuxii saas fala oo idinka mid ah abaalkiisu ma aha dulli adduun iyo maalinta qiyaame oo loo celin cadaab daran mooyee, Eebana ma halmaamayo waxaad falaysaan.'),
(93, 2, 86, 'kuwaasina waa kuwa ku gatay nolosha adduunyo (ee dhaw) aakhiro, lagamane fududeeyo xaggooda cadaabka loomana gargaaro.'),
(94, 2, 87, 'dhab ahaan yaan u siinnay (Nabi) musse Kitaabka, waxaana raacinnary gadaashiis Rasuullo, waxaana siinnay (Nabi) Ciise Ibnu maryama Mucjizaad cad cad, waxaana ku xoojinay ruux daahir ah (Jibriil) marastoose idiinla yimaaddo rasuul waxaan naftiinu jeclayn (xaqa) ma iskibrisaan, koox waad beenisaan, kooxna waa dishaan.'),
(95, 2, 88, 'waxayna dheheen yuhuud quluubtanadu waxay ku sugantahay dabool, saas ma aha ee waa la nacladay gaalnimadooda darteed wax yar bay rumeeyn.'),
(96, 2, 89, 'markuu uga yimid Yuhuud Kitaab xagga Eebe isagoo rumeyn waxa agtooda ah (Tawreed) horayna u ahaayeen kuwa ku gargaarsada kuwii gaaloobay, markuu yimid waxay garanayeen way ka gaaloobeen Nabiga, naclad korkooda ha ahaato, gaaladee.'),
(97, 2, 90, 'waxaa u xun waxay ku gataan Naftooda inay ka gaaloobeen xaqa Eebe soo dajiyey xasad dartiis, ku soo dajinta Eebe fadligiisa ciduu doono, oo adoomadiisa ka mid ah, waxayna la noqdeen Cadho Cadho kale lagu kordhiyey, gaalana waxaa u sugnaaday cadaab wax dulleeya.'),
(98, 2, 91, 'marka lagu yidhaah Yuhuudda rumeeya waxa Eebe soo dajiyey waxay dhahaan waxaannu rumeeyn waxa nalagu soo dejiyey, wayna ka gaaloobaan waxa ka dambeeyey, isagoo xaq ah oo rumeyn waxa agtooda ah, waxaad dhahdaa maxaad ugu disheen nabiyada Eebe horay haddaad Mu\'miniin tihiin.'),
(99, 2, 92, 'dhab ahaan yuu idiin la yimid (Nabi) Muuse mucjisooyin, markaasaad yeelateen Dibi (ilaah) isaga kadib idinkoo daalimiin ah.'),
(100, 2, 93, 'xusa markaan idinka qaadnay ballan, korkiinana yeelay (Buurta Dhuur) oon idinku nidhi ku qaata waxaan idin siinay xoog (niyadadag) maqlana (xaqa) waxayna dheheen waan mawalay waana caasinay, waxaana laga waraabiyey quluubtooda (jacaylkii) Dibiga (ay caabudeen) gaalnimadooda darteed, waxaad dhahdaa waxaa xumaan badan wuxuu idinfarayo imaankiinu haddaad muuminiin tihiin.'),
(101, 2, 94, 'waxaad dhahdaa hadday daarta aakhiro idinka uun idiinsugnaatay Dadka kale ka sokow jeelaada geeti haddaad run sheeqaysaan.'),
(102, 2, 95, 'man jeelaanayaan (Geerida) waligood, waxat hormardadeen Gacmahoodu darteed, Eebana waa ogyahay daalimiinta.'),
(103, 2, 96, 'dhab ahaan waxaad ugu ogaan Yuhuudda inay ugu dadaal badanyihiin nolosha, kuwa Gaalada ah qaarkood wuxuu jeeelyahay in la cimrisiiyo kun Sano, cimrigaasna kama fogeeyo cadaab Eebana waa arkaa waxay camal fali.'),
(104, 2, 97, 'waxaad dhahdaa ruuxi Col u ah (Malaku) Jibriil (waa Colka Eebe) maxaayeelay wuxuu ku dajiyey Qalbigaaga Idin Eebe dartiis, isagoo rumeyn Quraanka wixii ka horeeyey waana Hanuunja iyo Bishaarada Mu\'miniinta.'),
(105, 2, 98, 'ruxii col Eebe ah, Malaa\'igtiisa, Rasuuladiisa, Jibriil iyo Miikaa\'iil Eebana Colbuu u yahay Gaalada.'),
(106, 2, 99, 'dhab ahaan yaan kuugu dajinay Aayaad Cad cad kamana gaaloobo Faasiq cid aan ahayn.'),
(107, 2, 100, 'markastooy ballantamaana Yuhuuddu waxaa tuura (buriya) ballanka koox ka mid ah, saas ma ahee Yuhuudda badankood waxba ma rumeeyn.'),
(108, 2, 101, 'markuu uga yimid Rasuulku xagga Eebe isagoo rumayn waxa agtooda ahna (Tawrced) waxay ka tuuraan koox ka mid ah kuwii Kitaabka la siiyey (culimada Yuhuudda) Kitaabka Eebe Dhabarkooda Gadaashiisa, waxayna la mid yihiin iyagoon garanayn.'),
(109, 2, 102, 'waxay raaceen Gaaladii Yuhuuddu waxay Shayaadiintu Akhriyeysay waqtigii xukukii Nabi Suleyman, ma Gaaloobin Nabi Suleymaan, laakiin Shayaadiinta yaa Gaalowday, waxayna baraan Dadka Sixirka, iyo wixii lagu soo dajiyey labadii Malag ee Baabil joogay ahaana Haaruut iyo Maaruut, mana baraan cidna intay uga dhahaan waxaan uun nahay Fidnee ha Gaaloobin, waxayna ka bartaan xagooda wax kalageeya Ninka iyo Haweenaydiisa, kumana dhibaan ruuxna idinka Eebe la\'aantiis, waxayna bartaan Dadkaasu wax dhiba oon anfacayn, dhab ahaan yey u ogaadeen Yuhuuddu ruuxii sixir barta inuusan Aakhiro nasiib ku lahayn, waxaana baasleh Sixirkay Naftooda ku gateen hadday wax ogyihiin.'),
(110, 2, 103, 'hadday kuwa (Sixirkabaran) rumeeyaan xaqa oy dhawrsadaan wanaag kuabaalamarinta Eebe yaa u Khayr roon hadday ogyihiin.'),
(111, 2, 104, 'kuwa xaqa rumeeyow ha dhihina na ilaali ee dhaha na sug maqlana, Gaalana waxaa a sugnaaday cadaab daran.'),
(112, 2, 105, 'ma jeela kuwii Gaaloobay oo ehelu Kitaabka ahaa (Yuhuud iyo Na- saara) lye Gaaladii Carbeed in Iaydinku soo dajiyo khayr xagga Eebe ka yimid. Eebana wuxuu ku kaliyeelaa Naxariistiisa ruuxuu donna, Eebana fadligiisu (Khayrkiisu) waa waynyahay.'),
(113, 2, 106, 'waxaan tirro oo aayad ah ama aan dib u dhigno waxaan keenaynaa mid ka khayrbadan ama la mid ah, miyaadan ogayn in Eebe wax kastoos (doono) uu karo.'),
(114, 2, 107, 'miyaadan ogayn in Eebe leeyahay xukunka Samaawaadka iyo Dhulka, Idinmana sugnaanin Eebe ka sokow sokoeye iyo gargaare midna.'),
(115, 2, 108, 'mise waxaad dooneeysaan inaad wax warsataan Rasuulkkiinna sidii loo warsaday (Nabi) Muuse mar hore ruuxii ku bedasha Gaalnimo limaan wuxuu ka dhumay Jidka toosan.'),
(116, 2, 109, 'waxay jeelyihiin in badan oo ehelu Kitaab ah (Yuhuud iyo Nasaara) inay idiin eeliyaan iimaankiina ka dib Gaalo, Xasad naftooda ka yimid dartiis, intuu xaqu u eaddaaday ka dib, ee iska eafiya iskana saamaxa inta Eebe ka keeni amarkiisa (Ia dagallankooda ama islaamkooda) Eebana waxkastuu doono wuu karaa.'),
(117, 2, 110, 'Salaaddana ooga sakadana bixiya waxaad u hormarsataan Naftiina oo khayr ah waxaad ka helaysaan Eebe agtiisa, Eebana waxaad camalfaIaysaan waa arkaa.'),
(118, 2, 111, 'waxay dhaheen ma galo Jannado ruuxaan Yuhuud ahayn ama Nasaaro, taasina waa yididiilo (been ah) waxaad dhahdaa keena xujadiina haddaad run sheegeysaan.'),
(119, 2, 112, 'Ruuxiise u hogaamiya Wajigiisa Eebe isagoo soma fala ah, wuxuu ku Ieeyahay Ajrigiisa Eebe agtiisa, mana cabsadaan mana walbahaaraan.'),
(120, 2, 113, 'Yuhuud waxay tidhi Nasaaro waxba ma aha, Nasaarana waxay tidhi Yuhuudu wax ma aha, iyagoo akhrin Kitaabka (Tawreed iyo lnjiilba) saasay dhahaan kuwaan wax ogaynna «Gaaladii Carbeed», hadalkoodoo kale, Eebana wuu kala xukumin dhexdooda Maalinta Qiyaame waxay isku diidanaayeen.'),
(121, 2, 114, 'l cidna kama dulmi badna ruux reebay (diiday) in masaajidda Eebe dhexdiisa lagu xuso Magaeiisa, una eamal fala say u kharaahi Iahayd (Masaa-Jiddu) kuwaas (Gaalada ah) Xaq uma leh inay galaan Masaajidda iyagoo eabsan mooyee, Adduunka waxay ku mudan dulli Aakhirana Cadaab Wayn.'),
(122, 2, 115, 'Qorax ka soo box iyo u Dhaeba Eebaa iska leh, meel kastood u jeedsataanna waa Jaho Eebe (markaad Qiblada garanweydaan) Eebana waa waasac (Naxariistiisu) wax walbana og.'),
(123, 2, 116, 'waxay dhaheen Gaaladii wuxuu yeeshay Eebo Ilmo, waana ka nasahanyahay, saas ma aha ee waxaa u sugnaaday Eebe waxa ku jira Samada iyo Dhulka, dhamaanna Eebay u hogaansamaan.'),
(124, 2, 117, 'isagaa abuuray Samooyinka iyo Dhulka, markuu doono arrinna wuxuu uun dhahaa ahaw wuuna ahaadaa.'),
(125, 2, 118, '1I waxay dhaheen kuwii aan wax ogayn (Gaaladii) muxuu Eebe nooola hadli waayey ama noogu imaan wayday aayad, saaso kaley dhaheen kuwii ka horreeyey hadalkooda oo kale, waxaa is Shabbahday (isu ekaatay) qulubtooda dhab ahaan yaan aayadaha ugu caddaynay ciddii wax yaqiinin.'),
(126, 2, 119, 'anagaa ku dirray Nabiyow adoo xaq wata oo Bishaarayn Digana, lagu warsanna maayo ehelka (Naarta) Jaxiimo.'),
(127, 2, 120, 'Kaama raalli noqdaan Yuhuud iyo Nasaara intaad ka raaedo Diintooda, waxaad dhahdaa hanuunka Eebaa hanuun ah, haddaad raaedo hawadooda intuu kuu yimid cilmi ka dib, kama heshid xagga Eebe sokeeye iyo Gargaare (midna).'),
(128, 2, 121, 'kuwaan siinnay Kitaabka waxay u raacaan raacid Xaq ah (u akhriyaan) kuwaasina waxay rumeyn Qurannka, ciddii ka Gaalowdana Quraankase (ama Nabiga) waa cidda Khasaartay uun.'),
(129, 2, 122, 'Banii Isra\'iilow xusa Niemadaydii aan iddinku Niemeeyey iyo inaan idiinka fadilay adduunka (Waqtigiinii).'),
(130, 2, 123, 'ka dhawrsada maalintayna Nafna naf kale ka abaalmarinaynin waxba, Furashana aan laga aqbalayn, Shafeeeana ayan anfaeayn oona loo gargaarayn.'),
(131, 2, 124, 'xusuusta marka Eebe ku imtixaamay Nabi Ibrahim kalimooyin (ereyo) uuna dhameeyey, kuna yidhi waxaan kaaga yeeli Dadka Imaam, wuxuuna yidhi (Ibrahiim) Caruurtaydana (kayeel) (Eebe) wuxuu yidhi ballankaygaa (liican) ma gaadho daalimiinta.'),
(132, 2, 125, 'xusuuso markaan ka yeeley baydka (Kaebada) meel Dadku ku kulmo, iyo Nabadgalyo kana yeesha Maqaamka Ibrahim meel lagu tukado, waxaan u waxyoonay Nabi Ibrahim iyo Ismaaeiil inay u daahiriyaan baydkayaga kuwa dhawaafi, kuwa ku nagi, iyo kuwa Rukuuei ee sujuudi.'),
(133, 2, 126, 'xusuuso markuu yidhi (Nabi) Ibraahim Eebow kayeel Magaaladan (Maka) aamin kuna Arzaaq Dadkeeda Midho kooda rumeeya Eebe iyo Maalinta dambe (Qiyaamada) (Eebe) wuxuu yidhi iyo kan gaalobaba waan u raaxayn in yar markaas waanu galin Cadaabka Naarta meel la ahaadana iyadaa u xun.'),
(134, 2, 127, 'xusuuso markuu kor yelay Nabi Ibraahim Tiirarka Kaebada iyo Nabi Ismaaeiil iyagoo leh Eebow naga aqbal adaa waxna maqla waxna oge,'),
(135, 2, 128, 'Eebow naga yeel kuwa kuu hogaansama, Faraeanagana ka yeel Umad kuu hogaansama nana tusi eamalkannaga (Xajka) nagana toobad aqbal adaa toobad aqbalbadane. Wanaaghabadane ahe.'),
(136, 2, 129, 'Eebow u soo bixi Rasuul ka mid ah oo u akhriya aayaadkaaga barana Kitaabka (Quraanka) iyo xigmada (sunada Nabiga) oo nadiifiya adigaa adkaade, Falsame ahe.'),
(137, 2, 130, 'Yaa naea diinta Nabi Ibraahim ruux xumeeyey naftiisa mooyee, waana doorannay Nabi Ibraahim, adduunyada, aakhirana wuxuu ka midnoqon kuwa suusuuban.'),
(138, 2, 131, 'xusuuso markuu Eebe ka yidhi hogaansan oo uu yidhi waan u hogaansamay Eebaha Caalamka,'),
(139, 2, 132, 'wuxuu u dardaarmay arintaas (Nabi) Ibraahim Caruurtiisa, Nabi Yacquubna wuu u dardaarmay, wuxuuna yidhi Carrurtaydiiyey Eebe wuxuu idiindoortay Diinta ee ha dhimanina idinkoon Muslim ahayn.'),
(140, 2, 133, 'miyaad joogten (Yuhuuday) marka Geeridu u timid (Nabi) Yacquub markuu ku yidhi Caruurtiisa maxaad caabudaysaan Gadaashay, ayna dheheen waxaan caabudaynaa Eebahaaga iyo Eebaha Aabayowgaa Ibraahim, Ismaeiil iyo Isxaaq Eebe kaliya, isagaana u hogaansamaynaa.'),
(141, 2, 134, 'taasina waa ummad taktay, waxayna Mudatay waxay kasbatay, idinkuna waxaad mudanaysaan waxaad kasbateen, laydina warsan maayo waxay Camalfalayeen.'),
(142, 2, 135, 'waxay ku dhaheen Gaaladii Muslimiinta ama Yuhud noqda ama Nasaara aad hanuunteene, waxad dhahdaa mayee milladda (Diinta Nabi) Ibraahim yaan raacaynaa ee Xaqa u iishay, kamana mid ahayn Gaalda.'),
(143, 2, 136, 'waxaad dhahdaan waxaan rumaynay Eebe iyo waxa naloo soo dajiyey iyo waxa lagu soo dajiyay Ibraahim, Ismaeiil, Isxaaq, Yacquub. iyo Caruurtoodii iyo wixii la siiyay (Nabi) Muuse iyo (Nabi) Ciise iyo wixi laga siiyey Nabiyadii (Kale) xagga Eebohood kala gaynamayno dhexdooda, waxaan u hogaansamaynaa Eebe.'),
(144, 2, 137, 'hadday u rumeeyaan saad u rumayseen way hanuuneen, hadday jeedsadeenna waxay ku suganyihiin khilaaf, waxaana idinka kaafin Eebe isagaa wax maqla waxna oge.'),
(145, 2, 138, 'ku sugnaada Diinta Eebe, yaa ka fiican Diinta Eebe (ma jiro) isagaana caabudaynaa.'),
(146, 2, 139, 'waxaad dhahdaa Nabiyow ma waxaad igula doodaysaan Eebe isagoo Eebahanno iyo Eebihiinna ah, anaguna Camalkanagu noo sugnaaday idinkuna Camalkiina, isagaana u kali yeelaynaa Diinta.'),
(147, 2, 140, 'mise waxaad dhahaysaan Nabi Ibraahim, Ismaeiil, Isxaaq, Yacquub iyo Caruurtii waxay ahaayeen Yuhuud ama Nasaara, waxad dhahdaa ma idinkaa wax og Mise Eeben yaa ka dulmi badan ruux qariya marag agtiisa Eebe ku yeeshay, Eebana ma halmaamo waxaad falaysaan.'),
(148, 2, 141, 'taasi waa umad tagtay, waxayna mudan waxay kasbatay idinkuna waxaad mudaynasaan waxaad kasbateen, laydin warsannamaayo waxay camalfalayeen.'),
(149, 2, 142, 'waxay dhihi kuwii xumaa ee Dadka (Gaalada) ka mid ahaa maxaa ka jeediyey Muslimiinta Qiblay Qaabilijireen (Baytul Maqdis) waxaad dhahdaa Nabiyow Qorrax ka soobax iyo u Dhaeba Eebaa iska leh, wuxuuna ku hanuunin Ciduu doono Jidka toosan.'),
(150, 2, 143, 'saasaan idinka dhignay umad caadil (khayr ah) si aad marag ugu noqotaan Dadka, Rasuulkuna idiinku noqdo markhaati, mana aa naan yeelin Qibladii aad qaablijirtay inay kala muuqato ruuxa Rasuulka raaca iyo kan dib u noqon (Gaaloobi) mooyee waana arrin kuweyn Dadka marka laga reebo kuwa Eebe hanuuniyey, Eebana ma dhumiyo limaankiinna (Salaadiina) Eebana waa u Naxariisbadanyahay Dadka.'),
(151, 2, 144, 'dhabahaan yaan ugu jeednay gagadookna Wajigaaga ee Samada, waxaana kuu jeedinaynaa Qiblad aad ka raali noqon, ee u jeedi Wajigaaga xagga Masjidka Xurmeysan, meelkastood joogtana u jeediya Wajigiinna xagiisa, kuwa Kitaabka la siiyeyna waa ogyihiin in jeedintaas xaq Eebe tahay, Eebana ma halmaansana waxay Camal fali.'),
(152, 2, 145, 'hadaad ula timaado kuwa ehelu Kitaabka ah aayad kasta ma raacayaan Qibladaada adiguna ma raacaysid Qibladooda, qarkoodna ma aha kan raaca Qiblada qaarka kale, hadaad raaedowaxay jeceliyihiin Gaalada, inta cilmi kuu yimid (waxyiga Eebe), waxaad ka mid noqon daalimiinta.'),
(153, 2, 146, 'kuwaan siinay Kitaabka waxay u yaqaanaan Nabiga (iyo xaquu la yimid) siday u yaqaanaan Caruurtooda. koox kamid ahna waxay qarin xaqa iyagoo og.'),
(154, 2, 147, 'xaqu wuxuu ka yimid xagga Eebahaa ee ha ka mid noqonin kuwa shakiya.'),
(155, 2, 148, 'cidwalba waxay leedaahay joho ay dhawrto (raaedana) ee u oratama Wanaaga, meelkastood ahaataana Eebe wuu idin keeni (oo idin kulmin) dhammaantiin, Eebana wax walba wuu karaa (haduu doono),'),
(156, 2, 149, 'meelkastood u baxdo u jeedi Wajigaaga xagga Masjidka Xurmaysan, Xaqu xagga Eebahaa buu ka ahaaday Eebana Ma Mooga Waxaad Falaysaan.'),
(157, 2, 150, 'meelkastood u baxdana jeedi wajigaada xaggisa Masjidka Xurmaysan, meelkastood joogtaanna u jeediya Wajiyadiinna xagiisa, si ayna xujo ugu ahaan Dadka Korkiinna, kuwa Daalimiinta ah mooeye (ee gardarada ku sooda) hana ka cabsanina ee aniga iga cabsada, waxaana idiin taamyeeli (dhamayn) Nemadayda si aad u hanuuntaan.'),
(158, 2, 151, '«waan idiin Niemeynay» sidaan idinku dirnay dhexdiina Rassul idinkamid ah oo idinku akhriya Aayaadkanaga, oo idin daahiriya, oo idin bara Kitaabka (Quraanka) iyo Sunada Nabiga, idinna bara waxaydaan aqoonin.'),
(159, 2, 152, 'ee I xusa aan idin Xusee (ajir idin siiyee) iguna mahadiya hana iga Gaaloobina.'),
(160, 2, 153, 'kuwa (Xaga) rumeeyow kaalmaysta Samir iyo Salaad, Eebe wuxuu la jiraa kuwa Samree.'),
(161, 2, 154, 'hana ku dhihina kuwa lagu Dilo Jidka Eebe (Jahaadka) waa Dhinteen, saas ma aha ee waa noolyihin, mase kaseysaan.'),
(162, 2, 155, 'waxaan idin ku Imtixaannaynaa wax Cabsi ah iyo Gaajo, iyo Nusqaamin Xoolaha iyo Nafta iyo midhaha, ee u Bishaaree kuwa Samra.'),
(163, 2, 156, 'kuwaas markuu dhib ku dhoeo yidhaahda Eebaa na leh isagaana u noqonaynaa.'),
(164, 2, 157, 'kuwaas waxaa korkooda ahaan Deeq Eebe iyo Naxariistiisa, waana kuwa hanuunsan.'),
(165, 2, 158, 'Safo iyo Marwo waxay ka midyihiin Calaamooyinka (Wayneynta) Eebe ee ruuxii u Xajiya Baydka ama Cumraysta wax ma saarra hadduu ku Wareego Dehexdooda, ruuxa wax dheeraad ah la yimaadana Eebe wuu ku mahdin wuuna ogyahay.'),
(166, 2, 159, 'Kuwa qariya waxaan soo dajinay oo aayaad ah iyo hanuun intaan dadka ugu caddaynay Kitaabka kuwaas Waxaa naeladi Eebe iyo Kuwa wax Naealada (malaaigta iyo Mu\'miniinta).'),
(167, 2, 160, 'Marka laga reebo kuwa toobad keena oo Wanaaga fala oo Xaga caddeeya, kuwas waan ka toobad aqbali, waxaana ahay toobad aqbal badane Naxariis badan.'),
(168, 2, 161, 'Kuwii Gaaloobay oo ku dhintay gaalnimo Kuwaas waxaa korkooda ah nacaladda Eebe iyo Malaa\'igta iyo Dadka dhammaan.'),
(169, 2, 162, 'Waxayna kuwaari dhexdeeda (naarta iyo nacaladda) lagamana fududeeyo xagooda cadaabka Loomana sugo.'),
(170, 2, 163, 'Eebihiin waa Eebe kaliya isaga mooyee Eebe kalana majiro, Waana naxariis guud iyo mid gaarba naxariiste.'),
(171, 2, 164, 'Cirka abuurkiisa iyo dhulka iyo kala duwanaanta Habeenka iyo maalinka iyo doonta La socota badda wax anfaca dadka iyo waxa Eebe ka soo Dajiyay samada oo Biyo ah kuna nooleeyay Dhulka intuu dhintay ka dib, kuna fidiyay Dhulka Daabad kasta iyo gadgadoonka dabaysha iyo Daruuraha loo sahlay Samada iyo Dhulka dhexdiisa waxaa ugu sugan calaamo kuwa wax kasi.'),
(172, 2, 165, 'Dadka waxaa ka mid ah eid ka yeelan Eebe ka sokow wax lala caabudo una jecelyihiin say u jecelyihiin Eebe, kuwa rumeeyey xaqa yaa ka daran jeceelkooda Eebe, hadday arkaan kuwa dulmiga falay Cadaabka (Aakharo) waxay ogaan lahaan lahaayeen in xoog dhamaantiis u sugnaaday Eebe, Eebana cadabkiisu daranyahay.'),
(173, 2, 166, 'markay iska bariyeelaan juwii la raacay kuwii raacay, oy arkeen Cadaabka kalana go day xidhiidhkoodii.'),
(174, 2, 167, 'waxayna dhaheen kuwii raacay haddii nala celiyo waan iska bariyeeli lahayn xaggooda say iska kaanna bariyeeleen, saasuuna Eebe u tusiyaa camalkooda'),
(175, 2, 168, 'Dadoow cuna waxa Dhulka ku sugan kiisa xalaasha fiican ah hana raacina Wadooyinka Sheydaanka illen Cadaw ead buu idiinyahaye,'),
(176, 2, 169, 'wuxuuna idin fari uun xumaan iyo Macsiyo iyo inaad ku sheegtaan Eebe waxaydaan ogayn.'),
(177, 2, 170, 'marka lahu dhaho Gaalada raaca wuxuu soo dajiyey Eebe, waxay dhihi saas ma aha ee waxaanu eaaci waxaan ka helley Aabayowganno (oo caabudi) oo hadday yihiin Aabayowgood kuwaan wax kasayn Hanuunsanayna (miyey raaci),'),
(178, 2, 171, 'waxay la midyihiin kuwii Gaalobay mid u dhawaaqay (Heesi) waxaan wax maqlayn, kasayna Qaylada iyo Dhawaaqa mooyee, wax ma maqlaan mana hadlaan waxna ma arkaan, waxna ma kasaan.'),
(179, 2, 172, 'kuwa Xaqa rumeeyow wax ka Cuna wanaagaan idinku aezuqnay, kuna mahdiya Eebe hadaad tihiin kuwo isaga Caabudi.'),
(180, 2, 173, 'wuxuu uun idinka reebay Bakhtiga, Dhiigga, Hilibka Doofaarka iyo wixii Eebbe Magiciisa waxaan ahayn lahu gawraco ruuxiise dhibaatooda (baahan) oon xadgudbin dulmina galin korkiisa dambi masaara (inuu) wax ka cuno Eebana waa dambi dhaafbadane Naxariisbadan.'),
(181, 2, 174, 'kuwa qariya waxa Eebe soo dajiyey oo Kitaabka ah (Quraanka) kuna gata lacay yar kuwaasi kuma cunayaan (ma galinayaan) Calooshooda waxaan Naar ahayn, lamana hadlo Eebe Maalinta Qiyaame mana daahiriyo waxayna Mudan Cadaab daran.'),
(182, 2, 175, 'kuwaasi waa kuwa ku gatay baadida Hanuun, Cadaabna dambidhaafid maxaa uga adkaysi badan Naarta.'),
(183, 2, 176, 'arrintaas waxa ugu wacan in Eebe u soo dejiyey kitaabka xaqa ah kuwii iskudiiday kitaabkana waxay ku suganyihiin khilaaf Fog.'),
(184, 2, 177, 'Samo falku ma aha inaad wajiga u jeedisaan Qorax ka soo bax iyo Qorax u dhac laakin sama fale waa ruuxii rumeeya Eebe, Qiyaame Malaa\'igta. Kitaabka (kutubta Alle), Nabiyada, oo siiya Xoolaha isagoo jeeel Qaraabada, Agoonta, Masaakiinta, Masaafirka, kura baryootama, Fakin qoor, oogana Salaadda, bixiyana Zakada, oofiyana ballankooda, markay ballamaan, Samra (adkaystana) Waqtiga saboolnimada. Kirrada iyo Dagaalka, kuwaasina waa kuwa ky runsheeqay Iimaankooda waana kuwa dhawrsada.'),
(185, 2, 178, 'kuwa xaqa rumeeyow wxaa laydinku waaJibiyey Qisaasta Dadka la dilo, xurta xurbaa loo qisaasan, Adoonkana addoon, Haweeneydana Haweeney, ruxiise looga cafiyey Walaalkis (xaggiisa) wax waxaa saarun inuu si fiican u raaco isna Si fiican Loogugudo, taasina waa fudayd xagga Eebe iyo naxariis, ruuxiise xadgudba intaas ka dib wuxuu mudan Cadaab daran.'),
(186, 2, 179, 'waxaa idinku sugan Qisaasta nolol Dadka wax garadka ahow waxaadna mudantihiin inaad dhawrsataan.'),
(187, 2, 180, 'waxaa laydinku waajibiyey markaad Dhimanavsaan hadaad ka tagav saan Xoolo dardaaranka Waalidaynka iyo Qaraabada si wanaagsan, Xaq vayna ku tahay kuwa dhawrsada.'),
(188, 2, 181, 'ruuxiise badala dardaaranka intuu maqlay ka dib dambigu wuxuu saaranyahay uun kuwa wax badala, Eebana waa maqle og.'),
(189, 2, 182, 'ruuxii uga cabsada dardaaran iilasho ama dambi oo hagaajiya dhexdoda, korkiisa danbi masaara, Eebana waa dambi dhaafbadane naxariis badan.'),
(190, 2, 183, 'kuwa xaqa rumeeyow waxaa Laydinku wajibiyay Soonka sidii loogu waajibiyey kuwii idinka horreeyey si aad u dhawrsataan.'),
(191, 2, 184, 'waa Maalmo tirsan ee ruuxii idinka mid ah oo buka ama Safar ah waxaa korkiisa ah ayaamp kale (soonkood) kuwa uu dhibina waxay bixin «quudin» Miskiin, ruuxii badsada (Khayrkana) saasaa u fiican, inad Soontaan yaana idiin khayrbadan haddaad tihiin kuwo wax og.'),
(192, 2, 185, 'waa bisha Soon ee la soo dajiyey dhexdiisa Quraanka, isagoo hanuun Dadka u ah, iyo caddayn hanuun iyo kala bixin (xaqa iyo baadhilka) ee ruuxii jooga (nagi) ha Soomo, ruuxiise buka ama Safar ah waxaa saaran maalmo kale, wuxuu idinla dooni Eebe fudayd ee idin lama doonayo culaysm waana inaad dhamaystirtaan tirada wayneysaanna Eebe wuxuu idinku hanuuniyey, waxaadna u dhawdihin, mudantihiin, inaad mahadisaan Eebe.'),
(193, 2, 186, 'hadday ku warsadaan Nabiyow addoomadu xagayga anigu waan dhawahay waana ajiibaa (aqbalaa) baryada markay i baryaan, ee ha i maqleen iyaguna hana i rumeeyeen si ay u hanuunaan.'),
(194, 2, 187, 'waxaa laydiin banneeyey inaad Habeenka Soonka is aragtaan Haweenka, maxaayeelay dhar (asturaad bay) idiinyihiin, idinkuna dhar (asturaad baad) u tihiin, Eebana wuxuu ogaaday inaad Khayaamayseen Naftiinna. wuuna idinka toobad aqbalay idina cafiyey ee haddu u tega Haweenkiina kana doona waxaa Eebe idiinku Qoray, Cuna oo Cabbana intuu ka muuqdo liilanka cadi (waagu) kan madow Aroortii, dhammeeyana Soonka tan iyo Habeenka hana u tagina Haweenka idinkoo ku nagi Masaajidda taasina waa xuduudda (Soohdinta) Eebe ee haw dhawaanina, saasuuna ugu Caddayn Eebe aayaadkiisa Dadka si ay u dhawrsadaan,'),
(195, 2, 188, 'hana ku Cunina Xoolihiinna dhecdiinna si baadhil (Xaq darri ah) idinkoo u dhiibi (Laaluush) Xukaamta si aad ugu cuntaan qayb ka mid ah Xoolaha Dadka dambi idinkoo og.'),
(196, 2, 189, 'waxay ku warsan Nabiyow Bisha ee waxaad Dhahdaa waqtigay (mudday) ugu tahay Dadka iyo Xajka, mana aha Samo falku inaad ugaga timaadaan Guryaha xagga dhabarka ee sama fal waa ruuxii dhawrsada, ugagana immadda Guryaha Albaabada, kana dhawrsada Eebe waxaad u dhawdihiin inaad Liibaantaane.'),
(197, 2, 190, 'kula dagaalama Jidka Eebe kuwa idinla dagaalama, hana xadgubina Eebe ma jeelee kuwa xadgudbee.'),
(198, 2, 191, 'ku dila meejaad ka heshaanba, kana bixiya meejey idin ka bixiyeen, Gaalnimaa ka daran Dilka, ha kula dagaalamina Masajidka Xurmaysan agtiisa intay idinkula dagaalamaan Dhexdiisa, haddayse idinkula dagaalaan la dagaalama, sasaa abaalka Gaalda ahe.'),
(199, 2, 192, 'haddayse reebtoomaan Eebe waa dhaafidbadanae naxariis badan.'),
(200, 2, 193, 'la dagaalama intaan la helin gaalnimo, oo Diintu u ahaan Eebe, hadday reebtoomaan lalama Collaytamo cidaan xadgudbayn.'),
(201, 2, 194, 'Bisha Xurmaysan (dagaalkeeda) waxaa laga horgayn bisha Xurmaysan, waxyaalaha xurmaysanna Qisaasbaa ah, ee ciddii idinku xadgudubta ku xadgudba «ku abaal mariya» intuu idinku xadgudbay wax la eg, Eebana ka dhawrsada, ogaadana in Eebe la jiro kuwa dhawrsada.'),
(202, 2, 195, 'ku nafaqeeya (ku bixiya wax) Jidka Eebe, hana ku tuurina Gaemihiina halaag (waxbixin la\'aan) wanaagna fala, Eebe wuxuu jecelyahay kuwa wanaaga fala.'),
(203, 2, 196, 'dhamaystirana Xajka iyo Cumrada, haddii laydin sharana waxaa idiin saaran wixii fuduudaada oo hadya ah (inaad gawraedaan) hana ciirina madaxiina intay ka gaadhi hadyada meejeeda, ruuxi idinka mid ah oo buko ama dhib madaxa kaga yaallo, waxaa saaran Fidyo (Furasho) Soon ah, ama Sadaqo, ama gawrae, markaad aamin noqotaan ruuxii cumro iyo Xajku ku raaxaysta (kulmiya) waxaa saaran wixii Fududaada oo hadya ah (gawrae), ruxaanse helin ku Soon Saddex maalmood Xajka iyo todoba markuu ku noqdo gurigiisii, taasina waa toban buuxda, waase ruuxaan deganayn Masaajidka la xurmeeyey (maka) ka dhawrsada Eebe ogaadana in Eebe yahay kay darantahay Ciqaabtiisu.'),
(204, 2, 197, 'Xajka waa Bilo la yaqaan, ee ruuxii gudagala bilahaas Xajka, isutagid, fisqu iyo muran ma jiro Xajka dhexdiisa, waxaad fashaan oo khayr ah wuu ogaan Eebe, ee sahay qaara, sahay tan u khayrbadanna waa dhawrsashada, ee iga dhawrsada kuwa caqliga lahow.'),
(205, 2, 198, 'korkiina wax dhib ah ma aha haddaad dalabtaan fadliga Eebihiin (ganacsi) ee markaad ka soo gadoontaan Carrafo ku xusa Eebe mashearulxaraam agtiisa, una xusa siduu iddin hanuuniyey, waxaadna ahaydeen xaqa hortiis kuwa dhunsan.'),
(206, 2, 199, 'markaas ka gadooma meesha Dadka ka gaddoomi dambidhaafna waydiista Eebe, ilaahay waa dambi dhaafe naxariistee.'),
(207, 2, 200, 'markaad gudataa camalkiina xusa Eebe saad u xustaan Aabayowgiin ama si ka daran u xusa, Dadka waxaa ka mid ah kuwo dhihi Eebow na sii adduunka wanaag, aan kuna lahayn Aakhiro qayb.'),
(208, 2, 201, 'waxaana ka mid ah Daka kuwo dhihi Eebow na sii adduunka wanaag, aakhirana nasii wanaag, nagana dheeree Cadaabka Naarta.'),
(209, 2, 202, 'kuwaasi waxay mudan qayb waxay kasbadeen, Eebana waa degtaa xisaabtiisu.'),
(210, 2, 203, 'xusa Eebe maalmo la yaqaano «Iidda carafo dabadeed», ciddiise dag dagta laba maalmood dhexdood ma saarra dambi korkiisa ciddii dhawrsata, ka dhawrsada Eebe, ogaadana in xagiisa laydiin soo kulmin.'),
(211, 2, 204, 'Dadka waxaa ka mid ah mid ku yaab galin hadalkiisu noloshan adduun, maragna gashay Eebe waxa qalbigiisa ku sugan, isagoo dood badan.'),
(212, 2, 205, 'markuu jeedsadana, soeda dhulka fasaad darteed iyo inuu halligo beeraha iyo faracaba, Eebana ma jeela fasaadka.'),
(213, 2, 206, 'marka loo dhoho ka dhawrso Eebana waxa qabata isla weyni dambi dartiis, waxaa ku filan jahannamo iyadaana u xun gogol.'),
(214, 2, 207, 'dadkana waxaa ka mid ah mid ku gadi naftiisa dalab raali ahaanshah Eebe, Eebana waa u naxariistaha addoomada.'),
(215, 2, 208, 'kuwa xqqa rumeeyow u gala Islaamka dhammaan (kuna camal fala) hana raacina Waddooyinka Shaydaanka, wuxuu idiin yahay col Cade.'),
(216, 2, 209, 'haddaad simbiriiraxataan intuu idiin yimid xujooyin ogaada in Eebe yahay adkaade falsan.'),
(217, 2, 210, 'miyey sugi inuu Eebe u yimaaddo ciqaabtiisa Hoos Daruura ah mooyee, iyo Malaa\'igtu lana xukumo amarka, xagga Eebaana loo celin umuuraha.'),
(218, 2, 211, 'waydii Banii Israa\'iil imisaan siinay Aayad Cad, ciddii badasha Nicmada Eebe intay u timid, Eebe Ciqaabtiisu waa ku darantahay.'),
(219, 2, 212, 'waxaa loo qurxiyey kuwa Gaaloobay Nolosha dhaw ee adduun, waxayna ku jees jeesaan kuwa rumeeyey xaqa, kuwa dhawrsada iyagaana ka sarrayn Maalinta Qiyaame, Eebana wuu Arzaaqaa cidduu doono Xisaab la\'aan.'),
(220, 2, 213, 'dadky wuxuu ahaa ummad kaloya, wuxuuna Eebe u bixiyey Nabiyo u bishaareeya una diga, wuxuuna la soo dajiyey Kitaab Xaq ah, si ay ugu xukumaan Dadka dhexdiisa waxay isku khilaafaan, iskumana khilaafin dhexdiisa kuwii la siiyey mooyee intay u yimaadaan xujooyin, Xasad dhexdooda ah dartiis, wuxuuna ku hanuuniyey Eebe waxay isku kihlafeen oo Xaq ah idankiisa kuwii rumeeyey, Eebana wa ku hanuuniyaa Ciddu doono Jidkiisa toosan.'),
(221, 2, 214, 'ma waxaad u maleyseen inaad galaysaan Jannada isagoon idiin imaanin wixii u yimid kuwii tegay ee hore oo kale, waxaa taabtay Baahi iyo Cudur, waana la gilgilay intuu ka dhaho Rassulku iyo kuwa rumeeyey lana jira, waa goorma gargaarka Eebe, gargaarka Eebana waa dhawyahay.'),
(222, 2, 215, 'waxay ku warsan maxay bixiyaan, waxaad dhahdaa waxaad bixisaan oo khayr ah waxaa mudan Labada waalid qaraabada, Agoonta, Masaakiinta iyo Soodaha Waxaad fashaana oo kahr ah Eebe waa ogyahay.'),
(223, 2, 216, 'waxaa laydinku waajibiyey Dagaal idinkoo neceb, waxaa laga yaabaa inaad nacdaan wax, isgoo khayr idiin ah, waxaana laga yaabaa inaad jeelaataan wax isagoo shar idiin ah, Eeba wax og idinkuse ma ogdin.'),
(224, 2, 217, 'waxay ku warsan Dagaalka Bisha xurmaysan (Rajab iyo wax la mid ah) waxaad dhahdaa dagaalku dhexdiisa waa waynyahay, ka celinta Jidka Eebe iyo ka Gaalowgiisa iyo Masaajidka xurmeysan iyo ka bixinta ehelkiisa yaase kaga wayn Eebe agtiisa. Gaalaynta Dadka yaana ka wayn Dilka, kama tagaan gaalada inay idinla dagaalamaan intay idinka eeliyaan Diintiinna hadday karaan, ruuxii ka ridooba Diintiisa oo dhinta isagoo Gaal ah kuwaasi waa kuwa buray camalkoodu adduun iyo aakhiraba kuwaasina waa Naarta Ehelkeeda, wayna kuwaari dhedeeda.'),
(225, 2, 218, 'kuwa rumeeyey Xaqa iyo kuwii Hijrooday ee ku jahaaday Jidka Eebe kuwaasi waxay rajayn naxariista Eebe, Eebana waa dambidhaafe naxariista.'),
(226, 2, 219, 'waxay ku waydiin Khamrada iyo Qamaarka waxaad dhahdaa waxaa ku sugan dhexdooda Dambi weyn, iyo wax Naefi ah dambigooda yaana ka weyn naefigooda, waxayna kuwaydiin maxay bixiyaan, waxaad dhahdaa (bixiya) saa\'idka, saasuuna Eebe idinku caddayn aayadihiisa waxaadna mudantihiin inaad fikirtaan.'),
(227, 2, 220, 'adduun iyo aakhiraba (inaad ka fikirtaan) waxayna ku waydiin agoonta, waxaad dhahdaa wanaajintooda yaa kahyrbadan, haddaad dheehdaana waa Walaalihin, Eebana waa ogyahay cidda waz fasaadin iyo kan hagaajin, haduu doono Eebana wuu idin dhibi lahaa, Eebana waa adkaade falsan.'),
(228, 2, 221, 'ha guursanina Haweenka mushrikaadka ah, intay ka rumeeyaan, addoon rumaysan xaqa yaa ka khayr roon mid mushrikad ah (Eebe la wadaasjisay cibaada) haba idiin yaabgaliso, hana u guurinina ragga mushrikiinta ah intay ka rumeyn, addoon rumeeyey xaqa yaa ka khayr badan mid mushrik ah haba idin yaab galiyee, kuwaasu waxay idiin ku yeedhi Naarta, Eebana wuxuu idiin ku yeedhi Janno iyo Dambidhaaf idinkiisa, wuxuuna u caddayn aayaadkiisa Dadka si ay u xusuustaan.'),
(229, 2, 222, 'waxay ku waydiin Caadada (Ilaweenka) dheh waa dhib (wasakh) ee ka fogaada meeja Caadada, hana u dhawaanina intay ka daahir noqon, markay daahir noqdaan uga taga meejuu idinfaray Eebe, Ilaahaynna wuxuu jecelyahay kuwa toobadda badan, wuxuuna jecelyahay kuwa isdaahiriya.'),
(230, 2, 223, 'Ilaweenkiinnu beerbay iddinyihiin ee ugutaga beertiinna saad doontaan «oo bannaan» una hormarsada naftiinna «wanaag» Eebana ka dhawrsada ogaadana inaad la kulmaysaan Eebe una bishaaree Muminiinta.'),
(231, 2, 224, 'ha uga yeelina Eebe kabdhood dhaartiinna adinkoon samo falayn oo dhawrsanayn, oo hagaajineyn Dadka dhexdiisa, Eebana waa maqle wax og.'),
(232, 2, 225, 'iddin ma qabanayo Eebe dhaar qasad la \'aaneed, wuxuuse idiin qaban waxay kasbatay quluubtiinnu, Eebana waa dambi dhaafe dul badan.'),
(233, 2, 226, 'kuwa ka dhaarta Haweenkooda (tan iyo muddo) waxaa la sugi afar bilood, hadday ka noqdanna Eebe waa dambi dhaafe naxariista.'),
(234, 2, 227, 'hadday goostaan furidna Eebe waa maqle oge ah.'),
(235, 2, 228, 'Haweenka la furayna waxay sugi naftoodu saddex daahir, mana u banaana inay qariyaan waxa Eebe ku abuuray Makaankooda haddy yihiin kuwo rumeeyey Eebe iyo Maalinta dambe. Ragoodaana ku xaq badan soo celintooda arrintaas (cidadaas dhexdeeda) hadday doonaan wannag, Haweenkuna waxaay leeyihiin wax la mid ah waxa korkooda ah si fiican, Raggase waxaa ugu sugnaa korkooda Darajo (Dheeraad) Eebana waa adkaade falsan.'),
(236, 2, 229, 'furiddu waa labo jeer, intaas ka dib ama waa hayshaho fiican, ama sii dayn fiican, mana bannaana inaad ka qaadataan waxaad siiseen wax, inay ka cabsadaan inayn oogin xuduudda Eebe Mooyee, haddaad ka cabsataan inayan oogin xuduudda Eebe wax dhib ah ma saarro korkooda waxay isku furato, tassi waa xuduudda Eebe ee ha tallaabina ruxii tallaaba xuduudda Eebe kuwaasi waa daalimiinta.'),
(237, 2, 230, 'hadduu furo uma banaana kadib intay ka guursato Nin kale, hadduu (kaasi) furana dhib ma saarra hadday is Ceshadaan (Isguursadaan) hadday u malayn inayoogixuduudda Eebe, taasi waa xuduudda Eebe wuxuuna u caddayn ciddi wax og.'),
(238, 2, 231, 'haddaad furtaan Haweenka oy gaadhaan mudadooda u haysta si fiican ama u siidaaya si fiican, hana u haysanina inaad dhibtaan si aad u xadgudubtaan. ruuxii fala sidaas wuxuu dulmiyey naftiisa, ee ha ka yeelina aayaadka Eebe jees jees, xusuustana Nicmada Eebe ee korkiina, iyo wuxuu ku dejiyey korkinna oo Kitaab iyo xigmad ah (Sunnada) wuu idinku wacdoon Eebe Quraanka, ee ka dhawrsada Eebe, ogaadana in Eebe wax kasta ogyahay.'),
(239, 2, 232, 'haddaad furtaan Haweenka oo ay gaadhaan mudadooda ha u diidina inay guursadaan nimankooda hadday raalli isu noqdaan si fiican, arrintaasu waa mid la idinku waanin ciddi idinka mid ah oo rumayn Eebe iyo Maalinta dambe, saasaana idiin waanaagsan oo idiin nadiifsan, Eebana waa ogyahay idinkuna ma ogidin.'),
(240, 2, 233, 'waalidaadka (Hooyooyinku) waxay nunjin Caruurtooda Labo Sano oo dhamaystiran Cidii doonta inay taamyeesho nuugidda, kan wax loo dhalayna waxaa saaran Cunadooda iyo arradbaxooda si fiican lagumo dhibo naf waxayna awoodin laguma dhibo Hooyo Ilmaheeda dartiis (lamana dhibo) aabo ilmihiisa dartiis, kan wax dhaxlaya waxaa saaran taaso kale, hadday doonaan gudhin iyagoo raali ah iyo wadatashi dhib ma saarra korkooda, haddaad doontaan inaad ka nuujin dalabtaan Caruurtiina (Haweenay Kale) dhib korkiinna ma saarra haddaad udhiibtaan waxaad bixinaysaan si fiican, ka dhawrsada Eebe, ogaadana in waxaad camalfalaysaan Eebe arko.'),
(241, 2, 234, 'kuwa la oofsado (Dhinto) oo idinka mid ah oo ka taga Haween waxay sugi (Ciddo) naftoodu afar bilood iyo toban bari, markay gaadhaan mudadoodana wax dhib ah idinkama saarra waxay ku falaan naftooda si fiican, Eebana waxaad falaysaan waa ogyahay.'),
(242, 2, 235, 'dhib (dambi) korkiinna ma aha waxaad sarbeebtaan oo hadal Haween ah (doonid) ama aad ku qarisaan naftiina, Eebe waa ogyahay inaad sheegaysaan Haweenkaas, laakiin hawgu yaboohina qarsoodi inaad dhahaysaan hadal fiican mooyee, ha goynin (ha u qasdina) xidhidda guurka intuu ka gaadho Kitaabku «ciddu» Watigooda ogaadana in Eebe ogyahay waxa naftii na ku sugan ee ku digtoonaada, ogaadana in Eebe dambi dhaafe dulsan yahay.'),
(243, 2, 236, 'dhib ma saaro korkiina hadaad Haweenka furtaan idinkoon taaban ama u qardarin meher, ugu deeqa kii hodona qaddarkiisa kan cidhiidhyanna qaddarkiisa, waa raaxayn wanaag ah oo xaq ku ah kuwa dhawrsada.'),
(244, 2, 237, 'haddaad furtaan Haweennka taabasho ka hor idinkoo wax (Merher ah) u qaddaray waxay mudan kala badhka waxaad qaddarteen, inay bafiyaan mooyee ama uu cafiyo midka gacantiisu xidhidda nikaaxu ku jiro, (waa ninka ama waliga) haddaad iska cafisaan yaa u dhowse dhawrsa shada, hana halmaamina fadliga (dheeraadka) dhexdiina ah, Eebana waxaad camalfalaysaan wuu arkaa.'),
(245, 2, 238, 'xifdiya (ilaaliya) Salaadaha iyo Salaadda dhexe (sharafta badan) (Carsaka) una istaaga dar Eebe idinkoo adeeci, (khushuuci).'),
(246, 2, 239, 'haddaad cabsataan Tukada idinkoo lug ah ama saaran gaadiid. markaad aamin noqotaan xusa Eebe siduu idiin baray waxaydaan aqoonin, (caadi u tukada).'),
(247, 2, 240, 'kuwa la oofsado (dhinto) idinka mid ah kana taga Haween dardaaranka Haweenkoodu waa u raaxayn tan iyo Sano iyagoon la bixinayn, haddayse baxaan dhib (Dambi) ma saarra korkiinna waxay ku falaan naftooda oo wanaag ah, Eebana waa adkaade falsan.'),
(248, 2, 241, 'kuwa la furo waxaa u sugnaaday gacan maris (Mataac) si fiican, waxayna ku tahay Xaq kuwa dhawrsada.'),
(249, 2, 242, 'saasuuna Eebe idiinku caddayn aayaadkiisa inaad kastaan darteed.'),
(250, 2, 243, 'ka warran (warkoodu ma ku soo gaadhay) kuwii ka baxay guryahooda iyagoo kuman ah digtooni geeri darteed, markaas ku yidhi Eebe dhinta, soona nooleeyey, Eebana waa saaxibul fadli (dheeraad) Dadka korkiisa, Dadka badankiisuse kuma mahdiyaan.'),
(251, 2, 244, 'ku dirira Jidka Eebe, ogaadana in Eebe maqli oo ogyahay.'),
(252, 2, 245, 'waa kuma kan amaah fiican gashan Eebe, oo uu u laban laabo, Laban laab badan, Eebana wuu ku cidhiidhiyaa Rizqiga oo u waasiciyaa (Cidduu doono) xaggiisaana loo celiyaa Dadka.'),
(253, 2, 246, 'ka warran (warkoodu ma ku soo gaadhay) Madaxdii Bani Israa\'iil Muuse ka dib markay ku dheheen Nabigoodii Noo soo bixi Malig (xaakim) oon ku dagaallanno Jidka Eebe, wuxuuna yidhi armaad mudantihiin haddii laydinku faralyeelo Dagaalka inaydaan dagaallamin, waxayna dhaheen maxaanaan ugu dagaalamayn Jidka Eebe isagoo nalaga bixiyey guryahannagii iyo Caruurtannadii (Wiilashannadii) markii lagu faralyeelay korkooda Dagaalkii wey jeedsadeen, in yar oo kamid ah mooyee, Eebana waa ogyahay daalimiinta.'),
(254, 2, 247, 'wuxuuna ku yidhi Nabigoodii Eebe wuxuu idiinky bixiyey Dhaaluut Malig (Xaakim), waxayna dheheen sidee ngu ahaan xukun korkannaga annago ka mudan Xukunka aan lana siinin Xoolo Waasac ah (Badan) wuxuuna yidhi (Nabigii) Eebe wuu u doortay Korkiina wuxuuna u siyaadiyey Waasacnimo Cilmi iyo Jidh (Xoog) Eebana wuxuu siiyaa Xukunkiisa Cidduu doono, Eebana waa Deeqbadane og.'),
(255, 2, 248, 'Nabigoodii wuxuu ku yidhi calaamadda Xukunkiisu waa inay u timaado taabuud (sanduuq) oy ku jirto xasiloonida Eebihiin iyo wax ka soo hadhay waxay ka tageen ehelkii Muuse iyo ehelkii Haaruun oo Mala\'igtu xambaari arrintaasna calaamaa idiinku sugan haddaad tihiin Mu\'miniin.'),
(256, 2, 249, 'markuu la baxay Dhaluut Askartii wuxuu yidhi Eebe wuxuu idinku imtixaami Wabi ee eidii ka cabta igamid ma aha, ruuxaan wax ka dhadhaminna waa iga mid ruux ku qaata gacanta qaadasho mooyee, waxayna ka cabbeen xaggiisa wax yar mooyee markuu tallaabay isaga colkii rumeeyay la jiray, waxay dhaheen awood uma lihin Maanta Jaaluud iyo colkiisa, waxayna dhaheen kuwii yaqiinsanaa inay la kulmi Eebe badanaa intay koox yari ka adkaatay koox badan idinka Eebe, ilaahayna wuxuu la jiraa kuwa Samra.'),
(257, 2, 250, 'markay u soo muuqdeen Jaaluud iyo Colkiisii waxay dhaheen Mu\'miniintii Eebbow ku shub korkannaga Samir, sugna Gomadahanaga noogana gargaar Qoomka Gaalada ah.'),
(258, 2, 251, 'markaasay (Mu\'miniintii) jabiyeen Gaaladii idinka Eebe, wuuna dilay Daawuud Jaaluud, wuxuuna siiyey Eebe Daawuud Xukun iyo xigmo (Nabinimo iyo Cilmi) wuxuuna baray wuxuu doono, hadayan jirin ku reebidda eebe dadka qaarkood qaarka (kale) waxaa fasaadi lahaa dhulka hasayeeshee Eebe waa fadli u saaxiib «u deeqe» caalamka.'),
(259, 2, 252, 'taasi waa aayadka Eebe waana kugu akhriyaynaa si xaq ah, adiguna Waxaad kamidtahay kuwa la soo diray «Rasuulada»;'),
(260, 2, 253, 'kuwaasu waa Rasuuladi Waana ka fadilnaa qaarkood warka (kale) waxaa ka mid ah kuwo la hadlay Eebe wuuna koryeelay qarkood darajooyin. waxaana siinay Ciise Binu Maryama xujooyin, Waxaana ku xoo jinay ruuxdii dahirka ahayd Malaku Jibriil. haduu Ebe doono ismalaayeen kuwii ka dambeeyay intay u timid xujooyin kadib. laakiin way is khilaafeen waxaa ka Midah mid rumeeyay (xaqa) waxaana kamidah mid ka gallobay. haduu Ebe doonana isma laayeen. laakiin Eebe wuxuu falaa wuxuu doono.'),
(261, 2, 254, 'kuwa (xaqa) rumeeyow nafaqeeya (bixiya) waxaan idinku arzuqnay qaarkood ka hor Imaatin maalin (Qiyaamada) oon gadasho jirin iyo saaxiibtinimo iyo shafeeco (midna) Gaaladuna iyaga unbaa daalimiin ah.'),
(262, 2, 255, 'Eebe Ilaahaan isada ahayn ma jiro, waana Eebaha Nool, Khalqigana maamula. Ma qabato Lulmo iyo hurdo (midna) wuxuuna iska leeyahay waxa Samooyinka ka sugan iyo Dhulka, waa kuma kan ka shafeeci agtiisa (Eebe) idankiisa mooyee, wuxuu ogyahay (Eebe) waxa Dadka hortooda ah iyo waxa ka dambeeya mana koobaan wax Cilmigiisa ah, wuxuu Eebe doono mooyee, wuuna u waasacnoqday kursigiisu Samaawaadka iyo Dhulka, mana Cusleeyo ilaalinteedu, Eebana waa sareeye wayn.'),
(263, 2, 256, 'Igraah (Qasab) ma aha Diinta galideedu, Hanuunkii (limaanku) waa ka cadaaday (soocanyahay) Baadida (Gaalnimada) ruuxiise diida Dhaaquut (shayaadiinta iyo xuma fare wixii ah) oo Tumeeya Eebe wuxuu qabsaday xadhig (guntay) sugan oo adag (Islaamka) oon go\'ayn, Eebana waa maqle og.'),
(264, 2, 257, 'Eeba waa u gargaaraha kuwa rumeeyey xaqa, wuxuuna ka bixiyaa mugdiga (Gaalnimada) una bixiyaa Nuurka (iimaanka), kuwa Gaaloobayna gargaarahoodu waa Dhaaquut (Xumaan fare) wuxuuna ka bixiyaa Nuurka una bixiyaa Mugdiga (Gaalnimada) kuwaasina waa ehelka Naarta wayna ku waari dhexdeeda.'),
(265, 2, 258, 'ka warran (warkiisu nia ku soogaadhay) kit kula murmay Nabi Ibraahim Eebihiis, (waa Namruud) in Eebe siiyey xukun markuu Nabi Ibraahim yidhi Eebehay waa Nooleeye Dile, maarkaa yidhi (Gaalkii) amba waan nooleeyaa oon dilaa, markaas Nabi Ibraahim yidhi Eebe wuxuu ka keeni Qoraxda Bari ee ka keen Galbeed, markaasaa la wareeriyey (oo aamusay) kii gaaloobay, Eebana ma hanunniyo Qoom daalimiin ah.'),
(266, 2, 259, 'ka warran kii maray magaalo iyadoo la duntay darbigeedii (oo cidla ah) markaas yidhi sidee Eebe u nooleyn midan dhimasho (kharaah) kadib markaasaa Eebe dilay Boqol Sano (qadarkeed) markaasuu soo bixiyey wuxunna ku yidhi imisaad nagaatay adigoo mayd ah, uuna yidhi waxaan nagaaday maalin ama maalin qaarkecd, uu kuna yidhi (Eebe) waxaad nagaatay Boqol sano: ee day Cuntadaada iyo Cabidaada, oon doorsoomin, day Dameerkaaga waxaan kaaga yeeleynaa calaamo Dadka, day Lafaha sidaan u sookoryeeeleyno (u nooleyn) una huwinayno Hilib markuu u cadaaday xaalku wuxu yidhi waxaan ogahay in Eebe wax walba karo.'),
(267, 2, 260, 'Xusuusio markuu yidhi Nabi Ibraahim Eebow i tusi sidaud u nooleyso wax a dhinia uuna yidhi miyaadan rumeynin (Xaqa) oo uu yidhi waan rumeeyey laakiin (waxaan doonay) inuu xasilo qalhigay^u maiktias (Eebc) yidhi qaad afar shimbirood una iil xagaaga (JarJar) kadibna yeel Buur kasta qayb ka mid ah, markaas u ycedh waykuu imaan iyagoo ordi ogoowna in Eebe adkaade Falsan yahay.'),
(268, 2, 261, 'kiiwa ku bixiya Xoolahooda Jidka Eebe waxay la midyihiin Xabbad (Masago ah) oo bixisay Todobo Sabuul, Sabuul kastiina bixiyey Boqol xabbo Eebe waa u Laab laabaa (Ajirka) Cidduu doono Eebana waa Deeq badane og.'),
(269, 2, 262, 'kuwa ku bixiya Xuulahoooda Jidka Eebe markaasna raacinin waxay bixiyeen Manno iyo dhib waxay ku mudan ajrigooda Eebahood agtiisa, Cabsina korkooda ma ahaato, mana murugoon.'),
(270, 2, 263, 'hadal wanaagsan iyu dhaafid (Saamaxaad) yaa ka khayr badan Sadaqo uu raaco dhib, Eebana waa hodan dulbadan.'),
(271, 2, 264, 'kuwa xaqa remeeyow ha ku burinina sadaqadiina Manno iyo dhib sida kan u bixiya xoolihiisa istusla Dadka, aan rumeynin Eebe iyo Maalinta aakhiro, wuxuuna la mid yahay sida Sallax korkiisa Carro lahay oo roob badan heley oo ma karaan kuwaasu wax ka mid ah waxay kasbadeen (ajri kamahelaan) Eebana ma hanuuniyo Qoom gaalo ah.'),
(272, 2, 265, 'kuwa ku bixiya Xoolahooda doonidda raali ahaanshaha Eebe iyo yaqiinsi naftooda (Khayrka) waxay la midyihiin Beer ku taal meelsare oo heley Roob badan, oo markaas keentay Cunadeedii Labanlaab hadduusan Roob - badan helinna nu helo Roob yar (Shuux) Eebana waxaad Camalfalaysaan wuu arkaa,'),
(273, 2, 266, 'ma jecelyahay midkiin inay u ahaato Beer Timir iyo Canabyo ah oy dareeri dhexdeeda Wabiyaal oos ku leeyahay dhexdeeda wax kastoo Midho ah ayna hayso Da\'weyni uuna leeyahay Carrur tabaryar oo markaas beertii Assibto (ku dhaedo) Dabayl daran oo dab wadata oo Gubatay Beertii Saasu Eebenidiinku eadeeyaa aayaadkiisa si ad u fikirtaan.'),
(274, 2, 267, 'kuwa xaqa rumeeyow wax ka bixiya ka wanaagsan waxaad kasbateen, iyo waxaan idinka soo bixinay Dhulka, hana u qasdina kan xun inaad wax kabixisaan, oo idinkuba aydaan qaadateen, inaad indho laabtaan mooyee, ogaadana in Eebe yahay Hodan la mahdiyo,'),
(275, 2, 268, 'Shaydaanku wiixuu idiin yahoohi Faqri, wuxunna idin fari xumaan, Eebana wuxuu idiinyaboohi dambi dhaaf iyo Fadl, Eebana waa waasac (Deeqdiisu) waxna og.'),
(276, 2, 269, 'wiixuu siiyaa xigmo cidduu doono, ruuxii la siiyo xigmona waxaa la siiyey khayr Badan, wax waantoobo oon kuwa Caqliga leh ahayna majiro.'),
(277, 2, 270, 'waxaad bixisaan oo nafaqo ah (sadaqo) amaad ku nadartaan ou nadar ah Eebe waa ogyahay, daalimiina gargaare uma sugnaanin.'),
(278, 2, 271, 'haddaad muujisaan Sadaqaadka waa fiicantahay, haddaadse qarisaan ood siisaan fuqarada yaa idiin khayr badan, wuunu asturi Eebe xumaantiina (Dambiga) Eebana waxaad falaysaan wuu ogyahay.'),
(279, 2, 272, 'korkaaga ma saarra hanuunintoodu, Eebaase hanuuniya cidduu doono, waxaad bixinaysaan oo khay ah naftiinaa iska leh, umana bixisaan doonid Waji Eebe mooyee, waxaad bixisaan oo khayr ahna waa laydiin oofin, idinkoon laydin dulmiyeyn.'),
(280, 2, 273, '(waxaa muta Sadaqadaas) fuqarada lagu koobay Jidka Eebe (Jahaadka) oon karin inay dhulka ku safraan, wuxuna u malayn kaan ogayn Hodan dhawrsanaanta, waxaadse ku garan Calaamadooda, umana waydiiyaan Dadka ku eeleelin waxaad bixisaan oo khayr ah Eebe waa ogyahay (oo ka jasayn).'),
(281, 2, 274, 'kuwa bixiya Xoolahooda Habeen iyo Dharaarba qarsoodi iyo muuqaalba waxay ku mudan ajirkooda Eebalhood agtiis, Cabsina korkooda ma ahaato iyo murug midna.'),
(282, 2, 275, 'kuwa Cuna Ribada kama istaagaan (Qubuurahooda) siduu u istaago mid la dhiei Shaydaan taabasjadiis mooyee, waxaana ugu waean arrintaas inay dhaheen Beecu (Gadashadu) waa sida Ribada, Wuu baneeye Eebe Beeca (Gadashada) wuxuuna xarrimay Ribada, ruuxuu u yimaado waano Eebihiis oo reebtooma wuxuu mudan wixii horeeyey, amarkiisana waxaa (loo bandhigi) Eebe ruuxiise ku noqda (Ribada) kuwaasu waa asaxaabta (ehelka) Naarta wayna ku waari dhexdeeda.'),
(283, 2, 276, 'wuxuu tiraa Eebe Ribada wuxuuna badiyaa (barakeeyaa) Sadaqooyinka, Eebana ma jeela Gaalnimo badane Dambi badan Dhammaantiis.'),
(284, 2, 277, 'kuwa rumeeyey xaqa, oo Camalfiican falay, oo Oogay Salaadda, oo Bixiyey sakada waxay ka mudan ajirkooda Eebahood agtiisa, wax cabsi ah iyo murugana korkooda ma ahaato.'),
(285, 2, 278, 'kuwa xaqa rumeeyow ka dhawrsada Eebe kana taga waxa hadhay oo Ribo ah haddaad Mu\'mintihiin.'),
(286, 2, 279, 'haddaydaan falin (sidaas) ogaada Dagaal Eebe iyo Rasuulkiisa, haddaad Toobad keentaanna waxaad leedihiin Raasamaalkii Xoolihiina, wax dulminmaysaan idiina laydin ma dulmayo.'),
(287, 2, 280, 'haddii la helo isagoo caydh ah (kan wax laguleeyahay) ha la sugo intay uga fududaato, haddaad sadaqaysataan (ka saamaxdaan) yaa idiin khayr badan haddaad ogtihiin.'),
(288, 2, 281, 'kana dhawrsada Maalinta laydiin soo celin Eebe, kadibna qof walba loo oofin waxay kasbatay, lamana dulmiyo.'),
(289, 2, 282, 'kuwa xaqa rumeeyow haddaad wax kala Daynsataan tan iyo muddo magacaaban quora hana idiin kala quoro dhexdiina qof si Cadaalad ah, yunna diidin Quore inuu u qoro siduu wax u baray Eebe, ha quoro hana u yeedhiyo kan xaqa lagu leeyahay, hana ka dhawrsado Eebe, yuuna wax ka nuqsaaminin hadduu yahay kan xaqa lagu leeyahay safiih ama tabaryare ama uusan karin inuu yeedhiyo ha u yeedhiyo Waligiisu si Caddaalad ah, maragana ka yeela Laba marag oo Rag ah, haddaan laba Nin la helinna Nin iyo Laba haweenood ka raalli thiin marag ahaan (laga yaabo) inay midi halmaanto oo tankale Xusuusiso, yeyna diidin maragu haddii loo yeedho, hana ka daalina inaad quortann wax yar iyo wax waynba tan iyo mudadiisa, saasaa ku cadaaladbadan Eebe agtiisa una sugan maragga una dhaw inaan la shakiyin, inay tahay Tijaara (Ganacsi) joogta aad maamulaysaan dhexdiina oo korkiina dhib (Dambi) ma aha inaydaan qorin, Marag u yeela haddaad wax kala Gadanaysaan, yanna la dhibin Qore iyo Marag midna, haddaad fashaan waa faasiqnimadiina, ka dhawrsada Eebe waxbuu idin bariye, Eebana wax kasta waa ogyahay.'),
(290, 2, 283, 'haddaad safartihiin oydaan helin Qore rahan la qibloodo (ha badasho qoridda) haddu qaarkiin aamino qaar ha siiyo kii la aaminay amaanadiisa hana ka dhawrsado Eebihiisa barbaariyey, hana qarinina maragga ruuxii qariyana wuu dambaabi Qalbigiisu\' Eebena waxaad camalfashaan waa ogyahay.'),
(291, 2, 284, 'Eebaa iska leh waxa Samooyinka iyo Dhulka ku sugan, hadaad muujisaan waxa naftiina ku sugan ama qarisaan wuu idin ka xisaabin Eebe, wuuna u dhaafi cidduu doono ama Caadaabi Ciduu doono, Eebena wax kasta (oo uu doono) wuu karaa.'),
(292, 2, 285, 'wuu rumeeyey Rasuulku wixii looga soo dajiyey Xaga Eebihiis, Mu\'miniintuna (way rumeeyeen) dhamaana waxay rumeeyeen Eebe, Malaa\'igtiisa kulubtiisa iyo Rasuuladiisa, mana kala geynayno mid ka mid ah Rasuuladiisa dhexdooda, waxayna dhaheen waan maqalay oon adeecnay, dhaafidaada. Eebahanow xaggaagaana loo ahaan.'),
(293, 2, 286, 'kuma mashaqeeyo Eebe naf waxay awoodo mooyee, waxay mudan waxay kasbatay (oo wanaag ah) waxaana korkeeda ah waxay isu kasbatay (oo xumaano) Eebow ha noo qabanin haddaan halmaanno gafno, Eebow hanagu xambaarin Culays saad ugu xambaartay kuwii naga horeeyey, Eebow hanagu xambaarin (dhibin) waxaanaan awood u lahayn, na cafina oo noodhaaf noon naxariiso, adaa gargaarahanaga ahe, nooga gargaar qaarka gaalada ah.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, '(Waxay ku tusin mucjisada Quraanka).'),
(295, 3, 2, 'Eebe Haah kale majiro isaga mooyee, ee Nool, ee Maamula khaliga.'),
(296, 3, 3, 'Wuxuu kugu Dejiyey (Nabiyow) Kitaabka (Quraanka) si Xaq ah, isagoo u Rumcyn wixii ka Horeeyey, wuxuuna soo Dejiyey Eebe Tawreed iyo Injiil (waqti).'),
(297, 3, 4, 'Hore iyagoo Hanuun u ah Dadka, wuxuuna soo Dejiyey (Eebe) Furqaan (Quraanka) kuwa ka Gaaloobay Aayaadka Eebe waxay Mudan Cadaab Daran, Lebana waa Adkaade Aarsada.'),
(298, 3, 5, 'Eebana kagama Qarsoona Korkiisa waxba Dhulka iyo Samada midna.'),
(299, 3, 6, 'Waana Eebaha idinku Sawira (Abuura) Riximyada (Ilmo galeenka) siduu Doono, Eebe mooyee Ilaah kale ma jiro waana Adkaade Falsan.'),
(300, 3, 7, 'Eebe waa kan kugu soo dejiyey Kitaabka, waxaana ka mid ah Aayado sugan (Macnahoodu Cadyahay) oo ah Hooyada Quraanka (Asalkiisa) iyo kuwa kale oo isu eg, kuwa Qalbigoodu leexasho ku jirto waxay, Raacraacaan kan is shabaha (isu eg) doonid Fidmo (Xumaan) iyo doonid Ta\'wiil (Leexin Fasir) darteed, wax og Fasirka (kaasna) Ma jiro Eebe iyo kuwa ku Xididaystay Cilmiga mooyee, waxayna dhihi waan rumeynay kaas, dhammaana wuxuu ka ahaaday Eebeheen agtiisa, mana waantoobo kuwa caqliga lch mooyee.'),
(301, 3, 8, 'Eebow ha iilin Quluubtannada intaad hanuunisay ka dib. nagana sii agtaada Naxariis, adiga unbaa bixinbadane ehc.'),
(302, 3, 9, 'Eebow adaa u kulmin Dadka Maalin darteed (Qiyaamada) oon shaki lahayn, Eebana ma baajiyo Ballanka.'),
(303, 3, 10, 'kuwa Gaaloobay ugama taraan Xoolahoodu iyo Caruurtoodu Eebe xagiisa waxha, kuwaasina iyaga umbaaah Shidaalka Naarla.'),
(304, 3, 11, 'Waa sidii caadadii Fircoon iyo Ehelkiisii iyo kuwii ka horreeyey, waxay beeniyeen Aayaadkangii, markaasuu Qabtay Eebe Dambigooda dartiis, Eebana waa darantahay Ciqaabtiisu.'),
(305, 3, 12, 'Waxaad u dhahdaa kuwii Gaaloobay waa laydinka adkaan, waxaana laydiin soo kulmin Jahannamo iyadaana u xun Gogol.'),
(306, 3, 13, 'Waxaa iddinku sugan Aayad Labo Kooxood oo kulmay (Dagaalarnay) Kooxu Waxay ku Dagaalami Jidka Eebe tan kalana waa Gaal, waxyna ku arkaan Mu\'miniinta laba Jibaarkood Xagga aragtida Isha, Eebana waa ku ayidaa (Xoojiyaa) Gargaarkiisa Cidduu doono arrintaasna waanaa ugu sugan Ciddii Arag leh (wax garan).'),
(307, 3, 14, 'Waxaa loo qurxiyey Dadka Jacaylka waxa Naftu doonto oo ah Flaween, Wiilal iyo wax badan oo laban laaban oo Dahab iyo qalin ah, iyo Fardo laylyan oo darban, iyo Ancaamta (Xoolahanool) iyo Beeraha, arrintaasna waa raaxada Nolosha Aduun, Eebe agtiisaana noqosho wanaagsan leh.'),
(308, 3, 15, 'Maydiin ka warramaa wax ka khayroon kiinaas, kuwa dhawrsada waxaa ugu sugnaaday Eebahood agtiisa Jannoovin ay dareeri dhexdeeda Wabiyaal iyagoo ku waari dhexdeeda iyo Haween la Daahiriyey iyo raalli ahaansho Eebe. Eebana waa arkaa Addoomada.'),
(309, 3, 16, 'ee ah kuwa dhihi Eebow anagu waan rumeynay ee noo dhaaf Dambiyadanada nagana dhawr Cadaabka Naarta.'),
(310, 3, 17, 'Ee ah kuwa Samra ee Runloowgaah, ee Adeeca Eebe, ee wax bixiya, ee dambi dhaaf warsada Aroortii.'),
(311, 3, 18, 'Eebe wuxuu Qirayaa inaan Eebe mooyee Ilaah kale jirin, Malaa\'igtuna (way qiri) iyo kuwa Cilmiga leh, Maamulana Cadaalad, Eebe kale ma jiro isaga mooyee waana adkaade Falsan.'),
(312, 3, 19, 'Diintuna (Xaqa ah) Eebe agtiisa waa Islaamka, ismana khilaafin kuwii la siiyey Kitaabka intuu u yimid Cilmi mooyee, Xasad (Dulmi) dhexdooda ah dartiis, ruuxii ka Gaalooba Aayaadka Eebe Ilaahay waa deg deg badantahay Xisaabitiisu (wuu Ciqaabi).'),
(313, 3, 20, 'Hadday kula Xujaytamaan (Murmaan) waxaad dhahdaa waxaan u Flogaamiyey Wajigayga Eebe (Ani) iyo inta i raaeday, waxaad ku dhahdaa kuwa la siiyey Kitaabka (Ehelu Kitaabka) iyo Umiyiinta (Wax aqoon) ma Hogaansamaysaan (Hogaansama) hadday bogaansamaana way Hanuuneen, Hadday Jeedsadaanna waxaa uun ku saaran - Gaadhsiin, Eebana waa arkaa addoomada.'),
(314, 3, 21, 'Kuwa ka Gaaloobi Aayaadka Eebe oo u Dila Nabiyada Xaq darro oo Dila kuwa fara Caddaaladda oo Dadka ka mid ah ugu Bishaaree Cadaab Daran.'),
(315, 3, 22, 'Kuwaasi waa kuwuu buray (Hoobtay) Camalkoodu Adduun iyo Aakhiraba mana u sugnaan wax u gargaara.'),
(316, 3, 23, 'Ma aragtay (ka waran) kuwii la siiyey Qayb ka mid ah Kitaabka oo loogu yeedhi Kitaabka Eebe inuu kala xukurno dhexdooda markaas ay jeedsan Koox ka mid ah iyagoo diidan (Xaqa).'),
(317, 3, 24, 'Xaxaana ugu wacan inay dheheen ma na tabaato Naaru Maalmo mooyee, waxaa ku dhagray Diinlooda waxay been abuuran jireen.'),
(318, 3, 25, 'Ee say ahaan markaanu u kulmino Maalin aan shaki lahayn, loona oofiyo. (dhameeyo) Naf kastaa waxay kasbatay iyagoon la dulmayn.'),
(319, 3, 26, 'Waxaad dhahdaa Eebow adaa hanta Xukunka waxaad siisaa Xukunka Ciddaad doonto waxaad ka siibtaa «Wareejisaa» Xukunka Ciddaad doonto waxaadna cisaysaa Ciddaad doonto waxaadna Dulaysaa Ciddaad doonto adaana Hanta Khayrka, adaana wax kasta kara.'),
(320, 3, 27, 'Waxaadna dhexgelisaa Habeenka Maalinta, dhexgelisaa Maalinta Habeenka, kana soo bixisaa wax Nool wax dhimaad ah wax Nool, waadna Arzaaqdaa Ciddaad doonto Xisaab la\'aan.'),
(321, 3, 28, 'Yeyna ka yeelan Mu\'miniintu Gaalada Sokeeye Mu\'miniinta ka Sokow ruuxii fala saas waxba Eebe kama mudna, inaad dhawrsasho xaggooda ka dhawrsataan mooyee, wuxuuna idiinka digi Eebe Naftiisa, xaga Eebaana loo ahaan.'),
(322, 3, 29, 'Waxaad dhahdaa haddaad Qarisaan waxa Laabtiina ku sugan amaad muujisaan waa ogaan Eebe, wuxuuna ogaan waxa Samooyinka ku sugan iyo Dhulka, Eebana wax kasta uu (doono) wuu karaa.'),
(323, 3, 30, '(Xusuuso) Maalinta ay heli Nafkastaa waxay Camal fashay oo Khayr ah oo la soo xaadiriyey (la keenay) waxay Camal Fashay oo Xumaan ahna waxay jeelaan in la yeelo iyada iyo isaga dhexdooda muddo dheer, wuxuuna idiinka digi Eebe Naftiisa (Ciqaabtiisa) Eebana waa u Naxariistaha Adoomadiisa.'),
(324, 3, 31, 'Waxaad dhahdaa haddaad tihiin kuwo jeeel Eebe i raaca haydin jeelaado Eeebee hana idiin dhaafo dambigiinee, Eebana wa dambi dhaafe Naxariista.'),
(325, 3, 32, 'Waxaad dhahdaa adeeca Eebe iyo Rasuulkiisa, haddaad jeedsataan Eebe ma jeela Gaalada.'),
(326, 3, 33, 'Eebe wuxuu ka doortay Aadan, Nuux, Ehelkii Ibraahim iyo Ehelkii Cimraan Calaamka.'),
(327, 3, 34, 'Waana Farac Qaarkood Qaar ka yimid Eebana waa Maqle oge ah.'),
(328, 3, 35, 'Xusuuso markay tidhi Haweenaydii Cimraan Eebow waxaan kuugu Nadray waxaa uurkayga ku jira inuu Xor kuu ahaado (Cibaadadaada) ee iga aqbal adiga umbaa wax Maqleoge ehe.'),
(329, 3, 36, 'Markay dhashay Gabadh waxay tidhi Eebow waxaan dhalay Dhaddig, Eebana waa ogyahay waxay dhashay Labna la mid ma aha dhaddig, waxaana ka magacaabay Maryam waxaana kaa magan galin iyada iyo Faraceeda Shaydaanka la dhagaxyeeyey (la dheereeyey).'),
(330, 3, 37, 'Wuxuuna ka aqbalay Eebe aqbal fiican, wuuna koriyey korin fiican (Xanaano) waxaana kafaala qaaday (Dhaqaaleeyey) Zakariye, markastoos ugu galana Mixraabka (Qolka) wuxuu ka helaa agteeda Rizqi, wuxuuna dhahaa xaggee kaaga yimid Maryamow kan, waxayna dhahdaa wuxuu ka yimid Eebe agtiisa, Eebana wuu u Arzuqaa Cidduu doono Xisaab la\'aan.'),
(331, 3, 38, 'Halkaasuu baryey Zakariye Eebihiis, yidhina Eebow iga sii agtaada Farac wanaagsan adaa Maqla (ajiiba) Ducadee.'),
(332, 3, 39, 'Waxaana u dhawaaqday Malaa\'igtii isagoo taagan oo ku Tukan Mixraabka Eebe wuxuu kuugu Bishaarayn Yaxye isagoo rumeyn Kalimada Eebe (Ciise) Sayidna ah Dhawrsoonna, Nabina ah, ka mid ah kuwa wanaagsan.'),
(333, 3, 40, 'Wuxuu yidhi Eebow sidee iigu ahaan Wiil Anooy isoo gaadhay Duqnimo, Haweeneydeyduna Madhalaystahay, wuxuu yidhi saasuu ku falaa Eebe wuxuu doono.'),
(334, 3, 41, 'Wuxuu yidhi Eebow ii yeel Calaamo wuxuuna yidhi Calaamaddu waa inaadan la hadlin Dadka Saddex Maal mood ishaaro Mooyee, Xusna Eebahaa wax badan una Tasbiixso Galabatii iyo A roortiiba.'),
(335, 3, 42, '(Xusuuso) markay tidhi Malaa\'igtu Maryamoy Eebe waa ku doortay waana ku daahir yeelay wuxuuna kaa doortay Haweenka Caalamka.'),
(336, 3, 43, 'Maryamooy adeec (u khushuue) Eebahaa sujuudna Rukuuena (la tuko) kuwa rukuuea (Tukada).'),
(337, 3, 44, 'Taasi waa warka waxa Maqan oonnu kuu waxyoon mana aadan la joogin markey tuurayeen Qalimadooda koodii kafaala qaadi Maryam, mana aadan la joogin markey murmayeen.'),
(338, 3, 45, '(Xusuuso) markey tidhi Malaa\'igtu Maryamooy Eebe wuxuu kuugu Bishaarayn Kalimo Xaggiisa ah (Wiil) Magaeiisu yahay Masiix Ciise Ibnu Maryam isagoo Sharaf leh Adduun iyo Aakhiraba kana mid ah kuwa la soo dhaweeyey.'),
(339, 3, 46, 'Kulana hadli Dadka Dharabta iyo isagoo (Gadhmadoobe) nin wayn ah kana mid ah kuwa Suuban.'),
(340, 3, 47, 'Waxay tidhi (Maryam) Eebow sidee Ilmo iigu ahaan isagoon i taaban Dad (Nin wuxuu yidhi (Eebe) saasuu Eebe wax u abuuraa wuxun doono, markuu xukmin amarna wuxuu uun u dhahaa ahow wuuna ahaadaa.'),
(341, 3, 48, 'Wuxuuna (Eebe) bari Ciise Kitaabkka, Xigmad, Towreed iyo Injiil.'),
(342, 3, 49, 'Waa Rasuulka Bani Isra\'iil, (isagoo) u dhihi waxaan idiin la imid Aayad xaga Eebihiin ka ahaatay, waxaana idiinka abuuri dhoobada shimbira oo kale, oon afuufi ooy noqon shimbir Idamka Eebe, waxaana bogsiiyaa indhala\'aan kudhashe Baraslaha, waxaana nooleeyaa Maydka Idamka Eebe, waxaana idiin ka warrami waxaad Cunaysaan iyo waxaad ku kaydsanaysaan Guryihiinna, taasira waxay idiin tahay Calamad haddaad rumeynaysaan (Xaqa).'),
(343, 3, 50, 'Waxaana rumeyn wixii iga horreeyey oo Tawreed ah, waxaan idiin bannayn wixii laydinka reebay Qaarkiis, waxaana idiin la imid Calaamad xagga Eebihiin ka ahaatay, ee ka dhawrsada Eebe, ina adeeca.'),
(344, 3, 51, 'Ilaah waa Eebahay iyo Eebihiin ee Caabuda, taasaana ah Jidka toosane.'),
(345, 3, 52, 'Markuu ka kasay Ciise Xaggooda Gaalnimo yuu yidhi yaa iigu gargaari Eebe, waxayna dhaheen Xawaariyiintii (gargaarayaashii) anagaa ah gargaaraha Eebe (Diintiisa) waxaan rumeynay Eebe ee marag ka noqo inaan Muslimiin nahay.'),
(346, 3, 53, 'Eebow waan rumeynay waxaad soo dejisay waxaana raaenay Rasuulka ee nagu qor (nagadhig) la jirka markhaatiyada. (Umadda Nabi Muxamad).'),
(347, 3, 54, 'Way dhakreen, Eebana wuu ka abaalmariyey Eebana u wanaagsan wax abaal mariya.'),
(348, 3, 55, 'Xusuuso markuu ku yidhi Eebe Ciisow waan ku oofsan waana ku koryeeli, waana kaa dahirin kuwii gaaloobay, waxaana yeeli kuwa raaca (xaqa dhabta ah raaca) korka kuwa Gaaloobay tan iyo Qiyaamada, markaasna xaggaygaa laydiin soo celin waana idin kala Xukumi dhexdiina waxaad isku khilaafsanaydeen.'),
(349, 3, 56, 'Kuwa Gaaloobase waxaan Cadaabi, cadaab daran Adduun iyo Aakhiraba, mana helaan gargaare.'),
(350, 3, 57, 'kuwa (Xaqa) rumeeyeyse oo camal fiican falay wuxuu u oofin (dhammayn Eebe) ajirkooda, Eebana ma jeela daalimiinta.'),
(351, 3, 58, 'kaasaan ku akhriyeynaa korkaaga oo aayaad ah iyo xus (Quraan) xigmo leh.'),
(352, 3, 59, 'Tusaalaha Ciise Eebe agtiisa waa sida (Nabi) aadam, Eebaa ka Abuuray Carro markaasuu u yidhi ahow wuuna ahaan.'),
(353, 3, 60, 'Xaqu Eebahaa nuu ka ahaaday ee ha noqonin kuwa shakiya.'),
(354, 3, 61, 'ee ruuxii kugula dooda intuu kuu yimid Cilmi ka dib waxaad dhahdaa kaalaya aan u yeedhno Caruurtannada iyo Caruurtiinna iyo Naftanaada iyo Naftiinna markaas aan baryootanno oon laeanadda Eebe kor yeello Beenaalayaasha.'),
(355, 3, 62, 'Kaas (Quraanka) unbaa Qisooyin Xaq ah, Eebe mooyee Ilaah (kalana) ma jiro, Eebana waa adkaade falsan.'),
(356, 3, 63, 'Hadday jedsadaan Gaaladuna Eebe waa ogyahay kuwa wax fasahaadiya.'),
(357, 3, 64, 'Waxaad dhahdaa Nabiyow Ehelu Kittabow u kaalaya kalimo (odhaah) aan u simanahay aniga iyo adinkaba oo ah inaynaan caabudin Eebe mooyee, (wax kale) oynaan lana wadaajin waxba kana yeelan qaarkeen qaar Eebayaal, Hadday jeedsadaanna waxaad dhahdaa marag noqda inaan Muslimiin nahay.'),
(358, 3, 65, 'Ehelu Kitaabow maxaad ugu murmaysaan (Nabi) Ibraahim, Tawreed iyo Injiilba lama soo dejin gadaashiisa mooyee, meydaan wax kasayn.'),
(359, 3, 66, 'Kuwanow (Yuhuud iyo Nasaaro) waxaad ku muranteen waxaad wax ka ogtihiin ee maxaad ugu murmeysaan waxaydaan Cilmi u lahayn, Eebaa og idinkuse wax ma ogidiin.'),
(360, 3, 67, 'Ma aha (Nabi) Ibraahim Yuhuud iyo Nasaara (midna) waase toosane Muslim ah kana mid aha Mushrikiinta (Gaalada).'),
(361, 3, 68, 'Waxaa Dadka ugu mudan (Nabi) Ibraahim kuwii raacay iyo nabigan (Muxamad ah) iyo kuwa (Xaqa rumeeyey) Eebana waa Waliga (Gargaare) Mu\'miniinta.'),
(362, 3, 69, 'Waxay jeelaatay koox ka mid ah Ehelu Kitaabka inay idin baadiyeeyaan, mana baadiyeeyaan waxaan Naftooda ahayn, mana kasayaan.'),
(363, 3, 70, 'Ehelu Kitaabow maxaad uga Gaaloobaysaan Aayadka Eebe idinkoo marag ah (og xaqnimadeeda).'),
(364, 3, 71, 'Ehelu Kitaabow maxaad ugu Khaldaysaan Xaqa baadil (Xumaan) oo u qarineysaan xaqa idinkoo og.'),
(365, 3, 72, 'Waxay dhaheen koox ka mid ah Ehelu Kitaabka rumeeya waxa lagu soo dajiyey kuwa (Xaqa) rumeeyey (Quraanka) Maalinta horteeda kana gaalooba maalinta gadaasheeda waxay u dhawyihiin Mu\'miniintu inay noqdaane (Gaaloobaan).'),
(366, 3, 73, 'Hana aaminina (rumeynina) ruuxaan Diintiinna raacin, waxaad dhahdaa Hanuun waa hanuunka Eebe, in la siiyo cid wax la mid ah waxa laydin siiyey ama idinkula doodaan Eebihiin agtiisa, waxaad dhahdaa Fadligu gacanta Eebe yuu ku suganyahay wuxuuna siiyaa Cidduu doono, Eebana waa waasae (deeq badane) og.'),
(367, 3, 74, 'Wuxuuna ku gaaryeelaa Naxariistiisa cidduu doono, Eebana iska leh Fadliga Wayn.'),
(368, 3, 75, 'Ehelu Kitaabka waxaa ka mid ah Ruux Haddaad ku Aaminto Xoolo badan ku siin (Nasaarada Qaarkeed) waxaana ka mid ah mid haddaad ku Aaminto Diinaar aan ku siinaynin, inaad kor taagnaato mooyey (waa Yuhuudda iyo wixii la mid ah) waxaana ugu wacan Arrintaas inay dhaheeh naguma laha Umiyiintu Jid, waxayna ku dhihi Eebe Been iyagoo og.'),
(369, 3, 76, 'Saas ma aha ee ruuxii oofiya Ballankiisa oo Dhawrsada Eebe waa Jecelyahay Dhawrsadayaasha.'),
(370, 3, 77, 'Kuwa ku Gata Ballankii Eebe iyo Dhaartoodii Lacag (Qiimo) yar kuwaasu Nasiib kuma laha Aakhiro, lamana hadlo Eebe (si Naxariis ah) mana dayo Xaggooda Maalinta Qiyaame, mana Daahiriyo, waxayna mudan Cadaab daran.'),
(371, 3, 78, 'Waxaa ka mid ah (Yuhuudda) Kox ku Leexisa Carraabkooda Kitaabka (Quraanka) si aad ugu malaysaan Kitaabka, kana mid ma aha Kitaabka waxayna odhan Isagu wuxuu ka ahaaday Eebe agtiisa kamana ahaanin Eebe agtiisa. waxayna ku dhihi Eebe Been iyagoo og.'),
(372, 3, 79, 'Kuma haboona Dad in Eebe siiyo Kitaab, Xukun iyo Nabinimo markaas ku dhaha Dadka Addoomo ii noqda Eebe ka sokow, hasayeeshee noqdo Cumino (Dabeeco fiican) Kitaabkaad baraysaan dartiis iyo waxaad baranaysaan.'),
(373, 3, 80, 'Idinmana farayo Eebe inaad ka yeelataan Malaa\'igta iyo Nabiyada Eebayaal, ma wuxuu idin fari Gaalnimo intaad Muslim noqoteen ka dib.'),
(374, 3, 81, '(Xusuuso) markuu ka qaaday Eebe Ballan adag Nabiyada waxaan idinsiiyo oo Kitaab iyo Xigmo ah markaas idiin yimaado Rasuul rumeyn waxaad haysatan waa inaad rumeysaan una gargaartaan, kuna yidhi (Eebe) Ma qirteen oo ma qaadeen Arrintaas Ballankeyga ah oo dheheen waan qirnay (ogolaanay) markaas ku yidhi Marag ka noqda Anna la jirkiina yaan ka mid noqon kuwa marag kici.'),
(375, 3, 82, 'Ruuxiise jeedsada intaas ka dib kuwaasi waa kuwo Faasiqiin ah.'),
(376, 3, 83, 'Ma Diinta Eebe waxaan ahayn yey rabaan isagoo u hogaansamay waxa Samaawaadka iyo Dhulka ku sugan ogolaan iyo ikraahba Xaggiisaana loo Celin (Dadka).'),
(377, 3, 84, 'Waxaad dhahdaa waxaan rumaynay Eebe iyo waxa nalagu soo dajiyey iyo waxa lagu soo dajiyey (Nabi) Ibraahim, Ismaaeiil, Isxaaq, Yacquub, Faracyadii, wixii la siiyey Muuse, Ciise iyo Nabiyadii Xagga Eebahood kala bixin mayno dhexdooda Anaguna Eehaan u hogaansameynaa.'),
(378, 3, 85, 'Ruxii doona Diin aan Islaamka ahayn laga aqbali maayo, Aakhirana wuxuu noqon kuwa khasaaray.'),
(379, 3, 86, 'Sidee u Hanuunin Eebe Qoom Gaaloobay rumayn ka dib, oo qiray in Rasuulku Xaq yahay una Yimaadeen Xujooyin, Eebana ma hanuuniyo Qoom Daalimiin ah.'),
(380, 3, 87, 'Kuwaas abaalkoodu waa Lacnad Eeboo korkooda ah iyo Malaa\'igta iyo Dadka Dhammaantiis.'),
(381, 3, 88, 'Wayna ku waari (Ciqaatbtooda) dhexdeeda lagamana fududeeyo Cadaabka lamana Sugo.'),
(382, 3, 89, 'Kuwa Toobad keena intaas ka dib mooyee oo wanaajiya Eebana waa dambi dhaafe Naxariista.'),
(383, 3, 90, 'Kuwa Gaalooba rumayn ka dib, markaas kororsada Gaalnimo lagama aqbalo Toobadooba kuwaasina waa kuwa Dhumay.'),
(384, 3, 91, 'Kuwii Gaaloobay oo dhintay iya goo Gaalo ah lagama aqbalo mid ka mid ah Dhulka Muggii oo Dahab ah hadduu isku furto, kuwaasina waxaa u sugnaaday Cadaab daran wax u gargaarana ma leh.'),
(385, 3, 92, 'Ma gaadhaan sama fal intaad wax ka bixisaan waxaad jeeeshihiin, waxaad bixisaana Eebe waa ogyahay.'),
(386, 3, 93, 'Cuntada dhamaanteed xalaal bay u ahayd Banii-israa\'iil wuxuu ka reebay Israa\'iil Naftiisa mooyee, soo degidii Tawreed ka hor waxaad dhahdaa keena Tawreed oo Akhriya haddaad runlow tihiin.'),
(387, 3, 94, 'Ruuxii ku been abuurta Eebe korkiisa intaas ka dib kuwaasu waa uun Daalimmin.'),
(388, 3, 95, 'Waxaad dhahdaa Runbuu sheegay Eebe, ee raaca Milladda Ibraahiim (Jidkiisii) isagoo toosan, kamana mid ahayn Mushrikiinta.'),
(389, 3, 96, 'Guriga ugu horreevey ee loo soo dejiyey Dadka waa kan Maka (Kachada) waana barakaysanyahay waana Hanuunka Caalamka.'),
(390, 3, 97, 'Waxaa ku sugan Aayaad Cad Cad Maqqaamu Ibraahiimna (waa ku yaal) Ruuxii galana wuxuu ahaan Aamin, Eebana wuxuu ku leeyahay Dadka Xajka Baydka Ruuxii kara Wadada, Ruuxii Gaaloobana Eebe waa ka kaaftoonyahay Caalamka.'),
(391, 3, 98, 'Waxaad Dhahdaa Ehelu Kitaabow maxaad uga Gaaloobaysaan Aayaadka Eebe, isagoo Eebana Marag u yahay waxaad Camal falaysaan.'),
(392, 3, 99, 'Ehelu Kitabow maxaad uga Celinaysaan Dadka Eebe Ruuxii Rumeyn idinkoo la dooni (Xaqa) Qallooc idinkoo marag ah Eebana ma halmaansana waxaad Falaysaan.'),
(393, 3, 100, 'Kuwa (Xaqa) rumeeyow Haddaad Adeecdaan (Maqashaan) Koox ka mid ah kuwa la Siiyey Kitabka (yuhuud iyo Nasaara) waxay idiin Celin Iimaankiinna ka dih Gaalnimo.'),
(394, 3, 101, 'Seed u Gaaloobaysaan idinkoo laydinku kor Akhrin Aayaadka Eebe Dhexdiinnana Joogo Rasuulkiisii, ruuxii Cuskada Eebe (oo ka dhawrsada) waxaa lagu hanuuniyey Jidka Toosan.'),
(395, 3, 102, 'Kuwa (Xaqa) rumeeyow ka dhawrsada Eebe dhawrsashadiisa Xaqa ah, hana dhimanina idinkoon Muslim ahayn.'),
(396, 3, 103, 'Qabsada Xadhigga Eebe (Islaamka) dhamaantiin hana kala tagina, xusana Nicmada Eebe ee korkiinna ah, markaad Col ahaydeen oos isu Dumay Quluubtiinna ood Noqoteen Niemada Eebe darteed Walaalo, idinkoo ahaa Qar God Naar ah, oos idinka koriyey Xaggeeda, saasuu idiinku Cadayn Eebe Aayaadkiisii si aad u Hanuuntaan.'),
(397, 3, 104, 'Ha laydinka helo Umad u yeedha Khayrka oo fari Wanaagga oo ka reebta waxa la Naco (Xumaanta) kuwaasina waa uun kuwa Liibaanay.'),
(398, 3, 105, 'Hana noqonina kuwa kala tegay oo kale ee iskhilaafay intay u yimaadeen Xujooyin ka dib kuwaasina waa kuwa Cadaab weyn u Sugnaaday.'),
(399, 3, 106, 'Maalintay Caddaan (Nuuri) Wajiyaal oyna Madoobaan (Doorsoomi) Wajiyaal, kuway Madoobaato Wajiyadoodu (Waxaa lagu dhihi) miyaad Gaaloowdeen Rumeyntiinii ka dib, Dhadhamiya Cadaabka Gaalnimadiinii darteed.'),
(400, 3, 107, 'Kuwuu Caddaado (Nuuro) Wajigoodu Naxariista Eebe Dhexdeeday ku waarayaan.'),
(401, 3, 108, 'Taasi waa Aayadkii Eebe oon ugu Akhriyeynaa korkaaga si Xaq ah, Eebana ma aha mid la doona Dulmi Caalamka.'),
(402, 3, 109, 'Eebe waxaa u sugnaaday waxa Samaawaadka iyo Dhulka ku Sugan, Xaggiisaana loo Celiyaa Umuuraha.'),
(403, 3, 110, 'Waxaad tihiin Ummadda u Khayroon ee loo soo bixiyey Dadka, waxaad fartaan Wannaagga waxaadna ka reebtaan Xumaanta waxaadna ru meysaan Eebe, hadday rumeeyaan Ehelu Kitaabka (Xaqa) Saasaa u khayrroonaanlahayad, waxaa ka mid ah Mu\'miniin, badankoodna waa Faasiqiin.'),
(404, 3, 111, 'Idin wax yeellayn mahayaan wax dhibah mooyee, hadday idin la dagaallamaana waxay idiin jeedin Dabada (way carari) loona Gargaari Maayo.'),
(405, 3, 112, 'Waxaana lagu qadaray Korkooda Dulli meel kastooy joogaanba, Xadhig Eebe iyo Xadhig Dad mooyee, waxayna la noqdaan Cadho Eebe, waxaana lagu qadaray Miskiinimo, waxaana ugu wacan inay ka gaaloobaan Aayaadka Eebe Dilayaanna Nabiyada Xaq daro, waxaana ugu wacan Caasinimadooda iyo Xadgudubkooda.'),
(406, 3, 113, 'Isku mid ma aha, Ehelu Kitaabka waxaa ma mid ah Ummad u Taagan (Xaqa) oo Akhrida Aayaadka Eebe Saacadaha Habeenka iyagoo Sujuudsan, (waa Cadbullaahi Binu Salaamo iyo Saaziibadiis).'),
(407, 3, 114, 'Rumeynna Eebe iyo Maalinta Dambe farana Wanaagga kana reeba waxa la naco (Xun) una dag dagi Wanaagga, kuwaasi waxay ka mid yihiin kuwa suuban.'),
(408, 3, 115, 'Waxay Falaan oo Khayr ah laga ma qariyo (Dayaeo), Eebana waa ogyahay kuwa dhawrsada.'),
(409, 3, 116, 'Kuwa Gaaloobay kama kaafido Xoolahoodu iyo Caruurtoodu Eebe Xaggiisa waxba, kuwaasuna waa Ehelka Naarta wayna ku Dhex waari.'),
(410, 3, 117, 'Waxay Bixiyaan Noloshan Adduunna waxay la mid tahay Dabayl Qabow (daran) oo ku dhaeday Beer Qoom dulmiyey Naftooda ooy Halaagtay, ma dulmiyin Eebe iyagaase Naftooda Dulmiyey.'),
(411, 3, 118, 'Kuwa (Xaqa) rumeeyow ha ka yeelanina Saaxiib (Hoose) wax idinka soo hadhay, kuma gabood falaan Fasaadintiinna waxayna Jecelyihiin dhibkiinna, waxay ka muuqataa Cadhadu Afkooda, waxay qarsan Laabahoodu yaa ka weyn, waan idiin Caddaynay Aayaadka haddaad wax kasaysaan.'),
(412, 3, 119, 'Idinkani waad Jeceshihiin Iyaguna idinma jeela, waxaadna rumeysaan Kitaabka dhammaantiis, hadday idin la kulmaanna waxay dhahaan waannu rumeynay Hadday kaliyoobaanna waxay idiin Qaaniinaan Xubnaha (Faraha) Cadho darteed, waxaad dhahdaa ku dhinta Cadhadiinna, Eebana waa ogyahay waxa Laabta ku sugan.'),
(413, 3, 120, 'hadday idin taabato Wanaag way Xumeysaa hadday idinku dhaedo Xumaanna way ku Farxaan, haddaad Samirtaan ood Dhawrsataan idinkama dhibayso Dhagartoodu waxba, Echana waxay Camal fali wuu Koobay.'),
(414, 3, 121, '(Xusuuso) markaad ka Kallahday (tagtay) Ehelkaaga adoo u yeeli Mu\'miniinta Goob Dagaal Eebana waa Maqle og.'),
(415, 3, 122, 'Markay Hamiday «Doontay» Labo Kooxood oo idinka mid ah inay Fashilaan (Cabsadaan) Eebana Waligooda (Gargaarahooda) ahaa ee Eebe uun ha tala saarteen Mu\'miniintu.'),
(416, 3, 123, 'Dhab ahaan buu idiin ku Gargaaray Eebe (Maalintii) Badar idinkoo dulaysan «Tabar yar», ee ka Dhawrsada Eebe waxaadna mudantihiin inaad ku Mahdisaan.'),
(417, 3, 124, 'Markaad ku lahayd Mu\'miniinta Miyuusan idiinku Fillayn inuu idinku Xoojiyo Eebihiin Saddex Kun oo Malaa\'ig ah oo soo Degi.'),
(418, 3, 125, 'Waa na kaafiyaan ee haddaad Samirtaan (Adkeysataan) ood dhawrsataan (waydinku fillaan) waxayna idiinku imaan deg daggooda, kaasna waa Xoojin Eebihiin Shan Kun oo Malaa\'ing ah oo Calaamaysan.'),
(419, 3, 126, 'Uma yeelin Eebe (Arrintaas) Bishaaradiinna mooyee iyo inay ku Xasisho Quluubtiinnu isaga, Gargaar aan Eebe agtiisa ahayna ma jiro ee ah Adkaade Falsan.'),
(420, 3, 127, 'Si uu u Gooyo (Halaago) qayb ka mid ah kuwa Gaaloobay ama Walbahaariyo oy Noqdaan iyagoo Khasaaray.'),
(421, 3, 128, 'Waxba Amarka kuma lihid Nabiyow ama wuu ka Toobad aqbali Eebe ama wuu Cadaabi Gaalada, illeen waa Daalimiine.'),
(422, 3, 129, 'Eebaa aska leh waxa Samooyinka ku sugan iyo waxa Dhulka ku sugan, wuxuu u dhaafaa (Dambiga) Ciduu doono, wuxuuna Cadaabaa Ciduu doono, Eebana waa Dambi dhaafe Naxariista.'),
(423, 3, 130, 'Kuwa (Xaqa) rumeeyow ha u Cunina Ribada Laablaab, kana Dhawrsada Eebe waxaad Mudantihiin inaad Liibaantaane.'),
(424, 3, 131, 'Kana dhawrsada Naarta loo Darbay Gaalada.'),
(425, 3, 132, 'Adeecana Eebe iyo Rasuulka waxaad u dhawdihiin in laydiin Naxariistee.'),
(426, 3, 133, 'Una deg daga Dambi dhaafka Eebihiin iyo Janno Ballaadhkeedu yahay Samaawaadka iyo Dhulka oo loo darbay kuwa dhawrsada.'),
(427, 3, 134, 'Ee ah kuwa wax bixiya Waqtiga Ladnaanta iyo Dhibkaba, ee Damiya Cadhada ee iska Cafiya Dadka, Eebana waa Jeelyahay Samadalayaasha.'),
(428, 3, 135, 'Kuwa markay falaan Xumaan ama Dulmiyaan Naftooda Xuse Eebe, Warsadana Dambi dhaafid dambigooda, wax dambi dhaafana ma jiro Eebe mooyee, oon daa\'imin (Xumaantay) faleen iyagoona og.'),
(429, 3, 136, 'Kuwaas Abaalkoodu waa Dambi dhaafka Eebahood iyo Jannooyin ay Dureeri dhexdeeda Wabiyaal iyagoo ku waari dhexdeeda, waxaa Wanaagsan Abaalka kuwa Camal Falay.'),
(430, 3, 137, 'Waxaa dhab ahaan u tagay Hortood Sunno (Waddo) ee Socda Dhulka oo eega siday noqotay Cidhibtii Beeniyayaashii.'),
(431, 3, 138, 'Kaasi waa Caddayn Dadka waana Hanuun iyo Waanada kuwa dhawrsada.'),
(432, 3, 139, 'Ha tabar yaraanina hana Murugoonina idinkaa Sareyn haddaad Tihiin Mu\'miniine.'),
(433, 3, 140, 'Hadduu idin taabtay Dhib (Dhaawac) Dhabahaan buu u taabtay Woomkana Dhib la mid ah, waana Saas Adduunyadu waxaanuu ku kala wareejinaa Dadka Dhexdiisa iyo in Eebe Muujiyo kuwa rumeeyey kana yeesho Xaggiinna Marag, Eebana ma jeela Daalimiinta.'),
(434, 3, 141, 'Iyo inuu Imtixaamo kuwa rumeeyey Tirtirana Gaalada.'),
(435, 3, 142, 'Ma waxaad u Maleyseen inaad Galayssaan Jannada isagoon Muujinin Eebe kuwa Jahaaday oo idinka mid ah oosan Muujinin kuwa Samra.'),
(436, 3, 143, 'Dhab ahaan waxxad u ahaydeen kuwa jecel Geerida ka hor la kulankeeda, waadna aragteen Geeridii idinkoo Fiirin.'),
(437, 3, 144, '«Nabi» Muxamadna ma aha waxaan Rasuul ahayn, waxaa Tagey hortiis Rasuullo, cc hadduu Dhinto ama La dilo ma waxaad u gaddoomaysaan Cidhibtiinii (Dib), Ruuxii U gadooma Cidhibtiisii (Gaalooba) kama Dhibo Eebe waxba, wuuna Abaal marin kuwa ku Shukriya.'),
(438, 3, 145, 'Nafna ma aha tii Dhimata Idanka Eebe la\'aantiis waa Kitaab Muddeysan, Ruuxii Doona Abaalka Adduunyo waxbaan ka siineynaa, ruuxii doona Abaalka Aakhirana Waxbaan Ka Siinaa, Waxaana Abaal marin kuwa Shukriya.'),
(439, 3, 146, 'Imisa Nabi la Dilay ooy la Jireen culimo Dagaallami oo Badan, oyna Cabsanin wax ku Dhacay Jidka Eebe Dartiis, Tabarna yareynin, Dulloobinna, Eebana waa jecelyahay kuwa Samra.'),
(440, 3, 147, 'Mana aha Hadalkoodu waxaan ahayn Eebow nooDhhaf Dambigannaga iyo Xadgudubkanaga Amarkanaga, Sugna Gornadahanaga noogana Gargaar Qoomka Gaalada ah.'),
(441, 3, 148, 'Markaasuu Siiyey Eebe Abaalka Adduunyo iyo Wanaagga Abaalka Aakhiro, Eebana wuxuu Jecelyahay Samafalayaasha.'),
(442, 3, 149, 'Kuwa Xaqa Rumeeyow haddaad Addeeedaan (Maqashaan) Kuwa Gaaloobay waxay idiin Celin Cidhibtiina, markaasaad Noqonaysaan idinkoo khasaaray.'),
(443, 3, 150, 'Eebaase Gargaarihiinna ah, isagaana ugu Khayrroon Gargaare.'),
(444, 3, 151, 'Waxaan ku Tuuraynaa Quluubta kuwa Gaaloobay Argagax la Wadaajintooda Eebe waxaan loo soo Dejinin Xujo Darteed, Hoygooduna waa Naar, lyadaana U xun Xerada Daalimiinta.'),
(445, 3, 152, 'Dhab ahaan yuu idiinku Rumeeyey Eebe Yaboohiisii markaad ku Dhamayneyseen (Ku dilayseen) Idamkiisa, intaad ka Cabsataan ood ku Murantaan Amarka ood Caasidaan intuu idin Tusiyey waxaad Jeceshihiin ka Dib, waxaa idinka Mid ah Ruux Dooni Adduunyo, waxaana idinka mid ah Ruux dooni Aakhiro, markaas idinka Tilay Xagooda si uu idiin Imtixaano, dhab ahaanna wuu idiin Cafiyey, Eebena wuu ku Fadli leeyahay Mu\'miniinta.'),
(446, 3, 153, 'Markaad koreyseen aydaan Milicsanayn Cidna, Rasuulkuna idiinka Yeedhi Gadaashiina oo idinku dhacay Murug la jirto Murug (kale) si aydaan ugu Walbahaarin wixii idin ka tagay iyo wixii idinku Dhacay, Eebana waa ogyahay waxaad Falaysaan.'),
(447, 3, 154, 'Markaas uu ku soo Dejiyey Korkiinna Murug ka dib Aaminnimo Darteed Lulmo daboolaysa Koox idinka mid ah, Kooxna waxaa Walaacisay Naftooda waxayna u Malayn Eebe Male Jaahiliyeed waxayna Dhihi ma waxbaan ku leenahay Amarka, waxaad dhahdaa Amarka Dhamaantiis Eebaa iska lch, waxayna ku Qarin Naftooda waxayna kuu muujinayn iyagoo dhihi haddaan Amarka wax ku leenahay Nalaguma Dileen Halkan, waxaad Dhahdaa haddaad tihiin Guryihiinna way u soo bixilahaayeen kuwa loo Qoray Geerida halkay Seexan (Lagu Dili), (saasna wuxuu u Falay Eebe) inuu Imtixaano Eebe waxa Laabtiinna ku Sugan iyo inuu kala Shaandheeyo waxa Quluubtiinna ku Sugan, Eebana waa Ogyahay waxa Laabta Ku Sugan.'),
(448, 3, 155, 'Kuwii Jeedsaday oo idinka mid ah Maalintay Kulmeen Labadii Col waxaa uun Simbiriirixiyey Shaydaan waxay kasbadeen Sababtiisa Qaarkood wuuna iska Cafiyey Eebe Xagooda Eebena waa Dambi Dhaafe dulbadan.'),
(449, 3, 156, 'Kuwa (Xaqa) Rumeeyow ha Noqonina sida kuwa Gaaloobay oo ku yidhi Walaalahood markay ku Safraan Dhulka ama Duulaan hadday Agtanda Joogaan ma dhinteen Lamana Dileen si uu uga yeelo Arrintaas Qoomamo Quluubtooda, Eebana waa Nooleeye iyo Dile, Eebana waxaad Camal Falaysaan wuu arkaa.'),
(450, 3, 157, 'Haddii laydinku Dilo Jidka Eebe amaad Dhimataan Dambi Dhaaf Eebe iyo Naxariistiisa yaa idiinka Khayrbadan waxay Kulmin.'),
(451, 3, 158, 'Haddaad Dhimataan ama laydin Dilo Xagga Eebe umbaa laydiin Celin.'),
(452, 3, 159, 'Naxariista Eebe Darteed yaad ugu Jilicday haddaadse tahay Mid Ingagan oo Qalbi Adag way kaakala tagi lahaayeen Gaararkaada, ee iska Cafi una Dambi Dhaaf warso kalana Tasho Amarka, markaas haddaad wax goosatana talo saaro Eebe (K u Dhaqaaq), Eebe wuxuu Jecelyahay kuwa Tala Saartee.'),
(453, 3, 160, 'Hadduu idin Gargaaro Eebe wax idiinka Adkaan ma jiro Hadduu idin Hoojiyana waa Kuma kan idiin Gar- gaari Gadaashiis, Eebe uun ha Tala Saarteen Mu\'miniintu.'),
(454, 3, 161, 'Kama suurowdo Nabi inuu wax Khayaamo, Ruuxii wax Khayaama wuu la Imaan wuxuu Khayaamay Maalinta Qiyaame, markaas waxaa loo Oofin (Dhamayn) Nafkasta waxay Kashatay Lagamana dulmiyo.'),
(455, 3, 162, 'Ruux Raacay Raalli ahaanshaha Eebe ma la mid Baa Ruux la noqday Cadho Eebe Hoygiisuna yahay Jahannamo iyadaana U xun meel loo Noqdo.'),
(456, 3, 163, 'Iyagu waa Darajaad Eebe agtiisa, Eebana waa Arkaa waxay Fali.'),
(457, 3, 164, 'Dhab abaanbuu ugu Manaystay Eebe Mu\'miniinta markuu ka Bixiyey Dhexdooda Rasuul Naftooda ka mid ah kuna Akhriyi Korkooda Aayaadkiisa Daahirinna Barina Kitaabka (Quraanka) iyo Xigmada (Sunada Nabiga) waxayna ahaayeen Horay Baadi Cad.'),
(458, 3, 165, 'Hadday idin Adiibtay (idinku Dhaeday) Dhibaato waxaad Asiibteen Laba Jibaarkeed (Badar) waxaad dhahdeen xaggee Nooga yimid (Dhibku) waxaad dhahdaa Agta Naftiinna, Eebana wax kasta wuu karaa.'),
(459, 3, 166, 'Waxa idinku dhacay Maalintay kulmeen Labadii Kooxood waa idamka Eebe iyo inuu Muujiyo Mu\'miniinta.'),
(460, 3, 167, 'Iyo inuu Muujiyo kuwa Munaafaqoobay, oo loo yidhi Kaalaya ku Dirira Jidka Eebe ama Difaaca, waxayna dheheen Haddaan ognahay Dagaal waan idin raaci lahayn, iyagu Gaalnimaday Maalintaas uga Dhawyihiin Rumeyn, waxay ka odhan Afkooda waxaan ahayn Quluubtooda, Eebana waa ogyahay waxay Qarin.'),
(461, 3, 168, 'waa kuwa ku yidhi Walaalahood (Munaafiqiintu) iyagoo iska fadhiistay Hadday na Adeecaan lama Dilcen, waxaad dhahdaa ka Dhawra Naftiinna Geeri Haddaad Run sheegaysaan.'),
(462, 3, 169, 'Hana u Malaynina kuwa Lagu Dilay Jidka Eebe kuwa dhintay, Saas ma aha waxay ku Noolyihiin Eebahood Agtiisa Laguna Arzaaqi.'),
(463, 3, 170, 'Iyagoo ku Faraxsan wuxuu siiyey Eebe oo Fadligiisa ah, kuna Bishaaraysan kuwaan wali Haleelin ee Gadaashooda ah inuusan Korkoda Cabsi iyo Murugo midna «ahyn».'),
(464, 3, 171, 'Waxayna ku Bishaaraystaan Nicmo Eebe iyo Fadli iyo inaan Eebe Dayacayn ajriga Mu\'miniinta.'),
(465, 3, 172, 'Ee ah kuwa Ajiibay (Maqlay) Eebe iyo Rasuulka ka Dib intuu Asiibay Dhaawac kuwa Wanaag Falay oo ka mid ah oo dhawrsaday waxaa u sugnaaday Ajir wayn.'),
(466, 3, 173, 'Ee ah kuwuu Dadku (Gaaladu) Dadkii baa idiin kulmay ee ka Cabsada oo u Siyaadiyey Rumeyn, oy dhaheen Kaafiyahanagu waa Ilaahay Isagaana u Fiican Wakiil.'),
(467, 3, 174, 'Oo Noqday Nicmo Eebe iyo Fadli oyna Taaban Xumaan Raacayna Raalli ahaanshaha Eebe, Ilaahayna waa Fadli wayn lahaade.'),
(468, 3, 175, 'Kaasi waa Shaydaan wuxuuna idinku Cabsi Galin Awliyadiisa (Saaxiibadiis) ee ha Cabsanina, ee iga Cabsada Haddaad Mu\'miniin tihiin.'),
(469, 3, 176, 'Yeyna ku murug galin kuwa u Deg degi Gaalnimada iyagu kama Dhibaan Eebe waxba, wuxuu Dooni Eebe inuusan ugu yeelin Nasiib Aakhiree waxayna Mudan Cadaab Weyn.'),
(470, 3, 177, 'Kuwa ku Gatay Gaalnimo Iimaanka kama Dhibayaan Eebe waxba waxayna Mudan Cadaab Daran.'),
(471, 3, 178, 'Yeyna u Malaynin kuwa Gaaloobay inuu Sugidoodu Khayr u tahay, waxaannu u Sugi uun inay Siyaadsadaan Dambi, waxayna mudan Cadaab Dulleeya.'),
(472, 3, 179, 'Ma aha Eebe kii kaga Taga Mu\'miniinta Sidaad tihiin intuu ka kala Sooco ka Xun iyo kan Fiican, mana aha Eebe kii idin Daaliciya (Tusiya) waxa Maqan, hase yeeshee wuxuu ka dooran Rasuulkiisa Ciddduu Doono ee Rumeeya Eebe iyo Rasuulkiisa, Haddaad Rumeysaan Xaqa oo Dhawrsataan waxaad Mudanaysaan Ajir Weyn.'),
(473, 3, 180, 'Yeyna u malaynin kuwa ku Bakhayli wuxuu Siiyey Eebe oo Fadli ah inay Khayr u Tahay, Sharbayse u Tahay waxaana Luqunta loo Galin waxay ku Bakhaylayeen Maalinta Qiyaame, Eebaana iska leh Dhaxalka Samaawaadka iyo Dhulka, Eebana waxaad Camal Falaysaan waa Ogyahay.'),
(474, 3, 181, 'Dhab ahaan yuu u Maqlay Eebe Hadalka Kuwii Yidhi Eebe waa Faqiir Anaguna waxaan nahay Hodan (waa Yuhuuh ha halaagmeene) waan Qoraynaa waxay dhihi iyo Diliddooda Nabiyada ee Xaq daro waxaan u dhahaynaa dhadhamiya Cadaabka Gubashada,'),
(475, 3, 182, 'Taasina waa waxay hormarsatay Gacmahoodu Eebana ma aha Dulmiyaha Addoomada.'),
(476, 3, 183, 'Waa kuwa Yidhi Eebe wuxuu nagula Ballantamay Inaanaan Rumeyn Rasuul intuu Nooga keeno Qurbaan (Sadaqo) oy Cunto Naar, waxaad dhahdaa waxay idiinla yimaadeen Rasuullo hortay Xujooyin iyo waxaad sheegteen ee Maxaad u Disheen haddaad run sheegaysaaan.'),
(477, 3, 184, 'Hadday ku Beeniyaan waxaa dhab ahaan loo Beeniyey Rasuullo kaa Horreeyey oo la yimid Xujooyin iyo Kutub iyo Kitaab Cad.'),
(478, 3, 185, 'Nafkasta waxay Dhadhamin Geeri, waxaa uun laydiin Oofin Ajrigiinna Maalinta Qiyaame, Ruusiise laga iilo Narta oo la galiyo Janada wuu Liibaanay, Nolosha Adduuna ma aha waxaan Raaxo kado ahayn.'),
(479, 3, 186, 'Waan idinku Imtixaamaynaa Xoolihiinna iyo Naftiinna waxaadna ka Maqlaysaan kuwa la siiyey Kitaabka oo idinka horeeyey iyo Mushrikiinta (Gaaladii Carbeed) dhib Badan haddaadse Samirtaan ood dhawrsataan Arrintaasu waa Umuuraha loo Qasdo.'),
(480, 3, 187, '(Xusuuso) markuu Qaaday Eebe Ballanka kuwii la siiyey Kitaabka inaad u Cadaysaan (Xaqa) Dadka Oydaan Qarinin waxayse ka Tuureen Dhabarkooda Gadaashiis waxayna ku gateen Lacag Yar waxaa Xun waxay Gadan.'),
(481, 3, 188, 'Haw Maleyn kuwa ku Farxa waxay la yimaadaan oo Jecel in lagu Mahdiyo waxayna Falin Haw malayn inay ka Nabadgali Cadaabka waxaana u Sugnaaday Cadaab Daran.'),
(482, 3, 189, 'Eebaa iska leh Xukunka Samaawaadka iyo Dhulka, Eebaana wax kasta kara.'),
(483, 3, 190, 'Abuuridda Samaawaadka iyo Dhulka iyo is Khilaafidda Habeenka iyo Maalinta Calaamooyin yaa ugu Sugan kuwa Cawliga leh.'),
(484, 3, 191, 'Ee ah kuwa ku Xusa Eebe Taagni iyo Fadhi iyo Jiifba, oo ku fikira Abuuridda Samaawaadka iyo Dhulka (iya goo dhihi) Eebow Umaadan Abuurin kan (Khalqiga) Baadil (Ciyaar) waad Nasahantahay Ee naga kori Cadaabka Naarta.'),
(485, 3, 192, 'Eebow adigu ruuxaad Galiso Naarta Dhab ahaan baad u Dulaysay Daalimiintana uma Sugnaanin Gargaarayaal.'),
(486, 3, 193, 'Eebow Anagu waxaan Maqallay Yeedhe u yeedhi limaanka oo dhihi Rumeeya Eebihiin waana Rumeynay, Eebahanow noo dhaaf Dambiyadanada Asturna Xumaantanada naguna Dil la jirka kuwa Baarriga ah (fiican).'),
(487, 3, 194, 'Eebahanow na sii waxaad noogu Yaboohday Rasuuladaada hana na Dul leyn Maalinta Qiyaame, Adigu ma Baajisid Ballane.'),
(488, 3, 195, 'Wuxuu ku Ajiibay Eebahood Anagu Ma dayacno Camal Mid Camal Falay oo Idinka mid ah Lab iyo Dhadig, Qaarkiin Qaarbuu ka ahaaday kuwa Hijrooday ee laga Bixiyey Guryahooda oo lagu Dhibay Jidkayga oo Dagaalamay oo la Dilay waannu Asturi Xumaanahooda waxaana Galin Janooyin ay Dureeri Dhexdeeda Wabiyaal waana Abaal marin Eebe Xagiisa ah, Eebe agtiisaana Abaalmarin Wanaagsan tahay.'),
(489, 3, 196, 'Yeyna ku Dhagrin ka Gadoonka kuwa Gaaloobay ee Dhulka (Adduunka).'),
(490, 3, 197, 'Waa Raaxo Yar kadib Hoygoodu waa Jahannamo, iyadaana u xun Gogol.'),
(491, 3, 198, 'Hase Yeeshee kuwa ka Dhawrsada Eebahood waxay mudan Jannooyin ay dureeri Dhexdeeda Wabiyaal iyagoo ku warai Dhexdeeda, waa marti Qaad Xagga Eebe ka ahaaday, waxa Eebe agtiisa ah yaana u Khary roon kuwa Baarriga ah (Wanaagsan).'),
(492, 3, 199, 'Ehelu Kitaabka waxaa ka mid ah mid Rumeyn Eebe iyo waxa laydiin soo dejiyey iyo wixii loo soo Dejiyey iyagoo u Khushuuei Eebe kumana Gataan aayaadka Eebe Qiimo Yar, kuwaas waxaa u Sugnaaday Ajirgooda Eebahood Agtiis, Eebana waa Deg degtaa Xisaabtiisu.'),
(493, 3, 200, 'Kuwa (Xaqa) rumeeyow Samra oo Samirtarna kuna Nagaada Dhawridda (Xuduudda Islaaxa) kana Dhawrsada Eebe waxaad Mudantihiin inaad Liibaantaane.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'Dadow ka Dhawrsada Eebihiina Idinka Abuuray Nafkaliya (Aadam) kana Abuuray xaggeeda Haweenaydeeda (Xaawo) kana Abuuraay Xagooda Rag Badan iyo Haweenba, ka Dhawrsada Eebihiina aad wax isku Warsataan iyo Qaraabada, Eebana wuxuu u Yahay Korkiina Ilaaliye.'),
(495, 4, 2, 'Siiya Agoonta xoolahooda hana ku Badalina Xumaan Wanaag, hana Cunina xoolahooda La jirka Xoolihiinna Taasina waa Dambi weyn.'),
(496, 4, 3, 'Haddaad ka Cabsataan inaydaan ku Cadaalad Falin Agoonta Guursada wixii idiin Wanaagsan oo Haween ah, Labo, Saddex iyo Afar, Haddaad ka Cabsataan inaydaan Caddaalad Falin Mid kaliya (Guursada) ama waxay Hanatay Gacmihiinnu Saasaa u Dhaw inaydaan Jawrfalinc.'),
(497, 4, 4, 'Siiya Haweenka Meherkooda isagoo Waajib ah hadday idiinka Raali noqoto wax ka mid ah Naftoodu Cuna idinkoo Shifaysan oo Kadban.'),
(498, 4, 5, 'Ha Siinina Sufahada (Maamuli karin) Xoolahooda ah kuwo idiinka yeelay Eebe Ilaaliye ka Quudiya kana Arradbixiya una Dhaha Hadal Fiican.'),
(499, 4, 6, 'Imtixaamana Agoonta markay Gaadhaan Guur Haddaad ku Aragtaan (Ogaataan) Rashiidnimo (Wanaag) siiya iyaga Xoolahooda hana u Cunina Xad gudub iyo ka degdagis intayna Weynaana, Ruuxii Hodan ah ha dhawrsado Ruuxii Faqiir ahna ha ka Cuno si Wanaagsan, markaad u Dhiibtaan Xoolahooda Marag u yeela Koriisa, waana ku Filanyahay Eebe Xisaabiye.'),
(500, 4, 7, 'Ragga waxaa u Sugnaaday Qayb ka mid ah waxay ka tageen Laba Waalid iyo Qaraabadu Haweenkana waxaa u Sugnaaday Qayb ka mid ah waxay ka Tageen Laba Waalid iyo Qaraabadu'),
(501, 4, 8, 'Hadday Joogaan Qaybta Qaraabo, Agoon iyo Masaakiin (aan dhaxlayn) wax ka Siiya una dhaha Hadal Wanaagsan.'),
(502, 4, 9, 'Hana ka Cabsadeen kuwa Hadday kaga Tagaan Gadaashooda Caruur Tabar Yar una Cabsan lahaa hana ka Dhawrsadeen Ecbe hana Dheheen Hadal Toosan.'),
(503, 4, 10, 'Kuwa u Cunaya Xoolaha Agoonla Dulmi (Xaq darro) waxay uun ku Cunayaan Calooshooda Naar waxayna Gali Saciiro.'),
(504, 4, 11, 'Wuxuu idiin Dardaarmi Eebe Caruurtiinna kan Lab la mid Qaybta Labo Dhadig, Hadday Yihiin Hawween ka Sarreeya Labo waxay Mudun Labo Dalool wiixuu ka Tagay, hadday tahay mid waxay Mudan Nus, Labada Waalidna mid kastoo ka mid ah wuxuu Mudan Sudus (I.ix meelood meel) Hadduu ka Dhintay (Ilmahoodu) Caruur, hadduu Caruur lahayn oy Dhaxlayaan Labada Waalid Hooyadu waxay Mudan Saddex Meelood Mcel, Hadduu Walaalo leeyahay Hooyadu waxay Mudan Sudus Dardaaranka iyo Daynta ka Dib, Aabayaalkiina iyo Wiilashana ma Ogidin Kooda idinku Dhaw Nacfi, waa Faralyeel «Qaddaraad» Eebe, Ilahayna waa Oge Falsan.'),
(505, 4, 12, 'Waxaa idiin Sugnaaday Nus waxay ka Tagaan Haweenkiinu hadayna Ilmo lahayn, haddayse lahaayeen Ilmo waxaad leedihiin Rubuc (Afar meelood meel) waxay ka tagaan Dardaaran ay dardaarmeen iyo Dayn ka Dib, Haweenkuna waxay Mudan Rubuc waxaad ka Tagteen Haddaydaan Ilmo lahayn, haddaad Ilmo leedihiin waxay mudan thumun (Sideed Meelood meel) waxaad ka tagtaan, dardaaran aad dardaarmaysaan iyo Dayn ka Dib, hadduu yahay Ninku mid la Dhaxli Waalid iyo Ilmo la\'aan ama Haweenaydu isagoo leh Walaal ama Walaashiis mid kasta wuxuu Mudan Sudus (Hadday la hooya yihiin) hadday yihiin wax ka badan intaas waxay wadaagi thuluth (Saddexmeelood meel) Dardaaran la dardaarmo iyo Dayn ka Dib, isagoon Dardaaranka Cidna lagu Dhibayn, waana Dardaaran Eebe, Ilaahayna waa Oge Dulsan.'),
(506, 4, 13, 'Taasi wa Xuduud Eebe, Ruuxii Adeeca Eebe iyo Rasuulkiisa wuxuu Galin Jannooyin ay Dareeri Dhexdeeda Wabiyaal iyagoo ku Waari Dhexdeeda, Taasina waa Liibaan weyn.'),
(507, 4, 14, 'Ruuxiise Caasiya Eebe iyo Rasuulkiisa oo Tallaaba Xuduudiisa wuxuu Galin Naar isagoo ku Waari Dhexdeeda, wuxuuna Mudan Cadaab wax Dulleeya.'),
(508, 4, 15, 'Kuwa la imaan Xumaan (Zino) oo Haweenkiina ka mid ah, Marag uga Dhiga Afar idinka mid ah, hadday ku marag Kacaan ku Haya Guryaha intay ka Disho Geeridu ama u yeelo Eebe Waddo.'),
(509, 4, 16, 'Labadii la Timaadda Xumaanta (Zinada) oo idinka mid ah Dhiba Hadayse Toobadkeenaan ooy Wanaagsanaadaan ka Jeedsada Xagooda, Eebana waa Toobad aqbale Naxariista.'),
(510, 4, 17, 'Toobadda (Aqbalkeeda) waxay Eebe uga ahaatay kuwa Fala Xumaha iyagoo Jaahil ah markaas Toobad keena Waqti dhaw Kuwaas wuu ka Toobad aqbala Eebe korkooda, Eebana waa Oge Falsan.'),
(511, 4, 18, 'Uma aha Toobaddu ku Fala Xumaha intuu uga Yimaaddo Midkood Geeridu markaas dhaha Anigu waxaan Toobad keeni hadda, iyo Kuwa Dhiman Iyagoo Gaala ah, Kuwaas waxaan u Darabnay Cadaab Daran.'),
(512, 4, 19, 'Kuwa (Xaqa) Rumeeyow idiinma Banaana inaad u Dhaxashaan Haweenka Xoog iyo inaad dhibtaan si aad ula tagtaan (Qaadataan) Qaar waxaad Siiseen inay la Yimaadaan Xumaan Cad Mooyee, kulana Noolaada si Fiican, haddaad Kahataan waxaa Suurowda inaad Kahataan wax uu Yeelo Eebe dhexdiisa Khayr Badan.'),
(513, 4, 20, 'Haddaad Doontaan inaad ku Baddashaan Haweenay mid kale idinkoo Siiyey Meher (Badan) ha ka Qaadanina Xaggiisa waxba, Ma waxaad Qaadanaysaan Been iyo Dambi Cad.'),
(514, 4, 21, 'Seed uga Qaadanaysaan idinkoo Qaarkiin Qaar Arkay, ood kana Qaaddeen Ballan Adag.'),
(515, 4, 22, 'Ha Guursanina waxay Guursadeen Aabayaashiin wixii hor maray mooyee, waa Xumaan iyo wax loo cadhoodo Waddana Iyadaa U xun.'),
(516, 4, 23, 'Waxaa laydinka Reebay Hooyooyinkiin (inaad Guursataan) iyo Gabdhihiinna, Walaalihiin, Eedooyinkiin, Habaryarihiin, Gabdhaha Walaalkaa, Gahdhaha walaashaa, Hooyooyinka idin Nuujiyey Walaalaha Nuugmada Hooyooyinka Haweenkiinna, Gabdhaad Dhaqaalaysaan., ee Haweenkiinna aad is aragteen Dhaleen, Haddaydaan is arkin wax dhib ah (Danbi) ma saara korkiinna, Haweenka Wiilashiinna Dhabta ah, iyo inaad kulmisaan Labo Walaalo ah wixii horay maray mooyee, Eebana waa u Dhaafe Naxariista.'),
(517, 4, 24, '(Waxaa Kaloo idinka reeban) Haweenka la Qabo, waxay Hantaan Gacmihiinnu mooyee Kitaabka Eebaa korkiinna ah, waxaa laydiin banneeyey waxa ka Soohadhay kuwaas inaad ku dalablaan Xoolahiinna idinkoo dhawrsoon oon Xumaan (Zino) Falin, waxaad ku intifaacdaan Xaggooda Siiya Meherkooda waa Qadaraad Eebe, Dhib (Dambi) korkiinna ma Saarra waxaad isaga raalli Noqoteen Qadarrida ka Dib, Eebana waa Oge Falsan.'),
(518, 4, 25, 'Ruuxaan Karin oo idinka mid ah Awood uu ku Guursado Haweenka Dhawrsoon ee rumeysan (Xaqa) ha Guursado waxay hanatay Gacmihiin nu oo ah Gabdhaha Rumeysan (Xaqa) Eebana waa ogyahay limaanskiina, Qaarkiin Qaarbuu ka ahaaday, kuna guursada idamka Ehelkooda u Siiyana Ujuurada (Meherkooda) si Fiican, iyagoo Dhawrsoon oo Xumaan (Zino) ka Dheer, Saxiibana yeelanayn, Haddii lagu dhawro (Guur) ooy Xumaan la Yimaadaan korkooda waxaa ah Nus waxa ah kuwa Xorta ah oo Ciqaab ah, Arrintaas waa Ruuxii ka Cabsada Zino, oo idinka mid ah, Haddaadse Samirtaan ya idiin khayr roon, Eebana waa Dambi Dhaafe Naxariista.'),
(519, 4, 26, 'Wuxuu Dooni Eebe inuu idiin Caddeeyo Idinkuna Hanuuniyo Wadadii kuwii idinka Horreeyey oo idinka Toobad aqbalo Eebana waa Oge Falsan.'),
(520, 4, 27, 'Eebe wuxuu dooni inuu idinka Toobad aqbalo waxayse dooni kuwii Raacay waxa Naftoodu Jeceshahay inaad iilataan iilasho wayn.'),
(521, 4, 28, 'Wuxuu dooni Eebe inuu idinka Fudeyiyo, waxaase La Abuuray Dadka isagoo tabar Yar.'),
(522, 4, 29, 'Kuwa (Xaqa) Rumeeyow ha Cunina Xoolihiina dhexdiinna (Si Baadil ah) (Xaaraam ah) inay Ganacsi Raalli aad tihiin tahay mooyee, hana Dilina Naftinna, Eebe wuxuu idiinyahay Naxariistee.'),
(523, 4, 30, 'Cidii Fasha Arrintaas Colnimo iyo Dulmi Waxaannu galin Doonnaa Naar, Arrintaasna Eebe waa u Fududahay.'),
(524, 4, 31, 'Haddaad ka Dheeraataan Dambiyada waa weyn waxaan idinka Reebeynno waxaannu ka asturi Xaggiinna Xumaantiinna waxaana idin Galin Meel Fiican (Sharaf leh).'),
(525, 4, 32, 'Ha tamaniyina wuxuu kaga Fadilay Eebe Qaarkiin Qaar, Ragga waxaa u Sugnaaday Qayb waxay kasbadeen. Haweenkana waxaa u Sugnaaday qayb waxay kasbadeen Waydiista Eebe Fadligiisa, Eebana waxkaasta waa Ogyahay.'),
(526, 4, 33, 'Cid kasta waxaan u Yeellay Kuwo dhaxla waxay ka tageen Labada waalid iyo Qaraabadu iyo kuwaad Guntateen Dhaar (Isdhaarsateen) siiya Qaybtooda, (oo Gargaar ah), Eebana wax kasta waa u Marag.'),
(527, 4, 34, 'Raggaa u taagan (Maamulka) Haweenka, ka Fadilida Eebe Qaarkood Qaar iyo ku nafaqaynta Xoolahooda darteed, (Haweenka) Wanaagsan way Adeecaan Haaliyaanna Maqnaas- ho ilaalinta Eebe Darteed, kuwaadse ka Cabsataan Caasinimadooda waaniya, kagana Fogaada Jiifka Edbiyana, hadday idin Adeecaanna ha u dalbina Korkooda jid (inaad dhibtaan), Eebana waa Sare Wayn.'),
(528, 4, 35, 'Haddaad ka Cabsataan Iskhilaafka Dhexdooda u bixiya Xaq soore Ehelkiisa ah iyo Xaq soore Ehelkeeda ah, hadday doonaan Wanaajinna Eebe wuu iswaafajin Dhexdooda, Eebana waa Oge Xeeldheer.'),
(529, 4, 36, 'Caabuda Eebe hana la Wadaajinina waxba, Labada Waalidna u Wanaag fala, iyo Qaraabada, Agoonta, Masaakiinta, Dariska dhaw, Dariska Durugsan, Saaxiibka Dhinaca, Socotada iyo waxay hanatay Gacmihiinnu, Eebana ma Jecla Cidda Kibirka badan ee Faanka badan.'),
(530, 4, 37, 'Oo ah kuwa Bakhayla Farana Dadka Bakhaylnimo qarinna wuxuu Siiyey Eebe oo Fadli ah, waxaana u darabnay Gaalada cadaab wax dulleeya.'),
(531, 4, 38, 'Iyo kuwa u bixiya Xoolahooda istuska Dadka oon rumeyn Eebe iyo Maalinta Dambe, ruuxii shaydaan u noqdo Saaxiib wuu Xumaaday Saaxiibkiis.'),
(532, 4, 39, 'Maxaa korkooda ah hadday Rumeeyaan Eebe iyo Maalinta Dambe oy Bixiyaan Qayb wuxuu ku Arzuqay Eebe, Ilaahayna waa Ogyahay iyaga.'),
(533, 4, 40, 'Eebe ma Dulmiyo wax la eg Darro (waxyar) hadday Wanaagtahayna wuu faab laabaa wuuna ka siin Agtiisa Ajir Weyn.'),
(534, 4, 41, 'ee Say ahaan Markaan ka keenno Umad kasta marag oon kuu keenno adigana (Nabiga) kuwaas korkooda Marag.'),
(535, 4, 42, 'Maalintaas waxay Jeclaan kuwii Gaaloobay oo Caasiyey Rasuulka in lala Simo (Liqo) Dhulka kamana Qariyaan Eebe Hadal.'),
(536, 4, 43, 'kuwa (Xaga) Rumeeyow haw dhawaanina Salaadda idinkoo sakhraansan intaad ka garataan waxaad dhahaysaan iyo idinkoo Junub ah inaad Jid gudbaysaan mooyee intaad ka maydhataan, haddaad tihiin kuwa Buka ama Safar ah ama ka yimid Ruux idin ka mid ah kortag, ama Taabataan haweeneey aadanna helayn Biyo ku Gagabaysta Carro Wanaagsan, kuna masaxa (Mariya) Wajiyadiinna iyo Gac- mihiinna, Eebana waa Cafiye Dambi dhaafa.'),
(537, 4, 44, 'Ka warran kuwa la siiyey Qayb Kitaabka ah (Tawreed) oo Gadan Baadi doonina inaad ka Dhuntaan Jidka.'),
(538, 4, 45, 'Eebaa Og Colkiinna, waxaana ku filan Eebe, wuxuuna ugu filanyahay Eebe Gargaare.'),
(539, 4, 46, 'Kuwii Yuhuudoobay waxay ka Leexin Kalimooyinka Meelahooda waxayna dhihi waan Maqalay oon Caasinay, Maqla ha maqlinee, waxayna (dhihi) na ilaali iilid Carrabkooda iyo Durid Diinta (Islaamka) darteed, hadday dhahaan waan maqalay waana Adeeenay ee maqal na dayna saasaa u khayroonaan lahayd una toosnaanlahayd, laakiin waxaa lacnaday Eebe Gaalnimadooda Darteed mana Rumeeyaan wax yar mooyee.'),
(540, 4, 47, 'Kuwa la siiyey Kitaabkow Rumeeya waxaan soo dajinay isagoo rumeyn waxaad haysataan ka hor tirid wajiyaal oon u Celino gadaasheeda ama Nacladno sidaan u Nacladnay Asaxaabtii (Kuwii) Sabtida (Yuhuudda) amarka Eebana waa kan lafalo.'),
(541, 4, 48, 'Eeba ma dhaafo in lala wadaajiyo wuxuuse ka dhaafaa waxaa wixii ka soo hadhay Cidduu doono, Ruuxii la wadaajiyo Eebe wuxuu beenabuurtay Dambi Weyn.'),
(542, 4, 49, 'Ka warrama kuwa daahirin (amaani) Naftooda, Eebaa Daahiriya Cidduu doono lagamana Dulmiyo waxa ku dhaggan Jeex Laf Timireed (Wax yar).'),
(543, 4, 50, 'Day siday ugu Been Abuuran Eebe isagaa ugu filan Dambi Cad.'),
(544, 4, 51, 'Ka warran kuwa la siiyey qayb Kitaabka ah oo rumeyn Sixir iyo Shaydaan, una dhihi kuwa Gaaloobay kuwaasaa ka hanuunsan (Toosan) Kuwa Rumeeyey Jidkooda.'),
(545, 4, 52, 'Kuwaas waa kuwuu Nacladay Eebe, Cid Eebe Nacladayna uma heshid Gargaare.'),
(546, 4, 53, 'Mise waxay leeyihiin Qayb Xukunka oo markaas ayan siinayn Dadka Laf Timir Duleelkeed (wax buuxiya).'),
(547, 4, 54, 'Mise waxay ku Xasdi Dadka waxa Eebe Siiyey oo Fadli ah, waxaan Siinay Ehelkii Ibraahiim Kitaabka iyo Xigmad, waxaana siinay Xukun Wayn.'),
(548, 4, 55, 'Waxaase ka mid noqday mid Rumeeya (Xaqa) waxaana ka mid ah mid ka Jeediya Xaqa, waxaa ugu Filan Jahannamo Gubid.'),
(549, 4, 56, 'Kuwii ka Gaaloobay Aayaadkanaga waxaanu Galin Naarta, mar kastoos gubto Haragoodu waxaan ku badalnaa harko kale si ay u Dhadhamiyaan Cadaabka, Eebana waa Adkaade Falsan.'),
(550, 4, 57, 'Kuwa Rumeeyey (Xaqa) oo falay Camal Fiican waxaan galinaynaa Jannooyin ay Dareeri Dhexdeeda Wabiyaal, oyna ku waari Dhexdeeda Waligood, waxayna ku mudan haween la Daahiriyey waxaana Galinaynaa Hoos Fiican.'),
(551, 4, 58, 'Eebe wuxuu idin Fari inaad u Gudaan (siisaan) Amaanada Ehelkeeda, (Ciddeeda) haddaad kala Xukumaysaan Dadkana inaad ku kala Xukuntaan Caddaalad, Eebe waxaa Wanaagsan wuu idinku waanin Eebana waa Maqle Arka.'),
(552, 4, 59, 'Kuwa (Xaqa) Rumeeyow Adeeca Eebe oo Adeeca Rasuulka iyo kuwa Amarka leh, oo idinka mid ah, haddaad ku Dooddaan Arrin u Celiya Eebe iyo Rasuulka haddaad tihiin kuwo Rumeeyey Eebe iyo Maalinta Dambe (Qiyaamada) saasaana khayr roon oo Wanaag badan Xagga Abaalmarinta.'),
(553, 4, 60, 'ka warran kuwa Sheegan inay rumeeyeen waxa lagugu soo dejiyey iyo waxa lagu Dejiyey kuwii kaa horreeyay oo ku Xukuntami Shaydaan, iyagoo la faray inay diidaan, wuxuuna dooni Shaydaanku inuu Baadiyeeyo Baadi Fog.'),
(554, 4, 61, 'Marka lagu dhaho u kaalay wuxuu Soo Dejiyey Eebe iyo xagga Rasuulka waxaad Arkaysaa Munaafiqiinta oo kaa Jeedsan jeedasho.'),
(555, 4, 62, 'Say noqon markay ku Dhaedo Musiibo (dhibaato) waxay hor marsadeen Gaemahoodu Darteed, markaas ay kuu yimaadaan iyagoo ku dhaaran Eebe inaanaan doonin waxaan Wanaag iyo Tawfiiq ahayn.'),
(556, 4, 63, 'Kuwaasu waa kuwa Ogyahay Eebe waxa Quluubtooda ku Sugan ee ka Jeedso Xaggooda waanina waxaadna u dhahdaa naftooda Hadal xeel dheer.'),
(557, 4, 64, 'Umaanaan dirin Rasuul in la Adeeco mooyee Idanka Eebe, hadday markay Dulmiyaan Naftooda ay kuu yimaadaan oy Dambi dhaaf waydiistaan Eebe una Dambi dhaaf waydiiyo Rasuulku waxay Eebe (ogaan) dambi dhaafe Naxariista.'),
(558, 4, 65, 'Eebahaa baan ku dhaartaye ma rumeeyaan intay kugu Xukuntamaan waxa dhex mara oyna ka helin Naftooda dhib, waxaad Xukuntay ooy hogaansamaan hogaansi.'),
(559, 4, 66, 'Haddaan ku Faral yeello korkooda Dila Naftiinna ama ka baxa Guryihiinna ma Faleen wax yar oo ka mid ah mooyee, hadday Falaan waxa lagu waanin yaa u. Khayr roon kuna daran (adag) sugnaan (Rumeyn).'),
(560, 4, 67, 'Markaasaan ka siin lahayn Agtanada Ajir wayn.'),
(561, 4, 68, 'waxaana ku hanuunin lahayn Jid Toosan.'),
(562, 4, 69, 'Ciddii Adeeca Eebe iyo Rasuulka kuwaasu waxay ahaan lajirka kuwuu u niemeeyey Eebe, oo nabiyada, Rumeeyayaasha, Shuhadada iyo kuwa Suubban waxaana u fiican rafiiq kuwaas.'),
(563, 4, 70, 'Arrintaasuna waa Fadliga Eebe waxaana ku Filan Eebe Ogaansho.'),
(564, 4, 71, 'Kuwa (Xaqa) Rumeeyow Qaata Digtoonidiinna Duulana idinkoo Koox Koox ah, ama Duula Dhamaan.'),
(565, 4, 72, 'Waxaa idinka mid ah (Munaafiqiin) Cabsin (Dib marin) hadday idinku dhaedo Musiibp (dhibaato) wuxuu dhahaa waxaa ii Niemeeyey Eebe korkayga haddaanan la joogin.'),
(566, 4, 73, 'Hadduu idin soo gaadho (Heshaan) Fadli Eebe (Khayr) wuxuu dhihi sidii isagoo ayna ahayn dhexdiinna Jeceyl Shallaaytee maxaan ula Joogi waayay, oon Liibaano Liibaan wayne.'),
(567, 4, 74, 'Ha kula Dagaallameen Jidka Eebe kuwa ku Gatay Nolasha Dhaw Aakhiro (doorta Adduun) Ruuxii ku dagaalama Jidka Eebe oo la Dilo ama Adkaada waxaan Siinaynaa Ajir wayn.'),
(568, 4, 75, 'Maxaad leedihiin ood ugu Dagaalami waydeen Jidka Eebe, iyagoo kuwa la Daeiifsan (Tabarta yaraa) oo rag iyo Haween iyo Caruurba ay leeyihiin Eebow naga Bixi Magaaladan Daalimiinta ah Ehelkeedu noogana yeel agtaada Wali iyo gargaare.'),
(569, 4, 76, 'kuwa (Xaqa) rumeeyey waxay ku Dagaallami Jidka Eebe, kuwa Gaaloobayna waxay ku Dagaallami Jidka Shaydaanka, ee la dagaallama Sokeeyaha Shaydaanku dhagarta Shaydaanku waa tabar yaryahaye.'),
(570, 4, 77, 'Ka warran kuwa loo yidhi Reeba Gaemihiinna (Billoowgii Islaamka) Oogana Salaadda, Bixiyana Zakada markii lagu Faral yeelay Dagaalkana waxaa soo baxay Koox ka mid ah oo uga Cabsan Dadka Cabsida Eebe oo kale ama ka daran Cabsi, waxayna dhaheen Eebow maxaad noogu Faral yeeshay Dagaalka, maxaad Dib Noogu dhigi wayday Tan iyo Muddo dhaw, waxaad dhahdaa Raaxada Adduunyo waa yartahay, Aakhiraana u khayroon Ciddii Dhawrsan Laydinkama dulmiyo Fatiil oo kale (wax yar).'),
(571, 4, 78, 'Meejaad ahaataanba wuu idin haleeli Mawdku (Geeridu) aad ahaateenba Daaro Dheerdheer oo la adkeeyey, hadday taabato wanaag waxay dhahaan tani waxay ka timid Xaga Eebe, hadday ku dhaedo Xumaanna waxay dhahaan tani waxay ka timid Agtaada (Nabiga) waxaad dhahdaa Dhammaan waxay ka ahaadeen Eebe Agtiisa, maxaa u sugnaaday Qoomkaas uma dhawa inay Fahmaan Hadale.'),
(572, 4, 79, 'Wixii ku soo gaadha oo Wanaaga waxay ka ahaadeen ebe, wixii kugu dhaea oo Xumaan ahna waxay ka ahaatay Naftaada, waana kuu Diray Dadka adoo Rasuul ah, wuuna ku Filanyahay Eebe Marag.'),
(573, 4, 80, 'Ruuxii adeeca Rasuulka wuxuu Dhab u Adeecay Eebe, Ruuxii Jeedsadana kuugu maanaan dirin Haaliye.'),
(574, 4, 81, 'Waxay dhihi (Munaafiqiintu) Daaead (baan nahay) markay ka tagaan Agtaadana waxay maamulaan (Habeenkii) Koox ka mid ah waxay leedahay waxaan ahayn, Eebana wuu Qori waxay Maamuli (miri) ee Ka jeed so Xaggooda talana saaro Eebe, waxaana ku filan Wakiil Eebe.'),
(575, 4, 82, 'Miyeyna Fiirfiirinayn Quraanka, hadduu ka yimid Eebe Agtiisa Meelaan ahayn waxay ka heli lahaayeen dhexdiisa is khilaaf badan.'),
(576, 4, 83, 'markuu u yimaaddo amar Nabadgalta ah ama Cabdi way Faafiyaan hadday u Celiyaan Xagga Rasuulka ama kuwa cilmiga Leh oo ka mid ah waxaa ogaan lahaa kuwa la soo baxa (Dhabta) oo ka mid ah, hadduusan jirin Fadliga Eebee korkiinna iyo Naxariistiisa waad raaci lahaydeen Shaydaan wax yart mooyee.'),
(577, 4, 84, 'Ee ku Dagaallan Jidka Eebe lama mashaqayn Naftaada mooyee, kuna boorri Mu\'miniinta (Dagaalka) wuxuu u dhawyahay Eebe inuu idinka reebo dhibka kuwa Gaaloobay, Eebaana daran dhib daranna Xanuujin.'),
(578, 4, 85, 'Ruuxii Shafeeca shafeeco Wanaagsan waxaa u ahaada Qayb ka mid ah, Ruuxiise Shaafeeca Shafeeco Xun waxaa u ahaan Dambi ka mid ah, Eebaana wax kasta Maaliya.'),
(579, 4, 86, 'Haddii laydin Salaamo Salaan ku Salaama mid ka Fiican ama ku celiya (mid la mid ah) Eebana waa wax kasta Xisaabiye (Kaafiye).'),
(580, 4, 87, 'Eebe ILaah (kale) ma jiro isaga mooyee wuuna idin soo kulmin Maalinta Qiyaame. shaki ma leh, yaa ka run badan Eebe Hadal (ma jiro).'),
(581, 4, 88, 'Maxaad leedihiin (Mu\'miniinta) ood Munaafiqiinta laba Kooxood ugu Noqoteen (in la laayo iyo in kale) Eebe wuu Halaagay (oo celiyey) waxay kasbadeen dartiis, ma waxaad doonaysaan inaad Hanuunisaan Cidduu dhumiyey Eebe, Ruuxuu dhumiyo Eebana uma heshid Waddo (Hanuun).'),
(582, 4, 89, 'Waxay jecelyihiin (Munaafiqiintu) inaad Gaaloowdaan siday u Gaaloobeen ood isku mid noqotaan, ha ka yeelanina Xaggooda Sokeeye intay uga hijroodaan Jidka Eebe, haddayse Jeedsadaan qabta oo ku dila Meejaad ka heshaanba, hana ka yeelanina Xaggooda Sokeeye iyo Gargaare (Midna).'),
(583, 4, 90, 'Kuwa Gaadhi qoom dhexdiinna iyo dhexdooda ballan (heshiis) yahay mooyee, ama idiin yimid yadow Cidhiidhyantahay Laabtoodu inay idinla Dagaallamaan ama la dagaallamaan Qoomkooda, hadduu doono Eebe wuu idinku diri lahaa wayna idin la dagaallami lahaayeen haddayse idinka dheeraadaan oyan idin la Dagaallamin oy idiin soo hormariyaan Nabadgalyo idiin ma yeelin Eebe korkooda Jid (Xaq kuma lihidiin Dagaal).'),
(584, 4, 91, 'Waxaad helaysaan kuwa kale oo dooni inay idin nabadgaliyaan, oo nabadgaliyaan Qoomkooda (daahirka) markastoo loo celiyo Gaalnimana waa lagu dedijiyaa dhexdiisa, haddayna idinka dheeraan ooyan idin siinin Nabadgalyo ooyan idinka reebin Gaemahooda qabta oo ku Dila meejaad ka heshaanba, kuwaasna waxaan idiinka yeellay Xujo Cad.'),
(585, 4, 92, 'uma habboona (banaana) Ruux Mu\'min ah muu Dilo Mu\'min si gaf\'ah mooyee, Ruuxii u Dila Mu\'min Gaf waxaa saaran Xorayn qoor Mu\'min, iyo Mag la siiyo Ehelmiisii, inay Sadaqaystaan moyee, hadduu ka midyahay Qoom aad Coltihiin isna Mu\'min yahay waxaa Saaran Xorayn Qoor Mu\'min, hadduu ka midyahay Qoom ballan, idin dhexyaal waxaa saaran Mag la siiyo Ehelkiisii iyo Xorayn Qoor Mu\'min ah, hadduusan helin (Qoorna) wuxuu Soomi Laba Bilood oo Israac raacsan, waana Toobad Eebe, Eebana waa Oge Falsau.'),
(586, 4, 93, 'Ruuxii u Dila Mu\'min si Kas ah, Abaalkiisu waa Jahannamo oos ku Waari dhexdeeda, wuuna u Cadhoon Eebe wuuna Laenadi wuxuu u darbi Cadaab wayn.'),
(587, 4, 94, 'Kuwa xada Rumeeyow markaad u Safartaan Jidka Eebe wax caddaysta (Hubsada) hana dhihina Ruux Salaan idiin soo gudbiyey ma tihid Mu\'min idinkoo dooni Dhalateedka Nolosha dhaq. Eebe agtiisa yey tahay qaniimooyin Badan, saasaad ahaydeen horay waxaana idinku Mannaystay Eebe korkiinna, ee caddaysta (Hubsada), Eebana waxaad Camal falaysaan waa Ogyahay.'),
(588, 4, 95, 'Ma Sinna kuwa Fadhiya ee Mu;miniinta ah oon kuwa dhibban ahayn iyo kuwa kula Jahaadi Jidka Eebe Xoolahooda iyo Naftooda, wuu ka Fadilay Eebe kuwa ku Jahaaday Xoolahooda iyo Naftooda kuwa fadhiya Darajo, dhammaanna wuxuu u Yaboohay Wanaag, wuuna kaga fadilay Eebe kuwa Jahaaday kuwa Fadhiya Ajir wayn.'),
(589, 4, 96, 'Waa Darajooyin Xaggiisa ah iyo Dambidhaaf iyo Naxariis, Eebana waa Dambi Dhaafe Naxariista.'),
(590, 4, 97, 'Kuway Oofsato Malaa\'igtu iyagoo Dulmiyey Naftooda waxay ku Dhahaan maxaad ahaydeen waxayna dhahaan kuwo lagu Daeiifsadat (tabar yar) Dhulka, waxayna dhahaan (Malaa\'igtii) miyeyna ahayn Dhulka Eebe Wasaac ood ku Hijrootaan dhexdeeda, kuwaas Hooygoodu waa Jahannamo wayna u Xuntahay Aaye.'),
(591, 4, 98, 'Kuwa la Daeiifsado oo Rag, Haween iyo Caruur oon tabar lahayn kuna Toosi karin Jidka mooyee.'),
(592, 4, 99, 'Kuwaas wuxuu u dhawyahay Eebe inuu iska Cafiyo Xaggooda, Eebana waa Saamaxe Dhaafa (Dambiga).'),
(593, 4, 100, 'Ruuxii ku Hijrooda Jidka Eebe wuxuu ka heli Dhulka Magangal badan iyo Waasacnimo, Ruuxii ka Baxa Gurigiisa isagoo u Hijroon Xagga Eebe iyo Rasuulkiisa markaas uu sugnaaday Ajrigiisu Eebe (Fadhi dartiis) Eebana waa (Dambi) Dhaafe Naxariista.'),
(594, 4, 101, 'Haddaad ku Safartaan Dhulka ma aha korkinna Dhib (Dambi) inaad Gaabisaan Salaadda Haddad ka Cabsataan inay idin Fidmeeyan (Dhibaan) kuwa Gaaloobay, Gaaladuna waxay idiin yihiin Col Cad.'),
(595, 4, 102, 'Markaad tahay dhexdooda Muslmiinta ood u oogto Salaadda ha istaagto Koox ka mid ah Agtaada hana Qaateen Hubkooda, markay Sujuudaan ha ahaadeen Gadaashiinna, hana Tukadeen la jirkaaga hana Qaateen Digtooniddoda iyo Hubkooda, waxay Jecelyihiin kuwa Gaaloobay inaad halmaantaan Hubkiinna iyo Alaabtiinna oo markaas ay idin soo Weeraraan mar kaliya (Idunku kadiyaan) Korkiinna ma aha Dhib hadduu idin hayo dhib Roob ah ama aad tihiin kuwa buka inaad dajisaan Hubka Qaatana Digtoonidiinna. Eebana wuxuu u darbay Gaalada Cadaab wax Dulleeya.'),
(596, 4, 103, 'Markaad Gudataan Salaadda Xusa Eebe Taagni. Fadhi iyo Dhinieiinaba (Jiif) markaad xasishaanna (Aamin noqotaan) Ooga Salaadda (Dhameeya) Salaaddu waxay ku tahay Mu\'miniinta Xukun Waqtiyeysan (Mudaysan).'),
(597, 4, 104, 'Haka Daeiifina (Jileina) Doonidda Qoomka (Gaalada) haddaad tihiin kuwa Xanuunsan iyaguba way xanuunsan sidaad u xanuunsanaysaan, waxaadna ka rajaynasaan, waxaadna ka rajaynasaan Eebe waxayna ka rajaynayn, Eebana waa Oge falsan.'),
(598, 4, 105, 'Anagaa kugu soo Dajinay Kitaabka si Xaq inaad ku kala Xukunto Dadka dhexdiisa wuxuu ku tusiyo Eebe, hana u noqonin Khaa\'in u doode.'),
(599, 4, 106, 'Dhambi dhaaf warso Eebe, Ilaahay waa Dambi Dhaafe Naxariista.'),
(600, 4, 107, 'Ha ka Doodin kuwa Khayaamayn Naftoodda, Eebana ma Jeela Khayaamo badane Dambi badan.'),
(601, 4, 108, 'Waxay iska darin Dadka iskamana Qariyaan Eebe isagoo la jooga markay Maamuli wuxuusan raalli ka ahayn oo hadal ah. Eebana waxay Camal fali wuu Koobay.'),
(602, 4, 109, 'Idinku kuwaasow waad ka doodeen Xaggooda Nolosha Adduunyo, yaase ka doodi Xaggooda Maalinta Qiyaame ama u noqon Wakiil.'),
(603, 4, 110, 'Ruuxii fala Xumaan ama Dulmiya Naftiisa (Dambaaba) markaas dambi dhaaf warsada Eebe wuxuu ka heli (ogaana) isagoo Dambi dhaafa ah Naxariistana.'),
(604, 4, 111, 'Ruuxii kasbada danbi wuxuu uun u kasbaday Naftiisa (Dhibkeeda) Eebana waa oge Falsan.'),
(605, 4, 112, 'Ruuxii kasbada Gaf ama Dambi markaas ku gana Dambiga (ku sheega) mid barii ah wuxuu Xambaartay Been abuurasho iyo Dambi Cad.'),
(606, 4, 113, 'Hadduusan jirin fadliga Eebee korkaaga iyo Naxariistiisa waxay Hammin lahayd Koox (dooni lahayd) oo ma kid ah inay ku dhumiyaan mana dhumiyaan Naftooda mooyee mana kaa dhibaan waxba, wuxuuna kugu soo dajiyey Eebe Kitaabka iyo Xigmadda wuxuuna ku baray waxaadan aqoon Fadliga Eebana korkaaga waa ku waynyahay.'),
(607, 4, 114, 'Khayr ma leh wax badan oo Faqooda ah, oon ahayn mid fara Sadaqo ama Wanaag ama Hagaajin Dadka dhexdooda, Ruuxii fala Saas doonid raali ahaanshaha Eebe waxaanu siin Ajir wayn.'),
(608, 4, 115, 'Ruuxii Khilaafa Rassulka ka dib intuu u cadaaday Hanuunku oo Raaca Jidka Mu\'miniinta midaan ahayn waxaa uga wali yeeleynaa wuxuu yeeshay waxaana galinaynaa Jahannamo iyadaana u Xun meel loo ahaado.'),
(609, 4, 116, 'Eebe ma dhaafo in lala Wadaajiyo (Cidkale Cibaadada) wuuse ka dhaafa wixii waxaas ka soo hadhay Cidduu doono. Ruuxii la wadaajiya Eebe wuxuu dhumay dhumid wayn.'),
(610, 4, 117, 'ma Caabudayaan Eebe ka sokow waxaan dhadig ahayn, mana Caabudayaan waxaan Shaydaan madax adag ahayn.'),
(611, 4, 118, 'Ha Laenado Eebe shaydaan wuxuu yidhi waxaan ku yeelan Addomada Qayb Sugan.'),
(612, 4, 119, 'Waan baadiyeyn waan yididiilo (Xun) galin waana fari (Xumaanta) waxayna jeex jeexi Dhagaha Xoolaha waana fari (Xumaanta) waxayna doorin abuurka Eebe ruuxii ka yeesha shaydaan Wali Eebe ka sokow dhab ahaan buu u Khasaaray Khasaare Cad.'),
(613, 4, 120, 'wuu u yaboohi oo yididiila galin mana yabooho shaydaan waxaan dhagar ahayn.'),
(614, 4, 121, 'kuwaasna hooygoodu waa Jahannamo kamana helaan xaggeeda iilasho (iyo fakasho).'),
(615, 4, 122, 'kuwa rumeeyey xaqa oo fala camal fiican waxaannu galin Janooyin oy socoto dhexdeeda Wabiyaal oyna ku waari dhexdeeda waligeed. waana yabooha Eebe oo sugan. yaa ka run badan Eebe hadal (cidna).'),
(616, 4, 123, 'Ma aha (xaalku) yididiiladiinna iyo yididiilada ehelu Kitaabka midna, ciddii fasha xumaan waa laga Abaal marin mana helayo Eebe ka sokow wali iyo gargaare (midna).'),
(617, 4, 124, 'ruuxii fala wanaag lab ah ama dhadig isagoo rumeysan (xaqa) kuwaasu waxay gali Janada lagamana dulmiyo duleel (laftimireed).'),
(618, 4, 125, 'yaa ka diin fiican ruux u jeediyey wajigiisa Eebe isagoo sama fali oo raaca diinta (Nabi) Ibraahim, wuxuuna ka yeeshay Eebe Ibraahiim khaliil (mid u gaar ah).'),
(619, 4, 126, 'Eebe waxaa u sugnaaday waxa ku sugan Samooyinka iyo Dhulka. Eebana waa wax walba koobe.'),
(620, 4, 127, 'waxay ku warsan Haweenka, waxaad dhahdaa Eebaa idiin fidyoon (sheegi) Xaalkooda iyo waxa lagu akhiriyi korkiinna Kitaabka Haweenka Agoonta ah ee ah kuwaydaan siinayn waxa loo qadaray oo doonaysaan inaad guursataan iyo kuwa tabarta yar ee caruurta ah, iyo inaad u maamushaan Agoonta cadaalad, waxaad fashan oo kheeyr ahna Eebe waa ogyahay.'),
(621, 4, 128, 'hadday Haweeney ka cabsato Ninkeeda xaggiisa fogaansho ama jeedsasho dhib ma saara korkooda hadday heshiiyaan heshiis, heshiiskaana khayr ah, waxaana lagu soo kulmiyey nafta bakhaylnimo, haddaad so Wanaag fashaan ood dhawrsataan Eebe waxaad Camal falaysaan waa ogyahay.'),
(622, 4, 129, 'ma kartaan inaad sintaan Haweejkiinna dhexdooda aadba dadaasheene ee ha ka iilanina iilasho dhan, oo ka tagtaan sida mid xidhan (dayacan). haddaad wanaajisaan ood dhawrsataan Eebe waa dambi dhaafe Naxariista.'),
(623, 4, 130, 'hadday kala tagaan wuu kaafiyaa Eebe mid kasta waasacnimadiisa, Eebana waa waasac (Fadligiisu) waana falsame.'),
(624, 4, 131, 'Eebaa iska leh waxa Samaawaadka ku sugan iyo dhulka. waxaana u dardaarnay kuwii la siiyey Kitaabka ee idinka horeeyey iyo idinkaba ka dhawrsada Eebe, haddaadse gaaloowdaan Eebe waxaa u sugnaaday waxa ku sugan Samaawaadka iyo dhulka. Eebana waa hodan Amaanan.'),
(625, 4, 132, 'Eebaa iska leh waxa ku sugana Samaawaadka iyo Dhulka waana ku filanyahay Eebe Wakiil.'),
(626, 4, 133, 'hadduu doono wuu idin tagsiin (dhamayn) Dadow lana imaan kuwo kale waana karaa Eebe Taas.'),
(627, 4, 134, 'Ruuxii dooni abaal Adduunyo Eebe agtiisa yuu jiraa Abaalka Adduunyo iyo Aakhiraba Eebana waa Maqle Arka.'),
(628, 4, 135, 'kuwa (Xaqa) rumeeyow noqda kuwo u istaaaga Caddaalad oo ah marag Eebe, naftiinnaba ha ahaato ama Waalidkiin ama qaraabo, hadday hodan yihiin ama faqiir Eebaa u mudnaansho badan ee ha ka raacina hawada inaad Caddaalo fashaan, haddaadse iilataan ama jeedsataan Eebe waxaad camal falaysaan waa ogyahay.'),
(629, 4, 136, 'kuwa (Xaqa) rumeeyow rumeeya Eebe (si dhabah) iyo Rasuulkiisa iyo Kitaabka uu ku dejiyey Rasuulkiisa iyo Kitaabkuu u dejiyey horay, ruuxiise ka gaalooba Eebe, malaa\'igtiisa, kutubtiisa, rasuuladiisa iyo maalinta dambe (Qiyaamada) wuu dhumay dhumid fog.'),
(630, 4, 137, 'kuwa rumeeyey ee gaaloobay ee hadana rumeeyey ee gaaloobay markaas sii badsaday Gaalnimo ma aha Eebe mid u dhaafi iyaga, kumana hanuuniyo Jidka (Toosan).'),
(631, 4, 138, 'ugu bishaaree munaafiqiinta inay mudan Cadaab daran.'),
(632, 4, 139, 'waana kuwa ka yeesha Gaalada sokeeye Mu\'miniinta ka sokow, ma waxay ka dooni Agtooda sharaf, sharafna Eebaa iska leh dhammaanteed.'),
(633, 4, 140, 'wuxuuna idiin ku soo dejiyey korkiinna Kitaabka dhexdiisa haddaad maqashaan Aayaadka Eebe oo laga gaaloobi ama lagu jees jeesi ha fadhiisanina agtooda intay ka dhumbadaan hadal kale, waxaad noqonaysaan iyaga oo kale, Eebana wuxuu ku kulmin Munaafiqiinta iyo Gaalada Jahannamo dhammaan.'),
(634, 4, 141, 'oo ah kuwa udun la suga (dhibaato) hadduu gargaar xagga Eebe idiinka ahaado waxay dhihi miyaanaan idinla jirin, hadduu gaalada u sugnaado nassibna waxay dhahaan miyaanaan idin adkeynin oonaan idinka reeebin Mu\'miniinta, Eebaa xukumi dhexdooda maalinta Qiyaame, mana ugu yeelayo Eebe gaalada mu\'miniinta korkooda waddo.'),
(635, 4, 142, 'munaafiqiintu waxay dhagri Eebe (isku dayi) isna wuu dhagri (oo abaal Marin) markay u istaagaan Salaaddana way istaagaan iyagoo wahsan, oo is tusi Dadka Mana xusaan Eebe wax yar mooyee.'),
(636, 4, 143, 'waxayna ku noqnoqon (wareeri) arrintaas dhexdeeda kuwaasina lama jiraan kuwaana lama jiraan, cidduu dhumiyey Eebe uma heshid Waddo (eid hanuunin).'),
(637, 4, 144, 'kuwa (Xaqa) rummeyow ha ka yeelanina Gaalada sokeeye Mu\'miniinta ka sokow, Ma waxaad doonaysaan inaad isugu yeeshaan Eebe korkiinna xujo cad (inuu idin ciqaabo).'),
(638, 4, 145, 'munaafiqiintu waxay ku sugnaan dabaqa hoose ee Naarta umanaheshid gargaare.'),
(639, 4, 146, 'kuwa toobadkeena ee wanaajiya camalka, oo isku dhawra Eebe, oo u kali yeela Diintooda (Cibaadadooda) Eebe kuwaasi waxay la jiri mu\'miniinta, wuuna siin doonaa Eebe Mu\'miniinta Ajir wayn.'),
(640, 4, 147, 'muxuu ku fali Eebe cadaabkiinna haddaad mahdisaan ood rumeysaan, Eebana waa mahdiye Waana oge.'),
(641, 4, 148, 'Ma Jeela Eebe in lala Qayliyo Xumaan oo Madal ah Ruux la Dulmiyey Mooyee Eebana waa Maqle og.'),
(642, 4, 149, 'Haddaad Muujisaan Khayr ama Qarisaan ama Cafidaan Xumaan Eebana waa Dhaafe kara.'),
(643, 4, 150, 'Kuwa ka Gaalooba Eebe iyo Rassulkiisa oo dooni inay kala (Bixiyaan) Eebe iyo Rasuulkiisa oo odhan waxaannu Rumayn Qaar waana Diidi Qaar oo dooni inay ka yeeshaan Arrintaa dhexdeeda Waddo.'),
(644, 4, 151, 'Kuwaasi iyaga umbaa Gaalo dhab ah, waxaana u darbannay Gaalada Cadaab wax Dulleeya.'),
(645, 4, 152, 'Kuwa rumeeyey Eebe iyo Rassulkiisa oon kala bixinin Cid ka mid ah kuwaas wuu Siindoonaa (Eebe) Ajirkooda, Eebana waa Dhaafe Naxariista.'),
(646, 4, 153, 'Waxay ku warsan Ehelu Kitaabku Inaad kaga soo Dejiso Kitaab Xagga Samada, Dhabahaan yey u warsadeen (Nabi) Muuse wax ka wayn kaas waxayna dheheen noo tusi Eebe si Cad waxaana qabatay qaylo (daran) Dulmigooda dartiis, waxayna yeesheen Dibi intay u Timid Xujooyin, waana ka Cafinay Arrintaas, waxaana siinay Nabi muuse Xujo Cad.'),
(647, 4, 154, 'Waxaan koryeellay (Buurta) Dhuur Ballankoodii dartiis waxaana ku nidhi ka gala Irridda idinkoo Sujuudsan, waxaana ku nidhi ha ku xadgudhina Sabtida waxaana ka qaadnay Ballan adag.'),
(648, 4, 155, '(Waan Lacnadnay) Burintooda Ballankooda iyo ka Gaalowgooda Aayaadka Eebe iyo u Dilidda Nabiyada Xaqdarro iyo Hadalkooda Quluubtanadu way daboolantahay, saas ma aha ee waxaa Daabacay (daboolay) Eebe Gaalnimadooda darteed mana rumeeyaan wax yar mooyee.'),
(649, 4, 156, 'iyo Gaalnimadooda darteed iyo Hadalkooda Maryama oy ku Been abuurteen si wayn.'),
(650, 4, 157, 'iyo Hadalkoodii ahaa anagaa Dillay Masiix Ciise Ibnu Maryam Rasuulkii Eebe ahaa mayna Dilin mana Wadhin laakiin waa loo Shabahay, (ekeysiiyey), kuwii isku Khilaafay Ciise waxay kaga suganyihiin Shaki, wax Cilmi ahna uma leh oon ahayn Raacid Male umana dilin dhab.'),
(651, 4, 158, 'Saas ma aha ee waxaa u koryeelay Xaggiisa Eebe, ilaahayna waa Adkaade Falsan.'),
(652, 4, 159, 'Ehelu Kitaab kasta wuu rumeyn Ciise Geeridiisa ka hor, Maalinta Qiyaamana wuxuu ku noqon korkooda Markhaati.'),
(653, 4, 160, 'Dulmi kuwii Yuhuudoobay dartiis yaan uga Reebnay korkooda Wanaaga loo xalaaleeyey iyo ka Celintooda Jidka Eebe wax badan.'),
(654, 4, 161, 'Iyo Qaadashadooda Ribada iyagoo laga Reebay Xaggeeda iyo ku Cunidda Xoolaha Dadka si Baadil ah, waxaana u darbanay Gaalada ka mid ah Cadaab Daran.'),
(655, 4, 162, 'Laakiin kuwa ku xeeldheer Cilmiga oo ka mid ah iyo Mu\'miniintu waxay rumeyn waxa lagugu Dejiyay, iyo wixii la Dejiyey hortaa, iyo kuwa Oogo Salaadda ee Bixiya Zakada ee rumeeya Eebe iyo Maalintii Dambe kuwaas waxaan siinaynaa Ajir wayn.'),
(656, 4, 163, 'Annagaa kuu waxyoonnay sidaan ugu waxyoonay (Nabi) Nuux iyo Nabiyadii ka dambeeyey waxaana u waxyoonay Nabi Ibrahim, Ismaaeiil, Isxaaq, Yacquub (Awlaad) Faracii, Ciise, Ayuub, Yuunus, Haaruun, Sulaymaan, waxaana siinay Daawuudna Zabuur.'),
(657, 4, 164, 'iyo Rasuullo aan kaaga Qisoonay horay iyo Rasuullo aanaan kaaga qisoon, wuuna la Hadlay Eebe Nabi Muuse Hadal.'),
(658, 4, 165, 'Waa Rasuullo Bishaarayn oo Digi si ayan Dadka Xujo ugu yeelan Eebe ka dib Rasuullada, Eebana waa Adkaade Falsan.'),
(659, 4, 166, 'Laakiin Eebaa Marag Furi wuxuu kugu Dejiyey wuxuuna ku Dejiyey Cilmigiisa, Malaa\'igtuna way Marag furi, Eebana ku Filan Marag.'),
(660, 4, 167, 'Kuwa Gaaloobay ee Dulmi falay ma aha Eebe waay Dhumeen Dhumid Fog.'),
(661, 4, 168, 'kuwa Gaaloobay ee Dulmi falay ma aha Eebe kii u kii u Dambi dhaafa ama ku Hanuuniya Jid.'),
(662, 4, 169, 'Jidka Jahannamo mooyee iyagoo ku waari Waligood. Arrintaasna waa u Fududahay Eebe.'),
(663, 4, 170, 'Dadow wuxuu idiinla Yimid Rasuulku Xaq Xagga Eebe ee Rumeeya (Xaqa) saasaa idiin khayr roon, haddaad Gaalowdaanna Eebe waxaa u Sugnaaday waxa ku sugan Samaawaadka iyo Dhulka Eebana waa oge falsan.'),
(664, 4, 171, 'Ehelu Kitaabkoow ha ku Xadgudbina Diintiinna hana ku Dhihina Eebe waxaan Xaq ahayn, Masiix Ciise Ibnu Maryam waa uun Rasuulkii Eebe iyo Kalimadiisii uu ku Tuuray Maryama iyo Ruux Xaggiisa ah ee rumeeya Eebe iyo Rasuulasiisa, hana dhihina Saddex (ILaah) reebtooma, saasaa idiin Khayr Roon, Eebe waa uun ILaah Kaliya, waa ka Nasahanyahay in ILmo u sugnaado, waxaa u sugnaaday waxa Samaawaadka iyo DHulka ku sugan, Eebaana ku filan Wakiil.'),
(665, 4, 172, 'Iskama kibriyo Masiix inuu ahaado Addoon Eebe, Malaa\'igta la dhaweeyeyna (iskama kibriso) ruuxii iska kibriya Cibaadadiisa oo iswayneeya wuu u soo kulmin Xaggiisa Dhammaan.'),
(666, 4, 173, 'Kuwa rumeeyey Xaqa oo Camal fiican fala wuxuu u Oofin Ajrigooda wuxuuna u kordhin Fadligiisa, kuwa iska wayneeya oo iskibriya wuxuu Cadaabi Cadaab Daran mana helayaan Eebe ka sokow Wali iyo Gargaare (midna).'),
(667, 4, 174, 'Dadow waxaa idiin yimid Xujo Xagga Eebe ah waxaanan idiin soo Dejinay Nuur Cad.'),
(668, 4, 175, 'Kuwa rumeeyase Eebe oo Qabsaday (isku dhawray) Eebe (iyo Quraanka) wuxuu Galin Naxariis xagiisa ah iyo Fadli wuxuuna ka Hanuunin Xaggiisa Jid Toosan.'),
(669, 4, 176, 'Waxbay ku warsan, waxaad dhahdaa Eebaa idiin Fidyoon Kalaalada, hadduu dhinto Ruux aan ILmo lahayn oo leh Walaashiis waxay Mudan Nus wuxuu ka tagay isaguna wuu Dhaxli hadduusan ILmo u sugnaanin, hadday Labo yihiina waxay Mudan Labo Dalool wuxuu ka tagay, hadday yihiin Walaal Rag iyo Haween ah kan Lab waxaa u sugnaaday Labo Dhadig Qaybtood, wuxuu idiin caddayn Eebe (Xaqa) inaydaan Dhumin Eebana wax kasta waa Ogyahay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'Kuwa (Xaqa) Rumeeyoow Oofiya Ballanka, waxaa Laydiin Baneeye Xoolaha Nicmoolayda waxa laydinku Ahrin Korkiina Mooyee idinkoon banaysanayn Ugaadshiga idinkoo xarman Eebana wuxuu Xukumaa wuxuu Doono.'),
(671, 5, 2, 'Kuwa (xaqa) Rumeeyow ha Banaysanina Calaamooyinka Eebe (waxa reeban) iyo Bisha Xurmaysan iyu Hadyada iyo Midda loo Luqun Xidhay (Hadyada Baydka) iyo kuwa u Qasdi Baydka Xurmaysan, iyagoo Dooni Fadliga Eebahood iyo Raali ahaanshihiisa, Markaad Xalaalowdaanna (Dhamaysaan Xajka iyo Cumrada) Ugaadhsada (hadaad Doontaan) yeyna Idin ku Xambaarin Cadhada Qoom idinka Celiyey Masaajidka Xurmaysan inaad Xad gudubtaan, Isugu kaalmeeya Samaha iyo Dhawrsiga hana Isugu Kaalmaynina Dambi iyo Colnimo, kana Dhawrsada Eebe, Ilaahay way darantahay Ciqaabtiisu,'),
(672, 5, 3, 'Waxaa laydinka Xarrimay Bakhtiga iyo dhiigga iyo Hilibka Doofaarka iyo wixii lagu Xuso (Lagu Gawraco) waxaan magaca Eebc ahayn, iyo tii In Ceejiyo, iyo la Ganac gooyo, iyo lan meel ku dhacda, iyo Tii la Hardiyo, iyo Midduu Cunay bahal, wuxaud Gawracdaan mooyee (waxaa kaloo reeban) wixii lagu gawraco Sanam, iyo inaad ku Nasiib doontaan Faal, kaasi waa Fisqi, maantayna ka quusteen kuwii Gaaloobay Diintiinna, Ee ha ka Cabsanina ee iga Cabsada, Maantaan idiin kaamilay (Dhameeyey) Diintiinna waxaana idiin Taamyeelay Korkiinna Nicmadayda waxaana idiinka Raalli noqday Islaamka Diin ahaan, Ciddii loo Dhibaateeyo Baahi inay cunaan wixii laga xaaraameeyey oon Falayn Dambi Eebc waa dambi dhaafe Naxariista.'),
(673, 5, 4, 'waxay ku warsan maxaa loo Xalaaleeyey waxaad dhahdaa waxaa laydiin xallilay Wanaagga (Xalaasha) iyo waxaad wax barteen oo wax Dhaawaca idinkoo ku ugaadhsan, ood baraysaan wuxuu idin baray Eebe ee ka cuna waxay idiin qabtaan ku Xusana Magaca Eebe kana Dhawrsada Eebe, ILaahay waa deg degtaa Xisaabtiisuye.'),
(674, 5, 5, 'Maanta waxaa Laydiin Xalaaleeyey Wanaagga Cunada kuwa la siiyey Kitaabna Xalaalbuu idiin yahay. Cunadiinuna xalaalbay u tahay, iyo kuwa Dhawrsoon oo Mu\'minaadka ah (Guurkooda) iyo kuwa Dhawrsoon oo ka Mid ah kuwa la siiyey Kitaabka oo idinka horreeyey haddaad siisaan ujuuradooda (Meherkooda) iyagoo Dhawrson oon Xumaan (Zino) Samaynayn Yeelanaynna Saaxibbo (Xun) Ciddii ka Gaalowda limaanka wuu Hoobtay Camalkiisu wuxuuna ka mid noqon aakhiro kuwa Khasaaray.'),
(675, 5, 6, 'Kuwa (xaqa) Rumeeyow Haddaad u Kacdaan Salaadda Dhaqa Wajiyadiina, iyo Gacmihiinna Tan iyo Jiqilka (Xusulka) Masaxana Madaxiinna (Dhaqana) Lugihiina Tan iyo anqawyada haddaad Janaabo qabtaan isdaahiriya, Haddaad Buktaan ama Safartihiin ama ka timaadaan Kortag ama Istaabataan haween oydaan helayn Biyo ku Gagabaysta shul daahir kuna Masaxa Wajigiinna iyo Gacmihiinna, idin lama doonayo Eebe inuu isin yeelo korkiina Dhib wuxuuse dooni inuu idin Daahiriyo idiin dhammeeyo Nicmadiisa korkiinna inaad Shugridaan Darteed.'),
(676, 5, 7, 'Xusuusta Nicmada Eebe iyo Ballankiisa uu idin kula Ballamay markaad Dhahdeen waan maqalay waana adeecnay kana Dhawrsada Eebe ILIeen ILaahay waa Ogyahay waxa Laabta ku Sugane.'),
(677, 5, 8, 'Kuwa (xaqa) Rumeeyow Noqda kuwa Istaaga (u Darban) dar Eebe oo ku marag kaca Caddaalad yeyna idinku Xambaarin Cadhada qoom inaydaan Caddaalad falin, Caddaalad fala iyadaa u dhaw Dhawrsashada (Eebe) kana Dhawrsada ILaahay, Eebe waa Ogyahay waxaad falaysaane.'),
(678, 5, 9, 'Wuxuu u Yaboohay Eebe kuwa Rumeeyey (Xaqa) oo falay Camal Fiican inuu u sugnaaday Dambi dhaaf iyo Ajir wayn.'),
(679, 5, 10, 'Kuwa Gaaloobay oo Beeniyey Aayaadkannaga kuwaasu waa Asaxaabta (Ehelka) Jaxiimo.'),
(680, 5, 11, 'Kuwa (xaqa) Rumeeyow Xusuusta Nicmada Eebe korkiinna markay Hamiyeen «dooneen» qoom inay idinku Fidiyaan (Xumaan) Gacmahooda oo (Eebe) idinka reebay Gacmohooda Xaggiina kana Dhawrsada Eebe, Ilaahayna ha tala Saarteen Mu\'miniintu.'),
(681, 5, 12, 'Dhab ahaanbuu u qaaday Eebe Ballanka Banii\'Israa\'iil, waxaana ka Bixinay Xaggooda Labo iyo toban Madax ah, wuxuuna yidhi Eebe anagu waan idin la jiraa Haddaad Oogtaan Salaadda oo Bixisaan Zakada ood Rumaysaan Rasuuladayda, ood una Gargaartaan oo amaah gashataan Eebe amaah (Sadaqo) Fiican waxaan ka Asturi Xaggiinna Xumaanta waxaana idin galin Jannooyin ay Dureeri Dhecdeeda Wabiyaal Ruuxii Gaalooba intaas ka Dib oo idin Ka mid ah wuxuu ka Dhumay Jidka Toosan.'),
(682, 5, 13, 'Burintooda Ballankooda (Yuhuud) yaan u Lacnadnay kana Yeellay Quluubtooda mid Ingagan, iyagoo ka leexin Kalimada meejeeda waxayna halmaameen qayb ka mid wixii lagu waaniyey kamana waydid Xaggooda inaad ku aragtid Khayaamo wax yar mooyee ee iska Cafi iskana Saamax Eebe wuxuu Jecelyahay samo Falayaashee.'),
(683, 5, 14, 'Kuwa Yidhi waxaan nahay Nasaara waxaan ka qaadnay Ballankooda waxayna halmaameen qayb wixii lagu Waaniyey, markaasaan ku sii daynay Dhexdooda Colnimo iyo Cadho Tan iyo Maalinta Qiyaame, wuuna uga warrami Eebe waxay Samaynayeen.'),
(684, 5, 15, 'Ehelu Kitaabow waxaa idiin Yimid Rasuulkannaga oo idiin Caddayn wax badan ood ahaydeen kuwo ka qariya Kitaabka (Tawreed) wuxuuna idinka Cafiyi wax badan, waxaana idiinka Yimid Xagga Eebe Nuur iyo Kitaab Cad.'),
(685, 5, 16, 'Uuna ku Hanuuniyo Eebe Ruuxii Raaca Raalli ahaanshihiisa Jidka Nahadgalyada kana bixin Mudiyada Xagga Nuurka Idinkiisa kuna hanuunin jidka Toosan.'),
(686, 5, 17, 'Waxaa Gaaloobay kuwa yidhi Eebe waa uun Masiixi Ibnu maryama, yaa uga Hanan Eebe waxba hadduu doono inuu Halaago Masiixi Ibnu Maryama iyo Hooyadiis iyo waxa Dhulka ku sugaan Dhammaan, Eeba iska leh Xukunka Samooyinka iyo Dhulka, iyo waxa u Dhaxeeya, wuxuuna abuuraa wuxun Doono Eebana wax kasta wuu karaa.'),
(687, 5, 18, 'Waxay Dheheen Yuhuud iyo Nasaaraba anagu waxaan nahay Caruur Eebe iyo juwuu Jecelyahay waxaad Dhahdaa Muxuu idiinku Cadaabaa dambigiinna saas ma aha ee waxaad Tihiin Dadka Eebe Abuuray, wuu u Dhaafi Cidduu Doono wuuna Cadaabi Cidduu Doono, Eebana waxaa u sugnaaday Xukunka Samooyinka, iyo Dhulja iyo waxa u Dhexeeya Xaggiisaana loo ahaan.'),
(688, 5, 19, 'Ehelu Kitaabow waxaa idiin Yimid Rasuulkanagii isagoo Idiin Caddayn Muddo (kadib) rasuulada inaydaan dhihin nooma Imaan Bishaareeye iyo Dige, Eebana wax kasta wuu karaa.'),
(689, 5, 20, '(Xusuuso) Markuu ku Yidhi (Nabi) Muuse Qoomkiisa, Qoomkayow Xusuusta Niemada Eebe ee korkiinna markuu yeelay Dhediinna Nabiyo idinkana yeelay Xukaam idinsiiyeyna waxaan la siin Cid Caalamka ah, (Waqtigiinnii).'),
(690, 5, 21, 'Qoomkayow gala Dhulka la Daahiriyey oo idiinyaboohay Eebe hana u noqonina Gadaashiinna oo gadoontaan idinkoo Kashaaray.'),
(691, 5, 22, 'Waxay dheheen Muusow waxaa (Dhulka) Dhexdiisa ah qoom daran mana galayno intay ka baxaan xaggeeda hadday ka baxaanna waanu gali.'),
(692, 5, 23, 'waxay Dheheen Labo Nin oo ka mid ah kuwa ka Cabsada (Eebe) una Niemeeyey Eebe uga gala albaabka Markaad gashaan idinkaa Adkaane, Eebana tala saarta haddad Mu\'miniin tihiin.'),
(693, 5, 24, 'Waxay dheheen Muusow anagu ma galayno Waligeed intay joogaan ee taga adiga iyo Eebaha oo la dagaalama anagu halkanaan Fadhiyeynaaye.'),
(694, 5, 25, 'Wuxuu yidhi (Nabi) Muuse Eebow ma hanto Naftayda iyo Walaalkay Mooyee ee nakala Bixi (Xukun) Dhexdannada iyo Qoomka Faasiqiinta ah (Xun).'),
(695, 5, 26, '(Eebe) Wuxuu Yidhi iyadu «Dhulka» waa ka Xaaraan korkooda Afartan Sano oy ku Wareeri Dhulka ee haw Murugoon qoomka Faasiqiinta ah.'),
(696, 5, 27, 'Ku akhri Korkooda warka Labadii Wiil ee Ilmo Nabi aadam ahayd si dhab ah, markay Dhaweeyeen (Sadaqaysteen) Sadaqo oo laga Aqbalay Midkood lagana aqbalin kii kale, oo uu Yidhi waan ku Dili uu Yidhi (Kii kale) wuxuu uun Eebe ka aqbalaa kuwa Dhawrsada.'),
(697, 5, 28, 'Haddaad u soo Fidiso Xaggayga Gaeantaada inaad i disho anigu kugu Fidin maayo gaeanta inaan ku dilo Anugu waxaan ka Cabsan ilaahay ee ah Eebaha Caalamka.'),
(698, 5, 29, 'Waaxaana Dooni inaad la Noqoto Dambigayga iyo kaagaba ood noqoto Naarta Ehelkeeda Taasina waa Abaalka Daalimiinta.'),
(699, 5, 30, 'Markaasay u qurxisay Naftiisii Dilka Walaalkiis Wuuna Dilay wuxuuna Nooqday kuwa Khasaaray.'),
(700, 5, 31, 'Wuxuuna Eebe u Bixiyey (Muujiyey) Tuke Faadhi Dhulka inuu Tusiyo siduu u Asturo Maydka Walaalkiis, wuxuuna Yidhi Shalaytee miyaan ka Cajisay inaan noqdo Sida Tukahan oon Asturo Maydka Walaalkay wuxuuna ahaaday mid Qoomamooda.'),
(701, 5, 32, 'Saas Darteed yaan ugu Jidaynay Banii-Israa\'iil Cidii Disha Naf aan (Dilin) Naf ama Fasaadin Dhulka waxay la mid tahay inuu Dilay Dadka Dhammaan, Ciddii Noolaysa Nafna (Korisa) Waxay la mid Tahay Isagoo Nooleeyey Dadkoo Dhan, Waxay ula Timid Rasuuladanadii Xujooyin Markaas in Badan oo ka mida Intaas ka dib Dhulkay ku Xadgudbeen.'),
(702, 5, 33, 'Abaalka kuwa la Dagaalama Eebe iyo Rasuulkiisa oo la Socda Dhulka Fasaad waa in la Dilo ama la Wadho, ama loo gooyo Gacmahooda iyo Lugabooda Isdhaaf, ama laga Fogeeyo Dhulka, arrintoodaasna waa inay Mudanyihiin Dulli Adduun iyo Aakhiro oo Cadaab Wayna.'),
(703, 5, 34, 'Kuwa Toobad keena qabasho ka hor mooyee Ogaadana in Eebe Dambi Dhaafo oo Naxariisto.'),
(704, 5, 35, 'Kuwa (Xaga) Rumeeyow ka Dhawrsada Eebe una Doona Xaggiisa Dhawaansho kuna Jahaada Jidkiisa waxaad u Dhawdihiin inaad Liibaantaane.'),
(705, 5, 36, 'Kuwa Gaaloobay hadduu u ahaado waxa Dhulka ku Sugan oo Dhan iyo wax la mid ah inay iskaga Furtaan Cadaabka Maalinta Qiyaame lagama Aqbaleen waxayna Mudan Cadaab Daran..'),
(706, 5, 37, 'Waxay Dooni inay ka soo Baxaan Naarta kamana Baxaan Xaggeeda waxayna mudan Cadaab Joogta ah.'),
(707, 5, 38, 'Tuugga iyo Tuugadda Gooya Gaemahooda Abaalmarin waxay kasbadeen Darteed waana Xanuujin Eebe ILaahayna waa adkaade Falsan.'),
(708, 5, 39, 'Ciddiise Toobad keenta Dulmigiisa ka Dib oo Fiienaada ILaahay wuu ka Toobad Aqbali, Eebana waa Dambi Dhaafe naxariista.'),
(709, 5, 40, 'Miyaadan Ogayn in Eebe u sugnaaday Xukunka Samooyinka iyo Dhulka uuna Cadaabi Cidduu Doono, waana u dambi dhaafi Ciddu la doono Ilaahayna wax kasta wuu karaa.'),
(710, 5, 41, 'Rasuulow Yeyna ka Walbahaarin kuwa ku Dag dagi Gaalnimada oo ah kuwa ku Yidhi Afkooda waan Rumaynay ooyna Rumayn Quluubtoodu, iyo kuwa Yuhuudoobay waxay maqli ogyihiin Beenta waxayna maqli Ogyihiin qoom kale oon kuu Imaanin waxayna ka leexiyaan Kalimooyinka Meelaheeda (Dhabta) waxayna Odhan haddii laydinsiiyo kan (Leexsan) qaata haddaanse laydin siinin ka Digtoonaada, Ruxuu Doono Eebe Ibtilayntiisa waxba ugama hanatid Eebe waxba, kuwaasina waa kuwaan Eebe la Doonin inuu Daahiriyo quluubtooda, waxayna Mudan adduunka Dulli Aakharana waxay mudan Cadaab wayn.'),
(711, 5, 42, 'Waxay Maqli Ogyihiin Beenta waxayna Cunid Badanyihiin Xaaraanta hadday kuu Yimaaddaana kala Xu kun Dhexdooda ama ka Jeedsohaddaad kajeedsatana Wax kaama dhibaan haddaad kala xukuntana Caddaalad kukala xukun Eebe wuxuu Jecelyahay caadiliintee.'),
(712, 5, 43, 'Siday kuugu Xukuntami Iyadoo agtooda yahay Tawreed oo Xukunkii Eebe ku Yaal markaasna ay ka Jeddsan intaas ka Dib Kuwaasi ma aha Mu\'Muniin.'),
(713, 5, 44, 'Anagaa soo Dejinay Tawreed oo Dhexdeeda ku suganyahay Hanuun iyo Nuur, oyna ku xukumayeen Nabiyadii u Hogaansamay Eebe kuwii Yuhuudoobay, waxaana Xukumi Jiray madaxdii iyo Culimadii wixii laga Doonay inay ILaaliyaan oo Kitaabka Eebe ahm waxay ku ahaayeen Markhaati ee ha ka Cabsanina Dadka ee iga Cabsada ani, hana ku Gadanina Aayaadkayga Lacag (Qiimo) Yar. Ciddaan Xukumin wuxuu soo Dejiyey Eebe kuwaasi waa Gaalo uun.'),
(714, 5, 45, 'Waxaan ku Faralyeelay korkooda in Nafta (Loo qisaaso) Nafta, ishana Isha, Sankana Sanka, Dhagtana Dhagta, ILigana ILiga, Nabaradana waa laysu Qisaasi, Ciddii iska Sadaqaysata (Samaxda) waxay u Noqon Kafaaro (Dambi Dhaaf) Cidaanse Xukumin wuxuu soo Dejiyey Eebe kuwaasi waa Daalimiin uun.'),
(715, 5, 46, 'Waxaana Raacsiinay Raadkoodii ciise Ibnu Maryama Isagoo Rumayn, wixii Hortiisa ahaa oo Tawreeda, waxaana Siinay Injiil oo Hanuun iyo Nuur ku Suganyahay, una Rumayn wixii ka Horeeyey oo Tawreeda Hanuun iyo Waanana u ah kuwa Dhawrsada.'),
(716, 5, 47, 'Ila Xukumeen Ehelka Injiil wuxuu ku soo Dejiyey Eebe Dhexdiisa, Cidaan Xukumin wuxuu soo Dejiyey Eebe waa Mid Faasiq ah uun.'),
(717, 5, 48, 'Waxaana kugu Soo Dejiyey Kitaabka Xaqa Isagoo Rumayn wixii ka Horeeyey oo Kitaab ah. Isagoo Marag iyo ILaaliye u ah, ee ku kala Xukun Dhexdooda wuxuu soo Dejiyey Eebe hana ka Raacin Hawadooda waxa kuu Yimid oo Xaq ah, Dhammaan waxaan u Yeellay Shareeco iyo Waddo hadduu Doono Eebana wuxuu idinka Yeeli Umad kaliya, Laakiin wuu idinku Imtixaami wuxuu idin Siiyey ee u Tartama Khayrka, Xagga Eebe yey Noqoshadiinu tahay Dhammaan wuxuuna idiinka Warrami waxaad isku Diidanaydeen.'),
(718, 5, 49, 'Ku kala Xukun Dhexdooda wuxuu soo Dejiyey Eebe, hana Raacin Hawadooda, kana Digtoonow inay kaa Fidmeeyaan waxa Eebe kugu soo Dejiyey qaarkiis, hadday Jeedsadaanna Ogow in Eebe la Doonayo inuu ku Aassi ibo (Ciqaabo) Dambigooda Qarkiis, wax badan oo Dadka ka mid ahna waa Faasiqiin.'),
(719, 5, 50, 'Ma Xukunka Jaahilayad yey doonayaan, yaa ugu Fiican Eebe Xukunkiisa qoomka wax Yaqiinsan (Rumaysan).'),
(720, 5, 51, 'Kuwa Xaqa Rumeeyow ha ka yeelanina Yuhuud iyo Nasaara Sokeeye, Qaarkood waa Sokeeyaha Qaarka (Kale) Ciddii ka sokeeya Yeelata oo idinka Mid ah wuxuue ka midyahay Iyaga, Eebe ma Hanuuniyo Qoomka Daalimiinta ah.'),
(721, 5, 52, 'Waxaad Arkaysaan kuwa Qalbiga ka Buka (Munaafiqiinta) oo u Degdagi Dhexdooda, iyagoo Dhihi waxaannu ka Cabsan inay nagu dhaedo Dhibaato (Socota) waxay u Dhawdahay in Eebe keeno Fatxi (Gargaar) Xagiisa ah oo Markaas ay ahaadaan kuwo waxay u Qarsanayeen Naftooda (oo Gaal jacayl ah) Qoomameeya.'),
(722, 5, 53, 'Waxay Odhan kuwii Rumeeyey (Xaqa) ma kuwaasaa Kuwii ku Dhaartay Eebe Dhaar Adag inay idinla Jiraan, waxaa Hoobtay Camalkoodii waxayna ahaadeen kuwo Khasaaray.'),
(723, 5, 54, 'Kuwa Xaqa Rumeeyow Ruuxii ka Ridooba Diintiisa oo idinka mid ah, wuxuu Eebe keeni qoom uu Jeeelyahay iyana Jeeel una naxariista Mu\'miniinta kuna Daran Gaalada, kuna Jahaadi Jidka Eebe oon ka Cabsanayn Canaanta Canaante, Taasina waa Fadliga Eebe wuxuuna Siiyaa Cidduu Doono, Eebana waa Deeq Badane Og.'),
(724, 5, 55, 'Waligiinnu waa uun Eebe iyo Rasuulkiisa iyo kuwa Rumeeyey (Xaqa) ee Ooga Salaadda oo Bixiya Zakada oo Rukuuea.'),
(725, 5, 56, 'Ciddii ka Waliyeelata Eebe iyo Rasuulkiisa iyo Kuwa Rumeeyey Xaqa Xisbiga ILaahay Umbaa Adkaan.'),
(726, 5, 57, 'Kuwa xaqa Reemeyow ha ka Yeelanina kuwa ka Yeeshay Diinta Jees Jees iyo Ciyaar oo ah kuwa Ehelu Kitaabka ah Idinkana Horeeyey iyo Gaalada sokeeye, kana Dhawrasada Eebe haddaad Tihiin Mu\'miniin.'),
(727, 5, 58, 'Markaad ugu Dhawaaqdaan Salaaddana waxay ka Yeeshaan Jees Jees iyo Ciyaar, waxaana Arrintaas ugu Wacan inay Yihiin Qoomaan waxkasayn.'),
(728, 5, 59, 'Waxaad Dhahdaa Ehelu Kitaabow Miyaad Noogu Cadhoonaysaan inaan Rumaynay Eebe iyo waxa naloo soo Dejiyey iyo wixii la Dajiyey horay Mooyee, Badankiinnu waa Faasiqiin.'),
(729, 5, 60, 'Waxaad Dhahdaa maydinka warramaa waxa ka Sharbadan Arrintaas abaal Marinta Eebe Agtiisa waa mid Eebe Nacladay una Cadhooday kana Yeelay Daanyeerro, Doofaarro iyo Shaydaan Caabude, Kuwaasay Shar badantahay Meejoodu kana Dhumid Badan Jidka Toosan.'),
(730, 5, 61, 'Hadday idiin Yimaaddaan waxay Dhahaan waan Rumaynay (Xaga) iyagoo la soo Galay Gaalnimo, lana Baxay, Eebana waa Ogyahay waxay Qarin,'),
(731, 5, 62, 'Waxaad Arkaysaa wax Badan oo ka mid ah oo u degdegi dambiga iyo Colnimada iyo cunidda Xaaraanta, waxaa Xumaan badan waxay Fali.'),
(732, 5, 63, 'May ka Reebaan Ikhyaartu iyo culimadu hakalkooda dambiga ah iyo Cunidda Xaaraanta, waxaa Xumaan badan waxay Samayn.'),
(733, 5, 64, 'Waxay Tidhi Yuhuuddu Gacanta Eebe waa Laabantahay, ha lallaabo Gaemahoodu hana La naclado hadalkay Dheheen Dartiis, saas ma aha ee waxay ku Fidsanyihiin Khayrka (Deeq badane) wuxuuna u Nafaqeeyaa Siduu Doono, wuxuuna u Kordhin wax badan oo iyaga ka mid ah waxa lagaaga soo Dejiyey Eebahaa Xad Gudub iyo Gaalnimo, waxaana ku Dhex tuuray Col iyo Cadho tan iyo Qiyaame, mar kastooy huriyaan dab Dagaalna waxaa bakhtiiya Eebe, waxayna la Soedaan Dhulka Fasaad, ILaahayna ma Jeela kuwa wax Fasaadiya.'),
(734, 5, 65, 'Hadday Ehelu Kitaabku Rumeeyaan xaqa oy Dhawrsadaan waxaan ka Asluri lahayn Xagooda Xumaantooda waxaana Galinlahayn Jannada Naciimo.'),
(735, 5, 66, 'Hadday ku Camal falaan Tawreed, Injiil, iyo waxa looga soo Dejiyey Xagga Eebahoodna waxay ka Cuni lahaayeen Korkooda iyo Hoostooda, «barwaaqo» waxaa ka mid ah Umad Dhex dhexaysa wax badan oo ka mid ahna waxaa Xun Waxay Fali.'),
(736, 5, 67, 'Rasuulow Gaadhsii waxa lagaaga soo Dejiyey Eebahaa (Dadka) Haddaadan Falin Maadan Gaadhsiin Risaaladiisa «Fariintiisa», Eebaana kaa Haalin Dadka, Eebana ma hanuuniyo qoom Gaala ah.'),
(737, 5, 68, 'Waxaad Dhahdaa Ehelu Kitaabkow waxba ma Tihidiin intaad ka Oogtaan Tawreed, Injiil iyo waxa Laydiinka soo Dejiyye Xagga Eebihiin (Quraanka) wuxuuna u Kordhin wax badan oo ka mid ah waxa Xagga Eebahaa lagaaga soo Dejiyey Xad Gudub iyo Gaalnimo ee haw murugoon qoomka gaalada ah.'),
(738, 5, 69, 'Kuwa Xaqa Rumeeyey iyo kuwa Yuhuudoobay iyo Saabi\'iinta iyo Nasaara Ciddii Rumeysa Eebe, Maalinta Dambe (Qiyaamada) oo fala camal fiican cabsi korkooda ma ahaato iyo Walbahaar (Midna).'),
(739, 5, 70, 'Waxaan Qaadnay Ballanka Banii\'israa\'iil waxaana u Diray Xaggooda Rasuullo, Markastoos ula Yimaaddo Rasuul waxaan Naftoodu Jeelayn Koox way Beeniyaan Kooxna way Dilaan.'),
(740, 5, 71, 'Waxayna u Maleeyeen inaan Fidmo Jirin, wayna Indho Beeleen oo Dhago Beeleen, Markaasuu Eebe ka Toobad Aqbalay, markaasay Indho Beeleen oo Dhago Beeleen wax badan oo ka mid ah Eebana waa Arkaa waxay Fali.'),
(741, 5, 72, 'waxaa Gaaloobay kuwa Yidhi Eebe waa Masiixi Ibnu Maryama, Wuxuuna Widhi Masiix (Ciise) Banii Israa\'iilow Caabuda Eebe ee ah Eebanay iyo Eebihiin, Ruuxii u Shariig Yeela Eebe (La wadaajiya) wuxuu Eebe ka Xarrimay Jannada wuxuuna ku hoyan Naarta Daalimna ma helo Gargaare.'),
(742, 5, 73, 'Waxaa Gaaloobay kuwii Yidhi ILaahay waa Saddex Midkood, ILaah kaliya Mooyee ILaah kalana ma jiro, hadayna ka Joogin waxay Sheegina waxaa Taaban kuwa Gaaloobay oo ka mid ah Cadaab Daran.'),
(743, 5, 74, 'Miyeyna u Toobad keenayn Eebe oyna Dambi Dhaaf warsanayn, Eebaana Dambi Dhaafe Naxariista ah.'),
(744, 5, 75, 'Masiix (Ciise) Ibnu maryama ma aha waxaan Rasuul ahayn, waxaana tagay hortiis rasuuladii, hooyadiisna waxay ahayd rumayso, waxayna cuni jireen Cunnada, day Saannu ugu Caddayn Aayaadka Markaas day Sida loo iilo.'),
(745, 5, 76, 'Waxaad Dhahdaa ma waxaad Caabudaysaan Eebe ka Sokow waxaan Hananin Dhib iyo Dheef (midna) Eebana Yahay Maqle Oge ah.'),
(746, 5, 77, 'Waxaad Dhahdaa Ehelu Kitaabow ha ku Xadgudbina Diintiinna si aan Xaq ahayn, hana Raacina hawada qoom Dhumay mar hore Dhumiyeynna wax badan kana Dhumay Jidka Toosan.'),
(747, 5, 78, 'Waa lagu Lacnaday Kuwii Gaaloobay oo Banii Israa\'iil ah Carrabka Daa\'wuudd iyo Ciise Ibnu Maryama Arrintaasna waxaa ugu Wacan Caasinimadooda ahaayeenna kuwo Xadgudba.'),
(748, 5, 79, 'Waxayna ahaayeen kuwaan iska Reebayn Xumaan ay Falayaan, waxaa Xun waxay Falayeen.'),
(749, 5, 80, 'Waxaad Arkaysaan wax badan oo ka mid ah oo ka Sokeeye Dhigan kuwo Gaaloobay waxaa Xun waxay u Hortmarsatay Naftoodu oo ah in Eebe u cadhooday, Cadaabkana ay ku Waari.'),
(750, 5, 81, 'Hadday Yihiin kuwo Rumeyn Eebe iyo Nabiga iyo waxa lagu soo Dejiyey mayna Yeesheen Sokeeye Gaalo laakiin wax badan oo ka mid ah waa Faasiqiin.'),
(751, 5, 82, 'Waxaad Ogaan kuwa Dadka u Daran Colnimada kuwa Rumeeyey (Xaqa) (Muslimiinta) Yuhuud iyo Mushrikiinta (Diin laawayaasha) waxaad Ogaan kuwa ugu soo Dhaw Jacayl kuwa Rumeeyey Xaqa (muslimiinta) inay yihiin kuwa dhihi waxaan nahay Nasaaro, Illeen waxaa ka mid ah qaysas iyo Raahibiin Ismana Kibriyaan.'),
(752, 5, 83, 'Markay maqlan waxaa lagu soo Dejiyey Rasuulkana waxaad arkaysaa indhahoodoo Ilmo ka Daadan waxay aqoonsadeen oo xaqa ah iagoo dhihi Eebow waan Rumeynay ee nagu dhig (Yeel) kuwa marag furi (Xaqa qiri).'),
(753, 5, 84, 'Maxaanaan u rumeynaynin Eebe iyo waxa noo yimid oo xaq ah oonaan u Damcaynin inuu na galiyo (naga yeelo) Ilaahanno qoomka Suuban la Jirkooda.'),
(754, 5, 85, 'wuuna ku Abaal marin Eebe waxay Dhaheen Jannooyin oy Socoto Dhexdeeda Wabiyaal iyagoo ku Waari Dhexdeeda, Taasina waa Abaal marinta Samafalayaasha.'),
(755, 5, 86, 'kuwa Gaaloobay oo Beeniyey Aayaadkanaga Kuwaasu Waa Jaxiimo Ehelkeeda.'),
(756, 5, 87, 'Kuwa xaqa Rumeeyow ha Xarrimina Wanaagga (Xalaasha) Eebe Idiin Banneeyey hana Xadgudbina, Eebe ma Jeela kuwa Xad gudbee.'),
(757, 5, 88, 'Waxna ka Cuna Wuxuu Idinku Arzaaqay Eebe isagoo Xalaal Fiican ah kana Dhawrsada Eebe ee ah kaad Rumeysantihiin.'),
(758, 5, 89, 'Eebe Idiin ma Qabto Dhaar DanLa\'aaneed (Laqwi) Laakin wuxuu idiin qaban Dhartaad u Qasaddaan, Kafaaradeeduna waa Quudin Toban Miskiin (oo siisaan) kan u dhexeeya waxaad ku Quudisaan Ehelkiina ama Arrad Bixintooda ama Xorayn qoor, Ciddaan Helinna waxay Soomi Saddex Maalmood, Taasaana ah Kafaarada Dhaartiina Markaad Dhaarataan, ee dhawra dhaartiinna Saaduuna idiinku Caddayn Eebe Aayadkiisa, waxaadna Mudantihiin inaad ku Mahdisaan.'),
(759, 5, 90, 'Kuwa Xaqa Rumeeyow Khamrada, qamaarka, Sanamyada «Taagan», iyo Faalku waa uun Xuman (Wasakh) oo Camal Shaydaana ee ka Foogaada inaad Liibaantaan.'),
(760, 5, 91, 'Wuxuu uun Dooni Shaydaanku inuu Idinku Dhectuuro Colnimo iyo isnac Khamrada iyo Qamaarka idinkana Celiyo Xusidda Eebe iyo salaadda ee ma ka Joogaysaan (ka Jooga).'),
(761, 5, 92, 'Adeeca Eebe Adeecana Rasuulka Digtoonaadana, Haddaadse Jeedsataan Ogaada waxaa uun Rasuulkanaga Saaran Gaadhsiin Cadi.'),
(762, 5, 93, 'Dhib (Dhambi) ma Saarra kuwa Xaqa Rumeeyey oo Camal Fiican Falay waxay Quuteen hadday Dhawrsadaan ooy Xaqa Dhab u Rumeeyaan Camal Fiicanna Falaan, oy Xumaanta ka Dhawrsadaan oy Xaqa Rumeeyaan, Haddana Dhawrsadaan samana falaan, Eebana wuxuu Jeelyahay Samo Falayaasha.'),
(763, 5, 94, 'Kuwa Xaqa Rumeeyow wuxuu idinku Imtixaami Eebe wax Ugaadha ka mid ah (Waqtiga Xajka) oo Gaadhayaan Gacmihiinnu iyo waramihiinnu in Eebe Muujiyo Cidda ka cabsata isagoo kali ah, Ciddiise xad gudubta intaas ka dib wuxuu Mudan Cadaab daran.'),
(764, 5, 95, 'Kuwa Xaqa Rumeeyow ha Dilina Ugaadha idinkoo Xarman, Ciddiise Disha oo idinka mid ah si kas ah waxaa Saaran wuxuu Dilay wax la mid ah oo Xoolo ah oy Xukumi labo Caadil ah oo idinka mid ah isagoo ah Hadiyad Gaadhi Kacbada, ama Kafaraaro gud Quudin Masaakiin ama wax u Dhigma oo Soon ah inuu Dhadhamiyo Ciqaabta amarkiisa, wuu idinka Saamaxay Eebe wixii horreeyey Ciddiise ku Noqota wuu ka aarsan Eebe, Ilaahayna waa Adkaade Aarsada.'),
(765, 5, 96, 'Waxaa Laydiin Baneeyey Ugaadhsiga Badda iyo Cunnadeeda idinkoo ku intifaaci iyo Socdaalba, (Idinkoo Xarman) waxaana Laydinka Reebay ka Ugaadshiga Barriga intaad Xarmantihiin, ka Dhawrsada Eebaha Xaggiisa Laydiin soo Kulmin.'),
(766, 5, 97, 'Wuxuu ka Yeelay ILaahay Kacbada Bayd (Guriga) Xurmada Leh, Dhawridda dadka iyo Bilaha Xurmada leh iyo Hadyada iyo Tan la Calaameeyey (Hadyada Darteed) waana inaad Ogaataan in Eebe Ogyahay waxa ku Sugan Samooyinka iyo Dhulka iyo in Eebe wax kasta Ogyahay.'),
(767, 5, 98, 'Ogaada in Eebe Ciqaabtiisu Darantahay Eebana yahay Dambi Dhaafe Naxariista.'),
(768, 5, 99, 'Ma Saarra Rasuulka waxaan Gaadhsiin ahayn, Eebana waa Ogyahy waxaad Muujinaysaan iyo waxaad Qarinaysaan.'),
(769, 5, 100, 'Waxaad Dhahdaa (Nabiyow) ma eka wax Xun iyo Wax Fiican Aadba la yaabtid Badnida kan Xune, kana dhawrsada Eebe Dadka Caqliga lahow inaad Liibaantaan.'),
(770, 5, 101, 'Kuwa Xaqa Rumeeyow ha Warsanina waxyaalo Haddii Laydiin Muujiyo idin Murug Galin, Haddaana Warsataan marka Quraanka la soo Dejin waa Laydiin Muujin, wuuna idinka Cafiyey Eebe Xaggeeda ILaahayna waa Dambi Dhaafe Dulbadan'),
(771, 5, 102, 'Waxaa warsaday Qoom idinka Horreeyey Markaasay Noqdeen Kuwo ka Gaalooba (Markii loo Muujiyey).'),
(772, 5, 103, 'Ma Yeelin Eebe Xoolo Caanahooda Sanamyada loo Bixiyo (Baxiira) iyo Xoolo Sanamyada loo siidaayo (Saa\'ibo) iyo Xoolo Dhadig oo Sanamyada lagu sadaqaysto (Wasiilo) iyo Riti Sanamyada loo Daayo (Xaam) Laakiin kuwii Gaaloobay waxay ku Been abuuraan Eebe Been, Badankoodna wax ma Kasayaan.'),
(773, 5, 104, 'Marka loo Yidhaahdo u Kaalaya waxa Eebe soo Dejiyey iyo Xagga Rasuulkana waxay dhahaan waxaa nagu Filan waxaan ka Hellay Aabayaalkanno, oo Hadday Aabbayaalkood ayna wax Ogayn Hanuunsanaynna (Ma raacayaan).'),
(774, 5, 105, 'Kuwa Xaqa Rumeeyow waxaa idin Saaran Naftiinna Idinma Dhibo Ciddii Dhunta Haddaad Hanuunsantaan, Xagga Eebe Umbaana Noqoshadiinnu tahay Dhammaan wuxuuna Idinka Warrami Waxaad Camal falayseen.'),
(775, 5, 106, 'Kuwa Xaqa Rumeeyow Maragga Dhexdiinna ah markii mid idinka mid ah Geeridu u Timaaddo uu Dardaarmi Waa Labo Caadil oo idinka mid ah, ama Labo Kale oon idinka mid ahayn, markaad Socdaal Tihiin oo Geeeri idin Hesho, idinkoo ku Reebi Salaad Dabadeed iyagoo ku Dhaaran Eebe Haddaad Shakidaan inaanan ku Gadanayn Qiimo yar Qaraababa ha ahaadee qarinna mayno maragga Eebe Markaas aan noqonno kuwa Dambaabay.'),
(776, 5, 107, 'Haddii la Ogaado inay Muteen Dambi waxaa Meejooda Joogsan (oo la Dhaarin) Labo kale oo ah kuwa ugu Mudnaan Badan Dhaxalkiisa, wayna ku Dhaaran Eebe inuu Maragoodu ka Xaqsanyahay Maragooda, mana xadgudbaynno markaas aan Noqonno Daalimiin.'),
(777, 5, 108, 'Saasaana u Dhaw inay Maragga ula Yimaadaan Sidiisa Fiican ama ay ka Cabsadaan in la Diido Dhaartooda intay Dhaartaan ka dib, ee ka Dhawrsada Eebe Maqlana Eebana ma hanuuniyo qoom Fasiqiina.'),
(778, 5, 109, '(Ka warran) Maalintuu Kulmin Eebe Rasuulada kuna dhihi Maxaa laydiinku Jawaabay oy Dhihi ma Ogin adiga unbaa Ogaal Badan waxa maqan.'),
(779, 5, 110, '(Xusuuso) Markuu Eebe dhihi Ciise Binu Maryamow Xusuuso Nicmadaydii korkaaga ahayd iyo hooyadaaba markaan kugu Xoojinay Ruuxii Daahirka ahaa (Jibriil) ood kula Hadlaysay Dadka sariir-ilmood iyo adoo meel dhaxaada, iyo Markaan ku Baray Kitaabka iyo Xigmadda, iyo Tawreed, Injiil, iyo Markaad ka Yeelaysay Dhoobada Sidii Shimbir Idankayga ood Afuufaysay oy Noqonaysay Shimbir Idankayga, ood Bogsiinaysay indhoole Ku dhashe iyo Baras laha Idankayga, ood soo Bixinaysay Maydka Idankayga, Markaan kaa kaafiyey Banii Israa\'iil markaad ula timid Xujooyin, oy Dheheen kuwii Gaaloobay oo ka mida kani waxaan Sixir ahayn ma aha.'),
(780, 5, 111, 'Markaan u Waxyooday (kuILhaameeyey) Saaxiibadii Rumeeya aniga iyo Rasuulkayga oy dhaheen waan Rumaynay ee marag ka Noqo inaan Muslimiin nahay.'),
(781, 5, 112, '(Xusuuso) Markay Dheheen Xawaariyiintii Ciise Ibnu Maryamow Makaraa Eebahaa inuu nooga soo Dejiyo Xeedho (Cunno) Samada wuxuuna yidhi ka Dhawrsada Eebe Haddaad Mu\'miniintihiin.'),
(782, 5, 113, 'Oyna Dheheen waxaannu dooni inaan wax ka Cunno oy Xasisho Quluubtanadu oon Ogaanno inaad Run noo sheegi oonu Noqonno Kuwa ka Marag kaca.'),
(783, 5, 114, 'Uuna Yidhi Ciise Binu Maryama Ilaahayow Eebahanow Nooga soo Deji Xeedho Samada u Noqota Ciid kaanaga hore iyo Kaanaga Dambe iyo Aayad Xaggaaga ka ahaatay nana Arzaaq adaa arzaaqe u Khayr roone.'),
(784, 5, 115, 'Wuxuuna Yidhi Eebe waan ku soo Dejiin Korkiinna ee Ciddii ka Gaalowda ka dib oo idin ka mid ah waxaan Cadaabi Cadaab Aanan Cadaabayn Ruux Caalamka ka mid ah.'),
(785, 5, 116, '(Xusuuso) Markuu Yidhi Eebe, Ciise Binu Maryamow Ma adaa ku Yidhi Dadka ka Yeesha ani iyo Hooyaday Ilaahyo Eebe ka sokow, uuna Yidhi waad Nasahantahay Iguma Haboona inaan Dhaho Waxaanan Xaq u lahayn, Haddaan idhina waad ogtahay, waxaadna Ogtahay waxa Naftayda ku sugan Anuguse kuma Ogi Adiga unbaa ah kan waxa Maqan Og.'),
(786, 5, 117, 'Kuma Dhihin waxaad i fartay waxaan ahayn, oo ah Caabuda Eeba hay iyo Eebihiin waxaana ku ahay marag intaan ku Noolahay Dhexdooda markaad i oofsatana adaa ILaaliye u ah adi Umbaana wax walba Marag u ah.'),
(787, 5, 118, 'Haddaad Cadaabto waa uun addoomaadii, Haddaad u Dhaaftana adi umbaa Adkaade falsan ah.'),
(788, 5, 119, 'Wuxuu Yidhi Eebe Kani (Maanta) waa Maalinta ay Anfici kuwa Runta Sheega Runtoodu, waxaana u Sugnaaday Jannooyin oy Socoto Dhexdeeda Wabiyaal iyagoo ku Waari Dhexdeeda Waligood waxaana ka Raalli Noqday Eebe Iyana way ka Raalli Noqdeen, Taasina waa Liibaanta wayn.'),
(789, 5, 120, 'Eeba Iska leh Xukunka Samooyinka iyo Dhulka iyo waxa ku Dhex sugan Eebana wax kasta (uu Doono) wuu Karaa.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Mahad Eebaa iska leh ahna kan Abuuray Samooyinka iyo Dhulka, Yeelayna Mugdiya iyo Nuurka, Kuwa Gallobayse Eebahood yey wax la simi (la Caabudi)'),
(791, 6, 2, 'Eebe waa kan idinka Abuuray Dhoobo Markaas Xukumay Ajal (Muddo) Ajal Magacaaban Yaana Agtiisa ah, idinkuse Gaalooy waad Shakisantihiin.'),
(792, 6, 3, 'Eebe waa ILaaha Samooyinka waxa ku Sugan iyo waxa Dhulka ku Sugan wuxuuna Ogyahay Qarsoodigiinna iyo Muujiskiinna, wuxuuna Ogyahay, waxaad kasbanaysaan.'),
(793, 6, 4, 'Aayaddii u Timaadda oo Aayadaha Eebahaa ka mid ah way ka Jeedsadaan.'),
(794, 6, 5, 'Waxayna Dhab u Beeniyeen Xaqii Markuu u yimid Waxaase u Imaan Doona (Cidhibta) warkay ku Jees jeesayeen.'),
(795, 6, 6, 'Miyeyna Arkayn intaan Halaagnay Hortood (wax badan) oo Qarniyo ah, oon ku Makaninay (siinay) Dhulka waxaanaan idin Makanin (Siinin) waxaan uga soo deeynay Samada (Roob) Darar ah, Waxaana ka Yeelay Wabiyada kuwo Socda Dhexdooda waanse Halaagnay Dambigooda Dartiis waxaana Ahaysiinay Gadaashood Qarni Kale.'),
(796, 6, 7, 'Haddaan kugu soo Dejino Nabiyow Kitaab Qoran oo ay ku Taabtaan Gaemahooda waxay odhan kuwii Gaaloobay kani waxaan Sixir Cad aan ahayn ma aha.'),
(797, 6, 8, 'Waxayna Dhihi maa lagu so Dejiyo Malag, Haddaan soo Dejinno Malagna waxaa la Dhamaynlahaa Amarka (Halaagga) lamana Sugeen.'),
(798, 6, 9, 'Haddaan ka Yeello Malagna waxaan ka Yeeli lahayn Nin, Markaasaan Iskaga Dari lahaydeen Xaalka siday isaga Dari Jireen.'),
(799, 6, 10, 'Waxaa Dhab ahaan loogu Jeesjeesay Rasuullo kaa Horreeyey Waxaana kuwii Jees jeesay ku dhacay (Ciqaabti) waxay ku Jees jeeseen.'),
(800, 6, 11, 'Waxaad Dhahdaa ku Socda Dhulka oo Eega Siday Noqotay Cidhibtii Beeniyeyaasha Xaqa.'),
(801, 6, 12, 'Waxaad Dhahdaa yaa leh waxa ku Sugan Samooyinka iyo Dhulka, Dheh Eebe, wuxuuna Isasaaray Naxariis, wuxuuna idin kulmin Maalinta Qiyaame Shakina ma lch, kuwa Khasaariyey Naftoodase ma Rumaynayaan.'),
(802, 6, 13, 'Eebe waxaa u Sugnaaday waxa Xasila Habeenkii iyo Maalintiiba waana Maqle Oge ah.'),
(803, 6, 14, 'Waxaad Dhahdaa ma wax Eebe ka soo Hadhay yaan ka yeelan wali (la Caabudo) Eebaana ah kan Abuuray Samooyinka iyo Dhulka Isagaana wax Quudiya Lamase quudiyo, waxaad Dhahdaa waxaa lay Faray inaan Noqdo kan ugu Horreeya Cid Islaanta (Hogaansanta Ummaddan) oonan ka Midnoqoninna Mushrikiinta.'),
(804, 6, 15, 'Waxaad dhahdaa anigu waxaan ka Cabsan Haddaan Caasiyo Eebehay Cadaab Maalin wayn.'),
(805, 6, 16, 'Ciuddii laga lilo Cadaabka Maalintaas wuu u Naxariistay Eebe Taasina waa Liibaanta Cad.'),
(806, 6, 17, 'Hadduu ku Taabsiiyo Eebe Dhib wax Faydi ma jiro Isaga mooyee Hadduu ku Taabsiiyo Khayrna Isagaa wax Walba kara.'),
(807, 6, 18, 'Waana ka Adkaadaha Addoomadiisa Korkooda waana Falsame Xeel Dheer.'),
(808, 6, 19, 'Waxaad Dhahdaa yaa ugu Wayn Marag waxaad Dhahdaa Eebaa Maragga Dhexdeena ah, waxaana lay waxyooday Quraankan inaan idiinku Digo Idinka iyo cidduu Gaadho, Idinku ma waxaad Marag Furaysaan Inay Eebe la Jiraan ILaahyo kale, waxaad Dhahdaa ma Marag Furayo (Saas) Waxaad Dhahdaa Eebe waa uun ILaah kaliya Anuguna waxaan ka Bari ahay waxaad la Wadaajinaysaan.'),
(809, 6, 20, 'Kuwaan Siinnay Kitaabka Waxay u Yaqaannaan Nabiga (Xaqnimadiisa) Siday u Yaqaanaan Caruurtooda kuwa Khasaariyey Naftoodana ma Rumeeyaan (Xaqa).'),
(810, 6, 21, 'Wax ka Dulmi Badanna ma jirto cid ku Abuuray Eebe Been ama beeniyey Aayaadkiisa mana Liibaanto Daalimiintu.'),
(811, 6, 22, 'Ka Warran Maalintaan Kulmineyno (Dadka) Dhammaan Markaas aan ku Dhahayno Kuwii la Wadaajiyey Aaway kuwaad la Wadaajiseen (Eebe Cibaadada) aadna ahaydeen kuwo Sheegan (Inay wax tari).'),
(812, 6, 23, 'Markaas Xujadoodii ma ahayn inay Dhahaan Eebaan ku Dhaarannaye Maanaan ahayn Mushrikiin (Wax la wadaajiya).'),
(813, 6, 24, 'Bal day say ugu beensheegeen Naftooda ugana Dhumay waxay Been Abuuran jireen.'),
(814, 6, 25, 'Waxaa ka mida kuwo ku Dhagaysan waxaana yeellay Quluublooda Dabool inay kasaan (Quraanka) Dhagahoodana waxaan Yeellay Culays, Hadday Arkaan Aayaad kasta ma Rumaynayaan, Markay kuu Yimaaddaanna way kula Murmaan iyagoo dhihi kuwii Gaaloobay kani (Quraanku) ma aha waxaan warkii Kuwii Hore ahayn.'),
(815, 6, 26, 'Waxayna ka Reebi Dadka rumaynta Nabiga iyaguna way ka Fogaan mana halaagayaan waxaan Naftooda ahayn, mase Oga.'),
(816, 6, 27, 'Haddaad aragto Marka la Joojin Gaalada Naarta Korkeeda oy Dhihi Shallaytee Maa nala Celiyo oo aanaan Beenin Aayaadka Eebahanno oo Noqonno Kuwa Rumeeyey (waxaad arki lahayd arrin Wayn).'),
(817, 6, 28, 'Waxaase U Muuqday waxay Qarinayeen horay haddii la Celiyana waxay ku Noqon lahaayeen wixii laga Reebay waana Beenaalayaal.'),
(818, 6, 29, 'Waxay dhihi wax kale ma jiro ee waannu ku Noolaan Adduunyada Nalamana soo Bixinaayo.'),
(819, 6, 30, 'Haddaad Aragto Marka la hor Joojiyo Eebahood oo ku Dhaho Miyuuna kan Xaq ahayn, oy Dhahaan waa Xaq Eebaan ku Dhaaranaye oo uu ku dhaho Dhadhamiya Cadaabka Gaalnimadiinna Darteed (Waxaad arki arrin wayn).'),
(820, 6, 31, 'Waxaa Dhab u Khasaaray Kuwa Beeniyey la Kulanka Eebe markay ugu Timaaddo Saacadda (Qiyaame) si kada ahna waxay Dhahaan Shallaytadanno waxaan ku Gabood fallay Dhexdeeda, waxayna ku Xambaari Dambigooda Dhabarkooda waxaana Xumaanbadan waxay Xambaari.'),
(821, 6, 32, 'Nolosha Adduunyana ma aha waxaan Ciyaar iyo Madadaalo ahayn Guriga Aakharaana u Khayr roon kuwa Dhawrsan ee Miyeyydaan wax kasayn.'),
(822, 6, 33, 'Waxaan Ognahay inuu ku Walbahaarin waxay Sheegi Dhab ahaan kuuma Beeninayaan Laakiin Daalimiintu Aayaadka Eebe yey Diidi.'),
(823, 6, 34, 'Waxaa Dhab loo Beeniyey Rasuullo kaa Horreeyey waana ku Samreen wixii lagu Beeniyey waana la Dhibay intuu uga Yimaaddo Gargaarkannagu wax bedeli Hadalka Eebana ma jiro, waxaana ku soo Gaadhay Warkii Rasuuladii.'),
(824, 6, 35, 'Hadday kugu Wanaatay (Cuslaatay) Jeedsigoodu haddaad karto inaad god ka Teelato Dhulka ama Sallaan Samada ood Aayad u keentid (Samee) Hadduu Doono Eebana wuxuu ku kulmin lahaa hanuunka ee ha ka mid noqonin Jaashiliinta.'),
(825, 6, 36, 'Waxaa uun Ajiibi Ogolaan Xaqa kuwa Maqli, kuwa Dhintayse waxaa soo Bixin Eebe Xaggiisaana loo Celin.'),
(826, 6, 37, 'Waxay Dhaheen maxaa loogu soo Dejin waayey «Mucjisad» Aayad Xagga Eebihiis, waxaad Dhahdaa Eebe waa karaa inuu soo Dejiyo Aayad Badankoodse ma Oga.'),
(827, 6, 38, 'Wax ka Socda Dhulka ama Shimbir ku Duuli Garbihiisa oo Dhan waa umado Idinla mid ah, waxba kagama aanan tagin Kitaabka, waxaana loo soo Kulmin Dadka Xagga Eebahood.'),
(828, 6, 39, 'Kuwa Beeniyey Aayadkanaga waa DhagaLa\'yihiin oo Af la`yihiin Mugdibayna ku Suganyihiin, cidduu Doono wuu Dhumiyaa Eebe Tuu Doonana wuxuu Yeela Jid Toosan.'),
(829, 6, 40, 'Waxaad Dhahdaa Bal ka warrama hadduu idiin Yimaaddo Cadaabka Eebe ama idiinTimaadda Saacaddu (Qiyaame) wax Eebe kasoo hadhay Miyaad Baryeysaan haddaad Runsheegaysaan.'),
(830, 6, 41, 'Isaga Unbaana Baryeysaan oo Faydi waxaad ka baryeysaan hadduu Doono waadna Halmaamaysaan waxaad la Wadaajinaysaan.'),
(831, 6, 42, 'Waxaan u Dirray (Rasuul) Ummado idinka Horreeyey waxaana ku gabanay Dhibaato iyo Cudur intay Baryaan (Eebe).'),
(832, 6, 43, 'Maa Markuu u Yimaaddo Dhibku ay baryaan (Eebe) Waxaase ingagtay Quluubtoodii wuxuuna u Qurxiyey Shaydaan waxay Falayeen.'),
(833, 6, 44, 'Markay Halmaameen (Ka Jeedsadeen) wixii lagu Waaniyey Yaan u Furray Korkooda irridaha wax Kasta Markay ku Farxaan waxaa la Siiyey yaan ku qabannaa kado, Markaasay ahaadaan kuwo Quusta.'),
(834, 6, 45, 'Waxaana la Gooyey Cidhibta Qoomkii Dulmiga Falay Mahadna Eebaa iska leh ah Eebaha Caalamka.'),
(835, 6, 46, 'Waxaad Dhahdaa Bal Warrama Hadduu Eebe qabto Maqalkiinna iyo Araggiinna oo Daboolo Quluubtiinna waa ILaahee ka soo Hadhay Eebe ee idiin keeni, Day Sedan u Cel-Celinno Aayaadka Markay say u Jeedsan.'),
(836, 6, 47, 'Waxaad Dhahdaa Bal Warrama hadduu Idiinku Yimaaddo Cadaabka Eebe Kado ama Caddaan miyaa la Halaagaa waxaan Qoomka Daalimiinta Ahayn.'),
(837, 6, 48, 'Rasuulada waxaan u Dirraa inay Bishaareeyaan oo Digaan, Ciddii Rumaysa Xaqa oo Wanaajiya (Camalka) Cabsi iyo Murug midna ma Saarro.'),
(838, 6, 49, 'Kuwa Beeniyey Aayaadkanaga waxaa Taaban Cadaab Faasiq Nimadooda Darteed.'),
(839, 6, 50, 'Waxaad Dhahdaa Ma Dhahayo waxaa Agtayda ah Khasnadaha Eebe, waxy Maqanna ma Ogi, mana Dhahayo waxaan ahay Malag, waxaan uun Raaci wixii lay waxyoodo wwaxaad dhahdaa ma egyihiin Indhoole iyo Arke Miyeydaan Fikirayn.'),
(840, 6, 51, 'Ugu dig Quraanka kuwa ka Cabsan in Ioo soo kulmiyo Eebahood iyagoon lahayn Sokaydiis Qaraabo iyo Shafeece Midna inay Dhawrsadaan Darteed.'),
(841, 6, 52, 'Hana Eryin kuwa Baryi Eebahood aroor iyo Galabba iyagoo dooni Wajigiisa waxba kaama saarra Xisaabtooda iyana Xisaabtaada Waxba kama Saarra ood eridid ood ka mid noqotid daalimiinta.'),
(842, 6, 53, 'Saasaaan isugu Imtixaanay (Dadka) Qaarkood Qaarka kale inay Dhahaan ma kuwanaa Eebe ku Manaystay (Islaamka) Dhexdannada, Miyuusan Eebe ahayn kan og kuwa ku Mahdiya.'),
(843, 6, 54, 'Markay kuu Yimaadaan kuwa Rumeeyey Aayaadkanaga waxaad Dhahdaa Nabadgalyo Korkiinna ha ahaato, wuxuu Eebe Isasaaray Naxariis, oo ah Ciddii idinka mid ah oo Fasha Xumaan isagoo ka Jaahil ah oo Toobad keena nn wanaajiya Eebaa Dhaafi una Naxariisan.'),
(844, 6, 55, 'Saasaanu u Kala Bixinaa Aayaadka si ay u Caddaato Jidka Dambiilayaasha.'),
(845, 6, 56, 'Waxaad Dhahdaa waxaa layga Reebay inaan Caabudo waxaad Caabudaysaan oo Eebe ka soo Hadhay, waxaad Dhahdaa Raaci maayo hawadiinna oon Dhumo markaas Nogoninna kuwaan Hanuunsanayn.'),
(846, 6, 57, 'Waxaad Dhahdaa waxaan ku Sugnahay Xujo Eebahay waadna Beeniseen Xaqaas, agtayda ma aha waxaad Dadajisanaysaan, Xukunkana Eebaa iska leh, Xaquuna Xukumi, waana kuu Xukunkiisu Khayr Leeyahay.'),
(847, 6, 58, 'Waxaad Dhahdaa hadduu Yahay agtayda waxaad Dedejisanaysaan (Cigabta) waxaa Ia kala Xukumi Lahaa Dhexdeenna Eebana waa Ogyahay Daalimiinta.'),
(848, 6, 59, 'Eebe Agtiisa waxaa ah Furaha waxa maqan wax og oon isaga ahayna ma jiro, wuxuuna Ogyahay waxa Barriga iyo Badda ku Sugan, wixii Caleena oo Dhacdana wuu Ogyahay, Xabbad Masaga ah oo Mugdiga Dhulka ku Sugan iyo wixii Qoyan ama Ingagan waxay ku Suganyihiin Kitaab Cad.'),
(849, 6, 60, 'Eebe waa kan idin Oofsada Habeenkii ogna waxaad Fashaan Maalintii Markaasna idin soo Bixiya in la Xukumo Ajal Magacaaban Xaggiisaana laydiin Celin, markaasuu idiinka warami waxaad Falaysaan.'),
(850, 6, 61, 'Eebe waa kan ka Sarreeya Addoomadiisa, wuxuuna idiin soo Diraa ILaaliye markuu u Yimaaddo Midkiin Mawdku waxaa Oofsata «Disha» Malaa\'igtanada mana Gabood Falaan.'),
(851, 6, 62, 'Markaasa loo Celin Dadka Xagga Sayidkooda Xaqa ah isagaana leh Xukunka Dedejiyana Xisaabta.'),
(852, 6, 63, 'Waxaad Dhahdaa Yaa idinka koriya Mugdiga Barriga iyo Badda ood u Baridaan Isdullayn iyo Qarsoodi, Idinkoo dhihi Hadduu naga koriyo Tan waxaan ka mid Noqonaynaa kuwa Mahadiya.'),
(853, 6, 64, 'Waxaad Dhahdaa Eebaa idinka Koriya Taas iyo Shiddo Kasta, Markaasaad u Shariigyeeshaan.'),
(854, 6, 65, 'Waxaad Dhahdaa Eebe waa kan kara inuu idinku soo Daayo Cadaab Korkiinna ama Lugihiinna Hoostooda ama idin qaso (idinka yeelo) Kooxo uu Dhadhansiiyo Qaarkood qaarka kale Dhibkiisa, bal day Sidaan ugu Caddaynaynno Aayaadka inay Kasaan.'),
(855, 6, 66, 'Waxaa Beeniyey (Quraanka) Qoomkaagii Isagoo Xaq ah, waxaad Dhahdaa kama ahi Korkiinna Wakiil (ILaaliye).'),
(856, 6, 67, 'War kasta Meel uu ku Sugnaado yuu leeyahay Waadna Ogaandoontaan.'),
(857, 6, 68, 'Markaad Aragtaan kuwa dhumban Aayaadkanaga, isaga Jeedso intay ka Dhumbadaan Sheeko kale, haddduu ku Halmaansiiyo Shaydaanna ha Fadhiyin Xusuusta ka Dib Daalimiinta Agtooda.'),
(858, 6, 69, 'Kuwa Dhawrsoonna Xisaabtooda waxba kaama Saarra Laakiin waa Waano inay Dhawrsadaan.'),
(859, 6, 70, 'Iskaga tag Kuwa ka Yeeshay Diintooda Ciyaar iyo Madaddaalo kuna Kadsoomay Nolosha Dhaw Xusuusina intaan laga Abaalmarinin (Loa Xabisin) Nafi waxay kasbatay lyadoon Lahayn Eebe ka sokow Wali «Sokeeye» iyo Shafeece midna. Hadday isku Furato Furasho oo Dhanna lagama Qaado, Kuwaasu waa kuwa loo Dhiibay (Ilaag) waxay kasbadeen Dartiis, waxayna Mudan Cabbid KuluI iyo Cadaab daran, Gaalnimadooda Darteed.'),
(860, 6, 71, 'Waxaad Dhahdaa ma Caabudaynaa Eebe ka sokow waxaan na Anfacayn nana Dhibayn oo Dib naloo Celin (Gaalnimo) inta Eebe Hanuuniyey ka Dib, Sida kan ay ku Wareerisay (Dhumisay) Shayaadiintu Dhulka Isagoo Saaxiibbo ugu Yeedhi Hanuunka leh oo ku Leh noo Kaalay, waxaad Dhahdaa Hanuunka Eebe unbaa Hanuun ah, waxaana Nala Faray inaan u Hogaansanno Eebaha Caalamka'),
(861, 6, 72, 'Oogana Salaadda kana Dhawrsada Eebe waa kan Xaggiisa laydiin soo Kulmine.'),
(862, 6, 73, 'Waana kan Eebe u Abuuray Samooyinka iyo Dhulka Xaq Maalinta uu Dhihi Ahow wuu ahaan (waxaasu) Hadalkiisu waa Xaq, wuxuuna Leeyahay Xukunka Maalinta la Afuufi Suurka, waana Ogyahay wax Maqan (la arkayn) iyo waxa Joogaba, waana Falsame Xeel Dheer.'),
(863, 6, 74, 'Ka Warran markuu ku Yidhi (Nabi) Ibraahim Aabihiis Aazara ma waxaad ka Yeelan Sanamyo ILaahyo, waxaan kugu Arkaa adiga iyo Qoomkaagaba Baadi Cad.'),
(864, 6, 75, 'Saasaan u Tusinnay Ibraahim Xukunka Samooyinka iyo Dhulka inuu ka Mid Noqdo kuwa Yaqiiniya (Xaqa).'),
(865, 6, 76, 'Markuu ku Maddobaaday Habeenku wuxuu Arkay Xiddig Wuxuuna Yidhi kani ma Eebahaybaa, markuu Dhacay wuxuu Yidhi ma Jedi kuwa Dhaca.'),
(866, 6, 77, 'Markuu Arkay Dayaxa oo soo Muuqda wuxuu yidhi kani ma Eebahaybaa markuu Dhacay wuxuu Yidhi Hadduusan I hanuunin Eebahay waxaan ka mid Noqon kuwa Dhuma.'),
(867, 6, 78, 'Markuu Arkay Qorraxda oo soo Muuqata wuxuu Yidhi kani ma Eebahaybaa kanaa wayne, Markay Dhaeday wuxuu Yidhi Qoomkayow Bari baan ka ahay waxaad la wadaajinaysaan (Eebe).'),
(868, 6, 79, 'Anugu waxaan u Jeediyey Wajigayga Eebaha Abuuray Samooyinka iyo Dhulka anoo Toosan oon ka mid ahayn Gaalada.'),
(869, 6, 80, 'Way Ladoodeen Nabi Ibraahim Qoomkiisii, wuxuuna yidhi ma waxaad igula Doodaysaan Eebe, Isagoo I hanuuniyey, oonan ka Cabsanayn waxaad la Wadaajinaysaan, in Eebahay wax doono Mooyee, Eebahay waa u Waasac (Awoodleeyahay) wax kasto Ogaansho, niyeydaan Xusuusanayn (wax).'),
(870, 6, 81, 'Sideen uga Cabsan waxaad la Wadaajiseen, idinkuna aydaan ka Cabsanayn inaad la Wadaajiseen Eebe wuxuusan u soo Dejinen Xujo, ee Labada Kooxood (Muslimka iyo Gaalada) koodee mudan Nabdagalyo Haddaad wax Ogtihiin.'),
(871, 6, 82, 'Kuwa Xaqa Rumeeyey oon ku Dheehin Iimaankooda Dulmi (Gaalimo) kuwaasaa Nabadgalto u Sugnaatay Hanuunsanna.'),
(872, 6, 83, 'Taasina waa Xujadanadii aa u siinnay Ibraahim Qoomkiisa waxaan koryeelaa Darajaadka Ciddaan Doonno Eebahaana waa Falsame Oge ah.'),
(873, 6, 84, 'Waxaan siinay Isxaaq iyo Yacquub Dhammaana waan Hanuuninay, Nabi Nuuxna waan Hanuuninay waqti Hore, waxaana ka mid ah Faraciisii Nabi Daawuud, Sulaymaan, Ayuub, Yuusuf, Muuse iyo Haaruun, Saasaana ku Abaal marinaa Samofalayaasha.'),
(874, 6, 85, 'Iyo Zakariye, Yaxye, Ciise ILYaas, Dhammaana waxay ahaayeen kuwo Suubban,'),
(875, 6, 86, 'Iyo Ismaaeiil Alyasac, Yuunus, iyo Luudh, Dhammaana waxaad ka Fadilnay Caalamka.'),
(876, 6, 87, 'Iyo Aabayaalkaod iyo Faracoodii iyo Walaalahood waana Dooranny waana ku Hanuuninay Jidka Toosan.'),
(877, 6, 88, 'Kaasi waa Hanuunki Eebc wuxuulla ku Hanuuniyai Cidduu Doono oo ka mid ah Addoomadiisa hadday wax La wadaajiyaari Eebana waxaa ka Bun waxay Fali Jireati.'),
(878, 6, 89, 'Kuwaasu ‘awn kuwaan siinay Kitaabka, Xukunkniiyo Nabixinuada, fladday ka Gaaloabaanna Kuwaasu aCjaalada Makaads waxaan a a’akaalanayQoom ann ka (Jaaloobayn, (Muslimiinta).'),
(879, 6, 90, 'Kuwuas (Nabiyada) waeye kuwu Eebe hasiuusniyey ee Hanuunkouda kit dayo. waxaadssa Dhahdaa ldirna warsananyc LJjuuro, eewaauunwaanada caalsmka'),
(880, 6, 91, 'Mayna Waynaynin kebe Waynaantiiia Xaqa ah, Mackay Dhw been Kuma son Dejin hebe Dad waxba, waxtad t}hahdaa yaa 500 Dejiyey Kitaahkii uu Iayimid Nahi M anne Isagoo ftiin Hanuunna u ab Dadka ood YeeIaysecn wurqado and Muujisaan. nodsin qurisuan wan Budun, waxawna laydin Baray waxaydaan Aqoonin idinku iya Aubaynnlkiin, waxaud Dhuhdau Eebaa (sun Dejiyey) markaus saga tag Dhumbaahudusnda lyagon Ciyaari'),
(881, 6, 92, 'Kanna wan Kitaub aan soo Dejinay oo Barakaysan, oo Runiayn wixii ka Horreeyey iyo inaad ugu Digtid Maka ye Dhinaeyadeeda. kuwa Rumayn Aakhira waxay Rumeyn Quraanka salaaddana way ILaalin.'),
(882, 6, 93, 'Yaa ka Dambi Radon Cid ku Been Abuuratay Eaba ama Yidhi waa Lay Wanyaoday Isagoon Ion Waxyoonin Waxba. iyo Ckldii Dhahda waxaan son Dejin waxa Eebe soo Dejiyey no kale. Haddaad Araglo markay Daalmiintu Sakaraadka Geerida ko Suganyihiin Malaa\'igtuna ku E)hihi Iyagoo ku Taogi Gacmaha Bixiya Naftiina Maanta waxaa laydinku Ahaal maria Cadaab Dulli ah waxaad ku Dhahayseen Eebe Xaqdarro ood Ahaydeen kuwu Iska Wayneeya Aayadkiisa (Waxaad as-ki lahayd wax Wayn).'),
(883, 6, 94, 'Waxaad Noogu Timaaddeen Kali Kali Sidaon idiinku Abuuray Marka hoc waxaadna kaga Timaaddeen waxaan idinku Nicmaynay Gadaashiina mana ku Aragno Agtiina Shafeecayaalkiinnii and Sheegayseen slay Shuruko Yihiia. waxaana Go\'ay Xidhiidhkiinnii waxaana Dhumay waxuad Sheeganeyseen.'),
(884, 6, 95, 'Eebaa Jeexa Xabbadda Midhaha ah, iyo Timirta, wuxuuna ka soo Bixiyaa wax Nool wax dhimaad ah, kana soo Bixiyaa wax Dhimaad ah wax Nool, Kaasi waa Eebe ee Xaggee laydiinka lili (Xaqa).'),
(885, 6, 96, 'Eebe waa kan Abuuray Iftiinka kana Yeelay Habeenka Xasil, Qorraxda iyo Dayaxana Xisaab, Taasina waa Maamulka Eebe Adkaada ee og.'),
(886, 6, 97, 'Eebe waa kati idiin Yeelay Xiddigo inaad ku Toostaan Mugdiga Barriga iyo Badda, waxaan u Caddaynay Aayaadka Ciddii Garan.'),
(887, 6, 98, 'Eebe waa kan Idinka Abuuray Naf kaliya (Nabi Aadam) ldiinna Yeelay Sugnaansho iyo Hoyosho, waan u Caddaynay Aayaadka Ciddi wax kasi.'),
(888, 6, 99, 'Eebe waa kan ka soo Dejiya Samada Biyo kuna soo Bixinno Doog Cagaaran oon ka soo Saarro Midho kala Koreeya, Timirtana Fiiddeeda ah Laamo Dhow, Beerana (waan ku so Bixinaa) oo ah Cinab, Zaytuun, Rummaan (qaar) Isu eg, iyo Qaar aan Isu Ekayn, Daya Midhihiisa markuu Midho Dhalo iyo Bisaylkiisa Taasina Calaamaa ugu Sugan Ciddii wax Rumayn.'),
(889, 6, 100, 'Waxay u Yeeleen Gaalado Eebe Shurako Jinni ah Isagoo Abuuray waxay ku Sheegeen(Been) Eebe Wiilal iyo Gabdho Iyagoon wax Ogayn, waa ka Nasahanyahay Eebe waana ka Sarreeyaa waxay ku Sheegi.'),
(890, 6, 101, 'Eebe waa Kan Abuuray Samooyinka iyo Dhulka sideese Ilmo ugu ahaan Isagoon Haweenay lahayn wax walbana Abuuray wax kastana Og.'),
(891, 6, 102, 'Kaasi waa Eebihiin isaga mooyee ILaah kale ma jiro wax kastana isagaa Abuuray ee Caabuda wax walbana isagaa u Wakiil ah (ILaaliya).'),
(892, 6, 103, 'Ma haleesho Aragti Isagaase Haleela Aragtida (Wuu arkaa Eebe lamase Arko adduunka) Waana wax Oge Xeeldheer.'),
(893, 6, 104, 'Waxaa idiin Yimid (Idin soo Gaadhay) Xujooyin Eebihiin ka yimid Ciddii Aragta (ku Hanuunta) Naftiisa umbuu wax u taray Cidii ka Indho Beeshana Naftiisuu Dhibay, anna ma ihi mid idin ILaalin.'),
(894, 6, 105, 'Saasaannu u gagadinnaa (Caddaynaa) Aayaadka inay Dhahaan waad Akhrisatay iyo Inaan u Caddayno Ciddii wax Garan.'),
(895, 6, 106, 'Raac nabiyow waxa lagaaga Waxyooday Xagga Eebahaa ILaahay mooyee Eebe kalana ma jiro kana Jeedso Mushrikiinta.'),
(896, 6, 107, 'Hadduu doono Eebe mayna la Wadaajiyeen kaagamana aanaan Yeelin Ilaaliye Wakiilnakama tihid.'),
(897, 6, 108, 'Ha Caayin waxay Caabudi oo Eebe ka soo Hadhay oo Markaas iyana Caayaan Eebe Colnimo iyo Cilmi la\'aan, saasaan ugu Qurxinay ummad kasto Camalkeeda Xagga Eebe Umbayna Noqoshadoodu tahay wuxuuna uga Warrami waxay Fali Jireen.'),
(898, 6, 109, 'Waxay ku Dhaarteen Gaaladii Dhaar Adag, Hadday soo Gaadho Aayadi inay Rumaynayaan, waxaad Dhahdaa Aayaadku waxay Jiraan Eebe Agtiisa, Miyeyse Ogyihiin Hadday Aayaddu soo Gaadho inayna Rumaynayn.'),
(899, 6, 110, 'Waxaannu Gaddin Quluubtooda iyo Araggooda Sidayna u rumayn, Markii horaba waxaana ku Daynanaa Iyagoo ku Wareersan Xadguddybkooda.'),
(900, 6, 111, 'Haddaan ku soo Dejinno Malaa\'ig oo Dadkii Dhintay la Hadlo oon wax kasta Hortooda ku soo Kulminno, mayna rumeeyeen in Eebe doono mooyee, Hase Yeeshee Badankood waa Jaahiliin.'),
(901, 6, 112, 'Saasaan ugu Yeellay Nabi Kasta Col Shaydaan Insi iyo Jinniba leh, oo Qarkood qaarka kale u Waxyoon (Sheegi) Hadal la Dhoob-Dhoobay Dhumin Darteed. Hadduu Eebe Doonana mayna Faleen ee Iskaga Tag iyaga iyo waxay Been-Abuuran.'),
(902, 6, 113, 'Iyo in ay Maqasho Quluubta Kuwaan Rumayn Aakhiro, iyo Inay ka Raalli Noqdaan, iyo inay Kashadaan waxay Guranayeen.'),
(903, 6, 114, 'Ma Diinta Eebe waxaan ahayn yaan u Dooni Xaakim Isagoo idiin soo Dejiyey Kitaabka oo la Caddeeyey kuwaan Siinay Kitaabka waxay Ogyihiin in Quraanka laga soo Dejiyey Xagga Eebahaa ee ha Noqonin kuwa Shakiya.'),
(904, 6, 115, 'Waxay ku Taam Noqotay Kalimooyinka Eebahaa run iyo Caddaalad wax Badali Kalimooyinka Eebana ma jiro, Eebana waa Maqle og.'),
(905, 6, 116, 'Haddaad Maqashid waxa Dhulka ku Sugan Badankooda waxay kaa Dhumin Jidka Eebe, mana Raacayaan waxaan Malo ahayn waana Beenaalayaal.'),
(906, 6, 117, 'Eebahaa yaa og Cidda ka Dhumi Jidkiisa, Isagaan og Cidda Hanuunsan.'),
(907, 6, 118, 'Ee Cuna waxa Magaca Eebe lagu Xusay Haddaad Aayadkiisa Rumaynasaan.'),
(908, 6, 119, 'Maxaad la cuni waydeen waxa Magaca Eebe lagu Xusi, Isagoo laydiin Caddeeyey waxa Laydinka Reebay inaad Dhibbantihiin Mooyee, wax Badan waxay ku Dhumin (Dadka) Hawadooda Cilmi darro, Eebahaana waa Ogyahay kuwa Xad-gudba.'),
(909, 6, 120, 'Ka Taga Dambiga Muuqda iyo Kan Qarsoonba, kuwa Jasbada Dambigana waxaa laga Abaal marin waxay Kasbanayeen.'),
(910, 6, 121, 'Ha cunina waxaan lagu Xusin Magaca Eebe, illeen waa Xumaane, Shaydaamaduna waxbay u Sheegaan Saaxiibkooda inay idinla murmaan, Haddaad Maqashaanna waxaad Noqonaysaan Gaalo.'),
(911, 6, 122, 'Cid mayd ahayd oon noolaynay oon u Yeellay Nuur uu ku Dhex socdo Dadka ma lamidbaa mid Mugdi ku Sugan oo ka baxayn, Saasaana loogu Qurxiyey Gaalada waxay Falayeen.'),
(912, 6, 123, 'Saasaan ugu Yeellay Magaalo kasto Dambilayaal waawayn, inay wax ku Dhagraan Dhexdeeda, waxaan Naftooda ahayna ma Dhagrayaan masc oge.'),
(913, 6, 124, 'Markay soo Gaadho aayad waxay Dhahaan Rumayn maynno inta nalooga keeno wixii la siiyey Rasuuladii Eebe Ilaahay baana og Meejuu Yeeli Rasuulnimada waxaana ku Dhiei kuwii Dambaabay Dulli Eebe Agtiisa ah iyo Cadaab Daran waxay Dhagrayeen Darteed.'),
(914, 6, 125, 'Cidduu Eebe Hanuuniyo wuxuu u Waasieiyaa Laabtiisa Islaamka, Cidduu Doono inuu Dhumiyana wuxuu ka sidii Isagoo kori Samada saasuuna u Yeelay Eebe Xumaanta korka Kuwaan Rumaynin Xaqa.'),
(915, 6, 126, 'Wakaas Jidkii Eebahaa oo Toosan Aayaadkana waxaan u Caddaynay Ciddii wax Xusuusan.'),
(916, 6, 127, 'Waxayna Mudanyihiin Guri Nabadgalyo oo Eebahood Agtiisa ah Isagaana Gargaarahooda ah waxay Camal falayeen Dartiis.'),
(917, 6, 128, 'Xusuuso Maalintuu Eebe Kulmin Dhammaantood (kuna Dhihi) Kulanka Jinniyow waad badsateen (Baadiyeynta) Insiga waxayna Dhehaan Saaxiibadood oo Insiga ah Eebahannow Qaarkanno qaarbuu ku shaqaystay waxaana Gaadhnay Ajashaad (Mudadaad) noo Samaysay, wuxuuna ku yidhi Naarbaa Iloyaadkiina ah idiinkoo ku Waari Dhexdeeda wuxuu Eebe doono Mooyee, Eebahaana waa Falsame Oge ah.'),
(918, 6, 129, 'Saasaana ugu Dirraa Daalimiinta Qaarkood Qaarka kale waxay Kasbanayeen Darteed.'),
(919, 6, 130, 'Jinni iyo Insiyow Miyuuna idiin Imaanin Rasuuloo Idinka mid ah oo iddinka Warrama Aayaadkayga idiinkana Diga la kulanka Maalintan waxayna Dheheen waan ku Marag furray Naftanada waxaana Dhagartay Nolosha Dhaw, waxayna ku Marag fureen Naftooda inay gaalo ahaayeen.'),
(920, 6, 131, 'Arrintaasna waa Inaan Eebahaa halaagin Magaalo Dulmi Dartiis iyadoo Dadkeedu halmaansanyihiin (aan loo digin).'),
(921, 6, 132, 'Cid kasta Darajooyin yey Leedahay oo waxay Camal fashay ah Eebahaana ma ah mid halmaansan waxay Camal Faleen.'),
(922, 6, 133, 'Eebhaaana waa Hodan Naxriis badan, Hadduu Doono wuu idin Baabi\'in idinkuna Bedali Gadaashiin Ciduu Doono siduu iddinka Abuuray Faracii Qoom kale.'),
(923, 6, 134, 'Waxa laydiin Yaboohi wuu Imaan Eebana ma daalisaan.'),
(924, 6, 135, 'Waxaad Dhahdaa Qommkayow ku Camal Fala Sidiinaas, anna waan Camal faliye, waxaadna Ogaan doontaan Cidday Cidhib Dambe u ahaatee mana Liibano Daalime.'),
(925, 6, 136, 'Waxay uga yeeleen Eebe wuxuu Abuuray oo Beero iyo Xoola ah qayb, waxayna Dheheen kan Eebaa iska leh Sheegashadooda kanna waxaa iska leh Shurukadanada (Sanamyda) waxa Sanamyadu ma Gaadho Eebe, waxa Eebana wuxuu Gaadhaa Sanamyadooda waxaa Xun waxay Xukumeen.'),
(926, 6, 137, 'Saasayna ugu Qurxiyeen in Badan oo Gaalada ah Dilidda Caruurtooda Shurukadoodu, (Shayaadiintu) inay Halaagaan kana Khaldaan Diintooda, hadduu Eebe Doonana mayna Faleen ee Isaga Tag iyaga iyo waxay Been Abuuran.'),
(927, 6, 138, 'Waxay Dheheen Tan waa Niemooleey iyo Beer Reeban oosan ka Quudanayn Cidaan Doonno Mooyee, Sheegashadooda, iyo Niemoolay la Xarrimay Dhabarkeeda (In la rarto) iyo Niemoolay aan Magaca Eebe lagu Xusayn, ku Been abuurasho Darteed wuxuu ka Abaal marin Eebe waxay Been Abuuranayeen.'),
(928, 6, 139, 'Waxay Dheheen Gaaladii waxa ku Jira Uurka Niemoolaydan waxay Gaar u tahay Raggannaga waxayna ka Reebantahay Haweenkannaga hadday Bakhti tahayna way wadaagi wuxuu ka Abaal marin Eebe Tilmaantooda, Illeen waa Falsame Oge.'),
(929, 6, 140, 'Waxaa Dhab u Khasaaray kuwa Dilay Caruurtooda Caqli Xumo Darteed iyo Cilmi la\'aan oo Reebay wixii Eebe ku Arzuqay Been abuurasho Eebe Darteed way Dhumeen mana Hanuunsana.'),
(930, 6, 141, 'Eebe waa kan soo Bixiyey Beero la Dhisay iyo Kuwaan Dhisnayn, iyo Timir, iyo Tallad kala Duwanyahay Cunidiisu, iyo Saytuun, iyo Rumaan isu eg iyo qaaraan isu ekeyn, ka Cuna Midhihiisa (Sakada) Maalinta la goyn, hana Xad Gudbina Eebe ma Jeela kuwa Cadgudbee.'),
(931, 6, 142, 'Niemoolayda waxaa ka mida mid la Rarto, iyo mid kuwa yar yara, ka Cuna waxa Eebe idinku Arzaaqay hana raacina Tallaabooyinka Shaydaanka Illen waa Colkiinna oo Cade.'),
(932, 6, 143, 'Waa Siddeed Nooc Labo Ido ah iyo Labo Riyo ah (Lab iyo Dhidig) ma Labada lab yuu Reebay Eebe Mise Labada Dhadig Mise waxa Uurka kuwa Dhadig ku Jira, noo Warrama cilmi haddaad Run Sheegaysaan.'),
(933, 6, 144, 'Geelana Labo ka mida Lo\'dana labo dheh ma labada Lab yuu Eebe reebay mise kuwa Dhadig mise waxa Uurka kuwa Dhadig ku Jira, mise Maragbaad ka ahaydeen markuu idin Faray Eebe Sidaas, Yaase ka Dulmi badan Cid ku Abuuratay Eebe, inuu Dhumiyo Dadka Cilmi Darro, Eebana ma hanuuniyo Qoom Daalimiina.'),
(934, 6, 145, 'Waxaad Dhahdaa kama helayo waxa laywaxyooday wax ka Reeban Cidda Cuni oon ahayn, Bakhti, Dhiig Butaaci, Hilib Doofaar, Illen waa Nijaase iyo wax Xun oo lagu Gawracay Magaca Eebe waxaan ahayn, Ruuxii Baahda Dulmi iyo cadgudub la\'aan, Eebahaa wuu u Dambi Dhaaf una Naxariisan (hadduu baahidaa u Cuno)'),
(935, 6, 146, 'Kuwa Yuhuudda ah waxaan ka reebnay wax kastoo Ciddi leh, Lo\'da iyo Adhigana waxaan ka reebnay Baruurtooda, waxay Dhabarka ku qaadaan (Baruurta Dhabarka) mooyee, ama Uur ku Jirta ama wixii ku Dheehan Laf, saasaana ku Abaal marinay Dulmigooda Dartiis, Runbaana Sheegaynaa.'),
(936, 6, 147, 'Hadday ku Beeniyaan waxaad dhahdaa Eebihiin war Naxariis badane lagamana Celiyo Ciqaabtiisa qoomka Dambiilayaasha ah.'),
(937, 6, 148, 'Waxay Odhan kuwii Gaaloobay Hadduu Eebe Doono, maanaan Gaalowneen Anaga iyo Aabayowganno, waxna maanaan Xarimneen saasayna u Beeniyeen kuwii ka horreeyey intay ka Dhadhamiyaan Ciqaabtanada, waxaad dhahdaa Agtiinna Cilmi mayahay aad noo soo Bixisaan (Muujisaan) waxaan malo ahayn ma Raacaysaan waxaadna uun tihiin Beenaalayaal,'),
(938, 6, 149, 'Waxaad Dhahdaa Eebaa iska leh Xujada Wayn, hadduu doonana wuu idin Hanuunin Dhammaan.'),
(939, 6, 150, 'Waxaad Dhadaa keena Markhaatgiina idiin Marag Furi in Eebe Reebay kan (aad Reebteen) hadday Marag Furaan ha marag furina la Jirkooda, hana raacina hawada kuwa Beeniyey aayaadkanaga, kuwaan Rumaynin Aakhiro Iyaguna Eebahood waxbay la simman.'),
(940, 6, 151, 'Waxaad Dhahdaa Kaalaya aan idiin Akhriyo waxa Eebe idinka reebaye, waa inaydaan wax la Wadaajin, Labada waalidna waa inaad u samo Fashaan (Oydaan Caasiyin) oydaan laynna Caruurtiinna Saboolnimo (Baahi) Anagaa idinka iyo Iyagaba Arzaaqi, hana u Dhawaanina Xumaanta teeda muuqata iyo Teeda Qarsoonba, hana dilina Naftii Eebe Reebay in Xaq darro lagu Dilo, saasuuna Eebe idin fari inaad wax kastaan.'),
(941, 6, 152, 'hana u Dhawaanina Xoolaha Agoonta si Fiican mooyee intay ka Gaadhaan Xoogooda, Dhammaystirana Miisidda iyo Miisaanka, si Caddaalada, laguma kalifo Nafi waxayna karin haddaad hadlaysaanna Caddaalad fala, Qaraababa ha ahaadee, oofiyana Ballanka Eebe, Sidaasaana Eebe idin faray inaad waantoowdaan.'),
(942, 6, 153, 'Kaasinawaa Jidkayga toosan ee Raaca, hana Raacina Wadiiqaha oo idin la kala Tago (Idinka leexiyo Jidka Eebe) Saasaana Eebe idin faray inaad Dhawrsataan.'),
(943, 6, 154, 'Waxaana Siinnay Nabi Muuse Kitaabkii (Towreed) Annagoo Dhammaystiri waxa wanaagsan, wax walbana Caddayn, Isagoo hanuun iyo Naxariis ah, inay la kulanka Eebahood Rumeeyaan.'),
(944, 6, 155, 'Kanna (Quraanku) waa Kitaab aan soo Dejinnay oo Barkaysan, ee Raaca Dhawrsadana in laydiin Naxariisto.'),
(945, 6, 156, 'Oydaan Dhihin waxaa uun Kitaab lagu soo Dejiyey Labadii qolo ee naga horreeyey, anaguna ma naqaanno Cilmigoodii.'),
(946, 6, 157, 'Ama aydaan dhihin haddii Nalagu soo Dejiyo Kitaab waxaan ahaan lahayn kuwo ka hanuunsan iyaga, waxaa idiinka yimid Xujo (Cad) xagga Eebihiin, iyo Hanuun, iyo Naxariis, cid ka dulmi badanna ma jirto mid beenisay Aayaadka Eebe oo ka Jeedsatay, waxaannu ku Abaal marin kuwa ka Jeedsada aayaadkanaga Cadaab Xun Jeedsigooda dartiis.'),
(947, 6, 158, 'Ma waxay Sugayaan inay Eebe iyo Malaa\'igta la Kumaan ama u Yimaaddaan Aayadaha Eebahaa Qaarkood Maalintay u Imaan Aayadaha Eebahaa qarkood wax uma tarto Naf rumayn iyadoon horay u Rumayn ama aan ku Kasban iimankeeda Khayr, waxaad Dhahdaa suga Anaguna waanu Sugaynaa.'),
(948, 6, 159, 'Kuwa ku kala tagay Diintooda oo noqday Kooxo, ka mid ma tahid, Amarkoodana Eebe umbaa iska leh markaasuu uga warrami waxay Falayeen.'),
(949, 6, 160, 'Ciddii la Timaadda Wanaag wuxuu mudan Toban la mid ah Ciddii la Timaadda Xumaanna waxaa laga abaal mariyaa mid la mid ah lamana Dulmiyo.'),
(950, 6, 161, 'Waxaad Dhahdaa wuxuu Igu Hanuuniyey Eebahay Jid Toosan Diin Sugan oo Diinti (Nabi) Ibraahim isagoo toosan oon gaalada ka mid ahayn.'),
(951, 6, 162, 'Waxaad Dhadaa Salaaddayda, waxaan Gawriei, Noloshayda iyo Geeridaydaba Eebaa iska leh Eebaha Caalamka ah.'),
(952, 6, 163, 'Wax la Wadaagana ma jiro, Saasaana lay faray Anigaana ugu Horreeya Muslimiinta (Ummaddan).'),
(953, 6, 164, 'Waxaad Dhadaa ma wax ilaah ka soo hadhay yaan Eebe ka dooni (Dhigan) Isagoo ILaahay wax walba Eebe u ah, naf kastana waxay kasbato waa uun Korkkeda, Nafna Dambi naf kale ma Qaaddo, Xagga Eebihiin umbaana laydiin Celin, wuxuuna idiinka warrami waxaad isku Diidanaydeen.'),
(954, 6, 165, 'Eebana waa kan idinka Yeelay kuwo Dhulka u Hadhay, Qaarkiinna qaar ka sara mariyey Darajooyin, inuu idinku Imtixaamo wuxuu idin siiyay, Eebana waa Deg degtaa Ciqaabtiisu waana Dambi Dhaafe Naxariista.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, '(Waxay ku Tusin Muejisada Quraanka).'),
(956, 7, 2, 'Kani waa Kitaab aannu kuu soo Dejinnay ee yeyna Laabtaada Dhibi (Cidhiidh) ahaan inaad ugu Digto, waana Waanada Mu\'miniinta.'),
(957, 7, 3, 'Raaca waxa Laydiinka soo Dejiyey Xagga Eebe, hana Raacina wax ka soo hadhay oo Awliyo ah (Gargaara ah) yaraana intaad Waantoobaysaan.'),
(958, 7, 4, 'Badanaa Intaan Magaalo Halaagnay oo u Timid Ciqaabtanadii iyadoo Jiifta (Habeen) ama Qayluushan (Maalin).'),
(959, 7, 5, 'Baryadoodu (Qayladooduna) markuu u Yimid halaagganagu ma ahayn waxaan ahayn inay Dhahaan annagaa daalimiin ahayn.'),
(960, 7, 6, 'Waana Warsanaynaa kuwii loo Diray (Ummadihii waxay ku Jawaabeen) waana warsanaynaa kuwii la Diray (Rasuulladii inay Gaadhsiiyeen).'),
(961, 7, 7, 'Waana uga Warramaynaa Ogaansho mana aanaan ka Maqnayn.'),
(962, 7, 8, 'Miisaanku waa Xaq Maalintaas (Qiyaamada) Cidday Cuslaato Miisaankiisa (Fiican) Kuwaasu waa uun kuwa Liibaanay.'),
(963, 7, 9, 'Cidduu Fududaado Miisaankiisuna kuwaasuna waa kuwa Khasaariyey Naftooda Dulmigay ku Falayeen Aayaadkanaga Dartiis.'),
(964, 7, 10, 'Waxaan idin Makaninay Dhulka waxaan idiinka Yeellay Meel lagu Noolaado, waxaase yar Intaad Shukriyeysaan.'),
(965, 7, 11, 'Waan idin Abuurray (Nabi Aadan) waana idin Suuraynay, markaasaan ku midhi Malaa\'igta u Sujuuda (Nabi) Aadam (Salaan) wayna Sujuudeen, Ibliise ma ahayn kuwa Sujuuda.'),
(966, 7, 12, 'Wuxuu Yidhi (Eebe) maxaa kaa reebay inaad Sujuuddo markaan ku faray, wuxuu Yidhi anaa ka Khayr roon, waxaad iga Abuurtay Dab isagana waxaad ka Abuurtay Dhoobo.'),
(967, 7, 13, 'Wuxuu Yidhi (Eebe) ka Daga Xaggeeda (Jannada) Xaq uma lehid inaad isku Dhex Kibriside, Bax waxaad ka mid tahay kuwa Dullaysane.'),
(968, 7, 14, 'Wuxuu Yidhi ii sug tan iyo Maalinta la soo Bixin.'),
(969, 7, 15, 'Wuxuu Yidhi (Eebe) waxaad ka mid tahay kuwa la sugi.'),
(970, 7, 16, 'Wuxuu Yidhi Ibaadiyeyntaada Darteed waxaan u Fadhiisan Jidkaaga Toosan (waan Dhumin).'),
(971, 7, 17, 'Waxaana kaga iman hortooda, Gadaashooda, Midigta iyo Bidixda mana hekaysid badankooda oo Shugriyi.'),
(972, 7, 18, 'Wuxuu Yidhi Eebe ka bax Xaggeeda adoo Caayan oo la Dheereeyey, Ciddii ku raacdana waxaa laga Buuxin Jahannamo Dhammaantiin.'),
(973, 7, 19, 'Nabi Aadamow Dega Adiga iyo Haweenaydaadu Jannada, kana Cuna Meejaad Doontaan, hana u Dhawaanina Geedkan, ood Noqotaan Daalimiin.'),
(974, 7, 20, 'Waxaana Waswaasiyey Shaydaan, inuu ka Muujiyo Xagooda wixii laga Asturay oo Cawradooda ah, wuxuuna Yidhi wuxuu idinka Reebay Eebe Geedkan inaydaan Noqonin Malaa\'ig, ama aydaan waarin.'),
(975, 7, 21, 'Wuxuuna ugu Dhaartay inaan idiin ahay Naseex.'),
(976, 7, 22, 'Wuuna soo Dejiyey Dhagar Darteed, markay Dhadhaniyeen Geedka waxaa u muuqday Xumaantooda waxayna ahaayeen kuwo isku Dhadhajiya Caleenta Jannada waxaana u Yeedhay Eebahood Miyaanan idinka reebin Geedan, oonan Idinku odhan Shaydaanku wuxuu idiin yahay Col Cad.'),
(977, 7, 23, 'Waxayna Dheheen Eebow waxaan Dulminay Naftanada Haddaadan noo Dambi Dhaafin oodan oo Naxariisanna waxaan ahaanaynaa kuwo Khasaaray.'),
(978, 7, 24, 'Wuxuu Yidhi Eebe ka dega Qaarkiin Qaarbuu Col u yahaye, waxaan Dhulka idiinku Sugan Nagaadi iyo Raaxo tan iyo Muddo.'),
(979, 7, 25, 'Wuxxuna Yidhi Eebe Dhexdiisaad ku Noolaanaysaan Dhexdiisaana ku Dhimanaysaan Xaggiisaana laydinka soo Bixin.'),
(980, 7, 26, 'Banii Aadamow waxaan idiin soo Dejinay Dhar Astura Xumaantiinna (Cawrada) iyo Labbis (Xarago) Dharka Dhawrsashada yaase Khayr roon, Kaasina waa Aayaadka Eebe inay Dadku wax Xusuustaan.'),
(981, 7, 27, 'Banii Aadamow Yuusan idin Fidnayn Shaydaan siduu uga soo Bixiyey Labadiinii Waalid Jannada, isagoo ka siibi Dharkoodii inuu Tusiyo Xumaantooda (Cawrada) wuu idin Arkaa isaga iyo Colkiisu idinkuse ma aragtaan, waxaana uga Yeelay Shaydaanka Sokeeya kuwaan Rumeynin Xaqa.'),
(982, 7, 28, 'Markay Falaan Xumaanna waxay Dhahaan waxaan ka Hellay Aabayaalkanno Eebaana na faray iyada, waxaad Dhahdaa Eebe ma faro Xumaan ma waxaad ku Dhahaysaan Eebe waxaydaan Ogayne.'),
(983, 7, 29, 'Waxaad Dhahdaa wuxuu faraa Eebe Cadaaladda iyo inaad u Jeedisaan Wajigiinna (Xagiisa) Masaajidkasta Agtiisa, baryana idinkoo u kaliyeeli Diinta siduu idinku Billaabay Yaad ku soo Noqonaysaan.'),
(984, 7, 30, 'Koox wuu Hanuuniyey, Kooxna waxaa ku sugnaaday korkooda Baadi, maxaayelay waxay ka Yeesheen Shayaadiinta Awliyo, waxayna u Malayn inay hanuunsanyihiin.'),
(985, 7, 31, 'Banii Aadamow qaata Quruxdiinna (Dharka) Masaajid kasta agtiisa, Cuna oo Cabbana hana xad gudbina Illeen Eebe ma Jeela kuwa xadgudubee.'),
(986, 7, 32, 'Waxaadna Dhahdaa yaa Reebay Quruxdii Eebe u soo Bixiyey Addoomadiisa, iyo Wanaagga Rizqiga ag, waxaad Dhahdaa waxaa iska leh kuwa Rumeeyey Nolosha Adduunyo Dhexdeeda, Gaarna u tahay Maalinta Qiyaame, saasaana ugu Caddaynay Aayaadkanaga Ciddii wax Og.'),
(987, 7, 33, 'Waxaad dhahdaa wuxuu uun Xarrimay Eebahay wax Xun kan Muuqda iyo ka Qarsoonba, iyo Dambiga, iyo Xad Gudub Xaq darro iyo inaad la wadaajisaan Eebe wuxuusan Xujo u soo Dejinin, iyo inaad ku Sheegtaan Eebe waxaydaan Ogayn «Xaqnimadiisa».'),
(988, 7, 34, 'Ummadkasta waxaa u sugnaaday Ajal (Muddo) markay Timaaddo Ajashuna kama dibmaraan Saacad kamana hor maraan.'),
(989, 7, 35, 'Banii Aadamow hadday idiin yimaadaan Rasuullo idinka mida oo idiinka warrama Aayaadkayga Ciddiise Dhawrsata oo Wanaag Fasha, Cabsi ma Saarra mana Walbahaaraan.'),
(990, 7, 36, 'Kuwa Beeniyey Aayaadkanaga oo iska Mibriya Xaggeeda kuwaasi waa Naarta Ehelkeeda wayna ku Waari.'),
(991, 7, 37, 'Yaa ka Dulmi Badan Cid ku Abuuratay Eebe Been ama Beenisa Aayaadkiisa, kuwaasu waxay Heli qaybtoodii loo qoray, markay u Timaaddo Malaa\'igtanada (Mawdka) oo Dili waxay ku Dhahaan Aaway waxaad Caabudayseen Eebe ka Sokow waxay Dhahaan way naga Dhumeen, waxayna ku marag Fureen Naftooda inay ahaayeen Gaalo.'),
(992, 7, 38, 'Wuxuu Yidhi Eebe la gala Naarta Umado hortiin Tegay oo Jinni iyo Insiba leh, markastooy Umadu Hasho waxay Laenadaa Walaasheed (Saaxiibkeed) markay isku haleelaan Dhammaantood Dhexdeedana waxay ku Dhahdaa Tan dambe wax (Raaeda) Tan hore la (Raaco) Eebow kuwanaa na Dhumiyey ee u Laablaab Cadaab, Eebe wuxuu Odhan mid kasta waa loo Laablaabi mase Ogidin.'),
(993, 7, 39, 'Waxay ku Odhan Tan hore Tan Dambi wax Dheeraada naguma lihidin ee Dhadhamiya Cadaabka waxaad kasbanayseen Darteed.'),
(994, 7, 40, 'Kuwa Beeniyey Aayadkanaga oo iska Kibriyey looma Furo Iridaha Samada mana Galaan Janada intuu ka Galo Riti Irbad Dulkeed Saasaana ku abaal marinaa Dambiilayaasha,'),
(995, 7, 41, 'Waxay ku Mudan Jahannamo Gogol korkoodana waxaa ahaan Dabool, Saasaana ku Abaal marinnaa Daalimiinta.'),
(996, 7, 42, 'Kuwa Rumeeyey Xaqa oo Falay Caml Fiican, kuma Mashaqaynno Naf waxayna Karin, Kuwaasu waa Janada Ehelkeeda wayna ku Dhex waari.'),
(997, 7, 43, 'Waxaana ka Siibnaa waxa ku Jira Laabtooda oo Xasada, waxaana Dareeri Hoostooda Wabiyadii, waxayna Dhahaan Mahad waxaa Mudan Eebaha Nagu Hanuuniyey kan (Wanaagga) Maanaan hanuuneen haddaan Eebe na hanuunin, waxay la Timid Rassuladii Eebaheen Xaq, waxaana loogu Dhawaaqay Tiinani waa Jannadii Laydin Dhaxalsiiyey Camalkiinna Dartiis.'),
(998, 7, 44, 'Waxay u Dhawaaqaan Ehelu Jannuhu Ehelu Naarka waan hellay wixii Eebe Noo Yaboohay si Dhab ah ee idinku ma Hesheen wuxuu idiin yahoohay Eebe si Dhab ah, waxayna Dhahaan haa waxaana ka dhawaaqa Dhexdooda mid Dhawaaqa Laenadda Eebe ha ku Dhaedo Daalimiinta.'),
(999, 7, 45, 'Ah kuwa ka Celiya Dadka Jidka Eebe lana Doona Qallooca, Iyagoo Aakhiro ka Gaaloobay.'),
(1000, 7, 46, 'Dhexdoodana waxaa ahaan Xijaab Aeraafkana (Meel sare) waxaa ahaan Dad ku Garan Cid Kasta Calaamaddooda, wacayna u Dhawaaqaan Ehelu Jannaha iyagoo ku leh Nabadgalyo Korkiina ha ahaato, mayna galin Jannada wayse damci.'),
(1001, 7, 47, 'Markii loo Jeediyo Wajigooda Xagga Ehelu Naarka waxay Dhahaaan Eebow ha na yeelin la Jirka Daalimiinta.'),
(1002, 7, 48, 'Waxay u Dhawaaqayaan Dadka Acraafku Dad ay ku garan Calaamaddooda, waxayna ku Odhan waxba idiinma tarin badnaantiinii (Kulankiinnii) iyo Waxaad isku Kibrinayseen.'),
(1003, 7, 49, 'Ma kuwanaa kuwaad ku Dhaarateen inaan Eebe Gaadhsiinayn (Siinayn) Naxariis (Waxaana lagu Odhan) Gala Janada idinkoon Cabsi iyo Walbahaar midna Arkayn.'),
(1004, 7, 50, 'Waxay u Dhawaaqi Ehelu Naarku Ehelu Jannaha (Iyagoo dhihi) Noogu soo Deeqa Biyaha ama waxa Eebe idinku Arzaaqay, waxayna Dhahaan Eebe wuu ka Xarrimay Labadaas Gaalada.'),
(1005, 7, 51, 'Ee ah kuwa ka Yeeshay Diintooda Madadalo iyo Ciyaar, oyna Dhagartay Nolosha Adduunyo, Maanta wuu ku Dayn Naarta siday u Halmaameen la Kulanka Maalintan, iyo Diidooda Aayaadkanaga Dartiis.'),
(1006, 7, 52, 'Dhab ahaan baannu ugula Nimid Kitaab Aannu Caddaynay Annagoo ku Cilmi leh, Isagoo Hanuun iyo Naxariis u ah Ciddi Rumayn.'),
(1007, 7, 53, 'Miyey sugi waxaan ahayn wuxuu Sheegi Quraanku, Maalintuu Yimaaddo wuxuu Sheegi Quraanku waxay Odhan kuwii Horay u Halmaamay (Kuna Camal falin) waxay la Timid Rasuuladii Eebe Xaq, ee majiraa Shufaco ha noo Shafeeceene, ama ma nala Celin inaan Fallo waxaan ahayn waxaan fali jirray, waxay Khasaariyeen Naftooda waxaana ka Dhumay waxay been abuuran jireen.'),
(1008, 7, 54, 'Eebihiin waa kan ku Abuuray Samooyinka iyo Dhulka Lix Maalmood kuna istawooday Carshiga, wuxuuna ku Daboolaa Habeenka Maalinta wuuna doonaa Habeenku si Deg dega Qorraxda, Dayaxa iyo Xidigahana waxaa loo Sakhiray Amarka Eebe, Eebaana Abuuridda iyo Amarkaba iska leh, waxaana waynaaday «oo Khayrbatay» Eebaha barbaariya Caalamka.'),
(1009, 7, 55, 'U Barya Eebihiin si Khashuue ah iyo Qarsoodi Ellen Eebe ma jeela kuwa Xad gudbee.'),
(1010, 7, 56, 'Hana fasaadinina Dhulka inta la Wanaajiyey ka dib, Baryana Eebe Cabsi iyo Doonidba Naxariista Eebana waxay u Dhawdahay kuwa Samaha Fala.'),
(1011, 7, 57, 'Eebe waa kan u dira Dabaysha Bishaaro Naxariistiisa (Roobka) Hortiisa markay Xambaarto Daruur Culusna waxaan u Kaxaynaa Dhul Dhintay (Abaar) waxaana ku Dejinaa (Shubnaa) Biyaha, waxaana ku soo Bixinaa wixii Dhintay inaad Xussusataan.'),
(1012, 7, 58, 'Dhulka Fiican wuxuu ku soo baxaa Doogiisu idinka Eebe, midka Xunna waxaan Dhib ahayn kuma soo baxo, saasaan ugu Caddaynaa Aayaadka Ciddii ku Shukrin Eebe.'),
(1013, 7, 59, 'Waxaan u Dirray Nabi Nuux Qoomkiisii, wuxuuna ku Yidhi Qoomkayow Caabuda Eebe idiinma sugna Eebe ka soo hadhaye, anugu waxaan idiinka Cabsan Cadaab Maalin Wayn.'),
(1014, 7, 60, 'Waxay Dheheen Madaxdii Qoomkiisa ka mid ahayd waxaan kugu Aragnaa Baadi Cad.'),
(1015, 7, 61, 'Wuxuuna yidhi Qoomkayow Baadi kuma Sugni waxaanse ahay Rasuul Eebaha Caalamka.'),
(1016, 7, 62, 'Waxaana idiin Gaadhsiin Farriinta Eebahay waana idiin Naseexayn, waxaana ka Ogyahay Xagga Eebe waxaydaan Ogayn.'),
(1017, 7, 63, 'Ma waxaad la Yaabteen inuu u Yimaaddo Nin idinka mida Xuska Eebihiin si uu idiinku digo iyo inaad dhawrsataan laydiinna Naxariisto.'),
(1018, 7, 64, 'Wayse Beeniyeen waxaana ku korinay Nuux iyo intii la jirtay Doontii waxaana Maanshaynay kuwii Beeniyey Aayaadkanaga, Illeen waxay ahaayeen Qoom indhala, (Xaqa arkayn).'),
(1019, 7, 65, 'Waxaan u Dirray Caadna Walaalkood (Nabi) Huud wuxuuna yidhi Qoomkayow Caabuda Eebe idiinma sugna Eebe ka soo hadhay, Miyeydaan Dhawrsanayn.'),
(1020, 7, 66, 'Waxay Dheheen Madaxdii Gaalowday oo Qoomkiisa ka mid a, Anagu waxaan kugu aragnaa Safaaho (Xumaan) waxaana kuu malaynaynaa Beenaalayaasha.'),
(1021, 7, 67, 'Wuxuuna yidhi Qoomkayow Safaaho (Xumaan) imahayso waxaanse ahay Rasuul Eebaha Caalamka.'),
(1022, 7, 68, 'Waxaana idin gaadhsiin Farrinta Eebahay waxaana idiin ahay Nasteex Aamina.'),
(1023, 7, 69, 'Ma waxaad la Yaabteen inuu u Yimaaddo Xuska Eebihiin Nin idinka mid ah inuu iddin Digo, Xusuusta Markuu idinka yeelay Eebe kuwa u Hadhay (Dhulka) Qoomkii Nabi Nuux ka dib, idiinna kordhiyey Xoog, ee Xusuusta Niemada Eebe, waxaad u Dhawdihiin inaad Liibaantaane.'),
(1024, 7, 70, 'Waxay Dheheen ma waxaad noola timid inaan Caabudno Eebe Kaliya, oon ka Tagno Waxay Caabudayeen Aabayaalkanno, noo keen waxaad noogu Goodiyeysay Haddaad runle tahay.'),
(1025, 7, 71, 'wuxuu yidhi waxaa idinkaga Dhacaya Xagga Eebe Cadaab iyo Cadho ma waxaad igula Murmaysaan Magacyo ood Magacoowdeen idinka iyo Aabayaalkiin oon Eebe u soo Dejinin Xujo, Suga anna waan idinla Sugiye.'),
(1026, 7, 72, 'Markaasaan Korinay isaga iyo Intii la jirtay Naxariistanada Darteed, waxaana Goynay Cidhibta kuwa Beeniyey Aayaadkanaga, mana ayan rumayn (Xaqa).'),
(1027, 7, 73, 'Waxaan u Dirray Thamuudna Walaalkood Nabi Saalax, wuxuuna yidhi Qoomkayow Caabuda Eebe wax ka soo hadhay Eebe idiinma sugnee, waxaa idiinka timid Xagga Eebe Xujo (Cad) Tanna waa hashii Eebe, waxayna idiin tahay Calaamo, ee iska Daaya ha ka Cunto Dhulka Eebe hana ku taabanina Xumaan oo markaas idin qabto Cadaab Daran.'),
(1028, 7, 74, 'Xusuusta markuu idinka yeelay (Eebe) kuwo Baddalay Caad kadib idin Dajiyeyna Dhulka idinkoo ka Yeelay Dooxyaheeda Daaro, ood kana qoranaysaan Buuraheeda Guryo Xusuusta Niemooyinka hana Fasaadinina Dhulka.'),
(1029, 7, 75, 'Waxayna ku dheheen Madaxdii Iskibrisay oo Qoomkiisa ka mida kuwii Tabarta yaraa oo Rumeeyey ma waxaad Ogtihiin in Saalax laga soo diray Xagga Eebe, waxayna dheheen Annagu waxa lagu diray waan rumaynay.'),
(1030, 7, 76, 'Waxayna dheheen kuwii iskibriyey annagu waxaad Rumeeyseen waan ka Gaaloownay.'),
(1031, 7, 77, 'Waxayna Dileen Hashii waxayna ka Madaxadaygeen amarkii Eebahood, waxayna dheheen Saalaxow noo keen waxaad noogu Goodiyeeyso haddaad tahay mid la soo Diray.'),
(1032, 7, 78, 'Waxaana qabtay Gariirkii waxayna ahaadeen kuwo, Guryahoodii ku Dhintay.'),
(1033, 7, 79, 'Wuxuuna ka Jeedsaday «Nabi Saalax» caggooda wuxuuna yidhi waan idin Gaadhsiiyey Fariintii Eebahay waana idiin naseexeeyey Mase Jeelidiin Nasteexa.'),
(1034, 7, 80, 'Xusuuso Nabi Luudh Markuu ku yidhi Qoomkiisii Ma waxaad la imaanaysaan Xumaanta uusan idiinka hormarin Ruux Caalamka ka mid ah.'),
(1035, 7, 81, 'Idinku waxaad u Tagaysaan Ragga Hunguriyeeyn Darteed Haweenka ka sokow, waxaadse tihiin qoom Xad Gudbay.'),
(1036, 7, 82, 'Waxayna ahayd Jawaabtii qoomkiisa, ka Bixiya Luudh iyo inta Rumaysay Magaaladiinna waaba Dad isdaahirinaye.'),
(1037, 7, 83, 'Waana Korinay Luudh iyo Ehelkiisa Haweenaysiisii mooyee oo ka mid noqotay kuwa halaag ku hadhay.'),
(1038, 7, 84, 'Waxaana ku soo Daadinay Roob (Naareed) bal day siday Noqotay Cidhibtii Dambiilayaasha.'),
(1039, 7, 85, 'Madyanna waxaan u Diray Walaalkood Nabi Shucayb wuxuuna yidhi Qoomkayow Caabuda Eebe idiinma sugna Eebe ka soo hadhaye, waxaa idiinka Timid Xagga Eebe Xujee Dhammaystirana Miisidda iyo Miisaanka, hana ka Nusqaaminina Dadka waxooda, hana Fasaadinina Dhulka wanaajin kadib, saasaa idiin khayr roon haddaad Mu\'miniin tihiin.'),
(1040, 7, 86, 'Hana Fadhiisanina Jid kasta idinkoo Goodin oo ka Celin Dadka Jidka Eebe Ciddii Rumaysa idinkoo Qalooe la dooni, xusuustana markaad yareydeen oo Eebe idin badiyey, dayana siday Noqotay Cidhibtii Fasaadiyaasha.'),
(1041, 7, 87, 'Haddayse Koox idinka mida Rumaysay waxa laygu soo Diray Kooxna ayna Rumeynin Samra (Suga) inta Eebe ka kala Xugmin Dhexdeenna Isagaa Xaakim Khayr Roon ah.'),
(1042, 7, 88, 'Waxay dheheen Madaxdii Iskibrisay oo Qoomkiisii ah waxaannu kaa Bixin Shucaybow adiga iyo kuwa ku Rumeeyey Magaaladanada ama waxaad ku soo Noqon Diintanada, wuxuuna yidhi oo haddaan Necebnahayna.'),
(1043, 7, 89, 'Waxaan ku Been abuuranay Eebe hadaan ku noqonno Diintiinna inta Eebe naga Koriyey ka dib, Nagumana habboona inaan ku Noqonno in Eebe Doono Mooyee, Illeen Eebaa Cilmigiisu waasac yahaye, waxaana talo Saarannay Eebe, Eebow Nagu kala Xukun Dhexdanada Xaq adaa Xaakim Khayr Roon ahe.'),
(1044, 7, 90, 'Waxayna Dheheen Madaxdii Gaalowday oo Qoomkiisii ka mida haddaad Raacdaan Shucayb waad Khasaarteen.'),
(1045, 7, 91, 'Waxaase qabtay Gariir waxayna ahaayeen kuwo Guryahoodii ku Dhintay (Iyagoo lagdan).'),
(1046, 7, 92, 'Kuwii Beeniyey Shucayb waxaad Mooddaa inayna ku Nagaanin Magaaladaas Dhexdeeda, kuwii Beeniyey Shucaybna iyaga umbaa Khasaaray.'),
(1047, 7, 93, 'Markaasuu isaga Jeedsaday oo yidhi Qoomkayow waan idin Gaadhsiiyey Fariintii Eebahay waana idiin Naseexeeyey ee sideen ugu walbahaari Qoom Gaala ah.'),
(1048, 7, 94, 'Magaalo Kasta oon Nabi u dirraba waxaan ku Qabanay EhelkeedaCudurro iyo Dhibaato inay Khushuueaan (oo Baryaan Eebe).'),
(1049, 7, 95, 'Markaasaan u Baddallaa meejii Xumaan (Dhibka) Wanaag (Khayr) intay ka Bataan oy Dhahaan waxaa taabtay Aabayaalkaanno Dhibaato iyo Farax, Markaasaan ku Qbannaa kado Iyagoon Ogayn.'),
(1050, 7, 96, 'Hadday Dadka Magaalooyinku Rumeeyaan (Xaqa) oo Dhawrsadaan waxaan ku Furi lahayn korkooda (Sii dayn lahayn) Barakada Samada iyo Dhulka, wayse Beeniyeen (Xaqii) Waana qabanay waxay kasbanayeen Dartiis.'),
(1051, 7, 97, 'Miyey ka aamin Noqdeen Dadka Magaaladu inuu u Yimaaddo Dhibkanaga Habeenkii iyagoo Hurda.'),
(1052, 7, 98, 'Miyeyse ka aamin yihiin Dadka Magaaladu inuu u Yimaaddo Dhibkanagu Barqinkii iyagoo Ciyaari.'),
(1053, 7, 99, 'Ma waxay ka aamin Noqdeen Dhibka Eebe, cid khasaartay yaa ka aamin Noqota Ciqaabta iyo Dhibka Eebe.'),
(1054, 7, 100, 'Miyeyna u Caddaanin kuwa Dhaxlaya Dhulka Ehelkiisa (Halaagiisa) ka dib haddaan doono Waan qaban lahayn Dambigooda Dartiis, oon Dabooli lahayn Quluubtooda oyna wax Maqlin.'),
(1055, 7, 101, 'Taasi waa Magaalooyinkii waxaana kaaga qisoonaynaa warkoodii, waxayna ula timid Rasuuladii Xujooyin mana ayan Rumayn Beenintooda darteed horay, saasuuna u Daabacaa Eebe quluubta Gaalada.'),
(1056, 7, 102, 'Kamaanaan helin badankooda Ballan, waxaanse ka hellay badankood iyagoo Faasiqiina.'),
(1057, 7, 103, 'Markaasaan la Bixinay Nabi Muuse Aayaadkanaga Xagga Fircoon iyo Qoomkiisii wayna Dulmiyeen (Diideen) ee day Siday Noqotay Cidhibtii Mufsidiinta.'),
(1058, 7, 104, 'Wuxuuna Yidhi (Nabi) Muuse Fircoonow Anagu Rasuul Eebaha Caalamkaan ahay.'),
(1059, 7, 105, 'Waxaana Dhab ah inaan Eebe ka Sheegayn waxaan Xaq ahayn, waxaana idiin la mid Xujo Eebihiin ee u daa Banii Israa\'iil la Jirkayga.'),
(1060, 7, 106, 'Wuxuuna yidhi (Fircoon) haddaad la Timid Aayad keen haddaad Run Sheegi.'),
(1061, 7, 107, 'Markaasuu Tuuray Ushiisii markaasay soo Baxday iyadoo Mas Cad ah.'),
(1062, 7, 108, 'Wuxuuna Siibay Gacantiisii markaasay soo Baxday iyadoo u Cad Dadka Eegi.'),
(1063, 7, 109, 'Waxay dheheen Madaxdii qoomkii Fircoon ka mida kani waa sixirroow aad u yaqaanna.'),
(1064, 7, 110, 'wuxuuna Dooni inuu idinka Bixiyo Dhulkiinnaa ee Maxaad Faraysaan.'),
(1065, 7, 111, 'waxayna Dheheen dib u Dhig (Xaalkiisa) iyo Walaalkiis una dir Magaalooyinka kuwo soo kulmiya.'),
(1066, 7, 112, 'oo kuu Keena Sixir badane kasta aadna u yaqaanna.'),
(1067, 7, 113, 'Waxayna u Yimaaddeen Sixirlayaashii Fircoon waxayna Dheheen Ujuuro maleenahay haddaan annagu Adkaanno.'),
(1068, 7, 114, 'Wuxuuna yidhi haa, waxaadna noqonaysaan kuwa la soo Dhaweeyo.'),
(1069, 7, 115, 'Waxayna dheheen Muusow, ama Tuur ama aan tuurro.'),
(1070, 7, 116, 'Wuxuuna yidhi Tuura, markay Tuureenna waxay Sixreen Indhaha Dadka wayna Cabsi galiyeen waxayna la yimaadeen Sixir wayn.'),
(1071, 7, 117, 'Waxaana u Waxyoonay (Nabi) Muuse Tuur Ushaada, waxayna soo Baxday inay Cunto waxay Dhoobdhoobeen.'),
(1072, 7, 118, 'Waxaana Sugnaaday Xaqii waxaana Buray waxay Falayeen.'),
(1073, 7, 119, 'Waxaana lagaga Adkaaday Gaaladii halkaas, waxayna noqdeen iyagoo Dullaysan.'),
(1074, 7, 120, 'Waxaana loo riday Sixirlayaashii Sujuud.'),
(1075, 7, 121, 'Waxayna Dheheen waxaan Rumeynay Eebaha Caalamka.'),
(1076, 7, 122, 'Eebaha Muuse iyo Haaruun.'),
(1077, 7, 123, 'Wuxuuna yidhi Fircoon Miyaad rumeyseen Muuse, Idan ka hor, Arrintani waa Dhagar aad ku Dhagarteen Magaalada si aad uga Bixisaan Xaggeeda Dadkeeda, waad ogaan doontaan.'),
(1078, 7, 124, 'Waxaan u gooyn Gaemihiinna iyo Lugahiinna isdhaaf waana idin Wadhi Dhamaantiin.'),
(1079, 7, 125, 'Waxayna Dheheen Xagga Eebahanaa u Noqonaynaa.'),
(1080, 7, 126, 'Waxaad nagu nacayso oon ahayn inaan Rumaynay Aayaadka Eebahanno ma jiro, markay noo Timid, Eebow samir naga Buuxi na dilna anagoo Muslimiina.'),
(1081, 7, 127, 'Waxay Dheheen Madaxdii oo Qoonkii Fircoona ma ku Dayn Muuse iyo Qoomkiisa inay Fasaadiyaan Dhulka inuu ku daayo adiyo Cibaadadaada (iyo lLaahyadaada). wuxuuna yidhi waxaannu Dili Wiilashooda waxaannu dayn Gabdhahooda anagaana ka sarrayna oo Dullayn.'),
(1082, 7, 128, 'Muusana wuxuu ku yidhi Qoomkiisa Kaalmaysta Eebe, samrana Dhulka Eebaa iska leh wuxuuna dhaxalsiin Cidduu doono oo addoomadiisa ka mida, Cidhibta (Fiicanna) waxaa iska leh kuwa Dhawrsada.'),
(1083, 7, 129, 'Waxayna ku dhaheen waa nala Dhihay ka hor Imaatinkaaga iyo ka dib Imaatinkaaga, wuxuuna (Nabi Muuse) ku Yidhi waxay u Dhawdahay in Eebihiin halaago Cadawgiinna oo Dhulka idinku reebo si uu u Eego waxaad Fashaan.'),
(1084, 7, 130, 'Waxaan ku qabanay Fircoon Ehelkiisii Abaar iyo Nusqaan Midhaha inay wax Xusuustaan.'),
(1085, 7, 131, 'Hadduu Wanaag u Yimaaddo waxay Dhahaan anaa leh tan (Mudan), hadday Dhibaato ku Dhacdana waxay aharaystaan Muuse iyo inta la jirta, Baaskoodu wuxu uun jiraa Eebe Agtiisa, badankooduse ma Oga.'),
(1086, 7, 132, 'Waxayna dheheen wax kasta oo aayad ah ood noola timaaddo inaad nagu Sixirlo anagu kuma rumaynayno.'),
(1087, 7, 133, 'Markaasaan ku Dirray Daad, Ayax, injir iyo: Rah iyo Dhiig aayaad Cad iyagoo ah, wayna. iskibriyeen waxayna ahaayeen Qoom Dambiilayaal ah.'),
(1088, 7, 134, 'Markuu ku Dhacay korkooda Cadaabku waxay dheheen Muusow noo Bari Eebahaa wuxuu kugula Ballamay, haddaad naga fayddo Cadaabka waannu ku Rumayn, waxaana la diri (u dayn) la jirkaaga Banii Israa\'iil.'),
(1089, 7, 135, 'Markaan ka faydnay Cadaabkii lan iyo Muddo ay Gaadhayaan yey ahaadeen kuwo Buriya (Ballankii).'),
(1090, 7, 136, 'Waan ka aarsannay Gaaladii waxaana ku Maanshaynay Badda Illeen waxay beeniyeen Aayaadkanaga, wayna Halmaameen.'),
(1091, 7, 137, 'Waxaana Dhaxalsiinnay Qoomkii ahaa kuwa lagu Dullaysto Bariga Dhulka iyo Galbeedkiis (Masar iyo Shaam) een barakaynay Dhexdiisa, wayna u Dhammaatay Kalimada (Yaboohii) Eebahaa ee wanagsanaa Banuu Israa\'iil samirkoodii Dartiis. waxaana Baabi\'inay wuxuu Samaynayey Fircoon iyo Qoomkiisii iyo waxay Dhisayeeen.'),
(1092, 7, 138, 'Waxaana Tallaabinay Banuu Israa\'iil baddii waxayna u Yimaadeen Qoom ku Nagi Sanamyadoodii markaasay dheheen Muusow Nooyeel Ilaah siday iyaba Ilaahyo u yeesheen wuxuuna ku Yidhi idinku waxaad tihiin Qoom Jaahiliina.'),
(1093, 7, 139, 'Kuwaas waa lagu Halaagi waxay ku suganyihiin waana Baadhil waxay fali.'),
(1094, 7, 140, 'Wuxuu yidhi ma wax Eebe ka soo hadhay yaan Ilaahnimo idiinka doonaa isagoo Caalamkan (Jooga) idinka Fadilay.'),
(1095, 7, 141, 'Xusuusta markaan idinka Korinay Fircoon Dadkiisii iyagoo idin dhadhansiiyey Cadaab Xun, oo laynayey Wiilashiinna daynayeyna Gabdhihiinna, Arrintaasna waxaa ku Sugan Ibtilo Eebihiin Xaggiisa ka ahaatay oo wayn.'),
(1096, 7, 142, 'waxaan u yaboohnay Nabi Muuse Soddan habeen waxaana ku dhamaystirray Toban, wuxuuna ku dhamaaday Ballankii Eebihiis Afartan Habeen, wuxuuna ku yidhi Nabi Muuse Walaalkiis Haaruun iiga dambee Qoomkayga Wanaakina hana raacin Jidka Mufsidiinta.'),
(1097, 7, 143, 'Markuu u Yimid Nabi Muuse Ballankanagii oo la hadlay Eebihiis wuxuu yidhi Eebow i tus aan ku arkee, wuxuuna yidhi Eebe ima arkaysid ee day Buurta Hadday Meejeeda ku sugnaato markaasaad i arki, markuu Eebc u Daahiray Buurtii wuxuu ka yeelay Burbur waxaana dhacay Nabi Muuse suuxdin Darteed, markuu miyirsadayna wuxuu Yidhi (Eebow adaa) Nasahan waan kuu Toobad keenay waxaan ahay Mu\'minunta kan ugu horreeya (Waqtigan) inaan Adduunka lagugu (arkayn).'),
(1098, 7, 144, 'Wiixuu Yidhi Eebe Muusow waxaan kaa doortay Dadka (Sabankan) Diriddayda iyo lahadalkayga Dartiis ee qaado waxaan ku siiyey ahowoa kuwa Mahdiya.'),
(1099, 7, 145, 'Waxaan ugu qurray alwaaxda wax kastoo wacdi iyo Caddayna, ee ku qaado Niyadadag farna Qoomkaaga may qaalaan, ha qaataan Teeda Fiicane Waxaan idin tusin Guryihii Faasiqiinta.'),
(1100, 7, 146, 'Waxaan ka lilaa Aayaadkayga kuwa isku Kibriya Dhulka Xaq darro, hadday arkaan Aayaad kasta ma Rumeeyaan hadday arkaan Jidka Hanuunka kama Yeeshaan Waddo, hadday Arkaan Waddada Baadidana waxay ka yccshaan Jid, waxaana ugu Waran inay Beeniyeen Aayaadkanaga oy ahaayeeo kuwo Halmaamay.'),
(1101, 7, 147, 'Kuwa Beeniyeen Aayaadkanaga iyo la Kulankanaga Aakhiro waxaa Buray Camalyaalkooda Miyaase laga abaal mariyaa waxay Camal falayeen Mooyee.'),
(1102, 7, 148, 'Waxay ka Yeesheen qoomkii Nabi Muuse Gadaashiis Jawhartoodii Dibi Cod leh, miyeyna arkaynin inuusan lahadlaynin kuna Toosinaynin Jid, way Yeesheen iyagoo Daalimiin ah.'),
(1103, 7, 149, 'Markii Gacmahooda lagu Riday (Nidaamoodeen) oy arkeen inay Dhumeen waxay Dhaheen Hadduusan noo Naxariisan Eebahanno oosan noo Dambi Dhaafin waxaan Noqonaynaa kuwo Khasaara.'),
(1104, 7, 150, 'Markuu ku soo Noqday Nabi Muse qoomkiisii isagoo Cadhaysan oo Walbahaarsan wuxuu yidhi waxaa Xun Waxaad iiga dambeysccn Gadaashay, ma waxaad Dadajisateen Amarkii Eebihiin wuxuuna Tuuray Alwaaxdii wuxuuna Qabtay Madaxa Walaalkiis Isagoo Xaggiisa u Jiidi, wuxuuna Yidhi Ina Hooyoow qoomku way i tabaryaraysteen waxayna u dhawaadeen inay i Dilaan ee ha igaga farxin Colka hana iga yeehn la Jirka Qoomka Daalimiinta ah.'),
(1105, 7, 151, 'Wuxuuna yidhi Nabi Muuse Eebow u Dambi Dhaaf ani iyo Walaalkay nana gali Naxariistaada adaa Naxariis badane ahe.'),
(1106, 7, 152, 'Kuwa Yeeshay Dibi waxaa Gaadhi Cadho Lebahood iyo Dulli Nolosha adduunyo saasaana ku Abaal marinaa Been abuurtayaasha.'),
(1107, 7, 153, 'Kuwa Falay Xumaan Markaas Toobad keenay intaas ka dib oo Rumeeyey Xaqa Eebahaa intaas ka dib waa Dambi Dhaafe Naxariista.'),
(1108, 7, 154, 'Markay Cadhadu ka Xasishay Nabi Muuse wuxuu Qaatay Alwaaxdii waxaana ku qorraa hanuun iyo Naxariis u ah kuwa Eebahood ka Cabsan.'),
(1109, 7, 155, 'Wuxuu ka Doortay Nabi Muuse Qoomkiisa Toddobaatan Nin Ballankanaga Dartiis, markay qabatay Gariirku wuxuu Yidhi Eebow haddaad Doonto waad halaagi lahayd horay iyaga iyo Anigaba, ma waxaad noo halaagi waxay faleen Sufaho naga mida, waa Imtixaankaaga Ciddaad doontana waad Dhumisaa kaad dontana waad hanuunisaa adaa Wali noo ah ee noo Dambi Dhaaf noona Naxariiso adaa Dambi Dhaafe Khayr roon ahe.'),
(1110, 7, 156, 'Noona qor adduunyadan Wanaag iyo Aakhiraba waannu kuu noqonaye, wuxuu Yidhi Cadaabkaygu wuxuu ku dhici Ciddaan Doono, Naxariistayduna waxay u Waasac Noqotay wax kasta, waxaana u qori kuwa Dhawrsada oo Bixiya Zakada iyo Kuwa Aayaadkaygana Rumeeya.'),
(1111, 7, 157, 'ee ah kuwa Raaca Rasuulka Nabiga ah ec Umiga ah ee ah kay ka heleyeen Isagoo ku qoran agtooda Tawreed iyo Injiil Farana wanaagga kana reeba Xumaanta una Baneeya Wanaagga (Xalaasha) kana Xarrima Xumaanta (Xaaraanta) kana dejiya Culayskii iyo Xadhkihii korkooda ahaa, kuwa Rumeeyey Nabigaas oo Qaddariyey oo u Gargaaray oo Raacay Nuurka lagu soo Dejiyey kuwaasu waa uun kuwa Liibaanay.'),
(1112, 7, 158, 'Waxaad Dhahdaa Dadow anugu waxaan ahay Rasuulkii Eebe ee Xaggiinna Dhammaan, waana Eebaha iska leh Xukunka Samooyinka iyo Dhulka, ILaahay mooyee Eebe kalana ma jiro isagaa wax nooleeya, waxna Dila ee Rumeeya Eebe iyo Rasuulkiisa Nabiga ah ee Umiga ah ee Rumeeyey Eebe iyo Malaa\'igtiisa Raacana inaad hanuuntaan.'),
(1113, 7, 159, 'Waxaa ka mida Qoomkii Nabi Muuse Umad ku Hanuimsanayd Xaqa kiina Caddaalad falayaay.'),
(1114, 7, 160, 'Waxaana u kala gaynay Laba iyo Toban Farac oo Ummadood, waxaana u Waxyoonay Nabi Muuse markay Waraab warsadeen Qoomkiisu inuu ku garaaco Ushiisa Dhagaxa waxaana ka soo burqatay laba iyo toban ilood cid kastana waxay Ogaatay Meejcy ka Cabbi lahayd, waxaana ku hadhaynay Daruuro, waxaana ku soo Dejinay Manni (Macaan) iyo Cunno, waxaana ku nidhi ka Cuna wanaagaan idinku Arzuqay, mana ayan na dulmiyin waxaysc Dulmiyccn Naftooda.'),
(1115, 7, 161, 'Markii lagu yidhi dega Magaaladaa kana Cuna Meejaad doontaan Dhehana Eebow noo Dambi dhaaf, kana gala Albaabka idinkoo Sujuudsan aan idiin Dhaafno Dambigiince unu siyaadinno samafalayaashee.'),
(1116, 7, 162, 'Waxayse ku badaleen kuwii dulmi falay oo ka mida hadal ka duwan kii loo yidhi (Fala) waxaana ku Dirray Cadaab Samada kaga Yimid dulmigoodii Dartiis.'),
(1117, 7, 163, 'Warso (Yuhuud) Magaaladii Badda ku Dhawayd (Dadkeedii) markay Xadgudbayeen Sabtida, oo Kalluunkoodu u Imanayey Maalinta Sabtida ah isagoo Muuqda, Maalintaan Sabti ahaynna uma Timaaddo, saasaana ugu Imtixaanaa Faasiqni\' madooda Darteed.'),
(1118, 7, 164, 'Ka Warran markay Tidhi Umad ka mida maxaad u Wacdiyeysaan qoom Eebe halaagi ama u Cadaabi si daran, waxayna dhaheen waa Eebe u Cudur daarasho, iyo inay Dhawrsadaan inay u Dhawyihiin.'),
(1119, 7, 165, 'Markay Halmaameen wixii lagu waaniyey waxaan korinay kuwii Xumaanta Reebayey waxaana ku qabanay kuwii Dulmiga falay Cadaab Xun Faasiqnimadooda Darteed.'),
(1120, 7, 166, 'Markay ku Madax adaygeen wixii laga reebay yaan ku nidhi Noqda Daanyeer Dullaysan.'),
(1121, 7, 167, 'Xusuusta markuu Eebe Ogeysiiyey inuu u soo Bixiyaa Yuhuud tan iyo Qiyaamaha cid Dhadhansiisa Cadaab Xun, Eebahaana Ciqaabtiisu way Deg degtaa, waana Dambi Dhaafe Naxariista.'),
(1122, 7, 168, 'Waxaana ku kala gaynay Dhulka iyagoo Umada ah, kuwo suuban iyo kuwo kalaba waxaana ku imtixaannay wanaag iyo Dhibba inay Noqdaan.'),
(1123, 7, 169, 'Waxaana Baddelay kuwo Xun oo Dhaxlay Kitaabkii qaadanina Muuqa Adduunyadan Dhaw, iyagoo Dhihi waa naloo dhaafi hadduu u Yimaaddo muuqaal la midana way qaataan, miyaan laga qaadin Ballankii Kitaabka inayan Eebe waxaan Xaq ahayn ku sheegin oyna Akhriyin waxa Dhaxdiisa ah, Daarta Aakhiro yaana u Khayr roon kuwa dhawrsada ee miyeeydan wax kasayn.'),
(1124, 7, 170, 'Kuwa Qabsada Kitaabka oo Ooga Salaadda annagu ma dayacno ajriga kuwa wax Suubiya (Samo fala).'),
(1125, 7, 171, '(Ka warran) markaan ku koryeelay Buurtii, sidii isagoo hadhayn, una maleeyeen inuu ku Dhici, kuna nidhi ku qaata waxaan idin siinay Niyad Adag, kuna waana qaata waxa ku sugan si aad u dhawrsataan.'),
(1126, 7, 172, '(Xusuuso) markuu Eebahaa ka qabtay Dadka Dhabarkooda Faracoodii uuna marag uga Yeelay Naftooda, kuna Yidhi Miyaanaan Eebihiin ahayn, oy dhaheenna inaad Eebahanno tahay yaan Marag Kacnay, inaydaan dhihin Maalinta Qiyaame annagu Waan halmaansanayn kan.'),
(1127, 7, 173, 'Ama aydaan Dhihin waxaa wax la wadaajiyey (Edbe) Aabbayaalkanagii mar hore annaguna waxaan nahay farac ka Dambeeyey ee miyaad noo halaagi waxay faleen Xumo falayaal.'),
(1128, 7, 174, 'Saasaanu Caddaynaa Aayaadka iyo inay u Noqdaan Xaqa.'),
(1129, 7, 175, 'Ku Akhri korkooda kii warkiisii aan Siinay Aayaadkanaga oo ka Siibtay Shaydaanna uu Raacay (Isaga) Noqdayna kuwa Baadida ah.'),
(1130, 7, 176, 'Haddan Doonno waannu koryeeli lahayn, hasayeeshee wuxuu u iishay Dhulka (Adduunyo Jacayl) wuxuuna Raacay Hawadiisa, wuxuuna la mid yahay Eey haddaad Erido wuu lalmin Carrabka, haddaad Deysana wuu lalmin, saasna waa Masalka Qoomka Beeniyey Aayaadkanaga ee uga warran Qisooyin inay Waantoomaan.'),
(1131, 7, 177, 'Waxaa Tusaale u Xun Qoomkii Beeniyey Aayaadkanaga, Naftoodana waxay ahaayeen kuwo Dulmiya.'),
(1132, 7, 178, 'Ciddii Eebe hanuuniya umbaa hanuuni, Cidduu Dhumiyana waa uun kuwa Khasaaray.'),
(1133, 7, 179, 'Waxaan u Abuuray Jahannamo wax badan oo Jinni iyo Insiba leh, waxayna Leeyihiin Quluub ayna wax ku Kasayn, waxayna Leeyihiin Indho ayna wax ku Arkayn, waxayna Leeyihiin Dhago ayna wax ku Maqlayn, Kuwaasina waa Xoolo oo kale, waana ka sii Dhunsanyihiin, kuwaasina waa uun kuwa Halmaansan.'),
(1134, 7, 180, 'Eebe waxaa u Sugnaaday Magacyo Wanaag badan, ee ku Barya, Iskagana taga kuwa leexin Magacyadiisa, waxaannu ka abaal marin waxay Camal falayeen.'),
(1135, 7, 181, 'Waxaa ka mida waxaan Abuuray Ummad ku Hanuunsan Xaqa, Kuna socon.'),
(1136, 7, 182, 'Kuwa Beeniyey Aayaadkanaga waxaannu ka qaban meel ayna Ogayn.'),
(1137, 7, 183, 'Waana Sugi Kaydkaygu «Awooddayduna» waa darantahay.'),
(1138, 7, 184, 'Miyeyna Fikirin inaan Saaxiibkooda (Nabiga) Waalli hayn, waxaan dige Cad ahayna uuna ahayn.'),
(1139, 7, 185, 'Miyeyna Dayin Xukunka Samooyinka iyo Dhulka, iyo waxa Eebe abuuray oo wax ah, iyo inay Mudantahay inay Dhawdahay Ajashoodu, Hadalkayse Quraanka ka dib Rumayn.'),
(1140, 7, 186, 'Cidduu Dhumiyo Eebe wax Hanuunin ma lch, wuxuuna kaga tegi Xad gudubkooda iyagoo ku Wareersan.'),
(1141, 7, 187, 'Waxay ku Warsan Saacadda (Qiyaame) markay Sugnaan, waxaad Dhahdaa Ogaanshaheedu waa Eebahay Agtiisa, waqtigeeda wax Muujin kara oon Eebe ahayn ma jiro, waxay ku Cuslaatay Samooyinka iyo Dhulka, waxaan kado ahaynna idiinkuma Timaaddo, waxay ku warsan Saacadda sidii adoo og, waxaad dhahdaa Ogaanshaheedu waa uun Eebe Agtiisa Dadka Badankiisase ma oga (Xaalkaas).'),
(1142, 7, 188, 'Waxaad Dhahdaa uma hanto Naftayda Nacfi iyo Dhib midna, wuxuu Eebe doono mooyee, haddaan ahay mid og waxa maqan waxaan badsan lahaa Khayrka imana taabteen Xumaani, anigu waxaan uun ahay u dige una Bishaareeye Qoomkii Rumayn (Xaqa).'),
(1143, 7, 189, 'Eebe waa kan idinka abuuray Naf kaliya, kana yeelay Xaggeeda Haweenaydiisii inuu Weheshado markay Kulmeenna waxay qaadday uur Fudud Saasayna ahayd Markay Cuslaatayna waxay Baryeen Eebahood haddaad Na siiso (Ilmo) Suubban waxaan noqonaynaa kuwa Shugriya.'),
(1144, 7, 190, 'Markuu siiyey mid Suubban waxay u Yeeleen wax la wadaaga wuxuu Eebe siiyey, Eebaa ka sarreeya waxay la Waddajiyeen.'),
(1145, 7, 191, 'Ma waxay la wadaajin waxaan wax Abuuran iyagana la Abuuro.'),
(1146, 7, 192, 'OOn Karaynna gargaar naftoodana aan u gargaarayn.'),
(1147, 7, 193, 'Haddaad ugu Yeedho Hanuunkana idima raacaan waana isku mid haddaad u Yeedhaan iyo haddaad ka Aamustaanba.'),
(1148, 7, 194, 'Kuwaad caabudaysan (U yeedheysaan) Eebe ka Sokow waa Addoomo idinkoo kala ah, bal u Yeedha oo haydin Yeeleen (Aqbaleen) haddaad Run sheegaysaan.'),
(1149, 7, 195, 'Ma waxay Leeyihiin lugo ay ku Socdaan, mise waxay Leeyihiin Gacmo ay wax ku qabtaan mise waxay Leeyihiin indho ay wax ku arkaan, mise waxay leeyihiin Dhago ay wax ku Maqlaan, waxaad Dahdaa u Yeedha Shurakadiinna ina Dhagra hana i sugina.'),
(1150, 7, 196, 'Waligaygu waa Ilaaha soo Dejiyey Kitaabka isagaana u Gargaara kuwa Suuban.'),
(1151, 7, 197, 'Waxaad Caabudaysaan Eebe ka Sokow ma karaan Gargaarkiinna Naftoodana uma Gargaarayaan.'),
(1152, 7, 198, 'Hadday ugu Yeedhaan Hanuunkana ma Maqlaan waxaadna aragtaa iyagoo ku soo Eegi oon waxba Arkayn.'),
(1153, 7, 199, 'Qaado waxa Fudud Farna Wanaagga kana Jeedso Jaahiliinta.'),
(1154, 7, 200, 'Hadduu ku Waswaasiyo Shaydaan waswaasiye ka Magangal Eebe isagaa Maqle oge ahe.'),
(1155, 7, 201, 'Kuwa Dhawrsaday hadduu Taabto Shar Shaydaan way Xusuustaan (Waantoomaan) wayna Arkaan Xaqa (Toonsaadaan).'),
(1156, 7, 202, 'Walaalaha Shaydaankuse waxay ku Kaalmeeyan Baadida kamana Daalaan.'),
(1157, 7, 203, 'Haddaydaan ula Imaanin Aayad waxay Dhahaan Maxaad u soo dooran wayday, waxaad dhahdaa waxaan uun Raaci wixii layga waxyooday Xagga Eebahay Kaasina waa Xujooyin Xagga Eebihiin ka ahaatay iyo Hanuunka iyo Naxariista Ciddii Rumayn (Xaqa).'),
(1158, 7, 204, 'Markii la Akhriyo Quraanka Dhagaysta una Dhag raariciyana in laydiin Naxariisto.'),
(1159, 7, 205, 'U Xus Eebaha Naftahaa Naftaada Khushuuc (Baryo) iyo Cabsi, iyo Qaylo la\'aan hadal, Aroor iyo Galabba, hana Noqon kuwa Halmaama (Xuska Eebe).'),
(1160, 7, 206, 'Kuwa Eebe agtiisa ah iskama Kibriyaan Cibaadadiisa wayna u Tasbiixsadaan (Nasahaan) Isagayna u Sujuudaan.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Waxay ku warsan Dadku (Xukunka) Qaniimada, waxaad dhahdaa Qanimada waxaa iska leh Eebe iyo Rasuulka ee ka Dhawrsada Eebe Wanaajiyana Xaalka Dhexdiinna ah Eebana Adeeca iyo Rasuulkiisa haddaad kuwo Rumeeyey tihiin.'),
(1162, 8, 2, 'Mu\'miniinta dhabta ah waa uun kuwa marka Eebe la Xuso ay Cabsato Quluubtoodu, marka lagu Akhriyo korkooda Aayaadka Eebana ay Iimaan u kordhiso, Eebahood uun tala saarta,'),
(1163, 8, 3, 'Ee ah kuwa ooga Salaadda waxaannu ku Arzaaqnayna wax ka Bixiya.'),
(1164, 8, 4, 'Kuwaasaana ah Mu\'miniinta Xaqa ah, waxayna Mudan Jannooyin Eebahood agtiisa ah, iyo Dambi Dhaaf iyo Rizqi Sharaf leh.'),
(1165, 8, 5, 'Siduu Kaaga Bixiyey Eebaha Gurigaaga si Xaq ah (Jahaad) Koox Mu\'miniinta ka mida way Nacayeen.'),
(1166, 8, 6, 'Waxayna kugula Murmi Xaqa intuu u Cadaaday sidii iyagoo loo wado Geeri iyagoo Eegi.'),
(1167, 8, 7, 'Xusuuso markuu idiin Yaboohay Eebe Labadii Kooxood midkood (Colkii iyo Safarkii) idiin Subnaatay idinkoo Jeeel in Midda Tabarta Yar idiin ahaato, Eebana wuxuu dooni inuu Xaqa ky sugo Kalimadiisa, Gooyana Cidhibta Gaalada.'),
(1168, 8, 8, 'Si uu Xaqa u sugo, una Buriyo Baadhilka (Xumaanta) haban Naceen Dambiilayaashuye.'),
(1169, 8, 9, 'Xusuusta markaad ka Gargaar warsanayseen Eebe oo uu idinku Ajiibay waxaan idinku Xoojin Kun Malaa\'igta ka mida oo Israacraacsan.'),
(1170, 8, 10, 'Wuxuu Eebe u Yeelay Bishaaro iyo inuu Xasilo Qalbigiinnu, Gargaarra waxaan Eebe Agtiisa ahayn ma aha, Eebana waa Adkaade Fal San.'),
(1171, 8, 11, 'Xusuustana markuu idinku Daboolay Hurdo aaminnimo Darteed idinkagana soo Dejiyey Samada Biyo inuu idinku Daahiriyo idinkagana Tegsiiyo Waswaaska Shaydaanka iyo innu ku Adkeeyo Quluubtiinna kuna Sugo Gommadihiinna.'),
(1172, 8, 12, 'Markuu u waxyoon Eebahaa Malaa\'igta anaa idinla jira ee suga kuwa Xaqa Rumeeyey (Xoojiya) waxaan ku Tuuri Quluubta kuwa Gaaloobay Argagax ee ka Garaaca Luqunta korkeeda kana Garaaca Xubnaha faraha.'),
(1173, 8, 13, 'Maxaayeelay waxay Khilaafeen Eebe iyo Rasuulkiisa, Ciddii Khilaafta Eebe iyo Rasuulkiisana Eebe waa u Ciqaab daranyahay.'),
(1174, 8, 14, 'ee Arrintaas Dhadhamiya, Gaalana waxaa u Sugnaaday Cadaabka Naarta.'),
(1175, 8, 15, 'Kuwa Xaqa Rumeeyow hadaad la Kulantaan Kuwa Gaaloobay idinkoo isusoo Dhawaan ha u jeedinina dabada (ha cararina).'),
(1176, 8, 16, 'Ruuxi u Jeediya Maalintaas Dabadiisa (Carara) isagoo u Xeelaysanayn Dagaal ama u Dumayn Koox wuxuu la Noqdaa Cadho Eebe Hooygiisuna waa Jahannamo iyadaana u Xun meel loo ahaado.'),
(1177, 8, 17, 'Idinku maydaan ku layn Gaalada (Xooggiinna) Eebaase laayey, mana Aadan Ganayn markaad ganaysay Eebaase Ganay (ku Hagaajiyey) inuu u Niemeeyo Mu\'miniita Niemo Wanaagsan, Eebana waa Maqle Og.'),
(1178, 8, 18, 'Arrintaasna waxaa ugu waean in Eebe uu Tabaryarayn Dhagrate Gaalada.'),
(1179, 8, 19, 'Haddaad kala Xukumid Dalabteen Gaaloy waxaa idiin Yimid Gargaarkii (Nabiga) inaadse Reebtoontaan yaa idiin Khayr roon, haddaad ku noqotaan Xumaantana waxaannu ku Noqon Gargaarka (Nabiga) waxna idiinma tarayso Kooxdiinnu haba badnadeene Eebana wuxuu la Jiraa Mu\'miniinta.'),
(1180, 8, 20, 'Kuwa (Xaqa) Rumeeyow Adeeca Eebe iyo Rasuulkiisa hana ka Jeedsanina idinkoo Maqli.'),
(1181, 8, 21, 'Hana Noqonina kuwii Yidhi Maqallay iyagoon wax Maqlayn.'),
(1182, 8, 22, 'Dhul Socod waxaa ugu Shar badan (Uxun) Eebe agtiisa ma Maqle (Xaqa) ma hadle oon kasayn (Wanaagga).'),
(1183, 8, 23, 'hadduu ku Ogyahay Eebe Khayr wuu maqashiin lahaa (Garansiin lahaa) hadduuse Maqashiiyo way Jeedsanlahaayeen (Kibirdartiis) iyagoo is dadbi.'),
(1184, 8, 24, 'Kuwa Xaqa Rumeeyow Maqla Eebe iyo Rasuulka Markuu idiinku Yeedho wax idin nooleeya (XAQA) Ogaadana in Eebe (Karo) inuu kala Dhexmaro Ruuxa iyo Qalbigiisa, oo Xaggiisa laydin soo Kulmin.'),
(1185, 8, 25, 'Ka Dhawrsada Fidmo oon ugu Dhacayn kuwii Dulmiga falay Gaar ahaan oo idinka mida, Ogaadana in Eebe Ciqaabtiisu Darantahay.'),
(1186, 8, 26, 'Xusuustana markaad ahaydeen kuwo yaroo lagu Tabar darraysto Dhulka (Makaad) idinkoo ka Cabsan in Dadku idin dafo oo markaas uu idin Dhaweeyey (Dumay) idinkuna Xoojiyey Gargaarkiisa, idinkuna Arzaaqay Wanaag (Xalaal) inaad ky Mahdisaan.'),
(1187, 8, 27, 'Kuwa (Xaqa) Rumeeyow ha Khayaamina Eebe iyo Rasuulka iyo Ammaanadiinna idinkoo Og.'),
(1188, 8, 28, 'Odaadana in Xoolihiinnu iyo Caruurtiinnu Fidno uun yihiin oo Eebe Agtiisa Ajir wayn yahay.'),
(1189, 8, 29, 'Kuwa (XAQA) Rumeeyow haddaad ka Dhawrsataan Eebe wuxuu idiin yeeli Faraj (iyo Nabadgalyo) wuxuuna Asturi Xumaantiinna «Dambigiinna», Wuuna idiin Dhaafi Eebana waa Fadli Wayne.'),
(1190, 8, 30, 'Xusuuso (Nabiyow) markay ku dhakrayeen Kuwii Gaaloobay inay ku Xidhaan, ama ku Dilaan ama ku Bixiyaan, way ku Dhakrayeen Eebaase ka abaal marin isagaana Abaal mariye Khayr roona ah.'),
(1191, 8, 31, 'Marka lagu Akhriyo Aayaadkanaga waxay Dhahaan waan maqallay haddaan doonana waan Dhahaynaa kanoo kale (Quraanka) kanu waxaan warkii kuwii hore ahayn ma aha.'),
(1192, 8, 32, 'Xusuuso markay Dheheen Eebow hadduu kanu Xaqa Agtaada ah yahay Nagaga soo daadi Dhagaxyo (Naara) Samada, ama noo keen Cadaab daran.'),
(1193, 8, 33, 'Eebase ma aha kii Cadaaba Adoo Dhexjooga, mana aha mid Cadaaba iyagoo Dambi dhaaf warsan.'),
(1194, 8, 34, 'Muxuusanse Eebe u Cadaabayn iyagoo ka Celin Dadka Masjidka Xurmada leh, mana aha Ehelkiisa, Ehelkiisu waxaan kuwa dhawrsada ahayn ma aha, Badan koodse ma Oga.'),
(1195, 8, 35, 'Salaaddooda Kacbada agteeda ahna ma aha waxaan Foodhi iyo Sacabbo Tumid ahayn, ee Dhadhamiya Cadaabka Gaalnimadiinna Darteed.'),
(1196, 8, 36, 'Kuwii Gaaloobay waxay ku Bixin Xoolahooda inay ka Celiyaan Dadka Jidka Eebe way Bixin doonaan waxayna ku noqon Qoomamo markaasaa laga Adkaan, kuwa Gaaloobayna Xaga Jahannamaa Loo kulmin.'),
(1197, 8, 37, 'in Eebe kala Bixiyo Xumaanta iyo Wanaagga, Yeelana Xumaanta Qaarkeed tan Kale Korkeeda uuna Kulmin Dhammaan uuna Yeeli Jahannamo kuwaasuna waa uun kuwa Khasaaray.'),
(1198, 8, 38, 'Ku Dheh kuwii Gaaloobay hadday ka Reebtoomaan (Gaalnimada) waxaa loo Dhaafi wixii hor maray, haddayse Daa\'imaan (Gaalnimada) waxaa tagtay «horraysay» jidkii kuwii hore.'),
(1199, 8, 39, 'la Dagaallama Galada intaan Gaalnimo iyo «Xumaan» laga helin una ahaato Diintu Dhammaan Eebe, hadday Reebtoomaan Eebe waxay Fali wuu Arkayaa.'),
(1200, 8, 40, 'Hadday Jeedsadaanna ogaada in Eebe yahay Gargaarihiinna isagaana u Fiican Gargaare.'),
(1201, 8, 41, 'Ogaadana waxaad Qanimaysataan in Eebe Shan Meelood Meel Leeyahay, iyo Rasuulku, iyo Qaraabada, iyo Agoonta, iyo Masaakiinta, iyo Socdaalka, haddaad tihiin kuwo Rumeeyey Eebe iyo waxaan ku Dejinnay Addoonkanaga Maalintii kala Bixinta, Maalintay kulmeen Labadii Col (Badar) Eeebana wax kasta (oo uu Doono) wuu karaa.'),
(1202, 8, 42, 'Xusuusta markaad ahaydeen Dhinicii u Dhawaa (Badar) Gaaladuna ahayd Dhinicii ka Fogaa (Badar) Safarkiina idinka Hooseeyey hadaad isu yahoohdan (Ballana) waad isku Khilaafi lahaydeen Ballanka, Eebaase Falay inuu Xukumo Amar uu Fali in Cidto, Ciddii Noolaanna si Cad u Noolaato, Eebana waa Maqle Og.'),
(1203, 8, 43, 'Markuu idinku Tusiyey Eebe Galada Hurdada iyagoo yar, haddu ku Tusiyo lyagoo Badanna waad Fashili lahaydeen ood ku Murmi lahaydeen Amarka, Eebaase Nabadgaliyey, Illeen wuu Ogyahay waxa Laabta ku Sugane.'),
(1204, 8, 44, 'Iyo markuu idin Tusiyey markaad Kulanteen iyagoo ku Yar indhihiinna, idinkuna yareeyey Indhahooda in Eebe Xukumo Amar uu Fali, Xagga Eebaana loo Celin Unuuraha.'),
(1205, 8, 45, 'Kuwa Xaqa Rumeeyow haddaad la Kulantaan Koox (Cadow ah) Sugnaada Xusana Eebe wax Badan inaad Liibaantaan.'),
(1206, 8, 46, 'Adeecana Eebe iyo Rasuulkiisa hana Murmina ood Fashishaan uu tagana Xooggiinnu Samra Eebe wuxuu la jiraa kuwa Samree (Adkaysta).'),
(1207, 8, 47, 'Hana ka mid Noqonina kuwii ka baxay Guryahooda Kibir iyo istuska Dadka Darttiis kana Celinaya (Dadka) Jidka Eebe, Eebana waa Koobay waxay Camal Fali.'),
(1208, 8, 48, 'Markuu u Qurxiyey Shaydaanku Camalkooda kuna Yidhi wax idinka Adkaan oo Dada ma jiro Maanta, anna kaalmeeyaan idiin ahay, markay isarkeen Labadii Kooxoodna wuxuu u gurtay Cidhibtiisa, wuxuuna yidhi anugu Bari baan idinka ahay, waxaana arkaa waxaydaan Arkin waxaana ka Cabsan Eebe Ilaahayna waa Ciqaab darane.'),
(1209, 8, 49, 'Markay dhahayeen Munaafiqiintu iyo kuwa Cudurku Qalbiga kaga jiro, waxaa Dhagray Kuwaas Diintooda, Ciddii Eebe tala saarata Ilaahay waa Adkaade Falsan.'),
(1210, 8, 50, 'Haddaad Aragto Markay Oofsan (Nafta ka qaadi) Malaa\'igtu kuwii Gaaloobay oy ka Garaaci Wajiyadooda iyo Gadaashooda iyagoo ku Dhihi Dhadhamiya Cadaabka Gubidda (waxaad arki lahayd Arrin wayn).'),
(1211, 8, 51, 'Arrintaasuna waa waxay hormarsatay Gacmihiinnu darteed Eebana ma Dulmiyo Addoomada.'),
(1212, 8, 52, 'Waaa Caadadii Fircoon Qomkiisii iyo kuwii ka horreeyey ee ka Gaaloobay Aayaadkii Eebe oo Eebe Qabtay Dambigooda Dartiis, Eebana waa Xoong badane Ciqaab daran.'),
(1213, 8, 53, 'Arrintaasna waa inaan Eebe doorinin Nicmo uu ugu Nicmeeyey Qoom intay ka Dooriyaan wax Naftooda ah, Eebana waa Maqla Og.'),
(1214, 8, 54, 'Waa Caadadii Fircoon Qoomkiisii iyo kuwii ka Horreeyey, waxay ka Gaaloobeen Aayadkii Eebahood markaasuu Halaagay Dambigooda Dartiis waxayna Dhammaan ahaayeen Daalimiin.'),
(1215, 8, 55, 'Dhul Socde waxaa ugu Shar badan Eebe Agtiisa kuwa Gaaloobay oon Rumeynin.'),
(1216, 8, 56, 'ee ah Kuwaad la Ballantay markaas Buriya Ballankooda mar kasta, oon Dhawrsanayn.'),
(1217, 8, 57, 'Haddaad ku Libaanto (Adkaato) Dagaalka ku Eri (xanuuji) kuwa ka Dambeeya inay Xusuustaan.'),
(1218, 8, 58, 'Haddaad ka Cabsato Qoom Khayaanadii u Tuur (Ogaysii) Ballankii si siman, Illeen Eebe ma Jeela Khaa\'imiinta.'),
(1219, 8, 59, 'Yeyna u malayn kuwii Gaaloobay inay Carari (Karaan) iyagu ma Daaliyaan (Eebe).'),
(1220, 8, 60, 'Ugu Darbada waxaad kartaan oo Xooga, iyo Darbidda Fardaha inaad ku Cabsi galisaan Colka Eebe iyo Colkiina (Galaada) iyo kuwa kale oo ka soo hadhay (Munafiqiinta) ma ogidin Eebaase Og, waxaad ku Bixisaan Jidka Eebana waa laydiin oofin (Dhammayn) idinkoon laydin Dulmiyeyn.'),
(1221, 8, 61, 'Hadday u Iishaan Gaaladu Nabad u lilo adna talana Saaro Eebe, Illeen isagaa uun Maqla ognee.'),
(1222, 8, 62, 'Hadday Doonaan inay ku Dhagraan waxaa Kaafiyahaaga ah Eebaha kugu Xoojiya Gargaarkiisa iyo Mu\'miniinta.'),
(1223, 8, 63, 'Isuna soo Dumay Quluubtooda, haddaad Bixiso waxa Dhulka ku Sugan oo Dhan Maadan isu Dunteen Quluubtooda, Eebaase isu Dumay Dhexdooda Illeen isagaa Adkaade falsan ehe.'),
(1224, 8, 64, 'Nabiyow waxaa kugu Filan Eebe Adi iyo inta ku Raacday ee Mu\'miniinta ah.'),
(1225, 8, 65, 'Nabiyow ku boorri mu\'miniinta Dagaalka hadday idinka Mid yihiin Labaatan Samra (Adkaysta) Waxay ka adkaan Labo Boqol, Hadday idinka Mid yihiin Boqol waxay ka Adkaan Kun ah kuwa Gaaloobay Illeen waa Qoomaan wax kasayne.'),
(1226, 8, 66, 'Hadda wuu idinka Fududeeyey Eebe wuxuuna ogyahay inaad tabar Yartihiin hadday idinka Mid yihiin Boqol Samra (Adkaysta) waxay ka Adkaan Labo Boqol, Hadday idinka Mid yihiin Kunna waxay ka Adkaan Labo Kun Idanka Eebe, ILaahayna wuxuu la Jiraa kuwa samra.'),
(1227, 8, 67, 'Kuma habboona Nabi inay u ahaato kuwo la qafaashay intuu kaga Jilciyo Dhulka Dhexdiisa (Gaalada) waxaad Doonaysaan Muuqa Addunyada Eebana wuxuu dooni Aakhiro Eebana waa Adkaade Falsan.'),
(1228, 8, 68, 'Hadduusan Jirin Kitaab Eebe oo horreeyey waxaa idin taaban lahaa waxaad qaadateen Darteed Cadaab wayn.'),
(1229, 8, 69, 'ee Cuna waxaad Qanimaysataan oo Xalaal Fiicana kana dhawrsada Eebe Illeen Ilaahay waa Dhaafe Naxariistee.'),
(1230, 8, 70, 'Nabiyow ku Dheh kuwa la Qafaashay ee Gaemihiinna ku jira hadduu ku Ogyahay Eebe Quluubtiina Khayr wuxuu idin siin mid ka Khayrroon wixii laydinka Qaaday wuuna idiin Dhaafi, Eebana waa Dhaafe Naxariista.'),
(1231, 8, 71, 'Hadday Doonaan Khiyaamadaadana waxay Khayaameen Eebe mar hore markaasuu idin Makaniyey Eebana waa Oge Falsan.'),
(1232, 8, 72, 'Kuwa Rumeeyey ee Hijrooday ee ku Jahaaday Xoolahooda iyo Naftooda Jidka Eebe iyo kuwa Dumay (soo Dhaweeyey) una Gargaaray kuwaasu Qaarkood waa Gargaaraha Qaar, kuwa Rumeeyey oon hijroonin Gargaarkooda waxba idinkama Saarra intay ka Hijrooni, haddayse idiinku Gargaar warsadaan Diinta korkiinna Gargaarbaa ah qaar Dhexdiinna Ballan yahay mooyee, Eebana waxaad Falaysaan wuu Arkaa.'),
(1233, 8, 73, 'Kuwa Gaaloobay Qaarkood Waa Sokeeyaha Qaar haddaydaan saas falinna (Ogaanna) Fidmaa ahaan Dhulka iyo Fasaad wayn.'),
(1234, 8, 74, 'Kuwa Rumeeyey ee Hijrooday ee ku Jahaaday Jidka Eebe iyo kuwa soo Dhaweeyey una Gargaaray kuwaasu iyagaa Mu\'miniina Dhaba ah, waxayna Mudan Dambi Dhaafid iyo Rizqi Sharafle.'),
(1235, 8, 75, 'Kuwa Rumeeya Gadaal oo Hijrooda oo Jahaada la Jirkiinna kuwaasu waa idinka mid, Qaraabadaana Qaarkeed Qaar ku Mudanyahay Xukunka Eebe Ilaahayna waxkasta waa ogyahay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Tani waa ka bari ahaasho Eebe iyo Rasuulkiisa kuwaad la Ballanteen oo Mushrikiinta (Gaalada) ah.'),
(1237, 9, 2, 'ee socda Dhulka afar bilood ogaadana inaydaan daalinayn Eebe iyo in Eebe uu dulleyn gaalada.'),
(1238, 9, 3, 'waana ogaysiin Eebe ka ahaaday iyo Rasuulkiisa oo Dadka Maalinta Xajka wayn in Eebe ka bari yahay Gaalada iyo Rasuulkiisu, haddaad Toobadkeentaanna saasaa idiin Khayrroon, haddaad jeedsataanna ogaada inaydaan Eebe Daalinayn, uguna Bishaareeya kuwii gaaloobay Cadaab daran.'),
(1239, 9, 4, '4 Kuwaad ballanteen Mooyee oo Gaalada ah markaasna aan idinka Nusqaaminin waxba, idiinkuna kaalmaynin cidna, ee dhameeya tan iyo ballankooda «Muddadood» Eebana wuxuu jecelyahay kuwa dhawrsada.'),
(1240, 9, 5, 'markay faydmaan bilaha xurmada leh ku dila gaalada meejaad ka heshaan qabtana (qafaasha) Go\'doomiya una fadhiista meel gabasho ah oo dhan, haddayse toobad keenaan oogaanna salaadda, bixiyaanna Sakada u sii daaya jidkooda Eebe waa dambi dhaafe Naxariistee.'),
(1241, 9, 6, 'mid ka mida gaaladana hadduu magangal idin waydiisto magangaliya intuu ka maqlo hadalka Eebe markaas Gaadhsiiya meeluu Aamin ku yahay, illeen waa qoom aan wax ogayne.'),
(1242, 9, 7, 'siday ugu ahaan gaalada ballan Eebe agtiisa iyo Rasuulkiisa agtiisa kuwaad kula ballanteen Masaajidka Xur- mada leh Agtiisa mooyee ee hadday idin toosnaadaan u loosanaada Eebe wuxuu jecel yahay kuwa Dhawrsadee.'),
(1243, 9, 8, 'waa sidee hadday idinka adkaadaan ayna idiin dbawrayn Qaraabanimo iyo ballan midna, waxay idinku raalli galin afkooda wayna diidi quluubtoodu, badankooduna waa faasiqiin.'),
(1244, 9, 9, 'waxay ku gataan aayaadka. Eebe qiimo yar waxayna ka leexiyeen jidka Eebe waxaana xun waxay falayeen.'),
(1245, 9, 10, 'umana dhawraan Mu\'min qaraabonimo iyo ballan (midna) kuwaasuna waa uun xadgudbayaal.'),
(1246, 9, 11, 'haddayse toobadkeenaan oo oogaan salaadda hixiyaanna sakada waa walaalihiina diinta waana u caddaynay aayaadka qoomkii wax garan.'),
(1247, 9, 12, 'haddayse buriyaan dhaartooda ballankoodii ka dib, duraanna diintiinna dila madaxda gaalada waa kuwaan dhaar lahayne inay ka joogan (Xumaanta).'),
(1248, 9, 13, 'miyeydaan la dagaallamayn qoom buriyey dhaartooda oo hamiyey (doo-nay) bixinta Rasuulka idinkuna billaabay markii horaba, miyaad ka cabsanaysaan Eebaa mudan inaad ka cabsataan haddaad Mu\'miniin tihiin.'),
(1249, 9, 14, 'la dagaallama, Eebe ha ku cadaabo gaemihiinnee hana dulleeyee hana idinku gargaaree oo bugsiiyo laabta qoom Mu\'miniina.'),
(1250, 9, 15, 'tegsiiyana cadhada quluubtooda, wuxuuna ka toobad aqbalaa eebe cidduu doono, Eebana waa oge falsan.'),
(1251, 9, 16, 'ma waxaad u malayseen in laydinka tegi isagoon Eebe muujin kuwa jahaaday oo idinka mida oon ka yeelanin wax ka soo hadhay Eebe iyo Rasuulkiisa iyo Mu\'miniinta Saaxiib, Eebana waa ogyahay waxaad falaysaan.'),
(1252, 9, 17, 'uma bannaana gaalada inay camiraan Masaajiidda Eebe iyagoo ku qiri Naftooda gaalnimo kuwaas waxaa buray camalkoodii naartana way ku dhex waari.'),
(1253, 9, 18, 'waxaa uun camira masaajidda Eebe cid rumaysay Eebe iyo maalinta dambe oo salaadda ooga oo bixiya sakada kana cabsanayn waxaan Eebe ahayn, waxay u dhawyihiin kuwaas inay ka mid noqdaan kuwa hanuunsan.'),
(1254, 9, 19, 'ma waxaad kala mid dhigteen waraabinta xaajiga iyo camiridda masaajidka xurmaysan ruux rumeeyey Eebe iyo maalinta dambe oo ku jahaaday jidka Eebe kuma sinna Eebe agtiisa, Eebana ma hanuuniyo qoomka daalimiinta ah.'),
(1255, 9, 20, 'kuwa rumeeyey (Xaqa) oo hijrooday oo ku jahaaday jidka Eebe xoolahooda iyo naftooda, yaa ku darajo wayn Eebe agtiisa, kuwaasina waa kuwa uun liibaanay.'),
(1256, 9, 21, 'wuxuu ugu bishaarayn Eebahood naxariis xaggiisa ah, ralli ahaansho iyo jannooyin oy ku mudan dhexdeeda niemo nagaadi ah «Joogto».'),
(1257, 9, 22, 'iyagoo ku waari dhexdeeda waligood Eebe agtiisaana Ajir wayn yahay.'),
(1258, 9, 23, 'kuwa (xaqa) rummeyow ha ka yeelanina Aabayaalkiin iyo walaalihiin sokeeye hadday ka jeelaadaan gaalnimada iimaanka, ciddiise ka sokeeye yeelata oo idinka mida kuwaasu iyaga umbaa daalimiina.'),
(1259, 9, 24, 'waxaad dhahdaa haddaad aabayaalkiin, caruurtiinna, walaalihiin, haweenkiinna, qaraabadiinna, xoolo aad kasbateen, ganacsi aad ka cabsanaysaan inuu baaro iyo guryo aad ka raalli tihiin ka jeeeeshihiin Ilaahay, rasuulkiisa iyo jahaad jidkiisa suga inta Eebe ka keeni amarkiisa, Eebana ma hanuuniyo qoomka faasiqiinta ah.'),
(1260, 9, 25, 'wuxuu idiinku gargaaray Eebe meelo badan iyo maalintii xunayn markay idin cajabisay badnidiinnii oona waxba iddin tarin dhulkuna idinku cidhiidyamay isagoo waasaca markaas aad jeedsateen idinkoo carari.'),
(1261, 9, 26, 'markaas Eebe ku soo dajiyey xasilkiisii Rasuulkiisii iyo Mu\'miniintii kuna soo dejiyey junuud aydaan arkayn uuna cadaabay kuwii gaaloobay, taasina waa abaalka gaalada.'),
(1262, 9, 27, 'markaas Eebe ka toobad aqbalo intaas ka dib cidduu doono Eebana waa dambi dhaaafe naxariista.'),
(1263, 9, 28, 'kuwa xaqa rumeeyow mushrikiintu (Gaalada) waa nijaas ee yeyna masaajidka xurmaysan u dhawaanin sanadkan ka dib, haddaad ka cabsataan saboolnimana wuxuu idinku hodmin Eebe fadligiisa hadduu doono illeen waa oge falsane.'),
(1264, 9, 29, 'la dagaallama kuwaan rumeyn Eebe iyo maalinta aakhiro oon xaaraan yeelayn wuxuu reebay Eebe iyo Rasuulkiisu ka diin dhiganayn diinta xaqa ah (islaamka) oo ah kuwa ehelu kitaabka ah yuhuud iyo nasaarada intay ka dhiibaan jisyada (iyadoo darban) iyaguna dullaysanyihiin.'),
(1265, 9, 30, 'waxay dhaheen yuhuud cusayr waa wiil Eebe, nasaarana waxay tidhi masiix (ciise) waa wiil eebe, kaasina waa hadalkoodii oy afka ka sheegeen, waxay shabahaan kuwii horey u gaaloobay hadalkoodii, Eebe hallaayo ee xaggee loo leexin.'),
(1266, 9, 31, 'waxay ka yeesheen culimadoodii iyo suufiyadoodii Eebayaal ilaahay ka sokow iyo ciise ibnu maryama waxaan cibaadada ilaahay kali ah ahaynna lama farin. Eebe mooyee ilaah kalana ma jiro waana ka nasanyahay waxay la wadaajin.'),
(1267, 9, 32, 'waxay dooni gaaladu inay ku damiyaan Nuurka Eebe afkooda Eebana wuu diidi dhamaystirka nuurka moyee haba naceen gaaladuye.'),
(1268, 9, 33, 'Eebe waa kan ku diray rasuulkiisa hanuun iyo diin xaqa inuu ka kor mariyo diimaha dhammaan haba naceen gaaladuye.'),
(1269, 9, 34, 'kuwa xaqa rumeeyow wax badan oo ka mida axbaarta (culimada yuhuud) iyo ruhbaantu (culimada Nasaarada) wxay ku cunaan xoolaha dadka xaq darro waxayna ka celiyaan (dadka xaqa) kuwa kaydsada dahabka iyo fiddada oon ku bixinayn jidka Eebe ugu bishaaree cadaab daran.'),
(1270, 9, 35, 'maalinta lagu gubi xoolahaas dartood naarta jahannamo ay ku guban wajiyadoodu iyo dhinacyadoodu iyo dhabarkoodu oo lagu odhan kani waa waxaad u kaydsanayseen Naftiinna ee dhadhamiya waxaad kaydsanayseen ciqaabtiisa.'),
(1271, 9, 36, 'tirada bilaha Eebe agtiisa waa Labiyo toban kitaabka Eebe dhexdiisa taniyo maalintuu abuuray samooyinka iyo dhulka, waxaana ka mid ah afar xurmo (gaara) leh waana taas diinta toosan ee ha dulmiyina dhexdooda naftiinna, lana dagaalama gaalada dhammaantiin, siday idiinla dagaalamayaan dhammaan ogaadana in Eebe la jiro kuwa dhawrsada.'),
(1272, 9, 37, 'dib u dhigidda (bilo) waa uun badsasho gaalnimo laguna baadiyeeyo kuwa gaaloobay, waxay banneeyaan sanad waxayna reebaan mid inay waafaqaan tirada Eebe reebay waxayna banneeyaan wax Eebe reebay, waxaa loo qurxiyey camalkoodii xumaa Eebana ma hanuuniyo qoomka gaalada ah.'),
(1273, 9, 38, 'kuwa xaqa rumeeyow maxaad leedihiin oo marka laydin dhaho ku baxa jidka Eebe aad ugu cuslaataan dhulka, ma waxaad ku ralli noqotaan nolosha adduun aakhiro ka sokow, nolosha adduunyo marka aakharo loo dayo waa uun raaxo yar.'),
(1274, 9, 39, 'haddaydaan bixin wuxuu Eebe idincadaabi cadab daran, wuxuuna idinku badali qoom aan idinka ahayn, waxna kama dhibaysaan Eebe, ilaahayna wax kasta oo uu doono waa karaa.'),
(1275, 9, 40, 'haddaydaan u gargaarin Nabiga waxaa u gargaaray Eebe markay bixiyeen kuwii gaaloobay isagoo labo midkooda markay godka ku jireen markuu ku lahaa saaxiibkiis ha walbahaarin Eebaa inala jirce, oo Eebe ku dajiyey xasiikiisa kuna xoojiyey Januud aydaan arkayn kana yeelay kalimadda kuwa gaaloobay tan hoosaysa, kalimadda Ilaahayna waa tan sarreysa, Eebana waa adkaade falsan.'),
(1276, 9, 41, 'baxa idinkoo fudud iyo idinkoo culus kiinajahaada xoolihiinna iyo naftiinna jidka Eebe, saasaana idiin khayrroon haddaad wax ogtihiin.'),
(1277, 9, 42, 'hadduu jiro muuqaal adduunyo oo dhaw iyo socdaal gaaban way ku raaci lahaayeen waxaase ku fogaaday socdaalkii, waxayna ku dhaaran Eebe haddaanu kari waan idinia bixi lahayn, waxayna halaagi naftooda Eebana waa ogyahay inay been sheegi.'),
(1278, 9, 43, 'Eebe ha ku caliyee maxaad u idantay intay kaaga cadaato kuwa runta sheegi ood ka ogaato Reenaalayaasha.'),
(1279, 9, 44, 'kaama idin waydiistaan kuwa rumeeyey Eebe iyo maalinta dambe inay ku jahaadaan xoolahooda iyo naftooda, Eebana waa ogyahay kuwa dhawrsada.'),
(1280, 9, 45, 'waxaase ku idin waydiin kuwaan rumaynin Eebe iyo maalinta dambe ayna shakiday quluubtoodu, waxayna ku noqnoqon (ku wareersanyihiin) shakigooda.'),
(1281, 9, 46, 'hadday doonaan bixid waxay u darban lahaayeen qalab, wuxuuse nacay Eebe Bixintooda wuuna dib mariyey waxaana lagu yidhi «gaaladii» la fadhiista kuwa fadhiya.'),
(1282, 9, 47, 'hadday baxaan dhexdiinna waxaan xuman ahayn idiinma kordhiyaan, waxayna u dagdagi lahaayeen fasaadinta dhexdiinna iyagoo idinla dooni fidmo, waxaana idin ku jira kuwo idiindhagaysan Eebana waa ogyahay daalimiinta.'),
(1283, 9, 48, 'waxay idinla dooneen fidno mar hore waxayna kuu gagadiyeen umuuraha intuu ka yimaado xaqu oo ka muuqdo amarka Eebe (xaqa) iyagoo nici.'),
(1284, 9, 49, 'waxaa ka mid ah kuwo dhihi i fasax hana i fidneyn waxayse ku dhaceen fidno jahannamana way koobtay gaalada.'),
(1285, 9, 50, 'hadduu wanaag ku soo gaadho wuu walbahaarinayaa Munaafiqiinta, hadday ku hesho dibaatana waxay dhahayaan waan ka dadaalay raaciddiisa, mar hore, wayna jeedsadaan iyagoo faraxsan.'),
(1286, 9, 51, 'waxaad dhahdaa naguma dhaco wax Eebe noo qoray mooyee, isagaa gargaarahannaga ah ee Ilaahay uun ha tala Saarteen Mu\'miniintu.'),
(1287, 9, 52, 'waxaad dhahdaa ma waxaad nala sugaysaan waxaan labada wanaag midkood ahayn (Guul ama shahaado) annaguna waxaan idin la sugaynaa inuu Eebe idinku assibo (ciqaabo) cadaab agtiisa ah ama gaemahanaga, ee suga annaguna waan idin la sugaynaaye.'),
(1288, 9, 53, 'waxaad dhahdaa Bixiya wax ama ogolaansho ama qasab marnaba laydinkama aqbalee, illeen waxaad tihiin qoom faasiqiina.'),
(1289, 9, 54, 'wax u diiday in la aqbalo bixinteeda oon ka gaaloowgooda Eebe iyo Rasuulkiisa ahayn majiro, mana yimaaddaan salaadda iyagoo wahsan mooyee, mana bixiyaan iyagoo neeeb mooyee.'),
(1290, 9, 55, 'yeyna ku yaab galinin xoolahoodu iyo caruurtoodu, wuxuu un dooni Eebe inuu ku ciqaabo nolosha adduun oy baxdo naftoodu iyagoo gaala ah.'),
(1291, 9, 56, 'waxay ku dhaaran Eebe inay idinla jiraan idinlamana jiraan, waase qoom cabsan.'),
(1292, 9, 57, 'hadday helayaan meel ay magan galaan ama godod (buuro) ama meel ay galaan way u jeedsan lahaayeen iyagoo deg degi.'),
(1293, 9, 58, 'waxaa ka mid ah kuwo kugu ceebin sadaqada (sakada) haddii la siiyana raali noqon haddaan la siininna cadhoon.'),
(1294, 9, 59, 'hadday ku raalli noqdaan waxay siin Eebe iyo rasuulkiisu oy dhahaan waxaana nagu filan Eebe wuxuu naga siin Eebe fadligiisa rasuulkiisuna wuu nasiin Anaguna Eebe unbaan wax ka doonaynaa (saasaa u khayroon).'),
(1295, 9, 60, 'sadaqada (Zakada) waxaa uun muta fuqarada, masaakiinta, kuwa ka shaqeeya, kuwa la soo dhawayn quluubtooda, kuwa la xorayn, kuwa (xaqa) ku daynoobay, jidka Eebe iyo soedaalka, waana wax Eebe Faral yeelay, ilaahayna waa oge falsan.'),
(1296, 9, 61, 'waxaa ka mid ah (munaafiqiinta) kuwa dhiba nabiga oo dhaha waa wax walba maqle, waxaad dhahdaa waa Khayr maqlihiinna wuxuuna rumayn Eebe, wuxuuna u rumayn Mu\'miniinta, waana u naxariista kuwa rumeeyey oo idinka mid ah, kuwa dhiba Rassulka Eebe waxay mudan cadaab daran.'),
(1297, 9, 62, 'waxay idiinku dhaaran Eebe inay idin raalli galinayaan, Eebe iyo Rasuulkiisa yaa mudnaan badan inay raalli galiyaan hadday Mu\'miniinyihiin.'),
(1298, 9, 63, 'miyeyna ogayn ciddii la dagaalanta (oo khilaafta) Eebe iyo rasuulkiisa inuu mudan naarta jahannama, kuna waari dhexdeeda, arrintaasuna waa dulli wayn.'),
(1299, 9, 64, 'waxay ka digtoonyihiin munaafiqiintu in lagu soo dajiyo suurad ka warrami waxa quluubtooda ku sugan, waxaad dhahdaa jeesjeesa Eebe wuxuu soo bixin waxaad ka digtoontihiin (cabsanaysaan).'),
(1300, 9, 65, 'haddaad warsato waxay dhihi waan un yar tiimbanaynay oon ciyaaraynay, waxaad dhahdaa ma Eebe iyo aayaadkiisa iyo Rasuulkiisa yaad ku jees jeesayseen.'),
(1301, 9, 66, 'ha cudur daaranina waad gaalowdeen Rumayn ka dib, haddaan cafino koox idinka mid ah waxaan cadaabaynaa koox illeen iyagu waa dambiilayaale.'),
(1302, 9, 67, 'munaafiqiintu rag iyo haweenba qaarkood qaarka kale yuu ka ahaaday waxayna faraan sumaanta, waxayna ka reebaan wanaagga, waxayna laabaan gaemahooda (wax ma bixiyaan) Eebayna halmaameen isna aagayeeldarro yuu kula dhaqmay, munaafiqiin iyaga umbaa faasiqiin ah.'),
(1303, 9, 68, 'wuxuuna Eebe u yaboohay Munaafiqiintaa rag iyo haweenba iyo gaalada Naarta Jahannamo wayna ku dhex waari iyadaana ku filan, waxaana lacnaday Eebe waxayna mudan cadaab joogta ah.'),
(1304, 9, 69, 'waxaad la midtihiin kuwii idinka horreeyey, waxayna ahaayeen kuwo idinka badan xoog iyo xoolo iyo caruur waxayna ku raaxaysteen nasiibkoodii idinkuna waxaad raaxaysatecn nasiibkiin siday ugu raaxaysleen kuwii idinka horreeyey nasiibkoodii, waadna dhumbateen xumaanta siday u dhumbadeen, kuwaas waxaa buray camalkoodii adduun iyo aakhiraba kuwaasyna iyaga umbaa khasaaray.'),
(1305, 9, 70, 'miyeyna soo gaadhin warkii kuwii ka horreeyey oo ah qoomkii nabi Nuux, Caad, Thamuud, Qoomkii Nabi Ibraahiim, madyan dadkeedi iyo Qoomkii Nahi Luudh, waxay ula timid rasuuladoodii xujooyin, Eebana ma aha mid dulmiya iyagaase naflooda dulmiya.'),
(1306, 9, 71, 'Mu\'miniinta rag iyo Haweenba qaarkood qaarka (kale) waa u Sokeeye waxayna faraan wanaagga kana reebaan xumaanta, waxayna oogaan salaadda, waxayna bixiyaan Zakada, waxayna Addeecaan Eebe iyo Rasuulkiisa, kuwaas waxaa u naxariisan Eebe, illeen Eebe waa adkaade fal sane.'),
(1307, 9, 72, 'wuxuu u yaboohay Eebe Mu\'miniinta Rag iyo haweenba Jannooyin ay socoto dhexdeeda Wabiyaalkii iyagoo ku waari dhexdeeda, iyo guryo wanaagsan oo Jannada cadnin (Nagaadiga) Raalli ahaanshaha Eebaana ka wayn, arrintaasuna waa uun liibaanta wayn.'),
(1308, 9, 73, 'Nabiyow la jahaad gaalda iyo Munaafiqiinta, kuna darrow dheelmadkooduna waa naarta Jahannamo iyadaana u xun meel loo ahaan.'),
(1309, 9, 74, 'waxay ku dhaaran inayna dhihin (hadal xun) iyagoo yidhi hadal gaalnimo ayna gaaloobeen Islaanimo (muuqa) kadib, ayna dooneen waxayna gaadhin, waxayna ku naceen ku hodanaynta Eebe iyo Rasuulkiisa fadligiisa hadday toobadkeenaan khayr bay u tahay, hadday jeedsadaanna wuxuu Eebe cadaabi cadaab daran adduun iyo aakhiraba, gargaarana kuma leh dhulka dhexdiisa iyo sokeeye midna.'),
(1310, 9, 75, 'waxaa ka mid ah kuwo la ballamay Eebe hadduu wax ka siiyo fadligiisa inuu sadaqaysto noqdana kuwa suuban.'),
(1311, 9, 76, 'markuu wax ka siiyey fadligiisa ay ku bakhayleen jeedsadeenna iyagoo diidi.'),
(1312, 9, 77, 'wuxuu ku reebay quluubtooda munaafiqnimo tan iyo maalintay la kulmi ka baajintooda Eebe waxay kula ballantameen iyo beenintoodii darteed.'),
(1313, 9, 78, 'miyeyna ogayn in Eebe ogyahay qarsoodigooda iyo muuqooda iyo in Eebe ogyahay waxa maqan.'),
(1314, 9, 79, 'waa kuwa ku ceebayn kuwa sadaqaysan oo mu\'miniinta ah sadaqadooda iyo kuwaan helaynin tabartooda mooyee, way ku jees jeesi Eebana wuu kula macaamiloon abaalkooda waxayna mudan cadaab daran.'),
(1315, 9, 80, 'ama u dambidhaaf warso ama ha u dambidhaaf warsanin, haddaad u dambi dhaaf warsato toddobaatan jeer uma dambidhaafo Eebe, ileen iyagu waxay ka gaaloobeen Eebe iyo rasuulkiisa, eebana ma hanuuniyo qoomka faasiqiinta ah.'),
(1316, 9, 81, 'waxay ku farxeen kuwii ka dib dhacay fadhigoodii rasuulka gadaashiisa, waxayn naceen inay ku jahaadaan xoolahooda iyo naftooda jidka Eebe, waxayna dhaheen ha ku bixina kulaylka, waxaad dhahdaa naarta jahannamo yaa ka kulayl daran hadday wax kasi.'),
(1317, 9, 82, 'ee ha qosleen wax yar (adduunka) hana ooyeen wax badan (aakhiro) iyadoo laga abaal marin waxay kasbadeen.'),
(1318, 9, 83, 'hadduu kuu soo celiyo Eebe koox ka mid ah oy kaa idin waydiistaan bixid waxaad dhahdaa bixi maysaan lajirkayga waligiin, lana dagaallami maysaan Col lajirkayga illeen waxaad jeclaateen fadhi markii horaba ee la fadhiya kuwa dib maray.'),
(1319, 9, 84, 'ha ku tukanin mid ka mid ah oo dhinta waligeed, hana kor istaagin qabrigiisa, illeen waxay ka gaaloobeen Eebe iyo Rasuulkiisa waxayna dhinteen iyagoo faasiqiin ah.'),
(1320, 9, 85, 'ee yeyna ku yaab galinnin xoolahoodu iyo caruurtoodu, wuxuu uun Eebe dooni inuu ku cadaabo adduunka ay baxdana naftooduna iyagoo gaalo ah.'),
(1321, 9, 86, 'marka la soo dejiyo suurad (dhihi) rumeeya Eebe jahaadana la jirka Rasuulkiisa waxaa idan ku warsada kuwa awoodda leh oo ka mid ah waxayna dhahaan na daa aan noqonno kuwa fadhiyee.'),
(1322, 9, 87, 'waxay ka raalli noqdeen inay noqdaan kuwa dib maray, waxaana la daboolay quluubtooda waxna ma kasayaan.'),
(1323, 9, 88, 'Rasuulkase iyo kuwa rumeeyey ee la Jira way ku jahaadaan xoolahooda iyo naftooda waxaana kuwaas u sugnaaday khayraad, kuwaasuna waa uun kuwa Liibaanay.'),
(1324, 9, 89, 'wuxuuna u Darbay Eebe Jannooyin oy socoto dhexdeeda wabiyadii iyagoo kuwaari dhexdeeda taasina waa liibaan wayn.'),
(1325, 9, 90, 'waxaa yimid kuwii cudurdaaranayey oo Reebaadiyaha ahaa in loo idmo (Dib mar) waxaana iska fadhiistay kuwii beeniyey Eebe iyo Rasuulkiisa, waxaana ku dhici kuwii Gaaloobay oo ka mida cadaab daran.'),
(1326, 9, 91, 'dhib ma saara kuwa tabarta yar, kuwa huka iyo kuwaan helayn waxay bixiyaan, hadday u naseexeeyaan (Diinta) Eebe iyo Rasuulkiisa, kuwa sama falana jid laguma leh Eebana waa Dambi dhaafe Naxariista.'),
(1327, 9, 92, 'kuwiina dhib masaarra kuu yimid inaad xambaarto (Gaadiid siisid) markaas aad ku tidhi ma helayo waxaan idinku xambaaro, markaas jeedsaday iyagoo indhahoodu la daadan illin walbahaar inayna helin waxay bixiyaan.'),
(1328, 9, 93, 'waxaa uun jid laguleeyahay kuwa ku idan warsada iyagoo hodanna oo ka raati noqday inay ka midnoqdaan kuwa had hay (Xaaska) wuxuuna Eebe daabacay quluubtooda waxna ma oga.'),
(1329, 9, 94, 'way idiin cudurdaaran markaad u noqotaan xaggooda, waxaad dhahdaan ha cudurdaaranina idiin rumeynmenee, wuu nooga warrdmay Eebe xaalkiinne, Eebana wuu arkaa camalkinne iyo rasuulkiisu, markaasna waxaa laydiin Celin Eebaha og waxa maqan iyo waxaa muuqdaba oo idiinka warrami waxaa camal falayseen.'),
(1330, 9, 95, 'way idiin dhaaran markaad u gadoontaan xaggooda inaad ka jeedsataan ee iskaga jeedsada iyagu waa wasakhe hoygooduna waa jahannamee waana ka abaal marin waxay kasbanayeen.'),
(1331, 9, 96, 'way idiin dhaaran inaad ka raalli noqotaan, haddaad ka raalli noqotaan Eebe kama raalli noqdo qoomka faasiqiinta ah.'),
(1332, 9, 97, 'Reer baadiyahaa ugu daran Gaalnimada iyo Munaafaqnimada, mudanna inayna waxba ka ogayn xuduudda (Soohdimaha) waxa Eebe ku soo Dejiyey Rasuulkiisa, Eebena waa oge fal san.'),
(1333, 9, 98, 'Reer baadiyaha waxaa ka mida kuwo kayeelan waxay bixin dayn Bax oo kale, oo idin la sugi dhibaato korkooda ha ahaato wareegga xumaanta, Eebana waa maqle og.'),
(1334, 9, 99, 'Reer baadiyaha waxaa ka mida kuwa rumeyn Eebe iyo maalinta aakhiro kana yeelan wuxuu bixin dhawaansho Eebe agtiisa, iyo u ducaynta Rasuulka (Iyaga) taasi waa dhawaanshahooda wuxuuna galin Eebe naxariistiisa, illeen Eebe waa dambi dhaafe naxariista.'),
(1335, 9, 100, 'kuwii ku dheereeyey «khayrka» ee horreeyey oo ka mida Muhaajiriinta iyo Ansaar iyo kuwii ku raacay wanaagga Eebaa ka raalli noqday iyana way ka raalli noqdeen, wuxuuna u darbay Jannooyin ay socoto dhexdeeda Wabiyadii iyagoo ku waari waligood, taasina waa Liibaan wayn.'),
(1336, 9, 101, 'gaararkiinna waxaa ka mida reebaadiye Munaafiqiina, Reer Madiinana (waa kamid) waxay ku madax taageen munaafaqnimo, ma ogidin annagaase og, waxaannu cadaabi Labo jeer, markaasaa loo celin cadaab wayn.'),
(1337, 9, 102, 'kuwo kalana waxay qirteen Dambigooda, waxayna isku khaldeen Camal fiican iyo mid kale oo xun, Eebe wuxuu mudanyahay inuu ka toobad aqbalo, illeen Eebe waa Dambi dhaafe naxariista.'),
(1338, 9, 103, 'Ka qaad xoolahooda Xagooda sadaqo (zakada) ood ku daahirin kuna Nadiifin una ducee illeen Ducadaadu Waxay u noqon Xasile, Eebana waa maqle og.'),
(1339, 9, 104, 'Miyayna Ogayn in Eebe ka aqbalo Toobadda Addoomadiisa Kana qaado Sadaqada (Aqbalo) iyo in Eebe yahay kan toobad aqbalka Badan ee Naxariista.'),
(1340, 9, 105, 'Waxaad dhahdaa Camal fala wuu arki Eebe Camalkiinna iyo Rasuulkiisu iyo mu\'miniintu Waxaana La idiin celin xagga (Eebaha) og Waxa maqan iyo waxa Jooga idiinkana Warrami (abaalmarin) waxaad falayseen.'),
(1341, 9, 106, 'Kuwo kalana waxaa dib loogu dhigay amarka Eebe inuu Cadaabo iyo inuu Ka toobad aqbalo, Eebana waa oge Falsan.'),
(1342, 9, 107, 'Waxaa ka mida kuwo u yeeshay Masaajid, gaalnimo, kala goynta Mu\'miniinta iyo darbidda Cid la dagaallanta Eebe iyo Rasuulkiisa mar hore waxayna ku dhaaran inaanaan doonayn Waxaan wanaag ahayn Eebana waa ogyahay inay been sheegi.'),
(1343, 9, 108, 'ha joogsan dhexdiisa waligaa Masaajid lagu billabay maalintii horaba Dhawrsasho yaa mudan inaad joogsato Dhexdiisa (Masaajidka Nabiga iyo Quba) waxaa dhex jooga Niman jecel inay is Daahiriyaan Eebana wuu jecel yahay Kuwa is daahiriya.'),
(1344, 9, 109, 'Ma Ruux ku billaabay dhismihiisa ka dhawrsasho Eebe iyo raalli noqoshadiisa yaa Khayrroon mise Ruux ku billaabay dhismihiisa God dumi dhinaciis oo kula dhici Naarta Jahannamo Eebana ma hanuuniyo Qoomka daalimiinta ah.'),
(1345, 9, 110, 'Kama tago dhismahooda ay dhiseen inuu ku Jiro shaki Quluubtoodu intay ka Kala go\'do «dhimatana» Quluubtoodu, Eebana waa oge falsan.'),
(1346, 9, 111, 'Eebe wuxuu kaga gatay Mu\'miniinta Naftooda iyo Xoolahooda inay janno u ahaato ku dagaallami Jidka Eebe oy waxna dili iyana la dili, waana yabooh ku sugan (dhaba) Tawreed iyo Injiil iyo Quraanka, yaa ka oofin badan ballankiisa Eebe ku bishaaraysta gadiddii ood gaddeen kaasina waa uun Liibaanta wayn.'),
(1347, 9, 112, 'waa kuwa toobadkeena, ee caabuda (Eebe) ee mahdiya, ee saa\'ixiinta ah (Sooma oo Jahaada) ee Rukuca ee Sujuuda, ee fara wanaagga reebana xumaanta, ee dhawra Xuduudda Eebe, ee u Bishaaree Mu\'miniinta.'),
(1348, 9, 113, 'kuma habboona Nabiga iyo kuwa Rumeeyey (Xaqa) inay u dambi dhaaf warsadaan Mushrikiinta «gaalada» haba ahaadeen qaraabadoodee intay u caddaatay kadib inay Ehelka Naarta Jaxiima yihiin.'),
(1349, 9, 114, 'ma ahayn u dambi dhaaf doonidii Nabi Ibraahim Aabihiis ballan uu u yaboohay mooyee, markayse u caddaatay inuu Col Eebe yahay wuu iska bari yeelay Xaggiisa (Nabi) Ibraahim wuxuu ahaa duco (Baryo) badanaha (Eebe) oo dul badan.'),
(1350, 9, 115, 'Eebana ma aha mid dhumiya qoom intuu hanuuniyey ka dib intuu uga caddeeyo waxay ku dhawrsadaan, Eebana wax kasta waa ogyahay.'),
(1351, 9, 116, 'Eebena waxaa u sugnaaday Xukunka Samooyinka iyo Dhulka isagaana wax nooleeya waxna dila, isagaana wax kasoo hadhayna oo wali ah ama gargaara ah ma lehidiin.'),
(1352, 9, 117, 'wuxuu Eebe ka toobad aqbalay Nabiga, Muhaajiriinta iyo Ansaartii raaeday Saacaddii cidhiidhiga intay u dhawaatay inay iilato quluub koox ka mida ka dib, markaasuu ka toobad aqbalay, illeen waa u turaha u naxariistee.'),
(1353, 9, 118, 'saddexdiina wuu (ka toobad aqbalay) la dib mariyey intuu kaga cidhiidhyamo dhulku isagoo waasac ah, kuna cidhiidhyantay naftooda una maleeyeen (Yaqiinsaden) inayna jirin meel la magan galo oon Eebe ahayn, markaas Eebe ka toobad aqbalay inay toobadkeenaan, Eebana waa toobad aqbale Naxariista.'),
(1354, 9, 119, 'kuwa xaqa rumeeyow ka dhawrsada Eebe Noqdana la jirka kuwa runta badan.'),
(1355, 9, 120, 'kuma habboona (uma bannaana) reer Madiino iyo kuwa gaararkeeda ah oo Baadiyaha Carbeeda inay ka gadaal maraan Rasuulka Eebe oy kana Jeclaadaan naftooda Naftiisa, illeen wixii ku dhaca oo oona, ama dhiba ama gaajo ah Jidka Eebe dhexdiisa ama meejey ku joogsadaan oo Cadho galin Gaalada iyo waxay Gaadhsiiyaan Colka (Dhammaan) waxaa loogu Dhigi camal suuban, Eebana ma halleeyow Ajirka samo falayaasaha.'),
(1356, 9, 121, 'waxay Bixiyaan oo yar ama wayn iyo Togay gooyaanna waa loo qori inuu ka Abaal mariyo Eebe wanaagga waxay falayeen.'),
(1357, 9, 122, 'mana aha inay Mu\'miniinta oo dhan Baxaan, ee may Koox kastaha qaar ka baxdo inay Diinta kasaan, una igaan dadkooda markay u soo noqdaan waxay u dhawdahay inay digtoonaadaane.'),
(1358, 9, 123, 'Kuwa Xaqa rumeeyow la dagaallama kuwa idinla Xidhiidha ee gaalada ah, hana idinka heleen adayg, ogaadana in Eebe la Jiro kuwa Dhawrsada.'),
(1359, 9, 124, 'marka la soo Dejiyo Suurad waxaa ka mida kuwo (Munaafiqiina) dhihi kiinay u kordhisay Iimaan, kuwa xaqa rumeeyeyse waxay u kordhisat Iimaan iyagoo Bishaaraysan.'),
(1360, 9, 125, 'kuwa quluubta ka bukase waxay u kordhisaa xuman xumaantoodii (Kale) wayna dhintaan iyagoo gaalo ah.'),
(1361, 9, 126, 'miyeyna arkayn in la Imtixaamo sanad kasta hal jeer ama labo jeer markaas ayan toobadkeenin xusuusanaynnam'),
(1362, 9, 127, 'marka la soo Dejiyo Suurad way dayaan Munafiqiinta qaarkood waar waxayna isdhahaan ruuxna maydin arkaa markaasay gadoomaan, Eebe ha iilo Quluubtooda illeen waa kuwo aan wax kusayn.'),
(1363, 9, 128, 'waxaa idiin yimid Rasuul idinka mida oyna ku darantahay waxa idin dhiba, idiin kuna dadaali, una turid iyo naxariis badan Mu\'miniinta.'),
(1364, 9, 129, 'haddayse jeedsadaan Dadku waxaad dhahdaa Kaalmeeyahaygu waa Ilaahay \'Eebe mooyee Ilaah kalana ma jiro isagaana talo saartay waana Eebaha Carsghiga wayn.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Kalimada hore waxay ku tusin Mucjisada Quraanka, Taasin waa Aayaadka Kitaabka falkasan.'),
(1366, 10, 2, 'Dadka ma waxaa la yaab ku Noqotay inaan u waxyoon Nin ka mida u Dig Dadka, uguna Bishaaree kuwa Rumeeyey inay ku leeyihiin Camal fiican Eebahood agtiisa, waxay Dheheen Gaaladiina kani waa Sixirrow Cad.'),
(1367, 10, 3, 'Eebihiin waa kan ku Abuuray Samooyinka iyo Dhulka Lix Maalmood markaas ku istiwooday Carshiga Isagaana maamula Amarka wax Shafeecina Idinkiisa la\'aantiisa ma jiro, Kaasi waa Eebihiinna idin Barbaariya ee Caabuda, Miyeydaan wax Xusuusanayn.'),
(1368, 10, 4, 'Xaggiisaa laydiin Celin Dhammaan Yabooha Eebena waa Xaq, Eebaa Billaaba Abuurka Haddana soo Celin inuu ku Abaal mariyo kuwa Rumeeyey (Xaqa) oo falay Camal Fiican, si Caddaalada, kuwa Gaaloobayna waxaa u Sugnaaday Cabbid kulul iyo Cadaab Daran Gaalnimadoodai Darteed.'),
(1369, 10, 5, 'Eebe waa kan ka yeelay Qorraxda Ifid Dayaxana Nuur una Qaddaray Meelo ay Socdaan, inaad Ogaataan Tirada Sanooyinka iyo Xisaabta wuxuu u Ahuuray Eebe Arrintaas Xaq Wuxuuna u Caddayn Aayaadka Ciddii wax Og.'),
(1370, 10, 6, 'Ishadalka Habeenka iyo Maalinta iyo Abuurka Eebe ee Samooyinka iyo Dhulka aayadbaa ugu sugan Ciddii Dhawrsan.'),
(1371, 10, 7, 'Kuwaan Rajaynayn la Kulankanaga kuna Raalli Noqday Nolosha Dhaw kuna Xasilay iyo kuwa Halmaansan Aayaadkannaga.'),
(1372, 10, 8, 'Kuwaas Hooygoodu waa Naar waxay Kasbanayeen Darteed.'),
(1373, 10, 9, 'Kuwa Rumeeyey Xaqa oo falay Camal wanaagsan wuxuu ku Hanuunin Eebahood Iimaankooda waxaana socon Dhexdooda Wabiyadii Jannooyinka Naciimo Dhexdeeda iyagoo ku Sugan.'),
(1374, 10, 10, 'Yeedhidooduna dhexdeeda waa Eebow waad Nasahantahay, Bariidadooduna waa (Nabadgalyo) Salaan, Ducadooduna waxaa ugu Dambayn Mahad Ilaahbaa leh Eebaha Caalamka ah.'),
(1375, 10, 11, 'Haddu Eebe u Dedejiyo Dadka Xumaanta siday khayrka u Dedejistaan waxaa loo Dhamayn lahaa Ajashooda, Waanuse ku Dayn Kuwaan Rajaynayn la kulank nanaga Fasaadkooda iyagoo ku Wareersan.'),
(1376, 10, 12, 'Markuu Taabto Dadka dhib wuu Na baryayaa isagoo Jiifa, ama Fadhiya ama Taagan, markaan ka faydno Dhibkana wuu iska Socdaa sidii isagoon Nabaryinba Dhibkii Taabtay, Saasaa loogu Qorxiyey Xadgudbayaasha waxay Falayeen.'),
(1377, 10, 13, 'Waxaan Halaagnay Quruumo idinka horreeyey markay Dulmi faleen, waxayna ula Timid Rasuuladoodii Xujooyin, mana ay an Rumeynayn, Saasaana ku abaal marinaa Qoomka Dambiilayaasha ah.'),
(1378, 10, 14, 'Markaasaan idinka yeelay kuwo u hadha Dhulka Gadaashood inaan Eegno sidaad Fashaan.'),
(1379, 10, 15, 'Marka lagu Akhriyo Korkooda Aayaadkanaga oo Cad Cad waxay Dhahaan kuwaan Rajaynayn la kulankanaga noo keen Quraan aan kan ahayn ama badal, waxaad dhahdaa igama suurowdo inaan ka baddalo agtayda, waxaan nun Raaci waxa lay waxyoodo, waxaan ka cabsan Haddaao Eebe Caasiyo Cadaab Maalin wayn.'),
(1380, 10, 16, 'Waxaad Dhahdaa Hadduu doono Eebe idnikiima Akhriycen idinmana Ogaysiiyeen isaga, waxaan ku Nagaaday Dhexdiinna ka hor Cimri ee Meyeydaan wax kasayn.'),
(1381, 10, 17, 'Yaa ka Dulmi Badan Cid ku Been Abuuratay Eebe ama Beeniyey Aayaadkiisa, Illeen ma Lubaano Dambiilayaal.'),
(1382, 10, 18, 'Waxay Caabudayaan Eebe ka sokow waxaan Dhibayn Anfaceyna waxayna Odhan kuwana waa Shafeecayaalkanaga Eebe Agtiisa, waxaad Dhahdaa ma waxaad uga warramaysaan Eebe wuxuusan ku Ogayn Samooyinja iyo Dhulka, wuxuu ka Nasahanyahay oo ka Sarreeyaa waxay la Wadaajiyeen Eebe.'),
(1383, 10, 19, 'Dadka wuxuu ahaa Ummad Kaliya Markaasay Iskhilaafeen, haddayan Jirin Kalimad Eebahaa u horraysay waa la kala Xukmin lahaa Dexdooda waxay isku diidanyihiin.'),
(1384, 10, 20, 'Waxay Odhan Gaaladu maa lagaga soo Dejiyo Xagga Eebahaa Aayad, waxaad dhahdaa waxa Maqan Eebe umbaa iska leh, ee Suga Anuguna waan idinka Sugiye.'),
(1385, 10, 21, 'Markaan Dhadhansiinno Dadka Naxariis Dhibaato ka dib Taabatay waxaa soo Baxa iyagoo dhagri Aayaadkanaga, waxaa Deg-Deg badan Ciqaabta Eebe Malaa\'igtanaduna way Qori waxaad Dhagraysaan.'),
(1386, 10, 22, 'Eebe waa Kan Idinku Wada Barriga iyo Baddaba markay ahaadaan Doonta oo ay ku socdaan Dabayl Fiican oo ay Markaas ku Farxaan oy u Timaaddo Dabayl Daran ugana yimaaddo hirku Dhan kasta oy una Maleeyaan in la koobay (la Halaagay) waxay Baryaan Eebe iyagoo u kaliyeeli Diinta Iyagoo (Dhihi) Haddaad naga koriso tan waannu kugu Mahdin.'),
(1387, 10, 23, 'Markuu koriyeyna Markaas ku xad gudbaan Dhulka Xaq Darro Dadoow waxaad uun ku Xad Gudbaysaan Naftinna, waa uun Nolosha Dhaw Markaas Xagganagaa laydiin soo Celin waxaana idiinka warramaynaa waxaad Camal Falayseen.'),
(1388, 10, 24, 'Nolosha Adduunyo waa uun Biyo oo kale oon ka soo Dejinay Samada oo ku Dheehnay waxa ka so baxa Dhulka oo ah wuxuu Dadku Cuno iyo Xooluhu markuu dhulku Qurux ka Buuxsamo oy isqurxhiso oo Dadkeedu u Maleeyo inay Karaan (Goosashadeeda) yaa waxaa u Yimaadda Amarkannagii Habeen ama Dharaar waxaana ka yeelaa wax la Shafay sidii iyadoon Shalay Jirin, Saasaana ugu Caddaynay Aayaadka Qoomkii Firkiri.'),
(1389, 10, 25, 'Eebe wuxuu ugu yeedhi (Dadka) Guriga Nabadgalyada (Jannada) wuxuuna ku Hanuuniyaa Cidduu Doono Jid Toosan (Xaqa).'),
(1390, 10, 26, 'Kuwa Wanaagga Falayna waxaa u Sugnaaday Wanaag (Janno) (Aakhiro) iyo Siyaado (Aragga Eebe) mana haleelo Wajigooda Boodh iyo Dulli midna kuwaasuna waa Asaxaabta (Ehelka) Jannada wayna ku waari Dhexdeeda.'),
(1391, 10, 27, 'kuwa kasbaday xumaan Abaalkood waa Xumaan la mida, waxaana haleeli dulli wax Eebe ka ilaalinna ma jiro, waxaadna moodaa in lagu daboolay wajigooda goosin habeen oo mugdi ah, kuwaasu waa Asaxaabta (Ehelka) Naarta wayna ku waari dhexdeeda,'),
(1392, 10, 28, 'ka warran maalintaan kulminaynno Dhammaan markaas aan ku dhahaynno kuwii la wadaajiyey (Gaaloobay) Meejiinna ahaada idinka iyo kuwaad la wadaajiseen markaasaan kala gaynay dhexdooda, waxayna dheheen kuway la wadaajiyeen (Eebe) an naga nama aydaan Caabudayn.'),
(1393, 10, 29, 'waxaa markhaati dhexdeenna ugu filan Eebe inaan Cibaadadiinna Moogayn.'),
(1394, 10, 30, 'halkaasaa lagu imtixaami naf kasta waxay hor marsatay, waxanna loo eeliyaa Ilaaha Sayidkooda xaqa ah waxaana ka dhumay wa.xay been abuuranayeen.'),
(1395, 10, 31, 'waxaad dhahdaa yaa idinka Arsaaqa Samada iyo dhulka, yaana hanta maqalka, Aragyada, yaana ka soo bixiya wax Nool wax dhintay, kana soo bixiya was Dhintay wax Nool, yaana Maamula Amarka waxay ku odhan Eebe, waxaad dhahdaa miyeydaan Dhawrsanayn.'),
(1396, 10, 32, 'kaasi waa Ilaahiin Eebihiinna xaqa ah, maxaase xaqa ka dambeeya (Ka duway) oon baadi ahayn, ee xaggee laydiin Leexin.'),
(1397, 10, 33, 'Saasay ugu Waajibtay Kalimadii Eebahaa kuwa faasiqoobay oo ah inayan rumeeynin (Xaqa).'),
(1398, 10, 34, 'waxaad dhahdaa ma ka midyihiin Shurakadiinna cid billowda Abuurka Soona celiya, waxaad dhadaa Eebaa Bilaaba khalqiga soona eeliya ee xaggee loo iili.'),
(1399, 10, 35, 'waxaad dhahdaa ma ka midyahay shurakadiinna cid ku hanuunin xaqa, waxaad dhadaa Eebaa ku hanuunin Xaqa, mase mid ku hanuunin Xaqa yaa mudan in la raaco mise midaan hanuuninayn in la hanuuniyo mooyee, maxaad leedihiin sideed xukumaysaan.'),
(1400, 10, 36, 'mana raaco badankoodu waxaan malo ahayn, malana waxba kama taro xaqa, Eebana waa ogyahay waxay falayaan.'),
(1401, 10, 37, 'ma aha Quraankanu mid laga been abuurtay Eebe ka sokow, waase u rumeeyaha wixii ka horreeyey iyo caddaynta Kitaabka (Xukunka) shakina kuma jiro wuxuuna ka ahaaday Eebaha Caalamka.'),
(1402, 10, 38, 'mise waxay odhan wuu Been abuurtay, waxaad dhahdaa la imaadda suurad la mida una yeedha ciddaad kartaan oo Eebe ka soo hadhay haddaad run sheegaysaan.'),
(1403, 10, 39, 'waxayse beeniyeen waxayna cilmigiisa koobin (Quraanka) uusanna wali u imaanin aayahiisu saasayna u beeniyeen kuwii ka horreeyey ee bal day siday noqotay Cidhibtii Daalimiinta.'),
(1404, 10, 40, 'waxaa ka mida Dadka mid rumeyn Quraanka iyo midaan rumaynayn Eebana waa ogyahay Mufsidiinta.'),
(1405, 10, 41, 'hadday ku Beeniyaan waxaad dhahdaa anuugu waxaan leeyahay Camalkayga idinkuna camalkiinna idinku bari baad ka tihiin waxaan fali anna bari baan ka ahay waxaad falaysaan.'),
(1406, 10, 42, 'waxaa ka mida gaalada kuwo ku dhagaysan ma adaa wax maqashiin kara mid dhaga la\' oon wax kasayn.'),
(1407, 10, 43, 'waxaana ka mida mid ku soo dhawri ma adaa toosin kara mid indha la\' oon wax arkayn.'),
(1408, 10, 44, 'Eebe kama dulmiyo Dadka waxba, hasayeeshee Dadka Naftiisa Dulmiya.'),
(1409, 10, 45, 'Maalinta la kulminna waxay la mid noqon iyagoon nagaanin Saacad Maalin mooyee, wayna is aqoonsan dhexdooda, waxaana khasaaray kuwa beeniyey la kulanka Eebe mana hanuunsanayn.'),
(1410, 10, 46, 'haddaan ku tusinno waxaannu ugu goodin qaarkiis ama aan ku oofsanno xagganaga umbaa loo soo celin, markaasna Eebaa ka maraga waxay falayaan.'),
(1411, 10, 47, 'Ummad kasa Rasuulbay leedahay, markuu u yimaaddo Rasuulkooduna waxaa lagu kala xukmin caddaalad lamana dulmiyo.'),
(1412, 10, 48, 'waxayna odhan gaaladu waa goorma yaboohaas (Gooddiga) haddaad runsheegaysaan.'),
(1413, 10, 49, 'waxaad dhahdaa uma hanto naftayda Dhib iyo nacfi midna wuxuu Eebe doono mooyee, Ummaad kastana waxay leedahay Ajal (Muddo) markay Ajashoodu timaadana lagama dib mariyo Saacad lagamana hor mariyo.'),
(1414, 10, 50, 'waxaad dhahdaa bal ka warrama hadduu idiinku yimaaddo Cadaabkiisu (Eebe) Mir ama Maalin maxay ka dedejisan xaggiisa Dambiilayaashu.'),
(1415, 10, 51, 'Mise markuu dhaeaad rumaynaysaan hadda idinkoo Dedejisanayey.'),
(1416, 10, 52, 'markaasa lagu odhan kuwii dulmi falay dhadhamiya cadaabka waaridda, miyaana laydinka abaal marinin waxaad kasbanayseen mooyee.'),
(1417, 10, 53, 'waxay ku warsan ma xaqbaa isagu, dheh haa Eebahaybaan ku dhaartaye isagaa Xaqa, idinkuna ma tihidiin kuwo (Eebe) daalin.'),
(1418, 10, 54, 'hadday naf kasta oo dulmi fashay leedahay waxa dhulka ku sugan way isku furan lahayd, waxayna qarsan gaaladu shalayto markay Arkaan Cadaabka, waxaana lagu kala xukmin Dhexdooda Xaq lamana dulminayo.'),
(1419, 10, 55, 'Eebana waxaa u sugnaaday waxa ku sugan Samooyinka iyo Dhulka, yabooha Eebana waa Xaq Dadka badankiisuse ma oga.'),
(1420, 10, 56, 'Eebaa wax nooleeyn waxna dila xaggiisaana laydiin soo celin.'),
(1421, 10, 57, 'Dadow waxaa idiin timid Waano Eebihiin iyo Caafimaadka waxa Laabta ku sugan iyo Hanuun iyo naxariista Mu\'miniinta.'),
(1422, 10, 58, 'Waxaad dhahdaa Fadliga Eebe iyo Naxariisiisa arintaas ha ku farxeen (Mu\'miniintu) iyadaa ugu Khayrroon waxay kulmine.'),
(1423, 10, 59, 'waxaad dhahdaa ka warrama waxa Eebe idiin soo dejiyo oo Risqi ah ood ka yeesheen Qaar ka mida Xaaraan (qaarna) Xalaal, waxaad dhahdaa ma Eebaa idiin idmay mise Eebaad ku Beenabuuranaysaan.'),
(1424, 10, 60, 'Maxay u malayn kuwa ku been abuuran Eebe (inuu ku fali) Maalinta Qiyaame, Eebe waa kan Fadliga ku leh Dadka badankooduse kuma mahadiyaan.'),
(1425, 10, 61, 'Wixii Xaala ood ku Sugan tahay wixii quraana ood Akhrin, wixii camala ood falaysaan waan idin la joognaana (ognahay) narkaad dhex galaysaan, kagama Qarsoona Eebahaa waxa u yar Dhulka dhexdiisa iyo Samada, wixii ka yar iyo wixii ka wanyna Kitaab cad bay ku sugnayihiin.'),
(1426, 10, 62, 'Awliyada Eebana cabsi masaarra mana murugoodaan.'),
(1427, 10, 63, 'waana kuwa rumeeyay (xaqa) dhawrsadana.'),
(1428, 10, 64, 'Waxaana u sugnaaday Bishaaro nolosha adduunyo iyo Aakhiraba, wax badali Kalimada Eebana ma jiro, taasina waa uuu Liibaanta wayn.'),
(1429, 10, 65, 'yuuna ku walbahaarin hadalkoodu cisi dhamaantiis Eebaa iska leh, waana Maqle oge.'),
(1430, 10, 66, 'Eebaa iska leh waxa samooyinka iyo Dhulka ku sugan, waxay raacayaan oy Caabudi Eebe ka sokow wa Shurako (Sanamyo) waxaan mala ahayna ma raacayaan, waxaan heenaalayaal ahaynna ma aha.'),
(1431, 10, 67, 'Eebe waa kan idiin yeelay Habeenka inaad xasishaan, Maalintana Arag (Ka yeelay) arrintaasna waxaa ugu sugan calaamooyin qoomkii wax maqli.'),
(1432, 10, 68, 'waxay dheheen wuxuu yeeshay Eebe Ilmo, waa nasahanyahay, Eebana waa uun Hodan, wuxuuna leeyahay waxa samooyinka iyo dhulka ku sugan, agtiinna xujo uma taal waxaas (Beenta) mise Eebaad ku sheegaysaan waxaydaan ogayn.'),
(1433, 10, 69, 'waxaad dhahdaa kuwa Eebe ku abuurta beenta ma libaanaan.'),
(1434, 10, 70, 'waa uun waaxada adduunyo markaas xagganagaa noqoshadoodu noqon, markaasaan Dhadhansiinaynaa cadaab daran gaalnimadooda darteed.'),
(1435, 10, 71, 'ku akhri korkooda warkii Nabi Nuux markuu ku yidhi qoomkiisii qoomkayow hadday idin la waynaatay Nagaadigayga iyo ku waanintayada aayaadka Eebe waxaan tala saartay Eebe ee kulmiya amarkiinna iyo shurakadinnaa yuuna ahaan amarkiinna wax qarsoon (oo dhiba) markaas i xukuma hana i sugina.'),
(1436, 10, 72, 'haddaad jeedsataan idin warsanmaayo ujuuro, ujuuradayda waxaa idiin Eebe, waxaana lay faray inaan ka mid ahaado Muslimiinta.'),
(1437, 10, 73, 'Wayna beeniyeen waxaana ku korinnay Nabi Nuux iyo intii la jirtay Doontii, waxaana ka yeelay kuwo u hadha dhulka, waxaana maanshaynay kuwii beeniyey Aayaadkannaga ee day siday noqotay cidhibtii kuwii loo digay.'),
(1438, 10, 74, 'markaas kadib waxaan u Bixinnay Rasuullo Qoomkoodii Waxayna ula yimaadeen xujooyin, mana Rumayn beenintoodii darteed ee horraysay Saasaan u Daabacnaa «daboollaa» Quluubta xadgudbayaasha.'),
(1439, 10, 75, 'markaasaan u Bixinay gadaashood (Nabi) Muuse iyo (Nabi) Haaruun fircoon iyo colkiisii iyagoo Wata aayaadkannagii, wayna Isla waynaadeen waxayna Noqdeen Qoom dambiilayaal ah.'),
(1440, 10, 76, 'markuu uga yimid Xaqu Caggannaga waxay Dhaheen kani waa sixir cad.'),
(1441, 10, 77, 'Wuxuuna yidhi (Nabi) Muuse ma waxaad ku Dhahaysaan Xaqa markuu idiin yimid ma Sixirbaa kani? isagoosan Liibaanin saaxiriintu.'),
(1442, 10, 78, 'Waxayna dhaheen ma waxaad noogu timid inaad naga leexisid waxaan ka hellay Aabayaalkanni oy idiin Ahaato waynaanta Dhulka (xukunka) annagu idinka idin Rumaynnaynno.'),
(1443, 10, 79, 'wuxuu yidhi fircoon ii keena saaxir cilmi badan oo dhan.'),
(1444, 10, 80, 'markay yimaadeen saaxiriintiina wuxuu ku yidhi (Nabi) Muuse Tuura waxaad Tuuraysaan.'),
(1445, 10, 81, 'markay tuureenna wuxuu yidhi (Nabi) Muuse sixir baad la timaadeen Eebaana Burin, Eebana ma hagaajiyo camalka fasaadiyayaasha.'),
(1446, 10, 82, 'wuxuuna ku sugaa Eebe xaqa kalimadiisa haba naceen Dambiilayaashuye.'),
(1447, 10, 83, 'ma rumaymn Nabi Muuse Farac yar oo Qoomkiisii ka mida mooyee iyagoo ka Cabsan fircoon fyo Kooxdiisii inay fidmeeyaan, fircoonna wuu isku kibriyey dhulka wuxuuna ka mid ahaa xadgudbayaaaha.'),
(1448, 10, 84, 'wuxuu yidhi (Nabi) Muuse Qoomkayow haddaad tihiin kuwa rumeeyey Eebe isaga uun tala saarta haddaad MusIimiin Tihiin.'),
(1449, 10, 85, 'waxayna dheheen Ilaahay yaan lalo saarannay, Eebahannow hanooga yeelina fidmo Qoomka Daalimiinta ah.'),
(1450, 10, 86, 'nagana kori naxariistaada darteed qoomka gaalada ah.'),
(1451, 10, 87, 'waxaana u waxyoonay (Nabi) Muuse iyo walaalkiis inaad uga yeeshaan Qoomkiina Masar Guryo kana yeesha guryihiinna Qiblada (Ku tukada) ooga salaadda Lina bishaaree Mu\'miniinta.'),
(1452, 10, 88, 'wuxuuna yidhi (Nabi) Muuse Eebahannow adaa siiyey Fircoon iyo Kooxdiisa Qurux iyo Xoolo Nolosha Adduunka Eebow inay ka dhumiyaan Jidkaaga Eebow halaag (Tirtir) Xoolahooda Adkeena Quluubtooda oo yeyna rumeynin intay ka arkaan Cadaab daran.'),
(1453, 10, 89, 'Eebe wuxuu yidhi waa la ajiibay Baryadiinna ee toosnaada hana raacina Jidka kuwaan wax ogayn.'),
(1454, 10, 90, 'waxaan tallaabinay Banii Israa\'iil Badda waxayna u raaceen Fircoon iyo Qoomkiisii kibir iyo cadaawanimo, markay haleeshay maansheyntiina wuxuu yidhi (Fircoon) waxaan rumeeyey inaan Eebahay rumeeyeen Bani Israa\'iil mooyee Ilaah kale jirin waxaana ka mid ahay Muslimiinta.'),
(1455, 10, 91, '(Eebe wuxuu yidhi) hadda (Miyaad rumeyn) adoo horay u caasiyey oodna ka mid ahayd kuwa wax fasaadiya.'),
(1456, 10, 92, 'Maanta waxaannu korin badankaaga inaad u noqotid Cidda kaa dambaysa Calaamo wax badan oo Dadka ka mida Aayaadkannaga waa halmaansanyihiin.'),
(1457, 10, 93, 'waxaan dejinay Bani Israa\'iil Dejin runa (meel fiican) waxaana ku arsaaqnay wanaag, waxayna is khilaafeen markuu cilmigi u yimid, Eebahaana wuu kala xukumi maalinia Qiyaame waxay isku khilaafeen.'),
(1458, 10, 94, 'haddaad Shakisantahay waxaan kugu soo Dejinay warso kuwa Akhriya Kitaabka hortaa, waxaa kaaga yimid xagga Eebe Xaq, ee ha Noqonin kuwa Shakiya.'),
(1459, 10, 95, 'hana ka mid noqon kuwa beeniya Aayaadka Eebe ood ka mid noqoto kuwa Khasaaray.'),
(1460, 10, 96, 'kuwa ku waajibtay Kalimadii Eebahaa (Ciqaab) rumeynmaayaan.'),
(1461, 10, 97, 'xataa huddaad ula timaaddid Aayad kasta, inlay ka arkaan Cadaab Daran.'),
(1462, 10, 98, 'maxay u jiriwayday Magaalo rumeysa (Xaqa) oo uu anfaco Iimaankeedu, laakiin Qoomkii (Nabi) Yuunus markay nimeeyeen waxaan ka faydnay Cadaabkii dulliga ee Adduunka, waxaana u raaxaynay tan iyo Muddo.'),
(1463, 10, 99, 'haddwi doono Eebabaa waxaa rumayn tahaa waxa Dhulka ku sugan oo dhan, ee ma adaa Dad ku qasbi inlay ka rumeeyaan (Xaqa).'),
(1464, 10, 100, 'nafuna ma rumeyso idanka Eebe ka dib mooyee, wuxuuna Eebe yeelaa xumaanta kuwaan wax kasayn korkooda.'),
(1465, 10, 101, 'waxaad dhahdaa daya waxa ku sugan Samooyinka iyo Dhulka waxna kama deeqaan Aayado (Calaamooyin) iyo Digid qoom aan rumeyn (Xaqa).'),
(1466, 10, 102, 'ma waxay sugi waxaan ahayn Ayaamihii kuwii tagay hortood oo kale, waxaad dhahdaa suga anna waan idinla sugiye.'),
(1467, 10, 103, 'markaasaan korinnaa Rasuulada iyo kuwa rumeeyey saasaana Xaq nagu ah, inaan u korinno Mu\'miniinta.'),
(1468, 10, 104, 'waxaad dhahdaa Dadow hadaad tihiin kuwo shakisan diintayda Caabudi maayo waxaad caabudaysaan oo Eebe ka soo hadhay waxaanse Caabudi Eebaha idin Dili, waxaana lay faray inaan ka mid noqdo Mu\'iniinta.'),
(1469, 10, 105, 'iyo inaan u jeediyo wajiga Diinta anoo Toosan hana ka mid noqonin Mushrikiinta.'),
(1470, 10, 106, 'Hana caabudin Eebe ka Sokow waxaan wax ku Tarayn kuna dhibayn, Haddaad fasho waxaad ka mid Noqon Daalimiinta.'),
(1471, 10, 107, 'Hadduu ku taabsiiyo Eebe dhib wax faydi oon isaga Ahayn ma jiro, Hadduu kula doono Khayra wax celin kara fadligiisa ma jiro, Wuxuuna gaadhsiiyaa cidduu doono oo Addoomadiisa ka mida, waana Dambi dhaafe Naxariista.'),
(1472, 10, 108, 'Waxaad dhahdaa Dadow waxaa idiinka yimid xagga Eebe Xaqii, ciddii Hanuuntana wuxuu uun u Hanuunay Naftiisa, cidii dhuntana wuxuu uun u Dhumay Naftiisa (Dhibkeeda) Anna korkiinna Wakiil uma ihi.'),
(1473, 10, 109, 'Ee raac waxa laguu waxyooday Samirna inta Eebe ka kala Xukmin (Dadka) isagaa u Wanaagson Xaakime.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'Erayga hore wuxuu ku Tusin Mujisada Quraanka, Kitaabka Quraanka alma waa mid la sugay Aayadihiisa, markaas lagana Caddeeyey Eebaha Falka san ee wax walba og Xaggiisa.'),
(1475, 11, 2, 'inaydaan Caabudin Eebe mooyee wax kale, anugu waxaan idiinka ahay xagga Eebe Dige Bishaareeya.'),
(1476, 11, 3, 'dambi dhaafna warsada Eebe una toobad keena wuxuu idiinku raaxayn nolol wanaagsan tan iyo muddo, wuxuuna siin cid kasta dheeraad leh (Fadli) dheeraadkiisa (Fadligiisa) haddaad jeedsataanna anugu waxaan idiinka cabsan Cadaab Maalin wayn.'),
(1477, 11, 4, 'xagga Eebaa noqoshadiinnu tahay wax kastana wuu karaa.'),
(1478, 11, 5, 'waxay iilayaan Laabtooda inay iska qariyaan Eebe, markay isku dadi maryahooda waa ogyahay Eebe waxay qarsan iyo waxay Muujin, illeen Eebe waa igyahay waxa laabta ku sugane.'),
(1479, 11, 6, 'Dhul socod kasta Risqigeedu wuxuu saaranyahay Eebe wuxuuna ogyahay meejey ku Hoyan ooy u dheelman dhammaanna Kitaab Cad yey ku suganyihiin.'),
(1480, 11, 7, 'Eebe waa kan ku Abuuray Samooyinka iyo Dhulka Lix Maalmood, wuxuuna ahaa Carshihiisu Biyo korkood inuu idin Imtixaamo kiinna Camalka Wanaagsan, haddaad dhahdo waa laydin soo bixin Geeri ka dib waxay odhan kuwii gaaloobay kani waxaan sixir Cad ahayn ma aha.'),
(1481, 11, 8, 'haddaan dib uga dhigno Cadaabka Xaggooda tan iyo muddo tirsa waxay odhan maxaa celinaya, maalintuuse u yimaaddo mid Iaga iilo ma aha, waxaana ku dhaca waxay ku, jeesjeesi (Ciqaabtiisii).'),
(1482, 11, 9, 'haddaan dhadhansiinno Dadka naxariis xaggannaga ah markaas aan ka qaadno wuxuu noqdaa mid quusasho badan oo dafir hadan.'),
(1483, 11, 10, 'haddaan dhadhansiinno Dadka Niemo dhib taabtay ka dib wuxuu dhihi wwaxaa iga tagay sumaanti isagoo Farax iyo faan ku sugan.'),
(1484, 11, 11, 'kuwa Samra ee canal fiica fala mooyee, kuwaasna waxaa u sugnaaday dambi dhaaf iyo ajir wayn.'),
(1485, 11, 12, 'waxaad u dhawdahay inaad ka tegto waxa laguu waxyoon qaarkiis oy Ia cidhiidhyanto Laabtaadu inay dhahaan maa lagu soo Dejiyo korkiisa Kansi ama Ia yimaaddo malag, adugu waxaad uun tahay Dige, Eebana wax kasta wuxuu u yahay (Ilaaliye) Wakiil.'),
(1486, 11, 13, 'mise waxay odhan wuu been abuurtay, waxaad dhahdaa karaa Toban Suuradood oo la mida oo la been abuurtay una yeedha ciddaad kartaan oo Eebe ka soo hadhay haddaad run sheegaysaan.'),
(1487, 11, 14, '14, haddayna idin Ajiibin «yeelin» ogaada in lagu soo dejiyey Quraanka ogaansho Eebe iyo inaan Eebe mooyee Ilaah kale jirin, a ma islaamaysaan.'),
(1488, 11, 15, 'ciddii dooni nolosha adduunyo iyo quruxdeeda waxaan u dhamaynaynaa camalkooda dhexdeeda lagamana nusqaamiyo.'),
(1489, 11, 16, 'Kuwaasuna waa kuwaan kulahayn Aakhiro waxaan Naar ahayn, waxaana buray waxay ku sameeyeen dhexdeeda waxaana baadhila (wax kama jire ah) waxay falayeen.'),
(1490, 11, 17, 'Ruux ku sugan (Xujo) cad oo Eebihiis, uu raacina marag Eebe, hortiisna ahaayeen Kitaabkii Muuse isagoo Imaam iyo Naxariis ah ma la midbaa mid adduunyo uun doonay, kuwaas fiican way rumeyn Quraanka, ciddii ka gaalowda Quraanka iyo Nabiga oo Xisbiyada ka mida Naarbaa ballan u ah, ee ha noqon mid Shakiya Xaggiisa isagu waa Xaq Eebe ka ahaadaye, laakiin Dadka Radankiis ma Rumeeyaan.'),
(1491, 11, 18, 'yaa ka dulmi badan ruux ku abuurtay Eebe Been, kuwaas waxaa loo bandhigi Eebahood waxayna odhan maraggu kuwaasu waa kuwa ku been abuurtay Eebahood Lacnadi ha ahaato Daalimiinta korkooda.'),
(1492, 11, 19, '19, ee ah kuwa ka jeedsada Jidka Eebe lana dooni Qallooc Aakharana ka gaaloobay.'),
(1493, 11, 20, 'kuwaasu ma aha kuwa ku daalin Eebe Dhulka dhexdiisa umana sugna wax Eebe ka soo hadhay oo u gargaari waxaana loo Iaablaabi cadaabka, mana karaan inay (Xaqa) maqlaan ama arkaan.'),
(1494, 11, 21, 'kuwaasu waa kuwa Khasaariyey Naftooda waxaana ka dhumay waxay been abuurteen.'),
(1495, 11, 22, 'waxaana sugan inay kuwaasu Aakharo iyaga uun khasaaro badnaadeen.'),
(1496, 11, 23, 'kuwa rumeeyey Xaqa oo camal fiican falay oo u noqday Eebahood kuwaasu waa Jannada Ehelkeeda wayn, ku dhex waari.'),
(1497, 11, 24, 'waxay La midyihiin Labada kooxood mid indhala\' iyo mid dhaaa la\' mid wax arka iyo mid wax maqli ma simanyihiin tusaale ahaan miyeydaan wax xusuusaneyn.'),
(1498, 11, 25, 'waxaan u diray (Nabi) Nuux qoomkiisii (Isagoo leh) waxaan idiin ahay dige Cad.'),
(1499, 11, 26, 'inaydaan Caabudin wax Eebe ka soo hadhay, waxaan idiinka cabsan korkiinna cadaab Maalin daran.'),
(1500, 11, 27, 'waxayna dheheen madaxdii Gaalowday oo qoomkiisa ka mida waxaan bashar nala mida ahayn kuguma aragno, kugumana aragno inuu ku raacay waxaan ahayn kuwannaga xun xun ee ku deg dega ra\'yiga, Kuugumana ara- gno korkannaga wax dheeraada waxaanse idiin malaynaynaa Beenaalayaal.'),
(1501, 11, 28, 'wuxuuna yidhi Qoomkayow ka warrama haddaan Xujo (cad) oo Eebahay ku sugnahay uu ina siiyey naxariis Xaggiisa oo Laydinka indho tiray miyaao idinkii laasimin karraa idinkoo nici.'),
(1502, 11, 29, 'Qoomkayow idin warsan maayo Xoolo, Ajirkayga Eebaa haya, mana eryayo kuwa xaqa rumeeyey illeen iyagu waxayla kulmi Eebahoode, waxaanse idinku arkaa Qoom Jaahiliina.'),
(1503, 11, 30, 'Qoomkayow yaa iiga gargaari Eebe haddaan eryo kuwaas (Xaqa rumeeyey) miyeydaao wax Xusuusanayn.'),
(1504, 11, 31, 'idima dhahayo waxaa agtayda ah Khasaa\'intii Eebe mana ogi waxa maqan, idinmana odhanaayo waxaan ahay Malaa\'ig, idinmana odhanaayo kuway yasayso indhihiinnu ma siinayo Eebc khayr, Eebaa og waxa Naftooda ku sugan, markaas waxaan ka mid noqon Daalimiinta.'),
(1505, 11, 32, 'waxay dheheen Nuuxow waad nala dooday ood badisay la doodkannaga, ee noo keen waxaad noo yaboohayso haddaad run sheegi.'),
(1506, 11, 33, 'wuxuuna yidhi waxaa uun idiin keeni (kara) Eebe hadduu doono, mana tihiin kuwo daalin Eebe.'),
(1507, 11, 34, 'waxna idiinma tarto Naseexadaydu haddaan doono inaan idiin naseexeeeyo hadduu Eebe doono inuu iddin baadiyeeyo illeen waa Eebihiine. xagiisaana laydiin celin.'),
(1508, 11, 35, 'mise waxay dhihi isagaa been ahuurtay, waxaad dhahdaa haddaan been abuurto dambigaygu anuu isaaranyahay, anuguna bari baan ka ahay waxaad dambaabtaan.'),
(1509, 11, 36, 'waxaa loo waxyooday Nabi Nuux inuusan ku rumeynayn Qoomkaaga inta ku rumaysay waxaan ahayn ee ha u murugoon waxay fali.'),
(1510, 11, 37, 'samaysana doon annagoo ku dhawri kuuna waxyoon hana igula hadlin (xaalka) kuwii dulmi falay waa Ia maansheyne.'),
(1511, 11, 38, 'wuxuu samaynayye Doon mar kasta oo Qoomkiisa koox ka mida martana way ku jees jeesayeen wuxuuna ku dhahayey haddaad nagu jees jeestaan anaguna waan idinku jees jeesi saad u jees jeesaysaan.'),
(1512, 11, 39, 'waadna ogaan doontaan cidduu u yimaaddo cadaab dulleeya kuna dago cadaab joogta ah.'),
(1513, 11, 40, 'markuu yimid amarkannagii oo Tanuurkii burqaday waxaan ku nidhi ku xambaar Doonida nooc kasta Labo iyo Xaaskaaga ciddii dhumid loo xukumay mooyee, iyo cidii rumaysa xaqa (iyana ku xambaar) mana rumeynin wax yar mooyee.'),
(1514, 11, 41, 'wuxuuna yidhi ku kora Magaca Eebe oy kuna socon kuna joogsan Eebahayna waa dambi dhaafe naxariista.'),
(1515, 11, 42, 'wayna la socotay hir dhexdiis oo buuro la mida wuxuuna u dhawaaqay Nabi Nuux Wiilkiisii wuxuuna ahaa meel ka gaar ah (Isagoo leh) Wiilkaygow nala soo kor hana la jirin gaalada.'),
(1516, 11, 43, 'wuxuuna ku yidhi waxaan u dunmi buur iga dhawrta Biyaha wuxuu yidhi (Nabi Nuux) wax ilaalin kara maanta amarka Eebe ma jiro cid Eebe u naxariistay mooyee, waxaana kala dhex maray hirkii wuxuuna noqday Wiilkii kuwa la maansheeyey.'),
(1517, 11, 44, 'waxaana la yidhi dhulow Liq Biyahaaga Samooyna qaad (Jooji) waxaana la yareeyey «la gudhiyey» Biyihii waxaana la xukumay Amarkii waxayna Ku dul ekaatay Doontii Juudi waxaana la yidhi fogaansho haw sugnaato Qoomka Daalimiinta ah.'),
(1518, 11, 45, 'wuxuu u dhawaaqay (Baryey) Nabi Nuux Eebihiis wuxunna yidhi Eebow Wiilkaygu wuxuu ka mid yahay Ehelkeyga Yaboohaaguna waa xaq (sugan)adaana xuk unkaagu falsanyahay.'),
(1519, 11, 46, 'Eebe wuxuu yidhi Nuuxow Wiilku Ehelkaaga kama mid ma aha waana camal aan suubanayn ee hay warsan waxaadan cilmi u lahayn waxaan kaa waanin inaad jaahiliinta ka mid noqoto.'),
(1520, 11, 47, 'wuxuuna yidhi Nabi Nuux Eebow waxaan kaa magan galay inaan ku warsado waxaanan u cilmi lahayn haddaadan ii dambi dhaafin oodan ii naxariisan waxaan ka mid noqon kuwa khasaaray.'),
(1521, 11, 48, 'waxaa lagu yidhi Nuuxow ku deg Nabadgalyo xaggannaga ah iyo ummadaha kula jira, ummadana waannu u raaxayn markaas waxaa taaban cadaab daran oo xaggannaga ka ahaada.'),
(1522, 11, 49, 'kaasi waa warkii maqnaa oon kuu waxyoonayno maadan ahayn mid og adi iyo Qoomkaagu midna kan hortiis ee samir cidhibta (Fiican) waxaa leh kuwa dhawrsada.'),
(1523, 11, 50, 'Caadna waxaa Ioo diray Walaalkood Nabi Huud, wuxuuna yidhi Qoomkayow caabuda Eebe Ilaah kale idiinma sugnee waxaan been abuurasho ahayna kuma sugnidin.'),
(1524, 11, 51, 'Qoomkayow idinka warsan maayo xaqa ujuuro ujuuradayda waxaan uun ka mutaa Eebaha i Abuuray ee miyeydaan wax kasayn.'),
(1525, 11, 52, 'Qoomkayow Dambi dhaaf warsada Eebihiin una toobad kenna ha soo (Daayo) diro Samada (Roobka) korkiinna si badane hana idiinku kordhiyo xoog xooggiinii hana jeedsanina idinkoo Dambiilayaal ah.'),
(1526, 11, 53, 'waxay dheheen Huudow noolama aadan imaanin Xujo cad kagamana tageeyono llaalryahannaga hadalkaaga kumana rumeynayno.'),
(1527, 11, 54, 'wax kale kuguma dhaheeyno oon ahayn in Ilaahyahannaga Qaarkood kugu asiibeen xumaan (Cudur) wuxuu yidhi waxaan Eebe marag galin ee idinkuna marag nogda inaan bari ka ahay waxaad la wadaajinaysaan, (Cibaadada).'),
(1528, 11, 55, 'oo Eebe ka soo hadhay ee i dhagra dhammaan hana i sugina.'),
(1529, 11, 56, 'anugu waxaan tala saartay llaahay ee ah Eebahay iyo Eebihiin wax socda oosan foodda qabanayn ma jiro (Awoodayn) Eebahayna wuxuu ku sugan yahay jid toosan.'),
(1530, 11, 57, 'haddaad Jeedsataan waxaan idin gaadhsiiyey wixii laygu soo diray xaggiinna, wuuna idinka gadaal marin Eebahay qoom kale waana kama dhibaysaan, Eebahayna wax kasta wuu ilaaliyaa.'),
(1531, 11, 58, 'markuu yimid amarkannagii waxaan ku korinnay Huud iyo intii rumaysay Naxariistanada wexaana ka korinay Cadaab Adag.'),
(1532, 11, 59, 'taasi waa Caad waxay diideen Aayaadkii Eebahood waxayna caasiyeen rasuuladiisii waxayna raaceen amarka islaweyne madax adag dhammaantiis.'),
(1533, 11, 60, 'waxaana la raaciyey Adduunyadan Lacnad iyo Maalinta Qiyaamaba, Caad way ka gaaloobeen Eebahood Fogaanshaa (Halaagbaana) u sugnaaday Caadkii Qoomkii Huud ahaa.'),
(1534, 11, 61, 'Thamuudna waxaan u dirray Walaalkeed (Nabi) Saalax, wuxuuna yidhi Qoomkayow Caabuda Eebe Ilaa Eebe ka soo hadhay ma idiin sugnee, isagaana idiinka ahaysiiyey Dhulka idinkuna Camiray dhexdeeda ee dambidhaaf warsada una toobad keena illeen Eebahay waa dhawyahay oo ajiibaa (Baryade)'),
(1535, 11, 62, 'waxay dheheen Saalaxow waxaad ku ahayd dhexdannada mid la rajeeyo arrintan ka hor ma waxaad naga reebi inaan caabudno waxay caabudayeen Aabayaalkanno annagu shaki daran baan kaga suganahay waxaad noogu yeedhi.'),
(1536, 11, 63, 'wuxuu yidhi bal ka warrama haddaan xujo Eebahay ku sunahay oo uu i siiyey Naxariis xaggiisa ah yaa iiga gargaari Eebe haddaan Caasiyo iimana kordhinaysaan waxaan khasaarc ahayn.'),
(1537, 11, 64, 'Qoomkayow tan waa Hashii Eebe Calaamad bayna idiintahay ee iska Daaya ha cunto Dhulka Eebe hana ku taabanina Xumaan oo markaas idin qabto Cadaab dhaw.'),
(1538, 11, 65, 'wayse dileen Hashii, wuxuuna ku yidhi ku raaxaysta guryihiina Saddex maalmood kaasina waa yabooh aan la beeninayn.'),
(1539, 11, 66, 'markuu u yimdi amarkii Eebe waxaan ku korinay Nabi Saalax iyo intii rumaysay Naxariistanada iyo inaan (ka korinay) dulliga Maalintaas Eebana waa xoog badane Adkaada.'),
(1540, 11, 67, 'waxaana wabatay kuwii dulmi falay qaylo waxayna ahaadeen kuwo guryahooda ku dhexdhinta.'),
(1541, 11, 68, 'sidii iyagoon ku dhex nagaanin, Thamuud waxay ka gaaloobeen Eebahood waxayna muteen fogaansho (Halaag).'),
(1542, 11, 69, 'waxay ula yimaaddeen Malaa\'igtanadii aan soo diray Nabi Ibraahim Bishaaro, waxayna dheheen salaamo korkaaga ha ahaato, isna wuxuu yidhi Salaam, kamana nagaan (Daahin) inuu u keeno dibi la dubay (Hilibkiis).'),
(1543, 11, 70, 'markuu arkay in ayna gacmahoodu gaadhayn wuu la yaabay wuxuuna iska kasay cabsi xaggooda ah, waxayna dheheen ha cabsan waxaa naloo soo diray Qoomka (Nabi) Luudhe.'),
(1544, 11, 71, 'haweeneydiisiina way taagnayd wayna qososhay waxaana ugu bishaaraynay (Nabi) Isxaaq gadaashiisna Yacquub (Wiilkiisii).'),
(1545, 11, 72, 'waxayna tidhi waa la yaabe miyaan dhali anoo cajuuso ah ninkaygana oday yahay arrintaasu waa wax la yaab leh.'),
(1546, 11, 73, 'waxay dheheen ma waxaad la yaabi amarka Eebe naxariistiisa iyo barakadiisa korkiinna, qoyskanow, Eebana waa la mahdiye la Sharrifo.'),
(1547, 11, 74, 'markay Nabi Ibraahim ka tegtay Cabsidii (argagaxii) una timid Bishaaradii yuu noqday mid nagula dooda xaalka Qoomka (Nabi Luudh).'),
(1548, 11, 75, 'Nabi Ibraahimna waa dulsame noqosho iyo toobadkeen badan.'),
(1549, 11, 76, 'Ibraahimow iskaga jeedso xaalkan, waxaa dhab u yimid Amarkii Eebahaaye iyagana waxaa u imaan cadaab aan laga celinayne.'),
(1550, 11, 77, 'markay Malaa\'igtaan soo diray u timid Nabi Luudh wuu ka walbahaaray wuuna la cidhiidhyamay wuxuuna yidhi tani waa Maalin daran.'),
(1551, 11, 78, 'waxaana yimid Qoomkiisii oo u deg degi xaggiisa horayna waxay u ahaayeen kuwo fala Xumaan, wuxuuna yidhi Qoomkayow kuwaasu waa gabdhahaygii iyagaa idiin dhahiran ee ka dhawrsada Eebe bana igu dullaynina martidayda miyuusan Nin rashiida «fiican» idinku jirin.'),
(1552, 11, 79, 'waxay dheheen waaad ogtahay inaanaan ku lahayn gabdhahaaga xaq waadna ogtahay waxaannu dooni.'),
(1553, 11, 80, 'wuxuu yidhi Nabi Luudh haddaan xoog idiin leeyahay ama u dumi tiir adag (waan idin ciqaabi lahaa).'),
(1554, 11, 81, 'waxay dheheen (Malaa\'igtii) Luudhow waxaan nahay farriintii Eebahaa dhibna kuma gaadhsiinayaan ee Guuri Ehelkaaga qayb habeenka ka mida dibna hasoo dayina haweeyneydaada mooyee, waxaa ku dhici waxa ku dhacaya ballankooduna waa subaxa miyuusan subuxu dhawayn.'),
(1555, 11, 82, 'markuu yimid amarkannagii waxaan ka yeellay korkoodii hoostoodii waxaana ku daadinay dhagaxyo la shiday oo israac raacsan.'),
(1556, 11, 83, 'lagu calaameeyey Eebahaa agtiisa wax daalimiinta ka fogna ma aha.'),
(1557, 11, 84, 'Madyanna waxaan u diray Walaalkood (Nabi) Shucayb, wuxuuna yidhi Qoomkayow Caabuda Eebe idiinma sugna Ilaah kalee, hana nusqaaminina Beegidda (Miisiidda) iyo miisaanka waxaan idinku arkaa khayre (xoolo) waxaana idiinka cabsan Cadaab Maalin koobi.'),
(1558, 11, 85, 'Qoomkayow oofiya Miisidda iyo miisaanka si cadaalada hana ka nusqaaminina Dadka waxooda Dhulkana ha fasaadinina.'),
(1559, 11, 86, 'Baaqiga Eebe yaa idiin khayrroone ma ihi mid idin ilaalin (karee).'),
(1560, 11, 87, 'waxay dheheen shucaybow ma salaadahaagaa ku fari inaan ka tagno waxay caabudayeen Aabayaalkaanno ama ku fallo xoolahanaga waxaan doonno adugu waxaad uun tahay dulbadane toosan (Jees jees dartiis).'),
(1561, 11, 88, 'wuxuu yidhi Qoomkayow bal ka warrama haddaan ku sugnahay xujo Eebahay iguna arsuqay risqi wanaagsan, oonan doonaynna inaan idiin dabo maro waxaan idinka reebi oonan doonayn waxaan wanaajin ahayn, intaan karo, waafajintaydana waxaan Eebe ahayn ma karo isagaana talo saartay xaggiisaana u noqon, (Toobad).'),
(1562, 11, 89, 'Qoomkayow yuusan idinku xambaarin khilaafkaygu inuu idinku dhaco wixii ku dhacay Qoomkii (Nabi) Nuux ama qoomkii Nabi Huud ama (Nabi) Saalax idinkamana foga waqtigii Qoomkii (Nabi) Luudh.'),
(1563, 11, 90, 'ee dambi dhaaf warsada Eebihiin una toobadkeena ileen Eebahay waa Naxariiste Jeeel (Ciddii toobad keentee).'),
(1564, 11, 91, 'waxay dheheen Schucaybow ma kasaynno wax badan ood sheegi waxaana kugu aragnaa inaad dhexdannada ku tabar yartahay, hadduusan Qoomkaagu jirin waan ku dhegexyeyn lahayn, mana tihid adugu mid cisi naguleh.'),
(1565, 11, 92, 'wuxuu yidhi Qoomkayow ma jamaacaddaydaa idin kala wayn Eebe ood ka yeelateen waxaad dhabarkiinna ka tuurtaan (Xaqa) Eebahayna waxaad falaysaan wuu koobay.'),
(1566, 11, 93, 'wuxuu yidhi Qoomkayow ku camal fala dariiqadiinna anna waan camal fali waxaadna ogaan doontaan cidduu u yimaaddo cadaab dulleeya iyo cidda beenaalaha ah ee suga anna waan idinla sugiye.'),
(1567, 11, 94, 'markuu yimid amarkanagii waxaan ku korinnay Shucayb iyo kuwii rumeeyey naxariistanada, waxaana qabatay kuwii dulmi falay qaylo waxayna ahaadeen kuwo guryahoodii ku dhintay.'),
(1568, 11, 95, 'sidii iyagoon ku nagaanin fogaansho (halaag) baa u sugnaaday Madyan siday u fogaatay Thamuud (u halaagsantay).'),
(1569, 11, 96, 'waxaannu dhab ula dirray Nabi Muuse Aayaadkanagii iyo xujo cad.'),
(1570, 11, 97, 'xagga Fircoon yaana u dirray iyo Qoomkiisii, waxayna raaceen amarkii Ficoon, amarka Fircoonna ma ahayn mid toosan (Hanuunsan).'),
(1571, 11, 98, 'wuxuu hor qaadi Qoomkiisa Maalinta qiyaame, wuxuuna ku aroorin Naarta, meel lagu aroorana naarbaa u xun.'),
(1572, 11, 99, 'waxaana la raacsiiyey Adduunyada Lacnad iyo maalinta Qiyaamaba, wax laysu kordhiyana waxaa u xun (Arrintaa).'),
(1573, 11, 100, 'arrintaasu waa warkii Magaalooyinka waana kaaga qisoonaynaa, waxaana ka mida kuwo taagan iyo kuwo la halaagay.'),
(1574, 11, 101, 'maanaan dulmiyin iyagaase dulmiyey naftooda waxna ugama tarin Ilaahyahoodii ay caabudayeen Eebe ka sokow markuu u yimid amarkii Eeba-'),
(1575, 11, 102, 'waana sidaas qabashada Eebahaa markuu doono inuu qabto oo ciqaabo magaalo (Dadkeed) oo dulmi fashay, qabashadiisuna way xanuun leedahay oy darantahay.'),
(1576, 11, 103, 'arrintaasna calaamadbaa ugu sugan ciddii ka cabsan cadaabka aakhiro waana maalin loo kulmin dadka Dartiis, waana maalin la soo Joogsan.'),
(1577, 11, 104, 'dibna uguma dhigaynno muddo magacaaban darteed mooyee.'),
(1578, 11, 105, 'maalintuu Qiyaamuhu yimaaddo ma hadasho nafi idinka Eebe mooyee, waxaana ka mida dadka kuwo xun (Khasaaray) iyo kuwo liibaanay.'),
(1579, 11, 106, 'kuwa xumaadayna naarbay gali dhexdeedayna ku hiinraagi kuna qaylyi.'),
(1580, 11, 107, 'iyagoo ku waari inta samooyinku jiraan iyo dhulku wuxuu Eebe doono mooyee, Eebahaana wuxuu doonuu falaa.'),
(1581, 11, 108, 'kuwa la liibaanayna jannay ku waari inta samooyinku jiraan iyo Dhulku wuxuu Eebe doono mooyee, waana siismo aan go\'ayn.'),
(1582, 11, 109, 'ha shakin (xumaanta) waxay caabudi kuwaasu, ma caabudayaan waxay Aabayaalkood caabudayeen mar hore mooyee, waana u dhammaynaynaa qaybtooda (Ciqaabta) oon laga Nusqaaminayn.'),
(1583, 11, 110, 'waxaan siinnay (Nabi) Muuse Kitaabka (Tawreed) waana laysku khilaafay, hadduunan jirin hadal Eebahaa oo horreeyey waa lakala xugmin lahaa dhexdooda, waxayna iyagu kaga suganyihiin shaki daran.'),
(1584, 11, 111, 'dhammaan wuu u oofin Eebahaa Camalkooda illeen Eebe waxay camal fali wuu ogyahay.'),
(1585, 11, 112, 'ee u toosnow sida lagu faray adiga iyo inta kula toobadkeentay hana xadgudbina illeen Eebe waxaad camal falaysaan wuu arkaaye.'),
(1586, 11, 113, 'ha u iilanina kuwa dulmi falay oo Naaru idin taabato idiinmana sugno Eebe ka sokow Awliyo markaas laydiinna gargaari Maayo.'),
(1587, 11, 114, 'oog salaadda maalinta darfaheeda iyo qayb habeenka ka mida (saacaad) illeen wanaagu wuxuu tegsiiyaa (Tirtiraa) xumaantee, taasina waa waanada kuwa Ilaah xuska badan.'),
(1588, 11, 115, 'samir (adkayso) ilaahay ma dayaco ajirka kuwa sama falee.'),
(1589, 11, 116, 'maxay u ahaan waayeen Quruumihii hore dad khayr (caqli) leh oo ka reeba fasaadka dhulka, wax yar mooyee oo ka mida kuwaan korinnay xaggooda, waxayna raaceen kuwii dulmi falay wixii loogu raaxeeyey waxayna ahaayeen dambiilayaal.'),
(1590, 11, 117, 'Eebana uma halaago magaalo dulmi iyadoo Ehelkeedu wax wanaajin.'),
(1591, 11, 118, 'hadduu doono Eebahaa wuxuu ka yeeli lahaa Dadka mid kaliya mana ka tagaan kuwo iskhilaafsan.'),
(1592, 11, 119, 'cid Eebe u naxariistay mooyee saasaana Eebe ku abuuray, wayna taam noqotay kalimadii Eebahaa oo ah innu ka buuxiyo jahannamo Jinni iyo insi dhammaan.'),
(1593, 11, 120, 'Dhammaan waxaannu kaaga qisoon wararkii rasuulada waxaan ku sugno qalbigaaga, waxaana taam kuugu yimid xaq iyo waedi iyo waanada Mu\'miniinta.'),
(1594, 11, 121, 'waxaad ku dhahdaa kuwaan rumeynin ahaada sidiinnaas (ku camal fala) annana waannnu «ahaan» camal faliye.'),
(1595, 11, 122, 'sugana annana waaanu sugiye.'),
(1596, 11, 123, 'Eebe waxaa u sugnaaday waxa ku maqan samoolyinka iyo dhulka, xaggiisaana loo celin amarka dhammaan ee caabud oo talo saaro, Eebana mahalmaansana waxaad falaysaan.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'xuruufta hore waxay ku tusin mucjisada Quraanka, tanna waa aayaadkii Kitaabka ca (Quraanka).'),
(1598, 12, 2, 'Annagaana soo dejinay isagoo Quraan Carabi ah inaad kastaan.'),
(1599, 12, 3, 'Annagaa kaaga qisoon qissoyin tan u fiican waxyigaan Quraankan kuu waxyoonay dartiis, waxaadna ka mid ahayd hortiisa kuwaan wax igayn.'),
(1600, 12, 4, 'markuu (Nabi) Yuusuf ku yidhi Aabbihiis aabow waxaan arkay Kaw iyo Toban xiddigood iyo Qorraxda iyo Dayaxa waxaan Arkay iyagoo ii Sujuudsan.'),
(1601, 12, 5, 'Wuxuuna yidhi (Aabbihiis Yacquub) Wiilkayow hawga Qisoonin Riyadaada Walaalahaa oy markaas ku dhagraan dhagar, illeen Shaydaanku Dadka wuxuu u yahay col cade.'),
(1602, 12, 6, 'Saasuuna kuu doortay Eebahaa kuuna baray fasiraadda Riyada iyo Xaddithyada, kuuna taam yeeli Niemadiisa korkaaga iyo Yacquub Ehelkiisa siduu ugu taamyeelay Labadaadii Waalid mar hore (Nabi) Ibraahim iyo (Nabi) Isxaaq Eebahaana waa oge falsan.'),
(1603, 12, 7, 'Yuusuf iyo Walaalihiis Calaamadbaa ugu sugan kuwa warsan (Waano).'),
(1604, 12, 8, 'markay dheheen Yuusuf iyo Walaalkiis yaa Aabbe naga jecelyahay inagoo koox ah, Aabbeheen wuxuu ku suganyahay Baadi cad (Jecaylkaas).'),
(1605, 12, 9, 'Dila Yuusuf ama ku tuura Dhulka ha idiin kaliyoobo Wajiga Aabbihiine aadna ahaateen gadaashiis qoom Suubane.'),
(1606, 12, 10, 'mid ka midaa yidhi ha dilina Yuusuf ee ku tuura Ceel salkiis ha qaateen qaar safaree haddaad wax falaysaan.'),
(1607, 12, 11, 'waxay dheheen Aabow maxaad noogu aamini la\'dahay yuusuf anagoo u Naasixa.'),
(1608, 12, 12, 'nala dir barri ha cuno hana Ciyaaree anaguna isaga waannu ilaaline.'),
(1609, 12, 13, 'wuxuu yidhi Yacquub waxaa i walbahaarin inaad la tegtaan waxaana ka cabsan in Yeeyi Cunto idinkoo halmaansan.'),
(1610, 12, 14, 'waxay dheheen hadday Yey Cunto annagoo kooxa waxaan ka mid nahay kuwa khasaaray.'),
(1611, 12, 15, 'Markay la tageen oy ku kulmeen inay yeelaan Ceel salkii, Waxaan u waxyoonay waad uga warrami Amarkoodan iyagoon kasayn.'),
(1612, 12, 16, 'Waxayna u yimaadeen Aabahood habeenkii iyagoo ooyi.'),
(1613, 12, 17, 'Waxayna dhaheen aabbow waannu tagnay inaan orotonno Waxaana ku dhaafnay Yuusuf alaabtannada agteeda Markaasay yey cuntay aduguse nama rumaynaysid aanba Run sheegnee.'),
(1614, 12, 18, 'Waxayna la yimaadeen Qamiiskiisii oo Dhiig beena leh, wuxuuna yidhi (Yacquub) saas ma aha ee waxay idiin Qurxisay naftiinnu arrin (xaalkayguse) waa Samir fiican Eebaana laga kaalmaystaa waxaad tilmaamaysaan.'),
(1615, 12, 19, 'Waxaa yimid socoto waxayna direen Dhaamiye Wuxuuna yidhi Bishaaradayda waa Wiil, wayna Qarsadeen isagoo Badeeco ah, Eebana waa og yahay waxay fali.'),
(1616, 12, 20, 'waxayna ku gateen Qiima jaban Dirhamo tirsan Waxayna ahaayeen kuwaan rabin.'),
(1617, 12, 21, 'wuxuu ku yidhi kii gatay oo Masar ah Haweenaydiisii (wanaaji) ixtiraam hooygiisa waxay u dhawdahay innu na anfaco ama ka yeelanno ilmo, saasaana u Makaninay Yuusuf dhulka iyo inaan barro fasiraad «Riyada» xadiithyada, Eebaana u adag arrinkiisa, Laakiin badida dadku ma oga.'),
(1618, 12, 22, 'markuu gaadhay Xoogiisa yaan siinnay Xigmad iyo Cilmi (Nabinimo) saasaana ku Abaal marinaa samafalayaasha.'),
(1619, 12, 23, 'Waxaa dalbatay naftiisa tii uu joogay gurigeeda waxayna xidhay albaabyadii waxayna ku tidhi kaalay, wuxuuna yidhi magan gal Ilaah illeen waa Sayidkaygii wuxuu wanaajiyay horgaygee mana Liibaanaan daalimiintu.'),
(1620, 12, 24, 'Way ku hamiday «doontay» isna waa ku hammin lahaa hadduusan arkin xujada Eebe, saasaana uga iili Xumaanta iyo falxumida illeen wuxuu ka mid yahay addoomahannaga la doortay (Camalkana u kali yeela Eebe).'),
(1621, 12, 25, 'Waxay u orotameen albaabka (irridda) waxayna ka goysay Qamiiskiisa gadaasha waxayna kula kulmen ninkeedii irridda agteeda, Waxayna ku tidhi maxaa lagu Abaal marin Cid la doontay Ehelkaaga (haweenaydaada) daran la gaashdiiyo mooyee.'),
(1622, 12, 26, 'wuxuu yidhi Nabi Yuusuf iyadaa i dalabtay Naftayda Wuxuuna ku marag furay Marag ehelkeeda ah haduu Qamiiskiisu ka go\'an yahay xagga hore iyadaa run sheegi isna waa Beenalayaasha.'),
(1623, 12, 27, 'Hadduu Qamiiskiisu ka go\'an yahay xagga Danbana iyadaa Beenalaay ah isna (Yuusuf) wuxuu Ka mid yahay runlayaasha.'),
(1624, 12, 28, 'Markuu arkay ninkeedii Qamiiskii Yuusuf oo Gadaal ka go\'an wuxuu yidhi kaasi waa Dhagartiinii Dhagartiinuna waa wayn tahay.'),
(1625, 12, 29, 'Yuusufow iskaga jeedso Xaalkan (ha ka waramin), Haweeneeyahay adna ka Dambi Dhaaf warso Eebe danbi- gaaga waxaad kamid tahay Kuwa gafaye.'),
(1626, 12, 30, 'waxayna dheheen Haweenkii magaalada haweenaydii Casiis (Wasiirka) waxay dooni Khaadimkeeda Naftiisa (la nool) Jecelkiisaa qalbiga ka galay, waxaana ku aragnaa Baadi cad.'),
(1627, 12, 31, 'markay maqashay dhagartooda (Xantoodii) way u farriin dirtay waxayna u darabtay fadhi (martiqaad) waxayna siisay mid kasta mindi waxayna ku tidhi Yuusuf ku soo bax markay arkeenna way waynesteen waxayna iska gooyeen «Sareen» Gaemaha (Faraha) waxayna dheheen xaashaa Ilaah kani bashar (Dad) ma aha kani waxaan Malag sharaf leh ahayn ma aha.'),
(1628, 12, 32, 'waxay tidhi kaasi waa waxaad igu dagaasheen anaa doonay Naftiisa wuuna is dhawray hadduusanse falin waxaan fari walee waa la xabisa uu noqon kuwa dullooba.'),
(1629, 12, 33, 'wuxuuna yidhi (Nabi Yuusuf) Eebow Xabbis baan ka jeelahay waxay iigu yeedhi haddaadan iga iilin dhagartooda waan u iilan Xaggooda waxaana ka mid noqon Jaahiliinta.'),
(1630, 12, 34, 'waxaan ajiibay Eebihiis wuuna ka iilay dhagartooda illeen Eebe waa Maqle og.'),
(1631, 12, 35, 'markaasaa waxaa u muuqday intay arkeen aayaad (Calamooyin) inay xidhaan tan iyo Muddo.'),
(1632, 12, 36, 'waxaana la galay Xabiiska Laba dhalinyara ah midkoodbaana yidhi waxaan arkay (Riyo) anoo Miiri kahmro, kii kalana wuxuu yidhi anna waxaan arkay anoo ku xambaari madaxayga korkiisa Khubus (kibis) kana cunayso shimirtu xaggiisa nooga warran fasiraadda waxaan kugu aragnaa inaad ka mid tahay sama falayaashee.'),
(1633, 12, 37, 'wuxuu yidhi (Yuusuf) wixii cunna ah oo laydinku Arzaaqo waxaan idiinka warrami fasirkiisa ka hor imaatinkiisa, kaasina waa waxa Eebahay i baray, anuguna waxaan ka tagay (Nacay) milladda Qoomkaan rumaynayn Eebe oo aakhirana ka gaaloobay.'),
(1634, 12, 38, 'waxaana raacay milladda (Diinta) Aabayaalkay Ibraahiim, Isxaaq iyo Yacquub, Nuguma habboona inaan la wadaajinno Eebe cibaadada waxna, taasina waa fadliga Eebe ee korkannaga iyo Dadkaba, laakin Dadka badankiisu kuma shugriyaan.'),
(1635, 12, 39, 'Saaxiibadayda Xabbiskow ma ilaahyo kala tagsan yaa khayrroon mise ilaah kaliya ee awoodda badan.'),
(1636, 12, 40, 'ma caabudaysaan Eebe ka sokow waxaan magacyo aad magacowdeen ahayn idinka iyo Aabayaalkiin oon Eebe u soo dejinin xujo, xukunkana Eebe mooyee cid kale ma leh, wuxuuna faray inaydaan caabudin isaga mooyee, saasaana diinta toosana laakiin Dadka badankood ma oga.'),
(1637, 12, 41, 'Saaxiibadayda xabbiskow midkiin wuxuu waraabin sayidkiisa khamro, kan kalase waa la wadhi (Dili) oy shimbiruhu ka cuni Madaxiisa, waana la xukumay amarkaad warsateen.'),
(1638, 12, 42, 'wuxuuna (Yuusuf) ku yidhi kii uu u Maleeyay inuu nabad gali oo midkooda iigu sheeg Sayidkaaga agtiisa, Waxaase halmaansiiyay Shaydaan Xusidda Eebihiis, wuxuuna (Yuusuf) ku Nagaaday Xabbiska Dhawr sano.'),
(1639, 12, 43, 'Xaakinkii wuxuu yidhi waxaan arkay Todobo sac oo Shilshilis oy cunayaan todobo Wayda, iyo Todobo sabuul oo cagaaran, iyo kuwo kaloo ingagan dad Yahaw iiga warrama Riyadayda hadaad Fasiri kartaan riyada.'),
(1640, 12, 44, 'Waxay dhaheen waa qaraw annaguna ma naqaanno Fasirka qarawyada «dhadhabka».'),
(1641, 12, 45, 'wuxuu yidhi kii nabadgalay oo midkooda markuu xasuustay muddo ka dib Anaa idiinka Warrami fasirkiisa ee i dira.'),
(1642, 12, 46, 'Wuxuu yidhi Yuusufow Saaxiibow nooga warran Toddoba sac oo Shilshilis oycunayaan todobo Wayd ihi iyo todobo Sabuul oo cagaaran iyo kuwo ingagan oo kale inaan ula noqdo dadka inay ogaadaan.'),
(1643, 12, 47, 'Waxuuna yidhi waxaad tacbanaysaan Todoba Sano oo israaesan ee waxaad goosataan ku daaya Sabuulkiisa wax yarood cunaysaan mooyee.'),
(1644, 12, 48, 'markaas waxaa imaan arrintaas ka dib Todobo daran (abaara) oo cuni waxaad hor marsateen wax yar ood kaydsateen mooyee.'),
(1645, 12, 49, 'markaas ka dib waxaa imaan Sano loo gargaaro Dadka oo dhexdeeda ay wax miiran (Barwaaqo).'),
(1646, 12, 50, 'wuxuu yidhi Xaakimkii ii keena Yuusuf markuu kii loo diray u yimidna wuxuu ku yidhi ku noqo Sayidkaaga oo warso muxuu ahaa Xaalkii haweenkii iska Saray farahooda Eebehay waa ogyahay Dhagartooda.'),
(1647, 12, 51, 'Wuxuu yidhi Xaakimkii xaalkiinu muxuu ahaa markaad doonteen Yuusuf naftiisa waxayna Dhaheen Eebaa xumaan ka nasahane kuma ogin xumaan, Waxayna tidhi Haweenaydii Wasiirka hadduu cadaaday Xaqii anaa dalbay Yuusuf naftiisa isaguna runbuu sheegi.'),
(1648, 12, 52, 'Arrintaasna waa inuu ogaado inaanan Khayaamin isagoo maqan iyo inuusan Eebe toosinin dhagarta Khaa\'imiinta.'),
(1649, 12, 53, 'mana bari yeelayo naftayda naftu waa farid Badanta Xumaanta Naxariis Eebahay mooyee Eebahayna waa dambi dhaafe naxariista.'),
(1650, 12, 54, 'wuxuu yidhi Xaakimkii ii keena Yuusuf gooni ha ii ahaadee (talinta) markuu La hadlayna wuxuu yidhi adigu maanta agtannada waxaad ku tahay Sharafle aamina.'),
(1651, 12, 55, 'wuxuuna yidhi Yuusuf iga yeel Masuulka Khasnadaha dhulka waxaan ahay aamin cilmi lehe.'),
(1652, 12, 56, 'saasaan u makaninay Yuusuf dhulka (Masar) kagana tasarufi oo degi meejuu doono, waxaan gaadhsiinnaa Naxariistanada ciddaan doonno mana dhuminaynno Ajirka samo falayaasha.'),
(1653, 12, 57, 'Ajirka aakhiraana u khayrroon kuwa rumeeyey (Xaqa) oo ah kuwa dhawrsada.'),
(1654, 12, 58, 'waxaana yimid Yuusuf Walaalihiis wayna u soo galeen wuuna aqoonsaday iyagoon garanayn.'),
(1655, 12, 59, 'markuu darbay rarkoodii u (oofiyey miisiddii) wuxuu ku yidhi ii keena Walaalkiinna Aabbe miyeydaan arkayn inaan oofiyo Kaylka «miiska» ahayna mid dejin fiican.'),
(1656, 12, 60, 'haddaydaan ii keeninna agtayda miisid kama helaysaan hana ii soo dhawaanina.'),
(1657, 12, 61, 'waxay dheheen waannu ka dalbi Aabbihiis waana falaynaa (sidaas).'),
(1658, 12, 62, 'wuxuuna ku yidhi dhallinyaradiisii yeela alaabtooda (Badeecadooda) rarka dhexdiisa si ay u aqoonsadaan markay ku noqdaan Ehelkoodii inay soo noqdaan.'),
(1659, 12, 63, 'Kolkay u noqden Aabahood waxay dhaheen Aabaw waa naloo diiday Beegidda (hadda kadib haduuna walaalkood raacin) ee nala dir Walaalkanno aan miisannee Annaguna waanu Ilaalin isagee.'),
(1660, 12, 64, 'Wuxuuna yidhi Miyaan idinku aamini karaa sidaan idiinku aaminay walaalkiis horay, Eebaa u khayrroon Dhawre waana Naxariis badane.'),
(1661, 12, 65, 'Markay fureen Alaabtoodii waxay ka heleen Badeecadoodii oo loo soo celiyay, Waxayna Dhaheen aabow maxaan rabnaa tani waa badeecadeenii oo laynoo soo celiyay Waxaanu u Shamadsanaynaa Ehelkeenna Waxaan ilaalinaynaa Walaalkanno waxaana korosanaynaa miisid riti Taasina waa beegid sahlan.'),
(1662, 12, 66, 'Wuxuuna yidhi (Yacquub) idinla diri maayo intaad Ballan Eebe iga siisaan inaad iikeenaysaan wiilka in laydin koobo mooyee, markay siiyeen Ballankiina wuxuu yidhi ilaahay waxaynu sheegi waa u WakiilShaahida.'),
(1663, 12, 67, 'Wuxuu yidhi Wiilashaydow ha ka galina (Magaalada) irrid kaliya ee ka gala irido kala Tagsan, Waxna idiinkama tarayo xagga Eebe, Amarka cidaan Eebe ahayn maleh, isagaan talo saartay isaga uun ha talo saarteen kuwa wax talo saaran.'),
(1664, 12, 68, 'Markay ka galeen meeju faray Aabahood ma ahayn wax Eebe xagiisa wax uga tari ee waxay ahayd Dan Nafta (Nabi) Yacquub ku sugnayd oo uu Gutay, isaguna wuxuu ahaa mid cilmi leh oon Barray dadka badankiise ma oga.'),
(1665, 12, 69, 'Markay u galeen Yuusuf wuxuu Dhaweeyay Walaalkiis Wuxuuna ku yidhi Anigu walaalkaabaan ahay ee haka Murugoon Waxay faleen.'),
(1666, 12, 70, 'Markuu darbay Safarkoodii wuxuu yeelay Galaaskii walaalkii Rarkiisa, Markas waxaa Dhawaaqay mid Dhawaaqi Safaryahaw Tuugaad Tihiin.'),
(1667, 12, 71, 'Waxayna dhaheen intay soo Qaabileen maxaad waydeen.'),
(1668, 12, 72, 'Waxayna dhaheen Saacii Xaakimka ciddi keentana wuxuu yeelan rar riti anaana kafiil u ah.'),
(1669, 12, 73, 'waxayna dheheen Eebaan ku dhaaranaye waad ogtihiin inaanaan u imaanin inaan fasaadinno Dhulka Tuugana aanaan ahayn.'),
(1670, 12, 74, 'waxayna dheheen (Qoomkii Yuusuf) waa maxay Abaalkiisu haddaad Been sheegaysaan.'),
(1671, 12, 75, 'waxay dheheen Abaalkiisu waa ruuxa laga helo rarkiisa saasaana ku abaal marinnaa Daalimiinta.'),
(1672, 12, 76, 'wuxuuna ka billaabay (Fatashihii Yuusuf) Alaabtoodii Walaalkii Alaabtiisa ka hor markaasuu ka soo bixiyey Weelkii Walaalkiis, saasaana u baray kaydkaas yusuuf mana ahayn mid ku waadan kara walaalkiis diinta Boqorka in Eebe doono mooyee, Waxaan kor yeelaa darajada ciddaan doono, Mid kastoo cilmi lehna waxaa ka Sarreeya cilmi badane (Eebe)'),
(1673, 12, 77, 'waxayna dheheen (Yuusuf Walaalihiis) hadduu xaday waxaa xaday mar hore Walaalkiis, markaasuu Yuusuf u Qarsaday Naftiisa oosan u muujin, wuxuuna yidhi idinkaa ka shar badan Eebana waa ogyahay waxaad sheegeeysaan.'),
(1674, 12, 78, 'waxayn dheheen wasiirow wuxuu leeyahay (Wiilku) Aabbo wayn oo oday ah ee qaado midkanno badalkiisa waxaan kugu aragnaa samo falee.'),
(1675, 12, 79, 'wuxuuna yidhi magan Eebe inaan waadanno midkaan ka hellay alaabtannadii agtiisa mooyee, markaas daaliminiinbaan noqonaynaa.'),
(1676, 12, 80, 'markay ka quuesteen yey kaliyoobeen iyagoo faqi wuxuuna yidhi kii u waynaa waad ogtihiin in aabbihiin ballan adag idinka waaday iyo inaad horay ugu xadgudubteen yuusuf anugu dhulkan kama tagayo inta Aabbahay iga idmi ama Eebe i xukumi isagaa Xukun fiicane.'),
(1677, 12, 81, 'wuxuun ku yidhi ku noqda Aabbihiin waxaadna dhahdaan Aabbow Wiilkaagii wuu xaday, mana furaynno waxaan ognaannay mooyee, mana nihin kuwo waxa maqan illaliya (og).'),
(1678, 12, 82, 'waydiina magaaladaan ahayn iyo safarkaan la soo noqonnay annaguna runbaannu sheegi.'),
(1679, 12, 83, 'wuxuuna yidhi saas ma aha ee waxay idiin qurxisay naftiinnu arrin (xaalkayguse) waa samir fiican wuxuu u dhawyahay Eebe inuu ii keeno dhammaan illeen isagaa oge falsan ehe.'),
(1680, 12, 84, 'wuuna iskaga jeedsaday xaggooda wuxuuna yidhi walbahaarkay Yuusufow waxayna la caddadeen indhihiisu Walbahaar wuxuuna noqday mid aad u tiiraanyeysan.'),
(1681, 12, 85, 'waxayna dheheen dhaar allee ma daynaysid sheegidda Yuusuf intaad ka tabar gabtid ama ka halaagsantid.'),
(1682, 12, 86, 'wuxuuna yidhi waxaan uun u sheegan xusnigayga iyo walbaharkayga Eebe, waxaana ka ogyahay xagga Eebe waxydaan ogayn.'),
(1683, 12, 87, '(Wuxuu yidhi Yacquub) Wilashayow taga oo soo daydaya (Warkii) Yuusuf iyo Walaalkiis hana ka quusanina naxariista Eebe (Farajkiisa) kama quusto farajka Eebe qoom gaala ah mooyee.'),
(1684, 12, 88, 'markay u soo galeen Yuusuf waxay dheheen Wasiiryahow waxaa na taabtay annaga iyo Ehelkanagaba dhib waxaana la nimid badeeco liidata ee noo oofi dhammee Beegidda nuguna sadaqayso ilaahay wuxuu abaal mariyaa kuwa sadaqaystee.'),
(1685, 12, 89, 'wuxuuna ku yidhi ma ogtihiin waxaad ku fasheen Yuusuf iyo Walaalkiis idinkoo Jaahiliina.'),
(1686, 12, 90, 'waxayna dheheen oo adugu ma Yuusuf baa tahay wuxuu yidhi anugu Yuusuf baan ahay kanna waa Walaalkay Eebaa nagu mannaystay (Kulmin) illeen ciddii ka dhawrsata Eebe oo samirta Eebe ma dayaca Ajirka samofalayaashee.'),
(1687, 12, 91, 'waxay dheheen dhaar Eebee waxaa naga kiin doortay Eebe waana gafsanayn.'),
(1688, 12, 92, 'wuxuuna yidhi canaani korkiinna ma aha maanta, Eebe ha idiin dambi dhaafo isagaa Naxariis badane.'),
(1689, 12, 93, 'la taga qamiiskaygan kuna tuura Wajiga Aabbahay ha noqdo mid wax arkee iina keena Ehelkiinna dhammaan.'),
(1690, 12, 94, 'markuu safarkii soo baxay wuxuu yidhi Aabbahood anugu waxaan heli Yuusuf Dabayshiisii «Caraftiisii» haddaydaan i dhaliilayn.'),
(1691, 12, 95, 'waxayna dheheen adugu wali waxaad ku sugantahay gafkii hore, (Jacaylkii Yuusuf).'),
(1692, 12, 96, 'markuu yimid kii bishaaraynayey wuxuu ku tuuray (Qamiiskii) Wajiigiisa wuxuuna noqday mid wax arka, wuxuuna yidhi miyaanan idinku dhihin anugu waxaan ka ogahay xagga Eebe waxaydaan ogayn.'),
(1693, 12, 97, 'waxayna dheheen Aabbow nooga dhaafid warso dambigannaga waan gafsanayne.'),
(1694, 12, 98, 'wuxuuna yidhi waan idiinka dambi dhaaf warsandoonaa Eebahay isagaa dambi dhaafe Naxariista ehe.'),
(1695, 12, 99, 'Markay u galeen Yuusuf wuxuu isu dumay Labadiisii Waalid, Wuxuuna yidhi gala Masar Eebe hadduu Doono idinkoo aamina.'),
(1696, 12, 100, 'Wuxuuna u koryeelay labada waalid Kursigii, Waxayna ula hoobteen Sujuud, «Salaan» wuxuuna yidhi Aabbow waatan Fasirkii Riyadaydii horay, Wuxuu ka yeelay Eebahay Xaq (sugan) wuuna ii Wanaag falay Markuu iga soo Bixiyay Xabbiska idinkana keenay Baadiyaha Kadib intuu Shaydaan fasaadiyay ani iyo Walaalahay Dhexdanada Eebahayna waa u turaha wuxuu Doono waana Oge falsan.'),
(1697, 12, 101, 'Eebow waxaad i siisay Xukun Waxaadna i bartay Fasiraadda riyada, Eebaha abuuray Samooyinka iyo Dhulkow adaa wali (Gargaare) ii ah aduun iyo Aakhiroba ee i oofso anoo Muslim ah ina Haleeshii kuwa suuban.'),
(1698, 12, 102, 'Kaasi waa wararka wax Maqan Annagaana Kuu Waxyoon, Mana aadan ahayn Agtooda Markay kulmayeen Amarkoodii iyagoo Shirqooli,'),
(1699, 12, 103, 'Dadka badankiisna ma Rumaynayaan aad kuba Dadaashide.'),
(1700, 12, 104, 'Wax ujuura ahna kama Warsanaysid (Xaqa) waa uun waanada Caalamka.'),
(1701, 12, 105, 'Badanaa Calaamad samooyinka iyo Dhulka oo ay Marayaan iyagoo ka Jeedsan.'),
(1702, 12, 106, 'Mana rumaynayaan Badankoodu Eebe iyagoo la Wadaajin mooyee.'),
(1703, 12, 107, 'Ma waxay ka Aamin noqdeen inay u timaado MasiiboCadaabka Eebe ama ugu Timaado Saacaddu Kado iyagoo ogayn.'),
(1704, 12, 108, 'Dheh tan waa waddadayda (xaqa ah) Waxaana ugu yeedhi (dadka) Xagga Eebe anoo Ku sugan Xujo cad aniga iyo inta i raacda, Waxaana Nasahay Eebe, kamidna ma ihi Mushrikiinta.'),
(1705, 12, 109, 'Ma ahayn kuwii aan diray hortaa waxaan Rag loo Waxyoodo ahayn o Ehelka Magaalooyinka miyayna soconin Dhulka oo ay Fiiriyaan siday noqotay Cidhibta Kuwii ka horeeyay, Daarta Aakhiro yaana u khayrroon kuwii Dhawrsadayee Miyaydaan kasayn.'),
(1706, 12, 110, 'markay Quusteen Rasuuladii una Maleeyeen in la beeniyay waxaa U yimaadda gargaarkanaga waxaana korinaynaa ciddaan doono lagamana celiyo Ciqaabtannada Qoomka Danbiilayaasha ah.'),
(1707, 12, 111, 'Waxaa ugu sugan Qisadooda waano Kuwa Caqliga leh, mana aha Sheeko la Been abuurtay Waase urumayn wixii ka horeeyay (oo Kutubtii Xaqa ahayda) iyo Caddaynta Wax kasta iyo Hanuunka iyo Naxariista Ciddi Rumayn Xaqa.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Xarfaha hore oo kale waan soo Sheegnay Fasirkooda, Kuwaasna waa Aayaadkii Kitaabka (Quraanka), Waxaa lagaaga soo Dejiyay xagga Eebahaa waana Xaq Dadka Badankiise ma Rumaynin.'),
(1709, 13, 2, 'Eebe waa kan koryeela Samooyinka Tiir La\'aan aad Aragtaan Markaas ku istiwooday Carshiga, Sakhirayna Qorraxda iyo Dayaxa Dhammaana waxay ku socdaan Muddo magacaaban, Eebaa maamula amarka Caddeeyana aayaadka Inaad la kulanka Eebihiin yaqiinsataan.'),
(1710, 13, 3, 'Eebe waa kan Fidiyay Dhulka Yeelayna Dhexdiisa buuro, iyo wabiyo Midho kastana ka yeelay Dhexdiisa laba nooc Wuxuuna ku Dadaa Habeenkii Maalinta, Arrintaasna Calaamooyin yaa ugu Sugan ciddii fikiri.'),
(1711, 13, 4, 'Dhulka waxaa ah Goosinno daris ah (isku dhaw) iyo Bustaan Cinaba iyo Beer iyo Timir oo Mataano ah iyo kuwo aan Mataano ahayn, laguna Waraabiyo Biyo kaliya Waxaana ku kala Fadilaynaa (Wanaajinaynaa) qaar qaarka kale Cunnada (Dhadhanka) Arrintaasna Calaamaa ugu Sugan Ciddii Wax kasi.'),
(1712, 13, 5, 'Haddaad yaabtana waxaa Yaaba Hadalkoodii Ahaa ma Markaan noqonno Carro yaa Markaas Abuur Cusub (Nala abuuri) Kuwaasi waa kuwa Luquntooda la Yeelay Katiinado waana kuwo Naarta Ehelkeeda ah Kuna Dhex waari.'),
(1713, 13, 6, 'Waxayna kaa Dedejisan Xumaan wanaag ka hor Iyadooy Tagtay Hortood tusaalayaal (Ciqaabeed) Eebahaana waa u danbi Dhaafaha dadka iyagoo Dulmi ku sugan Eebahaana waa Ciqaab darane.'),
(1714, 13, 7, 'waxay dheheen kuwii gaaloobay maxaa loogu soo Dejin waayey korkiisa Aayad xagga Eebihiis ka timid, adugu waxaad uun tahay Dige cid kastana waxay leedahay Hanuuniye.'),
(1715, 13, 8, 'Eebaa og waxay ku siddo uurka Dhaddig kasta, iyo waxay Ilmo galleennadu Nusqaamin, iyo waxay kordhin (Dhicis iyo Shin) wax kastana Eebe agtiisa Qaddar (Waqti) yuu leeyahay.'),
(1716, 13, 9, 'wuxuuna ogyahay wax maqan iyo waxa jooga waana weyne ka sarreeya (wax kasta).'),
(1717, 13, 10, 'waana isku mid kiinna qarsada hadalka iyo kiinna la qaylya, iyo kii isqariya habeenkii iyo kii Muujiya Maalintii.'),
(1718, 13, 11, 'wuxuuna leeyahay (Ruux kasta) kuwo ku isbedela (Malaa\'ig) oo hortiisa iyo Gadaashiisa ah kuna dhawra amarka Eebe, Ilaahayna ma dooriyo wax dad ku suganyahay intay ka dooriyaan waxa naftooda ku sugan, markuu Eebe la doono Dad dhibna wax ka celin ma jiro, mana laha Eebe ka sokow Gargaare.'),
(1719, 13, 12, 'Eebe waa kan idin tusiya Hillaaca cabsi gelin iyo rajo gelin darteed, wuxuuna abuuraa daruurta culus.'),
(1720, 13, 13, 'wuxuuna la tasbiixsadaa onkadku Mahadda Eebe ku dheehan iyo Malaa\'igtuna (waxay la tasbiixsan) Cabsida Eebe, wuxuuna diraa Danabyo wuxuuna ku ridaa cidduu doono, iyaguna waxay ku murmi Eebe isagoo ciqaabtiisu darantahay.'),
(1721, 13, 14, 'Eebe waxaa u Sugnaaday Baryo (iyo Cibaado) Xaqa, waxa ka soo hadhayna waxba uma ajiibaan (kuwa baryi) sida mid ku fidiyey Gacmihiisa Xagga Biyaha inay Afkiisa gaadhaan, mana gaadhayaan, Baryada Gaaladuna waxaan baadi ahayn ma aha.'),
(1722, 13, 15, 'waxaa Ilaahay u Sujuuda «u khuduuca» waxa ku sugan Samooyinka iyo Dhulka Ogolaansho iyo Qasab iyo Hooskooda aroor iyo Galbihiiba.'),
(1723, 13, 16, 'waxaad dhahdaa yaa ah Eebaha Samooyinka iyo Dhulka, waxaadna dhahdaa waa Eebe, waxaadna dhahdaa ma yeelanaysaan Eebe ka sokow Awliyo (gargaarayaal) oon u hananayn naftooda Nacfi iyo dhib midna, dheh ma egyihiin Indhoole iyo wax Arke, mase ehyihiin Mugdiyo iyo Nuur mise waxay u yeeleen Eebe Shuruko wax u Abuuray sida Abuurka Eebe oo waxaa isaga ekaaday Abuurkii, waxaad dhahdaa Eebaa wax kasta Abuuray waana kali awood badan.'),
(1724, 13, 17, 'wuxuu ka soo dejiyey Samada Biyo waxaana la durduray waadiyo Awooddood wuxuuna xambaartaa daadku daadxoor sarraysa, waxay dabka ku huriyaanna Doonid isku qurxin, ama alaabo waxaa ah abuur la mida, saasuuna Eebe ugu yeelan tusaale xaqa iyo Baadilka, Xumbaduse way tagi iyadoo ingagan waxa dadka anficise wuxuu ku nagaan dhulka, saasuuna Eebe u yeelaa Tusaalayaasha.'),
(1725, 13, 18, 'kuwa Eebahood ajiibay (Maqlay) waxaa u sugnaaday wanaag (janno) kuwaan ajiibin (maqlinna) hadduu u sugnaado wax dhulka ku sugan dhammaan oo wax la mida la jiro way isku furan lahaayeen kuwaasna waxaa u sugnaaday xisaab xun (daran) Hooygooduna waa Jahannamo iyadaana u xun gogol.'),
(1726, 13, 19, 'cid og in waxa laggaga soo dejiyey xagga Eebahaa uu xaq yahay ma la midbaa mid indho la\', waxaa uun waantooma kuwa caqliga leh.'),
(1727, 13, 20, 'ee ah kuwa Oofiya ballanka Eebe oon burininna ballanka adag.'),
(1728, 13, 21, 'ee ah kuwa xidhiidhiya waxa Eebe faray in la xidhiidhiyo kana cabsada Eebahood kana cabsada xisaab xun.'),
(1729, 13, 22, 'ee ah kuwa u samray doonid wajiga Eebahood dartiis, oogayna Salaadda, kana bixiyey waxaan ku arzaaqnay qayb qarsoodi iyo muuqaalba, raacsiiyana wanaagga xumaanta, kuwaas cidhib fiican yaa u sugnaatay.'),
(1730, 13, 23, 'waxaana gali jannada cadnin iyaga iyo cidda suuban oo Aabbayaalkooda, Haweenkooda iyo caruurtooda, Malaa\'igtuna waxay uga soo gali irid kasta.'),
(1731, 13, 24, 'iyagoo ku dhihi Nabadgalyo korkiinna ha ahaato samirkiinna dartiis waxaa wanaag badan cidhibta daarta (Janno).'),
(1732, 13, 25, 'kuwa buriya Ballanka Eebe adkayntiisa ka dib gooyana wuxuu faray Eebe in la xidhiidhiyo oo fasaadiyana dhulka kuwaas waxaa u sugnaaday Lacnad iyo Guri xun.'),
(1733, 13, 26, 'Eebaana u fidiya rizqiga cidduu doono kuna cidhiidhya (cidduudoono), waxayna ku farxeen gaaladii Nolosha Adduunyo, mana aha nolosha adduunyo Aakharo agteeda waxaan maalmo lahu raaxaysto ahayn.'),
(1734, 13, 27, 'waxay dhihi kuwii gaaloobay maxaa loogu soo dejin waayey korkiina Aayado Eebihiisa xaggiisa, waxaad dhahdaa Eebe wuu dhumiyaa ciduu doono (oo xaqa diida) wuxuuna u hanuuniyaa xagiisa ciddii u noqota.'),
(1735, 13, 28, 'ee ah kuwa rumeeyey Xaqa kuna xasishay Quluubtoodu xuska Eebe, xuska Eebayna ku xasishaa Quluubtu.'),
(1736, 13, 29, 'ee ah kuwa rumeeyey xaqa falayna camal suuban waxaana u sugnaaday khayr iyo Nicmo iyo Noqosho wanaagsan.'),
(1737, 13, 30, 'saasaan kuugu dirray Ummad ay tageen Ummado hortood inaad ku akhrido kaan kuu waxyoonay iyaguna waxay ka gaaloobi Eebaha Raxmaana, waxaad dhahdaa Eebe mooyee Illah kale ma jiro, isagaana tala saartay xagiisaana u noqon.'),
(1738, 13, 31, 'haddii Quraan dartiis buuro lala kaxayn, ama lala goyn dhulka ama lagula hadli Dadki dhintay (Wuxuu noqon lahaa Quraankan) amarkase Eebaa dhammaantiis iska leh, miyeyanse ogayn kuwa rumeeyey xaqu hadduu Eebe doonoinuu hanuunin lahaa Dadka dhammaan kamana tagaan kuwii gaaloobay (Xumaan) intay kaga dhacdo Musiibo waxay faleen Darteed ama degto meel ku dhaw guryahooda intuu ka yimaaddo Yaboohii Eebe, Illaah Ma baajiyo ballanka.'),
(1739, 13, 32, 'waxaa lagy Jees Jeesay Rasuullo ka horreeyey markaasaan sugay kuwii gaaloobay, ka dibna waan Qabtay ee siday noqotay ciqaabtaydii (Way darrayd).'),
(1740, 13, 33, 'cid ku ilaalin naf kastaa waxay kasbatay (Eebe) ma la midbaa midaan saas ahayn, waxayna u yeeleen Eebe Shurako dheh magacaaba, mise waxaad Eebe uga warramaysaan waxaan Eebe uga warramaysaan waxaan joogin Dhulka, mise hadal muuqaala, saas ma aha ee waxaa loo qurxiyey kuwii gaaloobay dhagartooda waxaana laga leexiyey Jidka, cid Eebe dhumiyeyna ma laha wax hanuunin.'),
(1741, 13, 34, 'waxayna mudan cadaab nolosha adduun, cadaabka aakhiraana daran mana u sugna wax Eebe ka Ilaalin.'),
(1742, 13, 35, 'tusaalaha (Tilmaanta) jannada loo darbay kuwa dhawrsada waa inay dhex socdaan Wabiyaalkii, Cunnadeeduna waa joogto iyo hooskeedu, taasina waa cidhibta kuwa dhawrsada cidhibta gaaladuna waa Naar.'),
(1743, 13, 36, 'kuwaan siinnay Kitaabka waxay ku farxaan waxa lagugu soo dejiyey, xisbiyada waxaa ka mida kuwo diidi Qaarkiis, waxaad dhahdaa waxaa uun lay faray inaan caabudo Eebe una shariig yeelin, xaggiisaan u yeedhi xaggiisaana u noqon.'),
(1744, 13, 37, 'saasaana kuugu soo dejinnay xukun Carabi ah, haddaad raacdo hawadoodana intuu cilmi ku soo gaadhay ka dib, kama helaysid xagga Eebe gargaare iyo dhawre.'),
(1745, 13, 38, 'waxaan dirray hortaa rasuullo waxaana u yeellay Haween iyo caruur, Rasuulna Mucjiso lama imaan karo idimka Eebe La\'aantiis, muddo kastana way Qorantahay.'),
(1746, 13, 39, 'eebana wuu tiraa wuxuu doono wuuna sugaa, Agtiisana waxaa ah Looxul maxfuudka.'),
(1747, 13, 40, 'haddaan ku tusinno qaar waxaan u yaboohi (oo dulli ah waa saas) haddaanse ku Qofsanno waxaa uun korkaaga ah Gaadhsiin Anagana waxaa na saaran inaan xisaabinno.'),
(1748, 13, 41, 'miyeyna arkayn inaannu uga imaan dhulka darfaha anagoo nusqaamin furid (ama Abaar) Eebana wuu xukumi, wax burinna ma jiro xukunkiisa, waana midkay deg deg tahay xisaabtiisu.'),
(1749, 13, 42, 'waxaa wax dhagray kuwii ka horreeyey Eebaase abaal maris (Dhagar) iska leh dhammaan, wuxuuna ogyahay waxay kasban naf kasto, waxayna ogaan Gaaladu cidday u ahaato cidhib fiican.'),
(1750, 13, 43, 'waxayna dhihi kuwii Galoobay ma tihid Rasuul, waxaad dhahdaa waxaa marag ugu filan dhexdeenna Eebe iyo cidda agteed ogaanshaha Kitaabku yahay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'xarfaha hore oo kale waa la soo sheegay macnahooda, kanna waa Kitaab aannu kugu soo dejinnay inaad kaga bixisid dadka mugdiyada xagga Nuur idamka Eebehood iyo jidka Eebaha adkaada ee la Mahadiyo.'),
(1752, 14, 2, 'Ilaaha iska leh waxa Somooyinka iyo Dhulka ku sugan, halaagna waxaa mudan Gaalada cadaab daran xaggiis.'),
(1753, 14, 3, 'oo ah kuwa ka jecel nolosha adduun Aakhiro oo ka leexiya (Dadka) Jidka Eebe lana dooni Qallooc, kuwaasuna waxay ku suganyihiin Baadi fog.'),
(1754, 14, 4, 'Rasuul kuma dirro waxaan Afka Qoomkiisa ahayn si uu ugu caddeeyo, wuuna baadiyeeyaa Eebe cidduu doono inta xaqa loo caddeeyey ka dib, wuuna hanuunin cidduu doono, waana adkaade falsan.'),
(1755, 14, 5, 'waxaan la dirray (Nabi) Muuse Aayaadkannaga bixi Qookaaga Mugdiyada xagga Nuurka, kuna waani «xusuusi» Nicmooyinkii Ilaahay arrintaasna Aayaadbaa (Calaamooyin) ugu sugan samir badane, shugri badan dhammaantiis.'),
(1756, 14, 6, 'iyo markuu Nabi Muuse ku yidhi Qoomkiisa xusuusta Nicmada Eebe korkiinna markuu idinka koriyey Colkii Fircoon iyagoo idin dhadhansiin cadaab (Daran) Xun oo gawrici Wilashiinna oo dayn Gabdhihiinna (Dullaysi) arrintaasna waxaa ku sugnayd Balayo (Intixaam) Eebihiin oo wayn.'),
(1757, 14, 7, 'iyo markuu Eebihiin ogaysiiyey haddaad shugridaan waan idiin kordhin, haddaad gaalowdaanna cadaabkaygu waa daranyahay.'),
(1758, 14, 8, 'wuxuuna yidhi (Nabi) Muuse haddaad Gaalowdaan idinka iyo waxa ku sugan Dhulka dhammaan Eebe waa Hodan mahadsan.'),
(1759, 14, 9, 'miyuuna idin soo gaadhin warkii kuwii idinka horreeyey (oo ah) Qoomkii Nabi Nuux, Caad, Thamuud, iyo kuwii ka dambeeyey, ma oga (Tiradooda) Eebe mooyee, waxayna ula yimaadeen Rasuuladoodii xujooyin waxayna ku celiyeen Gacmahooda Afkooda (Way beeniyeen) waxayna dhaheen anagu waan ka gaalownay waxa laydinku soo diray annguna shakibaan kaga sugannahay waxaad noogu yeedhaysaan xaggiisa oo daran.'),
(1760, 14, 10, 'waxay dheheen Rasuuladoodii ma Eebaa shaki ku jiraa ee abuuray Samooyinka iyo Dhulka idiinkuna yeedhi inuu idinka dhaafo dambiyadiinna dibna idiin dhigo tan iyo muddo magacaaban, waxayna dheheen idinku ma tihiin waxaan bashar (Dad) nala mida ahayn, waxaadna doonaysaan inaad naga leexisaan waxay caabudi jireen Aabbayaalkanno noo keena xujo cad.'),
(1761, 14, 11, 'Rasuulladii waxay dheheen annagu ma nihin waxaan dad idin la mida ahayn, Eebaase ku mannaysta (khayr) cidduu doono oo addoomadiisa ka mida, xujana idiinma keeni karro idinka Eebe mooyee, Eebe uunna ha tala saarteen Mu\'miniintu.'),
(1762, 14, 12, 'maxaanaan u talo saaranayn Eebe isagoo nagu hanuuniyey Jidkannaga, waana ku samraynaa waxaad nagu dhibaysaan Eebe uun ha talo saarteen kuwa talosaaran (Cid).'),
(1763, 14, 13, 'waxay ku dheheen kuwii gaaloobay Rasuulladoodii waxayna ku soo noqonaysaan (soo galaysaan) diintannda, wuxuuna u waxyooday Eebahood inuu halaago daalimiinta.'),
(1764, 14, 14, 'dajinna idinka dhulka kuwaas ka dib, arrintaasna waxaa heli ciddii ka cabsata la kulankayga oo ka cabsata gooddigayga.'),
(1765, 14, 15, 'wayna gargaar dalbeen waxaana khasaaray kibir badane madax adag dhammaantiis (xaq diid)'),
(1766, 14, 16, 'waxaana ka horraysa jahannamo waxaana laga waraabin Biyo carow ah (Dhaacaanka Ehelu Naarka).'),
(1767, 14, 17, 'wuuna ku margan mana ka degayo wuuna uga imaan Mawdku (Geeridu) meel kasta, mana dhimanayo, gadaashiisna waxaa ah cadaab adag.'),
(1768, 14, 18, 'kuwa ka gaaloobay Eebahood wuxuu la midyahay camlakoodu dambas ku darraatay «ku dhacday» dabayli maalin dabayl dhabasho daran, kama karaan (helaan) waxay Kasbadeen waxba, kaasina waa baadida fog uun.'),
(1769, 14, 19, 'miyeydaan arkayn in Eebe u abuuray Samooyinka iyo dhulka xaq, hadduu doonana idin tagsiin karo lana imaan karo khalqi cusub.'),
(1770, 14, 20, 'arrintaasina Eebe kuma adka.'),
(1771, 14, 21, 'waxayna dadku u soo bixi Eebe markaasay ku dhihi kuwii «raaciyadda» ahaa kuwii madaxda ahaa anagu idinkaan idin raacaynay ee wax ma nooga taraysaan cadaabka Eebe, waxayna dhahaan hadduu Eebe na hanuuniyo waan idin hanuunin lahayn, waa isugu mid korkeenna haddaan argagaxno ama samirnno (adkaysanno) ma helayno fakasho.'),
(1772, 14, 22, 'wuxuuna dhahaa shaydaankii marka amarka la xukumo, Eebe wuxuu idiin yaboohay yabooh xaq ah anna waan idiin yaboohay waana idinku beeniyey, wax xujo ahna idinkuma lahayn oon ahayn inaan idiin yeedhay ood i maqasheen ee hay dagaalina ee dagaala naftiinna anuguna idiinma gargaari karo idinna iima gargaari kartaan, anugu waan diiday waxaa ila wadaajiseen horay daalimiintuna waxay mudan cadaab daran.'),
(1773, 14, 23, 'waxaana la gelin kuwii rumeeyey xaqa oo camal fiican falay Jannooyin ay socoto dhexdeeda wabiyadii iyagoo ku dhex waari idanka Eebe dartiis soo dhawayskooduna dhexdeeda waa salaan.'),
(1774, 14, 24, 'ma ogtahay sida Eebe ugu yeelay Tusaale kalimada (Erayga) Fiican sida Geed wanaagsan oo laamaheediina samada (kor jiraan).'),
(1775, 14, 25, 'oo siisa (Bixisa) Cunnadeeda Waqti kasta idanka Eebaheed dartiis, wuuna u yeelada Eebe tusaayaal Dadka inay waantoomaan.'),
(1776, 14, 26, 'Kalimada (Erayga) xunna (Gaalnimada iyo xumaanta) waxay la mid tahay geed xun lagana rujiyey dhulka korkiisa oon sugnaan lahayn.'),
(1777, 14, 27, 'wuxuu ku sugaa Eebe kuwa rumeeyey xaqa Erayga sugan (ashaadada iyo xaqa) Nolosha adduun iyo Aakhiraba wuxuuna falaa Eebe wuxuu doono.'),
(1778, 14, 28, 'ma ogtahay kuwa ku badelay Nicmaddii Eebe Gaalnimo, Dejiyeyna qoomkoodii guri halaag.'),
(1779, 14, 29, 'Jahannamayna gali meel lagu sugnaadana iyadaa u xun (Jahannamo).'),
(1780, 14, 30, 'waxayna u yeeleen Eebe shariig inay ka dhumiyaan jidkiisa, waxaad dhahdaa raaxaysta ahaanshihiinnu waa Naare.'),
(1781, 14, 31, 'Ku dheh addoomadayda rumeeyey (Xaqa) ha oogeen salaadda hana ka Nafaqeeyeen waxaan ku arzaaqay qarsoodi iyo muuqba ka hor imaatin Maalin aan gadaho iyo saaxiib jirin (qiyaamada).'),
(1782, 14, 32, 'Eebe waa kan Abuuray samooyinka iyo dhulka kana soo dejiyey samada Biyo kuna soo bixiyey midho rizqigiinna dartiis, idiinna sakhiray doonta inay ku socoto badda amarka Eebe idiinna sakhiray Wabiyada.'),
(1783, 14, 33, 'idiinkuna sakhiray Qorraxda iyo Dayaxa si joogta ah idiinna sakhiray habeenka iyo Maalinta.'),
(1784, 14, 34, 'idinkana siiyey wax kastood warsataan, haddad tirisaanna Nicmada Eebana ma koobi Kartaan, dadse waa dulmi badane gaalnimo badan.'),
(1785, 14, 35, 'zusuusta markuu Nabi Ibraahim yidhi Eebow ka yeel baladkan (Maka) Aamin igana dheeree ani iyo caruurtayda inaan caabudno Sanamyada.'),
(1786, 14, 36, 'Eebow waxay dhumiyeen wax badan oo ciddii raacdana waa iga mid tii i caasidana adaa dambi dhaafe naxariista ah.'),
(1787, 14, 37, 'Eebow waxaan dejiyey caruurtaydii (Qaar) tog aan been lahayna oo ah Baydkaaga sharafta leh agtiisa Eebow inay Oogaan salaadda ee ka yeel Quluubta dadka tii u iilata xaggooda kuna arzaaq midho inay shugriyaan.'),
(1788, 14, 38, 'Eebow adugu waxaad ogtahay waxaannu qarin iyo waxaannu Muujin, kamana Qarsoona Eebe wax ba samada iyo dhulka (midna).'),
(1789, 14, 39, 'mahad Eebaa iska leh ee ah kan i siiyey anoo wayn Ismaaeiil iyo Isxaaq Eebahayna waa maqlaha baryada (ajiibe).'),
(1790, 14, 40, 'Eebow iga yeel mid ooga salaadda aniga iyo caruurtayda Eebow Aqbal baryadayda.'),
(1791, 14, 41, 'Eebow ii dambi dhaaf ani iyo Labadaydii Waalid iyo Mu\'miniinta Maalinta ay kici xisaabtu (Qiyaamadu).'),
(1792, 14, 42, 'ha u malayn in Eebe halmaansanyahay waxay fali daalimiintu, wuxuu uun dib ugu dhigi maalin ay taagni dhexdiisa indhuhu.'),
(1793, 14, 43, 'oyna deg degi oo madax kor yeeli oosan soo noqonayn aragoodu (taagmi indhuhu) Quluubtooduna oy madhantahay «cabsi darteed».'),
(1794, 14, 44, 'uga did dadka maalintuu u iman cadaab oy odhan kuwii dulmi falay eebow dib noogu dhig muddo dhaw aan ajiibno yeedhidaadee oon raacno rasuuladee, miyeydaan ahayn kuwii ku dhaartay horay inaydaan tegayn.'),
(1795, 14, 45, 'oo degteen guryihii kuwii dulmiyey naftooda oo idiin caddaaday sidaan ku fallay iyagii oon idiin yeellay tusaalayaal.'),
(1796, 14, 46, 'iyagoo dhagar samayn Eebe agtiisaana Abaalka dhagartoodu yahay, dhagartooduna ma aha mid la suulaan (tagaan) Buuruhu.'),
(1797, 14, 47, 'ee ha u malayn in Eebe baajin u yabooha Rasuulladiisa illeen Ilaahay waa adkaade aarsadee.'),
(1798, 14, 48, 'Maalin Buuna (oofin) lagu bedeli dhulka Dhul kale iyo Samooyinkaba, waxaana loo soo bixi Eebaha kalida ah ee awoodda sara leh.'),
(1799, 14, 49, 'Waxaadna arkaysaa Danbiilayaashii oo Maalintaas lagu xidhiidhiyay (lays kulaxidhay) Biro (Naareed).'),
(1800, 14, 50, 'Dharkooduna (Qamiisyadoodu) waa Daamur (Naara) waxaana dabooli wajigooda naar.'),
(1801, 14, 51, 'inuu ka Abaal mariyo Eebe naf kasta Waxay kasbatay, Eebana way deg degtaa xisaabtiisu.'),
(1802, 14, 52, 'Quraankana waa gaadhsiin Dadka iyo in Loogu Digo iyo inay ogaadaan in Eebe Ilaah Kaliya uun Yahay, iyo inay waantooto Cidda Caqliga leh (Mu\'miniinta).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Xarfaha hore waa la soo sheegay Macnahooda, Kuwana waa Aayadaha Kitaabka iyo Quraanka cad.'),
(1804, 15, 2, 'Wax badanbay tamanin (Jeclaan) kuwii Gaaloobay (Aakhiro) inay Muslimiin ahaadaan (adduunkii).'),
(1805, 15, 3, 'ka tag ha cuneen hana raaxasyteene Hana Shuqliso yididiilo way ogaan doonaane,'),
(1806, 15, 4, 'Magaalo la halaago oo kasta waxaa u sugnaaday Wakhti la yaqaanno.'),
(1807, 15, 5, 'Kamana hor marto umadi Ajasheeda (wakhtigeeda) Kamana dib marto.'),
(1808, 15, 6, 'Waxay dhaheen kan Quraanka lagu soo dejiyayow Adugu waad waalantahay.'),
(1809, 15, 7, 'Maad malaa\'ig noo keentid haddaad run Sheegi.'),
(1810, 15, 8, 'Umana soo Dehinno Malaa\'igta xaq mooyee lamana sugeen Gaalada (markaas).'),
(1811, 15, 9, 'Annagaa soo dejinnay Quraanka annagaana Dhawri.'),
(1812, 15, 10, 'waxaan u dirray hortaa (rasuullo) Ummadihii hore.'),
(1813, 15, 11, 'Rasuul kasta oo u yimaaddana waxay ahaayeen kuwo ku jeesjeesa.'),
(1814, 15, 12, 'saasaana u galinaa (Gaalnimada) quluubta dambiilayaasha.'),
(1815, 15, 13, 'mana rumeeyaan Quraanka waxaana tegay (hor maray) sumaddii «Jidkii» kuwii horreeyey.'),
(1816, 15, 14, 'haddaan uga furro korkooda irrid samada ooy ahaadaan kuwo kora'),
(1817, 15, 15, 'waxay odhan lahaayeen waxaan uun la awday (La tiray) aragyadannadii, waxaabanse nahay qoom la sixray.'),
(1818, 15, 16, 'waxaan yeellay samada buruuj (Meelo) waxaan u qurxinnay kuwa dayi (eegi).'),
(1819, 15, 17, 'waxaana ka dhawray shaydaan la tuuray «fogeeyey» dhammaantiis.'),
(1820, 15, 18, 'mid dafa maqalka oo danab cad raaco mooyee.'),
(1821, 15, 19, 'dhulkana waan fidinay waxaana ku ridnay «sugnay» dhexdeeda Buuro, waxaana ka soo bixinay wax kasta oo Miisaaman.'),
(1822, 15, 20, 'waxaana idiinka yeellay dhexdiisa nolol iyo waxaydaan arzaaqi karin.'),
(1823, 15, 21, 'waxaan agtanada khasnadihiisu «kaydkiisu» ahayn ma jiro, umana soo dejinno qaddar la ogyahay mooyee.'),
(1824, 15, 22, 'waxaana dirnaa dabaysha oo Tallaasha (Daruurta) waxaana ka soo dajina samada Biyo aan idinku waraabinno idinkoo ahayn kuwo ka keydsan kara (gudhidda).'),
(1825, 15, 23, 'annagaa wax looleeyna waxna dilla, anagaana wax dhaxalla uun (u hadha).'),
(1826, 15, 24, 'waxaana ognahay kuwiinna hor maray waxaana ognahay kuwiina dib maray.'),
(1827, 15, 25, 'Eebahaana isagaa soo kulmin dadka waana falsame oge ah.'),
(1828, 15, 26, 'annagaana ka abuuray dadka dhoobo doorsoon.'),
(1829, 15, 27, 'Jaankana (Jinniga) waxaan ka abuuray mar hore Naar samuum leh (Kulayl).'),
(1830, 15, 28, 'Xusuuso markuu ku yidhi Eebahaa malaa\'igta Anugu waxaan ka abuuri Dad dhoobo doorsoon.'),
(1831, 15, 29, 'Ee markaan ekeeyo kuna afuufo dhexdiisa Ruuxdayda ugu dhaca Sujuud.'),
(1832, 15, 30, 'wayna Sujuudeen malaa\'igtii dhammaan oo Kulansan.'),
(1833, 15, 31, 'Ibliis oo diiday inuu ka mid noqdo kuwa Sujuuday mooyee.'),
(1834, 15, 32, 'Wuxuuna yidhi (Eebe) Ibliisow maxaad leedahay oodan u noqonin kuwa Sujuuda.'),
(1835, 15, 33, 'Wuxuuna yidhi ma ihi Mid u sujuudi dad aad ka Abuurtay Dhoobo doorsoon.'),
(1836, 15, 34, 'Wuxuuna yidhi Eebe ka bax xageeda adigu waa lagu fogeeyaye.'),
(1837, 15, 35, 'Korkaagana waxaa ah Lacnad tan iyo Maalinta Abaal marinta (Qiyaamada).'),
(1838, 15, 36, 'Wuxuuna yidhi Eebow i sug tan iyo maalinta La soo bixin dadka.'),
(1839, 15, 37, 'Wuxuuna yidhi Eebe Adugu kuwa la sugi Yaad ka mid tahay.'),
(1840, 15, 38, 'Tan iyo maalinta Wakhtiga la yaqaan leh.'),
(1841, 15, 39, 'Wuxuuna yidhi Ibliis Eebow i baadiyayntaada Darteed waxaan u Qurxin dhulka Dhexdiisa waana Dhumin dhammaan.'),
(1842, 15, 40, 'Addoomadaada cibaadada kuu kali Yeelay mooyee (lana doortay).'),
(1843, 15, 41, 'Eebe wuxuu yidhi kaasi waa Jid igu toosan (oo layska abaal marin).'),
(1844, 15, 42, 'Addoomadayda (toosan) Kuma lihid xujo Cid Ku raacday oo kuwa Baadida ah ka mida Mooyee.'),
(1845, 15, 43, 'Jahanamaana yaboohoodu yahay (inta dhunta) Dhammaan.'),
(1846, 15, 44, 'Waxayna leedahay todoba irridood oo irrid Kastana waxay leedahay Qaddar loo qaybiyay (oo Ibliis iyo Colkiisa ah).'),
(1847, 15, 45, 'kuwa Eebe ka dhawrsadana waxay gali Janooyin iyo Ilo (durduri).'),
(1848, 15, 46, 'waxaana la dhahaa ku gala Nabad galyo iyo Aaminimo.'),
(1849, 15, 47, 'waxaana ka siibnaa waxa laabtooda ku jira oo xiqdi ah, waana walaalo sariira isqaabilsan ku sugan.'),
(1850, 15, 48, 'kumana taabto jannada dhesdeeda dhib xageedana lagama Bixiyo.'),
(1851, 15, 49, 'U warran addomadayda inaan anugu Dambi dhaafe Naxariista ahay,'),
(1852, 15, 50, 'iyo in Cadaabkaygu uu yahay Cadaabka daran uun.'),
(1853, 15, 51, 'Ugana waran dadka Martidii (Malaa\'ig) Nabi Ibraahim.'),
(1854, 15, 52, 'Markay u soo galeen kuna Dhaheen salaan Kuna yidhi waanu idinka cabsan.'),
(1855, 15, 53, 'ooy Dhaheena ha cabsan Waxaanu kuugu Bishaarayn Wiil cilmi yeelane.'),
(1856, 15, 54, 'Wuxuuna ku yidhi Ma waxaad ii bishaaraynaysaan Anoo odaynimo Itaabatay, Maxaad iigu Bishaaraynaysaan.'),
(1857, 15, 55, 'Waxayna dhaheen waxaanu Kuugu Bishaarayn Xaq ee ha noqonin Mid quusta.'),
(1858, 15, 56, 'Wuxuuna yidhi yaa ka Quusta Naxariista Eebihiis kuwa Baadida ah mooyee.'),
(1859, 15, 57, 'Wuxuu yidhi Xaalkiinnu muxuu yahay Kuwan la Soo dirayaw.'),
(1860, 15, 58, 'Waxayna dhaheen waxaa naloo Diray Qoom Danbiilayaal ah xagood.'),
(1861, 15, 59, 'Ehelka (nabi) Luudh mooyee oo Waanu korin Dhammaantood.'),
(1862, 15, 60, 'Haweenaydiisa mooyee oo noqon Kuwa ku Hadhi (Halaagga).'),
(1863, 15, 61, 'Markay u yimaadeen Ehelki nabi Luudh kuwii La soo diray.'),
(1864, 15, 62, 'Wuxuu yidhi Idinku waxaad Tihiin Qoom aan la Garanaynin.'),
(1865, 15, 63, 'Waxayna dhaheen saas ma aha ee Waxaannu kuula Nimid waxay Shakisanaayeen (Halaagoodii).'),
(1866, 15, 64, 'Waxaana kuula nimid Xaq, runna waan sheegaynaa.'),
(1867, 15, 65, 'Ee guuri Ehelkaaga qayb Habeenka ka mida Socona Gadaashooda yayna soo Milicsan Ruux ka Mida (Dib) kuna socda meeja Laydin Faray.'),
(1868, 15, 66, 'Waxaana u waxayoonay Amarkaas ah in Cidhibta Kuwaas la goyn Subaxdii.'),
(1869, 15, 67, 'Waxaana yimid dadkii Baladka oo bishaaraysan (aragga martida).'),
(1870, 15, 68, 'Wuxuuna yidhi kuwan waa Martidaydii ee Ha igu ceebaynina.'),
(1871, 15, 69, 'kana dhawrsada Eebe hana i dullaynina.'),
(1872, 15, 70, 'Waxayna dhaheen Miyaanaan kaa Reebin Caalamka (imaad marti galido cidna).'),
(1873, 15, 71, 'wuxuuna yidhi Kuwaasi waa gabdhahaygii Iladaad wax Falaysaan (ood guursanaysaan).'),
(1874, 15, 72, 'Cimrigaagee iyagu Baadibay ku Wareersan Yihiin.'),
(1875, 15, 73, 'Waxaana qabatay Qayladii qorrax soo baxa.'),
(1876, 15, 74, 'Waxaana ka yeellay korkoodii hoostooda Waxaana ku soo daadinnay Korkooda Dhagaxyo Naar ah.'),
(1877, 15, 75, 'Arrintaasna calaamooyin baa ugu sugan Kuwa Fiirfiiriya (Fikira).'),
(1878, 15, 76, 'Waxayna kutaal Magaaladaas waddo toosan (oy maraan oo cad).'),
(1879, 15, 77, 'Arrintaasna calaamad yaa ugu sugan kuwa Xaqa rumeeyay.'),
(1880, 15, 78, 'Waxayna ahaayeen Qoomkii kaynta Daalimiin (Qoomkii Nabi Shucayb).'),
(1881, 15, 79, 'Waana ka aarsanay, waxayna labaduba degenaayeen Waddo cad (oo la yaqaanno).'),
(1882, 15, 80, 'Waxayna beeniyeen Qoomkii Xijrina kuwii Loo soo diray (Saalax).'),
(1883, 15, 81, 'Waana siinay Aayaadkanagii waxayna ahaayeen Kuwa xageeda ka jeedsada.'),
(1884, 15, 82, 'Waxaana ahaayeen kuwo ka qota Buuraha Guryo aamina.'),
(1885, 15, 83, 'Waxaana qabatay Qaylo Aroortii.'),
(1886, 15, 84, 'Waxna uma tarin waxay Kasbanayeen.'),
(1887, 15, 85, 'Samooyinka iyo Dhulka iyo waxa u dhaxeeyana waxaan Xaq ahayn umaanan abuurin Saacaduna way iman ee iska saamax saamixid quruxsan.'),
(1888, 15, 86, 'Eebahaabaa ah abuuraha cilmiga lehe.'),
(1889, 15, 87, 'waxaan ku siinay Todobo soo noqnoqota «faataxada» iyo Quraanka wayn.'),
(1890, 15, 88, 'ee ha u taagin indhahaaga waxaan ugu raaxaynay qaybo ka mida. hana u murugoon una raarici (Jilei) garabkaaga (macaamiladooda) Mu\'miniinta.'),
(1891, 15, 89, 'waxaadna dhahdaa anugu waxaan uun ahay u dige cad.'),
(1892, 15, 90, 'sidaan ugu soo dejinay Cadaab kuwii isu dhaarsaday (Dhibka xaqa).'),
(1893, 15, 91, 'ee kayeelay Quraanka Qaybo.'),
(1894, 15, 92, 'Eebahaa baan ku dhaartaye waan warsanaynaa dhammaantood.'),
(1895, 15, 93, 'waxay falayeen.'),
(1896, 15, 94, 'ee Muuji waxa lagu fari iskagana jeedso Mushrikiinta.'),
(1897, 15, 95, 'anagaa kaa kaafiyeyna kuwa jeesjeesa.'),
(1898, 15, 96, 'ee yeela Eebe Miciisa Ilaah kale wayna ogaan doonaan (waxa ku dhaca).'),
(1899, 15, 97, 'waxaana ognahay Laabtaadu inay ka cidhiidhyami waxay sheegi (oo xun).'),
(1900, 15, 98, 'ee ku Tasbiixso mahadda Eebahaa ahawna kuwa Sujuuda.'),
(1901, 15, 99, 'Caabudna Eebahaa intay Yaqiintu kaaga timaaddo (Geeridu).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'waxaa yimid amarkii Eebe Qiyaamihii (iyo ciqaab) ee ha dedejisanina, waxaa ka nasahan oo sarreeya Eebe waxay la wadaajin.'),
(1903, 16, 2, 'wuxuu ku soo dejiyaa Malaa\'igta waxyi oo amarkiisa ka mida cidduu doono oo addoomadiisa ka mida oo ah ugu diga inaan Ilaahay mooyee Eebe kale jirin ee ani uun iga dhawrsada.'),
(1904, 16, 3, 'wuxuu u abuuray Eebe Samooyinka iyo Dhulka xaq, wuuna ka sarreeya waxay la wadaajin.'),
(1905, 16, 4, 'wuxuu ka abuuray dadka dhibic markaasuu noqdaa dood badane cad.'),
(1906, 16, 5, 'xoolahana wuu idiin abuuray dhexdoodana waxaa ah dugaal iyo nacfi xaggeedaadna ka cuntaan.'),
(1907, 16, 6, 'quruxbaana idiinku sugan dhexdooda markaad dheelmaysaan iyo markaad foofinaysaan.'),
(1908, 16, 7, 'waxaadna ugu rarataan culaysyo xagga magaalo oydaan gaadhayn naf dhibkeed mooyee Eebihiinna waa u ture Naxariista.'),
(1909, 16, 8, 'Fardaha iyo Baqlaha iyo Dameeraha wuxuu (idiin ku abuuray Eebe) Inaad kortaan iyo Qurux, Wuxuuna abuuri Waxaydaan Ogayn.'),
(1910, 16, 9, 'Xagga Eebaana jid toosan yahay (Hanuun), Mid leexsanna waa jiraa (xumaanta) Hadduu Doonana wuu idin hanuunin lahaa dhammaan.'),
(1911, 16, 10, 'Eebe waa kan idinka soo dejiyay Samada Biyo xagiisaa laga cabaa Xagiisaana Geeduhu ka ahaadaan aad Daajisaan (xoolaha).'),
(1912, 16, 11, 'Wuxuuna idiinku soo Bixiyaa Beero Saytuun, Timir, Cinab iyo Wax kastoo midha ah, arrintaasna calaamadbaa ugu sugan Ciddii fikiri.'),
(1913, 16, 12, 'Wuxuuna idiin sakhiray Habeenka, Maalinta, qirraxda, Dayaxa, Xiddigahana waxaa lagu sakhiray Amarkiisa, Arrintaasna Calaamooyin yaa Ugu sugan Ciddii wax kasi.'),
(1914, 16, 13, 'Iyo wuxuu idiinku abuuray Dhulka oo kala Duwan yahay Midabkeedu, Arriintaasna Calaamad Yaa ugu sugan ciddii wax xasuusan (fikiri).'),
(1915, 16, 14, 'Eebe waa kan idiin sakhiray Badda inaad ka cuntaan Hilib cusub kalana baxdaan Qurux (Macdan) ood xidhataan, Waxaadna aragtaan doonida (Markabka) oo Dillaacin (Dabbaalan) Dehxdeeda iyo inaad Doontaan fadliga iyo inaad Doontaan fadliga Eebe iyo Inaad ku shugridaan.'),
(1916, 16, 15, 'Wuxuuna ku riday (sugay) Dhulka buuro inayna Idinla iilan (wuxuuna yeelay) wabiyo iyo Wadooyin inaad ku toostaan.'),
(1917, 16, 16, 'Iyo calaamado iyo Xiddigo ay dadku ku toosaan.'),
(1918, 16, 17, 'Cidwax abuuri Ma la midbaa mid aan wax Abuurayn miyaydaan wax xasuusanayn.'),
(1919, 16, 18, 'Haddaad tirisaan Nicmada Eebe ma koobi Kartaan, Eebana waa danbi dhaafe Naxariista'),
(1920, 16, 19, 'Eebe waa ogyahay waxaad qarsanaysaan iyo waxaad Muujinaysaan.'),
(1921, 16, 20, 'waxay Caabudayaan oo Eebe ka soo hadhayna ma abuuraan waxba iyagaase la abuuraa.'),
(1922, 16, 21, 'waana Mayd aan Noolayn mana oga waqtiga la soo bixin (Dadka).'),
(1923, 16, 22, 'ILaahiinnu waa Ilaah kaliya, kuwa aan rumeynin Aakhirana Quluubtoodu way diidi (Kalinimada Eebe), wayna iskibrin.'),
(1924, 16, 23, 'waxaa sugan in Eebe ogyahay waxay qarsan iyo waxay Muujin Eebana ma jecla kuwa iskibriya.'),
(1925, 16, 24, 'marka lagu dhoho muxuu soo dejiyey Eebihiin waxay dhahaan waa warkii kuwii hore.'),
(1926, 16, 25, 'inay xambaaraan culayskooda «dambigooda» oo dhan Maalinta Qiyaame, iyo culayska «dambiga» kuway dhumiyeen cilmi la\'aan waxaana xun waxay xambaaranayaan.'),
(1927, 16, 26, 'waxaa wax dhagray kuwii ka horreeyey markuu Eebe (amarkiisii) uga yimid xagga Asaaska (Tiirarka) waxaana kaga dumay saanqaafkii korkooda wuxuuna uga yimid cadaab meelayan ogayn.'),
(1928, 16, 27, 'ka dibna maalinta Qiyaame wuu dullayn wuxuuna dhihi aaway shura- kadaydii aad ku doodayseen dhexdeeda, kuwiina waxay dheheen cilmiga la siiyey (Culimadii) dulli iyo xumaan maanta wuxuu saaranyahay Gaalada.'),
(1929, 16, 28, 'ee ah kuway oofsan Malaa\'igtu iyagoo dulmiyey naftooda, waxayna Muujiyaan hogaansi iyagoo dhihi maanaan ahayn kuwo xumaan fala, saas ma aha ee Eebe waa ogyahay waxaad falayseen.'),
(1930, 16, 29, 'ee ka gala irridaha Jahannamo idinkoo ku waari dhexdeeda waxaana xumaan badan Hooyga kuwa iskibriya.'),
(1931, 16, 30, 'Waxaa lagu Dhahaa kuwa dhawrsaday muxuu soo Dejiyay Eebihiin Waxayna dhahaan Khayr, Kuwa Sama falay adduunyadana waxay mudan wanaag Daarta aakhiraana u Khayrroon waxaana wanaag Badan daarta mutaqinta (Kuwa dhawrsaday).'),
(1932, 16, 31, 'Waa jannada cadnin oy gali oy soconna dhexdeeda Wabiyaashii waxayna ka heli waxay doonaan, Saasuuna Eebe ku abaal mariyaa kuwa dhawrsada.'),
(1933, 16, 32, 'Ee ah kuway oofsato Malaa\'igtu iyagoo Fiican (Kuna dhihi) Nabadgalyo korkiina ha ahaato ee Ku gala jannada Waxaad camal fali Jirteen Dartiis.'),
(1934, 16, 33, 'Gaaladuse miyay sugi inay u timaado Malaa\'igtu mooyee ama u yimaado Amarka Eebahaa, Saasayna Faleen kuwii ka horeeyay, Mana Dulmiyin Eebe iyagaase dulmiyay Naftooda.'),
(1935, 16, 34, 'Waxaana ku dhacay Xumaantii waxay faleen, Waxaana ku dagay (Ciqaahtii) Waxay ku jeesi Jireen.'),
(1936, 16, 35, 'Waxay dhahaan kuwii Eebe la wadaajiyay (Cibaadada) Haduu doono Eebe maanaan caabudneen cid ka Soo Hadhay waxba annaga iyo aabayaalkanno mana Xarrimneen isaga ka sokow waxba, Saasayna faleen Kuwii ka horeeyay ee Rasuullada ma saaran yahay Waxaan gaadhsiin cad ahayn.'),
(1937, 16, 36, 'Waxaan ka bixinay Umad kasta Rasuul inay caabudaan Eebe kana dheeraadaan Dhaaquutka (Cibaadada Eebe ka sokow), Waxaana ka mida dadka Cid Eebe Hanuuniyay, Waxaana ka Mida ciday ku sugnaatay Baadinimo, Ee socda dhulka oo Eega siday Noqotay Cidhibtii Beeniyayaasha.'),
(1938, 16, 37, 'Hadaad ku Dadaashid hanuunkooda Eebe ma hanuuniyo ciduu Dhumiyay mana helaan Gargaare,'),
(1939, 16, 38, 'waxay ugu dhaarteen Eebe dhaar adag inuusan soo bixinayn Eebe cid dhimatay saas ma aha ee waa yabooh Eebe isasaaray (sugan) Dadka badidiise ma oga.'),
(1940, 16, 39, 'inuu u caddeeyo waxay isku diidanyihiin iyo inay ogaadaan kuwii Gaaloobay inay ahaayeen Beenaalayaal.'),
(1941, 16, 40, 'haddaan doono arrinna waa inaan uun ku dhahno ahow oy abaato.'),
(1942, 16, 41, 'kuwii u haajiray dar Eebe intii la dulmiyey ka dib waxaan dejinaynaa adduunka wanaag, ajirka aakhiraana ka wayn hadday wax ogyihiin.'),
(1943, 16, 42, 'waana kuwa samray «adkaystay» Eebahoodna uun tala saarta.'),
(1944, 16, 43, 'waxaan dirray hortaa Rag aan u waxyoonay, ee warsada kuwa cilmiga leh haddaydaan wax ogayn.'),
(1945, 16, 44, '(waxaana la dirray) xujooyin iyo Kutub waxaana kugu soo dejinay Quraanka inaad u caddayso Dadka waxa loo soo dejiyey, inay fikiraan.'),
(1946, 16, 45, 'ma aamin bay ka noqdeen kuwii dhagarta falay in Eebe la gooyo Dhulka, ama uga yimaaddo cadaab meelayna filayn.'),
(1947, 16, 46, 'ama ku qabto Eebe gadgadoonkooda mana aha kuwa Eebe daaliya.'),
(1948, 16, 47, 'ama ku qabto Nusqaamin (iyo cabsi) iyagoo ku sugan, Eebihiinna waa u Ture Naxariista.'),
(1949, 16, 48, 'Miyeyna Arkayn wuxuu Eebe Abuuray oo u iilan Hooskiisu Midig iyo Bidix Sujuud Eebe dartiis iyagoo u Khushuucsan.'),
(1950, 16, 49, 'Eebe waxaa u Sujuuda waxa Samooyinka iyo Dhulka ku sugan oo dhul socoda iyo Malaa\'ig iskamana wayneeyaan (Sujuuddiisa).'),
(1951, 16, 50, 'Waxayna ka Cabsan Eebahooda Sarreeya waxayna falaan wax la faro.'),
(1952, 16, 51, 'Wuxuuna Yidhi Eebe ha Yeelanina Ilaahyo Labo ah Eebe waa uun Ilaah kaliya ee Aniga uun iga Cabsada.'),
(1953, 16, 52, 'Eebe waxaa u sugnaaday waxa Samooyinka iyo Dhulka ku sugan, waxaana u sugnaaday Diin (Cibaado) Joogta ah, ee nia Eebe cidaan ahayn yaad ka dhawrsanaysaan.'),
(1954, 16, 53, 'Waxa Niema ah ood haysataan waxay ka timid Eebe markuu idin Taabsiiyo Dhibna xaggiisaad u Qaylyeysaan.'),
(1955, 16, 54, 'Markuu idinka Faydo Dhibkana Koox idinka mida yaa Eebahood la wadaajisa (Cibaadada).'),
(1956, 16, 55, 'Inay ka Gaaloobaan waxaan siinnay, Raaxaysta waad Ogaan doontaan «Cidhibta Xaalkiinne».'),
(1957, 16, 56, 'Waxay u yeelaan waxayna ogayn Qayb waxaan ku Arzaaqnay ka mida, Dhaar Eebee waa laydin warsan (Gaaloy) waxaad Been Abuuranaysaan.'),
(1958, 16, 57, 'Waxayna u yeelaan Eebe Gabdho waa ka Nasahanyahay, iyaguna waxay yeelan waxay rabaan.'),
(1959, 16, 58, 'Marka Loogu Bishaareeyo midkood Dhaddig wuxuu Noqdaa Wajigiisu Madow waIbahaarina.'),
(1960, 16, 59, 'Wuxuuna iska Qariyaa Dadka Xumida waxa loogu Bishaareeyey, miyuu ku haystaa Dulli misc wuxu Dhumbiyaa Dhulka, waxaa xun waxay Xukumi.'),
(1961, 16, 60, 'Kuwaan rumayn Aakhiro waxaa u sugnaaday Tusaale xun, Eebana waxaa u sugnaaday Sifada sare, waana Adkaade falsan.'),
(1962, 16, 61, 'Hadduu Eebe u qaban Dadka Dulmigooda kagama tageen korka dhulka wax socda, wuxuuse dib ugu dhigi Muddo Magacaaban, markay Ajashoodu timaadana kama dib maraan Saacad kamana hormaraan.'),
(1963, 16, 62, 'Waxayna u yeelaan Eebe waxay Nacayaan, waxayna tilmaami carrabyadoodu Been inuu wanaag u sugnaaday, waxaa sugan in Naar uun u sugnaatay, oo loo hormarin (Luguna dhaafi).'),
(1964, 16, 63, 'Dhaar Eebee waxaan (Rasuullo) u dirray Umado kaa horreeyey wuxuuna u Qurxiyey Shaydaan camalkooda isagaana sokeeye u ah Maanta waxayna mudan Cadaab daran.'),
(1965, 16, 64, 'Quraankana waxaan uun kuugu soo Dejinay inaad u Caddaysid Dadka waxay isku Khilaafsanyihiin iyo Hanuunka iyo Naxariista Ciddii Rumayn.'),
(1966, 16, 65, 'Eebana waa kan ka soo Dejiya Samada Biyo, kuna Nooleeya Dhulka Abaar ka dib, Arrintaasna Calaamad yaa ugu sugan Ciddii wax maqli.'),
(1967, 16, 66, 'Waxaana idiinku sugan Xoolaha Cibrad (Waano qaadasho) waxaana idinka waraabinaa waxa ku sugan Caloosheeda Digo iyo Dhiig dhexdooda ah Caano Saafi ah oo u fudud kuwa Cabbi.'),
(1968, 16, 67, 'iyo Midhaha Timirta iyo Cinabka ood ka yeelataan Cabbid iyo Risqi Fiican, Arrintaasna Calaamadbaa ugu sugan cidii wax kasi.'),
(1969, 16, 68, 'Wuxuu u Waxyooday (ku Ilhaameeyey) Eebahaa Shinnida ka yeelo Buuraha Guryo iyo Geedaha iyo waxay Dhistaan.'),
(1970, 16, 69, 'Markaas Cun Midho kasta xaggooda qaadna Jidadka Eebahaa oo laylyan, waxaana ka soo baxa Calooshooda Cabbid kala duwantahay Midabkoodu dhexdiisana waxaa ah Daawo Dadka Arrintaasna Calaamaa ugu sugan ciddii Fikiri.'),
(1971, 16, 70, 'Eebaa idin abuuray markaas idin oofsan (Dili) waxaana idinka mida cid loo eelin (la gaadhsiin) cimriga ugu liita inuusan ogaan waxba cilmi «ogaansho» dabadiis, Eebana wax walba waa ogyahay karaana.'),
(1972, 16, 71, 'Eebe wuu ka fadilay Qaarkiin qaar Risqiga, mana aha kuwa la fadilay kuwii ku celiya Risqigooda waxay gacantoodu hanato oy isku mid ku noqdaan ee Ma Nicmada Eebaad diidaysaan.'),
(1973, 16, 72, 'Eebe wuxuu idinka yeelay naftiinna azwaaj wuxuuna idinka yeelay Haweenkiinna caruur iyo farac idinkuna arsaaqay wanaag ma baadhilbay rumayn oy Nicmada Eebay ka Gaaloobi.'),
(1974, 16, 73, 'waxayna Caabudayaan Eebe ka sokow waxaan uga hananayn Risqi Samooyinka iyo Dhulka waxba, waxna karin.'),
(1975, 16, 74, 'ee hawyeelina Eebe wax la mida Eebe wuu ogyahay idinkuse ma ogidine.'),
(1976, 16, 75, 'Eebe wuxuu ka yeelay tusaale Addoon la leeyahay oon Waxba karin iyo Ruux aannu ku Arzaaqnay rizqi fiican oo u bixin qarsoodi iyo muuqaalba, Ma isku midbaa, Mahad Eebaa iska leh, Badankooduse wax ma oga.'),
(1977, 16, 76, 'Waxaa (kale) oo Eebe tusaale ka yeelay laba Nin oo midkood Af la\'yahay (Mahadle) waxna karin dhibna ku ah (Culays) Ilaaliyihiisa meel Kastoos ujeediyana aan wanaag ka imanaynin Ma Egyihiin isaga iyo ruux fari Caddaalada Jid toosanna ku sugan.'),
(1978, 16, 77, 'Eebaa iska leh waxa ku maqan Samooyinka iyo Dhulka, Saacadda amarkeeduna ma aha ilbidhiqsigeed mooyee ama ka dhaw, Eebana wax Kasta wuu karaa.'),
(1979, 16, 78, 'Eebe wuxuu idinka soo bixiyay Caloosha Hooyooyinkiin Waxba idinkoon ogayn wuxuuna Idiin yeelay Ma- qal iyo Arag iyo Quluub inaad Ku Shukridaan Eebe.'),
(1980, 16, 79, 'Miyayna arkayn Shinbiraha loo Sakhiray Hawada samaa, Waxaana Eebe ahayna haynin, taasina Calaamooyin yaa ugu Sugan Cidii rumayn (Xaqa).'),
(1981, 16, 80, 'Eebe wuxuu idinka yeelay Guryihiinna xasil (Degaan) wuxuuna idinka yeelay harkaha xoolaha Guryo idiin fudud maalinta geediga iyo maalinta nagaadigaba, idinkana yeelay Suufkeeda iyo dhogarta (Geela) iyo dhogorta (Riyaha) Alaab iyo Maacuun tan iyo muddo.'),
(1982, 16, 81, 'Eebe wuxuu idiinka yeelay wuxuu abuuray (Geedaha) hadh, wuxuuna idiinka yeelay Buuraha Dugaal, idiinkana yeelay dhar idinka dhawra kulaylka iyo Qamiis Bireed idinka dhawrta (Dhibka) Dagaalka, saasuuna idiinku Taamyeeli Eebe Nicmadiisa inaad Islaam noqotaan (Xaqa raacdaan).'),
(1983, 16, 82, 'hadday jeedsadaan waxaa uun korkaaga ah gaadhsiin cad.'),
(1984, 16, 83, 'way garan Nicmada Eebe wayna dafiri, badankooduna waa Gaalo.'),
(1985, 16, 84, '(Xusuusi) maalintaan ka soo bixin Umad kasta marag, markaasna aan loo Idmayn kuwii Gaaloobay cudar daarasho, Lagana doonayn cudur daar.'),
(1986, 16, 85, 'Markay arkan kuwii Dulmi falay Cadaabka Lagama fududeeyo xaggooda lamana sugo.'),
(1987, 16, 86, 'Markay arkaan kuwii Gaaloobay shurakadooda waxay dhahaan Eebow kuwaasu waa Shurakadannadii aannu Caabudi jirray Eebe ka sokow, markaasay ku soo tuureen Hadal ah idinku Been baad sheegaysaan.'),
(1988, 16, 87, 'Waxayna u Dhiibaan Eebe Maalintaa Hogaansiga, waxaana ka shuma waxay Been Abuuran jireen.'),
(1989, 16, 88, 'Kuwii gaaloobay ee ka awday dadka jidka Eebe waxaan ugu siyaadinnaa Cadaab Korka cadaabka Faasuqnimadooda Darteed.'),
(1990, 16, 89, '(Xusuusi) Maalintaan ka soo bixinayno Umad kasta marag Naftooda ah oon ku Keenayno (Nabiyow) Adoo marag ku ah kuwan (Umadaada) Waxaana kugu dajinnay Kitaab oo ah Caddaynta wax kasta iyo hanuun iyo Naxariis iyo bishaarada dadka muslimiinta ah.'),
(1991, 16, 90, 'Eebe wuxuu Fari Caddaalad iyo Samafal iyo Wax siinta qaraabada Wuxuuna reebi Xumaan iyo Munkar iyo Gardarro, Wuuna idin Waanin inaad wax Xasuusataan.'),
(1992, 16, 91, 'Oofiya Ballanka Eebe markaad Ballantantaan hana burinina Dhaarta ka dib intaad adkayseen ood uga yeesheen Eebe korkiina Kafiil (ilaaliye), Eebana waa ogyahay Waxaad falaysaan.'),
(1993, 16, 92, 'Hana Noqonina Sidii tii Burisay (furfurtay) Soohiddii Xoog ka dib furfurid idinkoo ka yeelan dhaartiina Dhagar dhexdiina ah, inay ahaatay ummad mid ka badan Ummad kale, Eebe wuu idinku imtixaami (Armakaas) iyo inuu idiin Cadeeyo maalinta Qiyaame waxaad isku diidanaydeen.'),
(1994, 16, 93, 'hadduu doono Eebe wuxuu idinka yeeli lahaa umad kaliya wuxuuse dhumiyaa cidduu doono, wuuna hanuuniyaa cidduu doono, waana laydin warsan waxaad camal falayseen.'),
(1995, 16, 94, 'haka yeelanina dhaartiinna dhagar dhexdiinna ah ood markaas simbiriirxataa gomadi intay sugnayd ka dib ood dhadhamisaan xumaan (Cadaab) ka jeedintiinna dadka jidka Eebe darteed, waxaana idiin sugnaaday Cadaab wayn.'),
(1996, 16, 95, 'hana ku gadanina ballanka Eebe qiimo yar (waxba) waxa Eebe agtiisa ah yaa idiin khayrroone haddaad wax ogtihiin,'),
(1997, 16, 96, 'waxa agtiinna ah wuu dhamaan waxa Eebe agtiisa ahna wuu hadhi waxaana ku abaal marinaynaa kuwa samra (adkaysta) Ajirkooda midka ugu fiican ee ay falsayeen.'),
(1998, 16, 97, 'Ciddii camal fasha wanaag oo Raga ama haweena isagoo mu\'mina waxaan noolaynaynaa Nolol wanaagsan, waxaana ku abaal marinaynaa Ajirkooda waxa ugu Wanaagsan ee ay falayeen.'),
(1999, 16, 98, 'markaad akhiriyi Quraanka ka magan gal Eebe shaydaan la dheereeyey.'),
(2000, 16, 99, 'isagu kuma leh xujo kuwa Rumeeyey xaqa Eebahoodna talo saarta'),
(2001, 16, 100, 'wuxuuse xujo ku yeeshaa kuwa ka wali yeesha iyo kuwa la wadaajiya Eebe.'),
(2002, 16, 101, 'haddaan ku badallano Aayad meel Aayad kale Eebana ogyahay wuxuu dejin waxay dhahaan waxaad uun tahay Beenabuurte, badankooduse wax ma oga.'),
(2003, 16, 102, 'waxaad dhahdaa waxaa ka soo dejiyey Ruuxii Daabirka ahaa (Jibriil) xagga Eebaha xaqa ah inuu ku sugo kuwii rumeeyey xaqa iyo hanuun iyo Bishaaro isagoo u ah Muslimiinta.'),
(2004, 16, 103, 'Waxaan dhab u ognahay inay dhihi Waxaa bara Nabiga Quraanka dad, Carrabkay ku leexin waa Cajami Kanna waa Lisaan (af carabi) cad.'),
(2005, 16, 104, 'Kuwaan rumayn Aayaadka Eebe ma Hanuuniyo Eebe Waxayna Mudan cadaab daran.'),
(2006, 16, 105, 'Waxaa uun abuurta Been kuwaan rumayn Aayaadka Eebe kuwaasuna iyaga umbaa Beenaalayaala.'),
(2007, 16, 106, 'Oo ah kuwa Eebe ka gaaloobay rumayn ka dib (ee mudan halaag) Ruux lagu Qasbay qalbigiisuna ku Xasillanyahay iimaanka mooyee, ruuxiise laabtiissu u Waasac noqoto (rumayso) Gaalnimo Waxaa korkooda ah Cadho Eebe, Waxayna Mudan Cadaab wayn.'),
(2008, 16, 107, 'Arrintaasuna waa inay ka doorteen Nolosha dhaw (ee adduun) Aakhiro iyo Inaan Eebe hanuuninayn kuwa gaalada ah.'),
(2009, 16, 108, 'Kuwaas Eebaa daboolay quluubtooda, Maqalkooda iyo Aragooda waana kuwa uun Halmaansan (xaqa).'),
(2010, 16, 109, 'Waxaa sugan in kuwaasu aakharo ay Iyagu uun Khasaareen'),
(2011, 16, 110, 'Eebahaase kuwa Hijrooday intii la fidmeeyey (la dhibay) ka dib markaas jahaaday oo samray (adkaystay) Eebahaana intaas ka dib waa u dambi dhaafe Naxariista.'),
(2012, 16, 111, 'waana Maalin ay imaan naf walba iyadoo ka doodi (Daafici) nafteeda oo loo Oofin naf kasta waxay fasahay lana duminayn.'),
(2013, 16, 112, 'Eebe wuxuu tusaale ka yeelay Magaalo ahayd aamin oo xasiloon ugana yimaaddo risqigeedu (Cunnada) oo barwaaqo ah meel kasta oo ka Gaalowday Nicmooyinkii Eebe oo Eebe dhadhansiiyey Gaajo dabooshay iyo cabsi waxay sameynayeen darteed.'),
(2014, 16, 113, 'waxaa dhab ahaan ugu yimid Rasuul ka mida wayna beeniyeen, waxaana qabtay Cadaab iyagoo Daalimiina.'),
(2015, 16, 114, 'ee cuna waxa Eebe idinku Arsuqay ee xalaasha fiicana kuna shukriya Nicmada Eebe haddaad isaga Caabudaysaan.'),
(2016, 16, 115, 'wuxuu uun idinka xarrimay Bakhtiga, Dhiigga, Hilibka Doofaarka iyo wixii lagu gawraco waxaan Magaca Eebe ahayn, ciddii dhibaatoota (Gaajo) iyadoon dulmiyin xadna gudbin Eebe waa dambi dhaafe Naxariista.'),
(2017, 16, 116, 'ha dhihina wuxuu carrabkiina ku tilmaani been kani waa xalaal kanna waa xaaraan, inaad ku been abuurataan Eebe, kuwa ku Been abuurta Eebe ma Liibaanaan.'),
(2018, 16, 117, 'waa Nolol yar (Adduunku) waxayna mudan Cadaab daran.'),
(2019, 16, 118, 'kuwii Yuhuudoobay waxaan ka xarrimay waxaan kaaga qisoonay horay, mana aanaan dulmiyin iyagaase naftooda Dulmiyey.'),
(2020, 16, 119, 'markaas Eebahaa kuwa xumaan ku fala Jaahilnimo markaas toobadkeena intaas ka dib oo suubiya Camalka eebahaa ka dib wuu u dhaafi una Naxariisan.'),
(2021, 16, 120, '(Nabi) Ibraahim wuxuu ahaa Umad «Khayr Fare» Eebe adeece Xaqa u iishe mana ka mid ahayn Mushrikiinta.'),
(2022, 16, 121, 'Kuna shukriya Nicmada Eebe wuuna doortay wuxuuna ku hanuuniyey Jidka Toosan.'),
(2023, 16, 122, 'waxaana siinay Adduunka wanaag, aakhirana wuxuu ka mid noqon kuwa Suuban.'),
(2024, 16, 123, 'markaas waxaan kuu waxyoonay Nabiyow raac Diinta (Nabi) Ibraahim ee Toosan kama mid ahayn Mushriikiinta.'),
(2025, 16, 124, 'Sabtida waxaa uun looyeelay kuwii isku khilaafay, Eebahaana wuu kala xukumi dhexdooda Maalinta Qiyaame waxay isku Khilaafayeen.'),
(2026, 16, 125, 'ugu yeedh Jidka Eebahaa si xigmada iyo Wacdi fiicana kulana dood sida u fiican, Eebahaana wuu ogyahay cidda ka dhunta Jidkiisa wuuna ogyahay kuwa Hanuunsan.'),
(2027, 16, 126, 'haddaad wax ciqaabaysaan u Ciqaaba inta laydin ciqaabay, haddaad samirtaana isagaa u khayr roon kuwa samra.'),
(2028, 16, 127, 'Samir, mana aha samirkaagu dar Eebe mooyee, hana ka murugoon, hana ka cidhiidhyamin waxay Dhagri.'),
(2029, 16, 128, 'Eebana wuxuu la jira kuwa dhawrsada wuxuuna la jira kuwa samo fala.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'waxa Nasahan (Ceeb ka fog) Eebaha ka guuriyey Addoonkiisa (Nabiga) Habeen masaajidka Xurmada leh (Maka) xagga Masjidkii Aqsa een barakaynay Gaararkiisa inaan Tusinno Nabiga Aayaadkanaga Eebana waa maqle arka.'),
(2031, 17, 2, 'waxaana siinay Nabi Muuse kitaabkii (Tawreed) waxaana uga yeellay hanuun BaniiIsraa\'iil inaydaan ka yeelanin wax iga soo hadhay Wakiil.'),
(2032, 17, 3, 'faricii kuwaan la xambaarnay Nabi Nuux, wuxuuna ahaa Addoon (Eebe) ee Shugri badan.'),
(2033, 17, 4, 'Waxaan ku (Ogaysiinnay) ugu Waxyoonay Banii Isra\'iil Kitaabka (Tawreed) inaad fasaadinaysaan Dhulka Labo Jeer, isna kibrinaysaan iskibrin Wayn.'),
(2034, 17, 5, 'Markuu yimaaddo Yabooha (Watiga) midda hore waxaan idinku soo Bixinaa «Dirraa» Addoomadanada oo Xoog daran leh, dhexgalina (Hanan) Gurya dhexdooda waana Yabooh la fali.'),
(2035, 17, 6, 'Markaasaan idinku soo Celinaa Dawladnimada, idinkuna Xoogaynaa Xoolo iyo Caruur idinkana yeellaa kuwa Tiro badan.'),
(2036, 17, 7, 'Haddaad Samo Fashaan waxaad u samo fasheen Naftiinna, haddaad Xumaan fashaanna waa uun Naftiinna, markuu Yimaaddo Yabooha Dambana way Xumeeyaan Wajigiinna iyo inay Galaan Masaajidka (Baytul Maqdis) siday u galeen markii hore iyo inay Damiraan «Tirtiaan» waxay u Adkaadaan Tirtirid.'),
(2037, 17, 8, 'Waxay u dhawdahay in Eebihiin idiin Naxariisto, haddaad ku Noqotaanna waannu ku noqon, waxaana uga yeelnay Jahannamo Gaalada Gogol.'),
(2038, 17, 9, 'Quraankanna wuxuu ku Hanuunin Dadka Arrinta u Toosan ugana Bishaarayn kuwa fala Wanaag inay Mudan Ajir wayn.'),
(2039, 17, 10, 'Kuwaan Rumeynin Aakhirana waxaan y Darabnay Cadaab Daran.'),
(2040, 17, 11, 'Wuxuu u Yeedhaa (Baryaa) Dadku Shar siduu ugu Yeedho «Baryo» Khayrka, Dadkuna waa Deg dege.'),
(2041, 17, 12, 'Waxaan ka Yeellay Habeenka iyo Maalinta Labo Calaamo, waxaana Tirraa Calaamada Habeenka, waxaana ka Yeellaa Calaamada Maalinta mid wax Tusisa (Arag) inaad doontaan Fadliga Eebihiin iyo inaad ku ogaataan Tirada Sanooyinka iyo Xisaabta, wax kastana waxaannu u Caddeynay Caddayn (aada).'),
(2042, 17, 13, 'Ruux kasta waxaan Laasiminay Camalkiisa (loo Qaddaray) Luquntiisa, waxaana u soo bixinnaa Maalinta Qiyaame Kitaab uu la kulmi Isagoo fidsan.'),
(2043, 17, 14, 'Laguna Dhihi akhri Kitaabkaaga isagaa kuugu Filan Maanta Korkaaga Xisaabe.'),
(2044, 17, 15, 'Ruuxii Hanuuna wuxuu uun u Hanuunay Naftiisa kii Dhumana wuxuu uun u Dhumay Naftiisa, mana xambaarto Nafi Dambi Naf kale, mana Cadaabno intaan uga soo Dirro Rasuul.'),
(2045, 17, 16, 'Markaan doono inaan Halaagno Qaryad waxaan farraa kuweeda loo Nicmeeyey «Khayr» markaasay ka Faasiqoobaan waxaana ku sugnaada Ciqaab waana Tirtirraa Tirtirid (Halaag).'),
(2046, 17, 17, 'Imisaau Halaagnay Quruun (Nabi) Nuux ka dib Eebahaabaana ugu filan Dambiyada Addoomadiisa Ogaade Arka.'),
(2047, 17, 18, 'Ruuxi dooni Adduunyo waan ugu soo dedejinaa dhexdeeda waxaan Doonno Ciddaan doonno, markaasaan kayeellaa Jahannamo mid gala isagoo Caayan oo Dullaysan.'),
(2048, 17, 19, 'Ciddiise Doonta Aakhiro oo u fala camalkeedii isagoo Mu\'mina kuwaas Camalkoodu wuxuu Noqon mid la Mahdiyo.'),
(2049, 17, 20, 'Dhammaan waxbaan uga Fidinaa kuwaas (Doona Adduun) iyo kuwaas (Doona aakhiro) siismada Eebahaa, mana aha siismada Eebahaa mid Reeban.'),
(2050, 17, 21, 'Day sidaan uga Fadilnay qaarkood qaarka (Kale) (Addunka) Aakharaase wayn darajooyin oo wayn Fadilid.'),
(2051, 17, 22, 'Ila Yeelin Eebe Miciisa Ilaah kale ood noqotid mid Caayan oo la Khasaariyo.'),
(2052, 17, 23, 'Wuxuu Faray Eebahaa inaydaan Caabudin isaga mooyee iyo inaad u samo fashaan Labadii Waalid Samo falid, hadduu ku gaadho Agtaada wayni midkood ama Labaduba ha ku Dhihin uf, hana Canaanan una dheh hadal Fiican.'),
(2053, 17, 24, 'Una Raarici (Jilci) Garab Naxariiseed (u Naxariiso) dhehna Eebow ugu Naxariiso siday ii Koriyeen anoo Yar.'),
(2054, 17, 25, 'Eebihiin wuu ogyahay waxa Natiinna ku Sugan, haddaad Suubantihiin isagu kuwa Toobad keenka badan wuu u Dhaafaa «Dambiga».'),
(2055, 17, 26, 'Sii qaraabada xaqeeda iyo Miskiinka iyo socdaalka hana ku xadgudbin bixin Xadgudub (iyo si xun).'),
(2056, 17, 27, 'illeen kuwa ku xadgudba waa walaalo Shaydaane, shaydaanna Eebihiis wuu ka gaaloobay.'),
(2057, 17, 28, 'haddaad ka jeedsato xaggooda (wax la\'aan) adoo rajayn Naxariista Eebhaa ood rajayn waxaad ku dhahdaa hadal fudud (fiican).'),
(2058, 17, 29, 'ha ka yeelin gacantaada mid ku xidhan Luquntaada hana wada fidin dhammaan ood noqoto mid la dagaalo oo tabar gaba.'),
(2059, 17, 30, 'Eebahaa wuu u fidiya Risqiga cidduu doono wuuna u qaddaraa «cidhiidhyaa» (cidduu doono) illeen Eebe Addoomadiisa waa oge arkee.'),
(2060, 17, 31, 'ha Dilina Caruurtiinna ka Cabsi Faqri (Saboolnimo) anagaa Arsaaqayna iyaga iyo idinkaba Dilkooduna waa Gaf wayn.'),
(2061, 17, 32, 'Ha u dhawaanina sino Illeen waa xumaane Waddana u xune.'),
(2062, 17, 33, 'Hana dilina Nafta Eebe xarrimay Xaq Mooyee, Ruuxii lagu dilo Gardarro waxaan u yeellay waligiisa Xujo ee yuusan ku xad gudbin Dilka waa loo Gargaaraye.'),
(2063, 17, 34, 'Hana u dhawaanina Xoolaha agoonta sida U fiican mooyee intay ka gaadhaan xooggooda, Oofiyana ballanka illeen Ballanka waa lays warsane.'),
(2064, 17, 35, 'Oofiyana kaylka «beegidda» markaad beehaysaan kuna miisama Miisaan toosan (caadila) Saasaa khayr roon oo Fiican cidhibe.'),
(2065, 17, 36, 'hana dhihin (Raacraacin) Waxaadan u Cilmi lahayn, Illeen Maqalka, Araga iyo Qalbigaba waa lays warsane.'),
(2066, 17, 37, 'Hana u soconin Dhulka kibir Illeen ma Jeexi Kartid Dhulka kumana gaadhaysid Buuraha dherare.'),
(2067, 17, 38, 'Dhammaan waxaasna Xumaantiisu Eebahaa Agtiisa waa lagu Neceb yahay.'),
(2068, 17, 39, 'Arrintaasu waa waxa Eebahaa kuu waxyooday oo xigmada, hana yeelin Eebe la jirkiisa Ilaah kale oo lagugu tuuro Jahannamo adoo lagu dagaalay oo lagu fogeeyey.'),
(2069, 17, 40, 'ma wuxuu idiin doortay Eebihiin Wiilal oos ka yeeshay Malaa\'igta gabdho idinku waxaad ku hadlaysaan hadal wayn.'),
(2070, 17, 41, 'dhab ahaan waan ku Caddaynay (gagadinay) quraankan (wax badan) inay waantoobaan umase kordhin waxaan carar ahayn.'),
(2071, 17, 42, 'waxaad dhahdaa hadduu la jiro Eebe Ilaah kale siday sheegi waxay ka dooni lahaayeen Eebaha Carshiga leh jid (Cibaado ama la mid noqosho).'),
(2072, 17, 43, 'Eebaa ka Nasahan Kana Sarreeya waxay sheegi sarrayn wayn.'),
(2073, 17, 44, 'waxaa u tasbiixsada (Qaddariya) Eebe Samooyinka todobada ah iyo Dhulka iyo waxa u dhexecya, waxaan Eebe u Tasbiixsanayn ama ku mah- dinayna ma jiro, waydaanse garanayn tasbiixdooda, Eebana waa dulsame dambi dhaafa.'),
(2074, 17, 45, 'markaad Akhriyi Quraanka waxaan yeellaa dhexdaada iyo dhexda kuwaan rumeynin Aakhiro xijaab (dayr) asturan.'),
(2075, 17, 46, 'waxaana yeellay Quluubtooda dabool inay kasaan Quraanka dhagohoodana culays, markaad ku xusto Eebhaana Quraanka kaligis waxay u jeedsadaan Gadaal iyagoo carari.'),
(2076, 17, 47, 'anagaa og waxay dhagaysan markay ku dhagaysan adiga iyagoo faqi markay dhihi Daalimiintu Waxaan Nin sixran ahayn ma raacaysaan.'),
(2077, 17, 48, 'Day siday kuugu yeelaan tusaalayaal oy U dhumeen oyna u karin jidka Hanuunka.'),
(2078, 17, 49, 'Waxay dhaheen (gaaladii) ma markaan noqonno lafo iyo burbur yaa nalagu soo celin Abuur cusub.'),
(2079, 17, 50, 'waxaad dhahdaa noqda dhagax ama bir.'),
(2080, 17, 51, 'ama khalqi (Abuur) ku wayn laabtiinna, waxayna dhihi yaa na soo eelin ku dheh Eebihiin idin abuuray markii hore, waxayna kuu gilgili madaxooda iyagoo leh waa goorma isagu, waxaad dhahdaa waxaa suurowda inay dhawdahay.'),
(2081, 17, 52, 'waana maalintuu idiin yeedhi Eebe ood maqlaysaan isagoo mudan Mahad una malaynysaan inaydaan nagaanin wax yar mooyee.'),
(2082, 17, 53, 'waxaad ku dhahdaa Addoomadayda ha dheheen sida u fiican, Shaydaanku wuu fasaadin dhexdiinnee, Shaydaanna Dadka col cadbuu u yahay.'),
(2083, 17, 54, 'Eebihiinna idin og (waxaad tihiin) hadduu doono wuu idiin Naxariisan, hadduu doonana wuu idin Cadaabi, kuugumana aanaan dirin inaad wakiil (Ilaaliye u Noqoto).'),
(2084, 17, 55, 'Eebahaana waa ogyahay waxa ku sugan Samooyinka iyo Dhulkam, dhab ahaan baana ugu fadilnay Nabiyada Qaarkood qaarka (kale) waxaana Siinay Nabi Daawuud Zabuur.'),
(2085, 17, 56, 'waxaad dhahdaa u yeedha kuwaad sheeegateen oo Eebe ka soo hadhay ma hantaan inay idinka faydaan dhib ama idinka wareejiyaan.'),
(2086, 17, 57, 'kuwaas ay caabudina iyagaaba ka dooni xagga Eebe u dhawaansho iyo kusii dhawaansho waxayna Rajayn Naxariistiisa, wayna ka cabsan cadaabkiisa, cadaabka Eebana waa wax laga digtoonaado.'),
(2087, 17, 58, 'Qaryo (Magaalo) Aanaan halaagayn ma jirto ka hor Qiyaamada ama aan la cadaabayn (Ciqaabayn) cadaab daran waana arrin ku qoran Kitaabka (Looxul Maxfuudka).'),
(2088, 17, 59, 'Umana aanaan daynin soo diridda Aayado (Mucjiso) inay beeniyeen kuwii hore mooyee, waxaana siinnay Thamuud Hashii oo wax tusisa wayna ku dulmi faleen (ka gaaloobeen) Aayadaha waxaan Cabsi galin ahayn uma soo dirro (Dejinno).'),
(2089, 17, 60, '(Xusuuso) markaan kugo nidhi Eebahaa wuxuu koobaa Dadka, kamana aanaan yeelin Riyadaan ku tusinay fidmada Dadka mooyee (Imtixaan) iyo Geedka lagu lacnaday Quraanka, waanu ku cabsiin umana siyaadiyo waxaan xadgudub wayn ahayn.'),
(2090, 17, 61, '(Xusuuso) markaan ku nidhi Malaa\'igta u Sujuuda Aadan oy Sujuudeen Ibliis moyee, oo yidhi ma waxaan u sujuudi waxaad Abuurtay isagoo dhooba ah,'),
(2091, 17, 62, 'wuxuuna yidhi ka warran kan aad iga sharriftay, haddaad dib ii dhigto tan iyo Qiyaamada waxaan ka adkaan (Dhumin) Faraciisa wax yar mooyee.'),
(2092, 17, 63, 'Eebe wuxuu ku yidhi: Tag ciddii ku raacda oo ka midana Jahannamaa Jashiisu yahay abaal marin dhamaystiiran.'),
(2093, 17, 64, 'ku duufso ciddaad karto oo ka mida codkaaga kuna soo Jiido fardahaaga iyo lugtaada lana wadaag xoolaha iyo Carruurta una yabooh, mana yabooho shaydaan waxaan dhagar ahayn (iyo Baadi).'),
(2094, 17, 65, 'Addoomadayda kuma lihid xujo, Eebahooda waa ku filan yahay wakiil (Ilaaliye).'),
(2095, 17, 66, 'Eebihiin waa kan idinku wada doonta (Markabka) badda dhexdeeda inaad doonataan Fadligiisa (Risqi) Eebana waa idiin Naxariiste.'),
(2096, 17, 67, 'markuu idinku taabto badda dhibna wuu dhumaa waxaad baryeyseen Eebe mooyee, markuu idiin soo koriyo Barrigana waad jeedsatan Dadkuna waa Gaalnimo badane.'),
(2097, 17, 68, 'Miyaad ka Aamin Noqoteen in Eebe idinla Gooyo dhinac Dhulka ka mida, ama idinku soo diro Dabayl daran markaas aydaan helin Wakiil.'),
(2098, 17, 69, 'Miyaadse ka Aamin Noqoteen inuu idinku eeliyo (Badda) mar kale oos idinku diro Dabayl (wax jajabisa) oos idin Maansheeyo gaalnimadiinna darteed, markaas aydaan u helin wax noo raacda.'),
(2099, 17, 70, 'dhabbaanu u sharrifnay Binii Aadamka waxaana ku xambaarray Barriga iyo Baddaba waxaana ku arsaaqnay wanaag, waxaana ka fadilnay wax badan oo ka mida waxaan Abuurray fadilaad.'),
(2100, 17, 71, '(xusuuso) Maalintaan cid walba ugu yeedhayno Imaamkeeda «Quraanka ama Nabiga» ciddiise laga siiyo Kitaabkiisa Midigta kuwaasi way akhrin Kitaabkooda lagamana dulmiyo xuub laf timireed.'),
(2101, 17, 72, 'cidiise Addunka ku indho la\' (xaqa diida) aakharana wuu indho la\'aan oo ka dhumi Jidka.'),
(2102, 17, 73, 'Waxay u dhawaadeen Gaaladii inay kaa Fidmeeyaan waxaan kuu waxyoonay inaad Nagu been abuuratid wax kale, Markaas ay kaa Yeeshaan Saaxiib.'),
(2103, 17, 74, 'Hadaanan ku sugin waxaad u dhawaan lahayd inaad u iilato xagooda wax yar.'),
(2104, 17, 75, 'Markaasaan ku dhadhansiin lahayn (Cadaab) Labanlaaban nolosha Adduun iyo Mid labanlaaban Geerida manahesheen gargaare.'),
(2105, 17, 76, 'Waxayna u dhawaadeen inay kaa argagixiyaan Dhulka inay kaa bixiyaan Markaas oyna nagaadeen Gadaashaada wax yar Mooyee.'),
(2106, 17, 77, 'Waana sunnada Rasuuladaan dirray Hortaa, una heli maysid Sunnadannada Wax dooriya (Leexin).'),
(2107, 17, 78, 'Oog salaadda markay Qorraxdu suusho Ilaa madawga Habeenka iyo Quraanka (Salaada) waabariga (subaxa) Quraanka waabariguna waa mid la Joogee (Malaa\'igta).'),
(2108, 17, 79, 'Habeenkana Tahajud (Qaar tuka) isagoo Dheeraad kuu ah, Waxay u dhawdahay In Eebahaa kuu soo bixiyo maqaan la Mahadiyo (Shafeecada).'),
(2109, 17, 80, 'Waxaad dhahdaa Eebow i gali Meel galid run ah, ina bixi bixin runa, iigana Yeel agtaada Xujo gargaara.'),
(2110, 17, 81, 'Waxaad dhahdaa waxaa yimid Xaqii Waxaana tirtirmay Baadhilkii, Baadhilkiina wuxuu noqday mid la tirtiro.'),
(2111, 17, 82, 'Waxaana soo dajinaynaa Quraanka Isagoo caafimaad iyo Naxariis u ah Mu\'miniinta, Daalimiintana uma siyaadiyo Waxaan Khasare ahayn.'),
(2112, 17, 83, 'Markaan u nicmaynno dadka wuu Jeedsadaa (xaqa ka fogaadaa) Haduu taabto dhibna wuu quustaa.'),
(2113, 17, 84, 'Waxaad dhahdaa cid kasta ha ku camal fasho jidkkeda (Diinteeda) Eebihiinbaa og Cidda jidkeedu hanuun badan yahaye.'),
(2114, 17, 85, 'Waxay ku warsan Ruuxda, dheh ruuxdu waa Amarka Eebahay Cilmina wax yarbaa la idin siiyay.'),
(2115, 17, 86, 'Haddaan doonno waanu la tagi kaan kuu Waxyoonay (Quraanka) Markaasna uma helaysid Korkannaga wakiil (gargaare).'),
(2116, 17, 87, 'Naxariista Eebahaa Mooyee illeen fadligiisu Korkaaga waa ku wayn yahaye.'),
(2117, 17, 88, 'Waxaad dhahdaa hadday u kulmaan Insi iyo Jinni inay la yimaadaan Quraankan oo Kale lama imaan karaan wax lamida Qaarkood qaar haba caawiyee.'),
(2118, 17, 89, 'Dhabaan ugu Cadaynay dadka Quraanka Tusaale kasta dadka badankiise wuu diiday Gaalnimo mooyee.'),
(2119, 17, 90, 'waxay dheheen Gaaladii (Makaad) ku rumayn mayno intaad nooga jeexdo Dhulka Ilo (Burqan).'),
(2120, 17, 91, 'ama kuu ahaan Beer Timir iyo Cinaba ood ku sii dayso dhexdeeda Wabiyo socda.'),
(2121, 17, 92, 'ama nagaga soo riddid Samada saad sheegatay, Goosimo iyadoo ah, ama u keentid Eebe iyo Malaa\'igta si caddaana.'),
(2122, 17, 93, 'ama ku ahaado Guri dahaba ama ka kortid samada, kuna rumayn mayno koriddaada intaad noogaga soo dejisid kitaab aan Akhrino, waxaad dhahdaa Eebahaybaa nasahane ma waxaan bashar (Dad) Rasuula ahayn baan ahay.'),
(2123, 17, 94, 'Dadka uma diidin inay Rumeeyaan markuu xaqu u yimid oon ahayn inay dhahaan ma wuxuu soo bixiyaa Eebe Bashar (Dad) ah Rasuullo.'),
(2124, 17, 95, 'waxaad dhadaa hadday Dhulka yihiin Malaa\'ig ku xasilloon (Degan) waxaan uga soo Dejin lahayn Samada Malag Rasuula.'),
(2125, 17, 96, 'waxaad dhahdaa Eebaa ugu filan Marag dhedeenna, isaguna waa ogaha arka Addoomadiisa.'),
(2126, 17, 97, 'Cidduu Eebe Hanuuniyo umbaa hanuunsan, cidduu Dhumiyana uma helaysid ugargaare Eebe ka soo hadhay, waxaana ku soo kulminaynaa Qiyaamada Wajigooda iyagoo Indho la\' oo hadal la\' oo Dhaga la\', Hooygooduna waa Jahannamo, markastooy Dantana waxaan u siyaadinaa Shidi (Hurin).'),
(2127, 17, 98, 'taasina waa Abaalkooda ah inay ka gaaloobeen Aayaadkanaga, oy dheheen markaan lafo noqonno iyo burbur miyaa nala soo bixin Abuur Cusub.'),
(2128, 17, 99, 'miyeyna ogayn Eebaha Abuuray Samooyinka iyo Dhulka inuu karo inuu Abuuro wax la mida una yeelo muddo aan shaki lahayn, wayse diideen Daalimiintii Gaalimo mooyee (wax kale).'),
(2129, 17, 100, 'waxaad dhadaa idinku haddaad hanataan khasnadaha Naxariista Eebahay waad xajisan lahaydeen ka cabsi faqri, Dadku waa Bakhayl.'),
(2130, 17, 101, 'waxaan dhab u siinay (Nabi) Muuse sagaal Aayadood (Mucjiso) oo cad cad ee warso Binii Israa\'iil markuu u yimid oo ku yidhi Fircoon waxaan kuu malayn Muusow mid la sixray.'),
(2131, 17, 102, 'wuxuuna yidhi (Nabi Muuse) waad ogtahay inuusan soo dejinin kuwaan waxaan Eebaha Samooyinka iyo Dhulka ahayn iyagoo xujooyin ah, anna waxaan kuu malayn Fircoonow mid la halaagi.'),
(2132, 17, 103, 'markaasuu doonay fircoon inuu ka kaxeeyo Dhulka markaasaan Maansheynay isagii iyo intii la jirtay dhammaan.'),
(2133, 17, 104, 'waxaana ku nidhi gadaashiis Banii Isra\'iil dambane waanu idin keeni dhamaan.'),
(2134, 17, 105, 'xaqbaana ku soo dejinay Quraanka xaqbuuna ku soo degay, waxaana ku dirray adoo Bishaareeye u diga ah,.'),
(2135, 17, 106, 'waana Quraan oon u kala bixinnay (soo dejintiisa) inaad ku akhrido dadka kaadsiinyo, waana soo dejinay soo dejin Qaar Qaar ah.'),
(2136, 17, 107, 'waxaad dhahdaa rumeeya Quraanka ama ha rumeynina kuwa la siiyey cilmiga (kuwa fiican) marka lagu akhriyo korkooda waxay u dhacaan wajiga iyagoo Sujuudi,'),
(2137, 17, 108, 'waxayna dhihi waxaa Nasahan Eebahanno ee yabooha Eebahanno waa la fali (Imaan).'),
(2138, 17, 109, 'waxayna u dhacaan wajiga iyagoo ooyi wuxuuna u kordhiyaa «Quraanku» Khushuuc.'),
(2139, 17, 110, 'waxaad dhahdaa ama ku barya Ilaah, ama ku barya Raxmaan wax kasta ood ku baridaan wuxuu leeyahay magacyo fiican, ee ha la qaylyina Akhriskaaga (salaadda) hana qarsan ee doon jid u dhexeeya Arrintaas «dhexdehexaad».'),
(2140, 17, 111, 'waxaadna dhahdaa mahad Eebaa iska leh een Yeelanin Ilmo, una signayn shariig xukunkiisu (lawadaage) una sugnayn gargaare dulli dartiis (uga gargaare) ee waynee waynayn (Sharrif).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Mahad Eebaa iska leh Eebaha ku soo Dejiyey Addoonkiisa (Nabi Muxamad ah) Kitaabka (Quraanka) oon yeelinna Dhexdiisa Qallooc.'),
(2142, 18, 2, 'Waana Toosanyahay si uu ugaga Digo Ciqaab daran oo Eebe Xaggiisa ka ahaatay, uguna Bishaareeyo Mu\'miniinta fala Wanaagga inay Muteen Ajir Fiican.'),
(2143, 18, 3, 'Kuna Nagaanayaan (janada) Waligood.'),
(2144, 18, 4, 'Ugana Digo kuwa Yidhi wuxuu yeeshey Eebe Ilmo.'),
(2145, 18, 5, 'Uma laha wax Cilmi ah iyaga iyo Aabayaalkood midna, waxaana waynaatay Kalimad ka soo Baxday Afafkooda, mana dhahayaan waxaan Been ahayn.'),
(2146, 18, 6, 'Waxaad u Dhawdahay inaad ku (Halaagto) Naftaada Nabiyow Raadkooda (gadaashooda) haddayna rumeyn Quraankan, walbahaar dartiis.'),
(2147, 18, 7, 'Waxaan ka yeellay waxa Dhulka Korkiisa ah Quruxdiisa, si aan u Imtixaanno Midkooda Fiican-Camal.'),
(2148, 18, 8, 'Waxaana ka yeelaynaa waxa korkeeda ah Bannaan aan wax ka soo Bixin.'),
(2149, 18, 9, 'Ma waxaad u malaysay Nabiyow Godkii Asaxaabtiisii (Dadkiisii) iyo Kitaabkii (iyo Looxii) inay ugu yaab badantahay Aayaadkanaga.'),
(2150, 18, 10, 'Waa markay u Dummeen Dhallinyaradu Godka oy dhaheen Eebow naga sii Agtaada Naxariis noona Darab Amarkanaga Hanuun.'),
(2151, 18, 11, 'Markaasaan ku Siidaynay Hurdo Godka dhexdiisa Sanooyin Badan.'),
(2152, 18, 12, 'Markaasaan soo Bixinnay si aan u Muujinno Labada Xizbi midkooda Kobid Badan intay Nagaadeen Muddo.'),
(2153, 18, 13, 'Anagaa kaaga Qisoon warkooda si Xaq ah, waxay ahaayeen Dhallinyaro rumeeyey Eebehood, waxaana u siyaadinay hanuun.'),
(2154, 18, 14, 'waxaan xoojinay (oon adkayna) quluubtooda, oyna dhaheen Eebehanno waa Eebeha Samaawaadka iyo Dhulka, baryinamayno (Caabudimayno) Ilaah ka soo hadhay, waxaan ku hadalay markaas hadal Xun (oo baadil ah).'),
(2155, 18, 15, 'Kuwaasu waa qoomkanagii waxay yeesheen Eebe ka sokow ilaahyo, mey u keenaan korkooda wax xujo cad ah, yaase ka dulmi badan ruux ku abuurtay Eebe dushiisa been.'),
(2156, 18, 16, 'markaad ka fogaataan gaalada iyo waxay caabudi oo Eebe ka soo hadhay u dunma godka wuxuu idiin fidin Eebihiin naxariise idinkanadarbi amarkiinna waxaad ku istareexdaan kuna kala saartaan xumaanta iyo wanaagga.'),
(2157, 18, 17, 'waxaad arkaysaa Qorraxda oo markay Dhalato uga iilan Godkooda xagga Midig, markay dhicina ka Gooyn xagga bidix iyagoo ku sugan Meel Waasac ah oo Godka, taasina waxay ka mid tahay Aayaadka Eebe, Ruuxuu hanuuniyo Eebe unbaa hanuunsan, Ruuxuu Dhumiyana u helimaysid Gargaare hanuuniya.'),
(2158, 18, 18, 'Waxaad u malayn inay soo jeedaan iyagoo Jiifa, waxaana u gagadin Midig iyo Bidix, Eygoodiina wuxuu ku fidin Dhudhumadiisa Albaabka, haddaad aragto waad ka jeedsanlahayd, adoocarari waxaana lagaa buuxinlahaa Xagooda Argagax.'),
(2159, 18, 19, 'Saasaana u soo bixinay inay is warsadaan dhexdooda, wuxuu yidhi mid ka mid ah imisaad nagaateen, waxay dhaheen waxaan nagaannay Maalin ama maalin qaarkeed, waxay dhaheen Eebeheenbaa og waxaad nagaateen ee ladira midkiin lacagtan xagga Magaalada, hana soo fiiriyo Cid Cunno xalaalaysan * hana idiinka Keeno Rizqi hana isqariyo (oo istartiibiyo) yuuna idin ogeeysiin xaggooda ruuxna.'),
(2160, 18, 20, 'iyagu hadday idin arkaan way idin dhagaxyeyn, ama idinku Celin diintooda mana Liibaanaysaan markaas waligiin.'),
(2161, 18, 21, 'Saasaan u daalacsiinay (u tusinay) Dadka si ay u ogaadaan in yabooha Eebe Xaq yahay, saacadda (qiyaamana) aan shaki ku jirin (waxaan tusinay) markay ku murmeen dhexdooda amarkooda, oy (isku) dhaheen ka dhisa korkooda dhismo, Eebahood baa og iyagee, waxay dhaheen kuwii ku reeyey amarkooda aan ka yeelano korkooda Masaajid.'),
(2162, 18, 22, 'waxay dhihi waxay ahaayeen saddex waxaana afreeyey Eygoodii, waxay dhihi waxay ahaayeen shan waxaana lixeeyey eygoodii iyagoo iska gani wax maqan (iska dhihi cilmi la\'aan) waxay dhihi waxay ahaayeen todobo waxaana siddeedeeyey eygoodii, waxaad dhahdaa Eebahay baa og tiradooda, mana oga wax yar mooyee, ee ha kula murmin xaalkooda muran muuqda oo fudud mooyee, hana waydiin xaggooda Ruuxna.'),
(2163, 18, 23, 'ha ku dhihin shaygaasaan fali barii.'),
(2164, 18, 24, '(adoon dhihin) hadduu doono Eebe, xusna Eebahaa haddaad halmaanto, waxaadna dhahdaa wuxuu u dhawyahay inuu igu hanuuniyo Eebahay wax ka dhaw kaas oo toosni ah.'),
(2165, 18, 25, 'waxay ku nagaadeen godkoodii seddex boqol oo sano waxayna siyaadsadeen sagaal.'),
(2166, 18, 26, 'waxaad dhahdaa Eebaa og waxay nagaadeen waxaa u sugnaaday waxa ku maqan samooyinka iyo dhulka, arki ogaa oo maqli ogaa Eebe, umana sugnaanin Dadka isaga ka sokow gargaare lamana wadaajiyo xukunkiisa Cidna.'),
(2167, 18, 27, 'akhri (oo raac) waxa laguu waxyooday oo kitaabka Eebahaa ah, wax baddali kalimooyinkiisana ma jiro, mana helaysid wax ka sokeeya ood magangashid.'),
(2168, 18, 28, 'ee ku samirsii naftaada la jirka kuwa baryaya Eebahood aroor iyo galabba, iyagoo dooni wajigiisa, yeyna ka iilanin Indhahaagu xagooda adoo dooni quruxda nolosah dhaw (ee aduun) hana adeeein ruuxaan halmaansiinnay qalbigiisa Xusiddanada oo raacay hawadiisa uuna ahaaday amarkiisu xadgudub (iyo Xumaan).'),
(2169, 18, 29, 'waxaad dhahdaa xaqu Eebihiin buu ka ahaaday ee ciddii doonta ha gaalowdo, anagu waxaan u darabnay daalimiinta naar oy koobtay iyaga (barxadeedu) iyo darbigeedu, hadday kaalmo warsadaanna waxaa, loogu kaalmeeya biyo daamuur oo kale ah oo duba wajiyada iyagaa u xun cabbid iyadaana u xun dangiigsi.'),
(2170, 18, 30, 'Kuwa rumeeyey (Xaqa) oo camal fiican falay annagu ma hallayno Ciddii Wanaajisa Camalka.'),
(2171, 18, 31, 'Kuwaas waxay mudan janada Cadnin oo dureeri Dhexdeeda Wabiyaal, looguna Labisi Dhexdeeda Jijimo Dahab ah, una xidhay Maryo Caggaaran oo Xariir Jilicsan iyo mid adagba, waxayna ku Dangiigsan dhexdeeda Sariiro korkood, waa abaal marin Fiican una Wanaagsan meel lagu (istareexo) laguna nasto.'),
(2172, 18, 32, 'Uga yeel tusaalc Labo Nin oon u yeellay Midkood Laba Beerood oo Cinab ah, kuna koobnay Timir yeellayna Dhexdeeda Tallaal (beer).'),
(2173, 18, 33, 'Labada Beeroodba oy keentay Cunnadeedii, oyna waxba Nusqaamina, oon kana Dhambalay dhexdeeda Wabiyo.'),
(2174, 18, 34, 'Midhana uu leeyahay oo markaas ku yidhi Saaxiibkiis asagoo hadalka ku celcelin anaa kaa Xoolo badan Kaana Tol badan.'),
(2175, 18, 35, 'Wuxuu galay Beertiisii isagoo dulmiyey Naftiisa, wuxuu yidhi uma malaynayo inay Halaagsami Waligeed.'),
(2176, 18, 36, 'Umana Malaynayo in Saacadda (Qiyaame) kici, haddii lay celiyo Xagga Eebahayna waxaan ka heli mid ka khayrroon Noqoshada.'),
(2177, 18, 37, 'Wuxuu yidhi saaxiibkiisii isagoo la hadli ma waxaad ka gaaloowday Eebaha kaa Abuuray Carro, hadana dhibic markaas ku ekeeyey adoo nin ah.'),
(2178, 18, 38, 'hasa yeeshee ilaahay waa Eebahay lana wadaajiin maayo Eebahay (cibaadada) cidna.'),
(2179, 18, 39, 'maxaadan markaad gasho beertaada u dhihin wuxuu doone Eebe*, xoog ma jiro Eebe mooyee, haddaad aragtay anoo kaa yar adiga xoolo iyo Caruurba.'),
(2180, 18, 40, 'wuxuu u dhawyahay Eebehay inuu i siiyo wax ka khayr badan Beertaada, kuna diro korkeeda aafo samo, oy markaas ahaato bannaan lagu simbiririxoodo.'),
(2181, 18, 41, 'ama ahadaan biyaheedu kuwa gudha oodan karin doonid (soo celin).'),
(2182, 18, 42, 'waa la koobay midhihiisii (la halaagay) oo ahaaday kii gaddin gaemihiisa wuxuu ku bixiyey iyadoo ku dhacday darbiyadeeda, isagoo leh shalleytee maanan la wadaajin Eebahay Cidna.'),
(2183, 18, 43, 'uma ahaanin koox u gargaarta oo ka soo hadhay Eebe mana aha mid gargaarta.'),
(2184, 18, 44, 'halkaas (qiyaamada) yey awoodu u ahaan Eebaha xaqa ah, isagaana khayrroon abaal marin khayrroonna cidhib.'),
(2185, 18, 45, 'uga yeel Nabiyow tusaale nolosha Adduunyo Biyo oo kale oon ka soo dejinay samada oo isku darsamay waxa ka soo baxay dhulka oo markaas noqday ingayg kala firdhiso dabayluhu, wuxuuna ahaaday Eebe kii wax kasta kara.'),
(2186, 18, 46, 'xoolaha iyo wiilashu (caruurtu) waa quruxda nolosha adduunyo, kuwa hadha ee wanaagsan yaa ku khayrroon Eebahaa agtiisa abaalmarin una khayrroon yididiilo (camalka fiican).'),
(2187, 18, 47, 'xus maalinta aannu kaxayn buuraha ood arkayso dhulka oo muuqda, oon soo kulminay dadkii oonan ka tagin cidna.'),
(2188, 18, 48, 'waxaana loo banbhigi dadkii Eebahaa iyagoo saf ah, dhab ahaan yaad noogu timaadaan saan idiin abuuray markii horaba (yaa lagu dhihi) waxaadse sheegateen inaanaan idiin yeelayn ballan «kulama».'),
(2189, 18, 49, 'waxaana la dejin (keeni) Kitaabkii (camalka) waxaadna arkaysaa dambiilayaashii oo ka cabsan waxaa ku sugan oo dhihi halaaganee muxuu leeyahay kitaabkan oosan uga tagayn wax yar iyo wax wayn ee uu koobay wayna heli waxay camal faleen oo jooga, manadulmiyo Eebahaa cidna.'),
(2190, 18, 50, '(xusuuso) markaan ku nidhi Malaa\'igta u sujuuda Aadam, oo ay sujuudeen Ibliis mooyee, wuxuu ka mid yahay Jinniga wuxuuna ka faasiqoobay (baxay) amarka Eebe, miyaad ka yeelanaysaan isaga iyo faraciisa gargaare ani ka sokow iyagoo col idiin ah, waxaa u xun daalimiinta badelkooda (ibliis).'),
(2191, 18, 51, 'maraga ugama dhigin kuwaas abuurka Samooyinka iyo dhulka iyo abuurka Naftooda, mana ihi mid ka yeelan baadiyayaalka kaalmo.'),
(2192, 18, 52, '(xusuuso) maalinta Eebe dhihi u yeedha kuwaad ila wadaajiseen (Cibaadada) ood sheegteen (adduunka) markaasay u yeedheen mana ajiibin (u jawaabin) waxaana yeellay dhexdooda Meel halaag.'),
(2193, 18, 53, 'waxayna arkaan dambiilayaashu naarta waxayna yaqiinsan inay ku dhici mana helayaan xageeda meelay uga iishaan.'),
(2194, 18, 54, 'dhab ahaan yaan ugu caddaynay quraankan dadka tusaale kasta, waxayna ahaadeen dadkii kuwo muran badan.'),
(2195, 18, 55, 'wax ka reebay dadka inay rumeeyaan markuu u yimid hanuunku oy dambi dhaaf warsadaan Eebahood ma jiro oon ahayn (sugid) inay u timaaddo waddadii kuwii hore, ama ugu yimaaddo cadaab caddaan.'),
(2196, 18, 56, 'mana dirro rasuullada iyagoon bishaaraynneyn oon digayn, waxayna ku murmi kuwii gaaloobay xumaan inay ku buriyaan xaqa, waxayna ka yeesheen aayaadkeygii iyo wixii loogu digay jeesjees.'),
(2197, 18, 57, 'ruuxna kama dulmin badana cid lagu waaniyey aayaadka Eebihiis markaas ka jeedsaday xageeda oo halmaamay waxay hormarsadeen gacmihiisu, waxaan yeellay quluubtooda dabool inayan fahmin quraanka dhagahooda culays haddaad ugu yeedho hanuunkana ma hanuunayaan waligood.'),
(2198, 18, 58, 'Eebahaa waa dambi dhaafe naxariisleh hadduu u qaban dadka waxay kasbadeen (oo xumaan) wuxuu u dadajin cadaabka, waxayse leeyihiin ballan oyna helayn meelay uga iishaan.'),
(2199, 18, 59, 'taasi waa magaalooyinkii oon halaagnay markay dulmi faleen waxaana u yeellay halaagoodii ballan (waqti).'),
(2200, 18, 60, '(xusuuso) markuu ku yidhi (Nabi) Muuse yarkiisii (ardaygiisii) ma joogayo intaan ka gaadho kulanka labada Badood, amaan socdo muddo.'),
(2201, 18, 61, 'markay gaadheen kulankooda waxay halmaameen kalluunkoodii oo ka dhigtay waddadiisa badda dhexdeeda Godoo kale.'),
(2202, 18, 62, 'markay tallaabeen yuu ku yidhi khaadimkiisii ina sii qadadeenna waxaan kala kulannay safarkeenna daale.'),
(2203, 18, 63, 'wuxuu yidhi (yarkii) ka warran markaan u dumannay dhagixii inaan halmaamay kalluunkii wax i halmaansiiyey oon shaydaan ahayn inaan xusuusto ma jiro, wuxuuna ka dhigtay waddadiisa badda la yaab.'),
(2204, 18, 64, 'wuxuu yidhi (Nabi Muuse) kaasi waa kaan doonaynay, waxayna ku noqdeen raadkoodii iyagoo raaci.'),
(2205, 18, 65, 'waxayna ka heleen addoon ka mid ah addoomadanada oon siinay naxariis agtanada ah ka barneyna agtanada cilmi.'),
(2206, 18, 66, 'wuxuu ku yidhi muuse ma ku raaci karaa inaad i bartid waxa lagu baray oo hanuun ah.'),
(2207, 18, 67, 'wuxuuna ku yidhi (Khadir) adigu ma karaysid la jirkayga samir.'),
(2208, 18, 68, 'seed ugu samri waxaadan koobin cilmi (aqoon).'),
(2209, 18, 69, 'wuxuu yidhi (muuse) waad i heli hadduu Eebe doono mid ku samra oon kuguna caasiyin arrin.'),
(2210, 18, 70, 'wuxuu yidhi haddaad i raacdo hay waydiinin waxba intaan kaaga sheego (warramo).'),
(2211, 18, 71, 'way tageen intay ka koreen markab oo uu rujiyey (loox) markaas uu yidhi ma waxaad u rujisay inaad dadkeeda maansheyso waxaad la timid arrin la kahdo.'),
(2212, 18, 72, 'wuxuu ku yidhi miyaanan ku dhihin adigu ma karaysid la jirkayga samir.'),
(2213, 18, 73, 'wuxuu yidhi (muuse) ha iiqabanin waxaan halmaamay hana igu mashaqayn amarkayga si dhib ah.'),
(2214, 18, 74, 'way tageen intay kala kulmaan wiil (yar) markaasuu dilay, markaasuu yidhi Muuse ma waxaad dishay naf daahir ah oon naf dilin, waxaad la timid shay la kahdo.'),
(2215, 18, 75, 'wuxuu yidhi miyaanan kuu dhihin adigu ma karaysid la jirkayga samir.'),
(2216, 18, 76, 'wuxuu yidhi haddaan ku warsado wax tan dabadeed hayla saaxiibin waxaad gaadhay garowsho xaggayga ahe.'),
(2217, 18, 77, 'way tageen intay ka yimaadaan magaalo oy warsadaan cunno dadkeeda wayna diideen inay martigaliyaan waxayna heleen darbi dooni inuu dumo markaasuu joojiyey wuxuuna yidhi haddaad doonto waxaad ka yeelan lahayd ujuuro.'),
(2218, 18, 78, 'wuxuu yidhi kan waa kala tagii dhexdeenna, waxaana kaaga warrami fasiraadda waxaadan karin inaad ku samirto.'),
(2219, 18, 79, 'markabka waxaa lahaa masaakiin ka shaqqeeya badda waxaana doonay inaan Ceebeeyo waxaana ahaa hortooda xaakim ka qaata doon kasta (oo fayow) dhaca.'),
(2220, 18, 80, 'Wiilkana waxay ahaayeen labadiisii waalid mu\'miniin waxaana ka cabsannay inuu ku mashaqeeyo xadgudub iyo (gaalimo).'),
(2221, 18, 81, 'waxaana doonnay inuu ugu badalo Eebahood mid ka khayroon daahirnimo iyo u dhawi naxariis.'),
(2222, 18, 82, 'darbigana waxaa leh labo wiil oo agoon ah oo magaalada, waxaana hoostiisa ah kansi ay leeyihiin wuxuuna ahaa aabahood mid suubban, wuxuuna doonay Eebahaa inay gaadhaan xooggooda lana baxaan kansigooda naxariista Eebahaa dartiis, uma falin rayigayga kaasi waa fasirka waxaadan karin samirkiisa*.'),
(2223, 18, 83, 'waxay ku warsan Dul qarnayn xaggiisa waxaad dhahdaa waxaan idinku akhrin xaggiisa war (waxyi).'),
(2224, 18, 84, 'a nagaa makaninay (siinnay) xukun dhulka waxaana u sinnay wax kasta sabab (cilmi).'),
(2225, 18, 85, 'wuxuuna raacay waddo.'),
(2226, 18, 86, 'markuu gaadhay qorrax u dhacna wuxuu helay iyadoo ku dhici il dhoobo ah (muuqaalka) wuxuuna ka helay agteeda qoom, waxaana nidhi Dul qarnaynow waa inaad cadaabto ama ka yeelato wanaag.'),
(2227, 18, 87, 'wuxuuna yidhi ruuxii dulmi fala waannu cadaabi (ciqaabi) markaasaa loo celin Eebihiis wuuna cadaabi cadaab daran.'),
(2228, 18, 88, 'ruuxiise rumeeya oo fala wanaag wuxuu mudan abaal fiican waxaana u dhihi amarkannaga fudayd.'),
(2229, 18, 89, 'markaasuu raacay waddo.'),
(2230, 18, 90, 'markuu gaadhay qorrax ka soo baxna wuxuu helay iyadoo ku soo baxda qoom aanaan yeelin sokeydeeda asturaad.'),
(2231, 18, 91, 'waa sidaas, waxaana ku koobnaa waxa agtaada ah ogaansho.'),
(2232, 18, 92, 'markaasuu raacay waddo,'),
(2233, 18, 93, 'markuu gaadhay labada xidhmo dhexdooda wuxuu ka helay hortooda qoom aan garanayn hadal (af gaar ah k u hadla).'),
(2234, 18, 94, 'waxayna dheheen dul qarnaynow yaajuuj iyo maajuj waxay fasaadin dhulka ee ma kuu yeellaa ujuuro inaad yeesho dehxdanada xidheen jidaar.'),
(2235, 18, 95, 'wuxuu yidhi wuxuu imakaniyey (isiiyey) Eebahay yaa ka kjayrroon ee iigu caawiya xoog aan yeelo dhexdiinna xidheene.'),
(2236, 18, 96, 'i siiya goosimo bir ah, markuu ekeeyey labada dhinac dhexdooda wuxuu yidhi afuufa markuu ka yeelay dabna wuxuu yidhi i siiya aan ku shubee maar la dhalaaliyey.'),
(2237, 18, 97, 'mana karayaan inay ka soo kor baxaan (ka soo dhacaan) mana karayaan inay duleeliyaan.'),
(2238, 18, 98, 'wuxuuna yidhi kan waa naxariis Eebahay, Markuu yimaaddo yabooha Eebahayna wuxuu ka yeela burbur, yabooha Eebahayna waa zaq (sugan).'),
(2239, 18, 99, 'waxaana ka yeeli qaarkood maalintaas kii qaarka (kale) dhexgali waxaana la afuufaa suurka waana soo kulminnaa kulmin.'),
(2240, 18, 100, 'waxaana u bandhigaynaa jahannamo maalintaa gaalada bandhig.'),
(2241, 18, 101, 'waana kuway indhahoodu yihiin dabool dhexdiis xagga xuskayga mana aha kuwo kara (maqalka) xaqa.'),
(2242, 18, 102, 'ma waxay u maleeyeen kuwii gaaloobay inay ka yeesheen addoomadayda iga sokow awliyo (oy caabudaan) waana u darabnay jahannamo gaalada dagid.'),
(2243, 18, 103, 'waxaad dhahdaa ma idiin warramaa kuwa khasaaro badan camallo.'),
(2244, 18, 104, 'waa kuwuu dhumay camalkoodu nolosha adduun iyagoo isuna malayn inay wanaajin camalka.'),
(2245, 18, 105, 'kuwaasu waa kuwa ka gaaloobay aayaadka Eebahood iyo la kulankiisa oo buray camalkoodu umana joojinno maalinta qiyaame miisan «qiimana ma leh».'),
(2246, 18, 106, 'kaasina waa abaalkoodii jahannamo gaalnimadooda darteed, waxayna ka yeesheen aayaad kayga iyo rasuulladayda jeesjees.'),
(2247, 18, 107, 'kuwa rumeeyey (xaqa) oo fala camal fiican waxay u ahaatay jannada firdowso degid, (martiqaad).'),
(2248, 18, 108, 'wayna ku waari dhexdeeda mana doonaan inay xaggeeda ka wareegaan (guuraan).'),
(2249, 18, 109, 'waxaad dhahdaa hadday u noqoto badda khad kalimooyinka Eebahay waxaa dhammaan lahaa badda ka hor dhammaanshaha kalimaadka Eebahay aaban keenno isagoo kale (oo siyaadin ah).'),
(2250, 18, 110, 'waxaad dhadha waxaan uun ahay anigu bashar (dad) idinkoo kale ah, waxaa laywaxyooday in uun yahay ilaahiin ilaah kaliya, ruuxiise dooni la kulanka Eebihiis ha falo camal fiican, yuuna la wadaajinna cibaadada Eebihiis cidna.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'waa la soo sheegay kuwo lamid ah.'),
(2252, 19, 2, '(kanna) waa sheegid naaxariista Eebahaa addoonkiisa zakariye.'),
(2253, 19, 3, 'markuu u dhawaaqay Eebihiis dhawaaq qarsoodi ah.'),
(2254, 19, 4, 'yidhina Eebow anigu waan tabaryaraaday madaxayguna wuxuu la huray cirro, Mana ahayn baryadaada Eebow mid ku khasaara.'),
(2255, 19, 5, 'anigu waxaan ka cabsan qaraabada iga dambaysa (inay xumaadaan) haweenaydayduna waa mandhalays iga sii agtaada wali (walad).'),
(2256, 19, 6, 'i dhaxla (nabinimada) oo dhaxla ehelkii yacquub kana yeel Eebow mid laga raalli noqdo.'),
(2257, 19, 7, 'zakariyow annagu waxaanu kuugu bishaarayn wiil magaciisu Yahay yaxye oonaan u yeelin horay wax lamid ah (lamagac ah).'),
(2258, 19, 8, 'wuxuu yidhi Eebow sidee iigu ahaan wiil iyadooy tahay haweeneydaydu madhalays oon ka gaadhay waynida gabow.'),
(2259, 19, 9, 'wuxuu yidhi waa saas, wuxuuna Eebaha yidhi isagu waa ii Fududahay waana ku abuuray horay adoon ahayn waxba.'),
(2260, 19, 10, 'wuxuu yidhi Eebow ii yeel calaamo (xaqnimada yaboohaada) wuxuu yidhi (Eebe) calaamadaadu waa inaadan la hadlin dadka seddex habeen oo israacsan (adoo fayow).'),
(2261, 19, 11, 'markaasu ugaga soo baxay qoomkiisii mixraabka wuxuuna u ishaaray xaggooda inay tukadaan aroor iyo galabba.'),
(2262, 19, 12, '(waxaa loo yidhi) yaxyow ku qaado kitaabka (tawreed) xoog (dadaal) waxaan siinnay xigmo isagoo yar.'),
(2263, 19, 13, 'iyo naxariis agtanada ka ahaatay iyo daahirnimo wuxuuna ahaa dhawrsade.'),
(2264, 19, 14, 'iyo baarri labadiisa waalid mana ahayn daalim caasi ah.'),
(2265, 19, 15, 'nabadgalyo korkiisa ha ahaato maalinta la dhalay iyo maalintuu dhiman iyo maalinta la soo bixin isagoo nool.'),
(2266, 19, 16, '(ku xusuuso) kitaabka maryama markay uga fogaatay ehelkeeda meel bari ah, (bariga qudsi).'),
(2267, 19, 17, 'oyna ka yeelatay xaggeeda astur, oon u dirray xaggeeda ruuxannagii (jibriil), oo isugu shabahay dad eg.'),
(2268, 19, 18, 'una tidhi anigu waxaan kaa magan galay (Eebaha) Raxmaan ah haddaad tahay dhawrsade.'),
(2269, 19, 19, 'kuna yidhi anigu waxaan uun ahay rasuulka Eebahaa inaan ku siiyo wiil daahir ah.,'),
(2270, 19, 20, 'waxayna tidhi sidee iigu ahaan wiil anoosan i taaban dad, ahaynna mid xun (la tuhmo).'),
(2271, 19, 21, 'wuxuuna yidhi waa sidaas wuxuuna yidhi Eebahaa wuu u fududyahay yidhi Eebahaa wuu u fududyahay korkayga iyo inaan uga yeello calamaad dadka iyo naxariis xagayga ah waana arrin la xukumay (dhammeeyey).'),
(2272, 19, 22, 'way xambaartay (uurowday) waxayna u fogaatay meel dheer (durugsan).'),
(2273, 19, 23, 'waxayna u dhibaataysay fooshii jirrid timir waxayna tidhi shalleytee maan dhinto ka hor arrintan oon noqodo mid la halmaamo o laga tago.'),
(2274, 19, 24, 'wuxuuna uga dhawaaqay hoosteeda ha murugoonin wuxuu yeelay Eebahaa hoostaada wabi (yar).'),
(2275, 19, 25, 'una gilgil xaggaaga jirridda timirta ha kuu soo riddo korkaaga timir (curdana) oo fiicane.'),
(2276, 19, 26, 'ee cun oo cab qabowsana il (farax) haddaad ka aragto dadkana ruux waxaad dhahdaa anigu waxaan ugu nadray (Eebaha) Raxmaan oon u soomi oonan la hadlin maanta cidna.'),
(2277, 19, 27, 'waxayna ula timid ciise qoomkeeda uyadoo xambaarsan waxayna dheheen maryamoy waxaad la timid wax wayn.'),
(2278, 19, 28, 'haruun walaashiisay (u eg wanaagga) ma eheyn aabahaa ruux xun, mana ahayn hooyadaa mid xun (la tuhmo).'),
(2279, 19, 29, 'wayna u ishaartay xaggiisa waxayna dhaheen sideen ula hadlaynaa ruux ku sugan Sariirilmood isagoo yar.'),
(2280, 19, 30, 'wuxuuna yidhi (ciise) anigu waxaan ahay addoon Eebe, wuxuuna i siiyey kitaabka (injiil) wuxuuna iga yeelay nabi.'),
(2281, 19, 31, 'wuxuuna iga yeelay mid la barakeeyey meel kastoon ahay, wuxuuna ii dardaarmay salaadda iyo zakada intaan noolahay.'),
(2282, 19, 32, 'wuxuuna iga yeelay baarriga waalidaday (hooyaday) igamana yeelin madax adage xun.'),
(2283, 19, 33, 'nabadgalyana ha ahaato korkayga maalintii lay dhalay iyo maalintaan dhiman iyo maalinta laysoo bixin anoo nool.'),
(2284, 19, 34, 'kaasi waa Ciise ibnu maryama, waana hadalka xaqa ah ee ay shakin.'),
(2285, 19, 35, 'kuma haboona Eebe inuu yeesho ilmo, waana ka nasahanyahay, markuu doono xukun «amar» wuxuu uun dhahaa ahaw wuuna ahaan.'),
(2286, 19, 36, 'Ilaahay waa Eebahay iyo Eebihiin ee caabuda, kaasaa ah jidka toosane.'),
(2287, 19, 37, 'wayse iskhilaafeen xisbiyadii dhexdooda wuxuuna halaag uga sugnaaday kuwii gaaloobay kulan maalin wayn.'),
(2288, 19, 38, 'maqli ogaa oo arki ogaa maalintay noo imaan, hase yeeshee daalimiintu maanta waxay ku suganyihiin baadi cad.'),
(2289, 19, 39, 'uga dig iyaga maalin qoomamo, marka la xukmin amarka iyagoo kusugan halmaansho oon rumeynayn.'),
(2290, 19, 40, 'annagaa dhaxli dhulka iyo waxa korkiisa ah xagganagaana laydiin soo celin.'),
(2291, 19, 41, 'ku sheeg kitaabka (nabi) Ibraahim, wuxuuu ahaa runbadane nabi ah.'),
(2292, 19, 42, 'markuu ku yidhi aabihiis aabbow maxaad u caabudi waxaan maqlayn waxna arkayn kaana qeeqayn waxba.'),
(2293, 19, 43, 'aabow aniga waxaa ii yimid cilmi waxaan adi kuu imaanin ee i raac aan kugu hanuuniyo jid toosane (xaqa).'),
(2294, 19, 44, 'aabbow ha caabudin shaydaan, shaydaanku wuxuu u yahay Eebaha Rasmaan ah caasi.'),
(2295, 19, 45, 'aabbow anigu waxaan ka cabsan inuu ku taabto cadaabka Eebaha Raxmaana ah ood u noqoto shaydaan qaraabo.'),
(2296, 19, 46, 'wuxuu yidhi (aabihiis) ma waxaad nici adigu ilaahyadayda Ibraahimow hadaadan joogin waan ku dhagaxyeyn ee iga tag waligeed.'),
(2297, 19, 47, 'wuxuu yidhi (nabi) Ibraahim nabadgalyo korkaaga ha ahaato waan kuu dambi dhaaf warsan adiga Eebahay, Eebe waa ii naxariiste.'),
(2298, 19, 48, 'waana idinka fogaan idinka iyo waxaad caabudaysaan oo Eebe ka soo hadhay, waxaana baryi Eebahay, waxaana mudan inaanan ku noqonin baryada Eebahay mid ku xumaada (khasaara).'),
(2299, 19, 49, 'markuu ka fogaaday iyaga iyo waxay caabudi oo Eebe ka soo hadhay waxaan siinay Isxaaq iyo Yacquub mid kastana waxaan ka yeellay nabi.'),
(2300, 19, 50, 'waxaana ka siinnay naxariistanada (wanaag) waxaana u yeellay amaan run ah oo sarraysa.'),
(2301, 19, 51, 'ku sheeg kitaabka (nabi) Muuse wuxuu ahaa rasuul nabi ah.'),
(2302, 19, 52, 'waana uga dhawaaqnay dhinaca (buurta) dhuur ee midigta ah, waana soo dhawaynay annagoo la hadli.'),
(2303, 19, 53, 'waxaana ka siinnay naxariistanada walaalkiis Haaruun oo nabi ah.'),
(2304, 19, 54, 'kuna sheeg kitaabka (nabi) Ismaaeiil, wuxuu ahaa rumeeyaha yabooha wuxuuna ahaa rasuul nabi ah.'),
(2305, 19, 55, 'wuxuuna ahaa mid fara chelkiisa salaadda iyo zakada, wuxuuna ahaa mid Eebe agtiisa lagaga raalli yahay.'),
(2306, 19, 56, 'kuna sheeg kitaabka (nabi) Idriis, wuxuu ahaa runbadane nabi ah.'),
(2307, 19, 57, 'waxaana u koryeellay meel sare.'),
(2308, 19, 58, 'kuwaas waa kuwuu u nicmeeyey Eebe oo nabiyadii ah, oo durriyadii aadam ah, iyo kuwaan xambaarray la jirka (nabi) nuux iyo durriyaddii nabi Ibraahim iyo israa\'iil iyo kuwaan hanuuninay oon doorannay, marka lagu akhriyo korkooda aayaadka Eebaha Raxmaan ah way dhacaan iyagoo sujuudi oo ooyi.'),
(2309, 19, 59, 'waxaase ka gadaal maray kuwo xun oo dayacay salaadda oo raacay waxay naftoodu doonto (oo xun) waxayna la kulim sharr.'),
(2310, 19, 60, 'cidda toobad keenta oo rumeysa oo camal fiican fasha mooyee, kuwaasu waxay gali jannada lagamana dulmiyo waxba.'),
(2311, 19, 61, 'jannooyinka cadnin (yey gali) ee ah tuu u yaboohay (Eebaha) Raxmaana addoomadiisa, iyagoo maqan, wuuna ahaan yaboohiisu kii yimaadda.'),
(2312, 19, 62, 'kuma maqlaan dhexdeeda hadal macno darro, ee waxay maqlaan salaan, waxayna ku mudan rizqigooda dhexdooda aroor iyo galabba.'),
(2313, 19, 63, 'taasi waa jannada aannu dhaxalsiin addoomadanada kooda ah mid dhawrsada.'),
(2314, 19, 64, 'kuma soo dagno amarka Eebahaa mooyee, waxaa u sugnaaday waxa horteenna ah, iyo waxa inaga dambeeya iyo waxa u dhexeeya arrintaas, mana aha Eebahaa mid halmaansan.'),
(2315, 19, 65, 'waa Eebaha samooyinka iyo dhulka iyo waxa u dhaxeeya ee caabud kuna samir cibaadadiisa, ma u ogtahay Eebe wax la mid ah, (oo la magaca).'),
(2316, 19, 66, 'wuxuu dhihi dadkii (gaalka) ma markaan dhinto yaa laysoo bixin anoo nool.'),
(2317, 19, 67, 'miyuuna xusuusanayn dadku anagu inaanu abuuray horay isagoon waxba ahayn.'),
(2318, 19, 68, 'Eebahaan ku dhaartaye waannu kulmin iyaga iyo shayaadiinta, waxaana keenaynaa gaararka jahannamo iyagoo fadhiya.'),
(2319, 19, 69, 'markaas waxaannu ka siiban koox kasta midkooda ugu daran madax adayg (gaalimada).'),
(2320, 19, 70, 'annagaa og kuwa mudnaan badan galidda naarta.'),
(2321, 19, 71, 'cid idinka mid ah oon ku aroorayn ma jiro naarta, arrintaasna waxay ku ahaatay Eebahaa wax go\'an oo xukuman.'),
(2322, 19, 72, 'Markaas waxaannu korin kuwa Dhawrsaday, waxaannu kaga tagi Daalimiinta Dhexdeeda iyagoo Jilbo u Fadhiya.'),
(2323, 19, 73, 'marka lagu akhriyo korkooda aayaadkanaga oo cad cad waxay u dhihi kuwii gaaloobay kuwa rumeeyey labada kooxood midkee khayrroon naggadi oo wanaagsan kulan (fadhi).'),
(2324, 19, 74, 'badanaa intaan halaagnay hortood quruun ka fiican xoolo (iyo dhar) iyo aragba.'),
(2325, 19, 75, 'waxaad dhahdaa ciddii baadi ku sugan ha u fidiyo (sugo) Eebaha Raxmaana ah fidin, markay arkaan waxaa loogu gooddiyey ama cadaaba ama saacadda (qiyaame) waxay ogaan cidday xumaan badantahay gurigiisu oo taharyar junuud (ciidamo).'),
(2326, 19, 76, 'wuu u siyaadiyaa Eebe kuwa hanuunay hanuun, kuwa hadha ee suuban (camalka) yaa khayrroon Eebahaa agtiisa abaal marin khayrna roon meel laysu celiyo.'),
(2327, 19, 77, 'ka warran midka ka gaalooby aayaadkanaga oo yidhi waxaa lay siin (aakhiro) xoolo iyo carruur.'),
(2328, 19, 78, 'ma wuxuu daalaeday waxa maqan, mise wuxuu ka yeeshay Eebaha Raxmaana agtiisa ballan.'),
(2329, 19, 79, 'saas ma aha ee waannu qori wuxuu sheegi, waxaana u fududaynaa (kordhin) cadaabka fidin.'),
(2330, 19, 80, 'waana dhaxlaynaa wuxuu sheegi wuxuuna noo imaan kali.'),
(2331, 19, 81, 'waxay yeesheen Eebe ka sokow ilaahyo inay ugu noqdaan cizi (tabar).'),
(2332, 19, 82, 'saas ma aha ee waxay ka gaaloobi cibaadadooda, waxayna ku noqon korkooda lid (col).'),
(2333, 19, 83, 'miyaadan ogayn inaan ku dirro shayaadiinta gaalada oy boorrin (baadiyeyn) baadi.'),
(2334, 19, 84, 'ee ha ku degdegin korkooda, waannu uun u tirin tirec.'),
(2335, 19, 85, 'xusuuso maalintaan u kulmineyno kuwa dhawrsaday Eebaha Raxmaana iyagoo wafdi ah.'),
(2336, 19, 86, 'oon u kaxaynayno dambiilayaasha xagga jahannamo iyagoo aroora.'),
(2337, 19, 87, 'mana hantaan shafeeco cid ka yeelata Eebaha Raxmaana agtiisa ballan mooyee.'),
(2338, 19, 88, 'waxay dheheen (gaaladu) wuxuu yeeshay Eebaha Raxmaana ilmo.'),
(2339, 19, 89, 'waxaad la timaadeen wax wayn.'),
(2340, 19, 90, 'oy u dhawdahay samooyinku inay la dillaacaan lana jeexmo dhulku oy la hoobato buuruhu burbur.'),
(2341, 19, 91, 'inay u sheegeen Eebaha Raxmaana ilmo.'),
(2342, 19, 92, 'kuma habboona Eebaha Raxmaana inuu yeesho ilmo.'),
(2343, 19, 93, 'wax kastoo Samada iyo Dhulka ku sugan uxuu u imaan Eebe isagoo Addoona.'),
(2344, 19, 94, 'wuu koobay Eebe wuuna Tiriyey Tiro.'),
(2345, 19, 95, 'Dhammaantoodna waxay u imaan Eebe Qiyaamada iyagoo Kali ah.'),
(2346, 19, 96, 'kuwa Xaqa rumeeyey ee Camal fiican falay wuxuu u yeeli Eebaha Raxmaana Jacayl.'),
(2347, 19, 97, 'waxaan uun ugu fududaynay Quraanka Carrabkaaga inaad ugu Bishaarayso Quraanka kuwa dhawrsada uguna Digtid Qoom dood badan (oo xun).'),
(2348, 19, 98, 'Badanaa intaan halaagnay hortood Qarni, ma ka kasaysaa xaggooda (aragtaa) Ruuxna, ama ma ka maqli xaggooda Cod.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'Dhaahaa (Nabiyow) micna kalana'),
(2350, 20, 2, 'maanaan dejin korkaaga Quraanka inaad ku Dhibbooto.'),
(2351, 20, 3, 'waase waanada Ciddii cabsan.'),
(2352, 20, 4, 'waana soo dejinta Eebaha Abuuray Dhulka iyo Samooyinka sare.'),
(2353, 20, 5, 'Eebaha Raxmaana Carshiguu ku Istawooday (si u cunanta).'),
(2354, 20, 6, 'waxaana u sugnaaday waxa Samooyinka iyo Dhulka ku sugan iyo waxa u dhaxeeya iyo waxa ka hooseeya Dhulka.'),
(2355, 20, 7, 'haddaad kor u Qaaddo Hadalka Eebe waa ogyahay waxa qarsoon iyo waxa la qariyey.'),
(2356, 20, 8, 'Eebe Ilaah kale ma jiro isaga mooye, waxaana u sugnaaday Magacyo Fiican.'),
(2357, 20, 9, 'ma ku soo Gaadhay warkii (Nabi) Muuse.'),
(2358, 20, 10, 'markuu arkay Dab oo u yidhi Ehelkiisa Nagaada (suga) Anigu waxaan Arkaa Dabe, waxaana u dhawahay inaan idiinka keeno Dhuxul ama aan ka helo Dabka korkiisa Cid wax tusisa.'),
(2359, 20, 11, 'markuu yimid (Muuse) Dabkii waxa loogu Dhawaaqay Muusow.'),
(2360, 20, 12, 'anigu waxan ahay Eebahaa ee Siib Kabahaaga Adigu waxaad ku sugantahay Toggii Daahirnaa ee Dhuwa.'),
(2361, 20, 13, 'anaana ku doortay ee maqal waxa laguu waxyoon.'),
(2362, 20, 14, 'Anigu waxaan ahay Alle Ilaah (kalana) ma jiro Ani mooyee ina Caabuda, Oogna Salaadda Xuskayga.'),
(2363, 20, 15, 'Saacadda (Qiyaame) way imaan waxaana u Dhawayahy inaan Qariyo in laga Abaal mariyo Naf kasta waxay Camal Fashay.'),
(2364, 20, 16, 'ee Yuusan kaa lilin Xaggeeda Ruuxaan Rumeyn Saacadda, raacayna Hawadiisa (Xun) ood Halaagsanto.'),
(2365, 20, 17, 'waa maxay Taas midigtaada ku Jirta Muusow (ogaal waydiin).'),
(2366, 20, 18, 'wuxuu yidhi iyada waa Ushaydii waa Cuskadaa Korkeeda waxna waan ugu Lulaa Adhigayga Dana kalana waa iigu sugnaadeen.'),
(2367, 20, 19, 'wuxuu yidhi (Eebe) Tuur Usha Muusow.'),
(2368, 20, 20, 'wuuna Tuuray waayna soo baxday Mas Ordi.'),
(2369, 20, 21, 'wuxuuna yidhi (Eebe) Qaad hana Cabsanin waxaannu ku celin Xaalkeedii Horce.'),
(2370, 20, 22, 'U Dum Gacantaada Garabkaaga waxay soo Bixi iyadoo Cad Xumaan la\'aan, waana Calaamad kale.'),
(2371, 20, 23, 'Inaan ku tusinno Aayaadkanaga wey.'),
(2372, 20, 24, 'ee Aad xagga Fircoon wuu Xadgudbaye (Kibray).'),
(2373, 20, 25, 'wuxuu yidhi (Muuse Eebow ii waasici Laabta.'),
(2374, 20, 26, 'iina Sahal (fududee) Amarkayga.'),
(2375, 20, 27, 'Furna Guntinta Carrabkayga.'),
(2376, 20, 28, 'ha Kaseen Hadalkeygee.'),
(2377, 20, 29, 'Iina yeel Wasiir Ehelkeyga ah.'),
(2378, 20, 30, 'Haaruun Walaalkay.'),
(2379, 20, 31, 'Kuna adkee «Xooji» Tabartayda (Dhabarkayga).'),
(2380, 20, 32, 'Lana wadaaji Amarkayga.'),
(2381, 20, 33, 'Inaan kugu Tasbiixsanno wax badan.'),
(2382, 20, 34, 'oon ku Xusno wax badan.'),
(2383, 20, 35, 'Adugu waxaad tahay mid na Arkee.'),
(2384, 20, 36, 'Wuxuu yidhi (Eebe) waa lagu siiyey Warsigaagii Muusow.'),
(2385, 20, 37, 'Waana kugu Mannaysannay Wanaag (ku siinay) korkaaga markale.'),
(2386, 20, 38, 'markaan u waxyoonay Hooyadaa (ku ilhaamay) wixii lagu ilhaameeye.'),
(2387, 20, 39, 'ku Tuur Muuse Taabud, kuna tuur Taabuudka Hirka Badda, Hirkuna ha ku rido Xeebta, waxaa qaadan Colkayga iyo Colkiisa (Fircoon), waxaana kugu tuuray Jeceylkeyga korkaaga iyo in lagu barbaariyo Ilaalintayda.'),
(2388, 20, 40, '(waxaan kugu Mannaysanay) markay Socotay Walaashaa oy lahayd maydin tusiyaa Ruux idiin Dhaqaalayn waxaana Kugu eelinay Hooyadaa si ay u qabowdo isheedu, oyna Murugoon, waxaadna Dishay Naf waana kaa korinay walbahaar, waana ku Imtixaanay Imtixaan, waxaadna ku nagaatay Sanooyin Ehelkii Madyan, markaasaad ku timid Qaddar (Eebe) Muusow.'),
(2389, 20, 41, 'waxaana kuu doortay Naftayda.'),
(2390, 20, 42, 'ee La taga Adiga iyo Walaalkaa Aayaadkayga hana ka daalina Xuskayga.'),
(2391, 20, 43, 'Aad Xagga Fircoon wuu Xadgudbaye.'),
(2392, 20, 44, 'una dhaha Hadal Jilicsan bal inuu waxa xusuusto ama Cabsado.'),
(2393, 20, 45, 'waxay dheheen Eebow waxaannu ka Cabsan inuu nagu degdego ama nagu xadgudbo.'),
(2394, 20, 46, 'wuxuu yidhi (Eebe) ha Cabsanina anaa idinla jira waana Maqli oon Arkaa.'),
(2395, 20, 47, 'ee u Taga (Fircoon) una dhaha annagu waxaan nahay Rasuulladii Eebahaa ee Dir (u daa) Banii Israa\'iil hanala jireene, hana Cadaabin (Ciqaabin) waxaan kuula nimid Aayad xagga Eebahaa, nabadgalyana ha ahaato Tuuxii raaca Hanuunka.'),
(2396, 20, 48, 'Annaga waxaa Naloo Waxyooday in Cadaab u Sugnaaday Ruuxii Beeniya (Xaqa) oo Jeedsada.'),
(2397, 20, 49, 'Wuxuu Yidhi (Fircoon) waayo Eebihiin Muusow.'),
(2398, 20, 50, 'Wuxuu yidhi (Muuse) Eebahanno waa kan Siiya wax kasta Abuurkiisa oo Toosiyey.'),
(2399, 20, 51, 'Wuxuu yidhi (Fircoon) Sidee Yahay Xaalkii Quruumihii hore.'),
(2400, 20, 52, 'Wuxuu Yidhi (Muuse) Cilmigoodu waa Eebe Agtiis Kitaab aan ka dhumeyn Eebe uuna Halmaameyn.'),
(2401, 20, 53, 'Waa Eebaha ka yeelay Dhulka Gogol, oo idinka bixiyey Dhexdeeda Waddooyin oo idiinka soo Dejiyey Samada Biyo oon ku soo Bixinay Noocyo Doog ah oo kala Tagsan.'),
(2402, 20, 54, 'ee Cuna oo Daajiya Xoolihiinna, Arrintaasna Calaamooyin yaa ugu Sugan kuwa Caqliga leh.'),
(2403, 20, 55, 'Xaggeedaan (Dhulka) idinka Abuuray, Dhexdeedana idinku Celin, Xaggeedaana Laydinka soo Bixin Mar Kale.'),
(2404, 20, 56, 'Waxaan Dhab ahaan u Tusinay (Fircoon) Aayaadkanaga Dhamaan, wuuna Beeniyey oo Diiday.'),
(2405, 20, 57, 'Wuxuu yidhi (Fircoon) Ma waxaad noola timid Inaad nagaga Bixiso Dhulkanaga Sixirkaaga Muusow.'),
(2406, 20, 58, 'Waxaannu kuula imaanaynaa Sixir la mid ah ee mooyeel Dhexdeenna Balaan aynaan Baajinayn anaga iyo Adigu, Meel eg (La yaqaan).'),
(2407, 20, 59, 'Wuxuu yidhi (Nabi Muuse) Ballankeennu waa Maalinta Quruxda (Kulankooda) waana in la soo kulmiyo Dadka Barqin.'),
(2408, 20, 60, 'Wuuna jeedsaday Fircoon wuxuuna kulmiyey Dhagartiisii wuuna yimid.'),
(2409, 20, 61, 'Wuxuu ku yidhi Muuse Magaciin be ha ku Abuuranina Eebaha korkiisa Been oos idinku Halaago Cadaab, waxaana Khasaaray Ruuxii Been Abuurta.'),
(2410, 20, 62, 'Waxayna ku Doodeen amarkooda dhexdooda wayna faqeen.'),
(2411, 20, 63, 'Waxayna dheheen Labadani waa Saaxirro waxayna dooni inay Idinkaga Bixiyaan Dhulkiinna Sixirkooda oy la tagaan Dariiqadiinna (Sharaftiinna) Fiican.'),
(2412, 20, 64, 'ee Kulmiya Dhagar (tiinna) Imaadana idinkoo Saf ah waxaa Liibaanay Maanta Ciddii Sarreysee.'),
(2413, 20, 65, 'waxay dheheen Muusow ama Tuur Usha ama aan anagu ahaanno kuwa hor tuura.'),
(2414, 20, 66, 'wuxuu yidhi (Muuse) bal Tuura waxaana soo baxay xadhkahoodii iyo Ulahoodii oo loogu ekeysiiyey sixirkoodii inay ordi.'),
(2415, 20, 67, 'wuxuuna ka kasay naftiisa Cabsi Muuse.'),
(2416, 20, 68, 'waxaana nidhi ha Cabsan adigu adaa Sarreyne.'),
(2417, 20, 69, 'Tuur waxa midigtaada ku jira ha gurato waxay sameeyeene, waxay uun sameeyeen Dhagar sixir, mana Liibaano Sixirrow meejuu yimaaddo.'),
(2418, 20, 70, 'waxaa la riday Saaxiriintii iyagoo Sujuudi, waxayna dheheen waxaan rumeynay Eebaha Haaruun iyo Muuse.'),
(2419, 20, 71, 'wuxuuna yidhi (Fircoon) miyaad u rumeyseen ka hor idankayga isagu waa kan idinku wayn ee idin baray Sixirka waxaaan goyn Gacmihiinna iyo Lugihiinna si isdhaaf ah waxaana idinku (daldali) wadhi Jirrid Timir, waxaadna ogaanaysaan midkanaga daran cadaab hadhidna badan.'),
(2420, 20, 72, 'waxay dhaheen ka dooran meyno waxa noo yimid oo xujo ah iyo Eebaha na abuuray, ee xukmi waxaad xukumi, waxaad uun xukumi nolosha adduunyo.'),
(2421, 20, 73, 'annagu waxaan rumeeynay Eebahanno inuu noo dhaafo gafafkanaga iyo waxaad nagu qasabtay (fircoon) oo Sixira Eebaa Khayrroon oo hadhi.'),
(2422, 20, 74, 'ruuxii u yimaadda Eebihiis (aakharo) isagoo dambiile ah waxaa u sugnaaday jahannamo kumana dhinto dhexdeeda kumana noolaado.'),
(2423, 20, 75, 'ruuxiise u yimaadda isagoo Mu\'min ah oo falay camal fiican kuwaas waxaa u sugnaaday Jannooyinka sare.'),
(2424, 20, 76, 'Jannooyinka nagaadiga (yey gali) ooy socoto dhexdeeda Wabiyaal iyagoo kuwaari dhexdeeda, taasina waa abaalmarinta Ciddii nadiif noqota.'),
(2425, 20, 77, 'waxaan dhab ugu waxyoonay (Nabi) Muuse guuri Addoomahayga uguna yeel waddo Badda oo ingagan hana ka cabsan haleel, hana cabsan (Badda).'),
(2426, 20, 78, 'waxaana raacay Fircoon (oo wata) Colkiisii waxaana ka daboolay hirka Xaggiisa wax daboolay.'),
(2427, 20, 79, 'wuxuuna dhumiyey Fircoon Qoomkiisii mana hanuunin.'),
(2428, 20, 80, 'Banii Israa\'iilow waxaan idinka korinay Cadawgiinna, waxaana idinkula yaboohtanay (ballari) dhinieii buurta ee midig, waxaana ku soo dejinay kor kiinna Macaan iyo Shimbir.'),
(2429, 20, 81, 'ka cuna wanaaggaan idinku Arsuqay hana ku xadgudbina dhexdiisa, oo idinku dhaco cadhadaydu, ruuxay ku dhaedo cadhadaydu dhabbuu u Hoobtay (halaagmay).'),
(2430, 20, 82, 'anigu waan u dhaafid badanahay ruuxii Toobad keena oo rumeeya oo fala wanaaga oo hanuuna.'),
(2431, 20, 83, 'maxaa kaa soo dedejiyey Qoomkaagii Muusow.'),
(2432, 20, 84, 'wuxuu yidhi (Muuse) iyagu waa kuwan raadkaygay ku soo joogaan, waana kuu soo dag degay Eebow inaad iga raalli noqoto.'),
(2433, 20, 85, 'wuxuu yidhi (Eebe) Annagu waxaan imtixaannay qoomkaagii gadaashaa waxaana dhumiyey Saamiri.'),
(2434, 20, 86, 'markaasuu u noqday (Nabi Muuse) qoomkiisii isagoo cadhaysan oo walbahaarsan wuxuuna yidhi qoomkayow miyuuna idiin yaboohiin Eebihiin yabooh wanaagsan, ma waxaa idinku dheeraaday korkiinna ballankii mise waxaad doonteen inay idinku dhaedo Cadhada Eebe ood u baajiseen ballankaygii.'),
(2435, 20, 87, 'waxay dheheen maanaan u baajin ballankaaga awooddanada waxaase nala saaray culaysyo Alaab oo ah waxa qoomku isku qurxinjireen waana tuurray siduu u tuuray Saamiri.'),
(2436, 20, 88, 'wuxuuna u soo bixiyey dibi muuqal ah oo ood leh, waxayna dhaheen kani waa Ilaahiin iyo Ilaahii Muuse wuuna halmaamay.'),
(2437, 20, 89, 'miyeyna arkay inuusan u soo Celinayn Hadal una hananayn dhib iyo Nacfi (midna).'),
(2438, 20, 90, 'wuxuunau yidhi Haaruun mar hore qoomkayow waa uun laydinku fidmeeyey, Eebihiinna waa Raxmaan ee i raaca, adeecana amarkayga.'),
(2439, 20, 91, 'waxay dhaheen kama tagayno korkiisa inaan ku nagaanno intuu nooga soo noqon muuse.'),
(2440, 20, 92, 'wuxuu yidhi (muuse) Haaruunow maxaa kuu diiday markaad aragtay inay dhumeen.'),
(2441, 20, 93, 'inaadan raacin ma waxaad caasiday Amarkeygii.'),
(2442, 20, 94, 'wuxuu yidhi (Haaruun) ina hooyow hayqabanin Gadhka iyo madaxa, anigu waxaan ka cabsaday inaad dhahdo waxaad kala gaysay Banii israa\'iil dhexdooda oodan maqlin hadalkayga.'),
(2443, 20, 95, 'wuxuu yidhi (muuse) xaalkaagu muxuu yahay Saamiriyow.'),
(2444, 20, 96, 'wuxuuna yidhi waxaan arkay waxayna arkin, waxaana qaatay wax ka mid ah raad rasuul (malaga) waana tuuray saasayna ii qurxisay naftaydu.'),
(2445, 20, 97, 'wuxuu yidhi (Muuse) tag, waxaa kuu sugnaaday nolosha inaad dhahdo taabasho ma jirto, waxaana kuu sugnaaday waqti aadan baajinayn, day ilaahaagii aad ahayd mid ku kor nagi (caabudi) waanu gubi markaas waxaana ku firdhin mowjadda (badda) firdhin.'),
(2446, 20, 98, 'Eebihiin waa uun Ilaahiinna ah kaan Eebe jirin isaga mooyee, wuuna u waasac noqday wax kasta ogaansho.'),
(2447, 20, 99, 'saasaan kuugu qisoon (sheegi) warkii hor maray, waxaana kaa siinay agtanada quraan.'),
(2448, 20, 100, 'ciddii ka jeedsata xaggiisa waxay xambaari maalinta qiyaame culays (dambi).'),
(2449, 20, 101, 'iyagoo ku waari (ciqaabta) dhexdeeda waxaana u xumaaday iyaga maalinta qiyaame rar.'),
(2450, 20, 102, 'waa maalinta la afuufi suurka, oon soona kulmineyno dambiilayaasha maalintaas iyagoo indho cawlan.'),
(2451, 20, 103, 'wayna is warsan (xansan) dhexdooda maydaan ku nagaanin (adduunyada) Toban mooyee (maahmood)'),
(2452, 20, 104, 'anagaa og waxay dhihi, markuu dhihi kooda ugu jid leh maydaan nagaanin maalin mooyee.'),
(2453, 20, 105, 'waxay ku warsan Buuraha waxaad dhahdaa waxaa burburin Eebahay burburin.'),
(2454, 20, 106, 'wuxuuna ka yeeli meel siman oo ebar ah.'),
(2455, 20, 107, 'ku arki maysid qallooc iyo taag midna.'),
(2456, 20, 108, 'maalintaas waxay raaci dhawaaqaha, kamana leexdaan waxayna u xasishay Cododku Eebaha Raxmaana mana maqashid shanqadh mooyee.'),
(2457, 20, 109, 'maalintaas ma anfacdo shafeeco cid Eebaha Raxmaana idmo oo ka raalli noqday hadalkiisa mooyee.'),
(2458, 20, 110, 'Wuxuu ogyahay Eebe waxa hortaada ah iyo waxa ka dambeeya mana ku koobayaan Eebe ogaansho.'),
(2459, 20, 111, 'waxay u khuduucday Wajiyadu Eebaha nool ee wax maamula, waxaana khasaaray ruuxii xambaara dulmi.'),
(2460, 20, 112, 'ruuxii fala wanaag isagoo rumeeyey kama Cabsado dulmi iyo nusqaamin midna.'),
(2461, 20, 113, 'saasaanu kugu soo dejinnay Quraan Carabi ah uguna caddaynay ((celcelinay) dhexdiisa Gooddi inay dhawrsadaan darteed ama u timaado xusuus (waano).'),
(2462, 20, 114, 'waxaa sarreeya Eebaha xaakimka xaqa ah, hana ku degdein quraanka ka hor dhamayn waxyigiisa xaggaaga, waxaadna dhahdaa Eebow ii kordhi Cilmi.'),
(2463, 20, 115, 'dhab ahaan yaan ula Ballantannay Aadan mar hore wuuna halmaamay kama aanaan helin niyo Adag.'),
(2464, 20, 116, 'xusuuso markaan ku nidhi Malaa\'igta u Sujuuda Aadam wayna Sujuudeen Ibliis mooyee oo diiday.'),
(2465, 20, 117, 'waxaana nidhi Aadamow kani (Ibliis) waa Colkaaga iyo colka Haweeneydaada ee yuusan idinka bixinin janada ood dhibbooto.'),
(2466, 20, 118, 'waxaa kuu sugnaaday inaadan ku gaajoon dhexdeeda kuna arradin.'),
(2467, 20, 119, 'waxaana kuugu sugnaaday inaadan ku oomin dhexdeeda kuna Qorroxoobin.'),
(2468, 20, 120, 'waxaase waswaasiyey shaydaan wuxuuna yidhi Aadamow ma ku tusiyaa Geedda waaridda iyo xukun aan gaboobayn.'),
(2469, 20, 121, 'wayna cuneen xaggeeda waxaana u muuqday Cawradoodii, waxayna noqdeen kuwa isku dhejiya korkooda caleenta jannadam wuu caasiyey Aadan Eebihiis wuuna leexday.'),
(2470, 20, 122, 'waxaase doortay Eebihiis wuuna ka Toobadaqbalay oo hanuuniyey.'),
(2471, 20, 123, 'wuxuu yidhi (Eebe) ka hoobta Jannada dhammaantiin qaarkiin qaar colbuu u yahaye, hadduu xaggayga idiinka yimaaddo hanuun ruuxii raaca hanuunkeyga ma dhumo (adduunka) mana xumaado (aakhiro).'),
(2472, 20, 124, 'ruuxisa ka jeedsada xuskeyga (Quraanka) waxaa u sugnaaday nolol cidhiidhi ah, waxaana soo kulminaynaa maalinta qiyaame isagoo arag La\'.'),
(2473, 20, 125, 'wuxuuna dhihi Eebow maxaad ii soo kulmisay (keentay) anoo arag la\' oona ahaa arke (adduunkii).'),
(2474, 20, 126, 'wuxuu yidhaa (Eebe) waa saas waxaa kuu timid aayadahanaga waadna halmaantay saasaana maanta lagaaga tagi (sida halmaan).'),
(2475, 20, 127, 'saasaan ku abaal marinaa ciddii xadgudubta oon rumayn aayaadka Eebihiis cadaabka aakhiraana daran oo hadhid badan.'),
(2476, 20, 128, 'miyuuna u caddayn Gaalada intaan halaagnay hortood quruumo iyagoo ku socda guryahooda, taasna calaamaa ugu sugan kuwa caqliga leh.'),
(2477, 20, 129, 'haddayna jirin kalimad u hor martay Eebahaa iyo muddo maga caaban wuxuu ahaan lahaa cadaabku mid ku soo deg dega.'),
(2478, 20, 130, 'ee ku samir waxay sheegi kuna tasbiixso adoo mahadda Eebahaa ku dheehan ka hor qorrax soo bax iyo ka hor dhicidda, saacadaha habeenkiina tasbiixso (Tuko) iyo darfaha maalinta inaad raalli noqoto.'),
(2479, 20, 131, 'hawtaagin indhahaaga waxaan ugu raaxaynay nooc ka mid ah oo quraxda nolashaa adduun si aan ugu fidnayno, rizqiga Eebahaa yaase khayrroon oo hadhid badan.'),
(2480, 20, 132, 'far ehelkaaga salaadda kuna samir korkeeda ku warsanmayno rizqiye annagaa ku arzuqi cidhibta (fiicana) waxaa iska leh dhawrsashada.'),
(2481, 20, 133, 'waxay dheheen gaaladii muu noola yimaaddo aayad xagga Eebihiis ka timid, miyuuna u imaanin Maraggii wixii ku sugnaa Kutubihii hore.'),
(2482, 20, 134, 'haddaan ku halaagno Cadaab Rasuulka (iyo Quraanka) ka hor waxay dhihilahaayeen Eebahannow maxaad noogu diri wayday rasuul oon raacno aayaadkaaga ka hor dullowganaga iyo khasaarahanaga.'),
(2483, 20, 135, 'waxaad dhahdaa (Nabiyow) dhammaan way sugi ee suga, waxaad ogaan doontaan cidda jidka toosan iyo cidda hanuunsane.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'Xisaabtoodii iyagoo Halmaansho ku Sugan oo Jeedsan.'),
(2485, 21, 2, 'Quraankastee oo uga Yimaadda Xagga Eebahood kana Soo darriya way maqlaan iyagoo Ciyaari.'),
(2486, 21, 3, 'Oy shuqloontahay Quluubtoodu, waxayna hoos u dhigeen Faqii kuwii Dulmi Falay (iyagoo dhihi) kanu ma waxaan Dad idinkoo kale ahaynbaa, ma waxaad u imanaysaan sixir idinkoo Arka.'),
(2487, 21, 4, 'Wuxuu yidhi (Nabigii) Eebahaybaa Og Hadalka Samada ku Sugan iyo Dhulka, waana Maqle Oge ah.'),
(2488, 21, 5, 'Waxay Dheheen (Gaaladii) Saas ma aha ee waa Qarowyo saa ma aha ee wuu Been Abuurtay, Saas ma aha ee waa gabayaa ee ha noo keeno Aayad Sidii Lagu diray Kuwii hore.'),
(2489, 21, 6, 'Mayna Rumeynin Hortood Magaalo aan Halaag (La doonay) ee Ma iyagaa Rumeyn.'),
(2490, 21, 7, 'Maanaan Dirin Hortaa Rag mooyee, kuwaas oon u Waxyoonay, ee Warsada kuwa Cilmiga leh Haddaydaan wax Ogayn.'),
(2491, 21, 8, 'Kamaanaan Dhigin (Nabiyada) Jasad (Muqaal) oon Cunayn Cunno mana ahayn kuwo Waara.'),
(2492, 21, 9, 'Markaasna waxaan u Rumeynay Yaboohii waana Korrinay iyagii iyo Ciddaan Doono, waxaana Halaagnay Xadgudbayaasha.'),
(2493, 21, 10, 'Waxaan idiin soo Dejinay Kitaab Sharaftiinnu ku Sugantahayee Miyeydaan Kasayn.'),
(2494, 21, 11, 'Badanaana intaan Halaagnay Magaalo Dulmi Badan (Dadkeedu) oon Ahaysiinay Gadaasheed Qoom kale.'),
(2495, 21, 12, 'Markay kaseen Cadaabkanaga yey ahaayeen kuwo ka Carari.'),
(2496, 21, 13, 'Ha Cararina kuna noqda wixii laydiinku Raaxeeyey iyo Guryihiinna waxay u Dhawdahay in wax laydin warsadee.'),
(2497, 21, 14, 'Waxay Dhaheen Halaaganagee waxaan nahay Daalimiin.'),
(2498, 21, 15, 'Saasna kuma tagin Qayladoodu intaan ka yeello, wax la shafay oo Bakhtiya.'),
(2499, 21, 16, 'Maanaan Abuurin Samooyinka iyo Dhulka iyo waxa u Dhaxeeya Anagoo Ciyaari.'),
(2500, 21, 17, 'Haddaan Doono Madadaallo inaan yeelano waxaan ka yeelan lahayn Agtanada haddaan Falayno.'),
(2501, 21, 18, 'Xaqaanse ku Gannaa Baadilka oo Tirtira markaasuu tagaa, waxaa idiinku Sugnaaday Halaag waxaad Sheegaysaan.'),
(2502, 21, 19, 'Ilaahay waxaa u sugnaaday waxa Samooyinka ku sugan iyo Dhulka, kuwa Eebe Agtiisa ahna Iskama Kibriyaan Cibaadadiisa kamana daalaan.'),
(2503, 21, 20, 'wayna Nasahaan Habeen iyo Dharaarba kamana Noogaan.'),
(2504, 21, 21, 'Mise waxay yeesheen (Gaaladii) Ilaahyaal Dhulka Xaggiisa oo wax soo Nooleeya.'),
(2505, 21, 22, 'Hadday yihiin dhexdooda (Samada iyo Dhulka) Ilaahyo way Fasaadi Lahaayeen, waxaa ka Nasahan Eebaha Carshiga leh waxay ku Tilmaami.'),
(2506, 21, 23, 'Lama warsanayo wuxuu Falo Iyagase waa la warsan.'),
(2507, 21, 24, 'Mise waxay Yeesheen Eebe ka sokow Ilaahyo, waxaad Dhahdaa keena Xujadiinna, kana waa (quraanka) sheegi waxa ila jooga iyo sheegiddii kuwii iga horreeyey, Badidoodse ma oga Xaqa wayna ka Jeedsan.'),
(2508, 21, 25, 'Rasuulkaan hortaa dirayba waxaan u waxyoonay ilaah aan Ani ahayn ma jiro ee I caabuda.'),
(2509, 21, 26, 'waxay dhaheen Gaaladii wuxuu Yeeshay (Eebaha) Raxmaana Ilmo, waa ka Nasahanyahay, waase Addoomo (Malaa\'igtu) la Sharrifo.'),
(2510, 21, 27, 'kagama hor maraan Hadal iyaguna Amarkiisay ku Camalfalaan.'),
(2511, 21, 28, 'wuxuu ogyahay waxa hortooda iyo Gadaashooda ah umana shafeecaan Ciduu ka raaliyahay mooyee iyaguna Cabsidiisay la Baqaan.'),
(2512, 21, 29, 'ruuxii dhaha oo ka mid ah Malaa\'igta anaa Ilaah ah Eebe ka sokow, kaas waxaannu ku Abaal marin Jahannamo, saasaana ku Abaal marinnaa Daalimiinta.'),
(2513, 21, 30, 'miyeyna arkayn kuwii Gaaloobay in Samooyinka iyo dhulku ahaayeen kuwo isku dhaggan oona kala Bixinay, kana yeelay Biyaha wax kasta oo Nool ee miyeyna Rumaynayn.'),
(2514, 21, 31, 'waxaana yeellay Dhulka Sugayaal (buuro) inayna la iilanin Dadka, waxaana yeellay Dhexdeeda Karimo Waddooyin ah, inay ku Toosaan.'),
(2515, 21, 32, 'waxaana ka yeelay Samada Saanqaaf ilaashan iyaguna Calaamooyinkeeda way ka jeedsan.'),
(2516, 21, 33, 'Eebe waa kan Abuuray Habeenka, Maalinta, Qorraxda iyo dayaxa mid kastana Falag buu Dhexdabbaalan.'),
(2517, 21, 34, 'maanan yeelin Dadka ka horreeyey kuwo waara ee haddaad dhimato ma iyagaa waari (Gaaladu).'),
(2518, 21, 35, 'Naf kastaa waxay dhadhamin Geeri waxaana idinku Imtixaami Shar iyo Khayr Ibtilo darteed, xagganagaana laydiin soo Celin.'),
(2519, 21, 36, 'markay ku arkaan kuwii Gaaloobay waxay kaa yeeshaan jeesjees iyagoo dhihi ma kanaa kan sheegi Ilaahyihiinna iyagoo ka gaaloobi xusidda Eebaha Raxmaana.'),
(2520, 21, 37, 'waxaa laga abuuray Dadka Deg deg, waxaana idin tusin Aayaadkayga ee ha degdegina.'),
(2521, 21, 38, 'waxay dhihi waa goorma Yaboohii (Cadaabku) haddaad run sheegaysaan.'),
(2522, 21, 39, 'hadday ogyihiin kuwii Gaaloobay markaan laga reebeyn Wajiyadooda Naarta iyo Dhabarkooda oon loona gargaarayn (ma degdegsadeen).'),
(2523, 21, 40, 'waxayse ugu imaan (naartu) si kada ah wayna wareerin mana karayaan Celinteeda lamana Sugo.'),
(2524, 21, 41, 'waxaa lagu jees jeesay Rasuullo kaa horreeyey waxaana ku dhacay kuwii ku jees jeesay oo Gaalada ah waxay ku jees jeesi jireen.'),
(2525, 21, 42, 'waxaad dhahdaa yaa idinka Ilaalin Habeen iyo Dharaarba Eebaha Raxmaana waxayse iyagu ka jeedsan Xusidda Eebahood.'),
(2526, 21, 43, 'ma waxaa u sugnaaday Ilaahyo naga celin ma karaan (waxay caabudi) Gargaarka Naftooda nalagamana Magan galiyo.'),
(2527, 21, 44, 'Saas ma aha cc waxaan u Raaxaynay kuwaas iyo Aabayaalkood Intuu ku Dheeraado Cimrigu, Miyeyna Arkayn Inaannu ku imanayno Dhulka Anagoo Nusqaamin (Guusha Islaamka) Miyagaa Adag.'),
(2528, 21, 45, 'Waxaad Dhahdaa waxaa uun aan idiinku Digi Waxyiga Mana Maqlo Dhagoole Dhawaaqa marka loo Digi.'),
(2529, 21, 46, 'Hadduu Taabto wax Yar oo Cadaabka Eebahaa ah waxay Odhan Halaagganagee Anagaa Daalimiin ah,.'),
(2530, 21, 47, 'Waxaannu u Dejinaynaa Miisaanka Cadilka ah Maalinta Qiyaame lagamana Dulmiyo Naf waxba, haba ahaado wax la eg Xabbad Khardal ah (wax yar) ee waan siinaynaa, annagaana ugu Filan Xisaabiye.'),
(2531, 21, 48, 'Dhab ahaan yaan u Siinay (Nabi) Muuse iyo (Nabi) Haaruun kala Bixiye (Tawreed) iyo Nuur iyo Waanada kuwo Dhawrsada.'),
(2532, 21, 49, 'Oo ah kuwa ka Cabsada Eebahood iyagoo kali ah iyagoo Saacadda (Qiyaame) ka Cabsan.'),
(2533, 21, 50, 'Kan (Quraankuna) waa Quraan la Barakeeyey oon Soo Dejinay ee midinkaa Diidi Isaga.'),
(2534, 21, 51, 'Dhab ahaan waxaan u Siinay Nabi Ibraahim Hanuukiisa mar hore anagaana Ogayn.'),
(2535, 21, 52, 'Markuu ku Yidhi Aabihiis iyo Qoomkiisii waa maxay Sawiradan aad ku kor Negidihiin (Caabudaysaan).'),
(2536, 21, 53, 'waxay Dheheen waxaan Hellay Aabayaalkanno oo Caabudi.'),
(2537, 21, 54, 'Wuxuu yidhi (Nabi) Ibraahim waxaad ku Sugantihiin idinka iyo Aabayaalkiin Baadi Cad.'),
(2538, 21, 55, 'Waxay Dheheen ma waxaad Noola Timid Xaq Mise Adigu waxaad ka mid Tahay kuwa Ciyaari.'),
(2539, 21, 56, 'Wuxuuna Yidhi Eebihiin waa Eebaha Samooyinka iyo Dhulka ee ah Kan Abuuray Aniguna Arrintaas yaan ahay kuwa ka Marag Kici.'),
(2540, 21, 57, 'Eebaan ku Dhaartayc waan Dhagri Sanamyadiinna ka Dib markaad Jeedsataan Idinkoo Tagi.'),
(2541, 21, 58, 'Wuxuuna ka Yeelay Burbur, Koodii waynaa mooye inay Xaggiisa u Noqdaan (Inuu Jajabiyey Isagu).'),
(2542, 21, 59, 'waxay Dheheen yaa ku falay Sidan Ilaahyadanadii Kaasi waa Daalime.'),
(2543, 21, 60, 'Waxayna Dheheen Waxaan Maqallay Wiil Sheegi lana Dhaho Ibraahim.'),
(2544, 21, 61, 'Waxay Dheheen keena dadka Indhihiisa inay ku Marag Furaan.'),
(2545, 21, 62, 'waxay Dheheen Gaaladii Maadaa ku Falay Sidan Ilaahyadannada Ibraahimow.'),
(2546, 21, 63, 'Wuxuu Yidhi waxaa Fatay Kooda Weyn ee Kana, Waydiiya Iladday Hadli.'),
(2547, 21, 64, 'Markaasay u Noqdeen Naftooda waxayna Isu Dhaheen war Idinkaa Daalimiina.'),
(2548, 21, 65, 'Markaasaa Loogadiyey Madaxooda (waxayna Dhaheen) Dhabbaad u Ogtahay inayna kuwani Hadlayn.'),
(2549, 21, 66, 'Wuxuu Yidhi ma waxaad Caabudaysaan Eebe ka Sokow waxaan Idin Anfacayn waxba idinna Dhibayn.'),
(2550, 21, 67, 'Ufbaa laydin Yidhi Idinka iyo Waxaad Caabudaysaan Eebe ka Sokow Miyeydaan wax kaseyn.'),
(2551, 21, 68, 'Waxay Dhaheen Guba una Gargaara Ilaahyadiinna Haddaad wax Falaysaan.'),
(2552, 21, 69, 'Waxan Nidhi Naaray u Noqo Qabow iyo Nabadgalyi Ibraahim.'),
(2553, 21, 70, 'Waxay la Dooneen Dhagar waxaana ka yeellay kuwo Khasaaray.'),
(2554, 21, 71, 'Waannu u Korinay (Nabi) Ibraahim iyo Nabi Luudh Dhulkii aan u Barakaynay Caalamka (Shaam).'),
(2555, 21, 72, 'Waxaan Siinay Isxaaq iyo Yacquub oo Siyaada ah, waxaana ka Yeellay Dhammaan kuwo Suubban.'),
(2556, 21, 73, 'Waxaana ka yeellay Imaamyo ku Hanuuniya Amarkanaga, waxaana u waxyoonay Falidda Khayrka, Oogidda Salaadda iyo Bixinta Sakada waxayna ahaayeen kuwo Na caabuda.'),
(2557, 21, 74, '(Nabi) Luudhna waxaan Siinay Xukun iyo Cilmi, waxaana ka Korinay Magaalaadii ahayd mid Fasha Xumo waxay ahaayeen Qoom Xun oo Faasiqiina.'),
(2558, 21, 75, 'Waxaana Galinay Naxariistanada, wuxuuna ka mid ahaa kuwa Suuban.'),
(2559, 21, 76, '(Nabi) Nuuxna waa Korinay, markuu Dhawaaqay mar hore, waana ajiibnay, waana ka Korinay isaga iyo Ehelkiisaba Murugadii Waynayd.'),
(2560, 21, 77, 'Waana uga Gargaaray Qoomkii Beeniyey Aayaadkanaga, waxayna ahaayeen Qoom Xun waana Maanshaynay Dhammaantood.'),
(2561, 21, 78, 'Xusuuso (Nabi) Daawuud iyo (Nabi) Suleymaan markay Xukumayeen Beerta markuu Daaqay Adhi Qoom (Dad) waxaana ahayn kuwa Xukunkooda u Maraga (Arka).'),
(2562, 21, 79, 'Waxaana Fahansiinay Sulaymaan, Dhammaanna waxaan Siinay Xukun iyo Cilmi, waxaana u Sakhirray Daawuud Buuraha iyagoo la Tasbiixsan iyo Shimbiraha, waana Fallaa «karraa».'),
(2563, 21, 80, 'Waxaana Barray Daawuud Samaynta Duruucda inay Idinka Dhawrto Dagaalkiina, ee ma Tihiin kuwo Mahadin.'),
(2564, 21, 81, 'Suleymaanna waxaan u Sakhirray Dabaysha iyadoo Daran oo ku Socota Amarkiisa Xaggii Dhulkii ahaa kaan Barakaynay Dhexdiisa, waxaana Nahay Kuwo wax walba Og.'),
(2565, 21, 82, 'Shaydaamadana, waxaan uga Sakhirray Kuwo u Dhumban (Badda), una Fala Camal aan kaas ahayn Anagaana Ilaalinna.'),
(2566, 21, 83, '(Xusuuso) Ayuubna markuu u Dhawaaqay Eebihiis (Isagoo Dhihi) Aniga waxaa I taabtay Dhib Adiguna waxaad Tahay Naxariis Badane.'),
(2567, 21, 84, 'Waana Ajiibnay Waxaana ka Faydnay waxa Haya oo Dhiba, waxaana Siinay Ehelkiisii iyo wax la mid ah Naxariis Xagganaga ah Darteed iyo Waanada Caalamka.'),
(2568, 21, 85, 'Xusuuso Ismaaciil, Idiriis, Dulkifli Dhammaan waxay ka mid ahaayeen kuwa Samra.'),
(2569, 21, 86, 'Waxaana Dhex galinay Naxariis tanada waxayna ka Mid ahaayeen kuwa Suuban.'),
(2570, 21, 87, 'Nabi Yuunus (Xusuuso) markuu Tagay isagoo Cadhaysan una Maleeyey Inaanaan Cidhiidhyayn korkiisa oo uu ku Dhawaaqay Mugdiyada Dhexdooda Ilaah aan Adi Ahayn ma Jiro Nasahnaantaada Anigu waxaan ka Mid ahaa kuwa Daalimiinta ah.'),
(2571, 21, 88, 'Waana Ajiibnay kana Korinay Murugtii, Saasaana u Korinaa Mu\'miniinta.'),
(2572, 21, 89, 'Zakariyana (Xusuuso) markuu u Dhawaaqay Eebihiis isagoo Ich Eebow hayga Tagin Anoo Kali ah Adaa u Khayrroon Cid wax dhaxashee «u hadhee».'),
(2573, 21, 90, 'Waana Ajiibnay waxaana Siinay Yaxye waxaana u Hagaajinay Haweennaydiisii, waxayna ahaayeen kuwo u Deg dega Khayaadka oo Na barya iyagoo Rajayn oo Cabsan waxayna ahaayeen kuwa Noo Khusuuca.'),
(2574, 21, 91, 'Tiina (Xusuuso) Dhawrtay Xubinteedaa waa (maryama) oon ku Afuufnay Dhexdeeda Ruuxdanadii kana yeellay iyada iyo Wiilkeeda Calaamadda Caalamka.'),
(2575, 21, 92, 'Tani waa Diintiinii waana Diin kaliya Aniguna waxaan Ahay Eebihiin ee I caabuda.'),
(2576, 21, 93, 'waxayse isku Khilaafeen Amarkoodii Dhexdooda Dhammaanna Xagaygaa loo soo Celin.'),
(2577, 21, 94, 'Ruuxii Fala Wanaag isagoo Mu\'min ah lagama Qariyo Camalkiisa Anguna waan u Qoraynaa.'),
(2578, 21, 95, 'Waxaa ka Reebban Magaalo aan Halaagnay inay soo Noqoto «Soo bixin ka hor».'),
(2579, 21, 96, 'Inta laga Furi soo (Dayn) Yaajuuj iyo Maajuuj iyaguna Jaho Sarreysa Dhammanteed yey ka soo Deg degi.'),
(2580, 21, 97, 'Waxaa Dhawaaday Yaboohii Xaqa Ahaa waxaana soo bixi inaytaagnaan Indhaha kuwa Gaaloobay (Iyagoo Dhihi) Halaaganagee waxaan kaga Sugnayn Halmaansho kan Waxaanse ahayn Daalimiin.'),
(2581, 21, 98, 'Idinka iyo waxaad Caabudaysaan oo Eebe ka Soo hadhay waxay Noqon Shidaalka Jahannamo idinkuna waad ku Arooraysaan.'),
(2582, 21, 99, 'Hadday Yihiin kuwaasu Ilaahyo kuma Arooreen, Dhamaanna way ku Waari.'),
(2583, 21, 100, 'waxay ku Yeelan Dhexdeeda Qaylo, waxna Dhexdeeda kuma Maqlaan.'),
(2584, 21, 101, 'kuway u Hor martay Xagganaga Wanaag kuwaasu Xaggeeda waa laga Fogayn.'),
(2585, 21, 102, 'mana Maqlaan Shanqadteeda iyaguna waxay Doonto Naftoodu yey ku Waari.'),
(2586, 21, 103, 'mana Walbahaariyo Argagaxii Waynaa, waxayna kula kulmi Malaa\'igtu kani waa Maalintiinnii ah Tii laydiin Yaboohay.'),
(2587, 21, 104, 'waana Maalintaan u Duubayno Samada Sida loo Duubo Warqadaha Kitaabka, Sidaan ku Billownay Abuuriddii hore Yaana ku soo Celinaynaa, waa Yabooh korkanaga ah, annagaana Falna saas.'),
(2588, 21, 105, 'Dhab ahaan waxaan ugu Qorray Zabuur Sheegid ka Dib (Luuxul Maxfuudka) Dhulka waxaa Dhaxli Addoomadayda Suuban.'),
(2589, 21, 106, 'kan Gaadhsiin baa ugu Sugan Ciddi Caabudi (Eebe).'),
(2590, 21, 107, 'kumana aanaan Dirin Naxariista Caalamka adoo ah Mooyee.'),
(2591, 21, 108, 'Waxaad Dhahdaa waxaa uun lay Waxyooday Aniga in Ilaaheennu Ilaah kaliya yahay ee Idinku Muslimiin ma Tihiin (Ma Hogaansameysaan).'),
(2592, 21, 109, 'Haddayse Jeedsadaan waxaad Dhahdaa waxaan idin Ogaysiiyey si Siman (Cad) Colnimo Mana Ogi inay Dhawdahay ama Fogtahay waxa laydiin Yaboohay.'),
(2593, 21, 110, 'Eebe wuxuu Ogyahay waxa lala Qaylyo oo Hadala wuxuuna Ogyahay waxaad Qarinaysaan.'),
(2594, 21, 111, 'Mana Ogi waxay u Dhawdahay inay Fidmo idiintahay iyo Raaxo Tan iyo Muddo.'),
(2595, 21, 112, 'Wuxuu yidhi (Nabigu) Eebow ku kala Xukun (Dhexdanada) Xaq, Eebahanana waa Raxmaan ee Looga Kaalmo waydiisto waxaad sheegaysaan.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'Dadaw ka Dhawrsada Eebihiin Maxaayeelay Gilgilashada Saacadda (Qiyaame) waa wax wayn (Daran).'),
(2597, 22, 2, 'Waana Maalintaad Arkaysaan Iyadooy Halmaami Mid wax Nuujisa Dhammaanteed waxay Nuujinaysay, ooy Dejin (Tuuri) mid Kastoo Uur leh Uurkeeda, waxaadna Arkaysaa Dadkoo Waalan, mana aha kuwo (dhab) u Waalan ee Cadaabka Eebaa Daran.'),
(2598, 22, 3, 'Dadka waxaa ka mid ah Ruux ku Murmi Eebe Cilmi la\'aan oo raaci Shaydaan kastoo Madax Adag.'),
(2599, 22, 4, 'Waxa loo Qoray (Shaydaan) Ruuxii Talada u Dhiibta (Raaca) inuu Baadiyeeyo kuna Toosiyo Cadaabka'),
(2600, 22, 5, 'Dadow haddaad Tihiin kuwo Shakisan oo Bixinta Annagaa idinka Abuurray Carro, Hadaana Dhibic Haddana Calaqo, Hadaana Goosin la Abuuray iyo Midaan la Abuurin si aan idiinku Caddayno (Qudrada Eebe) waxaana ku sugnaa Makaanka waxaan Doono Tan iyo Muddo Magacaaban, markaasaan idin soo Bixinaynaa Idinkoo Yar, markaas inaad Gaadhaan Xooggiinna: waxaa idinka mid ah Cid la oofsan (Dhiman) waxaana idinka mid ah kuwo loo celin Cimri Liita (Gabow) si ayna u Garanin intay wax Yaqiineen ka dib waxba, waxaad Arkaysaa Dhuloo Abaar (Dhimaad) ah markaan ku soo Dejinno Korkeeda Biyana way Gilgilataa wayna Kortaa, waxayna soo Bixisaa Nooc kastoo Quruxsan.'),
(2601, 22, 6, 'Tassna waxaa ugu Wacan in Eebe uun Xaq yahay uuna Nooleeyo waxa Dhintay iyo inuu wax walba Karo.'),
(2602, 22, 7, 'Iyo in Saacadda Qiyaame iman Shaki la\'aan, Eebana wuxuu soo Bixin waxa Qubuuraha ku Sugan.'),
(2603, 22, 8, 'Dadka waxaa ka mid ah mid ku Dloodi Eebe Cilmi La\'aan iyo Hanuun La\'aan iyo Kitaab wax Nuurin la\'aantiis.'),
(2604, 22, 9, 'oo Dadbi Luqunta (Iskibrin) inuu ka Dhumiyo Jidka Eebe, wuxuu Mudan Adduunyada Dulli waxaana Dhadhansiin Maalinta Qiyaame Cadaab wax Guba.'),
(2605, 22, 10, 'Taasna waa waxay Ilor marsatay Gacmahaagu, Eebana ma aha Dulmiyaha Addoomada.'),
(2606, 22, 11, 'Waxaa ka mid ah Dadka mid u Caabudi (Eebe) Dhinac (Shaki) Hadduu soo Gaadho Khayra Xasili, Hadduu soo Gaadho Imtixaan (Dhibna) u Gabdooma Wajigiisa (Gaalooba) wuxuu Khasaariyey Adduun iyo Aakhiraba Kaasina waa Khasaaraha Cad uun.'),
(2607, 22, 12, 'Wuxuu Caabudi (Baryina) Eebe ka Sokow waxaan Dhibayn Anfacaynna Kaasina waa uun Baadida Fog.'),
(2608, 22, 13, 'Wuxuu Caabudi wax Dhibkiisu ka Dhawyahay Naefigiisu, isagaa u Xun Sokeeye una Xun la noolaadi.'),
(2609, 22, 14, 'Ilaahay wuxuu Galiyaa kuwa Rumeeyey (Xaqa) oo Falay Camal Fiican Jannooyin ay Socoto Dhexdeeda Wabiyaal, Eebana wuxuu Falaa wuxuu Doono.'),
(2610, 22, 15, 'Ciddii u Malayn Inuusan u Gargaarayn Eebe Nabigiisa Adduun iyo Aakhiraba ha u Fidiyo Xadhig Xagga Samada hana isku Gooyo (Marjiyo) hana dayo inay Dhagartiisu Tegiiso waxa Cadho Galin.'),
(2611, 22, 16, 'Saasaana kuugu soo Dejinay Aayaad Cad Eebana wuxuu Hanuuniyaa Cidduu Doono.'),
(2612, 22, 17, 'Kuwa Rumeeyey (Xaqa) iyo Kuwa Yuhuudoobay iyo Saabi\'inta (Xiddigaha Caabuda) iyo Nasaarada iyo Majuusiga iyo Mushrikiinta Eebaa kala bixin Dhexdooda Maalinta Qiyaame, Eebana wax kasta wuu Arkaa.'),
(2613, 22, 18, 'Miyaadan Arkayn inay Eebe u Sujuudi waxa Samooyinka iyo Dhulka iyo Qorraxda iyo Dayaxa iyo Xidigaha iyo Buuraha iyo Geedaha iyo Xayawaanka iyo Waxbadan oo Dadka ah, wax badanna waxaa ku sugnaaday Korkooda Cadaab, Ruuxuu Ihaaneeyo Eebana ma laha wax Sharrifi, Eebana wuxuu Falaa wuxuu Doono.'),
(2614, 22, 19, 'Labadani waa Doodayaal, waxayna ku Doodeen Eebahood, kuwa Gaaloobay waxaa loo Goyn Maryo Naar ah waxaana Lagu Shubi Korkooda Biyo Kulul.'),
(2615, 22, 20, 'Waxaana lagu Gubaa (Lagu Dhalaaliyaa) waxa Caloosha ku Jira iyo Jidhka (Haraga).'),
(2616, 22, 21, 'waxay Mudan Budhadh Bir ah.'),
(2617, 22, 22, 'Markastooy Doonaan inay ka Baxaan Xaggeeda Walbahaar Dartiis waa lagu Celiyaa Dhexdeeda (waxaana la Dhahaa) Dhadhamiya Cadaabka Gubidda.'),
(2618, 22, 23, 'Eebana wuxuu Galiya kuwa Rumeeyey (Xaqa) oo Fala Camal Fiican Jannooyin ay Dureeri Dhexdeeda Wabiyaal, waxaana, Loogu Xidhaa Dhexdeeda Jinjimo Dahab ah iyo Jawhar, Dharkooduna Dhexdeeda waa Xariir.'),
(2619, 22, 24, 'Waxaana lagu Hanuuniyey Wanaagga Hadalka waxaana lagu Hanuuniyey Jidka la Mahdiyo/'),
(2620, 22, 25, 'kuwa Gaaloobay oo ka Celiya Jidka Eebe iyo Masaajidka Xurmaysan ee looga Yeelay Dadka Sinnaan mid ku Nagi iyo mid Meel ka yimidba cidii la Doonta Dhexdiisa Leexin Dulmi ah (Xumaan) waxaanni Dhadhansiin Cadaab Daran.'),
(2621, 22, 26, '(Xusuuso) markaan u Muujinay Nabi Ibraahim Meeja Baytja (Kacbada) Annagoo ku Dhihi Ha la Wadaajin Eebe (Cibaadada) waxba, u Daahirina Gurigayga kuwa Dhawaafi iyo kuwa Taagan iyo kuwa ku Rukuucsan ee Sujuudsan.'),
(2622, 22, 27, 'Ogaysiina Dadka Xajka ha kuu Yimaaddeen iyagoo Lugayn iyo mid Bog Dheer Dhammaanteed oo ka iman Jaha kasta oo Dheer.'),
(2623, 22, 28, 'inay Joogaan (u yimaadaan) Nacfigooda Dartiis, oy xusaan Magaca Eebe Maalmo la Yaqaan, waxa Eebe ku Arzaaqay oo ah Xayawaanka nicmoolayda, ca Cuna Xaggeeda Quudiyana kan Dhibaataysan ee Faqiirka ah.'),
(2624, 22, 29, 'markaas ha Dhammeeyeen hasaastooda, hana Oofiyeen nadorkooda, hana Dhawaafeen Baydka Horeeyey.'),
(2625, 22, 30, 'Waa Saas, Ruuxii Wayneeya wuxuu Reebay Eebe Saasaa u Khayrroon Eebihiis Agtiisa, waxaana laydiin banneeyey Nicmoolayda waxa Laydinku akhriyo Mooyee, ee ka Dheeraada Xumaanta ah Sanamyada kana Dheeraada Hadalka Beenta ah.'),
(2626, 22, 31, 'Idinkoo u Toosan Eebe oon Mushrikiin ahayn, Ruuxii u Shariig yeela Eebe wuxuu la mid yahay mid ka Soo Dhacay Samada oy Dafi Shimbirtu ama ay ku Tuuri Dabaysho Meel Dheer oo halaag ah.'),
(2627, 22, 32, 'waa Saas Ruuxii wayneeya Amarada Eebe (Sharcigiisa) taasi waa dhawrsashada Quluubta.'),
(2628, 22, 33, 'waxaa idiinku Sugnaaday (hadiyada) Nacfi Tan iyo Muddo Markaas. waxay ku Dhammaan Baydka Horeeyey.'),
(2629, 22, 34, 'Ummad kasta waxaan u yeellay Cibaado (Ciid) inay ku Xusaan Eebe wuxuu ku Arzaaqay oo Xayawaanka Nicmoolayda ah, Ilaahiinna waa Ilaah kaliya ee u Hogaansama una Bishaaree, kuwa Xasilloon (ee Cabsada) ee ah.'),
(2630, 22, 35, 'Kuwa marka la Xuso Eebe ay Cabsato Quluubtoodu, kuna Samra waxa ku Dhaca ee Ooga Salaadda, waxaan ku Arzaaqnayna wax ka Bixiya.'),
(2631, 22, 36, 'Loogga (Hadyada) waxaan idiinka Yeellay Sharciga Calaamooyinka Eebe waxaana idiinku Sugan Dhexdeeda Khayr ee ku Xusa Magaca Eebe Korkeeda iyadoo Jilib Xidhan, markay U dhacdo Dhinacna wax ka Cuna, kana Quudiya kan Dhawrsoon iyo kan Warsanba, Saasaana idiinka Fududaynaa, inaad Mahdisaan.'),
(2632, 22, 37, 'Ma gaadho Eebe Hilibkeedu iyo Dhiiggeedu waxaase Gaadha ka Dhawrsoonidiinna, Saasaana Eebe idiinku Fududeeyaa inaad ku Waynaysaan wuxuu idinku Hanuuniyey una Bishaaree kuwa Wanaagga Fala.'),
(2633, 22, 38, 'Eebana wuu Daafacaa kuwa Rumeeyey (Xaqa) Eebana ma Jecla mid kastoo Khayaamo Badan oo Gaalimo Badan.'),
(2634, 22, 39, 'waxaa loo Idmay kuwa lala Dagaallami (inay Dagaalamaan) maxaayeelay waa la Dulmiyey Eebena Gargaarkooda wuu Karaa.'),
(2635, 22, 40, 'waana kuwa laga Bixiyey Guryahoodii xaq Darro iyo Inay Dheheen Eebahanno waa Ilaahay, haddayna Jirin ku Daficidda Eebe Dadka Qaarkood Qaar waxaa la Dumin lahaa Macaabidda Yuhuud, iyo Biyac Kaniisadaha, iyo Kaniisadda Dad kale, iyo Masaajidda lagu Xuso Dhexdeeda magaca Eebe wax badan, wuuna u Gargaari Eebe Cidii u Gargaarta (Diintiisa) Eebana waa Xoog Badane Adkaada.'),
(2636, 22, 41, 'waana kuwa Haddaan Makanino Dhulka Ooga Salaadda oo Bixiya Zakada oo Fara Wanaagga oo ka Reeba Xumaanta, Eebaana iskale Cidhibta Arrimaha.'),
(2637, 22, 42, 'Hadday ku Beeniyaan waxaa Beeniyey Ilortood Qoomkii (Nabi) Nuux iyo Caad iyo Thamuud.'),
(2638, 22, 43, 'iyo Qoomkii (Nabi) Ibraahim iyo Qoomkii (Nabi) Luudh.'),
(2639, 22, 44, 'iyo Ehelkii Madyan, waana la Beeniyey (Nabi) Muuse, waana Sugay Gaalada markaasaan Qabtay ee see noqday Ciqaabkii.'),
(2640, 22, 45, 'maxay Badantahay Magaalo aan Halaagnay iyadoo Gardaran iyaduna u Dhacday Darbiyadeedii iyo Ceel la Halleeyey iyo Daar wayn (markii la Halaagay Dadkii).'),
(2641, 22, 46, 'Miyeyna ku Soconin Dhulka oy u Ahaato Quluub ay wax ku Kasaan ama Dhago oy wax ku Maqlaan, Indha Beelka Dhabta ahna waa Indho Beelka Quluubta ku Sugan Laabta.'),
(2642, 22, 47, 'waxay Dedejisan Cadaab mana Baajinaayo Eebe Yaboohiisa, Maalin Eebe Agtiisana waxay la mid tahay kun Sano oo waxaad Tirinaysaan.'),
(2643, 22, 48, 'Badanaa Magaalo aan u Sugnay iyadoo Daalimad ah, Markaas aan Qabtay Xaggayga Umbaana loo Ahaan.'),
(2644, 22, 49, 'waxaad Dhahdaa Dadow waxaan uun Anigu idiin ahay Dige Cad.'),
(2645, 22, 50, 'kuwa Rumeeyey (Xaqa) oo falay Camal Fiican waxay mudan Dambi Dhaaf iyo Rizqi Sharaf leh (Janno).'),
(2646, 22, 51, 'kuwa u Socda (Xumaanta) Aayaadkanaga iyo (isku dayi) inay na daaliyaan kuwaasu waa Ehelka Jaxiimo.'),
(2647, 22, 52, 'Rasuulkastoon Dirray ama Nabi Hortaa, markuu wax Akhrin waxbuu ku Tuuraa Shaydaanku Akhriskiisa, markaasuu Baabi\'yaa Eebe wuxuu Tuuri Shaydaanku wuxuuna Sugaa Aayaadkiisa, Eebana waa Ogaade Falsan.'),
(2648, 22, 53, 'si uu uga yeelo waxa Shaydaanku Tuuri Fidmo kuwa Quluubtoodu Bugto ee ka Ingagan Quluubta Daalimiintuna waxay ku Suganyihiin Khilaaf (Baadi) Fog.'),
(2649, 22, 54, 'inay Ogaadaan kuwa Cilmiga la siiyey «Mu\'miniintu» innu yahay Xaq Eebe ka yimid (Quraanka) oy Rumeeyaan, oy u Khushuucdo Dartiis Quluubtoodu Eebana wuu ku hanuunin kuwa Rumeeyey (xaqa) Jid Toosan.'),
(2650, 22, 55, 'kana tagaan kuwa gaaloobay Shaki intay ugu Timaaddo Saacaddu Kado ama u yimaaddo cadaab Maalin daran.'),
(2651, 22, 56, 'Xukunka Maalintaas Eebaa iska leh, wuuna kala Xukmin Dhexdooda, kuwa Rumeeyey (Xaqa) oo falay Camal Fiican waxay Gali Janada Naciimo.'),
(2652, 22, 57, 'kuwa Gaaloobay oo beeniyey Aayaadkanagana kuwaas waxaa u Sugnaaday Cadaab wax Dulleeya.'),
(2653, 22, 58, 'kuwa ku Hijrooday Jidka Eebe Haddana la Dilo ama dhinta wuxuu ku Arzaaqi kuwaas Eebe Rizqi Wanaagsan, Eebana waa ku Khayrroon yahay Rizqigiisu.'),
(2654, 22, 59, 'wuxuuna Galin Meel ay ka Raalli Noqon (Janno) Eebana waa Oge Dhulbadan.'),
(2655, 22, 60, 'waa saas, Ruuxii Ciqaaba inta la Ciqaabay oo kale Markaas la gardaraysto wuu u Gargaari Eebe, Eebana waa Cafiye Dhaafa.'),
(2656, 22, 61, 'waxaana ugu wacan in Eebe Galiyo Habeenka Maalinta Galiyana Maalinta Habeenka, Eebana waa Maqle Arka.'),
(2657, 22, 62, 'waxana ugu Wacan Taas in Eebe Xaq (Sugan) yahay, waxay Caabudi oo ka soo Hadhayna Baadil yahay, Eebana yahay Sarreeye Weyn.'),
(2658, 22, 63, 'Miyeydaan Ogayn in Eebe ka soo Dejiyo Samada Biyo oo ahaado Dhulku Cagaar, Eebana waa Oge Xeel Dheer.'),
(2659, 22, 64, 'Waxaa u Sugnaaday Eebe waxa Samaawaadka ku Sugan iyo waxa Dhulku ku Suga, Eebana waa Hodan Mahadsan.'),
(2660, 22, 65, 'Miyeydaan ogayn in Eebe idiin Sakhiray waxa ku sugan Dhulka iyo Markabka Socda Badda Dhexdeeda Amarka Eebe, wuxuuna ka Hayaa Samada inay ku dhacdo Dhulka Idinka Eebe mooyee, Eebana Dadka waa u Naxariis badane.'),
(2661, 22, 66, 'Eebana waa kan idin Nooleeya, Haddana idin Dila haddana idin Nooleeya Dadkuna waa Diidmo badane (Nicmada Eebe).'),
(2662, 22, 67, 'Umaad kasta waxaan u yeellay Hab Cibaado oy ku Cibaadaysan ee yeyna kugula Doodin Amarka (Xaqa) uguna yeedh Xagga Eebahaa Adigu waxaad ku Sugantahay Hanuun Toosane.'),
(2663, 22, 68, 'Hadday kula Doodaan waxaad dhahdaa Eebaa Og waxaad Falaysaan.'),
(2664, 22, 69, 'Eebana wuu kala Xugmin Dhexdooda Maalinta Qiyaame waxay isku Khilaafsanaayeen.'),
(2665, 22, 70, 'Miyaadan Ogayn in Eebe Ogyahay waxa ku Sugan Samada iyo Dhulka, Arrintaasna waxay ku Sugantahay Kitaab, Arrintaasuna Eebe waa u Fududahay.'),
(2666, 22, 71, 'Waxay Caabudi Eebe ka sokow Wuxuusan u soo Dejini Xujo oyna u Lahayn cilmi, umana Sugna Daalimiinta Gargaare.'),
(2667, 22, 72, 'Marka lagu Akhriyo korkooda Aayaadkanaga oo Cad cad waxaad ka Garataa Wajiyada kuwa Gaaloobay Diido, waxayna u Dhawyihiin inay ku Boodaan (Dhibaan) kuwa ku Akhrin Aayaadkanaga, waxaad Dhahdaa Maydiinka Warramaa wax ka Shar badan Taas, waa Naar u Darbay Eebe kuwa Gaaloobay, Iyadaana u Xun Meel Loo Ahaado.'),
(2668, 22, 73, 'Dadow waxaa layeelay Tusaale ee Dhagaysta, kuwaad Baryeysaan (Caabudaysaan) Eebe ka Sokow ma Abuuraan Daqsi haba u Kulmeene, Hadduu ka Qaato Daqsi waxna kama Ceshadaan waxaa Tabar yar kan wax Dooni iyo kan la Dooni.'),
(2669, 22, 74, 'Mayna Waynayn Eebe Mudnida Weynidiisa, Eebana waa Xoog Badane Adkaada.'),
(2670, 22, 75, 'Eebe wuxuu ka Doortaa Malaa\'igta Rasuullo Dadkana (Rasuullo) Eebana waa Maqle Arka.'),
(2671, 22, 76, 'Wuxuuna Ogyahay waxa Hortooda ah iyo waxa gadaashooda ah Zagga Eebaana loo Celin Umuuraha.'),
(2672, 22, 77, 'kuwa (Xaqa) Rumeeyow Rukuuca oo Sujuuda oo Caabuda Eebihiin Falana Khayrja waxaad u Dhawdihiin inaad Liibaantaane.'),
(2673, 22, 78, 'kuna Dadaala dar Eebe Dadaal Dhab ah, Isagaana idin Doortay, mana yeelin Diinta Dhib, waa Milladdii Aabihiin Ibraahim, isagaana (Eebe) Idiinku Magacaabay Muslimiin horay iyo Quraankan si Rasuulku marag korkiina ugu Noqdo, idinkuna aad Marag u ahaataan Dadka, ee Ooga Salaadda oo Bixiya Xakada oo Qabsada Eebe isagaa Gargaarihiina ah isagaana u Fii can Sayid isagaana u Fiican Gargaare.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'Waxaa Dhab ahaan u Liibaanay Mu\'miniinta.'),
(2675, 23, 2, 'Ah kuwa Salaaddooda ku Khushuuca.'),
(2676, 23, 3, 'Oo ah kuwa Hadalka Macno Darro ka Jeedsada.'),
(2677, 23, 4, 'Oo ah kuwa Fala Zakada «bixiya».'),
(2678, 23, 5, 'Oo ah kuwa Furuujtooda Ilaaliya.'),
(2679, 23, 6, 'Haweenkooda iyo waxay Hanatay Gacantoodu mooyee, lagurna Dagaalo Taas.'),
(2680, 23, 7, 'Ruuxiise Doona waxaad wax ka Soo Hadhay kuwaasu waa Xad Gudbayaal.'),
(2681, 23, 8, 'Waa kuwa Ammaanadooda iyo Ballankooda Dhawra.'),
(2682, 23, 9, 'waana kuwa Salaadda Ilaaliya.'),
(2683, 23, 10, 'Kuwaasu waa kuwa Dhaxli.'),
(2684, 23, 11, 'oo ah kuwa Dhaxli Firdowsa wayna ku Dhexwaari.'),
(2685, 23, 12, 'Waxaan ka Abuuray Dadka wax laga soo Soocay Dhoobo.'),
(2686, 23, 13, 'Markaasan ka yeellay Dhibic ku Jirta meel Sugan.'),
(2687, 23, 14, 'Markaasaan ka Abuuray Dhibicdii Calaqo kana Abuuray Calaqdii Goosin, kana Abuuray Goosintii Lafo oon Huwinnay Lafihii Hilib Markaasaan ka ahaysinnay Khalqi kale, waxaa Khayr Badnaday Eebaha Abuurkiisu Fiicanyahay.'),
(2688, 23, 15, 'Markaas Idinku Arrintaas ka Dib waad Dhimmaysaan.'),
(2689, 23, 16, 'Markaas Maalinta Qiyaame Laydin soo Bixin.'),
(2690, 23, 17, 'Waxaana ku Abuuray Korkiinna Toddobo Samo mana nihin mid Khalqiga Halmaansan.'),
(2691, 23, 18, 'Waxaan ka soo Dejinay Samada Biyo Qiyaasan, waxaana Dejinay dhulka inaan la Tagnana waan karraa.'),
(2692, 23, 19, 'Waxaana idinku Ahaysiinnay Beero Timir ah iyo Cinabyo oo Dhexdeed Khudaar Badani ku sugantahay ood wax ka Cuntaan.'),
(2693, 23, 20, 'Iyo Geed ka Baxa Dhuur Siina oo soo saarta Subag iyo Koosaar kuwa Cuni.'),
(2694, 23, 21, 'Waxaa idiinku Sugnaaday Xoolaha Waano, waxaana idinka Waraabinnaa waxa Caloosheeda ku Sugan waxaana idinku Sugan Manfaco badan, Xaggeedaadna wax ka Cuntaan.'),
(2695, 23, 22, 'Korkeeda iyo Markabka Korkiisa yaana laydinku Xambaaraa.'),
(2696, 23, 23, 'Waxaan u Dirray (Nabi) Nuux Qoomkiisii wuxuuna Yidhi Qoomkayow Caabuda Eebe idinma sugnaanin Ilaah ka soo Hadhaye, miyeydaan dhawrsanayn.'),
(2697, 23, 24, 'Waxayna Dheheen Madaxdii Gaalowday oo Qoomkiisa ahaa kanu ma aha waxaan Dad idin la midah ahayn, wuxuuna doonayaa inuu idinka Fadilmo, Hadduu Doono Eebe wuxuu soo Dejinlahaa Malaa\'ig, manaan ka Maqlin Arrintan Aabayaalkanagii Horeeyey.'),
(2698, 23, 25, 'Ma aha waxaan Nin Waalan ahayn ee Suga Tan iyo Muddo, Geerida.'),
(2699, 23, 26, 'Wuxuu yidhi Eebow iiga Gargaar Beenintooda.'),
(2700, 23, 27, 'Waxaana u waxyoonay ku Samee Doon Ilaalintanada iyo Waxyiganaga, markuu yimaado Amarkanagu oo Burqado Tanuurku, Gali Doonta Nooc kastaba Labo Labo iyo Ehelkaaga Ciddii Hadal u Hor maray (Halaag) mooyee oo ka mid ah Haygulana Hadlin (Xaalka) kuwa Dulmiga falay iyaga waa la Maanshayne.'),
(2701, 23, 28, 'Markaad Korto Adiga iyo inta kula Jirta Doonta waxaad Dhahdaan Mahad Eebaa iska leh ah kan iga koriyey Qoomka Daalimiinta ah.'),
(2702, 23, 29, 'Waxaadna Dhahdaa Eebow I daji Guri la Barakeeyey Adaa u Khayrroon Dejiyee.'),
(2703, 23, 30, 'Arrintaasna waxaa ku Sugan Aayaad waxaana nahay kuwo wax Imtixaama.'),
(2704, 23, 31, 'Markaasaan ahaysiinay Gadaashood Qarni kale.'),
(2705, 23, 32, 'Waxaana u Dirray Dhexdooda Rasuul ka mid ah Caabuda Eebe idiinma sugna Ilaah Eebe ka soo Hadhay ee Miyeydaan Dhawrsanayn.'),
(2706, 23, 33, 'waxay Dheheen Madaxdii Qoomkiisa ee ah kuwii Gaaloobay oo Beeniyey la Kulanka Aakhiro oon ugu Raaxaynay Nolosha Dhaw, kani ma aha waxaan Dad idinla mid ahayn Cunina waxaad Cunaysaan, Cabbina waxaad Cabbaysaan.'),
(2707, 23, 34, 'Haddaad Adeecdaan Dad idila mid ah idinku waa Khasaarteen.'),
(2708, 23, 35, 'ma wuxuu idiin Yaboohi Markaad Dhimataan ood Noqotaan Carro iyo Lafo in laydin soo Boxin.'),
(2709, 23, 36, 'Waa Fogtahay oo Fogtahay waxa laydinku Goodin.'),
(2710, 23, 37, 'Ma aha waxaan Noloshanada Adduunyo ahayn, waanu Dhiman (Qaar) oonnu Noolaan (Qaar) kuwa la soo Bixinna ma Nihin.'),
(2711, 23, 38, 'Mana aha waxaan Nin ku Been Abuurtay Eebe ahayn, mana nihin kuwo u Rumeyn.'),
(2712, 23, 39, 'Wuxuuna yidhi Eebow iiga Gargaar Beenintooda.'),
(2713, 23, 40, 'Wuxuu yidhi Eebe Mudda Yar ka Dib yey ahaan kuwo Nidaamooda.'),
(2714, 23, 41, 'Waxaana ku Qabatay si Xaq ah Qaylo waxaana ka yeellay Daadxoor ee Fagaansho Haw Sugnaado Daaliminta.'),
(2715, 23, 42, 'Markaasaan Ahaysiinay Gadaashood Quruumo kale.'),
(2716, 23, 43, 'Kamana hor marto Ummadi Ajasheeda (Waqtigeeda) kamana Dib marto.'),
(2717, 23, 44, 'Markaasaan Diray Rasuullada nadii oo Israacsan markastooy Ummad u Yimaaddoo Rasuulkoodu waybeeniyaan, waxaana Raacsiinay Qarkood Qaar (Halaag) waxaana ka yeelay Sheeko Fogaanshaana u Sugnaaday Qoomaan (Xaqa) Rumeyn.'),
(2718, 23, 45, 'Markaasaan la Diray (Nabi) Muuse iyo Walaalkiis Haaruun Aayadkanagii iyo Xujo Cad.'),
(2719, 23, 46, 'Xagga Fircoon iyo Jameecadiisii (yaana u dirray) wayna is kibriyeen waxayna ahaayeen Qoom is korraysiiya.'),
(2720, 23, 47, 'waxayna dheheen ma waxaanu u rumayn Labo ruux oo nala mid ah Qoomkooduna anaga na Caabudo.'),
(2721, 23, 48, 'Wayna Beeniyeen waxayna Noqdeen kuwo la Halaagay.'),
(2722, 23, 49, 'Waxaana Siinay (Nabi) Muuse Kitaabka inay Hanuunaan.'),
(2723, 23, 50, 'Waxaana ka yeellay ina Maryamna iyo Hooyadiis Aayad waxaana u Dunnay Meel sare oo Sugnaansho iyo Biyo Socda leh.'),
(2724, 23, 51, 'Rasuullow ka Cuna Xalaasha, Falana Wanaag, Anigu waxaad Falaysan waan Ogahaye.'),
(2725, 23, 52, 'Tanna waa Umaddiinii (Diinta) umad (Diin) kaliya anna waxaan ahay Eebihiin ee iga Dhawrsada.'),
(2726, 23, 53, 'Waxayse y kala Goosteen Amarkooda Dhexdooda Kooxo, Xisbi kastana waxa Agtooda ah yey ku Faraxsanyihiin.'),
(2727, 23, 54, 'ee Isagaga Tag Halmaanshahooda Dhexdiisa Tan iyo Muddo.'),
(2728, 23, 55, 'ma waxay u Malayn waxaannu Siin oo Xoolo iyo Caruur ah.'),
(2729, 23, 56, 'Inaanu u soo Hormarinay Khayraad wax ma Oga.'),
(2730, 23, 57, 'kuwa uun Ka Cabsada Eebahood ka Yaabid.'),
(2731, 23, 58, 'kuwa uun Aayaadka Eebahood Rumeeya.'),
(2732, 23, 59, 'kuwa uun Eebahood aan la Wadaajinin.'),
(2733, 23, 60, 'kuwa Bixin waxa la Siiyay Iyadooy ka Cabsan Quluubtoodu inay Xagga Eebe u Noqon.'),
(2734, 23, 61, 'Kuwaasaa u deg degi Khayraadka una Hormarinay.'),
(2735, 23, 62, 'kuma Mashaqayno Naf waxayna karin, Agtanadana waxaa ah Kitaab ku Hadli Xaq, lamana Dulmiyo Iyagana.'),
(2736, 23, 63, 'waxayse kaga Sugantahay Quluubtoodu Halmaansho Xagga Kaas, waxayna Leeyihiin Camallo Kaas ka Duwan (oo Xun) oy iyagu u Camal Fali.'),
(2737, 23, 64, 'Markaanse ku Qabanno Kuwooda Nicmaysan Cadaab Markaasay Qaylyaan.'),
(2738, 23, 65, '(Waxaana la Dhihi) ha Qaylyina Maanta Idinku Xagganaga Laydiin kama Gargaaree.'),
(2739, 23, 66, 'Waxay ahayd Aayaadkeygii Tii lagu Akhriyo Korkiinna waxaadse ahaydeen kuwa Cidhibtiinna Dib ugu noqda (Xaqa Diida).'),
(2740, 23, 67, 'Idinkoo isku Kibrin Quraanka (ama Kacbada iyo Nabiga) Cawaysimina idinkoo Hadal Xun.'),
(2741, 23, 68, 'Miyeyna Fiirinin Quraanka, mise waxaa u Yimid Waxaan U imaanin Aabayaalkoodii hore.'),
(2742, 23, 69, 'Mise waxayna Aqoonin Rasuulkooda oo ay Diidi.'),
(2743, 23, 70, 'Mise waxay Dhihi waa Waalanyahay, wuxuuse Ula Yimid Xaq Badankoodse Xaqay Nici.'),
(2744, 23, 71, 'Hadduu Raaco Xaqu Hawadooda waxaa Fasaadi Lahaa Samooyinka iyo Dhulka iyo waxa u Dhaxeeya, waxaanse Siinay Quraanka wayse ka Jeedsan quraanka.'),
(2745, 23, 72, 'Mise waxaad warsan Kharaj (u Juuro) Ajirka Eebahaabaa Khayr Roon, waana kan u Khayr Badan Arzaaqe.'),
(2746, 23, 73, 'Adiguna waxaad ugu Yeedhi Jid Toosan.'),
(2747, 23, 74, 'Kuwa aan Rumeyn Aakhiroona Jidka way ka lilan.'),
(2748, 23, 75, 'Haddaan u Naxariisanno oon ka Faydno waxa haya oo Dhib ah waxay ku Madax Taagi Xumaantooda iyagoo ku Indho la\'.'),
(2749, 23, 76, 'Waxaannu ku Qabanay Cadaab umana ayan Hogaansamin «Dulloobin» Eebahood umana Khushuucin.'),
(2750, 23, 77, 'Markaan ku Furro Korkooda Cadaab Daran markaasay Quustaan.'),
(2751, 23, 78, 'Eebe waa kan idiin Yeelay Maqalka, iyo Araga iyo Quluubta wax Yar baadna ku Shugriyeysaan (Eebe).'),
(2752, 23, 79, 'Eebe waa Kan idinka Abuuray Dhulka Xagiisaana laydiin soo Celina.'),
(2753, 23, 80, 'Eebe waa Kan wax Nooleeya, waxna Dila wuxuuna Leeyahay Iskhilaafka (Kala Duwaanta) Habeenka iyo Maalinta ee Miyeydaan Wax kasayn.'),
(2754, 23, 81, 'Waxayse Dheheen wax la mid ah waxay Dhaheen kuwii hore (Gaaladii).'),
(2755, 23, 82, 'Waxay Dheheen Ma Markaan Dhimanno oon Noqonno Carro iyo Lafo Miyaa Anaga nala soo Bixin.'),
(2756, 23, 83, 'Waa Naloo Yaboohay Aabayaalkanno kan (Soo Bixinta) horay mana aha kani waxaaan warkii hore ahayn.'),
(2757, 23, 84, 'Waxaad Dhahdaa yaa leh Dhulka iyo waxa ku sugan haddaad wax Ogtihiin.'),
(2758, 23, 85, 'Waxay Odhan Eebe, waxaad Dhahdaa Miyeydaan wax Xusuusanayn.'),
(2759, 23, 86, 'Dheh waayo Eebaha Samooyinka Todobada ah iyo Eebaha Carshiga Wayn.'),
(2760, 23, 87, 'Waxay Odhan Eebe, Dheh Miyeydaan Dhawrsanayn.'),
(2761, 23, 88, 'Dheh Yey yaddiisa ku Jirtaa Xukunka wax kasta, oo wax Magan Galiya, isna aan wax laga Magan galinayn, Haddaad wax Ogtihiin.'),
(2762, 23, 89, 'Waxay Odhan Eebe, Dheh Xaggee Laydiin Sixri.'),
(2763, 23, 90, 'Waxaanse Siinay Xaq waana Beenaalayaal.'),
(2764, 23, 91, 'Ma yeelan Eebe Ilmo, mana aha Miciisa Ilaah (kale) Illeen markaas wuu la Tagi lahaa Ilaah walba wuxuu Abuuray, wuxuuna ka Kormari lahaa (Adkaan lahaa) Qaarkood Qaar, waxaa ka Nasahan Eebe waxay ku Tilmaami.'),
(2765, 23, 92, 'Waa Ogaha waxa Maqan iyo waxa Jooga, wuuna ka Sarreeyaa waxa la Wadaajin.'),
(2766, 23, 93, 'Waxaad Dhahdaa Eebow Hadaad i Tusiso waxa Looyaboohay.'),
(2767, 23, 94, 'Eebow hayga Yeelin Qoomka Daalimiinta ah.'),
(2768, 23, 95, 'Annaguna inaan ku Tusinno waxaan u Yaboohnay waan Karraa.'),
(2769, 23, 96, 'ee Raaci (Dhaafso) Tan Wanaagsan Midda Xun Anagaa og Waxay Tilmaamiye (Sheegi).'),
(2770, 23, 97, 'Waxaadna Dhahdaa Eebow waxaan kaa Magangalay Waswaaska Shayaadiinta.'),
(2771, 23, 98, 'Waxaana kaa Magan Gali Eebow inay ii Yimaadaan Shayaadiintu.'),
(2772, 23, 99, 'Markuu u Yimaaddo Midkood (Gaalada) Mawdku wuxuu Dhihi Eebow I celiya.'),
(2773, 23, 100, 'Waxaan u Dhawahay inaan Falo Wanaage waxaan ka Tagay, saas ma aha ee waa Hadal uu iska Yidhi Gadaashoodana waxaa ah Barzakh, tan iyo Maalinta la Soo Bixin.'),
(2774, 23, 101, 'Marka la Afuufo Suurka ma Jiro Nasab Dhexdooda Maalintaas, waxna isma Warsadaan.'),
(2775, 23, 102, 'Ciddayse Cuslaato Miisaankiisu (Wanaagga) kuwaasi waa kuwa uun Liibaanay.'),
(2776, 23, 103, 'Ciddayse Fududaato Miisaankiisu kuwaasi waa kuwa Khasaariyey Naftooda Jahannamayna ku Waari.'),
(2777, 23, 104, 'Waxaana Gubi Wajigooda Naarta iyagoo Waji diiran.'),
(2778, 23, 105, '(Waxaa lagu Dhihi) Miyeyna ahayn Aayaadkanagu kuwa lagu Akhriyo Korkiinna ood Beenin Jirteen.'),
(2779, 23, 106, 'Waxay Dhahaan Eebow waxaa naga Adkaatay Xumaantanadii, waxaana ahayn Qaar Dhunsan.'),
(2780, 23, 107, 'Eebow naga Bixi Xageeda Haddaan u Noqonana Anagaa Daalimiin ah.'),
(2781, 23, 108, 'Wuxuu Dhahaa Eebe ku Fogaada Dhexdeeda Idinkoo (Dullaysan) hana Ila Hadlina.'),
(2782, 23, 109, 'Waxaa Jirtay in Koox ka mid ah Addoomadayda ay Dhahaan Eebow waan Rumaynay Xaqa ee noo Dhaaf noona Naxariiso Adaa Naxariiste u Khayr Roone.'),
(2783, 23, 110, 'Waxaadse ka Yeelateen Jees jees intay idinka Halmaansiiyaan xuskayga ood kuna Qoslayseen.'),
(2784, 23, 111, 'Waan ka Abaal marinay Maanta Samirkoodii Dartiis, iyaga unbaana Liibaanay.'),
(2785, 23, 112, 'Wuxuuna Dhihi Eebe imisa Sanaad ku Nagaateen Dhulka.'),
(2786, 23, 113, 'Waxayna Odhan waxaan ku Nagaanay Maalin ama Maalin Qaarkeed, warsada kuwa wax tiriya «Malaa\'gta».'),
(2787, 23, 114, 'Wuxuu Yidhi Maydaan Nagaanin wax Yar Mooyee Haddaad wax Ogtihiin.'),
(2788, 23, 115, 'ma Waxaad u Malayseen inaan Idiin Abuuray Ciyaar oon idinka Xagganaga loo soo Celinayn.'),
(2789, 23, 116, 'Waxaa Sarreeya Eebaha Xaakimka Xaqa ah Ilaah aan isaga ahayna ma jiro Waana Eebaha Carshiga Sharafta leh.'),
(2790, 23, 117, 'Ciddii Caabudda Eebe la Jirkiisa Ilaah kale oon Xujo lahayn Xisaabtiisu waa uun Eebe Agtiisa, mana Liibaanto Gaalo.'),
(2791, 23, 118, 'Waxaad Dhahdaa Eebow Dhaaf oo Naxariiso adaa Naxariiste u Khayr Roone.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, '(Tani) waa Suurad aan Soo Dejinay oo Faralnay (wax ku Caddaynay) kuna soo Dejinay Dhexdeeda Aayaad Cad Cad inaad wax Xusuusataan (waano qaada Taan)'),
(2793, 24, 2, 'Zaaniyadda (Haween) iyo Zaaniga (Ragga) Garaaca mid kastoo ka mid ah Boqol Garaac yeyna Idiin Qabanin Xagooda Naxariis Diinta Eebe Dhexdeeda Haddaad Tihiin kuwa Rumeeyey Eebe iyo Maalinta Dambe (Qiyaamada) hana Joogo Garaacooda Koox ka Mid ah Mu\'miniinta.'),
(2794, 24, 3, 'Zaani ma Guursado waxaan Zaaniyad ahayn, ama Gaal, Zaaniyadna ma Guursato waxaan Zaani ahayn ama Gaal, waana laga Reebay Arrintaas Mu\'miniinta.'),
(2795, 24, 4, 'Kuwa (Zino) ku Gana (Sheega) Haweenka Dhawrsoon oon Afar Marag ah keenin Garaaca Siddeetan Garaacood, hana ka Aqbalina Marag Waligood, kuwaasina waa uun Faasiqiin.'),
(2796, 24, 5, 'Marka laga Reebo kuwa Toobad Keena intaas ka Dib oo Wanaajiya, Eebana waa (Dambi) Dhaafe Naxariista.'),
(2797, 24, 6, 'Kuwa Qadfa (Zino ku Sheega) Haweenkooda oon Marag aan Naftooda ahayn Lahayn midkood Maraggiisu waa inuu ku Marag Kaco Dhaarta Afar Dhaarood Eebe inuu Run Sheegi'),
(2798, 24, 7, 'Tan Shanaad waa inuu ku Dhaarto in Lacanadda Eebe ku Dhacdo hadduu Beenaale yahay.'),
(2799, 24, 8, 'Waxaa Ka rida Iyadana Ciqaabta inay ku Dhaarato Afar Dhaarood inuu Beenaale yahay.'),
(2800, 24, 9, 'Tan Shanaadna waxay ku Dhaaran in Cadhada Eebe ku Dhacdo hadduu Run Sheegi.'),
(2801, 24, 10, 'Hadduuna Jirin FAdliga Eebe iyo Naxariistiisa Korkiinna oosan ahayn Toobad Aqbal Badane Falsan «Waxaa Beenaalaha ku Dhici lahaa Cadaab Daran».'),
(2802, 24, 11, 'Kuwa la Yimid Been Abuurka (Qadafka Caasho) waa Koox Idinka mid ah, How Malaynina inay Shar Idiin tahay, waxayse idiin tahay Khayr, Qof kasta oo ka Mid ahna waxaa u Sugnaaday wuxuu kasban oo Dambi ah midka Qaatay Waynanka Arintana oo ka mid ah wuxuu Mudan Cadaab weyn (Ibnu Salool).'),
(2803, 24, 12, 'Maxaydaan Markaad Maqasheen Xaalka (Qadafka) ayna ugu malayn Mu\'miniinta ragga iyo Haween Naftooda Khayr, oyna u Dhihin Kaasi waa Been Abuur Cad.'),
(2804, 24, 13, 'Mayse u Keenaan Korkiisa Afar Marag, Haddayna keenin Marag kuwaasu Eebe Agtiisa waxay ku Yihiin Beenaalayaal.'),
(2805, 24, 14, 'Hadduuse Jirin Fadliga Eebe ee Korkiinna iyo Naxariistiisa Adduun iyo Aakhiraba waxaa Idin Taaban lahaa waxaad Dhexgasheen Dartiis Cadaab weyn.'),
(2806, 24, 15, 'Markaad kula kulmaysaan Qadafka Carrabkiinna oo ku Dhahaysaan Afkiinna waxaydaan Ogayn, una Malaynaysaan wax Fudud Isagoo Eebe Agtiisa ku wayn.'),
(2807, 24, 16, 'Markaad Maqasheen Maad Dhahdaan Nooma Habboona Inaan ku Haddallo kan, Eebaa Nasahan ee kan (Qadafka) waa Been Abuur wayn.'),
(2808, 24, 17, 'wuu idinka Waanin Eebe inaad u Noqotaan Isagoo kale Waligeed Haddaad Tihiin Mu\'miniin.'),
(2809, 24, 18, 'Wuxuuna idiin Caddayn Eebe Aayaadkiisa, Eebana waa Oge Falsan.'),
(2810, 24, 19, 'Kuwa Jecel inay ku Faafto Xumaantu Kuwa Rumeeyey (Xaqa) waxaa u Sugnaaday Cadaab daran Adduuniyo Aakhiraba, Eebena waa Ogyahay idinkuna ma Ogidin.'),
(2811, 24, 20, 'Hadduuna jirin Fadliga Eebe ee Korkiina iyo Naxariistiisa iyo in Eebe yahay Naxariis badane Ilaaliya, (Waxaa ahaan lahaa wax kale).'),
(2812, 24, 21, 'Kuwa (Xaqa) Rumeeyow ha Raacina Tallaabooyinka (camalka) Shaydaanka Ciddii Raacda Tallaabooyinka Shaydaanka Isagu wuxuu Faraa Xumaanta iyo waxa la Naco, Hadduusan jirin Fadliga Eebe ee Korkiinna iyo Naxariistiisa ma Daahirineen Ruux idinka Mid ah Waligeed, Laakiin Eebaa Daahiriya Cidduu Doono, Eebana waa Maqle Og.'),
(2813, 24, 22, 'Yeyna ku Dhaaranin kuwa Fadliga (Dheeraadka) leh iyo Ilodanimada inayan wax siinayn Qaraabada, Masaakiinta iyo kuwa ku Hijrooda Jidka Eebe, hana iska Cafiyaan hana Saamaxeen, miyeydaan Jeelayn inuu idiin Dhaafo Eebe, Ilaahayna waa Dhaafe Naxariista.'),
(2814, 24, 23, 'Kuwa Gana «Qadfa» Haweenka Dhawrsoon ee Halmaansan (Xumaanta) ee Mu\'minaadka ah waa La Lacnaday Adduun iyo Aakhiraba, waxayna Mudan Cadaab Weyn.'),
(2815, 24, 24, 'Maalinta Ay ku Marag-kici Carrabadoodu, Caemahoodu, iyo Lugahoodu waxay Camalfaleen.'),
(2816, 24, 25, 'Maalintaas wuxuu u oofin Eebe Abaal Marintooda Sugan, waxayna Ogaan in Eebe Yahay Xaqa Cad (Sugan).'),
(2817, 24, 26, 'Haweenka iyo Hadalka Xunba wuxuu u Sugnaaday Nimanka Xun, kuwa Xunna waxay u sugnaadeen kuwa Xun, Kuwa Wanaagsanna waxay u sugnaadeen Kuwa Wanaagsan, kuwaasna (Caa\'isha iyo Ehelkeedu) waa Ka Bari (Dheeryihiin) waxay Sheegi, waxayna Mudan Dambi Dhaaf iyo Rizqi Sharaf leh (Janno).'),
(2818, 24, 27, 'Kuwa (Xaqa) Rumeeyow ha Galina Guryo aan Kuwiinna ahayn Intaad ka Idam Warsataan kana Salaantaan Ehelkooda, Saasaana idiin Khayr Roon si aad u Xusuusataan.'),
(2819, 24, 28, 'Haddaydaan ka Helin Dhexdeeda Ruuxna ha Galina inta Laydinka Idmi, Haddii laydindhaho Noqdana Noqda, Saasaa idiin Wanaagsan, Eebana Waxaad Falaysaan wuu Ogyahay.'),
(2820, 24, 29, 'Ma aha Korkiinna Dhib (Dambi) Inaad Gashaan Guryo aan la Deganayn oo Dan (Anfaco) idiinku Jirto, Eebe wuxuu Ogyahay waxaad Muujinaysaan iyo waxaad Qarinaysaan.'),
(2821, 24, 30, 'Waxaad ku Dhahdaa Mu\'miniinta Ha Laabeen Indhahooda (Daymada Reeba) hana ka Dhawreen Furuujtooda (Zinada) saasaa u Wanaagsan, Eebana wuu Ogyahay waxay Sameyn.'),
(2822, 24, 31, 'Waxaad ku Dhahdaa (Haweenka) Mu\'minaadka ah, (Xaqa Rumeeyey) Ha Laabeen Indhahooda hana Dhawreen Farjigooda Muujininna Quruxdooda wax Muuqda Mooye, Hana ku Dadeen Hagoogtooda dhuuntooda, yayna u Muujin Quruxdooda Ragooda Mooyee, iyo Aabayoowgood iyo Ninkooda Aabihiis, iyo Wiilashooda iyo Wiilasha Nimankooda iyo Walaalahood iyo Wiilasha Walaalahhod, iyo Wiilasho (Gabdhahay) Walaalaha yihiin, iyo Haweenkooda (Muslimka ah) ama waxay Hanato Midigtoodu (Gacantoodu) ama kuwa Raaca een Dan Lahayn (Hawood u Lahayn) oo Ragga aha ama Caruurta aan Fiirsanayn Cawrada Haweenka, Yeyna kuna Garaacin Lugahooda (Dhulka) in la Ogaado waxay Qarin oo Quruxdooda ah, una Toobad keena (U Noqda) Xagga Eebe Dhammaan Mu\'miniineey waxaad u Dhawdihiin Inaad Liibaantaane.'),
(2823, 24, 32, 'U Guuriya Xaaslaawaha Idinka Mid ah iyo Kuwa Suuban oo Addoomadiinna ah (Rag iyo Haweenba) hadday Sabool Yihiin wuxuu ka Hodmin Eebe Fadligiisa, Eebana waa Waasac (Fadligiisu) Waana Oge.'),
(2824, 24, 33, 'Hase Dhawrsadeen Kuwaan Helayn (awood) Guur inta Eebe kaga Hodmin Fadligiisa, kuwa Dooni Inay Isfurtaan oo Hanatay Gacantiinnu Kitaabeeya (Ka Ogolaada) Haddaad ku Ogtihiin Khayr, wax ka Siiyana Xoolaha Eebe ee uu Idin Siiyey, hana ku Qasbina Gabdhihiinna (aad Ilanataan) Zino Hadday Doonaan Dhawrsooni Idinkoo Dooni Sadka Nolosha Adduunyada, Ciddii Qasabta Eebe Qasabkaas ka Dib waa Dambi Dhaafe Naxariista.'),
(2825, 24, 34, 'Waxaan idiin soo Dejinay Aayaad Cad Cad iyo Tusaale Kuwii Tagay ee Idinka Horreeyey iyo Waanada kuwa Dhawrsada.'),
(2826, 24, 35, 'Eebe waa Nuuriyaha Samooyinka iyo Dhulka, Nuurkiisu wuxuu la mid yahay Daaqad Misbaax ku Jiro Misbaaxuna ku Jiro Dhalo, Dhaladana aad Mooddo Xiddig lfi lagana Huriyey Geed la Barakeeyay oo ah Zaytuun (Saliidd leh) oon bari iyo Galbeed midna ahayn, waxayna u Dhawdahay Saliideedu inay iska Ifto Yuuna Taabaninba Dabe, waa Nuur lagu kordhiyay Nuur, wuxuuna ku Hanuuniyaa Eebe Nuurkiisa Cidduu Doono, wuxuuna u Yeelaa Eebe Dadka Tusaale, Eebana wax kasta waa Ogyahay.'),
(2827, 24, 36, '(Kaasoo ah) Guryo (Masaajidda) uu idmay Eebe in la Koryeelo Laguna Xuso Dhexdeeda Magaciisa, waxaana ugu Tasbiixsada Dhexdeeda Aroor iyo Galabba.'),
(2828, 24, 37, 'Raga ayna ka Shuqlinayn Ganacsi iyo Gadasho Xuska Eebe, Oogidda Salaadda iyo Bixinta Zakada kana Cabsan Maalin oy Gagadoomi Dhexdeeda Quluubta iyo Aragguba.'),
(2829, 24, 38, 'Inuu ka Abaal Mariyo Eebe Wanaagay Faleen una Kordhiyo Fadligiisa, Eebana wuu Arzuqaa Cidduu Doono Xisaab la\'aan.'),
(2830, 24, 39, 'Kuwii Gaaloobay Camalkoodu wuxuu la Midyahay Dhalanteed Dhul Siman (Ka Muuqda) oos u Malayn kan Oomani biyo Markuu Yimaadana uuna ka Helayn waxba, oo uu ka heli Eebe (Abaal marintiisa) Agtiisa una Oofiyey Xisaabtiisa, Eebana way Deg degtaa Xisaabtiisu.'),
(2831, 24, 40, 'Ama wuxuu la Midyahay Mugdiyo Bad Dhexdeed ah oos Dabooli Hir Korkiisana yahay Hir, Korkiisana Tahay Daruur, waa Mugdiyo Qaarkood Qaar ka Sarreeyo Hadduu Bixiyo Gacantiisana uma Dhawa inuu Arko, Ruuxaan Eebe u yeelin Nuur malaha Nuur.'),
(2832, 24, 41, 'Miyaadan Ogayn in Eebe u Tasbiixsado waxa ku Sugan Samooyinka iyo Dhulka iyo Shimbiraha oo (Fidsan) Safan mid kasta wuu Ogyahay Salaaddiisa iyo Tasbiixdiisa, Eebana waa Ogyahay waxay Fali.'),
(2833, 24, 42, 'Eebe waxaa u Sugnaaday Xukunka Samooyinka iyo dhulka Xagga Eebaana loo Ahaan.'),
(2834, 24, 43, 'Miyeydaan Arkayn in Eebe Kexeeyo Daruuro Markaas Isudumo Kana Yeelo Mid is dulSaaran ood aragto Roobkoo ka Soo Bixi Dhexdiisa wuxuuna ka soo Dejiyaa (Eebe) Samada Buuro Qabow ah (Thalaj) wuxuuna Gaadhsiiyaa Cidduu Doono, wuxuuna u Dhawyahay Nuurka Hilaaciisu inuu la Tago Aragga.'),
(2835, 24, 44, 'Wuu Gagadiyaa Eebe Habeenka iyo Dharaarta, Arrintaasna waano Qaadasho yaa ugu Sugan kuwa Aragga leh (Caqliga lch).'),
(2836, 24, 45, 'Eebaa ka Abuuray wax Kastoo socda (oo nool) Biyo waxaana ka Mid ah mid ku Socda Calooshiisa, waxaana ka mid ah mid ku Socda Labo Lugood, Waxaana ka mid ah mid ku Socda Afar, wuxuu Abuuraa Eebe wuxuu Doono, Eebana wax kasta waa Karaa.'),
(2837, 24, 46, 'Waxaan soo Dejinay Aayaad Cad Cad, Eebana wuxuu ku Hanuuniyaa Cidduu Doono Jid Toosan.'),
(2838, 24, 47, 'Waxay Dhihi (Munaafiqiintu) Waxaan rumeeynay Eebe iyo Rasuulka waana Adeecnay markaasaa waxaa Jeedsada Kooxo ka Mid ah Intaas ka Dib, Kuwaasi ma ah Mu\'miniin.'),
(2839, 24, 48, 'Marka Loogu Yeedho Eebe iyo Rasuulkiisa inuu kala Xukumo Dhexdooda waxaa soo Baxa Koox ka Mid ah oo Jeedsan.'),
(2840, 24, 49, 'Hadduu Xaqu iyaga U sugnaado way u Yimaadaan iyagoo Adeeci.'),
(2841, 24, 50, 'Ma Qalbigay ka Bukaan mise way Shakiyeen mise waxay ka Cabsan in Eebe ku Jawr falo iyo Rasuulkiisu, saas ma aha ee kuwaasu waa kuwo Daalimiin ah.'),
(2842, 24, 51, 'Mu\'minka Hadalkiisu marka loogu yeedho Eebe iyo Rasuulkiisu inuu kala Xukumo Dhexdooda waa uun Inay Dhahaan waan Maqallay Waana Adeecnay kuwaasuna waa uun kuwa Liibaanay.'),
(2843, 24, 52, 'Ciddii Adeecda Eebe iyo Rasuulkiisa oo Cabsata kana Dhawrsata Kuwaasi waa uun kuwa Liibaanay.'),
(2844, 24, 53, 'Waxay ugu Dhaarteen Eebe (Munaafiqiinta) Dhaar Daran inay u Baxaan (Jahaad) waxaad Dhahdaa ha Dhaaranina Adeecid la Yaqaan (ha Ahaado Xaalkiinnu) Eebana waa Ogyahay waxaad Falaysaan.'),
(2845, 24, 54, 'Waxaad Dhahdaa Addeeca Eebe oo Adeeca Rasuulka Haddaad Jeedsataanna waxaa uun Korkiisa ah (Nabiga) waxa la Saaray (la Faray) idinkana waxa Korkiinna ah waxa laydin Saaray (Xaq raacid) haddaad Addeecdaan (Nabiga) waad Hanuunaysaan, Rasuulkana ma Saarra waxaan Gaadhsiin Cad ahayn.'),
(2846, 24, 55, 'Wuxuu u Yaboohay Eebe kuwa Rumeeyay Xaqa oo Camal Fiican Falay inuu u Reebo Dhulka siduu ugu Reebay kuwii ka Horreeyay, wuxuuna Makanin Diintooda uu uga Raalli Noqday wuxuuna ugu Badali Cabsidooda ka Dib Nabadgalyo, wayna I cabudi Ilamana Wadaajiyaan waxba, Ciddii Gaalowda intaas Ka Dibna kuwaasu waa uun Faasiqiin.'),
(2847, 24, 56, 'Ooga Salaadda, Bixiya Zakada oo Addeeca Rasuulka waxaad Mudantihiin in laydiin Naxariistee.'),
(2848, 24, 57, 'Hana Umalayn Kuwa Gaaloobay inay ku Daalin (Eebe) Dhulka, Guryahooduna Waa Naar waxaana u Xun Meel Loo Ahaado (Naar).'),
(2849, 24, 58, 'Kuwa Xaqa Rumeeyow Haydin Idan Warsadeen kuway Hanatay Gacantiinnu, iyo Kuwaan Qaan Gaadhin oo Idinka Mid ah Saddex Jeer, Salaadda Subax ka Hor iyo Markaad iska Dhigtaan Dharka Duhurkii, iyo Salaadda Cishaha ka Dib, waa Seddex Waqti oo Cawro ah «la Nasto», ma aha Korkiinna iyo korkooda midna Dhib «ma Saarra» intaas ka Dib, way idin kor Socdaan (Shaqeeyaan) Qaarkiinna Qaarbuu ka Ahaaday, saasuuna idiinku Caddayn Eebe Aayaadkiisa Eebana waa Oge Falsan.'),
(2850, 24, 59, 'markay Carrurtiinnu Qaangaadhana haydin Idan warsadeen siday idiin Idamwarsadeen kuwii ka Horreeyay, saasuuna idiinku Caddayn Eebe Aayaadkiisa, Eebana waa Oge Falsan.'),
(2851, 24, 60, 'Kuwa ka Fadhiistay (Ka quustay dhalmada) oo Haweenka ah oon guur Rajaynayn Korkooda Dhib (Dambi) ma Saarra inay iska Dhigaan Dharkooda (Iska Fududeeyaan) iyagoon ismuujinayn Qurux, Inay Dhawrsadaan yaase u Khayr Roon Eebana waa Maqle Og.'),
(2852, 24, 61, 'Ma Saarra Indhoole Dhib, Lugoolana Dhib ma Saarra, Mid Bukana Dhib Ma Saarra, Naftiinnana Dhib ma Saarra Inaad wax ka Cuntaan Guryihiinna ama Guryaha Aabayaalkiin, ama Guryaha Hooyooyinkiin, ama Guryaha Walaalihiin, ama Guryaha Walaashiin, ama Guryaha Adeeradiin, ama Guryaha Eeddooyinkiin, ama Guryaha Abtiyadiin, ama Guryaha habaryarihiin, ama Guryahaad Hanataan Furihiisa, ama Guryaha Saaxiibahiin, Dhibna Idinma Saarra Inaad wax Cuntaan Idinkoo Kooxa ah ama kala Tagsan, markaad Gashaan Guryana Salaama Naftiinna, Salaan ka Ahaatay Eebe Xagiisa oo la Barakeeyey oo Fiican, Saasuuna idiinku Caddayn Eebe Aayaadka Inaad wax kastaan.'),
(2853, 24, 62, 'Mu\'miniintu Waa Uun kuwa Rumeeyey Eebe iyo Rasuulkiisa, markay la Joogaan Amar kulmiyey Dartiis aan Tagin intay ka Idan Warsadaan, kuwa Idan warsada kuwaasu waa kuwa Rumeeyey Eebe iyo Rasuulkiisa, ee hadday kuu Idan warsadaan Danahooda Qaarkood u Idan Ciddaad Doonto oo ka Mid ah una Dambi Dhaafwarso Eebe, Ilaahay waa Dambi Dhaafe Naxariistee.'),
(2854, 24, 63, 'Ha ka Yeelina u Yeedhidda Rasuulka sida U Yeedhidda Qaarkiin qaar, Eebe wuu ogyahay kuwa Dusi iyagoo Gabban, (Fadhiga Nabiga) ha ka Digtoonaadeen kuwa Khilaafi Amarka Nabiga inay ku Dhacdo Fidmo (Masiibo) ama ku Dhoco Cadaab Daran.'),
(2855, 24, 64, 'Eebaa iska Leh waxa ku Sugan Samooyinka iyo Dhulka, waana Ogyahay Waxaad ku Sugantihiin, iyo Maalinta loo Celin Xaggiisa, oo uga Warami waxay Camal faleen, Eebana wax kasta waa Ogyahay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Waxaa Khayr Batay (oo wayn) Eebaha ku soo Dejiyey Quraanka Addoonkiisii (Nabiga) inuu u ahaado Caalamka u Dige.'),
(2857, 25, 2, 'Eebaha uu u Sugnaaday Xukunka Samooyinka iyo Dhulka, Yeelaninna Ilmo una Sugnaanin Shariig (la Wadaage) Xukunka, Abuurayna wax kasta, oo u ahaysiiyey si Qiyaasan.'),
(2858, 25, 3, 'Waxay ka Yeesheen Eebe ka Sokow Ilaahyo oon Abuurayn waxba Iyagana la Abuuri una hananayn Naftooda Dhib iyo Nacfi (Midna) oonan hananayn Geeri iyo Nolol iyo soo Bixin (midna).'),
(2859, 25, 4, 'Waxay Dheheen Gaaladii kani (quraanku) ma aha waxaan Been Abuur ahayn, uu la Yimid, Uguna Kaalmeeyeen Qoom kale, waxay la Yimaadeen Dulmi iyo Been (Daran).'),
(2860, 25, 5, 'Waxay Dheheen waa Warkii Dadkii Hore oos ka Qoray Iyadaana loo Yeedhiyaa Aroor iyo Galabba.'),
(2861, 25, 6, 'Waxaad Dhahdaa waxaa soo Dejiyey Eebaha Og waxa ku Qarsoon Samooyinka iyo Dhulka Waana Dambi Dhaafe Naxariista.'),
(2862, 25, 7, 'Waxay dheheen Gaaladii Muxuu leeyahay Rasuulkani oos u Cuni Cunto una Socon Suuqa, maa loo Soo Dejiyo Malag oo ahaado mid la Diga.'),
(2863, 25, 8, 'Ama maa loo Tuuro (Dejiyo) Kansi ama u ahaato Beer uu wax ka Cuno Xageeda, waxayna Dheheen Daalimiintu ma Raacaysaan waxaan Nin Sixran ahayn.'),
(2864, 25, 9, 'Bal day siday kuugu Yeeleen Tusaale oy u Dhumeen una Karin (Qaadidda) Jidka (Xaqa).'),
(2865, 25, 10, 'Waxaa Khayr Badan Eebaha Hadduu Doono kuu Yeeli wax ka Khayrroon Taas, Jannooyinna (Kuu yeeli) oy Socolo Dhexdeeda Wabiyaal Kuuna Yeeli Daaro (Janno).'),
(2866, 25, 11, 'Waxayse Beeniyeen Saacadda (Qiyaame) waxaana u Darabnay Ciddii Beenisa Saacadda Naarta Saciira.'),
(2867, 25, 12, 'Markay ka Aragto Meel Fog waxay ka Maqlaan Cadho iyo Guux.'),
(2868, 25, 13, 'Marka lagu Tuuro Dhexdeeda Meel Cidhiidhi ah Iyagoo isku Xidhan waxay u Yeedhaan Halaag, iyo (Khasaare).'),
(2869, 25, 14, '(Waxaana la Dhihi) Howyeedhanina Maanta Halaag Kaliya ee u Yeedha Halaag Badan.'),
(2870, 25, 15, 'Waxaad Dhahdaa Ma Taasaa Khayrroon mise Jannada Waaridda ee ah Middii loo Yaboohay kuwa Dhawrsada, waxayna u Ahaatay Kuwaas (Dhawrsada) Abaal Marin iyo Noqosho.'),
(2871, 25, 16, 'Waxayna ka Helayaan Dhexdeeda waxay Doonaan wayna ku Waari, Arrintaasuna waa Yabooh Isaaray Eebaha oo Sugan.'),
(2872, 25, 17, 'Xusuusta Maalintaan Kulminayno Gaalada iyo waxay Caabudi oo Eebe ka soo Hadhay oos ku Dhihi Ma idinkaa Dhumiyey Addoomadaydaas, Mise Iyahgaa ka Dhumay Jidka.'),
(2873, 25, 18, 'Waxayna Odhun Waad Nastahantahay Naguma Habboona Inaan Yeelanno Adi ka Sokow Awliyo (Gargaare), waadse u Raaxaysay iyaga iyo Aabayaalkood Intay ka Halmaamaan Waxyiga, waxayna Noqdeen kuwa Halaagsamay.'),
(2874, 25, 19, 'Way Idinku Beeniyeen waxaad Sheegaysaan mana Kartaan Furasho iyo Gargaar midna, Ciddii wax Dulmida oo Idinka midah Waxaannu Dhadhansiin Cadaab Wayn.'),
(2875, 25, 20, 'Manaan Dirin Rasuul kaa Horeeyey oon Cunayn Cunnada Soconaynna Suuqa, waxaana uga Yeellay Qaarkiin Qaarka kale Fidmo (Ibtilo) ee ma Samraysaan, Eebahaana waa Arke.'),
(2876, 25, 21, 'Waxay Dheheen Kuwaan Rajaynayn la kulankanaga naloogu soo Dejinwaayey Malaa\'ig, ama maan Aragno Eebahanno, way Iskibriyeen (isula waynaadeen) Naftooda wayna Madax Adaygeen Madax Adayg Wayn.'),
(2877, 25, 22, 'Maalintay Arki Malaa\'igta (Geerida) ma Bishaaraystaan Maalintaas Dambiila Yaashu, waxayna Odhan Xijaab oodan iyo Reebnaan yaa ah (Wanaag iyo Isla kulan majiro).'),
(2878, 25, 23, 'Waannu u soo Hormarinay waxay Faleen oo Camal ah Waxaana ka yeelay Dhalanteed la Firdhiyey.'),
(2879, 25, 24, 'Ehelu Jannaha yaa Maalintaas Khayr Roon Sugnaansho iyo Wanaag badan Qayluushi (Hurdo).'),
(2880, 25, 25, 'Xusuuso Maalintay Samadu la Dillaaci Daruuraha, lana soo Dejin Malaa\'igta soo Dejin.'),
(2881, 25, 26, 'Xukunka Maalintaas ee Xaqa ah waxaa iska leh Eebaha Raxmaana, Waana Maalin Gaalada ku Daran.'),
(2882, 25, 27, 'Xusuusana Maalintuu Qaniini Daalimku Gacantiisa (Faraha) Isagoo Dhihi Shalleytee maan Yeesho la Jirka Rasuulka Jid (maan Rumeeyo).'),
(2883, 25, 28, 'Halaag iyo Shalleytee Maxaan hebel Saaciib uga Yeeshay.'),
(2884, 25, 29, 'Wuxuu iga Leexiyey Quraanka intuu ii yimid ka Dib, Shaydaanna Dadka Wuxuu u Yahay Dayace (Mee Xun kaga tage).'),
(2885, 25, 30, 'Wuxuu Yidhi Rasuulkuna Eebow Qoomkaygii waxay ka Yeesheen Quraankan mid laga Tagay (Ma Maqlaan).'),
(2886, 25, 31, 'Saasaan ugu Yeellay Nabi kasta Col Dambiilayaal ah, Eebana ku Filan Hanuuniye iyo Gargaare.'),
(2887, 25, 32, 'Waxay Dheheen kuwii gaaloobay maa lagu Soo Dejiyo Nabiga Quraanka Mar kaliya (Isagoo dhan) saasaan Yeellay, inaan ku Sugno Qalbigaaga, waana kugu Akhriyeynaa Akhrin (Fiican).'),
(2888, 25, 33, 'Wixii Tusaale ah oy Kuula Yimaadaanna waxaannu Kuugula imaan Xaq iyo Fasiraadda u Fiican.'),
(2889, 25, 34, 'Kuwa lagu soo Kulmin Wajigooda (Naarta) Jahannamo kuwaasu waa kuwa u Xun meel uguna Dhumid Badan Jid.'),
(2890, 25, 35, 'Waxaana Siinay Nabi Muuse Kitaabkii Waxaana ka Yeellay la Jirkiisa Walaalkiis Haaruun Wasiir.'),
(2891, 25, 36, 'Waxaana Nidhi Aada Qoomkii beeniyey Aayaadkanaga Waana baabi\'inay (Halaagnay) baabi\'in, (Halaagnay).'),
(2892, 25, 37, '(Xusuuso) Qoomkii Nabi Nuux, Markay Beeniyeen Rasuulladii Yaan Maan-Sheynay Ugana Yeellay Dadka Calaamad, Waxaana u Darabnay Daalimiinta Cadaab Daran.'),
(2893, 25, 38, 'Caadna waan Halaagnay iyo Thamuud, iyo Rasi (Ceelkii) Ehelkiisii, iyo Quruumo Intaas u Dhaxeeyey oo Badan.'),
(2894, 25, 39, 'Dhammaan waxaan u Yeellay Tusaale Dhammaanna waan Halaagnay Halaag.'),
(2895, 25, 40, 'Way Tageen Gaaladu Magaaladii lagu soo Daadiyey Roob Xun (Dhagax Cadaab) Miyeyna Arkeyn, waxayse Ahaayeen kuwaan Rajaynayn (ka yaabayn) soo kulmin.'),
(2896, 25, 41, 'Hadday ku Arkaan Gaaladu waxay kaa yeeshaan Jees Jees, iyagoo (Dhihi) ma kanaa kan Eebe Soo Bixiyey Isagoo Rasuul ah.'),
(2897, 25, 42, 'Wuxuu U dhawaaday inuu Naga dhumiyo Ilaahyadanada Haddaanaan ku Samrin, (ku adkaysan) waxay Ogaadaan Markay Arkaan Cadaabka Cidda Dhunsan.'),
(2898, 25, 43, 'Ka Warran Ruux ka Yeeshay Ilaahiisa Hawadiisa (Suu Jecelyahay) Ma adaa Wakiil (Ilaaliye) ka Noqon.'),
(2899, 25, 44, 'Mise waxaad u Malayn in Badankood wax Maqli ama wax kasi, ma aha waxaan Xoolo oo kale ahayn, Iyagaabase ka Sii Dhunsan.'),
(2900, 25, 45, 'Miyeydaan Ogayn sida Eebe u Fidiyo hadhka, hadduu Doonana wuxuu ka Yeeli lahaa Mid Xasillan (Taagan) markaasaan ugu Yeellay Qorraxda korkiisa Daliil (Xujo).'),
(2901, 25, 46, 'Markaasaan u Soo qabanaa Xagganaga Qabasho Yar.'),
(2902, 25, 47, 'Eebe waa kan Idinka Yeelay Habeenka Astur, Hurdadana Raaxo, kana yeelay Maalinta soo Kulan (Dhaqdhaqaaq).'),
(2903, 25, 48, 'Eebe (Sareeye) waa kan u Dira Dabaysha Bishaaro Darteed Naxariistiisa (Roobka) Hortiisa, Waxaana ka soo Dejinay Samada Biyo wax Daahiriya.'),
(2904, 25, 49, 'Inaan ku Nooleyno Magaalo Dhimatay oon kuna Waraabino waxaan Abuuray, Xoolo iyo Dad Badan.'),
(2905, 25, 50, 'Waana ku kala Duwnay «Badinay» Dhexdooda, inay Xusuustaan, Wuuna Diiday Dadka Badidiis waxaan Gaalnimo Badan ahayn.'),
(2906, 25, 51, 'Haddaan Doonno waxaan ka Bixin Lahayn Magaalo Kasta Dige (Nabi).'),
(2907, 25, 52, 'ee ha Adeecin Gaalaga kulana Jahaad Quraanka Jahaad Weyn.'),
(2908, 25, 53, 'Eebe wuu isku daraa Labada Badood, Kaasi waa Macaan Daran, kaasna waa Khadhaadh Daran (Afku Diido) yeelayna Dhexdooda Soohdin iyo Xaajis Awdan.'),
(2909, 25, 54, 'Eebana waa kan ka Abuuray Biyaha Dad kana Yeelay Qaraabo iyo Soddognimo, Eebahaana wuu karaa (Wuxuu Doono).'),
(2910, 25, 55, 'Waxayna Caabudi Gaaladu Eebe ka Sokow waxaan Anfacayn Dhibayna Gaalna wuxuu ahaaday mid ka soo hor Jeeda Eebihiis.'),
(2911, 25, 56, 'Maa naan kuu Dirin inaad Bishaareeye Dige ah (Noqoto Mooyee).'),
(2912, 25, 57, 'Waxaad Dhahdaa Idinkama warsado (Xaqa Gaadhsiintiisa) UJuuro Ruuxiise dooni inuu ka Yeesho Eebehiis Xaggiisa Waddo (Khayr ha falo).'),
(2913, 25, 58, 'Talo Saaro Eebaha Nool een Dhimanayn una Tasbiixso adoo Mahdin, Eebaana ku Filaan Dambiga Addomadiisa Ogaanshihiisa.'),
(2914, 25, 59, 'Waana Eebaha ku Abuuray Samooyinka iyo Dhulka iyo Waxa u Dhexeeya Lix Maalmood, kuna Istawooday Carshiga (Si u Cununta), ee Warso Cid wax Og.'),
(2915, 25, 60, 'Marka la Dhaho u Sujuuda (Eebaha) Raxmaan ah waxay Dhahaan Muxuu Yahay Raxmaan, ma waxaan u Sujuudeynaa waxaad na Farayso, wuxuuna u Kordhiyey Carar.'),
(2916, 25, 61, 'Waxaa Khayr Badnaaday Eebaha Yeela Samada Burjiyo Yeelayna Dhexdeeda Siraaj (Qorrax) iyo dayax ifi.'),
(2917, 25, 62, 'Eebana waa Ilaah uga Yeelay Habeenka iyo Maalinta Isbaddel Ciddii Doonta inuu wax Xusuusto ama Doono inuu Mahdiyo.'),
(2918, 25, 63, 'Addoomada Eebaha Raxmaana waa kuwa u Socda Dhulka Xasillooni, markay la Hadlaan (Af Xumeeyaan) Jaahilliinna Yidhaahda Nabadgalyo.'),
(2919, 25, 64, 'ee ah Kuwa ku Barya (Habeenkii) Iyagoo u Sujuudi oo u Istaagi (Cibaado) Eebe.'),
(2920, 25, 65, 'ee Ah kuwa Dhihi Ilaahow naga Iil Cadaabka Jahannamo, Cadaabkeedu waa joogtee.'),
(2921, 25, 66, 'Iyaduna waxay u Xuntahay ku Sugnaansho iyo ku Nagaadi.'),
(2922, 25, 67, 'Ee ah kuwa Markay wax Bixiyaan aan Xad gudbin Cidhiidhyinna, oo intaas Dhexdeeda ku Tolosnaada.'),
(2923, 25, 68, 'Ee ah kuwaan Caabudayn Eebe La Jirkiisa Ilaah kale, oona Dilayn Naftuu Eebe Xarrimay Dilkeeda Xaq Mooyee oon Zinaysanayn, Ciddii Saas fashana waxay la Kulmi Dambi.'),
(2924, 25, 69, 'Waxaana loo Laab laabi Cadaabka Maalinta Qiyaame wuuna ku Waari Dhexdiisa Isagoo Dullaysan.'),
(2925, 25, 70, 'Ciddii Toobad keenta oo Rumaysa oo Fasha Camal Fiican Mooyee, oo kuwaas waxaa loogu Badali Xumaantooda Wanaag, Eebana waa Dambi Dhaafe Naxariista.'),
(2926, 25, 71, 'Ruuxii Toobad keena oo Fala Wanaag wuxuu u Noqon Eebe Noqosho.'),
(2927, 25, 72, '(Kuwa Fiican) waa kuwaan Furayn Been, Markay Maqlaan Hadal Xunna Mara iyagoo Wanaagsan (Ka Jeedsada).'),
(2928, 25, 73, 'Waana kuwa marka lagu Waaniyo Aayaadka Eebahooda aaan Dhicin Dhago iyo Indho La\'aan.'),
(2929, 25, 74, 'waana kuwa Dhihi Eebow nagasii (naga tusi) Haweenkanaga iyo Caruurtanada wax Ishu ku Qabowsato, noogana yeel kuwa Dhawrsada Imaam.'),
(2930, 25, 75, 'Kuwaas waxaa lagu Abaal marin Fooqa (Janno) Samirkooddi Dartiis waxayna kula Kulmi dhexdeeda Salaan iyo soo Dhawayn.'),
(2931, 25, 76, 'Wayna ku waari Dhexdeeda waxaana u Wanaagsan Janada ku Sugnaan iyo Nagaadiba.'),
(2932, 25, 77, 'Waxaadna Dhahdaa Idiinma Aabo Yeeleen Eebahay Haddaydaan Baryeyn (Illeen) waad beeniseen (Xaqee) wuxuuna ahaan mid idin Laasima Beeninta «Ciqaabta».');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'Waxay ku Tusin Mucjisada Quraanka sida soo Hor martay.'),
(2934, 26, 2, 'Taasi waa Aayadihii Kitaabka Cad.'),
(2935, 26, 3, 'Waxaadna u Dhawdahay Nabiyow inaad Halaagto Naftaada Haddayna Rumayn Xaqa.'),
(2936, 26, 4, 'Haddaan Doono Waxaan kaga soo Dejinaynaa Korkooda Samada Aayad oy Noqoto Luquntoodu Miday u Khushuucdo.'),
(2937, 26, 5, 'Waxkasta oo Quraana oo Uga Yimaadda Xagga Eebaha Raxmaana oo soo Darriyo (Cusboonaada) waxay ahaan kuwo ka Jeedsada.'),
(2938, 26, 6, 'Waxayna Beeniyeen (Xaqa) waxaase u Imaan Warka waxay ku Jees Jeesayeen.'),
(2939, 26, 7, 'Miyeyna Arkayn Dhulka Inaan ka soo Bixinno Nooc kasta oo Fiican.'),
(2940, 26, 8, 'Taasna waxaa ku sugan Aayad Badankooduna ma aha Mid Rumeyn (Xaqa).'),
(2941, 26, 9, 'Eebahaana Waa Adkaade Naxariista.'),
(2942, 26, 10, '(Xusuuso) markuu ugu Yeedhay Eebahaa (Nabi) Muuse inuu Aado Qoomka Daalimiinta ah.'),
(2943, 26, 11, 'Qoomka Fircoon, Maydhawrsadaan.'),
(2944, 26, 12, 'Wuxuu Yidhi (Nabi) Muuse Eebow waxaan ka Cabsan inay I beeniyaan.'),
(2945, 26, 13, 'Ooy Cidhiidhyanto Laabtaydu Siina Daysmin Carrabkaygu ee Dir la Jirkayga Haaruun.'),
(2946, 26, 14, 'Waxayna igu Leeyihiin Dambi (Godob) waxaana ka Cabsan inay I dilaan.'),
(2947, 26, 15, 'Wuxuu Yidhi Ilaahay saas ma aha ee La aada Aayaadkanaga, Anagaa Idinla Jirra Maqlina.'),
(2948, 26, 16, 'ee u Taga Firecoon una Dhaha Annagu waxaan Nahay Rasuulka Eebaha Caalamka.'),
(2949, 26, 17, 'Ee Dir «u Ogolow» Banii Israa\'iil la jirkannaga.'),
(2950, 26, 18, 'Wuxuu Yidhi (Firecoon) Miyaanaan kugu Barbaarinin Dhexdanada Adoo Yar oodan ku Nagaanin Dhexdanada Cimrigaaga Sanooyin.'),
(2951, 26, 19, 'Ood Fashay Arrintaad Fashay Adoo Diidan (Sama Falkii).'),
(2952, 26, 20, 'Wuxuu Yidhi (Nabi) Muuse waan Falay Anoon Wax kasayn.'),
(2953, 26, 21, 'Waana Idinka Cararay Markaan Idinka Cabsaday Markaasuu Eebe I siiyey Nabinimo, Igana Yeelay kuwa La Diray (Rasuul).'),
(2954, 26, 22, 'Taasise ma Nicma aad igu Mannaysanbaa Haddaad Addoonsatay Banii Israa\'iil.'),
(2955, 26, 23, 'Fircoon wuxuun Yidhi waa maxay Eebaha Caalamka.'),
(2956, 26, 24, 'Wuxuu Yidhi (Nabi) Muuse waa Eebaha Samooyinka iyo Waxa u Dhaxeeya haddaad wax Yaqiinsanaysaan (Rumaynaysaan).'),
(2957, 26, 25, 'Wuxuu ku Yidhi (Fircoon) kuwii Gaararkiisa ahaa War ma Maqlaysaan.'),
(2958, 26, 26, 'Wuxuu Yidhi Nabi Muuse waa Eebihiin iyo Eebaha Aabayaalkiinii Hore.'),
(2959, 26, 27, 'Wuxuu Yidhi Fircoon Rasuulkiinna laydiin soo diray waa Waalanyahay.'),
(2960, 26, 28, 'Wuxuu Yidhi Nabi Muuse waa Eebaha Bari iyo Galbeed iyo Waxa u Dhaxeeya Haddaad wax kasaysaan.'),
(2961, 26, 29, 'Wuxuu Yidhi Fircoon haddaad Yeelato Ilaah iga soo Hadhay waxaan kaa Yeeli kuwa Xabbisan.'),
(2962, 26, 30, 'Wuxuu Yidhi Nabi Muuse Haddaan kuula Imaaddo wax Cadna.'),
(2963, 26, 31, 'Wuxuu Yidhi Fircoon la Imow Haddaad Run Sheegi.'),
(2964, 26, 32, 'Markaasuu Tuuray Nabi Muuse Ushiishii waxayna Noqotay Mas Muuqda.'),
(2965, 26, 33, 'Wuxuuna Siibay Gacantiisa waxayna ahaatay mid u Cad Dadka Dayi.'),
(2966, 26, 34, 'Wuxuu ku yidhi (Fircoon) kuwii Gaararkiisa ahaa kani waa Sixirre Xeel Dheer.'),
(2967, 26, 35, 'Doonina Inuu Idinkaga Bixiyo Dhulkiinna Sixirkiisa ee Maxaad Faraysaan.'),
(2968, 26, 36, 'Waxay Dheheen Dib u Dhig Isaga iyo Walaalkiis (Xaalkooda) una Dir Magaalooyinka kuwo soo Kulmiya.'),
(2969, 26, 37, 'Ha kuu Keeneen Sixirre kasta oo Xeel Dheer.'),
(2970, 26, 38, 'Waxaana Loo kulmiyey Saaxiriintii Imaatin Maalin la Yaqaan.'),
(2971, 26, 39, 'Waxaana Loo Yidhi Dadkii ma Kulmaysaan.'),
(2972, 26, 40, 'Inaan Raacno Saaxiriinta Hadday Adkaadaan.'),
(2973, 26, 41, 'Markay Yimaaddeen Saaxiriintii waxay ku Dheheen Fircoon Ujuuro ma Leenahay haddaan Adkaanno.'),
(2974, 26, 42, 'Wuxuuna Yidhi haa Waxaadna Noqonaysaan kuwa ii dhow.'),
(2975, 26, 43, 'Wuxuu ku Yidhi (Nabi) Muuse Tuura waxaad Tuuraysaan.'),
(2976, 26, 44, 'Waxayna Tuureen Xadhkahoodii iyo Ulahoodii waxayna Dheheen Sharafta Fircoon yaan ku dhaarannaye Anagaa adkaan.'),
(2977, 26, 45, 'Markaasuu Tuuray (Nabi) Muuse Ushiisii waxayna Ahaatay mid Gurata waxay Dhoodhoobeen.'),
(2978, 26, 46, 'waxaana la Riday Saaxiriintii Sujuud Darteed.'),
(2979, 26, 47, 'Waxayna Dheheen waxaan Rumaynay Eebaha Caalamka.'),
(2980, 26, 48, 'Eebaha (Nabi) Muuse iyo (Nabi) Haaruun.'),
(2981, 26, 49, 'Wuxuu yidhi Fircoon Miyaad Rumeyseen Muuse ka Hor Idankayga, Isagu waa kan idiinku wayn ee idin Baray Sixirka waadse Ogaan doontaan, waxaana u Goyn Gacmahiinna iyo Lugahiinna Isdhaaf, waana idin Wadhi Dhammaantiin.'),
(2982, 26, 50, 'Waxayna dhaheen Dhib ma leh annagu Xagga Eebahannaan u Noqonaynaa.'),
(2983, 26, 51, 'waxaana Dalbaynaa (Damcaynaa) inuu noo Dhaafo Eebahanno Gafafkanaga Inaan Noqonay kuwii ugu Hor Rumeeyey (Xaqa).'),
(2984, 26, 52, 'Waxaan u waxyoonay Nabi Muuse Guuri Addoomadayda waa Laydin Raaciye.'),
(2985, 26, 53, 'Markaasuu u Diray Fircoon magaalooyinka kuwo Soo kulmiya.'),
(2986, 26, 54, 'Isagoo dhihi Kuwaasu «Banii Israa\'iil» waa Koox Yar.'),
(2987, 26, 55, 'Wayna Na cadho Galiyeen.'),
(2988, 26, 56, 'Waxaana nahay kuwo Had iyo Jeer Digtoon.'),
(2989, 26, 57, 'Waxaana ka Bixinnay Beero iyo (Biyo) ilo.'),
(2990, 26, 58, 'Iyo Kanziyo iyo Nagaadi Fiican.'),
(2991, 26, 59, 'Waxaana Dhaxalsiinay Banii Israa\'iil.'),
(2992, 26, 60, 'Markaasay Raacen (ka daba Duulcen) Qorrax soo Bixii.'),
(2993, 26, 61, 'Markay isu Muuqdeen Labadii Kooxood waxay Dheheen Asaxaabtii Nabi Muuse Inaga waa Layna Haleelay.'),
(2994, 26, 62, 'Wuxuuna Yidhi (Nabi) Muuse Saas ma aha ee Eebahaybaa Ila jira wuuna i Toosin.'),
(2995, 26, 63, 'Waxaana u Waxyoonay (Nabi) Muuse ku Garaac Ushaada Badda wayna kala Jeexantay Baddii wuxuuna Noqday Jeex kasta Buur wayn oo kale.'),
(2996, 26, 64, 'Waana ku soo Dhawayanay Halkaas kuwii kale (Colkii Fircoon).'),
(2997, 26, 65, 'Waxaana Korinay (Nabi) Muuse iyo Intii la Jirtay Dhammaan.'),
(2998, 26, 66, 'Ka dibna waxaan Maanshaynay Kuwii kale.'),
(2999, 26, 67, 'Arrintaasna waxaa ku Sugan Aayad Mana aha Badankood kuwo Rumeeyn.'),
(3000, 26, 68, 'Eebahaana waa Adkaade Naxariista,'),
(3001, 26, 69, 'Ku Akhri Korkooda Warkii Nabi Ibraahim.'),
(3002, 26, 70, 'Markuu ku Yidhi Aabihiis iyo Qoomkiisii maxaad Caabudaysaan.'),
(3003, 26, 71, 'Waxay Dheheen waxaannu Caabudi Sanamyo oonu ahaan kuwo ku Nagaada.'),
(3004, 26, 72, 'Wuxuu Yidhi (Nabi Ibraahim) Maydin Maqlayaan markaad Baryeysaan.'),
(3005, 26, 73, 'Mase idin Anfaci ama idin Dhibi.'),
(3006, 26, 74, 'Waxay Dheheen waxaan Hellay Aabayaalkanno oo Sidaas Fali.'),
(3007, 26, 75, 'Wuxuu Yidhi Bal Warrama waxaad Caabudaysaan.'),
(3008, 26, 76, 'Idinka iyo Aabayaalkiinii Horeeyey.'),
(3009, 26, 77, 'Hadday Iyagu Col ii Yihiin Eebaha Caalamka Mooyee.'),
(3010, 26, 78, 'Ee Eebaha I Abuuray ee ah kan I Hanuunin.'),
(3011, 26, 79, 'Kan I Quudin ee I Waraabin.'),
(3012, 26, 80, 'Markaan Bukoodana Isagaa I Caafiya.'),
(3013, 26, 81, 'Waana kan I dili Ina Noolayn.'),
(3014, 26, 82, 'Ee ah kaan Damci inuu Ii Dhaafo Gafafkayga Maalinta Abaal marinta.'),
(3015, 26, 83, 'Eebow i Sii Xikmo (Nabinimo) Ina Haleeshii kuwa Suuban.'),
(3016, 26, 84, 'Iina Yeel Sheegid Run ah (Fiican) kuwa Dambe.'),
(3017, 26, 85, 'Igana yeel kuwa Dhaxli Janada Naciimo.'),
(3018, 26, 86, 'Una Dambi Dhaaf Aabahay wuxuu ka Mid ahaa kuwa Dhumaye.'),
(3019, 26, 87, 'Hana i Dullayn Maalinta la Soo Bixin (Dadka).'),
(3020, 26, 88, 'Maalintayna wax Anfacayn Xoolo iyo Ilmo midna.'),
(3021, 26, 89, 'Ruux ula Yimaadda Eebe Qalbi Fayow Mooyee (Iimaan).'),
(3022, 26, 90, 'Waana loo Dhawayn Jannada kuwa Dhawrsada (Mu\'miniinta Dhabta ah).'),
(3023, 26, 91, 'Waana Loo Muujin Jaxiimo kuwa Baadiyoobay.'),
(3024, 26, 92, 'Waxaana Lagu Dhihi Aaway waxaad Caabudayseen.'),
(3025, 26, 93, 'Oo Eebe ka soo Hadhay Maydiin Gargaarayaan ama Gargaarsanayaan.'),
(3026, 26, 94, 'Waana lagu Hambooriyey (Tuuri) Dhexdeeda iyaga iyo kuwa Baadiyoobay.'),
(3027, 26, 95, 'Iyo Junuudda (Askarta) Ibliis Dhamaan.'),
(3028, 26, 96, 'Waxayna obhan iyagoo Dhexdeeda ku doodi.'),
(3029, 26, 97, 'Eebaan ku Dhaaranaye waxaan ku Sugnayn Baadi Cad.'),
(3030, 26, 98, 'Markaan Idilla Sinay Eebaha Caalamka.'),
(3031, 26, 99, 'Wax na Dhumiyey oon Dambiila Yaasha ahayn ma Jiro.'),
(3032, 26, 100, 'Mana Lihin wax Noo Shafeeca.'),
(3033, 26, 101, 'Iyo Saaxiib Dhaw.'),
(3034, 26, 102, 'Ee hadaan Noqonno oon Ahaanno Mu\'miniin.'),
(3035, 26, 103, 'Taas waxaa ku Sugan Calaamad Badankoodna ma Rumeeyo (Xaqa).'),
(3036, 26, 104, 'Eebahaana waa Adkaade Naxariista.'),
(3037, 26, 105, 'Waxay Beeniyeen Qoomkii (Nabi) Nuux Rasuulladii.'),
(3038, 26, 106, 'Markuu ku Yiidhi Walaalkood Nuux Miyeydaan Dhawrsanayn.'),
(3039, 26, 107, 'Anigu waxaan idiin ahay Rasuul Aamin ah.'),
(3040, 26, 108, 'Ee ka Dhawrsada Eebe ina Adeeca.'),
(3041, 26, 109, 'Idinkana Warsanmaayo (Xaqa) Ujuuro, Ajrigayga Eebaha Caalamkaan ka Sugi.'),
(3042, 26, 110, 'Ee Eebe ka Dhawrsada ina Adeeca.'),
(3043, 26, 111, 'Waxay Dheheen ma waxaanu ku Rumayn adooy ku Raaceen kuwa Dullaysan (Xun Xun).'),
(3044, 26, 112, 'Wuxuu Yidhi Maxaan ka Ogahay Waxay Fali.'),
(3045, 26, 113, 'Xisaabtooda Eebe unbaa leh haddaad wax Garanaysaan.'),
(3046, 26, 114, 'Anuguna ma Eryayo Mu\'miniinta.'),
(3047, 26, 115, 'Waxaan u Dige Cad ahayna ma ihi.'),
(3048, 26, 116, 'Waxay Dheheen Haddaadan Joogin Nuuxow Waxaad Noqon kuwa la Dhagaxyeeyo.'),
(3049, 26, 117, 'Wuxuuna yidhi Eebow Qoomkaygii waa I beeniyeen.'),
(3050, 26, 118, 'Ee Nakala Xukun Dhexdanada, ina kori Ani iyo Inta ila jirta ee Mu\'miniinta ah.'),
(3051, 26, 119, 'Waxaannu ku Korinay Isaga iyo intii la Jirtay Doontii Buuxday.'),
(3052, 26, 120, 'Ka Dibna waxaan Maanshaynay kuwii Hadhay (Gaaladii).'),
(3053, 26, 121, 'Taasna waxaa ku Sugan Calaamad mana aha Badankoodu kuwo Rumayn.'),
(3054, 26, 122, 'Eebahaana waa Adkaade Naxariista.'),
(3055, 26, 123, 'Waxay Beeniyeen Caad Rasuulladii.'),
(3056, 26, 124, 'Markuu ku Yidhi Walaalkood Huud Miyeydaan Dhawrsanayn.'),
(3057, 26, 125, 'Anigu waxaan idiin ahay Rasuul Aamin ah.'),
(3058, 26, 126, 'Ee ka Dhawrsada Eebe Ina Adeeca.'),
(3059, 26, 127, 'Idinkamana Warsanaayo Xaqa ujuuro, Ajrigaygana Eebaha Caalamkaan ka Sugi.'),
(3060, 26, 128, 'Miyaad ka Dhisaysaan Meel Sare oo Dhan Calaamad (Daar) Idinkoo Ciyaari.'),
(3061, 26, 129, 'Ood Yeelanaysaan Masaanic (Dhismo) inaad Waartaan Darteed.'),
(3062, 26, 130, 'Haddaad wax Qabataan Ciqaabtaan una Ciqaabaysaan si Daran.'),
(3063, 26, 131, 'ee Eebe ka Dhawrsada Ina Adeeca.'),
(3064, 26, 132, 'Kana Dhawrsada Eebaha idin Siiyey waxaad Ogtihiin.'),
(3065, 26, 133, 'Wuxuu idin Siiyey Xoolo iyo Caruur.'),
(3066, 26, 134, 'Iyo Beero iyo Ilo.'),
(3067, 26, 135, 'Waxaana Idiinka Digi Cadab Maalin Wayn.'),
(3068, 26, 136, 'Waxay Dheheen waxaa Isu kaanna Mid ah Waanisayaa ama Maadan Waanininaa.'),
(3069, 26, 137, 'Kanna ma aha waxaan Dabeecaddii «Diintii» Dadkii hore ahayn.'),
(3070, 26, 138, 'Nalamana Cadaabayo.'),
(3071, 26, 139, 'Wayna Beeniyeen, markasaan Halaagnay. Taasina waxaa ku Sugan Calaamad, mana aha Badankoodu kuwo Rumayn.'),
(3072, 26, 140, 'Eebahaana waa Adkaade Naxariista.'),
(3073, 26, 141, 'Waxay Beenisay Thamuud suulladii.'),
(3074, 26, 142, 'Markuu ku Yidhi Walaalkood (Nabi) Saalax Miyaydaan Dhawrsanayan.'),
(3075, 26, 143, 'Waxaan idiin ahay Rasuul Aamin ah.'),
(3076, 26, 144, 'ee ka Dhawrsadda Eebe ina Adeeca.'),
(3077, 26, 145, 'Idinkamana Warsanaayo Xaqa ujuuro, Ajrigayga Eebaha Caalamkaan ka Sugi.'),
(3078, 26, 146, 'Ma waxaa Laydinkaga Tagi Halkan idinkoo Aamina.'),
(3079, 26, 147, 'Oo ku Sugan Basaatiin iyo Ilo (Socda).'),
(3080, 26, 148, 'Iyo Beero iyo Timir Fiideedu Bisayl Tahay.'),
(3081, 26, 149, 'Ood ka Qoranaysaan Buuraha Guryo idinkoo Kibirsan.'),
(3082, 26, 150, 'Ee Eebe ka Dhawrsada ina Adeeca.'),
(3083, 26, 151, 'Hana Adeecina «Maqlina» Amarka Xadgudbayaasha.'),
(3084, 26, 152, 'ah kuwa Fasaadiya Dhulka oon Hagaajinayn.'),
(3085, 26, 153, 'Waxay Dheheen waxaad uun ka mid Tahay kuwa la Sixray.'),
(3086, 26, 154, 'Mana Tihid waxaan Dad nala mid ah ahayn ee noo keen Aayad Haddaad run Sheegi.'),
(3087, 26, 155, 'Wuxuu Yidhi Tani waa Hal, Maalinbay Cabbi idinkuna Maalinhaad Cabbaysaan la Yaqaan.'),
(3088, 26, 156, 'Ee ha ku Taabanina Xumaan, oos idin qabto Cadaab Maalin wayn.'),
(3089, 26, 157, 'Wayse Dileen waxayna ahaadeen kuwo Qoomameeya.'),
(3090, 26, 158, 'Waxaana Qabtay Cadaab Taasna waxaa ku Sugan Calaamad, Badankoodna ma aha kuwo Rumayn (Xaqa).'),
(3091, 26, 159, 'Eebahaana waa Adkaade Naxariista.'),
(3092, 26, 160, 'Waxay Beeniyeen Qoomkii (Nabi) Luudh Rasuuladii.'),
(3093, 26, 161, 'Markuu ku Yidhi Walaalkood Luudh Miyeydaan Dhawrsanayn.'),
(3094, 26, 162, 'Waxaan idiin ahay Rasuul Aaminee.'),
(3095, 26, 163, 'ee ka Dhawrsada Eebe ina Adeeca.'),
(3096, 26, 164, 'Idinkama Warsanaayo Xaqa Ujuuro, Ajrigayga Eebaha Caalamkaan ka Sugi.'),
(3097, 26, 165, 'Ma waxaad u Tagaysaan Ragga Caalamka.'),
(3098, 26, 166, 'Ood ka Tagaysaan waxa Eebe idiin Abuuray oo Haweenkiina ah, waxaadse Tihiin Kuwo Xadgudba.'),
(3099, 26, 167, 'Waxay Dheheen haddaadan Joogin Luudhow Waxaad Noqon kuwa la Bixiyo.'),
(3100, 26, 168, 'Wuxuu Yidhi (Luudh) Anigu Camalkiinna waan la Cadhaysnahay.'),
(3101, 26, 169, 'Eebow iga Kori iyo Ehelkaygaba waxay Falayaan.'),
(3102, 26, 170, 'Waana korinay Isaga iyo Ehelkiisiiba Dhammaan.'),
(3103, 26, 171, 'Habar ku hadhay Mooyee (Haweeneydiisii).'),
(3104, 26, 172, 'Markaasaan Halaagnay Intii kale.'),
(3105, 26, 173, 'Waxaa ku soo Daynay (Roob Dhagax Naara) waxaana u Xun Roobka kuwa Loo digay.'),
(3106, 26, 174, 'Taasna waxaa ku Sugan Calaamad, Badankoodna ma aha kuwo Rumayn.'),
(3107, 26, 175, 'Eebhaana waa Adkaade Naxariista.'),
(3108, 26, 176, 'Waxay Beeniyeen Kayntii Ehelkeedii Rasuulladii.'),
(3109, 26, 177, 'Markuu ku Yidhi Nabi Shucayb Miyeydaan Dhawrsanayn.'),
(3110, 26, 178, 'Waxaan idiin ahay Rasuul Aaminee.'),
(3111, 26, 179, 'ee ka Dhawrsada Eebe ina Adeeca.'),
(3112, 26, 180, 'Idinkama Warsanaayo Xaqa Ujuuro, Ajrigayga Eebaha Caalamkaan ka Sugi.'),
(3113, 26, 181, 'Oofiya (Dhammaystira) Beegidda hana ka mid Noqonina kuwa Nusqaamiya.'),
(3114, 26, 182, 'Una Miisama Cadaalad Toosan.'),
(3115, 26, 183, 'Hana ka Nusqaaminina Dadka waxooda hana Xumaynina Dhulka Idinkoo Fasaadin.'),
(3116, 26, 184, 'Kana Dhawrsada Eebaha idin Abuuray iyo Khalqigii Horreeyey.'),
(3117, 26, 185, 'Waxay Dheheen waxaad uun Tahay kuwa la Sixray.'),
(3118, 26, 186, 'Mana Tihid waxaan Dad nala mid ah ahayn, waxaana kuu Malaynaynaa kuwa Beenaalayaasha ah.'),
(3119, 26, 187, 'ee Nagu soo Rid Goosimo Samada ka mid ah haddaad Run sheegi.'),
(3120, 26, 188, 'Wuxuu Yidhi Eebahay baa og waxaad Falaysaan.'),
(3121, 26, 189, 'Wayna Beeniyeen waxaana Qabtay Cadaab Maalintii Hadhka, waana Cadaab Maalin Wayn.'),
(3122, 26, 190, 'Arrintaasna waxaa ku Sugan Calaamad mana aha Badankoodu mid Rumayn.'),
(3123, 26, 191, 'Eebahaana waa Adkaade Naxariista.'),
(3124, 26, 192, 'Quraankuna waa soo Dejin Ebaha Caalamka.'),
(3125, 26, 193, 'Waxaa kula soo degan Ruux Aamin ah (Jibriil).'),
(3126, 26, 194, 'Qalbigaaga inaad ahaato kuwa ku Diga.'),
(3127, 26, 195, 'Af Carabi ahoo Cad.'),
(3128, 26, 196, 'Quraankuna wuxuu ku Sugnaa Kutubtii hore.'),
(3129, 26, 197, 'Miyeyna Calaamad ugu Fillayn inuu Yaqaanno Culimadii Banii Israa\'iil/'),
(3130, 26, 198, 'Haddan ku soo Dejino Quraankan Cajamiga Qaarkiis.'),
(3131, 26, 199, 'oo uu ku Akhriyo Korkooda mayna Rumeeyeen.'),
(3132, 26, 200, 'Saasaan u Galinay Diidada Quluubta Dambiilayaasha.'),
(3133, 26, 201, 'Mana Rumaynayaan Xaqa Intay ka Arkaan Cadaab Daran.'),
(3134, 26, 202, 'Oos ugu Yimaaddo Cadaabku Kado Iyagoon Ogayn.'),
(3135, 26, 203, 'Oy Dhahaan ma nala Sugi.'),
(3136, 26, 204, 'Ma Cadaabkanagay Dadajisan.'),
(3137, 26, 205, 'Ka Warraan Haddaan u Raaxayno Sanooyin.'),
(3138, 26, 206, 'Markaas uu u Yimaaddo wixii loogu Goodinaayey.'),
(3139, 26, 207, 'Muxuu uga Tari Xaggooda wixii loogu Raaxeeyey.'),
(3140, 26, 208, 'Magaalo aan Halaagno oon loo Diginna ma Jirto.'),
(3141, 26, 209, 'Waano Darteed, mana nihin Daalimiin.'),
(3142, 26, 210, 'Kumana soo Degto Quraanka Shayaadiintu.'),
(3143, 26, 211, 'Kumana Habboona mana Karaan.'),
(3144, 26, 212, 'Shayaadiintu Maqalka waa ka Fogyihiin.'),
(3145, 26, 213, 'Ee ha Caabudin (Baryin) Eebe Miciisa Ilaah kale ood Noqoto kuwa la Cadaabo.'),
(3146, 26, 214, 'Una Dig Qaraabadaada Kuu sii Dhaw.'),
(3147, 26, 215, 'Una Raarici Garabkaaga (U Naxariiso) Ciddii ku Raacda oo Mu\'miniinta ah.'),
(3148, 26, 216, 'Hadday ku Cassiyaanna waxaad Dhahdaa anigu Bari baan ka ahay waxaad Falaysaan.'),
(3149, 26, 217, 'Talo Saaro Eebaha Adkaada ee Naxariista.'),
(3150, 26, 218, 'Ee ku Arka Markaad Kici.'),
(3151, 26, 219, 'Iyo Lagagadoonkaaga kuwa Tukan.'),
(3152, 26, 220, 'Eebana waa Maqle Og.'),
(3153, 26, 221, 'Ma idiinka Warramaa Ruuxay ku soo Degi Shayaadiintu.'),
(3154, 26, 222, 'Waxay ku soo Degi Beenaale Dambi Badan oo Dhan.'),
(3155, 26, 223, 'Waxayna Xadi Maqalka Badankoodna waa Beenaalayaal.'),
(3156, 26, 224, 'Gabayaana waxaa Raaca kuwa Baadida ah.'),
(3157, 26, 225, 'Miyaadan Arkin in Iyagu Tog kasta (Xumaan) ay ku Dhex Wareersanyihiin.'),
(3158, 26, 226, 'Oyna Sheegi waxayna Falayn.'),
(3159, 26, 227, 'Kuwa Xaqa Rumeeyey Mooyee oo Camal Fiican Fala oo Xusa Eebe wax Badan, oo Gargaarta Dulmi ka Dib, waxayna Ogaan doonaan kuwii Dulmi Falay soo Noqoshaday soo Noqdaan (Qiyaamada).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, '(Xarafka hore Isagoo kale waan soo Maray) Inuu ku Tusin Mucjisada Quraanka, Taasi Waa Aayaadka Quraanka iyo Kitaabka Cad.'),
(3161, 27, 2, 'Waana Hanuunka iyo Bishaarada Mu\'miniinta.'),
(3162, 27, 3, 'Ee ah kuwa Ooga Salaadda oo Guta Zakada oo Aakhiro Yaqiinin.'),
(3163, 27, 4, 'Kuwaanse Rumayn Aakhiro waxaan u Qurxinay Camalkooda waana ku Indho la\'Yihiin (Wareersanyihiin).'),
(3164, 27, 5, 'Kuwaasi waa Kuwuu u Sugnaaday Cadaab Xun Aakharaba Iyaga unbaa Khasaaray.'),
(3165, 27, 6, 'Adiguna Nabiyow waxaad kala Kulmi Quraanka Falka San ee Og Agtiisa.'),
(3166, 27, 7, 'Xusuuso Markuu ku Yidhi Nabi Muuse Ehelkiisa Anugu waxaan Arkaa Iftiin waxaan Idiinka Keeni Xaggiisa War Ama Waxaan Idiinka Keeni Dhuxul Daba inaad Kulaashaan.'),
(3167, 27, 8, 'Markuu Yimid waxaa Loogu Dhawaaqay Waa la Barakeevev Cidda Dabka la Joogta iyo kuwa Gaararkiisa ah, waxaana Nasahan Ilaaha Caalamka.'),
(3168, 27, 9, 'Muusow Anugu waxaan ahay Ilaaha Adkaada ee Falka San.'),
(3169, 27, 10, 'Ee Tuur Ushaada, Markuu Arkay Iyadoo Gilgilan Sida Maska wuu Jeedsaday Isagoo Carari, Mana soo Noqon, Muusow ha Cabsanin, kuma Cabsadaan Agtayda Rasuulladuye.'),
(3170, 27, 11, 'Ciddiise Dulmi Fasha markaas Wanaag ku Baddasha Xumaan ka Dib Anugu waxaan ahay Dambi Dhaafe Naxariista.'),
(3171, 27, 12, 'Gali Gacantaada Jeebkaaga waxay soo Bixi Iyadoo Cad Xumaan La\'aan (Waxayna ka mid yihiin) Sagaal Calaamo oo Fircoon iyo Qoomkiisa loo Muujiyey, waxayna ahaayeen Qoom Faasiqiina.'),
(3172, 27, 13, 'Markay u Timid Calaamaddannadii Iyadoo Cad waxay Dheheen Kani waa Sixir Cad.'),
(3173, 27, 14, 'Wayna Diideen Iyagooy Yaqiinsatay Naftoodu Dulmi iyo Isla Sarreeyn Darteed, ee Day Siday ahaatay Cidhibtii Mufsidiinta (Xumo Wadayaasha).'),
(3174, 27, 15, 'Waxaan siinay Daawuud iyo Sulaymaan Cilmi, waxayna Dheeheen Mahad Ilaahbaa iska leh ee ah kan Naga Fadilay wax badan oo Addoomdiisa Mu\'miniinta ah.'),
(3175, 27, 16, 'Wuxuuna Dhaxlay Sulaymaan Daawuud, wuxuuna yidhi Dadow waxaa nala Baray Codka Shimbiraha, waxaana nala siiyey wax kasta (oo Xukun ah) kaasina waa Fadliga Cad.'),
(3176, 27, 17, 'Waxaa Loo kulmiyey Sulaymaan Junuudiisii oo Jinni, Insi iyo Shimbiro leh, iyagoo iseeleelin.'),
(3177, 27, 18, 'Markay Yimaaddeen Togii Qudhaanjooy Gala Guryihiinna Yeyna idin Jajabin Sulaymaan iyo Junuudiisu Iyagoo idin Ogayn.'),
(3178, 27, 19, 'Markaasuu Muusooday Isagoo ku Qosli Hadalkeeda wuxuuna yidhi Eebow I waafaji inaan ku Mahdiyo Nicmadaad ugu Nicmaysay Ani iyo Waalidkay iyo Inaan Falo Wanaag Aad ka Raali tahay ina Gali Naxariistaada Addoomadaada Suuban Dhexdooda.'),
(3179, 27, 20, 'Wuxuu Fiirfiiriyey Shimbiribii (Sulaymaan) Wuxuuna Yidhi Maxaan la Arki La\'ahay Hud-Hudkii Mise kuwa Maqan buu ka Midyahay.'),
(3180, 27, 21, 'Waxaan Cadaabi Cadaab Daran ama waan Gawrici ama wuxuu iikeeni Xujo Cad.'),
(3181, 27, 22, 'Markaasuu Muddo Yar Maqnaa Markaasu Yidhi (Markuu Yimid) waxaan Ogaaday waxaadan Ogaanin, waxaana kaaga keenay ReeSaba War Yaqiina.'),
(3182, 27, 23, 'Waxaan Helay Haweeney Xukumaysa Iana Siiyey wax kasta (oo Xukun ah) waxayna Leedahay Carshi Wayn.'),
(3183, 27, 24, 'Waxaan Helay Iyada iyo Qoomkeedii oo u Sujuudi Qorraxda Eebe ka Sokow wuxuuna U qurxiyey Shaydaan Camalkooda wuxuuna ka Leexiyey Jidka Mana Hanuunsana.'),
(3184, 27, 25, 'May u Sujuudaan Ilaaha soo Bixiya waxa ku Qarsoon Samooyinka iyo Dhulka oo Og waxaad Qarinaysaan iyo waxaad Muujinaysaan.'),
(3185, 27, 26, 'Ilaahay Isaga Mooye Ilaah kale ma jiro, waana Eebaha Carshiga Wayn.'),
(3186, 27, 27, 'Wuxuuna Yidhi Waannu Eegi inaad Run Sheegi iyo inaad ka mid Tahay Beenaalayaasha.'),
(3187, 27, 28, 'La Tag Warqaddaaydan kuna Tuur Xaggooda kana Durug Xaggooda, markaas Fiiri waxay soo Celiyaan.'),
(3188, 27, 29, 'Waxayna Tidhi Maxaxeey waxaa La ii soo tuuray Warqad Sharaf leh.'),
(3189, 27, 30, '(Kuna Qoran) Waxay ka Timid Sulaymaan waxayna ku Billaabatay Magaca Eebaha Naxariis Guud iyo Mid Gaaraba Naxariista.'),
(3190, 27, 31, 'Ee Hayska Kay waynaynina iina Imaadda Idinkoo Muslimiina.'),
(3191, 27, 32, 'Waxayna Tidhi Madaxeey ii Ishaara Amarkayga ma Ihi Mid Goysa Amar Intaad ka Joogtaan.'),
(3192, 27, 33, 'Waxayna Dhaheen Annagu waxaan Nahay kuwo Xoog badan oo Geesinimo Badan, amarkase adaa leh ee Fiiri Waxaad Fari.'),
(3193, 27, 34, 'Waxay Tidhi Xaakimyadu Haday Magaalo ku Galaan (Xoog) way Fasaadiyaan waxayna ka yeelaan Ehelkeeda Sharafta leh kuwo Dullaysan Saasayna Falaan.'),
(3194, 27, 35, 'Waxaanse u Diri Hadyad waxaana Eegi waxay La soo Noqdaan kuwa la Diray.'),
(3195, 27, 36, 'Markay soo Gaadhay Hadyaddii (Nabi) Sulaymaan Yuu Yidhi ma waxaad ii Kordhinaysaa Xoolo, wuxuu I siiyey Eebe yaa ka khayr Roon wuxuu idin siiyey, Idinkaase ku Farxi Hadyadiina.'),
(3196, 27, 37, 'Ee U celi Waxaana ula Imaanaynaa Junuud (Col) ayna Awoodayn waxaaana ka Bixinaynaa Magaalada Iyagoo Dullaysan oo Ihaanaysan.'),
(3197, 27, 38, 'Wuxuuna Yidhi Qoomow Yaa ii Keena Carshigeeda (Kursigeeda) ka hor Imaatinkooda Iyagoo Muslim ah.'),
(3198, 27, 39, 'Wuxuuna yidhi Mid Xoog badan oo Jin ah anaa kuu Keeni Intaadan ka Kicin Fadhigaada, waxaana ahay Xoog badane Aamina.'),
(3199, 27, 40, 'Wuxuuse yidhi kay Agtiisa ahayd Ogaanshaha Kitaabka Anaa kuu keeni Ishaada Laabideeda horteed (Ilbidhiqsi) Markuu ku Arkay Agtiisa Isagoo ku Sugan wuxuu Yidhi Kani waa Fadliga Eebahay inuu Imtixaamo inaan ku Mahdiyo iyo Inaan ka Gaaloobo, Ciddii ku Mahadisa wuxuu uun u Mahdiyey Naftiisa Ciddii Gaalowdana Eebahay waa ka Kaaftoonyahay waana Sharafbadanyahay.'),
(3200, 27, 41, 'Wuxuuna Yidhi Dooriya Kursigeeda inay Garato iyo Inay Noqoto Cidaan Garanayn.'),
(3201, 27, 42, 'Markay Timid waxaa lagu Yidhi ma sidanaa Kursigaagu, waxayna Tidhi waxaad Mooddaa Isagii (Wuxuuna yidhi) waxaa nala Siiyey Horteed Cilmi waxaana nahay Muslimiin.'),
(3202, 27, 43, 'Waxaana (Xaqa) ka Reebay Waxay Caabudaysay oo Eebe ka soo Haddhay, waxayna ka mid ahayd Qoom Gaala ah.'),
(3203, 27, 44, 'Waxaa lagu Yidhi Gal Daarta Markay Aragtayna waxay U Malaysay Mawjad (Biya ah) waxayna ka Faydatay Dhudhumadeeda, wuxuuna ku yidhi waa Daar Salaax Sugan oo Quruurado ah, waxayna Tidhi Eebow anigu waxaan Dulmiyey Naftayda, waxaana u Islaamay la Jirka Sulaymaan Ilaahay Dartiis ee Eebaha Caalamka ah.'),
(3204, 27, 45, 'Waxaan u Dirray Ree Thammuud Walaalkood Saalax inay Eebe Caabudaan Markaasay soo Bexeen iyagoo Labo Kooxooda oo Doodi.'),
(3205, 27, 46, 'Wuxuuna Yidhi Qoomkayow maxaad u Dedejisanaysaan Xumaan Wanaag ka Hor, Maadse Dambi Dhaaf Warsataan Eebe inuu idiin Naxariisto.'),
(3206, 27, 47, 'Waxayna Dheheen waan ku Baasaysanay Adiga iyo Inta kula Jirta, wuxuuna ku Yidhi Baaskiinnu wuxuu Jiraa Eebe Agtiisa, waxaadse Tihiin Qoom la Fidneeyey.'),
(3207, 27, 48, 'Waxaana Magaalada ku Sugnaa Sagaal Qof oo Fasaadin Dhulka Hagaajinayna.'),
(3208, 27, 49, 'Waxayna Dheheen isku Dhaarta Eebe inaan mirro isaga iyo Ehelkiisaba Markaas aan ky Dhahno Qaraabadiisa Maanaan Joogin Meeju ku Halaagmay Ehelkiisu waxaana Sheegaynaa Run.'),
(3209, 27, 50, 'Way Dhagreen Dhagar Anana waan Abaal marinay Iyagoon Ogayn.'),
(3210, 27, 51, 'ee Day Siday Noqotay Cidhibtii Dhagartooda Anagaa Halaagnay Iyagii iyo Qoomkoodiiba Dhammaan.'),
(3211, 27, 52, 'Taasina wa Guryaahoodii oo Cidla ah Dulankoodii Dartiis, Arrintaasna Calaamad yaa ugu Sugan Qoomkii wax Ogaan.'),
(3212, 27, 53, 'Waxaana Korinay kuwii Rumeeyey oo Dhawrsanayey.'),
(3213, 27, 54, '(Nabi) Ludh (Xusuuso) markuu ku Yidhi Qoomkiisii ma waxaad la Imanaysaan Xumaan Idinkoo Arko.'),
(3214, 27, 55, 'Idinku ma Raggaad u Tagaysaan Doonid darteed Haweenka ka sokow, waxaadse Tihiin Qoom Jaahiliina.'),
(3215, 27, 56, 'Mana Noqonin Jawaabtii Qoomkiisa waxaan ahayn ka Bixiya Luudh iyo Ehelkiisa Magaaladiina waa Dad Isdaahirine.'),
(3216, 27, 57, 'Waana Korinay Isagii iyo Ehelkiisii Haweenaydiisii mooyee, waxaan ku Qaddarray (ka dhignay) kuwa Halaagsami.'),
(3217, 27, 58, 'Waxaana ku soo Daadinay korkooda Roob (Dhagax Naara) waxaana Xumaaday Roobka kuwa Loo Digay.'),
(3218, 27, 59, 'Waxaad Dhahdaa mahad Eebaa iska leh Nabadgalyana ha Ahaato Addoomadiis uu Doortay Korkooda, ma Eebaa Khayr roon mise waxay la Wadaajin.'),
(3219, 27, 60, 'Ma Eebaha Abuuray Samooyinka iyo Dhulka oo idiinka soo Dejiyey Samada Biyo oon ku soo Bixinay Beero Qurux Badan (Miyaa Khayrroon mise waxaad Caabudaysaan) ma kari kartaan inaad Soo bixisaan Geedkeeda, ma Ilaah kalaa jira Eebe Miciis, waase Qoom (xaqa) ka Leexaan.'),
(3220, 27, 61, 'Mise (waxaa Khayr roon) Eebaha ka Yeelay Dhulka Sugnaan, oo U yeelay wabiyo, buuro oo u Yeelay Badaha Dhexdooda Soohdin, ma Ilaah kalaa Jira Eebe Miciisa, Badankooduse wax ma Oga.'),
(3221, 27, 62, 'Mise Eebaha Aqbala ka Dhibban markuu Baryo oo Fayda Xumaanta (Dhibka) idinkana Yeeli kuwa Dhulka u Hadhi (Iska Baddala), ma Ilaah kalaa Jira Eebe Miciisa, Yaraa Intaad wax Xusuusanaysaan.'),
(3222, 27, 63, 'Mise Eebaha idin Toosiya Mugdiyada Barriga (Dhulka) iyo Badda Dhexdeeda, oo ah kan Dira Dabaylaha oo Bishaarayn Naxariista Eebe (Roobka) hortiisa, ma Ilaah kalaa Jira Eebe Miciisa, Eebaa ka Sarreeya waxay la Wadaajin.'),
(3223, 27, 64, 'Mise Eebaha Billaaba Khalqiga (Abuurkiisa) Haddana soo Celin oo ah kan Idinka Arzaaqa Samada iyo Dhulkaba, ma Ilaah (kalaa) Jira Eebe Miciisa, waxaad Dhahdaa Keena Xujadiinna Haddaad Run Sheegaysaan.'),
(3224, 27, 65, 'Waxaad Dhahdaa ma Oga waxa Samooyinka iyo Dhulka ku Sugan waxa Maqan Eebe mooyee, mana Oga Goorta la Soo Bixin.'),
(3225, 27, 66, 'Wuxuuse Gaadhay Cilmigoodi Aakhiro (Buuxsami) waxayse kaga Suganyihiin Xageeda Shaki, wayna ka Indho La\'yihiin Xaggeeda.'),
(3226, 27, 67, 'Waxay Dheheen kuwii Gaaloobay Markaan Noqonno Carro Anaga iyo Aabayaalkanno miyaa nala soo Bixin.'),
(3227, 27, 68, 'Waa Naloo Yaboohay Kan (soo Bixinta) Anaga iyo Aabayaalkanno mar hore, kanna ma aha waxaan Warkii Dadkii hore ahayn.'),
(3228, 27, 69, 'Waxaad Dhahdaa ku Socda Dhulka oo Daya siday Noqotay Cidhibtii Dambiilayaasha.'),
(3229, 27, 70, 'Hana u Murgin Korkooda, hana ka Cidhiidhyamin waxay Dhagri.'),
(3230, 27, 71, 'Waxayna Odhan waa Goorma Yaboohaasi Haddaad Run sheegaysaan.'),
(3231, 27, 72, 'Waxaad Dhahdaa waxaa Suurowda inay Dhawdahay waxaad Dedejisanaysaan Qaarkeed.'),
(3232, 27, 73, 'Eebahaana waa Fadli Siiyaha Dadka, Laakiin Badankoodu kuma Mahdiyaan.'),
(3233, 27, 74, 'Eebahaana wuxuu Ogyahay Waxay Qarin Laabahoodu iyo waxay Muuiin.'),
(3234, 27, 75, 'Waxa ku Maqan Samada iyo Dhulkana waxay ku Suganyihiin Kitaab Cad.'),
(3235, 27, 76, 'Quraankanna wuxuu ka Qisoon (Warrami) Banii Israa\'iil wixii Badnaa ay isku Khilaafeen.'),
(3236, 27, 77, 'Quraankuna waa Hanuun iyo Naxariista Mu\'miniinta.'),
(3237, 27, 78, 'Eebahaana wuxuu ku kala Xukumi Dhexdooda Xukunkiisa waa Adkaade Og.'),
(3238, 27, 79, 'ee Talo Saaro Eebe Adugu waxaad ku Sugantahay Xaq Cade.'),
(3239, 27, 80, 'Adugu wax ma Maqashiisid kuwa Dhintay mana Maqashiisid Dhagoolaha Dhawaaq markay Jeedsadaan iyagoo sii Socda.'),
(3240, 27, 81, 'Mana Tihid mid ka Toosin kara Indhoolaha Baadidiisa, waxna ma maqashiin kartid Ruux Rumeeyey Aayaadkanaga oo Muslima mooyee.'),
(3241, 27, 82, 'Markuu ku Dhaco Korkooda Qawlkii (Ciqaabtii) waxaan u soo Bixinnaa Daabbaddii Dhulka oo la Hadli Dadkuna waxay ahaayeen kuwaan Yaqiinsanayn Aayaadkanaga.'),
(3242, 27, 83, 'Xusuuso Maalintaan ka soo Kulminayno Ummad kasta Koox ka mid ah kuwii Beeniyey Aayadkanaga iyagoo is Cidhiidhyi.'),
(3243, 27, 84, 'Markay Yimaaddaana wuxuu ku Dhahaa Miyaad Beeniseen Aayaadkaygii idinkoon koobin Ogaansho, Maxaadse Fali Jirteen.'),
(3244, 27, 85, 'Waxaa ku Dhacey Qawlkii (Ciqaabtii) Dulmigooda Dartiis mana Hadlayaan.'),
(3245, 27, 86, 'Miyeyna Arkayn inaan uga Yeellay Habeenka inay ku Xasilaan, Maalintana Arag, Arrintaasna Calaamooyin yaa ugu Sugan Ciddi Rumayn (Xaqa).'),
(3246, 27, 87, 'Xusuuso Maalinta la Afuufi Suurka oos Argagixi waxa ku Sugan Samooyinka iyo Dhulka Cidduu Eebe Doono Mooyee, Dhammaanna way u Imaan Eebe Iyagoo Dullaysan.'),
(3247, 27, 88, 'Waxaadna Arkaysaa Buurihii ood u Malayn inay ingagantahay (Sugantahay) iyadoo u Socoto sida Daruuraha, waase Sancada Eebaha Sugay wax kasta, Eebana waa Ogaha waxaad Falayasaaan.'),
(3248, 27, 89, 'Ciddii la Timaadda Wanaag wuxuu Mudan wax ka Khayr roon Argaxa Maalintaasna Iyagu way Aamin Noqon.'),
(3249, 27, 90, 'Ciddiise la Timaadda Xumaan waxaa Loogu Tuuri Wajiga Naarta, miyaa Laydinka Abaal Mariya waxaad Falayseen Mooyee.'),
(3250, 27, 91, 'Waxaa uun Lay Faray inaan Caabudo Eebaha Baladkan (Maka) ee ah kan Xarrimay oo wax kastana iska leh, waxaana lay Faray inaan ahaado Muslimiinta.'),
(3251, 27, 92, 'iyo Inaan Akhriyo Quraanka Ruuxiise Hanuuna wuxuu uun u Hanuunay Naftiisa, Ruuxiise Dhuma waxaad dhahdaa Anugu waxaan uun ahay kuwa Diga.'),
(3252, 27, 93, 'Waxaadna dhahdaa Mahad Eebaa iska leh wuxuu idin tusin Aayaadkiisa oo Aqoonsanaysaan, mana aha Eebaha mid Halmaansan waxaad Falaysaan.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'Wuxuu ku Tusin Mucjisada Quraanka.'),
(3254, 28, 2, 'Taasina waa Aayaadka Kitaabka (Quraanka) ee Cad.'),
(3255, 28, 3, 'Waxaannu kugu Akhrin Korkaaga Warkii (Nabi) Muuse iyo Fircoon si Xaq ah Qoomkii Rumayn.'),
(3256, 28, 4, 'Fircoon wuu Isku Kibriyey Dhulka (Masar) wuxuuna ka Yeelay Ehelkiisii Kooxo, Isagoo Yaraysan (Dullayn) Koox ka Mid ah Gawricina Wiilashooda oo Dayn Haweenkooda wuxuuna ka mid ahaa kuwa wax Fasaadiya.'),
(3257, 28, 5, 'Waxaanu Dooni inaan ku Mannaysanno (Waxsiino) kuwii lagu Dullaysanayay Dhulka kana yeello Imaamyo, kana Yeello kuwa Dhaxli Dhulka.'),
(3258, 28, 6, 'Oon Makansiinno Dhulka, Tusinana Fircoon iyo Haamaan iyo Januuddooda waxay ka Digtoonaayeen (Halaagooda).'),
(3259, 28, 7, 'Waxaan u Waxyoonay ku (Ilhaamaynay) Muuse Hooyadiis Nuuji, markaad u Cabsatana ku Tuur Hirka (Badda) hana Cabsan hana Murugoon anagaa kugu soo Celine kana Yeeli kuwa la Diro (Rasuul).'),
(3260, 28, 8, 'Waxaana Qaalay Fircoon Dadkiisii Isagoo u Noqon doona Col iyo Walbahaar, Fircoon iyo Haamaan iyo Colkoodiina waxay Ahaayeen kuwo Gefay.'),
(3261, 28, 9, 'Waxayna Tidhi Haweenaydii Fircoon waa Raaxada Ishayda iyo taada ee ha Dilina, waxay u Dhawdahay inuu ina Anfaco ama aan ka yeelanno Wiile, mana Oga Iyagu (Dhabta).'),
(3262, 28, 10, 'Wuxuu Ahaaday Qalbigii Muuse Hooyadiis mid Madhan waxayna u Dhawaatay inay Muujiso Haddaanaan Adkaynin Qalbigeeda inay ahaato Mu\'miniinta.'),
(3263, 28, 11, 'Waxayna ku Tidhi Walaashiis Raac, waxayna ka eegaysay Dhinac Fog, iyagoon ogayn.'),
(3264, 28, 12, 'Waxaana ka Reebnay Korkiisa Nuugmada Horay, waxayna Tidhi Gabadhii Maydin Tusiyaa Qoys idiin Dhaqaalayn una Samo Fali.'),
(3265, 28, 13, 'Waxaana ku soo Celinay Hooyadiis, si ay u Raaxaysato Isheedu una Murgin, Ogaatana in Yabooha Eebe Yahay Xaq Laakiin Dadka Badankiisu ma Oga.'),
(3266, 28, 14, 'Markuu Gaadhay (Nabi) muuse Xooggiisa oo Ekaaday Yaan siinay Xigmo iyo Cilmi, Saasaana ku abal Marinaa Sama Falayaasha.'),
(3267, 28, 15, 'Wuxuuna Galay Magaaladii Waqti aan Dadkeedu ogayn, wuxuuna Helay Labo Nin oo Dagaallami, oo Kaasi Yahay Qaraabadiisa kaasna Yahay Colkiisa, waxaana u Kaalmo warsaday kii Qaraabadiisa ahaa Colkiisa (Korkiisa) wuuna Feedhay Muuse wuuna Dilay, wuxuuna Yidhi waa Camal Shaydaan, Isaguna waa Col Baadiyeeya oo Cad.'),
(3268, 28, 16, 'Wuxuu Yidhi Eebow Anigu waxaan Dulmiyey Naftayda ee ii Dambi Dhaaf wuuna u Dambi Dhaafay Illeen Ilaahay waa Dambi Dhaafe Naxariistee.'),
(3269, 28, 17, 'Wuxuuna Yidhi Eebow Nicmadaad ii Nicmaysay Darteed Yaanan Noqonin mid u Kaalmeeya Dambiilayaasha.'),
(3270, 28, 18, 'Wuxuu ku Waabariistay Magaaladii Isagoo Cabsan oo Sugi (Dhib) Markasuu ku soo Baxay kii Gargaarka Warsaday Shalay oo Qaylyi, wuxuuna ku yidhi Muuse Adugu waxaad tahay baadi cad.'),
(3271, 28, 19, 'Markuu Doonay inuu Qabto (Wax yeelo) kii Colka u ahaana wuxuu yidhi (kii loo gargaarayey) Muusow ma waxaad dooni inaad I disho Sidaad u Dishay Naf (Kale) Shalay, ma Doonaysid waxaan Jabbaar (Kibir badane) ahayn Dhulka ku Noqoto, mana Doonaysid inaad Noqoto kuwa wax Wanaajiya.'),
(3272, 28, 20, 'Waxaana ka Yimid Magaalada Xageeda God Nin Ordi oo leh Muusow, Qoomku waxay ku Tashanayaan inay ku Dilaan ee Bax Anuguna waan kuu Naseexayne.'),
(3273, 28, 21, 'Wuuna ka Baxay (Magaaladii) Isagoo Cabsan oo Filan (waxyeello) wuxuuna yidhi Eebow iga Kori Qoomka Daalimiinta ah.'),
(3274, 28, 22, 'Markuu U jahaystay madyan Xaggeedana wuxuu yidhi wuxuu u Dhawyahay Eebahay inuu igu (Hanuuniyo) jidka Toosan.'),
(3275, 28, 23, 'Markuu Gaadhay Biyaha Madyanna wuxuu ku Helay Ummad Dad ahoo Waraabinaysa wuxuuna ka helay Sokodooda Labo Haweena oo Reebi (Adhigooda) wuxuuna yidhi muxuu Xaalkiinu yahay, waxayna Dheheen Ma Waraabinno intay ka Fulaan Xoola jirku, Aabbahanana waa Oday Wayn.'),
(3276, 28, 24, 'Markaasuu u Waraabiyey wuxuuna u Jeedsaday Xagga Hooska wuxuuna Yidhi Eebow waxaad ii soo Dejiso (Isiiso) oo Khayra waan u Baahnahay.'),
(3277, 28, 25, 'Markaasay u Timid Gabdhihii Middood Iyadoo Xishoon waxayna ku Tidhi Aabahay wuu kuu Yeedhi inuu kaa Abaal Mariyo Ujuuradii Waraabintaada Annaga, markuu u Yimid oos Uga Warramay Qisadii yuu ku yidhi ha Cabsan Waad ka Kortay Qoomkii Daalimiinta ahaa.'),
(3278, 28, 26, 'Waxayna Tidhi Middood Aabbow noo Ijaar, Illeen waxaa u Khayr roon Cid la Ijaarto Mid Xoog Badan oo Aamin ah.'),
(3279, 28, 27, 'Wuxuuna ku Yidhi Anugu waxaan dooni inaan kuu Guuriyo Labadaydan Gabdhood Middood inaan ku Ijaarto Siddeed Sano Haddaad Dhamayso Tobankana waa Adiga Agtaada, mana Doonayo inaan ku Dhibo waxaana igu Ogaan Hadduu Eebe Doono kuwa Wanaagsan.'),
(3280, 28, 28, 'Wuxuuna Yidhi Kaasi waa Ballan Dhexdeenna ah, Labada muddo Kaan Dhammaystana dhib iguma aha, Eebana waxaan Sheegayno waa u Wakiil.'),
(3281, 28, 29, 'Markuu Dhammeeyey (Nabi) Muuse Muddadii oo la Tegay Ehelkiisii wuxuu ku Arkay Dhinaca Dhuur Dab, wuxuuna ku Yidhi Ehelkiisii Suga waxaan Arkaa Dabe waxaan u Dhawahay inaan Idiinka Keeno War ama Dhuxul Daba inaad Kulaashaan.'),
(3282, 28, 30, 'Markuu Yimidna waxaaa Looga Dhawaaqay Togga Dhiniciisa Midig, Meejii Barakaysnayd oo Geedka (Isagoo la Yidhi) Anugu Ilaahay Baan ahay Eebaha Caalamka ah.'),
(3283, 28, 31, 'Ee Tuur Ushaada Markuu Arkay iyadoo Gilgilanna ood Mooddo Mas wuu Jeedsaday Isagpp Carari, mana soo Jeedan, (waxaana lagu Yidhi) Muusow soo Qaabil (soo Noqo), hana Cabsan Adugu Aaminbaad tahay (waad nabadgali).'),
(3284, 28, 32, 'Gali Gacantaada Jeebkaaga waxay so Bixi Iyadoo Cad Xumaan (cudur) La\'aan, Iskuna Dhaji Garabkaaga Argagaxa Dartiis taasina waa Xujooyin Eebe ka ahaatay oo Fircoon iyo Qoomkiisa (Loo diray) waana Qoom Faasiqiina.'),
(3285, 28, 33, 'Wuxuu Yidhi Eebow waxaan ka Dilay Naf waxaana ka Cabsan inay I dilaan.'),
(3286, 28, 34, 'Walaalkay Haaruunbaana iga Fasiixsan Carrabka (Hadalka), ee Ila Dir Xoojin ha ii Rumeeyee, waxaan ka Cabsan inay I beeniyaan.'),
(3287, 28, 35, 'Wuxuu yidhi (Eebe) waannu ku Xoojin Cududdaada Walaalkaa waxaana idiin yeeli Xujo Idimana Gaadhayaan (Dhibkiinna) aayaadkanaga Dartood, Idinka iyo Inta idin Raacday yaana Adkaan.'),
(3288, 28, 36, 'Markuu Ula Yimid muuse Aayaadkanaga oo Cad Cad waxay Dheheen kani waa Sixir la Abuurtay mana Aanaan ku maqal kan (oo kale) Aabbayaalkannagii horreeyey.'),
(3289, 28, 37, 'Wuxuuna yidhi Nabi Muuse Eebahaybaa og Cidda kala Timid Hanuun Agtiisa iyo Cidda kala Timid Hanuun Agtiisa iyo Cidday u ahaan Cidhibta dambe illeen ma Liibaano daalime.'),
(3290, 28, 38, 'Wuxuu Yidhi Fircoon Maxaxeey Idiinma Ogi Eebe Iga soo Hadhay ee ii Huri Haamaanow Dhoobada iina Yeel Daar (dheer) inaan Daalacdo Ilaaha Muuse waxaan u Malayn inuu Beenaale yahaye.'),
(3291, 28, 39, 'Waxay Isku Kibriyeen isaga iyo Colkiisiiba Dhulka Xaq Darro waxayna u Maleeyeen inaan Xagganaga loo soo Celinayn.'),
(3292, 28, 40, 'Waana Qabanay Isaga iyo Colkiisii waxaana ku Turray Badda ee Day Siday Noqotay Cidhibtii Daalimiinta.'),
(3293, 28, 41, 'waxaana ka Yeellay Madax ugu Yeedha Naarta, Maalinta Qiyaamana looma Gargaaro.'),
(3294, 28, 42, 'Waxaana Raacinay Adduunayadan Lacnad, Maalinta Qiyaamana waxay noqon kuwa la dheereeyey (La halaagay).'),
(3295, 28, 43, 'Waxaan Siinay (Nabi) Muuse Kitaabka ka Dib Intaan halaagnay Quruumihii hore, Isagoo Aragti u ah Dadka iyo Hanuun iyo Naxariis inay Xusuustaan Darteed.'),
(3296, 28, 44, 'Maadan ahayn Dhinaca Galbeed ee Buurta markaan Sinnaynay Muuse Amarka «Nabinimada» mana aadan ahay Kuwii Joogay.'),
(3297, 28, 45, 'Laakiin waxaan ahaysiinay Quruumo waxaana ku dheeraday Cimrigii, mana aadan ahayn mid ku nagaa Ehelkii Madyan Adoo ku Akhrin Korkooda Aayaadkanaga Laakiin anagaa ku Dirray.'),
(3298, 28, 46, 'Mana aadan ahayn Dhinicii (Buurta) Dhuur markaan u Dhawaaqnay Laakiin waa naxariista Eebahaa Inaad u Digto qoom uusan u Imaan Dige kaa Horreeyey inay Waantoobaan.'),
(3299, 28, 47, 'Hadday markay ku Dhacdo Musiibo (Dhib) waxay hormarsadeen Gacmahoodu Darteed oo Markaas ay Dhahaan Eebow Maxaad noogu soo Diri wayday Rasuul oo Raacno Aayaadkaaga Noqonnana Mu\'miniinta «waa la Ciqaabi lahaa».'),
(3300, 28, 48, 'Markuu uga yimid Xaqii Xagganaga yey Dheheen maa la Siiyo wixii la Siiyey (Nabi) Muuse oo kale, miyeyna ka Gaaloobin wixii la Siyyey Muuse Horay, oyna Dhihin waa Labo Sixir oo Iskaalmaystay waxayna dheheen Annagu Dhammaan waan ka Gaalownay.'),
(3301, 28, 49, 'Waxaad Dhahdaa ka Keena Kitaab Xagga Eebe ka Hanuun badan Labadooda aan raacnee haddaad Run Sheegaysaan.'),
(3302, 28, 50, 'Haddayna ku Ajiibin (Maqlin) Ogow inay uun Raacayaan Hawadooda, wax ka Dhumid badanna ma jirto cid Raacday Hawadeeda Hanuun Eebe La\'aantiis, Eebana ma Hanuuniyo Qoom Daalimiin ah.'),
(3303, 28, 51, 'Dhah ahaanbaan ugu Xidhiidhinay (ugu Caddayn) Hadalka inay Waantoobaan.'),
(3304, 28, 52, 'Kuwaannu Siinay Kitaabka Hortiis way Rumayn isaga.'),
(3305, 28, 53, 'Marka lagu Akhriyo Korkoodana waxay Dhahaan waan Rumaynay Quraanka inuu Xaq Eebaheen ka Yimid yahay Annaguna waxaan ahayn hortiis Muslimiin.'),
(3306, 28, 54, 'Kuwaas waxaa la Siiyey Ajrigooda Labo mar Samirkoodii Dartiis, waxayna Raaciyaan Xumaanta Wanaag waxaan ku Arzugnayna waxbay ka Bixiyaan.'),
(3307, 28, 55, 'Markay Maqlaan hadal Macno Darrana way ka Jeedsadaan waxayna Dhahaan Annagu Camalkanaga yaan leenahay idinna Camalkiinna Waad Salaamantihiin ma Rabno Jaahiliin (Waddadooda).'),
(3308, 28, 56, 'Adugu ma Hanuunisid Ciddaad Jeceshahay laakiin Eebaa Hanuuniya Cidduu Doono Isagaana Og kuwa Hanuunsan.'),
(3309, 28, 57, 'Waxay Dheheen Gaaladii (Maxaad) Haddaan Raacno Hanuunka waxaa Nalaga Dafi Dhulkannaga, Miyaanaan Siinin Xaram Aamina oo la Keeno Midhaha wax Kasta quudin Darteed oo Xagganaga ah, Laakiin Badankood ma Oga.'),
(3310, 28, 58, 'Badanaa intaan Halaagnay Magaalo ku Kibirtay Nolasheeda waana Taas Guryahoodii lamana Degin Gadaashood wax yar mooyee, Anagaana ah kuwo Dhaxla (Hadha).'),
(3311, 28, 59, 'Mana aha Eebahaa mid Halaaga Magaalo intuu uga Bixiyo Tan Hooyada u ah «Caasimaadda» Rasuul ku Akhriya Korkooda Aayaadkanaga mana nihin kuwo Halaaga Magaalo aan Ehelkeedu Daalimiin ahayn.'),
(3312, 28, 60, 'Wixii Laydin Siiyey oo Wax ah waa uun Raaxada Nolasha Dhaw iyo Quruxdeed waxase Eebe Agtiisa ah yaa Khayr Roon oo Hadhid badan ee Miyeydaan wax Kasayn.'),
(3313, 28, 61, 'Ruuxan u Yaboohnay Yabooh Fiican oo La Kulmi ma la Midbaa Ruuxaan ugu Raaxaynay Nolosha Dhaw markaas noqon Maalinta Qiyaame kuwa la Soo Joojin (La Cadaabi).'),
(3314, 28, 62, 'Xusuuso Maalinta Eebe u Yeedhi Gaalada oo ku Dhihi Aaway kuwaad ila Wadaajiseen ah kuwaad Sheeganayseen (Caabudayseen).'),
(3315, 28, 63, 'Kuwii waxay Dheheen uu ku Sugnaaday Hadalkii Eebe (Ciqaabtii) Eebow kuwaasu waa kuwaan Baadinyeynay, waana Baadiyeynay sidaan u Baadiyoownay waana isagakaa Baryeelay mana ahayn kuwo Anaga Nacaabuda.'),
(3316, 28, 64, 'Waxaana loo Odhan Uyeedha Shurukadiinna markaasay u Yeedhaan mase Ajiibaan (Maqlaan) waxayna arkaan Cadaabka Markaasay Qoomamoodaan Hadday Hanuunilahaayeen.'),
(3317, 28, 65, 'Xusuuso Maalintaan u Yeedhayno Gaalada oos ku Odhan Eebe Maxaad ugu Jawaabteen Rasuulladii.'),
(3318, 28, 66, 'Markaasay ku Indho Beeshay korkooda Xujadii (Waayeen Xujo) Maalintaas, waxna Isma Warsadaan.'),
(3319, 28, 67, 'Ruuxiise Toobadkeena oo Rumeeya oo Fala Camal Fiican wuxuu u Dhawyahay inuu Noqdo kuwa Liibaanay.'),
(3320, 28, 68, 'Eebahaa wuxuu Abuuraa wuxuu Doono oo Doorto, Doorasho ma Leh Iyagu (Dadku) waa ka Nasahanyahay Eebe waana ka Sarreeyaa waxay la Wadaajin.'),
(3321, 28, 69, 'Eebahaana waa Ogyahay waxay qarin Laabtoodu iyo waxay Muujinba.'),
(3322, 28, 70, 'Eebana waa Ilaaha isaga mooyee aan Ilaah kale Jirin, Mahad buu mutaa Adduun iyo Aakhiraba, Xukunkana isagaa iska leh Xaggiisaana loo Noqon.'),
(3323, 28, 71, 'Waxaad Dhahdaa bal Warrama Hadduu ka Yeelo Eebe Korkiinna Habeen Joogta ah tan iyo Qiyaamada waa Kee Eebaha ka soo Hadhay Ilaahay ee idiin keeni Nuur miyaydaan wax Maqlayn.'),
(3324, 28, 72, 'Waxaadna Dhahdaa bal Warrama Hadduu ka Yeelo Eebe Korkiinna Maalin Joogta ah tan iyo Qiyaamada waa kee Eebaha ka soo Hadhay Ilaahay ee idiin keeni Habeen aad ku Xasishaan Dhexdiisa miyeydaan wax arkayn.'),
(3325, 28, 73, 'Naxariistiisa waxaa ka mid ah inuu idiin yeelay Habeen iyo Dharaarba inaad Xasishaan dhexdiisa Dalabtaanna Fadligiisa iyo inaad ku Shugridaan.'),
(3326, 28, 74, 'Xusuuso Maalintaan u Yeedhayno Gaalada oo Eebe odhana Aaway Shurakadaydii ah kuwaad Sheeganayseen.'),
(3327, 28, 75, 'Waxaana ka soo Siibnaa ummad kasta Markhaati waxaana ku nidhi keena Xujadiinna waxayna Ogaadaan in Xaqa Eebe Leeyahay waxaana ka Dhumay Xagooda waxay Been Abuuran Jireen.'),
(3328, 28, 76, 'Qaaruunna wuxuu ka mid ahaa qoomkii (Nabi) muuse, wuuna ku Kibray Korkooda, waxaana Siinay wax Furayaalkiisu ku Culustahay Koox Xoog badan, markay ku Dheheen qoomkiisii ha Farxin (Kibrin) Eebe ma Jeela kuwa Farxa «Kibree».'),
(3329, 28, 77, 'Kuna doon waxa Eebe ku siiyey Guriga Aakhiro hana Halmaamin qaybtaada Adduunka Samana Fal sida Eebe kuugu Samo Falay hana La doonin Fasaad Dhulka, Eebe ma Jeela Fasaadiyaashee.'),
(3330, 28, 78, 'Wuxuuna Yidhi waxaa uun La ii siiyey Xoolaha Ogaanshahayga, Miyuusan ogayn in Eebe Halaagay Hortiis Quruumihii kuwo ka daran Xagiisa Xoogga kana Badan Kulan, lamana Warsado Dambigooda Dambiilayaasha.'),
(3331, 28, 79, 'Markaasuu u soo Baxay Qoomkiisii isagoo Xarragoon waxayna Dheheen kuwa Dooni Nolosha Dhaw Shallaytee Hadaan leenahay waxa la siiyey Qaaruun oo kale waa Nasiib wayn.'),
(3332, 28, 80, 'Waxayna Dheheen kuwii la Siiyey Cilmiga Magaciinba\'ee Abaal marinta Eebaa u Khayr roon Ciddii Rumaysa oo Fasha Camal fiican lamana Waafajiyo hadalkaas Saabiriinta Mooyee.'),
(3333, 28, 81, 'Markaasaan la Goynay Isagii iyo Xoolihiisiiba Dhulka mana Helin Koox uga Gargaarta Eebe ka Sokow, mana Noqonin mid Gargaarta.'),
(3334, 28, 82, 'Waxayna ahaadeen kuwii Doonayey Meejiisa Shalay inay Dhahaan. waa Yaabe Illeen Eebaa u Waasiciyey Rizqiga Cidduu Doono oo Addoomadiisa ah kuna Cidhiidhya, Hadduusan Eebe Nagu Galladaysan wuxuu nala Goynlahaa (Dhulka) waa Yaab ma Liibaanto Gaalo.'),
(3335, 28, 83, 'Taasi waa Daartii Aakhiro waxaana u Yeelay kuwaan Doonayn isla Sarraynta (Kibirka) Dhulka iyo Fasaad, cidhibta (Fiicanna) waxaa leh kuwa Dhawrsada.'),
(3336, 28, 84, 'Ciddii la Timaadda Wanaag wuxuu Mudan wax ka khayr roon Ciddiise la Timaadda Xumaan lagama Abaal mariyo kuwa Xumaanta Sameeya, waxaan waxay faleen ahayn.'),
(3337, 28, 85, 'Eebaha kugu Faral yeelay Quraanka wuxuu kuu soo Celin Meejii Ballanka (Qiyaamada ama Maka), waxaad Dhahdaa Eebahaybaa og ruuxii la Yimid Hanuun iyo kan ku Sugan Baadi Cad.'),
(3338, 28, 86, 'Mana Aadan ahayn mid Rajayn in lagugu soo dejiyo Kitaabka, Hasayeeshee waa Naxariista Eebahaa ee ha Noqonin mid u Kaalmeeya Gaalada.'),
(3339, 28, 87, 'Yeyna kaa leexin Aayaadka Eebe intii lagugu soo Dejiyey ka Dib uguna Yeedh Xagga Eebahaa, hana ka mid noqonin Mushrikiinta.'),
(3340, 28, 88, 'Hana Caabudin Eebe la Jirkiisa Ilaah kale Eebbe mooyee Ilaah (kalana) ma Jiro, wax kasta wuu Halaagsami (Tagi) Eebe mooyee Xukunkana Isagaa iska leh, Xaggiisaana laydiin Celin.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'waxay ku tusin mucjisada Quraanka (sida la soo sheegay).'),
(3342, 29, 2, 'ma wuxuu u maleeyey Dadku in loogaga tagi inay dhahaan waan rumeynay oon la imtixaamayn.'),
(3343, 29, 3, 'dhab ahaan yaan u imtixaanay kuwii ka horreeyay, Eebana wuu muujin kuwii run sheegay, wuxuuna muujin kuwa beenlowga ah.'),
(3344, 29, 4, 'mise waxay u maleeyeen kuwii xumaanta samaynayey inay naga dheereyn (carari) waxaa xun waxay xukumi.'),
(3345, 29, 5, 'ruuxii rajayn (ka cabsan) la kulanka Eebe, Ajashii Eebe (uu qadaray) way imaan, Eebana waa maqle oge ah.'),
(3346, 29, 6, 'ruuxii dadaala wuxuu uun u dadaalay naftiisa, Eebana waa ka kaaftoon yahay caalamka.'),
(3347, 29, 7, 'kuwa (xaqa) rumeeyey camalfiicanna falay waxaan asturaynaa xumaantooda, waxaana ka abaalmarinaynaa kii u fiicnaa ay camalfali jireen.'),
(3348, 29, 8, 'waxaan u dardaaaranay Dadka Labadiisii Waalid wanaagfalkooda, hadday kugula dadaalaan inaad ila wadaajiso waxaadan cilmi u lahayn (Gaalowdo) ha ku adeecin, xaggayga unbaa laydiin soo celin, waxaana idiinka warrami (abaalna marin) waxaad camalfali jirteen.'),
(3349, 29, 9, 'kuwiise (xaqa) rumeeyey camalfiicanna falay waxaan dhexgalinaynaa kuwa suuban (Janada).'),
(3350, 29, 10, 'Dadka waxaa ka mid ah ruux dhihi waxaan rumeynay Eebe, markii lagu dhibo Eebe dartiisna kayeela imtixaamka Dadka (dhibookda) cadaabka Eebe oo kale, hadday timaado Guul xagga Eebe ka ahaatay waxay dhahaan anagu waan idin lajiraa, Eebe miyuusan ahayn kan og waxa laabta Dadka ku jira.'),
(3351, 29, 11, 'dhab ahaan yuu u muujin Eebe kuwa xaqa rumeeyey, dhab ahaanna wuxuu u muujin kuwa munaafiqiinta ah.'),
(3352, 29, 12, 'waxay ku dhaheen kuwii galoobay kuwii xaqa rumeeyey raaca waddadannada aan xambaarro gafafkiinee, mana aha kuwo xambaari waxa gafafkooda ah, ee waa beenaalayaal.'),
(3353, 29, 13, 'waxayse dhab ahaan xambaari culaysyaalkooda iyo culaysyo Culaysyadoodii la jira, waxaana dhab ahaan loo warsan maalinta Qiyaame waxay been abuuran jireen.'),
(3354, 29, 14, 'dhab ahaan baan ugu dirray (Nabi) Nuux Qoomkiisii, wuxuuna ku dhexnagaaday Kun sano oo kontan la\'waxaana wabtay dhuufaankii (Halaagii Biyaha) iyagoo daalimiin ah.'),
(3355, 29, 15, 'waana korinay Nuux iyo Dadkii Doonta, waxaana ka yeellay aayad (uu ku waantoomo) Caalamku.'),
(3356, 29, 16, '(Nabi) Ibraahimna (waa diray) wuxuuna ku yidhi qoomkiisii Cabuda Eebe akana dhawrsada saasaa idiin khayrbadane haddaad wax ogtihiin.'),
(3357, 29, 17, 'waxaad uun caabudaysaan Eebe ka sokow Sanamyo, waxaadna abuuraysaan been, kuwaad caabudaysaan Eebe ka sokow idiinma hantaan wax rizqi ah ee ka dalba Eebe rizqiga Cabudana kuna mahdiya xaggiisaa laydin celine.'),
(3358, 29, 18, 'haddaad Beenisaan (Xaqa) waxaa beeniyey Umado idinka horreeyey Rasuulkana ma saarra waxaan gaadhsiin cad ahayn.'),
(3359, 29, 19, 'miyeyna arkayn sida Eebe u billaabay (Abuuridda) Khalqiga una soo celin, arrintaasna Eebe waa u fududahay.'),
(3360, 29, 20, 'waxaad dhahdaa socda Dhulka oo daya sida Eebe u biilaabay khalqiga, hadana Eebe u ahaysiin abuuridda dambe (Soo bixinta), Eebana wax walba wuu karaa.'),
(3361, 29, 21, 'wuxuu cadaabaa cidduu doono wuxuuna u naxariistaa cidduu doono xaggiisaana laydiin celin.'),
(3362, 29, 22, 'idinkuna ma tihidin kuwa Eebe ku daaliya Dhulka iyo Samada midna wax Eebe ka soohadhay oo idiin sokeeya ah ama idiin gargaarina ma jiro.'),
(3363, 29, 23, 'kuwii ka gaaloobay aayaadka Eebe iyo la kulankiisa kuwaasi waxay kaquusteen naxariistayda waxaana kuwaas u sugnaaday Cadaab daran.'),
(3364, 29, 24, 'Jawaabtii Qoomkiisuna waxaad ahayn Dila ama Guba ma ahayn, markaasaa Eebe ka koriyey Dabkii, arrintaasna waano yaa ugu sugan Cidii wax rumeyn.'),
(3365, 29, 25, 'wuxuuna ku yidhi (Nabi Ibraahim) waxaad yeelateen Eebe ka sokow Sanamyaal Jacayl dhexdiinna ah dartiis Nolosha adduunyo, Maalinta Qiyaamese wuu diidi qaarka kale Lacanadina qaarkin qaarka kale, Hoygiinuna waa Naarta wax idiin gargaarina idiin ma sugnaan.'),
(3366, 29, 26, 'waxaa rumeeyey Nabi Ibraahim (Nabi) Luudh wuxuuna yidhi Nabi Ibraahim waxaan u hijroon Eebehay illeen waa adkaade falsane.'),
(3367, 29, 27, 'waxaana siinay isxaaq iyo yacquub waxaana yeelay faraciisii Nabinimo iyo Kitaab (loo soodajiyey) waxaana siiney ajrigiisii adduunyada dhexdeeda aakhirana wuxuu ka mid noqon kuwa suuban.'),
(3368, 29, 28, '«Nabi» Luudhna «xusuuso» markuu ku yidhi qoomkiisii waxaad latimaadeen xumaan aan cidna idiinkahormarin addunyada.'),
(3369, 29, 29, 'idinku ma waxaad u tagaysaan Ragga ood goynaysaan Jidka Kulana imaanaysaan Naadigiina wax la naco, mana noqonin qoomkiisii jawaabtoodii inay dhahaan noo keen cadaabka Eebe hadaad ka Mid tahay runlayaasha mooyee.'),
(3370, 29, 30, 'wuxuuna yidhi Eebow iiga gargaar qoomka wax fasaadiya.'),
(3371, 29, 31, 'markay ulatimid fariintanadii (Malaa\'igtii) Nabi Ibraahim bishaaro waxay dhaheen anagu waxaan halaagayna ehelka magaaladan, ehelkeedu waa daalimiine.'),
(3372, 29, 32, '(wuxuu yidhi Nabi Ibraahim) waxaa ku sugan Luudh, waxay dhaheen anagaa og cidda dhexdeeda ah waanu korinaynaa Luudh iyo ehelkiisa Haweenaydiisa mooyee oo noqon ku wa halaaggu ku hadhi.'),
(3373, 29, 33, 'markay Rasuuladanadii (Malaa\'igtii) u timid Luudh wuu ka tiiraanyooday wuuna la cidhiidhyamay waxayna ku dhaheen ha cabsan hana tiiraanyoon waanu ku korin adiga iyo ehelkaagabee Haweenaydaada mooyee oo noqon kuwa lahalaagi.'),
(3374, 29, 34, 'waxaanu ku soo dajin magaalada Dadkeeda Dhagaxyo (Naarced) oo Samada ka yimid faasiqnimadooda darteed.'),
(3375, 29, 35, 'waxaanu kaga tagnay magaaladaa calaamad u muuqata Qoomkii wax kasi.'),
(3376, 29, 36, 'ree Madyanna (waxaanu u diray Walaalkood (Nabi) Shucayb wuxuuna ku yidhi Qoomkayow Caabuda Eebe, rajeeyana (ka yaaba) Maalintii dambaysay hana la soconina dhulka fasaadin.'),
(3377, 29, 37, 'markaasay beeniyeen waxaana qabtay Gariir (iyo Qaylo) waxayna noqdeen kuwo Lawyaha u dhaca.'),
(3378, 29, 38, 'Caad iyo Thamuudna (waan halaagnay), «halaagooduna» wuxuu idiinka muuqdaa Guryahoodii, wuxuuna u qurxiyey Shaydaan Camal koodi, wuxuuna kaleexiyey Jidka waxayna ahaayeen kuwo wax arka (haday waantoobi).'),
(3379, 29, 39, 'Qaaruun, Fircoon iyo Haamaana (waa lahalaagay), dhab ahaanna wuxuu ugula yimid (Nabi) Muuse xujooyin wayna isku kibriyeen dhulka, mana aha kuwo naga dheereyn (carari) kara.'),
(3380, 29, 40, 'dhammantood waxaan u qabanay dambigooda, waxaa ka mid ah mid aan ku diray Dabayl Dhagax wadata, waxaa ka mid ah miday qabtay Qaylo, waxaa ka mid ah midaan la goynay Dhulka, waxaa ka mid ah midaan maanshaynay, Eebana ma aha kii dulmiya hasayeeshee iyagaa naftooda dulmiyey.'),
(3381, 29, 41, 'kuwii yeeshay Eebe kasokow Awliyo (sokeeye u gargaara) waxay lamid yihiin Caaro yeelatay Guri waxaana ugu xun Guri\' Guri Caaro hadday wax ogyihiin.'),
(3382, 29, 42, 'Eebe waa ogyahay waxay baryayaan (Caabudayaan) oo isaga kasoo hadhay, Eebana waa adkaade falsan.'),
(3383, 29, 43, 'taasina waa tusaale aannu u yeelayno Dadka\'wax kasi oon ahayn kuwa wax yaqaanna ma jiro.'),
(3384, 29, 44, 'Eebaa u abuuray Samooyinka iyo Dhulka si xaq ah, arrintaasna Calaamaa ugu sugan Mu\'miniinta.'),
(3385, 29, 45, 'Akhri (Nabiyow) waxa luguu waxyoon oo Kitaabka ah oog Salaaddana maxaayeelaySalaaddu waxay ka reebta (Dadka) Xumaanta iyo waxa la noco, xusidda Eebaana weyn, Eebana waa ogyahay waxaad sanceyneysaan.'),
(3386, 29, 46, 'hakula murmina ehelu Kitaabka wax fiican mooyee, marka lagareebo kuwa dulmiga fala oo ka mid ah, waxaadna dhahdaan waxaan rumeynay wixii nalagu soo dajiyey iyo wixii laydinku soo dajiyey Ilaahaanno iyo ilaahiinna waa mid annaguna isagaan u hogaansanay.'),
(3387, 29, 47, 'saasaanu kuugu soodajiney Kitaabka, kuwa la siiyey Kitaabkana way rumeeyaan, kuwaasna waxaa ka mid ah kuwo rumeyn (ree Makaad), mana diido aayaadkanaga Gaalmooyee.'),
(3388, 29, 48, 'matihid mid wax akhriya Quraanka ka hor kumana aadan dhigi jirin Midigtaada markaas ay shakiyaan kuwa baadhilka (xumaanta) ku socda.'),
(3389, 29, 49, 'waase aayaad cad cad kuna sugan laabta kuwa cilmiga la siiyey, wax diidi aayaadkanaga oon daalimiinta ahayna ma jiro.'),
(3390, 29, 50, 'waxay dhaheen gaaladii (Makaad) maxaa loogu dajinwaayey korkiisa aayado xagga Eebe ka yimid, waxaad dhahdaa aayaadku waxaa uun ay ka yimaadaan Eebe agtiisa aniguse waxaan uun ahay udige muuqda.'),
(3391, 29, 51, 'miyuusanse ku fileyn inaan kugu soo dajinay Kitaabka (Quraanka) oo lagu akhriyo korkaaga, arrintaasna naxariis iyo waano yaa ugu sugan qoomkii rumeyn (xaqa).'),
(3392, 29, 52, 'waxaad dhahdaa waxaa ku filan dhexdeenna Eebe markhaati ahaan wuxuuna ogyahay waxa ku sugan samooyinka iyo Dhulka, kuwa rumeeyey badhil kana gaaloobay Eebe kuwaasi iyaga uunbaa khasaaray.'),
(3393, 29, 53, 'waxay kaa dadajisan Cadaab, haddaynajirin muddo magacaaban waxaa u imaan lahaa Cadaabka, wuxuuna ugu imaanlahaa kado iyagoon ogayn.'),
(3394, 29, 54, 'waxay kaa dadajisan Cadaab, Naarta Jahannamana waxay koobi gaalada.'),
(3395, 29, 55, 'maalinta (Qiyaamada) waxaa dabooli Gaalada Cadaab korkooda iyo Lugahooda hoostoodaba, wuxuuna ku dhihi Eebe dhadhamiya waxaad Camalfali jirteen.'),
(3396, 29, 56, 'addoomadayda rumeeyow Dhulkayga waa waasac ee Aniga uun i caabuda.'),
(3397, 29, 57, 'Nafkastaa waxay dhadhamin Geerida xaggaygaana laydiin soo celin markaas.'),
(3398, 29, 58, 'kuwa (xaqa) rumeeyey Camalfiicanna falay waxaanu dajin Jannada Qolol ay dureri dhexdeeda wabiyaal ayna ku waari dhexdeeda, waxaana ugu wanaagsan ujuurada kuwa shaqeeyey (Jannada).'),
(3399, 29, 59, 'ee ah kuwa samray Eebahoodna tala saarta.'),
(3400, 29, 60, 'badanaa dhul socod aan xambaaran karin rizqigeeda ee Eebe arzuqo iyada iyo idinkaba. Eebana waa maqle oge ah.'),
(3401, 29, 61, 'haddaad waydiiso Gaalada yaa abuuray samooyinka iyo dhulka oo sakhiray Qoraxda iyo Dayaxa, waxay odhan Ilaahay, ee xagee loo iili Gaalada.'),
(3402, 29, 62, 'Eebaa u waasiciya rizqiga ruuxuu doono oo addoomihiisa ah, kuna cidhiidhya (kuu doono), Eebana wax walba waa ogyahay.'),
(3403, 29, 63, 'haddaad waydiiso Gaalada yaa ka soo dajiya Samada Biyo oo ku nooleeya dhulka intuu dhintay (abaaroobay) kadib waxay odhan Ilaahay, waxaad dhahdaa mahad Eebaa iska leh, badankooduse wax ma kasayaan.'),
(3404, 29, 64, 'mana aha noloshan adduunyo Ciyaar iyo dheeldheel mooyee, Daarta aakhiraana meel nololeed ah hadday wax ogyihiin Dadku.'),
(3405, 29, 65, 'markay koraan Doonta waxay baryaan Eebe iyagoo u kaliyeeli Cibaadada, markuu u koriyo (oo geeyo) Barrigana (Dhulkana) markaasay u shariigyeelaan (wax la caabudaan).'),
(3406, 29, 66, 'si ay uga gaaloobaan waxaan siinay uguna raaxaystaan, wayse ogaan doonaan (Cidhibteeda).'),
(3407, 29, 67, 'miyeyna arkeyn Gaaladii (Qureesheed) inaan yeeley (dajiney) Xaram aamin ah oo ladafayo Dadka dhinacyadooda ah, mabaadilbay rumeyn oo nicmada Eebe ka gaaloobi.'),
(3408, 29, 68, 'Cidna kama dulmi badna ruux ku been abuurtay Eebe korkiisa ama beeniyey xaqa markuu u yimid, miyeyna ahayn Jahannamo Hoyga Gaalada.'),
(3409, 29, 69, 'kuwa ku jahaaday Dartanno waxaanu ku hanuunin Jidkanaga, Eebana wuxuu la jiraa kuwa wanaagga fala.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'waxay ku tusin mucjisada Quraanka (sidii soo hormartay).'),
(3411, 30, 2, 'waxaa lagaga adkaaday Ruum.'),
(3412, 30, 3, 'Dhulkii dhawaa, intii laga adkaaday ka dibna way adkaan.'),
(3413, 30, 4, 'dhawr sano dhexdeed, amarkana Eebaa iska leh horey iyo diba, Maalintaasayna ku farxi Mu\'miniintu.'),
(3414, 30, 5, 'Gargaarka Eebe, wuxuuna u gargaari cidduu doono, waana adkaade naxariista ah.'),
(3415, 30, 6, 'waa yabooha Eebe, mana baajiyo Eebe yaboohiisa, laakin Dadka badankiisu ma oga.'),
(3416, 30, 7, 'waxay yaqaanaan Dadku muuqaalka nolosha addunyo Aakhirose way halmaansanyihiin (ka jaahil yihiin).'),
(3417, 30, 8, 'miyeyna ka fikirin naftooda Inuusan Eebe Samaawaadka iyo Dhulka iyo waxa u dhaxeeya u abuurin waxaan xaq iyo muddo magacaaban ahayn, wax badan oo dadka ka mid ahna waxay ka Gaaloobi la kulanka Eebahood.'),
(3418, 30, 9, 'meyeyna ku soconin Dhulka oy eegan siday ahaatay cidhibtii kuwii ka horreeyey (Gaaladii Makaad) waxay ahaayeen kuwo ka daran iyaga kana xoog badan, Dhulkana waybeerteen wayna Dhiseen Dhulka intay dhiseen wax ka badan. waxayna ula timid Rasuulladoodii Xujooyin, Eebana ma aha mid wax dulmiya, laakiin iyagaa naftooda dulmiyey.'),
(3419, 30, 10, 'markaasay noqotay cidhibtii kuwii xumaanta falay, xumaan iyo (Jahannamo) beenintay aayadaha Eebe beeniyeen darteed, waxayna ahaayeen kuwo aayadaha ku jees jeesa.'),
(3420, 30, 11, 'Eebaa billaahay (Abuurka) Khalqiga isagaana soo celin, xaggiisaana laydiin celin.'),
(3421, 30, 12, 'Maalintay kici Saacaddu (Qiyaamadu) way aamusi dambiilayaashu (ooy quusan).'),
(3422, 30, 13, 'ugumana sugnaanin kuway Eebe lawadajiyeen (Cibaadada) Shafeeco waxayna noqon kuwo diida Shurakadoodii.'),
(3423, 30, 14, 'Maalintay kici Saacadu waa maalin ay kala tagi Dadku.'),
(3424, 30, 15, 'kuwa rumeeyey (xaqa) oo camalfiicanfalay Beer Janno yaa lagu faraxgaliyaa.'),
(3425, 30, 16, 'kuwa Gaaloobayse oo beeniyey aayaadkanaga iyo lakulanka aakhiro kuwaas waxaa lagu kulmin Cadaabka.,'),
(3426, 30, 17, 'Eebaa nasahan (Ceeb ka fog) markaad galabaysanaysaan iyo markaad waabariisanaysaan.'),
(3427, 30, 18, 'mahadna isagaa ku leh Samooyinka iyo Dhulka dhexdooda iyo Habeenkii iyo Duhurkiiba.'),
(3428, 30, 19, 'kana soobixiya wax nool wax dhintay wax dhintayna wax nool, nooleeyana Dhulka intuu dhintay ka dhib, saasaana laydiin soo bixin.'),
(3429, 30, 20, 'waxaana ka mid ah Calaamooyinkiisa inuu idinka abuuray Carro markaasaad noqoteen Dad dhaqdhaqaaqi (Faafi).'),
(3430, 30, 21, 'waxaa ka mid ah Calaamooyinkiisa inuu idiinka abuuray naftiina Haween si aad isugu xasishaan, yeelayna dhexdiina isjacayl iyo naxariis, taasina waxaa ugu sugan Calaamad qoomkii fikiri.'),
(3431, 30, 22, 'Waxaa ka mid ah Aayaadka ku tusin (Qudrada) Eebe samooyinka uu abuuray iyo Dhulka iyo kala duwanaanta Afkiina (Luuqada) iyo midabkiinaba, taasina calaamad yaa ugu sugan kuwa Cilmiga leh.'),
(3432, 30, 23, 'waxaa ka mid ah Aayadka tusin (Qudrada) Eebe Hurdadiina Habeenkii iyo Maalintii iyo doonidiina Fadliga Eebe, taasna calaamad yaa ugu sugan kuwa wax maqli.'),
(3433, 30, 24, 'waxaa ka mid ah Aayaadka ku tusin (qudra) Eebe Hillaaca uu idin tusiyo, Cabsi iyo Doonid darteed, iyo Samada uu idiinka soo dajiyo Biyo, kuna nooleeyo Dhulka intuu dhintay ka dib, taasina Calaamado yaa ugu sugan kuwa wax kasi.'),
(3434, 30, 25, 'waxaa ka mid ah Aayaadka ku tusin (qudrada) Eebe inay Samada iyo Dhulku ku taaganyihiin amarka Eebe, markuu Dhulka idiinka yeedhana (soobixinta) aad durba soo baxdaan.'),
(3435, 30, 26, 'Eebaa iska leh waxa Samooyinka iyo Dhulkaba ku sugan, isagayna u khushuucaan (u hogaansamaan).'),
(3436, 30, 27, 'Eebe waa kan billaabay abuurka Khalqiga, hadana soo celin wayna u fududahay arrintaasu, Eebaana sifo sare ku muta Samooyinka iyo Dhulka, waana adkaade falsan.'),
(3437, 30, 28, 'Eebe wuxuu idiin yeeley tusaale naftiina ah, waxaad hanataan miyey idinlawadaagaan wax laydinku arzuqay, ood si isku mid ah u maamulaysaan, ugana cabsanaysaan sidaad isaga cabsataan, saasuuna u caddayn aayaadka ciddii wax kasi.'),
(3438, 30, 29, 'haseyeeshee waxay raaceen kuwii dulmilowga ahaa Hawadooda iyagoon wax ogayn, yaa hanuunin kara ruux Eebe dhumid u qadaray, wax u gargaarana lahayn.'),
(3439, 30, 30, 'ee Wajigaaga u jeedi Diinta adoo toosan (Diintu) waa abuuridda Eebe ee Dadka uu ku abuuray, wax badali kara abuurka Eebana ma jiro, saasina waa Diinta toosan laakiin dadka babidiis ma oga.'),
(3440, 30, 31, 'idinkoo Eebe u noqon, kana yaaba, Salaadana ooga, Galadana ha ka mid noqonina.'),
(3441, 30, 32, 'ee Diintooda ku kala tagay noqdayna kooxo, Xisbi walba uu ku farxi waxa agtiisa ah.'),
(3442, 30, 33, 'hadduu Dadka taabto dhib waxay baryaan Eebahood iyagoo u noqon, markuuse dhadhansiiyo naxariis qayb iyaga ka mid ah waxay wax lawadaaijyaan Eebahood.'),
(3443, 30, 34, 'si ay uga gaaloobaan waxaan siinney, iska raaxaysta, waadna ogaandoontaan Cidhibta raaxadaasi.'),
(3444, 30, 35, 'mise waxaan ku soo dejiney xujo, oo ku hadli waxay Eebe la wadaajin.'),
(3445, 30, 36, 'haddaan dhadhansiino dadka naxariis way ku farxi «kibraan» hadduu dhibku dhacana waxay hor marsadeen darteed way quustaan.'),
(3446, 30, 37, 'miyeyna arkayn in Eebe u waasiciyo rizqiga cidduudoono, kuna cidhiidhyo (Cidduu doono) arrintaasna Calaamooyinbaa ugu sugan kuwa rumeyn (Xaqa).'),
(3447, 30, 38, 'ee Qaraabada sii xaqeeda iyo Miskiinka iyo Musaafurka, saasaa u khayroon kuwa dooni Wajiga Eebe, (u dhawaanshahiisa) kuwaasina waa kuwa liibaanay.'),
(3448, 30, 39, 'waxaad bixisaan oo Ribo ah si aad uga badsataan xoolaha dadka ma badnaado Eebe agtiisa, wixiise aad bixisaan oo Zako (xalaal ah) idinkoo ku dooni Wajiga Eebe kuwaas waa loo laablaabi.'),
(3449, 30, 40, 'Eebe waa kan idin abuuray Iladana idin arzaaqay hadana idin Dili, hadana idin soo noolayn, majiraa shurakadiina mid fali sidaas wax ka mid ah, Eebaa ka nasahana kana sareeya waxay la wadaajin.'),
(3450, 30, 41, 'xumaanbaa ka daahirtay Barri iyo Badba, waxay kasbatay Gaemo Dad dardiis, si Eebe u dhadhansiiyo waxay camalfaleen qaarkiis, inay noqdaan.'),
(3451, 30, 42, 'waxaad dhahdaa ku socda Dhulka fiiriyana siday noqotay cidhibtii kuwii hore, wuxuu ahaa badankoodu Mushrikiin.'),
(3452, 30, 43, 'ee Wajigaaga Diinta toosani (Islaamka) u jeedi, maalin aan la eelin karin ka hor, Eebe xaggiisana ka iman, Maalintaas Dadku way kala tagi.'),
(3453, 30, 44, 'ruuxii Gaalooba isagay (dhibi) Gaalimadiisu, ruuxii camal wanaagsan fala Naftiisa yuu u goglay.'),
(3454, 30, 45, 'si Eebe u abaalmariyo kuwa rumeeyey xaqa oo camal wanaagsan falay fadligiisa, Eebana ma jeela Gaalo.'),
(3455, 30, 46, 'Calaamaadka Eebe ku tusin waxaa ka mid ah inuu dabaylaha diro iyagoo ku bishaarayn (Roob) iyo in Eebe idin dhadhansiiyo naxariistiisa iyo in doontu ku socoto amarkiisa iyo inaad dalabtaan fadligiisa, markaas aad Eebe ku mahdisaan.'),
(3456, 30, 47, 'waxaan dhab ahaan ugu diray hortaa Rasuulo qoomkoodii, waxayna ulayimaadeen xujooyin waana ka aarsanay kuwii dambi Falay, waxaana korkanaga ah u gargaarka Mu\'miniinta.'),
(3457, 30, 48, 'Eebe waa kan diray Dabaylaha oo markaas kicisa Daruuro kuna fidiya Samada siduu doono kana yeela Goosimo aad aragtid Roobka oo ka soobixi dhexdeeda, markaasuu Eebe Roobka siiyaa Ciduu doono oo adoomadiisa ka mid ahna markaasay bishaaraystaan.'),
(3458, 30, 49, 'waxayna ahaayeen soo dajinta Roobka ka hor kuwo Quusan.'),
(3459, 30, 50, 'ee day naxariista Eebe raadkeeda sida Eebe, ugu nooleeyo dhulka intuu dhintay ka dib Eebaha wuxuu nooleeyaa wixii dhintay wax walbana waa arkaa.'),
(3460, 30, 51, 'haddaan ku diro Dabaylo (daran) Beertooda ay arkaan iyadoo doorsoon, waxay markaas ka dib noqdaan kuwo Gaalooba.'),
(3461, 30, 52, 'adigu wax ma maqashiin kartid kuwo dhintay, Mid dhagala\' oo jeedsadayna wax ma maqashiin kartid.'),
(3462, 30, 53, 'ruux Indha la\'na baadi kama hanuunin kartid, ma maqashiinkartid (xaqa) ruuxaan rumeynin Aayaadkanaga oo muslimiin ahayn.'),
(3463, 30, 54, 'Eebe waa kan idin ka abuuray Tabaryare, hadana idinka yeelay Tabaryare ka dib xoog, hadana xoog dabadi idinka yeelay Tabaryari iyo Gabow, wuxuu doono yuu abuuraa, waana wax walba oge, wax walba karc (oo uu doono).'),
(3464, 30, 55, 'maalinta Qiyaamadu kacdo waxay ku dhaartaan dambiilayaashu inayan nagaanin Saacad mooyee, saasaana (xaqa) looga leexiyey.'),
(3465, 30, 56, 'kuwii waxay dheheen cilmiga iyo Iimaanka la siiyey waad ku nagaateen Qadarka Eebe tan iyo maalinta soobixinta, kanna waa maalintii soobixinta, laakiin waxaad ahaydeen kuwo aan ogayn.'),
(3466, 30, 57, 'Maanta (Qiyaamada) ma anfacdo kuwii dulmiga falay cudur daar iyo toobad keen.'),
(3467, 30, 58, 'dhab ahaanbaan ugu caddaynay Dadka Quraankan dhexdiisa tusaale kasta, haddaadna ula timaado aayad waxay dhihi kuwii Gaaloobay waxaan baadhil (xumaan) ahayn kuma sugnidin.'),
(3468, 30, 59, 'saasaa Eebe u daboolaa Quluubta kuwaan wax ogayn (wax anfaca).'),
(3469, 30, 60, 'ee samir yabooha Eebe waa xaqe, yeyna ku fududayn kuwaan yaqiinsanayn (Xaqa).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'waxay ku tusin mucjisada Quraanka (sida soo hormartay).'),
(3471, 31, 2, 'tan waa aayaadka Kitaabka (Quraanka) xigmada leh.'),
(3472, 31, 3, 'waana hanuunka iyo naxariista samalfalayaasha.'),
(3473, 31, 4, 'kuwa salaadda ooga, Zakadana bixiya, iyagoo aakhiro yaqiinsan.'),
(3474, 31, 5, 'kuwaasi waxay ku suganyihiin hanuun Eebe, kuwaasina waa kuwa liibaanay.'),
(3475, 31, 6, 'Dadka waxaa ka mid ah kuwo gadan hadal wax shuqliya si uu uga dhumiyo (Dadka) Jidka Eebe iyagoon wax ogayn, Jidka Eebene ku Jees jeesa, kuwaasi waxay mudan Cadaab Dulleeya.'),
(3476, 31, 7, 'marka lagu akhriyo aayadkanaga wuu jeedsadaa isagoo iskibrin, sidii isagoon maqal, oo dhagihiisu culusyihiin, ugu bishaaree Cadaab daran.'),
(3477, 31, 8, 'kuwa rumeeyey (xaqa) camal wanaagsanna falay waxay mudan Janatu Naciima.'),
(3478, 31, 9, 'iyagoo ku waari dhexdeeda waana yabooh Eebe oo xaq ah, Eebana waa adkaade falsan.'),
(3479, 31, 10, 'isaga abuuray Samooyinka Tiir aad aragtaan la\'aantiis, dhulkana wuxuu ku tuuray Buuro (suga) inayan dhaqdhaqaaqin, wuxuuna ku fidiyey dhexdiisa daabad kasta, Samadana waxaan ka soo dejinay Biyo, waxaana ku soo bixiney nooc kasta oo quruxsan.'),
(3480, 31, 11, 'kaasi waa abuurkii Eebe, ee i tusiya waxay abuureen kuwa ka soo hadhay Eebe, saas ma aha, ee dulmi falayaashu waxay ku suganyihiin baadi Cad.'),
(3481, 31, 12, 'dhab dhaan ayaan luqmaan u siinay Xigmad iyo inuu Eebe ku mahdiyo, ruuxii mahdiya wuxuu uun u mahdiyey naftiisa, ruuxii Gaaloobana Eebe waa ka kaaftoonyahay waana amaananyahay.'),
(3482, 31, 13, '(xusuuso) markuu ku yidhi Luqmaan Wiilkiisa isagoo waanin Wiilkayow Eebe hawshariigyeelin, shirkigu waa dulmi weyne.'),
(3483, 31, 14, 'waxaan u dardaaranay dadka labadiisii Waalid, way sidday Hooyadis iyadoo tabaryar tabar yari kalana ku sugan, Gudhidiisuna waa Labo sano ee ku mahadi ani iyo Labadaadii Waalid, xagaygaana loo ahaan.'),
(3484, 31, 15, 'hadday kugula dadaalaan (Labada waalid) inaad ii shariig yeesho (ila wadaajiso) waxaadan aqoon u lahayn ha ku adeecin kulana noolow adduunka si fiican, raacna Jidka kuwa ii noqda, xagayga yaa laydiin soo celin waxaana idiinka warami waxaad camalfali jirteen.'),
(3485, 31, 16, 'Wiilkayow hadday xumaantu tahay Xabbad Khardal ah oo kale (wax yar) oo sallax dhexdii ku sugnaato ama samooyinka dhexdooda ama Dhulka dhexdiisa wuu keeni Eebe, Eebana waa oge xeeldheer.'),
(3486, 31, 17, 'Wiilkayow oog Salaadda, oo far waanaagga oo reeb waxa lanaco kuna Samir waxa ku gaadha, arrintaasu waxay ka midtahay umuuraha wanaagsane.'),
(3487, 31, 18, 'hana ka jeedin Wajiga dadka hana ugu socon dhulka kibir, Eebana ma jeela mid kastoo isla weyn oo faanbadane.'),
(3488, 31, 19, 'socodkaagana ku dhexdhexayso, hoosna u dhig Codkaaga, Cod waxaa ugu xun Codka Dameerahee.'),
(3489, 31, 20, 'miyeydaan arkayn in Eebe idiin sakhiray waxa Samooyinka iyo Dhulka ku sugan oo idin mid qarsoonba, Dadkana waxaa ka mid ah kuwo Eebe ku murmi Cilmi iyo hanuun iyo Kitaab nuuriya la\'aantiis.'),
(3490, 31, 21, 'marka loodhaho raaca waxa Eebe soo dajiyeyna waxay dhahaan waxaannu raaci waxaan ka helay Aabayaalkanno, haduu Shaydaan ugu yeedho Cadaabka Saciirana (miyey raaci).'),
(3491, 31, 22, 'ruuxii u jeediya Wajigiisa xagga Eebe isagoo samo fali wuxuu qabsaday guntin (Ballan adag), xagga Eebaana Cihdhibta arrimuhu u noqon.'),
(3492, 31, 23, 'Ciddiise Gaalowda yuuna ku walbahaar galin Gaalimadiisu, xagganagay u soo noqon waxaana uga warramaynaa waxay camalfali jireen, illeen Eebaa og waxa laabta ku sugane.'),
(3493, 31, 24, 'waanu u raaxayn wax yar, markaasaannu u dhibaatayn (u iili) Cadaab adag.'),
(3494, 31, 25, 'haddaad waydiisid yaa abuuray samooyinka iyo dhulka waay odhan ilaahay, waxaad dhahdaa mahad Eebaa iska leh, hadankoodse ma oga.'),
(3495, 31, 26, 'Eebaa iska leh waxa samooyinka iyo Dhulka ku sugan, Eebana waa hodan amaanan.'),
(3496, 31, 27, 'hadday waxa dhulka ku sugan oo geeda ah Qalimo noqdaan Badduna khadayso oo gadaasheeda Todobo badood yihiin madhamaateen Kalimooyinka Eebe, Eebana waa adkaade falsan.'),
(3497, 31, 28, 'abuurkiinna iyo soobixintiinu ma aha sida Nafkaliya mooyee (Fudaydka), Eebana waa maqle arke ah.'),
(3498, 31, 29, 'miyeydaan ogayn in Eebe Habeenka dhex galiyo Maalinta, Maalintana dhexgaliyo Habeenka sakhirayna Qoraxda iyo Dayaxa mid, kastana uu u socdo muddo magacaaban, Eebana waxaad Camalfalaysaan waa ogyahay.'),
(3499, 31, 30, 'Taasna wa in Eebe xaq yahay waxay caabudi ee ka soohadhayna baadil yahay iyo in Eebe yahay Sareeye wayn.'),
(3500, 31, 31, 'miyeydaan ogayn in Doontu dhexsocoto Badda Nicmada Eebe darteed si uu idiin tusiyo aayaadkiisa, arrintaasna aayaadbaa ugu sugan Samirbadane shugri badan oo dhan.'),
(3501, 31, 32, 'marka hirku daboolo dadka oo Hoos la moodo waxay baryaan Eebe iyagoo u kali yeeli Diinta (Cibaadada) markuu u koriyo xagga Barrigana waxaa ka mid noqon Dadka mid dhexdexeysan, mana diido aayadkanaga mid khaa\'in ah oo gaalimo badan mooyee.'),
(3502, 31, 33, 'Dadow ka dhawrsada Eebihiin oo ka Cabsada Maalin uusan ka abaalmarinayn Waalid Ilmihiisa, Ilmuhuna Waalidka waxba, yabooha Eebana waa Xaq, ee yeyna idinku dhagrin nolosha adduunyo, yuuna idinku dhagrin Eebe dhagar badane (shaydaan).'),
(3503, 31, 34, 'Eebe agtiisuu yahay ogaanshaha Saacadda (Qiyaame) iyo Roob di\'iddu, wuxuuna ogyahay waxa makaanka (Ilma galeenka) ku sugan, nafna ma oga waxay mudan Barri, Nafna ma oga Dhulkay ku dhiman, Eebaa og xeelna dheer.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'waxay ku tusin mugjisada Quraanka'),
(3505, 32, 2, 'soo dajinta Quraanka shaki kuma jiro, wuxuuna ka soodagay Eebaha Calaamka xaggiisa.'),
(3506, 32, 3, 'mise waxay dhihi waxaa been abuurtay (Nabiga) saas ma aha ee waa xaq Quraanku Eebahaa xaggiisa ka yimid si aad ugu digtid qoom uusan u imaanin dige kaa horeeya, si ay u hanuunaan,'),
(3507, 32, 4, 'Eebe waa ka ku abuuray Samooyinka, iyo Dhulka iyo waxa u dhaxeeya Lix maalmood dhexdeed, markaasna ku istawooday Carshiga «si u cunanta» idiinmana sugnaanin Eebe ka sokow wali (gargaare) iyo shafecee midna, ee miyeydaan wax xusuusanayn.'),
(3508, 32, 5, 'amarka Eebaa ka maamula Samada xagga Dhulka markaasaa amarkaasi xagga Eebe u koraa Maalin qadarkeedu yahay Kun sano oo waxaad tirinaysaana.'),
(3509, 32, 6, 'kaasina waa Eebaha og wax maqan iyo waxa joogaba, ee adkaada ee Naxariista.'),
(3510, 32, 7, 'Eebaa wanaajiyey abuuridda wax kasta, wuxuuna ka billaabay abuuridda Dadka Dhoobo, (Nabigii Aadam ahaa).'),
(3511, 32, 8, 'markaas kayeelay faraciisii mid ka soo taxma Biyo taharyar.'),
(3512, 32, 9, 'markaas ekeeyey oo ruuxdiisa ku afuufay idiinna yeelay maqal, Aragyaal iyo quluuba, waxyar baadse Eebe ku mahdisaan.'),
(3513, 32, 10, 'waxay dhaheen Gaaladii ma markaan ku dhexluno dhulka (geeriyoonno) yaa abuurid cusub nala abuuri, saas ma aha ee iyagu la kulanka Eebahood bay ka gaaloobeen.'),
(3514, 32, 11, 'waxaad dhahdaa waxaa idin oofsan (dili) Malaga mowdka laydiin wakiishay markaasaa xagga Eebihiin laydiin celin.'),
(3515, 32, 12, 'haddaad arki lahayd marka dambiilayaashu madaxa hoos u dhigi (qiyaamada) Eebahood agtiisa (iyagoo dhihi) Eebahanow waan aragnay waana maqalay ee naceli si aan camalfiican u fallo waan yaqiininaye (waxaad arki lahayd wax yaableh).'),
(3516, 32, 13, 'haddaan doono waxaan siin lahayn (ku toosin lahayn) Nafkasta hanuunkeeda hase yeeshee waxaa hormaray Hadalkaygii in laga buuxin Jahannamo Jinno iyo Insi dhammaan.'),
(3517, 32, 14, 'ee dhadhamiya halmaanshihiinii la kulanka Maalintiinnan (qiyame) annaguna waan idinka tagaynaa (maanta) ee dhadhamiya cadaabka waaridda camalkiinii dartiis.'),
(3518, 32, 15, 'waxaa uun rumayn aayaadkanaga kuwa marka lagu waaniyo aayadaha sujuud la dhaca kuna tasbiixsada mahadda Eebahood iyagoon is kibrinayn (isla waynayn).'),
(3519, 32, 16, 'waxayna ka dheeraataa dhinacyadoodu jiifka iyagoo baryi Eebahood cabsi iyo rajaynba waxaan ku arzuqnayna wax ka bixiya.'),
(3520, 32, 17, 'ma oga Nafu waxa loo qariyay kuwaas oo Indho ku qaboowsadaan abaal marin waxay camalfalayeen dartiis.'),
(3521, 32, 18, 'ruux Mu\'min ah mala midbaa mid faasiq ah, ma eka.'),
(3522, 32, 19, 'kuwa rumeeyay (xaqa) oo camal fiican falay waxay mudan Jannooyinka Ma\'wa martiqaad waxay xamal falayeen dartiis.'),
(3523, 32, 20, 'kuwase faasiqoobay waxay ku hoyan Naar, markay doonaan inay ka baxaanna waa lagu celin dhexdeeda waxaana lagu dhihi dhadhamiya Cadaabka Naartii aad beenin jirteen.'),
(3524, 32, 21, 'waxaana dhadhansiinaynaa cadaab hoose cadaab wayn kasokow inay noqdaan darteed.'),
(3525, 32, 22, 'yaa ka dulmi badan ruux lagu waaniyay aayaadka Eebihiis oo ka jeedsaday xaggeeda, anaguna dambiilayaasha waan ka aarsanaynaa.'),
(3526, 32, 23, 'dhab ahaanbaan u siinay Nabi Muuse Kitaabkii (Tawreed) ee hashakiyin lakulankiisa waxaana ka yeelay Kitaabkaas hanuunka Banii Israa\'iil.'),
(3527, 32, 24, 'waxaana ka yeelay qaarka mid ah imaamyo ku hanuuniya amarkanaga markay samreen, waxayna ahaayeen kuwa aayaadkanaga yaqiiniya.'),
(3528, 32, 25, 'Eebahaabaana kala bixin dhexdooda Maalinta Qiyaame waxay ahaayeen kuwo isku diidan.'),
(3529, 32, 26, 'myayna ku hanuunin kuwaasi intaan halaagnay hortood oo quruun ah oy socon guryahoodii, taasina waxaa ku sugan calaamado ee miyayna wax maqlaynin.'),
(3530, 32, 27, 'miyeyna arkeyn inaan u soo kaxayno Biyaha Dhul abaar ah oon markaas ku soobixino Beero ay wax ka cunaan Xoolahooda iyo Naftooduba miyeyna wax arkaynin.'),
(3531, 32, 28, 'waxay dhihi waa goorma kala xukunku haddaad run sheegaysaan.'),
(3532, 32, 29, 'waxaad dhahdaa maalinta kala xukunka Gaalada ma anfaco Iimaankoodu lamaga sugo.'),
(3533, 32, 30, 'Ee isaga jeedso oo sug iyana ha sugeene.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'Nabiyow ka dhawrso (ka yaab) Eebe hana adeecin Gaalada iyo Munaafiqiinta, Eebana waa oge falsan.'),
(3535, 33, 2, 'Raacna waxa lagaaga waxyoon xagga Eebahaa, Eebana waxaad camal falaysaan waa ogyahay.'),
(3536, 33, 3, 'Talana Saaro Eebe, Eebaana kufilan wakiile.'),
(3537, 33, 4, 'Mayeelin Eebe Laba qalbi Ruux Laabtiis, mana yeelin Haweenkiina aad dihaaraysaanna (ku dhaaranaysaana) Hooyooyinkiin oo kale, mana yeelin kuwaad sheegateenna Caruurtiinna oo kale, taasi waa hadalka afkiinna uun Eebana wuxuu sheegi xaqa, isagaana ku hanuuniya Jidka (toosan).'),
(3538, 33, 5, 'Ugu yeedha kuwaas Aabayaalkood saasaa ku cadaalo badan Eebe agtiise, hadaydaan aqoonin aabayaalkoodna waa walaalihiinna Diinta iyo Sokeeyihiinna, Korkiinana dhib ma saara waxaad ku gaftaan, hasayeeshee waxay u kasto Qulubtiinu (yaa laydin waydiin) Eebana waa dambi dhaafe Naxariista.'),
(3539, 33, 6, 'Nabigaa uga Roon (Turid badan) Mu\'miniinta Naftooda, haweenkiisuna waa Hooyooyinkood, Qaraabaduna iyagaa qarkood qaarka kale kaga Mudan Kitaabka Eebe, Mu\'miniinta (kale) iyo Muhaajiriinta, inaad wanaag u fashaan gargaarayaashiina mooyee, Xukunkaasna wuxuu ku Qoran yahay Kitaabka.'),
(3540, 33, 7, '«Xusuuso» markaan ka qaadnay Nabiyada ballankoodii iyo adiga iyo Nuux, Ibraahim, Muuse iyo Ciise Ibnu Maryam, oo ka qaadnay Ballan adag.'),
(3541, 33, 8, 'si Eebe u warsado kuwa Runlayaasha ah Runtooda, Gaaladana wuxuu u darbay Cadaab daran.'),
(3542, 33, 9, 'Kuwa (xaqa) rumeeyow xusuusta Eebe siduu idiinku Nicmeeyey, markay Junuud (Askar) idiin timid markaas oon ku diray Dabayl iyadoy aydaan arkayn, Eebana waxaad Camal falaysaan waa arkaa.'),
(3543, 33, 10, 'xusuusta markay idinkaga yimaadeen Kor iyo hoosba, Indhuhuna lisheen Quluubtuna dhuunta gaadhay Eebana aad u maleyseen malooyin.'),
(3544, 33, 11, 'Halkaasaa lagu imtixaamay Mu\'miniinta Gilgilid daranna lagu gil gilay.'),
(3545, 33, 12, 'Markey Munaafiqiintu lahaayeen iyo kuwa Quluubta ka buka Eebe iyo Rasuulkiisu waxaan dhagar ahayn nooma yaboohin.'),
(3546, 33, 13, 'markay qayb ka mid ah lahayd ree Madiinow meel aad kunagaataan (goobta) dagaalka ma jirto, ee noqda (xagga Madiino) koox iyaga ka mid ahna waxay idan warsan nabiga iyagoo dhihi Guryihiibaa naga dayacnaa, mana dayacna ee waxaan carar ahayn ma rabaan.'),
(3547, 33, 14, 'Haddii loogu soo galo Magaalada dhan kasto, markaas la warsado Gaalnimo way la imaan lahaayeen, waxaan wax yar ahaynse kuma dhexnagaadeen.'),
(3548, 33, 15, 'Horay waxay ugu ballantameen inayan cararin, Ballanka Eebana waa lays warsan.'),
(3549, 33, 16, 'waxaad dhahdaa carar aad geeri ka carartaan ama dil wax idiinma taro wax yar waxaan ahayna laydiin ku raaxeyn maaayo (adduunka).'),
(3550, 33, 17, 'dheh yaa idinka ilaalin Eebe hadduu dhib idiin la doono, ama naxariis, wax Eebe ka soo hadhay oo sokeeya ama gargaare ahna heli meysaan.'),
(3551, 33, 18, 'Dhab ahaan yaa Eebe u ogyahay kuwa reebayaalka ah (ku cabsiinta dagaalka) oo idinka mid ah iyo kuwa ku dhaba Walaalahooda (munaafiqiinta ah) noo imaadda Dagaalkana ma tagaan wax yar mooyee.'),
(3552, 33, 19, 'way idinkala bakhayli (wanaaga) marka cabsi timaadna waxaad arki iyagoo ku soo eegi indhahooduna wareegi sida mid Geeri hayso, marka cabsidu tagtana waxay idiinkugu Dhibaan Carrabo afbadan, isagoo idinkala bakhayli khayrka, kuwaasi ma rumeynin (xaqa) waana Hoobiyey Eebe camalkooda arrintaasuna waa u fududahay Eebe.'),
(3553, 33, 20, 'waxay malayn inayan tagayn Xisbiyadu (colalka) waxayna Jecelyihiin in marka Cololku yimaadaan ay baadiyaha jiraan ay warkiina warsadaan, hadday idiin joogaana ma dagalameen wax yar mooyee.'),
(3554, 33, 21, 'waxaa idinku sugan Rasuulka Eebe Kudayasho wanaagsan, ruuxii rajayn Eebe iyo Maalinta Aakhiro, Eebana xusa in Badan.'),
(3555, 33, 22, 'markay Mu\'miniintu arkeen Xisbiyadii (colkii) waxay dhaheen kan waa wixii Eebe iyo Rasuulkiisu noogu yagooheen, runbuu sheegay Eebe iyo Rasuulkiisu, waxaan Iimaan iyo Hagaansan ahayna uma siyaadinin.'),
(3556, 33, 23, 'Mu;miniinta waxaa ka mid ah Rag ka rumeeyey waxay Eebe kula ballantameen, waxaana ka mid ah mid Gutay Ballankii (Ajashiisii) waxaana ka mid ah mid sugi mana ayna badalin.'),
(3557, 33, 24, 'in Eebe ka abaalmariyo Runlowyaasha Runtooda, oo cadaabana Munaafiqiinta, hadduu doono, ama ka toobad aqbalo, Eebana waa dambidhaaf badane Naxariista.'),
(3558, 33, 25, 'Eebe wuxuu ku eeliyey kuwii Gaaloobay cadhadoodii, wax khayr ahna mayna gaadhin, Eebaana ka kaafiyey Mu\'miniinta Dagaal, Eebana waa xoogbadane adkaada.'),
(3559, 33, 26, 'kuwii u kaalmeeyey ehlu Kitaabka ahaana wuxuu ka soo dajiyey (ka soo saaray) Dhufaysyadoodii, wuxuuna ku tuuray Quluubtooda argagax, koox ka mid ah waad dilaysaan, Kooxna waa Qafaalanaysaan.'),
(3560, 33, 27, 'wuxuuna idin dhaxalsiiyey Dhulkoodii Guryahoodii iyo xoolahoodii, iyo Dhul aydaan hadda ka hor ku joogsan, Eebana wax kasta (oo uu doono) wuu karaa.'),
(3561, 33, 28, 'Nabiyow ku dheh Haweenkaaga haddaad tihiin kuwo dooni Nolosha adduunyo iyo Quruxdeeda kaalaya waan idin gacan marin si quruxsanna idiinsiideyne.'),
(3562, 33, 29, 'Haddaadse tihiin kuwo dooni Eebe iyo Rasuulkiisa iyo Dartii Aakhiro Eebe wuxuu u darbay Haweenka wanaagga fala oo idinka id ah ajir wayn.'),
(3563, 33, 30, 'Haweenka Nabigow tiinii la timaadda xumaan cad waxaa loo laalaabi cadaabka arrintaasna waa u fududahay Eebe.'),
(3564, 33, 31, 'Tiise adeecda Eebe iyo Rasuulkiisa oo camal fiican fasha waxaan siinaynaa Labo ajir, waxaana u darbaynaa Rizqi wanaagsan.'),
(3565, 33, 32, 'Haweenka Nabigow ma tihidiin sida Haweenka kale haddaadse dhowrsataan hadalka ha nuglaynina oo uu damco midka qalbiga ka buka, hadal wanaagsanna ku hadla.'),
(3566, 33, 33, 'kuna sugnaada Guryihiinna, hana u wareegaysanina (idinko isfaydi) sidii jaahiliyadii horraysay, Salaadana ooga, Sakadana biya, Eebe iyo Rasuulkiisana adeeca, Eebe wuxuu uun dooni inuu xumaanta idinkatagsiiyo (fogeeyo) dambiga, Ehelubaytkow idinna daahiryeelo.'),
(3567, 33, 34, 'xusuustana waxa lagu akhrin Guryihiin oo aayaadka Eebe ah iyo Sunada (Nabiga), Eebana waa wax walba oge Xeeldheer.'),
(3568, 33, 35, 'Raga Muslimiinta ah iyo Haweenka Muslimaadka ah, Ragga Mu\'miniinta ah iyo Haweenka Mu\'minaadka ah, Ragga Eebe adeeca iyo Haweenka adeeca, Ragga Runlowga ah iyo Haweenka Runlowga ah, Ragga Samra iyo Haweenka Samra, Ragga u khushuuca Eebe iyo Haweenka u kushuuca, Ragga Sadaqaysta iyo Haweenka Sadaqaysta, Ragga Sooma iyo Haweenka Sooma, Ragga isdhawra iyo Haweenka isdhawra, iyo Ragga imbadan Eebe xusa iyo Haweenka inbadan xusa, wuxuu Eebe u darbay Dambi dhaaf iyo ajir weyn.'),
(3569, 33, 36, 'kuma haboona (umana banaana) nin Mu\'min ah iyo Haweenay Mu\'minad ah Marka Eebe iyo Rasuulkiisu wax xukumayaan Inay khiyaar u yeeshaan amarkooda, ruuxiise caasiya Eebe iyo Rasuulkiisa wuxuu dhumay dhumid Cad.'),
(3570, 33, 37, '(xuxuuso) markaad ku lahayd kuu Eebe u Nicmeeyey, adna aad u nicmaysay (Zayd BinuXaarithah) hayso Haweenaydaada Eebana ka yaab, Naftaadana aad ku Qarinaysay wax Eebe Muujin, ood kana cabsanaysid Dadka (xishoonaysid) Eebaana u muta in laga cabsado (lagana xishoodo) markuu Zayd dantiisii ka dhamaaystayna (isfureenna) aan kuu guurinay si ayan wax dhib ah u ahaanin Mu\'miniinta (hadday guursadaan) Haweenka ay fureen kuway Wiilal ka dhigteen (iyagoo dhalin) amarka Eebana waa kii la Fuliyo.'),
(3571, 33, 38, 'Nabiga korkiisa dhib ma aha waxa Eebe u Banneeyey, waana Jidkii Eebe ee Dadkii tagay ee hore amarka Eebana waa qadar Xukuman.'),
(3572, 33, 39, '(waxaa amaan mudan) kuwa gaadhsiiya (Dadka) Fariimaha Eebe kana cabsada Eebe, oon kana cabsan Axad ka soo hadhay, Eebaana ku filan, Xisaabiye (Kaalmayn).'),
(3573, 33, 40, 'ma aha (Nabiga) Muxamad ah aabaha Ruux ragiina ka mid ah, laakiin waa Rasuulkii Eebe iyo Khatimidii Nabiyada, Eebana wax walba waa ogyahay.'),
(3574, 33, 41, 'kuwa xaqa rumeeyow xusa Eebe wax badan.'),
(3575, 33, 42, 'weyneeyana Aroortii iyo Galabtii.'),
(3576, 33, 43, 'Eebana waa kan idiin naxariista Malaa\'igtiisuna idiin ducayso, si uu idiin ka bixiyo Nuurka, waana kan u Naxariista Mu\'miniinta.'),
(3577, 33, 44, 'Bariidoodu Maalintay Eebe la kulmi waa Salaan wuxuuna u darbay ajir weyn.'),
(3578, 33, 45, 'Nabiyow waxaanu ku dirray adoo marag ah, oo bishaarayn oo digi.'),
(3579, 33, 46, 'oo Dadka Eebe ugu yeedhi Idankiisa, oo siraad ifi ah.'),
(3580, 33, 47, 'ee u bishaaree Mu\'miniinta inay xaga Eebe ku leeyihiin fadli (dheeraad) weyn.'),
(3581, 33, 48, 'hana, adeecin Gaalada iyo Munaafiqiinta, dhibkooda iska gudub (hana u aabayeelin) Eebana talo Saaro isagaa ku filan wakiile.'),
(3582, 33, 49, 'Kuwa (xaqa) rumeeyow hadaad guursataan Haween Mu\'minaad ah markaas aad furtaan taabasho horteed, wax cidda ah kuma lihidin korkooda oy tirsan, ee gacan mariya una siidaaya si quruxsan.'),
(3583, 33, 50, 'Nabiyow waxaan kuu banaynay Haweenkaaga aad siisay meherkoodii iyo waxaad hanatay (Xagga gaalada) oo Eebe kugu soo celiyey, iyo Gabdhaha Adeerkaa, Gabdhaha Eedadaa, Gabdhaha Abtiyadaa, Gabdhaha Habaryarahaa oo kula soo hijrooday, iyo Haweenay Mu\'minaad ah hadday Nafteeda Nabi u hibayso, hadduu doono inuu guursado Nabigu, iyadoo gaar kuga tahay Mu\'miniinta ka sokow, waan ognahay waxaan ku faralyeelay Mu\'miniinta xagga Haweenkooda, iyo waxa gacantoodu hanato, sii aan korkaaga dhib u ahaan, Eebana waa dambi dhaafe Naxariista.'),
(3584, 33, 51, 'Waad dib dhigi taad doonto, waadna soo dhaweyn taad doonto oo ka mid ah kuwaad ka fogaatay dhibna korkaaga ma aha. saasaana u dhaw inay indhahoodu qabowsadaan ooyan murugoon oy raali ku noqdaan waxaad siisay dhamaantood, Eebana waa ogyahay waxa Quluubtiina kuy sugan, Eebana waa oge Dulqaad leh.'),
(3585, 33, 52, 'kuuma banaana Nabiyow Haweenka (kale) mar dambe iyo inaad Haween kale ku badasho si kasta ood ula yaabto Quruxdooda waxaad hanato mooyee (oo Gaalo laga helo) Eebana wax walba waa ilaaliye.'),
(3586, 33, 53, 'kuwa (xaqa) rumeeyow ha galina Guryaha Nabiga in cunno leydiinku Idmo (yeedho) mooyee idinkoon sugayn Bislaadkiisa, hasayeeshee marka laydiin yeedho gala, markaad cuntaanaa socda hanasugina sheeko, Illeen taasi waxay dhibi Nabigee wuuna idinka xishoone Eebese xaqa kama xishoodo, haddaad wax warsataan Haweenka (Nabigana) ka warsada Xijaab gadaashiis. saasaa u daahirnimo badan (ufiican) Quluubtiina iyo Quluubtoodaba, xaqna uma lihidin inaad dhibtaan Rasuulka Eebe ama aad Guursataan Haweenkiisa Gadaashiis waligeed, arrintaasuna way ku wayn tahay Eebe agtiisa.'),
(3587, 33, 54, 'Haddaad muujisaan wax ama qarisaan Eebe waa wax kaste oge.'),
(3588, 33, 55, 'Korkooda (Haweenka) dhib kama Saarra Aabayaalkood, Wiilashooda, Walaalahood, Wiilasha Walaalahood, Wiilasha Walaashood, Haweenkooda (Mu\'miniinta ah) iyo waxay hanatay Gacmahoodu, kana dhawrasada Eebe, Eebe wax kaste wuxuu u yahay Marage.'),
(3589, 33, 56, 'Eebe iyo Malaa\'igtiisu waxay ku sallin (amaani) Nabiga, ee kuwa (xaqa) rumeeyow ku Salliya korkiisa oo salaama Salaan.'),
(3590, 33, 57, 'kuwa dhiba Eebe iyo Rasuulkiisa wuu lacnaday Eebe Adduun iyo Aakhiroba, wuxuuna u darbay cadaab wax dulleeya.'),
(3591, 33, 58, 'Kuwa ku dhiba Mu\'miniita Rag iyo Haweenba waxayan kasbanin waxay xambaarteen Been abuurasho iyo Dambi cad.'),
(3592, 33, 59, 'Nabiyow ku dheh Haweenkaaga iyo Gabdhahaada iyo Haweenka Mu\'miniinta isku dada Shukadiinna, taasaa idiinku dhaw in laydin aqoonsado oon laydin dhibine, Eebana waa dambi dhaafe Naxariista ah.'),
(3593, 33, 60, 'haddayna ka joogin Munaafiqiintu iyo kuwa qalbiga ka buka iyo kuwa Gilgili (Beenta ku faafin) madiino waanu kugu diri kulamana Darsayaan wax yar mooyee.'),
(3594, 33, 61, 'Waana la lacnaday meelkastooy joogaanba hana la qabto oo la diloDilmo.'),
(3595, 33, 62, 'waana sunadii Eebe dadkii horee tagay wax badalina uma heshid Sunada Eebe.'),
(3596, 33, 63, 'Dadku waxay warsan Nabiyow Saacadda (Qiyaame) waxaad dhahdaa Cilmigeedu wuxuu jiraa Eebe agtiis, miyaadse ogtahay inay suuroowdo inay Saacaddu dhawdahay.'),
(3597, 33, 64, 'Eebe wuxuu Lacnaday Gaalada wuxuuna u darbay (Naarta) Saciiro.'),
(3598, 33, 65, 'Iyagoo ku waari dhexdeeda waligood oon helaynna Sokeeye iyo gargaare midna,'),
(3599, 33, 66, 'Maalinta lagu gagadin Wajigooda Naarta waxay dhihi waan halaagsanaye haddaan Adeecno Eebe oon Adeecno Rasuulkii.'),
(3600, 33, 67, 'Waxayna dhihi Eebow waxaan Adeecnay Madaxdannadii iyo kuwii noowaaweynaa waxayna naga dhumiyeen Jidkii.'),
(3601, 33, 68, 'Ee Eebow Sii kuwaas Laban laab Cadaab Lacanadwayna Lacnad.'),
(3602, 33, 69, 'Kuwa (xaqa) rumeeyow hanoqonina sidii kuwii dhibay (Nabi) Muuse oo Eebe ka bariyeelay waxay (ku) sheegan, Eebe agtiisana wuxuu ku leeyahay Waji (Sharaf).'),
(3603, 33, 70, 'kuwa (xaqa) rumeeyoow ka dhawrsada Eebe hadal toosanna ku hadla.'),
(3604, 33, 71, 'Haydiin wanaajiyo Camalkiinna hana idiin dhaafo Dambigiinee, Ruuxii Adeeca Eebe iyo Rasuulkiisa wuxuu u liibaanay dhab ahaan Liibaan weyn.'),
(3605, 33, 72, 'Annagaa u bandhignay Amaanadda (Cibaadada) Samooyinka, Dhulka iyo Buuraha wayna diideen inay Xambaaraan wayna ka Cabsadeen xaggeeda, waxaase xambaaray Dadka wuxuuna noqday Dulmi badane Jahli badan.'),
(3606, 33, 73, 'Si Eebe u cadaabo Munaafiqiinta Rag iyo Haweenba iyo Mushrikiinta (Gaalada) Rag iyo Haweenba, uu kana toobad aqbalo Mu\'miniita Rag iyo Haweenba, Eebana waa dambidhaafe Naxariista.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'mahad Eebaa iska leh ay awooddiisa kujiraan waxa samooyinka iyo dhulka ku sugan, mahadna isagaa muta aakharo iyo aduunba, waana falsame xeel dheer (waxwalba oge).'),
(3608, 34, 2, 'wuxuuna ogyahay waxa dhulka gali iyo waxa ka soobixi iyo waxa ka soodagi samada iyo waxa u kori, waana naxariiste dambidhaafa ah.'),
(3609, 34, 3, 'kuwii gaaloobay waxay dhaheen nooma imaaneyso saacadda (Qiyaame), waxaad dhahdaa saas ma aha ee Eebahay baan ku dhartey way idiin imaan, Eebahay waa wax maqan oge, wax darro la egna kagama qarsoona samooyinka iyo dhulka dhexdooda iyo wax waxaas ka yar iyo wax la weyn midna, wax walbana kitaab cad bey ku suganyihiin.'),
(3610, 34, 4, 'Si Eebe u abaal mariyo kuwa rumeeyey (xaqa) oo camal fiican Falay kuwaasna waxay mudan dambi dhaaf iyo risqi wanaagsan.'),
(3611, 34, 5, 'Kuwa u socda iney aayaadkanaga (kaleexdaan) iyagoo u maleyn iney daalin (Eebe) kuwaasi waxay mudan cadaab daran oo xun.'),
(3612, 34, 6, 'Waxey arki kuwii cilmiga la siiyeyna (mu\'miniinta) in waxa laga soo dajiyey xagga Eebe xaq yahey kuna hanuunin jidka Eebaha adkaada ee la mahdiyo.'),
(3613, 34, 7, 'Waxey dhaheen kuwii gaaloobey maydin tusinaa nin idiin ka warrami in marka laydinkala geeyo (jidkiina) kala gayn dhamaanteed in abuur cusub leydin abuuri.'),
(3614, 34, 8, 'Ma Eebuu ku been abuuran mase wuu waalanyahay, saas ma aha ee kuwaan rumeynin aakhiro waxay ku sugnaan cadaab iyo baadi fog.'),
(3615, 34, 9, 'Miyeyna arkaynin waxa hortooda ah iyo waxa gadaashooda ah oo samo iyo dhul ah hadaan doono waxaanu la goyn dhulka, mise waxaan ku soo rideynaa goosimo samada kamid ah arintaasna aayadbaa ugu sugan adoonkastoo towbadkeen badan.'),
(3616, 34, 10, 'Dhab ahaanbaan u siinay Nabi Daawuud fadli (dheeraad) xaganaga ah, waxaana nidhi buurahoow ka celceliya tasbiixda, shimbirahoowna (la celceliya tasbiixda,) waxaana u jilcincy birta.'),
(3617, 34, 11, 'Waxaana ku nidhi ka samce dirieyada (dharka dagaalka) sugna tixiddeeda wanaagna fala, waxaad faleeysaana waan arkaa.'),
(3618, 34, 12, 'Nabi suleymaanna waxaan u Sakhirray dabeysha, kallahaadeedu waabil, carraabaheeduna waa bil, waxaan u dureeriney il Nuxaad ah (maar) Jinigana waxaa ka mid ah kuwo ku hor shaqeeya idanka Eebe, ruuxii ka mid ah oo ka iilata amarkanaga waxaan dhadhansiineeynaa cadaabka saciiro.'),
(3619, 34, 13, 'Waxay u sameyn suleymaan wuxuu doono oo mixraabyo (Fooqaq) iyo suurooyin, iyo xeedhooyin dar oo kale ah, iyo dharyo sugan, waxaana ku nidhi fala ehelka Daawuudow mahadnaq, waxaana yar adoomadayda shugriga badan.'),
(3620, 34, 14, 'Markaan geerida ku xukunay Nabi suleymaan wax tusiyey geeridiisa ma jiro oon ahayn aboorka cunay ushiisa, markuu dhacay yaa waxaa u cadaaday jinigii hadey waxa maqan ogyihiin ineyna ku nagaadeen cadaab wax duleeya (shaqadiisa darteed).'),
(3621, 34, 15, 'Guryihii reer sabaad waxaa ku sugnayd calaamo, Beeraana ka xigay xaga midig iyo xagga bidixba, waxaana Eebe ku yidhi cuna risqiga Eebihiin kuna mahadiya, waa balad wanaagsan iyo Eebe dambi dhaafbadane.'),
(3622, 34, 16, 'Wayse jeedsadeen, waxaana ku diray daad daran, waxaana ku badaley labadoodii beerood beero kale oo geedo khadhaadh iyo qoryo iyo sidri yar ah.'),
(3623, 34, 17, 'Sidaasaana ku abaalmarinay gaalnimadoodii darteed, abaalmarin xunna waxaan abaal marinaa kuwa gaalnimada badan.'),
(3624, 34, 18, 'Waxaana yeeley dhexdooda iyo magaalooyinkii barakeysnaa (Yaman iyo Qudus) magaalooyin muuqdo waxaana qadaray socodkooda, waxaana ku nidhi ku socda dhexdeeda habeen iyo maalinba idinkoo aamin ah.'),
(3625, 34, 19, 'Waxayse dhaheen Eebow noo kala fogee socodka, wayna dulmiyeen naftooda, waxaana ka yeelay wax lagu sheekeysto. waana kala goyney, sidaasna waxaa ugu sugan calaamo ruux kastoo Samirbadanoo Mahadin badan.'),
(3626, 34, 20, 'Dhab ahaan waxaa ku rumoobey gaalada korkooda Ibliis Malihiisii, wayna raaceen wax yar oo ka mid ah oo mu\'min ah mooyee.'),
(3627, 34, 21, 'Mu\'miniinta awood kuma laha ibliis wax kalana uma aanaan yeelin inay kala muuqato ruuxa rumeeyey Aakhiro iyo kan ka shakisan xaggeeda. Eebahaana wax walba waa ilaaliye.'),
(3628, 34, 22, 'Waxaad dhahdaa u yeedha kuwaad sheegateen Eebe ka sokow (iney ilaahyihiin) kuma hantaan darro wax la eb samooyinka iyo dhulka midna, wax wadaag ahna «shirkadna» kumalch dhexdooda Eebe wax u kaalmeeyana kuma laha khaliqiga.'),
(3629, 34, 23, 'Kumana anfacdo shafeeco Eebe agtiisa, ruuxuusan idmin. Markase laga faydo argagaxa quluubtooda waxay dhihi muxuuyidhi Eebihiin, markaasay dhihi kuwa shafeeci wuxuu yidhi xaq waana Eebaha sarreeya. ee weyn.'),
(3630, 34, 24, 'Waxaad dhahdaa yaa idinka arsaaqa samooyinka iyo dhulka, waxaad dhahdaa Eebe, anaga iyo idinka mid unbaa hanuunsan ama baadicad ku sugan.'),
(3631, 34, 25, 'Waxaad dhahdaa laydin warsan mahayo waxaan dambaabney anagana nalama warsado waxaad camalfaleysaan.'),
(3632, 34, 26, 'Waxaad dhahdaa waxaa ina kulmin Eebaheen, markaasuu ina kala xukumin si xaqa ah. waana caadil wax walba og.'),
(3633, 34, 27, 'waxaad dhahdaa i tusiya kuwaas Eebe haleeshiiseen oo shurako ah (wax la wadaago) saas ma aha ee Eebe waa adkaade falsan.'),
(3634, 34, 28, 'Kuu maanaan dirin waxaan dadkoo dhan ahayn, adoo u bishaaraynaya Mu\'miniinta, una digi gaalada, laakiin dadka badidiisu ma oga.'),
(3635, 34, 29, 'Waxayna dhihi waa goorma yaboohaasi (soobixinta) hadaad run sheegaysaan.'),
(3636, 34, 30, 'Waxuud dhahdaa waxaa idiin sugnaaday Waqti aydaan saacad ka dib dhacaynin kana hor dhacaynin.'),
(3637, 34, 31, 'Waxay dheheenna kuwii galoobay rumeynmayno quraankan iyo kuwii ka horreeyey midna, hadaad arki labayd daalimiinta oo taagan Eebe agtiisa (qiyaamada) oo qaarkood qaarka kale hadal u celin ooy ku dhihi kuwii raaciyadda ahaa kuwii madaxda ahaa ee isla wayna, hadaydaan jirin waxaan ahaan laheyn Mu\'miniin. (waxaad arki laheyd arin weyn oo lala yaabo).'),
(3638, 34, 32, 'Kuwii iskibriyey (Madaxdii) waxay ku dhaheen kuwii tabarta yaraa (Raaciyaddii) ma anagaa idinka celinay hanuunka markuu idiinyimid saas ma ah ee idinkaa danbiilayaal ahaa.'),
(3639, 34, 33, 'Kuwii tabarta yaraa (raaciyaddii) waxay ku dhaheen kuwii iskibriyay (Madaxdii) saas ma aha ee waxaa hanuunka naga celiyay dhagartii Habeen iyo Maalinba markaad nafarayseen inaan Eebe ka gaalowno una yeelo shariigyo, Waxay qarsadeen qoomarno markay arkeen cadaabka, Waxaana yeelay kuwii gaaloobay luquntooda Biro Naar ah. Miyaa laga abaalmarinin gaaladu waxay camal faleen mooyee.'),
(3640, 34, 34, 'Magaalo kasta oo aan dige (Nabi) u dirro waxay dhahaan kuwoodii loo nicmeeyay anagu waxa laydiinku soo diray waan ka gaalownay.'),
(3641, 34, 35, 'Waxayna dhaheen anagaa xoolo iyo Caruurba idinka badan, nalamana cadaabayo.'),
(3642, 34, 36, 'Waxaad dhahdaa Eebehay wuxuu u fidiyaa rizqiga ruxuu doono, wuuna ku cidhidhhiyaa ruxuu doono, laakiin dadka badankiisu ma oga.'),
(3643, 34, 37, 'Ma aha xoolahiinnu iyo caruurtiinnu kuwii noo kiin dhaweeya, hase yeeshee ruuxii rumeeya (xaqa) oo camal fiican cala, kuwaas waxaa u sugnaaday abaalmarin labalaab ah camalkooda dartiis, waxayna ku aamin noqdaan qawlado janno.'),
(3644, 34, 38, 'Kuwa u socda ka (reebidda) aayaadkanaga iyagoo isleh Eebe daaliya, kuwaas waxaa lagu kulmin cadaab.'),
(3645, 34, 39, 'Waxaad dhahdaa Eebehay wuxuu risqiga u fidiyaa cidduu doono oo addoomadiisa ka mid ah, kuu doonana wuu ku cidhiidhyaa, waxaad bixisaan oo kastana Eebaa idiin bedeli, Eebena isagaa u khayroon wax wax arsaaqa.'),
(3646, 34, 40, 'Xusuuso maalinta uu Eebe kulmin dhamaan gaalada, markaas uu ku odhan malaa\'igtiisa kuwani ma idinkay idin caabudi jireen.'),
(3647, 34, 41, 'Markaas ay dhihi adaa ceeb ka fog Eebow, adaa gargaarahanaga ah iyaga ka sokay, waxayse caabudi jireen Jinni badidoodna isagay rumaysnaayeen.'),
(3648, 34, 42, 'Maantase «Qiyaamada» uma hanato qaarkiin qaarkakale naefi iyo dhib midna. waxaana ku dhahaynaa kuwii dulmi falay dhadhamiya cadaabka naartaad beeninayseen.'),
(3649, 34, 43, 'Marka lagu akhriyo korkooda aayaadkanaga waxay dhahayaan kani ma aha mid dooni inuu idinka iilo waxay Aabbayowgiin caabudijireen mooyee, waxayna dheheen kan (quraanku) ma aha waxaan beenabuurasho ahayn, waxayna dhaheen kuwii Gaaloobay Xaqu markuu u yimid kani waxaan sixir muuqda ahayn ma aha.'),
(3650, 34, 44, 'Ma aanaan siinin Kutubo ay akhriyaan, umana aanaan dirin xagooda dige kaa horreeyay (Carabta).'),
(3651, 34, 45, 'Waxay beeniyeen (xaqa) kuwii ka horreeeyayna, mana gaadhin (kuwii Nabiga beeniyay) Toban meelood meel waxaan siinay kuwaas, waxayna beeniyeen Rasuuladaydii, Sayse noqotay ciqaabtaydii (aan ciqaabay kuwaas xaqa beeniyay).'),
(3652, 34, 46, 'Waxaad dhahdaa Nabiyow mid kaliya yaan idinku waanin inaad u jahaysataan Eebe laba labo iyo mid mid markaasna aad fikirtaan inaan saaxiibkiin «Nabiga Muxamed ah» uusan waallayn, uu digihiinna cadaab daran hortiisna uu yahay,'),
(3653, 34, 47, 'Waxaad dhahdaa Nabiyow idiinma warsanin ujuuro idinkaase iskalch (haii laydin warsado), Ujuuradayda Eebe unbaa isiin, wax walbana Eebe wuu daalacan.'),
(3654, 34, 48, 'Waxaad dhahdaa Eebehay xaquu soo dajiyaan (Xumaanta ku baaabi\'iyaa) waana wax maqan oge badane.'),
(3655, 34, 49, 'Waxaad dhahdaa xaqii waa yimid, baadhilna wa tirtirmay,'),
(3656, 34, 50, 'Waxaad dhahdaa haddaandhumo waxaad uun u dhumay naftayda, hadaan hanuunana waa wixii Eebe ii waxyooday, sababtiisa Eebe waa maqli dhawe.'),
(3657, 34, 51, 'Hadaad arki lahayd Nabiyow markay argagaxaan gaaladu oo meeley u cararaan jirin oo meel dhaw laga qabto, (waxaad arki lahayd arrin wayn).'),
(3658, 34, 52, 'Waxay dhihi markaas waxaan rumaynay (xaqa) xagay uga ahaan inay meel fog wax ku qaataan (rumayn).'),
(3659, 34, 53, 'Dhab ahaanna waa iyagii uga gaaloobay xaqa, horray ay ka tuurayeen waxayan ogayn meel fog.'),
(3660, 34, 54, 'Waana la kala ooday iyaga iyo waxay doonayeen sidii lagu falay kuwii la midka ahaa ee ka horeeyay, waxayna ahaayeen kuwo shaki daran ku sugan.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Mahad Eebaha billaabay «abuuray» samooyinka iyo dhulka yaaleh kana yeelay Malaa\'igta farriinwade, oo garbolabo labo ahiyo saddex saddex iyo afar afar ah leh, wuuna siyaadiyaa Eebe abuuriduu doono, Eebana wax walba uu doono wuu karaa.'),
(3662, 35, 2, 'Wixii Eebe dadka ugu nicmeeyo oo naxariis ah wax celin karaa majiro, wuxuu celiyaana wax soodayn kara ma jiro dabadiis, waana adkaade falsan.'),
(3663, 35, 3, 'Dadow xusuusnaada nicmada Eebe korkiina ah, abuure kale ma jiraa oo Eebe ka soo haday oo idinka arsuqi samada iyo dhulka, Eebe mooyee ilaah kale majiro ee xaggee laydiin ka iilii xaqa.'),
(3664, 35, 4, 'Hadday ku beeniyaan waxaa dhab ahaan loo beeniyay rasuulladii kaa horeeyay, Eeba xagiisaana loo celin umuuraha.'),
(3665, 35, 5, 'Dadaw yabooha Eebe waa xaq ee yayna idindhagrin nolasha aduunyo, yuuna idinkudhagrin Eebe ku dhagris badane (shaydaan).'),
(3666, 35, 6, 'Shaydaanku waa colkiin ee idinna col ka yeesha, wuxuuna idiin ku yeedhi uun xisbigiisa si ay asaxaabta Naaarta saciiro u noqdaan.'),
(3667, 35, 7, 'Kuwii gaaloobay waxay mudan cadaab darab, kuwii xaqa rumeeyay oo camal fiican falayna waxay mudan dambi dhaag iyo ajri wayn.'),
(3668, 35, 8, 'Ruuxse loo qurxiyay camalkiisa xun oo u arkay inuu fiicanyahay (ma lamid baa kaan saas falin), Eebe cidduu doono yuu dhumiyaa Ruuxuu doonana wuu Hanuuniyaa ee yeyna Naftaadu ku tagan walbahaarkooda, Eebe waa ogyahay waxay samayne.'),
(3669, 35, 9, 'Eebe waa kan diray dabaysha oy kiciso daruuro markaasaan u kaxayn magaalo dhimatay (abaarowday) markaasaan ku noolaynay roobkaas dhulka intu dhintay ka dib, saas weeye soo bixintiina.'),
(3670, 35, 10, 'Ruuxii dooni sharaf Eebe iska lehsharaf dhammaanteed, xagiisayna u koraan kalimooyinka fiican, camalka suubanna wuu kor yeelaa, kuwa xumaanta ku dhagra (maamula) waxay mudan cadaab daran, dhagarta kuwaasna way uun halaagi.'),
(3671, 35, 11, 'Eebe waa kan idinka abuuray Carro, hadana dhibic, markaas Lab iyo dhadig, haweenayna wax uurka kuma qaado mana dajiso ogaansho Eebe mooyee, ruuxii cimri dhe iyo ruuxii cimri gaabanba waxay ku sugan yihiin kitaabka Eebe, waana u fudud yahay.'),
(3672, 35, 12, 'Ma eke labada badood taasi waa macaantahay si daran, taasina waa dhanaan daran mid kastana waxaad ka cunaysaan hilib cusub, waxaadna kala baxdaan Macdan aad xidhataan, waxaadna arkaysaan doonta oo jiidhi si aad u dalabtaan fadliga Eebe iyo inaad ku mahadisaan.'),
(3673, 35, 13, 'Habeenka yuu Eebe dhexgaliyaa maalinta Maalintana wuxuu dhexgaliyaa Habeenka, Qoraxda iyo dayaxana wuu sakhiray mid kastana wuxuu ku socda muddo magacaaban, saas waxa falana waa Eebihiin, isagaana xu- kunka iska leh, waxaadse baryeysaan (caabudaysaan) oo Eebe ka soo hadhay ma hantaan xuub laf timireed.'),
(3674, 35, 14, 'Hadaad u yeedhataana idin ma maqlaan, haday idinbase maqlaan wax idiima taraan, Qiyaamadana way diidigaalnimadooda, cid wax og, sidaana wax kuugu warrama ma jiro.'),
(3675, 35, 15, 'Dadow idinka unbaa fuqara ah (u baahan) Eebe, Eebena waa hodan amaanan.'),
(3676, 35, 16, 'Hadduu doono wuu idin dhamayn wuxuuna keeni kuwo cusub.'),
(3677, 35, 17, 'Arrintaasna wax Eebe ku culus ma aha.'),
(3678, 35, 18, 'Nafna naf kale dambigeed ma qaado, haddii looguyeedho Naf culusoo dambaabday inay xambaarto (danbi naf kale) lagama xambaaro waxba haba noqoto qaraabanee, waxaad uun digi kuwa ka yaabi Eebe iyagoo maqan, oo ooga salaadda, ruuxii isdaahiriyana wuxuu uun isudaahiriyay naftiisa, xaga Eebe unbaana loo ahaan (geeri dabadeed).'),
(3679, 35, 19, 'Ma Eka indhoole iyo wax arke.'),
(3680, 35, 20, 'Mana eka mugdiyo iyo iftiin.'),
(3681, 35, 21, 'Mana eka hoos iyo kulayl.'),
(3682, 35, 22, 'Mana eka kuwa nool iyo kuwo dhintay, Eebana wuxuu wax maqashiiyaa cidduu doono, mase maqashiin kartid cid qubuur ku sugan (gaaladuna) waa wax dhintay oo kale).'),
(3683, 35, 23, 'Waxaan u dige ahayn ma tihid.'),
(3684, 35, 24, 'Annagaa kugu dirray xaq adoo bishaarayn oo digi, umadaan udige loo dirinna ma jirto.'),
(3685, 35, 25, 'Haday gaaladu ku beeniyaan waxaa wax beeniyay kuwii ka horreeyay, waxay ula timid rasuuladoodii xujooyin iyo kutub (waano) oo xukun leh.'),
(3686, 35, 26, 'Markaasaan qabtay kuwii gaaloobay, sayse noqotay ciqaabtaydii (aan ciqaabay kuwaas)'),
(3687, 35, 27, 'Miyaadan arkayn in Eebe ka soo dajiyay Samada biyo markaasna aan ku soo bixinaa Midho ay kala duwanyihiin midabkoodu, Buurahana waxaa ka mid ah wadooyin cad cad, iyo kuwo gaduud ah oo midabo kala duwan leh iyo kuwo madow daran ah.'),
(3688, 35, 28, 'Dadka iyo dhulsocotada iyo xoolahaba waxaa ka mid ah midabo kala duwan saas oo kale.'),
(3689, 35, 29, 'Kuwa akhriya kitaabka Eebe oo salaadda ooga wax ka nafaqeeya (ka bixiya) waxaan ku arsaaqnay qarsoodi iyo caddaanba waxay rajayn tijaaro (ganacsi) aan baaraynin.'),
(3690, 35, 30, 'Si Eebe ugu oofiyo ajrigooda uguna siyaadiyo fadligiisa, illeen Eebe waa dambi dhaafe mahad badane.'),
(3691, 35, 31, 'Kaan kuu waxyoonay ee Quraanka ah unbaa xaq ah wixii ka horreeyayna u rumayn Eebana addoomadiisa waa ogyahay, arkaana.'),
(3692, 35, 32, 'Markaasaan dhaxalsiinay kitaabka kuwaan dooranay oo addoomadanada ka mid ah, waxaase ka mid ah mid dulmiyay naftiisa, iyo mid dhexdhexaystay, iyo mid ku hanuunay khayrka idanka Eebe, arrintaasna waa fadli weyn.'),
(3693, 35, 33, 'Jannada cadnin yeyna gali iyagoo loogu xidhi dhexdeed asaawir (wax loogu xarragoodo) oo dahab iyo Jawharba leh, dhar kooduna dhexdeeda waa xariir.'),
(3694, 35, 34, 'Waxayna dhihi waxaa mahad leh Eebaha naga tagsiiyay walbahaarka, illeen Eebeheen waa danbi dhaaf badane mahad badane.'),
(3695, 35, 35, 'Eebihii nadagjiyay guri nagaadi fadligiisa dartiis (janno) nagumana taabto dhexdeeda dhib, nagumana taabto daal.'),
(3696, 35, 36, 'Kuwii gaaloobay waxay mudan naarta Jahannamo, laguma xukumo geeri oy dhintaan, lagamana fududeeyo Cadaabkeeda, saasaana ku abaal marinaa mid kastoo gaalimo badan.'),
(3697, 35, 37, 'Waxay ku qaylyi dhexdeeda Eeboownaga bixi aan camal fiican ookaan sameynjirray ka duwan fallee, miyaanaan idin cimri siin muddo uu wax ku xusuusto ruuxii wax xusuusan oosan idiin imaanin dige (Nabiga) ee dhadhamiya gaaloy (ciqaabta) uma sugnaanin daalimiin gargaaree.'),
(3698, 35, 38, 'Eebe wuxuu ogyahay waxa ku maqan samooyinka iyo dhulka, wuxuuna ogyahay waxa laabta (Qalbiga) ku sugan.'),
(3699, 35, 39, 'Eebe waa kan idinka dhigay kuwo dhulka iska badala, ruuxii gaaloobase gaalnimadiisu isagaydhibh, Gaaladana uma kordhiso gaalimadoodu Eebahood agtiisa cadho mooyee, umana kordhiso gaalada gaalnimaadu khasaaro mooyee.'),
(3700, 35, 40, 'Ka warrama shariigyada aad baryeysaan ood (caabudaysaan) Eebe ka sokor, i tusiya waxay abuureendhulka, ama wadaag ah oy ku leeyihiin samooyinka, mise waxaan siinay Kitaab oo ay xujeesan, saas ma aha ee gaalada qaarkood qaar uma yabooho waaxaan Dhagar ahayn.'),
(3701, 35, 41, 'Eebaa ka haya Samooyinka iyo dhulka inay gilgishaan (oo tagaan), haday gilgishaana waxaan Eebe ahayn oo qaban ma jiro, Eebena waa dulbadane dhaafid badan.'),
(3702, 35, 42, 'Waxay ugu dhaarteen Eebe dhaar adag hadduu uyimaado udige inay noqdaan kuwa ka hanuunsan (ka toosan) umadaha kale, markuu uyimid digihiina uma siyaadinin waxaan carar ahayn.'),
(3703, 35, 43, 'isku kibrin dhulka iyo dhagar xun darteed, dhagarna xumaan waxaan ahayn ehelkiisa kuma dhaco ee miyaad sugaysaan waxaan wadadii kuwii hore ahayn, mana helaysid sunnada Eebe wax badala, ma helaysid sunnada Eebe wax wareejiya. Miyeyna ku soconin dhulka ooyna dayeeynin siday noqotay cidhibtii kuwii ka horreeyey gaaladii Makaad, waxay ahaayseen kuwo ka xoogbadan, Ebana wax ku cajisiya (daaliya) ma jiro Samooyinka iyo dhulka dhexdooda midna, waana oge wax walba kara.'),
(3704, 35, 44, 'Miyeyna ka soconin dhulka ooyna dayeeynin siday noqotay cidhibtii kuwii ka horreeyey gaaladii Makaad, waxay ahaayeen kuwo ka xoogbadan, Eebana wax ku cajisiya (daaliya) ma jiro Samooyinka iyo dhulka dhexdooda midna, waana oge wax walba kara.'),
(3705, 35, 45, 'Haddu Eebe u qaban dadka waxay kasbadeen (oo xumaan ah) kagama tageen dhulka korkiisa waxsocda, laakin wuxuu dib ugu dhigi tan iyo muddo magabaaban, marka ajashoodu (Geeridoodu) timaadana Eebe addoomadiisa wuu arkaa.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, '(Waxay ku tusin mucjisada Quraanka).'),
(3707, 36, 2, 'Quraanka xigmada leh yaan ku dhaartaye.'),
(3708, 36, 3, 'Nabiyoow waxaad ka mid tahay kuwa la diray (Rasuulada).'),
(3709, 36, 4, 'Jid toosanna waad ku sugan tahay.'),
(3710, 36, 5, 'Quraankuna waa soo dajinta Eebaha adkaada ee naxariista,'),
(3711, 36, 6, 'inaad ugu digto qoom aan loo digin Aabayaalkood oo halmaansan.'),
(3712, 36, 7, 'Dhab ahaanna waxaa ugu waajibay badankoodo (ciqaab) mana rumeeyaan xaqa.'),
(3713, 36, 8, 'Annagaa yeelay cunahooda Bir waxayna qaban tan iyo gadhka Madaxayna kor u taagi (dhibaato darteed).'),
(3714, 36, 9, 'Waxaana yeelay hortooda jidaar gadaashoodana jidaar waana indha tiray waxna ma arkaan.'),
(3715, 36, 10, 'waxaa isku mid ah hadaad u digto iyo hadaadan udigin (marna) ma rumeeyaan.'),
(3716, 36, 11, 'Waxaad un u digi (ooy anfici digidu) Ruxii raaca Quraanka oo Eebe ka yaaba isagoon arkayn, uguna bishaaree dambi dhaaf iyo ajiir wanaagsan.'),
(3717, 36, 12, 'Annaagaa nooleyna wixii dhintay, waana qornaa waxay hor marsadeen iyo raaadkoodiiba, wax walbana waxaan ku koobnay Kitaab muuqda.'),
(3718, 36, 13, 'Uga yeel tusaale asaxaabtii magaaladii markay uyimaadeen Rasuulladii (loo soo diray).'),
(3719, 36, 14, 'Markaan u diray xagooda labo, ooy beeniyeen, markaas aan ku xoojinay mid saddexaad, oy dhaheen idinkaa naloo kiin soo diray.'),
(3720, 36, 15, 'Waxayna dhaheen (gaaladii) idinku ma tihidiin waxaan bashar (dad) nala mid ah ahayn, Eebaha Raxamaan ahna waxba ma soo dajinin, idinkuna waxaan been ahayn masheegaysaan.'),
(3721, 36, 16, 'waxay dhaheen (Rasuuladii) Eebahannaa og in naloo kiin soodiray.'),
(3722, 36, 17, 'Korkannagana ma aha waxaan gaadhsiin cad ahayn.'),
(3723, 36, 18, 'Waxay dhaheen (gaaladii) waan idin baasaysannay, haddaydaan joogin waan idin dhagaxyeynaynaa, waxaana xagganaga idinka taahan cadaab daran.'),
(3724, 36, 19, 'Waxay dhaheen Rasuulladii baaskiinu wuu idinla joogi, haddii laydin waaniyo (Miyubaasku idinlajiri) waxaadse tihiin qoon xadgudbay,'),
(3725, 36, 20, 'Waxaa ka yimid Magaalada dhankeeda fog nin dagdagi (ordi) wuxuuna ku yidhi qoomkayow raaca rasuullada.'),
(3726, 36, 21, 'Raaca cid aan idin warsanayn ujuuro, oo hanuunsan.'),
(3727, 36, 22, 'Maxaana u caabudaynin Eebihii i abuuray xagiisana laydiin celin.'),
(3728, 36, 23, 'Miyaan ka yeelan wax ka soo hadhay ilaahyo hadduu Eebaha Raxmaan ah dhib ila doono ayan wax iitareyn shafeecadoodu waxba, ayana i korinayn.'),
(3729, 36, 24, 'Markaas waxaan ku sugnaan baadi muuqata (cad).'),
(3730, 36, 25, 'Anigu waxaan rumeyay Eebehiin ee maqla.'),
(3731, 36, 26, 'Waxaa lagu yidhi gal jannada wuxuuna yidhi shallaytadaydee qoomkaygii muu ogaado.'),
(3732, 36, 27, 'Waxa Eebe iigu dambi dhaafay iigana yeelay kuwo la karaameeyey.'),
(3733, 36, 28, 'Kuma aanaan dajinin qoomkiisii gadaashiis Januud samad ka timid, mana aanaan ku soo dajinin.'),
(3734, 36, 29, 'Waxaan qaylo Kaliya ahayna laguma halaagin oo markaas ay bakhtiyeen (halaagsameen).'),
(3735, 36, 30, 'Nidaamo (Qoomamo) yaa u sugmaatay addomada Rasuulkii u yimaadaba waxay ahaadaan kuwo ku jees jeesa.'),
(3736, 36, 31, 'Miyayna arkeyn intaan halaagnay hortood quruumo, aan xagooda usoo noqonayn.'),
(3737, 36, 32, 'Dhammaanna waxaa lagu soo kulmin agtannada.'),
(3738, 36, 33, 'Waxaa calaamo u ah dhulka dhinta (abaarooba) oo noolayno kana soo bixino midho ay wax ka cunaan.'),
(3739, 36, 34, 'Waxaana yeellay dhexdeeda beero Timir iyo Cinabyo ah, waxaana ka dillaacinay dhexdeeda ilo (Biyo ah).'),
(3740, 36, 35, 'Si ay u cunaan midhihiisa iyo waxay camal faleen gaemahoodu, miyeyna mahdinaynin Eebe.'),
(3741, 36, 36, 'Waxaana nasahana Eebaha abuuray nooeyada dhamaanteed oo ah waxa dhulka soo dhaliyo iyo naftoodaba iyo wax ayna ogayn.'),
(3742, 36, 37, 'Waxaaa dadka calaamo ugu filan habeenkaan Maalinta ka soo siibno, oo markaas ay mugdi galaan Dadkii.'),
(3743, 36, 38, 'Qorraxduna waxay kuorodaa meelay leedahay, taasina waa qadaraadda Eebaha adkaada ee waxwalba og.'),
(3744, 36, 39, 'Dayaxana waxaan uqadaray (uyeelay) meelo uu ku hoydo intuu ka noqdo sida laan gabowday oo kale.'),
(3745, 36, 40, 'Qorraxdana uma suurowdo inay haleesho Dayaxa Habeenkana uma suuroowdo inuu maalinta ka hormaro, mid kastana falagiisuu dhex dabaalan.'),
(3746, 36, 41, 'Waxaa calaamo ugu filan dadka inaan ku xambaaray aabaylkood Doontii buuxday.'),
(3747, 36, 42, 'Waxaana uga abuuraynaa iyada oo kale waxay Koraan.'),
(3748, 36, 43, 'Hadaan doonana waan maansheynaa wax u gargaarana mahelaan lamana koriyo.'),
(3749, 36, 44, 'Naxariis Eebe iyo uraaxayn tan iyo wakhti mooyee.'),
(3750, 36, 45, 'Markaa gaalada lagu dhaho ka dhowrsada waxa hortiina ah iyo waxa gadaashiina ah waxaad udhawdihiin in laydiin naxariistee (ma maqlaan).'),
(3751, 36, 46, 'Aayad kasta oo u timaada oo ka mid ah aayaadka Eebahood way ka jeedsadaan.'),
(3752, 36, 47, 'Marka lagu dhaho bixiya waxa Eebe idinku arzuqay wax ka mid ah waxay ku dhahaan kuwa gaaloobay kuwii rumeeyay ma waxaan quudinaynaa cid hadduu doono Eebe uu quudin lahaa, waxaan baadi cad ahayna kuma sugnidin.'),
(3753, 36, 48, 'Waxay odhan gaaladu waa goorma (waqtiguu dhici) yaboohaas (Qiyaamadu) hadaad run sheegaysaan.'),
(3754, 36, 49, 'Ma sugaayaan (gaaladu) hal qaylo mooyee oo qabanaysa iyagoo doodi.'),
(3755, 36, 50, 'Mana karaan dardaaran ehelkoodiina uma soo noqdaan.'),
(3756, 36, 51, 'Suurkaa la afuufi markaasaa dadku qubuurahooda xagga Eebahood uga soo dagdagi.'),
(3757, 36, 52, 'Iyagooy dhihi gaaladu waan khasaarnaye yaa naga soobixiyey meeshaan jiifnay (qabrigii) taasina waa wixii Eebaha Raxmaan ah yaboohay Rasuulladuna ku run sheegeen.'),
(3758, 36, 53, 'Waxaan qayki kaliyo ahayn ma aha, maarkaasaa dhamaantood agtanada lagu soo kulmin.'),
(3759, 36, 54, 'Maantana (qiyaamada) lagama dulmiyo nafna waxba, laydiin kana abaalmarin maayo waxaad camal falayseen mooyeen.'),
(3760, 36, 55, 'Asaxaabta Jannada (ehelkeeda) Maantaa waxay ku shuqloonyihiin Nicmo iyo farax.'),
(3761, 36, 56, 'Iyaga iyo haweenkooduba waxay ku sugnaan hadh, waxayna ku dangiigsanaan sariiro (sare).'),
(3762, 36, 57, 'Waxayna dhexdeeda ka heli faakihad, waxayna heli waxay dalbaan.'),
(3763, 36, 58, 'salaan Eebaha Naxariista xagiisa ka timidna way heli.'),
(3764, 36, 59, 'Soocma maanta danbiilayaalyahow (gaalada).'),
(3765, 36, 60, 'Miyaanan idinkula ballamin Bani aadamow inaydaan caabudin Dhaydaan, illen col muuqda yuu idiin yahay.'),
(3766, 36, 61, 'Ood ani i caabudaan, taasina waa wadada toosan.'),
(3767, 36, 62, 'Dhab ahaan wuxuu udhumiyay (Shaydaan) xaggiina dad badan, miyaydaan wax kasaynin.'),
(3768, 36, 63, 'Tanina waa jahannamadii laydiinku yaboohay.'),
(3769, 36, 64, 'Ee gala Maanta gaalimadinii darteed.'),
(3770, 36, 65, 'Maanta waxan daboolaynaa afkooda, waxaana nala hadli gacmahooda waxaana ku marag furi lugahooda waxay kasban jireen.'),
(3771, 36, 66, 'Haddaan doono waan indha tiraynaa oo markaas ay udag dagaan wadada, sayse u arki.'),
(3772, 36, 67, 'Hadaan doonana waan doorinaynaa, mana karayaan tagid iyo soo noqosho midna.'),
(3773, 36, 68, 'Ruuxaan cimri siinno waxaannu ku gadin abuuridda (waannu tabar yarayn) ee miyaydaan wax kasayn.'),
(3774, 36, 69, 'Maanaan barin Nabiga shicirka, Kumana haboona, waxaan (barna) un waano iyo Quraan cad.'),
(3775, 36, 70, 'Si uu ugu digo ruuxii nool (xaqa raaci) cadaabna ugu sugnaado Gaalada.'),
(3776, 36, 71, 'Miyeyna arkaynin inaan uga abuuray waxaan sameynay xoolo ay hantaan.'),
(3777, 36, 72, 'OOn u laylinay, ooy waxna ka Koraan waxna ku cunaan.'),
(3778, 36, 73, 'Waxaana ugu sugan manfacado iyo cabid ee miyayna mahdinaynin Eebe.'),
(3779, 36, 74, 'Waxay ka yeesheen Eebe ka soke ilaahyaal inay u gargaaraan.'),
(3780, 36, 75, 'Mana karaan gargaarkooda waxayna uyihiin Junuud (askar) kulmisan.'),
(3781, 36, 76, 'Ee yeyna ku walbahaar galinin hadalkoodu annagaa og qaxay qarsan iyo waxay muujinbee.'),
(3782, 36, 77, 'Miyuusan arag dadku (oo ogayn) Inaan ka abuuray dhibic, markaas uu noqday dood badane cad.'),
(3783, 36, 78, 'Wuxuuna noo yeelay tusaale wuuna halmaamay abuurkiisa wuxuuna yidhi yaa noolayn lafo baaliyoobay.'),
(3784, 36, 79, 'Waxaad dhahdaa waxaa noolayn Eebihii ahaysiiyay markii horaba, abuur walbana waa ogyahay.'),
(3785, 36, 80, 'Eebaha idiinka yeelay geedka cagaaran dab, oo markaas aad bursataan.'),
(3786, 36, 81, 'Eebaha abuuray Samooyinka iyo dhulka miyuusan karin inuu abuuro isagoo kale, waa karaa, waana abuur badane ogaansho badan.'),
(3787, 36, 82, 'Amarka Eebena markuu wax inuu abuuro doono waa inuu ku yidhaahdo uun ahaw oo uu ahaado.'),
(3788, 36, 83, 'Waxaa nasahan (xumaan ka fog) Eebaha awooddiisu ku jirto xukunka wax kasta, xaggiisana laydiinsoo celin.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, '(Waxaan ku dhaartay) malaa\'igta safan.'),
(3790, 37, 2, 'Iyo tan kaxaysa daruuraha.'),
(3791, 37, 3, 'Iyo tan akhrida Quraanka.'),
(3792, 37, 4, 'Ee ilaahiin waa mid (qudha).'),
(3793, 37, 5, 'Waa Eebaha samooyinka iyo dhulka iyo waxa udhaxeeya, iyo Eebaha qorrax ka soo bixidda.'),
(3794, 37, 6, 'Annagaa ku qurxinay Samada dhaw quraxda xidiga ah.'),
(3795, 37, 7, 'Iyo ka dhowrid shaydaan kastoo madax adag.'),
(3796, 37, 8, 'Mana dhagaystaan jameecada sare, (Malaa\'igta) waxaana laga soo tuuraa dhan kasta.'),
(3797, 37, 9, 'Iyagoo la cryi oo cadaab joogta ah usugnaaday.'),
(3798, 37, 10, 'Marka laga reebo mid mid dafa hadalka oo uu raaco danab ifi.'),
(3799, 37, 11, 'Waydii gaalada ma iyagaa ku daran abuuridda mise waxaan abuuray, annagaa ka abuurray dhoobo dhagta.'),
(3800, 37, 12, 'Waxaadse la yaabtay (beenintooda) iyaguna way jees jeesi.'),
(3801, 37, 13, 'Marka la waaniyana ma waantooban.'),
(3802, 37, 14, 'Hadday calaamo arkaana way ku jees jeesaan.'),
(3803, 37, 15, 'Waxayna dhahaan kani waxaan sixir cad ahayn ma aha.'),
(3804, 37, 16, 'Waxayna dhahaan ma markaan dhimanno oon carro noqono iyo lafo yaa nala soo bixin.'),
(3805, 37, 17, 'Ama aabayaalkannagii hore (iyagana malasoocelin).'),
(3806, 37, 18, 'Waxaad dhahdaa haa waalaydin soo celin idinkoo waliba dullaysan.'),
(3807, 37, 19, 'Xaaladduna waa uun qaylo kaliya markaas, waxaa soo bixi dadkii oo wax eegi.'),
(3808, 37, 20, 'Waxayna dhihi gaaladii halaagganaggee kan waa maalintii abaalmarinta.'),
(3809, 37, 21, 'Waxaana lagu dhihi kani waa maalintii kala bixinta ee aad beeninjirteen.'),
(3810, 37, 22, 'Kulmiya kuwii daalimiinta ahaa iyo wixii la mid ahaa iyo waxay caabudijireen.'),
(3811, 37, 23, 'Oo Eebe ka soo hadhay, tusiyana jidka Jaxiimo.'),
(3812, 37, 24, 'Joojiyana waxbaa la waydiine.'),
(3813, 37, 25, '(Waxaana lagu dhihi) Maxaad leedihiin oydaan isugu gargaaraynin.'),
(3814, 37, 26, 'Saas ma aha ee maanta way isdhiibeen.'),
(3815, 37, 27, 'Wuxuu qaabilay qaarkood qaarko kale iyago wax iswarsan.'),
(3816, 37, 28, 'Waxayna dhaheen kuwii waxraacay (raaciyadii) waxaa ahaydeen kuwo nooga yimaadda xaga midig (wanaaga).'),
(3817, 37, 29, 'Waxay dhaheen madaxdii ma aha ee idinkaan mu\'miniin ahayn.'),
(3818, 37, 30, 'Wax xoog ah oon idin ku lahayn ma jirin waxaadse ahaydeen qoom xad gudbey.'),
(3819, 37, 31, 'Markaasuu nagu waajibay (nagu rumoobey) hadalkii Eebe (ciqaab) waxaana dhadhaminaynaa (cadaabka).'),
(3820, 37, 32, 'Markaasaan idin dhuminay annaguna waan dhunsanayn.'),
(3821, 37, 33, 'Iyagu maalintaas cadaabkay wadaagi.'),
(3822, 37, 34, 'Annaguna saasaan kufalla dambiilayaasha.'),
(3823, 37, 35, 'Waxayna ahaayeen marka lagu dhaho Eebe mooyee ilaah kale majiro kuwa iskibriya.'),
(3824, 37, 36, 'Oo dhaha ma waxaan uga tagaynaa ilaahyadannada gabayaa waalan.'),
(3825, 37, 37, 'Saas ma ahaa ee wuxuu la yimid (Nabigu) xaq wuuna rumeeyey Rasuulladii (Eebe soodiray).'),
(3826, 37, 38, 'Idinku (gaaloy) waxaaad dhadhamisaan cadaabka daran (wax xanuujiya).'),
(3827, 37, 39, 'Waxaan camalkiina ahayna laydinkama abaalmariyo.'),
(3828, 37, 40, 'Addoomada Eebe ee niyada san (xumaan ma arkaan).'),
(3829, 37, 41, 'Waxayna mudan kuwaasi risqi layaqaanno (Janno).'),
(3830, 37, 42, 'Faakihadna way heli, iyagoo lagu sharti.'),
(3831, 37, 43, 'Jannooyinka Naciima.'),
(3832, 37, 44, 'Waxayna ku sugnaan sariiro iyagoo is qaabili.'),
(3833, 37, 45, 'Waxaana lala kor socon weel laga soo buuxiyay il (Jaano).'),
(3834, 37, 46, 'Taasoo cad oo u macaan kuwa cabi.'),
(3835, 37, 47, 'Mana laha caqli qaadid iyo waalli midna (Khamrada Jannada).'),
(3836, 37, 48, 'Agtoodana waxaa ahaan haween dhoowsoon oo indha waaweyn.'),
(3837, 37, 49, 'Ood moodo ukun la dhowray.'),
(3838, 37, 50, 'Wuxuuna qaabilay qaarkood qaarka kale (ehelo Jannaha) iyagoo wax iswaydiin.'),
(3839, 37, 51, 'Wuxuuna dhihi mid hadlay oo ka mid ah waxaan kulahaa (adduunkii) saaxiih.'),
(3840, 37, 52, 'Dhihi jiray adigu mawaxaad ka mid tahay kuwa rumeeyay (soobixinta).'),
(3841, 37, 53, 'Ma markaan dhimana oon carro noqono iyo lafo yaa nala abaalmarin.'),
(3842, 37, 54, 'Wuxuuna dhihi mayla dayeeysaan.'),
(3843, 37, 55, 'Markaasu dayaa kuna arkaa jaxiima dhexdeeda.'),
(3844, 37, 56, 'Wuxuuna dhahaa Eebaan kudhaartayee waxaad udhaweyd inaad ihalaagto (idhumiso).'),
(3845, 37, 57, 'Haddayna Nicmada Eebe jirinna waxaan ahaan lahaa kuwa lagu soo kulmiyay (naarta).'),
(3846, 37, 58, '(Waxayna dhahaan ehelu jannuhu) miyeynaandhimaneynin.'),
(3847, 37, 59, 'Geerideenii hore mooyee laynama cadaabayn.'),
(3848, 37, 60, 'Taasina waa uun liibaanta wayn.'),
(3849, 37, 61, 'Ee taasoo kale ha faleen kuwa camal fali.'),
(3850, 37, 62, 'Mataasaa marti qaad u fiican (Jannada) mise geedii zaquum (xumeyd).'),
(3851, 37, 63, 'Annagaa uga yeelay fitno (imtixaan) daalimiinta.'),
(3852, 37, 64, 'Waana geed kasoobaxda gunta jaxiimo.'),
(3853, 37, 65, 'Midhaheeduna waxaad moodaa madaxyo shaydaan.'),
(3854, 37, 66, 'Iyaguna (ehelu naarku) waxbay ka cunaan, waxayne ka buuxiyaan caloosha.'),
(3855, 37, 67, 'Waxayna kalooy mudan dheehid biyo kulayl daran.'),
(3856, 37, 68, 'Markaas waxay ku noqon jaxiimo.'),
(3857, 37, 69, 'Waxay heleen aabayaalkood oo baadi ah.'),
(3858, 37, 70, 'Iyaguna raadkoodii bay ku dag dagi.'),
(3859, 37, 71, 'Waxaa dhab ahaan u dhumay hortood badi kuwii hore.'),
(3860, 37, 72, 'Waxaana udiraydhexdooda udigayaal.'),
(3861, 37, 73, 'Ee eeg siday noqotay cidhibtii kuwii loo digay.'),
(3862, 37, 74, 'Hasa yeeshee addoomadii Eebe ee niyada samaa (cadaab ma arkaan).'),
(3863, 37, 75, 'Waxaa dhabahaan noogudhawaaqay (beryey) Nabi nuux, Cid wax ajiibana (maqlana) annagaa u fiican.'),
(3864, 37, 76, 'Waxaana Ka korinay isaga iyo ehelkiisiiba shidadii waynayd (maanshayntii).'),
(3865, 37, 77, 'Waxaana ka yeelay faraciisii kuwa soo hadhay uun.'),
(3866, 37, 78, 'Waxaana ugu reebnay kuwo dambeeya (amaan ah).'),
(3867, 37, 79, 'NabadGalyo Nuux korkiisa adduunyada ha udhex ahaato.'),
(3868, 37, 80, 'Saasaana ku abaal marinaa samafalayaalka.'),
(3869, 37, 81, 'Wuxuuna ka midyahay addomadannada rumeeyay (xaqa).'),
(3870, 37, 82, 'Markaasaan maanshaynay kuwii kale,'),
(3871, 37, 83, 'Waxaa kamid ah gargaarayaashiisa (Jidkiisaqaaday) Nabi Ibrahim.'),
(3872, 37, 84, 'Markuu ula yimid Eebihiis qalbi fayow (xaq rumeeyey).'),
(3873, 37, 85, 'Markuu ku yidhi Aabihiis iyo qoomkiisii maxaad caabudaysaan.'),
(3874, 37, 86, 'Ma been abuurasho yaad ilaahyo Eebe ka soo hadhay u dooranaysaan.'),
(3875, 37, 87, 'Maxaadse ka maleysaneysaan Eebaha Calaamka.'),
(3876, 37, 88, 'Markaasuu eegay Nabi Ibraahim xiddigaha.'),
(3877, 37, 89, 'Wuxuuna yidhi waan bukaa.'),
(3878, 37, 90, 'Markaasay katageen kana jeedsadeen.'),
(3879, 37, 91, 'Markaasuu aaday Ilaahyaalkoodii (sanamyadii) wuxuuna ku yidhi miyeydaan (wax) cunayn.'),
(3880, 37, 92, 'Maxaad leedihiin oydaan la hadlaynin.'),
(3881, 37, 93, 'Markaasuu foorariyay midigta oo ku garaacay'),
(3882, 37, 94, 'Wayna soo qaabileeen gaaladii xaggiisa iyagoo dag dagi. (markuu jajabiyay).'),
(3883, 37, 95, 'Wuxuuna ka yidhi ma waxaad caabudaysaan waxaad qoraysaan (sameyseen).'),
(3884, 37, 96, 'Eebaana idin abuuray idinka iyo waxaad camalfalaysaan.'),
(3885, 37, 97, 'Waxayna dhaheen u dhisa meel (dab lagushido) kuna tuura Jaxiimo (dabka).'),
(3886, 37, 98, 'Waxay la dooneen dhagar iyagaana ka yeellay kuwo hooseeya (laga adkaaday).'),
(3887, 37, 99, 'Wuxuu yidhi anigu waxaan aadi Eebahay (hijro) wuuna i toosin.'),
(3888, 37, 100, 'Eebow wax iga sii kuwa suuban (ilmo).'),
(3889, 37, 101, 'Waxaana ugu bishaaraynay wiil dabeeco.'),
(3890, 37, 102, 'Markuu ku gaadhay agtiisa socod wuxuu ku yidhi Nabi Ibraahim wiilayow waxaan ku arkay hurdada anoo ku gawrici ee eeg waxaad fali, wuxuuna yidhi aabow samee waxa lagu faray waxaad heli hadduu Eebe doono inaan kamidnoqdo kuwa samree.'),
(3891, 37, 103, 'Markay hogaansameen oos u lagday wajiga.'),
(3892, 37, 104, 'Yaan udhawaaqnay Ibraahimow.'),
(3893, 37, 105, 'Dhab ahaan baad ugu rumeysay riyadii, sasaana ku abaalmarinaa samafalayaasha.'),
(3894, 37, 106, 'Taasina waa imtixaan muuqda (oo weyn).'),
(3895, 37, 107, 'Waxaana ku furanay loog weyn (wan).'),
(3896, 37, 108, 'Waxaana ugaga tagnay (ugureebnay) kuwa dame.'),
(3897, 37, 109, 'Nabadgalyo (salaan) Nabi Ibraahim korkiisa ha ahaato.'),
(3898, 37, 110, 'Saasaana ku abaal marinaa samofalayaasha.'),
(3899, 37, 111, 'Wuxuu ka mid ahaa addoomadanada mu\'miniinta ah.'),
(3900, 37, 112, 'Waxaan ugu bishaaraynay Isxaaq oo Nabi suuban ah.'),
(3901, 37, 113, 'Waana barakaynay Ibraahim iyo Isxaaqba, waxaana ka mid ah faracooda mid samagala iyo mid naftiisa si cad u dulmiya.'),
(3902, 37, 114, 'Dhab ahaan yaan ugu mannaysanay (ugunicmeeynay) Nabi Muuse iyo NabiHaruun (Nabinimo).'),
(3903, 37, 115, 'Waana ka korinay iyaga iyo Qoomkooda dhibaatadii waynayd.'),
(3904, 37, 116, 'Waana u gargaaray, waxayna noqdeen kuwo adkaaday (sareeyey).'),
(3905, 37, 117, 'waxaana siinay Kitaabkii caddaa.'),
(3906, 37, 118, 'Waxaana ku hanuuninay Jidka toosan.'),
(3907, 37, 119, 'Waxaana ugu dhaafnay dadka dambe, (amaan ah).'),
(3908, 37, 120, 'Nabadgalyo ha ahaato Nabi Muuse iyo Nabi Haaruun korkooda.'),
(3909, 37, 121, 'Saasaan ku abaalmarinaa kuwa wanaagga fala,'),
(3910, 37, 122, 'Waxayna ka midyihiin addoomadanada mu\'miniinta ah.'),
(3911, 37, 123, 'Ilyaasna Wuxuu ka mid ahaa Rasuullada.'),
(3912, 37, 124, 'Markuu ku yidhi qoomkiisii miyeydaan dhawsaneyn (Eebe kayaabayn).'),
(3913, 37, 125, 'Mawaxaad baryeysaan Bacli (Sanam) ood ka tagaysaan Eebaha abuurka wanaajiya.'),
(3914, 37, 126, 'Ee ah Eebihiina iyo Eebaha aabayaalkiinii hore.'),
(3915, 37, 127, 'Markaasay beeniyeen, waxaana loo soo kulmin (Cadaab).'),
(3916, 37, 128, 'Laakiin adoomada Eebe ee niyada san (waxay lakulmi khayr).'),
(3917, 37, 129, 'Waxaana ugu reebnay dadka danbe (amaan ah).'),
(3918, 37, 130, 'Nabadgalyo ha ahaato Ilyaas korkiisa.'),
(3919, 37, 131, 'Saasaana ku abaalmarinaa kuwa sama fala.'),
(3920, 37, 132, 'Wuxuuna ka midyahay adoomadanada mu\'miniinta ah.'),
(3921, 37, 133, 'Nabi luudhna wuxuu ka mid ahaa rasuulada (la diray).'),
(3922, 37, 134, 'Xus markaan korinay isaga iyo ehelkiisaba dhamaan.'),
(3923, 37, 135, 'marka laga reebo Cajuuso (haweeneydiisii) oo ku hadhay halaaga.'),
(3924, 37, 136, 'Ka dibna waxaan halaagnay intii kale.'),
(3925, 37, 137, 'Idinkuna (reer makaad) waxaad martaan dhulkoodii waabariga.'),
(3926, 37, 138, 'Iyo Habeenkaba ee miyeydaan wax kasaynin.'),
(3927, 37, 139, 'Nabi Yuunusna wuxuu ka mid ahaa Rasuulladii (ladiray).'),
(3928, 37, 140, 'Xus markuu kudhuuntay (kucaray) Doontii buuxday.'),
(3929, 37, 141, 'Oo uu qoriritay nodayna mid laga reeyey. (Baddana lagu tuuray).'),
(3930, 37, 142, 'Kalluunna liqay isagoo ladagaalay.'),
(3931, 37, 143, 'Hadduusan noqonin kuwa Eebe u tasbiixsada (xusa).'),
(3932, 37, 144, 'Wuxuu kanagaan lahaa calooshiisa tan iyo maalinta lasoo bixin dadka.'),
(3933, 37, 145, 'Waxaana kutuuray banaan cidla ah isagoo buka.'),
(3934, 37, 146, 'Waxaana ka soo bixinay korkiisa Geedka yaqdhiinka ah (hadhayn darteed).'),
(3935, 37, 147, 'Waxaana u diray Boqolkun oo dad ah ama ka badan.'),
(3936, 37, 148, 'Wayna rumeeyeen, waxaana uraaxaynay (sugnay) tan iyo mudo.'),
(3937, 37, 149, 'Waydii gaalada, in Eebahaa gabdho yeeshay iyaguna Wiilal.'),
(3938, 37, 150, 'Mise waxaan abuuray malaa\'igta oo dhadig iyaguna ay Joogeen.'),
(3939, 37, 151, 'Taasi waa been abuurshadoodii ay dhahayeen.'),
(3940, 37, 152, 'Eeba waa dhalay waana beenaalayaal.'),
(3941, 37, 153, 'Ma wuxuu ka doortay gabdho wilasha.'),
(3942, 37, 154, 'Maxaa idiinku wacan ood sidaas u xukumaysaan.'),
(3943, 37, 155, 'Miyeeydaan wax xusuusaneyn.'),
(3944, 37, 156, 'Mise waxaa idiin sugnaatay xujo cad.'),
(3945, 37, 157, 'Keena Kitaabkiina (cadayn) hadaad runlayaaltihiin.'),
(3946, 37, 158, 'Waxay yeeleen gaaladu Eebe iyo Jinniga dhexdooda (qaraabo) nasab, Jinniguna dhabahaanbuu u ogyahay in gaalada lakeeni (qiyaamada).'),
(3947, 37, 159, 'Eebaa kanasahan (kadheer) waxay ku tilmaamayaan.'),
(3948, 37, 160, 'Laakin adoomada Eebe ee niyada suubiyey (Waa ka fogyihiin xumaan).'),
(3949, 37, 161, 'Idinka iyo waxaad caabudaysaan (gaalooy).'),
(3950, 37, 162, 'Waxaa ka fidmaysaan (dhumisaan) Eebe.'),
(3951, 37, 163, 'Marka laga reebo kuwagali jaxiimo.'),
(3952, 37, 164, 'Ruuxkastoo nagamid ah (malaa\'igta) wuxuuleeyahay meel lagu yaqaan.'),
(3953, 37, 165, 'Waana safannahay (cibaado darteed).'),
(3954, 37, 166, 'Waana tasbiixsanaa (qadarinEebe).'),
(3955, 37, 167, 'Waxayna ahaayeen gaaladu kuwii dhaha.'),
(3956, 37, 168, 'Hadduu yahay agtanada xuskii (warkii) kuwii hore (Kitaab).'),
(3957, 37, 169, 'Waxaan noqonlahayn addoomaha Eebe ee suuban.'),
(3958, 37, 170, 'Wayse ka gaaloobeen Kitaabkii (quraanka) wayna ogaandoonaan (waxa ku dhaca).'),
(3959, 37, 171, 'Dhab ahaanbuu ugu hormaray yaboohannagii adoomahan nagii aan diray (Rasuuladii).'),
(3960, 37, 172, 'In iyaga uun loo gargaari.'),
(3961, 37, 173, 'Iyo in junuudanada (xaqa raacay) ay adkaan (Guuleysan),'),
(3962, 37, 174, 'Ee kajeedso xagooda tan iyo muddo.'),
(3963, 37, 175, 'Fiirina iyaguna wayfiirin doonaane (waxa kudhaca).'),
(3964, 37, 176, 'Macadaabkanagay (Gaaladu) dadajisan.'),
(3965, 37, 177, 'Markuu ku dago cadaabku ardaagooda waxaa xumaan subaxa kuwa loodigay.'),
(3966, 37, 178, 'Ee kajeedso xagooda tan iyo muddo.'),
(3967, 37, 179, 'Fiirina iyaguna wayfiirindoonaane (waxa kudhaca).'),
(3968, 37, 180, 'Waxaana kanasahan Eebahaaga sharafta leh waxay kutilmaarnaan gaaladu (oo xun).'),
(3969, 37, 181, 'Nabadgalyana ha ahaato Rasuuladii korkooda.'),
(3970, 37, 182, 'Mahadna waxaa iskaleh Eebaha (Barbaariyaha) Caalamka ah.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'Xarafka hore wuxuu kutusin mucjisada (Quraanka), Quraanka sharafta leh yaan kudhaaranay (waalaydin soo bixin).'),
(3972, 38, 2, 'Kuwii gaaloobay waxay kusuganyihiin isla wayni iyo khilaaf (xaqdiido).'),
(3973, 38, 3, 'Imisaan halaagnay hortood qarni ay markaas dhawaaqeen waqti aan dhawaaq waxba tareynin.'),
(3974, 38, 4, 'Waxayna layaabeen gaaladii inuu uyimaado dige ka mid ah waxayna dhaheen gaaladii kani waa sixirroow beenaale ah.'),
(3975, 38, 5, 'Ma wuxuu ka dhigay ilaahyadii ilaah kaliya, arrintaasi waa wax lala yaabo.'),
(3976, 38, 6, 'Wuu kala tagay shirkii (madaxdii) kamidka ahayd, waxayna isu dhahen war socda oo ku samra (kusugnaada) Ilaahyaalkiina, arrintani waa wax laynala dooni.'),
(3977, 38, 7, 'Manaan kumaqlin tanoo kale diintii kale (Nasraaniyada) arintan Eebe kaliya waa wax la been abuurtay.'),
(3978, 38, 8, 'Ma quraankaa lagudajiyey isagoo na dhexjooga (anagaa ka mudan) waxayse kaga sugan yihiin Quraankayga shaki walina mayan dhahdhamin Cadaabkayga.'),
(3979, 38, 9, 'Ma agtaadaa khasnadaha naxariista Eebehaaga adkaada ee bixinta badan (khayrka) yahay.'),
(3980, 38, 10, 'Mise waxaa u sugnaaday xukunka Samooyinka iyo dhulka iyo waxa u dhaxeeya, ha uu koreen sallaanno xagga (samada).'),
(3981, 38, 11, 'Junuuda (Colka) halkaas joogaana (Gaalada) waa xisbiyo la jabin (laga adkaan).'),
(3982, 38, 12, 'Waxaa beeniyey xaqan iyaga ka hor qoomkii (Nabi) Nuux iyo (reer) caad iyo Fircoonkii tiirarka lahaa.'),
(3983, 38, 13, 'Iyo Reer Thamuud iyo qoomkii (Nabi) Luudh iyo Asaxaabtii kaynta (Qoomkii) Nabi Shucayb, kuwaas waa xisbiyadii (Xaqa beeniyey).'),
(3984, 38, 14, 'Midaan Rasuullada beenin majiro, markaasay ciqaabtaydii ku dhacday.'),
(3985, 38, 15, 'Kuwaasuna (Gaaladii Makaad) ma sugayaan waxaan hal qaylo ahayn, mana laha cilmi iyo sugid midna.'),
(3986, 38, 16, 'Waxayna dhaheen Gaaladii Eebow noo dadaji qaybtanada (Ciqaabtanada) Maalinta xisaabta ka hor, (Qiyaamada).'),
(3987, 38, 17, 'Ku samir (u adkayso) waxay sheegi xusuusana addoonkaygii Daa\'uud ahaa ee xoogga lahaa, wuxuuna ahaa noqosho (Toobad keen) badane.'),
(3988, 38, 18, 'Annagaa u sakhirray (u fudaynay) Buuraha, wayna la tasbiixsadaan Galab iyo Subaxba.'),
(3989, 38, 19, 'Shimbirahana waa loo kulmiyey, dhammaantoodna waxay u noqdaan (Adeecaan) Eebe.'),
(3990, 38, 20, 'Waana adkaynay xukunkiisa (Daa\'uud) waxaana siiney xigmad (Nabinimo) iyo fahmo iyo hadal cadday (iyo garsoorid).'),
(3991, 38, 21, 'Ma ku soo gaadhay warkii kuwii doodayey, markey Mixraabka ugu soo dheceen.'),
(3992, 38, 22, 'una soo galeen Daa\'uud markaas uu ka argagaxay, ayna ku dhaheen ha cabsan, waxaan nahay doodayaal qaarkanno qaarkakale dulmiyey, ee si ah noo kala xukun, hana joowrfalin, naguna hanuuni jidka toosan (xaqa).'),
(3993, 38, 23, '(Waxayna dheheen) kani waa walaalkay wuxuuna leeyahay sagaal iyo sagaashan Ido ah, anna waxaan leeyahay mid kaliya, wuxuuna sheegtay inaan siiyo hadalkiina wuu igaga adkaaday.'),
(3994, 38, 24, 'Wuxuuna yidhi «Daa\'uud» wuu kugu dulmiyey warsashada Laxdaada (inuu ku biiriyo) Idihiisa, in badan oo ka mid ah kuwa wax isku darsadana qaarkood waxay ku xadgudbaan (dulmiyaan) qaarka kale, marka laga reebo kuwa xaqa rumeeyey ee camalka wanaagsan falay, wayna yaryihiin kuwaasi, Daa\'uudna wuxuu umaleeyey inaan ibtileynay wuyxuuna ka dambidhaaf dalbay Eebihiis, rukuuc (sujuudbuuna) la dhacay wuuna noday.'),
(3995, 38, 25, 'Markaasaan u dambi dhaafnay, agtanada wuxuu ku leeyahay dhawaansho iyo noqosho fiican (darajo sare).'),
(3996, 38, 26, '(Eebe wuxuu yidhi): Daa\'uudow annagaa kaa yeeley khaliifka dhulka ee dadka kukala xikun xaqa hana raacin hawada (xun) oo kolkaas uu kaa dhumiyo jidka Eebe kuwa ka dhuma jidka Eebe waxaa u sugnaaday cadaabdaran halmaankoodii Maalinta xisaabta (Qiyaamada) darteed.'),
(3997, 38, 27, 'Maanaan u abuurin Samada iyo Dhulka iyo waxa u dhaxeeya dana laan (Ciyaar). taasina waa siday maleeyeen kuwii Gaaloobay halaaguna wuxuu ugu sugnaaday kuwii Gaaloobey Naar.'),
(3998, 38, 28, 'Miyaan ka yeeleynaa kuwii xaqa rumeeyey oo camal fiican falay sida kuwa hallayn dhulka, mise waxaan kala mid dhigaynaa kuwa dhowrsada kuwa xun oo kale (Gaalada).'),
(3999, 38, 29, '(Quraanku) waa kitaab aan kugu soo dajinay oo barakeysan, si ay u fiiriyaan dadku aayaadkiisa iyo inay waantoobaan kuwa caqliga leh,'),
(4000, 38, 30, 'Waxaan siinnay Nabi Daa\'uud (wiikiisii) Suleymaan ahaaa wuxuuna ahaa adeec (Eebe) oo fiican toobad keen badan.'),
(4001, 38, 31, 'Xusuuso marka loo bandhigay Casarkii Fardo oradbadan oo fiiefiican.'),
(4002, 38, 32, 'Oo markaas uu yidhi ma waxaan ka jeclaan xusidda Eebe xoolo, inta Qorraxdu ka dhacdo.'),
(4003, 38, 33, 'ii soo celiya wuxuuna bilaabay inuu ka taabto Dhudhumade iyo Luqunta (uu gawraco sadaqo darteed).'),
(4004, 38, 34, 'Dhabahaan yaan u fidmeynay (imtixaanay) Nabi Suleymaan, waxaana ku tuuray kursigiisii jasad markaasuu toobad keeney.'),
(4005, 38, 35, 'Wuxuuna yidhi Eebow ii dambi dhaaf ina sii xukun aan ku habooneyn cid igadambeeysa adaa bixin badane ahe.'),
(4006, 38, 36, 'Waxaana u sakhirrey daheysha iyadoo ku socota amarkiisa iyadoo jilicsan meeshuu doono.'),
(4007, 38, 37, 'Shayaadiinta (Waxaan usakhirey) mid wax dhisid iyo mid dhunbasho badanba.'),
(4008, 38, 38, 'Iyo kuwo kale oo ku xidhxidhan silsilado.,'),
(4009, 38, 39, '(Waxaan ku nidhi) tan waa siismada ee bixi ama hayso xisaab la\'aan.'),
(4010, 38, 40, 'Wuxuuna ku leeyahay agtanada dhawaansho iyo noqosho fiican.'),
(4011, 38, 41, 'Xusuuso addoonkeygii Ayuub ahaa markuu u dhawaaqay (baryey) Eebihiis isagoo dhihi wuxuu itaabsiiyey shaydaan dhib iyo Cadaab.'),
(4012, 38, 42, 'Waxaana ku nidhi ku garaac Lugtaada (dhulka) waxaana ku nidhi kani waa Biyo meydhi ah qabowna lana cabdo.'),
(4013, 38, 43, 'Waxaana siinnay Caruurtiisii iyo kuwo kale oo lamid ah naxariis xagganaga ka ahaatay iyo xusuus kuwa caqliga lch.'),
(4014, 38, 44, 'Kuna qabo gacantaada xidhmo laamo ah kuna garaac hana jabinin dhaarta waxaana ku ognahay inuu yahay samre addoon fiican ah toobadkeenna badan.'),
(4015, 38, 45, 'Xusuuso addoomadannadii (Nabi) Ibraahim iyo Nabi Isxaaq iyo Nabi Yacquub ee ku xoog badnaa cibaadada kuna aragdheeraa (xaqa).'),
(4016, 38, 46, 'Anagaa u dooranay xusidda aakhiro.'),
(4017, 38, 47, 'Waxayna ka yihiin agtanada kuwo la doortay oo wanaagsan.'),
(4018, 38, 48, '48, Xusuusana Nabi Ismaaciil, Alyasac iyo Dul kifli dhamaanna waxay ahaayeen kuwo wanaagsan.'),
(4019, 38, 49, 'Waxaas la soo sheegay waa xusuus, kuwa dhowrsadana waxaa u sugnaaday noqosho fiican.'),
(4020, 38, 50, 'Janada cadnimo loo furay albaabada yey mudan.'),
(4021, 38, 51, 'Waxayna ku dangiigsan dhexdeeda (sariiro) waxayna u yeedhaan faakhihad badan iyo Cabbid.'),
(4022, 38, 52, 'Waxaana agtooda ahaan Haween indhahoodu ku koobanyahay Ragooda (dhowrsoon) oo Isla eg.'),
(4023, 38, 53, 'Kaas waxa leydiin yaboohay Maalinta Xisaabta (qiyaamada).'),
(4024, 38, 54, 'Kaasuna waa Rizqigannaga mana dhamaado.'),
(4025, 38, 55, 'Xaalku waa sidaas, Gaalana waxaa u sugnaadey noqosho xun.'),
(4026, 38, 56, 'Jahannamo yeyna gali iyadaana u xun Gogol.'),
(4027, 38, 57, 'Kaas waa Cadaab, ee dhadhamiya, isagoo ah Biyo kulul iyo Dhaacaanka ehelu Naarka.'),
(4028, 38, 58, 'Iyo mid kale oo Cadaab ah oo mooeya ah.'),
(4029, 38, 59, 'Malaa\'igtuna waxay dhihi kani waa koox idinla gali Cadaabka waxayna ku dhihi ha soo dhawaanina, waxayna galaan Naarta.'),
(4030, 38, 60, 'Waxay ku dhaheen kuwii raacay kuwii madaxda ahaa ha soo dhawaanina (wanaag halla kulmina) idinkaa na soo galiyey Cadaabkan isagaana uxun meel lagu nagaado.'),
(4031, 38, 61, 'Waxayna dheheen kuwii wax raacay Eebow ruuxii nasoo galiyey Cadaabkan u siyaadi laban laab Naarta.'),
(4032, 38, 62, 'Waxayna dhaheen (Madaxdii) Gaalada maxaan la arkiweyney rag aan ku tirinay kuwo xun xun,'),
(4033, 38, 63, 'Ma waxaan ka yeelanay (adduunkuu) Jees jees mise waxaa ka iishey xaggooda Araggii.'),
(4034, 38, 64, 'Kaasina (doodaas) waa xaq sugan oo ah dooda ehelu naarka.'),
(4035, 38, 65, 'Waxaad dhahdaa Nabiyow waxaan uun ahay dige, Eebe aan Eebe ahayna ma jiro ee kaliga ah awoodana badnaa.'),
(4036, 38, 66, 'Ee ah Eebaha samooyinja iyo dhulka iyo waxa udhaxeeyaba, ahna adkaade dambidhaaf badan.'),
(4037, 38, 67, 'Waxaad dhahdaa Nabiyow Quraanku waa war weyn.'),
(4038, 38, 68, 'Ood idinku ka jeedsaneysaan.'),
(4039, 38, 69, 'mana ogayn shirka sare (malaa\'igtu) markey doodayaan (Haddaan waxyi jirin).'),
(4040, 38, 70, 'waxaa uun leywaxyoodey inaan ahaydige muuqda.'),
(4041, 38, 71, 'Xusuuso markuu Eebe ku yidhi malaa\'igta anigu waxaan ka abuuri dadka (Aadam) Dhoobo.'),
(4042, 38, 72, 'Ee markaan ejeeyo oon ku afuufo Ruuxdeyda ugu dhaca sujuud (salaan).'),
(4043, 38, 73, 'Wayna sujuudey malaa\'igtii dhamaantood oo idil.'),
(4044, 38, 74, 'Ibliss mooyee oo is kibriyey kana mid noqday gaalada.'),
(4045, 38, 75, 'Eebe wuxuu yidhi Ibliisow maxaa kaa reebay inaad u sujuuddo kaan ku abuuray yaddayda ma iskibrisey mise waxaad ka mid noqotay kuwa islasareeya.'),
(4046, 38, 76, 'Wuxuuna yidhi anaa ka kheyroon isaga (aadam), waxaad iga abuurtey naar isagana waxaad ka abuurtey dhoobo.'),
(4047, 38, 77, 'Eebe wuxuu ku yidhi iiga bax samadeyda, waxaad tahay mid la dheereeyeye.'),
(4048, 38, 78, 'Korkaagana ha ahaato lacanade tan iyo maalinta abaalmarinta.'),
(4049, 38, 79, 'Wuxuu yidhi (ibliis) Eebow i sugtan iyo maalinta la soo bixin dadka.'),
(4050, 38, 80, 'Eebe wuxuu yidhi waxaad ka midtahay kuwa la sugi.'),
(4051, 38, 81, 'Tan iyo maalinta waqtiga layqaan ah.'),
(4052, 38, 82, 'Wuxuuna yidhi waxaan ku dhaartey sharaftaadee waan baadiyeyn dhamaantood.'),
(4053, 38, 83, 'marka laga reebo addomadaada suuban.'),
(4054, 38, 84, 'Eebe wuxuu yidhi dhabtaan kusifoobaa, dhabtaana uhadlaa.'),
(4055, 38, 85, 'Waxaana ku dhaaran inaan kabuuxiyo Jahannamo adiga iyo inta kuraacda oo ka midah dadka dhamaan.'),
(4056, 38, 86, 'Waxaad dhahdaa Nabiyow idin kama warsanaayo xaqa ujuuro, mana ibi kuwa is dhiba (isku dhibawaxaan la firin).'),
(4057, 38, 87, 'Waxyiguna waxaan waanada caalamka ahayn ma aha.'),
(4058, 38, 88, 'Dhab ahaanna waxaad u ogaan doontaan warkiisa muddo ka dib.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'Soo dajinta Kitaabka (Quraanka) wuxuu ka ahaaday xagga Eebaha adkaada ee falka san.'),
(4060, 39, 2, 'Annagaana kuugu soo dajinay Nabiyow Kitaabka si xaq ah, ee caabud Eebe adoo kali yeeli Diinta.'),
(4061, 39, 3, 'Diinta khaaliska ahna waxay u sugnaatey Eebe, kuwa ka yeesha Eebe ka sokow awliyo waxay odhan uma caabudno inay Eebe noo dhaweeyaan mooyee dhawaansho, Eebaana kala xukumi dadka dhexdooda waxay isku diidaan, Eebana ma hanuuniyo Been badane gaalnimobadan.'),
(4062, 39, 4, 'Hadduu doono Eebe inuu yeesho ilmo wuxuu ka dooran lahaa wuxuu abuuro wuxuu doono, waa naxahanyahey, waana Eebaha kalida ah ee awoodda badan.'),
(4063, 39, 5, 'Isagaa abuuray samooyinka iyo dhulkaba si dhab ah, wuxuuna Habeenka ku daboolaa Maalinta, Maalintana ku daboolaa Habeenka, wuxuuuna sakhiray Qorraxda iyo Dayaxa, mid walbana wuxuu ku socdaa muddo magacaaban, Eebana waa adkaade dambi dhaafbadan.'),
(4064, 39, 6, 'Wuxuu idinka abuurey naf kaliya (Nabi aadam) markaasuu Eebe ka yeeley (ka abuurey) xagga aadam haweeneydiisi (Xaawo) wuxuuna Eebe idiin soo dajiyey xoolaha siddeed nooc oo ka mid ah, wuxuuna idinku abuurey uurka hooyadin abuurid ka dambeyso abuurid saddex mugdi dhexdooda saasna waxaa yeela Eebaha idin barbaariya, isagaana iska leh xukunka, Eebe mooyee ilaah kale majiro, ee xaggee laydiinka iili (Xaqa).'),
(4065, 39, 7, 'Haddaad gaalowdaan dadow Eebe waa idinka kaaftoomey, ugamana raalli aha addoomadiisa gaalnimo, hadaad ku mahdisaanna wuu idin ka raalli noqon, nafna naf kale danbigeeda ma qaaddo, waxaana leydiin celin Eebihiin xaggiisa, wuxuuna idiin ka warrami waxaad camal fasheen, wuuna ogyahey wax laabta (qalbiga) ku sugan.'),
(4066, 39, 8, 'Markuu dadka taabto dhib wuxuu baryaa Eebihiis isagoo u noqon, markuuse Nicmo siiyo wuu illoobaa wuxuu baryey horey, wuxuuna u yeelaa Eebe shariigyo si uu uga baadiyeeyo (kaasi) jidka Eebe, waxaad dhahdaa ku raaxaysogaalnimadaada wax yar waxaad ka mid tahay chelu naarke.'),
(4067, 39, 9, 'Ruux tukan oo Eebe adeece saacadaha habeenka isagoo sujuudsan oo taagan kana digtoon aakhiro quudarreynayana naxariista Eebihiis (Ma la midbaa cidaan saas faleynin). waxaad dhahdaa Nabiyow ma egyihiin kuwa wax yaqaanna iyo kuwaan wax aqoonin, waxaa uun waantoomi kuwa caqliga leh.'),
(4068, 39, 10, 'Waxaad dhahdaa Nabiyow addoomadeyda xaqa rumeeyow ka dhowrsada Eebihiin, ciddii wanaag fashana addooomadeyda waxaa usugnaadey wanaag, dhulka Eebena waa waasac (ee ku caabuda) waxaa uun loo oofiyaa (dhameyaa) kuwa samra ajrigooda xisaab la\'aan.'),
(4069, 39, 11, 'Waxaad dhahdaa Nabiyow waxaa lay faray inaan caabudo Eebe anoo u kali yeeli diinta.'),
(4070, 39, 12, 'Waxaana ley faray inaan noqdo kan ugu horreeya muslimiinta.'),
(4071, 39, 13, 'waxaad dhahdaa Nabiyoow anigu waxaan ka cabsan hadaan caasiyo Eebahay cadaab maalin weyn.'),
(4072, 39, 14, 'Waxaad dhahdaa Eebe uun baan caabudi anoo ukaliyeeli Diintayda.'),
(4073, 39, 15, 'Ee caabuda Gaaloy waxaad doontaan Eebe ka sokow, waxaadna dhahdaa kuwa khasaaray waa kuwa khasaariyey Naftooda iyo Ehelkoodii Maalinta Qiyaame, taasina waa khasaaraha cad.'),
(4074, 39, 16, 'Korkooda waxaa ah lakabyo Naar ah, hoostoodana waxaa ah lakabyo Naar ah, kaasina waa waxa Eebe ku cabsi galiyo (ugu digo) addoomadiisa ee addoomadeydoow iga dhawrsada.'),
(4075, 39, 17, 'Kuwa ka fogaaday inay caabudaan shaydaan (iyo xima fare) oo Eebe u noqday waxaa usugnaaday Bishaaro ee u bishaaree addoomadayda.'),
(4076, 39, 18, 'Ee ah kuwa dhageysta hadalka oo raaca kiisa fiican, kuwaasna waa kuwa Eebe hanuuniyey, kuwaasna iyaga uunbaa ah kuwa caqliga leh.'),
(4077, 39, 19, 'Ruuxay u waajibtay ereyga cadaab ma adaa Nabiyoow ka korin cid Naar gali.'),
(4078, 39, 20, 'kuwa ka dhawrsaday Eebahoodse waxay mudan Qawlado (Janno) oo qawlada kale ka korreeyaan sifiicanna (udhisan), waxaana dhexsocda Wabiyaalkii, waana yabooh Eebe, mana baajiyo Eebe yaboohissa.'),
(4079, 39, 21, 'Miyaadan arkeyn in Eebe ka soodajiyey samada biyo oo markaas uu galin dhulka iyagoo ka soo burqan dhulka, markaasna Eebe ku soo bixin Beer midabkeedu kala duwanyahay markaas ka dibna uu ingagi ood arki isagoo cawlan, kadibna kayeeli bur bur, arrintaasna waxay xusuus u tahay kuw caqliga leh.'),
(4080, 39, 22, 'Ruux Eebe uwaasiciyey Laabtiisa Islaamka oo nuurka Eebe ku sugan ma lamidbaa (mid la daboolay) halaag wuxuu u sugnaaday kuwey ka ingagtay quluubtoodu xuska Eebe (Quraanka) kuwaasina waa kuwa baadi cad kusugan.'),
(4081, 39, 23, 'Eebaa soo dejiyey kan ugu fiican hadal, kitaabuu soo dajiyey isu eg, oo soo noqnoqda, ay lana qamandhacyooto Jidhka kuwa ka yaaba Eebahood, markaasay la jilci jidhkoodu iyo quluubtoodu xusuusta Eebe, arrintaasuna waa hanuunka Eebe uu ku hanuuniyo cidduu doono, ruuxuu Eebe dhumiyana ma jiro cid hanuunin.'),
(4082, 39, 24, 'Ma ruux wajigiis kaga gabada cadaab xun maalinta qiyaame mala midbaa mid aan saas ahayn, waxaana loo yidhaa daalimiinta dhadhamiya waxaad kasban Jirteen, (abaalkiisii).'),
(4083, 39, 25, 'Waxaa beeniyey (xaqa) kuwii ka horreeyey (gaaladan) wuxuuna uga yimid cadaabku meelayn kasayn.'),
(4084, 39, 26, 'Markaasuu Eebe dhadhansiiyay dulli nolasha adduunyo, cadaabka aakhiraana weyn hadday wax garan.'),
(4085, 39, 27, 'Dhabbaan ugu caddayney dadka Quraanka tusaale kasta inay waantoobaan,'),
(4086, 39, 28, 'Waana Quraan carabi ku (qoran) oon qalloclahayn may dhawrsadaan.'),
(4087, 39, 29, 'Eebe wuxuu qadaray tusaale nin dad wadaagaan iskuna khilaafsan iyo mid usugnaada ruux si gaar ah ma egyihiin tusaale ahaan. mahad Eebaa iska lch dadka badidiisna ma oga.'),
(4088, 39, 30, 'Adigu waad dhiman iyaguna (Gaaladu) way dhiman.'),
(4089, 39, 31, 'Markaas maalinta qiyaame waxaad ku doodaysaan Eebihiin agtiisa.'),
(4090, 39, 32, 'Yaa ka dulmi badan Ruux ku been abuurtey Eebe korkiisa oo beeniyey runta (Xaqa) markuu uyimid, miyeyna ahayn Jahannamo Guriga Gaalada.'),
(4091, 39, 33, 'Kii la yimid Runta iyo kii rumeeyey waa dhawrsadeen/'),
(4092, 39, 34, 'Waxayna ku leeyihiin Eebe agtiisa waxay doonaan taasina waa abaalmarinta kuwa wanaagga fala.'),
(4093, 39, 35, 'Si Eebe ugu asturo wixii xumaa oy camal faleen, kuna abaal mariyo ajirkii ufu fiicnaa waxay falayeen.'),
(4094, 39, 36, 'Miyuusan ahayn Eebe kufillaha addoonkiisa waxay kugu cabsi galin waxa Eebe ka soo hadhay (ay caabudi) ruux Eebe dhumiyeyna ma laha cid hanuunin.'),
(4095, 39, 37, 'Cid Eebe hanuuniyeyna ma jiro wax dhumin, Eebana miyuusan ahayn adkaade aarsada.'),
(4096, 39, 38, 'Hadaad warsato Gaalada yaa abuuray Cirka iyo Dhulka waxay odhan Eebe, waxaad dhahdaa bal ka warrama waxaad caabudaysaan Eebe ka sokow hadduu ila doono Eebe dhib miyey yihiin kuwa fayda dhibka ama hadduu ila doono naxariis ma joojin karaan naxariistiisa, waxaad dhahdaa waxaa i kaafiya Eebe ee korkiisa ha tala saarteen kuwa tala saaran.'),
(4097, 39, 39, 'Waxaad dhahdaa qoomkayoow sidaadtihiin u camalfala anna waan camal faliye waadna ogaandoontaan.'),
(4098, 39, 40, 'Ruuxu yimaado cadaab dulleeya oo ku dago korkiisa cadaab joogta ah.'),
(4099, 39, 41, 'Annagaa kugu soodajinay Kitaabka (Quraanka) Dadka dartiis si dhab ah, ruuxii hanuunana wuxuu anfici naftiisa, kii dhumana wuxuu dhibi naftiisa, adiguna Nabiyow kama tihid wakiil.'),
(4100, 39, 42, 'Eebaa oofsada nafta markay dhiman iyo tii aan dhimanin ee hurudday, wuxuna hayn tuu geerida ku xukumay wuxuuna siideyn tii kale tan iyo muddo magacaaban, arrigtaasna aayaad (Calaamooyin) yaa ugu sugan ciddii fikireysa.'),
(4101, 39, 43, 'Mise waxay ka yeesheen Eebe wax ka soohadhay shafeecayaal, waxaad dhahdaa (oo ma kayeelanyesaan) xaataa haddayna wax hananayn waxna kasayn.'),
(4102, 39, 44, 'Waxaad dhahdaa Eebaa iska leh shafeeco oo dhan, waxaana u sugnaaday xukunka Cirka iyo Dhulka xaggiisaana leydiin celin.'),
(4103, 39, 45, 'Marka la xuso Eebe kaliya waxaa diida qalbiyada kuwaan rumeynin aakhiro markii la xuso waxa Eebe ka soo hadhayna markaasay bishaareystaan farxaan.'),
(4104, 39, 46, 'Waxaad dhahdaa Nabiyow Eebow adaa abuuray Cirka iyo dhulka ogna waxa maqan iyo waxa jooga, adaase kala bixin addoomadaada dhexdooda waxay isku Khilaafsanyihiin.'),
(4105, 39, 47, 'Hadday kuwii dulmiga falay waxa dhulka ku sugan shamaantii iyo wax la mid ah leeyihiin way iskaga furan lahaayeen Cadaabka Maalinta Qiyaamaha, waxaana uga muuqday xagga Eebe Waxayn maleeysanaynin.'),
(4106, 39, 48, 'Waxaana u muuqday xumaantay kasbadeen, waxaana ku dhacay waxay ku jees jeesi jireen.'),
(4107, 39, 49, 'Markuu dhibtaabto dadku wuu na baryayaa, markaanse siino Nicmo wuxuu dhahaa waxaa la i siiyey waan mutaa, saas ma aha ee waannu imtixaami mase oga badankood.'),
(4108, 39, 50, 'Waxaa saas yidhi kuwii ka horeeyey waxna uma tarin waxay kasbadeen.'),
(4109, 39, 51, 'Waxaana ku dhacay xumaantay kasbadeen, kuwii dulmifalay oo ka mid ahaa kuwaas waxaa ku dhici xumaantey kasbadeen, namana daaliyaan.'),
(4110, 39, 52, 'Miyeyna ogayn inuu Eebe u waasiciyo Rizqiga cidduu doono kuna cidhiidhyo (cidduu doono) arrintaasna waxaa ugu sugan aayad cidii wax rumeyn.'),
(4111, 39, 53, 'Waxaad dhahdaa Nabiyow addoomadayda ku xad gudbay Naftoodow ha ka quusanina naxariista Eebe, Eeba dhaafa danbiyada dhamaantoode, waana dambi dhaafbadane naxariis badane.'),
(4112, 39, 54, 'U noqda Eebihiin una hogaansama ka hor cadaab idiin ku yimaaddoon laydiinkana gargaarayn.'),
(4113, 39, 55, 'Raaca kan wanaagsan e laydiinka soo dajiyey Eebe xaggiisa, ka hor cadaab idinku yimaadda kado, idinkoon ogayn.'),
(4114, 39, 56, 'Ama odhan nafu shaleyte waxaan ku gaboodfalay cibaadada Eebe waxaana ahaa kuwa jees jeesa.'),
(4115, 39, 57, 'Ama ayan odhan haddii Eebe ihanuuniyo waxaan noqon lahaa kuwa dhawrsada.'),
(4116, 39, 58, 'Ama ayan odhan markay aragto cadaabka hadday noqosho iisugnaan laheyd waxaan ahaan lahaa kuwa samo fala.'),
(4117, 39, 59, 'Saas ma ah ee waxaa kuu yimid aayaadkaygii waadna beenisay waadna iswaynaysay waxaadna ka mid noqotay gaalada.'),
(4118, 39, 60, 'Maalinta qiyaamana waxaad arkeysaa kuwii ku been abuurtay Eebe korkiisa oo wajiyadoodu madowyahay, miyayna Jahannamo dhexdeedu ahayn hoyga kuwa is kibriya.'),
(4119, 39, 61, 'Wuuna koriyaa Eebe kuwa dhawrsaday liibaantooda darteed, mana taabto xumaanu mana murugoodaan.'),
(4120, 39, 62, 'Eebana waa abuuraha wax kaste, waana wax walba wakiilkiisa, «maamulaha».'),
(4121, 39, 63, 'Isagaana iska leh xukunka Samooyinka iyo Dhulkaba, kuwa gaaloobay aayaadka Eebana waa kuwa khasaaray.'),
(4122, 39, 64, 'Waxaad dhahdaa ma waxkasoohadhay Eebaad i faraysaan inaan caabudo jaahiliinyahay.'),
(4123, 39, 65, 'Waxa dhab ahaan loogu waxyoodayadiga iyo kuwii kaa horreeyeyba Nabiyow haddaad wax la wadaajisa (Eebe) waxaa buri camalkaaga, waxaadna ka mid noqon kuwa khasaaray.'),
(4124, 39, 66, 'ee Eebe uun caabud noqona kuwa ku mahdiya.'),
(4125, 39, 67, 'Mayna waynaynin gaaladii Eebe waynaantiisa dhabta ah, isagoo dhulka dhamaantiis kujiro xukunkiisa Maalinta qiyaame Samooyinkuna ku duuban yihiin Yamiintiisa Eebaa ka nasahan kana sarreeya waxay la wadaajiyeen.'),
(4126, 39, 68, 'Waxaa la afuufi suurka (Buunka qiyaame) waxaana suuxi (Dhiman) waxa samooyinka iyo dhulka ku sugan, cidduu Eebe doona mooyee, markaasaa la afuufi mid kale waxaana soo bixi dadkii iyagoo taagan oo sugi (xukunka Eebe).'),
(4127, 39, 69, 'Wuxuuna la ifi dhulku nuurka Eebihiis waxaana la soo dajin (dhawayn) Kitaabkii (Camalka) waxaana la keeni Nabiyadii iyo maraggii (Malaa\'igta), waxaana lagu kala xukumi dhexdooda xaq lamana dulmiyo.'),
(4128, 39, 70, 'Waxaana loo oofin (dhameyn) nafkastaa waxay camalfashay, Eebana waa ogyahay waxay camalfalayaan.'),
(4129, 39, 71, 'Waxaana loo kaxeyn gaaloobay xagga jahannamo iyagoo kooco ah markay yimaadaan Naartana waxaa la foraaa Albaabadeeda waxayna udhahaan adeegaheedu miyeyna idiin imaanin Rasuullo idinka mid ah oo idinku akhriya aayaadka Eebihiin idiinkana diga lakulanka Maalintiinan. waxayna dhihi way nooyimaadeen waxaase ku rumoobay Gaalada kalimadii Cadaabka (ee Eebe).'),
(4130, 39, 72, 'Waxaana lagu dhihi gala albaabada Jahannamo idinkoo ku waari dhexdeeda iyadaana u xun meelay ku hoydaan kuwa is kibriya.'),
(4131, 39, 73, 'Waxaana loo wadi kuwii ka dhawrsaday Eebahood xagga jannada iyagoo kooxa ah markay yimaadaan jannadana iyadoo la furay albaabadeeda (marhore) waxay ku dhihi adeegaheedu nabadgalyo korkiina ha ahaato, waad wanaagsanaateen (liibaanteen) ee gala janada idinkoo ku waari.'),
(4132, 39, 74, 'Waxayna dhihi mahad waxaa iska leh Eebaha noo rumeeyey yaboohiisa, nana dhaxalsiiyay dhulka (janada) waxaana ka dagnaa janada meeshaan doonno waxaana wanaag badan ajriga kuwii camal falay.'),
(4133, 39, 75, 'Waxaad arkaysaa malaa\'igtoo ku wareegsan gaararka carshiga kuna tasbiixsan mahadda Eebahood, waana la kala xukumi dadkii dhexdooda si xaqw ah, waxaana la dhihi mahad Eebaa iska leh ee ah Eebaha caalamka.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, '(waxay ku tusin mucjisada quraanka sidii soo hor martay).'),
(4135, 40, 2, 'Soo dajinta Kitaabka (quraanka) waxay ka ahaatay Eebaha adkaada ee wax walba og.'),
(4136, 40, 3, 'Ee dhaafa danbiga aqbalana toobadda, ayna darantahay ciqaabtiisu, ee nicmada badanleh, Eebe aan ilaahay ahayna ma jiro, xaggiisaana loo noqon.'),
(4137, 40, 4, 'Kuma doodo aayaadka Eebe kuwa gaaloobey mooyee, ee yeyna kugu dhagrin gagadoonkooda adduunka.'),
(4138, 40, 5, 'waxaa beeniyay hortood qoomkii (Nabi) Nuux iyo xisbiyadii ka dambeeyey, waxayna doontay ummad kastaa rasuulkoodii inay qabato (oy dilaan) waxayna ku doodeen baadil (xumaan) inay xaqa ku tirtiraan, markaasaan qabtay ee waa sidee ciqaabteydii.'),
(4139, 40, 6, 'Saasayna ugu sugnaatay kalimadii Eebe «ciqaabtiisu» kuwii gaaloobay, waana inay yihiin naarta ehelkeeda.'),
(4140, 40, 7, 'Kuwa (Malaa\'igta) xambaarsan carshiga iyo kuwa gaararkiisa ah waxay ku tasbiixsan kuna Mahadin Eebehood wayna rumeysanyihiin, waxayna u dambi dhaaf warsan kuwa rumeeyey iyagoo dhihi Eebahanow waxaad ugu waasac noqon wax kasta naxariis iyo ogaanshee u dambidhaaf kuwa toobadkeena ee raaca jidkaaga kana dhawr cadaabka jaxiimo.'),
(4141, 40, 8, 'Eebow gali kuwaas jannada cadni ee ah taad u yaboohday iyaga iyo ruuxii suubanaada oo ah Aabayaalkood, Haweenkooda iyo Caruurtooda, adigaa ah adkaade falsane.'),
(4142, 40, 9, 'Kana dhawr xumaanta (Ciqaabta) ruuxaad ka dhawrto xumaanta maalintaas waad u naxariisatay taasina waa liibaanta wayn.'),
(4143, 40, 10, 'Kuwa gaaloobay waxaa loogu dhawaaqaa (qiyaamada) cadhada Eebaa ka wayn cadhadaad naftiina ucadhootaan, maxaayeelay waxaa laydiinku yeedhi Jiray Iimaanka markaasaad gaaloobayseen.'),
(4144, 40, 11, 'Waxay dhihi Eebow waxaad na dishay laba jeer waxaadna na noolaysay laba jeer waana qiranay dambiganagii ee ma suuroowdaa jid naarta looga baxo.'),
(4145, 40, 12, 'Arrintaas waxaa ugu wacan in marka loo yeedho Eebe kaliya aad gaaloowdaan, haddii wax lala wadaajiyana aad rumeysaan xukunka dhamantiisna Eebaa iska leh ee sarreeya waynna.'),
(4146, 40, 13, 'Eebe waa kan idin tusiya aayaadkiisa idiinkana soodajiya Samada Rizqi (Roob) mana xusuusto ruux Eebe unoqon mooyee.'),
(4147, 40, 14, 'Ee bari (Caabud) Eebe adoo ukali yeeli cibaadada haba naceen gaaladuye.'),
(4148, 40, 15, 'Eebe waa kan ay sarrayso darajadiisu (waxna koryeela) carshigana iska leh, kuna soo dajiya waxyigiisa (iyo jibriil) oo ah amarkiisa cidduu doono oo adoomadiisa ah, si uu uga digo maalinta kulanka (Qiyaamada).'),
(4149, 40, 16, 'Maalintay usoo muuqan (usoo bixi) Eebe waxba karna qarsoona korkiisa, wuxuuna dhihi Eebe yaa xukunka iska leh maanta, markaasuu dhihi Eebaha kalida ah ee awooda badan.'),
(4150, 40, 17, 'Maanta (Qiyaamada) waxaa laga abaalmarin nafkastaa waxay kasbatay, dulmina majiro Maanta, Eebana wey dag dag badantahay xisaabtiisu.'),
(4151, 40, 18, 'Uga dig Nabiyow dadka Maalinta dhaw (Qiyaamada) markuu wadnuhu imaan dhuunta (Cabsi darteed) iyagoo naxsan daalimiinna uma sugnaan sokeeye iyo mid u shafeecoo la maqlo.'),
(4152, 40, 19, 'Eebe waa ogyahay khayaanada indhaha iyo waxay qarin laabtu.'),
(4153, 40, 20, 'Eebe wuxuu xukumaa xaqa, waxay caabudina oo Eeba ka sokeeya waxba ma xukumaan, Eebana waa maqle arka ah.'),
(4154, 40, 21, 'Miyayna ku soconin Dhulka ooyan fiirin siday noqotay cidhibtii kuwii ka horreeyey (Gaaladan) waxay ahaayeen kuwa ka xoogbadan kana camiraadbadan dhulka, markaasuu qabtay Eebe danbigooda dartiis umana sugnaan waxa Eebe ka soo hadhay wax daafaca.'),
(4155, 40, 22, 'Waxaana saas ugu wacan inay ahayd xujooyin, oy ka gaaloobeen markaas Eebe qabtay maxaa yeelay waa xoogbadane ay darantahay ciqaabtiisu.'),
(4156, 40, 23, 'Dhab ahaan yaan (Nabi) Muuse ugu dirray isagoo wata aayaadkanaga iyo xujo cad.'),
(4157, 40, 24, 'Xagga Fircoon, Haamaan iyo Qaaruun waxayna dheheen waa sixirroow beenaala ah.'),
(4158, 40, 25, 'Markuu ugala yimid xaqa agtanada waxay dheheen dila Wiilasha kuwa rumeeyey, daayana Haweenka (Gabdhaha) dhagarta gaaladuna ma aha waxaan khasaare ahayn.'),
(4159, 40, 26, 'Wuxuu yidhi Fircoon, i daaya aan silo Muusee hana uyeedho Eebihiisa, waxaan ka cabsan inuu idinka badalo Diinta ama uu ka muujiyo dhulka fasaad.'),
(4160, 40, 27, 'Wuxuuna yidhi (Nabi) Muuse waxaan ka magangalay Eebahay iyo Eebihiin mid kasta oo is kibriya (isla wayn) oon rumayn Maalinta xisaabta qiyaamada.'),
(4161, 40, 28, 'Wuxuu yidhi nin Mu\'min ah oo ka mid ah dadka Fircoon oo qarinaayay limaankiisa, ma waxaad udilaysaan in uu yidhi Eebahay waa Alle, isagoo idiinkala yimid xijooyin xagga Eebihiin hadduu beenaale yahayna isagay dhibi beentiisu, hadduu runle yahayna waxaa idin ku dhici wuxuu idiin ku yaboohay qaarkiis, Eebana ma hanuuniyo danbi badane been badan.'),
(4162, 40, 29, 'Qoomkayoow idinkaa iska leh xukunka maanta una adag dhulka, yaase nooga gargaari caddabka Eebe hadduu inoo yimaado, fircoon wuxuu yidhi idin kula talin maayo waxaan ahayn ra\'yigayga (waxa ii muuqda) idinkumana hanuuniyo waxaan ahayn waddada xaqa ah.'),
(4163, 40, 30, 'Wuxuu yidhi kii rumeeyey (Xaqa) qoomkayoow waxaan idiin ka cabsan sidii maalintii xisbiyadii.'),
(4164, 40, 31, 'Sidii Qoomkii Nabi Nuux iyo reecaad iyo thamuud iyo kuwii ka dambeeyey, Eebana ma aha mid la doonadulmi addoomada.'),
(4165, 40, 32, 'Qoomkayow wxaan idinka cabsan maalinta isu yeedhidda (Qiyaamada).'),
(4166, 40, 33, 'Maalintaad jeedsandoontaan idinkoo carari, idiinna sugnayn wax Eebe idinka ilaaliya, ruux Eebe dhumiyeyna malaha «ma jiro» wax hanuunin.'),
(4167, 40, 34, 'Waxaa dhab ahaan idiin kula mid xujooyin marhore (Nabi) Yuusuf, kamana aydaan tagin shaki wuxuu idiin la yimid, markuu dhintayna waxaad dhahdeen ma soobixinaayo Eebe gadaashiis Rasuul, saasuuna udhumiyaa Eebe Ruux xad gudba oo shaki badan.'),
(4168, 40, 35, 'Kuwa kudoodaya aayaadkanaga xujo utimid la\'aanteed waxaa ku waynaatay cadho Eebe agtiisa iyo kuwa rumeeyay, saasuuna u daabacaa Eebe qalbi kastoo is kibriya oo daalim ah.'),
(4169, 40, 36, 'Wuxuu yidhi fircoon Haamaanoow ii dhis Daar (sare) si aan ugu gaadho wadooyinka.'),
(4170, 40, 37, 'Wadooyinka Samooyinka markaas aandaalacdo Ilaaha Muuse anigu waxaan u malayn beenaalee, saasaana loogu qurxiyay fircoon camalkiisa xun loogana leexiyay jidka, dhagarta fircoonna, ma aha waxaan khasaare ahayn.'),
(4171, 40, 38, 'Wuxuu yidhi kii rumeeyay (Xaqa) qoomkayoow i raaca waxaan idinku hanuunin jidka fiicane.'),
(4172, 40, 39, 'Qoomkayoow nolashan adduunyo ee dhaw waa uun raaxo (yar) aakharaase guri nagaadi ah.'),
(4173, 40, 40, 'Ruuxii camal xun sameeya laguma abaalmariyo wax lamid ah mooyee, Ruuxiise camal fiican fala Lab iyo Dhaddiigba isagoo rumaysan kuwaasi waxay gali Jannada, waana lagu arzuqi dhexdeeda xisaab la\'aan.'),
(4174, 40, 41, 'Qoomkayoow maxaa ugu wacan inaan idiinku yeedho korid idinna iigu yeedhaan Naar.'),
(4175, 40, 42, 'liguna yeedhaan inaan ka gaaloobo Eebe lana wadaajiyo (Cibaadada) waxaanan oqoon ulahayn, aniguna waxaan idiin ku yeedhi xagga Eebaha adkaada ee dambidhaafa.'),
(4176, 40, 43, 'Waxaan shaki lahayn in waxaad iigu yeedhaysaan xaggiisa uusan lahayn Nacfi (iyo wax ajiiba) adduun iyo aakhiraba, waxaana laynoo celin xagga Eebe kuwa xadgudbana iyaga uunbaa ah asaxaabta (Ehelka) naarta.'),
(4177, 40, 44, 'Waadna xusuusan doontaan waxaan idin kuleeyahay, waxaana u bandhigay amarkayga xagga Eebe, illeen Eebaa arka addoomadee.'),
(4178, 40, 45, 'Markaasaa Eebe ka dhawray Mu\'minkaas xumaantay ku dhagreen, waxaana ku dhacay (ku dagay) Fircoon iyo ehelkiisii cadaab xun.'),
(4179, 40, 46, 'Naarbaana loo bandhigaa aroortii iyo galabtiiba, Maalintay kacayso Saacaduna (Qiyaamada waxaa ladhihi) galiya fircoon iyo ehelkiisa kan ugu daran cadaab.'),
(4180, 40, 47, 'Xusuuso waqtigay ku doodi ehelu Naarku Naarta dhexdeeda ooy ku odhan kuwii (raaciyada ahaa) kuwii (madaxda ahaa) ee isla weynaa idinkaan idin raacaynay ee wax manooga taraysaan qayb naarta ka mid ah.'),
(4181, 40, 48, 'waxay dheheen kuwii iskibriyey dhamaanteen waxaan ahaanaynaa naarta dhexdeeda, Eebana waa kala xukumay addoomadiisa dhexdooda.'),
(4182, 40, 49, 'Waxayna ku dhaheen kuwa naarta kusugan adeegaha jahannamo noo barya Eebihiin inuu naga fududeeyo maalin cadaabka.'),
(4183, 40, 50, 'Waxay dheheen adeegayaashii miyeyna ahayn inay idiinla timid Rasuuladii xujooyin, waxay dhaheen way noola timid, waxayna dhaheen harya idinkii marka Eebe, baryada gaaladana ma aha waxaan baadi iyo khasaare ahayn.'),
(4184, 40, 51, 'Annagaa u gargaari Rasuuladanada iyo kuwa rumeeyey xaqa nolosha adduun iyo maalinta ay kici maragyadu.'),
(4185, 40, 52, 'Maalintayna antacayn daalimiinta cudur daarkoodu, ayna u sugnaan Naclad iyo Guri xun (Naar).'),
(4186, 40, 53, 'Dhab ahaan yaan u siinay Nabi Muuse hanuun (Nabinimo) una dhaxalsiinayna Bani Israa\'iil Kitaabka (Tawreed).'),
(4187, 40, 54, 'Hunuun iyo waano isagoo u ah kuwa caqliga leh.'),
(4188, 40, 55, 'Ee samir Nabiyow yabooha Eebe wan dhabe, dambi dhaafna waydiiso kuna tasbiixso adoo ku dheehan mahadda Eebahaa Galab iyo Aroorba.'),
(4189, 40, 56, 'Kuwu ku murmi aayaadka Eebe xujo la\'aan u timid laabtooda ma aha waxaan kibir ayna gaadhayn ahayn, ee ka magan gala Eebe isagaa wax muqla waxna arkee.'),
(4190, 40, 57, 'Abuuriddu cirka iyo Dhulka yaa kawayn abuuridda Dadka (sida u muuqata) laakiin Dadka badidiis ma oga.'),
(4191, 40, 58, 'Ma eka indhoole iyo arkc iyo kuwa rumeeyey (Xaqa) camalfiicanna falay iyo xumuunloow, wax yar baadse xusuusanaysaan.'),
(4192, 40, 59, 'Saacadda (Qiyaame) way iman shaki la\'aan laakiin Dadka badidiis ma rumeynayaan.'),
(4193, 40, 60, 'Wuxuu yidhi Eebehiin i barya aan idiin ajiibee, (aqbalee) kuwa iska weyneeya cibaadadayda waxay gali doonaan naarta jahannamo, iyagoo dulleysan.'),
(4194, 40, 61, 'Eebe waa kan idiin yeelay habeenka inaad xasiLoonaataan dhexdiisa, maalintana (Ifid) arag, Eebana waa kan wax ku galladaysta dadka, laakiin dadka badankiisu kuma mahdiyaan.'),
(4195, 40, 62, 'Kaasi waa allihiin, Eebana idiin ah wax walbana abuuro, Eebe mooyee mid kale ma jiro ee xagee xaqa laydiinka iili.'),
(4196, 40, 63, 'Saas ookale yaa loo iilay kuwii aayaadka Eebe diidi jiray.'),
(4197, 40, 64, 'Eebe waa kan idiinka yeelay Dhulka meel sugnaansho, Cirkana dhismo oo idin sawiray wanaajiyayna Suuraddiina oo idinku Arzaaqay wax wanaagsan kaasi waa Eebihiin waxaana sarreeya oo nasahan Eebaha Caalamka Eebihiisa ah,'),
(4198, 40, 65, 'Eebaha nool, Eebe aan isaga ahaynna ma jiro ee barya (caabudana) idinkoo u kaliyeeli Diinta, mahadna waxaa iska leh Eebaha Caalamka.'),
(4199, 40, 66, 'Waxaad dhahdaa Nabiyow waxaa layga reebay inaan caabudo waxaad baryeysaan Eebe ka sokow markay iigayimaadeen xujooyin xagga Eebahay, waxaana lay faray inaan uhogaansamo Eebaha Caalamka.'),
(4200, 40, 67, 'Eebe waa kan idinka abuuray CarRada Hadana Dhibic, haddana Calaqo markaas idin soo bixiyey idinkoo ilma ah, hadana aad gaadhaan xoogiina, haddana aad noqotaan Odayo waxaase idinka mid ah cid la oofsado (Dhimata) intaas ka hor, iyo inaad gaadhaan waqti magacaaban waxaad udhawdihiin inaad wax kastaan.'),
(4201, 40, 68, 'Eebe waa kan wax nooleeya waxna dila markuu doono inuu xukumo amarna wuxuu uun dhahaa ahaw wuuna ahaadaa.'),
(4202, 40, 69, 'Ka warran kuwa ku murmi aayaadku Eebe xaggee looga iili xaqa.'),
(4203, 40, 70, 'Kuwa beeniyay Kitaabka «Quraanka» iyo waxaan la soo dirray Rasuulladanada way ogaandoonaan, cidhibtooda.'),
(4204, 40, 71, 'Marka Katiinado layeelo Luquntooda Silsiladdana lagu jiido.'),
(4205, 40, 72, 'Kulayl haddana «Naar» lagu jiido.'),
(4206, 40, 73, 'Markaas lagu dhaho aaway waxaad la wadaajiseen cibaadada.'),
(4207, 40, 74, 'Eebe ka sokoow, waxayna odhan waa naga Dhumeen (waynay), mase aanaan ahayn kuwo Wax ucaabuday horay, saasuuna Eebe udhumiyaa Gaalada.'),
(4208, 40, 75, 'Taasina waa waxaad ugu farxi jirteen Xumaanta dhulka dhexdiisa xaq daro iyo Waxaad la kibri jirteen.'),
(4209, 40, 76, 'Eegala irdaha jahannamo idinkoo ku Waari waxaana u xun meeluu ku hoydo isla wayne Naarta Jahanamo.'),
(4210, 40, 77, 'Samir (adkayso) nabiyow yabooha Eebe waa xaqe, haddaanu kutusinno waxa (guusha) aannu Kuu yaboohi qaarkeed (intaad nooshahay waa Wanaagayamaan ku oofsanno (intaas Ka hor) Xaganagaa loo soo celin (gaaladaas).'),
(4211, 40, 78, 'Waxan dirray rasuullo adiga hortaa Waxaa ka mid ah kuwo aan kaaga qisoonay iyo kuwo aanaan kaaga qisoonin, rasuulna aayad (mucjisad) ah idiin Lama imaan karo Eebe la\'aantiis hadduuse yimaado amarka Eebe (cadaabkiisu) waxaa la Xukumi xaq waxaana ku khasaaray halkaas kuwa Xumaanlowga ah.'),
(4212, 40, 79, 'Eebe waa kan idiin yeelay Nicmoolayda (xoolaha) inaad waxna ka fuushaan waxna ka cuntaan.'),
(4213, 40, 80, 'Waxaana idinka sugnaaday dhexdeeda Nacfi iyo inaad ku gaadhaan danihiina Laabta idinkaga jira, korkeeda iyo doonta Ayaana la idinku xambaaraa.'),
(4214, 40, 81, 'Wuxuuna idin tusiyaa aayaadkiisa ee teed aayaadka Eebe diidaysaan.'),
(4215, 40, 82, 'miyayna ku soconin dhulka oo ay fiiriyaan Siday noqotay cidhibtii kuwii ka horreeyay, waxay ahaayeen kuwo ka badan kana xoog badan iyo camiridda dhulka, waxna uma tarin waxay kasbadeen.'),
(4216, 40, 83, 'markay ula yimaadeen rasuuladoodii xujooyin waxay ku farxeen gaaladii waxa agtooda ah oo cilmi ah, waxaana ku dagay waxay ku jees-jeesi jireen (abaalkiisii).'),
(4217, 40, 84, 'markay arkaan cadaabkanaga waxay dhahaan waxaan rumaynay Eebe kaliya waana diidnay waxaan Eebe la wadaajin jirray.'),
(4218, 40, 85, 'Mana aha kuu anfici iimaankoodu markay Arkaan cadaabkanaga, waa sunnada (dariqada) Eebe ee u horaysay adoomadiisa, waxaana ku khasaaray halkaas gaalada.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'Waxay ku tusin mucjisada Quraanka sida soo hormartay.'),
(4220, 41, 2, 'Quraanka waa soo dajinta Eebe naxariista guud iyo mid gaaraba naxariista,'),
(4221, 41, 3, 'Waana Kitaab loo caddeeyey aayaadkiisa isagoo Quraan carabi ku qoran ah ciddii wax garan.'),
(4222, 41, 4, 'Waana bishaareeye u dige ah, waase ka jeedsadeen badankoodu waxna ma maqlayaan.'),
(4223, 41, 5, 'Waxayna dheheen quluubtanadu waxay kaga sugantahay dabool waxaad noogu yeedhi, dhagahanagana waxaa kaga jira culeys, dhexdanada iyo adigana waxaa ah xijaab, ee camal fal anana waancamalfaleynaaye.'),
(4224, 41, 6, 'Waxaad dhahdaa Nabiyow anigu waxaan uun ahay bashar (Dad) idinkookale ah, waxaana la iiwaxyooday in Eebehenno yahay Eebe kaliya, ee u toosnaada xaggiisa, kana dambidhaaf waydiista, halaagna wuxuu u sugnaaday mushrikiinta.'),
(4225, 41, 7, 'Kuwaan bixinin zakada ahna aakhiro kuwa ka gaaloobey.'),
(4226, 41, 8, 'Kuwa xaqa rumeeyeyse oo camalfiican falay waxaa usugnaaday ajri aan guraynin.'),
(4227, 41, 9, 'Waxaad dhahdaa Nabiyow idinku ma waxaad ka gaaloobaysaan kan (Eebaha) ku abuuray Dhulka laba maalmood, ood uyeelaysaan kuwo la mid ah, kaasi waa Eebaha Caalamka.'),
(4228, 41, 10, 'Wuxuuna yeelay Dhulka dhexdiisa Buuro wuuna barakeeyey dhexdiisa una qadaray (u qiyaasay) Cunnadiisa afar maalmood iyadooy usimantahay dadka weydiin.'),
(4229, 41, 11, 'Markaasuu Eebe u istawooday (qasday) Cirka oo qiiq ah kuna yidhi Cirka iyo Dhulka ahaada idinkoo adeecsan ama la ikraahi (laqasbi) waxayna dheheen waan nimid anagoo addeecsan.'),
(4230, 41, 12, 'Wuxuuna Eebe ka abuuray Cirka Todobo Samo laba maalmood wuxuuna faray Samo kasta amarkeedii, waxaana ku qurxinay Cirka dhaw Misbaaxyo, waana ilaalinay, taasina waa qadaridda (maamulka) Eebaha adkaada ee wax og.'),
(4231, 41, 13, 'Hadday ka jeedsadeen Gaaladu (xaqa) waxaad ku dhahdaa Nabiyow waxaan idiinka digi cadaab daran oo lamid ah kii Reer Caad iyo Thamuud.'),
(4232, 41, 14, 'Markay ugaga timid Rasuuladii hor iyo gadaalba, iyagoo ku leh ha caabudina waxaan Eebe ahyn, ayna ku dhaheen hadduu doono Eebeheen wuxuu soo dajinlahaa Malaa\'ig, anaguna waxa laydiin soo diray waan ka gaaloownay.'),
(4233, 41, 15, 'Ree Caad iyagu way isku kibriyeen Dhulka xaq darro, waxayna dheheen yaa naga xoog badan miyeyna arag Eebaha abuuray inuu ka xoogbadanyahay waxayna ahaayeen kuwo aayaadkanaga diidan.'),
(4234, 41, 16, 'Waxaana ku dirray korkooda Dabayl qaylo daran Todobo Maalmood oo uxun (iyaga) si aan u dhandhansiino cadaabka dulliga Nolosha adduun, cadaabka aakhiraana ka sii dulli badan loomana gargaaro.'),
(4235, 41, 17, 'Ree thamuud waxaantusinay xaqa, waxayna ka jeeclaadeen indho la\'aan (baadi) hanuunkii, waxaana qabtay qaylo cadaab wax dulleeya, waxay kasbanayeen darteed.'),
(4236, 41, 18, 'Waxaana korinay kuwii rumeeyey (xaqa) oo dhawrsan jiray.'),
(4237, 41, 19, '(u sheeg gaalada) Maalinta lagu kulmin colka Eebe naarta iyagoo is cidhiidhyi.'),
(4238, 41, 20, 'Markay yimaaddaana uu ku maragfuro korkooda, maqalkooda, aragoodu, iyo haragoodu (jidhkoodu) waxay falijireen.'),
(4239, 41, 21, 'ayna ku dhahaan korkooda (Jidhkooda) maxaad noogu marag kacdeen, ayna ku dhahaan waxaa naga hadliyey Eebaha ka hadliya wax kasta, isagaana idin abuuray markii hore xagiisana laydiin celin.'),
(4240, 41, 22, 'Mana aydaan ahayn kuwa iska astura inay ku maragkacaan jidhkiinu, araggiinu iyo haraggiinu, inuusan Eebe ogayn wax badan ood falaysaan.'),
(4241, 41, 23, 'kaasina waa malihiinii aad u malayseen Eebihiin isagaana idin halaagay, waxaadna ahateen kuwo khasaaray.'),
(4242, 41, 24, 'haday samraanna naarbay ku hoyan hadday cudur daar dalbaana lagama aqbalo.'),
(4243, 41, 25, 'Waxaan u darabnay Gaalada saaxiibo u qurxiya waxa ka horeeya, iyo waxa ka dambeeya, waxaana ku xukunaatay hadalkii Eebe isagoo umada kale ku dhex sugan (oo la cadaabi) oo ka horeeyey Jinni iyo Insina leh waxayna ahaayeen kuwo khasaaray.'),
(4244, 41, 26, 'Waxay dhaheen kuwii Gaaloobay ha maqlina Quraanka kuna qaylya (marka la akhrin) waxaad u dhawdihiin inaad adkaataane.'),
(4245, 41, 27, 'Waxaan dhadhansiinaynaa kuwii Gaaloobay Cadaab daran, waxaana ka abaalmarinaynaa kii ugu xumaa waxay camal falayeen.'),
(4246, 41, 28, 'Kaasi waa abaalmarinta colka Eebe, waxayna gali Naar iyadoo u noqon Curi waarid, iyagoo laga abaal marin aayaadkanaga aydiideen.'),
(4247, 41, 29, 'Waxay dheheen kuwii Gaaloobay Eebow na tusi labadaas nadhumiyey oo Jinni iyo Insi ah aan yeelo Gomadahanaga hoostoodee si ay u noqdaan kuwo ku hooseeya (Naarta).'),
(4248, 41, 30, 'kuwa yidhi Eebahanno waa Alle hadana toosnaaday waxaa ku soo dagta Malaa\'igta (markey dhiman iyadoo ku dhihi) ha cabsanina hana murugoonina, kuna bishaareysta Jannadii laydiin yaboohay.'),
(4249, 41, 31, 'anagaa sokeeye idiin ah nolosha addunyo iyo aakhiraba, waxaana idiinsugnaaday dhexdeeda waxay doonto Naftiinnu waxaadna ka helaysaan waxaad dalabtaan.'),
(4250, 41, 32, 'Waana martiqaadka Eebaha dambi dhaafa ee Naxariista.'),
(4251, 41, 33, 'yaa ka fiican hadal ruux ugu yeedhay (Dadka) Eebe xagiisa, oo camalfiican sameeyey, oo dhaha anigu waxaan ka mid ahay Muslimiinta.'),
(4252, 41, 34, 'Mana eka Wanaag iyo Xumaan, reebna oo (bixi) arrinta fiican markaas waxaa soobixi in midkii col idin dhexyiil uu noqon sokeeye iyo saaxiib.'),
(4253, 41, 35, 'Lalamana kulmiyo arrintaas kuwii samrey mooyee, lalamana kulmiyo mid nasiib weyn leh mooyee.'),
(4254, 41, 36, 'Hadduu ku waswaasiyo shaydaan xaggiisa waswaasiye ka magangal Eebe isagaa wax maqla waxna oge.'),
(4255, 41, 37, 'Aayaadka Eebe (jiridiisa iyo qudradiisa) waxaa ka mid ah Habeenka, Maalinta, Qorraxda iyo Dayaxa, ha u sujuudina Qorraxda iyo Dayaxa ee u sujuuda Eebaha abuuray haddaad tihiin kuwo (dhab) u caabudi.'),
(4256, 41, 38, 'Haday iska kibriyaan (cibaadada) kuwa Eebahaa agtiisa ah way u tasbiixsadaan Habeen iyo Dharaarba kana noogin.'),
(4257, 41, 39, 'Aayaadka Eebe waxaa ka mid ah inaad aragtidDhulkoo dulleysan (abaar ah) markaan ku soo dajinno Biyaha uu gilgisho soona kor noqdo, midka nooleeyey wuxuu nooleyn wixii dhintay wax walbana wuu karaa.'),
(4258, 41, 40, 'Kuwa iilaya aayaadkanaga nagama qarsoona, ruux lagu tuuray Naarta miyaa khayrroon mise mid isagoo aamin ah imaan Qiyaamada, fala waxaad doontaan, Eebe waxaad faleysaan waa arkaa.'),
(4259, 41, 41, 'Kuwa ka gaaloobay Quraanka markuu u yimid (waa la abaal marin) waana Kitaab qaali ah.'),
(4260, 41, 42, 'Mana timaado xumaan hortiisa iyo gadaashiisa, (waa dhawran yahay) waana soo dajinta Eebaha falka san ee lamahdiyo.'),
(4261, 41, 43, 'Lagumana dhaho waxaan ahayn waxii lagu yidhi Rasuuladii ka horreeyey, Eebahaana waadambi dhaafe ciqaab daran.'),
(4262, 41, 44, 'Hadaan kayeelo «Kitaabka» Quraanka Cajami ah waxay dhihi lahaayeen maxaa loo caddayn waayey Aayaadkiisa, ma (quraan) Cajami ah iyo Nabi (Carabi ah) waxaad dhahdaa isagu kuwa rumeeyey wuxuu u yahay Haanuun iyo Caafimaad, kuwa aan rumeynina dhagahooda waxaa ah culays, waana ka indha la\' yihiin kuwaasna waxaa looga yeedhi meel fog.'),
(4263, 41, 45, 'Dhab ahaanbaan u siinay Nabi Muuse Kitaabka waana laysku khilaafay, haddayan jirin kalimo ka hormartay Eebahaa waa lakala xukminlahaa dhexdooda waxayna ku suganyihiin gaaladu shaki daran.'),
(4264, 41, 46, 'Ruuxi camalfiican fala wuxuu u falay Naftiisa, Ruuxii xumaan fala wuxuu dhibay Naftiisa, Eebaheena ma aha dulmiyaha Addoomada.'),
(4265, 41, 47, 'Xagiisaana loo celiyaa ogida Saacadda (Qiyaame) midhana kama soobaxaan Sabuulkooda, mana uuroobo Dhadig mana dhalo ogaanshaha Eebe la\'aantiiis, Maalintuu ku dhihi away kuwaad ila wadaajiseenna waxay dhihi waxaan ku ogaysiinnay Nagamidma aha Ruux marag furi arintaas.'),
(4266, 41, 48, 'Waxaa ka dhumey waxay ahaayeen kuwo (baryo) caabuda horey, waxayna yaqiinsadeen inuusan u sugnaan gargaare.'),
(4267, 41, 49, 'Dadku kama daalo baryada khayrka, hadduu taabto sharra wuu quusasha badanyahay.'),
(4268, 41, 50, 'Haddaan dhadhansiinno naxariis xagganaga ka timid dhib taabtay kadib, wuxuu dhahaa kan anaa iska leh mana u maleeynaayo inay Saacadda (qiyaame) kici, haddii layceliyo Eebahayna waxaan ku leeyahay agtiisa wanaag, dhab ahaan baan ugu warranmeynaa kuwii Gaaloobey waxay camal faleen, waxaana dhabahaan u dhadhansiinaynaa Cadaab daran.'),
(4269, 41, 51, 'Markaan unicmeyno Dadka wuu jeedsadaa oo is dadbaa (daacadduu ka tagaa) markuu taabto dhibna wuxuu soo baxaa isagoo baryo badan.'),
(4270, 41, 52, 'Waxaad ku dhahdaa bal kawarrama hadduu Quraankani xaga Eebe ka yimid markaas aad ka Gaaloowdaan yaa ka baadi badan Ruux Gaalnimo iyo khilaaf fog ku sugan.'),
(4271, 41, 53, 'Waxaanu ku tusin aayaadkanaga jahooyinka iyo Naftooda intey uga caddaato inuu Quraanku xaq yahay, miyuuna ku filleyn inuu waxkaste ogyahay.'),
(4272, 41, 54, 'Waxayna Gaaladu kaga suganyihiin shaki lakulanka Eebe, Eebana waxkasta wuu koobay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'Waxay ku tusin mucjisada Quraanka.'),
(4274, 42, 2, 'Sidoo kale.'),
(4275, 42, 3, 'Sida waxyiga laguugu soodajiyey Nabiyoow yaa Nabiyadii horana loogu waxyooday, waxaana waxyooda Eebaha adkaada ee falka san.'),
(4276, 42, 4, 'Waxaa u sugnaaday waxa Samada iyo Dhulka ku sugan, Eebana waa sarreeyaha weyn.'),
(4277, 42, 5, 'Waxay u dhawdahay Samooyinku inay ku dildillaacaan korkooda, Malaa\'igtuna waxau ku tasbiixsan mahadda Eebahood, waxayna u dambidhaaf warsan kuwadhulka ku sugan (Dadka) Eebana waa dambidhaafe Naxariista.'),
(4278, 42, 6, 'Kuwa yeeshay Eebe ka sokoow owliyo Eebe waa ogyahay korkooda (wuu daalacay) adiguna kama tihid wakiil.'),
(4279, 42, 7, '(Sidaan ugu waxyoonay Nabiyadii hore) yaan adigana Nabiyow kuugu waxyoonay Quraan Carabi ah saad ugu digtid Ree Makaad iyo wax ka hareereeya (dadka kale) ugana digtid maalinta kulanka (Qiyaamada) shaki male Maalintaas, kooxi Jannay gali kooxna Jaxiimo.'),
(4280, 42, 8, 'Hadduu doono Eebe wuxuu ka yeeli lahaa dadka umad kaliya, laakiin wuxuu galiyaa cidduu doono naxariistiisa, daalimiintuna ma helaan sokeeye iyo Gargaare midna.'),
(4281, 42, 9, 'Mise waxay yeesheen Gaaladii Eebe ka sokow awliyo (ay caabudaan) Eebe umbaa wali ah, isagaana nooleeya wixii dhintay, isagaana wax walba kara.'),
(4282, 42, 10, 'Waxaad isku khilaaftaan oo shay ah Eebaa leh xukunkiisa (loo celin) kaasina waa Eebaha i barbaariya, isagaana tala saartaa, xaggiisaana u noqon.'),
(4283, 42, 11, 'Isagaa abuuray Samooyinka iyo dhulka idiinkana yeelay naftiinna noocyo (Lab iyo Dhaddig) xoolahana idiinka yeelay Noocyo, wuuna idin abuuraa wax la mid ahna ma jiro, waana maqle arka.'),
(4284, 42, 12, 'Isagaa iska leh xukunka Samooyinka iyo Dhulka, wuuna u fidiyaa Rizqiga ruuxuu doono, wuxuuna ku cidhiidhyaa Cidduu doono, wax walbana waa ogyahay.'),
(4285, 42, 13, 'Wuxuuna idiin jideeyey xagga Diinta wuxuu u dardaarmay (Nabi) Nuux iyo kaan kuu Waxyoonay adiga iyo waxaan u dardaaranay (Nabi) Ibrahiim (Nabi) Muuse iyo (Nabi) Ciise inay oogaan Diinta aydaan kuna kala tagin dhexdiisa, waxaa ku waynaaday Gaalada waxaad ugu yeedhi, Eebaa u doorta xaggiisa Cidduu doono una hanuuniya xaggiisa cidduu hanuunin.'),
(4286, 42, 14, 'Mana kala tagin markuu u yimid cilmigu ka dib mooyee, dulmi iyo xasad dartiis, hadayana jirin kalimo Eebahaa ka hormartay oo tan iyo muddo magacaaban (in lasugo) waxaa lakala xukmin lahaa dhexdooda, kuwii la dhaxalsiiyey Kitaabka kadib waxay ku suganyihiin shaki daran.'),
(4287, 42, 15, 'Waxaas dartiis ugu yeedh Dadka, utoosnawna sidii lagu faray, hana raacin hawadooda, waxaadna dhahdaa waxaan rumeeyey waxa Eebe soo dajiyey oo Kitaab ah, waxaana lay faray inaan u caddaalo falo dhexdiina, Eebana waa Rabbiganno iyo Rabbigiin, waxaa noo sugnaaday waxaan camal fallo, idinkana waxaa idiinsugnaaday waxaad camal fashaan, doodina inama dhextaallo Eebaa kulmin dhexdeena xaggiisaana loo ahaan.'),
(4288, 42, 16, 'Kuwa ku doodaya Eebe intaa la ajiibay kadib xujadoodu waa baadil Eebe agtiisa, korkoodana cadhaa ah, waxaana usugnaaday Cadaab daran,'),
(4289, 42, 17, 'Eebe waa kan u dajiyey Kitaabka si xaq ah iyo miisaan. (caddaalada) mana ogid waxay mudantahay in sacaadda (Qiyaame) dhawdahay.'),
(4290, 42, 18, 'Waxaa dadajista saacadda kuwaan rumeynin iyafa, kuwa rumeeyayna way ka cabsadaan xaggeeda waxayna ogyihiin inay xaq tahay kuwa ku murmi saacaddana waxay ku suganyihiin baadi cad.'),
(4291, 42, 19, 'Eebe waa u naxariisbadanaha addoomadiisa wuxuuna arzuqaa cidduu doono, waana xoogbadane adkaade ah.'),
(4292, 42, 20, 'Ruuxii dooni camalka aakhiro waan u siyadinaa camalkiisa, Ruuxiise ah mid dooni camal adduunyo waan ka siinaa xaggeeda, kumana leh aakhiro wax nasiib ah.'),
(4293, 42, 21, 'Mise waxay leeyihiin Gaaladu shurako Eebe oo u Jideeyey diin iyo (xukun) aan Eebe idmin haddayna jirin kalimadda kala bixinta (qiyaamada) waa la kala xukumi lahaa dhexdooda, daalimiintana waxaa u sugnaaday cadaab daran.'),
(4294, 42, 22, 'Waxaad arkeysaa daalimiinta oo ka cabsan waxay kasbadeen, wuuna ku dhici korkooda, kuwa rumeeyey xaqse oo camalfiican falay waxay gali Beero Janno, waxayna ku leeyihiin waxay doonaan Eebe agtiisa, taasina waa fadli weyn.'),
(4295, 42, 23, 'kaasi waa midka ugu bishaareyn Eebe addoomihiisa ah kuwa rumeeyey (xaqa) camal fiicanna falay, waxaadna dhahdaa Nabiyooy maydin warsanaayo ujuuro xaqa gaadhsiintiisa oon ahayn jacayl qaraabanimo, ruuxii kasbada wanaagna waxaannu u siyaadin wanaag, Eebana waa dambidhaafe mahdiyo.'),
(4296, 42, 24, 'mise waxay dhihi wuxuu ku been abuurtay Eebe, hadduu doono Eebe wuxuu dabooli lahaa qalbigaaga, wuxuuna tiraa Eebe xumaanta xaqana wuxuu ku sugaa kalimadiisa (quraanka) Eebana waa ogyahay waxa laabta kusugan.'),
(4297, 42, 25, 'Eebana waa kan aqbala loobada addoomadiisa, kana dhaafa xumaanta ogna Waxaad falaysaan.'),
(4298, 42, 26, 'Ee (Eebana) wuxuu ajiibaa kuwa rumeeyey xaqa oo camal fiican falay wuxuuna u siyaadiyaa fadligiisa, gaalana waxaa usugnaaday cadaab daran.'),
(4299, 42, 27, 'Hadduu Eebe ufidiyo (waasiciyo) rizqiga addoomadiisa waxay ku kibri lahaayeen dhulka wuxuuse u soodajin qadarkuu doono, Eebana addoomadiisa wuu ogyahay uu arkaa.'),
(4300, 42, 28, 'Eebana waa kan soo dajiya Roobka intay quustaan kadib fidiyana naxariistiisa, waana Eebaha waliga ah (gargare) ee la mahdiyo.'),
(4301, 42, 29, 'Calaamooyinkiisa waxaa ka mid ah Abuuridda Samooyinka iyo Dhulka iyo wuxuu ku fidiyay (ku abuuray) dhexdooda oo socda, Eebana inuu soo kulmiyo dhammaantood markuu doono wuu karaa.'),
(4302, 42, 30, 'Wixii idinku dhaca oo musiiba ah (dhib ah) waa waxay kasbadeen Gacmihiinu, wuxuuna idinka cafiyaa (Eebe) wax badan.'),
(4303, 42, 31, 'Mana tihidin kuwo Eebe ku daaliya dhulka dhexdiisa, idiimana sugnaanin isaga ka sokoow Wali iyo Gargaare midna.'),
(4304, 42, 32, 'Calaamooyinka Eebe waxaa ka mid ah Maraakiibta (socota) Badda iyadood moodo buuro.'),
(4305, 42, 33, 'Hadduu doono (Eebe) wuxuu xasiliyaa (joojiyaa) Dabaysha markaasay fadhiistaan Biyaha korkooda, taasina waxaa ugu sugan calaamo ruux kastoo samir iyo mahad badan.'),
(4306, 42, 34, 'Hadduu doonana (Eebe) wuu halaaga Doomaha waxay kasbadeen darteed wax badanna wuu ka cafiyaa.'),
(4307, 42, 35, 'Waxayna ogaan kuwa ku murmi aayaadkanaga inuusan u sugnaanin meeley magan galaan.'),
(4308, 42, 36, 'Wixii laydiin siiyo oo wax ah waa uun nolasha dhaw raaxadeeda waxa Eebe agtiisa ah yaase u khayrbadan una hadhid badan kuwa rumeeyey (xaqa) Eebehoodna tala saarta.'),
(4309, 42, 37, 'kuwaasi waa kuwa ka fogaada dambiga waaweyn iyo xumaanta, markey cadhoodaanna u Cafiya (u dhaafa).'),
(4310, 42, 38, 'Waana kuwa ajiibay (maqlay) Eebahood oogayna Salaadda amarkooduna tashi yahay dhexdooda waxaan ku arzaaqnayna wax ka bixiya.'),
(4311, 42, 39, 'Waana kuwa marka gardarro (iyo dulmi) ku dhaco gargaarta (iska celiya).'),
(4312, 42, 40, 'Xumaan abaalkeeduna waa ku abaal marin mid lamid ah Ruuxiise iska cafiya oo wanaajiya ajrigiisa Eebaa siin, Eebana ma jecla daalimiinta.'),
(4313, 42, 41, 'Ruuxiigargaarta inta la dulmiyey ka dib kuwaas korkooda wax masaarro (jid lagumaleh).'),
(4314, 42, 42, 'Waxaase dhib saaranyahay uun (jid laguleeyahay) kuwa dulmiya Dadka kuna kibra Dhulka xaq daro, kuwaas waxaa u sugnaaday cadaabdaran.'),
(4315, 42, 43, 'Ruuxii samra oo iska cafiya taasi waa arrin loo qasdo (la jecelyahay).'),
(4316, 42, 44, 'Ruxii Eebe dhumiyo ma helo sokeeye gadaashiis, waxaadna arkeysaa daalimiintu markey arkaan cadaabka iyagoo leh ma suroobi karaa waddo (hab) loogu noqdo adduunkii.'),
(4317, 42, 45, 'Waxadna arkeysaa iyagoo loo bandhigi Naarta, oo la khushuucsan dulli, kana dayi wax si Qarsoon, waxayna dhaheen kuwii rumeeyey (xaqa) kuwa khasaaray waa kuwa khasaariyey naftooda iyo ehelkooda Maalinta Qiyaame, daalimiintuna waxay gali cadaab joogta ah.'),
(4318, 42, 46, 'Umana sugnaan awliyo (sokeeye) ugargaara Eeve ka sokoow ruux Eebe qaddaray dhumidiisana jid uma sugnaanin (uu ku tooso).'),
(4319, 42, 47, 'Ajiiba (maqla) Eebihiin ka hor imaatin maalin aan laga celinkarin Eebe, idiinmama sugnaanayo wax la magangalo, Maalintaas, idiinmana sugna gargaare.'),
(4320, 42, 48, 'Haday jeedsadaane kuuma aanaan dirin inaad ilaaliso korkooda, korkaaga ma aha waxaan gaadhsiin ahayn, markaan dhadhansiino Dadkana naxariis way farxaan «si kibir ah», hadday ku dhacdo xumaan waxay Gacmahoodu hor marsadeen darteed dadkii aadbuu u Gaaloobaa.'),
(4321, 42, 49, 'Eebaa iska leh xukunka Samooyinka iyo Dhulka, wuxuuna abuuraa wuxuu doono, wuxuu siiyaa cidduu doono gabdho, cidna wuxuu siiyaa Wiilal.'),
(4322, 42, 50, 'Ama wuu isku lamaaneeyaa Wiilal iyo Gabdho, ruuxuu doonana wuxuu ka yeelaa Mandhaleys Eebana wax og, karana.'),
(4323, 42, 51, 'Uma suurawdo Dad inuu Eebe la hadlo waxyi mooyee, ama kala hadlo xijaab gadaashiis, ama uu u soo diro farriin (Malag), oo markaas ugu waxyooda idamka Eebe wuxuu doono, Eebana waa sarreeye falsan.'),
(4324, 42, 52, 'Saasaannu kuugu waxyoonay nolol amarkanaga ah (Quraanka) maadan aqoonin Kitaab wuxuu yahay iyo Iimaan midna, laakiin waxaan ka yeellay Nuur aannu ku hanuunino ciddaan doono oo addoomadanada ka mid ah, adiguna Nabiyow waxaad ku hanuunin jidka toosan.'),
(4325, 42, 53, 'Jidka Eebaha iska leh xukunka waxa Samooyinka iyo Dhulka ku sugan xagga Eebeyna u ahaan umuuruhu.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'Mucjisada Quraankay ku tusin.'),
(4327, 43, 2, 'Waxaan ku dhaartay Kitaabka (Quraanka) cade (muuqda) ee.'),
(4328, 43, 3, 'annagaa ka yeelley Quraan Carabi ku soo dagay, inaad kastaan darteed.'),
(4329, 43, 4, 'Wuxuuna ku suganyahay Kitaabka asalkiisa (Looxul maxfuudka) agtiisa, waana sarreeyaa Quraanku falna sanyahay.'),
(4330, 43, 5, 'Ma waxaan Quraanka idiinka haysanaynaa (daysanayna) inaad tihiin qoom ku xadgudbay xumaanta.'),
(4331, 43, 6, 'Badanaa intaan udiray Nabi kuwii hore.'),
(4332, 43, 7, 'Nabi uma yimaado Gaaladii hadii kale way ku jees jeesi jireen.'),
(4333, 43, 8, 'Waxaana halaagnay kuwo ka xoogbadan qabasho waxaana tagay (hor maray) tusaalihii kuwii horreeyey (ee halaaga ahaa).'),
(4334, 43, 9, 'Haddaad warsato Gaalada yaa abuuray Samooyinka iyo dhulka waxay odhan waxaa abuuray Eebaha adkaada ee wax walba og.'),
(4335, 43, 10, 'Waana Eebaha idiin kayeeley dhulka gogol idiinkana yeeleydhexdiisa Wadooyin si aad ugu hanuuntaan (toostaan).'),
(4336, 43, 11, 'Waana Eebaha ka soo dajiyey Samada Biyo qadaran oon ku noolayno Magaalo dhimatay, saasaana leydiin soobixin.'),
(4337, 43, 12, 'Waana Eebaha abuuray noocyada dhamaantood idiinkana yeeley Maraakiibta iyo Xoolaha waxaad kortaan.'),
(4338, 43, 13, 'Inaad ku ekaataan dhabarkeeda markaas aad xusuusataan Nicmada Eebihiin markaad ku ekaataan ood dhahdaan waxaa nasahan Eebaha noosahley tan mana nihin kuwo kara leyligeeda.'),
(4339, 43, 14, 'Anaguna xagga Eebahanno yaannu u gadoomeynaa.'),
(4340, 43, 15, 'Waxay uga yeeleen Eebe addoomadiisa qayb, Dadkuna waa gaaloow badane cad.'),
(4341, 43, 16, 'Miyuu ka yeeshay Eebe wuxuu abuuray qarkood Gabdho, oo idiin doorey Wiilal.'),
(4342, 43, 17, 'Marka loogu bishaareeyo midkood wuxuu Eebaha Raxmaana uga yeeley tusaale, wuxuu noqdaa wajigiisu mid madow oo walbahaarsan.'),
(4343, 43, 18, 'Ma Eebey u yeeleen wax ku barbaara isqurxin, oon doodna caddayn karin.'),
(4344, 43, 19, 'Waxay ka yeeleen Malaa\'igta ah addoomada Eebaha Raxmaan ah dhadig, miyey joogeen abuurkoodii, waan qoreynaa maragooda waana la warsan (waxaas).'),
(4345, 43, 20, 'Waxay dhaheen hadduu doono Eebaha Raxmaan ah maanaan caabudneen waxkale ugumana sugnaanin wax cilmi ah arrintaas, waxaan beensheegayaal ahayna ma aha.'),
(4346, 43, 21, 'Mise waxaan siinay Kitaab ka horreyey (quraanka) oy haystaan isagaas.'),
(4347, 43, 22, 'Waxaybase dhaheen waxaan bellay Abbayowganoo jid ku socda anaguna iyaga raadkoodii yaan ku hanuunsanahay.'),
(4348, 43, 23, 'Waa saasoo kale umaanaandirin Magaalo hortaa u dige haddii kale waxa dhihi Jireen kuwa raaxaystay anagu waxaan hellay Aabayoowganoo jid ku sugan anaguna raadkooda yaan ku dayanaynaa.'),
(4349, 43, 24, 'Waxaad dhahdaa (ma waxaad raaxaysaan waxaas) haddaan idiinla imaado wax ka hanuunsan waxaad ka hesheen Aabayaalkiin (oo ku sugan) waxay dhihi annagu waxa laydiin ku soo diray waannu ka Gaalownay.'),
(4350, 43, 25, 'Markaasaan ka aarsanay, bal day siday noqotay cidhibtii beeniyayaasha (xaqa).'),
(4351, 43, 26, 'Xusa Markuu ku yidhi (nabi) Ibraahim Aabihiis iyo Qoomkiisii anigu waxaan bari ka ahay waxaad caabudaysaan.'),
(4352, 43, 27, 'Waxaanse caabudi Eebaha I abuuray isagaana i hanuunin.'),
(4353, 43, 28, 'Wuxuuna kayeelay Kalimada (tawxiidka) tii ku baaqi ah Faraca Ibraahiim si ay xaqa ugu noqdaan.'),
(4354, 43, 29, 'Saas ma aha ee waan u raaxeeyey kuwaas iyo Aabbayaalkood intuu uga yimaado xaqu iyo Rasuul cad (oo muuqda).'),
(4355, 43, 30, 'Markuu u yimid xaqii waxay dhaheen kan waa sixir annaguna waannu ka Gaalownay.'),
(4356, 43, 31, 'Waxay dhaheen gaaladii maxaa loogu dajin waayey Quraankan mid ka mid ah labada Magaalo ee weyn.'),
(4357, 43, 32, 'Miyagaa qaybin Naxariista Eebahaa anagaa u qaybina dhexdeeda rizqigooda nolosha adduunyo waana ka kor yeelay qaarkood qaarka kale darajooyin, si leysugu shaqeeyo, Naxariista Eebahaa yaa ka khayrbadan waxay kulmin.'),
(4358, 43, 33, 'Hadduusan Dadku noqoneyn umad kaliya (Gaalimada) waxaan uga yeelilahayn kuwa ka Gaalooba Eebe guryahooda Saanqaag Fidlo ah iyo Sallaano Fidla ah, oy ku koraan.'),
(4359, 43, 34, 'Guryhoodana waxaan uga yeeli lahayn Albaabyo iyo Sariiro ay ku dangiigsadaan oo fidla ah.'),
(4360, 43, 35, 'Iyo Dahab, waxaas dhamaantiisana waxaan raaxada nolasha dhaw ahayn ma aha, aakhiraase u ah Eebe agtiisa kuwa dhawrsada.'),
(4361, 43, 36, 'Ruuxii ka jeedsada «aragheela» xuska Eebahaa Raxmaan ah (Quraanka) waxaannu la xidhiidhinaa shaydaan oo saaxiib u noqon.'),
(4362, 43, 37, 'Shayaadiintuna waxay ka leexin Jidka (xaqa) waxayna isu maleyn inay hanuunsanyihiin (toosan yihiin).'),
(4363, 43, 38, 'Markuu nooyimaaddo (Gaalku Qiyaamada) waxuu ku dhahaa (Shaydaanku) shaleytee mey dhexdeenna ahaato «wax la mid ah» fogaanshaha qorrax ka soobax iyo u dhaca, adaana u xun Saaxiib.'),
(4364, 43, 39, 'Wax idiinma tarayso Maanta haddaad dulmi fasheen inaad cadaab wadaagtaan (ku kulantaan).'),
(4365, 43, 40, 'Ma adaa wax maqashiin kara wax ma maqle, ma adaase hanuunin Kara (toosin kara) wax ma arke iyo cid ku sugan baadi cad.'),
(4366, 43, 41, 'Haddaan kulatagno adiga ku (oofsano) anaga uunbaa ka aarsan Gaalada.'),
(4367, 43, 42, 'Haddaan ku tusino waxaan u yaboohnayoo cadaab ah, waanu karraa korkooda.'),
(4368, 43, 43, 'Ee qabso waxa laguu waxyooday Nabiyow waxaad ku sugantahay Jid toosan.'),
(4369, 43, 44, 'Quraankuna sharaf buu u yahay adiga iyo qoomkaagaba, waana leydin warsan doonaa.'),
(4370, 43, 45, 'Warso kuwaan dirray ee kaa horeeyey oo Rasuuladanada ka mid ahaa inaan yeellay Eebaha Raxmaana Kasoboow Ilaahyaal la caabudo.'),
(4371, 43, 46, 'Dhab ahaan yaan ugu dirray Nabi Muuse isagoo wata aayaadkanaga xaga Fircoon iyo Jamaacadiisa, wuxuu yidhi anigu waxaan ahay Rasuulkii Eebaha Caalamka.'),
(4372, 43, 47, 'Markuu ulayimid aayadkanaga waxay noqdeen kuwo ku qosla.'),
(4373, 43, 48, 'Ma tusino aayad haddii kale wey ka weyntahay walaasheed (tan kale) waxaana ku qabanay cadaab inay noqdaan darteed.'),
(4374, 43, 49, 'Waxay dhaheen kan Saaxirka (Caalimka) ahaw (Muuse) noo bari Eebahaa wuxuu kugula ballantamay wanagu waan hanuuneynaaye.'),
(4375, 43, 50, 'Markaan ka faydnay xaggooda cadaabkii waxay soo bexeen kuwo buriya (ballankii).'),
(4376, 43, 51, 'Wuu u dhawaaqay Fircoon Qoomkiisii wuxuu yidhi miyuusan ii sugnaan xukunka Masar, iyo Wabiyadan socota hoostayda, miyeydaan wax arkaynin.'),
(4377, 43, 52, 'Saw anagu kama khayroonin kan dulleysan een karayn waxcaddayn (Nabi Muuse).'),
(4378, 43, 53, 'Maxaa loogu soo tuuriwaayey Jijimooyin Dahab ah, ama maxay ula imaan wayday Malaa\'ig israac raacsan.'),
(4379, 43, 54, 'Wuxuu fudaysaday (Fircoon) qoomkiisii wayna adeeceen waxayna ahaayeen Qoom faasiqiin ah.'),
(4380, 43, 55, 'Markey nacadha galiyeenna waan ka aarsanay xaggooda, waana maanshaynay dhamaan.'),
(4381, 43, 56, 'Waxaan ka yeeley waano iyo tusaaale Dadka dambe.'),
(4382, 43, 57, 'Markaan uga yeellay ina maryama (Ciise) tusaale, markaasay qoomkaagu Nabiyow ka jeedsadaan.'),
(4383, 43, 58, 'Waxayna dheheen ma Ilaahyadanaa khayr roon mise isaga, mayna kuugu qadarin tusaale muran mooyee, waana qoom dood badan.'),
(4384, 43, 59, 'Wax kale ma aha Ciise ee waa uun addoon aan u Nicmaynay ugana yeellay tusaale ree Bani Israa\'iil.'),
(4385, 43, 60, 'Haddaan doono waxaan idiinka yeelilahayn Malaa\'ig badalkiina Dhulka u hadha.'),
(4386, 43, 61, 'Nabi Ciisana waa calaamadda Saacadda (Qiyaame) ee ha shakiyina, ina raaca kaasaa ah jidka toosane.'),
(4387, 43, 62, 'Yuuna idinka leexinin Shaydaan (Jidka) wuxuu idiin yahay Col cade.'),
(4388, 43, 63, 'Markuu ulayimid Ciise xujooyin wuxuu ku yidhi waxaan idiin la imid xikmad iyo inaan idiin cadeeyo waxaad isku diidantihiin qaarkiis ee ka dhawrsada Eebe ina adeeca.'),
(4389, 43, 64, 'Eebe isagaa ah Eebahay iyo Eebihiinba ee caabuda taasaa ah jidka toosane.'),
(4390, 43, 65, 'Wayse is khilaafeen Xisbiyadii dhexdooda halaagna wuxuu u sugnaaday kuwii dulmi falay Cadaab Maalin daran xageed.'),
(4391, 43, 66, 'Miyey sugi (Gaaladaasi) waxaan saacadda (qiyaame) ahayn inay ugu timaado si kado ah iyagoon ogayn.'),
(4392, 43, 67, 'Saaxiibadu Maalinta Qiyaame qaarkood qaarka (kale) waa u col kuwa dhawrsaday mooyee (Mu\'miniinta).'),
(4393, 43, 68, '(Waxaana loo odhon) addoomadaydoow korkiina cabsi ma aha Maanta mana murugoonaysaan.'),
(4394, 43, 69, 'Waana kuwa rumeeyey Aayaadkanaga oo ahaa Muslimiinta.'),
(4395, 43, 70, '(Waxaana lagu dhihi) gala Janada idinkiyo Haweenkiinna (iyo kuwa idin la midka ah) idinkoo laydiin Nicmeeyey (laydin kana farxin).'),
(4396, 43, 71, 'Waxaana lala korsocon Weelal (Saxamo) Dahab ah iyo Koobab, waxayna ka heli (Jannada) dhexdeeda waxay doonto Naftu kuna raaxaystaan Indhuhu idinkoo dhexdeeda ku waari.'),
(4397, 43, 72, 'Taasina waa janadii aad ku dhaxasheen waxaad camalfalayseen.'),
(4398, 43, 73, 'Waxaadna ku leedihiin dhexdeeda Faakiho badan ood xaggeeda wax ka cuntaan.'),
(4399, 43, 74, 'Dambiilayaashu waxay gali cadaabka jahanamo iyagoo ku waari.'),
(4400, 43, 75, 'Lagamana fudaydiyo dhexdiiseyna ku quustaan (aamusaanna).'),
(4401, 43, 76, 'Manaan dulmiyin hasa yeeshee iyagaa dulmiilayaal ahaa.'),
(4402, 43, 77, 'Waxayna u dhawaaqi Maalig (Malaga Naarta) iyagoo dhihi Eebahaa ha nadilo markaasuu dhahaa idinku waas kuwaaraysaan (naarta).'),
(4403, 43, 78, 'Dhab ahaanbaan idiin la nimid xaq hasa yeeshee badankiin xaquu nacay.'),
(4404, 43, 79, 'Ma waxbayse gooyeen Gaaladu oo amar ah (dhibka Nabiga) anaguna waxbaan goynaynaa (ciqaabtooda).'),
(4405, 43, 80, 'Mise waxay umaleeyeen inaanaan maqlayn qarsoodigooda iyo muujintoodaba, saas ma ah ee Mala\'igtanadaa agtooda wax ku qoro.'),
(4406, 43, 81, 'Waxaad dhahdaa Nabiyow haddii Eebaha Raxmaan ah usugnaado Ilmo anaa u hor caabudilahaa.'),
(4407, 43, 82, 'Waxaa ka nasahan Eebaha Samooyinka iyo Dhulka ee Eebaha Carshiga ah waxay ku timaamaan.'),
(4408, 43, 83, 'Ee katag ha dhunbadeen (xumaanta) hana ciyaareen intay kala kulmaan maalintoodii loo yaboohaye.'),
(4409, 43, 84, 'Eebe waa macbuudka Samada iyo macbuudka Dhulka, waana falsame wax og.'),
(4410, 43, 85, 'Waxaana barako badnaaday (sarreeyeyna) Eebaha iska leh xukunka Samooyinka iyo dhulka iyo waxa u dhaxeeya, agtiisaana ah ogaanshaha Saacadda (Qiyaame) xaggiisaana laydiin celin.'),
(4411, 43, 86, 'Mana hantaan kuway caabudeen Eebe ka sokow wax shafeeca ah hasayeeshee ciddii xaqa qirta iyagoo og (waxbay ushafeeci idanka Eebe ka dib).'),
(4412, 43, 87, 'Haddaad warsato Gaalada yaa abuuray waxay dhihi Eebe ee xaggee loo iili.'),
(4413, 43, 88, 'Nabiguna wuxuu yidhi: Eeboow kuwani waa qoom aan rumaynayn (xaqa).'),
(4414, 43, 89, 'Iska saamax waxaadna dhahdaa Nabadgalyo, way ogaandoonaan (waxa ku dhacee).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'Waannu sheegnay iyadoo kale.'),
(4416, 44, 2, 'Waxaan ku dhaartay Kitaabka Cade (Quraanka).'),
(4417, 44, 3, 'Anagaa dejinay Quraanka Habeen barakeysan (Layletulqadar) anagaana wax u digi.'),
(4418, 44, 4, 'Habeenkaasaana la kala bixiyaa amar kastoo xigmo leh.'),
(4419, 44, 5, 'Waana amar agtanada ka ahaaday, anagaana wax dirra (Rasuulo).'),
(4420, 44, 6, 'Naxariis ka ahaater xagga Eebahaa, Eebaana wax maqle ah wax og.'),
(4421, 44, 7, 'Waana Eebaha samooyiinka iyo Dhulka iyo waxa u dhaxeeya haddaad yaqiinsanaysaan.'),
(4422, 44, 8, 'Ilaahay mooyee Eebe kalane ma jiro, isagaa wax nooleeya waxna dila, waana Eebihiina iyo Eebaha Aabbayaalkiinii horreeyey.'),
(4423, 44, 9, 'Waxayse ku suganyihiin shaki wayna ciyaari.'),
(4424, 44, 10, 'Ee sug Maalinta uu ku imaan Cirku qiiq muuqda (isagoo ku dheehan).'),
(4425, 44, 11, 'Oo dabooli Dadka kaasina waa cadaab daran.'),
(4426, 44, 12, 'Waxayna dheheen Eebow naga fayd cadaabka waxaan nahay Mu\'miniine.'),
(4427, 44, 13, 'Goormay waantoobi isagoos u yimid Rasuul cad «muuqda».'),
(4428, 44, 14, 'Markaasay ka, jeedsadeen Nabiga waxayna dheheen waa waxlabare waalan.'),
(4429, 44, 15, 'Anagu waxaan idiinka faydaynaa cadaabka wax yar idinkuna waxaad ku noqonaysaan xumaanta.'),
(4430, 44, 16, 'Maalintaan qabanayno qabashada wayn (Qiyaamada) (ama Badar) waanu aarsan.'),
(4431, 44, 17, 'Dhab ahaanbaan Imtixaanay hortood qoomkii Fircoon, waxaana u yimid Rasuul sharaf leh.'),
(4432, 44, 18, 'Isagoo dhihi iidhiiba addoomada Eebe (ama maqalka) waxaan idiin ahay Rasuul aamin ah.'),
(4433, 44, 19, 'Hana iska wayneynina Eebe waxaan idiin la imid xujo cade.'),
(4434, 44, 20, 'Waxaana ka magangalay Eebahay iyo Eebihiin inaad idhagaxyeysaan.'),
(4435, 44, 21, 'Ee hadaydaan i rumeyn iga fogaada (idaaya).'),
(4436, 44, 22, 'wuxuuna u qaylyey Eebihiis in kuwaasu yihiin Qoom dambiila yaal ah.'),
(4437, 44, 23, 'guuri addoomadayda Habeenimo, waa laydin raaciye.'),
(4438, 44, 24, 'kana tag badda iyadoo xasilloon oo furan iyagu waa col la maanshayne.'),
(4439, 44, 25, 'imisey ka tageen Beero iyo Ilo.'),
(4440, 44, 26, 'iyo Tallaal iyo meel sharaf leh.'),
(4441, 44, 27, 'iyo Nicmooyin ay ku raaxaysanayeen.'),
(4442, 44, 28, 'xaalkuna waa saas, waxaana dhaxalsiinay qoom kale.'),
(4443, 44, 29, 'uma ooyin samada iyo Dhulku korkooda, lamana sugin.'),
(4444, 44, 30, 'waana ka korinay ree Bani Israa\'iil cadaab wax duleeya.'),
(4445, 44, 31, '(Fircoombaan ka korinay) wuxuuna ahaa isla weyne xadgudbayaasha ka mid ah.'),
(4446, 44, 32, 'waxaana ka dooranay (dadkii waqtigoodii) anagoo og.'),
(4447, 44, 33, 'waxaana siinay aayado imtixaan cad kusuganyahay.'),
(4448, 44, 34, 'kuwaasi (Gaaladu) waxay dhihi.'),
(4449, 44, 35, 'arrintu ma aha geerida hore mooyee, mana nihin kuwo la soobixin.'),
(4450, 44, 36, 'noo keena aabayowgano haddaad run sheegaysaan.'),
(4451, 44, 37, 'miyagaa khayr roon mise qoomkii Tubax iyo kuwii ka horreeyey waan halaagnay waxayna ahaayeen dambiilayaal.'),
(4452, 44, 38, 'Samooyinka iyo Dhulka iyo waxa u dhaxeeya umaanaan abuurin ciyaar.'),
(4453, 44, 39, 'mana aanaan u abuurin wax aan xaq ahayn hasa yeeshee badankood ma oga.'),
(4454, 44, 40, 'maalinta kala bixinta yaa ballan u ah dhamaan.'),
(4455, 44, 41, 'waana Maalintaan Qaraabanimo waxba laysugu tarayn laysuna gargaarayn.'),
(4456, 44, 42, 'cid Eebe u naxariisto mooyee, illeen waz adkaade naxariistee.'),
(4457, 44, 43, 'Geedka Zaquumka ah.'),
(4458, 44, 44, 'waa cunnada dambi badanaha.'),
(4459, 44, 45, 'waana Laami oo kale kuna kari Caloosha.'),
(4460, 44, 46, 'sida Xamiimka (Kulayl daran) oo kale.'),
(4461, 44, 47, '(waxaana lagu dhihi) qabta Gaalka una Jiida Jaximo dhexdeeda.'),
(4462, 44, 48, 'markaas ku shuba Madaxiisa korkiisa cadaab kulul.'),
(4463, 44, 49, '(waxaana lagu dhihi) dhadhami (Cadaabka) waxaad tahay adkaade sharaflehe.'),
(4464, 44, 50, 'kaasina waa kaad ahaydeen kuwo shakiya (Cadaabka).'),
(4465, 44, 51, 'kuwa dhawrsadana waxay kusugnaan meel aamin ah.'),
(4466, 44, 52, 'Jannooyin iyo Ilo (yey kusugnaan).'),
(4467, 44, 53, 'waxayna xidhan Xariir jilicsan iyo mid adag (oo dhalaali) wayna is qaabili.'),
(4468, 44, 54, 'saasoo kale waxaa usugnaaday Haween Indho waaweyn.'),
(4469, 44, 55, 'waxayna uyeedhan Janada dhexdeeda Khudaar kasa iyagoo aamin ah.'),
(4470, 44, 56, 'mana ku dhadhamiyaan Jannada dhexdeeda Geeri, Geeridii hore mooyee wuxuuna ka dhawray (Eebe) Cadaabka Jaxiimo.'),
(4471, 44, 57, 'waana fadliga Eebahaa, taasina waa liibaanta wayn.'),
(4472, 44, 58, 'waxaan ugu fududaynay Quraanka Carrabkaaga inay waantoobaan.'),
(4473, 44, 59, 'ee sug iyaguna way sugiye.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'waa soo hormaray macnaheedu.'),
(4475, 45, 2, 'soo dajinta Kitaabka (Quraanka) ah wuxuu ka ahaaday Eebaha adkaada iyo falkan san.'),
(4476, 45, 3, 'Samooyinka iyo Dhulka dhexdiisana aayadbaa ugu sugan Mu\'miniinta.'),
(4477, 45, 4, 'abuurkiinna iyo waxa Eebe ku fidin «abuuri» Dhulka oo wax socda ah aayaadbaa ugu sugan ciddii wax yaqiinsan.'),
(4478, 45, 5, 'iskhilaafka Habeenka iyo Maalinta iyo waxa Eebe ka soodajiyey Samada oo Rizqi ah oo ku nooleeyo Dhulka intuu dhintay ka dib iyo gagadoonka Dabaylaha waxaa ugu sugan aayaad ciddii wax kasi.'),
(4479, 45, 6, 'taasi waa aayaadka Eebe aan ku akhriyeyno korkaaga si xaq ah, ee hadalkay Eebe ka dib iyo aayaadkiisa rumayn.'),
(4480, 45, 7, 'halaagna wuxuu u sugnaaday beenaale dambi badan dhammaantiis.'),
(4481, 45, 8, 'oo maqli aayaadka Eebe oo la akhrin haddana daa\'ima gaalnimo, isagoo iskibrin sidii isagoon maqlin, ugu bishaaree cadaab daran.'),
(4482, 45, 9, 'markuu ogaado aayaadkanaga wax ka mid ah wuxuu ka yeeshaa jees-jees, kuwaasina waxaa u sugnaaday cadaab wax dulleeya.'),
(4483, 45, 10, 'waxaa koobi «kana dambeysa» Naarta Jahannamo, waxna uma taro waxay kasbadeen, iyo waxay ka yeesheen Eebe ka sokow owliyo, waxayna mudan Cadaab wayn.'),
(4484, 45, 11, 'Quraankani waa hanuun, kuwii ka gaaloobay aayaadka Eebehoodna waxaa u sugnaaday Cadaab xumaan daran.'),
(4485, 45, 12, 'Eebe waa kan idiinfududeeyey Badda say ugu socoto Doonnidu «iyo Markabku» dhexdiisa amarka Eebe, iyo inaad gaadhaan Fadligiisa iyo inaad ku mahadisaan.'),
(4486, 45, 13, 'wuxuu idiin fududeeyey waxa ku sugan samooyinka iyo Dhulka Dhammaan xagiisay ka ahaadeen, arrintaasna waxa ugu sugan aayaad ciddii fikiri.'),
(4487, 45, 14, 'waxaad u dhahdaa kuwa rumeeyey u dhaafa kuwaan ka yaabayn ciqaabta Eebe si uu uga abaal mariyo qoom waxay kasbadeen.'),
(4488, 45, 15, 'ruuxii camal fiicanfala Naftiisuu u falay, ruuxii Xumaan sameeyana waxay uun dhibhi Naftiisa markaas xagga Eebe uunbaa laydiin celin.'),
(4489, 45, 16, 'dhab ahaanbaan u siinay ree Binii Israa\'iil Kitaabka iyo Xukun iyo Nabinimo, waxaana ku arzuqnay wax wanaagsan, waxaana ka fadillay Caalamkii (markaas).'),
(4490, 45, 17, 'waxaana siinay xujooyin ka mid ah amar, ismana khilaafin, inta cilmi u yimid ka dib mooyee, waxayna u diideen Xasad iyo Dulmi, dhexdooda ah, Eebahaana wuu kala xukumi dhexdooda Malinta Qiyaame waxay isku khilaafsanyihiin.'),
(4491, 45, 18, 'waxaana ku yeellay waddo amarka Diinta ah, Ee raac, hana raacin hawada kuwaan wax ogayn.'),
(4492, 45, 19, 'iyagu kaagama taraan Eebe xagiisa waxba, Daalimiintana qaarkood wuxuu u yahay sokeeye qaarka kale, Eebana waa u wali kuwa dhawrsada.'),
(4493, 45, 20, 'kaasi waa (Quraanku) waa aragti (Nuurka) Dadka iyo hanuun iyo Naxariis ciddii waxyaqiinsan.'),
(4494, 45, 21, 'mise waxay umaleeyeen kuwii kashaday xumaanta inaan kayeeleyno kuwa xaqa rumeeyey ee camal fiican falay oo kale, nolol iyo geeriba, waxaa xun waxay xukumi.'),
(4495, 45, 22, 'Eebaa u abuuray samooyinka iyo Dhulka, xaq iyo in laga abaalmariyo Naf walba waxay kasbatay, lamana dulmiyo.'),
(4496, 45, 23, 'ka waran ruux ka yeeshay ilaahiisa hawadiisa, oo Eebe dhumiyey isagoo cilmi leh, oo daboolay maqalkiisa iyo qalbigiisa, yelayna aragiisa dabool, yaa hanuunin Eebe kadib, miyaydaan wax xusuusanayn.'),
(4497, 45, 24, 'waxay dheheen Gaaladii wax kale ma jiro ee waa uun nolashanada dhaw, waana dhimaneynaa qaarna noolaan, waxaan waqtiga ahayna nama dilo, wax ogaansho ahna uma leh arrintaas waxaan male ahayna kuma socdaan.'),
(4498, 45, 25, 'marka lagu akhriyo korkooda aayaadkanaga oo cad wax xuja ah ma helaan oon ahayn noo keena aabbayowganno haddaad run sheegaysaan.'),
(4499, 45, 26, 'waxaad dhahdaa Eebaa idin nooleeya idina dili, idinna kulmin Maalinta Qiyaame shakina ma leh, laakiin Dadka badankiis ma oga.'),
(4500, 45, 27, 'Eebaa iska leh xukunka samooyinka iyo Dhulka, Maalintay kacdo Saacadda Qiyaame maalintaasay Khasaari xumaanlowgu.'),
(4501, 45, 28, 'waxaadna arki umad kastoo Lawyahay ku fadhida Qiyaamada, Umad kastana waxaa loogu yeedhi Kitaabkii «camalkeeda» Maalintaas waxaa laydiinka abaalmarin waxaad camalfaleyseen.'),
(4502, 45, 29, 'kani waa Kitaabkanagii wuxuuna ku hadli xaq korkiina, annaguna waan qoraynay waxaad camal falaysaan.'),
(4503, 45, 30, 'kuwa xaqa rumeeyey oo camal fiican falay wuxuu galin Eebahood Naxariistiisa, taasina waa liibaan wayn.'),
(4504, 45, 31, 'kuwa Gaaloobayse waxaa lagu dhihi miyeyna ahayn aayaadkeyga kuwa korkiina lagu akhriyo ood iskibriseen noqoteen qoom dambi falayaal ah.'),
(4505, 45, 32, 'marka la dhaho yabooha Eebe waa xaq, Saacadda (Qiyaamana) shaki ma leh, waxaad dhahdaan ma naqaan Saacadda (qiyaame) waannu uun malayn mase yaqiinsanin.'),
(4506, 45, 33, 'waxaa u muuqan Gaalada xumaantay camalfaleen (Ciqaabteedi) waxaana koobi waxay ku jees jeesi jireen (ciqaabtiisii).'),
(4507, 45, 34, 'waxaana loo dhihi Maanta (Qiyaamada) waanu idinka tagi (idinko cadaaban) sidaad u halmaanteen la kulanka Maalintiinan, Hooygiinuna waa Naar wax idiin gargaarana ma jiro.'),
(4508, 45, 35, 'Arrintaasna waxaa ugu waxan inaad ka yeelateen aayaadka Eebe Jees jees, idinna dhagartay nolosha dhaw (ee adduun) Maanta Qiyaamada lagama bixiyo Naarta, cudurdaarna lama warsado.'),
(4509, 45, 36, 'mahad Eebaa iskaleh Eebaha Samooyinka iyo Dhulka, Eebaha caalamkana ah.'),
(4510, 45, 37, 'waxaana u sugnaatay waynida Samooyinka iyo Dhulka dhexdiisa, waana adkaade falsan.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'Waxay ku Tusin mucjisada Quraanka ee wayn, Eebaa ogna waa la dhahaa.'),
(4512, 46, 2, 'Kitaabka (Quraanka) ah wuxuu ka soo dagay Eebaha adkaada ee falka san xaggiisa.'),
(4513, 46, 3, 'Samaawaadka iyo dhulka iyo waxa u dhexeeyana waxaan xaq iyo muddo magacaaban ahayn uma aannaan abuurin, kuwii gaaloobayna wixii loogu digay (ee quraanka) ah way ka jeedsadeen.'),
(4514, 46, 4, 'Waxaad Dhahdaa nabiyoow bal ka warrama waxaad caabudaysaan ee Eebe ka soo hadhay ina tusiya waxay ku abuureen dhulka, ama shirkad ah oy ku leeyihiin Samaawaadka, ii keena (oo i tusiya) Kitaab Quraanka, ka horreyay, ama wax raad cilmi ah, haddaad run sheegeeysaan.'),
(4515, 46, 5, 'Wax ka dulmi badan ma jiro ruux baryi Eebe ka sokow waxaan ajiibeynin (maqleyn) tan iyo qiyaamada, baryadoodana halmaansan (ogyn).'),
(4516, 46, 6, 'Marka dadka la soo kulmiyana u noqondoona kuwii caabudi jira, col, Inay caabudi jireenna dafiri.'),
(4517, 46, 7, 'Marka gaalada lagu dul akhriyo aayaadkannaga (quraanka) oo cad waxay dhahaan kuwa gaalada ah markuu u yimid xaqu kani waa sixir cad.'),
(4518, 46, 8, 'Waxay dhihi waa been uu abuurtay (Nabigu), Waxaad dhahdaa haddaan been abuurto waxba iigama taraysaan Eebe xaggiisa, isagaana og waxaad dhumbanaysaan, (oo been ah) Eebaana marag ugu filan dhexdeenna, waana dambi dhaafe naxariista ah.'),
(4519, 46, 9, 'Waxaad dhahdaa ma ihi bidei ka gaar ah Rasuullada, mana ogi waxa lagu fali aniga iyo idinka, waxaan uun raaci waxa lay waxyoodo (ee quraanka ah) waxaana ahay u dige cad (oo muuqda).'),
(4520, 46, 10, 'Waxaad Dhahdaa bal ii warrama hadduu Quraanku Eebe ka soo dagay ood ka gaalowdaan oo uu ka marag furo marag ree Bani\'iisraaiil ah isaga oo kale (xaqnimada iyo runta) oo uu rumeeyay idinkuna aad is kibriseen, Eebana ma hanuuniyo kuwa dulmilowga ah,'),
(4521, 46, 11, 'Gaaladii waxay dheheen hadduu Quraanku khayr yahay noogama hormareen rumayntiisa, iyagoon hanuunsanayn gaaladu yay haddana dhihi kan (Quraanka ah) waa beenihii hore.'),
(4522, 46, 12, 'Quraanka waxaa ka horreeyay kitaabkii (Nabi) Muuse, isagoo ah hanuuniye iyo naxariis, kanna waa kitaab u rumayn (xaqa). Af carabina ku soo dagay, si uu ugu digo kuwa dulmiga falay, uguna bishaareeyo kuwa wanaagga falay.'),
(4523, 46, 13, 'kuwa yidhi Eebahanno waa Alle haddana toosnaaday cabsi iyo murugi midna ma arkaan.'),
(4524, 46, 14, 'Kuwaasna waa ehlu-Janno wayna ku waari dhexdeeda, abaalmarin camalkoodii (wanaagsanaa) darteed.'),
(4525, 46, 15, 'Dadka waxaan u dardaarannay labadiisii Waalid inuu u wanaag falo, Hooyadiis uurkay ku sidday iyadoo dhibban wayna dhashay iyadoo dhibban, uurkiisa iyo gudhintiisuna waa Soddon bilood, markuu ilmihii tabar yeesho oo Afartan gaadho yuu (kan fiican) dhahaa eebow igu toosi inaan kugu mahadiyo nicmadaada aad iigu nicmaysay Aniga iyo waaliddiintay, iguna toosi inaan camal fiican ood ka raalli tahay falo, iina wanaaji faracayga, waaba kuu toobadkeenaye, waxaana ka mid ahay muslimiinta.'),
(4526, 46, 16, 'Kuwaasi waa kuwaannu ka aqbalayno camalkoodii fiicnaa, kana dhaafayno koodii xumaa, waxayna noqon ehelu-Janno Eebana wuxuu u yaboohay yabooh run ah.'),
(4527, 46, 17, 'Waxaa jira mid ku dhihi labadiisii waalid Ufbaa la idin yidhi ce ma waxaad ii yaboohaysaan in la i sou bixin iyadoo dadkii horaba lagay, Labada waalidna ay Eebe uga baryi (hanuun), iyagoo ku dhihi magacaa ba\'; ee rumee, ballanqaadka Eebana waa xaqe, oo markaas ku dhihi waxaasi waa beentii dadkii hore.'),
(4528, 46, 18, 'Kuwaasi waa kuwa caddibaadi u sugnaatay, iyagoo ka mid noqon kuwii horay u tagey oo Jinni iyo Insiba leh, waxayta ahaayeen kuwa khasaaray.'),
(4529, 46, 19, 'Cid walba waxay mudan waxay camal fashay Eebana wuu u oofin camalkooda iyagoon la dulmiyaynin.'),
(4530, 46, 20, '(Xusuuso) maalinta kuwii gaaloobay loo bandhigi Naarta, iyagoo lagu dhihi waxaad wanaaggiinii ku dhamaysateen noloshiinnii dhawayd, waadna ku raaxaysateen, maantase waxaa laydinku abaal marin caddibaad dulli ah, iskibrintiinnii aad xaq darro isula weeyneeydcen iyo faasiqnimadiinna darteed.'),
(4531, 46, 21, 'Xusuuso Ree caad walaalkood (Nabi huud) markuu u digay qoomkiisii iyagoo daggan Axqaaf, iyagoo Rasuullo wax u diga ka horreeyeen kana dambeeyeen, isagoo ku dhihi Eebe mooyee wax kale ha caabudina, waxaan idiinka yaabi caddibaad maalin wayn/'),
(4532, 46, 22, 'Waxay ku dheheen ma waxaad noola timid inaad naga iisho Ilaahyadannada, noo keen waxaad noogu doogiyayso haddaad run sheegeyso,'),
(4533, 46, 23, 'Wuxuuna ku yidhi Eebaa ku cilmi leh (goorta caddibaaddiinna) ee anugu waxaan idin gaadhsiin waxa layfaray inaan gaadhsiiyo, waxaanse idinku arkaa kuwa jaahiliin ah.'),
(4534, 46, 24, 'Markay Gaaladii Reer caad arkeen caddibaaddii (Sida daruur) oo Toggooda soo qaabishay waxay, dheheen waa Daruur nawaraabinaysa, Ma aha ee waa waxaad dadajisanayseen inuu timaaddo oo caddibaad ah, waana dabayl caddibaad darani ku jirto.'),
(4535, 46, 25, 'Wax kasta oy martana way burburin amarka Eebe dartiis, markaasay ahaadeen kuwa aan la arkaynin Guryohoodii mooyee, saasaana ku abaal marinnaa kuwa dambiilayaasha ah.'),
(4536, 46, 26, 'Waxaan makaninnay (siinnay) Ree caad wax aannaan idin makaninnin, waxaana u yeellay maqal, arag iyo Quluub, waxbase uma tarin Maqalkoodii, Araggoodii iyo Quluubtoodii, Maxayeelay Waxay diidayeen Aayaadka Eebe, waxaana ku dhacay (oo koobay) caddibaadii ay ku jees jeesayeen.'),
(4537, 46, 27, 'Waxaan halaagnay wixii gaararkiinna ahaa oo Magaalooyin ah, xujuuyinkana waan caddaynay si dadku Eebe ugu noqdo.'),
(4538, 46, 28, 'Maxay ugu gargaariwaayeen kuway ilaahyaal ay u dhawaadaan ka dhigteen Eebe ka sokow, way ka dhumeen, taasina waa beentoodii iyo war abuurashadoodii.'),
(4539, 46, 29, 'Xusuuso markaan kuu soo iilnay koox Jinni ah oo Quraanka dhagaysan, markay yimaadeen (meeja lagu akhriyina) ay isu dheheen dhagaysta, markuu la dhammeeyayna ay ciddoodii ku noqdeen iyagoo u digi.'),
(4540, 46, 30, 'Oo ku dhihi qoomkannow waxaan maqallay Kitaab (Quraan) la soo dajiyay (Nabi) Muuse ka dib oo rumayn Kitaabihii ka horreeyey, kuna hanuunin xaq iyo waddada toosan.'),
(4541, 46, 31, 'Qoomkannow ajiiba (raaca) uyeedhaha xaqa Eebe, rumeeyana, dambigiinna Eebe ha dhaafee, hana idinka koriyo caddibaad darane;'),
(4542, 46, 32, 'Ruuxaan ajiibin (raacin) uyeedhaha Eebe kuma daaliyo Eebe dhulka dhexdiisa (kama cararo) wax Eebe ka soo hadhay oo u gargaarina ma jiro, kuwaas «aan maqlaynin yeedhidda Eebana» waa kuwa baadi cad ku sugan.'),
(4543, 46, 33, 'Miyeeyna ogayn Eebaha abuuray Samaawaadka iyo dhulka aan kana noogin abuurkooda inuu karo inuu soo nooleeyo wixii dhintay, saas ma aha ee Eebe wax walba oo uu doono wuu karaa.'),
(4544, 46, 34, 'Maalinta kuwii gaaloobay loo bandhigi Naarta waxaa lagu odhan Miyuuna xaq ahayn kan (caddibaadda ah) markaasay dhahaan Eebaan ku dhaawannaye waa xaq, Eebe wuxuu ku dhahaa dhadhamiya caddibaadda Gaalnimadiinna darteed.'),
(4545, 46, 35, 'Usamir siday u samreen kuwii sugnaanta iyo niyadda u saaxiibka ahaa ee rasuullada ahaa, hana ku dagdagin in la caddibo waxay la midnoqon markay arkaan waxa loogu goodiyay ineeyan ku nagaanin Adduunka Saacad maalin ah waxaan ahayn, Quraankuna waa gaadhsiin, kuwa faasiqiinta ah umbaana la halaagaa.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'kuwii Gaaloobay oo jidka Eebana ka leexiyay Dadka Eebe waa buriyay camalkooda.'),
(4547, 47, 2, 'kuwa rumeeyay xaqase oo camal fiican falay oo rumeeyay Quraanka lagu soo dajiyay (Nabiga) Muxammad ah ee xaqa ah Eebe wuu astuuri xumaantooda, xaalkoodana wuu wanaajin.'),
(4548, 47, 3, 'saasna waxaa ugu wacan in kuwa Gaaloobayna ay raaceen Baadhil, kuwa rumeeyayna ay raaceen Baadhil, kuwa rumeeyayna ay raaceen xaqa Eebe, saasuuna Eebe u yeelaa tusaale.'),
(4549, 47, 4, 'Haddaad kula kulantaan Gaalada Dagaal Luqunta ka garaaca, markaad sugtaanna ku adkeeya xidhidda, markaas ama iska siidaaya ama haysfurteen, inta Dagaalku ka xasili, xaalku waa saas, hadduu Eebe doonana wuu ka aarsankaraa, hase yeeshee wuxuu saas u falay inuu qaarkiin qaarka kale ku imtixaano, kuwa Jidka Eebe lagu dilana Camalkooda Eebe ma halleeyo.'),
(4550, 47, 5, 'Eebana wuu toosin oo hanuunin kuwaas, wuuna wanaajinn xaalkooda.'),
(4551, 47, 6, 'Wuxuuna galin Janno (uu baray) u caddeeyay.'),
(4552, 47, 7, 'Kuwa xaqa rumeeyow haddaad u gargaartaan Eebe (Diintiisa) isna wuu idiin gargaari, Gommadihinnana wuu sugi (Geesinnimo).'),
(4553, 47, 8, 'Kuwa Gaaloobayse Halaag iyo (hoos u dhac) yaa u sugnaaday, Camalkoodiina Eebe wuu halleeyay.'),
(4554, 47, 9, 'Saas waxaa ugu wacan inay neceen waxa Eebe soo dajiyay oo xaqa ah, markaasuu hoobiyay Camalkoodii.'),
(4555, 47, 10, 'Miyayna ku soconin dhulka oo markaas ay arkaan siday noqotay Cidhibtii kuwii Gaaloobay ee Ree Maka ka horreeyay, Eebaa halaagay, Gaaladana waxaa u sugnaaday saasoo kale halaag.'),
(4556, 47, 11, 'Saas waxaa ugu wacan in Eebe yahay gargaaraha Mu\'miniinta, Gaaladuna ayna gargaare lahayn.'),
(4557, 47, 12, 'Eebe wuxuu galin Mu\'miniinta Camalka fiicanna falay Jannooyin ay dureeri dhexdeeda Wabiyaal, kuwa Gaaloobayna way raaxaysan (Addnunka) una cuni sida xooluhu wax u cunaan, Naarna waa Gurigay ku hoyan.'),
(4558, 47, 13, 'Imisay jirtay Magaalo ka xoog badan Magaaladaadan ku bixisay (Maka) aanna hallignay, gargaarana aan helin.'),
(4559, 47, 14, 'Ruux xaqa cad ee Eebe ku sugan ma la midbaa Ruux loo qurxiyay Camalkiisa xun oo haddana waxa naftiisu jeceshahay (ee xun) raaca.'),
(4560, 47, 15, 'Tilmaanta Jannada loogu yaboohay mu\'miniinta waa in Wabiyaal biya ah oon doorsoomaynin dhexdeeda yihiin, iyo Wabiyaal Caano ah ooo iyana dhadhankoodu doorsoomaynin, iyo Wabiyaal Khamro (xalaal ah) oo u macaan dadka cabbi, iyo Wabiyaal Malab ah oo la sifeeyay, waxayna Jannada ku leeyihiin nooc kasta ooMidho ah, iyo dambidhaafka Eebe. Ma la midbaa Ruux Narta ku waari lagana waraabin Biyo kulayl daran oo Xiidmahooda gooya/'),
(4561, 47, 16, 'Waxaa ka mid ah Gaalada kuwo ku dhagaysan (Nabiyow), Markay agtaada ka baxaanna ku dhihi kuwa Cilmiga leh (Asxaabta) Muxuu lahaa dhawaan, kuwaasi waa kuwa Eebe daabacay Quluubtooda, waxay naftoodu jeclaydna (oo xunna) raacay.'),
(4562, 47, 17, 'Kuwa hanuunay (Eebe) wuxuu u siyaadiyaa hanuun, wuxuuna waafajiyaa (Eebe) kayaabiddiisa.'),
(4563, 47, 18, 'Gaaladu ma waxay sugi in Qiyaamadu kado ugu timaaddo, waxaase yimidba Calaamooyinkeedii, ee sideebay xusuus ugu sugnaan hadday Qiyaamadu timaaddo.'),
(4564, 47, 19, 'Ogow (kuna sugnow Nabiyow) inaan Eebe mooyeen Ilaah kale jirin, una dambi dhaaf waydii naftaada iyo mu\'miniinta Rag iyo Haweenba, Eebana waa ogyahay dhaqdhqaaqiinna iyo nagaadigiinnaba.'),
(4565, 47, 20, 'Waxay kuwa xaqa rumeeyay dhahaan Maa la soo dejiyo Suurad, markiise la soodajiyo Suurad sugan Dagaalkana (Jahaadkana) lagu sheego waxaad arkaysaa kuwa quluubta ka buka oo ku soo fiirin (Nabiyow) sida Ruux sakaraad hayo oo kale, halaag baynamutaan kuwaasi.'),
(4566, 47, 21, 'Daacadnimo iyo hadal wanaagsan yaa u khayr roonaan lahayd, markase xaalku dhaboobo (Jahaad la galo) haday Eebe u runsheegaan yaa u khayr badan.'),
(4567, 47, 22, 'Amaad mudantihiin haddaad xaqa (Jahaadka) ka jeedsataan inaad fasaadisaan dhulka, Qaraabadiinnana goysaan.'),
(4568, 47, 23, 'Kuwaasi waa kuwa Eebe lacnaday, dhagaha iyo Indhahana tiray.'),
(4569, 47, 24, 'Miyayna Quraanka u fiirsanin (sidhab ah) mise Quluubtoodaa daboolan.'),
(4570, 47, 25, 'Kuwa riddoobay (gaalnimo ku noqday) inta xaqu iyo hanuunku u caddaaday ka dib shaydaanbaa u qurxiyay, yididiilo (been ahna) uguna yaboohay.'),
(4571, 47, 26, 'Saas waxaa ugu wacan iney kuwa riddoobay ku dheheen kuwa xaqa nacay waxaan idinku (waafaqaynaa) adeecaynaa amarka qaarkiis (colnimada Nabiga), Eebana waa ogyahay waxay qarsan.'),
(4572, 47, 27, 'Ee say noqon markay nafta ka qaadayso Malaa\'igtu iyadoo ka garaaci Wajiga iyo gadaashaba.'),
(4573, 47, 28, 'Saas waxaa ugu wacan inay raaceen kuwaasi wax Eebe ka cadhoodo, neceenna raalli ahaanshihiisa, markaasuu buriyay camalkoodii.'),
(4574, 47, 29, 'Kuwii Qalbiga ka bukay ma waxay u maleeyeen inaan Eebe soo muujinayn xumaanta (iyo xasadka) Qalbiga kaga jira.'),
(4575, 47, 30, 'Haddaan doonno waan ku tusin karnaa kuwaas, waxaadna ku aqoonsan Calaamad, waxaad kaloo ku oqoonsan hadalka leexsan, Eebana waa ohyahay camalkooda.'),
(4576, 47, 31, 'waannu idin imtixaanaynaa intaan ka muujinno kuwiinna Jahaadi iyo kuwa samra, waana imtixaanaynaa Xaalkiinna.'),
(4577, 47, 32, 'Kuwa gaaloobay ee Jidka Eebe ka leexiya dadka Rasuulkana khilaafa (lana collooba) inta xaqu u caddaaday ka dib waxba Eebe kama dhimayaan Camalkoodana wuu burrin.'),
(4578, 47, 33, 'Kuwa xaqa rumeeyow Eebe adeeca, Rasuukiisana adeeca, Camalkiinnana hayska burrinina.'),
(4579, 47, 34, 'Kuwa gaaloobay oo Jidka Eebe ka leexiyay dadka kadibna dhinta iyagoo gaala ah Eebe uma Dambi dhaafo.'),
(4580, 47, 35, 'Ha tabar darraanina mu\'miniintaay ood markaa heshiis u yeedhaan, idinkaaba ka sarreeyee Eebana idinla jiraaye, Camalkiinnana ma nusqaaminaayee.'),
(4581, 47, 36, 'Nolosha Adduunyo waa uun ciyaar iyo dheeldheel, haddaadse xaqa rumeysaan ood dhawrsataan wuxuu idin siin (Eebe) abaalkiinna, xoolihiinnana idin waydiisan maayo.'),
(4582, 47, 37, 'Hadduu idin waydiiyo (Eebe) xoolihiinna (dhammaan) idinkuna celceliyo waad bakhayloobaysaan, waxaana soo bixi colnimadiinna.'),
(4583, 47, 38, 'Kuwaas xaqa rumeeyow waxaa laydiinku yeedhi inaad jidka Eebe wax ku bixisaan, waxaase idinka mid ah kuwo ku bakhayli, Ruuxiise bakhayla wuxuu uun kala bakhaylay Naftiisa Eebana waa hodon, idinkuna waxaad tihiin fuqaro, haddaad xaqa ka jeedsataanse wuxuu Eebe idinku baddali kuwa kale oon idin lamid noqonayn.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Annagaa ku siinnay fatxi (gargaar) cad Nabiyow.'),
(4585, 48, 2, 'Si Eebe kuugu dhaafo waxaad hormarsatay iyo wixii dib maray oo gaf ah, Nicmadiisana kuugu taam yeelo, Jidka toosanna kuugu hanuuniyo,'),
(4586, 48, 3, 'Kuuna gargaaro Eebe Gargaar wayn.'),
(4587, 48, 4, 'Eebe waa kan ku dajiyay xasilloonida Quluubta mu\'miniinta si ay Iimaan ugu siyaadsadaan Iimaankoodii, Eebaana iska leh Samaawaadka iyo dhulka Junuudda ku sugan, Eebana waa wax walba oge falsan.'),
(4588, 48, 5, 'si uu u galiyo mu\'miniinta rag iyo haweenba Jannooyin ay dureereyso dhexdeeda Wabiyaal iyagoo ku waari, Dambigoodana wuu asturi, arrintaasuna Eebe agtiisa waxay ku tahay Liibaan wayn.'),
(4589, 48, 6, 'iyo inuu caddibo munaafiqiinta Rag iyo Haweeenba, iyo mushrikiinta (gaalada) rag iyo haweenba, Eebana u maleeyey mala xun, xumaantu korkooda ha ku wareegto, Eebana Ha u cadhoodo, hana lacnado, hana u darbo Naarta Jahannamo, meel loo ahaadana iyadaa u xun.'),
(4590, 48, 7, 'Eebaa iska leh Junuudda ku sugan Samaawaadka iyo Dhulka, Eebana waa adkaade falsan.'),
(4591, 48, 8, 'Annagaa ku dirray Nabiyow adigoo marag ah, Bishaareeye Digena ah.'),
(4592, 48, 9, 'Si aad dadow Eebe u rumaysaan iyo Rasuulkiisa uguna gargaartaan, una waynaysaan, Eebana ugu tasbiixsataan Aroor iyo Galabba.'),
(4593, 48, 10, 'Kuwa Nabiyow kula ballamaya waxay la ballamayaan uun Eebe, ballanna ka qaadeen, Ruuxiise burriya Naftiisa umbuu ku buriyay, Ruuxii oofiyana wuxuu Eebe kula ballamay wuxuu siin Ajri wayn.'),
(4594, 48, 11, 'Waxay kugu dhihi kuwii kaddib maray (Jahaadka) ee Ree-Baadiyaha (carbeed ahaa), waxaa na shuqliyay xoolahannagii, iyo ahalkannagii, ee Eebe noo dambi dhaaf waydii, Carrabkooda yay ka dhihi waxaan qalbigooda jirin, waxaad ku dhahdaa yaa Eebe wasx idiinka hanankara hadduu dhib idinla doono, ama gargaar idinla doono Saas ma aha, ee Eebe waa ogyahay waxaad camal falaysaan.'),
(4595, 48, 12, 'Waxaadse u malayseen inayan Rasuulka iyo Mu\'miniinta u soo noqonnaynin ehelkooda waligood, saasaana loogu qurxiyay qalbigiinna, waxaadna ahaateen kuwo halaagsamay.'),
(4596, 48, 13, 'Ruuxaan Eebe rumayn iyo Rasuulkiisa waxaan Gaalo u darabnay Naarta Saciiro.'),
(4597, 48, 14, 'Eebaa iska leh xukunka waxa Samaawaadka iyo Dhulka ku sugan, wuxuuna u dambi dhaafi cidduu doono, wuxuu caddibi cidduu doono, waana dambi dhaafe naxariiste ah.'),
(4598, 48, 15, 'Waxay idinku odhan kuwii dib maray markaad qaniimo aadaan si aad u soo qaadataan nadaaya aan idin raacnee, iyagoo dooni inay baddalaan hadalka Eebe, waxaad dhahdaa naraaci maysaan Eebaana sidaa yidhi mar hore, waxayse dhihi waad na xasdaysaan, waase kuwo aan wax kasaynin in yar mooyeen.'),
(4599, 48, 16, 'Waxaad ku dhahdaa Nabiyow kuwa dib maray ee Ree baadiyaha ah waxaa laydinku yeedhi kuwa xoog badan oo daran inaad la dagaallantaan intay ka islaamaan, haddaadse addeecdaan wuxuu Eebe idin siin ajri fiican, haddaadse jeedsataan saad horayba ugu jeedsateen wuu idin caddibi caddibaad daran.'),
(4600, 48, 17, 'Ruux Indha la\' korkiisa dhib (Jahaad) ma saarra iyo ka naafada ah iyo kan buka, Ruuxiise Eebe addeeca iyo Rasuulkiisa wuxuu galin Jannooyin ay dhexdureeri Wabiyaal, Ruuxiise jeedsada wuu caddibi caddibaad daran.'),
(4601, 48, 18, 'Eebe wuu ka raalli noqday mu\'miniinta markay kugula ballantamayeen Nabiyow Geedka hoostiisa, Eebana waa ogaaday waxa Quluubtooda ku sugan, wuxuuna ku dajiyay xasilooni, wuxuuna ku abaal marin fatxi (nasri) dhaw.'),
(4602, 48, 19, 'Iyo Qaniimooyin badan oy qaadandoonaan, Eebana waa adkaade falsan.'),
(4603, 48, 20, 'Eebena wuxuu idiin yaboohay Qaniimooyin badan ood Qaadanaysaan, Tanna wuu idiin hormariyay, (qaa Qaniimadii khaybar), wuuna idinka reebay Gacmaha dadka (gaalada) si ay ugu noqoto mu\'miniinta calaamo (Runta Nabiga) idiinkuna hanuuniyo jidka toosan.'),
(4604, 48, 21, 'Waxaa kale oo uu idiin dadajiyay Qaniimo kale oydaan awooddeen, Eebana wax walba waa karaa.'),
(4605, 48, 22, 'Hadday idinla diriraan kuwa gaaloobay way carari (oy jabi) sokeeye iyo gargaarna heli maayaan.'),
(4606, 48, 23, 'Saasna waa waddadii Eebe ee u horreysay ummadihii tagay, wax baddali karana ma jiro.'),
(4607, 48, 24, 'Eebe waa kan idin kala reebay «idinna kala ilaaliyay» gaaladii markaad maka gudeheeda joogteen intuu idin makaniyay korkooda kaddib, Eebana waa arkaa waxaad camal falaysaan.'),
(4608, 48, 25, 'Gaaladii makaad waa kuwii masaadjidka xurmaysan idinka reebay iyadooy hadyadii darbantahay inaygaadho halkii lagu xalaalaynlahaa, haddaynan jirin Rag iyo Haween mu\'miniin ah (oo la nool) idinna aydaan ogayn marka aad baabi\'neeysaan ayna mashaqo idin haleesho (Eebe waa idiin fasixi lahaa). Si uu u galiyo naxariistiisa cidduu doono (yuu saas u yeelay), Hadday mu\'miniintu ka soocmi lahaayeen gaalada wuxuu Eebe caddibi lahaa gaalada caddibaad daran.'),
(4609, 48, 26, 'Xusuusta marka Eebe yeelay kuwii gaaloobay Quluubtooda islawayni iyo cadho, sidii isla waynidii iyocadhadii jaahiliinta oo Eebe ku dajiyay xasilloonidiisii Rasuulkiisa korkiisa iyo mu\'miniinta, waafajiyayna kalimada dhawrsashada (Ashahaadada iyo ballan ku sugnaanshaha), iyagaana ugu mudan kalimadaas chelna u ah, Eebana wax walba waa ogyahay.'),
(4610, 48, 27, 'Eebe waa u rumeeyay Rasuulka riyadiisii si dhab ah, abaydna in mu\'miniintu gali MAsjidka xurmaysan hadduu Eebe doono, iyagoo aamin ah oo Timaha xiiri ama gaabin (Xajka iyo Cumrada ka dib) iyagoon cidna ka cabsanaynin, wuxuuna Eebe ogyahay waxaydaan ogayn, markaasuu yeelay galitaanka Maka ka soke Fatxi (Xudaybiya).'),
(4611, 48, 28, 'Eebe waa kan la diray Rasuulkiisa hanuun iyo diin xaq ah, si uu uga sare mariyo Diimaha oo dhan, Eebaana marag ku filan.'),
(4612, 48, 29, 'Muxammad waa Rasuulkii Eebe kuwa la jirana waa ku daranyihiin gaalada, dhexdoodana way isu naxariistaant waxaad arkaysaa iyagoo rukuucsan oo sujuudsan oo dalbi Deeqda Eebe iyo raalli ahaanshihiisa, Calaamadooduna wajigooday ka muuqataa, Sujuudda raadkeeda darteed, saasaana iagu tilmaamay Tawreed, Tilmaantooda injiilna waxay lamid yihiin Beer soo bixisay fiidda oo xoogaysatay oo si fiican u istaagtay layaabna galisay beertaha, si uu Eebe ugu Cadho galiyo gaalada, Eebe wuxuu u yaboohay kuwa xaqa rumeeyay');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'Kuwa xaqarumeeyow ha ku dagdagina Eebe iyo Rasuulkiisa hortiisa (waxayan fasixin), Eebana ka dhawrsada ileen wax walba wuu maqli wuuna ogyahaye.'),
(4614, 49, 2, 'Kuwa xaqa rumeeyow ha ka koryeelina codkiinna codka Nabiga, hana kula jahrina (qaylina) hadal sida qaarkiin qaar ugula jahro (qaylyo) si aan camalkiinnu u burin idinkoon agayn.'),
(4615, 49, 3, 'kuwa codkooda hoos u dhiga Rasuulka agtiisa kuwaasi waa kuwo Eebe imtixaanay quluubtooda dhawrsasho darteed, waxayna mudanyihiin dambi dhaaf iyo ajri wayn.'),
(4616, 49, 4, 'kuwa kaaga dhawaaqi qolalka gadaashooda badankoodu wax ma kasayaan.'),
(4617, 49, 5, 'Hadday samraan intaad uga soo bixi yaa u khayr badnaan lahayda, Eebana waa dambi dhaafe naxariista,'),
(4618, 49, 6, 'Kuwa xaqa rumeeyow hadduu idiinla yimaaddo faasiq war, hubsada, si aydaan dad u dhibin ogaansho la\'aan, markaasna aad shallaydaan waxaad fasheen.'),
(4619, 49, 7, 'Ogaadana in Rasuulkii Eebe idin dhexjoogo, hadduu idinku addeeco waxyaala hadanna aad dhibboonaysaan, wuxuuse Eebe idinla jeclaaday limaan, una qurxiyay quluubtiinna, wuxuuna idinla nacay gaalnimo, faasiqnimo iyo caasinnimo, kuwaasina waa kuwa hanuunsan.'),
(4620, 49, 8, 'Waa fadliga (siinta) Eebe iyo naxriistiisa, Eebana wax walba waa ogyahay, waana falsame.'),
(4621, 49, 9, 'Hadday Laba qolo oo mu\'miniinta ah dagaallamaan heshiisiiya dhexdooda, hadday midu ku xadgudubto tan kale la dirira tan xadgudubtay intay amarka Eebe uga noqoto (xaqa aqoonsato), Hadday u noqotana si caddaalad ah u wanaajiya dhexdooda una garsoora, Eebe wuxuu jecelahay kuwa garsooree.'),
(4622, 49, 10, 'Mu\'miniintu waa uun walaala, ee wanaajiya walaalihiin dhexdooda, Eebana ka dhawrsada, waxaad u dhawdihiin inuu idiin naxariistee.'),
(4623, 49, 11, 'Kuwa xaqa rumeeyow yuuna yasin Ragna Rag (kale) waxay u dhawdahay inuu ka khayr badanyahaye, Haweenna yayna haween yasin waxay u dhawyihiin inay ka khayr badanyihiine, hana ceebeynina Naftiinna, hana isugu dhawaaqina naanays xun, waxaa xun magaca faasiqinimada iimaan kadib, Ciddaan tawbad keeninna waa daalimiin.'),
(4624, 49, 12, 'Kuwa xaqa rumeeyow ka dhawrsada wax badan oo mala ah, maxaa yeelay malaha qaarkiis waa dambi, hana is jaasuusina, qaarkiinna qaar yuusan xamanin, miyuu jecelyahay midkiinna inuu cuno Hilibka walaalkiis oo mayd ah, waad necebtihiin arrintaa, Eebana ka dhawrsada waa tooba aqbale naxariistee.'),
(4625, 49, 13, 'Dadow waxaan idinka abuuray lab iyo dhaddig, waxaana idinka yeellay shucuub iyo qabiilooyin si aad isu aqoonsataan, Ruuxase ugu sharaf badan Eebe agtiisa waa ka idiinku dhawrsasho badan, Eebana wax walba waa ogyahay,'),
(4626, 49, 14, 'Waxay dheheen ree baadiyihii carbeed waan rumaynay (xaqa), waxaad ku dhahdaa maydaan rumaynin, laakiin dhaha waan hoggansannay, Iimaankuna wali Quluubtiinna ma galin, haddaadse adeecdaan Eebe iyo Rasuulkiisa kama nusqaamiyo camalkiinna waxba, Eebana waa dambi dhaafe naxariista.'),
(4627, 49, 15, 'Kuwa mu\'miniinta ah waa kuwa uun rumeeyay Eebe iyo Rasuulkiisa oon shakiyin, kuna Jahada Xoolahooda iyo naftooda jidka Eebe dartiis, kuwaasi waa kuwa runlowga ah.'),
(4628, 49, 16, 'Waxaad dhahdaa ma Eebbaad usheegaysaan diintiinna isagoo og waxa Samaawaadka iyo Dhulkaba ku sugan, Eebana wax walba waa ogyahay.'),
(4629, 49, 17, 'Waxau kugu mannaysan inay Islaameen, waxaad dhahdaa haygu mannaysanina Islaamkiinna Eebaa manna idinku leh inuu idinku hanuuniyay Iimaanka haddaad run sheegeysaan.'),
(4630, 49, 18, 'Eebe waa ogyahay waxa Samaawaadka ku maqan iyo dhulkaba, Eebana waa arkaa waxaad camal falaysaan.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Eebe wuxuu ku dhaartay Quraanka sharafta leh (in dadka la soo bixin).'),
(4632, 50, 2, 'Waxayse La yaabeen inuu u yimid dige ka mid ah oy markaas dheheen gaaladii kan waa wax la yaab leh.'),
(4633, 50, 3, 'Ma markaan dhimanno oon carro noqonno yaa nala soo celin, taas waa soo celin fog.'),
(4634, 50, 4, 'Waan ognahay waxa dhulku ka cunay jidhkooda, agtannadana waxaa yaallaa Kitaab koobay wax walba, oo ilaashan.'),
(4635, 50, 5, 'Waxaybase beeniyeen xaqii markuu u yimid, waxayna ku suganyihiin xaal qasan.'),
(4636, 50, 6, 'Miyayna eegin Samada korkooda ah sidaan u dhisnay una qurxinay ayna duleella u lahayn.'),
(4637, 50, 7, 'Dhulkana waan waasicinnay waxaana ku sugnay Buuro, waxaana ka soo bixinnay dhexdiisa nooc kasta oo quruxsan.'),
(4638, 50, 8, 'Inay aragti iyo waano u noqoto Ruux kasta oo Addoon Eebe oo toobad keen badan ah.'),
(4639, 50, 9, 'Waxaan ka soo dajinnay samada Biyo barakaysan oon ku soo bixinnay Beero iyo Midho la goosta,'),
(4640, 50, 10, 'Iyo timir dheer oo fidsan oo leh fiido wax ka soo dhashaan oo is dul saaran.'),
(4641, 50, 11, 'Si Addoommada Eebe loogu irsaaqo, Biyahaana waxaan ku noolaynay Magaalo dhimatay, saasayna soo bixintu tahay.'),
(4642, 50, 12, 'Kuwa Reer Makaad hortooda waxaa xaqii beeniyay Qoomkii (Nabi) Nuux iyo Rasi dadkeedii iyo thamuud.'),
(4643, 50, 13, 'Iyo Ree Caad iyo Fircoon iyo (Nabi) Luudh walaalihiis (qoomkiisii).'),
(4644, 50, 14, 'Iyo kayntii dadkeedii (Nabi Shucayb qoomkiisii) iyo Qoomkii Tubbac, dhammantood waxay beeniyeen Rasuulladdi, waxaana ku dhacay wixii Eebe ugu goodiyay (ciqaabtii).'),
(4645, 50, 15, 'Ma waxaan ka noognay abuuriddi hore, waxayse shakisan yihiin abuuridda cusub (soo bixinta).'),
(4646, 50, 16, 'Dhab ahaan yaan u abuurray Insaanka (dadka) waana ognahay waxay naftiisu ku waswaasin Annagaana ugsa dhaw xididka dhuunta.'),
(4647, 50, 17, 'Markay kulmi malaa\'igta ilaalinaysa oo Midigta iyo bidixda ka fadhida.'),
(4648, 50, 18, 'Wax kastoo lugu hadlo waxaa la jooga Raqiib iyo Catiid (oo qori).'),
(4649, 50, 19, 'Waxaa u yimid sakaraadkii geerida si dhab ah wanataas geeridaad ka cararaysay.'),
(4650, 50, 20, 'Suurkii (Qiyaamana) waa la afuufi waana Maalin laysu yaboohay (oo sugan).'),
(4651, 50, 21, 'Naf walba way iman iyadoo malag hoggaamin midna ku marag furi.'),
(4652, 50, 22, 'Waxaana loo odhan waad halmaansanaydeen ariintan (Qiyaamada) waxaana kaafaydnay daboolkii araggaaguna Maanta waa xoog badan yahay.'),
(4653, 50, 23, 'Malaggi la xidhiidhayna wuxuu dhihi waa kan waana darbanyahay, (kiilayxilsaaray).'),
(4654, 50, 24, 'Markaasaa lugu dhihi ku tuura Naarta Jhannamo Gaalnimo badane madax adag dhammaantiis.'),
(4655, 50, 25, 'Oo khayrka reebidbadan gardarraloowna ah shakiilowna ah.'),
(4656, 50, 26, 'Ee Eebe miciisa Ilaah kale yeelay, ku tuura caddibaad daran.'),
(4657, 50, 27, 'Saaxiibkiisii (shaydaankiisii) wuxuu dhihi Eeboow anigu ma dhuminnin laakiin isagaa baadi fog ku sugnaa.'),
(4658, 50, 28, 'Eebe wuxuu dhihi ha ku murmina agtayda waan idiin hormariyay gooddigii.'),
(4659, 50, 29, 'Hadalla agtayda laguma baddalo, mana dulmiyo addoomada.'),
(4660, 50, 30, 'Xusuusta Maalinta u dhahayno Naarta Jahannama Ma buuxsantay, oo markaa ay dhihi wax ma la ii siyaadin.'),
(4661, 50, 31, 'Jannadiina loo so dhaweeyo kuwii dhawrsaday iyadoon ka fogayn.'),
(4662, 50, 32, 'Waxaasina waa wixii loo yaboohay Ruux kasta oo tawbadkeen badan oo dhawrid badan (ballaanka).'),
(4663, 50, 33, 'Ruuxii Eebaha Raxmaan ah ka yaaba isagoon arkaynin, oo la yimaada Qalbi toobadkeen badan.'),
(4664, 50, 34, 'Waxaa lugu dhihi ku gala Jannada Nabadgalyo waana Maalintii waariddee.'),
(4665, 50, 35, 'Jannada waxay ka heliwaxay doonaan, agtannadana waxaa ah siyaado.'),
(4666, 50, 36, 'Imisa Qarniyaan halaagnay Reer Makaad ka hor oo kana xoogbadan iyagoo dhulka ku gagaddoomay, meel Eebe looga cararana ma jirto.'),
(4667, 50, 37, 'Waxaasina waa waanada ruuxii qalbi leh ama wax dhagaysan isagoo Qalbigiisu joogo.'),
(4668, 50, 38, 'Dhabahaan annagaa Samaawaadka iyo Dhulka iyo waxa u dhaxeeya ku abuura lix maalmood, wax daal ahna nama taabanin.'),
(4669, 50, 39, 'Ku samir (Nabiyow) waxay sheegayaan, Eebana u tasbiixso adigoo ku mahadin qorrax soo bax ka hor iyo dhicidda ka hor.'),
(4670, 50, 40, 'Habeenkana qaar u tasbiixso iyo sujuudda dabadeed.'),
(4671, 50, 41, 'Dhagayso Maalinta uu ka dhawaaqi mid dhawaaqa meel dhaw.'),
(4672, 50, 42, 'Waa ay maqli qaylada oo dhab ah, waana Maalinta soo bixidda.'),
(4673, 50, 43, 'Annagaa waxna noolaynna waxna dilla, xaggannagaana loo soo ahaan.'),
(4674, 50, 44, 'Maalinta Dhulka ka dillaaci iyagoo dagdagi, taasina waa soo kulmin noo fudud.'),
(4675, 50, 45, 'Annagaa og waxay dhihi, aduguna matihid mid xoogi korkooda, ee ku waani Quraanka Ruuxii Eebe waciidkiisa (goodigiisa) ka yaabi.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Eebe wuxuu ku dhaartay dabaysha (Carrada kicisa).'),
(4677, 51, 2, 'Iyo daruurta xambaarta culayska (Roobka).'),
(4678, 51, 3, 'iyo doonnida sida fudud u socota.'),
(4679, 51, 4, 'Iyo Malaa\'igta amarka qaybisa.'),
(4680, 51, 5, 'Ee dadow waxa laydiinku goodin waa run.'),
(4681, 51, 6, 'Abaalmarintuna waa sugantahay.'),
(4682, 51, 7, 'Samada yuu Eebe ku dhaartay ee quruxda iyo waddooyinka leh.'),
(4683, 51, 8, 'Ee dadow waxaad ku sugantihiin hadal is khilaafsan.'),
(4684, 51, 9, 'Iimaankana waxaa laga iilaa ruux dhumay.'),
(4685, 51, 10, 'Waxaana la nacladay Beenalayaasha.'),
(4686, 51, 11, 'Ee ah kuwa xumaantooda (baadinimadooda) ku dhexhilmaansan.'),
(4687, 51, 12, 'waxay waydiin goorta abaalmarinta (qiyaamada).'),
(4688, 51, 13, 'Waana maalinta naarta lagu gubi.'),
(4689, 51, 14, 'Laguna dhihi dhadhamiya caddibaddii aad dadajisanayseen.'),
(4690, 51, 15, 'Kuwa dhawrsaday waxay ku sugnaan Jannooyin iyo Ilo.'),
(4691, 51, 16, 'Iyagoo qaadan waxa Eebe siiyay, maxaayeelay waxay ahaayeen horay kuwo wanaag fala.'),
(4692, 51, 17, 'Waxayna ahaayeen kuwo wax yar Habeenkii seexda.'),
(4693, 51, 18, 'Waagana way dambi dhaaf waydiisanayeen.'),
(4694, 51, 19, 'Xoolohoodana xaq bay ku leeyihiin kuwa waydiista iyo kadhibaataysan.'),
(4695, 51, 20, 'Dhulkana waxaa ugu sugan calaamooyin kuwa wax yaqiinin.'),
(4696, 51, 21, 'Naftiinna miyaydaan arkaynin (waana qaadasho).'),
(4697, 51, 22, 'Samada yuuna jiraa risqigiinnu iyo waxa laydiin yaboohi.'),
(4698, 51, 23, 'Ebaha Cirka iyo dhulka yaan ku dhaarannaye waxa quraanku sheegi waa suganyahay sida hadalkiinna oo kale.'),
(4699, 51, 24, 'Ma ku soo gaadhay warkii martidii (Nabi) Ibraahiim ee sharafta lahayd.'),
(4700, 51, 25, 'Markay salaameenna, isna uu salaantii ka celiyey yidhi waxaad tihiin kuwo aan la oqoonnin.'),
(4701, 51, 26, 'Markaasu aaday ehelkiisii lana yimid Dibi shilis (una gawracay).'),
(4702, 51, 27, 'Una soo dhaweeyay isagoo ku leh maxaad u cuni waydeen.'),
(4703, 51, 28, 'Wuxuuna naftiisa ka kasay cabsi xaggooda ah, waxayna ku dheheen ha cabsanin, waxayna ugu bishaareeyeen wiil cilmi yeelan,'),
(4704, 51, 29, 'Markaasay soo qaabishay haweenaydiisii iyadoo ku qaylyi wajigana garaaci Ma Habar mandhalays ah (yaa wax dhali).'),
(4705, 51, 30, 'waxayna ku dheheen xaalku waa saas, Eebahaaga falkasan ee waxwalba og yaa yidhi'),
(4706, 51, 31, '(Nabi Ibraahim) wuxuu ku yidhi xaalkiinnu see yahay kuwayohow la soo diray.'),
(4707, 51, 32, 'Waxayna dheheen waxaa lanoo soo diray qoon dambiilayaal ah.'),
(4708, 51, 33, 'Si aan ugu soo dirro korkooda dhagaxyo dhoobo (kulul).'),
(4709, 51, 34, 'Oo Eebe agtiisa loogu calaameeyay kuwa xadgudbay.'),
(4710, 51, 35, 'Markaasaan ka bixinnay kuwii mu\'miniinta ahaa oo ku dhexjiray.'),
(4711, 51, 36, 'kamana aannaan helin waxaan halguri oo muslin ah ahayn.'),
(4712, 51, 37, 'Waxaana kaga tagnay dhexdeeda calaamad (ay ku waana qaatan) kuwa ka cabsan caddibaad daran.'),
(4713, 51, 38, '(Nabi) Muusana calaamo lagu waana qaatu ahaa markaan u dirray Fircoon isagoo wata xujo cad.'),
(4714, 51, 39, 'Wuuna jeedsaday isagoo la jira colkiisii, wuxuuna ku yidhi waa Saaxir ama waa waalanyahay.'),
(4715, 51, 40, 'Markaasaan qabannay (ciqaabnay) isaga iyo Askartiisiiba, waxaana ku tuurray Badda isagoo la dagaalay.'),
(4716, 51, 41, 'Ree Caadna (calaamo lagu waana qaato yaa ku sugan) markaan ku dirray dabayl wax halaagta.'),
(4717, 51, 42, 'Wax kasta oy martana ka dhigi wax tirtiran.'),
(4718, 51, 43, 'Ree Thamuudna (Calaamo lagu waana qaato yaa ku sugan) markii lagu yidhi taaxaysta ilaa waqti.'),
(4719, 51, 44, 'Waxayna iska kibriyeen amarkii Eebe, waxaana qabtay Qaylo daran iyagoo eegi.'),
(4720, 51, 45, 'Mana ayna karin kicid, isumana ayna gargaarin.'),
(4721, 51, 46, '(Nabi) Nuux qoomkiisiina waa horreeyeen, waxayna ahaayeen kuwo faasiqiin ah,'),
(4722, 51, 47, 'Samada annagaa u dhisnay si xoog ah, waana waasicinnay.'),
(4723, 51, 48, 'Dhulkana waan gogollay cid wax gogoshana annagaa u fiican (Eebe).'),
(4724, 51, 49, 'Waxwalbana waxaan ka abuurray laba nooc, si aad ugu waana qaadataan.'),
(4725, 51, 50, 'Ee Eebe u carara (toobad keena), waxaana idiin ahay dige cade (muuqda).'),
(4726, 51, 51, 'Hana yeelina Eebe miciisa ilaah kale, waxaan idiin ahay dige cade;.'),
(4727, 51, 52, 'Xaalku waa saas, Gaaladii ReeMakaad ka horreysayna markastoo Rasuul uyimaado waxay dhihi jireen waa Saaxir ama waa waalanyahay.'),
(4728, 51, 53, 'Miyay isu dardaarmeen arrintaas, waase qoom xadgudbay.'),
(4729, 51, 54, 'Ee Nabiyow iskaga jeedsado xaggooda, ma tihid mid la dagaaliye (Eebe agtiisa).'),
(4730, 51, 55, 'Ee wax waani, waanaduna waxay anfici mu\'minintee.'),
(4731, 51, 56, 'Jinni iyo Insi waxaan Cibaadadayda ahayn uma abuurin.'),
(4732, 51, 57, 'Kamana doonaayo Risqi, iyo inay i quudiyaan.'),
(4733, 51, 58, 'Illeen Eebe umbaa ah arsuqe xoogbadane.'),
(4734, 51, 59, '59 Kuwii dulmiilowga ahaa waxaa u sugnaaday. Qyb (ciqaab ah) oo la mid ah tii saaxiibbadoodii (ka horreeyey) ee ha dadajisanin.'),
(4735, 51, 60, 'Halaag wuxuu usugnaaday kuwii gaaloobay maalintoodii (Qiyaame) ee loogu yaboohay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, '(Eebe wuxuu ku dhaartay) Dhuurisiina.'),
(4737, 52, 2, 'Iyo Kitaabka la dhigay,'),
(4738, 52, 3, 'Oo ku qoran warqado la fidiyay.'),
(4739, 52, 4, 'Iyo Gurigii cammirraa (Beytulmacmuur).'),
(4740, 52, 5, 'IYo saanqaafka la koryeelay (cirka).'),
(4741, 52, 6, 'Iyo Baddii la kululeeyay.'),
(4742, 52, 7, 'Ee caddibaadda Eebe waa wax dhici (sugan).'),
(4743, 52, 8, 'wax celinna ma jiro.'),
(4744, 52, 9, 'Waxay dhici caddibaaddaas Maalinta Samadu wareegi.'),
(4745, 52, 10, 'Buuruhuna socon.'),
(4746, 52, 11, 'Halaagna waxaa iska leh maalintaas kuwa xaqa beeniyay.'),
(4747, 52, 12, 'Ee ah kuwa tiimbashada xumaanta ku dhex ciyaari.'),
(4748, 52, 13, 'Maalinta Naarta xoog loogu tuurina (waxa lagu dhihi).'),
(4749, 52, 14, 'Waatan Naartii aad beenin jirteen.'),
(4750, 52, 15, 'Ma sixirbaa, mase waxbaydaan arkayn.'),
(4751, 52, 16, 'Gala, ama samra ama ha samrine, waa isugu kiin mid, waxaa uun laydinka abaalmarin waxaad camal falayseen.'),
(4752, 52, 17, 'Kuwa Eebe ka dhawrsaday waxay gali Jannooyin iyo Nicmo.'),
(4753, 52, 18, 'Iyaga oo ku raaxaysan waxa Eebe siiyay, Eebohoodna wuxuu ka dhawray caddibaadda Jaxiiimo.'),
(4754, 52, 19, '(Waxaana lagu dhihi) cuna oo cabba idinkoo shifaysan camalkiinnii dartiis.'),
(4755, 52, 20, 'Idinkoo ku dangiiga Sariiro la safay, waxaanna (Eebe) u guurin Haween Janno (Xuuralcayn).'),
(4756, 52, 21, 'Kuwa xaqa rumeeyay ee carurtooduna Iimaanka ka raacday waxaannu haleeshiin carruurtooda (Darajada iyo naxariista), camalkoodana waxba kama nusqaaminayno, Ruuxwalbana wuxuu kasbaday yuu u rahmanaan «u xidhnaani».'),
(4757, 52, 22, 'waxaannu u badinnay faakiho iyo Hilib oo ah waxay jecelyihiin.'),
(4758, 52, 23, 'Waxayna isku siin Jannada dhexdeeda (iyagoo sheekeysan) Weel (Macaan) kabuuxo, oon hadal xun iyo dambi midna lahayn.'),
(4759, 52, 24, 'Waxaana u adeegi wiilal aad moodo Jawhar la dhawray.'),
(4760, 52, 25, 'Qaarkood (ehlu-Jannaha) yaa qaarka kale qaabila iyagoo wax is waydiin.'),
(4761, 52, 26, 'Waxayna isu dheheen waxaan ahayn horay Adduunkii kuwo ehelkoodii dhexdiisa ku cabsada.'),
(4762, 52, 27, 'Eebaase nagu mannaystay (dambi dhaaf), wuxuuna naga dhawray caddibaadda Samuumka (Jahannama).'),
(4763, 52, 28, 'Waxaan ahayn horay kuwo barya Eebe, Ilaahayna waa Baarrii naxariista.'),
(4764, 52, 29, 'Ee waani (Nabiyow dadka), nicmada darteed ma tihid wax sheege iyo waalane midnee.'),
(4765, 52, 30, 'Mise waxay dhihi waa gabayaa ee aan la sugno Geerida.'),
(4766, 52, 31, 'Waxaad dhahdaa suga anna waan idin la sugiye.'),
(4767, 52, 32, 'Mise caqligooda (xun) yaa saas faray, ma aha ee waa Qoom xadgudbay.'),
(4768, 52, 33, 'Mise waxaydhihi isagaa abuurtay (Quraanka) waxba ma ayan rumeynin.'),
(4769, 52, 34, 'Ha la yimaadeen hadal la mid ah hadday Run sheegi.'),
(4770, 52, 35, 'Mise waxaa la abuuray abuure la\'aan, mise iyagaa abuuray (naftooda).'),
(4771, 52, 36, 'Mise iyagaa Samaawaadka iyo Dhulka abuuray, saas ma aha ee wax ma ayan yaqiininin.'),
(4772, 52, 37, 'Mise agtoodaa khayraadka Eebe yaallaa, Mise iyagaa xisaabin dadka.'),
(4773, 52, 38, 'Mise waxay leeyihiin Sallaan ay wax ku Dhageeystaan, ha la yimaado kooda wax dhaagaystay xujo cad.'),
(4774, 52, 39, 'Mise Eebaa Gabdho u sugnaaday idinkana wiilal.'),
(4775, 52, 40, 'Mise waxaad warsan Nabiyow ujuuro, oo markaas ku cuslaatay.'),
(4776, 52, 41, 'Mise waxa maqanbaa agtooda ah oo ay qoraan.'),
(4777, 52, 42, 'Mise Dhagar Bay Rabaan, kuwa gaaloobay iyagaa dhakran.'),
(4778, 52, 43, 'Mise Ilaah Eebe ka soo hadlay leeyhiin, Eebaa ka fog waxay la wadaajiyeen «Ilaah-nimada».'),
(4779, 52, 44, 'Hadday arkaan goosin ka mid ah Samada oo soo dhacaysa waxay odhan waa Daruuro is dulsaaran (madax adayg dartiis).'),
(4780, 52, 45, 'Ee iskaga tag Nabiyow intay kala kulmaan maalinta dhexdeeda lagu halaagi doono.'),
(4781, 52, 46, 'Maalinta ayan dhagartoodu wax u taraynin wax u gargaarana uusan jirin.'),
(4782, 52, 47, 'Kuwa xad gudbay waxaa u sugnaaday caddibaad ka sokaysa (Qiyaamada), laakiin badankoodu ma oga.'),
(4783, 52, 48, 'Ee ku samir xukunka Eebahaa (Nabiyow) annagaa ku ilaalinaynee, kuna; tasbiixso oo ku mahadi Eebahaa markaad kici.'),
(4784, 52, 49, 'Habeenka qaarkiisna tuko, iyo marka xiddiguhu jeedsadaan.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Wuxuu ku dhaartay (Eebe) Xiddiggu markuu hoobto.'),
(4786, 53, 2, 'Ee saaxiibkiin (Nabigu) ma dhumin, xaqana kama leexan.'),
(4787, 53, 3, 'Kumana hadlo hawadiisa.'),
(4788, 53, 4, 'quraankuna waxaan waxyi Eebe ahayn ma aha.'),
(4789, 53, 5, 'Waxaana wax baray (oo xagga Eebe ka soo gaadhsiiyay) ku xoog wayn, (Malaku Jibriil).'),
(4790, 53, 6, 'Ee caafimaad iyo quwaba leh, abuuriddiisuna egtahay.'),
(4791, 53, 7, 'Wuxuu joogay jihadii sare (Jibriil).'),
(4792, 53, 8, 'Kaddibna u soo dhawaaday kuna soo hoobtay (Nabiga).'),
(4793, 53, 9, 'Intuu uga jirsado Qaanso (xadhiggeed) ama ka dhaw.'),
(4794, 53, 10, 'Wuxuuna Eebe waxyooday Addoonkiisa (Nabiga) wuxuu u waxyooday.'),
(4795, 53, 11, 'Qalbiguna Ma beeniyo wuxuu arkay (Nabigu).'),
(4796, 53, 12, 'Ee ma idinkaa ka murmi «kana doodi» wuxuu arkay.'),
(4797, 53, 13, 'Dhabnimuu Nabigu u arkay Jibrill mar kale.'),
(4798, 53, 14, 'Sidratul-Muntaha agteeda.'),
(4799, 53, 15, 'Agteedana waxaa ah Jannatul-ma\'waa.'),
(4800, 53, 16, '(Wuxuu arkay) waqtiguu daboolayay Sidrada wax daboola.'),
(4801, 53, 17, 'Arragguna kama iilan wuxuu arkay (Nabigu) mana xadgudbin.'),
(4802, 53, 18, 'Dhabnimo yuuna u arkay (Nabigu) Aayado Eebe oo waawayn.'),
(4803, 53, 19, 'Ka warrama (Sanamyada) Laata iyo Cuza.'),
(4804, 53, 20, 'Iya Manaatadii saddexaad ee kale.'),
(4805, 53, 21, 'Idinku ma waxaad leedihiin Lab Eebana Dhaddigga.'),
(4806, 53, 22, 'Taasi waa qayb dulan ah.'),
(4807, 53, 23, 'Sanammaduna magacyo aad magacowdeen idinka iyo Aabayawgiin mooyeen wax kale ma aha, wax xuja ahna Eebe uma soo dajinin, waxaan mala ahaynna ma raacayaan iyo waxa naftoodu doonto, xagga Eebana waxaa uga yimid hanuun.'),
(4808, 53, 24, 'Mise dadka waxaa u sugnaaday waxay yididiishaan.'),
(4809, 53, 25, 'Aakhiro iyo Adduunba Eebaa iska leh.'),
(4810, 53, 26, 'Badanaa Malag Samada ku sugan oon shafeecadiisu waxba anfacaynin in Eebe idmo mooyee, Cidduu la doono oo ka raalli noqdo.'),
(4811, 53, 27, 'Kuwa aan rumaynin Aakhiro waxay Malaa\'igta ku magacaabi magac dhaddig.'),
(4812, 53, 28, 'Wax cilmi ahna uma laha, ee waxay raaci mala uun, malana xaqa wax kama taro.'),
(4813, 53, 29, 'Ee iskaga jeedso (nabiyow) Ruux ka tagay xukayga (Quraanka) oon doonin waxaan nolosha Adduunka ahayn.'),
(4814, 53, 30, 'Kaasina waa garaadkooda aqooneed intuu gaadhay, Eebana waa ogyahay cidda ka dhuntay jidkiisa, waana ogyahay cidda hanuunsan.'),
(4815, 53, 31, 'Samaawaadka iyo Dhulka waxa ku sugan Eebaa iska leh, si uu uga abaalmariyo kuwa xumaanta falay camalkoodii, ugana abaalmariyo kuwii wanaagga falay wanaag.'),
(4816, 53, 32, 'Waana kuwa ka fogaada dambiga waawayn iyo xumaanta, wax yaryar mooyeen, Eebana waa waasac dambidhaafkiisu, wuuna idin ogyahay markuu idinka abuuray Dhulka iyo markaad ku asturantihiin uurka hooyadiin, ee Naftiinna ha ammaanina Eebaa og cidda dhawrsashada badane.'),
(4817, 53, 33, 'Ka warrama midka xaqa ka jeedsaday.'),
(4818, 53, 34, 'Oo wax ar bixiyay inta kalana reebtay.'),
(4819, 53, 35, 'Ma agtiisaa cilmiga maqan yaallaa oo wuu arkaa.'),
(4820, 53, 36, 'Miyaan looga warramin (dadka) wixii ku yiillay Kitaabkii (Nabi Muuse).'),
(4821, 53, 37, 'Iyo kii (Nabi Ibraahimkii) ee oofiyay (wixii lafaray).'),
(4822, 53, 38, 'Inaan nafna dambi naf kale qaadaynin.'),
(4823, 53, 39, 'Ruuxna wuxuusan camalfalin heleeynin.'),
(4824, 53, 40, 'Camalkiisana la arki doono.'),
(4825, 53, 41, 'Ka dibna looga abaalmarin doono si buuxda.'),
(4826, 53, 42, 'Eebaa xaggiisana loo ahaan.'),
(4827, 53, 43, 'Eebe waakan wax ka qosliya kana oohiya.'),
(4828, 53, 44, 'Isagaana wax dila waxna nooleeya.'),
(4829, 53, 45, 'Isagaana abuuray labada nooc ee ah lab iyo dhaddigba.'),
(4830, 53, 46, 'Kana ahaysiiyay dhibic soo baxday.'),
(4831, 53, 47, 'Isagaana ahaysiin soo celinta kale.'),
(4832, 53, 48, 'Eebe isagaa wax Hodmiya isagaana wax faqiiriya.'),
(4833, 53, 49, 'Waana Eebaha xiddigga Shicra.'),
(4834, 53, 50, 'Eebe isagaa halaagay Caadkii hore.'),
(4835, 53, 51, 'Iyo Thamuudba waxna kama reebin.'),
(4836, 53, 52, '(Nabi Nuux) Qoomkiisiina horuu (uhalaagay), waxayna ahaayeen kuwa dulmi badan oo xadgudub badan.'),
(4837, 53, 53, 'Qoomkii (Nabi Luudhna) wuu riday (Eebe).'),
(4838, 53, 54, 'Wuxuuna ka daboolay wuxuu ku daboolay (oo caddibaad ah).'),
(4839, 53, 55, 'Teebaadse nicmooyinka Eebe shakinaysaan (kana doodaysaan) dadow.'),
(4840, 53, 56, 'Kan (Nabiga iyo Quraankuba) waa dige ka mid ah digayaashii hore.'),
(4841, 53, 57, 'Wayna dhawaatay Saacaddii Qiyaame.'),
(4842, 53, 58, 'Wax Eebe ka soo hadhay oo ogna ma jiro (markay dhici).'),
(4843, 53, 59, 'Ma quraankanaad la yaabantihiin.'),
(4844, 53, 60, 'Ood qoslaysaan (idinkoo beenin) oydaanna ooyeynin (cabsi darteed).'),
(4845, 53, 61, 'Idinkoo waliba hilmaansan (islana wayn).'),
(4846, 53, 62, 'Ee dadow Eebe u sujuuda caabudana.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Waxaa dhawaatay Saacaddii (Qiyaame), dayixiina wuu dillaacay.'),
(4848, 54, 2, 'Hadday Gaaladu arkaan Aayad way ka jeedsadaan waxayna dhahaan waa Sixir daran.'),
(4849, 54, 3, 'Way beeniyeen (Nabiga iyo xaqa) waxayna raaceen Hawadooda, amar kastana wuxuu leeyahay meel uu ku sugnaado.'),
(4850, 54, 4, 'Waxaana ugu yimid dhab ahaan warar ku dheehan waano.'),
(4851, 54, 5, 'Waana Xigmad xeel dheer, maxaysa tari udigidi.'),
(4852, 54, 6, 'Ee iskaga jeedso (sugna) Maalinta mid dhawaaqaa uu ugu yeedhi wax daran.'),
(4853, 54, 7, 'Aragooduna wuu dullaysnaan waxayna ka soo bixi Qubuurahooda iyagood moodid Ayax faafay.'),
(4854, 54, 8, 'Iyagoo u dagdagi dhawaaqaha xaggiisa, waxayna dhihi Gaaladu kani waa Maalin daran.'),
(4855, 54, 9, 'Waxaa beeniyay (xaqa) iyaga hortood (Nabi) Nuux Qoomkiisii, waxay beeniyeen Addonkanagii, waxayna dheheen wuu waalanyahay, waana la guulguulay, «oo la canaantay».'),
(4856, 54, 10, 'Eebuuna tuugay inuu tabaryaryahay oo u gargaaro.'),
(4857, 54, 11, 'Markaasaan ku furay Albaabbada Samada (Cirka) Biyo badanna ka keenay.'),
(4858, 54, 12, 'Dhulkana Eebaa ka dillaaciyay ilo, wayna kulmeen biyihii amar la qadaray dartiis.'),
(4859, 54, 13, 'Waxaanna ku xambaaray Nabi Nuux (Doon) Looxyo iyo Musbaarro leh.'),
(4860, 54, 14, 'Kuna socota illalintanada si loo abaalmariyo cidda Gaalowday.'),
(4861, 54, 15, 'Waxaan uga tagnay (yuu Eebe yidhi) Calaamad (lagu wantoobo) ee ma jirtaa cid wacdoomi (oo wax xusuusan).'),
(4862, 54, 16, 'Seese yahay Caddib Eebe iyo digiddiisu.'),
(4863, 54, 17, 'Waan fududaynnay Quraanka xusuus darteed ee ma jirtaa cid wacdoomi.'),
(4864, 54, 18, 'Caadna way beenisay (xaqii), seese noqotay caddibaaddii Eebe iyo u digitiisii.'),
(4865, 54, 19, 'Waxaan ku dirray korkooda dabayl qabaw daran Maalin baasaysan dhexdeed oo Joogta ah.'),
(4866, 54, 20, 'Oo dadka siibaysa sida Timir gunteedii la rujiyay.'),
(4867, 54, 21, 'Seese ahaa caddibaaddii Eebe iyo u digiddiisii.'),
(4868, 54, 22, 'Waxaan ufududaynay Quraanka xusuus darteed ee ma jirtaa cid ku wacdoomi.'),
(4869, 54, 23, 'Waxaa beenisay (xaqii) loogu digay Thamuud.'),
(4870, 54, 24, 'Waxayna dheheen ma Ruux dad ah oo naga mid ah yaan raacaynaa, markaas waxaan ku sugannahay baadi iyo dhibaato.'),
(4871, 54, 25, 'Ma isagoo na dhexjoogaa waxyi lagu soo dajiyay, saas ma aha ee waa beenbadane kibirlow ah.'),
(4872, 54, 26, 'Way ogaan doonaan barri cidda beenbadane kibirlow ah.'),
(4873, 54, 27, 'Hashii Annagaa soo bixinnay Jirrabkooda dartiis, Ee sug kuna samir.'),
(4874, 54, 28, 'Una warran in biyuhu yihiin qayb (iyaga iyo Hasha), cidbana maalin gaara leedahay.'),
(4875, 54, 29, 'Waxay u dhawaaqeen saaxiibkood hashiina wuu dilay.'),
(4876, 54, 30, 'Seese ahaa caddibaaddii Eebe iyo u digiddiisii.'),
(4877, 54, 31, 'Dhawaaq kaliyaan ku siidaynay, waxayna la mid noqdeen jabad burburay.'),
(4878, 54, 32, 'Quraankana waan u fududaynay xusuus darteed ee ma jirtaa cid ku wacdoomi.'),
(4879, 54, 33, 'Way beeniyeen Qoomkii (NabiLuudh) u digiddii.'),
(4880, 54, 34, 'Waxaana ku dirray korkooda Dabayl Dhagaxyo wadaa, waxaanse ka nabadgalinnay (Luudh) ehelkiisii (rumeeyay) goor Aroor ah.'),
(4881, 54, 35, 'Nicmad xaggannaga ka timid darteed saasaana u abaalmarinnaa ciddii mahadisa (Eebe).'),
(4882, 54, 36, 'Nabi-Luudh wuxuu uga digay qoomkiisii qabashadannada (daran), wayse shakiyeen digiddaas.'),
(4883, 54, 37, 'Waxayna dooneen martidiisii (inay xumeeyaan) markaasaan indhaha tirray, waxaana lagu yidhi dhadhamiya caddibaadda Eebe iyo digiddiisa.'),
(4884, 54, 38, 'Waxaa ku waabariistay caddibaad sugan.'),
(4885, 54, 39, 'Waxaana lagu yidhi dhadhamiya caddibaadda Eebe iyo digiddiisa.'),
(4886, 54, 40, 'Waan fududaynay Quraanka xusuus darteed ee ma jirtaa cid wacdoomi.'),
(4887, 54, 41, 'Fircoon iyo ehelkiisii waxaa uyimid digid.'),
(4888, 54, 42, 'Wayna beeniyeen Aayaadkannagii dhammaanteed, markaasaan u qabannay si xoog iyo awood leh.'),
(4889, 54, 43, 'Ma Gaaladinnan baa ka khayr badan kuwaas mase dambi la\'aanbaa idiin ku sugan Kutubta.'),
(4890, 54, 44, 'Mase waxay dhihi koox guulaysan yaanahay.'),
(4891, 54, 45, 'Waa la jabin kooxda, Dabadayna jeedin.'),
(4892, 54, 46, 'Saacadda (Qiyaame) yaa ballan u ah, Saacadduna iyadaa daran oo khadhaadh.'),
(4893, 54, 47, 'Kuwa dambiilayaasha ah baadi iyo fogaansho yay ku suganyihiin.'),
(4894, 54, 48, '(Waxaana lagu dhihi) Maalinta Naarta WajiWaji loogu ridi dhadhamiya taabashada Saqara.'),
(4895, 54, 49, 'Wax kasta waxaan ku abuurray qadar.'),
(4896, 54, 50, 'Amarkannaguna ma aha mid mooyeen sida Ilbidhiqsi.'),
(4897, 54, 51, 'Dhab ahaan baan u halaagnay kuwa idin la mid ah, ee wax waantoobi ma jiraa.'),
(4898, 54, 52, 'Wax kastoo dadku falo wuxuu ku suganyahay Kutubta.'),
(4899, 54, 53, 'Wax kastoo yar ama waynna waa qoranyahay.'),
(4900, 54, 54, 'Kuwa dhawrsadana waxay gali Jannooyin iyo wabiyaal.'),
(4901, 54, 55, 'Meel la fadhiisto oo xaq ah oo Eebaha awoodda leh agtiisa ah (yaa u sugnaatay).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, '(Eebaha) Raxmaan ah.'),
(4903, 55, 2, 'Wuxuu baray (Nabiga iyo dadka kalaba) Quraanka.'),
(4904, 55, 3, 'Dadkana wuu abuuray.'),
(4905, 55, 4, 'Wuxuuna baray dadka (Hadalka).'),
(4906, 55, 5, 'Qorraxda iyo dayaxuna waxay ku socdaan xisaab.'),
(4907, 55, 6, 'Xiddigga iyo Geedkuba way Sujuudaan (Eebe Dartiis).'),
(4908, 55, 7, 'Samadana Eebaa koryeelay, Garsoorna wuu dajiyay.'),
(4909, 55, 8, 'Si aydaan uga gudbin Caddaaladda.'),
(4910, 55, 9, 'Miisaanka ku hagaajiya Caddaalad, hana nuqaamininna.'),
(4911, 55, 10, 'Dhulkana Eebe wuxuu u dajiyay khalqiga.'),
(4912, 55, 11, 'Dhexdiisa waxaa ah Faakihad (khudaar) iyo Timir Fiido leh.'),
(4913, 55, 12, 'Xubuubna (midho) ball leh iyo udgoona wuu abuuray.'),
(4914, 55, 13, 'Nicmooyinka Eebe teebaad beeninaysaan (Insi iyo Jinniyow).'),
(4915, 55, 14, 'Dadka Eebaa ka abuuray dhoobo Jilay oo kale ah.'),
(4916, 55, 15, 'Jaankana wuxuu ka abuuray ololka Naarta.'),
(4917, 55, 16, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4918, 55, 17, 'Ilaaheen waa Eebaha Labada Qorrax ka soo bax iyo Labada Qorrax u dhac.'),
(4919, 55, 18, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4920, 55, 19, 'Eebe waan kan Labada Badood dariseeyey iyagoo kulmi.'),
(4921, 55, 20, 'Dhexdoodana yeelay soohdin (kala reebta) iskumana xadgudbaan.'),
(4922, 55, 21, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4923, 55, 22, 'Waxaa lagala baxaa Baddaas Lu\'Lu\'iyo Murjaan (Jawhar).'),
(4924, 55, 23, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4925, 55, 24, 'Eebe waxaa u sugnaaday Doomaha socda Badda oo Buuro la mid ah.'),
(4926, 55, 25, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4927, 55, 26, 'Dhammaan waxa (Dhulka) korkiisa ah wuu tagi.'),
(4928, 55, 27, 'Waxaase hadhi Eebaha wayn ee sharafta leh.'),
(4929, 55, 28, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4930, 55, 29, 'Waxaa Eebe wax waydiista Samada iyo Dhulka waxa ku sugan, goorwalbana wuxuu maamuli arrin.'),
(4931, 55, 30, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4932, 55, 31, 'Waana idin xisaabin si dhab ah Insi iyo Jinniyow.'),
(4933, 55, 32, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4934, 55, 33, 'Jinni iyo Insiyow haddaad kartaan inaad ka baxdaan Samada Hajooyinkooda iyo Dhulka ka baxa, kamana bixikartaan xujo la\'aan.'),
(4935, 55, 34, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4936, 55, 35, 'Eebe (hadduu doono) wuxuu idinku diri Olol Naar ah, iyo Maar (la dhalaaliyay) umana gargaarsanaysaan.'),
(4937, 55, 36, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4938, 55, 37, 'Markay Samadu dillaacdo oy noqoto ubax guduudan oo subag la mid ah (dhalaasho) markaas dadkaa la abaalmarin).'),
(4939, 55, 38, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4940, 55, 39, 'Maalintaas Ruux la warsan dambigiisa Insi iyo Jinniba ma jiro.'),
(4941, 55, 40, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4942, 55, 41, 'Dambiilayaasha waxaa lagu aqoonsan astaamahooda, waxaana la qaban Foodda iyo gommadaha (Naartaana lagu tuuri).'),
(4943, 55, 42, 'Nimcooyinka Eebe teebaad beeninaysaan,'),
(4944, 55, 43, 'Tani waa Jahannamadii ay beeniyeen Dambiilayaashu (yaa lagu dhihi).'),
(4945, 55, 44, 'Waxay ku meereysan Jahannamo iyo biyo kulayl daran dhexdooda.'),
(4946, 55, 45, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4947, 55, 46, 'Ruuxii ka yaaba Eebe hor istaaggiisa wuxuu mudan Laba Janno.'),
(4948, 55, 47, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4949, 55, 48, 'Jannooyinkaas oo midabyo leh (iyo Laamo).'),
(4950, 55, 49, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4951, 55, 50, 'Waxaa laga helaa ilo socda.'),
(4952, 55, 51, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4953, 55, 52, 'Waxaa laga helaa nooc kasta oo Faakiha ah (Khudaar ah).'),
(4954, 55, 53, 'Nimcooyinka Eebe teebaad beeninaysaan.'),
(4955, 55, 54, 'Waxay ku dangiigsan (ehlu Jannuhu) Gogol ay tahay hoosteedo istabraq (xariir) Midhaha Jannooyinku waa u dhawyihiin.'),
(4956, 55, 55, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4957, 55, 56, 'Jannooyinka iyo Firaashka waxaa ku sugan Haween Indha waawayn oo dhawrsoon oosan taabanin horay Insi iyo Jinni midna.'),
(4958, 55, 57, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4959, 55, 58, 'Waxaad moodaa Haweenkaas Yaaquut iyo Murjaan (Jawhar midab leh).'),
(4960, 55, 59, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4961, 55, 60, 'Wanaag abaalkiisu soo wanaag ma aha.'),
(4962, 55, 61, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4963, 55, 62, 'Waxay heli Labadaas Janno ka sokow kuwa kale.'),
(4964, 55, 63, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4965, 55, 64, 'Waana Laba Jannoo madow (cagaaran).'),
(4966, 55, 65, 'Nicmooyinka Eebe teebad beeninaysaan.'),
(4967, 55, 66, 'Waxaa dhexdeeda ah Laba ilood oo burqan.'),
(4968, 55, 67, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4969, 55, 68, 'Dhexdooda waxaa ah Faakihad (khudrad) iyo Timir iyo Rummaan.'),
(4970, 55, 69, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4971, 55, 70, 'Dhexdooda waxaa ah Haween khayr leh oo Quruxsan.'),
(4972, 55, 71, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4973, 55, 72, 'Waa Haween Indhacad oo Gurigooda jooga.'),
(4974, 55, 73, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4975, 55, 74, 'Waa Haween uusan horay u taabanin Insi iyo Jinni midna.'),
(4976, 55, 75, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4977, 55, 76, 'Waxayna ehlu-Jannuhu ku dangiigsan Barkimo cagaaran iyo Gogol Quruxsan,'),
(4978, 55, 77, 'Nicmooyinka Eebe teebaad beeninaysaan.'),
(4979, 55, 78, 'Waxaa barakadiisu badnaatay Magaca Eebaha wayn ee shrafta leh.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Markay Qiyaamadu dhacdo.'),
(4981, 56, 2, 'Dhiciddaas wax beenin ma jiro (markay arkaan).'),
(4982, 56, 3, 'Waana (mid) hoos u dhiga kor u dhiga ah.'),
(4983, 56, 4, 'Marka la gilgilo Dhulka gilgilid.'),
(4984, 56, 5, 'Buurahana la riqdo.'),
(4985, 56, 6, 'Oy noqoto boodh Firdhisan.'),
(4986, 56, 7, 'Ood ahaataan Saddex nooc, (markaasaa dadka la soo bixin).'),
(4987, 56, 8, 'Kuwa midigta mari (waa Ehlu-Jannehee), ma taqaan kuwa midigta mari.'),
(4988, 56, 9, 'Iyo kuwa bidixda mari (waa ehluNaarkee) ma taqaan kuwa Bidixda mari.'),
(4989, 56, 10, 'Iyo kuwii aad u hormaray.'),
(4990, 56, 11, 'Waa kuwa Eebe dhaweeyay.'),
(4991, 56, 12, 'Waxayna gali Jannooyin Nicma leh.'),
(4992, 56, 13, 'Waana Koox ka mid ah kuwii hore.'),
(4993, 56, 14, 'Iyo wax yar oo ka mid ah kuwii dambeeyay.'),
(4994, 56, 15, 'Sariiro Dahab laga sameeyay yayna ku sugnaan.'),
(4995, 56, 16, 'Oy kuna dangiigsan korkeeda iyagoo is qaabili.'),
(4996, 56, 17, 'Waxaa la dhex wareegi wiilal waari.'),
(4997, 56, 18, 'Koobab iyo kildhiyo iyo weel uu ka buuxo macaan socda.'),
(4998, 56, 19, 'Madax xanuun iyo caqlitagid midna ma leh.'),
(4999, 56, 20, 'Faakihaday «khudaartay» doonaanna way heli.'),
(5000, 56, 21, 'Iyo Hilib Shimbir oo kay rabaan ah.'),
(5001, 56, 22, 'Iyo Haween Janno oo Indha cadcad.'),
(5002, 56, 23, 'Ood mooddo Jawhar la ilaaliyay.'),
(5003, 56, 24, 'Waana abaalmarin waxay Camalfali jireen (oo wanaag ahaa).'),
(5004, 56, 25, 'Kumana maqlaan Jannada hadal micno darra iyo dambi midna.'),
(5005, 56, 26, 'Waxayse ku Maqli Salaan (dhexdooda ah).'),
(5006, 56, 27, 'Kuwa Midigta mari, Ma taqaan kuwa midigta.'),
(5007, 56, 28, 'Waxay heli Geedka Sidriga ah oon Qodax lahayn.'),
(5008, 56, 29, 'Iyo Geedka Dhalxiga ah oo buuxa.'),
(5009, 56, 30, 'Iyo Hoos joogta ah.'),
(5010, 56, 31, 'Iyo Biyo socda (goor walba).'),
(5011, 56, 32, 'Iyo Faakiha badan.'),
(5012, 56, 33, 'Oon la goynaynin lana reebaynin.'),
(5013, 56, 34, 'Iyo Gogol la koryeelay.'),
(5014, 56, 35, 'Annagaa ahaysiin (Da\'yarayn Haweenka Jannada).'),
(5015, 56, 36, 'Kana dhigi kuwa aan la taaban.'),
(5016, 56, 37, 'Oy is jecelyihiin (Raggooda) simanna.'),
(5017, 56, 38, 'Liwa Midigta yaa muta (arrintaas).'),
(5018, 56, 39, 'Waana Koox kuwii hore ka mid ah.'),
(5019, 56, 40, 'Iyo Koox kuwa dambe ka mid ah.'),
(5020, 56, 41, 'Kuwa Bidixda mari, ma taqaan kuwa Bidixda.'),
(5021, 56, 42, 'Waxay ku sugnaan Naar daran iyo Biyo kulayl badan.'),
(5022, 56, 43, 'Iyo hoos madow oo wax guba.'),
(5023, 56, 44, 'Aan qaboobayn aragna fiicnayn.'),
(5024, 56, 45, 'Waxay ahaayeen arrintaas ka hor kuwo ku raaxaysta (Xumaanta).'),
(5025, 56, 46, 'Waxayna Daa\'imi jireen dambi wayn.'),
(5026, 56, 47, 'Waxayna dhihi jireen ma markaan dhimanno oon Lafo iyo Carro noqonnaa lana soo bixin.'),
(5027, 56, 48, 'Ama Aabayaalkannagii horeeyay.'),
(5028, 56, 49, 'Waxaad dhahdaa kuwii horeeyay iyo kuwii dambeeyayba.'),
(5029, 56, 50, 'Waxaa loo soo kulmin Maalin la yaqaan (Qiyaamo).'),
(5030, 56, 51, 'Markaas Baadiyaalyahow Beeniyayaasha ah.'),
(5031, 56, 52, 'Waxaad wax ka cunaysaan Geed xun.'),
(5032, 56, 53, 'Aad kana buuxinaysaan Caloosha.'),
(5033, 56, 54, 'Waxaadna ku cabbaysaan Biyo kulayl badan.'),
(5034, 56, 55, 'Waxaadna u cabbaysaan sida Geel aad u ooman.'),
(5035, 56, 56, 'Kaasina waa waxa lagu soori (Gaalada) Maalinta abaalmarinta (Qiyamada).'),
(5036, 56, 57, '(Eebe wuxuu yidhi) Annagaa idin abuurraye maxaad la rumaynwaydeen (soo bixinta).'),
(5037, 56, 58, 'Ka warrama Dhibicda aad tuuraysan «Manida».'),
(5038, 56, 59, 'Ma idinkaa abuura mase Annagaa abuurra,'),
(5039, 56, 60, 'Annaaa idinku eheynay Geerida, Ruux naga fakanna ma jiro.'),
(5040, 56, 61, 'Inaan idinku baddallo kuwo idin la mid ah oon idinka ahaysiinno waxaydaan aqoon.'),
(5041, 56, 62, 'Dhabahaanbaad u ogtihiin abuuriddii hore, maad wax xusuusataan.'),
(5042, 56, 63, 'Ka warrama waxaad beereysaan.'),
(5043, 56, 64, 'Ma idinkaa soo dhaliya mase Annagaa soo dhalinna.'),
(5044, 56, 65, 'Haddaan doonno waxaan ka dhigaynaa burbur (aan wax tarin) markaasaad yaabaysaan (murugoonaysaan).'),
(5045, 56, 66, '(Idinkoo dhihi) Waa nala khasaariyay.'),
(5046, 56, 67, 'Mase waa nala hoojiyay.'),
(5047, 56, 68, 'Ka warrama Biyahaad cabbaysaan.'),
(5048, 56, 69, 'Ma idinkaa ka soo dajiyay Daruurta mase annakaa soo dajinnay.'),
(5049, 56, 70, 'Haddaan doonno waxaan ka yeelaynaa khadhaadh (aan la hollin karin) ee maad ku mahadisaan (Eebe).'),
(5050, 56, 71, 'Ka warrama Dabkaad Madagta ka shiddaan.'),
(5051, 56, 72, 'Ma idinkaa ahaysiiyay Geedkeeda mase annagaa ahaysiinnay.'),
(5052, 56, 73, 'Annagaa ka dhignay Dabka waano iyo nacfiga kuwa Safarka ah (iyo cidlada).'),
(5053, 56, 74, 'Ee waynee oo u Tasbiixso Magaca Eebaha wayn.'),
(5054, 56, 75, '(Eebe wuxuu yidhi) Waxaan ku dhaartay halkay Xiddiguhu ku dhacaan (ku qarsoomaan).'),
(5055, 56, 76, 'Waana dhaar wayn haddaad ogtihiine.'),
(5056, 56, 77, 'Quraanku waa shay sharaf leh.'),
(5057, 56, 78, 'Wuxuuna ku suganyahay Kitaab la dhawray.'),
(5058, 56, 79, 'Mana taabto Ruuxan Daahir ahayn.'),
(5059, 56, 80, 'Waxaana soo dajiyay Eebaha Caalamka.'),
(5060, 56, 81, 'Ee ma Quraankan yaad beeninaysaan.'),
(5061, 56, 82, 'Ood ka yeeleysaan (mahaddii) Riqigiinna beenin (Xaqa).'),
(5062, 56, 83, 'Maxaydaan markay Naftu dhuunta gaadho.'),
(5063, 56, 84, 'Idinkuna aad fiirinaysaan (Ruuxa dhiman).'),
(5064, 56, 85, 'Annaguna aan idiinka dhawnahay, Laakiinse aydaan arkaynin.'),
(5065, 56, 86, 'Haddaad tihiin kuwa aan la soo bixinaynin.'),
(5066, 56, 87, 'Maad Nafta ku celisaan, haddaad Run sheegaysaan.'),
(5067, 56, 88, 'Hadduu ka mid yahay (Ruuxa dhiman) kuwa Eebe dhaweeyay.'),
(5068, 56, 89, 'Wuxuu heli naxariis iyo farax iyoJannada Naciima.'),
(5069, 56, 90, 'Hadduu yahay kuwa Midigta marina.'),
(5070, 56, 91, 'Nabadgalyaa u sugnaatay (iyo wanaag).'),
(5071, 56, 92, 'Hadduu yahay kuwa xaqa beeniyay ee dhumayna.'),
(5072, 56, 93, 'Wuxuu heli martiqaad iyo kulaylbadan.'),
(5073, 56, 94, 'Iyo galidda Naarta Jaxiimo.'),
(5074, 56, 95, 'Waxaad (la soo sheegay) waa run dhab ah.'),
(5075, 56, 96, 'Ee tasbiixso Magaca Eebaha wayn.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Waxaa Eebe sharrifa (oo wayneeya) waxa ku sugan Samawaadka iyo Dhulka, Eebana waa adkaade falsan.'),
(5077, 57, 2, 'Waxaa Eebe u sugnaaday xukunka samaawaadka iyo Dhulka, isagaa waxa nooleeya waxna dila, waxwalbana wuu karaa.'),
(5078, 57, 3, 'Eebe waa kan horreeyay, waana kan dambayn, waana kan muuqda (khalqigiisa) awgiis waana kan (Indhaha) ka qarsoon waxwalbana og.'),
(5079, 57, 4, 'Eebe waa kan ku abuuray Samaawaadka iyo Dhulka Lix Maalmood qaddarkood, markaasna Carshiga ku ekaaday (si u cunnanta) wuxuuna ogyahay waxa Dhulka gali iyo waxa ka soo bixi, waxa Samada ka soo dagi iyo waxa u kori, Eebana waa idinla joogaa meelkastood ku sugantihiinba, waxaad camal falaysaanna Eebe waa arkaa.'),
(5080, 57, 5, 'Eebaa iska leh xukunka Samada iyo dhulka, xaggiisaana loo celin ummuuraha.'),
(5081, 57, 6, 'Eebe waa kan habeenka iyo dhaarta is dhex galiya (kala badiya) waxa laabta ku jirana waa ogyahay.'),
(5082, 57, 7, 'Rumeeya Eebe iyo Rasuulkiisa, bixiyana waxa ka mid ah wuxuu idiinka dhigay wakiillo (maamulkiisa) kuwa rumeeyay Eebe oo idinka mid ah waxna bixiyay waxay Mudan ajir wayn.'),
(5083, 57, 8, 'Maxaad leedihiin ood la rumayn waydeen Eebe, isagoo Rasuulkuna idiinku yeedhi inaad rumaysaan Eebihiin, isagoo Eeba ballan idinka qaaday haddaad mu\'miniin tihiin.'),
(5084, 57, 9, 'Eebe waa ka soo dajiyay Aayaad cadcad si uu idiinka bixiyo mugdiga, Nuurna idiinku bixiyo, Eebana waa idiin naxariis badanyahay.'),
(5085, 57, 10, 'Maxaad leedihiin ood wax ugu bixinwaydeen Jidka Eebe isagoo Samaawaadka iyo Dhulka dhaxalkooda iska leeyahay, masinna Ruux wax bixiyay Maka furashadeedii ka hor oo Jahaaday, kuwaasaa ka Ajri wayn kuwii bixiyay oo dagaalamay ka dib, dhammaantoodna Eebe wuxuu u yaboohay wanaag, Eebana waa ogyahay waxaad camal falaysaan.'),
(5086, 57, 11, 'Waa kuma kan Eebe amaah wanaagsan amaahin (wax bixin) oo markaas uu u laablaabo, Ajri wanaagsanna hela.'),
(5087, 57, 12, '(Xusuusta) Maalinta aad arki Mu\'miniinta Rag iyo Haweenba iyagoo nuurkoodu socda hortooda iyo Midigtooda, waxaana lagu dhihi bishaaraysta Maanta, waa Jannooyin Ay dureeri dhexdooda Wabiyaal iyagoo ku waari dhexdeeda, taasina waa liibaan wayn.'),
(5088, 57, 13, 'waa Maalinta Munaafiqiintu Rag iyo Haweenba ay ku dhihi kuwa xaqa rumeeyay na suga aan ka Dhuxul qaadanno Nuurkiinnee, waxaana lagu dhihi ku noqda gadaashiinna, waydiistana Nuur, markaasaa layeelaa dhexdooda Darbi xaggiisa gudahana Naxariis tahay, xaggiisa dibaddana Caddibaad tahay.'),
(5089, 57, 14, 'Way u dhawaaqi Mu\'miiniinta iyagoo leh miyannaan idinla jirin, Mu\'miniintuna waxa ku dhihi haa (waad nala jirteen) laakiin waxaad balayseen Naftiinna, xumaanna waad la sugteen (Nabiga Iyo Mu\'miniinta), waadna shakideen waxaana idinku kadisay yididiilo (been ah) Inta amarka Eebe ka yimaaddo (Geerida) waxaana Eebe idinku kadsiiyay Kadiye (Shaydaan).'),
(5090, 57, 15, 'Maanta (Qiyaamada) laydin kama aqbalo is furasho idinka iyo kuwa Gaaloobay midna, Hoygiinnuna waa Nar waadna mudataan, meel loo hoydana iyadaa u xun.'),
(5091, 57, 16, 'Miyayan u dhawaanin kuwa xaqa rumeeyay inay u khushuucdo quluubtoodu xusidda Eebe (Quraanka) iyo waxa xaqa ah ee dagi, oyna la mid noqonin kuwii Kitaabka la siiyay horay (Yahuud iyo Nasaaro) oo muddadu ku dheeraatay, markaas ay ingagtay Quluubtoodu, wax badan oo ka mid ahna waa Faasiqiin (ka baxay Xerada).'),
(5092, 57, 17, 'Ogaada in Eebe nooleeyo Dhulka dhimasho ka dib, wuuna idiin caddeeyay Aayaadka si aad wax u kastaan.'),
(5093, 57, 18, 'Ragga wax Sadaqaysta iyo Haweenka wax Sadaqaysta oo Eebana Amaah wanaagsan Amaaahiya (waa Sadaqada) waa loo laablaabi, waxayna leeyihiin Ajri sharaf leh.'),
(5094, 57, 19, 'Kuwa rumeeyay Eebe iyo Rasuulkiisa kuwaasu waa Runbadanayaal, Shuhadaduna Eebe agtiisa ajri iyo Nuur bay ku mudan kuwase Gaaloobay beeniyayna Aayaadkanaga kuwaasi waa ehelka Naarta Jaxiimo.'),
(5095, 57, 20, 'Ogaada in nolosha Adduunyadu yahay ciyaar iyo dheeldheel iyo isqurxin iyo isufaan dhexdiinna ah iyo badsi Xoolo iyo Carruur, sida Roobi isjeclayssiyey beertah, wuxuu soo bixiyo, markaasna ingaga ood arki isaga oo doorsoomay noqonna burbur, Aakhirana waxaa jira caddibaad daran iyo dambidhaaf xagga Eebe ka iman iyo raalli ahaansho, nolosha Adduunyana waa raaxo lugu kadsoomi.'),
(5096, 57, 21, 'U orotama Dambidhaafka Eebe iyo Janno balladhkeedu yahay balladhka Samada iyo dhulka oo kale, loona darbay kuwa rumeeyay Eebe iyo Rasuulkiisa, Arrintaasuna waa samafal Eebana wuxuu siin Ruuxuu doono, Eebana Fadligiisu waa wayn yahay.'),
(5097, 57, 22, 'Wixii dhib ah oo Dhulka ka dhaca ama naftiinna ku dhaca wuxuu ku sugnaaday Kitaab Abuuriddiisa ka hor, arrintaasuna waa u fududdahay Eebe.'),
(5098, 57, 23, 'Si aydaan ugu murugoonin wixii idinka taga aydaan uguna farxin wuxuu idin siiyo, Eebe ma jecla Ruux kasta oo isla wayn oo faanbadan.'),
(5099, 57, 24, 'Oo ah kuwa bakhayli dadkana fari bakhaylnimo, Ruuxii jeedsadana Eebe waa ka hodon mahadsan.'),
(5100, 57, 25, 'Dhab ahaan baan ula dirray Rasuulladannadii xujooyin, waxaana u soo dajinnay Kutub iyo Caddaalad, si dadku ugu tooso Caddaalad, waxaana soo dajinnay birta iyadoo Shiddo ku dheehantahay iyo nacfi dadka iyo in Eebe muujiyo Cidda u gargaari isaga (Diinta) iyo Rasuulladiisa iyagoon arkaynin, Eebana waa Xoog badane Adkaade ah.'),
(5101, 57, 26, 'Dhab ahaan baan u dirray (nabi) Nuux iyo (nabi) Ibraahim, waxaana yeellay faracooda Nabinnimo iyo Kitaabbo loo soo dajiyay, waxaana ka mid ah kuwa hanuunay, waxbadan oo ka mid ahna waa Faasiqiin.'),
(5102, 57, 27, 'Markaasaan raacinnay Raadkoodii Rasuulladanadii, waxaana raacinnay Ciise Binu maryama, waxaana siinay Injiil, waxaana yeellay Quluubta kuwii raacay isudanqasho iyo naxariis iyo Ruhbaaniyad (Adduun ka jeedsi) ay iyagu billawdeen oonaan ku faral yeelin, doonidda raalli ahaanshaha Eebe dartiis yayna u faleen, mase ayna ilaalinin ilaalin xaq ah, waxaana siinnay kuwii rumeeyay oo ka mid ah ajrigoodii, wax badan oo ka mid ahna waa faasiqiin.'),
(5103, 57, 28, 'Kuwa xaqa rumeeyow Eebaa ka dhawrsada, rumeeyana Rasuulkiisa haydin siiyo laab laab naxariiseede, hana idiin yeelo Nuur aad ku socotaane, hana idiin dambidhaafee, Eebana waa dambidhaafe naxariista ah.'),
(5104, 57, 29, 'Inay ogaadan ehlukitaabku inayan karin inay hantaan Fadliga Eebe, fadliga Eebana awooddiisu uu ku jiraa, wuxuuna siin Ruuxuu doono, Eebana waa kan Fadliga wayn leh.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'Eebe wuxuu maqlay Haweenaydii hadalkeeda Nabiga kula doodaysay xaalkii ninkeeda, oo Eebe u sheeganaysay «dhibaatadeeda», Eebana wuu maqlayey doodooda, maxaayeelay Ilaahay waa ka wax walba maqle arka.'),
(5106, 58, 2, 'Kuwa haweenkooda kala mid dhiga hooyadood oo kale haweenkoodu ma aha hooyadood, hooyo waa midda wax dhashay, kuwaasina waxay ku hadlayaan hadal xun iyo been Ilaahayna hadal xun iyo been Ilaahayna waa Saamax badane Dhaafid badane.'),
(5107, 58, 3, 'Kuwa haweenkooda kala mid dhigi hooyadood haddana ku noqon «haweenkii» waxaa ku waajib ah qoor xoraynteed intayan istaaban, Arrintaasi waa waano, Ilaahayna waxa dadku camal fali waa ogyahay.'),
(5108, 58, 4, 'Ruuxaan helin qoor uu xoreeyo wuxuu soomi Laba Bilood oo israacsan intayan istaaban, ciddiise aan karin soonka wuxuu quudin Lixdan miskiin, arrintu waa saas si aad Eebe u rumeysaan iyo Rasuulkiisa, taasina waa xuduuddii Ilaahay Gaalase waxaa u sugnaaday Cadaab kulul.'),
(5109, 58, 5, 'Kuwa Eebe iyo Rasuulkiisa hilaafaya waxay mudan yihiin halaag sidii loo halaagay kuwii ka horreeyey, Eebana wuxuu soo dajiyey Aayado cad cad Gaalana waxaa u sugnaaday cadaab wax ihaaneeya.'),
(5110, 58, 6, 'Maalinbuuna ihaanayn Ilaahay uu soo bixin dhammaantood, ugana warrami waxay camal faleen, Eebana waa koobay iyana waa hilmaameen Ilaahayna wax kasta wuu daalacan (arkaa).'),
(5111, 58, 7, 'Ma ogtahay in Ilaahay ogyahay Cirka iyo Dhulka waxa ku jira, Seddexdii faqiba Ilaahay baa afreeya, Shantana Ilaahay baa Lixeeya; waxa ka yar iyo waxa ka badanba Ilaaahaybaa la jooga meeshay ahaadaanba, wuxuuna uga qarrami waxay camal faleen Maalinta Qiyaamo Ilaahayna wax walba waa ogyahay.'),
(5112, 58, 8, 'Miyaadan ogayn kuwa laga reebay faqa xun oo haddana ku noqon waxa laga reebay, kuna faqi dambi iyo cadawnimo iyo caasinnimada Rasuulka, markay Nabiga u yimaadaanna ku salaamaya waxaan Eebe ku salaamin oo hoos u dhihi maa Eebe nagu cadaabo waxaan ku hadlayno waxaa ku filan Jahaannamo oo ay galayaan waana Guri xun.'),
(5113, 58, 9, 'Kuwa rumeeyayow markaad faqaysaan ha ku faqina dambi, colnimo iyo caasinnimada Rasuulka ee ku faqa wanaag dhawrsani, Eebana ka yaaba isagaa la isu soo kulmine.'),
(5114, 58, 10, 'Faqa xun Shaydaan buu kayimaaddaa suu u walbahaar geliyo kuwa xaqa rumeeyay, waxbase kama dhibo idan Ilaahay Mooyee, Ilaahay uun ha tala saarteen kuwa xaqa rumeeyey.'),
(5115, 58, 11, 'Kuwa xaqa rumeeyayow haddii la idin dhaho isu waasiciya fadhiga; isuwaasiciya Ilaahay wanaagga ha idiin waasiciyee, marka la idin yiraahdo Jihaadka iyo Khayrka u degdegana; u degdega Ilaahay wuu koryeelayaa kuwa xaqa rumeeya kuwa cilmiga lehna daragooyin yuu koryeelayaa Eebana waxa la camal fali waa ogyahay.'),
(5116, 58, 12, 'Kuwa xaqa rumeeyayow markaad Nabiga qaabilaysaan hormariya Sadaqo, saasaa idiin khayr badan idiina nadiifsan, haddaydaan helaynin Eebe waa dambi dhaaf badane Naxariis badane.'),
(5117, 58, 13, 'Ma waxaad ka cabsateen inaad hormarsataan Sadaqooyin markaad Nabiga la kulmaysaan; haddaydaan saas falin Eebe waa idin ka toobada aqbalaye ee Salaadda ooga Sakadana bixiya Ilaahaay iyo Rasuulkiisana adeeca, Eebana waa ogyahay waxaad camal falaysaan.'),
(5118, 58, 14, 'Ma ogtahay kuwii ka sokeeye dhigtay kuwa Eebe u cadhooday kuwaasi idin ka mid ma aha iyagana ka mid ma aha (waa munaafiqiinta) waxayna ku dhaaran been iyagoo og.'),
(5119, 58, 15, 'Eebe wuxuu u darbay kuwaas cadaab daran maxaa yeelay wax xun bay falayeen.'),
(5120, 58, 16, 'Dhaartoodii bay gaashaan ka dhigteen dadkana xaqiibay ka leexiyeen waxayna mudan yihiin cadaab wax ihaaneeya.'),
(5121, 58, 17, 'Xoolahooda iyo Caruurtoodu wax ugama taraan Eebe xaggiisa, waana kuwa ehelu Naar ah wayna ku waari.'),
(5122, 58, 18, 'Maalinta Eebe soo bixiyo dhamaantood way u dhaaran Eebe siday idinka idiinku dhaartaan iyagoo u malayn inay wax (xuja ah) haystaan waase kuwo beenaalayaal ah.'),
(5123, 58, 19, 'Waxaa ka qaalib noqday Shaydaan oo hilaamsiiyey xusidda Eebe, kuwaasi waa Xisbiga shaydaanka, Xisbiga shaydaankuna waa kuwa khasaaray.'),
(5124, 58, 20, 'Kuwa Ilaahay iyo Rasuulkiisa khilaafaya waxay ka mid noqon kuwa dullooba.'),
(5125, 58, 21, 'Eebe wuxuu xugmiyey inuu adkaado isaga iyo Rasuulkiisa, maxaa yeelay waa xoog badane adkaade ah.'),
(5126, 58, 22, 'Ma helaysid dad Ilaahay rumeeyey iyo Maalintii dambeeysay oo haddana jecel ruux Ilaahay iyo Rasuulkiisa khilaafaya haba ahaadeen Aabayowgood, Caruurtooda, Walaahood ama Qaraabadooda, kuwaas (aan jeelaanin) waa kuwo qalbigooda iimaanka lagu dhigay Eebena ku xoojiyey gargaarkiisa, wuxuuna gelin Jannooyin Wabiyaal dhex socdaan ay kuna waari, kuwaas Ilaahay baa ka raalli noqday iyana way ka raallii noqdeen waana Xisbiga Ilaahay; Xisbiga Ilaahayna waa kuwa Liibaanay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Waxaa Eebe nasaha (sharifa) waxa Cirka iyo Dhulka ku dhexnool, waana xoo badane Falsan.'),
(5128, 59, 2, 'Eebe waa kan bixiyey kuwii gaaloobay ee ahlu Kitaabka ahaa «Yuhuud» Guryahoodii kulmintii hore, idinkoon u malaynin inay bixi iyana waxay u maleeyeen in Guryahoodu wax ka celin, markaasaa Eebe uga yimid meel ayan filayn, qalbigoodana wuxuu ku tuuray cabsi, guryahooda waxay ku kharribeen (ku halleeyeen) gacmahooda iyo gacmaha kuwa rumeeyey, ee dadka Caqliga leh ha waano qaato.'),
(5129, 59, 3, 'Haddaan Eebe xukumin inay baxaan waxaa lagu caddibi lahaa Adduunka, Aakhirana waxaa u sugnaaday cadaab naareed.'),
(5130, 59, 4, 'Maxaa yeelay Ilaahay iyo Rasuulkiisa yey khilaafeen, ruuxii Eebe khilaafana Ciqaabtiisu waa darantahay.'),
(5131, 59, 5, 'Wixii aad gooysaan oo geed ah (qq Cadawga) ama aad daysaan iyadoo ku taagan Salkeeda waa idinka Eebe iyo inuu dulleeyo Faasiqiinta.'),
(5132, 59, 6, 'Wixii Xoola ah ee Ilaahay Nabiga uga soo celiyo Gaalada isagoon Fardo iyo awr lagu dagaallamin waxaa amarkiisa leh Nabiga Eebana Cidduu doono yuu ku diraa Rasuulkiisa, wax walbana Eebe waa karaa.'),
(5133, 59, 7, 'Wixii Xoola aha ee Ilaahay ku soo celiyo ee uga yimaadda Degmooyinka Yuhuudda Rasuulkiisa; waxaa leh Ilaahay, Rasuulka, Qaraabada, Agoonta, Masaakiinta iyo Musaafiriinta «socdaalka» si ayan u noqon wax ku wareegaysta kuwa Hodanka ah dhexdooda wuxuu Rasuulku dadka siiyo ama faro waa inay qaataan, wuxuu ka reebana waa inay ka reebmaan, Eebana waa in laga dhawrsado, maxaa yeelay Ciqaabtiisu waa darantahay.'),
(5134, 59, 8, 'Waxaa Xoolahaas wax ku leh kuwa Saboolka ah ee ka soo hijrooday guryahoodii iyo Xoolahoodii dar Eebe iyo gargaarka Ilaahay iyo Rasuulkiisa, waana kuwa ku run sheegay iimaankooda.'),
(5135, 59, 9, 'Kuwii Madiino deganaa ee Xaqa rumeeyey waxay jecel yihiin dadka u soo hijroon (u soo qixi) wax necbaansho ahna Laabta uma geliyaan dadkaas waxay siin, waxayna Naftooda ka hormariyaan (ay uga doori) dadkaas inkastaba iyagu gaajo hahaysee, ruuxii laga dhawro bakhaylnimada Naftiisa waa mid Liibaanay.'),
(5136, 59, 10, 'Kuwa imaan Asxaabta Nabiga ka dib waxay Eebe weydiisan inuu u dambi dhaafo iyaga iyo Walaalahooda iimaanka uga hormaray uusan qalbigoodana xiqdi yeelin kuwa rumeeyey Eebena waa naxariis badane.'),
(5137, 59, 11, 'Ma ogtahay in kuwii Munaafiqiinta ahaa ay ku dhihi kuwii gaaloobay ee Yuhuudda ahaa haddii laydin bixiyo waan idinla jiraynaa Cid aan idiin maqlaynana ma jiro waligeed, haddii laydinla dagaallamana waan idiin gargaaraynaa, Ilaahayna waa ogyahay inay Beenaalayaal yihiin.'),
(5138, 59, 12, 'Haddii la bixiyo Yuhuudda lama baxaayaan haddii lala dagaallamana uma gargaarayaan hadday isku dayaan inay u gargaaraanna way carari loomana gargaarayo.'),
(5139, 59, 13, 'Mu\'miniinta yey uga cabsi badan yihiin Yuhuuddu Ilaahay, maxaa yeelay waa dad aan wax garad ahayn.'),
(5140, 59, 14, 'Kuwa xaqa rumeeyeyna lama dagaallami karaan «dhufaysyo iyo Gidaaro dhexdood mooyee» dhibka dhexdooda ahna waa daranyahay quluubtooduna waa kala tagsan tahay, maxaa yeelay waa dad aan wax kasayn.'),
(5141, 59, 15, 'Waxay la mid yihiin gaaladii dhawaa ee ka horreeyey ee dhadhamiyey cidhibtii camalkooda, Cadaab daranna u sugnaaday.'),
(5142, 59, 16, 'Waxay la midyihiin Shaydaan waa ka dadka gaalow ku dhaha, markay gaaloobaanna ku yidhaah bari baan kaa ahay (waxba isuguma keen jiraan) oo sheega inuu Ilaahay ka yaabi.'),
(5143, 59, 17, 'Markaasay Labadooduba Cidhibtoodu noqon naar ay ku waaraan, daalimiintana saasaa lagu abaal mariyaa.'),
(5144, 59, 18, 'Kuwa xaqa rumeeyey waa inay Eebe ka dhawrsadaan, Nafwalbana waa inay eegtaa waxay u hormarsatay barri (qiyaamaha) Eebena ha laga yaabo illeen wax walba waa ogyahaye.'),
(5145, 59, 19, 'Kuwa xaqa rumeeyey waa inayan noqon sidii kuwii Eebe hilmaamay, isna markaas uu hilmaansiiyey naftooda waana kuwo faasiqiin ah (xaqa ka leexday).'),
(5146, 59, 20, 'Ma eka Ahlu naarka iyo Ahlu Jannaha, Ahlu Jannuhu waa kuwa liibaanay.'),
(5147, 59, 21, 'Haddii quraankan Eebe Buur ku dejiyo (kula hadlo) waxaad arki lahayd Buurtii oo khushuucsan oo dillaaci cabsi Eebe darteed, saasaa Eebe mataallo ugu sheega dadka inay fikiraan darteed.'),
(5148, 59, 22, 'Eebe mooyee Ilaah kale ma jiro, waxa maqan iyo waxa joogaba waa ogyahay, waana kan naxariis guud iyo mid gaaraba naxariista.'),
(5149, 59, 23, 'Ilaahay isaga mooyee Eebe kale ma jiro, waa Xaakim Daahir ah, nabadgeliye, rumeeye, wax walbana ilaaliye xoog iyo wenaan isagaa leh, waana ka nasahanyahay waxay Cibaadada la wadaajinayaan.'),
(5150, 59, 24, 'Eebe waa kan wax abuura ee ahaysiiya sawirana, Magacyo fiicanna wuu leeyahay waxaana Eebe u tasbiixsada (weyneeya) waxa Cirka iyo dhulka ku sugan, waana adkaade falame.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'Kuwa xaqa rumeeyow ha ka dhiganina colka Eebe iyo Colkiina (Gaalada) sokeeye aad jacayl u gudbinaysaan, iyagoo ka gaaloobay xaqa idiin yimid, oo haddana bixiya Rasuulka iyo Muuminiinta inaad Eebe rumeeyseen darteed Jidka Ilaahayna ku dagaallanteen raalli ahaanshaha Eebe idinkoo rabo, kuwaas ood u qarisaan jacayl, Ilaahayna waa ogyahay waxaad qarinaysaan iyo waxaad muujinaysaanba, Ruuxii saas yeelana Jidka xaqa ah wuu ka dhumay.'),
(5152, 60, 2, 'hadday idin helaan (Gaaladu) waxay idiin noqon Col gacmaha iyo Carabkana xumaanbay idiinku taagi waxayna jecel yihiin inaad gaalowdaan.'),
(5153, 60, 3, 'Qaraabada iyo Caruurtu waxba idiin ma taraan Maalinta qiyaame waana laydin kala kaxayn, Ilaahayna waa arkaa waxaad Camal falaysaan.'),
(5154, 60, 4, 'Kuwa xaqa rumeeyey waa inay ku daydaan Nabi Ibraahim iyo kuwii la jiray waa kuwii iska bari yeelay Gaaladii Qoonkoodii ahayd (ay isla noolayeen) iyo waxay Caabudayeen ee Eebe ka soo hadhay oy Colnimo u muujiyeen ilaa ay Eebe kaliya ka rumeeyaan, laakiin Nabi Ibraahim wuxuu u dambi dhaaf weydiiyey Aabihiis, isagoon waxba u hanankarin (intaan laga reebin Arrintaas). Eebe yeynnu talo saaran, isagaan u toobad keenaynaa, isagaana loo noqon (Aakhiro).'),
(5155, 60, 5, 'Eebow hannagu fidnaynin kuwa Gaaloobay, noona dambi dhaaf illeen waxaad tahay adkaade (Casiis) Falsan.'),
(5156, 60, 6, 'Haku daydo dadkaas dayasho wanaagsan ruuxii dooni Ilaahay Maalinta Aakhiro, ciddiise ka jeedsata xaqa Eebe waa ka kaaftoon yahay waana ammaananyahay.'),
(5157, 60, 7, 'Wuxuu Eebe u dhawyahay inuu yeelo jacayl Mu\'miniinta iyo kuway la colloobeen ee Gaalada Qureesheed ah dhexdooda, maxaa yeelay Eebe wax walba waa karaa waana dambi dhaaf badane naxariis badane ah.'),
(5158, 60, 8, 'Eebe kama reebayo kuwa xaqa rumeeyey inay u samo iyo Caddaalad falaan kuwa aan Diinta kula dagaallamin Guryahoodana ka bixinin, maxaa yeelay Eebe waa jecel yahay kuwa Caadiliinta ah (Garsoor).'),
(5159, 60, 9, 'Eebe wuxuu ka reebi kuwa xaqa rumeeyey inay ka sokeeye yeeshaan oo xidhiidhiyaan kuwa Diinta kula dagaallamay ee Guryahoodii ka bixiyey gacanna ku siiyey in guryaha laga bixiyo, dadkii kuwaas cokeeye ka dhigtaa wa kuwa daalimiin ah.'),
(5160, 60, 10, 'Kuwa xaqa rumeeyow hadday idiin yimaaddaan Haween Mu\'miniin ah oo Gaalada ka soo Hijrooday (ka soo tegay) Imtixaana (inay ka dhabtahay iyo in kale) Eebaa og Iimaankooda, haddaad ogaataan inay xaqa (dhabnimo) u rumeeyeen waa inaydaan Gaalada u celinin, maxaa yeelay Xalaal isuma aha Meherkiina ha u celiyeen Gaalada, inaad isguursataanna waxba kuma jiro haddaad Meher siinaysaan, Haweenka Gaalooba Nikaax isuguma kiin xirna ee ha haysanina, weydiista waxaad Meher u bixiseen Gaaladuna ha weydiisato waxay Meher u bixiyeen, xaaladaasina waa xukunka Eebe uu dhexdiina xukumay, Ilaahayna waa ogyahay (waxa la Camal falo) waana Falsame.'),
(5161, 60, 11, 'Hadday u tagaan kuwa Haweenkiina ka mid ah Gaalada (Gaaloobaan) markaas ka dib la qaniimaysto hala siiyo kuwii Haweenkoodu tageen waxay ku masruufeen oo kale, Ilaaha aad rumaysantihiinna ka dhawrsada.'),
(5162, 60, 12, 'Nabiyow hadday kuu yimaaddaan haween xaqa rumeeyey si ay kuugula ballantamaan inay Eebe kaliya caabudaan, waxna xadin, sinaysanna, Caruurtoodana dilin, been abuurna ayan la imaan, wax wanaag ah oo la farayna ayan Nabiga ku caasiyin la ballantan una dambi dhaaf weydii Eebe waa dambi dhaafe naxariiste ahe.'),
(5163, 60, 13, 'Kuwa xaqa rumeeyow haka sokeeye dhiganina kuwa Eebe u carooday oo aakhirana ka quustay (waa Gaaloodhane) sida Gaaladu uga quusatay kuwii dhintay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Waxaa Ilaahay nasaha (weyneeya) waxa Cirka iyo Dhulka ku sugan, Eebana waa adkaade falsan.'),
(5165, 61, 2, 'dadka xaqa rumeeyayow maxaad u sheegaysaan wax aydaan falaynin.'),
(5166, 61, 3, 'Waxaa Cadho weyn Eebe agtiisa ku ah inaad sheegtaan wax aydaan falaynin.'),
(5167, 61, 4, 'Eebe wuxuu jecel yahay kuwa ku dagaallama Jidkiisa (Jihaadka) iyagoo af ah oo aad mooddo dhismo laysku dhejiyey (midnimadooda).'),
(5168, 61, 5, 'Xusuusta markii (Nabi) Muuse ku yidhi dadkiisii maxaad ii dhibaysaan idinkoo og inaan Rasuul Eebe idiin ahay, markay xaqii ka iisheen yaa Eebana sii iilay qalbigooda, Ilaahayna ma hanuuniyo kuwa faasiqiinta ah.'),
(5169, 61, 6, 'xusuusta marka (nabi) Ciise ibnu Maryama ku yidhi Banii Israa\'iil waxaan ahay Rasuul Eebe idiin soo diray oo rumayn Kitaabkii Tawraad ee horreeyey, kuna bishaarayn Rasuul gadaashayda imaan oo Magaciisuna yahay «Axmed», markuu xuja cad ula yimidna waxay dheheen kani waa sixir cad.'),
(5170, 61, 7, 'yaa ka dulmi badan ruux ku been abuurtay Ilaahay isagoo loogu yeedhi Islaamka, Eebe ma hanuuniyo dadka daalimiinta ah.'),
(5171, 61, 8, 'Waxay doonayaan (Gaaladu) inay Nuurka (xaqa) Eebe ku bakhtiiyaan Afkooda Eebena wuu taam yeeli nuurkiisa haba naceen Gaaladuye.'),
(5172, 61, 9, 'Eebe waa kan diray Rasuulkiisa isagoo hanuun wada iyo Diin xaq ah si uu (Eebe) Diimaha oo dhan uga sare mariyo haba naceen Mushrikiintuye (Gaaladu).'),
(5173, 61, 10, 'Kuwa xaqa rumeeyow ma idin tusiyaa ganacsi idinka koriya Cadaab daran.'),
(5174, 61, 11, 'Waxaad rumaynaysaa Ilaahay iyo Rasuulkiisa waxaadna ku dagaalamaysaan Jidkiisa xaqa ah Xoolihiina iyo Naftiina idinkoo u huri, saasaana idiin khayr roon haddaad tihiin kuwo wax og.'),
(5175, 61, 12, 'Markaas Eebe dambigiinuu dhaafi wuxuuna idin gelin Jannooyin ay dhex socdaan Webiyaal iyo Guryo wanaagsan oo ku dhexyaalla Jannada Cadnin (nagaanshaha), taasina waa liibaan weyn.'),
(5176, 61, 13, 'Arrin kale ood jeceshihiinna (wuu idiin siin) waa gargaarka Eebe iyo fatxi (guul) dhaw, kuwa xaqa rumeeyeyna u bishaaree.'),
(5177, 61, 14, 'dadka xaqa rumeeyow noqda gargaarayaasha Eebe sidii kuwii Nabi Ciise ibnu Maryama Saaxiibadiis ahaa (Xawaariyintii) ee uu ku yidhi yaa Eebe iila gargaara, markaas ay dheheen annagaa ah gargaarayaashii Eebe, ka dib ay xaqa rumaysay koox Banii Israa\'iil ka mid ah, kooxna ay ka gaalawday; Eebena ayiday (xoojiyey) kuwii xaqa rumeeyey (kana adkaadeen Cadawgoodii) oo iyagu ka muuqdeen (ka taliyeen) dhulkii.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Waxaa Eebe nasihi (weynayn) waxa samooyinka iyo Dhulka ku sugan, Ilaahay waa xaakin Xumaan ka daahirane ah aadkade falsame ah.'),
(5179, 62, 2, 'Eebe waa kan u soo bixiyey kuwaan wax aqoon Rasuul ka mid ah oo ku akhriya korkooda Aayaadkiisa «quraankana» bara iyo Sunnada (Nabiga) waxayna horey u ahaayeen kuwo haadi cad ku sugan.'),
(5180, 62, 3, '(Waxaa kale oo Eebe Nabigu u diray) kuwo kale oon weli la kulmin kuwaas, Eebena waa Adkaade falsame ah.'),
(5181, 62, 4, 'Arrintaasuna (Islaamka iyo Nabiga la soo diray) waa deeq Ilaah, wuxuuna siin ruuxuu doono Eebana waa deeq iyo fadli weyne.'),
(5182, 62, 5, 'Kuwii la faray inay Kitaabka Tawreed (ilaaliyaan) kuna Camal falaan (qaa Yuhuudda) haddana an ku camal falin, waxay la mid yihiin Dameerxambaarsan Kutub badan, waxaana xun masalka kuwaas beeniyey Aayadaha Eebe, Ilaahayna ma hanuuniyo kuwo daalimiin ah.'),
(5183, 62, 6, 'Waxaad dhahdaa (Nabiga) kuwa Yuhuuda ahow haddaad sheegateen inaad tihiin Awliyo Eebe dadka kale ka sokow, geeri jeelaada haddaad run sheegaysaan.'),
(5184, 62, 7, 'Geerina ma jeelaanayaan weligood waxay hormarsadeen (oo gaf ah) dartiis, Eebana waa ogyahay kuwa daalimiinta ah.'),
(5185, 62, 8, 'Waxaad ku dhahdaa geerida aad ka cararaysaan way idinla kulmi, markaasaa laydiin eelin Ilaah og waxa maqan iyo wax joogaba, markaasuu idinka warrami (idinka jaasin) waxaad camal falayseen.'),
(5186, 62, 9, 'Kuwa xaqa rumeeyow marka laysugu yeedho Salaadda Maalinta Jimcaha ah aada xusidda Eebe, wax gadashadana ka taga, saasa idiin khayr roon haddaad tihiin kuwo wax og.'),
(5187, 62, 10, 'Markase Salaadda la dhammeeyo Dhulka ku kala faafa, dalbana (doona) khayrka Eebe, Ilaahayna xusa in badan waxaad u dhawdihiin inaad liibaantaan.'),
(5188, 62, 11, 'Markay arkaan tijaaro (Ganacsi) ama Ciyaar (wax lagu shuqloomo) way aadaan, waxayna kaa tagaan Nabiyow adoo taagan (Khudbo akhrin) waxaad dhahdaa waxa Eebe agtiisa ah yaa ka khayr badan Ciyaar iyo Ganacsiba, Ilaahay waa kan ugu khayr badan inta wax arsaaqda.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Markay kuu timaadaan (Nabiyow) munaafiqiintu waxay dhihi waxaan marag ka nahay inaad tahay Rasuul Eebe, Ilaahayna waa ogyahay inaad tahay, rasuulkiisii Eebana wuxuu marag ka yahay inay Munaafiqiintu beenaalayaal yihiin (xaqa niyada).'),
(5190, 63, 2, 'Dhaartooda (beenta ah) yey ka dhigteen gaashaan, dadkane waxay ka iileen Jidkii Eebe, waxay samayna aad buu u xunyahay.'),
(5191, 63, 3, 'Saas waxaa ugu wacan inay (xaga muuqda) rumeeyeen haddana (hoosta) ka gaaloobeen, Quluubtoodana waa la daabacay (la daboolay) wax xaq ahna ma kasayaan.'),
(5192, 63, 4, 'Haddaad aragto Munaafiqiinta waad la yaabi Jidhkooda, muuqooda, hadday hadlaanna waad dhagaysan hadalkooda, waxaadse moodaa dogobyo la tiirtiiriyey, qayladii yeedhabana way ka cabsadaan, waa Col kuwaasi, Eebena ha lacnado ee xagee loo duufsan.'),
(5193, 63, 5, 'Marka lagu dhaho Munaafiqiinta kaalaya ha idiin dambi dhaaf weydiiye Rasuulka Eebe waxay ruxaan Madaxa (diico darteed) waxaad aragtaa iyagoo jeedsan oo iskibrin.'),
(5194, 63, 6, 'Waa isku mid haddaad u dambi dhaaf weydiisid (Nabiyow) iyo haddii kale, marnaba Eebe u dambi dhaafi mahayo, maxaa yeelay Eebe ma hanuuniyo dad Faasiqiin ah.'),
(5195, 63, 7, 'Waa kuwa dhihi (Munaafiqiintu) ha quudinina kuwa Rasuulka la jooga intay ka kala tagaan, Eebaa iska leh khasnadaha Samaawaatka iyo Dhulka, haseyeeshee Munaafiqiintu ma fahansana.'),
(5196, 63, 8, 'Munaafiqiintu waa kuwa dhihi haddaan Madiino u noqonno waa in kuwa tabarta lihi ka bixiyaan Magaalada kuwa dullaysan, tabar iyo Sharaf Eebaa iska leh Rasuulkiisa iyo Mu\'miniinta, haseyeeshee Munaafiqiintu ma oga.'),
(5197, 63, 9, 'Kuwa xaqa rumeeyow yaanay idin ka shuqlinin Xoolihiinnu iyo Caruurtiinnu xuska Eebe (daacaddiisa) Ciddii saas fashaana waa mid khasaartay.'),
(5198, 63, 10, 'Bixiyana wax ka mid ah waxa Eebe idinku arsuqay geerida horteeda oo markaas uu yiraahdo (ruuxaan wax baxsan) Eebow maad muddo dhow dib ii dhigtid oon sadaqaysto kuwa suubanna aan ka mid noqdee.'),
(5199, 63, 11, 'Naf malageed galayna Eebe dib uma dhigo, Ilaahayna waa ogyahay waxaad Camal falaysaan.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Waxaa Eebe nasaha (weyneeya) waxa Samaawaatka (Cirka) iyo Dhulka ku sugan awood iyo mahadbana Eebaa iska leh, wax walbana isagaa kara.'),
(5201, 64, 2, 'Eebe waa kan idin anbuuray qaarkiinna gaaluu noqday, qaarkiinna Mu\'miniin, Eebana waxaad Camal falaysan waa arkaa.'),
(5202, 64, 3, 'Samaawaatka (Cirka) iyo Dhulkaba wuxuu Eebe u abuuray si xaq ah, wuuna idin sawiray Eebe, dadoow suuraddiinana wuu wanaagjiyey, isagaana loo noqon (Ahkhiro).'),
(5203, 64, 4, 'Wuxuu ogyahay waxa samaawaatka (Cirka) iyo Dhulka ku sugan, wuxuuna ogyahay waxaad qarinaysaan iyo waxaad muujinaysaan, Eebana waa ogyahay waxa laabta (Qalbiga) ku jira.'),
(5204, 64, 5, 'Miyuuna idin soo gaarin warkii kuwii gaaloobay ee horreeyey, waxay dhadhamiyeen xumaantii camalkoodii, waxaana u sugnaaday caddibaad daran.'),
(5205, 64, 6, 'Arrintaas waxaa ugu wacan in Rasuullo ula yimaadeen xujooyin ayna dheheen (Gaaladii) ma dadbaa na hanuunin ayna gaaloobeen oo jeedsadeen, iskana deeqtoonaysiiyeen (weynaysiiyeen) Eebe, Eebana waa midka hodanka ah lana mahadiyo.'),
(5206, 64, 7, 'Waxay sheegeen kuwii gaaloobay inaan la soo bixinaynin, waxaad dhahdaa saas ma aha, Eebaa wuxuu ku dhaartay in la idin soo bixin laydinkana warrami waxaad camal fasheen, taasina waa u fududahay Eebe.'),
(5207, 64, 8, 'Ee rumeeya Eebe iyo Rasuulkiisa iyo nuurka Eebe soo dejiyey (Quraanka) Eebana waxaad camal falaysaan waa ogyahay.'),
(5208, 64, 9, 'Maalinta qiyaamada ee Ilaahay idin soo kulmin waa maalin kado, ruuxiise Ilaah rumeeya camal wanaagsanna fala Eebe xumaantiisa (dambigiisa) wuu asturi, wuxuuna galin Janooyin ay dhex dureeraan Wabiyaal way kuna waaraan waligood, taasina waa liibaan weyn.'),
(5209, 64, 10, 'kuwa gaaloobayse beeniyeyna Aayaadka Eebe waa kuwa Ahlu Naar ah waxayna ku waari dhexdeeda, meel loo ahaadana iyadaa u xun.'),
(5210, 64, 11, 'Wixii Musiiba ah (dhib ah) oo dhaca waa idinka Eebe, ruuxiise Eebe rumeeya waxaa hanuuni (oo xasili) qalbigiisa, Eebana wax walba waa ogyahay.'),
(5211, 64, 12, 'Eebe adeeca Rasuulkiisana adeeca, haddaadse jeedsataan (xaqa diidaan) waxaa Rasuulka saaran uun inuu si cad wax u gaargiiyo.'),
(5212, 64, 13, 'Eebe mooyee Ilaah kale ma jiro, isaga uunna ha tala saarteen Mu\'miniintu.'),
(5213, 64, 14, 'Kuwa xaqa rumeeyow Haweenkiinna iyo Caruurtiina waxaa ka mid ah kuwo Col idiin ah ee ka digtoonaada, haddaadse iska cafidaan saamaxdaanna Eebe waa dambi dhaafe naxariiste ah.'),
(5214, 64, 15, 'Xoolihiinnu iyo Caruurtiinnu waa uun fidno (ibtilo) Eebe agtiisaase ajir weyn (ku sugan yahay).'),
(5215, 64, 16, 'Eebe ka dhawrsada intaad kartaan, maqla oo adeecana (weyneeya), waxna baxsada, ruuxii laga dhawro bakhaylnimada naftiisa waa liibaanay.'),
(5216, 64, 17, 'haddaad Eebe amaah fiican amaahsataan (wax sadaqaysataan) Eebe waa idiin laab laabi, dambigiinana wuu dhaafi, Eebana waa kan la mahadiyo ee ee dusha san.'),
(5217, 64, 18, 'waxa maqan iyo waxa joogaba Eebe wa ogyahay waana adkaade Falsan.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'Nabiyow haddaad haweenka furaysaan Cidada ku fura (daahir aan la taaban) Cidadana tiriya, Eebana ka dhawrsada, hana ka bixinina Guryahooda (ay joogeen markay la furayey) iyana yeyna ka bixin (yeyan ka tagin) inay xumaan cad la yimaadaan mooyee, taasi waa xuduudii Eebe, ruuxii ka tallaba xuduuda Eebena naftiisuu dulmiyey, ma ogidin, wuxuu Eebe u dhawyahay inuu ka dib xaal keeno, (jacayl isku noqosho).'),
(5219, 65, 2, 'Markay Cidada dhamaadkeeda (ku dhawaadaan) si fiican u haysta, ama si fiican u faaruqa, dad caadiliina oo idin ka mid ahna marag uga dhiga, maragana ha u guteen Eebe dartiis, xaalkaasi waa wacdi ruuxii rumayn Eebe iyo Maalintii dambaysay «yiaamada» ruuxii Eebe ka dhawrsadana wuxuu u furi faraj.'),
(5220, 65, 3, 'Wuxuuna ka irsaaqi meel uusan ka filanayn, ruuxii Eebe tala saartana waa ku filan yahay, Eebana amarkiisu waa fuli, (waxna kama fakado) wax walbana Eebe wuxuu u yeelay waqti sugan (oo go\'an).'),
(5221, 65, 4, 'Haweenkiinna Caadada ka quustay haddaad shakidaan Cidadoodu waa seddex bilood, kuwaan Caada lahayn waa sidaas oo kale, Haweenka uurka leh cidadoodu waa inay umulaan (dhalaan), ruuxii Eeb ka dhawrsadana wuxuu u fududayn amarkiisa.'),
(5222, 65, 5, 'Kaasi waa amarkii Eebe uu idiin soo dejiyey, ruuxii Eebe ka dhawrsadana xumaantiisuu asturaa ajirkiisana wuu weyneeyaa.'),
(5223, 65, 6, 'dejiya Haweenkaas (cidada ku jira) meelahaad dagan tihiin eed heli kartaan, hana dhibina si aad u ciriiridaan, hadday uur leeyihiinna nafaqeeya (masruufa) intay ka umuulaan (kadhalaan), hadday ilmaha nuujiyaanna siiya ujuuro, wanaagna is fara, haddii laysku qabto nuujintana mid kale ayaa nuujin.'),
(5224, 65, 7, 'Haka nafaqeeyo (masruufo) midka awood xoola leh awooddiisa, kan wax yarna haka nafaqeeyo waxa Eebe siiyey (ee uu karo), Eebana kuma mashaqeeyo naf wuxuusan siinin, Eebana wuxuu yeeli cidhiidhi dabadiis fudayd (faraj).'),
(5225, 65, 8, 'Maxay badan tahay Magaalo Caasiday amarka Eebe iyo Rusushiisa, markaas Eebe xisaabay xisaab daran caddibaad adagna caddibay.'),
(5226, 65, 9, 'oo dhadhamisay Cidhibtii xaalkaa xumaantiisa, cidhibteediina ku dambaysay khasaare.'),
(5227, 65, 10, 'Eebe wuxuu u darbay kuwaas Caddibaad daran, ee Eebe ka dhawrsada kuwa Caqliga lahaw rumeeyeyowna, Ilaahay wuxuu idiin soo dejiyey Quraane.'),
(5228, 65, 11, 'Rasuulna wuu idiin soo diray kassoo idiin akhriya Aayaadka Eebe ee cad cad si uu kuwa Mu\'miniinta ah camal wanaagsanna falay uga bixiyo mugdiga Nuurkana ugu bixiyo mugdiga Nuurkana ugu bixiyo, ruuxii Eebe rumeeya camal wanaagsanna fala wuxuu gelin Jannooyin ay dureerayso dhexdeeda Wabiyaal wayna ku waari waligood, Eebe waa wanaajiyey kuwaas risqigooda.'),
(5229, 65, 12, 'Eebe waa kan abuuray Todobo Samo Dhulkana kala mid dhigay, amarka Eebena wuxuu ku soo degaa dhexdooda, si aad u ogaataan in Eebe wax walba karo wax walbana koobay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'Nibiyow maxaad u xarrimi wax Eebe kuu xalaaleeyey si aad u raalli geliso Haweenkaaga, Eebe waa dambi dhaafe naxariista ah.'),
(5231, 66, 2, 'Eebe waa idiin caddeeyey waxa dhaartiinnu ku xalaaloobi, Eebena waa gargaarihiinna, waana wax walba oge falsan.'),
(5232, 66, 3, 'Ma xusuusan Waqtigii Nabigu ku qarsaday Haweenkiisa Qaarkood hadal, markaas ay ka warrantay xaalkii, Eebana uu Nabiga u daahiriyey, markaas Nabigu uu ogeysiiyey qaarkiis (haweenaydii) qaarna uu ka dhaafay, markuu u warramay ay tiri yaa kuu sheegay xaalkan kuna yiri: waxaa ii warramay Eebaha wax walba oge ah ee yaqaana.'),
(5233, 66, 4, 'Haddaad Eebe u noqotaan inkastoo qalbigiinnu iishay (Eebe wuu idiin dhaafi) haddaadse isu kaalmaysataan Nabiga (dhibkiisa) waxaa u gargaari Eebe, Jibriil iyo kuwa suuban ee Mu\'miniinta ah Malaa\'igta (kalana) markaas ka dib yey xoojin.'),
(5234, 66, 5, 'Wuxuu Eebe u dhawyahay in Nabigu hadduu idin furo (Haweenka Nabiga qaarkood) idiinkuna beddelo kuwa idinka khayr roon oo ah Muslimiin, Mu\'miniin ah adeeca Eebe, Toobad keena, Eeba caabuda, Soomana kuwa horay loo guursaday iyo kuwo aan hadda ka hor la guursanba leh.'),
(5235, 66, 6, 'Kuwa xaqa rumeeyow ka dhawra Naftiina iyo ehelkiinna naar lagu shido dad iyo dhagax ay kana (shaqeeyaan) malaa\'ig adag oo daran oon Eebe ku caasiyeyn wuxuu faro, falana wixii la amru.'),
(5236, 66, 7, 'Kuwa Gaaloobow ha cudur daaranina maanta (Qiyaamada) waxaa laydinka abaal marin uun waxaad camal fali jirteen.'),
(5237, 66, 8, 'Kuwa xaqa rumeeyow u toobad keena Eebe toobad san, wuxuu Eebe u dhawyahay inuu asturo xumaantiina, idin geliyana Jannooyin ay dureerayso dhexdooda Wabiyaal, Maalinta uusan Eebe hoojinayn Nabiga iyo kuwa la jooga, nuurkooduna wuxuu socon hortooda iyo midigtooda iyagoo dhihi Eebow noo tam yeel Nuurkanaga noona dambi dhaaf\'adaa wax walba karee.'),
(5238, 66, 9, 'Nabiyow la jihaad gaalada iyo Munaafiqiinta kuna adkow, hoygooduna waa Jahannamo meel loo ahaadana iyadaa u xune.'),
(5239, 66, 10, 'Eebe wuxuu tusaale uga dhigay kuwa Gaaloobay Haweenaydii Nabi Nuux iyo Nabi Luudh, waxa qabay Laba Addoon oo Eebe oo suusuuban, markaasay khiyaameen Nabiyadaas waxbana ugama ayan tarin Eebe agtiis waxaana loo dhihi Labadaas haweenay la gala naarta kuwa gelaya.'),
(5240, 66, 11, 'Waxaa kale oo Eebe Tusaale uga dhigay kuwa rumeeyey Haweenaydii Fircoon markay tidhi Eebbow Guri agtaada iiga dhis jannada dhexdeeda, igana nabad geli Fircoon iyo Camalkiisa, igana nabad geli dadkaas daaliminta ah.'),
(5241, 66, 12, 'Maryama bintu cimraanna xusuusta ee isdhawrtay, markaas malagu jeebka ka afuufay, rumaysayna Sharciga Eebe iyo Kutubtiisaba, kana mid noqotay kuwa Eebe adeeca.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Waxaa barako badnaaday (oo weynaaday) Eebaha Xukunku awooddiisa ku jiro ee wax walbana kara.'),
(5243, 67, 2, 'Eebaha abuuray geeri iyo nololba, si uu dadow idiin imtixaamo kiinna Camal fiican, Eebana waa adkaade dambidhaafe ah.'),
(5244, 67, 3, 'Eebe waa midka abuuray Toddobo Samo oo isdul saaran kuna arki maysid abuurka Eebaha Raxmaanka ah kala tagsani, bal daymada celi inaad aragto wax dillaac ah.'),
(5245, 67, 4, 'Haddana celi daymada waxaa kugu soo noqon aragga oo dhiban daallanna.'),
(5246, 67, 5, 'Eebe wuxuu ku quxiyey Samada dhow Nalal (Xiddigaha) wuxuuna ka dhigay Xiddigahaas kuwo lagu gano Shayaadiinta, Wuxuuna Eebe u diyaariyay kuwaas Naarta Saciiro.'),
(5247, 67, 6, 'Kuwa ka gaaloobay Eebahood waxaa u sugnaaday Caddibaadda Jhannamo, meel loo ahaadana iyadaa u xun.'),
(5248, 67, 7, 'Marka Gaalada lagu tuuro Naarta waxay ka maqli Cod xun oo daran iyadoo karaysa.'),
(5249, 67, 8, 'waxay u dhawdahay inay Cadha daraadeed la kala dillaacdo, marka Koox (Gaala ah) lagu ridaba waxaa warsada Malaa\'igta Naarta ka shaqeysa iyagoo ku dhihi miyuusan idiin imaanin wax idiin diga.'),
(5250, 67, 9, 'Waxayna ugu jawaabi waa noo yimid wax noo diga waanse beeninay, annagoo leh ma soo dejinin Eebe waxba, idinkuna waxaad ku sugan tihiin baadi weyn.'),
(5251, 67, 10, 'Gaaladii waxay dhaheen haddaan wax maqlaynno ama aan wax kasayno Naarta Saciiro ehelkeeda ma aanaan noqoneen.'),
(5252, 67, 11, 'Waxay kirteen (Gaaladu) dambigoodii waxaana u sugnaatay fogaansho Naarta Saciiro ehelkeeda.'),
(5253, 67, 12, 'Kuwa Eebe ka yaaba iyagoon la arkaynin (Eebe mooyee) waxaa u sugnaaday dambi dhaaf iyo ajri weyn.'),
(5254, 67, 13, 'Ama hadalka hoos dhiga dadow ama kor u qaada Eebe waa ogyahay waxa laabta ku jira.'),
(5255, 67, 14, 'Ma waxaan wax ogeyn Eebaha wax walba abuuray ee wax walba daalacan.'),
(5256, 67, 15, 'Eebe waa kan Dhulka ka dhigay mid laylan ee ku socda Waddooyinkiisa kana Cuna Quudkiisa Eebaana Dadku loo soo kulmin (Qiyaamada).'),
(5257, 67, 16, 'Miyaad ka aamin noqoteen Eebaha Sare inuu Dhulka idin la gooyo, ayna idinla dhaqdhaqaaqdo.'),
(5258, 67, 17, 'Miyaadse ka aamin noqoteen Eebaha Sare inuu Dhagaxyo Naar ah idinku soo daadiyo, waad ogaan doontaan digiinta Eebe Cidhibteeda.'),
(5259, 67, 18, 'Waxaa Eebe beeniyey Gaaladii (reer Makaad) ka horreeyey, sidayse ahayd Ciqaabti Eebe.'),
(5260, 67, 19, 'Miyeyna dadku arkaynin Shimbirka korkooda ah marna baalka fidin marna laabi wax haya oon Eebaha Raxmaan ahayna uusan jirin, Eebana wax walba wuu arkaa.'),
(5261, 67, 20, 'Waa kuma Gargaaraha Eebaha Raxmaan ah ka soo hadhay ee u gaargaari Gaalada, Gaaladu waxay ku sugan yihiin kadsoomid.'),
(5262, 67, 21, 'Waa kuma kan wax quudiya haddii Eebe haysto Risqigiisa, Gaaladu waxay ku Madax adaygeen isla weyni iyo Xaq diidnimo.'),
(5263, 67, 22, 'Midkee wanaagsan Ruux Wajiga k socda iyo mid si toosan oo siman Wadda Toosan ugu socda.'),
(5264, 67, 23, 'Dheh Nabiyow Eebe waa midka Dadkow idin ahaysiiyey, Maqal iyo Arag iyo Caqlina idiin yeelay, wax yar baadse Eebe ku shukridaan.'),
(5265, 67, 24, 'Dheh Eebe waa midka dadkow Dhulka idinku beeray isagaana (Qiyaamada) loo soo kulmin doona Dadka.'),
(5266, 67, 25, 'Waxay dhihi Gaaladu waa goorma Waqtiguu dhici waxa naloogu gooddin haddaad run sheegaysaan.'),
(5267, 67, 26, 'Dheh Eebaa og goortaas, waxaanse ahay Anigu u dige Cad (muuqda).'),
(5268, 67, 27, 'Markaysa Gaaladu Naarta oo u dhow arkaan waxaa xumaan oo doorsoomi Wajiga Gaalada, waxaana lagu dhihi waaka Cadibaaddii aad ddonayseen (dedejisanayseen).'),
(5269, 67, 28, 'Dheh balse ka warrama haddii Eebe aniga (Nabiga) iyo inta ila jirta uu halligo ama u naxariisto yaa Gaalo ka korin Caddibaad daran.'),
(5270, 67, 29, 'Dheh Eebe waa Raxmaan waana rumaynay, waana Tala saaranay, waadse ogaan doontaan (Gaalooy) Cidda Baadi Cad ku sugan.'),
(5271, 67, 30, 'Dheh balse Warrama hadday Biyuhu Dhulka ku gudhaan yaa idin siin Biya durduri (ma jiro Eebe waxaan ahayn).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Eebe wuxuu ku dhaartay Qalinka iyo waxa Malaa\'igtu qorayso (Camalka dadka).'),
(5273, 68, 2, 'Ee Nabiyow Nicmada Eebe darteed lama waallid.'),
(5274, 68, 3, 'Waxaana kuu sugnaaday Ajri aan go\'ayn.'),
(5275, 68, 4, 'Waxaana kuu sugnaaday dabeeco (fiican) oo weyn.'),
(5276, 68, 5, 'Waad arki doontaa nabiyow Gaaladuna way arki doonaan.'),
(5277, 68, 6, 'kinna waalan (ama la caddibo).'),
(5278, 68, 7, 'Eebe waa ogyahay Cidda ka dhunta Jidkiisa, waana ogyahay Cidda hanuunsan.'),
(5279, 68, 8, 'Ee Nabiyow ha maqlin xaq beeniyayaasha (Warkooda).'),
(5280, 68, 9, 'Waxay jecel yihiin inaad u iilato iyana kuu soo iishaan.'),
(5281, 68, 10, 'Ha maqlin dhaar badane dhammaantiis oo dullaysan.'),
(5282, 68, 11, 'oo Xanlow ah, isku diraa ah.'),
(5283, 68, 12, 'Khayrka iyo Xoolahana reebta, gardarrow iyo dambiile ah.'),
(5284, 68, 13, 'Xumaantana ku madax agad isku dhejisna ah (xumaanlow ah).'),
(5285, 68, 14, 'inuu Xoolo iyo Wiilal uu leeyahay darteed (yuu saas u falay).'),
(5286, 68, 15, 'Marka Quraankannaga lagu akhriyana wuxuu dhahaa waa dadkii hore warkoodii.'),
(5287, 68, 16, 'wuxuuse Eebe ka calaamayn Sanqaroorka.'),
(5288, 68, 17, 'Eebe wuu Imtixaanay Gaaladii (Reer Makaad) sidii loo imtixanay kuwii beerta markay ku dhaarten inay Aroortii goostaan.'),
(5289, 68, 18, 'Iyagoon dhihin haddii Eebe idmo.'),
(5290, 68, 19, 'Waxaase Beertii xagaa Eebe uga yimid Amar iyagoo hurda.'),
(5291, 68, 20, 'Waxayna noqotay wax la shafay oo kale.'),
(5292, 68, 21, 'wayna isu dhawaaqeen Subaxdii.'),
(5293, 68, 22, 'iyagoo leh kallaha oo beerta goosta.'),
(5294, 68, 23, 'wayna tageen iyagoo Codka hoos u dhigi.'),
(5295, 68, 24, 'inaan Beerta maanta Miskiin soo gelin.'),
(5296, 68, 25, 'Wayna kallaheen iyagoo diidan (in Miskiin u soo galo).'),
(5297, 68, 26, 'Markay ay arkeen Beertii siday tahay yey isu dhaheen waan soo dhunnay.'),
(5298, 68, 27, 'haddana way isgarteen waxayna dhaheen ma ehee waa la ina hoojiyey.'),
(5299, 68, 28, 'Markaas kii u fiicnaa yiri: miyaana idin dhihin war Eebe weyneeya.'),
(5300, 68, 29, 'Markaasay dheheen Eebaa weyn, waan xad gudubnay.'),
(5301, 68, 30, 'Markaasaa qaarba qaar ku jeedsaday iyagoo is dagaali.'),
(5302, 68, 31, 'Waxayna dheheen Magacaana ba\'ye waan xad gudubnay.'),
(5303, 68, 32, 'Eebe wuxuu u dhow yahay inuu noogu beddelo mid ka fiican isagaan Khayr ka rajaynaynaa eh.'),
(5304, 68, 33, 'Caddibaaddu waa saas Caddibaadda Aakharaase ka weyn haddii ay wax ogyihiin.'),
(5305, 68, 34, 'Kuwa Eebe ka dhowrsada waxay Eeba agtiisa ku leeyihiin «Jannada Naciimada».'),
(5306, 68, 35, 'Ma Muslimiinta yaanu kala mid dhigaynaa Dambiilayaasha.'),
(5307, 68, 36, 'Seed u dhihi kartaan saas.'),
(5308, 68, 37, 'Mise Gaalooy waxaa idiin sugnaaday Kitaab aad akhrisataan.'),
(5309, 68, 38, 'oo Waxaad doontaanba aad ka helaysaan.'),
(5310, 68, 39, 'Mise ballan adag yaad naga qaadeen tan iyo Maalinta Qiyaame oo waxaad rabtaaan aad xukumaysaan.'),
(5311, 68, 40, 'Weydii (Nabiyow) Gaalada Cidda damiinka ugu ah Xaalkaas.'),
(5312, 68, 41, 'mise dad la wadaaga Arrintaas yey leeyihiin, ha keeneen kuwaas hadday run sheegi.'),
(5313, 68, 42, 'Maalinta dhudhunka la faydi doono Gaaladana loogu yeedhi doono inay sujuudaan ma karayaan Sujuud.'),
(5314, 68, 43, 'Indhaha Gaaladu Maalintaas waa dullaysan tahay waxaana ku muuqda Walbahaar iyo qoomamo, waxayna ahaayeen (Adduunka) kuwa loogu yeeray Sujuud iyagoo nabad qaba.'),
(5315, 68, 44, 'Isu kaana daa aniga iyo kuwa beeniyey Quraanka waana sasabi iyagoon ogeyn.'),
(5316, 68, 45, 'Waana sugi tan iyo muddo, Maamulka Eebana waa xoog badan yahay.'),
(5317, 68, 46, 'Nabiyow ma waxaad weydiisatay Gaalada Ujuuro oo markaas Cuslleeyey.'),
(5318, 68, 47, 'Mise waxa maqan yaa aktooda yaal ooy wax ka qori.'),
(5319, 68, 48, 'Ku samir Xukunka Eebe hana noqonin sidii Saaxiibkii Xuudka (Mallayga Yuunus) markuu naadiyey Eebihiis isaga oo walbahaarsan.'),
(5320, 68, 49, 'Haddan naxariista Eebe haleelin waxaa lagu tuuri lahaa Cidlo (bannaan) isagoo la dagaalay.'),
(5321, 68, 50, 'Waxaase doortay Eebihiis wuxuuna ka dhigay kuwa wanaagsan.'),
(5322, 68, 51, 'Waxay u dhow yihiin kuwi gaaloobay inay daymada kugu lagdaan markay maqlaan Qur\'aanka waxayna dhihi waa waalan yahay (Nabigu).'),
(5323, 68, 52, 'Quraanka iyo Nabiguna waa uun waanada Caalamka.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'Maalinta Xaaqo (Qiyaamadu waa wax sugan).'),
(5325, 69, 2, 'Xaaqo maxay tahay.'),
(5326, 69, 3, 'inaadan aqoon ma laga yaabaa xaaqada.'),
(5327, 69, 4, 'Waxa beeniyeen Reer «Thamuud» iyo «Caadba» Qiyaamada.'),
(5328, 69, 5, 'Thamuudse waxaa lagu halligay Qaylo daran.'),
(5329, 69, 6, 'Caadna waxaa lagu halligay Dabayl daran oo qabow.'),
(5330, 69, 7, 'oo lagu diray korkooda Toddoba habeen iyo Siddeed Maalmood oo iraacsan, waxaadna arkaysaa iyagoo daadsan ood mooddo jirrid Timir (dogob dhacay).'),
(5331, 69, 8, 'wax ka hadhay miyaad aragtaa.'),
(5332, 69, 9, 'Fircoon iyo kuwii ka horreeyeyna waa jireen iyo kuwii gafsanaa ee la daba geddiyey (Nabi Luudh Qoomkiisi).'),
(5333, 69, 10, 'Waxay Caasiyeen rusushii Eebahood Markaasuu si daran u qabtay.'),
(5334, 69, 11, 'Markay Biyihii xad dhaafeen (Waqtigii Nabi Nuux) waxaan idinku xambaarnay (Awoow yaalkiin) Doontii.'),
(5335, 69, 12, 'si aan uga yeelno waano ay una xafiddo dhag wax maqasha.'),
(5336, 69, 13, 'Marka Suurka la afuufo hal mar.'),
(5337, 69, 14, 'Dhulka iyo Buurahana la xambaaro oo la burburiyo hal mar.'),
(5338, 69, 15, 'Maalintaasay Qiyaamadu dhacaysaa.'),
(5339, 69, 16, 'Samaduna Maalintaas way dillaaci wayna tabar yaraan.'),
(5340, 69, 17, 'Malaa\'igtuna Cirka gaararkiisa yey ahaan Maalintaas, Carshiga Eebana waxaa dadka korkiisa ku xambaari Maalintaas Siddeed Malag.'),
(5341, 69, 18, 'Maalintaasaa dadka loo soo bandhigi Eebe wax xagooda kaga qarsoonna ma jiro.'),
(5342, 69, 19, 'Ruuxiise Kitaabkiisa midigta laga siiyo wuxuu dhihi (farax daraadiis) akhriya Kitaabkayga.'),
(5343, 69, 20, 'Waxaan yaqiinsanaa (Adduunka) inaan la kulmi Xisaabtayda.'),
(5344, 69, 21, 'wuxuu ruuxaasi ku noolaan nolol laga raalli yahay.'),
(5345, 69, 22, 'Wuxuuna geli «Janno» sarreeysa.'),
(5346, 69, 23, 'Midhaheeduna u dhaw yihiin (Ruuxa guran).'),
(5347, 69, 24, 'Waxaana lagu dhihi Cuna oo Caba idinkoo shifaysan Camalkaad hor marsateen ayaamihii tegay daraadiis (Adduunkii).'),
(5348, 69, 25, 'Ruuxiise Kitaabkiisa laga siiyo Bidixda wuxuu dhahayaa Magacayba\'ee maan la i siinin Kitaabkayga.'),
(5349, 69, 26, 'Xisaabtaydana maanan ogaanin.'),
(5350, 69, 27, 'Magaacayba\'ee maxaan u soo noolaaday.'),
(5351, 69, 28, 'Xoolahaygi wax iima tarin.'),
(5352, 69, 29, 'Waxaa lumay awooddaydii (iyo xujadaydii).'),
(5353, 69, 30, 'Waxaa loo odhan qabta oo Gacmihiisa iyo luquntiisa isku xira.'),
(5354, 69, 31, 'Naarta Jahannamada geliya.'),
(5355, 69, 32, 'Markaas geliya Silsilad Todobaatan dhudhun ah.'),
(5356, 69, 33, 'maxaayeelay wuxuu ahaa kaan rumaynin Eebaha weyn.'),
(5357, 69, 34, 'aana fari jirin in Masaakiinta wax la siiyo.'),
(5358, 69, 35, 'Maanta (Qiyaamada) kuma leh Saaxiib.'),
(5359, 69, 36, 'Cuunana kuma leh aan ahayn dhaacaanka «Ehlu Naarka».'),
(5360, 69, 37, 'Cunnadaasna ma cuno kuwa gafay waxaan ahayn (Gaalada).'),
(5361, 69, 38, 'Waxaan ku dhaaran waxaad aragtaan (Makhluuqaad ah).'),
(5362, 69, 39, 'iyo waxaydaan arkayninba.'),
(5363, 69, 40, 'ee Qur\'aanku waa hadalkii Eebe uu u soo dhiibay Rasuul Sharaf leh.'),
(5364, 69, 41, 'Hadal gabayaana ma aha, wax yar yaadse rumaynaysaan (Xaqa).'),
(5365, 69, 42, 'Hadal wax sheegana ma aha wax yar yaadse waantoomaysaan.'),
(5366, 69, 43, '(Qur\'aanku) wuxuu ka soo degay Eebaha Caalamka.'),
(5367, 69, 44, 'Hadduu nagu been abuurto (Nabi Muxamed).'),
(5368, 69, 45, 'Waxaanu ku qaban gacan adag.'),
(5369, 69, 46, 'Waxaannu gooynaynaa Xididka Wadnaha.'),
(5370, 69, 47, 'Ruux idinka mid ah oo naga eelinnna ma jiro.'),
(5371, 69, 48, 'Qur\'aankuna waa waanada kuwa Eebe ka dhawrsada.'),
(5372, 69, 49, 'Waxaan ognahay inay idin ka mid yihiin kuwa beenin (Qur\'aanka).'),
(5373, 69, 50, 'Qur\'aanku waa qoomamada Gaalada (Maalinta Qiyaame).'),
(5374, 69, 51, 'Qur\'aanka waa run dhab ah.'),
(5375, 69, 52, 'Ee Nabiyow u tasbiixso Magaca Eebe ee weyn.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Midbaa wuxuu Eebe warsaday in Caddibaad ku dhacda Gaalada (loo soo dedejiyo).'),
(5377, 70, 2, 'Caddibaadaasna wax Gaalada ka Celin ma jiro.'),
(5378, 70, 3, 'Waxayna ka imaan Caddibaaddaasu Eebaha sarraynta iyo Sharafta u Saaxiiba.'),
(5379, 70, 4, 'Ee Malaa\'igta kale iyo Jibriilba u koraan Maalin qaddarkeedu dhan yahay Konton Kun oo Sano.'),
(5380, 70, 5, 'Ee Nabiyow Samir Samir wanaagsan.'),
(5381, 70, 6, 'Gaaladu waxay Caddibaadda u arkaan inay fogtahay.'),
(5382, 70, 7, 'Annaguna waxaan u aragnaa inay dhawrdahay.'),
(5383, 70, 8, 'Caddibaaddaasu waxay dhici Maalinta Cirku noqon bir la dhalaaliyey oo kale.'),
(5384, 70, 9, 'Buuruhuna ay noqon Suuf la tifay oo kale.'),
(5385, 70, 10, 'Sokeeyana uusan sokeeye wax warsanaynin.'),
(5386, 70, 11, 'Wayna is arkayaan, dambiiluhuna wuxuu jeelaan inuu iskaga furto Maalintaas Caddibaadeeda Caruurtiisa.'),
(5387, 70, 12, 'Haweenaydiisa, Walaalkiis.'),
(5388, 70, 13, 'iyo qaraabadiisii soo dhawaynayey.'),
(5389, 70, 14, 'iyo waxa Dhulka ku sugan dhammaan markaa uu nabad galo.'),
(5390, 70, 15, 'Saas ma aha ee waxaa jirta Naar kulul (oo ay geli).'),
(5391, 70, 16, 'Waxayna siibi Galka (Madaxa iyo Jirka).'),
(5392, 70, 17, 'Waxayna u dhawaaqi dadka jeedsaday xaqana diiday.'),
(5393, 70, 18, 'Xoolahana kulmiyey oo hayey (oon xaqa ku bixinin).'),
(5394, 70, 19, 'Dadka waa la abuuray iyaga oo Naf jaclaysi badan.'),
(5395, 70, 20, 'Haddii dhib taabto wuu argagaxaa.'),
(5396, 70, 21, 'Hadduu khayr helana wuu reebaa.'),
(5397, 70, 22, 'Marka laga reebo Muslimiinta tukata.'),
(5398, 70, 23, 'Ee ah kuwa Salaaddooda daa\'ima.'),
(5399, 70, 24, 'Kuwa Xoolahoodana xaq la ogyahay ka bixiya.'),
(5400, 70, 25, 'Siiyana kuwa baryootama iyo kuwaan wax haysan.'),
(5401, 70, 26, 'Kuwa rumeeya Maalinta Qiyaame (ee Abaal marinta).'),
(5402, 70, 27, 'Kuwa ka Cabsaday, Caddibaadda Eebahood.'),
(5403, 70, 28, 'Maxaa yeelay Caddibaadda Eebe wax ka Aamin ah ma jiro.'),
(5404, 70, 29, 'Kuwa ka ilaaliya Xubinta (dhalmaha) sino.'),
(5405, 70, 30, '30 Waxa ka soo haray Xaaskooda ama ay hanteen, taasna laguma dagaalo.'),
(5406, 70, 31, 'Ruuxiise doona wax gudubsan kuwaa waa mid xad gudbay.'),
(5407, 70, 32, 'Kuwa waxa lagu aaminay iyo Ballankaba ilaaliya.'),
(5408, 70, 33, 'Kuwa maraggooda si fiican u furay.'),
(5409, 70, 34, 'Kuwa Salaaddooda xafida (dhawra).'),
(5410, 70, 35, 'Kuwaasi waxay geli Jannooyin lagu Sharrifo.'),
(5411, 70, 36, 'Maxaa u sugnaaday Gaalada oo ay xagga Nabiga u deg degi (iagoo beenin).'),
(5412, 70, 37, 'Iyagoo midigta Nabiga iyo Bidixdiisaba Koox Koox u jooga.'),
(5413, 70, 38, 'Ma wuxuu damci Ruux kasta oo ka mid ah in geliyo Jannatu Naciima.'),
(5414, 70, 39, 'Saas ma aha ee waxaan ka abuuray waxay ogyihiin.'),
(5415, 70, 40, 'Waxaan ku dhaaran Qorrax ka soo baxyada iyo Qorrax u dhacyada ee waan karraa.'),
(5416, 70, 41, 'Inaan ku baddallo kuwa ka khayr badan, nagamana dheerayn karaan.'),
(5417, 70, 42, 'Ee iska dhaaf ha dhumbadeen eh (xumaanta) hana Ciyaareen intay kala kulmaan Maalintoodii Qiyame ee loogu gooddiyey.'),
(5418, 70, 43, 'Maalinta ay ka soo bixi Qubuuraha iyagoo deg degi ood mooddo inay u laxaadsan Calaamo loo taagay (xaggooda).'),
(5419, 70, 44, 'Aragooduna waa dullaysan yahay Maalintaas, waxaana ka muuqda oo dabooli dulli, Maalintaasuna waa Maalintii loogu yaboohay (Abaal marinteeda).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Annagaa Nabi Nuux u dirray Qoomkiisii si uu ugu digo Caddibaadda horteed.'),
(5421, 71, 2, 'Wuxuuna ku yidhi Qoomkayow waxaan idiin ahay dige Cad «oo muuqda»'),
(5422, 71, 3, 'ee Eebe Caabuda kana dhawrsada\'anigana i adeeca.'),
(5423, 71, 4, 'Wuu idiin dambi dhaafi wuuna idin dib dhigi ilaa muddo Magacaaban, marka Eebe la yimaaddo ajasha wax dib u dhigi ma jiro haddaad wax ogtihiin.'),
(5424, 71, 5, 'Nabi Nuux wuxuu yiri Eebbow waan u yeeray Qoomkaygii Habeen iyo dharaar.'),
(5425, 71, 6, 'Yeedhiddayduna uma siyaadinin waxaan caraar ahayn.'),
(5426, 71, 7, 'Markasta oo aan u yeedhana si aad ugu dambi dhaafto waxay farahooda yeelaan dhagahooda, maryahoodana way isku dadaan, wayna ku Madax adaygaan xumaanta wayna is weyneeyaan.'),
(5427, 71, 8, 'si kor ah yaan ugu yeedhay, «dadka dhexdiisa»'),
(5428, 71, 9, 'Waana u qaylyey, hoosna waan ugu yeeray.'),
(5429, 71, 10, 'Waxaana ku idhi Eebihiin dambi dhaaf weydiista waa dambi dhaaf badance.'),
(5430, 71, 11, 'Cirkana Roob badan ha idinka keenee.'),
(5431, 71, 12, 'Hana idiin kordhiyo Xoolo iyo Wiilal iyo Beero hana idiin yeelo Wabi yaal.'),
(5432, 71, 13, 'Maxaad leedihiin ood weynida Eebe uga cabsoon weydeen.'),
(5433, 71, 14, 'Isagoo idin abuuray idinkoo soo maray Xaalado kala duwan.'),
(5434, 71, 15, 'Miyaydaan arkaynin sida Eebe u Abuuray toddoba Samo oo isdulsaaran.'),
(5435, 71, 16, 'Dayaxana uga yeelay Dhexdooda Nuur, qorraxdana ka yeelay siraad.'),
(5436, 71, 17, 'Eebe waa Kan idinka soo Bixiyay Dhulka.'),
(5437, 71, 18, 'Haddana idinku Celin doona Dhexdiisa idinkana soo bixindoona.'),
(5438, 71, 19, 'Eebe waa kan Idiinka yeelay Dhulka gogol.'),
(5439, 71, 20, 'Si aad ugu socotaan Dhexdiisa waddooyin waawayn.'),
(5440, 71, 21, '(Nabi) Nuux wuxuu yidhi Eebow way I caasiyeen waxayna Raaceen kuwo aan xoolohoodu iyo Carrurtoodu waxaan khasaara ahayn u kordhinaynin.'),
(5441, 71, 22, 'Dhagar waynna way Dhakreen.'),
(5442, 71, 23, 'Waxay isu Dheheen ha ka tagina Ilaahyaalkiinna, hana ka Tagina wuddi iyo suwaax iyo yaguuth, iyo Yacuuq iyo Nasri.'),
(5443, 71, 24, 'Waxayna Dhumiyeen dad Badan ee Eebow ha kordhinnin Daalimiinta waxaan baadi ahayn.'),
(5444, 71, 25, 'Gafkoodi Dartiis yaa loo Maansheeyay Naarna Loo Galiyey, mana helayaan Eebe ka soke gargaare,'),
(5445, 71, 26, 'Nuux wuxuu yidhi Eebow ha kaga tagin Dhulka korkiisa Gaalada Ruxna.'),
(5446, 71, 27, 'Maxaayeelay Hadaad kaga tagto waxay Dhumin Addomahaaga, mana dhalayaan waxaan gaal xun ahayn.'),
(5447, 71, 28, 'Eeboow u dambi Dhaaf Aniga iyo waalidiintay iyo ruuxii gala gurigeyga isagoo Mu\'min ah Raf iyo haweenba, hana u kordhinnin Daalimiinta waxaan halaag ahayn.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'Waxaad Dhahdaa (Nabiyow) waxaa lay waxyooday inay Dhagaysteen Koox Jinni ah (Quraanka) Dheheen waxaan maqallay quraan layaab leh.'),
(5449, 72, 2, 'Oo ku hanuunin xaqa waana Rumaynay waxaan Eebe cibaada la wadaajinhaynana ma jiro.'),
(5450, 72, 3, 'Waxaana Sarreeya sharafta Eebeheen, mana yeelan haweenay iyo ilmo midna.'),
(5451, 72, 4, 'Waxay Ahaayeen kuwannagii xumaa inay ku dhaheen hadallo xadgudug ah Eebe.'),
(5452, 72, 5, 'Waxaana u Malaynaynay inuusan insi iyo Jini Midna Eebe ku beensheegaynin.'),
(5453, 72, 6, 'Waxayna ahaayeen kuwo insiga ka mid ah inay Magangalaan kuwo Jinniga ka mida markaasna u kordhiyeen kibir (iyo xad Gudub).'),
(5454, 72, 7, 'Waxayna jinnigu u maleeyeen sidaad u malayseen inaan Eebe soo Bixinhaynin Ruuxna.'),
(5455, 72, 8, 'Waxaan Doonnay samada waxaana hellay iyadoo ka buuxda Ilaalin daran iyo danabyo.'),
(5456, 72, 9, 'Waxaana ahayn kuwo Fadhiista xageeda meelo si aan wax uga dhagaysanno Ruuxiise Dhagaysta hadda wuxuu heli danabyo darban «la kulmi».'),
(5457, 72, 10, 'Mana ogin inay tahay shar lala Doonay dadka Dhulka ku nool ama Eebohood la Doonay hanuun (iyo khayr).'),
(5458, 72, 11, 'Waxaa naga mid ah kuwo suuban iyo kuwo kaleba, waxaan nahay kooxa kala duwan.'),
(5459, 72, 12, 'Waxaanna Ognahay Inaannaan Eebe ku daalinkarin Dhulka Dhexdiisa, kuna Daalinhaynin Cararid.'),
(5460, 72, 13, 'Markaan Maqallay hanuunkana waan Rumaynay, Ruuxii rumeeya Eebihiisana kama yaabo Nusqaan iyo Dulmi Midna.'),
(5461, 72, 14, 'Waxaa naga mida Muslimiin iyo Kuwo xadgudbay, Ruusiise islaama waa mid ku dadaalay hanuun.'),
(5462, 72, 15, 'Kuwiise xadgudbay waxay ahaan Qoryaha Jahannamo.'),
(5463, 72, 16, 'Hadday ku toosaan Dadku jidka (wanaagsan) waxaaan ka waraabinlahayn biyo badan.'),
(5464, 72, 17, 'Imtixaan Dartiis, Ruuxii ka Jeedsada Xusidda Eebihiis wuxuu galin cadaab daran.'),
(5465, 72, 18, 'Masaajidda Eebaa iska leh ee ha kula baryina «caabudina» Eeb Cid kale.'),
(5466, 72, 19, 'Markuu istaagay Addoonkii Eebe (Nabigu) isagoo Caabudi Eebe waxay Jinnigu u dhawaadeen inay korkiisa is dulfuulan.'),
(5467, 72, 20, 'Waxaad Dhahdaa Nabiyow waxaan uun Caabudi Eebahay lana wadaajinmahayo cidna.'),
(5468, 72, 21, 'Waxaad dhahdaa idiin ma hanto shar iyo khayr midna.'),
(5469, 72, 22, 'Waxaad Dhahdaa cidna igama korinayso Eebe, mana helayo isaga ka sakow Magangalyo.'),
(5470, 72, 23, 'Hase yeeshee waxaan hantaa Gaadhsiinta xaqa Eebe iyo Farriimihiisa, Ruuxiise Eebe Caasiya iyo Rasuulkiisa waxaa u sugnaaday Naarta Jahannama isagoo ku waari Dhexdeeda.'),
(5471, 72, 24, 'Markay Gaaladu Arkaan waxa loo yaboohi waxay ogaan cidda Gargaarihiisu tabaryaryahay tirana yaryahay.'),
(5472, 72, 25, 'Waxaad Dhahdaa ma ogi inuu Dhawyahay waxa la idiinku Gooddin ama Eebahay waqti u yeeli.'),
(5473, 72, 26, 'Eebaa og waxa Maqan, umana muujiyo waxaa maqan Ruuxna.'),
(5474, 72, 27, 'Marka laga Reebo ciduu ka raalli Noqday oo Rasuulah, Isagoo hor iyo gadaalba ka ilaalin.'),
(5475, 72, 28, 'Si Eebe u muujiyo inay Gaadhsiiyeen Farriimihii Eebohood, Eebana waa koobay waxa Agtooda ah wayna u tirakoobanyihiin wax walba.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, '(Nabiga) Isduuduubayow.'),
(5477, 73, 2, 'Istaag habeenka (Tuko) wax yar Mooyee.'),
(5478, 73, 3, 'Nuskiisa tuko ama ka nusqaami wax yar.'),
(5479, 73, 4, 'Ama ka badi, Quraanku u akhri si fiican.'),
(5480, 73, 5, 'Annagaa Kugu Dakin Hadal Culus (wayne).'),
(5481, 73, 6, 'Habeenka Saacadihiisa (oo la tukado) yaa ku daran khushuucda Aadna u toosan.'),
(5482, 73, 7, 'Maalintii waxaa kuu sugnaaday shaqa Dheer.'),
(5483, 73, 8, 'Magaca Eebana xus cibaadadiisana u go\'.'),
(5484, 73, 9, 'Qorrax ka soobax iyo u Dhaeba Eebaa iska leh, isaga Mooyee ilaah kalena ma jiro ee ka yeelo wakiil (la cuskado).'),
(5485, 73, 10, 'kuna Samir waxay sheegi gaaladu, si Quruxsanna uga hijroo (iskaga tag).'),
(5486, 73, 11, 'Isu kaanna daa aniga iyo kuwa Beeniyay xaqa ee Nicmaysan wax yarna Sug.'),
(5487, 73, 12, 'Agtannada waxaa ah caddibaad iyo Naarta Jaxiimo.'),
(5488, 73, 13, 'Iyo cunno lagu Margado iyo Cadaab daran.'),
(5489, 73, 14, '(waxay ku dhiei Arrintaasu) Maalinta Dhulka iyo Buuruhu Gilgilan, Buuruhuna ay noqon Tamuux socda.'),
(5490, 73, 15, 'Annagaa idiin dirray Rasuul markhaati ah, sidaan ugu dirray fircoonba Rasuul.'),
(5491, 73, 16, 'Fircoonna wuu Caasiyay Rasuulkii (Nabi Muuse). Firconna waxaan ku Qabannay qabasha Daran.'),
(5492, 73, 17, 'Ee seebaad Gaalloy uga dhawrsanaysaan Caddibaadda Maalinta qiyaame ee carruurta Cirroole ka Yeeli Hadaad Gaalawdaan.'),
(5493, 73, 18, 'Samaduna Maalintaas Darteed ay la Dildillaaci Balaanqaadka Eebana waa wax Dhici.'),
(5494, 73, 19, 'Aayadahani Waa waano Ee Ruuxii Doona Eeba xaggiisa jid ha uga yeesho.'),
(5495, 73, 20, 'Eebahaa wuxuu ogyahay Nabiyow inaad tukatid wax ka yar habeenka saddex Meelood laba iyo Badhkiis iyo saddex meelood meel, iyo in koox ka mida kuwa kula jira ay tukato, Eebana waa u qadaranyahay Habeenka iyo Maalintuba, wuxuuna Ogyahay inaydaan Koobi karin, wuuna Idinka toobad aqbalay ee Akhriya wixii idiin Fududaada oo quraan ah, wuxuu Eebe ogyahay inay idin ka mid Noqon kuwa Buka iyo kuwo safri iyagoo dooni Khayrka Eebe iyo kuwo u dagaallami Jidka Eebe ee akhriya wixii idiin Fududaada oo Quraan ah, Salaaddana ooga Zakadana Bixiya Eebana wax wanaagsan daynsada Waxaad naftiinna u hormarsataan oo khayr ahna waxaad ka ogaandoontaan kana Heleysaan Eeba Agtiisa Isagoo khayr ah iyo Ajri wayn, ee Eebe Dambi dhaaf waydiista waa Dambi Dhaafe Naxariiste.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, '(Nabiga) Isadadayow.'),
(5497, 74, 2, 'Istaag oo dig.'),
(5498, 74, 3, 'Eebahaana waynee.'),
(5499, 74, 4, 'Dharkaagana daahiri.'),
(5500, 74, 5, 'Xumuuntana ka hijroo (iyo Sanamyada).'),
(5501, 74, 6, 'Hana ku mannaysan Adoo Badsan (waxaad Bixisay).'),
(5502, 74, 7, 'Eebahaa Dartiisna u Samir.'),
(5503, 74, 8, 'Marka Suurka (yiaame) la Afuufo.'),
(5504, 74, 9, 'Maalintaasu waa maalin ku Daran.'),
(5505, 74, 10, 'Gaalada oon u Fududayn.'),
(5506, 74, 11, 'Isukaanna daa Aniga iyo kaan Abuuray isaga oo kaligiisa.'),
(5507, 74, 12, 'Una yeelay Xoolo Badan.'),
(5508, 74, 13, 'Iyo Wilaal Jooga oo «Darban».'),
(5509, 74, 14, 'Aan una waasiciyay Risqiga.'),
(5510, 74, 15, 'Oo haddana sii damci inaan u Badiyo.'),
(5511, 74, 16, 'Saas ma aha, ee kaas wuxuu diiday Aayaadkannaga.'),
(5512, 74, 17, 'Waxaana ku kalligi (Ku Dhibi) Cadaab daran.'),
(5513, 74, 18, 'Gaalkaasu wuu Fikiray waxbuuna Goostay.'),
(5514, 74, 19, 'Waxaana loo Lacnaday Siduu wax u goostay.'),
(5515, 74, 20, 'Haddana waxaa loo Lacnaday siduu wax u goostay.'),
(5516, 74, 21, 'Kadib wuxuu eebay (si uu Quraanka u wax yeeleeyo).'),
(5517, 74, 22, 'Wuuna Waji Macbuusay oo Uruuriyay.'),
(5518, 74, 23, 'Markaasuu jeedsaday oo is Kibriyay.'),
(5519, 74, 24, 'Isagoo Dhihi quraankani ma aha waxaan sixir la soo Guuriyay ahayn.'),
(5520, 74, 25, 'quraankani ma aha waxaan hadal Bashar ahayn.'),
(5521, 74, 26, 'waxaannu galin kaas (Naarta) saqara.'),
(5522, 74, 27, 'Ma taqaannaa Naarta Saqara.'),
(5523, 74, 28, 'Hilib iyo laf midna ma Reebto.'),
(5524, 74, 29, 'waxayna Doorisaa jidhka (oy Gubtaa).'),
(5525, 74, 30, 'waxaana kor jooga (ka shaqeeya) sagaal iyo toban malag (ama saf).'),
(5526, 74, 31, 'Ugama Aannaan yeelin naarta kuwa ka shaqeeya malaa\'ig, tirana uma aannaan yeelin inay Gaaladu ku fidnoobaan Mooyeen, iyo inay yaqiin sadaan kuwa Kitaabka la siiyay, iyo inay kordhistaan kuwii xaqa Rumeeyay Iimaan, ayna shakiyiinna kuwii Kitaab- ka la siiyay iyo Mu\'miniintu, iyo inay Dhahaan kuwa Qalbiga ka buka iyo Gaaladu Eebe muxuu uga Jeedaa Tusaalahan, saasaa Eebe u dhumiyaa Ruuxuu Doono, una Hanuuniyaa Ruuxuu Doono, wax og junuuda Eebe oon isaga Ahaynna ma jiro, waxaan la soo Sheegayna ma aha waano Dadka Mooyee.'),
(5527, 74, 32, 'Waxaa sugan dayaxa ku Dhaartaye.'),
(5528, 74, 33, 'Iyo Habeenku Markuu Jeedsado.'),
(5529, 74, 34, 'Iyo Subaxu markuu iftiimo.'),
(5530, 74, 35, 'Ee Naarta Saqara waa waxyaalaha waawayn Midkood (Balaayooyinka).'),
(5531, 74, 36, 'Waana Digniinta Dadka.'),
(5532, 74, 37, 'Ruuxii Doona inuu Hormaro, iyo Ruuxii Doona inuu dib dhaco, (waa Loodigay).'),
(5533, 74, 38, 'Naf walba waxay u xidhantahay waxay Camal fashay.'),
(5534, 74, 39, 'Hase yeeshee kuwa Midigta mari (Ehlu khayrka).'),
(5535, 74, 40, 'Waxay gali Jannooyin, waxayna iswaydiin.'),
(5536, 74, 41, 'Dambiilayaasha.'),
(5537, 74, 42, 'Iyagoo ku dhihi Maxaa idin Galiyay Naarta saqara.'),
(5538, 74, 43, 'Waxayna ugu Jawabi ka mid ma\'aannaan ahayn kuwa Tukada.'),
(5539, 74, 44, 'Mana aannaan ahayn kuwa Quudiya Miskiinka.'),
(5540, 74, 45, 'Waxaana ahayn kuwa la dhumbada kuwa xumaanta Dumban.'),
(5541, 74, 46, 'Waxaana ahayn kuwa beeniya Maalinta Abaalmarinta (Qiyaamada).'),
(5542, 74, 47, 'Ilaa ay Dhabtu Nooga Timaado (Geeridu).'),
(5543, 74, 48, 'Ma anfacayso kuwaas Shafeecada kuwa Shafeeca.'),
(5544, 74, 49, 'Maxaase u sugnaaday iy waanada uga Jeedsadaan.'),
(5545, 74, 50, 'ood u moodaa Dameero Diday.'),
(5546, 74, 51, 'oo ka Cararay libaax.'),
(5547, 74, 52, 'Haddana wuxuu dooni midkasta oo ka mida warqado Qoran (kitaab).'),
(5548, 74, 53, 'Saas ma aha Ee kama yaabayaan Aakhiro.'),
(5549, 74, 54, 'Waxaa sugan in Quraanku waana yahay.'),
(5550, 74, 55, 'Ruuxii Doona yaana waantoobi.'),
(5551, 74, 56, 'Mana Waantoobayaan in Eebe Doona Mooyee, Eebana wuxuu mutaa in laga Dhawrsado, wuxuuna Ehel u yahay inii Dambi Dhaafo.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Waxaan ku dharaan maalinta qiyaame.'),
(5553, 75, 2, 'Waxaan ku dhaaran (oo kale) nafta Dagaasha (Saaxiibkeed) (Ee waa la idin soo bixin).'),
(5554, 75, 3, 'Ma wuxuu u maleeyay Dadku inaanaan kulminhaynin lafihiisa.'),
(5555, 75, 4, 'Saas ma aha ee waan karraa inaan sinno (soona Celinno) Farihiisa.'),
(5556, 75, 5, 'Wuxuuse Dooni Dadku inay ka Gaaloobaan waxa ka horreeya.'),
(5557, 75, 6, 'Wuxuu waydiin waa Goorma Maalinta Qiyaame.'),
(5558, 75, 7, 'Markase Araggu Wareero (Taagmo).'),
(5559, 75, 8, 'Dayuxuna Madoobaado.'),
(5560, 75, 9, 'Lana Kulmiyo Qorraxd iyo Dayaxa.'),
(5561, 75, 10, 'Wuxuu Dhihi Dadku Maalintaas xagee loo cararaa.'),
(5562, 75, 11, 'Saas ma aha ee ma jiro wax la magan Galo (Eebe Mooyee).'),
(5563, 75, 12, 'Xagga Eebahaa yaana Maalintaas loo ahaan.'),
(5564, 75, 13, 'Waxaa looga warrami dadka Maalintaas wuxuu hormarsaday iyo wuxuu Dibmarsaday.'),
(5565, 75, 14, 'Ruux walbana Naftiisa isagaa ku maragfuri.'),
(5566, 75, 15, 'Si kasta Hadduu u cudurdaartana (wax uma tarto).'),
(5567, 75, 16, 'Ha Dhaqaajinin Nabiyow Carrabka inaad Quraanka ku Dagdagto (Akhriskiisa).'),
(5568, 75, 17, 'Annagaa Laabtaada ku kulmin kuuna Akhriyi.'),
(5569, 75, 18, 'Ee Markaan akhrino Raac Akhriskiisa.'),
(5570, 75, 19, 'Annagaana isasaarray Cadaayntiisa.'),
(5571, 75, 20, 'saas ma aha Ee waxaad Jeceshihiin Dadow tan soo Dagdagaysa (Adduunyada).'),
(5572, 75, 21, 'Waxaadna ka tagaysaan aakhiro.'),
(5573, 75, 22, 'Wajiyaal Maalintaas way ifi.'),
(5574, 75, 23, 'Eebeheedna way Eegi.'),
(5575, 75, 24, 'Wajiyaalna Maalintaas way kaduudsami.'),
(5576, 75, 25, 'Waxayna hubtaa in lugu fali Balaayo.'),
(5577, 75, 26, 'Saas ma aha ee markay naftu gaadho Duunta (Waqtiga Geerida).'),
(5578, 75, 27, 'Oo la Dhoho uaa ku tufi.'),
(5579, 75, 28, 'Oo uu Yaqiinsado inuu Dhiman.'),
(5580, 75, 29, 'Oo labada Dhudhun isku marmaan (sakaraad Dartiis).'),
(5581, 75, 30, 'Eebahaa Xaggiisaa la aadi.'),
(5582, 75, 31, 'Mase Rumaynin Gaalku Xaqa mana tukannin.'),
(5583, 75, 32, 'Laakiin wuu Beeniyay wuuna Jeedsaday.'),
(5584, 75, 33, 'Wuxuuna aaday Ehelkiisii isagoo kibirsan.'),
(5585, 75, 34, 'Halaag Buuse Mutaa.'),
(5586, 75, 35, 'Haddana Halaagbuu Mutaa.'),
(5587, 75, 36, 'Ma wuxuu u maleeyay Dadku in looga tagi Bilaash (Dan la\'aan).'),
(5588, 75, 37, 'Miyuusan ahayn Dhibic mani ah oo la Dhibciyo.'),
(5589, 75, 38, 'Markaas Noqoto xinjir oo Eebe Abuuro Ekeeyana.'),
(5590, 75, 39, 'Kana yeelo Labadii Nooc lab iyo Dhaddig.'),
(5591, 75, 40, 'Eebaha Saas kara miyuusan Awoodin inuu soo Nooleeyo wixii Dhintay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Dhab ahaan waxaa dadka soo maray waqti wax la sheego uusan ahayn (uusan jirin).'),
(5593, 76, 2, 'Annagaa ka abuuray dadka dhibic isku Dheehan, Imtixaan Dartiis, waxaana ka yeellay wax maqle wax arka ah.'),
(5594, 76, 3, 'Annagaa Dadka tusinnay Jidka, waxayna Noqdeen mid Shukriya (mahadiya) iyo mid Gaalnimo Badan.'),
(5595, 76, 4, 'Annagaa u darabnay Gaalada silsilado iyo katinado iyo naarta Saciira.'),
(5596, 76, 5, 'kuwa Baarriyiinta ah waxay ka cabbi weel Buuxa oo lagu Dheehay kaafuur.'),
(5597, 76, 6, 'Waa il ay ka Cabbaan Addoomada Eebe, ayna ujiidaan say Doonaan,'),
(5598, 76, 7, 'Wayna oofiyaan Nadarka, waxayna ka yaabi maalin sharkeedu fidi (qiyaamada).'),
(5599, 76, 8, 'Waxayna Quudiyaan iyagoo jecel miskiinka Agoonka iyo kan xabbisan (Dhibban).'),
(5600, 76, 9, 'Iyagoo Dhihi waxaan idiin quudinnay dar Eebe, mana idinka doonaynno Ahaalmarin iyo Mahadnaq Midna.'),
(5601, 76, 10, 'Waxaan ka Yabaynaa Eebahanno Maalin (wajigu) doorsoomi oo Daran.'),
(5602, 76, 11, 'Eebana waa ka Dhawray sharka Maalintaas, wuxuuna siiyay nuur iyo Farax.'),
(5603, 76, 12, 'Wuxuuna ku abaal Mariyay Samirkooda Dartiis jano iyo Xariir.'),
(5604, 76, 13, 'Waxayna ku Dangiigsan Sariiro Sare, iyagoon ku arkayn qorrax iyo Dhaxan midna.'),
(5605, 76, 14, 'Waxaana u dhaw hooska jannada, waxaana loo soo Raariciyay Midheheeda.'),
(5606, 76, 15, 'Waxaa lala kor socon weelal Fidla ah iyo Koobab Quruurado ah.'),
(5607, 76, 16, 'Quruuradahaas oo fidla ah si fiicanna u Qiyaasan.'),
(5608, 76, 17, 'Waxaana laga Waraabiyaa Ehlu jannaha Sharaab macaan oo lugu Dheehay sinjibiil.'),
(5609, 76, 18, 'Waana il lugu Magacaabo Salsabiil (ay ka cabbi).'),
(5610, 76, 19, 'Waxaana u ageegi wiilal waari, Haddaad Aragtana aad u malayn Jawhar la saydhay.'),
(5611, 76, 20, 'Haddaad Aragto halkaas waxaad arki nicmo iyo sharaf wayn.'),
(5612, 76, 21, 'Dharkooduna (Ehlu Jannaha) waa xariir Jilicsan oo cagaaran iyo Xariir adagba, waxaana loo xidhi fidlo Gacmaha la Gashado, Eebana wuxuu ka waraabiyaa sharaab nadiifah.'),
(5613, 76, 22, 'Kaasi waa Abaalmarintiinii Falkiiniina wuxuu Noqday mid la Mahadiyay.'),
(5614, 76, 23, 'Annagaa Quraanka Nabiyow kugu soo Dajinnay.'),
(5615, 76, 24, 'Ee ku Samir Xukunka Eebe, hana Maqlin Dambiile iyo Gaalnima Badane (warkiis).'),
(5616, 76, 25, 'Xus magaca Eebe subax iyo Galabba.'),
(5617, 76, 26, 'Habeenkana Eebe u sujuud, una tasbiixso Habeenkii (u tuko) waqti Dheer.'),
(5618, 76, 27, 'Kuwaasi waxay Jecelyihiin tan soo Dagdagaysa (Adduunyada) waxay kaga Tagayaan Gadaashoodana Maalin Culus (Qiaamada).'),
(5619, 76, 28, 'Annagaa Abuurray Dadka, Adkaynayna xubnohooda, Haddaan Doonnana waxaan ku Baddalaynaa kuwa la mid ah.'),
(5620, 76, 29, 'Suuraddani waa waano, Ee Ruuxii Doona ha ka yeesho Eebe Xaggiisa waddo.'),
(5621, 76, 30, 'Waxna ma dooni kartaan in Eebe doona mooyee, Eebana waa wax walba oge falsan.'),
(5622, 76, 31, 'Wuxuu galiyaa naxariistiisa ruuxuu doono, daalimiintana wuxuu u darbay cadaab daran.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Waxaan ku dhaartay Dabaysha wanaaga lala Diro.'),
(5624, 77, 2, 'iyo Dabaysha sida daran u dhabata.'),
(5625, 77, 3, 'Iyo Dabaysha roobka kicisa.'),
(5626, 77, 4, 'Iyo malaa\'igta Xaqa iyo Baadilka kala Bixisa.'),
(5627, 77, 5, 'iyo malaa\'igta waxyiga soo Dajisa.'),
(5628, 77, 6, 'Si Dadka loo waaniyo looguna digo.'),
(5629, 77, 7, 'Ee waxa dadow Eebe ku Yaboohay wuu dhici.'),
(5630, 77, 8, 'Markase xiddiguhu Nuurku ka tago.'),
(5631, 77, 9, 'Oo samadu Dildillaacdo.'),
(5632, 77, 10, 'Oo Buuraha la Rujiyo.'),
(5633, 77, 11, 'Oo Rasuulladii la kulmiyo (iyo Dadkoodii).'),
(5634, 77, 12, 'Maalion wayn oo loo Qabtay Darteed.'),
(5635, 77, 13, 'In dadka la kala Bixiyo Dartiis.'),
(5636, 77, 14, 'Maalinta kala Bixinta ma Taqaannaa.'),
(5637, 77, 15, 'Halaag wuxuu u sugnaaday Maalintaas kuwa xaqa Beeniyay.'),
(5638, 77, 16, 'Miyaannaan Halaagin Ummadihii hore.'),
(5639, 77, 17, 'Haddana Aannaan Raccinhaynin kuwii Dambe.'),
(5640, 77, 18, 'Saasaana ku Falaynaa Dambiilayaasha.'),
(5641, 77, 19, 'Halaagna wuxuu u sugnaaday Maalintaas kuwa xaqa Beeniya.'),
(5642, 77, 20, 'Miyaannaan Idinka Abuurin biyo tabar yar.'),
(5643, 77, 21, 'Markaasna yeellay meel ilaashan.'),
(5644, 77, 22, 'Tan iyo Muddo la yaqaanno.'),
(5645, 77, 23, 'Annagaa wax Qaddarray, wax Qaddarana u Fiican.'),
(5646, 77, 24, 'Halaagna wuxuu u sugnaaday Maalintaas kuwa xaqa Beeniya.'),
(5647, 77, 25, 'Miyaanaan ka yeelin Dhulka kafaalaqaade.'),
(5648, 77, 26, 'Nolol iyo Geeriba.'),
(5649, 77, 27, 'Waxaana yeellay Dhulka Dhexdiisa buuro sugan oo waawayn, waxaana idinka Waraabinnay biyo macaan.'),
(5650, 77, 28, 'Halaagna wuxuu u sugnaaday Maalintaas kuwa xaqa Beeniyay.'),
(5651, 77, 29, 'Aada gaalooy cadaabkii xaggiisa aad Beenin Jirteen.'),
(5652, 77, 30, 'Aada hoos (Qiiba naarta) oo saddex madax ah.'),
(5653, 77, 31, 'Oon hoosinayn, ololla (Kulaylna) wax ka tarayn.'),
(5654, 77, 32, 'Waxayna Naartu tuuri Dhimbilo daran ood Mooddo daar.'),
(5655, 77, 33, 'Ama aad Mooddo geel madaw ah.'),
(5656, 77, 34, 'Halaagna wuxuu u sugnaaday Maalintaas kuwa xaqa Beeniyay.'),
(5657, 77, 35, 'Taasina waa maalin aynan hadlaynin Dadku.'),
(5658, 77, 36, 'Lamana idmo si Ay u cudurdaartaan.'),
(5659, 77, 37, 'Halaagna wuxuu u sugnaaday maalintaas kuwa xaqa Beeniyay.'),
(5660, 77, 38, 'Maalintan waa maalintii kala Bixinta, Waana idin kulminnay idinka iyo kuwii Horreeyayba.'),
(5661, 77, 39, 'Ee Haddaad wax Dhagrikartaan i Dhagra.'),
(5662, 77, 40, 'Halaagna wuxuu u sugnaaday Maalintaas kuwa xaqa Beeniyay.'),
(5663, 77, 41, 'Kuwa Eebe ka Dhawrsada waxay ku sugnaan hoos iyo ilo;'),
(5664, 77, 42, '(Waxayna Cuni) khudaartay Doonaan.'),
(5665, 77, 43, 'Waxaana Lagu dhihi Cuna oo cabba Idinkoo shifaysan Camalkiinnii Dartiis.'),
(5666, 77, 44, 'Sasaana ku abaalMarinaynaa kuwa wanaagga fala.'),
(5667, 77, 45, 'Halaagna wuxuu u Sugnaaday kuwa xaqa Beeniya.'),
(5668, 77, 46, '(Gaalada waxaa lagu Dhihi) Cuna oo Raaxaysta wax yar, waxaad tihiin Dambiilayaale;'),
(5669, 77, 47, 'Halaagna wuxuu u sugnaaday Maalintaas kuwa xaqa Beeniyay.'),
(5670, 77, 48, 'Markii la yidhaahdo Gaalada tukada ma tukadaan.'),
(5671, 77, 49, 'Halaagna wuxuu u sugnaaday Maalintaas kuwa xaqa Beeniya.'),
(5672, 77, 50, 'Hadalkeebay Quraanka Dabadiis Rumaynhayaan.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'Maxay iska waydiinayaan (Gaaladu).'),
(5674, 78, 2, 'Warkii waynaa xaggiisa (Qiyayaamada ama Quraanka).'),
(5675, 78, 3, 'Ay isku diiddanaayeen.'),
(5676, 78, 4, 'Saas ma aha ee way ogaan donaan.'),
(5677, 78, 5, 'Haddana way ogaandoonaan (wa xa ka dhaca).'),
(5678, 78, 6, 'Miyaannaan Dhulka gogol ka yeelin.'),
(5679, 78, 7, 'Buurahana Rukummo (ka yeelin).'),
(5680, 78, 8, 'Miyannaan idinka Abuurina Noocyo.'),
(5681, 78, 9, 'Miyannaan Hurdada Raaxo idiinka dhigin.'),
(5682, 78, 10, 'Miyaannaan Habeenka asturaal idiinka dhigin.'),
(5683, 78, 11, 'Miyaannaan Maalinta waqti shaqo idiinka dhigin.'),
(5684, 78, 12, 'Korkiinna miyaannaan ka dhisin Tooddoba xoog leh.'),
(5685, 78, 13, 'Miyaannaan dhexdeeda yeelin Sirraad aad u ifi (Qorraxda).'),
(5686, 78, 14, 'Waxaan idiinka soodajinnay daruuraha biyo badan.'),
(5687, 78, 15, 'Si aan ugu soo bixinno Midho iyo Daaq.'),
(5688, 78, 16, 'Iyo Beero kulansan (badan).'),
(5689, 78, 17, 'Maalinta kalabuxu waxay leedahay waqtu go\'an.'),
(5690, 78, 18, 'Waana Maalinta Suurka la afuufi ood u imanaysaan Kooxo.'),
(5691, 78, 19, 'Samadana waa la furi waxayna yeelan Albaabo.'),
(5692, 78, 20, 'Buurahana waa la kaxayn waxayna noqon Dhalanteed.'),
(5693, 78, 21, 'Naarta Jahannamana waxay u darbantahay.'),
(5694, 78, 22, '(Gaalada) Xadgudbay meelay u noqdaan.'),
(5695, 78, 23, 'Waxayna ku nagaan Dhexdeeda Sanooyin.'),
(5696, 78, 24, 'Mana ku dhadhamiyaan dhexdeeda qabaw (ama Hurdo) ama Cabbid midna.'),
(5697, 78, 25, 'Waxaan ahayn biyo kulul iyo Dhacaanka (ehlu Naarka).'),
(5698, 78, 26, 'Waxaana lagu abaalmariyay si waafaqsan Camalkoodii.'),
(5699, 78, 27, 'Waxayna ahaayeen kuwa aan ka yaabaynin xisaab.'),
(5700, 78, 28, 'Waxayna beeniyeen Aayaadkannaga.'),
(5701, 78, 29, 'Wax walbana waxaan ku koobnay Kitaab.'),
(5702, 78, 30, 'Ee dhadhamiya Caddibaad Gaalooy waxaan caddibaad ahayn idiin kordhinmaynee.'),
(5703, 78, 31, 'Kuwa Eebe ka dhawrsada waxay leeyihiin liibaan.'),
(5704, 78, 32, 'Beero iyo Cinabyana way heli.'),
(5705, 78, 33, 'Waxayna Guursan Haween da\'yar oo isle eg.'),
(5706, 78, 34, 'Waxayna heli Cabbid wanaagsan.'),
(5707, 78, 35, 'Jannada kuma dhexmaqlaan hadal macna la\'aan ah iyo is beenin midna.'),
(5708, 78, 36, 'Waa abaalmarin Eebe uu siiyay oo ku filan.'),
(5709, 78, 37, 'Eebaha Samada iyo Dhulka iyo waxa u dhexeeya waa Allaha Raxmaan ah, Hadal wax ku soocelinna ma jiro (Qiyaamada).'),
(5710, 78, 38, 'Maalinta Jibriil iyo Malaa\'igtu Saf istaagi wax hadli ma jiro Ruux Eebe idmo mooyee oo Hadal xaq ah ku hadla.'),
(5711, 78, 39, 'Maalintaasi waa sugantahay ee Ruuxii doona Eebe xaggiisa meeluu ku noqdo ha ka samaysto.'),
(5712, 78, 40, 'Waxaannu idiinka digaynaa Cadibaad dhaw Maalinta uu arki Ruux walba waxay Gacmihiisu hor marsadeen, Gaalkuna uu dhihi Shallaytadaydee maan Carro noqdo.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Eebe wuxuu ku dhaartay Malaa\'igta Nafta si daran uga siibta (gaalada).'),
(5714, 79, 2, 'Iyo tan si fudud uga qaadda (mu\'miniinta).'),
(5715, 79, 3, 'Iyo Malaa\'igta Hawada Dabbaalata (si amar fulin).'),
(5716, 79, 4, 'Iyo tan u orotanta wanaagga.'),
(5717, 79, 5, 'Iyo kuwa Maamula amarka (Eebe).'),
(5718, 79, 6, 'Maalinta ay gilgilan afuufka (hore).'),
(5719, 79, 7, 'Oyna raaci tii dambe (waa sugantahay).'),
(5720, 79, 8, 'Qalbiyaduna Maalintaa way cabsan.'),
(5721, 79, 9, 'Araggeeduna wuu dullaysnaan.'),
(5722, 79, 10, 'Waxayna dhihi (Gaaladu) Ma raadkiibaa naloo celin (Adduunkii).'),
(5723, 79, 11, 'Markaan noqonno Lafo duugoobay.'),
(5724, 79, 12, 'Waxay dheheen taasi waa noqosho khasaara ah.'),
(5725, 79, 13, 'Waa uun Hal Qaylo kaliya.'),
(5726, 79, 14, 'Dadkuna markaas ay Dhulka kor iman.'),
(5727, 79, 15, 'Ma ku soo gaadhay warkii (Nabi) Muuse.'),
(5728, 79, 16, 'Markii Eebe uga yeedahy Toggii barakaysnaa ee Dhuwa.'),
(5729, 79, 17, 'Kuna yidhi u tag Fircoon wuu xadgudbaye.'),
(5730, 79, 18, 'Oo ku dheh ma rabtaa inaad daahir noqotid.'),
(5731, 79, 19, 'Oon Eebe Jidkiisa kugu hanuuniyo kana yaabtid.'),
(5732, 79, 20, 'Wuxuuna tusiyay (Nabi-Muuse) Aayad wayn.'),
(5733, 79, 21, 'Wuuse beeniyay oo caasiyay (Fircoon).'),
(5734, 79, 22, 'Wuuna jeedsaday isagoo xumaan la socda.'),
(5735, 79, 23, 'Wuxuuna kulmiyay (Askartii) una Qaylyay.'),
(5736, 79, 24, 'Isagoo dhihi waxaan ahay Eebihiinna sare.'),
(5737, 79, 25, 'Markaasaa Eebe Ciqaabay Aakhiro iyo Adduunba.'),
(5738, 79, 26, 'Arrintaasna waano yaa ugu sugan Ruuxii Eebe ka yaabi.'),
(5739, 79, 27, 'Ma idinkaa daran abuuridda masc Samada Eebaa dhisay.'),
(5740, 79, 28, 'Korna yeelay jurmigeeda ekeeyayna.'),
(5741, 79, 29, 'Habeenkeedana madoobeeyay Maalinteedana soo bixiyay (Ifiyay).'),
(5742, 79, 30, 'Dhulkana markaas ka dib fidiyay.'),
(5743, 79, 31, 'Biyahiisa iyo Daaqiisana soo bixiyay.'),
(5744, 79, 32, 'Buurahana sugay.'),
(5745, 79, 33, 'Si aad idinka iyo Xoolihiinnu ugu nacfidaan.'),
(5746, 79, 34, 'Markay timaaddo Musiibadii waynayd (qiyaamadu).'),
(5747, 79, 35, 'Maalintaas Dadku way xusuusan waxay faleen.'),
(5748, 79, 36, 'Naarta Jaxiimana waxaa loo muujin Ruuxii Arki.'),
(5749, 79, 37, 'Ruuxiise xadgudba.'),
(5750, 79, 38, 'Nolosha Adduunyana doorta.'),
(5751, 79, 39, 'Wuxuu ku dheelman Naarta Jaxiimo.'),
(5752, 79, 40, 'Ruuxiise ka yaaba la kulanka Eebe Naftana ka reeba Hawada (xun).'),
(5753, 79, 41, 'Wuxuu u dheelman Janno.'),
(5754, 79, 42, 'Waxay ku waydiin Saacadda (Qiyaamaha) markay sugnaan.'),
(5755, 79, 43, 'Maxaa ku ogaysiiyay waqtigeeda.'),
(5756, 79, 44, 'Eebe umbuu ku dhammaan ogaansheheedu.'),
(5757, 79, 45, 'Adugu waxaad tahay uun u digaha Ruuxii ka yaabi.'),
(5758, 79, 46, 'Waxay la mid noqon Malintay arkaan (Qiyaamada) yagon nagaanin Galab iyo Barqin mooyeen (Addunka).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Wajiguu u uruuriyay oo jeedsaday (Nabigu).'),
(5760, 80, 2, 'Inuu u yimid kii Indhaha la\'aa.'),
(5761, 80, 3, 'Miyaad ogtahay inay u dhawdahay inuu hanuuno oo Daahir noqdo.'),
(5762, 80, 4, 'Ama uu xusuusto oy xusuustu anfacdo.'),
(5763, 80, 5, 'Ruuxiise is deeqtoonaysiiya (islana waynaada).'),
(5764, 80, 6, 'Ma; Adaa u bambixi.'),
(5765, 80, 7, 'Maxaa kaa saaran hadduusan hanuunin.'),
(5766, 80, 8, 'Ruuxiise kuu yimaadda isagoo dagdagi.'),
(5767, 80, 9, 'Oo Eeba ka yaabi.'),
(5768, 80, 10, 'Miyaad ka shuqloomi.'),
(5769, 80, 11, 'Saas ma; aha ee Aayadahani waa waano.'),
(5770, 80, 12, 'Ruuxii doonana wuu ku waana qaadan.'),
(5771, 80, 13, 'Waxay ku sugantahay (waanadu) Kutub Sharaf leh.'),
(5772, 80, 14, 'Oo la kor yeelay lana Daahiriyay.'),
(5773, 80, 15, 'Oy Gacanta ku hayaan Malaa\'ig (wax qorta).'),
(5774, 80, 16, 'Sharafna leh oo baarri ah.'),
(5775, 80, 17, 'Gaalo halla lacnado Gaalnima badanaa.'),
(5776, 80, 18, 'Muxuu Eebe ka abuuray kuwaas.'),
(5777, 80, 19, 'Dhibicbuu ka abuuray wuuna ekeeyay.'),
(5778, 80, 20, 'Jidkana wuu u fudaydiyay.'),
(5779, 80, 21, 'Markaasuu dilaa Qabrina ku Asturaa.'),
(5780, 80, 22, 'Markuu doonana wuu soo bixin.'),
(5781, 80, 23, 'Saas ma; aha (ee Dadku) wixii la faray ma yeelin.'),
(5782, 80, 24, 'Dadku ha fiiriyo Cuntadiisa.'),
(5783, 80, 25, 'Inaan Roob ku soo shubno.'),
(5784, 80, 26, 'Dhulkana markaas aan jeexno.'),
(5785, 80, 27, 'Kana soo bixinno Midho.'),
(5786, 80, 28, 'Iyo cinab iyo Daaqa (Xoolaha)'),
(5787, 80, 29, 'Iyo Zeytuun iyo Timir.'),
(5788, 80, 30, 'Iyo Beerow waawayn.'),
(5789, 80, 31, 'Iyo Faakiho (khudaar) iyo Daaq.'),
(5790, 80, 32, 'Si aad ugu intifaacdaan idinka iyo xoolihiinnu.'),
(5791, 80, 33, 'Markay Qayladii darrayd timaaddo (Suurka).'),
(5792, 80, 34, 'Waa maalinta Ruuxu ka carari Walaalkiis.'),
(5793, 80, 35, 'Iyo Hooyadiis Iyo Aabihiis.'),
(5794, 80, 36, 'Iyo Haweeneydiisii iyo Carruurtiisii.'),
(5795, 80, 37, 'Ruux walbana Maalintaas waxaa shuqlin xaalkiisa.'),
(5796, 80, 38, 'Wajiyaal Maalintaas way Nuuri (Farxi).'),
(5797, 80, 39, 'Wayna Qosli oy bishaaraysan.'),
(5798, 80, 40, 'Wajiyaalna Maalintaas waxaa ka muuqan Dulli.'),
(5799, 80, 41, 'Waxaana dabooli Madow (iyo naxdin).'),
(5800, 80, 42, 'Kuwaasina waa Gaalada Faasiqiinta ah (aadna u xun).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Marka qorraxda la duubo (Qiyaamada).'),
(5802, 81, 2, 'Xiddiguhuna daataan (iyagoo Madow).'),
(5803, 81, 3, 'Buurahana la kexeeyo (La rujiyo).'),
(5804, 81, 4, 'Geelii rimmanaana la halleeyo.'),
(5805, 81, 5, 'Dugaaggana la kulmiyo (Qisaas darteed).'),
(5806, 81, 6, 'Badahana la kululeeyo.'),
(5807, 81, 7, 'Naf walbana Jidhkeedii lammaaneeyo.'),
(5808, 81, 8, 'Gabdhihii la xabaalnololiyayna la warsado.'),
(5809, 81, 9, 'Maxaa dambi ah oo loo dilay.'),
(5810, 81, 10, 'Kutubta (camalkana) la fidiyo.'),
(5811, 81, 11, 'Samadana la rujiyo.'),
(5812, 81, 12, 'Naarta Jaxiimana la huriyo.'),
(5813, 81, 13, 'Jannadana la dhaweeyo.'),
(5814, 81, 14, 'Waxay ogaan nafu waxay camal fashay.'),
(5815, 81, 15, 'Waxaan ku dhaartay «huu Eebe yidhi» Xiddigaha Qarsooma (Maalintii).'),
(5816, 81, 16, 'Ee socda iftiinka Qorraxdana ku qarsooma.'),
(5817, 81, 17, 'Iyo Habecnku markuu jeedsado.'),
(5818, 81, 18, 'Iyo subaxu markuu soo qaabilo.'),
(5819, 81, 19, 'Ee Quraanku waa hadalka Eebe, Soona raray Malag sharaf lah.'),
(5820, 81, 20, 'Xoogna leh Eeba agtiisana Daraja ku leh.'),
(5821, 81, 21, 'Laguna addeeco halkaas aaminna ah.'),
(5822, 81, 22, 'Saaxiibkiinna (Nabiguna) ma aha ku waalan.'),
(5823, 81, 23, 'Horaybuuna ugn arkay Jibriil Jahadii Caddayd (Bariga Maka).'),
(5824, 81, 24, 'Nabiguna ma aha Ruux lugu shakiyo waxa Maqan.'),
(5825, 81, 25, 'Qaraankuna ma aha hadal shaydaan la fogeeyay.'),
(5826, 81, 26, 'Ee xaggeed Gaalooy aadaysaan.'),
(5827, 81, 27, 'Quraankuna waxaan waanada caalamka ahayn ina aha (waa waano).'),
(5828, 81, 28, 'Ruuxii doona inuu toosnaado (yuuna u yahay).'),
(5829, 81, 29, 'Waxna ma dooni kartaan in Eebaha Caalamku doona mooyec.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Markay Samadu Dildillaacdo.'),
(5831, 82, 2, 'Xiddiguhuna daataan.'),
(5832, 82, 3, 'Badahana laysku furo.'),
(5833, 82, 4, 'Xabaalahana la soo bixiyo (Dadkii).'),
(5834, 82, 5, 'Waxay ogaan nafu waxay hor marsatay iyo waxay dibmarisay.'),
(5835, 82, 6, 'Dadow maxaa kugu kadiyay Eebahaaga sharafta leh.'),
(5836, 82, 7, 'Ee kii ku abuuray ah kuna ekeeyay kuna simay.'),
(5837, 82, 8, 'Siduu doonana kuu sameeyey.'),
(5838, 82, 9, 'Saas ma aha ee waxaad beeninaysaan abaal marinta.'),
(5839, 82, 10, 'Malaa\'ig baana idin ilaalinaysa.'),
(5840, 82, 11, 'Oo sharaf leh waxna qori.'),
(5841, 82, 12, 'Ogna waxaad falaysaan.'),
(5842, 82, 13, 'Kuwa wanaagsani waxay gali Naciima.'),
(5843, 82, 14, 'Kuwa xun Xunna waxay gali Jaxiimo.'),
(5844, 82, 15, 'Wayna ku guban Maalinta abaalmarinta.'),
(5845, 82, 16, 'Kamana maqnaadaan Naarta.'),
(5846, 82, 17, 'Maalinta abaalmarinta ma taqaannaa.'),
(5847, 82, 18, 'Haddana ma taqaannaa Maalinta abaalmarinta.'),
(5848, 82, 19, 'Waa Maalinta ayna u hanannin Nafna Naf kale waxba, Amarkana Maalintaas waxaa iska leh Eebe.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Wuxuu Halaag u sugnaaday kuwa xaqa nusqaamiya.'),
(5850, 83, 2, 'Waa kuwa markay miisina oofsada (dhammaystirta).'),
(5851, 83, 3, 'Markay u miisi ama u miisaamina ka nusqaamiya.'),
(5852, 83, 4, 'Miyayna u malaynaynin kuwaasi in loo soo bixin.'),
(5853, 83, 5, 'Maalin wayn darteed (Qiyaamada).'),
(5854, 83, 6, 'Maalinta Dadku u istaagi Eebaha Caalamka.'),
(5855, 83, 7, 'Saas ma aha ee Camalka kuwa xun wuxuu ku Qoranyahay Sijiin.'),
(5856, 83, 8, 'Ma taqaannaa Sijiin.'),
(5857, 83, 9, 'Waa Kitaab calaamaysan.'),
(5858, 83, 10, 'Halaagna wuxuu u sugnaaday Maalintaas kuwa beeniya (xaqa).'),
(5859, 83, 11, 'Ee ah kuwa beeniya Maalinta abaal marinta.'),
(5860, 83, 12, 'Waxaana beeniya xaqa mid xad gudba oo dambiila ah.'),
(5861, 83, 13, 'Marka Quraanka lagu akhriyana yidhaahda waa warkii Dadkii hore.'),
(5862, 83, 14, 'Saas ma aha ee waxaa fuulay Qalbigooda (mirdhiyay) waxay kasbanjireen oo Dambi ah.'),
(5863, 83, 15, 'Waxaa sugan in Eebohood Maalintaas (Qiyaame) laga xijaabi.'),
(5864, 83, 16, 'Markaasna ay gali Jaxiimo.'),
(5865, 83, 17, 'Waxaana loo odhan tani waa Naartaad ku beeninayseen Adduunka.'),
(5866, 83, 18, 'Saas xaalku ma aha ee Kitaabka iyo Camalka Dadka Wanaagsan wuxuu ku Quranyahay Cilliyiin.'),
(5867, 83, 19, 'Cilliyiin ma taqaannaa.'),
(5868, 83, 20, 'Waa Kitaab la Calaameeyay (oo Dadka fiican magaciisa lagu qoro.'),
(5869, 83, 21, 'Waxaana Gacanta ku haya Malaa\'ig Eebe u dhaw.'),
(5870, 83, 22, 'Kuwa wanaagsan waxay gali Naciima (Janno).'),
(5871, 83, 23, '(Maalinta Qiyaamana) Sariiro yay ku dangiigsanaan oy wax ka daawan.'),
(5872, 83, 24, 'Wajigooda waanad ka garan Nicmada.'),
(5873, 83, 25, 'Waxaana laga waraabin cabbid la dhawray (oo macaan).'),
(5874, 83, 26, 'Waxaana lagu khatimaa Miski iyo udgoon, ee Arrintaas ha u tartameen Tartamayaashu.'),
(5875, 83, 27, 'Waxaana lagu dheehi Cabbitaan.'),
(5876, 83, 28, 'Waa Il ay ka cabbaan kuwa Eebe u dhaw.'),
(5877, 83, 29, 'Kuwii Dambaabay (Gaaloobay) waxay ahaayeen kuwa ku qosla kuwii xaqa rumeeyay.'),
(5878, 83, 30, 'Haday maraanna way xaqirijireen.'),
(5879, 83, 31, 'Markay Guryahoodii u noqdaanna (gaaladu) waxay noqonjireen iyagoo isla Wayn (oo raaxaysan).'),
(5880, 83, 32, 'Markay arkaan Mu\'miniintana waxay dhahaan kuwaasi waa dhunsanyihiin.'),
(5881, 83, 33, 'Loomana dirin inay ilaaliyaan Mu\'miniinta.'),
(5882, 83, 34, 'Maalinta (qiyaame) kuwa rumeeyay xaqa waxay ku qosli gaalada.'),
(5883, 83, 35, 'Sariiro korkood yayna wax daawan.'),
(5884, 83, 36, 'Miyaan laga abaalmarinnin Gaalada waxay falayeen. (waa laga abaalmariyay).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Marka Samadu jeexjeexanto (qiyaamada).'),
(5886, 84, 2, 'Oy Eebe Addeecdo waana xaq addeeciddiisunee.'),
(5887, 84, 3, 'Marka dhulka la fidiyo (qiyaamada).'),
(5888, 84, 4, 'Oo uu soo tuuro waxa ku hoos jira, kana madhnaado.'),
(5889, 84, 5, 'Oos Eeba Addeeco waana xaq adeeciddaasina. (wuxuu la kulmi Ruuxwalba Camalkiisa).'),
(5890, 84, 6, 'Dadow waxaad u dhibboon ood u camalfali xagga Eebe, waadna la kulmi.'),
(5891, 84, 7, 'Ruusiise Kitaabkiisa (fiican) Midigta laga siiyo (waa mu\'minkee).'),
(5892, 84, 8, 'Waxaa la xisaabin xisaab fudud.'),
(5893, 84, 9, 'Wuxuuna ku noqon Ciddiisii (Jannada joogtay) isagoo faraxsan.'),
(5894, 84, 10, 'Ruuxiise Kitaabkiisa laga siiyo gadaashiisa (waa Dadka xune).'),
(5895, 84, 11, 'Wuxuu u yeedhan Halaag.'),
(5896, 84, 12, 'Wuxuuna gali Naarta Saciira.'),
(5897, 84, 13, 'Maxaa yeelay Markuu dadkiisii dhexjoogay yuu kabirsanaa (Adduunkii).'),
(5898, 84, 14, 'Wuxuuna malaynhayay inuusan Eebe u soo noqonaynin (qiyaame).'),
(5899, 84, 15, 'Saas ma ahee Eebe waa arkaa.'),
(5900, 84, 16, 'Waxaan ku dhaartay «buu yidhi Eebe» Midabka Qorrax dhaca.'),
(5901, 84, 17, 'Iyo Habeenka iyo wuxuu qariyay.'),
(5902, 84, 18, 'Iyo Dayaxa markuu Nuurku buux Samo.'),
(5903, 84, 19, 'Ee Dadkow waxaad koraysaan xaalado kala duwan/'),
(5904, 84, 20, 'Dadka maxaa u sugnaaday oy la rumaynwaayeen (xaqa).'),
(5905, 84, 21, 'Marka Quraanka lagu akhriyana ayna u sujuudaynin.'),
(5906, 84, 22, 'Saas ma ahee kuwa Gaaloobay waxay beenin (xaqa).'),
(5907, 84, 23, 'Eebana waa ogyahay waxay qarsan.'),
(5908, 84, 24, 'Ugu bishaaree caddibaad daran.'),
(5909, 84, 25, 'Hase yeeshee kuwa rumeeyay xaqa camalfiicanna falay waxay mudan Ajri aan go\'aynin.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Eebe wuxuu ku dhaartay Samada Xiddigaha leh (iyo Jidadkooda).'),
(5911, 85, 2, 'Iyo Maalinta laysu yaboohay (qiyaamada).'),
(5912, 85, 3, 'Iyo Dadka joogsan Maalintaas iyo waxayaalaha iman Maalintaas.'),
(5913, 85, 4, 'Ee Lacnadi ha ku dhacdo kuwii Godka lahaa (Mu\'miniinta lagu cadibo).'),
(5914, 85, 5, 'Ee ahaa Naar olal (iyo shidaal) leh.'),
(5915, 85, 6, 'Gaaladiina ay korfadiyeen Godka.'),
(5916, 85, 7, 'iyagoo daawan waxa lagu fali Mu\'miniinta «oo Jirrabaad ah».'),
(5917, 85, 8, 'Waxay ku neceen Dadkaasna ma jiro oon ahayn inay rumeeyeen Eebaha adkaada ee la mahadiyo.'),
(5918, 85, 9, 'Ee leh Samada iyo Dhulka xukunkooda, Eebana wax walba wuu u jeedaa.'),
(5919, 85, 10, 'Kuwa Mu\'miniinta Rag iyo Haweenba jirraba haddana aan tawbadkeenin waxay mudan Caddibaadda Jahannamo iyo caddibaad gubi.'),
(5920, 85, 11, 'Kuwa xaqa rumeeyey Camalfiicana falay waxay mudan Jannooyin ay dureeri dhexdeeda Wabiyaal, taasina waa liibaan wayn.'),
(5921, 85, 12, 'Qabashada Eebana (Ciqaabtiisu) way darantahay.'),
(5922, 85, 13, 'Eebe isagaa khalqiga ahaysiiyay soona celin.'),
(5923, 85, 14, 'Waana dambidhaaf badane jecel (kuwa addeeca).'),
(5924, 85, 15, 'Carshiga sharafta lahna isagaa iska leh.'),
(5925, 85, 16, 'Wuuna falaa wuxuu doono.'),
(5926, 85, 17, 'Ma ku soo gaadhay warkii Junuuddii (Gaaladii).'),
(5927, 85, 18, 'Fircoon iyo Thamuud.'),
(5928, 85, 19, 'Hase yeeshee kuwii Gaaloobay waxay ku suganyihiin beenin (xaqa).'),
(5929, 85, 20, 'Eebana waa koobay (kamana baxsadaan).'),
(5930, 85, 21, 'Waase Quraan sharaf leh.'),
(5931, 85, 22, 'Oo ku Qoran Ioox la dhawray (looxulmaxfuudka).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Eebe wuxuu ku dhaartay Samada iyo Xiddigga guura.'),
(5933, 86, 2, 'Ma taqaan Xiddigga guura.'),
(5934, 86, 3, 'Waa Xiddig ifa.'),
(5935, 86, 4, 'Ee Nafwalba waxaa kor jooga ilaaliye (Malag).'),
(5936, 86, 5, 'Ee Dadku ha dayo waxa laga abuuray.'),
(5937, 86, 6, 'Waxaa laga abuuray Biyo booda.'),
(5938, 86, 7, 'Kana soobaxa Dhabar iyo laab dhexdood.'),
(5939, 86, 8, 'Eebana waa karaa soo celinta Dadka (qiyaamada).'),
(5940, 86, 9, 'Maalinta la imtixaani waxa (Laabta) ku qarsoon.'),
(5941, 86, 10, 'Dadkuna Xoog ma leh (Maalintaas) iyo wax u gargaaara midna.'),
(5942, 86, 11, 'Eebana wuxuu ku dhaartay Samada Roobka leh.'),
(5943, 86, 12, 'Iyo Dhulka jeexma (waxna ka soo baxo).'),
(5944, 86, 13, 'Ee Quraanku waa hadal wax kala bixiya (wanaagga iyo Xumaanta).'),
(5945, 86, 14, 'Ciyaarra ma aha (ee waa dhab).'),
(5946, 86, 15, 'Gaaladu waxbay dhakri.'),
(5947, 86, 16, 'Annaguna waanu kaydi (Abaalmarin).'),
(5948, 86, 17, 'Ee u sug Gaalada wax yar.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Waynee Magaca Eebe ee Sarreeya.'),
(5950, 87, 2, 'Eebaha abuuray khalqiga ekeeyayna.'),
(5951, 87, 3, 'Eebaha wax walba qaddaray Jidkana tusiyey.'),
(5952, 87, 4, 'Eebaha soo bixiya Daaqa.'),
(5953, 87, 5, 'Kana dhiga markaas ka dib caws ingagagan oo Madow.'),
(5954, 87, 6, 'Waxaan kuu akhriyaynaa Quraanka halmaaminamaysid.'),
(5955, 87, 7, 'Wuxuu Eebe doono mooyee, Eebana waa ogyahay waxa muuqda iyo waxa qarsoonba.'),
(5956, 87, 8, 'Waxaana kuu fududaynhaynaa Waddo fiican.'),
(5957, 87, 9, 'Ee wax waani (Nabiyow) markay wax tari waanadu.'),
(5958, 87, 10, 'Waxaa waantoobi Ruuxa Eebe ka yaabee.'),
(5959, 87, 11, 'Waxaana ka fogaan waanada Ruuxa xune.'),
(5960, 87, 12, 'Ee ah mid gali Naar wayn.'),
(5961, 87, 13, 'Oon kuna dhimanayn dhexdeeda kuna noolanayn.'),
(5962, 87, 14, 'Waxaana liibaanay Ruuxii Daahir noqda.'),
(5963, 87, 15, 'magaca Eebana Xusa, Salaaddana tukada.'),
(5964, 87, 16, 'Saas ma\'aha ee Dadku wuxuu doortay nolosha Adduunyo ee dhaw.'),
(5965, 87, 17, 'Aakhiraase khayr badan baaqina ah (hadhi).'),
(5966, 87, 18, 'Arrintaasuna waxay ku sugantahay Kutubtii hore.'),
(5967, 87, 19, 'Kutubtii (Nabi) Ibraahim iyo (Nabi) Muuse.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'Ma ku soo gadhay Qiyaamada warkeedii.'),
(5969, 88, 2, 'Wajiyaal Maalintaas way dullaysnaan.'),
(5970, 88, 3, 'Waxayna qaban Shaqo dhib ah.'),
(5971, 88, 4, 'Waxayna gali Naar kulul.'),
(5972, 88, 5, 'Waxaana laga waraabin il kulayl daran.'),
(5973, 88, 6, 'Wax cunto ahna kama helaan oon ahayn cunno xun (oo wax dhibta).'),
(5974, 88, 7, 'Taasoon waxna cayilinhaynin Gaajana wax ka taraynin.'),
(5975, 88, 8, 'Wajiyaalna maalintaas way Nicmaysnaan (Waa mu\'miniinta).'),
(5976, 88, 9, 'Waxayna ka raalli noqotay camalkeedii.'),
(5977, 88, 10, 'Janno sarana way gali.'),
(5978, 88, 11, 'Kumana maqasho Jannada hadal macna darro.'),
(5979, 88, 12, 'Waxaana ah dhexdeed il socota.'),
(5980, 88, 13, 'iyo Sariiro la koryeelay.'),
(5981, 88, 14, 'Iyo koobab la hor dhigay (ehlu Jannaha).'),
(5982, 88, 15, 'Iyo Barkimooyin la safay.'),
(5983, 88, 16, 'Iyo gogol la fidiyay.'),
(5984, 88, 17, 'Miyayna fiirinaynin sida Deela loo abuuray.'),
(5985, 88, 18, 'Iyo sida Samada loo koryeelay.'),
(5986, 88, 19, 'Iyo sida Ruuraha loo taagay,'),
(5987, 88, 20, 'Iyo sida dhulka loo fidiyay.'),
(5988, 88, 21, 'Ee Waani dadka waxaad tahay waaniyee (Nabiyow).'),
(5989, 88, 22, 'Mana tihid mid xoog ku leh (qasab).'),
(5990, 88, 23, 'Hase yeeshee Ruuxii xaqa ka jeedsada oo Gaalooba (ku soo celi xaqa).'),
(5991, 88, 24, 'Eebana wuu caddibi Caddibaadda u wayn.'),
(5992, 88, 25, 'Xagganagayna u soo noqon (khalqigu).'),
(5993, 88, 26, 'Annagaana xisaabin.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Eebe wuxuu ku dhaartay Waabariga.'),
(5995, 89, 2, 'Iyo Habenanada Tobanka ah (Arrafo).'),
(5996, 89, 3, 'Iyo Tirada siman iyo tan is dheer.'),
(5997, 89, 4, 'Iyo Habeenku markuu tagayu.'),
(5998, 89, 5, 'Miyayna arrintaasu ahayn wax loogu dhaarto Qof caqli leh.'),
(5999, 89, 6, 'Ma ku soo Gaadhay sida Eebahaa ku falay Caad.'),
(6000, 89, 7, 'Ee ahaa Irama xooggana lahaa.'),
(6001, 89, 8, 'Wax la mid ahna aan iugu abuurin dhulka.'),
(6002, 89, 9, 'Iyo Thamuud-kii ahaa dhagaxa kuwa ku jarjara Waadiga.'),
(6003, 89, 10, 'lyo Fircoonkii dhakawyada lahaa.'),
(6004, 89, 11, 'kuwiina ahaa ku xadgudbay dhulka.'),
(6005, 89, 12, '12 Kuna badiyay dhexdiisa fassaadka.'),
(6006, 89, 13, 'Markaasaana Eeba ku shubay Caddibaad daran.'),
(6007, 89, 14, 'Maxaayeelay Eebe waa u darbanyahay (abaalmarinta).'),
(6008, 89, 15, 'Marks Eebe Dadka imtixaano xoolana ugu nicmeeyo wuxuu dhihi (kan xun) waa lai sharrifay (isagoo faani).'),
(6009, 89, 16, 'Haddiise Eebe imtixaano oo Xoolaha ku yareeyo wuxuu dhihi Eebaa i dulleeyay.'),
(6010, 89, 17, 'Saas ma aha ee waxaydaan dhaqaalaynayn Agoonka.'),
(6011, 89, 18, 'Mana fartaan in masaakiinta wax la siiyo.'),
(6012, 89, 19, 'Waxaadna aad u cuntaan dhaxal xaq darro.'),
(6013, 89, 20, 'Xoolahana waxaad u jeceshihiin si daran.'),
(6014, 89, 21, 'Saas ma aha ee marka dhulka la burburiyo.'),
(6015, 89, 22, 'Eebe iyo Malaguna la yimaaddo amarkiisa Malaa\'igtoo safan.'),
(6016, 89, 23, 'Naarta Jahannamana Maalintaas la keeno, Dadku waxay xusuusan waxay faleen, Maxayse xusuusi tari.'),
(6017, 89, 24, 'Waxay dhihi shallaytee maan wanaag hormarsado.'),
(6018, 89, 25, 'Malintaas sida Eebe wax ruux u Cadaabi ma jiro.'),
(6019, 89, 26, 'Wax sida Eebe wax u xidhina ma jiro.'),
(6020, 89, 27, 'Naftase xaqa rumaysay ee Xasillooneey.'),
(6021, 89, 28, 'U noqo Eebaha adoo raalli ah lagana raalli noqday.'),
(6022, 89, 29, 'dhexgalna Addoomadayda (wanaagsan).'),
(6023, 89, 30, 'Galna Jannadayda.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Eebe wuxuu ka dhaaran Magaalada «Makaad ee Sharrifan».'),
(6025, 90, 2, 'Ood Nabiyow daggantahay dhexdeeda.'),
(6026, 90, 3, 'Iyo Nabi Aadam iyo carruurtiisa «Dadka».'),
(6027, 90, 4, 'Ee Eebe Dadka wuu Abuuray iyagoo dhib iyo shaqo (Adduunka kala kulmi).'),
(6028, 90, 5, 'Ma waxayse u maleeyeen inaan cidna awood ku lahayn (dadku).'),
(6029, 90, 6, 'Dadka (Gaalada) waxay odhan waxaan bixiyey xoolo badan (si xaqa loo horjoogsado).'),
(6030, 90, 7, 'Ma wuxuu u maleeyay (xaq diiduhu) inaan cidna arkaynin.'),
(6031, 90, 8, 'Miyaannaan u yeelin Laba indhood.'),
(6032, 90, 9, 'Iyo Carrab iyo Laba bishimood.'),
(6033, 90, 10, 'Oo miyaannaan u caddaynin Labada Jid (khayrka iyo sharka).'),
(6034, 90, 11, 'Ee Dadku muu jiidho Jiirta.'),
(6035, 90, 12, 'Ma taqaannaa Jiirta.'),
(6036, 90, 13, 'Waa qoor la xoreeyo.'),
(6037, 90, 14, 'Iyo cunno la siiyo maalin gaajo.'),
(6038, 90, 15, 'Agoon kuu dhaw.'),
(6039, 90, 16, 'Ama Miskiin Dhulka ku dhagay (dhibaato darteed).'),
(6040, 90, 17, 'Markaas la noqdo kuwa Rumeeyay xaqa, isuna dardaarma Samirka, isuna dardaarma naxariista.'),
(6041, 90, 18, 'Kuwaasina waa kuwa Midigta mari.'),
(6042, 90, 19, 'Kuwaka Gaaloobayse Aayaadkannaga waa kuwa jidka xun mari.'),
(6043, 90, 20, 'Waxaana lagu dabooli Naar.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Eebe wuxuu ku dhaartay Qorraxda iyo Barqinkeeda.'),
(6045, 91, 2, 'Iyo Dayuxu markuu Qorraxda raaco.'),
(6046, 91, 3, 'Iyo Maalintu markay ifiso.'),
(6047, 91, 4, 'Iyo Habeenku markuu daboolo (madoobaado).'),
(6048, 91, 5, 'Iyo Samada iyo say u dhisantahay.'),
(6049, 91, 6, 'Iyo Dhulka iyo suu u fidsanyahay.'),
(6050, 91, 7, 'Iyo Nafta iyo sida Eebe u ekeeyay.'),
(6051, 91, 8, 'Una tusiyay Jidka xun iyo ka Eebe kayaabiddaba.'),
(6052, 91, 9, 'Ee waxaa liibaanay Ruxii Naftiisa Daarhiriya.'),
(6053, 91, 10, 'Waxaana khasaaray Ruuxii Naftiisa xumaan dhex galiya.'),
(6054, 91, 11, 'Thamuud waxay beeniyeen (xaqii) kibir dartiis.'),
(6055, 91, 12, 'Marku tagay koodi xumaa (si uu hasha u dilo).'),
(6056, 91, 13, 'Oo Rasuulkii Eebana ku yidhi, iska daaya Hasha Eebe iyo Cabbideeda.'),
(6057, 91, 14, 'Wayna beeniyeen Rasuulkii Hashiina way Dileen markaasaa Eebe si daran u caddibay Dambigooda dartiis.'),
(6058, 91, 15, 'Isagoon Eebe ka yaabaynin cidhib (iyo wax yeello)');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Eebe wuxuu ku dhaartay Habeenku markuu wax qariyo.'),
(6060, 92, 2, 'Iyo markay Maalintu Muuqato (ifto).'),
(6061, 92, 3, 'Iyo Abuuridda Dadka Lab iyo dhaddigba.'),
(6062, 92, 4, 'In Camalkiinnu (dadow) kala duwanyahay.'),
(6063, 92, 5, 'Ruuxiise bixiya (xuquuqda) Eebana ka dhawrsada.'),
(6064, 92, 6, 'Rumeeyana tii wanaaga badnayd (ciwal).'),
(6065, 92, 7, 'Waxaanni u fududaynaa wanaagga (iyo Jidka fiican).'),
(6066, 92, 8, 'Ruuxiise ku bakhayla Xoolaha isna deeqtoonaysiiya «iskibriya».'),
(6067, 92, 9, 'Reeniyana Tii wanaaga badnayd (ciwal).'),
(6068, 92, 10, 'Waxaan u fududaynaa Jidka xun.'),
(6069, 92, 11, 'Xoolihiisuna wax uma taraan markuu halaagsamo.'),
(6070, 92, 12, 'Annagaa wax hanuuninna.'),
(6071, 92, 13, 'Aakhariyo Addunbana Annagaa iska leh.'),
(6072, 92, 14, 'Waxana idiinka digaynaa Naar kulayl daran.'),
(6073, 92, 15, 'Oosan galaynin Ruux xun mooyeen.'),
(6074, 92, 16, 'Oo xaqa beeniyay jeedsadayna.'),
(6075, 92, 17, 'Waxaana ka fogaada (galidda naarta) Ruuxii Eeba ka dhawrsada.'),
(6076, 92, 18, 'Ee bixiya Xoolihiisa si uu u daahirnoqdo.'),
(6077, 92, 19, 'Ruux wax ugu nicmeeyayna oo abaalmarinna ma uusanjirin.'),
(6078, 92, 20, 'Ee Wuxuu u bixin dar Eebaha sareeya.'),
(6079, 92, 21, 'Wuuna raalli noqondoonaa Ruuxaasi.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Eebe wuxuu ku dhaartay Barqinka.'),
(6081, 93, 2, 'Iyo Habeenku markuu xasilo (Madoobaado).'),
(6082, 93, 3, 'Inaan Eebe ka tagin Nabiyow kuuna cadhoonin.'),
(6083, 93, 4, 'Aakharana kaaga khayr badan Aduunka.'),
(6084, 93, 5, 'Eebaana ku siindoona (wanaag) waadna raalli noqon.'),
(6085, 93, 6, 'Miyuusan ku helin Eebahaa Adigoo Agoon ah oosan ku dhawaynin.'),
(6086, 93, 7, 'Miyuunase ku helin Adigoo baadi ah oosan ku hanuuninin.'),
(6087, 93, 8, 'Miyuunase ku helin Adigoo caydhi ah oosna ku hodminin (kuu waxyoonin).'),
(6088, 93, 9, 'Ee Agoonta ha xoogin (hana dhihin).'),
(6089, 93, 10, 'Ka wax su\'aalina ha canaanan.'),
(6090, 93, 11, 'Nicmada Eebahaana ka sheekee (ku mahadi).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Miyannaan kuu waasicinin Laabta (ku nuurinin).'),
(6092, 94, 2, 'Oonaan ka dajinin culayskii.'),
(6093, 94, 3, 'dhabarka kaa cusleeyay.'),
(6094, 94, 4, 'Miyaannaan kor yeelin magacaaga (xusiddaada).'),
(6095, 94, 5, 'Cidhiidhina waxaa ku dhaw fudayd (faraj).'),
(6096, 94, 6, 'Cidhiidhina waxaa ku dhaw fudayd.'),
(6097, 94, 7, 'Ee markaad Salaadda dhammayso isku dhib (Samo fal).'),
(6098, 94, 8, 'Eebana (Khayr) ka rajee.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Eebe wuxuu ka dhaartay Tiinka iyo Zeytuunka.'),
(6100, 95, 2, 'Iyo Dhuuri Siina.'),
(6101, 95, 3, 'Iyo Magaaladan Aaminka ah (Maka).'),
(6102, 95, 4, 'Ee waxaana ku abuurray Dadka Muuqal toosan.'),
(6103, 95, 5, 'Kaddibna waxaan u celinaynaa hoos (gabow dartiis).'),
(6104, 95, 6, 'Hase yeeshee kuwa rumeeyey xaqa camalwanaagsanna falay, waxay mudan Ajri aan go\'aynin.'),
(6105, 95, 7, 'Ee Maxaa Gaalada u sababay beeninta Qiyaamada intaas ka dib.'),
(6106, 95, 8, 'Miyuusan Eebe ahayn kan ugu xukun wanaagsan. (Eebaa xukun wanagsan).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Wax ku akhri Nabiyow Magaca Eebe ee abuuray (khaliqiga).'),
(6108, 96, 2, 'Dadkana ka abuuray Calaq.'),
(6109, 96, 3, 'Akhri Eebana waa kan Sharafta lehe.'),
(6110, 96, 4, 'Ee Dadka baray sida Qalinka wax loogu qoro.'),
(6111, 96, 5, 'Dadkana baray waxayna aqoonin.'),
(6112, 96, 6, 'Saas ma aha ee Dadku (Gaaladu) waa gudbeen.'),
(6113, 96, 7, 'Markuu isku ogaaday Hodontinnimo.'),
(6114, 96, 8, 'Eebaha xaggiisaase loo noqon.'),
(6115, 96, 9, 'Ka warrama midka ka reebaya.'),
(6116, 96, 10, 'Addoon (Eebe) oo tukan Salaadda.'),
(6117, 96, 11, 'Bal ka warrama hadduu ka laga reebi hanuunsanyahay (waa Nabiga).'),
(6118, 96, 12, 'Ama uu fari Eeba ka yaabid.'),
(6119, 96, 13, 'Balse ka warrama hadduu midka wax ka reebi uu beeniyay (xaqa) kana jeedsaday (waa AbuuJahal iyo wixii la mid ahe).'),
(6120, 96, 14, 'Miyuusan ogayn in Eebe arko.'),
(6121, 96, 15, 'Saas ma aha ee hadduusan joogin waxaannu qaban Foodda.'),
(6122, 96, 16, 'Food beenaleey ah ghafna badan.'),
(6123, 96, 17, 'Ha u yeedho gargaarihiisa.'),
(6124, 96, 18, 'Waxaanu u yeedhi Malaa\'ig darane.'),
(6125, 96, 19, 'Saas ma aha ee warkiisa ha maqlin Sujuudna una dhawow (Eebe).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Annagaa soo dajinnay Quraanka Habeenka LaylatulQadarka ah (sharafta leh).'),
(6127, 97, 2, 'Ma taqaanna LaylatulQadarka.'),
(6128, 97, 3, 'Waa Habeen ka khayr badan Kun Bilood (oo kale).'),
(6129, 97, 4, 'Waxaana soo daga Malaa\'igta (kale) iyo Malaku Jibriil habecnkaas idanka Eebe dartiis iyo amarkiisa.'),
(6130, 97, 5, 'Waana Habeen nabadgalyo inta waagu ka haryi.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Ma aha Kuwii Gaaloobay ee ahaa ehlu kitaabka (Yahuud iyo Nasaara) iyo Gaaladii kuwo ka taga (xumaanta) intuu uga yimaaddo wax cad (xuja ah).'),
(6132, 98, 2, 'Oo ah Rasuul Eebc oo akhriyi Kutub daahir ah.'),
(6133, 98, 3, 'Oo xukun fiicani ku yaal.'),
(6134, 98, 4, 'Mona kala tagin Gaalada ehlu Kitaabka ah intuu uga yimaaddo xaq cad (xasad dartiis).'),
(6135, 98, 5, 'Dadkana waxaan Eebe caabudiddiisa ahayn lama farin iyagoo u kali yeeli Eebe Diinte, toosanna, Salaaddana oogi Zakadana bixin, Saasina waadiinta toosan.'),
(6136, 98, 6, 'Kuwa Gaaloobay oo ehlu Kitaabka ah iyo Gaaladii waxay gali Naarta Jahannama ayna Ini waari dhexdeeda, kuwaasina waa kuwa ugli sharbadan khalqiga.'),
(6137, 98, 7, 'Kuwa rumeeyayse xaqa Camalfiicanna falay kuwaasi waa kuwa ugu khayr badan khalqiga.'),
(6138, 98, 8, 'Abaalkooda ay Eebe agtiisa kii leeyihiin waa Jannooyin Nagaadi, dhexdeedana ay dureeri Wabiyaal ay kuna waari dhexdeeda waligood, Eebaa ka raalli noqday iyana way ka raali noqdeen, arrintaasna waxay u sugnaatay Ruusii Eeba ka yaaba.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Marka DHulka la gilgilo.'),
(6140, 99, 2, 'Dhulkuna soo bixiyo culaykiisii (wixii ku aasnaa).'),
(6141, 99, 3, 'Dadkuna (Gaaladuna) dhahaan maxay leedahay.'),
(6142, 99, 4, 'Maalintaas waxay ka sheekayn warkeeda.'),
(6143, 99, 5, 'Maxaayeelay Eebaa u waxyooday.'),
(6144, 99, 6, 'Maalintaas Dadku way soo bixi iyagoo kala tagsan (xagga Diinta iyo Camalkaba) si ay u arkaaan camalkooda.'),
(6145, 99, 7, 'Ruuxiise wax yaroo khayra fala wuu arki.'),
(6146, 99, 8, 'Kiise wax yar oo shar fala wuu arki.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Eebe wuxuu ku dhaartay Fardaha Jahaadka u orda iyagoo qaylyi.'),
(6148, 100, 2, 'iyo kuwa dabka Qoobkooda ku ifiya (guuraha).'),
(6149, 100, 3, 'Iyo kuwa Cadawga weerara Subaxdii.'),
(6150, 100, 4, 'Ee Boodhka kiciya.'),
(6151, 100, 5, 'Cadawgana dhexgala.'),
(6152, 100, 6, 'Ee Dadku Eebohood waa diideen.'),
(6153, 100, 7, 'Arrintaasna Dadku wuu ogyahay.'),
(6154, 100, 8, 'Dadkuna Xoola jacaylka waa ku daranyahay.'),
(6155, 100, 9, 'Miyuuna ogayn Marka Xabaalaha waxa ku jira la soo bixiyo.'),
(6156, 100, 10, 'Laabaha waxa ku qarsoonna la muujiyo.'),
(6157, 100, 11, 'In Eebohood Maalintaas ogyahay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'Qaaricada (qalbiga garaacda).'),
(6159, 101, 2, 'Qaarica maxay tahay.'),
(6160, 101, 3, 'Mase taqaannaa Qaarico.'),
(6161, 101, 4, 'Waa Maalinta Dadku noqon sida Baalallay.'),
(6162, 101, 5, 'Buuruhuna ay noqon sida Suuf kala firdhay.'),
(6163, 101, 6, 'Ruuxiise miisaankiisa (fiican) cuslaado.'),
(6164, 101, 7, 'Wuxuu ku noolaan nolol raalli ah.'),
(6165, 101, 8, 'Ruuxiise uu fududaado Miisaankiisa (fiican).'),
(6166, 101, 9, 'Wuxuu u hoydaa (Naarta) Haawiya.'),
(6167, 101, 10, 'Ma taqaannaa Haawiya.'),
(6168, 101, 11, 'Waa Naar kulayl daran.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Waxaa idin shuqliyay Dadow wax badsasho (iyo isu faanid),'),
(6170, 102, 2, 'Intaad Xabaalaha ka tagtaan (dhimataan).'),
(6171, 102, 3, 'Saas ma aha ee waad ogaandoontaan.'),
(6172, 102, 4, 'Haddana saas ma ehee waad ogaandoontaan (Faanka cidhibtiisa).'),
(6173, 102, 5, 'Haddaad dhab ahaan wax u ogtihiin (Waad dayn lahaydeen xumaanta).'),
(6174, 102, 6, 'Dhab ahaan baad u arkaysaan (Naarta) Jaxiima,'),
(6175, 102, 7, 'Ka dibna waxaad u arkaysaan Nartaas si yaqiin ah.'),
(6176, 102, 8, 'Waxaana laydin warsan Maalintaas Nicmadii.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Eebe wuxuu ku dhartay Waqtiga Casarka ah.'),
(6178, 103, 2, 'In Dadku khasaaro ku sugunyahay.'),
(6179, 103, 3, 'Marka laga reebo kuwa rumeeyay xaqa ee Camal fiican fala, isuna dardaarma xaqa, isuna dardaarma Samirka.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Halaag Wuxuu u Sugnaaday xanle wax ceebeya oo Dhan,'),
(6181, 104, 2, 'Oo Xoolaha kulmiya (xaqana ku bixinin).'),
(6182, 104, 3, 'Ma wuxuu malayn in xoolihiisu waarin.'),
(6183, 104, 4, 'Saas ma aha ee waxaa lagu tuuri (Naarta) Xudhama.'),
(6184, 104, 5, 'Ma taqaannaa Xudhama.'),
(6185, 104, 6, 'Waa Naarta Eebe ee la huriyay.'),
(6186, 104, 7, 'Gubaysana Quluubta (Kulayl dartiis).'),
(6187, 104, 8, 'Waana lagu dabooli Naartaas Gaalada.'),
(6188, 104, 9, 'Tiraar la fidiyay yayna ahaan dhexdeeda.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Ma Ogtahay sida Eeba ku falay asaxaabtii Maroodiga «Colkii».'),
(6190, 105, 2, 'Miyaan Eebe ka yeelin dhagartoodii wax dhumay.'),
(6191, 105, 3, 'Oosan ku dirin korkooda Shimbiro kooxa ah.'),
(6192, 105, 4, 'Kuna tuuraysa Dhagaxyo la kululeeyay.'),
(6193, 105, 5, 'Markaas Eebe ka yeelay Bal la daaqay sidiis.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'Caadaysigii Qureesheed.'),
(6195, 106, 2, 'Ay ka caadayeesheen Safarka Xagaaga iyo Jilaalka darteed.'),
(6196, 106, 3, 'Ha u caabudeen Eebeha Beydkan (Kacbada).'),
(6197, 106, 4, 'Eebahi ka quudiyay gaajo, kana aamingaliyay cabsi.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Ka warrama kan beenin Abaalmarinta (Qiyaamada).'),
(6199, 107, 2, 'Kaas xooga Agoonta (xageeda).'),
(6200, 107, 3, 'Aan kuna boorrinaynin quudinta miskiinka.'),
(6201, 107, 4, 'Waxaa halaag u sugnaaday kuwa tukan (waa Munaafiqiinta).'),
(6202, 107, 5, 'Ee haddana halmaansan Salaaddooda.'),
(6203, 107, 6, 'Dadkana is tusiya.'),
(6204, 107, 7, 'Una diida alaabta (Laysugu kaalmeeyo).');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Annagaa ku Sinnay Nabiyow Waadi Janno (iyo khary badan).'),
(6206, 108, 2, 'Ee tuko Eebaha dartiis, waxna u gawrac.'),
(6207, 108, 3, 'Ruuxii ku necebna waa mid cidhib beelay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'Waxaad dhahdaa, Gaalooy.'),
(6209, 109, 2, 'Ma caabudayo waxaad caabudaysaan.'),
(6210, 109, 3, 'Idinna ma caabudaysaan Eebahaan caabudi.'),
(6211, 109, 4, 'Mana caabudin waxaad caabuddeen,'),
(6212, 109, 5, 'Idinkuna (hadda) ma caabudaysaan Eebahaan caabudi.'),
(6213, 109, 6, 'Idinkana Diintiina yaad leedihiin, Anna Diintaydaan leeyahay.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Markuu yimaaddo gargaarkii Eebe iyo Fatxiga (Furashada maka).'),
(6215, 110, 2, 'Ood aragtid Dadkoo Diinta Eebe u soo gali Kooxo.'),
(6216, 110, 3, 'U tasbiixso Eebe adoo ku mahadin Dambidhaafna waydiiso illen waa dambidhaaf badanee.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Waxaa khasaaray Gacmihii Abuulahab, halaagmayna.'),
(6218, 111, 2, 'Waxna uma tarin Xoolihiisii iyo wuxuu kasbadayna.'),
(6219, 111, 3, 'Wuxuuna gali Naar Olal leh.'),
(6220, 111, 4, 'Haweenaydiisii Qoryaha xambaari jirtayna (waxay gali Naar).'),
(6221, 111, 5, 'Luqunteedana waxaa la yeeli Xadhig Liif ah.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'Waxaad dhahdaa (Nabiyow) Eebe waa Kali (wehelna ma leh).'),
(6223, 112, 2, 'Waana Sayid (deeqtoon).'),
(6224, 112, 3, 'Eebe wax ma dhalin, isna lama dhalin.'),
(6225, 112, 4, 'Wax la mid ahna ma jiro.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'Waxaad dhahdaa (Nabiyow) waxaan ka magangalay Eebeha Subaxa (abuuray).'),
(6227, 113, 2, 'Wixii shar leh sharkiisa.'),
(6228, 113, 3, 'Iyo Habeenka sharkiisa markuu madoobaado.'),
(6229, 113, 4, 'Iyo sharka Sixirrowga wax ku tufa guntimaha.'),
(6230, 113, 5, 'Iyo sharka Xaasidka wax Xasda.');
INSERT INTO `so_abduh` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'Waxaad dhahdaa «Nabiyow» waxaan ka magangeley Eebaha dadka barbaariya.'),
(6232, 114, 2, 'Ee dadka xukuma.'),
(6233, 114, 3, 'Ee ah Ilaaha dadka.'),
(6234, 114, 4, 'Sharka «iyo xumaanta» waswaasiyaha qarsoon.'),
(6235, 114, 5, 'Ee waswaasiya laabta «Qalbiga» dadka.'),
(6236, 114, 6, '«Kuwaas wax waswaasinayna» oo ah jinni iyo insiba.');
