INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'Me emër të Allahut, Mëshirues dhe Përdëllimtar i Madh.'),
(2, 1, 2, 'Çdo lavdërim i përket Allahut (Perëndisë), Zotit të Gjithësisë'),
(3, 1, 3, 'Mëshirues dhe Përdëllimtar i Madh.'),
(4, 1, 4, 'Sunduesit të ditës së gjukimit.'),
(5, 1, 5, 'Vetëm Ty të adhurojmë dhe vetëm prej Teje ndihmë kërkojmë!'),
(6, 1, 6, 'Udhëzona në rrugë të drejtë.'),
(7, 1, 7, 'Në rrugën e atyre që u ke dhuruar mirësi. E jo në (rrugën) e atyre që kanë shkaktuar pezmin (Tënd), as në rrugën e atyre që janë të humbur!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'Elif, Lam, Mim.'),
(9, 2, 2, 'Ky libër, është i padyshimtë, udhëzues për të gjithë ata që i druajnë dënimit të Perëndisë.'),
(10, 2, 3, 'të cilët u besojnë gjërave të padukshme (në bazë të argumenteve mendore) dhe bëjnë namaz dhe japin nga ajo që Ju kemi dhënë;'),
(11, 2, 4, 'të cilët besojnë që të ka zbritur Ty (Kur’ani) dhe atyre para Teje (pejgamberëve tjerë) dhe që padyshim i besojnë jetës tjetër.'),
(12, 2, 5, 'Ata janë, me të vërtetë, në rrugën e Zotit dhe vetëm ata janë të shpëtuar.'),
(13, 2, 6, 'Me të vërtetë, për ata që janë (këmbëngulës) në mohim, është njësoj si i paralajmërove ti apo nuk i paralajmërove – ata nuk besojnë.'),
(14, 2, 7, 'Perëndia ua ka vulosur atyre zemrat dhe veshët dhe në sy kanë perde, dhe ata i pret denimi i madh.'),
(15, 2, 8, 'Ka disa njerëz që thonë: “Besojmë në Perëndinë dhe jetën tjetër!” – por, ata nuk janë besimtarë.'),
(16, 2, 9, 'Ata përpiqen të mashtrojnë Perëndinë dhe ata që besojnë, por vetëm mashtrojnë veten, e ketë nuk e vërejnë.'),
(17, 2, 10, 'Zemrat e tyre janë të sëmura (me injorancë) e Perëndia ua shtoi atyre sëmundjen; ata i pret dënim i ashpër, për shkak se gënjejnë.'),
(18, 2, 11, 'Kur t’u thuhet: “mos bëni çrregullime në Botë!” ata thonë: “Na vetëm zbatojmë rregullat në Botë!”'),
(19, 2, 12, 'Ah! Me të vërtetë, ata shkaktojnë çrregullime, por nuk i vërejnë.'),
(20, 2, 13, 'Kur t’u thuhet atyre: “Besoni edhe ju ashtu siç kanë besuar njerëzit (e ndershëm)” – ata (jobesimtarët) thonë: “Vallë! Si të besojmë ne ashtu siç kanë besuar njerëzit mendjelehtë?” – Ah! Me të vërtetë, ata janë mendjelehtë, por ata nuk e dinë këtë.'),
(21, 2, 14, 'Kur të takohen me ata që besojnë, thonë: “Besojmë!” – por kur të ngelin vetëm me djajtë e tyre (me njerëzit e këqinj) thonë: “Na vetëm bëjmë sahaka, na jemi me ju”.'),
(22, 2, 15, 'Perëndia tallet me ta; Ai do t’i lë një kohë të gjatë që të bredhin andej-këndej në mohimin e tyre.'),
(23, 2, 16, 'Ata janë prej atyre që kanë blerë (zgjedhur) rrugën e gabuar, në vend të së vërtetës. Tregtia atyre nuk u solli kurrfarë dobie, dhe as nuk janë orientuar në rrugë të drejtë.'),
(24, 2, 17, 'Ata (që janë zhytur në të këqia e bredhin andej-këndej) janë të ngjashëm me ata që ndezin zjarrin, i cili kur ta ndriçojë rrethin e tyre, Perëndia ua merr dritën, i le në errësirë dhe ata nuk shohin asgjë!'),
(25, 2, 18, 'Ata janë të shurdhër, memecë, e të verbër; prandaj nuk kthehen (nga e keqja në të mirë).'),
(26, 2, 19, 'Ose ata i gjasojnë atyre, të cilët gjatë kohës së shiut të stuhishëm (shtrëngatës) nga qielli, me errësirë, bubullimë dhe shkreptimë, nga frika e vdekjes – për të mos dëgjuar ushëtimën me gishtërinjtë mbyllin veshët, por Perëndia i ka rrethuar (qafirat) me fuqi (e s’kanë shpëtim).'),
(27, 2, 20, 'Shkrepëtima, pothuaj, ua merrë të pamurit atyre; sa herë që ajo ua ndriçon rrugën, ata ecin, e kur i kaplon errësira, ndalen. E sikur të dojë Perëndia, do t’ua merrte të dëgjuarit edhe të pamurit, se, me të vërtetë, Perëndia është i Plotëfuqishëm.'),
(28, 2, 21, 'O njerëz! Aduroni Perëndinë, i cili u ka krijuar juve dhe ata para jush, për t’u ruajtur nga mohimi.'),
(29, 2, 22, 'Zoti është Ai, që për ju tokën e ka bërë shtresë dhe qiellin ndërtesë; i cili prej qielli lëshon shira dhe bënë që me atë të rritën frutet, ushqimi juaj. Prandaj mos i bëni shoq Perëndisë, sepse ju e dini (që Ai nuk ka shoq).'),
(30, 2, 23, 'Nëse dushoni ju në atë (Kur’anin) që ia kemi dërguar robit tonë (Muhammedit), atëherë hartonie një sure të ngjashme si atë që ia kemi shpallur atij, dhe nëse jeni të drejtë thirrni dëshmitarët tuaj, (zotërat që i adhuroni), përveç Perëndisë.'),
(31, 2, 24, 'Nëse nuk mundeni, dhe kurrsesi nuk do të mundeni (me i bë shembull Kur’anit), atëherë ruajuni zjarrit, lëndë djegëse e të cilit janë njerëzit dhe gurët. Ai është përgatitur t’i ndëshkojë mohuesit.'),
(32, 2, 25, 'Ata që besojnë dhe bëjnë vepra të mira gëzoji me kopshte të xhenetit nëpër të cilat do të kalojnë lumenj çdoherë kur u jepet atyre ndonjë frut i tyre, ata të thonë: “Këtë e kemi ngrënë edhe përpara”, - e atyre do t’u jepen vetëm fruta të ngjashme me to. Në to do të ketë femra të pastra dhe në to do të jetojnë gjithmonë.'),
(33, 2, 26, 'Allahu nuk ngurron të jep shembullin e mushkajës ose diçka më të madhe se atë, ata që besojnë e dinë që kjo është e vërtetë nga Perëndia e tyre, e ata që nuk besojnë thonë:“-Çka don të tregojë Allahu me këtë shembull. Në këtë mënyrë Ai shumëkend e ka hutuar e shumëkujt ia ka treguar rrugën e vërtetë. Ai i la të humbur vetëm mëkatarët e mëdhenj”,'),
(34, 2, 27, 'të cilët shkelën obligimin e Allahut pasi e patën marrë dhe e ndërprenë atë që i ka urdhëruar Perëndia për ta mbajtur, e bëjnë ngatërresa në Tokë; ata janë të shkatërruar.'),
(35, 2, 28, 'Si mund të mos e besoni Allahun, ju që s’keni qenë e Ai ju ka dhënë jetën; Ai pastaj do t’ju bëjë të vdekur e pastaj do t’ju ngjallë, e në fund do të ktheheni tek Ai.'),
(36, 2, 29, 'Ai për ju ka krijuar çdo gjë që ka në Tokë; Pastaj vullnetin e vet e ka drejtuar kah qielli dhe e ka rregulluar si shtatë qiejë, Ai di çdo gjë.'),
(37, 2, 30, 'Kur Zoti yt u tha engjujve: “Unë do të caktojë përfaqësuesin në tokë” – Ata thanë: “A do të jetë përfaqësuesi yt ai që në të do të bëjë çrregullime e do të derdhë gjak? Na të madhërojmë e të lavdërojmë dhe të ngrisim ashtu si të takon Ty”. Ai tha: Unë di atë që ju nuk dini.'),
(38, 2, 31, 'Allahu ia mësoi Ademit të gjithë emrat e pastaj ata ua paraqiti engjujve dhe u tha: “Nëse e keni thënë të vërtetën, m’i tregoni emrat e këtyre sendeve”.'),
(39, 2, 32, '“Qofsh i lavdëruar!” – thanë ata – “ne dimë vetëm atë që na ke mësuar Ti. Ti je mendjemprehtë dhe i gjithëdijshëm”.'),
(40, 2, 33, 'O Adem – tha Ai – tregoju emrat e tyre! – Kur ai u tregoi emrat e tyre, Allahu tha: “A nuk ju kam thënë se vetëm Unë i di fshehtësitë e qiellit e të Tokës dhe vetëm Unë e di atë që tregoni haptazi dhe atë që e fshihni”.'),
(41, 2, 34, 'Kur u thamë engjujve: “Përuluni para Ademit” – ata u përulën, por Iblisi nuk deshi, ai e rriti mendjen dhe u bë i pafé.'),
(42, 2, 35, 'Ne thamë “O Adem të jetosh ti dhe gruaja jote në xhennet dhe të hani në të sa të doni dhe kur të doni, por mos iu afroni këtij lisi sepse i bëni keq vetvetes.'),
(43, 2, 36, 'Shejtani për shkak të këtij lisi i shtyri të gabojnë e t’i nxjerrë nga vendi ku ishin. “Zbritni – u tham Ne – do të jeni armik i njëri tjetrit e në Tokë do të qëndroni dhe do të jetoni deri në afatin e caktuar”.'),
(44, 2, 37, 'Ademi dëgjoi disa fjalë (Urdhëra) e i zbatoi nga Zoti i vet, Ai ia pranoi pendimin dhe i fali gabimin. Ai shumë i pranon pendimet, Ai është i mëshirshëm.'),
(45, 2, 38, 'Ne u kemi thënë: “Zbritni të gjithë prej (xhennetit) tij. Juve me të vërtetë do t’ju vijë një dirigjues prej meje dhe ata që i përmbahen dirigjimit Tim nuk do të kenë frigë dhe nuk do të hidhërohen.'),
(46, 2, 39, 'Ata që nuk do të besojnë dhe i mohojnë argumentet tona do të jenë banorë të zjarrit, a në të do të mbesin gjithmonë.'),
(47, 2, 40, 'O djemë të Israilit, kujtoni të mirat e Mia që u kam dhënë dhe përmbushni premtimin që më keni dhënë – Edhë Unë do ta përmbushi atë që u kam dhënë, e frikohuni vetëm prej Meje.'),
(48, 2, 41, 'Besoni asaj që e kam shpallë Unë (Kur’anin), me të cilin vërtetohet ajo që keni ju (Teurati), e mos u bëni mohuesit e parë të tij. Dhe, mos i këmbeni argumentet e Mia me vlerë të vogël; ruhuni vetëm prej Meje!'),
(49, 2, 42, 'Mos e ngatërroni të vërtetën me të pavërtetën, duke e përzier njërën me tjetrën, dhe të vërtetën mos e fshehni me vetëdije.'),
(50, 2, 43, 'Falni namazin e jepni zeqatin dhe lutuni bashkë me ata që luten.'),
(51, 2, 44, 'Vallë, a po i urdhëroni ju njerëzit që të bëjnë të mira, e veten po e harroni, ju që e mësoni Librin? A nuk po mblidhni mend?'),
(52, 2, 45, 'Ndihmoni vetes me durim e me namaz, e kjo është njëmend vështirë pos për ata që i përulen Zotit, të cilët janë të bindur se do të dalin para Zotit dhe me të vërtetë do të kthehen tek Ai.'),
(53, 2, 46, 'Ndihmoni vetes me durim e me namaz, e kjo është njëmend vështirë pos për ata që i përulen Zotit, të cilët janë të bindur se do të dalin para Zotit dhe me të vërtetë do të kthehen tek Ai.'),
(54, 2, 47, 'O djemë të Israilit, kujtoni të mirat e Mia që ju kam pasë dhënë dhe që ju kam pasë madhëruar mbi njerëzit e tjerë (të asaj kohe).'),
(55, 2, 48, 'dhe frikohuni Ditës kur askush për askend nuk do të mund të bëjë asgjë dhe kur nuk do të pranohet vërejtja e askujt, kur prej askujt nuk do të mirret kompensim dhe kur askush nuk do t’ju dalë në ndihmë.'),
(56, 2, 49, 'dhe kujtoni kur ju shpëtuam nga populli i Faraonëve, i cili ju ka torturuar me mundime të rënda: iu prisnin djemtë e juaj, e ua linin të gjalla femërat. E, kjo ka qenë një sprovë e madhe për ju – nga Zoti juaj.'),
(57, 2, 50, 'dhe kujtohuni kur e kemi çarë detin e ju kemi shpëtuar, ndërsa në praninë tuaj e kemi fundosur popullin e Faraonit.'),
(58, 2, 51, 'Kujtoni ju premtimin Tonë dhënë Musait për takim (për të biseduar) dyzet netë me të, e pastaj duke i bërë vetes padrejtësi, e adhuruat viçin.'),
(59, 2, 52, 'Pastaj Ne ju kemi falë, që të bëheni mirënjohës;'),
(60, 2, 53, 'dhe kur Musait ia kemi pasë dhënë librin e Furkanit (Teuratin), që të udhëzoheni në rrugën e drejtë;'),
(61, 2, 54, 'dhe kur Musai i tha popullit të vet: “O populli im, ju vetëm vetes i bëtë padrejtësi, kur viçin e pandehet për zot; prandaj, pendohuni te Krijuesi juaj dhe vritni njëri-tjetrin (i pafajshmi – fajtorin). Kjo është më mirë për ju te Krijuesi juaj; Ai ju falë gabimin! Ai pranon pendimin dhe është mëshirues;'),
(62, 2, 55, 'dhe (kujtonie), kur i keni thënë Musait: “O Musa! Kurrsesi nuk të besojmë ty pa e parë Allahun për fushë!” – në atë çast ju përlau rrufeja, e ju e patë.'),
(63, 2, 56, 'Pastaj, pas vdekjes suaj, Na ju ngjallëm juve, që të bëheni falenderues.'),
(64, 2, 57, 'Dhe Ne bëmë që reja t’ju bëjë hije dhe u dërguam – men – (një lloj ushqimi i ëmbël) dhe zogj (si fëllanza): “Hani ushqime të mira me të cilat ju kemi furnizuar!” Ata, neve nuk na dëmtuan, por dëmtuan veten.'),
(65, 2, 58, '(Kujtonie) kur ju kemi thënë: “Ejani në këtë qytet dhe hani ç’të doni dhe sa të dëshironi, por, duke hyrë në qytet përuluni dhe thoni: “Falna!” – Na ua falim gabimet tuaja, e atyre që bëjnë vepra të mira, ua shtojmë shpërblimin!'),
(66, 2, 59, 'Dhe, ata që dëmtuan veten, duke ndryshuar fjalën me një fjalë tjetër (në tallje), Ne, u sollëm atyre – mëkatarëve një dënim nga qielli, meqë bënin ngatërresa;'),
(67, 2, 60, 'dhe kur Musai kërkoi ujë për popullin e tij, Na i thamë: “Bjeri gurit me shkopin tënd!” – (Ai i ra) dhe nga ai rrodhën dymbëdhjetë burime, dhe çdo fis e dinte burimin e vet. (U tham): “Hani e pini prej dhuratës së Perëndisë, dhe mos bëni çrregullime në faqe të Dheut!”'),
(68, 2, 61, 'Kur ju thatë: “O Musa! – ne nuk mundemi të kënaqemi vetëm me një lloj ushqimi, pra lutju për ne Zotit tënd që të na dhurojë nga ato që jep toka: perime – tranguj, bizele, qepë dhe hudhra!” Ai (Musai) ju tha: “Vallë a dëshironi të ndërroni atë që është më e mirë me atë që është më e dobët? Shkoni në qytet, atje do të gjeni atë që kërkoni!” Por, ata i kaploi poshtërimi dhe përulja (kokëulur për toke) merituan pezmin e Perëndisë; sepse ata i mohonin argumentet e Zotit dhe i mbytnin pejgamberët pa farë të drejte. Ky ndëshkim ishte pasojë e kundërshtimit të tyre dhe e tejkalimit të kufirit të kërkesave.'),
(69, 2, 62, 'Ata që kanë besuar (me gojë) madje edhe Hebrenjtë, të krishterët dhe Sabi’inët – kushdo që beson Perëndinë (si është urdhëruar) dhe jetën tjetër, e bënë vepra të mira, për ata, me të vërtetë, ka shpërblim te Zoti i tyre; për ta nuk ka kurrëfarë droje, e as mërzie!'),
(70, 2, 63, 'Dhe (kujtonie), kur prej jush morëm premtimin dhe ngritëm Turin (malin) mbi ju; (e Na ju thamë): Merreni me seriozitet atë që ua kemi dhënë juve (Tevratin) dhe kinie ndërmend atë që gjendet në të, për t’u ruajtur!”'),
(71, 2, 64, 'Por, ju pastaj abstenuat (nuk e mbajtët fjalën), e sikur të mos ishte mirësia e Zotit ndaj jush dhe mëshira e tij, ju do të ishit të humbur.'),
(72, 2, 65, '(Betohem) se ju me të vërtetë e dini grupin tuaj, i cili kaloi kufirin ditën e shtunë (që e kundërshtoi urdhërin e Zotit) dhe të cilëve Na ju thamë: “Bëhuni majmunë të poshtër!”'),
(73, 2, 66, 'Këtë ngjarje e kemi bërë dënim për bashkëkohanikët dhe për breznitë e tyre, kurse këshillë për ata që i drojnë Perëndisë.'),
(74, 2, 67, 'E, kur Musai i tha kombit të vet: “Perëndia, me të vërrtetë ju urdhëron të therrni një lopë”, - ata thanë: “A don të tallesh me ne?” – Zoti më ruajtë!” tha Musai “të bëhem unë me grupin e injorantëve (me ata që ia mveshin Perëndisë atë që s’e ka thënë).'),
(75, 2, 68, 'Ata thanë: “Lutju Zotit tënd, që të na shpjegojë qysh është ajo (lopë). Musai tha: “Ajo është një lopë as plakë as e re, por e mesme; pra bëi atë që u është urdhëruar!'),
(76, 2, 69, 'Ata thanë: “Lutju Zotit tënd, që të na shpjegojë çfarë ngjyre ka ajo!” Musai tha: “Me të vërtetë Ai, (Perëndia) thotë se ajo është lopë ngjyra e së cilës është tejet e verdhë, të cilën e pëlqejnë shikuesit”.'),
(77, 2, 70, 'Ata (përsëri) thanë: “Lutju Zotit tënd, që të na shpjegoje çfarë është ajo, sepse, lopët po i gjasojnë (njëra-tjetrës) e ne, në dashtë Zoti, do të udhëzohemi në rrugën e drejtë (si jemi urdhëruar)”.'),
(78, 2, 71, 'Ai (Musai) tha: “Me të vërtetë Ai (Perëndia) thotë se ajo lopë nuk është e lodhur nga lavra, as nga ujitja e tokës; s’ka të metë dhe kurrfarë shenjë në të”. Ata thanë: “Tani na the të vërtetën”. E prenë lopën; por për një fije nuk dashtën ta bënin këtë.'),
(79, 2, 72, 'Dhe pasi vratë një njeri, dhe me këtë rast u grindët në mes veti – Zoti e nxori në shesh atë që e fshihnit ju.'),
(80, 2, 73, 'Na (Perëndia) kemi thënë: “Mëshoni me një copë të asaj (lopës) të vrarit!” Dhe kështu Perëndia do t’i ngjallë të vdekurit dhe do të ju tregojë argumentet e veta për të kuptuar ju.'),
(81, 2, 74, 'Por, pastaj (mbasi i patë këto argumente) zemrat tuaja u forcuan e u bënë si guri ose edhe më të forta, megjithëse ka gurë nga të cilët burojnë lumenj, e ka me të vërtetë, edhe shkembenjë që çahen e nga të cilët rrjedh uji e ka madje, gjithashtu që prej frikës së Perëndisë të rrëzohet. – E Perëndia nuk është i pakujdesshëm ndaj veprimeve tuaja.'),
(82, 2, 75, 'A shpresoni ju se për hir tuajin Hebrenjt do të bëhen besimtarë, kur disa prej tyre, i dëgjonin fjalët e Zotit, e pasi i kuptonin, me vetëdije i pështjellonin (ato fjalë).'),
(83, 2, 76, 'Kur të takohen me besimtarët ata thonë: “Na besojmë, e kur ngelin vetëm njëri me tjetrin – thonë: “A do t’u tregoni atyre, atë që ua ka shpallë Perëndia (në Tevrat), për ta përdorë atë si argument kundër jush te Zoti juaj? A nuk po mendoni ju?'),
(84, 2, 77, 'A nuk e dinë ata se Perëndia di atë që fshehin dhe atë që shpallin.'),
(85, 2, 78, 'Disa prej tyre janë analfabetë (nuk dinë të lexojnë) Librin, përveç disa trillimeve; dhe ata flasin vetëm me hamendje.'),
(86, 2, 79, 'mjer ata që me duart e tyre shkruajnë vepër (të rrejshme), e pastaj thonë: “Kjo është prej Zotit” që për këtë të kenë ndonjë dobi të vogël. Dhe mjer ata, që kanë shkruar me duart e tyre, dhe mjer ata që fitojnë në këtë mënyrë!'),
(87, 2, 80, 'Ata (Hebrenjtë) thanë: “Zjarri nuk do të na prekë, përveç disa ditësh të numruara”. Thuaj (O Muhammed): “A keni marrë për këtë ndonjë zotim prej Perëndisë – se Zoti nuk e shkel kurrë zotimin e vet; ose po shpifni kundër Perëndisë aso gjërash që nuk i dini?”'),
(88, 2, 81, 'O, me të vërtetë! Ata që punojnë vepra të këqia dhe fajet e tyre të përhapen nga të gjitha anët, ata do të jenë banorë të skëterrës, ku do të qëndrojnë përgjithmonë.'),
(89, 2, 82, 'Kurse, ata që punojnë vepra të mira – ata do të jenë banorë të xhennetit ku do të qëndrojnë përgjithmonë.'),
(90, 2, 83, 'Dhe kur nga bijt e Israelit morëm premtimin se vetëm Perëndisë do t’i luten, dhe se do t’u bëjnë mirë prindërve, të afërmve, bonjakëve dhe të varfërve, e do t’u flasin fjalë të mira njerëzve dhe do të bëjnë namazin, e do të japin zeqatin; mirëpo, pastaj, ata, përpos një pakice të vogël, këto detyra i lanë pas dore, duke iu shmangur obligimit;'),
(91, 2, 84, 'Dhe kur nga ju morëm premtimin që të mos derdhni gjakun e njëri-tjetrit dhe që të mos e dëboni njëri-tjetrin nga atdheu juaj – ju e pranuat këtë dhe ishi dëshmitarë (të këtij premtimi), -'),
(92, 2, 85, 'por pastaj, megjithatë vratë njëri-tjetrin e disa prej jush i dëbuat nga atdheu i tyre – duke i ndihmuar njëri-tjetrit kundër tyre, duke bërë faje dhe armiqësi. E nëse ju vijnë të robëruar, ju shpëtoni ata nga robëria me të holla; kurse me marrëveshje u është e ndaluar t’i dëboni; apo një pjesë të Librit (Teuratit) i besoni, e kurse pjesës tjetër nuk i besoni?! (Kjo është një vepër e keqe). – Kushdo që vepron kështu nga ju, ndëshkimi i tij është poshtërimi i tij në këtë jetë, e në Ditën e Kijametit do të dërgohen në dënimin më të ashpër. – E, Perëndia nuk është i pakujdesshëm ndaj veprimeve tuaja.'),
(93, 2, 86, 'Këta janë ata, të cilët jetën e kësaj bote e kanë blerë me jetën e ardhshme dhe atyre nuk do t’u lehtësohet dënimi as nuk do të ndihmohen.'),
(94, 2, 87, 'Me të vërtetë, Na ia dhamë Librin (Teuratin) Musait dhe pas tij dërguam profetët njërin pas tjetrit, e Isait, të birit të Merjemes ia dhamë argumentet e qarta (mrekullirat) dhe i ndihmuam me Xhebrailin. Dhe kurdoherë që ndonjë profet u sjellte atë e cila nuk u pëlqente juve, ju kaperdiseshit, prandaj disa i përgënjeshtruat, e disa i vratë.'),
(95, 2, 88, 'Ata (Hebrenjtë) thanë: “Zemrat tona janë të mbuluara”. Jo, s’është ashtu, por ata i ka mallkuar Perëndia për shkak të mohimit të tyre; dhe prandaj prej tyre shumë pak besojnë.'),
(96, 2, 89, 'Kur atyre u erdhi Libri (Kur’ani nga ana e Perëndisë (të cilin e prisnin), e i cili vërtetoi atë që patën ata, - e qysh përpara luteshin për ndihmë kundër paganëve – dhe kur u erdhi ajo që e dinin se është e vërtetë, e mohuan atë; dhe prandaj mallkimi i Perëndisë qoftë mbi mohuesit! -'),
(97, 2, 90, 'Keq e shitën veten ata, që nga zilia nuk besuan në atë që ua shpalli Perëndia. Perëndia ia ka zbritur shpalljen atij që ka dasht Ai nga robërit e tij; dhe u kthyen pezëm mbi pezëm! E mohuesit i pret dënimi poshtërues.'),
(98, 2, 91, 'E kur atyre u thuhet: “Besoni në atë që ua ka shpallë Perëndia!” – ata përgjigjen: “Na besojmë në atë që na është shpallë neve” – dhe nuk besojnë në atë që ka zbritur pas (Kur’anit), e kjo është e Vërtetë e cila vërteton atë që kanë ata. Thuaju atyre: “E nëse ishit besimtarë, përse i vratë pejgamberët e Zotit në atë kohë kur ju zbritën?! (Me këto sjellje u bëtë qafira).'),
(99, 2, 92, 'Me të vërtetë, Musai ju zbriti me mrekulli të mëdha, por gjatë mungesës së tij, ju viçin e adhuruat si zot, prandaj u bëtë fajtorë të mëdhenj (me këtë sjellje).'),
(100, 2, 93, 'Kur Na morëm premtimin nga ju dhe ngritëm malin e Turit mbi ju, thamë: “Merrni seriozisht atë që ua kemi dhënë juve (Teuratin) dhe dëgjoni!” – Ata thanë: “Na dëgjuam, por kundërshtojmë!” – Për shkak të mohimit, atyre në zemra u ishte rrënjosë dashuria e viçit (adhurimi i tij). Thuaju: “Nëse jeni besimtarë, sa gjë të keqe po ju urdhëruaka besimi juaj!”'),
(101, 2, 94, 'Thuaju (O Muhammed): “Nëse vetëm për ju te Perëndia u është siguruar xhenneti, e jo për njerëzit tjerë, atëherë, nëse flisni të vërtetën, kërkoni vdekjen”.'),
(102, 2, 95, 'Por ata kurrsesi nuk e dëshirojnë vdekjen, për shkak të të këqijave që kanë punuar! – E Perëndia i di mirë ata që shkelin drejtësinë.'),
(103, 2, 96, 'Dhe padyshim, do t’i gjesh ata (Hebrenjtë) më lakmues për të jetuar se sa njerëzit tjerë, e madje edhe se paganët. Çdonjëri nga ata dëshiron të jetojë njëmijë vjet; por edhe nse jeton aq, nuk do të mund t’i ikë dënimit! – Se Zoti i sheh punët e tyre.'),
(104, 2, 97, 'Thuaju: “Kush është armik i Xhebrailit? (duke qenë i lavdërueshëm); Ai e ka zbritur Kur’anin në zemrën tënde me lejen e Perëndisë – për të vërtetuar librat e shenjta – dhe është udhërrëfyes e sihariq për muslimanët.'),
(105, 2, 98, 'Kush është armik i Perëndisë, i engjujve të Tij dhe pejgamberëve të Tij, dhe Xhebrailit e Mikailit – (ai është qafir); e Perëndia është me të vërtetë armik i qafirëvet.'),
(106, 2, 99, 'Me të vërtetë, Na të kemi zbritur Ty argumente të qarta, e që vetem njerëzit e këqij nuk duan t’u besojnë.'),
(107, 2, 100, 'A thua çdoherë që japin ndonjë premtim – disa prej tyre e refuzojnë, ndërsa shumica prej tyre nuk besojnë.'),
(108, 2, 101, 'Kur ju erdhi atyre Pejgamberi i Zotit (Muhammedi), vërtetues i librit që kanë ata, një grup i tyre, të cilëve iu dha Libri, gjoja se nuk e dinin për të, e hodhën pas shpine Librin e Zotit (Teuratin).'),
(109, 2, 102, '(Jehuditë e braktisën Teuratin) dhe shkonin pas shpifjeve që bënin djajt kundër mbretërisë së Sulejmanit. E Sulejmani nuk ishte mohues, por djajt ishin mohues, që u mësonin njerëzve magjinë dhe dijeninë që u ishte dërguar dy ëngjujve në Babiloni – Harutit e Marutit. Këta ëngjuj nuk i mësonin ndokujt magji pa i thënë: “Na vetëm provojmë, dhe ti mos u bën mohues!” Dhe njerëzit mësuan nga këta dy se si t’i ndajnë bashkëshortët. Por magjistarët – pa lejen e Zotit nuk mund t’i bënin dëm askujt. Njerëzit megjithatë mësonin ato gjëra që i dëmtojnë e nuk u bëjnë dobi; dhe me të vërtetë e dinin se ai që e ka përvetësuar këtë mjeshtri nuk do të ketë kurrfarë të mire në jetën tjetër. E sa e keqe është ajo shit-blerje me magji. Oh! Sikur ta dinin ata?!'),
(110, 2, 103, 'Dhe sikur ata (Hebrenjt) të besonin dhe të druanin, shpërblimi i Perëndisë do të ishte më i mirë (të cilën e merituan prej besimit). Oh! Sikur ta dinin ata!'),
(111, 2, 104, 'O besimtarë! Mos thuani: “Ráiná”, por thuani: “Undhurná”, - shikona neve dhe dëgjoni! E, për mohuesit ka dënim pikëllues.'),
(112, 2, 105, 'Nuk duan mohuesit: ithtarët e Librit dhe paganët, që t’u zbresë juve ndonjë e mirë nga Zoti juaj. E, Perëndia, mëshirën e vet ia dhuron kujt të dojë; Perëndia është pronar i mirësisë së madhe.'),
(113, 2, 106, 'Na nuk i mohojmë ligjet e as nuk i lëmë që të harrohen ato, e ejellim ndonjë tjetë rmë të mirë ose të ngjashëm me të (e me të vërtetë), ti e di se Perëndia është i plotëfuqishëm.'),
(114, 2, 107, 'A nuk e di ti (me siguri e di) se vetëm Perëndia është pronar i qiejve dhe Tokës dhe ju përpos Perëndisë nuk keni tjetër mbrojtës as ndihmës?'),
(115, 2, 108, 'A mos doni të pyesni Pejgamberin tuaj ashtu siç u pyet Musai më parë? E kush e ndërroi besimin me mohim, padyshim, ai e ka humbur edhe rrugën e drejtë.'),
(116, 2, 109, 'Shumica e ithtarëve, të Librit të Shenjtë, prej zilisë që kanë në vete, dëshirojnë që t’ju kthejnë nga besimi në mohim, pasi e vërteta atyre iu tregua haptazi; por, ju falni ata dhe hiqni dorë, për deri sa të arrijë urdhëri i Perëndisë. – Me të vërtetë Perëndia është i Plotëfuqishëm.'),
(117, 2, 110, 'Bëni namaz, dhe jepni zeqatin, e çfarëdo të mirë që bëni do ta gjeni te Perëndia, se Perëndia, me të vërtetë, sheh mirë atë që punoni.'),
(118, 2, 111, '(Hebrenjt dhe të krishterët) thonin: askush nuk do të hyjë në xhennet përveç hebrenjve ose të krishterëve. – Këto janë dëshirat e tyre boshe! – Thuaju ti (O Muhammed): “Nëse ju jini të drejtë, sillni argumentin (për atë që po thoni).'),
(119, 2, 112, 'Jo! – Po! Ai që i përulet Perëndisë dhe punon vepra të mira, për atë ka shpërblim te Zoti i tij, për ta nuk ka frikë as pikëllim.'),
(120, 2, 113, 'Hebrenjt thanë: “Krishterët nuk kanë kurrfarë baza!” Kurse të krishterët thonë: “Hebrenjt nuk kanë kurrfarë baza!” E, ata lexojnë Librin. Po kështu, thanë edhe ata që nuk dinë, siç thonë ata (hebrenjt e të krishterët). E, Perëndia, do të gjykojë në Ditën e Kijametit, për atë që nuk pajtoheshin ata.'),
(121, 2, 114, 'Kush është më fajtorë se ai që pengon të përmendurit e emrit të Perëndisë nëpër faltoret (xhamitë) dhe përpiqet për shkatërrimin e tyre? Ata nuk hyjnë në to, përveç atëherë kur kanë ndonjë frikë (trishtim). Për ta, në këtë jetë ka poshtërim, kurse në jetën tjetër dënim të madh.'),
(122, 2, 115, 'Lindja dhe Perëndimi të Perëndisë janë e, kahdo që të ktheheni (me urdhër të Tij) është kënaqësia e Perëndisë. – Perëndia me të vërtetë është universal (me cilësi të larta) dhe Ai di çdo gjë.'),
(123, 2, 116, 'Ata (të krishterët) thanë: “Perëndia ka fëmijë”. Ai është i pastër nga ajo veçori. Por, me të vërtetë, çdo gjë që gjendet në qiej dhe në Tokë janë të Tij, dhe Atë e adhurojnë të gjithë.'),
(124, 2, 117, 'Ai është krijues i pashembull i qiejve dhe i Tokës dhe kur të vendos për ndonjë gjë, vetëm thotë për të: “Bëhu!” – dhe ajo bëhet.'),
(125, 2, 118, 'Kurse të paditurit thonë: “Të kishte të bisedojë Perëndia me ne, ose së paku të na sillte ndonjë argument! Njësoj thonin edhe ata para tyre; zemrat e tyre i përngjanin njëra-tjetrës! E Na, me të vërtetë, atyre që besojnë ua shpjegojmë dokumentet bindshëm.'),
(126, 2, 119, 'Na me të vërtetë, të kemi dërguar Ty (O Muhammed!) me të drejtë sihariques dhe paralajmërues, dhe ti nuk do të pyesësh për të dënuarit në shkëterrë.'),
(127, 2, 120, 'As hebrenjt, as të krishterët nuk do të jenë të kënaqur me ty, për deri sa të mos pranosh besimin e tyre. Thuaju: “Vetëm drejtimi i Perëndisë është i drejtë!” E, në qoftë se pas asaj diturie që të ka zbritur ty, ti ndjek dëshirën e atyre, atëherë prej dënimit të Perëndisë ti nuk do të kishe kurrfarë mbrojtësi as kurrfarë ndihmëtari.'),
(128, 2, 121, 'Atyre që u kemi dhënë Librin, e lexojnë ashtu siç është shpallur; ata besojnë në të. Kurse, ata që nuk besojnë në Te, padyshim janë të humbur.'),
(129, 2, 122, 'O bijt e Israilit, kujtoni të mirat e Mia që ua kam pasë dhënë dhe që iu kam pasë madhëruar mbi njerëzit e tjerë (të asaj kohe).'),
(130, 2, 123, 'Dhe frikohuni Ditës, kur askush për askend nuk do të mund të bëjë asgjë dhe kur këmbimi (trampi) nga askush nuk do të pranohet, e kur intervenimi i askujt nuk vlenë, dhe kur askush nuk do t’u dalë në ndihmë.'),
(131, 2, 124, 'Dhe (kujtonie), kur Perëndia e provoi Ibrahimin me disa urdhëra, e ai i plotësoi ato. Perëndia tha: “Unë do të bëjë ty imam (prijës) të njerëzve!” – Tha: “Edhe disa prej pasardhëve të mi!” (Perëndia) tha: “Premtimi im nuk i përfshinë zullumqarët.”'),
(132, 2, 125, '(Dhe atë kohë) kur Na e bëmë Qaben – vend dhuntie dhe sigurimi për njerëzit. “Merrni vendin e Ibrahimit – vend të kryerjes së namazit!” – Edhe Ibrahimit edhe Ismailit i kemi rekomanduar: “Faltoren (Qaben) Time pastronie për ata që e vizitojnë, dhe që banojnë aty, dhe për ata të cilët kryejnë namazin”.'),
(133, 2, 126, '(Dhe kujto atë kohë) kur Ibrahimi tha: “Zoti im bëne këtë vend qytet të sigurt, dhe furnizoi banorët e këtushëm me prodhime, ata që besojnë në Perëndinë dhe në jetën tjetër!” Perëndia tha: “Atë që nuk beson për pak kohë do të bëj që të kënaqet, e pastaj do ta dërgoj në dënimin e zjarrit, e, sa vendbanim i keq që është ai!”'),
(134, 2, 127, '(Dhe kujto atë kohë) kur Ibrahimi dhe Ismaili ngritën themelet e Qabes (e u lutën): “O Zoti ynë! Prano prej nesh (këtë vepër)! Me të vërtetë Ti je ai, që i dëgjon të gjitha dhe që të gjitha i di!'),
(135, 2, 128, 'O Zoti ynë bëna neve të përulun ndaj urdhërave Tuaja! Dhe bënë që pasardhësit tanë të jenë të përulur ndaj urdhërave Tua, dhe tregona format e ceremonive të Haxhit! Pranoje edhe pendimin tonë, se padyshim, Ti i pranon pendimet dhe je mëshirues i madh!'),
(136, 2, 129, 'O Zoti ynë! Dërgo në mesin e tyre një pejgamber prej tyre, që do t’u lexojë ajetet (versetet) Tuaja, dhe t’ua mësojë atyre Librin dhe t’i ofrojë dijeni të përsosur e t’i pastrojë ata (prej fajesh), se, me të vërtetë, Ti je i plotëfuqishëm dhe i gjithëdijshëm!”'),
(137, 2, 130, 'Fesë së Ibrahimit i shmanget vetëm ai që nuk e njeh vetveten. Na me të vërtetë atë e kemi zgjedhë në këtë botë, e ai, edhe në botën tjetër do të jetë ndër të mirët.'),
(138, 2, 131, 'Kur Zoti i tij i tha (Ibrahimit): “Bëhu i bindshëm!” – ai u përgjegj: “Unë i jam bindur Zotit të gjithësisë!”'),
(139, 2, 132, 'Ibrahimi u rekomandoi këtë fëmijëve të tij, po edhe Jakubi: “O bijt e mi! perëndia me të vërtetë ka zgjedhë për ju fenë e vërtetë, dhe mos vdisni ndryshe veçse duke qenë muslimanë!”'),
(140, 2, 133, 'A ishit ju prezent kur Jakubit iu afrua vdekja, dhe i pyeti të bijt: “Kënd do ta adhuroni pas meje?” – Ata u përgjegjën: “Na do ta adhurojmë Perëndinë tënd (i cili është) edhe Zoti i baballarëve tu: Ibrahimit, Ismailit dhe Is’hakit, një Zotit të vetëm, dhe ne i bindemi vetëm Atij!”'),
(141, 2, 134, 'Ai popull qe, e shkoi; ata i pret ajo që kanë merituar, edhe juve u pret ajo që e meritoni, e ju nuk do të pyeteni për veprat që kanë punuar ata.'),
(142, 2, 135, 'Ata thanë: “Bëhuni Hebrenj, gjegjësisht (ose) të krishterë, atëherë do të jeni në rrugën e drejtë!” – Thuaju “Jo”. Na jemi të fesë së Ibrahimit e cila është e pastër, se ai nuk ka qenë adhurues i idhujve”.'),
(143, 2, 136, 'Thoni ju (or besimtarë): “Na besojmë vetëm Perëndinë dhe në atë që na është shpallur neve (Kur’anin), dhe në atë që i është shpallur Ibrahimit, Ismailit, Is’hakut, Jakubit dhe nipërvet, dhe në atë që u është dhënë Musait dhe Isait, dhe në atë që u është dërguar pejgamberëve nga Zoti i tyre. – Na nuk bëjmë kurrfarë dallimi në mes tyre, dhe na vetëm Atij (Perëndisë) i përulemi”.'),
(144, 2, 137, 'E nëse besojnë ata sikundër që besoni ju (atëherë) janë në rrugën e drejtë; e nëse kthejnë shpinën nga kjo, ata janë larguar nga rruga e drejtë, dhe Perëndia ti (O muhammed) do të ruajë me siguri nga ata, e Ai është dëgjues dhe i di të gjitha.'),
(145, 2, 138, '“Na jemi ngjyrosë me ngjyrën e Perëndisë (fenë e cila është me cilësi të larta) e s’ka ngjyrë (fe) më të bukur se ajo e Perëndisë! Na vetëm atë e adhurojmë”.'),
(146, 2, 139, 'Thuaj: “A mos doni të polemizoni ju me ne, për kompetencat e Perëndisë – kur Ai është Zoti ynë dhe i juaj? Na i kemi veprat tona e ju tuajat. E, vetëm na jemi të sinqertë ndaj Zotit”.'),
(147, 2, 140, 'A po thoni se Ibrahimi, Ismaili, Is’haku, Jakubi dhe nipërit (e tyre) kanë qenë Hebrenj, ose të krishterë? Thuaju: “A e dini më mirë ju apo Perëndia? E kush është më mëkatar se ai që e fsheh të vërtetën e ardhur prej Perëndisë? – Por Perëndia nuk është i pakujdesshëm ndaj veprave që punoni ju”.'),
(148, 2, 141, 'Ai popull qe e shkoi; ata i pret ajo që kanë merituar, edhe juve u pret ajo që e meritoni, e ju nuk do të pyeteni për veprat që kanë punuar ata.'),
(149, 2, 142, 'Disa njerëz mendjelehtë thonë: “Ç’i ktheu ata (muslimanët) prej kibles kah e cila ishin drejtuar?” Thuaju: “Të Perëndisë janë Lindja dhe Perëndimi. Ai e udhëzon kend të dojë e kend të dëshirojë në rrugën e drejtë, që cakton Ai”. –'),
(150, 2, 143, 'Gjithashtu (sikundër që ju kemi udhëzuar në rrugë të drejtë) ju kemi bërë popull të drejtë, që të bëheni dëshmitarë të njerëzve dhe që Pejgamberi të jetë dëshmitar i juaji. – Na nuk e kemi bërë obligim kthimin tënd kah Qabeja (o Muhammed!) për tjetër arsye, por vetëm për të parë se kush ndjekë rrugën dhe kush do të shkojë pas Pejgamberit e për t’i dalluar nga ata që kthehen nga thembrat, - ky ndryshim është i rëndë, por jo për ata që i ka drejtuar Zoti në rrugë të drejtë. E, se, Perëndia nuk ua humbë besimin tuaj (namazin që keni bërë kah Kudsi), se Perëndia, me të vërtetë, është fort i dhembshëm dhe mëshirues për njerëzit.'),
(151, 2, 144, 'Na të pamë ty që shikimin tënd e drejtoje kah qielli (duke u lutur për caktimin e kiblës) e Na me të vërtetë do të kthejmë ty kah ajo anë, që të kënaq ty. Prandaj, ktheje fytyrën tënde kah faltorja e shenjtë (Qabja)! Dhe kudo që të gjendeni kthenie fytyrën tuaj kah ajo anë! Padyshim, atyre që u është dërguar Libri (Teurati) e dinë se ai (drejtimi kah kibla) është i vërtetë, është prej Zotit të tyre, - se Perëndia nuk është i pakujdesshëm ndaj punëve që bëjnë njerëzit.'),
(152, 2, 145, 'Dhe sikur ti t’u sillje të gjitha dokumentet atyre, të cilëve u është dhënë Libri, ata prapëseprapë nuk do ta pranonin kiblën tënde, por edhe ti nuk do të pranosh kiblën e tyre dhe që askush nga ata nuk do të pranonte kiblën e njëri-tjetrit. – E nëse ti, pasi të është zbritur dijenia, të ndiqshe dëshirën e tyre, atëherë padyshim do të ishe nga ata që kanë kaluar kufirin.'),
(153, 2, 146, 'Atyre q ëu kemi dërguar Librin (Teuratin) e njohin (Muhammedin për pejgamber), ashtu si i njohin bijt e tyre, por disa prej tyre e fshehin të vërtetën, duke e ditur mirë.'),
(154, 2, 147, 'E vërteta është në anën e Zotit tënd, pra, kurrsesi, mos u bënë nga ata që dyshojnë.'),
(155, 2, 148, 'Çdo popull ka kiblën (drejtimin kah Vendi i Shenjtë); Pra, bëni gara në punë të mira. Kudo që të gjendeni, Perëndia do t’u tubojë të gjithëve; se Perëndia, me të vërtetë, është i Plotëfuqishëm.'),
(156, 2, 149, 'Nga cilido vend që të dalësh ktheje fytyrën tënde kah Faltorja e shenjtë. Kjo është e vërtetë, që vie nga Zoti yt; e Perëndia nuk është i pakujdesshëm ndaj veprave që punoni ju.'),
(157, 2, 150, 'Nga cilido vend që të shkosh, ti shko, e fytyrën ktheje kah Faltorja e Shenjtë. E kudo që të gjendeni, ktheni fytyrat tuaja kah ajo anë, që bota mos të ketë çka të thotë për ju përpos atyre që kanë kaluar kufirin e drejtësisë dhe dijenisë, - por atyre mos iu frikoni e frikohuni vetëm prej Meje! Këtë (ndërrim të kiblës) e kam bërë për t’ju plotësuar dhuntitë e Mia juve, dhe për t’ju shpie në udhë të drejtë.'),
(158, 2, 151, 'Sikundër që ua kam plotësuar dhuntinë Time, ashtu (dhunti) ju kam dërguar një Pejgamber, nga mesi juaj, i cili ju lexon versetet Tona, dhe ju pastron (nga të këqiat) e ju begaton me dijeni të holla, dhe që t’ju mësojë ato gjëra që nuk i keni ditur.'),
(159, 2, 152, 'Më kujtoni Mua (ashtu si ju ka mësuar Profeti Im), e Unë ju kujtoj ju (me dhuratë); falënderomëni Mua e mos i mohoni dhuntitë e Mia!'),
(160, 2, 153, 'O besimtarë! Kërkoni ndihmë prej Zotit duke duruar dhe duke u falur! Me të vërtetë Zoti është me durimtarët.'),
(161, 2, 154, 'Dhe mos thuani për ata që vriten në udhë të Perëndisë (dëshmorët) se kanë vdekur! Jo! Ata janë të gjallë, por ju nuk i vëreni!'),
(162, 2, 155, 'Na do t’ju provojmë me ndonjë gjë: me pak frikë, uri, me dëmtim të pasurisë, dëmtim të njerëzve dhe të të lashtave. Ti sihariqoji durimtarët!'),
(163, 2, 156, 'Atyre të cilët kur i godit ndonjë fatkeqësi thonë: “Të Zotit jemi dhe vetëm tek Ai do të kthehemi!”'),
(164, 2, 157, 'Ata do të shpërblehen me dhunti dhe mëshirë nga Zoti i tyre; ata janë në rrugën e drejtë!'),
(165, 2, 158, 'Safaja dhe Merva janë shenja të fesë së Perëndisë, prandaj ai që viziton Qaben ose bën Umre, s’ka kurrfarë gjunahi nëve viziton ato (të dyja); ai që bën vepër të mirë, - me të vërtetë, Perëndia është falenderues dhe i Gjithëdijshëm.'),
(166, 2, 159, 'Me të vërtetë, ata që fshehin dokumenta Tona të qarta, që i kemi shpallur Ne, dhe orientimin e drejtë, pasi që ua kemi treguar njerëzve në Librin (e Teuratit), ata i mallkon Perëndia, i mallkojnë edhe të gjithë ata që janë kompetentë të mallkojnë;'),
(167, 2, 160, 'E nuk mallkohen, vetëm ata që pendohen dhe përmirësohen dhe haptazi e shfaqin këtë, se Unë jam pranues i pendimeve dhe mëshirues.'),
(168, 2, 161, 'Ata që nuk besojnë dhe vdesin duke qenë jobesimtarë, mbi ta është mallkimi i Perëndisë, i ëngjujve dhe i të gjithë njerëzve.'),
(169, 2, 162, 'Ata do të jenë gjithmonë të mallkuar (të dënuar), e nuk ju lehtësohet vuajtja dhe as nuk u jepet afat.'),
(170, 2, 163, 'E zoti juaj – është një! S’ka zot tjetër përveç Tij, Ai është Mëshirues dhe Përdëllimtar!'),
(171, 2, 164, 'Me të vërtetë në krijimin e qiejve dhe të Tokës, në ndryshim të natës dhe të ditës; në anijet që lundrojën nëpër det për t’u sjellë dobi njerëzve; në ujërat që zbret Perëndia prej qiellit, e me të cilët i ngjallë tokat e vdekura (tokat e thata) – nëpër të cilën ka shpërndarë gjithfarë lloj shtazësh; në ndryshimin e erërave e reve të nënshtruara lëvizjeve në mes qiellit e Tokës – (në të gjitha këto), ka dokumente për të gjithë ata që mendojnë.'),
(172, 2, 165, 'Ka njerëz që në vend të Perëndisë marrin disa gjëra të tjera për zotë dhe i duan ato si Perëndinë; ndërsa ata që besojnë, e duan shumë më tepër Perëndinë. Sikur ta dinin paganët në atë çast kur do ta shohin dënimin – se me të vërtetë e tërë fuqia i përket Perëndisë dhe se dënimi i Zotit është fort i ashpër (atëherë, ata nuk do të vepronin ashtu).'),
(173, 2, 166, 'dhe kur të shmangen të pasuemit (prijsat) nga ata që i pasonin (ithtarët) dhe shohin dënimin, e midis tyre këputet çdo lidhje.'),
(174, 2, 167, 'E (atëherë), pasuesit kanë për të thënë: “Ah, sikur të kishte edhe një kthim për ne, e të heqim dorë prej tyre, dikundër që hoqën dorë ata prej nesh!” Që kështu, Perëndia ua rrëfen veprat e tyre tmerruese mbi ta. Ata s’kanë për të dalë kurrë nga zjarri.'),
(175, 2, 168, 'O njerëz! Ushqehuni me të gjitha ato që gjenden në Tokë, që janë të lejueshme dhe të mira dhe mos ndiqni gjurmët e djallit! Se ai është armik i përbetuar i juaji.'),
(176, 2, 169, 'Ai vetëm ju nxitë në të këqija dhe në vepra të turpshme dhe të flisni kundër Perëndisë ato gjëra që nuk i dini.'),
(177, 2, 170, 'E kur atyre t’ju thuhet: “ndiqeni Shpalljen (Kur’anin) e Perëndisë!” – Ata përgjigjen: “Jo! Na ndjekim atë udhë në të cilën i kemi gjetur të parët tanë” – Vallë! Edhe atëherë kur të parët e tyre nuk kanë ditur asgjë dhe kur nuk kanë qenë në rrugën e drejtë?!'),
(178, 2, 171, 'Mohuesit i shëmbëllejnë çobanit (bariut) i cili i thërret shtazët, të cilat dëgjojnë vetëm thirrje e zë, por s’kuptojnë asgjë. (Ata janë) të shurdhër, memecë dhe verbët, e nuk mendojnë asgjë.'),
(179, 2, 172, 'O besimtarë! Hani nga ushqimet e këndshme që ua kemi dhuruar Na juve dhe nëse jeni adhurues të Perëndisë bëhuni falenderues të Tij!'),
(180, 2, 173, 'Me të vërtetë, Perëndia ua ka ndaluar ngrënien e kafshës së ngordhur, gjakun e derdhur, mishin e derrit dhe atë kafshë që nuk është therrur në emër të Zotit. Por ata që ngushtohen nga uria e madhe, sa për të zhdukur krizën e urisë nuk kanë mëkate t’i hanë, por nuk duhet kaluar kufirin e lejueshmërisë. Se Perëndia me të vërtetë është Përdëllimtar i madh dhe falë.'),
(181, 2, 174, 'Padyshim, ata që fshehin atë që ua ka shpallur Perëndia në Libër (Teurat) dhe i zëvendësojnë fjalët e Mia me diç tjetër të pavlerë (i shesin), ata nuk hanë tjetër gjë vetëm atë, që në barkun e tyre u sjell zjarrin. Në Ditën e Kijametit, Perëndia nuk do t’u flasë atyre dhe s’ka për t’i dlirë prej fajesh, dhe për ta ka dënim pikëllues.'),
(182, 2, 175, 'Ata (mohuesit) rrugën e drejtë e kanë ndërruar me të humburën dhe faljen e Zotit me mundim, dhe sa ë durueshëm që janë vetëm ata në zjarr!'),
(183, 2, 176, 'Kjo është për shkak se Perëndia ka zbritur Librin, të vërtetën e saktë, e ata që bëjnë polemikë rreth përmbajtjes së Librit janë në kundërshtim të madh me të drejtën.'),
(184, 2, 177, 'Mirësia, nuk është të kthyerit e fytyrës suaj kah lindja dhe perëndimi, por, mirësi është kur (njeriu) beson Perëndinë, Ditën e Kijametit, ëngjujt, Librin, Pejgamberët dhe të cilët me vullnetin e vet, u japin nga pasuria të dashurve të tyre, te afërmve, bonjakëve, varfanjakëve, udhëtarëvet – musafirëvet (që kanë ngelur pa të holla), lypësvet dhe të robëruarvet (për t’u liruar); e (mirësi) është edhe kryerja e namazit, dhënia e zeqatit, dhe kryesit e obligimit të tyre, kur marrin përsipër diçka; e (mirësi), është edhe për ata që durojnë në skamjen, sëmundjen dhe durimi në kohë të luftës. Këta janë besimtarët e vërtetë dhe këta janë ata që i drojnë Perëndisë (ruhen prej mëkateve).'),
(185, 2, 178, 'O besimtarë! Është porosi për ju ndëshkimi për vrasje: I liri për të lirin, skllavi për skllavin dhe femra për femrën; e atij që ia falë farefisi i të vrarit, duhet të sillet njerëzishëm (me mirësi për dëmshpërblim) dhe le të zbatohet dëmshpërblimi me të mirë. Kjo (falje) është një lehtësi dhe mëshirë nga ana e Zotit. E kushdo që pas kësaj (faljes), e kapërcen kufirin, për atë ka dënim të rreptë.'),
(186, 2, 179, 'O mendar! Për ju ndëshkimi është gjallëri, që të ruheni (prej vrasjeve).'),
(187, 2, 180, 'Kur ndokush prej jush gjendet buzë vdekjes e pas veti lë pasuri, ju urdhërohet që porosia (testamenti) të bëhet në mënyrë të drejtë ndaj prindërve dhe farefisit; ky është obligim i drejtë për njerëzit e mirë.'),
(188, 2, 181, 'Kush e ndryshon (porosinë – testamentin) pasi e ka mësuar të vërtetën, - mëkat është vetëm për ata që e ndryshojnë; - e Perëndia, me të vërtetë, i dëgjon dhe i di të gjitha.'),
(189, 2, 182, 'E ai që frikohet se porositësi (i testamentit) ka bërë pa qëllim gabim ose padrejtësi, e ndreq atë, ai – për këtë punë nuk ka mëkat. – Se me të vërtetë, Perëndia është falës dhe mëshirues.'),
(190, 2, 183, 'O besimtarë! Ju është urdhëruar agjërimi, ashtu si u ka qenë urdhëruar atyre para juve, për t’iu shmangur mëkateve,'),
(191, 2, 184, '(agjërimi) zgjat disa ditë. Ndonjëri prej jush që gjendet i sëmur ose gjendet në udhëtim e sipër, ai i agjëron aq ditë sa i ka prishur – në ditët e tjera. Dhe ata (njerëz) që kanë vështirësi për të agjëruar (si për shembull, plaku i mbetur), duhet të japë aq sa mund të ngopet një i varfër me ushqim gjatë ditës (për një ditë agjërimi – një ditë ushqimi). E kushdo që të bëjë ndojë të mirë më tepër, për të është më mirë. Por, agjërimi juaj është më i dobishëm, në qoftë se e dini (do ta agjëronit).'),
(192, 2, 185, 'Këto ditë janë, ditët e muajit të ramazanit, në të cilin muaj ka zbritur Kur’ani, që është udhërrëfyes për njerëzit, plot me argumente të dokumentuara për rrugën e drejtë dhe dallues i së mirës nga e keqja. Pra, kushdo që nga ju gjendet në këtë muaj – le të agjërojë, ndërsa ai që është i sëmurë ose gjendet në udhëtim e sipër (le të agjërojë aq ditë sa nuk i ka agjëruar), - Perëndia ka bërë lehtësi për ju, e jo vështirësi e mundime, - për të plotësuar numrin e ditëve të agjërimit (në një kohë tjetër), dhe për këto (lehtësi) duhet të madhëroni Perëndinë, që u ka drejtuar në rrugë të drejtë e të bëheni falenderues ndaj Tij.'),
(193, 2, 186, 'Kur besimtarët e Mi të pyesin për Mua, (thuaju): Unë jam afër, i përgjigjem lutjeve të lutësit, kur ai t’më lutet Mua. Prandaj le ta plotësojnë thirrjen Time (për agjërim e detyra tjera) dhe le të më besojnë Mua (mos të shmangen nga besimi), për të qenë në rrugën e drejtë.'),
(194, 2, 187, 'Juve u lejohet që gjatë netëve të agjërimit t’i afroheni bashkëshorteve tuaja. Ato janë petku i juaj, e ju jeni petku i tyne. Perëndia e di mirë se ju nuk mund të duroheni, e keni tradhëtuar veten, e Perëndia u ka pranuar pendimin tuaj dhe u ka falur gabimin. Prandaj, u lejohet kontakti me bashkëshortet tuaja dhe tani kërkoni atë që ka caktuar Perëndia për ju. Dhe hani e pini deri në mëngjes (deri në imsak) deri sa të dallohet peri i bardhë nga peri i zi, pastaj plotësonie agjërimin deri në muzg, (deri në perëndim të diellit). Mos ju afrohuni atyre gjatë kohës, kur gjeneni në itikafë në xhami! Këta janë kufijt e Perëndisë; pra mos ju afrohuni atyre! Kështu pra, Perëndia ua shpjegon njerëzve dispozitat e veta, që të ruhen e të mos bëjnë punë të ndaluara.'),
(195, 2, 188, 'Mos i hani pasurinë njëri-tjetrit me pa të drejtë! Dhe mos u bëni mitdhënës te gjykatësit për t’i ngrënë njerëzve me tradhëti një pjesë të mallit, se ju e dini (që këto janë mëkate).'),
(196, 2, 189, 'Të pyesin ty (o Muhammed!) për hënën, (ndryshimin e saj); thuaju: “Këto për njerëzit janë shenja të kohës dhe të haxhxhillëkut”. Nuk shprehet mirësia me të hyrit në shtëpi kah shpina (sipas zakonit të atëhershëm të arabëve), por mirësia qëndron në të ruajturit nga të këqijat. Hyni në shtëpi kah dyert e tyre dhe droni Perëndisë, për të qenë të shpëtuar!'),
(197, 2, 190, 'Luftoni në rrugën e Zotit – kundër atyre që luftojnë kundër jush, por mos tejkaloni kufirin! – Se, Perëndia, nuk i don ata që e kalojnë kufirin.'),
(198, 2, 191, 'Vritni ata kudo që t’i gjeni dhe dëboni prej vendeve ashtu siç ju dëbuan ata juve. Se keqpërdorimi është më i rëndë se vrasja. Mos luftoni me ta pranë Mesxhidi Haramit! Për derisa ata të vriten me ju-aty! E, nëse ata luftojnë me ju (aty), atëherë ju vritni ata! I këtillë duhet të jetë ndëshkimi për jobesimtarët.'),
(199, 2, 192, 'Por, në qoftë se ata heqin dorë (nga të këqijat), atëherë Perëndia me të vërtetë është mëshirues dhe falë.'),
(200, 2, 193, 'Luftoni ata deri sa të zhduken ngatërresat dhe deri sa të bëhet besimi vetëm për Perëndinë! E në qoftë se ata heqin dorë (prej ngatërresave), atëherë s’ka armiqësi, përveç atyre që kanë tejkaluar kufirin.'),
(201, 2, 194, 'Muaji i madh për muaj të madh; respektimet janë reciproke. Pra, kushdo që ju sulmon duke tejkaluar kufirin, edhe ju sulmoni ata, ashtu siç ju sulmuan ata juve (reciprokisht)! Droni Perëndisë, dhe dijeni se, me të vërtetë, Perëndia është me ata që ruhen nga të këqijat.'),
(202, 2, 195, 'Dhe shpenzonie pasurinë në rrugën e Perëndisë, dhe mos e sillni veten tuaj në shkatërrim, por bëni mirë; se Perëndia me të vërtetë i do bamirësit.'),
(203, 2, 196, 'Kryeni haxhxhin dhe umren për hir të Perëndisë. Nëse në këtë rrugë pengoheni (në kryerjen e këtij qëllimi), atëherë therrni kurban sipas mundësisë dhe mos i rruani kokat tuaja, për derisa të mos arrijë kurbani në vend (në therrtoren e caktuar); e kushdo që prej jush është i sëmurë ose që ka ndonjë mundim në kokë (e rruhet) le ta kompenzojë me agjërim, lëmoshë ose kurban. E kur të qetësoheni, ai që prej jush kryen umren deri në haxhxh le të therrë kurban sipas mundësisë. Ndërsa, ai që nuk ka mundësi, është i detyruar të agjërojë tri ditë në haxhxh dhe shtatë ditë pas kthimit nga haxhxhi, e le të plotësohen dhjetë ditë. Kjo është për ata që nuk janë nga Mekka. E druajuni Perëndisë dhe dinie se Ai dënon ashpër.'),
(204, 2, 197, 'Koha e haxhxhillëkut është në muaj të caktuar; ai që e obligon veten të kryejë haxhxhin, nuk duhet të ketë marrëdhënie me gruan dhe as nuk duhet të bëjë grindje e sharje. Ndërkaq çdo vepër të mirë që bëni, Perëndia e di. Dhe furnizohuni me gjërat që u nevojiten për rrugë, e furnizimi më i mirë është të largoheni nga të lypurit (në haxhxhillëk). Prandaj druani prej Meje or mendar!'),
(205, 2, 198, 'Nuk është mëkat të kërkoni nga Zoti juaj ndonjë të mirë (materiale). E kur të ktheheni nga Arafati, përmendni Zotin në vendet e shenjta, përmendni atë Zot, që ju ka udhëzuar në rrugën e drejtë, sepse, ju më parë ishi të humbur.'),
(206, 2, 199, 'Mandej kthehuni nga Arafati, siç kthehen të gjithë të tjerët dhe kërkoni ndjesë prej Perëndisë! Se Perëndia është mëshirues e falë.'),
(207, 2, 200, 'E pasi t’i kryeni ceremonitë e haxhxhit, përmendni Perëndinë ashtu si i keni kujtuar prindërit tuaj, apo edhe më tepër! Disa njerëz thonë: “Zoti ynë epna të mira në këtë botë!” Por për këta s’ka asnjë të mirë në ahiret.'),
(208, 2, 201, 'E, disa nga njerëzit thonë: “Zoti ynë, epna të mira në këtë jetë dhe në jetën tjetër! Dhe na ruaj nga dënimi i zjarrit!”'),
(209, 2, 202, 'Për veprat që këta kanë punuar ka shpërblim. – Se, Perëndia, i bënë llogaritë shpejt.'),
(210, 2, 203, 'Dhe përmendni Perëndinë në ditët e caktuara. Por, edhe për ata që nguten dhe rrinë vetëm dytë ditë nuk kë mëkate, e nuk do të ketë mëkate edhe ai që rrinë më shumë, e ruhet (nga mëkatet tjera). Dhe, kinie frikë Perëndinë e dinie mirë se vetëm tek Ai do të tuboheni.'),
(211, 2, 204, 'Ka njerëz fjalët e të cilëve për jetën në këtë botë të entuziazmojnë, dhe betohen në Perëndinë për atë (të mirë) që ka në zemër, e që në të vërtetë është kundërshtari më i ashpër.'),
(212, 2, 205, 'E kur të kthehet (prej teje) përpiqet të bëjë ngatërresa në tokë, duke shkatërruar fushat dhe bagëtinë. Por perëndia nuk i don ngatërresat.'),
(213, 2, 206, 'E kur atij i thuhet: “Druaju Perëndisë!” ai atëherë mburret për mëkatë. Atij i mjafton skëterra. E, sa vendbanim i keq është ai vend!'),
(214, 2, 207, 'Ka njerëz që e sakrifikojnë veten për të fituar kënaqësinë e Perëndisë, - e Perëndia është shumë mëshirues i adhuruesve të vet.'),
(215, 2, 208, 'O besimtarë! Qasjuni të gjithë shpëtimit (Bindjes Hyjnore) dhe mos hkoni gjurmëv etë djallit, se ai është armik i juaj i përbetuar.'),
(216, 2, 209, 'E nëse bëni gabim (i shmangeni të drejtës) pasi jeni njoftuar me argumente të qarta, atëherë dinie se Perëndia është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(217, 2, 210, 'Nuk presin ata tjetër, veçse t’u vie ndëshkimi i Perëndisë prej réve të bardha (si rrufeja që godet në mot të kthjellët), edhe ëngjujt, dhe të merr fund puna (të zhdukën)?! – E, te Perëndia do të kthehen të gjitha punët.'),
(218, 2, 211, 'Pyeti bijt e Israelit: sa ajete (argumente) të qarta u kemi dërguar Ne atyre? – E, ata që mohojnë të mirat e Perëndisë, pasi që u janë dërguar (do të dënohen), se Perëndia, me të vërtetë është ndëshkues i rreptë.'),
(219, 2, 212, 'Atyre që nuk kanë besuar u është hijeshua jeta e kësaj bote, ndaj tallen me besimtarët. Por ata që janë ruajtur nga mohimi i mëkatevet do të jenë mbi ata në Ditën e Kijametit. – E, Perëndia i bagaton pa kufi ata që i don Ai.'),
(220, 2, 213, '(Dikur) të gjithë njerëzit i kanë takuar një bashkësie (pastaj lindi mospajtimi ndër ta), dhe Perëndia u ka dërguar profetët si sihariques dhe si frikësues, dhe me ta dërgoi Libër në të cilin gjendej e vërteta, që me të, të gjykoheshin njerëzit për ato gjëra që nuk janë pajtuar. Mirëpo, njerëzit u grindën ndërmjet veti, pasi që u erdhën argumentet e qarta, nga zilia e njëri-tjetrit; dhe atëherë Perëndia, me autorizimin e Vet i udhëzoi besimtarët që ta kuptojnë të vërtetë, lidhur me atë që nuk u pajtuan ata. – Se, Perëndia e shpie në udhë të drejtë atë që don Ai.'),
(221, 2, 214, 'A mendoni ju të hyni në xhennet pa i përjetuar ato (vuajtjet) ashtu si i kanë përjetuar të tjerët para jush? Ata i pat goditur mjerimi dhe sëmundjet dhe aq shumë qenë tronditur saqë pejgamberi dhe ata që ishin me të thirrën: “Kur më do të arrijë ndihma e Perëndisë?!” Qe, ndihma e Perëndisë është afër!'),
(222, 2, 215, 'Të pyesin ty në çfarë mënyre duhet dhënë lëmoshë? Thuaju: “Çfarëdo lëmoshe të mirë që epni ajo le t’i takojë prindërve, farefisit, bonjakëve, të varfërve dhe udhëtarëve (që kanë ngelur në rrugë pa mjete), e çfarëdo të mire që bëni, Perëndia e di atë”.'),
(223, 2, 216, 'Ju është urdhëruar lufta, e ju e urreni. Mund ta urreni një gjë, por ajo është e mirë për ju; e mund të doni një gjë, por ajo është e dëmshme për ju. – Perëndia e di e ju nuk e dini.'),
(224, 2, 217, 'Të pyesin ty për muajin e shenjtë, për luftën në të. Thuaju: “Lufta në të është mëkat i madh, - por pengimi prej udhës së Perëndisë, të mohuarit e Perëndisë, heqja dorë nga vizita Qabës dhe dëbimi i banorëve të saj nga ajo, është mëkat më i madh te Perëndia. E, keqtrajtimi (torturat) është mëkat më i madh se vrasja. Ata vazhdimisht luftojnë kundër jush, që nëse munden t’ju kthejnë nga besimi juaj. E, ata që e mohojnë fenë dhe vdesin si mohues, - veprat e (mira) të tyre do të humbasin në këtë dhe tjetrën botë, dhe ata do të jenë bnorë të skëterrës, ku do të qëndrojnë përgjithmonë.'),
(225, 2, 218, 'Ata që besuan dhe emigruan, e luftuan në udhën e Perëndisë, ata shpresojnë në mëshirën e Perëndisë. Se Perëndia është mëshirues dhe falë.'),
(226, 2, 219, 'Të pyesin ty për verë dhe bixhoz. Thuaju: “Ato u sjellin dëme të mëdha (mëkate), por edhe dobi për njerëzit, mirëpo, dëmi është më i madh se dobia”. Dhe të pyesin ty sa duhet dhënë lëmoshë. Thuaju: “Tepricën!” Qe, kështu ua shpjegon Perëndia argumentet e Tija, që të mendoni. –'),
(227, 2, 220, 'për këtë jetë dhe atë tjetrën. Dhe të pyesin ty për bonjakët, thuaju: “T’i bëni mirë atyre është vepër e bukur, e nëse jetoni bashkë me ta, ata janë vëllezërit tuaj, se Perëndia di të dallojë keqbërësin prej mirëbërësit. Sikur të donte Perëndia, do t’ju detyronte me punë të vështira, se Perëndia është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(228, 2, 221, 'Mos u martoni me paganet, përderisa të mos bëhen besimtare. Skllavja besimtare është më e mirë se paganja, edhe nëse u pëlqen juve. Mos i martoni (kurorëzoni) muslimanet për paganë, përderisa nuk bëhen besimtarë; skllavi besimtar është më i mirë se pagani, edhe nëse u pëlqen juve. Ata ju thërrasin në zjarr, kurse Perëndia, me mirësinë e Tij, ju thërret në xhennet e falje (të mëkateve) dhe ua shpjegon njerëzve argumentet e Veta, që të mendojnë ata.'),
(229, 2, 222, 'Të pyesin ty për ciklin mujor të femrave. Thuaju: “Ajo ëhstë e neveritshme”. Prandaj, gjatë kësaj kohe, largohuni prej tyre dhe mos ju afrohi derisa të pastrohen. E pasi të pastrohen, atëherë afrohi tek ato ashtu si u ka urdhëruar Perëndia. Pa dyshim, Perëndia i don ata që pendohen fort (për mëkatet) dhe ata që pastrohen shumë.'),
(230, 2, 223, 'Gratë tuaja janë arat tuaja, dhe afroheni arave tuaja si të doni e paraqitni diçka për shpirtin tuaj (të mirë). Dhe druajuni Perëndisë dhe dinie se do të takoheni me Te. (Ti o Muhammed)! Sihariqoji besimtarët.'),
(231, 2, 224, 'Mos e bëni pengesë betimin në Perëndinë për bërjen e veprave të mira, e në largimin prej çdo të lige dhe për harmoni të njerëzve! Se, Perëndia i dëgjon të gjitha dhe i din.'),
(232, 2, 225, 'Perëndia nuk ju dënon nëse betoheni pa qëllim, por judënon për atë që e bëni me qëllim. – Se, Perëndia është i butë dhe falë (nuk nxiton në dënime).'),
(233, 2, 226, 'Ata që betohen se nuk do t’u afrohen grave të veta, duhet të presin katër muaj; e nëse pendohen nga betimi, - Perëndia, me të vërtetë, është mëshirues dhe falë;'),
(234, 2, 227, 'e nëse vendosin të ndahen – Perëndia, me të vërtetë, dëgjon dhe di çdo gjë.'),
(235, 2, 228, 'Gratë e lëshuara duhet të presin tre cikle mujore, dhe nuk është e lejueshme ta fshehin atë që ka krijuar Perëndia në mitrën (anin e fëmijës) e tyre, nëse besojnë Perëndinë dhe Ditën e Kijametit. Burrat e tyre, nëse dëshirojnë mirësi dhe qetësi, kanë të drejtë t’u kthehen atyre gjatë këtij afati. Ato kanë aq të drejta sa kanë edhe detyra, sipas arsyes, - përpos që burrat kanë përparësi për vetëm një shkallë. Se, Perëndia, është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(236, 2, 229, 'Lëshuarja mund të bëhet dy herë; pra, ose të mbahet me arsye, ose të ndahen me të mirë. E juve, nuk u lejohet të merrni çfarëdo nga ato që iu keni dhuruar, përpos nëse këta të dy frikohen se nuk do t’i zbatojnë dispozitat e Perëndisë. E, nëse, ju të dy frikoheni, se nuk do të mundeni t’i kryeni dispozitat e Perëndisë, atëherë nuk është mëkat për asnjërin për atë që ia jep gruaja. Këto janë dispozitat (kufijt) e Perëndisë, andaj mos i rrënoni ato! E, ata që i rrënojnë dispozitat e Perëndisë, janë mëkatarë të mëdhenj.'),
(237, 2, 230, 'E, nëse burri e lëshon gruan (për të tretën herë), nuk mund të martohet më me te, pa u martuar ajo me një burrë tjetër. Pra, në qoftë se ndahet nga burri i dytë, atëherë nuk kanë mëkatë as ajo e as burri i parë për martesë të sërishme me shoqi-shoqin, nëse mendojnë se do t’i respektojnë dispozitat e Perëndisë; këto janë dispozitat e Perëndisë, që ua shpjegon Ai njerëzve që kuptojnë.'),
(238, 2, 231, 'Kur t’i lëshoni gratë dhe atyre t’u afrohet afati i fundit, mbani ato me një mënyrë të arsyeshme, ose i lëshoni me mirësi. Dhe mos i mbani ato me qëllim dëmtimi, se ashtu i keni bërë zullum vetes. Mos luani me urdhërat (versetet) e Perëndisë dhe kujtoni begatitë që u ka dhënë Perëndia, edhe Librin (Kur’anin) dhe diturinë e hollësishme, që ua ka zbritur, për t’ju këshilluar juve. Kinie frikë Perëndinë dhe dinie se Perëndia di çdo gjë!'),
(239, 2, 232, 'E kur t’i lëshoni gratë dhe ato ta plotësojën kohën e pritjes ligjore, mos i pengoni ato që të martohen përsëri me burrat e tyre, kur të pajtohen në mes tyre, me arsye. Kështu këshillohet çdokush prej jush që beson Perëndinë dhe Ditën e Kijametit. Kjo është më mirë dhe më pastër. Perëndia di çdo gjë, ndërsa ju nuk dini.'),
(240, 2, 233, 'Nënat le t’i japin sisë fëmijëve dy vjet, për ata që duan t’ia plotësojnë dhënien e sisës. Babai i fëmijës është i obliguar, sipas mundësisë së tij, që t’i furnizojë ato me ushqim dhe veshmbathje. Nuk ngarkohet askush përtej mundësisë që ka: nëna le të mos dëmtohet për shkak të fëmijës së saj, e as babai të mos dëmtohet për shkak të tij. Dhe trashëgimtari është i obliguar për të gjitha këto. Nëse këta të dy (prindërit), vendosin me marrëveshje dhe në mënyrë paqësore ta ndajnë fëmijën prej sisës, kjo nuk është mëkat për ta; e nëse doni t’i gjeni fëmijës suaj ndonjë mëndeshë (grua që i jep gji fëmijës së tjetër kujt), - nuk është mëkat për ju kur me mirësi doni t’ia jepni atë që e keni menduar. Druajuni Perëndisë dhe dinie se Perëndia i sheh mirë të gjitha punët që i bëni ju.'),
(241, 2, 234, 'Ata që vdesin e lënë gra, gratë e tyre duhet të presin katër muaj e dhjetë ditë (pas vdekjes së burrit). Pas kalimit (skadimit) të këtij afati, sipas dispozitave të fesë, ju nuk jeni përgjegjës për veprat dhe fatin e tyre, - se Perëndia i di mirë ato që punoni ju.'),
(242, 2, 235, 'Dhe nuk është mëkat për ju nëse atyre grave ju jepni sinjal (shenj) se do t’i kërkoni, ose, nëse këtë qëllim e fshehni në shpirtin tuaj. Perëndia e di se do të mendoni për to, mos u premtoni atyre as fshehurazi, por bisedoni me to vetëm mirësisht. Dhe mos vendosni për martesë para se të kalojë afati i paraparë i pritjes. Dhe dinie se Perëndia e di ç’ka në zemrat tuaja, andaj ruajuni prej Tij, dhe dinie se Ai është i butë dhe falë (nuk nxitonnë dënime).'),
(243, 2, 236, 'Nuk është mëkat për ju nëse i lëshoni gratë para se të keni marrëdhënie me to ose para se të vëni kurorë. Dhuroni ato me dhuratë të pëlqyeshme të parapara me dispozita: i pasuri – sipas gjendjes së tij, dhe i vobekti sipas gjendjes së vet. Kjo është detyrë e atyre që dëshirojnë të bëjnë vepra të mira.'),
(244, 2, 237, 'E nëse i lëshoni ato para se të keni marrëdhënie me to, por ia keni caktuar dhuratën e kurorëzimit, atëherë, atyre u përket gjysma e dhuratës, përpos nëse ua falë ajo, ose ia falë ai që vendos lidhjen e martesës (prindi ose kujdestari i tyre); e falja juaj është e lavdueshme. Mos harroni që të bëhi bamirës në mes jush! Se Perëndia sheh ç’punoni ju.'),
(245, 2, 238, 'Kryeni rregullisht namazin, sidomos atë të mesmin. Dhe bëni namazin për Perëndinë, duke iu lutur Atij.'),
(246, 2, 239, 'Nëse kini frikë, faluni duke qenë këmbësorë ose kalorës! E kur të jeni të sigurtë, atëherë kujtonie Perëndinë (faluni), ashtu si ju ka mësuar Ai – atë që nuk e keni ditur!'),
(247, 2, 240, 'Ndonjërit prej jush që i është afruar vdekja, e që lënë gratë pas – duhet t’u lërë atyre testament për furnizimin dhe strehimin e tyre njëvjeçar. E, nëse ato vetë e braktisin (shtëpinë), ju nuk jeni përgjegjës për atë që veprojnë ato, (e) që u është e lejueshme. Se Perëndia është i Plotfuqishëm dhe i Gjithdijshëm.'),
(248, 2, 241, 'Grave të lëshuara u takon kompensimi (sigurimi i shpenzimeve të jetesës) i caktuar (sipas mundësisë). Kjo është detyrë e besimtarëvet.'),
(249, 2, 242, 'Kështu Perëndia ua shpjegon dispozitat e Veta për të menduar ju.'),
(250, 2, 243, 'A nuk i pe ti (o Muhammed!) ata që nga frika e vdekjes – ikën nga qytetet e tyre, - e ishin me mijëra. E, Perëndia u tha atyre: “Vdisni!” – Pastaj, Ai i ngjalli ata se Perëndia është bëmirës i pakufishëm ndaj njerëzve, por shumica e tyre nuk e falenderojnë.'),
(251, 2, 244, 'Luftoni në rrugën e Perëndisë dhe dinie se Perëndia i dëgjon të gjitha dhe di çdo gjë.'),
(252, 2, 245, 'Kush është ai që i jep hua Perëndisë – hua të bukur, e që Ai ia kthen shpërblimin shumëfish? Perëndia ngushton dhe zgjeron, dhe tek Ai do të ktheheni.'),
(253, 2, 246, 'A nuk ke dëgjuar ti (o Muhammed!) që paria e bijve të Israilit – pas Musait, i thanë njërit pejgamber të tyre: “Na caktoni një sundimtar që të luftojmë në rrugën e Perëndisë!” – Pejgamberi u tha: “A do të dëgjoni ju kur të shpallet lufta?” Ata thanë: “E pse mos të luftojmë ne në rrugën e Perëndisë, na që jemi dëbuar nga atdheu ynë dhe ndarë nga fëmijët tonë?” Mirëpo, kur iu urdhërua të luftojnë, me përjashtim të një pakice, ata nuk luftuan. Perëndia i njeh mirë zullumqarët.'),
(254, 2, 247, 'Edhe Pejgamberi i tha: “Perëndia u ka dërguar Talutin për sundimtar tuaj. – Ata thanë: “Si mund të bëhet ai sundimtari ynë, kur na kemi më tepër merita se ai; për të sunduar? Atij nuk i është dhënë ndonjëfarë pasurie e konsiderueshme”. Pejgamberi tha: “Perëndia e ka zgjedhë atë për sundimtar tuajin, e ka pajisë atë me dijeni të gjerë dhe me fuqi trupore; se, Perëndia i jep pushtet kujt të dojë. Perëndia është për së tepërmi bëmirës dhe Ai është i Gjithdijshëm.'),
(255, 2, 248, 'Pejgamberi u tha atyre: “Veçoritë e sundimit të tij janë arritja e sandëkut, në të cilin ka prehje (qetësi) për ju nga Zoti juaj, dhe aty ka disa gjëra që kanë lënë familja e Musait dhe Harunit. Atë e bartin ëngjujt. Në këtë, me të vërtetë; ka argument për ju, nëse jeni besimtarë!”'),
(256, 2, 249, 'Kur Taluti bashkë me ushtrinë u nis për luftë, u tha atyre: “Perëndia do t’ju provojë ju me një lum, ai që do të pijë ujë në të, nuk është ushtar i imi, e ai që nuk pi ujë në të, përpos një grusht ujë, është imi”. Ndërsa, ata, të gjithë pinë, përveç një pakice. E, pasi kaluan lumin ai me besimtarët e tij, (pakica) i thanë njëri-tjetrit: “Ne sot nuk kemi fuqi të luftojmë me Xhalutin dhe ushtrinë e tij”. Por, ata që ishin të bindur, (një pjesë nga ajo pakicë), se do të takohen me Perëndinë, thanë: “Ah! Sa herë, me ndihmën e Perëndisë, ushtria e vogël ka ngadhnjyer mbi ushtrinë e madhe!” Se, Perëndia është me durimtarët.'),
(257, 2, 250, 'Pasi u ballafaquan me Xhalutin dhe ushtrinë e tij, thanë (besimtarët): “Zoti ynë! Na pajis me durim, na forco këmbët tona dhe na ndihmo kundër këtij populli mohues!”'),
(258, 2, 251, 'E, ata (besimtarët) me ndihmëne Perëndisë i thyen ata, e Dauti e vrau Xhalutin. Perëndia i dha atij pushtet dhe profetni, dhe i mësoi atij dituritë që deshi. Sikur Perëndia mos t’u ndihmonte njerëzve të mirë kundër të këqinjve, bota do të ishte shkatërruar. Por, Perëndia është bamirës i gjithësisë.'),
(259, 2, 252, 'Këto janë dokumentet e Perëndisë, të cilat në të vërtetë, t’i zbuluam ty (o Muhammed). Dhe, ti, me të vërtetë je njëri nga pejgamberët.'),
(260, 2, 253, 'Disa nga ata pejgamberë i kemi dekoruar më shumë se disa të tjeë. Me disa prej tyre ka folë (biseduar) Perëndia, kurse disa prej tyre i ka lartësuar në shkallë të lartë. E Isait, birit të Merjemës, i kemi dhënë dokumente të qarta dhe e kemi forcuar me Xhebrailin. Sikur të donte Perëndia, nuk do të vriteshin njerëzit që erdhën pas pejgamberëve, meqë (pasi) atyre madje u kishin zbritur argumentet, por ata u shpërndanë; disa nga ata besuan, e disa mohuan. E, sikur të donte Perëndia, ata nuk do të vriteshin mes vet, por Perëndia punon ashtu si dëshiron Ai vetë.'),
(261, 2, 254, 'O besimtarë! Jepni nga ata që kemi dhuruar Na, para se të vijë Dita në të cilën nuk ka shit-blerje, as miqësi as ndërmjetësi! – mohuesit janë të vetmit ata që e tejkaluan kufirin (zullumqarë).'),
(262, 2, 255, 'Perëndia është – s’ka tjetër zot përveç Tij, (i cili) jeton përgjithmonë dhe zotëron (mbi të gjitha krijesat)! Atë nuk e kaplon as kotja as gjumi! Të Atij janë të gjitha ato që gjenden në qiej dhe në Tokë. Kush mund të angazhohet për ndokend pa lejën e Tij? Ai di çdo gjë që ka ndodhë përpara dhe çdo gjë që do të ndodhë në të ardhmen. Por, njerëzit, nuk dinë asgjë nga dijenia e Tij, përveç asaj që Ai ka dashur t’ua tregojë. Froni (Madhëria) e Tij përfshin qiejt dhe Tokën dhe Atij nuk i vie rëndë t’i ruajë ato. Ai është shumë i Lartë, i Madhëruar!'),
(263, 2, 256, 'S’ka shtrëngim në fe se me të vërtetë është dalluar e drejta nga e shtrembëta! Ai që i mohon putat (të këqijat) dhe beson në Perëndinë – ai ka siguruar lidhjen më të fortë, e cila nuk këputet kurrë. – Se Perëndia i dëgjon të gjitha dhe di çdo gjë.'),
(264, 2, 257, 'Perëndia është mik dhe ndihmëtar, i atyre që besojnë dhe Ai i nxjerr ata nga errësira në dritë, ndërsa ata, që nuk besojnë – për mik kanë djallin, i cili i nxjerr ata nga drita e i shpie në errësirë. Këta janë banorët e skëterrës, ku do të qëndrojnë aty përgjithmonë.'),
(265, 2, 258, 'A nuk ke dëgjuar ti për atë që u fjalos me Ibrahimin për Zotin e tij: përse Perëndia i kishte dhënë pushtet (ai – Nemrudi, u bë arrogant). Kur Ibrahimi i tha: “Zoti im është Ai që jep jetë dhe vdekje”. Ai tha: “Unë ngjalli e mbyti”. Ibrahimi pastaj i tha: “E Perëndia e sjell Diellin prej lindjes, andaj sille ti prej perëndimit!” Atëherë, ai (mohuesi) mbeti me gojë hapët. Se Perëndia nuk i udhëzon ata që janë këmbëngulës e kryeneq në të keq.'),
(266, 2, 259, 'Ose a nuk ke dëgjuar për atë, i cili duke kaluar pranë një qyteti të rrënuar për tokë thirri: “Si do t’i ngjallë Perëndia këta që kanë vdekur?” Dhe Perëndia e vdiq atë për njëqind vjet e pastaj e ringjalli atë dhe e pyeti: “Sa kohë ke ndejtur këtu?” – Ai u përgjegj: “Një ditë ose gjysmë dite”. – “Jo” – i tha Perëndia, “por ke ndejtur njëqind vjet. Shiko ushqimin dhe pijen tënde, - që nuk është prishur; e shikoje edhe gomarin tënd; andaj të bëmë ty dokument (dëshmi) të ringjalljes për njerëzit; e kqyri edhe eshtrat, - shih si i mbërthejmë (lidhim), e pastaj i veshim me mish”. E, kur atij iu bë e qartë, ai thirri: “Unë e di se Perëndia është i Plotëfuqishëm për çdo gjë!”'),
(267, 2, 260, 'E, kur Ibrahimi tha: “O Zoti im! Tregom si i ngjallë Ti të vdekurit!” – Zoti tha: “A nuk ke besuar?” – “Unë kam besuar” – u përgjegj Ibrahimi, “por po dëshiroj të më qetësohet zemra (duke e parë mënyrën e ringjalljes)”. Perëndia i tha: “Merri katër zogj, afroi pranë veti (therri e coptoji), e shpërndaji në çdo kodër nga një copë prej tyre, e pastaj thirri! Ata do të vijnë me të shpejtë. Dije, se Perëndia është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(268, 2, 261, 'Ata që shpenzojnë pasurinë e vet në rrugë të Perëndisë, i shembëllejnë atij që mbjell (një) kokërr nga e cila dalin shtatë kallinj, dhe në çdo kallinj nga njëqind kokërra. – Perëndia ia shton (shpërblimin) edhe më tepër atij që don; se Perëndia është i Gjithëdijshëm dhe mirëbërës i madh.'),
(269, 2, 262, 'Ata që shpenzojnë pasurinë e vet në rrugë të Perëndisë, e që nuk e përcjellin me reklamacion as fyerje, për ata ka shpërblim te Zoti i tyre, për ta nuk ka frikë as pikëllim.'),
(270, 2, 263, 'Fjala e mirë dhe fjala e gabimit është më e vlefshme se lëmosha që përcillet me fyerje. Perëndia është i pasur (i panevojshëm për asgjë) dhe i butë (nuk nxiton në dënime).'),
(271, 2, 264, 'O besimtarë! Mos e shëmtoni të mirën e lëmoshës suaj me reklamacion dhe fyerje, siç vepron ai që shpenzon pasurinë e vet për sy e faqe të botës, e nuk beson në Perëndinë dhe Ditën e Kijametit, Ai i gjason gurit të lëmuar, në të cilin ka pak dhé. E, kur shiu i bie rrëbesh, e zhveshë atë dhe e le gur të thatë. (Hipokritët), nuk kanë kurrfarë dobie nga veprat që kanë bërë. Se, Perëndia nuk e udhëzon në rrugë të drejtë popullin mohues.'),
(272, 2, 265, 'Ata që shpenzojnë pasurinë e vet, për hirë të Perëndisë dhe që e forcojnë veten me punë të mira, i gjasojnë kopshtit të mbjellë në rrafshnaltë, në të cilin bie shi i bollshëm andaj jep fryt të dyfishtë. Nëse nuk bie shi i bollshëm, bie vesë (jep fryt). Perëndia i sheh mirë punët që bëni ju.'),
(273, 2, 266, 'Kush dëshiron prej jush të ketë një kopsht plot palme hurmash dhe hardhish nëpër të cilin rrjedhin lumenj dhe në të cilin ka gjithfarë lloj pemësh, e t’i arrijë pleqëria atij, e të ketë fëmijë të mitur, e atëherë të vijë stuhia e zjarrit mbi të dhe ta djegë? – Që kështu Perëndia ua shpjegon argumentet e Tija, që të mendoni.'),
(274, 2, 267, 'O besimtarë! Jepni nga të mirat që keni fituar ju dhe nga ato që ua kemi dhënë Ne, nga frytet e tokës. Mos dhuroni nga ato gjëra të pavlefshme të pasurisë suaj të cilat ju nuk do t’i pranonit ndryshe pos symbyllur. Dinie ju se Perëndia është i pasur (i panevojshëm për asgjë) dhe vetvetiu i lavdëruar.'),
(275, 2, 268, 'Djalli ju frikëson juve me skamje e varfëri dhe ju urdhëron të bëni mëkate, (si koprraci, etj.). Por, Perëndia ju premton falje mëkatesh dhe dhuntitë e Tij. Se, Perëndia është bëmirës i madh dhe i Gjithëdijshëm.'),
(276, 2, 269, '(Zoti) ia jep dijeninë e hollë (kuptimin e Kur’anit) kujt të dojë. E, kujtdo që i është dhënë dijenia, me të vërtetë, i është dhënë begati e pakufishme. Por, këtë, nuk e kupton tjetër kush, përveç mendarëve.'),
(277, 2, 270, 'Çkado që të shpezoni dhe çkado që synoni për të mirë – Perëndia, me siguri di për të! – Por për zullumqarët nuk ka asnjë ndihmëtar.'),
(278, 2, 271, 'Nëse lëmoshën e jepni haptazi, kjo është mirë, e nëse ua jepni të varfërve fshehurazi – ajo është edhe më mirë, dhe Ai ua shlyen disa nga mëkatet tuaja. – Se Perëndia i di mirë ato që punoni ju.'),
(279, 2, 272, 'Nuk është obligim yti (o Muhammed!) t’i udhëzosh ata në rrugë të drejtë, por Perëndia e udhëzon kënd të dojë në rrugën e drejtë. Çfarëdo ndihme të mirë që bëni, (shpërblimi) është në dobi të juaj, se ju këtë e bëni vetëm për hirë të Perëndisë! Çfarëdo që jepni ju nga të mirat do t’ju kompensohet dhe nuk do t’ju bëhet padrejtësi.'),
(280, 2, 273, '(Lëmosha) u takon të varfërve, të cilët, të angazhuar në rrugën e Zotit nuk janë në gjendje të udhëtojnë (për të fituar). Pasi që ata janë modest e nuk lypin, ai, që nuk i njeh ata, kujton se janë pasanikë. I njeh ata në fizionominë e tyre. Ata nuk i mërzisin njerëzit duke lypur. E çdo gjë që shpenzoni prej të mirave, Perëndia i di ato.'),
(281, 2, 274, 'Ata që japin nga pasuria e tyre (lëmoshë) natën dhe ditën, fshehurazi dhe haptazi, për ata ka shpërblim te Zoti i tyre; për ta nuk ka frikë as pikëllim.'),
(282, 2, 275, 'Ata që hajnë e marrin kamatë, pasurohen ashtu siç pasurohet ai, të cilin e ka prekur dhe e ka bërë të hutuar djalli. Kjo nodhë për shkak se ata thonin se tregtia është njëlloj – si kamata. Mirëpo, Perëndia e ka lejuar tregëtinë, kurse kamatën e ka ndaluar. E kujtdo që i vie këshilla nga Zoti i tij e shmanget prej kamatës, malli i tij, i fituar më parë (para ndalimit të kamatës), është i tij. E, vendimi për këtë i takon Perëndisë. E, kushdo që i kthehet (marrjes së kamatës si shit-blerje) ai do të shoqërohet me zjarrin, në të cilin do të qëndrojë përgjithmonë.'),
(283, 2, 276, 'Perëndia e shkatërron kamatën, kurse e rritë pasurinë nga e cila është dhënë lëmosha. Se Perëndia nuk i don ata që janë mohues – mëkatarë.'),
(284, 2, 277, 'Ata që besojnë dhe punojnë vepra të mira, falin namazin dhe japin zeqatin, për ata ka shpërblim te Zoti i tyre; për ta nuk ka frikë as pikëllim.'),
(285, 2, 278, 'O besimtarë! Druajuni Perëndisë dhe hiqni dorë nga kamata, nëse jeni besimtarë të vërtetë.'),
(286, 2, 279, 'E, nëse këtë nuk e bëni, ta dini se do të hapet luftë nga ana e Perëndisë dhe Pejgamberit të Tij. Nëse pendoheni, atëherë juve u takon pasuria juaj (pa kamatë). Në këtë mënyrë nuk dëmtoni askend e as nuk dëmtoheni vetë.'),
(287, 2, 280, 'E, nëse e dini se borxhliu gjendet në ngushticë, atëherë pritnie derisa të përmirësojë gjendjen; por është edhe më mirë për ju, që t’ia falni borxhin, nëqoftë se e dini.'),
(288, 2, 281, 'Dhe druajuni asaj Dite, që do të ktheheni te Perëndia, në të cilën, çdokujt, do t’i jepet ajo që ka fituar, - dhe askush nuk do të dëmtohet.'),
(289, 2, 282, 'O besimtarë! – (shënoni çfarëdo) borxhin kur ia jepni njëri-tjetrit në afatin e caktuar. Le të shënojë drejtë (atë që jepni e merrni) një shkrues në mes jush e të mos ngurrojë shkruesi që të shënojë ashtu siç e ka mësuar Perëndia. (Shkruesi) le të shënojë, e borxhliu le të diktojë dhe le t’i druajë Zotit të vet. Le të mos mungojë asgjë nga borxhi. E, në qoftë se borxhliu është mendjelehtë (batakçi) ose i dobët, ose që nuk është në gjendje të diktojë, atëherë saktësisht le të diktojë kujdestari i tij. Merrni dy dëshmitarë, prej njerëzve tuaj, e nëse nuk i keni dy dëshmitarë burra, atëherë merrni një burrë dhe dy gra – për dëshmitarë, nga ata që të jeni të kënaqur ju; në qoftë se njëra grua harron le t’ia kujtojë tjetra. Dëshmitarët nuk duhet të heqin dorë kurdo që të thirren. Mos e leni pa e shënuar borxhin, qoftë i vogël apo i madh, duke shënuar edhe afatin e pagimit. Ky proces është më i drejtë para Perëndisë, më i saktë për dëshmi dhe më i afërt për të mos dyshuar. Por, nëse është fjala për mallin që qarkullon prej dorës në dorë (shit-blerja me para në dorë), atëherë nuk do të keni mëkat nëse nuk e shënoni atë. Merrni dëshmitarë edhe kur të lidhni kontrata për shit-blerje dhe të mos dëmtohet as shkruesi as dëshmitari. E, nëse e bëni të kundërtën, atëherë keni bërë mëkat dhe ruajuni Perëndisë, - Perëndia ju mëson juve, dhe Perëndia di çdo gjë.'),
(290, 2, 283, 'E, nëse jeni në udhëtim e nuk gjeni shkrues, atëherë merrni peng. Dhe nëse njëri prejjush ka besim në tjetrin, atëherë borxhliu le ta kthejë atë që e ka marrë në besim dhe le t’i frikohet Perëndisë, Zotit të vet. Mos fshihni dëshminë; se kush e fsheh ate, zemra e tij është mëkatare. Se, Perëndia di çdo gjë që punoni ju.'),
(291, 2, 284, 'Të Perëndisë janë të gjitha që gjenden në qiej dhe në Tokë. I tregoni ju ato që kini në shpirtin tuaj, apo i fshehni, Perëndia do t’ju pyet për të dhe do ta falë kënd të dojë, e do ta dënoj kënd të dojë. Se, Perëndia, me të vërtetë, është i Plotëfuqishëm.'),
(292, 2, 285, 'Pejgamberi beson në ate që i është shpallur (Kur’ani) nga Zoti i tij, dhe besimtarët; - të gjithë ata besojnë Perëndinë, engjëjt e Tij, Librat e Tij dhe pejgamberëve të Tij: “Na nuk e dallojmë asnjerin nga pejgamberët e Tij”. Ata thonë: “Na dëgjuam dhe përulemi, - falna neve o Zoti ynë dhe te Ti do të kthehemi!'),
(293, 2, 286, 'Perëndia nuk e ngarkon askend përtej fuqisë që ka: në dobi të tij është e mira që bënë, kurse në dëm të tij është e keqja që e punon, Zoti ynë, mos na ndëshko për atë që harrojmë ose veprojmë pa qëllim ashtu si i pate ndëshkuar ata para nesh! Zoti ynë! Mos na ngarko me atë që nuk mund ta bartim! Shlyena mëkatet tona, falna ato dhe mëshirona! Ti je Zoti ynë! Andaj, ndihmona kundër atyre që nuk besojnë.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'Elif Lám Mim.'),
(295, 3, 2, 'Perëndia është (një), s’ka zot tjetër përveç Tij, (i Cili) jeton përgjithmonë dhe zotëron (mbi të gjitha krijesat).'),
(296, 3, 3, 'Ai ta ka shpallur Librin ty, të vërtetën e saktë duke vërtetuar ato të mëparshmet. Ai e ka zbritur Teuratin dhe Ungjillin. –'),
(297, 3, 4, 'më parë udhërrëfyes për njerëzit, dhe ka zbritur Furkanin, që dallon të vërtetën nga e pavërteta. Ata që nuk besojnë versetet e Perëndisë, i pret dënimi i ashpër, se Perëndia është dënues i Plotëfuqishëm.'),
(298, 3, 5, 'Pa dyshim, Perëndisë nuk i mbetet asgjë sekrete, as në Tokë as në qiell!'),
(299, 3, 6, 'Ai ju formon juve në mitër ashtu si don Ai; s’ka zot tjetër përveç Tij, të Plotëfuqishëm dhe të Gjithëdijshëm!'),
(300, 3, 7, 'Ai ta ka shpallur Librin ty (Kur’anin). Disa nga versetet janë të qarta, me kuptim konkret e të hapët sheshazi, ato janë themelet e Librit, e për disa të tjera nevojitet komepntimi (pasi që me pak fjalë shprehen shumë kuptime të thella). Ata, që janë zemërkëqinj – priren kah shpjegimi i atyre që janë më pak të qarta, duke krijuar pështjellim dhe duke komentuar sipas dëshirës së vet. Por, komentimin e tyre e di vetëm Perëndia. E, ata që janë thelluar në dijeni thonë: “Na i besojmë (Kur’anit). Të gjitha këto janë nga Zoti ynë!” Këtë e kuptojnë vetëm mendarët.'),
(301, 3, 8, 'Zoti ynë! Mos na i shmang zemrat tona, pasi që na i ke udhëzuar në rrugën e drejtë, dhe epna nga mëshira Juaj; Ti, me të vërtetë je Ai që dhuron shumë!'),
(302, 3, 9, 'Zoti ynë! Ti je tubues i njerëzve në ditën që nuk ka farë dyshimi. Me të vërtetë, Perëndia e realizon premtimin.'),
(303, 3, 10, 'Me të vërtetë atyre që nuk besojnë, nuk do t’i shpëtojë asgjë nga dënimi i Perëndisë, - as pasuria as fëmijët e tyre. Ata do të jenë lëndë djegëse e zjarrit,'),
(304, 3, 11, 'do të jenë ashtu siç ishin shoqëruesit (ithtarët) e Faraonit dhe ata që ishin para tyre. Ata i përgënjeshtruan versetet tona; prandaj, Perëndia i përlau ata për mëkatet që bënë. Se Perëndia është ndëshkues i rreptë.'),
(305, 3, 12, 'Thuaj (o Muhammed!) atyre që mohuan: “Ju do të shtypeni dhe do të tuboheni në skëterrë. E, sa shtrat i keq është ai vend!'),
(306, 3, 13, 'Ju keni shembullin në dy ushtritë që ishin në konflikt: njëra që luftoi në rrugën e Perëndisë, dhe tjetra – e mohuesve, së cilës i dukej se para vetes ka dy herë më shumë kundërshtarë. Se Perëndia me ndihmën e vet e forcon kend të dojë. Kjo, me të vërtetë, është këshillë për largpamësit.'),
(307, 3, 14, 'U hijeshua për njerëzit lakmia ndaj grave, fëmijëve, arit dhe argjendit të grumtulluar, kuajve të bukur, bagëtive dhe të lashtave. Këto janë kënaqësitë e kësaj jete; por vendi më i bukur i kthimit është te Perëndia.'),
(308, 3, 15, 'Thuaj (o Muhammed!): “A doni t’ju lajmëroj juve për gjëra më të mira se ato? Ata që ruhen nga mëkatet, te Zoti i tyre do të kenë kopshte, nëpër të cilat rrjedhin lumenjt, në to do të banojnë përgjithmonë, e bashkëshorte të pastra dhe kënaqësi nga Perëndia”. Se Perëndia i njeh mirë robërit e vet!'),
(309, 3, 16, 'të cilët thonë: “Zoti ynë! Me të vërtetë na kemi besuar, andaj na fal mëkatet tona dhe na ruaj nga dënimi i zjarrit!”'),
(310, 3, 17, 'ata janë durimtarët, dashamirë të së vërtetës, përulësit e Perëndisë, ata që japin lëmoshë dhe që kërkojnë faljen e mëkateve para agimit.'),
(311, 3, 18, 'Perëndia dëshmon se Ai është vetëm një, - por edhe engjëjt, e të diturit (e dëshmojnë këtë), duke qenë të qëndrueshëm në të vërtetën. S’ka zot tjetër përveç Tij. Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(312, 3, 19, 'Feja e vërtetë te Perëndia është vetëm Islamizmi. Ata, të cilëve u është dhënë Libri, ranë në konflikt – pasi u erdhi njohuria dhe këtë, për shkak të egoizmit në mes tyre; e kushdo që i mohon argumentet e Perëndisë, Perëndia është i shpejt në llogari.'),
(313, 3, 20, 'Nëse ata polemizojnë me ty (o Muhammed!) thuaju: “Unë i përulem vetëm perëndisë, si dhe ata që më pasojnë mua”. Dhe thuaju atyre që u është dhënë Libri si dhe analfabetëve (arabëve). “A e keni pranuar Islamizmin?” E, nëse e pranojnë Islamizmin, atëherë kanë gjetur udhën e drejtë. E, nëse refuzojnë, detyra jote është vetëm që t’i thërrashës. Se Perëndia i sheh mirë (ç’punojnë) robërit e vet.'),
(314, 3, 21, 'Ata që nuk besojnë në dokumentet e Perëndisë dhe vrasin pejgamberët pa farë të drejte, e vrasin edhe ata njerëz të cilët kërkojnë të veprohet me drejtësi, sihariqoi (paralajmëroi) me dënim të dhëmbshëm.'),
(315, 3, 22, 'Këta janë ata, veprat e të cilëve kanë falimentuar në këtë botë dhe në tjetrën. Ata nuk do të kenë kurrfarë ndihmëtarë.'),
(316, 3, 23, 'A nuk po i sheh ata, të cilëve u është dhënë një pjesë e Librit (Teurati)? Ata thirrën në Librin e Zotit për gjykim në mes tyre, e pastaj, një pjesë e tyre kthyen shpinën, duke qenë refuzues.'),
(317, 3, 24, 'Ky mospranim është se ata thanë: “Neve nuk na prek zjarri i skëterrës, përveç nja disa ditë”. Trillimet e tyre i kanë mashtruar ata – në besimin e vet.'),
(318, 3, 25, 'E çka do të ndodhë me ata, kur do t’i tubojmë në Ditën, për të cilën s’ka dyshim? Dhe çdokujt i jepet shpërblimi i veprave, e ata nuk do të dëmtohen.'),
(319, 3, 26, 'Thuaj (o Muhammed!): “O Zoti im, Zotërues i gjithë pushtetit! Ti i jep pushtetin kujt të duash, dhe Ti ia heq dorësh pushtetin kujt të duash; Ti e lartëson kënd të duash dhe e poshtëron kënd të duash. E mira është në dorën Tënde! Ti me të vërtetë, për çdo gjë je i Plotëfuqishëm”.'),
(320, 3, 27, 'Ti bënë që nata të hyjë në ditë, dhe bënë që dita të hyjë në natë (duke u zgjatur herë njëra – hera tjetra). Ti e nxjerr të gjallin prej të vdekurit. Dhe Ti nxjerr të vdekurin prej të gjallit. Ti furnizon kënd të duash pa llogari (pa faturë).'),
(321, 3, 28, 'Besimtarët mos t’i marrin për miq qafirat (mohuesit), duke i lënë anash besimtarët. Ata që bëjnë këtë, nuk kanë çka të shpresojnë te Perëndia. Ketë mund ta bëni vetëm për t’u ruajtur nga ata – Perëndia u bënë vërejtje për pezmin e Tij. Dhe, të gjithë do të ktheheni te Perëndia.'),
(322, 3, 29, 'Thuaju: “Nëse ju fshehni atë që keni në zemrat tuaja, ose e shpallni haptazi, Perëndia e di atë, dhe Ai di çdo gjë që ka në qiej dhe në Tokë. Se Perëndia për çdo gjë është i Plotëfuqishëm.'),
(323, 3, 30, '(Kujtoje) Ditën, kur çdokush gjenë atë që ka punuar mirë dhe atë që ka punuar keq; e dëshiron ai që në mes tij dhe të keqes së tij, të ketë distancë të madhe. Perëndia u bënë vërejtje për pezmin e Tij, se Perëndia është fort i dhëmbshëm për adhuruesit e Tij.'),
(324, 3, 31, 'Thuaju (o Muhammed!): “Nëse ju e doni Perëndinë, eni pas meje, atëherë Perëndia ju do juve dhe ju falë mëkatet tuaja! Se Perëndia falë dhe është mëshirues.'),
(325, 3, 32, 'Thuaju (o Muhammed!): “Përuluni Perëndisë dhe Profetit!” E, në qoftë se abstenojnë, Perëndia, me të vërtetë, nuk i don mohuesit.'),
(326, 3, 33, 'Perëndia e zgjodhi Ademin, Nuhun dhe familjen e Ibrahimit dhe familjen e Imranit në mesin e popujve;'),
(327, 3, 34, 'pasardhës njëri pas tjetrit. Perëndia është dëgjues dhe di çdo gjë.'),
(328, 3, 35, '(Kujtoje atë kohë, o Muhammed!) Kur gruaja e Imranit tha: “Zoti im, unë Ty të kam përgjëruar (qëllimuar) atë që është në barkun tim, që të shërbejë vetëm Ty; andaj, pranoje prej meje! Se Ti, me të vërtetë, je dëgjues dhe di çdo gjë.'),
(329, 3, 36, 'E kur ajo e lindi (fëmijën) tha: Zoti im! Unë lindra femër, - Perëndia di më mirë çka lindi ajo, - “Mashkulli nuk është si femra, unë e emërova atë Merjem dhe ta kam lënë atë dhe brezin e saj nën mbrojtjen Tënde nga djalli i mallkuar”.'),
(330, 3, 37, 'Dhe Zoti i saj e pranoi mirë atë (gocën), e bëri që të rritet bukru dhe e la nën kujdestarinë e Zekerias. Sa herë që hynte Zekeriai në dhomën e saj (në xhami), gjente pranë saj ushqim. (Atëherë ai¬¬) e pyeste: “O Merjem! Nga të vie ky furnizim ty?” Ajo përgjigjej: “Ky është nga Perëndia, se Perëndia e furnizon kënd të dojë, pa llogari (faturë)”.'),
(331, 3, 38, 'Atëherë Zekerijaja iu lut Zotit të vet e tha: “Zoti im! Dhuromë mua nga ana Jote pasardhës të mirë! Se Ti, me të vërtetë, je dëgjues i lutjes!”'),
(332, 3, 39, 'E thirrën atë engjëjt, duke u lutur ai në këmbë, në xhami (e i thanë): “Perëndia po të sihariqon ty me Jahjanë, (i cili do të jetë) vërtetues i fjalës së Perëndisë, (dhe do të bëhet) udhëheqës (i popullit), ruajtës i vetvetes dhe (do të jetë) nga pejgamberët, pasardhës i atyre të mirëve”.'),
(333, 3, 40, '(Zekerijaja) tha: “Zoti im! Qysh do të ketë djal për mua, kur mua më ka kapluar pleqëria, e gruaja ime është sterile (shterpë)? (Ai) i tha: “Kështu!” Perëndia punon çka të dojë.'),
(334, 3, 41, '(Zekerijaja) tha: “O Zot! (bënma një shenj!” (Zoti) i tha: “Shenj për ty është se tri ditë nuk do të (mund të) flasish me njerëzit, veçse me gjeste. Por, Zotin tënd përmende shumë dhe adhuroje Atë në mbrëmje dhe në mëngjes”.'),
(335, 3, 42, '(kujtoje) kur engjëjt i thanë (Merjemes): “O Merjeme! Perëndia të ka zgjedhë ty, të ka pastruar ty, dhe të ka privilegjuar ty mbi të gjitha femrat e botës.'),
(336, 3, 43, 'O Merjeme! Bindju Zotit tënd! Bëni sexhde dhe ruqu bashkë me ata që përulen!”'),
(337, 3, 44, 'Këto janë lajme të panjohura (sekrete). Na po t’i shpallim ty (o Muhammed). Ti nuk ishe i pranishëm kur ata hedhën short, se kush nga ata do të marrë Merjemën në mbrojtje. Dhe ti nuk ishe i pranishëm kur ata grindeshin.'),
(338, 3, 45, '(Kujtoje) atë kohë kur engjëjt i thanë: “O Merjeme! Perëndia të sihariqon ty me një djalë: emrin do ta ketë Mesih – Isa i biri i Merjemit, do të bëhet i famshëm në këtë botë dhe në tjetrën dhe do të jetë njëri nga të afërmit e Perëndisë.'),
(339, 3, 46, 'Dhe do të bisedojë (Isai) me njerëzit edhe duke qenë i vogël në djep edhe duke qenë si njeri i rritur, dhe do të jetë nga më të mirët.'),
(340, 3, 47, 'Merjemi tha: “Zoti im! Qysh do të kem unë fëmijë, kur nuk më ka prekur asnjë mashkull?” – Ja kështu – tha: “Perëndia krijon ç’të dojë Ai. Kur vendos diçka, Ai vetëm thotë për të: ‘Bëhu!’ – dhe ajo bëhet”.'),
(341, 3, 48, 'Dhe do t’ia mësojë atij shkrimin dhe diturinë, Teuratin dhe Ungjillin,'),
(342, 3, 49, 'e (pastaj) do ta dërgojë atë Pejgamber te bijtë e Israelit (të cilëve do t’u thotë): “U solla një mrekulli nga Zoti juaj”. Do të bëjë diçka prej balte në formë shpendi, dhe do të fryejë në te, e me urdhërin e Perëndisë do të bëhet shpend. Dhe do të shërojë të verbëtit dhe abrashët dhe me emrin e Perëndisë do t’i ngjallë të vdekurit. Ju informojë juve çka hani dhe çka ruani në shtëpitë tuaja. Në këto, nëse jeni besimtarë të vërtetë, me të vërtetë, ka argumente për ju.'),
(343, 3, 50, 'Dhe (u kam ardhur juve) për t’ju vërtetuar Teuratin që është para meje dhe t’ua lejoj disa gjëra që i keni pasur të ndaluara. Dhe ju kam sjellur argumentin nga Zoti juaj. Druajuni Perëndisë dhe bindmuni!'),
(344, 3, 51, 'Me të vërtetë Perëndia është im Zot dhe Zot i juaji, andaj adhuronie Atë. Kjo është rruga e drejtë!'),
(345, 3, 52, 'Kur Isai e hetoi mohimin e tyre, tha: “Kush janë ndihmëtarët e mi në rrugën e Perëndisë?” Nxënësit (e tij) u përgjegjën: “Na jemi ndihmëtarët e fesë së Perëndisë, na besojmë Perëndinë e ti Isa bëhu dëshmitar se na jemi muslimanë.'),
(346, 3, 53, 'Zoti ynë! Na besojmë ate që na ke shpallur dhe pasojmë Pejgamberin (Isanë). Regjistrona neve bashkë me dëshmitarët!”'),
(347, 3, 54, 'Dhe ata (mohuesit) përgatitën dinakëri (dredhi të keqe); por Perëndia ua ktheu më me dinakëri. Se Perëndia është më i përsosuri në dinakëri (për qëllime të mira).'),
(348, 3, 55, '(Kujtoje, o Muhammed!) kur Perëndia tha: “O Isa! Unë do të vdes ty (pasi ta plotësoj jetën e caktuar, hebrenjt nuk do të mbysin ty) dhe do të marrë pranë vetes dhe do të shpëtojë nga ata që nuk besojnë, dhe do t’i bëj ata që të ndjekin ty, për mbi ata që të mohojnë, deri në Ditën e Kijametit. Pastaj, do të ktheheni tek Unë, e do të gjykoj në mes jush për çështjet që jeni grindur.'),
(349, 3, 56, 'Ata që nuk besojnë, do t’i dënoj me dënim të ashpër në këtë botë dhe në tjetrën. Dhe nuk do të kenë askend që t’u ndihmojë.'),
(350, 3, 57, 'E ata që besojnë dhe punojnë vepra të mira, Zoti u plotëson shpërblimin. Se, Perëndia, nuk i don zullumqarët.'),
(351, 3, 58, 'Këto që po t’i tregojmë ty (o Muhammed!) janë ajete dhe këshilla të larta (Kur’ani).'),
(352, 3, 59, 'Rasti i Isait te Perëndia, në të vërtetë, është si rasti i Ademit, që e krijoi prej baltës e pastaj tha: “Bëhu!” – dhe ai u bë.'),
(353, 3, 60, 'E vërteta është nga Zoti yt, andaj, mos u bën nga ata që dyshojnë!'),
(354, 3, 61, 'Kushdo që të bëjë polemikë me ty për çështje të Isait, pasi të ka ardhur njohuria, thuaj: “Ejani, (e) po i thërrasim bijt tonë dhe bijt tuaj, gratë tona dhe gratë tuaja, dhe po e thërrasim na dhe ju veten, pastaj, të lutemi, që mallkimi i Perëndisë t’i goditë gënjeshtarët!”'),
(355, 3, 62, 'Padyshim, të gjitha këto janë lajme të vërteta. S’ka zot tjetër përveç Perëndisë. Se Perëndia është i Gjithëpushtetshëm dhe i Gjithëdijshëm.'),
(356, 3, 63, 'Në qoftë se ata ia kthejnë shpinën të drejtës, Perëndia i njeh mirë ngatërrestarët.'),
(357, 3, 64, 'Thuaj: “O ithtarët e Librit, kujtonie tregimin, që është për ne dhe për ju: “T’i shërbejmë vetëm Perëndisë që të mos i bëjmë shoq asgjë Atij, që njëri-tjetrin mos ta konsiderojmë për zot, në vend të Perëndisë!” E nëse ata kthehen nga kjo e vërtetë, thuaju atyre: “Bëhuni dëshmitarë se ne jemi muslimanë!”'),
(358, 3, 65, 'O ithtarët e Librit! Pse polemizoni për Ibrahimin, kur Teurati dhe Inxhilli janë dërguar pas tij? A nuk po mbusheni mend?'),
(359, 3, 66, 'Ju po polemizoni për atë, për të cilin dini diçka; por, pse polemizoni për atë, për të cilën s’dini asgjë? Perëndia e di hollësisht, e ju nuk dini asgjë për këtë çështje.'),
(360, 3, 67, 'Ibrahimi nuk ka qenë as hebrej as i krishterë, por ka qenë besimtarë i vërtetë. E, nuk ka qenë as nga idhujtarët.'),
(361, 3, 68, 'S’ka dyshim, se, më të afërmit e Ibrahimit, janë ata njerëz, që kanë pasuar atë dhe ky Pejgamber (Muhammedi) me besimtarët (e tij). Perëndia është mbrojtës i besimtarëvet.'),
(362, 3, 69, 'Një grup ithtarësh të Librit dëshirojnë që ju të devijoni (nga rruga e drejtë). Por, ata, e devijojnë vet – veten, e këtë nuk e vërejnë.'),
(363, 3, 70, 'O ithtarë të Librit! Përse i mohoni versetet e Perëndisë, e ju jeni dëshmitarë (që ato janë versetet e Perëndisë)?'),
(364, 3, 71, 'O ithtarët e Librit! Përse e pështjelloni të vërtetën me të pavërtetën, dhe përse e fsheni të vërtetën, kur ju e dini (që ajo është e vërtetë)?'),
(365, 3, 72, 'Një grup ithtarësh të Librit kanë thënë: “Besonie në mëngjes atë që u ka zbritur muslimanëve (Kur’anin), por në mbrëmje mohonie atë; meqë, ndoshta edhe ata (muslimanët) do të shmangën”.'),
(366, 3, 73, '(Mohuesit thanë): “Dhe besoniu vetëm atyre që ndjekin fënë tuaj!” Thuaj (O Muhammed!): “Udhëzimi i Perëndisë shtë udhëzimi i vërtetë”. (Vallë), a t’u jepën ndokujt argumentet siç u janë dhënë juve, apo (vallë) ata t’i marrin (dëshmitë e veta) si arsyetim kundër jush te Zoti juaj? Thuaj (O Muhammed!): “Dhuntia është në dorë të Perëndisë. Ai ia jep atë kujt të dojë; - se, Perëndia është mirëbërës i madh dhe i Gjithëdijshëm;'),
(367, 3, 74, 'Mëshirën e Vet, Ai ia dhuron kujt të dojë. Se, Perëndia është pronar i mirësisë së pakufishme.'),
(368, 3, 75, 'Ka edhe aso ithtarësh të Librit, që sikur t’u besosh një barrë të tërë ari, do t’jua kthejnë, por ka edhe të atillë që nëse ua jep në besim, qoftë edhe një dinar, nuk ta kthen, përveç nëse nuk i ndahesh (me ankesë e kërkesë). Kjo është kështu, ngase ata thonë: “Neve nuk na obligon asgjë ndaj analfabetëve”. Ata flasin gënjeshtra për Perëndinë, e ata e dinë (se vetë janë gënjeshtarë).'),
(369, 3, 76, 'Jo! (nuk është ashtu si thanë hebrenjt), por kushdo që plotëson premtimin e tij dhe ruhet nga të këqijat, Perëndia, me të vërtetë, i don ata që i drojnë Atij.'),
(370, 3, 77, 'Me të vërtetë, ata që obligimin dhe betimin e tyre ndaj Perëndisë, e shkëmbejnë me vlerë të vogël – për ta nuk do të ketë kurrfarë të mire në jetën e ardhshme, dhe Perëndia nuk ka për t’ju folur atyre, as nuk do t’i shikojë (me sy të mirë në Ditën e Kijametit), dhe as nuk do t’i pastrojë prej mëkatësh, e për ta ka dënim të rëndë.'),
(371, 3, 78, 'Me të vërtetë, një grup nga ithtarët e Librit përdredhin gjuhët e tyre (duke lexuar Librin), që të mendoni ju se ai farë leximi është prej Librit, e ajo në të vërtetë nuk është prej Librit. Dhe ata thonë: “Kjo që lexojmë është nga Perëndia”; por, ajo nuk është nga Perëndia. Ata flasin gënjeshtra për Perëndinë, e ata e dinë këtë.'),
(372, 3, 79, 'Nuk mund të mendohet, që njeriut, të cilit, Perëndia ia ka dhënë Librin dhe dijeninë e gjërë dhe profetninë, e ai t’u thotë njerëzve: “Bëhuni adhuruesit e mi, e jo të Perëndisë”. Por, ai (profeti) thotë: “Bëhuni të Zotit, meqë keni mësuar Librin dhe po mësoni!”'),
(373, 3, 80, 'Ai njeri nuk do t’ju urdhërojë juve që engjëjt dhe profetnit t’i konsideroni për zotra. Vallë! Si mund t’ju urdhërojë ai juve të bëheni mohues, pasi jeni bërë muslimanë.'),
(374, 3, 81, 'Kujtoje (o Muhammed!) atë kohë kur Perëndia mori obligimin nga pejgamberët (e ju tha): “Unë ua solla Librin dhe dijeninë e kur t’ju vijë juve Pejgamberi, vërtetues i asaj që keni ju, padyshim do t’i besoni dhe do ta ndihmoni atë”. (Perëndia) u tha atyre: “A e pranuat këtë dhe a morët obligimin Tim?” Ata u përgjegjën: “E pranuam”. (Perëndia) u tha: “Bëhuni dëshmitarë (i njëri-tjetrit), se edhe Unë bashkë me ju jam dëshmitar”.'),
(375, 3, 82, 'E ata, që i shmangen obligimit të dhënë, me të vërtetë, janë të këqinj.'),
(376, 3, 83, 'A mos kërkojnë ata tjetër, përveç fesë së Perëndisë? E, Atij i përulën të gjitha ato që gjenden në qiej dhe në Tokë, me dashje ose padashje, dhe tek Ai do të kthehen të gjithë.'),
(377, 3, 84, 'Thuaj (o Muhammed!): “Na besojmë Perëndinë dhe atë që na është shpallur neve, dhe çka i është shpallur Ibrahimit, Ismailit, Is’hakut, Jakubit, pasardhësve të tyre dhe në atë që u është dhënë Musait, Isait dhe pejgamberëve nga Zoti i tyre; - na nuk bëjmë kurrfarë dallimi në mes tyre. Na vetëm Atij (Perëndisë) i përulemi (na jemi muslimanë)”.'),
(378, 3, 85, 'E ai që kërkon tjetër fe përveç islamizmit, nuk do t’i pranohet, dhe ai në botën tjetër do të jetë i humbur.'),
(379, 3, 86, 'Perëndia nuk i udhëzon në rrugë të drejtë njerëzit që janë bërë mohues, pasi që patën besuar dhe dëshmuan se Pejgamberi është i vërtetë, e ju prezentuan atyre edhe dokumentet e qarta. Populli që është këmbëngulës në të keqe, Perëndia nuk e udhëzon në rrugë të drejtë.'),
(380, 3, 87, 'Mallkimi i Perëndisë, i engjëjve dhe i mbarë njerëzve, është shpërblimi i tyre.'),
(381, 3, 88, 'Ata përherë do të jenë nën peshën e (dënimit e të mallkimit), vuajtjet nuk do t’u lehtësohen, e as nuk do t’u jepet afat (pushim);'),
(382, 3, 89, 'përveç atyre që pas kësaj pendohen dhe përmirësohen, se Perëndia është mëshirues i madh dhe falë.'),
(383, 3, 90, 'Ata që pasi patën besuar, mohuan, - e pastj edhe shtuan mohimin e tyre, pendimi kurrsesi nuk do t’u pranohet; e ata, me të vërtetë, kanë humbur rrugën.'),
(384, 3, 91, 'Ata që janë mohues dhe vdesin mohues, me të vërtetë nuk do t’u pranohet as shpërblesa e tyre, sikurse ajo të ishte sa e tërë toka e mbuluar me ar. Ata i pret dënim i rëndë dhe për ta nuk ka ndihmëtarë.'),
(385, 3, 92, 'Kurrsesi nuk do të arrini mirësinë e lartë, përderisa të mos ndani një pjesë nga ajo që e doni. Por çkado që të ndani, Perëndia e di ate.'),
(386, 3, 93, 'Të gjitha ushqimet kanë qenë të lejuara për bijt e Israilit, përveç asaj që Israili ia ndaloi vetes para shpalljes së Teuratit. Thuaj (o Muhammed!): “Silleni Teuratin dhe lexonie nëse flisni të vërtetën!”'),
(387, 3, 94, 'E kushdo që shpif gënjeshtra ndaj Perëndisë, pas këtyre (argumenteve) ai ka kaluar nga e drejta në të shtrembëten.'),
(388, 3, 95, 'Thuaj (o Muhammed!): “Perëndia thotë të vërtetën; dhe ndiqeni fenë e Ibrahimit, e cila ka qenë e pastër. Ai s’ka qenë pagan”.'),
(389, 3, 96, 'Me të vërtetë, Faltorja e parë e ngritur për njerëz, është ajo në Bekke (Mekke), e uruar dhe udhërrëfyese e popujve.'),
(390, 3, 97, 'Në të ka shenja (simbole të dukshme, siç është) vendi i Ibrahimit (mekami Ibrahim). Kush hyn në të është i sigurt. Perëndia i ka obliguar njerëzit, ata që kanë mundësi t’i përballojnë rrugës, ta vizitojnë (haxhxhillëk) këtë Faltore. E kush i mohon urdhërat e Perëndisë (e dëmton veten). Se, me të vërtetë, Perëndia nuk ka nevojë për asgjë.'),
(391, 3, 98, 'Thuaj (o Muhammed!): “O ithtarët e Librit! – pse mohoni argumentet e Perëndisë, e Perëndia është dëshmitarë i çdo gjëje që punoni”.'),
(392, 3, 99, 'Thuaj: “O ithtarët e Librit! Pse atë që beson e prapësoni nga rruga e Perëndisë, e ju e dini që feja islame është fe e vërtetë. Se Perëndia nuk është i pakujdesshëm ndaj veprave që punoni ju.'),
(393, 3, 100, 'O besimtarë! Nëse u përuleni një grupi prej atyre që u është dhënë Libri, ata, pasi keni pranuar fenë e vërtetë, juve u kthejnë në mohues.'),
(394, 3, 101, 'E si mund të mohoni kur juve u lexohen versetet e Perëndisë dhe në mesin tuaj është Pejgamberi i Tij?! E kush mbahet fortë me Perëndinë (Kur’anin), ai është i drejtuar në udhë të mbarë.'),
(395, 3, 102, 'O besimtarë! – druajuni Perëndisë ashtu si duhet t’i druani dhe vdisni vetëm duke qenë muslimanë!'),
(396, 3, 103, 'Dhe të gjithë ju mbahuni fortë për litarin e Perëndisë (Kur’anin) dhe mos u shpartalloni. Dhe kujtonie dhuntinë e Perëndisë (që u është dhënë) juve; kur ishit në armiqësi, e u japtoi zemrat tuaja e në saje të dhuntisë së Tij u bëtë vëllezër. Dhe ju ishit buzë greminës e skëterrës, ndërsa Ai ju shpëtoi juve nga ajo. Kështu Perëndia ua shpjegon versetet e veta për t’u drjtuar ju në udhën e drejtë.'),
(397, 3, 104, 'Le të bëhet një grup prej jush që do të thërrasë në të mirë, e që të urdhërojë vepra të bukura e të ndalojë prej të shëmtuaravet! E, ata njerëz do të jenë të shpëtuar.'),
(398, 3, 105, 'Mos u bëni si ata që pasi që u erdhën argumentet e dukshme, u shpërndanë dhe u distancuan në mendime. E, për këta do të ketë dënim të madh, -'),
(399, 3, 106, 'atë ditë (të Kijametit) kur disa fytyra do të zbardhen, e disa do të nxihen. Atyre që u nxihet fytyra u thuhet: “Pasi ishit besimtarë, përse u bëtë mohues? Prandaj, shijonie dënimin për shkak se nuk besuat!”'),
(400, 3, 107, 'Kurse ato fytyra që zbardhen e shkëlqejnë do të jenë në mëshirën e Perëndisë, ku do të qëndrojnë përgjithmonë;'),
(401, 3, 108, 'Këto janë versetet e Perëndisë, që t’i lëxojmë ty me të drejtë dhe se Perëndia nuk i dëshiron askujt padrejtësi.'),
(402, 3, 109, 'Të Perëndisë janë të gjitha ato që gjenden në qiej dhe në Tokë, dhe te Perëndia do të përfundojnë të gjitha punët.'),
(403, 3, 110, 'Ju (besnikët e Kur’anit) jeni njerëzit më të mirë nga i gjithë populli që janë paraqitur ndonjëherë: dhe besoni Perëndinë, kërkoni që të punohen vepra të mira, e të shmangen nga të këqijat. E, sikur ithtarët e Librit të besonin do të ishte më mirë për ta, ka prej tyre besimtarë të vërtetë, por shumica e tyre janë të këqinj.'),
(404, 3, 111, 'Ata, përveç shqetësimit, nuk mund t’ju dëmtojnë juve. Nëse ju luftojnë juve, ata do të kthejnë thembrat dhe pastaj nuk do të ndihmohen.'),
(405, 3, 112, 'Kudo që gjenden ata janë të kapluar nga poshtërsia, përpos atyre që janë mbështetur në vargonin e Perëndisë e të njerëzve; ata do të meritojnë pezmin e Perëndisë dhe do t’i kaplojë mjerimi, për shkak se mohuan dokumentet e Perëndisë, e pa kurrfarë të drejte, i mbytnin Pejgamberët; nuk u përulën dhe kaluan kufirin në të këqija.'),
(406, 3, 113, 'Ata nuk janë të njëjtë. Disa nga ithtarët e Librit janë në të drejtën: në orët e natës lexojnë versetet e Librit të Perëndisë dhe bëjnë namaz.'),
(407, 3, 114, 'Ata besojnë Perëndinë dhe Ditën e Kijametit dhe insistojnë për vepra të mira, e të pengohen nga ato të shëmtuarat dhe prijnë kah e mira. Këta janë ndër të mirët.'),
(408, 3, 115, 'Çfarëdo të mire që punoni nuk do t’ju mohohet (për atë do të shpërbleheni). Perëndia i di mirë ata që ruhen nga të këqijat.'),
(409, 3, 116, 'Ata që nuk besojnë, nuk do t’i mbrojë nga dënimi i Perëndisë as pasuria e tyre e as fëmijët e tyre, asgjë. Ata jën banorët e skëterrës ku do të qëndrojnë përgjithmonë.'),
(410, 3, 117, 'Ate, që japin ata (mohuesit) në jetën e kësaj bote, i përngjanë frymës së akulltë, e cila goditë të lashtat e ndonjë populli që i kanë bërë dëm vetes (me mëkate), e ua zhdukë të lashtat. Perëndia nuk u ka bërë dëm atyre, por ata i kanë bërë dëm vetes.'),
(411, 3, 118, 'O besimtarë! Mos zgjidhni për miq në mesin e të tjerëve, të cilët nuk ngurrojnë që t’ju bëjnë dëm. Ata dëshirojnë dëmin tuaj. Urrejtja është shfaqur prej gojës së tyre, e atë që e fshehin në zemrën e tyre, është edhe më e madhe. Na, ju shpjeguam versetet (ajetet), nëse mendoni ju.'),
(412, 3, 119, 'Ju jeni asi njerëzish që i doni ata, por ata nuk ju duan juve! Dhe ju u besoni të gjitha libravet. Kur ata ju takojnë juve, thonë: “Na besojmë”, e kur të mbeten vetëm, nga pezmatimi ndaj jush, ata i kafshojnë majet e gishtave me dhëmbë. Thuaju: “Vdisni me pezmatimin tuaj. Se Perëndia me të vërtetë di ç’keni në zemrat tuaja”.'),
(413, 3, 120, 'Nëse juve u vjen ndonjë e mirë, at ai brengosë ajo, e nëse juve u gjen ndonjë e keqe, ata gëzohen për atë. Nëse ju duroni dhe ruheni nga mëkatet, dhelpëritë e tyre nuk mund të ju dëmtojnë asgjë. Se Perëndia ngërthen (në dijeninë e Tij) çdo gjë që punojnë ata.'),
(414, 3, 121, 'Kujtoje (o Muhammed!) atë mëngjes kur dole nga shtëpia jote për t’u caktuar besimtarëvet pozitat luftarake, - Perëndia dëgjon dhe di çdo gjë, -'),
(415, 3, 122, 'kur dy grupe nga ju menduan të kthehen prej frikës, Perëndia është ndihmëtar i tyre (e nuk ka nevojë të kthehen). Andaj besimtarët le të mbështeten vetëm te Perëndia!'),
(416, 3, 123, 'Perëndia u ka ndihmuar në Bedër, duke qenë ju të pafuqishëm – andaj druajuni Perëndisë, për të qenë falenderues, -'),
(417, 3, 124, 'kujtoje (o Muhammed!) atë kohë kur u thoshe besimtarëvet: “A nuk ju mjafton ndihma e Zotit tuaj me tremijë engjëj të dërguar?”'),
(418, 3, 125, 'Po! Po! (Ju mjaftojnë). Nëse ata ju sulmojnë, bëhuni të durueshëm dhe të dëgjueshëm dhe Zoti juaj menjëherë do t’ju dërgojë në ndihmë pesë mijë engjëj, të shënuar me shenj të dalluar.'),
(419, 3, 126, 'Perëndia e bëri këtë për t’ju sihariquar juve dhe për t’jua qetësuar zemrat. Se fitorja (ndihma) është vetëm nga Perëndia i Plotëfuqishëm dhe i Gjithëdijshëm,'),
(420, 3, 127, 'që një turmë mohuesish t’i shkatërrojë apo t’i turpërojë e të kthehen ata të humbur.'),
(421, 3, 128, 'Ty (o Muhammed!) nuk të përket asgjë (lidhur me këtë çështje), se a do t’ua pranojë Ai (Perëndia) pendimin e atyre apo do t’i dënojë, se ata me të vërtetë janë zullumqarë.'),
(422, 3, 129, 'Të Perëndisë janë gjithë ato çka gjenden në qiej dhe në Tokë! Ai falë kend të doje dhe e mundon kend të dojë. Se, Perëndia është falës e mëshirues.'),
(423, 3, 130, 'O besimtarë! – mos pranoni kamatë të shumëfishtë mbi kamatë! Kinie frikë Perëndinë, për të shpëtuar; -'),
(424, 3, 131, 'druajuni zjarrit, i cili është përgatitur për mohuesit!'),
(425, 3, 132, 'Përuluni Perëndisë dhe Profetit, për të shpresuar mëshirën.'),
(426, 3, 133, 'Nxitoni kah falja e mëkateve nga Zoti juaj dhe xhenneti, hapësira e të cilit është sa qielli e Toka; i cilit është përgatitur për ata që i ruhen Perëndisë (duke punuar vepra të mira).'),
(427, 3, 134, 'të cilët japin lëmoshë kur janë në lumturi edhe kur janë në vështirësi, që fshehin zemërimin, e ua falin fajet njerëzve. Se Perëndia i don bamirësit, -'),
(428, 3, 135, 'dhe ata, që kur bëjnë vepra të turpshme ose i bëjnë dëm vetes, e kujtojnë Perëndinë – dhe kërkojnë falje për mëkatet e tyre. E kush falë mëkatet përveç Perëndisë? – Ata nuk ngulmojnë në atë që kanë punuar, e ata e dinë këtë.'),
(429, 3, 136, 'Shpërblimi i tyre është falja madhështore (e mëkateve) nga ana e Zotit të tyre dhe xhennetet nëpër të cilët rrjedhin lumenjt! E në to do të qëndrojnë përherë. Sa shpërblim i bukur është ky, për ata që punojnë vepra të bukura!'),
(430, 3, 137, 'Para jush kanë kaluar (shumë popuj) e ligje. Udhëtoni nëpër botë dhe shihni si ka qenë fundi i atyre që i kanë përgënjeshtruar profetët.'),
(431, 3, 138, 'Këto lajme e shpjegime janë këshilla, udhërrëfyese për njerëzit besimtarë.'),
(432, 3, 139, 'Mos tregoni ligësi dhe mos u pikëlloni! Se ju gjithsesi jeni më të lartë, nëse jeni besimtarë të vërtetë.'),
(433, 3, 140, 'Nëse ju ka goditur juve ndonjë plagë edhe popullin (armik) e kanë goditur aso plagë. Ato ditë Na i ndërrojmë midis njerëzve (me triumf dhe disfatë) për t’i dalluar besimtarët e durueshëm dhe për t’i bërë disa nga ju dëshmorë. Se Perëndia nuk i donë zullumqarët, -'),
(434, 3, 141, 'e kështu, Perëndia i dlirë (prej mëkatesh) besimtarët, e për t’i zhdukë mohuesit.'),
(435, 3, 142, 'A mendoni se do të hyni në xhennet e Perëndia të mos u tregojë juve për ata që kanë luftuar dhe për ata që kanë duruar.'),
(436, 3, 143, 'E ju e dëshiruat vdekjen para se të ballafaqoheshi me te; qe pra, ju e patë ate, duke qenë vështruesit e saj.'),
(437, 3, 144, 'Muhammedi është vetëm Profet. Por, edhe para tij ka pasur pejgamberë. E, nëse ai vdes ose vritet, a mos do të ktheheni ju prapa? Kushdo që kthehet prapa (në fe të gabuar) nuk i bënë kurrfarë dëmi Perëndisë. Se, Perëndia do t’i shpërblejë falenderuesit.'),
(438, 3, 145, 'Çdo gjallesë vdes vetëm me vendimin e Perëndisë, në kohë të caktuar. Kush dëshiron mirësitë e kësaj bote, Na ia japim, e kush dëshiron shpërblimin e botës tjetër na ia japim, dhe me siguri i shpërblejmë falenderuesit.'),
(439, 3, 146, 'Sa e sa pejgamberë bashkë me shumë besimtarë të vërtetë luftuan, e megjithatë nuk u përkulën për ato që u goditën në rrugën e Perëndisë, as nuk u demoralizuan as nuk u dobësuan. Perëndia i don durimtarët'),
(440, 3, 147, 'dhe vetëm thonin: “O Zoti ynë! Falna mëkatet tona dhe teprimin në punën tonë, forcona këmbët tona dhe ndihmona kundër popullit jobesimtar!”'),
(441, 3, 148, 'Dhe Perëndia ua dhuroi atyre shpërblimin e kësaj bote dhe shpërblimin edhe më të madh në jetën tjetër. Se, Perëndia i don bamirësit.'),
(442, 3, 149, 'O besimtarë! Nëse u përuleni mohuesve, ata do t’ju kthejnë juve prapa (në të pa fe), e ju do të jeni të dëmtuar, -'),
(443, 3, 150, 'por, Perëndia është Zoti juaj dhe Ai është ndihmëtari më i mirë'),
(444, 3, 151, 'Na do të mbjellim frikë në zemrat e mohuesve, sepse ata i bëjnë shok Perëndisë, për të cilën Zoti nuk ka dhënë kurrfarë argumenti. Vendbanimi i tyre është zjarri, e sa vendstrehim i shëmtuar është ky për mohuesit!'),
(445, 3, 152, 'Me të vërtetë, Perëndia e realizoi premtimin e vet, kur ju me vullnetin e Tij e thyet armikun. Por kur ju, lidhur me detyrën që u ishte caktuar, u demoralizuat e u grindët në mes vet dhe kundërshtuat, e Ai madje u tregoi atë që e dëshironit, - disa nga ju e dëshirojnë këtë botë, kur disa nga ju dëshirojnë botën tjetër; - pastaj Ai, për t’ju provuar juve, bëri që të zmbrapseni para armikut. Dhe, me të vërtetë, Perëndia ju fali juve atë gabim. Se, Perëndia është pronar i mirësisë së madhe për besimtarët.'),
(446, 3, 153, 'Kujtonie ju atë kur ngjiteshit malit dhe nuk vinit vesh për askend, e dëshironit, - disa nga ju e dëshirojnë këtë botë, kurse disa nga ju Ai ju dënoi juve me dëshprim pas dëshprimi (për padëgjueshmëri ndaj Profetit dhe Perëndisë), që të mos pikëlloheni për atë që ju doli duarsh (fitorja), as për atë që u goditi juve (humbja). E Perëndia i di të gjitha ç’punoni ju.'),
(447, 3, 154, 'Pastaj (Perëndia), pas dëshpërimit, ju dërgoi juve një qetësi – gjum, që e kaploi një grup prej jush, kurse grupi tjetër kujdeseshin vetëm për vetveten, duke menduar për Perëndinë të pavërtetën (ndihmën e Profetit), ashtu si mendonin paganët. Ata thoshin: “Ku është fitorja që na ishte premtuar?” Thuaju (o Muhammed!): “Për të gjitha vendosë vetëm Perëndia!” Ata fshehin në vete atë që nuk duan ta shfaqin ty. Ata thonë: “Sikur të pyeteshim ne për këtë gjë, nuk do të vriteshim këtu”. Thuaju: “Dhe sikur të kishit qenë nëpër shtëpitë tuaja, prapëseprapë atyre që u është shkruar të vriten, do të dilnin në atë vend të vrasjes, - e Perëndia juve në luftë ju then për t’ju gjurmuar dhe për t’ju pastruar atë që keni në zemrat tuaja. Se Perëndia, me të vërtetë din ç’keni në zemrat tuaja.'),
(448, 3, 155, 'Ata, nga mesi i juaj që u zmbrapsën në ditën kur u ndeshën të dy ushtritë, në të vërtetë, djalli i shtyri në gabim, për shkak të disa mëkateve që kishin bërë. E, Perëndia, madje, ua fali (gabimin), sepse – Perëndia është falës dhe i butë.'),
(449, 3, 156, 'O besimtarë! – mos bëni si mohuesit që u thonin vëllezërve kur (këta) niseshin në udhëtim ose në luftim: “Sikur të rrinit me ne, nuk do të vdisnit as nuk do të vriteshi” – që këtë Perëndia t’ua bëjë pikëllim në zemrat e tyre; edhe jeta edhe vdekja janë vepër e Perëndisë. Ai i sheh të gjitha punët që bëni ju.'),
(450, 3, 157, 'Nëse ju vriteni ose vdisni në udhë të Perëndisë, falja (e mëkatëve) dhe mëshira e Perëndisë, me të vërtetë, janë më të mira nga ato që kanë grumbulluar ata.'),
(451, 3, 158, 'Vdiqët apo u mbytët, me siguri do të tuboheni te Perëndia.'),
(452, 3, 159, 'Në saje të mëshirës së Perëndisë, u solle butësisht me ta (o Muhammed!) – e sikur të ishe i ashpër dhe vrazhdë, ata do të largoheshin prej teje. Falju gabimin atyre dhe kërko falje te Perëndia për ta, e bisedo me ta për punë të jetesës. Kur të vendosish për diçka, mbështetu te Perëndia. Se Perëndia me të vërtetë, i don ata që mbështeten tek Ai.'),
(453, 3, 160, 'Nëse ju ndihmon Perëndia, s’ka kush t’ju mposht juve, e nëse ju turpëron juve, kush është ai që do t’ju ndihmojë përveç Tij (pas atij turpërimi)? Pra, besimtarët vetëm te Perëndia le të mbështeten.'),
(454, 3, 161, 'Është e pamundur për ndonjë Pejgamber, që të bëjë farë mashtrimi (të marrë fshehurazi nga pasuria e luftës), e kushdo që të bëjë mashtrim do të vijë (i ngarkuar me këtë mëkat) në Ditën e Kijametit, e pastaj do t’i jepet çdokujt ajo që ka punuar dhe askujt nuk do t’i bëhet padrejtësi.'),
(455, 3, 162, 'Vallë! A mund të krahasohet ai që gëzon kënaqësinë e Perëndisë me atë që ka merituar pezmin e Perëndisë. Strehimi i tij (mëkatarit) është skëterra. E, sa strehim i keq është ai.'),
(456, 3, 163, 'Te Perëndia ata kanë grada të ndryshme, se Perëndia i sheh të gjitha veprat që punojnë ata.'),
(457, 3, 164, 'Perëndia i dhuroi besimtarët me dhuratë të madhe, meqë nga mesi i tyre u dërgoi Profet i cili u lexon atyre ajetet e Tij, i pastron ata nga mëkatet dhe ua mëson Kur’anin e diturinë e hollë. Ata, më parë, me të vërtetë, ishin në rrugë krejtësisht të gabuar.'),
(458, 3, 165, 'Kur ju goditi juve fatkeqësia (në Uhud), e ju më parë i patët goditur armiqët tuaj dyfish (në Bedër), thatë ju: “Prej nga na erdhi neve kjo?!” Thuaj (o Muhammed!): “Kjo është prej vetë juve”. Perëndia, me të vërtetë, është i Plotëfuqishëm.'),
(459, 3, 166, 'Ajo që ju gjeti (goditi) juve, atë ditë kur u ndeshën dy ushtritë, ajo ishte me vullnetin e Perëndisë – për të zbuluar kush janë besimtarët e vërtetë,'),
(460, 3, 167, 'dhe për t’i zbuluar hipokritët, e kur iu tha atyre: “Ejani të luftoni në rrugën e Perëndisë, ose të paktën mbrohuni!” – ata u përgjigjen: “Sikur ta dinim që do të bëhet luftë e vërtetë, na, me siguri, do t’ju pasonim”. Atë ditë ata ishin më afër mohimit sesa besimit, sepse me gojën e tyre flisnin atë, që nuk e kishin në zemrat e tyre, - se, Perëndia i di mirë ato që i fshehin ata.'),
(461, 3, 168, 'Atyre që nuk luftuan e u thanë vëllezërve të vet: “Sikur të na dëgjonit neve nuk do të mbyteshit”, thuaju: “Prapsojeni vdekjen nga vetja, nëse flisni të vërtetën!”'),
(462, 3, 169, 'Kurrsesi mos i konsideroni të vdekur ata, që janë vrarë në rrugën e Perëndisë. Jo, (ata) janë të gjallë dhe të ushqyer (shpirtërisht) te Zoti.'),
(463, 3, 170, 'Janë të gëzuar për atë që ua ka dhënë Perëndia nga dhuntitë e Tij dhe sihariqohen me ata të cilët ende nuk u janë bashkuar (shoqëruar) dhe që kanë ngelur pas tyre, meqë nuk do të ketë kurrfarë frike dhe të cilët për asgjë nuk do të pikëllohen;'),
(464, 3, 171, 'sihariqohen ata me dhuntitë dhe begatitë e Perëndisë, sepse, Perëndia nuk ua humbë shpërblimin besimtarëve.'),
(465, 3, 172, 'Ata që pas disfatës që pësuan (në Uhud), iu përgjigjën thirrjes së Perëndisë dhe të Pejgamberit, dhe për ta, të cilët kanë bërë mirësi dhe që iu kanë druajtur (Perëndisë), ka shpërblim të madh;'),
(466, 3, 173, 'atyre, të cilëve u thanë njerëzit: “Armiku është përgatitur kundër jush, frikohuni!” Kjo përkundrazi ua shtoi atyre besimin, e thanë: “Neve na mjafton Perëndia, dhe Ai është mbrojtës i mrekullueshëm!”'),
(467, 3, 174, 'Ata u kthyen (nga Bedri) me dhuntitë dhe begatitë e Perëndisë, nuk i gjeti asgjë e keqe dhe bënë që Perëndia të jetë i kënaqur. Perëndia është Zot i begatisë së madhe.'),
(468, 3, 175, 'Vetëm djalli i frikëson miqët e vet (për të mos shkuar në luftë). Ju mos iu frikoni atyre! Por frikohuni prej Meje, nëse jeni besimtarë!'),
(469, 3, 176, 'Mos të brengosin ty (o Muhammed!) ata që nxitojnë në mohim. Sepse, me të vërtetë ata nuk mund t’i bëjnë kurrfarë dëmi Perëndisë. Perëndia don që të mos i shpërblejë ata me kurrfarë dhurate në botën tjetër; dhe për ata ka ndëshkim të madh.'),
(470, 3, 177, 'Me të vërtetë, ata që ndërrojnë besimin e vërtetë me mohim, nuk mund t’i bëjnë kurrfarë dëmi Perëndisë; e për ta ka ndëshkim të madh.'),
(471, 3, 178, 'Le të mos mendojnë kurrsesi mohuesit, që për ata është mirë ajo, që u zgjasim jetën. Na, me të vërtetë ua zgjasim jetën atyre vtëm për t’i shtuar ata gabimet; dhe për ata ka ndëshkim poshtërues.'),
(472, 3, 179, 'Perëndia nuk i le besimtarët në gjendjen që ndodheni ju (tash të përzier – muslimani i vërtetë me hipokritin) përderisa të mos e ndajë të keqin prej të mirit. Perëndia nuk do t’ua tregojë ate që është e fshehur, pro Perëndia zgjedh atë që të dojë nga Pejgamberët e Tij. Besoni Perëndinë dhe Pejgamberët e Tij! Nëse besoni dhe ruheni (prej hipokritit), u do të keni shpërblim të madh.'),
(473, 3, 180, 'Kurrsesi le të mos mendojnë ata se është mirë për ta që bëhen dorështrënguar e koprracë me dhuntitë që ua ka dhënë Perëndia atyre; jo, kjo është keq për ta. Padyshim, koprracia do t’u ngarkohet atyre në qafë Ditën e Kijametit. Të Perëndisë janë trashëgimi i qiejve dhe i Tokës, dhe Perëndia e di mirë ç’punoni ju.'),
(474, 3, 181, 'Perëndia i dëgjon fjalët e atyre që thanë: “Perëndia është i varfër, kurse na jemi të pasur”. Na do ta shënojmë atë që thanë ata si dhe mbytjen e pejgamberëve pa kurrfarë të drejte dhe do t’u themi Ne atyre: “Shijonie dënimin në zjarrë!”'),
(475, 3, 182, 'Ky është ndëshkimi i veprave të duarve tuaja, se Perëndia nuk është zullumqar i robërvet të vet.'),
(476, 3, 183, 'Ata (Hebrenjt) thonë: “Perëndia na ka urdhëruar që të mos i besojmë asnjë pejgamberi për derisa të mos na sjellë një kurban që ta përbijë zjarri”, - thuaju (o Muhammed!): “U kanë sjellë juve pejgamberët para meje argumente edhe për atë që thoni ju, e pse i mbytët ata nëse jeni të sinqertë?”'),
(477, 3, 184, 'Nëse ata ty (o Muhammed!) të konsiderojnë për gënjeshtar, mos u hidhëro! Se janë përgënjeshtruar edhe pejgamberë të tjerë që kanë ardhur para teje dhe kanë sjellë dokumente, broshura dhe Libër ndriçues. -'),
(478, 3, 185, 'Çdo qenie e gjallë do ta shijojë vdekjen. Shpërblimet do t’u plotësohen vetëm në Ditën e Kijametit, dhe kushdo që largohet nga zjarri i xhehennemit (skëterrës) dhe hyn në xhennet, ai ka arritur atë që ka dëshiruar. E, jeta e kësaj bote është vetëm kënaqësi mashtruese.'),
(479, 3, 186, 'Ju me siguri do të viheni në provë me pasurinë tuaj dhe me jetën tuaj dhe ju do të dëgjoni shumë fyerje nga ana e atyre që u është dhënë Libri pra jush dhe nga paganët. Nëse jeni durimtarë dhe i druani Perëndisë, me të vërtetë, ajo është vendosmëri (e madhe) në vepër.'),
(480, 3, 187, 'Kujtoje (o Muhammed!) kohën kur Perëndia mori premtimin nga ata, që u është dhën Libri se do t’ua shpjegojnë atë njerëzve dhe me siguri nuk do të fshehin asgjë nga ai; e ata pastaj e hodhën pas shpine (nuk vepruan sipas tij) dhe atë (premtimin) e ndërruan për një vlerë të vogël. E, sa këmbim i shëmtuar është ai që bënë ata!'),
(481, 3, 188, 'Mos i konsidero ata, të cilët gëzohen për ate që kanë punuar (të mshehurit e së vërtetës) dhe dëshirojnë të jenë të lavdëruar për atë që nuk e kanë punuar. Kurrsesi mos mendo se ata do të gjenden në një vend – shpëtimi; se për ata ka ndëshkim të rëndë.'),
(482, 3, 189, 'Të Perëndisë janë qiejt dhe Toka. Dhe Perëndia është i Gjithëpushtetshëm për çdo gjë.'),
(483, 3, 190, 'Me të vërtetë, në krijimin e qiejve dhe të Tokës dhe në ndërrimin e natës e të ditës (dukjen dhe zgjatjen e tyre), ka argumente për mendarët,'),
(484, 3, 191, 'për ata që e përmendin Perëndinë duke qëndruar në këmbë, dhe ndenjur, shtrirë; dhe që mendojnë në krijimin e qiejve dhe të Tokës. (Dhe thonë): “O Zoti ynë! Nuk i ke krijuar kot këto”, - falenderojmë Ty – dhe ruana nga ndëshkimi i zjarrit.'),
(485, 3, 192, 'O Zoti ynë! Cilindo që Ti e hedhë në zjarr, Ti e ke poshtëruar atë. E, për mohuesit nuk ka kurrfarë ndihmëtari.'),
(486, 3, 193, 'O Zoti ynë! Na dëgjuam grishtarin që na grishte në besim: “Besoni Zotin tuaj!” E, besuam. O Zoti ynë! Falna mëkatet tona dhe shkarkona nga gabimet, e pas vdekjes na radhit me të mirët.'),
(487, 3, 194, 'o Zoti ynë! Epna shpërblimin që na ke premtuar me anën e pejgamberëve Tu dhe mos na poshtëro në Ditën e Kijametit! Se, Ti, me të vërtetë, nuk shmangesh nga premtimi i dhënë.'),
(488, 3, 195, 'Dhe Zoti iu përgjegj lutjes së tyre: “Unë, nuk ia humbi mundin asnjë punëtori nga ju, qoftë mashkull apo femër, - ju jeni të gjithë (të barabartë në shpërblim). Atyre që emigruan, dhe u dëbuan nga vatrat e tyre, dhe u munduan në rrugën Time, dhe luftuan e u vranë, Unë me siguri, do t’ua mbuloj veprat e këqija (me falje) dhe do t’i shpie në kopshtije, nëpër të cilat rrjedhin lumenj, - shpërblim nga Perëndia. E, shpërblimi më i mirë është te Perëndia.'),
(489, 3, 196, 'Le të mos mashtrojë ty qarkullimi i mohuesve qytet në qytet.'),
(490, 3, 197, 'Kjo kënaqësi është ë shkurtër; pastaj, strehimi i tyre është skëterra. E sa shtrat i keq është ai vend!'),
(491, 3, 198, 'Por, për ata që i druajnë Perëndisë, për ata ka kopshtije, nëpër të cilat rrjedhin lumenj, në të cilat (kopshtije) do të banojnë përherë, - duke qenë këto dhurata të përgatitura prej Perëndisë, e për besimtarët e vërtetë më e mira dhuratë është ajo që është prej Perëndisë.'),
(492, 3, 199, 'Në mesin e ithtarëve të Librit, me siguri ka të atillë që besojnë Perëndinë dhe ate që u është shpallur juve dhe ate që u është shpallur atyre, janë të përulur Perëndisë, nuk i këmbejnë versetet e Perëndisë me ndonjë vlerë të pakët; ata kanë shpërblimin e tyre te Zoti i tyre. Se, Perëndia, me të vërtetë, është llogaritar i shpejtë!'),
(493, 3, 200, 'O besimtarë! – bëhuni të durueshëm dhe të qëndrueshëm (në durim), bëhuni të vendosur (në kufi të armikut), dhe kinie frikë Perëndinë, për ta arritur shpëtimin e dëshiruar.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'O njerëz! Druajuni Perëndisë, i Cili ju krijoi prej një individi, ndërsa prej atij krijoi bashkëshorten e tij, kurse prej këtyre të dyve krijoi shumë mashkuj e femra. Dhe druajuni Perëndisë, me emrin e të cilit, ju i kërkoni njëri-tjetrit diçka dhe i ruani lidhjet farefisnore. Se, Perëndia, me të vërtetë, është përherë mbikëqyrës i juaji.'),
(495, 4, 2, 'Epni pasurinë bonjakëve (kur të ëhen të aftë e vetvetes), mos ua ndërroni të keqen (nga pasuria juaj) me të mirën (nga pasuria e tyre). Mos e hani pasurinë e tyre (të përzier) me pasurinë tuaj. Se kjo, me të vërtetë, është mëkat i madh.'),
(496, 4, 3, 'Nëse droni se nuk bëni drejtësi ndaj bonjakëve, (në martesën me to), atëherë, martohuni me ato femra që ju pëlqejnë nga dy, nga tri ao nga katra; e nëse frikoheni se nuk do të jeni të drejtë (ndaj të gjithave njësoj), atëherë mjaftohuni me një grua ose me atë që e keni në pushtetin tuaj. Në këtë mënyrë, më së lehti do t’i ruheni padrejtësisë.'),
(497, 4, 4, 'Epjuni me të mirë grave tuaja dhuratën e kurorëzimit, e nëse ato u falin me dëshirën e vet diçka prej atyre dhurtave, hani ate me të mirë dhe kënaqësi!'),
(498, 4, 5, 'Dhe mo u jepni (prindër dhe kujdestar) plëngprishëve pasurinë tuaj, që ua ka bërë Perëndia mjet ekzistimi dhe udhëheqje në jetë, e ushqeni ate me këtë (pasuri) dhe vishni ata, dhe bisedoni me ta me fjalë të mira.'),
(499, 4, 6, 'Provoni ju bonjakët kur të bëhen për martesë, e nëse çmoni pjekuri tek ata, atëherë epjuni pasurinë e tyre. Dhe mos nxitoni në shpënzimin e pasurisë së tyre në mënyrë shkapërderdhëse, për derisa të mos rriten ata. Në qoftë se (kujdestari) është pasanik, le të frrenohet në marrjen e pasurisë së tyre, e në qoftë se është i varfër, le të hajë (nga ajo pasuri) aq s është e domosdoshme sipas zakonit. Kur t’jua dorëzoni pasurinë e tyre, bëni këtë në praninë e dëshmitarëve. E, mjafton që para Perëndisë do të jepni llogari.'),
(500, 4, 7, 'Meshkujve u takon pjesa nga ajo që ua kanë lënë prindërit dhe të afërmit, por, edhe femrave u takon pjesë nga ajo që ua kanë lënë prindërit dhe të afërmit qoftë ajo pak apo shumë si pjesë e caktuar.'),
(501, 4, 8, 'E kur në ndarjen e pasurisë (trashëgimit) të jenë prezent farefisi i largët, bonjakët dhe të varfërit, dhuroni edhe atyre diçka dhe thuajuni fjalë të ëmbla.'),
(502, 4, 9, 'Le të druajnë (prindërit) nëse ata lënë pas veti fëmijë të mitur duke u brengosur për ta (nëse mbesin bonjakë), le t’i druajnë Perëndisë dhe le të flasin drejt.'),
(503, 4, 10, 'Me të vërtetë, ata që hanë mallin e bonjakut me pa të drejtë, ata mbushin barkun e tyre me zjarr dhe ata do të përcllohen në zjarrin flakërues.'),
(504, 4, 11, 'Perëndia ju urdhëron juve në pështjen e trashëgimit për fëmijët tuaj: mashkullit i takon aq sa pjesa e dy femrave; nëse trashëgimtarë janë femrat – dy e më tepër, atyre u takojnë dy të tretat nga trashëgimia; nëse (trashëgimtar) është një femër – i takon gjysma e pasurisë; e për çdonjërin prind të të vdekurit – u përket një e gjashta nga pasuria e trashëguar, nëse i vdekuri ka lënë fëmijë; e nëse (i vdekuri) nuk ka fëmijë e që e trashëgojnë prindërit, nënës së tij i bie e treta; nëse i vdekuri ka vëllezër, nënës së tij i bie e gjashta, pas kryerjes së testamentit të lënë dhe larjes së borxhit. Ju nuk dini se cili është më i afërt për ju – përkah dobia: prindërit tuaj apo fëmijët tuaj. – Ky caktim është nga ana e Perëndisë. Se, Perëndia, me të vërtetë, është i Gjithëdijshëm dhe Plotëdijshëm.'),
(505, 4, 12, 'Juve u takon gjysma e pasurisë nga ajo që kanë lënë bashkëshortet tuaja, nëse ato nuk kanë fëmijë. E, nëse kanë fëmijë, juve (burrave) u takon e katërta e asaj (pasurie) që kanë lënë ato, pasi të kryhet testamenti e të lahet borxhi. Në qoftë se nuk keni fëmijë, atyre (grave) u takon e katërta e pasurisë që keni lënë, e nëse keni fëmijë, atyre u takon e teta nga ajo që keni lënë, pasi të kryhet testamenti e të lahet borxhi. E, nëse i vdekuri, qoftë burrë ose grua, (të) është – pa prindër dhe fëmijë, por ka vëlla ose motër (prej nënës), atëherë çdonjërit prej tyre u takon e gjashta; e nëse ka më shumë nga ata, atëherë marrin pjesë bashkarisht në të tretën e pasurisë, pasi të kryhet testamenti e të lahet borxhi, duke mos e dëmtuar askënd. Kjo është porosia e Perëndisë! – Se, Perëndia është i Gjithëdijshëm dhe i butë (nuk nxiton) në ndëshkime.'),
(506, 4, 13, 'Këta janë kufijt e dispozitave të Perëndisë. E ai që i përulet Perëndisë dhe Profetit të Tij, Perëndia e shpie atë në xhennet, nëpër të cilët rrjedhin lumenj; dhe në to do të qëndrojnë përherë. E ky është shpëtim i madh.'),
(507, 4, 14, 'E kush vepron kundër Perëndisë dhe Profetit të Tij dhe shkelë kufijt e dispozitave të Tij, Perëndia e shpie atë në skëterrë (ferr), në të cilën do të qëndrojë përherë dhe për të ka ndëshkim poshtërues.'),
(508, 4, 15, 'Për ato gra tuaja që bëjnë kurvni, kërkoni që të dëshmojnë për këtë gjë katër vetë prej jush; nëse dëshmojnë ata për këtë gjë, mbani ato në shtëpi përderisa t’i arrijë vdekja ose derisa Perëndia të bëjë rrugëdalje për to.'),
(509, 4, 16, 'Dhe, ata dy prej jush që kanë bërë këtë punë të turpshme, mundoni ata me qortim; e nëse pendohen dhe përmirësohen lironi nga të munduarit ata! Se Perëndia pranon pendimin dhe është mëshirues.'),
(510, 4, 17, 'Perëndia është pranues i pendimit vetëm për ata që bëjnë ndonjë të keqe prej padijenisë, e shpejt pendohen; e këtyre Perëndia ua pranon pendimin. Se, Perëndia është i Gjithëdijshëm dhe i Plotëdijshëm.'),
(511, 4, 18, 'Nuk pranohet pendimi i atyre, që vazhdimisht bëjnë të këqija për derisa t’u arrijë vdekja; e atëherë thonë: “Unë tani me të vërtetë po pendohem”. As për ata që vdesin duke qenë mohues, për këta, Na kemi përgatitur dënim të dhembshëm.'),
(512, 4, 19, 'O besimtarë! – nuk ju lejohet të bëni përdhunisht trashëgimtarë të grave dhe mos i pengoni ato me qëllim që t’ua merrni një pjesë nga dhuratat që ua keni dhënë, vetëm nëse bëjnë gabime sheshazi. Silluni mirë me to! Në qoftë se ato nuk ju pëlqejnë aq shumë (duroni) sepse është e mundur që juve diçka të mos u pëlqejë, por që Perëndia në atë do t’ju sjellë shumë të mira.'),
(513, 4, 20, 'Në qoftë se ju dëshironi të ndërroni gruan, duke e lëshuar njërën për të marrë tjetrën, ndërsa njërës nga ato i keni dhënë shumë pasuri, mos merrni nga ajo asgjë. A do ta merrnit ju atë duke bërë shpifje dhe gabime sheshazi?'),
(514, 4, 21, 'S’i do të merrnit ju nga ajo dhuratë, kur ju i keni dhënë njëri-tjetrit haptazi (në jetën bashkëshortore) dhe kur ato kanë marrë nga ju obligim të fortë.'),
(515, 4, 22, 'Mos i merrni ato gra, që kanë qenë gratë e baballarëve tuaj, përveç rasteve që kanë ekzistuar para islamizmit. Me të vërtetë, kjo është vepër e keqe dhe urrejtje e madhe, dhe rrugë e shëmtuar.'),
(516, 4, 23, 'U ndalohet kurorëzimi me nënat tuaja, me bijat tuaja, me motrat tuaja, me hallat tuaja, me tezet tuaja, me bijat e vëllaut tuaj, me bijat e motrës suaj, me nënat (gratë) që u kanë dhënë gji, me motrat tuaja që kanë thithur prej një gjiu, me nënat e grave tuaja (vjehrat), me vajzën e gruas suaj, - e cila gjendet nën kujdestarinë tuaj, nëse me atë grua keni pasur marrëdhënie bashkëshortore; e nëse nuk keni pasur marrëdhënie bashkëshortore, atëherë nuk ka pengesa (për kurorëzim me vajzën e gruas). U ndalohet kurorëzimi me gratë e bijve të barkut tuaj dhe t’i tuboni dy motra (artake), përveç rasteve që kanë ekzistuar para islamizmit. Se Perëndia, me të vërtetë është falës dhe mëshirues.'),
(517, 4, 24, '(U ndalohet juve të kurorëzoheni) dhe me gratë që gjenden nën kurorë, përveç atyre që janë nën pushtetin tuaj (skllave). Ky është urdhëri i Perëndisë. U janë lejuar të tjerat, përpos tyre (që u përmendën), që t’i kërkoni (për kurorëzim) me pasurinë tuaj për martesë definitive, e jo për jetë të përkohëshme (që është në kundërshtim me dispozitat e fesë). Me ato që kaloni jetë bashkëshortore, epjuni atyre dhuratat e obligueshme (shpenzimet) për jetesë. S’ka mëkat për ju, me pëlqimin e të dy palëve, që ta shtoni apo ta mungoni sasinë e dhuratës së premtuar. Se, Perëndia, me të vërtetë, është i Gjithëdijshëm dhe i Plotëdijshëm.'),
(518, 4, 25, 'Cilido nga ju, që nuk mund të martohet me gra të lira besimtare, për shkaqe të vështira materiale, le të martohet me një vajzë besimtare që gjendet nën pushtetin tuaj (robëreshë) – E Perëndia e din më së miri besimin tuaj, - se ju (të lirët dhe robërit) jeni të të njëjtit besim. Kurorëzoni ato (robëreshat) me lejen e pronarëve të tyre. Epjuni dhuratat e kurorës së tyre si është e zakonshme, duke qenë ato gra të ndershme e jo të pandershme, gjegjësisht, nga ato që jetojnë (çojnë dashuri) me dashnar. Nëse ato, si të kurorëzura bëjnë kurvëri, dënimi i tyre është për gjysmë me dënimin e grave të lira. Kjo martesë (me robëresha) është për ata nga ju, që kanë frikë se do të bëjnë gabim (kurvëri). E, nëse abstenoni (përmbaheni), kjo është edhe më mirë! Perëndia është falës dhe mëshirues.'),
(519, 4, 26, 'Perëndia don që t’jua shpjegojë dhe t’ju udhëzojë në rrugën e Pejgamberëve të mëparshëm dhe t’ju pranojë pendimet tuaja. Perëndia është i Gjithëdijshëm dhe i Plotëdijshëm.'),
(520, 4, 27, 'Perëndia don që t’ju pranojë pendimet tuaja, por ata që ipen pas punëve të këqija (epsheve), dëshirojnë që ju të largoheni krejtësisht nga udha e drejtë.'),
(521, 4, 28, 'Perëndia don që t’ju lehtësojë (barrët), e njeriu është krijuar si qenie e pafuqishme.'),
(522, 4, 29, 'O besimtarë, mos e hani ju pasurinë e shoqi-shoqit në mënyrë të palejueshme, përpos nëse ajo është tregti me kënaqësi reciproke tuajen, dhe mos vritni vetveten (dhe njëri-tjetrin)! Perëndia, me të vërtetë, është mëshirues për ju.'),
(523, 4, 30, 'E, kushdo prej jush që e bën këtë nga armiqësia dhe dhuna, ndaj tjetrit, Na padyshim do ta hedhin në zjarr. Kjo, për Perëndinë është shumë lehtë.'),
(524, 4, 31, 'Në qoftë se ju i shmangeni gabimeve të mëdha, që u janë ndaluar juve, Na do t’ju mbulojmë gabimet tuaja të vogla dhe do t’ju shpiejmë në (një) vend të mrekullueshëm (në xhennet).'),
(525, 4, 32, 'Mos lakmoni në ato gjëra, që Perëndia i ka shpërblyer disa nga ju mbi të tjerët; për mashkujt është shpërblimi i punës që bëjnë, e po ashtu edhe për femrat ka shpërblim për punën që bëjnë. Kërkoni prej Perëndisë nga mirësitë e Tij. Se, Perëndia, është i Gjithëdijshëm për çdo gjë.'),
(526, 4, 33, 'Na kemi caktuar trashëgimtarët për çdo gjë (pasuri) që lënë prindërit ose të afërmit, e për ata që jeni obliguar epjuni pjesën e tyre. Se, Perëndia, me të vërtetë, është dëshmitar për çdo gjë.'),
(527, 4, 34, 'Meshkujt udhëheqin (dhe përkujdesën) për grtë, meqë Perëndia i ka graduar (meshkujt) me disa cilësi (si fuqia fizike e tyre) mbi ato, - dhe, meqë ata shpenzojnë pasurnë e tyre (për furnizim) gra të mira janë ato që i përulën (Perëndisë), ruajnë fshehtësitë (sekretet), që ka urdhëruar Perëndia. E, për ato gra, që i droni kundërshtimit (të tyre), - këshilloni ato, bojkotoni në shtrat dhe rrihni ato (lehtas, - si për shembull, me brushë të dhëmbëve). E, nëse ato ju bindën, atëherë mos ndiqni kundër tyre kurrfarë rruge (mos ua kujtoni gabimet). Se, Perëndia, me të vërtetë, është i lartësuar dhe i madhëruar!'),
(528, 4, 35, 'Nëse i druani përçarjes midis tyre (burrit e gruas), atëherë dërgoni një arbitër (ndërmjetës) nga ana e burrit dhe një arbitër nga ana e gruas. Në qoftë se ata dëshirojnë paqëtim, Perëndia bënë marrëveshje në mes tyre – o në pajtim me kurorën, ose në shkurorëzim. Perëndia, me të vërtetë, është i Gjithëdijshëm dhe i di të gjitha qëllimet.'),
(529, 4, 36, 'Adhuronie Perëndinë, mos i bëni kurrnjë shoq Atij; bëni mirësi prindërve, të afërmëve, bonjakëve, të varfërve, fqinjëve të afërt dhe fqinjëve të largët, dhe atij që e ke pranë dhe udhëtarit (që ka mbetur ngushtë) dhe atyre që janë nën pushtetin tënd (skllavët). Se Perëndia, me të vërtetë, nuk i do ata, të cilët krenohen dhe lavdërohen,'),
(530, 4, 37, 'ata që janë koprracë (që janë dorështrënguar), ndërsa kërkojnë që edhe të tjerët të bëhen koprracë dhe, fshehin atë që ua ka dhuruar Perëndia nga mirësitë e Tij, (këta janë njerëz të këqinj). Na kemi përgatitur për mohuesit ndëshkim poshtërues.'),
(531, 4, 38, '(Perëndia nuk i don) as ata që ndajnë nga pasuria e tyre sa për sy e faqe të botës, e nuk besojnë Perëndinë as Ditën e Kijametit, (këta kanë për shok djallin), e ai që ka për shok djallin, dihet sa shok i keq është ai!'),
(532, 4, 39, 'E, ç’dëm do të kishin ata sikur të besonin Perëndinë dhe Ditën e Kijametit, dhe të ndanin nga pasuria që u ka dhënë Perëndia? Perëndia është njohës i mirë i tyre.'),
(533, 4, 40, 'Me të vërtetë, Perëndia as sa thërrmia nuk e dëmton askend; por në qoftë se mirësia është sa thërrmia, Perëndia e shumëfishon atë dhe jep nga mirësia e Tij shpërblim të madh.'),
(534, 4, 41, 'Si do të jetë pozita e të këqinjve, kur prej çdo populli të sjellin dëshmitarë (pejgamberët) dhe të sjellin ty (o Muhammed!) për dëshmitar mbi të gjithë ata?!'),
(535, 4, 42, 'Atë ditë, ata që nuk kanë besuar dhe që nuk i janë bindur Pejgamberit, do të dëshironin të rrafshohen me tokë; e Perëndisë nuk mund t’ia fshehin asnjë fjalë.'),
(536, 4, 43, 'O besimtarë! Mos bëni namaz kur jeni të dehur, përderisa të kthjelloheni e të dini se ç’flisni, as kur jeni të papstër (xhunub) – përveç nëse jeni udhëtarë, përderisa të laheni; e nëse jeni të sëmurë ose gjendeni në rrugë, ose keni kryer nevojën natyrore, ose ju keni afruar grave, e nuk gjeni ujë, atëherë synoni (pastrimin) me dhé të pastër, dhe fërkoni (me duar) fytyrën tuaj dhe duart tuaja. Se, Perëndia, me të vërtetë është shlyes dh falës i mëkateve.'),
(537, 4, 44, 'A nuk i sheh ti (o Muhammed!) ata, të cilëve iu është dhënë një pjesë prej Librit, e të cilët e ndërrojnë të mirën me të keqen dhe dëshirojnë që edhe ju të humbisni rrugën e mbarë.'),
(538, 4, 45, 'Perëndia i di më së miri armiqët tuaj. Mjafton Perëndia mbrojtës për ju. Dhe mjafton Perëndia ndihmës për ju.'),
(539, 4, 46, 'Në mesin e Hebrenjve ka asish që i ngatërrojnë fjalët prej vendesh të tyre dhe thonë: “Dëgjuam, por kundërshtuam!” dhe “Dëgjo, mos dëgjofsh!” dhe “Rá’iná!” duke prishur kuptimin me gjuhën e tyre dhe duke sulmuar në fenë. Po sikur ata të thonin: “Dëgjuam dhe u bindëm!” dhe “Dëgjo!” e “Shikona neve!” – kjo do të ishte më e mirë dhe më e drejtë për ata; por Perëndia i mallkoi ata për shkak të mohimit të tyre; ata nuk besojnë, përpos një numri të vogël.'),
(540, 4, 47, 'O ju që u është dhënë Libri! – besoni në atë që kemi zbritur Na (Kur’anin), e i cili vërteton atë që keni ju para se t’ju fshijmë fytyrat e t’ju kthejmë prapa, ose (para se) t’i mallkommë ata (t’i dënojmë) ashtu siç i mallkuam ithtarët e së shtunës. Urdhëri i Perëndisë është i zbatushëm.'),
(541, 4, 48, 'Perëndia, me të vërtetë, nuk e falë atë që i bën shok Atij, por ia falë të tjerat mëkate kujt të dojë, përveç këtij. Kushdo që i bën shok Perëndisë, ai ka bërë mëkat të madh.'),
(542, 4, 49, 'A nuk i ke parë ata, të cilët e konsiderojnë veten të pastër? Jo, Perëndia pastron kënd të dojë, e atyre nuk u bëhet dëm asnjë fije.'),
(543, 4, 50, 'Shiko si shpifin ata gënjeshtra kundër Perëndisë; mjafton që kjo shpifje të jetë sheshazi mëkat.'),
(544, 4, 51, 'A nuk i ke parë ata, të cilëve u është dhënë një pjesë e Librit? Besojnë në statuja dhe në të këqija dhe ju thanë mohuesve: “Këta janë në rrugë më të drejtë se ata që besojnë (muslimanët)”.'),
(545, 4, 52, 'Këta janë ata që i ka mallkuar Perëndia, e atë që e mallkon Perëndia për te nuk gjendet ndihmëtar.'),
(546, 4, 53, 'A do të kenë ata (Hebrenjt) ndonjë pjesë të pushtetit? Atëherë (sikur të kishin pushtet), nuk do t’i jepnin njerëzve as sa puçërza e bërthamës.'),
(547, 4, 54, 'A mos (vallë) u kanë zili njerëzve, në ato që ua ka dhuruar Perëndia nga të mirat e veta? Na u kemi dhënë breznive të Ibrahimit Librin e dijeninë e plotë dhe u kemi dhënë pushtet të madh.'),
(548, 4, 55, 'Disa prej tyre besuan në të, e disa nga ata iu shmangën atij. Për ata mjafton xhehennemi përcëllues.'),
(549, 4, 56, 'Ata që s’besojnë në dokumentat Tona, Na padyshim, do t’i djegim në zjarr. Kurdo që t’u digjet lëkura e tyre, Na do t’ua ndërrojmë me lëkurë tjetër, për të përjetuar dënimin (përsëri). Me të vërtetë, Perëndia është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(550, 4, 57, 'E, ata që besojnë dhe punojnë vepra të mira, Na do t’i shpiem në kopshte – në të cilat rrjedhin lumenjt; ata do të banojnë në to (xhennete) vazhdimisht e përgjithmonë. Ata do të kenë në to bashkëshorte të pastra dhe Na do t’i shpiem ata në hije të dendura.'),
(551, 4, 58, 'Perëndia ju urdhëron që detyrat e besueshme t’ua besoni atyre që janë të denjë për to dhe kur të gjykoni në mes njerëzve – gjykoni drejt; Perëndia, në të vërtetë, ju këshillon mrekullueshëm. E, Perëndia me të vërtetë dëgjon çdo gjë dhe vështron të gjitha punët!'),
(552, 4, 59, 'O besimtarë! – përuljuni Perëndisë. Dhe përuljuni Profetit dhe atyre që drejtojnë punët tuaja. Nëse nuk pajtoheni për ndonjë gjë (ju dhe ata që udhëheqin punët tuaja), paraqitjani Perëndisë (Kur’anit) dhe Profetit, thëniet dhe veprat e tij, nëse besoni Perëndinë dhe Ditën e Kijametit. Kjo për ju është më së miri dhe ky është komentimi më i bukur.'),
(553, 4, 60, 'A nuk i ke parë ti (o Muhammed!) ata, që pohojnë se kanë besuar në atë, që të është shpallur ty dhe ate që është shpallur para teje. Dëshirojnë që t’i gjykojë djalli (njeriu i keq), e ata janë urdhëruar që ta mohojnë (të keqin). Djalli, madje, dëshiron, që t’ua humbë atyre krejtësisht udhën e drejtë.'),
(554, 4, 61, 'Po kur t’u thuhet atyre: “Përvetësoni atë që ka shpallur Perëndia dhe Pejgamberi” i sheh hipokritët se si largohen krejtësisht prej teje.'),
(555, 4, 62, 'Si do të jetë gjendja e tyre, kur t’i godas ata katasrofa, për ate që vetë kanë punuar, e të vijnë te ti, duke u betuar në Perëndinë, thonë: “Na kemi dashur vetëm të bëjmë mirësi dhe harmoni”.'),
(556, 4, 63, 'Këta janë ata, për të cilët Perëndia di se ç’kanë në zemrat e tyre. Largohu prej tyre, (mos ua prano arsyetimet) e këshilloi ata dhe thuaju fjalë që kanë ndikim te ta.'),
(557, 4, 64, 'Dhe Na kemi dërguar pejgamber vetëm me qëllim, që njerëzit me lejen e Perëndisë t’u përulen atyre. E, kurdo që ata pas gabimit të bërë, të vinin te ti për të kërkuar falje te Perëndia dhe Pejgamberi t’i lutet Perëndisë për amnestimin e tyre, do të vërenin ata, se Perëndia, me të vërtetë, është pranues i pendimit dhe mëshirues.'),
(558, 4, 65, 'Betohem në Zotin tënd, se ata nuk do të jenë besimtarë të vërtetë, përderisa mos të marrin ty për arbitër, për ato konflikte që ekzistojnë ne mes tyre; e pastaj, të mos ndiejnë kurrfarë dëshprimi ndaj gjykimit tënd dhe të të binden ty plotësisht.'),
(559, 4, 66, 'E sikur Na t’i urdhëronim ata: “Vritni veten!” ose “Shpërnguluni nga atdheu juaj!” – pak prej tyre do ta bënin këtë. E sikur të punonin ata siç këshilloheshin, kjo për ata do të ishte më mirë dhe do të ishin më të fortë në besim,'),
(560, 4, 67, 'e atëherë, Na, padyshim, do t’u jepnim shpërblim të madh nga ana Jonë.'),
(561, 4, 68, 'dhe me siguri do t’i çonim në rrugë të drejtë.'),
(562, 4, 69, 'Kush i bindet Perëndisë dhe Profetit (në dispozitt e urdhëruara), ai do të jetë me ata të cilëve, Perëndia u ka dhënë shumë dhunti: me pejgamberët, me të drejtët, me dëshmorët dhe me njerëzit (tjerë) të mirë! E sa shokë të mrekullueshëm janë këta!'),
(563, 4, 70, 'Kjo dhunti është nga Perëndia, e mjafton që Perëndia është dijtës i çdo gjëje.'),
(564, 4, 71, 'O besimtarë! Bëhuni syçelë e marrshoni (në luftë), qoftë në grupe-grupe ose të gjithë së bashku!'),
(565, 4, 72, 'Me të vërtetë, disa prej jush ngurrojnë për të shkur në luftë. Nëse ju ndodhë ndonjë fatkeqësi (hipokriti i tillë) thotë: “Vetë Perëndia më ka shpërblyer mua për të mos u gjendur i pranishëm me ta (në atë ngjarje)”.'),
(566, 4, 73, 'E nëse ju arrinë juve ndonjë e mirë nga Perëndia, ai (hipokriti) atëherë, me siguri thotë – thua se nuk ka në mes tij dhe jush dashuri e njoftësi: - “Ah, sikur të kisha qenë me ta, e të arrija fitim të madh!”'),
(567, 4, 74, 'Le të luftojnë ata në rrugën e Perëndisë, të cilët nuk e kursejnë jetën e kësaj bote për jetën e ardhshme. E, ai që lufton në rrugën e Perëndisë, të vritet ose të fitojë, Na, me siguri, do t’i japim atij shpërblim të madh.'),
(568, 4, 75, 'Ç’keni ju që nuk luftoni në udhë të Perëndisë dhe për shpëtimin e të shtypurve: mashkujt e pafuqishëm, gratë dhe fëmijët, të cilët thërrasin: “O Zoti jonë! Nxirrna nga ky qytet, banorët e të cilit janë zullumqarë! Na cakto nga ana Juaj një mbrojtës, dhe na jep nga ana Jote një ndihmëtar!'),
(569, 4, 76, 'Ata që besojnë, luftojnë në rrugën e Perëndisë; kurse ata që nuk besojnë – luftojnë në rrugën e djallit. Luftoni kundër miqve të djallit! Sepse me të vërtetë, dinakëria e djallit është e ligët.'),
(570, 4, 77, 'A nuk i ke parë ata (o Muhammed!), të cilëve u është thënë: “Tërhiqni duart tuaja nga lufta, falnie namazin dhe jepni zeqatin!” e, kur pas kësaj, iu urdhërua që të luftojnë, një grup prej tyre u frikua nga njerëzit (mohuesit), ashtu si i frikohen Perëndisë, ose dhe më tepër dhe thirrën: “O Zoti ynë! Pse na urdhërove të luftojmë? Ah, sikur të na kishe kursyer edhe për një kohë të shkurtër!” Thuaju (atyre): “Kënaqësitë e kësaj jete janë të vogla. Jeta tjetër është më e mirë për ata që i druajnë Perëndisë e s’bëjnë mëkate. Juve nuk do t’ju bëhet asnjë fije padrejtësi.'),
(571, 4, 78, 'Kudo që të gjendeni ju, do t’ju arrijë vdekja, madje qofshi edhe në kështjellat më të forta. Në qoftë se (hipokritëve) u arrin ndonjë e mirë, ata thonë: “Kjo është nga ana e Perëndisë”. E, nëse i godit ndonjë e ligë, ata thonë: “Kjo është prej teje (o Muhammed!)” Thuaju (atyre): “Të gjitha (e mira dhe e liga) janë prej Perëndisë”. Ç’është me këtë popull, që kurrsesi nuk e kupton (Kur’anin)?!'),
(572, 4, 79, 'E mira që të gjen ty o njeri, prej Perëndisë është, kurse e keqja që të godit, është nga merita jote. Na të kemi dërguar ty (o Muhammed!) për Profet të të gjithë njerëzve. E, Perëndia mjafton për dëshmitar (të profetnisë sate).'),
(573, 4, 80, 'Kush i bindet Pejgamberit, ai iu ka bind Perëndisë. E, kush zmbrapset (mos u brengos), se Na nuk të kemi dërguar ty që të jesh rojtar i tyre.'),
(574, 4, 81, 'Dhe ata (hipokritët) thonë: “T’u kemi bind ty”. E kur largohen prej teje, një grup nga ata shjellojnë diçka tjetër nga ato që t’i thanë ty. Por Perëndia i shënon shtjellimet e tyre. Dhe largohu prej tyre, e mbështetu te Perëndia! Se Perëndia të mjafton ty për mbrojtës.'),
(575, 4, 82, 'A nuk e meditojnë ata Kur’anin! Sikur ai mos të ishte prej Perëndisë, do të gjenin në të shumë kontradiksione.'),
(576, 4, 83, 'Kur t’u vijë atyre ndonjë lajm i rëndësishëm që i përket sigurisë ose frikës, ata e përhapin ate, por në qoftë se ata këtë lajm ia përcjellin Pejgamberit dhe parisë (bashkëpunëtorëve të afërm të tij) do të kuptonin prej tyre vetëm atë, që dëshirojnë ta marrin vesh. Sikur të mos ishte mirësia e Perëndisë dhe mëshira e Tij, përpos një numri të vogël, ju të gjithë do të ndiqnit djallin.'),
(577, 4, 84, 'Lufto në rrugën e Perëndisë! Vetëm ti je i obliguar; e nxiti edhe besimtarët! Perëndia e pengon fuqinë e mohuesve. Se Perëndia është më i fuqishëm dhe më i ashpër në dënime.'),
(578, 4, 85, 'Ai që përpiqet për të mirë – edhe ai do të ketë pjesë në shpërblim, e ai që angazhohet për të keqe – edhe ai do të ketë pjesë në ndëshkim. – Se Perëndia është i Gjithëpushtetshëm.'),
(579, 4, 86, 'Kur të ju përshëndet dikush me respekt, kthenia përshëndetjen në mënyrë më të bukur, ose si ajo! Se Perëndia është llogaritar për çdo gjë.'),
(580, 4, 87, 'Perëndia është një, i vetëm, - i pashoq. Ai me të vërtetë, do t’ju tubojë në Ditën e Kijametit. Për këtë nuk ka dyshim fare! E, kush është në fjalë më i drejtë se Perëndia?'),
(581, 4, 88, 'Përse ju u ndajtët në dy grupe për çështjen e hipokritëve, kur Perëndia për shkak të veprave të tyre, i rrokullisi ata në pabesim? A mos vallë, dëshironi t’i ktheni në rrugë të drejtë ata, të cilëve Perëndia ua ka humbur rrugën, e, atë që Perëndia e lë në humbje, ti nuk mund të gjesh rrugë të drejtë për të.'),
(582, 4, 89, 'Ata dëshirojnë që edhe ju të mos besoni, ashtu siç nuk besojnë as ata, e të bëheni të barabartë me ta. Kurrsesi mos u bëni shokë me ta, përderisa të mos bëjnë emigrim në rrugën e Perëndisë. E, nëse ata shmangen, arratisni e mbytni ata, kudo që t’i gjeni! Dhe mos zgjedhni nga ata as miq as ndihmëtarë,'),
(583, 4, 90, 'përpos atyre që janë strehuar te një popull me të cilin ju keni marrëveshje, ose atyre që kanë ardhë te ju, e u vjen rënd që të luftojnë kundër jush ose të luftojnë kundër popullit të vet. Sikur të kishte dashur Perëndia, do t’u kishte dhënë pushtet mbi ju, dhe ata do të luftonin kundër jush. Në qoftë se ata tërhiqen prej jush dhe nuk hyjnë në luftë kundër jush, e ju afrojnë paqe juve, atëherë për ju Perëndia nuk lë shtek që të luftoni kundër tyre.'),
(584, 4, 91, 'Ju do të hasni në njerëz të tjerë, të cilët dëshirojnë të sigurohen nga ju dhe nga kombi i vet. Sa herë që të thirren at në ngatërresa menjëherë rrokullisen në të. Në mos u shmangëshin ata prej jush dhe në mos ju ofrofshin paqe, dhe në mos heqshin dorë nga luftimi kundrë jush, atëherë arratisni e mbytni ata kudo që t’i gjeni! Këta janë ata, ndaj të cilëve ju kemi dhënë juve argument të qartë kundër tyre.'),
(585, 4, 92, 'Nuk bënë ta vrasë besimtari-besimtarin, përveç me pahir. Kush vret besimtarin me pahir, ai duhet të lirojë nëj besimtar të robëruar dhe të paguaj gjakun trashëgimtarëve të të vrarit, përpos në qoftë se ia falin gjakun familja e të vrarit. Mirëpo, nëse i vrari është nga populli që e keni armik, e njëkohësisht është besimtar, (në këtë rast) duhet të lirohet një besimtar i robëruar. E, nëse, i vrari është nga populli, me të cilin ju keni lidhur marrëveshje, (në këtë rast) duhet paguar gjakun familjes së tij, duke liruar edhe një besimtar të robëruar. Ai që s’mund të gjejë rob (duhet) të agjërojë dy muaj rresht. (Kjo është bërë) për pranimin e pendimit nga ana e Perëndisë. Se, Perëndia është i Gjithëdijshëm dhe i Plotëdijshëm.'),
(586, 4, 93, 'Kushdo që mbyt një besimtar me qëllim, ndëshkimi i tij është xhehennemi, në të cilin do të qëndrojë përgjithmonë. Dhe Perëndia është pezmatuar në të; e ka mallkuar dhe i ka përgatitur dënim të madh.'),
(587, 4, 94, 'O besimtarë! Kur të shkoni në luftë, në rrugë të Perëndisë, sqaroni çështjen (ndaj njerëzve): mos i thoni atij që ju përshëndet juve me përshëndetje islamike: “Nuk je ti besimtar”, me qëllim të kërkoni kënaqësi të kësaj jete; e te Perëndia ka fitime të shumta. Të tillë keni qenë edhe ju më parë, e Perëndia juve ju dha mirësi; prandaj gjithmonë të gjitha punët sqaroni mirë! Se Perëndia është dijtës i veprave tuaja.'),
(588, 4, 95, 'Nuk janë të barabartë ata besimtarë, që rrinë në shtëpitë e tyre, - përveç të paaftëve, dhe ata që luftojnë në rrugën e Perëndisë me pasurinë dhe jetën e tyre. Perëndia i ka graduar ata që kanë luftuar me pasurinë dhe jetën e tyre për një gradë më të lart ndaj atyre që kanë ndejtur në shtëpitë e veta, dhe Ai të gjithëve u ka premtuar mirësi. E, Perëndia i ka dekoruar me shpërblim të madh ata që luftojnë përmbi ata që rrinë (në shtëpitë e veta e s’kanë shkuar në luftë):'),
(589, 4, 96, 'me pozita nga Ai, me falje (të mëkateve) dhe mëshirë. Se, Perëndia është falës (i mëkateve) dhe mëshirues.'),
(590, 4, 97, 'Kur engjëjt u merrnin atyre shpirtërat të cilët i kanë bërë zullum vetvetes, u thanë atyre: “Ku ishit?” (që nuk i zbatuat dispozitat e fesë). Ata u përgjegjën: “Ishim të pafuqishëm në Tokë”. (Engjëjt) u thanë: A nuk qe Toka e Perëndisë e gjerë e të emigroni në të?” Këta janë ata, vendbanimi i të cilëv eështë xhehennemi, e sa i keq është ai vendbanim!'),
(591, 4, 98, '(Në këtë dënim nuk përfshihen) mashkujt e pafuqishëm, femrat dhe fëmijët, të cilët nuk kanë mund të gjenden (në situatë) e as të orientohen në rrugë (për të emigruar),'),
(592, 4, 99, 'këta janë ata, të cilëve Perëndia ua shlyen mëkatet e tyre, se Perëndia, me të vërtetë, është shlyes dhe falës i mëkateve.'),
(593, 4, 100, 'Kush emigron në rrugën e Perëndisë, ai gjen shumë vendbanime dhe standard. Kush lë shtëpinë e vet, duke emigruar për Perëndinsë dhe Pejgamberin e Tij, pastaj t’i vijë vdekja, (në rrugë), Perëndia me siguri do ta shpërblejë atë. Se Perëndia është falës dhe mëshirues.'),
(594, 4, 101, 'Kur të udhëtoni ju, nuk është mëkat që ta shkurtoni namazin, nëse droni që mohuesit do t’u bëjnë ndonjë dëm. Me të vërtetë, mohuesit janë armiqët tuaj të hapët.'),
(595, 4, 102, 'Kur të gjendesh ti (o Muhammed!) në mesin e besimtarëve dhe t’u bëhesh imam për faljen e namazit (në luftë), atëherë njëri grup prej tyre le të falet me ty, e le t’i mbajnë pranë edhe armët e veta. Pasi që këta të bëjnë sexhde, le të rrinë pas jush, e le të vijë grupi tjetër, i cili nuk është falë dhe, le të falet me ty. Le të jenë syçelë dhe t’i marrin armët e veta. Mohuesit dëshirojnë që të jeni të pakujdesshëm ndaj armëve tuaja dhe pajisjes, që të sulmojnë në ju me tërë fuqinë. Nuk ka mëkat për ju nëse hasni në ndonjë shqetësim, prej shiut ose sëmundjes e i lëni armët, por edhe atëherë, bëhuni syçelë. Se, Perëndia, me të vërtetë, ka përgatitur dënim poshtërues për mohuesit.'),
(596, 4, 103, 'Pasi që ta kryeni namazin, përmendni Perëndinë duke qëndruar në këmbë, ulur dhe të shtrirë. Kur të jeni të qetësuar (prej rrezikut), atëherë falni namazin e plotë. Se, namazi, është detyrë e obligueshme për besimtarë në kohë të caktuar.'),
(597, 4, 104, 'Mos tregohi të dobët në ndjekjen e armiqve (për të luftuar kundër tyre). E, nëse ju ndjeni dhëmbje prej luftës, edhe ata ndjejnë dhëmbje ashtu si ndjeni edhe ju. Mirëpo, ju shpresoni prej Perëndisë atë që nuk e shpresojnë ata. Perëndia është i Gjithëdijshëm dhe i Plotëdijshëm.'),
(598, 4, 105, 'Na ty (o Muhammed!) ta kemi zbritur Kur’anin me të drejtën, për të gjykuar në mes njerëzve ashtu si ta ka shpallur ty Perëndia. Dhe mos u bën mbrojtës i tradhëtarëve!'),
(599, 4, 106, 'Kërko ndjesë prej Perëndisë! Se, Perëndia, është falës e mëshirues.'),
(600, 4, 107, 'Dhe mos i mbroj ata që e tradhëtojnë vetveten (me mëkate)! Se, Perëndia, nuk i don tradhëtarët – mëkatarët.'),
(601, 4, 108, 'Ata fshihen (duke u turpëruar) nga njerëzit, e nuk fshihen nga Perëndia. E, Ai është me ta, (i di dhe i sheh) kur natën flasin gjëra me të cilat (Ai) nuk është i kënaqur. Perëndia i di të gjitha ato që punojnë.'),
(602, 4, 109, 'Ja pra, ju i mbrojtët ata në këtë botë, por kush do t’i mbrojë ata te Perëndia në Ditën e Kijametit ose kush do të bëhet përfaqësues i tyre?'),
(603, 4, 110, 'Kush punon vepër të keqe ose i bën zullum vetes, pastaj kërkon falje prej Perëndisë, do të gjejë se Perëndia është falës e mëshirues.'),
(604, 4, 111, 'Kush bën mëkat, ai i ka bërë vetit, e Perëndia është i Gjithëdijshëm dhe i Plotëdijshëm.'),
(605, 4, 112, 'Kush bën ndonjë mëkat me hapir (pa qëllim) ose me qëllim, pastaj ia mvesh të pafajshmit, atëherë ai ka ngarkuar në vete një shpiftje dhe sheshazi ka bërë mëkat.'),
(606, 4, 113, 'Sikur mos ta kishe mirësinë e Perëndisë dhe mëshirën e Tij, gjithsesi, do të përpiqej një grup i tyre (mohuesve) që të të nxjerrin ty nga udha e drejtë. Ata me këtë e humbin vetëm vetveten, e ty nuk të bëjnë kurrfarë dëmi. Perëndia, ty ta ka shpallur Librin (Kur’anin) dhe dijeninë e plotë, dhe të mësoi ty atë që nuk e ke ditur. Dhuntia e Perëndisë në ty është shumë e madhe.'),
(607, 4, 114, 'S’ka kurrfarë të mire në bisedimet e tyre të fshehta, pëveç nëse ndokush kërkon që të jepet sadaka, ose të kryhet vepër e mirë ose paqe në mes njerëzve. E, ai, që bën këtë për të fituar kënaqësinë e Perëndisë, Na do t’i japim një shpërblim të madh.'),
(608, 4, 115, 'Kush kundërshton Perëndinë, pasi që i është dhënë udhëzimi dhe të ndjekë rrugën e jobesimtarëve, Na e orientojmë andej kah është nisur dhe do ta përcëllojmë në zjarrin e xhehennemit, e sa vendbanim i keq që është ai!'),
(609, 4, 116, 'Perëndia, me të vërtetë, nuk e falë atë që i bën shok Atij, por ia flë tjerat mëkate kujt të dojë (përveç këtij). Kushdo që t’i bëjë shok Perëndisë, ai me të vërtetë, ka humbur larg prej udhës së drejtë.'),
(610, 4, 117, 'Ata nuk thërrasin (adhurojnë) tjetër përveç idhujt (që kanë emra femërorë), dhe nuk thërrasin tjetër përveç djallin – kryengritës,'),
(611, 4, 118, 'të cilin e ka mallkuar Perëndia. Ai (djalli) ka thën: “Unë, doemos do të marr një pjesë të caktuar të robërve Tu (nën ndikimin tim);'),
(612, 4, 119, 'dhe unë doemos do t’i shpie ata në rrugë pa rrugë dhe me siguri do t’i joshi me shprsa të kota, dhe me siguri do t’i urdhëroj që t’u prejnë veshët shtazëve, e pastaj me siguri do t’i urdhëroj madje t’i ndërrojnë krijesat e Perëndisë. “Kush merr djallin për mbrojtës, e jo Perëndinë, ai ka bërë humbje të pakufishme.'),
(613, 4, 120, 'Ai (djalli) u premton atyre dhe i joshë me shpresa të kota, e ato që ua premton djalli, janë vetëm mashtrime.'),
(614, 4, 121, 'Për ata, vendbanim ëshë skëterra (ferri) dhe ata nuk mund të gjejnë shpëtim prej tij.'),
(615, 4, 122, 'Ata që besojnë dh bëjnë vepra të mira, Na do t’i shpiem në kopshte të xhennetit, nëpër të cilat rrjedhin lumenjt, ku do të qëndrojnë vazhdimisht e përgjithmonë. Ky është premtimi i vërtetë i Perëndisë. E kush është më i vërtetë se Perëndia në fjalë?!'),
(616, 4, 123, 'Puna nuk qëndron ashtu, as sipas dëshirës suaj e as sipas dëshirës së ithtarëve të Librit. Por është, se kushdo që të bëjë një të keqe, do të ndëshkohet për atë dhe nuk do të gjejë as mbrojtës as ndihmëtar përveç Perëndisë;'),
(617, 4, 124, 'kushdo që të punojë vepra të mira, qoftë ai mashkull ose femër, duke qenë ai besimtarë, do të hyjë në xhennet dhe nuk do t’i bëhet dëm as sa një thërrmi.'),
(618, 4, 125, 'Kush është besimtar më i mirë nga ai që i dorëzohet sinqerisht Perëndisë, duke qenë bëmirës dhe zbatues i fesë së pastër të Ibrahimit (që nuk i ka bërë shok Perëndisë); e Perëndia e ka marrë Ibrahimin për mik?!'),
(619, 4, 126, 'Të Perëndisë janë të gjitha çka gjenden në qiej dhe në Tokë dhe Perëndia i përfshin të gjitha (me dijeninë dhe pushtetin e Tij).'),
(620, 4, 127, 'Ata prej teje (o Muhammed!) kërkojnë sqarimin e çështjeve për punë të femrave. Thuaju: “Perëndia ua sqaron çështjet e atyre, dhe për atë që ua ka shpjeguar në libër mbi femrat bonjake, të cilave nuk u jepni atë që është caktuar për to, e dëshironi të martoheni me to, (dhe ju mëson) për fëmijët e mitur dhe (ju urdhëron) që të silleni drejt ndaj bonjakëve”. E, Perëndia, e di mirë për çdo të mirë që punoni.'),
(621, 4, 128, 'Nëse gruaja druan nga burri i saj se do të sillet keq ai, ose nuk do ta përfill atë, atëherë nuk ka mëkat për ata, nëse paqëtohen në mes vete. Paqëtimi është (madje) më i mirë (se shkurorëzimi). Te njerëzit është e prirur koprracia. Por në qoftë se ju do të silleni bukur dhe do të ruheni prej të këqijave, Perëndia, madje di mirë atë që punoni ju.'),
(622, 4, 129, 'Ju nuk jeni në gjendje, madje edhe sikur të dëshironi, të silleni drejt në mes të grave. Andaj, mos anoni krejtësisht vetëm kah njëra, e ta leni tjetrën si të varur (as me burrë as pa burrë!) Nëse ju i përmirësoni marrëdhëniet dhe ruheni nga padrejtësia, - e, Perëndia, me të vërtetë, është falës dhe mëshirues.'),
(623, 4, 130, 'E nëse ata (burri e gruaja) ndahen, Perëndia me begatinë e Tij, çdonjërin prej tyre e bën të pavarur. Se Perëndia, është i bollëkshëm në begatinë e Tij dhe i Gjithëdijshëm.'),
(624, 4, 131, 'Të Perëndisë janë të gjitha që gjenden në qiell dhe në Tokë. Na u kemi rekomanduar (porositur) atyre, të cilëve u është shpallur Libri para jush dhe juve: “Druajuni Perëndisë! E, nëse mohoni (do të ndëshkoheni). Se, me të vërtetë, të Perëndisë janë të gjitha që gjenden në qiej dhe në Tokë, (e mohimi i juaj nuk i bën dëm). Dhe, se, Perëndia është i lavdëruar dhe s’ka nevojë për asgjë”.'),
(625, 4, 132, 'Të Perëndisë janë të gjitha çka gjenden në qiej dhe në Tokë. Perëndia është i mjaftueshëm për udhëheqjen e të gjithave.'),
(626, 4, 133, 'E, nëse don (Ai), ju shporr juve or njerëz, e i bie (një palë) të tjerë. Perëndia ka fuqi për këtë.'),
(627, 4, 134, 'Kush dëshiron shpërblimin e kësaj bote – (le ta dijë), se te Perëndia janë shpërblimi i kësaj dhe i botës tjetër. Perëndia dëgjon dhe vështron çdo gjë.'),
(628, 4, 135, 'O besimtarë! Bëhuni ithtarë të së vërtetës, duke dëshmuar në emër të Perëndisë, ajo qoftë madje edhe kundër jush, ose kundër prindërve dhe të afërmëve tuaj. Qoftë i pasur ose i varfër ai (për të cilin dëshmoni), Perëndia është për ata, vlerësuesi më i drejtë. Dhe mos shkoni pas epsheve tuaja e të shtrembëroni drejtësinë! E, nëse ju ngatërroni dëshminë ose tërhiqeni (nga të dëshmuarit), Perëndia, me të vërtetë di ate ç’punoni ju.'),
(629, 4, 136, 'O besimtarë! Besoni Perëndinë, Pejgamberin e Tij, Librin, që ia ka dërguar Pejgamberit të Vet dhe Librin, që e ka dërguar më parë. E ai që nuk beson Perëndinë, engjëjt e Tij, Librat e Tij, pejgamberët e Tij dhe Ditën e Kijametit, ai, me të vërtetë, ka humbur larg prj udhës së drejtë.'),
(630, 4, 137, 'Ata që paten besuar, e pastaj u bënë mohues, e përsëri u bënë besimtarë, dhe përsëri u bënë mohues dhe e shtuan mohimin, Perëndia me të vërtetë, nuk do t’i falë ata dhe nuk do t’i shpie në rrugë të drejtë.'),
(631, 4, 138, 'Sihariqoji (o Muhammed!) hipokritët për dënimin e mjerueshëm që i pret.'),
(632, 4, 139, 'ata, të cilët miqësohen me mohuesit, e jo me besimtarët! A thua te ata kërkojnë fuqi, kur, me të vërtetë e gjithë fuqia i përket vetëm Perëndisë!'),
(633, 4, 140, 'Ai, juve, madje, ua k shpallur në Libër (Kur’anin): kur të dëgjoni se mohohen argumentet e Perëndisë dhe bëjnë tallje me to, atëherë mos rrini me ata, përderisa të rrokin tjetër temë (përndryshe nëse rrini me ta), - ju atëherë (në dënime) jeni si ata. Perëndia do t’i tubojë në xhehennem të gjithë hipokritët dhe mohuesit,'),
(634, 4, 141, 'ata që përgjojnë se çka do të bëhet me ju. E, nëse ju vie juve fitorja nga Perëndia, ata thonë: “A nuk ishim vallë na me ju?” E, nëse mohuesit arrijnë fitore, hipokritët u thanë mohuesve: “A thua nuk u ndihmuam deri në fitore dhe ju mbrojtëm prej besimtarëve të vërtetë? Perëndia do të gjykojë mes jush në Ditën e Kijametit. Perëndia nuk do t’ju jap kurrë rast mohuesve që t’i shkatërrojnë besimtarët.'),
(635, 4, 142, 'Me të vërtetë, hipokritët përpiqen ta mashtrojnë Perëndinë, por ai i mashtron ata (i dënon). Kur ata ngritën për namaz, ngriten pritueshëm, duke u paraqitur karshi botës (që janë besimtarë), e Perëndinë e përmendin pak herë.'),
(636, 4, 143, 'Luhaten në mes dilemës – as nuk janë me këta (muslimanët), as me ata (mohuesit). Cilitdo që Perëndia ia humbë udhën, ti (o Muhammed!) nuk do të mundesh t’u gjesh rrugë atyre.'),
(637, 4, 144, 'O besimtarë! Mos merrni mohuesit për miq në vend të besimtarëve! A mos vallë doni t’i afroni Perëndisë dëshmi konkrete, kundër jush?'),
(638, 4, 145, 'Padyshim, hipokritët do të jenë në thellësinë më të madhe të xhehennemit, dhe ti (o Muhammed!) kurrsesi nuk do t’ju gjesh ndihmëtarë atyre,'),
(639, 4, 146, 'përpos atyre që janë penduar, përmirësuar dhe mbështetur te Perëndia dhe që besimin e tyre ndaj Perëndisë e shfaqin haptas. Këta do të jenë me besimtarët e vërtetë. Me të vërtetë, Perëndia, do t’u japë shpërblim të madhe besimtarëve.'),
(640, 4, 147, 'Përse t’ju ndëshkojë Perëndia, - nëse jeni falenderues dhe besimtarë? E, Perëndia, është falenderues (duke dhënë shpërblim) dhe i Gjithëdijshëm.'),
(641, 4, 148, 'Perëndia nuk don që të flitet sheshazi e keqja (nga askush), përveç nga të dëmtuarit. Se Perëndia çdo gjë dëgjon dhe çdo gjë dinë.'),
(642, 4, 149, 'Në qoftë se ju bëni ndonjë vepër të mirë haptazi ose fshehurazi, ose falni ndonjë vepër të keqe, (Perëndia edhe juve ua falë). Se, Perëndia është falës imadh dhe i Plotëfuqishëm.'),
(643, 4, 150, 'Me të vërtetë, ata që mohojnë Perëndinë dhe pejgamberët e Tij (në besim) dhe të cilët thonë: “Na diçka besojmë, e diçka nuk besojmë” dhe dëshirojnë që në mes të kësaj të zgjedhin një rrugë – (ndëmjet besimit e mohimit),'),
(644, 4, 151, 'ata me siguri, janë mohues të vërtetë. Na kemi përgatitur dënim poshtërues për mohuesit.'),
(645, 4, 152, 'E, atyre që besojnë Perëndinë dhe pejgamberët e Tij dhe nuk bëjnë ndryshim ndërmjet asnjërit nga ata, atyre do t’ju japim shpërblimin (e premtuar). Se Perëndia është falës dhe mëshirues.'),
(646, 4, 153, 'Kërkojnë prej teje (o Muhammed!) ithtarët e Librit që t’u zbresish atyre një libër prej qiellit; ata patën kërkuar nga Musai diçka edhe më të madhe: “Tregona Perëndinë dukshëm!” por ata i shkatërroi rrufeja, për shkak të zullumit që bënë. Pastaj zgjodhën viçin (për adhurim), pasi që ju patën ardhur dokumentet e qarta. Na, ua kemi falë ate, e Musait i dhamë pushtet të dukshëm,'),
(647, 4, 154, 'dhe Ne kemi ngritur mbi ta malin Tur, sipas obligimit të tyre (për t’i besuar Musait). Na u kemi thënë atyre: “Hyni në portë duke u përulur”, (dhe (madje) u kemi thënë: “Mos e kaloni (urdhërin) ditën e shtunë!” E, morëm nga ata obligim të fortë.'),
(648, 4, 155, '(Na i mallkuam ata) për shkak e kanë shkelur obligimin, dhe për shkak të mohimit të verseteve të Perëndisë, dhe për shkak të mbytjes së pejgamberëve pa kurrfarë të drejte dhe për thënien e tyre: “Zemrat tona janë të mbyllura në këllef (nuk e ndiejnë të drejtën)”. Jo, por Perëndia ua ka mbyllur ato për shkak të mohimit të tyre – e pak prej tyre kanë besuar –'),
(649, 4, 156, 'edhe për shkak të mohimit të tyre edhe për shkak të fjalës së tyre – akuzave të mëdha kundër Merjemes.'),
(650, 4, 157, 'Dhe për fjalët e tyre: “Na kemi mbytë Mesihun – Isain të birin e Merjemes, Pejgamberin e Perëndisë”. Por, ata as nuk kanë mbytur as nuk e kanë varë, por iu ka përngja atyre (një person). Ata që bënë polemikë për çështjen e tij, ata me siguri gjenden në dyshim përr të, (Isain). Ata nuk kanë ditur kurrgjë për te, por vetëm u janë mbështetur supozimeve. Ata, në të vërtetë, nuk e kanë mbytur,'),
(651, 4, 158, 'por, Perëndia e ka ngritur pranë Vetes (në një vend që nuk e arrijnë njerëzit). Se Perëndia, është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(652, 4, 159, 'Të gjithë ithtarët e Librit, para se të vdesin, - do t’i besojnë Isait (që është Profet, e jo zot); e, në Ditën e Kijametit, ai (Isai), do të jetë dëshmitar kundër tyre.'),
(653, 4, 160, 'Madje, për shkak të zullumit që bënë Hebrenjt, Na ua ndaluam atyre disa ushqime të shijshme, të cilat ishin të lejueshme për ta (më parë) dhe për shkak se i zmbrapsen shumë njerëz nga udha e Perëndisë,'),
(654, 4, 161, 'dhe për shkak se ata morën kamatë, duke qenë e ndaluar për ta, dhe për shkak të ngrënies (marrjes) së pasurisë së njerëzve pa të drejtë. Na këtyre mohuesve u kemi përgatitur dënim pikëllues.'),
(655, 4, 162, 'Por disa nga ata (Hebrenjt), të cilët janë udhëzuar plotësisht në dijeni dhe muslimanët, - ata besojnë në atë që të është shpallur ty (o Muhammed!) dhe në atë, që është shpallur para teje; e atyre që falin namazin dhe ndajnë zeqatin, dhe atyre që i besojnë Perëndisë dhe Ditën e Kijametit, - këtyre, me siguri do t’u japim shpërblim të madh.'),
(656, 4, 163, 'Na të kemi dërguar ty shpalljen, ashtu siç i kemi dërguar shpallje Nuhit, dhe pejgamberëve pas tij; e, Ne iu kemi dërguar shpallje edhe Ibrahimit, edhe Ismailit, edhe Is’hakut, edhe Jakubit, edhe Isait, edhe Ejjupit, edhe Junusit, edhe Harunit, edhe Sulejmanit. E, Dautit ia kemi dhënë (Librin) Zeburin.'),
(657, 4, 164, '(Na kemi dërguar) edhe pejgamberë, për të cilët, të kemi treguar më parë dhe pejgamberë për të cilët nuk të kemi treguar ty. E, Perëndia, me siguri, ka biseduar me Musain.'),
(658, 4, 165, '(Na kemi dërguar) pejgamberë, sihariques dhe lajmërues (frikën), që njerëzit të mos kenë ndonjë vërejtje ndaj Perëndisë, pas ardhjes së pejgamberëve. Se, Perëndia, është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(659, 4, 166, 'Perëndia dëshmon Profetninë tënde (o Muhammed!) me atë që ta ka shpallur ty, të cilën Ai ta ka shpallur me dijeninë e Tij – edhe engjëjt dëshmojnë (këtë), e Perëndia, të mjafton ty dëshmitar.'),
(660, 4, 167, 'Me të vërtetë, ata që kanë mohuar dhe kanë penguar njerëzit nga rruga e Perëndisë, ata kanë humbur larg prej udhës së drejtë.'),
(661, 4, 168, 'Ata që nuk besojnë dhe bëjnë zullum, Perëndia nuk i falë ata, as nuk do t’i udhëzojë në kurrfarë rruge,'),
(662, 4, 169, 'përveç në rrugën e skëterrës, në të cilën do të qëndrojnë vazhdimisht dhe përgjithmonë, - e, kjo për Perëndinë është lehtë.'),
(663, 4, 170, 'O njerëz! Pejgamberi, madje ua ka sjellë të vërtetën nga Zoti juaj; andaj, besoni – se (kjo) për ju është më mirë! E në qoftë se, nuk besoni (Perëndisë nuk i dëmtoni asgjë), dinie se të Perëndisë janë të gjitha që gjenden në qiej dhe në Tokë. E, Perëndia është i Gjithëdijshëm dhe i Plotëdijshëm.'),
(664, 4, 171, 'O ithtarët e Librit! – mos e kaloni kufirin në besimin tuaj dhe për Perëndinë thoni vetëm të vërtetën! Mesihu – Isai, i biri i Merjemit, është vetëm Profet i Perëndisë dhe Fjala e Tij, të cilën ia ka dërguar Merjemit, dhe shpirt nga Ai. Pra, besoni Perëndisë dhe pejgamberëve të Tij! Dhe mos thoni: “Tre zotra!” Frenohuni (nga kjo); është më mirë për ju. Se, Perëndia është vetëm një Zot – qoftë lavdëruar Ai! – Ai është i dlirët (nga shpifja) për të pasur fëmijë. Të Tij janë të gjitha që gjenden në qiej dhe në Tokë. Perëndia mjafton për rregullimin e Gjithësisë.'),
(665, 4, 172, 'Mesihu aspak nuk ngurron që të jetë rob i Perëndisë, si dhe as engjëjt më të afërm; e kushdo që ngurron nga të adhuruarit Perëndinë dhe bëhet arrogant (mendjemadh), Ai do t’i tubojë të gjithë përpara Veti.'),
(666, 4, 173, 'E, ata që besojnë dhe punojnë vepra të mira, Ai ju plotëson shpërblimin e tyre dhe ua shton nga mirësitë e Tij. E, ata që ngurrojnë (në të adhuruarit e Perëndisë) dhe krenohen, do të dënohen me ndëshkim pikëllues. Ata nuk do të gjejnë për vete, përveç Perëndisë, - as mbrojtës as ndihmëtar.'),
(667, 4, 174, 'O njerëz! Me të vërtetë, ju ka ardhë dokumenti (Muhammedi) nga Zoti juaj, dhe ua kemi zbritur një Dritë (Kur’anin) të dukshme.'),
(668, 4, 175, 'E, ata që besojnë Perëndinë dhe i përmbahen fort Atij (dispozitave të Perëndisë), Ai do t’i shpie në mëshirën dhe mirësinë e Tij, do t’i udhëzojë ata kah Ai në rrugën e drejtë.'),
(669, 4, 176, 'Ata, kërkojnë nga ti fetva (vendim). Thuaju (o Muhammed!): “Perëndia u jep vendim per – kalalen – (njeriu që nuk lë për trashëgimtar as prindër e as fëmijë). Nëse vdes njeriu që nuk ka fëmijë, por ka motër, atëherë asaj i takon gjysma (e pasurisë) nga ajo që ka lënë ai, e ai (vëllau) e trashëgon atë, nëse ajo nuk ka fëmijë. Nëse janë dy motra, ato trashëgojnë dy të tretat nga ajo që ka lënë ai. Në qofshin trashëgimtarët vëllezër e motra, atëherë mashkullit i takon trashëgim sa dy femra (motra). Perëndia ju shpjegon këto, që të mos humbni rrugën ju. Se, Perëndia, me të vërtetë, di çdo gjë.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'O besimtarë! Plotësoni obligimet (marrëveshjet)! U lejua për ju ngrënia e shtazëve, përveç atyre që do t’ju lexohen juve; përderisa të gjendeni në haxhxhillëk, nuk u lejohet gjuetia. Me të vërtetë, Perëndia, vendos për atë që dëshiron.'),
(671, 5, 2, 'O besimtarë! Mos e llogaritni për të lejueshme cenimin e shenjave të Perëndisë në haxhxhillëk, as muajt e mëdhenj, as kurbanin e Qabes, as kurbanët që janë të shënuar me xherdan, as (mos i pengoni) ata që kanë synuar të vizitojnë Qaben, duke kërkuar mirësinë dhe kënaqësinë e Zotit të vet. E, kur të dilni prej ihramit (pas kryerjes së haxhxhillëkut), gjuani (nëse doni). Dhe, mos t’ju nxisë juve urrejtja ndaj disa njerëzve, nga se ju kanë penguar juve në të vizituarit e Qabesë, kurrsesi mos t’ju nxisë që t’i sulmoni! Ndihmoni njëri-tjetrit në punë të mira dhe në të ruajturit nga të këqijat, dhe mos ndihmoni në mëkate dhe armiqësi! Dhe druajuni Perëndisë! Se, Perëndia, dënon ashpër.'),
(672, 5, 3, 'U ndalohet juve ngrënia e coftinës, gjakut, mishi i derrit, asaj kafshe që është therrur jo në emër të Perëndisë, mishi i kafshës që është lidhur në fyt e ka plasë, mishi i kafshës që është rrahë e ka ngordhë, e i asaj kafshe që është mbytur nga të rrëzuarit, e i asaj që është mbytur me brirë nga kafshët e tjera, e i asaj që është mbytur nga egërsirat, përveç atyre që i preni ju para se të ngordhin. U ndalohet juve ngrënia e asaj që është therrur për nder të kryqëve dhe të të kërkuarit e fatit me fall. Të gjitha këto që u përmendën janë mëkat. Sot, dëshprohen ata, që nuk besojnë dhe kanë humbur çdo shpresë, që t’ju prishin dispozitat e fesë suaj. Mos u frikësoni prej tyre, por druani (vetëm) prej meje! Sot ua përsosa dispozitat e fesë suaj dhe ua plotësova dhuntinë Time ndaj jush. Jam i kënaqur që feja islame të jetë fé e juaja. Por kushdo që të jet ëi shtrënguar nga uria, (e han nga këto jo me qëllim përr t’u kënaqur) nuk ka mëkat. Se, Perëndia është falës e mëshirues.'),
(673, 5, 4, 'Të pyesin ty (muslimanët) ç’është e lejueshme që të hanë ata. Thuaju (o Muhammed!): “U lejohet juve çdo gjë që është e bukur dhe at që ua gjuajnë shtazët që i keni stërvitur ju e i përdorni në gjueti, duke qenë ato të mësuara me dijeninë që ua ka dhënë Perëndia juve. Pra, hani ate që ua sjellin juve (kafshët që janë gjuajtur), e përmendni emrin e Perëndisë! Druajuni Perëndisë! Se, Perëndia është llogaritar i shpejtë.'),
(674, 5, 5, 'Sot u janë lejuar gjërat e mira e të pastra. U janë lejuar juve edhe ushqimet e ithtarëve të Librit, si dhe ushqimet tuaja janë të lejuara për ata. Dhe janë të lejuara për ju femrat e ndershme besimtare; dhe femrat e ndershme të atyre që u është dhënë Libri para jush, kur t’jua jepni dhuratat e kurorëzimit, duke u martua me ato, e duke mos bërë me to kurvëri, as duke i marrë për mikesha (konkubine). Kushdo që mohon besimin, puna e tij është e falimentuar. Ai në Ditën e Kijametit do të jetë nga të dënuarit.'),
(675, 5, 6, 'O besimtarë! Kur doni të bëni namaz lanie fytyrën dhe duart tuaja dei në bërryla dhe fshinie kokën me dorë të lagët; lani edhe këmbët deri në nyje. E, nëse jeni xhunub, pastrohuni! Por, në qoftë se jeni të sëmurë (që mund t’u dëmtojë uji), ose gjendnei në udhëtim, ose ndonjëri nga ju vie pas kryerjes së nevojës natyrore, ose nëse keni pasur marrëdhënie (seksuale) me granitë e nuk gjeni ujë, atëherë gjeni (bëni tejemmum) në dhé të pastër dhe fshinie fytyrat tuaja dhe duart me te. Perëndia nuk dëshiron t’ju krijojë ndonjë vështirësi juve, por dëshiron t’ju pastrojë juve dhe t’i plotësojë dhuntitë e Tij ndaj jush, që të jeni falenderues.'),
(676, 5, 7, 'Kujtonie dhuntinë e Perëndisë dhe obligimin me të cilin u ka obliguar Ai juve, kur thatë: “Dëgjuam dhe u bindëm!” Druajuni Perëndisë! Se, Perëndia, di ç’keni në zemrat tuaja.'),
(677, 5, 8, 'O besimtarë! – bëhuni të qëndrueshëm në urdhërat e Perëndisë dhe bëhuni dëshmues me të drejtë! Le të mos u nxis juve urrejtja ndaj një kombi për të bërë padrejtësi. Bëhuni të drejtë (ndaj miqve dhe armiqve), se drejtësia është më afër takvasë (dispozitave të islamizmit), dhe durajuni Perëndisë! Se, Perëndia, është i dijshëm për çdo gjë që punoni ju.'),
(678, 5, 9, 'Perëndia u ka premtuar atyre që besojnë dhe bëjnë vepra të mira – falje (të mëkateve) dhe shpërblim të madh;'),
(679, 5, 10, 'kurse atyre që nuk kanë besuar dhe që kanë mohuar argumentet tona – ata do të jenë banorë të zjarrit.'),
(680, 5, 11, 'O besimtarë! – kujtonie dhuntinë e Perëndisë, të cilën ua dhuroi atë ditë kur një grup njerëzish deshën t’u dëmtojnë juve me duart e tyre prej jush. Druajuni Perëndisë! Dhe le të mbështetën vetëm te Perëndia besimtarët!'),
(681, 5, 12, 'Me të vërtetë, Perëndia mori premtimin e bijve të Israilit dhe nga mesi i tyre caktoi dymbëdhjetë kujdestarë (mbikëqyrës), – dhe Perëndia tha: “Unë jam (në ndihmën tuaj)! Në qoftë se do të kryeni namazin e jepni zeqatin, e besoni pejgamberët e Mi, i ndihmoni ata dhe t’i jepni vullnetarisht hua Perëndisë (në rrugën e Tij), atëherë, Na, me të vërtetë, do t’ju mbulojmë mëkatet tuaja dhe do t’ju shpiem në kopshtije, nëpër të cilat rrjedhin lumenj. E, ai që prej jush mohon edhe pas kësaj, me të vërtetë, e ka humbur rrugën e drejtë.'),
(682, 5, 13, 'Për shkak se ata e thyen obligimin e tyre, Na i mallkuam ata dhe ua forcuam (si gur) zemrat e tyre. Ata ngatërrojnë rendin e fjalëve nga vendet e tyre, duke harruar një pjesë nga ato këshilla. Ti përherë do të vërejsh tradhëtinë e tyre, përpos një pakice të tyre, pra, falju atyre fajet dhe mos i qorto! Se, Perëndia, i don bamirësat.'),
(683, 5, 14, 'Dhe prej atyre që thanë: “Jemi të krishterë”, kemi marrë premtimin e tyre, por edhe këta kanë harruar një pjesë nga ato që janë këshilluar, prandaj ua shtiem armiqësinë dhe urrejtjen në mes tyre deri në Ditën e Kijametit. Se, Perëndia, me të vërtetë, di t’i lajmërojë ata për ato që kanë punuar.'),
(684, 5, 15, 'O ithtarët e Librit! Ju erdhi juve Pejgamberi Ynë i cili u shpjegon gjëra nga to që i keni fshehur ju prej Librit (Teuratit dhe Ungjillit) dhe ju falë shumë (kalon në heshtje). E, prej Perëndisë ju erdh juve dritë (Pejgamberi) dhe Libri i qartë (Kur’ani),'),
(685, 5, 16, 'me të cilin Perëndia i drejton ata që ndjekin kënaqësinë e Tij, në rrugën e shpëtimit, i nxjerr nga errësira në dritë me lejen e Vet dhe i shpie në rrugën e drejtë.'),
(686, 5, 17, 'Me të vërtetë, kanë mohuar (të Vërtetën) ata që thonë: “Me siguri perëndi është Mesihu, i biri i Merjemës!” Thuaj: “Kush mund të pengojë gjë në punët e Perëndisë, nëse Ai don ta zhdukë Mesihun, të birin e Merjemës dhe nënën e tij dhe gjithë çka gjendet në Tokë?” E pronë e Perëndisë janë: qiejt, Toka dhe çdo gjë që gjendet në mes tyre. Ai krijon çka të dojë, dhe Perëndia është i Plotëfuqishëm për çdo gjë.'),
(687, 5, 18, 'Hebrenjt dhe të krishterët kanë thënë: “Na jemi të bijt e Perëndisë dhe të dashurit e Tij”. Thuaj: “E përse ju dënoi (Ai) juve për shkak të mëkateve tuaja? Jo! (Nuk është ashtu si thoni ju), por ju jeni njerëz nga ata që ka krijuar Ai. Ai falë kënd të dojë dhe e dënon kënd të dojë. E, pronë e Perëndisë janë: qiejt, Toka dhe çdo gjë që gjendet në mes tyre. Dhe vetëm tek Ai kthehen të gjithë.'),
(688, 5, 19, 'O ithtarë të Librit! – juve ju ka ardhur Pejgamberi Ynë, - shpjeguesi juaj mu në kohë të ndërprerjes së dërgimit të pejgamberëve, për të mos thënë ju (kur të dënoheni), se nuk na ka ardhur ndonjë sihariques e as ndonjë paralajmërues. Por, ja ju erdhi juve sihariquesi dhe paralajmëruesi. Se, Perëndia është i Plotëfuqishëm për çdo gjë.'),
(689, 5, 20, '(Kujtoje) kur Musai i tha kombit të vet: “O populli im! Kujtonie mirësinë e Perëndisë ndaj jush, kur prej jush bëri pejgamber, dhe ju bëri disa prej jush sundimtarë, dhe juve ju dha atë që nuk i kishte dhënë askujt para jush;'),
(690, 5, 21, 'O populli im! Hyni në Tokën e shenjtë që Perëndia ua ka cktuar juve e mos u ktheni prapa, se atëherë do të ktheheni të dëmtuar”. –'),
(691, 5, 22, 'ata thanë: “O Musa! – a ty, në atë tokë gjendet popull i pa mëshirë (dhunues), dhe ne në të nuk do të hyjmë pa dalë ata; e nëse dalin ata prej saj, ne atëherë, me siguri do të hyjmë në të”.'),
(692, 5, 23, 'Dy njerëz nga ata që i druanin Perëndisë, që i kishte pajisur Ai me mirësi – thanë: “Futuni (befas) në porten e qytetit! E, kur të kaloni nëpër te, me siguri do të jeni fitues. Dhe, nëse jeni besimtarë (të vërtetë), mbështetuni në Perëndinë!”'),
(693, 5, 24, 'Ata thanë: “O Musa! Na kurrsesi nuk do të hyjmë atje, përderisa aty të gjenden ata. Shko ti te Zoti yt e luftoni ata. Se ne do të rrimë këtu”.'),
(694, 5, 25, 'Musai tha: “O Zotim im! Unë jam i pushtetshëm vetëm për vete dhe vllaun tim. O Zot! Ndana neve prej këtij populli të korruptuar!”'),
(695, 5, 26, 'Perëndia tha: “Ajo (toka e shenjtë) u është ndaluar dyzet vjet atyre. Ata do të bredhin nëpër tokë. E, mos u zemro ndaj atij populli ngatërrestar”.'),
(696, 5, 27, 'Tregoju (o Muhammed!) saktësisht atyre (popullit tënd) ngjarjen e dy bijve të Ademit, kur ata therrën kurban, njërit iu pranua, kurse tjetrit jo. Tha – njëri: “Tu gjithsesi do të mbysë”. Tjetri tha: “Perëndia pranon (kurbanin) vetëm prej atij që ruhet prej mëkatesh.'),
(697, 5, 28, 'Nëse ti ngre dorë të më vrasësh mua, unë në të vërtetë nuk do ta ngre dorën në ty që të të vras. Dhe unë me të vërtetë, i druaj Perëndisë, Zotit të gjithësisë.'),
(698, 5, 29, 'Unë preferoj që ti të kthehesh me mëkatin tim dhe me mëkatin tënd (për vrasjen time dhe për kundrështimin tënd); e kështu të bëhesh ndër banorët e zjarrit. E, ky është ndëshkimi i zullumqarëve”.'),
(699, 5, 30, 'Vetja e tij e nxiti në vrasjen e të vëllait, dhe e mbyti. Kështu, ai u bë njëri nga të humburit.'),
(700, 5, 31, 'Perëndia e dërgoi një sorrë, e cila gropontë në tokë, për t’i treguar se si ta mbulojë trupin e kalbur të vëllait të tij. (Kabili) tha: “Mjeri unë, a nuk qenkam i zoti të bëhem si kjo sorrë e ta mbuloj trupin e tim vëllau?!” E u bë nga të penduarit.'),
(701, 5, 32, 'Andaj Na i urdhëruam Izrailitët, se ai që mbyt ndokend, i cili s’ka mbytur askend, ose atë që nuk ka bërë çrregullim në Tokë – është sikur t’i mbysish të gjithë njerëzit; e nëse ndokush bëhet shkaktar që të shpëtojë jetën e ndokujt – është sikur me i shpëtuar jetën e të gjithë njerëzve. Pejgamberët tanë u kanë sjellë argumente të qarta, por shumë nga ata edhe pas kësaj, kanë kaluar tërë kufijt me të këqija në Tokë.'),
(702, 5, 33, 'Ndëshkimi i atyre që luftojnë kundër Perëndisë dhe të dërguarit të Tij dhe të cilët bëjnë ngatërresa në Tokë është të vriten ose të varen, ose t’u këputen duart dhe këmbët e tyre nga anët e ndryshme (djathtas e majtas), ose të dëbohen nga vendi. Ky ndëshkim për ta është poshtërim në këtë jetë, e në jetën tjetër ka mundim të madh për ta,'),
(703, 5, 34, 'përveç atyre që pendohen pa i zënë ju. Dinie se Perëndia është falës dhe mëshirues.'),
(704, 5, 35, 'O besimtarë! Kinie frikë Perëndinë dhe tentoni që t’i afroheni Atij me vepra të mira, dhe luftoni në rrugën e Tij për të shpëtuar!'),
(705, 5, 36, 'Me të vërtetë, mohuesit – sikur të kenë tërë pasurinë që gjendet në tokë, madje edhe njëherë aq, e ta sakrifikojnë për të shpëtuar nga dënimi i Dikës së Kijametit, nuk do të pranohet prej tyre, sepse, për ta ka dënim pikëllues.'),
(706, 5, 37, 'Ata dëshirojnë të dalin nga zjarri, por nuk kanë dalje nga ai. Ata i pret dënimi i përhershëm.'),
(707, 5, 38, 'Vjedhësit dhe vjedhëses këputjuani duart e tyre, për ndëshkim të asaj që kanë punuar – dënim nga ana e Perëndisë; se, Perëndia, është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(708, 5, 39, 'E, kushdo që të pendohet pas të këqijave të tij dhe përmirëson punën e tij, Perëndia ia pranon pendimin, se Perëndia, me të vërtetë, është falës dhe mëshirues.'),
(709, 5, 40, 'Ti (o Muhammed!), e di me siguri, se të Perëndisë janë pushteti mbi qiejt dhe Tokën. Ai dënon kë të dojë dhe falë kë të dojë. Se Perëndia është i Plotëfuqishëm për çdo gjë.'),
(710, 5, 41, 'O i dërguari i Perëndisë! Mos të brengos ty ajo, që ata shpejt shprehin mosbesimin, të cilët me gojën e tyre thonë: “Besojmë” kurse zemrat e tyre nuk besojnë, dhe Hebrenjt, të cilët trillimet i dëgjojnë shumë dhe të cilët i pranojnë lehtë fjalët e huaja, të atyre që s’kanë ardhur te ti; ata i ngatërruan vendet e fjalëve të tyre dhe thanë: “Nëse kështu gjykohet ndërlikueshëm atëherë pranoni këtë, e nëse nuk gjykohet kështu, atëherë, ruhuni e mos e pranoni. – E, këtë dojë Perëndia ta lë në humbje, ti asgjë nuk do të mund të pengosh Perëndinë për këtë. Këta janë të asaj dore (fare), të cilët Perëndia nuk dëshiron t’ua pastrojë zemrat e tyre. Për ta, në këtë jetë ka poshtërim, kurse në jetën tjetër për ta ka ndëshkim të rëndë.'),
(711, 5, 42, '(Ata) janë dëgjues të gënjeshrës, e hanë me kënaqësi të madhe atë që është e ndaluar. E, nëse vinë te ti, gjykoji ata ose shmangu prej tyre! Nëse shmangesh nga ata, nuk mund të dëmtojnë ty asgjë. E, nëe i gjykon – gjykoji me të drejtë! Se, Perëndia, me të vërtetë, i don të drejtët.'),
(712, 5, 43, 'E qysh të drejtohen ata ty për t’i gjykuar, e te ata është Teurati, në të cilin janë dispozitat e Perëndisë, e madje edhe pas kësaj shmangen prej teje. Sepse, ata nuk janë besimtarë.'),
(713, 5, 44, 'Na kemi dërguar Teuratin, në të cilin është udhëzimi dhe drita. Sipas tij –pejgamberët, të cilët ishin të bindur e të ndershëm ndaj Perëndisë i gjykonin hebrenjt, por edhe të diturit dhe juristët gjykonin sipas aaj që është ruajtur nga Libri i Perëndisë, e për të cilin ata dëshmonin (se është i vërtetë). Prandaj, mos druani nga njerëzit, por druani vetëm prej Meje! Dhe mos i ndërroni argumentet e Mia me ndonjë vlerë të vogël! E, ata që nuk gjykojnë sipas asaj që ka shpallur Perëndia, ata janë mohues të vërtetë.'),
(714, 5, 45, 'Na iu caktuam atyre (Izraelitëve) në të (Teurat), që të: shkëmbehet me personin (të dënohet vrasësi me vrasje), syri me sy, hunda me hundë, veshi me vesh, dhëmbi me dhëmb, dhe plaga me plagë. E, kushdo që t’ia falë atë, ai është shpagues për mëkatet e tij. Dhe, kushdo që nuk gjykon me ate që ka zbritur Perëndia, ata janë zullumqarë të vërtetë.'),
(715, 5, 46, 'Na dërguam në gjurmët e pejgamberëve, Isain – të birin e Merjemës, vërtetues të Teuratit që e kanë para tyre. Dhe Na ia dhamë atij Inxhilin, në të cilin ka udhëzim dhe dritë, vërtetues i Teuratit, të cilin e kanë para vet – udhërrëfim dhe këshillim për ata që i druajnë Perëndisë.'),
(716, 5, 47, 'Le të gjykojnë ithtarët e Inxhilit ashtu siç ka shpallë Perëndia në të. E, kush nuk gjykon ashtu siç ka shpallë Perëndia – ata janë mëkatarë të vërtetë.'),
(717, 5, 48, 'Na ty (o Muhammed!) ta kemi dërguar Librin (Kur’anin), me të vërtetë, vërtetues të librit që e kanë përpara dhe dëshmues i tij. Gjyko në mes tyre me atë që ka zbritur Perëndia dhe mos ndjek dëshirat e tyre, për të mos devijuar në atë që të ka ardhur ty nga e Vërteta. Na për të gjithë ju kemi bërë ligj një rrugë të qartë. E, sikur të donte Perëndia, do t’u kishte bërë juve një popull, por, Ai don që t’ju provojë juve në ate që ju ka dhënë, andaj bëni gara ju në punë të mira! Të gjithë do të ktheheni te Perëndia e Ai do t’ju lajmërojë për çështjet, në të cilat nuk jeni pajtuar ju.'),
(718, 5, 49, 'Dhe, (Na të dërguam Librin) që të gjykosh ti me atë që të ka shpallur Perëndia, e mos ndjek dëshirat e tyre! Ruaju nga ata, që të mos të turbullojnë ty prej disa sendesh që t’i ka zbritur Perëndia! Nëse ata kthehen e nuk dëgjojnë gjykimin tënd, atëherë ta dish se Perëndia don vetëm atë, që t’i dënojë ata, për disa mëkate të tyre. E, me të vërtetë, shumë njerëz janë mëkatarë.'),
(719, 5, 50, 'A mos kërkojnë ata gjykimin e kohës së paganizmit?! E, kush është gjykatës më i mirë se Perëndia për njerëzit që besojnë bindshëm?'),
(720, 5, 51, 'O besimtarë! Mos i konsideroni për miq hebrenjt dhe të krishterët! Ata janë miq të njëri-tjetrit. Kushdo që prej jush i konsideron ata për miq, ai është me ta. Me të vërtetë, Perëndia nuk i udhëzon në rrugë të drejtë zullumqarët.'),
(721, 5, 52, 'I sheh ti ata, zemrat e të cilëve lëngojnë (nga hipokrizia) se si vrapojnë për të lidhur miqësi me ta duke thënë: “Druajmë se mos po na goditë ndonjë fatkeqësi”. E, Perëndia do t’i sjell fitoren ose diçka tjetër nga ana e Tij, e prandaj, për atë që kanë fshehur në veten e tyre do të pendohen,'),
(722, 5, 53, 'e ata që besojnë do të thonë: “A këta janë ata që përbetoheshin, me betimin më të thellë, se ata me të vërtetë janë me ju?” Veprat e tyre kanë dështuar (për shkak të hipokrizisë) dhe ata u dëmtuan.'),
(723, 5, 54, 'O besimtarë! Nëse ndokush prej jush shmanget nga besimi i vet, me të vërtetë Perëndia do të sjell një popull, që e don dhe që ata e donë (Perëndinë), të cilët janë të përulur ndaj besimtarëve dhe të ashpër ndaj mohuesve; luftojnë në rrugën e Perëndisë dhe nuk i druajnë qortimit të asnjë qortuesi. Kjo është mirësia e Perëndisë, të cilën Ai ia jep kujt të dojë, se Perëndia ka mirësinë e gjerë dhe di çdo gjë.'),
(724, 5, 55, 'Miku juaj është vetëm Perëndia, Profeti i Tij dhe besimtarët, të cilët falin namazin, japin zeqatin, që janë modest e të përvuajtur ndaj Perëndisë.'),
(725, 5, 56, 'Me të vërtetë, ata që marrin për mik Perëndinë, Pejgamberin e Tij dhe ata që besojnë, (ata janë në anën e Perëndisë); dhe ana e Perëndisë do të jetë fitues.'),
(726, 5, 57, 'O besimtarë! Mos i merrni për miq ata, të cilët, fenë tuaj e marrin për tallje dhe lojë, qofshin ata, të cilëve ju është dhënë Libri para jush, apo qofshin mohuesit, - dhe druajuni Perëndisë, nëse jeni besimtarë!'),
(727, 5, 58, '(Dhe mos i merrni për miq ata), që kurr t’i thirrni për namaz ata e marrin atë për tallje dhe lojë, sepse ai është popull i zhveshur nga mendja.'),
(728, 5, 59, 'Thuaj: “O ithtarët e Librit! – a mos na urreni neve për shkak se besojmë në Perëndinë dhe atë që na ëshë shpallur, dhe atë që është shpallur më parë, e shumica prej jush jeni mëkatarë?”'),
(729, 5, 60, 'Thuaj: “A doni t’ju tregoj juve për të keqën më të madhe (dhe kush meriton më të rëndin), dënimin te Perëndia? (Këta janë ata), në të cilët është pezmatuar Perëndia, dhe që i ka mallkuar, i ka shndërruar në majmunë dhe derra dhe ata që adhurojnë djallin – ata janë në vendin më të mjerueshëm, sepse, ata qëndrojnë larg rrugës së vërtetë”.'),
(730, 5, 61, 'E, kur ju erdhën ata juve, thanë: “Besojmë”, por ata hynë mohues dhe dolën mohues, e Perëndia e di mirë atë që fshehin ata.'),
(731, 5, 62, 'Ti do t’i shohësh shumë nga ata që vrapojnë në mëkate, në armiqësi dhe në ngrënien e haramit. E, me të vërtetë, të këqija janë ato vepra!'),
(732, 5, 63, 'Sikur fetarët dhe dijetarët të mos i ndalonin ata, nga të folurit gënjeshtra dhe ngrënies së haramit, sa keq do të kishin punuar ata!'),
(733, 5, 64, 'Hebrenjt thanë: “Dora e Perëndisë është koprrace!” Ju lidhshin duart atyre! – Dhe, qofshin të mallkuar për atë që thanë! Jo, duart e Tija janë të hapura. Ai dhuron dhe furnizon si të dojë. E, ajo që t’u ka shpallë ty nga Zoti yt, me të vërtetë, shumicës prej tyre, do t’ua shtojë (azganllëkun) dhe mohimin. Na, kemi shkaktuar në mes tyre armiqësi dhe urrejtje deri në Ditën e Kijametit. Kurdo që ata ndezin zjarrin e luftës, Perëndia e shuan atë. Ata përpiqen të shkaktojnë ngatërresa në Tokë. E Perëndia nuk i don ngatërrestarët.'),
(734, 5, 65, 'E, sikur ithtarët e Librit të besonin dhe të ruhen nga mohimi, Ne do t’ua mbulonim atyre veprat e këqija dhe do t’i shpienim në kopshtet e kënaqësisë.'),
(735, 5, 66, 'Sikur ata t’i përmbaheshin Teuratit dhe Inxhilit dhe asaj që iu është shpallur nga Zoti i tyre, ata do të kënaqeshin me frytet (që rriten) mbi ata dhe nën këmbët e tyre. Disa prej tyre nuk janë ekstremë, por shumë prej tyre bëjnë vepra të këqija.'),
(736, 5, 67, 'O Pejgamber! Trego atë që të është shpallur nga Zoti yt! – Në qoftë se nuk e bën këtë, atëherë nuk e ke kryer profetninë tënde; Perëndia të ruan ty prej njerëzve. Se, Perëndia, me të vërtetë, nuk i udhëzon në rrugë të drejtë njerëzit që nuk besojnë.'),
(737, 5, 68, 'Thuaj: O ithtarët e Librit! Ju nuk keni asgjë (në besim), përderisa të mos i përmbaheni Teuratit dhe Inxhilit dhe asaj që iu është shpallur nga zoti juaj, e ajo që t’u ka shpallur ty nga Zoti yt, do t’ua shtojë shumicës së tyre të harbuarit dhe mohimin. E, mos u hidhëro ti (o Muhammed!) për fatin e mohuesve.'),
(738, 5, 69, 'Ata që besojnë, madje Hebrenjt, të krishterët dhe Sabi’inët; - kushdo që beson Perëndinë (si është urdhëruar), jetën tjetër dhe punon vepra të mira, për ta nuk ka kurrfarë droje as mërzie.'),
(739, 5, 70, 'Na kemi marrë premtimin nga bijt e Izraelit (për besim) dhe ju kemi dërguar pejgamberë. Por, sa herë që ju ka ardhur atyre ndonjë pejgamber me atë (dispozitë), që s’jua donte shpirti i tyre, disa nga ata (pejgamberë) i konsideronin për gënjeshtarë e disa prej tyre i mbytnin.'),
(740, 5, 71, 'Ata menduan se nuk do t’i gjejë kurrfarë dënimi, andaj ngelën të verbër dhe të shurdhët (nuk e panë drejtësinë). Pastaj, Perëndia ua pranoi pendimin tyre, e përsëri, u verbuan dhe u shurdhuan shumë nga ata; e Perëndia sheh mirë atë që punojnë ata.'),
(741, 5, 72, 'Me të vërtetë, kanë mohuar (të Vërtetën) ata që thonë: “Me siguri Perëndi është Mesihu – i biri i Merjemës! E, Mesihu tha: “O bijt e Israelit, adhuronie Perëndinë, që është Zoti im dhe Zoti juaj. Se, me të vërtetë, kush i ën shok Perëndisë, Perëndia ia ndalon atij Xhennetin dhe strehim i tij është zjarri. Dhe, për zullumqarët nuk ka ndihmëtarë.'),
(742, 5, 73, 'Me të vërtetë, u bënë mohues ata, që thonë: “Perëndia është njëri prej treve!” S’ka Zot tjetër përveç se një Zot! E, nëse nuk frenohen nga ato që thanë, me të vërtetë, ka për t’i goditë ata – mohuesit, dënimi pikëllues.'),
(743, 5, 74, 'E, përse ata nuk pendohen, (të kthehen te Perëndia) e të kërkojnë ndjesë nga Ai? Se, Perëndia, është falës dhe mëshirues.'),
(744, 5, 75, 'Mesihu – i biri i Merjemës, është vetëm Pejgamber, - edhe para tij kanë kaluar pejgamberë. Nëna e tij përherë ka qenë e drejtë. Që të dy, ngrënin ushqim. Shiko si ua shpjegojmë Ne atyre argumentet dhe shih si zmbrapsën ata (nga e Vërteta).'),
(745, 5, 76, 'Thuaju (o Muhammed!): “Vallë, a e adhuroni ju, përpos Perëndisë, atë që nuk është në gjendje t’u sjell dëm e as dobi?” E, Perëndia është Ai, që dëgjon dhe di çdo gjë.'),
(746, 5, 77, 'Thuaj: “O ithtarët e Librit! Mos kaloni kufirin në besimin tuaj, në kundërshtimin me të vërtetën dhe mos ndiqni pasionin e një populli, që qysh më parë patën humbur dhe kanë nxjerrë prej udhës së drejtë shumë njerëz dhe vetë janë shmangur nga udha e drejtë”.'),
(747, 5, 78, 'U mallkuan ata nga bijtë e Israelit që ishin mohues, me gjuhën e Dautit dhe të Isait – të birit të Merjemës, për shkak se ishin të padëgjueshëm dhe ngase kishin kaluar kufirin:'),
(748, 5, 79, 'njëri-tjetrin nuk e pengonin nga veprat e këqija që punonin. E, sa të shëmtuara ishin veprat që punonin ata!'),
(749, 5, 80, 'Ti i sheh shumicën nga ata që miqësohen me mohuesit. Me të vërtetë, e shëmtuar ësthë ajo që i përgatisin ata vetes: që Perëndia të pezmatohet në ta dhe t’i dënojë përherë.'),
(750, 5, 81, 'Sikur t’i besonin ata Perëndisë, Pejgamberit dhe asaj që i është shpallur atij, nuk do t’i merrnim ata (mohuesit) për miq. Por, shumica prej tyre janë mëkatarë.'),
(751, 5, 82, 'Ti gjithsesi do të vëresh, se armiqët më të ashpër për besimtarët (muslimanët) janë Hebrenjt dhe paganët. E, gjithsesi do të vëresh, se miqët më të afërt të besimtarëve janë ata që thonë: “Na jemi të krishterë”. Kjo rrjedh, nga se ndër ta ka dijetarë dhe murgë dhe ata me të vërtetë nuk madhështohen (në pranimin e së vërtetës).'),
(752, 5, 83, 'Kur dëgjuan ata për ate (Kur’anin) që i është zbritur Pejgamberit, i sheh ti ata, prej se kuptuan të Vërtetën, se si ju rrjedhin lotët nga sytë. Ata thanë: “O Zoti ynë! Na besojmë, andaj na regjistro në mesin e atyre që dëshmojnë (profetninë e Muhammedit)”.'),
(753, 5, 84, 'E përse të mos besojmë Perëndinë dhe të Vërtetën (Kur’anin), që na është dërguar, kur shpresojmë se edhe neve Zoti ynë do të na shpie me njerëz të mirë.'),
(754, 5, 85, 'E Perëndia do t’i shpërblejë ata për fjalët që thanë (që besuan) me kopshte, nëpër të cilat rrjedhin lumenj dhe ku ata do të qëndrojnë përgjithmonë. E ky është shpërblimi i bamirësve.'),
(755, 5, 86, 'E ata që mohuan dhe i konsideruan të rrejshme argumentet tona, ata do të jenë banorë të ferrit (skëterrës).'),
(756, 5, 87, 'O besimtarë! Mos ia ndaloni vetes gjërat e bukura, të cilat Perëndia ua ka lejuar dhe mos kaloni kufirin. Se, Perëndia, nuk i don ata që e kapërcejnë kufirin (e urdhërave të Perëndisë).'),
(757, 5, 88, 'Hani nga ato që u ka furnizuar Perëndia – të lëjuara dhe të mira! Druajuni Perëndisë, të cilit ju i keni besuar!'),
(758, 5, 89, 'Perëndia nuk do t’ju ndëshkojë për betimin që e bëni pa qëllim, por do t’ju ndëshkojë për betimin e bërë me qëllim. Larja e mëkatit për thyerjen e betimit është: ushqyerja e dhjetë të varfërve me mesataren e ushqimit, të familjes suaj, ose t’i vishni ata, ose të lironi një skllav. Ai që nuk gjen mundësi për këto, le të agjërojë tri ditë. Ky është dënimi i thyerjes së betimeve tuaja – kur betoheni. Mbani betimet tuaja! Kështu ua shpjegon Perëndia argumentet e Veta, që të jeni falënderues.'),
(759, 5, 90, 'O besimtarë! Me të vërtetë, vera (pijet alkoolike), bixhozi, statujet dhe shigjetat e fallit janë vepra të ndyta nga punët e djallit. Pra, për të shpëtuar, largohuni nga këto!'),
(760, 5, 91, 'Me të vërtetë, djalli me pije alkoolike dhe me bixhoz dëshiron që të fusë në mes jush armiqësi dhe urrejtje dhe t’ju shmangë juve nga të kujtuarit e Perëndisë dhe kryerjen e namazit. Prandaj, a doni të shmangeni? (Shmanguni ju nga këto vepra të këqia)!'),
(761, 5, 92, 'Bindjuni Perëndisë, bindjuni dhe Pejgamberit dhe ruajuni (nga kundërshtarët)! E, nëse zmbrapseni (nga bindja), ta dini se Pejgamberi Ynë është i obliguar të bëjë vetëm shpalljen e qartë.'),
(762, 5, 93, 'S’ka mëkat për ata besimtarë që bëjnë punë të mira (në atë) që kanë ngranë (para ndalimit), kur ata të jenë ruajtur (nga ndalimet), të kenë besuar e të kenë bërë vepra të mira; të ruhen e të besojnë; dhe të ruhen e të bëjnë vepra të mira (vijimisht). E, Perëndia i don bamirësit.'),
(763, 5, 94, 'O besimtarë! Perëndia do t’ju provojë juve me ndalim nga një gjueti që e arrijnë duart tuaja dhe shigjetat tuaja, që Perëndia ta tregojë atë që i druan Atij, kur askush nuk e sheh. E, kushdo që ta kalojë kufirin (këtë dispozitë), për të ka dënim pikëllues.'),
(764, 5, 95, 'O besimtarë! Mos gjuani, duke qenë në ihram! E, kushdo që qet të vret (egërsirën) me paramendim, për dënim do të therrë një shtazë shtëpiake të ngjashme me kafshën e gjuajtur. Këtë, e vlerësojnë dy vetë të drejtë, duke dërguar kurban në Qabe, ose duke i ushqyer të varfërit për të larë mëkatin, ose të agjëroj në këtë masë, që ta ndiejë ndëshkimin e mëkatit të bërë. Perëndia i falë atë që ka kaluar (para ndalimit). E, kush vepron edhe pas ndalimit, Perëndia do ta ndëshkojë. Se, Perëndia është i Plotëfuqishëm dhendëshkues.'),
(765, 5, 96, 'U është lejuar gjuetia e detit dhe ngrënia e saj, duke u kënaqur me të, në shtëpi dhe në udhëtim. E, ju është ndaluar gjuetia e tokës, deri sa të gjendeni në Ihram. Druajeni Perëndinë, para të cilit do të tuboheni.'),
(766, 5, 97, 'Perëndia e bëri Qaben – Shtëpi të shenjtë që njerëzit t’i kryejnë detyrat fetare dhe muajin e shenjt, kurbanin dhe kurbanin e shënuar. Këto të gjitha, i bëri, për të ditur se Perëndia di çdo gjë në qiej dhe në Tokë dhe që Perëndia është i Gjithëdijshëm.'),
(767, 5, 98, 'Dinie se Perëndia është ndëshkues i rreptë dhe se është falës e mëshirues.'),
(768, 5, 99, 'Pejgamberi ka për detyrë vetëm t’ju komunikojë Shpalljes. Se Perëndia di çka punoni sheshazi e çka fshehurazi.'),
(769, 5, 100, 'Thuaju: “Nuk barazohet e keqja me të mirën, sikur edhe të të kënaqte shumica e të këqijave. Druajeni Perëndinë, o të zotët e mendjes së hollë, që të shpëtoni.'),
(770, 5, 101, 'O besimtarë! Mos pyetni për disa sende, që sikur t’ju thuhet haptazi do të ishit të pakënaqur, e nëse pyetni për ato kur zbritet Kur’ani, do t’u jenë të qarta. Perëndia ua ka falë kërshërinë. Se, Perëndia, është falës dhe i butë, (nuk nxiton në ndëshkime).'),
(771, 5, 102, 'Disa njerëz kanë pyetur kështu, para jush, e nuk kanë besuar (u bënë mohues).'),
(772, 5, 103, 'Perëndia nuk e përligji Behirën (devën femër me veshë të prerë); as Saibën (devën që e flijonin për statujë); as Vesilën (devën që ka pjellë pesë herë rresht qingja – femër e u është flijuar statujeve); as Hamin (devën që nuk e ngarkonin as shalonin). Por, mohuesit (duke besuar kështu shtrembër), kanë trilluar gënjeshtra për Perëndinë! Shumica nga ata nuk kanë mend.'),
(773, 5, 104, 'E kur ju thuhet atyre: “Ejani në atë që e ka zbritur Perëndia dhe ejani te Pejgamberi!” Ata thanë: “Neve na mjafton ajo, në çka i kemi gjetë etërit tonë”. A mos edhe atëherë, kur etërit e tyre nuk kanë ditur asgjë, e as nuk ishin në udhën e vërtetë?'),
(774, 5, 105, 'O besimtarë! Ruajuni! Kur të gjendeni në rrugën e vërtetë, nuk do t’ju dëmtojë ai që është i humbur. Te Perëndia të gjithë do të ktheheni, e do t’ju lajmërojë për atë që keni punuar.'),
(775, 5, 106, 'O besimtarë! – kur t’ju afrohet vdekja, me rastin e dhënies së testamentit, le të dëshmojnë dy të drejtë prej jush, ose dy të tjerë që nuk janë prej jush – nëse gjendeni në rrugë dhe u duken shenjat e vdekjes. E, nëse dyshoni në ta, mbani ata pas namazit dhe le të betohen në Perëndinë. “Na nuk do ta shesim betimin me kurrfarë çmimi, edhe nëse është fjala për çfarëdo të afërmi, dhe dëshminë që na urdhëron Perëndia nuk do ta fshehim, sepse, atëherë, do të ishim, me të vërtetë mëkatarë”.'),
(776, 5, 107, 'Nëse hetohet se ata dy dëshmues kanë bërë mëkat, atëherë dy të tjerë do t’i zëvendësojnë ata të dy, nga ata që u është bërë padrejtësi, e këta dy do të betohen në Perëndinë (duke thënë): “Dëshmia jonë është gjithsesi më e drejtë se dëshmia e atyre dyve dhe na nuk i kemi kaluar kufijt. Sepse, atëherë, ne do të ishim zullumqarë”.'),
(777, 5, 108, 'Kështu, më së lehti mund të bëjnë dëshminë e tyre të saktë, ose të frikohen që betimet e tyre të refuzohen (mohohen) me betime tjera. – Druajuni Perëndisë dhe dëgjonie! Se, Perëndia, nuk i udhëzon në rrugë të drejtë njerëzit ngatërrestarë.'),
(778, 5, 109, 'Kujtojeni ditën kur Perëndia i tubon Pejgamberët e ju thotë: “Çka ju përgjigjën?” Ata thonë: “Na s’kemi dijeni (të plotë), se vetëm Ti i di të gjitha fshehtësitë”.'),
(779, 5, 110, '(Kujtoje o Muhammed!), kur Perëndia tha: “O Isa, i biri i Merjemës, kutjoje mirësinë Time ndaj teje dhe nënës tënde: kur të forcova ty me Shpirtin e shenjtë (Xhebrailin), e ju flitje njerëzve duke qenë fëmijë (në djep) dhe si njeri i rritur; dhe kur ta mësova shkrimin dhe dijeninë e thellë, Teuratin dhe Inxhilin, dhe kur krijove një figurë shpendi prej balte me urdhërin Tim e fryeve në të, e u bë zog, me urdhërin Tim; dhe kur me urdhërin Tim, i ngjalle të vdekurit; dhe kur i pengova sulmet e Israelitëve në Ty, atëheë kur ju solle atyre argumente të qarta, e ata që nuk besuan ndër, ta, thanë: “Kjo është vetëm magji e kulluar!”'),
(780, 5, 111, 'dhe kur i urdhërova Havarijjunët (Apostujt): “Besomëni Mua dhe Pejgamberin Tim!” Ata u përgjigjën: “Na besojmë, e ti bëhu dëshmues që ne jemi muslimanë!”'),
(781, 5, 112, 'E, kur apostujt thanë: “O Isa, i biri i Merjemës, a mundet Zot’i yt të na zbresë një sofër prej qielli?” Ai tha: “Druani Perëndinë, nëse jeni besimtarë!”'),
(782, 5, 113, 'Ata thanë: “Na dëshirojmë ta hamë nga ajo (sofër), dhe të na qetësohen zemrat, dhe ta dimë (konkretisht) që na ke thanë të vërtetën dhe për atë të jemi dëshmitarë”.'),
(783, 5, 114, 'Isai, i bir’i Merjemës tha: “O Perëndi, o Zoti ynë! Zbritna neve një sofër nga qielli, që të na jetë festë për bashkëkohanikët dhe pasardhësit tonë; dhe të jetë mrekulli prej Teje dhe furnizona, se Ti je më i miri furnizues”.'),
(784, 5, 115, 'Perëndia tha: “Unë do t’ju zbres sofrën, e kush nga ju, pas kësaj nuk beson, Unë do ta dënoj me një dënim, me çfarë nuk do ta dënojë askënd në botë”.'),
(785, 5, 116, 'E, kur Perëndia thotë: “O Isa, i bir’i Merjemës, a mos ti u ke thënë njerëzve: “Merrmëni mua dhe nënën time si dy zotra, përpos Perëndisë?” Isai thotë: “Ti je i pastër nga të metat!” Mua nuk më përket të them ate çka ka në Ty. Vetëm Ti i di të gjitha ato që janë të fshehta.'),
(786, 5, 117, 'Unë nuk ju kam thënë asgjë tjetër, përveç asaj që më ke urdhëruar Ti: “Adhuronie Perëndinë, Zotin tim dhe Zotin tuaj!” Kam qenë dëshmitar deri sa kam qenë në mesin e tyre. E pasi më more mua, vetëm Ti ishe mbikëqyrës i tyre. E, Ti je dëshmitar për çdo gjë.'),
(787, 5, 118, 'Nëse Ti i dënon ata – robërit e Tu janë, e nëse i falë ata, me të vërtetë, vetëm Ti je i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(788, 5, 119, 'Perëntia thotë: “Kjo është Dita, në të cilën, të sinqertëve u sjell dobi sinqeriteti i tyre. Atyre u takojnë kopshtet, nëpër të cilat rrjedhin lumenjt, ku do të qëndrojnë përgjithmonë. Perëndia është i kënaqur me ta; dhe ata janë të kënaqur me Te. Kjo është fitorja e madhe!”'),
(789, 5, 120, 'Të Perëndisë janë pushteti mbi qiej dhe mbi Tokë dhe çka gjendet në To. Ai është i pushtetshëm për çdo gjë.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Qoftë lavdëruar Perëndia që krijoi qiejtë dhe Tokën, e themeloi errësirat dhe dritën. E, ata që nuk besojnë, (përsëri) – me Zotin e tyre krahasojnë zotëra.'),
(791, 6, 2, 'Ai ju ka krijuar nga balta, ka caktuar afatin (e vdekjes) dhe Ai di kohën e (Kijametit), e ju përsëri dyshoni.'),
(792, 6, 3, 'Perëndia është i adhuruar në qiej dhe në Tokë; Ai di fshehtësitë dhe të sheshtat tuaja, si dhe të arriturat tuaja.'),
(793, 6, 4, 'E nuk ju ka ardhur (të pabesëve) asnjë dokument prej dokumenteve të Zotit të tyre, e ata, të mos ia kthejnë shpinën.'),
(794, 6, 5, 'E përgënjeshtruan të vërtetën, posa ju erdhi, por së shpejti do ju arrijnë lajme (me pasoja) për atë çka janë tallur.'),
(795, 6, 6, 'A thua nuk i panë ata, se sa popuj i kemi zhdukur Ne, para tyre, të cilët i patëm vendosur në aso toke, në çfarë nuk ju kemi vendosur jvue; e u sollëm atyre shira të bollshëm dhe bëmë që t’ju rrjedhin lumenjë nëpër viset e tyre; e që, për shkak të mëkateve të tyre – i zhdukëm ata; dhe pas tyre – krijuam gjenerata tjera.'),
(796, 6, 7, 'E, sikur Ne ta zbritnim ty një Libër të shkruar në letër; e ata ta preknin me duart e tyre, ata që nuk besojnë, me siguri do të thonin: “Kjo s’është gjë tjetër, përpos një magji e hapët”.'),
(797, 6, 8, 'Ata thanë: “T’ju kishte zbritë atij (Muhammedit) një ëngjëll?!” E, sikur ta dërgonim Ne ëngjëllin, puna do të merrte fund dhe nuk do t’u ipej asnjë afat.'),
(798, 6, 9, 'E, sikur ta dërgonim atë (Pejgamberin) engjëll, do ta bënim gjithsesi me tipare njeriu. E, ata, përsëri do të dyshonin – si më parë.'),
(799, 6, 10, 'Me të vërtetë, janë përqeshur shumë pejgamberë para teje, e i përfshin ata që i i përqeshnin (pejgamberët) dënimi i talljes së tyre.'),
(800, 6, 11, 'Thuaj: “Udhëtoni nëpër botë e shihni çfarë ka qenë dënimi ndaj atyre që thonin – (pejgamberët) gënjejnë.'),
(801, 6, 12, 'Thuaj: “E kujt është tërë ajo, që gjendet në qiej dhe në Tokë?” Përgjigju: “E Perëndisë është!” Vetes ia ka përcaktuar mëshirën. Ai, padyshim, do t’ju tubojë në Ditën e Kijametit. Ata që vetveten e kanë shkatërruar, ata në të vërtetë nuk do të besojnë.'),
(802, 6, 13, 'E Tij është çdo gjë që rri dhe lëvizë natë e ditë. Ai i dëgjon të gjitha dhe di çdo gjë.'),
(803, 6, 14, 'Thuaj: “A të bëjë mik tjetër, përpos Perëndisë, krijuesit të qiejve dhe Tokës? Ai e ushqen (botën), e Atë askush nuk e ushqen”. Thuaj: “Mua më është urdhëruar që të jemi i pari nga ata që e kanë pranuar islamizmin”, - dhe kurrsesi, mos u bënë nga ata që i bëjnë shok Atij!”'),
(804, 6, 15, 'Thuaj: “Nëse e kundërshtoj Zotin tim i droj dënimit të Ditës së Madhe”.'),
(805, 6, 16, 'Ai që i shpëton dënimit të kësaj dite, Zoti e ka mëshiruar atë. Dhe ky është shpëtimi i vërtetë.'),
(806, 6, 17, 'Nëse nga Perëndia të godet ndonjë fatkeqësi, atëherë askush përvçe Tij nuk mund të të shpëtojë. E, nëse të dhuron ndonjë mirësi – vetëm Ai është i Plotëfuqishëm për çdo gjë.'),
(807, 6, 18, 'Ai është i pushtetshëm mbi robërit e vet; Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(808, 6, 19, 'Thuaj: “Cila është dëshmia më e madhe (që ti je profet)?” – dhe përgjigju: “Perëndia është dëshmitar në mes meje dhe jush”. Më është shpallur ky Kur’an, që t’ju tërheqë vërejtjen me të, juve dhe atyre te kush arrin ky. A mos vallë dëshmoni ju, se, përveç Perëndisë ka edhe zotra tjerë? Thuaj: “Unë nuk e dëshmoj”. Thuaj: “Vërtetë, Ai është vetëm Një Zot dhe unë jam larg nga ato që ju i konsideroni shok Perëndisë”.'),
(809, 6, 20, 'Atyre që u kemi dhënë Librin, e njohin (Muhammedin), ashtu si i njohin fëmijët e tyre. E, nuk do të besojnë ata që e kanë shkatërruar veten.'),
(810, 6, 21, 'Kush është më zullumqarë se ai që trillon për Perëndinë ose i përgënjeshtron dokumentat e Tij? Vërtetë, përgënjeshtarët nuk do të shpëtojnë.'),
(811, 6, 22, '(Kujtoje atë) ditë kur do t’i tubojmë të gjithë, e do t’u themi atyre, që i kanë vënë shoq Perëndisë: “Ku janë shokët tuaj, për të cilët mendonit se janë (shokë të Perëndisë)!”'),
(812, 6, 23, 'E, ata nuk do të kenë kurrfarë arsyetimi, përveç se të thonë (rrejnë): “Pasha Perëndinë, Zotin tonë, nuk kemi qenë paganë!”'),
(813, 6, 24, 'Shiko se si ata veten e fusin në rrena; e humbi nga ata – ajo që e trilluan.'),
(814, 6, 25, 'Ka disa prej tyre që të dëgjojnë, por Ne kemi vënë perde në zemrat e tyre, për të mos e kuptuar atë, e në veshët e tyre shurdhimin. Edhe nëse e shohin çdo ajet, ata nuk besojnë në to. E, kur të vijnë te ti për të polemizuar me ty, thonë ata që nuk besojnë: “Këto janë vetëm trillime të popujve të lashtë!”'),
(815, 6, 26, 'Ata e ndalojnë besimin në Kur’an, edhe vetë largohen nga ai, dhe vetveten e dëmtojnë, e nuk e hetojnë.'),
(816, 6, 27, 'Ah! Sikur t’i shohish ata kur të ballafaqohen para zjarrit, duke thënë: “Ah! Sikur të ktheheshim pas, mos t’i përgënjeshtronim dokumentet e Zotit tonë dhe të bëheshim besimtarë!”'),
(817, 6, 28, 'Jo! Jo! Por atyre iu doli ajo, që më parë e fshihnin, dhe sikur ata të ktheheshin, prapë do të bënin ato që u ishin ndaluar. Ata, me të vërtetë, janë gënjeshtarë.'),
(818, 6, 29, 'Ata thoshin: “S’ka jetë përveç në këtë botë dhe ne nuk do të ringjallemi”.'),
(819, 6, 30, 'Ah! Sikur t’i shohësh ata kur të dalin para Zotit, Ai do t’ju thotë: “A nuk është e vërtetë kjo?” Ata do të përgjigjen: “Po, është e vërtetë, për Zotin tonë!” Perëndia do t’ju thotë: “E pra, tani, shijoni dënimin, sepse nuk keni besuar!”'),
(820, 6, 31, 'Gjithsesi, ata e kanë dëmtuar veten, që e kanë përgënjeshtruar takimin me Perëndinë; e kur befas t’u arrijë Dita e Kijametit, do të thonë: “Të mjerët ne çka kemi humbur në jetën e Tokës!” dhe do t’i bartin mëkatet e veta në shpinën e tyre. E, sa e shëmtuar është ajo barrë!'),
(821, 6, 32, 'Jeta e kësaj bote është vetëm lojë e argëtim, kurse, bota tjetër, është e vërtetë, më e mirë për ata që i druajnë Perëndisë. A nuk po mendoni?'),
(822, 6, 33, 'Na e dijmë, se ty, me siguri, të zemëron ajo që thonë ata. Ata, me të vërtetë nuk të fajësojnë ty se je gënjeshtar, por zullumqarët mohojnë argumentet e Perëndisë.'),
(823, 6, 34, 'Edhe para teje (o Muhammed!), shumë pejgamberë janë përgënjeshtruar, por ata kanë duruar për atë që janë konsideruar. Dhe kanë vuajtur derisa nuk ka ardhur ndihma Jonë, - e askush nuk mund t’i ndryshojë fjalët e Perëndisë, - dhe, padyshim, te ti kanë arritur disa lajme për pejgamberë.'),
(824, 6, 35, 'Nëse ty të vie rëndë kthimi i tyre (prej teje), e nëse mund të kërkosh zgavër në tokë ose shkallë në qiell, e t’u sjellësh një mrekulli (bëne këtë)! Sikur Perëndia të donte, do t’i kishte tubuar ata në rrugë të mbarë; mos u bënë nga ata që nuk e dinë këtë!'),
(825, 6, 36, 'Do të gjegjen vetëm ata që dëgjojnë (të kuptueshmit). Të vdekurit do t’i ngjallë Perëndia; pastaj, ata, do të kthehen tek Ai.'),
(826, 6, 37, 'Ata thonë: “Ah! Pse nuk ia zbriti atij ndonjë mrekulli, Zoti i tij?” Thuaj: “Perëndia është i fuqishëm të dërgojë mrekulli, por shumica e tyre nuk e di (Fuqinë e Perëndisë)”.'),
(827, 6, 38, 'S’ka asnjë kafshë në Tokë e të gjitha shpendët që fluturojnë me krahët e tyre, që të mos jenë në një grup të ngjashëm me ju (në krijim dhe ekzistim). Ne nuk kemi harruar asgjë në Libër (Levhi Mahfudhin)”. Pastaj do të tubohen te Zoti i tyre.'),
(828, 6, 39, 'Ata që i përgënjeshtrojnë dokumentet Tona janë shurdhë e memecë, janë në errësirë. Perëndia kë të do e shpie të gabojë, ndërsa kë të do e udhëzon në rrugë të drejtë.'),
(829, 6, 40, 'Thuaj: “Çka mendoni nëse ju vie dënimi i Perëndisë apo ju godet Dita e Kijametit, a do ta thërrisni dikë tjetër pos Perëndisë, nëse e flitni të vërtetë?”'),
(830, 6, 41, 'Jo, jo! Ju do ta thërritni vetëm Ate, Ai do t’ju zbulojë atë për çka e thërritni, nëse Ai do. Dhe ju, atëherë, do t’i harroni ato që ia bëjshit Atij.'),
(831, 6, 42, 'Na, ju kemi dërguar pejgamberë shumë popujve para teje dhe i kemi dënuar ata me skamje dhe fatkeqësira, për t’u përulur.'),
(832, 6, 43, 'Pse nuk u penduan, kur u erdh ndëshkimi Jonë! Por, zemrat e tyre ishin gurë, e djalli ua zbukuronte, ato që bënin ata.'),
(833, 6, 44, 'Dhe, kur i harruan ata – për ato që u paralajmëruan, Na iu hapëm të gjitha dyert (të gjitha të mirat); e kur u gëzonin me atë që u dhamë, Ne i dënuam ata befasisht dhe atëherë ata humbën çdo shpresë;'),
(834, 6, 45, 'dhe humbi gjurma e atij kombi zullumqarë. Lavdërimi qoftë i Perëndisë, Zotit të gjithësisë.'),
(835, 6, 46, 'Thuaj: “Çka mendoni sikur t’jua merrte Perëndia të dëgjuarit dhe të pamit dhe t’ua vuloste zemrat tuaja; cili zot, përveç Perëndisë do t’ua kthente ato?” Shih si ua shpjegojmë Na dokumentet, e ata përsëri e kthejnë kryet.'),
(836, 6, 47, 'Thuaj: “Më thuani ju, nëe ju vjen dënimi i Perëndisë papritmas ose sheshazi, a do të zhdukej kush përveç popullit zullumqar?”'),
(837, 6, 48, 'Na ua dërguam pejgamberët vetëm si sihariques dhe qortues. Pra kushdo që beson dhe bën mirë, mos të frikësohen e as nuk do të pikëllohen.'),
(838, 6, 49, 'Për ata që përgënjeshtrojnë dokumentet Tona, ata do t’i godet dënimi për mëkatet e bëra.'),
(839, 6, 50, 'Thuaj: “Unë nuk do t’ju them – se i kam depot e Perëndisë, se i di sekretet, as nuk them se jam ëngjëll, - Unë ndjekë vetëm atë që më shpallet”. Thuaj: “A barazohet i vërbëri me atë që sheh? A s’po mendoni?”'),
(840, 6, 51, 'Dhe i qorto me Kur’an ata, të cilët druajnë se kanë për t’u tubuar para Zotit të tyre, ku çërveç Tij, nuk do të kenë as mbrojtës as ndihmës – për t’u ruajtur nga mëkatet.'),
(841, 6, 52, 'Mos i dëbo ata që i luten Zotit të vet në mëngjes e mbrëmje, duke e dëshiruar Atë – ti nuk je i obliguar të kujdesesh për ta për asgjë, e as ata nuk janë përgjegjës për ty asgjë, sepse, nëse i dëbon ti ata, do të jesh zullumqarë.'),
(842, 6, 53, 'Dhe kështu Na i provojmë njërin me tjetrin, për të thënë: “A mos janë këta që Perëndia ua dhuroi mirësinë? – A mos vallë Perëndia nuk i njeh më mirë ata të cilët i janë mirënjohës.'),
(843, 6, 54, 'Kur të vijnë te Ti ata që besojnë në argumentet Tona, thuaj: “Shpëtimi (Paqa) qoftë mbi ju!” Zoti juaj vetes ia ka caktuar mirësinë: nëse dikush nga ju bënë ndonjë vepër të keqe pa ditur, e pastaj pendohet e përmirësohet, - Ai falë dhe mëshiron.'),
(844, 6, 55, 'Na kështu shpjegojmë hollësisht dokumentet, në mënyrë që të kuptohet rruga e njerëzve të këqinjë.'),
(845, 6, 56, 'Thuaj: “Mua më është ndaluar t’i adhuroj ata, që ju, përpos Perëndisë i adhuroni”. Thuaj: “Unë nuk i ndjek dëshirat tuaja, sepse atëhrë do të isha i humbur dhe nuk do të isha nga ata, që janë në rrugën e drejtë”.'),
(846, 6, 57, 'Thuaj: “Unë kam (ardhur) me argument të dukshëm nga Zot’i im, e ju e përgënjeshtruat atë. Atë që kërkoni ju (dënimin), nuk është në pushtetin tim; pushteti i takon vetëm Perëndisë. Ai e tregon të vërtetën dhe Ai e bënë dallimin në mes të vërtetës dhe të pavërtetës.'),
(847, 6, 58, 'Thuaj: “Sikur ta kisha në dorë atë që e kërkoni ju, shpejt e shpejt (dënimin) do të sqarohej puna mes meje dhe jush (do të shkatërroheshit). Perëndia i di më së miri zullumqarët.'),
(848, 6, 59, 'Perëndia i ka çelësat e të gjitha fshehtësive dhe vetëm Ai i di ato. Ai di ç’ka në tokë dhe në det; asnjë gjeth nuk bie, pa e ditur Ai, dhe nuk ekziston asnjë kokërr në thellësitë e tokës, as e freskët as e thatë, e të mos jetë shënuar në Librin e qartë (Levhi Mahfudh).'),
(849, 6, 60, 'Ai natën ju vë në gjumë dhe di çka punoni ditën. Ju zgjon ditën, për të arritur kohën e caktuar të jetës. Pastaj, i ktheheni Atij dhe ju tregon atë që keni punuar.'),
(850, 6, 61, 'Ai sundon mbi robërit e vet dhe ju dërgon rojtarë; kur njërit nga ju i vie vdekja, të dërguarit tanë (engjëjt) e bëjnë të vdekur (ia marrin shpirtin), duke mos bërë kurrfarë lëshimi (pa përjashtim).'),
(851, 6, 62, 'Pastaj ata do të kthehen te Perëndia, Zoti tyre i vërtetë. Se vetëm Atij i takon të gjykojë dhe Ai është logaritari më i shpejtë.'),
(852, 6, 63, 'Thuaju: “Kush do t’ju shpëtojë nga errësira (rreziku) e tokës dhe tetit, kur i luteni Atij haptazi dhe fshehurazi (duke thënë): Nëse Ai na shpëton prej këtyre (rreziqeve), vërtetë do të bëhemi mirënjohës”.'),
(853, 6, 64, 'Thuaj: “Perëndia ju shpëton nga ato (rreziqe) dhe çdo e keqe tjetër, e ju përsëri i bëni shoq Atij”.'),
(854, 6, 65, 'Thuaj: “Ai është i fuqishëm t’ju ndëshkojë prej së larti ose nën këmbët tuaja, ose t’ju ndajë në grupe, e njëri-tjetrit t’ia tregoni fuqinë (në luftë në mes veti). Shih si ua shpjegojm Na dokumentet, që të kuptojnë ata!'),
(855, 6, 66, 'Populli yt e pëgënjeshtron atë (Kur’anin), por ai është i vërtetë. Thuaj: “Unë nuk jam rojtari jua,'),
(856, 6, 67, 'çdo lajm e ka kohën e vet, të caktuar, e ju atë do ta mësoni.'),
(857, 6, 68, 'Kur t’i shohësh ata që thellohen (në nënçmim) në dokumentet Tona, largohu prej tyre, deri sa të (mos) kalojnë në bisedë tjetër. E, nëse, djalli të mashtron, mos rri me zullumqarë të përkujtohesh.'),
(858, 6, 69, 'Ata që i drojën Perëndisë, s’kanë kurrfarë përgjegjësie për mëkatet e tyre (të mohuesvet), por (kjo) është vërejtje për t’u ruajtur.'),
(859, 6, 70, 'Lerëni ata që fenë e tyre e marrin si lojë dhe dëfrim, e të cilët i ka mashtruar jeta e kësaj bote. Këshilloji me Kur’anin që njeriu të mos shkatërrohet për atë që ka vepruar, duke mos pasur – pos Perëndisë, as ndihmëtar, as ndërmjetës; dhe sikur ai të ofronte çfarëdo shpagimi, nuk do të pranohet nga Ai. Këta janë ata, të cilët janë shkatërruar për atë që kanë punuar. Ata do të fitojnë pije nga uji i vluar dhe dënim të dhimbshëm për shkak se kanë mohuar.'),
(860, 6, 71, 'Thuaj: “A ta adhurojmë dike tjetër përveç Perëndisë, atë që nuk na sjell dobi as dëm, e të kthehemi kah thëmbrat tona (në humbje), pasi që Perëndia na udhëzoi në rrugë të drejtë? (E, të bëhemi si ai) që e kanë trullosur djajtë në Tokë e ai mbeti i humbur; e të cilin shokët e tij e thirrin në rrugën e drejtë: “Eja tek ne!” (E, ai nuk përgjigjet). Thuaj: “Rruga e Perëndisë – është e vetmja rrugë e drejtë, dhe neve jemi të urdhëruar t’i bindemi Zotit të gjithësisë”.'),
(861, 6, 72, 'dhe të falim namazin, dhe t’i ruhemi Atij; Ai është, tek i cili do të tuboheni ju.'),
(862, 6, 73, 'Dhe Ai është ai, i cili i krijoi qiejt dhe tokën me të rë të vërtetën. Dhe kur Ai thotë: “Bëhu!” ajo bëhet; fjala e Tij është e Vërtetë dhe Atij i përket pushteti i asaj Dite kur fryhet në Sur. Ai e di botën e padukshme dhe të dukshme dhe Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(863, 6, 74, '(Kujtoje o Muhammed!) kur Ibrahimi i tha babës së tij Azerit: “A mos do t’i marrësh statujat për zotra?! Unë po shoh se ti dhe kombi yt jeni në humbje të madhe”.'),
(864, 6, 75, 'Dhe Na ia treguam Ibrahimit perandorinë e qiejve dhe të Tokës për t’u bërë nga ata që besojnë vendoshmërisht.'),
(865, 6, 76, 'Dhe kur arriti nata, ai vërejti yllin dhe tha: “Ky është Zoti im!” Por, kur perëndoi ai yll, tha: “Nuk i dua ata që perëndojnë”.'),
(866, 6, 77, 'E, kur vërejti Hënën duke lindur, tha: “Ky është Zoti im!” E, pasi perëndoi ajo, ai tha: “Nëse Zoti im nuk më udhëzon në rrugën e mbarë, me siguri do të bëhem njeri nga të humburit”.'),
(867, 6, 78, 'E, kur e vërejti Diellin duke lindur, ai thërriti: “Ky është Zoti im, ky është më i madhi!” – E, pasi perëndoi, ai tha: “O kombi im, unë jam i dëlirë nga ajo që ju i bëni shok Perëndisë!'),
(868, 6, 79, 'Unë e kthej fytyrën time kah Ai që i ka krijuar qiejt dhe Tokën, duke qenë besimtarë i vërtetë, dhe unë nuk jam nga ata që i bëjnë shok Perëndisë!”'),
(869, 6, 80, 'Dhe populli i tij polemizoi me te (lidhur për njësimin e Perëndisë). Ai tha: “A mos vallë ju dëshironi të pelemizoni me mua për Perëndinë, e ai më ka udhëzuar në rrugën e drejtë. Unë nuk frikësohem prej putash që veni ju shoq atij. (Unë nuk frikohem prej asgjëje), përveç nëse don Perëndia të më shkaktojë ndonjë katastrofë. Zoti im, me dijeninë e vet e ka përfshirë çdo gjë. A nuk po mendoni?'),
(870, 6, 81, 'E, si të frikësohem unë prej putash tuaja, me të cilët ju i bëni shoq Perëndisë; e ju nuk frikësoheni nga ajoqë i bëni shoq Perëndisë, pa iu zbritur kurrfarë argumenti. Prandaj, kush prej këtyre dy grupeve meriton të jetë i sigurt, nëse dini ju?'),
(871, 6, 82, 'Për ata që besojnë dhe nuk e përziejnë besimin e tyre me zullum, atyre u përket siguria dhe ata janë në rrugë të djretë”.'),
(872, 6, 83, 'Ky është argumenti Ynë, të cilin Ne, ia dhamë Ibrahimit kundër popullit të tij. E Ne e ngrisim në shkallë më të lartë kë të dojmë. Me të vërtetë, Zoti yt është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(873, 6, 84, 'Na ia dhuruam atij (Ibrahimit) Is’hakun dhe Jakubin. Të dy këta i kemi udhëzuar në rrugë të drejtë. Dhe Nuhun e patëm udhëzuar më parë në rrugë të drejtë, e nga pasardhësit e tij: Davudin, Sulejmanin, Ejjubin, Jusufin, Musain, dhe Harunin. Dhe kështu Ne i shpërblejmë bamirësit –'),
(874, 6, 85, 'dhe Zekerijan, Jahjain, Isain, Iljazin – të gjithë këta kanë qenë nga njerëzit e mirë;'),
(875, 6, 86, 'dhe Ismailin, Eljaásan, Junusin dhe Lutin – të gjithë këta i kemi lavdëruar ng të tjerët;'),
(876, 6, 87, 'edhe disa nga prindërit e tyre; pasardhësit e tyre dhe vëllezërve të tyre – i kemi zgjedhur ata dhe i kemi udhëzuar në rrugë të drejtë.'),
(877, 6, 88, 'Ky është udhëzimi i Perëndisë, Ai udhëzon në rrugë të drejtë kë të dojë nga robërit e vet. E, sikur t’i bënin shok Atij, do t’ju humbë çdo gjë, nga ajo që kanë punuar.'),
(878, 6, 89, 'Këta janë ata që u kemi dhënë libër, dijeni të hollë dhe profetni. E, nëse aa (banorët e Mekkes) imohojnë këto, Na do t’ia besojmë një populli tjetër që nuk do t’i mohojë.'),
(879, 6, 90, 'Këta janë ata (pejgamberët e përmendur), të cilët Perëndia i ka udhëzuar në rrugën e drejtë, andaj edhe Ti (o Muhammed!) ndiqe rrugën e tyre. Thuaj: “Unë nuk kërkoj nga ju për Shpaljen e Kur’anit, kurrfarë shpërblimi. Se, ai është vetëm këshillë për gjithë njerëzimin”.'),
(880, 6, 91, 'Hebrenjt nuk e njohtën si duhet Perëndinë kur thanë: “Perëndia asgjë nuk i ka zbritur njeriut”. Thuaj: “E kush e shpalli Librin, që e solli Musai si dritë dhe udhërrëfyes për njerëzit, të cilin ju e keni shpërndarë në fleta, nga të cilat (disa) i tregoni, e shumicën e fshehni. Edhe ju jeni mësuar (me mësimin e Muhammedit) për atë që nuk keni ditur as ju, as etërit tuaj. Thuaj: “Perëndia u ka shpallur (atë Libër)!” Pastaj lërni ata, që të argëtohen me gënjeshtrat e tyre, (në të cilat janë zhytur).'),
(881, 6, 92, 'Ky Libër (Kur’ani), që ta kemi shpallur ty, është i madhërishëm, vërtetues i atij që gjendet para jush (Teurati), dhe frikësues i Ummul kuras (popullit të Mekkes) dhe atyre që gjenden rreth saj. Ata që besojnë në jetën e ardhshme besojnë edhe në të (Kur’an) dhe ata kujdesen për kryerjen e namazit të tyre.'),
(882, 6, 93, 'Kush është më zullumqar i madh se ai, që trillon gënjeshtra për Perëndinë ose që thotë: “Mua më është shpallur”, por asgjë nuk iu ka shpallur atij. Dhe ai që thotë: “Unë do ta shpalli të njëjtën gjë çka ka shpallur Perëndia”. Ah! T’i shohësh zullumqarët kur janë në mundimet e vdekjes, e engjëjt zgjasin duart (për t’ua marrë shpirtin, duke thënë): “Mirrni shpirtërat tuaj!” Sot ju do ndëshkoheni me dënim të poshtëruar për shkak të pavërtetave që i keni thënë për Perëndinë dhe që jeni krenuar (duke mos besuar) versetet e Tij.'),
(883, 6, 94, 'E do të Na vini ju veç e veç, ashtu siç ju kemi krijuar herën e parë (lakuriq). Dhe do t’i lini pas veti ato që iu patëm dhuruar. Na nuk po i shohim me ju ndërmjetësuesit tuaj; për të cilët mendonit se ata te ju janë shokët e Perëndisë. Në mes jush janë shkëputur të gjitha lidhjet dhe humbi nga ju, ajo që mendonit ju.'),
(884, 6, 95, 'Me të vërtetë, Perëndia është Ai që farën dhe bërthamën e bën të shpërthejë (të mugullojë). Ai, e nxjerr të gjallin prej të vdekurit dhe është nxjerrës i të vdekurit prej të gjallit. Ky është Perëndia. E ku po zmbrapseni ju (prej të vërtetës).'),
(885, 6, 96, 'Ai është Krijues i dritës ditore, natën e bëri për pushim, e Diellin e Hënën për të njehësuar kohën. Kjo është dispozita e Perëndisë, i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(886, 6, 97, 'Ai është që ju ka krijuar yjet, për t’u orientuar në errësirat e tokës dhe të detit. Na u shpjegojmë hollësisht dokumentet Tona, njerëzve që dinë.'),
(887, 6, 98, 'Ai është që ju ka krijur prej një njeriu (Ademit) që të jetoni dhe të varroseni në tokë. Na ua sqarojmë hollësisht dokumentat Tona njerëzve që kuptojnë.'),
(888, 6, 99, 'Dhe Ai është që ka zbritur ujin nga qielli, me të cilin nxjerrim çdo lloj bime dhe me te rritet gjelbërimi, e nga ky nxjerrim kokërrat e dendura; dhe prej palmës së hurmës, nxjerrim nga majat e saj, kalaveshat e ulët; dhe nxjerrim kopshtije rrushi, ulliri dhe shege, disa janë të ngjashme me njëra-tjetrën e disa jo. Vështroni frutet e tyre kur lidhin dhe piqen. Në to, me të vërtetë, ka argumente, për ata njerëz që besojnë.'),
(889, 6, 100, 'Idhujtarët i kanë trilluar Atij nga injoranca, djem e vajza. Qoftë lavdëruar Ai dhe qoftë shumë lart nga ajo që i përshkruajnë ata!'),
(890, 6, 101, 'Ai është krijues i qiejve dhe i Tokës! E si mund të ketë Ai fëmijë, për Te s’ka grua. Ai ka krijuar çdo gjë dhe vetëm Ai di çdo gjë.'),
(891, 6, 102, 'Ky është Perëndia, Zoti juaj. S’ka zot tjetër përveç Tij, Krijuesi i çdo gjëje, andaj adhuroni Atë! Ai është mbikëqyrës i çdo gjëje!'),
(892, 6, 103, 'Shikimet nuk mund ta përfshijnë Atë. Ai i përfshinë shikimet e të gjithëve. Ai është bamirës i pakufi dhe di çdo gjë.'),
(893, 6, 104, 'Me të vërtetë, ju kanë ardhur juve nga Zoti juaj, argumente të qarta; ai që i studjon ato, i sjellë dobi vetes; e ai që nuk i vëren ato, i bën dëm vetes. Unë nuk jam rojtari juaj.'),
(894, 6, 105, 'Kështu, Ne, në mënyrë të ndryshme i tregojmë dokumentet, për të thënë ata: “Ti i ke mësuar ato (prej librave të mëparshëm)!” dhe t’ju shpjegojmë Kur’anin njerëzve që dinë (të drejtën).'),
(895, 6, 106, 'Paso (ndiq) ti atë që të ka shpallur Zoti yt. S’ka tjetër zot veç Tij. Shmangu prej idhujtarëve.'),
(896, 6, 107, 'Sikur të donte Perëndia, ata nuk do t’i bënin shok Atij. Na nuk të kemi bërë ty rojtar të tyre, e as që je mbikëqyrës i tyre.'),
(897, 6, 108, 'Mos i shani ata, që adhurojnë tjetër pos Perëndisë, e që shajnë Perëndinë, duke kaluar kufirin me padije. Kështu, Ne ua kemi hijeshuar çdo populli veprat e tyre. Pastaj, ata do të kthehen te Zoti i tyre, e Ai do t’i lajmërojë ata për atë që kanë punuar.'),
(898, 6, 109, 'Ata betohen në Perëndinë, me betimin më të rëndë, se nëse ju vjen një dokument, ata me siguri do të besojnë. Thuaj: “Dokumentet janë vetëm te Perëndia!” E kush ua bënë me dije juve, se ata, kur t’ju vie dokumenti, nuk do të besojnë?'),
(899, 6, 110, 'Na ua kthejmë zemrat dhe shikimet e tyre (e nuk do të besojnë), ashtu si nuk kanë besuar (Kur’anin) as më parë. Na do t’i lëmë ata në humbje, që të bredhin andej-këndej.'),
(900, 6, 111, 'Sikur atyre t’ju dërgonim engjëjt, dhe sikur t’i ngjallnim të vdekurit e t’ju flisnin atyre dhe t’i tubonin të gjithë dokumentet para tyre, ata përsëri nuk do të besoni; përveç në dashtë Zoti, por shumica e tyre nuk dinë.'),
(901, 6, 112, 'Si për ty ashtu edhe për çdo profet, Na kei krijuar armiq djajtë e njerëzve dhe të xhinëve, të cilët i flasin njëri-tjetrit fjalë të zbukuruara për t’i mashtruar. E, sikur të donte Zoti yt, ata nuk do të bënin atë; andaj, lëri ata dhe atë, që trillojnë ata –'),
(902, 6, 113, 'e që zemrat e atyre që nuk besojnë në Ditën e Kijametit, ta kthehen dhe të kënaqen me të, e le të fitojnë çka të fitojnë (prej mëkateve).'),
(903, 6, 114, 'Vallë, të kërkojë tjetër arbitër përveç Perëndisë, e Ai është që ju ka zbritur Librin (Kur’anin) e shpjegueshëm. Atyre që Ne ua kemi dhënë Librin (para jush), e dinë se ai (Kur’ani) është me të vërtetë i zbritur nga Zoti yt. Dhe, kurrsesi mos u bënë nga ata që dyshojnë!'),
(904, 6, 115, 'U plotësuan fjalët e Zotit tënd, që janë kulmi, i së vërtetës dhe i drejtësisë; fjalët e Tij askush nuk mund t’i ndryshojë. Ai dëgjon çdo gjë dhe di çdo gjë.'),
(905, 6, 116, 'nëse i përulesh shumicës së njerëzve që gjenden në tokë; ata do të të shmangin nga rruga e Zotit. Ata ndjekin vetëm hamendjet. Ata vetëm gënjejnë.'),
(906, 6, 117, 'Me të vërtetë, Zoti yt i njeh më së miri ata që i shmangen rrugës së Perëndisë, dhe Ai i di më së miri ata që janë në rrugë të drejtë.'),
(907, 6, 118, 'Hani atë, që gjatë therrjes është përmendur emri i Perëndisë, nëse jeni besimtarë të dokumenteve të Tij.'),
(908, 6, 119, 'E pse mos ta hani ate që është therrë në emër të Perëndisë, e Ai ju ka shpjeguar ç’është e ndaluar, - përveç se kur jeni në shtrënges. ë shumë prej tyre nga padia, i shmangin ata prej udhës së mbarë për kënaqësinë e vet. Me të vërtetë, Zoti yt i njeh më së miri ata që e kanë kaluar kufirin.'),
(909, 6, 120, 'Lëreni mëkatet e hapta dhe të fshehta. Se, me të vërtetë, ata që kanë bërë mëkate, do të ndëshkohen për atë që kanë gaguar.'),
(910, 6, 121, 'Mos e hani atë, që kur është therrë nuk është përmendur emri i Perëndisë; ajo me të vërtetë, është mëkat! Me të vërtetë, djajtë i nxisin miqtë e vet që të polemizojnë me ju. E nëse ju u përuleni atyre, atëherë edhe ju, me të vërtetë, jeni idhujtarë.'),
(911, 6, 122, 'A barazohet ai që është i vdekur (i humbur) e Ne e ngjallëm dhe i dhamë dritë me të cilën ecë në mesin e njerëzve, me atë që është në errësirë e nuk mund të dalë prej saj. Kështu, mohuesve u janë zbukuruar ato vepra që kanë punuar.'),
(912, 6, 123, 'Dhe poashtu, Ne kemi bërë në çdo anë (sikur në Mekke) të mëdhenj – mëkatarë, që të pshtjellojnë në të. Mirëpo, ata pshtjellojnë vetëm vetveten, e nuk hetojnë.'),
(913, 6, 124, 'Kur atyre t’u vijë ndonjë dokument; ata thonë: “Na nuk do të besojmë deri sa të mos na jepet, si ajo që iu është dhënë pejgamberëve të Perëndisë”. Perëndia di më së miri ku e vendos profetninë e Vet. Ata që kanë bërë mëkate, do t’i arrijë një poshtrim nga ana e Perëndisë dhe ndëshkim i ashpër për atë që kanë pshtjelluar.'),
(914, 6, 125, 'Atë që Perëndia don ta shpie (në udhë të mbarë) – Ai, zemrën e atij, ia disponon për islamizëm, e atë që don ta lë në humbje – Ai zemrën e tij ia shtrëngon dhe ia bënë të ngushtë, si të jetë duke u ngjitur në qiell. Kështu, Perëndia, i ndëshkon ata që nuk besojnë.'),
(915, 6, 126, 'Kjo është rruga e drejtë e Zotit tënd. E, Na ju kemi shpjeguar argumentet, njerëzve që meditojnë.'),
(916, 6, 127, 'Ata do të kenë Shtëpinë e shpëtimit te Zoti i tyre. Dhe Ai është mbrojtës i tyre për veprat që punojnë ata.'),
(917, 6, 128, '(Kujtoje) atë Ditë kur Ai do t’i tubojë të gjithë (dhe do t’ju thotë): “O tog i xhindëve, ju shum njerëz i keni mashtruar!” – E miqët e tyre që ishin njerëz, thanë: “O Zoti ynë! Na u kënaqëm me njëri-tjetrin dhe arritëm në afatin tonë, të cilin na ke caktuar Ti. Perëndia do të thotë: “Zjarri është vendstrehimi juaj. Në të do të qëndroni përherë, përveç çka don Perëndia. Zoti yt, me të vërtetë, është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(918, 6, 129, 'Që, kështu, Ne i japim pushtet disa zullumqarëve mbi të tjerët, për atë që kanë fituar ata.'),
(919, 6, 130, '“O tog i xhindëve dhe i njerëzve, a nuk ju patën ardhur pejgamberë nga mesi i juaj, për t’ju treguar dokumentat Tona dhe për t’ju paralajmëruar takimin me këtë ditë?” Ata do të thonë: “Na këtë e pranojmë në dëm tonin”. Ata i ka mashtruar jeta e kësaj bote dhe ata dëshmojnë kundër vetes se janë mohues.'),
(920, 6, 131, 'Kjo është, ngase Zoti yt nuk e shkatërron asnjë vend për shkak të zullumit të tyre pa paralajmërimin e banorëve të tyre.'),
(921, 6, 132, 'Çdokujt i përket pozita për atë që ka punuar, se Zoti yt nuk është i pakujdesshëm për veprat e tyre.'),
(922, 6, 133, 'Zoti yt është i pasur dhe i mëshirshëm. Nëse don, ju zhdukë dhe sjellë pas jush çka të dojë, ashtu si ju ka krijuar nga pasardhësit e popujve të tjerë.'),
(923, 6, 134, 'Ajoqë u është premtuar, me të vërtetë do t’ju arrijë, e ju nuk mund t’i ikni asaj.'),
(924, 6, 135, 'Thuaj: “O populli im, punoni në gjendjen tuaj, e unë punojë (në gjendjen time); e do ta dini se për këtë do të jetë fundi i mirë. Me të vërtetë, zullumqarët nuk do të shpëtojnë.'),
(925, 6, 136, 'Ata (mohuesit) kanë caktuar pjesë nga të lashtat dhe kafshët shtëpiake, të cilat i ka krijuar Ai (Zoti) dhe thonë sipas hamendjes së tyre: “Kjo është për Perëndinë, e kjo është për putat tona”. Mirëpo, ajo pjsë që është për putat e tyre, nuk arrin (nuk jepen) te Zoti, kurse ajo që ata e kanë caktuar për Perëndinë – ajo arrinë (jipet) te putat e tyre. Sa gjykim të shëmtuar bëjnë ata!'),
(926, 6, 137, 'Po ashtu, shumë mohuesve ua kanë hijeshuar – shokët e tyre, - vrasjen e fëmijëve të vet për t’i zhdukur ata dhe për t’ju përzi fenë e tyre. E, sikur të donte Perëndia, ata nuk do ta bënin atë. Lërini ata dhe trillimet e tyre.'),
(927, 6, 138, 'Ata thonë: “Këto shtazë shtëpijake dhe këto e këto të lashta janë të ndaluara, nuk mund t’i hajë askush, përveç kujt ia lejojmë ne; e sipas hamendjes së tyre edhe disa kafshë shtëpiake janë të ndaluara për ngarkim dhe disa shtazë që kur i therrin nuk e përmendin emrin e Perëndisë, duke e përgënjeshtruar Atë (Perëndinë). Ai do t’i ndëshkojë ata për gënjeshtrat e trilluara.'),
(928, 6, 139, 'Dhe ata thanë: “Ajo që gjendet në barkun e këtyre shtazëve shtëpiake u përket vetëm meshkujve tonë, kurse për gratë tona janë të ndaluara. E, nëse lindin ato të ngordhura, ata të gjithë (burra e gra) janë pjesëmarrës në të”. Perëndia do t’i ndëshkojë ata për gënjeshtra. Se Ai, me të vërtetë, është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(929, 6, 140, 'Të humbur janë ata që i kanë mbytur fëmijët e tyre nga mendjelehtësia dhe padija dhe i kanë ndaluar ato që u ka dhuruar Perëndia, duke përgënjeshtruar ndaj Perëndisë. Ata kanë humbur dhe nuk qenë në rrugë të drejtë.'),
(930, 6, 141, 'Ai është që ka krijuar kopshte, vëreshta të larta dhe të shtruara, hurme dhe të lashta, me shije të ndryshme, ullinj e shega, të ngjashme (për nga forma), kurse të ndryshme (për nga shija). Hani frutet e tyre kurdo që të japin, dhe jepni atë që është detyrë në ditën e mbledhjes së tyrë. Por, mos e teproni, se Perëndia nuk i do ata që e teprojnë.'),
(931, 6, 142, 'Ai është që ka krijuar disa shtazë për ngarkim dhe për shtruarje, (shtresë e bërë prej leshi apo lëkurës së tyre). Hani nga ato që ua ka dhuruar Perëndia dhe mos ndiqni gurmët e djallit. Ai është armiku juaj i hapët.'),
(932, 6, 143, 'Perëndia ka krijuar tetë çifte kafshësh: dy nga dhentë (mashkull e femër) dhe dy nga dhitë. Thuaj: “A i ka ndaluar Perëndia dy meshkujt (dashin e sqapin) ose dy femërat ose atë që mbajnë në bark dy femra?” Lajmëromëni Mua me dije të saktë, nëse e thani të vërtetën.'),
(933, 6, 144, '(Perëndia ka krijuar) edhe dy deve dhe dy lopë. Thuaj: “A i ka ndaluar Perëndia dy meshkujt apo dy femërat ose atë që e mbajnë në bark dy femërat?” Apo mos keni qenë prezent ju kur Perëndia i ka urdhëruar për këtë? E, kush është më zullumqar se ai që shpif gënjeshtra ndaj Perëndisë, për t’i shpënë njerëzit në humbje nga padija? Me të vërtetë, Perëndia, nuk e çon në rrugë të drejtë kombin zullumqarë.'),
(934, 6, 145, 'Thuaj: “Unë nuk gjej në atë që më është shpallur mua (në Kur’an), se është e ndaluar ta hajë njeriu atë që do, gjithçka tjetër, përveç coftinë, gjakut të derdhur, ose mishit të derrit, se ai është i ndytë; ose është mëkat ajo që është therrë në emër të tjetër kujt, e jo në emër të Perëndisë. Por, për atë që ngushtohet nga uria e madhe, nuk është mëkat të hajë ato që i përmendëm, por pa u ngopur, vetëm sa të zhduket uria. Se, Perëndia, me të vërtetë, është Falës i madh dhe Përdëllimtar.'),
(935, 6, 146, 'Na ua kei ndaluar Hebrenjve të gjitha shtazët që kanë thonj (kthetra), ndërsa nga lopët e delet, për ta kemi ndaluar dhjamin e brendshëm të tyre, me përjashtim të dhjamit të shpinës, ose të atij dhjami që është puqë me zorrët, ose atë që është përzier me eshtra. Me këtë, Na i kemi ndëshkuar ata, për shkak të zullumit të tyre, se, Ne, padyshim, themi të vërtetën.'),
(936, 6, 147, 'Nëse ata të përgënjeshtrojnë ty, thuaju: “Zoti juaj është pronar i mëshirës së gjerë, e ndëshkimi i Tij nuk pengohet nga populli mëkatarë”.'),
(937, 6, 148, 'Idhujtarët do të thonë: “Sikur të donte Perëndia, nuk do t’i bënim shok Atij as ne as baballarët tanë, dhe asgjë të ndaluar nuk do të punonim”. Kështu mohonin edhe ata para tyre, derisa e shijuan dënimin tonë. Thuaj: “A keni ju ndonjë argument të na paraqitni? Ju mbështeteni vetëm në hamendje dhe jeni vetëm gënjeshtarë”.'),
(938, 6, 149, 'Thuaj: “Perëndia ka argument të qartë dhe po të donte Ai, të gjithëve do t’ju udhëzonte (me forcë) në rrugë të drejtë!”'),
(939, 6, 150, 'Thuaj: “Sillni dëshmitarët tuaj të cilët dëshmojnë se Perëndia e ka ndaluar këtë!” E nëse dëshmojnë (duke gënjyer), ti mos dëshmo me ta. Mos i ndiq dëshirat e atyre, që argumentet tona i konsiderojnë gënjeshtra dhe të cilët nuk besojnë në Ditën e mbramë! E, ata i bëjnë krahasim dike tjetër Zotit të tyre.'),
(940, 6, 151, 'Thuaj: “Ejani t’ju lexoj ato që ju ka ndaluar Perëndia: Atij mos i bëni shok; bëju mirë prindërve; mos vritni fëmijët tuaj për shkak të varfërisë, Na ju ushqejmë ju dhe ata. Mos ju çasni të këqijave, qofshin ato të hapëta apo të fshehta. Mos e vritni personin, vrasjen e të cilit e ka ndaluar Perëndia, përveç kur e kërkon drejtësia (gjyqi). Këto ju porosit Ai, që ta fisnikëroni mendjen.'),
(941, 6, 152, 'Dhe mos iu çasni pasurisë së bonjakut, përpos në një mënyrë të mirë, dreisa ta mbërrijë pjekurinë, kryeni drejt matjen dhe peshimin! Na nuk e ngarkojmë askë përtej mundësisë së tij. E, kur flisni, bëhuni të drejtë, qoftë edhe (kundër) të afërmit tuaj. Plotësoni obligimet e Perëndisë. Këto janë porositë e Tij, për t’ju këshilluar.'),
(942, 6, 153, 'Dhe, qe, kjo është rruga ime e drejtë; prandaj ndiqnie, e mos shkoni rrugëve tjera që t’ju shmangin ato nga rruga e Tij. Kjo është ajo që ju porositë Ai për t’u ruajtur nga të këqijat.'),
(943, 6, 154, 'Pastaj, ia kemi dhënë Musait Librin, duke ia plotësuar dhuntinë për atë të mirë, duke i shpjeguar çdo gjë, udhërrëfyes e mëshirës, për të besuar ata, në takimin me Zotin e tyre.'),
(944, 6, 155, 'Ky Libër (Kur’ani) që ua kemi shpallur, është i uruar, ndiqnie ate dhe druajuni kundërshtimit, për të qenë të mëshiruar,'),
(945, 6, 156, '(e kemi zbritur) për të mos thënë: “Libri u është shpallur vetëm dy grupeve apra nesh (Hebrenjve dhe të krishterëve) e nuk kemi mundur ta kuptojmë (meqë nuk kemi pasur njohuri të mëparshme për të)”.'),
(946, 6, 157, 'Ose për të mos thënë: “Sikur Libri të na ishte shpallur neve, ne do t’i përmbaheshim rrugës së drejtë më mirë se ata”. Pra, ju ka ardhur nga Zoti juaj argument i qartë dhe udhërrëfyes dhe mëshirë; e kush është më zullumqar nga ai që i përgënjeshtron argumentet e Perëndisë dhe largohet nga ato? Ne, do t’i ndëshkojmë ata që shmangën nga argumentet Tona, me dënim të rëndë, për shkak se u larguan.'),
(947, 6, 158, 'A mos vallë presin ata t’u vijnë engjëjt, ose të vijë (urdhëri) i Zotit tënd (me dënim) ose të vijnë disa argumente të Zotit tënd. Kur të vijë ndonjë argument i Zotit tënd, atëherë nuk do t’i bëjë dobi askujt besimi i tij, nëse nuk ka besuar më parë, ose nuk ka bërë ndonjë të mirë, në besimin e tij. Thuaj: “Pritni ju, se edhe ne, me të vërtetë po presim!”'),
(948, 6, 159, 'Me të vërtetë, ti s’ke asgjë me ata që e përndajnë fenë e tyre dhe shndërrohen në grupe. Te Perëndia është puna e tyre, e Ai do t’i informojë ata për atë që kanë punuar.'),
(949, 6, 160, 'Kush punon vepër të mirë, do të shpërblehet dhjetë fish. Kush punon vepër të keqe, do të dënohet vetëm sipas mëkatit, dhe atyre nuk do t’u bëhet kurrfarë padrejtësie.'),
(950, 6, 161, 'Thuaj: “Mua, me të vërtetë, Zoti im, më ka udhëzuar në rrugë të drejtë, në fenë e vërtetë, fenë e pastër të Ibrahimit, i cili nuk ka qenë adhurues i putavet”.'),
(951, 6, 162, 'Thuaj: “Me të vërtetë, namazi im, edhe adhurimi im, jeta ime, dhe vdekja ime, i përkasin vetëm Perëndisë, Zotit të Gjithësisë.'),
(952, 6, 163, 'Për, të s’ka shoq; dhe kjo më është urdhëruar dhe unë jam i pari musliman”.'),
(953, 6, 164, 'Thuaj: “A mos vallë do të kërkojë unë tjetër zot, përveç Allahut, kur Ai është Zot i të gjithave? Kushdo që punon diçka, punon për vete, dhe askush nuk ngarkohet me barrën e tjetrit. Pastaj do të ktheheni te Zoti juaj, dhe Ai do t’ju informojë për atë që keni qenë në konflikt.'),
(954, 6, 165, 'Ai bën që ta ndërroni njëri-tjetrin në Tokë dhe Ai ju ngritë nëpër pozita disa nga ju mbi të tjerët, për t’ju provuar për atë që ju ka dhënë. Me të vërtetë, Zoti yt, është i shpejtë në dënim, por me të vërtetë, është edhe falës e mëshirues.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'Elif Lam Mim Sad.'),
(956, 7, 2, 'Ky është Libri që t’u ka dërguar ty dhe le të mos bëhet kurrfarë shtrëngimi në zemrën tënde për shkak të tij, që me të t’i paralajmërosh dhe këshillosh besimtarët.'),
(957, 7, 3, 'Ndiqnie atë që u është zbritur nga Zoti juaj, e mos ndiqni tjetër mbrojtës përpos Tij. Pak po i pranoni këshillat ju.'),
(958, 7, 4, 'Sa shumë qytete kemi rrënuar Ne! e, dënimi Ynë ju erdhi natën ose ditën kur ata pushonin.'),
(959, 7, 5, 'Dhe thirrja e tyre, kur ju erdhi dënimi, qëndronte vetëm në fjalët: “Na, me të vërtetë kemi qenë zullumqarë!”'),
(960, 7, 6, 'Na, me të vërtetë, do të kërkojmë llogari prej popujve që u kemi dërguar pejgamberë; e gjithsesi, do t’i pyesim edhe pejgamberët.'),
(961, 7, 7, 'Dhe do t’jua praqesim atyre, të gjitha ato që i dimë, sepse, ne kemi qenë prezent.'),
(962, 7, 8, 'Peshimi (i veprave), atë ditë do të jetë i drejtë, atij që i rëndohet terezia me vepra të mira, do të jetë i lumtur;'),
(963, 7, 9, 'kurse, atij që i lehtësohet terezia me vepra të mira, ai do të humb veten, për shkak se i ka mohuar argumentet Tona.'),
(964, 7, 10, 'Na ju kemi vendosur në tokë dhe, në të, ju kemi dhënë çka ju nevojitet për gjallëri. – E sa pak falënderoheni!'),
(965, 7, 11, 'Na ju krijuam (baban tuaj Ademin), e pastaj ua dhamë formën; e ju thamë engjëjve: “Përuljuni Ademit!” e të gjithë iu përulën përveç Iblisit. A nuk ishte nga ata që përulet.'),
(966, 7, 12, '(Perëndia) tha: “Çka të pengojë ty që nuk iu përule, kur të urdhërova?” (Iblisi) tha: “Unë jam më i mirë se ai. Mua më krijove nga zjarri, kurse atë e krijove nga balta”.'),
(967, 7, 13, '(Perëndia) tha: “E atëherë, dil prej andaj (xhennetit)! Ti nuk ke të drejtë që të krenohesh në të; dil, me të vërtetë, ti je prej të urrejturve”.'),
(968, 7, 14, '(Iblisi) tha: “Më jep afat deri në ditën kur ngjallen njerëzit!”'),
(969, 7, 15, '(Perëndia) tha: “Ti je nga ata që u është dhënë afati”.'),
(970, 7, 16, '(Iblisi) tha: “Betohem se për atë që me ke humbur, do t’ju vë pengesa atyre në rrugën Tënde të drejtë,'),
(971, 7, 17, 'e do t’ju çasem atyre edhe përpara edhe për mbrapa, edhe në të djathtë, edhe në të majtë, e Ti do të konstatosh se shumica prej tyre nuk janë falenderues!”'),
(972, 7, 18, '(Perëndia) tha: “Dil prej andej (xhennetit) i qortuar dhe i poshtëruar. Pa dyshim, me ty dhe me të gjithë ata që shkojnë pas teje, do ta mbushi xhehennemin!”'),
(973, 7, 19, 'E ti o Adem! Bano ti dhe bashkëshortja jote në xhennet dhe hani ç’të dëshironi, por mos iu afroni kësaj peme se bëheni zullumqarë!”'),
(974, 7, 20, 'E, djalli iu përshpëriti, për t’ju zbuluar pjesët e turpshme të mbuluara të trupit të tyre dhe (djalli) tha: “Zoti juaj ju ka ndaluar (afrimin e këtij druri) vetëm për atë që të mos bëheni engjëj ose mos të mbeteni përherë (në xhennet)”.'),
(975, 7, 21, 'Dhe ai (djalli) iu betua atyre (duke thënë): “Unë jam për ju, me të vërtetë, këshilltar i sinqertë!”'),
(976, 7, 22, 'Dhe i mashtroi me dinakërinë e tij. Pasi që e shijuan (frutin) e drurit, iu zbuluan vendet e turpshme atyre dhe nisën të mbulohen me gjethet e xhennetit. E, ata i thirri Zoti i tyre: “A nuk ju kam ndaluar atë? (frutin e drurit); dhe, a nuk ju kam thënë: “Se, me të vërtetë, djalli është armiku juaj i hapët?”'),
(977, 7, 23, 'Ata thanë: “O Zoti ynë! Na vetes i kemi bërë dëm; e nëse Ti nuk na falë dhe nuk na mëshiron, na do të jemi nga ata që janë të humbur”.'),
(978, 7, 24, '(Perëndia) tha: “Zbritni! Ju, njëri-tjetrit jeni armik. Në Tokë do të banoni dhe do të jetoni deri në një kohë”.'),
(979, 7, 25, '(Perëndia) tha: “Në të do të jetoni, në të do të vdisni dhe prej saj do të dilni (ringjalleni)”.'),
(980, 7, 26, 'O bijtë e Ademit, Na ju dërguam rrobet që t’jua mbulojnë vendet e turpshme, si edhe rrobet luksoze; por, rrobet e virtyteve janë më të mirat. – Këto janë disa argumente të Perëndisë për t’i kujtuar ju (mirësitë e Perëndisë).'),
(981, 7, 27, 'O bijtë e Ademit, le të mos u mashtrojë kurrsesi djalli, ashtu si i nxori prindërit tuaj nga xhenneti (meqë i mashtroi), duke u zhveshur rrobat për t’ju dukur vendet e turpshme të tyre. Ai (djalli) dhe ushtria e tij ju sheh, prej nga ju nuk i shihni ata. Na i kemi bërë djajtë ndihmës të atyre që nuk besojnë.'),
(982, 7, 28, 'E kur të punojnë vepër të shëmtuar, thonë: “Na i kemi gjetur prindërit tonë në to; por edhe Perëndia na ka urdhëruar me këte”. Thuaj: “Perëndia nuk urdhëron të punohen vepra të shëmtuara! Përse flisni për Perëndinë atë që nuk e dini?”'),
(983, 7, 29, 'Thuaj: “Zoti im urdhëron – drejtësi. Dhe drejtohuni vetëm Atij, kurdo që bëni lutje, dhe lutjuni sinqerisht Atij, duke i bërë përulje! Ashtu siç ju ka krijuar prej asgjëje, ashtu do t’ju ringjallë.'),
(984, 7, 30, 'Një pjesë i ka udhëzuar në rrugë të drejtë, kurse pjesa tjetër e ka merituar humbjen. Ata e kanë bërë djallin mik, përpos Perëndisë, dhe mendojnë se janë në rrugë të drejtë.'),
(985, 7, 31, 'O bijtë e Ademit, vishuni përshtatshëm kurdo që të luteni! Dhe hani e pini, por mos e teproni; se Ai (Perëndia) nuk i do ata që e teprojnë.'),
(986, 7, 32, 'Thuaj: “Kush i ka penguar stolitë e Perëndisë, të cilat, Ai i ka krijuar për robërit e vet, dhe ushqimet e këndshme?” Thuaj: “Ato janë për besimtarë në këtë botë (dhe për të tjerët); e, në botën tjetër, vetëm për besimtarë. Që, kështu, Na ua shpjegojmë argumentet njerëzve që dijnë.'),
(987, 7, 33, 'Thuaj: “Zoti im i ka ndaluar vetëm punët e këqia, - qofshin të hapta ose të fshehta dhe mëkatet, shtypjen (dhunimin) pa të drejtë, të bëmurit shok Perëndisë, për të cilën nuk ka kurrfarë argumenti, dhe ka ndaluar të flisni për Perëndinë atë që nuk e dini.'),
(988, 7, 34, 'Çdo popull e ka fundin e vet, dhe kur t’i vijë koha e fundit, nuk mund të shtyhet as të shpejtohet, kurrsesi.'),
(989, 7, 35, 'O bijtë e Ademit, nëse nga mesi juaj vijnë pejgamberë që ju shpjegojnë dispozitat e Mia, atëhrë, ata që i frikësohen Perëndisë dhe punojnë vepra të mira, nuk ka frikë as pikëllim për ata.'),
(990, 7, 36, 'Kurse ata që i mohojnë argumentet e Mia, dhe sillen me mendjemadhësi ndaj tyre, ata do të jenë banorë të zjarrit, në të do të qëndrojnë përgjithmonë.'),
(991, 7, 37, 'Kush është më zullumqarë se ai që trillon gënjeshtra kundër Perëndisë ose që mohon argumente të Tij? Atyre do t’u jepet e tërë ajo që është shënuar (në Levhi Mahfudh). E, kur t’u vijnë të dërguarit Tonë (engjëjt), për t’ua marrë shpirtin, do t’u thonë (engjëjt): “Ku janë ata, të cilët ju i adhuroni, përveç Perëndisë”. Ata thonë: “Humbën prej nesh”. Kështu ata dëshmojnë kundër vetes, që kanë qenë mohues.'),
(992, 7, 38, '(Perëndia) u thotë: “Hyni në zjarr me popujt – xhindët dhe njerëzit – të cilët kanë qenë para jush. Dhe kurdo që një popull hyn në zjarr, e mallkon shokun tjetër. E, kur të tubohen të gjthë në të, atëherë ai i fundit u thotë atyre të parëve: “O Zoti ynë, këta na kanë sjellë në humbje, epju dënimin e dyfishtë të zjarrit!” (Perëndia) u thotë: “Për çdonjërin (nga ju) ka dënim të dyfishtë, por ju nuk e dini”.'),
(993, 7, 39, 'U thonë të parët atyre të fundit: “Ju nuk keni kurrfarë përparësie mbi ne, prandaj shijoni dënimin për atë që keni punuar.'),
(994, 7, 40, 'me të vërtetë, ata që argumentet Tona i konsiderojnë gënjeshtra e të cilët, janë sjellë me mendjemadhësi ndaj atyre, nuk do t’u hapën dyert e qiellit, as që do të hyjnë në xhennet, derisa të mos kalojë devja nëpër birë të gjilpërës. Kështu Na i ndëshkojmë mëkatarët.'),
(995, 7, 41, 'Për ta janë përgatitur shtretër zjarri, e mbi ta do të ketë mbulesa zjarri. Kështu Na i ndëshkojmë zullumqarët.'),
(996, 7, 42, 'Ata që besojnë dhe punojnë vepra të mira – Na nuk i ngarkojmë më tepër, veç sa kanë mundësi – ata janë banorë të xhennetit, në të cilin do të qëndrojnë përgjithmonë.'),
(997, 7, 43, 'Na, nga krahërori i tyre do të zhgulim çdo urrejtje; e nën ta do të rrjedhin lumenjtë. Dhe ata thonë: “Qoftë lavdëruar Perëndia që na udhëzoi në këtë. Ne nuk do të ishim udhëzuar sikur të mos na udhëzonte Perëndia. Me të vërtetë, të dërguarit e Zotit tonë, e kanë sjellë të vërtetën”. Dhe ata do të thërrasin: “Ky është xhenneti që ju është dhënë në trashëgim, për atë që keni punuar”.'),
(998, 7, 44, 'Dhe banorët e xhennetit u thonë banorëve të zjarrit: “Na e kemi gjetur të vërtetën, atë që na është premtuar nga Zoti ynë, e ju, a e keni gjetur të vërtetën, atë që u ka premtuar Zoti juaj?” Ata thonë: “Po!” e, atëherë, një lajmëtar në mes tyre thërret: “Me të vërtetë, mallkimi i Perëndisë u takon zullumqarëve”,'),
(999, 7, 45, 'të cilët i pengojnë të tjerët nga rruga e Perëndisë dhe kërkojnë ta shtrembërojnë, e nuk besojnë në jetën tjetër!”'),
(1000, 7, 46, 'Në mes tyre është perdja (muri), e, në majë të tij, janë njerëzit, të cilët të gjithë i njohin sipas fizionomisë. Dhe ata i thërrasin banorët e xhennetit: “Kënaqësia qoftë mbi ju!” Ata (njerëzit nga muri, ende) s’kanë hyrë në xhennet, por atë e dëshirojnë.'),
(1001, 7, 47, 'E kur t’ju kthehet shikimi kah banorët e zjarrit, thonë: “O Zoti ynë, mos na bënë (shoqëri) me zullumqarët!”'),
(1002, 7, 48, 'Ata që gjenden në mur, u thërrasin disa njerëzve, të cilët i njihnin sipas fizionomisë duke ju thënë: “Nuk ju ndihmoi as grumbullimi i pasurisë suaj, as ajo që u sollët me mendjemadhësi (ndaj të Vërtetës).'),
(1003, 7, 49, 'A janë këta, për të cilët ju u betuat se nuk do t’i arrijë mëshira e Perëndisë? (Atyre, të cilët janë nënçmuar, u thuhet): “Hyni në xhennet! Për ju s’ka frikë as pikëllim!”'),
(1004, 7, 50, 'Banorët e zjarrit do t’u thonë banorëve të xhennetit: “Derdhni pak ujë në ne, ose diçka nga ajo që u ka dhuruar Perëndia!” – Ata thonë: “Me të vërtetë, Perëndia këto i ka ndaluar për mohuesit,'),
(1005, 7, 51, 'të cilët e kanë marrë fenë lojë dhe argëtim, dhe të cilët, i ka mashtruar jeta e kësaj bote. Ne sot do t’i harrojmë ata, ashtu siç e kanë harruar ata takimin me këtë Ditë dhe për shkak se i kanë mohuar argumentet Tona.'),
(1006, 7, 52, 'Por, Ne u dëguam atyre Librin, të cilin ua kemi shpjeguar gjerësisht, si udhërrëfyes dhe mëshirë për njerëzit që besojnë.'),
(1007, 7, 53, 'A mos vallë ata presin realizimin konkret të tij? E, kur t’u vijë ajo që e presin (premtimi dhe qortimi), do të thonë për atë që e kanë harruar më parë: “Pejgamberët e Zotit tonë na kanë sjellë të vërtetën! A ka ndonjë ndërmjetësues që angazhohet për ne, ose të kthehemi (në këtë jetë), që të punojmë ndryshe seç kemi punuar”. Me të vërtetë, ata e kanë shkatërruar veten, e u zhdukën edhe ato që i trilluan.'),
(1008, 7, 54, 'Me të vërtetë, Zoti juaj është Perëndia, që i ka krijuar qiejt dhe tokën për gjashtë ditë (periudha të caktuara kohe), e pastaj e mbisundoi gjithësinë. Ai me errësirën e natës e mbulon ditën, e cila e ndjek atë; e Dielli, Hëna dhe yjet i përulen urdhërit të Tij. Vetëm ai krijon dhe sundon. I Lartësuar është Perëndia, Zoti i gjithësisë!'),
(1009, 7, 55, 'Lutjuni Zotit tuaj përulshëm dhe ngadalë! Ai, me të vërtetë, nuk i do ata që e kalojnë kufirin.'),
(1010, 7, 56, 'Dhe mos bëni çrregullime në tokë, kur në të është vënë rregulli, e Atij lutjuni duke iu friguar dhe shpresuar; se, me të vërtetë, mëshira e Perëndisë është afër bamirësve.'),
(1011, 7, 57, 'Ai i dërgon erërat si lajme të gëzuara para mëshirës së Tij; e kur erërat lëvizin retë e rënda me shi, Na i çojmë ato kah vendi i vdekur, e lëshojmë shi në të dhe nxjerrim lloj-lloj frutash; po kështu do t’i ngjallim të vdekurit, për të menduar ju!'),
(1012, 7, 58, 'Toka e mirë i jep frutet me lejën e Zotit të saj, e ajo tokë që është e ligë jep vetëm me mund. Që kështu, Na, në mënyrë të ndryshme ua shpjegojmë dokumentet Tona popullit mirënjohës.'),
(1013, 7, 59, 'Na me të vërtetë, ua kemi dërguar Nuhun popullit të tij, i cili u tha: “O populli im! Adhuronie Perëndinë, ju nuk keni zot tjetër përveç Tij! – Unë frikësohem për ju nga dënimi në Ditën e madhe!”'),
(1014, 7, 60, 'Paria e popullit të tij thanë: “Na me të vërtetë, të shohim ty në humbje të madhe”.'),
(1015, 7, 61, 'Ai tha: “O populli im! Unë nuk jam në kurrfarë humbje, por jam pejgamber nga ana e Zotit të gjithësisë;'),
(1016, 7, 62, 'ju sjell urdhërat e Zotit tim dhe ju dëshiroj çdo të mirë, unë di nga Perëndia atë që nuk e dini ju.'),
(1017, 7, 63, 'A po çuditeni që këshilla e Zotit tuaj, t’ju vijë nga një njeri i mesit tuaj, që t’ju tërheq vërejtjen, të bëheni besimtarë dhe për t’ju mëshiruar?'),
(1018, 7, 64, 'E ata e konsideruan atë gënjeshtar, andaj e shpëtuam atë dhe ata që ishin me të në barkë. Ndërsa, i fundosëm ata të cilët i konsideruan gënjeshtra argumentet Tona. Ata kanë qenë popull i verbër (injorant).'),
(1019, 7, 65, 'Ja dërguam (popullit) Adit, vëllaun e tyre – Hudin. Ai tha: “O populli im! Adhuronie Perëndinë, ju nuk keni zot tjetër përveç Tij. E, a nuk po i ruheni dënimit?”'),
(1020, 7, 66, 'Paria e popullit të tij, të cilët nuk besonin thanë: “Na, me të vërtetë, të konsiderojmë ty mendjelehtë dhe ne mendojmë se je gënjeshtar”.'),
(1021, 7, 67, 'Ai tha: “O populli im! Unë, me të vërtetë, nuk jam mendjelehtë, por jam profet i Zotit të gjithësisë”.'),
(1022, 7, 68, 'Unë ua sjell urdhërat e Zotit tim dhe unë jam këshilltar i sinqertë.'),
(1023, 7, 69, 'A po çuditeni që këshilla e Zotit tuaj, t’ju vijë ng një njeri i mesit tuaj, që t’ju tërheq vërejtjen. Kujtonie që Ai ju bëri zëvendës pas popullit të Nuhut dhe ju rriti me trup (dhe fuqi). Kujtonie dhuntitë e Zotit për të qenë të lumtur!”'),
(1024, 7, 70, 'Ata thanë: “A mos na ke ardhur ti neve për ta adhuruar na vetëm një Perëndi, e t’i lëmë ato që i kanë adhuruar prindërit tanë? Na jep atë çka na premton, nëse je i vërtetë!”'),
(1025, 7, 71, 'Ai tha: “Në ju ka rënë dënimi dhe zemrimi i Zotit tuaj. A doni të polemizoni me mua për emrat (e putave tuaj), të cilët ua keni dhënë ju dhe prindërit tuaj, e për të cilat, Perëndia nuk u ka sjellë kurrfarë argumentesh. Pra, pritni, se edhe unë po pres me ju!”'),
(1026, 7, 72, 'Dhe Ne e shpëtuam atë dhe ata që ishin me të, me mëshirën Tonë. E, i shfarosëm të gjithë ata, që i përgënjeshtruan argumentet Tona dhe nuk besuan.'),
(1027, 7, 73, 'Dhe (Na, ia dërguam popullit) themudit – vëllaun e tyre – Salihun. Ai tha: “O populli im! Adhuronie Perëndinë, ju nuk keni zot tjetër përveç Tij. Madje, me të vërtetë, ju ka ardhur argument nga Zoti juaj: kjo deve e Perëndisë për ju, është një mrekulli. Lëreni të kullosë në tokën e Perëndisë dhe mos i bëni ndonjë të keqe, se ju pret ndëshkimi i ashpër!'),
(1028, 7, 74, 'Dhe kujtohuni, ai juve ju bëri zëvendës pas Adit dhe ju vendosi në Tokë; në rrafshinat e saj po ngritni pallate, e në kodrina po gëdhendni shtëpia. Kujtoni të mirat e Perëndisë dhe mos bëni çrregullime në faqe të Dheut!”'),
(1029, 7, 75, 'E, paria e popullit të tij, ata që madhëroheshin, u thanë besimtarëve, të cilët i konsideronin për të dobët: “A dini ju që Salihu është i dërguar nga Zoti i tij?” Ata thanë: “Na, me të vërtetë, besojmë në të gjitha ato, që i janë shpallë atij”.'),
(1030, 7, 76, 'Ata që madhëroheshin thanë: “Na nuk besojmë në atë që besoni ju”.'),
(1031, 7, 77, 'Dhe therrën devën e nuk e përfillën urdhërin e Perëndisë së tyre dhe thanë: “O Salih, sillna atë që na premton, nëse je pejgamberë!”'),
(1032, 7, 78, 'Dhe ata i goditi një tërmet katastrofal e mbetën të vdekur në shtëpitë e tyre.'),
(1033, 7, 79, 'Ai (Salihu), u largua prej tyre dhe tha: “O populli im! Unë ua kam sjellë urdhërin e Zotit tim dhe ju kam këshilluar, por ju nuk i doni ata që këshillojnë”.'),
(1034, 7, 80, 'Dhe kujtohuni, kur e dërguam Lutin, i cili i tha popullit të vet: “Pse po bëni aso mbrapshti, çfarë askush nuk ka bërë në botë?”'),
(1035, 7, 81, 'Ju, me pasion (epsh) i çaseni meshkujve në vend të femrave. Ju, me të vërtetë, jeni popull që e teproni me të këqia!”'),
(1036, 7, 82, 'E, përgjegjia e popullit të tij ishte: “Dëboni ata nga vendbanimi jua! Ata janë njerëz që pastrohen shumë”.'),
(1037, 7, 83, 'E, Na e kemi shpëtuar atë dhe familjen e tij, përveç gruas së tij. Ajo ishte nga ata që humbën. –'),
(1038, 7, 84, 'Dhe mbi ta lëshuam shi – rrëbesh (gurësh), e shiko si përfunduan mëkatarët!'),
(1039, 7, 85, 'Dhe Na (ia dërguam) popullit të Medjenit, vëllain e tyre – Shuajbin e ai tha: “O populli im! Adhuronie Perëndinë! Ju nuk keni zot tjetër përveç Tij. Madje, me të vërtetë, ju ka ardhur argument nga Zoti juaj: Plotësoni matjen dhe peshimin e mos iu cungoni njerëzve sendet. Mos bëni ngatërresa në Tokë, pasi në të është vënë rregull. Kjo është më mirë për ju, nëse jeni besimtarë!'),
(1040, 7, 86, 'Dhe mos rrini në pusi në çdo rrugë, duke frikësuar dhe duke penguar nga rruga e Perëndisë ata që besojnë në Te, duke kërkuar shtrembërimin e saj! Dhe mendoni, kur ishit pak në numër, e Ai ju shumoi. Shikoni si përfundojnë ngatërrestarët!'),
(1041, 7, 87, 'Nëse, një grup i juaj beson në profetninë time, a një grup tjetër nuk beson, duroni, derisa Perëndia të gjykojë në mes nesh, se Ai është gjykatësi më i mirë!”'),
(1042, 7, 88, 'E paria e popullit të tij, ata që madhëroheshin thanë: “O Shuajb, na, me të vërtetë, do të dëbojmë ty dhe ata që kanë besuar me ty, nga vendbanimi ynë, ose do të ktheheni ju në fenë tonë”. (Shuajbi) tha: “A ngjanë, edhe pasi ta urrejmë atë!?'),
(1043, 7, 89, 'Na, me të vërtetë, do t’i bënim Perëndisë gënjeshtër, nëse kthehemi në fenë tuaj, pasi që Perëndia na shpëtoi nga ajo. Na nuk mund të kthehemi në të, përpos, nëse don Perëndia, Zoti ynë. Zoti ynë me dijeninë e Tij i përfshinë të gjitha. Na, te Perëndia mbështetemi. O Zoti ynë, gjyko Ti drejt në mes nesh dhe popullit tonë! Ti je gjykatësi më i mirë”.'),
(1044, 7, 90, 'E, paria e popullit të tij, ata që nuk kanë besuar, u thanë (atyre që kanë besuar): “Nëse shkoni pas Shuajbit, atëherë, me siguri, do të jeni të humbur”.'),
(1045, 7, 91, 'Dhe i goditi ata tërmeti katastrofal, e mbetën të vdekur në shtëpitë e tyre, të shtangur.'),
(1046, 7, 92, 'Ata që e kanë konsideruar Shuajbin gënjeshtar – (u zhdukën), thuajse nuk kanë ekzistuar kurrë; ata që e kanë konsideruar Shuajbin gënjeshtar, u zhdukën krejtësisht.'),
(1047, 7, 93, 'E, Shuajbi u largua prej tyre dhe tha: “O populli im, unë u kam sjellë urdhërat e Zotit tim dhe ju kam këshilluar. E pse të hidhërohem unë për popullin mohues?!”'),
(1048, 7, 94, 'Na nuk kemi dërguar asnjë pejgamber – në ndonjë vendbanim, e që të mos i kemi dënuar banorët e tij, me skamje dhe sëmundje (për shkak të padëgjueshmërisë), për t’u përulur.'),
(1049, 7, 95, 'Pastaj, ua shndërruam fatkeqësinë me lumturi, e kur u shtuan dhe u madhështuan, ata thanë: “Edhe prindërit tonë i patën goditur hidhërimet e gëzimet”. E atëherë i dënuam ata papritmas, e ata nuk e hetuan (ardhjen e dënimit).'),
(1050, 7, 96, 'Sikur banorët e vendbanimeve të besonin dhe të ruheshin prej mëkateve, Na do t’i vërshonim me dhunti nga qielli dhe toka, por ata gënjyen, prandaj, i dënuam për atë që vepruan.'),
(1051, 7, 97, 'A janë të sigurtë banorët e vendbanimeve, se nuk do t’i arrijë dënimi Ynë, natën, - duke qenë ata në gjumë?'),
(1052, 7, 98, 'A janë të sigurtë banorët e vendbanimeve, se nuk do t’i arrijë dënimi Ynë, ditën, - duke qenë në argëtim?'),
(1053, 7, 99, 'A janë të sigurtë ata nga dënimi i Perëndisë? Dënimit të Perëndisë, nuk i frikësohen vetëm ata që janë të humbur.'),
(1054, 7, 100, 'A nuk iu është e qartë atyre që e trashëguan tokën e banorëve të mëparshëm të saj, se, nëse Na dëshirojmë, - i dënojmë ata për mëkatet e tyre dhe u vëmë vulë në zemrat e tyre, e ata nuk e ndiejnë (nuk e pranojnë).'),
(1055, 7, 101, 'Për ato vendbanime, Na t’i kemi treguar ty disa ngjarje të tyre. Me të vërtetë, u erdhën atyre pejgamberë që u sollën argumente të qarta, por ata nuk besuan atë që më parë e konsideronin gënjeshtër. Kështu, Perëndia ua vulosi zemrat mohuesve, -'),
(1056, 7, 102, 'e Na, te shumica e tyre nuk kemi gjetur plotësim të çfarëdo zotimi; por, në të vërtetë, shumicën e tyre e kemi gjetur ngatërrestarë.'),
(1057, 7, 103, 'Pastaj dërguam – pas tyre, Musain me argumentet Tona, te Faraoni dhe paria e tij. Ata i mohuan ato; pra, shiko si është përfundimi i ngatërrestarëve!'),
(1058, 7, 104, 'Dhe musai tha: “O Faraon, unë me të vërtetë, jam i dërguari i Zotit të gjithësisë!'),
(1059, 7, 105, 'Jam i obliguar që për Perëndinë të them vetëm të vërtetën. Madje, me të vërtetë, ju kam sjellë argumentin e qartë nga Zoti juaj; andaj, lëshoi të vijnë me mua bijtë e Israelit!”'),
(1060, 7, 106, '(Faraoni) tha: “Nëse ke sjellë ndonjë argument, tregoje, nëse je nga ata që e thonë të vërtetën”.'),
(1061, 7, 107, 'E, (Musai) e lëshoi shkopin e vet, e ai u bë gjarpër i madh;'),
(1062, 7, 108, 'dhe e nxori dorën e tij – kur qe, për shikuesit dritë e bardhë.'),
(1063, 7, 109, 'Paria e popullit të Faraonit thanë: “Ky, me të vërtetë, qenka magjistar i shkathët,'),
(1064, 7, 110, 'i cili don t’ju dëbojë nga toka juaj, e çka propozoni ju?'),
(1065, 7, 111, 'ta thanë: “Mbani pak atë dhe vëllain e tij dhe dërgoni nëpër qytet që të tubojnë'),
(1066, 7, 112, 'e që të sjellin ty çdo magjistar të shkathët”.'),
(1067, 7, 113, 'Dhe erdhën magjistarët e Faraonit e thanë: “A me të vërtetë, do të shpërblehemi, nëse fitojmë?”'),
(1068, 7, 114, '(Faraoni) tha: “Po, ju jeni nga më të afërmit e mi”.'),
(1069, 7, 115, '(Magjistarët) thanë: “O Musa! Ose hedhe ti, ose të bëhemi hedhësa na”.'),
(1070, 7, 116, '(Musai) tha: “Ju hedheni!” E kur e hedhën, i mahnitën sytë e njerëzve, i frikësuan ata dhe shfaqën një magji të madhe.'),
(1071, 7, 117, 'E, Na, ia shpallëm Musait: “Hedhe shkopin!” E, ai (shkopi) filloi t’i gëlltisë të gjitha ato që i trilluan ata.'),
(1072, 7, 118, 'Dhe, kështu, doli në shesh e vërteta dhe dështoi ajo që vepruan ata.'),
(1073, 7, 119, 'Këtu, ata u mundën dhe u kthyen të poshtëruar,'),
(1074, 7, 120, 'e magjistarët ranë në sexhde (përulje).'),
(1075, 7, 121, '(Magjistarët) thanë: “Na besojmë në Zotin e gjithësisë, -'),
(1076, 7, 122, 'Zotin e Musait dhe të Harunit”.'),
(1077, 7, 123, 'Faraoni tha: “Ju e besuat para se t’ju lejoj unë, kjo, me të vërtetë, është mashtrim që e keni përgatitur në qytet, për t’i dëbuar banorët e tij. Do ta shihni ju!'),
(1078, 7, 124, 'Do t’ju këpusë duart dhe këmbët tërthorazi, e pastaj do t’ju vari të gjithëve.'),
(1079, 7, 125, 'Ata thanë: “Na, me të vërtetë, i kthehemi Zotit tonë!'),
(1080, 7, 126, 'Ti mund të na hakmerresh vetëm që i besuam argumentet e Zotit tonë – posa na erdhën. O Zoti ynë, na jep durim të madhe dhe bëna të vdesim muslimanë!”'),
(1081, 7, 127, 'Paria e popullit të Faraonit thanë: “A mos vallë do ta lësh Musain dhe popullin e tij që të bëjnë ngatërresa në tokë, dhe që të lë ty dhe zotrat tu?” (Faraoni) u përgjegj: “Na do t’i mbysim djemtë e tyre e do t’i lëmë gjallë gratë (bijat) e tyre; e na, me të vërtetë, jemi sundues mbi ta”.'),
(1082, 7, 128, 'Musai i tha popullit të vet: “Kërkoni ndihmë nga Perëndia dhe duroni, se, me të vërtetë, toka është e Perëndisë. Ai ia jep trashëgim kujt të dojë prej robërve të Tij. E, përfundim i bukur do të jetë për ata që i druajnë Perëndisë”.'),
(1083, 7, 129, '(Populli i Musait) thanë: “Na kemi qenë të torturuar para se të vish ti dhe pas ardhjes tënde”. (Musai) tha: “Zoti juaj do ta zhdukë armikun tuaj, e juve do t’ju bëjë trashëgimtarë në Tokë, e t’ju vërejë se si do të veproni”.'),
(1084, 7, 130, 'Dhe na e kemi dërrmuar popullin e Faraonit me thatësi, me vite të thata dhe mungesë frutesh, për t’u këshilluar ata.'),
(1085, 7, 131, 'Kur t’u vijë ndonjë e mirë, ata thonë: “Këtë e kemi merituar, e kur t’i godasë ndonjë e keqe, për atë e fajsojnë Musain dhe ata që janë me të. Por jo! Fatkeqësia e tyre është prej Perëndisë (për veprat e tyre), e shumica e tyre nuk e dinë!'),
(1086, 7, 132, 'Dhe ata kanë thënë: “Çfarëdo argumenti të na sjellësh ti, që të na magjepsish me të, na nuk jemi besimtarë tu”.'),
(1087, 7, 133, 'Prandaj, Na atyre ua kemi sjellë vërshimin, karkalecat, rriqërat, bretkosat dhe gjakun, - dokumente të qarta, por ata madhështoheshin dhe ishin popull keqbërës.'),
(1088, 7, 134, 'E kur i godiste dënimi, ata thonin: “O Musa, lutju Zotit tënd për ne, ashtu si të ka premtuar; e nëse na liron nga kjo vuajtje, na, me të vërtetë, do të besojmë ty, dhe do t’i dërgojmë bijtë e Israelit me ty”.'),
(1089, 7, 135, 'Dhe pasi e liruam nga vuajtja – deri në afatin e arritur, - ata, menjëherë e shkelën premtimin.'),
(1090, 7, 136, 'Andaj, Na i denuam ata dhe i fundosëm në det, ngase i përgënjeshtruan dokumentet Tona dhe ishin të pakujdesshëm ndaj tyre,'),
(1091, 7, 137, 'kurse popullit të shtypur (besimtarëve të Israelit) ia dhamë në trashëgim viset e tokave lindore dhe perëndimore, të cilat i kemi bekuar, dhe u plotësua premtimi i bukur i Zotit tënd ndaj Israelitëve, meqë duruan. Kurse, shkatërruam çdo gjë që bëri Faraoni dhe populli i tij dhe atë që ngritën ata.'),
(1092, 7, 138, 'Na i kaluam bijtë e Israelit përtej detit, ku hasën në popull që i luteshin putave të tyre; (Ata) thanë: “O Musa, bëna edhe neve një zot, ashtu si kanë ata zotëra”. (Musai) tha: “Ju me të vërtetë, jeni populli injorant”.'),
(1093, 7, 139, 'Këta, me të vërtetë janë të humbur dhe veprat që i punojnë janë të pavlefshme.'),
(1094, 7, 140, '(Musai) tha: “A mos vallë do t’ju kërkoj unë zot tjeër përveç Perëndisë, kur Ai ju ka lavdëruar mbi të tjerët”.'),
(1095, 7, 141, '(Kujtohuni) kur Na ju shpëtuam nga populli i Faraonit, i cili ju torturoi pa masë: fëmijët meshkuj i mbytnin, kurse femrat tuaja u linin gjallë. Kjo ka qenë sprovë e madhe për ju nga Zoti juaj.'),
(1096, 7, 142, 'Na, ia caktuam Musait tridhjetë netë (për adhurim) dhe ia plotësuam ato edhe me dhjetë të tjera, dhe kështu u plotësua koha e caktuar e Zotit të tij në dyzet ditë. E, Musai, i tha vëllait të tij Harunit: “Më zëvendëso te populli im dhe mbaj rregull, e mos ndiq rrugën e ngatërrestarëve!”'),
(1097, 7, 143, 'Kur Musai erdhi në kohën e caktuar Tonën dhe Zoti foli me të, (Musai) tha: “O Zoti im, diftomu që të të shoh Ty!” (Zoti) tha: “Ti nuk mund të më shohësh, por shiko në atë mal, e nëse qetësohet në vend të vet, ti do të më shohësh”. E kur erdhi në shprehje fuqia e Zotit të tij – në mal, e dërmoi atë në copa, e Musait i ra të fikët (i pakur). E, kur erdhi në vete, tha: “Qofsh i lavdëruar Ti, kthehem te Ti dhe unë jam i pari i besimtarëve!”'),
(1098, 7, 144, '(Zoti) tha: “O Musa, Unë të kam zgjedhur ty mbi të tjerët – me dërgimet e Mia dhe bisedimin Tim. Merre atë që ta kam dhënë dhe bëhu nga mirënjohësit!”'),
(1099, 7, 145, 'Na ia kemi shkruar në pllaka (Teurat) këshillën për çdo gjë dhe shpjegimin për gjithçka (që kanë nevojë): “Merre këtë, me seriozitet, dhe urdhëroje popullin tënd që të përvetësojë në mënyrën më të mirë. Unë do t’ju tregojë shtëpinë e mëkatarëve.'),
(1100, 7, 146, 'Do t’i shmangi prej argumenteve të Mia, ata që madhështohen pa të drejtë në Tokë. Nëse shohin çfarëdo dokumenti; nuk e besojnë atë. Nëse shohin rrugën e drejtë, nuk e ndjekin atë, e nëse shohin rrugën e gabuar, e marrin atë – rrugë. Kjo është për shkak se ata i përgënjeshtruan dokumentet Tona dhe ishin të pakujdesshëm ndaj tyre.'),
(1101, 7, 147, 'E, ata që përgënjeshtruan argumentet Tona dhe takimin në botën tjetër, do t’u dështojnë veprat e tyre. Ata, nuk do të dënohen ndryshe, veç ashtu si kanë punuar.'),
(1102, 7, 148, 'Populli i Musait – pas shkuarjes së tij – filluan ta adhurojnë viçin e bërë nga stolisjet e tyre të arit – viç për kah pamja dhe pallja. A nuk panë ata, se ai as nuk mund t’u fliste, as t’i udhëzojë në rrugë të drejtë. Ata morën këtë viç për adhurim dhe u bënë zullumqarë.'),
(1103, 7, 149, 'Dhe pasi u penduan shumë, pastaj dhe panë se janë trullosur, ata thanë: “Nëse Zoti ynë nuk na mëshiron dhe nuk na falë, me të vërtetë, do të jemi të humbur!”'),
(1104, 7, 150, 'E, kur Musai u kthye te populli i tij, i zemëruar dhe i pikëlluar, thirri: “Sa në mënyrë të shëmtuar jeni sjellë pas shkuarjes sime! A mos vallë e keni shpejtuar urdhërin e Zotit tuaj?” Dhe i hodhi pllakat (e Teuratit) e kapi vëllain për koke, duke ia afruar vetes. (Haruni) i tha: “O djali nënës sime! Ky popull me të vërtetë, më konsideroi për të dobët dhe gati më mbytën. Dhe, mos më turpëro para armiqëve dhe mos më numro në mesin e zullumqarëve”.'),
(1105, 7, 151, '(Musai) tha: “O Zoti im, falëm mua dhe vëllain tim dhe shpiena në mëshirën Tënde! Ti je më mëshiruesi i mëshiruesve”.'),
(1106, 7, 152, 'Me të vërtetë, ata që morën viçin për zot të tyre, do t’i arrijë dënimi i Zotit dhe poshtërimi edhe në këtë jetë. Kështu Na i ndëshkojmë ata që na përgënjeshtrojnë.'),
(1107, 7, 153, 'E, ata që kanë punuar vepra të këqija, e pastaj janë penduar dhe janë bërë besimtarë, me të vërtetë, Zoti yt, pas kësaj, është falës dhe mëshirues.'),
(1108, 7, 154, 'Dhe, kur Musait i kaloi zemrimi, i mori pllakat, në të cilat ishte shënuar udhëzimi për rrugën e drejtë dhe mëshira për ata që i druajnë Zotit të tyre.'),
(1109, 7, 155, 'Dhe Musai zgjodhi nga populli i tij shtatëdhjetë njerëz, për caktimin Tonë. E, kur i goditi tërmeti, ai (Musai) tha: “O Zoti im! Sikur të doje Ti, do t’i zhdukje ata dhe mua që më përpara. A do të na shkatërrosh, për ato vepra që kanë punuar budallenjtë tanë? Kjo është vetëm sprovë e Yte, me të cilën e shpie në humbje kënd të duash, kurse e udhëzon në rrugë të drejtë kënd të duash. Ti je i dashuri ynë, andaj fanla dhe mëshirona! Ti je më i miri i mëshiruesve;'),
(1110, 7, 156, 'dhe caktona të mira neve në këtë botë dhe në tjetrën. Se, ne, me të vërtetë, te Ti kthehemi”. (Perëndia) tha: “Unë e godas me dënimin Tim kë dua, e mëshira Ime përfshinë çdo gjë; ua jap atyre që ruhen nga mëkatet dhe japin zeqatin dhe atyre që u besojnë argumenteve Tona”,'),
(1111, 7, 157, 'ata që ndjekin rrugën e Pejgamberit, lajmëtarit të fesë, i cili nuk di shkrim lexim (Muhammedi), të cilin ata e gjejnë të shënuar në Teurat dhe Inxhil, i urdhëron ata të bëjnë vepra të mira dhe i ndalon nga të këqiat, ua lejon të mirat, e ua ndalon të këqiat dhe i liron nga barrët e rënda dhe vështirësitë që i kanë pasur. Prandaj, ata që do të besojnë ate (Muhammedin), që do ta përkrahin dhe ndihmojnë, e do të ndjekin dritën që i është shpallur atij – vetëm ata janë të lumtur.'),
(1112, 7, 158, 'Thuaj: “O njerëz, unë jam Pejgamber i Perëndisë për të gjithë ju, i Atij që i përket pushteti mbi qiej dhe Tokë. Vetëm Ai është i vetmi Zot, jeta dhe vdekja janë vepra të Tij, - prandaj besoni në Perëndinë dhe në të Dërguarin e Tij, lajmëtarin e fesë, i cili nuk di shkrim-lexim, i cili beson në Perëndinë dhe fjalët e Tij; shkoni pas tij – për të qenë në rrugën e drejtë!”'),
(1113, 7, 159, 'Në popullin e Musait ka disa njerëz që udhërrëfim e kanë të vërtetën dhe gjykojnë drejtë.'),
(1114, 7, 160, 'Na e kemi ndarë (popullin e Musait) në dymbëdhjetë fise dhe ia kemi shpallur Musait – kur populli i tij kërkoi ujë: “Bjeri me shkopin tënd shkëmbit!” Dhe rodhën nga ai dymbëdhjetë burime. Çdo fis e dinte burimin e vet. Dhe, Ne, u kemi bërë hije me ré (për t’u freskuar) dhe u zbritëm – men – (një lloj ushqimi i ëmbël) dhe zogj (si fëllaza). “Hani ushqime të mira të cilat ju kemi dhënë!” Ata nuk na dëmtuan Neve, por i bënë dëm vetvetes.'),
(1115, 7, 161, '(Kujtoju atyre) kur u është thënë: “Vendosuni në këtë vendbanim dhe hani në të si të doni dhe thuani: “Falna mëkatet! Dhe hyni të përulur në derë; e Na do t’u falim mëkatet dhe do t’u shtojmë bamirësve shpërblimin”, -'),
(1116, 7, 162, 'e atëherë, mëkatarët, ndërruan fjalët me tjera që nuk iu kanë thënë, dhe Na u dërguam atyre dëni nga qielli, sepse bënin zullum.'),
(1117, 7, 163, 'Pyeti ata për qytetin që gjendej në bregdet, kur ata e thyen dispozitën e së shtunës: atë ditë u vinin peshqit me shumicë, e ditëve tjera që nuk i kremtonin, nuk u vinin ata. Kështu, Ne, ata i vënim në provë, sepse vënin mëkate.'),
(1118, 7, 164, '(Kujtoje) kur disa prej tyre thanë: “Pse e këshilloni popullin, të cilin Perëndia do ta zhdukë ose do ta ndëshkojë me ndëshkim të ashpër?” (Këshilltarët) u përgjegjën: “Për t’u arsyetuar para Zotit tuaj dhe për t’i ruajtur ata nga mëkatet”.'),
(1119, 7, 165, 'Dhe kur e harruan atë, për të cilën ishin këshilluar, Na i shpëtuam ata që të tjerët i pengonin nga të këqiat, kurse i ndëshkonim me ndëshkim të ashpër ata që bënin zullum, për shkak të mëkateve të tyre.'),
(1120, 7, 166, 'Meqë ata e refuzuan me mendjemadhësi, atë që u ishte ndaluar, Na ju thamë: “Bëhuni majmunë të urryer”.'),
(1121, 7, 167, '(Kujtoje) kur Zoti yt ka paralajmëruar se do të dërgojë tek ata dikend, i cili do t’i mundojë, në mënyrën më të keqe, deri në Ditën e Kijametit. Me të vërtetë, Zoti yt dënon shpejt, por Ai, është edhe falës e mëshirues.'),
(1122, 7, 168, 'Dhe Na, i kemi shpërndarë ata në Tokë si popuj: Disa prej tyre janë të mirë e disa jo. Na, i kemi vënë në sprovë ata me gjëra të mria dhe të këqia për (pendim) t’u kthyer.'),
(1123, 7, 169, 'Pas tyre erdh gjenerata, e cila e trashëgoi Librin (Teuratin), duke i marrë gjërat e kalueshme (pavlefshme) të kësaj bote. Dhe thonin: “Do të na falet! E, nëse prapë u binte në dorë diçka e ngjashme, prapë e merrnin. Vallë, a nuk është marrë nga ana e tyre premtimi solemn në Libër (Teurat), që për Perëndinë të thonë vetëm të vërtetën. E, ata, kanë mësuar çka ka në të (Libër): Bota tjetër (e përhershme) është më e mirë për besimtarët, prandaj, vallë a nuk e kuptoni këtë?!'),
(1124, 7, 170, 'E, ata që i përmbahen dispozitave të Librit dhe që e kryejnë namazin, - Na, me të vërtetë, nuk do t’u humbim shpërblimin të mirëve.'),
(1125, 7, 171, '(Kujtoje) kur Na, ngritëm malin mbi ta, - që dukej si hije (ré), - e ata besonin se do të binte mbi ta. (Na u thamë atyre): “Merreni vendosmërisht atë që ua dhamë, dhe ta keni ndërmend atë që është në të (në tabelat e Teuratit), për t’u ruajtur nga të këqiat.'),
(1126, 7, 172, '(Kujtoje) kur Zoti yt – nga korrizi i bijve të Ademit nxori pasardhësit e tyre dhe ata i bëri dëshmitar (të vërtetës) kundër vetes. (Iu tha atyre): “A nuk jam Unë Zoti juaj?” Ata thanë: “Po, me të vërtetë”. Na ju kemi bërë dëshmitar që të mos thoni në Ditën e Kijametit: “Na për këtë nuk kemi qenë të informuar”.'),
(1127, 7, 173, 'Ose që të mos thoni: “Me të vërtetë, të parët tanë i kanë vënë shok Perëndisë para nesh, e ne jemi gjeneratë pas tyre. A mos vllë do të na shkatërrosh për atë çka kanë punuar të degjeneruarit?”'),
(1128, 7, 174, 'Dhe, kështu Na, hollësisht i shpjegojmë argumentet, për t’u kthyer ata (nga imitimi i të parëve).'),
(1129, 7, 175, 'Tregoju atyre për njeriun, të cilit Na ia kemi dhënë argumentet Tona, e i cili është zhveshur prej tyre, e djalli e ka bërë për vete, dhe, ai është bërë i humbur.'),
(1130, 7, 176, 'Sikur të donim Ne, do ta ngritnim atë (në shkallën e të mirëve) me argumentet Tona, por ai iu kthye tokës (kësaj jete) dhe ndoqi epshin e vet. Shembulli tij është si shembulli i qenit: nëse e ndjekë, ai e qet gjuhën, nëse largohesh, ai, përsëri e qet gjuhën. Ky është shembull i njerëzve, që i konsiderojnë gënjeshtra argumentet Tona. E, tregoju këto ngjarje, që të mendojnë ata.'),
(1131, 7, 177, 'I shëmtuar është shembulli i njerëzve që i konsiderojnë gënjeshtra argumentet Tona dhe i bëjnë vetvetes zullum.'),
(1132, 7, 178, 'Kë e udhëzon Perëndia, ai është i udhëzuar në rrugën e drejtë, e kë e humbë nga rruga e drejtë, ai është i dëmtuar.'),
(1133, 7, 179, 'Na kemi krijuar, me të vërtetë, shumë xhind dhe njerëz për xhehennem: ata kanë zemra (mend) me të cilat nuk kuptojnë, kanë sy me të cilët nuk shohin, kanë veshë me të cilët nuk dëgjojnë. Ata janë si shtazë, madje, edhe më të zi. Ata janë të pakujdesshëm.'),
(1134, 7, 180, 'Perëndisë i përkasin emrat më të bukur, andaj thirreni Atë me to (dhe lutjuni), e shmangiuni atyre që i shtrembërojnë emërat e Tij. Ata do të ndëshkohen ashtu si kanë vepruar.'),
(1135, 7, 181, 'E, në mesin e tyre qe kemi krijuar, ka njerëz që i udhëzojnë të tjerët në rrugën e drjetë dhe gjykojnë drejtë.'),
(1136, 7, 182, 'E, ata që i mohojnë argumentet Tona, Na, dalngadalë, do t’i shpiem kah shkatërrimi, pa e kuptuar ata, -'),
(1137, 7, 183, 'dhe do t’u japë afat atyre. Me të vërtetë, dënimi Im është i fortë.'),
(1138, 7, 184, 'A nuk mendojnë ata se shoku i tyre (Muhammedi), nuk ka kurrfarë marrëzie ai është vetëm paralajmërues i hapët.'),
(1139, 7, 185, 'A nuk e shohin ata perëndorinë e qiejve dhe të Tokës dhe të gjithë ato që i ka krijuar Perëndia dhe se ju ka afruar fundi i tyre (mohuesve). Në çfarë fjale, pos Tij (Kur’anit), do të besojnë?'),
(1140, 7, 186, 'Kënd e la Perëndia në humbje, askush nuk mund ta udhëzojë atë në rrugë të drejtë! Ai do t’i lërë ata të bredhin në humbje.'),
(1141, 7, 187, 'Të pyesin ty kur do të ngjajë Dita e Kijametit. Thuaju: “Atë e di vetëm Zoti im. Kohën kur do të ndodhë ajo do ta tregojë vetëm Ai. (Tmerret e Ditës së Kijametit) do të janë të rënda për qiejtë dhe Tokën, do t’u vijë krejtësisht papritmas. Të pyesin, thuase ti di diçka për të. Thuaj: “Këtë e di vetëm Perëndia; e shumica e njerëzve nuk e kuptojnë këtë (të vërtetë)”.'),
(1142, 7, 188, 'Thuaj: “Unë nuk mund t’i sjell vetes as dobi as dëm, përveç atë që don Perëndia. E sikur t’i dija fshehtësitë, do t’i shumëzoja të mirat dhe nuk do të më prekte asnjë e keqe. Unë jam vetëm paralajmërues dhe sihariques për njerëzit që besojnë”.'),
(1143, 7, 189, 'Ai (Perëndia) u krijoi ju prej një njeriu dhe nga ai krijoi bashkëshorten e tij, që të qetësohet me të. Dhe kur e mbuloi atë, ngarkuar me një barrë të lehtë, kali me të. E kur ajo u rëndua, ata të dy iu lutën Perëndisë, Zotit të tyre: “Nëse na dhuron pasardhës të shëndoshë, na, me të vërtetë, do të bëhemi falenderues!”'),
(1144, 7, 190, 'E kur Ai, ua dhuroi pasardhësit e shëndoshë, të dy gjinitë trilluan pastaj shok për Te; për atë që iu dha atyre. Por Perëndia, është shumë larg nga ato që ia trilluan paganët Atij!'),
(1145, 7, 191, 'A mos vllë ata ia trillojnë për shok (Zotit) atë që nuk mund të krijojë asgjë, kurse ata janë të krijuar.'),
(1146, 7, 192, 'As nuk mund t’u ndihmojnë gjë atyre, as t’i ndihmojnë vetes.'),
(1147, 7, 193, 'Nëse i thirrni ata (mohuesit) në rrugë të drejtë, nuk do t’u ndjekin ju: njësoj e keni – i thirrët apo nuk i thirrët.'),
(1148, 7, 194, 'Atyre që u luteni ju, përveç Perëndisë, janë robër si ju, prandaj thirrni ata që t’u përgjigjen, nëse thoni të vërtetën.'),
(1149, 7, 195, 'A kanë ata këmbë të ecin me to, ose duar të kapin me to, a kanë sy të shohin me ta, ose veshë të dëgjojnë me ta? Thuaj: “Thirrni zotërat tuaj, e kundër meje trilloni çfarëdo dredhie e mos zvarrisni,'),
(1150, 7, 196, 'mbrojtësi im, me të vërtetë, është Perëndia, që e ka zbritur Librin dhe Ai kujdeset për të mirët.'),
(1151, 7, 197, 'E, ata që i adhuroni, përveç Tij, nuk mund t’u ndihmojnë as ju e as vetes.'),
(1152, 7, 198, 'Dhe nëse i thirrni në rrugën e drejtë, ata nuk dëgjojnë; ti i sheh thuajse po të shikojnë, por, ata nuk shohin.'),
(1153, 7, 199, 'Bëhu i matur (në sjellje ndaj tyre), urdhëro vepra të mira dhe shmangu nga xhahilat (injorantët)!'),
(1154, 7, 200, 'Nëse djalli përpiqet të të shpie nga e keqja, kërko strehim te Perëndia, se Ai, me të vërtetë, i dëgjon dhe i di të gjitha.'),
(1155, 7, 201, 'Me të vërtetë, ata që besojnë – kur i gjen ndonjë e keqe nga djalli, i kujtojnë (urdhërat e Perëndisë), e atëherë e shohin rrugën e drejtë,'),
(1156, 7, 202, 'e vëllezërit e tyre (të djallit) i shpien edhe më tej në humbje dhe nuk shmangën nga ajo.'),
(1157, 7, 203, 'E nëse nuk iu sjell ndonjë argument, ato thonë: “Ah! Sikur ta trilloje ti vetë!” Thuaj: (o Muhammed!): “Unë shkoj vetëm pas asaj që ma ka shpallur Zoti im”. Këto janë argumente të qarta nga Zoti juaj, udhërrëfim dhe mëshirë për njerëzit që besojnë.'),
(1158, 7, 204, 'E kur të lexohet Kur’ani, ju dëgjonie dhe heshtni, për t’u mëshiruar.'),
(1159, 7, 205, 'Dhe në vete përmende Zotin tënd, me përulje dhe drojë – jo me zë të lartë, në mëngjes dhe mbrëmje, dhe mos u bën i pakujdesshëm,'),
(1160, 7, 206, 'me të vërtetë, ata që janë afër Zotit tënd, nuk ngurojnë ta adhurojnë Ate; vetëm Ate e lavdërojnë dhe vetëm para Tij bien në sexhde (me fytyrë për tokë).');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Të pyesin ty (o Muhammed), për plaçkën e luftës. Thuaj: “(Dispozitat) mbi plaçkat e luftës i përkasin Perëndisë dhe Pejgamberit”. Pranaj, druajuni Perëndisë dhe rregulloni mërrëdhëniet në mes jush dhe bëhuni të dëgjueshëm ndaj Perëndisë dhe Pejgamberit të Tij, nëse jeni besimtarë të vërtetë”.'),
(1162, 8, 2, 'Besimtarë të vërtetë janë vetëm ata, zemrat e të cilëve, kur të përmendet Perëndia, vërgëllojnë (nga frika dhe përulja), e kur t’u lexohen argumentet e Tij, iu shtohet thellimi në besim dhe vetëm te Zoti i tyre mbështeten,'),
(1163, 8, 3, 'ata që kryejnë namazin dhe japin nga pjesa e asaj që u kemi dhënë Ne.'),
(1164, 8, 4, 'Ata, padyshim, janë besimtarë të vërtetë. Për ata ka pozita te Zoti i tyre, falje dhe dhunti fisnike.'),
(1165, 8, 5, '(Këto dispozita) janë po ashtu si atëherë, kur të nxorri ty, Zoti yt, nga shhtëpia jote, në emër të së Vërtetës. Me të vërtetë, kjo një grupi prej besimtarëve nuk u pëlqeu.'),
(1166, 8, 6, 'Ata polemizojnë me ty për të Vërtetën, e cila madje u bë e qartë, sikur t’i shpie në vdekje, e ata vdekjen e shihnin haptazi.'),
(1167, 8, 7, '(Kujtoje) kur Perëndia ju premtoi njërën prej dy grupeve, që me ët vërtetë, ajo të jetë e juaja. E ju dëshiruat që ajo e paarmatosura të jetë e juaja; e Perëndia dëshiron ta forcojë të vërtetën me fjalë të Tij dhe mohuesve t’u zhdukë rrënjët,'),
(1168, 8, 8, 'për ta forcuar të Vërtetën (Islamizmin) dhe ta zhdukë gënjeshtrën, edhe sikur madje ta urrenin mëkatarët.'),
(1169, 8, 9, '(Kujtoje) kur ju kërkuat ndihmë nga Zoti jua, e Ai ju përgjegj: “Unë do t’jundihmoj me njëmijë engjëj, të cilët do të vijnë njëri pas tjetrit”.'),
(1170, 8, 10, 'Perëndia e bëri këtë vetëm sihariq, që të qetësohen zemrat tuaja me të. E ndihma është vetëm nga Perëndia. Me të vërtetë, Perëndia është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(1171, 8, 11, '(Kujtoje) kur (Zoti) bëri që ju të koteni në gjumë të lehtë për qetësimin tuaj nga ana e Tij, e lëshoi nga qielli – shi, për t’ju pastruar me të dhe për t’ju larguar shqetësimin e djallit, për t’i bashkuar zemrat tuaja me besim dhe t’ua forcojë këmbët tuaja.'),
(1172, 8, 12, '(Kujtoje) kur i urdhëroi Zoti yt engjëjt: “Unë jam me ju, andaj forconi ata që besojnë”. Unë do t’u shtie frikë në zemra mohuesve, andaj rrihni ju ata në qafë dhe në të gjithë gishtërinjtë.'),
(1173, 8, 13, 'Kjo është për shkak se ata e kundërshtuan Perëndinë dhe Pejgamberin e Tij; e kush e kundërshton Perëndinë dhe Pejgamberin e Tij, Perëndia, me të vërtetë, është ndëshkues i ashpër.'),
(1174, 8, 14, 'Ky është ndëshkimi, shijonie ju (mohuesit) atë. Me siguri, për mohuesit ka dënim zjarri.'),
(1175, 8, 15, 'O besimtarë, kur të ndesheni me mohues, e duke qenë ata shumë, mos ua ktheni shpinën!'),
(1176, 8, 16, 'E kush ua kthen shpinën atë ditë, përveç atij që tërhiqet me qëllim që të sulmojë përsëri, ose për t’iu bashkangjitur një grupi tjetër – ai kthehet me pezmin e Perëndisë dhe strehimi i tij është xhehennemi, e sa vend i shëmtuar është ai.'),
(1177, 8, 17, 'Ju nuk i keni mbytur ata, por i ka mbytur Perëndia! E ti nuk ke hudhë kur ke hudhë, por Perëndia ka hudhë (ajo ishte hudhje e mrekullueshme) që nga Vetja besimtarëve, t’u dhurojë dhuratë të bukur. Perëndia, me të vërtetë, i dëgjon të gjitha dhe di çdo gjë.'),
(1178, 8, 18, 'Kështu është dhurata, se, Perëndia i bënë të dobëta dredhitë e mohuesve.'),
(1179, 8, 19, 'Nëse ju (mohuesit) kërkuat fitoren; e që pra, fitorja ju erdhi (kundër jush). Por nëse tërhiqeni (nga kundërshtimi), kjo do të jetë për ju shumë më e dobishme. Nëse ktheheni (në luftë), edhe Na do të kthehemi (në ndihmë). Shoqëria juaj nuk do t’ju bëjë dobi asgjë, sikur madje të jetë e shumënumërt. Sepse, Perëndia, me të vërtetë, është me besimtarët.'),
(1180, 8, 20, 'O besimtarë! – bindjuni Perëndisë dhe Pejgamberit të Tij, mos u shmangëni nga Ai, se ju dëgjoni (Kur’anin),'),
(1181, 8, 21, 'e mos u bëni si ata që thonë: “E kemi dëgjuar!” – por ata, (në të vërtetë) nuk e kanë dëgjuar.'),
(1182, 8, 22, 'Krijesat më të këqia te Perëndia janë ato që janë shurdhë dhe memecë, të cilët nuk duan të kuptojnë.'),
(1183, 8, 23, 'Sikur Perëndia të dijë ndonjë të mirë të tyre, Ai do t’u jepte të dëgjuarit; por, edhe sikur ta kenë, ata do të shmangen dhe do të largohen nga besimi.'),
(1184, 8, 24, 'O besimtarë! Përgjigjuni Perëndisë dhe Pejgmberit kur t’ju thërrasë, meqë ju ngjallë juve; dhe ta dini se Perëndia ndërhyn në mes njeriut dhe zemrës së tij, dhe se tek Ai do të ktheheni.'),
(1185, 8, 25, 'Ruajuni ngatërresave, të cilat nuk i godasin vetëm ata që bëjnë keq, dhe ta dini se Perëndia dënon ashpër!'),
(1186, 8, 26, 'Dhe kujtohuni kur ishit pak në numër, ju konsideronin të dobët e druajshit se njerëzit do t’ju përlajnë në Tokë; e Ai me ndihmën e Tij ju strehoi dhe ju forcoi juve, e ju furnizoi me të mira, për t’iu falenderuar.'),
(1187, 8, 27, 'O besimtarë! Mos e tradhëtoni Perëndinë dhe Pejgamberin, se e keqpërdorni amanetin tuaj, e këtë ju e dini,'),
(1188, 8, 28, 'dinie se pasuria juaj dhe fëmijët tuaj janë vetëm sprovë, dhe se vetëm te Perëndia është shpërblimi i madh.'),
(1189, 8, 29, 'O besimtarë! Nëse i druani Perëndisë, Ai do t’ju japë aftësinë e të gjykuarit (të bëni dallimin në mes të vërtetës dhe të pavërtetës), do t’ju mbulon mëkatet tuaja dhe do t’ju falë. Perëndia është bamirës i madh.'),
(1190, 8, 30, '(Kujtoje) kur bënë dredhi mohuesit kundër teje: për të të burgosur, mbytur ose dëbuar. Ata bënin ngatërresa, por Perëndia ua prapësonte dredhitë e tyre, se Perëndia, është shkatërruesi më i mirë i dinakërive.'),
(1191, 8, 31, 'Kur atyre u lexohen ajetet Tona, ata thonë: “Na, madje kemi dëgjuar; po të duam ne, edhe na do të thonim diçka të ngjashme. Këto nuk janë kurrgjë, përpos përralla të të parëve”.'),
(1192, 8, 32, '(Kujtoje) kur, ata thanë: “Zoti ynë, nëse ky (Kur’ani) është e Vërtetë e zbritur prej Teje, lësho mbi ne gurë si shiu nga qielli, ose dërgona ndëshkimin e dhembshëm!”'),
(1193, 8, 33, 'Perëndia nuk i ndëshkon ata, derisa ti je në mesin e tyre; dhe Perëndia nuk i ndëshkon, nëse ata kërkojnë falje.'),
(1194, 8, 34, 'Ç’kanë ata, e (përse) të mos i dënojë Perëndia, sepse, ata e pengojnë vizitën në Mesxhidil-haram (Qabe), edhe pse ata nuk janë padronë (kujdestarë) të tij. Padronë të tij duhet të bëhen vetëm ata që i druajnë Perëndisë, por shumica e tyre nuk e dinë.'),
(1195, 8, 35, 'Lutja e tyre në Qabe qëndron vetëm në fërshëllimë dhe duartrokitje. Andaj priteni ndëshkimin, pasi që jeni mohues!'),
(1196, 8, 36, 'Me të vërtetë, mohuesit e ndajnë (shpenzojnë) pasurinë e tyre, për t’i penguar njerëzit nga rruga e Perëndisë. Ata do ta ndajnë atë (pasuri), e pastaj do ta ankojnë atë humbje, sepse, do të jenë të mundur. E, ata që nuk besojnë, do të tubohen në xhehennem,'),
(1197, 8, 37, 'që Perëndia të veçoj të keqin nga i miri; të këqinjt i palon njërin mbi tjetrin, e të gjithë i grumbullon e i hudhë në xhehennem. Këta, me të vërtetë, janë të humbur.'),
(1198, 8, 38, 'Thuaju mohuesve: nëse largohen (nga mohimi), do t’ju falet e kaluara, e nëse kthehen (në mohim), atëherë dihet se si ka qenë ligji i të parëve (se si janë ndëshkuar mohuesit dhe si janë ndihmuar besimtarët).'),
(1199, 8, 39, 'Dhe luftoni kundër tyre, derisa të zhduket idhujtaria dhe derisa të mbetet vetëm feja e Perëndisë. E, nëse frenohen prej mohimit, - Perëndia i sheh punët e tyre;'),
(1200, 8, 40, 'E, nëse largohen nga feja, dinie se Perëndia është miku juaj. Ai është mik i mrekullueshëm dhe ndihmës i mrekullueshëm!'),
(1201, 8, 41, 'Dinie ju, se çkado që të fitoni në luftë, një e pesta i takon Perëndisë, Pejgamberit dhe farefisit të tij, bonjakëve, të varfërve dhe udhëtarit (që ka ngelur në rrugë), nëse besoni në Perëndinë dhe në atë çka ia kemi shpallur robit Tonë në ditën e fitores, në ditën kur u ndeshën dy taborret, - se Perëndia, me të vërtetë, është i Plotëfuqishëm për çdo gjë.'),
(1202, 8, 42, '(Kujtoje) kur ju ishit në luginën e afërme, e ata në luginën e largët, ndërsa karavani përfundi jush. E, sikur t’i kishit premtuar njëri-tjetrit, nuk do t’ishit pajtuar për kohën e konfliktit, por Perëndia e plotësojë atë ngjarje që ish e caktuar: për t’u shkatërruar ai që është i padrejtë, - me dokumente të qarta, e të jetojë ai që është i drejtë – me dokumente të qarta. Se, Perëndia, me të vërtetë, dëgjon të gjitha dhe di çdo gjë.'),
(1203, 8, 43, '(Kujtoje) kur Perëndia në ëndërr të tregoi se ata (armiqët) janë pak, e sikur të tregonte që ata janë shumë, ju do të frikësoheshit dhe do të grindeshit për këtë çështje, por Perëndia ju shpëtoi. Ai di se çka keni në zemrat tuaja.'),
(1204, 8, 44, '(Kujtoje) – kur u ndeshët, e Ai ua dëftojë atë në sytë e juaj pak, - e ju pakësoi juve në sytë e atyre, për ta zbatuar atë që ishte e caktuar; - e të Perëndia kthehen të gjitha punët.'),
(1205, 8, 45, 'O besimtarë, kur të ndesheni me ndonjë çetë (të armikut), bëhuni këmbëngulës dhe përmendi shumë Perëndinë, për të qenë të shpëtuar,'),
(1206, 8, 46, 'dhe përuljuni Perëndisë dhe Pejgamberit të Tij, dhe mos u grindni në mes vete, se do të demoralizoheni e do t’ju lë fuqia; dhe bëhuni të durueshëm, se, Perëndia, me të vërtetë, është me të durueshmit.'),
(1207, 8, 47, 'Mos u bëni si ata që dolën nga shtëpitë e tyre me mendjemadhësi, e për sy e faqe të botës, - e i pengojnë njerëzit nga rruga e Perëndisë. Perëndia, i përfshinë të gjitha ato që punojnë ata.'),
(1208, 8, 48, '(Dhe kujtoje) kur djalli ua zbukuroi veprat atyre e u tha: “Nuk ka kush prej njerëzve sot që mund t’ju mundë dhe unë jam mbrojtësi juaj”. E, kur u ndeshën dy çetat, u zmbraps ai (djalli) kah thembrat e tij, dhe tha: “Unë nuk kam asgjë me ju, unë shoh atë që nuk e shihni ju, dhe unë i frikësohem Perëndisë, sepse Perëndia ndëshkon ashpër.'),
(1209, 8, 49, '(Kujtoje) kur hipokritët dhe ata, në zemrat e të cilëve kishte sëmundje (dyshim), thoshin: “Këta i ka mashtruar feja e tyre!” Por, ai që mbështetet në Perëndinë, me të vërtetë, Perëndia është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(1210, 8, 50, 'E sikur t’i shihje ti se si ua merrnin shpirtërat engjëjt – mohuesve, duke i rrahur në fytyrë dhe shpinë (para e pas) dhe duke (ju thënë): “Shijonie dënimin e zjarrit.'),
(1211, 8, 51, 'Kjo është për atë, që me duartë tuaja e keni përgatitur, sepse Perëndia nuk i dëmton robërit e Vet pa të drejtë!”'),
(1212, 8, 52, '(Sjelljet e tyre) i ngjajnë sjelljeve – të ithtarëve të Faraonit dhe atyre që kanë qenë para tyre. Ata i mohuan argumentet e Perëndisë, e Perëndia i dënoi për shkak të mëkateve të tyre. Me të vërtetë, Perëndia është i fuqishëm dhe ashpër dënon;'),
(1213, 8, 53, 'kjo është për arsye se Perëndia nuk ia prishë popullit mirësitë që ia ka dhuruar – për derisa ata të mos i prishin sjelljet e veta, - se Perëndia, me të vërtetë, i dëgjon të gjitha dhe di çdo gjë.'),
(1214, 8, 54, '(Sjelljet e tyre) i ngjajnë sjelljeve të ithtarëve të Faraonit dhe të atyre që jetuan para tyre. Ata i përgënjeshtronin argumentet e Zotit të tyre, andaj, për shkak të mëkateve të tyre, Na i kemi shkatërruar dhe fundosur ithtarët e Faraonit, - të gjithë ata kanë qenë zullumqarë.'),
(1215, 8, 55, 'Krijesat më të këqia te Perëndia, janë ato që mohojnë, - ata nuk besojnë,'),
(1216, 8, 56, 'ata me të cilët ke lidhë marrëveshje, e ata çdo herë e prishin marrëveshjen e vet, dhe nuk i druajnë Perëndisë.'),
(1217, 8, 57, 'Nëse i ndesh ti ata në luftë, shpartalloji ata që janë pas tyre, për t’u këshilluar ata.'),
(1218, 8, 58, 'Posa të vërejsh që ndonjë fis e thei besën, edhe ti thyeja atij – në të njëjtën mënyrë prishja marrëveshjen – se, Perëndia, me të vërtetë, nuk i don ata që tradhëtojnë.'),
(1219, 8, 59, 'Le të mos mendojnë kursesi ata që nuk besojnë, se do t’i shpëtojmë (dënimit të Zotit), sepse ata nuk mund ta dobësojnë fuqinë e Perëndisë.'),
(1220, 8, 60, 'Kundër tyre përgatitni kuaj dhe forcë për luftë, sa të mundeni, me të cilën do t’i shtini frikën armikut të Perëndisë dhe armikut tuaj, dhe të tjerëve, përveç tyre. Ju nuk i njihni ata. Perëndia i njeh ata. Çkado që të shpenzoni në rrugën e Perëndisë do t’ju kompensohet dhe nuk do t’ju bëhet padrejtësi.'),
(1221, 8, 61, 'Nëse ata tregojnë prirje, për paqe, trego edhe ti prirje për të dhe mbështetu te Perëndia! Se Ai, me të vërtetë, i dëgjon të gjitha dhe di çdo gjë.'),
(1222, 8, 62, 'E, nëse dëshirojnë të të mashtrojnë ty, - me të vërtetë, të mjafton Perëndia; Ai të ka forcuar, me ndihmën e Tij dhe me besimtarë,'),
(1223, 8, 63, 'dhe, Ai, i bashkoi zemrat e tyre. Sikur ti të shpenzoje tërë atë pasuri që gjendet në Tokë, nuk do të mund t’i bashkoje zemrat e tyre, por Perëndia i bashkoi ato, - Ai, me të vërtetë është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(1224, 8, 64, 'O Pejgamber! Perëndia të mjafton ty dhe besimtarëve që të pasojnë.'),
(1225, 8, 65, 'O Pejgamber! Inkurajoi besimtarët në luftë. Nëse në mesin tuaj gjenden njëzet të durueshëm, do t’i mundin dyqind, e nëse nga ju janë njëqind të durueshëm, do t’i mundin njëmijë mohues, sepse, ata janë njerëz që nuk kuptojnë.'),
(1226, 8, 66, 'Tani, Perëndia u ka lehtësuar juve; Ai di se jeni të lodhur: nëse prej jush njëqind janë të durueshëm do t’i mundin dyqind; e nëse ju jeni njëmijë – me vullnetin e Perëndisë do t’i mundni dymijë. Se, Perëndia, është me të durueshmit.'),
(1227, 8, 67, 'Asnjë pejgamber s’ka të drejtë të ketë rob për vete, derisa të mos arrijë fitore të plotë në Tokë; ju i dëshironi të mirat e kalueshme të kësaj bote, kurse Perëndia dëshiron botën tjetër. Perëndia është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(1228, 8, 68, 'Të mos ishte dispozita e mëparshme e Perëndisë, do t’ju godiste mundimi i madh – për atë që keni marrë.'),
(1229, 8, 69, 'Hani ushqimet që keni zënë (në luftë), si gjë të lejueshme dhe të bukur, dhe druajuni Perëndisë. – Se, Perëndia, me të vërtetë, është falës dhe mëshirues.'),
(1230, 8, 70, 'O Pejgamber! Thuaju robërve që gjenden në duart tuaja: “Nëse Perëndia di se në zemrat tuaja ka çfarëdo të mirë, do t’u japë ende më të mirë nga ajo që u është marrë juve dhe do t’ju falë”. – Se, Perëndia është falës dhe mëshirues.'),
(1231, 8, 71, 'E, nëse ata duan të të tradhëtojnë ty, e ata – qysh më parë kanë tradhëtuar Perëndinë, dhe prandaj, Ai ty ta mundësoi fitoren ndaj tyre. Perëndia është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(1232, 8, 72, 'Me të vërtetë, ata që besojnë dhe emigrojnë, e që luftojnë me pasurinë dhe jetën e tyre në rrugën e Perëndisë, dhe ata që u bëjnë strehim dhe u ndihmojnë, janë miqë për njëri-tjetrin. Por, ata që besojnë e që nuk kanë emigruar, - nuk mund të bëhen ndihmëtarë e trashëgimtarë – derisa të mos emigrojnë. E, nëse ju luten që t’u ndihmoni në fe, jeni të obliguar t’u dilni në ndihmë, përpos me popullin me të cilin keni marrëveshje (për mossulmim). E, Perëndia e sheh mirë atë që veproni.'),
(1233, 8, 73, 'E, mohuesit janë miq për njëri-tjetrin. Nëse nuk veproni kështu siç jeni urdhëruar, do të bëhet ngatërresë dhe çrregullim i madh në Tokë.'),
(1234, 8, 74, 'Ata që besojnë dhe migrojnë, e luftojnë në rrugën e Perëndisë, dhe ata që u bëjnë strehim e u ndihmojnë, - ata, padyshim, janë besimtarë të vërtetë, - për ta ka falje dhe furnizim të mirë.'),
(1235, 8, 75, 'E, ata që u bënë besimtarë më vonë, - që emigrojnë dhe luftojnë bashkë me ju, - ata radhiten me ju, kurse ata (besimtarë) që janë në lidhje farefisnore – sipas Librit të Perëndisë – janë më të afërt për njëri-tjetrin. Me të vërtetë, Perëndia është i dijshëm për çdo gjë.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Që ky është akti i ndërprerjes së marrëveshjes paqësore nga ana e Perëndisë dhe Pejgamberit të Tij me idhujtarët, që keni pasë bërë lidhje me ta:'),
(1237, 9, 2, '“Udhëtoni nëpër botë ju (idhujtarë) edhe katër muaj, por ta dini se nuk do të mund t’i shpëtoni Perëndisë (nga dënimi), dhe Perëndia do t’i poshtërojë mohuesit”;'),
(1238, 9, 3, 'dhe ja shpallja e Perëndisë dhe Pejgamberit të Tij – njerëzve, - në ditën e haxhxhit të madh: “Perëndia dhe Pejgamberi i tij shkëpusin çdo obligim ndaj idhujtarëve”. E, nëse pendoheni (nga mashtrimi dhe mohimi), për ju është më mirë; por, nëse largoheni (nga pendimi), ta dini se nuk do të mund t’i shpëtoni Perëndisë! Sihariqoji mohuesit me dënim të dhëmbshëm,'),
(1239, 9, 4, 'përpos ata idhujtarë, me të cilët keni lidhur marrëveshje, e që nuk ju kanë prishur asgjë nga marrëveshja, dhe që nuk kanë ndihmuar askë kundër jush. Dhe, plotësojeni marrëveshjen e tyre deri në fund. Me të vërtetë, Perëndia, i do ata që ruhen prej mëkateve.'),
(1240, 9, 5, 'Kur të kalojnë mujat e mëdhenj, atëherë mbytni idhujtarët kudo që t’i takoni, zëni robër, rrethoni dhe bënju pusi kudo! E, nëse pendohen e bëjnë namaz dhe japin zeqatin, atëherë lini të qetë. Se, Perëndia, me të vërtetë, është falës dhe mëshirues.'),
(1241, 9, 6, 'Nëse, ndonjë nga idhujtarët kërkon mbrojtje nga ti, mbroje, derisa t’i dëgjojë fjalët e Perëndisë, e pastaj shpëne në vend të sigurt të tij. Kjo është për atë (shkak), meqë janë popull që nuk dijnë.'),
(1242, 9, 7, 'Si mund të kenë idhujtarët marrëveshje me Perëndinë dhe Pejgamberin e Tij?! – Mirëpo, me ata që keni lidhur ju marrëveshje te Mesxhidil-harami, derisa t’i përmbahen ata marrëveshjes – përmbajuni edhe ju, sepse Perëndia, i don ata që ruhen prej mëkateve.'),
(1243, 9, 8, 'Si, (me pasë marrëveshje), nëse ju mundin ata, nuk e respektojnë as farefisninë as marrëveshjen? Ata ju kënaqin me fjalë, ndërsa zemrat e tyre ju kundërshtojnë; shumica e tyre janë mëkatarë.'),
(1244, 9, 9, 'Ata i kanë ndërruar argumentet e Perëndisë me ndonjë vlerë të vogël, dhe i pengojnë njerëzit nga rruga e Tij. Me të vërtetë, janë të shëmtuara ato që punojnë ata.'),
(1245, 9, 10, 'Ata nuk iu respektojnë besimtarëve – as farefisninë as marrëveshjen, këta janë tejkaluesit e kufirit (në të këqia).'),
(1246, 9, 11, 'E, nëse ata pendohen, dhe falin namazin e japin zeqatin, (atëherë) për nga besimi janë vëllezër me ju. E, Na ua shpjegojmë argumentet njerëzve që dinë.'),
(1247, 9, 12, 'E, nëse ata e prishin betimin, pasi që e kanë lidhur marrëveshjen, dhe e sulmojnë fenë tuaj, luftoni kundër udhëheqësit të mohuesve, sepse, ata nuk kanë as betim (as lidhje); këtë e bëni që të frenohen ata (nga të këqiat).'),
(1248, 9, 13, 'Vallë, a nuk do të luftoni kundër popullit që e ka shkelur betimin dhe lidhjen dhe i cili është përpjekur ta dëbojë Pejgamberin, e ata së pari kanë filluar (t’ju sulmojnë)? Vallë, a ju frikoheni atyre? Të druajturit Perëndisë është me vend, nëse jeni besimtarë.'),
(1249, 9, 14, 'Luftoni kundër tyre! Perëndia i dënon ata me duart tuaja, dhe i poshtëron, u ndihmon juve kundër tyre, dhe i shëron zemrat e populli besimtar.'),
(1250, 9, 15, 'dhe e largon hidhërimin nga zemrat e tyre. Perëndia e pranon pendimin e atij që do. Perëndia është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(1251, 9, 16, 'Vallë, a mendoni ju se do të braktiseni, e që Perëndia të mos i tregojë ata që kanë luftuar dhe të cilët nuk e kanë bërë asnjë mik intim, përpos Perëndisë, Pejgamberit të Tij dhe besimtarëve? – E, Perëndia e di atë që bëni.'),
(1252, 9, 17, 'Idhujtarët nuk janë të denjë për mirëmbajtjen e tempujve të Perëndisë, meqë ata vetë pranojnë se janë mohues. Këta janë ata, veprat e të cilëve janë të humbura. Dhe, ata përherë, do të mbeten në skëterrë.'),
(1253, 9, 18, 'Tempujt e Perëndisë i mirëmbajnë vetëm ata që besojnë Perëndinë. Ditën e Fundit, falin namazin, japin zeqatin dhe që nuk i frikohen askujt, përveç Perëndisë. Këta janë ata, që janë në rrugën e drejtë.'),
(1254, 9, 19, 'Vallë, a mendoni ju se furnizimi i haxhinjve dhe mirëmbajtja e Mesxhidi-haramit është e njëjtë me ata që besojnë në Perëndinë, në Ditën e Kijametit dhe që luftojnë në rrugën e Perëndisë? Nuk janë të brabartë ata – te Perëndia. E, Perëndia nuk i udhëzon në rrugë të drejtë zullumqarët.'),
(1255, 9, 20, 'Ata që besojnë, e që shpërngulen dhe luftojnë në rrugën e Perëndisë – me pasurinë e tyre dhe jetën e tyre, janë më lartë për nga pozita – te Perëndia, dhe këta janë ata, që kanë fituar.'),
(1256, 9, 21, 'Ata do t’i sihariqojë Zoti i tyre me mëshirën e Tij dhe kënaqësinë, si dhe me kopshtie, në të cilat ka dhunti të përhershme për ta;'),
(1257, 9, 22, 'në to do të qëndrojnë përherë e përgjithmonë. Me të vërtetë, vetëm te Perëndia është shpërblimi i madh.'),
(1258, 9, 23, 'O besimtarë! Mos e bëni mik baban tuaj dhe vëllezërit tuaj, nëse ata më tepër e duan mohimin se sa besimin. Kushdo prej jush që i bënë ata miq, është zullumqarë.'),
(1259, 9, 24, 'Thuaj: “Nëse etërit tuaj, bijtë tuaj, vëllezërit tuaj, gratë tuaja, farefisi juaj, pasuria jua, që e kini fituar, tregtia – për të cilën keni frikë se nuk do të shkojë mirë dhe shtëpitë – në të cilat e ndieni veten rehat, u janë më të dashura se sa Perëndia, i Dërguari i Tij dhe lufta në rrugën e Tij, atëherë pritni deri sa ta sjellë vendimin Perëndia. E, Perëndia, nuk don ta udhëzojë në rrugën e drejtë popullin e korruptuar.'),
(1260, 9, 25, 'Me të vërtetë, Perëndia ju ka ndihmuar në shumë vende, si dhe në ditën e (luftës) së Huneinit kur ju entuziazmoi numri i madh i juaj, por ai numër nuk ju solli dobi, asgjë, toka iu dukë e ngushtë, edhe pse është e gjërë, prandaj u zmbrapsët duke vrapuar.'),
(1261, 9, 26, 'Pastaj, Perëndia ia dërgoi qetësinë e Tij Pejgamberit të vet dhe besimtarëve dhe e dërgoi ushtrinë, të cilën ju nuk e keni parë, e i dënoi ata që kanë mohuar. Ky është ndëshkimi i mohuesve.'),
(1262, 9, 27, 'E, pas kësaj, Perëndia e pranon pendimin për kë të dojë. Perëndia është falës dhe mëshirues.'),
(1263, 9, 28, 'O besimtarë! Me të vërtetë, idhujtarët janë të ndytë, dhe mos t’i afrohen Mesxhidil-haramit pas haxhxhit të këtij viti. E, nëse i frikoheni varfërisë, në dasht Perëndia, ju bënë të pasur dhe dhuntitë e Veta. Se, Perëndia, me të vërtetë, është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(1264, 9, 29, 'Luftoni kundër atyre që nuk besojnë në Perëndinë, as Ditën e Kijametit, që nuk e marrin për të ndaluar atë që e ka ndaluar Perëndia dhe Pejgamberi i Tij, dhe që nuk e pranojnë fenë e vërtetë, - atyre që u është dhënë Libri, derisa të mos japin xhizje (tatim) me dorën e vet, të përulur.'),
(1265, 9, 30, 'Hebrenjt thonin: “Uzejri është i biri i Perëndisë, të krishterët thonin: “Isai është i biri i Perëndisë. Këto janë fjalët e tyre, nga goja e tyre, i imitojnë fjalët e mohuesve të mëparshëm. I vraftë Perëndia! Si po shmangën (nga e vërteta)!'),
(1266, 9, 31, 'Ata, përpos Perëndisë kanë konsideruar për zot dijetarët e tyre dhe murgët e tyre dhe Isain – të birin e Merjemit, kurse u është urdhëruar që të adhurojnë vetëm një Zot, - s’ka zot tjetër përveç Tij. Ai është shumë lartë nga ata të cilët i barazojnë me Te.'),
(1267, 9, 32, 'Ata dëshirojnë ta fikin dritën e Perëndisë me gojën e tyre, kurse Perëndia don vetëm ta plotësojë dritën e vet, e madje, edhe sikur ta urrejnë mohuesit.'),
(1268, 9, 33, 'Ai është që ka dërguar Pejgamberin e Tij me udhërrëfim (Kur’an) dhe fenë e vërtetë, për ta lartësuar mbi të gjitha fetë, e madje, edhe sikur ta urrejnë idhujtarët.'),
(1269, 9, 34, 'O besimtarë! Me të vërtetë, shumë priftërinj dhe murgë hanë pasurinë e popullit – pa të drejtë dhe zmbrapin njerëzit nga rruga e Perëndisë. E, ata që e grumbullojnë arin dhe argjendin dhe nuk e shpenzojnë në rrugën e Perëndisë (si janë urdhëruar), sihariqoji me dënim të dhëmbshëm,'),
(1270, 9, 35, '(që do t’i goditë), në Ditën kur do të digjen në zjarrin e xhehennemit, e me të do të damkoset balli i tyre, anët (ijet) e tyre dhe shpina e tyre. (U thuhet atyre): “Këtë e keni mbledhur për vete; andaj, shijoni atë që e grumbulluat!”'),
(1271, 9, 36, 'Me të vërtetë, numri i muajve te Perëndia është dymbëdhjetë, sipas Librit të Perëndisë, prej ditës që i ka krijuar qiejtë dhe Tokën. Katër prej tyre janë të shenjtë; ky është besim i drejtë. E, në këta, mos i bëni vetes zullum! Por luftoni të gjithë kundër idhujtarëve, ashtu si luftojnë ata të gjithë kundër jush. Të dini, se Perëndia është me ata që ruhen nga mëkatet.'),
(1272, 9, 37, 'Me të vërtetë ndërrimi i vendeve (të muajve të shenjtë) vetëm e rritë mohimin, me të cilin, mohuesit arrijnë në humbje: një vit e proklamojnë ate të lejueshëm e vitin tjetër, të ndaluar (luftimin), për ta plotësuar numrin e muajve që i ka ndaluar Perëndia, dhe e lejojnë atë që e ka ndaluar Perëndia. Atyre, iu janë dukur të bukura veprat e shëmtuara të tyre. Por Perëndia, nuk i udhëzon në rrugë të drejtë ata që nuk besojnë.'),
(1273, 9, 38, 'O besimtarë! Çka kini që ngurroni, - thuajse jeni ngjitur për tokë – kur ju thuhet: “Dilni në luftë, në rrugën e Perëndisë! A jeni të kënaqur me jetën e kësaj bote, në vend të përhershmes (amshueshmës). E, kënaqësia e kësaj bote, në krahasim me atë të botës tjetër – është shumë e vogël.'),
(1274, 9, 39, 'Nëse nuk shkoni në luftë, Ai (Perëndia) do t’ju ndëshkojë me dënim të dhembshëm dhe do t’ju ndërrojë me një popull tjetër, Atij ju nuk do të mund t’i bëni dëm asgjë. Se Perëndia është i Plotëfuqishëm për çdo gjë.'),
(1275, 9, 40, 'Nëse ju nuk i ndihmoni atij (Pejgamberit), Perëndia i ka ndihmuar, kur e dëbuan mohuesit atë vetë të dytin, kur këta të dy ishin në shpellë, e kur i tha shokut të vet: “Mos u mërzit! Se, Perëndia, me të vërtetë është me ne!” E, Perëndia, e zbriti qetësinë mbi shokun e tij, kurse ate e ndihmoi me ushtrinë, të cilën ju nuk e keni parë. Dhe, bëri që fjala e mohuesve të jetë e poshtër, kurse fjala e Perëndisë të mbetet lartë. Me të vërtetë, Perëndia, është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(1276, 9, 41, 'Dilni në luftë të lehtë e të rëndë (të gjithë pa dallim); dhe luftoni me pasurinë tuaj dhe trupin tuaj në rrugën e Perëndisë! Kjo është më mirë për ju, nëse dini!'),
(1277, 9, 42, 'Sikur ajo të ishte një gjë e afërt dhe udhëtimi mesatar, ata do të pasonin ty. Por atyre iu dukë rrugë e largët. Dhe ata do të betohen në Perëndinë: “Sikur të kishim mundur, me siguri do të dilnim bashkë me ju”; dhe kështu ata e shkatërrojnë vetveten, e Perëndia e di se ata janë gënjeshtarë.'),
(1278, 9, 43, 'Të faltë Perëndia ty: që u lejove atyre (të mungojnë nga lufta); derisa të kuptosh, se kush të flet të vërtetën dhe derisa t’i njohësh gënjeshtarët.'),
(1279, 9, 44, 'Nga ti nuk do të kërkojnë leje (të mungojnë), për të luftuar me pasurinë dhe trupin e tyre, ata që besojnë në Perëndinë dhe Ditën e Kijametit. E, Perëndia i di mirë ata që ruhen nga mëkatet.'),
(1280, 9, 45, 'Nga ti do të kërkojnë leje, vetëm ata që nuk besojnë në Perëndinë dhe Ditën e Kijametit dhe zemrat e atyre që dyshojnë. Ata, luhaten në dyshimin e vet.'),
(1281, 9, 46, 'E, sikur ata të donin të shkonin (në luftë), do ta përgatisnin pajisjen për të; por Perëndia nuk e pëlqeu shkuarjen e tyre në luftë, e prandaj, i pengoi ata me dembeli. Dhe atyre u është thënë: “Rrini me ata që janë ulur (si pleq e të sëmurë)!”'),
(1282, 9, 47, 'Sikur të kishin dalë me ju, ata do t’u shtonin juve vetëm të keqe dhe, në mesin tuaj do të shtinin ngatërresa, duke ju dëshiruar të keqen. E, në mesin tuaj ka edhe përgjues të tyre. E Perëndia i di zullumqarët.'),
(1283, 9, 48, 'Ata edhe më parë përpiqeshin të të bëjnë ngatërresa dhe t’i pshtjellojnë punët e tua, derisa erdhi e Vërteta dhe u tregua e qartë feja e Perëndisë, e ata e urrenin këtë.'),
(1284, 9, 49, 'Disa prej tyre thonë: “Më lejo (të mungoj në luftë) dhe mos më ço në sprovë!” Qe, ata, ranë pikërisht në sprovë. Me të vërtetë, skëterra i përfshinë mohuesit.'),
(1285, 9, 50, 'Nëse të gjen ty ndonjë e mirë, ata hidhërohen, e nëse të godet ndonjë e keqe, ata thonë: “Na, edhe më parë kemi qenë të matur, dhe kthehen te të vetët me gëzim.'),
(1286, 9, 51, 'Thuaj: “Do të na godas vetëm ajo që na ka caktuar Perëndia, Ai është Zoti ynë dhe vetëm te Perëndia le të mbështeten besimtarët!”'),
(1287, 9, 52, 'Thuaj: “A po pritni ju për ne – hiç tjetër – përveç njërën prej dy të mirave? E, na po presim që Perëndia t’ju godet me dënim nga ana e Tij ose me duart tona. Pra, pritni ju se edhe na me ju do të presim”.'),
(1288, 9, 53, 'Thuaj: “Shpenzoni me ëndje apo dhunë! Nga ju nuk do të pranohet kurrsesi. Ju, me të vërtetë jeni popull i korruptuar”.'),
(1289, 9, 54, 'E, lëmoshat e tyre nuk do të pranohen, ngase ata, nuk besojnë në Perëndinë dhe Pejgamberin e Tij dhe namazit i janë qasur vetëm në mënyrë të ngathët, dhe nuk shpenzojnë (nga pasuria), veç se në mënyrë përbuzëse.'),
(1290, 9, 55, 'Le të mos ngazëllejë ty (o Muhammed!) pasuria e tyre, e as fëmijët e tyre! Se, Perëndia dëshiron t’i ndëshkojë me to në këtë jetë dhe t’ju dalë shpirti atyre duke qenë mohues.'),
(1291, 9, 56, 'Ata u betohen në Perëndinë se janë në fenë tuaj, por ata nuk janë me ju (prej muslimanëve), por janë popull frigacak.'),
(1292, 9, 57, 'Sikur ata të gjenin ndonjë strehim, shpellë apo vend mbrojtjeje, do të niseshin atje me nxitim.'),
(1293, 9, 58, 'Ke edhe të tillë që të qortojnë për shkak të ndarjes së lëmoshës. Nëse u jepet ajo – janë të kënaqur, e nëse nuk u jepet, përnjëherësh zemërohen.'),
(1294, 9, 59, 'Sikur të kënaqeshin ata, me atë që u ka dhënë Perëndia dhe Pejgamberi i Tij (do t’ishte më mirë për ta), dhe të thonin: “Na mjafton neve Perëndia; Perëndia do të na japë nga mirësitë e Veta, dhe Pejgamberi i Tij. Na, me të vërtetë, vetëm Perëndinë e dëshirojmë”.'),
(1295, 9, 60, 'Lëmosha u takon vetëm të varfërve, të gjorëve dhe atyre që e mbledhin atë (rrogë e nëpunësit që punon në atë lëmë), dhe atyre që duhet përfituar zemrat (në islamizëm); dhe për lirim nga skllavëria, dhe të ngarkuarve në borxhe, dhe për rrugën e Perëndisë, dhe udhëtarit të rastit. Këto janë dispozitat, e Perëndisë. Se, me të vërtetë, Perëndia është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(1296, 9, 61, 'Ka asosh që e shqetësojnë Pejgamberin dhe thonë: “Ai është vesh (dëgjon çdo gjë që i thuhet)”. Thuaj (o Muhammed): “Ai dëgjon atë që është e mirë për ju, beson në Perëndinë dhe u beson besimtarëve. Dhe ai është mëshirë për ata që besojnë”. E, ata që e shqetësojnë Profetin e Perëndisë, i pret ndëshkim i dhembshëm.'),
(1297, 9, 62, 'Ata ju betohen në Perëndinë për t’ju kënaqur juve, por Perëndia dh Pejgamberi i Tij, është më e drejtë me i kënaq, nëse janë besimtarë.'),
(1298, 9, 63, 'Vallë, a nuk dijnë ata, se ai që i kundërshton Perëndisë dhe Pejgamberit të Tij, i është përgatitur zjarri i xhehennemit, në të cilin do të qëndrojnë përherë? Ky është poshtërim i madh!'),
(1299, 9, 64, 'Hipokritët druajnë se do të shpallet ndonjë sure, e cila do të larjmërojë se ç’ka në zemrat e tyre. Thuaj: “Talluni ju! Se, Perëndia, me të vërtetë do ta nxjerrë atë, cilës i druani ju”.'),
(1300, 9, 65, 'E, nëse i pyet ata (për këtë tallje), do të përgjigjen: “Na vetëm biseduam dhe u argëtuam”. Thuaj: “A mos vallë me Perëndinë, e versetet e Tij dhe Pejgamberin e Tij po talleni?”'),
(1301, 9, 66, 'Mos u arsyetohuni! Se, ju, mohuat fenë, pasi që patët besuar. Nëse Ne e falim një grup tuajin, do ta ndëshkojmë grupin tjetër, ngase ishin mëkatarë.'),
(1302, 9, 67, 'Hipokritët dhe hipokritet janë të ngjashëm me njëri-tjetrin: kërkojnë që të bëhen vepra të këqia e pengojnë të mirat dhe janë duarshtrënguar (koprracë). Ata kanë harruar Perëndinë, por edhe Ai, i harron ata (i la pa dhuratë, si të ishin të harruar). Me të vërtetë, hipokritët janë mëkatarë.'),
(1303, 9, 68, 'Perëndia u ka premtuar hipokritëve, hipokriteve dhe mohuesve – zjarrin e xhehennemit, në të cilin do të qëndrojnë përgjithmonë. Ai ju mjafton! Dhe Perëndia i ka mallkuar ata. Për ta ka dënim të vazhdueshëm.'),
(1304, 9, 69, '(Ju jini) si ata para jush! Ata kanë qenë më të fortë se ju, kanë pasur më shumë pasuri dhe fëmijë. Ata janë kënaqurme veprat e veta (të kësaj bote), andaj edhe ju jeni kënaqur me veprën tuaj, ashtu siç janë kënaqur me veprën e vet ata para jush; edhe ju jeni dhënë pas të këqiave, ashtu siç ishin dhënë edhe ata. Ata janë, veprat e të cilëve do t’u zhduken në këtë botë dhe në tjetrën; ata janë të shkatërruar.'),
(1305, 9, 70, 'A nuk u erdh lajmi për ata që ishin para tyre: për popullin e Nuhit, për popullin e Adit dhe Themudit, për popullin e Ibrahimit, për banorët e Medjenit dhe për ata që janë dënuar me shkatërrim. Pejgamberët e tyre – u sollën dokumente të qarta. Perëndia nuk iu bëri zullum, por ata vetvetes i bënë zullum.'),
(1306, 9, 71, 'Besimtarët dhe besimtaret janë miq për njëri-tjetrin: urdhërojnë që të bëhen vepra të mira, e pengojnë të këqiat, falin namazin, japin zeqatin, dhe i përulen Perëndisë dhe Pejgamberit të Tij. Këta janë ata që do t’i mëshirojë Perëndia. Se, Perëndia me të vërtetë, është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(1307, 9, 72, 'Perëndia u ka premtuar besimtarëve dhe besimtareve – kopshte të xhennetit, nëpër të cilat rrjedhin lumenj, në të cilat do të banojnë përherë. Dhe (u ka premtuar) pallate të mrekullueshme në xhennetin e Edenit. E, kënaqësia prej Perëndisë është shpërblim më i madh. Kjo është lumturi e madhe.'),
(1308, 9, 73, 'O Pejgamber! Lufto kundër mohuesve dhe hipokritëve, dhe bëhu i ashpër ndaj tyre! Strehimi i tyre është skëterra, e ky është vendbanim i shëmtuar.'),
(1309, 9, 74, 'Hipokritët betohen në Perëndinë se nuk kanë folur. Por, ata, me të vërtetë, kanë thënë fjalën e mohimit dhe u zbuluan se janë mohues, pasi e patën pranuar haptazi islamizmin dhe synuan gjëra të paarritshme. Ata i urrenin (muslimanët), vetëm për shkak se Perëndia dhe Pejgamberi i Tij i kishin bërë të pavarur me të mirat e Tij. E, nëse pendohen, do të jetë më mirë për ta. E, nëse e kthejnë shpinën, Perëndia do t’i ndëshkojë me dënim të rëndë në këtë jetë dhe në tjetrën, dhe ata nuk do të kenë në Tokë kurrfarë mbrojtësi as ndihmësi.'),
(1310, 9, 75, 'Ka nga ta që janë obliguar te Perëndia duke thënë: “Nëse Ai na jep nga dhuntitë e Tij, me të vërtetë do të japim lëmoshë dhe me të vërtetë, do të bëhemi njerëz të mirë!”'),
(1311, 9, 76, 'E, kur Ai u dha atyre nga dhuntitë e Veta, ata – me to u bënë koprracë dhe u kthyen – e ata edhe ashtu ishin të larguar (nga përulja Perëndisë).'),
(1312, 9, 77, 'Prandaj, Ai, ua përcolli (këtë sjellje) hipokrite në zemrat e tyre deri në ditën kur do të dalin para Tij, sepse, e shkelën premtimin dhenë Perëndisë. Ata e kishin zakon gënjeshtrën.'),
(1313, 9, 78, 'Vallë, a nuk e dinë ata, se Perëndia i di sekretet e tyre dhe bisedat e fshehta. Me të vërtetë, Perëndia është Ai, që i di të gjitha fshehtësitë.'),
(1314, 9, 79, 'Ata që i qortojën besimtarët të cilët japin lëmoshë me vullnet, edhe ata që nuk janë në gjendje, por megjithatë japin, (qoftë nga kafshata e vet), (hipokritët) tallen me ta. Por Perëndia tallet me hipokritët, dhe për ta ka dënim të dhëmbshëm.'),
(1315, 9, 80, 'U lute ti për ta ose nuk u lute, (njësoj është). Nëe ti do të kërkosh falje për ta edhe shtatëdhjetë herë, Perëndia nuk do t’i falë. Kjo është për shkak se ata e mohuan Perëndinë dhe Pejgamberin e Tij. E, Perëndia, nuk e udhëzon në rrugë të mbarë popullin ngatërrestarë.'),
(1316, 9, 81, 'U gësuan ata që mbetën në shtëpitë e tyre, kundër Profetit të Perëndisë – ishte e neveritshme për ta lufta në rrugën e Perëndisë, - me pasurinë dhe trupin e tyre, dhe ata thanë: “Mos shkoni në luftë, nëpër vapë!” Thuaju: “Zjarri i xhehennemit është edhe më i nxehtë!” (Sikur ta dinin ata!)'),
(1317, 9, 82, 'Do të qeshin pak ata, e do të qajnë shumë; ky do të jetë dënimi për atë që e kanë merituar.'),
(1318, 9, 83, 'Dhe nëse të kthen Perëndia (nga lufta në Medine) te një grup i hipokritëve, e, ata kërkojnë leje nga ti, që të shkojnë me ty në luftë, thuaju (o Muhammed): “Kurrë nuk do të shkoni me mua në luftë dhe kurrë me mua nuk do tëluftoni kundër armikut! Ju, ishit të kënaqur kur nuk erdhët herën e parë, prandaj rrini me ata që ngelin pas (me gratë dhe fëmijët).'),
(1319, 9, 84, 'Askujt prej hipokritëve kur të vdesin, mos ia fal xhenazën, as mos rri afër varrit të tij, sepse ata e kanë mohuar Perëndinë dhe Pejgamberin e Tij dhe kanë vdekur si ngatërrestarë.'),
(1320, 9, 85, 'Le të mos joshë (o Muhammed) pasuria e tyre dhe fëmijët e tyre! Se, Perëndia, don vetëm t’i ndëshkojë me to ata në këtë jetë, e të vdesin duke qenë mohues.'),
(1321, 9, 86, 'E, kur t’ju shpallet një sure (e t’ju thuhet atyre): “Besoni Perëndinë dhe luftoni së bashku me Pejgamberin e Tij!” pasanikët e tyre kërkuan leje dhe thanë: “Na lini të rrimë me ata që nuk shkojnë në luftë!”'),
(1322, 9, 87, 'Kënaqen të jenë me ata që nuk shkojnë në luftë, zemrat e tyre janë të vulosura e ata nuk kuptojnë!'),
(1323, 9, 88, 'Por, Pejgamberi dhe ata që besojnë në të, luftuan me pasurinë e tyre dhe trupin e tyre. Për ta ka çdo të mirë dhe ata janë të shpëtuar.'),
(1324, 9, 89, 'Perëndia u ka përgatitur këtyre kopshtet e xhennetit, nëpër të cilët rrjedhin lumenjtë, e në të cilët do të banojnë përgjithmonë. Kjo është fitore e madhe!'),
(1325, 9, 90, 'Erdhën disa beduinë duke u arsyetuar për të kërkuar leje që të mos shkojnë (në luftë). Dhe munguan ata që e gënjyen Perëndinë dhe Pejgamberin e Tij. E, prej atyre që mohuan, me të vërtetë, do t’i godet dënim i dhëmbshëm.'),
(1326, 9, 91, 'Nuk është mëkat mos të shkojnë në ushtri – të dobëtit (fizikisht), as të sëmurët, as ata që nuk mund të sigurojnë shpenzimet, nëse janë të sinqertë ndaj Perëndisë dhe Pejgamberit të Tij. S’ka arsye t’u bëhet asnjë vërejtje bamirësve. Se, Perëndia është falës dhe mëshirues,'),
(1327, 9, 92, '(e nuk ka mëkat) as për ata, të cilët vijnë te ti për të kërkuar kafshë shalimi, e ti u thua: “Unë nuk mund t’ju sigurojë kafshë shalimi”; andaj ngase nuk mundën të gjejnë shpenzime (për luftë), ata kthehen e sytë e tyre rrjedhin lotë nga dëshpërimi që nuk mundën të gjejnë mjete shpenzimi,'),
(1328, 9, 93, 'e ka arsye t’u bëhet vërejtje vetëm atyre, të cilët kërkojnë leje nga ti, duke qenë të pasur. Ata, kënaqen të shoqërohen me ata që nuk shkojnë në luftë. Perëndia, ua ka vulosur zemrat e tyre, por ata nuk dijnë.'),
(1329, 9, 94, 'Kur të ktheheni në mesin e tyre, ata do të arsyetohen te ju. Thuaju: “Mos u arsyetoni, sepse, na nuk ju besojmë, meqë Perëndia na ka lajmëruar për qëllimet e juaja. Me siguri, Perëndia dhe Pejgamberi i tij do t’i shohin veprat tuaja; pastaj ju do të ktheheni tek Ai, që i di gjërat e padukshme dhe të dukshme, e do t’u lajmëroj për atë që keni punuar.'),
(1330, 9, 95, 'Ata betohen në Perëndinë, kur të ktheheni ju te ata, që të shmangeni prej tyre; pra, shmanguni prej tyre! Ata janë të ndytë dhe strehimi i tyre është skëterra. Ky është dënimi për veprat e tyre.'),
(1331, 9, 96, 'Ata u betohen juve, që të jeni të kënaqur me ta. Nëse ju do të jeni të kënaqur me ta, Perëndia, me siguri, nuk është i kënaqur me popullin ngatërrestar.'),
(1332, 9, 97, 'Beduinët janë mohuesit dhe hipokritët më të mëdhenj (se tjerët) dhe është e kuptueshme, që nuk i njohin dispozitat që ia ka shpallur Perëndia – Pejgamberit të Tij. E, Perëndia, është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(1333, 9, 98, 'Ndër beduinët ka të atillë, që shpenzimin në rrugë të Perëndisë e konsiderojnë si humbje, e presin që t’u godet juve fatkeqësia. Le t’i godet ata vallja e fatkeqësisë! E, Perëndia, i dëgjon dhe i di të gjitha.'),
(1334, 9, 99, 'E ka nga beduinët, që besojnë Perëndinë dhe Ditën e Kijametit dhe atë që e kanë shpenzuar (në rrugën e Perëndisë), e marrin për afrim te Perëndia dhe meritimin e bekimit të Pejgamberit. E, me të vërtetë, ai shpenzim i tyre, është afrim i tyre (te Perëndia). Perëndia ata do t’i shpie në mëshirën e Vet. Se, Perëndia, me të vërtetë, është falës dhe mëshirues.'),
(1335, 9, 100, 'Perëndia është i kënaqur me muslimanët e parë, muhaxhirët dhe ensarët (ndihmëtarët e Pejgamberit) dhe të gjithë ata që i pasuan në të mira, por edhe ata janë të kënaqur me Te; Perëndia për ta ka përgatitur kopshte nëpër të cilat rrjedhin lumenj, ku ata do të jetojnë vazhdimisht dhe përgjithmonë. Kjo është fitore e madhe!'),
(1336, 9, 101, 'Ka beduin në mjedisin tuaj që janë hipokritë, por ka edhe ndër banorët e Medinës, që janë të shkathët në hipokrizi. Ti nuk i di ata. Na i dimë. Na do t’i dënojmë ata – dy herë e pastaj, do të kthehen në dënimin e madh.'),
(1337, 9, 102, 'Ndërkaq, disa të tjerë i pranojnë mëkatet e veta, sepse, i kanë përzier veprat e mira me të këqijat. Ka gjasë, që Perëndia, t’u pranojë pendimin e tyre. Me të vërtetë, Perëndia është falës dhe mëshirues.'),
(1338, 9, 103, 'Merr nga pasuria e tyre lëmoshë me të cilën do t’i pastrojë ata dhe me të cilën do t’u rrisish veprat e mira. Lutu për ta. Me të vërtetë, lutja juaj është qetësim për ta. Dhe, Perëndia, i dëgjon dhe i di të gjitha.'),
(1339, 9, 104, 'A nuk e dinë ata, se vetëm Perëndia pranon pendimin nga robërit e vet dhe pranon lëmoshë, dhe se vetëm Ai është pranues i pendimeve dhe mëshirues.'),
(1340, 9, 105, 'Dhe thuaj: “Punoni, Perëndia, Pejgamberi i Tij dhe besimtarët do të shohin punë tuaj. Ju do të ktheheni tek Ai, që i di gjërat e padukshme dhe të dukshme, e do t’u lajmërojë për atë që keni punuar.'),
(1341, 9, 106, 'E ka edhe të tjerë, të cilët shpresojnë urdhërin e Perëndisë, - që ose do t’i dënojë ata, ose do t’ua pranojë pendimin – Dhe Perëndia, është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(1342, 9, 107, '(Nga hipokritët janë) edhe ata që ndërtuan një xhami për t’i shkaktuar dëm, për ta forcuar mohimin, dhe për të krijuar përçarje në mesin e besimtarëve, duke iu shërbyer ajo si pusi, atyre që qysh më parë luftonin kundër Perëndisë dhe Pejgamberit të Tij. Dhe ata betoheshin: “Na dëshirojmë vetëm vepër të mirë”. E, Perëndia, dëshmon se ata, me të vërtetë, janë gënjeshtarë.'),
(1343, 9, 108, 'Ti në atë kurrë mos bënë namaz. E, xhamia – themelet e së cilës iu vunë që në ditën e parë, në të mirën e Perëndisë, me të vërtetë, meriton që të bësh namaz në te. Në te, janë njerëzit, të cilët e duan pastërtinë. E, Perëndia, i do ata që pastrohen shumë.'),
(1344, 9, 109, 'Cili është më i mirë, - ai që themelet e godinës së vet i vënë në rrugën e Perëndisë dhe kënaqësinë e Tij, apo ai, që, themelet e godinës së tij i vënë në bregun e gërryer, e rrokulliset me të në zjarrin e xhehennemit? – E, perëndia, nuk don ta udhëzojë në rrugë të drejtë popullin zullumqarë.'),
(1345, 9, 110, 'Godinën që ata e ndërtuan është vazhdimisht një dyshim në zemrat e tyre, përderisa të mos u copëtohen zemrat e tyre. Perëndia është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(1346, 9, 111, 'Perëndia, me të vërtetë, ka blerë nga besimtarët e vet trupin dhe pasurinë e tyre, për t’iu dhuruar xhennetin atyre. Ata luftojnë në rrugën e Perëndisë, vrasin dhe vriten. (Ky është) premtimi i tij i vërtetë në Teurat, Inxhil dhe Kur’an. E kush është më i plotë (më konsekuen) se Perëndia në premtim? Andaj, gëzojuni kësaj shitjeje të juaj, që e keni kontraktuar me Te, dhe kjo është fitore e madhe.'),
(1347, 9, 112, '(Këta jën ata) që pendohen (për mëkatet e tyre), që e adhurojnë (Perëndinë), dhe e falenderojnë Atë, dhe agjërojnë, dhe bëjnë ruqu dhe sexhde, (falin namazin), urdhërojnë me vepra të mira, e pengojnë prej të këqiave dhe të cilët ruajnë dispozitat e Perëndisë. E sihariqoj besimtarët!'),
(1348, 9, 113, 'Nuk është për Pejgamberin dhe besimtarët, që të kërkojnë falje për idhujtarët, madje, qofshin edhe të afërm të tyre, pasi që t’ju bëhet e qartë se ata janë banorë të xhehennemit.'),
(1349, 9, 114, 'E, kërkesa e Ibrahimit për faljen e babës së tij, ka qenë vetëm për shkak të premtimit që ia kishte dhënë ai (Ibrahimi) atij. Por, kur iu bë e qartë se ai (babai i tij), është armik i Perëndisë, u tërhoq prej tij. Me të vërtetë, Ibrahimi ishte lutës i madh dhe i butë.'),
(1350, 9, 115, 'Perëndia nuk e humbë një popull pasi ta ketë udhëzuar në rrugë të drejtë, përderisa të mos u tregojë se prej se duhet të ruhen. Me të vërtetë, Perëndia, është i dijshëm për çdo gjë.'),
(1351, 9, 116, 'Me të vërtetë, Perëndisë i takon pushteti i qiejve dhe i Tokës. Jeta dhe vdekja janë vepra të Tij. Dhe ju, përveç Perëndisë, nuk keni as mik as ndihmës.'),
(1352, 9, 117, 'Perëndia, me të vërtetë, pranoi pendimin e Pejgamberit, të muhaxhirëve dhe të ensarëve, të cilët shkaun pas atij në çastin e vështirë, pasi që zemrat e disa prej tyre, për një fije qenë afër shmangies, pastaj Perëndia pranoi pendimin e tyre. Se, Ai, me të vërtetë, është i butë dhe i mëshirshëm ndaj tyre.'),
(1353, 9, 118, '(Perëndia pranoi pendimin) edhe të atyre treve që patën munguar (në ushtri), dhe kjo, prej atëherë, kur toka – përkundër gjerësisë së saj, atyre iu dukë e ngushtë, dhe ishte shtrëngim për shpirtërat e tyre, dhe qenë të bindur se nuk ka strehim tjetër përveç Perëndisë, e pastaj Perëndia i përgatiti ata për pendim, që të pendohen. Me të vërtetë, Perëndia pranon pendimin dhe është mëshirues.'),
(1354, 9, 119, 'O besimtarë! Druajuni Perëndisë dhe bëhuni me ata që janë të drejtë!'),
(1355, 9, 120, 'Banorët e Medinës dhe beduinët që janë rreth saj, nuk është e drejtë të mungojnë (nga ushtria) pas Pejgamberit të Perëndisë, as të kursejnë jetën e tyre prej jetë së tij, sepse ata që janë në rrugën e Perëndisë – nuk do t’i godas as etja, as lodhja, as uria, e as që do të shkelin në ndonjë vend, që do t’i zemrojë mohuesit, as që do t’i gjejë gjë nga ana e armikut, e që ajo të mos iu shkruhet atyre si vepër e mirë. Me të vërtetë, Perëndia, bamirësve nuk do t’ua humbë shpërblimin.'),
(1356, 9, 121, 'dhe nuk do të japin kontribut as të vogël as të madh, as nuk do të kapërcejnë kurrfarë lugine e që ajo të mos u shënohet atyre. Le t’i shpërblen Perëndia, ata më mirë se që kanë punuar!'),
(1357, 9, 122, 'Nuk duhet të shkojnë (në ushtri) të gjithë besimtarët. E, pse të mos shkojë prej çdo grupi nga një njësi, që të mësohet në besim dhe kur të kthehet tek ata të këshillojë popullin e vet që t’i druajnë Perëndisë.'),
(1358, 9, 123, 'O besimtarë! Luftoni kundër mohuesve, që gjenden afër jush dhe le ta ndiejnë ata ashpërsinë tuaj. Dhe, dinie se Perëndia është me ata që ruhen nga mëkatet.'),
(1359, 9, 124, 'E, kur të shpallet ndonjë sure, disa prej tyre thonë: “Kujt prej jush kjo do t’ia shtojë besimn?” Sa i përket besimtarëve, atyre (kjo sure) ua shton besimin, dhe ata do të gëzohen,'),
(1360, 9, 125, 'e sa u përket atyre, zemrat e të cilëve janë të sëmura (me hipokrizi), atyre ua shton mohimin në mohimin që kanë dhe ata vdesin si mohues.'),
(1361, 9, 126, 'Vallë, a nuk shohin ata se çdo vit, një herë ose dy herë vihen në sprovë, e megjithatë, përsëri, as nuk pendohen as nuk këshillohen.'),
(1362, 9, 127, 'E, kur të shpallet ndonjë sure (që i qorton hipokritët), ata shikojnë njëri-tjetrin (dhe thonë): “A po ju sheh ndokush?” pastaj kthehen. Perëndia i shmangë zemrat e tyre, sepse ata janë popull që nuk kuptojnë!'),
(1363, 9, 128, 'Nga gjiu juaj u erdhi një pejgamber. Atij i vjen rëndë për mëkatet që bëni ju, por ai përpiqet që ju të shkoni rrugës së drejtë, dhe ndaj besimtarëve është i butë dhe mëshirues.'),
(1364, 9, 129, 'E, nëse ata zmbrapsen, thuaj: “Më mjafton mua Perëndia, s’ka zot tjetër përveç Tij. Unë mbështetem tek Ai, dhe Ai është Zot i gjithësisë madhështore (Arshit të Madh)!”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Elif Lâm Râ. Këto janë versetet e Librit plot dijeni.'),
(1366, 10, 2, 'Vallë, pse çuditën njerëzit që Na ia shpallëm njërit prej tyre (duke i thënë): “Paralajmëroji njerëzit dhe sihariqoji ata që besojnë; se, me të vërtetë, për ta ka shpërblim të mrekullueshëm te Zoti i tyre!” Mohuesit thanë: “Ky, me të vërtetë, është magjistar i madh!”'),
(1367, 10, 3, 'Perëndia, me të vërtetë, është Zoti juaj, i cili për gjashtë ditë (periudha të caktuara kohore) ka krijura qiejt dhe Tokën, u bë sundues i tërë gjithësisë, duke drejtuar çdo gjë. S’ka ndërmjetës tek Ai, përpos me lejen e Tij. Ky është Perëndia, Zoti juaj, andaj adhuronie Atë! A s’po mendoni ju?!'),
(1368, 10, 4, 'Tek Ai do të ktheheni të gjithë ju. Premtimi i Perëndisë është i vërtetë. Ai krijon prej fillimit (prej kurrgjëje), e pastaj e kthen atë në jetën e përhershme, që t’i shpërblejë me të drejtë – ata që besojnë dhe punojnë vepra të mira. E, për ata që nuk besojnë, ka pije nga uji i vluar dhe dënim të madh, për shkak se kanë mohuar.'),
(1369, 10, 5, 'Ai, Diellin e bëri burim të dritës, kurse Hënën e bëri të shkëlqejë, duke i caktuar fazat e saj, për të ditur numrin e viteve dhe njehsimin e kohës). Vetëm Perëndia i krijoi këto në mënyrë të mrekullueshme. Ai ua shpjegon argumentet njerëzve që kuptojnë.'),
(1370, 10, 6, 'Me të vërtetë, në ndërrimin e natës dhe të ditës dhe në atë që ka krijuar Perëndia në qiej dhe në Tokë, janë argumentet për ata që besojnë.'),
(1371, 10, 7, 'Me të vërtetë, ata që nuk shpresojnë se do të takohen me Ne, dhe janë të kënaqur me jetën e kësaj bote, dhe janë qetësuar në të (botë), dhe të cilët janë të pakujdesshëm ndaj argumenteve Tona –'),
(1372, 10, 8, 'për ta, vendstrehim është zjarri, për atë që kanë punuar.'),
(1373, 10, 9, 'Me të vërtetë, ata që besojnë dhe punojnë vepra të mira, Zoti i tyre i udhëzon në rrugë të drejtë, ngase besojnë; e rrjedhin lumenjtë pranë tyre në kopshtet e kënaqësisë,'),
(1374, 10, 10, 'lutja e tyre në to (kopshtije) është: “Qofsh i lavdëruar o Zot!” e përshëndetja e tyre në të: “Selam” e lutja e tyre e fundit është: “Falenderojmë Perëndinë, Zotin e gjithësisë!”'),
(1375, 10, 11, 'Sikur Perëndia, t’ua shpejtonte të keqen njerëzve, ashtu siç e dëshirojnë ata shpejt të mirën, ata me të vërtetë, do të zhdukeshin. E, Ne, megjithatë, ata që nuk shpresojnë takimin Tonë – i lëmë që të bredhin andej e këndej në humbjet e tyre.'),
(1376, 10, 12, 'E, kur njeriun ta gjejë e keqja, atëherë Na lutet: shtrirë, ose ulur, ose në këmbë (në çdo mënyrë). E, posa Na t’ia largojmë të keqen atij, vazhdon – thuajse nuk Na është lutur për fatkeqësinë që e ka goditur. Kështu plangësprishësve (batakqinjëve) iu zbukurohet ajo që punojnë.'),
(1377, 10, 13, 'Na i kemi zhdukur madje – shumë popuj para jush, përse kanë bërë zullum. Dhe pejgamberët e tyre, u kanë sjellë atyre dokumente të qarta, por ata nuk kanë besuar. Kështu Na i dënojmë njerëzit e këqinj.'),
(1378, 10, 14, 'Pastaj u kemi bërë juve pas tyre – zëvendës në Tokë, për të parë se si do të veproni.'),
(1379, 10, 15, 'E, kur t’ju lexohen ajetet Tona, të qarta, atëherë thonë ata, që nuk shpresojnë se do të dalin para Nesh: “Na sjell ti ndonjë Kur’an tjetër ose ndërroje atë!” Thuaj (o Muhammed): “Është e pamundur ta ndërroj unë atë – nga ana ime. Unë shkoj vetëm pas asaj që më është shpallë. Unë druaj – nëse i kundërshtoj Zotit tim – dënimit të Ditës së Madhe”.'),
(1380, 10, 16, 'Thuaj: “Sikur të kishte dashur Perëndia, unë nuk do t’jua mësoja dhe as që do të bënte Ai që ta mësoni ju atë. Unë kam jetuar një kohë (të gjatë) në mesin tuaj – para Pejgamberllëkut, a nuk po mendoni?”'),
(1381, 10, 17, 'Kush është zullumqar më i madh se ai që trillon gënjeshtra për Perëndinë ose që i përgënjeshtron ajetet e Tij?! Me të vërtetë, mëkatarët nuk kanë shpëtim.'),
(1382, 10, 18, 'Ata adhurojnë – përveç Perëndisë – atë që as nuk u bënë dëm, as nuk u sjell dobi dhe thonë: “Këta janë ndërmjetësit tonë te Perëndia! Thuaj: “A mundeni ju t’i tregoni Perëndisë se në qiej dhe në Tokë ekziston diçka që nuk e di Ai?!” Qoftë lavdëruar Ai dhe lartë qoftë nga ajo që i bëjnë shok Atij!'),
(1383, 10, 19, 'Të gjithë njerëzit kanë qenë vetëm një popull (të një feje), e pastaj u kundërshtuan (u përçanë). E, sikur të mos ishte thënë më parë fjala nga Zoti yt (për shtyerjen e dënimit), atëherë do të gjykoheshin në mes tyre, për atë që nuk u pajtuan.'),
(1384, 10, 20, 'Dhe ata thonë: “Sikur t’i ishte zbritur atij ndonjë mrekulli prej Zotit të tij?” Thuaj: “Me të vërtetë, të padukshmet i di vetëm Perëndia. E, pritni ju, se edhe unë po pres me ju”.'),
(1385, 10, 21, 'Kur Na, pas të keqes që i goditi, u dhamë njerëzve të shijojnë mëshirën, ata përsëri bënë intriga me argumentet Tona! Thuaj: “Perëndia është më i shpejtë në dënimin e ngatërrestarëve”. Me të vërtetë, të dërguarit (engjëjt) Tonë shënojnë intrigat tuaja.'),
(1386, 10, 22, 'Ai ua mundëson të udhëtoni nëpër tokë dhe det. E, kur ju të gjendeni në barkë (anije) dhe ajo lundron me ju me erën e përshtatshme, e ata i gëzohen kësaj, arrin një stuhi dhe ata do t’i sulmojnë valët nga të gjitha anët e mendojnë ata që do të shkatërrohen, (atëherë) i luteni Perëndisë sinqerisht, duke i premtuar besimin: “Nëse na shpëton nga kjo, na, me të vërtetë, do të jemi falenderues”.'),
(1387, 10, 23, 'E kur Perëndia i shpëtoi ata; ata përnjëherë pa kurrfarë të drejte bënë çrregullime në Tokë. O njerëz! Me të vërtetë, padrejtësia që bëni për t’u kënaqur në jetën e kësaj bote, vetëm juve ju dëmton. Ju do të ktheheni te Na dhe Ne pastaj do t’ju njoftojmë për atë që keni punuar.'),
(1388, 10, 24, 'Me të vërtetë, jeta e kësaj bote i shembëllen shiut që Na e lëshojmë nga qielli, me të përzihen bimët e tokës, me të cilat ushqehen njerëzit dhe shtazët. E, kur Toka të vishet dhe zbukurohet, dhe kur banorët e saj mendojnë se ata janë zotërues të saj, (atëherë) u vie urdhëri Ynë, natën ose ditën, dhe Na e bëjmë atë si të ketë qenë e korrur, thuajse nuk kishte ekzistuar dje. Kështu, Na ua shpjegojmë argumentet, njerëzve që mendojnë.'),
(1389, 10, 25, 'Perëndia i thërret njerëzit në Shtëpinë e shpëtimit (xhennetit) dhe Ai e udhëzon kë të dojë në rrugën e drejtë.'),
(1390, 10, 26, 'Ata që bëjnë mirë, do të kenë shpërblim të mirë, e edhe më tepër! Fytyrat e tyre nuk do t’i mbulojë errësira as poshtërimi. Ata do të jenë banorë të xhennetit, në të cilin do të banojnë përherë.'),
(1391, 10, 27, 'E ata, që kanë bërë vepra të këqia, kanë dënim gjegjës për atë të keqe dhe ata do t’i mbulojë poshtërimi. Ata nuk do të kenë askend që t’i mbrojnë nga dënimi i Perëndisë. Fytyrat e tyre thuajse iu kanë mbuluar me hije të errësirës së natës. Këta do të jenë banorët e zjarrit dhe në to do të qëndrojnë përgjithmonë.'),
(1392, 10, 28, '(Kujtoje) kur do t’i tubojmë të gjithë ata që i kanë bërë shok Perëndisë dhe t’u themi: “Rrini në vendet tuaja ju dhe idhujtë tuaj!” E, do t’i ndajmë ata. Dhe idhujtë e tyre do të thonë: “Ju nuk na keni adhuruar neve.'),
(1393, 10, 29, 'Mjafton Perëndia të jetë dëshmitar në mesh nesh dhe jush. Na, me të vërtetë, kemi qenë të pakujdesshëm ndaj adhurimit tuaj”.'),
(1394, 10, 30, 'Aty (atë ditë), çdokush do të paguhet për atë që ka punuar më parë dhe do t’i kthehen Perëndisë, Zotit të tyre të vërtetë, e do të zhduken ato që i kanë trilluar ata (idhujtë).'),
(1395, 10, 31, 'Thuaj: “Kush ju ushqen juve nga qielli dhe Toka? Ose, kush mund të krijojë të dëgjuarit dhe të pamurit? Dhe kush mund të nxjerrë të gjallin nga i vdekuri dhe të vdekurin nga i gjalli? Dhe kush i drejton gjërat? Ata do të thonë: “Perëndia”. E ti (o Muhammed!) thuaj: “E, përse atëherë nuk i ruheni atij?”'),
(1396, 10, 32, 'Ky është Perëndia, Zoti juaj i vërtetë. E, çka keni pas së vërtetës, përpos humbjes? E, ku shkoni ju atëherë?'),
(1397, 10, 33, 'Kështu u sendërtua (realizua) fjala e Zotit tënd, te ata që bënë çrregullime; se, me të vërtetë, ata nuk do të besojnë.'),
(1398, 10, 34, 'Thuaj: “A ka ndër shokët tuaj (idhujtarë që i adhuroni ju) ndonjë që mund të krijojë prej kurrgjëje, e pastaj (pas vdekjes) përsëri ta kthejë (në jetë)?” Thuaj: “Perëndia krijon prej kurrgjëje, e pastaj e përsërit atë (në jetën e amshueshme)!” E, si shmangeni ju (nga besimi në Perëndinë)?'),
(1399, 10, 35, 'Thuaj: “A ka ndër shokët tuaj (idhujtarë që i adhuroni ju) ndonjë që mund të udhëzojë në rrugë të drejtë?” Thuaj: “Vetëm Perëndia udhëzon në rrugë të drejtë! E pra, a është më e merituar të shkosh pas atij që udhëzon në rrugë të drejtë, apo ai, i cili as vetën nuk është në rrugën e drejtë, përveç, nëse e shpie dikush atë? E, çka keni. Si po gjykoni ju ashtu?!”'),
(1400, 10, 36, 'Shumica veprojnë vetëm sipas hamendjes. Me të vërtetë hamendja nuk e plotëson të vërtetën. Perëndia, pa dyshim di mirë atë që punojnë ata.'),
(1401, 10, 37, 'Kyr Kur’an nuk është i trilluar prej askujt, por vetëm është shpallur prej Perëndisë, për të vërtetuar shpalljet e mëparshme, dhe të shpjegojë dispozitat; në të s’ka dyshim (ka zbritur) nga Zoti i gjithësisë!'),
(1402, 10, 38, 'Ose ata thonë: “Ai (Muhammedi) e ka trilluar atë (Kur’anin)”. Thuaj: “E pra, silleni ju një sur të ngjashme dhe thirrni (në ndihmë) kë të doni, përpos Perëndisë, nëse jeni të drejtë”.'),
(1403, 10, 39, 'Ata e mohojnë atë (Kur’anin) pa e kuptuar me themel çka ka në të e pa i ardhur akoma as shpjegimi i tij; po kështu, kanë mohuar edhe ata para tyre; e shiko, si kanë përfunduar zullumqarët!'),
(1404, 10, 40, 'Disa prej tyre besojnë në të, e disa nuk besojnë në të (Kur’an). E, Zoti yt më së miri i njeh ngatërrestarët.'),
(1405, 10, 41, 'Nëse ata të përgënjeshtrojnë ty, thuaju: “Për mua janë veprat e mia, e për ju – veprat tuaja, ju jeni larg prej veprave që punoj unë, e unë jam larg prej veprave që punoni ju.'),
(1406, 10, 42, 'Ka prej tyre, që të përgjojnë. Por, a mund të bësh ti, që të dëgjojnë të shurdhëtit, të cilët skanë as mend?'),
(1407, 10, 43, 'Ka prej tyre, që të shikojnë ty. E, mos vallë ti do t’i udhëzosh në rrugë të drejtë të verbëtit, të cilët nuk shohin hollësisht (me mend)?'),
(1408, 10, 44, 'Me të vërtetë, Perëndia nuk u bënë kurrfarë të keqe njerëzve, por njerëzit i bëjnë keq vetvetes.'),
(1409, 10, 45, 'E, atë ditë kur do t’i tubojmë Na ata, do t’u duket atyre se vetëm një moment të shkurtër të ditës kanë bredhur në Tokë, aq sa për ta njohur njëri-tjetrin. Madje, janë të humbur, ata që kanë përgënjeshtruar takimin me Perëndinë, e nuk ishin në rrugë të drejtë.'),
(1410, 10, 46, 'Qoftë që të tregojmë Na ty, diçka nga ajo që u kemi premtuar atyre ose ta marrim shpirtin Na ty, te Na do të kthehen ata, e Perëndia është dëshmitar për që punojnë ata.'),
(1411, 10, 47, 'Çdo popull ka pasur pejgamber. Dhe kur atyre t’u vie pejgamberi, do të gjykohet mes tyre me të drejtë, dhe nuk do t’u bëhet zullum.'),
(1412, 10, 48, 'Ata thonë: “Kur do të plotësohet ky premtim, nëse thoni të vërtetën?”'),
(1413, 10, 49, 'Thuaj: “Unë nuk jam në gjendje t’i bëj vetes as dëm as dobi, përveç asaj që dëshiron Perëndia. Çdo popull ka afatin e vet; kur t’i vijë ai, nuk mund ta shtyejnë (ata), për asnjë çast, e as ta shpejtojnë.'),
(1414, 10, 50, 'Thuaj: “Çka mendoni ju, nëse ju vie dënimi i Tij natën apo ditën, pse e nxitojnë atë (dënimin) mëkatarët?”'),
(1415, 10, 51, 'A mos vallë, vetëm atëherë kur të ndodhë – besoni në të? A, tash po besoni, e madje më parë e nxitonit dënimin?!'),
(1416, 10, 52, 'E pastaj, u thuhet zullumqarëve: “Shijoni dënimin e përhershëm! Dhe, nuk dënoheni ndryshe, përveç ashtu si keni punuar”.'),
(1417, 10, 53, 'Ata kërkojnë shpjegim prej teje: “A është e vërtetë ajo?” Thuaj: “Po pasha Zotin tim, ajo padyshim është e vërtetë, e ju nuk do të mund t’i shmangeni!”'),
(1418, 10, 54, 'Kur çdonjëri që ka bërë zullum, sikur të kishte ai tërë pasurinë e botës, ai do të jepte krejt atë, për të shpëtuar (nga dënimi). Ata do të fshehin pendimin kur të shohin dënimin, e do të gjykohen mes tyre me të drejtë dhe nuk do t’u bëhet padrejtësi.'),
(1419, 10, 55, 'A! me të vërtetë, të Perëndisë janë të gjitha që gjenden në qiej dhe në Tokë. A! me të vërtetë, premtimi i Perëndisë është i vërtetë, por shumica e njerëzve nuk e dijnë.'),
(1420, 10, 56, 'Ai jep gjallëri dhe vdekje dhe tek Ai do të ktheheni.'),
(1421, 10, 57, 'O njerëz, ju ka ardhur madje këshilla (Kur’ani) nga Zoti juaj, ilaç për zemrat tuaja, udhëzim dhe mëshirë për besimtarët.'),
(1422, 10, 58, 'Thuaj: “Me dhuntinë e Perëndisë dhe mëshirën e Tij – vetëm me këto le të gëzohen! Kjo është më mirë se ajo që grumbullojnë ata”.'),
(1423, 10, 59, 'Thuaj: “Çka mendoni ju për ushqimin, që ua ka zbritur Perëndia, e ju prej tij keni bërë diçka të ndaluar e diçka të lejuar?” Thuaj: “A ju ka lejuar Perëndia atë, apo po ia shpifni Perëndisë?”'),
(1424, 10, 60, 'Çka mendojnë ata që shpifin gënjeshtra për Perëndinë, - në Ditën e Kijametit? Me të vërtetë, Perëndia është bamirës i madh i njerëzve, por, pjesa dërmuese nuk e falenderojnë Ate.'),
(1425, 10, 61, 'Ti nuk do të bësh asgjë, as nuk do të lexosh asgjë nga Kur’ani, as nuk do të punoni kurrfarë pune, e që Ne mos të jemi dëshmitar, kur ju të thelloheni në të. Nuk do të humbë nga Zoti yt as sa një thërmi peshe, në Tokë as në qiell, as më pak nga ajo as më shumë, e që të mos jetë e shënuar në Librin e qartë.'),
(1426, 10, 62, 'A! Me të vëtetë, pë të dashurit e Perëndisë nuk ka frikë as pikëllim,'),
(1427, 10, 63, 'për ata që besojnë dhe që i druajnë Perëndisë,'),
(1428, 10, 64, 'për ta ka sihariq në këtë jetë dhe në tjetrën. S’ka ndryshim në fjalët e Perëndisë. Kjo është fitore e madhe.'),
(1429, 10, 65, 'Mos të pikëlloj ty fjala e tyre. E, gjithë fuqia i përket Perëndisë! Ai dëgjon dhe di çdo gjë.'),
(1430, 10, 66, 'A! Me të vërtetë, Perëndisë i përket pushteti i çdo gjëje që gjendet në qiej dhe në Tokë. E, ata që, pos Perëndisë, adhurojnë idhujtë, shkojnë vetëm pas hamendjeve dhe vetëm gënjejnë.'),
(1431, 10, 67, 'Ai (Perëndia) ua krijoi natën për të pushuar në të, kurse ditën për të parë. Me të vërtetë, në këtë ka argumente për njerëzit që dëgjojnë.'),
(1432, 10, 68, 'Kanë thënë: “Perëndia ka djalë”. I lavdëruar është Ai. Ai është i pastër, s’ka nevojë për asgjë. Të Atij janë të gjitha – ç’gjendet në qiej dhe në Tokë. Ju nuk keni asnjë argument për këtë. A thoni ju për Perëndinë, atë që nuk e dini!'),
(1433, 10, 69, 'Thuaj: “Me të vërtetë, ata që shpifin gënjeshtra për Perëndinë nuk do të shpëtojnë”.'),
(1434, 10, 70, '(Do të keni) kënaqësi të shkurtër në këtë botë, e pastaj do të kthehen te Ne, dhe Ne, do t’u japim të shijojnë dënimin e ashpër për shkak se kanë mohuar.'),
(1435, 10, 71, 'Tregoju historinë e Nuhut! Kur ai popullit të vet i tha: “O populli im, nëse u është i rëndë qëndrimi im në mesin tuaj, dhe këshillat e mia me dokumente të Perëndisë, e unë jam mbështetur vetëm te Perëndia, - atëherë, bashkë me shokët (idhujt) tuaj, vendosni për mua, dhe vendimi juaj le të mos fshihet, e pra, zbatoni në mua; e mos pritni!'),
(1436, 10, 72, 'E, nëse ktheheni (nuk i dëgjoni këshillat), unë prej jush nuk kërkoj kurrfarë shpërblimi. Shpërblimi im është vetëm te Perëndia. Mua më është urdhëruar të jem në anën e muslimanëve”.'),
(1437, 10, 73, 'Por, ata, e quajtën gënjeshtar, kurse Ne e shpëtuam atë dhe të gjithë ata që ishin në anije me të. Dhe i bëmë ata zëvendës (të atyre që u zhdukën) kurse, i fundosëm ata që përgënjeshtruan dokumentet Tona. Shikoje se si ishte përfundimi i atyre që ishin paralajmëruar.'),
(1438, 10, 74, 'Pastaj, pas tij – u dërguam pejgamberë popujve të tyre e ata u sollën argumente atyre, por nuk deshën të besojnë atë që e kishin përgënjeshtruar më parë. Kështu, Ne, ua vulosim zemrat atyre, që kalojnë kufirin.'),
(1439, 10, 75, 'Pastaj, pas tyre, iu dërguam Musain dhe Harunin – Faraonit dhe parisë së tij – me mrekullitë Tona, por ata u sollën me mendjemadhësi (nuk besuan), meqë ishin popull mëkatarë:'),
(1440, 10, 76, 'kur u erdhi e Vërteta prej Nesh, ata thanë: “Kjo, me të vërtetë, është magji e qartë!”'),
(1441, 10, 77, 'Musai tha: “A mos vallë, për të Vërtetën thuani (se është magji), pasi që ju ka ardhur juve? A kjo është, magji? E, magjistarët nuk shpëtojnë!”'),
(1442, 10, 78, 'E, ata thanë: “A mos ke ardhur ti të na kthesh neve nga ajo, në të cilën i kemi gjetur etërit tonë, e ju të dyve t’u takojë pushteti në Tokë? E, nuk duam t’u besojmë ju dyve!”'),
(1443, 10, 79, 'Faraoni tha: “M’i sillni të gjithë magjistarët e shkathët!”'),
(1444, 10, 80, 'Pasi erdhën magjistarët, Musai u tha: “Hedhni ç’keni për të hedhur!”'),
(1445, 10, 81, 'E, kur i hodhën ata, Musai tha: “Atë që e keni sjellë ju, është magji. Me të vërtetë, Perëndia do ta shkatërrojë atë. Perëndia, me siguri, nuk do ta forcojë punën e ngatërrestarëve.'),
(1446, 10, 82, 'Perëndia, me premtimin e Tij e forcon të Vërtetën, e madje edhe sikur ta urrejnë mëkatarët!”'),
(1447, 10, 83, 'Dhe Musait nuk i besoi askush, përveç një pakice nga populli i tij, nga frika se do t’i keqpërdorte ata Faraoni dhe paria e tij, e Faraoni – me të vërtetë, ishte zullumqar në Tokë dhe në të keqe kalonte çdo kufi.'),
(1448, 10, 84, 'Musai tha: “O populli im! Nëse besoni Perëndinë, mbështetuni tek Ai, nëse jeni musliman!”'),
(1449, 10, 85, 'Ata thanë: “Na ju kemi mbështetur Perëndisë. O Zoti ynë, mos na vënë në sprovë të popullit zullumqarë,'),
(1450, 10, 86, 'dhe shpëtona neve, me mëshirën Tënde, nga populli mohues!”'),
(1451, 10, 87, 'Na ia kemi shpallur Musait dhe vëllait të tij: “Ndërtoni shtëpi për popullin tuaj në Egjipt dhe shndërroni ato në faltore dhe falni namazin! Dhe sihariqoi besimtarët!”'),
(1452, 10, 88, 'Musai tha: “O Zoti ynë! Ti i ke dhënë Faraonit dhe parisë së tij stoli dhe pasuri në jetën e kësaj bote, por ata, o Zoti ynë, na largojnë nga rruga Jote! O Zoti ynë! Shkatërroje pasurinë e tyre dhe vulosi zemrat e tyre, se ata nuk besojnë, përderisa të shijojnë dënimin e dhëmbshëm!”'),
(1453, 10, 89, '(Perëndia) tha: “U pranua lutja e ju të dyve! Qëndroni ju dy dhe kurrsesi mos ndiqni rrugën e të padijshmëve!”'),
(1454, 10, 90, 'Na i bartëm Israelitët përtej deit, e ata i ndoqi Faraoni dhe ushtria e tij nga e keqja dhe armiqësia. E kur e arriti ate (Faraonin) mbytja (në det), tha: “Besoj se s’ka zot tjetër përveç Atij që i besojnë të bijtë e Israelit dhe unë jam musliman”.'),
(1455, 10, 91, '“A, tani (po beson) e më parë ishe rrebel dhe ishe prej ngatërrestarëve?!'),
(1456, 10, 92, 'Sot do të shpëtojmë ty – trupin tënd, që të bëhesh vërejtje për ata që vijnë pas teje”. Por, me të vërtetë, shumica e njerëzve janë të pakujdesshëm ndaj argumenteve Tona.'),
(1457, 10, 93, 'Na i kemi vendosur në vend të mirë të bijtë e Israelit dhe i kemi furnizuar me ushqim të këndshëm dhe ata, nuk u përçanë në mendime, derisa u erdhi ajo që e dinin ata (profetnia e Muhammedit). Me të vërtetë, zoti yt do t’i gjykojë ata në Ditën e Kijametit, për atë që u përçanë ata.'),
(1458, 10, 94, 'Nëse ti je në dyshim në atë që të kemi shpallur ty, pyeti ata që kanë lexuar Librin para teje. Padyshim, ty të ka ardhur e Vërteta nga Zoti yt, e mos u bën kurrsesi nga ata që dyshojnë.'),
(1459, 10, 95, 'Dhe mos u bën nga ata që i përgënjeshtrojnë dokumentet Tona, e të bëhesh ndër ata që janë të humbur.'),
(1460, 10, 96, 'Me të vërtetë, ata në të cilët ka ra fjala e Zotit tënd (dënimi), nuk besojnë,'),
(1461, 10, 97, 'madje sikur t’u vijnë të gjitha dokumentet, përderisa mos ta ndiejnë dënimin e dhëmbshëm.'),
(1462, 10, 98, 'E, sikur të ishte ndonjë vendbanim që beson, e që t’i bëjë dobi besimi i tij (s’ka pasur dobi), përveç popullit të Junusit, meqë ata besuan (në momentin e dënimit), e Na e larguam nga ata dënimin e poshtërimit në jetën e kësaj bote, dhe u dhamë të kënaqen edhe njëfarë kohe.'),
(1463, 10, 99, 'Sikur të donte Zoti yt, do të besonin të gjitha ata – që gjenden në tokë. E, përse atëherë ti t’i detyrosh njerëzit të bëhen besimtarë!?'),
(1464, 10, 100, 'Askujt nuk i është dhënë të besojë, përveç me lejen e Perëndisë. Ai dënon ata që s’mendojnë.'),
(1465, 10, 101, 'Thuaj: “Shikoni çka ka në qiej dhe në Tokë!” – Por, nuk i bëjnë kurrfarë dobie argumentet dhe vërejtjet – popullit që nuk beson.'),
(1466, 10, 102, 'Vallë, a presin ata tjetër gjë, përpos (dënimeve) të ngjashme si të atyre që kanë përjetuar më parë. Thuaj: “Pritni ju (dënimin) se edhe unë po pres me ju!”'),
(1467, 10, 103, 'Pastaj, Na do t’i shpëtojmë pejgamberët Tanë dhe ata që kanë besuar. Kjo është detyra (e premtuar) e Jona, që t’i shpëtojmë besimtarët.'),
(1468, 10, 104, 'Thuaj: “O njerëz, nëse dyshoni ju në fenë time, - e unë, përveç Perëndisë, nuk do të adhurojë atë që e adhuroni ju, por do të adhuroj Perëndinë, i cili ua merr shpirtin juve; e mua më është urdhëruar të bëhem besimtar'),
(1469, 10, 105, '(dhe më është urdhëruar): “Ktheju ti fesë së vërtetë dhe kurrsesi mos u bën nga idhujtarët,'),
(1470, 10, 106, 'dhe, përveç Perëndisë, mos iu lut tjetër kujt, të cilët nuk të sjellin as dobi as dëm, e nëse e bën këtë, ti atëherë, me të vërtetë, je nga zullumqarët”.'),
(1471, 10, 107, 'Nëse Perëndia të sjellë ndonjë dëm, askush nuk mund të ta largojë, përveç Tij, e nëse Ai don të ta bëjë ndonjë të mirë, s’ka kush të ta pengojë mirësinë e Tij. Ai ia jep atë mirësi kujt të dojë nga robërit e Tij; dhe Ai është falës dhe mëshirues.'),
(1472, 10, 108, 'Thuaj: “O njerëz, ju erdhi juve e Vërteta nga Zoti juaj. Kush udhëzohet në rrugën e drejtë, ai është udhëzuar për dobi të vet, e kush është humbur, ka humbur në dëm të vet. E, unë nuk jam mbikëqyrësi juaj”.'),
(1473, 10, 109, 'Paso ti atë që të është shpallë ty dhe duro derisa të gjykojë Perëndia, se, Ai, është gjykatësi mëi mirë!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'Elif Lâm Râ. Ky është Libri, ajetet e të cilit janë radhitur mrekullueshëm dhe janë shpjeguar nga ana e Gjithdijshmit dhe Plotëdijshmit.'),
(1475, 11, 2, 'Adhuronie vetëm Perëndinë, unë jam për ju nga Ai, paralajmërues dhe sihariques, -'),
(1476, 11, 3, 'dhe kërkoni falje nga Zoti juaj e kthehuni tek Ai. Ai do t’ju japë të shijoni kënaqësi të bukura deri në afatin e caktuar dhe do t’i japë çdo të miri shpërblimin e merituar. E, nëse shmangeni, unë i druaj dënimit (që do t’ju gjejë) në Ditën e Madhe.'),
(1477, 11, 4, 'Te Perëndia do të ktheheni, e Ai është i Plotëfuqishëm për çdo gjë!'),
(1478, 11, 5, 'Ah! me të vërtetë, ata mbulojnë krahërorin e tyre, që të fshihen nga Ai. Ah! por edhe kur të mbulohen në petkat e tyre, Ai di se çka fshehin ata dhe çka shfaqin, - Ai, me të vërtetë, është i Gjithëdijshëm se ç’ka në zemra (të çdokujt).'),
(1479, 11, 6, 'S’ka asnjë gjallesë në Tokë, që Perëndia të mos ia ketë caktuar furnizimin dhe Ai di vendbanimin e tij dhe vendin e vdekjes. Të gjitha keto janë (të shënuara) në Librin e qartë.'),
(1480, 11, 7, 'Ai është që ka krijuar qiejt dhe Tokën për gjashtë ditë (periudha të caktura kohore), e Arshi (pushteti) i Tij është mbi ujin – për t’ju provuar kush është më i mirë në vepër. Nëse ti thua: “Ju do të ringjalleni pas vdekjes”, mohuesit, me siguri do të thonin: “Kjo nuk është asgjë tjetër, përpos magji e kulluar!”'),
(1481, 11, 8, 'Nëse Ne ua shtyjmë dënimin atyre për një kohë të shkurtër, ata me siguri do të thoshin: “Kush po e pengon?” Ah! E, atë ditë që t’ju vie (dënimi), nuk do të zmbrapset nga ata dhe do t’i përfshijë ajo, që e përqeshnin ata.'),
(1482, 11, 9, 'Kur Na i japim njeriut mirësinë Tonë, a pastaj ia zhveshim atë prej tij, ai atëherë bëhet shpresëhumbur dhe mohues.'),
(1483, 11, 10, 'E kur pas fatkeqësisë që e ka goditur atë, Na t’i japim atij dhunti, ai me siguri do të thotë: “Mëkatet e mia janë larguar nga unë!” Me të vërtetë, ai është (njeri tepër) i gëzueshëm dhe mendjemadh,'),
(1484, 11, 11, 'e vetëm për ata që durojnë dhe kanë punuar vepra të mira, për ta ka falje dhe shpërblim të madh.'),
(1485, 11, 12, 'Mund të ndodh që ti të lësh (mos të tregosh në çdo vend) diçka nga ato që të janë shpallur ty dhe thonë: “Sikur atij t’iu kishte dërguar ndonjë thesar”, ose, “të kishte ardhur një engjëll me te?” Ti je vetëm paralajmërues, e Perëndia është mbikëqyrës për çdo gjë.'),
(1486, 11, 13, 'Apo do të thonë ata: “E ka trilluar atë (Kur’anin) ai!” Thuaj (o Muhammed!): “Sillni dhjetë sure të ngjashme të trilluara dhe thirrni kë të mundeni, përpos Perëndisë, nëse thuani të vërtetën!”'),
(1487, 11, 14, 'E, nëse nuk ju përgjigjën (ftesës) juve, dijeni se ai (Kur’ani) ka zbritur me dijen e Perëndisë dhe s’ka zot tjetër përveç Tij. Andaj, a jeni ju muslimanë? (meqë i patë këto argumente).'),
(1488, 11, 15, 'Ata që e duan (vetëm) jetën e kësaj bote dhe bukuritë e saj, Na do t’ua plotësojmë krejtësisht punët e tyre në të (botë) dhe atyre në të (këtë botë) nuk do t’u mungohet asgjë.'),
(1489, 11, 16, 'Këta janë ata, që në botën tjetër nuk do të kenë kurrgjë, përveç zjarrit. Do t’u shëmbet çdo gjë që kanë punuar në të (këtë botë) dhe do t’u humbet ajo që kanë punuar.'),
(1490, 11, 17, 'A mos vallë, ata që duan vetëm jetën e kësaj bote, janë të barabartë me ata që mbështeten në dokumentet e qarta të Zotit të tyre; këtë e pason dëshmitari nga Perëndia (Kur’ani), dhe qysh para tij (Kur’anit) Libri i Musait, i cili ka qenë udhërrëfyes dhe mëshirë nga Perëndia? Këta (që mbështetën në këto dokumente) besojnë Kur’anin. E, cilido nga grupet që e mohojnë atë, vend i premtuar i tij është zjarri. Andaj, ti kurrsesi, mos dysho në të; se, ai padyshim, është e vërteta prej Zotit tënd, por shumica e njerëzve nuk besojnë.'),
(1491, 11, 18, 'Kush është zullumqarë më i madh se ai që trillon gënjeshtra për Perëndinë? Këta do të sillen para Zotit të tyre, e dëshmitarët do të thonë: “Këta janë ata që kanë gënjyer për Zotin e tyre. E, mallkimi i Perëndisë qoftë mbi zullumqarët,'),
(1492, 11, 19, 'të cilët pengojnë (të tjerët) nga rruga e Perëndisë dhe përpiqen ta shtrembërojnë atë, e ata nuk besojnë (as) në botën tjetër”.'),
(1493, 11, 20, 'Të tillët nuk do të mund t’i ikin Perëndisë (nga dënimi) në Tokë, e këta as që kanë ndonjë mbrojtës, përveç Perëndisë. Atyre do t’u shumëfishohet dënimi, meqë nuk dëshironin as të dëgjojnë, as të shihnin,'),
(1494, 11, 21, 'këta janë ata që e kanë dëmtuar vetveten, dhe u zhdukën nga këta, - ato që i trillonin, -'),
(1495, 11, 22, 'këta, me të vërtetë, do të jenë krejtësisht të humbur në botën tjetër.'),
(1496, 11, 23, 'Me të vërtetë, ata që besojnë dhe punojnë vepra të mira dhe përulen te Zoti i tyre, këta janë banorë të xhennetit, në të cilin do të qëndojnë përherë.'),
(1497, 11, 24, 'Shembulli i këtyre dy grupeve është si i verbëri dhe i shurdhëri (në njërën anë) dhe ai që sheh dhe dëgjon (në anën tjetër). A janë të njëjtë këta (kur të krahasohen)? A nuk po këshilloheni?!'),
(1498, 11, 25, 'Dhe Na, me të vërtetë, ia dërguam Nuhun popullit të tij (e, u tha): “Unë jam për ju paralajmërues i hapët;'),
(1499, 11, 26, 'Adhuronie vetëm Perëndinë. Unë për ju i druaj dënimit të (një) Ditës së dhëmbshme”.'),
(1500, 11, 27, 'E, paria e popullit të tij, ata që nuk kanë besuar, thanë: “Na shohim se ti je vetëm njeri si na, dhe shohim që ty të shkojnë pas, vetëm ata më të ulëtit, nga ne – pa menduar fare dhe se nuk shohim se ju keni diçka më të mirë se ne. Jo, (por) na ju konsiderojmë për gënjeshtar”.'),
(1501, 11, 28, 'Ai (Nuhu) tha: “O populli im, ç’mendoni ju, nëse unë i përmbahem dokumentit të qartë të Zotit tim, madje Ai më ka dhënë nga Vetja mëshirë – e ju jeni të verbër për të, a mund t’jua imponojmë juve atë, kur ju nuk e dëshironi?”'),
(1502, 11, 29, 'O populli im! Unë për këtë nuk kërkoj nga ju kurrfarë pasurie. Mua më shpërblen vetëm Perëndia. E, unë nuk mund t’i dëboj ata që besojnë. Ata do të takohen me Zotin e tyre. Mirëpo, unë shoh se ju, me të vërtetë, jeni popull që nuk di.'),
(1503, 11, 30, 'O populli im kush do të më mbroj mua nga Perëndia, nëse unë ata (besimtarët) i dëboj? A nuk doni të këshilloheni?”'),
(1504, 11, 31, 'Unë juve nuk dua t’ju them: “Unë kam thesarin e Perëndisë, as që di të padukshmet, as që do të them se unë jam engjëll, as që do t’u them atyre, të cilët, sytë tuaj i poshtërojnë: se Perëndia nuk do t’u japë kurrfërë të mire. Perëndia di më mirë se ç’ka në shpirtërat e tyre. (Sikur unë të veproja ashtu), atëherë do të isha, me të vërtetë, njëri nga zullumqarët”.'),
(1505, 11, 32, 'Ata thanë: “O Nuh, ti bëre polemikë me ne, dhe madje u zgjerove në polemikën tonë. Sillna ti atë premtim (dënim), nëse thua të vërtetën!”'),
(1506, 11, 33, 'Tha (Nuhu): “Vetëm Perëndia do t’ua sjellë atë, nëse dëshiron, e ju Atij nuk mund t’i ikni.'),
(1507, 11, 34, 'Këshilla ime, nuk do t’ju bëjë dobi, edhe sikur të doja t’ju këshillojë – nëse Perëndia don t’ju shkatërrojë. Ai është Zoti juaj dhe ju tek Ai do të ktheheni”.'),
(1508, 11, 35, 'Ose ata thonë: “Ai (Muhammedi) e ka trilluar (Kur’anin). Thuaj: “E nëse unë e kam trilluar, mëkati im bie mbi mua, e unë jam larg nga mëkatet që bëni ju”.'),
(1509, 11, 36, 'Dhe Nuhut i qe shpallur: “Nga populli yt askush nuk do të besojë, përpos atyre që kanë pranuar fenë, andaj mos u pikëllo për atë që punojnë ata,'),
(1510, 11, 37, 'dhe ndërto anijen nën mbikëqyrje dhe udhëzimin Tonë, dhe mos m’u drejto më për ata që janë zullumqarë! Ata me siguri do të fundosen”.'),
(1511, 11, 38, 'Dhe ndërtonte anijen. Dhe sa herë që kalonin pranë tij paria e popullit të tij, e përqeshnin atë. (Nuhi) tha: “Nëse ju talleni me ne, edhe na do të tallemi me ju, ashtu si talleni ju,'),
(1512, 11, 39, 'dhe së shpejti, me siguri, do të dini, kë do ta gjejë dënimi poshtërues dhe në kë do të bie dënimi i përhershëm”.'),
(1513, 11, 40, 'Dhe, kur arriti urdhëri Ynë, e uji ng asipërfaqja e tokës nisi të gufojë. Ne thamë: “Ngarko në anije nga çdo lloj gjallese nga një palë (një mashkull e një femër) dhe familjen tënde, përveç atyre që ka qenë fjala (e dënimit) për ta, - dhe besimtarët!” E pak ka pasur të atillë, që kanë besuar me të.'),
(1514, 11, 41, '(Nuhu) tha: “Hypni në të! Me emrin e Perëndisë ajo lundron dhendalet! Me të vërtetë, Zoti im është falës dhe mëshirues.'),
(1515, 11, 42, 'Dhe, ajo lundronte me ta nëpër valët e mëdha e të larta sa malet, ndërsa Nuhu thërriste birin e vet, i cili ishte tërhequr (anash): “O biri im, hyp me ne e mos u bënë me mohuesit!”'),
(1516, 11, 43, '(I biri) tha: “Unë do të strehohem në bjeshkën, e cila do të më ruajë nga uji”, (e Nuhu) tha: “Sot askush nuk do të kursehet nga dënimi i Perëndisë, përveç atë që e mëshiron Ai!” E, një valë në mes tyre i ndau dhe ai (i biri) – u fundos.'),
(1517, 11, 44, 'Dhe është thënë: “O Tokë, gëlltitë ujin tënd, dhe ti o qiell, ndaloje (shiun)!” Dhe uji u tërhoq e u plotësua urdhëri (i Perëndisë), e anija u ndal në malin Xhudij, dhe është thënë: “Le të jetë larg populli zullumqarë!”'),
(1518, 11, 45, 'E Nuhu iu lut Zotit të tij dhe tha: “O Zoti im, me të vërtetë, biri im është nga familja ime dhe premtimi Yt, është i vërtetë, e Ti je gjykatësi më i drejtë!”'),
(1519, 11, 46, '(Perëndia) tha: “O Nuh, ai nuk është nga familja jote, ai ka bërë punë të keqe (që nuk ka ardhur me ty). Mos më pyet për atë që nuk e di ti. Unë të këshilloj që të mos bëhesh nga ata që nuk dinë”.'),
(1520, 11, 47, '(Nuhu) tha: “O Zoti im, unë mbështetem te Ti (për t’u ruajtur) nga pyetjet që nuk i di, e nëse Ti nuk më falë dhe nuk më mëshiron, do të jem nga të humburit”.'),
(1521, 11, 48, 'Është thënë: “O Nuh – zbrit me përshëndetjen Tonë dhe dhuntitë Tona për ty dhe pasardhësit e atyre që gjende me ty”. E, disa popujve, Na do t’u japim kënaqësi (në këtë botë), e që pastaj, do t’i goditë nga Ne dënimi i dhembshëm!”'),
(1522, 11, 49, 'Këto janë lajmet e ngjarjeve të padukshme, që Ne t’i shpallim ty. Nuk i ke ditur as ti, as populli yt para kësaj (shpaljes së Kur’anit). Andaj, duro! Se, me të vërtetë, në fund, fitorja është e besimtarëve.'),
(1523, 11, 50, '(Ia dërguam) Adit – vëllaun e tij Hudin. Ai tha: “O populli im, adhuronie Perëndinë! Ju nuk keni Zot tjetër përveç Tij; ju jeni vetëm trillues (nëse adhuroni tjetër kënd).'),
(1524, 11, 51, 'O populli im! Unë nuk kërkoj nga ju shpërblim për këtë. Mua më shpërblen vetëm Ai që më ka krijuar! A nuk po mendoni?!'),
(1525, 11, 52, 'O populli im! Kërkoni falje nga Zoti juaj dhe kthehuni tek Ai, e Ai do t’ju sjellë shi të begatshëm dhe do t’ju shtojë fuqinë mbi fuqinë tuaj, dhe mos u shmangëni si mëkatarë!'),
(1526, 11, 53, 'Ata thanë: “O Hud! Ti nuk na ke sjellë kurrfarë argumenti dhe për fjalë tuaja – ne nuk do t’i braktisim zotrat tonë, as nuk të besojmë na ty!'),
(1527, 11, 54, 'Na pohojmë se vetëm ty të ka qëlluar ndonjë e keqe nga zotrat tonë” Ai tha: “Unë e dëshmoj Perëndinë, e dëshmonie edhe ju, se unë jam larg nga ato që ju i bëni shok Perëndisë,'),
(1528, 11, 55, '(heqi dorë nga të bëmit shok) përpos Tij. Të gjithë ju bëni dredhi kundër meje, dhe mos më jepni afat aspak!'),
(1529, 11, 56, 'Unë mbështetem te Perëndia, Zoti im dhe Zoti juaj. S’ka asnjë gjallesë që të mos jetë nën pushtetin e Tij. Me të vërtetë, Zoti im është në rrugë të drejtë.'),
(1530, 11, 57, 'E, nëse ju shmangeni, thuaj: “Unë ju kam treguar për atë që jam dërguar te ju. E Zoti im në vend tuaj do të sjellë një popull tjetër, kurse ju, - nuk mund t’i bëni kurrfarë dëmi Atij. Me të vërtetë, Zoti im është ruajtës mbi çdo gjë”.'),
(1531, 11, 58, 'Kur erdhi urdhëri Ynë (dënimi), Na, me mëshirën Tonë e shpëtuam Hudin bashkë me besimtarët e tij. I shpëtuam ata nga dënimi i rëndë.'),
(1532, 11, 59, 'Qe, ky ishte populli i Adit. I mohuan rgumentet e Zotit të tyre dhe ishin të padëgjueshëm ndaj pejgamberëve të Tij dhe shkonin pas urdhërit të çdo zullumqari, kokëfortë.'),
(1533, 11, 60, 'Ata i ndoqi mallkimi në këtë jetë dhe do t’i ndjekë edhe në Ditën e Kijametit. Ah! Me të vërtetë, Adi mohuan Zotin e tyre. Qe pra, le të jetë larg (nga shpëtimi) Adi – populli i Hudit!'),
(1534, 11, 61, '(Ia dërguam) Themudit, vëllaun e tyre Salihun. “O populli im”, - tha ai: “Adhuronie vetëm Perëndinë. Ju, s’keni zot tjetër përveç Atij! Ai ju krijoi nga toka (dheu) dhe ju dha të jetoni në të; andaj, kërkoni falje nga Ai dhe kthehuni tek Ai! Me të vërtetë, Zoti im është afër dhe Ai u përgjigjet (lutjeve)”.'),
(1535, 11, 62, 'Ata thanë: “O Salih! Ti ke qenë shpresa jonë, para kësaj (fjale). A mos vallë, ti don të na ndalosh ta adhurojmë atë, që e kanë adhuruar prindërit tanë? Me të vërtetë, na jemi në dilemë, që na bën të dyshojmë në thirrjen tënde”.'),
(1536, 11, 63, 'Tha (Salihu): “O populli im! Ç’mendoni ju, nëse unë jam mbështetur në dokumentin e qartë të Zotit tim, e Ai më ka dhënë nga Vetja mëshirë, (profetni); kush do të më mbrojë nga dënimi i Perëndisë, nëse e kundërshtoj Ate? Ju, (me këtë) mua vetëm do të më shtonit humbjen.'),
(1537, 11, 64, 'O populli im! Kjo është devja e Perëndisë – mrekulli për ju, e lënie të kullosë në tokën e Perëndisë, dhe mos i bëni ndonjë të keqe asaj, se ju godet ndëshkimi i afërt”.'),
(1538, 11, 65, 'Por, ata e prenë (therrën), e ai (Salihu) tha: “Jetoni ju në shtëpitë tuaja vetëm edhe tri ditë! Ky është premtimi i (dënimit të) vërtetë”.'),
(1539, 11, 66, 'E, kur erdhi urdhëri Ynë (dënimi), Na e shpëtuam Salihun bashkë me besimtarët e tij – me mëshirën Tonë, dhe (i shpëtuam ata) nga poshtërimi i asaj dite. Me të vërtetë, Zoti yt është i fortë dhe i Plotëfuqishëm;'),
(1540, 11, 67, 'e ata, që bënë zullum, i kaploi zëri i tmerrshëm, të cilët në tokën e tyre u gëdhinë të vdekur – të palëvizshëm,'),
(1541, 11, 68, 'thuajse, nuk kanë ekzistuar kurrë në të. Me të vërtetë, Themudi mohuan Zotin e tyre. Qe pra, le të jetë larg (nga mëshira e Zotit) Themudi!'),
(1542, 11, 69, 'Të dërguarit Tonë (engjëjt), i sollën Ibrahimit sihariq dhe i thanë: “Selam!” Ai u përgjegj: “Selam!” Dhe me të shpejtë, u solli atyre një viç të pjekur.'),
(1543, 11, 70, 'Kur pa (Ibrahimi) se duart e tyre nuk po e përkasin atë (viçin), nuk u pëlqeu (ky ngurrim i tyre ndaj ushqimit) dhe ndjeu frikë nga ata. (Engjëjt) thanë: “Mos u friko, na jemi dërguar te populli i Lutit”.'),
(1544, 11, 71, 'E, gruaja e tij (e Ibrahimit) rrinte aty dhe qeshi, dhe Na e gëzuam atë me Is’hakun, e pos Is’ahkut, me Jakubin.'),
(1545, 11, 72, 'Tha (gruaja e Ibrahimit): “Ah! e mjera unë! A mos vallë, kështu plakë të lindë unë; e, edhe ky burri im është plak! Me të vërtetë, kjo është diçka e jashtëzakonshme!”'),
(1546, 11, 73, 'Ata (engjëjt), thanë: “A mos vallë, ti po çuditesh me urdhërin e Perëndisë? Mëshira e Perëndisë dhe bekmi i Tij qoftë mbi ju – familjen e pejgamberit. Me të vërtetë, Ai (Perëndia) është i lavdëruar dhe bamirës për së tepërmi!”'),
(1547, 11, 74, 'Pasi që i kaloi frika Ibrahimit dhe i erdhi lajmi i gëzuar, (nisi) të diskutojë me të dërguarit Tonë, për popullin e Lutit.'),
(1548, 11, 75, 'Ibrahimi me të vërtetë, ishte i butë, i dhembshëm dhe i kthyeshëm (kah Perëndia).'),
(1549, 11, 76, '“O Ibrahim, shmangu prej kësaj! Me të vërtetë, erdhi urdhëri nga Zoti yt – dhe, me të vërtetë, ata do t’i gjejë dënimi i paevitueshëm”.'),
(1550, 11, 77, 'Kur i erdhën të dërguarit (engjëjt) Tanë Lutit, (ardhja e tyre) e hidhëroi dhe për shkak të tyre ndjeu një shtrëngim në shpirtin e tij dhe tha: “Kjo është ditë e rëndë!”'),
(1551, 11, 78, 'Dhe erdhi populli i tij duke nxituar kah ai, e që më parë patën punuar vepra të këqia. (Luti) tha: “O populli im, këto janë bijat e ia. Ato për ju janë më tëpastra, e druajuni Perëndisë dhe mos më turpëroni para mysafirëve të mi. a nuk ka në mes jush njeri të ndershëm?'),
(1552, 11, 79, 'Ata thanë: “Ti e di se neve nuk na nevojiten bijat tua. Me të vërtetë, ti e di se ç’duam ne”.'),
(1553, 11, 80, 'Ai tha: “Sikur unë të kisha fuqi (që t’ju dëboj) ose të mbështetesha në ndonjë fuqi të madhe, (do t’i mbroja mysafirët e mi).'),
(1554, 11, 81, 'Thanë (mysafirët): “O Lut! Na jemi të dërguarit e Zotit tënd. Ata nuk mund të bëjnë ty asgjë, e pra, urdhëto ti me familjen tënde në orët e vona të natës dhe askush prej jush mos të këqyrë prapa; (familja do të shpëtojë) përpos gruas tënde. Atë, me të vërtetë, do ta godas ajo që do t’i gjejë edhe ata. Afati i tyre është agimi i ditës. E, vallë, a nuk është afër agimi i ditës?'),
(1555, 11, 82, 'Dhe kur erdhi urdhëri Ynë, Na rrotulluam çdo gjë, - ato që ishin lart i përmbysëm poshtë, dhe në ta lëshuam tulla e gurë, që binin vazhdimisht, si shiu,'),
(1556, 11, 83, 'e shënuar (për dënim) nga Zoti yt. Dhe ai (dënimi), nuk është larg nga zullumqarët.'),
(1557, 11, 84, '(Ia dërguam) Medjenit – vëllaun e tyre Shuajbin. Ai tha: “O populli im, adhuronie Perëndinë! Ju s’keni tjetër zot përveç Tij. Mos pakësoni (mungoni) as gjatë matjes me litër as me kandar. Unë po shoh se ju gjendeni në begati të mirë dhe druaj se juve, do t’ju godas dënimi, i një dite që u përfshinë të gjithëve.'),
(1558, 11, 85, 'O populli im, plotësoni matjen në litër dhe kandar – me të drejtë dhe mos ua cungoni njerëzve gjërat e tyre! Dhe mos bëni ngatërresa në Tokë!'),
(1559, 11, 86, 'Atë që ua ka lënë Perëndia (dhe që e keni arritur në mënyrë të lejueshme) u është më e mirë, nëse jeni besimtarë; e unë nuk jam ruajtës i juaj.'),
(1560, 11, 87, 'Ata thanë: “O Shuajb, a mos vallë namazi yt urdhëron që na t’i lëmë ato që i kanë adhuruar të parët tanë ose që të bëjmë me pasurinë tonë çka të duam. Me të vërtetë, sa i butë dhe i drejtë që je ti! (thanë ata në formë ironike”.'),
(1561, 11, 88, '(Shuajbi) tha: “O populli im, ç’mendoni ju nëse unë jam në dokumentin e qartë të Zotit tim dhe, Ai, më ka furnizuar nga Vetja me ushqim të mirë. E, unë nuk dëshiroj të punoj atë çka ua ndaloj juve. Unë dëshiroj vetëm të përmirësojë atë çka mundem. E, suksesi im varet vetëm nga Perëndia. Tek Ai mbështetem dhe Atij i drejtohem.'),
(1562, 11, 89, 'O populli im, kundërshtimi ndaj meje le të mos u shpiej kurrsesi (në mëkat), e që t’ju ndodhë ajo që i ndodhi popullit të Nuhut, ose popullit të Hudit apo popullit të Salihut; por edhe populli i Lutit nuk është larg prej jush.'),
(1563, 11, 90, 'Dhe kërkoni falje nga Zoti juaj e kthehuni tek Ai. Me të vërtetë, Zoti im është mëshirues dhe i dashur”.'),
(1564, 11, 91, 'Ata thanë: “O Shuajb, na nuk kuptojmë shumë nga ato që na thua ti dhe na të shohim të dobët në mesin tonë. E, sikur të mos ishte farefisi yt, na të kishim mbytur me gurë, ti nuk je i respektuar te ne”.'),
(1565, 11, 92, '(Shuajbi) tha: “O populli im, a mos vallë farefisi im për ju është më i respektuar se Perëndia, të cilin e keni lënë krejtësisht anash. Me të vërtetë, Zoti im, kaplon atë që punoni ju.'),
(1566, 11, 93, 'O populli im, bëni sa të mundeni, por edhe unë do të bëjë. Ju, me siguri do ta dini se kë do ta gjejë dënimi që do ta poshtërojë dhe kush është gënjeshtar. E, pritni ju (fundin), se edhe unë do të pres me ju”.'),
(1567, 11, 94, 'E, kur erdhi urdhëri Ynë (dënimi), e shpëtuam Shuajbin bashkë me besimtarët e tij – me mëshirën Tonë, kurse mohuesit i kaploi zë i tmerrshëm dhe ata në tokën e tyre u gëdhinë të vdekur – të palëvizshëm,'),
(1568, 11, 95, 'thuajse, nuk kanë ekzistuar kurrë në të. Le të jetë larg (nga mëshira e Zotit) Medjeni, ashtu siç ka qenë larg edhe Themudi!'),
(1569, 11, 96, 'Na e kemi dërguar Musain me argumentet Tona dhe me dokumentet e qarta'),
(1570, 11, 97, 'te Faraoni dhe parisë së tij, por ata shkuan pas urdhërit të Faraonit. E, urdhëri i Faraonit nuk ishte i drejtë.'),
(1571, 11, 98, 'Do ta udhëheq ai (Faraoni) popullin e tij në Ditën e Kijametit, e do ta sjellë në zjarr. E, sa vend i shëmtuar është ai, ku do të sillen ata!'),
(1572, 11, 99, 'Ata do të përcillen me mallkim në këtë jetë dhe në jetën tjetër. E sa “dhuratë” e shëmtuar është ajo që do t’u jepet!'),
(1573, 11, 100, 'Këto janë lajmet për vendbanimet që po t’i tregojmë Na ty (o Muhammed!). Disa prej tyre ende ekzistojnë, e disa janë rrënuar.'),
(1574, 11, 101, 'Na nuk i kemi bërë atyre zullum, por ata vetvetes i kanë bërë zullum. E, nuk u ndihmuan atyre hyjnitë, të cilëve ata iu luteshin, pos Perëndisë, asgjë – kur erdhi urdhëri (dënimi) i Zotit tënd. Dhe, nuk iu shtuan (hyjnitë) atyre kurrgjë tjetër, përpos humbjen.'),
(1575, 11, 102, 'I tillë është dënimi i Zotit tënd kur ndëshkon vendbanimet, të cilat bënin zullum. Me të vërtetë, dënimi i Tij është i dhëmbshëm dhe i ashpër.'),
(1576, 11, 103, 'Me të vërtetë, në këtë ka këshillë për atë që i dron dënimit të Ditës së gjykimit. E, ajo është Dita në të cilën do të tubohen të gjithë njerëzit dhe ajo është Dita në të cilën do të jenë prezent të gjithë.'),
(1577, 11, 104, 'Dhe Ne e shtyjmë atë vetëm gjer në afatin e caktuar.'),
(1578, 11, 105, 'Atë ditë që do të vijë, mund të flasë njeriu vetëm me lejen e Tij. Disa prej tyre do të jenë fatkëqinj, e disa fatlumë.'),
(1579, 11, 106, 'Ata që janë fatkëqinj, do të gjenden në zjarr, në të cilin për ata ka kërrhamë (frymënxjerrje me zë), e po ashtu edhe rrënkim,'),
(1580, 11, 107, 'në të cilin do të mbetën përherë, derisa të jenë qiejt dhe Toka, përveç nëse ndryshe e cakton Zoti yt. Me të vërtetë, Zoti yt punon atë që don (pa mundësi kundërshtimi).'),
(1581, 11, 108, 'Kurse fatlumët, do të gjenden në Xhennet, ku do të qëndrojnë përherë në të, derisa të jenë qiejt dhe Toka, përveç nëse ndryshe e cakton Zoti yt. Kjo është dhuratë e pashkëputshme.'),
(1582, 11, 109, 'Prandaj, mos u bënë dyshues për atë që këta adhurojnë. Ata adhurojnë vetëm atë që kanë adhuruar më parë – të parët e tyre. Dhe Ne do t’ua plotësojmë atyre pjesën e vet (të dënimit), duke mos ua pakësuar aspak.'),
(1583, 11, 110, 'Na, me të vërtetë, ia kemi dhënë Musait Librin, për të cilin është shkaktuar përçarje mendimesh. E, sikur të mos i kishtë paraprirë fjala e Zotit tënd, (që do të bëhet dënimi në jetën tjetër), do të gjykoheshin menjëherë. Me të vërtetë, ata për këtë janë në dilemë, e cila i shpie në dyshim të madh.'),
(1584, 11, 111, 'Me të vërtetë, çdonjërit prej tyre, Zoti yt do t’ia plotësojë pagimin për veprat e tyre. Se, Ai, me të vërtetë, është i njoftuar për atë që punojnë ata.'),
(1585, 11, 112, 'Shko ti udhës së drejtë, ashtu siç je i urdhëruar; dhe ashtu le të veprojnë edhe besimtarët që janë me ty, e mos i kaloni kufijt, se, Ai, me të vërtetë, i sheh mirë ato që punoni ju.'),
(1586, 11, 113, 'Dhe, mos anoni kah ata që bëjnë zullum, meqë do t’u përcëlloj zjarri. Ju nuk keni mbrojtës tjetër përveç Perëndisë, dhe nuk do të jeni të ndihmuar.'),
(1587, 11, 114, 'Dhe kryeje namazin në mëngjes dhe mbrëmje dhe me një kohë të natës. Me të vërtetë, veprat e mira i shlyejnë veprat e këqija. Kjo këshillë vlenë për ata që i pranojnë këshillat.'),
(1588, 11, 115, 'Dhe duro! Se, Perëndia, me të vërtetë, nuk e humbë shpërblimin e bamirësve.'),
(1589, 11, 116, 'Vallë! A nuk ka pasur njerëz të mençur te popujt para jush – të cilët pengonin ngatërresat në Tokë? Ka pasur vetëm disa prej tyre – që i shpëtuam Ne! e, ata që bënin zullum, ipeshin pas kënaqësive dhe ishin mëkatarë.'),
(1590, 11, 117, 'Dhe, Zoti yt, kurrë nuk i ka shkatërruar vendbanimet padrejtësisht, nëse banorët e tyre kanë jetuar në harmoni dhe drejtësi në mes vete.'),
(1591, 11, 118, 'E, sikur të kishte dashur Zoti yt, të gjithë njerëzit do t’i bënte njerëz të një feje. Por, ata edhe më tej janë në kundërshtim mendimesh.'),
(1592, 11, 119, 'Përveç atyre që i ka mëshiruar Zoti yt. E, për ata edhe i ka krijuar Ai. Dhe, u plotësua fjala e Zotit tënd: “Do ta mbushë, me të vërtetë, xhehennemin me xhind dhe njerëz bashkarisht!”'),
(1593, 11, 120, 'Na po t’i tregojmë ty të gjitha lajmet e pejgamberëve, për me ta forcua zemrën. Dhe në këtë sure të ka ardhur ty e vërteta; këshilla dhe të përkujtuarit për besimtarët.'),
(1594, 11, 121, 'Dhe thauju atyre që nuk besojnë: “Punoni ju si të mundeni, edhe ne do të punojmë;'),
(1595, 11, 122, 'dhe pritni ju (fundin e punës), dhe ne do të presim!”'),
(1596, 11, 123, 'Perëndia i di sekretet e qiejve dhe të Tokës dhe çdo gjë kthehet tek Ai. Lutju Atij dhe mbështetu tek Ai! Dhe Zoti yt nuk është i pakujdesshëm për atë që punoni ju.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'Elif, Lâm, Râ. Këto janë ajetet e Librit të qartë!'),
(1598, 12, 2, 'Na e zbritëm këtë Kur’an në gjuhën arabe, për ta kuptuar ju.'),
(1599, 12, 3, 'Na ty (o Muhammed!) do t’i tregojmë ngjarjet më të bukura përmes shpalljes së këtij Kur’ani; e ti, me të vërtetë, këto nuk i ke ditur më parë:'),
(1600, 12, 4, 'kur Jusufi i tha babës së vet: “O baba im! Unë kam ëndërruar njëmbëdhjetë yje, edhe Diellin e Hënën! I pashë se si ato më bënin sexhde (më përuleshin)”.'),
(1601, 12, 5, '(Baba i tij) tha: “O biri im! Mos u trego ëngrrën tënde vëllezërve tu, që mos të ta bëjnë ndonjë dredhi. Se, me të vërtetë, djalli është armik i hapët i njeriut.'),
(1602, 12, 6, 'Dhe qe kështu, Zoti yt të ka zgjedhur ty dhe të mëson në shpjegimin e ngjarjeve (ëndërrave, e të tjera) dhe e plotëson dhuntinë e Vet ndaj teje dhe familjes së Jakubit, ashtu siç e ka plotësuar më parë ndaj të parëve tu, Ibrahimit dhe Is’hakut. Me të vërtetë Zoti yt është i Plotëdijshëm dhe i Gjithëdijshëm”.'),
(1603, 12, 7, 'Me të vërtetë, për të gjithë ata që pyesin Jusufin dhe vëllezërit e tij ka shumë argumente.'),
(1604, 12, 8, '(Kujtoje ti) kur (vëllezërit e tij) thanë: “Jusufi dhe vëllai i tij, janë më të dashur për babën tonë se na, e na jemi grup i tërë. Baba i ynë, me të vërtetë, është në humbje të qartë.'),
(1605, 12, 9, 'Mbyteni Jusufin ose dëbonie në ndonjë vend të largët; e baba i juaj do të kthehet kah ju, dhe pas kësaj do të bëheni njerëz të mirë, -'),
(1606, 12, 10, 'njëri prej tyre tha: “Mos e mbytni Jusufin, por hedhnie në fund të ndonjë pusi; do ta marrë ndonjë karavan; - nëse përnjëmend doni të bëni diçka me të”.'),
(1607, 12, 11, '(Vëllezërit thanë): “O babai ynë! Përse për Jusufin nuk po ke besim në në, e na me të vërtetë, i dëshirojmë çdo të mirë atij.'),
(1608, 12, 12, 'Dërgoje nesër me ne që të hajë pemë dhe të luajë, e na me siguri do ta ruajmë”.'),
(1609, 12, 13, '(Baba) tha: “Unë brengosem që ju ta merrni atë, e druaj se mos do ta hajë ujku, kur nuk do të jeni të kujdesshëm”.'),
(1610, 12, 14, '(Vëllezërit) thanë: “Si do ta hajë ujku, kur ne jemi një grup i fortë! Na, atëherë, me të vërtetë, do të ishim të humbur”.'),
(1611, 12, 15, 'Pasi e morën atë (Jusufin) dhe vndosën që ta hedhin në fund të pusit, Na ia shpallëm atij: “Ti do t’i lajmërosh ata për këtë vepër, e ata nuk do ta vërejnë (që ti je Jusufi)”.'),
(1612, 12, 16, 'Dhe në mbrëmje erdhën (vëllezërit e Jusufit) te babai i tyre, duke qajtur.'),
(1613, 12, 17, 'Thanë: “O baba ynë, na shkuam të vrapojmë, e Jusufin e lamë te sendet tona, andaj e hëngri ujku. E, ti nuk na beson neve, edhe pse na flasim të vërtetën”.'),
(1614, 12, 18, 'Dhe sollën këmishën e tij të përgjakur, me gjak të rrejshëm. (Jakubi) tha: “Jo, por shpirtërat tuaj kanë zbukuruar diçka. (Durimi im) është durim i mrekullueshëm. Prej Perëndisë kërkoj ndihmë për atë që po e paraqitni ju”.'),
(1615, 12, 19, 'Dhe erdhi një karavan, e dërgoi ujëbartësin e vet dhe ai lëshoi kovën e tij dhe thirri: “O sa gëzim! Qe një djalë!” Dhe ata, e fshehën atë si plaçkë tregtie, e Perëndia di mirë atë që punojnë ata.'),
(1616, 12, 20, 'Dhe e shitën atë për pak para – për disa grosh; mezi pritën që ta shporrin qafet atë.'),
(1617, 12, 21, 'E, atëherë, kur e bleu ai personi nga Misiri, i tha gruas së vet: “Bënia të këndshëm strehimin e tij! Ndoshta do të na shërbejë ose do ta përvetësojmë si djalë!” Dhe ja kështu, Ne e vendosëm Jusufin në atë Tokë të bukur dhe ia mësuam komentimin e ëndërrave. E, Perëndia është ngadhnjyes i të gjitha gjërave, por shumica e njerëzve nuk e dijnë.'),
(1618, 12, 22, 'Dhe kur arriti ai moshën e pjekurisë, Na i dhamë atij aftësi të të gjykuarit (në shpjegimë) dhe dijeni; dhe kështu Ne i shpërblejmë bamirësit.'),
(1619, 12, 23, 'Dhe ajo, (gruaja) në shtëpinë e së cilës gjendej ai, nisi ta nxisë atë në mëkat, i mbylli të gjitha dyert dh tha: “Jam e gatshme për ty!” E, ai tha: “Perëndia më ruajtë! Me të vërtetë, Ai – Zoti im më ka bërë të mirë strehimin; e me të vërtetë, zullumqarët nuk do të shpëtojnë kurrë”.'),
(1620, 12, 24, 'Dhe ajo e dashti atë; por edhe ai do ta dëshironte atë, sikur të mos e kishte parë dokumentin e Zotit të vet. Kështu ka qenë kjo, për ta ruajtur Na atë nga mëkati dhe vepra e shëmtuar, sepse ai, në të vërtetë, ka qenë nga robërit Tanë të sinqertë.'),
(1621, 12, 25, 'Që të dy vrapuan kah dera, e ajo ia shkeu këmishën e tij mbrapa. Dhe te dera e hasën burrin e saj. Ajo tha: “Çfarë dënimi meriton ai që don t’i bëjë të keqe gruas tënde, përpos burgosjes apo dënimit të dhembshëm?”'),
(1622, 12, 26, '(Jusufi) tha: “Ajo tentoi të më përvetësojë”. E, një i afërt i saj, konstatoi: “Nëse këmisha e tij është shkyer përpara, atëherë ajo thotë të vërtetën, e ai të pavërtetën,'),
(1623, 12, 27, 'e, nëse këmisha e tij është shkyer prapa atëherë ajo gënjen, e ai thotë të vërtetën”.'),
(1624, 12, 28, 'Dhe kur ai (burri i saj) pa se këmisha e tij është shkyer përmbrapa, tha: “Kjo me të vërtetë, është një nga dredhitë tuaja (të femrave). – Me të vërtetë, dredhitë tuaja janë të mëdha!'),
(1625, 12, 29, 'Ti, o Jusuf, largohu nga kjo (ndodhi), e ti (grua ime) kërko falje për këtë mëkat! Se, me të vërtetë, je njëra prej mëkatarëve”.'),
(1626, 12, 30, 'Dhe disa gra nëpër qytet thonin: “Gruaja e Azizit (ministrit) dëshiron ta përvetësojë shërbëtorin e vet. Me të vërtetë, ajo e don shumë. Na e shohim se ajo ka bërë gabim të madh”.'),
(1627, 12, 31, 'Dhe kur ajo dërgjoi për thashethënat e tyre, i thirri ato në gosti dhe çdonjërës nga ato ia dha nga një thikë (për të ngrënë pemë dhe mish) dhe i tha (Jusufit): “Dil para tyre!” E kur e panë atë, u mahnitën nga bukuria e tij dhe u prenë duarve, e thanë: “O Zot, o Zot! Ky nuk është njeri, por ky shtë vetëm një engjëll fisnik!”'),
(1628, 12, 32, 'Ajo tha: “Jo, ky është ai, për të cilin ju më qortuat. Jam përpjekur ta përvetësoj, por ai është ruajtur. Nëse nuk do ta bëjë atë që e urdhëroj unë, me siguro do të burgoset dhe do të poshtërohet”.'),
(1629, 12, 33, '(Jusufi) tha: “O Zoti im, më shumë e dua burgun se atë që më ofrojnë ato. E, nëse ti nuk e largon prej meje dredhinë e tyre, unë do të prirem kah ato dhe do të bëhem njëri nga xhahilat”.'),
(1630, 12, 34, 'Dhe Zoti ia plotësoi lutjen e tij dhe shmangu nga ai dredhitë e tyre. Me të vërtetë, Ai dëgjon dhe di çdo gjë.'),
(1631, 12, 35, 'E pastaj i rë ndërmend atyre (familjes së gruas) – pasi panë argumentet (mbi ndershmërinë e Jusufit) që ta burgosin për një kohë.'),
(1632, 12, 36, 'Me të hynë në burg dhe dy djelmosha. Njeri prej tyre tha: “Unë kam parë (në ëndërr) veten se si shtrydhja verë”. E tjetri tha: “Unë kam parë (në ëndërr) veten, se si bartja bukë në kokë, të cilën e hanin shpendët. Shpjegona (o Jusuf) komentimin e tyre. Na, me të vërtetë, shohim se ti je prej njerëzve bamirës”.'),
(1633, 12, 37, '(Jusufi) tha: “Nuk do t’ju vijë asnjë shujtë e ushqimit, me të cilin ushqeheni ju, e që unë të mos u lajmëroj për atë (përmbajtjen) para se t’ju vijë. Kjo është prej dijenive që m’i ka mësuar Zoti im. Unë e kam flakë fenë e popullit që nuk beson Perëndinë dhe i cili mohon jetën tjetër,'),
(1634, 12, 38, 'dhe besoj fenë e të parëve të mi – Ibrahimit, Is’hakut dhe Jakubit; për ne nuk bënë që Perëndisë t’i bëjmë shok – asgjë. Kjo është dhuntia e Perëndisë ndaj nesh dhe njerëzve tjerë, por shumica e njerëzve nuk janë mirënjohës.'),
(1635, 12, 39, 'O shokët e mi të burgut! A janë më të mirë zotrat e llojllojshëm apo Perëndia, i Vetmi dhe Ngadhnjimtari?'),
(1636, 12, 40, 'Ju adhuroni, pos Perëndisë, vetëm emra, të cilët ua keni ngjitur ju dhe të parët tuaj, - Perëndia nuk ka shpallur asnjë dokument për ta. Vendimi i përket vetëm Perëndisë: Ai ka urdhëruar që të adhuroni vetëm Atë. Kjo është e vetmja fe e vërtetë, por shumica e njerëzve nuk e din.'),
(1637, 12, 41, 'O shokët e mi të burgut! Njëri prej ju dyve do ta qerasë me verë pronarin (zotriun) e vet, e sa i përket tjetrit, ai do të varet, e shpendët do të hanë prej kokës së tij. Kështu është caktuar kjo gjë, për të cilën pyetët ju!”'),
(1638, 12, 42, 'Dhe (Jusufi) i tha njërit prej këtyre dyve, për të ciln mendoi se do të jetë i shpëtuar: “Përmendëm mua te Zoti yt!” Por djalli bëri që ai (i shpëtuari) të harrojë ta përmend te Zoti i vet (Jusufin) dhe kështu Jusufi mbeti edhe disa vjet në burg.'),
(1639, 12, 43, '(Dhe tha mbreti): “Kam parë (në ëndërr) se si shtatë lopë të majme i hanin ato shtatë të liga, dhe kam parë (në ëndërr) shtatë kallinj të gjelbër, shtatë të tjerë të tharë. O paria ime, më shpjegoni ëndërrën time, nëse dini komendimin e ëndërrave!”'),
(1640, 12, 44, 'Ata thanë: “Këto janë ëndërra të ngatërruara, e nuk dimë t’i shpjegojmë ëndërrat (e tilla)”.'),
(1641, 12, 45, 'Njeri nga ata dy që shpëtoi (nga burgu), iu kujtua pas një kohe e tha: “Unë do t’ju njoftoj për shpjegimin e kësja, vetëm më dërgoni mua!”'),
(1642, 12, 46, '(Kur ai shkoi te Jusufi, tha): “O Jusuf, o njeri i drejtë! Na shpjego (ëndërrën) për shtatë lopët e majme, që i hanin ato shtatë të liga dhe për shtatë kallinj të gjelbër e shtatë tjerë të tharë, - që të kthehem të njerëzit, e ta dijnë atë edhe ata”.'),
(1643, 12, 47, '(Jusufi) tha: “Do të mbillni shtatë vjet rresht, e atë që do ta korrni, linie në kallinj, përveç një pakice që do ta hani,'),
(1644, 12, 48, 'sepse pastaj do të vijnë shtatë vjet të thata, të cilat do t’i hanë ato që i keni ruajtur ju, përveç një pakice që e keni ruajtur (për farë).'),
(1645, 12, 49, 'Pastaj, pas këtyre do të vijë një vit në të cilin njerëzit do të kenë bereqet dhe në të cilin do të shtrydhin (rrush, etj.)”.'),
(1646, 12, 50, 'Dhe mbreti tha: “Sillmani atë (Jusufin)!” Kur (Jusufit) i erdh i deleguari (për ta nxjerrë prej burgut), ai i tha: “Ktheu te mbreti yt, e pyete: ‘Ç’patën ato gra që i prenë duart e veta?’ Me të vërtetë, Zoti im i di dredhitë e tyre!”'),
(1647, 12, 51, '(Mbreti u tha grave): “Si qe puna juaj kur deshët ta mashtronit Jusufin?” Ato thanë: “Perëndia na ruajtë! Na për të nuk dimë asgjë të keqe”. Gruaja e Azizit (ministrit) tha: “Tani do të dalë në shesh e vërteta! Unë jam përpjekur që ta mashtrojë atë, por ai me të vërtetë është prej njerëzve të drejtë.'),
(1648, 12, 52, '(Kur Jusufi mori vesh për të gjitha këto), tha: “Kjo është që ta dijë ai (ministri), se unë me të vërtetë, nuk e kam tradhëtuar atë gjatë kohës së mungesës së tij dhe, me të vërtetë Perëndia nuk i përfundon mirë dredhitë e tradhëtarëve.'),
(1649, 12, 53, 'Unë nuk e shfajsoj veten, meqë shpirti (i njeriut) është fort i prirur për të keqe, përveç atij që e mëshiron Zoti im. Me të vërtetë, Zoti im është falës dhe mëshirues”.'),
(1650, 12, 54, 'Dhe tha sundimtari (mbreti): “Ma sillni atë, do ta marr në suitën time (si këshilltar special)”. E, pasi bisedoi me të, i tha: “Prej sot, ti tek ne, ke pozitë të lartë dhe je i besuar”.'),
(1651, 12, 55, '(Jusufi) tha: “Më cakto të udhëheq thesarin e vendit. Unë, me të vërtetë, jam ruajtës dhe i dijshëm”.'),
(1652, 12, 56, 'Dhe kështu, ia dhamë Jusufit pozitën në vend (Misir), dhe ai në të vendosej ku të donte. Na e shpërblejmë me mëshirën Tonë, kë ë duam. Bamirësve nuk ua humbim shpërblimet.'),
(1653, 12, 57, 'E, shpërblimi i jetës tjetër është më i mirë për ata që besojnë dhe i druajnë Perëndisë.'),
(1654, 12, 58, 'Dhe erdhën vëllezërit e Jusufit, e hyn tek ai. Ai i njohu ata, por ata nuk e njohën atë.'),
(1655, 12, 59, 'Dhe kur i furnizoi ata me ushqim të nevojshëm, tha: “Ma sillni vëllaun tuaj prej babës! A nuk po shihni se unë i plotësoj masat dhe unë jam më i miri mikpritës.'),
(1656, 12, 60, 'Nëse nuk ma sillni atë (vëllaun tuaj), nuk do të ketë tek unë më ushqim për ju dhe mos ejani te ne!”'),
(1657, 12, 61, 'Ata thanë: “Na do të përpiqemi që disi ta sjellim nga baba i tij, dhe ne do të bëjmë këtë”.'),
(1658, 12, 62, '(Jusufi) u tha atyre djelmoshave (nëpunësve) të vet: “Lënie pagesën, të hollat e tyre në barrët e tyre, e do t’i njohin ato kur të shkojnë ata te familja e vet, e do të kthehen përsëri te ne”.'),
(1659, 12, 63, 'Kur u kthyen te baba i tyre, thanë: “O baba ynë! Nuk do të na japin më ushqim. Prandaj dërgoje me ne vëllain tonë, për të do të mund të marrim ushqim, e na, me të vërtetë, do ta ruajmë atë!”'),
(1660, 12, 64, '(Baba) u tha: “A mos vallë t’jua besoj juve, ashtu siç ua pata besuar më parë vëllain e tij? Perëndia është ruajtësi më i mirë dhe Ai është më i mëshirshmi i mëshiruesve!”'),
(1661, 12, 65, 'E, kur i çelën barrët e tyre, e gjetën pagën (të hollat) e tyre, që ua kishin kthyer, (atëherë) thanë: “O baba i ynë! Ç’duam më tepër? Këto, janë gjërat (të hollat) tona; na janë kthyer. Na, do ta furnizojmë familjen tonë dhe do ta ruajmë vëllain tonë, dhe do ta shtojmë një barrë deveje (ushqim), e kjo është barrë e lehtë”.'),
(1662, 12, 66, '(Baba) tha: “Unë kurrsesi nuk do ta dërgoj me ju, përderisa të mos betoheni ju në Perëndinë, se do të ma ktheni ate, përpos nëse ju rrezikon ndonjë e keqe e madhe (të gjithëve)”. E kur ata dhanë betimin e vet, (baba) u tha: “Perëndia është mbikëqyrës për atë që flasim ne”.'),
(1663, 12, 67, 'E ai tha: “O djemtë e mi, mos hyni në të njëjtën derë, por në dyer të ndryshme. Unë asgjë nuk mund të largoj prej jush (çka u ka caktuar) Perëndia! Se pushteti i përket vetëm Perëndisë. Tek ai mbështetem uën dhe tek Ai le të mbështeten ata që dëshirojnë të jenë të mbështetur (te Perëndia)!”'),
(1664, 12, 68, 'Pasi që hynë (në qytet), ashtu siç i urdhëroi babai i tyre, kjo nuk u ndihmoi asgjë atyre para caktimit të Perëndisë, përpos dëshirës së Jakubit, të cilën ia plotësoi (Zoti). Ai (Jakubi), në të vërtetë, ka qenë dijetar i madh, sepse Ne e kemi mësuar, por shumica e njerëzve nuk e di (këtë).'),
(1665, 12, 69, 'Dhe kur dolën para Jusufit, ky e afroi vëllaun e vet (Bunjamin) dhe i tha: “Unë me të vërtetë, jam vëllai yt dhe mos u hidhëro për atë që vepruan vëllezërit tanë!”'),
(1666, 12, 70, 'Dhe pasi i furnizoi me ushqimin e nevojshëm, shtini një gotë (të çmueshme) në ngarkesën e vëllait të vet, e pastaj një thërritës thirri: “O karavan, ju me të vërtetë jeni vjedhësa!”'),
(1667, 12, 71, 'Ata (vëllezërit e Jusufit) – u kthyen kah ata (e thanë): “Ç’ju ka humbur?”'),
(1668, 12, 72, 'Thanë: “Po kërkojmë gotën e mbretit. Kush e sjell atë, ka shpërblim një barrë deveje ushqim. Unë për këtë garantoj”.'),
(1669, 12, 73, 'Thanë (vëllezërit e Jusufit): “Pasha Perëndinë, ju e dini se na nuk kemi ardhur të bëjmë turbullira në tokë, dhe na nuk jemi vjedhësa”.'),
(1670, 12, 74, 'Ata pyetën: “E çfarë është dënimi, nëse ju gënjeni?”'),
(1671, 12, 75, '(Bijtë e Jakubit) thanë: “Dënimi për atë, në barrën e të cilit gjendet gota është vetë ai person (bëhet skllav i të vjedhurit një vit). Kështu i dënojmë ne zullumqarët”.'),
(1672, 12, 76, 'Dhe ai (thërritësi), nisi të kontrollojë barrët e tyre para barrës së vëllait të Jusufit, e pastaj nxori gotën nga barra e vëllait të atij. Na, e mësuam Jusufin kështu, që ta bëjë këtë mashtrim. Ai, sipas ligjit të mbretit nuk mund ta merrte (për skllav) vëllain e vet, përveç se me lejën e Perëndisë. Ne e ngrisim lart atë që duam Ne, e mbi çdo të ditur ka edhe më të ditur.'),
(1673, 12, 77, '(Bijtë e Jakubit) thanë: “Nëse ka vjedhë, po edhe më parë vjedhi vëllai i tij! E, Jusufi – heshti për këtë dhe nuk shpalli gjë, por mendoi në vetvete: “Ju jeni në pozitë më të keqe (sepse e keni vjedhë vëllaun)! E, Perëndia është më i dijshëm për ato që i përshkruani ju”.'),
(1674, 12, 78, '(Bijtë e Jakubit) thanë: “O ministër, ai e ka babën shumë plak, andaj merre ndonjërin prej nesh në vend të tij! Na po shohim se ti je bamirës”.'),
(1675, 12, 79, '(Ministri) tha: “Perëndia na ruajtë, - ta marrim na atë tjetrin në vend të atij te i cili e kemi gjetur sendin tonë! Atëherë, na, me të vërtetë, do të ishim të padrejtë!”'),
(1676, 12, 80, 'Meqë e humbën shpresën në të (Jusufin), u shpërndanë dhe bisedonin fshehurazi. Më i madhi nga ata, tha: “A nuk e dini që baba juaj, ka marrë prej jush betimin në Perëndinë, e madje, edhe më parë keni bërë krim ndaj Jusufit. Nuk do të ndahem prej kësaj toke, derisa të mos më lejojë babai im ose të mos më gjykojë Perëndia, e Ai është gjykatësi më i mirë.'),
(1677, 12, 81, 'Kthehuni te babai juaj dhe thuani: “O baba ynë, djali yt ka vjedhur. Na dëshmojmë vetëm për atë që dimë. Na nuk e kemi ditur një gjë të padukshme.'),
(1678, 12, 82, 'Pyeti banorët e qytetit në të cilin kemi qenë dhe karavanin me të cilin kemi udhëtuar, dhe ne jemi të drejtë”.'),
(1679, 12, 83, 'Jakubi tha: “Jo, por shpirti juaj e ka kurdisur krejt këtë. Durimi im është, durim i bukur. Shpresoj se Perëndia do t’mi kthejë të gjithë, sepse Ai, me të vërtetë, është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(1680, 12, 84, '(Jakubi) u kthye prej tyre dhe tha: “O i mjeri unë për Jusufin!” Sytë iu kishin zbardhur prej pikëllimit dhe ishte plot zemërim.'),
(1681, 12, 85, '(Bijtë e Jakubit) thanë: “Pasha Perëndinë, ti po e përmend Jusufin papra, saqë do të sëmuresh rëndë ose do të vdesish!”'),
(1682, 12, 86, '(Jakubi) tha: “Unë, pikëllimin dhe zemrimin tim ia paraqes vetëm Perëndisë, e nga Perëndia e di atë që nuk e dini ju”.'),
(1683, 12, 87, '“O bijtë e mi, shkoni e hulumtonie Jusufin dhe vëllain e tij dhe mos e humbni shpresën në mëshirën e Perëndisë. Me të vërtetë, shpresën në mëshirën e Perëndisë nuk e humbin, përpos populli mohues”.'),
(1684, 12, 88, 'Dhe kur ata dolën para Jusufit, thanë: “O ministër, neve dhe familjen tonë e ka goditur një fatkeqësi e madhe e kemi sjellë pak gjëra! E, ti na plotëso ushqimin tonë dhe bënë mirësi me ne! Se Perëndia, me të vërtetë, i shpërblen dhënësit e lëmoshës”.'),
(1685, 12, 89, '(Jusufi) tha: “A dini çka keni bërë me Jusufin dhe vëllain e tij, kur nuk keni qenë të vetëdijshëm?”'),
(1686, 12, 90, 'Ata thanë: “A mos qenke ti vallë, me të vërtetë, Jusufi?” Ai u përgjigj: “Po, unë jam Jusufi, e ky është vëllai im. Perëndia na ka bërë mirësi neve. Me të vërtetë, ai që ruhet nga të këqijat dhe bëhet i durueshëm, Perëndia, me të vërtetë, kurrë nuk ua humbë shpërblimin bamirësve.'),
(1687, 12, 91, 'Ata thanë: “Pasha Perëndinë, me të vërtetë, Perëndia të ka lartësuar ty mbi ne, e na, me të vërtetë, kemi qenë të gabueshëm.'),
(1688, 12, 92, '(Jusufi) u tha: “Sot, s’ka qortim për ju; Perëndia do t’ju falë. Ai është më i mëshirshmi i mëshiruesve!'),
(1689, 12, 93, 'Shkoni me këtë këmishë time dhe vënie në fytyrën e babës tim e ai do të shohë, dhe m’i sillni tërë familjet tuaja!'),
(1690, 12, 94, 'E, kur karavani u largua nga toka (e Egjiptit), babai i tyre tha: “Unë, me të vërtetë, po ndjejë erën e Jusufit, sikur ju të mos më quanit matuf”.'),
(1691, 12, 95, 'Ata thanë: “Pasha Perëndinë, me të vërtetë, ti edhe tani qëndron në gabimin e mëparshëm”.'),
(1692, 12, 96, 'E, kur erdhi sihariquesi, ia vuri këmishën në fytyrën e tij (Jakubit), e atij iu kthye të pamurit. (Atëherë), tha (Jakubi): “A nuk ju kam thënë, se unë di nga Perëndia atë që nuk e dini ju”.'),
(1693, 12, 97, 'Ata thanë: “O babai ynë, kërko falje për ne (nga Perëndia) për gabimet tona; ne, me të vërtetë, kemi qenë të gabueshëm”.'),
(1694, 12, 98, 'Ai u përgjegj: “Do ta lus Zotin tim që t’ju falë. Me të vërtetë, Ai është falës dhe mëshirues”.'),
(1695, 12, 99, 'Dhe kur ata hyn te Jusufi, i afroi pranë vedit prindërit e vet dhe tha: “Hyni në qytetin e Egjiptit! Me lejen e Perëndisë, ju do të jeni të sigurtë prej çdo të keqe”.'),
(1696, 12, 100, 'Dhe ai i ngriti prindërit e vet në fron (në një vend më të lartë), dhe ata të gjithë (prindërit dhe vëllezërit) iu përulën atij. (Jusufi) tha: “O baba im, ky është komentimi i ëndrrës sime të dikurshme. Zoti im e plotësoi atë. Dhe mua më bëri mirësi, kur më nxori nga burgu, e juve u solli nga shkretëtira, pasi djalli pat futur ngatërresa në mes meje dhe vëllezërve të mi. Me të vërtetë, Zoti im u bën lehtësi kujt të dojë. Me të vërtetë, Ai është i Plotëdijshëm dhe i Gjithëdijshëm!'),
(1697, 12, 101, 'O Zoti im! Ti më ke dhënë pushtetin dhe më ke mësuar komentimin e ngjarjeve! (O) krijues i qiejve dhe i Tokës, Ti je Mbrojtësi im edhe në këtë edhe në botën tjetër! Bën që të janë të mirë!”'),
(1698, 12, 102, 'Këto janë lajmet e ngjarjeve të padukshme, të cilat Ne po t’i shpallim ty (o Muhammed), e ti nuk ke qenë me ata kur vendosën për punën e tyre, e ata bënin dredhira.'),
(1699, 12, 103, 'E, shumica e njerëzve, sado që të dëshirosh ti, nuk janë besimtarë.'),
(1700, 12, 104, 'Ti nuk po kërkon prej tyre kurrfarë shpërblimi për këtë. Ai (Kur’ani), është vetëm këshillë për botën.'),
(1701, 12, 105, 'E, sa shumë dokumente ka në qiej dhe në Tokë pranë të cilëve kalojnë ata, e ata shmangën nga ato, e nuk mendojnë!'),
(1702, 12, 106, 'Shumica e tyre nuk besojnë në Perëndinë, por i bëjnë shok Atij.'),
(1703, 12, 107, 'A janë të sigurtë ata se nuk do t’i godas fatkeqësia – dënim nga Perëndia, ose që do t’i arrijë ata papritmas çasti (Dita e Kijametit), e që ata as që do ta hetojnë atë?'),
(1704, 12, 108, 'Thuaj: “Kjo është rruga ime, unë thërras (në besimin) e Perëndisë, duke pasur argumente të qarta, unë dhe çdonjëri që më pason mua, dhe qoftë lavdëruar Perëndia, unë nuk jam prej idhujtarëve!”'),
(1705, 12, 109, 'E, Na kemi dërguar edhe para teje vetëm burra, nga banorët e qyteteve, të cilëve ua dërguam Shpalljen. E, vallë! A nuk kanë udhëtuar ata nëpër Botë, e të shohin se si ka qenë fundi i atyre që ishin para tyre? E, bota tjetër është, me të vërtetë, më e mirë për ata që i druajnë Perëndisë. A nuk po mendoni?'),
(1706, 12, 110, 'Dhe kur pejgamberët gati e humbën shpresën dhe menduan se do të shpallen për gënjeshtarë, atëherë i arriti ndihma Jonë: Na e shpëtojmë atë që duam Ne, e nuk zmbrapset dënimi Ynë nga populli mëkatarë.'),
(1707, 12, 111, 'Në tregimet e atyre (pejgamberëve) ka këshilla për ata që janë mendarë. Kur’ani nuk është tregim i trilluar, por ai është vërtetues i (librave) që janë shpallë para tij dhe shpjegues i çdo gjëje, dhe udhërrëfyes e mëshirë për popullin besimtarë.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Elif Lám Mim Râ. Këto janë versetet e Librit (Kur’anit)! E, ajo që të është shpallë ty prej Zotit tënd është e Vërtetë, por shumica e njerëzve nuk e besojnë.'),
(1709, 13, 2, 'Perëndia është ai që qiejt i ka ngritur pa shtylla, e ju i shihni, e ka mbizotëruar Arshin, dhe ka nënshtruarn Diellin dhe Hënën. Të gjitha këto rrotullohen për kohë të caktuar. Ai drejton të gjitha dhe i shpjegon argumentet, dhe për t’u bindur ju që do të takoheni me Zotin tuaj.'),
(1710, 13, 3, 'Ai është, i cili e ka shtrirë Tokën, duke krijuar në të male dhe lumenj. Dhe nga çdo lloj fruti krijoi çifte nga dy; Ai bëri që nata ta mbulojë ditën. Me të vërtetë, në këto ka dokumente për njerëzit që mendojnë.'),
(1711, 13, 4, 'Në Tokë ka vise që janë të lidhura njëri me tjetrin dhe ka kopshtije të mbjellura me hardhi, dhe ara dhe palme me një pip ose shumë pipa; ujiten me të njëjtin ujë, e disa nga frutet i bëjnë më të shijshme nga të tjerat. Me të vërtetë, në këto ka dokumente për njerëzit që kanë mend.'),
(1712, 13, 5, 'E, nëse çuditesh, pra – çudi janë fjalët e tyre: “A vallë, me të vërtetë pasi të bëhemi tokë, do të krijohemi përsëri?” Ata, nuk besojnë në Zotin e tyre, këta janë ata, në qafat e të cilëve do të ketë pranga; këta janë banorët e xhehennemit, në të cilin do të qëndrojnë përherë.'),
(1713, 13, 6, 'Ata e kërkojnë me nguti prej teje të keqen para të mirës, e para tyre ka pasur shumë dënime. Zoti yt është falës për njerëzit përkundër zullumeve të tyre, por Zoti yt, me të vërtetë, është edhe dënues i ashpër.'),
(1714, 13, 7, 'E, mohuesit thonë: “Sikur t’i ishte dërguar atij një mrekulli nga Zoti i tij?” ti je vetëm paralajmërues. E, për çdo popull ka udhërrëfyes (në rrugë të drejtë).'),
(1715, 13, 8, 'Perëndia di se ç’mban (në barkun e saj) çdo femër, dhe di se sa shkurtohet dhe sa zgjatet (koha e lindjes). Dhe, çdo gjë tek Ai është me masë.'),
(1716, 13, 9, 'Ai di gjërat e dukshme dhe të padukshme. Ai është i Madhërueshëm dhe i Lartë.'),
(1717, 13, 10, 'Tek Ai, ju jeni të barabartë: qoftë se flisni në vete apo flisni me zë të lartë, si dhe ai që fshihet natën apo ai që ecë ditën.'),
(1718, 13, 11, 'Njeriu ka përcjellës (engjëj), para tij dhe pas tij, - e mbikëqyrin atë me urdhërin e Perëndisë. Me të vërtetë, Perëndia nuk ua ndryshon atë që kanë nga e mira në të keqe. Kur Ai don ta ndëshkojë një popull, s’ka kurrfarë pengese për te. Dhe ata, përpos Tij (Perëndisë), s’kanë kurrfarë ndihmësi.'),
(1719, 13, 12, 'Ai ua dëfton juve vetëtimën, duke u frikësuar dhe shpresuar ju, dhe Ai krijon re të rënda (me shi).'),
(1720, 13, 13, 'Edhe bubullima e falenderon Ate (Perëndinë), por edhe engjëjt, nga droja ndaj Tij; Ai dërgon rrëfetë dhe godet me ato kë të dojë; - dhe ata përsëri diskutojnë për Perëndinë, e Ai është i ashpër në ndëshkime.'),
(1721, 13, 14, 'Vetëm për Te është lutja e vërtetë! E, ata (mohuesit) që i luten tjetërkujt – përpos Perëndisë, nuk u përgjigjet atyre me kurrgjë, ashtu sikundër ai që i zgjatë dy duart tek uji (pranë pusit), për t’i ardhur te goja; por, ai, kurrsesi nuk i vjen. Lutja e mohuesve është e kotë.'),
(1722, 13, 15, 'Perëndisë i bëjnë sexhde të gjithë ata që gjenden në qiej dhe në Tokë, dashtë e padashtë, si dhe hijet e tyre (bëjnë sexhde), në mëngjes dhe në mbrëmje.'),
(1723, 13, 16, 'Thuaj: “Kush është Zoti i qiellërave dhe i Tokës?” – dhe përgjigju: “Perëndia!” Thuaj: “A mos vallë, i merrni ju, përveç Tij, për miq, ata që vetes nuk mund t’i sjellin as dobi as dëm?” Thuaj: A janë të njëjtë i verbëti dhe ai që sheh, ose, a janë të njëjtë errësirat dhe drita, ose, mos vallë, ata që i kanë bërë shok Perëndisë, a kanë krijuar siç ka krijuar Ai, e që t’ju bëhen krijesat e ngjashme atyre?” Thuaj: “Perëndia, është krijues i çdo gjëje dhe Ai është i Vetmi dhe Triumfues”.'),
(1724, 13, 17, 'Ai lëshon shiun nga qielli, andaj rrjedhin lumenjtë me masë (të caktuar), dhe vala i bartë mbeturinat mbi sipërfaqe të ujit. Edhe atë që e shkrijnë njerëzit në zjarr, me qëllim që të përfitojnë stoli ose vegla, është mbeturinë (e pavlefshme), si ajo (në sipërfaqe të ujit). Kështu, Perëndia, jep shembullin e së vërtetës dhe të pavërtetës: Mbeturinat hudhën, e ajo që u shërben njerëzve mbetet në tokë. Kështu, Perëndia u shpjegon shembujt.'),
(1725, 13, 18, 'Për ata që i gjegjen Zotit të tyre, i gjen e mira e madhe, e ata që nuk i gjegjen Atij – sikur të ishte e tyre e gjithë ç’ka në Tokë, dhe edhe aq (më tepër), ata do ta sakrifikonin atë (për të shpëtuar). Të atillëve, u është përgatitur llogari e rëndë; e strehimi i tyre është xhehennemi. E, sa shtrat i shëmtuar që është ai!'),
(1726, 13, 19, 'A mos vallë, ai që di se e vërteta që të është shpallë ty nga Zoti yt është njësoj sikur ai që është i verbër? Vetëm të mençurit mendojnë:'),
(1727, 13, 20, 'Ata që kryejnë obligimin ndaj Perëndisë dhe nuk e prishin premtimin;'),
(1728, 13, 21, 'dhe ata që ruajnë atë që ka urdhëruar Perëndia që të ruhet dhe i druajnë Zotit të tyre, dhe i frikohen llogarisë së rëndë;'),
(1729, 13, 22, 'dhe ata që durojnë për të fituar dashurinë e Zotit të tyre, dhe që falin namazin, dhe nga ajo që ua kemi dhënë Ne – ndajnë (japin) fshehurazi dhe haptazi, dhe kthejnë të keqen me të mirë – për ta ka përfundim të mirë,'),
(1730, 13, 23, 'Xhenneti i Adnit, në të cilin do të hyjnë ata, dhe prindërit e tyre, dhe gratë e tyre, dhe pasardhësit e tyre – ata ë kanë qenë të mirë (që kanë besuar), e engjëjt do të hyjnë tek ata nga çdo derë:'),
(1731, 13, 24, '(e ju thonë): “Shpëtimi qoftë mbi ju, meqë keni duruar, e sa përfundim i bukur që është ky!”'),
(1732, 13, 25, 'E, ata që prishin obligimin ndaj Perëndisë, pasi që janë zotuar për atë, e që këputin atë që ka urdhëruar Perëndia të ruhet, dhe bëjnë çrregullime në Tokë – për ata ka mallkim dhe përfundim të keq!'),
(1733, 13, 26, 'Perëndia ia zgjëron furnizimin kujt të dojë dhe ia pakson. Ata i gëzohen jetës së kësaj bote, por jeta e kësaj bote në krahasim me botën tjetër, është vetëm kënaqësi transite (e përkohshme).'),
(1734, 13, 27, 'Mohuesit thonë: “Sikur t’i ishte dërguar atij një mrekulli nga Zoti i tij?” Thuaj: “Me të vërtetë, Perëndia e shpie në humbje atë që don (meqë është zhytur në të keqe), ndërsa e udhëzon në rrugë të drejtë atë që kthehet tek Ai,'),
(1735, 13, 28, 'ata që besojnë dhe zemrat e të cilëve, kur përmendet Perëndia, qetësohen – e, me të vërtetë, kur të përmendet Perëndia, zemrat qetësohen!'),
(1736, 13, 29, 'Ata që besojnë dhe punojnë vepra të mira, të lumtë ata; për ta ka vendbanim të mrekullueshëm!'),
(1737, 13, 30, 'Dhe, kështu të dërguam Ne ty te një popull, para të cilit kanë kaluar shumë popuj, që t’jua lexosh ti atyre, atë që ta kemi shpallur Na ty, sepse ata nuk besojën në Mëshiruesin (Perëndinë). Thuaj: “Ai (Mëshiruesi) është Zoti im, s’ka zot tjetër përveç Tij, tek Ai mbështetem dhe tek Ai kthehem!”'),
(1738, 13, 31, 'Sikur me ndonjë Kur’an të lëviznin malet ose me të – të copëtohet toka ose me të – të flisnin të vdekurit (të ngjalleshin, atëherë, edhe me këtë Kur’an do të bëheshin këto). Por, të gjitha i takojnë Perëndisë. Vallë, a nuk e dinë ata që besojnë që – sikur të donte Perëndia do t’i kishte udhëzuar të gjithë njerëzit (në besim). Kurse, mohuesit për atë që kanë punuar, do të vazhdonte t’i godasë fatkeqësia, ose ajo (fatkeqësia) do të ngjajë pranë shtëpive të tyre, - përderisa të realizohet premtimi i Perëndisë (atëherë zhdukën tërësisht). Perëndia, me siguri, e realizon premtimin.'),
(1739, 13, 32, 'Edhe para teje, janë tallë me pejgamberë, andaj Unë u kam dhënë afat atyre që kanë mohuar, e pastaj i kam ndëshkuar, e çfarë ka qenë dënimi Im!'),
(1740, 13, 33, 'A mos vallë, Atij që është mbikëqyrës për çdokënd – çka punon, mund t’i bëhet shok – ai që nuk di asgjë, (siç konsiderojnë mohuesit)? Ose ju doni ta lajmëroni Ate, për atë çka nuk di Ai (Perëndia) në Tokë, (kurse Ai di edhe për më të voglën imtësi), ose (po i emroni ju ata) me fjalë boshe? Jo, por mohuesve dinakëritë e veta i hijeshohen dhe ata janë larguar nga rruga e drejtë, e kë e shmangë Perëndia, për te s’ka udhërrëfyes.'),
(1741, 13, 34, 'Për ta ka dënim në këtë jetë, por dënimi në jetën tjetër, me siguri, është edhe më i ashpër, dhe për ta, s’ka mbrojtës nga dënimi i Perëndisë.'),
(1742, 13, 35, 'Xhenneti i tillë, që u është premtuar muslimanëve: ka (kopshtije) nëpër të cilat rrjedhin lumenj, frutet e kopshtit janë të përhershme, si dhe freskia e tij. Ky është mbarimi (epilogu) i besimtarëve, kurse fundi i mohuesve është zjarri.'),
(1743, 13, 36, 'E, atyre që ua kemi zbritur Librin, ata gëzohen për atë që të kemi shpallë ty. Dhe (ka) disa prej grupeve që mohojnë diçka prej tij (Kur’anit). Thuaj: “Mua më është urdhëruar që ta adhuroj vetëm Perëndinë dhe që mos t’i bëj shok Atij. Vetëm Atij i lutëm dhe vetëm tek Ai kthehem.'),
(1744, 13, 37, 'Ja, kështu, Na e kemi zbritur Kur’anin – ligj (të mençurisë së shprehur) në gjunë arabe. E, nëse ti ndjekë dëshirat e tyre, pas njohurisë që të ka ardhur, ti nuk do të kesh as ndihmës as mbrojtës prej (dënimit të) Zotit.'),
(1745, 13, 38, 'Na, me të vërtetë, edhe para teje kemi dërguar pejgamberë dhe u kemi dhënë gra dhe pasardhës. Asnjë pejgamber nuk ka sjellë me vete asnjë mrekulli, përveç me lejen e Perëndisë. Çdo periudhë e kohës ka dispozita ligjore.'),
(1746, 13, 39, 'Perëndia shlyen çka të dojë e themelon çka të dojë; dhe tek Ai është Libri kryesor.'),
(1747, 13, 40, 'Qoftë të të tregojmë Na ty, nga ajo që u kemi premtuar atyre, ose ta marrim shpirtin Na ty, ti je i obliguar vetëm ta shpallish (profetninë tënde e jo dënimin e tyre), se llogaria është te Ne.'),
(1748, 13, 41, 'A nuk po shohin ata, që Ne po ua shkatërrojmë tokën (mohuesve), duke i zvogluar skajet e saj. Perëndia gjykon, dhe s’ka askush që mund ta thyejë gjykimin e Tij dhe Ai është llogaritës i shpejtë.'),
(1749, 13, 42, 'Dredhi kanë bërë edhe ata që kanë qenë para tyre, e Perëndia është zotërues i të gjitha dredhive. Ai di çka punon çdokush dhe mohuesit do ta dinë se për kë do të jetë përfundimi më i mirë.'),
(1750, 13, 43, 'Mohuesit thonë: “Ti nuk je pejgamber!” Thuaj (o Muhammed!): “Mjafton Perëndia për dëshmitar në mes meje dhe jush, dhe Ai që e di Librin”.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'Elif Lâm Râ. Ky është Libri, që ta kemi shpallë ty, që njerëzit t’i nxjerrish nga errësira në dritë, me lejen e Zotit të tyre, në rrugën e të Plotëfuqishmit dhe të Falenderueshmit,'),
(1752, 14, 2, 'Perëndi, të cilit i përkasin çka ka në qiej dhe në Tokë. E, mjer për mohuesit nga dënimi i tmerrshëm (që do t’i godasë),'),
(1753, 14, 3, 'ata që jetën e kësaj bote e duan më shumë se atë të botës tjetër, dhe pengojnë njerëzit nga rruga e Perëndisë dhe dëshirojnë ta shtrembërojnë atë! Ata janë në humbje të madhe.'),
(1754, 14, 4, 'Na nuk kemi dërguar asnjë pejgamber, përpos në gjuhën e popullit të tij, për t’u shpjeguar atyre (e ta kuptojnë). Perëndia e shpie në humbje kë të dojë dhe e udhëzon në rrugë të drejtë kë të dojë. Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(1755, 14, 5, 'Na e dërguam Musain me argumentet Tona (dhe i thamë atij): “Nxirre popullin tënd nga errësira në dritë dhe përkujtoju atyre dëtët e Perëndisë (ngjarjet e mira dhe të këqija)!” Me të vërtetë, në këtë, ka argumente për çdonjërin që është i durueshëm dhe falenderues.'),
(1756, 14, 6, '(Kujtoje) kur Musai i tha popullit të vet: “Kujtoni dhuntinë që ju dha Perëndia, kur (Ai) ju shpëtoi nga populli i Faraonit, të cilët ju munduan me torturat më të rënda, e i prenin djemtë tuaj, e ju linin në jetë femrat (tuaja), - kjo ka qenë një sprovë e madhe për ju nga Zoti juaj, -'),
(1757, 14, 7, '(Kujtoje) dhe kur Zoti juaj parashtroi: “Nëse bëheni mirënjohës, Unë, me siguri, do t’ju jap edhe më shumë; e nëe bëheni mohues, (do të dënoheni). Me të vërtetë, dënimi Im është i rëndë”.'),
(1758, 14, 8, 'Dhe Musai ka thënë: “Nëse nuk besoni, ju dhe të gjithë ç’janë në Tokë, - Perëndia, me të vërtetë, është i pasur dhe plot lavdi (s’ka nevojë për falenderimin tuaj)”.'),
(1759, 14, 9, 'A nuk u ka ardhur juve lajmi për ata që kanë qenë para jush: për popullin e Nuhut, dhe të Adit, dhe të Themudit dhe për ata pas tyre? – Vetëm Perëndia i di ata! – Pejgamberët e tyre kanë ardhur me dokumente të qarta, por ata venin duart e tyre në gojë (me tallje dhe pezm) dhe thonin: “Na nuk besojmë me atë që jeni dërguar ju. Na jemi me dyshim të madh ndaj asaj që na thirrni ju!”'),
(1760, 14, 10, 'Pejgamberët e tyre u kanë thënë: “Vallë a mund të dyshohet në Perëndinë, Krijuesin e qiejve dhe të Tokës? Ai u thërret juve për t’ju falur disa mëkate tuaja dhe që t’ju lë juve deri në afatin e caktuar”. Ata (jobesimtarët) thanë: “Ju jeni vetëm njerëz si ne. Ju dëshironi të na shmangëni nga ajo që kanë adhuruar të parët tanë. Andaj, na sillni mrekulli të dukshme!”'),
(1761, 14, 11, 'Pejgamberët e tyre, u thanë: “Na jemi vetëm njerëz si ju, por Perëndia e cilëson (me pejgamberllëk) kë të dojë prej robërve të Vet, ne nuk mund t’u sjellim mrekulli pa urdhërin e Perëndisë, e besimtarët le të mbështeten vetëm në Perëndinë.'),
(1762, 14, 12, 'E, përse të mos mbështetem (shpresojmë) në Perëndinë, kur Ai na ka dëftuar rrugën e drejtë në të cilën po ecim? Na, me të vërtetë, do të durojmë në atë që na mundoni ju – e ata që mbështeten (shpresojnë), le të shpresojnë vetëm në Perëndinë!”'),
(1763, 14, 13, 'Dhe mohuesit u thanë pejgamberëve të tyre: “Na do t’ju dëbojmë juve nga toka e jonë, ose do të ktheheni ju në fenë tonë!” Andaj, Zoti (pejgamberëve të Vet) u shpalli: “Na, me të vërtetë, do t’i zhdukim zullumqarët,'),
(1764, 14, 14, 'e juve do t’ju vendosim në tokën e tyre – pas tyre. Kjo (dhuratë) është për ata që i druajnë Madhërisë Sime dhe i frikohen premtimit (dënimit) Tim”.'),
(1765, 14, 15, '(E, pejgamberët) kërkuan ndihmë (nga Perëndia), andaj është shkatërruar çdo kokëfortë e kryelartë, -'),
(1766, 14, 16, 'para tyre është xhehennemi – dhe ai do të furnizohet me ujë të qelbët,'),
(1767, 14, 17, 'të cilin e hërpatin, e nuk mund ta përbijnë. E, vdekja u vie atyre nga të gjitha anët, por ata nuk vdesin; dhe pas tij ka dënim të rëndë.'),
(1768, 14, 18, 'Vepra e atyre që nuk besojnë Zotin e tyre, i shembëllen hirit, që në ditën e stuhishme bartet nga era. Nuk do të kenë asnjë dobi nga veprat që kanë punuar. Kjo është humbje e madhe.'),
(1769, 14, 19, 'Vallë, a nuk sheh ti se si Perëndia ka krijuar përsosmërisht qiejtë dhe Tokën? E, nëse Ai don, do t’ju zhdukë juve dhe do të sjellë popull të ri;'),
(1770, 14, 20, 'kjo nuk është punë e rëndë për Fuqiplotin Perëndi.'),
(1771, 14, 21, 'Dhe të gjithë do të dalin para Perëndisë. (Atëherë), ata që kanë qenë të shfrytëzuar, do t’u thonë atyre që janë kryelartësuar: “Na kemi qenë ithtarët tuaj. A do të mund të largoni ju prej nesh diçka nga dënimi i Perëndisë?” Ata thonë: “Sikur të na kishte udhëzuar Perëndia në rrugën e drejtë, edhe na do t’ju kishim udhëzuar juve. Rrënkojmë ne apo durojmë, - njësoj na është neve, nuk do të ketë shpëtim për ne”.'),
(1772, 14, 22, 'Dhe, do të thotë djalli, kur të merr fund puna: “Me të vërtetë, Perëndia u ka premtuar juve premtimin e vërtetë. Edhe unë ju kam premtuar, por ju kam mashtruar. Por, unë nuk kam pasur kurrfarë pushteti mbi ju, përpos që ju kam thirrur, e ju më jeni përgjegjur. Prandaj, mos më qortoni, por qortonie veten! Unë nuk jam shpëtimtari juaj, e as ju nuk jeni shpëtimtari im. Unë, tani mohoj atë që ju më parë më patët bërë shok (me Perëndinë)”. Me të vërtetë, për zullumqarët ka dënim të dhembshëm.'),
(1773, 14, 23, 'E, ata që besojnë dhe punojnë vepra të mira, do të hyjnë në kopshtije, nëpër të cilat rrjedhin lumenjt, ku ata, me lejen e Zotit të tyre, do të banojën përherë. Në to, do të përshëndeten me fjalën: “Selam!”'),
(1774, 14, 24, 'Vallë a nuk sheh se si Perëndia jep shembullin – fjala e bukur si druri i bukur: rrënja është e fortë në tokë, kurse degët kah qielli;'),
(1775, 14, 25, 'që me lejen e Zotit të saj jep frut në çdo kohë. Dhe Perëndia u tregon shembullin njerëzve për t’u këshilluar ata.'),
(1776, 14, 26, 'Fjala e shëmtuar është si druri i shëmtuar: i shëmbur për dhé dhe i paqëndrueshëm për ekzistencë.'),
(1777, 14, 27, 'Perëndia besimtarët i forcon me fjalë të qëndrueshme, në këtë botë dhe në botën tjetër, kurse zullumqarët i shpie në humbje; Perëndia punon ç’të dojë.'),
(1778, 14, 28, 'Vallë, a nuk i sheh ata që dhuntinë e Perëndisë e kanë ndërruar me mohim dhe e kanë sjellë, popullin e tyre në Shtëpinë e shkatërrimit, -'),
(1779, 14, 29, 'në xhehennem, në të cilin do të digjen – e sa vendbanim i tmerrshëm është ai! –'),
(1780, 14, 30, 'dhe i kanë trilluar shok Perëndisë për t’i larguar (njerëzit tjerë) nga rruga e Tij. Thuaj: “Kënaquni, se kthimi juaj është në zjarr!”'),
(1781, 14, 31, 'Thuaju robërve të Mi, të cilët besojnë: Le të falin namazin dhe le të japin fshehurazi dhe haptazi nga ajo që ua kemi dhënë Ne, para se të arrijë dita, në të cilën s’ka shitje as miqësi.'),
(1782, 14, 32, 'Perëndia është Ai që ka krijuar qiejt dhe Tokën; Ai zbret ujin nga qielli e me të jep frutet për t’u ushqyer ju; dhe u jep juve që të shfrytëzoni anijet për të lundruar me to nëpër dete, me autorizimin e Tij, dhe u jep që të shfrytëzoni lumenjt;'),
(1783, 14, 33, 'dhe ju jep që të shfrytëzoni Diellin dhe Hënën, që lëvizin vazhdimisht, dhe ju jep që të shfrytëzoni natën dhe ditën;'),
(1784, 14, 34, 'dhe ju jep nga të gjitha ato që keni kërkuar nga Ai (sipas nevojave tuaja), e nëse doni t’i numëroni dhuntitë e Perëndisë, atëherë nuk do të mund t’i numëroni. – Njeriu, me të vërtetë, është zullumqarë dhe jomirënjohës.'),
(1785, 14, 35, '(Kujtoje) kur Ibrahimi tha: “O Zoti im, bëne të sigurt këtë qytet dhe më largo mua dhe bijtë e mi nga të adhuruarit e statujave,'),
(1786, 14, 36, 'O Zoti im, ata kanë shmangur shumë njerëz (nga rruga e drejtë). Ai që më pason mua, është i fesë sime; e ai që më kundërshton mua, - Ti, pra, me të vërtetë, je falës dhe mëshirues.'),
(1787, 14, 37, 'O Zoti ynë, unë, me të vërtetë, i kam vendosur disa pasardhës të mi në një luginë ë nuk mbillet, te Shtëpia e Jote e shenjtë; o Zoti ynë (i kam vendosur këtu) për të falur namazin; andaj, bënë që zemrat e disa njerëzve të nxitojnë kah ata dhe furnizoji ata me frute, për t’u bërë falenderues ata.'),
(1788, 14, 38, 'O Zoti ynë, Ti, me të vërtetë, di ato që fshehim e çka shfaqim ne haptazi. Nuk i fshehet asgjë Perëndisë as në Tokë as në qiell.'),
(1789, 14, 39, 'Qoftë lavdëruar Perëndia, i cili në pleqëri më dhuroi Ismailin dhe Is’hakun; Zoti im, me të vërtetë, është pranues i lutjeve.'),
(1790, 14, 40, 'O Zoti im, bën që unë të kryejë përherë namazin dhe ashtu disa pasardhës të mi! O Zoti ynë, pranoje lutjen time!'),
(1791, 14, 41, 'O Zoti ynë, më fal mua dhe prindërit e mi dhe besimtarët – në Ditën kur do të jepet llogari!”'),
(1792, 14, 42, 'E ti, mos mendo, kurrsesi, që Perëndia nuk vëren atë që punojnë zullumqarët! Ai, vetëm ua shtyen (dënimin) dei në Ditën kur sytë e tyre do të mbeten të hapur,'),
(1793, 14, 43, 'dhe duke vrapuar, me kokë ngrehur, nuk mund t’i lëvizin sytë (e shikojnë me ngulmim); e zemrat e tyre do të jenë të zbrazëta (nga tmerri).'),
(1794, 14, 44, 'Ti, paralajmëroji njerëzit për Ditën që atyre do t’u vie dënimi, e zullumqarët do të thonë: “O Zoti ynë, na lë (kthena në jetë) neve vetëm edhe pak kohë; na do t’i pëgjigjemi thirrjes Tënde dhe do t’i pasojmë pejgamberët!” (Ju thuhet atyre): “E vallë, a nuk jeni betuar më parë se nuk ka për ju zhdukje?'),
(1795, 14, 45, 'Dhe keni banuar në shtëpitë e atyre, të cilët i kanë bërë zullum vetes dhe e keni pasur të qartë se si kemi vepruar me ta. Dhe, Na, ju kemi treguar shumë shembuj”.'),
(1796, 14, 46, 'Dhe ata thurin dredhitë e tyre, e te Perëndia është dënimi i dredhive të tyre; për dredhitë e atyre nuk mund t’i lëkundin malet.'),
(1797, 14, 47, 'As mos mendo se Perëndia nuk do ta mbajë premtimin e Vet (që u ka dhënë) pejgamberëve të Vet – se, Perëndia, me të vërtetë, është i Plotëfuqishëm dhe ndëshkon –'),
(1798, 14, 48, 'në Ditën kur Toka do të ndërrohet me tokë tjetër, por edhe qiejt, e kur të gjithë të dalin para të Vetmit dhe të Plotëfuqishmit Perëndi.'),
(1799, 14, 49, 'Dhe atë ditë do t’i shohësh mëkatarët të lidhur në pranga;'),
(1800, 14, 50, 'Këmishat do t’i kenë prej katranit, e zjarri do t’ua mbështjellë fytyrat e tyre, -'),
(1801, 14, 51, '(Kështu) Perëndia shpërblen çdo njërin për atë që ka punuar. Perëndia, me të vërtetë, ëshë llogaritar i shpejtë.'),
(1802, 14, 52, 'Ky (Kur’ani) është shpallë për njerëzit, dhe me të paralajmërohen ata, që ta dinë se Ai është i vetmi Perëndi, dhe për t’u këshilluar njerëzit e mençur!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Elif Lâm Râ. Këto janë ajetet e Librit, të Kur’anit të qartë!'),
(1804, 15, 2, 'Shpeshherë mohuesit do të dëshironin që të ishin muslimanë.'),
(1805, 15, 3, 'Lëri ata që të hanë dhe le të kënaqen, dhe lë t’i mashtrojë shpresa; ata do ta dinë (fundin)!'),
(1806, 15, 4, 'Na nuk i kemi zhdukur banorët e asnjë vendi, për pa ardhur koha e caktuar,'),
(1807, 15, 5, 'asnjë popull nuk mund as ta shpejtojë as ta ngadalësojë (caktimin Tonë për të).'),
(1808, 15, 6, 'Ata thanë: “O ti, që të është shpallë Kur’ani, ti je, me të vërtetë, i marrë!'),
(1809, 15, 7, 'Të na sillshe engjëjt, nëse je i drejtë?”'),
(1810, 15, 8, 'Na i dërgojmë engjëjt vetëm me të Vërtetën (me dënim të premtuar), dhe atëherë nuk u jepet afat për të pritur (por dënimi merr fund).'),
(1811, 15, 9, 'Na, me të vërtetë, kemi shpallë Kur’anin dhe, Na, me të vërtetë, do ta ruajmë atë (origjinalitetin e tij).'),
(1812, 15, 10, 'Na, me të vërtetë, u kemi dërguar pejgamberë edh para teje, - grupeve (popujve) të mëparshëm,'),
(1813, 15, 11, 'dhe asnjë pejgamber nuk ka ardhur e që ata të mos talleshin me të.'),
(1814, 15, 12, 'Ja, kështu Na, fusim (talljen) në zemrat e mëkatarëve,'),
(1815, 15, 13, 'ata nuk e besojnë atë (profetin Muhammed), e madje, ka kaluar ligji (mbi dënimin) e popujve të kaluar (për tallje me pejgamberët).'),
(1816, 15, 14, 'Sikur Ne ta hapnim një derë të qiellit dhe ata të ngjiteshin nëpër të,'),
(1817, 15, 15, 'do të thonin (ata): “Sytë na kanë qenë të mjegulluar, na jemi popull i magjepsur!”'),
(1818, 15, 16, 'Na kemi krijuar hyllësi në qiej dhe i kemi zbukuruar ato për shikuesin,'),
(1819, 15, 17, 'dhe i kemi ruajtur nga çdo djall i mallkuar;'),
(1820, 15, 18, 'përpos atij që vjedh të dëgjuarën, e arrinë atë një flakë e dukshme.'),
(1821, 15, 19, 'Dhe Tokën e kemi shtrirë dhe nëpër të kemi shpërndarë male të forta, dhe kemi bërë që në të, të zhvillohet çdo gjë në masa proporcionale,'),
(1822, 15, 20, 'dhe kemi bërë për ju në të (Tokë) sendet që u japin gjallëri dhe për ata që nuk i mbani (furnizoni) ju.'),
(1823, 15, 21, 'Nuk ekziston asnjë send, e që të mos gjendet ajo në thesarin Tonë, e nga ajo Na japim vetëm aq sa është e nevojshme.'),
(1824, 15, 22, 'Na dërgojmë erërat për të frytnuar, e zbresim shiun nga qielli e ju kemi furnizuar me ujë – dhe depot e tij (ujit) nuk gjenden tek ju.'),
(1825, 15, 23, 'Dhe, me të vërtetë, Na japim gjëllëri dhe vdekje, dhe vetëm Na jemi trashëgimtar (të përhershëm),'),
(1826, 15, 24, 'dhe vetëm Na dimë për ata që kanë qenë para jush, dhe vetëm Na dimë për ata që do të vijnë pas jush,'),
(1827, 15, 25, 'dhe, me të vërtetë, Zoti yt, do t’i tubojë; Ai, me të vërtetë, është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(1828, 15, 26, 'Na kemi krijuar njeriun prej një balte të fortë – të zezë, së cilës i është dhënë forma,'),
(1829, 15, 27, 'kurse xhindet i kemi krijuar që më parë nga zjarri i flakët.'),
(1830, 15, 28, '(Kujtoje) kur Zoti yt u tha engjëjve: “Unë do ta krijojë njeriun nga balta e zezë e fortë, e ndryshueshme (e figuruar),'),
(1831, 15, 29, 'e kur t’ia jap formën dhe i jap shpirtin (e bëhet i gjallë), ju bëni sexhde atij!”'),
(1832, 15, 30, 'Të gjithë engjëjt, bashkarisht, i bën sexhde atij,'),
(1833, 15, 31, 'përveç Iblisit. Ai refuzoi t’u bashkohet atyre që bënin sexhde.'),
(1834, 15, 32, '(Perëndia) tha: “O Iblis, ç’pate ti që nuk u bëre me ata që bënë sexhde?”'),
(1835, 15, 33, '(Iblisi) tha: “Nuk jam unë ai që t’i bëjë sexhde një njeriu të cilin Ti e ke krijuar nga balta e zezë, e fortë, e ndryshueshme (e figuruar), së cilës i është dhënë forma.'),
(1836, 15, 34, '(Perëndia) tha: “Atëherë, dil prej xhennetit, ti, në të vërtetë, je i mallkuar,'),
(1837, 15, 35, 'dhe le të jetë mallkimi mbi ty deri në Ditën e Kijametit!”'),
(1838, 15, 36, 'Iblisi tha: “Zoti im, më jep afat deri në ditën kur do të ringjallen ata (njerëzit)!”'),
(1839, 15, 37, '(Zoti) tha: “Ty, me të vërtetë, të është dhënë afat'),
(1840, 15, 38, 'deri në ditën e kohës së caktuar”.'),
(1841, 15, 39, '(Iblisi) tha: “Zoti im, meqë më ke shpie në humbje, unë në Tokë do t’ju zbukuroj atyre sendet dhe të gjithë do t’i shpie në mashtrim,'),
(1842, 15, 40, 'përveç robërve Tu të sinqertë”.'),
(1843, 15, 41, '(Perëndia) tha: “Kjo është rruga ime e drejtë.'),
(1844, 15, 42, 'Ti, me të vërtetë, nuk ke kurrfarë pushteti ndaj robërve të Mi, përveç atyre të humburve, që të pasojnë ty”.'),
(1845, 15, 43, 'Dhe, me të vërtetë, xhehennemi është vendbanimi i tyre i premtuar, për të gjithë:'),
(1846, 15, 44, 'ai i ka shtatë dyer, e për çdo derë ka pjesë të caktuar”.'),
(1847, 15, 45, 'Me të vërtetë, ata që ruhen nga mëkatet, do të gjenden në kopshtet e xhennetit pranë burimeve.'),
(1848, 15, 46, '(Ju thuhet): “Hyni në to me përshëndetje dhe të siguruar (prej frikës)”.'),
(1849, 15, 47, 'Dhe, Ne, do ta shporrim urrejtjen nga zemrat e tyre (që kanë pasur më parë), ata do të rrinë ulur si vëllezër në kanape (kolltuqe) – njëri përballë tjetrit,'),
(1850, 15, 48, 'aty, nuk do t’i gërget lodhja, ata prej atyhit kurrë nuk do të dëbohen.'),
(1851, 15, 49, 'Lajmëroji robërit e Mi (o Muhammed!) se Unë, me të vërtetë, jam Ai që falë dhe mëshiroj,'),
(1852, 15, 50, 'e që dënimi Im (për të këqinjtë), është me të vërtetë, dënim i dhembshëm!'),
(1853, 15, 51, 'Dhe lajmëroji për mysafirët e Ibrahimit,'),
(1854, 15, 52, 'kur, hynë tek ai dhe thanë: “Selam!” (Ibrahimi) tha: “Na po frikohemi nga Ju”.'),
(1855, 15, 53, 'Ata thanë: “Mos u friko se na po të sihariqojmë ty me një djalë të dijshëm”.'),
(1856, 15, 54, '(Ibrahimi) tha: “A mos vallë do të më sihariqoni, tani, kur më ka goditur pleqëria? Me çka do të më sihariqoni mua?”'),
(1857, 15, 55, 'Ata thanë: “Na do të sihariqojmë ty me një të Vërtetë që do të realizohet, e mos u bën shpresëhumbur!”'),
(1858, 15, 56, '(Ibrahimi) tha: “Kush mund ta humbë shpresën nga mëshira e Zotit të vet, përpos atyre që janë të humbur” –'),
(1859, 15, 57, 'dhe pyeti (Ibrahimi): “E, çfarë është qëllimi juaj, o të dërguar?”'),
(1860, 15, 58, '(Ata) thanë: “Na jemi dërguar te një popull mëkatarë, -'),
(1861, 15, 59, 'përpos familjes së Lutit (që nuk janë mëkatarë). Na do t’i shpëtojmë të gjithë ata (anëtarët e familjes së Lutit) –'),
(1862, 15, 60, 'përveç gruas së tij; për atë, Na kemi caktuar dënim, të cilin do ta shiojë ajo”.'),
(1863, 15, 61, 'Dhe, kur të dërguarit erdhën te Luti,'),
(1864, 15, 62, '(Luti) tha: “Ju, me të vërtetë, jeni njerz të panjohur!”'),
(1865, 15, 63, 'Ata thanë: “Jo, na të kemi sjellur atë, në të cilën ata (mohuesit nga populli yt) dyshojnë,'),
(1866, 15, 64, 'të sjellim atë që do të ngjajë me siguri, e ne, me të vërtetë, jemi të sinqertë.'),
(1867, 15, 65, 'Udhëto ti me familjen tënde, në një kohë të natës, e shko ti pas tyre, e askush prej jush të mos shikojë prapa, por vazhdoni në drejtim të urdhëruar!”'),
(1868, 15, 66, 'Dhe Ne, ia kemi shpallur atij, atë që do të ngjajë: që ata të gjithë – deri në të mbramin, do të zhduken, në agim.'),
(1869, 15, 67, 'Dhe, erdhën të gëzueshëm banorët e qytetit.'),
(1870, 15, 68, '(Luti) tha: “Këta janë mysafirët e mi, andaj mos më turpëroni,'),
(1871, 15, 69, 'dhe druajuni Perëndisë, dhe mos më poshtëroni mua!”'),
(1872, 15, 70, 'Ata (populli i Lutit) thanë: “A nuk të kemi ndaluar na ty, që të pranosh mysafirë?”'),
(1873, 15, 71, '(Luti) tha: “Ja këto vajzat e (popullit) tim! Nëse doni të bëni çka synoni, (martohuni me to).'),
(1874, 15, 72, '(O Muhammed!) Pasha jetën tënde, me të vërtetë, ata janë déjur në iluzionet e veta.'),
(1875, 15, 73, 'Dhe, ata i kaplojë një zë i tmerrshëm kur lindir Dielli,'),
(1876, 15, 74, 'dhe Ne, kemi bërë që, ajo që gjendej lartë – të bie poshtë, duke lëshuar në ta si shi gurësh prej dheut të pjekur, -'),
(1877, 15, 75, 'Këto, me të vërtetë, janë argumente për ata që vëzhgojnë me kujdes,'),
(1878, 15, 76, 'me të vërtetë, ato (qytete) janë në rrugë, kur gjenden edhe tani.'),
(1879, 15, 77, 'Kjo, me të vërtetë, është dokument për besimtarët.'),
(1880, 15, 78, 'Por, edhe banorët e Ejkes kanë qenë zullumqarë,'),
(1881, 15, 79, 'andaj, i kemi ndëshkuar, dhe që të dy janë në rrugë – të dukshme.'),
(1882, 15, 80, 'Edhe banorët e Hixhrës, pejgamberët i konsideruan për gënjeshtarë.'),
(1883, 15, 81, 'Dhe Ne, u patëm dhënë atyre dokumentet Tona, por ata shmangeshin prej tyre.'),
(1884, 15, 82, 'Ata gëdhendnin shtëpia me gurë-malesh, duke besuar se nuk do t’i gjejë asgjë,'),
(1885, 15, 83, 'andaj, edhe ata në agim i goditi zë i tmerrshëm'),
(1886, 15, 84, 'dhe nuk patën farë dobie nga ajo që patën ndërtuar (banesat)'),
(1887, 15, 85, 'Na kemi krijuar qiejt dhe Tokën dhe atë që gjendet në mes tyre – në mënyrë të mrekullueshme. Me të vërtetë, do të vie Kijameti, andaj sillu ti (o Muhammed) me sjellje të bukura (zemërgjerësi),'),
(1888, 15, 86, 'me të vërtetë, Zoti yt është i vetmi Krijues dhe i Gjithëdijshëm. –'),
(1889, 15, 87, 'Na të kemi shpallë ty shtatë ajete, që përsëriten dhe Kur’anin e madhërueshëm. –'),
(1890, 15, 88, 'Mos i drejto sytë e tu kah ato kënaqësi që Na ua kemi dhënë disave prej tyre dhe mos u hidhëro për ta (në mos besofshin). E, ndaj besimtarëve bëhu i butë'),
(1891, 15, 89, 'dhe thuaj: “Unë, në të vërtetë, jam vetëm paralajmërues i qartë!” –'),
(1892, 15, 90, '(Ashtu u kemi dërguar cënim) atyre, të cilët e kanë ndarë Librin,'),
(1893, 15, 91, 'e të cilët e kanë ndarë Kur’anin pjesë-pjesë (diçka kanë besuar e diçka jo).'),
(1894, 15, 92, 'Dhe, kështu, pasha Zotin tënd, Na me të vërtetë, do t’i pyesim ata të gjithë,'),
(1895, 15, 93, 'për atë që kanë punuar!'),
(1896, 15, 94, 'Ti haptazi prediko atë që të është urdhëruar dhe shmangiu idhujtarëve,'),
(1897, 15, 95, 'Na të mjaftojmë ty, kundër atyre që të tallin (ty),'),
(1898, 15, 96, 'të cilët përveç Perëndisë, marrin edhe zot tjetër, dhe do ta dinë ata mirë (fundin)!'),
(1899, 15, 97, 'Na e dimë mirë, që ty të ngushtohet zemra, për atë që thonë ata,'),
(1900, 15, 98, 'andaj, madhëroje dhe falenderoje Zotin tënd dhe bëhu prej atyre që falin namazin!'),
(1901, 15, 99, 'Dhe, adhuroje Zotin tënd përderisa të arrijë gjendja e sigurtë (vdekja)!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'Urdhëri i Perëndisë, do të arrijë, andaj mos e nxitoni ju atë! Qoftë i lavdëruar Ai dhe i lartësuar nga ajo që i bëjnë shok Atij.'),
(1903, 16, 2, 'Ai dërgon engjëjt me Shpallje, me dëshirën e Vet, kujt të dojë prej robërve të Tij: “Paralajmëroni se s’ka zot tjetër përveç Meje, dhe ruajuni ju prej kundërshtimit Tim!”'),
(1904, 16, 3, 'Ai i krijoi qiejt dhe Tokën me mrekulli të madhe! Është i lartësuar (Perëndia) nga ajo që i bëjnë shok Atij!'),
(1905, 16, 4, 'Ai e ka krijuar njeriun prej embrionit të farës, e ai (njeriu) përnjëherësh – kundërshtar i hapët!'),
(1906, 16, 5, 'Dhe shtazët i krijoi Ai për ju; me to – ju ngroheni (nga prodhimet e tyre), e keni edhe dobi të tjera, dhe me to – më së shumti ushqeheni;'),
(1907, 16, 6, 'dhe ju në to – keni hijeshi kur i ktheni nga kullosa dhe kur i qitni në kullosë,'),
(1908, 16, 7, 'dhe ato na transportojnë barrët tuaja në një vendbanim deri te i cili ju do të arrinit me mundim të madh; me të vërtetë, Zoti juaj është i butë dhe i mëshirshëm –'),
(1909, 16, 8, '(Krijoi) edhe kuajt, mushkt dhe gomarët, për t’i shaluar ju, dhe për zbukurim, e krijon (Perëndia) edhe ato që nuk i dini ju.'),
(1910, 16, 9, 'E Perëndia është që t’ju tregojë rrugën e drejtë, e ka prej tyre edhe të shtrembëra, e sikur të donte Perëndia do t’i udhëzonte të gjithë (në rrugë të drejtë).'),
(1911, 16, 10, 'Ai lëshon ujët nga qielli, të cilin ju e pini, dhe me te rriten bimët, ku i kullotni (kafshët tuaja).'),
(1912, 16, 11, 'Me atë (ujin) kultivohen të lashtat: ullinjt, palmet, rrushi dhe prej fruteve tjera të llojllojshme – kjo është, me të vërtetë, argument për njerëzit që kuptojnë.'),
(1913, 16, 12, 'Ai ka bërë që ta shfrytëzoni ju natën dhe ditën dhe Diellin e Hënën. Edhe yjet i janë nënshtruar urdhërit të Tij. Me të vërtetë, në këto ka argumente për njerëzit që mendojnë.'),
(1914, 16, 13, '(Dhe ka nënshtruar) për ju atë që ka krijuar në Tokë, llojet e të cilave janë të ndryshme. Me të vërtetë, në këto ka argumente për njerëzit që këshillohen.'),
(1915, 16, 14, 'Dhe Ai është që e ka bërë të mundshëm shfrytëzimin e detit, për të ngrënë ju nga ai mish të freskët dhe të nxirrni nga ai stoli që i vishni. – Ti sheh se si anijet e çajnë atë (detin) – për të kërkuar nga dhuratat e Tij (Perëndisë) dhe që të bëheni ju falenderues.'),
(1916, 16, 15, 'Ai në Tokë ka krijuar male të palëvizshme, për të mos ju luhatuar ato juve, e ka krijuar edhe lumenj në rrugë për të arritur ku keni synuar,'),
(1917, 16, 16, 'dhe (ka krijuar) tregues të rrugëve, si dhe orientimin e njerëzve me anë të yjeve.'),
(1918, 16, 17, 'Prandaj, a është njësoj si ai që krijon – e si ai që nuk krijon?! A nuk po kuptoni?'),
(1919, 16, 18, 'E, nëse ju doni t’i numëroni dhuntitë e Perëndisë, atëherë nuk do të mund t’i numëroni. Me të vërtetë, Perëndia është falës dhe mëshirues,'),
(1920, 16, 19, 'Perëndia di atë që ju e fshihni, dhe atë që e shfaqni haptazi.'),
(1921, 16, 20, 'E, ata që adhurojnë, pos Perëndisë – nuk krijojnë asgjë; ata vetë janë të krijuar;'),
(1922, 16, 21, 'ato (statujet) janë gjëra të vdekura, pa kurrfarë gjallërie. Ata nuk dinë kur do të ringjallen.'),
(1923, 16, 22, 'Zoti juaj është vetëm një Perëndi. E, ata që nuk besojnë në jetën tjetër, zemrat e tyre janë mohuese dhe ata janë mendjemëdhenj.'),
(1924, 16, 23, 'S’ka dyshim, se, Perëndia di për atë që fshehin ata dhe për atë që shprehin haptazi. Me të vërtetë, Ai nuk i do mendjemëdhenjt.'),
(1925, 16, 24, 'E, kur ju thuhet atyre: “Çka ka shpallë Zoti juaj?” Ata thonë: “Përrallat e popujve të lashtë!”'),
(1926, 16, 25, '(Këtë e thanë që) t’ju ngarkohen mëkatet e tyre të plota në Ditën e Kijametit, si dhe aq sa kanë bërë mëkate ata që u mashtruan dhe humbën rrugën nga këta – nga padijenia; sa ë shëmtuara janë ato (mëkatet) që i bartin ata!'),
(1927, 16, 26, 'Me të vërtetë, kanë thurrur ngatërresa ata që kanë qenë para tyre, e Perëndia e rrënoi godinën e tyre deri në themel, dhe u rrëzua kulmi mbi ta e u erdhi dënimi andej – nga nuk e prisnin.'),
(1928, 16, 27, 'Pasta (Perëndia) i poshtëron ata në Ditën e Kijametit dhe u thotë: “Ku janë shokët e Mi (të cilët i trillonit ju), për shkak të të cilëve tregonit armiqësi (ndaj besimtarëve)?” E, atyre që u është dhënë dijenia, thonë: “Me të vërtetë, sot, poshtërimi dhe dënimi është mbi mohuesit,'),
(1929, 16, 28, 'të cilëve, engjëjt ua marrin shpirtin, duke i bërë vetes ata zullum, e do të shfaqin përulshmëri (duke thënë): “Na nuk kemi punuar asgjë të keqe!” – Po, Perëndia me të vërtetë, është dijtës i veprave që keni punuar,'),
(1930, 16, 29, '(Ju tuhet atyre): “hyni në portet e skëterrës, ku do të mbeteni përgjithmonë!” E, sa vendbanim i shëmtuar është ai për mendjemëdhenjt!'),
(1931, 16, 30, 'E, atyre që i druajnë Perëndisë – u thuhet: “Çka u ka shpallur Zoti juaj?” – Ata do të thonë: “(Na ka zbritur) mirësi!” Ata që bëjnë vepra të mira në këtë botë, do të kenë mirësi, e në botën tjetër do të kenë edhe më të mira. E, sa e mrekullueshme do të jetë shtëpia e atyre që i druajnë Perëndisë:'),
(1932, 16, 31, 'xhennetet e Adnit, në të cilët do të hynë ata, nëpër të cilët rrjedhin lumenjt, e në to ka gjithçka që dëshirojnë ata. Kështu i shpërblen Perëndia besimtarët,'),
(1933, 16, 32, 'të cilëve engjëjt ua marrin shpirtërat, (duke qenë ata) të pastër. (E engjëjt) u thonë atyre: “Shpëtimi qoftë mbi ju! Hyni në xhennet për ato që keni punuar!”'),
(1934, 16, 33, 'Ata – mohuesit, nuk presin kurrgjë tjetër, përpos që t’u vijnë engjëjt (t’ua marrin shpirtin) ose t’ju vie urdhëri (dënimi) i Zotit tënd. Kështu kanë bërë edhe ata që kanë qenë para këtyre. Perëndia nuk u ka bërë atyre padrejtësi, por ata vetvetes i kanë bërë zullum,'),
(1935, 16, 34, 'dhe i arriti dënimi ata, për veprat që kanë punuar dhe ata i kaploi ajo (dënimi), meqë talleshin me ta.'),
(1936, 16, 35, 'Dhe thonin ata, të cilët i bënin shok Perëndisë: “Sikur të donte Perëndia, nuk do të adhuronim ne, as të parët tanë – asgjë, përveç Tij, dhe nuk do të kishim ndaluar ne asgjë, pa dëshirën e Tij. Kështu kanë vepruar ata para këtyre (kanë mohuar). Për pejgamberët s’ka tjetër detyrë përpos që të shpallin qartë.'),
(1937, 16, 36, 'Na çdo populli i kemi dërguar nga një pejgamber. (Ata u thonin atyre): “Adhuronie Perëndinë, e shmanguni nga djalli!” Disa prej tyre, Perëndia i shpuri në rrugë të drejtë, e disa prej tyre merituan të mbeten në humbje. Andaj, udhëtoni nëpër botë, e shihni se çfarë ka qenë dënimi për ata, të cilët pejgamberët e vet i konsideronin për gënjeshtarë!'),
(1938, 16, 37, 'Edhe nëse angazhohesh ti (o Muhammed!) që ata të jenë në rrugën e drejtë, me të vërtetë, Perëndia nuk do ta shpie në rrugë të drejtë, atë që don ta lerë në humbje. Dhe për ata nuk ka kurrfarë ndihmësi.'),
(1939, 16, 38, 'Dhe ata (mohuesit), janë betuar në Perëndinë, me betimin e tyre më të fortë: “Perëndia nuk do ta ngjallë atë që ka vdekur”. Por, me të vërtetë, (ringjallja) është premtim i Tij i saktë – por që, shumica e njerëzve nuk e dinë,'),
(1940, 16, 39, '(Do t’i ringjallë ata Perëndia) për t’ju shpjeguar, atë, për të cilën përçaheshin ata dhe që ta kuptojnë mohuesit se kanë qenë gënjeshtarë.'),
(1941, 16, 40, 'Nëse dëshirojmë të bëhet diçka, Na – vetëm me një të thënme për atë: “Bëhu!” dhe ajo bëhet.'),
(1942, 16, 41, 'E, ata që kanë emigruar në emër të Perëndisë, pasi u është bërë zullum, Na do t’i vendosim në vendbanim të mirë në këtë botë, e shpërblimi në botën tjetër, është edhe më i madh. Ah! sikur ta dinin ata!'),
(1943, 16, 42, '(Ata që emigruan) kanë duruar dhe janë mbështetur në Zotin e thyre.'),
(1944, 16, 43, 'Na edhe para teje kemi dërguar vetëm njerëz – burra, (e jo engjëj), të cilëve u kemi dërguar shpallje, - andaj pyetni dijetarët e Librit, nëse nuk e dini (këtë) –'),
(1945, 16, 44, '(I kemi dërguar ata) me argumente të qarta dhe libra. Dhe Na, ty ta kemi dërguar Kur’anin që t’u shpjegosh njerëzve dispozitat e shpalljes, dhe që të mendojnë ata.'),
(1946, 16, 45, 'A janë të sigurtë ata, që bëjnë dredhi me vepra të këqia, që Perëndia nuk do t’i shafojë në tokë, ose që nuk do t’u vie dënimi nga nuk e presin,'),
(1947, 16, 46, 'ose që nuk do t’i dënojë ata në udhëtimet e tyre – ata nuk mund të shpëtojnë (nga dënimi) –'),
(1948, 16, 47, 'ose (a janë të sigurtë ata) se nuk do t’i godas dënimi, duke qenë ata në frikë; me të vërtetë, Zoti juaj është i butë dhe i mëshirshëm.'),
(1949, 16, 48, 'A nuk po shohin at, se të gjitha ato që i ka krijuar Perëndia, hija e tyre anon djathtas e majtas, duke iu përulë Perëndisë, si dhe vet ata i nënshtrohen pushtetit të Perëndisë?'),
(1950, 16, 49, 'Perëndisë i bënë sexhde çdo gjallesë, në qiej dhe në Tokë, edhe engjëjt, dhe ata (engjëjt) nuk janë kryelartë:'),
(1951, 16, 50, 'I druajnë Zotit të tyre, që ka pushtet mbi ta dhe punojnë atë që u urdhërohet.'),
(1952, 16, 51, 'Dhe, Perëndia, ka thënë: “Mos adhuroni dy zotëra! Perëndia është vetëm një. Dhe, druajuni vetëm prej Meje!”'),
(1953, 16, 52, 'Dhe, Atij i përket çdo gjë që gjendet në qiej dhe në Tokë dhe Atij i përket bindja përherë. A, mos vallë, përpos Perëndisë, kujt t’i druani tjetër?'),
(1954, 16, 53, 'Çdo dhunti që keni është nga Perëndia. E, kur t’ju godit ndonjë fatkeqësi, prap prej Tij – me zë kërkoni ndihmë.'),
(1955, 16, 54, 'Dhe kur Ai – pasi t’jua shmangë fatkeqësinë, disa nga ju, në atë çast – i bëjnë shok Zotit të vet,'),
(1956, 16, 55, 'për ta treguar mosmirënjohjen e tyre ndaj asaj që ua kemi dhënë Ne. pra, kënaquni (për një kohë), po ju, me të vërtetë, do ta dini!'),
(1957, 16, 56, 'Dhe, qëllimojnë për ata që nuk dijnë gjë (për idhujtë e tyre), një pjesë prej furnizimit Tonë. Pasha Perëndinë, ju me siguri, do të pyeteni për atë që keni trilluar (për Zotin).'),
(1958, 16, 57, 'Ata, Perëndisë i përshkruajnë (mveshin) vajza (duke thën se: Engjëjt janë vajzat e Perëndisë). Qoftë lavdëruar Ai (dhe larg nga kjo), e për vete (përvetësojnë) atë që dëshirojnë (fëmijë-meshkuj).'),
(1959, 16, 58, 'Dhe, kur ndonjëri prej tyre sihariqohet (lajmërohet) se i lindi një vajzë, fytyra i nxihet dhe bëhet i dëshpëruar,'),
(1960, 16, 59, 'fshehet nga njerëzit për shkak të fatkeqësisë me të cilën është sihariquar: (mendojnë) a ta mbajnë të poshtëruar këtë (fëmijë-femër) apo ta varrosin (të gjallë) atë në dhé. Ah! sa shëmtuar që gjykojnë ata!'),
(1961, 16, 60, 'Ata që nuk besojnë për jetën tjetër, kanë cilësi të këqija, kurse Perëndia ka veçoritë më të larta, dhe Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(1962, 16, 61, 'Sikur Perëndia t’i dënonte njerëzit për shkak të mëkateve të tyre, nuk do të lënte asgjë të gjallë në Tokë, por Ai i lë deri në afat të caktuar, dhe kur t’ju vie afati atyre, as për një moment nuk mund as ta shtyejnë as ta shpejtojnë.'),
(1963, 16, 62, 'Dhe ata, ia ngjesin (mveshin) Perëndisë atë çka nuk e dëshirojnë për vete, kurse gjuhët e tyre flasin gënjeshtra, se ata do të kenë shpërblimin më të mirë; por ata, nuk ka dyshim, i pret zjarri; dhe ata – të parët do të prijnë në të.'),
(1964, 16, 63, 'Pasha Perëndinë, Na, me të vërtetë, edhe para teje u kemi dërguar pejgamberë popujve, por djalli ua ka zbukuruar sjelljet e tyre, dhe ai tani është mik i tyre; andaj ata i pret dënimi i dhëmbshëm.'),
(1965, 16, 64, 'Na ta kemi shpallur ty Librin, vetëm që t’ju shpjegosh atyre atë për të cilën ata nuk pajtoheshin, dhe (që të jetë) udhërrëfyes dhe mëshirë për njerëzit që besojnë.'),
(1966, 16, 65, 'Perëndia ka zbritur ujin nga qielli, e me të, ka ngjallë tokën pasi që ishte tharë ajo! Kjo, me të vërtetë, është argument për njerëzit që duan të dëgjojnë.'),
(1967, 16, 66, 'Me të vërtetë, ju edhe shtazët keni shenjë të përshtatshme për mësimin tuaj; Na ju furnizojmë me atë që kanë në barkun e tyre – ndërmjet ushqimit të përpunuar dhe gjakut: qumësht të pastër dhe të shijshëm për ata që e pijnë.'),
(1968, 16, 67, 'E, nga frutet e palmeve dhe të hardhisë, ju përgatitni pije dhe ushqim të këndshëm. Kjo, me të vërtetë, është argument për ata që mendojnë.'),
(1969, 16, 68, 'Dhe, e frymëzoi (e krijoi me një cilësi të mrekullueshme) Zoti yt bleten: “Ndërto, shtëpi në male dhe në drunj dhe në atë që kanë ngritur njerëzit,'),
(1970, 16, 69, 'ushqehu me gjithfarë lloje frutesh dhe ec rrugës së Zotit tënd përulshëm!” Nga barku i tyre del pije (mjaltë) ngjyrash të ndryshme, në të cilin ka ilaç për njerëzit. Kjo, me të vërtetë, është argument për ata që meditojnë.'),
(1971, 16, 70, 'Perëndia u ka krijuar juve, pastaj ua merr shpirtin; ka disa prej jush që arrijnë kufijt më të ulët të jetës (pleqërisë) që (më vonë), të mos kuptojë asgjë, nga ajo që ka ditur. Me të vërtetë, Perëndia, është i Gjithëdijshëm dhe i Plotëdijshëm.'),
(1972, 16, 71, 'Perëndia, disa prej jush i ka furnizuar më mirë. Por, atyre, që u ka dhënë më shumë, ata nuk iu japin furnizim atyre që i kanë nën pushtetin e vet, edhe pse ata janë të barabartë, (në furnizimin që është nga Perëndia për tërë njerëzit). Si po i mohoni dhuntitë e Perëndisë? (Kur nuk i bëni shok vetes, si mund t’i bëni shok Perëndisë?)'),
(1973, 16, 72, 'Perëndia ka krijuar gratë nga lloji juaj, e nga gratë tuaja u jep djemtë e nipat dhe u furnizon me ushqime të mira. A, mos vallë, ata besojnë në gënjeshtra (idhuj), e mohojnë dhuntinë e Perëndisë,'),
(1974, 16, 73, 'dhe adhurojnë, përpos Perëndisë, ata që nuk janë në gjendje t’u japin çfarëdo ushqimi, as nga qielli as nga toka, dhe të cilët nuk mundën asgjë?!'),
(1975, 16, 74, 'Andaj, mos i mveshni Perëndisë gjasime! Me të vërtetë, Perëndia di, e ju nuk dini.'),
(1976, 16, 75, 'Perëndia ka bërë krahasim shembullin e skllavit, që s’ka mundësi për asgjë, dhe atë që e kemi furnizuar Ne me dhuntitë e mira, e ai nga ato – jep fshehtazi dhe haptazi. A janë këta njëlloj? Falenderimi është për Perëndinë! Por shumica e tyre nuk dinë.'),
(1977, 16, 76, 'Perëndia (përsëri) bënë krahasim shembullin e dy njerëzve, prej të cilëve njëri është shurdhë-memec, i cili nuk mund të bëjë asgjë dhe është barrë e pronarit të vet, - kurdo e kudo që ta dërgojë nuk i sjell asnjë të mirë. A është i barabartë ky (memeci apo skllavi) dhe ai (besimtari) që urdhëron me të drejtë, si dhe vet është në rrugën e drejtë!'),
(1978, 16, 77, 'Perëndisë i përkasin fshehtësitë e qiejve dhe të Tokës, e çështja e Ditës së Kijametit është sa çel e mbyll sytë, ose edhe më shpejt. Perëndia, me të vërtetë, është i pushtetshëm mbi çdo gjë!'),
(1979, 16, 78, 'Perëndia u ka nxjerrë juve nga barku i nënave tuaja e ju nuk dinit asgjë, ai u dha të dëgjuarit, të pamurit dhe zemrat për të qenë falenderues.'),
(1980, 16, 79, 'A nuk shohin ata, se si shpendët, pa mundim fluturojnë në hapësirat qiellore. Ato i mban vetëm Perëndia (që i ka krijuar për atë aftësi). Kjo, me të vërtetë, është argument për popullin që beson.'),
(1981, 16, 80, 'Perëndia ka bërë për ju nga shtëpiat tuaja vendbanim, dhe ka bërë për ju prej lëkurave të shtazëve shtëpi (si tenda), të cilat ju i bartni lehtë kur udhëtoni dhe të zëni vend, e nga leshi i shtazëve: dhenëve, deveve dhe i dhive, ka bërë që t’i përdorni për veshje dhe për shtrojë, deri në një kohë.'),
(1982, 16, 81, 'Perëndia ka bërë për ju – nga ajo që ka krijuar – hije (që të mbron nga nxehtësia) dhe ka bërë për ju – nga malet – strehim dhe ka bërë për ju rroba që u mbrojnë nga nxehtësia; dhe ka bërë për ju veshje prej metali për t’ju mbrojtë në luftë; dhe kështu ua plotëson dhuntitë e Veta për t’ju bindë Perëndisë.'),
(1983, 16, 82, 'E, nëse ata shmangen, ti je i obliguar vetëm që të shpallësh qartazi.'),
(1984, 16, 83, 'Ata e dinë dhuntinë e Perëndisë, por pastaj e mohojnë, e shumica e tyre janë mohues.'),
(1985, 16, 84, '(Tërhiqju vërejtjen) për Ditën kur do të sjellim dëshmitarë nga çdo popull, e nuk do t’ju jepet leje (për arsyetim) mohuesve, e as që u lejohet atyre që të kërkojnë falje.'),
(1986, 16, 85, 'Kur ata që kanë bërë keq, të shohin dënimin, ai nuk do t’u lehtësohet (dënimi), e as nuk u shtyhet.'),
(1987, 16, 86, 'Kur të shohin ata që i kanë bërë shok Perëndisë – ato gjëra që i kanë bërë shok Atij, thonë: “O Zoti ynë, këta janë ata shokët tonë që ne iu kemi lutur, përpos Teje”. Ata (shokët e tyre të adhuruar) do t’u drejtojnë këto fjalë: “Ju, me të vërtetë, jeni gënjeshtarë!”'),
(1988, 16, 87, 'Ata – në atë ditë, do t’i shprehin mbështetje Perëndisë dhe do të shkatërrohet ajo, që ia mveshin Atij (Perëndisë).'),
(1989, 16, 88, 'Ata që nuk kanë besuar dhe që kanë penguar në rrugën e Perëndisë, atyre ua shtojmë dënimin mbi dënim, ngase kanë bërë ngatërresa.'),
(1990, 16, 89, 'Dhe, (përkujtoju) ditën kur Na do të sjellim kundër çdo populli dëshmitarë nga mesi i tyre (pejgamberi i tyre) dhe (atëherë), Na do të sjellim ty (Muhammed) dëshmitar kundër këtyre. E, Na ta kemi dërguar ty Librin, - shpjegim për çdo çështje dhe si udhërrëfyes, mëshirë dhe sihariq për muslimanët.'),
(1991, 16, 90, 'Perëndia, me të vërtetë, urdhëron që të veprohet me drejtësi, mirësi dhe t’u ndihmohet të afërmve, e ndalon imoralitetin, veprat e shëmtuara dhe dhunën. Ai ju këshillon që të pranoni këshillat.'),
(1992, 16, 91, 'Dhe, zbatoni obligimet e Perëndisë kur t’i merrni mbi vete dhe mos e thyeni betimin – pasi që e keni forcuar, meqë Perëndinë e keni bërë mbikëqyrës tuajin; Perëndia, me të vërtetë di ç’punoni ju.'),
(1993, 16, 92, 'Mos u bëni (në thyerjen e obligimeve) si ajo (gruaja) e cila e ka shprishur atë që e ka tjerrë, pasi është forcuar – e ka mbetur e shprishur, duke i marrë betimet tuaja-hyrje (mashtrime) në mes vete, vetëm nga shkaku se një popull është më i madh në numër se tjetri. Perëndia, me këtë, vetëm u vënë në provë, e me siguri, Ai do t’ju shpjegojë në Ditën e Kijametit, për atë që ju nuk jeni pajtuar.'),
(1994, 16, 93, 'Sikur të kishte dashur Perëndia, do t’ju kishte bërë juve një popull, por Ai e lë në humbje kë të dojë, e kë të dojë e udhëzon në rrugën e drejtë; e ju, gjithsesi, do të përgjigjeni për atë që punoni.'),
(1995, 16, 94, 'Dhe, mos i merrni betimet tuaja për mashtrim në mes jush, e t’ju rrëshqas këmba, pasi që nguliti, e të shijoni dënimin, meqë penguat nga rruga e Perëndisë; e, për ju do të ketë ndëshkim të madh.'),
(1996, 16, 95, 'Mos e këmbeni obligimin e Perëndisë me ndojë vlerë të pakët. Ajo që është te Perëndia, është më e mirë për ju, nëse e dini.'),
(1997, 16, 96, 'Ajo që është te ju – është e përkohshme, e ajo që është te Perëndia – është e përhershme. Dhe Na, me të vërtetë, do t’i shpërblejmë ata që kanë duruar, me shpërblimin më të mirë për atë që kanë punuar.'),
(1998, 16, 97, 'Kush punon vepra të mira – qoftë mashkull apo femër – e është besimtar, Na do t’i japim atij që të kalojë jetë të bukur. Dhe, me të vërtetë, për veprat që kanë punuar, do t’i shpërblejmë ata me shpërblimin më të mirë se sa që e kanë merituar.'),
(1999, 16, 98, 'Kur të dëshirosh të lexosh Kur’anin, mbështetu te Perëndia (thuaj: “Eudhu-bil-lahi...”) që të të shpëtojë nga djalli i mallkuar!'),
(2000, 16, 99, 'Me të vërtetë, djalli nuk ka kurrfarë pushteti ndaj atyre që besojnë dhe (të cilët) mbështeten te Zoti i tyre,'),
(2001, 16, 100, 'pushteti i tij (djallit) është vetëm mbi ata, të cilët i binden atij dhe të cilët i bëjnë shok Perëndisë.'),
(2002, 16, 101, 'Dhe, kur Ne, ta zëvendësojmë një ajet me një ajet tjetër – e Perëndia di më së mir se çka shpall – (Mohuesit) thonë: “Ti je vetëm trillues!” Por, nuk është ashtu, se shumica prej tyre nuk dijnë.'),
(2003, 16, 102, 'Thuaj: “E ka sjellur (Kur’anin) Shpirti i pastër (Xhebraili) nga Zoti yt me të vërtetën, për t’i forcuar ata që besojnë – udhërrëfyes dhe sihariq për muslimanët”.'),
(2004, 16, 103, 'Na e dimë mirë që ata thonë: “Atë (Muhammedin) e mëson një njeri!” Gjuha e tij, që po ia mveshin – se po e mëson atë – është gjuhë e të huajve (të cilën nuk e dinë arabët), kurse ky (Kur’ani) është në gjuhën e qartë arabe (me një stil të lartë).'),
(2005, 16, 104, 'Me të vërtetë, ata që nuk u besojnë argumenteve të Perëndisë, Perëndia nuk i udhëzon dhe për ata ka dënim të dhembshëm.'),
(2006, 16, 105, 'Me të vërtetë, trillojnë gënjeshtra vetëm ata që nuk u besojnë argumenteve të Perëndisë dhe ata janë gënjeshtarë të vërtetë.'),
(2007, 16, 106, 'Ai që mohon Perëndinë, pasi që pat besuar – përpos atij që është i detyruar me forcë (që të mohojë), por që zemra e tij është plot besim, (për të nuk ka mëkat) – por për ata që e hapin zemrën për mohim, për ta ka zemrim nga Perëndia dhe për ata ka dënim të madh,'),
(2008, 16, 107, 'sepse, ata e duan më shumë jetën e kësaj bote se sa jetën e botës tjetër, e Perëndia nuk i udhëzon në rrugë të drejtë mohuesit.'),
(2009, 16, 108, 'Këta janë ata të cilëve Perëndia ua ka vulosur zemrat e tyre, të dëgjuarit dhe të pamurit e tyre, dhe këta janë njerëz, vërtetë të shkujdesur.'),
(2010, 16, 109, 'S’ka dyshim se, ata, në botën tjetër do të jenë nga të humburit.'),
(2011, 16, 110, 'Me të vërtetë, Zoti yt – për ata që kanë emigruar, pasi janë torturuar e mandej kanë luftuar (në të mirën) dhe kanë duruar, Perëndia, pas kësaj, padyshim, është falës dhe mëshirues.'),
(2012, 16, 111, '(Kujtoje) ditën që do të vijë kur çdo njeri do të mbrojë vetëm veten dhe kur do t’i plotësohet çdokujt ajo që e ka punuar; e atyre nuk do t’u bëhet padrejtësi!'),
(2013, 16, 112, 'Dhe, Perëndia sjell shembull një qytet, i cili është i sigurtë dhe i qetë, të cilit i arrinte furnizimi i bollshëm nga të gjitha anët, e që pastaj, ata mohuan dhuntitë e Perëndia, për atë që punuan ata, u dha që ta shijojnë urinë dhe frikën.'),
(2014, 16, 113, 'Me të vërtetë, atyre u erdhi një pejgamber – nga mesi i tyre – por ata e konsideruan atë për gënjeshtar, dhe ata i goditi dënimi, ngase ishin zullumqarë.'),
(2015, 16, 114, 'Ushqehuni me furnizimet që u ka dhënë Perëndia – të lejuara dhe të mira, dhe falenderohuni dhuntive të Perëndisë, nëse vetëm Atij i luteni.'),
(2016, 16, 115, 'Perëndia u ka ndaluar juve vetëm coftinë, gjakun, mishin e derrit deh atë që ësthë therrur në emër tjetër, e jo në emër të Perëndisë. Por, ata, që ngushtohen nga uria e madhe, mund t’i hanë ato, sa për të zhdukur krizën e urisë, e jo të kalojnë kufirin; me të vërtetë, Perëndia është falës dhe mëshirues.'),
(2017, 16, 116, 'Mos theksoni me gjuhët tuaja rrejshëm: “Kjo është e lejueshme, e kjo e ndaluar”, që kështu të trilloni gënjeshtra për Perëndinë. Me të vërtetë, ata që trillojnë gënjeshtra ndaj Perëndisë, nuk do të shpëtojnë,'),
(2018, 16, 117, 'do të kenë kënaqësi të shkurtër, por për ta do të ketë dënim të dhembshëm.'),
(2019, 16, 118, 'Dhe Hebrenjve, u kemi ndaluar atë që të treguam Na ty më parë. Na, nuk u kemi bërë atyre padrejtësi, por ata vetvetes i kanë bërë zullum.'),
(2020, 16, 119, 'Pastaj Zoti yt, atyre që kanë bërë keq nga padijenia, e që pastaj janë penduar dhe përmirësuar, - me të vërtetë, Zoti yt, pas kësaj, është falës dhe mëshirues.'),
(2021, 16, 120, 'Me të vërtetë, Ibrahimi ka qenë (përkah virtytet e tij sa) një popull, i përulur Perëndisë, i prirur (kah e mira) dhe nuk ka qenë nga idhujtarët,'),
(2022, 16, 121, 'ishte falenderues ndaj dhuntive të Tij (Perëndisë). Perëndia e zgjodhi atë dhe e udhëzoi në rrugë të drejtë,'),
(2023, 16, 122, 'dhe Ne, i kemi dhënë atij të mira në këtë botë, e në botën tjetër, me të vërtetë – do të jetë nga të mirët.'),
(2024, 16, 123, 'Pastaj ty të kemi porositur: “Paso fenë e Ibrahimit – të prirur kah e mira!” Ai nuk ka qenë prej idhujtarëve.'),
(2025, 16, 124, '(Kremtja) e ditës së shtunë, është urdhëruar për ata, të cilët janë përçarë për këtë. Dhe, me të vërtetë, Zoti yt do t’i gjykojë ata në Ditën e Kijametit, për atë që nuk pajtoheshin ata.'),
(2026, 16, 125, 'Thirri në rrugën e Zotit tënd me mençuri dhe këshillë të bukur. Dhe, diskuto me ta si është më bukur! Me të vërtetë, Zoti yt di më së miri ata, që janë shmangur nga rruga e Tij, e Ai di më së miri (edhe) ata që janë në rrugën e drejtë.'),
(2027, 16, 126, 'E, nëse ndëshkoni, ndëshkoni ashtu si keni qenë të ndëshkuar ju, e nëse duroheni, kjo është më së miri për të durueshmit.'),
(2028, 16, 127, 'Duro! Durimi yt është vetëm me ndihmën e Perëndisë. Dhe, mos u pikëllo për ta, e mos u ngushto ti për shkak të dredhive të tyre,'),
(2029, 16, 128, 'me të vërtetë, Perëndia është me ata, që i druajnë Perëndisë dhe që janë bamirës.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Qoftë lavdëruar Ai; i pastër është Ai nga të metat – i cili, në një çast të një nate, e ka bartë robin e Tij (Muhammedin) nga Tempulli i shenjtë (Mekka) në Tempullin e largët (Jerusalemi), rrethinën e së cilës e kemi bekuar, për t’ia dëftuar atij disa nga dokumentet Tona. Ai (Perëndia), me të vërtetë, dëgjon çdo gjë dhe sheh çdo gjë.'),
(2031, 17, 2, 'Dhe ia dhamë Musait Librin dhe e bëmë atë udhërrëfyes për bijtë e Israelit – (duke u thënë atyre): “Mos merrni përpos Meje kurrfarë përfaqësuesi,'),
(2032, 17, 3, '(O), pasardhësit e atyre që Ne i bartëm me Nuhun (në barkë)!” Ai (Nuhu) me të vërtetë, ka qenë rob mirënjohës (i Zotit).'),
(2033, 17, 4, 'Dhe Ne u kemi shpallur në Libër bijve të Israelit: “Ju me të vërtetë, do të bëni dy herë ngatërresa në Tokë dhe do të tregoni mendjemadhësi të tepërt.'),
(2034, 17, 5, 'E, kur erdhi premtimi i parë prej atyre dyjave (ngatërresave), i dërguam kundër jush robërit Tanë, të fuqishëm luftëtarë, dhe u sollëm gjerë e gjatë nëpër tokën tuaj. Ky ishte premtimi i kryer (dënimi).'),
(2035, 17, 6, 'Pastaj, ua kthyem sërish juve (fuqinë e sulmit) kundër tyre dhe u ndihmuam juve me pasuri dhe djem dhe u bëmë në numër më të madh ushtarësh. –'),
(2036, 17, 7, 'Nëse bëni mirësi, bëni për vete. E, nëse bëni keq, i bëni vetes. E, kur erdhi premtimi i dytë, i dërguam Ne ata (armiqtë tuaj) për t’ju nxirë fytyrat tuaja, dhe që të hyjnë ata në tempull, sikundër që patën hyrë herën e parë dhe të shkatërrojnë plotësisht çdo gjë që e pushtojnë.'),
(2037, 17, 8, 'Behet që Zoti juaj t’ju mëshiroj, e nëse ju ktheheni (në të keqe), Na kthehemi (në dënim) kundër jush. Dhe xhehennemin e kemi bërë burg për mohuesit”.'),
(2038, 17, 9, 'Me të vërtetë, ky Kur’an udhëzon kah ajo që është më e drejtë dhe i sihariqon besimtarët që punojnë vepra të mira, se ata, me të vërtetë, do të kenë shpërblim të madh,'),
(2039, 17, 10, 'e, atyre që nuk besojnë në jetën tjetër, u kemi përgatitur dënim të dhembshëm.'),
(2040, 17, 11, 'Njeriu mallkon në të keqe, ashtu siç lutet edhe për të mirë (pa menduar fundin); e njeriu është i ngutshëm.'),
(2041, 17, 12, 'Dhe, Ne kemi bërë natën dhe ditën dy argumente (që dëshmojnë fuqinë Tonë); e kemi shlyer argumentin e natës, kurse argumentin e ditës e kemi bërë të ndritshëm, për të kërkur ju dhunti nga Zoti juaj, dhe që të dini numrin e viteve dhe njehsimin e kohës, dhe çdo gjë e kemi shpjeguar plotësisht.'),
(2042, 17, 13, 'Çdo njeriu i kemi varë në qafë punën e tij dhe Na – do t’ia paraqesim atij në Ditën e Kijametit – librin që do ta gjejë të hapur:'),
(2043, 17, 14, '(i thuhet): “Lexoje librin tënd, mjafton që ti sot vetë për vetveten të japësh llogari!”'),
(2044, 17, 15, 'Kush udhëzohet në rrugën e drejtë, ai është udhëzuar (për dobi të vet), e ai që bredhë, ka bredhur në dëm të vet dhe askush nuk e merr të keqen e tjetërkujt. Na nuk dënojmë (askend) para se të dërgojmë pejgamber!'),
(2045, 17, 16, 'Kur Ne duam ta shkatërrojmë një qytet, (për mëkatet e bëra), i nxitim ata që janë mësuar me jetë luksoze, që të jepen pas korrupsionit (mbrapshtive), e kështu të meritojnë dënimin, dhe pastaj t’i shkatërrojmë ata gjer në themel.'),
(2046, 17, 17, 'Sa e sa gjenerata Na i kemi shkatërruar pas Nuhut! E, mjafton që Zoti yt di dhe sheh mëkatet e robërve të Tij.'),
(2047, 17, 18, 'Kush dëshiron këtë botë, Na ia shpejtojmë atij që t’i japim në të (në këtë botë) çka të duam e për kë të duam. E, pastaj ia caktojmë atij xhehennemin, në të cilin ai do të fërgohet, duke qenë i fyer dhe i dëbuar.'),
(2048, 17, 19, 'E, kush dëshiron botën tjetër dhe përpiqet për të, e është besimtar, përpjekja e tyre është e falenderuar.'),
(2049, 17, 20, 'Na të gjithëve u ndihmojmë: këtyreve dhe atyreve – me dhuratat e Zotit tënd. E, dhuratat e Zotit tënd nuk janë të penguara.'),
(2050, 17, 21, 'Shiko si i kemi dhuruar ne disa ndaj të tjerëve. E, bota tjetër, në të vërtetë, është më e madhe përkah pozitat dhe dhuntitë.'),
(2051, 17, 22, 'Mos merr për zot tjetër kend, përpos Perëndisë, e të mbetesh i sharë dhe i poshtëruar.'),
(2052, 17, 23, 'Zoti yt ka urdhëruar që ta adhuroni vetëm Ate dhe t’u bëni mirësi prindërve. Nëse njëri prej tyre, ose të dy, arrijnë pleqërinë te ti, mos u thuaj atyre: “Uh!”, dhe mos i kundërshto ata (me fjalë të këqia), dhe drejtohu atyre me fjalë respekti.'),
(2053, 17, 24, 'Lëshoj para tyre krahët tuaj të përuljes prej mëshirës dhe thuaj: “O Zoti im, mëshiroji ata, ashtu siç më kanë edukuar kur isha unë i vogël!”'),
(2054, 17, 25, 'Zoti juaj di më së miri ç’keni në shpirtërat tuaj. Nëse ju jeni të mirë, Ai, me të vërtetë, është falës për ata që pendohen.'),
(2055, 17, 26, 'Epjuni të afërmve të drejtën e tyre (ashtu si jeni urdhëruar), të varfërit dhe udhëtarit (që ka ngelë në rrugë), por mos bën shpenzim të pavend.'),
(2056, 17, 27, 'Shpenzuesit e pavend (batakçinjtë) me të vërtetë, janë vëllezërit e djallit, e djalli është mohues i (të mirave të) Zotit të tij.'),
(2057, 17, 28, 'E nëse shmangesh nga ata, duke kërkuar mëshirën e Zotit tënd, të cilën ti e shpreson, atëherë, thuaju atyre ndonjë fjalë të butë.'),
(2058, 17, 29, 'Mos e bënë dorën tënde të lidhur për qafën tënde (mos u bënë koprrac), as mos e hap tepër dorën tënde, e të mbetesh i qortuar (për shkak të koprracisë) dhe i pikëlluar (për shkak se ngelesh pa mjete).'),
(2059, 17, 30, 'Me të vërtetë, Zoti yt i jep me bollëk furnizim kujt të dojë, e ia kufizon kujt të dojë; Ai, me të vërtetë, i di dhe i sheh robërit e vet.'),
(2060, 17, 31, 'Mos i mbytni fëmijët tuaj nga frika e varfërisë! Na i furnizojmë ata dhe juve; se, mbytja e tyre, me të vërtetë, është mëkat i madh.'),
(2061, 17, 32, 'Dhe, mos iu qasni kurvërisë, se ajo është shfrenim dhe rrugë e shëmtuar!'),
(2062, 17, 33, 'Mos mbytni atë që e ka ndaluar Perëndia, përpos me të drejtë (sipas gjykimit)! E, ai që mbytet pa të drejtë, trashëgimtarit të tij i kemi dhënë pushtet, por edhe ai mos ta teprojë në mbytje. Pra, ai është i ndihmuar (nga ligji dhe njerëzit).'),
(2063, 17, 34, 'Mos iu qasni pasurisë së bonjakut, përpos në mënyrën më të mirë, përderisa të mos arrijë moshën e pjekurisë. Dhe kryeni obligimet. Me të vërtetë, për obligime jeni përgjegjës.'),
(2064, 17, 35, 'Plotësoni matjen kur matni, dhe peshoni me terezi (peshojë) të drejtë. Kjo është më së miri dhe pasojat janë më të bukura.'),
(2065, 17, 36, 'Mos ano (shko) kah ajo që nuk e dini, me të vërtetë, të dëgjuarit, të pamurit dhe zemra (mendja), të gjitha këto janë përgjegjëse për atë (që ka anuar).'),
(2066, 17, 37, 'Mos ecën nëpër tokë me mendjemadhësi, se ti, me të vërtetë, nuk mund ta shposh tokën e as të arrish lartësinë e maleve.'),
(2067, 17, 38, 'Të gjitha këto janë të këqija dhe të neveritshme te Zoti yt.'),
(2068, 17, 39, 'Kjo është nga mençuria (dijenia e madhe) që të ka shpallur ty Zoti yt dhe mos bën zot tjetër me Perëndinë, e të hedhesh në skëterrë, i sharë dhe i larguar (nga mëshira e Perëndisë)!'),
(2069, 17, 40, 'A mos vallë Perëndia u ka dhuruar juve djem e (për Vete) ka marrë engjëjt për vajza? Ju, me të vërtetë, flisni fjalë të madhe (trillim të madh).'),
(2070, 17, 41, 'Na, me të vërtetë, në këtë Kur’an kemi shpjeguar që të këshillohen ata, por (ky shpjegim) vetëm ua ka shtuar atyre largimin (nga e vërteta).'),
(2071, 17, 42, 'Thuaj: “Nëse krahas Tij ekzistojnë zotëra tjerë, siç thonë ata, atëherë, ata do të kërkonin rrugë për të arritur te Zoti i Arshit”.'),
(2072, 17, 43, 'Qoftë i pastër dhe i lartësuar nga ajo që thonë ata – me lartësi të madhe!'),
(2073, 17, 44, 'Atë e madhërojnë (dhe cilësitë e Tij) shtatë qiejt dhe Toka, dhe gjithçka gjendet në to. Dhe, nuk ka asgjë që nuk e madhëron Atë, duke iu falenderuar Atij, por ju nuk e kuptoni madhërimin që i bëjnë ata. Ai, me të vërtetë, është i butë dhe falës.'),
(2074, 17, 45, 'Kur ti lexon Kur’anin, Na vëmë një perde të fshehtë në mes teje dhe atyre që nuk besojnë jetën e ardhshme,'),
(2075, 17, 46, 'e, në zemrat e atyre vëmë mbulesa, për të mos e kuptuar atë (Kur’anin), ua shurdhojmë veshët atyre, e kur ta përmendish në Kur’an Zotin tënd – Atë të vetmin, ata kthejnë shpindën e tyre, duke ikur (prej dëgjimit të Kur’anit).'),
(2076, 17, 47, 'Na dimë çka përgjojnë ata, kur përgjojnë te ti (leximin e Kur’anit) dhe kur bisedojnë në mes vete, kur thonë mohuesit (në bisedim): “Ju, vetëm shkoni pas një njeriu të magjepsur”.'),
(2077, 17, 48, 'Shiko çfarë shembuj të kanë mbathur ty, ata! Ata janë të humbur e nuk mund të gjejnë rrugën.'),
(2078, 17, 49, 'Ata thonë: “Vallë kur të bëhemi eshtra dhe pluhur, a do të ringjallemi në krijesa të reja?”'),
(2079, 17, 50, 'Thuaj: “Bëhuni gurë apo hekur,'),
(2080, 17, 51, 'ose çfarëdo krijese tjetër që është e madhe në zemra tuaja (do ta pranojnë ringjalljen me urdhërin Tonë)!” Ata do të thonë: “Kush do të na kthejë neve (në gjallëri)?” Thuaj: “(Do t’ju ringjallë juve) Ai që ju ka krijuar herën e parë”. Ata do t’i lëkundin kokat e tyre kah ti (duke të përqeshur) dhe thonë: “Kur është ajo?” Ti thuaj: “Mund të bëhet shpejt!”'),
(2081, 17, 52, 'Atë Ditë kur do t’ju thërras juve Ai, e do t’i përgjigjeni duke iu falenderuar Atij dhe do të mendoni se në këtë jetë, nuk keni qëndruar veçse pak kohë.'),
(2082, 17, 53, 'Thuaju robërve të Mi, le të flasin atë që është më e bukur. Me të vërtetë, djalli mbjell ngatërresa në mes tyre. Me të vërtetë, djalli është armik i hapët i njeriut.'),
(2083, 17, 54, 'Zoti juaj ju njeh më së miri. Nëse don, Ai do t’ju mëshirojë juve, ose, nëse don, ju dënon. Na ty nuk të kemi dërguar përfaqësues tek ata.'),
(2084, 17, 55, 'Dhe Zoti yt di më së miri për ata që gjenden në qiej dhe në Tokë; Na i kemi dekoruar disa pejgamberë ndaj disa të tjerëve dhe Dautit i kemi dhënë Zeburin.'),
(2085, 17, 56, 'Thuaj: “Thirrni ju ata, të cilët i mendoni (për zotra) përveç Tij (Perëndisë), e ata nuk mund t’ju largojnë të keqen nga ju, as nuk mund ta hedhin tjetër kund”.'),
(2086, 17, 57, 'Ata që i luten atyre, vetë kërkojnë mënyrë ndërmjetësimi te Zoti i tyre, cili është më i afërt, shpresojnë mëshirën e Tij dhe i frikohen dënimit të Tij. Me të vërtetë, dënimit të Zotit tënd çdokush duhet t’i ruhet.'),
(2087, 17, 58, 'Dhe s’ka asnjë vend që Ne nuk do ta zhdukim atë para Ditës së Kijametit, ose t’ia nënshtrojmë mundimit të madh. Kjo është shkruar në Libër.'),
(2088, 17, 59, 'Neve asgjë nuk na pengon që të dërgojmë mrekullira, mirëpo ato i kanë përgënjeshtruar popujt e lashtë, e i dhamë popullit të Themudit – devën si mrekulli të qartë, por ata, nuk besuan në të. Na, dërgojmë mrekullira vetëm për t’i frikësuar njerëzit.'),
(2089, 17, 60, '(Kujtoje ti) kur të kemi thënë: “Me të vërtetë, Zoti yt i ka kapluar njerëzit (me dije dhe fuqi)!” E, ëndërrën që ta kemi treguar ty, e kemi bërë vetëm sprovë për njerëzit si dhe drurin e mallkuar (të përmendur) në Kur’an. Dhe Na, i frikësojmë ata, por kjo vetëm ua shton atyre gufimin e mohimit të madh.'),
(2090, 17, 61, '(Përkujtoju atyre) kur u kemi thënë engjëjve: “Bëni sexhde Ademit!” Ata të gjithë i bënë sexhde, përpos Iblisit! Ai, ka thënë: “Vallë unë t’i bëj sexhde atij, të cilin Ti e ke krijuar nga balta?”'),
(2091, 17, 62, '(Djalli) tha: “A e konsideron Ti këtë më të mirë se mua? Me të vërtetë, nëse më lë deri në Ditën e Kijametit, unë do t’i shfaros me mashtrim pasardhësit e tij, përpos një numri të vogël nga ata”.'),
(2092, 17, 63, '(Perëndia) tha: “Shko! E, (ty dhe) atyre që të pasojnë ty – nga pasardhësit e tij, me të vërtetë, xhehennemi është dënimi juaj – dënim i plotë.'),
(2093, 17, 64, 'Dhe, bëri për vete – me zërin tënd, - kë të mundesh nga ata, dhe sulmo në ta me britmë me kalorësinë dhe këmbësorinë tënde dhe integrohu me pasurinë dhe fëmijët e tyre dhe jepju premtime!” E, djalli, nuk u premton atyre tjetër, përpos mashtrimit.'),
(2094, 17, 65, 'Me të vërtetë, ti nuk ke kurrfarë pushteti ndaj robërve të Mi! Të mjafton ty Zoti yt si mbrojtës!'),
(2095, 17, 66, 'Zoti juaj është ai që e lëvizë anijen nëpër det për të kërkuar dhuntinë e Tij (Perëndisë). Ai, me të vërtetë, është mëshirues ndaj jush.'),
(2096, 17, 67, 'E, kur t’ju godasë juve fatkeqësia në det, zhduken ata që ju i adhuronit, përpos Tij. E, pasi t’ju shpëtojë (e t’ju qesi) në tokë, ju ktheheni (kah mohimi). Njeriu, është mohues (i të mirave).'),
(2097, 17, 68, 'Po, a jeni të sigurt që (Perëndia) nuk do t’ju fundosë në tokë, ose që nuk do të dërgojë mbi ju stuhi me rërë (orkan) e që pastaj, ju nuk do të gjeni mbrojtës.'),
(2098, 17, 69, 'Apo, jeni të sigurt që Ai juve nuk do t’ju kthejë njëherë tjetër në det, e që në ju të dërgojë furtunë e t’ju fundosë, ngase keni mohuar. E pastaj, ju nuk do të gjeni kundër Neve askend, i cili do të na thërrasë në përgjegjësi për këtë.'),
(2099, 17, 70, 'Na, me të vërtetë, i kemi dekuruar bijt e Ademit: u kemi bërë të mundshme të udhëtojnë në tokë dhe në det, duke i furnizuar me gjellëra të shijshme, dhe u kemi dhënë përparësi të madhe ndaj shumicës nga ata që i kemi krijuar.'),
(2100, 17, 71, '(Kujtoje) atë Ditë kur Na do t’i thërrasim të gjithë njerëzit me udhëheqësin (pejgamberin) e vet. E, kujt i jepet libri i tij në anën e djathtë, ata do ta lexojnë librin e vet dhe nuk do të dëmtohen as sa një thërrmi.'),
(2101, 17, 72, 'Ai që në këtë botë ka qenë i verbër, ai do të jetë i verbër edhe në botën tjetër dhe ai më së tepërmi e ka humbur rrugën.'),
(2102, 17, 73, 'Dhe, për pak – me të mashtruar ty ata nga ajo që të kemi shpallur Ne, për të shpifë diçka tjetër kundër Neve. Dhe atëherë, ata do të merrnin ty për mik.'),
(2103, 17, 74, 'Dhe, sikur mos të kishim forcuar Na ty (në drejtim), gati do të kishe anuar pak kah ata'),
(2104, 17, 75, 'dhe atëherë, me të vërtetë, Na do të bënim që të shijosh dënimin e dyfishtë në këtë botë dhe dënimin e dyfishtë pas vdekjes; e atëherë, nuk do të gjejshe askend për të ndihmuar kundër Nesh.'),
(2105, 17, 76, 'Me të vërtetë, gjendja ka qenë e atillë, për të shqetësuar dhe me të dëbuar ty nga toka, atëherë, (nëse do ta bënin këtë), ata do të qëndronin pas teje (në atë tokë) vetëm pak kohë.'),
(2106, 17, 77, 'Ky është ligji Jonë që e kemi bërë për pejgamberët e dërguar para teje. Dhe, as ti nuk do të gjesh shmangie nga ligji i Perëndisë.'),
(2107, 17, 78, 'Kryeje namazin prej kthimit të Diellit (nga gjysma e qiellit) deri në errësirën e natës dhe leximin e Kur’anit në agim (namazin e sabahut); me të vërtetë, namazi i sabahut është i dëshmuar.'),
(2108, 17, 79, 'Dhe, kaloje ti një pjesë të natës (duke bërë namaz), si shtojcë të lutjes vetëm për ty! Është shpresë, se Zoti yt do të vendosë ty në një vend të lavdëruar.'),
(2109, 17, 80, 'Dhe, thuaj: “O Zoti im, më shpjerë Ti mua në një hyrje të kënaqur (në vdekje), dhe më nxjerr (ringjallem) në një dalje të kënaqur, dhe, më dhuro nga Ti fuqi, që do të më ndihmojë!”'),
(2110, 17, 81, 'Dhe, thuaj: “E vërteta – arriti, e gënjeshtra (e keqja) u shkatërrua. Me të vërtetë, gënjeshtra është në zhdukje!”'),
(2111, 17, 82, 'Dhe, Ne, shpallim nga Kur’ani atë që është ilaç dhe mëshirë për besimtarët, e mohuesve – ai vetëm ua shton humbjen.'),
(2112, 17, 83, 'E, kur Na i japim dhunti njeriut, ai shmanget dhe largohet anash, e kur e godet e keqja është i dëshpëruar.'),
(2113, 17, 84, 'Thuaj: “Çdokush punon në mënyrë të vet, por Zoti juaj di më së miri kush është në rrugën më të drejtë”.'),
(2114, 17, 85, 'do të pyesin ty për shpirtin. Thuaj: “Shpirti është krijuar me urdhërin e Zotit tim, e juve u është dhënë vetëm pak dijeni (se dijenia e plotë është vetëm e Perëndisë)”.'),
(2115, 17, 86, 'Me të vërtetë, sikur të donim Ne, do ta merrnim ty atë shpallje që ta kemi dhënë, dhe ti, pastaj nuk do të gjeje askënd që do të ndihmonte kundër Nesh,'),
(2116, 17, 87, 'përpos mëshirës nga ana e Zotit tënd. Me të vërtetë, mirësia e Tij ndaj teje, është e madhe.'),
(2117, 17, 88, 'Thuaj: Sikur të tuboheshin të gjithë njerëzit dhe xhindët, për të hartuar një Kur’an të tillë, ata nuk do të mund ta hartonin ashtu siç është ai, madje, edhe sikur t’i ndihmonin njëri-tjetrit”.'),
(2118, 17, 89, 'Na, në këtë Kur’an, u shpjegojmë njerëzve gjithfarë shembujsh, por shumica e njerëzve, kurrsesi nuk duan të besojnë.'),
(2119, 17, 90, 'Dhe, ata thonë: “Na nuk do të besojmë ty, derisa të mos na nxjerrësh nga toka – gurra të ujit,'),
(2120, 17, 91, 'ose, derisa të mos kesh kopsht hurmesh dhe rrushi, e që nëpër të, të sjellish lumenj, rrjedhës,'),
(2121, 17, 92, 'ose, derisa të mos e rrëzosh qiellin në ne – si thua ti në copa-copa; ose, përderisa të mso sjellësh Perëndinë dhe engjëjt, si garantues;'),
(2122, 17, 93, 'ose, derisa të mos kesh shtëpi prej ari, ose përderisa të mos hipësh në qiell. Na, nuk do të besojmë ty – edhe sikur të hipësh në qiell, derisa të mos na sjellësh një libër që ta lexojmë ne”. Thuaj: “Lavdi për Zotin tim, unë nuk jam tjetër, përpos njeri – pejgamber!”'),
(2123, 17, 94, 'Njerëzit nuk i pengon të besojnë, kur ju erdhi shpallja, (asgjë) përpos që thanë: “A mos vallë Perëndia e dërgon njeriun si pejgamber?”'),
(2124, 17, 95, 'Thuaj: “Sikur engjëjt në Tokë të ecnin qetësisht, Na do t’u dërgonim atyre nga qielli engjëj – pejgamberë”.'),
(2125, 17, 96, 'Thuaj: “Perëndia mjafton për dëshmitar në mes meje dhe jush. Me të vërtetë, Ai i di dhe i sheh robërit e Vet.'),
(2126, 17, 97, 'Atë që e udhëzon Perëndia në rrugë të drejtë, ai është i udhëzuar, e kë ta shmangë Ai ng rruga e drejtë, me siguri nuk do të gjendet mbrojtës për të, përpos Tij. Dhe Na, do t’i tubojmë ata në Ditën e Kijametit (duke i tërhequr zvarrë) me fytyrat e tyre të verbëta, shurdhe e memece. Vendbanimi i tyre është xhehennemi. Kurdo që të zbehet ai (zjarri), Na ia shtojmë flakën.'),
(2127, 17, 98, 'Ky është dënimi i tyre, ngase kanë mohuar argumentet Tona, e që kanë thënë: “A mos vallë kur të bëhemi eshtra dhe pluhur, a thua ne do të ngjallemi si krijesa të reja?”'),
(2128, 17, 99, 'A nuk e dinë ata, se Perëndia, Krijues i qiejve dhe i Tokës është i gjithpushtetshëm të krijojë qenie të ngjashme (si ata), dhe që, madje, duke ia cktuar momentin e ringjalljes, për të cilën nuk ka dyshim? Por, mohuesit, nuk pranojnë (asgjë), ata vetëm mohojnë.'),
(2129, 17, 100, 'Thuaj: “Sikur ju të posedoni thesaret e mëshirës së Zotit tim, edhe atëherë ju do të bënit koprraci nga droja se do të shpenzoheni. Me të vërtetë, njeriu, rëndom, është koprrac”.'),
(2130, 17, 101, 'Na i kemi dhënë Musait nëntë argumente të qarta, e pyeti bijt e Israelit, kur u erdhi të parëve të tyre dhe kur Faraoni i tha atij: “O Musa, unë të konsideroj ty për të magjepsur”,'),
(2131, 17, 102, '(Musai) tha: “Me të vërtetë, ti e di se këto mrekulli të qarta, nuk i ka shpallur askush tjetër, përpos Zotit të qiellit dhe të Tokës, dhe unë jam i bindur se ti je i hudhur (në humbje)”.'),
(2132, 17, 103, 'Dhe, Faraoni deshi t’i dëbojë nga vendi, e Ne – e fundosëm atë dhe të gjithë ata që ishin me të,'),
(2133, 17, 104, 'dhe pas kësaj, u thamë bijve të Israelit: “Vendosuni në tokën (nga e cila ka dashtë t’ju dëbojë Faraoni). E, kur të arrijë premtimi i Ditës së Kijametit, do t’u sjellim juve të përzier”.'),
(2134, 17, 105, 'Dhe, Ne, e kemi shpallur Kur’ani në mënyrë të vërtetë dhe ai është i shpallur me plotë dijeni. Dhe, Ne, të kemi dërguar ty vetëm sihariq dhe paralajmërues.'),
(2135, 17, 106, 'Dhe, Ne, Kur’anin e kemi shpallur pjesë-pjesë, që ti t’jua mësosh njerëzve dalngadalë, dhe Ne, e kemi shpallur atë kohë pas kohe (sipas nevojës).'),
(2136, 17, 107, 'Thuaj: “Besoni në te (Kur’an) apo nuk besoni, me të vërtetë, atyre që u është dhënë dijenia para tij, kur t’ju mësohet ai (Kur’ani), binë me fytyrat e veta, duke i bërë sexhde Atij (Perëndisë),'),
(2137, 17, 108, 'dhe thonë: “Qoftë i lavdëruar Zoti ynë! Me të vërtetë, premtimi i Zotit tonë është i kryer!”'),
(2138, 17, 109, 'Dhe, bien me fytyrë, për tokë, duke qajtur, dhe ai (leximi i Kur’anit) ua rritë përuljen atyre.'),
(2139, 17, 110, 'Thuaj: “Thirre: ‘Mëshiruesin (Ja Rrahman!)’. Me cilindo emër që ta thirrni ju Atë, Ai ka emrat më të bukur. Mos e lexo Kur’anin me zë të lartë, e as edhe me zë tepër të ulët! Kërkoje në mes këtyre – rrugën (formën) mesatare”.'),
(2140, 17, 111, 'Dhe thuaj: “Qoftë lavdëruar Perëndia i cili (për Vete) nuk ka marrë fëmijë, dhe i cili nuk ka shok në pushtet, dhe i cili nuk ka nevojë për ndihmës, për shkak të pafuqisë dhe madhëroje Ate me madhërinë e plotë!”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Qoftë lavdëruar Perëndia, i cili ia shpalli Librin robit të Vet (Muhammedit), e që, nuk ka në të kurrfarë shtrembërimi, por'),
(2142, 18, 2, 'është i drejtë për të paralajmëruar me anë të tij dënim të ashpër (për mohuesit), e që t’i sihariqojë besimtarët, që punojnë vepra të mira, se, për ta ka shpërblim të bukur,'),
(2143, 18, 3, 'duke qëndruar në të (xhennet), përherë,'),
(2144, 18, 4, 'dhe, që t’i paralajmërojë ata që thonë: “Perëndia ka marrë për Vete – fëmijë”.'),
(2145, 18, 5, 'Ata, nuk kanë kurrfarë dijenie për këtë, e as etërit e tyre. Është kjo fjalë e madhe (e shëmtuar) që del nga goja e tyre! Ata flasin vetëm gënjeshtra.'),
(2146, 18, 6, 'A mos vallë do të bësh vetëvrasje për shkak të tyre, nëse ata nuk besojnë në këtë Kur’an, duke qenë i pikëlluar ti?'),
(2147, 18, 7, 'Na, çdo gjë që gjendet në Tokë, e kemi bërë stoli për të (njerëzit e Tokës), për të provuar se kush prej tyre është veprues më i mirë.'),
(2148, 18, 8, 'Dhe, Na, do të bëjmë atë që gjendet në të (Tokë) – sipërfaqe të thatë.'),
(2149, 18, 9, 'Vallë, a mendon ti se shoqëria e Shpellës dhe e Rekimit (vendbanim) është një mrekulli nga argumentet Tona?'),
(2150, 18, 10, '(Kujtoje ti) kur disa sjelmosha u strehuan në shpellë dhe thanë: “O Zoti ynë, dhurona mëshirë nga ana Jote dhe përgatitna neve në udhëzim të drejtë!”'),
(2151, 18, 11, '(Ua plotësuam lutjen e tyre) dhe ua kemi mbyllur veshët atyre (i vumë në gjumë) në shpellë për shumë vjet.'),
(2152, 18, 12, 'Pastaj i kemi zgjuar ata, për të treguar (haptazi) se cili nga këto dy grupe e vlerësojnë më mirë sa kohë kanë qenë të strehuar.'),
(2153, 18, 13, 'Na po ta tregojmë ty historinë e tyre me tërë vërtetësinë e saj. Ata, ishin djelmoshat që besonin në Zotin e tyre, dhe Na ua kemi forcuar atyre drejtimin.'),
(2154, 18, 14, 'Dhe Ne, ua forcuam zemrat e tyre kur u ngritën dhe thanë: “Zoti ynë është Zoti i qiejve dhe i Tokës. Na, nuk i lutëm, kurrsesi – zotit tjetër, përpos Tij. (Përndrushe) – atëherë do të thonim gënjeshtër të madhe.'),
(2155, 18, 15, 'Ky, populli ynë, ka marrë – përveç Tij, zotra tjerë. Sikur të kishin sjellë ata – për ta (zotërat) argument të qartë! E, kush është zullumqar më i madh se ai që trillon gënjeshtra për Perëndinë?'),
(2156, 18, 16, 'Kur t’i lëni ju ata (popullin tuaj) dhe atyre që iu luteshin ata, përveç Perëndisë – (i thanë njëri-tjetrit): “Strehohuni në shpellë, Zoti juaj do t’ju begatojë juve nga mëshira e Tij dhe do t’ju përgatisë për veprën tuaj, atë, nga e cila do të keni dobi”.'),
(2157, 18, 17, 'Ti e shikon Diellin kur lind, se ai anon kah ana e djathtë prej shpellës së tyre, e kur perëndon, ata i lë në anën e majtë, e ata gjendeshin në mesin e saj. Ky është nga argumentet e Perëndisë. Kënd e udhëzon Perëndia në rrugë të drejtë, ai është i udhëzuar, e kënd Ai e lë në humbje, ti, me siguri – nuk do të mund t’i gjesh ndihmës, që do ta udhëzojë atë.'),
(2158, 18, 18, 'Dhe, ti, mendon se ata janë zgjuar, por ata janë në gjumë; dhe Ne i rrotullonim ata – herë në krah të djathtë, e herë në të majtë, kurse qeni i tyre, kishte shtrirë dy këmbët e para në hyrje të shpellës; që sikur t’i kishte parë ti, do të ikëshe nga ata dhe do të kaplonte frika.'),
(2159, 18, 19, 'Ashtu (siç i kemi vënë në gjumë) Ne i kemi zgjuar ata, që të pyesin, njëri-tjetrin. Njëri nga ata tha: “Sa keni ndenjur (në gjumë)?” – U përgjegjën: “Kemi qëndruar një ditë, apo një pjesë të ditës”. (Disa nga ata) thanë: “Zoti juaj di më së miri, sa keni ndenjur. Dërgoni njërin prej jush me këtë monedhë të argjendtë tuajën, në qytet, e le të shikojë se kush prej tyre (në qytet) ka ushqim më të pastër. Le t’ju sjellë juve furnizim nga ajo, le të sillet mirë dhe askënd mos ta lajmërojë për ju.'),
(2160, 18, 20, 'Me të vërtetë, ata, nëse ju zbulojnë, do t’ju gjuajnë (mbysin) me gurë, ose do t’ju kthejnë juve (me forcë) në fenë e tyre, dhe atëherë nuk do të shpëtoni kurrsesi!”'),
(2161, 18, 21, 'Dhe, kështu, Ne i bëmë me dije (njerëzit) për ta, që të dinë se premtimi i Perëndisë është i vërtetë dhe, që s’ka dyshim, se do të arrijë Dita e Kijametit, kur ata bashkëqytetarët (bashkëvendësit), polemizonin në mes vete për rastin e tyre dhe thonin: “Ndërtoni në hyrje të saj (shpellës) një godinë! Zoti i tyre i di më së miri ata”. E thonë ata, mendimi i të cilëve mbizotëroi: “Na do të ndërtojmë një faltore rreth tyre!”'),
(2162, 18, 22, 'Disa thonin: “Ishin tre vetë, e qeni i tyre ishte i katërti”. E, disa (të tjerë) thonin: “Ishin pesë vetë, e qeni i tyre ishte i gjashti”, duke supozuar për atë që është sekrete, derisa – të tjerë thonin: “Ishin shtatë vetë, e i teti i tyre ishte qeni”. Thuaj: “Zoti im di më së miri numrin e tyre”. Për ta, dinë, vetëm një numër i vogël dhe mos polemizo për ta – vetëm në mënyrë të përciptë dhe, për ta mos pyet askë prej tyre!”'),
(2163, 18, 23, 'Dhe, kurrsesi, mos thuaj për çkadoqoftë, se: “Unë do ta bëj atë – me siguri, nesër!” – pa thënë (shtuar):'),
(2164, 18, 24, '“Në dashtë Perëndia!” Dhe, kur të harrosh – kujtoje Zotin tënd – e thuaj: “Shprsoj se Zoti im do të më udhëzojë në atë që është më e mirë dhe më e dobishme nga kjo”.'),
(2165, 18, 25, 'E, ata, kanë qëndruar në shpellën e tyre – treqind vjet, duke shtuar nëntë (vjet).'),
(2166, 18, 26, 'Thuaj: “Perëndia di më së miri sa kanë qëndruar. Vetëm për Te është fshehtësia e qiejve dhe e Tokës. Se sa mirë i sheh Ai ato dhe sa mirë i dëgjon ato! Ata (njerëzit), nuk kanë asnjë ndihmës, përpos Tij, e Ai nuk merr askë për ndihmës në vendimet e Tij”.'),
(2167, 18, 27, 'Dhe lexo atë që të është shpallur ty nga Libri (Kur’ani) i Zotit tënd! Askush nuk mund të ndryshojë fjalët e Tij – dhe ti nuk do të gjesh kurrfarë strehimi, përpos tek Ai.'),
(2168, 18, 28, 'Dhe, bënu i durueshëm, me ata, që i luten Zotit të vet, në mëngjes dhe mbrëmje, duke dëshiruar kënaqësinë e Tij dhe mos i shmang sytë e tu nga ata (në të tjerë), duke dëshiruar stolitë e kësaj jete. Mos dëgjo atë – zemrën e të cilit ia kemi lënë të zbrazët nga të përmendurit Neve dhe që i ndjek qejfet (epshet) e veta, dhe punët e të cilit janë të shfrenuara,'),
(2169, 18, 29, 'dhe, thuaj: “E vërteta është nga Zoti yt. Kush të dojë, le të besojë, e kush të dojë, le të mohojë (e për të, ka dënim)”. Na për zullumqarët kemi përgatitur zjarr i cili do t’i rrethojë ata nga të gjitha anët (si çadra). Nëse kërkojnë ndihmë (për shkak të etjes), do t’ju afrohet një ujë – si xeheja e shkrirë që ua përcëllon fytyrën. Pije e shëmtuar është (ajo), e sa strehim i keq është (zjarri)!'),
(2170, 18, 30, 'Ata që besojnë dhe bëjnë vepra të mira, Na – me të vërtetë, nuk do t’ia humbim shpërblimin, atij që bënë vepra të mira –'),
(2171, 18, 31, 'për ta është xhenneti i Adnit, nëpër të cilin rrjedhin lumenjt. Në të, ata do të stolisen me bylyzykë ari dhe do të vishen me rroba të gjelbërta – nga mëndafshi i hollë dhe i trashë, duke ndenjur të mbështetur në kanape. Shpërblim i mrekullueshëm është ky! Dhe, sa vendbanim i bukur që është ky!'),
(2172, 18, 32, 'Dhe, sjell si shembull – për ata, dy njerëz. Njërit prej tyre i kemi dhënë dy vreshta dhe i kemi rrethuar me hurme, e në mes tyre kemi bërë ara.'),
(2173, 18, 33, 'Të dy vreshtat kanë dhënë frutet e veta, asgjë nuk ka munguar, dhe kemi bërë që, në mes tyre të rrjedh lumi.'),
(2174, 18, 34, 'Ai, ka pasur edhe një tjetër pasuri. Dhe, i tha shokut të vet në bisedim: “Unë kam më shumë pasuri se ti, dhe, unë jam më i fortë se ti – me rreth (shoqëri)”.'),
(2175, 18, 35, 'Dhe hyri në kopshtin e vet, duke i bërë vetes dëm (zullum) me mendjemadhësi, e tha: “Unë nuk mendoj se do të humbë kjo ndonjëherë,'),
(2176, 18, 36, 'dhe nuk mendoj se do të arrijë ndonjëherë Dita e Kijametit, e nëse unë do t’i kthehem Zotit tim, unë me të vërtetë, do të gjej vend më të mirë kthimi nga ky!”'),
(2177, 18, 37, 'Dhe, shoku i tij, i tha – duke biseduar me të: “A mohon ti Ate, i cili të ka krijuar prej dheut, pastaj nga pika e farës (sperma), dhe më në fund – të bëri njeri të plotë?'),
(2178, 18, 38, 'Por, unë besoj se Perëndia është Zoti im, dhe unë – Zotit tim, nuk i bëj shok askë.'),
(2179, 18, 39, 'Dhe, sikur ti, kur hyre në kopshtin tënd, të kishe thënë: (kjo është) si ka dashtë Perëndia! Askush nuk ka fuqi (që të ndryshojë) përveç Zotit. Nëse ti sheh se unë kam më pak pasuri dhe pasardhës,'),
(2180, 18, 40, 'shpresoj se Zoti im do të më jep më mirë se kopshti yt, e në kopshtin tënd të dërgojë shtatërrim nga qielli e të bëhet sipërfaqe e lëmuar,'),
(2181, 18, 41, 'ose, që uji të humbasë në greminë (në nëntokë), e që të mos mund ta gjesh kurrë më”.'),
(2182, 18, 42, 'Dhe, u shkatërrua pasuria e tij (e mohuesit), e ai i rrihte duart e veta (nga pikëllimi), për atë që investoi në të, e atë që e punoi, ra për dhé, e thirri: “Ah! Sikur të mos i kisha bërë Zotit tim kurrfarë shoku!”'),
(2183, 18, 43, 'Dhe, ai nuk kishte kurrfarë shoqërie, që t’i ndihmonte atij, përpos Perëndisë, e ai nuk ishte i ndihmuar as nga vetvetja.'),
(2184, 18, 44, 'Këtu, mund të ndihmojë vetëm Perëndia – i Vërteti. Ai më së miri shpërblen dhe jep përfundimin më të mirë.'),
(2185, 18, 45, 'Krahasoju atyre shembullin e jetës së kësaj bote, me ujin që e lëshojmë Ne nga qielli, e me të përzihet bima në tokë, e (më vonë) bëhet bar i thatë, të cilin e shpërndanë era (fryma). E, Perëndia është i fuqishëm për çdo gjë!'),
(2186, 18, 46, 'Pasuria dhe djemtë janë stoli e jetës së kësaj bote, por veprat e mira, që mbetën përherë, janë më mirë të shpërblyera te Zoti dhe janë shpresë më e mirë.'),
(2187, 18, 47, '(Kujtoju atyre) Ditën kur Ne i lëkundim bjeshkët dhe ti sheh tokën e zhveshur dhe kur Ne, i ringjallim njerëzit (për të dhënë llogari), e nuk e lëmë askë anash (të paringjallur).'),
(2188, 18, 48, 'Dhe ata do të sillen para Zotit tënd, të radhitur (e atyre u thuhet): “Ju, Na keni ardhur ashtu siç u kemi krijuar herën e parë. E, ju keni menduar, se nuk do t’jua plotësojmë premtimin.'),
(2189, 18, 49, 'Dhe, do të sillet Libri (i punëve që kanë bërë), e do t’i shohësh mëkatarët se si tmerrohen nga ato që gjenden në të dhe thonë: “Të mjerët ne, çfarë është ky libër! Nuk po lëjka as të voglën as të madhen (punë) pa e numëruar”. Dhe, do të gjejnë të shënuar atë që kanë punuar. Zoti yt, nuk i bënë askujt padrejtësi.'),
(2190, 18, 50, '(Kujtuju atyre) kur ju thamë engjëjve: “Bëni sexhde (përuljuni) Ademit!” Ata, i bënë sexhde, përpos Iblisit. Ai ish nga xhindët dhe andaj bëri mëkat ndaj urdhërit të Zotit të vet. A mos vallë, atë dhe pasardhësit e tij do t’i merrni ju për miq – përpos Meje? E, ata, janë armiët tuaj. Sa ndërrim i shëmtuar që është ky për zullumqarët!'),
(2191, 18, 51, 'Unë, nuk i kam marrë ata për dëshmitarë gjatë krijimit të qiejve dhe të Tokës, as disa prej tyre gjatë krijimit të të tjerëve, dhe, Unë, nuk i kam marrë për ndihmës ata që mashtrojnë.'),
(2192, 18, 52, '(Kujtoju atyre) kur thotë (Perëndia): “Thirrni shokët e Mi që ju mendoni (se janë shokët e Mi)”. E, ata i thirrën, por ata nuk u përgjegjën.'),
(2193, 18, 53, 'Dhe, mëkatarët do të shohin zjarrin dhe do të bindën, se, ata me të vërtetë, do të bijnë në të dhe, nuk do të gjejnë largim nga ai vendstrehim.'),
(2194, 18, 54, 'Na, në këtë Kur’an u kemi shpjeguar njerëzve gjithfarë lloj shembujsh, por njeriu, më tepër se kushdo është polemizues.'),
(2195, 18, 55, 'Njerëzit asgjë nuk i pengon që të besojnë – kur iu ka ardhur atyre udhëzimi – dhe të kërkojnë falje nga Zoti i tyre, përveç që t’ju vijnë atyre ligji (i Perëndisë) i popujve të lashtë (shkatërrimi), ose që t’ju vijë dënimi i hapët.'),
(2196, 18, 56, 'Na i dërgojmë pejgamberët vetëm si sihariques dhe paralajmërues, e mohuesit polemizojnë me gënjeshtra për ta shmangë të Vërtetën, dhe, - argumentet e Mia dhe paralajmërimet, i marrin për tallje.'),
(2197, 18, 57, 'Kush është më zullumqarë se ai, i cili është këshilluar me argumentet e Zotit të vet, e u shmangë nga ato dhe harroi se çka kanë punuar duartë e tij më parë (nga të këqiat)? Na, kemi vërë perde në zemrat e tyre për të mos e kuptuar Kur’anin dhe ua kemi rënduar veshët e tyre. E, nëse i thërret ti – ata, në rrugën e drejtë, ata kurrë nuk do të pasojnë atë (rrugën e drejtë).'),
(2198, 18, 58, 'E, Zoti yt është falës dhe mëshirues. Sikur t’i dënonte për atë që kanë bërë, Ai do t’ua shpejtonte dënimin (në këtë jetë). Jo, për ta – është momenti i caktuar, nga i cili ata, kurrë nuk do të gjejnë strehim.'),
(2199, 18, 59, 'E, ato janë vendbanimet, që Ne i kemi shkatërruar, kur ato bënë zullum, e për shkatërrimin e tyre kemi bërë kohë të caktuar.'),
(2200, 18, 60, '(Kujtoje) kur Musai i tha djaloshit (përcjellësit) të vet: “Do të udhëtojë vazhdimisht, derisa të arrijë në vendin ku takohen dy detet, ose do të udëhëojë shumë kohë”.'),
(2201, 18, 61, 'Dhe, kur ata të dy arritën në vendin ku takoheshin ato (detet), harruan peshkun e tyre (që – e kishin me vete shenjë) e që, ai (peshku) rrëshqiti në det.'),
(2202, 18, 62, 'Kur ata kaluan (atë vend), Musai i tha përcjellësit të vet: “Na jep atë sillën tonë, sepse, jemi lodhur nga ky udhëtim ynë”.'),
(2203, 18, 63, '(Ai) i tha: “Shiko! Kur zbritëm tek ai shkëmbi, unë e harrova peshkun. Vetëm djalli më ka shpie në harresë, përme ta kujtuar ty atë, e që, peshku – për çudi, mori rrugën kah deti!”'),
(2204, 18, 64, '(Musai) tha: “E, kjo është ajo që po kërkojmë. Andaj u kthyen nëpër gjurmët e veta (duke i treguar njëri-tjetrit),'),
(2205, 18, 65, 'dhe gjetën një rob prej robërve Tonë, të cilit i kemi dhuruar mëshirën Tonë dhe e kemi mësuar nga ana Jonë me dijeni të plotë.'),
(2206, 18, 66, 'Musai i tha atij: “A të pasoj ty, për të më mësuar mua – me një pjesë të dijenisë së mrekullueshme që t’u ka dhënë ty?”'),
(2207, 18, 67, 'Ai i tha (Musait): “Ti nuk mund të durosh (në shoqëri) me mua;'),
(2208, 18, 68, 'e si do të durosh për atë, për të cilën nuk di asgjë?”'),
(2209, 18, 69, '(Musai) tha: “Do të më gjesh të durueshëm, në dashtë Perëndia, e nuk do të kundërshtoj ty asgjë”.'),
(2210, 18, 70, 'I tha (Musait): “Nëse shkon pas meje, mos më pyet për asgjë, përderisa të mos ta shpjegoj unë ty!”'),
(2211, 18, 71, 'Dhe, shkuan (ata), e kur hipën në anije, e zhbiroi (dërrasën e anijes). (Musai) tha: “Mos vallë, e zhbirove atë, për t’i mbytë udhëtarët e saj? Me të vërtetë, ke bërë punë të çuditshme (të shëmtuar)”.'),
(2212, 18, 72, 'I tha (Musait): “A nuk të thashë unë se ti nuk mund të durosh (në shoqëri) me mua?”'),
(2213, 18, 73, '(Musai) tha: “Mos më qorto për atë që kam harruar, dhe mos më ngarko me vështirësi në punët e mia!”'),
(2214, 18, 74, 'Dhe, shkuan (ata), e kur e takuan një djalosh, dhe e mbyti atë tha (Musai): “Pse e mbyte njeriun e pafajshëm që nuk ka mbytur askë! Me të vërtetë, bëre një punë të shëmtuar!”'),
(2215, 18, 75, 'I tha (Musait): “A nuk të thasë vallë unë ty, se ti – me të vërtetë, nuk mund të durosh me mua (në shoqëri)”.'),
(2216, 18, 76, '(Musai) tha: “Nëse të pyes ynë ty – çkadoqoftë – pas kësaj, mos më merr mua në shoqëri. Ja pra, me të vërtetë, ia arrite arsyetimit nga ana ime!”'),
(2217, 18, 77, 'Dhe, shkuan (ata). E, ur arritën te banorët e një fshati, (nga të cilët) kërkuan që t’i ushqejnë (të dy), ata (fshatarët) refuzuan që t’i marrin mysafirë; e ata, gjetën në fshat një mur, që ish duke u rrëzuar, e të cilin – ai (shoku i Musait), e drejtoi. (Musai) tha: “Sikur të kishe dashur ti, do të merrshe shpërblim për këtë”.'),
(2218, 18, 78, '(Shoku i Musait) tha: “Kjo është ndarja në mes meje dhe teje. Unë do të shpjegoj ty për ato gjëra që ti nuk munde t’i durosh.'),
(2219, 18, 79, 'Sa i përket anijes, ajo ka qenë pronë e të varfërve që punonin në det, andaj, unë desha ta dëmtoj, sepse, para tyre, gjendej një sundimtar, i cili çdo anije (të shëndoshë) e merrte me dhunë.'),
(2220, 18, 80, 'E sa i përket djaloshit, prindërit e tij ishin besimtarë, andaj, ne u frikuam që ai (djali i tyre) mos t’i kthejë ata në të keqe dhe mohim,'),
(2221, 18, 81, 'e ne dëshirojmë që Zoti i tyre, në vend të atij (djalit), t’ua jap më të mirë dhe më të pastër (prej mëkateve) dhe më të mëshirueshëm (ndaj prindërve);'),
(2222, 18, 82, 'e sa i përket atij murit, - ai u takonte dy djelmoshave bonjakë në qytet, e nën at (mur) gjendej një thesar i tyre. E, babai i tyre ka qenë njeri i mirë dhe Zoti yt dëshiroi që ata të arrijnë moshën e pjekurisë dhe ta nxjerrin thesarin e tyre; - kjo është mëshira e Zotit tënd. Unë këtë, nuk e kam bërë sipas gjykimit tim (por, me urdhër të Perëndisë). Ky është, shpjegimi i asaj, për të cilën ti s’mund u durove!'),
(2223, 18, 83, 'Dhe, të pyesin ty për Dhulkarnejnin. Thuaj: “Unë do t’ju tregoj disa lajme për të”.'),
(2224, 18, 84, 'Na i kemi mundësuar atij (që të bëjë ç’të dojë) në Tokë dhe i kemi dhënë mjete për çdo gjë (që i janë nevojitur)'),
(2225, 18, 85, 'dhe, ai u nis në një rrugë.'),
(2226, 18, 86, 'Kur arriti te vendi i perëndimit të Diellit, e gjeti atë (të ashtudukshëm) që praronte në një burim balte të zi dhe në afërsi të tij gjeti një popull. Na thamë: “O Dhulkarnejn, ose do t’i dënosh ata ose do të sillesh bukur ndaj tyre”.'),
(2227, 18, 87, '(Dhulkarnejni), tha: “Sa i përket atij që bënë zullum, na do ta ndëshkojmë atë, e pastaj do të kthehet te Zoti i vet, e që edhe Ai do ta dënojë me dënim të rëndë.'),
(2228, 18, 88, 'E, ai që beson dhe bënë vepra të mira, dhe Ne, do t’i premtojmë lehtësira atij”.'),
(2229, 18, 89, 'Dhe, ai përsëri shkoi (në një rrugë).'),
(2230, 18, 90, 'Dhe, kur arriti te vendi i lindjes së Diellit, ai gjeti se ai (Dielli) lindte mbi një popull, të cilin, Ne nuk kemi bërë perde për ta (për t’u mbrojtur).'),
(2231, 18, 91, 'Kështu ka qenë ajo (ngjarja e Dhulkarnejnit). Na e kemi përfshirë tërë dijeninë se ç’ka pasur tek ai.'),
(2232, 18, 92, 'Dhe, ai përsëri shkoi (në një rrugë kah veriu).'),
(2233, 18, 93, 'Kur arriti në mes dy maleve, gjeti afër tyre një popull, i cili, pothuaj nuk kuptonte asnjë të folme.'),
(2234, 18, 94, 'Ata thanë: “O Dhulkarnejn, - me të vërtetë, Jexhuxhi dhe Mexhuxhi janë (popuj) ngatërrestarë në Tokë. A don të të japim dhuratë që të ndërtosh pendën në mes nesh dhe atyre?”'),
(2235, 18, 95, '(Dhulkarnejni) tha: “Atë që ma ka mundësuar Zoti im, është më e mirë (nga ajo që ofroni ju), por më ndihmoni ju mua me fuqi (jo me pasuri)! Do të ndërtoj një pendë në mes jush e atyre.'),
(2236, 18, 96, 'Më sillni copa hekuri!” E, kur ai i rrafshoi dy anët e bjeshkëve, tha: “Fryeni zjarrit, përderisa hekuri të mos skuqet si zjarri”. Pastaj tha: “Më sillni bakër të shkrirë që ta hudhi në të”.'),
(2237, 18, 97, 'Ata (Jexhuxhi dhe Mexhuxhi), nuk kanë mundur ta kapërcejnë as nuk kanë mundur ta zhbirojnë (pendën).'),
(2238, 18, 98, '(Dhulkarnejni) tha: “Kjo ështe dhuntia e Zotit tim! E, kur të arrijë premtimi i Zotit tim, (Ai) do ta rrafshojë atë (pendën). Premtimet e Zotit tim janë të vërteta.'),
(2239, 18, 99, 'Dhe Na, do të bëjmë që disa prej tyr të ndeshen me të tjerët – si valët (e detit), dhe, do të fryhet në Sur (brirë), e do t’i tubojmë që të gjithë,'),
(2240, 18, 100, 'Atë ditë, do t’ua tregojmë haptazi skëterrën mohuesve,'),
(2241, 18, 101, 'atyre të cilët i kanë pasur sytë e mbuluar me perde kundër këshillave të Mia, dhe, të cilët, nuk mundën (nga inati) ta dëgjonin (Kur’anin).'),
(2242, 18, 102, 'Vallë, a mendojnë mohuesit, se do të mund t’i marrin për zotëra robërit e Mi – përveç Meje? Na, u kemi përgatitur mohuesve skëterrën për vendbanim.'),
(2243, 18, 103, 'Thuaj: “A doni t’ju lajmëroj juve për ata që më së shumti dëmtojnë me punët e veta,'),
(2244, 18, 104, 'për ata, përpjekja e të cilëve ka qenë e kotë në jetën e kësaj bote, e mendojnë se kanë punuar vepra të mira?'),
(2245, 18, 105, 'Këta janë ata, të cilët i kanë mohuar dokumentet e Zotit të tyre dhe takimin me Te, e punët e tyre u janë shpartalluar; ndaj Na, nuk do të vëmë kurrfarë mase për ta, në Ditën e Kijametit.'),
(2246, 18, 106, 'Shpërblimi (dënimi) i tyre është xhehennemi ngase mohuan dhe, nga se argumentet e Mia dhe pejgamberët e Mi – i merrnin për (objekt) talljeje”.'),
(2247, 18, 107, 'Me të vërtetë, ata që besojnë dhe bëjnë vepra të mira, kopshtet e Firdevsit (xhennetit) do t’i kenë banesa.'),
(2248, 18, 108, 'Në to do të qëndrojnë përherë. Nuk do të dëshirojnë ndërrim prej tyre, (meqë janë tepër të kënaqur).'),
(2249, 18, 109, 'Thuaj: “Sikur deti të isht ngjyrë (për të shkruar) fjalët e Zotit tim, do të shterrej deti, par ase të sosen fjalët e Zotit tim, e madje, sikur të sillnim Ne si ndihmë edhe aq ngjyrë shkrimi”.'),
(2250, 18, 110, 'Thuaj: “Unë jam vetëm njeri si ju, mua më është shpallru që Zoti juaj është vetëm një Zot. Kush shpreson takimin me Zotin e vet, le të punojë vepër të mirë, dhe, le të mos i bëjë shok askë në të adhuruarit e Zotit të vet!”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'Kâf, Hâ, Jâ, Ajín, Sád.'),
(2252, 19, 2, '(Ky është) tregimi mbi mëshirën e Zotit tënd ndaj robit të Tij – Zekerijjas.'),
(2253, 19, 3, 'Kur ai e thirri (e luti) Zotin e vet me lutje të fshehtë'),
(2254, 19, 4, 'dhe tha: “O Zoti im, me të vërtetë, më janë dobësuar eshtrat, e koka më është zbardhur nga pleqëria. E, lutja ime Ty, nuk ka mbetë kurrë pa u pranuar.'),
(2255, 19, 5, 'I frikësohem të afërmve të mi – pas meje (ngase do të bëjnë keq), e bashkëshortja ime është sterile, andaj, më dhuro nga Ti – një të afërm (djalë),'),
(2256, 19, 6, 'që të më trashëgojë mua dhe familjen e Jakubit, dhe bënë Zoti im që të jesh i kënaqur me të!”'),
(2257, 19, 7, '“O Zekerijja, Na, do të sihariqojmë ty me një djalë emri i të cilit është Jahja, të cilit, Ne më parë nuk i kemi bërë emnak”.'),
(2258, 19, 8, '(Zekerijjai) tha: “O Zoti im, prej nga mua djali, kur bashkëshortja ime është sterile? Dhe, unë kam arritur pleqërinë e thellë?”'),
(2259, 19, 9, 'Tha: “Ashtu, ka thënë Zoti yt: “Kjo për Mua është lehtë. Unë të kam krijuar ty më parë, e ti nuk ke qenë fare”.'),
(2260, 19, 10, '(Zekerijjai) tha: “O Zoti im, më jep ndonjë shenjë!” – (Perëndia) tha: “Shenjë do të jetë ajo që nuk do të mundesh të flasësh me njerëz tri netë (e tri ditë), duke qenë madje i shëndoshë”.'),
(2261, 19, 11, 'Dhe, ai doli nga faltorja në popullin e tij dhe u dha shenjë atyre: “Madhëronie (Perëndinë), në mëngjes dhe në mbrëmje!”'),
(2262, 19, 12, '(Pasi që u lind Jahjai dhe u rrit, iu tha atij): “O Jahja, merre Librin me vendosmëri”. Dhe, Ne, ia kemi dhënë mençurinë (profetninë) duke qenë fëmijë,'),
(2263, 19, 13, 'dhe butësi prej Nesh dhe pastërti, dhe ishte besimtar,'),
(2264, 19, 14, 'dhe ishte bamirës ndaj prindërve të vet, e nuk ishte i ashpër dhe i padëgjueshëm.'),
(2265, 19, 15, 'Dhe, i qoftë selam (shpëtim) atij, në ditën që ka lindur, dhe në ditën që ka vdekur dhe në ditën që do të ringjallet.'),
(2266, 19, 16, 'Dhe përmende (o Muhammed) në Libër (Kur’an), Merjemin (historinë e saj): “kur u tërhoq nga familja në një vend, në lindje,'),
(2267, 19, 17, 'dhe mori një perde që të skajohet nga ata; Na dërguam tek ajo engjëllin – Xhebrailin dhe ai iu paraqit asaj në formën e mashkullit të zhvilluar mirë.'),
(2268, 19, 18, 'Ajo i tha: “Unë kërkoj që të më mbrojë Mëshiruesi im – prej teje, nëse i druan Perëndisë.'),
(2269, 19, 19, 'Ai tha: “Unë jam vetëm i dërguari i Zotit tënd, që të dhurojë ty një djalë të pastër”.'),
(2270, 19, 20, '(Merjemi) tha: “Si mund të kam unë djalë, kur asnjë mashkull nuk më ka prekur, e unë nuk jam e korruptuar!”'),
(2271, 19, 21, 'Tha (Xhebraili): “Kjo është kështu!” – ka thënë Zoti yt: “Kjo për Mua është lehtë (të krijojë njeriun pa babë). Dhe, ta bëjmë Na atë argument për njerëzit dhe mëshirë nga Ne. Dhe, kjo është punë e kryer”.'),
(2272, 19, 22, 'Dhe, mbeti shtatzënë, e u tërhoq me të (barrë) në një vend të largët.'),
(2273, 19, 23, 'E, dhembjet e lindjes, e detyruan atë që të vijë te trungu i një hurme. Tha ajo: “Ah sikur të kisha vdekur para kësaj (ngjarje) dhe të isha harruar krejtësisht”.'),
(2274, 19, 24, 'Dhe thërriti (zëri) nën të: “Mos u pikëllo, se ty, të ka dhuruar Zoti yt një krijesë dinjitoze.'),
(2275, 19, 25, 'Shkunde trungun e palmës, e do të pikin mbi ty hurmet e freshkëta,'),
(2276, 19, 26, 'e ha, e pi, dhe bëhu e gëzueshme! E, nëse vëren ndonjë njeri, ti thuaj: “Unë jam betuar në Mëshiruesin se do të agjërojë (përmbahem), e sot nuk do të flas me askë”.'),
(2277, 19, 27, 'Dhe, erdhi ajo (Merjemi) me të (fëmijën) te populli i vet, duke e bartë. Ata, i thanë (asaj): “O Merjeme, ke bërë diçka të turpshme!'),
(2278, 19, 28, 'O motra e Harunit! Babai yt nuk ka qenë njeri i keq, as nëna jote nuk ka qenë grua e korruptuar”.'),
(2279, 19, 29, 'Ajo e tregoi atë (Isain, me gjeste pa bërë zë). E, ata thanë: “Si t’i flasim ne fëmijës që është në djep?”'),
(2280, 19, 30, 'Ai (Isai) tha: “Unë jam rob i Perëndisë. Ai, më ka dhënë Librin dhe më ka bërë pejgamber.'),
(2281, 19, 31, 'Më ka bërë të lavdëruar kudo që të jem, dhe më ka rekomandaur që të bëj namaz dhe të jap zeqatin gjatë tërë jetës.'),
(2282, 19, 32, '(Më ka bërë) bamirës ndaj nënës sime e nuk më ka bërë të ashpër dhe të padëgjueshëm.'),
(2283, 19, 33, 'Dhe, le të jetë selami (shpëtimi) mbi mua në ditën që kam lindur dhe në ditën që do të vdes dhe në ditën që do të ringjallem!'),
(2284, 19, 34, 'Ky është Isai, i biri i Merjemës. Tregimi i tij është fjalë e vërtetë, për të cilën ata dyshojnë.'),
(2285, 19, 35, 'Për Perëndinë nuk është (as afër mendjes) të marrurit dikë për fëmijë. Qoftë lavdëruar Ai! Kur të vendos diçka, Ai vetëm thotë për të: “Bëhu!” Dhe, ajo bëhet.'),
(2286, 19, 36, '(Isai tha): “Dhe me të vërtetë, Perëndia, është Zoti im dhe Zoti juaj; andaj adhuronie vetëm Ate! Kjo është udha e vërtetë.'),
(2287, 19, 37, 'Ithtarët u përçanë në mes vete (në këtë). Mjer për ata që mohojnë, në të prezentuarit në Ditën e madhe!'),
(2288, 19, 38, 'E, çka do të dëgjojnë e do të shohin atë Ditë kur do të vijnë te Ne! Por, sot, zullumqarët janë në humbje të madhe!'),
(2289, 19, 39, '(O Muhammed), paralajmëroji ata me Ditën e pikëllimit, kur merr fund dhënia e llogarive, e ata janë të pakujdesshëm dhe nuk besojnë.'),
(2290, 19, 40, 'Na, me të vërtetë, e trashëgojmë Tokën dhe gjithçka gjendet në të dhe ata do të kthehen te Ne.'),
(2291, 19, 41, '(O Muhammed!) përmende në Libër (Kur’an) Ibrahimin! Ai ka qenë (njeri) i sinqertë, (dhe) pejgamber,'),
(2292, 19, 42, 'kur i ka thënë babës së tij: “O babai im, përse adhuron atë, që, as nuk dëgjon, as nuk sheh, e as që mund të të sjellë ndonjë dobi?'),
(2293, 19, 43, 'O babai im, mua më ka ardhur një pjesë e dijenisë, që të ka munguar ty; andaj më paso mua dhe unë do të udhëzoj në rrugën e drejtë.'),
(2294, 19, 44, 'O babai im, mos adhuro djallin; me të vërtetë, djalli është i padëgjueshëm nda Mëshiruesit (Perëndisë).'),
(2295, 19, 45, 'O babai im, unë druaj se mos do të të vijë dënimi nga Mëshiruesi, e do të bëhesh shok i djallit”.'),
(2296, 19, 46, '(Baba i Ibrahimit) tha: “O Ibrahim, ti vallë po shmangesh nga zotrat e mi? nëse nuk frenohesh nga kjo, unë do të të mbys ty me gurë; andaj, largohu prej meje për një kohë të gjatë!”'),
(2297, 19, 47, '(Ibrahimi) tha: “Shpëtimi qoftë mbi ty! Do t’i lutem Zotit tim që të falë ty. Me të vërtetë, Ai (Perëndia), është i mirë ndaj meje (pa masë).'),
(2298, 19, 48, 'Dhe, u lë juve dhe të gjithë ata që adhurojnë (tjerë zotra), pos Perëndisë; e, unë do të adhuroj Zotin tim. Shpresoj se nuk do të jetë i papranueshëm të adhuruarit e Zotit tim”.'),
(2299, 19, 49, 'E, kur i la ata që adhuronin (zotra tjerë), përpos Perëndisë, Na ia dhuruam atij Is’hakun dhe Jakubin, dhe që të dy i bëmë pejgamberë.'),
(2300, 19, 50, 'Dhe, Na, ata i dhuruam nga mëshira Jonë dhe bëmë që ata, me të vërtetë, të lavdërohen me mirësi.'),
(2301, 19, 51, 'Dhe përmende (o Muhammed!) në Libër (Kur’an), Musain! Me të vërtetë, ai ishte i sinqertë dhe ishte pejgamber, lajmëtar.'),
(2302, 19, 52, 'Dhe, Na, e kemi thirrë atë (Musain), në anën e djathtë të Turit, dhe e kemi afruar atë në bisedë (pa ndërmjetësim),'),
(2303, 19, 53, 'dhe, i dhuruam atij, nga mëshira Jonë, që vëllai i tij – Haruni të jetë pejgamber.'),
(2304, 19, 54, 'Dhe, përmende (o Muhammed!) në Libër (Kur’an), Ismailin! Me të vërtetë, ai plotësonte premtimin e dhënë dhe ka qenë pejgamber, lajmëtar,'),
(2305, 19, 55, 'dhe urdhëronte popullin e vet, që të bëjë namaz dhe të japë zeqatin dhe arriti kënaqësinë te Zoti i vet.'),
(2306, 19, 56, 'Dhe, përmende (o Muhammed!) në Libër (Kur’an) Idrizin; ai ka qenë (njeri) shumë i sinqertë dhe lajmëtar,'),
(2307, 19, 57, 'dhe Ne, e kemi ngritur atë në vend të lartë.'),
(2308, 19, 58, 'Këta janë ata pejgamberë, të cilëve Perëndia u dhuroi dhuntitë, nga pasardhësit e Ademit, dhe të atyre që Ne i patëm ngarkuar në anije me Nuhun e nga pasardhësit e Ibrahimit dhe të Israelit, që Ne i patëm udhëzuar dhe i patëm zgjedhur. Kur t’ju lexohen ajetet e Perëndisë – Mëshiruesit, bien (me fytyrë për tokë), duke bërë sexhde dhe qajnë.'),
(2309, 19, 59, 'E, pos tyre, erdhën breznitë (e shëmtuara), të cilat braktisën namazin dhe ndoqën epshet e veta. Ata do të hasin në të keqe (në humnerën e skëterrës),'),
(2310, 19, 60, 'përpos atyre që pendohen, që besojnë dhe bëjnë (vepër) të mirë! Këta do të hyjnë në xhennet dhe nuk do t’u bëhet kurrfarë dëmi,'),
(2311, 19, 61, '(do të hyjnë) në xhennetin e Adnit, të cilin Mëshiruesi ua ka premtuar robërve të vet, edhe pse ende nuk e kanë parë atë (xhennetin). Me të vërtetë, premtimi i Tij është i sigurtë, -'),
(2312, 19, 62, 'Ata, në xhennet nuk do të dëgjojnë llomotitje. Do të dëgjojnë vetëm fjalët përshëndetëse. Ata, aty do të furnizohen, në mëngjes dhe në mbrëmje.'),
(2313, 19, 63, 'Ky është xhenneti që ia japim në trashëgim robërve Tanë, të cilët janë ruajtur nga mohimi.'),
(2314, 19, 64, '(Xhebraili tha): “Dhe, ne, zbresim (në Tokë), vetëm me urdhërin e Zotin tënd. Atij i përket ajo që është para nesh, dhe që është pas nesh, dhe ajo që është në mes këtyre dyjave. Dhe Zoti yt nuk është i harrueshëm”.'),
(2315, 19, 65, 'Ai është Zoti i qiejve dhe i Tokës dhe asaj që është në mes tyre, prandaj, vetëm Ate adhuroje dhe bëhu i durueshëm në vazhdimin e lutjes së Atij. A di ti për Te ndonjë emnak!?'),
(2316, 19, 66, 'Njeriu thotë: “Vallë, pasi të vdes, a do të kthehem përsëri në jetë?”'),
(2317, 19, 67, 'A nuk e kujton njeriu se Na e kemi krijuar atë qysh më parë, e që nuk ka qenë asgjë!'),
(2318, 19, 68, 'Pasha Zotin tënd, Na do t’i tubojmë ata dhe djajtë, pastaj do t’i sjellim ata që të gjunjëzohen rreth xhehennemit!'),
(2319, 19, 69, 'Pastaj, do të ndajmë prej çdo grupi atë, që ka qenë më mendjemadh ndaj Perëndisë Mëshirues.'),
(2320, 19, 70, 'E, Ne pastaj më së miri e dimë se kush prej tyre ka përparësi që të fërgohet në te (xhehennem).'),
(2321, 19, 71, 'Dhe, nuk ka asnjë prej jush e që të mos arrijë tek ai (zjarri). Kjo është dispozitë e caktuar nga Zoti yt.'),
(2322, 19, 72, 'Pastaj do t’i shpëtojmë ata që kanë qenë besimtarë, e do t’i lëmë zullumqarët në të – të gjunjëzuar.'),
(2323, 19, 73, 'E, kur t’ju lexohen ajetet Tona të qarta, ata që nuk kanë besuar u thonë atyre që kanë besuar: “Cili grup është më i mirë për nga jeta dhe më i mirë për nga shoqëria?”'),
(2324, 19, 74, 'E sa e sa brezni, Ne i kemi shkatërruar para tyre, që kanë qenë më të begatshëm dhe më të pashëm!'),
(2325, 19, 75, 'Thuaj (o Muhammed!): “Kush është i humbur, le t’ia zgjasë (jetën) sa të dojë Mëshiruesi”. E, kur të shohin ata atë që u është premtuar atyre: ose dënimin ose Ditën e Kijametit, ata do të dinë se kush është më keq për nga pozita he më i dobët për nga shoqëria.'),
(2326, 19, 76, 'Perëndia ua shton udhëzimin atyre që janë në rrugën e drejtë, e veprat e mira – të qëndrueshme, janë më mirë të shpërblyera te Zoti yt dhe kanë përfundim më të mirë.'),
(2327, 19, 77, 'Vallë, a e ke parë atë që nuk beson në argumentet Tona dhe thotë: “Mua do të më jepet (Ditën e Kijametit) edhe pasuria edhe fëmijët”.'),
(2328, 19, 78, 'A, mos ai ka mësuar për atë që nuk është parë, ose ka marrë obligim te Perëndia – Mëshirues.'),
(2329, 19, 79, 'Kurrsesi! Na do ta shënojmë atë që flet, dhe Ne do t’ia vazhdojmë dënimin,'),
(2330, 19, 80, 'Na do t’ia marrim atij ate për të cilën flet (mallin dhe fëmijët), e ai ke Ne do të vijë vetëm.'),
(2331, 19, 81, 'Dhe, ata merrnin zotëra, përpos Perëndisë, për t’ju bërë atyre ndihmës.'),
(2332, 19, 82, 'Kurrsesi; do të mohojnë ata (zotërat e tyre) se mohuesit i kanë adhuruar ata dhe do t’u bëhen atyre kundërshtarë.'),
(2333, 19, 83, 'Vallë, a nuk ke parë ti, se Ne, kemi dërguar te mohuesit djajtë, që t’i nxisin ata në të keqe të madhe?'),
(2334, 19, 84, 'Andaj, mos kërko shkatërrimin e tyre të shpejtë, se, me të vërtetë, Ne ua numrojmë ditët e tyre të caktuara.'),
(2335, 19, 85, '(Kujtoju atyre) kur do t’i tubojmë besimtarët te Mëshiruesi, si të ftuar,'),
(2336, 19, 86, 'e i shtyejmë mëkatarët në skëterrë (si grixhën e shtazëve kah burimi),'),
(2337, 19, 87, 'e askush – për askë, nuk do të mund të angazhohet, përveç atij që ka marrë premtim nga Mëshiruesi!'),
(2338, 19, 88, 'Ata thonë: “Bamirësi i Gjithmbarshëm ka marrë (për Veti) fëmijë”.'),
(2339, 19, 89, 'Ju, me të vërtetë, flisni diçka të shëmtuar!'),
(2340, 19, 90, 'Gati janë copëtuar qiejt (prej kësaj thënie), dhe Toka të shëmbet, dhe të rrëzohen malet – duke u thërrmuar,'),
(2341, 19, 91, 'meqë ata i përshkruajnë fëmijë Bamirësit të Gjithmbarshëm.'),
(2342, 19, 92, 'E, për Bamirësin e Gjithmbarshëm, është e pakuptueshme, që të ketë fëmijë.'),
(2343, 19, 93, 'S’ka krijesë që gjendet në qiej dhe në Tokë, e që të mos vijë si rob te Bamirësi i Gjithmbarshëm.'),
(2344, 19, 94, 'Ai, i ka përfshirë të gjithë ata dhe i ka numëruar të gjithë saktësisht.'),
(2345, 19, 95, 'Dhe, çdonjëri prej tyre, në Ditën e Kijametit, do të vijë fill i vetëm tek Ai.'),
(2346, 19, 96, 'Me të vërtetë, ata që besojnë dhe bëjnë vepra të mira, Bamirësi i Gjithmbarshëm do t’i pajisë me dashuri (që t’i dojë Perëndia dhe që t’i duan njerëzit).'),
(2347, 19, 97, 'Na, me të vërtetë, e kemi bërë atë (Kur’anin) të lehtë, në gjuhën tënde, që me të, t’i sihariqosh besimtarët, dhe me të, t’i paralajmërosh njerëzit, armiqët e përbetuar.'),
(2348, 19, 98, 'Sa e sa brezni kemi shkatërruar Ne para tyre. Vallë, a po sheh ndonjë prej tyre, dhe a po dëgjon përshpëritjen më të vogël të tyre?');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'Tá, Hâ.'),
(2350, 20, 2, 'Na, nuk ta kemi shpallur ty Kur’anin për të munduar,'),
(2351, 20, 3, 'por, që të jetë këshillë për atë që druan,'),
(2352, 20, 4, 'ai është i shpallur – nga Krijuesi i Tokës dhe i qiejve të lartë,'),
(2353, 20, 5, 'Bamirësi i Gjithmbarshëm (Perëndia), që sundon Arshin, (si i takon Madhërisë së Tij).'),
(2354, 20, 6, 'Atij i përket ç’ka në qiej dhe ç’ka në Tokë dhe ç’ka në mes tyre dhe gjithçka gjendet në tokë!'),
(2355, 20, 7, 'E, nëse ti flet me zë (të lartë), Ai, me të vërtetë, di fshehtësinë dhe atë që ti vetëm mendon!'),
(2356, 20, 8, 'Perëndia është (Një), s’ka zot tjetër përveç Tij. Ai ka emrat më të bukur”.'),
(2357, 20, 9, 'Dhe, a të ka arritur ty lajmi për Musain,'),
(2358, 20, 10, 'kur e pa zjarrin ai i tha familjes së vet: “Rrini ju këtu! Unë, me të vërtetë, e kam parë një zjarr; shpresoj se unë do t’ju sjell juve një gacë, ose që pranë tij do të gjej ndonjë udhërrëfyes”.'),
(2359, 20, 11, 'E kur arriti ai te zjarri, u thirr: “O Musa,'),
(2360, 20, 12, 'Unë, me të vërtetë, jam Zoti yt! Andaj, zbathi këpucët tuaja, se ti, me të vërtetë, gjendesh në luginën e shenjtë – të Tuvasë.'),
(2361, 20, 13, 'Dhe, Unë ty të kam zgjedhur; andaj dëgjo ti atë që të shpallet (ty)!'),
(2362, 20, 14, 'Me të vërtetë, Unë jam Perëndia; s’ka zot tjetër përveç meje; andaj, vetëm Mua adhuromë dhe kryej namazin, për të më kujtuar Mua!'),
(2363, 20, 15, 'Çasti i gjykimit do të arrijë me siguri – atë e kam fsheh (nga çdokush) – kur secili do të shpërblehet për atë (përpjekje) që ka bërë.'),
(2364, 20, 16, 'Le të mos shmangë ty nga ai (çast), ai që nuk beson në të (Kijametin) dhe që jepet pas epsheve të tija, e të humbasësh.'),
(2365, 20, 17, 'E, ç’ke ti në dorën e djathtë, Musa?'),
(2366, 20, 18, '(Musai) u përgjegj: “Ky është shkopi im, në të cilin mbahem dhe me të u shkundi gjethe dhenëve të mia, e me të kam edhe dobi të tjera.'),
(2367, 20, 19, '(Perëndia) i tha: “Hudhe atë Musa!”'),
(2368, 20, 20, 'Ai e hudhi atë por ai (shkopi, u bë) gjarpër që zvarritej.'),
(2369, 20, 21, '(Perëndia) i tha: “Merre atë (gjarpërin) dhe mos u frikëso! Na do ta kthejmë atë në gjendjen e mëparshme.'),
(2370, 20, 22, 'Dhe, fut dorën tënde nën sqetullën tënde, ajo do të dalë e bardhë pa kurrfarë sëmundje, dhe ja kjo – është mrekulli tjetër,'),
(2371, 20, 23, 'për t’i treguar ty disa nga mrekullitë Tona të mëdha!'),
(2372, 20, 24, 'Shko te Faraoni! Ai, me të vërtetë, është azdisur, nga të këqiat!”'),
(2373, 20, 25, '(Musai) tha: “O Zoti im, zgjeroma krahërorin tim,'),
(2374, 20, 26, 'dhe lehtësoma punën time,'),
(2375, 20, 27, 'dhe zgjidhma lidhjen prej gjuhës sime,'),
(2376, 20, 28, 'që të kuptojnë ata fjalën time,'),
(2377, 20, 29, 'dhe bëne për mua një ndihmës nga familja ime'),
(2378, 20, 30, 'Harunin, vëllain tim,'),
(2379, 20, 31, 'e forcoje me të – fuqinë time,'),
(2380, 20, 32, 'dhe shoqëro atë në punën time,'),
(2381, 20, 33, 'për me të falënderuar Ty shumë,'),
(2382, 20, 34, 'dhe për të përmendur Ty vazhdimisht!'),
(2383, 20, 35, 'Me të vërtetë, Ti je mbikëqyrësi ynë”.'),
(2384, 20, 36, '(Perëndia) tha: “Të është dhënë ty ajo që kërkove, o Musa!”'),
(2385, 20, 37, 'Dhe Ne, me të vërtetë, të kemi bërë mirë ty edhe tjetër herë (para kësaj),'),
(2386, 20, 38, 'kur nënën tënde e frymëzuam me atë, që mund të arrihet vetëm me frymëzim:'),
(2387, 20, 39, '“Vendose atë (Musain) në sandëk e hudhe në lum (Nil), lumi e qet atë në breg, e që atë, do ta marr armiku i Im dhe armiku i tij”. Dhe, Unë kam hudhur në ty – dashurinë Time (për me të dashtë ty gjithkush), dhe që të rritesh nën mbikëqyrjen Time.'),
(2388, 20, 40, 'Kur shkoi motra jote (në pallatin e Faraonit) e tha: “A doni t’ju tregoj unë juve atë që do të kujdeset për të?” Na, të kthyem ty te nëna jote që të gëzohet ajo dhe të mos pikëllohet më. Dhe ti e mbyte një njeri (pa qëllim), e Ne të shpëtuam ty nga brenga (e dënimit) dhe Ne të sprovuam ty me sprova të shumta. Dhe ti qëndrove me vite të tëra në mesin e banorëve të Medjenit, e pastaj, ti, erdhe, sipas dispozitës së caktuar, o Musa!'),
(2389, 20, 41, 'Dhe, Unë të kam zgjedhur ty për Vete.'),
(2390, 20, 42, 'Shko ti dhe vëllai yt me argumentet e Mia dhe mos u shkrehni në të kujtuarit Mua!'),
(2391, 20, 43, 'Shkoni (ju të dy) te Faraoni! Ai, me të vërtetë, është azdisur nga të këqijat,'),
(2392, 20, 44, 'e flitni atij fjalë të buta, që të këshillohet apo të frikësohet”.'),
(2393, 20, 45, 'Ata thanë: “O Zoti ynë, na, me të vërtetë, druajmë se do të na dënojë menjëherë ai, ose do të azdiset më tepër”.'),
(2394, 20, 46, '(Perëndia) tha: “Mos u frikësoni! Unë me të vërtetë, jam me ju, dëgjoj dhe shoh”.'),
(2395, 20, 47, 'Shkoni tek ai dhe i thoni: “Na dy jemi të dërguarit e Zotit tënd, lëshoji bijtë e Israelit që të vijnë me ne dhe mos i mundo ata! Na, të kemi sjellë ty argument nga Zoti yt. Qoftë i shpëtuar ai që ndjek rrugën e drejtë!'),
(2396, 20, 48, 'Me të vërtetë, neve na është shpallur, se dënimi do ta godasë atë që e konsideron për gënjeshtër (shpalljen) dhe shmanget”.'),
(2397, 20, 49, '(Faraoni) tha: “E, kush është Zoti juaj, o Musa?”'),
(2398, 20, 50, '(Musai) tha: “Zoti ynë është ai që çdo gjëje i ka dhënë formën e vet e pastaj i ka udhëzuar (në përdorimin e tyre).'),
(2399, 20, 51, '(Faraoni) tha: “Si është gjendja e popujve të mëparshëm?”'),
(2400, 20, 52, '(Musai) tha: “Dijenia lidhur me ta gjendet te Zoti im, në Libër. Zoti im nuk gabon dhe nuk harron!'),
(2401, 20, 53, '(Perëndia), i cili për ju Tokën e ka bërë shtrat, u ka lehtësuar juve rrugët nëpër të, u ka lëshuar ujë (shi) nga qielli e që Ne – me të, kemi nxjerrë shumë lloj bimësh të ndryshme.'),
(2402, 20, 54, 'Hani dhe kullotni bagëtinë tuaj! Këto, me të vërtetë, janë argumente për ata që janë mentarë.'),
(2403, 20, 55, 'Na ju kemi krijuar juve prej saj (tokës), dhe në të do t’ju kthejmë, dhe nga ajo do t’ju nxjerrim juve herën tjetër.'),
(2404, 20, 56, 'Na, me të vërtetë, ia kemi treguar atij (Faraonit) të gjitha argumentet Tona, e ai i ka konsideruar gënjeshtra dhe ka refuzuar besimin.'),
(2405, 20, 57, '(Faraoni) tha: “A mos ti, o Musa, ke ardhur të na nxjerrësh nga toka jonë – me magjitë tuaja?”'),
(2406, 20, 58, 'Dhe ne, do të sjellim ty një magji të ngjashme, e cakto në mes meje e teje takim, të cilit do t’i përmbahemi edhe na edhe ti, në vend të barabartë.'),
(2407, 20, 59, '(Musai) tha: “Takimi me ju është në ditën e kremtës dhe njerëzia lë të tubohen para mesditës”.'),
(2408, 20, 60, 'Faraoni u largua, i tuboi ngatërresat e veta, e pastaj erdhi.'),
(2409, 20, 61, 'Musai u tha atyre: “Mjerë për ju! Mos trilloni gënjeshtra për Perëndinë, e që Ai, t’ju shkatërrojë me dënim. Do të falimentojnë me siguri, ata që trillojnë gënjeshtra!”'),
(2410, 20, 62, 'Ata (magjistarët), polemizuan – për punën e tyre – në mes vete, duke pëshpëritur dhe tinëzisht u morën vesh.'),
(2411, 20, 63, 'Ata thanë: “Me të vërtetë, këta janë dy magjistarë; duan t’ju shporrin juve nga toka e juaj – me magjitë e tyre, dhe të shkatërrojnë fenë tuaj tejet të mirë,'),
(2412, 20, 64, 'andaj, mblidhni dredhitë tuaja e pastaj – ejani të radhitur. Do të shpëtoj sot – ai që fiton!”'),
(2413, 20, 65, 'Ata thanë: “O Musa, ose gjuaj ti, ose të bëhemi ne hedhësit e parë?”'),
(2414, 20, 66, '(Musai) tha: “Jo, hedhni ju!” E, atë – herë, (pasi që i hedhën ata) litarët dhe shkopinjtë e tyre, - prej magjive të tyre – iu dukën atij, se me të vërtetë, po rrëshqasin,'),
(2415, 20, 67, 'andaj Musai – ndjeu në vete frikë,'),
(2416, 20, 68, 'Ne i thamë: “Mos u frikëso, se me të vërtetë, ti je fitues!'),
(2417, 20, 69, 'Dhe hudhe atë që e ke në dorën e djathtë! Do t’i gëlltisë ato që i kanë punuar ata. Atë që kanë punuar ata, është vetëm një mashtrim i magjistarëve. E, magjistari, nuk do të ketë sukses, kudo që të gjendet.'),
(2418, 20, 70, 'E, magjistarët (pastaj), ranë në sexhde dhe thanë: “Na besojmë në Zotin e Harunit dhe Musait”.'),
(2419, 20, 71, '(Faraoni) tha: “Ju e besuat Atë, para se t’u japë leje unë juve! Ai (Musai), me të vërtetë, është eprori juaj, që u ka mësuar juve magjitë. Dhe, me të vërtetë, unë do t’ju këpus duart dhe këmbët tuaja tërthorazi, dhe unë do t’ju varr juve në trungjet e hurmeve. Ju, do ta dini, me siguri, - se dënimi i kujt prej nesh – është më i ashpër dhe më i vazhdueshëm”.'),
(2420, 20, 72, '(Magjistarët) u përgjegjën: “Na, ty kurrë nuk do të japim përparësi ndaj argumenteve që na kanë ardhur neve dhe ndaj Atij që na ka krijuar neve, e gjyko si të duash; ti gjykon vetëm në jetën e kësaj bote!'),
(2421, 20, 73, 'Na besojmë Zotin tonë, që të na falë gabimet tona dhe magjitë, në të cilat na ke detyruar ti (Faraon). E, Perëndia është më i miri dhe më i vazhdueshmi (në shpërblime dhe dënime).'),
(2422, 20, 74, 'Me të vërtetë, ai që para Zotit të vet del si kriminel, atë, me të vërtetë, e pret skëterra; në të – ai, as nuk vdes as nuk jeton.'),
(2423, 20, 75, 'E, kush vie tek Ai, si besimtarë, e që madje, ka punuar vepra të mira, vetëm ata kanë pozita të larta,'),
(2424, 20, 76, 'kopshtet e Adnit, nëpër të cilët rrjedhin lumenjtë, e, në të cilët, ata do të qëndrojnë përherë. Ky është shpërblimi për atë që është i spastruar nga mohimi.'),
(2425, 20, 77, 'Na, i kemi shpallur Musait: “Udhëto natën me robërit e Mi dhe bënju atyre një rrugë të tharë nëpër det; nuk do të frikësohesh ti, se do të të arrijë ai (Faraoni), as nuk do të frikësohesh (nga fundosja)”.'),
(2426, 20, 78, 'E, Faraoni – me ushtrinë e tij, i ndoqi ata, e deti i mbuloi ata – me një mbulesë të madhe;'),
(2427, 20, 79, 'Faraoni i solli humbje popullit të vet, e nuk e udhëzoi në rrugë të drejtë.'),
(2428, 20, 80, 'O bijtë e Israelit, Na ju kemi shpëtuar juve nga armiku juaj, u kemi premtuar juve (shumë dhunti) në anën e djathtë të Turit, dhe u kemi dhënë menn – (një lloj ushqimi i ëmbël) dhe fëllanza.'),
(2429, 20, 81, 'Hani nga ushqimet e këndshme, me të cilat u kemi furnizuar ne juve, mos u bëni të azdisur në to, e t’ju arrijë zemrimi (dënimi) Im. Atë që e arrinë zemrimi Im, (ai) është i humbur!'),
(2430, 20, 82, 'Me të vërtetë, Unë jam falës për atë që pendohet dhe beson e punon vepra të mira, e qëndron në rrugë të drejtë.'),
(2431, 20, 83, 'E, përse nxitove para popullit tënd, Musa?'),
(2432, 20, 84, '(Musai) tha: “Ja, ata, shkojnë pas gjurmëve të mia, e unë nxitova te Ti, o Zoti im, për të fituar kënaqësinë Tënde”.'),
(2433, 20, 85, '(Perëndia) tha: “Na e kemi vënë në sprovë popullin tënd – pas teje, e i humbi ata Samirija”'),
(2434, 20, 86, 'U kthye Musai te populli i vet, i zemruar dhe i pikëlluar. Tha: “O populli im, vallë, a nuk ju premtoi Zoti juaj premtim të mirë? A mos ju zgjat tepër koha (prej se jam ndarë nga ju), ose dëshironi ju, që t’ju godasë zemrimi i Zotit tuaj, e për këtë, nuk i jeni përmbajtur premtimit që më dhatë mua!”'),
(2435, 20, 87, 'Ata u përgjegjën: “Na nuk e kemi thyer premtimin që të patëm dhënë ty me vullnetin tonë, por ne qemë të ngarkuar me peshë të rëndë – me stoli të popullit, andaj i hodhëm ato (në zjarr)”. Po kështu, i hodhi edhe Samirija,'),
(2436, 20, 88, 'dhe nga këto (stoli të hedhura në zjarr), ai (Samirija) nxori për popull trup viçi që pëlliste, dhe ata, atëherë, thanë: “Ky është zoti juaj dhe zoti Musait, ai e ka harruar atë (e ka shkuar ta kërkojë në Tur)!”'),
(2437, 20, 89, 'Vallë, a nuk shohin ata, se (viçi) nuk u drejtohet atyre me asnjë fjalë dhe, nuk është në gjendje t’u sjellë atyre kurrfarë dëmi e as kurrfarë dobie.'),
(2438, 20, 90, 'E, atyre – Haruni u ka thënë qysh më parë: “O populli im! Ju, me këtë, vetëm jeni sjellë në sprovë, e Zoti juaj është Mëshirues, andaj pasomëni mu dhe dëgjoni urdhërin tim!”'),
(2439, 20, 91, 'Ata u përgjegjën: “Na do t’i lutemi atij (viçit) vazhdimisht, derisa të mos na kthehet Musai”.'),
(2440, 20, 92, '(Musai) tha: “O Harun, a të ka penguar ty, - kur i ke parë ata se kanë humbur –'),
(2441, 20, 93, 'përse të mos shkosh pas meje? A mos qe ti i padëgjueshëm ndaj urdhërit tim?”'),
(2442, 20, 94, '(Haruni) tha: “O biri nënës sime, mos më kap mua për mjekrën time as për kokën time, unë jam frikësuar që ti do të thuash: “Ke bërë përçarje në mesin e bijve të Israelit e nuk i ke mbajtur fjalët e mia”.'),
(2443, 20, 95, '(Musai) tha: “E ç’është puna jote, o Samiri?”'),
(2444, 20, 96, '(Samirija) u përgjegj: “Unë kam parë atë që nuk e kanë parë ata, - e kam marrë një grusht dhe në gjurmët e (kalit të) Pejgamberit, e atë – e kam hedhur (në stolinë e shkrirë), dhe kështu, ajo, më është zbukuruar mua (e keqja ime)”.'),
(2445, 20, 97, '(Musai) tha: “E, atëherë shko! – tërë jetën do të flasësh: “S’ka prekje (le të mos më prekë askush) dhe ty të është caktuar një premtim, i cili doemos do të zbatohet. Shiko në zotin tënd, të cilin ti e ke adhuruar vazhdimisht. Na, do ta djegim atë, e pastaj do ta hedhim atë në det – si pluhur.'),
(2446, 20, 98, 'Zoti juaj është vetëm Perëndia; që s’ka zot tjetër përveç Tij; që dijenia e Tij i përfshinë të gjitha gjërat!”'),
(2447, 20, 99, 'Kështu pra, Na ty (o Muhammed) t’i tregojmë disa lajme (për atë) që kanë kaluar, dhe ta kemi, dhënë ty Kur’anin – prej anës Sonë.'),
(2448, 20, 100, 'Kush shmaget nga ai (Kur’ani), ai në Ditën e Kijametit, do të bartë ngarkesë të rëndë (dënim),'),
(2449, 20, 101, 'përherë do të mbetet (duke bartur atë). Sa barë e shëmtuar që është ajo në Ditën e Kijametit,'),
(2450, 20, 102, 'në Ditën kur do të fryhet në Sur. Atë ditë do t’i tubojmë mëkatarët duke qenë syzgurdulluar,'),
(2451, 20, 103, 'dhe njëri-tjetrit do t’i pëshpërisin (duke thënë): “Ju keni qëndruar (në të kaluarën) vetëm dhjetë ditë”.'),
(2452, 20, 104, 'Na dimë më së miri se çka do të thonë ata (në Ditën e Kijametit), kur më i mençuri nga ata, thotë: “Ju keni qëndruar vetëm një ditë”.'),
(2453, 20, 105, 'Dhe, të pyesin ty (o Muhammed) për malet (çka do të bëhet me to në Ditën e Kijametit). Thuaj: “Do t’i copëtoj krejtësisht Zoti im,'),
(2454, 20, 106, 'e toka do të bëhet fushë e rrafshët,'),
(2455, 20, 107, 'nuk do të shohësh në të (tokë) as lugina as brigje”.'),
(2456, 20, 108, 'Atë ditë, ata do të shkojnë pas thirrësit, duke mos pasur shmangie në të, e zërat do t’i përulen Mëshiruesit të Gjithmbarshëm, e ti nuk do të dëgjosh tjetër, pos pëshpëritjes.'),
(2457, 20, 109, 'Atë ditë, nuk do të bëjë dobi ndërmjetësimi i askujt, përveç ndërmjetësimit të atij, që i jep leje Mëshiruesi dhe që është i kënaqur me fjalët e tij.'),
(2458, 20, 110, 'Ai di ç’ka para tyre dhe ç’ka pas tyre; e ata, nuk mund ta ngërthejnë dijeninë e Perëndisë – me dijeninë e tyre.'),
(2459, 20, 111, 'Fytyrat (e njerëzve) do t’i përulen (Perëndisë) të Gjallit, të Amshueshmit (Mbikëqyrësit të Gjithësisë). Do të humbë ai që ka ngarkuar të keqen (që i ka bërë shok Perëndisë),'),
(2460, 20, 112, 'e ai që punon vepra të mira dhe është besimtar, nuk do të druajë nga padrejtësia (shtimi i dënimit), as nga të manguarit (e shpërblimit).'),
(2461, 20, 113, 'Dhe kështu Ne, e kemi zbritur Kur’anin në gjuhën arabe, dhe në të kemi përsëritur vërejtjet, që ata të ruhen nga mëkatet, ose që t’i orientojë ata në këshillim.'),
(2462, 20, 114, 'I Lartë është Perëndia – Sundues – i Vërtetë! Dhe, mos nxito në të lexuarit e Kur’anit (o Muhammed!) para se të përfundojë (shpallja e tij), dhe thuaj: “O Zoti im, shtoma dijeninë mua!”'),
(2463, 20, 115, 'Na, madje – edhe Ademit i kemi shpallur më parë (në xhennet), e ai harroi, por nuk kemi gjetur tek ai vendim (për gabim të qëllimuar).'),
(2464, 20, 116, '(Kujtoje o Muhammed!) kur u thamë engjëjve: “Bëni sexhde Ademit; e i bënë sexhde të gjithë, përpos Iblisit; ai refuzoi (atë).'),
(2465, 20, 117, 'E, Ne, kemi thënë: “O Adem, me të vërtetë, ky është armik yti dhe i gruas sate, e le të mos u nxjerr juve kurrsesi nga xhenneti, e që të vuash pastaj.'),
(2466, 20, 118, 'Me të vërtetë, në të nuk do të mbetësh i uritur as i zhveshur, të jesh i ushqyer dhe i veshur'),
(2467, 20, 119, 'as nuk do të kesh etje, as vapë.'),
(2468, 20, 120, 'Por, djalli filloi ta namatis (magjeps) atë e i tha: “O Adem, a don të ta tregoj drurin e pavdekshëm dhe pasurinë që nuk zhduket?”'),
(2469, 20, 121, 'Dhe, ata të dy hëngrën nga ajo (pema për t’u bërë të pavekshëm dhe që të arrijnë pasurinë e përhershme), e filluan t’u zbulohen vendet e turpshme dhe nisën të mbulohen me gjethet e xhennetit. Dhe, Ademi nuk e zbatoi urdhërin e Zotit të vet, ndaj edhe humbi.'),
(2470, 20, 122, 'Pastaj, Zoti i tij e zgjodhi atë, ia pranoi pendimin dhe e udhëzoi atë.'),
(2471, 20, 123, '(Perëndia) tha: “Zbritni prej xhennetit të gjithë! Do të jeni armiq të njëri-tjetrit. Me të vërtetë, do t’ju arrijë juve udhëzim nga ana Ime, e kush pason udhëzimin Tim, nuk do të humbë (në këtë botë), as që do të jetë fatkeq (në botën tjetër).'),
(2472, 20, 124, 'E, ai që shmanget nga Libri Im, ai do të ketë jetë të vështirë dhe, Na në Ditën e Kijametit do ta ringjallim të verbër atë.'),
(2473, 20, 125, 'Ai do të thtotë: “O Zoti im, përse më ringjalle të verbër, kur unë, me të vërtetë, kam pasur të pamurit më parë?”'),
(2474, 20, 126, '(Perëndia) i tha: “Kështu (ke bërë); të kanë ardhur argumentet Tona, e ti i ke harruar ato. Kështu edhe ti sot do të jesh i harruar.'),
(2475, 20, 127, 'Kështu Na e ndëshkojmë atë që jepet pas mëkateve dhe nuk beson në argumentet e Zotit të tij; e dënimi në jetën tjetër, në të vërtetë, është më i dhembshëm dhe i pandërprerë.'),
(2476, 20, 128, 'A nuk i udhëzoi ata në rrugë të drejtë (argumenti Ynë) – që sa e sa gjenerata i kemi zhdukur Ne para tyre, të cilët ecin nëpër vendet e tyre. Me të vërtetë, në këtë, ka argumente për njerëzit mentarë.'),
(2477, 20, 129, 'Sikur të mos ishte fjala e caktuar më parë – nga Zoti yt (o Muhammed) dënimi i tyre do të ishte i pashmangshëm dhe afati (i dënimit) do të ishte i caktuar (në këtë botë).'),
(2478, 20, 130, 'Bëhu i durueshëm për atë që flasin ata, dhe lavdëroje me falenderim Zotin tënd para lindjes së diellit, dhe para perëndimit të tij, dhe lavdëroje (Perëndinë) në orët e natës dhe në kohërat e ditës, për të qenë ti i kënaqur.'),
(2479, 20, 131, 'Mos lakmo në begatitë e llojllojshme që ua kemi dhuruar Na atyre (mohuesve), duke qenë ato stoli të jetës së kësaj bote, për t’i vënë në sprovë ata, e furnizimi i Zotit tënd është më i mirë dhe më i amdhueshëm.'),
(2480, 20, 132, 'Dhe urdhëroje familjen tënde që të bëjë namaz dhe në këtë bënu këmbëngulës! Na nuk kërkojmë furnizim prej teje. Na të furnizojmë ty, e përfundimi i mirë është për ata që ruhen nga mëkatet.'),
(2481, 20, 133, 'Mohuesit thonë: “Përse të mos na sjellë ai ndojë argument të qartë që gjendej në librat e mëparshëm?”'),
(2482, 20, 134, 'Sikur Na t’i shkatërronim ata para tij (para shpalljes së Kur’anit) ata, me siguri do të thonin: “O Zoti ynë! Sikur të na kishe dërguar pejgamber, e të shkojmë pas argumenteve Tuaja (të Perëndisë), para se të poshtërohemi dhe turpërohemi!”'),
(2483, 20, 135, 'Thuaju (o Muhammed): Të gjithë presin, andaj pritni edhe ju, e do të kuptoni ju kush janë ithtarët e rrugës së drejtë dhe kush është i udhëzuar”.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'Ju ka afruar njerëzve llogarie e tyre (e veprave të tyre), e ata, janë të pakujdesshëm e janë shmangur.'),
(2485, 21, 2, 'Atyre nuk u erdhi nga Zoti i tyre kurrfarë paralajmërimi i ri, e që ata të mos e përgjojnë atë, duke u tallur me të,'),
(2486, 21, 3, 'e zemrat e tyre janë të hutuara. E, ata që kanë bërë zullum, tinzisht flasin: “A mos ky është diçka tjetër, përpos njeri si ju? Vallë a do të shkoni ju pas magjive, e ju, po i shikoni me sytë tuaj (ata)?”'),
(2487, 21, 4, '(Pejgamberi) tha: “Zoti im di se ç’flitet – qoftë në qiell, qoftë në Tokë, Ai, të gjitha i dëgjon dhe i di.'),
(2488, 21, 5, 'Ata, madje thonë: “Këto janë vetëm ëndërra të turbullta, jo, ai e ka trilluar atë (Kur’anin); jo, ai është poet. Le të na sjellë ai neve një argument, ashtu si i kanë sjellë pejgamberët e parë!”'),
(2489, 21, 6, 'Nuk ka besuar asnjë vendbanim para tyre, të cilët Ne i kemi shkatërruar (duke kërkuar si këta argumente), e vallë, dot ë besojnë këta?'),
(2490, 21, 7, 'Edhe para teje kemi dërguar vetëm njerëz, të cilëve u kemi shpallur (ashtu si ty); andaj, pyetni njerëzit e dijshëm, nëse nuk dini ju!'),
(2491, 21, 8, 'Dhe Ne, nuk i kemi bërë ata (pejgamberët) trupa që nuk hanë gjellëra, as ata nuk kanë qenë të pavdekshëm.'),
(2492, 21, 9, 'Pastaj, ua kemi realizuar atyre premtimin, e i kemi shpëtuar ata (pejgamberët) dhe ata që kemi dashur Ne; ndërsa i kemi shkatërruar ata që kanë bërë kundërvajtje.'),
(2493, 21, 10, 'Na, ua kemi shpallur juve Librin, i cili është këshillë e lavdi për ju. Vallë, a nuk po kuptoni ju?'),
(2494, 21, 11, 'Sa e sa vendbanime i kemi shkatërruar, banorët e të cilëve kanë qenë mohues; e pas tyre kemi krijuar tjetër popull!'),
(2495, 21, 12, 'Kur e shijonin fuqinë (dënimin) Tonë, ata iknin nga ato (vendbanimet) me të shpejtë.'),
(2496, 21, 13, 'Mos ikni! Kthehuni atje, në lukset tuaja dhe në pallatet tuaja, se ju do të pyeteni”.'),
(2497, 21, 14, 'Ata do të thonë: “Kuku për ne, me të vërtetë, kemi qenë zullumqarë!'),
(2498, 21, 15, 'Këto rrënkime të tyre nuk pushuan, deri sa Ne nuk i bëmë ata si drith i korrur – i shuar.'),
(2499, 21, 16, 'Na nuk kemi krijuar qiellin dhe Tokën, e ç’gjendet në mes tyre, për argëtim (Tonë).'),
(2500, 21, 17, 'Sikur Na, të donim ta merrnim atë për argëtim, Na do ta merrnim atë prej anës Sonë (ashtu si i përket madhërisë Sonë), por, Ne nuk e bëjmë këtë,'),
(2501, 21, 18, 'por Ne, me të vërtetë gjuajmë të keqen, e ajo (e vërteta) e shkatërron atë (gënjeshtrën) dhe kështu ajo zhduket. Dhe, kuku për ju, për atë që i mvishni (Perëndisë).'),
(2502, 21, 19, 'Dhe Atij i përket gjithçka gjendet në qiej dhe në Tokë, e ata që janë tek ai (engjëjt), nuk ngurrojnë për të adhuruar Atë, duke mos ndier lodhje,'),
(2503, 21, 20, 'e lavdërojnë (Atë) natën dhe ditën, palodhmërisht.'),
(2504, 21, 21, 'Vallë, a mos idhutj (zotërat) që i bëjnë ata prej dheut, do të jenë në gjendje t’i ngjallin të vdekurit?'),
(2505, 21, 22, 'Sikur të kishte në to (qiej dhe Tokë) zotëra, përveç Perëndisë, do të shkatërroheshin të dyja. Qoftë i pastër Perëndia – Zot’i Arshit, nga ato që ia përshkruajnë Atij!'),
(2506, 21, 23, 'Nuk do të pyetet (Ai) për atë që punon, e ata do të jenë të pyetur.'),
(2507, 21, 24, 'Vallë, a marrin ata tjerë zotëra, përpos Tij? Thuaj: “Sillni argumentin tuaj!” Ky (Kur’ani) është një këshillë për ata që janë me mua dhe këshillë për ata që kanë para meje, por, shumica e tyre nuk di të Vërtetën dhe shmangen nga ajo.'),
(2508, 21, 25, 'Na, nuk kemi dërguar asnjë pejgamber para teje e që të mos ia kemi shpallur atij: “S’ka zot tjetër përveç Meje, andaj adhuromëni Mua!”'),
(2509, 21, 26, 'Dhe ata thonë: “Mëshiruesi ka marrë për Vete fëmijë”. – I pastër është Ai – Jo, ata (engjëjt) janë robër të ndershëm.'),
(2510, 21, 27, 'Ata nuk flasin pa lejen e Tij, dhe ata punojnë me urdhërin e Tij.'),
(2511, 21, 28, 'Ai di se ç’kishte para tyre dhe çka do të ketë pas tyre, dhe (ata) ndërmjetësojnë vetëm për ata që është i kënaqur (Perëndia), e ata janë të brengosur nga frika prej Tij.'),
(2512, 21, 29, 'E kush prej tyre thotë: “Unë jam zot, përpos Atij”, atë, Na e dënojmë me xhehennem. Kështu Na i ndëshkojmë zullumqarët.'),
(2513, 21, 30, 'A nuk e dinë mohuesit, se qiejt dhe Toka kanë qenë një tërësi, e Ne i kemi ndarë ato; e nga uji Ne kemi krijuar çdo gjë (të gjitha gjallesat)? Dhe vallë, nuk po besojnë ata?'),
(2514, 21, 31, 'Dhe, Ne, kemi vendosur nëpër Tokë male stabile, për të mos u lëkundur ajo bshkë me ta (njerëz), dhe kemi bërë lugina dhe rrugë për të udhëtuar në to.'),
(2515, 21, 32, 'Dhe, Ne, e kemi bërë qiellin plafon të mbrojtur, e ata megjithatë, shmangen nga mrekullitë që gjenden në të.'),
(2516, 21, 33, 'Dhe, Ai është i cili krijoi natën dhe ditën, Diellin dhe Hënën; e të gjitha këto lundrojnë në gjithësi.'),
(2517, 21, 34, 'Dhe, Na, para teje, askë nuk e kemi bërë të pavdekshëm, e nëse ti vdes; a do të mbesin ata gjallë?'),
(2518, 21, 35, 'Çdo person do ta shijojë vdekjen! Na ju vëmë në sprovë juve – me të keqe dhe me të mirë dhe te Ne do të ktheheni.'),
(2519, 21, 36, 'E, kur mohuesit të marrin ty vetëm si objekt talljeve (dhe thonë): “A ky është ai që po i përmend zotrat tuaj?” E, ata janë mohues në të përmendurit e Mëshiruesit të Gjithmbarshëm.'),
(2520, 21, 37, 'Njeriu është krijuar me cilësi nxituese (është i ngutshëm). Unë do t’jua tregoj argumentet e Mia, prandaj, mos më nxitoni Mua (në dënime).'),
(2521, 21, 38, 'Dhe ata thonë: “Kur do të realizohet ky premtim, nëse flitni të vërtetën?”'),
(2522, 21, 39, 'Sikur të dinin mohuesit kohën se kur nuk do të mund ta evitojnë zjarrin nga fytyrat e tyre dhe as nga shpinët e tyre, e as nuk do të ndihmohen prej askujt (nuk do të kishin kërkuar nxitimin e dënimit),'),
(2523, 21, 40, 'por, atyre do t’u arrijë Kijameti papritmas, e do t’i trullosë ata dhe nuk do të mund ta kthejnë atë, as nuk do t’u jepet afat atyre!'),
(2524, 21, 41, 'Në të vërtetë, ata i kanë përqeshur pejgamberët edhe para teje, e që (dënimi) u ka arritur shpotitësve për shkak se ata talleshin.'),
(2525, 21, 42, 'Kush do t’ju ruajë natën dhe ditën (nga dënimi) i Perëndisë, Mëshirues. Jo, askush! E, megjithatë, ata shmangën nga të përmendurit Zotin e tyre (Kur’anin).'),
(2526, 21, 43, 'Apo ata kanë zotëra të tjerë, të cilët i mbrojnë prej Nesh. Ata (zotërat e tyre) nuk mund t’i ndihmojnë as vetes, dhe askush nuk mund t’i marrë mohuesit në mbrojtje nga dënimi Ynë.'),
(2527, 21, 44, 'Jo, (ata nuk kanë mbrojtës), por Ne, u kemi dhënë atyre (mohuesve) dhe baballarëve të tyre komoditet, e jeta e tyre u zgjat. A nuk shohin vallë ata që Ne i qasemi tokës së mohuesve dhe ia cungojmë skajet e saj? E, si mund të jenë ata fitues?'),
(2528, 21, 45, 'Thuaj: “Unë vetëm ju paralajmëroj juve me shpallje”. Por, të shurdhërit nuk dëgjojnë thirrjen kurparalajmërohen.'),
(2529, 21, 46, 'E, sikur t’i prekë një puhi prej dënimit të Zotit tënd, ata me siguri do të thonin: “Kuku për ne, na kemi qenë zullumqarë”.'),
(2530, 21, 47, 'Dhe Ne, në Ditën e Kijametit do të vendosim peshoja të sakta, e askujt nuk do t’i bëhet padrejtësi për asgjë edhe nëse është fjala për peshë sa të një kokërre hara dalli (lloj mëlmese), Na do ta sjellim atë (për llogari), e Na jemi të mjaftueshëm për llogaritje.'),
(2531, 21, 48, 'Na, me të vërtetë, ia kei dërguar Musait dhe Harunit – Librin, (i cili ndanë të Vërtetën prej të pavërtetës) edhe dritë edhe këshillë për ata që besojnë,'),
(2532, 21, 49, 'të cilët i druajnë Zotit të tyre – kur nuk i sheh askush, dhe ata brengosen për çastin e Ditës së Kijametit.'),
(2533, 21, 50, 'Dhe ky (Kur’ani) është këshillë e uratë, të cilën Ne (Perëndia) e kemi shpallur, e vallë, si ta mohoni ju atë?'),
(2534, 21, 51, 'Na ia kemi dhënë Ibrahimit qysh më parë pjekurinë (e profetnisë) dhe Ne e kemi ditur (se ai është i denjë për te),'),
(2535, 21, 52, 'kur ai i tha babës së vet dhe popullit të tij: “Ç’janë këta idhuj, që ju po i adhuroni kaq shumë?”'),
(2536, 21, 53, 'Ata thanë: “Kemi gjetur se baballarët (të parët) tanë i adhuronin këta”.'),
(2537, 21, 54, '(Ibrahimi) tha: “Me të vërtetë, ju (si) dhe baballarët tuaj jeni në humbje të qartë”.'),
(2538, 21, 55, 'Ata thanë: “A na ke sjellë ti të Vërtetën, apo ti je nga ata që tallen?”'),
(2539, 21, 56, '(Ibrahimi) tha: “Jo, por Zoti juaj është Zot’i qiejve dhe i Tokës, që Ai i krijoi ndërsa unë jam njëri prej atyre që e dëshmojnë këtë.'),
(2540, 21, 57, 'Dhe, për Zotin, do t’ju bëjë një dredhi idhujve tuaj, posa të largoheni ju!” –'),
(2541, 21, 58, 'Dhe, i bëri ata copacopa, përpos idhullit të tyre më të madh. Ndoshta ata do t’i drejtohen atij.'),
(2542, 21, 59, 'Ata thanë: “Kush veproi kështu me zotërat tanë? Me të vërtetë, ai është zullumqarë!”'),
(2543, 21, 60, 'Ata thanë: “E dëgjuam një djalosh se si i nxiste ata (me fyerje), e ai quhej Ibrahim”.'),
(2544, 21, 61, 'Thanë ata: “Silleni atë para botës (njerëzve), që ata të dëshmojnë (kundër tij).'),
(2545, 21, 62, 'E pyetën ata: “A ti veprove kështu me zotërat tanë, o Ibrahim?”'),
(2546, 21, 63, '(Ibrahimi) tha: “Jo, por këtë e ka bërë ky i madhi (idhulli) i tyre, e pyetni ata, nëse (mund të) flasin”.'),
(2547, 21, 64, 'E, u kthyen ata dhe i thanë (njëri-tjetrit): “Me të vërtetë, ju jeni zullumqarë”.'),
(2548, 21, 65, 'Pastaj, u rrotulluan në kokat e tyre (e ndrruan mendimin dhe i thanë Ibrahimit): “Po ti, me të vërtetë, e di që këta nuk flasin”.'),
(2549, 21, 66, '(Ibrahimi) tha: “Mos vallë, ju adhuroni, përpos Perëndisë atë që nuk mund t’ju sjellë kurrfarë dobie as dëmi?”'),
(2550, 21, 67, 'Mjerë ju dhe mjerë ata që i adhuroni ju, përveç Perëndisë! A nuk po mendoni ju?'),
(2551, 21, 68, '(Mohuesit) thanë: “Dijeni atë (Ibrahimin) dhe ndihmoni zotrat tuaj, nëse doni të bëni ndonjë vepër!”'),
(2552, 21, 69, 'Ne, thamë: “O zjarr, bëhu i ftohtë dhe bëhu shpëtim për Ibrahimin!'),
(2553, 21, 70, 'Dhe, ata dashtën t’i bëjnë një dredhi atij, por Ne, përpjekjen e tyre, e bëmë krejtësisht të kotë.'),
(2554, 21, 71, 'Dhe, e shpëtuam atë dhe Lutin në tokën, të cilën e kemi bekuar për njerëzit.'),
(2555, 21, 72, 'Na, ia kemi dhuruar atij Is’hakun, dhe Jakubin si dhunti, dhe, të gjithë i kemi bërë të mirë.'),
(2556, 21, 73, 'Dhe Na, i kemi bërë ata udhëheqës, të cilët udhëzonin sipas urdhërit Tonë dhe u kemi shpallur atyre që të punojnë vepra të mira, të bëjnë namazin dhe të japin zeqatin, e vetëm neve na kanë adhuruar.'),
(2557, 21, 74, 'Dhe Lutit ia kemi dhënë profetninë dhe dijeninë, dhe e shpëtuam atë prej banorëve të vendbanimit, të cilët punonin vepra të këqia. Me të vërtetë, ata kanë qenë njerëz të këqinj e të mbrapsht.'),
(2558, 21, 75, 'E, atë e kemi shpënë në mëshirën Tonë. Me të vërtetë, ai është prej të mirëve.'),
(2559, 21, 76, '(Kujtoje) Nuhun kur ai thirri – para këtyre (që u përmendën), e iu gjegjëm atij dhe e shpëtuam atë dhe familjen e tij nga pikëllimi i madh.'),
(2560, 21, 77, 'Dhe i ndihmuam atij kundër popullit, i cili argumentet Tona i konsideronte gënjeshtra. Me të vërtetë, ata kanë qenë popull i keq, andaj i fundosëm të gjithë ata.'),
(2561, 21, 78, '(Kujtoje) edhe Dautin dhe Sulejmanin, kur gjykuan mbi të lashtat, në të cilat kullotën natën bagtia (pa barinj), e Ne kemi qenë dëshmitarë të gjykimit të tyre.'),
(2562, 21, 79, 'E, Na, ia mësuam Sulejmanit gjykimin dhe dyve u dhamë mençuri e dijeni. Na kemi nënshtruar malet dhe shpendët që bashkë me Dautin ta lavdërojnë Perëndinë, këtë, Ne kemi fuqi ta bëjmë.'),
(2563, 21, 80, 'Dhe Na, ia kemi mësuar atij (Dautit) punimin e këmishave të hekurit për ju, për t’ju mbrojtur juve në luftë me armikun, andaj, a jeni ju mirënjohës?'),
(2564, 21, 81, 'Dhe, i kemi dhënë Sulejmanit frymë të fortë, që të ecë ajo (fryma) sipas urdhërit të tij, kah toka, të cilën Ne e kemi bekuar; dhe Na i dimë të gjitha gjërat;'),
(2565, 21, 82, 'dhe, (ia nënshtruam atij) disa nga djajtë që u zhytnin në det për të dhe bënin edhe punë të tjera, dhe Na i ruanim ata,'),
(2566, 21, 83, 'dhe (kujtoje) Ejubin kur iu lut Zotit të vet: “Mua, më ka goditur mjerimi, e Ti je më i mëshirshmi prje mëshiruesve!”'),
(2567, 21, 84, 'E, iu gjegjëm Na atij dhe ia larguam mjerimin që kishte ai, dhe ia dhamë atij familjen e tij, dhe krahas tyre – edhe aq (ia dyfishuam familjen) nga mëshira Jonë, që kjo të jetë këshilë për ata që Na adhurojnë.'),
(2568, 21, 85, 'Dhe (kujtoje) Ismailin, Idrisin dhe Dhulkiflin! Të gjithë këta kanë qenë të durueshëm.'),
(2569, 21, 86, 'Dhe, Na, i kemi kaluar ata me mëshirën Tonë. Me të vërtetë, ata janë njerëz të mirë.'),
(2570, 21, 87, 'Dhe, (kujtoje) Dhen-Nunin (Junusin) kur iku i zemëruar (nga populli i vet), e mendoi se Ne nuk do ta dënojmë, andaj thërriti nga errësira: “S’ka zot tjetër përveç Teje! Ti je i lavdëruar! Me të vërtetë, unë kam gabuar (ndaj vetes)!'),
(2571, 21, 88, 'Na iu gjegjëm atij dhe e shpëtuam nga brengosja. Kështu, Ne i shpëtojmë besimtarët.'),
(2572, 21, 89, 'Dhe, (kujtoje) Zekerijjan, kur iu lut Zotit të vet: “O Zoti im, mos më le vetëm (pa fëmijë – djalë), e Ti je trashëgimtari më i mirë”.'),
(2573, 21, 90, 'E, Ne, iu gjegjëm atij, dhe ia dhuruam atij Jahjan, dhe Na, ia bëmë gruan e tij të aftë (për lindje). Me të vërtetë, ata (pejgamberët) nxitonin në vepra të mira dhe Na u luteshin, duke shpresuar (mëshirën Tonë), dhe duke druajtur (nga dënimi Ynë), dhe ishin të përulur ndaj Nesh.'),
(2574, 21, 91, 'Dhe (kujtoje) atë, e cila ruajti virgjinitetin e saj, e Na, i dhamë asaj frymëzim nga shpirti Ynë, dhe e bëmë atë (Merjemin) dhe djalin e saj – argument për botërat – njerëzit (që Perëndia është i pushtetshëm për çdo gjë).'),
(2575, 21, 92, 'Me të vërtetë, ky është besimi juaj – besimi i vetëm, e Unë jam Zoti juaj, andaj më adhuroni Mua!'),
(2576, 21, 93, 'E, ata në çështje të besimit u përçanë ndërveti, por të gjithë do të kthehen te Ne (e do t’i gjykojmë).'),
(2577, 21, 94, 'Ai që punon vepra të mira dhe është besimtar, përpjekja nuk do t’i mohohet. Dhe, këtë (përpjekje) Na ia shënojmë atij.'),
(2578, 21, 95, 'Dhe e pamundur është, që banorët e një vendi të cilët i kemi zhdukur, Na, të mos kthehen (te Ne, në Ditën e Kijametit).'),
(2579, 21, 96, 'Dhe, kur të hapet ajo (penda), e dalin, Jexhuxhi dhe Mexhuxhi dhe lëshohen prej çdo lartësie duke shpejtuar'),
(2580, 21, 97, 'dhe të afrohet premtimi i vërtet, në atë çast – shikimet e atyre që nuk kanë besuar, do të zgurdullohen, (e thonë): “Kuku për ne! na kemi qenë të pakujdesshëm ndaj kësaj. Jo, por ne – vetes i kemi bërë zullum”.'),
(2581, 21, 98, 'Me të vërtetë, ju dhe ata që i adhuroni, - përpos Perëndisë, jeni lëndë djegëse të xhehennemit. Ju, do të vini në të.'),
(2582, 21, 99, 'Sikur këta të ishin zotra, nuk do të vinin te ajo (skëterra), dhe, të gjithë (edhe adhuruesit edhe të adhuruarit) – do të mbeten përherë në të.'),
(2583, 21, 100, 'Ata do të kenë në të (zjarr) gërhamë (frymënxjerrje me zë) dhe ata në të (zjarr) nuk do të dëgjojnë (asgjë).'),
(2584, 21, 101, 'E, ata, të cilëve u kemi premtuar shpërblimin e bukur qysh më parë, ata do të jenë larg nga ajo (skëterra).'),
(2585, 21, 102, 'Ata, nuk do të dëgjojnë fërshëllimën e tij (zjarrit) dhe ata do të qëndrojnë përherë në atë që u ka dëshiruar shpirti i tyre,'),
(2586, 21, 103, 'ata, nuk do t’i brengosë tmerri më i madh, por engjëjt do t’i presin ata (duke u thënë): “Kjo është (ajo) dita juaj e premtuar”.'),
(2587, 21, 104, '(Kjo do të ngjajë) atë Ditë, kur do të palojmë qiellin – ashtu si palohet letra e shkrimit. Ashtu siç e kemi filluar krijimin e parë, (ashtu) do t’i kthejmë ato (krijesat). Ky është premtimi Ynë. Na, këtë, me të vërtetë, do ta bëjmë.'),
(2588, 21, 105, 'Na, me të vërtetë, e kemi shënuar në Zebur, pas Tevratit, që Tokën do ta trashëgojnë robërit e Mi të mirë.'),
(2589, 21, 106, 'Me të vërtetë, në këtë (Kur’an) ka këshilla të mjaftueshme për ata që e adhurojnë Perëndinë.'),
(2590, 21, 107, 'Dhe, Na të kemi dërguar ty (o Muhammed!) vetëm si mëshirë për botërat – njerëzit.'),
(2591, 21, 108, 'Thuaj: “Mua më është shpallur vetëm (ajo), që Zoti juaj është vetëm një Zot, pra i jeni të përulur ju kësaj (Shpallje)”.'),
(2592, 21, 109, 'E, nëse shmangen ata, ti thuaju: “Unë, ju kam lajmëruar juve, të gjithëve – pa dallim dhe unë nuk di a është afër apo larg ajo, që u është premtuar juve”.'),
(2593, 21, 110, 'Ai, me të vërtetë, di të folmën (tuaj) me zë dhe di atë që fshihni ju.'),
(2594, 21, 111, 'E, unë nuk di: a mos është ajo (shtyerja e dënimit) sprovë për ju, apo kënaqësi e përkohshme.'),
(2595, 21, 112, '(Muhammedi) tha: “O Zoti im, gjyko ashtu si e kanë merituar! E, Zoti ynë, Bamirës i Gjithmbarshëm, është Ai, nga i cili kërkohet ndihmë kundër atyre që ju ia mvishni Atij”.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'O njerëz, druajuni (dënimit) Zotit tuaj! Me të vërtetë, dridhja e Ditës së Kijametit, është ngjarje e madhe!'),
(2597, 22, 2, '(Atë) ditë kur do ta shihni atë, çdo mendeshë (grua që është duke i dhënë gji fëmijës), do të shkujdeset ndaj tij (do ta flakë atë) dhe çdo grua shtatzënë do ta hedh frytin e vet (prej barkut) dhe do t’i shohë njerëzit si të dehur e ata nuk janë të dehur, por dënimi i Perëndisë është i ashpër.'),
(2598, 22, 3, 'Ka disa njerëz që polemizojnë për Perëndinë – pa kurrfarë dijenie dhe ndjekin rrugën e çdo djalli rrebel,'),
(2599, 22, 4, 'e është caktuar për të (djallin), se ai që e bën mik atë (djallin), e shmangë (nga rruga e drejtë) atë, e sjell atë në ënimin e zjarrit.'),
(2600, 22, 5, 'O njerëz, nëse ju dyshoni në ringjalljen (pas vdekjes), dinie se Na ju kemi krijuar juve prej dheut (Ademin), pastaj prej pikës së farës, (spermës) pastaj prej droçkave të gjakut, pastaj prej (copëzës) mishit (embrionit) me formë të plotë ose jo të plotë, për t’ju treguar juve (fuqinë Tonë). Dhe Na, e mbajmë në mitër atë që duam deri në kohën e caktuar. Andaj, ju nxjerrim juve si fëmijë; e pas kësaj, do të arrini ju (fuqinë) më të madhe. Disa nga ju vdesin e disa ngelin deri në kufirin më të ulët të jetës, që mos dijë asgjë nga ajo që ka ditur më parë. Dhe, ti, e sheh tokën e thatë, e kur Ne lëshojmë ujin (të reshurat) në të, ajo lëkundet dhe gufon dhe mbijnë (rritën) gjithfarë lloj bimësh të mrekullueshme,'),
(2601, 22, 6, 'meqë Perëndia ekziston, dhe Ai është – i pushtetshëm për t’i ngjallë të vdekurit, dhe Ai është i Plotëfuqishëm për çdo gjë,'),
(2602, 22, 7, 'dhe, nga se, me të vërtetë, Dita e Kijametit do të vijë. Për të, s’ka dyshim dhe që, në të vërtetë, Perëndia, do t’i ringjallë ata që janë në varre.'),
(2603, 22, 8, 'Ka disa njerëz që polemizojnë për Perëndinë – pa kurrfarë dijenie, dhe pa kurrfarë baze dhe pa Libër të shenjtë,'),
(2604, 22, 9, 'duke e shtrembëruar kokën, që të shmanget nga rruga e Perëndisë, Ai në këtë botë ka poshtërim, e Ne, në Ditën e Kijametit do t’i japim atij që të shijojë dënimin e flakës.'),
(2605, 22, 10, '“Kjo është, ngase kësaj, i kanë prirë duart tuaja, dhe se, Perëndia, në të vërtetë, nuk është i padrejtë ndaj robërve të Tij”.'),
(2606, 22, 11, 'Dhe, ka njerëz që e adhurojnë Perëndinë sipas laverdisë e interesit, që – nëse e përcjellë fati, ai qetësohet, e nëse e goditë fatkeqësia, ai kthehet në mohim; e kështu, e humbë këtë botë dhe tjetrën. Kjo, në të vërtetë, është humbje e qartë.'),
(2607, 22, 12, '(Të atillët) i luten, pos Perëndisë, edhe atyreve, të cilët nuk mund t’u sjellin ndonjë dëm, e as nuk mund t’u sjellin ndonjë dobi. Kjo, me të vërtetë, është devijim i madh;'),
(2608, 22, 13, '(të atillët) i luten asaj, dëmi i së cilës është më i afërt se dobia. Sa ndihmës i keq që është ai, e sa njerëz të këqinj – janë adhuruesit e tyre.'),
(2609, 22, 14, 'Me të vërtetë, Perëndia, i shpie ata, që besojnë dhe punojnë vepra të mira – në kopshtije, nëpër të cilat rrjedhin lumenj. Me të vërtetë, Perëndia punon çka të dojë.'),
(2610, 22, 15, 'Kush mendon, se Perëndia nuk do të ndihmojë (Muhammedin) në këtë botë dhe në botën tjetër, le të përgatitë një litar (lartë – në tavan të shtëpisë për t’u varrë) dhe le të mendon a ia largon veprimi i tij hidhërimin (për atë vërtetë të Perëndisë ndaj Profetit).'),
(2611, 22, 16, 'Dhe, kështu, Na, ia kemi shpallur atij argumentet e qarta (Kur’anin) dhe Perëndia, me të vërtetë, e udhëzon në rrugë të drejtë kë të dojë.'),
(2612, 22, 17, 'Me të vërtetë, ata që kanë besuar, dhe Hebrënjtë, Sabi’inët, të krishterët, Mexhust (adhuruesit e zjarrit) dhe ata që i bëjnë shok Atij, do t’i gjykojë drejtë Perëndia – në mes tyre, në Ditën e Kijametit. Me të vërtetë, Perëndia është dëshmitar për çdo gjë.'),
(2613, 22, 18, 'A nuk po sheh ti, se, me të vërtetë, Perëndisë i bëjnë sexhde: ata që gjenden në qiej dhe ata që gjenden në Tokë, edhe Dielli edhe hëna, edhe yjët, edhe malet, edhe drunjtë edhe shtazët dhe shumë nga njerëzit, por shumë (njerëz) meritojnë dënimin. E, kë ta poshtërojë Perëndia, atë askush nuk mund ta bëjë të nderuar. Perëndia, me të vërtetë, punon çka të dojë.'),
(2614, 22, 19, 'Këto dy tabore kundërshtarësh, grinden për Zotin e tyre. Ata që nuk kanë besuar, kanë rroba të prera prej zjarrit, e nëpër kokat e tyre do të hedhet ujë i vluar,'),
(2615, 22, 20, 'do të shkrihen me të (me atë ujë) rropulitë (organet e brendshme) dhe lëkura,'),
(2616, 22, 21, 'dhe për ta ka kamxhikë prej hekuri.'),
(2617, 22, 22, 'Sa herë që përpiqen të dalin nga ai (zjarri), për shkak të brengosjes, do të kthehen në të (duke i rrahur me kamxhikë) dhe (u thuhet atyre): “Shijoni dënimin me djegëje të tmerrshme!”'),
(2618, 22, 23, 'Me të vërtetë, Perëndia, i shpie ata, që besojnë dhe punojnë vepra të mira – në kopshtije, nëpër të cilat rrjedhin lumenj. Ata, do të stolisen me bylyzykë të artë dhe inxhi, e rrobat e tyre në të (xhennet), do të jenë prej mëndafshi.'),
(2619, 22, 24, 'Ata janë udhëzuar (në këtë jetë) kah fjala e mirë dhe janë udhëzuar në rrugën e Falenderuesit (Perëndisë).'),
(2620, 22, 25, 'Me të vërtetë, ata që nuk besojnë dhe pengojnë njerëzit nga rruga e Perëndisë dhe Mesxhidil-haramit, të cilin Ne e kemi bërë të njëjtë për të gjithë njerëzit, si për vendasin ashtu edhe për atë që vie nga tjetër vend (vizitori), e kush synon të bëjë në të ndonjë shmangie me të keqe, Na do t’i japim të shijojë dënimin e dhembshëm.'),
(2621, 22, 26, '(Kujtoje, o Muhammed!) kur Na i patëm përgatitur Ibrahimit vendin e faltorës (duke i thënë): “Mos më përshkruaj Mua shok asgjë, dhe pastroje Shtëpinë Time për ata që e vizitojnë, që rrinë aty dhe që bëjnë namaz,'),
(2622, 22, 27, 'dhe ftoi njerëzit në haxhillëk, do të vijnë ata ty – në këmbë dhe (duke shaluar) në çfarëdo deve; duke ardhur prej të gjitha viseve të largëta,'),
(2623, 22, 28, 'që të prezentojnë në dobitë e tyre dhe të përmendin emrin e Perëndisë në ditët e njohura (të therrjes së kurbanit) – të shtazëve me të cilat i ka furnizuar Ai. E, hani nga ato (mishin) dhe ushqeni nevojtarin e varfër!'),
(2624, 22, 29, 'Pastaj, le të heqin ndotësirat nga trupi i tyre dhe le t’i kryejnë zotimet e tyre dhe le të vizitojnë (le të sillen rreth e rrotull) Faltorës së lashtë (të nderuar).'),
(2625, 22, 30, 'Këto (janë dispozitat). E, kush respekton dispozitat e Perëndisë, kjo është më mirë për të, te Zoti i tij, - e juve u janë lejuar (ngrënia) e kafshëve, përpos që u janë thënë juve (se janë të ndaluara). Andaj largohuni prej ndotësisë së idhujve dhe largohuni prej fjalëve të rrejshme'),
(2626, 22, 31, 'duke iu përulur Perëndisë, e duke mos i bërë (asgjë) shok Atij. Kush i bën shok Perëndisë, ai i ngjanë atij që bie nga qielli e që e rrëmben zogu ose që stuhia e erës e ka hedhur në vende të largëta.'),
(2627, 22, 32, 'Po, kështu është kjo. E, kush respekton dispozitat e Perëndisë (kurbanin), ky (respektim) është prej zemrave të tyre që i druajnë Perëndisë.'),
(2628, 22, 33, 'Ju keni nga ato (kafshët) dobi deri në kohë të caktuar; pastaj, vendi i tyre (i therrjes) është te Faltorja e lashtë.'),
(2629, 22, 34, 'Për çdo popull (fetar), Na kemi caktuar vend të therrjes së kurbanit, që të përmendin emrin e Perëndisë (kur therrin kafshën), të cilën ua ka dhuruar Ai. Zoti juaj është një Zot, dhe Atij përuljuni (bëhuni muslimanë) dhe sihariqojë të përulurit,'),
(2630, 22, 35, 'ata, zemrat e të cilëve – kur përmendet Perëndia frikësohen, dhe (sihariqoji) të durueshmit (në çdo veprim) që i godasin ata, dhe ata që bëjnë namaz dhe që ndajnë nga ajo, me çka i kemi furnizuar Ne – ata.'),
(2631, 22, 36, 'Dhe (therrjen) e deves e kemi bërë për ju (njërën) nga dispozitat e Perëndisë, dhe ju keni dobi nga ato; andaj, përmendeni emrin e Perëndisë mbi ato (gjatë therrjes), duke u radhitur në këmbë, e kur të bien në tokë (pasi t’i therrni), hani nga to, dhe ushqeni edhe atë që nuk lypë, edhe atë që lypë; kështu i mei nënshtruar Ne ato për ju, për t’u bërë ju falenderues.'),
(2632, 22, 37, 'Te Perëndia nuk arrin as mishi i tyre as gjaku i tyre, por te Ai arrin vepra e mirë – e bërë nga ju, kështu Ai – ua ka nënshtruar juve ato, për ta madhëruar ju Perëndinë, që ju ka udhëzuar (në rrugën e drejtë). Dhe, sihariqoji bamirësit!'),
(2633, 22, 38, 'Perëndia, me të vërtetë, i mbron ata që bindshëm besojnë, Perëndia nuk don asnjë tradhëtar, mohues.'),
(2634, 22, 39, 'U lejohet (lufta) atyre që janë të sulmuar, ngase ju është bërë zullum atyre – dhe Perëndia, me të vërtetë, është i fuqishëm që t’u ndihmojë, -'),
(2635, 22, 40, 'atyre që janë dëbuar nga vatra e tyre padrejtësisht, vetëm për shkak se thanë: “Zoti ynë është Perëndia. Dhe, sikur Perëndia të mos prapsonte (të keqen) nga një pjesë e njerëzve, nga pjesa tjetër, do të shkatërroheshin manastiret (faltoret e vogla), kishat, sinagoget dhe xhamitë, në të cilat, përmendet shumë emri i Perëndisë. Me të vërtetë, Perëndia e ndihmon atë, i cili ndihmon Ate (fenë islame). Perëndia, me të vërtetë, është i fortë dhe i fuqishëm (u jep pushtet e fuqi), -'),
(2636, 22, 41, 'atyre, të cilëve, nëse u japim pushtet në Tokë – kryejnë namazin, japin zeqatin, urdhërojnë për të mirë, e ndalojnë atë që është e mbrapshtë. Dhe, te Perëndia është fundi i çdo gjëje.'),
(2637, 22, 42, 'E, nëse ata ty të konsiderojnë për gënjeshtar, - madje edhe para teje i kanë quajtur gënjeshtarë (pejgamberët e vet): populli i Nuhut, edhe Adi, edhe Themudi,'),
(2638, 22, 43, 'dhe populli i Ibrahimit dhe populli i Lutit,'),
(2639, 22, 44, 'dhe banorët e Medjenit; dhe Musai është konsideruar për gënjeshtar, dhe Unë u kam dhënë afat mohuesve; pastaj i kam dënuar ata, dhe çfarë ka qenë dënimi Ynë!'),
(2640, 22, 45, 'E, sa e sa vendbanime i kemi shkatërruar Ne, banorët e të cilëve ishin mëkatarë, dhe mbi kulmet e tyre u rrëzuan muret, dhe sa puse mbetën të braktisura, dhe sa pallate të larta të shkretëruara!'),
(2641, 22, 46, 'Vallë, a nuk kanë udhëtuar ata nëpër botë e të kenë zemra me të cilat të kuptojnë dhe vesh që të dëgjojnë? Me të vërtetë, nuk i kanë sytë e verbër (në këto gjëra), por u janë të verbëra zemrat e tyre, që i kanë në krahëror.'),
(2642, 22, 47, 'Dhe, ata të nxisin ty (që t’ju vijë tyre) dënimi, e Perëndia kurrsesi nuk e then premtimin e Vet. Me të vërtetë, një ditë te Zoti yt është sa një mijë vjet sipas numrimit tuaj (në këtë jetë).'),
(2643, 22, 48, 'Sa e sa vendbanime, të cilave u kam shtyer Unë dënimin duke qenë ata zullumqarë – e prapëseprapë i kam dënuar. E, tek Unë kthehet çdo gjë.'),
(2644, 22, 49, 'Thuaj (o Muhammed): “O njerëz, unë jam për ju vetëm paralajmërues i qartë!”'),
(2645, 22, 50, 'Ata që besojnë dhe bëjnë vepra të mira, për ta ka falje dhe dhunti fisnike;'),
(2646, 22, 51, 'e ata që përpiqen t’i mohojnë argumentet Tona, duke supozuar se nuk do të mund t’i dënojmë, janë banorë të xhehennemit.'),
(2647, 22, 52, 'Dhe, Ne, nuk kemi dërguar para teje, asnjë të dërguar, e asnjë pejgamberë – që kur dëshironte ai (i dërguari), djalli të mos hedhte ndonjë (parashikim) në dëshirën e tij; e Perëndia largonte atë që e hedhte djalli, e pastaj i forconte argumentet e Veta. Perëndia është i Plotëdijëshëm dhe i Gjithëdijshëm.'),
(2648, 22, 53, '(Perëndia don) të bëjë atë që e hedhë djalli, sprovë për ata, zemrat e të cilëve janë të sëmura dhe për ata, zemrat e të cilëve janë të egra. Me të vërtetë, zullumqarët janë në përçarje të madhe;'),
(2649, 22, 54, 'dhe (Perëndia don që) të dinë ata, të cilëve u është dhënë dijenia, se ai (Kur’ani), është e Vërteta nga Zoti yt, e që ta besojnë atë dhe që zemrat e tyre të priren në të. Me të vërtetë, Perëndia i udhëzon besimtarët në rrugën e drejtë.'),
(2650, 22, 55, 'E, ata që mohojnë, vazhdojnë të dyshojnë në të (Kur’an), derisa të mos u arrijë atyre çasti i papritur (vdekja), ose (derisa të mos arrijë dënimi i tyre) në Ditën e fundit.'),
(2651, 22, 56, 'I tërë pushteti, atë ditë i përket Perëndisë. Ai do të gjykojë në mes tyre: ata që kanë besuar dhe punuar vepra të mira, do të gjenden në kopshtet e dhuntive,'),
(2652, 22, 57, 'e ata që nuk besojnë dhe i përgënjeshtrojnë ajetet Tona, për ta ka dënim të poshtëruar.'),
(2653, 22, 58, 'Dhe ata, që kanë emigruar në rrugën e Perëndisë, e kanë rënë dëshmorë, apo kanë vdekur, me siguri do t’i furnizojë Perëndia me shpërblim të mirë. Dhe, me të vërtetë, Perëndia është furnizuesi (dhuruesi) më i mirë,'),
(2654, 22, 59, 'Ai do t’i shpie ata, në vendin ku, me të vërtetë, do të jenë të kënaqur! Perëndia me të vërtetë, është i Plotëdijshëm dhe i butë.'),
(2655, 22, 60, 'Po, kështu është kjo! Kush dënon aq sa është dëmtuar ai me të, e pastaj përsëri i bëhet zullum atij, Perëndia, me siguri, do ta ndihmojë atë. Me të vërtetë, Perëndia është shlyes i mëkateve dhe falës.'),
(2656, 22, 61, 'Po, kështu është kjo! E, Perëndia, me të vërtetë, e shtie natën në ditë, e ditën në natë (e ndërron gjatësinë në mes tyre), dhe Perëndia, i dëgjon dhe i sheh të gjitha.'),
(2657, 22, 62, 'Po, kështu është kjo! Që Perëndia, me të vërtetë, është Ai, që ekziston, e që, të gjitha ato që ata i adhurojnë, përpos Tij, janë të paekzistueshme. Dhe, padyshim, Perëndia është i lartësuar dhe i madh.'),
(2658, 22, 63, 'A nuk sheh ti se Perëndia lëshon shiun nga qielli, e toka gjelbërohet. Me të vërtetë, Perëndia është i butë dhe i dijshëm për çdo gjë,'),
(2659, 22, 64, 'të Tija janë gjithë çka ka në qiej dhe gjithë ç’ka në Tokë! Me të vërtetë, Perëndia nuk ka nevojë për asgjë, dhe vetëm Ai është i denjë për falenderim!'),
(2660, 22, 65, 'A nuk sheh ti, se Perëndia, me të vërtetë, ka bërë për ju ato që janë në Tokë. Dhe, anijen që lundron nëpër det, me urdhërin e Tij. Dhe Ai mban qiellin që të mos bie në Tokë, vetëm me lejen e Tij. Me të vërtetë, Perëndia është i butë dhe mëshirues për njerëzit.'),
(2661, 22, 66, 'Ai, ju jep jetë, dh pastaj do t’ju bëjë të vdekur, e pastaj do t’ju ringjallë. Me të vërtetë, njeriu (mohues) është jomirënjohës.'),
(2662, 22, 67, 'Na, për çdo popull kemi hartuar dispozita fetare, e ata shkojnë sipas tyre, dhe mos lejo, kurrsesi që të bëjnë polëmikë me ty, - për atë çështje. Dhe thirri ti (ata) te Zoti yt! Ti, me të vërtetë, je në rrugën e drejtë.'),
(2663, 22, 68, 'E, nëse ata grinden me ty, thuaju: “Perëndia di më së miri atë që e punoni ju”.'),
(2664, 22, 69, 'Perëndia, do të gjykojë në mesh jush – në Ditën e Kijametit, për konfliktet që keni pasur.'),
(2665, 22, 70, 'A nuk din ti, se Perëndisë – i është e njohur çdo gjë që gjendet në qiej e në Tokë? Të gjitha këto janë (shënuar) në Libër. Me të vërtetë, të gjitha këto, për Perëndinë janë lehtë.'),
(2666, 22, 71, 'Dhe, adhurojnë përpos Perëndisë – (atë), të cilën, Ai nuk u ka dërguar atyre asnjë argument, dhe për atë, që ata nuk kanë kurrfarë dijenie. E, zullumqarët, nuk kanë kurrfarë ndihmësi.'),
(2667, 22, 72, 'E, kur t’ju lexohen atyre ajetet Tona të qarta, ti, do të vërejsh në fytyrat e atyre që nuk besojnë – pakënaqësinë e madhe; ata duan të vërsulen ndaj atyre, që i lexojnë ajetet Tona. Thuaj: “A doni t’ju tregoj juve se çka është më e keqe se ajo? – Zjarri! Perëndia, ua ka premtuar atyre që nuk besojnë. E, sa vendbanim i shëmtuar që është ai!”'),
(2668, 22, 73, 'O njerëz, ja një shembull, e dëgjojeni atë: “Ata, të cilët, përpos Perëndisë, i adhuroni ju, nuk mund të krijojnë – kurrsesi asnjë mizë, madje – edhe nëse tubohen për këtë (krijimin e mizës). E, nëse miza, u merr atyre diçka, nuk do të mund ta marrin (ata) nga ajo. I dobët është adhuruesi (i tjetërkujt, pos Perëndisë), por edhe i adhuruari – (pos Perëndisë)!'),
(2669, 22, 74, 'Ata, nuk e njohin Perëndinë sa duhet, e Perëndia, me të vërtetë, është i fortë dhe i fuqishëm.'),
(2670, 22, 75, 'Perëndia i zgjedh pejgamberët nga engjëjt dhe njerëzit; me të vërtetë, Perëndia i dëgjon dhe i sheh të gjitha.'),
(2671, 22, 76, '(Ai) di ç’ka para tyre dhe ç’ka pas tyre, dhe te Perëndia, kthehen të gjitha gjërat.'),
(2672, 22, 77, 'O besimtarë! Bëni ruqu, bëni sexhde, adhuronie Zotin tuaj, dhe punoni vepra të mira për të arritur shpëtimin.'),
(2673, 22, 78, 'Dhe, luftoni në rrugën e Perëndisë, ashtu si duhet luftuar. Ai, ju ka zgjedhë dhe nuk ju ka bërë juve në fé kurrfarë vështirësie – ashtu si (ka qenë) në fenë e babës suaj, Ibrahimit. Ai (Perëndia), ju ka quajtur muslimanë – qysh më parë, por edhe në këtë (Kur’an), që pejgamberi të jetë dëshmitar i juaj, dhe që ju të jeni dëshmitar të njerëzve tjerë. Andaj, faleni namazin dhe jepni zeqatin dhe mbështetuni te Perëndia! Ai është mbrojtës i juaj. E, sa mbrojtës dhe ndihmës i mrekullueshëm është Ai!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'Me të vërtetë, shpëtuan besimtarët,'),
(2675, 23, 2, 'të cilët, në namazin e tyre janë të përulshëm,'),
(2676, 23, 3, 'dhe të cilët shmangen nga kotësitë,'),
(2677, 23, 4, 'dhe të cilët japin zeqatin,'),
(2678, 23, 5, 'dhe të cilët i ruajnë organet e veta gjenitale,'),
(2679, 23, 6, 'përpos (në martesë) me gratë e tyre ose me skllavëreshat e tyre, (për këto) ata nuk janë të qortuar.'),
(2680, 23, 7, 'E, kush kërkon (kënaqësi) përpos këtyre, të atillët – me të vërtetë, janë kundërvajtës.'),
(2681, 23, 8, '(Të shpëtuar janë) edhe ata, që i ruajnë amanetet dhe obligimet (e marra përsipër),'),
(2682, 23, 9, 'dhe ata që e ruajnë namazin e tyre, (prej mungesave),'),
(2683, 23, 10, 'ata janë trashëgimtarë të denjë,'),
(2684, 23, 11, 'të cilët, do ta trashëgojnë (xhennetin) Firdeusin. Ata, do të banojnë përherë në të.'),
(2685, 23, 12, 'Me të vërtetë, Na e kemi krijuar njeriun praj baltës (thelbit të tokës),'),
(2686, 23, 13, 'e pastaj, si pikë – fare të vendosur në vend të sigurt'),
(2687, 23, 14, 'e mandej, pikën e farës e kemi bërë gjak (të droçkuar), e pastaj e kemi bërë copëz mishi; mandej, atë copëz mishi e kemi shndërruar në eshtra, e eshtrat i kemi veshur me mish dhe pastaj kemi krijuar krijesë tjetër. Qoftë lartësuar Perëndia, krijuesi më i përsosur!'),
(2688, 23, 15, 'Mandej, pas kësaj ju do të vdisni,'),
(2689, 23, 16, 'pastaj, ju me të vërtetë, do të ringjalleni në Ditën e Kijametit.'),
(2690, 23, 17, 'Ne, kemi krijuar mbi ju shtatë qiej dhe Ne nuk jemi të pakujdesshëm, ndaj krijesave Tona;'),
(2691, 23, 18, 'Ne, lëshojmë shiun nga qielli me masë të caktuar. Pastaj, Ne e mbajmë në Tokë dhe Ne jemi të pushtetshëm ta ngrisim (ujin nga toka dhe ta shpërndajmë)'),
(2692, 23, 19, 'dhe me ndihmën e tij kemi bërë për ju kopshtije hurmesh dhe rrushi, në to ka shumë pemë për ju, që hani prej tyre –'),
(2693, 23, 20, '(kemi krijuar) edhe drurin, që rritet në malin e Sinajit, i cili jep vaj dhe mëlmesë (erurinë), atyre që e hanë.'),
(2694, 23, 21, 'Dhe ju, në kafshët keni këshillë: Na ju japim të pini atë që gjendet në barqet e tyre, e për ju, në to, ka shumë dobi dhe nga ato ju hani,'),
(2695, 23, 22, 'dhe ju, me ato dhe me anije barteni.'),
(2696, 23, 23, 'Na e kemi dërguar Nuhun te populli i tij, dhe ai tha: “O populli im, adhuronie Perëndinë! Ju nuk keni zot tjetër përveç Tij, vallë, a nuk druani?”'),
(2697, 23, 24, 'Por, paria e popullit të tij, që nuk besonin, thanë: “Ky është vetëm njeri si ju e don vetëm të madhërohet mbi ju. Sikur të kishte dashur Perëndia, do t’i dërgonte engjëjt; kështu diçka – nuk kemi dëgjuar nga të parët tanë të lashtë,'),
(2698, 23, 25, 'ai është njeri i marrë, e lëreni atë njëfarë kohe!”'),
(2699, 23, 26, '(Nuhu) tha: “O Zoti im, më ndihmo, meqë ata më konsiderojnë për gënjeshtar!”'),
(2700, 23, 27, 'Dhe Ne, i shpallëm atij: “Ndërto anijen nën mbikëqyrjen dhe frymëzimin Tonë, e kur të vie urdhëri Ynë e vëlon furra (gufon uji), ngarko në anije prej çdo lloj gjallese – nga një palë (një mashkull e një femër) dhe familjen tënde, përpos atyre që ka qenë fjala (për dënim), dhe mos M’u drejto, për ata që kanë bërë zullum, se ata, me të vërtetë, do të fundosen.'),
(2701, 23, 28, 'E, kur të vendosesh në barkë ti dhe ata që janë me ty, thuaj: “Falenderojmë Perëndinë, i cili na shpëtoi prej popullit zullumqarë”.'),
(2702, 23, 29, 'Dhe thuaj: “Zoti im, më lësho në vend të bekuar dhe Ti je më i miri, që jep vend (të bekuar).'),
(2703, 23, 30, 'Me të vërtetë, në këtë ka argumente dhe Ne, me të vërtetë, i vëmë në sprovë (njerëzit).'),
(2704, 23, 31, 'Na, pas tyre – krijuam gjeneratë tjetër,'),
(2705, 23, 32, 'e u dërguam atyre pejgamber nga mesi i tyre, (i cili u tha atyre): “Adhuronie Perëndinë! Ju, nuk keni zot tjetër përveç Tij; vallë a nuk druani?”'),
(2706, 23, 33, 'Por, paria e popullit të tij, që nuk besonin dhe e konsideronin për gënjeshtër takimin në botën tjetër dhe të cilëve u kemi dhënë begati në këtë botë botë, thanë: “Ky është vetëm njeri si ju, han nga ato që hani edhe ju dhe pin nga ato që pini edhe ju;'),
(2707, 23, 34, 'e nëse ju i përuleni njeriut që është si ju, me siguri – ju do të jeni të humbur.'),
(2708, 23, 35, 'A ju premton ai, që kur të vdisni e të bëheni dhé dhe eshtra, ju, me të vërtetë, do të ringjalleni?'),
(2709, 23, 36, 'Larg e larg, janë ato që po u premtohen!'),
(2710, 23, 37, 'Nuk ka jetë tjetër përpos jetës së kësaj bote, na jetojmë e vdesim e nuk do të ringjallemi më.'),
(2711, 23, 38, 'Ai është vetëm njeri që trillon gënjeshtra për Perëndinë, dhe na nuk i besojmë atij”.'),
(2712, 23, 39, '(Pejgamberi) tha: “O Zoti im, më ndihmo, meqë po më konsiderojnë gënjeshtar!”'),
(2713, 23, 40, '(Perëndia) tha: “Së shpejti do të pendohen ata!”'),
(2714, 23, 41, 'E, me të drejtë i kaploi ata zëri i tmerrshëm (dënimi i merituar), dhe Ne i bëmë ata si lymërishtë të lumit. Larg qoftë (nga mëshira) populli zullumqarë!'),
(2715, 23, 42, 'E, pas tyre, Ne kemi krijuar gjenerata të reja.'),
(2716, 23, 43, 'Asnjë popull nuk mund as ta shpejtojë as ta ngadalësojë kohën e caktuar të (zhdukjes) së tij;'),
(2717, 23, 44, 'dhe kemi dërguar pejgamberë njërin pas tjetrit. Kurdo që i ka ardhur ndonjë populli pejgamberi i tyre, ata e kanë përgënjeshtruar atë. Kemi bërë që njëri (popull) të vijë pas tjetrit (popull) dhe i kemi bërë ata tregim historik. Qoftë larg populli që nuk beson!'),
(2718, 23, 45, 'Pastaj dërguam Musain dhe vëllain e tij Harunin me argumentet Tona dhe mrekullinë e qartë'),
(2719, 23, 46, 'te Faraoni dhe paria e tij, por ata që u bënë mendjemëdhenj, ata ishin njerëz të kryelartësuar.'),
(2720, 23, 47, 'E thanë ata: “Vallë, a t’u besojmë dy njerëzve, të cilët janë si ne, e populli i tyre na janë përulur?”'),
(2721, 23, 48, 'Dhe këta dy i shpallën për gënjeshtarë, andaj ishin nga të shkatërruarit.'),
(2722, 23, 49, 'Na, në të vërtetë, ia dhamë Librin Musait, që ata (populli i tij) të udhëzohen në rrugë të drejtë.'),
(2723, 23, 50, 'Dhe, kemi bërë birin e Merjemës dhe nënën e tij – argument, dhe Ne i kemi vendosur ata, në një rrafshnaltë, ku mund të jetohet (me jetesë mirë) dhe që ka ujë rrjedhës.'),
(2724, 23, 51, 'O Pejgamberë! Hani nga të mirat dhe punoni vepra të mira! Unë me të vërtetë, e di mirë çka punoni ju!'),
(2725, 23, 52, 'Dhe, me të vërtetë, ky besim juaji – është i vetmi besim (unik); e, Unë jam Zoti juaj, andaj druani prej Meje (mos bëni keq)!'),
(2726, 23, 53, 'Ata, u ndanë në grupe – në çështje të fesë së tyre. Çdo grup ishte i kënaqur me atë që kishte,'),
(2727, 23, 54, 'andaj, lëri ti ata në humbjen e tyre deri në një kohë!'),
(2728, 23, 55, 'A mos mendojnë ata që Ne duke u ndihmuar atyre me pasuri dhe djem,'),
(2729, 23, 56, 'po nxitojmë kah të mirat e tyre?! Kurrsesi, por ata nuk vërejnë.'),
(2730, 23, 57, 'Me të vërtetë, ata që prej frikës së madhe ndaj Zotit të tyre, frikësohen (nga dënimi i Tij),'),
(2731, 23, 58, 'dhe ata që besojnë në argumentet e Zotit të tyre,'),
(2732, 23, 59, 'dhe ata që nuk i bëjnë shok Zotit të tyre,'),
(2733, 23, 60, 'dhe ata që japin nga ajo që u është dhënë atyre, e zemrat e tyre janë të frikësuara, meqë do të kthehen te Zoti i tyre, -'),
(2734, 23, 61, 'ata nxitojnë në vepra të mira dhe për to i kalojnë të tjerët.'),
(2735, 23, 62, 'Ne askë nuk e ngarkojmë përtej mundësive të tij; te Ne është Libri që flet të vërtetën, dhe askujt nuk do t’i bëhet padrejtësi.'),
(2736, 23, 63, 'Por, zemrat e mohuesve janë indiferente ndaj kësaj (Kur’anit), e ata kanë punë tjera më të këqia se këto që i punojnë (vazhdimisht).'),
(2737, 23, 64, 'E, kur filluam t’i dënojmë pasanikët e tyre, ata atëherë vajtonin (duke kërkuar ndihmë).'),
(2738, 23, 65, '(U thuhet): “Mos vajtoni sot, nuk do të keni ju ndihmë prej Nesh,'),
(2739, 23, 66, 'juve, madje, u janë lexuar ajetet e Mia, por ju tërhiqeshit mbrapa,'),
(2740, 23, 67, 'duke u mendjemadhësuar me të (me tempullin e Qabës), duke biseduar natën fjalë të shëmtuara”.'),
(2741, 23, 68, 'Vallë, a nuk kanë menduar ata për Fjalën (Kur’anin), apo tyre u ka ardhur ajo çka nuk u kishte ardhur të parëve të tyre?'),
(2742, 23, 69, 'Apo ata nuk e kanë njohur Pejgamberin e tyre, andaj e mohojnë atë?'),
(2743, 23, 70, 'Apo thonë: “Tek ai ka çmendje”. Jo, por ai ua sjell të Vërtetën atyre, e shumica prej tyre e urrejnë të Vërtetën.'),
(2744, 23, 71, 'E, sikur e Vërteta (Kur’ani) të pasonte sipas dëshirave të tyre, do të shkatërroheshin qiejt dhe Toka dhe gjithçka që gjendet në to. Jo, Na, atyre – u kemi dërguar përkujtimin (Kur’anin), pro ata shmangen nga ai.'),
(2745, 23, 72, 'Apo, prej tyre kërkon shpërblimin? E, shpërblimi i Zotit tënd ëshë më i miri, dhe Ai është furnizuesi më i mirë.'),
(2746, 23, 73, 'Ti, me të vërtetë, i fton ata në rrugën e drejtë.'),
(2747, 23, 74, 'E, ata që nuk besojnë jetën tjetër, shmangen nga rruga e drejtë.'),
(2748, 23, 75, 'Sikur Ne – t’i mëshironim ata dhe t’u largonim dëmet (që janë goditur me to), përsëri ata do të bredhnin të hutuar e të humbur.'),
(2749, 23, 76, 'Na, madje, i kemi dënuar ata, por ata nuk iu përulën Zotit të tyre, e as nuk i drejtuan lutje.'),
(2750, 23, 77, 'E, kur ua hapëm atyre, derën e dënimit të madh, ata, atëherë ishin të dëshpëruar.'),
(2751, 23, 78, 'Dhe, Ai është, që u ka krijuar të dëgjuarit, të pamurit dhe zemrën (mendjen). E, sa pak që falenderoheni ju!'),
(2752, 23, 79, 'Dhe, Ai është, që u ka krijuar juve në Tokë, dhe tek Ai do të tuboheni ju,'),
(2753, 23, 80, 'dhe Ai është, që jep jetë dhe vdekje, dhe Atij i përket ndërrimi i ditës dhe i natës, e vallë, a nuk po kuptoni ju?'),
(2754, 23, 81, 'Jo, ata (arabët), thonë – ashtu siç kanë thënë të mëparshmit.'),
(2755, 23, 82, 'Ata pyesin: “Vallë, pasi të vdesim e të bëhemi eshtra e dhé, vërtetë, do të ringjallemi?'),
(2756, 23, 83, 'Me të vërtetë, kjo na është premtuar neve dhe të parëve tanë (para Muhammedit), por këto janë vetëm përralla të popujve të lashtë”.'),
(2757, 23, 84, 'Thuaj: “E kujt është Toka dhe të gjitha ato që gjenden në të, nëse e dini?”'),
(2758, 23, 85, 'Ata thonë: “Të Perëndisë!” E ti, thuaj: “Vallë, a nuk po meditoni ju?”'),
(2759, 23, 86, 'Thuaj: “Kush është Zot’i shtatë qiejve dhe Zot i Arshit të Madh?”'),
(2760, 23, 87, 'Ata thonë: “Të Perëndisë!” E ti, thuaj: “Vallë, a nuk po druani ju?”'),
(2761, 23, 88, 'Thuaj: “Në dorën e kujt është pushteti mbi çdo gjë? E, Ai mbron, por prej Tij nuk mund të mbrohet askush, nëse e dini?”'),
(2762, 23, 89, 'Ata thonë: “I Perëndisë (është pushteti)”. E, ti thuaj: “E përse jeni të mashtruar?”'),
(2763, 23, 90, 'Jo, Na u kemi sjellë atyre të Vërtetën, por ata, vërtetë janë gënjeshtarë:'),
(2764, 23, 91, 'Perëndia nuk ka marrë për Vete fëmijë, dhe s’ka me te kurrfarë zoti (sikur të kishte) atëherë çdo zot do të merrte atë që ka krijuar dhe do të ngritej njëri mbi tjetrin. Qoftë i pastër Perëndia nga ajo që ia përshkruajnë Atij,'),
(2765, 23, 92, 'i cili di të padukshmen dhe Ai është shumë lart mbi ato, që ia bëjnë shok Atij!'),
(2766, 23, 93, 'Thuaj (o Muhammed!): “O Zoti im, nëse më tregon atë që u ke premtuar atyre (dënimin),'),
(2767, 23, 94, 'atëherë, o Zoti im, mos më lë mua me popullin zullumqarë!”'),
(2768, 23, 95, 'Dhe, me të vërtetë, Na jemi të fuqishëm që të tregojmë ty – atë që ua kemi premtuar atyre.'),
(2769, 23, 96, 'Largoje të keqen me atë që është më së miri. Na, më së miri dimë të vërtetën që përshkruajnë ata.'),
(2770, 23, 97, 'Dhe thuaj: “O Zoti im, te Ti mbështetem për mbrojtje nga nxitjet e djallit,'),
(2771, 23, 98, 'dhe te Ti mbështetem, o Zoti im (për mbrojtje), nga prezenca e tyre (djajve) pranë meje”.'),
(2772, 23, 99, 'Kur t’i arrijë vdekja ndonjërit prej tyre (mohuesve), ai thotë: “O Zoti im, më kthe (në këtë Botë),'),
(2773, 23, 100, 'që të punoj më mirë në ato që i kam lënë!” – Kurrsesi! Me të vërtetë, kjo është ajo fjalë të cilën ai kot e thotë, - para tyre do të ketë barrierë deri në ditën kur do të ringjallen.'),
(2774, 23, 101, 'E, kur të fryhet në Sur, atëherë, në mes tyre – nuk do të ketë lidhje farefisnore, e as që ata do të pyeten në mes vete.'),
(2775, 23, 102, 'Atij që i rëndohen terezitë (me vepra të mira), këta janë të shpëtuar,'),
(2776, 23, 103, 'e, ata – terezitë e të cilëve janë të lehta (prej të mirave të pakëta), këta janë ata, që i kanë bërë dëm vetes; e do të qëndrojnë përherë në xhehennem.'),
(2777, 23, 104, 'Zjarri do t’i kaplojë fytyrat e tyre, dhe ata, në të, janë të shëmtuar.'),
(2778, 23, 105, '(Ju thuhet atyre): “Vallë, a nuk u janë lexuar juve ajetet Tona, e ju i keni përgënjeshtruar”.'),
(2779, 23, 106, 'Do të thonë ata: “O Zoti ynë, neve na ka munduar e keqja jonë, andaj jemi bërë popull i humbur.'),
(2780, 23, 107, 'O Zoti ynë, nxirrna prej tij (zjarrit); e nëse na kthehemi (në mohim), (atëherë) do të jemi zullumqarë”.'),
(2781, 23, 108, '(Perëndia) do të thotë: “Rrini në të (zjarr) të përbuzur dhe mos Më flisni asgjë!”'),
(2782, 23, 109, 'Ka qenë një grup i robërve të Mi, i cili thoshte: “O Zoti ynë, na kemi besuar, andaj na falë dhe na mëshiro! E, Ti je më i miri mëshirues!” –'),
(2783, 23, 110, 'e ju i keni marrë ata për (objekt) talljeje, andaj, për këtë shkak, Mua, më harruat së përmenduri, e përherë i përqeshnit ata.'),
(2784, 23, 111, 'Unë, i kam shpërblyer ata sot, për durueshmërinë e tyre, e ata, me të vërtetë, janë të shpëtuar.'),
(2785, 23, 112, '(Engjëlli) do të thotë: “Sa vjet i keni kaluar në Tokë (botë)?”'),
(2786, 23, 113, 'Do të pëgjigjen ata: “Kemi qëndruar një ditë apo një pjesë të ditës; - pyetni numruesit (ata që i kanë numruar)?”'),
(2787, 23, 114, 'Do të thotë (Perëndia): “Keni qëndruar shumë pak. E, sikur të kishit ditur!”'),
(2788, 23, 115, 'Mos vallë, keni menduar ju, që Ne ju kemi krijuar juve kot dhe që nuk do të ktheheni te Ne?”'),
(2789, 23, 116, 'I lartësuar është Perëndia, sundimtari i Vërtetë! S’ka zot tjetër përpos Tij, Zotit të Arshit (Kozmosit) madhështor!'),
(2790, 23, 117, 'Ai që pos Perëndisë, adhuron zot tjetër, duke mos pasur kurrfarë dokumenti për këtë, me siguri – llogaria është vetëm te Zoti tij. Me të vërtetë, nuk ka shpëtim për mohuesit.'),
(2791, 23, 118, 'Dhe, thuaj (o Muhammed!): “O Zoti im, fal dhe mëshiro, e Ti je më i miri mëshirues!”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, '(Ky është) kapitulli, të cilin Ne e kemi shpallur dhe caktuar (dispozitë). Dhe, kemi shpallur në të argumentet e qarta për t’u këshilluar ju.'),
(2793, 24, 2, 'Kurvën dhe kurvarin rr5ihnie çdonjërin prej tyre me nga njëqind goditje. Mos t’ju kaplojë juve ndaj tyre kurrfarë mëshire (në kryerjen) e dispozitave fetare, nëse besoni Perëndinë le të prezentojnë në dënimin e tyre një grup besimtarësh.'),
(2794, 24, 3, 'Kurvari mund të martohet vetëm me kurvën apo idhujtaren, kurse kurvën mund ta marrë për grua vetëm kurvari apo idhujtari, e kjo (kurvëria – martesa me kurvat), është e ndaluar për besimtarët.'),
(2795, 24, 4, 'Ata që i fajësojnë gratë e ndershme (për kurvëri), e nuk i sjellin katër dëshmitarë, rrihnie me tetëdhjetë goditje dhe mos pranoni më kurrë të dëshmuarit e tyre, këta janë njerëz ngatërrestarë,'),
(2796, 24, 5, 'pos atyre, që pas kësaj pendohen dhe përmirëzohen. Me të vërtetë Perëndia është falës dhe mëshirues.'),
(2797, 24, 6, 'E, ata që i fajësojnë gratë e tyre (për kurvëri), duke mos pasur dëshmitar përpos vetes, do të vërtetojnë dëshminë e tyre me betim në Perëndinë – katër herë, që me siguri thotë të vërtetën.'),
(2798, 24, 7, '(Betimi) i pestë është të ndjellë mallimin e Perëndisë ndaj vetes, nëse ai ka gënjyer.'),
(2799, 24, 8, 'E, ajo do të kursehet nga dënimi, nëse katër herë betohet në Perëndinë, se ai (burri i saj), me të vërtetë, është, nga gënjeshtarët,'),
(2800, 24, 9, 'e (betimi) i pestë është të ndjellë zemrimin e Perëndisë ndaj vetes, nëse ai (burri i saj) ka thënë të vërtetën.'),
(2801, 24, 10, 'Dhe sikur të mos ishte mirësia e Perëndisë ndaj jush dhe mëshira e Tij, dhe Perëndia (të mos ishte) Ai që pranon pendimin dhe i Gjithëdijshëm, (do t’ia shpejtonte dënimin mëkatarit – me demaskimin e tij).'),
(2802, 24, 11, 'Me të vërtetë, ata që bënë shpifje (ndaj bashkëshortes së Muhammedit), janë një grup prej jush. Mos e konsideroni këtë (shpifje), të keqe për ju. Jo, kjo është mirë për ju. Çdonjëri prej tyre, do të dënohet për atë që ka bërë, e atë, prej tyre që ka bërë më së shumti, e pret dënimi i madh.'),
(2803, 24, 12, 'Përse, - me të dëgjuar këtë (shpifje) – besimtarët dhe besimtaret nuk menduan mirë për njëri-tjetrin dhe të thonë: “Kjo është shpifje e hapët!”'),
(2804, 24, 13, 'Pse nuk sollën për këtë katër dëshmitarë? Meqë nuk sollën dëshmitarë, ata (atëherë), te Perëndia janë gënjeshtarë.'),
(2805, 24, 14, 'Dhe, sikur të mos ishte mirësia e Perëndisë ndaj jush dhe mëshira e Tij, në këtë botë dhe në tjetrën, me siguri do t’ju godiste dënimi i madh, për atë që u lëshuat,'),
(2806, 24, 15, 'kur atë (shpifjen) e përcillnit me gjuhët tuaja dhe flisnit me gojët tuaja atë për të cilën nuk kishit dijeni, ju këtë e merrnit për cikrrim, por te Perëndia – kjo është (çështje) e madhe.'),
(2807, 24, 16, 'Përse – kur e dëgjuat (shpifjen) – nuk thatë: “Nuk është për ne – të flasim për këtë”. Ti je i lavdëruar (Zot)! Kjo është shpifje e madhe!'),
(2808, 24, 17, 'Perëndia ju këshillon (duke ua tërhequr vërejtjen), që të mos përsëritni kurrë më diçka të ngjashme, nëse jeni besimtarë,'),
(2809, 24, 18, 'dhe, Perëndia ju shpjegon argumentet; dhe Perëndia është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(2810, 24, 19, 'Ata që duan të përhapin lajmet e turpshme për besimtarët, i pret dënimi i dhembshëm në këtë botë dhe në tjetrën; Perëndia i di të gjitha, e ju nu i dini.'),
(2811, 24, 20, 'Dhe sikur të mos ishte mirësia e Perëndisë ndaj jush dhe mëshira e Tij, (dhe sikur të mos ishte) Perëndia i butë dhe mëshirues (do t’jua shpejgonte dënimin)...'),
(2812, 24, 21, 'O besimtarë! Mos ndiqni gjurmët e djallit! E, kush ndjek gjurmët e djallit (do të dënohet, sepse) ai, me të vërtetë urdhëron imoralitet dhe vepra të shëmtuara. Dhe sikur të mos ishte mirësia e Perëndisë ndaj jush dhe mëshira e Tij, kurrë nuk do të ishte pastruar prej mëkateve askush prej jush; por, Perëndia e pastron atë që don Ai. Perëndia, i dëgjon të gjitha dhe i di të gjitha.'),
(2813, 24, 22, 'Dhe, le të mos betohen të ndershmit dhe të pasurit prej jush – se nuk do t’u japin të afërmve, të varfërve dhe të emigruarve në rrugën e Perëndisë; le të falin ata dhe le të mos brigojnë (mos të mbajnë inat)! Vallë, a nuk doni ju që t’ju falë Perëndia? E, Perëndia, është falës e mëshirues.'),
(2814, 24, 23, 'Me të vërtetë, ata që i fajësojnë gratë e ndershme, zemërdlirë, besimtare, janë të mallkuar në këtë botë dhe në tjetrën, dhe për ta ka dënim të madh.'),
(2815, 24, 24, 'Atë Ditë që do të dëshmojnë kundër tyre – gjuhët e tyre, dhe duart e tyre, dhe këmbët e tyre për atë që kanë punuar ata;'),
(2816, 24, 25, 'atë Ditë Perëndia do t’ju plotësojë dënimin e merituar dhe do ta dijnë ata, se Perëndia, është e vërteta e qartë.'),
(2817, 24, 26, 'Gratë e pandershme janë për meshkuj të pandershëm; e burrat e pandershëm, janë për femra të pandershme. Gratë e ndershme janë për meshkuj të ndershëm; e burrat e ndershëm janë për femra të ndershme. Ata të ndershmit, janë larg prej atyre thënieve; atyre (të ndershmëve) ju përket falja e mëkateve, dhe dhuntia e madhe.'),
(2818, 24, 27, 'O besimtarë! Mos hyni në shtëpitë e huaja përderisa të mos lajmëroheni e të mos i përshëndetni banuesit e saj. Kjo është më mirë, që ju të këshilloheni!'),
(2819, 24, 28, 'E, nëse në to nuk gjeni askë, mos hyni në to, për – derisa të mos u lejohet, e nëse u thuhet juve: “Kthehuni” ju kthehuni! Kjo është më e mirë për ju, e Perëndia di atë që punoni ju.'),
(2820, 24, 29, 'Nuk ka mëkat për ju nëse hyni (pa leje) në godinat e pabanuara, në të cilat gjenden gjërat tuaja. Perëndia di ç’tregoni ju haptazi e çka mëshihni.'),
(2821, 24, 30, 'Thuaju besimtarëve që të mos hedhin shikimet e tyre dhe le t’i ruajnë organet e veta gjenitale; kjo është më mirë për ta. Me të vërtetë, Perëndia është i dijshëm për atë që punojnë ata.'),
(2822, 24, 31, 'Dhe, thuaju besimtareve, që t’i shmangin shikimet e tyre dhe le t’i ruajnë organet e veta gjenitale; dhe le të mos tregojnë nga stolitë e tyre (pjesët e trupit), përveç atyre që janë të dukshme (të domosdoshme). Dhe, le të lëshojnë pelerinën e vet në anët e (trupit të) tyre dhe le t’u mos tregojnë stolitë e tyre, përpos: bashkëshortëve të tyre, ose baballarëve të tyre, ose vjehrrive të tyre, ose bijve të tyre, ose djemve të bashkëshortëve të tyre, ose vëllezërve të tyre, ose djemve të vëllezërve të tyre, ose djemve të motrave të tyre ose grave (mikeshave) të tyre, ose atyre që i kanë nën pushtet (si skllavët), ose atyre që u shkojnë pas (për të arritur lëmoshë) e që nuk kanë pretendime tjera ndaj femrave. E këta janë meshkujt apo fëmijët që nuk i dinë ngacmimet e femrave. Dhe mos t’i rrahin këmbët e tyre (për tokë) që të hetohen stolitë e tyre të fshehta. Të gjithë ju, o besimtarë, pendohuni te Perëndia, që të arrini shpëtimin!'),
(2823, 24, 32, 'Martoni (mos pengoni) të pamartuarit dhe të pamartuaret, edhe skllavët dhe skllavet tuaja – besimtarë të ndershëm. Nëse janë të varfër, Perëndia do t’i begatojë ata me dhuntinë e Tij; e Perëndia është i gjerë në mirësi dhe di çgo gjë.'),
(2824, 24, 33, 'Dhe, le të frenojnë veten ata që nuk gjejnë mundësi për martesë, përderisa Perëndia të mos i begatojë ata me dhuntinë e Tij. E, ata që gjenden në pushtetin tuaj (skllavët dhe skllavet) që kërkojnë të lirohen (duke e kompensuar veten) shkruani (lidhni kontratën), nëse dini që janë të mirë. Dhe, epjuni diçka nga pasuria juaj, me të cilën ju ka dhuruar Perëndia juve. Dhe mos i detyroni skllavet tuaja në kurvëri, nëse ato dëshirojnë jetë të ndershme – për të arritur ju dobi të përkohshme të kësaj bote. E, kush i detyron ato – me të vërtetë, Perëndia pasi janë detyruar ato (e janë penduar), është falës dhe mëshirues.'),
(2825, 24, 34, 'Na u kemi shpallur juve ajetet e qarta dhe shembujt nga jeta e atyre që kanë kaluar para jush dhe këshillë për ata që i druajnë Perëndisë.'),
(2826, 24, 35, 'Perëndia është ndriçues (dirigjues e begatues) i qiejve dhe i Tokës. Shembulli i dritës së Perëndisë është si dritarja (në mur), në të cilën është feneri, - e feneri është në një xham, e ky xhami është si yll i shndritshëm, i cili ndizet nga druri i bekuar i ullirit, as i lindjes as i perëndimit, vaj i të cilit gati shkëlqen edhe pa e prekur zjarri; (kjo është) dritë mbi dritat. Perëndia e udhëzon në dritën e Vet kë të dojë dhe Perëndia shpjegon shembuj për njerëzit. Perëndia është i dijshëm për çdo gjë.'),
(2827, 24, 36, 'Në xhamitë, që u ngritën me urdhërin e Perëndisë dhe në të cilat përmendet emri i Tij – Atë e lavdërojnë, në mëngjes dhe mbrëmje,'),
(2828, 24, 37, 'njerëzit, të cilët shitblerja nuk i pengon në të përmendurit e Perëndisë dhe të cilët bëjnë namazin dhe japin zeqatin, dhe të cilët druajnë nga Dita, në të cilën zemrat dh shikimet janë të shqetësuara,'),
(2829, 24, 38, '(e lavdërojnë Perëndinë) që Perëndia t’i shpërblejë ata me dhuratat më të mira për atë që kanë punuar, e që t’jua shtojë atyre dhuntinë e Tij. E, Perëndia e furnizon kë të dojë, pa llogari.'),
(2830, 24, 39, 'E, veprat e atyre që nuk besojnë, janë si mjegullirë (fatamorgana) në rrafshirë, të cilën, i etshmi e konsideron ujë, por kur të arrijë tek ai vend, nuk gjen asgjë, - e do të hasë në dënimin e Perëndisë dhe Ai ia paguan llogarinë e tij, se Perëndia është llogaritës i shpejtë,'),
(2831, 24, 40, 'ose (veprat e tyre) jnë si errësirat në detin e thellë, të cilat i mbulon dallga pas dallgës, mbi të cilat gjenden rëtë, errësira – njëra mbi tjetrën, aq sa dorën e vet (kur ta zgjatë njeriu) gati mos ta shohë. Kujt mos t’i japë Perëndia dritë (udhëzim), nuk do të ketë (kurrfarë) dritë për të.'),
(2832, 24, 41, 'A nuk e vëren ti, se – Perëndinë, me të vërtetë, e lavdërojnë (të gjithë) ata që gjenden në qiej dhe në Tokë, e madje edhe shpezët duke i hapur krahët e tyre, gjatë fluturimit, Ai (Perëndia) di lutjen dhe lavdërimin e çdonjërit (nga ata). Perëndia, di për atë që punojnë ata.'),
(2833, 24, 42, 'Vetëm të Perëndisë janë pushteti i qiejve dhe i Tokës dhe tek Ai kthehet çdo gjë.'),
(2834, 24, 43, 'A nuk e vëren ti, se Perëndia i shtyan ngadalë retë, e pastaj i bashkon e i grumbullon njërën mbi tjetrën, e ti sheh shiun se si del nga mesi i tyre dhe bie nga qielli; Ai, prej reve të mëdha si mali, lëshon breshërin, e me të e godit kë të dojë, kurse e shmangë prej kujt të dojë. Shkëlqimi i vetëtimës së tij – gati t’i merr sytë.'),
(2835, 24, 44, 'Ai (Perëndia) e sjell ditën dhe natën (ndërrimin dhe zgjatjen e tyre). Me të vërtetë, në këtë – ka këshillë për ata që janë largpamës.'),
(2836, 24, 45, 'Perëndia ka krijuar çdo lloj shtaze prej ujit: disa nga ato ecin me barkun e tyre, kurse një pjesë ecin në dy këmbë, e disa prej tyre në katër këmbë. Perëndia krijon çka të dojë. Me të vërtetë, Perëndia është i pushtetshëm për çdo gjë.'),
(2837, 24, 46, 'Na – madje, i kemi shpallur ajetet e qarta; e Perëndia e udhëzon kë të dojë në rrugën e drejtë.'),
(2838, 24, 47, 'E, hipokritët thonë: “Na kemi besuar Perëndinë dhe Pejgamberin dhe kemi qenë të përulur”. E, pastaj, u shmangën disa prej tyre (pasi e thanë këtë). E, këta (në të vërtetë), nuk janë besimtarë.'),
(2839, 24, 48, 'Dhe, kur të thirrën te Perëndia dhe Pejgamberi i Tij, që t’i gjykojë në mes tyre, një grup nga ata shmangët;'),
(2840, 24, 49, 'vetëm nëse dinë që drejtësia është në anën e tyre, ata vijnë përulshëm (me shpëjtim).'),
(2841, 24, 50, 'A mos është në zemrat e tyre sëmundja (hipokrizia), apo dyshojnë, apo druajnë se Perëndia dhe Pejgamberi i Tij do t’u bëjnë padrejtësi? Jo, por ata janë zullumqarë të vërtetë.'),
(2842, 24, 51, 'Vetëm është fjala e besimtarëve, kur ata të thirren te Perëndia dhe Pejgamberi i Tij, që t’i gjykojë në mes tyre, për të thonë: “Dëgjojmë dhe përulemi!” Dhe, këta me siguri janë të shpëtuar.'),
(2843, 24, 52, 'E, ata që i përulen Perëndisë dhe Pejgamberit të Tij dhe druajnë nga Perëndia dhe i frikohen Atij, vetëm ata janë të lumtur.'),
(2844, 24, 53, 'Ata (hipokritët) janë betuar në Perëndinë, me betimin më të madh, se, nëse ti (o Muhammed) i urdhëron ata, që të shkojnë në luftë – ata me siguri do të dalin. Thuaj: “Mos betoheni! (Preju jush) kërkohet përulja si duhet. Perëndia, me të vërtetë, është i dijshëm për atë që punoni ju”.'),
(2845, 24, 54, 'Thuaj: “Përuljuni Perëndisë dhe përuljuni Pejgamberit!” E, nëse shmangeni, vetëm (Muhammedi) është përgjegjës për atë që është i obliguar; e ju jeni përgjegjës për atë që jeni ngarkuar. E, nëse i përuleni atij, do të jeni në rrugën e drejtë; - e Pejgamberi vetëm është i detyruar që t’ju shpallë qartazi.'),
(2846, 24, 55, 'Perëndia, u ka premtuar disave prej jush, që kanë besuar dhe që punojnë vepra të mira – se do t’i bëjë sundimtarë në Tokë, ashtu, si i ka bërë ata para tyre dhe që do t’ua forcojë fenë e tyre, me të cilën Ai është i kënaqur, dhe që do t’ua shndërrojë atyre – pas frikës, sigurinë. Ata, më adhurojnë Mua, e nuk më bëjnë shok asgjë. E, kush bën mohim pas kësaj, ata janë ngatërrestarë të vërtetë.'),
(2847, 24, 56, 'Dhe, kryeni namazin, jepni zeqatin dhe përuljuni Pejgamberit, për të fituar mëshirën e Perëndisë!'),
(2848, 24, 57, 'Kurrsesi mos mendo, se mohuesit do ta bëjnë të pamundshëm (dënimin nga ana e Perëndisë) në Tokë! E, strehimi i tyre është zjarri, e sa vend kthimi i shëmtuar që është ai!'),
(2849, 24, 58, 'O besimtarë! Le të kërkojnë leje ata që janë nën pushtetin tuaj (në familje) dhe ata nga ju, të cilët nuk e kanë arritur moshën e pjekurisë në tri raste: para namazit të sabahit dhe kur i zhveshni rrobat tuaja në mesditë dhe pas namazit të jacisë. Këto janë tri kohë kontestuese (jo të përshtatshme). Nuk jeni as ju, as ata – mëkatarë, pas këtyre kohëve, (nëse hyni pa leje) duke u takuar njëri me tjetrin. Kështu, ua shpjegon Perëndia dispozitat juve! Perëndia, është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(2850, 24, 59, 'E, kur fëmijët tuaj të arrijnë moshën e pjekurisë, le të kërkojnë leje (në çdo kohë) ashtu siç kanë kërkuar leje ata para këtyre. Kështu, ua shpjegon Perëndia juve dispozitat e Tij! Perëndia, është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(2851, 24, 60, 'E, gratë e vjetra, të cilat më – nuk kanë për qëllim të martohen, nuk është mëkat ta heqin mbulesën (që kanë mbi rrobat e trupit), por jo – duke i treguar stolitë e tyre. E, nëse i përmbahen virtytësisë, është edhe më mirë për to. Perëndia, i dëgjon dhe i di të gjitha.'),
(2852, 24, 61, 'Nuk është mëkat për të verbërin, as nuk është mëkat për të çalin, as për të sëmurin s’ka mëkat, as për ju nuk është mëkat që të hani në shtëpinë tuaj (të fëmijëve tuaj), ose në shtëpitë e baballarëve tuaj, ose në shtëpitë e nënave tuaja, ose në shtëpitë e motrave tuaja, ose në shtëpitë e axhallarëve tuaja, ose në shtëpitë e hallave tuaja, ose në shtëpitë e dajëve tuaj, ose në shtëpitë e tezeve tuaja, ose tek ata çelësat e të cilëve u janë lanë juve (për ta ruajtur atë), ose (në shtëpi) te miqtë e sinqertë. Nuk jeni mëkatarë që të hani bashkarisht apo veçmas; e kur të hyni në shtëpi – ju përshëndeteni banuesit e saj me një përshëndetje nga Perëndia, të begatshme e të mirë. Kështu ua shpjegon Perëndia juve dispozitat, për të medituar ju!'),
(2853, 24, 62, 'Besimtarë të vërtetë janë vetëm ata që besojnë Perëndinë dhe Pejgamberin e Tij. E, kur të gjenden me te (Pejgamberin) në një punë të përbashkët, nuk largohen para se të kërkojnë leje prej tij. Me të vërtetë, ata që kërkojnë leje prej teje, padyshim, besojnë në Perëndinë dhe Pejgamberin e Tij. E, kur të kërkojnë ata leje prej teje, për ndonjë punë të tyre, jepju leje kujt ë duash prej tyre dhe kërko për ta falje nga Perëndia. Me të vërtetë, Perëndia është falës dhe mëshirues.'),
(2854, 24, 63, 'Mos e konsideroni thirrjen e Pejgamberit në mesin tuaj, si thirrje të rëndomtë që ju ia bëni njëri-tjetrit; Perëndia, me siguri, i di ata që bishtërojnë prej jush tinëzisht. Le të druajnë ata që i kundërshtojnë urdhërit të tij, që të mos i arrijë ndonjë sprovë ose që të mos i godas ata dënimi i dhembshëm.'),
(2855, 24, 64, 'E, me të vërtetë, të Perëndisë janë gjithçka gjendet në qiej dhe në Tokë. Ai, di se në çfarë gjendje jeni. Dhe – di Ditën kur ata do të kthehen tek Ai, e do t’i laj¬mërojë ata për veprat që kanë punuar. E, Perëndia, është i dijshëm për çdo gjë!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'I lartësuar është Ai, që i ka shpallur Kur’anin (i cili ndanë të vërtetën prej të pavërtetës) robit të Vet (Muhammedit), që të jetë paralajmërues për botërat (njerëzit),'),
(2857, 25, 2, 'Atij që i përket pushteti i qiejve dhe i Tokës, i cili nuk ka fëmijë dhe i cili nuk ka shok në pushtet dhe i cili ka krijuar çdo gjë, e i ka rregulluar si duhet ato!'),
(2858, 25, 3, 'E, ata, kanë marrë – pos Tij, zotëra, të cilët nuk krijojnë asgjë, por të cilët vetë janë të krijuar, të cilët nuk janë në gjendje të shmangin as dëmin prej vetes, as t’i sjellin dobi (vetes), as nuk janë në gjendje që të bien vdekjen as jetën, as që të ringjallin.'),
(2859, 25, 4, 'E, mohuesit, thonë: “Ky (Kur’ani) është vetëm gënjeshtër, të cilën e ka trilluar ai (Muhammedi), e i kanë ndihmuar në këtë (edhe) njerëz tjerë”. Ata, (me këtë) kanë bërë zullum dhe akuzë,'),
(2860, 25, 5, 'dhe thanë: “Këto janë përrallat e popujve të lashtë; të cilat i ka kopjuar, e i lexohen atij në mëngjes e mbrëmje.'),
(2861, 25, 6, 'Thuaj: “E ka shpallur (Kur’anin) Ai, i cili di fshehtësitë e qiejve dhe të Tokës; Ai falë shumë dhe është mëshirues”.'),
(2862, 25, 7, 'Dhe ata thonë: “Çka ka ky pejgamber? Ahan ushqimin dhe shëtitë nëpër tregje; sikur t’ju kishte dërguar atij një engjëll, e të bëhet me të paralajmërues,'),
(2863, 25, 8, 'ose t’ju kishte zbritur një thesar, ose të kishte një kopsht, nga i cili të hante ai?” Dhe, mohuesit thanë: “Ju ndiqni vetëm një njeri të magjepsur!”'),
(2864, 25, 9, 'Shiko çfarë shembujsh të sjellin ty (o Muhammed), e ata (me këtë), kanë humbur e nuk mund të gjejnë rrugën e vërtetë.'),
(2865, 25, 10, 'I lartësuar është Ai, i cili nëse don, do të japë më mirë nga ajo (që thanë), kopshte nëpër të cilat rrjedhin lumenjtë dhe do të bëjë për ty pallate.'),
(2866, 25, 11, 'Jo, (nuk të përgënjeshtrojnë vetëm ty), por, ata e konsiderojnë gënjeshtër edhe ardhjen e Ditës së Kijametit, e Na – për ata që e mohojnë Ditën e Kijametit, kemi përgatitur zjarrin e flakëruar.'),
(2867, 25, 12, 'Kur t’i vërejë ai (xhehennemi) prej një largësie (ata ia ndiejnë) gurgullimën dhe gërhamën,'),
(2868, 25, 13, 'e kur të hudhen në një vend të ngushtë të tij, të lidhur duarsh, e thirrin aty – shkatërrimin (e vet).'),
(2869, 25, 14, '“Mos thërritni sot një shkatërrim, (por) thërritni shumë shkatërrime!”'),
(2870, 25, 15, 'Thuaj: “A është më i mirë ky (shkatërrim) apo xhenneti i përhershëm, i cili u është premtuar besimtarëve? Për ta, (xhenneti) është shpërblim dhe vend kthimi.'),
(2871, 25, 16, 'Për ta ka në të, ç’të duan, duke qëndruar në të përherë. Ky është premtimi i Zotit tuaj, i kërkuar (prej jush)”.'),
(2872, 25, 17, 'Dhe, atë Ditë kur (Perëndia) i tubon ata (idhujtarët) dhe ata që i adhuronin ata, pos Perëndisë, e u thotë atyre: “A jeni ju ata që i keni shmangur këta robër të Mi, apo ata vetë kanë humbur rrugën e drejtë?” –'),
(2873, 25, 18, 'ata thanë: “Lavdi qoftë për Ty! Nuk është e mundshme që ne të marrim mbrojtës tjetër, përpos Teje, por Ti u ke dhënë këtyre dhe të parëve të tyre – jetë të begatshme, e ata kanë harruar këshillimin. Dhe, ata kanë qenë popull i shkatërruar!”'),
(2874, 25, 19, 'Ata, e konsiderojnë për gënjeshtër atë që flitni ju, e nuk do të mundeni të shmangëni (prej vetes dënimin), as t’i ndihmoni vetes. E, kush prej jush bën zullum, Na, atij do t’i japim të shijojë dënimin e madh.'),
(2875, 25, 20, 'E, Na – edhe para teje kemi dërguar vetëm pejgamberë, të cilët hanin ushqimin dhe shetisnin nëpër tregje, dhe Ne – kemi bërë, që disa prej jush, të jenë sprovë për të tjerët. A do të jeni të durueshëm ju e duroni! E, Zoti yt i sheh të gjitha.'),
(2876, 25, 21, 'Ata që nuk i druajnë takimit me Ne (ringjalljes), thonë: “Pse nuk na dërgohen neve engjëjt? Ose të shohim Zotin tonë!” Ata, me të vërtetë, janë kryelartësuar në vetvete, e kanë kaluar në zullum të madh.'),
(2877, 25, 22, 'Atë ditë kur t’i shohin engjëjt, për mohuesin nuk ka sihariq dhe thonë: “Ruana o Zot, neve!” (prej engjëjve)'),
(2878, 25, 23, 'Dhe, Ne – do t’i qasemi veprës që kanë punuar ata dhe do ta shndërrojmë atë në pluhur të shpërndarë.'),
(2879, 25, 24, 'Banorët e xhennetit, atëherë, do të kenë vendbanim më të mirë dhe pushimore më të bukur.'),
(2880, 25, 25, '(Kujtoju atyre) Ditën kur qielli çahet me ré e engjëjt do të lëshohen (prej qiellit) në grupe-grupe,'),
(2881, 25, 26, 'atë ditë, pushteti i vërtetë, do të jetë vetëm për Zotin Mëshirues, e kjo do të jetë ditë e mundimshme për mohuesit.'),
(2882, 25, 27, 'Dhe, atë Ditë zullumqari do t’i hajë gishtat e duarve të veta, e thotë: “Ah! Ta kisha marrë rrugën (e drejtë) me Pejgamberin!'),
(2883, 25, 28, 'Ah! Sikur të mos e kisha bërë atë apo këtë mik!'),
(2884, 25, 29, 'Ai, më ka zmbrapsur nga Kur’ani që më ka ardhur mua!” – e djalli (po ashtu edhe njeriu i keq), e lë gjithmonë njeriun në poshtërim.'),
(2885, 25, 30, 'Dhe, Pejgamberi (Muhammed) tha: “O Zoti im! Me të vërtetë, populli im (po) e merr këtë Kur’an, si (diçka) të urrejtur”.'),
(2886, 25, 31, 'Dhe, kështu Na, çdo pejgamberi i kemi kundërvënë armiqët mohues. E, ty të mjafton Zoti yt – udhërrëfyes dhe ndihmës!'),
(2887, 25, 32, 'E, mohuesit thonë: “Përse Kur’ani nuk është shpallur në tërësi – përnjëherë!” Kështu, (ta kemi shpallur pjesë-pjesë), për ta forcuar ty – me te, zemrën tënde dhe ta kemi lexuar qartazi.'),
(2888, 25, 33, 'Dhe ata – nuk do të sjellin ty kurrfarë shembulli (për të kundërshtuar ty), e që Ne mos ta sjellim të Vërtetën dhe komentimin më të mirë.'),
(2889, 25, 34, 'Këta (mohues), të cilët do të zhvarriten me fytyrat e tyre kah skëterra, këta janë ata më të këqinjtë për nga pozita (e tyre) dhe më së largu prej rrugës së vërtetë.'),
(2890, 25, 35, 'Dhe Ne, me të vërtetë, ia dhamë Musait Librin dhe ia bëmë ndihmës vëllain e tij – Harunin.'),
(2891, 25, 36, 'E Ne, u thamë atyre: “Shkoni (ju dy) te populli, i cili i ka përgënjeshtruar argumentet Tona, e pastaj, e kemi shkatërruar atë popull krejtësisht.'),
(2892, 25, 37, '(Dhe kujtoje) popullin e Nuhut – kur i quajti gësnjeshtarë pejgamberët, i fundosëm dhe njerëzve ua bëmë këshillë (mësim), e zullumqarëve u kemi përgatitur dënim të dhembshëm.'),
(2893, 25, 38, '(Dhe, i kemi shkatërruar) Adin dhe Themudin, dhe popullin e Re’ssit, si dhe shumë gjenerata në mes tyre.'),
(2894, 25, 39, 'E, të gjithëve, u kemi sjellë shembuj dhe të gjithë – i kemi shkatërruar krejtësisht.'),
(2895, 25, 40, 'Dhe, ata në të vërtetë, kanë kaluar pranë vendbanimit, që është shkatërruar me shi të tmerrshëm, e vallë, a nuk e panë atë? Jo, ata nuk i frikohen ringjalljes.'),
(2896, 25, 41, 'E, kur të shohin ty, të marrin vetëm për (objekt) talljeje: “Vallë, a këtë e ka dërguar Perëndia pejgamber?'),
(2897, 25, 42, 'Për pak, qe duke na shmangur (larguar) nga hyjnitë tona, por ne u qëndruam besnikë atyre!” Ata do ta dinë – kur të shohin dënimin – se kush është shmangur më larg rrugës së drejtë.'),
(2898, 25, 43, 'A e ke parë ti atë, që epshin e vet e ka marrë për hyjni? A don t’i bëhesh mbrojtës atij?'),
(2899, 25, 44, 'A, emdpm to se shumica e tyre dëgjojnë dhe kuptojnë? Ata janë vetëm si shtazë. Jo, ata janë (edhe më të këqinj, meqë janë) të humbur edhe më shumë.'),
(2900, 25, 45, 'A nuk e ke parë ti (pushtetin) e Zotit tënd, si e ka zgjatur hijen, e sikur të kishte dashur, do ta lënte të pandryshueshme, dhe – e kemi bërë Diellin tregues të saj,'),
(2901, 25, 46, 'pastaj, e kemi tërhequr atë, dalëngadalë – pranë Vetes.'),
(2902, 25, 47, 'Dhe, Ai është, që ua ka bërë natën mbulesë dhe gjumin pushim, e ditën – të lëvizni.'),
(2903, 25, 48, 'Dhe ai, është, që dërgon erërat si sihariq para mëshirës së tij (sjelljen e shiut). Dhe, kemi lëshur prej qiellit ujë të pastër,'),
(2904, 25, 49, 'që me atë ta ngjallim visin e vdekur, dhe t’u japim ujë kafshëve dhe shumë njerëzve, që i kemi krijuar.'),
(2905, 25, 50, 'Dhe, Ne, e kemi lëshuar (shiun) në mesin e tyre, për të medituar (dhuntinë e Perëndisë), por shumica e njerëzve i refuzojnë (të gjitha), përveç mohimit.'),
(2906, 25, 51, 'Dhe, sikur të donim Ne, do të dërgonim në çdo qytet paralajmërues.'),
(2907, 25, 52, 'E, mos i dëgjo mohuesit dhe luftoi ata me Kur’an, në një luftë të madhe.'),
(2908, 25, 53, 'Dhe Ai është, që u ka dhënë rrjedhje të lirë dy sipërfaqeve ujore (det e lum i madh). Njëri është i pijshëm, i ëmbël; e tjetri është i njelmët, i idhët. Dhe, ka ngritur pengesa ndërmjet tyre (rryp toke).'),
(2909, 25, 54, 'Dhe, Ai është që ka krijuar prej ujit njeriun, e ka bërë (në mes njerëzish) farefisni (përkah gjaku), dhe miqëi (përkah martesa). Dhe, Zoti yt, është i Plotëfuqishëm.'),
(2910, 25, 55, 'Dhe, ata adhurojnë, pos Perëndisë atë që nuk mund t’u sjellë as dobi as dëm, e mohuesi është ndihmës (i djallit) kundër Zotit të tij,'),
(2911, 25, 56, 'e Ne, të kemi dërguar ty, vetëm sihariques (për vepra të mira) dhe paralajmërues.'),
(2912, 25, 57, 'Thuaj: “Unë nuk kërkoj nga ju kurrfarë shpërblimi, por vetëm ai që don, le të shkojë rrugës që shpie te Zoti i tij”.'),
(2913, 25, 58, 'Mbështetu ti – në të Gjallin (Perëndinë), i cili nuk vdes, dhe lavdëroje (Atë) – duke i bërë falenderime Atij! Ai i di mjaftë mirë mëkatet e robërve të Vet.'),
(2914, 25, 59, 'Ai, i cili krijoi qiejt dhe Tokën dhe çdo gjë që gjendet në mes tyre – për gjashtë ditë (periudha kohore – gjeologjike), e pastaj mbisundoi Arshin (Gjithësinë). Ai është Mëshiruesi dhe pyete për Ate – atë që di.'),
(2915, 25, 60, 'E, kur t’u thuhet atyre: “Bëni sexhde Bamirësit të Gjithëmbarshëm!” Ata i thonë: “E, çka është Ai Bamirësi i Përgjithshëm. Vallë, t’i bëjmë sexhde atij që na urdhëron ti?” E, (kjo) ua shton atyre largimin (prej fesë).'),
(2916, 25, 61, 'I lartësuar është Ai, i cili ka krijuar hyllësitë në qiell, dhe në të ka krijuar Kandilin (Diellin) e ndritshëm dhe Hënën që shkëlqen.'),
(2917, 25, 62, 'Dhe Ai, krijoi natën dhe ditën që të vijnë njëra pas tjetrës, këshillë për ta që duan ta kujtojnë (Perëndinë), ose ata që duan ta falenderojnë (Ate).'),
(2918, 25, 63, 'E, robërit e Bamirësit të Gjithëmbarshëm janë ata, të cilët ecin thjeshtë nëpër Tokë. E, kur xhahilat i sulmojnë këta, me fjalë – përgjigjen: “Qetësia qoftë!”'),
(2919, 25, 64, 'Dhe ata që e kalojnë natën, duke i bërë sexhde Zotit të tyre, dhe duke ndenjur në këmbë,'),
(2920, 25, 65, 'dhe ata që thonë: “O Zoti ynë, largoje prej nesh mundimin e xhehennemit! Sepse, me të vërtetë, dënimi i tij (zjarrit), është shkatërrim i paevitueshëm,'),
(2921, 25, 66, 'me të vërtetë, ai (xhehennemi), është vendbanim dhe vendstrehim i shëmtuar'),
(2922, 25, 67, 'edhe ata që, kur ndajnë (nga pasuria e tyre) – as nuk shkapërderdhin, as nuk koprracojnë, por që i përmbahen mesatares;'),
(2923, 25, 68, 'edhe ata që pos Perëndisë, nuk adhurojnë zot tjetër, dhe të cilët, nuk vrasin njeri që e ka ndaluar Perëndia, përveç sipas ligjit (që ka merituar dënimin), dhe që nuk bëjnë kurvëri, - e kush i bën këto do t’i takojë dënimi i mëkatit,'),
(2924, 25, 69, 'i cili (dënim), do t’i dyfishohet në Ditën e Kijametit dhe ai do të qëndrojë përherë në të (dënim), i poshtëruar,'),
(2925, 25, 70, 'përveç atij që pendohet, dhe beson e punon vepra të mira. Këtyre, Perëndia, ua ndërron veprat e këqia në të mira. E, Perëndia është falës e mëshirues.'),
(2926, 25, 71, 'E, ai që pendohet dhe punon vepër të mirë, është i kthyer krejtësisht kah Perëndia;'),
(2927, 25, 72, 'edhe ata që nuk dëshmojnë rrejshëm, e kur kalojnë pranë kotësive, kalojnë me dinjitet;'),
(2928, 25, 73, 'dhe ata, të cilët, kur të përkujtohen me argumentet e Zotit të tyre, nuk binë para tyre, as të shurdhër as të verbër (por dëgjojnë e meditojnë);'),
(2929, 25, 74, 'dhe ata që thonë: “O Zoti ynë dhurona neve në gratë tona dhe trashëgimtarët tanë burim gëzimi, dhe bëna neve shembull për të mirë!” –'),
(2930, 25, 75, 'të tillët (që u përmendën), do të shpërblehen me xhennet, meqë duruan, dhe do të takohen në të – me përshëndetje (në mes njëri-tjetrit) dhe me selam (prej Perëndisë dhe engjëjve),'),
(2931, 25, 76, 'ku, do të qëndrojnë përherë, e sa vendbanim dhe vendstrehim i mrekullueshëm që është ai!'),
(2932, 25, 77, 'Thuaj: “Zoti im nuk do t’ju përfillte, sikur të mos ishte lutja juaj; e ju keni përgënjeshtruar, andaj dënimi për ju do të jetë i paevitueshëm”.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'Tâ, Sín, Mîm.'),
(2934, 26, 2, 'Këto janë ajetet e Librit të qartë!'),
(2935, 26, 3, 'A mos vallë ti (o Muhammed) don ta shkatërrosh veten, ngase ata nuk duan të besojnë?'),
(2936, 26, 4, 'Sikur të donim Ne, do të zbritnim në ta – nga qielli – një mrekulli, para së cilës, ata do të përulnin kokat e tyre.'),
(2937, 26, 5, 'Dhe, atyre, nuk u ka ardhur kurrfarë këshille e re prej Mëshiruesit, e që të mos jenë shmangur nga ajo.'),
(2938, 26, 6, 'Ata, me të vërtetë, e kanë përgënjeshtruar (këtë këshillë), andaj, do t’i arrijë ata, me siguri përfundimi i asaj, me të cilën janë tallë.'),
(2939, 26, 7, 'Vallë, a nuk shohin ata që në Tokë, Ne kemi bërë të mbijnë sa e sa lloje çifte bimësh të mrekullueshme?'),
(2940, 26, 8, 'Me të vërtetë, në këtë ka argument, por shumica e tyre (njerëzve) nuk kanë besuar,'),
(2941, 26, 9, 'e Zoti yt, me të vërtetë, është i Plotëfuqishëm dhe mëshirues.'),
(2942, 26, 10, '(Përkujtoju atyre, o Muhammed) kur Perëndia i thërriti Musait: “Shko te populli zullumqar,'),
(2943, 26, 11, 'populli i Faraonit; vallë, a nuk po i druajnë ata Perëndisë?”'),
(2944, 26, 12, '(Musai) tha: “O Zoti im, unë kam frikë se do të më konsiderojnë gënjeshtar,'),
(2945, 26, 13, 'e zemra po më ngushtohet, dhe gjuha nuk po më zhvillohet, andaj, dërgoja pejgamberllëkun edhe Harunit!'),
(2946, 26, 14, 'Unë jam edhe përgjegjës ndaj tyre për një gabim (mbytjen e një Kopti), andaj kam frikë se do të më vrasin.'),
(2947, 26, 15, '(Perëndia) tha: “Kurrsesi! (Nuk do të vrasin, por) shkoni që të dy me argumentet Tona. Me të vërtetë, Na jemi me ju dhe dëgjojmë.'),
(2948, 26, 16, 'Shkoni te Faraoni dhe i thoni: “Na jemi pejgamberët e Zotit të botërave,'),
(2949, 26, 17, 'lejoni bijtë e Israelit që të vijnë me ne!”'),
(2950, 26, 18, '(Faraoni) tha: “Vallë, a nuk të kemi rritur ne – në mesin tonë si fëmijë? Dhe, ke qëndruar në mesin tonë shumë vjet të jetës tënde –'),
(2951, 26, 19, 'dhe, ke punuar atë veprën (e keqe) tënde, të cilën e ke punuar, e ti madje – je edhe jomirënjohës?”'),
(2952, 26, 20, '(Musai) tha: “E kam punuar atë (vepër) kur nuk kam qenë i udhëzuar (para profetnisë),'),
(2953, 26, 21, 'e kam ikur prej jush, ngase jam frikësuar prej jush, e Zoti im më dhuroi mençurinë dhe më bëri pejgamber.'),
(2954, 26, 22, 'A është mirësi ajo që po më bëni vetëm mua, ndërsa Israelitët i bëre skllavë?”'),
(2955, 26, 23, 'Faraoni pyeti (Musain): “E ç’është Zoti i gjithë botërave?”'),
(2956, 26, 24, '(Musai) u përgjegjë: “(Ai është Allahu) Zot’i qiejve dhe i Tokës, dhe çka gjendet në mes tyre, nëse besoni?”'),
(2957, 26, 25, '(Faraoni) u tha atyre rredh vetes: “Vallë, a nuk po dëgjoni ju?”'),
(2958, 26, 26, '(Musai) tha: “(Ai është Allahu) Zot’i juaj dhe Zot’i të parëve tuaj”.'),
(2959, 26, 27, '(Faraoni) tha: “Me të vërtetë, Pejgamberi i juaj, që u është dërguar, është i çmendur”.'),
(2960, 26, 28, '(Musai) tha: “(Ai është Allahu) Zot’i lindjes dhe i perëndimit, dhe asaj që gjendet në mes tyre, nëse mendoni.'),
(2961, 26, 29, '(Faraoni) tha: “Nëse ti merr për zot tjetër kë, pos meje, unë, me siguri, do të të hedhë në burg!”'),
(2962, 26, 30, '(Musai) tha: “Vallë, (e bën atë) edhe pasi të kam sjellë mrekulli të qartë”.'),
(2963, 26, 31, '(Faraoni) tha: “E, demonstroje pra, nëse thua të vërtetën!”'),
(2964, 26, 32, 'E (Musai) e lëshoi shkopin e vet (në tokë), e ai u shndërrua në gjarpër të vërtetë!'),
(2965, 26, 33, 'E, pastaj, nxorri dorën e vet (nga xhepi), kur ajo, u bë e bardhë para shikuesve!'),
(2966, 26, 34, '(Faraoni) u tha parisë së tij, që i kishte përreth: “Ky, me të vërtetë, qenka magjistar i dijshëm,'),
(2967, 26, 35, '(i cili) me magjitë e veta don t’ju dëbojë nga toka juaj, e ç’rekomandoni (ju)?”'),
(2968, 26, 36, '(Ata) u përgjegjën: “Lëre këtë dhe vëllain e tij, e dërgoni nëpër qytete që t’i tubojnë,'),
(2969, 26, 37, 'të gjithë magjistarët e dijshëm, (e le t’i sjellin ty)”.'),
(2970, 26, 38, 'Dhe, u tubuan magjistarët në kohën e ditës së caktuar,'),
(2971, 26, 39, 'e popullit iu tha: “A jeni tubuar'),
(2972, 26, 40, 'që të shkojmë pas magjistarëve, nëse ata do të jenë fitues!”'),
(2973, 26, 41, 'E, kur erdhën, magjistarët i thanë Faraonit: “A, me të vërtetë, do të shpërblehemi ne, nëse bëhemi fitues?”'),
(2974, 26, 42, '(Faraoni) u përgjegj: “Po, - do të jeni atëherë – edhe ndër të afërmit e mi”.'),
(2975, 26, 43, 'Musai u tha atyre: “Hudheni atë që doni ta hudhni!”'),
(2976, 26, 44, 'Dhe, ata hodhën konopët e tyre dhe shkopinjtë e tyre dhe thanë: “Pasha madhërinë e Faraonit, na, me të vërtetë, do të jemi fitues!”'),
(2977, 26, 45, 'Pastaj, Musai hodhi shkopin e tij, e (shkopi i tij) i kapërdiu përnjëherësh, ato që i patën prodhuar ata rrejshëm.'),
(2978, 26, 46, 'Dhe, magjistarët ranë (në tokë) duke bërë sexhde,'),
(2979, 26, 47, 'dhe thanë: “Na besojmë në Zotin e Gjithësisë,'),
(2980, 26, 48, 'Zotin e Musait dhe të Harunit!”'),
(2981, 26, 49, '(Faraoni) tha: “I besuat atij para se t’ju lejoja juve! – Me të vërtetë, ai është i madhi juaj, i cili u ka mësuar juve magjitë. E, ju do ta dini – unë, në të vërtetë, do t’ju këpusë duart dhe këmbët tuaja tërthorazi dhe do t’ju var të gjithëve!'),
(2982, 26, 50, '(Magjistarët) thanë: “S’ka gjë! – na do të kthehemi te Zoti ynë.'),
(2983, 26, 51, 'Na shpresojmë, se Zoti ynë do të na falë mëkatet tona, meqë jemi besimtarët e parë”.'),
(2984, 26, 52, 'Dhe, Na ia shpallëm Musait: “Ti ecë natën me robërit e Mi (besimtarët), por do të jeni të ndjekur (prej mohuesve)”.'),
(2985, 26, 53, 'Dhe, Faraoni dërgoi njerëz nëpër qytete, tubues (të ushtarëve):'),
(2986, 26, 54, '(duke thënë): “Me të vërtetë, këta jën një grup i vogël, të cilët'),
(2987, 26, 55, 'na kanë pezmatuar neve,'),
(2988, 26, 56, 'por, ne – të gjithë jemi të gatshëm!”'),
(2989, 26, 57, 'Dhe, Na, i kemi dëbuar ata (Faraonin dhe shoqërinë e tij) prej kopshtijeve dhe lumenjve,'),
(2990, 26, 58, 'nga thesarët dhe nga pallatet e mrekullueshme.'),
(2991, 26, 59, 'Ja, kështu ka qenë, dhe (këtë) Na ia dhamë trashëgim bijve të Israelit.'),
(2992, 26, 60, 'Dhe, ata (populli i Faraonit), në mëngjez iu afruan atyre (Israelitëve),'),
(2993, 26, 61, 'e kur e panë njëra-tjetrën shokët e Musait thirrën: “Me të vërtetë, na zunë ushtarët e Faraonit!”'),
(2994, 26, 62, '(Musai) tha: “Kurrsesi! Me të vërtetë, me mua është Zoti im. Ai – ma tregon rrugën mua”.'),
(2995, 26, 63, 'Dhe Ne, i shpallëm Musait: “Bjeri me shkopin tënd detit!” (Ai i ra) e (deti) u nda dhe çdo pjes ëe tij u bë si kodër e madhe;'),
(2996, 26, 64, 'dhe Ne – atëherë, aty - i afruam ata të tjerët –'),
(2997, 26, 65, 'e Musain dhe të gjithë ata që ishin me të, i shpëtuam –'),
(2998, 26, 66, 'dhe pastaj, të tjerët i fundosëm.'),
(2999, 26, 67, 'Me të vërtetë, në këtë ka argument (këshillë), por shumica e tyre, nuk janë besimtarë,'),
(3000, 26, 68, 'e Zoti yt, me të vërtetë, është i Plotëfuqishëm dhe Mëshirues.'),
(3001, 26, 69, 'Dhe, tregoju atyre lajmin për Ibrahimin,'),
(3002, 26, 70, 'kur i tha babës së vet dhe popullit të vet: Çka adhuroni ju?'),
(3003, 26, 71, 'Ata u përgjegjën: “Adhurojmë idhujt, dhe përherë u jemi të prirur atyre”.'),
(3004, 26, 72, '(Ibrahimi) tha: “A ju dëgjojnë ata juve kur t’ju luteni,'),
(3005, 26, 73, 'ose, a mund t’ju sjellin dobi apo dëm?”'),
(3006, 26, 74, 'U përgjegjën: “Jo, por i kemi gjetur të parët tanë që vepronin kështu”.'),
(3007, 26, 75, '(Ibrahimi) tha: “E, a po vëreni ju, se ç’po adhuroni –'),
(3008, 26, 76, 'ju dhe të parët tuaj të lashtë?”'),
(3009, 26, 77, 'Me të vërtetë, ata janë armiqët e mi. E, nuk më është armik Zoti i Gjithësisë,'),
(3010, 26, 78, 'i cili më ka krijuar dhe më udhëheq në rrugë të drejtë,'),
(3011, 26, 79, 'dhe i cili më ushqen dhe furnizon me ujë,'),
(3012, 26, 80, 'e kur të sëmuhem, Ai më shëron,'),
(3013, 26, 81, 'dhe i cili do të bëjë që të vdes, e pastja do të më ringjallë,'),
(3014, 26, 82, 'dhe i cili, shpresoj se ma falë gabimin tim në Ditën e Kijametit!'),
(3015, 26, 83, 'O Zoti im, më dhuro dijeni dhe më rradhit me të mirët,'),
(3016, 26, 84, 'dhe bën që të përmendem për të mirë te gjeneratat e ardhshme,'),
(3017, 26, 85, 'dhe më bën trashëgimtar të xhennetit të dhuntive,'),
(3018, 26, 86, 'dhe falja mëkatet babës tim, sepse ai ka qenë njëri nga të humburit,'),
(3019, 26, 87, 'dhe mos më turpëro në Ditën kur do të ringjallen njerëzit,'),
(3020, 26, 88, 'në Ditën kur nuk do të bëjë dobi as pasuria as djemtë,'),
(3021, 26, 89, 'por do të bëjë (kenë) dobi (vetëm) ai që vie me zemër të pastër te Perëndia”.'),
(3022, 26, 90, 'Dhe, xhenneti do t’u afrohet besimtarëve,'),
(3023, 26, 91, 'ndërsa, xhehennemi do t’u dëftohet mohuesve,'),
(3024, 26, 92, 'dhe do t’ju thuhet atyre: “Ku janë ata që i adhuronit ju;'),
(3025, 26, 93, 'e jo Perëndinë? A mund t’u ndihmojnë aty juve, ose a mund t’i ndihmojnë edhe vetes?”'),
(3026, 26, 94, 'E do të hudhen në (zjarr) ata (hyjnitë e tyre), dhe mohuesit,'),
(3027, 26, 95, 'dhe ushtria e Iblisit, të gjithë së bashku.'),
(3028, 26, 96, 'Dhe, ata – në xhehennem grindeshin në mes vete e thonin:'),
(3029, 26, 97, '“Pasha Perëndinë, me të vërtetë, ne kemi qenë në humbje të qartë,'),
(3030, 26, 98, 'kur juve (hyjnive) u kemi barazuar me Zotin e gjithësisë,'),
(3031, 26, 99, 'e, vetëm mëkatarët na kanë mashtruar neve,'),
(3032, 26, 100, 'e nuk kemi asnjë ndërmjetësues (te Perëndia)'),
(3033, 26, 101, 'as mik të ngushtë, -'),
(3034, 26, 102, 'sikur të ktheheshim përsëri (në jetë), e të bëhemi besimtarë!”'),
(3035, 26, 103, 'Me të vërtetë, në këtë ka argument (këshillë), por shumica e tyre, nuk janë besimtarë,'),
(3036, 26, 104, 'e Zoti yt, me të vërtetë është i Plotëfuqishëm dhe Mëshirues.'),
(3037, 26, 105, 'Populli i Nuhut, i konsideronte gënjeshtarë pejgamberët.'),
(3038, 26, 106, 'Kur vëllai i tyre – Nuhu u tha: “Vallë, a nuk i druani ju Perëndisë?'),
(3039, 26, 107, 'Me të vërtetë, unë jam pejgamber i besuar për ju,'),
(3040, 26, 108, 'andaj, druani Perëndisë dhe bëhuni të dëgjueshëm ndaj meje!'),
(3041, 26, 109, 'Për këtë, unë nuk kërkoj nga ju kurrfarë shpërblimi, mua do të më shpërblejë vetëm Zoti i gjithësisë,'),
(3042, 26, 110, 'andaj, druani Perëndisë dhe bëhuni të dëgjueshëm ndaj meje!”'),
(3043, 26, 111, 'Ata thanë: “Vallë, a të besojmë na ty, kur ty po të pasojnë të ulëtit?”'),
(3044, 26, 112, '(Nuhu) tha: “Unë nuk di ç’kanë punuar ata,'),
(3045, 26, 113, 'llogaria e tyre është vetëm te Zoti im, dinie ju,'),
(3046, 26, 114, 'e unë nuk do t’i dëbojë besimtarët,'),
(3047, 26, 115, 'unë jam vetëm paralajmërues i qartë”.'),
(3048, 26, 116, 'Ata thanë: “Nëse nuk tërhiqesh nga këto, o Nuh, me siguri, do të goditesh me gurë”.'),
(3049, 26, 117, '(Nuhu) tha: “O Zoti im, me të vërtetë, populli im më konsideron për gënjeshtar,'),
(3050, 26, 118, 'andaj, Ti – gjyko mes meje dhe atyre, dhe më shpëto mua e besimtarët që janë me mua!”'),
(3051, 26, 119, 'Dhe, Na, e shpëtuam atë dhe ata që ishin me të në barkën (e ngarkuar) plotë,'),
(3052, 26, 120, 'e pastaj, të tjerët i fundosëm.'),
(3053, 26, 121, 'Me të vërtetë, në këtë ka argument (këshillë), por shumica e tyre nuk janë besimtarë,'),
(3054, 26, 122, 'e Zoti yt, me të vërtetë, është i Plotëfuqishëm dhe Mëshirues.'),
(3055, 26, 123, 'Adi i konsideronte gënjeshtarë pejgamberët.'),
(3056, 26, 124, 'Kur vëllai i tyre – Hudi, u tha: “Vallë, a nuk i druani ju Perëndisë?'),
(3057, 26, 125, 'Me të vërtetë, unë jam pejgamber i besuar për ju,'),
(3058, 26, 126, 'andaj, druani Perëndisë dhe bëhuni të dëgjueshëm ndaj meje!'),
(3059, 26, 127, 'Për këtë, unë nuk kërkoj nga ju kurrfarë shpërblimi, mua do të më shpërblejë vetëm Zoti i gjithësisë.'),
(3060, 26, 128, 'A mos vallë në çdo kodrinë po ngritni ju godinë (përmendore), për t’u tallë (me kalimtarët),'),
(3061, 26, 129, 'dhe ngritni pallate (luksoze), thuas edo të jetoni gjithmonë,'),
(3062, 26, 130, 'e kur dënoni, dënoni si absolut?'),
(3063, 26, 131, 'Andaj, druani Perëndisë dhe bëhuni të dëgjueshëm ndaj meje!'),
(3064, 26, 132, 'Dhe, druani Atij, i cili u ka dhuruar juve ato që i dini:'),
(3065, 26, 133, 'e dhuron juve baqëti (pasuri) dhe fëmijë,'),
(3066, 26, 134, 'edhe kopshtije edhe burime.'),
(3067, 26, 135, 'Unë, me të vërtetë, druaj për ju dënimin e Ditës së madhe”.'),
(3068, 26, 136, 'Ata thanë: “Për ne, është njëlloj – na këshillove ti (neve), ose nuk na këshillove.'),
(3069, 26, 137, 'Këto janë vetëm doket e popujve të lashtë,'),
(3070, 26, 138, 'e ne nuk do të jemi të dënuar”.'),
(3071, 26, 139, 'Ata – e konsideruan gënjeshtar atë (Hudin), andaj Ne i shkatërruam ata. Me të vërtetë, në këtë ka argument (këshillë), por shumica e tyre nuk janë besimtarë,'),
(3072, 26, 140, 'e Zoti yt, me të vërtetë, është i Plotëfuqishëm dhe Mëshirues.'),
(3073, 26, 141, 'Dhe Themudi i konsideronte gënjeshtarë pejgamberët.'),
(3074, 26, 142, 'Kur vëllai i tyre – Salihu, u tha: “Vallë, a nuk i druani ju Perëndisë?'),
(3075, 26, 143, 'Me të vërtetë, unë jam pejgamber i besuar për ju,'),
(3076, 26, 144, 'andaj, druani Perëndisë dh bëhuni të dëgjueshëm ndaj meje!'),
(3077, 26, 145, 'Për këtë, unë nuk kërkoj nga ju kurrfarë shpërblimi, mua do të më shpërblejë vetëm Zoti i gjithësisë.'),
(3078, 26, 146, 'Vallë, a mendoni ju – se do të lëheni këtu të sigurtë,'),
(3079, 26, 147, 'në kopshtije dhe pranë burimeve,'),
(3080, 26, 148, 'në të lashta dhe palme hurmesh, me frute të shijshme?'),
(3081, 26, 149, 'E, ju po gdhendni shtëpitë në male me mjeshtri të shkathët,'),
(3082, 26, 150, 'andaj, druani Perëndisë dhe bëhuni të dëgjueshëm ndaj meje,'),
(3083, 26, 151, 'dhe mos dëgjoni urdhërat e atyre që e teprojnë në të keqe,'),
(3084, 26, 152, 'të cilët bëjnë ngatërresa në Tokë, e nuk përmirësojnë (asgjë)”.'),
(3085, 26, 153, 'Ata thanë: “Ti je vetëm njeri – nga të magjepsurit;'),
(3086, 26, 154, 'ti je vetëm njeri – ashtu si ne, andaj na sjell një mrekulli, nëse thua të vërtetën!”'),
(3087, 26, 155, '(Salihu) tha: “Ja, kjo është devja (dokument), - për të ka pije (uji) në ditë të caktuara; e për ju – në ditë tjetër të caktuar,'),
(3088, 26, 156, 'dhe mos i bëni kurrfarë të keqe asaj, se ju godet dënimi i Ditës së madhe!'),
(3089, 26, 157, 'Por, ata – e therrën atë, e pastaj u penduan,'),
(3090, 26, 158, 'dhe i goditi dënimi ata. Me të vërtetë, në këtë ka argument (këshillë), por shumica e tyre, nuk janë besimtarë,'),
(3091, 26, 159, 'e Zoti yt, me të vërtetë, është i Plotëfuqishëm dhe Mëshirues.'),
(3092, 26, 160, 'Populli i Lutit i konsideronte gënjeshtarë pejgamberët.'),
(3093, 26, 161, 'Kur vëllai i tyre – Luti, u tha: “Vallë, a nuk i druani ju Perëndisë?'),
(3094, 26, 162, 'Me të vërtetë, unë jam pejgamber i besuar për ju,'),
(3095, 26, 163, 'andaj, druani Perëndisë dhe bëhuni të dëgjueshëm ndaj meje!'),
(3096, 26, 164, 'Për këtë, unë nuk kërkoj nga ju kurrfarë shpërblimi, mua do të më shpërblejë vetëm Zoti i gjithësisë.'),
(3097, 26, 165, 'Vallë, ju – pos botës tjetër, u qaseni mashkujve,'),
(3098, 26, 166, 'e i leçitni gratë tuaja, të cilat Zoti i juaj i ka krijuar për ju? Ju, me të vërtetë, jeni popull që kaloni çdo kufi të së keqes”.'),
(3099, 26, 167, 'Ata thanë: “Nëse nuk tërhiqesh nga këto, o Lut, me siguri do të jesh i dëbuar”.'),
(3100, 26, 168, '(Luti) tha: “Unë, me të vërtetë, neveritem për atë që bëni ju!'),
(3101, 26, 169, 'O Zoti im, më shpëto (nga dënimi), mua dhe familjen time nga ajo që bëjnë ata!”'),
(3102, 26, 170, 'Dhe Ne – e shpëtuam atë dhe familjen e tij – të gjithë,'),
(3103, 26, 171, 'pos plakën (gruas së Lutit, e cila mbeti) me të tjerët,'),
(3104, 26, 172, 'e pastaj, të tjerët i shkatërruam'),
(3105, 26, 173, 'dhe në ta lëshuam shi (të tmerrshëm – gurësh), e sa shi i shëmtuar që është ai, për ata që janë paralajmëruar!'),
(3106, 26, 174, 'Me të vërtetë, në këtë ka argument (këshillë), por shumica e tyre nuk janë besimtarë,'),
(3107, 26, 175, 'e Zoti yt, me të vërtetë, është i Plotëfuqishëm dhe Mëshirues.'),
(3108, 26, 176, '(Edhe) banorët e Ejketit – i konsideronin gënjeshtarë pejgamberët.'),
(3109, 26, 177, 'Kur Shuajbi, u tha atyre: “Vallë, a nuk i druani ju Perëndisë?'),
(3110, 26, 178, 'Me të vërtetë, unë jam pejgamber i besuar për ju,'),
(3111, 26, 179, 'andaj, druani Perëndisë dhe bëhuni të dëgjueshëm ndaj meje!'),
(3112, 26, 180, 'Për këtë unë nuk kërkoj nga ju kurrfarë shpërblimi, mua do të më shpërblejë vetëm Zoti i gjithësisë.'),
(3113, 26, 181, 'Plotësonie matjen dhe mos u bëni nga ata që mungojnë (në matje)'),
(3114, 26, 182, 'dhe peshoni me terezi të drejtë,'),
(3115, 26, 183, 'dhe mos ua pakësoni njerëzve gjërat e tyre, dhe mos bëni ngatërresa në Tokë,'),
(3116, 26, 184, 'dhe druani Atij, që u ka krijuar juve dhe gjeneratat e mëparshme!'),
(3117, 26, 185, 'Ata thanë: “Ti je vetëm njeri – nga të magjepsuarit,'),
(3118, 26, 186, 'edhe ti je – vetëm njeri, si ne. Na ty, me të vërtetë, të konsiderojmë gënjeshtar;'),
(3119, 26, 187, 'andaj, lëshoje mbi ne – një copë nga qielli (si dënim) nëse thua të vërtetën!'),
(3120, 26, 188, '(Shuajbi) tha: “Zoti im, di më së miri atë që punoni ju”.'),
(3121, 26, 189, 'Dhe, ata vazhduan ta përgënjeshtrojnë atë, e i goditi ata dënimi i resë (së tmerrshme), me të vërtetë, ky ishte dënimi i një Ditës së madhe.'),
(3122, 26, 190, 'Me të vërtetë, në këtë ka argument (këshillë), por shumica e tyre nuk janë besimtarë,'),
(3123, 26, 191, 'e Zoti yt, me të vërtetë, është i Plotëfuqishëm dhe Mëshirues.'),
(3124, 26, 192, 'Dhe me të vërtetë, ai (Kur’ani), është Shpallja e Zotit të gjithësisë,'),
(3125, 26, 193, 'atë e ka sjellë Shpirti i besueshëm (Xhebraili)'),
(3126, 26, 194, 'në zemrën tënde që të bëhesh nga ata që paralajmërojnë,'),
(3127, 26, 195, 'në gjuhën e qartë arabe.'),
(3128, 26, 196, 'Dhe, me të vërtetë, ai (Kur’ani) është përmend në librat e (pejgamberëve) të mëparshëm, -'),
(3129, 26, 197, 'vallë, a nuk ka qenë dëshmi për ta (ajo) – atë që e kanë ditur dijetarët e bijve të Israelit?'),
(3130, 26, 198, 'E, sikur t’ia shpallnim atë (Kur’anin) edhe ndonjë joarabi,'),
(3131, 26, 199, 'e t’jua lexonte ai – ata përsëri nuk do të kishin besuar.'),
(3132, 26, 200, 'Ja kështu, Na – ua kemi shpie (mohimin) në zemrat e mëkatarëve,'),
(3133, 26, 201, 'ata, nuk do të besojnë atë, përderisa të mos e shijojnë dënimin e dhembshëm,'),
(3134, 26, 202, 'e do t’ju vie (dënimi) atyre papritmas, e ata nuk do ta hetojnë (ardhjen e dënimit),'),
(3135, 26, 203, 'e do të thonë: “A do të na jepet afat?”'),
(3136, 26, 204, 'A mos vallë, ata – po e shpejtojnë dënimin Tonë!?'),
(3137, 26, 205, 'E, çka mendon ti, nëse Ne u japim atyre, që të kënaqen disa vjet,'),
(3138, 26, 206, 'e pastaj, t’i godet ata, ajo që u është premtuar (dënimi),'),
(3139, 26, 207, 'nuk do t’ju vlejë asgjë atyre, ajo, me të cilën janë kënaqur.'),
(3140, 26, 208, 'E, Ne – nuk e kemi shkatërruar asnjë vendbanim e që të mos ketë pasur paralajmërues –'),
(3141, 26, 209, 'këshillë për ta (që të kthehen në rrugën e drejtë); e Ne – nuk kemi qenë të padrejtë.'),
(3142, 26, 210, 'Atë (Kur’anin) nuk e kanë sjellë djajtë,'),
(3143, 26, 211, 'as që u përket ai – atyre, as që mundën atë,'),
(3144, 26, 212, 'ata (djajtë), me të vërtetë, janë të larguar nga të dëgjuarit (e Shpalljes).'),
(3145, 26, 213, 'Andaj, pos Perëndisë mos adhuro tjetër zot, e të bëhesh nga ata që do të dënohen!'),
(3146, 26, 214, 'Dhe, paralajmëroje farefisin tënd më të afërm,'),
(3147, 26, 215, 'dhe bënu i butë ndaj besimtarëve, që të pasojnë ty!'),
(3148, 26, 216, 'E, nëse nuk të dëgjojnë ty, thuaju: “Unë jam i pastër nga ato që punoni ju”.'),
(3149, 26, 217, 'Dhe, mbështetu në të Plotëfuqishmin dhe Mëshiruesin,'),
(3150, 26, 218, 'i cili të sheh kur ti rri në këmbë (në namaz)'),
(3151, 26, 219, 'dhe (i cili sheh) përkuljen tënde në mesin e atyre që bëjnë sexhde,'),
(3152, 26, 220, 'sepse, Ai – me të vërtetë, i dëgjon të gjitha dhe di të gjitha.'),
(3153, 26, 221, 'A doni t’ju tregoj se kujt i vjen djalli?'),
(3154, 26, 222, 'Ata, i vijnë çdo gënjeshtari, mëkatari,'),
(3155, 26, 223, 'ata përgjojnë, - e të shumtën (e rasteve) janë gënjeshtarë.'),
(3156, 26, 224, 'E, vjershëtarët – i pasojnë të humburit.'),
(3157, 26, 225, 'Vallë, a nuk sheh ti, që ata bredhin në çdo luginë (lëshohen në gjithëfare lloj bisedash)'),
(3158, 26, 226, 'dhe që flasin atë që nuk e punojnë,'),
(3159, 26, 227, 'pos atyre (vjershëtarëve) që kanë besuar, që punojnë vepra të mira, e përmendin shumë Perëndinë e që ndihmohen – pasi u është bërë padrejtësi. E, me të vërtetë, zullumqarët – do ta dinë se në çfarë mundimi do të kthehen (pas vdekjes).');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'Tâ, Sín. Këto janë ajetet e Kur’anit dhe Librit të qartë,'),
(3161, 27, 2, 'udhërrëfyes dhe sihariques për besimtarët,'),
(3162, 27, 3, 'të cilët kryejnë namazin dhe ndajnë zeqatin dhe të cilët besojnë bindshëm në jetën tjetër.'),
(3163, 27, 4, 'Me të vërtetë, ata që nuk kanë besuar në jetën tjetër, Na ua kemi zbukuruar veprat e tyre (të prirura kah e keqja) – andaj, ata, janë të hutuar.'),
(3164, 27, 5, 'Këta janë ata, që i pret dënimi i rëndë dhe, të cilët – në jetën tjetër do të jenë në humbjen më të madhe.'),
(3165, 27, 6, 'Dhe, ty (o Muhammed!), me të vërtetë, po të shpallet Kur’ani nga i Gjithëdijshmi dhe i Plotëdijshmi!'),
(3166, 27, 7, '(Kujtoje) kur Musai i tha familjes së tij: “Unë kam parë një zjarr, do t’ju sjell juve ndonjë lajm prej atje ose do t’ju sjell një un zjarri, për t’u nxe”,'),
(3167, 27, 8, 'e kur (Musai) erdhi te zjarri, u thirr: “Është i bekuar ai që është afër zjarrit dhe kush është rreth tij, dhe qoftë lavdëruar Perëndia – Zot’i gjithësisë!'),
(3168, 27, 9, 'O Musa! Me të vërtetë, Unë jam Perëndia, i Plotëfuqishëm dhe i Gjithëdijshëm!'),
(3169, 27, 10, 'Dhe, hudhe shkopin tënd! E, kur e pa (Musai) atë që po lëvizë si gjarpër ai (shkopi), u kthye para (menjëherë), e nuk shikoi më (nga frika). (Perëndia i tha): “O Musa, mos u friko! Me të vërtetë, tek Unë nuk frikohen pejgamberët (meqë janë të sigurt);'),
(3170, 27, 11, 'e atij, i cili ka bërë keq, e pastaj të keqen e zëvendëson me vepër të mirë, Unë me të vërtetë, jam Falës dhe Mëshirues (e falë atë).'),
(3171, 27, 12, 'Shtine dorën tënde në xhepin e këmishës, do të dalë e bardhë pa kurrfarë sëmundje, - do të jetë kjo njëra prej nëntë mrekullive (me të cilat është dërguar Musai) te Faraoni dhe populli i tij; ata, me të vërtetë, janë popull ngatërrestarë”.'),
(3172, 27, 13, 'E, pasi u erdhën atyre argumentet Tona konkrete, ata thanë: “Kjo është magji e qartë!”'),
(3173, 27, 14, 'Dhe, ata i mohuan ato – duke qenë zullumqarë dhe mendjemëdhenj – por shpirti i tyre i besonte për të vërteta. Pa, shiko, çfarë ka qenë fundi i ngatërrestarëve!'),
(3174, 27, 15, 'Dhe Ne, me të vërtetë, i kemi dhënë Davudit dhe Sulejmanit dijeni, dhe ata thanë: “Falenderojmë Perëndinë që na ka dekoruar mbi shumicën e robërve të Tij besimtarë!'),
(3175, 27, 16, 'Dhe, Sulejmani e trashëgoi Davudin dhe tha: “O njerëz, na është mësuar neve të folurit e shpendëve (kuptimi i zërave të tyre) dhe na është dhënë gjithçka (nga ato që u jepen pejgamberëve dhe sundimtarëve)!”'),
(3176, 27, 17, 'Dhe, u tubua ushtria e Sulejmanit e përbërë prej xhindëve, njerëzve dhe shpendëve, të gjithë të radhitur në çeta të gatshme,'),
(3177, 27, 18, 'dhe kur arritën në luginën e thneglave, një thnegël tha: “O thnegla, hyni në banesat tuaja, ë të mos ju shkel Sulejmani dhe ushtria e tij, meqë nuk ju hetojnë juve!”'),
(3178, 27, 19, 'E, ai (Sulejmani) preku në gaz, duke qeshur në fjalët e saj (thneglës) dhe tha: “O Zoti im, më oriento që të jem mirënjohës në dhuntinë Tënde, që më ke dhënë mua dhe prindërve të mi, dhe që të punojë vepra të mira, me të cilat do të jeshë i kënaqur Ti, dhe më shpie me mëshirën Tënde, në mesin e robërve Tu të mirë!'),
(3179, 27, 20, 'Dhe, ai (Sulejmani), bëri kontrollin (rivistën) e shpendëve dhe tha: “Pse nuk e shoh pupëzën këtu, a nuk është e pranishme ajo?'),
(3180, 27, 21, 'Nëse nuk më sjell arsyetim të bindshëm, do ta dënoj atë me dënim të rëndë ose do ta pres!”'),
(3181, 27, 22, 'E, nuk vonoi shumë, e erdhi ajo edhe tha: “Kam marrë vesh atë që nuk e ke ditur ti dhe të kam sjellë lajm të sigurt prej Sabes.'),
(3182, 27, 23, 'Kam parë nëj grua që i sundonte ata dhe që i është dhënë çdo gjë (që i nevojitet një sundimtareje) dhe e cila kishte një fron madhështor;'),
(3183, 27, 24, 'kam parë se edhe ajo edhe populli i saj adhuronin Diellin, e jo Perëndinë, djalli ua ka zbukuruar sjelljet e tyre dhe i ka zmbrapë nga rruga e drejtë, andaj nuk e gjetën rrugën e drejtë,'),
(3184, 27, 25, 'e që t’i bëjnë sexhde Perëndisë, i cili i nxjerr të gjitha gjërat sekrete në qiej dhe në Tokë dhe i cili di çka fshihni ju dhe çka tregoni haptazi.'),
(3185, 27, 26, 'Perëndia është Një, s’ka zot tjetër përveç Tij, Zotit të Arshit të madh!”'),
(3186, 27, 27, '(Sulejmani) tha: “Do të shohim se, a thua të vërtetën, apo je nga gënjeshtarët.'),
(3187, 27, 28, 'Shko me këtë letër timen e hudhju atyre. Pastaj largohu pak nga ata, dhe shiko ç’po të përgjigjen ty!”'),
(3188, 27, 29, 'Ajo (mbretëresha e Sabes) tha: “O pari, mua, me të vërtetë, më ka arritur letër e madhërueshme.'),
(3189, 27, 30, 'Ajo (letër) është nga Sulejmani dhe citon: “Me emër të Perëndisë, Mëshiruesit dhe Përdëllimtarit të Madh!'),
(3190, 27, 31, 'Mos u madhështoni para meje dhe ejani të përulur!'),
(3191, 27, 32, '(Mbretëresha) tha: “O pari, më këshilloni se ç’duhet të bëjë në këtë çështje time! Unë nuk do të vendos asgjë pa ju!”'),
(3192, 27, 33, 'Ata thanë: “Na jemi shumë të fortë dhe luftëtarë të guximshëm, por puna është te ti, andaj mendo mirë se ç’do të urdhërosh!”'),
(3193, 27, 34, 'Ajo tha: “Me siguri, kur sundimtarët e pushtojnë ndonjë vendbanim, e çrregullojnë atë dhe i poshtërojnë banorët e tyre autoritativë. Kështu veprojnë ata.'),
(3194, 27, 35, 'Unë, do t’ju dërgoj atyre një dhuratë dhe do të shoh se me çka do të kthehen të deleguarit”.'),
(3195, 27, 36, 'Kur erdhi (i deleguari) te Sulejmani, ai (Sulejmani) tha: “A mos doni ju të më ndihmoni me pasuri? Atë që ma ka dhënë Perëndia mua, është më e mirë nga ajo që u ka dhënë juve. Por, ju, gëzohenime dhuratën tuaj.'),
(3196, 27, 37, 'Kthehuni tek ata! Na, me siguri do të sjellim ushtri, të cilës nuk do të mund t’i përballojnë ata – dhe ne me siguri, do t’i dëbojmë ata prej Sabes (krahinë në Jemen), të poshtëruar dhe të përulur.'),
(3197, 27, 38, '(Sulejmani tha): “O parësi, kush prej jush do të më sjellë fronin e saj, para se të vijnë ata të përulur?”'),
(3198, 27, 39, 'Njëri nga xhindet – Ifriti tha: “Unë do të ta sjelli ty para se të ngritesh ti prej vendit tënd, dhe – unë për këtë jam i fuqishëm dhe i sigurtë”.'),
(3199, 27, 40, 'Ai, që ishte i pajisur me dijen e Librit – prezent te Sulejmani, tha: “Unë do të ta sjellë ty atë – sa çel e mbyll sytë”. Kur (Sulejmani) pa (fronin) e vendosur para tij, tha: “Kjo është nga dhuntitë e Zotit tim, për të më provuar mua se – a jam mirënjohës apo mohues (i dhuntisë). Kus falenderon, ai falenderon për dobi të veten, e kush mohon... (dënohet), e Zoti im është i pavarur (s’ka nevojë për falenderim) dhe i madhërueshëm.'),
(3200, 27, 41, '(Sulejmani) tha: “Ndryshojani edhe fronin e saj! Do të shohim se a do ta hetojë ajo këtë, apo do të jetë nga ata që nuk dinë të orientohen!”'),
(3201, 27, 42, 'E, kur erdhi ajo (mbretëresha), i thanë asaj: “A i tillë është froni yt?” Ajo u përgjegj: “Gati si të ishte ai”. Dhe neve, na është dhënë dijenia para këtij rasti dhe ne kemi qenë të përulur (muslimanë)”.'),
(3202, 27, 43, 'Atë e pengonin (nga të adhuruarit Perëndinë) ato që adhuronte ajo, pos Perëndisë, sepse, ajo ishte nga populli mohues.'),
(3203, 27, 44, 'I është thënë asaj: “Hyn në pallat! Dhe, kur e pa ajo atë, mendoi se është ujë i thellë dhe përvoli fustanin për këmbë (për të hyrë në ujë). (Sulejmani) i tha (asaj): “Me të vërtetë, ky pallat është i (shtruar) prej xhamave të lëmuara”. Ajo tha: “O Zoti im, me të vërtetë unë e kam dëmtuar veten dhe tani, bashkë me Sulejmanin i dorëzohem Perëndisë – Zotit të gjithësisë!”'),
(3204, 27, 45, 'Na i kemi dërguar Themudit, vëllain e tij Salihun (me urdhër): “Adhuronie Perëndinë!” E, ata, u ndanë në dy grupe, që grindeshin në mes tyre.'),
(3205, 27, 46, '(Salihu) tha: “O populli im, përse e nxitoni të keqen para së mirës? Përse nuk kërkoni ju nga Perëndia faljen e mëkateve, që të mëshiroheni?”'),
(3206, 27, 47, 'Ata thanë: “Na po parandiejmë fatkeqësinë me ty dhe me ata që janëme ty. (Salihu) u tha: “E, mira dhe e keqja është prej Perëndisë. Në të vërtetë, ju jeni popull i vënë në sprovë.'),
(3207, 27, 48, 'Në qytet, kanë qenë nëntë persona që bënin ngatërresa, e nuk vënin rregull.'),
(3208, 27, 49, 'Ata i thanë (njëri-tjetrit): “Betohuni në Perëndinë – se ne, natën, do ta mbysim atë (Salihun) dhe familjen e tij; e pastaj, gjithsesi do t’ju themi të afërmve të tij: “Na nuk kemi prezentuar në zhdukjen e familjes së tij dhe, na – me siguri, flasim të vërtetën”.'),
(3209, 27, 50, 'Dhe, trillonin dredhira, por Ne i dënonim ata për këtë trillim, e ata nuk e hetonin.'),
(3210, 27, 51, 'Pa, shiko, çfarë ka qenë fundi i ngatërresave të tyre. Na i kemi shkatërruar ata të gjithë dhe popullin e tyre,'),
(3211, 27, 52, 'ja, shtëpitë e tyre të shkretëruara, për shkak të zullumit që kanë bërë. Në këtë, me të vërtetë, ka argument të madh për popullin që dinë,'),
(3212, 27, 53, 'e, i kemi shpëtuar ata që kanë besuar dhe të cilët ruheshin nga të këqiat.'),
(3213, 27, 54, '(Dhe, e kemi dërguar) Lutin, (e kujtoje) kur ai i tha popullit të vet: “Përse bëni vepra imorale, madje në sytë e njëri-tjetrit (haptazi)?'),
(3214, 27, 55, 'Vallë, a mos ju u çaseni me epsh meshkujve, në vend të femrave? Ju, me të vërtetë, jeni popull injorant”.'),
(3215, 27, 56, 'E, përgjegjja e vetme e popullit të Lutit, thoshte: “Dëboni familjen e Lutit prej qytetit tuaj; ata, me të vërtetë, janë njerz të pastër (këtë e thonin në formë ironie)!”'),
(3216, 27, 57, 'Dhe Ne, i kemi shpëtuar ata dhe familjen e tij (të gjithë), pos gruas së tij, që ajo të mbetet me të dënuarit.'),
(3217, 27, 58, 'Dhe, mbi ta lëshuam shi – rrebesh (prej gurësh), e sa i tmerrshëm që është ai shi, për ata që janë paralajmëruar.'),
(3218, 27, 59, 'Thuaj (o Muhammed): “Qoftë falenderimi për Perëndinë dhe shpëtimi për robërit e Tij, të cilët i ka zgjedhur Ai. A më i mirë është Perëndia, ao ata (idhuj) që i kanë adhuruar?'),
(3219, 27, 60, 'A është më i mirë Ai që ka krijuar qiejt dhe Tokën – dhe ua ka lëshuar ujin nga qielli, me të cilin kemi krijuar kopshtet e mrekullueshme. Ju, nuk keni mundur të krijoni drunjtë e tyre. Vallë, a krahas Perëndisë, ekzistuaka zot tjetër? Jo, por ai popull është shmangur nga e vërteta.'),
(3220, 27, 61, 'Apo është më i mirë, Ai që e ka bërë Tokën të qetë, ka bërë në të lumenj, ka ngritu në të male dhe ka bërë në mes deteve pendë (pengesë, për të mos u përzierë)? Vallë, a krahas Perëndisë, ekzistuaka zot tjetër?! Jo, por shumica e tyre nuk dinë.'),
(3221, 27, 62, 'Apo është (më i mirë) Ai, i cili i vie në ndihmë nevojtarit të ngushtë kur i lutet Atij dhe i cili ua largon të keqen dhe u bënë juve pronar (brez pas brezi) në Tokë? Vallë, a krahas Perëndisë – ekzistuaka zot tjetër?! Sa pak që po këshilloheni ju! –'),
(3222, 27, 63, 'Apo është (më i mirë) Ai, i cili ua rrëfen në errësirat e tokës dhe të detit dhe (Ai), i cili dërgon erërat si sihariq par mëshirës së Tij (shiut)? Vallë, a krahas Perëndisë – të ekzistojë zot tjetër?! Por, Perëndia është shumë lart nga ata, që i konsiderojnë shok Atij! –'),
(3223, 27, 64, 'Apo është (më i mirë) Ai, i cili ka krijuar të gjitha prej asgjëje, i cili, pastaj, përsëri do ta kthejë atë (pas vdekjes) – dhe (Ai) që ju furnizon nga qielli (me të reshura) dhe toka (me të lashta)? Vallë, a ekziston krahas Perëndisë, zot tjetër? Thuaj (o Muhammed): “Sillni argumentet tuaja, nëse thuani të vërtetën!”'),
(3224, 27, 65, 'Thuaj: “Askush, pos Perëndisë, as në qiell as në Tokë nuk di sekretet, ata nuk dinë se kur do të ringjallen.'),
(3225, 27, 66, 'Vallë, a dinë ndopak ata – për jetën tjetër?! Asgjë! – ata dyshojnë në të, ata janë të verbër ndaj saj.'),
(3226, 27, 67, 'Dhe, mohuesit thonë: “Vallë, a mos do të ringjallmi ne dhe etërit tanë, pasi të jemi bërë dhé?'),
(3227, 27, 68, 'Me të vërtetë, neve dhe etërve tanë na është premtuar kjo qysh më parë, këto, me të vërtetë, nuk janë tjetër, pos trillime të popujve të lashtë”.'),
(3228, 27, 69, 'Thuaj: “Udhëtoni nëpër botë – e shikoni si ka qenë fundi i mëkatarëve!”'),
(3229, 27, 70, 'Dhe, mos u pikëllo ti për shkak të tyre, dhe mos u ngushto ti – për pështjellimet e tyre.'),
(3230, 27, 71, 'Dhe, ata thonë: “Kur do të realizohet ky premtim (diftona) nëse thuani të vërtetën?”'),
(3231, 27, 72, 'Thuaj: “Do t’ju arrijë, së shpejti diçka nga ajo që po e nxitoni!'),
(3232, 27, 73, 'Dhe, me të vërtetë, Zoti yt është bamirës i madh i njerëzve, por shumica e tyre nuk janë falenderues.'),
(3233, 27, 74, 'Dhe, me të vërtetë, Zoti yt – di atë që fshehin zemrat e tyre dhe atë që e shfaqin haptazi.'),
(3234, 27, 75, 'S’ka asgjë të padukshme në qiell dhe në Tokë; e që të mos jetë e shënuar në Librin e qartë (Levhi Mahfudh).'),
(3235, 27, 76, 'Me të vërtetë, ky Kur’an, u tregon bijve të Israelit – shumë nga konfliktet e tyre.'),
(3236, 27, 77, 'dhe, me të vërtetë, ai (Kur’ani) është udhërrëfyes dhe mëshirë për besimtarët.'),
(3237, 27, 78, 'Me të vërtetë, Zoti yt i gjykon ata me drejtësinë e Tij. Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(3238, 27, 79, 'Andaj, ti mbështetu në Perëndinë! Me të vërtetë, ti je në të drejtën e qartë!'),
(3239, 27, 80, 'Me të vërtetë, ti nuk mund të bësh që të vdekurit të dëgjojnë, as nuk mund të bësh të shurdhërin që të dëgjojë, pasi që të kthejnë shpinën (prej teje),'),
(3240, 27, 81, 'as që mundesh t’i shmangësh të verbërit prej humbjeve të tyre; mund t’i thërrasish vetëm ata që besojnë në argumentet Tona. E ata janë muslimanë.'),
(3241, 27, 82, 'Dhe, kur të vie koha që të dënohen ata, Na do të bëjmë që nga Toka të del një kafshë, e cila do t’u thotë atyre se njerëzit nuk u janë bindur argumenteve Tona.'),
(3242, 27, 83, '(Kujtoju ti atyre) kur Na do t’i tubojmë prej çdo populli nga një grup – nga ata që i kanë përgënjeshtruar argumentet Tona – e ata do të radhiten –'),
(3243, 27, 84, 'e kur të vijnë (për të dhënë llogari), Ai (Perëndia), do t’i pyesë: “A mos vallë ju i keni përgënjeshtruar argumentet e Mia, e që nuk keni pasur kurrfarë njohurie për to? Ç’keni bërë ju ashtu?!”'),
(3244, 27, 85, 'Dhe do t’i godet ata dënimi, meqë kanë qenë zullumqarë, e nuk do të mund të bëzejnë asnjë fjalë.'),
(3245, 27, 86, 'Vallë, a nuk shohin ata, që Ne e kemi bërë natën për pushim, e ditën të ndritshme (për punë). Në këtë, me të vërtetë, ka argument për popullin që beson.'),
(3246, 27, 87, '(Përkujtoju ti atyre) Ditën kur do të fryhet në Sur, e që do të tmerrohet çdokush që gjendet në qiej e në Tokë, përpos atyre që Perëndia nuk do t’i frikësojë. Të gjithë do t’i vijnë Atij të përulur.'),
(3247, 27, 88, 'Ti i shikon malet e mendon se janë të palëvizshme, por ato lëvizin si retë. Kjo është vepër e Perëndisë, i cili e ka përsosur çdo gjë. Me të vërtetë, Ai di atë që punoni ju.'),
(3248, 27, 89, 'Kush punon vepër të mirë, do të ketë shpërblim të madh për të dh do të jetë i qetësuar atë Ditë nga frika.'),
(3249, 27, 90, 'E, kush punon vepër të keqe, fytyrat e tyre (ata) do të hudhen në zjarr. (Dhe u thuhet atyre): “Nuk jeni dënuar, përpos për atë që keni punuar!”'),
(3250, 27, 91, '(Thuaju atyre o Muhammed): “Mua më është urdhëruar që të adhuroj vetëm Zotin e këtij Qyteti (Mekkes), të cilin Ai e ka bërë të shenjtë dhe Atij i përket gjithçka. Dhe, më është urdhëruar që të jam prej atyre që besojnë –'),
(3251, 27, 92, 'dhe ta këndoj Kur’anin vazhdimisht. E kushdo që udhëzohet në rrugë, ti thuaj: “Ai është udhëzuar për dobi të vet”, e kushdo që humbë, ti thuaj: “Unë jam vetëm paralajmërues”.'),
(3252, 27, 93, 'Dhe, thuaj: “Falenderimi qoftë për Perëndinë, Ai do t’ju tregojë dokumentet e Tij, e ju do t’i njihni ato!” E Zoti yt nuk është i pakujdesshëm ndaj veprave që punoni ju.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'Tâ, Sín Mím.'),
(3254, 28, 2, 'Këto janë ajetet e Librit të qartë!'),
(3255, 28, 3, 'Na do të tregojmë ty disa lajme mbi Musain dhe Faraonin, ashtu siç ka qenë me të vërtetë, dhe këtë – për ata që besojnë.'),
(3256, 28, 4, 'Me të vërtetë, Faraoni është madhështuar në Tokë dhe banorët e tij i ka në grupacione (shtresa shoqërore); disa prej tyre i ka shtypur – ua mbyste fëmijët mashkuj, e femrat e tyre ua linte në jetë. Ai, me të vërtetë, ka qenë ngatërrestar.'),
(3257, 28, 5, 'Na kemi dashur t’u bëjmë mirësi atyre që kanë qenë të shtypur në Tokë, e t’i bëjmë udhëheqës ata dhe t’i bëjmë trashëgimtar (të pushtetit të Faraonit),'),
(3258, 28, 6, 'dhe t’ua mundësojmë atyre vendosjen në Tokë dhe t’jua tregojmë Faraoit, Hamanit dhe ushtrisë së tyre mu atë prej së cilës frikësoheshin ata.'),
(3259, 28, 7, 'Dhe, Na, e kemi inspiruar nënën e Musait: “Jepi gji, e kur të frikësohesh (për jetën e tij) hudhe në det (lumi Nil) dhe mos u frikëso (se do të mbytet), as mos u hidhëro; Na, me të vërtetë, do ta bëjmë Pejgamber”.'),
(3260, 28, 8, 'Dhe, familja e Faraonit, e përvetësoi ate, - e që një ditë – t’u bëhet armik dhe katastrofë e tyre. Me të vërtetë, Faraoni e Hamani dhe ushtria e tyre, kanë qenë mëkatarë.'),
(3261, 28, 9, 'Dhe gruaja e Faraonit tha: “Ky, do të jetë gëzimi im dhe i yti! Mos e mbytni, ndoshta do të na sjellë dobi, ose ta përvetësojmë për djalë”. E, ata, nuk e parandien përfundimin.'),
(3262, 28, 10, 'Dhe zemra e nënës së Musait mbeti e zbrazët (nga çdo gjë, pos kjtimit të djalit të saj). Dhe, gati që e zbuloi, sikur mos t’ia kishim forcuar Ne zemrën e saj, që të jetë njëra nga besimtaret (që beson, se premtimi i Perëndisë do të realizohet) –'),
(3263, 28, 11, 'Dhe, ajo i tha, motrës së atij: “Shko pas tij (fëmijës)!” dhe, ajo e vërejti anash (nga largësia), e ata nuk e hetonin.'),
(3264, 28, 12, 'E, Na ia kemi pasë ndaluar mëndeshat (gruaja që i jep gji fëmijës së huaj), Musait, e ajo (motra e Musait) tha: “A doni t’ju gjejë një familje, e cila do të kujdeset për të, duke ia dashur të mirën atij?”'),
(3265, 28, 13, 'Dhe, Na e kthyem atë – te nëna e vet, që të gëzohet e të mos pikëllohet, dhe që të dijë se premtimi i Perëndisë është i sigurtë, por, shumica e njerëzve nuk e dinë.'),
(3266, 28, 14, 'Dhe, kur ai – arriti moshën madhore dhe u zhvillua, Na i dhamë atij mençuri dhe dijeni. Kështu, Na i shpërblejmë bamirësit.'),
(3267, 28, 15, 'Dhe, Musai hyri në qytet, pa u hetuar nga banorët e tij – dhe në të, hasi dy njerëz që rriheshin. Njëri ishte nga grupi i tij, e tjetri nga grupi i armikut. E, i kërkoi ndihmë ai – i grupit të tij kundër atij të popullit armik. Dhe Musai i ra grusht atij dhe e mbyti (Musai) tha: “Kjo është punë e djallit! Ai, me të vërtetë, është armik – ngatërrestar i qartë!'),
(3268, 28, 16, '(Musai) tha: “O Zoti im! Unë me të vërtetë, i kam bërë zullum vetvetes, andaj më fal!” – dhe Ai e fali. Me të vërtetë, Ai është falës e mëshirues.'),
(3269, 28, 17, '(Musai) tha: “O Zoti im! Pasha të mirat që m’i ke dhuruar, unë nuk do të bëhem ndihmës i të këqinjëve!”'),
(3270, 28, 18, 'Dhe Musai – u zgjua i frikësuar në qytet, duke pritur se ç’do të nodhë kur që – ia behi ai, që dje e kishte thirrë në ndihmë, e që përsëri – e thirri në ndihmë. Musai i tha: “Ti, me të vërtetë, je ngatërrestar!”'),
(3271, 28, 19, 'Dhe, kur deshi të sulmojë (Musai), atë që ishte armik i të dyve, i tha ai (israeliti) se po e qëllon atë: “O Musa, a don të më mbytësh mua siç e mbyte dje njeriun? Ti dëshiron të bëhesh vetëm dhunues në tokë, e nuk dëshiron të bëhesh nga ata që mbajnë rregull”.'),
(3272, 28, 20, 'Dhe një njeri, erdhi, duke nxituar prej skajit të qytetit, e tha: “O Musa, paria po konsultohen për me të mbytur ty; andaj dil, se unë për ty – jam këshillues i sinqertë”.'),
(3273, 28, 21, 'E, doli prej tij (Egjiptit) i frikësuar, duke pritur se ç’do të ndodhë. Tha (Musai): “O Zoti im, më shpëto prej populli zullumqarë!”'),
(3274, 28, 22, 'Dhe, kur u nis kah Medjeni, ai tha: “Shpresoj se Zoti im do të më tregojë rrugën e drejtë!”'),
(3275, 28, 23, 'E, kur arriti tek uji i Medjenit, hasi rreth tij shumë njerëz, që u jepnin ujë kafshëve. E përveç tyre, vërejti dy vasha që i pengonin kafshët e veta (të pinin ujë). Ai (Musai) tha: “Ç’është me ju?” Ato thanë: “Na, nuk u japim ujë, derisa të mos shkojnë barinjtë, e babën e kemi fort plak”.'),
(3276, 28, 24, 'E, Musai – u dha ujë kafshëve të atyre vashave, e pastaj u kthye te një hije (e lisit) dhe tha: “Zoti im çfarëdo dhuntie që të më japësh, unë jam nevojtar!”'),
(3277, 28, 25, 'Dhe, njëra nga ato dyja, erdhi pastaj, duke u turpëruar dhe tha: “Me të vërtetë, të thërret babai im që të shpërblejë ty, për atë që u ke dhënë ujë shtazëve tona. E, kur erdhi te Shuajbi dhe i tregoi çka ka përjetuar, ai (Shuajbi) tha: “Mos u frikëso, meqë ke shpëtuar prej popullit zullumqar!'),
(3278, 28, 26, 'O babai im, - tha njëra nga ato dyja – “merre këtë njeri në shërbim, se – me të vërtetë, i forti dhe i besueshmi, është më i mirë që ta marrësh në shërbim”.'),
(3279, 28, 27, '(Shuajbi) tha: “Unë dua që të jap njërën prej këtyre dy vashave të mia, por që të më shërbesh mua tetë vjet; e nëse i plotëson dhjetë vjet (shërbim), ajo është prej të mirës (nderës) tënde, e unë nuk dëshiroj të shkaktoj vështirësi ty. Ti do të gjesh (shohësh) – në dashtë Zoti, se jam prej të mirëve”.'),
(3280, 28, 28, '(Musai) tha: “Le të jetë kështu ajo, në mes meje dhe teje. Cilindo afat prej këtyre dyve ta plotësoj, nuk do të mund të më bëhet vërejtje. E, Perëndia është mbikëqyrës për atë që folëm”.'),
(3281, 28, 29, 'Kur Musai e plotësoi afatin e caktuar dhe u nis me familjen e vet, vërejti një zjarr në anën e (malit të) Turit. I tha familjes së vet: “Pritni! Me të vërtetë, kam vërejtur një zjarr. Shpresoj se do t’ju sjelli një lajm (për orientim të rrugës), ose një un të zjarrit, për t’u nxehur ju”.'),
(3282, 28, 30, 'E, kur erdhi tek ai (zjarri), u thirr nga ana e djathtë (begatshëm) e luginës – prej lisit, në visin e bekuar: “O Musa, me të vërtetë, Unë jam Perëndia, Zoti i gjithësisë!'),
(3283, 28, 31, 'Dhe, hudhe shkopin tënd!” Kur e hodhi atë dhe e pa se po lëvizë shpejt, sikur të ishte gjarpër, u zmbraps me të shpejtë e nuk u kthye (të shikojë pas). “O Musa! Afrohu dhe mos u friko! Me të vërtetë, ti je nga ata, që janë të mbrojtur.'),
(3284, 28, 32, 'Fute dorën tënde në xhepin e krahërorit – do të paraqitet e bardhë, pa të meta. Dhe, kështu, përmbaju prej frikës! Këto janë dy argumente prej Zotit tënd te Faraoni dhe paria e tij; ata, me të vërtetë janë popull i korruptuar!”'),
(3285, 28, 33, '(Musai) tha: “O Zoti im, unë, me të vërtetë, e kam mbytur një njeri të tyre, prandaj frikoj se do të më mbysin.'),
(3286, 28, 34, 'E, vëllai im Haruni, është më i zhdërvjelltë se unë – në të folur, andaj dërgoje me mua atë, si ndihmës timin, që t’i vërtetoj fjalët e mia. Unë, me të vërtetë, frikohem se ata do të më quajnë gënjeshtar”.'),
(3287, 28, 35, '(Perëndia) tha: “Na të forcojmë ty me vëllaun tënd dhe që të dyve do t’ju japim pushtet; e ata, nuk do të mund t’ju afrohen juve (me qëllim të keq); me argumentet Tona, ju dy dhe ata që do t’ju pasojnë, do të jeni fitues”.'),
(3288, 28, 36, 'Dhe kur Musai u solli argumentet Tona të qarta, ata thanë: “Kjo është vetëm magji e trilluar, dhe ne nuk kemi dëgjuar këtë prej baballarëve tanë”.'),
(3289, 28, 37, 'E, Musai tha: “Zoti im e di mirë atë që i sjell udhëzimet prej tij dhe atë, që do ta ketë mbarimin e mirë; e me të vërtetë, mohuesit nuk do të shpëtojnë.'),
(3290, 28, 38, 'Dhe, Faraoni tha: “O parësi, unë nuk di që për ju ka tjetër zot përveç meje, e ti, o Haman (ministër i Faraonit), m’i pjek qerpiqtë dhe ma ndërto godinën e lartë që të ngritem te zoti i Musait, meqë, unë – atë e konsideroj për gënjeshtar!”'),
(3291, 28, 39, 'Dhe, u madhështua ai dhe ushtria e tij në tokë pa kurrfarë të drejte dhe mendonin se nuk do të kthehen te Ne.'),
(3292, 28, 40, 'E, Na e dënuam atë dhe ushtrinë e tij dhe i hodhëm në det, andaj shiko, si qe fundi i zullumqarëve!'),
(3293, 28, 41, 'E, i kemi pasë bërë udhëheqës, që thirrnin në atë që shpie në zjarr; e në Ditën e Kijametit nuk do të ndihmohen.'),
(3294, 28, 42, 'Dhe Na – i kemi përcjellë ata në këtë jetë, me mallkim, e në Ditën e Kijametit do të jenë të keqtrajtuar.'),
(3295, 28, 43, 'Na, me të vërtetë, ia kemi dhënë Musait Librin pasi i kemi shkatërruar popujt e lashtë, që të jetë dritë për njerëzit dhe udhëzim e mëshirë – për t’u këshilluar ata.'),
(3296, 28, 44, 'Dhe ti (o Muhammed!) nuk ke qenë në anën perëndimore (të Turit), kur Ne ia besuam Musait profetninë dhe ti nuk ishe dëshmitar (i kësaj ngjarjeje).'),
(3297, 28, 45, 'Por, Ne kemi krijuar shumë gjenerata e ka kaluar një kohë e gjatë. E, ti – nuk ke qëndruar në mesin e banorëve të Medjenit, që t’ju lexosh (Mekkasve) argumentet Tona, por Ne t’i kemi dërguar ty (lajmet e këtyre mrekullive).'),
(3298, 28, 46, 'Dhe, ti nuk ke qenë pranë Turit, kur Na e kemi thirrë (Musain), por (të kemi dërguar ty) nga mëshira e Zotit tënd për t’i paralajmëruar njerëzit, të cilëve nuk u ka ardhur kurrfarë paralajmëruesi para teje, për t’u këshilluar ata,'),
(3299, 28, 47, 'dhe, që të mos thonë kur t’i godas dënimi, për atë që kanë bërë: “O Zoti ynë, të na kishe dërguar pejgamber që t’i pasojmë argumentet Tuaja dhe të bëhemi besimtarë.'),
(3300, 28, 48, 'Pasi që u erdhi e Vërteta prej Nesh, thanë ata (arabët): “Sikur t’ju kishte dhënë (Muhammedit) diçka e ngjashme siç i është dhënë Musait!” Vallë, a nuk e mohuan ata – atë që iu kishte dhënë Musait më parë? Ata kanë thënë: “Këto janë dy magji (Teurati dhe Kur’ani), që ndihmohen reciprokisht”. Dhe, (Mekkasit) thanë: “Na, nuk besojmë në asnjërën”.'),
(3301, 28, 49, 'Thuaju atyre: “Sillni një libër nga Perëndia, i cili udhëzon më mirë nga këta të dy (Teurati dhe Kur’ani), unë do të pasoj atë, nëse thoni të vërtetën!”'),
(3302, 28, 50, 'E nëse ata nuk të përgjegjen ty, dije se ata ndjekin vetëm pasionin e vet. E, kush vallë, ka humbur më tepër se ai që ndjek pasionin e vet, pa udhëzim prej Perëndisë? Perëndia, me të vërtetë, nuk e udhëzon popullin zullumqarë.'),
(3303, 28, 51, 'Na, ua dërgojmë atyre fjalët njërën pas tjetrës (Kur’anin), për t’u këshilluar ata.'),
(3304, 28, 52, 'Atyre që Ne ua kemi dhënë Librin para tij (Kur’anit), ata i besojnë atij,'),
(3305, 28, 53, 'dhe, kur t’ju lexohet atyre (Kur’ani), ata thonë: Ne e besojmë atë. Me të vërtetë, ai (Kur’ani) është (i zbritur) nga Zoti ynë. Na – edhe më parë kemi qenë muslimanë”.'),
(3306, 28, 54, 'Atyre do t’u jepet shpërblimi i dyfishtë, meqë kanë duruar dhe që të keqen e kthejnë me të mirë – dhe ndajnë nga ajo që u kemi dhënë Ne;'),
(3307, 28, 55, 'e kur të dëgjojnë ndonjë marrëzi, shmangen prej saj dhe thonë: “Veprat tona janë për ne e veprat tuaja janë për ju; paqa qoftë mbi ju (lamtumirë!) Na nuk duam shoqëri me të padishmit (xhahilat)”.'),
(3308, 28, 56, 'Ti (o Muhammed), me të vërtetë, nuk mund ta udhëzosh (në formë imponuese) në rrugën e drejtë, atë që dëshiron ti, - por Perëndia e udhëzon (në të gjitha normat). Ai, i di më së miri ata që janë të udhëzuar.'),
(3309, 28, 57, 'Dhe ata thonë: “Nëse ne – me ty ndjekim rrugën e drejtë (islamizmin), shpejt do të jemi të dëbuar nga vendlindja”. Vallë, a nuk u kemi dhënë rast Na – atyre, që të qëndrojnë në vendin e shenjtë dhe të sigurtë, ku – sillen frytet e llojllojshme – ushqim prej Nesh; por, shumica e tyre nuk e dinë.'),
(3310, 28, 58, 'E, sa e sa fshatra e qytete i kemi zhdukur Ne, banorët e të cilëve kanë bërë jetë të shfrenuar! Ja, shtëpiat e tyre (të shkretëruara), të cilat, nuk janë banuar pas tyre – vetëm pak kohë, dhe Ne kemi qenë trashëgimtarë.'),
(3311, 28, 59, 'Dhe, Zoti yt – kurrë nuk i ka shkatërruar vendbanimet, para se t’u dërgojë pejgamber në kryeqytetin e tyre, i cili ju ka treguar argumentet Tona. Dhe, Ne – kurrë nuk i kemi shkatërruar vendbanimet, por vetëm kur banorët e tyre kanë qenë zullumqarë.'),
(3312, 28, 60, 'Çdo gjë që ju është dhënë juve, ajo është kënaqësi e kësaj jete dhe stoli e saj, e ajo që është te Perëndia, ajo është më e mirë dhe më e përhershme. Vallë, a nuk po mendoni ju?'),
(3313, 28, 61, 'Si mund të jetë i njëjtë ai, të cilit i kemi premtuar shpërblim të mirë, e ai do ta arrijë atë dhe atij që i kemi dhënë kënaqësi në jetën e kësaj bote, e i cili pastaj, në Ditën e Kijametit, do të gjendet në zjarr?'),
(3314, 28, 62, '(Dhe, kujtoju o Muhammed!) Ditën, kur do t’i thirrë (Perëndia) ata dhe u thotë: “Ku janë ata që ju i konsideronit për shokë të Mi?”'),
(3315, 28, 63, 'Do të thonë ata, te të cilët është sendërtuar fjala (Jonë), O Zoti ynë, këta jën ata, që na i kemi zhgënjyer, - e i kemi zhgënjyer ata, ashtu siç kemi qenë edhe ne të zhgënjyer. Na heqim dorë prej tyre te Ti; ata nuk na kanë adhuruar neve”.'),
(3316, 28, 64, 'Dhe, do t’u thuhet atyre: “Thirrni hyjnitë tuaja! E, ata do t’i thirrin ato, por ato nuk do t’ju përgjigjen (atyre). E, do ta shohin dënimin. Sikur të kishin qenë në rrugën e drejtë, (nuk do të kishin përjetuar dënimin)!'),
(3317, 28, 65, '(Dhe, kujtoju o Muhammed!) Ditën kur do t’i thirrë (Perëndia) ata dhe u thotë: “Ç’ju keni përgjegjur pejgamberëve?”'),
(3318, 28, 66, 'Atë ditë, atyre lajmet do t’u jenë memece (të shurdhëta); e ata nuk do të mund të pyesin njëri-tjetrin (asgjë).'),
(3319, 28, 67, 'E, ai që pendohet, e beson dhe punon vepra të mira, do të jetë nga të shpëtuarit.'),
(3320, 28, 68, 'Dhe, Zoti yt krijon ç’të dojë dhe Ai zgjedh; e, ata (adhuruesit e idhujve), nuk mund të zgjedhin. Qoftë i falenderuar Perëndia dhe larg nga ato që i përshkruhen Atij për shok!'),
(3321, 28, 69, 'Dhe, Zoti yt, di çka fshehin zemrat e tyre, e çka shprehin ata.'),
(3322, 28, 70, 'Dhe, Ai është Perëndia! S’ka zot tjetër përveç Tij! Për Te është falenderimi në këtë jetë dhe në tjetrën! Për Te është vendimi (për çdo fjalë) dhe tek Ai – do të ktheheni.'),
(3323, 28, 71, 'Thuaju (o Muhammed!): “Çka mendoni ju, nëse Perëndia ua bënë natën të përhershme deri në Ditën e Kijametit, cili zot – pos Perëndisë, do t’ua sillte dritën? Vallë, a nuk po dëgjoni ju?”'),
(3324, 28, 72, 'Thuaju (o Muhammed!): “Çka mendoni ju nëse Perëndia ua bënë ditën të përhershme deri në Ditën e Kijametit, cili zot – pos Perëndisë, do t’ua sjellë natën, në të cilën qetësoheni ju? Vallë, a nuk po shihni ju?'),
(3325, 28, 73, 'Dhe prej mëshirës së Tij, ua ka bërë juve natën dhe ditën, që të qetësoheni në të dhe të kërkoni nga dhuntitë e Tij, - dhe që ju të jeni mirënjohës”.'),
(3326, 28, 74, 'Dhe (kujtoju) Ditën kur i thërret (Perëndia) dhe u thotë: “Ku janë ata që ju i konsideronit për shokë të Mi?”'),
(3327, 28, 75, 'Dhe, Ne, prej çdo populli do të sjellim nga një dëshmitar e do të themi: “Paraqitni argumentet tuaja!” Ata do ta dinë se e vërteta është (vetëm) e Perëndisë, e do të humbin ato që i trillonin ata.'),
(3328, 28, 76, 'Me të vërtetë, Karuni ka qenë nga populli i Musait, por që ai (Karuni) i eksploatoi ata. E, Ne, i patëm dhënë atij aq thesar – saqë çelësat e tyre vështirë do të mund t’i bartin një grup njerëzish të fuqishëm. (Kujtoje ti, o Muhammed!) kur njerëzit e popullit të tij, i thanë (Karunit): “Mos u shfreno (me pasuri) Perëndia, me të vërtetë, nuk i do ata që shfrenohen!'),
(3329, 28, 77, 'Dhe, kërko ti jetën e botës së ardhshme me atë që të ka dhënë Perëndia, e mos harro pjesën tënde në këtë botë. Bënu mirë të tjerëve ashtu si të ka bërë ty mirë Perëndia. Mos kërko ngatërresa në Tokë! Me të vërtetë, Perëndia nuk i do ngatërrestarët”.'),
(3330, 28, 78, 'Ai (Karuni) tha: “Kjo më është dhënë vetëm në bazë të dijenisë sime”. E, vallë, a nuk e di ai, se Perëndia i ka zhdukur shumë brezni para tij, të cilët kanë qenë më të fuqishëm dhe që patën grumbulluar më shumë se ky. Dhe, njerëzit e këqinj, as që do të pyeten për arsyetimin e mëkatëve të tyre.'),
(3331, 28, 79, 'E, doli ai (Karuni) para popullit të vet me shkëlqimin (salltanatin) e tij. E, thanë ata që lakmonin jetën e kësaj bote: “Ah! sikur të na ishte dhënë edhe neve si Karunit! Ai, (me të vërtetë), ka fat të madh”.'),
(3332, 28, 80, 'E, njerëzit e dijshëm thonin: “Mjerë për ju! Shpërblimi i Perëndisë është më i mirë për atë që beson dhe punon vepra të mira, e do t’u jepet ky (shpërblim) vetëm të durueshmëve”.'),
(3333, 28, 81, 'Dhe Ne, e kemi fundosë atë (Karunin) dhe shtëpinë e tij në tokë, dhe askush nuk ka mund t’i ndihmojë atij nga dënimi i Perëndisë, e as që ka mund të mbrohet (prej dënimit të Perëndisë).'),
(3334, 28, 82, 'E, ata, që më parë dëshironin të jenë në pozitën e tij, thoshin: “Ububu! Po duket se Perëndia, me të vërtetë, i jep bollëk kujt të dojë prej robërve të Tij dhe ia mungon (kujt të dojë). Sikur Perëndia të mos na kishte bërë dhunti neve, me të vërtetë, do të na kishte fundosë. Ububu! (po duket se) me të vërtetë, mohuesit nuk kanë shpëtim!”'),
(3335, 28, 83, 'Atë shtëpinë e botës tjetër, Na do ta bëjmë për ata që nuk dëshirojnë madhështi e as ngatërresa në Tokë. E, përfundimi (i mirë) është për ata që ruhen nga të këqiat.'),
(3336, 28, 84, 'Kush bënë një të mirë, për te ka shpërblim më të mirë (se atë që ka punuar). E, ai që bën një të keqe, ai dënohet vetëm aq sa e meriton vepra e tij.'),
(3337, 28, 85, 'Me të vërtetë, Ai që ta ka shpallë ty Kur’anin, do të kthejë ty në vendin që të është premtuar. Thuaj: “Zoti im di më së miri kush ka ardhur me udhëzim të drejtë, e kush është në humbje të plotë.'),
(3338, 28, 86, 'Ti nuk ke shpresuar se do të dërgohet Libri (Kur’ani), përveç mëshirës nga Zoti yt (të është shpallë ty); andaj, kurrsesi mos iu bënë ndihmës mohuesve!'),
(3339, 28, 87, 'Dhe, kurrsesi – le të mos pengojnë ty (ata) prej ajeteve të Perëndisë, pasi që të janë shpallur ty! Lutju Zotit tënd eh kurrsesi mos u bënë prej idhujtarësh!'),
(3340, 28, 88, 'Dhe, mos adhuro – pos Perëndisë, zot tjetër! S’ka zot tjetër përveç Tij! Çdo gjë, pos Tij, është e destinuar të shkatërrohet! Për Te është gjykimi dhe tek Ai është kthimi!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'Elif, Lám, Mîm.'),
(3342, 29, 2, 'mendojnë njerëzit se do të lihen (të qetë) nëse thonë: “Na kemi besuar! E që ata nuk do të vihen në sprovë?'),
(3343, 29, 3, 'E, me të vërtetë, Ne i kemi sjellë në sprovë, ata që kanë qenë para tyre, e Perëndia do të tregojë për ata që thonë të vërtetëen dhe për ata që gënjejnë.'),
(3344, 29, 4, 'A mos mendojnë ata që bëjnë vepra të këqia – se do t’i shpëtojnë (dënimit Tonë)? Sa keq po gjykojnë ata!'),
(3345, 29, 5, 'Kush shpreson takimin te Perëndia – me të vërtetë do të arrijë Dita e caktuar. E Ai, i dëgjon dhe i di të gjitha.'),
(3346, 29, 6, 'Kush përpiqet (në rrugën e Perëndisë), përpiqet vetëm për të mirën e vetvetes, se Perëndia, me të vërtetë, është i pavarur prej çdo gjëje.'),
(3347, 29, 7, 'E, ata që besojnë dhe punojnë vepra të mira, me siguri do t’u shlyejmë të këqiat e tyre dhe do t’i shpërblejmë ata më mirë se punën që kanë bërë.'),
(3348, 29, 8, 'Na e kemi obliguar çdo njeri, që t’u bëjë mirë prindërve të vet. Por nëse ata të nxisin ty që të më bësh Mua shok, për të cilin ti nuk di asgjë, atëherë mos i dëgjo ata. Tek Unë do të ktheheni ju, e do t’ju lajmëroj Unë juve për atë që keni punuar.'),
(3349, 29, 9, 'Dhe ata që kanë besuar dhe kanë bërë punë të mira, me siguri do t’i radhisim në mesin e të mirëve.'),
(3350, 29, 10, 'E, ka disa njerëz që thonë: “Na besojmë Perëndinë”. E, kur të sillet në shqetësim për Perëndinë, ai e konsideron sprovimin e njeriut – ashtu si dënimin e Perëndisë. E, kur të vijë ndihma prej Zotit tënd, ata, me siguri – do të thonë: “Na kemi qenë me ju”. Vallë! A nuk e di Perëndia më së miri se ç’ka në zemrat e çdokujt?'),
(3351, 29, 11, 'Perëndia, me siguri, do t’i tregojë ata që kanë besuar, dhe do t’i tregojë hipokritët.'),
(3352, 29, 12, 'Dhe mohuesit u thonë besimtarëve: “Ndiqni rrugën tonë, e ne do t’i bartim gabimet tuaja!” Por, ata nuk janë bartës të asnjë gabiti të tyre, ata, me të vërtetë, janë gënjeshtarë.'),
(3353, 29, 13, 'Ata (mohuesit), do t’i bartin brengat e veta dhe bashkë me bregat e tyre, edhe brengat tjera – dhe ata, me të vërtetë, do të pyetën Ditën e Kijametit për atë që trillonin.'),
(3354, 29, 14, 'Ne e kemi dërguar Nuhun te populli i tij – e ai qëndroi në mesin e tyre një mijë pa pesëdhjetë vjetë, e i përfshiu ata përmbytja (nga uji), meqë ishin zullumqarë.'),
(3355, 29, 15, 'Dhe Ne, e shpëtuam atë dhe ata që u gjenden në barkë, dhe e bëmë dokument (të fuqisë Sonë) për të gjithë.'),
(3356, 29, 16, 'Dhe (kujtoje) Ibrahimin kur i pat thënë popullit të vet: “Adhuronie Perëndinë dhe druajuni Atij. Kjo është më së miri për ju, nëse e dini.'),
(3357, 29, 17, 'Ju, pos Perëndisë, me të vërtetë, po adhuroni idhuj dhe po shpifni gënjeshtra. Me të vërtetë, ata që po i adhuroni ju pos Perëndisë, nuk kanë pushtet t’ju furnizojnë juve, andaj, te Perëndia kërkoni furnizimin dhe adhuronie Atë dhe falenderohuni Atij. Tek Ai do të ktheheni.'),
(3358, 29, 18, 'E, nëse ju më konsideroni për gënjeshtar, madje edhe popujt e mëparshëm i kanë konsideruar për gënjeshtar (pejgamberët e vet). E, pejgamberi është i obliguar vetëm që të shpall qartazi.'),
(3359, 29, 19, 'Vallë, a nuk vërejtën ata se si Perëndia i zu fill krijesat prej asgjëje, e pastaj përsëri i kthen në jetë (pas vdekjes). Kjo, me të vërtetë, për Perëndinë është lehtë.'),
(3360, 29, 20, 'Thuaj: “Udhëtoni nëpër Tokë (Botë) dhe shikoni se si i ka zënë Ai fillin krijesave (prej asgjëje), e pastaj Perëndia u jep gjallëri për të dytën herë. Me të vërtetë, Perëndia është i pushtetshëm për çdo gjë.'),
(3361, 29, 21, 'Ai ndëshkon atë që don Ai dhe e mëshiron kë të dojë Ai, dhe te Ai do të ktheheni.'),
(3362, 29, 22, 'Ndëshkimit të Tij nuk mund t’i shpëtoni as në Tokë as në qiell, e pos Perëndisë, nuk keni as mbrojtës as ndihmës.'),
(3363, 29, 23, 'E, ata që mohojnë ajetet e Perëndisë dhe takimin e Tij, të atillët e kanë humbur shpresën në mëshirën Time dhe për ta do të ketë dënim të dhembshëm”.'),
(3364, 29, 24, 'E, populli i tij, nuk patën kurrfarë përgjegje, përveç se thanë: “Mbyteni atë ose digjeni!” – por, Perëndia e shpëtoi atë nga zjarri. Këto janë, në të vërtetë argumente për popullin që beson.'),
(3365, 29, 25, 'Dhe, (Ibrahimi) tha: “Ju, me të vërtetë, keni marrë, pos Perëndisë, (adhurimin) e idhujve, për shkak të dashurisë në mes jush – në këtë jetë. E, në Ditën e Kijametit do ta mohoni njëri-tjetrin. Dhe strehimorja juaj është zjarri. Dhe s’ka për ju kurrfarë ndihmësi.'),
(3366, 29, 26, 'E, Luti i besoi atij (Ibrahimit) dhe (Ibrahimi) tha: “Unë do të emigrojë te Zoti im; me të vërtetë, Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(3367, 29, 27, 'Dhe, Ne ia kemi dhuruar Is’hakun dhe Jakubin dhe pasardhësve të tij u kemi dhënë profetninë dhe Librin, e i kemi dhënë atij shpërblimin në këtë botë. Me të vërtetë, ai edhe në jetën tjetër, do të jetë prej njerëzve të mirë:'),
(3368, 29, 28, 'Dhe, (kujtoje), Lutin, kur i tha popullit të vet: “Ju, me të vërtetë, bëni so marrëzirash çfarë askush para jush nuk ka bërë në botë:'),
(3369, 29, 29, 'bëni marrëdhënie (seksuale) me meshkuj, pengoni (ndërpreni) rrugët (me të këqia) dhe në tubimet tuaja bëni punët më të ndyta”. E, populli i tij, nuk patën kurrfarë përgjegje, përveç se thanë: “Hajt, sillna dënimin e Perëndisë nëse thua të vërtetën!”'),
(3370, 29, 30, 'Tha (Luti): “O Zoti im, më ndihmo kundër popullit ngatërrestarë!”'),
(3371, 29, 31, 'Dhe, kur të dërguarit Tonë (engjëjt), i sollën sihariq Ibrahimit, thanë: “Na do t’i zhdikim banorët e këtij vendbanimi, sepse, banorët e tij janë zullumqarë”.'),
(3372, 29, 32, 'Tha (Ibrahimi): “Me të vërtetë, në mesin e tyre është Luti”. Ata thanë: “Na e dimë mirë atë që është në mesin e tyre, - Na do ta shpëtojmë atë dhe familjen e tij, përpos gruas së tij, ajo do të jetë me ata që janë për dënim”.'),
(3373, 29, 33, 'E, kur të dërguarit Tonë, i erdhën Lutit, u shqetësua ai për ardhjen e tyre e u shtangua. Dhe ata i thanë: “Mos u friko dhe mos u brengos! Na do të shpëtojmë ty dhe familjen tënde, pos gruas tënde. Ajo do të jetë me ata që janë për dënim.'),
(3374, 29, 34, 'E, me të vërtetë, mbi banorët e këtij vendbanimi, do të lëshojmë dënimin nga qielli, ngase ata janë të korruptuar.'),
(3375, 29, 35, 'Dhe, prej tij kemi lënë një dokument të dukshëm për popullin që mendon.'),
(3376, 29, 36, '(I kemi dërguar) edhe Medjenit vëllaun e tij Shuajbin, e ai ka thënë: “O populli im, adhuronie Perëndinë, shpresoni në Ditën e Kijametit (shpëtimin) dhe mos bëni keq në tokë, si ngatërrestarë!”'),
(3377, 29, 37, 'Por, ata, e përgënjeshtruan atë, e i goditi ata tërmet i tmerrshëm dhe ata gdhinë në shtëpitë e tyre të vdekur e të gjunjëzuar.'),
(3378, 29, 38, '(Dhe kujtoje) Adin dhe Themudin, e ju e kishit të qartë (çka ka ndodhur) me shtëpitë e tyre. Atyre, djalli ua ka zbukuruar veprat e tyre, e madje i ka shmangur nga rruga e drejtë, edhe pse kanë qenë largpamës.'),
(3379, 29, 39, 'Dhe, (kujtoje) Karunin dhe Faraonin dhe Hamanin! Me të vërtetë, atyre u ka ardhur Musai me dokumente të qarta, e që ata u mendjemadhësuan në Tokë dhe nuk i shpëtuan dënimit.'),
(3380, 29, 40, 'Ne të gjithë i kemi dënuar për mëkatin e vet. Ne, disave u kemi dërguar fortunën dhe disa të tjerë i ka goditur zëri i tmerrshëm, ndërsa disa prej tyre i kemi sharruar në tokë; e disa prej tyre i kemi fundosë në ujë. E, Perëndia nuk u ka bërë atyre zullum, por ata vetes i kanë bërë zullum.'),
(3381, 29, 41, 'Shembulli i atyre që kanë marrë ndihmës, pos Perëndisë, - është si shembulli i marimangës, e cila ka ndërtuar për vete shtëpinë (për t’u ruajtur), e shtëpia më e dobët është rrjeti i marimangës. Sikur ta dinin (këtë) ata!'),
(3382, 29, 42, 'Me të vërtetë, Perëndia i di (të gjitha) ato që i adhurojnë ata, pos Atij; Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(3383, 29, 43, 'Dhe, këta shembuj – Ne u paraqesim njerëzve, por këta nuk i kupton kush, pos dijetarëve.'),
(3384, 29, 44, 'Perëndia ka krijuar qiejt dhe Tokën me qëllim të plotë; në këtë, me të vërtetë, ka argument për besimtarët!'),
(3385, 29, 45, 'Lexo Librin (Kur’anin) që të është shpallur ty dhe kryeje namazin! Namazi, me të vërtetë, të largon (pengon) nga korrupcioni dhe nga çdo vepër e shëmtuar. E, të përmendurit e Perëndisë është më e madhja (lutje)! – E, Perëndia di çka punoni ju.'),
(3386, 29, 46, 'Dhe, polemizoni me ithtarët e Librit në mënyrën më të bukur, por jo me zullumqarët e tyre. Dhe, thuani ju: “Na besojmë në atë që na është shpallur neve dhe në atë që u është shpallur juve. Edhe Zoti ynë edhe Zoti juaj është një – dhe na Atij i përulemi”.'),
(3387, 29, 47, 'Dhe, kështu Na ta kemi shpallur ty Librin (Kur’anin). E, atyre që u kemi shpallur Librin (Teuratin), besojnë në këtë (Kur’an). E, disa (nga Mekkasit) besojnë në të (Kur’an), e ajetet Tona i mohojnë vetëm mohuesit.'),
(3388, 29, 48, 'Dhe ti – para (Kur’anit) nuk ke lexuar kurrfarë libri, e as që ke shkruar me dorën tënde, (se) atëherë, do të dyshonin të degjeneruarit.'),
(3389, 29, 49, 'Jo! Ai (Kur’ani) – është me argumente të qarta në zemrat e tyre që u është dhënë dija, e argumentet Tona i mohojnë vetëm zullumqarët –'),
(3390, 29, 50, 'dhe thonë: “Përse (Muhammedit) nuk i janë zbritur mrekulli nga Zoti i tij?” Thuaj: “Mrekullitë janë vetëm te Perëndia, e unë jam vetëm paralajmërues i qartë”.'),
(3391, 29, 51, 'Vallë, a nuk u mjafton atyre që Ne ta kemi shpallur ty Librin, i cili u lexohet atyre? Me të vërtetë në te ka mëshirë dhe këshillë për popullin që beson.'),
(3392, 29, 52, 'Thuaj: “Mjafton Perëndia dëshmitar mes meje dhe jush. Ai di çka ka në qiej dhe në Tokë. E, ata që besojnë në idhuj (trillime), e nuk besojnë në Perëndinë, ata janë të humbur.'),
(3393, 29, 53, 'Dhe, ata kërkojnë prej teje shpejtimin e dënimit. E, sikur të mos ishte afati i caktuar, atyre do t’u kish arritur dënimi. Dhe, me siguri, atyre, do t’u vie papritmas e nuk do ta hetojnë.'),
(3394, 29, 54, 'Ata kërkojnë prej teje shpejtimin e dënimit; e, me të vërtetë, xhehennemi do t’i përfshijë të gjithë mohuesit –'),
(3395, 29, 55, 'në Ditën kur t’i kaplojë dënimi mbi ta dhe nën këmbët e tyre. Dhe, (engjëlli) thotë: “Shijoni dënimin për atë që keni bërë!”'),
(3396, 29, 56, 'O robërit e Mi që keni besuar, me të vërtetë, Toka Ime është e gjerë, andaj më adhuroni vetëm Mua!'),
(3397, 29, 57, 'Çdokush do të shijojë vdekjen dhe pastaj do të ktheheni tek Unë.'),
(3398, 29, 58, 'Na, ata që besojnë dhe bëjnë vepra të mira, do t’i vendosim në pallatet e xhennetit, nën të cilët rrjedhin lumenjtë, aty do të qëndrojnë përgjithmonë. Sa i bukur është shpërblimi i punëtorëve.'),
(3399, 29, 59, 'të cilët kanë duruar dhe janë mbështetur te Zoti i tyre'),
(3400, 29, 60, 'E, sa shtazë ka, të cilat nuk e bartin me vete furnizimin. Perëndia i furnizon ato dhe juve! Ai, i dëgjon të gjitha dhe i di të gjitha.'),
(3401, 29, 61, 'E, nëse i pyet ti ata: “Kush i ka krijuar qiejt dhe Tokën dhe kush e nënshtroi Diellin e Hënën?” – ata, me siguri, do të thonë: “Perëndia!” E, kah po arratisen atëherë?'),
(3402, 29, 62, 'Perëndia, ia zgjëron furnizimin kujt të dojë nga robërit e Tij, e dikujt ia zvogëlon. Perëndia, me të vërtetë, është i dijshëm për çdo gjë.'),
(3403, 29, 63, 'E, nëse ti i pyet ata: “Kush e ka zbritur ujin nga qielli, e me te e gjallëron tokën e shkretëruar?” Me siguri, do të thonë ata: “Perëndia!” Thuaj: “Falenderimi qoftë për Perëndinë!” Por, shumica e tyre nuk mendojnë.'),
(3404, 29, 64, 'Jeta e kësaj bote nuk është gjë tjetër përveç se argëtim dhe lojë. E, me të vërtetë, jeta e botës tjetër është jetë. Sikur ta dinin ata këtë!'),
(3405, 29, 65, 'Kur të hipin në barkë, i lutën Perëndisë sinqerisht, e kur Ai t’i sjellë (shpëtojë) në tokë, përnjëherësh ata i bëjnë shok Atij,'),
(3406, 29, 66, 'për të mohuar atë që u kemi dhënë Ne dhe për t’u kënaqur. E, shpejt do ta dinë ata!'),
(3407, 29, 67, 'Vallë, a nuk e vërejnë ata, se Ne e kemi bërë Haramin (Mekken) të sigurtë, kurse njerëzit rreth tij po grabiten (e mbytën)? E, vallë, a po besojnë ata në gjëra të kota, ndërsa dhuntinë e Perëndisë po e mohojnë?'),
(3408, 29, 68, 'Kush është zullumqar më i madh se ai që trillon gënjeshtra kundër Perëndisë ose mohon të vërtetën që i ka ardhur? Vallë, a nuk është xhehennemi vend për mohuesit?'),
(3409, 29, 69, 'E, ata që luftojnë (përpiqen) për të mirat Tona, Na, me siguri do t’i udhëzojmë në rrugën Tonë. Perëndia, me të vërtetë, është me të mirët!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'Elif, Lám, Mîm.'),
(3411, 30, 2, 'U mundën Bizantinët,'),
(3412, 30, 3, 'në afërsi të tokës (arabe), por ata pas thyerjes së tyre, me siguri do të fitojnë'),
(3413, 30, 4, 'brenda shtatë – nëntë (disa) vjetëve; me të vërtetë, vendimi i Perëndisë, është para (humbjes) dhe pas (fitorjes). Dhe, atë ditë, do të gëzohen besimtarët –'),
(3414, 30, 5, 'për ndihmën e Perëndisë, - Ai ndihmon kë të dojë dhe Ai është i Plotëfuqishëm dhe Mëshirues –'),
(3415, 30, 6, '(ky është) premtimi i Perëndisë, e Perëndia nuk e then premtimin e vet, por shumica e njerëzve nuk e dinë.'),
(3416, 30, 7, 'Ata dinë vetëm anën sipërfaqësore të kësaj jete, ndërsa për jetën tjetër janë të pakujdesshëm (indiferent).'),
(3417, 30, 8, 'Vallë, a nuk mendojnë ata për vetveten? Perëndia, ka krijuar qiejt dhe Tokën dhe ato që gjenden në mes tyre – vetëm me qëllim dhe afat të caktuar. E, me të vërtetë, shumica e njerëzve nuk besojnë në takimin me Zotin e tyre.'),
(3418, 30, 9, 'Vallë, a nuk kanë udhëtuar nëpër botë e të shohin se si kanë pëfunduar popujt e mëparshëm? Ata, kanë qenë më të fortë se këta, dhe tokën e kanë shfrytëzuar dhe punuar më mirë se që e punojnë këta, e pejgamberët e tyre u kanë sjellë argumente të qarta. Perëndia, nuk u ka bërë atyre zullum, por ata vetvetes i kanë bërë zullum.'),
(3419, 30, 10, 'Pastaj, përfundimi i atyre që punuan keq, qe e keqja e madhe, ngase i përgënjeshtronin ajetet e Perëndisë dhe talleshin me to.'),
(3420, 30, 11, 'Perëndia e fillon krijimin (prej asgjëje), e pastaj, e përsëritë (pas vdekjes), dhe pastaj tek Ai do të ktheheni.'),
(3421, 30, 12, 'E, atë ditë kur të arrijë Çasti (Dita e Kijametit), njerëzit e këqinj do të mbesin shpresëkëputur:'),
(3422, 30, 13, 'ata nuk do të kenë ndërmjetës nga idhujt e vet, dhe ata do t’i mohojnë idhujtë e tyre.'),
(3423, 30, 14, 'E, atë ditë kur të arrijë çasti i ringjalljes (Dita e Kijametit), atë ditë ata, - do të ndahen:'),
(3424, 30, 15, 'ata që anë besuar dhe që kanë punuar vepra të mira – në kopshtin e xhennetit do të jenë të gëzuar,'),
(3425, 30, 16, 'e ata që kanë mohuar dhe përgënjeshtruar ajetet Tona dhe takimin në Ditën e Kijametit, ata do të hudhen në zjarr.'),
(3426, 30, 17, 'Larësonie Perëndinë kur bie muzgu dhe kur gëdhinë drita.'),
(3427, 30, 18, 'Dhe, për Te është falenderimi në qiej dhe në Tokë – në mbrëmje dhe në mesditë –'),
(3428, 30, 19, 'Ai, krijon (nxjerrë) të gjallin prej të vdekurit, dhe krijon të vdekurin prej të gjallit dhe e gjallëron tokën pas vdekjes (shkretërimit) të saj; - poashtu edhe ju do të nxirreni (prej varreve – të ringjallur).'),
(3429, 30, 20, 'Dhe, prej argumenteve të Tij, është që ju u krijuat prej dheut, pastaj përnjëherë jeni bërë njerëz dhe jeni shpërndarë në të gjitha anët;'),
(3430, 30, 21, 'dhe njëri prej argumenteve të Tij, është që prej jush janë krijuar femrat, që të qetësoheni pranë tyre, dhe që në mes jush të lindë (vendoset) dashuria dhe mëshira. Me të vërtetë, në këtë, ka argumente për njerëzit që mendojnë;'),
(3431, 30, 22, 'dhe njëri prej argumenteve të Tij, është krijimi i qiejve dhe i Tokës, dhe llojllojshmëria e gjuhëve tuaja dhe ngjyrave tuaja (racave). Me të vërtetë, në këtë, ka argumente për të diturit;'),
(3432, 30, 23, 'dhe njëri prej argumenteve të Tij, është gjumi juaj – natën dhe ditën, dhe kërkesa e juaj nga dhuntitë e Tij. Me të vërtetë, në këtë ka argumente për njerëzit që dëgjojnë;'),
(3433, 30, 24, 'dhe njëri prej argumenteve të Tij, është paraqitja e vetëtimës – frikë dhe shpresë, e që ua dërgon shiun prej qiellit, që me te, të gjallërohet toka pas shkretërimit të saj. Me të vërtetë, në këtë ka argumente për njerëzit që mendojnë.'),
(3434, 30, 25, 'Dhe njëri prej argumenteve të Tij, është që qiejt dhe Toka qëndrojnë sipas urdhërit të Tij, pastaj kur t’ju thërret Ai me një të thirrur nga toka, ju atëherë – përnjëherë do të dilni (do të ringjalleni).'),
(3435, 30, 26, 'Dhe, Atij i përkasin çka ka në qiej dhe në Tokë. Të gjitha ipërulen Atij.'),
(3436, 30, 27, 'Ai krijon prej asgjëje, dhe Ai e përsëriti atë; për Te kjo është shumë lehtë. Atij i përkasin virtytet më të larta në qiej dhe në Tokë. Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(3437, 30, 28, 'Ai u ka sjellë një shembull prej vet jush: a janë shokë me ju – skllevërit tuaj, në furnizimin Tonë që u kemi bërë juve, e ju jeni të njëjtë në furnizim, dhe ju i druani atyre sikur që i druani njëri-tjetrit? (E, si atëherë më bëni shok Mua?) – Ja, kështu Ne, ua shpjegojmë argumentet Tona, njerëzve që mendojnë.'),
(3438, 30, 29, 'Por, zullumqarët, nga padijenia ndjekin qejfet e veta. E, kush do ta udhëzojë atë që e ka humbur Perëndia? Dhe, për ta, nuk ka kurrfarë ndihmësi.'),
(3439, 30, 30, 'Drejtohu me përkushtim në fenë e drejtë, krijimtarinë e Perëndisë, sipas të cilës Ai i ka krijuar njerëzit. S’ka ndryshim në krijimtarinë e Perëndisë. Kjo është fe e drejtë, por shumica e njerëzve nuk e dinë, -'),
(3440, 30, 31, 'duke iu drejtuar Atij (Perëndisë)! Druajuni Atij dhe falni namazin e mos u bëni si ata që i bëjnë shok Atij,'),
(3441, 30, 32, 'si ata, që fenë e vet e kanë përçarë dhe janë ndarë në grupe, e çdo grup është i kënaqur me atë që ka para vetes.'),
(3442, 30, 33, 'E, kur njerëzit i godet ndonjë e keqe, ata i luten Zotit të vet, duke iu kthyer vetëm Atij, e pastaj, kur Ai t’u jap të shijojnë mëshirën e Tij, atëherë një grup prej tyre – i bëjnë shok Zotit të vet,'),
(3443, 30, 34, 'për ta mohuar atë që u kemi dhënë Ne. (U thuhet atyre): “E, kënaquni, meqë së shpejti do ta dini!”'),
(3444, 30, 35, 'A mos vallë u kemi dërguar Ne atyre (ndonjë) argument, që flet për atë që më bënin Mua shok ata?'),
(3445, 30, 36, 'E, kur Ne t’u japim njerëzve ta shijojnë dhuntinë, ata gëzohen me të, e kur t’i godas fatkeqësia për atë që e kanë punuar duart e tyre, atëherë e humbin shpresën.'),
(3446, 30, 37, 'Vallë, a nuk dinë ata se Perëndia i jep furnizim të gjerë dhe ia pakëson kujt të dojë. Me të vërtetë, për këtë ka argument për njerëzit që besojnë.'),
(3447, 30, 38, 'Jepja të afërmit të drejtën e tij, edhe të varfërit edhe udhëtarit (që ka ngelë në rrugë)! Kjo është më së miri për ata që e duan kënaqësinë e Perëndisë; e të tillët janë të shpëtuar.'),
(3448, 30, 39, 'E, të hollat që i jepni për t’u shtuar me të hollat e njerëzve tjerë, nuk do të shtohen te Perëndia, kurse çkado që të jepni nga lëmosha, për ta arritur kënaqësinë e Perëndisë, të atillëve do t’u shumëfishohen (të mirat).'),
(3449, 30, 40, 'Perëndia është ai që ju ka krijuar, e ju ka dhënë furnizim; pastaj ju vdes e pastaj ju ngjallë. A ka ndonjë nga idhujt tuaj që bënë diçka kësisoji? Ai, është i lavdëruar dhe i lartësuar nga ajo që ata i bëjnë shok Atij!'),
(3450, 30, 41, 'U shfaq ngatërresa në tokë dhe në det për atë që punuan duart e njerëzve, që t’u japë të shijojën pjesë nga ajo që kanë punuar, për t’u kthyer ata në përmirësim.'),
(3451, 30, 42, 'Thuaj (o Muhammed!): “Udhëtoni nëpër botë e shikoni si ka qenë përfundimi i të mëparshmëve; shumica e tyre kanë qenë idhujtarë.'),
(3452, 30, 43, 'Drejtohu ti me përkushtim në fenë e drejtë (islamizmin), para se të vie prej Perëndisë Dita që nuk mund të shtyhet (nga askush). Atë ditë – ata do të ndahen:'),
(3453, 30, 44, 'ata që nuk kanë besuar, mohimi është në dëm të tyre, e ata që kanë punuar vepra të mira – vetes i kanë përgatitur (të mira),'),
(3454, 30, 45, 'ata që kanë besuar dhe kanë bërë vepra të mira, Perëndia do t’i shpërblejë nga dhuntitë e Veta. E, ai, me të vërtetë, nuk i don mohuesit.'),
(3455, 30, 46, 'Dhe, njëri prej dokumenteve të Tij, është dërgimi i erërave sihariquese, që të shijoni nga mëshira e Tij, që anija të lundrojë me urdhërin e Tij, që të kërkoni nga dhuntia e Tij dhe që të jeni mirënjohës.'),
(3456, 30, 47, 'Dhe, me të vërtetë, Na, kemi dërguar pejgamber para teje – te popujt e tyre, e ata u kanë ardhur atyre me argumente të qarta, e i kemi ndëshkuar ata që kanë bërë mëkate, - e për ne, është që t’i ndihmojmë besimtarët.'),
(3457, 30, 48, 'Perëndia është ai që i dërgon erërat, që ato t’i lëvizin retë, e (Ai) i shpërndanë ato nëpër qiell si të dojë dhe i copton ato në pjesë-pjesë, e ti sheh shiun si bie prej tyre. E, me te mund ta dhurojë kë të dojë nga robërit e Vet, e atëherë ata gëzohen përnjëherësh,'),
(3458, 30, 49, 'edhe pse, me të vërtetë, para se t’u vie shiu, kanë qenë të dëshpëruar.'),
(3459, 30, 50, 'Andaj, shikoji gjurmët e mëshirës së Perëndisë, si e gjallëron tokën pas shkretërimit të saj. Ai, me të vërtetë, do t’i ringjallë edhe të vdekurit dhe Ai është i pushtetshëm për çdo gjë.'),
(3460, 30, 51, 'Dhe, sikur të dërgonim Ne – furtunë, e ata të shihnin që çdo gjë është zverdhur, edhe pas kësaj do të mbeteshin mohues të dhuntive.'),
(3461, 30, 52, 'Me të vërtetë, ti (o Muhammed!) nuk mund të bësh që i vdekuri, dhe as i shurdhëti të dëgjojnë tirrjen kur ata ta kthejnë shpinën ty duke u larguar,'),
(3462, 30, 53, 'dhe ti nuk mund ta udhëzosh të verbërin nga humbja e tij. Ti, mund të bësh që të dëgjojnë vetëm ata që besojnë në ajetet Tona. Dhe, ata janë muslimanë të vërtetë.'),
(3463, 30, 54, 'Perëndia është ai që ju ka krijuar prej një dobësie, pastaj – pas dobësisë, u ka bërë të fuqishëm, e ju ka bërë – pas fuqisë të dobët dhe pleqë; Ai krijon çka të dojë; Ai është i Plotëdijshëm dhe i pushtetshëm për çdo gjë.'),
(3464, 30, 55, 'Dhe, atë Ditë kur të arrijë Çasti (Kijameti), betohen mëkatarët, se në varr (tokë) kanë qëndruar një kohë të shkurtër. Kështu ata shmangen nga e Vërteta.'),
(3465, 30, 56, 'E, (mëkatarëve), të cilëve u është dituria dhe besimi, u thonë: “Ju, me të vërtetë, keni qëndruar aq sa ju ka caktuar Perëndia në Libër – deri në Ditën e ringjalljes, - e ja kjo është Dita e ringjalljes, por që ju (këtë) nuk e keni ditur”.'),
(3466, 30, 57, 'Atë Ditë – atyre që kanë bërë zullum – nuk do t’u bëjë dobi arsyetimi, as nuk do t’u lejohet që të kërkojnë falje.'),
(3467, 30, 58, 'Dhe, me të vërtetë, në këtë Kur’an, Na u kemi sjellë njerëzve gjithëfarë shembuj. E, sikur ti, t’u sjellësh atyre çfarëdo argumenti (mrekullie), ata (mohuesit), me siguri, do të thonin: “Ju jeni vetëm të degjeneruar”.'),
(3468, 30, 59, 'Ja, kështu, Perëndia ua vulos zemrat atyre që nuk dinë.'),
(3469, 30, 60, 'Duro ti (o Muhammed), se me siguri, premtimet e Perëndisë, janë të vërteta dhe le të mos mashtrojnë kurrsesi ata që nuk besojnë.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'Elif, Lám, Mîm.'),
(3471, 31, 2, 'Këto janë ajetet nga Libri plotë dijeni,'),
(3472, 31, 3, '(i cili) është udhërrëfyes dhe mëshirë për bamirësit,'),
(3473, 31, 4, 'ata që kryejnë namazin dhe japin zeqatin, dhe të cilët besojnë bindshëm në jetën tjetër.'),
(3474, 31, 5, 'Këta janë ata që janë udhëzuar nga Zoti i tyre dhe vetëm ata janë të shpëtuar.'),
(3475, 31, 6, 'E, ka disa nga njerëzit që besojnë në tregime të kota, për t’u shmangur nga rruga e Perëndisë, pa kurrfarë dijenie, duke e pandehur (rrugën e Zotit) qesharake. Të atillët i pret dënimi i poshtëruar.'),
(3476, 31, 7, 'Kur ndonjërit prej tyre, u lexohen ajetet Tona (Kur’ani), ai shmanget me mendjemadhësi, thuase nuk i ka dëgjuar, thuase iu kanë shurdhuar veshët. Andaj, sihariqoi ata me dënim të dhembshëm.'),
(3477, 31, 8, 'Me të vërtetë, ata që kanë besuar dhe punojnë vepra të mira do të gjenden në kopshtet e dhuntisë,'),
(3478, 31, 9, 'në të cilat do të qëndrojnë përherë. Premtimi i Perëndisë është i vërtetë, e Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(3479, 31, 10, '(Ai) ka krijuar qiejt pa shtylla, e i shihni ju – ka vendosur male të palëvizshme në Tokë, për të mos u lëkundur ju, dhe ka shpërndarë në të, gjithfarë lloj shtazësh. Ne, e kemi zbritu ujin nga qielli dhe me të, kemi bërë të mbijnë sa e sa lloje bimësh të mira (buta).'),
(3480, 31, 11, 'Ky është krijimi i Perëndisë; më tregoni çka kanë krijuar të tjerët pos Tij? Jo, por, me të vërtetë, zullumqarët janë në humbje të vërtetë.'),
(3481, 31, 12, 'Dhe, Ne, me të vërtetë, i kemi dhënë Llukmanit dijeninë e mrekullueshme, (dhe i kemi thënë): “Falenderoje Perëndinë, e kush është falenderues, ai falenderon për të mirën e vet, e kush mohon – Perëndia, me të vërtetë, është i pavarur dhe i lavdëruar”.'),
(3482, 31, 13, 'Dhe, (kujtoje) kur Llukmani i tha djalit të vet, duke e këshilluar: “O djali im, mos i bënë shok Perëndisë! Me të vërtetë, të bërit shok Perëndisë, është zullum i madh”.'),
(3483, 31, 14, 'Dhe, Ne, i kemi urdhëruar njeriun që t’i nderojë prindërit e vet. Nëna e ka bartur atë, duke duruar mundim pas mundimi, e ndarja e tij (nga gjiri) është brenda dy vitesh. (Dhe i kemi thënë njeriut): “Bëhu falenderues ndaj Meje dhe prindërve tu! Tek Unë do të ktheheni të gjithë”.'),
(3484, 31, 15, 'Nëse insistojnë ata, që të më bësh Mua shok për atë që nuk din asgjë, mos i dëgjo, por shoqërohu me ta në këtë jetë me të mirë, e ndiqe rrugën e atij që drejtohet tek Unë; pastaj, tek Unë do të ktheheni dhe Unë do t’ju lajmëroj për atë që keni punuar.'),
(3485, 31, 16, '“O djali im, nëse (vepra e punuar) është e rëndë qoftë se një kokërr hardeli (lloj mëlmese), e ajo të jetë në shkëmb, ose në qiej ose në tokë, Perëndia do ta nxjerrë në shesh atë. Me të vërtetë, Perëndia është i butë dhe njohës i të gjitha fshehtësive.'),
(3486, 31, 17, 'O djali im, kryeje namazin, urdhëro me të mirë dhe pengo nga të këqiat dhe bëhu i durueshëm për atë që të ka goditur! Me të vërtetë, kjo është nga punët e mëdha, të vendosura.'),
(3487, 31, 18, 'Mos e shmang fytyrën tënde nga njerëzit dhe mos ec nëpër tokë mendjemadhësisht, se Perëndia nuk e don asnjë mendjemadh, as mburracak.'),
(3488, 31, 19, 'Ec me kujdes e modesti, e fol me zë të ulët; se zëri më i vrazhdë është zëri (pallja e) i gomarit!”'),
(3489, 31, 20, 'A nuk shihni, se si Perëndia u ka mundësuar – t’i shfrytëzoni të gjitha ato që gjenden në qiej dhe në Tokë dhe ju ka plotësuar dhuntitë e Tij – të jashtme dhe të brendshme? E, ka njerëz që polemizojnë për Perëndinë pa kurrfarë dijenie, pa kurrfarë rrëfyesi dhe pa libër ndriçues.'),
(3490, 31, 21, 'Kur atyre u thuhet: “Pasoni atë që u ka shpallur Perëndia!” ata thonë: “Jo, (por) ne pasojmë të parët tanë”. E, sikur ata t’i thërriste djalli edhe në dënimin e skëterrës, (a do ta pasonin)?'),
(3491, 31, 22, 'Kush kthehet kah Perëndia, me tërë qenien e tij, duke qenë i mirë, ai është kapur për vegën më të fortë. E, te Perëndia, është fundi i çdo gjëje.'),
(3492, 31, 23, 'Dhe kush mohon, të mos të brengos mohimi i tij. Tek Ne do të kthehen ata, e Ne do t’i lajmërojmë për atë që kanë punuar. Me të vërtetë, Perëndia di ç’ka në zemrat e njerëzve.'),
(3493, 31, 24, 'Ne, ata do t’i kënaqim përkohësisht, e pastaj, do t’i hudhin furishëm në dënimin e ashpër.'),
(3494, 31, 25, 'E, nëse i pyet ti (o Muhammed) ata: “Kush ka krijuar qiejt dhe Tokën?” – me siguri do të thonë: “Perëndia!” E ti thuaj: “Falenderimi qoftë për Perëndinë!” Por, shumica e tyre nuk dinë.'),
(3495, 31, 26, 'Të Perëndisë janë të gjitha, në qiej dhe në Tokë. Me të vërtetë, Perëndia është i pavarur dhe i lavdëruar.'),
(3496, 31, 27, 'Sikur të gjithë drunjtë të gjenden në Tokë – të jenë penda (shkrimi), e deti ngjyrë shkrimi (tintë), e t’i shtohen atij edhe shtatë dete të tjera, nuk do të soseshin fjalët e Perëndisë. Me të vërtetë, Perëndia është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(3497, 31, 28, 'Të krijuarit dhe të ringjallurit e të gjithë juve është njësoj (për Te) – si të krijuarit e vetëm një njeriu. Me të vërtetë, Perëndia i dëgjon dhe i sheh të gjitha.'),
(3498, 31, 29, 'A nuk po sheh ti se Perëndia e shtie natën në ditë dhe ditën në natë (e zgjatë njërën dhe e shkurton tjetrën), dhe e ka nënshtruar Diellin dhe Hënën – secili lëvizë deri në kohë të caktuar – dhe se Perëndia e di mirë çka punoni ju.'),
(3499, 31, 30, 'Kjo është (kështu), ngase Perëndia është i vetmi i Vërtetë, e ato që adhurojnë ata – pos Tij, janë të kota, dhe se Perëndia është i lartësuar dhe i madh.'),
(3500, 31, 31, 'A nuk po sheh ti, e anijet lundrojnë nëpër det me dhuntinë e Perëndisë, për t’ju treguar disa argumente të Veta? Këto janë, me të vërtetë, argumente për çdo njeri të durueshëm dhe mirënjohës.'),
(3501, 31, 32, 'E, kur ata t’i mbulojë vala – si bjeshkë, e thërrasin Perëndinë me lutje të sinqerta; e, posa t’i shpëtojë ata në tokë, një pjesë e tyre e mbajnë mesataren; e ajetet Tona i mohon vetëm mshtruesi dhe jomirënjohësi.'),
(3502, 31, 33, 'O njerëz! Druajuni Zotit tuaj dhe frikësojuni Ditës kur prindi nuk do të mund t’i ndihmojë fëmijës së vet asgjë, e as fëmija nuk do të mund t’i ndihmojë asgjë prindit të vet! Me të vërtetë, premtimi i Perëndisë është i sigurt, e, le të mos u mashtrojë jeta e kësaj bote, dhe le të mos u mashtrojë mashtruesi (djalli) në Perëndinë.'),
(3503, 31, 34, 'Me të vërtetë, vetëm Perëndia e di Çastin (Ditën e Kijametit), Ai e zbret shiun dhe di çka k në barkun e shtatzënës, e askush nuk di ç’do të fitojë nesër dhe askush nuk di se në ç’vend do të vdes. Me të vërtetë, Perëndia është i Gjithëdijshëm dhe njohës i çdo gjëje.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'Elif, Lám, Mîm.'),
(3505, 32, 2, 'Shpallja e këtij Libri, për të cilin s’ka dyshim – është nga Zoti i gjithësisë,'),
(3506, 32, 3, 'e ata thonë: “Ai (Muhammedi) e ka trilluar atë! Jo, ajo është e Vërteta nga Zoti yt – për ta paralajmëruar popullin, të cilit, para teje nuk i ka ardhur kurrfarë paralajmëruesi, që të shkojë në rrugë të drejtë.'),
(3507, 32, 4, 'Perëndia është Ai që ka krijuar qiejt dhe Tokën dhe çdo gjë që gjendet në mes tyre, për gjashtë ditë (periudha kohore), dhe e ka mbizotëruar Gjithësinë (Arshin). Ju, nuk keni – pos Tij, ndihmës as ndërmjetësues; e vallë, a nuk po mendoni?'),
(3508, 32, 5, 'Ai i udhëheq të gjitha gjërat, prej qiellit deri në Tokë, e atëherë kthehen tek Ai në ditën, gjatësia e së cilës është një mijë vjet sipas llogaritjes suaj (në këtë Botë).'),
(3509, 32, 6, 'Ai është dijtës i gjërave të padukshme dhe të dukshme, i Plotëfuqishëm dhe Mëshirues,'),
(3510, 32, 7, 'i cili e ka bërë të mirë çdo gjë që ka krijuar dhe ka filluar krijimin e njeriut prej balte,'),
(3511, 32, 8, '(e) pastaj, ka bërë që pasardhësit e tij të krijohen prej një rrjedhje të dobët të ujit (spermës),'),
(3512, 32, 9, 'pastaj, e ka krijuar atë (njeriun) dhe i ka dhënë gjallëri, me frymëzimin e Tij. Ai ju ka dhënë të dëgjuarit, të pamurit dhe të menduarit, - e sa pak prej jush që jeni mirënjohës.'),
(3513, 32, 10, 'Dhe ata thonë: “E, vallë, pasi të humbasim nën tokë, përsëri do të ngjallemi?” Dhe ata, nuk besojnë se do të takohen me Zotin e tyre.'),
(3514, 32, 11, 'Thuaju: “Do t’jua merr shpirtin engjëlli i vdekjes, i cili është caktuar për ju, e pastaj do të ktheheni te Zoti juaj”.'),
(3515, 32, 12, 'E, sikur t’i shohësh mëkatarët se si para Zotit të tyre kokëulur thonë: “O Zoti ynë, pamë dhe dëgjuam, andaj, kthena që të punojmë vepra të mira! Na, me të vërtetë, me ngulmëri besojmë!”'),
(3516, 32, 13, 'E, sikur Ne të donim, do t’ia jepnim çdo personi udhëzimin e tij, por kjo është fjala Ime e vërtetë: “Me të vërtetë, do ta mbush xhehennemin me xhindë dhe njerëz bashkërisht!”'),
(3517, 32, 14, 'E, shijoni (dënimin), ngase e keni harruar takimin e kësaj dite. Në të vërtetë, Ne ju kemi braktisur – dhe shijoni dënimin e përhershëm për atë që keni punuar.'),
(3518, 32, 15, 'Ajetet Tona i besojnë vetëm ata të cilët kur të këshillohen me to, bijnë në sexhde, dhe të cilët, e madhërojnë Zotin e tyre duke iu falenderuar Atij, dhe të cilët, nuk bëhen mendjemëdhenj.'),
(3519, 32, 16, 'Pjesët e trupit të tyre ngriten nga shtrati (për t’u falur); i luten Zotit të tyre duke druajtur dhe shpresuar, dhe japin (lëmoshë) nga ajo që u kemi dhënë Ne.'),
(3520, 32, 17, 'Askush nuk di se çfarë gëzime të fshehta janë për ta – shpërblimi për atë që kanë punuar.'),
(3521, 32, 18, 'Vallë! A është njësoj si besimtari si mëkatari? Jo, ata nuk janë të barabartë:'),
(3522, 32, 19, 'ata që kanë besuar dhe punuar vepra të mira, për ta janë kopshtet e xhennetit Meëva – të përgatitur për atë që kanë punuar;'),
(3523, 32, 20, 'e ata që kanë bërë keq, vendbanim i tyre është zjarri; sa herë që duan të dalin nga ai (zjarri), do të kthehen në të dhe do t’u thuhet: “Shijoni dënimin e zjarrit, të cilin e keni përgënjeshtruar!”'),
(3524, 32, 21, 'Dhe Ne, do të bëjmë që të shijojnë dënimin e kësaj jete para (atij) dënimit të madh, për t’u penduar ata.'),
(3525, 32, 22, 'Kush është zullumqar më i madh se ai, që është këshilluar me argumentet e Zotit të tij, e shmanget prej tyre? Na, me të vërtetë, do t’i dënojmë mëkatarët!'),
(3526, 32, 23, 'Na, madje, ia kemi dhënë Librin Musait, dhe mos dysho në takimin me të, dhe e kemi bërë atë (Librin) udhërrëfyes për bijtë e Israelit.'),
(3527, 32, 24, 'Na, prej tyre – kemi bërë udhëheqës, të cilët udhëzonin me urdhërat Tona, meqë ishin të durueshëm dhe besonin bindshëm në argumentet Tona.'),
(3528, 32, 25, 'Me të vërtetë, Zoti yt, do t’i gjykojë në mes tyre Ditën e Kijametit, për atë që e kundërshtuan njëri-tjetrin.'),
(3529, 32, 26, '(Vallë)! A nuk u është e qartë atyre se sa popuj i kemi shkatërruar Ne para tyre; nëpër shtëpiat e të cilëve ecin? Me të vërtetë, në këto ka argumente, e ata nuk po dëgjojnë (këshillat).'),
(3530, 32, 27, 'Vallë! A nuk shohin ata, që Ne, me të vërtetë e dërgojmë ujin në tokën e shkretëruar, e me të – nxjerrim të lashtat, me të cilat ushqehen shtazët e tyre dhe ata vetë, e ata madje nuk po shohin?!'),
(3531, 32, 28, 'Dhe, ata thonë: “E, kur më fitorja (Dita e Kijametit), nëse thoni të vërtetën?”'),
(3532, 32, 29, 'Thuaju (o Muhammed!): “Ditën e gjykimit, nuk do t’u bëjë dobi mohuesve besimi i tyre, e as që do t’u jepet afat”.'),
(3533, 32, 30, 'Andaj, ti shmangu nga ata dhe prit, edhe ata, me të vërtetë, po presin!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'O pejgamber! Druaje Perëndinë dhe mos i dëgjo mohuesit dhe hipokritët! Me të vërtetë, Perëndia është i Plotëdijshëm dhe i Gjithëdijshëm,'),
(3535, 33, 2, 'dhe paso atë, që të është shpallur ty nga Zoti. Me të vërtetë, Perëndia e di mirë atë që punoni ju,'),
(3536, 33, 3, 'dhe mbështetu te Perëndia, të mjafton ty mbështetja te Perëndia!'),
(3537, 33, 4, 'Perëndia, asnjë njeriu nuk ia ka dhënë dy zemra në krahëror; Perëndia nuk ua ka bërë gratë tuaja – nëna, të cilave u thoni: “Shpina juaj është si e nënës sime”, as që ua ka bërë fëmijët e përvetësuar – bijë të vërtetë. Këto janë vetëm fjalët tuaja – prej gojës suaj (joreale), e Perëndia thotë të vërtetën dhe Ai udhëzon në rrugë të drejtë.'),
(3538, 33, 5, 'Ju thërritni ata sipas etërve të tyre! Kjo është më e drejtë te Perëndia. E, nëse nuk e dini (emrin) e etërve të tyre, ata janë vëllezërit tuaj për kah besimi dhe mbrojtësit tuaj. Nuk është mëkat nëse gaboni, por nëse atë e bëjnë me qëllim zemrat tuaja; se, Perëndia është falës dhe mëshirues.'),
(3539, 33, 6, 'Pejgamberi është më mbikëqyrës për besimtarët se sa ata për vetveten, e gratë e tij – janë (si) nënat e tyre. Dhe, ata nga farefisi – janë më të afërt të njëri-tjetrit, sipas Librit të Perëndisë – se sa besimtarët tjerë dhe muhaxherët, përveç nëse doni t’u bëni ndonjë të mirë dashamirëve tuaj. Kjo është shënuar në Libër.'),
(3540, 33, 7, '(Kujtoje ti o Muhammed!) kur nga pejgamberët Ne morëm obligimin e tyre; edhe prej teje, edhe prej Nuhut, edhe prej Ibrahimit, edhe prej Musait, edhe prej Isait, të birit të Merjemës. Ne, prej tyre kemi marrë obligimin e fortë, për'),
(3541, 33, 8, 't’i pyet të sinqertit për sinqeritetin e tyre; e për mohuesit, Ai ka përgatitur dënim të dhembshëm.'),
(3542, 33, 9, 'O besimtarë! Kujtonie dhuntinë e Perëndisë (juve) kur ju erdhi ushtria; e ne dërguam mbi ta frymë, dhe ushtrinë të cilën ju nuk e keni parë, e Perëndia i sheh të gjitha ato që punoni ju,'),
(3543, 33, 10, 'kur ju erdhën nga lartë dhe nga poshtë, dhe sytë tuaj përqëndroheshin kah ata (argmiku), e shpirti u kishte ardhur në fyt (prej frikës), e ju mendonit për Perëndinë lloj-lloj mendimesh, -'),
(3544, 33, 11, 'atëherë, besimtarët u vunë në provë dhe qenë të tronditur nga tronditja e madhe.'),
(3545, 33, 12, '(Kutjoje ti o Muhammed!) kur hipokritët dhe ata në zemrat e të cilëve ka sëmundje (besim të dobët), thanë: “Nuk ka premtuar Perëndia dhe Pejgamberi i Tij asgjë, pos mashtrimit!”'),
(3546, 33, 13, '(Dhe kujtoju atyre) kur një grup prej tyre, thanë: “O banorët e Jethribit, s’ka qëndrim këtu për ju, andaj kthehuni (në shtëpiat tuaja)!” E një grup nga ata kërkuan leje prej Pejgamberit, duke thënë: “Me të vërtetë, shtëpiat tona janë të pambrojtura!” – por ato nuk kanë qenë të pambrojtura. Ata duan vetëm të ikin.'),
(3547, 33, 14, 'E, sikur atyre t’u depërtonin nga anët e Jethribit e të kërkohet prej tyre që të bëhen idhujtarë, ata do të bëheshin. Por, në të, - vetëm pak do të qëndronin,'),
(3548, 33, 15, 'e ata – qysh më parë i qenë betuar Perëndisë se nuk do të zmbrapsen, - e për betim në Perëndinë do të përgjigjen!'),
(3549, 33, 16, 'Thuaju: “Nuk do t’ju bëjë bobi të ikurit, nëse ikni prej vdekjes apo mbytjes. Edhe atëherë (nëse ikni), ju vetëm pak do të kënaqeni”.'),
(3550, 33, 17, 'Thuaju: “Kush është ai që do t’ju ruajë nga Perëndia, nëse Ai don që t’ju godas e keqja, ose, nëse Ai don që t’ju japë mëshirë?” Ata, pos Perëndisë nuk do të gjejnë, - as mbrojtës as ndihmës.'),
(3551, 33, 18, 'Perëndia – me siguri, i di ata nga mesi juaj që pengojnë dhe që u thonë vëllezërve të tyre: “Ejani te ne!” E, nuk vijnë në luftë, pos një pakicë e tyre,'),
(3552, 33, 19, 'duke u kursyer që t’ju ndihmojnë. E, kur të frikësohen, i sheh ata se si të shikojnë: i rrotullojnë sytë sikur të ishin në agoni. E, posa t’ju kalojë frika, ju shqetësojnë me gjuhë të mprehtë, e janë koprac për çfarëdo të mire. Këta janë ata që nuk kanë besuar, andaj Perëndia ua ka asgjësuar veprat e tyre. E, kjo për Perëndinë është lehtë.'),
(3553, 33, 20, 'Ata mendonin se ende nuk ka shkuar ushtria (e Kurejshit), e nëse do të vinte ushtria e aleatëve (edhe njëherë), ata do të dëshironin të jenë në mesin e beduinëve dhe të pyesin për ju; e, edhe sikur të mbeteshin me ju, pak do të luftonin.'),
(3554, 33, 21, 'Ju, me të vërtetë, Pejgamberin e Perëndisë e keni shembull të mrekullueshëm, për atë që i druan Perëndisë dhe Ditës së Kijametit, dhe i cili e përmend shumë Perëndinë.'),
(3555, 33, 22, 'E, kur besimtarët e panë ushtrinë (e Kurejshit me aleatë), thanë: “Kjo është ajo që na ka premtuar Perëndia dhe Pejgamberi i Tij; edhe Perëndia dhe Pejgamberi i Tij, e kanë thënë të vërtetën!” E kjo (ngjarje) atyre – vetëm ua ka forcuar besimin dhe përuljen.'),
(3556, 33, 23, 'Ndër besimtarët ka njerëz që e kanë plotësuar premtimin e dhënë Perëndisë, disa prej tyre kanë vdekur; e disa nga ata e presinatë – nuk kanë ndryshuar asgjë –'),
(3557, 33, 24, 'për t’i shpërblyer Perëndia të sinqertit për sinqeritetin e tyre, e për t’i dënuar hipokritët, nëse don, ose t’u pranojë pendimin e tyre. Me të vërtetë, Perëndia është falës dhe mëshirues.'),
(3558, 33, 25, 'Perëndia i ka zmbrapsur mohuesit me hidhërimin e tyre, ngase nuk e arritën asnjë të mirë. Perëndia i kursei besimtarët nga lufta, - Perëndia, me të vërtetë, është i fuqishëm dhe i pushtetshëm,'),
(3559, 33, 26, 'e ithtarët e Librit, të cilët i ndihmuan ata (Kurejshit), i nxori prej kështjellave të tyre, dhe mbolli frikë në zemrat e tyre; një grup (të tyre e keni mbytur, e një tjetër e keni zënë rob,'),
(3560, 33, 27, 'dhe ju dha trashëgim tokën e tyre, shtëpitë e tyre, pasuritë e tyre, dhe tokën që nuk e keni shkelur më parë; Perëndia është i pushtetshëm për çdo gjë.'),
(3561, 33, 28, 'O Pejgamber! Thuaju grave tuaja: “Nëse e dëshironi jetën e kësaj bote dhe shkëlqimin e saj, ejani (vendosni), unë do t’ju japë vlerën (e shkurorëzimit) dhe do t’ju lëshoj në mënyrë të bukur (njerëzishme).'),
(3562, 33, 29, 'E, nëse dëshironi Perëndinë, dhe Pejgamberin e Tij, dhe botën tjetër, - e Perëndia, me të vërtetë, për ato – nga mesi juaj, që bëjnë vepra të mira, ka përgatitur shpërblim të madh”.'),
(3563, 33, 30, 'O gratë e Pejgamberit! Cilado prej jush që bënë ndonjë të keqe të hapët (si kërkesa e shkurorëzimit për fantazi të kësaj jete), asaj ndëshkimi, - do t’i dyfishohet, e kjo për Perëndinë është lehtë;'),
(3564, 33, 31, 'e ajo që i përulet Perëndisë dhe Pejgamberit të Tij dhe bënë vepra të mira, do t’i japim shpërblim të dyfishtë, e i kemi përgatitur furnizim fisnik.'),
(3565, 33, 32, 'O gratë e Pejgamberit! Ju, nuk jeni si gratë tjera. Nëse i druani Perëndisë, mos flisni përkëdhelshëm, e që t’ju dëshirojë ai – i cili në zemrën e tij ka sëmurje (qëllim të keq); dhe, flisni në mënyrë serioze (të njerëzishme)!'),
(3566, 33, 33, 'Dhe, qëndroni në shtëpitë tuaja e mos u zbuloni ashtu siç janë zbuluar në kohën e lashtë (pagane), falni namazin, jepni zeqatin dhe përuljuni Perëndisë dhe Pejgamberit të Tij! Perëndia don që t’i shmangë prej jush mëkatet o familje (e Pejgamberit), dhe t’ju pastrojë plotësisht.'),
(3567, 33, 34, 'Dhe, përmendni ato që lexohen në shtëpitë tuaja prej ajeteve të Perëndisë (Kur’anit) dhe dijenisë! Me të vërtetë, Perëndia është bamirës i pakufishëm dhe di çdo gjë.'),
(3568, 33, 35, 'Me të vërtetë, muslimanëve dhe muslimaneve, dhe besimtarëve dhe besimtareve, dhe të dëgjueshmëve dhe të dëshuteshmeve, dhe të sinqertëve dhe të sinqertave, dhe të durueshmëve dhe të durueshmeve, dhe të përulurëve dhe të përulurave, dhe atyre që japin lëmoshë dhe ato që japin lëmoshë, dhe atyre që agjërojnë dhe ato që agjërojnë, dhe ata që i ruajnë organet e tyre gjenitale, dhe ato që i ruajnë (organet e tyre gjenitale), dhe të cilët e përmendin shpesh Perëndinë, dhe të cilat e përmendin (Perëndinë), - me të vërtetë, për të gjithë këta, Perëndia ka përgatitur falje dhe shpërblim të madh.'),
(3569, 33, 36, 'Dhe nuk është për besimtarin dhe besimtaren – kur Perëndia dhe Pejgamberi i Tij e caktojnë një gjë – ata të bëjnë zgjedhje në atë gjë. Kush e kundërshton Perëndinë dhe Pejgamberin e Tij, ai me siguri ka humbë.'),
(3570, 33, 37, '(Kujtoje ti o Muhammed!) kur i ke thënë atij të cilit Perëndia i ka bërë mirësi dhe të cilit ti i ke bërë mirësi: “Mbaje gruan tënde dhe druaji Perëndisë!” E, ti po e fsheh në vetvete, atë që Perëndia do ta qetë në shesh, dhe ti po u druan njerëzve (nga thashethemet), e më e drejtë është t’i druash Perëndisë (vetëm Atij). E, pasi që Zejdi e plotësoi dëshirën e vet (martesën dhe ndarjen), Na, atë ta kurorëzuam ty, në mënyrë që besimtarët të mos ngurrojnë më – që të martohen me gratë e djemve të përvetësuar të tyre, pasi që ata e plotësuan dëshirën ndaj tyre. E, urdhëri i Perëndisë është i përmbarueshëm.'),
(3571, 33, 38, 'S’ka kurrfarë ngushtese për Pejgamberin për atë që i ka caktuar Perëndia. E tillë ka qenë dispozita e Perëndisë edhe për ata (pejgamberë) që kanë qenë më parë, e urdhëri i Perëndisë është dispozitë definitive –'),
(3572, 33, 39, 'për ata (pejgamberë) që i kanë sjellur shpalljet e Perëndisë dhe ë i kanë druajt Atij, dhe që nuk janë frikësuar prej askujt, pos Perëndisë. E, mjafton që të japësh llogari te Perëndia!'),
(3573, 33, 40, 'Muhammedi nuk është babë i askujt prej njerëzve (meshkujve) tuaj, por është Pejgamber i Perëndisë dhe vula e pejgamberëve, e Perëndia është i Gjithëdijshëm për çdo gjë.'),
(3574, 33, 41, 'O besimtarë, përmendni shpesh Perëndinë,'),
(3575, 33, 42, 'dhe lavdëroni Ate në mëngjes dhe në mbrëmje!'),
(3576, 33, 43, 'Ai është që ju mëshiron, e – edhe engjëjt e Tij (luten për ju) që t’ju nxjerrin nga errësira në dritë, - Ai është mëshirues ndaj besimtarëve.'),
(3577, 33, 44, 'Përshëndetja e tyre kur të takohen me Te, është “Selam! (jeni të shpëtuar prej çdo të lige)”. Dhe, Ai, u ka përgatitur tyre shpërblim të mrekullueshëm.'),
(3578, 33, 45, 'O Pejgamber, Na të kemi dërguar si dëshmitar, dhe si sihariques dhe si paralajmërues;'),
(3579, 33, 46, 'dhe thirrës (në rrugën e drejtë) te Perëndia – me urdhërin e Tij dhe që të jeshë kandil ndriçues.'),
(3580, 33, 47, 'Dhe, sihariqoji muslimanët, për të cilët Perëndia ka dhunti të madhe,'),
(3581, 33, 48, 'mos i dëgjo mohuesit dhe hipokritët! Mos i përfill fyerjet e tyre dhe mbështetu te Perëndia, e Perëndia të mjafton (ty) për mbrojtës.'),
(3582, 33, 49, 'O besimtarë, kur të kurorëzoheni me besimtare, e i lëshoni ato para se të keni marrëdhënie bashkëshortore, ato nuk janë të obliguara ta presin afatin kohor që ju e numroni, por dhuroni ato dhe i lëshoni në mënyrë të mirë (njerëzore).'),
(3583, 33, 50, 'O Pejgamber, Na të kemi lejuar ty që gratë tuaja të jenë ato të cilave u ke dhënë dhurtat e kurorëzimit, dhe ato që i ke në pushtetin tënd (skllavet), të cilat t’i ka dhënë ty Perëndia (si kompensim të luftës), dhe vajzat e axhës tënd, dhe vajzat e hallës tënde, dhe vajzat e dajës tënd, dhe vajzat e tezës tënde, të cilat emigruan bashkë me ty, dhe gruan – besimtare, nëse ajo e dhuron veten për pejgamberin – e nëse pejgamberi don të martohet me te; (kjo e fundit është) vetëm për ty. Na, gjithsesi, dimë çka u kemi caktuar atyre (muslimanëve) për gratë e tyre dhe për ato që gjenden nën pushtetin e tyre, që për ty të mos ketë brengosje e ngushtesë. – E, Perëndia është falës dhe mëshirues.'),
(3584, 33, 51, 'Ti mund ta shmangësh kë të duash nga ato e ta ofrosh kë të duash, e nuk është mëkat për ty t’i kërkosh (ato) prej të cilave je larguar. Kjo është më e përshtatshme që ato të gëzohen, që të mos pikëllohen dhe që të jenë të kënaqura me atë që u ke dhënë (grave) – të gjithave, e Perëndia di ç’ka në zemrat tuaja; Perëndia është i Gjithëdijshëm dhe i butë.'),
(3585, 33, 52, 'Prej tash – nuk të lejohen gratë (që t’i marrësh për bashkëshorte), as të marrësh tjetër grua në vend të tyre, e sikur madje edhe të të magjeps bukuria e tyre, pos skllaveve. E, Perëndia është mbikëqyrës për çdo gjë.'),
(3586, 33, 53, 'O besimtarë, mos hyni në dhomat e Pejgamberit, përveç nëse u lejohet juve për të ngrënë, por jo të pritni përgatitjen e tij (ushqimit); vetëm kur t’ju thërrasin, atëherë hyni dhe pasi të hani, shpërndahuni pa biseduar me njëri-tjetrin. Kjo (ndalja), me të vërtetë, e shqetësonte Pejgamberin, e ai turpërohej (që t’jua thotë këtë); e Perëndia nuk turpërohet që t’ju thotë të vërtetën. E, kur të kërkoni prej atyre diçka, kërkoni (atë) prapa perdes. Kjo është më e pastër për zemrat tuaja dhe të atyreve. Juve nuk u lejohet që ta shqetësoni Pejgamberin e Perëndisë, as që t’i kurorëzoni gratë e tij – kurrë pas tij. Kjo, me të vërtetë, te Perëndia është mëkat i madh.'),
(3587, 33, 54, 'Në qoftë se e shfaqni apo e fshehni një gjë, Perëndia, me të vërtetë, është i Gjithëdijshëm për çdo gjë.'),
(3588, 33, 55, 'Nuk është mëkat për ato (nëse nuk mbulohen) prej etërve të tyre, dhe djemve të tyre dhe vëllezërve të tyre, dhe djemëve të vëllezërve të tyre, dhe djemëve të motrave të tyre, dhe grave të tyre dhe atyre që janë nën pushtetin e tyre. Druani Perëndisë, se, me të vërtetë, Perëndia është dëshmitar për çdo gjë.'),
(3589, 33, 56, 'Me të vërtetë, Perëndia e mëshiron Pejgamberin, e engjëjt e Tij lutën për te. O besimtarë, lutuni për te dhe përshëndetni ate me “salatë” dhe “selam”!'),
(3590, 33, 57, 'Me të vërtetë, ata që e ofendojnë Perëndinë dhe Pejgamberin e Tij, Perëndia i mallkon (edhe) në këtë botë edhe në tjetrën, dhe për ta ka përgatitur ndëshkim poshtërues.'),
(3591, 33, 58, 'E, ata që i ofendojnë muslimanët dhe muslimanet, për atë që nuk e kanë bërë, (të tillët), në të vërtetë, i kanë ngarkuar vetes shpifje dhe mëkat të qartë.'),
(3592, 33, 59, 'O Pejgamber, thuaju grave tuaja dhe vajzave tuaja dhe grave të muslimanëve, që ta veshin pelerinën e vet. Kështu është më së lehti që të njihen ato (që janë të ndershme) për të mos u ngacmuar. Me të vërtetë, Perëndia është falës dhe mëshirues.'),
(3593, 33, 60, 'Nëse nuk frenohen hipokritët dhe ata – në zemrat e të cilëve ka sëmurje dhe ata që përhapin lajme të rrejshme në Medine, Na – me siguri, ty do të të japim pushtet – mbi ta, pastaj ata, pak kohë do të jetojnë si fqinj me ty në Medine –'),
(3594, 33, 61, 'duke qenë të mallkuar! Kudo që të gjenden, do të kapen dhe mbyten krejtësisht,'),
(3595, 33, 62, '(kjo është) dispozitë e Perëndisë tek ata që kanë qenë më parë! E ti, kurrsesi – nuk do të gjesh ndryshim në dispozitën e Perëndisë.'),
(3596, 33, 63, 'Njerëzit do të të pyesin për Çastin (Kijametin), thuaju: “Dijenia për te ëshë vetëm te Perëndia”. E, kush mund të të tregojë ty, - se Kijameti mund të jetë afër?!'),
(3597, 33, 64, 'Me të vërtetë, Perëndia i ka mallkuar mohuesit dhe për ta ka përgatitur zjarr (të flakëruar),'),
(3598, 33, 65, 'në të cilin ata do të qëndrojnë përherë, përgjithmonë, nuk do të gjejnë as mbrojtës as ndihmës.'),
(3599, 33, 66, 'Në Ditën kur fytyrat e tyre do të rrotullohen në zjarr, ata do të thonë: “Ah, sikur t’ju kishim përulur Perëndisë dhe ta kishim dëgjuar Pejgamberin!”'),
(3600, 33, 67, 'Dhe, do të thonë: “O Zoti ynë, na e kemi dëgjuar parinë tonë dhe eprorët tanë, por ata na shmangen prej rrugës së drjtë.'),
(3601, 33, 68, 'O Zoti ynë, atyre jepju dënim të dyfishtë dhe ata mallkoji me mallim të madh!”'),
(3602, 33, 69, 'O besimtarë, mos u bëni si ata, që e shqetësuan Musain, e Perëndia e pastroi ate nga fjalët që thonin ata, dhe ai te Perëndia ishte i graduar.'),
(3603, 33, 70, 'O besimtarë, druajuni Perëndisë dhe thoni vetëm fjalën e vërtetë,'),
(3604, 33, 71, 'që t’ju përmirësojë veprat tuaja, dhe t’ju falë mëkatet tuaja. E, kush e dëgjon Perëndinë dhe Pejgamberin e Tij, ai ka arritur shpëtim të madh.'),
(3605, 33, 72, 'Na u kemi ofruar amanetin qiejve, Tokës dhe maleve, ato ngurruan ta bartin atë dhe u frikësuan ta pranojnë atë, por, njeriu e pranoi. Me të vërtetë, njeriu është zullumqarë (për vete) dhe i padijshëm (për fundin – epilogun e amanetit), -'),
(3606, 33, 73, 'për t’i dënuar Perëndia: hipokritët dhe hipokritet, idhujtarët dhe idhujtaret, e t’jua pranojë pendimin besimtarëve dhe besimtareve. Se, Perëndia, është falës dhe mëshirues.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Falenderimi qoftë për Perëndinë, të cilit i përket çka ka në qielll dhe çka ka në Tokë! Ai, qoft i falenderuar edhe në jetën tjetër! Ai është i Gjithëdijshëm dhe i Plotëdijshëm.'),
(3608, 34, 2, 'Ai, di çka hyn në tokë dhe çka del prej saj, dhe çka zbret prej qiellit dhe çka ngritet në të. Ai është mëshirues dhe falës.'),
(3609, 34, 3, 'E, mohuesit thonë: “Nuk do të vijë Çasti!” Thuaju: “Po! Pasha Zotin tim, i cili i di të gjitha të padukshmet, me siguri do t’ju vie”. – Nuk do t’i fshehet Atij as sa një thërrmi, as në qiej as në Tokë, e nuk ekziston asgjë – as më e vogël nga ajo (thërrmia), as më e madhe, - e që të mos jetë (e shënuar) në Librin e qartë (Levhi Mahfudh) –'),
(3610, 34, 4, 'për t’i shpërblyer ata që kanë besuar dhe punuar vepra të mira, - për ta ka falje dhe furnizim të mrekullueshëm, -'),
(3611, 34, 5, 'e, ata që luftojnë kundër argumenteve Tona, duke u përpjekur që t’i pengojnë ato – për t’i ikë (dënimit Tonë), për ta ka dënim të tmerrshëm dhe të dhembshëm.'),
(3612, 34, 6, 'Ata, të cilëve u është dhënë dijenia, shohin se çka të është zbritur ty nga Zoti yt, ajo është e vërteta (Kur’ani), i cili të udhëzon në rrugën e të Plotëfuqishmit dhe të Falenderuarit,'),
(3613, 34, 7, 'e, mohuesit thonë: “A doni t’jua tregojmë njeriun i cili ju lajmëron, se, kur të shkapërderdheni plotësisht (në varreza), ju, me siguri do të jeni të krijuar përsëri (të ringjallur)?'),
(3614, 34, 8, 'A po trillon gënjeshtra për Perëndinë apo është i çmendur?” Asnjëra, por ata që nuk besojnë në jetën tjetër, do të dënohen dhe janë në humbje të madhe.'),
(3615, 34, 9, 'E, vallë, a nuk shohin ata çka ka para tyre e pas tyre, prej qiellit dhe Tokës?! Sikur të donim Ne, do t’i fundosnim ata në tokë ose do të hudhnim copa nga qielli. Me të vërtetë, në këtë ka argument për çdo rob që kthehet te Perëndia.'),
(3616, 34, 10, 'Me të vërtetë, Ne i kemi dhënë Davudit dhunti prej Nesh. (Dhe kemi thënë): “O male! Jehoni në lavdërimin Tim bshkë me te (Davudin)! Po ashtu – edhe ju shpendë dhe Ne ia zbutëm atij hekurin,'),
(3617, 34, 11, '(dhe i thamë Ne atij): “Puno ti pancire (këmisha të hekurit) të gjerë! Thurri ato në masa të nevojshme dhe puno vepra të mira. Me të vërtetë, Unë shoh çka punoni ju.'),
(3618, 34, 12, '(Dhe, ia nënshtruam Ne) Sulejmanit – frymën, me të cilën, udhëtimi i paraditës ishte sa i një muaji, e ai i pasditës (po ashtu) sa një muaj; dhe Ne kemi bërë që t’i rrjedhë atij bakri prej burimit (që ecën uji) dhe disa nga xhindët i nënshtroi që të punojnë para syve të tij, me urdhërin e Zotit të tij. E, kushdo prej tyre që largohet nga urdhëri Ynë, kemi bër që ai ta shijojë dënimin e zjarrit.'),
(3619, 34, 13, 'Ata i punonin atij ç’dëshironte: tempuj dhe përmendore, enë të gjera si rezervoar dhe qypa të mëdhenj – të palëvizshëm (për shkak të madhësisë). “O familje e Davudit! Punoni duke falenderuar (Perëndinë)!” – E, pak nga robërit e Mi janë falenderues.'),
(3620, 34, 14, 'E, kur Ne atij ia caktuam vdekjen, atyre nuk u dha shenjë askush, për vdekjen e tij, pos krimbit të drurit (i cili ia brejti shkopin). Dhe, pasi u rrëzua (Sulejmani, iu shkatërrua shkopi), u bë e qartë se xhindët – sikur ta dinin të padukshmen (vdekjen e tij), nuk do të mbetnin në dënimin e poshtëruar.'),
(3621, 34, 15, 'Me të vërtetë, banorët e Sabës kanë pasur argument të madh në vendbanimin e tyre: dy kopshte në të djathtë dhe në të majtë të lumit. (E, u kemi thënë): “Hani nga ato që u ka furnizuar Zoti juaj dhe falenderohuni Atij! (Ky) është vis i mrekullueshëm, (e Zoti juaj) është Zot që falë shumë!”'),
(3622, 34, 16, 'E, ata u shmangën (prej rrugës së drejtë) dhe Na ua dërguam vërshimën e Aremit, (duke e shkatërruar) pendën, dhe kopshtet e tyre ua zëvendësuam me kopshte të tjera, me frute të idhëta (lloj drunjësh) – tamariskë dhe pak lotusa (groshëza).'),
(3623, 34, 17, 'Kështu ata i kemi dënuar për shkak të mohimit, e, a mos dënojmë Ne dikë, pos mohuesve (jomirënjohësve)?!'),
(3624, 34, 18, 'Dhe Ne kemi bërë në mes tyre dhe vendbanimeve që i kemi bekuar, vendbanime të lidhura (dhe të rregulluara); dhe Ne e kemi caktuar nëpër to udhëtimin. (U kemi thënë): “Udhëtoni nëpër to – natë e ditë, të sigurtë!”'),
(3625, 34, 19, 'E ata thanë: “O Zoti ynë, bëne më të madhe distancën në mes udhëtimeve tona”. Andaj, i bënë dëm vetes, dhe Ne i bëmë ata objekt për t’i marrë nëpër gojë, dhe ata plotësisht i kemi shpartalluar. Dhe, në këtë, ka argumente për çdo të durueshëm dhe falenderues.'),
(3626, 34, 20, 'Me të vërtetë, Iblisi e realizoi paramendimin e tij për ta – dhe ata shkuan pas tij, pos një grup besimtarësh.'),
(3627, 34, 21, 'Ai (Iblisi), nuk ka pasur kurrfarë pushteti mbi ta; Ne vetëm kemi dashur të zbulojmë se kush beson në jetën tjetër e kush dyshon në të. E, Zoti yt është mbikëqyrës i çdo gjëje.'),
(3628, 34, 22, 'Thuaju (o Muhammed!): “Thirrni ata të cilët i konsideroni (zotëra), pos Perëndisë! Ata nuk knë pushtet as sa pesha e një thërmie – as në qiej as në Tokë; nuk ka për ta (idhujtarët) as në qiej as në Tokë ndonjë shok, dhe Perëndia – nga ata, nuk ka kurrfarë ndihmësi”.'),
(3629, 34, 23, 'Për dikë tek Ai nuk bënë dobi angazhkmi, përpos atij që i jep leje për të. E, kur të hiqet frika nga zemrat e tyre, i thonë (shoqi-shoqit): “Çka tha Zoti juaj?” Përgjigjen: “Të vërtetën!” Ai është i lartësuar dhe i madh.'),
(3630, 34, 24, 'Thuaju: “Kush u furnizon juve prej qiejve dhe prej Tokës?” – thuaju: “Perëndia! Në të vërtetë, na – ose ju, jemi në rrugën e drejtë ose në humbje të qartë!”'),
(3631, 34, 25, 'Thuaju: “Ju, nuk do të pyeteni për atë që kemi gabuar ne, as që ne do të përgjigjemipër atë që punoni ju”.'),
(3632, 34, 26, 'Thuaju: “Neve do të na tubojë Zoti ynë, e do të gjykojë në me nesh me të drejtë. Ai është Gjykatës i drejtë, i Gjithëdijshëm”.'),
(3633, 34, 27, 'Thuaju: “M’i tregoni ata që ia përshkruani për shok Atij (Perëndisë). Jo, kurrsesi, (nuk do të mund ta bëjnë këtë), por vetëm Ai është Perëndia, i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(3634, 34, 28, 'Na të kemi dërguar ty për tërë njerëzimin – sihariques dhe paralajmërues, por shumica e njerëzve nuk dinë,'),
(3635, 34, 29, 'dhe thonë: “Kur (do të realizohet) ky premtim, nëse ju thoni të vërtetën?”'),
(3636, 34, 30, 'Thuaju atyre: “Juve u është caktuar dita të cilën nuk do të mund ta ngadalësoni as për një çast, as ta shpejtoni”.'),
(3637, 34, 31, 'Dhe, ata që nuk besojnë, thonë: “Na, nuk do të besojmë kurrësesi në këtë Kur’an, as në ato që kanë qenë para tij (Kur’anit)”. E, sikur t’i shihje zullumqarët kur të ndalen para Zotit të tyre dhe kur t’i shkëmbejnë fjalët me njëri-tjetrin, do t’u thonë ata që konsideroheshin të dobët – atyre që madhështoheshin: “Sikur të mos kishit qenë ju, na do të ishim muslimanë”.'),
(3638, 34, 32, 'Ata që madhështoheshin, do t’u thonë atyre që i konsideronin për të dobët: “Vallë, a ne ju kemi penguar nga rruga e drejtë, pasi që ju ka ardhur ajo? Jo, por ju jeni mëkatarë”.'),
(3639, 34, 33, 'Dhe, ata të cilët konsideroheshin të dobët, do t’ju thonë atyre që madhështoheshin: “Jo, por trillimet tuaja natë e ditë, kur na urdhëronit që ta mohojmë Perëndinë dhe t’i përshkruajmë Atij shok”. Dhe ata, e mëshihnin pikëllimin kur e shihnin dënimin. E, Ne do t’u vëmë pranga rreth qafës së atyre që mohuan. E, ata nu do të ndëshkohen për (asgjë) tjetër, pos për atë që kanë punuar.'),
(3640, 34, 34, 'Në asnjë vendbanim Na nuk kemi dërguar ndonjë pejgamber, e ata që bënin jetë luksoze, të mos thonin: “Me të vërtetë, na nuk besojmë në atë, me të cilën jeni dërguar!”'),
(3641, 34, 35, 'Dhe, ata thonë: “Na kemi më shumë pasuri dhe fëmijë, e na nuk do të jemi të munduar”.'),
(3642, 34, 36, 'Thuaju: “Me të vërtetë, Zoti im, i jep furnizim të bollshëm kujt të dojë dhe ia mungon (kujt të dojë), por shumica e njerëzve nuk e dinë këtë.'),
(3643, 34, 37, 'As pasuria juaj, as fëmijët tuaj, nuk janë ata që do t’ju afrojnë në pozitë te Ne, por vetëm ata që besojnë dhe punojnë vepra të mira. Për ta shpërblimi është i shumëfishtë, për atë që kanë punuar, dhe ata do të jenë të sigurtë në dhomat e larta të xhennetit.'),
(3644, 34, 38, 'E, ata që përpiqen t’i thyejnë argumentt Tona, duke menduar se nuk do të mund t’i dënojmë, ata do të dënohen.'),
(3645, 34, 39, 'Thuaj: “Me të vërtetë, Zoti im, i jep furnizim të bollshëm kujt të dojë nga robërit e Tij dhe ia pakëson kujt të dojë prej tyre; e, Ai ua kompenson atë që e ndani ju; dhe Ai është më i miri furnizues”.'),
(3646, 34, 40, 'Dhe, (kujtoje) atë Ditë kur do t’i tubojë të gjithë e pastaj u thotë engjëjve: “Vallë, a këta ju kanë adhuruar juve?”'),
(3647, 34, 41, '(engjëjt) thonë: “Qofsh i lavdëruar Ti o Zoti ynë! Na, ty të adhurojmë, e me ata s’kemi asgjë. Aa i adhuronin xhindët dhe shumica e mohuesve u besonin (fjalëve) të tyre (të xhindëve)”.'),
(3648, 34, 42, 'E, sot, nuk do të mund t’i bëni as dobi as dëm njëri-tjetrit. E Ne, do t’ju themi zullumqarëve: “Shijoni dënimin e zjarrit të cilin e keni konsideruar gënjeshtër!”'),
(3649, 34, 43, 'E, kur ju lexohen atyre ajetet Tona të qarta, ata thonë: “Ky është vetëm njeri, që dëshiron t’ju pengojë nga ajo që etërit tuaj kanë adhuruar”, dhe ata thonë: “Kjo nuk është asgjë tjetër pos gënjeshtër e trilluar!” Dhe, thonë ata që nuk besojnë të Vërtetën (Kur’anin) pasi ju ka ardhur: “Kjo është vetëm magji e qartë!”'),
(3650, 34, 44, 'Dhe, Ne, nuk u kemi dërguar (banorëve të Mekkes) asnjë libër, për ta mësuar ata, dhe Ne nuk u kemi dërguar atyre para Teje, asnjë paralajmërues.'),
(3651, 34, 45, 'Ata që kanë qenë para tyre, i kanë konsideruar gënjeshtarë (pejgamberët). Ata nuk kanë arritur as të dhjetën e asaj që u kemi dhënë Ne (fuqi dhe jetë të gjatë), e megjithatë, i kanë konsideruar gënjeshtarë pejgamberët e Mi, e çfarë ka qenë dënimi Im (ndaj tyre)!'),
(3652, 34, 46, 'Thuaj (o Muhammed!): “Unë ju këhsilloj vetëm me një gjë: ngrituni sinqerisht për Perëndinë, dy nga dy, ose një nga një e pastaj gjykoni për shokun tuaj (Muhammedin) që nuk është i çmendur. Ai për ju është vetëm paralajmërues, para dënimit të rëndë.'),
(3653, 34, 47, 'Thuaj: “Çfarëdo shpërblimi që kam kërkuar prej jush, ajo qoftë për ju. Shpërblimi im është vetëm te Perëndia, e Ai është dëshmitar për çdo gjë”.'),
(3654, 34, 48, 'Thuaj: “Me të vërtetë, Zoti im e sjell të Vërtetën (te pejgamberët), Ai është i Gjithëdijshëm për të gjitha sekretet”.'),
(3655, 34, 49, 'Thuaj: “E vërteta (Islamizmi) ka ardhur, kurse gënjeshtra as nuk mund të shfaqet e as të përsëritet.'),
(3656, 34, 50, 'Thuaj: “Nëse humbi, unë humbi vetëm në dëmin tim, e nëse udhëzohem në rrugën e drejtë, kjo është nga shpallja e Zotit tim. Ai, me të vërtetë, i dëgjon të gjitha dhe është afër”.'),
(3657, 34, 51, 'Sikur t’i shihje ata kur frikësohen, e nuk kanë ku të ikin; do të kapen për së afërmi (pa mund të ikur asnjë hap),'),
(3658, 34, 52, 'e thonë: “Na besojmë në atë (Kur’anin)”. E, prej nga ata ta përvetësojnë (besimin) prej vendit të largët (botës së ardhshme);'),
(3659, 34, 53, 'E, ata e kanë mohuar atë (Kur’anin) qysh më parë (në këtë botë), dhe i hudhin të padukshmet nga aspekti i largësisë (prej të vërtetës);'),
(3660, 34, 54, 'dhe ka ekzistuar distancë në mes tyre dhe asaj që dëshironin ata, ashtu siç, është vepruar me të ngjashmit – më parë. Me të vërtetë, të gjithë ata kanë qenë në dyshim të madh.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Falenderimi qoftë për Perëndinë, krijuesit të qiejve, dhe Tokës, i cili i ka bërë engjëjt pejgamberë me krahë, me nga dy, tre dhe katër. Ai shtonnë krijimtari çka të dojë. Perëndia, me të vërtetë, është i pushtetshëm për çdo gjë.'),
(3662, 35, 2, 'Atë që Perëndia ua ka dërguar njerëzve nga mëshira e Tij, nuk mund ta pengojë askush, e atë që Ai e pengon – askush nuk mundet, pas Tij, ta jap; Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(3663, 35, 3, 'O njerëz! Kujtonie dhuntinë e Perëndisë juve. A ka ndonjë krijues, pos Perëndisë, i cili ju furnizon nga qielli dhe Toka? S’ka zot tjetër përveç Tij, e nga po ia mbani ju.'),
(3664, 35, 4, 'Nëse ata ty (o Muhammed!) të konsiderojnë gënjeshtar, e me të vërtetë, edhe para teje, pejgamberët i kanë konsideruar gënjeshtarë, e te Perëndia kthehen të gjitha punët.'),
(3665, 35, 5, 'O njerëz! Padyshim, premtimi i Perëndisë është i vërtetë, e mos t’ju mashtrojë, kurrsesi, jeta e kësaj bote dhe le të mos ju mashtrojë, kurrsesi mashtruesi në Perëndinë.'),
(3666, 35, 6, 'Me të vërtetë, djalli është armik për ju, andaj merreni atë për armik! Ai vetëm i thërret ithtarët e vet që të bëhen shokë të zjarrit.'),
(3667, 35, 7, 'Për ata që nuk besojnë ka dënim të rëndë, e për ata që besojnë dhe punojnë vepra të mira ka falje dhe shpërblim të madh.'),
(3668, 35, 8, 'E vallë, a është ai që iu kanë zbukuruar veprat e tij të këqia dhe i duken ato të mira (si ai që është i i udhëzuar në rrugë të drejtë)? Me të vërtetë, Perëndia e shpie në humbje kë të dojë, dhe e udhëzon në rrugë të drejtë kë të dojë. E, mos u shkatërro ti për humbjen e tyre! Perëndia, me të vërtetë, është i Gjithëdijshëm për atë që punojnë ata.'),
(3669, 35, 9, 'Dhe, Perëndia – është ai që dërgon erërat, që i lëvizin retë, dhe Ne i drejtojmë ato në viset e vdekura, e me to e gjallërojmë tokën pas vdekjes së saj. Kështu do të jetë edhe ringjallja.'),
(3670, 35, 10, 'Kush dëshiron madhërinë, e tërë madhëria është e Perëndisë, (andaj kërkoni prej Tij). Tek Ai ngriten fjalët e bukura, dhe veprën e mirë Ai e pranon. E, për ata që trillojnë vepra të këqia – do të ketë dënim të rëndë, dhe trillimi i tyre do të falimentojë.'),
(3671, 35, 11, 'Perëndia, u ka krijuar së pari prej dheut, pastaj nga pika e ujit (farës – spermës), dhe më në fund u ka bërë çifte (meshkuj e femra). Dhe, asnjë femër nuk mbetet shtatëzënë, e as nuk lind pa dijeninë e tij. Dhe jeta e askujt – as nuk zgjatet as nuk shkurtohet e që të mos jetë e shënuar në Libër. Me të vërtetë, kjo për Perëndinë është lehtë!'),
(3672, 35, 12, 'As dy detet nuk janë të njëjta: njëri është i ëmbël dhe i këndshëm, e që uji i pihet lehtë, e tjetri është i njelmët dhe i idhët. Ju ushqeheni me mish të njomë prej njërit dhe tjetrit dhe prej tyre nxirrni stoli që i mbani; dhe ti i sheh anijet se nëpër të çajnë ujin, për të kërkur nga dhuntitë e Tij, e që të bëheni falenderues.'),
(3673, 35, 13, 'Ai (Perëndia) e shpie natën (e zgjatë njërën dhe shkurton tjetrën), dhe e ka nënshtruar Diellin dhe Hënën – secili lëvizë deri në kohën e caktuar; ky është Perëndia, Zoti juaj. Pushteti është i Tij! E, ata që i adhuroni ju, pos Tij – nuk posedojnë as sa lëkura e bërthamës së hurmës.'),
(3674, 35, 14, 'Nëse ju i thërrisni ata, thirrjen tuaj ata nuk e dëgjojnë, e (edhe) sikur ta dëgjonin, nuk do t’ju përgjigjeshin. Edhe në Ditën e Kijametit, ata do ta mohojnë atë që ia përshkruanin për shok Perëndisë. Dhe, askush nuk do të informojë ty (o Muhammed) ashtu si Ai i Gjithëdijshmi.'),
(3675, 35, 15, 'O njerëz! Ju jeni të varfër te Perëndia, (nevojtarë për çdo gjë). E, Perëndia s’ka nevojë për asgjë dhe është i denjë për falenderim.'),
(3676, 35, 16, 'Nëse don (Ai), ju zhdukë dhe sjellë krijesa të reja,'),
(3677, 35, 17, 'kjo për Perëndinë nuk është vështirë.'),
(3678, 35, 18, 'Dhe, asnjë mëkatarë nuk do ta bartë mëkatin e tjetrit. En, ëse i ngarkuari (me mëkate), madje (i thirruri) qoftë edhe i afërt. E, ti i paralajmëron vetëm ata, që i druajnë Zotit të tyre edhe në fshehtësi dhe që bëjnë namazin. E, ai që pastrohet nga mëkatet, ai është pastruar për dobi të vet – e kthimi është te Perëndia.'),
(3679, 35, 19, 'Nuk janë, kurrsesi, njësoj i verbëti dhe ai që sheh,'),
(3680, 35, 20, 'as errësira dhe drita,'),
(3681, 35, 21, 'as freskia (e hijes) dhe vapa,'),
(3682, 35, 22, 'as nuk janë, kurrsesi, të barabartë, të gjallët dhe të vdekurit. Me të vërtetë, Perëndia bënë që të dëgjojë – kë të dojë Ai, e ti (o Muhammed) nuk mund të bësh që të dëgjojnë ata që janë në varre,'),
(3683, 35, 23, 'ti je vetëm paralajmërues.'),
(3684, 35, 24, 'Ne të kemi dërguar ty me të Vërtetën – sihariq dhe paralajmërues; e nuk ka pasur asnjë popull që të mos i ketë ardhur paralajmërues.'),
(3685, 35, 25, 'E, nëse ata të konsiderojnë ty gënjeshtar, e madje edhe paraardhësit e këtyre i kanë përgënjeshtruar pejgamberët. Pejgamberët e tyre kanë ardhur me dokumente të qarta, fletushka, dhe Libër ndriçues.'),
(3686, 35, 26, 'Pastaj, Unë i kam dënuar mohuesit, e çfarë ka qenë dënimi Im!'),
(3687, 35, 27, 'Vallë, a nuk di ti, që Perëndia e lëshon ujin prej qiellit, e që Ne, me të krijojmë frute llojesh e ngjyrash të ndryshme; e nëpër male gjenden rrugë të bardha dhe të kuqe, ngjyrash të ndryshme, dhe krejtësisht të zeza.'),
(3688, 35, 28, 'Ka edhe njerëz, edhe shtazë, edhe bagëti – po ashtu llojesh të ndryshme. Me të vërtetë, Perëndisë i druajnë vetëm të dijshmit prej robërve të Tij. Me të vërtetë, Perëndia është i Plotëfuqishëm dhe falës.'),
(3689, 35, 29, 'Padyshim, ata që e lexojnë Librin e Perëndisë dhe kryejnë namazin, dhe haptazi e fshehurazi japin nga ajo me çka i kemi furnizuar Ne, shpresojnë shpërblimin që nuk do të humbas,'),
(3690, 35, 30, 'që (Ai) t’jua plotësojë shpërblimin dhe që t’ju shtojë atyre nga dhuntitë e Tij. Me të vërtetë, Ai është falës dhe falenderues.'),
(3691, 35, 31, 'E, atë që ta kemi shpallur ty prej Librit, është e vërtetë e saktë, duke qenë vërtetues për atë që është shpallur para tij. – Perëndia, me të vërtetë, për robërit e Vet i di të gjitha dhe Ai i sheh të gjitha.'),
(3692, 35, 32, 'Pastaj, Ne u kemi dhënë Librin (Kur’anin) robërve Tanë të zgjedhur. Disa prej tyre i kanë bërë dëm vetes (duke bërë mëkate), e disa prej tyre janë në rrugë të mesme, e disa prej tyre shkojnë përpara me të mira, me lejën e Perëndisë. Kjo është dhunti e madhe,'),
(3693, 35, 33, 'xhennetet e Adnit në të cilin hyjnë ata, duke u veshur me bylyzykë të arit dhe margaritarë, veshja e tyre, në to, është prej mëndafshi.'),
(3694, 35, 34, 'Dhe thonë ata: “Falenderojmë Perëndinë, i cili e ka zhdukru hidhërimin nga ne. Me të vërtetë, Zoti ynë është falës dhe falenderues –'),
(3695, 35, 35, 'i cili, me mirësinë e Tij na ka shpënë në shtëpinë e banimit të përhershëm, në të cilën nuk do të lodhemi dhe në të cilën nk do të rraskapitemi”.'),
(3696, 35, 36, 'E, për ata që nuk besojnë është zjarri i skëterrës, ata nuk do të dënohen (me vdekje), dhe nuk do të vdesin, dhe as që do t’u lehtësohet dënimi në të, - ja kështu, Ne do ta dënojmë çdo mohues, -'),
(3697, 35, 37, 'ata, në të, do të thërrasin (duke bërtitur): “O Zoti ynë, nxirrna (prej zjarrit), do të bëjmë vepra të mira, e jo asosh çfarë kemi bërë”. (Atyre ju thuhet): “Vallë, a nuk u kemi dhënë Ne jetë juve, aq sa që të mendohet pët të – ai që don të këshillohet, e juve madje, u pat ardhur paralajmëruesi. Andaj, shijoni (dënimin). E, për zullumqarët nuk ka kurrëfarë ndihmësi!'),
(3698, 35, 38, 'me të vërtetë, Perëndia është i Gjithëdijshëm për sekretet e qiejve dhe të Tokës. Me të vërtetë, Ai di çka ka në zemrat e njerëzve.'),
(3699, 35, 39, 'Ai u ka bërë juve zëvendës (njërin pas tjetrit) në Tokë; e kush nuk beson, mosbesimi i tij është në dëm të tij. E, mohuesve – mosbesimi i tyre ua shton te Zoti i tyre pezmin, dhe mohuesve – mosbesimi i tyre ua shton humbjen.'),
(3700, 35, 40, 'Thuaju atyre: “A i keni pa shokët tuaj (idhujt) që i adhuroni, përveç Perëndisë? Më tregoni Mua – se çka kanë krijuar ata në Tokë ose a kanë marrë pjesë në krijimin e qiejve, ose a u kemi dhënë Ne atyre një Libër, e ata në të kanë argument për këtë (çështje)?” Jo, por zullumqarët i premtojnë njëri-tjetrit vetëm mashtrime.'),
(3701, 35, 41, 'Me të vërtetë, Perëndia mbron ekuilibrin e qiejve dhe Tokës nga shkatërrimi. E, nëse ato nisen kah shkatërrimi, askush nuk mund t’i mbrojë, pos Tij. Me të vërtetë, Ai është i butë (nuk nxiton për të dënuar), dhe falës (i mëkateve).'),
(3702, 35, 42, 'Dhe, ata janë betuar në Perëndinë, me betimin e rëndë (që) nse iu vie paralajmërues, ata – në të vërtetë, do të jenë ndër më të udhëzuarit nga çdo popull tjetër. E, pasi iu erdhi atyre paralajmëruesi, (kjo) vetëm ua shtoi largimin (prej rrugës së drejtë),'),
(3703, 35, 43, '(për) mendjemadhësinë në Tokë dhe dinakëritë e shëmtuara që e godasin vetëm pronarin e tij. Vallë, a presin ata diçka tjetër pos dispozitës së atyre të mëparshmëve? Ti, me të vërtetë, nuk mund të gjesh ndryshim në dispozitën e Perëndisë dhe as që do të gjesh transformim në dispozitën e Perëndisë.'),
(3704, 35, 44, 'Vallë, a nuk kanë udhëtuar ata nëpër botë e të shohin si ka qenë përfundimi i të mëparshmëve, e të cilët kanë qenë më të fortë se këta? Dhe, Perëndisë nuk mund t’i ikë (shpëtojë) asgjë, (as) në qiej as në Tokë. Ai, me të vërtetë, është i Gjithëdijshëm dhe i pushtetshëm për çdo gjë.'),
(3705, 35, 45, 'E, sikur Perëndia t’i dënonte njerëzit sipas veprave (të këqia) që bëjnë, nuk do të mbetej në sipërfaqen e Tokës asgjë (e gjallë), por Ai i shtyen ata deri në afatin e caktuar, dhe kur t’u vie afati i tyre, - Perëndia, me të vërtetë, i sheh mirë robërit e Vet.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'Jâ Sîn.'),
(3707, 36, 2, 'Pasha Kur’anin plotë dijeni,'),
(3708, 36, 3, 'me të vërtetë, ti (o Muhammed!) je pejgamber,'),
(3709, 36, 4, 'në rrugën e drejtë,'),
(3710, 36, 5, 'me shpalljen e të Plotëfuqishmit, Mëshiruesit,'),
(3711, 36, 6, 'që ta paralajmërosh popullin – paraardhësit e të cilit nuk ishin të paralajmëruar, prandaj (ata) kanë qenë të pakujdesshëm!'),
(3712, 36, 7, 'Me të vërtetë, u realizua Fjala (e dënimit) ndaj shumicës së tyre – e ata nuk besojnë.'),
(3713, 36, 8, 'Ne u vumë pranga në qafë, e ato u arrijnë deri në nënmjekërr, - andaj ata janë me kokë përpjetë,'),
(3714, 36, 9, 'dhe Ne, vumë perde para tyre dhe perde pas tyre, e i mbuluam, e ata nuk shohin,'),
(3715, 36, 10, 'dhe për ata është njëlloj, i paralajmërove oe nuk i paralajmërove, ata nuk besojnë.'),
(3716, 36, 11, 'Me të vërtetë, paralajmërimi yt i sjell dobi vetëm atij që ndjek Këshillën (Kur’anin) dhe i druan Mëshiruesit dhe fshehtas (pa e parë); e sihariqoje atë duke ia falë mëkatet dhe duke i dhënë shpërblim të begatshëm!'),
(3717, 36, 12, 'Ne, me të vërtetë, i ringjallim të vdekurit, shënojmë veprat që i kanë paraqitur dhe gjurmët e tyre, e Ne i numërojmë të gjitha ato në Librin e qartë.'),
(3718, 36, 13, 'Përmendju atyre shembullin e banorëve të qytetit, kur u erdhën atyre pejgamberët;'),
(3719, 36, 14, 'Kur Ne u dërguam atyre dy, e ata i konsideronin gënjeshtarë, madje i përforcuam me të tretin, e thanë: “Ne, me të vërtetë, jemi dërguar te ju!”'),
(3720, 36, 15, 'Ata (banorët e qytetit), ju thanë: “Ju jeni vetëm njerëz si ne, e Bamirësi nuk ju ka zbritur asgjë; ju vetëm gënjeni”.'),
(3721, 36, 16, 'Thanë (pejgamberët): “Zoti ynë e di që ne, me të vërtetë, jemi dërguar pejgamberë te ju,'),
(3722, 36, 17, 'dhe se jemi të obliguar vetëm që të shpallim qartë”.'),
(3723, 36, 18, 'Thanë (banorët): “Ne parandjejmë fatkeqësi nga ju; e nëse nuk frenoheni, do t’ju gjuamë me gurë dhe me siguri do t’ju godas dënimi i dhembshëm prej nesh”.'),
(3724, 36, 19, 'Thanë (pejgamberët): “Fatkeqësia juaj është me ju. Vallë, a për atë që jeni këshilluar (e konsideroni fatkeqësi)? Jo, (por) ju jeni popull që kaloni kufirin në të këqia”.'),
(3725, 36, 20, 'E, erdhi një njeri nga periferia e qytetit – duke nxituar dhe tha: “O populli im, pasoni pejgamberët;'),
(3726, 36, 21, 'pasoni ata, që nuk kërkojnë prej jush kurrfarë shpërblimi, e ata janë në rrugën e drejtë!'),
(3727, 36, 22, 'Pse të mos e adhuroj unë Ate, i cili më ka krijuar. E, te Ai do të ktheheni ju.'),
(3728, 36, 23, 'Vallë, a marr unë tjetër zot – pos Atij? Nëse Bamirësi i Gjithëmbarshëm dëshiron të më godas me ndonjë të keqe, - ndërmjetësimi i tyre nuk do të më sjell kurrfarë dobie, e as që do të munden të më shpëtojnë ata.'),
(3729, 36, 24, 'Unë, atëherë – me siguri, do të jem në humbje të plotë.'),
(3730, 36, 25, 'Unë, me të vërtetë, i kam besuar Zotit tuaj, andaj, më dëgjoni!”'),
(3731, 36, 26, 'I thuhet atij: “Hyn në xhennet!” Ai tha: “Ah, sikur ta dinte populli im,'),
(3732, 36, 27, 'për çka më fali (nga mëkatet) Zoti im dhe më bëri të nderuar!”'),
(3733, 36, 28, 'Dhe, Ne nuk i kemi dërguar kurrfarë ushtrie (nga qielli) popullit të tij – pas tij, dhe Ne nuk kemi dërguar asgjë,'),
(3734, 36, 29, 'vetëm një zë të tmerrshëm, e u shuan përnjëherësh.'),
(3735, 36, 30, 'Ah, sa mjerim për ata njerëz! Kurdo që u vinte ndonjë pejgamber, ata nuk bënin tjetër, por vetëm talleshin me te.'),
(3736, 36, 31, 'Vallë, a nuk panë ata se sa e sa gjenerata para tyre i kemi shkatërruar, e nuk u kthyen,'),
(3737, 36, 32, 'e të gjithë ata do të paraqiten bashkërisht te Ne.'),
(3738, 36, 33, 'E, dëshmi për ta – është toka e vdekur; Ne e ngjallim atë, dhe nga ajo nxjerrim kokrra (drithëra), me të cilat ushqeheni.'),
(3739, 36, 34, 'Ne kemi krijuar në tokë kopshte hurmesh dhe vreshta, - dhe kemi bërë në të burime uji,'),
(3740, 36, 35, 'që ata t’i hanë frutet e tyre dhe nga ato që i punojnë duart e tyre; e përse ata nuk duan të jenë mirënjohës?'),
(3741, 36, 36, 'Qoftë lavdëruar Ai që i ka krijuar të gjitha llojet (gjinitë): çka binë prej tokës, dhe nga ata vetë (njerëzit) dhe nga ajo që ata nuk dinë!'),
(3742, 36, 37, 'Dhe, argument për ta është nata: Na e menjanojmë (dritën) e ditës dhe atëherë – ata mbetën në errësirë.'),
(3743, 36, 38, 'Dhe Dielli sillet deri në kufirin e vet të caktuar. Kjo është dispozitë e të Plotëfuqishmit dhe Gjithëdijshmit.'),
(3744, 36, 39, 'Dhe Hënës ia kemi caktuar pozitat; ajo kthehet (duke qenë e plotë) e bëhet si rremi (i hollë) i vjetër i hurmës.'),
(3745, 36, 40, 'As Dielli nuk mund ta arrijë Hënën, e as nata (Hëna) nuk mund ta kalojë ditën (Diellin); të gjitha lundrojnë në hapsirën qiellore (ose: çdo trup qiellor lëvizë).'),
(3746, 36, 41, 'Dhe argument për ta është që pasardhësit e tyre i ngarkuam në anije përplot,'),
(3747, 36, 42, 'dhe, për ta, Ne krijuam bartëse të ngjashme me anijen në të cilën hipin (e udhëtojnë).'),
(3748, 36, 43, 'Dhe, po të duam Ne, ata i fundosim; andaj – as nuk do të mund t’u ndihmojë kush, as nuk do të mund të shpëtojnë,'),
(3749, 36, 44, 'përpos me mëshirën Tonë dhe të kënaqen deri në një kohë.'),
(3750, 36, 45, 'E, kur atyre u thuhet: “Druajuni asaj (dënimit) që keni para jush (në këtë botë) dhe atë që është pas jush (botën tjetër), për të qenë të mëshiruar...”'),
(3751, 36, 46, 'Dhe, atyre nuk u vinte asnjë argument nga argumentet e Zotit të tyre, e që ata të mos shmangeshin nga ato.'),
(3752, 36, 47, 'E, kur atyre ju thuhet: “Jepni nga ato me të cilat Perëndia u ka furnizuar juve”, - e atëherë, mohuesit u thonë besimtarëve: “Vallë, a ne t’i ushqejmë ata, të cilët do t’i ushqente Perëndia – po të donte? Me të vërtetë, ju jeni në humbje të qartë!”'),
(3753, 36, 48, 'Dhe, ata (mohusit) thonë: “Kur do të realizohet ky premtim, nëse thoni të vërtetën?”'),
(3754, 36, 49, 'Ata presin vetëm një zë të tmerrshëm, që do t’i kaplojë duke u grindur njëri me tjetrin,'),
(3755, 36, 50, 'E, ata nuk do të mund t’i kryejnë porositë e as të kthehen te familja e tyre.'),
(3756, 36, 51, 'Dhe, do të fryhet në Sur, e ata do të dalin nga varret dhe nxitojnë kah Zoti i tyre,'),
(3757, 36, 52, 'duke thënë: “Kuku për ne! kush na ngjalli (çoi) prej shtretërve tanë?” (Ju thuhet): “Ja, kjo është ajo që e ka premtuar Mëshiruesi i Madh, e pejgamberët e kanë thënë të vërtetën!'),
(3758, 36, 53, 'Do të jetë ai vetëm një zë i tmerrshëm, e ata të gjithë (përnjëherë) do të prezentohen para Nesh.'),
(3759, 36, 54, 'Ja, sot (në këtë ditë), askujt nuk do t’i bëhet ndonjë padrejtësi, dhe ju do të shpërbleheni vetëm për atë që keni punuar.'),
(3760, 36, 55, 'Atë ditë, me të vërtetë, banorët e Xhennetit janë të angazhuar me kënaqësi,'),
(3761, 36, 56, 'ata dhe gratë e tyre janë në hije (fllade), të mbështetur në kolltuqe,'),
(3762, 36, 57, 'në të (xhennet) – për ta, ka pemë dhe çdo gjë që dëshirojnë.'),
(3763, 36, 58, '“Selam!” – (përshëndetën ata) me fjalë prej Zotit Mëshirues, -'),
(3764, 36, 59, '(u thuhet): “e veçohuni sot ju, o mëkatarë!”'),
(3765, 36, 60, 'Vallë, a nuk ju kam urdhëruar, o bijtë e Ademit, që të mos e adhuroni djallin, se ai, me të vërtetë, është armik i përbetuar i juaji,'),
(3766, 36, 61, 'por vetëm Mua më adhuroni! Kjo është rrugë e drejtë.'),
(3767, 36, 62, 'Ai (djalli), me të vërtetë, ka humbur shumë grupe njerëzish prej jush, e vallë, a nuk keni menduar!'),
(3768, 36, 63, '(U thuhet atyre): “Ky është xhehennemi, që u është premtuar,'),
(3769, 36, 64, 'kalluni (digjuni) tani në të (xhehennem), ngase nuk keni besuar!”'),
(3770, 36, 65, 'Sot, Ne do t’u vulosim gojët e tyre, e do të flasin duart e tyre dhe do të dëshmojnë këmbët e tyre për atë që kanë fituar.'),
(3771, 36, 66, 'Sikur të donim Ne, Ne do t’ju vërbonim sytë atyre duke ua rrafshur fytyrën, e ata do ta kërkonin rrugën; po, vallë si do të shihnin?'),
(3772, 36, 67, 'Dhe, sikur të kishim dashur, Ne do t’i kishim shndërruar ata (prej krijese njerëzore në krijesa tjera) në atë moment, e ata nuk do të mund as të shkonin e as të ktheheshin.'),
(3773, 36, 68, 'Kujt i japim jetë të gjatë, Na ia ndryshojmë strukturën e tij. E, vallë, a nuk kuptojnë ata?'),
(3774, 36, 69, 'Na, atij (Muhammedit) nuk ia mësuam poetikën, e as që ajo është për te. Këtë (që e ka sjellë) është vetëm këshillë dhe Kur’ani i qartë,'),
(3775, 36, 70, 'për ta paralajmëruar atë që është i gjallë (i mençur), dhe për t’u realizuar fjala (dënimi) ndaj mohuesve.'),
(3776, 36, 71, 'Vallë, a nuk shohin ata, se Ne për ta i kemi krijuar shtazët – me mjeshtrinë Tonë, e ata i posedojnë!'),
(3777, 36, 72, 'dhe bëmë që ato t’u nënshtrohen atyre – disa i shalojnë e prej disave ushqehen,'),
(3778, 36, 73, 'dhe nga ato kanë shumë dobi, dhe pije (qumështi). E, vallë, si nuk falenderohen!'),
(3779, 36, 74, 'Dhe, ata pos Perëndisë, kanë besuar në zotëra tjerë, duke shpresuar se do t’u ndihmojnë ata (idhujt);'),
(3780, 36, 75, 'mirëpo, ata nuk mund t’u ndihmojnë atyre, dhe ata janë për ta një ushtri e pranishme (në ferr).'),
(3781, 36, 76, 'Dhe, mos të pikëllojnë ty fjalët e tyre! Na, me të vërtetë, i dimë ato që i fshehin ata dhe çka shpallin.'),
(3782, 36, 77, 'A nuk sheh njeriu se si Ne e krijuam atë prej pikës së ujit (farës – spermës), e megjithatë, ai është kundërshtar i hapët,'),
(3783, 36, 78, 'dhe ai na sjell shembull, e harron krijimin e vet. Thotë: “Kush do t’i ngjallë eshtrat duke qenë ato të kalbura (shkapërderdhura)?”'),
(3784, 36, 79, 'Thuaj: “Do t’i ngjallë Ai që i krijoi për herë të parë; Ai i di të gjitha ato që i ka krijuar,'),
(3785, 36, 80, '(Ai), i cili e bër për ju – prej drurit të njomë – zjarrin dhe ju prej ti ndizni”.'),
(3786, 36, 81, 'Vallë, a nuk është i pushtetshëm ai që krijoi qiejt dhe Tokën – të krijoi qenie të ngjashme (të ringjallë pas vdekjes)? Po, me të vërtetë, Ai është Krijues i çdo gjëje dhe i Gjithëdijshëm;'),
(3787, 36, 82, 'Dispozita e Tij (Perëndisë), kur dëshiron të bëjë diçka, është vetëm të thotë: “Bëhu!” – dhe ajo bëhet.'),
(3788, 36, 83, 'I lartësuar është Ai, në duart e të cilit është pushteti mbi çdo gjë! Dhe, tek Ai do të ktheheni!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Pasha ata (engjëjt) që janë të radhitur'),
(3790, 37, 2, 'dhe ata që i pengojnë (njerëzit prej mëkateve)'),
(3791, 37, 3, 'dhe ata që e lexojnë Kur’anin, -'),
(3792, 37, 4, 'në të vërtetë, Zoti juaj është një,'),
(3793, 37, 5, 'Zot i qiejve dhe i Tokës dhe çka gjendet mes tyre dhe Zot i lindjes (i verës dhe dimërit)!'),
(3794, 37, 6, 'Me të vërtetë, Ne e kemi stolisur qiellin e kësaj bote me shkëlqimin e yjeve,'),
(3795, 37, 7, 'dhe e mbrojmë nga çdo djall kryeneç,'),
(3796, 37, 8, 'që mos t’i përgjojë meleqet e lartësuar; ata (djajtë) sulmohen nga të gjitha anët,'),
(3797, 37, 9, 'për t’i dëbuar ata: për ta ka vuajtje të përhershme,'),
(3798, 37, 10, 'e ai që grabitë diçka, atë e ndjekë dritë verbuese.'),
(3799, 37, 11, 'Pyeti ata (o Muhammed): a është më i vështirë krijimi ityre apo të gjitha gjërat e tjera që i kemi krijuar? – Ata, i kemi krijuar nga bota (balta) ngjitëse.'),
(3800, 37, 12, 'Ti çuditesh (nga mohimi i atyre) e ata përqeshin,'),
(3801, 37, 13, 'e kur t’iu drejtohen këshilla, ata nuk i pranojnë,'),
(3802, 37, 14, 'dhe kur e shohin një argument, ata e nxisin njëri-tjetrin për përqeshje'),
(3803, 37, 15, 'dhe thonë: “Kjo s’është gjë tjetër veçse një magji e vërtetë!”'),
(3804, 37, 16, '(Ata thonë): “Vallë kur të vdesim dhe të bëhemi eshtra e dhé, a thua njëmend do të ngjallemi'),
(3805, 37, 17, 'dhe paraardhësit tonë të lashtë?”'),
(3806, 37, 18, 'Thuaju: “Po, e dot ë jeni edhe të poshtëruar!”'),
(3807, 37, 19, 'Ky do të jetë vetëm një zë dhe të gjithë do të presim (se ç’po ndodhë)'),
(3808, 37, 20, 'dhe do të thonë: “Mjer për ne, kjo është Dita e gjykimit!”'),
(3809, 37, 21, '(Atyre ju thuhet): “Po, kjo është Dita e gjykimit, të cilën ju e keni përgënjeshtruar!”'),
(3810, 37, 22, '(Engjëjve ju thuhet): “Tuboni njerëzit e këqinj e shokët e tyre dhe ato që i kanë adhuruar'),
(3811, 37, 23, 'pos Perëndisë, e tregoju atyre rrugëne Ferrit;'),
(3812, 37, 24, 'dhe i ndalni ata, ata do të pyetën:'),
(3813, 37, 25, '“Çka keni që nuk e ndihmoni njëri-tjetrin (idhujtë dhe adhuruesit e tyre)?”'),
(3814, 37, 26, 'Por, ata atë Ditë do të janë plotësisht të dorëzuar,'),
(3815, 37, 27, 'e do t’i afrohen njëri-tjetrit dhe do të bëjnë pyetje mes veti;'),
(3816, 37, 28, 'Do të thonë: “Ju, me të vërtetë, na keni ardhur nga ana e djathtë (e me forcë)”.'),
(3817, 37, 29, 'Ata do të përgjigjen: “Jo, por ju nuk keni qenë besimtarë,'),
(3818, 37, 30, 'e ne nuk kemi pasur kurrfarë pushteti mbi ju, por ju ishit popull i tërbuar,'),
(3819, 37, 31, 'andaj, u realizua fjala e Zotit mbi ne. Ne, me të vërtetë, po e shijojmë dënimin,'),
(3820, 37, 32, 'e, ju kemi mashtruar juve, meqë edhe ne kemi qenë të mashtruar”.'),
(3821, 37, 33, 'Dhe ata, atë Ditë do të jenë së bashku në mundim,'),
(3822, 37, 34, 'sepse, Ne kështu veprojmë me të këqinjtë.'),
(3823, 37, 35, 'Kur u thuhej atyre: “Vetëm Allahu është Zot!” – ata madhështoheshin,'),
(3824, 37, 36, 'dhe thonin (Mekkasit): “Vallë, a t’i braktisim hyjnitë tona për shkak të një poeti të marrë?”'),
(3825, 37, 37, 'Jo, (ai nuk është poet), por ai e ka sjellë të Vërtetën dhe i ka vërtetuar pejgamberët e mëparshëm,'),
(3826, 37, 38, 'e, me të vërtetë, ju do ta shijoni dënimin e dhembshëm, -'),
(3827, 37, 39, 'dhe do ta dënoheni ju vetëm për atë që keni bërë!'),
(3828, 37, 40, 'E robërit e sinqertë të Perëndisë,'),
(3829, 37, 41, 'do të kenë furnizim të caktuar,'),
(3830, 37, 42, 'pemë të ndryshme, dhe do të jenë të respektuar,'),
(3831, 37, 43, 'në kopshtet e dhuntive,'),
(3832, 37, 44, 'në kolltuqe, njëri përballë tjetrit,'),
(3833, 37, 45, 'do të shërbehen me pije nga burimi që do të rrjedhë gjithmonë,'),
(3834, 37, 46, '(me pije) të bardhë e të shijshme për ata që do ta pijnë,'),
(3835, 37, 47, 'me të (verën), nuk do të trullosen dhe prej saj nuk do të dehen.'),
(3836, 37, 48, 'Dhe pranë tyre do të jenë gratë me shikim të ulur, e sy të bukur'),
(3837, 37, 49, 'sikur të jenë vezë të ruajtura.'),
(3838, 37, 50, 'Dhe ata do t’i qasen njëri-tjetrit e do të bisedojnë,'),
(3839, 37, 51, 'dhe njëri prej tyre do të thotë: “Unë kam pasur një shok (mohues),'),
(3840, 37, 52, 'i cili thoshte: “Vallë edhe ti je nga ata që besojnë,'),
(3841, 37, 53, 'që kur të vdesim e të bëhemi dhé e eshtra, me të vërtetë do të japim llogari?”'),
(3842, 37, 54, '(do të thotë ai): “A doni të shikoni?”'),
(3843, 37, 55, 'Dhe ai do të shikojë, dhe atë do ta sheh në midis të zjarrit.'),
(3844, 37, 56, 'Ai thotë: “Pasha Perëndinë, për pak më shkatërrove edhe mua;'),
(3845, 37, 57, 'po të mos ishte dhuntia e Zotit tim, edhe unë do të isha në zjarr (bashkë me ty).'),
(3846, 37, 58, 'E për ne, nuk do të ketë më vdekje,'),
(3847, 37, 59, 'përveç vdekjes së parë, nuk do të jemi të munduar më,'),
(3848, 37, 60, 'kjo, me të vërtetë, është sukses i madh!'),
(3849, 37, 61, 'Për diçka të tillë, le të angazhohen punëtorët!'),
(3850, 37, 62, 'A më e mirë është kjo gjendje, apo druri zekkum.'),
(3851, 37, 63, 'Ne, këtë sprovë e kemi bërë për zullumqarët.'),
(3852, 37, 64, 'Njëmend, ky dru del nga fundi i ferrit,'),
(3853, 37, 65, 'fruti i tij është si koka e gjarpinjëve (djajve).'),
(3854, 37, 66, 'Me të vërtetë, ata do ta hanë këtë frut dhe me të do ta mbushin barkun.'),
(3855, 37, 67, 'Pastaj, ata do të kenë një përzierje me ujë të nxehtë (për të pirë)'),
(3856, 37, 68, 'e pas kësaj, me siguri, kthimi i tyre do të jetë në ferr.'),
(3857, 37, 69, 'Ata i kanë gjetur etërit e tyre në humbje,'),
(3858, 37, 70, 'prandaj, edhe ata vazhduan pas gjurmëve të tyre,'),
(3859, 37, 71, 'por edhe para tyre, me të vërtetë, e kanë humbur (rrugën) shumë popuj të lashtë,'),
(3860, 37, 72, 'Ne, me të vërtetë, u kemi dërguar atyre paralajmërues.'),
(3861, 37, 73, 'E, shiko se çfarë qe fundi i atyre që u paralajmëruan,'),
(3862, 37, 74, 'pos robërve të sinqertë të Zotit (ata nuk janë dënuar).'),
(3863, 37, 75, 'E Na thirri (në ndihmë) Nuhu, e Ne iu kemi përgjegjur bukur:'),
(3864, 37, 76, 'e kemi shpëtuar atë dhe familjen e tij prej fatkeqësisë së madhe,'),
(3865, 37, 77, 'duke i lënë në jetë vetëm pasardhësit e tij,'),
(3866, 37, 78, 'dhe u kemi lënë kujtim te brezat e tjerë;'),
(3867, 37, 79, '“Përshëndetja për Nuhun qoftë në mesin e të gjithë popujve!”'),
(3868, 37, 80, 'Ne, me të vërtetë, kështu i shpërblejmë bamirësit.'),
(3869, 37, 81, 'Me të vërtetë, ai është nga robërit Tonë, besimtar.'),
(3870, 37, 82, 'Pastaj, i fundosëm të tjrerët (jobesimtarët).'),
(3871, 37, 83, 'Dhe, me të vërtetë, nga ithtarët e tij është edhe Ibrahimi,'),
(3872, 37, 84, 'kur Zotit të vet i erdh me zemër të pastër,'),
(3873, 37, 85, 'kur babait të vet dhe popullit të vet i tha: “Çka adhuroni ju?'),
(3874, 37, 86, 'Vallë, a dëshironi hyjni të rrejshme në vend të Allahut?'),
(3875, 37, 87, 'E çka mendoni ju për Zotin e gjithësisë?”'),
(3876, 37, 88, 'Pastaj u hodhi një shikim yjeve,'),
(3877, 37, 89, 'dhe tha: “Unë jam i sëmurë”.'),
(3878, 37, 90, 'Dhe, ata u larguan prej tij, duke e lënë pas.'),
(3879, 37, 91, 'Pastaj, ai u përvodh te hyjnitë e tyre dhe u tha: “A nuk po hani?'),
(3880, 37, 92, 'Ç’keni, përse nuk flisni?”'),
(3881, 37, 93, 'Ju afrua tyre tinëzisht, duke i goditur me dorë të djathtë.'),
(3882, 37, 94, 'Dhe ata (njerëzit) nxituan kah ai.'),
(3883, 37, 95, 'Ibrahimi tha: “Vallë, a i adhuroni ju ato që i keni gdhendur (putat)?'),
(3884, 37, 96, 'E, Perëndia u ka krijuar juve, dhe ato që i bëni ju”.'),
(3885, 37, 97, '(Jobesimtarët) thanë: “Përgatitni për te një godinë (vatër) dhe hedheni atë në zjarr!”'),
(3886, 37, 98, 'Dhe ata dëshiruan t’i bëjnë dinakëri, por Ne i bëmë ata më të poshtëruarit.'),
(3887, 37, 99, '(Ibrahimi) tha: “Unë po shkoj te Zoti im. Ai do të më udhëzojë.'),
(3888, 37, 100, 'O Zoti im, dhuroma një fëmijë të mirë (e të ndershëm)!”'),
(3889, 37, 101, 'Dhe Ne e dihariquam atë me një çun të urtë.'),
(3890, 37, 102, 'Dhe kur fëmija u rrit aq sa t’i ndihmojë në punë, Ibrahimi i tha: “O djali im, kam parë ëndërr se duhet të të pres, çka mendon ti?” – O baba, - tha: “vepro ashtu siç je urdhëruar; njëmend do të bindesh, - nëse do Perëndia, se unë jam i durueshëm”.'),
(3891, 37, 103, 'Pasi e pranuan që të dy urdhërin, e rrëzoi (djalin) në krah,'),
(3892, 37, 104, 'Ne e thirrëm: “O Ibrahim,'),
(3893, 37, 105, 'ti madje e realizove ëndrrën. E, Ne, me të vërtetë, kështu i shpërblejmë bamirësit,'),
(3894, 37, 106, 'kjo, me të vërtetë ka qenë një sprovë e qartë!”'),
(3895, 37, 107, 'Edhe Ne e zëvendësuam atë me një kurban të madh,'),
(3896, 37, 108, 'dhe Ne i lamë atij kujtim të mirë në brezat e mëvonshëm;'),
(3897, 37, 109, '“Përshëndetja e madhe qoftë mbi Ibrahimin!”'),
(3898, 37, 110, 'Ja, kështu, Na i shpërblejmë bamirësit,'),
(3899, 37, 111, 'e ai, me të vërtetë, ka qenë nga robërit Tanë besimtar,'),
(3900, 37, 112, 'dhe Ne e kemi sihariquar atë me Is’hakun, pejgamber prej njerëzve të mirë,'),
(3901, 37, 113, 'dhe Ne e bekuam atë dhe Is’hakun, por në mesin e pasardhësve të tyre ka besimtarë dhe zullumqarë të hapët për veten e vet.'),
(3902, 37, 114, 'Ne e kemi dhuruar Musain dhe Harunin.'),
(3903, 37, 115, 'Dhe i kemi shpëtuar ata, të dy dhe popujt e tyre nga mjerimi i madh,'),
(3904, 37, 116, 'dhe Ne i ndihmuam ata, e ata u bënë fitimtarë,'),
(3905, 37, 117, 'dhe u dhamë atyre Librin e qartë'),
(3906, 37, 118, 'dhe i udhëzuam ata dy në rrugë të drejtë'),
(3907, 37, 119, 'dhe Ne u lamë kujtim të mirë atyre të dyve në mesin e brezave të mëvonshme.'),
(3908, 37, 120, '“Përshëndetja e madhe qoftë mbi Musain dhe Harunin!”'),
(3909, 37, 121, 'Ja, me të vërtetë, kështu Ne i shpërblejmë bamirësit,'),
(3910, 37, 122, 'e, në të vërtetë, ata dy janë nga robërit Tanë besimtarë.'),
(3911, 37, 123, 'Dhe, me të vërtetë, Iljasi ka qenë (njëri) prej pejgamberëve.'),
(3912, 37, 124, '(Përkujtoje) kur i tha popullit të vet: “A nuk po i frikësoheni (Perëndisë)?”'),
(3913, 37, 125, 'Vallë, a i luteni ju Ba’lit (lloj idhulli), ndërsa e lëni Krijuesin më të mirë,'),
(3914, 37, 126, 'Allahun, Zotin tuaj dhe Zotin e të parëve tuaj të lashtë!”'),
(3915, 37, 127, 'Ata e konsideruan gënjeshtar atë dhe për këtë shkak, me siguri, do të mbeten në dënim,'),
(3916, 37, 128, 'pos robërve të sinqertë të Perëndisë.'),
(3917, 37, 129, 'Dhe Ne i lamë atij kujtim të mirë për brezat e mëvonshëm:'),
(3918, 37, 130, '“Përshëndetja e madhe qoftë për Iljasin!”'),
(3919, 37, 131, 'Ja, kështu Ne i shpërblejmë bamirësit,'),
(3920, 37, 132, 'e ai, me të vërtetë, ka qenë nga robërit Tanë besimtar.'),
(3921, 37, 133, 'Dhe, me të vërtetë, Luti ka qenë njëri prej pejgamberëve.'),
(3922, 37, 134, '(Përkujtoje) kur Ne e shpëtuam atë dhe tërë familjen e tij,'),
(3923, 37, 135, 'pos plakës që ka mbetur me të tjerët (të dënurarit),'),
(3924, 37, 136, 'pastaj i zhdukëm të tjerët,'),
(3925, 37, 137, 'dhe me të vërtetë, ju kaloni pranë tyre në mëngjes'),
(3926, 37, 138, 'dhe natën, a nuk po mendoni?'),
(3927, 37, 139, 'Dhe, me të vërtetë, Junusi ka qenë njëri nga pejgamberët.'),
(3928, 37, 140, '(Përkujtoje) kur ai iku deri te anija e mbushur përplot,'),
(3929, 37, 141, 'dhe hodhi shortin (me detarët), e ai ishte njëri prej tyre që i ra shorti (të hidhet në det),'),
(3930, 37, 142, 'dhe peshku e përbiu atë, e ai ishte i qortuar,'),
(3931, 37, 143, 'dhe, po të mos ishte nga ata që i luten Zotit,'),
(3932, 37, 144, 'me siguri do të mbetej në barkun e tij deri në Ditën e ringjalljes (së të gjithëve),'),
(3933, 37, 145, 'dhe Ne e hodhëm në një vend të shkretë, e ai ishte i sëmurë,'),
(3934, 37, 146, 'dhe Ne bëmë që të dalë një bimë kungulli (përr t’i bërë hije),'),
(3935, 37, 147, 'dhe Ne e dërguam atë te njëqind mijë e më tepër njerëz,'),
(3936, 37, 148, 'dhe ata besuan, prandaj iu dhamë jetë të këndshme deri një kohë.'),
(3937, 37, 149, 'E pyeti ata (o Muhammed): “Vallë, a për Zotin tënd janë vajzat e për ta djemtë,'),
(3938, 37, 150, 'ose Ne i krijuam engjëjt si femra, e ata ishin dëshmitarë (për këtë)?'),
(3939, 37, 151, 'Ja pra, ata për shkak të gënjeshtrave të veta, thonë:'),
(3940, 37, 152, '“Zoti ka lindur fëmijë” - ata në të vërtetë, janë gënjeshtarë.'),
(3941, 37, 153, 'Vallë, a vajzat i ka zgjedhë ai para djemve?'),
(3942, 37, 154, 'Çka keni, si po gjykoni!?'),
(3943, 37, 155, 'A nuk po mendoni?!'),
(3944, 37, 156, 'A po, ju keni argument të qartë?'),
(3945, 37, 157, 'Sillni ju Librin tuaj, nëse thoni të vërtetën!'),
(3946, 37, 158, 'Ata (idhujtarët), kanë bërë farefisni në mes Zotit dhe engjëjve, kurse engjëjt e dinë se ata (që flasin ashtu) do të hidhen në zjarr.'),
(3947, 37, 159, 'lavdi Allahut, qoftë i pastër Perëndia nga ajo që ata ia përshkruajnë Atij!'),
(3948, 37, 160, 'Përveç robërve të sinqertë të Zotit (që e adhurojnë Atë).'),
(3949, 37, 161, 'Me të vërtetë, as ju as ato që i adhuroni ju,'),
(3950, 37, 162, 'nuk mund ta mashtroni askë kundër (Zotit),'),
(3951, 37, 163, 'pos atij që do të digjet në zjarr.'),
(3952, 37, 164, 'Çdonjëri prej nesh (engjëjve) e ka vendin e caktuar,'),
(3953, 37, 165, 'ne jemi ata që qëndrojmë në radhë,'),
(3954, 37, 166, 'dhe ne jemi ata që i lutemi Atij (Perëndisë)!'),
(3955, 37, 167, 'E ata (Kurejshitë), në të vërtetë, thonin:'),
(3956, 37, 168, '“Sikur të kishim ne Librin që kanë pasur popujt e mëparshëm,'),
(3957, 37, 169, 'ne, me siguri, do të ishim robër të sinqertë të Perëndisë.'),
(3958, 37, 170, 'Ata e kanë mohuar Ate (Kur’anin), e së shpejti do ta dinë!'),
(3959, 37, 171, 'Me të vërtetë, qysh moti është ditur premtimi ynë për robërit Tanë – pejgamberët.'),
(3960, 37, 172, 'Ata, me të vërtetë, do të jen të ndihmuar,'),
(3961, 37, 173, 'dhe, me të vërtetë, ushtria jonë do të jetë fitimtare!'),
(3962, 37, 174, 'Andaj shmangiu atyre deri në një kohë,'),
(3963, 37, 175, 'dhe vëzhgojë ata, e do të vëzhgojnë edhe ata!'),
(3964, 37, 176, 'Vallë, a mos ata po kërkojnë shpejtimin e dënimit Tonë!'),
(3965, 37, 177, 'Kur t’iu bie dënimi, në mesin e tyre, mëngjesi i atyre që janë paralajmëruar është shumë i shëmtuar,'),
(3966, 37, 178, 'andaj, shmangu prej tyre deri një kohë,'),
(3967, 37, 179, 'dhe vëzhgo, e do të vëzhgojnë edhe ata!'),
(3968, 37, 180, 'Qoftë lavdëruar Zoti yt, Zot i madhërisë (dhe qoftë i pastër) nga ajo që ia përshkruajnë ata!'),
(3969, 37, 181, 'Dhe përshëndetje e madhe qoftë mbi pejgamberët,'),
(3970, 37, 182, 'dhe falenderimi qoftë për Allahun – Zotin e gjithësisë!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'Sâd! Pasha Kur’anin e madhërueshëm (që këshillon),'),
(3972, 38, 2, '(por) jobesimtarët janë mendjemëdhenj dhe përçarës (nuk e pranojnë të Vërtetën)!'),
(3973, 38, 3, 'Sa e sa breza para tyre (popullit të Mekkes), Ne i kemi zhdukur, e ata kanë thirrë në ndihmë, por ka qenë vonë!'),
(3974, 38, 4, 'Ata janë çuditur për ardhjen e një paralajmëruesi nga mesi i tyre, dhe jobesimtarët kanë thënë: “Ky është magjistar dhe gënjeshtar;'),
(3975, 38, 5, 'A don ai që prej tërë perëndive të bëjë një perëndi? Me të vërtetë, kjo është një gjë e çudtishme!”'),
(3976, 38, 6, 'Dhe paria nga mesi i tyre u larguan (duke i thënë njëri-tjetrit): “Shkoni dhe bëhuni të durueshëm pranë perëndive tuaj; Me të vërtetë, kjo është një punë (e madhe) që dëshirohet.'),
(3977, 38, 7, 'Këtë nuk e kemi dëgjuar në fenë e mëparshme. Ky është vetëm një trillim;'),
(3978, 38, 8, 'Vallë, nga mesi ynë atij t’i dërgohet Kur’ani?!” Por, ata dyshojnë në Këshillën Time, meqë ende nuk e kanë shijuar dënimin Tim.'),
(3979, 38, 9, 'Vallë, a te ata janë thesarët e mëshirës së Zotit tënd të Plotëfuqishëm dhe Dhurues?'),
(3980, 38, 10, 'Apo i tyre është pushteti i qiejve dhe Tokës dhe ç’ka në mes tyre? E, le të ngjiten atëherë, me mjete (në qiell).'),
(3981, 38, 11, 'Ata (jobesimtarët-kurejshit) janë një ushtri e vogë e përbërë nga grupet, të cilat do të shpartallohen aty.'),
(3982, 38, 12, 'Edhe para tyre, kanë përgënjeshtruar populli i Nuhut, edhe Adi edhe Faraoni – pronar i godinave madhështore,'),
(3983, 38, 13, 'edhe Themudi edhe populli i Lutit edhe banorët e Ejketit; këta janë aleatë (grupe);'),
(3984, 38, 14, 'të gjithë këta i kanë konsideruar pejgamberët gënjeshtarë, andaj u realizua dënimi Im.'),
(3985, 38, 15, 'Këta presin vetëm një zë të madh që nuk përsëritet,'),
(3986, 38, 16, 'dhe ata (përqeshësit) thonë: “O Zoti ynë, shpejtona pjesën tonë (të dënimit) para Ditës së Llogarisë!”'),
(3987, 38, 17, '(Perëndia ka urdhëruar): “Duro (o Muhammed) për atë që thonë ata dhe kujtoje robin Tonë Daudin, e fuqishëm, ai me të vërtetë, i është drejtuar Zotit.'),
(3988, 38, 18, 'Na ia kemi nënshtruar bjeshkët që bashkë me të t’i luten Perëndisë, në mbrëmje dhe në mëngjes,'),
(3989, 38, 19, 'si dhe zogjët e tubuar. Të gjitha i drejtoheshin Atij (luteshin në Te).'),
(3990, 38, 20, 'Dhe, Ne, ia kemi forcuar pushtetin e tij dhe i kemi dhënë mençuri dhe dijeni për të gjykuar.'),
(3991, 38, 21, 'A të ka ardhur ty lajmi për kundërshtarët, kur hypen në tempullin e tij (fetar),'),
(3992, 38, 22, 'kur hynë te Daudi, ai u frikësua prej tyre. Ata thanë: “Mos u frikëso, (na jemi) dy kundërshtarë, që njërit-tjetrit i ka bërë padrejtësi, prandaj gjyko mes nesh, me të drejtë, dhe mos u shmangë (prej së vërtetës), po na udhëzo në rrugë të drejtë.'),
(3993, 38, 23, 'Me të vërtetë, ky është vëlla (mik) i im. Ai ka nëntëdhjetë e nëntë dele, kurse unë kam vetëm një dele, andaj më tha: “Ma jep ti mua atë? – dhe ai në bisedë (polemikë) më mundi”.'),
(3994, 38, 24, '(Daudi) tha: “Me të vërtetë, ai të ka bërë padrejtësi që të ka kërkur delen tënde, që ta bashkojë me delet e veta”. Dhe, me të vërtetë, shumë bashkëpronarë i bëjnë padrejtësi njëri-tjetrit, pos atyre që besojnë dhe bëjnë punë të mira, por ata janë pak”. Daudi e kuptoi, se Na, me të vërtetë – e kemi sprovuar atë, prandaj ka kërkuar falje nga Zoti i vet dhe u përul, duke rënë për dhé dhe duke u penduar.'),
(3995, 38, 25, 'Pastaj, Ne këtë ia falëm atij, e ati te Na, ka një pozitë të veçantë dhe një kthim të mirë (në jetën tjetër).'),
(3996, 38, 26, 'O Daud, Ne të kemi bërë sundimtar në tokë, prandaj gjyko ndërmjet njerëzve me të drejtë dhe, mos shko pas pasionit tënd, se (ai pasioni) të largon nga rruga e Perëndisë! Me të vërtetë, për ata që largohen nga rruga e Perëndisë, ka dënim të ashpër, ngase e kanë harruar Ditën e llogarisë.'),
(3997, 38, 27, 'Ne nuk e kemi krijuar kot qiellin dhe Tokën dhe atë që është në mes tyre. Ashtu (kot), mendojnë jobesimtarët. E mjrë për jobesimtarët kur do të hyjnë në zjarr!'),
(3998, 38, 28, 'A do të sillemi me ata që besojnë dhe bëjnë vepra të mira si me turbulluesit në Tokë; apo, vallë do të sillemi me njerëzit e mirë si me njerëzit e këqinj?'),
(3999, 38, 29, '(Ky) është një libër (Kur’ani), që ta kemi zbritur ty, libër i bekuar, për t’i studjuar dokumentet e tij dhe për t’u këshilluar mendimtarët me të.'),
(4000, 38, 30, 'Ne, Daudit, i kemi dhuruar Sulejmanin. Ai është rob i mrekullueshëm. Ai, me të vërtetë, i është drejtuar shum Zotit!'),
(4001, 38, 31, '(Kujtoje) kur atij (Sulejmanit), një pasdite iu paraqiten atllarët (kuaj të dalluar),'),
(4002, 38, 32, 'Ai tha: “Unë shfaqa dashuri më të madhe ndaj pasurisë (kuajve) se sa lutjes ndaj Zotit tim!” (ata defiluan para tij) derisa humbën mas beshkes.'),
(4003, 38, 33, '(Ai tha): “M’i ktheni ata” – e pastaj nisi t’i prekë me shpatë këmbët dhe qafat e tyre.'),
(4004, 38, 34, 'Ne, me të vërtetë, e sprovuam Sulejmanin dhe hodhëm mbi fronin e tij një trup, dhe pastaj (Sulejmani) iu drejtua Zotit,'),
(4005, 38, 35, 'dhe tha: “O Zoti im, të më falësh dhe më dhuro një pushtet çfarë nuk do ta ketë askush pas meje! Me të vërtetë, Ti je dhurues i madh!”'),
(4006, 38, 36, 'Pastaj, Ne ia nënshtruam erën e butë, e cila frynë me urdhërin e tij, andej kah don ai –'),
(4007, 38, 37, 'dhe djajtë, të gjithë ndërtuesit dhe zhytësit në ujë,'),
(4008, 38, 38, 'dhe të lidhur në pranga.'),
(4009, 38, 39, 'Kjo është dhurata Jonë, prandaj ti dhuroja (o Sulejman) kujt të duash ose mos ua jep; pa kurrfarë llogarie për këtë!”'),
(4010, 38, 40, 'Me të vërtetë, ai te Na ka një pozitë të veçantë dhe një kthim të mirë (në jetën tjetër).'),
(4011, 38, 41, 'Kujtoje robin Tonë Ejjubin, kur iu lut Zotit të vet: “Me të vërtetë, më preku djalli me mundim dhe vuajtje!”'),
(4012, 38, 42, '(Atij i tha): “Bjeri me këmbën tënde (Tokës)! Ja, ky është një burim i ujit të ftohtë për larje dhe pirje!”'),
(4013, 38, 43, 'Dhe Ne ia kemi dhuruar atij familjen dhe po aq një numër tjetër anëtarësh (të familjes) – mëshirë prej Nesh dhe këshillim për njerëzit e mënçur,'),
(4014, 38, 44, 'dhe merr me dorën tënde një tubë thuprash e rreh me to dhe mos e shkel betimin tënd!” Ne, e kemi ditur atë të durueshëm, (ai) është rob i mrekullueshëm. Ai, me të vërtetë, i është drejtuar (shumë) Zotit!'),
(4015, 38, 45, 'Dhe kujtoji robërit Tonë: “Ibrahimin, Is’hakun dhe Jakubin, të fortë (në besim) dhe largëpamës.'),
(4016, 38, 46, 'Ne, atyre iu dhuruam një virtyt të veçantë, që ta kenë gjithmonë në mend botën e ardhshme;'),
(4017, 38, 47, 'Njëmend, ata janë tek Ne njerëz të mirë e të zgjedhur.'),
(4018, 38, 48, 'Dhe kujtoje: Ismailin, Eljesain dhe Dhulkiflin! Të gjithë ata kanë qenë të mirë.'),
(4019, 38, 49, 'Ky është një kujtim i mirë – dhe, me të vërtetë, për njerëzit që ruhet nga mëkatet, ta kthim të mirë (në botën tjetër):'),
(4020, 38, 50, 'në kopshtijet e Adnit – dyert e të cilit janë të hapura për ta,'),
(4021, 38, 51, 'në të cilat rrijnë të mbështetur (në divane-kanape) e kënaqen em pemë dhepije të llojllojshme,'),
(4022, 38, 52, 'pranë tyre do të ketë hyrie moshatare, që nuk shikojnë anash.'),
(4023, 38, 53, 'Kjo iu është premtuar për Ditën e llogarisë;'),
(4024, 38, 54, 'ky është, njëmend, furnizimi Ynë që nuk ka të sosur!'),
(4025, 38, 55, 'Kjo është kështu (për besimtarët). E, për njerëzit e tërbuar (në të keqe), me të vërtetë, ka kthim të keq (në botën tjetër):'),
(4026, 38, 56, 'ferri, në të cilin ata do të digjen. E, sa shtrat i shëmtuar që është ai!'),
(4027, 38, 57, 'Kjo është kështu (për mohuesit)! E, le ta shijojnë ata këtë ujë të vëluar dhe të qelbët'),
(4028, 38, 58, 'dhe të tjera mundime të llojllojshme, të ngjashme me to.'),
(4029, 38, 59, '(Atyre u thuhet): “Ky është grumbullim i dendur me ju (në ferr) e (u thuhet): Nuk ka çlirm për ta! Ata, me të vërtetë, do të digjen në zjarr!”'),
(4030, 38, 60, '(Populli i thjeshtë) iu përgjigjet (udhëheqësve të vet): “Jo, por ju mos paçit çlirim as qetësi, ju na keni sjellë në këtë gjendje, e sa strehim i keq është ky!”'),
(4031, 38, 61, '(Njerëzit e thjeshtë) thanë: “O Zoti ynë! Kush na përgatiti këtë neve, dyfishoju atyre dënimin në ferr!”'),
(4032, 38, 62, '(Populli i Mekkes) thanë: “Pse nuk po i shohim njerëzit që i konsideronin të këqinj,'),
(4033, 38, 63, 'a mos i kemi përqeshur ne at, a apo na kanë humbur sysh?”'),
(4034, 38, 64, 'Kjo është, me të vërtetë, grindja e banorëve të ferrit.'),
(4035, 38, 65, 'Thuaj (o Muhammed!): “Me të vërtetë, unë jam vetëm paralajmërues, e nuk ka zot përveç Allahut, të Vetmit e Ngadhënjimtarit,'),
(4036, 38, 66, 'Zotit të qiejve e të Tokës dhe të asaj që gjendet midis tyre, të Plotëfuqishmit, Falësit”.'),
(4037, 38, 67, 'Thuaj: “Ky (Kur’ani) është lajm i madh,'),
(4038, 38, 68, 'e ju prej tij po shmangeni.'),
(4039, 38, 69, 'Unë nuk kam pasur kurrfarë dije për meleqtë e lartë, kur ata polemizuan mes vete, -'),
(4040, 38, 70, 'mua vetëm më është shpallë se jam paralajmërues i qartë, -'),
(4041, 38, 71, '(Kujtoju atyre) kur Zoti yt u ka thënë meleqve: “Me të vërtetë, Unë do ta krijojë njeriun prej balte,'),
(4042, 38, 72, 'e kur Unë ta plotësoj atë t’i jap frymë prej shpirtit Tim (kur t’i jap jetë), bëni sexhde atij!”'),
(4043, 38, 73, 'E të gjithë meleqtë (engjëjt), së bashku, bënë sexhde,'),
(4044, 38, 74, 'përveç Iblisit; ai u bë mendjemadh e ishte mohues.'),
(4045, 38, 75, '(Zoti) tha: “O Iblis, ç’të pengoi ty, që të mos bësh sexhde para atij që e kam krijuar Unë me duart e Mia (pa ndihmën e askujt)? A mos je mendjemadhësuar apo mendon se je nga të lartit?”'),
(4046, 38, 76, '(Iblisi) tha: “Unë jam më i mirë se ai. Mua më ke krijuar prej zjarrit, kurse atë prej baltës”.'),
(4047, 38, 77, '(Perëndia) tha: “Dil atëherë prej xhennetit! Me të vërtetë, ti je i dëbuar.'),
(4048, 38, 78, 'Me të vërtetë mallkimi Im është mbit ty deri në Ditën e gjykimit”.'),
(4049, 38, 79, '(Iblisi) tha: “O Zoti im, më jep afat deri në ditën kur do të ringjallen ata!”'),
(4050, 38, 80, '(Zoti) tha: “Me të vërtetë, do të jepet dy afat'),
(4051, 38, 81, 'deri në ditën e caktuar”.'),
(4052, 38, 82, '(Iblisi) tha: “Pasha madhërinë Tënde, unë do t’i mashtrojë ata të gjithë –'),
(4053, 38, 83, 'përveç robërve Tu të sinqertë!”'),
(4054, 38, 84, '(Zoti) tha: “Unë betohem me të drejtën dhe flas të vërtetën,'),
(4055, 38, 85, 'me të vërtetë, Unë do ta mbush ferrin me ty dhe me të gjithë ata që të pasojnë ty!”'),
(4056, 38, 86, 'Thuaj (o Muhammed!): “Unë prej jush nuk kërkoj kurrfarë shpërblimi për të (Kur’anin), e as nuk jam nga ata që paraqiten të atillë (çfarë nuk janë),'),
(4057, 38, 87, 'ai (Kur’ani), me të vërtetë, është këshilël për tërë botën!'),
(4058, 38, 88, 'Dhe ju, me të vërtetë, do ta dini lajmin e tij pas një kohe!”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, '(Kjo është) Shpallja e Librit nga Allahu i Plotëfuqishëm dhe i Gjithëdijshëm!'),
(4060, 39, 2, 'Njëmend, Ne ta kemi zbritur ty Librin me të vërtetën e qartë. Andaj adhuroje Perëndinë sinqerisht në besim!'),
(4061, 39, 3, 'Ah, vetëm për Perëndinë është adhurimi i sinqertë. E, ata që marrin përveç Perëndisë, mbrojtës, (thonë): “Ne i lutemi atyre vetëm që të na afrojnë te Perëndia” – Perëndia, me të vërtetë, gjykon ndërmjet tyre për atë që nuk janë pajtuar. Me siguri, Perëndia nuk e udhëzon atë që është gënjeshtar e mohues.'),
(4062, 39, 4, 'Sikur të donte Perëndia të marrë një fëmijë, Ai do të zgjidhte nga ata që i ka krijuar – çka të dojë. Qoftë lavdëruar Ai; Ai është Perëndi i Vetëm dhe Ngadhnjimtar!'),
(4063, 39, 5, 'Ai i ka krijuar qiejt dhe Tokën, me qëllim të plotë; Ai e mbështjellë natën në ditë dhe ditën në natë, Ai e ka nënshtruar Diellin dhe Hënën. Të gjitha lëvizin deri në kohë (shkallë) të caktuar. Ah, Ai është i Fuqishëm dhe Falës.'),
(4064, 39, 6, 'Ai ju ka krijur prej një njeriu (Ademit); pastaj, prej tij ka krijuar shoqen e tij dhe Ai u ka krijuar tetë lloj kafshësh; Ai ju krijon në barqet e nënave tuaja, duke iu dhanë formë pas forme në tri errësira. Ky është, Perëndia – Zoti juaj, i Tij është i tërë pushteti, nuk ka zot tjetër pos Tij, e pra, nga po ia mbani ju?'),
(4065, 39, 7, 'Nëse ju e mohoni (Atë), me të vërtetë, Perëndia është i pavarur prej jush, por Ai nuk është i kënaqur me mohimin e robërve të Tij; e është i kënaqur me ju, nëse e falenderoni. Asnjëri (mëkatar), nuk do t’i bartë mëkatet e tjetrit! Pastaj, do të ktheheni te Zoti juaj, e Ai pastaj do t’u informojë për atë që keni punuar sepse, me të vërtetë, Ai di se ç’keni në zemrat tuaja.'),
(4066, 39, 8, 'Kur njeriun e godet një e keqe, ai i lutet Zotit të vet, duke iu drejtuar krejtësisht, e kur atij t’i kthehet një e mirë prej Tij, ai harron atë për të cilën më parë iu lut Perëndisë dhe i bënë shok Perëndisë, andaj largohet nga rruga e Tij (Perëndisë). Ti thuaj (atij mohuesit): “Kënaqu pak me mosbesimin tënd! Ti, me siguri, do të jeshë nga banorët e zjarrit!'),
(4067, 39, 9, '(A më i mirë është ky) apo ai që bën lutje natën, duke bërë sexhde dhe duke qëndruar në këmbë e që ruhet (nga dënimi) i jetës tjetër dhe shpreson në mëshirën e Zotit të tij...? Thuaj: “A janë të barabartë ata që dinë dhe ata që nuk dinë? Vetëm mentarët i pranojnë këshillat!'),
(4068, 39, 10, 'Thuaj: “O robërit e mi, që keni besuar, ruajuni prej (dënimit) të Zotit tuaj! Ata që bëjnë mirë në këtë jetë do të kenë të mira, e toka e Perëndisë është e gjerë. Vetëm ata që janë të durueshëm do të shpërblehen pa masë”.'),
(4069, 39, 11, 'Thuaj: “Mua më është urdhëruar që ta adhuroj Perëndinë dhe ndaj Tij të jem i sinqertë në besim,'),
(4070, 39, 12, 'dhe më është urdhëruar që të bëhem musliman i parë.'),
(4071, 39, 13, 'Thuaj: “Unë, me të vërtetë, druajn – nëse kundërshtojë Zotin tim, dënimit të Ditës së madhe”.'),
(4072, 39, 14, 'Thuaj: “Vetëm Perëndinë e adhuroj sinqerisht me besimin tim të plotë,'),
(4073, 39, 15, 'e ju adhuroni, përveç Perëndisë, kë të doni!” Thuaj: “Me të vërtetë, do të jenë në humbje ata që kanë humbur veten dhe familjen e tyre në Ditën e Kijametit. Ka, kjo është humbje e madhe!”'),
(4074, 39, 16, 'Mbi ta dhe nën ta do të ketë shtresa të zjarrit. Me këtë, Perëndia i frikon robërit e Vet. “O robërit e Mi, ruajuni (dënimit Tim)!”'),
(4075, 39, 17, 'Ata që shmangen nga adhurimi i idhujve e kthehen kah Perëndia, për ta ka gëzim. Gëzoi pra (o Muhammed) robërit e Mi,'),
(4076, 39, 18, 'të cilët dëgjojnë fjalën (Kur’anin) e pasojnë atë që është më mirë. Këta janë ata që Perëndia i ka orientuar në rrugë të drejtë dhe ata janë të mençur.'),
(4077, 39, 19, 'Mos vallë, a ti do ta shpëtosh atë që e ka merituar dënimin, ta nxjerrësh atë që është në zjarr?'),
(4078, 39, 20, 'Ndërkaq, ata që i frikohen Zotit, do të kenë dhoma kat-kat – pranë të cilave rrjedhin lumenjtë. (Ky është) premtim i Perëndisë, (e) Perëndia nuk e shkelë premtimin (e Vet).'),
(4079, 39, 21, 'A nuk po sheh që Perëndia ka zbritur ujin (shiun) prej qiellit, e shpie (shpërndanë) atë nëpër burime në tokë, mandej me të nxjerrë të lashta të llojllojshme, ngjyrash të ndryshme, pastj thahen dhe i sheh të zverdhura, e mandej i shkatërron ato. Me të vërtetë, në të gjitha këto, ka këshilla për ata që janë të mençur.'),
(4080, 39, 22, 'A thua a është i (njëjtë) ai të cilit Perëndia ia ka hapur zemrën për islamizëm, e ai është në dritën e Zotit të vet...? Mjerë për ata që e kanë zemrën të pandishme kur përmendet Allahu; të tillët janë në humbje të qartë!'),
(4081, 39, 23, 'Perëndia e ka shpallur fjalën më të bukur – Librin (Kur’anin), pjesët e të cilit i gjasojnë njëra-tjetrës dhe përsëriten. Prej tij u rrënqethet lëkura atyre që frikohen prej Zotit të tyre, e mandej qetësohen dhe kënaqen lëkurat dhe zemrat e tyre, kur përmendet Perëndia (dhe premtimi i Tij). Ky Libër është udhëzim i Perëndisë, me të Perëndia e udhëzon kë të dojë, e kë Perëndia e shpie në humbje, s’ka kush që mund ta udhëzojë.'),
(4082, 39, 24, 'A është (i barabartë) ai që e ruan veten prej dënimit të rëndë në Ditën e Kijametit (dhe ai që nuk e ruan)? (Atëhrë) zullumqarëve, u thuhet: “Shijoni atë që keni punuar!”'),
(4083, 39, 25, 'Kanë përgënjeshtruar ata që kanë qenë para tyre (Mekkasve), e u erdhi atyre dënimi prej nga nuk e pritnin.'),
(4084, 39, 26, 'Dhe, Perëndia u ka dhëna tyre ta shijojnë poshtërimin në këtë jetë, e dënimi i jetës tjetër është më i madh, sikur ta dinin ata (këtë)!'),
(4085, 39, 27, 'Dhe, me të vërtetë, u kemi sjellë njerëzve shembuj në këtë Kur’an për çdo gjë, për t’u këshilluar,'),
(4086, 39, 28, 'në Kur’anin në gjuhën arabe, në të cilin nuk ka kurrfarë kundërthëniesh, e që t’i druajnë Zotit të tyre.'),
(4087, 39, 29, 'Perëndia ka sjelël si shembull (krahasimi) njeriun (robin), i cili ka shumë bashkëpronarë të padurueshëm që e kundërshtojnë njëri-tjetrin dhe njeriun (robin), i cili (i takon) vetëm një pronari (dhe e dëgjon vetëm atë). A është pozita e këtyre dyve e barabartë? Lavdi Perëndië, nuk është (e barabartë), por shumica e tyre nuk e dinë këtë!'),
(4088, 39, 30, 'Me të vërtetë, do të vdesësh ti, e me të vërtetë, do të vdesin edhe ata,'),
(4089, 39, 31, 'e pastaj, në Ditën e gjykimit, te Zoti juaj do të polemizoni.'),
(4090, 39, 32, 'Kush është më i keq se ai që gënjen (trillon) për Perëndinë dhe konsideron gënjeshtër të Vërtetën (Kur’anin), kur i erdhi ajo? A nuk është skëterra vendstrehim për mohuesit?'),
(4091, 39, 33, 'E, ai që ka sjellë të Vërtetën (Muhammedi) dhe (ata) që i kanë besuar asaj janë të ruajtur (nga të këqiat),'),
(4092, 39, 34, 'ata do të kenë, çka të duan te Zoti i tyre; e ky është shpërblim për bamirësit,'),
(4093, 39, 35, 'që Zoti t’ua mbulojë atyre veprimet më të këqia që i kanë bërë dhe t’i shpërblejë për veprat më të mira që i kanë bërë.'),
(4094, 39, 36, 'A nuk i mjafton Zoti robit të Vet? E ata të frikojnë ty me zotëra, pos Tij (Perëndisë). Kë e shpie Perëndia në humbje për të ska kurrfarë udhëzuesi.'),
(4095, 39, 37, 'E kë e udhëzon Perëndia, (atë) s’ka kush e shpie në humbje. A nuk është i fuqishëm Perëndia që dënon (mëkatarët).'),
(4096, 39, 38, 'Nëse ti i pyet ata se kush i ka krijuar qiejt dhe Tokën, me siguri do të thonë: “Perëndia” – Thuaju: “A mendoni për ata që i adhuroni ju përveç Perëndisë – që mund ta evitojnë të keqen e Tij, nëse Perëndia don të ma dërgojë, ose a mund ta pengojnë mëshirën e Tij, nëse Ai don të më dërgojë (atë mëshirë)”. Thuaj: “Më mjafton Perëndia. Tek Ai mbështeten ata që kërkojnë mbështetje”.'),
(4097, 39, 39, 'Thuaj: “O populli im! Ju punoni sipas gjendjes suaj, e unë do të punojë (ashtu siç po punojë), e së shpejti do ta dini,'),
(4098, 39, 40, 'kë do ta arrijë dënimi poshtërues dhe kë do ta arrijë dënimi i përhershëm”.'),
(4099, 39, 41, 'Ne, ta kemi zbritur ty Librin (Kur’anin), për dobi të njerëzve, me të vërtetën; e kush shkon në rrugë të drejtë, ai e ka për dobi të vet, e kush e humbë këtë rrugë, ai humb në dëm të vet, e ti (o Muhammed), nuk je përgjegjës për punët e tyre.'),
(4100, 39, 42, 'Perëndia iu merr shpirtërat atyre që u ka ardhur çasti i vdekjes, por dhe atyre që nuk kanë vdekur e janë në gjumë, e ai ndalë shpirtin e atij që i është caktuar vdekja, e të tjerëve ua kthen shpirtërat deri në kohën e caktuar... Me të vërtetë, për këtë ka argumente, për ata që mendojnë.'),
(4101, 39, 43, 'A marrin ata, përveç Perëndisë, ndërmjetësues tjetër? Thuaj: “Vallë, a (i merrni ata ndërmjetësues) edhe pse nuk kanë asgjë në dorë dhe nuk kuptojnë?!”'),
(4102, 39, 44, 'Thuaju: “Vetëm për Perëndinë është i tërë ndërmjetësimi (mirësia); atij i takon pushteti i qiejve dhe i Tokës, e pastaj – tek Ai do të ktheheni”.'),
(4103, 39, 45, 'Kur përmendet vetëm Perëndia, neveriten zemrat e atyre që nuk besojnë në jetën tjetër, ndërsa kur të përmenden ata (zotërat), përveç Perëndisë, ata menjëherë gëzohen.'),
(4104, 39, 46, 'Thuaj: “O Zoti im, Krijues i qiejve dhe i Tokës, njohës i së dukshmes e të padukshmes, Ti do të gjykosh mes robërve Tu, për atë që nuk janë pajtuar!”'),
(4105, 39, 47, 'Sikur zullumqarët të kishin çdo gjë që gjendet në Tokë, dhe po kaq më tepër, ata të gjitha këto do t’i flijonin (për të shpëtuar), prej dënimit të ashpër në Ditën e Kijametit. Atëherë, Zoti do t’u tregojë atyre atë që nuk e kanë llogaritur,'),
(4106, 39, 48, 'u shfaqen atyre të këqiat që i kanë bërë dhe do t’i kaplojë dënimi të cilin e përqeshnin.'),
(4107, 39, 49, 'Kur njeriut i bie ndonjë e keqe, ai na lutet Neve, e kur pas kësaj i afrojmë një të mirë prej Nesh, thotë: “Kjo më është dhënë vetëm pse kam ditur”. Jo, por kjo është vetëm një sprovim, e shumica e tyre nuk e dinë.'),
(4108, 39, 50, 'Kështu thonin edhe ata që ishin para tyre, prandaj nuk patën dobi nga ajo që patën fituar,'),
(4109, 39, 51, 'prandaj, i goditi ata dënimi i të këqiave që kanë punuar. Por, dhe ata që kanë bërë zullum prej këtyre (kurejshit), do t’u bie dënimi për të këqiat që kanë bërë, e nuk do të mund t’i ikin (dënimit).'),
(4110, 39, 52, 'A nuk e dinë ata se Perëndia i afron furnizim të bollshëm kujt të dojë, e ia ngushton (kujt të dojë); me të vërtetë, në këtë ka argumente për popullin që beson.'),
(4111, 39, 53, 'Thuaj: “O robërit e mi, që i keni bërë keq vetvetes, mos e humbni shpresën në mëshirën e Perëndisë! Perëndia, me siguri, i falë të gjitha mëkatet. Ai, me të vërtetë, është falës dhe përdëllimtar.'),
(4112, 39, 54, 'Kthehuni kah Zoti juaj dhe përuluni para Tij, para se t’u vijë dënimi, sepse, pastaj nuk do të keni ndihmë.'),
(4113, 39, 55, 'Dhe, pasoni atë më të bukurën që u është shpallur (Kur’anin) nga Zoti juaj, para se t’ju vie dënimi papritmas, pa e hetuar atë,'),
(4114, 39, 56, 'e njeriu të thotë: “Mjer për mua që nuk i kam kryer obligimet ndaj Perëndisë, e madje edhe jam tallur (me besimtarët)!”'),
(4115, 39, 57, 'Ose të thotë: “Sikur Perëndia të më kishte udhëzuar, do të isha bërë nga të mirët!”'),
(4116, 39, 58, 'Ose të thotë, kur ta sheh dënimin: “Sikur të ishte e mundshme për t’u kthyer edhe një herë (në këtë botë), e të bëhem prej të mirëve!”'),
(4117, 39, 59, '(Atij i thuhet): “Me të vërtetë, të ka ardhur dokumenti Im (Kur’ani), e ti e konsiderove të rrejshëm atë, u bëre mendjemadh (nuk e ke përfillur dhe ishe jobesimtar)”.'),
(4118, 39, 60, 'E, në Ditën e Kijametit do t’i shohësh ata që kanë përgënjeshtruar Perëndinë – me fytyra të zeza. E vallë, a nuk është ferri – vend për ata që janë mendjemëdhenj?!'),
(4119, 39, 61, 'Perëndia do t’i shpëtojë ata që ruhen për të mirat e bëra, prandaj ata nuk do t’i prekë e keqja, e as nuk do të pikëllohen.'),
(4120, 39, 62, 'Perëndia është Krijues i çdo gjëje dhe Ai është mbikëqyrës i çdo gjëje,'),
(4121, 39, 63, 'të Tij janë, çelësat e qiejve dhe të Tokës! E, ata që i mohojnë argumentet e Perëndisë, ata janë të humbur!'),
(4122, 39, 64, 'Thuaju atyre: “Vallë, a ju po më urdhëroni që të adhuroj dike tjetër, përveç Zotit, o injorantë!”'),
(4123, 39, 65, 'Me të vërtetë, të është shpallur ty edhe atyre (pejgamberëve) që kanë qenë para teje: “Nëse i përshkruan Perëndisë shok, me siguri do të të humbin punët tuaja dhe do të jesh ndër ata që janë shkatërruar”.'),
(4124, 39, 66, '(Mos adhuro idhujt), por adhuro vetëm Perëndinë dhe bëhu falenderues!'),
(4125, 39, 67, 'Ata nuk e kanë madhëruar Perëndinë siç duhet; e tërë Toka në Ditën e gjykimit do të jetë nën pushtetin e Tij, e qiejt do të jenë të përfshirë në fuqinë e Tij. Qoftë lavdëruar Ai dhe larg nga ajo që i bëjnë shok Atij!'),
(4126, 39, 68, 'Dhe fryhet në Sur (Buri), e vdesin të gjithë ata që gjenden në qiej dhe në Tokë, do të mbesin vetëm ata që do Perëndia; pastj do të fryhet për të dytën herë në Sur dhe ata përnjëherë do të ngjallen e do të presin (çka do të ndodhë).'),
(4127, 39, 69, 'Dhe Toka do të shndëritet me dritën e Zotit të vet dhe do të vendoset libri, do të sillen pejgamberët dhe dëshmitarët dhe do të gjykohen ata me drejtësi, e nuk do t’u bëhet asgjë e pa drejtë;'),
(4128, 39, 70, 'secilit njeri do t’i jepet shpërblimi për atë që ka bërë, meqë Ai di më së miri se çka kanë punuar.'),
(4129, 39, 71, 'Dhe jobesimtarët do të shtyhen në ferr, grupe-grupe, dhe kur të arrijnë atje, hapen dyert e tij dhe rojtarët u thonë atyre: “A nuk u kanë ardhur pejgamberët nga mesi juaj, të cilët ju lexonin argumentet e Zotit tuaj e ju tërheqnin vërejtjen për takimin e kësaj dite? Mohuesit do të thonë: “Po, me të vërtetë, (na kanë ardhur), por është e sigurt fjala e Perëndisë për dënimin e mohuesve”.'),
(4130, 39, 72, '(Atyre) u thuhet: “Hyni në portat e ferrit, ku do të qëndroni gjithmonë në të! Sa vend i shëmtuar që është ai për mendjemëdhenjtë!'),
(4131, 39, 73, 'E, ata që janë ruajtur (nga mëkatet), do të shtyhen në xhennet, grupe-grupe dhe dyert e tij hapen e do t’u thonë atyre rojtarët e tij: “Shpëtimi qoftë mbi ju! Ju jeni të kënaqur, andaj hyni në të, ku do të qëndroni përgjithmonë!”'),
(4132, 39, 74, 'Ata do të thonë: “Falenderimi qoftë për Perëndinë, që na plotësoi premtimin e Vet e na dha në trashëgim tokën (e xhennetit), që të vendosemi si të duam!” Sa i mrekullueshëm është shpërblimi i atyre që kanë punuar (sipas dispozitave të Perëndisë)!'),
(4133, 39, 75, 'Do ti shohësh engjëjt e tubuar rreth Arshit duke lavdëruar me falenderim Zotin e tyre; dhe do t’u gjykohet mes tyre me të drejtë dhe u thuhet: “Falenderimi qoftë për Perëndinë, Zotit të gjithësisë!”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'Hâ, Mîm.'),
(4135, 40, 2, 'Shpallja e këtij Libri është prej Perëndisë, të Plotëfuqishëm dhe të Gjithëdijshëm.'),
(4136, 40, 3, 'I cili falë mëkatet dhe pranon pendimin, që dënon ashpër, dhe është pronar i të mirave; nuk ka zot tjetër përveç Tij dhe tek Ai është kthimi.'),
(4137, 40, 4, 'Nuk bëjnë polemikë për ajetet e Perëndisë (meqë janë të qarta), pos tyre që nuk besojnë, prandaj mos të mashtrojë ty qarkullimi i tyre nëpër botë.'),
(4138, 40, 5, 'Në të vërtetë, edhe para tyre (Kurejshëve), ata (pejgamberët) i përgënjeshtruan populli i Nuhut, si dhe grupet tjera pas tyre. Secili popull u përpoq ta mundojë pejgamberin e vet dhe të bënin polemikë të kotë për ta zhdukur të vërtetën, andaj Unë i kam zhdukur ata; e çfarë ka qenë dënimi Im!'),
(4139, 40, 6, 'Dhe kështu, për ata që mohuan, u realizua fjala e Zotit tënd. – Ata, me të vërtetë, janë banorë të zjarrit.'),
(4140, 40, 7, 'Ata (engjëjt) që e mbajnë Arshin dhe ata që janë rreth tij, e madhërojnë Zotin e tyre me falënderim dhe i besojnë Atij, si dhe lypin falje (prej Perëndisë) për ata që kanë besuar (duke thënë): “O Zoti ynë, Ti përfshinë çdo gjë me mëshirën dhe dijeninë; prandaj fali ata që janë penduar dhe që ndjekin rrugën Tënde dhe ruaji ata prej dënimit të ferrit!'),
(4141, 40, 8, 'O Zoti ynë, shpjeri ata në kopshtijet e Adnit, të cilat ju ke premtuar tyre, dhe prindërit e tyre dhe pasardhësit e tyre, ata që kanë qenë të mirë; Ti je i Plotëfuqishëm dhe Gjithëdijshëm.'),
(4142, 40, 9, 'Dhe, ruaj ata nga veprat e këqia, e kë e ruan Ti nga veprat e këqia atë ditë, Ti e ke mëshiruar atë; e ky, me të vërtetë, është shpëtim i madh!”'),
(4143, 40, 10, 'Me të vërtetë, ata që kanë mohuar do të thirrën: “Urrejtja e Perëndisë ndaj jush, është më e madhe se urrejtja juaj ndaj vetvetes, kur jeni thirrë në besim, por keni mohuar”.'),
(4144, 40, 11, 'Ata do të përgjigjen: “O Zoti ynë, na ke bërë të vdekur dy herë dhe na ke ngjallur dy heë, prandaj, ne i pranuam mëkatet tona. E, a ka ndonjë rrugëdalje (nga dënimi)?”'),
(4145, 40, 12, 'Kjo është për atë, që kur thirrej Perëndia i Vetëm, mohojshi, e nëse i përshkruhej shok Atij – ju besojshi. Vendimi i përket vetëm Perëndisë, të lartësuar e të madh.'),
(4146, 40, 13, 'Ai është që ju tregon dokumentet e Tij, dhe ju zbret furnizim prej qiellit, e këshillën e pranon vetëm ai që kthehet (te Perëndia).'),
(4147, 40, 14, 'Andaj, lutjuni Perëndisë duke i besuar Atij sinqerisht, madje, edhe nëse këtë e urrejnë jobesimtarët! –'),
(4148, 40, 15, '(Ai) dhuron pozita të larta, është pronar i Arshit, dërgon Shpalljen me urdhërin e Tij tek ata që don Ai, prej robërve të Vet, për t’ju tërheqë vërejtjen në Ditën e Takimit (Kijametit),'),
(4149, 40, 16, 'në Ditën kur ata do të paraqiten të zbuluar, kur për Perëndinë asgjë e tyre nuk është e fshehtë. (Atyre u thuhet): “Kujt i përket pushteti sot? (Jipet përgjigja): “Për Perëndinë e Vetëm dhe Ngadhnjimtar!”'),
(4150, 40, 17, 'Sot, çdo njeri do të shpërblehet për atë që ka punuar; sot nuk ka padrejtësi! Perëndia, me të vërtetë, është llogaritar i shpejtë.'),
(4151, 40, 18, 'Dhe, paralajmëroi ata për Ditën e afërt të Gjykimit, kur zemrat do t’u vijnë deri në fyt prej pikëllimit. (Atëherë), për njerëzit e këqinj nuk ekziston asnjë mik i sinqertë dhe ndërmjetësues i pranueshëm.'),
(4152, 40, 19, '(Perëndia) di për shikimin e përvjedhur të syrit dhe atë që fsheh zemra;'),
(4153, 40, 20, 'Perëndia gjykon drejt, e ata që u luten atyre përveç Tij, nuk gjykojnë asgjë. Perëndia, me të vërtetë, dëgjon dhe sheh (çdo gjë).'),
(4154, 40, 21, 'A s’kanë udhëtuar ata nëpër botë, që të shohin se si ka qenë fundi i atyre që kanë jetuar më parë? Ata kanë qenë më të fuqishëm se ata (Kurejshit) dhe më me shumë gjurmë (përmendore), por, Perëndia i dënoi, për shkak të mëkateve të tyre, dhe për ta nuk ka pasur kurrfarë mbrojtësi prej dënimit të Perëndisë.'),
(4155, 40, 22, 'Ky (dënim) është, ngase atyre u vinin pejgamberët me dokumente të qarta, e ata nuk u besonin, prandaj Perëndia i dënoi. Me të vërtetë, Ai është i fuqishëm dhe ndëshkon ashpër.'),
(4156, 40, 23, 'Me të vërtetë, Ne e kemi dërguar Musain me dokumentet Tona dhe me mrekulli të qartë –'),
(4157, 40, 24, 'te Faraoni, Hamani dhe Karuni, e ata thanë: “Ky është magjistar dhe gënjeshtar!”'),
(4158, 40, 25, 'E, kur atyre u erdhi (Musai) me të Vërtetën prej Nesh, ata thanë: “Mbytni djemtë e atyre që kanë besuar në të e lëni të gjalla vajzat e tyre!” Por dinakëritë e mohuesve gjithmonë janë në humbje.'),
(4159, 40, 26, 'Faraoni tha: “Më lëni ta mbys Musain, e ai le ta thërret në ndihmë Zotin e vet. Unë, me të vërtetë, frikohem se ai do t’ju prishë fenë tuaj, ose do të shkaktojë trazira në Tokë”.'),
(4160, 40, 27, 'Musai tha: “Unë i lutem Zotit tim dhe Zotit tuaj për të më mbrojtur prej çdo mendjemadhi që nuk beson në Ditën e llogarisë!”'),
(4161, 40, 28, 'Kurse, një njeri besimtar nga paria e popullit të Faraonit, i cili fshihte besimin e vet, tha: “Vallë, ta mbys njeriun për shkak se thotë: “Perëndia është Zoti im, e ai, me të vërtetë, ju ka ardhur me dokumente të qarta prej Zotit tuaj. Nëse është gënjyes, atij i përket dënimi i gënjeshtrës së tij, e nëse është i drejtë, juve ka me ju goditë diçka, me të cilën ju kërcënohet ai juve. Me të vërtetë, Perëndia nuk e udhëzon në rrugë të drejtë atë që e tepron me të këqia dhe është gënjeshtar.'),
(4162, 40, 29, 'O populli im, sot juve u përket pushteti në lartësitë e Tokës, por kush do të na mbrojë nga goditja e Perëndisë, nëse na arrinë neve?” E, Faraoni tha: “Unë ju rekomandoj vetëm atë që e konsideroj të drejtë dhe vetëm unë ju udhëzoj në rrugën e drejtë”.'),
(4163, 40, 30, 'E, atëherë, ai besimtari tha: “O populli im, unë druaj që do t’ju arrijë dënimi – si popujve të mëparshëm,'),
(4164, 40, 31, 'ashtu siç ka qenë (shkatërrimi) i popullit të Nuhut, Adit dhe Themudit dhe breznive pas tyre. E, Perëndia nuk don padrejtësi për robërit e Vet.'),
(4165, 40, 32, 'O populli im, unë, me të vërtetë, i druaj dënimit taj në Ditën e ringjalljes,'),
(4166, 40, 33, 'në Ditën kur, nga frika – do të ikni prapa, e s’ka për ju mbrojtës nga dënimi i Perëndisë. E, atë që Perëndia e lë në humbje, për të s’ka udhërrëfyes.'),
(4167, 40, 34, 'Me të vërtetë, juve më parë u pat ardhur Jusufi, me argumente të qarta, por ju vazhdimisht dushonit në atë ë u kishte sjellë ai. E, kur vdiq ai, ju thatë: “Perëndia, kurrsesi nuk do të dërgojë pejgambe pas tij! Ja, kështu Perëndia e shpie në humbje çdokënd që e kalon kufirin në të këqia dh dyshon,'),
(4168, 40, 35, 'ata që polemizojnë për argumentet e Perëndisë, pa pasur kurrfarë baze për atë, këtë e urren Perëndia dhe ata që besojnë. Kështu, Perëndia, ia vulosë zemrën çdo mendjemadhi e dhunuesi”.'),
(4169, 40, 36, 'Dhe, Faraoni tha: “O Haman, ndërtoma një rrokaqiell (të lartë) që t’i arrij rrugët,'),
(4170, 40, 37, 'rrugët e qiellit – e ta shoh Zotin e Musait. Por, unë, me të vërtetë, e konsideroj atë (Musain) rrenacak”. Kështu, Faraonit i është zbukuruar vepra e tij e shëmtuar, dhe është shmangur nga rruga e drejtë, e dredhia e Faraonit, me siguri, është falimentuar.'),
(4171, 40, 38, 'Dhe, ai besimtari tha: “O populli im, më pasoni (ejani pas meje), unë ju udhëzoj në rrugë të drejtë!'),
(4172, 40, 39, 'O populli im, me të vërtetë, jeta e kësaj bote është vetëm kënaqësi e përkohshme.'),
(4173, 40, 40, 'Kush bën vepër të keqe, do të dënohet vetëm sipas veprës së bërë, e kush bën vepër të mirë, qoftë mashkull apo femër – duke qenë besimtar, - at do të hyjnë në xhennet, në te do të furnizohen, pa hesap (fatyrë).'),
(4174, 40, 41, 'O populli im, ç’është kjo, unë ju thërras në shpëtim, e ju më thirrni në zjarr!'),
(4175, 40, 42, 'Më thërritni që ta mohojë Perëndinë dhe t’i bëj shok Atij – atë, për të cilin unë nuk di, e unë ju thërras te i Plotëfuqishmi, Mëshiruesi i Madh.'),
(4176, 40, 43, 'Padyshim se, ajo që më thërritni ju, nuk mund të thërritet (të jetë e adhuruar), as në këtë botë as në tjetrën dhe se kthimi ynë është te Perëndia, dhe, ata që e teprojnë me të këqia, do të jenë banorë të zjarrit.'),
(4177, 40, 44, 'Ju, së shpejti do ta kujtoni atë që po ju them! E, unë – rastin tim ia lë Perëndisë, me të vërtetë, Perëndia i sheh robërit e Vet.'),
(4178, 40, 45, 'Dhe, Perëndia e ruajti atë prej të këqiave dhe trillimeve të tyre, e ithtarët e Faraonit i kaploi dënimi i vrazhdë,'),
(4179, 40, 46, 'zjarri (të cilit) i ekspozohen në mëngjes dhe mbrëmje, e kur të arrijë Çasti (Dita e Kijametit, ju thuhet): “Shpini ithtarët e Faraonit në dënimin më të rëndë!'),
(4180, 40, 47, 'Dhe, kur ata të grinden në zjarr dhe t’u thonë ithtarët e shtypur – eprorëve që madhështoheshin: “Na, me të vërtetë, ju kemi ndjekur (pasuar) juve, e a mund të shmangëni prej nesh një pjesë të zjarrit?”'),
(4181, 40, 48, 'Ata që madhështoheshin, do të thonë (që ishin eprorë): “Të gjithë ne jemi në të (zjarr). Perëndia, me të vërtetë, ka gjykuar mes robërve”.'),
(4182, 40, 49, 'Dhe, ata që gjenden në zjarr, do t’u thonë rojtarëve të skëterrës: “Lutjuni Zotit tuaj që të na lehtësojë dënimin, (madje) qoftë vetëm një ditë!”'),
(4183, 40, 50, '(e rojtarët do t’u thonë atyre): “Vallë, a nuk ju kanë sjellë pejgamberët tuaj argumente të qarta?” Ata përgjigjen: “Po, me të vërtetë, (na kanë sjellur)”. Ata (rojtarët) ju thonë: “Lutjuni pra ju! – Por, thirrja (lutja) e mohuesve është vetëm në humbje”,'),
(4184, 40, 51, 'Na, me të vërtetë, do t’u ndihmojmë pejgamberëve Tanë dhe atyre që besojnë – në jetën e kësaj bote dhe (atë) Ditë, kur do të sillën dëshmitarët (Ditën e Kijametit),'),
(4185, 40, 52, '(atë) Ditë kur zullumqarëve (mohuesve) nuk do t’u bëjë dobi arsyetimi i tyre dhe ata i pret mallkimi dhe vendbanimi i shëmtuar.'),
(4186, 40, 53, 'Na, me të vërtetë, ia kemi dhënë Musait udhëzimin dhe u kemi dhënë në trashëgim bijve të Israelit Librin,'),
(4187, 40, 54, 'si udhërrëfyes dhe këshillë për mentarët.'),
(4188, 40, 55, 'Andaj, bëhu i durueshëm ti (o Muhammed). Pa dyshim, premtimi i Perëndisë është i vërtetë (do të realizohet). Lutju Zotit për faljen e mëkateve tuaja dhe lartësoje Zotin tënd me falenderim, në mbrëmje dhe mëngjes!'),
(4189, 40, 56, 'Ata që polemizojnë mbi argumentet e Perëndisë, pa u ardhur kurrfarë dokumenti (tjetër), në zemrat e tyre ka vetëm mendjemadhësi, e cila nuk do ti shpie në qëllimin e tyre, andaj kërko mbrojtje prej Perëndisë! Me të vërtetë, Ai i dëgjon të gjitha dhe i sheh të gjitha.'),
(4190, 40, 57, 'Krijimi i qiejve dhe i Tokës është më i madh se krijimi i njerëzve, por (këtë) shumica e njerëzve nuk e di.'),
(4191, 40, 58, 'Nuk barazohet i verbëti dhe ai që sheh, dhe ai që beson e bën vepra të mira me atë që bën keq. E, pak po këshilloheni.'),
(4192, 40, 59, 'Me të vërtetë, do të vije Çasti i ringjalljes, për këtë s’ka dyshim, por shumica e njerëzve nuk besojnë.'),
(4193, 40, 60, 'Zoti juaj, ka thënë: “Më thirrni Mua (më adhuroni), Unë do t’ju përgjigjem (shpërblejë)! Me të vërtetë, ata që mendjemadhësohen e nuk duan të Më adhurojnë – do të hyjnë të poshtëruar në skëterrë.'),
(4194, 40, 61, 'Perëndia, për ju ka bërë natën për të pushuar, e ditën të ndritshme për të parë (dhe punuar). Me të vërtetë, Perëndia është dhurues i madh ndaj njerëzve, por shumica e njerëzve nuk falenderohen.'),
(4195, 40, 62, 'Ja, ky është Zoti juaj, krijues i çdo gjëje. S’ka zot tjetër përveç Tij. E, kah po shmangeni ju?!'),
(4196, 40, 63, 'Kështu ______________________?'),
(4197, 40, 64, 'Ja, ky është Perëndia juaj, Zoti i juaj. Është i lartësuar Perëndia, ai që ua ka bërë Tokën vendbanim e qiellin kulm, dhe u ka dhënë formë, e ju ka zbukuruar formën dhe u ka furnizuar me ushqime të mira. Ja, ky është Perëndia juaj, Zoti i juaj. Është i lartësuar Perëndia, Zoti i gjithësisë.'),
(4198, 40, 65, 'Ai është i Gjallë, s’ka zot tjetër përveç tij, anaj lutjuni vetëm Atij, duke qenë ajo lutje vetëm për Te: “Falenderimi qoftë për Perëndinë, Zotin e Gjithësisë!”'),
(4199, 40, 66, 'Thuaj (o Muhammed!): “Mua më është ndaluar të adhuroj atë që ju e adhuroni, përpos Perëndisë, pasi që më kanë ardhur argumentet e qarta prej Zotit tim, e më është urdhëruar që t’i përulem Zotit të Gjithësisë”.'),
(4200, 40, 67, 'Ai (Perëndia), është Ai që ju ka krijuar prej dheut, pastaj prej pikës së farës (spermës), pastaj prej gjakut të droçkuar, pastaj ju bënë që të lindeni si foshnje, pastaj të arrini etapën e pjekurisë, e pastaj të bëheni pleq, - e disa prej jush vdesin më herët, - e që ta arrini çastin e caktuar të jetës, për të kuptuar (menduar).'),
(4201, 40, 68, 'Ai (Perëndia), është Ai që i ngjallë (të vdekurit) dhe i vdes (të gjallët) dhe kur Ai vendos diçka, vetëm thotë pë të: “Bëhu!” dhe ajo menjëherë bëhet.'),
(4202, 40, 69, 'A i ke parë ata që polemizojnë mbi argumentet e Perëndisë, e vallë kah po ia mbajnë?'),
(4203, 40, 70, 'Ata që e kanë përgënjeshtruar Librin (Kur’anin) dhe ato që i kemi dërguar me Pejgamberët Tonë, së shpejti do ta dinë ata,'),
(4204, 40, 71, 'kur t’u vihen prangat në qafë dhe të sillen me zinxhirë,'),
(4205, 40, 72, 'në ujë të valë, e pastaj të hudhen (digjen) në zjarr,'),
(4206, 40, 73, 'e pastaj atyre ju thuhet: “Ku janë ata ë i adhuronit,'),
(4207, 40, 74, 'përveç Perëndisë?” Ata përgjigjen: “Na kanë humbur (nuk po i shohim). Jo, por ne nuk kemi adhuruar asgjë më parë”. Ja kështu, Perëndia i shpie mohuesit në humbje.'),
(4208, 40, 75, 'Ky dënim është për shkak të gëzimit tuaj në tokë, pa kurrfarë baze, dhe ngase keni qenë arogantë (fodull) në gëzim.'),
(4209, 40, 76, 'Hyni në dyert e skëterrës, duke qëndruar përherë në te! Sa i shëmtuar që është vendbanimi i mendjemëdhenjve!'),
(4210, 40, 77, 'Andaj, bëhu i durueshëm, premtimi i Perëndisë, me siguri, do të realizohet, e nëse të tregojmë ty diçka, nga ajo që u kemi premtuar atyre, apo nëse të vdesim ty (para dënimit të tyre), e ata te Ne do të kthehen (për llogari).'),
(4211, 40, 78, 'Na, me të vërtetë, para teje kemi dërguar pejgamberë dhe prej tyre t’i kemi treguar ty, e disa prej tyre nuk t’i kemi treguar. Asnjë pejgamber nuk mund të tregojë ndonjë argument, pos me lejen e Perëndisë. E, kur të vie urdhëri i Perëndisë, do të gjykohet me saktësi, e atëhrë, të këqinjtë do të shkatërrohen.'),
(4212, 40, 79, 'Perëndia është Ai që për ju i ka krijuar shtazët, disa për t’i shalur, e disa për t’i ngrënë.'),
(4213, 40, 80, 'Dhe, për ju ka në to dobi, që të arrini realizimin e nevojave të rëndësishme që i keni – dhe me to e me barka transportoheni.'),
(4214, 40, 81, 'Dhe Ai ju tregon argumentet e Tij, e cilat argumente të Perëndisë i mohoni ju?!'),
(4215, 40, 82, 'Vallë, a nuk kanë udhëtuar ata nëpër botë, e të shohin si ka qenë fundi i atyre të mëparshmëve? Ata kanë qenë më të shumtë (numerikisht), më të fortë, e kanë lënë më shumë përmendore në Tokë, e nuk u ka bërë dobi asgjë ajo që kanë fituar.'),
(4216, 40, 83, 'E kur ju erdhën pejgamberët e tyre me argumente të qarta, ata u gëzuan me dijeninë që kishin (dijeninë iluzore), e i kaploi ajo (dënimi) për atë që e përqeshnin.'),
(4217, 40, 84, 'E, kur ta shohin dënimin Tonë, atëherë thohë: “Ne besojmë në Perëndinë – të vetmin Zot, e i mohojmë ato që ia përshkruanim për shok Atij”.'),
(4218, 40, 85, 'Por besimi i tyre, nuk u sjell kurrfarë dobie, pasi e panë dënimin Tonë. Kjo është dispozita e Perëndisë, e cila është zbatuar te robërit e Tij – dhe mohuesit (atëherë) do të humbin.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'Hâ, Mîm.'),
(4220, 41, 2, '(Kjo është) Shpallja prej Bamirësit, Mëshiruesit,'),
(4221, 41, 3, 'Libër, argumentet e të cilit janë të qarta, (që është) Kur’ani në gjuhën arabe, për popullin që di,'),
(4222, 41, 4, 'si sihariques dhe paralajmërues, por shumica e tyre janë shmangë (prej studimit të tij) e nuk duan të dëgjojnë,'),
(4223, 41, 5, '(por) thonë: “Zemrat tona janë të mbuluara (blinduara e nuk kuptojnë) atë që na thërret ti neve, veshët tanë janë të shurdhër për atë, e në mes nesh e teje ka perde, andaj puno ti (në idenë tënde), (por) me të vërtetë, dhe ne do të punojmë”.'),
(4224, 41, 6, 'Thuaj: “Unë, me të vërtetë, jam njeri si ju, mua më është shpallur që Zoti juaj është vetëm një Zot, andaj lutjuni Atij sinqerisht dhe kërkoni falje prej Tij! E, mjerë për ata që i bëjnë shok Atij,'),
(4225, 41, 7, 'të cilët nuk japin zeqatin dhe të cilët nuk besojnë në jetën tjetër!'),
(4226, 41, 8, 'Me të vërtetë, ata që besojnë dhe bëjnë vepra të mira do të kenë shpërblim të pandërprerë.'),
(4227, 41, 9, 'Thuaj (o Muhammed!): “Vallë, a me të vërtetë ju e mohoni Atë, i cili e ka krijuar Tokën në dy ditë (periudha kohore) – dhe madje i përshkruani Atij shok? (Ja), ky është Zoti i gjithësisë!”'),
(4228, 41, 10, 'Dhe, krijoi në Tokë (sipërfaqe të saj) male të palëvizshme, e bëri që në të – të krijohen shumë begati, dhe u caktoi në te (të gjithëve) furnizimin e tyre, të gjitha këto për katër ditë (periudha kohore); ky është shpjegimi për të gjithë ata që pyesin.'),
(4229, 41, 11, 'Pastaj, vendosi (të rregullojë) qiellin, e ai ishte në gjendje mjegullire, e i tha atij (qiellit) dhe Tokës: “Dukuni (ejani të përulur) me dashje ose me dhunë!” e ata u përgjigjë: “Erdhëm vullnetarisht!”'),
(4230, 41, 12, 'Dhe, Ai krijoi (të jenë) shtatë qiej në dy ditë (periudha kohore), dhe çdo qielli i caktoi detyrat e tij. E, qiellin më të afërm e kemi zbukuruar me yje të shkëlqyer dhe i ruajmë ato. Kjo është dispozita e të Plotëfuqishmit dhe të Gjithëdijshmit.'),
(4231, 41, 13, 'Nëse shmangen (refuzojnë besimin), ti thuaju: “Ju paralajmëroj me bubullimë (dënimin e tmerrshëm)) aso dënimi çfarë e goditi Adin e Themudin,'),
(4232, 41, 14, 'kur atyre u erdhën pejgamberët – para tyre dhe pas tyre (duke thënë): “Mos adhuroni (asgjë) përveç Zotit!” e ata thanë: “Sikur të kishte dashtë Zoti ynë, Ai do të kishte dërguar engjëj. Ne nuk besojmë në atë që është dërguar me ju”.'),
(4233, 41, 15, 'Sai përket Adit, ata janë madhështuar në Tokë pa kurrfarë baze dhe thonin: “Kush është më i fortë se ne?” Vallë a nuk shohin ata se Perëndia që i ka krijuar, është më i fuqishëm se ata, - e ata i kanë mohuar argumentet Tona.'),
(4234, 41, 16, 'Dhe, Ne u dërguam atyre në ditët e shëmtuara (fatkeqësisë) erë të ftohtë, për ta shijuar dënimin poshtërues që në këtë jetë, e dënimi në jetën tjetër është edhe më poshtërues dhe atyre nuk do t’u ndihmohet.'),
(4235, 41, 17, 'Dhe, Themudit, Ne ia kemi treguar rrugën e drejtë, por ata e deshën (preferuan) verbërimin e jo rrugën e drejtë, andaj ata i goditi dënimi i stuhishëm, poshtërues, për atë që kanë punuar;'),
(4236, 41, 18, 'e, i shpëtuam ata që besuan dhe që janë ruajtë (prej të këqiave).'),
(4237, 41, 19, '(Kujtoju atyre) Ditën kur do të tubohen armiqët e Perëndisë te zjarri (në vendin e llogarisë), e do të tubohen duke u shtyrë me forcë (dhunë),'),
(4238, 41, 20, 'dhe kur të vijnë tek ai (zjarri), kundër tyre do të dërshmojnë, veshët e tyre, sytë e tyre, dhe lëkurat e tyre – për atë që kanë punuar.'),
(4239, 41, 21, 'Ata u thonë lëkurave të tyre: “Pse dëshmuat kundër nesh?” e ato përgjigjen: “Perëndia, na ka bërë që të flasim – ashtu siç i ka dhënë aftësinë e të folurit çdo sendi. Ai u ka krijuar për herë të parë dhe tek Ai do të ktheheni (përsëri)”.'),
(4240, 41, 22, 'Ju nuk jeni fshehur (nga frika), që kundër jush të mos dëshmojnë veshët tuaj, dhe sytë tuaj dhe lëkura juaj, por ngase keni besuar se Perëndia nuk do t’i dijë shumë ato që i keni punuar.'),
(4241, 41, 23, 'Dhe, kjo bindje e juaj, që keni pasur në Zotin tuaj, u ka shkatërruar juve, e u bëtë prej atyre që janë në humbje.'),
(4242, 41, 24, 'E, nëse ata bëhen (edhe) të durueshëm, zjarri është vendstrehim për ta; e nëse kërkojnë kthim, nuk do t’u lejohet kthimi.'),
(4243, 41, 25, 'Ne, u kemi pas caktuar atyre (mohuesve) shokët e këqinj, të cilët ua zbukuronin atyre – ato që janë para tyre dhe ato që janë pas tyre, e në ta u realizua fjala (e dënimit), së bashku me popujt që kanë qenë para tyre – prej xhindëve dhe njerëzve. Këta, me të vërtetë, kanë qenë të shkatërruar.'),
(4244, 41, 26, 'Mohuesit kanë thënë: “Mos e dëgjoni këtë Kur’an dhe flisni me zë të lartë, për ta mund atë!”'),
(4245, 41, 27, 'Do të bëjmë Ne, që ta shijojnë mohuesit dënimin e ashpër dhe do t’i dënojmë ata me më të keqen, për veprat që kanë bërë.'),
(4246, 41, 28, 'Ky zjarri është dënimi i armiqve të Perëndisë. Në te është vendbanimi i përhershëm i tyre. Ky është dënim për ta, ngase kanë qenë mohues të ajeteve Tona.'),
(4247, 41, 29, '(Atëherë) mohuesit do të thonë: “O Zoti ynë, tregona ata (dy grupe) prej xhindëve dhe njerëzve, që na kanë shpënë në humbje, që t’i shtrojmë nën këmbët tona, e të bëhen ndër më të fundmit (në zjarr)!”'),
(4248, 41, 30, 'Me të vërtetë, ata që thonë: “Zoti ynë është Perëndia, e pastaj vazhdojnë në të drejtë (sipas urdhërit të Perëndisë), do t’ju zbresin atyre engjëjt (dhe do t’ju thonë): “Mos u frikësoni dhe mos u pikëlloni! Dhe gëzojuni xhennetit që ju është premtuar.'),
(4249, 41, 31, 'Ne jemi të afërmit tuaj në jetën e kësaj bote dhe të së ardhmes; ju në te (xhennet) do të keni çka të dëshironi dhe çka të kërkoni,'),
(4250, 41, 32, 'si gosti prej Atij që falë shumë dhe që është mëshirues”.'),
(4251, 41, 33, 'E, kush flet më bukur se ai që thërret te Perëndia, bënë vepra të mira dhe thotë: “Unë, me të vërtetë, jam musliman!”'),
(4252, 41, 34, 'Nuk barazohet e mira dhe e keqja! Të keqen ktheje me të mirë e atëherë – armiku yt do të të bëhet menjëherë mik i ngushtë.'),
(4253, 41, 35, 'Këtë mund ta arrijnë vetëm ata që durojnë dhe këtë mund ta arrijnë vetëm ata që janë të pajisur (me virtyte të bukura).'),
(4254, 41, 36, 'E, nëse djalli fillon të të mashtrojë, kërko mbështetje te Perëndia. Me të vërtetë, Ai i dëgjon të gjitha dhe i di të gjitha.'),
(4255, 41, 37, 'Argumente të Tij janë edhe nata dhe dita, edhe Dielli dhe Hëna. Mos bëni sexhde (mos bini me fytyrë për tokë) as për Diellin as për Hënën, por bëni sexhde për Perëndinë, i cili i ka krijuar ato, nëse jeni adhurues të Atij.'),
(4256, 41, 38, 'E, nëse ata madhështohen, - po, ata që janë te Zoti yt (engjëjt), e lavdërojnë Atë natën dhe ditën, e kurrë nuk mërziten.'),
(4257, 41, 39, 'Njëri nga argumentet e Tij është edhe ai që ti e sheh tokën të përulur – të thatë, e kur e lëshojmë në të shiun, toka lëvizë dhe gjallohet. Me të vërtetë Ai që e ngjallë atë, i ngjallë edhe të vdekurit. Me të vërtetë, Ai është i pushtetshëm për çdo gjë.'),
(4258, 41, 40, 'Me të vërtetë, ata që i shtrembërojnë argumentet Tona, nuk do të jenë të fshehtë te Ne. e, a është më e mirë ai që do të hidhet në zjarr apo ai që në Ditën e Kijametit do të vie i qetë? Punoni çka të doni (se ju gjen ajo çka ju takon), e Ai, me të vërtetë, i sheh të gjitha çka punoni ju.'),
(4259, 41, 41, 'Me të vërtetë, ata që e kanë mohur Këshillën (Kur’anin), kur u ka ardhur atyre (do të dënohen). Me të vërtetë, ai (Kur’ani), është Libër i madhërueshëm,'),
(4260, 41, 42, 'të cilit nuk mund t’i vie gënjeshtra, nga asnjëra anë (as përpara, as përmbrapa). Kjo është shpallja prej të Gjithëdijshmit, të Falenderuarit.'),
(4261, 41, 43, 'Nuk të thuhet ty (o Muhammed) asgjë, përpos asaj që u është thënë pejgamberëve – para teje. Me të vërtetë, Zoti yt (edhe) falë shumë, por edhe dënon dhembshëm.'),
(4262, 41, 44, 'Sikur ta kishim shpallur Kur’anin në gjuhë të huaj, ata do të thonin: “Të ishin ajetet e shpallura të kuptueshme! Vallë, (a Kur’ani) të jetë në gjuhën joarabe, (e pejgamberi) të jetë arab!? Thuaj: “Ai, për besimtarët është udhërrëfyes dhe ilaç”. Dhe, ata që nuk besojnë, kanë pengesa në veshë (janë të shurdhër) dhe ai (Kur’ani), për ta është verbëri (ata janë të verbër), thuajse thirrën prej vendeve të largëta.'),
(4263, 41, 45, 'Dhe, Ne, me të vërtetë, ia kemi dhënë Musait Librin, e ata janë përçarë në të – dhe sikur të mos ishte Fjala e Zotit tënd e thënë më parë, atyre do t’u gjykohej menjëherë (në këtë jetë). Me të vërtetë, ata (mohuesit) dyshojnë thellë në këtë Libër.'),
(4264, 41, 46, 'Kush bën mirë, bën për dobi të vet, e kush punon keq, punon në dëm të vet. E, Zoti yt, nuk u bën zullum robërve (të Vet).'),
(4265, 41, 47, 'Vetëm Perëndia është i dijshëm kur do të ndodhë Çasti (Kijameti). Kurrfarë fruti nuk mund të dalë prej kupës së lules, as femra nuk mund të mbetet shtatzënë, as të lind, - pa dijeninë e Tij. E, atë Ditë kur Ai i thërret (idhujtarët): “Ku janë shokët e Mi, që m’i përshkruanit Mua?” Ata përgjigjen: “Na të lajmërojmë Ty se prej nesh nuk ka asnjë dëshmitar për ta (se janë shok të Tu)”.'),
(4266, 41, 48, 'Humbën (prej tyre) ata që i adhuronin më parë, dhe ata (tani), e dinë se nuk ka strehim për ta.'),
(4267, 41, 49, 'Njeriu nuk mërzitet kurrë duke kërkur të mirën (te Perëndia), e nëse e godet e keqja, ai menjëherë bie në dëshpërim dhe humbë shpresën.'),
(4268, 41, 50, 'Nëse i japim atij (njeriut) që ta shijojë mëshirën Tonë, pas një fatkeqësie që e ka goditur atë, ai me siguri, do të thotë: “Këtë e meritoj, dhe unë nuk besoj se do të ndodhë Çasti (Dita e Kijametit), e nëse kthehem te Zoti im, tek Ai më takon e mira e madhe”. Me të vërtetë, Ne do t’i lajmërojmë mohuesit, për atë që kanë punuar dhe do t’u japim ta shijojnë dënimin e rëndë.'),
(4269, 41, 51, 'Kur njeriut i dhurojmë dhuntitë Tona, ai shmanget dhe largohet me një anë, (nga të adhuruarit Neve), e kur ta godet e keqja, (atëherë) ai lutet gjerë e gjat).'),
(4270, 41, 52, 'Thuaju (atyre) o Muhammed: “A po shihni? Nëse ai (Kur’ani) është prej Perëndisë, e ju nuk e besuat; kush është në humbje më të madhe se ai që është në kundërshtim, larg (së vërtetës)?”'),
(4271, 41, 53, 'Na, atyre, do t’u dëftojmë argumentet Tona në hapësirat tokësore e qiellore, si dhe te vet ata, deri sa t’u bëhet plotësisht e qartë, se ai (Kur’ani), është i vërtetë. Vallë, a nuk të mjafton ty, që Zoti yt është, me të vërtetë, dëshmues i çdo gjëje?!'),
(4272, 41, 54, 'E, ata, me siguri, dyshojnë në takimin me Zotin e tyre. Dinie se, Ai (Perëndia), i përfshinë të gjitha gjërat (me fuqi dhe dijeni).');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'Hâ, Mîm.'),
(4274, 42, 2, 'Ajin, Sîn, Kâaf.'),
(4275, 42, 3, 'Kështu të shpallë ty, si dhe atyre para teje, Perëndia i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(4276, 42, 4, 'Atij i përkasin çka ka në qiej dhe çka ka në Tokë. Ai është i Lartësuar dhe i Madhërueshëm!'),
(4277, 42, 5, 'Gati që qiejt po shkyhen (prej madhërisë së Tij), njëri mbi tjetrin! E, engjëjt e madhërojnë duke e falenderuar Zotin e tyre dhe ërkojnë falje për ata që janë në Tokë. – (Ja, pra) Perëndia, me të vërtetë, është falës dhe mëshirues.'),
(4278, 42, 6, 'Ata që marrin, pos Perëndisë – ndihmë, shokë tjerë – Perëndia është mbikëqyrës mbi ta, e ti nuk je kujdestar i tyre.'),
(4279, 42, 7, 'Ja, kështu – Na ta kemi shpallur ty Kur’anin, në gjuhën arabe, për ta paralajmëruar popullin e Mekkes dhe ata rreth saj, dhe t’i paralajmërosh (ata) për Ditën e tubimit (Kijametit), për të cilën s’ka dyshim. (Atëherë) një grup (do të shkojë) në xhennet, e (grupi) tjetër në xhehennem.'),
(4280, 42, 8, 'E, sikur të dojë Perëndia, do t’i kishte bërë (të dy grupet) të një besimi, por Ai kë të dojë e shpie në mëshirën e Vet, kurse mohuesit nuk kanë as mbrojtës as ndihmës.'),
(4281, 42, 9, 'Vallë, a mos marrin ata mbrojtës tjetër, pos Atij?! E, Perëndia është i vetmi mbrojtës dhe Ai do t’i ngjallë të vdekurit. (Vetëm) Ai është i pushtetshëm për çdo gjë.'),
(4282, 42, 10, 'Për çfarëdo (çështje) që të polemizoni, vendimi është te Perëndia. Ky është Perëndia, Zoti im. Unë (vetëm) te Ai mbështetem dhe (vetëm) tek Ai kthehem.'),
(4283, 42, 11, 'Krijuesi i qiejve dhe i Tokës! Ai prej jush ka krijuar çifte për ju – e krijoi çifte edhe prej shtazëve – që kështu t’ju shumëzojë. Asgjë nuk i shembëllen Atij. Ai i dëgjon të gjitha dhe i sheh të gjitha.'),
(4284, 42, 12, 'Tek ai janë çelësat e thesarëve të qiejve dhe të Tokës. Ai i jep furnizim (të gjerë) kujt të dojë, por edhe ia mungon (kujt të dojë). Ai, në të vërtetë, është i Gjithëdijshëm për çdo gjë.'),
(4285, 42, 13, 'Ai ju ka urdhëruar në besim, ashtu siç e ka urdhëruar Nuhun dhe atë që ta shpallëm ty, dh me atë që e urdhëruam Ibrahimin, Musain dhe Isain: “Predikoni fenë e drejtë dhe mos u përçani në të!” Për idhujtarët është (shumë) e rëndë ajo për çka po i thërret ti. Perëndia e zgjedhë për Vete kënd të dojë Ai, dhe e udhëzon (në rrrugën e Tij) atë që i drejtohet me lutje.'),
(4286, 42, 14, 'E, ata u përçanë, prej egoizmit ne mes tyre – mu atëherë kur u erdhi dijenia. Dhe, sikur të mos ishte fjala e Zotit tënd, e thënë më parë (për shtyerjen e dënimit) deri në afatin e caktuar, atyre do t’u gjykohej menjëherë. E, ata të cilëve iu la Libri në trashëgim, dyshojnë thellë në të (Libër).'),
(4287, 42, 15, 'Andaj, ti thirri dhe bëhu këmbëngulës, ashtu siç je urdhëruar, e mos i ndjek epshet e tyre, dhe thuaj: “Unë besoj çdo libër që e ka shpallë Perëndia, dhe më është urdhëruar që të gjykojë drejt në mes jush; Perëndia është Zoti ynë dhe Zoti juaj. Veprat tona, janë për ne, e veprat tuaja janë për ju. Në mes nesh dhe jush nuk ka kurrfarë mosmarrëveshje. Perëndia do të na tubojë të gjithëve, dhe tek Ai do të kthehemi të gjithë”.'),
(4288, 42, 16, 'E, ata që bëjnë polemikë me fenë e Perëndisë, pasi që i janë përgjegjur asaj (polemike), dokumentet e tyre janë të pavlefshme te Zoti i tyre; mbi ta do të bie pezmi (i Perëndisë) dhe për ta ka dënim të ashpër.'),
(4289, 42, 17, 'Perëndia është Ai që e ka shpallur Librin, të vërtetën e paluhatshme dhe drejtësinë. E, kush të tregon ty, - ndoshta Dita e Kijametit është afër?!'),
(4290, 42, 18, 'E nxitojnë (Çastin) ata që nuk besojnë në të, e ata që besojnë, druajnë nga ajo dhe dijnë se ajo është plotësisht e vërtetë. E, me të vërtetë, ata që polemizojnë për Ditëne Kijametit, janë në humbje të largët!'),
(4291, 42, 19, 'Perëndia është i mirë ndaj robërve të Vet; Ai i jep furnizim kujt të dojë, dhe Ai është i Plotëfuqishëm dhe i Pushtetshëm.'),
(4292, 42, 20, 'Ai që dëshiron shpërblim në jetën tjetër – Na do t’ia shtojmë atë (shpërblimin) e asaj vepre, e ai që dëshiron shpërblim në këtë jetë, atij do t’i japim (diçka) prej saj, por ai nuk do të ketë pjesë në jetën tjetër.'),
(4293, 42, 21, 'Por ata, kanë shokë (udhëheqës të këqinj), të cilët u kanë përcaktuar (atyre) në fé aso dispozite, çfarë nuk ka lejuar Perëndia. Sikur të mos kishte qenë Fjala e vendimit të mëparshëm (që gjykimi do të shtyhet), ata madje do të ishin gjykuar. Me të vërtetë, për zullumqarët ka dënim të dhembshëm.'),
(4294, 42, 22, '(Në Ditën e Kijametit) do t’i shohish zullumqarët se si frikohen për atë që kanë punuar, e (shpërblimi i asaj pune, megjithatë) do t’i godet. E, ata që kanë besuar dhe punuar vepra të mira, do të jenë në kopshtet e mrekullueshme të xhenneteve. Ata do të kenë te Zoti i tyre çka të duan; kjo është dhuratë (shumë) e madhe.'),
(4295, 42, 23, 'Kjo (dhuratë e madhe) është ajo, me të cilën Perëndia i sihariqon robërit e Vet, (ata) që kanë besuar dhe që kanë bërë vepra të mira. Thuaju (atyre): “Për këtë nuk kërkoj kurrfarë shpërblimi tjetër prej jush, pos dashurisë ndaj meje – (për farefisin)” – E, kush bën vepër të mirë, Ne do t’ia shtojmë shpërblimin, se, Perëndia, me të vërtetë, është falës dhe mirënjohës.'),
(4296, 42, 24, 'Por, ata thonë: “Ai (Muhammedi), ka trilluar gënjeshtra për Perëndinë!” E, Perëndia, nëse don, zemrën tënde e bën të paluhatshme (me durim). Perëndia e shkatërron të pavërtetën (trillimin e bërë) dhe e forcon të vërtetën me fjalët e Veta. Ai di mirë çka ka në zemrat e tyre.'),
(4297, 42, 25, 'Ai e pranon pendimin e robërve të Vet dhe i falë mëkatet dhe i di të gjitha çka punoni ju;'),
(4298, 42, 26, 'Ai u përgjigjet (miraton) atyre që besojnë dhe bëjnë vepra të mira, e u jep edhe më tepër prej dhuntisë së Vet, kurse për mohuesit do të ketë dënim të ashpër.'),
(4299, 42, 27, 'Sikur Perëndia t’i furnizonte me sasi të mëdha robërit e Vet, ata do të tërboheshin në Tokë, por Ai u jep në atë masë sa dëshiron Ai, sepse, ai i njeh mirë dhe i sheh robërit e Vet.'),
(4300, 42, 28, 'Ai, shiun e dërgon pasi që ata ta kenë humbur shpresën dhe e përhapë mëshirën e Vet; Ai është mbrojtës i vërtetë dhe i vetmi, i denjë për falenderim.'),
(4301, 42, 29, 'Dhe, një prej argumenteve të Tij është krijimi i qiejve dhe i Tokës dhe i (të gjitha) qenieve të gjalla që i ka shpërndarë nëpër to; Ai është i pushtetshëm t’i tubojë ata kur të dojë.'),
(4302, 42, 30, 'Çfarëdo fatkeqësie që ju godet, kjo është për shkak se e keni futuar vetë, e Ai edhe falë shumë.'),
(4303, 42, 31, 'Dhe, ju nuk mund t’i shmangeni Atij në tokë dhe pos Perëndisë, nuk ka për ju (tjetër) mbrojtës as ndihmës.'),
(4304, 42, 32, 'Një prej argumenteve të Tij, janë anijet e mëdha si malet, që lundrojnë nëpër det.'),
(4305, 42, 33, 'Nëse Ai don, e qetëson (ndalon) erën – frymën dhe ata mbesin të palëvizshme në sipërfaqen e tij (detit). Për këtë ka argument për çdokënd që është i durueshëm dhe falenderues,'),
(4306, 42, 34, 'ose i fundosë ato, për shkak të mëkateve që i kanë bërë ata, e falë edhe shumë (mëkate),'),
(4307, 42, 35, 'e që ta dinë ata, të cilët polemizojnë me argumentet Tona, se nuk kanë ku t’i shmangen dënimit Tonë.'),
(4308, 42, 36, 'Çdo gjë që ju është dhënë juve, është vetëm kënaqësi në jetën e kësaj bote, e ajo që është te Perëndia (dhurata), është më e mirë dhe më e përhershme për ata që besojnë dhe mbështeten te Zoti i tyre;'),
(4309, 42, 37, 'për ata që ruhen prej mëkateve të mëdha dhe të këqiave dhe të cilët, kur t’i zemërojë dikush, falin;'),
(4310, 42, 38, 'për ata që i përgjigjen thirrjes së Zotit të tyre, dhe të cilët bëjnë namazin e këshillohen reciprokisht në punët e tyre, dhe të cilët ndajnë nga ajo që u kemi dhënë Ne,'),
(4311, 42, 39, 'dhe për ata të cilët kur i godet ndojë zullum (i vazhdueshëm), i rezistojnë (atij).'),
(4312, 42, 40, 'Dënimi i së keqes është gjegjës me atë të keqe, e kush falë dhe pajton, shpërblimi i tij është te Perëndia. Ai, në të vërtetë, nuk i don zullumqarët.'),
(4313, 42, 41, 'Ai që reziston (përballon), kur i është bërë një padrejtësi, nuk ka kurrfarë rruge qortimi ndaj tij,'),
(4314, 42, 42, 'e, ka rrugë qortimi, vetëm kundër atyre që u bëjnë zullum (shtypje) njerëzve dhe të cilët pa të drejtë mbjellin ngatërresa në Tokë; për ta ka dënim të dhembshëm.'),
(4315, 42, 43, 'E, ai që duron dh falë, këto, në të vërtetë, janë virtyte të larta (të rekomanduara).'),
(4316, 42, 44, 'Atë që Perëndia e shpie në humbje, për të nuk ka mbrojtës pas Tij. Dhe ti do të shohish se si zullumqarët, kur ta shohin dënimin, do të thonë: “A ekziston ndonjë rrugë për t’u kthyer?”'),
(4317, 42, 45, 'Dhe, i sheh ata që i ekspozohen zjarrit të përulur nga poshtërimi dhe vjedhin me bisht të syrit. Ata që kanë besuar thonë: “Me të vërtetë, në humbje të madhe janë ata që kanë humbur vetveten dhe familjen e tyre në Ditën e Kijametit”. A, me të vërtetë, zullumqarët janë në dënim të përhershëm!'),
(4318, 42, 46, 'Ata nuk do të kenë mborjtës, që do t’u ndihmojnë, përveç Perëndisë, e atë që Perëndia e shangë, për te s’ka kurrfarë rruge (shpëtimi).'),
(4319, 42, 47, 'Përgjigjuni thirrjes së Zotit tuaj, para se të vijë Dita, të cilën Perëndia nuk do ta ndalë. Atë Ditë, ju nuk do të keni kurrfarë strehimi dhe nuk do të mund të mohoni.'),
(4320, 42, 48, 'E, nëse ata kthehen (nuk të dëgjojnë), po Ne nuk të kemi dërguar ty si roje për ta. Detyra jote është vetëm të shpallësh. E, kur Ne i japim njeriut të shijojë mëshirën Tonë, ai i gëzohet asaj, e nëse e godet ndonjë e keqe për shkak të asaj që e kanë punuar duart e tija, atëherë, me të vërtetë, njeriu është mohues (i dhuntive).'),
(4321, 42, 49, 'Të Perëndisë janë pushteti mbi qiejt dhe Tokën. Ai krijon çka të dojë! Ai, i dhuron kujt të dojë vajza, e kujt të dojë i dhuron djem,'),
(4322, 42, 50, 'ose i dhuron dy gjinitë: djem e vajza, e kë të dojë e bënë steril. Ai, në të vërtetë, është i Gjithëdijshëm dhe i Pushtetshëm.'),
(4323, 42, 51, 'Asnjë njeriu nuk i është dhënë të flasë me Perëndinë, pos me anë të Shpalljes, pas perde, ose me dërgimin e Pejgamberit (engjëllit), e me lejen e Tij, të shpallë atë që don Ai (Perëndia). Ai, me të vërtetë, është i Lartësuar dhe i Gjithëdijshëm.'),
(4324, 42, 52, 'Po ashtu, Ne ta kemi dërguar ty Shpalljen. Ti (më parë) nuk ke ditur se çka është Libri as çka është besimi (i plotë), e (Librin – Kur’anin) Ne e bëmë dritë (udhërrëfyes), me të cilin udhëzojmë kënd të dojmë nga robërit Tonë. Ti (o Muhammed), me të vërtetë, udhëzon në rrugën e drejtë,'),
(4325, 42, 53, '(në) rrugën e Perëndisë, të cilit i përkasin çka ka në qiej dhe çka ka në Tokë. Dhe, ja, te Perëndia kthehen të gjitha gjërat!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'Hâ, Mîm.'),
(4327, 43, 2, 'Pasha Librin e qartë,'),
(4328, 43, 3, 'Na e kemi bërë (këtë libër) Kur’anin në gjuhën arabe, për ta kuptuar ju,'),
(4329, 43, 4, 'e ai (Kur’ani) është te Ne, në Librin kryesor (Levhi Mahfudh), i lartësuar dhe plotë dijeni.'),
(4330, 43, 5, 'Vallë, a të tërhiqemi Ne e të mos ju paralajmërojmë, ngase ju jeni popull ngatërrestar?'),
(4331, 43, 6, 'E, sa pejgamberë u dërguam Ne (popujve) të mëparshëm,'),
(4332, 43, 7, 'dhe nuk u ka ardhur atyre asnjë pejgamber e që të mos jenë tallur me te!'),
(4333, 43, 8, 'E, i kemi shkatërruar edhe ata që kanë qenë më të fuqishëm se këta (Mekkasit). Dhe më parë janë përmendur shembuj për popujt e lashtë.'),
(4334, 43, 9, 'Nëse ti i pyet ata: “Kush i ka krijuar qiejt dhe Tokën?” Ata me siguri do të thonë: “Ato i ka krijuar i Plotëfuqishmi dhe i Gjithëdijshmi,'),
(4335, 43, 10, 'Ai që iu ka bërë Tokën djep dhe u krijoi rrugë në të, për të arritur atje ku synoni.'),
(4336, 43, 11, 'Ai që prej qiellit lëshon ujë me masë, e që me të – Ne ngjallim tokën e vdekur. Kështu do të nxirreni edhe ju (prej varreve – të ringjallur);'),
(4337, 43, 12, 'Ai që krijoi çdo lloj krijese, dhe ka bërë anije e kafshë që t’i shaloni,'),
(4338, 43, 13, 't’i shaloni ato, e pastaj t’i kujtoni dhuntitë e Zotit tuaj (kur të shaloni në to) dhe të thoni: “Qoftë lavdëruar Ai që i nënshtroi këto për ne, se, na këto, vetë nuk do të mund t’i arrinim –'),
(4339, 43, 14, 'dhe ne, me siguri, do të kthehemi te Zoti ynë!”'),
(4340, 43, 15, 'E, ata i kanë përshkruar Atij pjesë prej robërve të Tij. Me të vërtetë, njeriu është mohues i hapët.'),
(4341, 43, 16, 'Vallë, a ng ato që krijon Ai, të marrë për Vete vajza, e ju t’ju dhorojë djem?'),
(4342, 43, 17, 'E, kur dikush nga ata sihariqohet me atë që i përshkruan Mëshiruesit – si ai (fëmijë – vajzë), i nxihet fytyra dhe bëhet i dëshpëruar.'),
(4343, 43, 18, 'Vallë, (Perëndisë i përshkruajnë) atë që rritet në stoli (vajza), e të cilat janë të pafuqishme për polemika?'),
(4344, 43, 19, 'Ata, engjëjt – të cilët janë robërit e Zotit, i konsiderojnë për femra. A mos vallë ata kanë qenë prezent gjatë krijimit të tyre?! Dëshmia e tyre do të shënohet dhe ata do të përgjigjen (për këtë shpifje)!'),
(4345, 43, 20, 'Dhe, ata thonë: “Sikur të kishte dashur Mëshiruesi, na nuk do t’i kishim adhuruar ata. Ata nuk dinë asgjë për këtë (çka flasin), ata vetëm gënjejnë.'),
(4346, 43, 21, 'Vallë, a u kemi dhënë Ne atyre Libër para tij (Kur’anit), e ata i janë përmbajtur atij?!'),
(4347, 43, 22, 'Jo, por ata thonë: “Na, me të vërtetë, i kemi gjetur etërit tanë në një rrugë – dhe, ne sigurisht udhëzohemi edhe nga gjurmët e tyre”.'),
(4348, 43, 23, 'Po ashtu, Ne në asnjë vend nuk kemi dërguar asnjë paralajmërues, para teje – e që pasanikët e korruptuar të mos thonin: “Na i kemi gjetur, me të vërtetë, etërit tanë në një rrugë, dhe me siguri, po ndjekim edhe ne gjurmët e tyre”.'),
(4349, 43, 24, '(Ai atyre) u tha: “Vallë, a nuk ju kam sjellë unë atë që më mirë e shënon rrugën e drejtë nga ajo që i keni gjetur te të parët tuaj (etërit)?” E, ata thanë: “Na, me të vërtetë, jemi mohues të asaj me të cilën jeni dërguar!”'),
(4350, 43, 25, 'Ne jemi hakmarrë (me dënim të merituar) ndaj atyre, e shiko pra, si ka qenë përfundimi i atyre që i konsideronin gënjeshtarë (pejgamberët).'),
(4351, 43, 26, '(Kujtoje ti o Muhammed!) kur Ibrahimi i tha të atit dhe popullit të tij: “Unë jam larg atyre që i adhuroni ju,'),
(4352, 43, 27, 'pos Atij, që më ka krijuar (mua) – dhe, me të vërtetë, Ai më udhëzon në rrugën e drejtë”,'),
(4353, 43, 28, 'dhe ai (Ibrahimi), e bëri këtë fjalë të përhershme te pasardhësit e tij, për t’u kthyer ata vazhdimisht në të Vërtetën.'),
(4354, 43, 29, 'Por Unë këta dhe etërit e tyre, i kam lënë në kënaqësi, përderisa u erdhi e Vërteta (Kur’ani) dhe Pejgamberi i qartë (me argumente),'),
(4355, 43, 30, 'e, tani, kur ju erdhi e Vërteta (Kur’ani), thanë: “Kjo është magji dhe na këtë nuk e besojmë!”'),
(4356, 43, 31, 'Dhe thanë (edhe këtë): “Sikur t’i kishte zbritur ky Kur’an ndonjë njeriu autoritativ prej njërit nga këto dy qytete (Mekkës dhe Taifës).'),
(4357, 43, 32, 'Vallë, a mos e ndajnë ata mëshirën e Perëndisë (profetninë)? Na, furnizimin e kësaj jete e kemi ndarë, në mes tyre dhe i ngritëm disa mbi të tjerët me shkallë (për nga pozita dhe malli), që disa t’u shërbejnë të tjerëve, e mëshira e Perëndisë është më e mirë nga ajo që grumbullojnë ata.'),
(4358, 43, 33, 'Dhe, sikur të mos bëheshin të gjithë njerëzit mohues, Ne do t’i bënim prej argjendtit kulmet e shtëpive të atyre që nuk besojnë në Mëshiruesin, e madje edhe shkallët nëpër të cilat ngjiten,'),
(4359, 43, 34, 'edhe dyert e shtëpive të tyre edhe kanapet (fotelet) në të cilat mbështeten,'),
(4360, 43, 35, 'dhe stoli të llojllojshme, se, të gjitha këto janë vetëm kënaqësi të kësaj jete, e jeta tjetër te Zoti yt është për ata që ruhen (kundërshtimit të Perëndisë).'),
(4361, 43, 36, 'Kush shmanget nga Këshilla (Kur’ani) e Mëshiruesit të Githëmbarshëm, Na do t’ia ngarkojmë djallin dhe ai do t’i bëhet shok atij.'),
(4362, 43, 37, 'Edhe, ata (djajtë), me të vërtetë, i shmangin ata nga rruga e drejtë, e ata (të mashtruarit) mendojnë se janë në rrugë të drejtë.'),
(4363, 43, 38, 'Dhe, kur të vijnë te Ne, do të thonë (të mashtruarit): “Ah! sikur të kishte qenë largësia në mes meje dhe teje (djallit) sa mes lindjes dhe perëndimit! Sa shok i keq ke qenë ti!”'),
(4364, 43, 39, 'Dhe, nuk do t’u bënë dobi juve sot (Ditën e Kijametit), meqë keni bërë zullum, me të vërtetë ju jeni shokë në dënim.'),
(4365, 43, 40, 'Vallë, a mos ti do të bësh të dëgjojnë të shurdhërit dhe t’i udhëzosh të verbërit dhe ata që janë në humbje të qartë?'),
(4366, 43, 41, 'Nëse të largojmë ty (para se ta shohësh dënimin e tyre), Na, me siguri do t’i dënojmë ata,'),
(4367, 43, 42, 'ose, nëse dëshirojmë të të tregojmë atë çka u premtojmë – atyre, po Ne, me të vërtetë, jemi të Plotëfuqishëm mbi ta.'),
(4368, 43, 43, 'Andaj, përmbaju asaj ë të është shpallë ty, sepse, ti, me të vërtetë, je në rrugën e drejtë.'),
(4369, 43, 44, 'Kur’ani me të vërtetë, është nderë për ty dhe popullin tënd; e ju do të pyeteni!'),
(4370, 43, 45, 'Pyeti pejgamberët Tanë, të cilët i kemi dërguar para teje, a mos kemi urdhëruar Ne që të adhurohen zotëra (tjerë), pos Mëshiruesit të Gjithmbarshëm?!'),
(4371, 43, 46, 'Na, me të vërtetë, ia kemi dërguar Musain me argumentet Tona – Faraonit dhe parisë së tij, dhe, ai (u) tha: “Unë jam, me të vërtetë, pejgamber i Zotit të gjithësisë!”'),
(4372, 43, 47, 'E, kur ai, u solli atyre argumentet Tona, ata filluan t’i përqeshin ato.'),
(4373, 43, 48, 'Dhe, Ne, nuk u kemi treguar atyre asnjë argument, e që ai të mos jetë më i madh se tjetri, dhe Ne i kemi dënuar ata me ndëshkim, për t’u kthyer (në të drejtë).'),
(4374, 43, 49, 'Ata i thanë (Musait): “O magjistar! Lutju Zotit tënd për ne, ashtu si të ka premtuar Ai ty; na, me siguri, do të shkojmë rrugës së drejtë (do të besojmë)!”'),
(4375, 43, 50, 'E, posa i liruam prej vuajtjeve, ata e shkelën premtimin.'),
(4376, 43, 51, 'Dhe, Faraoni nisi të thërrasë popullin e vet: “O populli im! Vallë, a nuk më përket mua sundimi i Misirit (Egjiptit) dhe këto degë të lumit që po rrjedhin pranë meje; a nuk shihni?'),
(4377, 43, 52, 'E, vallë, a nuk jam unë më i mirë se ky i mjerë (i gjorë) dhe i cili mezi mund të shprehet?'),
(4378, 43, 53, 'Sikur t’iu kishin vënë atij bylyzykët e florinjtë, ose sikur të kishin ardhur bashkë me të (pranë) engjëjt?”'),
(4379, 43, 54, 'Dhe, ai e mashtroi popullin e vet, e iu nënshtruan atij; ata, me të vërtetë, ishin popull ngatërrestar.'),
(4380, 43, 55, 'E kur Na zemruan, Na i dëbuam ata dhe i fundosëm të gjithë,'),
(4381, 43, 56, 'dhe i bëmë shembull dhe mësim për popujt e mëvonshëm.'),
(4382, 43, 57, 'Kur u muar si shembull djali i Merjemit (Isai), populli yt (Kurejshit) menjëherë nisën të bërtasin (prej gëzimit)'),
(4383, 43, 58, 'dhe, thanë: “A janë më të mirë idhujt tanë, apo ai (Isai)? Ata, ta paraqesin këtë shembull vetëm për shkak se duan të polemizojnë, sepse, ata janë popull grindavec.'),
(4384, 43, 59, 'Ai (Isai), është vetëm rob (njeri), të cilin Ne e kemi dhuruar (me profetni), dhe e kemi bërë shembull për bijtë e Israelit,'),
(4385, 43, 60, 'e, sikur të duam, Ne në vend të jush do të bënim engjëj, që do t’ju trashëgonin në Tokë.'),
(4386, 43, 61, 'Me të vërtetë, ai (Isai), është shenjë e Çastit (Ditës së Kijametit), ardhjen e të cilit mos e dyshoni kurrsesi. Ndiqeni (rrugën time); kjo është rruga e drejtë!'),
(4387, 43, 62, 'Le të mos ju shmangë kurrsesi djalli! Ai, me të vërtetë, është armiku juaj i hapët.'),
(4388, 43, 63, 'E, kur Isai i solli argumentet e qarta, tha: “Ju kam sjellë dijeni të mrekullueshme dhe do të ju shpjegoj disa gjëra, me të cilat nuk po pajtoheni. Andaj, druajuni Perëndisë dhe bëhuni të dëgjueshëm ndaj meje!'),
(4389, 43, 64, 'Me të vërtetë, Perëndia është Zoti im dhe Zoti juaj, andaj adhuronie Atë. Kjo është rrugë e drejtë!”'),
(4390, 43, 65, 'Grupet e ndryshme u grindën në mes tyre. Mjerë ata që kanë bërë zullum – nga dënimi i Ditës së dhembshme!'),
(4391, 43, 66, 'A mos ata presin që t’u vie (diçka tjetër), pos Çastit, papritms, e ata të mos e ndjejnë atë.'),
(4392, 43, 67, 'Atë Ditë – miqtë do të bëhen armiq të njëri-tjetrit, pos atyre që i janë druajtur Perëndisë:'),
(4393, 43, 68, '“O robërit e Mi, për ju sot nuk ka frikë, as që do të pikëlloheni;'),
(4394, 43, 69, 'ata që kanë besuar dokumentet Tona dhe kanë qenë të dëgjueshëm ndaj Nesh –'),
(4395, 43, 70, '(u thuhet): hyni në xhennet të gëzuar, ju dhe bashkëshortet tuaja”.'),
(4396, 43, 71, 'Ata do të jenë të shërbyer me pjata dhe gota prej ari; aty (në xhennet) do të ketë çka t’u dojë shpirti, dhe me çka kënaqet syri, në te do të qëndrojnë përgjithmonë.'),
(4397, 43, 72, 'Ja, ky është xhenneti që u është dhënë në trashëgim për atë që keni punuar,'),
(4398, 43, 73, 'në të, do të keni pemë të shumëllojshme, disa prej të cilave do t’i hani”.'),
(4399, 43, 74, 'E, mohuesit, me të vërtetë, do të qëndrojnë përherë në dënimin e xhehennemit,'),
(4400, 43, 75, 'ai (dënimi) nuk do t’u lehtësohet atyre kurrfarë shprese për shpëtim nuk do të kenë (duke u vënë në heshtje).'),
(4401, 43, 76, 'Ne, atyre nuk u kemi bërë padrejtësi, por ata kanë qenë të padrejtë (dhe zullumqarë të vetvetes).'),
(4402, 43, 77, 'Ata (atëherë) do të thërrasin: “O Malik (engjëlli i mbikëqyrjes së zjarrit)! Le të na gjykojë Zoti yt me shkatërrim (vdekje)!” E ai do të thotë: “Ju, do të qëndroni përherë aty!”'),
(4403, 43, 78, 'Na ju kemi sjellë të Vërtetën (Shpalljen), por shumica prej jush, e urren të Vërtetën.'),
(4404, 43, 79, 'Nëse ata (mohuesit) kurdisin kurtha, edhe Ne do t’u vëmë kurtha (në dënim).'),
(4405, 43, 80, 'Vallë, a mendojnë ata se Ne nuk dëgjojmë çka flasin ata tinëzisht dhe haptas në mes tyre? Përkundrazi, Na dëgjojmë, e të dërguarit Tanë (engjëjt), që janë pranë tyre, shënojnë.'),
(4406, 43, 81, 'Thuaju (atyre o Muhammed): “Sikur Bamirësi i Gjithmbarshëm të kishte fëmijë, unë i pari do ta adhuroja!'),
(4407, 43, 82, 'Qoftë i lartësuar Zoti i qiejve dhe i Tokës, Zoti i Arshit! Nga ajo çka i përshkruajnë Atij!'),
(4408, 43, 83, 'Lëni ata le të zhyten (në të këqia) dhe le të argëtohen derisa të takohen në ditën që u është premtuar (Dita e Kijametit).'),
(4409, 43, 84, 'Dhe Ai është Zot në qiell dhe Zot në Tokë. Ai është i Gjithëdijshëm dhe i Plotëdijshëm!'),
(4410, 43, 85, 'Dhe, qoftë i lartësuar Ai, të cilit i përket pushteti i qiejve dhe i Tokës dhe çka gjendet mes tyre. Vetëm tek Ai është dijania mbi Çastin dhe tek Ai do të ktheheni!'),
(4411, 43, 86, 'Ata që adhurojnë tjetër (zot), pos Perëndisë, - nuk do të mund të angazhohen për kënd, munden vetëm ata që e pranojën të Vërtetën, dhe ata këtë e dinë (që është vetëm një Zot).'),
(4412, 43, 87, 'E, nëse i pyet ata kush i ka krijuar, ata me siguri do të thonë: “Perëndia!” E, nga po ia mbajnë atëherë?'),
(4413, 43, 88, 'E, fjala e tij (Muhammedit), është: “O Zoti im, me të vërtetë, ky është popull që nuk beson.'),
(4414, 43, 89, '(O Muhammed!) largohu prej tyre dhe thuaju: “Selam!” (Lamtumirë), e ata së shpejti do ta dinë (gjendjen)!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'Hâ, Mîm.'),
(4416, 44, 2, 'Pasha Librin e qartë!'),
(4417, 44, 3, 'Na, me të vërtetë, e kemi shpallur (Kur’anin) në natë të shenjtë, dhe Ne, me të vërtetë, jemi paralajmërues.'),
(4418, 44, 4, 'Në atë (natë) zgjidhet çdo punë e gjykueshme (e paluhatshme),'),
(4419, 44, 5, 'me urdhërin Tonë! Na, me të vërtetë, kemi dërguar pejgamberë,'),
(4420, 44, 6, 'mëshirë prej Zotit tënd, - me të vërtetë, Ai i dëgjon të gjitha dhe i di të gjitha,'),
(4421, 44, 7, 'Zotit të qiejve dhe të Tokës dhe çka gjendet në mes tyre, nëse besoni me këmbëngulje.'),
(4422, 44, 8, 'S’ka zot tjetër pos Atij. Jeta dhe vdekja janë vepër e Tij. Ai është Zoti juaj dhe Zoti i të parëve tuaj të lashtë!'),
(4423, 44, 9, 'Por, at dyshojnë dhe argëtohen,'),
(4424, 44, 10, 'andaj ti prite atë (ditë), kur nga qielli do të lëshohet tym i qartë,'),
(4425, 44, 11, 'që do t’i mbulojë njerëzit, (e u thuhet atyre): “Ky është dënimi i dhembshëm!”'),
(4426, 44, 12, '(mohuesit) atëherë, do të thonë: “O Zoti ynë, lirona prej dënimit, na, me siguri do të besojmë!'),
(4427, 44, 13, 'E, prej nga ata do të marrin këshilla, e madje u pat ardhur atyre Pejgamberi (me argumente) të qarta?'),
(4428, 44, 14, 'E u shmangën prej tij dhe thanë: “(Ai është) i mësuar (prej tjetërkujt) – i çmendur (prej xhindëve)”.'),
(4429, 44, 15, 'Na, do t’i lirojmë ata nga dënimi pak (kohë), por ju, me siguri, do të ktheheni (në mohim)”.'),
(4430, 44, 16, '(Kujtoju) ditën kur do t’i kapim ata, me një fuqi të madhe – Na, me të vërtetë, do t’i dënojmë.'),
(4431, 44, 17, 'Me të vërtetë, Na qysh më parë e kemi vënë në sprovë popullin e Faraonit, e atyre u pat ardhur pejgamberi fisnik.'),
(4432, 44, 18, '(Duke u thënë): “Plotësoni ndaj meje atë që jeni të obliguar, o robërit e Perëndisë, sepse, unë jam për ju pejgamber i besuar;'),
(4433, 44, 19, 'dhe mos u madhëroni (mos u ngritni) mbi Perëndinë; unë, me të vërtetë, po ju sjell (prej Tij) dokument të qartë,'),
(4434, 44, 20, 'dhe unë mbështetem në Zotin tim dhe Zotin tuaj (për të më mbrojtur), që të mos më mbytni me gurë,'),
(4435, 44, 21, 'e, nëse nuk më besoni, atëherë (bile) largohuni prej meje!'),
(4436, 44, 22, '(Nuk dëgjuan ata) dhe ai e thirri Zotin e tij: “Me të vërtetë, ky është popull mëkatarë!”'),
(4437, 44, 23, '(Perëndia, atij i tha): “Ec me robërit e Mi natën, meqë do t’ju ndjekin,'),
(4438, 44, 24, 'dhe lëre ti detin e hapur, se ata, me të vërtetë, do të janë ushtri e fundosur”.'),
(4439, 44, 25, 'E, sa e sa kopshtije e burime i lanë ata,'),
(4440, 44, 26, 'ara të mbjella dhe pallate të mrekullueshme,'),
(4441, 44, 27, 'dhuntitë, në të cilat kënaqeshin,'),
(4442, 44, 28, '(ajo ka qenë) kështu, dhe Ne u kemi dhënë trashëgim ato (dhuntitë e tyre) popullit tjetër –'),
(4443, 44, 29, 'Nuk i vajtoi ata as qielli as Toka, nuk u lanë ata (për t’u dënuar në një kohë tjetër),'),
(4444, 44, 30, 'e Na, me të vërtetë, i shpëtuam bijtë e Israelit nga dënimi poshtërues,'),
(4445, 44, 31, 'prej Faraonit, - ai ishte mendjemadh, njeri që për keq kalonte çdo kufi –'),
(4446, 44, 32, 'dhe, në të vërtetë, i kemi zgjedhur ata, duke i ditur (rrethanat e tyre) mbi popujt tjerë (të asaj kohe),'),
(4447, 44, 33, 'dhe u kemi dhënë argumente, në të cilat ka sprova të qarta.'),
(4448, 44, 34, 'E, këta (Kurejshit), në të vërtetë, thonin:'),
(4449, 44, 35, '“Ekziston vetëm vdekja jonë e parë, na nuk do të ringjallemi (më);'),
(4450, 44, 36, 'na i sjellni pra (ringjallna) të parët tanë, nëse e thoni të vërtetën!”'),
(4451, 44, 37, 'A janë këta më të mirë apo populli i Tubbas, apo ata para tyre (Tubbas)? – Ata, Na i kemi shkatërruar, meqë, me të vërtetë, ishin popull mëkatarë.'),
(4452, 44, 38, 'Na, nuk i kemi krijuar qiejt dhe Tokën, as atë që gjendet në mes tyre, për t’u zbavitur (pa qëllim),'),
(4453, 44, 39, 'Na i kemi krijur ato me qëllim të plotë, por shumica e tyre nuk e dinë.'),
(4454, 44, 40, 'Me të vërtetë, Dita e dallimit (të së mirës nga e keqja), është e caktuar për të gjithë,'),
(4455, 44, 41, 'Dita kur i afërmi nuk mund t’i ndihmojë asgjë të afërmit, as që atyre do t’u ndihmojë kush,'),
(4456, 44, 42, 'pos ata që i ka mëshiruar Perëndia, sepse Ai, në të vërtetë, është i Plotëfuqishëm dhe Mëshirues.'),
(4457, 44, 43, 'Me të vërtetë, druri i Zekkumit (një lloj druri tepër i idhët)'),
(4458, 44, 44, 'do të jetë ushqim i mëkatarit,'),
(4459, 44, 45, 'në barkun e tyre vëlon sikur xehja e shkrirë,'),
(4460, 44, 46, 'ashtu si vëlon uji i nxehtë.'),
(4461, 44, 47, '(engjëjve u thuhet): “Rrëmbejeni atë dhe shpieni në mes të xhehennemit,'),
(4462, 44, 48, 'e, pastaj, për dënim, hedhni mbi kokën e tyre ujë të vluar!”'),
(4463, 44, 49, '(I thuhet atij): “Shijoje, (dënimin), meqë ti je, “i fuqishëm” dhe “fisnik” (me hamendjen tënde),'),
(4464, 44, 50, 'kjo është, me të vërtetë, ajo në të cilën keni dyshuar ju!”'),
(4465, 44, 51, 'E, ata që i druajnë Perëndisë, me të vërtetë, do të jenë në vend të sigurt,'),
(4466, 44, 52, 'në mes kopshtijeve dhe burimeve,'),
(4467, 44, 53, 'të veshur me petka (rroba) mëndafshi të hollë dhe të trashë, duke ndejtur të ulur njëri karshi tjetrit.'),
(4468, 44, 54, 'Kështu (do të jenë të shpërblyer), dhe Ne do t’i martojmë ata me hyri, sy bukur.'),
(4469, 44, 55, 'Aty do të kërkojnë çfarë pemësh dëshirojnë, e do të kënaqen (duke qenë të sigurt);'),
(4470, 44, 56, 'në to (xhennete), nuk do ta shijojnë vdekjen, pos vdekjes së parë (në këtë botë) dhe i ruan (Perëndia) prej dënimit të xhehennemit,'),
(4471, 44, 57, 'kjo do të jetë dhutni prej Zotit tënd. Ky, me të vërtetë, do të jetë sukses i madh!'),
(4472, 44, 58, 'Na, i kemi bërë të lehtë atë (Kur’anin për ta kuptuar) në gjuhën tënde, për t’u këshilluar ata,'),
(4473, 44, 59, 'andaj prit ti (të shohësh çka do të bëhet me ta), por edhe ata presin (çka do të bëhet me ty)!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'Hâ, Mîm.'),
(4475, 45, 2, '(Kjo është) Shpallja e Librit të Perëndisë, të Plotëfuqishëm dhe të Gjithëdijshëm!'),
(4476, 45, 3, 'Me të vërtetë, në qiej dhe në Tokë ekzistojnë dokumente për ata që besojnë,'),
(4477, 45, 4, 'edhe në krijimin tuaj dhe të shtazëve, të shpërndara (nëpër Tokë), ka argument për popullin që beson me këmbëngulje;'),
(4478, 45, 5, 'edhe në ndërrimin e natës dhe të ditës, dhe në shiun që e lëshon Perëndia prej qielli e që me të e gjallëron tokën, pas thatësisë së saj, edhe ndërrimit të erërave, ka argumente për njerëzit që mendojnë.'),
(4479, 45, 6, 'Këto janë argumentet e Perëndisë, të cilat Ne ty t’i tregojmë me tërë të vërtetën, e në çfarë fjale, pos Allahut (Kur’anit) dhe argumenteve të Tij, do të besojnë ata?!'),
(4480, 45, 7, 'Mjerë për çdo gënjeshtar, mëkatar (të madh)!'),
(4481, 45, 8, '(i cili) dëgjon argumentet e Perëndisë që i lexohen, e përsëri është mendjemadh (e duke mos pranuar), thuajse nuk i ka dëgjuar ato, sihariqoje atë me dënim të dhembshëm!'),
(4482, 45, 9, 'E, kur të njohtohet me diçka nga ajetet Tona, ai ato – i përqeshë. Për të tillët do të ketë dënim të poshtëruar.'),
(4483, 45, 10, 'Para tyre është xhehennemi, dhe nuk do t’ju ndihmojë asgjë ng ato që kanë fituar, as ata (idhujtë) që i kanë marrë, pos Perëndisë, për mbrojtës! Ata i pret mundim i madh.'),
(4484, 45, 11, 'Ky është (Kur’ani) udhërrëfyes në rrugën e drejtë, e ata që nuk besojnë argumentet e Zotit të tyre, për ta ka mundim të madh, të dhembshëm.'),
(4485, 45, 12, 'Perëndia është Ai që ju ka nënshtruar juve detin, që, nëpë të me urdhërin e Tij, të lundrojnë anijet, për t’i kërkur dhuntitë e Tij dhe për të qenë ju mirënjohës.'),
(4486, 45, 13, 'Dhe ju ka nënshtruar juve çka gjendet në qiej dhe në Tokë, të gjitha janë prej Tij. Me të vërtetë, në këto ka këshilla për popullin që mendon.'),
(4487, 45, 14, 'Thuaju (o Muhammed!) besimtarëve që t’i falin ata që nuk shpresojnë në ditët e Perëndisë, në të cilën njerëzit do të shpërblehen sipas veprave të tyre.'),
(4488, 45, 15, 'Kush bën mirë, e ka për vete (dobi), e kush bën keq – bënë kundër vetes, e në fund do të ktheheni (të gjithë) te Zoti juaj.'),
(4489, 45, 16, 'Ne, me të vërtetë, u kemi dhënë bijve të Israelit Librin, pushtetin dhe profetninë dhe i kemi furnizuar me gjëra të bukura, dhe i kemi ngritur at ambi të gjithë popujt tjerë (të asaj kohe),'),
(4490, 45, 17, 'dhe, u kemi dhënë argumente të qarta mbi besimin, e ata u përçanë, mu atëherë, kur u erdhi dijenia, pasiqë patën armiqësi në mes tyre. Zoti yt, me siguri, do t’i gjykojë (ata) në Ditën e Kijametit për atë ë ranë në konflikt.'),
(4491, 45, 18, 'E, pastaj, Ne ty ta kemi bërë një rrugë të drejtë (të mrekullueshme) të fesë, andaj ndiqe atë dhe mos shko pas kënaqësive të atyre që nuk dinë!'),
(4492, 45, 19, 'Me të vërtetë, ata nuk do të mund të ndihmojnë (ty) asgjë te Perëndia. Padyshim, zullumqarët i ndihmojnë njëri-tjetrit, e Perëndia i mbron ata që i druajnë Atij dhe ruhen nga mëkatet.'),
(4493, 45, 20, 'Ky Kur’an është argument i qartë (dritë e shpëtimit) udhërrëfyes dhe mëshirë për njerëzit që besojnë paluhatshëm.'),
(4494, 45, 21, 'A mendojnë ata që bëjnë vepra të këqia se, Ne, do t’i bëjmë të njëjtë me ata që kanë besuar dhe që kanë bërë vepra të mira, se jeta dhe vdekja e tyre do të jetë e njëjtë? Sa gjykim i keq që është ky!'),
(4495, 45, 22, 'E, Perëndia, krijoi qiej dhe Tokë me qëllim të plotë, në mënyrë që çdo njeri të jetë i shpërblyer (apo i dënuar) për atë që ka punuar. E, askujt nuk do t’i bëhet padrejtësi.'),
(4496, 45, 23, 'A e ke parë atë, që kënaqësinë e vet e ka marrë për zot, dhe Perëndia, e ka lënë në humbje duke e ditur (të keqen e tij) dhe ia ka vulosur të dëgjuarit e tij dhe zemrën e tij, e para syve të tij ka vënë perde? Kush do ta udhëzojë atë, pos Perëndisë? A nuk po kuptoni?'),
(4497, 45, 24, 'E, ata thonë: “Ekziston vetëm jeta e kësaj bote, vdesim (shumë si fëmijë) dhe jetojmë (një kohë), e vetëm koha na shkatërron”. Ata, për këtë, nuk kanë kurrfarë dijenie, ata, vetëm parafytyrojnë ashtu.'),
(4498, 45, 25, 'E, kur t’ju tregohen atyre ajetet Tona të qarta, ata nuk kanë tjetër shprehje, pos fjalës së tyre: “Ringjallni të parët tanë, nëse e thoni të vërtetën!”'),
(4499, 45, 26, 'Thuaju (atyre): “Perëndia ju ngjallë (u jep jetën), pastaj ju vdes, e mandej do t’ju tubojë në Ditën e Kijametit, në këtë nuk ka kurrfarë dyshimi, por (këtë) shumica e njerëzve nuk e dinë”.'),
(4500, 45, 27, 'Të Perëndisë janë pushteti i qiejve dhe i Tokës! E, kur të arrijë Dita e Kijametit, atë ditë të këqinjtë do të humbin.'),
(4501, 45, 28, 'Dhe, të gjithë popujt do t’i shohësh të gjunjëzuar; çdo popull do të thirret në Librin e vet: “Sot do të shpërbleheni për atë që keni bërë.'),
(4502, 45, 29, 'Ky është Libri Ynë që do t’ju tregojë të vërtetën. Na, me të vërtetë, i kemi urdhëruar (engjëjt) që të shkruhen (shënohen) të gjitha ç’keni bërë”.'),
(4503, 45, 30, 'E, sa i përket atyre që kanë besuar dhe kanë bërë vepra të mira, ata, do t’i shpie Zoti i tyre në mëshirën e Vet (xhennet). Ky është shpëtim i qartë!'),
(4504, 45, 31, 'E, sa i përket mohuesve, (atyre do t’ju thuhet): “Vallë, a nuk u janë lexuar ajetet e Mia, por ju u mendjemadhëruat dhe u bëtë popull mëkatarë”.'),
(4505, 45, 32, 'Kur të thuhet: “Me të vërtetë, premtimi i Perëndisë është i sigurt, dhe se për Ditën e Kijametit s’ka kurrfarë dyshimi! – ju thatë: “Na nuk dimë çka është Dita e Kijametit, na për këtë kemi vetëm një parafytyrim, na nuk jemi të bindur (për këtë).”'),
(4506, 45, 33, 'Dhe, atyre do t’u dalin në shesh të këqit që i kanë bërë, e do t’i kaplojë dënimi për atë që talleshin,'),
(4507, 45, 34, 'dhe, (atyre) u thuhet: “Ne sot po ju qesim në harresë, ashtu siç e keni lënë ju në harresë takimin e kësaj dite. Vendqëndrimi juaj është zjarri dhe, për ju nuk ka kurrfarë ndihmësi.'),
(4508, 45, 35, 'Ky dënim është ngase i keni përqeshur argumentet e Perëndisë, e jeta e kësaj bote ju ka mashtruar. Sot, ata nuk do të nxirren nga zjarri, as që do të kërkohet prej tyre arsyetimi (nuk do të përfillet).'),
(4509, 45, 36, 'Falenderimi i përket vetëm Perëndisë. Zotit të qiejve dhe Zotit të Tokës – Zotit të gjithësisë.'),
(4510, 45, 37, 'Atij i përket tërë madhështia në qiej dhe në Tokë. Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'Hâ, Mîm.'),
(4512, 46, 2, '(Kjo është) Shpallja e Librit prej Perëndisë, të Plotëfuqishëm dhe të Gjithëdijshëm!'),
(4513, 46, 3, 'Ne, kemi krijuar qiejt dhe Tokën dhe çka gjendet në mes tyre – me qëllim të plotë dhe me afat të caktuar, por mohuesit shmagen nga ajo që u është paralajmëruar.'),
(4514, 46, 4, 'Thuaju (atyre): “Më tregoni ju për ata që i adhuroni, pos Perëndisë? Më dëftoni ju se çka kanë krijuar ata në Tokë, apo a kanë ndonjë pjesëmarrje ata në (krijimin) e qiejve? Ma sillni Librin e shpallur para këtij (Kur’anit) apo vetëm ndonjë gjurmë nga dijenia, nëse jeni të vërtetë”.'),
(4515, 46, 5, 'Kush është më i humbur se ata, të cilët, pos Perëndisë, u luten atyre të cilët nuk mund t’u përgjigjen deri në Ditën e Kijametit, e ata janë të pakujdesshëm ndaj lutjes së tyre.'),
(4516, 46, 6, 'E, kur të ringjallen njerëzit, ata (të adhuruarit, pos Perëndisë), do të jenë armiq të tyre (adhuruesve) dhe do ta mohojnë adhurimin e tyre.'),
(4517, 46, 7, 'E, kur t’u lexohen ajetet Tona të qarta, mohuesit thonë për të Vërtetën – posa të dëgjojnë: “Kjo është magji e qartë!”'),
(4518, 46, 8, 'Ose ata thonë: “Ai (Muhammedi) e trilloi atë (Kur’anin)”. Thuaju (atyre): “Por, nëse unë e trilloj atë, ju nuk do të mund të më ndihmoni asgjë nga dënimi i Perëndisë. Ai di më së miri përse zhyteni në trillime për të (Kur’anin). Mjafton Ai dëshmitar mes meje dhe jush. Ai është Falës dhe Mëshirues”.'),
(4519, 46, 9, 'Thuaju (atyre): “Unë nuk jam i pari profet (i ardhur në këtë Botë) dhe unë nuk di çka do të ndodhë me mua ose me ju. Unë ndjek vetëm atë që më është shpallë dhe unë jam vetëm paralajmërues i qartë”.'),
(4520, 46, 10, 'Thuaju (atyre): “Më thuani ju mua se çka do të bëhet me ju nëse Kur’ani është prej Perëndisë, e ju e mohoni, e ka dëshmuar (për të) dikush prej bijve të Israelit në shembullin e tij e besoi, kurse ju u bëtë mendjemëdhenj (nuk besuat)!” Me të vërtetë, Perëndia, nuk e udhëzon (në rrugë të drejtë) popullin zullumqarë.'),
(4521, 46, 11, 'Dhe, mohuesit thonë për besimtarët: “Sikur të ishte ndonjë e mirë (besimi në Kur’an), ata nuk do të na kishin tejkaluar në këtë”. E, ngase ata nuk u udhëzuan me te në rrugën e drejtë, me siguri do të thonë: “Ky është trillim i lashtë”.'),
(4522, 46, 12, 'E, para Kur’anit ka ekzistuar Libri i Musait – udhërrëfyes dhe mëshirë. E, ky (Kur’ani), Libër vërtetues (i librave të shpallur) në gjuhën arabe për t’i paralajmëruar ata që bëjnë zullum dhe për t’i sihariquar bamirësit.'),
(4523, 46, 13, 'Me siguri, ata që thonë: “Zoti ynë është Perëndia! – dhe pastaj qëndrojnë në rrugën e drejtë, për ta nuk ka kurrfarë droje dhe as që do të pikëllohen.'),
(4524, 46, 14, 'Ata janë banorë të xhennetit, në të do të qëndrojnë përherë, dhe ky do të t’u jetë shpërblim për atë që kanë punuar.'),
(4525, 46, 15, 'Njeriut i kemi rekomanduar mirësi (sjellje të mira) ndaj prindërve të vet. Nëna e ka mbajtur me mund dhe lindur me mund atë, e mbajtja dhe të dhënit gji është tridhjetë muaj, e kur të arrijë në moshën e pjekurisë dhe t’i mbushë dyzet vjet, ai thotë: “O Zoti im, më nxit që të falenderohem me dhuntinë Tënd me të cilën më ke dhuruar mua dhe prindërit e mi, dhe, që të bëj vepra të mira, me të cilët Ti do të jesh i kënaqur, dhe bëni të mirë pasardhësit e mi; unë me të vërtetë, pendohem (kthehem) te Ti dhe unë jam musliman”.'),
(4526, 46, 16, 'Ata janë të atillë, prej të cilëve Ne i pranojmë veprat e mira të tyre, e i kapërcejmë veprat e tyre të këqia (nuk i dënojmë për to); ata do të jenë banorë të xhennetit; ky është premtimi i vërtetë, që u është premtuar.'),
(4527, 46, 17, 'E, ai që u ka thënë prindërve të vet: “Uf ju! A po më premtoni se do të ringjallem kur para meje kanë kaluar shumë gjenerata (e nuk janë ringjallë)!” – e ata (prindërit) i drejtohen Perëndisë, për ndihmë (duke i thënë pasardhësit): “Kuku për ty (nëse nuk pendohesh)! Beso, me të vërtetë, premtimi i Perëndisë është i sigurt! Ai (pasardhësi) përgjigjet: “Këto janë vetëm përralla të lashta!”'),
(4528, 46, 18, 'Ata janë prej atyre, ndaj të cilëve është plotësuar Fjala (e dënimit) mbi popujt: xhindët dhe njerëzit që kanë qenë para tyre, sepse ata, me të vërtetë, janë shkatërruar.'),
(4529, 46, 19, 'Për të gjithë do të ketë shkallë të caktuara, për atë që kanë punuar, për t’i shpërblyer (dhe dënuar) sipas veprave të tyre, - e nuk do t’ju bëhet atyre padrejtësi.'),
(4530, 46, 20, '(Kujtoju) Ditën, kur ata që kanë mohuar i ekspozohen zjarrit (e u thuhet atyre): “Ju keni shfrytëzuar kënaqësitë tuaja në jetën tuaj në tokë dhe jeni kënaqur me to, e sot do të ndëshkoheni me dënim të poshtëruar, ngase jeni madhështuar në Tokë pa kurrfarë të drejte dhe ngase keni bërë ngatërresa”.'),
(4531, 46, 21, 'Kujtoje vëllain e Adit (Hudin), kur e paralajmëroi popullin e vet në Ahkaf, e ka pasur paralajmërues edhe para tij edhe pas tij (kur u tha atyre): “Adhuronie vetëm Perëndinë! Unë, me të vërtetë, druaj se do t’ju godet dënimi i Ditës së Madhe! –'),
(4532, 46, 22, 'ata u përgjegjën: “Vallë, a ke ardhur të na shmangësh neve prej hyjnive tona. Sillna atë (dënim) që po na premton, nëse thua të vërtetën!” -'),
(4533, 46, 23, 'Ai u tha (atyre): “Vetëm te Perëndia është dijenia e (dënimit), e unë vetëm u lajmëroj juve me atë që jam dërguar, por unë po shoh se jeni popull i zhytur në injorancë”.'),
(4534, 46, 24, 'E, kur e panë atë (rénë – dënimin) që u duk dhe që vinte në drejtim të luginave të tyre, thanë: “Kjo ré na sjell shiun neve!” – (E, Hudi tha): “Jo, kjo është ajo që ju e keni shpejtuar: frymë në të cilën ka dënim të dhembshëm,'),
(4535, 46, 25, 'e cila, me urdhërin e Perëndisë, shkatërron çdo gjë”. E, në mëngjes u panë vetëm shtëpiat e tyre të zbrazëta; kështu Ne e ndëshkojmë popullin mëkatar.'),
(4536, 46, 26, 'Ne, i kemi vendosur ata në aso gjendje (të volitshme), në çfarë nuk u kemi vendosur juve (o Mekkas) dhe u kemi dhënë atyre të dëgjuarit, të pamurit dhe zemrën (mendjen), por as të dëgjuarit e tyre, as të pamurit e tyre, as mendja e tyre, nuk u kanë sjellë kurrfarë dobie, ngase mohuan argumentet e Perëndisë, e ata i goditi ajo me të cilën talleshin.'),
(4537, 46, 27, 'Me të vërtetë, Ne i kemi shkatërruar vendbanimet për rreth jush, e madje edhe u kemi shpjeguar atyre, (në mënyra ta ndryshme) argumentet Tona, për t’u kthyer ata (prej mohimit).'),
(4538, 46, 28, 'E, përse ata (idhujt) nuk u ndihmuan atyre, të cilët i kanë marrë, pos Perëndisë, për afrues (ndërmjetësues) te Perëndia? Por, ata nuk ekzistuan. Këto ishin vetëm gënjeshtrat e tyre dhe ato që shpifnin.'),
(4539, 46, 29, 'Dhe, (kujtoju atyre kohën) kur t’i dërguam ty disa xhindë për të dëgjuar Kur’anin. Kur morën pjesë (në të dëgjuarit e Kur’anit), ata i thanë (njëri-tjetrit): “Heshtni!” E, kur përfundoi (të lexuarit), u kthyen te populli i tyre si paralajmërues.'),
(4540, 46, 30, 'Ata thanë: “O populli ynë! Na dëgjuam Librin, të shpallur mas Musait, i cili vërteton se janë të vërteta edhe ato para tij, i cili udhëzon kah e vërteta dhe në rrugën e drejtë.'),
(4541, 46, 31, 'O populli ynë, përgjigjuni ju grishtarit të Perëndisë dhe besoni Atij! Ai do t’ua falë disa mëkate tuaja dhe do t’ju shpëtojë prej dënimit të dhembshëm.'),
(4542, 46, 32, 'E, ai që nuk i përgjigjet grishtarit të Perëndisë, të atillë nuk do të mund t’i ikin Atij në Tokë dhe për ta s’ka mbrojtës, pos Perëndisë. Ata janë në humbje të madhe.'),
(4543, 46, 33, 'A nuk shohin ata që Perëndia – i cili krijoi qiejt dhe Tokën, i cili nuk është lodhur në krijimin e tyre – është i pushtetshëm t’i ngjallë të vdekurit? Po, me të vërtetë, Ai është i pushtetshëm për çdo gjë.'),
(4544, 46, 34, '(Kujtoju) Ditën kur ata që kanë mohuar do t’i ekspozohen zjarrit (e u thuhet atyre): “Vallë, a nuk është kjo e vërtetë?” Ata, thonë: “Po, pasha Zotin tonë!” Ai do t’ju thotë (atyre): “E, pra, shijoni dënimin për mohimin që keni bërë!”'),
(4545, 46, 35, 'E, ti bëhu i durueshëm (o Muhammed) ashtu siç kanë duruar me këmbëngulje pejgamberët e tjerë dhe mos kërko shpejtimin e dënimit për ta! E, atë Ditë kur të përjetojnë atë që u është premtuar atyre (dënimi), do t’u duket atyre, se (në këtë botë), kanë qëndruar vetëm një çast të ditës. (Kjo është) shpallje! E, kush do të shkatërrohet tjetër, pos popullit ngatërrestar!?');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'Ata që kanë mohuar dhe që i penguan (njerëzit) në rrugën e Perëndisë – Ai, ua ka shkatërruar veprat e tyre.'),
(4547, 47, 2, 'E, ata që besojnë dhe punojnë vepra të mira dhe besojnë në atë që i është shpallur Muhammedit – e ajo është e Vërteta prej Zotit të tyre, - Ai do t’jua mbulojë mëkatet dhe do t’ua përmirësojë gjendjen atyre,'),
(4548, 47, 3, 'meqë, ata që kanë mohuar mbështeten në të pavërtetën, kurse besimtarët mbështeten në të Vërtetën e Zotit të tyre. Kështu Perëndia ua sjell njerëve shembujt e tyre.'),
(4549, 47, 4, 'E, kur të takoheni në luftë me mohuesit, bini në qafë derisa t’i rraskapitni e t’i shkatërroni ata, e pastaj lidhin (zëni robër), dhe mandej, ose i lironi me zemërgjerësi ose me kompensim, përderisa të mos bëhet armëpushimi. Kështu bëni! E, sikur të dojë Perëndia, Ai do t’i shkatërronte ata, por Ai dëshiron t’ju provojë juve njërin me tjetrin. Ai nuk ua humbë veprat atyre që vdesin në rrugën e Perëndisë,'),
(4550, 47, 5, 'dhe Ai, me siguri, do t’i udhëzojë ata dhe do t’ua përmirësojë gjendjen e tyre,'),
(4551, 47, 6, 'dhe do t’i shpie në xhennet ata, të cilin madje, ua ka përshkruar atyre.'),
(4552, 47, 7, 'O besimtarë, nëse ndimoni Perëndinë (rrugën e Tij), edhe Ai do t’ju ndihmon juve dhe do t’ua mundësojë përparimin tuaj.'),
(4553, 47, 8, 'E, ata që nuk besojnë – shkatërrimi për ta! Ai ua humbë veprat atyre.'),
(4554, 47, 9, 'Kjo është, ngase ata e urrejnë atë që e ka shpallë Perëndia, andaj Ai ua ka shkatërruar veprat e tyre.'),
(4555, 47, 10, 'Vallë, a nuk kanë udhëtuar ata nëpër Tokë, për të parë përfundimin e atyre të mëparshmëve?! Perëndia i ka zhdukur ata plotësisht, e kështu do të përfundojnë edhe mohuesit e tjerë.'),
(4556, 47, 11, 'Kjo është (ngase) Perëndia është mbojtës i atyre që besojnë – dhe, meqë, me të vërtetë, për mohuesit nuk ka mbrojtës (ndihmës).'),
(4557, 47, 12, 'Perëndia, me të vërtetë, ata që besojnë dhe bëjnë vepra të mira, do t’i shpie në xhennete, nëpër të cilat rrjedhin lumenj; e ata që nuk besojnë – që kënaqen dhe gëlltisin ashtu siç gëlltisin shtazët, vendbanim i tyre është zjarri!'),
(4558, 47, 13, 'Shumë vende (qytete) kanë qenë më të fuqishme se vendi yt që të ka përzënë ty. Na, i kemi shkatërruar ata dhe nuk ka pasur ndihmës për ta.'),
(4559, 47, 14, 'Vallë, a është i njëjtë ai që (pason) argumentet e qarta të Zotit të tij me atë që i zbukurohen punët e veta të shëmtuara dhe (i cili) pason epshet e veta?!'),
(4560, 47, 15, 'Shembulli i xhennetit, që u është premtuar besimtarëve – në të cilin gjenden lumenj me ujë të paprishur, dhe lumenj të qumështit me shije të pandryshuar, dhe lumenj të verës me shije të këndshme për ata që e pinë (pa përbërje alkooli), dhe lumenj prej mjaltit të kulluar, në të kanë edhe llojlloj pemësh dhe falje (të mëkateve) prej Zotit të tyre, (a është njësoj si shembulli i atij) i cili është përherë në zjarr dhe të cilëve u jepet ujë i vëluar, që ua copton zorrët?!'),
(4561, 47, 16, 'Në mes tyre ka (edhe të atillë) që të përgjojnë ty, e posa të largohen prej teje, i pyesin ata të cilëve u është dhënë dijenia: “Ç’tha ai më parë?!” Këta janë ata, zemrat e të cilëve ua ka vulosur Perëndia dhe të cilët ndjekin epshet e veta.'),
(4562, 47, 17, 'E, ata që kanë marrë rrugën e drejtë, Ai (Perëndia) ua shton udhëzimin dhe u jep atyre (forcon) ruajtjen (nga zjarri).'),
(4563, 47, 18, 'A mos vallë mohuesit presin që t’u vie befasisht (edhe) vetëm Çasti i caktuar, e madje – me të vërtetë, shenjat e tij kanë ardhur? E, çka do t’u shërbejnë atyre këshillat kur t’ju vie ai (Çasti)?!'),
(4564, 47, 19, 'Dije se s’ka zot (tjetër) pos Perëndisë! Kërko falje për mëkatet tua dhe për besimtarët e besimtaret. Perëndia di se ku silleni ju dhe ku qëndroni (banoni).'),
(4565, 47, 20, 'E, thonë ata që kanë besuar: “Sikur të ishte shpallur një sure!” E, kur u shpall një sure e qartë dhe në të u përmend (obligimi) i luftës, i sheh ti ata zemrash të sëmuara (me besim të dyshimtë), si të shikojnë ty me shikimin si të agonisë së jetës! E, më e përshtatshme do të ishte për ata –'),
(4566, 47, 21, 'përulja dhe fjala e mirë! E, kur, madje është caktuar dispozita e luftës, më mirë do të ishte për ta, (atëherë) të jenë të sinqertë ndaj Perëndisë.'),
(4567, 47, 22, 'E, vallë ndoshta edhe ju (hipokritët) sikur të vinit në pushtet, do të bënit ngatërresa (zullum) në Tokë dhe do t’i shkatërronit lidhjet farefisnore?!'),
(4568, 47, 23, 'Këta janë ata, të cilët i ka mallkuar Perëndia dhe i ka bërë të shurdhër (ndaj së vërtetës) dhe ua ka verbuar sytë (për të parë rrugën e vërtetë).'),
(4569, 47, 24, 'Vallë, a nuk duan ata të mendojnë për Kur’anin, apo në zemrat e tyre ka dryna?!'),
(4570, 47, 25, 'Me të vërtetë, ata që janë kthyer prapa (në mohim), pasi u është treguar atyre rruga e drejtë, djalli i ka shpie në mëkat dhe i ka udhqyer ata me shpresë të rrejshme.'),
(4571, 47, 26, 'Kjo ngjet, ngase ata u thonin atyre, të cilët nuk e donin atë që e ka shpallur Perëndia: “Na do t’ju përulemi juve në disa gjëra”, - e Perëndia i di mirë fshehtësitë e tyre.'),
(4572, 47, 27, 'E, (çka do të bëjnë) ata, kur engjëjt do t’u marrin shpirtin atyre, duke i rrahur ata nëpër fytyrat dhe shpindët e tyre.'),
(4573, 47, 28, 'Kjo (mënyrë e vdekjes) është, ngase ata, pasuan atë që shkakton pezmin e Perëndisë dhe që urrenin kënaqësinë e Tij; e Ai, ua ka shkatërruar veprat e tyre.'),
(4574, 47, 29, 'Vallë, a mendojnë ata me zemra të sëmura – se, Perëndia nuk do t’ua qet në shesh urrejtjen e tyre?'),
(4575, 47, 30, 'E, sikur të donim, Ne do t’i tregonim ty ata, e ti me siguri, do t’i njihje ata, sipas shenjave të tyre (të fytyrës). Por, ti do t’i njohësh ata sipas mënyrës së shprehjes së tyre, e Perëndia i di veprat tuaja.'),
(4576, 47, 31, 'Ne do t’ju vëmë në sprovë juve, përderisa t’i njohim në thelb ata të cilët prej jush luftojnë (në rrugën e drejtë) dhe durojnë (mundimin), si dhe për t’i vërtetuar lajmet për veprat tuaja.'),
(4577, 47, 32, 'Me të vërtetë, ata që nuk besojnë dhe i pengojnë (të tjerët) nga rruga e Perëndisë dhe kundërshtojnë Pejgamberin, pasi që atyre u është shpjeguar qartë rruga e drejtë, nuk do t’i sjellin (me këtë) Perëndisë kurrfarë dëmi, e Ai do t’ua zhdukë veprat e tyre.'),
(4578, 47, 33, 'O ju që keni besuar! Përuljuni Perëndisë dhe përuljni Pejgamberit (të Tij) dhe mos i prishni veprat tuaja!'),
(4579, 47, 34, 'Me të vërtetë, ata që nuk besojnë e vdesin si mohues, Perëndia, kurrsesi, nuk do t’i falë ata.'),
(4580, 47, 35, 'Dhe, mos u bëni qyqarë dhe mos afroni paqë duke qenë ju më të fuqishëm, sepse Perëndia është me ju (në ndihmë tuaj), Ai nuk ua pakëson shpërblimin e veprave tuaja.'),
(4581, 47, 36, 'Jeta e kësaj bote nuk është asgjë tjetër, pos lojë dhe zbavitje, e nëse besoni dhe ruheni (prej të këqiave), Ai do t’ju shpërblejë juve dhe nuk do të kërkojë prej jush (tërë) pasurinë tuaj.'),
(4582, 47, 37, 'Sikur ta kërkonte prej jush atë (tërë pasurinë me këmbëngulje), do të bëheshit koprrac dhe do të shkaktonte urrejtjen tuaj.'),
(4583, 47, 38, 'Ja, pra, ju thirreni se shpenzoni në rrugën e Perëndisë, por disa prej jush janë koprrac, - e kush është koprrac, është koprrac në dëm të vet, sepse Perëndia është i pasur (s’ka nevojë për asgjë), ndërsa ju jeni të varfër (jeni nevojtarë për gjithçka). E, nëse ju, shmangeni, Ai do t’ju zëvendësojë me një popull tjetër, i cili nuk do të bëhet si ju.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Ne, ta dhamë ty fitoren e qartë,'),
(4585, 48, 2, 'për t’i falur ty Perëndia gabimet e mëparshme dhe të ardhshme, për ta plotësuar dhuntinë e Vet ndaj teje dhe për të udhëzuar ty në rrugën e drejtë,'),
(4586, 48, 3, 'dhe që Perëndia të ndihmojë ty me ndihmë të madhe.'),
(4587, 48, 4, 'Ai është që sjell qetësi në zemrat e besimtarëve për t’ua forcuar (edhe më tepër) besimin, krahas besimit që kanë – e, të Perëndisë janë ushtritë e qiejve dhe të Tokës; Perëndia është i Plotëdijshëm dhe i Gjithëdijshëm, -'),
(4588, 48, 5, 'për t’i shpie besimtarët dhe besimtaret në kopshtije nëpër të cilat rrjedhin lumenjtë, ku do të qëndrojnë përherë, dhe që t’ju falë mëkatet e tyre – e kjo te Perëndia është fitore e madhe –'),
(4589, 48, 6, 'dhe që t’i ndëshkojë hipokritët dhe hipokritet, dhe idhujtarët e idhujtaret, që mendojnë keq për Perëndinë, - e vallja e fatkeqësisë le t’i godet ata! Perëndia është pezmatuar në ta dhe i ka mallkuar ata dhe u ka përgatitur atyre, xhehennemin, e sa vend i shëmtuar që është ai!'),
(4590, 48, 7, 'Të perëndisë janë ushtritë e qiejve dhe të Tokës; Perëndia është i Plotëfuqishëm (për t’i dënuar) dhe i Gjithëdijshëm!'),
(4591, 48, 8, 'Ne, me të vërtetë, të kemi dërguar ty dëshmitar dhe sihariques e paralajmërues'),
(4592, 48, 9, 'për të besuar Perëndinë dhe Pejgamberin e Tij, dhe që të ndihmoni (dispozitat) e Tij, dhe ta madhëroni Ate dhe falenderoni Ate me namaz në mëngjes dhe mbrëmje.'),
(4593, 48, 10, 'Ata që të janë betuar ty në besim (pë të ndjekë rrugën tënde) – në të vërtetë, janë betuar në besim të Perëndisë – Dora (dhuntia) e Perëndisë është mbi duart e tyre! Ai që e prishë betimin, e prishë në dëm të vetin, e nëse e plotëson betimin ndaj Perëndisë, Ai do t’i jep atij shpërblim të madh.'),
(4594, 48, 11, 'Do të të thonë ty arabët e shkretëtirës (beduinët), të cilët ngelën pas jush dhe nuk erdhën me ju: “Na angazhuan (preokupuan) neve pasuria jonë dhe familjet tona, e lutju (Perëndisë) të na falë neve!” Ata flasin me gjuhën e tyre, atë që nuk e kanë në zemrat e tyre. Thuaju (atyre): “Kush mund ta pengojë Perëndinë ndaj jush, nëse Ai don t’ju godet juve me ndonjë dëm, ose nëse don t’ju bëjë ndonjë të mirë?!” Jo, Perëndia di atë që punoni ju.'),
(4595, 48, 12, 'Jo, por ju keni menduar se Pejgamberi dhe besimtarët nuk do të kthehen kurrë te familjet e tyre, kjo ju ka zbukuruar juve në zemrat tuaja dhe ju keni menduar në mënyrën më të shëmtuar, dhe jeni bërë popull që do të zhdukeni.'),
(4596, 48, 13, 'Kush nuk beson në Perëndinë dhe në Pejgambeirn e Tij, - Na kemi përgatitur për mohuesit zjarrin flakërues!'),
(4597, 48, 14, 'Të Perëndisë janë pushteti i qiejve dhe i Tokës. Ai falë kë të dojë; e dënon kë të dojë – Perëndia falë shumë dhe është mëshirues.'),
(4598, 48, 15, 'Do t’u thonë juve ata që ngelën pas jush (në luftë), kur të shkoni me marrë plaçkat e luftës: “Lejona edhe neve që t’ju pasojmë juve!” Ata do të donin ta ndërrojnë fjalën e Perëndisë (premtimin). Thuaju: “Ju, kurrsesi nuk do të na pasoni, këtë e ka thënë Perëndia qysh më parë!” E ata do të thonë: “Nuk është ashtu, por, ju na keni zili neve”. Jo, nuk është as kjo, por ata pak kuptojnë.'),
(4599, 48, 16, 'Thuaju atyre beduinëve që ngelën pas jush (e nuk erdhën): “Do të thirreni ju në luftë kundër një populli luftarak, përderisa të mos përulet (ose të bëhet musliman). E, nëse dëgjoni (thirrjen), Perëndia do t’u japë shpërblim të mirë, e nse shmangeni (refuzoni) ashtu siç jeni shmangur më parë, (Ai) do t’ju ndëshkojë me dënim të dhembshëm.'),
(4600, 48, 17, 'Nuk është mëkat për të verbëtin, as për të çalin as të sëmurin (që të mugnojë nga ekspedita luftarake), e kush i përulet Perëndisë dhe Pejgamberit të Tij, Ai e shpie në xhenete, nëpër të cilat rrjedhin lumenjtë, e atë që shmanget, do ta ndëshkojë me dënim të dhembshëm.'),
(4601, 48, 18, 'Perëndia është i kënaqur me ata besimtar kur t’u betuan ty nën hijen e drurit. Ai (Perëndia), e ka ditur se ç’ka në zemrat e tyre, e u ka dhënë atyre qetësi (siguri), dhe i shpërbleu ata me fitoren e afërt,'),
(4602, 48, 19, 'dhe me plaçkën (pasurinë e zënë në luftë) e madhe që do ta fitojnë. Perëndia është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(4603, 48, 20, 'Perëndia u ka premtuar juve plaçkë të pasur që do ta fitoni, e ua shpejtoi këtë juve (Ai) dhe i pengoi duart e njerëzve (nga të dëmtuarit juve) – për të qenë ky argument për besimtarët (që premtimi i Perëndisë është i realizueshëm) dhe për t’u udhëzuar juve në rrugën e drejtë,'),
(4604, 48, 21, 'dhe (ju ka premtuar) tjetrën (fitore), të cilën nuk keni qenë në gjendje ta fitoni, - por Perëndia, me dispozitën e Vet, e ka përmbledhë atë për ju, sepse, Perëndia është i pushtetshëm për çdo gjë.'),
(4605, 48, 22, 'E, nëse mohuesit lëshohen në luftë kundër jush, ata, me siguri, do të iknin dhe pastaj nuk do të gjenin as mbrojtës as ndihmës –'),
(4606, 48, 23, '(Kjo është) dispozita e Perëndisë që ka qenë edhe më parë, e ti, kurrsesi, nuk mund të gjesh ndryshime në dispozitën e Perëndisë.'),
(4607, 48, 24, 'Ai ka penguar duart e tyre ndaj jush dhe duart tuaja ndaj tyre – brenda në Mekke, pasi u ka dhënë juve fitoren mbi ta. Perëndia i sheh të gjitha çka punoni ju.'),
(4608, 48, 25, 'Ata, të cilët nuk kanë besuar dhe ju pengojnë nga Mesxhidil-Harami (Qabja) dhe (pengojnë) të përgjëruarin – kurbanin e caktuar që të arrijë në vend. E, sikur të mos kishte besimtarë dhe besimtare, që ju nuk i dini e do t’i merrnit nëpër këmbë ata, e juve do t’ju godiste një hidhërim, duke mos ditur (do t’ju kisha lejuar të pushtoni Mekken). Kjo është, ngase Perëndia e shpie në mëshirën e Vet kë të dojë. Sikur të ishin të veçuar ata, Ne do t’i ndëshkonim ata në mes tyre, të cilët nuk kanë besuar – me dënim të dhembshëm.'),
(4609, 48, 26, '(Kujtoje ti) kur në zemrat e mohuesve u shfaq krenaria, krenaria pagane, e Perëndia zbriti qetësinë e Tij në zemrën e Pejgamberit dhe të besimtarëve dhe i obligoi ata me fjalën e takvasë (shehadetit) e ata gjithsesi – kanë pasur më shumë të drejtë dhe kanë qenë të denjë për të, - e Perëndia është i Gjithëdijshëm për çdo gjë.'),
(4610, 48, 27, 'Perëndia e realizoi (sendërtoi) ëndërrën e Pejgamberit të vet me të vërtetën: “Me siguri, ju do të hyni në Mesxhidil-Haram, nëse jep Perëndia, (dhe këtë) të sigurt, kokë-rruar dhe flokë-shkurtuar, pa kurrfarë frike. Ai e ka ditur atë që nuk e keni ditur ju. Dhe, para kësaj (hyrjes në Mekke), ju ka dhuruar një fitore të afërt.'),
(4611, 48, 28, 'Ai e ka dërguar Pejgamberin e Vet me Udhëzim (në rrugë të drejtë) he fenë e vërtetë, për ta ngritur mbi të gjitha fetë. E, Perëndia është dëshmitar i mjaftueshëm!'),
(4612, 48, 29, 'Muhammedi është Pejgamber i Perëndisë. E, ata që janë me të (shokët e tij) janë të ashpër ndaj mohuesve, e të mëshirshëm në mes tyre. I sheh ata si bëjnë ruqu dhe sexhde duke kërkuar dhuntinë dhe kënaqësinë e Perëndisë. Në fytyrat e tyre shihen shenjat e sexhdes. Ky është përshkrimi i tyre në Tevrat. E, përshkrimi i tyre në Ungjill, është: (ata janë) si fara (e mbjellur) që lëshon filizat (e vogla) të cilat e forcojnë atë, e bëhet e trashë, e pastaj drejtohet (vertikalisht) në trungun e vet; (kështu që), e entusiazmon atë që e ka mbjellë, - që për (këtë gjë) t’i zemrojë mohuesit. Perëndia u ka premtuar atyre që besojnë dhe punojnë vepra të mira (të gjithë atyre) – falje (të mëkateve) dhe shpërblim të madh.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'O ju që keni besuar! Mos nxitoi para urdhërit (dispozitës) së Perëndisë dhe Pejgamberit të Tij dhe druani Perëndisë! Perëndia, me të vërtetë, i dëgjon të gjitha dhe i di të gjitha.'),
(4614, 49, 2, 'O ju që keni besuar! Mos ngritni zërin tuaj mbi zërin e Pejgamberit dhe mos bisedoni me të me zë të lartë, ashtu si flitni zëshëm njëri me tjetrin, në mëhyrë që mos t’u humbin veprat tuaja – e që ju të mos i hetoni.'),
(4615, 49, 3, 'Me të vërtetë, ata që ulin zërin para (në prezencën e) Pejgamberit të Perëndisë – e ata janë (ata), zemrat e të cilëve Perëndia i ka kalitur për besim. Për ta ka falje dhe shpërblim të madh.'),
(4616, 49, 4, 'Me të vërtetë, ata që të thërrasin ty pas (mureve) të dhomave (tuaja), shumica e tyre nuk mendojnë.'),
(4617, 49, 5, 'E sikur të durojnë ata deri sa t’ju dalish ti atyre, do të ishte më mirë për ta; e Perëndia ësht falës dhe mëshirues.'),
(4618, 49, 6, 'O ju që keni besuar! Nëse ndonjë njeri i pandershëm ju sjell ndonjë lajm, analizoni mirë, për të mos i bërë dëm ndokujt nga mosdijenia, e të pendoheni pastaj për atë që keni punuar.'),
(4619, 49, 7, 'Dhe, dijeni se në mes jush gjendet Pejgamberi i Perëndisë; sikur ai t’ju dëgjonte juve në shumë gjëra, ju do të kishit pësuar; por Perëndia disave prej jush ua ka bërë të këndshëm besimin dhe ua ka zbukuruar atë në zemrat tuaja, e ua ka urryer juve mohimin, ngatërresat dhe mëkatet. Të tillët janë në rrugën e drejtë,'),
(4620, 49, 8, 'me mirësinë dhe dhuntinë e Perëndisë. – E, Perëndia është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(4621, 49, 9, 'Nëse dy grupe muslimanësh luftojnë në mes vete, pajtoni ata, e nëse njëra prej tyre sulmon atë tjetrën, atëherë luftoni kundër sulmuesit, përderisa të mos kthehet në dispozitat e Perëndisë. E, nëse kthehet, atëherë pajtoni ata mes tyre, me të drejtë dhe bëhuni të drejtë. Perëndia, me të vërtetë, i do të drejtët.'),
(4622, 49, 10, 'Me të vërtetë, muslimanët janë vëllezër, andaj pajtoni vëllezërit tuaj në mes vete dhe druajuni Perëndisë, për t’u mëshiruar ju.'),
(4623, 49, 11, 'O ju që keni besuar! Le të mos e përqeshin një popull atë tjetrin. Ndoshta ata të përqeshurit janë më të mirë se ata, e as gratë mos të përqeshin të tjerat. Ndoshta ato (të përqeshurat) janë më të mira se ato, dhe mos e nënçmoni njëri-tjetrin dhe mos e thirrni njëri-tjetrin me llagape (të këqia)! I shëmtuar është emri i keq pas besimit! E, ata që nuk pendohen, ata, me të vërtetë, janë zullumqarë.'),
(4624, 49, 12, 'O ju që keni besuar! Shmanguni prej shumë dyshimeve, meqë disa dyshime janë mëkat. Mos i hulumtoni të metat (e njëri-tjetrit) dhe mos e përgojoni njëri-tjetrin! Mos vallë dëshiron ndokush prej jush të hajë mishin e vëllait të vet të vdekur, e juve kjo u është e neveritshme! Druajuni Perëndisë! Me të vërtetë, Perëndia është pranues i pendimit dhe mëshirues.'),
(4625, 49, 13, 'O njerëz! Ne, me të vërtetë, u krijuam juve prej një mashkulli dhe një femre dhe u bëmë juve popuj dhe fise, për t’u njohur (në mes vete). Më i lavdërueshëm te Perëndia është ai që i druan më së shumti Atij. Me të vërtetë, Perëndia është i Plotëdijshëm dhe asgjë nuk i është e fshehtë Atij.'),
(4626, 49, 14, 'Beduinët (arabët e shkretëtirës) thanë: “Na besojmë”. Thuaju (atyre): “Nuk keni besuar!” Por, thoni: “Na jemi përulur!” meqë në zemrat tuaja ende nuk ka hyrë feja si duhet. E, nëse i përuleni Perëndisë dhe Pejgamberit të Tij, nuk do t’ju pakësohet juve asgjë prej veprave tuaja. Me të vërtetë, Perëndia është falës dhe mëshirues.'),
(4627, 49, 15, 'Besimtarë të vërtetë janë vetëm ata që besojnë Perëndinë dhe Pejgamberin e Tij, dhe pastja nuk dyshojnë dhe luftojnë në rrugën e Perëndisë me pasurinë dhe jetën e tyre. Këta janë besimtarë të sinqertë.'),
(4628, 49, 16, 'Thuaju (atyre): “Vallë, a ju ta lajmëroni Perëndinë për besimin tuaj, e Perëndia i di të gjitha çka ka në qiej dhe në Tokë! Perëndia është i dijshëm për çdo gjë.'),
(4629, 49, 17, 'Ata të përkujtojnë ty, (mirësitë), ngase kanë pranuar islamizmin. Thuaju (atyre): “Mos më përkutjoni ju për atë që keni pranuar islamizmin; përkundrazi, Perëndia ju ka dhuruar mirësi dhe ju ka orientuar në besim, nëse flisni të vërtetën!'),
(4630, 49, 18, 'Perëndia, me të vërtetë; i di fshehtësitë e qiejve dhe Tokës. Perëndia i sheh të gjitha ato që punoni ju.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Kâf. Pasha Kur’anin e madhërueshëm,'),
(4632, 50, 2, 'Po, ata janë çuditur që nga mesi i tyre u ka ardhur një paralajmërues, e mohuesit thonë: “Kjo gjë është e çuditshme”.'),
(4633, 50, 3, 'Vallë, a pasi të vdesin e të bëhemi dhé (do të ringjallemi)? Ai kthim është i largët!”'),
(4634, 50, 4, 'Na dimë çka do t’ua mungojë toka atyre (pasi të vdesin), te Ne është Libri që i ruan të gjitha.'),
(4635, 50, 5, 'Aq më tepër, ta e mohuan të Vërtetën kur u erdhi dhe ata janë në gjendje të luhatshme (në çështjen e profetnisë).'),
(4636, 50, 6, 'A, nuk e kanë shikuar ata qiellin që qëndron mbi ta, si e kemi ndërtuar dhe zbukuruar, e i cili nuk ka kurrfarë të mete (çrregullimi)!'),
(4637, 50, 7, 'E, Tokën e kemi shtruar dhe në të kemi shpërndarë male të palëvizshme dhe kemi bërë që nga ajo të mbijnë gjithfarë lloj bimësh të bukur,'),
(4638, 50, 8, 'për t’u menduar dhe këshilluar çdo rob që i drjtohet Zotit të vet.'),
(4639, 50, 9, 'Ne nga qielli lëshojmë ujin (shiun) e bekuar dhe bëjmë që me të, të ujiten kopshtijet dhe drithërat që korren.'),
(4640, 50, 10, 'Dhe palmet e hurmës së lartë me kalavesh njëri mbi tjetrin,'),
(4641, 50, 11, 'ushqim për robërit (njerëzit), dhe Ne me shi e ringjallim tokën (visin) e vdekur; e kështu do të jetë edhe dalja (prej tokës – ringjallja).'),
(4642, 50, 12, 'Pas tyre, kanë mohuar populli i Nuhut, edhe banorët e Ressit dhe Themudi,'),
(4643, 50, 13, 'dhe Adi dhe Faraoni, dhe vëllezërit (populli) e Lutit,'),
(4644, 50, 14, 'edhe banorët e Ejkës dhe populli i Tubbas; të gjithë ata i kanë përgënjeshtruar pejgamberët dhe ndaj tyre u sendërtua dënimi Im.'),
(4645, 50, 15, 'E, vallë, mos jemi lodhur Ne gjatë krijimit të parë? Jo, por ata dyshojnë në krijimin e sërishëm.'),
(4646, 50, 16, 'Na e kemi krijuar njeriun dhe Na dimë çka i pëshpërit shpirti atij, se Ne (me dijeninë Tonë) jemi më afër se damari i tij i qafës,'),
(4647, 50, 17, '(dhe nga të dy engjëjt) të cilët i rrinë atij nga ana e djathtë dhe e majtë,'),
(4648, 50, 18, 'dhe ai nuk e thotë asnjë fjalë e që pranë tij të mos jetë ai që mbikëqyrë.'),
(4649, 50, 19, 'Mundimet e vdekjes do të arrijnë me siguri – kësaj nuk mund t’i ikësh'),
(4650, 50, 20, 'dhe do të fryhet në Sur – kjo është Dita e premtimit –'),
(4651, 50, 21, 'dhe çdonjëri do të vijë, e me të ka grahës dhe dëshmitar.'),
(4652, 50, 22, 'Ti, me të vërtetë, ke qenë i pakujdesshëm për këtë (momentin e vdekjes), andaj ta kemi ngritur perden (e syve), dhe shikimi yt sot është i prehtë.'),
(4653, 50, 23, 'E, shoku i tij do të thotë: “Ky pranë meje, është i gatshëm”'),
(4654, 50, 24, '(u thuhet): “Hidheni në zjarr çdo mohues inatçi,'),
(4655, 50, 25, 'pengues i të mirave, që kalon (kufirin) dhe i cili dyshon (në fé),'),
(4656, 50, 26, 'i cili, pos Perëndisë, beson në zot tjetër – andaj hidhe atë në dënimin më të rëndë!”'),
(4657, 50, 27, 'E, shoku i tij do të thotë: “O Zoti ynë, unë nuk e kam mashtruar atë, por ai vet ka qenë në humbje të madhe”.'),
(4658, 50, 28, 'Atyre (Perëndia) do t’ju thotë: “Mos u grindni para Meje, Unë ju kam lajmëruar premtimin (dënimin),'),
(4659, 50, 29, 'Fjala Ime nuk ndryshohet dhe Unë nuk jam zullumqar i robërve të Mi”.'),
(4660, 50, 30, 'Në Ditën kur do ta pyesin xhehennemin: “A u mbushe?” e ai, përgjigjet: “A ka edhe më?”'),
(4661, 50, 31, 'E, xhenneti do t’u afrohet besimtarëve, jo larg.'),
(4662, 50, 32, 'Kjo ju është premtuar, çdokujt që është penduar dhe ruajtur (nga kalimi i kufirit),'),
(4663, 50, 33, 'i cili frikohet prej Perëndisë edhe atëhrë kur nuk e sheh kush dhe i cili vie me zemër të kthyer kah Perëndia”.'),
(4664, 50, 34, '(U thuhet atyre): “Hyni në të (në xhennet), në qetësi (e mirësi), kjo është Dita e përhershme!”'),
(4665, 50, 35, 'Në të do të kenë çka të duan – e prej Nesh edhe më shumë.'),
(4666, 50, 36, 'E, sa gjenerata i kemi shkatërruar par tyre, duke qenë ata më të fortë (se Kurejshit) dhe ata bredhnin nëpër Tokë. – E, a kanë mund të gjejnë strehim?!'),
(4667, 50, 37, 'Me të vërtetë, për të gjitha këto ka këshillë për atë që ka mend ose që dëgjon, e që është i përqëndruar.'),
(4668, 50, 38, 'Ne, kemi krijuar qiejt dhe Tokën dhe çdo gjë që gjendet mes tyre për gjashtë ditë (periudha kohore), dhe nuk na përshkoi kurrëfarë lodhje.'),
(4669, 50, 39, 'Andaj, duroje atë që e thonë ata dhe madhëroje Zotin tënd, dhe falenderoje Atë para lindjes së diellit dhe para muzgut,'),
(4670, 50, 40, 'dhe madhëroje Atë në një pjesë të natës dhe pas kryerjes së namazit.'),
(4671, 50, 41, 'Dhe përgjoje ti! Ditën kur thërritësi (engjëlli) thërret prej vendit të afërt,'),
(4672, 50, 42, 'Ditën kur ata do ta dëgjojnë zërin e vërtetë, ajo do të jetë Dita e daljes (ringjalljes).'),
(4673, 50, 43, 'Na, me të vërtetë, japim gjallëri dhe japim vdekje dhe te Ne është kthimi!'),
(4674, 50, 44, 'E, në Ditën kur toka do të çahet e të dalin ata me nxitim; kjo është ringjallja, për Ne (kjo) është lehtë.'),
(4675, 50, 45, 'Na dimë mirë çka thonë ata; e ti nuk je ai që i shtrëngon ata (të besojnë), por këshilloje me Kur’an atë që i druan premtimit (dënimit) Tim!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Pasha ato erëra që çojnë (pluhurin),'),
(4677, 51, 2, 'dhe të cilat bartin ngarkesat,'),
(4678, 51, 3, 'dhe ato (erëra) që lundrojnë lehtë,'),
(4679, 51, 4, 'dhe pasha engjëjt që shpërndajnë urdhërat, -'),
(4680, 51, 5, 'me të vërtetë, është e sigurt ajo që ju premtohet,'),
(4681, 51, 6, 'dhe çdo shpërblim (i premtuar) do të ndodhë!'),
(4682, 51, 7, 'Pasha qiellin e stolisur me yje,'),
(4683, 51, 8, 'ju jeni të larmishëm në fjalë,'),
(4684, 51, 9, 'prej tij (Pejgamberit ose Kur’anit) është shmangur ai që ka qenë i shmangur (edhe më parë).'),
(4685, 51, 10, 'U mallkofshin gënjeshtarët!'),
(4686, 51, 11, 'Ata që janë thelluar në injorancë (dhe harrojnë ringjalljen).'),
(4687, 51, 12, 'Ata pyesin: “Kur do të jetë Dita e shpërblimit?”'),
(4688, 51, 13, '(Do të jetë) atë Ditë kur ata do të përcëllohen në zjarr!'),
(4689, 51, 14, '(U thuhet atyre): “Shijoni dënimin tuaj! Ky (dënim) është ai që e nxitojshit!”'),
(4690, 51, 15, 'Ata që i druajnë Perëndisë, me të vërtetë, do të jenë në kopshte dhe burime,'),
(4691, 51, 16, 'duke marrë atë që ua ka dhuruar Zoti i tyre, meqë ata, me të vërtetë, më parë kanë qenë bamirës,'),
(4692, 51, 17, 'ata, kanë fjetur pak natën (duke iu lutur Perëndisë)'),
(4693, 51, 18, 'po dhe në agim kërkonin falje,'),
(4694, 51, 19, 'e, në pasuritë e tyre ka pasur pjesë për lypësit dhe nevojtarët.'),
(4695, 51, 20, 'Në Tokë ka argumente (të shumta) për ata që besojnë qëndrueshëm,'),
(4696, 51, 21, 'po edhe në ju – a nuk shihni vallë?'),
(4697, 51, 22, 'E në qiell është furnizimi dhe premtimi juaj.'),
(4698, 51, 23, 'Dhe, Pasha Zotin e qiellit dhe të Tokës, kjo është vërtetë e saktë, ashtu si është e vërtetë (aftësia e) të folurit tuaj.'),
(4699, 51, 24, 'A të ka arritur ty lajmi për mysafirët e ndershëm të Ibrahimit?'),
(4700, 51, 25, 'Kur ata hynë tek ai, e i thanë: “Selam (Paqë! – shpëtimi u qoftë!)” dhe ai u tha: “Selam (Paqë! Qofshi të shpëtuar!)” Ju jeni – (tha në vetvete) popull i panjohur!”'),
(4701, 51, 26, 'Dhe, pa u hetuar, ai shkoi te njerëzit e shtëpisë dhe solli viç të majmë (të pjekur),'),
(4702, 51, 27, 'e ua afroi atyre, e u tha: “A s’po hani?”'),
(4703, 51, 28, 'Dhe, ndjeu frikë prej tyre, (e) ata i thanë: “Mos u friko!” dhe e sihariquan atë (se do t’i lindë) një djalë i dijshëm.'),
(4704, 51, 29, 'Dhe, u duk gruaja e tij, duke thërritur dhe duke e rrahur fytyrën, e tha: “Vallë, unë plaka, sterile (a mund) të lind?!'),
(4705, 51, 30, 'Ata thanë: “Kështu ka thënë Zoti yt. Ai, me të vërtetë, është i Gjithëdijshëm dhe i Plotëdijshëm”.'),
(4706, 51, 31, '(Ibrahimi u tha atyre): “Çfarë është obligimi juaj, o pejgamberë?”'),
(4707, 51, 32, 'Ata thanë: “Na jemi dërguar te një popull mëkatar,'),
(4708, 51, 33, 'për të hudhur gurë mbi ta prej (baltës) së forcuar,'),
(4709, 51, 34, 'të shënuar te Zoti yt për ata që e kalojnë kufirin (në të këqia)'),
(4710, 51, 35, 'Dhe, Ne i nxorëm ata besimtarë që ishin aty (në atë vendbanim) –'),
(4711, 51, 36, 'e në të kemi gjetur vetëm një shtëpi muslimane –'),
(4712, 51, 37, 'dhe, në të – kemi lënë argument (shenjë) për ata që i druajnë dënimit të dhembshëm.'),
(4713, 51, 38, 'Dhe, ne kemi sjellë këshillë edhe te Musai kur ia dërguam atë – Faraonit, me argument të qartë,'),
(4714, 51, 39, 'e u shmang (Faraoni), duke u mbështetur në pushtetin e vet, dhe tha: “(Ai është) magjistar ose i çmendur!”'),
(4715, 51, 40, 'Dhe, ne e kapëm atë dhe ushtrinë e tij dhe e hodhëm në det; ai është i qortuar (mëkatar).'),
(4716, 51, 41, '(Dhe, Ne, kemi dhënë këshillë mbi ngjarjen) e Adit, kur e dërguam frymën shkatërruese mbi ta,'),
(4717, 51, 42, 'e cila nuk kurseu asgjë ngado që kaloi, e që të mos e ketë bërë si kalbësirë.'),
(4718, 51, 43, 'Edhe për Themudin, kur iu tha atyre: “Kënaquni një kohë!”'),
(4719, 51, 44, 'E, u madhështuan ndaj urdhërave të Perëndisë, e pastaj i goditi rrufeja (zëri i tmerrshëm), e ata e shikonin atë (dënimin),'),
(4720, 51, 45, 'dhe nuk mundën as të ngritën, as të mbrohen prej dënimit.'),
(4721, 51, 46, 'Edhe për popullin e Nuhut, që më parë (i kemi shkatërruar). Me të vërtetë, ata kanë qenë popull mëkatar!'),
(4722, 51, 47, 'Ne, e kemi ndërtuar qiellin me pushtetin Tonë, e Ne, me të vërtetë, jemi të pushtetshëm edhe më tepër,'),
(4723, 51, 48, 'dhe Tokën e kemi shtruar – sa i mrekullueshëm është Ai që e ka shtruar! –'),
(4724, 51, 49, 'dhe, prej çdo gjëje kemi krijuar çifte, që të meditoni ju!'),
(4725, 51, 50, 'Andaj, mbështetuni te Perëndia, se unë, me të vërtetë, jam dërguar te ju – prej Tij, paralajmërues i qartë;'),
(4726, 51, 51, 'Dhe, mos i përshkruani Perëndisë zot tjetër, se unë, me të vërtetë, jam dërguar te ju – prej Tij, paralajmërues i qartë!”'),
(4727, 51, 52, 'Po ashtu, nuk ka ardhur as para tyre asnjë pejgamber, e që të mos thonin (për te): “Është magjistar”, apo: “Është i çmendur!”'),
(4728, 51, 53, 'Vallë, a mos ia kanë rekomanduar këtë njëri-tjetrit? Jo, por ata janë popull rebel,'),
(4729, 51, 54, 'e, shmangu prej tyre; ti nuk do të qortohesh për këtë,'),
(4730, 51, 55, 'dhe (vazhdoje) këshillimin, se, me të vërtetë, këshilla u bën dobi besimtarëve.'),
(4731, 51, 56, 'Xhindët dhe njerëzit i kam krijuar vetëm që të Më adhurojnë,'),
(4732, 51, 57, 'Unë nuk kërkoj prej tyre furnizim, as nuk dëshiroj që të Më ushqejnë,'),
(4733, 51, 58, 'furnizimin e jep vetëm Perëndia, i Fuqishmi, i Paluhatshmi!'),
(4734, 51, 59, 'Me të vërtetë, për ata që kanë bërë zullum, kanë pjesë (të dënimit), të ngjashëm pjesës së shokëve të tyre që kanë qenë si ata, e le të mos e nxitojnë atë (dënimin),'),
(4735, 51, 60, 'sepse, mjerë për mohuesit prej Ditës që u është premtuar!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Pasha malin e Turit (Sinajit),'),
(4737, 52, 2, 'dhe Librin e shkruar në rreshta,'),
(4738, 52, 3, 'të zhvilluar në lëkurë,'),
(4739, 52, 4, 'dhe Tempullin plot haxhinj,'),
(4740, 52, 5, 'dhe kupës së ngritur (të qiellit),'),
(4741, 52, 6, 'dhe detit të mbushur,'),
(4742, 52, 7, 'me të vërtetë, dënimi i Zotit tënd, do të ndodhë,'),
(4743, 52, 8, 'dhe askush nuk mund ta pengojë,'),
(4744, 52, 9, 'atë Ditë kur qielli do të lëkundet fuqishëm,'),
(4745, 52, 10, 'e malet të lëkunden me të madhe nga vendi!'),
(4746, 52, 11, 'Atë Ditë, mjerë për ata që kanë përgënjeshtruar,'),
(4747, 52, 12, 'që, të zhytur në gënjeshtra, argëtoheshin!'),
(4748, 52, 13, 'Atë Ditë, kur me forcë do të hidhen në zjarrin e xhehennemit.'),
(4749, 52, 14, '(U thuhet atyre): “Ky është zjarri që e keni përgënjeshtruar,'),
(4750, 52, 15, 'pra, a është kjo magji, apo ju nuk shihni?'),
(4751, 52, 16, 'Përcëllohuni në të; e, duroni apo mos duroni, njësoj është për ju, - ky është ndëshkimi juaj vetëm për atë që keni bërë”.'),
(4752, 52, 17, 'Me të vërtetë, ata që kanë besuar do të gjenden në kopshtet e xhennetit dhe lumturi,'),
(4753, 52, 18, 'duke u kënaqur me atë që u ka dhënë Zoti i tyre, i ka ruajtur ata Zoti i tyre prej dënimit të skëterrës.'),
(4754, 52, 19, '(Atyre u thuhet): “Hani e pni me kënaqësi, për atë që keni punuar!”'),
(4755, 52, 20, 'Të mbështetur në divane (kanape) të renditura, do t’i martojmë ata me hyrie, sysh të bukur.'),
(4756, 52, 21, 'E, ata që kanë besuar dhe u shkojnë pas pasardhësit e tyre në besim, do t’ju bashkojmë pasardhësit e tyre dhe nuk do t’ju pakësojmë asgjë nga veprat e tyre – çdo njeri është peng (përgjegjës) për atë që ka punuar –'),
(4757, 52, 22, 'dhe do t’ju zgjërojmë dhuntitë atyre me pemë dhe mish që ju ka ënda;'),
(4758, 52, 23, 'në të (xhennet) ia zgjasin njëri-tjetrit gotat e mbushura, aty nuk do të ketë biseda të kota, as mëkate, -'),
(4759, 52, 24, 'e, do të sillen rreth tyre shërbyes (gilmanët) djelmosha të ngjashëm me margaritarin e ruajtur,'),
(4760, 52, 25, 'dhe njëri-tjetrit i drejtohen duke e pyetur,'),
(4761, 52, 26, 'e do të thonë: “Ne, më parë – në familjet tona jemi frikësuar (nga dënimi),'),
(4762, 52, 27, 'e, Perëndia na ka dhuruar mëshirë dhe na ka ruajtur nga dënimi i rëndë (i frumës helmuese).'),
(4763, 52, 28, 'Ne, më parë (në jetën tokësore) e kemi adhuruar Atë, Ai, me të vërtetë, është bamirës dhe mëshirues”.'),
(4764, 52, 29, 'Andaj, këshillo ti (o Muhammed), se ti, me dhuntinë (profetninë) e Zotit tënd, nuk je as fallxhi as i çmendur.'),
(4765, 52, 30, 'Ose të thonë ata: “(Ai – Muhammedi është) vjershtar, të presim derisa ta zhdukë vdekja”.'),
(4766, 52, 31, 'Thuaju (atyre): “Pritni, se, me të vërtetë, edhe unë jam nga ata që presin!”'),
(4767, 52, 32, 'A mos u vie atyre kjo nga mendja e tyre, apo ata janë popull kryeneç (që e kanë kaluar kufirin)?'),
(4768, 52, 33, 'Apo thonë: “Ai e ka trilluar atë (Kur’anin)”. – Jo, por ata nuk besojnë;'),
(4769, 52, 34, 'andaj, le ta sjellin ata një fjalim të ngjashëm me të, nëse thonë të vërtetën!'),
(4770, 52, 35, 'A mos vallë, ata janë krijuar pa Krijues, apo mos janë ata krijues të vetvetes?!'),
(4771, 52, 36, 'A mos vallë ata kanë krijuar qiejt dhe Tokën?! Jo, por ata nuk duan të besojnë.'),
(4772, 52, 37, 'Vallë, a mos në ta është thesari i Zotit tënd, ose, vallë ata sundojnë?'),
(4773, 52, 38, 'Vallë, a mos kanë shkallë (me të cilat ngjiten lart) e përgjojnë?! E, le të sjell (ai) përgjuesi i tyre argument të qartë!'),
(4774, 52, 39, 'Apo, për Te janë vajzat, e për ju djemtë?!'),
(4775, 52, 40, 'Vallë a mos kërkon ti prej tyre shpërblim, e që ata të jenë të ngarkuar me këtë?!'),
(4776, 52, 41, 'Ose, a mos tek ata është sekreti, e ata e përshkruajnë?!'),
(4777, 52, 42, 'Apo dëshirojnë ngatërresë, e vetëm mohuesit do të jenë të ngatërruar?'),
(4778, 52, 43, 'Ose ata kanë zot (tjetër) pos Perëndisë? Perëndia është i Lartësuar mbi atë çka i përshkruajnë Atij!'),
(4779, 52, 44, 'Dhe, sikur të shihni se po bie një copë nga qielli, do të thoshin: “Retë e grumbulluara!”'),
(4780, 52, 45, 'E, lëri ata, derisa të mos takohen me Ditën në të cilën do të shkatërrohen.'),
(4781, 52, 46, 'Ditën në të cilën, dinakëritë e tyre nu do t’u bëjnë dobi asgjë dhe kur ata nuk do t’i ndihmojë askush.'),
(4782, 52, 47, 'E, me të vërtetë, për zullumqarët ka edhe dënim tjetër pos këtij; por (këtë) shumica e tyre nuk e dijnë.'),
(4783, 52, 48, 'E, ti duro për Vendimin e Zotit tënd! Me të vërtetë, ti je nën mbrojtjen Tonë; dhe lavdëroje duke e falenderuar Zotin tënd kur të ngritesh,'),
(4784, 52, 49, 'dhe, madhëroje Ate edhe natën edhe pas perëndimit të yjeve!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Pasha yllin kur perëndon,'),
(4786, 53, 2, 'shoku juaj (Muhammedi) as nuk e ka humbë as nuk e ka shmangë rrugën e drejtë!'),
(4787, 53, 3, 'Ai nuk flet sipas qejfit të vet –'),
(4788, 53, 4, 'Ajo (që flet ai) është vetëm Shpallja, që i ka ardhur atij,'),
(4789, 53, 5, 'e mësoi atë një (engjëll) me fuqi të madhe,'),
(4790, 53, 6, 'me inteligjencë të lartë, e u paraqit (në formën e vet),'),
(4791, 53, 7, 'duek qenë në sferat e larta (qiellore),'),
(4792, 53, 8, 'pastaj u afrua, e u lëshua, -'),
(4793, 53, 9, 'afër (sa) dy harqe ose edhe më afër,'),
(4794, 53, 10, 'e ia shpalli robit të Tij (Pejgamberit) atë që ia shpalli.'),
(4795, 53, 11, 'Zemra e tij nuk e përgënjeshtroi atë që e pa,'),
(4796, 53, 12, 'vallë, a po polemizoni me te për atë që e ka parë?'),
(4797, 53, 13, 'Ai (Muhammedi), me të vërtetë, e ka parë atë (Xhebrailin) edhe një herë tjetër,'),
(4798, 53, 14, 'te Sidretul muntehaja,'),
(4799, 53, 15, 'tek ajo (Sidrea) është xhenneti – strehim,'),
(4800, 53, 16, 'kur Sidrën e mbuloi ajo që e mbulonte, (një mbulesë e madhe),'),
(4801, 53, 17, 'shikimi i tij (Muhammedit) nuku shmang, as që e kaloi (kufirin e caktuar).'),
(4802, 53, 18, 'Ai, me të vërtetë, i pa argumentet më madhështore të Zotit të tij.'),
(4803, 53, 19, 'A e keni pa Latin dhe Uzzan (si janë)?'),
(4804, 53, 20, 'Dhe Menatin, të tretin, të mbramin (sipas shkallës)? –'),
(4805, 53, 21, 'Vallë, a për ju janë djemtë, e për Ate (Perëndinë) vajzat?!'),
(4806, 53, 22, 'Kjo ndarje është e padrejtë. –'),
(4807, 53, 23, 'Ata (idhujt) janë vetëm emra që ua keni ngjitur ju dhe të parët tuaj, e Perëndia nuk ju ka dërguar për ta – kurrfarë argumenti. Ata ndjekin vetëm dyshimin dhe atë që janë të prirur; e, me të vërtetë, atyre u ka ardhur prej Zotit të tyre udhëzimi në rrugën e drejtë.'),
(4808, 53, 24, 'Ose, a për njeriun janë (të gjitha) çka dëshiron,'),
(4809, 53, 25, 'vetëm Perëndisë i përkasin (të gjitha), ajo dhe kjo botë!'),
(4810, 53, 26, 'E, sa engjëj ka në qiej – ndërmjetësimi i të cilëve nuk bën dobi asgjë, përveç pasi të japë leje Perëndia, për kë të dojë Ai dhe kush është i denjë për te.'),
(4811, 53, 27, 'Me të vërtetë, ata që nuk besojnë në jetën tjetër, i emërojnë engjëjt me emra femërorë (i konsiderojnë femra)'),
(4812, 53, 28, 'e, për këtë, ata nuk kanë kurrfarë dijenie, ndjekin vetëm hamendjen (supozimin), e hamendja nuk sjell dobi asgjë për të vërtetën.'),
(4813, 53, 29, 'Andaj, ti (o Muhammed) largohu prej atij që i ikën Kur’anit dhe që dëshiron vetëm jetën e kësaj bote, -'),
(4814, 53, 30, 'kjo është arritja e tyre në dijeni, me të vërtetë, Zoti yt i njeh më së miri ata që janë shmangur nga rruga e Tij, dhe Ai i di më së miri ata që ndjekin rrugën e drejtë.'),
(4815, 53, 31, 'Perëndisë i përkasin të gjitha (çka ka) në qiej dhe në Tokë – e, sipas asaj që kanë punuar, për t’i dënuar ata që bëjnë keq, e për t’i shpërblyer ata që bëjnë mirë me shpërblim shumë të mirë, të madh,'),
(4816, 53, 32, 'ata që ruhen prej mëkateve të mëdha dhe veprave të këqia (imorale), pos mëkateve të vogla. Me të vërtetë, Zoti yt është i gjerë (i pakufishëm) në falje. Ai, më së miri di për ju, qysh se u ka krijur prej dheut dhe qysh se keni qenë embion në barkun e nënave tuaja; andaj, mos u lavdëroni me pafajshmërinë tuaj – Ai e njeh mirë atë që ruhet nga mëkatet.'),
(4817, 53, 33, 'A e ke parë ti atë që është shmangur (nga e vërteta),'),
(4818, 53, 34, 'pak ka dhënë, e pastaj e pengoi tjetrën,'),
(4819, 53, 35, 'a i di ai gjërat sekrete, dhe a i sheh ato?'),
(4820, 53, 36, 'Vallë, a nuk është i lajmëruar ai – për atë që gjendet në fletët e Musait (në Teurat)'),
(4821, 53, 37, 'dhe të Ibrahimit: - “i cili i plotësoi (obligimet) –'),
(4822, 53, 38, 'që askush nuk do t’i bartë mëkatet e personit tjetër,'),
(4823, 53, 39, 'dhe që njeriu do të ketë vetëm atë që është marrë me te (punuar atë)'),
(4824, 53, 40, 'dhe që përpjekjet e tij do të shihen së shpejti, padyshim'),
(4825, 53, 41, '(dhe se) do të shpërblehet me shpërblim të plotë,'),
(4826, 53, 42, 'dhe, se, te Zoti yt do të jetë kthimi,'),
(4827, 53, 43, 'Ai jep gazin dhe vajin (gëzimin dhe hidhërimin),'),
(4828, 53, 44, 'Ai jep vdekjen dhe gjallesën,'),
(4829, 53, 45, 'Ai krijon çiftet, mashkull e femër,'),
(4830, 53, 46, 'prej pikës së farës (spermës) kur të hedhet (injektohet),'),
(4831, 53, 47, 'dhe, me të vërtetë, tek Ai është krijimi i dytë (rindjallja),'),
(4832, 53, 48, 'dhe Ai jep pasuri dhe kënaqësi të mjaftueshme,'),
(4833, 53, 49, 'dhe Ai është Zot’i Siriusit,'),
(4834, 53, 50, 'dhe Ai e ka shkatërruar popullin e lashtë të Adit,'),
(4835, 53, 51, 'dhe Themudin, dhe s’ka lënë askë prej tyre,'),
(4836, 53, 52, 'e madje, para tyre – popullin e Nuhut, i cili ka qenë (edhe) më zullumqarë dhe i zhytur edhe më shumë (në të këqia),'),
(4837, 53, 53, 'dhe Ai i përmbysi vendbanimet (e popullit të Lutit),'),
(4838, 53, 54, 'dhe i mbuloi ata çka i mbuloi (goditje e madhe),'),
(4839, 53, 55, 'e, në çfarë dhuntie të Zotit tënd dyshon ti (polemizon)?'),
(4840, 53, 56, 'Ky paralajmërues (pejgamber) është si ata të mëparshmit.'),
(4841, 53, 57, 'U afrua ajo që është afër (Dita e Kijametit),'),
(4842, 53, 58, 'nuk mund ta zbulojë (atë) askush, pos Perëndisë!'),
(4843, 53, 59, 'E, vallë prej këtyre fjalëve (Kur’anit) po çuditeni,'),
(4844, 53, 60, 'dhe po qeshni, e nuk po qani? –'),
(4845, 53, 61, 'e, adje, jeni edhe të pakujdesshëm!'),
(4846, 53, 62, 'Bëni sexhde për Perëndinë dhe adhuronie (Ate)!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'U afrua Çasti (Dita e Kijametit) dhe u ça Hëna!'),
(4848, 54, 2, 'E ata, sa herë shohin mrekulli, kthehen dhe thonë: “Magji e përhershme!”'),
(4849, 54, 3, 'Ata e përgënjeshtrojnë (të vërtetën) dhe pasojnë kënaqësitë e tyre, e çdo gjë, madje, është e caktuar.'),
(4850, 54, 4, 'Dhe, u patën ardhë atyre, me të vërtetë, lajme të cilat shpiejnë frikë,'),
(4851, 54, 5, '(Kur’ani është) dijeni e përsosur – por paralajmërimi nuk u bëri dobi,'),
(4852, 54, 6, 'andaj, shmangu prej tyre! Në Ditën kur do t’i thërret ta kasneci (ftuesi) në diçka të tmerrshme –'),
(4853, 54, 7, 'ata, me sy të përulur dalin nga varret, thua se janë karkaleca të shpërndarë,'),
(4854, 54, 8, 'duke shpejtuar kah kasneci, e atëherë thonë mohuesit: “Kjo është ditë e vështirë!”'),
(4855, 54, 9, 'Para tyre (mekkasve) ka përgënjeshtruar populli i Nuhut: mohonin robin Tonë (Nuhun) dhe thonin: “I çmendur!” – dhe e pengonin (në tregimin e Shpalljes).'),
(4856, 54, 10, 'Dhe, iu lut Zotit të vet: “Unë jam i mundur (prej popullit tim), andaj Më ndihmo!”'),
(4857, 54, 11, 'Dhe, Ne ia kemi hapur dyert e qiellit me ujë (shi) të rrëmbyeshëm,'),
(4858, 54, 12, 'dhe, i kemi hapur burimet e tokës, e u takuan ujërat për një veprim që ka qenë i caktuar.'),
(4859, 54, 13, 'E, atë e bartëm (në barkën) prej dërrasave dhe gozhdave'),
(4860, 54, 14, 'e cila lundronte nën kujdesin Tonë, - ky ishte shpërblim për atë që ishte i përgënjeshtruar.'),
(4861, 54, 15, 'Dhe, Ne atë e kemi lënë si dokument, e a ka kush që këshillohet (me të)?'),
(4862, 54, 16, 'dhe, çfarë ka qenë dënimi Im dhe paralajmërimet e Mia?!'),
(4863, 54, 17, 'Në të vërtetë, Ne Kur’anin e kemi bërë të lehtë për këshillim, – e a ka kush që këshillohet (me të)?'),
(4864, 54, 18, 'Dhe, Adi ka përgënjeshtruar (të vërtetën), - e çfarë ka qenë dënimi Im dhe paralajmërimet e Mia?!'),
(4865, 54, 19, 'Ne u dërguam atyre në një ditë fatkeqe frymë të stuhieshme – të pandërprerë –'),
(4866, 54, 20, 'e cila i nxirrte njerëzit (prej strehimeve të tyre), thuase të ishin rrënjë të shkulura të hurmeve,'),
(4867, 54, 21, 'e, çfarë ka qenë dënimi Im dhe paralajmërimet e Mia?!'),
(4868, 54, 22, 'Në të vërtetë, Ne, Kur’anin e kemi bërë të lehtë për këshillim, - e a ka kush që këshillohet (me të)?'),
(4869, 54, 23, 'Dhe Themudi i përgënjeshtroi paralajmëruesit Tonë –'),
(4870, 54, 24, 'ata thanë: “Vallë, a ta pasojmë një njeri nga mesi ynë?! Atëherë, ne, me të vërtetë, do të ishim në rrugë të humbur dhe të çmendur.'),
(4871, 54, 25, 'Vallë, në mesin (e sa) prej nesh, t’i dërgohet Shpallja atij?! Jo, ai është gënjeshtar mendjemadh!”'),
(4872, 54, 26, 'Nesër (së shpejti), do ta dinë ata se kush është gënjeshtari mendjemadh!'),
(4873, 54, 27, 'Ne, do ta dërgojmë devën për t’i sprovuar, andaj priti ti ata dhe bëhu i durueshëm.'),
(4874, 54, 28, 'Dhe, lajmëroi ata se uji do të ndahet në mes tyre e devës, në çdo racion (shujtë) do të jetë prezent (ai që e ka rendin)!'),
(4875, 54, 29, 'Ata e thirrën shokun e tyre, e ai mori guximin dhe e preu atë (devën) –'),
(4876, 54, 30, 'e, çfarë ka qenë dënimi Im dhe paralajmërimet e Mia?!'),
(4877, 54, 31, 'Ne e dërguam te ta një ulurimë e ata u bënë si sana e copëtuar në pleme,'),
(4878, 54, 32, 'në të vërtetë, Ne Kur’anin e kemi bërë të lehtë (të kuptueshëm) pë këshillim, e a ka kush që këshillohet (me të)?'),
(4879, 54, 33, 'Edhe populli i Lutit i ka përgënjeshtruar paralajmërimet.'),
(4880, 54, 34, 'Ne, me të vërtetë, e kemi dërguar një fortunë mbi ta, pos në familjen e Lutit, këtë e shpëtuam në agim,'),
(4881, 54, 35, 'nga dhuntia Jonë. Ja, kështu Ne i shpërblejmë ata që falenderojnë.'),
(4882, 54, 36, 'Me të vërtetë, ai (Luti) i paralajmëroi ata me fuqinë e dënimit Tonë, e ata polemizuan duke i mohuar paralajmërimet.'),
(4883, 54, 37, 'At aprej tij i kërkuan mysafirët e tij, e Ne ua fshimë sytë atyre (i verbuam): “Shijoni dënimin Tim dhe paralajmërimin Tim!”'),
(4884, 54, 38, 'E, të nesërmen në mëngjes, ata i goditi dënimi i përhershëm.'),
(4885, 54, 39, '“Shijoni dënimin Tim dhe paralajmërimin Tim!”'),
(4886, 54, 40, 'Me të vërtetë, Ne Kur’anin e kemi bërë të lehtë për (ta mësuar përmendësh) këshillim, e a ka kush që këshillohet (me të)?'),
(4887, 54, 41, 'Edhe njerëzve të Faraonit u patën ardhur paralajmërimet,'),
(4888, 54, 42, 'por ata i mohuan të gjitha argumentet Tona, andaj Ne i dënuam ata, ashtu siç dënon i Plotëfuqishmi dhe i Plotëpushtetshmi.'),
(4889, 54, 43, 'A mos vallë mohuesit tuaj (Mekkasit) janë më të mirë se ata (që u përmendën), apo ju keni ndonjë dekret në librat e shpallur?'),
(4890, 54, 44, 'Apo, ata thonë: “Ne jemi grup i pathyeshëm!”'),
(4891, 54, 45, 'Së shpejti do të thyhet grupi dhe me të vërtetë, mëkatarët do të jenë të humbur dhe në zjarr (në jetën tjetër) është më i tmerrshëm dhe më i idhët (se dënimi në këtë botë).'),
(4892, 54, 46, ','),
(4893, 54, 47, 'Me të vërtetë, mëkatarët do të jenë të humbur dhe në zjarr (në jetën tjetër).'),
(4894, 54, 48, 'Ditën që do të tërhiqen (me forcë) fytyrat e tyre në zjarr (e ju thuhet): “Shijoni zjarrin e xhehennemit!”'),
(4895, 54, 49, 'Ne, me të vërtetë, të gjitha i kemi krijuar sipas dispozitës,'),
(4896, 54, 50, 'Urdhëri Ynë është vetëm një fjalë (në kryerjen e punëve – i shpejtë) sa çel e mbyllë sytë.'),
(4897, 54, 51, 'E, Ne, madje i kemi shkatërruar të ngjashmit me ju, - e a ka kush që këshillohet (me të)?!'),
(4898, 54, 52, 'E, çkado që punojnë, ato janë në librat (e veprave të njerëzve)'),
(4899, 54, 53, 'të gjitha, të mëdha dhe të vogla, janë shënuar në rreshta.'),
(4900, 54, 54, 'Ata që i druajnë Perëndisë me të vërtetë, do të jenë në kopshtet e xhennetit dhe pranë lumenjve'),
(4901, 54, 55, 'në vendin e kënaqësisë, te Sunduesi i Plotëfuqishëm.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'Bamirësi i Gjithmbarshëm,'),
(4903, 55, 2, 'ua ka mësuar Kur’anin,'),
(4904, 55, 3, 'ka krijuar njeriun,'),
(4905, 55, 4, 'ia ka mësuar të shprehurit (e mendimeve dhe ndjenjave).'),
(4906, 55, 5, 'Dielli dhe Hëna (lëvizin) sipas llogarisë (rrugës) së caktuar,'),
(4907, 55, 6, 'edhe bimët (e imëta) dhe drunjt i përulen (Perëndisë),'),
(4908, 55, 7, 'e qiellin e ka ngritur dhe ka vënë masë (drejtësinë),'),
(4909, 55, 8, 'për të mos kaluar (kufirin) në matje –'),
(4910, 55, 9, 'dhe matni drejt e mos mungoni në teresi!'),
(4911, 55, 10, 'Tokën e ka bërë (shtruar) për krijesa,'),
(4912, 55, 11, 'në të ka pemë të llojllojshme dhe hurme me frute (në gëzhojë),'),
(4913, 55, 12, 'dhe drithëra me gjethe dhe bimë me erë të mirë, -'),
(4914, 55, 13, 'e, cilat dhunti të Zotit tuaj (o njerëz dhe xhindë) po i përgënjeshtroni (mohoni)?!'),
(4915, 55, 14, 'Ai e krijoi njeriun prej baltës së thatë, ashtu siç është vorba e pjekur,'),
(4916, 55, 15, 'e xhindët i krijoi prej flakës së zjarrit –'),
(4917, 55, 16, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4918, 55, 17, '(Ai është) Zoti i dy lindjee dhe i dy perëndimeve (dy solsticeve),'),
(4919, 55, 18, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4920, 55, 19, 'I ka lëshuar dy dete që të takohen,'),
(4921, 55, 20, 'e, në mes tyre ka stom (pengesë), andaj njëri nuk e mbizotëron tjetrin (nuk përzihen) –'),
(4922, 55, 21, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4923, 55, 22, 'Prej tyre nxirren margaritarë dhe korale (merxhanë) –'),
(4924, 55, 23, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4925, 55, 24, 'Atij i përkasin edhe anijet që lundrojnë nëpër dete dhe krijonë dallgë si malet,'),
(4926, 55, 25, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4927, 55, 26, 'Çdo gjë që është në Tokë është e kalueshme (do të shkatërrohet),'),
(4928, 55, 27, 'e mbetet vetëm Zoti yt, i Madhërueshmi dhe Bamirësi –'),
(4929, 55, 28, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4930, 55, 29, 'Prej Tij kërkojnë çka ka në qiej dhe në tokë; në çdo moment Ai është në gjendje (të krijojë, të shpërblejë, të zhdukë), -'),
(4931, 55, 30, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4932, 55, 31, 'Do t’ju qëllojmë juve (për shpërblim dhe llogari), o ju, dy barrët e Tokës (njerëzit dhe xhindët)?! -'),
(4933, 55, 32, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4934, 55, 33, 'O grpe të xhindëve dhe njerëzve, nëse mund të dilni prej kufijve të qiejve dhe të Tokës, dilni, por do të mund të dilni vetëm me fuqi të madhe (e këtë nuk e keni, andaj nuk mund të ikni)!'),
(4935, 55, 34, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4936, 55, 35, 'Kundër jush do të dërgohet flaka e zjarrit dhe bakër i shkrirë dhe ju nuk do të mund të mbroheni, -'),
(4937, 55, 36, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4938, 55, 37, 'E kur të çahet qielli dhe të bëhet i kuq si trëndafilja dhe si vaji i shkrirë,'),
(4939, 55, 38, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4940, 55, 39, 'atë Ditë njerëzit dhe xhindët nuk do të pyetën për mëkatet e tyre, -'),
(4941, 55, 40, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4942, 55, 41, 'e mëkatarët atëherë, do të njihen sipas pamjes (së fytyrës së tyre) dhe do të kapen për flokë (kacule) dhe për këmbë.'),
(4943, 55, 42, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4944, 55, 43, '(Atyre u thuhet): “Ja, ky është xhehennemi të cilin e përgënjeshtronin mëkatarët!'),
(4945, 55, 44, 'Ata do të sillen në mes zjarrit dhe ujit të vëluar ë ka arritur shkallën më të lartë të vëlimit!'),
(4946, 55, 45, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4947, 55, 46, 'E, për ata që i druajnë paraqitjes para Zotit të vet, do të kenë dy xhennete,'),
(4948, 55, 47, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4949, 55, 48, '(dy xhennete) me drunj të degëzuar,'),
(4950, 55, 49, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4951, 55, 50, 'në të ka burime që rrjedhin'),
(4952, 55, 51, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4953, 55, 52, 'në ta – prej çdo lloj peme, ka nga dy lloje, -'),
(4954, 55, 53, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4955, 55, 54, '(ata do të jenë) të mbështetur në shtrojet, astari i të cilave është prej mëndafshi të trashë, e frytet e dy xhenneteve do të jenë të afërta (për t’i kapë me dorë),'),
(4956, 55, 55, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4957, 55, 56, 'Në ta, do të jenë ato (virxhëreshat), me sy ulur, të cilat – para tyre, nuk kanë pasur takim as me njerëz as me xhindë,'),
(4958, 55, 57, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4959, 55, 58, 'ato do të jenë si rubin (gurë të çmueshëm) dhe merxhanë (korale),'),
(4960, 55, 59, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4961, 55, 60, 'Vallë, për veprën e mirë, a ka tjetër shpërblim pos të mirë?!'),
(4962, 55, 61, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4963, 55, 62, 'E, nën këto dy (xhennete) janë edye dy xhennete (tjera), -'),
(4964, 55, 63, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4965, 55, 64, '(Këta dy janë) të stolisur me gjelbërim,'),
(4966, 55, 65, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4967, 55, 66, 'në to janë dy burime (të përhershme) fiskishë (që hedhin lart ujin),'),
(4968, 55, 67, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4969, 55, 68, 'në to ka pemë, hurme dhe shega,'),
(4970, 55, 69, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4971, 55, 70, 'në to ka bukuroshe virtytesh të mrekullueshme,'),
(4972, 55, 71, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4973, 55, 72, 'hyritë (e xhennetit) të kufizuara më shatorra,'),
(4974, 55, 73, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4975, 55, 74, 'të cilat – para tyre, nuk kanë pasur takim as me njerëz as me xhindë, -'),
(4976, 55, 75, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4977, 55, 76, 'Ata do të rrinë mbështetur në shtroje të gjelbëra, të mrekullueshme dhe të bukura, -'),
(4978, 55, 77, 'e, cilat dhunti të Zotit tuaj po i përgënjeshtroni?!'),
(4979, 55, 78, 'Është i lartësuar emri i Zotit tënd, të Madhërueshëm dhe Bamirës!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Kur të ndodhë Ngjarja (Dita e Kijametit), -'),
(4981, 56, 2, 'të ndodhurit e saj askush nuk do ta mohojë,'),
(4982, 56, 3, 'e cila i ulë (të këqinjtë), i lartëson (të mirët),'),
(4983, 56, 4, 'kur Toka të dridhet fuqishëm,'),
(4984, 56, 5, 'dhe malet të derdhën copë-copë,'),
(4985, 56, 6, 'dhe shndërrohen në pluhur të shpërndarë,'),
(4986, 56, 7, 'kur ju njerëzit, do të ndaheni në tri grupe:'),
(4987, 56, 8, 'ata të lumturit – kush janë të lumturit?!'),
(4988, 56, 9, 'dhe ata fatkëqinjtë – kush janë fatkëqinjtë?!'),
(4989, 56, 10, 'Ata që kanë prirë (në besim), do të prijnë (edhe në xhennet),'),
(4990, 56, 11, 'ata janë të afërt'),
(4991, 56, 12, 'në kopshtet e xhennetit e të kënaqësisë,'),
(4992, 56, 13, '(e këta janë) shumë prej të parëve,'),
(4993, 56, 14, 'e pak prej të mëvondshmëve, -'),
(4994, 56, 15, 'në divanet e stolisura (me gurë të çmueshëm),'),
(4995, 56, 16, 'të mbështetur në to dhe të kthyer njëri karshi tjetrit;'),
(4996, 56, 17, 'do t’u sillen rreth tyre (për t’ju shërbyer) djelmoshat përherë të rinj,'),
(4997, 56, 18, 'me kupa, ibrikë dhe shishe (pijesh) prej burimit rrjedhës,'),
(4998, 56, 19, 'prej të cilit nuk do t’u dhembë koka dhe nuk do t’u humbë mendja, -'),
(4999, 56, 20, 'dhe pemë të cilat do t’i zgjedhin vetë,'),
(5000, 56, 21, 'dhe mish të shpendëve çfarë ju ka ënda.'),
(5001, 56, 22, 'Në to (xhennete) ka edhe hyrie (të bukura) sy-mëdha,'),
(5002, 56, 23, 'të ngjashme margaritarit të ruajtur,'),
(5003, 56, 24, 'shpërblim për atë që kanë punuar.'),
(5004, 56, 25, 'Në ta nuk do të dëgjojnë biseda të kota as fjalë mëkatësh,'),
(5005, 56, 26, 'por vetëm fjalët (përshëndetjet): “Selam, selam!” (Paqë, paqë)!'),
(5006, 56, 27, 'E, ata të lumturit – kush janë të lumturit?!'),
(5007, 56, 28, 'Do të gjenden në mes lotusve (lloj druri) pa gjemba,'),
(5008, 56, 29, 'dhe babanave të mbushura me frute'),
(5009, 56, 30, 'dhe në freskinë e gjerë,'),
(5010, 56, 31, 'pranë ujit rrjedhës,'),
(5011, 56, 32, 'dhe mes pemëve të llojllojshme,'),
(5012, 56, 33, 'të cilat kurrë nuk sosen, as nuk janë të ndaluara (për t’i marrë),'),
(5013, 56, 34, 'dhe në shtrojet e ngritura (bashkëshorte virtytesh të larta).'),
(5014, 56, 35, 'Ne i kemi krijuar ato në formë të re,'),
(5015, 56, 36, 'dhe i kemi bërë ato virxhëresha,'),
(5016, 56, 37, 'të dashura për bashkëshortët e tyre dhe moshatare me ta –'),
(5017, 56, 38, '(këto janë) për ata të lumturit.'),
(5018, 56, 39, '(këta – të mirët) janë shumë prej (popujve) të lashtëve'),
(5019, 56, 40, 'e shumë edhe prej të mëvonshmëve.'),
(5020, 56, 41, 'E, ata fatkëqinjtë – cilët janë fatkëqinjtë?!'),
(5021, 56, 42, 'Ata (që) do të jenë në frymën e zjarrit dhe të ujit të vëluar'),
(5022, 56, 43, 'dhe në hijen e tymit të zi,'),
(5023, 56, 44, 'në të cilën nuk do të ketë freski as kurrfarë të mire.'),
(5024, 56, 45, 'Ata, para kësaj (në këtë botë), kanë jetuar në luks,'),
(5025, 56, 46, 'dhe me këmbëngulje bënin mëkate të rënda,'),
(5026, 56, 47, 'dhe thonin: “Vallë, pasi të vdesim e të bëhemi tokë e eshtra, a, me të vërtetë, do të ringjallemi,'),
(5027, 56, 48, 'vallë, edhe të parët tonë të lashtë?!'),
(5028, 56, 49, 'Thuaju (o Muhammed!): “Me të vërtetë, edhe të parët edhe të mbramët, -'),
(5029, 56, 50, 'do të tubohen në një kohë (të caktuar), të ditës së njohur,'),
(5030, 56, 51, 'dhe, atëherë ju, o të humbur, që përgënjeshtroni ringjalljen,'),
(5031, 56, 52, 'me siguri do të hani nga druri i zekkumit,'),
(5032, 56, 53, 'dhe me te do të mbushni stomakun,'),
(5033, 56, 54, 'e pastaj në të, do të pini ujë të vëluar,'),
(5034, 56, 55, 'dhe do të pini ashtu siç pin devja e etshme,'),
(5035, 56, 56, 'kjo (që u përmend) do të jetë pozita e tyre në Ditën e Kijametit!'),
(5036, 56, 57, 'Ne, u krijuam juve – e, sikur të kishit besuar!'),
(5037, 56, 58, 'A po shihni ju farën (spermën) që e hedhni (injektoni)'),
(5038, 56, 59, 'a po e krijoni (dhe zhvilloni njeriun ju), apo Ne jemi krijues?'),
(5039, 56, 60, 'Ne e caktojmë vdekjen tuaj dhe askush nuk mund të na pengojë,'),
(5040, 56, 61, 'që t’ju ndryshojmë figurën tuaj dhe t’ju krijojmë në figurën çfarë ju nuk e dini.'),
(5041, 56, 62, 'Ju, madje, e dini krijimin e parë, e pse nuk po këshilloheni?!'),
(5042, 56, 63, 'A shihni atë që po e mbillni?'),
(5043, 56, 64, 'A mos ju jepni, që prej saj të mbijë (bima), apo Ne japim që të mbijë ajo?'),
(5044, 56, 65, 'Nëse duam Ne, Ne do ta thërrmojmë atë (bimën) dhe ju do të çuditeni,'),
(5045, 56, 66, '(dhe, do të thoni): “Ne, me të vërtetë, jemi të dëmtuar,'),
(5046, 56, 67, 'madje – na është penguar (furnizimi)!”'),
(5047, 56, 68, 'A po e shihni (atë) ujë që e pini –'),
(5048, 56, 69, 'a mos e lëshoni ju prej réve apo Ne e lëshojmë?!'),
(5049, 56, 70, 'Nëse duam Ne, Ne do ta bëjmë atë të njelmët – e përse nuk falenderoheni?!'),
(5050, 56, 71, 'A po e shihni atë zjarr, që e ndezni –'),
(5051, 56, 72, 'a mos e keni bërë ju drurin e saj, apo Ne e kemi bërë?!'),
(5052, 56, 73, 'Ne, e kemi bërë zjarrin përkujtues (të zjarrit të skëterrës) dhe që t’u sjellë dobi nevojtarëve,'),
(5053, 56, 74, 'andaj lavdëroje emrin e Zotit tënd të Madhërueshëm!'),
(5054, 56, 75, 'Dhe, betohu në çastin e perëndimit të yjeve,'),
(5055, 56, 76, 'e ky është, betim i madh, nëse e dini, -'),
(5056, 56, 77, 'ky (shpjeguesi i këtyre), është, me të vërtetë, Kur’ani i nderuar,'),
(5057, 56, 78, 'në Librin e ruajtur –'),
(5058, 56, 79, 'le ta prekin atë vetëm të pastërtit –'),
(5059, 56, 80, '(ai është) Shpallje prej Zotit të Gjithësisë.'),
(5060, 56, 81, 'E, vallë, ju këtë fjalë (Kur’anin) nuk e përfillni?'),
(5061, 56, 82, 'dhe, në vend të mirënjohjes për furnizimin e dhuruar, që u është dhënë – ju, në të vërtetë, po e përgënjeshtroni atë.'),
(5062, 56, 83, 'E, përse kur t’ju vie shpirti në fyt,'),
(5063, 56, 84, 'dhe kur ju atëherë e shikoni atë,'),
(5064, 56, 85, 'e ne jemi më afër atij se ju, por ju nuk shihni, -'),
(5065, 56, 86, 'e sikur të ishit ju të fuqishëm (e nuk sundon tjetërkush)'),
(5066, 56, 87, 'atëherë do t’ia kthenit (shpirtin atij), nëse thoni të vërtetën?'),
(5067, 56, 88, 'Dhe, nëse është (ai që është në agoni) nga ata që janë afër te Perëndia –'),
(5068, 56, 89, '(për te ka) komoditet, mëshirë dhe furnizim të madh dhe kopshtet e dhuntive!'),
(5069, 56, 90, 'E, nëse është (ai që është në agoni) nga të lumturit –'),
(5070, 56, 91, 'e, përshëndetje për ty nga ata që janë të lumtur!'),
(5071, 56, 92, 'E, nëse është (ai që është në agoni) nga ata që kanë përgënjeshtruar – të humburve –'),
(5072, 56, 93, 'ata do të gostiten me ujë të vëluar –'),
(5073, 56, 94, 'dhe përcëllim në zjarr të xhehennemit.'),
(5074, 56, 95, 'Në të vërtetë, e gjithë kjo, është e vërtetë e saktë.'),
(5075, 56, 96, 'Andaj lavdëroje emrin e Zotit tënd të Madhërueshëm!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Perëndinë e falenderon çdo gjë që është në qiej dhe në Tokë, e Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(5077, 57, 2, 'Pushteti i Tij është në qiej dhe në Tokë; jeta dhe vdekja janë vepër e Tij, dhe Ai është i pushtetshëm për çdo gjë.'),
(5078, 57, 3, 'Ai është i Pari dhe i Mbrami, edhe i Dukshmi edhe i Padukshmi, dhe Ai është i dijshëm për çdo gjë!'),
(5079, 57, 4, 'Ai ka krijuar qiejt dhe Tokën për gjashtë ditë (periudha kohore), dhe pastaj mbisundoi Arshin (Gjithësinë); Ai di se çka hyn në Tokë dhe çka del prej saj, çka zbret prej qiellit dhe çka ngritet kah ai. Ai është me ju (u sheh) kudo që gjendeni, dhe çkado që punoni, Perëndia i sheh (të gjitha) çka punoni.'),
(5080, 57, 5, 'Pushteti i Tij ësht ënë qiej dhe në Tokë, dhe te Perndia do të kthehen të gjitha!'),
(5081, 57, 6, 'Ai e shtie natën në ditë dhe ditën në natë, dhe ai e di mendimin e çdokujt!'),
(5082, 57, 7, 'Besoni Perëndinë dhe të Dërguarin e Tij dhe shpenzoni nga ato çka Ai ju ka lënë në dispozicion juve, se ata që kanë besuar nga ju dhe kanë shpenzuar (për të mirë), për ta ka shpërblim të madh!'),
(5083, 57, 8, 'Çka keni që nuk besoni në Perëndinë? E i Dërguari i Tij ju fton që ta besoni Zotin tuaj, kurse ai prej jush ka marrë obligimin – nëse dëshroni të jeni besimtarë.'),
(5084, 57, 9, 'Ai, robit të Vet i ka zbritur fakte të qarta për t’ju nxjerrë nga errësira në dritë – e Perëndia ndaj jush, me të vërtetë, është i mirë dhe i mëshirshëm.'),
(5085, 57, 10, 'Dhe, ç’keni që nuk shpenzoni në rrugën e Perëndisë, pasi që qiejt dhe Toka i mbetën trashëgim Perëndisë? Nuk janë të barabartë ata që kanë shpenzuar dhe luftuan para fitores – dhe ata që kanë luftuar dhe shpenzuar pastaj. Këtu, dallimi është në një shkallë të lartë; mirëpo, Perëndia të gjithëve u premton dhuntinë. – Perëndia di mirë atë që punoni ju.'),
(5086, 57, 11, 'Kush do t’i jap vullnetarisht Perëndisë hua – të mirë – që Ai pastaj t’ia kthejë shumëfish, e madje, për të ka shpërblim fisnik.'),
(5087, 57, 12, 'në Ditën që do të shohish besimtarët dhe besimtaret, që para tyre dhe në të djahtë ecë dritë e tyre (e ju thuhet): “Gëzim (ka) sot për ju: kopshtet e xhennetit nëpër të cilët rrjedhin lumenjtë, në të cilët do të banoni përgjithmonë. Ky është sukses i madh!'),
(5088, 57, 13, 'Në Ditën kur hipokritët dhe hipokritet do t’u flasin besimtarëve: “Pritna neve, që të shpërblehemi me dritën tuaj!” – u përgjigjet (atyre): “Kthehuni prapa, e kërkoni tjetër dritë!” Dhe, ndërmjet tyre do të vendose barriera (pengesa) që ka derë; brenda saj ka mëshirë, kurse jashtë saj ka vuajtje.'),
(5089, 57, 14, 'A nuk kemi qenë me ju? – thërrasin ata. – “Po” – përgjigjen, - por ju e keni komprometuar veten dhe keni pritur, edhe keni dyshuar, dhe dëshirat e kota ju kanë mashtruar, përderisa ka arritur urdhëri i Perëndisë, e djalli u ka mashtruar ndaj Perëndisë!'),
(5090, 57, 15, 'Sot, prej jush nuk pranohet kurrfarë kompensimi, e as prej atyre që nuk kanë besuar: zjarri është vendbanimi juaj, ai është për ju, e sa vendbanim i tmerrshëm që është ai!”'),
(5091, 57, 16, 'A nuk është koha që besimtarëve t’u zbutet zemra, kur përmendet Perëndia dhe e Vërteta (Kur’ani) që është reveluar (shpallur), dhe mos të bëhen ata sikur atyre që qysh më parë iu është dhënë Libri, por zemrat e tyre, me kalimin e një kohe të gjatë – u forcuan (në të keqe), dhe shumë prej tyre janë mëkatarë.'),
(5092, 57, 17, 'Dijeni, se Perëndia, me të vërtetë, e ngjallë tokën pas vdekjes së saj! Na ju sqarojmë argumentet për të kuptuar ju!'),
(5093, 57, 18, 'At dhe ato që japin lëmoshë dhe vullnetarisht i japin hua Perëndisë, do t’u kthehet shumëfish dhe për ta ka shpërblim fisnik.'),
(5094, 57, 19, 'Ata që besojnë Perëndinë dhe profetët e Tij, ata janë njerëz të drejtë dhe dëshmitarë te Zoti i tyre dhe do të shpërblehen dhe do të kenë dritë. Kurse, ata që nuk janë besimtarë dhe i mohojnë argumentet Tona, do të jenë banorë të zjarrit!'),
(5095, 57, 20, 'Dijeni që jeta në këtë botë nuk është kurrgjë tjeër veçse lojë dhe dëfrim, dhe zbukurim (stolisje) dhe mburrje ndërmjet jush dhe vrapim kah pasuria dhe numri i fëmijëve! Kjo i shembëllen bimës, e cila pas hiut rritet dhe entuziazmon bujqit, e ajo pastaj gufon, po më vonë e sheh të zverdhur (vyshkur), që më në fund (të shkatërrohet). Kurse, në botën tjetër ka vuajtje të mëdha dhe amnesti nga Perëndia dhe kënaqësi nga Ai; jeta në këtë botë është vetëm kënaqësi e mashtruar.'),
(5096, 57, 21, 'Bëni gara që te Perëndia i juaj të meritoni amnestinë parajsën e gjerë sa qielli dhe Toka, që është e përgatitur për ata që i kanë besuar Perëndisë dhe profetëve të Tij. Ajo është dhurata e Perëndisë që ia jep Ai kujt të dojë; e Perëndia është pronar i dhuntisë së madhe!'),
(5097, 57, 22, 'S’ka mjerim (fatkeqësi) që e godet Tokën dhe juve, përveç asaj që ëshë shënur më parë në Libër, - kjo për Perëndinë, me të vërtetë, është lehtë!'),
(5098, 57, 23, 'Për të mos u dëshpëruar për atë që ju ka kaluar, por edhe për të mos u gëzuar së tepërmi për atë që Ai ju ka dhënë juve, Perëndia nuk i don lavdëruesit dhe vetmburrësit,'),
(5099, 57, 24, 'ata që janë koprracë dhe nxisin edhe të tjerët në koprraci. E ata që i shmangën urdhërit, me të vërtetë, Perëndia është i pavarur (i pasur) dhe i lavdërueshëm.'),
(5100, 57, 25, 'Me të vërtetë, Ne i kemi dërguar profetët Tonë me argumente të qarta dhe me ta u kemi dhënë librin dhe terezitë, (drejtësinë): që njerëzit të veprojnë drejt; kurse hekurin e kemi krijuar, se, në atë ka tmerr të madh, po edhe dobi për njerëzit dhe që Perëndia të dëftojë ata që ndihmojnë besimin e Tij dhe të dërguarit e Tij – pa i parë. Me të vërtetë, Perëndia është i fuqishëm dhe i madhërueshëm.'),
(5101, 57, 26, 'Me të vërtetë, e kemi dërguar Nuhun dhe Ibrahimin dhe disa trashëgimtarë të tyre, që iu kemi shpallur profetninë dhe iu kemi dërguar Librin. Disa prej tyre kanë qenë në rrugë të drejtë, kurse shumica prej tyre kanë qenë ngatërrestarë.'),
(5102, 57, 27, 'Pastaj pas gjurmëve të tyre – ju kemi dërguar profetët Tonë, dhe pastaj kemi dërguar Isanë, të birin e Merjemës, të cilit ia dhamë Inxhilin, kurse në zemrat e ithtarëve të tij kemi mbjellë (frymëzuar) butësi dhe mëshirë, ndërsa, ata vetë e kanë trilluar jetën (lutëse – të tërhequr prej shoqërisë dhe jetës normale), për të arritur kënaqësinë e Perëndisë. Ne nuk i kemi obliguar ata për këtë, por ata nuk u kujdesën për atë si duhet. Ata të cilët kanë besuar si duhet – i shpërblejmë, e shumë prej tyre janë ngatërrestarë.'),
(5103, 57, 28, 'O ju besimtarë, - ruajuni Perëndisë dhe besoni Profetin e Tij, Ai do t’ju dhurojë mëshirë të dyfishtë, dhe do t’u japë dritë me ndihmën e së cilës do të ecni dhe do t’ju falë (gabimet); Perëndia është falës dhe mëshirues –'),
(5104, 57, 29, 'dhe le ta dinë ithtarët e Librit, se ata, kurrfarë dhuntie të Perëndisë nuk do të fitojnë (marrin), pasi që dhuntia është vetëm në dorën e Perëndisë – Ai ia dhuron kujt të dojë. E Perëndia është pronar i mirësisë së madhe.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'Perëndia i ka dëgjuar fjalët e asaj që u ankua në burrin e vet dhe i rrënkohej Perëndisë. Perëndia dëgjon bisedat tuaja. Se, Perëndia, me të vërtetë, i dëgjon dhe i sheh të gjitha.'),
(5106, 58, 2, 'Ka të tillë midis jush që i lëshojnë gratë e veta duke ju thënë se i konsiderojnë si nënat e veta; ato nuk janë nënat e tyre, nënat e tyre janë vetëm ato që i kanë lindur ata. Kurse ata, me të vërtetë, kanë shqiptuar fjalë neveritëse dhe të pavërteta. E, me të vërtetë, Perëndia është falës dhe shlyes i gabimeve.'),
(5107, 58, 3, 'Ata, të cilët, gratë e tyre i lëshojnë në atë mënyrë, e pastaj donë t’i kthejnë në gjendjen e mëparshme, duhet ta lirojnë një skllav apo skllave, para se bashkëshortët të takohen përsëri (si burrë e grua). Kështu ju urdhërohet. E, Perëndia i di të gjitha ato që bëni ju.'),
(5108, 58, 4, 'Ai që nuk ka, (prandaj) nuk mund ta lirojë, është i obliguar që të agjërojë dy muaj rresht para takimit të tyre bashkëshortorë. E, ai që nuk mund të përballojë agjërimin, duhet t’i ushqejë gjashtëdhjetë të varfër. Kjo (është për atë) që të zbatoni besimin në Perëndinë dhe Profetin e Tij. Këta janë kufijtë e Perëndisë. Ndërsa, për jobesimtarët ka vuajtje të mundimshme.'),
(5109, 58, 5, 'Me të vërtetë, ata që sillen në mënyrë kundërshtuese ndaj Perëndisë dhe të dërguarit të Tij, do të shkatërrohen, sikundër që janë shkatërruar ata para tyre. Me të vërtetë, kemi shpallur argumente të qarta, kurse për jobesimtarët do të ketë vuajtje poshtëruese,'),
(5110, 58, 6, 'në Ditën kur Perëndia i ngjallë të gjithë e i lajmëron Ai për veprat që kanë punuar. Perëndia të gjitha ato i numëron, kurse ata i kanë harruar ato – Perëndia është dëshmitar i çdo gjëje.'),
(5111, 58, 7, 'A nuk e di ti se Perëndia di çdo gjë që është në qiej dhe në Tokë? Nuk ka bisedë sekrete midis treve (personave), e që Ai (Perëndia), të mos jetë i katërti, as midis të pestëve, e që Ai të mos jetë i gjashti, as kur janë më pak as kur janë më shumë, e që Ai të mos jetë me ata (i dëgjon), kudo që të jenë; Ai në Ditën e gjykimit do t’i informojë ata se çka kanë punuar, se, me të vërtetë, Perëndia di çdo gjë!'),
(5112, 58, 8, 'A nuk i sheh ata të cilëve u është ndaluar biseda e fshehtë, e megjithatë, kthehen në atë çka u është ndaluar dhe bisedojnë mes tyre mbi mëkatet, armiqësinë dhe padëgjueshmërinë ndaj Profetit. E kur vijnë te ti, të përshëndesin ashtu si nuk të ka përshëndetur kurrë Perëndia dhe flasin në mes tyre: “Pse nuk na dënon Perëndia neve për këtë që po flasim?” – Do t’u mjaftojë atyre skëterra. Në të do të digjen. E, sa vendbanim i tmerrshëm që është ai! –'),
(5113, 58, 9, 'O besimtarë! Kur bisedoni mes jush, mos bisedoni për mëkate, armiqësi dhe padëgjueshmëri ndaj Profetit; por bisedoni për mirësi dhe ndershmëri. Druajuni Perëndisë, Atij para të cilit do të tuboheni.'),
(5114, 58, 10, 'Bisedat e fshehta vijnë nga djalli, për t’i dëshpëruar besimtarët; por ai nuk mund t’ju shkaktojë kurrfarë të keqe, përveç nëse Perëndia e lejon atë (dëm). Por, besimtarët, le të mbështeten (vetëm) te Perëndia!'),
(5115, 58, 11, 'O besimtarë! Kur ju thuhet: “Bëni vend të tjerëve në ndejat tuaja!” – ju bëni vend, e madje Perëndia ju zgjëron juve. E kur ju thuhet: “Ngrituni ju”, - ju ngituni, e Perëndia do t’i lartësojë besimtarët që janë prej jush, e veçanërisht dijetarët do t’i ngrisë në shkallë të lartë. E, Perëndia di çdo gjë që punoni.'),
(5116, 58, 12, 'O besimtarë, kur të doni të këshilloheni me Profetin, para këshillimit jepni lëmoshë, ajo është më mire dhe më pastër për ju. Po, nëse nuk keni – Perëndia me të vërtetë, është falës dhe mëshirues.'),
(5117, 58, 13, 'A mos po frikoheni që para këshillimit tuaj të jepni lëmoshë?! E nëse nuk jepni, e Perëndia ju ka falur, atëherë kryeni namazin dhe jepini zeqatin dhe dëgjoni Perëndinë e Profetin e Tij. Perëndia di çdo gjë që punoni.'),
(5118, 58, 14, 'A nuk i sheh ata, të cilët shoqërohen me njerëzit, ndaj të cilëve Perëndia është i zemruar? Ata nuk janë as nga ju as nga ata, e madje, betohen rrejshëm duke qenë të vetëdijshëm (për të)'),
(5119, 58, 15, 'Perëndia atyre u ka përgatitur denim të rëndë, sepse, me të vërtetë, janë të shëmtuara ato që punojnë:'),
(5120, 58, 16, 'betimet e tyre i kanë bërë perde, andaj i pengojnë nga rruga e Perëndisë; ata i prêt dënimi i poshtëruar.'),
(5121, 58, 17, 'As pasuria, as fëmijët e tyre, nuk do t’u ndihmojnë aspak te Perëndia, ata do të jenë banorë të zjarrit, ku do të qëndrojnë përgjithmonë.'),
(5122, 58, 18, 'Në Ditën kur Perëndia do t’i ngjallë të gjithë, ata do t’i betohen Atij, sikundër që ju kanë betuar juve, duke menduar se do të kenë fare dobie prej saj. Po, me të vërtetë, ata janë gënjeshtarë të mëdhenj!'),
(5123, 58, 19, 'Ata i ka mbizotëruar djalli dhe i ka bërë që ta harrojnë Perëndinë. Ata janë në grupin e djallit, e ata që janë në këtë grup, me siguri, do të pësojnë (shkatërrohen).'),
(5124, 58, 20, 'Ata që kundërshtojnë Perëndinë dhe Profetin e Tij, do të jenë në shkallën më të poshtëruar.'),
(5125, 58, 21, 'Perëndia ka shkruar (në Librin e shenjtë “Levhi Mahfudh”): “Unë dhe profetët e Mi do të fitojmë me siguri!” – Perëndia, me të vërtetë, është i fortë dhe i Plotëfuqishëm.'),
(5126, 58, 22, 'Nuk gjen njerëz që besojnë në Perëndinë dhe në Ditën e Kijametit, e të ushqejnë dashuri ndaj atyre të cilët kundërshtojnë Perëndinë dhe Profetin e Tij, madje qofshin ata edhe etërit e tyre, ose bijtë e tyre, ose vëllezërit e tyre, ose farefisi i tyre. Ai, në zemrat e tyre ka mbjellë besimin dhe me shpalljen e Vet i ka fuqizuar ata, dhe Ai do t’i shpie ata në kopshtije, nëpër të cilat rrjedhin lumenjtë ku do të qëndrojnë përgjithmonë. Perëndia është i kënaqur me ta, por edhe ata do të jenë të kënaqur ndaj Tij. Ata janë ithtarë të (rrugës së) Perëndisë, e ata, ithtarët e Perëndisë, me siguri, janë fitues.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Perëndinë e falenderon çdo gjë në qiej dhe çdo gjë në Tokë. Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(5128, 59, 2, 'Ai është që i ka dëbuar për here të pare prej shtëpive të tyre, ata të cilët nuk kanë besuar Librin; ju nuk keni menduar se ata do të shkojnë, kurse ata kanë menduar se kështjellat e tyre do t’i mbrojnë nga dënimet e Perëndisë, por dënimi i Perëndisë u ka ardhur prej nga ata nuk e kanë pritur, dhe Ai në zemrat e tyre ka mbjellë frikë; e, ata, me duart e tyre dhe me duart e besimtarëve i kanë shkatërruar shtëpitë e veta. Prandaj, mësoni nga kjo, o ju mentarë!'),
(5129, 59, 3, 'Sikur mos ta kishte caktuar Perëndia ndjekjen e tyre, Ai do t’i kishte dënuar në këtë jetë, por ata në jetën tjetër i pret dënimi në zjarr,'),
(5130, 59, 4, 'sepse, ata e kanë kundërshtuar Perëndinë dhe Profetin e Tij; e ai që kundërshton Perëndinë, Perëndia, me të vërtetë, do t’i dënojë ashpër.'),
(5131, 59, 5, 'Ato palme hurmesh që i keni prerë ju ose i keni lënë të qëndrojnë vertikalisht – i keni bërë me miratimin e Perëndisë dhe që Ai (Perëndia) t’i poshtërojë ngatërrestarët.'),
(5132, 59, 6, 'E, atë që Perëndia ia ka dhuruar (kthyer prej ngatërrestarëve) Profetit të Vet, e që ju nuk e keni marrë si plaçkë me sulmim as të kuajve as të deeve, por që, Perëndia i jep pushtet profetëve të Vet mbi ata që Ai dëshiron. Perëndia është i pushtetshëm për çdo gjë.'),
(5133, 59, 7, 'Pasuria e mbetur e fshatrave, të cilën Perëndia ia ka dhuruar Profetit të Tij, u takon: Perëndisë dhe Profetit të Tij, dhe të afërmëve të tij (Pejgamberit), bonjakëve, të varfërve, dhe udhëtarëve të rastit, - në mënyrë që të mos bie prej dorës në dorë të pasanikëve tuaj; atë që ua jep Profeti (si shpërblim), atë merreni, e atë çka ua ndalon, lënie; dhe ruajuni (mos i kundërshtoni) Perëndisë, sepse, Perëndia, me të vërtetë, dënon ashpër,'),
(5134, 59, 8, '(ajo pasuri u takon) edhe emigrantëve të varfër, të cilët janë përzënë nga vendlindja e tyre dhe pasurisë së tyre, të cilët ëshirojnë mirësinë dhe kënaqësinë e Perëndisë, dhe ta ndihmojnë Perëndinë dhe Profetin e Tij, ata, me të vërtetë, janë besimtarë të sinqertë, -'),
(5135, 59, 9, 'dhe (u takon pasuria) atyre që e kanë bërë Medinen vendstrehim (shtëpi) të besimit qysh më parë (para emigrimit), e i duan ata që vijnë në Medine dhe në zemrat e tyre nuk ndjejnë kurrfarë rëndimi, për atë që u është dhënë atyre (ardhacakëve), por më tepër duan t’u bëjnë mirë atyre se sa vetes, edhe pse madje vetë janë nevojtarë. E, ata që ruhen nga lakmia e madhe, me siguri, do të jenë fitues.'),
(5136, 59, 10, 'Edhe ata që vijnë pas tyre – thonë: “O Zoti ynë, falna neve dhe vëllezërit tanë, të cilët kanë besuar para nesh dhe mos lejo që në zemrat tona të ketë asnjë të keqe ndaj besimtarëve; O Zoti ynë, Ti je, me të vërtetë, i mirë dhe mëshirues!”'),
(5137, 59, 11, 'A nuk po sheh se si hipokritët ju thonë shokëve të vet, të cilët janë ithtarë të Librit, e që nuk besojnë: “Nëse ju dëbojnë, ne do të dalim me ju dhe kurrkujt – asnjëherë, nuk do t’i përulemi kundër jush; e nëse ju sulmoheni, me siguri, do t’u dalim në ndihmë”, - e, Perëndia dëshmon, se ata, me të vërtetë, janë gënjeshtarë:'),
(5138, 59, 12, 'nëse do të jenë të dëbuar, ata nuk do të shkojnë me ta; nëse do të jenë të sulmuar, ata nuk do t’i dalin në ndihmë; e nëse u dalin në ndihmë, sigurisht do të ikin, dhe ata do të ngelin pa ndihmë (përgjithmonë).'),
(5139, 59, 13, 'Ju, në zemrat e tyre jeni më të tmerrshëm (frikësues) se sa Perëndia, ngase ata janë popull i pakuptueshëm.'),
(5140, 59, 14, 'Ata luftojnë me ju duke qenë grumbull vetëm në vende të fortifikuara, ose pas mureve. Lufta ndërmjet tyre është e ashpër. Ti mendon se ata janë kompakt, mirëpo, zemrat e tyre janë të përçara, meqë ata janë njerëz që nuk mendojnë.'),
(5141, 59, 15, 'Këta janë të ngjashëm me ata që kanë qenë pak para tyre, që e kanë shijuar dënimin e punës së tyre – ata i pret dënimi i dhembshëm!'),
(5142, 59, 16, 'Janë të ngjashëm ata me djallin kur i thotë njeriut: “Mos beso!” – e kur bëhet mohues, atëherë ai i thotë: “Unë jam larg prej teje, unë, me të vërtetë, i frikohem Perëndisë, Zotit të gjithësisë!”'),
(5143, 59, 17, 'Fundi i këtyre të dyve, me të vërtetë, është qëndrimi i përhershëm në zjarr, e ky është dënimi për të gjithë njerëzit e këqinj.'),
(5144, 59, 18, 'O besimtarë, druajuni Perëndisë, dhe çdo njeri le të shikojë se ç’ka përgatitur për të nesërmen, duke mos e kundërshtuar Perëndinë, sepse, Ai di çdo gjë që punoni ju.'),
(5145, 59, 19, 'Dhe mos u bëni si ata që duke i harruar urdhërat e Perëndisë, Ai (Perëndia) i ka shpie ata që të harrojnë edhe vetveten; ata janë mëkatarë të vërtetë.'),
(5146, 59, 20, 'Nuk janë të barabartë banorët e xhehennemit dhe banorët e xhennetit; bnorët e xhennetit do të jenë fitues.'),
(5147, 59, 21, 'Sikur këtë Kur’an t’ia shpallim ndonjë mali, ti do ta shihje atë të përulur dhe të copëtuar nga frika e Perëndisë. Këso shembujsh u përkujtojmë njerëzve që të mendojnë.'),
(5148, 59, 22, 'Ai është Perëndia, që s’ka zot tjetër përveç Tij. Ai i di gjërat e dukshme dhe të padukshme, Ai është mëshirues dhe përdëllimtar!'),
(5149, 59, 23, 'Ai është Perëndia, që s’ka zot tjetër përveç Tij, Sundues, i Amshueshëm, Ai është i pastër nga të metat, Paqëdashës, ai që jep siguri, Ai që mbikëqyrë të gjitha, i Plotëfuqishëm, Imponus, Madhështor. Qoftë falenderuar Perëndia, Ai është shumë lart nga ato që ia përshkruajnë Atij!'),
(5150, 59, 24, 'Ai është Perëndia, Krijues, Ai që prej asgjëje krijon, Ai që çdo gjëje ia jep formën, ai posedon emërat më të bukur, Atë e falenderojnë të gjithë (çka ekziston) në qiej dhe në Tokë, Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'O besimtarë, mos e konsideroni armikun Tim dhe tuajin për dashamir; ju po shprehni dashuri ndaj tyre, kurse ata, me të vërtetë, kanë mohuar të drejtën që u ka ardhur juve, e dëbuan Profetin dhe juve, vetëm se besonit në Perëndinë, Zotin tuaj (mos i bëni ata dashamirë), në qoftë se jeni (keni dalë) luftëtarë në rrugën Time dhe për kënaqësinë Time. Ju fshehurazi i shfaqni atyre dashuri, por Unë di edhe atë që fshini dhe atë që punoni haptazi. Ai prej jush, i cili armikun e bën dashamir, ai ka devijuar rrugën e drejtë.'),
(5152, 60, 2, 'Nëse bini në duart e tyre, ata do të jenë armiq tuaj dhe do t’i zgjasin duart e tyre dhe gjuhët e tyre me qëllim të keq, dhe dëshirojnë që t’ju bëjnë jobesimtarë.'),
(5153, 60, 3, 'Nuk do t’ju ndihmojnë juve në Ditën e Kijametit, as fëmijët, as farefisi. Ai do t’ju ndanë mes juve; - e, Perëndia, sheh mirë atë që punoni ju.'),
(5154, 60, 4, 'Shembull i mrekullueshëm për ju është Ibrahimi dhe ata që kanë qenë me të, kur i thanë popullit të vet: “Ne jemi larg prej jush dhe prej atyre që i luteni ju, përveç Perëndisë, na nuk u besojmë juve dhe armiqësia e urrejtja midis nesh është e përhershme, përderisa të mos besoni se ekziston një Perëndi i vetëm”; por, përveç fjalës së Ibrahimit (për të mirën e babës së tij): “Unë do të kërkoj falje për ty, por nuk kam kurrfarë mundësie për të shpëtuar ty prej dënimit të Perëndisë”. O Zoti ynë, te Ti mbështetemi dhe Ty të drejtohemi dhe te Ti do të kthehemi.'),
(5155, 60, 5, 'O Zoti ynë, mos lejo që të bijmë në provë të pafeve dh falna neve. O Zoti ynë, me të vërtetë, Ti je i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(5156, 60, 6, 'Me të vërtetë, ky ka qenë shembull i mirë për ju, për ata që shpresojnë në Perëndinë dhe në Ditën e Kijametit. E kush shmanget, me të vërtetë, Perëndia është i pavarur, Ai meriton tërë falenderimin!'),
(5157, 60, 7, 'Ka gjasë që Perëndia do të lidhë (vendosë) dashuri midis jush dhe atyre ndaj të cilëve keni qëndrim armiqësor; Perëndia është i Plotëfuqishëm dhe falës e mëshirues.'),
(5158, 60, 8, 'Perëndia nuk ju ndalon të shprehni mirësinë dhe të jeni të drejtë ndaj atyre, të cilët nuk luftojnë kundër jush për shkak të fesë (suaj) dhe që nuk ju dëbojnë juve prej shtëpive tuaja. Me të vërtetë, Perëndia i don të drejtit.'),
(5159, 60, 9, 'Perëndia u ndalon juve t’i bnëi miq ata që luftojnë kundër jush për shkak të fesë dhe ju dëbojnë nga vatrat tuaja, si dhe kontribuojnë që të jeni të dëbuar; e kushdo që i bën miq ata – të tillët janë zullumqarë.'),
(5160, 60, 10, 'O besimtarë, kur u vijnë juve besimtaret emigrante, provoni (besimin) tek ato, - e Perëndia më së miri e di besimin e tyre. Nëse bindeni se ato janë besimtare, mos i ktheni te pabesimtarët: Ato nuk u lejohen atyre, si dhe as ata nuk janë të lejueshëm për to. Jepni shpenzimet e bëra atyre (jobesimtarëve). Nuk ka pengesë që t’i merrni ju ato për bashkëshorte, pasi t’u jepini dhurata martese. Mos qëndroni në martesë me idhujtare (pagane). Kërkoni atë që keni dhënë (si dhuratë martese), por dhe ata (jobesimtarët) le të kërkojnë atë që kanë dhënë (si dhuratë martese). Ky është gjykimi i Zotit, Ai gjykon në mes jush, e Perëndia di çdo gjë dhe është i Gjithëdijshëm.'),
(5161, 60, 11, 'E, nëse ndonjëra prej grave tuaja kthehet te jobesimtarët si dhe anasjelltas, atëherë plotësoniu atyre shpenzimet e martesës. Dhe druajuni Perëndisë, të cilit i besoni ju!'),
(5162, 60, 12, 'O Pejbamer, kur të vijnë ty besimtaret që të betohen se: nuk do t’i bëjnë shok Perëndisë asgjë, se nuk do të vjedhin, se nuk do të kurvërojnë, se nuk do t’i mbysin fëmijët e tyre dhe se nuk do të vijnë me ndonjë mashtrim (shpifje) të madh në atë që gjendet në mes duarve dhe këmbëve të tyre (mos të bëjnë kopil), dhe që të mos kundërshtojnë ty në atë që është e arsyeshme (në kryerjen e dispozitave të fesë), ti pranoje betimin e tyre dhe lutju Perëndisë për to; me të vërtetë, Perëndia është falës dhe mëshirues.'),
(5163, 60, 13, 'O besimtarë, mos e bëni mik atë popull, në të cilin Perëndia është i zemëruar, ata e kanë humbur shpresën për jetën tjetër, ashtu sikundër jobesimtarët që e kanë humbur tërë shpresën prej të varrosurve (se do të ringjallen).');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Perëndinë e falenderon çka gjendet në qiej dhe çka gjendet në Tokë. Ai është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(5165, 61, 2, 'O besimtarë, përse flisni atë që nuk e punoni?'),
(5166, 61, 3, 'Është neveri e madhe te Perëndia të thoni atë që nuk e punoni!'),
(5167, 61, 4, 'Me të vërtetë, Perëndia i don ata që luftojnë në rrugën e Tij, të radhitur si një godinë e lidhur fortë.'),
(5168, 61, 5, 'Kur Musai i tha popullit të vet: “O populli im, përse po më mundoni, kur mirë e dini se unë jam profeti i Perëndisë te ju!” E, pasi ata u shmangën – Perëndia u shmangu zemrat e tyre; - e Perëndia, nuk e shpie në rrugë të drejtë atë popull që është zhytur në mëkate.'),
(5169, 61, 6, 'E kur Isai, i biri i Merjemës, ka thënë: “O bijtë e Israelit, unë u jam profet i Perëndisë te ju, për t’ju vërtetuar Teuratin e shpallur para meje dhe për t’ju sjellë lajmin e gëzueshëm për profetin, emrin e të cilit është Ahmed, që do të vijë pas meje; dhe kur ai u solli atyre argumente të qarta, ata thanë: “Kjo është magji e qartë!”'),
(5170, 61, 7, 'E, kush është më zullumqarë se ai që shpif gënjeshtra kundër Zotit, duke u thirrur në fé të vërtetë? – E Perëndia nuk e shpie në rrugë të drejtë popullin zullumqarë (që bën padrejtësi).'),
(5171, 61, 8, 'Ata dëshirojnë të fikin dritën e Perëndisë, me gojën e tyre, e Perëndia – e përsos dritën e Vet, edhe, nëse e urrejnë mohuesit.'),
(5172, 61, 9, 'Perëndia është Ai, që ka dërguar Profetin e Vet me udhëzim për rrugën e drejtë dhe fenë e vërtetë, për ta ngritur më lartë se të gjitha fetë, edhe nëse e urrejnë paganët.'),
(5173, 61, 10, 'O besimtarë, a doni t’ju orientojë në një tregti, e cila ju shpëton nga vuajtja e dhembshme:'),
(5174, 61, 11, 'Besoni në Perëndinë dhe në Profetin e Tij, dhe luftoni në rrugën e Perëndisë me pasurinë dhe trupin tuaj. – Kjo është më mirë për ju, - nëse e dini.'),
(5175, 61, 12, 'Ai do t’ju falë gabimet tuaja dhe do t’ju shpie në kopshte, nëpër të cilat rrjedhin lumenjt, dhe në pallate – të mrekullueshme – në xhennetin e Adnit, - e ky është sukses i madh, -'),
(5176, 61, 13, 'E tjetrën (dhunti) që e doni ju: Ndihma e Perëndisë dhe fitorja e afërt! Andaj, sihariqoji besimtarët!'),
(5177, 61, 14, 'O besimtarë, bëhuni ndihmësit e besimit të Perëndisë, ashtu siç u ka thënë Isai, i biri i Merjemës, nxënësve (apostujve të tij): “Kush janë ndihmësit e mi te Perëndia? Apostujt, u përgjegjën: “Ne jemi ndihmësit e Perëndisë!” – Dhe, kanë besuar një grup nga bijtë e Israelit, e një grup tjetër kanë mohuar; - andaj Ne i kemi forcuar (ndihmuar) besimtarët kundër armikut të tyre dhe ata u bënë fitues.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Çka ka në qiej dhe çka ka në Tokë e falenderojnë Perëndinë, Sunduesin absolut, të pastër (prej të gjitha të metave), të Plotëfuqishëm dhe të Gjithëdijshëm.'),
(5179, 62, 2, 'Perëndia është Ai, që ju ka dërguar të pa arsimuarve një Profet nga mesi i tyre, që ua ka lexuar ajetet e Kur’anit dhe i ka pastruar (nga gjendjet e gabuara), ua ka mësuar Librin (Kur’anin) dhe urtinë (dijeninë e thellë), meqë, ata më parë, në të vërtetë, kanë qenë në humbje të plotë,'),
(5180, 62, 3, 'edhe të tjerëve nga ata, të cilët ende nuk u janë bashkuar – Ai (Perëndia), është i Plotëfuqishëm dhe i Gjithëdijshëm.'),
(5181, 62, 4, 'Kjo është dhuntia e Perëndisë, të cilën Ai ia jep kujt të dojë, e dhuntia e Perëndisë është e madhe.'),
(5182, 62, 5, 'Shembulli i atyr që janë obliguar me Teurat e pastaj nuk e kanë zbatuar, i shembëllen gomarit që i bartë librat (por pa dobi). E, sa të këqinj janë ata, të cilët i mohojnë argumentet e Perëndisë! E, Perëndia, nuk e orienton në rrugën e drejtë atë popull që është dhunues (zullumqar).'),
(5183, 62, 6, 'Thuaj: “O Hebrenj (Çifutë), nëse ju pohoni se jeni të vetmit njerëz të dashur të Perëndisë, atëhrë dëshiroie vdekjen, nëse jeni të sinqertë”.'),
(5184, 62, 7, 'Ata kurrë nuk e dëshirojnë vdekjen për shkak të veprave që kanë punuar; e Perëndia i njeh mirë zullumqarët.'),
(5185, 62, 8, 'Thuaj: “Vdekjes së cilës po i ikni ju, ajo do t’ju arrijë. Pastaj, ju do të ktheheni tek Ai, që i di gjërat e dukshme dhe të padukshme, dhe Ai do t’u informojë juve për atë që keni punuar”.'),
(5186, 62, 9, 'O besimtarë, kur të thirreni për lutje ditën e premte, nxitoni për ta përmendur Perëndinë (namazi i xhumasë) dhe pezulloni shitblerjen; kjo është më mirë për ju, nëse e dini!'),
(5187, 62, 10, 'E kur të përfundojë namazi, atëherë shpërndahuni nëpër tokë dhe kërkoni nga begatitë e Perëndisë dhe përmendeni shumë Perëndinë, për të arritur shpëtimin.'),
(5188, 62, 11, 'Mirëpo, kur ata shohin ndonjë tregti ose lojë, shkojnë tek ajo e të lënë ty të qëndrosh (vetëm). Thuaj: “Ajo që është te Perëndia – është më e mirë nga ahengu e nga tregtia”. – E Perëndia është furnizuesi më i mirë.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Kur të vijnë te ti hipokritët, ata thonë: “Ne dëshmojmë, se ti, me të vërtetë, je i dërguari i Perëndisë!” – E Perëndia e di, se ti, me siguri, je i dërguari i Tij, por Perëndia dëshmon edhe se hipokritët janë gënjeshtarë të qartë.'),
(5190, 63, 2, 'Ata betimet e tyre i kanë bërë maskë, andaj i kanë penguar (njerëzit) prej rrugës së Perëndisë. – Me të vërtetë, është shëmtim, atë që kanë punuar!'),
(5191, 63, 3, 'Kjo është ngase kanë besuar, pastaj kanë mohuar, e u vulosën zemrat e tyre, prandaj ata nuk kuptojnë.'),
(5192, 63, 4, 'Kur i shikon ata, figura e tyre të mahnitë; e nëse flasin, dëgjon fjalët e tyre, - mirëpo, ata u gjasojnë trungjeve të mbështetura dhe mendojnë se çdo fjalë është kundër tyre. Ata janë armiq të vërtetë, andaj ruajuni prej tyre! – Perëndia i vraftë, si po shmangen prej së vërtetës?!'),
(5193, 63, 5, 'E kur ju thuhet atyre: “Ejani, se i dërguari i Perëndisë do të lutet për t’ju falë”, - ata tundin kokat e tyre dhe i sheh ata që pengojnë, e që janë mendjemëdhenj.'),
(5194, 63, 6, 'Njësoj është – u lute për faljen e tyre apo nuk u lute, Perëndia kurrsesi nuk i falë. Perëndia, me të vërtetë, nuk e udhëzon popullin ngatërrestar.'),
(5195, 63, 7, 'Ata thonë: “Mos u jepni kurrgjë atyre që janë te Profeti i Perëndisë, përderisa të largohen (prej tij)!” Por, Perëndisë i takojnë thesarët në qiej dhe në Tokë, por hipokritët nuk kuptojnë.'),
(5196, 63, 8, 'Ata thonë: “Nëse kthehemi në Medinë, padyshim të fuqishmit do t’i dëbojnë të dobëtit nga ajo (Medina)!” Por, fuqia është e Perëndisë dhe e Profetit të Tij dhe e besimtarëve, mirëpo – hipokritët nuk e dijnë (këtë).'),
(5197, 63, 9, 'O besimtarë, mos t’ju habisin pasuria juaj dhe fëmijët tuaj nga të përmendurit Perëndinë. E ata që bëjnë këtë, do të humbin.'),
(5198, 63, 10, 'Jepni ju prej atyre dhuntive, që u kam dhënë Unë juve, para se t’ju vijë vdekja ndonjërit, e ai të thotë: “O Zoti im, sikur të më kishe lënë edhe një kohë të shkurtër në jetë, do të kisha dhënë lëmoshën dhe do të bëhesha ndër të mirët!”'),
(5199, 63, 11, 'Perëndia nuk i jep afat askujt, kur t’i vijë momenti i vdekjes; e Perëndia di mirë atë që punoni ju.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Perëndinë e falenderon çka gjendet në qiej dhe çka gjendet në Tokë. Atij i takon pushteti i vërtetë, dhe Atij i takon falenderimi. Ai është i pushtetshëm për çdo gjë!'),
(5201, 64, 2, 'Ai ju ka krijuar juve, e (ndonjëri) prej jush është mohues, e (disa) prej jush janë besimtarë: Çdo gjë që punoni ju, Perëndia e sheh mirë.'),
(5202, 64, 3, 'Ai ka krijuar qiejt dhe Tokën në mënyrë të mrekullueshme, dhe Ai ju ka dhënë juve formën, dhe figurat tuaja i bënë të bukura dhe të gjithë do të ktheheni te Ai.'),
(5203, 64, 4, 'Ai i di të gjitha gjërat që gjenden në qiej dhe në Tokë. Ai di fshehtësitë tuaja dhe ato që i bëni haptazi; Perëndia i di të gjitha ç’keni në zemrat tuaja.'),
(5204, 64, 5, 'A nuk ju kanë arritur lajmet e atyre që më parë nuk kanë besuar, dhe e kanë shijuar dënimin e veprimeve të tyre. Për ta ka dënim të dhembshëm –'),
(5205, 64, 6, 'ngase kur profetët e tyre vinin me dokumente të qarta, ata thoshin: “A njeriu (si ne) do të na tregojë rrugën?” Dhe nuk besuan, e u kthyen (prej këshillave); e Perëndisë nuk i nevojitet asgjë, Ai është i pavarur dhe i lavdishëm!'),
(5206, 64, 7, 'Mohuesit kujtojnë se kurrsesi nuk do të ngjallen; thuaju atyre: “Po, për Zotin tim, me siguri do të ngjalleni dhe pastaj do të informoheni se ç’keni punuar!” – E, kjo për Perëndinë, është lehtë.'),
(5207, 64, 8, 'Besoni në Perëndinë dhe në të Dërguarin e Tij dhe në dritën (Kur’anin) që ju kemi sjellë. Perëndia, di mirë çdo gjë që punoni ju.'),
(5208, 64, 9, 'Atë Ditë, kur Ai (Perëndia) do t’ju tubojë juve në Ditën e Kijametit, ajo është Dita e mashtrimit (ku shihen veprat e gabuara). Ai që beson në Zotin dhe punon vepra të ira, Perëndia ia falë mëkatet atij dhe do ta shpie në kopshtije, nëpër të cilët rrjedhin lumenjt, e ku do të qëndrojnë përherë, gjithmonë. Ai do të jetë sukses i madh!'),
(5209, 64, 10, 'E ata që nuk kanë besuar dhe i kanë mohuar dokumentet Tona – do të jenë banorë të zjarrit, në të do të qëndrojnë përherë. E sa mbarim i tmerrshëm ësthë ai!'),
(5210, 64, 11, 'Nuk ngjan kurrfarë fatkeqësie pa lejen e Perëndisë, e ai që i beson Perëndisë, ia udhëzon (në rrugë të drejtë) zemrën e tij. Perëndia di mirë çdo gjë.'),
(5211, 64, 12, 'Përuluni Perëndisë dhe Profetit të Tij; e nëse shmangeni ju, Profeti Ynë ka të vetmen detyrë që t’ju shpjegojë qartë.'),
(5212, 64, 13, 'Perëndia është i vetmi Zot dhe vetëm te Perëndia le të mbështeten besimtarët.'),
(5213, 64, 14, 'O besimtarë, me të vërtetë, edhe prej grave tuaja edhe prej fëmijëv tuaj keni armiqë, andaj ruajuni prej tyre! E nëse ju nuk ua shihin për të madhe (gabimet) dhe i harmonizoni marrëdhëniet dhe ua falni, (është mirësi), - e Perëndia, me të vërtetë, është falës dhe mëshirues.'),
(5214, 64, 15, 'Pasuria juaj dhe fëmijët tuaj janë vetëm provë për ju, e te Perëndia shpërblimi është i madh;'),
(5215, 64, 16, 'andaj druajuni Perëndisë sa të mundeni, dhe dëgjoni, dhe përuluni dhe shpenzoni (prej pasurisë suaj) për të mirën tuaj. E ata që ruhen nga lakmia e madhe, janë fitues.'),
(5216, 64, 17, 'Nëse i jepni hua të bukur Perëndisë, Ai ua kthen shumëfish atë dhe ua falë (gabimet). – Perëndia është falënderues dhe nuk nxiton në dënime.'),
(5217, 64, 18, 'Ai është njohës i gjërave të dukshme dhe të padukshme, (Ai është) i Plotëfuqishëm dhe i Gjithëdijshëm.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'O profet! Kur t’i lëshoni gratë, lëshoni në kohën e tyre të caktuar (Id-detit) dhe llogaritni kohën e pritjes dhe durajuni Perëndisë, Zotit tuaj. Mos i dëboni ato prej shtëpive të tyre, - por edhe ato mos të dalin, - përveç nëse haptazi kanë bërë ndonjë vepër të keqe. Këto janë dispozitat e Perëndisë; e ai që nuk i zbaton dispozitat e Perëndisë, ai vetvetes i ka bërë dëm. Ti nuk e di, se pas kësaj, Perëndia mund të sjellë ndonjë vepër (rast për pajtim).'),
(5219, 65, 2, 'Dhe, deri sa të arrijë afati i caktuar, ju mbani ato në mënyrë të arsyeshme, ose ndahuni me to në mënyrë të pëlqyeshme dhe merrni dy dëshmitarë të drejtë prej jush dhe zbatoni dëshminë (e plotë) për Zotin e Madhërueshëm! Kjo është këshillë për ata që i besojnë Perëndisë dhe Ditës së Kijametit, e kush i dron Perëndisë, Ai i bënë rrugëdalje atij,'),
(5220, 65, 3, 'dhe e furnizon atë prej nga nuk e pret; kushdo që mbështetet te Perëndia, Ai i mjafton atij. Perëndia, me të vërtetë, i zbaton deri në fund punët e Tij; Perëndia, për çdo gjë ka caktuar afatin.'),
(5221, 65, 4, 'E ato gra tuaja, të cilat kanë humbur shpresën për ciklin e tyre (të grave), nëse dyshoni (për këtë): koha e tyre e pritjes është tre muaj, si dhe tek ato që nuk kanë pasur ciklin mujor të femrave. Kurse, tek ato që janë shtatzënë, afati i tyre (i pritjes) është deri në lindjen e fëmijës. E, kush i druan Perëndisë, Ai ia lehtëson çështjen atij.'),
(5222, 65, 5, 'Ja, këto janë dispozitat e Perëndisë që u ka shpallur juve, e ai që i druan Perëndisë, Ai (Perëndia) ia shlyen atij veprat e këqia dhe ia rritë atij shpërblimin.'),
(5223, 65, 6, 'Leni ato të banojnë aty ku banoni edhe ju, sipas mundësive tuaja, dhe mos ju bëni vështirësi atyre, për t’i detyruar (që të dalin). Nëse janë shtatzënë furnizoni deri sa të lindin. Nëse fëmija furnizohet me gjiun e nënës, paguajuni atyre (nënave) shpërblimin dhe merruni vesh me të mirë. E, nëse ekzistojnë vështirësi, atëherë le ta ushqejë me gji tjetra.'),
(5224, 65, 7, 'Le të shpenzojë pasaniku sipas pasurisë që ka, por ai që ekonomikisht ka mungesa, le të shpenzojë nga e dhëna e Perëndisë; Perëndia nuk e ngarkon askë (më tepër) se sa i ka dhënë. Perëndia, me siguri, pas vështirësisë – sjell lehtësi.'),
(5225, 65, 8, 'Shumë vendbanime u janë larguar urdhërave të Zotit të vet dhe profetëve të Tij; Ne, do të kërkojmë prej tyre llogari precize dhe do t’i dënojmë me dënim të ashpër.'),
(5226, 65, 9, 'Ata e kanë shijuar dënimin e veprave të tyre, dhe fundi i tyre ka qenë shkatërrim.'),
(5227, 65, 10, 'Perëndia ka përgatitë për ta dënim të rëndë, prandaj druajuni Perëndisë (mos e kundërshtoni), o njerëz mentarë, ju që keni besuar. Perëndia, madje, ju ka dërguar udhërrëfyesin,'),
(5228, 65, 11, 'një Profet, i cili ju shpjegon argumentet e qarta, për t’i nxjerrë ata që besojnë dhe punojnë vepra të mira, nga errësira në dritë. E, ai që beson Perëndinë dhe punon vepër të mirë, Ai (Zoti), do ta shpie atë në kopshtet e xhennetit, nëpër të cilët rrjedhin lumenjt dhe do të qëndrojnë aty përgjithmonë, për jetë e mot. E, Perëndia, i jep atij furnizim të mrekullueshëm.'),
(5229, 65, 12, 'Perëndia është Ai, i cili ka krijuar shtatë qiej dhe aq pjesë të tokës; në to zbatohen urdhërat e Tij, për ta ditur ju, që Perëndia është i Plotëfuqishëm dhe se, me të vërtetë, dijenia e Perëndisë i ka përfshirë të gjitha!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'O Pejgamber! Përse po ia ndalon vetes atë që ta ka lejuar Perëndia ty, në kërkimin e kënaqësisë së grave tuaja. E, perëndia është falës dhe mëshirues.'),
(5231, 66, 2, 'Perëndia ju ka bërë të mundshme që të liroheni nga betimet tuaja; Perëndia është Zoti juaj; Ai është i Plotëdijshëm dhe i Gjithëdijshëm.'),
(5232, 66, 3, 'Kur Pejgamberi ia shfaqi një fjalë të fshehtë njërës prej grave të tij; e ajo pastaj e shpalli këtë fjalë, - e Perëndia ia zbuloi këtë Pejgamberit – ai një pjesë të këtij zbulimi e tregoi, kurse pjesën tjetër e heshti. Kur Pejgamberi e informoi atë (gruan) për këtë fshehtësi, ajo e pyeti: “E kush të ka treguar ty për këtë (se nuk e paskam ruajtur bisedën e fshehtë)?” Ai i tha: Ma tregoi ai që di gjithçka dhe të cilit çdo gjë i është e njohur.'),
(5233, 66, 4, 'Nëse ju të dyja pendoheni te Perëndia, se zemrat tuaja kanë anuar (Perëndia ju falë). Nëse ju të dyja bashkoheni kundër tij (kurrgjë nuk mund të arrini), se, me të vërtetë, Perëndia është mbrojtësi i tij, dhe Xhebraili dhe besimtarët e mirë; e pastaj edhe gjithë engjëjt janë përkrahësit e tij.'),
(5234, 66, 5, 'Në qoftë se ju lëshon ai juve, Zoti i tij mund t’ia zëvendësojë ato – atij, me gra më të mira se ju: muslimane dhe besimtare, përulëse, penduese, lutëse (shumë) agjëruese, të veja dhe vasha.'),
(5235, 66, 6, 'O ju që besoni! Ruani veten dhe familjet tuaja nga zjarri, karburante (lëndë djegëse) të të cilit janë njerëzit dhe gurët, dhe të cilin e mbikëqyrin engjëjt e fuqishëm dhe të ashpër, të cilët nuk u kundërshtojnë urdhërave të Perëndisë dhe të cilët punojnë për atë që urdhërohen.'),
(5236, 66, 7, 'O ju që nuk keni besuar! Mos u arsyetoni sot, sepse, me të vërtetë, dënoheni vetëm për veprat që keni bërë.'),
(5237, 66, 8, 'O ju që kei besuar! Pendohuni sinqerisht te Perëndia, se Zoti juaj mund t’ua shlyejë gabimet tuaja e t’ju shpiejë në kopshte, nëpër të cilët rrjedhin lumenjt, në Ditën në të cilën Perëndia nuk do ta turpërojë Profetin dhe ata që bashkë me të kanë besuar; drita e tyre ecë para tyre dhe në të djathtë të tyre. Ata thonë: “O Zoti ynë, plotësona dritën tonë dhe falna neve, sepse, Ti, me të vërtetë, je i Plotëfuqishëm.'),
(5238, 66, 9, 'O Profet! Luftoji të pafetë dhe hipokritët dhe bëhu i ashpër ndaj tyre! Vendbanimi i tyre do të jetë skëterra. E, sa i shëmtuar është ai vendbanim!'),
(5239, 66, 10, 'Perëndia jep shembull, për ata që nuk besojnë, - gruan e Nuhut dhe gruan e Lutit: ato kanë qenë të martuara për dy njerëz nga njerëzit Tonë të ndershëm, por ato ndaj tyre kanë qenë mashtruese (hipokrite) – dhe ata dy nuk do të mund t’u ndihmojnë atyre dyjave – te Perëndia (për t’i shpëtuar nga dënimi), e do t’u thuhet atyre: “Hyni ju të dyja në zjarr, me ata (të gjithë) që po hyjnë!”'),
(5240, 66, 11, 'E, Perëndia jep shembull për ata që besojnë – gruan e Faraonit, kur ajo tha: “O Zoti im, ndërtoma mua një banesë te Ti në xhennet, dhe më shpëto nga Faraoni dhe veprat e tij, dhe më shpëto nga populli zullumqar!”'),
(5241, 66, 12, 'dhe, (shembullin) e merjemës, të bijës së Imranit, e cila e ka ruajtur virgjinitetin e saj, e Ne kemi frymëzuar në te, prej Shpirtit Tonë. Ajo u besonte fjalëve të Zotit të saj dhe librave të Tij, dhe ajo ishte nga personat që luteshin shumë.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Është i lartësuar Ai, në dorën e të cilit është pushteti dhe Ai është i pushtetshëm për çdo gjë! –'),
(5243, 67, 2, 'Ai i cili ka krijur jetën dhe vdekjen, për t’ju provuar se kush prej jush vepron më mirë; - Ai është i Plotëfuqishëm dhe falës.'),
(5244, 67, 3, 'Ai, i cili, i ka krijuar shtatë qiej njërin mbi tjetrin, - ti nuk vëren në krijesat e Mëshiruesit kurrfarë disharmonie, dhe shiko edhe një ehrë a mos po sheh ndonjë plasaritje,'),
(5245, 67, 4, 'pastaj shiko përsëri shumë herë, shikimi do të kthehet i lodhur dhe i molisuar.'),
(5246, 67, 5, 'Na, juve ua kemi zbukuruar qiellin më të afërm me yje të shkëlqyeshme dhe kemi bërë që (zjarri i tyre) t’i gjuajë djajtë, për të cilët kemi përgatitur dënimin me zjarr.'),
(5247, 67, 6, 'Kurse, për ata që nuk besojnë në Zotin e tyre, dënimi është skëterra; e sa i shëmtuar është ai vendbanim!'),
(5248, 67, 7, 'Kur të hudhen në të, do të dëgjohet psherëtima e tyre, e zjarri do të ndizet furishëm,'),
(5249, 67, 8, 'gati për të eksploduar nga ashpërsia. Kurdo që të hedhet në të ndonjë grup (jobesimtarësh), - rojtarët e skëterrës i pyesin ata: “A nuk u erdhi askush t’ua tërheq vërejtjen juve?”'),
(5250, 67, 9, '“Po”, përgjigjen ata, gjithsesi na ka ardhur neve përkujtuesi, por ne i kemi mohuar ata dhe kemi thënë: “Perëndia nuk ka shpallur asgjë, ju jeni në humbje të madhe!”'),
(5251, 67, 10, 'Ata (jobesimtarët), thonë: “Sikur të kishim dëgjuar ose menduar, nuk do të gjendeshim midis banorëve të skëterrës!”'),
(5252, 67, 11, 'Dhe, ata e pranuan mëkatin e tyre. Larg qofshin prej mëshirës së Zotit banorët e skëterrës!'),
(5253, 67, 12, 'Me të vërtetë, ata që i frikohen Zotit të tyre edhe atëherë kur nuk i sheh askush, për ta ka falje dhe shpërblim të madh!'),
(5254, 67, 13, 'Flitni ju fshehtas ose haptas, por Ai, me të vërtetë, di çka në zemrat tuaja!'),
(5255, 67, 14, 'E si mos t’i dijë Ai, i cili ka krijuar çdo gjë dhe është i informuar hollësisht.'),
(5256, 67, 15, 'Ai ju ka bërë Tokën të nënshtruar – të shërbyeshme, andaj ecni nëpër viset e saj dhe ushqehuni me atë që ju ka dhënë Ai. Ju (pasi të ringjalleni), do të ktheheni tek Ai.'),
(5257, 67, 16, 'A jeni ju të sigurtë se Ai që është (Sundues) në qiell, nuk do t’ju fusë në tokë, kur ajo fillon të dridhet papritmas?'),
(5258, 67, 17, 'Ose, a jeni të sigurtë se Ai që është (Sundues) në qiell, nuk do t’ju çojë furtunë gurësh? Atëherë do të dini se çfarë është vërejtja Ime!'),
(5259, 67, 18, 'Me të vërtetë, i kanë konsideruar gënjeshtarë ata që kanë qenë përpara këtyre. E sa i ashpër ka qenë dënimi (pezmi) Im!'),
(5260, 67, 19, 'A thua nuk po i shohin ata shpendët mbi kokat e tyre, se si fluturojnë, duke i hapur dhe mbledhur krahët? Askush nuk i mbanë ata, përveç Zotit Mëshirues; Ai, me të vërtetë, sheh çdo gjë.'),
(5261, 67, 20, 'Ose, kush është ai që do të jetë ushtria e juaj, që t’u ndihmojë juve, përveç Mëshiruesit? Jobesimtarët janë vetëm në mashtrim, (s’ka ushtarë për ju).'),
(5262, 67, 21, 'Kush janë ata që ju furnizojnë, në qoftë se Ai (Perëndia) e ndalon furnizimin? – Por, ata janë zhytur në të këqia dhe në arrati (nga e vërteta).'),
(5263, 67, 22, 'Është më mirë ai që shkon rrëshqanas e kokëulur, apo ai që në rrugën e drejtë ecën vertikalisht e ballëlartë?'),
(5264, 67, 23, 'Thuaj: “Ai është që ju ka krijur dhe u ka dhënë të dëgjuarit, të pamurit dhe zemrat (mendjen), kurse ju pak falenderoni!”'),
(5265, 67, 24, 'Thuaj: “Ai ju ka krijuar të shpërndarë nëpër Tokë dhe para Tij do të tuboheni (për llogari)”.'),
(5266, 67, 25, 'E ata thonë: “Kur është ky premtim (caktojeni), nëse thoni të vërtetën?!”'),
(5267, 67, 26, 'Thuaj: “Vetëm Zoti e di (kohën), kurse unë jam vetëm paralajmërues i qartë”.'),
(5268, 67, 27, 'Kur për së afërmi e panë dënimin, u zymtuan fytyrat e atyre që nuk kanë besuar dhe do t’u thuhet: “Qe, kjo është ajo që e keni kërkuar (shpejtimin e dënimit)!”'),
(5269, 67, 28, 'Thuaj: “Ç’menoni ju, në qoftë se Perëndia më shkatërron mua dhe ata që janë me mua, ose na mëshiron; kush është ai që do t’i mbrojë jobesimtarët nga dënimi i padurueshëm?”'),
(5270, 67, 29, 'Thuaj: “Ai është Mëshiruesi, (që na shpëton neve), në Atë besojmë dhe në Atë mbështetemi, e ju sigurisht do ta dini se kush është i humbur plotësisht”.'),
(5271, 67, 30, 'Thuaj: “Çka mendoni, nëse ujërat tuaja ju shterren – kush do t’u sjell juve ujin rrjedhës?”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Nûn. Pasha pendën dhe në atë që shkruajnë ata,'),
(5273, 68, 2, 'ti, me dhuntinë e Zotit tënd, nuk je i marrë;'),
(5274, 68, 3, 'ti, me të vërtetë, do të jesh përherë i shpërblyer'),
(5275, 68, 4, 'dhe, me të vërtetë, ti je me virtyte të mëdha'),
(5276, 68, 5, 'dhe, ti do të shohësh, por edhe ata (jobesimtarët) do të shohin,'),
(5277, 68, 6, 'kush nga ju është i marrë.'),
(5278, 68, 7, 'Zoti yt e di më së miri për atë që e ka devijuar rrugën e tij dhe Ai i di mirë ata që janë në rrugën e drejtë.'),
(5279, 68, 8, 'Andaj, mos ju përul ti atyre që përgënjeshtrojnë.'),
(5280, 68, 9, 'Ata dëshirojnë që ti të bësh lëshime, e të bëjnë (lëshime) edhe ata,'),
(5281, 68, 10, 'dhe mos ju përul ti atyre që betohen (rrejshëm), e që janë të poshtër,'),
(5282, 68, 11, 'lajkatarit dhe atë që transmeton fjalët për të keq;'),
(5283, 68, 12, 'penguesin e të mirav, e kundërvajtësin e mëkatarin e madh;'),
(5284, 68, 13, 'të ashpërit dhe pos kësaj edhe kopil, -'),
(5285, 68, 14, '(mos ju përul ti) për shkak se është i pasur dhe ka shumë djemë,'),
(5286, 68, 15, 'i cili kur t’i lexohen ajetet Tona (Kur’ani), thotë: “Këto janë vetëm përralla të (popujve të) lashtë!”'),
(5287, 68, 16, 'Na (së shpejti), do t’ia damkosim hundën atij!'),
(5288, 68, 17, 'Na, do t’i provojmë ata (jobesimtaët), sikundër që i kemi provuar pronarët e kopshtit, të cilët ishin betuar se në mëngjes do t’i marrin frutet;'),
(5289, 68, 18, 'e nuk patën thënë: “Nëse do Zoti!”'),
(5290, 68, 19, 'Dhe, e goditi atë (kopshtin) një fatkeqësi nga Zoti yt, derisa ata flinin'),
(5291, 68, 20, 'dhe ai (kopshti) gëdhiu i shkretëruar.'),
(5292, 68, 21, 'Kurse, në agim, ata, e thirrën njëri-tjetrin:'),
(5293, 68, 22, '“Nxitoni herët në kopshtin tuaj, nëe doni t’i tuboni frutet!”'),
(5294, 68, 23, 'Dhe at au nisën duke biseduar qetësisht:'),
(5295, 68, 24, '“Mos të lejojmë që të paraqitet asnjë i vobektë në kopshtin tonë!”'),
(5296, 68, 25, 'Dhe, ata u nisën herët, të bindur se do të mund t’i pengojnë ata (të vobektit),'),
(5297, 68, 26, 'e kur e panë (kopshtin e shkretëruar), ata thanë: “Na, me të vërtetë, qenkemi të humbur;'),
(5298, 68, 27, 'por, jo, jo, - krejt paskemi mbaruar!”'),
(5299, 68, 28, 'Njëri më i arsyeshëm, prej tyre tha: “A nuk ju thashë unë se duhet ta kujtoni Perëndinë!”'),
(5300, 68, 29, '“Lavdëruar qoftë Zoti ynë!” – thanë. Na, me të vërtetë, paskemi qentë të padrejtë!”'),
(5301, 68, 30, 'Dhe, atëherë filluan ta qortojnë njëri-tjetrin.'),
(5302, 68, 31, '“Sa keq për ne!” thanë ata – ne, me të vërtetë, paskemi qenë të egërsuar;'),
(5303, 68, 32, 'Zoti ynë mund të na japë edhe më të mirë se këtë; me të vërtetë, ne mbështetemi te Zoti ynë!”'),
(5304, 68, 33, 'I tillë ka qenë dënimi, e dënimi në jetën tjetër është më i madh, nëse e dinë!'),
(5305, 68, 34, 'Për ata që i druajnë Perëndisë, te Perëndia do të ketë kopshte të dhuntisë, -'),
(5306, 68, 35, 'Vallë, a do t’i barazojmë muslimanët me njerëzit e këqinj?! –'),
(5307, 68, 36, 'Çka keni ju, si gjykoni ju?!'),
(5308, 68, 37, 'A keni ju ndonjë libër, në të cilin lexoni'),
(5309, 68, 38, 'se do të keni atë që e zgjedhni ju?'),
(5310, 68, 39, 'Apo, a ka për ju betime të mëdha, që do të vlejnë deri në Ditën e Kijametit, e që do ta keni atë çka e keni caktuar.'),
(5311, 68, 40, 'Pyeti ata – kush është garantues për këtë.'),
(5312, 68, 41, 'Ose, a kanë ata bashkëpunëtorë në këtë. E, le t’i sjellin ata bashkëpunëtorët e tyre, nëse flasin të vërtetën.'),
(5313, 68, 42, '(Kujtoju) Ditën e vështirë, kur zbulohen të gjitha dhe ata ftohen (thirrën) që të përulen në tokë (në sexhde), e nuk munden (nuk do të kenë fuqi),'),
(5314, 68, 43, '(ata do të jenë) me sy ulur dhe krejtësisht të poshtëruar, - e, me të vërtetë, kanë qenë të ftuar në përulje (sexhde), derisa qenë gjallë dhe shëndoshë.'),
(5315, 68, 44, 'Prandaj, më lë ti Mua, (Unë) do t’i dënoj ata që e mohojnë këtë Thënie (Kur’anin), Ne ata gradualisht do t’i mashtrojmë (për dënim) në mënyrë të panjohur'),
(5316, 68, 45, 'dhe u japim atyre afat, se, me të vërtetë, mashtrimi Im është i fortë!'),
(5317, 68, 46, 'A po kërkon ti shpërblim nga ata, e ata janë të rënduar nga borxhet?'),
(5318, 68, 47, 'E, a ka tek ata dijeni të fshehtë, e ata po i përshkruajnë ato?'),
(5319, 68, 48, 'Duro ti për gjykimin e Zotit tënd dhe mos u bën si peshkatari (Junusi), kur iu lut Zotit të vet, e ai (Junusi) ishte i hidhëruar,'),
(5320, 68, 49, 'dhe, sikur mos ta arrinte atë mirësi prej Perëndisë, ai do të hudhej në një shkretëtirë e do të ishte i qortuar,'),
(5321, 68, 50, 'por, Zoti i tij e ka zgjedhur dhe e ka bërë atë në grupin e njerëzve të mirë.'),
(5322, 68, 51, 'Pothuajse, me të vërtetë, jobesimtarët duan të të rrëzojnë me shikim kur dëgjojnë Kur’anin, duke thënë: “Ai, në të vërtetë, është i marrë!”'),
(5323, 68, 52, 'E, ai (Kur’ani), është këshillë për tërë botën!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'Çasti i të vërtetës!'),
(5325, 69, 2, 'Çka është Çasti i të vërtetës?'),
(5326, 69, 3, 'Kush ta shpjegon ty (plotësisht) Çastin e të vërtetës (Kijametin)?'),
(5327, 69, 4, 'Themudi dhe Adi e kanë përgënjeshtruar Kijametin,'),
(5328, 69, 5, 'e populli i Themudit është shkatërruar me një ulurimë (britmë) të madhe temrruese,'),
(5329, 69, 6, 'e populli i Adit është shkatërruar me një furtunë të hatashme,'),
(5330, 69, 7, 'të cilën Perëndia ua ka dërguar atyre shtatë netë dhe tetë ditë – parreshtur, e ku mund t’i vërejshe njerëzit e përmbysur si trungjet e hurmeve të kalbura,'),
(5331, 69, 8, 'e, a po sheh mos ka mbetur kush prej tyre?'),
(5332, 69, 9, 'E, ka ardhur Faraoni, dhe ata para tij, si dhe populli (i Lutit), me gabime të mëdha'),
(5333, 69, 10, 'dhe e kundërshtuan të dërguarin e Zotit të tyre, andaj Perëndia i ka dënuar ata me dënim të ashpër.'),
(5334, 69, 11, 'Ne, kur vërshoi uji i madh, ju ngarkuam juve në anije,'),
(5335, 69, 12, 'për t’ju bërë këtë juve mësim dhe që të mbajnë mend veshët e atyre njerëzve (të dijshëm),'),
(5336, 69, 13, 'e kur të fryejë njëherë buria,'),
(5337, 69, 14, 'e toka dhe malet të bartën e të copëtohen nga një shpërthim (eksplodim)'),
(5338, 69, 15, 'atë ditë do të ngjajë Dita e Kijametit,'),
(5339, 69, 16, 'dhe do të çahet qielli, e atë ditë do të jetë i thyeshëm,'),
(5340, 69, 17, 'dhe engjëjt do të rrinë anëve të qiellit, kurse atë ditë – tetë grupe engjëjsh do ta mbajnë Arshin (fronin) e Zotit tënd.'),
(5341, 69, 18, 'Atë ditë do të pyeteni (paraqiteni) ju, dhe asnjë fshehtësi e juaj nuk do të mbetët e pazbuluar:'),
(5342, 69, 19, 'atij që i jepet libri i veprave të tij në dorën e djathtë, do t’ju thotë (të tjerëve): Qe, lexoni librin, tim;'),
(5343, 69, 20, 'unë kam qenë i bindur se do të paraqes llogari.'),
(5344, 69, 21, 'E, ai është në një jetë të këndshme,'),
(5345, 69, 22, 'në një kopsht të mrekullueshëm,'),
(5346, 69, 23, 'frutet e të cilit janë të ulëta (afër duarve).'),
(5347, 69, 24, '(Ju thuhet atyre): “Ju bafshin mirë ushqimi dhe pijet për veprat që keni punuar, në ditët e kaluara!”'),
(5348, 69, 25, 'Atij që i jepet libri i veprave të tij në dorën e majtë, do të thotë: “Ah, mos të më ishte dhënë libri im,'),
(5349, 69, 26, 'dhe mos të kisha ditur për llogarinë time,'),
(5350, 69, 27, 'Ah, të kisha qenë i vdekur (të mos isha ringjallur)!'),
(5351, 69, 28, 'Pasuria ime nuk më solli dobi,'),
(5352, 69, 29, 'dhe falimentoi fuqia ime!'),
(5353, 69, 30, '(e atyre u thuhet): “Kapni ata dhe lidhni në pranga,'),
(5354, 69, 31, 'pastaj në skëterrë fërgoni,'),
(5355, 69, 32, 'e mandej lidhni në zingjirë prej shtatëdhjetë kutësh,'),
(5356, 69, 33, 'sepse, ai, me të vërtetë, nuk ka besuar Perëndinë e Madhërueshëm,'),
(5357, 69, 34, 'dhe nuk ka nxitur të ushqejë të varfërit e shkretë.'),
(5358, 69, 35, 'Prandaj, ai, sot këtu, nuk ka mik të dashur,'),
(5359, 69, 36, 'as ushim tjetër, përpos qelbësirës;'),
(5360, 69, 37, 'të cilin e hanë vetëm fajtorët (jobesimtarët).'),
(5361, 69, 38, 'E, Unë betohem me atë çka shihni ju,'),
(5362, 69, 39, 'dhe me atë që nuk shihni,'),
(5363, 69, 40, 'me të vërtetë, ajo (Kur’ani) është fjala e shpallur Profetit të shenjtë,'),
(5364, 69, 41, 'e nuk është fjalë e poetit. Pak ju besoni,'),
(5365, 69, 42, 'e as nuk është fjalë e ndonjë magjistari (falltori), pak po mendoni ju!'),
(5366, 69, 43, '(Kur’ani) është i zbritur nga Zotii gjithësisë!'),
(5367, 69, 44, 'Sikur të kishte trilluar (Muhammedi) diçka prej fjalëve në llogari Tonë,'),
(5368, 69, 45, 'Ne do ta kapnim atë ëpr dorën e djathtë,'),
(5369, 69, 46, 'dhe do t’ia prenim arterien e zemrës,'),
(5370, 69, 47, 'dhe askush prej jush nuk do të mund ta mbronte prej këtij (dënimi).'),
(5371, 69, 48, 'Me të vërtetë, ai (Kur’ani), është, këshillë për ata që dëshirojnë zbatimin e urdhërave dhe të ruhen prej të këqiave,'),
(5372, 69, 49, 'por, Ne, me siguri, e dimë, që ka prej jush përgënjeshtrues.'),
(5373, 69, 50, 'Me të vërtetë, ai (Kur’ani), është, fatkeqësi për mohuesit,'),
(5374, 69, 51, 'por, ai (Kur’ani), padyshim, është e vërtetë e pamohueshme.'),
(5375, 69, 52, 'Prandaj, falenderohu me emrin e Zotit tënd të Madhërueshëm.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Një kurreshtar ka pyetur për dënimin që do të ngjajë,'),
(5377, 70, 2, 'ndaj jobesimtarëve, - për të cilin dënim s’ka pengues,'),
(5378, 70, 3, '(ai dënim është) prej Perëndisë, Zotit të shkallëve të larta (të mirësisë),'),
(5379, 70, 4, 'ngjiten (hipin) tek Ai engjëjt dhe Shpirti (Xhebraili) në një ditë që zgjatë sa pesëdhjetë mijë vjet (sipas llogarisë së kohës sonë në Tokë),'),
(5380, 70, 5, 'andaj, bëhu i durueshëm, me durim të bukur.'),
(5381, 70, 6, 'Ata, me të vërtetë, (dënimin) e mendojnë të largët (të pamundshëm),'),
(5382, 70, 7, 'kurse Ne, e dimë të afërt,'),
(5383, 70, 8, 'atë Ditë kur qielli do të bëhet si llom i vajit,'),
(5384, 70, 9, 'e malet do të bëhen si lesh (i shprishur) llojlloj ngjyrash;'),
(5385, 70, 10, 'kur i afërmi nuk pyet asgjë për të afërmin,'),
(5386, 70, 11, 'e, njëri-tjetrin do ta shohin. Atëherë, fajtori, dëshiron të paguajë dënimin e asaj Dite me djemtë e vet,'),
(5387, 70, 12, 'me gruan e vet dhe vëllain e vet,'),
(5388, 70, 13, 'dhe me farefisin e tij të ngushtë,'),
(5389, 70, 14, 'si dhe me të gjithë ata që gjenden në faqe të Tokës – vetëm për të shpëtuar.'),
(5390, 70, 15, 'Jo, kurrsesi! Ai është zjarri,'),
(5391, 70, 16, 'që duke e kapur për kokë,'),
(5392, 70, 17, 'e tërheq kah veti atë (njeri) që ia ka kthyer shpinën dhe i është shmangur të vërtetës!'),
(5393, 70, 18, 'Dhe ka grumbulluar (pasuri) dhe ka depozituar (duke mos dhënë asgjë).'),
(5394, 70, 19, 'Njeriu, me të vërtetë, është krijuar lakmus vetjak:'),
(5395, 70, 20, 'kur e goditë e liga – dëshpërohet'),
(5396, 70, 21, 'e kur t’i bie e mira – bëhet koprrac (nuk jep – refuzon),'),
(5397, 70, 22, 'pos besimtarëve,'),
(5398, 70, 23, 'të cilët në lutjet e veta (namaz) janë të qëndrueshëm;'),
(5399, 70, 24, 'dhe që në pasurinë e tyre ka të caktuar një pjesë,'),
(5400, 70, 25, 'për lypësin dhe për të varfërin (që nuk e zgjatë dorën),'),
(5401, 70, 26, 'dhe ata që besojnë Ditën e Kijametit'),
(5402, 70, 27, 'dhe ata që druajnë nga dënimi i Zotit të tyre, -'),
(5403, 70, 28, 'e, me të vërtetë, (askush) nuk është i sigurtë prej dënimit të Zotit të vet –'),
(5404, 70, 29, 'dhe ata që i ruajnë pjesët e turpshme të organizmit të vet,'),
(5405, 70, 30, 'përveç ndaj bashkëshorteve të tyre dhe ato që i kanë në pronësi – at, a me të vërtetë, nuk meritojnë qortim, -'),
(5406, 70, 31, 'e kush kërkon jashtë këtyre caqeve, është kundërvajtës,'),
(5407, 70, 32, 'edhe ata që i ruajnë amanetet dhe i plotësojnë obligimet e tyre,'),
(5408, 70, 33, 'edhe ata që dëshminë e tyre e zbatojnë'),
(5409, 70, 34, 'edhe ata të cilët e kryejnë namazin (në kohën) e rregullt,'),
(5410, 70, 35, 'ata në kopshtie të xhennetit do të nderohen.'),
(5411, 70, 36, 'Përse jobesimtarët nxitojnë kah ti;'),
(5412, 70, 37, 'djathtas e majtas, tuba-tuba?!'),
(5413, 70, 38, 'E që çdonjëri prej tyre dëshiron të hyjë në xhennetin e dhuntive (pa merita)? –'),
(5414, 70, 39, 'Kurrsesi! Ne, me të vërtetë, i kemi krijuar ata, prej asaj që e dinë!'),
(5415, 70, 40, 'Dhe, Unë betohem me Zotin e lindjes dhe të perëndimit, që ne mundemi'),
(5416, 70, 41, 't’i ndërrojmë ata me më të mirë prej tyre dhe Ne nuk jeni të penguar.'),
(5417, 70, 42, 'Andaj, lëri ti ata le të zbaviten me gjëra të kota dhe të luajnë përderisa të hasin në Ditën e tyre të caktuar,'),
(5418, 70, 43, 'Dita në të cilën dalin nga varrezat duke vrapuar, thuajse po nxitojnë te idolet e tyre,'),
(5419, 70, 44, 'me sy të varur që i ka kapluar poshtërimi. Ajo është Dita që u është premtuar atyre!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Ne e kemi dërguar Nuhun te populli i tij: “Paralajmëro popullin tënd para se t’i arrijë dënimi i dhembshëm!”'),
(5421, 71, 2, 'Ai tha: “O populli im, unë, me të vërtetë, jam për ju paralajmërues i qartë;'),
(5422, 71, 3, 'që t’i luteni Perëndisë dhe t’i ruheni (kundërshtimit) të Atij dhe që të më dëgjoni mua,'),
(5423, 71, 4, 'Ai ju falë mëkatet tuaja dhe do t’ju lërë të jetoni deri në çastin e caktuar, e kur t’ju vije çasti i caktuar i Perëndisë, me të vërtetë, ai nuk shtyhet; ta dini ju”.'),
(5424, 71, 5, 'Ai tha: “O Zoti im, unë, me te vërtetë, e thirra popullin tim natë e ditë,'),
(5425, 71, 6, 'por thirrja ime (nuk solli dobi, por) shkaktoi largim edhe më të madh.'),
(5426, 71, 7, 'E, sa herë që i thërrita (unë) ata (në punë të mbarë), për t’i falur Ti, ata i shtinin gishtërinjtë në veshët e tyre dhe mbuloheshin me petkat e tyre (për të mos më parë) – ishin këmbëngulës dhe shumë mendjemëdhenj.'),
(5427, 71, 8, 'Pastaj, i thirra haptazi (me zë edhe më të lartë),'),
(5428, 71, 9, 'e mandej ua shpalla publikisht atë, si dhe në besim u përshpërita (i thirra në të gjitha format).'),
(5429, 71, 10, 'Dhe ju thashë: Kërkoni falje nga Perëndia i jua, sepse, me të vërtetë, Ai është falës, i madh;'),
(5430, 71, 11, 'Ai do t’ju dërgojë juve shi të bollshëm,'),
(5431, 71, 12, 'dhe do t’ju ndihmojë me pasuri dhe me fëmijë, dhe për ju do të krijojë kopshtije dhe për ju do të krijojë lumenj.'),
(5432, 71, 13, 'Përse nuk i frikoheni madhërisë së Perëndisë,'),
(5433, 71, 14, 'e, Ai ju ka krijuar me forma (faza) të ndryshme?!'),
(5434, 71, 15, 'A nuk po shihni se si Perëndia i ka krijuar shtatë qiejt – palë-palë (kat-kat),'),
(5435, 71, 16, 'dhe në ta e ka bërë Hënën dritë, kurse Diellin e ka bërë fener (ndriçues)?'),
(5436, 71, 17, 'Perëndia ju ka krijuar nga toka (dheu), ashtu si bimët,'),
(5437, 71, 18, 'pastaj ju kthen te ajo dhe prej saj sërish do t’ju nxjerrë.'),
(5438, 71, 19, 'Perëndia për ju Tokën e ka bërë shtrojë,'),
(5439, 71, 20, 'që të ecni nëpër të, nëpër rrugët e gjera?”'),
(5440, 71, 21, 'Nuhu tha: “O Zoti im, ata më kundërshtuan mua dhe u dhanë pas atyre, pasuria dhe fëmijët e të cilëve nuk kanë sjellë gjë tjetër, pos shkatërrimt,'),
(5441, 71, 22, 'dhe trilluan trillime të mëdha (kundër Nuhut)'),
(5442, 71, 23, 'dhe kanë thënë (populli i Nuhut): Mos i lini kurrsesi zotrat tuaja, mos e lini kurrsesi as Veddin, as Suvain, as Jeguthin, as Jeukin, as Nesrin’!'),
(5443, 71, 24, 'E, këta idhujë kanë mashtruar shumë (njerëz); e ti (keqbërësve), mos u smadho asgjë, përpos humbjes!”'),
(5444, 71, 25, 'Për shkak të mëkateve të tyre, ata janë përmbytur, e janë hedhur në zjarr dhe ata nuk kanë gjetur mbrojtës tjetër për vete, përveç Perëndisë.'),
(5445, 71, 26, 'Dhe Nuhu tha: “O Zoti im, mos lë të qarkullojë në faqe të dheut asnjë mohues,'),
(5446, 71, 27, 'sepse, me të vërtetë, nëse Ti i lë, ata – do t’i shpien në humbje robërit e Tu dhe do tëlindin vetëm mëkatarë dhe jobesimtarë!'),
(5447, 71, 28, 'O Zoti im, falmë mua dhe prindërit e mi, dhe ata të cilët hynë në shtëpinë time si besimtarë, dhe besimtarët e besimtaret, ndërsa zullumqarëve shtoju vetëm shkatërrimin!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'Thuaj (o Muhammed!): “Mua m’u ka shpallur se disa prej xhindëve (krijesa të padukshme), kanë dëgjuar dhe kanë thënë: “Me të vërtetë, kemi dëgjuar (një) Kur’an të mrekullueshëm,'),
(5449, 72, 2, 'i cili udhëzon në rrugën e drejtë – e ne kemi besuar në të, dhe kurrsesi më nuk do t’i bëjmë shok askë Perëndisë sonë,'),
(5450, 72, 3, 'e, me të vërtetë, qoftë, lartë madhëria e Tij – Zotit tonë! Ai, nuk ka marrë për Vete as bashkëshorte as fëmijë;'),
(5451, 72, 4, '(Disa) të marrët nga ne, flisnin gënjeshtra për Perëndinë,'),
(5452, 72, 5, 'kurse ne, kemi menduar se as njerëzit as xhindët nuk flasin gënjeshtra për Perëndinë;'),
(5453, 72, 6, 'e disa njerëz kanë kërkuar strehim te disa xhindë, e kjo vetëm që ua shtoi atyre tërbimin,'),
(5454, 72, 7, 'dhe, ata mendojnë siç mendoni edhe ju: se, Perëndia nuk do të ringjallë askë;'),
(5455, 72, 8, 'ne jemi ata që kemi arritur në qiell, por kemi hasur në plotë roje të forta (engjëj) dhe shigjeta të zjarrta (meteorë);'),
(5456, 72, 9, 'dhe uleshim rreth tij, nëpër vende të përshtatshme për të dëgjuar diçka, por ai që përgjon tani, do të hasë në shigjeta të zjarrta (meteorë) që janë në pritje (pusi),'),
(5457, 72, 10, 'dhe ne nuk dimë, se e keqja i qëllimohet atyre që janë në Tokë, apo Zoti i tyre don t’i udhëzojë në rrugë të mirë,'),
(5458, 72, 11, 'e midis nesh ka të mirë dhe ka që nuk janë të mirë, ka prej nesh kategori të ndryshme, (grupe të përçara);'),
(5459, 72, 12, 'dhe ne e kemi ditur se nuk do të mund t’i shpëtojmë Perëndisë në Tokë dhe nuk mund ta lodhim Atë duke ikur (në qiell);'),
(5460, 72, 13, 'dhe ne, posa dëgjuam udhëzimin (Kur’anin), besuam në të; e kush beson në Zotin e vet, nuk ka përse të druajë nga dëmi as nga zullumi (tërbimi);'),
(5461, 72, 14, 'dhe ka prej nesh muslimanë, e ka prej nesh edhe mëkatarë, e kush e ka përqafuar islamizmin, e ka zgjedhur rrugën e drejtë,'),
(5462, 72, 15, 'kurse, jobesimtarët, do të jenë lëndë djegëse të skëterrës”.'),
(5463, 72, 16, 'Sikur të qëndronin në rrugën e vërtetë, Ne do t’i furnizonim me ujë të bollshëm,'),
(5464, 72, 17, 'për t’i vënë me këtë në sprovë ata; e kushdo që largohet nga të përkujtuarit Zotin e tij, Zoti atë ia nënshtron dënimit të ashpër.'),
(5465, 72, 18, 'Xhamiat i janë kushtuar Perëndisë, dhe mos iu lutni askujt, përveç Perëndisë!'),
(5466, 72, 19, 'E, kur adhuruesi i Perëndisë (Muhammedi), u ngrit t’i lutet Atij, ata (xhindët) u tubuan rreth tij si të ishin shtëllungë leshi.'),
(5467, 72, 20, 'Thuaj (o Muhammed!): “Unë i lutem vetëm Zotit tim dhe askë s’ia bëjë shok Atij”.'),
(5468, 72, 21, 'Thuaj (o Muhammed!): “Unë nuk mundem t’jua largojë ndonjë dëm juve e as t’ua sjell ndonjë dobi (vetëm Perëndia ka pushtet për këtë)”.'),
(5469, 72, 22, 'Thuaj (o Muhammed!): “Me të vërtetë, mua askush nuk mund të më mbrojë nga dënimi i Perëndisë; as që mund të gjejë strehim tjetër kund, përveç te Ai, -'),
(5470, 72, 23, '(Unë s’kam tjetër funksion) përveç që t’ua predikojë atë që vjen nga Perëndia dhe të dërguarit e Tij”. E, ai që s’dëgjon Perëndinë dhe të Dërguarin e Tij, atë e pret zjarri i skëterrës, në të cilin do të mbetet përgjithmonë. –'),
(5471, 72, 24, 'E, kur ata të shohin atë për çka u është tërhequr vërejtja, do ta dinë ata se kush është më i dobët (në ndihmë) dhe në numër më i vogël.'),
(5472, 72, 25, 'Thuaj (o Muhammed!): “Unë nuk e di, a është afër ajo që u është premtuar juve, apo Zoti im ka caktuar për të afat (më të gjatë),'),
(5473, 72, 26, 'vetëm Ai i di sekretet dhe Ai nuk ia zbulon sekretet e Veta askujt;'),
(5474, 72, 27, 'përveç atij të cilin Ai e ka zgjedhur për të dërguar (pejgamber); andaj, Ai para tij dhe pas tij, i vë ata që e ruajnë (engjëjt)'),
(5475, 72, 28, 'në mënyrë që t’i dijë (qartë) se a i kanë zbatuar misionet e Zotit të tyre. Ai di në hollësi çdo gjë që është te të dërguarit; Ai di numrin e çdo gjëje që ekziston.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'O ti i mbështjellur (Muhammed)!'),
(5477, 73, 2, 'Zgjoju (lutju) Perëndisë natën, pos të paktën; -'),
(5478, 73, 3, 'gjysmën e saj ose edhe më pak nga ajo,'),
(5479, 73, 4, 'ose diçka më shumë se ajo, dhe lexo Kur’anin me kujdes dhe qartë,'),
(5480, 73, 5, 'Ne, me të vërtetë, do të dërgojmë ty fjalë të rënda –'),
(5481, 73, 6, 'sepse, zgjuarja natën, me të vërtetë, vepron më fuqishëm dhe shprehjet (mësimet) janë më të kthjellta,'),
(5482, 73, 7, 'meqë ti, me të vërtetë, ditën je shumë i angazhuar.'),
(5483, 73, 8, 'Dhe përmende emrin e Zotit tënd dhe përkushtoju krejtësisht Atij.'),
(5484, 73, 9, 'Ai ësht Zot i lindjes dhe i perëndimit, s’ka zot tjetër përveç Tij, dhe Atë merre për mbrojtës!'),
(5485, 73, 10, 'Dhe, duroji llomotitjet e atyre dhe largohu nga ata në mënyrë të bukur,'),
(5486, 73, 11, 'e lermëni Mua (t’i qeroj hesapet) me ata që të konsiderojnë (ty) për gënjeshtar, të cilët jetojnë në luks dhe jepju pak afat atyre:'),
(5487, 73, 12, 'se, padyshim, Ne kemi pranga dhe zjarr,'),
(5488, 73, 13, 'dhe gjellëra që ngelin në fyt, dhe dënime të dhembshme'),
(5489, 73, 14, 'në Ditën kur Toka dhe malet dridhen e bëhen si grumbuj të shkapërderdhur të rërës.'),
(5490, 73, 15, 'Ne, juve, me të vërtetë, ju kemi dërguar një Profet dëshmitar kundër jush, ashtu siç ia kemi pas dërguar një profet Faraonit,'),
(5491, 73, 16, 'por Faraoni e ka kundërshtuar Profetin, andaj e kemi dënuar atë me dënim të rëndë.'),
(5492, 73, 17, 'Qysh do të ruheni ju (nga dënimet) nëse mbeteni jobesimtarë Ditën në të cilën fëmijët bëhen të thinjët (prej tmerrit të madh)?!'),
(5493, 73, 18, 'Atë ditë, qielli do të çahet (nga tmerri), e premtimi i Zotit do të jetë i plotësuar.'),
(5494, 73, 19, 'Kjo, me të vërtetë, është këshillë; dhe kush dëshiron – le ta zgjedhin rrugën (që shpie) te Zoti i tij.'),
(5495, 73, 20, 'Me të vërtetë, Zoti yt e di, se ti (o Muhammed) lutesh më pak se dy të tretat e natës, ndonjëherë gjysmën ose të tretën e saj, si edhe disa prej atyre që janë me ty. Zoti e ka caktuar gjatësinë e ditës dhe të natës, Ai e di se ju nuk mundeni me e llogaritur atë, andaj ju ka bërë lehtësi; e ju lexoni Kur’an aq sa mundeni; Ai e di që ndokush nga ju do të jetë i sëmurë, kurse disa të tjerë udhëtojnë nëpër botë, duke kërkuar dhuntitë e Perëndisë (për jetesë), dhe të tjerë që luftojnë në rrugën e Perëndisë – prandaj, lexoni ju sa keni mundëis nga Kur’ani, dhe kryeni namazin dhe jepnie zeqatin, dhe për hir të Perëndisë jepnihua, sepse, çdo të mirë që e përgatitni ju për vete më parë, do ta gjeni te Perëndia më të mirë dhe (do të keni) shpërblim më të madh. Kërkoni falje te Perëndia, se, Perëndia, me të vërtetë, është falës dhe mëshirues i madh.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'O ti, i mbuluar (Muhammed)!'),
(5497, 74, 2, 'Çou dhe lajmëroi!'),
(5498, 74, 3, 'Dhe Zotin tënd madhëroje!'),
(5499, 74, 4, 'Dhe petkat tuaja pastroji!'),
(5500, 74, 5, 'Dhe largoju (veprave) të këqia!'),
(5501, 74, 6, 'Dhe mos ndajë me qëllim që të marrësh më shumë!'),
(5502, 74, 7, 'Dhe për hir të Zotit tënd, duro'),
(5503, 74, 8, 'e, kur të fryhet në buri –'),
(5504, 74, 9, 'do të jetë ato ditë e rëndë,'),
(5505, 74, 10, 'për jobesimtarët nuk do të jetë e lehtë.'),
(5506, 74, 11, 'Lemëni Mua dhe atë që e kam krijuar Unë, vet,'),
(5507, 74, 12, 'dhe i kam dhënë pasuri kolosale,'),
(5508, 74, 13, 'dhe djemtë që i ka të pranishëm,'),
(5509, 74, 14, 'dhe nderë e prestigj i kam dhuruar'),
(5510, 74, 15, 'dhe përpos kësaj - dëshiron, që Unë t’ia zmadhojë (të mirat)!'),
(5511, 74, 16, 'Jo, kurrsesi! Me të vërtetë, ai është mohues kryeneç i ajeteve Tona;'),
(5512, 74, 17, 'e Unë do ta ngarkojë atë me dënime të rënda,'),
(5513, 74, 18, 'sepse, ka paramenduar dhe ka kurdisur (kundër Kur’anit), -'),
(5514, 74, 19, 'dhe, qoftë mallkuar, si e ka kurdisur!'),
(5515, 74, 20, 'dhe sërish – edhe njëherë, qoftë mallkuar, si e ka kurdisur! –'),
(5516, 74, 21, 'Pastaj ka shikuar'),
(5517, 74, 22, 'e, u vrejt dhe iu mrrol (fytyra)'),
(5518, 74, 23, 'dhe u kthye (nga mendimi i drejtë) dhe u kapardis,'),
(5519, 74, 24, 'dhe tha: “Ky (Kur’ani) nuk është gjë tjetër, përpos magji trashëguese,'),
(5520, 74, 25, 'këto janë vetëm fjalë njeriu!”'),
(5521, 74, 26, 'Do ta hudhi (dënojë) në Sekar (Skëterrë) –'),
(5522, 74, 27, 'e kush të shpjegon ty se çka është Sekari?'),
(5523, 74, 28, 'Ai nuk lë gjë dhe i përfshinë të gjithë (i djegë)'),
(5524, 74, 29, 'lëkurat iu bënë të zeza (nga të djegurit)'),
(5525, 74, 30, 'mbikëqyrës të tij (Skëterrës) janë nëntëmbëdhjetë (engjëj). –'),
(5526, 74, 31, 'Na, kemi bërë rojtarë të zjarrit (engjëj) dhe kemi caktuar numrin e tyre, vetëm për t’i provuar jobesimtarët; për ‘tu bindur njerëzit, të cilëve, u është dhënë Libri, që t’u shtohet (forcohet) besimi atyre që besojnë, e mos të dyshojnë, ata, të cilëve u është dhënë Libri, - dhe për të thënë ata që janë zemërkëqinj (hipokrit) dhe jobesimtarë: “Ç’ka dashur Zoti (të tregojë) me këtë shembull?” Kështu, Perëndia i lë në hutim (dhe në humbje) ata që do, dhe në rrugë të drejtë e udhëzon atë që don. Askush tjetër, përveç Tij, nuk e di numrin e ushtrisë së Zotit tënd. Kjo është vetëm këshillë për njerëzit.'),
(5527, 74, 32, 'Kurrsesi! Betohem në Hënën'),
(5528, 74, 33, 'dhe në natën që largohet,'),
(5529, 74, 34, 'e mëngjesin që zbardhëllon, -'),
(5530, 74, 35, 'ajo (skëterra), me të vërtetë, është njëra nga fatkeqësitë më të mëdha,'),
(5531, 74, 36, 'kjo është si këshillim –'),
(5532, 74, 37, 'për ata nga ju që dëshirojnë të bëjnë mirë ose të abstenojnë (në këtë)!'),
(5533, 74, 38, 'Çdo njeri është peng (përgjegjës) për veprat e veta,'),
(5534, 74, 39, 'përveç fatlumëve;'),
(5535, 74, 40, 'të cilët në kopshtijet e xhenneti i pyesin fajtorët:'),
(5536, 74, 41, '-'),
(5537, 74, 42, '“Çka u ka sjellë juve në Sekar (Skëterrë)?”'),
(5538, 74, 43, 'Ata do të përgjigjen: “Nuk kemi qenë nga ata që kanë bërë namaz'),
(5539, 74, 44, 'dhe prej atyre që i kanë ushqyer të varfërit'),
(5540, 74, 45, 'dhe jemi lëshuar në biseda të kota me djerraditësit (badihavxhinjtë),'),
(5541, 74, 46, 'dhe kemi përgënjeshtruar Ditën e Kijametit,'),
(5542, 74, 47, 'përderisa nuk na erdhi vdekja”.'),
(5543, 74, 48, 'Kurrfarë ndërmjetësimi i ndërmjetësuesve nuk do t’u ndihmojë atyre.'),
(5544, 74, 49, 'E, përse shmangën ata nga këshillimi,'),
(5545, 74, 50, 'thua se janë gomarë të egër,'),
(5546, 74, 51, 'të frikësuar, që kanë ikur prej luanit.'),
(5547, 74, 52, 'Po! Çdonjëri nga ata do të kërkojë që t’i jepet rregjistri i qartë (nga Zoti).'),
(5548, 74, 53, 'Kurrsesi, pasi që ata nuk i frikohen Ditës së Kijametit!'),
(5549, 74, 54, 'Ai (Kur’ani), në të vërtetë, është këshillë,'),
(5550, 74, 55, 'dhe kush të dojë, le ta pranojë,'),
(5551, 74, 56, 'e këshillohen vetëm ata që do Perëndia; Ai është i vetmi të cilit duhet t’i druajmë dhe Ai është i vetmi falës.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Betohem në Ditën e Kijametit'),
(5553, 75, 2, 'dhe betohem në shpirtin e atij që e qorton vetveten.'),
(5554, 75, 3, 'A mendon njeriu që nuk do të mund t’ia mbledhim eshtrat e tij?'),
(5555, 75, 4, 'Po si jo, Ne mundemi me ia bë majet e gishtave ashtu si i ka pasur.'),
(5556, 75, 5, 'Por, njeriu dëshiron të gabojë përderisa të jetë gjallë,'),
(5557, 75, 6, 'e pyet: “Kur do të jetë Dita e Kijametit?”'),
(5558, 75, 7, 'Kur syri (shikimi) të shqetësohet nga frika'),
(5559, 75, 8, 'dhe të zihet Hëna,'),
(5560, 75, 9, 'dhe Dielli e Hëna të bashkohen,'),
(5561, 75, 10, 'atë ditë njeriu, thërret: “Ku të ikim?”'),
(5562, 75, 11, 'Askund! S’ka strehim.'),
(5563, 75, 12, 'Atë ditë, strehimi është te Zoti yt,'),
(5564, 75, 13, 'atë ditë, njeriu informohet për veprat që i ka punuar, si dhe për ato që i ka lëshuar,'),
(5565, 75, 14, 'njeriu vetë do të dëshmojë kundër vetvetes,'),
(5566, 75, 15, 'madje – edhe në qoftë se i paraqet arsyetimet e veta, (ato nuk pranohen).'),
(5567, 75, 16, 'Mos nxito në të lexuarit e Kur’anit me gjunë tënde,'),
(5568, 75, 17, 'Ne, do t’i tubojmë pjesët e tij (Kur’ani), për ta lexuar atë (si është).'),
(5569, 75, 18, 'E, kur ta lexojë Xhebraili atë (Kur’anin), përcille ti leximin e tij,'),
(5570, 75, 19, 'e pastaj, Ne do ta shpjegojmë.'),
(5571, 75, 20, 'Po, me të vërtetë! Ju e doni këtë jetë,'),
(5572, 75, 21, 'kurse, për jetën tjetër nuk keni kujdes.'),
(5573, 75, 22, 'Atë ditë – disa fytyra do të shkëlqejnë,'),
(5574, 75, 23, 'në Zotin e tyre do të shikojnë;'),
(5575, 75, 24, 'atë ditë – disa fytyra janë të mrrolura,'),
(5576, 75, 25, 'e dinë që do t’i gjejë fatkeqësi e madhe.'),
(5577, 75, 26, 'E, shiko! Kur të vijë shpirti në fyt,'),
(5578, 75, 27, 'dhe thërret: “A ka shërues?”'),
(5579, 75, 28, 'dhe ai do të bindet se ai është çasti i ndarjes'),
(5580, 75, 29, 'dhe këmba me këmbën lidhen,'),
(5581, 75, 30, 'atë ditë do të sillet të Zoti yt:'),
(5582, 75, 31, '“Ai nuk ka besuar dhe nuk është lutur (falë namaz),'),
(5583, 75, 32, 'por ka mohuar dhe është shmangë,'),
(5584, 75, 33, 'e pastaj ka shkuar te të afërmit (familja) e tij, duke u krenuar”.'),
(5585, 75, 34, 'Mjer për ty! Mjer për ty!'),
(5586, 75, 35, 'E, përsëri: Mjer për ty! Mjer për ty!'),
(5587, 75, 36, 'Vallë, a mendon njeriu se do të mbetet pa obligime (pa përgjegjësi)?'),
(5588, 75, 37, 'A s’ka qenë njeriu së pari pikë fare (sperme) që hudhet lehtë,'),
(5589, 75, 38, 'pastaj u bë gjak i doçkuar, nga i cili, Ai (Perëndia), e ka krijuar dhe e ka formur të plotë.'),
(5590, 75, 39, 'Dhe, prej tij i bëri çiftet: mashkullin e femrën,'),
(5591, 75, 40, 'e, vallë, Ai krijuesi, a nuk ka fuqi edhe t’i ngjallë të vdekurit?”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Pa dyshim ka qenë kohë e gjatë (ajo), e që njeriu nuk është përmendur (nuk ka ekzistuar).'),
(5593, 76, 2, 'Me të vërtetë, Ne e kemi krijuar njeriun nga një farë e përzier (spermës); për ta provuar, dhe e bëmë atë që të dëgjojë dhe të shoh;'),
(5594, 76, 3, 'Na, ia treguam atij rrugën, e ai (do të jetë) ose mirënjohës ose jomirënjohës;'),
(5595, 76, 4, 'Na, me të vërtetë, për jobesimtarët kemi përgatitur zingjirë e pranga dhe flakë të zjarrit.'),
(5596, 76, 5, 'Sigurisht, të ndershmit besimtarë do të pinë nga kupa (në xhennet), pije të përbërë me kambor.'),
(5597, 76, 6, 'Nga burimi që vetëm adhuruesit e Perëndisë pinë, dhe të cilin mund ta çojnë (bartin) kah të duan;'),
(5598, 76, 7, 'ata (njerëz) që e zbatojnë premtimin dhe i druajnë Ditës, tmerri i së cilës përhapet gjithëkah,'),
(5599, 76, 8, 'dhe i ushqejnë të varfërit, bonjakët dhe të zënët robë (në luftë), madje edhe pse janë vetë nevojtarë për te (ushqim).'),
(5600, 76, 9, '(Thonë ata): “Na, ju ushqejmë vetëm për hir të Zotit. Na – për këtë, nuk duam shpërblim as falenderim!'),
(5601, 76, 10, 'Na i druajmë Zotit tonë atë Ditë kur fytyrat janë të vrenjtura dhe të ashpra”.'),
(5602, 76, 11, 'Perëndia i ruan ata nga tmerret e asaj Dite; Ai atyre u jep shkëlqim (në fytyrat e tyre) dhe gëzim,'),
(5603, 76, 12, 'dhe, për durimin e tyre (në të drejtë), Ai i shpërblen ata me xhennet dhe (petka) të mëndafshta:'),
(5604, 76, 13, '(ata) të mbështetur në kanape, nuk do ta ndjejnë as vapën (e Diellit) dhe as të ftohtit (e dimërit),'),
(5605, 76, 14, 'dhe do t’u jetë afër tyre hija e pemëve, frutet e të cilave do të mund t’i shfrytëzojnë (kapin) lehtë.'),
(5606, 76, 15, 'Do të shërbehen ata nga enët e argjendta dhe kupat (si shtamba) e tejdukshme,'),
(5607, 76, 16, 'të tejdukshme, nga argjendi, madhësinë e të cilave (do ta zgjedhin) sipas dëshirës.'),
(5608, 76, 17, 'Në të (xhennet) do të pinë shishe pijesh të përziera me Zenxhebil'),
(5609, 76, 18, 'nga burimi i xhennetit, (pija) e quajtur Selsebil.'),
(5610, 76, 19, 'Atyre do t’u shërbejnë djelmoshat (shërbyesit) përherë të rinj, - që, kur t’i shikosh, duken se janë margaritarë të shpërndarë.'),
(5611, 76, 20, 'Dhe, kahdo të shikosh (në xhennet), do të shohësh komoditet e begati të gjerë.'),
(5612, 76, 21, 'Ata do të janë të veshur me petka të holla mëndafshi të gjelbërt, dhe nga mëndafsi i trashë, të stolisur me bylyzykë të argjendtë, dhe do t’u jap Zoti i tyre të pinë pije të pastërta (të mrekullueshme).'),
(5613, 76, 22, '(Ju thuhet atyre): “Ky është shpërblimi juaj; përpjekja juaj (në vepra të mira) është e falenderuar.'),
(5614, 76, 23, 'Me të vërtetë, Na ta kemi shpallur Kur’anin ty në intervale (kohë pas kohe),'),
(5615, 76, 24, 'Duro (prite) vendimin e Zotit tënd, e mos dëgjo as mëkatarët, as jobesimtarët!'),
(5616, 76, 25, 'Dhe, përmende emrin e Zotit tënd në mëngjes dhe në mbrëmje,'),
(5617, 76, 26, 'dhe, në një pjesë të natës kryej detyrat (falu) për Zotin, dhe falenderoje Atë natën, gjatë!'),
(5618, 76, 27, 'E, me të vërtetë, këta (jobesimtarët) e duan jetën e këtushme, e nuk e përfillin Ditën e vështirë (Kijametin) që do t’u vie.'),
(5619, 76, 28, 'Na i kemi krijuar ata dhe Na ua kemi dhënë forcën (e trupit të tyre).'),
(5620, 76, 29, 'Kjo, me të vërtetë, është këshillë, e kush të dojë, ndjekë rrugën që shpie te Zoti i tij, -'),
(5621, 76, 30, 'por ju nuk mund të duani, përveç nëse don Perëndia – se, Perëndia, me të vërtetë, është i Plotëdijshëm dhe i Gjithëdijshëm,'),
(5622, 76, 31, 'atë që don Ai, e shpie në mëshirën e Vet, e jobesimtarëve u ka përgatitur vuajtje të rënda.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Betohem në ata (engjëjt) që sjellin të mira,'),
(5624, 77, 2, 'e betohem në ata që shpejtojnë si stuhia;'),
(5625, 77, 3, 'dhe betohem në ata që sjellin shpalljet e pastaj dallojnë (të mirën nga e keqja), -'),
(5626, 77, 4, '?'),
(5627, 77, 5, 'në ata që përcjellin këshillat,'),
(5628, 77, 6, 'e arsyeshme ose qortuese, -'),
(5629, 77, 7, 'me të vërtetë, ajo ë ju premtohet, do të bëhet!'),
(5630, 77, 8, 'Kur yjet do të shuhen'),
(5631, 77, 9, 'dhe kur të çahet qielli'),
(5632, 77, 10, 'dhe kur malet të shkapërderdhen'),
(5633, 77, 11, 'dhe kur të dërguarit (pejgamberët) do të caktojnë kohën (e tubimit).'),
(5634, 77, 12, '(Do të thuhet): “Deri në cilën ditë është shtyer afati?” –'),
(5635, 77, 13, '(Ju përgjigjet): “Deri në Ditën e vendimit!”.'),
(5636, 77, 14, 'E, a e di ti se ç’është Dita e vendimit?'),
(5637, 77, 15, 'Mjer atë ditë për përgënjeshtarët!'),
(5638, 77, 16, 'A nuk i kemi zhdukur popujt e lashtë,'),
(5639, 77, 17, 'e pas tyre edhe të tjerët?!'),
(5640, 77, 18, 'Në këtë mënyrë do të veprojmë me të gjithë fajtorët!'),
(5641, 77, 19, 'Mjer atë ditë për përgënjeshtarët!'),
(5642, 77, 20, 'A nuk ju kemi krijuar (Ne) nga një pikë uji të pakonsiderueshëm,'),
(5643, 77, 21, 'të cilin e kemi vendosur në vend të sigurtë,'),
(5644, 77, 22, 'dei në afatin e caktuar?!'),
(5645, 77, 23, 'E tillë është fuqia Jonë; e sa të fuqishëm jemi Ne! –'),
(5646, 77, 24, 'Mjer atë ditë për përgënjeshtarët!'),
(5647, 77, 25, 'A nuk e bëmë Ne Tokën vendtubimi'),
(5648, 77, 26, 'të të gjallëve dhe të të vdekurëve'),
(5649, 77, 27, 'dhe në të kemi vendosur male të larta, dhe a nuk ju dhamë Ne juve të pini ujë të ëmbël (të pijes)? –'),
(5650, 77, 28, 'Mjer atë ditë për përgënjeshtarët!'),
(5651, 77, 29, '(Ju thuhet): “Shkoni kah ajo (dënimi), të cilën ju e keni konsideruar gënjeshtër,'),
(5652, 77, 30, 'shkoni kah tymi i ndarë në tri fjollë,'),
(5653, 77, 31, 'i cili nuk të freskon dhe nga flaka nuk shpëton.'),
(5654, 77, 32, 'Ai hedh shkëndija si rrënjët e lisit të madh,'),
(5655, 77, 33, 'sikur t’ishin deve (gamilla) të verdha. –'),
(5656, 77, 34, 'Mjer atë ditë për përgënjeshtarët!'),
(5657, 77, 35, 'Kjo është ajo ditë kur ata nuk mund të flasin'),
(5658, 77, 36, 'dhe nuk jepet leje për ta, për t’u arsyetuar. –'),
(5659, 77, 37, 'Mjer atë ditë për përgënjeshtarët!'),
(5660, 77, 38, '(Ju thuhet): Kjo është Dita e gjykimit, në të cilën juv edhe popujt e lashtë i kemi tubuar,'),
(5661, 77, 39, 'e nëse dispononi me ndonjë dinakëri, përdoreni kundër Meje! –'),
(5662, 77, 40, 'Mjer atë ditë për përgënjeshtarët!'),
(5663, 77, 41, 'Ata që ruhen nga të kundërshtuarit Perëndinë, do të jenë në mesin e burimeve dhe të hijeve,'),
(5664, 77, 42, 'dhe midis pemëve çfarë i dëshirojnë.'),
(5665, 77, 43, '(Ju thuhet): “Hani e pini, e ju bëftë mirë! (Shpërblim) për atë që keni punuar”.'),
(5666, 77, 44, 'Kështu Na i shpërblejmë ata, që bëjnë vepra të mira. –'),
(5667, 77, 45, 'Mjer atë ditë për përgënjeshtarët!'),
(5668, 77, 46, '(Ju thuhet jobesimtarëve): “Hani dhe kënaquni, por për pak kohë!”'),
(5669, 77, 47, 'Mjer atë ditë për përgënjeshtarët!'),
(5670, 77, 48, 'Por, kur ju thuhet atyre (në këtë jetë): “Përuluni ndaj Perëndisë (falni namazin)!” – ata nuk përulen. –'),
(5671, 77, 49, 'Mjer atë ditë për përgënjeshtarët!'),
(5672, 77, 50, 'E, në çfarë libri tjetër do të besojnë, pas Kur’anit?!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'Për çka pyesin ata njëri-tjetrin? –'),
(5674, 78, 2, 'Për lajmin e madh,'),
(5675, 78, 3, 'për të cilin ata kanë mendime të ndryshme.'),
(5676, 78, 4, 'Jo, jo, ata, me siguri, do ta dinë!'),
(5677, 78, 5, 'E pastaj, sërish, me të vërtetë, do ta dinë!'),
(5678, 78, 6, 'A nuk u kemi bërë ne Tokën shtrat?'),
(5679, 78, 7, 'E malet shtyll?'),
(5680, 78, 8, 'Ne ju kemi krijuar dy lloje (mashkull e femër),'),
(5681, 78, 9, 'dhe gjumin tuaj ua kemi bërë çlodhje,'),
(5682, 78, 10, 'kure natën ua kemi bërë mbulesë,'),
(5683, 78, 11, 'dhe ditën e kemi caktuar për jetesë (ekonomizim),'),
(5684, 78, 12, 'dhe mbi ju kemi ndërtuar shtratë qiej të fortë.'),
(5685, 78, 13, 'Dhe kemi krijur kandil (diell) të shëndritshëm!'),
(5686, 78, 14, 'Ne, nga retë, u leshojmë ujëra të mëdha (shira)'),
(5687, 78, 15, 'për rritjen e drithërave dhe bimëve,'),
(5688, 78, 16, 'dhe kopshteve të dendura.,'),
(5689, 78, 17, 'Me të vërtetë, Dita e sqarimit, është caktuar,'),
(5690, 78, 18, 'Dita kur do të fryhet në buri (Sur), e do të vini ju, grupe-grupe,'),
(5691, 78, 19, 'dhe qielli do të hapet dhe do të bëhet dyer,'),
(5692, 78, 20, 'dhe malet lëvizin e bëhen si hije e ajrit (fatamorganë).'),
(5693, 78, 21, 'Me të vërtetë, Skëterra do të bëhet pusi,'),
(5694, 78, 22, 'për të këqinjtë vendbanim, -'),
(5695, 78, 23, 'që aty të mbeten përgjithmonë:'),
(5696, 78, 24, 'në të, nuk do të shijojnë as freskim, as pije,'),
(5697, 78, 25, 'përpos ujit të vluar dhe qelbësirave,'),
(5698, 78, 26, '(si) shpërblim gjegjës (i veprave të tyre).'),
(5699, 78, 27, 'Ata, me të vërtetë, nuk kanë menduar në dhënie të llogarisë,'),
(5700, 78, 28, 'dhe argumentet Tona i kanë mohuar krejtësisht,'),
(5701, 78, 29, 'por Ne, çdo gjë e kemi numërura dhe shënuar.'),
(5702, 78, 30, '“Andaj, shijoni, Ne do t’ju shtojmë vetëm dënimin”.'),
(5703, 78, 31, 'E, me të vërtetë, për ata që i kanë druajtur Perëndisë, u janë përgatitur vende kënaqësie:'),
(5704, 78, 32, 'kopshte dhe vreshta,'),
(5705, 78, 33, 'dhe vajza të reja, të njëjtës moshë,'),
(5706, 78, 34, 'dhe kupa të plota.'),
(5707, 78, 35, 'Nuk do të dëgjojnë atje (në xhennet) biseda të kota as gënjeshtra,'),
(5708, 78, 36, '(ky është) shpërblimi i Zotit tënd, i dhënë sipas llogarisë (së veprave të tyre),'),
(5709, 78, 37, 'Zotit të qiejve dhe të Tokës, dhe çka gjendet në mes tyre, Mëshiruesi, të cilit askush nuk do të mund t’i bëzejë (flas),'),
(5710, 78, 38, 'në Ditën kur Xhebraili dhe engjëjt do të jenë të rreshtuar, e nuk do të flas askush, përpos atij që do t’i lejojë Mëshiruesi, e që do të thonë të vërtetën.'),
(5711, 78, 39, 'Ajo është Ditë e pashmangshme, e kush të dojë, do ta marr strehimin te Zoti i vet.'),
(5712, 78, 40, 'Me të vërtetë, Ne u kemi paralajmëruar se është i afërt dënimi, në Ditën kur njeriu do t’i shohë veprat e tija, dhe, kur jobesimtari, do të thotë: “Ah, të isha dhé!”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Betohem në ata (engjëj) që i marrin shpirtërat ashpër (jombesimtarëve)'),
(5714, 79, 2, 'dhe në ata që i marrin butësisht (besimtarëve)'),
(5715, 79, 3, 'dhe në ata (engjëj) që notojnë në hapësirë (në mes Tokës e qiellit),'),
(5716, 79, 4, 'si dhe në ata (engjëj) që garojnë (për kryerjen e urdhërave),'),
(5717, 79, 5, 'dhe betohem në ata (engjëj) që i rregullojnë ato që nuk janë të rregulluara,'),
(5718, 79, 6, 'në Ditën kur Toka do të dridhet nga tërmeti,'),
(5719, 79, 7, 'ps të cilit pason tjetri. –'),
(5720, 79, 8, 'Atë ditë, zemrat do të jenë të tronditura,'),
(5721, 79, 9, 'e shikimet e tyre të përulura.'),
(5722, 79, 10, 'Ata (jobesimtarët) thonë: “A, me të vërtetë, do të kthehemi ne – në jetë (në gjendjen e mëparshme)?'),
(5723, 79, 11, 'A thua edhe kur të bëhemi eshtra të kalbur?”'),
(5724, 79, 12, 'E thonë: “E atëherë (të kthyerit), do të jemi të humbur.'),
(5725, 79, 13, 'E do të jetë ajo (ringjallja) vetëm një thirrje,'),
(5726, 79, 14, 'dhe, ja ata përnjëherësh (të gjallë) në Tokë.'),
(5727, 79, 15, 'A të ka ardhur ty (o Muhammed) lajmi për Musain,'),
(5728, 79, 16, 'kur Zoti i tij e ka thirrë atë në luginën e shenjtë të Tuvasë:'),
(5729, 79, 17, '“Shko te Faraoni, se ai, me të vërtetë, është tërbuar,'),
(5730, 79, 18, 'dhe thuaj (Faraonit): ‘A don ti të pastrohesh (nga të këqiat),'),
(5731, 79, 19, 'dhe të udhëzoj te Zoti yt, që t’i druhesh (për të mos bërë të këqia)?'),
(5732, 79, 20, 'Dhe, ia tregoi (Musai-Faraonit) mrekullinë më të madhe,'),
(5733, 79, 21, 'por ai e mohoi dhe e kundërshtoi,'),
(5734, 79, 22, 'pastaj u kthye,'),
(5735, 79, 23, 'u përpoq dhe i tuboi njerëzit e thirri:'),
(5736, 79, 24, '“Unë jam zoti juaj suprem (më i lartë)!'),
(5737, 79, 25, 'dhe, Perëndia, ia ekspozoi atij dënimin e kësaj bote dhe të botës tjetër.'),
(5738, 79, 26, 'Kjo është këshillë për ata që i druajnë Perëndisë.'),
(5739, 79, 27, 'E, çka është më vështirë: krijimi i juaj apo i qiellit? Ai e ka ndërtuar (qiellin),'),
(5740, 79, 28, 'lart e ka ngritur kupën e tij dhe e ka përsosur,'),
(5741, 79, 29, 'dhe netët e tij i ka bërë të errta, kurse ditët të ndritshme,'),
(5742, 79, 30, 'e pastaj e rrafshoi (shtriu) Tokën,'),
(5743, 79, 31, 'dhe nga ajo (toka) i nxorri ujërat dhe kullosat,'),
(5744, 79, 32, 'dhe malet i bëri të palëvizshme (i forcoi)'),
(5745, 79, 33, 'për gjallërinë tuaj dhe të bagëtive tuaja.'),
(5746, 79, 34, 'E, kur të arrijë vështirësia më e madhe,'),
(5747, 79, 35, 'Dita kur njeriu e kujton atë çka ka punuar,'),
(5748, 79, 36, 'dhe i tregohet skëterra çdokujt që sheh,'),
(5749, 79, 37, 'atëherë, ai që ka qenë kërcënues,'),
(5750, 79, 38, 'dhe e ka preferuar (dashtë më shumë) jetën e kësaj bote,'),
(5751, 79, 39, 'pë te, me të vërtetë, vendbanim është skëterra.'),
(5752, 79, 40, 'Kurse, ai që i druan madhërisë së Zotit të tij dhe e frenon vete nga epshet,'),
(5753, 79, 41, 'me të vërtetë, vendbanim i tij është xhenneti.'),
(5754, 79, 42, 'Të pyesin ty për Kijametin: “Kur do të ndodhë?”'),
(5755, 79, 43, 'Çka ka rëndësi për ty ta përmendësh atë?!'),
(5756, 79, 44, 'Vetëm Zoti yt di për te.'),
(5757, 79, 45, 'Ti je vetëm paralajmërues i atyre që i druajnë atij (Kijametit);'),
(5758, 79, 46, 'kurse, atë ditë, që do ta shohin atë (Kijametin), atyre do t’u duket se kanë qëndruar në këtë botë, vetëm një mbrëmje apo një mëngjes (të asaj mbrëmjeje).');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Ai (Muhammedi) u mrrol e u kthye (me fytyrë)'),
(5760, 80, 2, 'ngase i erdhi pranë i verbëri.'),
(5761, 80, 3, 'E kush të dëfton ty – ndoshta ai dëshiron të pastrohet (nga mëkatet),'),
(5762, 80, 4, 'ose të këshillohet, e t’i sjell dobi atij këshillimi,'),
(5763, 80, 5, 'e atij që ësthë i pasur (e prezentohet si i panevojshëm për të besuar)'),
(5764, 80, 6, 'atij po i kthehesh'),
(5765, 80, 7, 'sepse, ti nuk ke përgjegjësi, nëse ai nuk don të pastrohet (të besojë);'),
(5766, 80, 8, 'e atij që të vjen ty duke nxituar (për këshillim)'),
(5767, 80, 9, 'dhe ndien drojë'),
(5768, 80, 10, 'e ti atë nuk po e merr në konsiderim.'),
(5769, 80, 11, 'Mos vepro ashtu! Ato janë këshilla,'),
(5770, 80, 12, 'e, kush të dojë, le të mësojë'),
(5771, 80, 13, 'në faqet e (Kur’anit të) çmueshme'),
(5772, 80, 14, 'të lartësuara (të madhërueshme), të pastra,'),
(5773, 80, 15, 'me duart e shkruesve (engjëjve)'),
(5774, 80, 16, 'të ndershëm dhe të drejtë.'),
(5775, 80, 17, 'Mallkuar qoftë njeriu! Sa jomirënjohës që është ai!'),
(5776, 80, 18, 'Prej çkahjes e krijoi Ai (Perëndia) atë (njeriun)?!'),
(5777, 80, 19, 'Prej pikës së farës e krijoi dhe përgatiti për të çdo gjë të dobishme,'),
(5778, 80, 20, 'dhe pastaj ia lehtësoi rrugën (e lindjes),'),
(5779, 80, 21, 'pas kësaj, (Ai) ia caktoi vdekjen dhe bëri që të varroset,'),
(5780, 80, 22, 'dhe, mandej kur të dojë Ai, e ringjallë.'),
(5781, 80, 23, 'Në të vërtetë! Ai – njeriu, ende nuk i ka plotësuar ato që i ka urdhëruar Ai!'),
(5782, 80, 24, 'Le të shikojë njeriu në ushqimin e vet:'),
(5783, 80, 25, 'Ne kemi derdhur ujëra (shira) të bollshme,'),
(5784, 80, 26, 'pastaj e kemi plasaritur tokën me çarje'),
(5785, 80, 27, 'dhe bëjmë që nga ajo të rriten drithërat,'),
(5786, 80, 28, 'dhe rrushi e perimet,'),
(5787, 80, 29, 'dhe ulliri e palma,'),
(5788, 80, 30, 'dhe kopshtet e dendura,'),
(5789, 80, 31, 'dhe pëmët e barishtet,'),
(5790, 80, 32, 'për ju dhe bagëtinë tuaj.'),
(5791, 80, 33, 'E, kur të arrijë zëri i madh shurdhues, -'),
(5792, 80, 34, 'në Ditën kur njeriu ikën nga vëllau i tij'),
(5793, 80, 35, 'dhe nga nëna e babai i tij'),
(5794, 80, 36, 'dhe nga bashkëshortja e fëmijët e tij;'),
(5795, 80, 37, 'atë Ditë, çdonjëri është i preokupuar me gjendjen e vetvetes,'),
(5796, 80, 38, 'disa fytyra atë Ditë, shëndrisin,'),
(5797, 80, 39, 'të qeshura, të gëzuara,'),
(5798, 80, 40, 'kurse disa fytyra, atë Ditë, pluhërosen,'),
(5799, 80, 41, 'errësira do t’i kaplojë,'),
(5800, 80, 42, 'këta janë jobesimtarët, të korruptuarit.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Kur Dielli të humbë shkëlqimin,'),
(5802, 81, 2, 'dhe kur yjet të rrëzohen (errësohen),'),
(5803, 81, 3, 'dhe kur malet të lëvizin,'),
(5804, 81, 4, 'dhe kur devet mbarsë të mbeten pa çoban (të pakujdes),'),
(5805, 81, 5, 'dhe kur shtazët e egra të tubohen,'),
(5806, 81, 6, 'dhe kur devet vlojnë,'),
(5807, 81, 7, 'dhe kur shpirtërat bashkohen me trupëra,'),
(5808, 81, 8, 'dhe kur vajza e varrosur për së gjalli, do të pyetet'),
(5809, 81, 9, 'për çfarë faji është mbytur,'),
(5810, 81, 10, 'dhe kur të shpërndahen listat (librat e veprave të njerëzve),'),
(5811, 81, 11, 'dhe kur qielli zhvendoset,'),
(5812, 81, 12, 'dhe kur të flakërojë skëterra,'),
(5813, 81, 13, 'dhe kur të afrohet xhenneti,'),
(5814, 81, 14, 'çdokush do të di për veprat që ka përgatitur.'),
(5815, 81, 15, 'Dhe – betohem në yjet – që perëndojnë'),
(5816, 81, 16, 'të cilët lëvizin dhe zhduken nga shikimi (horizonti),'),
(5817, 81, 17, 'dhe në natën kur bie terri,'),
(5818, 81, 18, 'dhe agimin kur gdhinë (zbardhëllon drita).'),
(5819, 81, 19, 'Me të vërtetë, Kur’ani është fjala e të Dërguarit fisnik (Xhebrailit),'),
(5820, 81, 20, 'të fuqishëm, të çmuar nga Zoti i Arshit (Kozmosit)'),
(5821, 81, 21, 'të cilin e dëgjojnë (engjëjt) dhe është i besuar (i madh).'),
(5822, 81, 22, 'E shoku juaj (Muhammedi), nuk është i marrë.'),
(5823, 81, 23, 'Ai (Muhammedi) e ka parë qartë (Xhebrailin) në horizont,'),
(5824, 81, 24, 'dhe, kur është fjala për Shpalljen, ai (Muhammedi) nuk është dorështrënguar (nuk e mshehë atë),'),
(5825, 81, 25, 'dhe Kur’ani nuk është fjalë e djallit të mllkuar,'),
(5826, 81, 26, 'e, kah po ia mbani (me këto mendime)?!'),
(5827, 81, 27, 'Ai (Kur’ani), është vetëm këshillë, për tërë botën (njerëzit):'),
(5828, 81, 28, 'për ata nga ju që duan rrugën e vërtetë,'),
(5829, 81, 29, 'e ju nuk mund të dëshironi (të drejtën), përveç atëherë kur dëshiron Perëndia, Zoti i gjithë botërave!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Kur të shkyhet qielli,'),
(5831, 82, 2, 'dhe kur të rrëzohen (derdhën) yjet,'),
(5832, 82, 3, 'dhe kur përzihen detërat (njëri me tjetrin),'),
(5833, 82, 4, 'dhe kur të rrotullohen varret,'),
(5834, 82, 5, 'çdokush do të di për veprat që ka punuar dhe lëshimet (që ka bërë).'),
(5835, 82, 6, 'O njeri, ç’të mashtroi ty që nuk i besove Zotit tënd bujar?'),
(5836, 82, 7, 'Ai të krijoi ty dh bëri që ta kesh (në formë) të harmonizuar tërë organizmin, -'),
(5837, 82, 8, 'dhe çfarë forme deshi (Ai), të dha!'),
(5838, 82, 9, 'Jo, jo! Kurrsesi! Ju po e konsideroni besimin gënjeshtër.'),
(5839, 82, 10, 'Me të vërtetë, ka rojtarë për ju,'),
(5840, 82, 11, 'që janë të nderueshëm tek ne,'),
(5841, 82, 12, 'që i shënojnë veprat tuaja, dinë çka punoni.'),
(5842, 82, 13, 'Të ndershmit, me të vërtetë, do të gjenden në xhennet,'),
(5843, 82, 14, 'kurse fajtorët, me të vërtetë, do të gjenden në skëterrë.'),
(5844, 82, 15, 'Në Ditën e Kijametit (ata) do të digjen në zjarr,'),
(5845, 82, 16, 'dhe kurrë nuk do të dalin prej tij.'),
(5846, 82, 17, 'E, kush të shpjegon ty për Ditën e Kijametit? –'),
(5847, 82, 18, 'dhe edhe njëherë: kush të shpjegon ty ç’është Dita e Kijametit?!'),
(5848, 82, 19, 'Ajo është Ditë kur askush nuk mund t’i ndihmojë ndokujt asgjë, atë ditë pushteti i tërë është vetëm i Perëndisë.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Mjer për ata që kur masin – mungojnë,'),
(5850, 83, 2, 'të cilët e marrin masën e plotë kur është e marrin vetveten,'),
(5851, 83, 3, 'e, kur t’iu masin të tjerëve me litër ose me kandar – ju pakësojnë.'),
(5852, 83, 4, 'A nuk mendojnë ata se do të ringjallen'),
(5853, 83, 5, 'në një Ditë të madhe (të Kijametit),'),
(5854, 83, 6, 'atë Ditë kur njerëzit do të dalin para Zotit të gjithësisë!'),
(5855, 83, 7, 'Po, po, me të vërtetë, Libri i mëkatarëve është në Sixh-xhin,'),
(5856, 83, 8, 'e, ata e di ti se ç’është Sixh-xhin?'),
(5857, 83, 9, '(Ai është) Libri i shkruar!'),
(5858, 83, 10, 'Mjer atë ditë për përgënjeshtarët,'),
(5859, 83, 11, 'që kanë mohuar Ditën e Kijametit –'),
(5860, 83, 12, 'e atë, e mohon vetëm kundërvajtësi, mëkatari (i madh),'),
(5861, 83, 13, 'i cili, kur i janë lexuar ajetet Tona, ka thënë: “Trillime të popujve të lashtë!”'),
(5862, 83, 14, 'E, nuk është ashtu! Por, veprat e tyre (të këqia) i kanë ndryshkur zemrat e tyre (i kanë nxirë).'),
(5863, 83, 15, 'Jo, jo, atë ditë, me të vërtetë, ata do të jenë të penguar që ta shohin Zotin e tyre,'),
(5864, 83, 16, 'pastaj, me siguri, do të hyjnë në skëterrë,'),
(5865, 83, 17, 'e do t’ju thuhet (nga engjëjt e skëterrës): “Qe, kjo është ajo që e keni mohuar!”'),
(5866, 83, 18, 'Po, po, me të vërtetë, Libri i të ndershmëve është në Il-lijjin,'),
(5867, 83, 19, 'E, a e di ti se ç’është Il-lijjin?'),
(5868, 83, 20, 'Ai është Libri i shkruar'),
(5869, 83, 21, 'që e dëshmojnë të afërmit (engjëjt) e Perëndisë.'),
(5870, 83, 22, 'Me të vërtetë, të mirët (njerëz) do të gjenden në xhennet,'),
(5871, 83, 23, 'e nga divanet – kanapet do të shikojnë,'),
(5872, 83, 24, 'në fytyrat e tyre do të njohësh freskinë e begative,'),
(5873, 83, 25, 'atyre do t’u jepet të pinë nga pijet e mbyllura (të ruajtura),'),
(5874, 83, 26, 'vula e të cilave është misk (parfem), - dhe për këtë, le të bëjnë gara garuesit! –'),
(5875, 83, 27, '(pija) do të jetë e përzier me ujin e Tesnimit,'),
(5876, 83, 28, 'nga burimi i të cilit do të pinë të afërmit e Perëndisë (të mirët).'),
(5877, 83, 29, 'Me të vërtetë, mëkatarët qeshnin (në jetën e kësaj bote), me ata që besonin;'),
(5878, 83, 30, 'kur kalonin pranë tyre, njëri-tjetrit ia bënin me sy,'),
(5879, 83, 31, 'e kur ktheheshin te familjet e tyre, ata argëtoheshin në formë tallëse (me besimtarët);'),
(5880, 83, 32, 'ata (mëkatarët), kur i shihnin (besimtarët), thonin: “Këta, me të vërtetë, janë të humbur”, -'),
(5881, 83, 33, 'e, ata nuk janë dërguar për kujdestarë të tyre (besimtarëve).'),
(5882, 83, 34, 'Sot, ata që kanë besuar, do t’i përqeshin mohuesit,'),
(5883, 83, 35, 'e, nga divanet (kanapet) do të shikojnë'),
(5884, 83, 36, 'a janë shpërblyer meritueshëm mohuesit, për veprat që kanë punuar?! (Po).');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Kur qiellit të shkyet,'),
(5886, 84, 2, 'dhe të dëgjojë me përvujtni Zotin e vet – siç i përket'),
(5887, 84, 3, 'dhe kur Toka do të shtrihet (me shafitjen) e maleve'),
(5888, 84, 4, 'dhe ta hedhë gjithë atë që ka brenda e të zbrazët'),
(5889, 84, 5, 'dhe të dëgjojë me përvujtni Zotin e vet – siç i përket –'),
(5890, 84, 6, 'ti, o njeri, që përpiqesh shumë, mundin tënd do ta gjesh te Zoti yt:'),
(5891, 84, 7, 'atij që do t’i jepet libri i tij në dorën e tij të djathtë'),
(5892, 84, 8, 'lehtë do të japi llogari,'),
(5893, 84, 9, 'dhe do të kthehet i gëzueshëm te të afërmit e tij;'),
(5894, 84, 10, 'e, atij që do t’i jepet libri i tij pas shpinës,'),
(5895, 84, 11, 'e thërret shkatërrimi'),
(5896, 84, 12, 'dhe në zjarr do të digjet,'),
(5897, 84, 13, 'sepse, (në jetë) ka qenë i gëzueshëm në familjen e tij,'),
(5898, 84, 14, 'dhe ka menduar se kurrë nuk do të kthehet (ringjallet për të dhënë llogari).'),
(5899, 84, 15, 'Po, po, do të kthehet te Zoti i tij, me të vërtetë, Ai i sheh të gjitha punët e tij!'),
(5900, 84, 16, 'Dhe, betohem në kuqërrimin e mbrëmjes,'),
(5901, 84, 17, 'dhe natën, dhe në atë që paklon ajo me errësirën e saj,'),
(5902, 84, 18, 'dhe hënën e plotë, -'),
(5903, 84, 19, 'ju, me siguri, do të gjendeni në rrethana të vështira dhe më të vështira!'),
(5904, 84, 20, 'E, çka kanë ata, që nuk duan të besojnë,'),
(5905, 84, 21, 'e, përse kur t’u lexohet Kur’ani, nuk përulen (nuk bëjnë sexhde)!'),
(5906, 84, 22, 'E, madje, jobesimtarët, e përgënjeshtrojnë atë (Kur’anin),'),
(5907, 84, 23, 'e Perëndia, me të vërtetë, di çka fshehin ata (në zemrat e tyre),'),
(5908, 84, 24, 'prandaj, sihariqoi ata me dënim të dhembshëm!'),
(5909, 84, 25, 'Por, ata që besojnë dhe punojnë vepra të mira, do të shpërblehen pandërprerë.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Betohem në qiellin e stolisur me hyllsi,'),
(5911, 85, 2, 'dhe në Ditën e premtuar (Kijametit),'),
(5912, 85, 3, 'dhe në dëshmitarët, si dhe në atë që do të dëshmohet (atë ditë),'),
(5913, 85, 4, 'mallkuar qofshin ata ë kanë hapur hendeqe,'),
(5914, 85, 5, 'dhe që i mbushën me zjarr, me gjithëfarë lënëdësh djegëse,'),
(5915, 85, 6, '(mallkuar qofshin ata) kur u ulën pranë tij'),
(5916, 85, 7, 'dhe ishin prezent të asaj (që u bënë besimtarëve)!'),
(5917, 85, 8, 'E, (jobesimtarët) i munduan ata vetëm pse i besuan Zotit të Plotëfuqishëm dhe Falenderues,'),
(5918, 85, 9, 'pushteti i të cilit shtrihet në qiej dhe në Tokë, - e Perëndia është Dëshmitar i çdo gjëje.'),
(5919, 85, 10, 'Pa dyshim, ata që i torturojnë besimtarët dhe besimtaret e që (pastaj) nuk pendohen – i pret dënimi i skëterrës dhe (për ta ka) dënim zjarri,'),
(5920, 85, 11, 'e, me të vërtetë, ata që besojnë dhe punojnë vepra të mira – për ta ka kopshte të xhennetit, nëpër të cilët rrjedhin lumenjtë; - e ky është sukses i madh.'),
(5921, 85, 12, 'Me të vërtetë, ndëshkimi i Zotit tënd, është i ashpër!'),
(5922, 85, 13, 'Me të vërtetë, Ai krijon prej kurrgjëje dhe përsëri i kthen (ps vdekjes – i ringjallë);'),
(5923, 85, 14, 'Ai është falës dhe plot dashuri,'),
(5924, 85, 15, 'Zot i Arshit (fronit) të Madhërueshëm,'),
(5925, 85, 16, 'Ai punon çka të dojë.'),
(5926, 85, 17, 'A ka arritur te ti lajmi për ushtritë'),
(5927, 85, 18, 'e Faraonit dhe Themudit?'),
(5928, 85, 19, 'Por, jobesimtarët – gjithmonë mohojnë,'),
(5929, 85, 20, 'e Perëndia (i Plotëfuqishëm), i përfshinë të gjithë,'),
(5930, 85, 21, 'ky Kur’an – (që e mohonin ata), është Kur’ani i madhërueshëm,'),
(5931, 85, 22, 'i shkruar në Levhi Mahfudh (në pllakën e ruajtur me kujdes).');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Betohem në qiellin dhe yllin e natës.'),
(5933, 86, 2, 'E, a e di ti se ç’është ylli i natës?'),
(5934, 86, 3, 'Ai është ylli që shpon terrin (me dritën e vet).'),
(5935, 86, 4, 'Për çdo njeri ka mbikëqyrës (ndaj tij)!'),
(5936, 86, 5, 'Le të shikojë njeriu prej çkahjes është krijuar:'),
(5937, 86, 6, 'është krijuar nga lëngu që kullon (hudhet),'),
(5938, 86, 7, 'i cili del nga kurrizi (i mashkullit) dhe krahërori,'),
(5939, 86, 8, 'dhe, me të vërtetë, Ai (Perëndia) është i pushtetshëm – për ta kthyer (ringjallë) atë,'),
(5940, 86, 9, 'në Ditën kur do të zbulohen sekretet'),
(5941, 86, 10, 'kur njeriu nuk do të ketë as fuqi, as ndihmës.'),
(5942, 86, 11, 'Betohem në qiellin, shëndrues i (plot) gjendjeve'),
(5943, 86, 12, 'dhe Tokën që plasaritet për të mbirë bimët, -'),
(5944, 86, 13, 'Kur’ani, me të vërtetë, është fjalë që ndanë të vërtetën prej të pavërtetës,'),
(5945, 86, 14, 'ai nuk është kurrfarë burleske (tregim i kotë)!'),
(5946, 86, 15, 'Me të vërtetë, at shërbehen me dinakëri (të mëdha),'),
(5947, 86, 16, 'e, Unë i ndëshkojë për dinakërinë, e tyre –'),
(5948, 86, 17, 'andaj, caktoju pak afat jobesimtarëve, lëri në qetësi ata edhe për një çast.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Falendero emrin e Zotit tënd të Lartësuar,'),
(5950, 87, 2, 'që krijon çdo gjë dhe e bënë të harmonizuar,'),
(5951, 87, 3, 'dhe që çdo gjë e ka caktuar dhe i ka dhënë inspirim,'),
(5952, 87, 4, 'dhe që bënë të rriten kullosat,'),
(5953, 87, 5, 'dhe pastaj bën që të shndërrohen, të thahen e të vyshken.'),
(5954, 87, 6, 'Na do ta mësojmë ty (Kur’anin, përmes Xhebrailit), e ti nuk do ta harrosh,'),
(5955, 87, 7, 'përveç asaj që don Zoti. Ai (Perëndia), me të vërtetë, di çdo gjë që shprehni haptazi dhe atë që fshihni –'),
(5956, 87, 8, 'dhe Ne do ta lehtësojmë ty (rrugën) e lehtë,'),
(5957, 87, 9, 'prandaj këshillo ti – nëse këshilla ka dobi:'),
(5958, 87, 10, 'ai që i druan Perëndisë, këshillohet,'),
(5959, 87, 11, 'e shmanget nga këshillat, ai më i keqi,'),
(5960, 87, 12, 'i cili do të digjet në zjarrin e madh,'),
(5961, 87, 13, 'e në të, as nuk do të vdes e as nuk do të jetojë (jetë normale).'),
(5962, 87, 14, 'Shpëtimin do të arrijë ai që pastrohet (nga të këqiat)'),
(5963, 87, 15, 'dhe që e përmend emrin e Zotit të vet dhe bënë namaz!'),
(5964, 87, 16, 'Por ju, përkundrazi, më shumë po e parapëlqeni jetën e kësaj bote,'),
(5965, 87, 17, 'por, jeta tjetër është më e mirë dhe e përhershme (amshuar).'),
(5966, 87, 18, 'Kjo, me të vërtetë, ka qenë shënuar në faqet (e librave) e broshurat e lashta,'),
(5967, 87, 19, 'librat (broshurat) e Ibrahimit dhe Musait.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'A të ka arritur ty lajmi për fatkeqësinë e kaplueshme (të Kijametit)?'),
(5969, 88, 2, 'Atë Ditë, fytyrat (e të këqinjve) janë të dëshpëruara,'),
(5970, 88, 3, 'me punë të vështira e të rraskapitura,'),
(5971, 88, 4, 'në zjarr të fortë do të digjen,'),
(5972, 88, 5, 'e do të pinë nga burimet e vluara,'),
(5973, 88, 6, 'kur tjetër ushqim për ta nuk do të ketë, përpos therrave,'),
(5974, 88, 7, 'e ky (ushqim), as nuk do t’i trashë as nuk do t’i ngop (nga uria).'),
(5975, 88, 8, 'Atë Ditë, fytyrat e disave (besimtarëve) do të jenë të gëzuara,'),
(5976, 88, 9, 'të kënaqura për punët e bëra, -'),
(5977, 88, 10, 'të vendosura në kopshtin madhështor,'),
(5978, 88, 11, 'në të cilin nuk do të dëgjojnë fjalë boshe.'),
(5979, 88, 12, 'Në të ka burime uji – rrjedhëse,'),
(5980, 88, 13, 'në të ka divane të çmueshme,'),
(5981, 88, 14, 'dhe kupa të përgatitura,'),
(5982, 88, 15, 'dhe jastëk të renditur'),
(5983, 88, 16, 'e shumë qilima të shtruar.'),
(5984, 88, 17, 'Vallë, a nuk shohin ata se si është krijuar devja'),
(5985, 88, 18, 'e si është ngritur qielli,'),
(5986, 88, 19, 'dhe si janë vendosur malet,'),
(5987, 88, 20, 'dhe si është shtrirë Toka?!'),
(5988, 88, 21, 'Ti këshillo (njerëzit) – se ti je vetëm këshillues,'),
(5989, 88, 22, 'ti nuk je mbi ta imponues (sundues)!'),
(5990, 88, 23, 'E, përveç atyre që shmangën dhe nuk besojnë, -'),
(5991, 88, 24, 'ata, Perëndia do t’i dënojë me mundimet më të mëdha.'),
(5992, 88, 25, 'Ata, me të vërtetë, do të kthehen te Ne,'),
(5993, 88, 26, 'dhe, me të vërtetë, do të japin llogari para Nesh!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Betohem në agimin,'),
(5995, 89, 2, 'dhe dhjetë netët,'),
(5996, 89, 3, 'dhe në të gjitha që janë çift e tek,'),
(5997, 89, 4, 'dhe natën që shkon, -'),
(5998, 89, 5, 'vallë, a nuk është ky betim për të mençurin?'),
(5999, 89, 6, 'Vallë, a nuk di ti se çka ka bërë Zoti yt me Adin,'),
(6000, 89, 7, 'e me banorët e qytetit Iremes, pronarë të pallatee madhështore,'),
(6001, 89, 8, 'çfarë nuk ka pasur askund tjetër;'),
(6002, 89, 9, 'dhe si i dënoi themudasit, të cilët kanë gëdhendur shkëmbinj në luginë,'),
(6003, 89, 10, 'dhe Faraonin – pronarin e shtyllave,'),
(6004, 89, 11, 'që kanë bërë zullum pa të drejtë në tokë'),
(6005, 89, 12, 'dhe shtonin ngatërresat,'),
(6006, 89, 13, 'andaj Zoti yt, e lëshoi mbi ta kamxhikun e dënimit (lloj-lloj dënimesh),'),
(6007, 89, 14, 'sepse, me të vërtetë, Zoti yt i pret ata në pusi (i përcjellë punët e tyre).'),
(6008, 89, 15, 'Por, Perëndia kur ta provojë njeriun e ta dhurojë me begati, (ai) thotë: “Zoti im është sjellë në mënyrë bujar endaj meje!”'),
(6009, 89, 16, 'E, kur ta provojë atë, duke ia munguar furnizimin, atëherë ai thotë: “Zoti im më ka poshtëruar”.'),
(6010, 89, 17, 'Jo, jo! Por ju nuk po i respektoni bonjakët,'),
(6011, 89, 18, 'dhe nuk po e nxitni njëri-tjetrin për t’i ushqyer të varfërit,'),
(6012, 89, 19, 'kurse trashëgiminë, e hani me lakmi të pangopur,'),
(6013, 89, 20, 'dhe dëshironi pasuri të pakufishme.'),
(6014, 89, 21, 'Por kjo nuk është mirë! Atëherë, kur Toka do të copëtohet në thërmia,'),
(6015, 89, 22, 'dhe kur do të arrijë urdhëri i Zotit tënd, e engjëjt të rreshtohen radhë pas radhe,'),
(6016, 89, 23, 'dhe atë Ditë kur afrohet (prezentohet) skëterra; atëherë njeriu do të kujtohet – por ç’dobi?!'),
(6017, 89, 24, 'dhe thotë: “Ah! Sikur të kisha punuar gjëra të mira në jetën time!”'),
(6018, 89, 25, 'Atë Ditë, askush nuk mund të dënojë, si Ai (dënimi i Tij),'),
(6019, 89, 26, 'dhe, atë ditë, askush nuk lidhë në pranga si Ai!'),
(6020, 89, 27, 'E ti, o shpirt i besuar (këmbëngulës në besim)'),
(6021, 89, 28, 'kthehu te Zoti yt i kënaqur, sepse, edhe Ai është i kënaqur me ty,'),
(6022, 89, 29, 'prandaj, hynnë mes të dashurve të Mi,'),
(6023, 89, 30, 'dhe hyn në xhennetin Tim!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Betohem në këtë qytet,'),
(6025, 90, 2, 'pasi në të, ti je i lirë (o Muhammed), -'),
(6026, 90, 3, 'dhe (betohem) në ata që kanë lindur dhe lindën,'),
(6027, 90, 4, 'Na, e krijuam njeriun që të luftojë në vështirësi (për të dy jetërat).'),
(6028, 90, 5, 'A mendon ai se askush nuk mund t’i bëjë gjë?'),
(6029, 90, 6, 'Ai thotë: “Kam shpenzuar pasuri të pamasë!”'),
(6030, 90, 7, 'Vallë, a mendon ai, se, nuk e ka parë askush?'),
(6031, 90, 8, 'A nuk ia kemi dhënë dy sytë,'),
(6032, 90, 9, 'edhe gjuhën edhe dy buzët,'),
(6033, 90, 10, 'dhe të mirën e të keqen ia kemi shpjeguar?'),
(6034, 90, 11, 'E, përse nuk është përpjekur të kapërcejë vështirësitë?'),
(6035, 90, 12, 'E kush mund të shpjegojë ç’është vështirësia (për të punuar vepra të mira)?'),
(6036, 90, 13, 'me liruar skllavin (nga skllavëria),'),
(6037, 90, 14, 'ose me ushqyer, kur bredh uria (të urët)'),
(6038, 90, 15, 'bonjakun e afërm'),
(6039, 90, 16, 'ose të varfërin e mjerë,'),
(6040, 90, 17, 'e pos këtyre, të jetë nga ata që besojnë, të cilët njëri-tjetri i japin kurajo për durim dhe kurajo për mëshirë;'),
(6041, 90, 18, 'ët gjithë këta, do të jenë të lumtur!'),
(6042, 90, 19, 'E, ata që nuk besojnë në argumentet Tona, ata do të jenë fatzinj,'),
(6043, 90, 20, 'ata do të jenë në zjarrin e mbyllur.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Betohem në Diellin dhe dritën e tij,'),
(6045, 91, 2, 'dhe Hënën që e përcjellë'),
(6046, 91, 3, 'dhe ditën që e bën të dukshëm atë,'),
(6047, 91, 4, 'dhe natën kur e mbulon'),
(6048, 91, 5, 'dhe qiellin dhe Atë që e ka ndërtuar (atë);'),
(6049, 91, 6, 'dhe Tokën dhe Atë që e ka shtruar atë (Tokën),'),
(6050, 91, 7, 'dhe shpirtin (njeriun e parë) dhe Atë që e krijoi'),
(6051, 91, 8, 'e ua ka shpjeguar të mirën e të keqen, -'),
(6052, 91, 9, 'me të vërtetë, ai që e pastron atë (shpirtin), do të jetë i lumtur,'),
(6053, 91, 10, 'e, me të vërtetë do të humbë ai që e devijon atë (shpirtin)!'),
(6054, 91, 11, 'Populli i Themudit, nga shfrenimi i vet – ka përgënjeshtruar:'),
(6055, 91, 12, 'kur nga mesi i tyre u ngrit një fatzezë,'),
(6056, 91, 13, 'i dërguari i Perëndisë (Salihu), ju tha atyre: “Lëreni deven e Perëndisë dhe kujdesuni për kohën kur duhet të pijë ajo!”'),
(6057, 91, 14, 'Por ata nuk i besuan (atij), madje e prënë atë (devën) – dhe Zoti i tyre shkatërroi ata për mëkatet e tyre, duke i zhdukur deri në të fundit,'),
(6058, 91, 15, 'se, Ai, nuk i druan pasojës nga ajo.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Betohem në natën që e mbulon terri,'),
(6060, 92, 2, 'dhe ditën që agon,'),
(6061, 92, 3, 'dhe që ka krijuar mashkullin dhe femrën –'),
(6062, 92, 4, 'me të vërtetë, veprat tuaja janë të ndryshme:'),
(6063, 92, 5, 'atij që jep (për bamirës) dhe ruhet (prej gabimeve)'),
(6064, 92, 6, 'dhe i cili dëshmon (vërteton) atë që është më e bukur (besimin)'),
(6065, 92, 7, 'Na, atij do t’ia lehtësojmë rrugën e lumturisë (xhennetit);'),
(6066, 92, 8, 'kurse atij që është koprrac dhe e konsideron veten të pavarur (nga Zoti),'),
(6067, 92, 9, 'dhe e përgënjeshtron atë më të bukurën (besimin),'),
(6068, 92, 10, 'Na, atij, do t’ia lehtësojmë rrugën e rëndë (skëterrës),'),
(6069, 92, 11, 'e kur të hedhet (në varr), pasuria e tij nuk do t’i bën dobi.'),
(6070, 92, 12, 'Neve na përket, që ta tregojmë rrugën e drejtë,'),
(6071, 92, 13, 'dhe, me të vërtetë, e Jona është ajo botë dhe kjo botë!'),
(6072, 92, 14, 'Andaj Unë, ju kam tërhequr vërejtjen me zjarrin që ndizet,'),
(6073, 92, 15, 'në të cilin do të hidhet vetëm më i keqi,'),
(6074, 92, 16, 'i cili ka përgënjeshtruar dhe është larguar nga besimi;'),
(6075, 92, 17, 'e do të jetë larg nga ai (zjarri), ai që i druan Perëndisë,'),
(6076, 92, 18, 'ai që ndanë nga pasuria e tij, duke e pastruar veten (nga të këqiat),'),
(6077, 92, 19, 'e nuk e jep atë – për t’ia kthyer ndokush,'),
(6078, 92, 20, 'por vetëm që të fitojë kënaqësinë e Zotit të tij të Lartëmadhërueshëm,'),
(6079, 92, 21, 'dhe ai, me siguri, do të jetë i kënaqur!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Betohem në mëngjesin,'),
(6081, 93, 2, 'dhe natën kur terrohet e qetësohet –'),
(6082, 93, 3, 'Zoti yt, nuk të ka braktisur e as nuk të ka urrejtur!'),
(6083, 93, 4, 'Me të vërtetë, jeta e ardhshme për ty është më e mirë se kjo jetë,'),
(6084, 93, 5, 'e, me të vërtetë, Zoti yt do të japi ty, e ti do të jesh i kënaqur.'),
(6085, 93, 6, 'A nuk ishe ti bonja, e Ai të mori në strehim,'),
(6086, 93, 7, 'dhe të gjeti të humbur (të paudhëzuar), e të udhëzoi me Shpalljen e Kur’anit,'),
(6087, 93, 8, 'dhe ishe i varfër e të bëri të pasur?'),
(6088, 93, 9, 'Mos e nënçmo bonjakun'),
(6089, 93, 10, 'e lypsin mos e refuzo me ashpërsi,'),
(6090, 93, 11, 'dhe dhuntitë e Zotit tënd, shprehi!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Vallë, a nuk ta kemi zgjëruar krahërorin (zemrën tënde),'),
(6092, 94, 2, 'dhe ta kemi heqë barrën ty,'),
(6093, 94, 3, 'e cila të ka rënduar tepër kurrizin tënd,'),
(6094, 94, 4, 'dhe e kemi ngritur lart emrin (kujtimin) tënd!'),
(6095, 94, 5, 'E, me të vërtetë, me vështirësi – arrihet në lehtësi,'),
(6096, 94, 6, 'me të vërtetë, me vështirësi – arrihet në lehtësi (lumturi)!'),
(6097, 94, 7, 'E, kur ta kryejsh njërën punë, filloja tjetrës (menjëherë)'),
(6098, 94, 8, 'dhe (vetëm) te Zoti yt syno (drejtohu)!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Betohem në fiq dhe ullinj,'),
(6100, 95, 2, 'dhe malin e Sinajit,'),
(6101, 95, 3, 'dhe në këtë qytet të sigurt,'),
(6102, 95, 4, 'me të vërtetë, Na e kemi krijuar njeriun në formën (hrmoninë) më të bukur,'),
(6103, 95, 5, 'pastaj, e kemi kthyer atë në gjendjen më të ulët (në këtë jetë dhe tjetrën),'),
(6104, 95, 6, 'përveç atyre që besojnë dhe punojnë vepra të mira, për ta ka shpëblime të pandërprera.'),
(6105, 95, 7, 'E, çka atëherë të shtyni ty (o njeri), që ta mohosh besimin?'),
(6106, 95, 8, 'A nuk është Perëndia gjykatësi më i drejtë?!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Lexo në emër të Zotit tënd, i cili ka krijuar (të gjitha),'),
(6108, 96, 2, 'i cili e krijoi njeriun nga gjaku i droçkuar (embrioni),'),
(6109, 96, 3, 'lexo, se Zoti yt, është më bujari,'),
(6110, 96, 4, 'që ia mësoi njeriut pendën (të shkruajë),'),
(6111, 96, 5, 'që njeriut ia mësoi atë që nuk e diti.'),
(6112, 96, 6, 'Jo, jo! (megjithatë), me të vërtetë, njeriu ka kalur kufirin (në të keqe),'),
(6113, 96, 7, 'posa e ka ndier veten të pasur (se nuk i nevojitet askush),'),
(6114, 96, 8, 'e, me të vërtetë, kthimi është te Zoti yt!'),
(6115, 96, 9, 'A e ke parë ti atë që pengon'),
(6116, 96, 10, 'njeriun (adhuruesin) kur falet (bënë namaz)?'),
(6117, 96, 11, 'Më thuaj ti (o Muhammed!), nëse ai mendon se është në rrugën e drejtë,'),
(6118, 96, 12, 'apo rekomandon që të adhurohen idhujt,'),
(6119, 96, 13, 'e, çka mendon ti nëse ai i përgënjeshtron (argumentet Tona) dhe kthehet?'),
(6120, 96, 14, 'A nuk e di ai – se Zoti i sheh të gjitha (e dhuron të mirin, ndërsa e dënon të keqin)?'),
(6121, 96, 15, 'Jo, jo! Me të vërtetë, nëse nuk tërhiqet nga kjo, do ta kapim për flokësh (gërshetën),'),
(6122, 96, 16, 'për flokët (gërshetin) e gënjeshtarit dhe fajtorit,'),
(6123, 96, 17, 'e, ai le ta thërras shoqërinë e vet (në ndihmë).'),
(6124, 96, 18, 'Na do t’i thërrasim Zebanijt (rojet e Skëterrës).'),
(6125, 96, 19, 'Jo, jo! Mos iu përul ti atij; por bënë ti sexhde dhe afroju (Zotit tënd)!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Na kemi fillur ta shpallim (Kur’anin) në natën e Kadrit –'),
(6127, 97, 2, 'e kush të tregon ty se ç’është nata e Kadrit?'),
(6128, 97, 3, 'Nata e kadrit është më e vlefshme se një mijë muaj –'),
(6129, 97, 4, 'engjëjt dhe Xhebraili, me lejen e Zotit të tyre, zbresin në të, për të zbatuar (të gjitha) urdhërat e Zotit,'),
(6130, 97, 5, 'përshëndetjet e kësaj nate zgjasin deri në agim.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Nuk u larguan nga feja e tyre – mohuesit, që ishin nga idhtarët e Librit, si dhe paganët, deri sa ju erdhi argumenti i qartë:'),
(6132, 98, 2, 'Pejgamberi nga Perëndia, i cili lexon broshurat e pastra (nga gabimet),'),
(6133, 98, 3, 'në të cilat ka kapituj me dispozita të çmueshme.'),
(6134, 98, 4, 'E u ndanë (nuk u pajtuan) ata që patën Librin, vetëm atëherë kur u erdhi argumenti i qartë,'),
(6135, 98, 5, 'e janë urdhëruar ata që t’i luten vetëm Perëndisë, që sinqerisht, si besimtarë të vërtetë, të predikojnë fenë dhe të bëjnë namaz, dhe të ndajnë lëmoshën (zeqatin); e kjo është feja e drejtë.'),
(6136, 98, 6, 'Ata që nuk besojnë, si ata që patën Libër ashtu edhe paganët, ata, me siguri do të gjenden në zjarrin e skëterrës, në te do të mbeten përherë, ata janë krijesat më të këqia.'),
(6137, 98, 7, 'E, me të vërtetë, ata që besojnë dhe punojnë vepra të mira – ata janë krijesat më të mira,'),
(6138, 98, 8, 'shpërblimi i tyre te Zoti i tyre janë kopshtet (xhennetet) e Adnit, nëpër të cilat rrjedhin lumenjt ku do të banojnë përherë dhe përgjithmonë. Perëndia është i kënaqur me ta, si dhe ata janë të kënaqur me Te. Kjo është dhuratë për ata që i druajnë Zotit të vet.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Kur Toka të dridhet me dridhjen e tmerrshme të saj,'),
(6140, 99, 2, 'dhe kur ajo (Toka) të nxjerrë materiale nga brendia e saj,'),
(6141, 99, 3, 'dhe njeriu të thërret: “Çka ka ajo?!”'),
(6142, 99, 4, 'atë Ditë ajo tregon lajmet e veta (veprat e mira dhe të këqia) që kanë ngjarë në të,'),
(6143, 99, 5, 'sepse, Zoti yt e ka urdhëruar atë.'),
(6144, 99, 6, 'Atë Ditë, njerëzit do të paraqiten në grupe-grupe (të ndara), për t’ju treguar veprat e tyre:'),
(6145, 99, 7, 'ai që ka punuar ndonjë të mirë, qoftë ajo e vogël madje sa thërmia, do ta shohin atë,'),
(6146, 99, 8, 'kurse ai që ka punuar të keqe, qoftë madje sa thërmia – do ta shohin atë.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Betohem në atë (kuajt) që vrapojnë duke dihatur,'),
(6148, 100, 2, 'e që qesin xixa nga thundrat e tyre,'),
(6149, 100, 3, 'dhe sulmojnë (armikun) në mëngjes,'),
(6150, 100, 4, 'dhe n’atë rast çojn pluhur,'),
(6151, 100, 5, 'të cilët futen në radhët e armikut, -'),
(6152, 100, 6, 'me të vërtetë, njeriu është jomirënjohës ndaj Zotit të vet,'),
(6153, 100, 7, 'dhe ai, me të vërtetë, për këtë, është i vetëdijshëm,'),
(6154, 100, 8, 'dhe, ai është lakmues i madh i pasurisë.'),
(6155, 100, 9, 'E, vallë, a nuk e di ai (njeriu), kur do të përzihen (ringjallen) ata që gjenden në varreza'),
(6156, 100, 10, 'dhe kur të dali në shesh ajo që është në zemra (e mira dhe e keqja),'),
(6157, 100, 11, 'Zoti i tyre, me siguri, atë Ditë do të dijë çdo gjë për ta.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'Goditja e tmerrshme (Kijameti)!'),
(6159, 101, 2, 'Ç’është Goditja e tmerrshme!'),
(6160, 101, 3, 'Kush të tregon ty për Goditjen e tmerrshme?'),
(6161, 101, 4, 'Njerëzit, atë Ditë do të jenë të shpërndarë si fluturat,'),
(6162, 101, 5, 'kurse malet – si leshi i shprishur.'),
(6163, 101, 6, 'Atij (njeriut), veprat e mira të të cilit rëndojnë në peshojë'),
(6164, 101, 7, 'ai do të jetë në jetën e kënaqur (në jetën e përhershme),'),
(6165, 101, 8, 'e atij (njeriu), veprat e mira të të cilit nuk rëndojnë në peshojë,'),
(6166, 101, 9, 'strehimi i tij do të jetë gremina e Skëterrës.'),
(6167, 101, 10, 'E, kush të tregon ty se ç’është gremina e Skëterrës?'),
(6168, 101, 11, '(Ajo është) zjarr i vrullshëm.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Juve, u ka hutuar vrapimi (garimi), për pasuri (e fëmijë)'),
(6170, 102, 2, 'deri sa të varroseni (vdisni).'),
(6171, 102, 3, 'Gjithsesi, ju do ta kuptoni (këtë gabim)!'),
(6172, 102, 4, 'E, madje edhe një herë, ju, me siguri, do ta kuptoni (këtë gabim)!'),
(6173, 102, 5, 'Jo, jo! Mashtroheni, sikur ta dini ju mirë, (nuk do të mburreshit me kotësi).'),
(6174, 102, 6, 'Ju do ta shihni skëterrën!'),
(6175, 102, 7, 'E, (madje) edhe një herë, me të vërtetë, do ta shihni skëterrën konkretisht (qartë)!'),
(6176, 102, 8, 'Pastaj, atë Ditë, do t’ju pyesin për kënaqësitë (e kësaj bote)!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Betohem në kohë, -'),
(6178, 103, 2, 'me të vërtetë, njeriu është në humbje,'),
(6179, 103, 3, 'përveç atyre që besojnë dhe punojnë vepra të mira, dhe të cilët rekomandojnë njëri-tjetrit të vërtetën (fenë Islame), dhe të cilët i rekomandojnë njëri-tjetrit durimin, (në zbatimin e urdhërave të Perëndisë).');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Mjer për çdo përqeshës – shpifës,'),
(6181, 104, 2, 'që grumbullon pasuri dhe e ruan, (për ardhmërinë e ëndërruar)'),
(6182, 104, 3, 'dhe mendon që ajo pasuri e tij, do ta bëjë të pavdekshëm!'),
(6183, 104, 4, 'Jo, jo! Mashtrohet! Ai, me të vërtetë, do të hidhet (në) Hutameh (Skëterrë), i habitur;'),
(6184, 104, 5, 'E kush të tregon ty se ç’është el-Hutameh?'),
(6185, 104, 6, 'Ai është zjarri i ndezur i Zotit,'),
(6186, 104, 7, 'i cili do të depërtojë deri në zemër.'),
(6187, 104, 8, 'Ai (zjarri) do të jetë i mbyllur mbi ta (si saç),'),
(6188, 104, 9, 'në shtylla të gjata flakëruese.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Vallë, a nuk ke dëgjuar ti se si ka vepruar Zoti yt me ushtrinë (pronarët) e elefantëve?!'),
(6190, 105, 2, 'A nuk ju shkuankot dinakëritë e tyre,'),
(6191, 105, 3, 'e dërgoi kundër tyre tufa zogjësh – (Ebabilash),'),
(6192, 105, 4, 'të cilët hedhnin mbi ta copa dheu (të forcuara si gurë),'),
(6193, 105, 5, 'e i bëri ata si ndonjë gjeth që është hëngër (prej krimit – tëjes)?');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, '(Perëndia e zhduku ushtrinë e elefantëve) për t’i qetësuar Kurejshët,'),
(6195, 106, 2, 't’i qetësojë ata në udhëtimin që bëjnë dimër e verë,'),
(6196, 106, 3, 'le të adhurojnë pra Zotin e kësaj shtëpie (Qabes),'),
(6197, 106, 4, 'i cili i ushqen (ngop) ata nga uria dhe i siguroi nga frika.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'A di ti për atë që e përgënjeshtron Ditën e llogarisë (e shpërblimit)?'),
(6199, 107, 2, 'E ai është, i cili ashpër i dëbon bonjakët'),
(6200, 107, 3, 'dhe nuk nxitë që të ushqejnë të varfërit.'),
(6201, 107, 4, 'Mjerë për ata, të cilët kur falen,'),
(6202, 107, 5, 'janë të pakujdesshëm (të harrueshëm),'),
(6203, 107, 6, 'e veprojnë, vetëm sa për sy e faqe (para botës)'),
(6204, 107, 7, 'dhe pengojnë dhënien e ndihmës (në shërbimet e vogla midis njerëzve)!');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Na, me të vërtetë, të kemi dhënë ty shumë të mira,'),
(6206, 108, 2, 'andaj, bënë namaz për Zotin tënd dhe therr kurban.'),
(6207, 108, 3, 'Me të vërtetë, ai që të urren ty, ai vetë është i cungët.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'Thuaj: “O ju mohues!'),
(6209, 109, 2, 'Unë nuk do ta adhurojë atë, që po e adhuroni ju,'),
(6210, 109, 3, 'e as ju nuk do ta adhuroni Atë që po e adhuroj unë;'),
(6211, 109, 4, 'Unë nuk kam adhuruar atë që e keni adhuruar ju,'),
(6212, 109, 5, 'por, edhe ju nuk e keni adhuruar Atë, që po e adhuroj unë.'),
(6213, 109, 6, 'Ju keni besimin tuaj, e unë kam besimin tim!”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Kur të arrijë ndihma e Zotit dhe fitorja,'),
(6215, 110, 2, 'dhe i sheh njerëzit grupe-grupe duke hyrë në fenë e Perëndisë (në Islamizëm)'),
(6216, 110, 3, 'atëherë lavdëroje Zotin tënd dhe kërko falje nga Ai; se Ai, me të vërtetë, është pranues i madh i pendimeve.');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Ju thafshin duart Ebu Lehebit, dhe iu thánë!'),
(6218, 111, 2, 'Nuk i ka ndihmuar atij as pasuria, e as fitimet tjera.'),
(6219, 111, 3, 'Ai, me siguri, do të hudhet në zjarrin flakërues,'),
(6220, 111, 4, 'ashtu dhe gruaja e tij – dru-bartëse;'),
(6221, 111, 5, 'në qafën e saj ka një litar të bërë prej fijeve (të palmës).');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'Thuaj: “Perëndia është Një!'),
(6223, 112, 2, 'Perëndisë i drejtohen për çdo gjë!'),
(6224, 112, 3, '(Ai) nuk ka lindur prej ndokujt, as nuk ka lindur kë,'),
(6225, 112, 4, 'dhe askush nuk i gjason Atij!”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'Thuaj: “I lutem dhe mbështetem te Zoti i agimit'),
(6227, 113, 2, 'që të më mbrojë nga sherri i të gjitha krijesave,'),
(6228, 113, 3, 'dhe prej sherrit të natës kur kaplon terri,'),
(6229, 113, 4, 'dhe prej sherrit të falltorëve që fryejnë në nyje (bëjnë magji),'),
(6230, 113, 5, 'dhe nga sherri i ziliqarit kur e shfaq zilinë!”');
INSERT INTO `sq_nahi` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'Thuaj: “I lutem dhe i mbështetem Zotit të njerëzve,'),
(6232, 114, 2, 'Sundimtarit të njerëzve,'),
(6233, 114, 3, 'Perëndisë së njerëzve,'),
(6234, 114, 4, 'nga sherri i djallit, bezdisës,'),
(6235, 114, 5, 'i cili fshehurazi hedh dyshime në shpirtërat e njerëzve –'),
(6236, 114, 6, 'prej xhindëve dhe prej njerëzve!”');
